INSERT INTO "abulsalam" VALUES(1,1,'1:1',' د الله په نامه سره (شروع كوم) چې ډېر زيات مهربان، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(1,2,'1:2',' ټول (د كمال) صفتونه خاص د الله لپاره دي چې د ټولو عالَمونو ښه پالونكى دى');
INSERT INTO "abulsalam" VALUES(1,3,'1:3',' ډېر زيات مهربان، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(1,4,'1:4',' د بَدلې د ورځې مالك دى');
INSERT INTO "abulsalam" VALUES(1,5,'1:5',' مونږ خاص ستا عبادت كوو او خاص له تا نه مدد غواړو');
INSERT INTO "abulsalam" VALUES(1,6,'1:6',' ته مونږ ته سَمَه (نېغه) لاره وښَيَه');
INSERT INTO "abulsalam" VALUES(1,7,'1:7',' د هغو خلقو لاره چې تا پر هغوى باندې انعام كړى دى؛ چې نه پر هغوى باندې غضب شوى او نه ګمراهان دي');
INSERT INTO "abulsalam" VALUES(2,1,'2:1',' الف، لام، ميم ');
INSERT INTO "abulsalam" VALUES(2,2,'2:2',' دا (عظيم) كتاب دى، په ده كې هېڅ شك نشته، د پرهېزګارانو لپاره هدايت دى');
INSERT INTO "abulsalam" VALUES(2,3,'2:3',' هغه خلق چې پر غيبو ايمان راوړي او لمونځ قايموي او له هغه (مال) نه څه حصه خرچ كوي؛ چې مونږ هغوى ته وركړى دى ');
INSERT INTO "abulsalam" VALUES(2,4,'2:4',' او هغه خلق چې ايمان راوړي پر هغه (كتاب) چې تا ته نازل كړى شوى دى او پر هغو (كتابونو) چې له تا نه مخكې نازل كړى شوي دي، او پر اخرت همدوى يقين لري ');
INSERT INTO "abulsalam" VALUES(2,5,'2:5',' دغه خلق د خپل رب له طرفه په راغلي هدايت باندې دي او همدغه خلق پوره كامياب دي ');
INSERT INTO "abulsalam" VALUES(2,6,'2:6',' بېشكه هغه (خاص) كسان چې كافر شوي دي؛ دوى ته يو شان ده كه ته دوى ووېروې يا ته دوى ونه وېروې، دوى ايمان نه راوړي ');
INSERT INTO "abulsalam" VALUES(2,7,'2:7',' الله د هغوى پر زړونو او د هغوى پر غوږونو مهر لګولى دى، او د هغوى پر سترګو (يوه خاصه) پرده ده، او د هغوى لپاره ډېر لوى عذاب دى ');
INSERT INTO "abulsalam" VALUES(2,8,'2:8',' او په خلقو كې ځېنې هغه څوك دي چې وايي: مونږ پر الله او د اخرت پر ورځ ايمان راوړى دى، حال دا چې دوى له سَره مومنان نه دي ');
INSERT INTO "abulsalam" VALUES(2,9,'2:9',' دوى له الله سره دوكه كوي، او له هغو خلقو سره چې ايمان يې راوړى دى، حال دا چې دوى يواځې له هم خپلو ځانونو سره دوكه كوي، او شعور نه لري ');
INSERT INTO "abulsalam" VALUES(2,10,'2:10',' د دوى په زړونو كې بېماري ده، نو الله دوى ته بېماري ورزياته كړه، او د دوى لپاره ډېر دردوونكى عذاب دى؛ په دې سبب چې دوى به همېشه دروغ ويل ');
INSERT INTO "abulsalam" VALUES(2,11,'2:11',' او كله چې دوى ته وويل شي چې په ځمكه كې فساد مه كوئ، دوى وايي: يقينًا همدا خبره ده چې مونږ خو بس اصلاح كوونكي يو ');
INSERT INTO "abulsalam" VALUES(2,12,'2:12',' خبردار! بېشكه همدوى فساد كوونكي دي، او لېكن دوى شعور نه لري ');
INSERT INTO "abulsalam" VALUES(2,13,'2:13',' او كله چې دوى ته وويل شي: تاسو ايمان راوړئ، لكه څنګه چې خلقو (صحابه وو) ايمان راوړى دى، نو دوى وايي: ايا مونږ ايمان راوړو، لكه څنګه چې بې وقوفو (كم عقلو) ايمان راوړى دى؟ خبردار! يقينًا همدوى بې وقوفه دي، او لېكن دوى نه پوهېږي ');
INSERT INTO "abulsalam" VALUES(2,14,'2:14',' او كله چې دوى له مومنانو سره ملاقات وكړي، نو وايي چې مونږ ايمان راوړى دى، او كله چې له خپلو شيطانانو (مشرانو) سره يواځې شي، نو (بيا) وايي: په رښتيا خو مونږ له تاسو سره يو، خبره همدا ده چې مونږ خو بس استهزا (خندا) كوونكي يو ');
INSERT INTO "abulsalam" VALUES(2,15,'2:15',' الله په دوى پورې استهزا كوي او دوى ته په خپلې سركشۍ كې ترقي وركوي، په داسې حال كې چې دوى حيران سرګردان ګرځي ');
INSERT INTO "abulsalam" VALUES(2,16,'2:16',' دا هغه خلق دي چا چې د نېغې لارې په بدل كې ګمراهي اخيستې ده، نو د دوى سوداګرۍ هېڅ ګټه ونه كړه او دوى نېغه لار موندونكي نه وو، ');
INSERT INTO "abulsalam" VALUES(2,17,'2:17',' د دوى مثال د هغه كس په شان دى چې اور يې بل كړ، نو كله چې دغه (اور) د هغه ګېرچاپېره (ځايونه) روښانه كړل، نو الله د هغوى رڼا ختمه كړه، او هغوى يې په تيارو كې پرېښودل، داسې چې نه ويني ');
INSERT INTO "abulsalam" VALUES(2,18,'2:18',' (دوى) كاڼه دي، ګونګيان دي، ړانده دي، نو دوى نه راګرځي ');
INSERT INTO "abulsalam" VALUES(2,19,'2:19',' يا (مثال د دوى) له اسمان نه د ورېدونكي تېز باران په شان دى، چې په هغه كې تيارې او ګړزارى او برېښنا وي، دوى د تندرونو په وجه د مرګ له وېرې نه خپلې ګوتې په خپلو غوږونو كې نَنَباسي، او الله كافرانو لره راګېروونكى دى ');
INSERT INTO "abulsalam" VALUES(2,20,'2:20',' نژدې ده چې برېښنا د دوى نَظَرونه وتښتوي، كله هم چې هغه د دوى لپاره رڼا كوي، نو دوى په هغې (رڼا) كې مزل كوي او كله چې پر دوى تياره شي، نو ودرېږي، او كه الله غوښتلى، نو خامخا به يې د دوى اَورېدل او د دوى نظرونه ختم كړي وى، بېشكه الله پر هر شي باندې ښه قادر دى ');
INSERT INTO "abulsalam" VALUES(2,21,'2:21',' اى خلقو! د خپل رب عبادت كوئ، هغه چې تاسو يې پيدا كړي يئ او هغه كسان چې له تاسو نه مخكې وو، د دې لپاره چې تاسو پرهېزګاره شئ ');
INSERT INTO "abulsalam" VALUES(2,22,'2:22',' هغه (رب) چې ستاسو لپاره يې ځمكه فرش او اسمان چت جوړ كړى دى او له بره نه يې اوبه نازلې كړي دي، بيا يې په دغو سره له مېوو نه ستاسو لپاره رزق راوېستلى دى، نو له الله سره هېڅ قسمه شريكان مه جوړوئ، حال دا چې تاسو پوهېږئ ');
INSERT INTO "abulsalam" VALUES(2,23,'2:23',' او كه تاسو د هغه (قرآن) په هكله په كوم شك كې يئ چې مونږ پر خپل بنده نازل كړى دى، نو د ده غوندې يو وړوكى سورت راوړئ، او له الله نه سِوا خپل مددګاران راوغواړئ، كه تاسو رښتوني يئ ');
INSERT INTO "abulsalam" VALUES(2,24,'2:24',' نو كه تاسو(دا كار) ونه كړ او كله به يې هم ونه كړى شئ، نو له هغه اور نه ځان بچ كړئ چې خشاك يې انسانان او كاڼي دي، د كافرانو لپاره تيار كړى شوى دى ');
INSERT INTO "abulsalam" VALUES(2,25,'2:25',' او زېرى وركړه هغو كسانو ته چې ايمان يې راوړى دى او نېك عمل يې كړى دى، چې يقينًا د دوى لپاره (داسې) جنتونه دي چې د هغو له لاندې وَلې بهېږي، هر ځلې چې دوى ته له هغو (جنتونو) نه څه مېوه د خوراك لپاره وركړى شي، نو دوى به وايي: دا خو هماغه ده چې مونږ ته مخكې راكړى شوې وه، او دوى ته به هغه يو له بله مُشابه (يو شانته) وركول كېږي، او د دوى لپاره په دغو (جنتونو) كې ډېرې پاكې بيبيانې (ښځې) دي، او دوى به په دغو كې همېشه وي ');
INSERT INTO "abulsalam" VALUES(2,26,'2:26',' بېشكه الله د هېڅ مثال په بيانولو نه شرمېږي، د ماشي وي، يا كه له ده نه د زيات (شي) وي، نو هر چې هغه خلق دي چې ايمان يې راوړى دى، نو هغوى پوهېږي چې يقينًا دا د هغوى د رب له طرف نه حق (مثال) دى، او پاتې شو هغه خلق چې كفر يې كړى دى، نو هغوى وايي: الله په دې مثال وركولو سره څه اراده كړې ده؟ (نو) هغه (الله) په دې سره ډېر خلق ګمراه كوي او ډېرو ته په دې سره هدايت وركوي او نه ګمراه كوي په دې سره مګر فاسقان ');
INSERT INTO "abulsalam" VALUES(2,27,'2:27',' هغه خلق چې د الله (سره كړى) لوظ ماتوي وروسته د هغه (لوظ) له مضبوطولو (او پخلي) نه او پرېكوي هغه څه چې الله يې د پيوست ساتلو حكم كړى دى، او په ځمكه كې ورانى كوي، همدغه خلق تاوانيان دي ');
INSERT INTO "abulsalam" VALUES(2,28,'2:28',' څنګه تاسو له الله نه منكرېږئ، حال دا چې تاسو نِشت وئ، نو هغه تاسو راژوندي كړئ، بيا به هغه تاسو مړه كوي، بيا به تاسو راژوندي كوي، بيا به خاص د هغه په طرف ستنولى شئ ');
INSERT INTO "abulsalam" VALUES(2,29,'2:29',' همدى هغه ذات دى چې ستاسو لپاره يې هغه ټول څه پيدا كړل چې په ځمكه كې دي، بيا يې پورته قصد وكړ، نو هغه يې ښه برابر اوه اسمانونه جوړ كړل، او هغه پر هر شي ښه عالم دى ');
INSERT INTO "abulsalam" VALUES(2,30,'2:30',' او كله چې ستا رب ملايكو ته وويل: بې شكه زه په ځمكه كې يو خليفه پيدا كوونكى يم، هغوى وويل: ايا ته په هغې كې داسې څوك پيدا كوې چې په هغې كې به ورانى كوي او وينې به تُويَوي؟ حال دا چې مونږ ستا له ستايلو سره تسبيح وايو او ستا پاكوالى بيانوو، (الله) وويل: يقينًا زه پوهېږم پر هغه څه چې تاسو پرې نه پوهېږئ ');
INSERT INTO "abulsalam" VALUES(2,31,'2:31',' او ادم ته يې ټول نومونه وښودل، بيا يې هغه ملايكو ته وړاندې كړل، نو ويې ويل: ما د دوى په نومونو خبر كړئ كه تاسو (په خپله خبره كې) رښتوني يئ ');
INSERT INTO "abulsalam" VALUES(2,32,'2:32',' هغوى وويل : ته پاك يې، مونږ ته هېڅ علم نشته سِوا له هغه نه چې تا مونږ ته راښودلى دى، بېشكه، خاص ته، ښه عالم، ښه حكمت والا يې ');
INSERT INTO "abulsalam" VALUES(2,33,'2:33','(الله) وويل: اى ادمه! دوى د هغو (څيزونو) پر نومونو خبر كړه، نو كله چې يې دوى د هغو پر نومونو خبر كړل، (الله) وويل: ايا ما تاسو ته نه و ويلي چې بېشكه زه د اسمانونو او ځمكې پر غيبو (پټو څيزونو) پوهېږم، او پوهېږم پر هغه څه چې تاسو يې څرګندوئ او پر هغه څه چې تاسو پټول ');
INSERT INTO "abulsalam" VALUES(2,34,'2:34',' او كله چه مونږ ملايكو ته وويل: ادم ته سجده وكړئ، نو هغوى سجده وكړه خو ابليس نه، هغه انكار وكړ او تكبر يې وكړ، او هغه و له كافرانو نه ');
INSERT INTO "abulsalam" VALUES(2,35,'2:35','او مونږ وويل: اى ادمه! اوسېږه ته او ستا ښځه په جنت كې، او دواړه له هغه (د جنت له مېوو) نه پرېمانه (په مزې سره) خوراك كوئ كوم ځاى چې مو خوښه وي، او تاسو دواړه دې ونې ته مه نژدې كېږئ، ګنې تاسو به له ظالمانو نه شئ ');
INSERT INTO "abulsalam" VALUES(2,36,'2:36','پس شيطان دوى دواړه د دغې (ونې) په سبب، وښُويَوَل، نو له هغه (ځاى) نه يې راووېستل چې دوى په كې وو، او مونږ وويل: تاسو ټول كوز شئ، ستاسو ځينې به د ځينو نورو لپاره دښمنان وي، او ستاسو لپاره په ځمكه كې تر يو وخته پورې استوګنه او فايده اخيستل دي ');
INSERT INTO "abulsalam" VALUES(2,37,'2:37','نو ادم له خپل رب نه څو كلمې زده كړې، نو د هغه توبه يې قبوله كړه، بېشكه هم هغه ښه توبه قبلوونكى، بې حده رحم كوونكى دى ');
INSERT INTO "abulsalam" VALUES(2,38,'2:38','مونږ وويل: تاسو ټول له دې(جنت) نه كوز شئ، نو كه زما له طرف نه تاسو ته كوم هدايت راغى، نو چا چې زما د هدايت پىروي وكړه، نو پر هغوى باندې نه هېڅ وېره شته او نه به هغوى غمژن كېږي ');
INSERT INTO "abulsalam" VALUES(2,39,'2:39','او هغه خلق چې كفر يې كړى دى او زمونږ ايتونه يې دروغ ګڼلي دي، همدغه كسان د (دوزخ د) اور والا دي، دوى به په هغه كې همېشه وي ');
INSERT INTO "abulsalam" VALUES(2,40,'2:40','اى بني اسرائيلو! زما هغه نعمتونه ياد كړئ چې ما پر تاسو باندې پېرزو كړي دي او زما لوظ پوره كړئ، زه به ستاسو لوظ پوره كړم، او خاص له ما نه وېرېږئ ');
INSERT INTO "abulsalam" VALUES(2,41,'2:41','او تاسو ايمان راوړئ پر هغه (كتاب) چې ما نازل كړى دى، چې د هغه (كتاب) تصدىق كوونكى دى چې له تاسو سره دى، او په ده سره اول كفر كوونكي مه جوړېږئ، او زما په ايتونو سره لږ قيمت مه اخلئ او خاص له ما نه وېرېږئ ');
INSERT INTO "abulsalam" VALUES(2,42,'2:42','او تاسو حق له باطل سره مه ګډوئ، او حق مه پټوئ، حال دا چې تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(2,43,'2:43',' او تاسو لمونځ قايموئ او زكات وركوئ او له ركوع كوونكو سره ركوع كوئ ');
INSERT INTO "abulsalam" VALUES(2,44,'2:44','ايا تاسو (نورو) خلقو ته د نېكۍ حكم كوئ او خپل ځانونه هېروئ، سره له دې چې تاسو كتاب لولئ، ايا نو تاسو عقل نه لرئ؟ ');
INSERT INTO "abulsalam" VALUES(2,45,'2:45','او په صبر او لمانځه سره مدد غواړئ او بېشكه چې يقينًا دا (كار) ډېر ګران دى مګر پر عاجزي كوونكو باندې (نه دى) ');
INSERT INTO "abulsalam" VALUES(2,46,'2:46','هغه كسان چې يقين كوي چې بېشكه دوى له خپل رب سره ملاقات كوونكي دي او دا چې بېشكه دوى خاص د هغه په طرف واپس ورتلونكي دي ');
INSERT INTO "abulsalam" VALUES(2,47,'2:47','اى بني اسرائيلو! زما هغه نعمتونه ياد كړئ چې ما پر تاسو باندې انعام كړي دي او دا چې يقينًا هم مَا تاسو پر عالَمونو باندې غوره كړي وئ ');
INSERT INTO "abulsalam" VALUES(2,48,'2:48','او له هغې ورځې نه ووېرېږئ چې هېڅ يو نفس به د بل نفس هېڅ په كار نه راځي او نه به له هغه نه څه سفارش قبلېږي او نه به له هغه نه څه (مالي) بدله اخيستل كېږي او نه به له هغوى سره مدد كېږي ');
INSERT INTO "abulsalam" VALUES(2,49,'2:49','او كله چې مونږ له فرعونيانو نه تاسو خلاص كړئ چې تاسو ته به يې سخت عذاب دركاوه، ستاسو زامن به يې حلالول او ستاسو ښځې (لوڼه) به يې ژوندۍ پرېښودلې، او په دې كې ستاسو د رب له طرف نه ډېر لوى ازمېښت و ');
INSERT INTO "abulsalam" VALUES(2,50,'2:50','او كله چې مونږ ستاسو لپاره سمندر څيرې كړ او تاسو ته مو خلاصى دركړ او فرعونيان مو ډوب كړل او تاسو كتل ');
INSERT INTO "abulsalam" VALUES(2,51,'2:51','او كله چې مونږ له موسٰى سره د څلوېښتو شپو وعده وكړه، بيا تاسو له هغه نه پس سخى (معبود) جوړكړ او تاسو ظالمان وَئ');
INSERT INTO "abulsalam" VALUES(2,52,'2:52','بيا مونږ له دې نه پس تاسو معاف كړئ، د دې لپاره چې تاسو شكر وباسئ ');
INSERT INTO "abulsalam" VALUES(2,53,'2:53','او كله چې مونږ موسٰى ته كتاب او فرقان (د حق او باطل سره بېلوونكى) وركړ، د دې لپاره چې تاسو نېغه لار ومومئ ');
INSERT INTO "abulsalam" VALUES(2,54,'2:54','او (ياد كړه) هغه وخت چې موسٰى خپل قوم ته وويل: اى زما قومه! يقينًا تاسو د سخي په (معبود) نيولوسره په خپلو ځانونو باندې ظلم كړى دى، لهٰذا خپل خالق ته توبه وباسئ، نو خپل ځانونه قتل كړئ، دا (وژل) تاسو لپاره ستاسو د خالق په نيز بهتر دي، بيا هغه ستاسو توبه قبوله كړه، يقينًا هم هغه ښه توبه قبلوونكى، بې حده رحم كوونكى دى ');
INSERT INTO "abulsalam" VALUES(2,55,'2:55',' او كله چې تاسو وويل: اى موسٰى! مونږ به هېڅ كله هم پر تا ايمان را نه وړو تر هغه پورې چې مونږ الله ښه ښكاره ووينو، نو تاسو تندر ونيولئ او تاسوكتل ');
INSERT INTO "abulsalam" VALUES(2,56,'2:56','بيا مونږ تاسو ستاسو له مرګ نه پس راژوندي كړئ، د دې لپاره چې تاسو شكر وباسئ ');
INSERT INTO "abulsalam" VALUES(2,57,'2:57','او مونږ پر تاسو ورېځ سيورى كړه او مَن او سلوٰى (ترنجبين او مړز ډوله مرغان) مو پر تاسو نازل كړل، هغه پاكيزه څيزونه خورئ، چې مونږ تاسو ته دركړي دي، او هغوى پر مونږ ظلم نه و كړى، او لېكن هم پر خپلو ځانونو يې ظلم كاوه ');
INSERT INTO "abulsalam" VALUES(2,58,'2:58','او كله چې مونږ وويل: دې كلي ته ورننوځئ، نو چرته چې مو خوښه شي، ترې پرېمانه يې خورئ او په دروازه كې داخل شئ، چې سجده كوونكي يئ او وايئ ’’مونږ وبخښه‘‘ مونږ به تاسو ته ستاسو ګناهونه وبخښو، او ژر ده چې مونږ به نېكانو ته (ثواب) نور زيات وركړو ');
INSERT INTO "abulsalam" VALUES(2,59,'2:59','نو هغو كسانو چې ظلم يې كړى و، هغه خبره چې ورته ويل شوې وه، په بل شان بدله كړه، نو په هغو ظالمانو باندې مونږه له اسمان نه عذاب نازل كړ، ځكه چې نافرماني يې كوله ');
INSERT INTO "abulsalam" VALUES(2,60,'2:60','او كله چې موسٰى د خپل قوم لپاره اوبه وغوښتې، نو مونږ وويل: په خپلې امسا سره تيږه (ګټه) ووهه، نو له هغې نه دُولس چينې جارې شوې، يقينًا ټولو خلقو خپل خپل ګودر (د څښاك ځاى) وپېژنده، د الله له رزق نه خورئ او څښئ، او په ځمكه كې له حده مه تېرېږئ، چې ورانى كوونكي اوسئ ');
INSERT INTO "abulsalam" VALUES(2,61,'2:61','او كله چې تاسو وويل: اى موسٰى! مونږ به په يو خوراك باندې كله هم صبر ونه كړى شو، لهذا زمونږ لپاره له خپل رب نه دُعا وغواړه چې زمونږ لپاره هغه څيزونه راوباسي چې ځمكه يې زرغونوي (لكه) د هغې سبزي (ساګ) بادرنګ، هوږه، مسور او پياز، هغه وويل: ايا تاسو په بدل كې هغه څيز اخيستل غواړئ چې هغه ادنٰى دى د هغه څيز په ځاى چې هغه بهتر دى، نو تاسو كوم ښار ته كوز شئ، پس بېشكه ستاسو لپاره هغه څه دي چې تاسو غوښتي دي، او پر هغوى ذلت او محتاجي مسلط كړى شوه، او راستانه (اخته) شول په غضب سره د الله له طرف نه، دا په دې سبب چې يقينًا دوى د الله له ايتونو نه انكار كاوه، او نبيان يې ناحقه وژل، دا په دې چې دوى نافرماني كوله، او زياتى يې كاوه ');
INSERT INTO "abulsalam" VALUES(2,62,'2:62','يقينًا هغه كسان چې ايمان يې راوړى دى، او هغه كسان چې يهوديان شوي دي، او نصرانيان او صابيان (بې دينه) دي، هر هغه چا چې پر الله او پر ورځ د اخرت ايمان راوړ او نېك عمل يې وكړ، نو د خپل رب په نيز د دوى لپاره اجر دى او نه په دوى باندې څه وېره شته او نه به دوى غمژن كېږي ');
INSERT INTO "abulsalam" VALUES(2,63,'2:63','او كله چې مونږ (له تاسو نه) ستاسو پخه وعده واخيسته او راپورته مو كړ ستاسو له پاسه (غر د) طُور (او درته ومو ويل:) كلك ونيسئ هغه څه چې مونږ تاسو ته دركړي دي؛ او څه چې په ده كې دي، (هغه) ياد كړئ، د دې لپاره چې تاسو پرهېزګاره شئ ');
INSERT INTO "abulsalam" VALUES(2,64,'2:64','بيا له دې نه بعد تاسو (له وعدې نه) وګرځېدئ، نو كه پر تاسو باندې د الله فضل او د هغه رحمت نه وى، نو تاسو به خامخا تاوانيان شوي وى ');
INSERT INTO "abulsalam" VALUES(2,65,'2:65','او يقينًا يقينًا هغه خلق تاسو ته معلوم دي، چې له تاسو ځنې يې د خالي د ورځې په هكله زياتى كړى و، نو مونږ هغوى ته وويل: تاسو ذليله بيزوګان (شادوګان) شئ ');
INSERT INTO "abulsalam" VALUES(2,66,'2:66','نو مونږه دا (واقعه) هغې ته د حاضرو (خلقو) او له هغې نه وروسته راتلونكو لپاره عبرت او د پرهېزګارو لپاره نصيحت وګرځوله ');
INSERT INTO "abulsalam" VALUES(2,67,'2:67','او كله چې موسٰى خپل قوم ته وويل: بېشكه الله تاسو ته حكم كوي چې يوه غوا حلاله كړئ، هغوى وويل: ايا ته مونږ مسخره ګرځوې (په مونږ پورې ټوقې كوې)؟ هغه وويل: زه د الله پناه غواړم له دې نه چې زه شم له جاهلانو نه ');
INSERT INTO "abulsalam" VALUES(2,68,'2:68','هغوى وويل: زمونږ لپاره له خپل رب نه دُعا وغواړه چې مونږ لپاره واضحه كړي چې هغه (غوا) څنګه ده؟ هغه وويل: يقينًا هغه وايي چې بېشكه هغه يوه داسې غوا ده چې نه زَړه ده او نه نوې ځوانه، (بلكې) د دې دواړو په مينځ كې مِيانَه حاله ده، نو هغه كار وكړئ چې تاسو ته يې حكم كېږي ');
INSERT INTO "abulsalam" VALUES(2,69,'2:69','هغوى وويل: زمونږ لپاره له خپل رب نه دُعا وغواړه چې مونږ ته بيان كړي چې د هغې رنګ څنګه دى؟ هغه وويل: يقينًا هغه وايي چې بېشكه هغه يوه زېړه غوا ده، رنګ يې تك زېړ دى، كتونكي خوشحالوي ');
INSERT INTO "abulsalam" VALUES(2,70,'2:70','هغوى وويل: زمونږ لپاره له خپل رب نه دُعا وغواړه چې زمونږ لپاره بيان كړي چې هغه څه قسم ده؟ بېشكه چې غواګانې پر مونږه ګَډې وډَې (مشتَبِه) شولې، او كه الله وغواړي، بېشكه مونږ خامخا لارموندونكي يو ');
INSERT INTO "abulsalam" VALUES(2,71,'2:71','(موسٰى) وويل: يقينًا هغه وايي چې بېشكه هغه يوه داسې غُوا ده چې نه كار كوونكې ده، چې ځمكه قُلبه كوي او نه فصل اوبه كوي، روغه رَمَټه ده، هېڅ داغ ټكى په كې نشته، هغوى وويل: اوس دې واضحه خبره راوړه، نو هغه يې حلاله كړه، او دوى نژدې نه وو چې دا كار يې كړى وى ');
INSERT INTO "abulsalam" VALUES(2,72,'2:72','او كله چې تاسو يو كس ووژلو، نو د هغه په باره كې تاسو په خپل مينځ كې په جګړه شوئ، او الله د هغه څه ښكاره كوونكى و چې تاسو پټول ');
INSERT INTO "abulsalam" VALUES(2,73,'2:73','نو مونږ وويل: دا د ده په څه حصې سره ووهئ، الله همداسې مړي ژوندي كوي او تاسو ته خپلې نښې درښَىِي، د دې لپاره چې تاسو له عقل نه كار واخلئ ');
INSERT INTO "abulsalam" VALUES(2,74,'2:74','بيا له دې نه بعد ستاسو زړونه سخت شول، نو هغه د تيږو په شان دي، يا په سخت والي كې نور هم زيات دي او بېشكه يقينًا ځينې تيږې هغه دي چې له هغو نه وَلِې بهېږي او بې شكه يقينًا ځينې هغه دي چې چوي، نو له هغو نه اوبه راوځي، او خامخا يقينًا ځينې هغه دي چې د الله له وېرې نه رارغړي، او الله ستاسو له عملونو نه كله هم غافل نه دى ');
INSERT INTO "abulsalam" VALUES(2,75,'2:75','ايا نو تاسو تمه كوئ چې دوى به ستاسو (د وينا) له وجې ايمان راوړي، او يقينًا په دوى كې يوه ډله وه چې د الله كلام به يې اورېده، بيا به يې په هغه كې له پوهېدو نه وروسته تحريف كاوه، حال دا چې دوى به پوهېدل ');
INSERT INTO "abulsalam" VALUES(2,76,'2:76','او كله چې دوى له مومنانو سره ملاقات وكړي، نو وايي: مونږ ايمان راوړى دى او كله چې د دوى ځينې له ځينو نورو سره يواځې شي (بيا) وايي: ايا تاسو دوى (مومنانو ) ته هغه خبرې بيانوئ چې الله تاسو ته ښودلي دي؟ د دې لپاره چې پر تاسو باندې په دغو (خبرو) سره د خپل رب په وړاندې غالبه شي، ايا نو تاسو عقل نه لرئ! ');
INSERT INTO "abulsalam" VALUES(2,77,'2:77','ايا دغه (خلق) پر دې نه پوهېږي چې يقينًا الله پر هغو خبرو پوهېږي چې دوى يې پټوي او پر هغو چې دوى يې ظاهروي ');
INSERT INTO "abulsalam" VALUES(2,78,'2:78','او په دوى كې ځينې اَميان دي، په كتاب نه پوهېږي، مګر په تشو بې دليله ارزوګانو، او دوى يواځې تش ګمانونه كوي ');
INSERT INTO "abulsalam" VALUES(2,79,'2:79','نو د هغو كسانو لپاره هلاكت دى چې په خپلو لاسونو (له ځانه) كتاب لىكي، بيا وايي: دا د الله له جانبه دى، د دې لپاره چې د هغه په بدل كې لږ قيمت واخلي، نو د دوى لپاره هلاكت دى په سبب د هغه څه چې د دوى لاسونو ليكلي دي، او د دوى لپاره هلاكت دى په سبب د هغه چې دوى څه عمل كوي ');
INSERT INTO "abulsalam" VALUES(2,80,'2:80','او دوى وايي: له څو په شمار ورځو نه سوا به مونږ ته كله هم اور را ونه رسېږي، ته (ورته) ووايه: ايا تاسو له الله نه څه وعده اخيستې ده؟ (كه داسې وي)، نو الله به كله هم د خپلې وعدې خلاف ونه كړي، بلكې تاسو په الله باندې هغه دروغ تپئ(اړوئ) چې تاسو پرې نه پوهېږئ ');
INSERT INTO "abulsalam" VALUES(2,81,'2:81','ولې نه! چا چې بدي وكړه او پر ده خپله ګناه راچاپېره شوه، نو همدغه كسان د اور والا دي، دوى به په هغه (اور) كې تل ترتله وي ');
INSERT INTO "abulsalam" VALUES(2,82,'2:82','او هغه كسان چې ايمان يې راوړى دى او نېك عملونه يې كړي دي، همدغه كسان د جنت والا دي، دوى به په هغه (جنت) كې تل ترتله وي ');
INSERT INTO "abulsalam" VALUES(2,83,'2:83','او كله چې مونږ د بني اسرائيلو (نه) پخه وعده واخيسته چې عبادت به يواځې او يواځې د الله كوئ، او له مور و پلار سره به ښېګړه كوئ او له خپلوانو او يتيمانو او مسكينانو سره (به هم ښېګړه كوئ) او خلقو ته ښايسته خبرې كوئ، او لمونځ قايموئ او زكات وركوئ، بيا تاسو (له خپلې وعدې نه) وګرځېدئ مګر ډېر لږ كسان (ونه ګرځېدل) او تاسو مخ ګرځونكي يئ ');
INSERT INTO "abulsalam" VALUES(2,84,'2:84','او كله چې مونږ ستاسو (نه) پخه وعده واخيسته چې خپلې وينې به نه تُويَوَئ او له خپلو كورونو نه به خپل ځانونه (يو بل) نه وباسئ، بيا تاسو اقرار وكړ، په دې حال كې چې تاسو ګواه يئ ');
INSERT INTO "abulsalam" VALUES(2,85,'2:85','بيا هم تاسو هغه كسان يئ چې خپل ځانونه وژنئ، او خپله يوه ډله د هغوى له كورونو نه وباسئ، په ګناه او ظلم سره د هغوى په خلاف د يو بل مدد كوئ، او كه چېرې هغوى تاسو ته بنديان راشي، نو د هغوى (دخلاصون لپاره) فديه وركوئ، حال دا چې شان دا دى چې پر تاسو باندې د هغوى وېستل حرام دي، ايا نو د كتاب پر ځينې (حكمونو) ايمان راوړئ او پر ځينو يې كافر كېږئ؟ نو د دغه كس څه سزا ده چې په تاسو كې دا كار كوي مګر په دنيايي ژوند كې ښه رسوايي او د قيامت په ورځ به تر ټولو سخت عذاب ته ورګرځولى شي، او الله له هغه څه نه چې تاسو يې كوئ كله هم غافل نه دى ');
INSERT INTO "abulsalam" VALUES(2,86,'2:86','دا هغه كسان دي چې دنيايي ژوند يې د اخرت په بدل كې اخيستى دى، نو نه به له دوى نه عذاب سپك (كم) كړى شي او نه به له دوى سره مرسته كېږي ');
INSERT INTO "abulsalam" VALUES(2,87,'2:87',' يقينًا يقينًا مونږ موسٰى ته كتاب وركړى و، او له هغه نه بعد مو پرله پسې رسولان لېږلي وو، او مونږه د مريمې زوى عيسٰى ته څرګندې نښې وركړې وې او مونږه هغه په پاك روح سره قوي كړى و، ايا نو كله چې به هم كوم رسول تاسو ته هغه (احكام) راوړل چې ستاسو نفسونو به نه خوښَوَل، تاسو به ډېره لويي وكړه؛ نو (د پېغمبرانو) يوه ډله مو دروغژنه وګڼله او بله ډله به تاسو وژله ');
INSERT INTO "abulsalam" VALUES(2,88,'2:88','دوى ويل: زمونږ زړونه پرده بَند دي، (داسې نه ده) بلكې د دوى د كفر په سبب الله پر دوى لعنت كړى دى، نو هغه (خلق) چې ايمان راوړي ډېر لږ دي ');
INSERT INTO "abulsalam" VALUES(2,89,'2:89','او كله چې دوى ته د الله له جانبه كتاب (قرآن) راغى چې د هغه څه تصدىق كوونكى دى چې له دوى سره دي، او له دې نه مخكې به دوى پر هغو خلقو باندې چې كافر شوي دي، فتح غوښتله، نو كله چې دوى ته هغه (رسول، كتاب) راغى چې دوى پېژنده، نو په هغه باندې كافر شول، نو په كافرانو باندې د الله لعنت دى');
INSERT INTO "abulsalam" VALUES(2,90,'2:90','بد دى هغه شى چې دوى د هغه په بدل كې خپل ځانونه خرڅ كړل، چې په هغه څه باندې كافر شول چې الله نازل كړى دى، د دې كينې (او حسد) له وجې چې (ولې) الله خپل فضل په خپلو بنده ګانو كې پر هغه چا نازلوي چې يې غواړي، نو راوګرځېدل (اخته شول) په يو غضب له پاسه د بل غضب، او د كافرانو لپاره سپكوونكى عذاب دى ');
INSERT INTO "abulsalam" VALUES(2,91,'2:91','او كله چې دوى ته وويل شي: ايمان راوړئ پر هغه (كتاب) چې الله نازل كړى دى، نو دوى وايي: مونږ پر هغه (كتاب) ايمان راوړو چې پر مونږ باندې نازل كړى شوى دى، او چې له هغه (كتاب) نه علاوه دي؛ په هغو (كتابونو) باندې كافر كېږي، حال دا چې هم دغه (قرآن) حق دى، د هغه (كتاب) تصدىق كوونكى دى چې له دوى سره دى، ته (ورته) ووايه چې تاسو پخوا (له دې نه) د الله نبيان ولې وژل؟ كه تاسو مومنان وئ ');
INSERT INTO "abulsalam" VALUES(2,92,'2:92','او يقينًا يقينًا تاسو ته موسٰى څرګند دليلونه راوړي وو، بيا له هغه نه بعد تاسو سخى (معبود) وګرځاوه او تاسو ظالمان يئ ');
INSERT INTO "abulsalam" VALUES(2,93,'2:93','او كله چې مونږه ستاسو (نه) پخه وعده واخيسته او مونږه طُور (غر) ستاسو له پاسه راپورته كړ (مونږ وويل:) څه چې مونږ تاسو ته دركړي دي هغه په قوت سره ونيسئ او واورئ، دوى وويل: مونږ اَورېدلي دي او مونږ نافرماني كړې ده او د دوى د كفر په سبب د دوى په زړونو كې د سخي (مينه) ورڅښول شوې وه، ته (ورته) ووايه: هغه شى بد دى چې ستاسو ايمان تاسو ته د هغه حكم دركوي، كه تاسو مومنان يئ ');
INSERT INTO "abulsalam" VALUES(2,94,'2:94','ته (ورته) ووايه: كه د الله په نيز د اخرت كور له نورو خلقو نه په غير، يواځې هم ستاسو لپاره وي، نو(بيا خو) د مرګ ارزو وكړئ كه تاسو رښتوني يئ');
INSERT INTO "abulsalam" VALUES(2,95,'2:95','او دوى به كله هم د هغه ارزو و نه كړي هېڅكله، په سبب د هغو (عملونو) چې د دوى لاسونو وړاندې لېږلي دي او الله په ظالمانو باندې ښه پوه دى ');
INSERT INTO "abulsalam" VALUES(2,96,'2:96','او خامخا ته به له ټولو خلقو نه زيات په ژوند پسې حرصناك هم دوى مومې، او تر دې چې له مشركانو نه هم(زيات حرصناك)، د دوى (د يهوديانو) هر يو كس دا ارزو كوي چې ارمان! كه زر كاله عمر وركړى شي، او نه دى هغه خَلاصَوُنكى ده لره له عذاب نه، دا چې(دومره) عمر وركړى شي، او الله هغه څه لره ښه ليدونكى دى چې دوى يې كوي ');
INSERT INTO "abulsalam" VALUES(2,97,'2:97','ووايه: هر څوك چې د جبريل دښمن دى، نو يقينًا هغه دا (قرآن) د الله په حكم سره ستا پر زړه نازل كړى دى، چې د هغو (كتابونو) تصدىق كوونكى دى، چې له ده نه مخكې دي، او د مومنانو لپاره هدايت او زېرى دى ');
INSERT INTO "abulsalam" VALUES(2,98,'2:98','هر څوك چې د الله دښمن دى او د هغه د ملايكو او د هغه د رسولانو او د جبرىل او د ميكائيل، نو يقينًا الله د كافرانو دښمن دى ');
INSERT INTO "abulsalam" VALUES(2,99,'2:99','او يقينًا په تحقيق سره مونږ ستا په طرف روښانه ايتونه نازل كړي دي او په هغو باندې يواځې او يواځې فاسقان (بدكاران) كافر كېږي ');
INSERT INTO "abulsalam" VALUES(2,100,'2:100','ايا هر كله چې دوى به وعده وكړه، كومه وعده، نو په دوى كې يوې ډلې به هغه وغورځوله، بلكې د دوى اكثره خلق ايمان نه راوړي ');
INSERT INTO "abulsalam" VALUES(2,101,'2:101','او كله چې دوى ته د الله له جانب نه رسول راغى، چې تصدىق كوونكى دى د هغه څه چې له دوى سره دي، نو له هغو كسانو نه چې كتاب (تورات) ورته وركړى شوى و (اهل كتاب وو) يوې ډلې د الله كتاب د خپلو شاګانو وروسته (داسې) وغورځاوه، ګوياكې دوى نه پوهېږي ');
INSERT INTO "abulsalam" VALUES(2,102,'2:102','او دوى د هغه څه تابع شول چې د سليمان په بادشاهت كې شيطانانو لوستل، او سليمان كفر نه و كړى او لېكن شيطانان كافر شوي وو، خلقو ته به يې جادو (سحر) ښوده او (دوى تابع شول) د هغه څه چې په بابل ښار كې په دوه ملايكو هاروت او ماروت نازل كړى شوى و او دغو دواړو به هيچا ته ښودنه نه كوله، مګر چې ويل به يې: اصل خبره دا ده چې مونږ يَوْ ازمېښت يو، نو ته مه كافر كېږه، نو هغوى به له دغو دواړو نه هغه څه زده كول چې په هغه سره به يې د سړي او د هغه د ښځې په مينځ كې جدايي راوستله، او په دې (جادو) سره دوى هيچا ته هم ضرر نه شو رسولى مګر د الله په حكم سره، او دوى به هغه څه زده كول چې دوى ته به يې ضرر رساوه او فايده به يې ورته نه رسوله او يقينًا يقينًا دوى په دې پوه وو چې چا دا (سحر) واخيسته، نو د هغه لپاره په اخرت (جنت) كې هېڅ برخه نشته، او يقينًا بد دى هغه څيز چې د هغه په بدل كې دوى خپل ځانونه خرڅ كړل، كه چېرې دوى پوهېدلى ');
INSERT INTO "abulsalam" VALUES(2,103,'2:103','او كه يقينًا دوى ايمان راوړى وى او پرهېزګاره شوي وى، نو خامخا د الله له جانبه بدله ډېره ښه ده، كه دوى پوهېدلى ');
INSERT INTO "abulsalam" VALUES(2,104,'2:104','اى هغو كسانو چې ايمان يې راوړى دى! تاسو’رَاعِنَا‘ (لفظ) مه وايئ او ’اُنْظُرْنَا‘ (مونږ ته وګوره) وايئ او (خبره) اورئ او د كافرانو لپاره ډېر دردوونكى عذاب دى ');
INSERT INTO "abulsalam" VALUES(2,105,'2:105','دا خبره نه هغه كسان خوښوي چې په اهل كتابو كې كافر شوي دي او نه يې مشركان، چې ستاسو د رب له جانبه پر تاسو څه خير نازل كړى شي، او الله په خپل رحمت سره خاص كوي هغه څوك چې يې وغواړي، او الله د عظيم فضل خاوند دى ');
INSERT INTO "abulsalam" VALUES(2,106,'2:106','مونږ چې كوم ايت منسوخ كوو، يا يې (د مومنانو له زړونو نه) هيرَوُو، نو له هغه نه غوره، يا د هغه په شان راوړو، ايا ته پوه نه يې چې يقينًا الله پر هر شي باندې ښه قادر دى ');
INSERT INTO "abulsalam" VALUES(2,107,'2:107','ايا ته پوه نه يې چې يقينًا الله چې دى، د اسمانونو او ځمكې بادشاهي خاص د هغه لپاره ده، او تاسو لپاره له الله نه غير (بل) هېڅ كارساز نشته او نه مددګار ');
INSERT INTO "abulsalam" VALUES(2,108,'2:108','ايا تاسو اراده لرئ چې له خپل رسول نه (داسې) سوال وكړئ لكه څنګه چې له دې نه مخكې له موسٰى نه سوال شوى و؟ او هغه څوك چې د ايمان په بدل كې كفر واخلي، نو يقينًا هغه له سمې لارې نه بې لارې شو ');
INSERT INTO "abulsalam" VALUES(2,109,'2:109','په اهل كتابو كې ډېر خلق د خپلو نفسونو د حسد په وجه، د حق له واضحه كېدو نه بعد (هم)، دا ارزو لري چې له ايمان راوړو نه پس تاسو بيا كافران كړي، نو (لا دوى ته) معافي كوئ او مخ ترې واړوئ، تر هغې چې الله خپل حكم راوړي، بېشكه الله پر هر شي باندې ښه قادر دى ');
INSERT INTO "abulsalam" VALUES(2,110,'2:110','او تاسو لمونځ قايموئ او زكات وركوئ، او هر هغه خير (نېكي) چې تاسو يې د خپلو ځانونو لپاره مخكې لېږئ، هغه به له الله سره ومومئ، بېشكه الله هغه عمل لره ښه ليدونكى دى چې تاسو يې كوئ ');
INSERT INTO "abulsalam" VALUES(2,111,'2:111','او دوى (اهل كتاب) وايي: جنت ته به هيڅكله هم (څوك) داخل نشي، مګر هغه څوك چې يهودي وي، يا نصراني، دا د دوى (باطلې) ارزوګانې دي، ته (ورته) ووايه: (په دې دعوا) خپل دليل راوړئ، كه تاسو رښتوني يئ ');
INSERT INTO "abulsalam" VALUES(2,112,'2:112','ولې نه! چا چې خپل مخ د الله تابع كړو او نېكي كوونكى و، نو د هغه لپاره د خپل رب په نيز اجر دى، او په دوى باندې نه هېڅ وېره شته او نه به دوى غمژن كېږي ');
INSERT INTO "abulsalam" VALUES(2,113,'2:113','او يهوديانو ويل: نصرانيان (د دين) په هېڅ څيز باندې (قايم) نه دي او نصرانيانو ويل: يهوديان (د دين) په هېڅ څيز باندې (ولاړ) نه دي، حال دا چې دوى كتاب لولي، همدارنګه، د دوى د خبرې په شان خبره هغو كسانو هم كړې وه چې بې علمه وو، نو الله به د قيامت په ورځ د دوى په مينځ كې د هغو خبرو په هكله فيصله كوي چې دوى به په كې اختلاف كاوه ');
INSERT INTO "abulsalam" VALUES(2,114,'2:114','او له هغه كس نه بل زيات ظالم څوك دى چې د الله جوماتونه بندوي (له دې خبرې نه) چې په دغو (جوماتونو) كې د هغه (الله) نوم ياد كړى شي او د دغو (جوماتونو) په ورانولو كې كوشش كوي، دغه كسان چې دي، دوى ته دا حق نشته چې دې (جوماتونو) ته ننوځي مګر دا چې وېرېدونكي وي، د دوى لپاره په دنيا كې رسوايي ده او د دوى لپاره په اخرت كې ډېر لوى عذاب دى ');
INSERT INTO "abulsalam" VALUES(2,115,'2:115','او خاص د الله لپاره مشرق او مغرب دى، نو كوم طرف ته چې تاسو مخ كوئ، نو هلته د الله مخ دى، بېشكه الله ډېر فراخۍ والا، ښه پوه دى ');
INSERT INTO "abulsalam" VALUES(2,116,'2:116','او هغوى وويل: الله (د ځان لپاره) اولاد نيولى دى، (داسې نه ده) هغه (له دې خبرو نه) پاك دى، بلكې څه چې په اسمانونو او ځمكه كې دي، هغه (ټول) خاص د هغه لپاره دي، ټول د هغه حكم منونكي دي ');
INSERT INTO "abulsalam" VALUES(2,117,'2:117','بې له نمونې پيدا كوونكى د اسمانونو او ځمكې دى، او كله چې د كوم كار فيصله وكړي، نو همدا خبره ده چې هغه ته ووايي: ’’وشه‘‘ نو هغه وشي ');
INSERT INTO "abulsalam" VALUES(2,118,'2:118','او وويل هغو كسانو چې (په هېڅ) نه پوهېږي: زمونږ سره الله ولې خبرې نه كوي، يا مونږ ته ولې كومه نښه نه راځي؟ همدارنګه د دوى د خبرې په شان خبره هغو كسانو (هم) كړې وه؛ چې له دوى نه مخكې وو، د دوى زړونه له يو بل سره مشابه (يو شان) شوي دي، يقينًا مونږ د هغو خلقو لپاره ايتونه بيان كړي دي چې يقين كوي ');
INSERT INTO "abulsalam" VALUES(2,119,'2:119',' يقينًا (اى نبي!) ته مونږه په حقه سره زېرى وركوونكى او وېروونكى لېږلى يې، او د دوزخيانو په باره كې به له تا نه تپوس نه كېږي ');
INSERT INTO "abulsalam" VALUES(2,120,'2:120','او له تا نه خو به هېڅكله نه يهوديان راضي شى، او نه نصرانيان، تر دې چې ته د دوى د دين پيروي وكړې، ته ووايه: بېشكه د الله هدايت، هُم هغه (اصل) هدايت دى، او يقينًا كه تا، له دې نه پس چې تا ته علم راغى، د هغوى د خواهشاتو پيروي وكړه، (نو) تا لره د الله (له عذاب) نه (خلاصوونكى) نه څوك دوست شته او نه كوم مددګار ');
INSERT INTO "abulsalam" VALUES(2,121,'2:121','هغه كسان چې مونږ هغوى ته كتاب وركړى دى، هغوى د دې (كتاب) تلاوت كوي (څنګه) چې يې د تلاوت حق دى، هم دغه كسان پر ده ايمان راوړي، او څوك چې په دې (كتاب) باندې كافر شه، نو هم دغه كسان تاوانيان دي ');
INSERT INTO "abulsalam" VALUES(2,122,'2:122','اى بني اسرائيلو! زما هغه نعمتونه ياد كړئ چې ما پر تاسو باندې لَورَولي دي او دا چې بېشكه ما تاسو پر عالَمونو باندې غوره كړي وئ ');
INSERT INTO "abulsalam" VALUES(2,123,'2:123','او له هغې ورځې نه ووېرېږئ چې هېڅ يو نفس به د بل نفس هېڅ په كار رانشي، او نه به له هغه نه فديه (بدل) قبلېږي او نه به هغه ته څه سفارش فايده وركوي او نه به له هغوى سره مرسته كېږي ');
INSERT INTO "abulsalam" VALUES(2,124,'2:124','او (ياد كړه) هغه وخت چې خپل رب يې ابراهيم په څو خبرو سره وازمايه، نو (ابراهيم) هغه پوره كړې (الله) وويل: يقينًا زه تا د خلقو لپاره امام جوړوم، هغه وويل: او زما په اولاد كې (هم امامان جوړكړه) (الله) وويل: زما عهد (د امامت وعده) ظالمانو (كافرانو) ته نه رسېږي ');
INSERT INTO "abulsalam" VALUES(2,125,'2:125','او كله چې مونږه بيت الله د خلقو لپاره د جمع كېدو او د امن ځاى وګرځاوه، او (مونږ وويل:) له مقامِ ابراهيم نه ’’لمونځ ځاى‘‘ جوړ كړئ، او مونږ ابراهيم او اسمٰعيل ته كلك حكم وكړ دا چې تاسو (دواړه) زما كور د طواف كوونكو او اعتكاف كوونكو او ركوع كوونكو، سجده كوونكو لپاره ښه پاك كړئ ');
INSERT INTO "abulsalam" VALUES(2,126,'2:126','او كله چې ابراهيم وويل: اى زما ربه! دا (ځاى) امن ناكه ښار جوړ كړه، او د ده اوسېدونكو ته له مېوو نه روزي وركړه، (خو) په دوى كې يواځې هغه چا ته چې پر الله او پر ورځ د اخرت يې ايمان راوړى وي. (الله) وويل: او څوك چې كافر شو، نو هغه ته به لږه فايده وركړم، بيا به هغه په زوره د اور عذاب ته بوځم او هغه د ورتلو بد ځاى دى ');
INSERT INTO "abulsalam" VALUES(2,127,'2:127','او كله چې ابراهيم د بيت الله بنيادونه اوچتول او (ورسره) اسمٰعيل (هم) (دواړو دا دعا كوله) اى زمونږه رَبه! زمونږ نه (دا عمل) قبول كړه، تهٔ چې يې، خاص تهٔ، ښه اورېدونكى، ښه عالم يې ');
INSERT INTO "abulsalam" VALUES(2,128,'2:128','اى زمونږه ربه! مونږ ستا (حكم) منونكي جوړ كړه او زمونږه په اولاد كې (هم) يوه ډله ستا حكم منونكې (جوړه كړه) او مونږ ته زمونږ د عبادت طرىقې وښَيَه او زمونږه توبه قبوله كړه، يقينًا همدا تهٔ ډېر ښه توبه قبلوونكى، بې حده رحم كوونكى يې ');
INSERT INTO "abulsalam" VALUES(2,129,'2:129','اى زمونږه ربه! دې (خلقو) ته له هم دوى نه يو رسول ولېږه، چې دوى ته ستا ايتونه تلاوت كوي او دوى ته كتاب (قرآن) او حكمت (سنت) ښَيي او دوى پاكوي، يقينًا، هم دا ته، ډېر غالب، ښه حكمت والا يې ');
INSERT INTO "abulsalam" VALUES(2,130,'2:130','او څوك دى چې د ابراهيم له ملت نه مخ اړوي، سِوا له هغه كس نه چې خپل ځان يې جاهل كړى وي او يقينًا يقينًا مونږه هغه (ابراهيم) په دنيا كې غوره كړى دى او بېشكه هغه په اخرت كې خامخا له صالحانو ځنې دى ');
INSERT INTO "abulsalam" VALUES(2,131,'2:131','كله چې هغه ته خپل رب وويل: حكم منونكى شه، هغه وويل: زه د رب العلمين حكم منونكى شوم ');
INSERT INTO "abulsalam" VALUES(2,132,'2:132','او ابراهيم خپلو زامنو ته د دې وصيت وكړ او يعقوب (هم، ويې ويل:) اى زما زامنو! بېشكه الله تاسو لپاره دا دين غوره كړى دى، نو بېخي مه مرئ مګر په داسې حال كې چې مسلمانان يئ ');
INSERT INTO "abulsalam" VALUES(2,133,'2:133','ايا تاسو هغه وخت موجود وئ چې يعقوب ته مرګ حاضر شو؟ كله چې هغه خپلو زامنو ته وويل: له ما نه بعد به د څه شي عبادت كوئ؟ هغوى وويل: مونږ به ستا د اِلٰه (معبود) او ستا د پلرونو ابراهيم، او اسمٰعيل او اسحٰق د اِلٰه عبادت كوو، د يو حق معبود، او مونږه (ټول) د هغه حكم منونكي يو ');
INSERT INTO "abulsalam" VALUES(2,134,'2:134','دغه يوه ډله وه چې يقينًا تېره شوې ده، څه (عمل) چې هغوى كړى دى؛ هغه د هغوى لپاره دى او څه چې تاسو كړى دى؛ هغه تاسو لپاره دى، او له تاسو نه به د هغوى د عملونو په باره كې تپوس نه كېږي ');
INSERT INTO "abulsalam" VALUES(2,135,'2:135','او دوى ويل چې تاسو يهوديان شئ، يا نصرانيان، نو نېغه لار به ومومئ، ته (دوى ته) ووايه: (داسې نه ده) بلكې (مونږ) د ابراهيم د ملت (تابع كېږو) چې حنيف (توحيد والا) و او دى له مشركانو څخه نه و ');
INSERT INTO "abulsalam" VALUES(2,136,'2:136','تاسو ووايئ: مونږ پر الله ايمان راوړى دى او پر هغه څه چې مونږ ته نازل كړى شوي دي، او پر هغه څه چې ابراهيم او اسمٰعيل او اسحٰق او يعقوب او (د هغوى) اولادې ته نازل كړى شوي دي، او (مونږ ايمان راوړى دى) پر هغه څه چې موسٰى او عيسٰى ته وركړى شوي دي، او پر هغه څه چې د خپل رب له جانبه (نورو) نبيانو ته وركړى شوي دي، مونږ په دوى كې د هېڅ يو په مينځ كې فرق نه كوو، او مونږ خاص د هغه (الله) حكم منونكي يو ');
INSERT INTO "abulsalam" VALUES(2,137,'2:137','نو كه دوى هغه شان ايمان راوړي څنګه چې تاسو پر دې (كتاب) ايمان راوړى دى، نو يقينًا دوى سمه لاره وموندله او كه مخ يې وګرځاوه، نو همدا خبره ده چې دوى په سخت مخالفت كې دي، نو ژر ده چې د دوى مقابلې ته به تا لپاره الله كافي شي او هم هغه ښه اورېدونكى، ښه پوه دى ');
INSERT INTO "abulsalam" VALUES(2,138,'2:138','(مونږ) د الله رنګ (قبول كړى دى) او له الله نه په رنګ كې څوك ډېر ښه دى! او مونږ خاص د هغهٔ عبادت كوونكي يو ');
INSERT INTO "abulsalam" VALUES(2,139,'2:139','ته (دوى ته) ووايه: ايا تاسو زمونږ سره د الله په باره كې جګړه كوئ؟ حال دا چې هغه زمونږ رب دى او ستاسو (هم) رب دى، زمونږ لپاره زمونږ عملونه دي او تاسو لپاره ستاسو عملونه دي اومونږ خاص د هغه لپاره اخلاص كوونكي يو ');
INSERT INTO "abulsalam" VALUES(2,140,'2:140','ايا تاسو (دا) وايئ چې بېشكه ابراهيم او اسمٰعيل او اسحٰق او يعقوب او (د هغه) اولاده يهوديان، يا نصرانيان وو؟ ته (دوى ته) ووايه: ايا تاسو ډېر پوه يئ، يا كه الله؟ او له هغه كس نه زيات ظالم څوك دى چې هغه ګواهي پټوي چې له ده سره د الله له جانبه ده؟ او الله ستاسو له عملونو نه كله هم غافل نه دى ');
INSERT INTO "abulsalam" VALUES(2,141,'2:141','دغه يوه ډله ده چې يقينًا تېره شوې ده، څه (عمل) چې هغوى كړى دى؛ هغه د هغوى لپاره دى او څه (عمل) چې تاسو كړى دى؛ هغه تاسو لپاره دى او له تاسو نه به د هغوى د عملونو په باره كې تپوس نه كېږي');
INSERT INTO "abulsalam" VALUES(2,142,'2:142',' له خلقو څخه بې وقوفان به ژر ووايي چې دوى له خپلې هغه قبلې نه څه شي واړول چې دوى پر هغې باندې وو؟ ته (ورته) ووایه: خاص د الله لپاره مشرق او مغرب دي، سمې (نېغې) لارې ته هدایت كوي چا ته چې يې وغواړي');
INSERT INTO "abulsalam" VALUES(2,143,'2:143',' او همدارنګه مونږه تاسو غوره امت جوړ كړي یئ، د دې لپاره چې تاسو پر خلقو باندې شاهِدان (ګواهان) شئ، او رسول پر تاسو باندې ګواه شي او هغه قبله چې ته په هغې باندې وې؛ مونږ نه وه مقرر كړې مګر د دې لپاره چې مونږ هغه څوك معلوم (څرګند) كړو چې د رسول پیروي كوي له هغه چا نه چې پر خپلو پوندو باندې بېرته ګرځي، او یقینًا دا (خبره) ډېره درنه ده، مګر پر هغو كسانو چې الله ورته هدایت كړى دى (درنه نه ده) او الله كله هم داسې نه دى چې ستاسو ایمان (لمونځونه) ضايع كړي، یقینًا الله پر خلقو باندې خامخا ډېر نرمي كوونكى، بې حده رحم كوونكى دى ');
INSERT INTO "abulsalam" VALUES(2,144,'2:144',' یقینًا اسمان ته ستا د مخ ګرځېدل (اوړېدل) مونږ وینو، نو مونږ به تا خامخا د هغې قبلې په طرف وګرځوو چې ته يې خوښَوې، نو خپل مخ د مسجد حرام (كعبې) په طرف وګرځوه او تاسو چې په هر ځاى كې یئ، نو خپل مخونه د هغهٔ په طرف وګرځوئ، او بېشكه هغه كسان چه هغو ته كتاب وركړى شوى دى؛ هغوى خامخا پوهېږي چې یقینًا دا (د قبلې بدلول) د دوى د رب له جانبه حق دي، او الله له هغه څه نه چه دوى يې كوي كله هم غافل نه دى');
INSERT INTO "abulsalam" VALUES(2,145,'2:145',' او یقینًا كه ته هغو كسانو ته چې ورته كتاب وركړى شوى دى، هره نښه (هر قسمه دلیل) راوړې ( بیا به هم) هغوى ستا د قبلې تابع نشي او ته (هم) د هغوى د قبلې تابع كېدونكى نه يې، او د دوى بعضې د بعضو نورو د قبلې تابع كېدونكي نه دي، او یقینًا له دې نه پس چې تا ته علم راغى، كه چېرې ته د هغوى د خواهشاتو تابع شوې (نو) یقینًا یقینًا ته به په دغه وخت كې له ظالمانو ځنې يې');
INSERT INTO "abulsalam" VALUES(2,146,'2:146',' هغه كسان چې مونږ هغوى ته كتاب وركړى دى، هغوى دا (محمدصلى الله عليه وسلم، يا قرآن يا اسلام) پېژني لكه چې دوى خپل زامن پېژني، او یقینًا له دوى نه یوه ډله لازمًا حق پټوي، حال دا چې دوى پوهېږي ');
INSERT INTO "abulsalam" VALUES(2,147,'2:147',' حق ستا د رب له جانبه دى، نو كله هم له شك كوونكو نه مه كېږه');
INSERT INTO "abulsalam" VALUES(2,148,'2:148',' او د هر چا لپاره یو جانب دى چې دى هغه ته مخ ګرځوونكى دى، نو نېكیو ته له یو بل نه مخكې كېږئ، په هر ځاى كې چې تاسو یئ الله به تاسو (ټول) په جمع راولي، یقینًا الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(2,149,'2:149',' او له كوم ځاى نه چې ته وځې، نو خپل مخ د مسجد حرام په طرف وګرځوه، او یقینًا همدا ستا د رب له جانبه خامخا حق دي، او الله له هغه څه نه چې تاسو يې كوئ كله هم غافل نه دى');
INSERT INTO "abulsalam" VALUES(2,150,'2:150',' او له كوم ځاى نه چې ته وځې، نو خپل مخ د مسجد حرام (قبلې) په طرف وګرځوه، او تاسو چې په هر ځاى كې یئ، نو خپل مخونه د هغه په طرف وګرځوئ، د دې لپاره چې د خلقو لپاره په تاسو باندې څه حجت (او اعتراض) نه وي، مګر له دوى څخه هغه كسان چې ظلم يې كړى دى، نو له هغو نه مه وېرېږئ، او له ما نه وېرېږئ، او د دې لپاره چې زه خپل نعمتونه پر تاسو باندې پوره كړم او د دې لپاره چې تاسو هدایت ومومئ ');
INSERT INTO "abulsalam" VALUES(2,151,'2:151',' لكه چې مونږ په تاسو كې له هم تاسو نه رسول درلېږلى دى چې پر تاسو باندې زمونږ ایتونه تلاوت كوي، او تاسو پاكوي او تاسو ته كتاب او حكمت (سنت) ښَیي، او تاسو ته د هغه څه تعلیم دركوي چې تاسو پرې نه پوهېدلئ');
INSERT INTO "abulsalam" VALUES(2,152,'2:152',' نو تاسو ما یادوئ زه به تاسو یاد كړم او زما شكر وباسئ او په ما باندې مه كافر كېږئ');
INSERT INTO "abulsalam" VALUES(2,153,'2:153',' اى هغو كسانو چې ایمان يې راوړى دى! په صبر او لمانځه سره مدد غواړئ، یقینًا الله د صبر كوونكو ملګرى دى');
INSERT INTO "abulsalam" VALUES(2,154,'2:154',' او تاسو هغه چا ته مړه مه وایئ چې د الله په لار كې ووژل شي بلكې (هغوى) ژوندي دي او لېكن تاسو (د هغوى په ژوند) نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(2,155,'2:155',' او خامخا مونږ به تاسو ضرور په یو شي سره له وېرې او له لوږې او د مالونو او ځانونو او مېوو په نقصان (كمي) سره ازمایو، او صبر كوونكو ته زېرى وركړه');
INSERT INTO "abulsalam" VALUES(2,156,'2:156',' هغه كسان چې كله دوى ته څه مصیبت ورسي (نو) وايي: یقینًا مونږ د الله یو او بېشكه مونږ خاص هغه ته ورګرځېدونكي یو');
INSERT INTO "abulsalam" VALUES(2,157,'2:157',' دغه خلق، پر هم دوى باندې د خپل رب له جانبه عنایتونه دي او رحمت دى، او همدغه خلق هدایت موندونكي دي');
INSERT INTO "abulsalam" VALUES(2,158,'2:158',' یقینًا صفا او مروه د الله له نښو څخه دي، نو هر څوك چې د بيت الله حج كوي، یا عمره كوي، نو په هغهٔ باندې هېڅ ګناه نشته چې د دې دواړو طواف وكړي، او هر څوك چې په خپله خوښه څه نېكي وكړي، نو الله شكر قبلوونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(2,159,'2:159',' یقینًا كوم خلق چې هغه څه پټوي چې مونږ نازل كړي دي، چې هدایت او ښكاره دلیلونه دي، له دې نه پس چې مونږ هغه په كتاب كې خلقو ته بیان كړي دي، دغه خلق (چې دي)، الله پر دوى لعنت كوي اولعنت ویونكي پر دوى لعنت وايي ');
INSERT INTO "abulsalam" VALUES(2,160,'2:160',' مګر هغه كسان چې توبه وباسي او (د ځان) اصلاح وكړي او (د حق) بیان وكړي، پس دغه كسان، زه د هغوى توبه قبلوم او زه ښه توبه قبلوونكى، بې حده رحم كوونكى یم ');
INSERT INTO "abulsalam" VALUES(2,161,'2:161',' بېشكه هغه كسان چې كفر يې كړى دى او په داسې حال كې مړه شول چې كافران وو، (نو) دغه كسان، په دوى باندې د الله لعنت دى او د ملايكو او د خلقو ټولو (لعنت دى) ');
INSERT INTO "abulsalam" VALUES(2,162,'2:162',' په دې حال كې چې همېشه به وي په دې (لعنت) كې، له دوى نه به نه عذاب سپك كړى شي او نه به دوى ته مهلت وركړى شي');
INSERT INTO "abulsalam" VALUES(2,163,'2:163',' او ستاسو حق معبود یو معبود دى، هېڅ لايق د عبادت نشته مګر يواځې هغه دى چې ډېر زیات مهربان، بې حده رحم كوونكى دى ');
INSERT INTO "abulsalam" VALUES(2,164,'2:164',' یقینًا د اسمانونو او ځمكې په پېدا كولو كې، او د شپې او ورځې په بدلېدو كې، او په هغو بېړیو كې چې په سمندر كې يې هغه څه روان كړي وي چې خلقو ته نفع رسوي، او په هغه څه كې چې الله له بره نه راورَولي دي، چې اوبه دي، نو په دې سره يې ځمكه د هغې د مَړه كېدو (وچېدو) نه پس ژوندۍ كړه، او په دې كې يې هر قسمه خوځنده (شیان) خواره كړل، او د بادونو په ګرځولو (راګرځولو) كې او په هغې ورېځ كې چې د اسمان او ځمكې په مینځ كې مُسَخَّر كړى شوې ده، خامخا د (هغه) قوم لپاره نښې دي چې عقلمند وي');
INSERT INTO "abulsalam" VALUES(2,165,'2:165',' او په خلقو كې ځېنې هغه څوك دي چې له الله نه غیر شریكان نیسي، له هغوى سره مینه كوي له الله سره د مینې په شان، او هغه كسان چې ایمان يې راوړى دى؛ له الله سره په مینه كې ډېر سخت (او كلك) دي، او كه چېری هغه كسان چې ظلم يې كړى دى؛ دا وویني، كله چې دوى عذاب ویني، چې یقینًا قوت ټول خاص د الله لپاره دى او دا چې یقینًا الله ډېر سخت عذاب وركوونكى دى');
INSERT INTO "abulsalam" VALUES(2,166,'2:166',' كله چې هغه كسان چې اتباع يې شوې وه، له هغو كسانو نه بېزاره شي چې اتباع يې كړې وه، او عذاب وویني او پر دوى (ټولې) وسېلې غوڅې شي');
INSERT INTO "abulsalam" VALUES(2,167,'2:167',' او چا چې پیروي كړې ده، هغوى به وايي: ارمان! كه مونږ لپاره (دنیا ته) بیا تلل وي، نو له دوى نه به مونږ بېزاره شو لكه چي دوى له مونږ نه بېزاره شول، همدارنګه به الله دوى ته خپل عملونه په دوى باندې د حسرتونو لپاره ښكاره كوي او دوى له اور نه كله هم وتونكي نه دي');
INSERT INTO "abulsalam" VALUES(2,168,'2:168',' اى خلقو! له هغو څیزونو نه چي په ځمكه كې دي، حلال پاك خورئ او د شیطان د قدمونو پیروي مه كوئ، یقینًا هغه ستاسو ښكاره دښمن دى');
INSERT INTO "abulsalam" VALUES(2,169,'2:169',' یقینًا همدا خبره ده چې هغه تاسو ته د بدۍ او بې حیايۍ حكم كوي او دا چې تاسو په الله باندې هغه خبرې وتړئ (وتپئ) چي تاسو پرې نه پوهېږئ ');
INSERT INTO "abulsalam" VALUES(2,170,'2:170',' او كله چې دوى ته وویل شي: د هغه څه پیروي وكړئ چې الله نازل كړي دي، (نو) وايي: (نه) بلكې مونږه د هغه څه پیروي كوو چې په هغه باندې مونږ خپل پلرونه (مشران) موندلي دي، ایا اګر كه د دوى پلرونه (داسې) وو چې پر هېڅ شي نه پوهېدل، اونه يې سمه لار موندلې وه ');
INSERT INTO "abulsalam" VALUES(2,171,'2:171',' د هغو كسانو مثال چې كفر يې كړى دى؛ د هغه كس په شان دى چې هغو (څارويو) ته نارې وهي چې نه اوري مګر يواځې تشه بلنه او غږ، (دوى) كاڼه دي، ګونګیان دي، ړانده دي، نو دوى هېڅ نه پوهېږي ');
INSERT INTO "abulsalam" VALUES(2,172,'2:172',' اى هغو كسانو چې ایمان يې راوړى دى! له هغو پاكو حلالو څیزونو څخه خورئ چې مونږ تاسو ته دركړي دي، او د الله شكر وباسئ، كه تاسو داسې یئ چې خاص د هغهٔ عبادت كوئ');
INSERT INTO "abulsalam" VALUES(2,173,'2:173',' بېشكه خبره همدا ده چې هغه په تاسو باندې همدا مرداره او وینه او د خنزیر غوښه حرامه كړې ده او هغه څه (يې حرام كړي دي) چې په هغه باندې د غیرالله لپاره اواز اوچت شوى وي، نو هغه څوك چې مجبور كړى شي، په دې حال كې چې نه سركشي كوونكى وي او نه ( له حد نه) تېرېدونكى وي، نو په هغه باندې هېڅ ګناه نشته، یقینًا الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(2,174,'2:174',' یقینًا هغه كسان چې هغه څه پټوي چې الله له (خپل) كتاب نه نازل كړي دي، او د دې په بدل كې لږ قیمت اخلي، (نو) دغه كسان، نه خوري (نه اچوي) په خپلو خېټو كې مګر اور، او د قیامت په ورځ به الله نه له دوى سره خبرې وكړي او نه به دوى پاك كړي او د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(2,175,'2:175',' دا هغه كسان دي چې د هدایت په بدله كې يې ګمراهي اخيستې ده او د مغفرت په بدله كې عذاب، نو (تعجب دى) دوى په اور باندې څومره صبر كوونكي دي!');
INSERT INTO "abulsalam" VALUES(2,176,'2:176',' دا ځكه چې بېشكه الله كتاب په حقه نازل كړى دى او یقینًا هغه كسان چې په كتاب كې يې اختلاف كړى دى؛ خامخا په ډېر لرې مخالفت كې دي');
INSERT INTO "abulsalam" VALUES(2,177,'2:177',' نېكي دا نه ده چې خپل مخونه د مشرق او مغرب په طرف وګرځوئ، او لېكن نېكي (د هغه چا) ده چې پر الله او پر ورځ د اخرت او پر ملايكو او پر كتابونو او پر نبیانو يې ایمان راوړى دى او د هغه په مینه (كې) خپلوانو او یتیمانو او مسكینانو او مسافرو او سوالګرو ته مال وركوي او په (ازادولو د) څټونو كې (يې لګوي) او لمونځ قايموي او زكات وركوي او د خپلو وعدو پوره كوونكي وي كله چې وعده وكړي، (خصوصًا) چې په سختۍ او تكلیف كې او د جنګ په وخت كې صبر كوونكي دي، دا هغه خلق دي چې رښتیني دي او همدغه خلق متقیان دي');
INSERT INTO "abulsalam" VALUES(2,178,'2:178',' اى هغو كسانو چې ایمان يې راوړى دى! پر تاسو باندې د وژل شويو په باره كې (د) قصاص (قانون) فرض كړى شوى دى، ازاد د ازاد په بدل كې، او غلام د غلام په بدل كې، او ښځه د ښځې په بدل كې (قصاصېږي)، پس هغه څوك چې هغه ته د خپل ورور له جانبه د څه شي معافي وكړى شي، نو په ښې طریقې سره مطالبه ( لازم) ده، او هغه (د مقتول ولي) ته په نېكې طرېقې سره ادا كول (لازم) دي، دا ستاسو د رب له جانبه اساني او مهرباني ده، نو له دې نه پس چې چا زیاتى وكړ، نو د هغه لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(2,179,'2:179',' او تاسو لپاره په قصاص كې لوى ژوند دى، اى د خالص عقل خاوندانو! د دې لپاره چې تاسو (ځان) وساتئ');
INSERT INTO "abulsalam" VALUES(2,180,'2:180',' پر تاسو باندې فرض كړى شوي دي د مور و پلار او خپلوانو لپاره په ښې طریقې سره وصیت كول، كله چې په تاسو كې یو كس ته مرګ راشي، په دې شرط چې مال يې پریښى وي، دا پر متقیانو ډېر لازم (حكم) دى');
INSERT INTO "abulsalam" VALUES(2,181,'2:181',' نو چا چې دا (وصیت) له اورېدلو نه پس بدل كړ، نو یقینًا همدا خبره ده چې د دې (بدلولو) ګناه پر هغو كسانو ده چې دا (وصیت) بدلوي، یقینًا الله ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(2,182,'2:182',' بیا چې څوك له وصیت كوونكي نه (په خطا سره) د ظلم، یا (په قصد سره) د ګناه وېره لري، نو د هغوى په مينځ كې يې جوړه وكړه، نو په هغه باندې هېڅ ګناه نشته، یقینًا الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(2,183,'2:183',' اى هغو كسانو چې ایمان يې راوړى دى! پر تاسو باندې روژه فرض كړى شوې ده لكه چې فرض كړى شوې وه پر هغو كسانو چې له تاسو نه مخكې وو، د دې لپاره چې تاسو متقیان شئ');
INSERT INTO "abulsalam" VALUES(2,184,'2:184',' یو څو ورځې شمېرل شوې، نو څوك چې په تاسو كې مریض وي، یا پر سفر وي، نو (په هغه باندې) د نورو ورځو شمېرل دي، او په هغو كسانو باندې چې د دې (روژې) طاقت لري، د یو مسكین خوراك فدیه لازم ده، پس څوك چې په خوښۍ سره نېكي وكړي، نو د ده لپاره هغه بهتره ده، او دا چه روژه ونیسئ (دا) تاسو لپاره ډېر غوره دي، كه چېرې يئ تاسو چې پوهېږئ');
INSERT INTO "abulsalam" VALUES(2,185,'2:185',' د رمضان میاشت هغه ده چې په هغې كې قرآن نازل كړى شوى دى، چې د خلقو لپاره هدایت دى، او د هدایت او د فرقان (د حق او د باطل بېلوونكي) ښكاره دلیلونه دي، نو په تاسو كې چې څوك دې میاشتې كې حاضر و، نو هغه دې د دې روژې ونیسي، او څوك چې مریض وي، یا پر سفر وي، نو (پر هغه) د نورو ورځو شمېرل دي، الله پر تاسو د اسانتیا اراده لري او پر تاسو باندې د سختۍ اراده نه لري او د دې لپاره چې تاسو شمېره پوره كړئ او د دې لپاره چې تاسو د الله لويي بیان كړئ، په دې چې هغه تاسو ته هدایت كړى دى، او د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(2,186,'2:186',' او كله چې زما په باره كې له تا نه زما بنده ګان تپوس وكړي، نو یقینًا زه قریب یم، د دعا كوونكي دعا قبلوم كله چې له ما نه دعا غواړي، پس دوى دې زما حكم ومني، او پر ما دې ایمان راوړي، د دې لپاره چې دوى سمه لار ومومي');
INSERT INTO "abulsalam" VALUES(2,187,'2:187',' تاسو لپاره د روژې په شپو كې له خپلو ښځو سره یو ځاى كېدل حلال كړى شوي دي، هغوى تاسو لپاره لباس (جامې) دي او تاسو د هغوى لپاره لباس (جامې) یئ، الله ته معلوم دي چې یقینًا تاسو له خپلو ځانونو سره خیانت كاوه، نو پر تاسو يې (د مهربانۍ) رجوع وكړه، او تاسو ته يې معافي وكړه، نو اوس له هغوى سره مُباشَرت (كوروالى) كوئ او څه چې الله تاسو لپاره لیكلي دي هغه لټوئ، او خورئ، او څښئ تر هغه چې تاسو ته سپین تار د سبا له تور تار نه ښه ښكاره (او بېل) شي، بیا روژه تر شپې پورې پوره كړئ، او له هغوى سره مباشرت (كوروالى) مه كوئ، په داسی حال كې چې تاسو په جوماتونو كې اعتكاف كوونكي یئ، دا د الله پُولې (حدونه) دي، نو دې ته مه نژدې كېږئ، هم په دې شان الله خپل ایتونه خلقو ته واضحه بیانوي، د دې لپاره چې دوى متقیان شي');
INSERT INTO "abulsalam" VALUES(2,188,'2:188',' او خپل مالونه په خپل مينځ كې په ناحقه سره مه خورئ او حاكمانو ته يې د دې لپاره مه وركوئ چې د خلقو له مالونو نه څه حصه په ظلم سره وخورئ، حال دا چې تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(2,189,'2:189',' دوى له تا نه د نويو میاشتو په باره كې تپوس كوي، ته (ورته) ووایه: دا (میاشتې) د خلقو او د حج لپاره ټاكلي وختونه دي، او (دا) نېكي نه ده چې كورونو ته د هغو له شاګانو نه راشئ، او لېكن نېكي د هغه چا ده چې متقي شي، او تاسو كورونو ته د هغو له دروازو نه راځئ، او له الله نه وېرېږئ، د دې لپاره چې كامیاب شئ');
INSERT INTO "abulsalam" VALUES(2,190,'2:190',' او د الله په لاره كې له هغو كسانو سره جنګ كوئ چې له تاسو سره جنګېږي، او له حد نه مه تېرېږئ، یقینًا الله له حد نه تېرېدونكي نه خوښوي');
INSERT INTO "abulsalam" VALUES(2,191,'2:191',' او دوى وژنئ كوم ځاى چې دوى ومومئ، او دوى له هغه ځاى نه وباسئ چې تاسو يې وېستلي وئ، او فتنه (شرك، له دین نه اړول) له قتل نه ډېره سخته (ګناه) ده او له دوى سره د مسجد حرام په خوا كې جنګ مه كوئ، تر دې چې دوى له تاسو سره په ده كې جنګ شروع كړي، نو كه دوى له تاسو سره جنګ شروع كړي، نو بیا دوى قتل كړئ، د دغو كافرانو سزا همداسې ده');
INSERT INTO "abulsalam" VALUES(2,192,'2:192',' پس كه چېرې دوى منع شول، نو یقینًا الله ښه بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(2,193,'2:193',' او له دوى سره جنګېږئ تر هغه چې فتنه (د كافرانو زور) پاتې نشي، او دین (ټول) د الله لپاره شي، نو كه دوى منع شول، نو هېڅ زیاتى نشته مګر په ظالمانو باندې');
INSERT INTO "abulsalam" VALUES(2,194,'2:194',' حرامه میاشت په بدل د میاشتې حرامې كې ده، او حرمتونه (عزتونه) په بدله دي، پس چا چې پر تاسو باندې تېرى وكړ، نو تاسو په هغه باندې (په جواب كې) تېرى وكړئ په مثل د هغه چې هغه پر تاسو باندې تېرى كړى دى، او له الله نه وېرېږئ او پوه شئ چې یقینًا الله د متقیانو ملګرى دى');
INSERT INTO "abulsalam" VALUES(2,195,'2:195',' او د الله په لاره كې خرڅ كوئ او په خپلو لاسونو هلاكت ته غورځول مه كوئ، او نېكي كوئ، یقینًا الله له نېكي كوونكو سره مینه كوي');
INSERT INTO "abulsalam" VALUES(2,196,'2:196',' او حج او عمره خاص د الله لپاره پوره كړئ، بیا كه چېرې (له حج، یا عمرې نه) ایسار كړى شوئ، نو (بیا لازم ده) هغه هَدي (د حرم قرباني) چې په اسانۍ سره ترلاسه شي او خپل سرونه مه خرَوئ تر هغه چې قرباني خپل (د حلالې) ځاى ته ورسي، پس څوك چې په تاسو كې ناجوړه وي، یا ده ته په خپل سر كې څه تكلیف وي، نو (پر ده) فدیه لازمه ده په روژې سره، یا په صدقې سره، یا په قربانۍ سره، نو كله چې تاسو په امن شئ، پس كه چا فايده واخيسته په عمرې سره حج ته، نو( پر هغه لازمه ده) هغه قرباني چې په اسانۍ سره ترلاسه شي، پس كه چا (قرباني) ونه موندله، نو درې ورځې روژې په حج كې (واجبې) دي او اوه روژې هغه وخت دي چې تاسو راوګرځئ، دا پوره لس (ورځې) دي، دا (حكم د تمتع) د هغه چا لپاره دى چې د هغه ټبر د مسجد حرام اوسېدونكي نه وي، او له الله نه وېرېږئ او پوه شئ چې یقینًا الله سخت عذاب وركوونكى دى');
INSERT INTO "abulsalam" VALUES(2,197,'2:197',' (وخت د احرام د) حج څو معلومې میاشتې دي، نو چا چې په دې (میاشتو) كې (په ځان باندې) حج فرض كړ، نو په حج كې نه له ښځو سره د شهوت خبرې كول شته او نه (د الله) نافرماني او نه جګړه او تاسو چې څه نېكي كوئ، الله پر هغې پوهېږي، او توښه واخلئ، پس یقینًا بهترینه توښه تقویٰ ده، اى د صفا عقل خاوندانو! له ما نه وېرېږئ');
INSERT INTO "abulsalam" VALUES(2,198,'2:198',' پر تاسو باندې په دې كې هېڅ ګناه نشته چی د خپل رب څه فضل ولټوئ، نو كله چې له عرفات نه راكوز شئ، نو په مشعرحرام كې د الله ذكر كوئ، او د هغه ذكر كوئ لكه څنګه چې يې تاسو ته ښودنه كړې ده، او یقینًا تاسو له دې نه مخكې خامخا له ګمراهانو څخه وئ');
INSERT INTO "abulsalam" VALUES(2,199,'2:199',' بیا راكوزېږئ له هغه ځاى نه چې خلق ترې راكوزېږي او له الله نه مغفرت غواړئ، یقینًا الله ښه مغفرت كوونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(2,200,'2:200',' نو كله چې تاسو خپل د حج عبادتونه (اعمال) ادا كړئ، نو الله یادوئ په شان د یادولو ستاسو خپلو پلرونو لره، بلكې زیات یادول، نو په خلقو كې ځینې هغه څوك دي چې وايي: اى زمونږ ربه! ته مونږ ته (هر څه) په دنیا كې راكړه، او د دغه كس لپاره په اخرت كې هېڅ برخه نشته');
INSERT INTO "abulsalam" VALUES(2,201,'2:201',' او په دوى كې بعضې هغه څوك دي چې وايي: اى زمونږ ربه! مونږ ته په دنیا كې نعمت راكړه او په اخرت كې (هم) نعمت راكړه او مونږ د اور له عذابه وساته');
INSERT INTO "abulsalam" VALUES(2,202,'2:202',' دغه كسان دي چې د دوى لپاره د دوى د كړو (نېكو) عملونو په سبب لویه برخه ده، او الله ډېر ژر حساب كوونكى دى');
INSERT INTO "abulsalam" VALUES(2,203,'2:203',' په شمېرل شويو ورځو كې الله یادوئ، پس څوك چې په دوه ورځو كې ژر لاړ، نو پر هغه هېڅ ګناه نشته، او چا چې تاخیر وكړ، نو پر ده (هم) هېڅ ګناه نشته، د هغه چا لپاره چې تقویٰ يې غوره كړې وي او له الله نه وېریږئ، او پر دې پوه شئ چې یقینًا تاسو به خاص هغه ته ورجمع كولى شئ');
INSERT INTO "abulsalam" VALUES(2,204,'2:204',' او بعضې له خلقو نه هغه څوك دي چې د دنيايي ژوند په باره كې د هغه خبرې تا په تعجب كې اچوي او دى په هغه څه باندې الله ګواه كوي چې د ده په زړه كې دي، حال دا چې دى ډېر زیات جګړه مار دى');
INSERT INTO "abulsalam" VALUES(2,205,'2:205',' او كله چې بېرته وګرځي، نو په ځمكه كې ګړندى ګرځي، د دې لپاره چې په دې كې فساد وكړي او فصل او نسل تباه كړي، حال دا چې الله فساد نه خوښوي');
INSERT INTO "abulsalam" VALUES(2,206,'2:206',' او كله چې هغهٔ ته وویل شي: له الله نه ووېرېږه، نو ده لره تكبر ونیسي په ګناه كولو سره، نو د ده لپاره جهنم بس دى او یقینًا هغه بده استوګنه ده');
INSERT INTO "abulsalam" VALUES(2,207,'2:207',' او بعضې له خلقو نه هغه څوك دي چې د الله د رضا لټولو لپاره ځان خرڅوي، او الله په بنده ګانو باندې ډېر نرمي كوونكى دى');
INSERT INTO "abulsalam" VALUES(2,208,'2:208',' اى هغو كسانو چې ایمان يې راوړى دى! په اسلام كې پوره پوره داخل شئ او د شیطان په قدمونو پسې مه ځئ، یقینًا هغه ستاسو ښكاره دښمن دى');
INSERT INTO "abulsalam" VALUES(2,209,'2:209',' بیا كه چېرې تاسو له دې نه پس وښویدلئ؛ چې تاسو ته ښكاره دلیلونه (هم) راغلل، نو پوه شئ چې یقینًا الله ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(2,210,'2:210',' دوى انتظار نه كوي مګر د دې چې الله دوى ته د ورېځو په سیورو كې راشي او ملايك (هم) او معامله فیصله كړى شي، او ټول كارونه خاص الله ته ورګرځول كېږي');
INSERT INTO "abulsalam" VALUES(2,211,'2:211',' له بني اسرائیلو نه تپوس وكړه چې مونږ څومره ډېرې واضحې نښې دوى ته وركړې وې، او څوك چې د الله نعمت بدل كړي، له دې نه پس چې ده ته راغى، نو یقینًا الله سخت عذاب وركوونكى دى');
INSERT INTO "abulsalam" VALUES(2,212,'2:212',' او هغو كسانو ته چې كافران شوي دي دنيايي ژوند ښايسته كړى شوى دى، او دوى په هغو كسانو پورې مسخرې كوي چې ایمان يې راوړى دى، او هغه كسان چې متقیان دي د قیامت په ورځ به د دوى له پاسه وي او الله چې چا لره وغواړي بې حسابه رزق وركوي');
INSERT INTO "abulsalam" VALUES(2,213,'2:213',' ټول خلق (په شروع كې) یو امت وو (بیا په كې ځینې كافر شول) نو الله نبیان راولېږل چې زېرى وركوونكي او وېروونكي وو او له هغوى سره يې په حقه سره كتاب نازل كړ، د دې لپاره چې هغه د خلقو په مينځ كې د هغه شي فیصله وكړي چې دوى په كې اختلاف كړى دى، او په هغه كې اختلاف نه دى كړى مګر هغو كسانو چې هغوى ته هغه (كتاب) وركړى شوى و، په خپل منځ كې د لويي او ظلم په وجه (يې اختلاف كړى و)، له دې نه پس چې هغوى ته ښې څرګندې نښې راغلې، نو الله هغو كسانو ته چې ایمان يې راوړى و، په خپل حكم سره هغه حق ته هدایت وكړ چې هغوى (كافرانو) په كې اختلاف كړى و او الله د سمې لارې هدایت كوي هغه چا ته چې ويې غواړي');
INSERT INTO "abulsalam" VALUES(2,214,'2:214',' ایا تاسو دا ګمان كوئ چې جنت ته به داخل شئ؟ حال دا چې پر تاسو باندې لا تر اوسه د هغو خلقو حالت نه دى راغلى چې له تاسو نه مخكې تېر شوي دي، هغوى ته سختي او بېماري رسېدلې وې، او جړقول شوي وو تر دې چې رسول او له هغه سره ایمان راوړونكو به وویل: د الله مدد به كله راځي؟ خبردار شئ! یقینًا د الله مدد ډېر نژدې دى ');
INSERT INTO "abulsalam" VALUES(2,215,'2:215',' دوى له تا نه پوښتنه كوي چې څه شى خرچ (انفاق) كړي؟ ته (ورته) ووایه: څه چې خرڅ كوئ له مال نه، نو (هغه) د مور و پلار لپاره او نژدې خپلوانو او مسكینانو او مسافرو لپاره دي، او تاسو چې څه نېكي هم كوئ، نو یقینًا الله پر هغې ښه عالم دى ');
INSERT INTO "abulsalam" VALUES(2,216,'2:216',' پر تاسو باندې جنګ كول فرض كړى شوي دي، حال دا چې دا ستاسو بدې شي، او كېدى شي چې تاسو به یو شى بد ګڼئ، حال دا چې هغه به تاسو لپاره ډېر غوره وي او كېدى شي چې تاسو به یو شى خوښوئ، حال دا چې هغه به تاسو لپاره ډېر بد وي او الله پوهېږي او تاسو نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(2,217,'2:217',' دوى له تا نه د حرامو میاشتو په باره كې تپوس كوي، په هغې كې د جنګ كولو ( په باره كې)، ته (ورته) ووایه: په هغې كې جنګ كول لویه ګناه ده او د الله له لارې نه منع كول او په هغه (الله ) سره كافر كېدل او له مسجد حرام نه منع كول او له هغه (مسجد) نه د هغه اهل (حقدار) وېستل د الله په نېز بېخي ډېره لویه ګناه ده، او فتنه (شرك) له قتل نه ډېره لویه ګناه ده، او دوى به همېشه له تاسو سره جنګ كوي تر هغه پورې چې تاسو له خپل دین نه واړوي، كه وس يې وشي، او په تاسو كې چې څوك له خپل دین نه وګرځېده، بیا په داسې حال كې مړ شو چې كافر و، نو دغه كسان (چې دي) د دوى عملونه په دنیا او اخرت (دواړو) كې ضايع شوي دي او دغه كسان د اور ملګري دي، دوى به په هغه كې تل ترتله (همېشه) وي');
INSERT INTO "abulsalam" VALUES(2,218,'2:218',' يقينًا هغه كسان چې ایمان يې راوړى دى او هغه كسان چې هجرت يې كړى دى او جهاد يې كړى دى د الله په لاره كې، دغه كسان د الله د رحمت امېد لري، او الله ډېر بخښونكى، ډېر مهربان دى');
INSERT INTO "abulsalam" VALUES(2,219,'2:219','... دوى له تا نه د شرابو او جوارۍ په باره كې تپوس كوي، ووایه: په دې دواړو كې بېخي لویه ګناه ده، او د خلقو لپاره په كې فايدې دي، او د دې دواړو ګناه ډېره لویه ده، د دې دواړو له فايدو نه او دوى له تا نه پوښتنه كوي چې څه خرچ (انفاق) كړي؟ ته (ورته) ووایه: له نفقې (اخراجاتو) نه زیاتي (څیز خرچ كړئ) الله همداسې تاسو لپاره ایتونه بیانوي، د دې لپاره چې تاسو ښه فكر وكړئ');
INSERT INTO "abulsalam" VALUES(2,220,'2:220',' د دنیا او اخرت په باره كې، او دوى له تا نه د یتیمانو په باره كې پوښتنه كوي، ووایه: د دوى اصلاح كول ډېر ښه دي، او كه تاسو هغوى (له ځان سره) شریك كړئ، نو ستاسو وروڼه دي، او الله پېژني ورانكارى له اصلاح كوونكي نه، او كه الله غوښتلى، نو تاسو به يې خامخا په مشقت كې اچولي وى، یقینًا الله ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(2,221,'2:221',' او له مشركو ښځو سره نكاح مه كوئ تر هغه پورې چې دوى ایمان راوړي، او خامخا مومنه وینځه بېخي غوره ده له ازادې مشركې نه اګر كه هغه تاسو حیران كړي (د خوښېدو په وجه) او مشركانو ته (مسلمانې ښځې) په نكاح مه وركوئ تر هغه پورې چې دوى ایمان راوړي، او خامخا مومن مَرَيـى بېخي غوره دى له ازاد مشرك نه، اګر كه هغه تاسو (د خوښېدو په وجه) حیران كړي، دغه كسان اور ته بلنه كوي، او الله په خپل اذن سره جنت او مغفرت ته بلنه كوي، او خلقو ته خپل ایتونه بیانوي، د دې لپاره چې دوى نصیحت قبول كړي');
INSERT INTO "abulsalam" VALUES(2,222,'2:222',' او دوى له تا نه د حیض په باره كې پوښتنه كوي، ته ووایه: دا یوه پلیتي ده، نو په حیض كې د ښځو له جماع نه په ډَډَه شئ، او دوى ته مه نژدې كېږئ (د جماع لپاره) تر هغه پورې چې پاكې شي، نو كله چې ښې پاكې شي (غسل وكړي)، بیا له دوى سره جماع كوئ، په هغه ځاى كې چې الله تاسو ته حكم كړى دى، یقینًا الله ډېر توبې وېستونكي خوښوي او ښه پاك اوسېدونكي خوښوي');
INSERT INTO "abulsalam" VALUES(2,223,'2:223',' ستاسو ښځې تاسو لپاره فصل دي، نو خپل فصل ته راځئ څنګه چې ستاسو خوښه وي، او د خپلو ځانونو لپاره (نېك عمل) وړاندې ولېږئ، او له الله نه وېرېږئ او پر دې پوه شئ چې یقینًا تاسو له هغه سره ملاقات كوونكي یئ، او مومنانو ته زېرى وركړه');
INSERT INTO "abulsalam" VALUES(2,224,'2:224',' او د خپلو قسمونو لپاره تاسو الله هدف مه ګرځوئ (د الله نوم مه استعمالوئ) چې نه به كوئ نېكي او پرهېزګاري او د خلقو په مينځ كې صلح، او الله ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(2,225,'2:225',' الله تاسو نه نیسي په خپلو قسمونو كې په لغو سره، او لېكن نیسي تاسو پر هغه (قسم) چې ستاسو زړونو يې قصد (نيت) كړى وي، او الله ډېر بخښونكى، ښه تحمل كوونكى دى');
INSERT INTO "abulsalam" VALUES(2,226,'2:226',' د هغو كسانو لپاره چې خپلو ښځو (ته له ورتلو) نه قسم خوري، د څلورو میاشتو مهلت دى، بیا كه دوى رجوع وكړه، نو یقینًا الله ډېر بخښونكى، ډېر مهربان دى');
INSERT INTO "abulsalam" VALUES(2,227,'2:227',' او كه دوى د طلاقو عزم كړى وي، نو یقینًا الله ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(2,228,'2:228',' او طلاقې كړى شوې ښځې دې پر خپلو ځانونو درې حیضه انتظار كوي، او د دوى لپاره حلال نه دي چې پټ كړي هغه څه چې الله د دوى په رَحِمونو كې پیدا كړي دي، كه دوى پر الله او پر ورځ د اخرت ایمان لري او په دې موده كې د دوى خاوندان د دوى د بیا راوستلو اصل حقدار دي كه هغوى د اصلاح (جوړښت) اراده لري، او د دستور مطابق د هغو (ښځو) لپاره (په خاوندانو باندې حقونه) دي په شان د هغو (حقونو) چې پر دغو (ښځو) باندې لازم دي (د خپلو خاوندانو لپاره) او په دوى باندې د سړيو لپاره درجه شته، او الله ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(2,229,'2:229',' طلاق دوه ځله دي، بیا په ښې طریقې سره (ښځې لره) ساتل دي، یا په ډېرې ښې طریقې سره رخصتول دي او (اى سړيو!) تاسو لپاره دا حلال نه دي چې له هغه (مال) نه چې تاسو دوى ته وركړى دى؛ څه لږ شى هم واخلئ، مګر دا چې دواړه له دې نه ووېرېږي چې دوى به د الله حدونه (حكمونه) قايم نه كړي، نو كه تاسو وېرېږئ چې دوى دواړه به د الله حكمونه پوره نه كړي، بیا نو په دې دواړو باندې هېڅ ګناه نشته په هغه (مال) كې چې هغه ښځه يې د جداوالي لپاره وركوي. دا د الله حدونه دي، نو له دغو نه مه تېرېږئ، او څوك چې د الله له حدونو نه تېر شي، نو همدغه كسان ظالمان دي');
INSERT INTO "abulsalam" VALUES(2,230,'2:230',' نو كه بیا يې هغه (په درېم ځلې) طلاقه كړه، نو له دې نه پس هغه د ده لپاره حلاله نه ده تر هغه پورې چې هغه (ښځه له عدت نه پس) له ده نه غېر بل خاوند سره نكاح وكړي، بیا كه هغه (دويم خاوند) دا طلاقه كړه، نو په دې دواړو (ښځه او پخواني خاوند) باندې هېڅ ګناه نشته چې دواړه (له عدت نه پس په نوې نكاح سره) یو بل ته رجوع وكړي، كه دواړه دا خيال كوي چې دوى به د الله حكمونه پوره كړي، او دا د الله حكمونه دي، دا د هغه قوم لپاره بیانوي چې پوهېږي');
INSERT INTO "abulsalam" VALUES(2,231,'2:231',' او كله چې تاسو ښځې طلاقې كړئ، بیا هغوى خپلې (د عدت اخري) مودې ته نژدې شي، نو دوى په ښې طریقې سره ایسارې كړئ (په رجوع كولو سره)، یا هغوى په ښې طریقې سره رخصت كړئ، او د ضرر رسولو لپاره هغوى مه ایساروئ چې زیاتى وكړئ، او چا چې دغه (كارونه) وكړل، نو یقینًا هغه پر ځان ظلم وكړ او د الله ایتونه مسخره مه جوړوئ او پر تاسو باندې (شوي) د الله نعمتونه یاد كړئ او هغه چې الله پر تاسو باندې نازل كړى دى چې كتاب (قرآن) او حكمت (سنت) دى، هغه تاسو ته په دې سره وعظ كوي، او له الله نه ووېرېږئ او پوه شئ چې یقینًا الله په هر شي باندې ښه پوه دى');
INSERT INTO "abulsalam" VALUES(2,232,'2:232',' او كله چې تاسو ښځو ته (رجعي) طلاق وركړئ، بیا هغوى خپلې نېټې ته ورسېږي (او عدت يې پوره شي) نو تاسو دوى مه منع كوئ (له دې نه) چې دوى له خپلو پخوانيو خاوندانو سره (دوباره) نكاح وكړي، كله چې دوى په خپل مينځ كې د دستور مطابق راضي شي، دا (حكم چې دى)، په ده سره هغه چا ته وعظ كولى شي چې په تاسو كې پر الله او پر ورځ د اخرت ایمان لري، دا تاسو لپاره ډېر غوره او ډېر پاكیزه دى، او الله پوهېږي او تاسو نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(2,233,'2:233',' او میندې دې خپل اولاد ته پوره دوه كاله تى وركوي، (دا حكم) د هغه چا لپاره دى چې د تي وركولو موده پوره كول غواړي او د چا لپاره چې بچى دى په هغه باندې د دغو (ښځو) نفقه ده او د دوى جامه ده په ښې طریقې سره، هیڅوك هم مكلف كولى نشي مګر د هغه له وَس سره سم، ضرر دې ونه رسول شي مور ته د هغې د بچي په وجه، او نه د بچي پلار ته د هغه د بچي په وجه او په وارث باندې د دې په مثل لازم دي، پس كه دواړه د خپلمينځي رضامندۍ او مشورې سره له تي نه د پرېكولو اراده وكړي، نو په دې دواړه باندې هېڅ ګناه نشته او كه تاسو خپل اولاد ته مُرضِعه (دايي) نیول غواړئ بیا (هم) پر تاسو باندې هېڅ ګناه نشته، كله چې هغه (مال) سپارئ چې تاسو يې وركول غواړئ، په ښې طریقې سره، او له الله نه ووېرېږئ او پوه شئ چې یقینًا الله هغه څه لره ښه لېدونكى دى چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(2,234,'2:234',' او هغه كسان چې په تاسو كې مړه شي او ښځې پرېږدي، هغه ښځې دې څلور میاشتې او لس ورځې خپل ځانونه په انتظار كې ساتي، بیا چې كله هغوى خپلې نېټې ته ورسېږي (عدت يې پوره شي) نو پر تاسو باندې هېڅ ګناه نشته په هغه كار كې چې دوى يې د خپلو ځانونو په حق كې په ښې طریقې سره كوي، او الله پر هغو عملونو چې تاسو يې كوئ؛ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(2,235,'2:235',' او پر تاسو باندې هېڅ ګناه نشته په هغه (خبره) كې چې په هغې سره تاسو د ښځو د غوښتنې اشاره كوئ، یا په خپلو نفسونو كې يې پټه ساتئ، (له هغو سره نكاح كول) الله پوه دى چې یقینًا ژر دى چې تاسو به دوى یادَوئ، او لېكن له دوى سره په پټه وعده مه كوئ، مګر دا چې مناسبه (تعریضي) خبره وكړئ، او د نكاح تړلو عزم مه كوئ تر هغه پورې چې فرض شوى (عدت) خپلې نېټې ته ورسېږي، او پوه شئ چې یقینًا الله پر هغه څه پوهېږي چې ستاسو په نفسونو كې دي، نو له هغه (الله) نه وېرېږئ، او پوه شئ چې یقینًا الله ډېر بخښونكى، ډېر تحمل كوونكى دى');
INSERT INTO "abulsalam" VALUES(2,236,'2:236',' پر تاسو باندې هېڅ بوج (مهر) نشته كه تاسو ښځو ته طلاق وركړئ څو پورې چې تاسو له هغوى سره كوروالى (جماع) كړى نه وي، یا مو د هغوى لپاره څه مهر مقرر كړى نه وي او دوى ته څه خرڅ، سامان وركړئ، په مالدار باندې د هغه د اندازې مطابق وركول لازم دي او په غریب باندې د هغه د اندازې سره سم لازم دي، له دستور سره سم ضروري سامان وركول، (دا) په نېكي كوونكو باندې لازم دي');
INSERT INTO "abulsalam" VALUES(2,237,'2:237',' او كه تاسو دوى ته طلاق وركړئ مخكې له دې نه چې تاسو له هغوى سره كوروالى وكړئ، په دې حال كې چې تاسو د هغوى لپاره مهر مقرر كړى وي، نو د هغه (مهر) نیم وركول لازم دي چې تاسو مقرر كړى دى مګر دا چې هغوى (ښځې) يې معاف كړي، یا هغه كس يې معاف كړي د چا په لاس كې چې د نكاح غوټه ده (خاوند وركړى مهر ښځې ته پرېږدي)، او ستاسو معاف كول تقویٰ ته ډېر نژدې دي، او په خپل مينځ كې احسان كول مه هېروئ، یقیناً الله هغو عملونو لره چې تاسو يې كوئ، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(2,238,'2:238',' د لمونځونو ساتنه كوئ او (خصوصًا) د بېخې منځني لمونځ، او الله ته ودرېږئ اطاعت كوونكي');
INSERT INTO "abulsalam" VALUES(2,239,'2:239',' بیا كه وېرېدلئ، نو (لمونځ كوئ) پیاده، یا سواره، بیا چې كله په امن شئ، نو الله یادوئ څنګه چې يې تاسو ته هغه څه ښودلي دي چې تاسو (پرې) نه پوهېدئ');
INSERT INTO "abulsalam" VALUES(2,240,'2:240',' او هغه كسان چې په تاسو كې مري او ښځې پرېږدي (نو په دوى باندې لازم دي) وصیت كول د خپلو ښځو لپاره تر یو كاله پورې د خرچ وركولو، بې له وېستلو نه، نو كه دوى (پخپله) ووتلې، نو پر تاسو باندې هېڅ ګناه نشته په هغه څه كې چې دوى (ښځې) يې د خپلو ځانونو په باره كې كوي څه نېك كار، او الله ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(2,241,'2:241',' او د طلاق كړى شویو ښځو لپاره، له عُرف سره سم، سامان وركول په كار دي، په متقیانو باندې (دا) لازم دي');
INSERT INTO "abulsalam" VALUES(2,242,'2:242',' همدارنګه الله تاسو لپاره خپل ایتونه بیانوي، د دې لپاره چې تاسو پوه شئ');
INSERT INTO "abulsalam" VALUES(2,243,'2:243',' ایا تا هغه كسان نه دې لیدلي چې د مرګ له وېرې له خپلو كورونو نه وتلي وو، په دې حال كې چې هغوى زرګونه وو، پس الله دوى ته وویل: مړه شئ، بیا يې هغوى راژوندي كړل، یقینًا الله خامخا په خلقو باندې فضل كولو والا دى او لېكن اكثره خلق شكر نه وباسي');
INSERT INTO "abulsalam" VALUES(2,244,'2:244',' او د الله په لاره كې جنګېږئ، او پوه شئ چې یقینًا الله ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(2,245,'2:245',' څوك دى هغه كس چې الله ته قرض وركوي، قرضِ حسنه، نو هغه (الله ) به د هغه لپاره دا (قرض) زیات كړي په ډېرو زیاتولو سره، او الله تنګي راولي او (هغه) فراخي راولي، او تاسو به خاص هغه ته بېرته ورګرځول كېږئ');
INSERT INTO "abulsalam" VALUES(2,246,'2:246',' ايا تا له موسٰى نه بعد راغليو، د بني اسرائىلو سردارانو ته نه دي كتلي، كله چې هغوى خپل نبي ته وويل: زمونږ لپاره يو بادشاه مقرر كړه چې مونږ د الله په لاره كې جنګ وكړو، هغه نبي وويل: ايا تاسو نژدې يئ كه پر تاسو باندې جنګ كول فرض كړى شي دا چې جنګ به ونه كړئ؟ هغوى وويل: او مونږ لره څه (موانع) دي چې د الله په لاره كې به جنګ نه كوو، حال دا چې مونږ له خپلو كورونو او خپلو زامنو نه وېستل شوي يو، بيا چې كله پر هغوى باندې جنګ كول فرض كړى شول، نو هغوى (له خپلې خبرې نه) وګرځېدل، مګر له هغوى څخه ډېر لږ (خلق ونه ګرځېدل) او الله په ظالمانو باندې ښه پوه دى');
INSERT INTO "abulsalam" VALUES(2,247,'2:247',' او دوى ته خپل نبي وویل: بېشكه الله تاسو لپاره یقینًا طالوت بادشاه مقرر كړى دى، هغوى وویل: د هغه لپاره پر مونږه باندې بادشاهي څنګه كېدى شي، حال دا چې مونږ له هغه نه د بادشاهۍ زیات حقدار یو، او هغه ته د مال فراخي (هم) نه ده وركړى شوې، هغه (نبي) وویل: یقینًا الله هغه پر تاسو باندې غوره كړى دى، او هغه ته يې په جسم او علم كې زیاته فراخي وركړې ده، او الله خپله بادشاهي وركوي چا ته چې يې وغواړي، او الله فراخه فضل والا، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(2,248,'2:248',' او دوى ته خپل نبي وویل: بېشكه د ده د باشاهۍ نښه دا ده چې تاسو ته به صندوق راشي چې په هغه كې ستاسو د رب له جانبه سكینت (د زړه ارام) دى او باقي پاتې شونې له هغو (څیزونو) نه چې الِ موسٰی او الِ هارون پرېښي وو، چې ملايكو به راپورته كړى وي، یقینًا په دې كې تاسو لپاره خامخا لویه نښه ده كه تاسو ایمان لرونكي یئ');
INSERT INTO "abulsalam" VALUES(2,249,'2:249',' نو كله چې طالوت سره د لښكرو راووت، ويې ويل: یقینًا الله په یوې ولِې سره ستاسو ازمايش كوونكى دى، نو څوك چې له هغې نه اوبه وڅښي، نو هغه زما له ملګرو نه نه دى، او څوك چې هغه ونه څكي، نو بېشكه هغه زما له ملګرو څخه دى مګر هغه څوك چې په خپل لاس سره یو چونګ (اوبه) راواخلي، نو هغوى له هغې (ولې) نه اوبه وڅښلې مګر له دوى نه ډېرو لږو (خلقو ونه څښلې) نو كله چې دى (طالوت) او هغه كسان چې له ده سره يې ایمان راوړى و له هغې نه پورې وتل، (مخالفت كوونكو وویل:) نن ورځ زمونږ لپاره د جالوت او د هغه د لښكرو په مقابله كې هېڅ طاقت نشته، او وویل هغو كسانو چې یقین يې لاره چې بېشكه دوى له الله سره ملاقات كوونكي دي: څومره ډېر ځلې ډېره وړه ډله په ډېرې لويې ډلې باندې د الله په حكم سره غالبه شوې ده، او الله د صبر كوونكو ملګرى دى');
INSERT INTO "abulsalam" VALUES(2,250,'2:250',' او كله چې هغوى د جالوت او د هغه د لښكرو د مقابلې لپاره (میدان ته) راووتل، ويې ويل: اى زمونږه ربه! راخوشې كړه پر مونږ باندې صبر، او زمونږ قدمونه ټینګ (كلك) كړه، او د كافر قوم په مقابله كې زمونږ مدد وكړه');
INSERT INTO "abulsalam" VALUES(2,251,'2:251',' نو دوى هغو ته د الله په حكم سره ماتې وركړه، او داود جالوت لره قتل كړو، او الله هغه (داود) ته بادشاهي او حكمت وركړ، او هغه ته يې له هغو (څیزونو) نه ښودنه وكړه چې (الله) غوښتل او كه د الله لرې كول نه وى خلقو لره، د دوى ځېنو لره په ځېنو نورو سره (نو) ځمكه به خامخا ورانه شوې وى او لېكن الله په عالَمونو باندې د لوى فضل كولو والا دى');
INSERT INTO "abulsalam" VALUES(2,252,'2:252',' دا د الله ایتونه دي، مونږ دا په تا باندې په حقه سره لولو او یقینًا ته خامخا له رسولانو ځنې يې');
INSERT INTO "abulsalam" VALUES(2,253,'2:253','دا رسولان (دي)، مونږہ د دوى ځینې په ځینو نورو باندې غوره كړي دي، له دوى نه ځینې هغه دي چې الله ورسره خبرې كړې دي، او د دوى د بعضو يې درجې اوچتې كړي دي، او مونږه د مریمې زوى عیسٰی ته ښكاره معجزې وركړې دي، او مونږ هغه په روح القدس (پاك روح) سره قوي كړى دى، اوكه الله غوښتلى، (نو) هغه كسان به په خپلو كې نه وو جنګېدلي، چې له دغو (رسولانو) نه پس وو، له دې نه وروسته چې دوى ته څرګندې نښې راغلې، او لېكن دوى اختلاف وكړ، نو په دوى كې ځینې هغه دي؛ چې ایمان يې راوړ، او ځینې په دوى كې هغه دي؛ چې كافر شول، او كه الله غوښتلى، (نو) دوى به په خپلو كې نه وو جنګیدلي، او لېكن الله كوي هغه څه چې اراده يې وكړي ');
INSERT INTO "abulsalam" VALUES(2,254,'2:254',' اى هغو كسانو چې ایمان يې راوړى دى! له هغه (مال) نه څه حصه خرچ كوئ چې مونږ تاسو ته دركړى دى، مخكې له دې چې هغه ورځ راشي چې نه په هغې كې اخيستل خرڅول شته، او نه څه دوستانه، او نه څه سفارش، او همدا كافران ظالمان دي');
INSERT INTO "abulsalam" VALUES(2,255,'2:255',' الله (چې دى) نشته هېڅ لايق د عبادت مګر هم دى دى، همېشه ژوندى دى، د ټول عالم انتظام كوونكى دى، پر ده نه پركالي راځي، او نه خوب، خاص هم ده لره دي هغه څه چې په اسمانونو كې دي، او هغه څه چې په ځمكه كې دي، څوك دى هغه كس چې د ده په وړاندې به سفارش كوي مګر د ده په اجازت سره؟ هغه پوهېږي پر هر هغه څه چې د دوى په وړاندې دي او پر هر هغه څه چې د دوى وروسته دي، او دوى (مخلوق) د هغه له علم نه هېڅ شى هم نشي راګېرولى، مګر هغه څه چې هغه (الله) يې وغواړي، د هغه كرسۍ اسمانونه او ځمكه راځای كړي (راګېر) كړي دي او د دې دواړو ساتنه هغه (الله) نه ستړى كوي، او هم هغه ډېر اوچت دى، ډېر لوى دى');
INSERT INTO "abulsalam" VALUES(2,256,'2:256',' په دین (منلو) كې هېڅ جبر نشته، یقینًا حقه لار له ګمراهۍ نه ښه څرګنده (بېله) شوې ده، نو چا چې له طاغوت (شیطان، بت) نه انكار وكړ او پر الله يې ایمان راوړ، نو یقینًا ده ډېره مضبوطه كړۍ ټینګه ونیوله، چې هغې لره هېڅ پرېكېدل نشته، او الله ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(2,257,'2:257',' او الله د هغو كسانو دوست دى چې ایمان يې راوړى دى، هغه دوى لره له تِیارو نه رڼا ته راوباسي، او هغه كسان چې كفر يې كړى دى؛ د هغوى دوستان شیطانان دي، دغه (شیطانان) دوى لره له رڼا نه تیارو ته وباسي، دغه كسان د اور ملګري دي، دوى به په هغه كې همېشه وي');
INSERT INTO "abulsalam" VALUES(2,258,'2:258',' ایا تا هغه كس نه دى لیدلى (د هغه په قصه نه يې خبر شوى) چې له ابراهیم سره يې د ده د رب په باره كې جګړه وكړه، ځكه چې الله هغه ته بادشاهي وركړې وه؟ كله چې ابراهیم وویل: زما رب هغه دى چې ژوندي كول او مړه كول كوي، هغه وویل: زه ژوندي كول او مړه كول كوم، ابراهیم وویل: پس یقینًا الله لمر له مشرق نه راخېژوي، نو ته هغه له مغرب نه راوخېژوه، نو هغه كس چې كفر يې كړى و؛ حیرانه، ملامته شو، او الله داسې ظالم قوم ته سمه لار نه ښیي');
INSERT INTO "abulsalam" VALUES(2,259,'2:259',' یا د هغه كس په شان، چې پر یو كلي باندې تېر شو، او هغه (كلى) په خپلو چتونو باندې راپرېوتى و، هغه وویل: الله به دا (د كلي خلق د دوى) له مرګ نه پس څنګه ژوندي كوي؟ نو الله هغه تر سلو كالو پورې مړ وساته، بیا يې هغه راژوندى كړ، (الله) وویل: څومره موده (داسې) پاتې شوى يې؟ هغه وویل: زه یوه ورځ، یا د ورځې څه حصه (داسې) پاتې شوى یم. هغه (الله) وویل: بلكې ته سل كاله (داسې مړ) پاتې شوى يې، نو ته خپل خوراك او څښاك ته وګوره، چې خراب شوى نه دى او خپل خر ته وګوره، او د دې لپاره چې مونږ تا د خلقو لپاره نښه وګرځوو، او هډوكو ته وګوره، مونږ هغو ته څنګه حركت وركوو، په پورته كولو سره، بیا هغو ته غوښه وراغوندو، نو كله چې ده ته (دا حال) ښه څرګند شو، ويې ويل:زه پوهېږم چې یقینًا الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(2,260,'2:260',' او كله چې ابراهیم وویل: اى زما ربه! په ما باندې ووینه چې ته مړي څنګه ژوندي كوې؟ ويې ويل:ایا ته یقین نه لرې؟ هغه وویل: ولې نه! او لېكن د دې لپاره چې زما زړه مطمئن شي، (الله) وویل: نو له مرغانو نه څلور راونیسه، نو هغه خپلې خوا ته (له بلَدولو نه پس) ټكړې كړه، بیا له هغو ځنې یوه یوه حصه په هر غره باندې كېږده، بیا هغوى راوبله، (نو) دغه به تا ته په منډو راځي، او پوه شه چې یقینًا الله ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(2,261,'2:261',' د هغو كسانو مثال چې د الله په لار كې خپل مالونه لګوي د هغه دانې په شان دى چې اوه وږي زرغون كړي، په هر وږي كې سل دانې وي، او الله (اجر) زیاتوي د چا لپاره چې وغواړي، او الله ډېر فراخۍ والا، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(2,262,'2:262',' هغه كسان چې خپل مالونه د الله په لاره كې لګوي، بیا دوى په خپل انفاق (خیرات) پسې احسان بارول نه كوي او نه ازار رسوي، خاص د دوى لپاره د خپل رب په نیز د دوى اجر دى، او نه به په دوى باندې څه وېره وي اونه به دوى غمژن كېږي');
INSERT INTO "abulsalam" VALUES(2,263,'2:263',' ښه خبره كول، او بخښنه كول، ډېر غوره دي له هغه خیرات نه چې په هغه پسې تكلیف (تنګول) راځي، او الله ډېر بې پروا، ډېر تحمل والا دى');
INSERT INTO "abulsalam" VALUES(2,264,'2:264',' اى هغو كسانو چې ایمان يې راوړى دى! خپل خیراتونه په احسان بارولو (زباتولو) او ضرر رسولو سره مه بربادوئ، د هغه كس په شان چې خلقو ته د ځان ښودنې لپاره خپل مال خرچ كوي، او په الله او د اخرت پر ورځ ایمان نه لري، نو د ده مثال د هغه ښويې ګټې په شان دى؛ چې په هغې باندې څه لږه خاوره وي، بیا په هغې (ګټې) زوروَر باران وورېږي، نو هغه (ګټه) صفا پرېږدي، دوى به له هغو عملونو نه چې كړي يې دي، په هېڅ شي (ثواب) باندې قادره نشي، او الله داسې كافر قوم ته نېغه لار نه ښیي');
INSERT INTO "abulsalam" VALUES(2,265,'2:265',' او د هغو كسانو مثال چې خپل مالونه د الله د رضا غوښتلو لپاره او د خپلو نفسونو د كلكوالي لپاره لګوي، د هغه باغ په شان دى؛ چې په نرمه څَربَه ځمكه باندې وي؛ چې په هغه زورور باران وورېږي، نو دغه (باغ) خپلې مېوې دوه چنده وكړي، نو كه پر هغه زورور باران ونه ورېږي، نو (هغه ته) نرى باران (هم كافي دى) او الله هغو (كارونو) لره ښه لیدونكى دى، چې تاسو يې كوئ ');
INSERT INTO "abulsalam" VALUES(2,266,'2:266',' ایا په تاسو كې یو كس دا خوښَوي چې د ده دې د كجورو، او انګورو داسې باغ وي چې د هغه له لاندې وَلِې بهېږي او د ده لپاره په هغه (باغ) كې هر قسمه مېوې وي، په دې حال كې چې ده ته بُوډاوالى (زوړوالى) راشي او د ده كمزوري (واړه) بچي وي، پس پر دغه(باغ) داسې سخت باد راوالوزي چې په هغه كې اور وي، نو دا (باغ) بېخي وسوَځي، همدارنګه الله تاسو لپاره ایتونه (نښې) بیانوي؛ د دې لپاره چې تاسو ښه فكر وكړئ');
INSERT INTO "abulsalam" VALUES(2,267,'2:267',' اى هغو كسانو چې ایمان يې راوړى دى! له هغو پاكو (مالونو) نه انفاق كوئ چې تاسو ګټلي دي او له هغو (مالونو) نه چې مونږه تاسو لپاره له ځمكې نه را اېستلي دي، او له هغه (مال) څخه چې تاسو يې خیرات كوئ، د بې كاره څیز اراده مه كوئ، حال دا چې تاسو (پخپله) د هغه اخيستونكي نه یئ مګر دا چې د ده په باره كې تاسو سترګې پټې كړئ، او پوه شئ چې یقینًا الله ډېر بې پروا، ډېر ستايل شوى دى');
INSERT INTO "abulsalam" VALUES(2,268,'2:268',' شیطان تاسو له غریبۍ څخه وېروي او تاسو ته د بې حیایۍ حكم كوي او الله تاسو سره له خپل جانب نه د مغفرت او فضل وعده كوي او الله فراخه فضل والا، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(2,269,'2:269',' چا ته چې وغواړي حكمت (د دین پوهه) وركوي، او چا ته چې حكمت وركړى شو، نو یقینًا هغه ته ډېر لوى خیر وركړى شو، او نصیحت نه اخلي مګر د خالص عقل خاوندان');
INSERT INTO "abulsalam" VALUES(2,270,'2:270',' او تاسو چې څه خرچ هم ولګوئ، یا څه نذر هم (پر ځان) لازم كړئ؛ نو یقینًا الله پر هغو پوهېږي او د ظالمانو لپاره هېڅ مدد كوونكي نشته');
INSERT INTO "abulsalam" VALUES(2,271,'2:271',' كه تاسو خیراتونه په ښكاره وكړئ، نو دا یو ښه كار دى او كه تاسو دا په پټه وكړئ او فقیرانو ته يې وركړئ، نو دا ستاسو لپاره ډېر غوره دي او هغه (الله) به له تاسو نه ستاسو ځینې ګناهونه لرې كړي، او الله پر هغو (كارونو) چې تاسو يې كوئ ؛ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(2,272,'2:272',' سمې لارې ته د دوى راړول ستا په ذمه نه دي او لېكن الله سمې لارې ته راړوي چا ته چې وغواړي، او هغه څه چې تاسو يې له مال څخه خرچ كوئ؛ نو ستاسو ځانونو لپاره (يې فايدې) دي او تاسو يواځې د الله د مخ لَټوَنِې (رضا) لپاره انفاق كوئ، او هغه څه چې تاسو يې له مال څخه لګوئ (انفاق كوئ، نو) هغه به تاسو ته پوره پوره دركړى شي، په داسې حال كې چې پر تاسو به ظلم نه كېږي');
INSERT INTO "abulsalam" VALUES(2,273,'2:273',' (خیراتونه) د هغو فقیرو كسانو لپاره دي چې د الله په لاره كې ایسار كړى شوي دي، په ځمكه كې د سفر كولو وَس نه لري، د ښه عفت (له سوال نه د ځان ساتلو) په وجه ناخبره كس پر دوى د غنیانو ګمان كوي، ته به دوى په خپلو علامو (سره) پېژنې، دوى له خلقو څخه په سختۍ سره (بې ضرورته) سوال نه كوي او هغه چې تاسو يې له مال څخه لګوئ، نو یقینًا الله پر هغه باندې ښه عالم دى');
INSERT INTO "abulsalam" VALUES(2,274,'2:274',' هغه كسان چې خپل مالونه (د الله لپاره) لګوي په شپه كې او په ورځ كې، په پټه او په ښكاره، نو د دوى لپاره د خپل رب په نیز د دوى اجر دى، او نه به په دوى باندې څه وېره وي او نه به دوى غمژن كېږي');
INSERT INTO "abulsalam" VALUES(2,275,'2:275',' هغه كسان چې سود خوري ؛ دوى به (له قبرونو نه) نه پاڅیږي مګر په شان د پاڅېدلو د هغه كس چې شیطان هغه په لېونتوب سره بدحواسه كړى وي، دا ځكه چې یقینًا دوى ویلي دي چې بېشكه بیع خو د سود په شان ده، حال دا چې الله اخيستل خرڅول (بیع) حلال كړي دي او سود يې حرام كړى دى، پس چا ته چې د خپل رب له جانبه نصیحت راغى، نو دى (فوراً ) منع شو، نو د ده لپاره هغه څه (معاف) دي چې مخكې تېر شوي دي او د ده معامله الله ته حواله ده، او كوم كس چې (سود ته) بېرته راوګرځي، نو دغه كسان د اور والا دي، دوى به په هغه كې همېشه وي');
INSERT INTO "abulsalam" VALUES(2,276,'2:276',' الله سود محوه كوي او خېراتونه (صدقې) زیاتوي، او الله هېڅ یو سخت ناشكره، سخت ګناهګار نه خوښوي');
INSERT INTO "abulsalam" VALUES(2,277,'2:277',' بېشكه هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، او لمونځ يې قايم كړى دى او زكات يې وركړى دى، د دوى لپاره د خپل رب په نیز د دوى اجر دى، او نه به په دوى باندې څه وېره وي او نه به دوى غمژن كېږي');
INSERT INTO "abulsalam" VALUES(2,278,'2:278',' اى هغو كسانو چې ایمان يې راوړى دى! له الله نه ووېرېږئ او هغه باقي پاتې سود پرېږدئ؛ كه تاسو مومنان یئ');
INSERT INTO "abulsalam" VALUES(2,279,'2:279',' نو كه تاسو ( داسې) ونه كړل، نو تاسو د الله او د هغه د رسول له جانبه په جنګ كولو خبر شئ، او كه چېرې تاسو توبه وباسئ، نو ستاسو رأس المال (اصل مال) ستاسو لپاره دى، مه تاسو ظلم كوئ او نه به پر تاسو ظلم كېږي');
INSERT INTO "abulsalam" VALUES(2,280,'2:280',' او كه هغه (قرض دارى) تنګ لاسى وي، نو تر اسانۍ پورې مهلت وركول دي، او خیراتي پرېښودل يې تاسو لپاره ډېر غوره دي؛ كه تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(2,281,'2:281',' او تاسو له هغې ورځې نه ووېرېږئ چې په هغې كې به تاسو الله ته بېرته ورګرځول كېږئ، بیا به هر نفس ته پوره پوره وركړى شي بدل د هغه عمل چې ده كړى دى، او پر دوى به ظلم ونه كړى شي');
INSERT INTO "abulsalam" VALUES(2,282,'2:282',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې تاسو یو له بله سره تر ټاكلې نېټې پورې په قرضو معامله كوئ، نو هغه ولیكئ او ستاسو په مينځ كې دې لیكونكى په انصاف سره لیكل وكړي، او لیكونكى دې له لیكلو نه انكار نه كوي ؛ لكه څنګه چې الله ده ته ښودنه كړې ده، نو هغه دې لیكل وكړي، او هغه كس دې اِملاء (اقراري عبارت) ووايي ؛ په چا باندې چې حق دى، او له خپل پالونكي الله نه دې ووېرېږي، او له هغه (حق) نه دې هېڅ شى نه كمَوي، بیا كه چېرې هغه كس، په چا باندې چې حق دى؛ ناپوه وي، یا (بدني) كمزورى وي، یا پخپله د اِملاء (عبارت) ویلو طاقت نه لري، نو د هغه وَلي (واكدار) دې په انصاف سره املاء ووايي او تاسو له خپلو (مسلمانانو) سړیو نه دوه ګواهان ګواه ونیسئ، نو كه دوه سړي ګواهان نه وي، نو یو سړى او دوه ښځې(دې ګواه شي) له هغو ګواهانو څخه چې تاسو يې خوښَوئ، د دې لپاره چې كه په دواړو كې له یوې نه هېر شي؛ نو په دوى كې یوه به يې بلې ته وریاد كړي او ګواهان دې انكار نه كوي؛ چې كله هم راوبلل شي او د خپلې نېټې پورې (د هغه قرض) په لیكلو باندې مه تنګېږئ ؛ لږ وي، یا كه ډېر وي، دا د الله په نیز ښه انصاف (والا كار) دى او ګواهۍ لره ښه درست ساتونكى دى او ډېر قریب دى دې ته چې تاسو به شكيان نشئ مګر دا چې دا (معامله) لاس په لاس سودا وي چې تاسو هغه په خپل مينځ كې نغده اخلئ راخلئ، نو پر تاسو باندې څه ګناه نشته چې دا (شان معامله) ونه لیكئ او كله چې په خپل مينځ كې اخيستل خرڅول كوئ (نو) ګواهان ونیسئ او ضرر دې ونه رسول شي لیكونكي ته او نه ګواه ته او كه تاسو (داسې) وكړئ، نو یقینًا دا نافرماني (ګناه) ده چې په تاسو پورې (نښتې) ده او له الله نه ووېرېږئ، او الله تاسو ته ښودنه كوي، او الله په هر شي باندې ښه پوه دى');
INSERT INTO "abulsalam" VALUES(2,283,'2:283',' او كه چېرې تاسو په سفر باندې یئ او كوم لیكونكى مو ونه مونده، نو (جايز ده چې) قبض كړى شوې ګروي (رهن) واخلئ، نو كه ستاسو بعضو په بعضو نورو باندې اعتبار وكړ (او ګروي يې وانه خيستله) بيا نو لازم ده چې امین ګڼل شوى كس دى د هغه (بايع) امانت (حق) ادا كړي، او له خپل پالونكي الله نه دې ووېرېږي، او ګواهي مه پټوئ، او چا چې دا پټه كړه، نو یقینًا د هغه زړه ګناهګار دى، او الله پر هغو (كارونو) چې تاسو يې كوئ؛ ښه پوه دى');
INSERT INTO "abulsalam" VALUES(2,284,'2:284',' خاص د الله لپاره دي؛ څه چې په اسمانونو كې دي او څه چې په ځمكه كې دي او كه تاسو هغه څه چې ستاسو په زړونو كې دي، څرګندوئ، یا هغه پټوئ (نو) الله به له تاسو سره د هغه حساب كوي، بیا به چا ته چې وغواړي بخښنه وكړي او چا ته چې وغواړي عذاب به وركړي او الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(2,285,'2:285',' رسول پر هغه څه ایمان راوړى دى ؛ چې په ده باندې د خپل رب له جانبه نازل كړى شوى دى او مومنانو هم، ټولو ایمان راوړى دى په الله باندې او د هغه پر ملايكو او د هغه پر كتابونو او د هغه په رسولانو باندې (او وايي) مونږه د هغه (الله) په رسولانو كې د هیچا په مینځ كې تفريق نه كوو او وايي: مونږ واورېدل او مونږ خبره ومنله (مونږ) ستا (نه) بخښنه (غواړو) اى زمونږه ربه! او خاص تا ته بېرته درتګ دى');
INSERT INTO "abulsalam" VALUES(2,286,'2:286',' او الله هېڅ یو نفس نه مكلف كوي مګر د هغهٔ له وَس سره سم، د همدهٔ لپاره دي څه نېكۍ چې ده كړي دي او په همدغه (نفس) باندې دي څه (ګناهونه) چې ده كړي دي، اى زمونږه ربه! مونږ مه نیسه كه مونږ (څه حكم) هېر كړو، یا مونږ غلطي وكړو، اى زمونږه ربه! او په مونږ باندې بوج مه ږده؛ لكه څنګه چې تا هغه (بوج) ایښى و په هغو خلقو باندې چې له مونږ نه مخكې وو، اى زمونږه ربه! او پر مونږ باندې داسې څه مه راباروه چې په مونږ كې د هغو (د اوچتولو) هېڅ طاقت نه وي او مونږ ته معافي وكړه او مونږه وبخښه او پر مونږ رحم وكړه، هم ته زمونږ كارساز يې، نو د كافر قوم په خلاف ته زمونږ مدد وكړه');
INSERT INTO "abulsalam" VALUES(3,1,'3:1',' الف، لام، ميم');
INSERT INTO "abulsalam" VALUES(3,2,'3:2',' الله (چې دى) نشته هېڅ لايق د عبادت مګر هم دى دى، همېشه ژوندى دى، د ټول عالَم انتظام كوونكى دى ');
INSERT INTO "abulsalam" VALUES(3,3,'3:3',' هغهٔ په تا باندې په حقه سره كتاب نازل كړى دى چې د هغو (كتابونو) تصدیق كوونكى دى چې له ده نه مخكې وو، او تورات او انجیل يې نازل كړي دي ');
INSERT INTO "abulsalam" VALUES(3,4,'3:4',' له ده نه مخكې، د خلقو د هدایت لپاره او فرقان يې نازل كړى دى، بېشكه هغه كسان چې د الله په ایتونو سره كافر شوي دي، د دوى لپاره ډېر سخت عذاب دى او الله ډېر غالب دى، انتقام والا دى');
INSERT INTO "abulsalam" VALUES(3,5,'3:5',' یقینًا الله (چې دى) له هغه نه هېڅ شى نه دى پټ په ځمكه كې او نه په اسمان كې');
INSERT INTO "abulsalam" VALUES(3,6,'3:6',' دى همغه ذات دى چې په رَحِمونو كې ستاسو شكلونه (صورتونه) جوړوي څنګه چې وغواړي، نشته هېڅ لايق د عبادت مګر هم دى دى، چې ډېر غالبه، ښه حكمت والا دى ');
INSERT INTO "abulsalam" VALUES(3,7,'3:7',' دى هغه ذات دى چې په تا باندې يې كتاب نازل كړى دى، په ده كې ځينې محكم (ښكاره مراد والا) ایتونه دي؛ چې هغه د كتاب اصل دي، نور يې متشابهات (پټ مراد والا) دي، نو هغه كسان چې د هغوى په زړونو كې كوږ والى دى؛ نو هغوى په هغو(ایتونو) پسې كېږي چې په دې (قرآن) كې متشابه (پټ مراد والا) دي، د فتنې لټوَلو لپاره او د هغو د مراد معلومولو لپاره، حال دا چې د هغوى په تاویل (مراد) باندې نه پوهېږي مګر الله، او هغه كسان چې په علم كې پاخه دي؛ هغوى وايي: مونږ پر ده ایمان راوړى دى، ټول (محكم او متشابه ایتونه)زمونږ د رب له جانبه دي، او نصیحت نه اخلي مګر د صفا عقل خاوندان');
INSERT INTO "abulsalam" VALUES(3,8,'3:8',' (او وايي) اى زمونږه ربه! زمونږ زړونه مه كږوه وروسته له دې چې تا مونږ ته سمه لار وښودله او مونږ ته له خپلې خوا نه رحمت راكړه، یقینًا هم ته، (د نعمت او رحمت) بې حده عطا كوونكى يې ');
INSERT INTO "abulsalam" VALUES(3,9,'3:9',' اى زمونږه ربه! بېشكه ته خلقو لره جمع كوونكى يې په داسې ورځ كې چې په هغې كې هېڅ شك نشته، یقینًا الله د وعدې مخالفت نه كوي');
INSERT INTO "abulsalam" VALUES(3,10,'3:10',' بېشكه هغه كسان چې كفر يې كړى دى، هېڅ كله به هم د هغوى مالونه او اولادونه له دوى څخه د الله (له عذاب) نه هېڅ شى لرې نه كړى شي او همدغه كسان د اور خشاك دي ');
INSERT INTO "abulsalam" VALUES(3,11,'3:11',' (حال د دوى داسى دى) لكه حال (او عادت) د فرعونیانو او د هغو كسانو چې له دوى نه مخكې وو، چې زمونږ ایتونه به يې دروغ ګڼل، نو الله هغوى د خپلو ګناهونو په سبب ونیول او الله ډېر سخت عذاب وركوونكى دى ');
INSERT INTO "abulsalam" VALUES(3,12,'3:12',' ته ووایه هغو كسانو ته چې كفر يې كړى دى: ژر دى چې تاسو به مغلوب كړى شئ او جهنم ته به را یوځای (راجمع) كړى شئ او هغه بده استوګنه ده');
INSERT INTO "abulsalam" VALUES(3,13,'3:13',' یقینًا شته تاسو لپاره لویه نښه په هغو دوه ډلو كې چې یو بل ته مخامخ شوې وې، یوه ډله د الله په لار كې جنګېدله او بله كافره وه، دوى به هغوى لره له خپل ځان نه دوه چنده (زیات) لیدل، د سترګو په لیدلو سره، او الله په خپل مدد سره قوت وركوي (مضبوطوي) چا ته چې وغواړي، یقینًا په دې كې د سترګو خاوندانو لپاره خامخا عبرت (اونصیحت) دى');
INSERT INTO "abulsalam" VALUES(3,14,'3:14',' ښايسته كړى شوې ده خلقو ته مینه د مرغوبو خوندَوَرو شیانو چې ښځې دي او زامن دي او راجمع كړى شوي ډېر مالونه دي له سرو زرو نه او سپینو زرو نه او اسونو نه، چې نښه دار كړى شوي دي او له څارويو نه او فصل نه، دا د دنيايي ژوند سامان دى او له الله سره د ورګرځېدلو ښه ځای دى');
INSERT INTO "abulsalam" VALUES(3,15,'3:15',' ته (ورته) ووایه: ایا تاسو ته د داسې شیانو خبر دركړم چې له دغو (دنياوي خوندونو) نه ډېر ښه دي؟ د هغو كسانو لپاره چې پرهېزګاره دي، (هغه) د خپل رب په نیز باغونه دي چې د هغو له لاندې ولې بهېږي، چې په هغو كې به دوى تل ترتله وي او (د دوى لپاره) ښې پاكې ښځې دي او د الله له جانبه رضامندي (ده) او الله بنده ګانو لره ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(3,16,'3:16',' هغه كسان چې وايي: اى زمونږه ربه! یقینًا مونږ ایمان راوړى دى، نو مونږ ته زمونږ ګناهونه وبخښه او مونږ د اور له عذابه وساته ');
INSERT INTO "abulsalam" VALUES(3,17,'3:17',' چې صبركوونكي دي او رښتیا ویونكي دي او حكم منونكي دي او انفاق كوونكي دي او د شپو په اخر كې مغفرت غوښتونكي دي');
INSERT INTO "abulsalam" VALUES(3,18,'3:18',' الله ګواهي وركړې ده د دې خبرې چې یقینًا نشته هېڅ لايق د عبادت مګر هم دى دى او ملايكو او د علم خاوندانو، (هم) په داسې حال كې چې دى په انصاف سره قايم دى، نشته هېڅ لايق د عبادت مګر هم دى دى چې ډېرغالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(3,19,'3:19',' بېشكه (معتبر) دین د الله په نیز اسلام دى، او هغو كسانو، چې كتاب ورته وركړى شوى دى، اختلاف نه دى كړى مګر وروسته له دې چې دوى ته علم راغى، په خپل مینځ كې د ضد او سركشۍ په سبب او هغه كس چې د الله په ایتونو باندې كافر شي، نو یقینًا الله بېخي ژر حساب كوونكى دى');
INSERT INTO "abulsalam" VALUES(3,20,'3:20',' بیا كه چېرې دوى له تاسره جګړه كوي، نو ته ووایه: ما خپل مخ خاص الله ته تابع كړى دى او هغه چا هم (تابع كړى دى) چې زما پېروي كوي او ته هغو كسانو ته ووایه چې هغوى ته كتاب وركړى شوى دى او اَمیانو (نالوستو) ته: ایا تاسو اسلام راوړئ؟ پس كه دوى اسلام راوړ، نو یقینًا دوى سمه لار وموندله او كه وګرځېدل، پس بېشكه په تا باندې يواځې تبلیغ كول (د حق رسَول) دي او الله بنده ګانو لره ښه لیدونكى دى ');
INSERT INTO "abulsalam" VALUES(3,21,'3:21',' بېشكه هغه كسان چې د الله په ایتونو كافران كېږي او نبیان ناحقه وژني او په خلقو كې هغه كسان وژني چې د انصاف كولو حكم كوي، نو هغوى ته د بېخي دردوونكي عذاب زېرى وركړه');
INSERT INTO "abulsalam" VALUES(3,22,'3:22',' دا هغه كسان دي چې د دوى عملونه په دنیا او اخرت (دواړو) كې ضايع شوي دي او نشته د دوى لپاره هیڅوك مدد كوونكي');
INSERT INTO "abulsalam" VALUES(3,23,'3:23',' ایا تا نه دي كتلي هغو كسانو ته چې هغوى ته له كتاب نه څه حصه (د علم) وركړى شوې ده، دوى كتاب ته رابللى شي، د دې لپاره چې (دا كتاب) د دوى په مینځ كې فیصله وكړي، بیا په دوى كې یوه ډله مخ اړوي او دوى (همېشه له حق نه) مخ ګرځوونكي دي');
INSERT INTO "abulsalam" VALUES(3,24,'3:24',' دا په دې سبب چې بېشكه دوى ویلي دي: مونږ ته به اور هېڅ كله هم را ونه رسېږي مګر څو په شمار ورځې او دوى په خپل دین كې هغو خبرو غولولي (دوكه كړي) دي چې دوى به له ځانه جوړولې');
INSERT INTO "abulsalam" VALUES(3,25,'3:25',' نو څه حال به وي كله چې مونږ دوى د هغې ورځې (د فیصلې) لپاره راجمع كړو چې په هغې كې هېڅ شك نشته او هر نفس ته به (جزا د) هغه څه پوره پوره وركړى شي چې ده كړي دي او پر دوى به ظلم ونه كړى شي');
INSERT INTO "abulsalam" VALUES(3,26,'3:26',' ته ووایه: اى الله!د بادشاهۍ مالكه! ته بادشاهي وركوې هغه چا ته چې وغواړې او بادشاهي بېرته اخلې له هغه چا نه چې وغواړې، چا ته چې وغواړې عزت وركوې او چا ته چې وغواړې ذلت وركوې، هم ستا په لاس كې خیر دى، بېشكه ته په هر شي باندې ښه قادر يې');
INSERT INTO "abulsalam" VALUES(3,27,'3:27',' ته شپه په ورځ كې نَنَباسې او ورځ په شپه كې نَنَباسې او ته ژوندى له مړي راوباسې او مړى له ژوندي راوباسې او چا ته چې وغواړې بې حسابه روزي وركوې');
INSERT INTO "abulsalam" VALUES(3,28,'3:28',' او مومنان دې له (خپلو) مومنانو نه غیر كافرانو لره دوستان نه جوړوي، او څوك چې دا كار وكړي، نو نه دى (دغه كس) د الله (له ډلې) نه په هېڅ شي كې، مګر دا چې تاسو له دغو (كافرو) نه ځان ساتل غواړئ، ځان ساتنه، او الله تاسو له خپل ځان نه وېروي او خاص الله ته ورتلل دي');
INSERT INTO "abulsalam" VALUES(3,29,'3:29',' ته ووایه: كه تاسو پټ ساتئ هغه څه چې ستاسو په سینو كې دي، یا هغه څرګندوئ، الله ته هغه معلوم دي، دى پوهېږي پر هر هغه څه چې په اسمانونو كې دي او پر هر هغه څه چې په ځمكه كې دي او الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(3,30,'3:30',' په هغه ورځ كې چې هر نفس به هره هغه نېكي چې ده كړې وي، (خپلې مخې ته) حاضره ومومي او هره هغه بدي چې ده كړې ده (هغه هم)، دا (نفس) به ارزو كوي چې كاشكې د ده په مینځ كې او د هغې (بدۍ) په مینځ كې ډېر لرې مسافت وى او الله تاسو له خپل ځان نه وېروي او الله په بنده ګانو باندې ډېر نرمي كوونكى دى');
INSERT INTO "abulsalam" VALUES(3,31,'3:31',' (اى نبي!) ته ووایه: كه چېرې یئ تاسو چې له الله سره مینه كوئ، بیا نو زما اتباع وكړئ، الله به له تاسو سره مینه وكړي او تاسو ته به ستاسو ګناهونه وبخښي او الله ډېر بخښونكى، بې حده رحم كوونكى دى ');
INSERT INTO "abulsalam" VALUES(3,32,'3:32',' (اى نبي!) ته ووایه: د الله اطاعت كوئ او د (هغه د) رسول، بیا كه دوى وګرځېدل، نو بېشكه الله له كافرانو سره مینه نه كوي ');
INSERT INTO "abulsalam" VALUES(3,33,'3:33',' بېشكه الله په عالَمونو باندې ادم او نوح او الِ ابراهیم او الِ عمران غوره كړي دي');
INSERT INTO "abulsalam" VALUES(3,34,'3:34',' په دې حال كې چې (دا داسې) نسل دى، چې ځینې د دوى له ځینو نورو دي او الله ښه اورېدونكى، ښه پوه دى ');
INSERT INTO "abulsalam" VALUES(3,35,'3:35',' كله چې د عمران ښځې وویل: اى زما ربه! بېشكه ما تا لپاره نذر منلى دى د هغه چې زما په ګېډه كې دى، په دې حال كې چې ازاد به وي، نو ته يې له ما نه قبول كړه، بېشكه هم ته ښه اورېدونكى، ښه پوه يې');
INSERT INTO "abulsalam" VALUES(3,36,'3:36',' نو كله چې دې هغه وزېږوله، دې وويل! اى زما ربه! بېشكه زه چې یم، ما هغه وزېږوله، چې ښځه (جينۍ) ده او الله ښه پوه دى پر هغه څه چې دې وزېږوه او نر (هلك) د ښځې (جینۍ) په شان نه دى او بېشكه ما دې ته مریم نوم كېښود، او بېشكه زه دا او د دې اولاد له شیطان رټل شوي نه ستا په حفاظت كې دركوم ');
INSERT INTO "abulsalam" VALUES(3,37,'3:37',' نو هغه د هغې رب په ډېرو ښو قبلولو سره قبوله كړه او هغه يې په ډېرو ښُكلو زرغونولو (لویَوَلو) سره زرغونه (لویه) كړه او هغه يې د زكریا په كفالت كې وركړه كله چې به هم زكریا دې ته عبادت خانې ته ننوت (نو) له هغې سره به يې څه رزق (خوراك) ومونده، هغه وویل: اى مریمې! تا ته دا له كومه كېږي؟ هغې وویل: داد الله له خوا راځي، یقینًا الله چې چا ته وغواړي بې حسابه روزي وركوي');
INSERT INTO "abulsalam" VALUES(3,38,'3:38',' په دغه ځای كې زكریا له خپل رب نه دعا وغوښته، ويې ويل:اى زما ربه! ما ته له خپلې خوا نه ښه پاكیزه اولاد راكړه، یقینًا ته د دعا ښه اورېدونكى يې');
INSERT INTO "abulsalam" VALUES(3,39,'3:39',' نو ملايكو ده ته اواز وكړ، په داسې حال كې چې دى په عبادت خانه كې ولاړ و، لمونځ يې كاوه، دا چې بېشكه الله تا ته د یحیٰی زېرى دركوي، چې تصدیق كوونكى به وي د هغې كلمې چې د الله له طرفه ده، او سردار به وي او ځان به ډېر منع كوونكى وي (له خواهشاتو نه) او نبي به وي له صالحانو نه');
INSERT INTO "abulsalam" VALUES(3,40,'3:40',' ويې ويل:اى زما ربه! ما لره به هلك څنګه كېږي، حال دا چې په ما باندې بوډا والى (زوړ والى) راغلى دى او زما ښځه شنډه ده؟! هغه وویل: همداسې الله كوي څه چې وغواړي');
INSERT INTO "abulsalam" VALUES(3,41,'3:41',' ويې ويل: اى زما ربه! زما لپاره څه نښه مقرر كړه، هغه وویل: ستا نښه دا ده چې ته به له خلقو سره خبرې نشې كولى خو په اشارې سره، او ته خپل رب ډېر یادوه او د ورځې په اخر او اول كې تسبیح (پاكي) بیانوه');
INSERT INTO "abulsalam" VALUES(3,42,'3:42',' او كله چې ملايكو وویل: اى مریمې! یقینًا الله ته غوره كړې يې او ته يې پاكه كړې يې او د ټول عالَم پر ښځو يې تا ته غوره والى دركړى دى');
INSERT INTO "abulsalam" VALUES(3,43,'3:43',' اى مریمې! د خپل رب فرمانبرداره شه او سجده كوه او له ركوع كوونكو سره ركوع كوه');
INSERT INTO "abulsalam" VALUES(3,44,'3:44',' دا د غیب له خبرونو څخه دي چې مونږه يې تا ته وحي كوو او ته له هغوى سره موجود نه وې كله چې هغوى خپل قلمونه غورځول، چې په دوى كې څوك د مریمې كفالت وكړي او ته له هغوى سره حاضر نه وې كله چې يې په خپلو كې جګړه كوله');
INSERT INTO "abulsalam" VALUES(3,45,'3:45',' كله چې ملايكو وویل: اى مریمې! بېشكه الله تا ته له خپل طرف نه د یوې كلمې زېرى دركوي، چې د هغه نوم مسیح عیسٰی زوى د مریم دى، چې په دنیا او اخرت كې به ډېر مخور وي او له مُقرَبینو څخه به وي');
INSERT INTO "abulsalam" VALUES(3,46,'3:46',' او له خلقو سره به خبرې كوي په زانګو (غېږ) كې او په پاخه عمر كې او وي به له صالحانو ځنې');
INSERT INTO "abulsalam" VALUES(3,47,'3:47',' هغې وویل: اى زما ربه! زما لپاره بچى څنګه كېدى شي،حال دا چې ما ته هېڅ یو بشر لاس (هم) نه دى راوړى؟! هغه (جبریل) وویل: همداسې الله پیدا كوي څه چې وغواړي، كله چې د یو كار اراده وكړي، نو بېشكه چې هغه (كار) ته همدا وايي: ’’وشه‘‘ نو هغه وشي');
INSERT INTO "abulsalam" VALUES(3,48,'3:48',' او هغه ته به كتاب او حكمت او تورات او انجیل ورزده كوي');
INSERT INTO "abulsalam" VALUES(3,49,'3:49',' او بني اسرائیلو ته به رسول وي (چې ورته به وايي:) بېشكه زه چې یم، یقینًا ما تاسو ته ستاسو د رب له طرفه لویه نښه راوړې ده، چې یقینًا زه تاسو ته له خَټې نه د مرغۍ د شكل په شان (یو شى) جوړوم، بیا په دې (شكل) كې پوكى كوم، نو هغه به د الله په حكم سره الوتونكې مرغۍ شي، او زه مورزادي ړوند او د برګي مرض والا روغوم او مړي ژوندي كوم د الله په حكم سره، او تاسو خبروم پر هغه څه چې تاسو يې خورئ او پر هغه څه چې تاسو يې په خپلو كورونو كې ذخیره كوئ، بېشكه تاسو لپاره په دې كې خامخا لویه نښه ده كه چېرې تاسو ایمان راوړونكي یئ');
INSERT INTO "abulsalam" VALUES(3,50,'3:50',' په دې حال كې چې تصدیق كوونكى یم د هغه (كتاب) چې له ما نه مخكې و، چې تورات دى او د دې لپاره (راغلى یم) چې تاسو لپاره بعضې له هغو شیانو حلال كړم چې پر تاسو باندې حرام كړى شوي وو او ستاسو د رب له طرفه نه ما تاسو ته واضحه نښه راوړې ده، نو له الله نه ووېرېږئ او زما اطاعت وكړئ');
INSERT INTO "abulsalam" VALUES(3,51,'3:51',' بېشكه الله زما رب دى او ستاسو رب دى، نو د هغه عبادت كوئ، دا ډېره نېغه لار ده');
INSERT INTO "abulsalam" VALUES(3,52,'3:52',' نو عیسٰی چې كله له دوى څخه كفر ولیده، نو ويې ويل:د الله په طرف (تللو كې) څوك زما مددكوونكي دي؟ حواریانو وویل: مونږ د الله مددګار یو، مونږ پر الله ایمان راوړى دى او ته ګواه شه پر دې چې بېشكه مونږ مسلمانان (فرمانبردار) یو');
INSERT INTO "abulsalam" VALUES(3,53,'3:53',' اى زمونږه ربه! مونږ ایمان راوړى دى په هغه څه چې تا نازل كړي دي او مونږ د رسول تابعداري كړې ده، نو ته مونږه (د حق) له ګواهي كوونكو سره ولیكه');
INSERT INTO "abulsalam" VALUES(3,54,'3:54',' او هغوى چلونه جوړ كړل او الله پټ تدبیر وكړ او الله له ټولو تدبیر كوونكو نه غوره دى');
INSERT INTO "abulsalam" VALUES(3,55,'3:55',' كله چې الله وویل: اى عیسٰی! بېشكه زه تا لره پوره پوره اخيستونكى یم او د خپل ځان طرف ته ستا راپورته كوونكى یم او تا لره له هغو كسانو نه پاكوونكى یم چې كفر يې كړى دى او چا چې ستا پیروي كړې ده هغوى لره د قیامت د ورځې پورې د هغو كسانو له پاسه كوونكى یم؛ چې كفر يې كړى دى، بیا خاص زما طرف ته ستاسو واپسي ده، نو ستاسو په مینځ كې به د هغو خبرو په باره كې فیصله كوم چې تاسو په كې اختلاف كاوه');
INSERT INTO "abulsalam" VALUES(3,56,'3:56',' پس هر چې هغه كسان دي چا چې كفر كړى دى، نو هغوى ته به زه په دنیا او اخرت كې ډېر سخت عذاب وركوم او د دوى لپاره به هیڅوك مددكوونكي نه وي');
INSERT INTO "abulsalam" VALUES(3,57,'3:57',' پاتې شو هغه كسان چا چې ایمان راوړى دى او نېك عملونه يې كړي دي، نو هغوى ته به (الله) د هغوى اجر پوره وركړي او الله له ظالمانو سره مینه نه كوي ');
INSERT INTO "abulsalam" VALUES(3,58,'3:58',' دا دي چې مونږ يې له ایتونو او له حكمته ډك نصیحت څخه پر تا لولو');
INSERT INTO "abulsalam" VALUES(3,59,'3:59',' بېشكه د عیسٰی مثال د الله په نیز د ادم د مثال په شان دى، الله هغه له خاورې پیدا كړ، بیا يې هغه ته وویل: (پیدا) شه، پس (پیدا) شو');
INSERT INTO "abulsalam" VALUES(3,60,'3:60',' حق ستا د رب له جانبه دى، نو له شك كوونكو نه مه كېږه');
INSERT INTO "abulsalam" VALUES(3,61,'3:61',' نو څوك چې د ده (عیسٰی) په باره كې له تا سره جګړه كوي، وروسته له دې چې تا ته علم حاصل شو، نو ته ووایه: راځئ! چې مونږ راوبلو خپل زامن او ستاسو زامن او خپلې ښځې او ستاسو ښځې او خپل ځانونه او ستاسو ځانونه، بیا به په زارۍ سره دعا وكړو، نو پر دروغژنو به د الله لعنت ووایو');
INSERT INTO "abulsalam" VALUES(3,62,'3:62',' بېشكه همدا حق بیان دى او نشته هېڅ برحق معبود مګر يواځې الله دى او بېشكه يواځې الله ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(3,63,'3:63',' نو كه دوى وګرځېدل، نو یقینًا الله پر فسادكوونكو ښه پوه دى');
INSERT INTO "abulsalam" VALUES(3,64,'3:64',' (اى نبي!) ته (دوى ته) ووایه: اى اهل كتابو! داسې كلمې ته راشئ چې زمونږ په مینځ كې او ستاسو په مینځ كې برابره ده، دا چې عبادت به نه كوو مګر يواځې د الله او له هغه سره به هېڅ شى نه شریكوو او زمونږ بعضې به بعضو نورو لره له الله نه غېر پالونكي (خدایان) نه نیسي، نو كه دوى وګرځېدل، نو تاسو (ورته) ووایئ چې تاسو پر دې ګواه شئ چې یقینًا مونږ مسلمانان یو');
INSERT INTO "abulsalam" VALUES(3,65,'3:65',' اى اهل كتابو! ولې تاسو د ابراهیم په باره كې جګړه كوئ، حال دا چې تورات او ا نجیل نه وو نازل كړى شوي مګر له هغه نه وروسته، ایا نو ولې له عقل نه كار نه اخلئ!');
INSERT INTO "abulsalam" VALUES(3,66,'3:66',' ګورئ! تاسو خصوصًا دغو، په هغه خبره كې جګړه وكړه چې تاسو ته د هغې څه علم و، نو په هغه خبره كې ولې جګړه كوئ چې تاسو ته د هغې هېڅ علم نشته؟ او الله پوهېږي او تاسو نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(3,67,'3:67',' ابراهیم نه یهودي و او نه نصراني او لېكن حنیف (پوخ توحید والا) مسلمان و او له مشركانو څخه نه و');
INSERT INTO "abulsalam" VALUES(3,68,'3:68',' بېشكه په خلقو كې ابراهیم ته حقيقتًا نژدې خامخا هغه كسان دي چا چې د هغه تابعداري كړې ده او دا نبي دى او هغه كسان دي چې ایمان يې راوړى دى او الله د مومنانو دوست دى');
INSERT INTO "abulsalam" VALUES(3,69,'3:69',' په اهل كتابو كې یوه ډله ستاسو د ګمراه كولو ارزو لري او هغوى يواځې خپل ځانونه ګمراه كوي او شعور نه لري');
INSERT INTO "abulsalam" VALUES(3,70,'3:70',' اى اهلِ كتابو! ولې د الله په ایتونو كافر كېږئ، حال دا چې تاسو (د هغو د حقانیت) ګواهي وركوئ؟');
INSERT INTO "abulsalam" VALUES(3,71,'3:71',' اى اهلِ كتابو! ولې تاسو حق له باطل سره ګَډوَډ كوئ؟ او تاسو حق پټوئ، حال دا چې تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(3,72,'3:72',' او له اهلِ كتابو څخه یوې ډلې (بلې ډلې ته) وویل: تاسو په هغه (كتاب) باندې چې په مومنانو باندې نازل كړى شوى دى، د ورځې په اول كې (په ظاهره) ایمان راوړئ او د هغې (ورځې) په اخر كې(بیا) كافر شئ، د دې لپاره چې دوى (مومنان) راوګرځي');
INSERT INTO "abulsalam" VALUES(3,73,'3:73',' او (د دې) یقین مه كوئ مګر د هغه چا لپاره چې ستاسو د دین متابعت يې كړى وي (اى نبي!) ته ووایه: یقینًا اصل هدایت (او حق دین) خو د الله هدایت دى (او د دې یقین هم مه كوئ) دا چې یو چا ته به د هغو (فضايلو) په مثل وركړى شي چې تاسو ته دركړى شوي دي، یا به دوى پر تاسو باندې د خپل رب په وړاندې غالبه شي، (اى نبي!) ته ووایه: بېشكه فضل د الله په لاس كې دى، چا ته چې وغواړي هغه وركوي او الله فراخۍ والا دى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(3,74,'3:74',' په خپلې مهربانۍ سره خاص كوي چا ته چې وغواړي او الله د ډېر لوى فضل والا دى');
INSERT INTO "abulsalam" VALUES(3,75,'3:75',' او ځینې له اهلِ كتابو څخه هغه څوك دي چې كه ته هغه ته ډېر مال امانت وركړې، نو تا ته به هغه (مال) بېرته ادا كوي او ځینې له دوى نه هغه څوك دي چې كه ته هغه ته یو دینار (هم) امانت وركړې (نو) تا ته به هغه (دینار) بېرته نه ادا كوي مګر دا چې ته په (سر د) هغه مدام ولاړ اوسې، دا په دې چې یقینًا دوى ویلي دي چې پر مونږ باندې د اَمیانو (د حق) په باره كې هېڅ ګناه نشته او په الله باندې دروغ ورتپي، حال دا چې دوى پوهېږي');
INSERT INTO "abulsalam" VALUES(3,76,'3:76',' ولې نه! چا چې خپله وعده پوره كړه او پرهېزګاره شو، نو یقینًا الله پرهېزګاران خوښوي');
INSERT INTO "abulsalam" VALUES(3,77,'3:77',' بېشكه هغه كسان چې د الله (سره د كړې) وعدې او د خپلو قسمونو په بدل كې لږې پیسې اخلي، دغه كسان، د دوى لپاره په اخرت كې هېڅ برخه نشته او د قیامت په ورځ به الله له دوى سره نه خبري كوي او نه به دوى ته ګوري او نه به دوى پاك كړي او د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(3,78,'3:78',' او یقینًا یقینًا په دوى كې یوه ډله ده چې په (لوستو د) كتاب سره خپلې ژبې تاووِي، د دې لپاره چې تاسو پر هغهٔ د (الله د) كتاب ګمان وكړئ، حال دا چې هغه له (حق) كتاب څخه نه دي، او دوى وايي چې دا (مُحرَّف عبارتونه او تعبيرونه) د الله له جانبه دي، حال دا چې هغه د الله له جانبه نه دي، او دوى په الله باندې دروغ تپي، حال دا چې دوى پوهېږي');
INSERT INTO "abulsalam" VALUES(3,79,'3:79',' هېڅ یو انسان ته دا حق نه دى حاصل چې الله هغه ته كتاب او حكم او نبوت (پېغمبري) وركړي، بیا دې هغه، خلقو ته وايي چې له الله نه غیر زما بنده ګان (عبادت كوونكي) شئ، او لېكن (هغه خو به وايي چې) تاسو الهي خلق شئ (په علم باندې عمل كوونكي شئ) ځكه چې تاسو د كتاب ښودنه كوئ او په دې چې تاسو زده كړه كوئ');
INSERT INTO "abulsalam" VALUES(3,80,'3:80',' او نه (ورته) دا (حق حاصل دى) چې تاسو ته حكم دركړي چې تاسو ملايكو او نبیانو لره پروردګاران (خدایان) ونیسئ، ایا هغه به تاسو ته د كفر كولو حكم كوي وروسته له دې چې تاسو مسلمانان یئ؟');
INSERT INTO "abulsalam" VALUES(3,81,'3:81',' او كله چې الله د نبیانو (نه) پخه وعده واخيسته چې هر هغه څه چې زه يې تاسو ته له كتاب او حكمته دركړم، بیا تاسو ته یو رسول راشي چې د هغه څه تصدیق كوونكى وي چې له تاسو سره دي (نو) تاسو به خامخا پر هغه ایمان راوړئ او خامخا به د هغه مدد كوئ، (الله) وویل: ایا تاسو اقرار وكړ او په دې باندې تاسو زما درنه وعده قبوله كړه، هغوى وویل: مونږ اقرار وكړ، (الله) وویل: نو تاسو ګواه شئ او زه (هم) تاسو سره له ګواهانو ځنې یم');
INSERT INTO "abulsalam" VALUES(3,82,'3:82',' نو څوك چې له دې نه بعد وګرځېده، نو همدغه خلق فاسقان دي');
INSERT INTO "abulsalam" VALUES(3,83,'3:83',' ایا نو دوى د الله له دین نه غیر (بل دین) غواړي، حال دا چې خاص هغه (الله) ته غاړه ایښې ده، هر هغه څه چې په اسمانونو او ځمكه كې دي، په خوښه، یا په ناخوښه، او خاص هغه ته به دوى ورګرځولى شي');
INSERT INTO "abulsalam" VALUES(3,84,'3:84',' ته ووایه: مونږ ایمان راوړى دى پر الله او پر هغه (كتاب) چې پر مونږه نازل كړى شوى دى او پر هغه څه چې په ابراهیم او سمٰعیل او اسحٰق او یعقوب او (د هغه) په اولادې باندې نازل كړى شوي دي او پر هغه څه چې موسٰی او عیسٰی او )نورو) نبیانو ته د خپل رب له جانبه وركړى شوي دي، مونږ په دوى كې د هېڅ یو په مینځ كې فرق نه كوو او مونږ خاص د هغه (الله) حكم منونكي یو');
INSERT INTO "abulsalam" VALUES(3,85,'3:85',' او څوك چې له اسلام نه غېر (بل) دین غواړي، نو هېڅكله به هم له ده نه قبول نه كړى شي او دغه كس په اخرت كې له تاوانیانو څخه دى ');
INSERT INTO "abulsalam" VALUES(3,86,'3:86',' الله به داسې قوم څنګه په سمه لار روان كړي چې له خپل ایمان راوړو نه بعد (بیا) كافر شول، حال دا چې دوى ګواهي وركړې وه چې بېشكه دا رسول حق دى او دوى ته ښكاره معجزې (هم) راغلې وې او الله (داسې) ظالمو خلقو ته سمه لار نه ښَیي');
INSERT INTO "abulsalam" VALUES(3,87,'3:87',' دغه خلق، د دوى جزا دا ده چې یقینًا په دوى باندې د الله لعنت دى او د ملايكو او د خلقو، د ټولو (لعنت دى) ');
INSERT INTO "abulsalam" VALUES(3,88,'3:88',' چې همېشه به وي په دې (لعنت) كې، له دوى نه به نه عذاب سپكولى شي او نه به دوى ته مهلت وركول كېږي');
INSERT INTO "abulsalam" VALUES(3,89,'3:89',' مګر هغه كسان چې له دې نه بعد توبه وباسي او د ځان اصلاح وكړي، نو یقینًا الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(3,90,'3:90',' بېشكه هغه كسان چې له خپل ایمان راوړو نه بعد كافر شول، بیا په كفر كې (ترځنكدنه) مخ په وړاندې لاړل، (نو) د هغوى توبه به له سره نه قبلېږي او همدغه كسان ګمراهان دي');
INSERT INTO "abulsalam" VALUES(3,91,'3:91',' بېشكه هغه كسان چې كافر شوي دي او په داسې حال كې مړه شول چې كافران وو، نو كله به هم قبول نه كړى شي د هغوى له هېڅ یو تن نه ډكه ځمكه سره زر اګر كه هغه يې د ځان خلاصي په بدل كې وركړي، دغه كسان، د دوى لپاره ډېر دردوونكى عذاب دى او د دوى لپاره به هیڅوك مدد كوونكي نه وي ');
INSERT INTO "abulsalam" VALUES(3,92,'3:92','تاسو به (پوره) نېكۍ ته له سره ونه رسېږئ تر هغه پورې چې له هغه (څیز) نه څه حصه خرچ كړئ چې تاسو يې خوښَوئ، او تاسو چې څه څیز هم خرچ كوئ، نو بېشكه الله په هغه ښه عالم دى');
INSERT INTO "abulsalam" VALUES(3,93,'3:93',' د خوراك ټول شیان د بني اسرائیلو لپاره حلال وو، مګر هغه (شیان) چې اسرائیل (یعقوب) په ځان باندې بند كړي وو، مخكې له دې چې تورات نازل كړى شي، ته ووایه: نو تورات راوړئ، بيا هغه ولولئ، كه تاسو رښتوني یئ');
INSERT INTO "abulsalam" VALUES(3,94,'3:94',' نو هغه چا چې له دې نه بعد په الله باندې دروغ وويل، نو همدغه كسان ظالمان دي');
INSERT INTO "abulsalam" VALUES(3,95,'3:95',' ته ووایه: الله رښتیا وَیلي دي، پس د ابراهیم د ملت تابع شئ، چې حنیف (پوخ توحید والا) و او له مشركانو څخه نه و');
INSERT INTO "abulsalam" VALUES(3,96,'3:96',' بېشكه اول كور چې د خلقو لپاره جوړ كړى شوى دى، خامخا هغه دى چې په مكه كې دى، چې ډېر بركتي دى او د عالَمونو لپاره هدایت دى');
INSERT INTO "abulsalam" VALUES(3,97,'3:97',' په هغه كې ښې واضحې نښې دي، چې (یوه په كې) مقامِ ابراهیم دى او څوك چې هغه (كور) ته داخل شي، (نو) هغه په امن شي، او خاص د الله لپاره په خلقو باندې د (هغه د) كور حج كول فرض دي، په هغه چا باندې چې هغه ته د لارې وسَ ولري او څوك چې كافر شو، نو بېشكه الله له مخلوقاتو نه بېخي بې پروا دى');
INSERT INTO "abulsalam" VALUES(3,98,'3:98',' (اى نبي!) ته ووایه: اى اهلِ كتابو! تاسو د الله په ایتونو ولې كافر كېږئ، حال دا چې الله ستاسو په عملونو باندې ګواه دى');
INSERT INTO "abulsalam" VALUES(3,99,'3:99',' (اى نبي!) ته ووایه: اى اهلِ كتابو! تاسو د الله له لارې نه هغه څوك ولې منع كوئ چې ایمان راوړي؟ تاسو په هغې (لاره)كې كوږوالى لټوئ، حال دا چې تاسو ګواهان یئ او تاسو چې څه عمل كوئ، الله له هغه (عمل) نه كله هم غافل نه دى');
INSERT INTO "abulsalam" VALUES(3,100,'3:100',' اى هغو كسانو چې ایمان يې راوړى دى! كه چېرې تاسو په اهلِ كتابو كې د یوې ډلې خبره ومنئ (،نو) هغه به تاسو لره ستاسو له ایمان راوړو نه پس بیا كافران كړي');
INSERT INTO "abulsalam" VALUES(3,101,'3:101',' او تاسو څنګه كافر كېږئ، او تاسو چې یئ، تاسو ته د الله ایتونه تلاوت كېږي او په تاسو كې د هغه رسول (هم موجود) دى او چا چې په (دین د) الله منګولې ښخې كړې، نو په رښتیا سره هغه په نېغه لار روان كړى شو');
INSERT INTO "abulsalam" VALUES(3,102,'3:102',' اى هغو كسانو چې ایمان يې راوړى دى! له الله نه ووېرېږئ، څنګه چې له هغه نه د وېرېدلو حق دى او تاسو مه مرئ مګر په دې حال كې چې تاسو مسلمانان یئ');
INSERT INTO "abulsalam" VALUES(3,103,'3:103',' او تاسو د الله پر رسۍ (ټول) په جمع سره منګولې ښخې كړئ او سره بېلېږئ مه او د الله (هغه) نعمتونه را یاد كړئ چې پر تاسو باندې شوي دي، كله چې تاسو دښمنان وَئ، بیا الله ستاسو د زړونو تر مینځ جوړښت راوسته، نو د هغه په (دې) نعمت سره تاسو وروڼه شوئ او تاسو له اور نه د ډكې كندې پر غاړه وَئ، نو تاسو يې له هغه نه وژغورلئ، همدارنګه الله تاسو ته خپل ایتونه بیانوي، د دې لپاره چې تاسو نېغه لار ومومئ');
INSERT INTO "abulsalam" VALUES(3,104,'3:104',' او لازم ده چې په تاسو كې دې یوه داسې ډله وي چې د خیر طرف ته بلنه كوي او د نېكۍ حكم كوي او له بدۍ نه منع كوي، او همدغه خلق پوره كامیاب دي');
INSERT INTO "abulsalam" VALUES(3,105,'3:105',' او تاسو د هغو خلقو په شان مه كېږئ چې بېل بېل شول او هغو ته د واضحو دلايلو له راتلو نه پس (هم) په خپلو كې مختلف شول، او دغه خلق (چې دي)، د هغوى لپاره بېخي لوى عذاب دى');
INSERT INTO "abulsalam" VALUES(3,106,'3:106',' په هغې ورځ كې چې څه مخونه به تك سپین وي او څه مخونه به تك تور وي، نو هر چې هغه كسان دي چې د چا مخونه به تك تور وي، (هغوى ته به ویلى شي) ایا تاسو له خپل ایمان نه پس كافر شوي وَئ، نو عذاب وڅكئ د هغه كفر په بدل كې چې تاسو به كاوه');
INSERT INTO "abulsalam" VALUES(3,107,'3:107',' او پاتې شو هغه كسان چې د هغوى مخونه به تك سپین وي، نو (هغوى) به د الله په مهربانۍ كې وي، دوى به په هغې كې همېشه وي');
INSERT INTO "abulsalam" VALUES(3,108,'3:108',' دا د الله ایتونه دي، چې مونږه يې تا ته په حقه سره لولو او الله په عالَمونو باندې د ظلم كولو اراده نه لري');
INSERT INTO "abulsalam" VALUES(3,109,'3:109',' او خاص د الله لپاره دي هغه څه چې په اسمانونو كې دي او هغه څه چې په ځمكه كې دي او خاص د الله په طرف (ټول) كارونه ورګرځولى شي');
INSERT INTO "abulsalam" VALUES(3,110,'3:110',' تاسو غوره امت یئ چې د خلقو لپاره پیدا كړى شوي یئ، تاسو د نېكۍ حكم كوئ او له بدۍ نه منع كوئ او په الله باندې ایمان راوړئ، او كه چېرې اهلِ كتابو ایمان راوړى وَى، نو دا به د هغوى لپاره ډېر ښه و، له دوى نه ځینې اِیمان راوړونكي دي او د دوى اكثره فاسقان (بدكاران) دي');
INSERT INTO "abulsalam" VALUES(3,111,'3:111',' دوى هیڅكله هم تاسو ته ضرر نشي دررسولى مګر لږ (د ژبې) تكلیف، او كه دوى له تاسو سره جنګ شروع كړي (نو) تاسو ته به شاګانې درواړوي، بیا به له دوى سره مرسته نه كېږي');
INSERT INTO "abulsalam" VALUES(3,112,'3:112',' په دوى باندې ذلت مسلط كړى شوى دى، هر ځای چې دوى وموندل شي، مګر (دا چې دوى منګولې ولګوي) د الله پر رسۍ او د خلقو پر رسۍ او دوى راستانه شول په هغه غضب سره چې د الله له طرفه و، او په دوى باندې محتاجي مسلط كړى شوه، دا په دې چې یقینًا دوى د الله له ایتونو نه انكار كاوه او نبیان يې ناحقه وژل، دا په دې وجه چې دوى نافرماني كوله او دوى زیاتى كاوه');
INSERT INTO "abulsalam" VALUES(3,113,'3:113',' دوى یو شان نه دي، په اهلِ كتابو كې یوه ډله ده قايمه، چې د شپې په ساعتونو كې د الله ایتونه تلاوت كوي او هغوى سجدې كوي');
INSERT INTO "abulsalam" VALUES(3,114,'3:114',' پر الله او د اخرت پر ورځ ایمان راوړي او د نېكۍ حكم كوي او له بدۍ نه منع كوي، او په نېكیو كې له یو بل نه تلوار كوي او دغه كسان له صالحانو څخه دي');
INSERT INTO "abulsalam" VALUES(3,115,'3:115',' او هغوى چې څه نېكي هم وكړي، نو هېڅكله به هم د هغوى د دې (عمل) ناقدري ونه كړى شي او الله پر پرهېزګارانو ښه پوه دى');
INSERT INTO "abulsalam" VALUES(3,116,'3:116',' بېشكه هغه كسان چې كفر يې كړى دى، هېڅ كله به هم د هغوى مالونه او اولادونه له دوى څخه د الله (له عذاب) نه هېڅ شى لرې نه كړى شي، او دغه كسان د اور والا دي، دوى به په هغه (اور) كې همېشه وي');
INSERT INTO "abulsalam" VALUES(3,117,'3:117',' مثال د هغه شي چې دوى يې په دې دنیايي ژوند كې خرچ كوي د هغه باد په شان دى چې په هغه كې سخته یخني وي، د داسې خلقو فصل ته ورسي چې پر خپلو ځانونو يې ظلم كړى وي، نو دا (باد) هغه (فصل) تباه كړي، او الله پر هغوى ظلم نه دى كړى او لېكن هغوى (پخپله) پر خپلو ځانونو ظلم كاوه');
INSERT INTO "abulsalam" VALUES(3,118,'3:118',' اى هغو كسانو چې ایمان يې راوړى دى! له خپلو (مسلمانانو) نه غیر (بل څوك) رازدار دوست مه جوړوئ، دوى تاسو ته په نقصان رسولو كې هېڅ كمى نه كوي، دوى هر هغه څه خوښوي چې تاسو په تكلیف كې اچوي، یقینًا د دوى له خولو ځنې (كینه او) بغض ښكاره شوى دى او هغه (بغض) چې د دوى سینې يې پټوي (هغه) ډېر لوى دى، یقینًا مونږه تاسو لپاره ایتونه ښه واضحه بیان كړي دي، كه چېرې یئ تاسو چې له عقل نه كار اخلئ');
INSERT INTO "abulsalam" VALUES(3,119,'3:119',' ګورئ! تاسو هغه خلق یئ چې له هغو (اهل كتابو) سره مینه كوئ او هغوى له تاسو سره مینه نه كوي او تاسو پر ټولو كتابونو ایمان راوړئ او كله چې دوى له تاسو سره ملاقات وكړي، (نو) وايي: مونږ ایمان راوړى دى، او كله چې يواځې شي، نو تاسو ته د غصې له وجې نه د ګوتو سرونه چیچي، (ورته) ووایه: په خپلې غصې سره مړه شئ، بېشكه الله ښه پوه دى پر هغو (خبرو) چې په سینو (زړونو) كې دي');
INSERT INTO "abulsalam" VALUES(3,120,'3:120',' كه چېرې تاسو ته څه ښېګڼه درورسېږي، نو هغه دوى لره خفه كوي او كه چېرې تاسو ته څه بدي (تكلیف) درورسیږي، نو دوى په هغې باندې خوشحالېږي او كه تاسو صبر وكړئ او(د دوى له دوستۍ نه) ځان وساتئ (نو) د هغوى چل (او فریب) به تاسو ته هېڅ ضرر هم در ونه رسولى شي، بېشكه چې الله د هغو كارونو چې دوى يې كوي، احاطه كوونكى دى');
INSERT INTO "abulsalam" VALUES(3,121,'3:121',' او(یاد كړه هغه وخت) كله چې د ورځې په اوله حصه كې له خپل ټبره راووتلې، چې د جنګ لپاره دې مومنان په (خپلو) ځایونو باندې كېنول، او الله ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(3,122,'3:122',' كله چې له تاسو څخه دوه ډلو د دې اراده وكړه چې بې زړه (بُزدله) شي او الله د هغو دواړو دوست و، او لازم ده چې مومنان خاص په الله باندې توكل وكړي');
INSERT INTO "abulsalam" VALUES(3,123,'3:123',' او خامخا په رښتیا سره الله په بدر كې ستاسو مدد كړى دى، حال دا چې تاسو ډېر كمزوري (او لږ) وئ، نو له الله نه ووېرېږئ، د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(3,124,'3:124',' كله چې تا مومنانو ته ویل: ایا تاسو ته به دا كافي نه شي چې ستاسو رب په درې زره نازل كړى شويو ملايكو سره ستاسو مدد وكړي!');
INSERT INTO "abulsalam" VALUES(3,125,'3:125',' ولې نه! كه تاسو صبر وكړئ او پرهېزګاره شئ او هغوى پر تاسو باندې په خپل جوش سره په تېزۍ حمله وكړي، (نو) دا دى چې ستاسو رب به په پنځو زرو علامې لرونكو ملايكو سره ستاسو مدد وكړي');
INSERT INTO "abulsalam" VALUES(3,126,'3:126',' او الله دا (مدد) نه دى ګرځولى مګر ستاسو لپاره زېرى او لپاره د دې چې ستاسو زړونه په دې مطمئن شي او نه دى مدد مګر د الله له جانبه، چې ډېر غالب، ډېر حكمت والا دى');
INSERT INTO "abulsalam" VALUES(3,127,'3:127',' لپاره د دې چې هغه هلاك كړي یو ټولى د هغو كسانو چې كفر يې كړى دى، یا يې ذلیله (خواروزار) كړي، پس چې بېرته محرومه، نامراده لاړ شي');
INSERT INTO "abulsalam" VALUES(3,128,'3:128',' تا لپاره په دې كار كې هېڅ شى (اختیار) نشته، یا به هغه (الله) په دوى باندې په مهربانۍ سره راوګرځي، یا به دوى ته عذاب وركړي، ځكه چې یقینًا دوى ظالمان دي');
INSERT INTO "abulsalam" VALUES(3,129,'3:129',' او خاص د الله لپاره دي هغه څه چې په اسمانونو كې دي او هغه څه چې په ځمكه كې دي، هغه بخښنه كوي چا لپاره چې وغواړي او چا ته چې وغواړي عذاب وركوي او الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(3,130,'3:130',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو سود مه خورئ چې څو چنده دوه ګونه كړى شوى وي او له الله نه وېرېږئ، د دې لپاره چې تاسو كامیاب شئ');
INSERT INTO "abulsalam" VALUES(3,131,'3:131',' او له هغه اور نه ځان وساتئ چې د كافرانو لپاره تیار كړى شوى دى');
INSERT INTO "abulsalam" VALUES(3,132,'3:132',' او د الله او د رسول اطاعت كوئ، لپاره د دې چې پر تاسو رحم وكړى شي،');
INSERT INTO "abulsalam" VALUES(3,133,'3:133',' او تاسو له یو بل نه تلوار كوئ هغه (مغفرت او) بخښنې ته چې د خپل رب له جانبه ده او داسې جنت ته چې د هغه پلن والى د اسمانونو او د ځمكې (په اندازه) دى، د پرهېزګارانو لپاره تیار كړى شوى دى');
INSERT INTO "abulsalam" VALUES(3,134,'3:134',' هغه كسان چې انفاق كوي په اسانۍ او تنګۍ كې او خصوصًا چې غصې لره زغمونكي وي او خلقو ته معافي كوونكي وي او الله نېكي كوونكي خوښَوي');
INSERT INTO "abulsalam" VALUES(3,135,'3:135',' او هغه كسان چې كله څه فاحش كار وكړي، یا پر خپلو ځانونو (بل) ظلم وكړي (نو) الله یاد كړي، (او) ورپسې د خپلو ګناهونو لپاره بخښنه وغواړي، او بل څوك دى چې ګناهونه وبخښي غیر له الله نه؟ او څه چې دوى كړي وي په هغو اصرار (كلكوالى) و نه كړي، په دې حال كې چې دوى پوهېږي');
INSERT INTO "abulsalam" VALUES(3,136,'3:136',' دغه كسان (چې دي) د دوى بدله د خپل رب له جانبه بخښنه ده او داسې جنتونه دي، چې د هغو له لاندې وَلې بهېږي، چې په هغو كې به دوى همېشه وي، او د (ښه) عمل كوونكو (لپاره) ښه اجر دى');
INSERT INTO "abulsalam" VALUES(3,137,'3:137',' بېشكه له تاسو مخكې ډېرې طریقې (او پېښې) تېرې شوي دي، نو تاسو په ځمكه كې وګرځئ، بیا وګورئ چې د دروغ ګڼونكو انجام څنګه و؟');
INSERT INTO "abulsalam" VALUES(3,138,'3:138',' دا د خلقو لپاره بیان دى او د پرهېزګارانو لپاره لارښوونه او نصیحت دى');
INSERT INTO "abulsalam" VALUES(3,139,'3:139',' او تاسو مه كمزوري كېږئ او مه غمژن كېږئ، په دې حال كې چې هم تاسو به غالبه یئ، (خو) كه تاسو مومنان یئ');
INSERT INTO "abulsalam" VALUES(3,140,'3:140',' كه چېرې تاسو ته څه زخم رسېږي، نو یقینًا (كافر) قوم ته (هم) د دې په شان زخم رسېدلى دى او دا ورځې (حادثې) دي چې مونږه يې د خلقو په مینځ كې په وار سره اړوو رااړوو او لپاره د دې چې الله مومنان معلوم (څرګند) كړي او له تاسو څخه شهیدان واخلي او الله له ظالمانو سره مینه نه كوي');
INSERT INTO "abulsalam" VALUES(3,141,'3:141',' او لپاره د دې چې الله مومنان ښه پاك كړي او دغه كافران ختم (او) نابوده كړي');
INSERT INTO "abulsalam" VALUES(3,142,'3:142',' ایا تاسو دا ګمان كوئ چې تاسو به جنت ته داخل شئ، حال دا چې لا تر اوسه الله په تاسو كې هغه كسان نه دي معلوم (څرګند) كړي چې جهاد يې كړى دى، او د دې لپاره چې هغه صبر كوونكي معلوم كړي');
INSERT INTO "abulsalam" VALUES(3,143,'3:143',' او خامخا په رښتیا سره تاسو د مرګ ارزو كوله، له دې (مرګ) سره له مخامخ كېدو نه مخكې، نو یقینًا تاسو هغه ولیده، په داسې حال كې چې تاسو كتل');
INSERT INTO "abulsalam" VALUES(3,144,'3:144',' او محمد نه دى مګر یو رسول، بېشكه له ده نه مخكې رسولان تېر شوي دي، ایا نو كه چېرې دى مړ شي، یا ووژل شي (،نو) تاسو به بېرته په شا لاړ شئ او څوك چې بېرته په شا لاړ شي، نو هغه به الله ته هیڅكله هم څه زیان ونه رسولى شي او الله به خامخا شكر ګذارو ته بدله (ثواب) وركړي');
INSERT INTO "abulsalam" VALUES(3,145,'3:145',' او هېڅ یو نفس لره دا اختیار نشته چې (پخپله) مړ شي مګر د الله په ارادې سره، لیكلى مقرر وخت دى، او څوك چې د دنیا بدله غواړي، (نو) مونږ به له دې (دنیا) نه هغه ته (بدله) وركړو او څوك چې د اخرت ثواب غواړي، (نو) هغه ته به يې له دې (اخرت) نه وركړو، نژدې ده چې مونږ به شكر ګذارو ته ښه بدله وركړو');
INSERT INTO "abulsalam" VALUES(3,146,'3:146',' او ډېر پېغمبران (تېر شوي) دي چې د هغوى په ملګرتیا كې ډېرو رباني (خدايي) خلقو جنګ كړى دى، نو نه هغوى د هغه مصیبت په سبب سستي وكړه چې پر هغوى باندې د الله په لار كې راغلى و او نه يې كمزورتیا ښكاره كړه او نه ټیټ شول، او الله له صبر كوونكو سره مینه كوي');
INSERT INTO "abulsalam" VALUES(3,147,'3:147',' او د هغوى وینا له دې نه غیر بله نه وه چې ویل به يې: اى زمونږه ربه! مونږ ته زمونږ ګناهونه او په خپل كار كې زمونږه اسراف كول (له حد نه تېرېدل) وبخښه او زمونږه (پښې) قدمونه ټینګ كړه او د كافر قوم په مقابله كې زمونږ مدد وكړه');
INSERT INTO "abulsalam" VALUES(3,148,'3:148',' نو الله هغوى ته د دنیا بدله او د اخرت ښكلى ثواب ورعطا كړ او الله له نېكي كوونكو سره مینه كوي');
INSERT INTO "abulsalam" VALUES(3,149,'3:149',' اى هغو كسانو چې ایمان يې راوړى دى! كه چېرې تاسو د كافر شویو كسانو خبره ومنئ (، نو) هغوى به تاسو (له دین نه) په شا وګرځوي، بیا به تاسو داسې شئ چې تاوانیان به یئ');
INSERT INTO "abulsalam" VALUES(3,150,'3:150',' بلكې الله ستاسو مددګار دى او هغه تر ټولو ښه مدد كوونكى دى ');
INSERT INTO "abulsalam" VALUES(3,151,'3:151',' ژر ده چې مونږ به د هغو كسانو په زړونو كې رعب واچوو چې كافران شوي دي، په سبب د دې چې دوى له الله سره هغه شى شریك كړى دى چې هغه (الله) پر دې هېڅ دلیل نه دى نازل كړى، او د دوى استوګنځی اور دى او هغه د ظالمانو بد اوسېدنځی دى');
INSERT INTO "abulsalam" VALUES(3,152,'3:152',' او خامخا یقینًا الله تاسو ته خپله وعده رښتیا كړله، كله چې تاسو هغوى وژل د هغه (الله) په حكم سره، تر دې چې تاسو بې زړه شوئ او د كار په باره كې مو په خپلو كې اختلاف وكړ او نافرماني مو وكړه، وروسته له دې چې هغه (الله) تاسو ته هغه څه دروښودل چې تاسو خوښول، په تاسو كې ځینې هغه څوك دي چې دنیا غواړي او ځینې په تاسو كې هغه څوك دي چې اخرت غواړي، بیا هغه (الله) تاسو له هغوى نه واړولئ، د دې لپاره چې تاسو و ازمايي، او خامخا په رښتیا سره يې تاسو معاف كړئ، او الله په مومنانو باندې د لوى فضل كولو والا دى');
INSERT INTO "abulsalam" VALUES(3,153,'3:153',' كله چې تاسو په بره په منډه روان وئ او تاسو هیچا ته مخ هم نه را اړولو او رسول ستاسو شا ته تاسو ته اوازونه كول، نو (د دې) په بدل كې يې تاسو ته غم له پاسه غم دركړ، د دې لپاره چې تاسو غمژن نشئ په هغه شي چې له تاسو نه پاتې شو او نه په هغه شي چې تاسو ته درورسېده، او الله ستاسو په عملونو ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(3,154,'3:154',' بیا يې له دغه غم نه پس پر تاسو باندې امن (اطمینان) نازل كړ، چې پركالي وه، چې ستاسو یوه ډله يې لاندې كړې وه او بله ډله وه چې یقینًا هغوى (فكرونو د) خپلو ځانونو غمژن كړي وو، په داسې حال كې چې هغوى په الله باندې ناحقه ګمان كاوه، د جاهلیت ګمان، هغوى ویل: ایا په دې كار كې مونږ له هم څه برخه شته؟ ته ووایه: بېشكه (د) كار ټول (اختیار) خاص الله لره دى، دوى په خپلو نفسونو (زړونو) كې هغه خبرې پټَوي، چې تا ته يې نه ښكاره كوي، دوى وايي: كه مونږ لره له دې كاره څه حصه وَى (،نو) مونږ به دلته نه وژل كېدلو، ته (ورته) ووایه: كه چېرې تاسو په خپلو كورونو كې وَى (،نو) هغه كسان به خامخا خپلو د غورځېدو ځایونو ته راوتلي وو، د چا چې وژل كېدل لیكل شوي وو (او دا كار الله تعالٰی وكړ) د دې لپاره چې الله هغه څه وازمايي چې ستاسو په سینو كې دي او ښه پاك كړي هغه څه چې ستاسو په زړونو كې دي او الله ښه عالم دى پر هغو خبرو چې په سینو كې پرتې دي');
INSERT INTO "abulsalam" VALUES(3,155,'3:155',' بېشكه هغه كسان چې له تاسو نه په هغې ورځې كې (له جنګ نه) وګرځېدل (كله) چې دوه ډلې یو بل ته مخامخ شولې، بېشكه همدا خبره ده چې شیطان دوى د خپلو ځينو (د ګناه د) عملونو په وجه ښُویولي وو، او خامخا یقینًا الله دوى ته معافي كړې ده، بېشكه الله ډېر بخښونكى، ډېر تحمل والا دى');
INSERT INTO "abulsalam" VALUES(3,156,'3:156',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د هغو كسانو په شان مه كېږئ، چې كافران شوي دي او د خپلو وروڼو په باره كې به يې ویل، كله چې به هغوى په ځمكه كې په سفر تلل، یا به هغوى غزا كوونكي وو: كه چېرې دوى له مونږ سره وَى (،نو) نه به مړه شوي وو او نه به وژل شوي وو، لپاره د دې چې الله (د دوى) دا (ګمان) د دوى په زړونو كې (د) حسرت (او افسوس باعث) وګرځوي او الله ژوندي كول او مړه كول كوي، او الله هغو عملونو لره، چې تاسو يې كوئ، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(3,157,'3:157',' او قسم دى، خامخا كه تاسو د الله په لار كې ووژل شئ، یا مړه شئ(نو) خامخا د الله له طرفه بخښنه او رحمت ډېر غوره دى له هغو (مالونو) نه چې دوى يې جمع كوي');
INSERT INTO "abulsalam" VALUES(3,158,'3:158',' او خامخا كه چېرې تاسو مړه شئ، یا ووژل شئ، (نو) خامخا خاص د الله په طرف به تاسو راجمع كړى شئ ');
INSERT INTO "abulsalam" VALUES(3,159,'3:159',' نو (يواځې) د الله د رحمت په سبب ته د دوى لپاره نرم يې او كه چېرې ته بدخویَه، سخت زړى وَى (،نو) خامخا دوى به ستا له چاپېره نه خواره شوي وو، نو دوى ته معافي كوه، او د دوى لپاره بخښنه غواړه، او له دوى سره په كار كې مشوره كوه، بیا چې كله ته پخه اراده وكړې، نو په الله باندې توكل كوه، یقینًا الله له توكل كوونكو سره مینه كوي');
INSERT INTO "abulsalam" VALUES(3,160,'3:160',' كه چېرې الله ستاسو مدد وكړي، نو بیا پر تاسو باندې هیڅوك غالب راتلونكى نشته او كه چېرې تاسو (بې مدده) پرېږدي، نو څوك دى هغه كس چې له هغه (الله) نه بعد به ستاسو مدد وكړي؟ او پس لازم ده چې مومنان خاص په الله باندې توكل وكړي ');
INSERT INTO "abulsalam" VALUES(3,161,'3:161',' او له نبي نه دا نشي كېدلى چې هغه دې خیانت وكړي او چا چې خیانت وكړ، نو د قیامت په ورځ به له خیانت سره راځي، بیا به هر نفس ته د هغه د عمل پوره پوره بدله وركړى شي او پر هغوى به ظلم ونه كړى شي');
INSERT INTO "abulsalam" VALUES(3,162,'3:162',' ایا نو هغه څوك چې د الله د رضامندۍ پیروي كوي، د هغه كس په شان دى چې د الله له جانبه په (راغلي) غضب سره واپس شي؟ او د هغه استوګنه جهنم دى او هغه د ورتلو بد ځای دى');
INSERT INTO "abulsalam" VALUES(3,163,'3:163',' د الله په نیز دوى د بېلو بېلو درجو (والا) دي او الله ښه لیدونكى دى هغو عملونو لره چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(3,164,'3:164',' خامخا په رښتیا سره الله په مومنانو باندې احسان كړى دى كله چې يې دوى ته د همدوى له نفسونو (جنس) څخه یو رسول راولېږه، چې دوى ته د هغه ایتونه تلاوت كوي او دوى پاكوي او دوى ته د كتاب او حكمت (سنت) تعلیم وركوي او یقینًا دوى له دې نه مخكې په ښكاره ګمراهۍ كې وو');
INSERT INTO "abulsalam" VALUES(3,165,'3:165',' ایا كله چې تاسو ته (په احد كې) یو مصیبت درورسېده، چې یقینًا تاسو د هغه دوه چنده (مصیبت كفارو ته) رسولى و، تاسو وویل: دا له كومه (راغى؟) ته ووایه: دا ستاسو د نفسونو له طرفه دى، بېشكه الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(3,166,'3:166',' او تاسو ته چې څه مصیبت درورسېده، په هغې ورځې كې چې دوه ډلې یو بل ته مخامخ شولې، نو (دا) د الله په حكم سره و او د دې لپاره چې هغه (الله) مومنان معلوم كړي');
INSERT INTO "abulsalam" VALUES(3,167,'3:167',' او د دې لپاره چې هغه (الله) هغه كسان معلوم كړي چې نفاق يې كړى دى او دوى ته وویل شو چې راځئ د الله په لار كې جنګ وكړئ، یا دفاع وكړئ، دوى ویل:كه چېرې مونږه په جنګ پوهېدلى، (نو) مونږ به خامخا ستاسو پیروي كړې وه، دوى په دغې ورځ كې ایمان ته د نژدې كېدو په نسبت كفر ته زیات نژدې وو، دوى په خپلو خولو سره هغه خبرې كوي چې په زړونو كې يې نشته، او الله پر هغه څه ښه عالم دى، چې دوى يې پټوي');
INSERT INTO "abulsalam" VALUES(3,168,'3:168',' هغه كسان چې د خپلو وروڼو په باره كې يې وویل او خپله (له جنګ نه) ناست وو: كه چېرې هغوى زمونږ خبره منلې وى، (نو) نه به وژل كېدل، ته (ورته) ووایه: نو تاسو له خپلو ځانونو نه مرګ لرې كړئ كه چېرې تاسو رښتیني یئ');
INSERT INTO "abulsalam" VALUES(3,169,'3:169',' او د الله په لاره كې پر وژل شویو كسانو باندې هېڅكله د مړیو ګمان مه كوه، بلكې (هغوى) ژوندي دي، د خپل رب په نیز هغوى ته رزق وركول كېږي');
INSERT INTO "abulsalam" VALUES(3,170,'3:170',' په دې حال كې چې ډېر خوشحاله دي په هغه شي باندې چې الله دوى ته له خپله فضله وركړى دى او د هغو كسانو په باره كې ډېر خوشحالېږي چې له دوى نه وروسته دوى پسې لا نه دي وررسېدلي، دا چې په دوى باندې هېڅ وېره نشته او نه دوى غمژن كېږي');
INSERT INTO "abulsalam" VALUES(3,171,'3:171',' ډېر خوشحالېږي د الله له جانبه په نعمت او فضل سره او په دې چې بېشكه الله د مومنانو اجر نه ضايع كوي');
INSERT INTO "abulsalam" VALUES(3,172,'3:172',' هغه كسان، چې د الله او د رسول حكم يې ومانه، وروسته له دې چې دوى ته زخم (هم) رسېدلى و، په دوى كې د هغو كسانو لپاره چې نېكي يې كړې ده او پرهېزګاره شوي دي بېخي لوى اجر دى');
INSERT INTO "abulsalam" VALUES(3,173,'3:173',' (دوى) هغه كسان دي چې دوى ته (ځینو) خلقو وویل: بېشكه (د مكې) خلق په رښتیا سره ستاسو (سره د جنګ) لپاره راټول شوي دي، نو تاسو له هغوى نه ووېرېږئ، نو (دې خبرې) د هغوى ایمان (نور هم) زیات كړ او ويې ويل:زمونږ لپاره الله كافي دى او هغه ښه كار جوړوونكى دى');
INSERT INTO "abulsalam" VALUES(3,174,'3:174',' نو دوى د الله له جانبه په نعمت او فضل سره بېرته راوګرځېدل، په دې حال كې چې ونه رسېد دوى ته هېڅ تكلیف او دوى د الله د رضامندۍ تابع شول او الله د بېخي لوى فضل والا دى');
INSERT INTO "abulsalam" VALUES(3,175,'3:175',' بېشكه همدا خبره ده چې دغه شیطان دى، چې خپل دوستان وېروي، نو تاسو له هغوى نه مه وېرېږئ او له ما نه وېرېږئ كه چېرې تاسو مومنان یئ');
INSERT INTO "abulsalam" VALUES(3,176,'3:176',' او تا لره دې هغه كسان غمژن نه كړي چې په كفر كې یو تر بله تلوار كوي، بېشكه دوى كله هم الله ته څه ضرر نشي رسولى، الله اراده لري چې د دوى لپاره په اخرت كې هېڅ برخه جوړه نه كړي او د دوى لپاره بېخې لوى عذاب دى');
INSERT INTO "abulsalam" VALUES(3,177,'3:177',' بېشكه هغه كسان چې د ایمان په بدله كې يې كفر اخيستى دى، دوى به كله هم الله ته څه ضرر ونه رسولى شي او د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(3,178,'3:178',' او كافران شوي خلق دې له سره دا ګمان ونه كړي چې بېشكه مونږ چې دوى ته مهلت وركوو، دا دوى د ځانونو لپاره ډېر ښه دى، یقینًا خبره دا ده چې مونږ دوى ته مهلت د دې لپاره وركوو چې دوى په ګناه كې مخ په وړاندې لاړ شي او د دوى لپاره سپكوونكى عذاب دى ');
INSERT INTO "abulsalam" VALUES(3,179,'3:179',' الله له سره داسې نه دى چې مومنان په هغه حال باندې پرېږدي په كوم باندې چې تاسو (اوس) یئ، تر دې چې ناپاك له پاكه بېل كړي، او الله كله هم داسې نه دى چې تاسو په غیبو باندې خبر كړي او لېكن الله د خپلو رسولانو (له ډلې) نه غوره كوي چا لره چې وغواړي، نو پر الله او د هغه په رسولانو باندې ایمان راوړئ او كه چېرې تاسو ایمان راوړ او پرهېزګاره شوئ، نو تاسو لپاره بېخي لوى اجر دى');
INSERT INTO "abulsalam" VALUES(3,180,'3:180',' او هغه كسان، چې په هغه څه سره شومتیا (بخل) كوي چې الله دوى ته له خپله فضله وركړى دى (دوى دې) له سره دا ګمان ونه كړي چې دا (شومتیا كول) د دوى لپاره ډېر ښه دي، بلكې دا د دوى لپاره ډېر بد دي، ژر ده چې د قیامت په ورځ به دوى ته هغه شى امېل كړى شي، چې په هغه سره دوى شومتیا كوله، او خاص الله لپاره د اسمانونو او ځمكې میراث دى او الله پر هغو كارونو چې تاسو يې كوئ؛ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(3,181,'3:181',' خامخا یقینًا الله د هغو خلقو خبره واورېدله چې ويې ويل:بېشكه الله فقیر دى او مونږه غنیان یو، خامخا به مونږ لیكو د دوى دا خبره او نبیانو لره د دوى ناحقه وژل، او مونږ به (دوى ته) ووایو: تاسو سخت سوزوونكى عذاب وڅكئ ');
INSERT INTO "abulsalam" VALUES(3,182,'3:182',' دا (عذاب) په سبب د هغه (بدعمل) دى چې ستاسو لاسونو مخكې لېږلى دى، او ځكه چې یقینًا الله پر بنده ګانو هېڅ ظلم كوونكى نه دى ');
INSERT INTO "abulsalam" VALUES(3,183,'3:183',' (دوى) هغه كسان دي چې وايي: بېشكه الله مونږ ته حكم كړى دى چې مونږ به په هېڅ یو پېغمبر باندې ایمان نه راوړو تر هغه پورې چې هغه مونږ ته یوه داسې قرباني راوړي چې اور يې خوري، ته (ورته) ووایه: یقینًا تاسو ته له ما نه مخكې رسولانو ښكاره معجزې راوړې وې او هغه شى هم چې تاسو يې وایئ، بیا ولې تاسو هغوى قتل كړل؟ كه چېرې تاسو رښتیني یئ ');
INSERT INTO "abulsalam" VALUES(3,184,'3:184',' نو كه دوى تا دروغژن وګڼي، نو یقینًا له تا نه مخكې (هم) رسولان دروغژن ګڼل شوي دي، هغوى ښكاره دلیلونه راوړي وو او واړه كتابونه او رڼا كوونكى كتاب (يې راوړى و)');
INSERT INTO "abulsalam" VALUES(3,185,'3:185',' هر ساه لرونكى د مرګ (مزه) څكونكى دى او یقینًا همدا خبره ده چې تاسو ته به د قیامت په ورځ ستاسو ثوابونه (پوره) پوره دركړى شي، نو څوك چې له اور (د جهنم) نه لرې كړى شو او جنت ته داخل كړى شو، نو یقینًا هغه كامیاب شو او دنيايي ژوند يواځې د غولېدلو سامان دى ');
INSERT INTO "abulsalam" VALUES(3,186,'3:186',' خامخا به تاسو په خپلو مالونو او خپلو ځانونو كې وازمایل شئ او خامخا به تاسو له هغو كسانو نه چې له تاسو نه مخكې ورته كتاب وركړى شوى دى او له هغو كسانو نه چې مشركان دي، د خفګان ډېرې زیاتې خبرې واورئ، او كه چېرې تاسو صبر وكړئ او پرهېزګاره شئ، نو یقینًا دا د همت له كارونو څخه دي');
INSERT INTO "abulsalam" VALUES(3,187,'3:187',' او كله چې الله له اهلِ كتابو نه پخه وعده واخيستله چې تاسو به هرومرو خلقو ته دا (كتاب) بیانوئ او تاسو به دا نه پټوئ، نو هغوى دا(كتاب) د خپلو شاګانو شا ته وغورځاوه او د ده په بدل كې يې لږ قیمت واخيست، نو بد دى هغه شى چې دوى يې اخلي');
INSERT INTO "abulsalam" VALUES(3,188,'3:188',' ته له سره ګمان مه كوه په هغو خلقو باندې چې په خپلو كړو (ناروا كارونو) باندې خوشحالېږي او خوښوي چې په هغو (نېكو) كارونو وستايل شي چې دوى نه دي كړي، نو ته د دوى په هكله له عذاب نه د خلاصي ګمان له سره مه كوه او د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(3,189,'3:189',' او خاص د الله لپاره د اسمانونو او ځمكې بادشاهي ده او الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(3,190,'3:190',' بېشكه د اسمانونو او ځمكې په پیدا كولو كې او د شپې او ورځې په تلو راتلو كې خامخا د خالص عقل خاوندانو لپاره ډېرې نښې دي');
INSERT INTO "abulsalam" VALUES(3,191,'3:191',' هغه كسان چې الله یادوي په ولاړه او ناسته او (چې پراته وي) په خپلو اړخونو باندې او د اسمانونو او ځمكې په پیدا كونه كې ښه فكر كوي (او وايي:) اى زمونږه ربه! تا دا عبث نه دي پیدا كړي، تا لره پاكي ده، نو ته مونږه (د جهنم) له اور نه بچ كړه ');
INSERT INTO "abulsalam" VALUES(3,192,'3:192',' اى زمونږه ربه! بېشكه ته چې څوك اور ته داخل كړې، نو یقینًا تا هغه رسوا كړ او د ظالمانو لپاره هیڅوك مدد كوونكي نشته');
INSERT INTO "abulsalam" VALUES(3,193,'3:193',' اى زمونږه ربه! بېشكه مونږه یو اواز كوونكى واورېده، چې ایمان ته يې بلنه كوله، (او دا يې ویل) چې په خپل رب باندې ایمان راوړئ، نو مونږ ایمان راوړ، اى زمونږه ربه! نو ته مونږ ته زمونږ ګناهونه وبخښه او له مونږ نه زمونږ بدۍ لرې كړه او مونږ ته وفات راكړه، په دې حال كې چې د نېكانو له ډلې سره يو');
INSERT INTO "abulsalam" VALUES(3,194,'3:194',' اى زمونږه ربه! او مونږ ته هغه (ثوابونه) راكړه چې تا له مونږ سره د خپلو رسولانو په ژبه (د هغو) وعده كړې ده، او د قیامت په ورځ مونږه مه رسوا كوه، بېشكه ته د وعدې مخالفت نه كوې');
INSERT INTO "abulsalam" VALUES(3,195,'3:195',' نو خپل رب يې دعا قبوله كړه چې بېشكه زه په تاسو كې د هېڅ یو عمل كوونكي عمل نه ضايع كوم، نر وي او كه ښځه، ستاسو ځینې له ځینو نورو څخه دي، نو هغه كسان چې هجرت يې كړى دى او له خپلو كورونو نه وېستل شوي دي او زما په لاره كې ورته تكلیفونه رسول شوي دي او جنګونه يې كړي دي او وژل شوي دي (نو) زه به خامخا ضرور لرې (معاف) كړم له دوى نه د دوى ګناهونه او خامخا ضرور به يې داسې جنتونو ته داخل كړم چې د هغو له لاندې وَلِې بهېږي، چې د الله له طرفه نه بدله ده او هم دا الله دى چې له هغه سره ښه بدله ده');
INSERT INTO "abulsalam" VALUES(3,196,'3:196',' (اى اورېدونكیه!) په ښارونو كې د كافرانو تلل راتلل دې تا تېر نه باسي (دوكه نه كړي)');
INSERT INTO "abulsalam" VALUES(3,197,'3:197',' (دا) ډېره لږه فايده ده، بیا د هغو اوسېدنځای جهنم دى او هغه بده استوګنه ده');
INSERT INTO "abulsalam" VALUES(3,198,'3:198',' خو هغه كسان چې له خپل رب نه ووېرېدل د هغوى لپاره داسې جنتونه دي چې د هغو له لاندې وَلِې بهېږي، چې دوى به په هغو كې همېشه وي، چې د الله له طرف نه مېلمستیا ده، او هغه څه چې له الله سره دي (هغه) د نېكانو خلقو لپاره ډېر غوره دي');
INSERT INTO "abulsalam" VALUES(3,199,'3:199',' او بېشكه ځینې له اهلِ كتابو څخه خامخا هغه څوك دي چې پر الله ایمان راوړي او پر هغه (كتاب) چې تاسو ته نازل كړى شوى دى او پر هغو (كتابونو) چې دوى ته نازل كړى شوي دي، چې الله ته عاجزي كوونكي دي، د الله د ایتونو په بدل كې لږ قیمت نه اخلي، دغه كسان (چې دي) د دوى لپاره د خپل رب په نیز د دوى اجر دى، بېشكه الله ډېر ژر حساب كوونكى دى');
INSERT INTO "abulsalam" VALUES(3,200,'3:200',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو صبر كوئ او په صبر ټینګ اوسئ او په (سرحدي) مورچو كې محكم اوسئ او له الله نه وېرېږئ، لپاره د دې چې تاسو كامیاب شئ');
INSERT INTO "abulsalam" VALUES(4,1,'4:1','اى خلقو! له خپل رب نه ووېرېږئ هغه چې تاسو يې له یو نفس نه پیدا كړي یئ او له هغه نه يې د هغه جوړه پیدا كړې ده، او له دغو دواړو نه يې (په ځمكه كې) ډېر سړي او ښځې خواره كړل، او له هغه الله نه ووېرېږئ چې تاسو له یو بل نه په(نامه د) هغه سره سوال كوئ او د خپلولۍ له پرېكولو نه (ووېرېږئ)، بېشكه الله پر تاسو باندې ښه ساتونكى (نګهبان) دى');
INSERT INTO "abulsalam" VALUES(4,2,'4:2',' او یتیمانو ته د هغوى مالونه وركړئ او ناپاكه څیز د پاك په بدل كې مه اخلئ او د هغوى مالونه له خپلو مالونو سره (په یوځای كولو) مه خورئ، یقینًا دا له همېشه بېخي لویه ګناه ده');
INSERT INTO "abulsalam" VALUES(4,3,'4:3',' او كه چېرې تاسو وېرېدلئ له دې چې د یتیمانو په حق كې به انصاف ونه كړى شئ، بیا نو له (نورو) هغو ښځو سره نكاح وكړئ چې ستاسو خوښې وي، دوه دوه، یا درې درې، یا څلور څلور، نو كه تاسو وېرېدلئ چې انصاف به ونه كړى شئ، بیا نو له یوې (اصیلې ښځې) سره (نكاح وكړئ)، یا وینځه (واخلئ)، دا (طریقه) ډېره نژدې ده دې ته چې تاسو به ظلم ونه كړئ');
INSERT INTO "abulsalam" VALUES(4,4,'4:4',' او ښځو ته د هغوى مهرونه په خوشحالۍ سره وركړئ، بیا كه دوى تاسو ته له هغه (مهر) نه څه شى د زړه په خوشحالۍ سره دركړي، نو هغه خورئ، چې ښه خوندوَر حلال (او) بې ضرره دى');
INSERT INTO "abulsalam" VALUES(4,5,'4:5',' او ناپوهو كسانو ته د هغوى مالونه مه ورسپارئ، هغه چې الله تاسو لپاره د ګذران سبب ګرځولي دي، او له د غو (مالونو) نه تاسو هغوى ته خوراك وركوئ او جامه وركوئ او هغوى ته ښه خبره كوئ');
INSERT INTO "abulsalam" VALUES(4,6,'4:6',' او تاسو یتیمان ازمایئ تر هغه پورې چې دوى (حد د) نكاح ته ورسېږي، نو كه تاسو له هغوى نه څه هوښیارتیا معلومه (محسوسه) كړه، بیا نو هغوى ته خپل مالونه وروسپارئ، او تاسو دا (د یتیمانو مالونه) په اسراف سره او جلتۍ سره مه خورئ، چې دوى به لوى شي (نو وابه يې خلي) او څوك (د یتیم ولي) چې مالداره وي، نو هغه دې (له دې ماله) ځان ښه بچ ساتي او څوك چې فقیر وي، نو هغه دې له دستور سره سم خوري، بیا چې كله هغوى ته خپل مالونه وركوئ، نو په دې (وركړه) باندې ګواهان ونیسئ او الله كافي دى، چې ښه حساب اخيستونكى دى');
INSERT INTO "abulsalam" VALUES(4,7,'4:7',' د نارینه وو لپاره حصه شته له هغه (مال) نه چې مور و پلار او نژدې خپلوان يې پرېږدي، او د ښځو لپاره (هم) حصه شته له هغه (مال) نه چې مور و پلار او نژدې خپلوان يې پرېږدي، له هغه (مال) نه چې لږ وي، یا ډېر وي، مقرر كړى شوې برخه ده');
INSERT INTO "abulsalam" VALUES(4,8,'4:8',' او كله چې (د میراث) تقسیم ته خپلوان او یتیمان او مسكینان حاضر شي، نو له هغه (مال) نه دوى ته (هم څه) وركړئ او دوى ته ښه خبره وكړئ');
INSERT INTO "abulsalam" VALUES(4,9,'4:9',' او هغه كسان دې ووېرېږي چې كه چېرې دوى له خپل ځان (له مرګ) نه وروسته كمزوري بچي پرېږدي، (نو) دوى به په هغو باندې وېرېږي، نو لازم ده چې له الله نه دې ووېرېږي او سمه نېغه خبره دې وكړي');
INSERT INTO "abulsalam" VALUES(4,10,'4:10',' بېشكه هغه كسان چې د یتیمانو مالونه په ظلم سره خوري، یقینًا همدا خبره ده چې هغوى په خپلو خېټو كې اور خوري او ژر ده چې لمبې وهونكي اور كې به داخل شي');
INSERT INTO "abulsalam" VALUES(4,11,'4:11',' الله تاسو ته ستاسو د اولاد په باره كې وصیت كوي (چې) د یو نر اولاد لپاره د دوه جینكو (لوڼو) د حصې په مثل (حصه) ده، نو كه دوى (دوه يا) له دوو نه زياتې جينكۍ وي، نو د دوى لپاره دوه په درې برخې دي د هغه (مال) چې ده پرېښى وي او كه چېرې يواځې یوه جینۍ وي، نو د هغې لپاره نیم (مال) دى او د هغه (مړي) مور و پلار لپاره په دواړو كې د هر یو لپاره شپږمه حصه ده له هغه (مال) نه چې هغه (مړي) پریښى وي، كه چېرې د هغه (مړي) لپاره اولاد وي او كه د هغه اولاد نه وي او د هغه وارثان د هغه مور و پلار وي، نو د هغه د مور لپاره درېمه حصه ده، نو كه چېرې د هغه (مړي) لپاره وروڼه وي، نو د هغه د مور لپاره شپږمه حصه ده (دا) پس له (اداكولو د) وصیت نه، چې هغه (مړي) يې وصیت كړى وي یا له قرض نه، ستاسو پلرونه او ستاسو زامن (چې دي)، تاسو نه پوهېږئ چې په دوى كې تاسو ته د فايدې په لحاظ سره څوك ډېر نژدې دي، دا مقرره برخه ده د الله له جانبه، بېشكه الله ښه پوه، ښه حكمت والا دى ');
INSERT INTO "abulsalam" VALUES(4,12,'4:12',' او تاسو لپاره (اى سړیو!) نیمه حصه ده له هغه (مال) نه چې ستاسو ښځې يې پرېږدي كه چېرې د هغوى اولاد نه وي، نو كه چېرې د هغوى اولاد وي، نو تاسو لپاره څلورمه حصه ده له هغه (مال) نه چې هغوى يې پرېږدي، پس له (ادا كولو د) وصیت نه چې هغوى په دې سره وصیت كوي، یا له قرض نه، او د هغو (ښځو) لپاره څلورمه حصه ده له هغه (مال) نه چې تاسو (خاوندان) يې پرېږدئ كه چېرې ستاسو اولاد نه وي، نو كه چېرې ستاسو لپاره وي، نو د هغو (ښځو) لپاره اتمه حصه ده له هغه (مال) نه چې تاسو يې پرېږدئ، پس له (اداكولو د) وصیت نه چې تاسو د هغه وصیت كوئ، یا له قرض نه او كه یو سړى، یا ښځه چې میراث ترې وړل كېږي، كلاله (بې پلاره او بې اولاده) وي او د هغه (یا د هغې) (مورنى) ورور، یا خور وي، نو په دې دواړو كې هر یو ته شپږمه حصه ده، بیا كه هغوى (د كلاله خویندې، وروڼه) له دې (شمېر) نه زیات وو، نو هغوى په درېمه حصه كې (ټول) شریك دي، پس له وصیته، چې د هغه وصیت كولى شي، يا له قرض نه، چې ضرر رسوونكى نه وي (بل ته په دې وصیت سره) دا د الله له جانبه وصیت دى، او الله ښه پوه، ښه تحمل ولا دى');
INSERT INTO "abulsalam" VALUES(4,13,'4:13',' دا د الله حدونه دي او چا چې د الله او د هغه د رسول اطاعت وكړ، (نو) هغه (الله) به يې داسې جنتونو ته ننباسي چې د هغو له لاندې وَلې بهېږي، چې دوى به په هغو كې همېشه وي او دا بېخي لویه كامیابي ده');
INSERT INTO "abulsalam" VALUES(4,14,'4:14',' او چا چې د الله او د هغه د رسول نافرماني وكړه او د هغه (الله) له حدونو نه مخكې تېر شو، نو هغه (الله) به يې اور ته داخل كړي، چې په هغه كې به همېشه وي او د هغه لپاره سپكوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(4,15,'4:15',' او ستاسو له ښځو ځنې چې كومې بدكاري (زنا) وكړي، نو تاسو پر هغوى باندې له خپلو (مسلمانانو) نه څلور (سړي) ګواهان ونیسئ، نو كه چېرې هغوى ګواهي ووايي، بیا دا ښځې په كورونو كې پابندې وساتئ تر هغه وخته پورې چې مرګ يې فنا كړي، یا الله د دوى لپاره څه بله لار راوباسي');
INSERT INTO "abulsalam" VALUES(4,16,'4:16',' او په تاسو كې چې كوم دوه كسان دا (بدكاري) كوي، نو هغوى دواړو ته تكلیف ورسوئ، بیا كه دوى توبه ووېسته او د ځان اصلاح يې وكړه، نو تاسو له دوى نه مخ واړوئ، بېشكه الله ډېر توبه قبلوونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(4,17,'4:17',' اصل خبره دا ده چې په الله باندې د توبې د قبلولو حق د هغه چا لپاره دى، چې په ناپوهۍ سره ګناه كوي، بیا ډېر ژر توبه وباسي، نو دغه خلق دي چې الله د هغوى توبه قبلوي او الله ښه پوه، ښه حكمت والا دى ');
INSERT INTO "abulsalam" VALUES(4,18,'4:18',' او د هغه چا لپاره توبه نشته چې لګیا وي ګناهونه كوي، تر دې چې كله په دوى كې یو كس ته مرګ راشي، (بیا د ځنكدن په وخت كې) وايي:بېشكه زما اوس توبه ده او نه د هغو كسانو لپاره (توبه شته) چې د كافرتوب په حالت كې مري، دغه كسان چې دي؛ مونږ د دوى لپاره ډېر دردوونكى عذاب تیار كړى دى');
INSERT INTO "abulsalam" VALUES(4,19,'4:19',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو لپاره حلال نه دي چې ښځې په زور سره په میراث كې واخلئ او په دې غرض دوى مه منع كوئ (مه تنګوئ) چې تاسو (له دوى نه) له هغه (مال) نه څه برخه (بېرته) واخلئ چې تاسو دوى ته وركړى و مګر دا چې دوى ښكاره بدكار وكړي (نو بیا يې تنګولى شئ) او له دوى سره په ښه طریقه ګذران كوئ، نو كه دوى ستاسو خوښې نه وي، نو كېداى شي چې تاسو به یو شى بد ګڼئ او الله به په هغه كې ډېر زیات خیر ایښى وي');
INSERT INTO "abulsalam" VALUES(4,20,'4:20',' او كه چېرې تاسو غواړئ چې د یوې ښځې په ځای بله ښځه بَدَله راولئ او تاسو په دوى كې یوې ته ډېرۍ مالونه وركړي وي، نو له هغه (مال) نه هېڅ شى هم مه اخلئ، ایا تاسو به هغه (مال) اخلئ په بهتان لګولوسره او په ښكاره ګناه سره؟');
INSERT INTO "abulsalam" VALUES(4,21,'4:21',' او تاسو به هغه (مال) څنګه واپس اخلئ، حال دا چې په تحقیق سره تاسو له یو بل سره یو ځای شوي یئ او هغو (ښځو) له تاسو نه ډېره پخه وعده اخيستې ده!');
INSERT INTO "abulsalam" VALUES(4,22,'4:22',' او له كومو ښځو سره چې ستاسو پلرونو نكا ح كړې ده، تاسو له هغوى سره نكاح مه كوئ، لېكن هغه چې مخكې تېر شوي دي، بېشكه دا له همېشه د ډېرې بې حیایۍ او د (الله د) غضب كار دى او بده لار ده');
INSERT INTO "abulsalam" VALUES(4,23,'4:23',' حرامې كړى شوې دي پر تاسو باندې ستاسو میندې، او ستاسو لوڼه، او ستاسو خویندې او ستاسو تروریانې (د پلار خویندې) او ستاسو خاله ګانې (د مور خویندې) او ورېرې او خورځې او ستاسو هغه میندې چې تاسو ته يې تى دركړى دى او ستاسو رضاعي خویندې او ستاسو د ښځو میندې (سكه خواښې) او ستاسو پركټیانې، چې (عُمومًا) ستاسو په پرورش كې وي، چې ستاسو له هغو ښځو نه (پیدا وي) چې تاسو ورسره كوروالى كړى وي او كه تاسو ورسره كوروالى نه وي كړى، نو (بیا) پر تاسو باندې څه ګناه نشته، او ستاسو د خپلو حقیقي زامنو ښځې (اینګوریانې) او دا چې تاسو د دوه خویندو تر مینځه جمع وكړئ (په یو وخت كې دواړه په نكاح كې وساتئ) لېكن هغه چې مخكې تېر شوي دي، بېشكه الله له همېشه ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(4,24,'4:24',' او (پر تاسو باندې حرامې كړى شوې دي) مړوښې ښځې مګر (حرامې نه دي) هغه چې تاسو يې مالكان شئ (ستاسو وینځې شي) دا پر تاسو باندې (د) الله لازم كړى (قانون) دى او چې له دغو (ښځو) نه ماسِوا دي هغه تاسو لپاره حلالې دي، (په دې شرط) چې د خپلو مالونو په ذریعه يې غواړئ، چې عفت غوښتونكي یئ، بدكاري كوونكي نه یئ، بیا چې تاسو له هغوى نه د ښځه والي فايده واخيستله، نو هغوى ته خپل مهرونه وركړئ، چې مقرر شوي دي او (اى خاوندانو او ښځو!) پر تاسو باندې هېڅ حرج نشته په هغه څه كې چې (د مهر) له مقررولو نه پس تاسو په خپلو كې په هغه (كې په كمي، یا زیاتي) باندې راضي شئ، بېشكه الله له ازله پر هر څه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(4,25,'4:25',' او په تاسو كې چې څوك له اصیلو مومنو ښځو سره د نكاح كولو مالي وَس نه لري، نو له هغو مومنو وینځو سره (دې نكاح وكړي) چې تاسو يې مالكان شوي يئ او الله ستاسو پر ایمان ښه عالم دى، ستاسو ځینې له ځینو نورو دي، نو د هغو (وینځو) د مالكانو په اجازت سره له هغوى سره نكاح وكړئ او هغوى ته خپل مهرونه له دستور سره سم وركړئ، (په دې شرط) چې پاك لمنې وي، بدكارې نه وي او نه پټ یاران نیونكې وي، بیا چې كله دوى مړوښې شوې، نو كه بدكاري (زنا) يې وكړه، نو په دوى باندې د اصیلو ښځو د سزا نیمايي (سزا) ده. دا (له وینځې سره د نكاح كولو اساني) په تاسو كې د هغه چا لپاره ده چې د زنا اندېښنه لري او كه تاسو (له وینځې سره له نكاح نه) صبر وكړئ (نو دا) تاسو لپاره غوره دي او الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(4,26,'4:26',' الله غواړي چې تاسو ته (حكمونه) ښه واضحه كړي او تاسو ته د هغو خلقو لارې وښیي چې له تاسو نه مخكې وو، او ستاسو توبه قبوله كړي (تاسو ته په خپل رحمت سره توجه وكړي) او الله ښه عالم، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(4,27,'4:27',' الله غواړي تاسو ته توجه وكړي (په خپل رحمت سره) او هغه كسان، چې د خواهشاتو تابع كېږي، (هغوى) غواړي چې تاسو (له حقې لارې نه) ډېر زیات كاږه شئ،');
INSERT INTO "abulsalam" VALUES(4,28,'4:28',' او الله غواړي چې پر تاسو تخفیف (اساني) راولي او انسان ډېر كمزورى پیدا كړى شوى دى');
INSERT INTO "abulsalam" VALUES(4,29,'4:29',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو په خپل مینځ كې د یو بل مالونه په باطله (ناحقه طریقې) سره مه خورئ مګر داچې تجارت وي، چې ستاسو په خپل مینځي رضامندۍ سره وي او تاسو خپل ځانونه مه وژنئ، بېشكه الله پر تاسو باندې بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(4,30,'4:30',' او چا چې دا (كارونه) وكړل، په دې حال كې چې زیاتي كوونكى او ظلم كوونكى و، نو ژر به مونږ دغه (كس) اور ته داخل كړو او دا (كار) الله ته ډېر اسان دى');
INSERT INTO "abulsalam" VALUES(4,31,'4:31',' كه چېرې تاسو له هغو غټو ګناهونو نه ځان وساتئ، چې تاسو ترې منع كولى شئ (، نو) مونږ به له تاسو نه ستاسو (واړه) ګناهونه لرې كړو او ډېر د عزت ځاى ته به مو داخل كړو');
INSERT INTO "abulsalam" VALUES(4,32,'4:32',' او د هغه شي ارزو مه كوئ چې په هغه سره الله ستاسو ځینې په ځینو نورو غوره ګرځولي دي، د سړيو لپاره د خپل عمل حصه ده او د ښځو لپاره د خپل عمل برخه ده او له الله نه د هغه د فضل سوال كوئ، بېشكه الله پر هر شي ښه پوه دى');
INSERT INTO "abulsalam" VALUES(4,33,'4:33',' او د هر یو (سړي او ښځې) لپاره مونږ وارثان مقرر كړي دي له هغه (مال) نه چې مور و پلار او نژدې خپلوانو پریښى وي او هغه كسان چې له هغوى سره ستاسو معاهده شوې ده، نو تاسو هغوى ته د هغوى حصه وركړئ، بېشكه الله په هر شي باندې ګواه دى');
INSERT INTO "abulsalam" VALUES(4,34,'4:34',' سړي په ښځو باندې نګرانان دي په سبب د دې چې الله د دوى ځینې په ځینو نورو باندې غوره كړي دي او په سبب د دې چې دوى (سړي) له خپلو مالونو نه انفاق كوي، نو نېكې ښځې اطاعت كوونكې دي او د الله په ساتنې سره پسې شا (د ځان او مال) حفاظت كوونكې دي. او هغه ښځې چې تاسو د هغوى له نافرمانۍ نه وېرېږئ، نو دوى ته نصیحت وكړئ او د ملاستې په ځایونو كې ترې جدا شئ او ويې وهئ، نو كه ستاسو خبره يې ومنله، نو (بیا) د دوى په خلاف بله لار مه لټوئ، بېشكه الله ډېر اوچت، ډېر لوى دى');
INSERT INTO "abulsalam" VALUES(4,35,'4:35',' او كه تاسو د دغو دواړو په مینځ كې د مخالفت وېره لرئ، نو یو حَكَم (مینځګړى) د هغه (سړي) له خاندان نه مقرر كړئ او یو مینځګړى د هغې (ښځې) له كورنۍ نه، كه دوى دواړه (ښځه، خاوند، یا ثالثان) د څه اصلاح اراده ولري، (نو) الله به د دې دواړو په مینځ كې موافقت (جوړښت) راولي، بېشكه الله له همېشه ښه پوه، ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(4,36,'4:36',' او تاسو د الله عبادت كوئ او له هغه سره هېڅ شى مه شریكوئ او له مور و پلار سره ښېګڼه كوئ او له خپلوانو سره او یتیمانو او مسكینانو سره او له خپلوان ګاونډي سره او له لرې يا اجنبي ګاونډي سره او د څنګ له ملګري سره او له مسافر سره او له خپلو وینځو، مریانو سره (ښېګڼه كوئ) بېشكه الله نه خوښوي هغه چا لره چې كبرجن، ډېر فخر كوونكى وي');
INSERT INTO "abulsalam" VALUES(4,37,'4:37',' هغه كسان چې شومتیا كوي او (نورو) خلقو ته د شومتیا كولو حكم كوي او هغه څه پټوي چې الله دوى ته له خپله فضله وركړي دي او مونږه د كافرانو لپاره سپكوونكى عذاب تیار كړى دى');
INSERT INTO "abulsalam" VALUES(4,38,'4:38',' او (نه) هغه كسان (خوښوي) چې خلقو ته د ځان ښودلو لپاره خپل مالونه خرچ كوي او پر الله او د اخرت پر ورځ ایمان نه راوړي او هغه څوك چې د هغه ملګرى شیطان وي، نو هغه بد ملګرى دى');
INSERT INTO "abulsalam" VALUES(4,39,'4:39',' او دوى ته به څه نقصان و كه چېرې دوى پر الله او پر ورځ د اخرت ایمان راوړى وَى او له هغه (مال) نه يې انفاق كړى وَى چې الله دوى ته وركړى دى او الله په دوى ښه پوه دى');
INSERT INTO "abulsalam" VALUES(4,40,'4:40',' بېشكه الله د یوې ذرې په اندازه هم ظلم نه كوي او كه دا (ذره) یوه نېكي وي، نو هغه به يې دوه چنده كړي او هغه ته به له خپل جانب نه بېخي لوى اجر وركړي');
INSERT INTO "abulsalam" VALUES(4,41,'4:41',' نو څه حال به وي په هغه وخت كې كله چې مونږ له هر امت نه یو ګواه راولو او تا په دوى باندې (د) ګواه (په حیث) راولو');
INSERT INTO "abulsalam" VALUES(4,42,'4:42',' په دغه ورځ كې به هغه كسان ارزو كوي چې كافر شوي دي او د رسول نافرماني يې كړې ده، چې كاشكې په دوى باندې ځمكه (له پاسه نه) همواره كړى شي، او دوى به له الله نه هېڅ خبره هم نشي پټولى');
INSERT INTO "abulsalam" VALUES(4,43,'4:43',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د نشې په حالت كې لمانځه ته مه نژدې كېږئ، تر هغه پورې چې تاسو پوه شئ په هغه څه چې تاسو يې وایئ او نه د جنابت په حالت كې، مګر دا چې لارَوي یئ، تر دې چې تاسو غسل وكړئ او كه تاسو بېماران یئ، یا په سفر یئ، یا په تاسو كې یو كس له اودس ماتي راشي، یا تاسو له ښځو سره كوروالى وكړئ، بیا تاسو اوبه ونه مومئ، نو د ځمكې پاك مخ (خاورې وغیره) ته اراده وكړئ، پس د خپلو مخونو او (تر مړوندونو پورې د) لاسونو مسح وكړئ، بېشكه الله ډېر معافي كوونكى، ښه مغفرت كوونكى دى ');
INSERT INTO "abulsalam" VALUES(4,44,'4:44',' ایا تاسو هغه كسان نه دي كتلي (د هغوى په حال نه یئ خبر شوي) چې هغوى ته څه حصه له كتابه وركړى شوې ده، چې هغوى ګمراهي اخلي او غواړي چې تاسو (هم حقه) لاره وركه كړئ');
INSERT INTO "abulsalam" VALUES(4,45,'4:45',' او الله ستاسو په دښمنانو ښه پوه دى او الله كافي دى كارساز او الله كافي دى مدد كوونكى ');
INSERT INTO "abulsalam" VALUES(4,46,'4:46',' او ځینې له هغو كسانو نه چې یهودیان دي، هغوى له خپلو ځایونو نه كلمِې بدلوي او هغوى وايي: سَمِعْنَا وَعَصَیْنَا مونږ واورېدل او مونږ نافرماني وكړه‘‘ وَاسْمَعْ غَیْرَ مُسْمَعٍ ’’او ته واوره، په دې حال کې چې تا ته دې اورول ونشي‘‘ او رَاعِنَا زمونږ رعایت وكړه د خپلو ژبو په تاوولو سره او په دین كې د طعن(او عیب) راوښكلو لپاره، او یقینًا كه دوى ویلي وى (چې) سَمِعْنَا وَاَطَعْنَا ’’مونږ واورېدل او اطاعت مو وكړ‘‘، او اِسْمَعْ ’’واوره‘‘، وَانْظُرْنَا ’’او مونږ ته وګوره‘‘، نو دا (كلام) به د هغوى لپاره ډېر غوره او ډېر درست ساتونكى و او لېكن الله د دوى د كفر پر سبب پر دوى لعنت كړى دى، نو دوى ایمان نه راوړي مګر ډېر لږ (خلق)');
INSERT INTO "abulsalam" VALUES(4,47,'4:47',' اى هغو كسانو چې هغوى ته كتاب وركړى شوى دى! تاسو پر هغه (كتاب) ایمان راوړئ چې مونږ نازل كړى دى، چې تصدیق كوونكى دى د هغو (كتابونو) چې له تاسو سره دي، مخكې له دې چې مونږه څه مخونه محوه كړو، بیا دغه (مخونه) خپلو شاګانو ته ورواړوو، یا پرې مونږ لعنت وكړو، لكه څنګه چې مونږ لعنت كړى و د خالي د ورځې پر ملګرو، او د الله كار (خامخا) پوره كړى شوى وي');
INSERT INTO "abulsalam" VALUES(4,48,'4:48',' بېشكه الله نه بخښي دا چې له هغه سره دې (څوك) شریك كړى شي او هغه بخښي هغه (ګناه) چې له دې (شرك) نه كمه وي د چا لپاره چې وغواړي او څوك چې له الله سره شریك ونیسي، نو یقینًا هغه ډېره لویه ګناه راجوړه كړله ');
INSERT INTO "abulsalam" VALUES(4,49,'4:49',' ایا تاسو هغه كسان نه دي كتلي چې د خپلو ځانونو تزكیه (ستاينه) كوي (داسې نه ده) بلكې الله پاكوي چا لره چې وغواړي، او پر دوى به د كجورې د نري تار په برابر هم ظلم ونه كړى شي');
INSERT INTO "abulsalam" VALUES(4,50,'4:50',' ته وګوره دوى څنګه په الله باندې دروغ ورتپي او دا (دروغ) د ښكاره ګناه كېدلو لپاره كافي دي');
INSERT INTO "abulsalam" VALUES(4,51,'4:51',' ایا تا هغو كسانو ته نه دي كتلي چې هغوى ته یوه برخه له كتابه وركړى شوې وه، چې هغوى (اوس) پر بت او پر شیطان (هم) ایمان راوړي او د كافر شويو كسانو (مشركانو) په باره كې وايي: دا (كفار) له مومنانو نه زیات، سمه لارموندونكي دي');
INSERT INTO "abulsalam" VALUES(4,52,'4:52',' دا هغه كسان دي چې الله پر دوى لعنت كړى دى او په چا باندې چې الله لعنت وكړي، نو ته به كله هم د هغه لپاره مدد كوونكى ونه مومې');
INSERT INTO "abulsalam" VALUES(4,53,'4:53',' ایا د دوى لپاره په بادشاهۍ كې څه برخه شته؟ (نشته او كه وَى) نو په دغه وخت كې به دوى نورو خلقو ته نقیر (د كجورې د زړي داغ) هم نه وركوي');
INSERT INTO "abulsalam" VALUES(4,54,'4:54',' یا دوى له خلقو سره حسد كوي په هغه څه چې الله هغوى ته له خپل فضل نه وركړي دي، پس یقینًا مونږه الِ ابراهیم ته كتاب او حكمت وركړى و او مونږه هغوى ته ډېره لویه بادشاهي وركړې وه ');
INSERT INTO "abulsalam" VALUES(4,55,'4:55',' نو له دوى نه ځینې هغه دي چې پر ده يې ایمان راوړى دى او ځینې له دوى نه هغه دي چې له ده نه يې مخ اړولى دى، او د (كافرانو لپاره) جهنم كافي دى، چې لمبې وهونكى دى ');
INSERT INTO "abulsalam" VALUES(4,56,'4:56',' بېشكه هغه كسان چې زمونږ په ایتونو كافر شوي دي ژر ده چې مونږ به هغوى اور ته ننباسو، هر كله چې د هغوى څرمنې (په اور) پخې شي، مونږ به هغوى ته له هغو نه غیر نورې څرمنې وربدلې كړو، د دې لپاره چې دوى عذاب وڅكي، بېشكه الله ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(4,57,'4:57',' او هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، یقینًا مونږ به دوى داسې جنتونو ته داخل كړو چې د هغو له لاندې وَلِې بهېږي، چې دوى به په هغو كې تل ترتله، همېشه وي، د دوى لپاره په هغو كې ښې پاكې بیبیانې دي او مونږ به دوى ښه ګور سيوري ته داخل كړو');
INSERT INTO "abulsalam" VALUES(4,58,'4:58',' بېشكه الله تاسو ته د دې حكم كوي چې امانتونه خپلو حقدارو ته وسپارئ او دا چې كله تاسو د خلقو په مینځ كې فیصله كوئ، (نو) په انصاف سره فیصله كوئ، بېشكه الله تاسو ته ډېر ښه نصیحت كوي، بېشكه الله ښه اورېدونكى، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(4,59,'4:59',' اى هغو كسانو چې ایمان يې راوړى دى! د الله خبره منئ او د رسول خبره منئ او د خپلو د امر د خاوندانو، نو كه چېرې تاسو په خپلو كې د څه شي په باره كې هم نزاع (جګړه او اختلاف) وكړئ، نو هغه (شى) الله او د هغه رسول ته وګرځوئ كه چېرې تاسو په الله او په ورځ د اخرت ایمان لرئ، دا (كار) ډېر غوره دى او د انجام په لحاظ ډېر ښه دى');
INSERT INTO "abulsalam" VALUES(4,60,'4:60',' ایا تا هغه كسان نه دي كتلي چې دا دعوا كوي چې دوى پر هغه څه ایمان راوړى دى چې تا ته نازل كړى شوي دي او پر هغه څه چې له تا نه مخكې نازل كړى شوي دي، دوى غواړي چې خپلمينځي فیصلې طاغوت ته یوسي، حال دا چې دوى ته له هغه نه د انكار كولو حكم شوى دى او شیطان غواړي چې دوى بې لارې كړي، په ډېرې لرې ګمراهۍ سره ');
INSERT INTO "abulsalam" VALUES(4,61,'4:61',' او كله چې دوى ته وویل شي هغه (قرآن) ته راشئ چې الله نازل كړى دى او رسول ته( راشئ، نو) ته به منافقان وینې چې له تا نه مخ اړوي، مخ اړول');
INSERT INTO "abulsalam" VALUES(4,62,'4:62',' نو څه حال به وي (د دوى) چې كله دوى ته څه مصیبت ورسېږي په سبب د هغو (كارونو) چې د دوى لاسونو مخكې لېږلي دي، بیا تا ته راشي، چې پر الله قسمونه خوري چې زمونږ اراده يواځې ښېګڼه كول او موافقت راوستل وو');
INSERT INTO "abulsalam" VALUES(4,63,'4:63',' دا هغه كسان دي چې الله پوهېږي پر هغه څه چې د دوى په زړونو كې دي، نو ته له دوى نه مخ واړوه او دوى ته نصیحت وكړه او دوى ته د دوى د نفسونو په باره كې ښه اثر كوونكې خبره وكړه');
INSERT INTO "abulsalam" VALUES(4,64,'4:64',' او نه دى لېږلى مونږ هېڅ رسول مګر د دې لپاره چې د الله په حكم سره د هغه اطاعت وكړى شي او كه په رښتیا دوى په هغه وخت كې تا ته راغلي وَى چې دوى پر خپلو ځانونو ظلم وكړ، بیا يې له الله نه بخښنه غوښتې وَى او د دوى لپاره رسول (هم)بخښنه غوښتې وَى(، نو) خامخا دوى به الله ښه توبه قبلوونكى، بې حده رحم كوونكى موندلى و');
INSERT INTO "abulsalam" VALUES(4,65,'4:65',' پس داسې نه ده، ستا پر رب مې دې قسم وي! دوى مومنان كېدى نشي تر هغه پورې چې دوى په خپل مینځ كې په پېښو جګړو كې تا فیصله كوونكى ومني، بیا په خپلو نفسونو كې ستا د كړې فیصلې په باره كې هېڅ تنګي ونه مومي او په ښو منلو سره يې ومني');
INSERT INTO "abulsalam" VALUES(4,66,'4:66',' او كه په رښتیا سره مونږه په دوى باندې دا فرض كړي وَى چې خپل ځانونه قتل كړئ، یا له خپلو كورونو نه ووځئ (، نو) دا كار به نه و كړى مګر په دوى كې ډېرو لږو (خلقو) او كه په رښتیا دوى هغه كار كړى وَى د څه چې ورته نصیحت كېږي (، نو) دا به د دوى لپاره یقینًا ډېر غوره و، او د كلكوالي په لحاظ به ډېر سخت و');
INSERT INTO "abulsalam" VALUES(4,67,'4:67',' او په دغه وخت كې به یقینًا مونږ دوى ته له خپل جانبه ډېر لوى اجر وركړى و');
INSERT INTO "abulsalam" VALUES(4,68,'4:68',' او مونږ به یقینًا دوى په نېغه لار روان كړي وو');
INSERT INTO "abulsalam" VALUES(4,69,'4:69',' او څوك چې د الله او د (هغه د) رسول اطاعت وكړي، نو دغه كسان به له هغو خلقو سره وي پر چا باندې چې الله انعام كړى دى، چې نبیان، او صدیقین او شهیدان او صالحان دي او دغه خلق ښه ملګري دي');
INSERT INTO "abulsalam" VALUES(4,70,'4:70',' دا د الله له جانبه (پېرزویَنه او) فضل دى او الله كافي دى، ښه پوه ');
INSERT INTO "abulsalam" VALUES(4,71,'4:71',' اى هغو كسانو چې ایمان يې راوړى دى! خپلې وسلې واخلئ، نو (د جنګ لپاره) بېلې بېلې ډلې ووځئ، یا ټول په جمع ووځئ');
INSERT INTO "abulsalam" VALUES(4,72,'4:72',' او بېشكه په تاسو كې ځینې خامخا هغه دي چې ارومرو به (غزا ته په تلو كې) تاخیر كوي، او كه چېرې تاسو ته څه مصیبت ورسېږي (، نو) هغه وايي: یقینًا الله پر ما باندې پېرزوینه وكړه، چې په دغه وخت كې زه له دوى سره حاضر نه وم');
INSERT INTO "abulsalam" VALUES(4,73,'4:73',' او كه چېرې پر تاسو باندې د الله له جانبه څه فضل وشي (، نو) دوى به خامخا (په داسې انداز كې) وايي ګویاكې ستاسو په مینځ كې او د هغه په مینځ كې هېڅ دوستي (او مینه) نه وه: كاشكې زه (هم) له دوى سره وَى، نو ډېره لویه كامیابي به مې حاصله كړې وَى');
INSERT INTO "abulsalam" VALUES(4,74,'4:74',' نو (لازم ده چې) هغه كسان دې د الله په لار كې وجنګېږي چې دنيايي ژوند د اخرت په بدل كې خرڅوي (قربانوي) او څوك چې د الله په لار كې جنګ كوي، بیا هغه ووژل شي، یا (پر دښمن) غالبه شي، نو مونږ به هغه ته ژر ډېر لوى اجر وركړو');
INSERT INTO "abulsalam" VALUES(4,75,'4:75',' او پر تاسو څه شوي دي چې تاسو د الله په لاره كې نه جنګېږئ او د كمزورو سړیو او ښځو او ماشومانو (د خلاصولو) په لاره كې؟! هغه كسان چې وايي: اى زمونږه ربه! ته مونږه له دې كلي نه وباسه، چې اوسېدونكي يې ظالمان دي او ته زمونږ لپاره له خپل اړخه كوم دوست (حمایت كوونكى) مقرر كړه او ته زمونږ لپاره له خپل جانبه كوم مدد كوونكى مقرر كړه');
INSERT INTO "abulsalam" VALUES(4,76,'4:76',' هغه كسان چې ایمان يې راوړى دى، هغوى د الله په لاره كې جنګ كوي او هغه كسان چې كفر يې كړى دى، هغوى د طاغوت (شیطان) په لاره كې جنګ كوي، نو تاسو د شیطان له دوستانو سره جنګ كوئ، بېشكه د شیطان چل (او مكر) له همېشه ضعیف دى');
INSERT INTO "abulsalam" VALUES(4,77,'4:77',' ایا تا هغه كسان نه دي كتلي، چې هغوى ته وویل شو: خپل لاسونه (له جنګ نه) بند وساتئ او لمونځ قايموئ او زكات وركوئ، نو كله چې پر هغوى باندې جنګ كول فرض كړى شول (، نو) ناڅاپه په دغه وخت كې په هغو كې یوه ډله وه چې له خلقو نه وېرېدله، له الله نه د وېرېدلو په شان، یا (له دې نه هم) زیات وېرېدل، او ويې ويل:اى زمونږه ربه! تا پر مونږ باندې جنګ كول ولې فرض كړل؟ ولې ته مونږ ته تر نژدې مودې پورې مهلت نه راكوې؟ ته (ورته) ووایه: د دنیا فايده ډېره لږه ده او اخرت ډېر غوره دى د هغه چا لپاره چې پرهېزګاره شو او پر تاسو به د كجورې د هډوكې د نرى تار (قدرې) ظلم هم ونه كړى شي');
INSERT INTO "abulsalam" VALUES(4,78,'4:78',' تاسو چې په هر ځاى كې یئ، مرګ به تاسو رالاندې كړي، اګر كه تاسو په سختو مضبوط كړى شويو قلاګانو كې يئ، او كه چېرې پر دوى څه خوشحالي راشي (، نو) وايي: دا د الله له جانبه ده او كه پر دوى څه سختي راشي (، نو) وايي: (اى نبي!) دا ستا له طرفه ده، ته (ورته) ووایه: دا هر څه (خوشحالي او تكلیف) د الله له خوا دي، نو پر دې قوم څه شوي دي چې نژدې نه دي چې پر څه خبره پوه شي');
INSERT INTO "abulsalam" VALUES(4,79,'4:79',' تا ته چې څه خوشحالي هم دررسېدلې ده، نو د الله له جانبه ده او تا ته چې څه تكلیف هم دررسېدلى دى، نو (هغه) ستا د نفس له جانبه دى او (اى نبي!) ته مونږه د خلقو لپاره رسول لېږلى يې او كافي دى الله (د) ګواه (په حیث)');
INSERT INTO "abulsalam" VALUES(4,80,'4:80',' چا چې د رسول اطاعت وكړ، نو یقینًا هغه د الله اطاعت وكړ او څوك چې (له اطاعت نه) وګرځېده، نو ته مونږه پر هغوى باندې څارونكى (نګهبان) نه يې لېږلى');
INSERT INTO "abulsalam" VALUES(4,81,'4:81',' او دوى وايي: (زمونږ كار ستا) خبره منل دي، نو كله چې ستا له خوا نه ووځي (، بیا) په دوى كې یوه ډله د شپې مشورې كوي، له هغې خبرې نه غیر چې دوى كوله، او الله لیكي هغه خبرې چې دوى يې د شپې تدبیر كوي، نو ته له دوى نه مخ واړوه او پر الله باندې توكل وكړه او الله كافي دى كارساز');
INSERT INTO "abulsalam" VALUES(4,82,'4:82',' ایا نو دوى په قرآن كې غور نه كوي او كه چېرې دا د غیرالله له طرفه وَى، دوى به په ده كې خامخا ډېر زیات اختلاف موندلى و');
INSERT INTO "abulsalam" VALUES(4,83,'4:83',' او كله چې دوى ته د امن، یا وېرې څه خبره راشي، دوى هغه (خبره) نشر كړي او كه چېرې دوى هغه، خپل رسول ته او خپلو د كار خاوندانو (حاكمانو) ته رسولې وَى، (نو) خامخا په دوى كې به هغو كسانو دا (خبره) معلومه كړې وَى، څوك چې د دې استنباط او راوېستنه كوي او كه پر تاسو باندې د الله فضل او د هغه رحمت نه وَى، نو تاسو به خامخا د شیطان تابعداري كړې وه مګر لږو خلقو (به نه وه كړې)');
INSERT INTO "abulsalam" VALUES(4,84,'4:84',' نو ته د الله په لاره كې جنګ كوه، ته يواځې د خپل نفس په هكله ذمه وار ګرځولى شې او مومنانو ته ترغیب وركړه، كېدى شي چې الله د هغو كسانو جنګ بند كړي چا چې كفر كړى دى، او الله ډېر سخت دى د جنګ په لحاظ او ډېر سخت دى د سزا وركولو په لحاظ');
INSERT INTO "abulsalam" VALUES(4,85,'4:85',' چا چې سفارش وكړ، ښه سفارش، هغه ته به په دې (سفارش) سره (د ثواب) برخه وي او چا چې سفارش وكړ، بد سفارش (، نو) هغه ته به په دې سره (د ګناه) پېټی وي او الله له همېشه پر هر شي باندې قدرت لرونكى دى');
INSERT INTO "abulsalam" VALUES(4,86,'4:86',' او كله چې تاسو ته په سلام ویلو سره دعا وكړى شي، نو تاسو له هغې نه غوره جواب وركړئ، یا همغه ور وګرځوئ ، بېشكه الله د هر شي ښه حساب كوونكى دى');
INSERT INTO "abulsalam" VALUES(4,87,'4:87',' الله (چې دى)، نشته هېڅ لايق د عبادت مګر يواځې هم دى دى، هغه به تاسو خامخا د قیامت ورځې ته راجمع كړي، چې په هغې كې هېڅ شك نشته او له الله نه په خبره كې ډېر رښتینى څوك دى؟');
INSERT INTO "abulsalam" VALUES(4,88,'4:88',' نو د منافقانو په باره كې پر تاسو څه شوي دي، چې دوه ډلې شوئ، حال دا چې الله هغوى د خپلو عملونو په سبب نسكور كړي دي، ایا تاسو غواړئ چې هغه كس په نېغه لار روان كړئ چې الله ګمراه كړى دى، او چې الله څوك ګمراه كړي، نو ته به هېڅ كله هم د هغه لپاره (نېغه) لار ونه مومې');
INSERT INTO "abulsalam" VALUES(4,89,'4:89',' دوى خوښوي چې تاسو (هم) كافران شئ، لكه څنګه چې دوى كافران شوي دي، بیا به تاسو (ټول) یو شان شئ، نو تاسو له دوى نه هېڅ دوستان مه نیسئ تر هغه پورې چې دوى د الله په لار كې هجرت وكړي، نو كه دوى (د الله له لارې نه) وګرځېدل، نو تاسو دوى نیسئ او دوى وژنئ كوم ځاى چې تاسو دوى ومومئ او تاسو له دوى نه مه هېڅ دوست نیسئ او نه مدد كوونكى');
INSERT INTO "abulsalam" VALUES(4,90,'4:90',' مګر هغه كسان چې له داسې قوم سره پیوند(تعلق) لري چې ستاسو په مینځ كې او د هغوى په مینځ كې معاهده (تړون) وي، یا هغوى تاسو ته راشي چې سینې يې له تاسو سره له جنګ كولو نه، یا له خپل قوم سره له جنګ كولو نه تنګې وي او كه الله غوښتلى (، نو) دوى به يې خامخا پر تاسو باندې زورور كړي وَى، بیا به دوى له تاسو سره خامخا جنګ كړى و، پس كه دوى له تاسو نه ډډه وكړي، نو له تاسو سره جنګ ونه كړي او تاسو ته د سولې درخواست وكړي، بیا نو الله تاسو لپاره پر دوى باندې هېڅ لار نه ده ګرځولې');
INSERT INTO "abulsalam" VALUES(4,91,'4:91',' ژر دى چې تاسو به نور خلق ومومئ چې هغوى غواړي چې له تاسو نه په امن (او بېغمه) شي او له خپل قوم نه (هم) په امن شي، هر وخت چې فتنې (كفر او وراني) ته وبلل شي، (نو) په هغې كې نسكور پرېوځي، نو كه دوى له تاسو نه په ډډه نه شول، او تاسو ته يې سوله پېش نه كړله او خپل لاسونه يې (له جنګ) نه بند نه كړل، بیا نو تاسو دوى نیسئ او دوى وژنئ كوم ځای چې تاسو دوى ومومئ او دغه خلق دي چې مونږ تاسو ته د دوى په خلاف (د جنګ كولو) ښكاره دلیل ګرځولى دى');
INSERT INTO "abulsalam" VALUES(4,92,'4:92',' او هېڅ مومن لره دا حق نشته چې بل مومن قتل كړي مګر دا چې په خطا سره وي، او چا چې مومن په خطا سره قتل كړ، نو د یو مومن څټ (مریي يا وینځې) ازادول لازم دي او دیت دى چې د هغه (مقتول) وارثانو ته به سپارلى شي، مګر دا چې هغوى (وارثان) يې معاف كړي، نو كه چېرې هغه ستاسو د دښمن له قوم ځنې وي او دا (مقتول) مومن وي، بیا نو د یو مومن څټ ازادول دي، او كه چېرې له داسې قوم ځنې وي چې ستاسو په مینځ كې او د هغوى په مینځ كې معاهده وي، نو بیا دیت دى چې د هغه وارثانو ته به سپارلى شي او مومن څټ (غلام يا وینځه) ازادول دي، نو چا چې دغه (څټ) ونه مونده، نو پرله پسې دوه میاشتې روژې نیول دي، د الله له جانبه د توبې لپاره او الله له همېشه ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(4,93,'4:93',' او هغه څوك چې مومن په قصد (او ارادې) سره قتل كړي، نو د هغه سزا جهنم دى، چې همېشه به په هغه كې وي او الله پر ده باندې غضب كړى دى او پر ده يې لعنت كړى دى او د ده لپاره يې بېخي لوى عذاب تیار كړى دى');
INSERT INTO "abulsalam" VALUES(4,94,'4:94',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې تاسو د الله په لار كې سفر كوئ، نو ښه تحقیق كوئ او څوك چې پر تاسو سلام واچوي، هغه ته دا مه وایئ چې ته مومن نه يې، تاسو د دنيايي ژوند سامان غواړئ، نو له الله سره ډېر زیات غنیمتونه دي، له دې نه مخكې تاسو هم همداسې وئ، بیا پر تاسو باندې الله فضل وكړ، نو ښه تحقیق كوئ، بېشكه الله په هغو كارونو چې تاسو يې كوئ، ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(4,95,'4:95',' په مومنانو كې ناست خلق، چې تكلیف والا نه دي، (دوى) او د الله په لار كې په خپلو مالونو او ځانونو سره جهاد كوونكي سره برابرېدلى نه شي، الله په خپلو مالونو او ځانونو سره جهاد كوونكو ته په ناستو باندې په لويې مرتبې سره فضیلت وركړى دى، او له هر یو سره الله د ښې بدلې وعده كړې ده، او الله جهاد كوونكو ته په ناستو باندې په ډېر لوى اجر سره غوره والى وركړى دى');
INSERT INTO "abulsalam" VALUES(4,96,'4:96',' چې د هغه (الله) له طرف نه مرتبې دي او بخښنه او مهرباني ده، او الله له همېشه بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(4,97,'4:97',' بېشكه هغه كسان چې ملايكو د هغوى روحونه قبض كړل، په داسې حال كې چې پر خپلو ځانونو ظلم كوونكي وو، ورته وايي به: تاسو په څه حال كې وئ؟ دوى به وايي: مونږه په (هغې) ځمكه كې كمزوري كړى شوي وو، ورته وبه وايي: ایا د الله ځمكه فراخه نه وه چې تاسو په هغې كې هجرت كړى وَى، نو دغه خلق (چې دي) د دوى اوسېدنځاى جهنم دى او هغه د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(4,98,'4:98',' مګر هغه (په رښتیا) كمزوري كړى شوي سړي او ښځې او ماشومان، چې نه د هېڅ حیلې وَس لري او نه څه لاره مومي');
INSERT INTO "abulsalam" VALUES(4,99,'4:99',' نو دغه خلق (چې دي) امېد دى چې الله به هغوى ته معافي وكړي او الله له ازله ښه معافي كوونكى، ښه بخښونكى دى');
INSERT INTO "abulsalam" VALUES(4,100,'4:100',' او چا چې د الله په لار كې هجرت وكړ (، نو) هغه به په ځمكه كې د اوسېدو ډېر ځایونه او فراخي ومومي او څوك چې له خپله كوره وځي، چې الله ته او د هغه رسول ته هجرت كوونكى وي، بیا مرګ دى رالاندې كړي، نو یقینًا د ده اجر په الله باندې ثابت شو، او الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(4,101,'4:101',' او كله چې تاسو په ځمكه كې سفر كوئ، نو پر تاسو باندې هېڅ ګناه نشته چې له لمانځه نه څه حصه كمه كړئ كه تاسو وېرېدلئ له دې نه چې كافران شوي خلق به تاسو په فتنه كې واچوي، بېشكه دغهكافران له شروع نه ستاسو ښكاره دښمنان دي');
INSERT INTO "abulsalam" VALUES(4,102,'4:102',' او كله چې ته په دوى كې موجود يې، بیا ته دوى ته لمونځ ودروې، نوله دوى نه دې یوه ډله له تا سره ودرېږي او خپلې وسلې دې (هم له ځانه سره) ونیسي، نو كله چې دوى سجده وكړي، نو دوى دې ستاسو شا ته شي او هغه بله ډله دې راشي چې لمونځ يې نه دى كړى، نو هغوى دې له تا سره لمونځ وكړي او خپل د ساتنې سامان او خپلې وسلې دې (له ځانه سره) ونیسي، دغه كافران شوي خلق ارزو لري چې كاشكې تاسو له خپلو وسلو او خپلو سامانونو نه غافله شئ، بیا هغوى پر تاسو باندې په یو ځلي زوروره حمله وكړي. او پر تاسو باندې هېڅ ګناه نشته كه تاسو ته د باران په وجه څه تكلیف وي، یا تاسو بیماران یئ دا چې تاسو خپلې وسلې كېږدئ، او خپله بیداري (د ساتنې سامان) راكلكه كړئ، بېشكه الله د كافرانو لپاره سپكوونكى عذاب تیار كړى دى');
INSERT INTO "abulsalam" VALUES(4,103,'4:103',' بیا چې كله تاسو لمونځ ادا كړئ، نو الله یادوئ، په ولاړه او ناسته او په خپلو اړخونو باندې (چې پراته يئ)، بیا چې كله تاسو مطمئن (ډاډمن) شئ، نو لمونځ قايموئ (د اطمينان د حالت لمونځ) بېشكه لمونځ له شروع نه په مومنانو باندې په (معلومو) وختونو كې فرض كړى شوى دى');
INSERT INTO "abulsalam" VALUES(4,104,'4:104',' او تاسو د دغو (كافرو) خلقو په لټون كې سستي (كمزوري) مه كوئ، كه تاسو یئ چې په تكليف كېږئ، نو بېشكه هغوى (هم) په تكليف كېږي، لكه څنګه چې تاسو په تكليف كېږئ او تاسو له الله نه د هغه څه امېد لرئ چې هغوى يې امېد نه لري او الله له همېشه ښه پوه، ښه حكمت والا دى ');
INSERT INTO "abulsalam" VALUES(4,105,'4:105',' بېشكه مونږ تا ته كتاب په حقه سره نازل كړى دى، د دې لپاره چې ته د خلقو په مینځ كې فیصله وكړې په هغه څه سره چې الله تا ته ښودلي دي او ته د خیانت كوونكو لپاره جګړه كوونكى مه جوړېږه');
INSERT INTO "abulsalam" VALUES(4,106,'4:106',' او له الله نه بخښنه غواړه، بېشكه الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(4,107,'4:107',' او ته د هغو كسانو له طرفه جګړه مه كوه چې له خپلو ځانونو سره خیانت كوي، بېشكه الله له هغه چا سره مینه نه كوي چې ډېر خاين، سخت ګناهګار وي');
INSERT INTO "abulsalam" VALUES(4,108,'4:108',' دوى (خپل جرمونه) له خلقو نه ډېر پټوي او له الله نه يې نه پټوي، حال دا چې هغه (الله) له دوى سره وي كله چې دوى د شپې د هغې خبرې په باره كې مشورې كوي چې هغه يې نه خوښوي، او الله هغو عملونو لره چې دوى يې كوي، راګېروونكى دى');
INSERT INTO "abulsalam" VALUES(4,109,'4:109',' واورئ! دا تاسو خلق یئ چې د دوى له طرفه مو په دنيايي ژوند كې جګړه وكړه، نو د قیامت په ورځ به له الله سره د دوى له طرفه څوك جګړه كوي؟ یا څوك به د دوى لپاره كارساز جوړېږي؟');
INSERT INTO "abulsalam" VALUES(4,110,'4:110',' او هر څوك چې څه بد عمل وكړي یا پر خپل ځان ظلم وكړي، بیا له الله نه بخښنه وغواړي (، نو) الله به ډېر بخښونكى، بې حده رحم كوونكى ومومي');
INSERT INTO "abulsalam" VALUES(4,111,'4:111',' او هر څوك چې څه ګناه وكړي، نو بېشكه هم دا خبره ده چې د خپل ځان په ضرر يې كوي، او الله له همېشه ښه پوه، ښه حكمت والا دى ');
INSERT INTO "abulsalam" VALUES(4,112,'4:112',' او هر څوك چې څه خطا وكړي، یا څه ګناه، بیا د هغې تور په (بل) بې ګناه پورې كوي، نو یقینًا هغه د غټ تهمت او ښكاره ګناه بار پورته كړ');
INSERT INTO "abulsalam" VALUES(4,113,'4:113',' او كه په تا باندې د الله فضل او د هغه رحمت نه وَى (، نو) يقيناً په دوى كې یوې ډلې اراده كړې وه چې تا ګمراه كړي او دوى نه ګمراه كوي مګر خپل ځانونه، او تا ته هېڅ قسمه ضرر نشي دررسولى او الله پر تا باندې نازل كړى دى كتاب (قرآن) او حكمت (سنت) او تا ته يې هغه څه درزده كړي دي چې ته پرې نه پوهېدلې، او پر تا باندې د الله فضل ډېر لوى دى');
INSERT INTO "abulsalam" VALUES(4,114,'4:114',' هېڅ خیر نشته د هغوى په ډېرو پټو مشورو كې مګر هغه څوك چې حكم وكړي د صدقې كولو، یا د نېكۍ كولو، یا د خلقو په مینځ كې د اصلاح (روغې) كولو، او هر څوك چې دغه كارونه د الله د رضا لټولو لپاره وكړي، نو ژر ده چې هغه ته به مونږ ډېر لوى اجر وركړو');
INSERT INTO "abulsalam" VALUES(4,115,'4:115',' او هغه څوك چې له رسول سره مخالفت وكړي، وروسته له دې نه چې هغه ته نېغه لار ښه څرګنده شوه، او د مومنانو له لارې نه غیر د بلې (لارې) تابع شي (، نو) مونږ به دغه (كس) هغه څه ته وروګرځوو چې دى ورته ورګرځېدلى دى او جهنم ته به يې داخل كړو او هغه د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(4,116,'4:116',' بېشكه الله نه بخښي دا چې له هغه سره شریك ونیول شي او بخښي هغه (ګناهونه) چې له دې (شرك) نه كم دي، د چا لپاره چې يې وغواړي، او څوك چې له الله سره شریك ونیسي، نو یقینًا هغه ګمراه شو په ډېرې لرې ګمراهۍ سره');
INSERT INTO "abulsalam" VALUES(4,117,'4:117',' دوى له هغه (الله) نه غیر نه بلي مګر ښځې (ښځنومي بتان) او دوى نه بلي مګر سركښه شیطان ');
INSERT INTO "abulsalam" VALUES(4,118,'4:118',' الله پر هغه لعنت كړى دى او هغه (شیطان) وویل: زه به خامخا لازمًا ستا په بنده ګانو كې یوه مقرره برخه راګېروم');
INSERT INTO "abulsalam" VALUES(4,119,'4:119',' او زه به خامخا لازمًا هغوى ګمراه كوم او زه به هغوى خامخا لازمًا په ارزوګانو كې اچوم او زه به هغوى ته خامخا لازمًا حكم كوم، بیا به يقيناً هغوى د څارویو غوږونه څیروي او زه به ضرور هغوى ته حكم كوم، نو هغوى به ارومرو د الله په تخلیق كې تغییر (بدلون) راولي او چا چې له الله نه غیر شیطان دوست ونیوه، نو یقینًا هغه تاواني شو، ښكاره تاواني كېدل');
INSERT INTO "abulsalam" VALUES(4,120,'4:120',' هغه (شیطان) دوى ته وعدې وركوي او په ارزوګانو كې يې اچوي او شیطان دوى ته وعدې نه وركوي مګر د دوكې (او چل ول)');
INSERT INTO "abulsalam" VALUES(4,121,'4:121',' دغه خلق دي چې د دوى استوګنه جهنم دى او دوى به له هغه نه هېڅ خلاصى ونه مومي');
INSERT INTO "abulsalam" VALUES(4,122,'4:122',' او هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي ژر ده چې مونږ به دوى داسې جنتونو ته داخل كړو چې د هغو له لاندې ولِې بهیږي، چې دوى به په هغو كې تل ترتله وي، (دا) د الله حقه وعده ده او له الله نه په خبره كې څوك زیات رښتینى دى؟!');
INSERT INTO "abulsalam" VALUES(4,123,'4:123',' نه ستاسو په ارزوګانو څه كېږي او نه د اهلِ كتابو په ارزوګانو، (بلكې) چا چې هم بد عمل وكړ هغه ته به د دغه (بد عمل) بدله وركولى شي او هغه به ونه مومي د ځان لپاره له الله نه غیر هېڅ كارساز او نه څوك مدد كوونكى');
INSERT INTO "abulsalam" VALUES(4,124,'4:124',' او چې چا هم د نېكیو عمل وكړ، نر وي يا كه ښځه، خو چې مومن وي، نو دغه خلق به جنت ته داخلېږي او پر دوى به د كجورې د زړي د داغ هومره ظلم هم نه كېږي');
INSERT INTO "abulsalam" VALUES(4,125,'4:125',' او د دين په لحاظ له هغه كس نه څوك زیات ښه دى، چې خپل مخ يې الله ته تابع كړ او هغه نېكي كوونكى و او د ابراهیم د ملت تابع شو، چې يواځې په حق پسې تلونكى و، او الله ابراهیم لره خلیل نیولى دى');
INSERT INTO "abulsalam" VALUES(4,126,'4:126',' او خاص د الله لپاره دي هر هغه څه چې په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي او الله هر شي لره ښه راګېروونكى دى');
INSERT INTO "abulsalam" VALUES(4,127,'4:127',' او (اى نبي!) دوى له تا نه د ښځو په باره كې تپوس كوي، ته ووایه: الله تاسو ته د هغوى په باره كې فتوا دركوي او هغه (حكم هم راغلی دی) چې تاسو ته په كتاب كې تلاوت كولى شي د هغو یتیمو ښځو په باره كې چې تاسو هغوى ته د هغوى لپاره مقرر شوى (مهر) نه وركوئ او له هغوى سره د نكاح كولو رغبت لرئ او د كمزورو ماشومانو په باره كې (هم همدا حکم دی) او (بل حکم دی) په دې باره كې چې تاسو د یتیمانو لپاره په انصاف سره قايم اوسئ او تاسو چې څه نېكي كوئ، بېشكه الله په هغې باندې ښه عالم دى');
INSERT INTO "abulsalam" VALUES(4,128,'4:128',' او كه چېرې یوه ښځه له خپل خاوند نه د زیاتي، یا مخ اړونې اندېښنه ولري، نو په دې دواړو باندې هېڅ ګناه نشته چې په خپل مینځ كې په څه شان هم روغه وكړي، او روغه كول ډېر غوره دي او نفسونه شومتیا ته حاضر كړى شوي دي او كه تاسو نېكي وكړئ او پرهېزګاره شئ، نو یقینًا الله پر هغو كارونو چې تاسو يې كوئ ؛ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(4,129,'4:129',' او تاسو به كله هم (دا) وَس ونه لرئ چې د خپلو ښځو په مینځ كې (په هر لحاظ سره او د زړه په مينه کې) انصاف وكړئ اګر كه تاسو (د دې) حرص (هم) وكړئ، نو مه كږېږئ (یوې ته) پوره كږېدل، چې تاسو هغه (بله) پرېږدئ په شان د زوړندې كړى شوې او كه تاسو صلح وكړئ او پرهېزګاره شئ، نو بېشكه الله له همېشه ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(4,130,'4:130',' او كه دواړه (ښځه او خاوند) له یو بل نه بېل شي (، نو) الله به هر یو په خپلې فراخۍ سره غني كاندي او الله له همېشه فراخۍ والا، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(4,131,'4:131',' او خاص د الله لپاره دي هر هغه څه چې په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي او یقینًا یقینًا مونږه هغو كسانو ته كلك حكم كړى دى چې له تاسو نه مخكې ورته كتاب وركړى شوى و او تاسو ته هم، چې له الله نه ووېرېږئ او كه تاسو كفر وكړئ، نو یقینًا خاص د الله لپاره دي هغه څه چې په اسمانونو كې دي او هغه څه چې په ځمكه كې دي او الله له همېشه غني (او) ښه ستايل شوى دى');
INSERT INTO "abulsalam" VALUES(4,132,'4:132',' او خاص د الله لپاره دي هغه څه چې په اسمانونو كې دي او هغه څه چې په ځمكه كې دي او الله ښه كافي دى كارساز');
INSERT INTO "abulsalam" VALUES(4,133,'4:133',' كه هغه وغواړي تاسو به فنا كړي اى خلقو! او نور خلق به راولي او الله په دې باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(4,134,'4:134',' څوك چې داسې دى چې د دنیا بدله غواړي، نو له الله سره د دنیا او اخرت دواړو بدله ده او الله ښه اورېدونكى، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(4,135,'4:135',' اى هغو كسانو چې ایمان يې راوړى دى! شئ تاسو قايم اوسېدونكي په انصاف باندې، ګواهي كوونكي د الله لپاره، اګر كه (دا ګواهي) ستاسو د خپلو ځانونو، يا مور و پلار او نژدې خپلوانو په خلاف وي، كه هغه (كس) مالداره وي، یا غریب وي، نو الله د دې دواړو ډېر خیر غوښتونكى دى، نو تاسو (له حق نه) د اوړېدلو لپاره د خواهشاتو پیروي مه كوئ او كه تاسو ژبې كږې كړئ، یا مخ واړوئ، نو یقینًا الله پر هغو كارونو چې تاسو يې كوئ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(4,136,'4:136',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو پر الله ایمان راوړئ او د هغه پر رسول او پر هغه كتاب چې هغه په خپل رسول باندې نازل كړى دى او پر هغو كتابونو چې له ده نه يې مخكې نازل كړي دي، او څوك چې په الله سره كافر شي او د هغه په ملايكو او د هغه په كتابونو او د هغه په رسولانو او د اخرت په ورځ، نو یقینًا هغه ګمراه شو په ډېرې لرې ګمراهۍ سره');
INSERT INTO "abulsalam" VALUES(4,137,'4:137',' بېشكه هغه كسان چې ایمان يې راوړ، بیا دوى كافر شول، بیا يې ایمان راوړ، بیا كافر شول، بیا دوى په كفر كې ښه مخ په وړاندې لاړل (، نو) الله كله هم داسې نه دى چې دوى ته بخښنه وكړي او نه داسې دى چې دوى به پر (نېغه) لاره روان كړي');
INSERT INTO "abulsalam" VALUES(4,138,'4:138',' منافقانو ته زېرى وركړه چې بېشكه د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(4,139,'4:139',' هغه كسان چې له مومنانو نه غیر كافران دوستان نیسي، ایا دوى د هغوى په خوا كې عزت لټوي؟ نو بېشكه عزت ټول د الله لپاره دى');
INSERT INTO "abulsalam" VALUES(4,140,'4:140',' او یقینًا هغه (الله) پر تاسو باندې په كتاب كې دا (حكم) نازل كړى دى چې كله تاسو د الله ایتونه واورئ، چې په هغو سره كفر كېږي او په هغو پورې مسخرې كېږي، نو تاسو له هغوى (كفر او استهزا كوونكو) سره مه كېنئ تر دې چې هغوى له دې نه سِوا (په نورو) خبرو كې مشغوله شي، بېشكه تاسو په دغه وخت كې د هغوى په شان یئ، بېشكه الله منافقانو او كافرانو ټولو لره په جهنم كې راجمع كوونكى دى ');
INSERT INTO "abulsalam" VALUES(4,141,'4:141',' هغه (منافقان) چې ستاسو په باره كې انتظار كوي، نو كه د الله له جانبه تاسو ته فتح حاصله شي، دوى وايي: ایا مونږ له تاسو سره ملګري نه وو؟ او كه چېرې د كافرانو لپاره څه برخه وي، (بیا نوكافرو ته) دوى وايي: ایا مونږ پر تاسو غالب شوي نه وو او (ایا) مونږ له مومنانو نه تاسو بچ نه كړئ؟ نو الله به ستاسو په مینځ كې د قیامت په ورځ فیصله كوي او الله به كله هم د كافرانو لپاره په مومنانو باندې (د غلبې) څه لار جوړه نه كړي');
INSERT INTO "abulsalam" VALUES(4,142,'4:142',' بېشكه منافقان له الله سره دوكه كوي او الله له دوى سره دوكه كوونكى دى او كله چې دوى لمانځه ته پاڅېږي (، نو) سست (كاهلان) پاڅېږي، خلقو ته ځانونه ښیي او الله نه یادوي مګر ډېر لږ');
INSERT INTO "abulsalam" VALUES(4,143,'4:143',' دوى د دې (كفر او اسلام) په مینځ كې زړه نازړه دي، نه (سم) د دوى په طرف دي او نه (سم) د هغوى په طرف دي او هغه څوك چې الله يې ګمراه كړي، نو ته به كله هم د هغه لپاره څه لار ونه مومې');
INSERT INTO "abulsalam" VALUES(4,144,'4:144',' اى هغو كسانو چې ایمان يې راوړى دى! له مومنانو نه غیر كافرانو لره دوستان مه نیسئ، ایا تاسو غواړئ چې د الله لپاره د خپل ځان په خلاف ښكاره دلیل ثابت كړئ');
INSERT INTO "abulsalam" VALUES(4,145,'4:145',' بېشكه منافقان د اور تر ټولو لاندینۍ طبقه كې دي او ته به كله هم د هغوى لپاره څوك مدد كوونكى ونه مومې');
INSERT INTO "abulsalam" VALUES(4,146,'4:146',' مګر هغه كسان چې توبه يې وېستله او د ځان اصلاح يې وكړه او پر (دین د) الله يې منګولې كلكې ولګولې او خپل دین يې د الله لپاره خالص كړ، نو دغه كسان به له مومنانو سره وي او ژر ده چې الله به مومنانو ته بېخي لوى اجر وركړي ');
INSERT INTO "abulsalam" VALUES(4,147,'4:147',' الله تاسو ته په عذاب دركولو څه كوي كه تاسو شكر وباسئ؟! او ایمان راوړئ او الله له همېشه شكر قبلوونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(4,148,'4:148','الله د بدې خبرې ښكاره ویل نه خوښوي خو هغه كس چې ظلم پرې شوى وي (هغه معاف دى) او الله له همېشه ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(4,149,'4:149',' كه تاسو څه نېكي څرګندوئ، یا هغه پټه كوئ، یا د بدۍ په هكله معافي كوي، بېشكه الله له ازله ښه معافي كوونكى، ښه قادر دى');
INSERT INTO "abulsalam" VALUES(4,150,'4:150',' بېشكه هغه كسان چې په الله او د هغه په رسولانو كافران كېږي او دوى غواړي چې د الله او د هغه د رسولانو په مینځ كې بېلتون وكړي او دوى وايي چې مونږ پر ځینو (رسولانو) ایمان راوړو او په ځینو كافر كېږو او دوى غواړي چې د دې (اسلام او كفر) په مینځ كې (بله) لار ونیسي');
INSERT INTO "abulsalam" VALUES(4,151,'4:151',' دغه كسان په رښتیا كافران دي او مونږه د كافرانو لپاره سپكوونكى عذاب تیار كړى دى');
INSERT INTO "abulsalam" VALUES(4,152,'4:152',' او هغه كسان چې پر الله او د هغه پر رسولانو يې ایمان راوړى دى او په دوى كې يې د هېڅ یو تر مینځه تفریق نه دى كړى، دغه كسان دي چې هغه (الله) به دوى ته د دوى ثوابونه ژر (او) يقينًا وركړي او الله له ازله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(4,153,'4:153',' اهلِ كتاب له تا نه د دې مطالبه كوي چې ته پر دوى باندې له اسمانه یو كتاب نازل كړې، پس یقینًا دوى له موسٰی څخه له دې نه غټه مطالبه كړې وه، نو دوى (هغه ته) ویلي وو: ته مونږ ته الله ښكاره مخامخ وښیه، نو دوى د خپل ظلم په سبب تندر ونیول، بیا دوى سخى معبود ونیوه، وروسته له دې چې دوى ته ښې واضحې نښې راغلې، بیا مونږ دوى ته د دې معافي وكړه او مونږ موسٰی ته ښكاره غلبه (دلیل) وركړه');
INSERT INTO "abulsalam" VALUES(4,154,'4:154',' او مونږ له دوى څخه د پخې وعدې اخيستلو لپاره د دوى له پاسه (د) طور (غر) راپورته كړ او مونږه دوى ته وویل: تاسو په دروازه كې داخل شئ، چې سجده كوونكي یئ او مونږه دوى ته وویل: تاسو د خالي په ورځ كې زیاتى مه كوئ او مونږه له دوى نه ډېره مضبوطه وعده واخيستله');
INSERT INTO "abulsalam" VALUES(4,155,'4:155',' نو (دوى ته سزا وركړى شوه) په سبب د دوى د ماتولو خپل لوظ لره او د الله په ایتونو د كفر كولو او د نبیانو د ناحقه وژلو (په سبب) او د دوى د دې خبرې په سبب چې زمونږ زړونه ’’پرده بند‘‘ دي (داسې نه ده) بلكې الله د دوى د كفر په وجه پر هغو (زړونو) مهر لګولى دى، نو دوى ایمان نه راوړي مګر ډېر لږ ');
INSERT INTO "abulsalam" VALUES(4,156,'4:156',' او په سبب د دوى د كفر كولو او پر مریم باندې د بېخي لوى تهمت ویلو ');
INSERT INTO "abulsalam" VALUES(4,157,'4:157',' او د دوى د دې وینا په سبب چې بېشكه مونږه د مریمې زوى مسیح عیسٰی، د الله رسول وژلى دى، حال دا چې دوى نه هغه وژلى دى او نه يې په دار كړى دى او لېكن دوى ته هغه (وژل شوی کس له عیسٰی سره) مُشابِه كړى شوى و او بېشكه هغه كسان چې د عیسٰی په باره كې يې اختلاف كړى دى، خامخا د ده په هكله په شك كې دي، دوى ته د هغه په باره كې هېڅ علم نشته سِوا د (خپل) ګمان له پیروۍ نه او دوى هغه یقینًا نه دى وژلى');
INSERT INTO "abulsalam" VALUES(4,158,'4:158',' بلكې الله هغه خپل طرف ته راپورته كړى دى او الله له ازله ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(4,159,'4:159',' او نشته هیڅوك له اهلِ كتابو نه مګر چې هغه به خامخا لازمًا د ده له مرګ نه مخكې پر ده ایمان راوړي او د قیامت په ورځ به هغه (عیسٰی) په دوى باندې ګواه وي');
INSERT INTO "abulsalam" VALUES(4,160,'4:160',' نو د یهودیان شويو خلقو د ظلم كولو په سبب مونږ پر هغوى باندې هغه پاكیزه څیزونه حرام كړل، چې د دوى لپاره (مخكې) حلال ګرځول شوي وو او په سبب د منع كولو د دوى ډېرو (خلقو) لره د الله له لارې نه');
INSERT INTO "abulsalam" VALUES(4,161,'4:161',' او (په سبب) د دوى د سود اخيستلو، حال دا چې دوى له دې نه منع كړى شوي وو، او (په سبب) د دوى په ناحقه خوړلو د خلقو مالونو لره، او مونږه په دوى كې د كافرانو لپاره ډېر دردوونكى عذاب تیار كړى دى ');
INSERT INTO "abulsalam" VALUES(4,162,'4:162',' خو په دوى كې په علم كې پاخه خلق او مومنان، هغوى ایمان رواړي پر هغه (كتاب) باندې چې تا ته نازل كړى شوى دى او پر هغو (كتابونو) باندې چې له تا نه مخكې نازل كړى شوي دي او خصوصًا چې لمونځ قايموونكي دي او زكات وركوونكي دي او پر الله او پر ورځ د اخرت ایمان لرونكي دي، دغه خلق (چې دي) ، مونږ به خامخا دغو ته ډېر لوى اجر وركړو');
INSERT INTO "abulsalam" VALUES(4,163,'4:163',' بېشكه مونږ تا ته وحي كړې ده، لكه څنګه چې مونږ وحي كړې وه نوح ته او له هغه نه پس (نورو) نبیانو ته او مونږ وحي كړې وه ابراهیم او اسمعیل او اسحاق او یعقوب او د (هغوى) اولادې ته او عیسٰی او ایوب او یونس او هارون او سلیمان ته او مونږ داود ته زبور وركړى و');
INSERT INTO "abulsalam" VALUES(4,164,'4:164',' او (مونږ لېږلي وو) ډېر رسولان، چې یقینًا د هغوى احوال مونږ تا ته له دې نه مخكې بیان كړي دي او (نور) رسولان چې د هغوى احوال مونږ تا ته نه دي بیان كړي او الله له موسٰی سره خبرې كړي دي، واضحې خبرې');
INSERT INTO "abulsalam" VALUES(4,165,'4:165',' او (مونږ لېږلي وو) رسولان، چې زېرى وركوونكي او وېروونكي وو، د دې لپاره چې د خلقو لپاره په الله باندې څه حجت پاتې نشي او الله له ازله ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(4,166,'4:166',' لېكن الله ګواهي كوي پر هغه شي چې تا ته يې نازل كړى دى، چې دا يې په خپل علم سره نازل كړى دى، او ملايك (هم) ګواهي كوي او الله ښه كافي دى (په حیث د) ګواه');
INSERT INTO "abulsalam" VALUES(4,167,'4:167',' بېشكه هغه كسان چې كافر شوي دي او د الله له لارې نه يې بندول كړي دي، یقینًا هغوى ګمراه شوي دي په ډېرې لرې ګمراهۍ سره');
INSERT INTO "abulsalam" VALUES(4,168,'4:168',' بېشكه هغه كسان چې كفر يې كړى دى او ظلم يې كړى دى، الله داسې نه دى چې دوى به وبخښي او نه دا چې دوى به پر نېغه لار روان كړي');
INSERT INTO "abulsalam" VALUES(4,169,'4:169',' مګر پر لار د جهنم، چې دوى به په هغه كې تل ترتله وي او دا (كار) الله ته ډېر اسان دى');
INSERT INTO "abulsalam" VALUES(4,170,'4:170',' اى خلقو! یقینًا تاسو ته د خپل رب له جانبه په حقه سره رسول راغلى دى، نو تاسو ایمان راوړئ، (دا به) تاسو لپاره ډېر غوره وي او كه تاسو كفر كوئ، نو یقینًا خاص د الله لپاره دي هر هغه څه چې په اسمانونو او ځمكه كې دي او الله له ازله ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(4,171,'4:171',' اى اهلِ كتابو! تاسو په خپل دین كې غُلُوّ (زیاتى) مه كوئ او الله ته يواځې د حقې خبرې نسبت كوئ، یقینًا همدا خبره ده چې د مریمې زوى مسیح عیسیٰ خو بس د الله رسول دى او د هغه كلمه ده، چې هغه يې مریم ته غورځولې وه او د هغه (الله) له جانبه روح دى، نو تاسو پر الله او د هغه پر رسولانو ایمان راوړئ او تاسو مه وایئ چې (معبودان) درې دي، تاسو (له دې نه) منع شئ، تاسو لپاره به (دا) ډېره ښه وي، یقینًا همدا خبره ده چې الله یو معبود دى زه د هغه پاكي بیانوم له دې نه چې د هغه لپاره دې اولاد وي، خاص د هغه لپاره دي هر هغه څه چې په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي او الله ښه كافي دى (په حیث د) كار ساز ');
INSERT INTO "abulsalam" VALUES(4,172,'4:172',' هیڅكله به هم مسیح (دا د ځان لپاره) عار ونه ګڼي چې هغه دې د الله بنده وي او نه ملايك (دغه عار ګڼي) چې مقرب دي او څوك چې د هغه عبادت كول (د ځان لپاره) عار ګڼي او لويي كوي، نو ژر به هغه (الله) دوى خپل طرف ته ټول راجمع كړي');
INSERT INTO "abulsalam" VALUES(4,173,'4:173',' نو هر چې هغه كسان دي چې ایمان يې راوړى دى او نېك عملونه يې كړي دي‘ نو هغه به دوى ته د دوى اجرونه پوره وركړي او له خپل فضل نه به دوى ته (نور) زیات وركړي او پاتې شول هغه كسان چې عار يې وكړو او لويي يې وكړه، نو هغه به دوى ته ډېر دردوونكى عذاب وركړي او دوى به د خپلو ځانونو لپاره له الله نه سوا (بل) نه څوك دوست ومومي او نه مدد كوونكى');
INSERT INTO "abulsalam" VALUES(4,174,'4:174',' اى خلقو! یقینًا تاسو ته د خپل رب له جانبه لوى دلیل راغلى دى او مونږه تاسو ته واضحه رڼا نازله كړې ده');
INSERT INTO "abulsalam" VALUES(4,175,'4:175',' نو هر چې هغه كسان دي چې پر الله يې ایمان راوړو او پر (دین د) هغه يې منګولې ښخې كړې، نو خامخا به هغه دوى په خپل رحمت او فضل كې داخلوي او د خپل ځان په طرف به يې په نېغه لار روانوي');
INSERT INTO "abulsalam" VALUES(4,176,'4:176',' دوى له تا نه فتوٰی غواړي، ته (ورته) ووایه: الله تاسو ته د ’’كَلَاله‘‘ (بې پلاره او بې اولاده كس) په باره كې فتویٰ دركوي، كه یو سړى وفات شي او د هغه هېڅ اولاد نه وي او د هغه یوه خور وي، نو د هغې لپاره د هغه مال نیمه (حصه) ده چې هغه (مړي) پریښى دى او دا (سړى) د هغې (خور) (د ټول مال) وارث ګرځي كه د هغې هېڅ اولاد نه وي، بیا كه هغه دوه (خوېندې) وي، نو د هغوى لپاره (د ټول مال) دوه په درې برخې ده، له هغه (مال) نه چې هغه پریښى وي او كه چېرې هغوى څو وروڼه (خويندې) وي، سړي او ښځې، نو د نر لپاره د دوه ښځو د حصې په مثل (حصه) ده، الله تاسو ته ښه وضاحت كوي، چې تاسو ګمراه نشئ او الله پر هر شي ښه عالم دى');
INSERT INTO "abulsalam" VALUES(5,1,'5:1',' اى هغو كسانو چې ایمان يې راوړى دى! (خپل) لوظونه پوره كړئ، تاسو لپاره اَنعام څاروي حلال كړى شوي دي، سوا له هغو نه چې تاسو ته به ولوستى شي، په دې حال كې چې تاسو د احرام په حالت كې ښكار لره حلال ګڼونكي نه یئ، بېشكه الله فیصله كوي د څه چې اراده وكړي');
INSERT INTO "abulsalam" VALUES(5,2,'5:2',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د الله د شعايرو (د دین د نښو) بې حرمتي مه كوئ او نه د عزتمنې میاشتې، او نه د حرم د قربانۍ، او نه د حرم د غاړكۍ والا څارويو او نه عزتمن كور ته د اراده لرونكو خلقو، چې د خپل رب فضل او رضامندي لټوي او كله چې تاسو حلال (بې احرامه) شئ، نو بیا تاسو ښكار كوئ او له كوم قوم (سره) بغض دې تاسو زیاتي كولو ته راپورته نه كړي، له دې وجې چې تاسو يې له مسجد حرام نه منع كړي وئ او تاسو له یو بل سره په نېكۍ او پرهېزګارۍ كې مدد كوئ او د ګناه او ظلم په كار كې د یو بل مدد مه كوئ او له الله نه وېرېږئ، بېشكه الله ډېر سخت عذاب وركوونكى دى');
INSERT INTO "abulsalam" VALUES(5,3,'5:3',' پر تاسو باندې حرامه كړى شوې ده مرداره، او وینه او د خنزیر غوښه او هغه چې په هغه د غیر الله لپاره اواز شوى وي او مرۍ خپه كړى شوى (زندۍ شوى) څاروى، او په كوتك وهل شوى او له بره راغورځېدلى او په ښكر وهل شوى، او هغه چې لیوان يې مات كړي، سوا له هغه (څاروي) نه چې تاسو يې حلال كړئ، او (حرام دى) هغه (څاروى) چې په استانو (درګاګانو) باندې ذبح كړى شي او دا چې تاسو په غشو سره قسمت معلوموئ، دغه (ټول) كارونه فسق او نافرماني دي، نن ورځ كافر شوي خلق ستاسو د دین (له ختمولو) نه ناامېده شول، نو تاسو له دوى نه مه وېرېږئ او له ما نه وېرېږئ، نن ورځ ما تاسو لپاره ستاسو دین مكمل كړو او پر تاسو باندې مې خپل نعمتونه تمام كړل او ما تاسو لپاره اسلام د دین په توګه خوښ كړو، نو څوك چې په سخته لوږه كې مجبور شو، په دې حال كې چې ګناه ته مايل كېدونكى نه وي، بیا نو یقینًا الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(5,4,'5:4',' دوى له تا نه تپوس كوي چې د دوى لپاره څه حلال كړى شوي دي، ته (ورته) ووایه: تاسو لپاره پاكیزه څیزونه حلال كړى شوي دي او هغه (ښكار د) ښكاري حیواناتو چې تاسو تربیه كړي وي، چې ښكاري جوړوونكي یئ، چې تاسو دوى ته له هغه علمه نه ښوونه كوئ چې الله تاسو ته ښوولى دى، نو تاسو خورئ له هغه (ښكار) نه چې دغو (ښكاري حیواناتو) تاسو لپاره نیولى وي او تاسو پر هغه د الله نوم یادوئ، او له الله نه وېرېږئ، بېشكه الله ډېر ژر حساب كوونكى دى');
INSERT INTO "abulsalam" VALUES(5,5,'5:5',' نن ورځ تاسو لپاره پاكیزه څیزونه حلال كړى شول او د هغو كسانو ذبیحه چې كتاب ورته وركړى شوى دى؛ تاسو لپاره حلاله ده او ستاسو طعام د هغوى لپاره حلال دى، او (تاسو لپاره حلالې دي) پاك لمنې مومنې ښځې او پاك لمنې ښځې له هغو خلقو ځنې چې هغوى ته له تاسو نه مخكې كتاب وركړى شوى دى، كله چې تاسو دغو (ښځو) ته د هغوى مهرونه وركړئ، چې عفت غوښتونكي یئ، بدكاري كوونكي نه یئ او نه په پټه سره یاران نیونكي یئ او څوك چې له ایمان نه انكار وكړي، نو یقینًا د هغه عمل برباد شو او هغه به په اخرت كې له تاوانیانو څخه وي');
INSERT INTO "abulsalam" VALUES(5,6,'5:6',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې تاسو لمانځه ته پاڅېږئ، نو تاسو خپل مخونه او خپل لاسونه تر څنګلو پورې ووېنځئ او د خپلو سرونو مسح وكړئ او تر ګیټو (ښنګرو) پورې خپلې پښې (ووېنځئ) او كه چېرې تاسو د جنابت په حالت كې یئ، نو ځان ښه پاك كړئ (غسل وكړئ) او كه چېرې تاسو بېماران یئ، یا په سفر كې یئ، یا په تاسو كې كوم یو له اودس ماتي نه راغى، یا تاسو له خپلو ښځو سره كوروالى وكړئ، بیا تاسو اوبه ونه موندلې، نو د پاكې ځمكې مخ ته قصد وكړئ (تیمم وكړئ)، نو خپل مخونه او خپل لاسونه (تر مړوندونو پورې) په دې (خاورې، شګې) سره مسح كړئ، الله اراده نه لري چې پر تاسو باندې څه حرج (سختی) راولي، او لېكن غواړي چې تاسو ښه پاك كړي او پر تاسو باندې خپل نعمتونه پوره كړي، د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(5,7,'5:7',' او تاسو د الله نعمتونه یاد كړئ، چې پر تاسو باندې شوي دي او د هغهٔ، هغه پخه وعده یاده كړئ چې له تاسو سره يې مضبوطه كړې وه، كله چې تاسو وویل: مونږ واورېدل او مونږ اطاعت وكړو، او تاسو له الله نه وېرېږئ، بېشكه الله د سینو په خبرو باندې (هم) ښه عالم دى');
INSERT INTO "abulsalam" VALUES(5,8,'5:8',' اى هغو كسانو چې ایمان يې راوړى دى ! تاسو د الله لپاره (په حق باندې) مضبوط ولاړ اوسئ، چې په انصاف سره ګواهي كوونكي یئ او د یو قوم بغض دې تاسو دې ته راپورته نه كړي چې تاسو عدل ونه كړئ، عدل كوئ، دا تقوا ته ډېر نژدې دى او له الله نه ووېرېږئ، بیشكه الله ښه خبر دى پر هغو كارونو چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(5,9,'5:9',' الله له هغو كسانو سره وعده كړې ده چې ایمان يې راوړى دى او نېك عملونه يې كړې دي، چې د هغوى لپاره بخښنه او ډېر لوى اجر دى');
INSERT INTO "abulsalam" VALUES(5,10,'5:10',' او هغه كسان چې كفر يې كړى دى او زمونږ ایتونه يې دروغ ګڼلي دي، دغه خلق د اور والا دي');
INSERT INTO "abulsalam" VALUES(5,11,'5:11',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د الله هغه نعمتونه رایاد كړئ چې پر تاسو باندې شوي دي، كله چې څه خلقو اراده وكړه چې تاسو ته خپل لاسونه در اوږده كړي، نو هغه (الله) د هغوى لاسونه له تاسو بند كړل او له الله نه ووېرېږئ او لازم ده چې مومنان خاص په الله باندې توكل وكړي');
INSERT INTO "abulsalam" VALUES(5,12,'5:12',' او یقینًا یقینًا، الله له بني اسرائیلو نه پخه وعده اخيستې وه او مونږ په دوى كې دولس تنه مشران مقرر كړل او الله وفرمایل: بېشكه زه له تاسو سره یم، یقینًا كه چېرې تاسو لمونځ قايم وساتئ او زكات ادا كړئ او زما پر رسولانو ایمان راوړئ او تاسو د هغوى مدد وكړئ او تاسو الله ته ډېر ښكلى قرض وركړئ، نو خامخا لازمًا به زه له تاسو څخه ستاسو ګناهونه ختم كړم او خامخا لازمًا به تاسو داسې جنتونو ته ننباسم چې د هغو له لاندې ولې بهېږي، نو په تاسو كې چې چا له دې نه وروسته كفر وكړ، نو یقینًا هغه له نېغې لارې نه بې لارې شو ');
INSERT INTO "abulsalam" VALUES(5,13,'5:13',' نو د خپلې كلكې وعدې د ماتولو په سبب مونږ پر دوى لعنت وكړ او مونږ د دوى زړونه سخت كړل، دوى (د تورات) كلمې له خپلو ځایونو نه اړوي او دوى ته چې څه نصیحت شوى و؛ د هغه (لویه) حصه يې هېره كړله او ته به همېشه د دوى په څه خیانت باندې اګاه ګېږي، مګر په دوى كې ډېر لږ خلق (داسې نه دي)، نو ته دوى ته معافي كوه او تېرېږه، بېشكه الله نېكي كوونكي خوښوي');
INSERT INTO "abulsalam" VALUES(5,14,'5:14',' او له هغو كسانو څخه چې وايي: بېشكه مونږ نصرانیان یو؛ مونږ له دوى نه پخه وعده اخيستې وه، نو دوى ته چې د څه نصیحت شوى و؛ د هغه لویه برخه يې هېره كړله، نو مونږ د دوى په مینځ كې د قیامت د ورځې پورې دښمني او كینه واچوله او الله به ژر دوى پر هغو عملونو خبر كړي چې دوى به كول');
INSERT INTO "abulsalam" VALUES(5,15,'5:15',' اى اهل كتابو! یقینًا تاسو ته زمونږ رسول راغلى دى چې تاسو ته ډېر هغه څیزونه بیانوي چې تاسو به له كتابه پټول او له ډېرو څخه تېرېږي، یقینًا تاسو ته د الله له جانبه رڼا او روښانه كتاب راغلى دى');
INSERT INTO "abulsalam" VALUES(5,16,'5:16',' چې په ده سره الله د سلامتۍ د لارو هدایت كوي هغه چا ته چې د هغه (الله) د رضا تابع وي او هغه په خپل حكم سره دوى لره له تیارو نه رڼا ته راوباسي او دوى ته د نېغې لارې هدایت كوي');
INSERT INTO "abulsalam" VALUES(5,17,'5:17',' یقینًا یقینًا هغه كسان كافر شول چې هغوى ویل: بېشكه الله هم دا د مریمې زوى مسیح دى، ته (ورته) ووایه: د الله په وړاندې به څوك د څه شي اختیار ولري كه هغه اراده وكړي چې د مریمې زوى مسیح او د هغه مور او څوك چې په ځمكه كې دي ؛ هغه ټول هلاك كړي، او خاص د الله لپاره د اسمانونو او ځمكې بادشاهي ده او د هغه څه چې د دې دواړو په مینځ كې دي، هغه چې څه وغواړي پیدا كوي يې او الله پر هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(5,18,'5:18',' او یهودیانو او نصرانیانو وویل: مونږ د الله زامن او د هغه دوستان یو، ته (ورته) ووایه: نو بیا ولې هغه تاسو ته ستاسو د ګناهونو په وجه عذاب دركوي؟ بلكې تاسو د هغه په مخلوقاتو كې انسانان یئ، هغه چې چا ته وغواړي بخښنه كوي او چا ته چې وغواړي عذاب وركوي او خاص د الله لپاره د اسمانونو او ځمكې بادشاهي ده او د هغه څه چې د دې دواړو په مینځ كې دي او خاص د هغه په طرف ورتلل دي');
INSERT INTO "abulsalam" VALUES(5,19,'5:19',' اى اهل كتابو! یقینًا تاسو ته زمونږ رسول، د رسولانو له وقفې نه پس راغلى دى، چې تاسو ته احكام بیانوي چې تاسو (بیا) دا ونه وایئ چې مونږ ته خو نه څوك زېرى وركوونكى راغلى و او نه وېروونكى، نو (دا دى چې) بې له شكه تاسو ته زېرى وركوونكى او وېروونكى راغى او الله پر هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(5,20,'5:20',' او كله چې موسٰی خپل قوم ته وویل: اى قومه! تاسو د الله نعمتونه یاد كړئ چې پر تاسو باندې (شوي) دي كله چې يې په تاسو كې پېغمبران پیدا كړل او تاسو يې ازاد (او خودمختاره) كړئ او تاسو ته يې هغه څه در كړل چې په عالمونو كې يې هیچا ته هم نه وو وركړي');
INSERT INTO "abulsalam" VALUES(5,21,'5:21',' اى زما قومه! تاسو هغه پاكې ځمكي ته داخل شئ چې الله تاسو لپاره لیكلې ده او په خپلو شاګانو بېرته مه ګرځئ (ګنې) نو تاوانیان به شئ');
INSERT INTO "abulsalam" VALUES(5,22,'5:22',' دوى وویل: اى موسٰی! بېشكه په دغې (پاكې ځمكه) كې ډېر زورور خلق دي او بېشكه مونږ به كله هم دغې ته داخل نشو تر هغه پورې چې دوى له هغې نه ووځي، نو كه دوى له هغې نه ووتل، نو بېشكه مونږ (هغې ته) ننوتونكي یو');
INSERT INTO "abulsalam" VALUES(5,23,'5:23',' وویل دوه سړیو له هغو كسانو نه چې (له الله نه) وېرېدل، (او) الله په هغو دواړو انعام كړى و: تاسو په دوى باندې په دروازه كې (حمله كوونكي) داخل شئ، نو كله چې تاسو هغې ته ننوتئ، نو بېشكه تاسو غالبه یئ او تاسو خاص په الله باندې توكل وكړئ كه چېرې تاسو مومنان یئ');
INSERT INTO "abulsalam" VALUES(5,24,'5:24',' هغوى وویل: اى موسٰی! بېشكه مونږه دې (ښاریې) ته له سره نه ننوځو هېڅكله هم، څو پورې چې دوى په هغې كې وي، نو ته او ستا رب لاړ شئ، نو تاسو دواړه وجنګېږئ، مونږ به همدلته ناست یو');
INSERT INTO "abulsalam" VALUES(5,25,'5:25',' وویل (موسٰی) اى زما ربه! بېشكه زه اختیار نه لرم مګر د خپل ځان او د خپل ورور، نو ته زمونږ په مینځ كې او د فاسق (نافرمان) قوم په مینځ كې جدايي راوله');
INSERT INTO "abulsalam" VALUES(5,26,'5:26',' وویل (الله) پس بېشكه هغه (مقدسه ځمكه) په دوى باندې څلوېښت كاله بنده (ممنوعه) ده، چې دوى به په ځمكه كې حیران سرګردان ګرځي، نو ته په فاسق (نافرمانه) قوم باندې مه خفه كېږه');
INSERT INTO "abulsalam" VALUES(5,27,'5:27',' او دوى ته د ادم د دوه زامنو خبر په حقه سره ولوله، كله چې هغو دواړو قربانې وړاندې كړه، نو په هغوى كې له یو څخه قبوله شوه او له بل څخه قبوله نشوه، هغه وویل: زه به خامخا لازمًا تا ووژنم، هغه (ورته) وویل: بېشكه همدا خبره ده چې الله له پرهېزګارانو څخه (قربانې) قبلوي');
INSERT INTO "abulsalam" VALUES(5,28,'5:28',' كه چېرې ته زما د وژلو لپاره خپل لاس را اوږد كړې (، نو) زه خپل لاس ستا د وژلو لپاره تا ته نه در اوږدوم، بېشكه زه له الله رب العلمین څخه وېرېږم');
INSERT INTO "abulsalam" VALUES(5,29,'5:29',' بېشكه زه غواړم چې ته زما (د وژلو) په ګناه باندې او په خپلې (مخكېنۍ) ګناه باندې اخته شي، بیا چې د دوزخ په ملګرو كې شې او دا د ظالمانو جزا ده');
INSERT INTO "abulsalam" VALUES(5,30,'5:30',' نو هغه ته خپل نفس د خپل ورور وژل ښكلي (او) اسان كړل، نو هغه يې قتل كړ، نو شو له تاوانيانو نه');
INSERT INTO "abulsalam" VALUES(5,31,'5:31',' نو الله یو كارغه راولېږه، چې ځمكه يې كنستله، د دې لپاره چې هغه (قاتل) ته وښیي چې څنګه هغه د خپل ورور جثه (كالبوت) پټه كړي، هغه وویل: هۍ افسوس! ایا زه د دې كارغه په شان له كېدو نه هم عاجز شوم، چې د خپل ورور جثه مې پټه كړې وى، نو له پښېمانه كېدونكو څخه شو');
INSERT INTO "abulsalam" VALUES(5,32,'5:32',' له همدې وجې نه مونږ په بني اسرائیلو باندې لازم كړي وو چې بېشكه خبره دا ده چې چا هم یو نفس له بدلې نه بغیر، یا په ځمكه كې له ورانكارۍ نه بغیر وواژه، نو ګویا كې هغه ټول خلق ووژل او چا چې د دغه نفس ژوند بچ كړ، نو ګویا كې هغه د ټولو خلقو ژوند بچ كړ او یقینًا یقینًا دوى ته زمونږه رسولانو څرګندې نښې راوړې وې، یقینًا بیا له دې نه پس (هم) په دوى كې ډېر خلق په ځمكه كې خامخا زیاتي كوونكي دي');
INSERT INTO "abulsalam" VALUES(5,33,'5:33',' بېشكه اصل خبره همدا ده چې څوك چې د الله او د هغه له رسول سره جنګ كوي او په ځمكه كې د وراني كولو كوښښ كوي، د هغوى سزا دا ده چې دوى دې قتل كړى شي، یادې په دار كړى شي، یا دې د دوى لاسونه او پښې ادلې بدلې پرې كړى شي، یا دې دوى له (دغې) ځمكې نه وشړل شي، دا د دوى لپاره په دنیا كې رسوايي ده او د دوى لپاره په اخرت كې بېخي لوى عذاب دى');
INSERT INTO "abulsalam" VALUES(5,34,'5:34',' مګر هغه كسان چې توبه وباسي مخكې له دې نه چې تاسو په دوى باندې قابو ومومئ، نو پوه شئ چې یقینًا الله ډېر بخښونكى، بې حده مهربان دى ');
INSERT INTO "abulsalam" VALUES(5,35,'5:35',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو له الله نه ووېرېږئ او هغه ته قرب (نژدې والى) ولټوئ او تاسو د هغه په لار كې جهاد كوئ، د دې لپاره چې تاسو كامیاب شئ');
INSERT INTO "abulsalam" VALUES(5,36,'5:36',' بېشكه هغه كسان چې كفر يې كړى دى كه د ځمكې ټول دولت د دوى شي او له دغې (دولت) سره د ده هومره (نور هم)، د دې لپاره چې دا د قیامت د ورځې د عذاب په بدله كې وركړي (،نو) دا (فدیه) به له دوى نه قبوله نه كړى شي او د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(5,37,'5:37',' دوى به غواړي چې له اور نه ووځي، حال دا چې دوى له هغه نه وتونكي نه دي او د دوى لپاره دايمي (همېشنى) عذاب دى');
INSERT INTO "abulsalam" VALUES(5,38,'5:38',' او غل او غله، نو تاسو د دوى لاسونه د دوى د كړي عمل په بدله كې پرې كړئ، چې د الله له جانبه عبرتناكه سزا ده او الله ډېر غالب، ښه حكمت والا دى ');
INSERT INTO "abulsalam" VALUES(5,39,'5:39',' نو چا چې له خپل ظلم نه پس توبه وكړه او (د ځان) اصلاح يې وكړه، نو بېشكه الله د ده توبه قبلوي، بېشكه الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(5,40,'5:40',' ایا ته پوه نه يې چې بېشكه خاص د الله لپاره د اسمانونو او ځمكې بادشاهي ده، چا ته چې وغواړي عذاب وركوي او چا ته چې وغواړي بخښنه كوي او الله پر هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(5,41,'5:41',' اى رسوله! تا دې هغه كسان غمجن نه كړي چې په كفر كې له یو بل نه تلوار كوي، له هغو خلقو څخه چې په خپلو خولو وايي چې مونږ ایمان راوړى دى، حال دا چې د دوى زړونو ایمان نه دى راوړى او له هغو خلقو څخه چې یهودیان دي، دوى د دروغو ډېر اورېدونكي (قبلوونكي) دي، د هغو نورو خلقو لپاره ډېر اورېدونكي دي چې تا ته نه دي راغلي، دوى خبرې بدلوي وروسته له دې نه چې هغه په خپلو ځایونو كې (ثابتې) شي، دوى وايي كه چېرې تاسو ته دا دركړى شي، نو هغه واخلئ، او كه چېرې تاسو ته هغه در نه کړى شي، نو بیا ځان وساتئ او هغه څوك چې الله د هغه ګمراه كول وغواړي، (بیا) نو ته د هغه لپاره د الله په وړاندې د هېڅ شي اختیار نه لرې، دا هغه كسان دي چې الله د دوى د زړونو د پاكولو اراده نه ده كړې، د همدوى لپاره په دنیا كې رسوايي ده او د همدوى لپاره په اخرت كې بېخي لوى عذاب دى');
INSERT INTO "abulsalam" VALUES(5,42,'5:42',' دوى د دروغو ډېر اورېدونكي دي او د حرامو ډېر خوړونكي دي، نو كه چېرې دوى تا ته راشي، نو ته د دوى په مینځ كې فیصله وكړه، یا له دوى نه مخ واړوه او كه چېرې ته له دوى نه مخ واړوې، نو دوى به تا ته هیڅكله هم هېڅ شى ضرر در ونه رسولى شي، او كه چېرې ته فیصله كوې، نو د دوى په مینځ كې په انصاف سره فیصله وكړه، بېشكه الله له انصاف كوونكو سره مینه كوي');
INSERT INTO "abulsalam" VALUES(5,43,'5:43',' او دوى (د زنا په باره كې) فیصله تا ته څنګه راوړي؟! حال دا چې له دوى سره تورات شته، چې په هغه كې (د دې په باره كې) د الله حكم دى، بیا دوى له دې (ستا له فیصلې) نه بعد بېرته ګرځي او دغه خلق له سره مومنان نه دي');
INSERT INTO "abulsalam" VALUES(5,44,'5:44',' بېشكه مونږ تورات نازل كړى دى، چې په هغه كې هدایت او رڼا ده، په ده سره به نبیانو، چې حكم منونكي وو، د یهودیانو لپاره فیصلې كولې او الهي خلقو او عالمانو (به هم فیصلې كولې)، ځكه چې دوى د الله د كتاب ساتونكي ګرځول شوي وو او دوى په ده باندې ګواهان وو، نو تاسو له خلقو نه مه وېرېږئ او له ما نه وېرېږئ او زما د ایتونو په بدل كې لږ قیمت مه اخلئ او چا چې د الله په نازل كړي (كتاب، قانون) باندې فیصله ونه كړه، نو همدغه خلق كافران دي');
INSERT INTO "abulsalam" VALUES(5,45,'5:45',' او مونږ په دوى باندې په دې (تورات) كې فرض كړي وو چې بېشكه ځان د ځان په بدله كې دى او سترګه د سترګې په بدله كې او پوزه د پوزې په بدله كې او غوږ د غوږ په بدله كې او غاښ د غاښ په بدله كې دى او زخمونه (هم) قصاص (برابر) دي، نو چا چې د دې (قصاص) معافي وكړه، نو دا د دغه (مجرم) لپاره كفاره ده (په دې سره له دې ګناه څخه پاكېږي) او چا چې د الله په نازل كړي (كتاب، قانون) باندې فیصله ونه كړه، نو همدغه خلق ظالمان دي ');
INSERT INTO "abulsalam" VALUES(5,46,'5:46',' او مونږه په هغوى پسې د دوى په نښو باندې د مریمې زوى عیسٰی ولېږه چې له ده نه د وړاندې (كتاب) تورات تصدیق كوونكى و او مونږ هغه ته انجیل وركړ، چې په هغه كې هدایت او رڼا وه او چې له ده نه د مخكې (كتاب) تورات تصدیق كوونكى و او د پرهېزګارانو لپاره هدایت او نصیحت و');
INSERT INTO "abulsalam" VALUES(5,47,'5:47',' او لازم ده چې انجیل والا دې فیصله وكړي په هغه څه چې الله په دې (انجیل) كې نازل كړي دي (چې د قرآن او محمدصلى الله عليه وسلم تصدیق دى) او چا چې د الله په نازل كړي (كتاب، قانون) باندې فیصله ونه كړه، نو همدغه خلق فاسقان دي');
INSERT INTO "abulsalam" VALUES(5,48,'5:48',' او (اى نبي!) مونږ تا ته كتاب په حقه سره نازل كړى دى، چې د هغو كتابونو تصدیق كوونكى دى چې له ده نه مخكې دي او د هغوى محافظ دى، نو ته د دوى په مینځ كې فیصله وكړه په هغه څه سره چې الله نازل كړي دي او ته د دوى د خواهشاتو پیروي مه كوه، چې هغه حق پرېږدې چې تا ته راغلى دى، مونږ په تاسو كې د هر یو لپاره یو دستور او لاره مقرر كړې ده، او كه الله غوښتلى، نو تاسو به يې یو امت ګرځولي وئ، او لېكن (بېل بېل يې كړئ) د دې لپاره چې تاسو وازمايي په هغه څه كې چې تاسو ته يې دركړي دي، نو تاسو نېكیو ته له یو بل نه مخكې كېږئ، خاص الله ته ستاسو د ټولو واپسي ده، نو هغه به تاسو ته د هغو كارونو خبر دركړي چې تاسو په هغو كې اختلاف كاوه');
INSERT INTO "abulsalam" VALUES(5,49,'5:49',' او دا چې ته د دوى په مینځ كې فیصله كوه په هغه څه سره چې الله نازل كړي دي او ته د دوى د خواهشاتو پیروي مه كوه او ته له دوى نه محتاط اوسه (هسې نه) چې دوى تا له ځینو (حكمونو) نه واړوي، چې تا ته الله نازل كړي دي، نو كه چېرې دوى وګرځېدل، نو پوه شه چې بېشكه همدا خبره ده چې الله غواړي چې دوى ته د دوى د ځینو ګناهونو په وجه سزا ورسوي او بېشكه په خلقو كې ډېر يقينًا فاسقان دي ');
INSERT INTO "abulsalam" VALUES(5,50,'5:50',' ایا نو دوى د جاهلیت فیصله غواړي؟ او په فیصله كولو كې له الله نه (بل) څوك ډېر ښه دى، د هغه قوم لپاره چې یقین كوي!');
INSERT INTO "abulsalam" VALUES(5,51,'5:51',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو یهودیان او نصرانیان دوستان مه جوړوئ، د دوى ځینې د ځینو نورو دوستان دي او په تاسو كې چې چا له دوى سره دوستي وكړه، نو یقینًا هغه له همدوى څخه دى، بېشكه الله ظالم قوم ته نېغه لار نه ښیي');
INSERT INTO "abulsalam" VALUES(5,52,'5:52',' نو ته به هغه كسان وینې چې د هغوى په زړونو كې بېماري ده چې په (مینه د) دوى كې منډې وهي او دوى وايي: مونږ وېرېږو (له دې نه) چې مونږ ته څه مصیبت راورسي، نو قریب ده چې الله فتح راولي، یا له خپلې خوا نه كوم بل حكم (راولي) نو دوى به پښېمانه شي پر هغو خبرو چې دوى په خپلو نفسونو كې پټې ساتلې وې');
INSERT INTO "abulsalam" VALUES(5,53,'5:53',' او مومنان به (د حیرانتیا په سبب) وايي: ایا دا هغه خلق دي چې پر الله يې ډېر پاخه قسمونه خوړلي وو چې بېشكه دوى خامخا له تاسو سره دي، د دوى عملونه ضايع شوي دي، نو دوى تاوانیان شول');
INSERT INTO "abulsalam" VALUES(5,54,'5:54',' اى هغو كسانو چې ایمان يې راوړى دى! په تاسو كې چې څوك له خپل دین نه مرتد شو، نو ژر ده چې الله به داسې خلق راولي چې هغه (الله) به له دوى سره مینه كوي او دوى به له هغه (الله) سره مینه كوي، په مومنانو به ډېر نرمي كوونكي وي او په كافرانو باندې به ډېر سخت وي، د الله په لار كې به جهاد كوي او د هېڅ ملامت كوونكي له ملامتیا نه به نه وېرېږي، دا د الله فضل دى، چا ته چې وغواړي هغه ته يې وركوي او الله ډېر د فراخۍ والا، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(5,55,'5:55',' ستاسو دوست يواځې (او يواځې) الله او د هغهٔ رسول دى او هغه كسان دي چې ایمان يې راوړى دى، (یعنې) هغه كسان چې لمونځ قايموي او زكات وركوي او دوى ركوع كوونكي دي');
INSERT INTO "abulsalam" VALUES(5,56,'5:56',' او څوك چې له الله او د هغه له رسول او مومنانو سره دوستي وكړي، نو یقینًا هم د الله ډله غالبه كېدونكې ده');
INSERT INTO "abulsalam" VALUES(5,57,'5:57',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو هغه كسان دوستان مه جوړوئ چې ستاسو دین يې ټوقې او لوبې ګرځولى دى، له هغو كسانو نه چې له تاسو نه مخكې ورته كتاب وركړى شوى و او مه كفار (دوستان جوړوئ) او له الله نه ووېرېږئ كه چېرې تاسو مومنان یئ');
INSERT INTO "abulsalam" VALUES(5,58,'5:58',' او كله چې تاسو لمانځه ته اواز كوئ (،نو) دوى هغه ټوقې او لوبې وګرځوي، دا ځكه چې بېشكه دوى داسې خلق دي چې هېڅ عقل نه لري');
INSERT INTO "abulsalam" VALUES(5,59,'5:59',' (اى نبي!) ته ورته ووایه: اى اهل كتابو! تاسو مونږه (په هېڅ وجه) د بدۍ (او) عیب والا نه ګڼئ، مګر په دې چې مونږ پر الله ایمان راوړى دى او پر هغه څه چې مونږ ته نازل كړى شوي دي او پر هغه څه چې له ده نه مخكې نازل كړى شوي دي او دا ځكه چې یقینًا ستاسو اكثره (خلق) فاسقان (له حده وتونكي) دي');
INSERT INTO "abulsalam" VALUES(5,60,'5:60',' ته ووایه! ایا زه تاسو ته د الله په نیز د جزا په لحاظ سره له دوى نه د بدترو خلقو خبر در نه كړم؟ (دا) هغه څوك دي چې الله پر دوى لعنت كړى دى او پر دوى يې غضب كړى دى او له دوى نه يې بیزوګان او خنزیران جوړ كړي دي او دوى (هغه څوك دي چې يې) د طاغوت عبادت كړى دى، دغه كسان د ځاى په لحاظ سره ډېر بدتره دي او له حقې لارې نه ډېر ګمراه دي');
INSERT INTO "abulsalam" VALUES(5,61,'5:61',' او كله چې دوى تاسو ته راشي (،نو) دوى وايي: مونږ ایمان راوړى دى، حال دا چې یقینًا دوى له كفر سره داخل شوي وو او یقینًا له هغه (كفر) سره ووتل او الله ډېر ښه پوه دى په هغو خبرو چې دوى پټولې');
INSERT INTO "abulsalam" VALUES(5,62,'5:62',' او ته به په دوى كې ډېر خلق وینې چې په ګناه او ظلم كې او په خپلې حرام خورۍ كې منډې وهي، خامخا بد دى هغه كار چې دوى كاوه');
INSERT INTO "abulsalam" VALUES(5,63,'5:63',' الهي خلق او عالمان دغو (یهودیانو) لره له خپلو د ګناه له ویناوو نه او له خپلې حرام خورۍ نه ولې نه منع كوي؟ خامخا بد دى هغه كار (د خاموشۍ) چې دوى كاوه');
INSERT INTO "abulsalam" VALUES(5,64,'5:64',' وویل یهودیانو: د الله لاس تړل شوى دى، د دوى لاسونه دې وتړل شي او د دې وینا په سبب دې پر دوى لعنت وشي، بلكې د هغه دواړه لاسونه خلاص دي، څنګه چې وغواړي انفاق كوي او خامخا هغه (كتاب) به، چې تا ته ستا د رب له جانبه نازل كړى شوى دى؛ په دوى كې د ډېرو خلقو سركشي او كفر خامخا ورزیات كړي او مونږه د دوى په مینځ كې د قیامت د ورځې پورې دښمني او بغض واچاوه، هر كله چې دوى د جنګ لپاره اور بلوي (،نو) الله هغه مړ كړي او دوى په ځمكه كې د وراني لپاره منډې وهي او الله وراني كوونكي نه خوښوي');
INSERT INTO "abulsalam" VALUES(5,65,'5:65',' او كه یقینًا اهل كتابو ایمان راوړى وى او پرهېزګاره شوي وى (، نو) لازمًا به مونږه له دوى نه د دوى ګناهونه لرې كړي وى او خامخا به مونږه دوى د نعمتونو جنتونو ته داخل كړي وو');
INSERT INTO "abulsalam" VALUES(5,66,'5:66',' او كه یقینًا دوى (احكام د) تورات او انجیل قايم كړي وى او هغه چې دوى ته د دوى د رب له جانبه نازل كړى شوي دي (،نو) دوى به خامخا خوراك كړى و د خپل له پاسه نه او د خپلو پښو له لاندې نه، په دوى كې یوه مینځلارې ډله ده او ډېر زیات په دوى كې (داسې) دي چې بد دي هغه چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(5,67,'5:67',' اى رسوله! ته ورسوه هغه څه چې تا ته د خپل رب له جانبه نازل كړى شوي دي او كه ته (دا كار) ونه كړې، نو تا د هغه (الله) پېغام نه دى رسولى او الله تا له خلقو نه بچ ساتي، بېشكه الله كافر قوم ته نېغه لار نه ښیي');
INSERT INTO "abulsalam" VALUES(5,68,'5:68',' ته (ورته) ووایه: اى اهل كتابو! تاسو (د حق) په هېڅ شي باندې نه یئ تر دې چې تاسو تورات او انجیل قايم كړئ او هغه (تعليمات) چې تاسو ته د خپل رب له جانبه نازل كړى شوي دي، خامخا به هغه (كتاب)، چې تا ته د خپل رب له جانبه نازل كړى شوى دى؛ په دوى كې د ډېرو خلقو سركشې او كفر يقينًا ورزیات كړي، نو ته په كافر قوم باندې مه خفه كېږه');
INSERT INTO "abulsalam" VALUES(5,69,'5:69',' بېشكه هغه كسان چې ایمان يې راوړى دى او هغه كسان چې یهودیان او صابیان او نصرانیان دي، هر چا چې پر الله او د اخرت پر ورځ ایمان راوړ او نېك عمل يې وكړ، نو په دوى باندې نه څه وېره شته او نه به دوى غمژن كېږي');
INSERT INTO "abulsalam" VALUES(5,70,'5:70',' خامخا یقینًا مونږ له بني اسرائیلو نه پخه وعده اخيستې وه او دوى ته مونږ ډېر رسولان لېږلي وو، هر كله چې به هم دوى ته كوم رسول هغه (حكمونه) راوړل چې د دوى نفسونو به نه خوښول (،نو) یوه ډله دوى دروغژنه وګڼله او بله ډله دوى وژله');
INSERT INTO "abulsalam" VALUES(5,71,'5:71',' او دوى دا ګڼله چې هېڅ ازمېښت به نه كېږي، نو ړانده شول او كاڼه شول، بیا الله دوى ته (په رحمت سره) توجه وكړه، بیا په دوى كې ډېر خلق ړانده شول او كاڼه شول او الله ښه لیدونكى دى هغه څه لره چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(5,72,'5:72',' خامخا یقینًا كافر شول هغه خلق چې ويې ويل:بېشكه الله همدا د مریمې زوى مسیح دى، حال دا چې مسیح، بني اسرائیلو ته وویل: تاسو د الله عبادت كوئ، چې زما رب دى او ستاسو رب دى، بېشكه خبره دا ده چې څوك له الله سره شریك نیسي، نو یقینًا په هغه باندې الله جنت حرام كړى دى او د هغه استوګنه اور دى او د ظالمانو لپاره هیڅوك مدد كوونكي نشته');
INSERT INTO "abulsalam" VALUES(5,73,'5:73',' خامخا یقینًا كافر شول هغه خلق چې ويې ويل:بېشكه الله د درېوو دريم دى، حال دا چې هېڅ لايق د عبادت نشته مګر یو معبود، او كه چېرې دوى له هغو خبرو نه منع نشي چې دوى يې كوي، نو خامخا لازمًا به په دوى كې كافر شويو خلقو ته ډېر دردوونكى عذاب ورسېږي');
INSERT INTO "abulsalam" VALUES(5,74,'5:74',' ایا ولې دوى الله ته توبه نه وباسي او له هغه نه بخښنه نه غواړي؟! حال دا چې الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(5,75,'5:75',' نه دى د مریمې زوى مسیح مګر یو رسول دى، یقینًا له ده نه مخكې رسولان تېر شوي دي او د ده مور صِدیقه (ډېره زیاته رښتینې) وه، دې دواړو به طعام خوړلو، ته وګوره مونږه څنګه دوى ته ایتونه بیانوو، بیا ته وګوره دوى كوم طرف اړولى شي');
INSERT INTO "abulsalam" VALUES(5,76,'5:76',' ته ووایه: ایا تاسو له الله نه غیر د هغه شي عبادت كوئ چې تاسو لپاره نه د نقصان اختیار لري او نه د نفع او همدا الله ښه اورېدونكى، ښه پوه دى ');
INSERT INTO "abulsalam" VALUES(5,77,'5:77',' ته (ورته) ووایه: اى اهلِ كتابو! تاسو په خپل دین كې ناحقه زیاتى مه كوئ او د هغه قوم د خواهشاتو پیروي مه كوئ چې له دې نه مخكې ګمراه شوي دي او هغوى ډېر خلق ګمراه كړي دي او له نېغې لارې نه بې لارې شوي دي');
INSERT INTO "abulsalam" VALUES(5,78,'5:78',' په هغو كسانو باندې، چې په بني اسرائیلو كې كافر شوي دي، لعنت شوى دى د داود او د مریمې زوى عیسٰی په ژبه، دا ځكه چې دوى نافرماني وكړه او دوى له حد نه تېرېدل');
INSERT INTO "abulsalam" VALUES(5,79,'5:79',' دوى به له هغه ناروا كار نه یو بل نه منع كول چې دوى به هغه كاوه، خامخا بد دى هغه (كار) چې دوى به كاوه');
INSERT INTO "abulsalam" VALUES(5,80,'5:80',' ته به په دوى كې ډېر (داسې) وینې چې له هغو كسانو سره دوستي كوي چې كافر شوي دي، خامخا بد دى هغه شى چې د دوى لپاره د دوى نفسونو مخكې لېږلى دى، دا چې الله دوى ته په غصه شو او دوى به په عذاب كې همېشه وي');
INSERT INTO "abulsalam" VALUES(5,81,'5:81',' او كه چېرې دوى پر الله او پر نبي او پر هغه څه چې ده ته نازل كړى شوي دي ایمان لرلى (،نو) دوى به هغه (كافران) دوستان نه وو جوړ كړي او لېكن په دوى كې ډېر خلق فاسقان دي');
INSERT INTO "abulsalam" VALUES(5,82,'5:82',' بې له شكه يقينًا چې ته به له مومنانو سره په دښمنۍ كې تر ټولو خلقو زیات سخت، یهودیان مومې او هغه كسان چې مشركان دي او خامخا لازمًا به ته له مومنانو سره په دوستۍ كې تر ټولو خلقو زیات نژدې هغه كسان مومې چې ویلي دي: بېشكه مونږ نصرانیان یو، دا ځكه چې په دوى كې په رښتيا عبادت ګذاره عالمان او راهبان (زاهدان) دي او دا ځكه چې واقعاً دوى تكبر نه كوي');
INSERT INTO "abulsalam" VALUES(5,83,'5:83',' او كله چې دوى هغه (قرآن) واوري چې رسول ته نازل كړى شوى دى (، نو) ته به د دوى سترګې وینې چې اوښکې به ترې بهېږي، په سبب د هغه حق چې دوى وپېژنده، دوى وايي: اى زمونږه ربه! مونږ ایمان راوړى دى، نو ته مونږ له ګواهي كوونكو سره ولیكه');
INSERT INTO "abulsalam" VALUES(5,84,'5:84',' او مونږ لره څه دي چې پر الله به ایمان نه راوړو او پر هغه حق چې مونږ ته راغلى دى، حال دا چې مونږ د دې طمع لرو چې زمونږ رب به مونږ د نېكانو خلقو په ډله كې داخل كړي');
INSERT INTO "abulsalam" VALUES(5,85,'5:85',' نو الله دوى ته د دوى د وینا په بدل كې داسې جنتونه وركړل چې د هغو له لاندې وَلِې بهېږي، چې دوى به په هغو كې همېشه وي او دا د نېكي كوونكو بدله ده');
INSERT INTO "abulsalam" VALUES(5,86,'5:86',' او هغه كسان چې كافر شوي دي او زمونږ ایتونه يې دروغ ګڼلي دي، دغه كسان د دوزخ ملګري (دوزخيان) دي');
INSERT INTO "abulsalam" VALUES(5,87,'5:87',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو هغه پاكیزه څیزونه مه حراموئ چې ستاسو لپاره الله حلال كړي دي او له حد نه مه تېرېږئ، بېشكه الله له حد نه تېرېدونكي نه خوښوي');
INSERT INTO "abulsalam" VALUES(5,88,'5:88',' او تاسو له هغه (رزق) نه، چې الله تاسو ته دركړى دى؛ حلال پاكیزه خورئ او له هغه الله نه ووېرېږئ چې تاسو پر هغه ایمان لرونكي یئ');
INSERT INTO "abulsalam" VALUES(5,89,'5:89',' الله تاسو، ستاسو په قسمونو كې په لَغْو (مُهمَل قسم) سره نه نیسي، او لېكن تاسو پر هغو قسمونو نیسي چې تاسو په پخې ارادې سره خوړلي وي، نو د دغه (قسم) كفاره لسو مسكینانو ته خوراك وركول دي له هغه درمیانه (خوراك) نه چې تاسو يې پر خپلو كورنیو خورئ، یا د هغو (لسو مسكینانو) جامه ده، یا د یو څټ (غلام، یا وینځې) ازادول دي، نو چا چې (دغه) ونه موندل، نو (د هغه پر ذمه) درې ورځې روژې دي، دا ستاسو د قسمونو كفاره ده، كله چې تاسو قسم وخورئ او تاسو د خپلو قسمونو حفاظت كوئ، همدارنګه الله تاسو ته خپل ایتونه بیانوي، د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(5,90,'5:90',' اى هغو كسانو چې ایمان يې راوړى دى! بېشكه همدا خبره ده چې شراب او جواري او بتان او د فال غشي پلیت دي، د شیطان له عمله دي، نو تاسو له دې نه ځان وساتئ، د دې لپاره چې تاسو كامیاب شئ');
INSERT INTO "abulsalam" VALUES(5,91,'5:91',' یقینًا همدا خبره ده چې شیطان غواړي چې ستاسو په مینځ كې د شرابو او جوارۍ په ذریعې سره دښمني او بغض واچوي او تاسو د الله له ذكر نه او له لمانځه نه بند كړي، نو ایا تاسو (له شرابو او جوارۍ نه) منع كېدونكي یئ؟');
INSERT INTO "abulsalam" VALUES(5,92,'5:92',' او تاسو د الله اطاعت كوئ او د (هغه د) رسول اطاعت كوئ او ځان بچ كړئ، نو كه چېرې تاسو وګرځېدلئ، نو پوه شئ چې یقینًا زمونږ د رسُول پر ذمه خو بس ښكاره رسَول (تبلیغ كول) دي');
INSERT INTO "abulsalam" VALUES(5,93,'5:93',' هېڅ ګناه نشته په هغو كسانو باندې چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، په هغه (شرابو) كې چې هغوى (له دې حكم نه مخكې) خوړلي (څښلي) دي، كله چې هغوى پرهېزګاره شوي وو او ایمان يې راوړى و او نېك عملونه يې كړي وو، بیا پرهېزګاره شوي وو او ایمان يې راوړى و، بیا پرهېزګاره شوي وو او نېكي يې كړې وه او الله نېكي كوونكي خوښوي');
INSERT INTO "abulsalam" VALUES(5,94,'5:94',' اى هغو كسانو چې ایمان يې راوړى دى! الله به خامخا لازمًا تاسو په یو شي سره ازمايي چې ښكار به وي، چې ستاسو لاسونه او ستاسو نېزې به هغه ته رسېږي، د دې لپاره چې الله معلوم (څرګند) كړي هغه چا لره چې له هغه نه په ناليدلو وېرېږي، نو چا چې له دې نه بعد زیاتى وكړ، نو د هغه لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(5,95,'5:95',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو ښكار مه وژنئ (ښكار مه كوئ)، په دې حال كې چې تاسو په احرام كې یئ، او په تاسو كې چې چا هغه قصدًا او عَمْدًا قتل كړ، نو (پر هغه) جزا (لازمه) ده د هغه څاروي په مثل (څاروى) چې ده وژلى دى، په تاسو كې دوه عدل والا كسان به د دې فیصله كوي، چې كعبې ته رسېدونكې قرباني به وي، یا (د ده پر ذمه) كفاره ده چې مسكینانو ته خوراك وركول دي، یا د دې په برابر روژې نیول دي، د دې لپاره چې د خپل كار سزا وڅكي، الله د هغه څه معافي كړې ده چې مخكې تېر شوي دي او څوك چې بیا راوګرځي، نو الله به له هغه نه انتقام واخلي او الله ډېر غالب دى، انتقام والا دى');
INSERT INTO "abulsalam" VALUES(5,96,'5:96',' ستاسو لپاره د سمندر ښكار او د هغه طعام حلال كړى شوى دى، ستاسو او د (نورو) مسافرو د فايدې لپاره، او پر تاسو باندې د وچې ښكار حرام كړى شوى دى، څو پورې چې تاسو په احرام كې یئ او تاسو له هغه الله نه ووېرېږئ چې تاسو به خاص هغه ته ورجمع كولى شئ');
INSERT INTO "abulsalam" VALUES(5,97,'5:97',' كعبه، چې د عزت كور دى، الله د خلقو د قیام سبب ګرځولې ده او د عزت میاشت او د حرم قرباني او امېل والا څاروي، دا د دې لپاره چې تاسو پوه شئ چې یقینًا الله پوهېږي پر هر هغه څه چې په اسمانونو كې دي او پر (هر) هغه څه چې په ځمكه كې دي او دا چې یقینًا الله پر هر شي ښه پوه دى');
INSERT INTO "abulsalam" VALUES(5,98,'5:98',' تاسو پوه شئ چې بېشكه الله سخت عذاب والا دى او دا چې بېشكه الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(5,99,'5:99',' د رسُول په ذمه نشته مګر يواځې (د حق) رسَول، او الله پوهېږي پر هغه څه چې تاسو يې څرګندوئ او پر هغه څه چې تاسو يې پټوئ');
INSERT INTO "abulsalam" VALUES(5,100,'5:100',' ته ووایه: ناپاك او پاك سره برابر نه دي اګر كه تا د ناپاكو ډېروالى په تعجب كې واچوي، نو اى د خالص عقل خاوندانو! تاسو له الله نه ووېرېږئ، د دې لپاره چې تاسو كامیاب شئ');
INSERT INTO "abulsalam" VALUES(5,101,'5:101',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د داسې څیزونو په باره كې تپوس مه كوئ چې كه تاسو ته ظاهر كړى شي (،نو) تاسو به خفه كړي، او كه چېرې تاسو په هغه وخت كې تپوس وكړئ كله چې قرآن نازلولى شي (،نو) تاسو ته به ظاهر كړى شي، الله تاسو ته (د تېرو سوالونو) معافي كړې ده او الله ډېر بخښونكى، ډېر تحمل والا دى');
INSERT INTO "abulsalam" VALUES(5,102,'5:102',' یقینًا له تاسو نه مخكې یو قوم دغه غوښتنې كړې وې (لكه نصاراوو دسترخوان او ثموديانو اوښه غوښتې وه)، بیا دوى په هغو (يا په سبب د هغو سره) كافر شول');
INSERT INTO "abulsalam" VALUES(5,103,'5:103',' الله نه ده مقرر كړې )د شرعي حكم په طور) هېڅ بَحیره او نه سايبه او نه وصیله او نه حام، او لېكن هغه كسان چې كافر شوي دي دوى په الله باندې دروغ تپي او د دوى اكثره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(5,104,'5:104',' او كله چې دوى ته وویل شي چې راشئ هغه شي ته چې الله نازل كړى دى او رسول ته (راشئ)، دوى وايي: مونږ ته هغه (لار) كافي ده چې په هغې باندې مونږ خپل پلرونه موندلي دي، ایا اګر كه د دوى پلرونه داسې وو چې نه پر كوم شي پوهېدل او نه هدایت موندونكي وو');
INSERT INTO "abulsalam" VALUES(5,105,'5:105',' اى هغو كسانو چې ایمان يې راوړى دى! پر تاسو د خپل ځان بچ كول لازم دي، تاسو ته هغه څوك ضرر نشي دررسولى چې ګمراه شوي دي، كله چې تاسو (پخپله) پر نېغه لار یئ، خاص الله ته ستاسو د ټولو واپس ورتلل دي، نو هغه به تاسو پر هغو عملونو خبر كړي چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(5,106,'5:106',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې په تاسو كې یو تن ته مرګ راشي، (نو) د وصیت په وخت كې ستاسو په مینځ كې له خپلو (مومنانو) څخه د دوه عادلو كسانو ګواه كول (لازم) دي، یا ستاسو له غیرو نه دوه نور كسان (ګواهان پكار دي) كه چېرې تاسو په ځمكه كې سفر كوئ، بیا تاسو ته د مرګ مصیبت ورسېږي، تاسو به دغه دواړه له لمانځه نه پس ایساروئ، نو هغه دواړه به پر الله قسم وخوري، كه تاسو شك وكړئ، (او هغه دواړه به وايي:) مونږ په دې (قسم) سره څه قیمت نه اخلو، اګر كه هغه خپلوان وي او مونږ د الله ګواهي نه پټوو، (كه مونږ قیمت واخيسته، یا مو ګواهي پټه كړه) بېشكه مونږ به په دغه وخت كې خامخا له ګناه كوونكو څخه یو');
INSERT INTO "abulsalam" VALUES(5,107,'5:107',' نو كه اطلاع حاصله شوه چې بېشكه هغه دواړه (د صحيح ګواهۍ د پټولو په وجه) د ګناه مستحق شوي دي، نو دوه نور (ګواهان) به د هغو پر ځاى ودرېږي چې پر هغوى (د ناسمې ګواهۍ وركولو په وجه) د حق دعویٰ وشوه، چې دغه دواړه (مړي ته) ډېر قریب وي، نو دوى دواړه به په الله سره قسم خوري، چې یقینًا د دوى ګواهي د هغو دواړو له ګواهۍ نه ښه حقه ده او مونږ زیاتى نه دى كړى، (كه مونږ وكړو) بېشكه مونږه به په دغه وخت كې خامخا له ظالمانو څخه یو');
INSERT INTO "abulsalam" VALUES(5,108,'5:108',' دا (طريقه) ډېره نژدې ده دې ته چې دوى به ګواهي په صحیح طریقې سره وكړي، یا به دوى ووېرېږي چې (د دوى) قسمونه به د هغو (نورو) له قسمونو نه پس رد كړى شي او تاسو له الله نه وېرېږئ او اَورئ، او الله فاسقو خلقو ته نېغه لار نه ښیي');
INSERT INTO "abulsalam" VALUES(5,109,'5:109',' هغه ورځ (یاده كړه) چې الله به رسولان راجمع كړي، نو (ورته) وايي به: تاسو ته څه جواب دركړى شوى و؟ هغوى به ووايي: مونږ ته هېڅ علم نشته، بېشكه هم ته پر غیبو ښه عالم يې');
INSERT INTO "abulsalam" VALUES(5,110,'5:110',' كله چې الله به ووايي: اى عیسٰی د مریمې زویه! زما هغه نعمتونه یاد كړه چې په تا او ستا په مور باندې شوي دي، كله چې ته ما په پاك روح (جبریل) سره قوي كړې، تا به له خلقو سره په غېږ كې او په پاخه عمر كې خبرې كولې او كله چې ما تا ته كتاب او حكمت او تورات او انجیل وښودل، او كله چې تا به زما په حكم سره له خَټې نه د مرغۍ د شكل په شان شكل جوړاوه، بیا به تا په هغه كې پوكى وواهه، نو هغه به زما په حكم سره مرغۍ شوه او تا به زما په حكم سره مورزادي ړوند او برګي مرض والا روغاوه او كله چې تا زما په حكم سره مړي (له قبرونو نه ژوندي) راوښكل او كله چې ما له تا نه بني اسرائیل بند (ايسار) كړل، كله چې تا هغوى ته ښكاره دلیلونه راوړل، نو په هغوى كې كافر شويو خلقو وویل: نه دى دا مګر ښكاره جادو');
INSERT INTO "abulsalam" VALUES(5,111,'5:111',' او كله چې مونږه حواریانو ته الهام وكړ چې پر ما او زما پر رسول ایمان راوړئ، هغوى وویل: مونږ ایمان راوړى دى او ته پر دې ګواه شه چې یقینًا مونږ مسلمانان یو');
INSERT INTO "abulsalam" VALUES(5,112,'5:112',' كله چې حواریانو وویل: اى عیسٰی د مریمې زویه! ایا ستا رب د دې طاقت لري چې پر مونږ باندې له اسمانه دسترخوان نازل كړي؟ عیسٰی وویل: تاسو له الله نه ووېرېږئ كه چېرې تاسو مومنان یئ');
INSERT INTO "abulsalam" VALUES(5,113,'5:113',' هغوى وویل: مونږ غواړو چې له هغه نه خوراك وكړو او زمونږ زړونه مطمئن شي او پوه شو چې تا مونږ ته رښتیا ویلي دي او مونږ به په هغه باندې له ګواهانو څخه شو');
INSERT INTO "abulsalam" VALUES(5,114,'5:114',' د مریمې زوى عیسٰی وویل: اى الله زمونږه ربه! ته پر مونږه له اسمانه دسترخوان نازل كړه چې زمونږ د ړومبنو او زمونږ د وروستنو لپاره اختر شي او ستا له جانبه یوه نښه او ته مونږ ته روزي راكړه او ته تر ټولو غوره رزق وركوونكى يې');
INSERT INTO "abulsalam" VALUES(5,115,'5:115',' الله وفرمایل: بېشكه زه پر تاسو باندې هغه نازلوونكى یم، بيا چې تاسو كې چا له دې نه بعد كفر وكړ، نو بېشكه زه به هغه ته عذاب وركوم، داسې عذاب، چې په مخلوقاتو كې هېڅ یو كس ته به هم هغه شان عذاب نه وركوم');
INSERT INTO "abulsalam" VALUES(5,116,'5:116',' او كله چې الله وفرمایل: اى عیسٰی د مریمې زویه! ایا تا خلقو ته ویلي وو چې له الله نه غیر ما او زما مور دوه معبودان ونیسئ؟ عیسٰی وویل زه ستا پاكي بیانوم، زما لپاره جايز نه دي چې زه هغه خبره وكړم چې د هغې حق ما ته نشته، كه چېرې ما دا خبره كړې وَى، نو یقینًا تا ته به معلومه وَى، ته عالم يې پر هر هغه څه چې زما په نفس كې دي او زه نه پوهېږم پر هغه څه چې ستا په نفس كې دي، بېشكه هم ته پر غیبو ښه عالم يې');
INSERT INTO "abulsalam" VALUES(5,117,'5:117',' ما دوى ته يواځې هغه خبره كړې ده چې تا ما ته د هغې حكم راكړى و، دا چې تاسو د الله عبادت كوئ، چې زما رب دى او ستاسو رب دى، او زه په دوى باندې ګواه وم تر څو پورې چې زه په دوى كې موجود وم، نو كله چې تا زه واخيستلم (، نو) هم ته له همېشه پر دوى نګهبان وې او ته په هر شي باندې ګواه يې');
INSERT INTO "abulsalam" VALUES(5,118,'5:118',' كه چېرې ته دوى ته عذاب وركوې، نو یقینًا دوى ستا بنده ګان دي او كه چېرې ته دوى ته بخښنه كوې، نو بېشكه هم ته ډېر غالب، ښه حكمت والا يې');
INSERT INTO "abulsalam" VALUES(5,119,'5:119',' الله وفرمایل: دا ورځ ده چې رښتينو ته به د هغوى رښتیا ویل فايده وركوي، د دوى لپاره داسې جنتونه دي چې د هغو له لاندې ولې بهېږي، دوى به په هغو كې تل ترتله وي، الله له دوى نه راضي دى او دوى له هغه نه راضي دي، همدا ډېره لویه كامیابي ده');
INSERT INTO "abulsalam" VALUES(5,120,'5:120',' خاص د الله لپاره ده بادشاهي د اسمانونو او د ځمكې او د هغه څه چې په دغو كې دي او هغه په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(6,1,'6:1',' ټول د كمال صفتونه خاص هغه الله لره دي چې اسمانونه او ځمكه يې پیدا كړي دي، او پیدا كړې يې دي تیارې او رڼا، بیا (هم) هغه خلق چې كافر دي، له خپل رب سره (نور څیزونه) برابروي');
INSERT INTO "abulsalam" VALUES(6,2,'6:2',' دى همغه ذات دى چې تاسو يې له خَټې نه پیدا كړئ، بیا يې یو وخت مقرر كړ او مقرر وخت له هغه سره (لیك) دى، بیا (هم) تاسو شك كوئ');
INSERT INTO "abulsalam" VALUES(6,3,'6:3',' او په اسمانونو كې او په ځمكه كې همغه الله دى، هغه ستاسو پر پټو (حالاتو) او ستاسو پر ښكاره (حالاتو) پوهېږي او پوهېږي پر هغه څه چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(6,4,'6:4',' او دوى ته د خپل رب له ایتونو څخه هېڅ یو ایت نه راځي مګر دا چې دوى له هغه نه مخ اړوونكي وي');
INSERT INTO "abulsalam" VALUES(6,5,'6:5',' پس یقینًا دوى د حق تكذیب وكړ، كله چې دوى ته راغى، نو ژر به دوى ته د هغه څیز خبرونه راشي چې دوى به په هغه پورې استهزا كوله');
INSERT INTO "abulsalam" VALUES(6,6,'6:6',' ایا دوى نه دي كتلي(نه دي خبر شوي) چې مونږ له دوى نه مخكې څومره (ډېر) امتونه هلاك كړي دي، چې مونږ هغوى ته په ځمكه كې ښه قوت وركړى و، دومره (قوت) چې تاسو ته مو (هومره) نه دى دركړى او مونږ پر هغوى باندې پرله پسې ورېدونكى باران ورولى و او داسې وَلې مو جاري كړې وې چې له هغوى نه ښكته بهېدلې، نو مونږ هغوى د خپلو ګناهونو په سبب هلاك كړل او له هغوى نه بعد مونږه نور خلق پیدا كړل');
INSERT INTO "abulsalam" VALUES(6,7,'6:7',' او كه چېرې مونږه په تا باندې په كاغذ كې (لیكلى) كتاب نازل كړى وى، بيا دوى پر هغه، خپل لاسونه هم لګولي وى، (نو) كافر شويو كسانو به خامخا ویل: نه دى دا مګر ښكاره جادو');
INSERT INTO "abulsalam" VALUES(6,8,'6:8',' او دوى وویل: په ده باندې مَلَك ولې نه دى نازل كړى شوى؟ او كه مونږه مَلَك نازل كړى وى (نو) معامله به ضرور فیصله كړى شوې وه، بیا به دوى ته مهلت نه وركول كېدى');
INSERT INTO "abulsalam" VALUES(6,9,'6:9',' او كه چېرې مونږ دا (رسول) مَلَك ګرځولى وَى (، نو) مونږ به هغه خامخا سړى جوړ كړى و او مونږه به دوى خامخا په هغه شبه كې اچولى، په كومه شبه كې چې(اوس) پراته دي');
INSERT INTO "abulsalam" VALUES(6,10,'6:10',' او یقینًا یقینًا له تا نه په مخكې رسولانو پورې استهزا ګانې شوي دي، نو هغو كسانو لره چې په دوى پورې يې مسخرې كړې وې؛ هغه شي راګېر كړل چې دوى به په هغه پورې استهزا كوله');
INSERT INTO "abulsalam" VALUES(6,11,'6:11',' ته ووایه: تاسو په ځمكه كې وګرځئ، بیا تاسو وګورئ چې د دروغ ګڼونكو انجام څنګه و؟');
INSERT INTO "abulsalam" VALUES(6,12,'6:12',' ته ووایه: هغه څه د چا دي چې په اسمانونو او ځمكه كې دي؟ ته ووایه: د الله دي، هغه په خپل ځان باندې رحمت لازم كړى دى، هغه به تاسو خامخا لازمًا د قیامت په ورځ راجمع كوي، چې په هغې كې هېڅ شك نشته، هغه كسان چې خپلو ځانونو ته يې تاوان رسولى دى، نو هغوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(6,13,'6:13',' او خاص د هغه لپاره دي هغه څه چې ارام كوي په شپه كې او په ورځ كې او هم هغه ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(6,14,'6:14',' ته ووایه: ایا زه (بل) ولي (مددګار) ونیسم، له الله نه غیر چې د اسمانونو او ځمكې پیدا كوونكى دى او هغه خوراك وركوي او هغه ته خوراك نشي وركولى؟ ته ووایه: بېشكه ما ته خو حكم شوى دى چې زه په اسلام راوړونكو كې اول شم او (ما ته ویل شوي دي چې) ته هېڅكله هم له مشركانو څخه مه كېږه');
INSERT INTO "abulsalam" VALUES(6,15,'6:15',' ته ووایه: بېشكه زه د لويې ورځې له عذاب نه وېرېږم، كه د خپل رب نافرماني وكړم');
INSERT INTO "abulsalam" VALUES(6,16,'6:16',' هغه څوك چې له هغه نه په دغې ورځ كې دغه (عذاب) واړول شو، نو یقینًا پر هغه يې رحم وكړ او همدا څرګنده كامیابي ده');
INSERT INTO "abulsalam" VALUES(6,17,'6:17',' او كه الله تا ته تكلیف درورسوي، نو د هغه (تكلیف) لرې كوونكى له هغه نه سِوا بل هیڅوك نشته او كه هغه تا ته خیر درورسوي، نو هغه په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(6,18,'6:18',' او همدى غالب دى، د خپلو بنده ګانو له پاسه دى او همدى ښه حكمت والا، ښه خبردار دى ');
INSERT INTO "abulsalam" VALUES(6,19,'6:19',' ته ووایه: د ګواهۍ په لحاظ كوم شى تر ټولو لوى دى؟ ته ووایه: الله دى، (هغه) زما په مینځ كې او ستاسو په مینځ كې ګواه دى او ما ته دا قرآن وحي شوى دى، د دې لپاره چې په دې (قرآن) سره زه تاسو ووېروم او هغه څوك چې دا ورته رسي، ایا تاسو په رښتيا (د دې خبرې) ګواهي كوئ چې یقینًا له الله سره نور معبودان (هم) شته؟ ته ووایه: زه (د دې) شاهدي نه كوم، ته ووایه: بېشكه همدا خبره ده چې همغه یو حق معبود دى او بېشكه زه له هغه څه نه بېزاره یم چې تاسو يې شریكوئ');
INSERT INTO "abulsalam" VALUES(6,20,'6:20',' هغه كسان چې هغوى ته مونږ كتاب وركړى دى، هغوى دى (داسې) پېژني لكه څنګه چې خپل زامن پېژني، هغه كسان چې خپلو ځانونو ته يې تاوان رسولى دى، نو هغوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(6,21,'6:21',' او له هغه كس نه (بل) زیات ظالم څوك دى چې په الله باندې دروغ تپي (اړوي)، یا د هغه ایتونه دروغ ګڼي، بېشكه حقيقت دا دى چې ظالمان نشي كاميابېدلى ');
INSERT INTO "abulsalam" VALUES(6,22,'6:22',' او په هغې ورځ كې چې مونږ به دوى ټول راجمع كوو، بیا به مونږه هغو كسانو ته چې شرك يې كړى دى ؛ وایو: ستاسو هغه شریكان چېرته دي چې تاسو به (له الله سره شریكان) ګڼل؟');
INSERT INTO "abulsalam" VALUES(6,23,'6:23',' بیا به نه وي (بله) بهانه د دوى مګر دا چې دوى به ووايي: قسم پر الله چې زمونږ رب دى! مونږ مشركان نه وو');
INSERT INTO "abulsalam" VALUES(6,24,'6:24',' ته وګوره څنګه دوى په خپلو ځانونو باندې دروغ وویل او له دوى نه هغه څه ورك شول چې دوى به په دروغه جوړول');
INSERT INTO "abulsalam" VALUES(6,25,'6:25',' او په دوى كې ځینې هغه څوك دي چې تا ته غوږ نیسي او مونږ د هغوى په زړونو باندې پردې اچولې دي چې پر دې (قرآن) پوه (نه) شي او د دوى په غوږونو كې مو كوڼ والى (اچولى دى) او كه دوى هره نښه وویني، (بيا هم) دوى په هغې ایمان نه راوړي، تر دې چې كله دوى تا ته راشي، چې له تا سره جګړه كوي (، نو) هغه كسان چې كافر شوي دي؛ وايي: نه دى دا (قرآن) مګر د مخكېنو خلقو باطلې قصې');
INSERT INTO "abulsalam" VALUES(6,26,'6:26',' او دوى له ده نه منع كول كوي او )پخپله هم) ترې لرې كېږي او دوى يواځې خپل ځانونه هلاكوي او دوى شعور نه لري');
INSERT INTO "abulsalam" VALUES(6,27,'6:27',' او كه ته ووینې كله چې دوى پر (غاړه د) اور ودرول شي، نو دوى به وايي: اى كاشكې! مونږ بیا (دنیا ته) واپس كړى شو، له دې (عقيدې او عمل) سره چې د خپل رب د ایتونو (بیا) تكذیب ونه كړو او مونږ شو له مومنانو ځنې');
INSERT INTO "abulsalam" VALUES(6,28,'6:28',' بلكې دوى ته هغه څه ظاهر شول چې دوى له دې نه مخكې پټول او كه چېرې دوى (دنیا ته) واپس كړى شي (، نو) دوى به هغه شي ته خامخا بيا وروګرځي، له څه نه چې دوى منع كړى شوي وو، او بېشكه دوى یقینًا دروغجن دي');
INSERT INTO "abulsalam" VALUES(6,29,'6:29',' او دوى وايي: نه دى دا (ژوند) مګر همدغه زمونږ دنيايي ژوند دى او مونږ به كله هم بیا راژوندي نه كړى شو');
INSERT INTO "abulsalam" VALUES(6,30,'6:30',' او كه چېرې ته ووینې كله چې دوى د خپل رب په وړاندې ودرول شي (، نو) هغه به وفرمايي: ایا دا (ژوند) رښتیا نه دى؟ دوى به ووايي: ولې نه! زمونږ دې پر خپل رب قسم وي، رب به وفرمايي: نو تاسو عذاب وڅكئ د هغه كفر په سبب چې تاسو به كاوه');
INSERT INTO "abulsalam" VALUES(6,31,'6:31',' هغه كسان یقینًا تاوانیان شوي دي چې له الله سره ملاقات يې دروغ ګڼلى دى، تر دې چې كله دوى ته قیامت ناڅاپه راشي، دوى به وايي: هۍ افسوس! په هغې كوتاهۍ باندې چې مونږ د دې (قیامت) په باره كې كړې ده، او دوى به خپل پېټي په خپلو شاګانو پورته كوي، اګاه شئ! بد دى هغه شى چې دوى يې (بوج) پورته كوي ');
INSERT INTO "abulsalam" VALUES(6,32,'6:32',' او دنيايي ژوند نه دى مګر لوبه او تماشه، او یقینًا د اخرت كور د هغو كسانو لپار ډېر غوره دى چې پرهېزګاره دي، نو ولې تاسو عقل نه كاروئ!');
INSERT INTO "abulsalam" VALUES(6,33,'6:33',' بېشكه مونږ پوهېږو چې یقینًا حقیقت دا دى چې تا هغه خبره خامخا غمجنوي، چې دوى يې وايي، نو یقینًا دوى تا دورغژن نه ګڼي او لېكن دغه ظالمان د الله له ایتونو نه انكار كوي');
INSERT INTO "abulsalam" VALUES(6,34,'6:34',' او یقینًا یقینًا له تا نه مخكې رسولان دروغژن بلل شوي وو، نو هغوى په دروغژن بلل كېدلو او ضرر رسېدلو صبر وكړ تر دې چې هغوى ته زمونږ مدد راغى، او د الله كلماتو لره هیڅوك اړوونكى نشته، او یقینًا تا ته د رسولانو څه خبرونه خو راغلي دي');
INSERT INTO "abulsalam" VALUES(6,35,'6:35',' او كه چېرې په تا باندې د دوى مخ اړول ګران وي، نو كه ته طاقت لرې د دې چې په ځمكه كې سُرنګ ولټوې، یا اسمان ته كومه زینه، بیا ته دوى ته څه نښه (مُعجِزه) راوړې، (نو همدغسې وكړه) او كه الله غوښتلى دوى به يې په نېغه لار باندې لازمًا جمع كړي وو، نو ته له ناپوهانو څخه مه كېږه');
INSERT INTO "abulsalam" VALUES(6,36,'6:36',' یقینًا همدا خبره ده چې (حق) هغه كسان قبلوي چې خبره اوري، او مړي به الله راژوندي كوي، بیا به خاص د هغه په طرف ورګرځولى شي');
INSERT INTO "abulsalam" VALUES(6,37,'6:37',' او دوى وايي: ولې په ده باندې د خپل رب له جانبه كومه معجزه نه نازلولى شي؟ ته (دوى ته) ووایه: بېشكه الله پر دې قادر دى چې معجزه نازله كړي او لېكن د دوى اكثره خلق نه پوهېږي');
INSERT INTO "abulsalam" VALUES(6,38,'6:38',' او نشته په ځمكه كې هېڅ یو خوځنده او نه هېڅ مارغه چې په خپلو دواړو وزرونو الوزي مګر (دوى) ستاسو په شان امتونه دي، مونږ په كتاب )لوحِ محفوظ) كې هېڅ شى (بې ليكلو) نه دى پریښى، بیا به دوى د خپل رب په طرف ورجمع كولى شي');
INSERT INTO "abulsalam" VALUES(6,39,'6:39',' او هغه كسان چې زمونږ د ایتونو تكذیب يې كړى دى (دوى) كاڼه او ګونګیان دي، په تیارو كې دي، چا ته چې الله وغواړي هغه ګمراه كوي او چا ته چې وغواړي هغه په نېغه لار روانوي');
INSERT INTO "abulsalam" VALUES(6,40,'6:40',' ته (دوى ته) ووایه: ما ته خبر راكړئ: كه چېرې پر تاسو د الله عذاب راشي، یا پر تاسو قیامت راشي، ایا تاسو به له الله نه غیر نور څوك بلئ، كه چېرې تاسو رښتیني یئ؟');
INSERT INTO "abulsalam" VALUES(6,41,'6:41',' (نه) بلكې تاسو به (په دغه وخت كې) خاص هغه بلئ، نو كه هغه بيا وغواړي هغه (تكلیف) به لرې كړي د څه لپاره چې تاسو هغه بلئ او تاسو به هغه هېر كړئ چې تاسو يې (له الله سره) شريكوئ');
INSERT INTO "abulsalam" VALUES(6,42,'6:42',' او یقینًا یقینًا مونږ پېغام لېږلى و له تا نه مخكې امتونو ته، بیا مونږ هغوى په سختیو او تكلیفونو کې واچول، د دې لپاره چې هغوى عاجزي وكړي');
INSERT INTO "abulsalam" VALUES(6,43,'6:43',' نو كله چې دوى ته زمونږ عذاب راغى، دوى ولې عاجزي ونه كړه او لېكن د دوى زړونه سخت شوي وو او شیطان دوى ته د دوى هغه عملونه ښايسته كړي وو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(6,44,'6:44',' نو كله چې دوى هغه شى هېر كړ په څه سره چې دوى ته نصیحت شوى و، بیا نو مونږ په دوى باندې د هر شي دروازې پرانستلې تر دې چې دوى لويي وكړه، په سبب د هغه څه چې دوى ته وركړل شوي وو (،نو) مونږه دوى ناڅاپه ونیول، بيا نو دوى ناګهانه ناامېده وو ');
INSERT INTO "abulsalam" VALUES(6,45,'6:45',' نو د هغه قوم بېخ پرې كړى شو چې ظلم يې كړى و او ټولې ستاينې خاص الله لره دي، چې د عالَمونو ښه پالونكى دى');
INSERT INTO "abulsalam" VALUES(6,46,'6:46',' ته (دوى ته) ووایه: تاسو ما خبر كړئ كه چېرې الله ستاسو غوږونه او ستاسو سترګې واخلي او ستاسو په زړونو باندې مهر ولګوي (، نو) له الله نه غير (بل څوك حق) معبود دى، چې دا تاسو ته واپس راوړي؟ ته وګوره، مونږه ایتونه په څنګه قسما قسم (انداز كې) بیانوو، بیا (هم) دوى مخ اړوي');
INSERT INTO "abulsalam" VALUES(6,47,'6:47',' ته ووایه: تاسو ما خبر كړئ كه چېرې پر تاسو باندې د الله عذاب ناڅاپه راشي، یا ښه ښكاره، (، نو) نه به شي هلاكولى مګر ظالمان خلق');
INSERT INTO "abulsalam" VALUES(6,48,'6:48',' او مونږ رسولان نه لېږو مګر زېرى وركوونكي او وېروونكي، نو چا چې ایمان راوړ او )د ځان) اصلاح يې وكړه، نو په دوى باندې به نه څه وېره وي او نه به دوى غمجن كېږي');
INSERT INTO "abulsalam" VALUES(6,49,'6:49',' او هغه كسان چې زمونږ د ایتونو تكذیب يې وكړ، دوى ته به د هغې نافرمانۍ په سبب عذاب رسېږي چې دوى كوله');
INSERT INTO "abulsalam" VALUES(6,50,'6:50',' ته ووایه: زه تاسو ته نه وایم چې ما سره د الله خزانې دي او نه زه عالم الغیب یم او نه زه تاسو ته دا وایم چې بېشكه زه مَلَك یم، زه پَيروي نه كوم مګر د هغه شي چې ما ته وحي كولى شي، ته ووایه: ایا ړوند او بينا سره برابر دي؟ نو ولې تاسو سوچ او فكر نه كوئ!');
INSERT INTO "abulsalam" VALUES(6,51,'6:51',' او ته په دې (وحي) سره هغه كسان ووېروه چې د خپل رب په وړاندې له ورټولول كېدو نه وېرېږي، په دې حال كې چې د دوى لپاره به له هغه نه غير نه څوك دوست وي او نه څوك سفارشي، لپاره د دې چې دوى پرهېزګاره شي');
INSERT INTO "abulsalam" VALUES(6,52,'6:52',' او ته هغه كسان (له ځانه) مه شړه چې خپل رب سبا او بېګاه بلي، چې د الله مخ (او رضا) غواړي، په تا باندې د هغوى له حساب نه هېڅ شى نشته او نه پر هغوى باندې ستا له حسابه څه شى شته، نو كه ته دوى وشړې، نو شې به ته له ظالمانو ځنې');
INSERT INTO "abulsalam" VALUES(6,53,'6:53',' او همدارنګه مونږ د دوى ځینې په ځینو (نورو) سره ازمایلي دي، د دې لپاره چې دوى ووايي: ایا دا خلق دي چې الله په دوى باندې زمونږ له مینځه احسان كړى دى، ایا الله په شكر ګذارو باندې ښه پوه نه دى!');
INSERT INTO "abulsalam" VALUES(6,54,'6:54',' او كله چې تا ته هغه كسان راشي چې زمونږ پر ایتونو ایمان لري، نو ته ووایه: پر تاسو دې سلام وي، ستاسو رب په خپل ځان باندې مهرباني كول لیكلي (لازم كړي) دي، چې یقینًا شان دا دى څوك چې په تاسو كې په ناپوهۍ سره بدي وكړي، بیا له هغې نه پس توبه وباسي او اصلاح وكړي، نو بېشكه هغه ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(6,55,'6:55',' او همدارنګه مونږ ایتونه په تفصیل سره بیانوو، او د دې لپاره چې د مجرمانو لاره ښه واضحه شي');
INSERT INTO "abulsalam" VALUES(6,56,'6:56',' ته (دوى ته) ووایه: بېشكه زه منع كړى شوى یم له دې نه چې زه د هغو (څیزونو) عبادت وكړم چې تاسو يې له الله نه غير بلئ، ته ووایه: زه ستاسو د خواهشاتو پیروي نه كوم، (كه ومې كړه، نو) یقینًا زه به په دغه وخت كې ګمراه شوى یم او نه به یم زه له نېغه لار موندونكو نه ');
INSERT INTO "abulsalam" VALUES(6,57,'6:57',' ته ووایه: بېشكه زه په ښكاره دلیل باندې ولاړ یم چې زما د رب له جانبه دى او تاسو د هغهٔ تكذیب كړى دى، ما سره هغه شى نشته چې تاسو يې په تلوار سره غواړئ، حكم (د فيصلې اختيار) يواځې له الله سره دى، هغه حق بیانوي او هغه ډېر غوره فیصله كوونكى دى');
INSERT INTO "abulsalam" VALUES(6,58,'6:58',' ته ووایه: یقینًا كه ما سره هغه شى وَى چې تاسو يې په تلوار سره غواړئ (، نو) زما او ستاسو په مینځ كې به خبره خامخا فیصله كړى شوې وه او الله په ظالمانو باندې ښه عالم دى');
INSERT INTO "abulsalam" VALUES(6,59,'6:59',' او خاص له الله سره د غیبو چابیانې دي، يواځې هغه پر دغو عالم دى او هغه عالم دى پر هغه څه چې په وچه او سمندر كې دي، او هېڅ یوه پاڼه نه غورځېږي مګر هغه پرې عالم دى او د ځمكې په تیارو كې نه یوه دانه (پرېوځي) او نه لوند څیز او نه وچ مګر (دا ټول) په څرګند كتاب (لوح محفوظ) كې دي');
INSERT INTO "abulsalam" VALUES(6,60,'6:60',' او دى همغه ذات دى چې تاسو په شپه كې وفات كوي (ويده كوي) او هغه پوهېږي پر هغه څه چې تاسو يې د ورځې كوئ، بیا به هغه تاسو په هغې (ورځې) كې راژوندي كوي، د دې لپاره چې ټاكلى وخت پوره كړى شي، بیا به خاص د هغه په طرف ستاسو واپسي وي، بیا به هغه تاسو پر هغو عملونو خبر كړي چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(6,61,'6:61',' او همغه غالب دى، د خپلو بنده ګانو له پاسه دى، او پر تاسو باندې ساتونكي (ملايك) رالېږي، تر دې چې كله په تاسو كې یو كس ته مرګ راشي (، نو) زمونږ استازي يې وفات كړي او هغوى هېڅ تقصیر (كمى) نه كوي');
INSERT INTO "abulsalam" VALUES(6,62,'6:62',' بیا به دوى ورګرځولى شي الله ته چې د دوى حق مالك دى، اګاه شئ! د حكم (او فيصلې كولو) ټول اختيار خاص د هغه دى، او هغه تر ټولو ژر حساب كوونكى دى');
INSERT INTO "abulsalam" VALUES(6,63,'6:63',' ته (دوى ته) ووایه: تاسو ته د وچې او سمندر له تیارو نه څوك خلاصى دركوي، چې تاسو هغه بلئ په زارۍ سره او په پټه سره، (او وایئ) كه چېرې الله مونږ ته له دې (مصیبت) نه خلاصى راكړ (، نو) مونږ به خامخا ارومرو له شكر كوونكو څخه شو');
INSERT INTO "abulsalam" VALUES(6,64,'6:64',' ته (ورته) ووایه: الله تاسو ته له دغه (مصيبت) نه نجات دركوي او له هر غم نه هم، بیا (هم) تاسو شرك كوئ');
INSERT INTO "abulsalam" VALUES(6,65,'6:65',' ته (دوى ته) ووایه: همغه قادر دى په دې باندې چې پر تاسو باندې ستاسو له پاسه نه عذاب راولېږي، یا ستاسو د پښو له لاندې طرف نه، یا تاسو د بېلو، بېلو ډلو په جوړولو سره ګډوَډ كړي او په ځینو باندې د ځینو نورو (د) جنګ (مزه) وڅكي، ته وګوره مونږ ایتونه په څنګه انداز كې بیانوو، د دې لپاره چې دوى پوه شي');
INSERT INTO "abulsalam" VALUES(6,66,'6:66',' او ستا قوم د دې (قرآن) تكذیب وكړ، حال دا چې همدا حق دى، ته (ورته) ووایه: زه پر تاسو باندې نګهبان نه یم');
INSERT INTO "abulsalam" VALUES(6,67,'6:67',' د هر یو خبر لپاره مقرر وخت دى او ژر به تاسو پوه شئ ');
INSERT INTO "abulsalam" VALUES(6,68,'6:68',' او كله چې ته هغه كسان ووینې چې زمونږ په ایتونو كې عبث بحثونه كوي، نو ته له هغوى نه مخ واړوه، تر دې چې دوى له دې نه په علاوه خبرو كې مشغول شي، او كه چېرې شیطان له تا نه (دا حكم) هېر كړي، نو له یادېدلو نه پس له دغو ظالمانو سره مه كېنه');
INSERT INTO "abulsalam" VALUES(6,69,'6:69',' او په پرهېزګاره كسانو باندې د دغو (عبث بحث كوونكو) خلكو له حسابه څخه د هېڅ شي ذمه واري نشته او لېكن (د هغوى پر ذمه بس) نصیحت كول دي، د دې لپاره چې دوى (هم)پرهېزګار شي');
INSERT INTO "abulsalam" VALUES(6,70,'6:70',' او ته هغه كسان پرېږده چې خپل دین يې لوبه او تماشه ګرځولى دى او دنيايي ژوند دوكه كړي (غولولي) دي او ته په دې (قرآن) سره نصیحت كوه، چې كوم نفس د خپلو كړو عملونو په سبب هلاك (نه) كړى شي، په داسې حال كې چې د هغه (نفس) لپاره له الله نه پرته نه څوك دوست وي او نه سفارشي او كه دا (نفس) د ځان د خلاصون لپاره فدیه وركړي، هر قسمه فدیه (،نو) دا به له هغه نه قبوله نه كړى شي، دا هغه كسان دي چې د خپلو كړو عملونو په سبب هلاك كړى شوي دي، د دوى لپاره ډېرې ګرمې اوبه څښاك دى او ډېر دردوونكى عذاب دى، په سبب د هغه كفر چې دوى به كاوه');
INSERT INTO "abulsalam" VALUES(6,71,'6:71',' ته (ورته) ووایه: ایا مونږه له الله نه سوا د هغه شي عبادت وكړو چې مونږ ته هغه نه فايده رارسولى شي او نه مونږ ته ضرر رارسولى شي؟ او (ایا) مونږه بېرته په شا وګرځول شو وروسته له دې چې الله مونږ ته نېغه لار وښودله، د هغه كس په شان چې شیطانانو په ځمكه كې ژورې كندې ته بېولى وي، چې حیران وي، د هغه ملګري وي چې دى نېغې لارې ته رابلي، (چې) مونږ ته راشه، (اى نبي!) ته ووایه: بېشكه د الله هدایت، همغه اصل هدایت دى او مونږ ته حكم شوى دى چې د رب العلمین اطاعت كوونكي شو');
INSERT INTO "abulsalam" VALUES(6,72,'6:72',' او دا چې تاسو لمونځ قايموئ او له الله نه وېرېږئ او دى همغه ذات دى چې خاص هغه ته به تاسو ورټولولى شئ ');
INSERT INTO "abulsalam" VALUES(6,73,'6:73',' او دى همغه ذات دى چې اسمانونه او ځمكه يې په حقه سره پیدا كړي دي او (یاده كړئ) هغه ورځ چې وبه وايي: موجود شه! نو هغه به موجود شي، هم د هغه وینا حقه ده، او خاص د هغه لپاره حكومت دى په هغې ورځ كې چې په شپېلۍ كې به پوكى وكړى شي، (هغه) د پټو او ښكاره څیزونو عالم دى او همغه ښه حكمت والا، ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(6,74,'6:74',' او كله چې ابراهیم خپل پلار ازر ته وویل: ایا ته بتان، معبودان نیسي، يقينًا زه تا او ستا قوم په څرګنده ګمراهۍ كې وینم ');
INSERT INTO "abulsalam" VALUES(6,75,'6:75',' او همدارنګه مونږ ابراهیم ته د اسمانونو او ځمكې بادشاهي ښودله او د دې لپاره چې هغه له یقین كوونكو ځنې شي');
INSERT INTO "abulsalam" VALUES(6,76,'6:76',' بیا كله چې په هغه باندې شپه تیاره شوه (، نو) یو ستورى يې ولیده، ويې ويل:دا زما رب دى؟ نو كله چې هغه پټ شو، ويې ويل: زه پټېدونكي (غايبېدونكي) نه خوښوم');
INSERT INTO "abulsalam" VALUES(6,77,'6:77',' بیا كله چې يې سپوږمۍ ولیده، ځلېدونكې، ويې ويل:دا زما رب دى؟ نو كله چې هغه پرېوتله (،نو) ويې ويل:یقینًا كه چېرې ما ته زما رب نېغه لار ونه ښیي (، نو) زه به خامخا له ګمراه خلقو ځنې شم');
INSERT INTO "abulsalam" VALUES(6,78,'6:78',' نو كله چې هغهٔ، لمرسترګه ولیدله، ځلېدونكې، ويې ويل:دا زما رب دى؟ دا خو تر ټولو لوى دى، نو كله چې هغه پرېوتله، ويې ويل:اى زما قومه! بېشكه زه بېزاره یم له هغه څه نه چې تاسو يې شریكان جوړوئ');
INSERT INTO "abulsalam" VALUES(6,79,'6:79',' بېشكه ما خپل مخ هغه ذات ته اړولى دى چې اسمانونه او ځمكه يې پیدا كړي دي، يواځې په حق پسې تلونكى یم او نه یم له مشركانو څخه');
INSERT INTO "abulsalam" VALUES(6,80,'6:80',' او له هغه سره خپل قوم جګړه وكړه، ويې ويل:ایا تاسو زما سره د الله په باره كې جګړه كوئ، حال دا چې هغهٔ ما ته نېغه لار راښودلې ده، او زه له هغو شیانو نه نه وېرېږم چې تاسو يې له الله سره شريكوئ، مګر دا چې زما رب څه شى وغواړي، زما رب د علم په لحاظ سره هر شى راګېر كړى دى، نو ولې تاسو نصیحت نه منئ!');
INSERT INTO "abulsalam" VALUES(6,81,'6:81',' او زه له هغو څیزونو نه څنګه ووېرېږم چې تاسو له الله سره شريك كړي دي، حال دا چې تاسو له دې څخه نه وېرېږئ چې واقعًا تاسو له الله سره هغه څه شریك كړي دي چې د هغو په باره كې يې پر تاسو باندې هېڅ دلیل نه دى نازل كړى، نو په دواړو ډلو كې كومه یوه د امن (له عذاب نه د بچ كېدو) زیاته حقداره ده؟ كه چېرې تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(6,82,'6:82',' هغه كسان چې ایمان يې راوړى دى او له خپل ایمان سره يې ظلم )شرك) نه دى ګډ كړى (، نو) دغه كسان دي چې د همدوى لپاره (له عذاب نه) امن (بچاو) دى او دغه كسان هدایت موندونكي دي');
INSERT INTO "abulsalam" VALUES(6,83,'6:83',' او دا زمونږ دلیل دى چې مونږه ابراهیم ته وركړى و د هغه د قوم په مقابله كې، مونږ درجې اوچتوو چا لره چې مونږ وغواړو، بېشكه ستا رب، ښه حكمت والا، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(6,84,'6:84',' او مونږ هغه ته اسحٰق او یعقوب عطا كړ، مونږ هر یو ته هدایت كړى و، او له ده نه مخكې نوح ته مونږ هدایت كړى و او د هغه په اولاد كې داود او سلیمان، او ایوب او یوسف او موسٰی او هارون ته (هم) او همدارنګه مونږ نېكي كوونكو ته بدله وركوو');
INSERT INTO "abulsalam" VALUES(6,85,'6:85',' (او هدایت كړى و مونږ) زكریا، او یحیٰی او عیسٰی او الیاس ته، (دوى) هر یو له صالحانو څخه دي');
INSERT INTO "abulsalam" VALUES(6,86,'6:86',' او اسمٰعیل، او اَلْیسَع او یونس او لوط ته، او (دوى) هر یو مونږه په ټولو عالَمونو باندې غوره كړي دي');
INSERT INTO "abulsalam" VALUES(6,87,'6:87',' او (فضیلت مو وركړى و) د دوى په پلرونو كې ځینو ته او د دوى په اولاد كې او د دوى په وروڼو كې (ځینو ته) او مونږ هغوى غوره كړي وو او مونږ هغوى ته د نېغې لارې هدایت كړى و');
INSERT INTO "abulsalam" VALUES(6,88,'6:88',' دغه د الله هدایت دى، په دې سره هدایت كوي په خپلو بنده ګانو كې چا ته چې وغواړي او كه چېرې هغوى شرك كړى وَى (، نو) له هغوى نه خامخا به ضايع شوي وو هغه عملونه چې دوى به كول');
INSERT INTO "abulsalam" VALUES(6,89,'6:89',' دا هغه كسان دي چې دوى ته مونږ كتاب او حُكم او نبوت وركړى و، نو كه دا خلق په دغو سره كفر وكړي، نو یقینًا مونږ د دې لپاره داسې خلق مقرر كړي دي چې په دغو سره كفر كوونكي نه دي');
INSERT INTO "abulsalam" VALUES(6,90,'6:90',' دا هغه كسان دي چې الله ورته هدایت كړى دى، نو ته د دوى د طریقې اتباع كوه، ته ووایه: زه له تاسو نه په دې (تبلیغ) باندې مزدوري نه غواړم، نه دى دا مګر د ټولو عالَمونو لپاره نصیحت');
INSERT INTO "abulsalam" VALUES(6,91,'6:91',' او دوى د الله قدر ونه كړ، څنګه چې د هغه د قدر كولو حق دى! كله چې دوى وویل: الله په انسان باندې هېڅ شى نه دى نازل كړى، ته (ورته) ووایه: هغه كتاب چا نازل كړى دى چې موسٰی راوړى و، چې رڼا او هدایت دى د خلقو لپاره؟ چې تاسو يې پاڼه پاڼه كوئ، چې (ځینې) ښكاره كوئ او ډېرې پټوئ، او تاسو ته هغه علم دركړى شو چې نه پرې تاسو پوهېدلئ او نه ستاسو پلرونه، ته ووایه: الله (نازل كړى دى) بیا ته دوى په خپلو باطلو بحثونو كې پرېږده چې لوبې كوي');
INSERT INTO "abulsalam" VALUES(6,92,'6:92',' او دا كتاب دى، چې مونږ نازل كړى دى، بركتي دى او د هغو (كتابونو) تصدیق كوونكى دى چې له ده نه مخكې وو او د دې لپاره چې ته د مكې خلق او د هغې ګېرچاپېره خلق ووېروې او هغه كسان چې پر اخرت ایمان لري، دوى پر ده ایمان راوړي، په داسې حال كې چې همدوى د خپلو لمونځونو ساتنه كوي');
INSERT INTO "abulsalam" VALUES(6,93,'6:93',' او له هغه كس نه لوى ظالم څوك دى چې په الله باندې دروغ تپي، یا وايي چې ما ته وحي شوې ده ! حال دا چې هغه ته هېڅ شى نه وي وحي شوى او له هغه چا نه (بل لوى ظالم څوك دى) چې وايي: ژر دى زه به (هم) نازل كړم د هغه په مثل چې الله نازل كړى دى ! او كه چېرې ته ووینې كله چې ظالمان د مرګ په سختیو كې (ګېر) وي او ملايكو خپل لاسونه وراوږده كړي وي (او ورته وايي) تاسو خپلې ساه ګانې راوباسئ، نن ورځ به تاسو ته د سپكوونكي عذاب سزا دركولى شي، په سبب د هغې ناحقه وینا چې تاسو به په الله باندې تپله، او تاسو به د ده د ایتونو له قبلولو نه انكار او تكبر كاوه ');
INSERT INTO "abulsalam" VALUES(6,94,'6:94',' او یقینًا یقینًا تاسو مونږ ته يواځې يواځې (ځانله، ځانله) راغلئ، لكه څنګه چې مونږ تاسو اول ځلې پیدا كړي وئ، او تاسو هغه څه د خپلو شاګانو شا ته پرېښودل چې مونږ تاسو ته دركړي وو او مونږ له تاسو سره ستاسو هغه سفارشیان نه وینو چې تاسو به دعوا كوله چې بې له شكه دغه په تاسو كې شريك دي، یقینًا یقینًا ستاسو خپل مینځي تعلق پرې شو او له تاسو څخه هغه څه ورك شول چې تاسو به (سفارشیان) ګڼل');
INSERT INTO "abulsalam" VALUES(6,95,'6:95',' بېشكه الله د دانې او زړي چوونكى دى، ژوندى له مړي څخه راوباسي او له ژوندي څخه د مړي راوېستونكى دى، همدغه الله دى، نو كوم طرف ته تاسو ګرځولى شئ!');
INSERT INTO "abulsalam" VALUES(6,96,'6:96',' د سبا (د رڼا) چوونكى دى او شپه يې د ارام ذریعه ګرځولې ده او لمرسترګه او سپوږمۍ يې د حساب ذریعې ګرځولې دي، دا د ښه غالب، ښه پوه ذات اندازه كول دي');
INSERT INTO "abulsalam" VALUES(6,97,'6:97',' او دى همغه ذات دى چې ستاسو لپاره يې ستوري پیدا كړي دي، د دې لپاره چې تاسو د هغو په ذریعې سره د وچې او سمندر په تیارو كې لار ومومئ، یقینًا مونږ ایتونه ښه واضحه بیان كړي دي د هغه قوم لپاره چې پوهېږي');
INSERT INTO "abulsalam" VALUES(6,98,'6:98',' او دى همغه ذات دى چې تاسو يې له یو نفس څخه پیدا كړئ، نو (تاسو لپاره) یو قرارځاى او یو امانتځاى دى، یقینًا مونږ ایتونه ښه واضحه بیان كړي دي د هغه قوم لپاره چې ښه پوهېږي');
INSERT INTO "abulsalam" VALUES(6,99,'6:99',' او دى همغه ذات دى چې له بره نه يې اوبه نازلې كړې دي، بیا مونږ په هغو سره د هر شي زرغونه راوښكلې ده، بیا مونږ له هغې (زرغونې) څخه شینكى راوښكلى دى، چې له دې (شینكي) نه مونږ پر يو بل سَورې دانې راوباسو او له كجورې څخه د هغې له كڅوړو نه جوخت او نژدې وږي، او د انګورو باغونه او زیتون او انار چې له یو بل سره څه مشابه وي او څه مشابه نه وي، تاسو د هغې مېوې ته وګورئ كله چې مېوه ونیسي او د هغې پخوالي ته، بېشكه په دې كې خامخا د هغه قوم لپاره نښې دي چې ایمان لري');
INSERT INTO "abulsalam" VALUES(6,100,'6:100',' او دوى له الله سره پېریان شریكان جوړ كړي دي، حال دا چې الله دوى پیدا كړي دي، او دوى بې له پوهې له ځانه، د هغهٔ لپاره زامن او لوڼه راپیداكړې، هغه لره پاكي ده او هغه (الله) له هغو خبرو نه ډېر اوچت دى چې دوى يې بیانوي');
INSERT INTO "abulsalam" VALUES(6,101,'6:101',' د اسمانونو او ځمكې بې له نمونې پیدا كوونكى دى، د هغه لپاره به اولاد څرنګه وي؟ حال دا چې د هغه لپاره هېڅ ښځه نشته، او هغهٔ، هر شى پیدا كړى دى او هغه په هر شي باندې ښه عالم دى');
INSERT INTO "abulsalam" VALUES(6,102,'6:102',' همدغه الله، ستاسو رب دى، له ده نه غير هېڅ لايق د عبادت نشته، د هر شي پیدا كوونكى دى، نو تاسو د هغه عبادت كوئ او هغه په هر شي باندې نګران دى');
INSERT INTO "abulsalam" VALUES(6,103,'6:103',' سترګې د هغهٔ ادراك نشي كولى او هغه د سترګو ادراك كوي او هغه ډېر باریكبین، ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(6,104,'6:104',' یقینًا تاسو ته د خپل رب له جانبه څرګند دلیلونه راغلي دي، نو چا چې بصیرت حاصل كړ، نو د خپل ځان لپاره يې حاصل كړ او څوك چې ړوند شو، نو ضرر يې په همدې نفس باندې دی او زه پر تاسو باندې نګهبان نه یم');
INSERT INTO "abulsalam" VALUES(6,105,'6:105',' او همدارنګه مونږ ایتونه بیانوو او د دې لپاره چې دوى ووايي: تا لوستل زده كړي دي او د دې لپاره چې مونږه دا ښه بیان كړو د هغه قوم لپاره چې پوهېږي');
INSERT INTO "abulsalam" VALUES(6,106,'6:106',' ته پیروي كوه د هغه شي چې تا ته د خپل رب له جانبه وحي كولى شي، نشته حق معبود مګر همغه دى او ته له مشركانو څخه مخ واړوه');
INSERT INTO "abulsalam" VALUES(6,107,'6:107',' او كه چېرې الله غوښتلى (، نو) دوى به شرك نه و كړى او ته مونږ په دوى باندې ساتونكى (نګران) نه يې ګرځولى او نه ته يې په دوى باندې نګران');
INSERT INTO "abulsalam" VALUES(6,108,'6:108',' او (اى مومنانو!) تاسو هغو (معبودانو) ته كنځلې مه كوئ چې دغه خلق يې له الله نه غیر بلي، نو دوى به په ناحقه سره، بې له پوهې، الله ته كنځل وكړي، همدارنګه مونږ د هر امت لپاره د هغوى عملونه ښايسته كړي دي، بیا خاص خپل رب ته د دوى واپسي ده، نو هغه به دوى ته د هغو عملونو خبر وركړي چې دوى به كول');
INSERT INTO "abulsalam" VALUES(6,109,'6:109',' او دوى د الله قسمونه وخوړل، پاخه قسمونه چې كه چېرې دوى ته مُعجِزه راشي، نو پر هغې به خامخا لازمًا ایمان راوړي، ته (ورته) ووایه: بېشكه همدا خبره ده چې مُعجِزې له الله سره دي او تاسو پر دې څه شي پوه كړئ چې بېشكه دغه مُعْجِزه چې كله راشي (بیا به هم) دوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(6,110,'6:110',' او مونږ د دوى زړونه او د دوى سترګې اړوو، لكه څنګه چې دوى په اول وارې په دې (نورو معجزاتو) باندې ایمان رانه وړ، او مونږ دوى په خپله سركشۍ كې پرېږدو، چې سرګردانه دي');
INSERT INTO "abulsalam" VALUES(6,111,'6:111','او كه مونږ دوى ته ملايك واقعاً نازل كړي وى او له دوى سره مړیو خبرې كړې وى او مونږ هر شى دوى ته مخامخ راجمع كړى وى، (بیا هم) دوى (داسې) نه وو چې ایمان يې راوړى وى مګر دا چې كه الله وغواړي، او لېكن د دوى زیاتره خلق جهالت كوي');
INSERT INTO "abulsalam" VALUES(6,112,'6:112',' او همدارنګه مونږ د هر نبي لپاره انساني او جني شیطانان، دښمنان ګرځولي وو، چې د دغو ځینو به ځینو نورو ته ښايسته كړى شوې خبرې د دوكه كولو (غولولو) لپاره ور اچولې او كه چېرې ستا رب، غوښتلى (، نو) دوى به دا (كار) نه و كړى، نو ته دوى پرېږده او هغه هم چې دوى له خپله ځانه څه دروغ جوړوي');
INSERT INTO "abulsalam" VALUES(6,113,'6:113',' او دا د دې لپاره چې دې (دروغو) ته د هغو خلقو زړونه مايل شي چې پر اخرت ایمان نه راوړي او د دې لپاره چې دوى دا خوښ كړي او د دې لپاره چې دوى هغه (ګناهونه) وكړي چې دوى يې كوونكي دي');
INSERT INTO "abulsalam" VALUES(6,114,'6:114',' ایا نو زه له الله نه سِوا بل كوم حَكم (عادل قاضي) ولټوم، حال دا چې همغه دى چې تاسو ته يې كتاب نازل كړى دى، چې ښه تفصیل يې شوى دى او هغه كسان چې مونږ هغوى ته (له ده نه مخكې) كتاب وركړى دى؛ هغوى پوهېږي چې بېشكه دا ستا د رب له جانبه په حقه سره نازل كړى شوى دى، نو ته هېڅ كله هم له شك كوونكو ځنې مه كېږه');
INSERT INTO "abulsalam" VALUES(6,115,'6:115',' او د رښتینولۍ او عدل له لحاظه ستا د رب خبره كمال ته رسېدلې ده، د هغهٔ كلماتو لره هیڅوك بدلوونكى نشته،او همغه ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(6,116,'6:116',' او كه چېرې ته اطاعت وكړې د هغو كسانو د اكثریت چې په ځمكه كې دي (، نو) دوى به تا د الله له لارې نه واړوي، دوى يواځې د ګمان پیروي كوي او دوى يواځې اټكل چلوي');
INSERT INTO "abulsalam" VALUES(6,117,'6:117',' بېشكه ستا رب پر هغه چا ښه پوه دى چې د هغه له لارې نه ګمراه كېږي او هغه په هدایت موندونكو (هم) ښه پوه دى');
INSERT INTO "abulsalam" VALUES(6,118,'6:118',' نو تاسو له هغې (مذبوحې) څخه خورئ چې پر هغې د الله نوم یاد كړى شوى وي، كه چېرې تاسو یئ چې د هغه پر ایتونو ایمان لرونكي یئ');
INSERT INTO "abulsalam" VALUES(6,119,'6:119',' او پر تاسو څه شوي دي چې له هغې (مذبوحې) څخه به خوراك نه كوئ چې پر هغې د الله نوم یاد كړى شوى وي او یقینًا الله تاسو ته هغه څه واضح كړي دي چې پر تاسو يې حرام كړي دي، سِوا له هغو (څيزونو) نه چې تاسو هغو ته مجبور كړى شئ، او بېشكه ډېر خلق، خامخا د خپلو خواهشاتو په ذرېعې سره، بې له پوهې نه (خلقو لره) ګمراه كوي، بېشكه ستا رب، هغه پر زیاتي كوونكو ښه پوه دى');
INSERT INTO "abulsalam" VALUES(6,120,'6:120',' او تاسو ښكاره او پټ ټول ګناهونه پرېږدئ، بېشكه هغه كسان چې ګناه كوي، ژر ده چې دوى ته به سزا وركړى شي د هغو (ګناهونو) په سبب چې دوى به كولې');
INSERT INTO "abulsalam" VALUES(6,121,'6:121',' او تاسو له هغې (مذبوحې) څخه خوراك مه كوئ چې پر هغې باندې د الله نوم نه وي یاد كړى شوى او بېشكه دا (خوراك كول) خامخا فسق او نافرماني ده او بېشكه يقينًا شیطانان خپلو دوستانو ته وسوسې اچوي، د دې لپاره چې له تاسو سره جګړه وكړي او كه چېرې تاسو د هغوى اطاعت وكړئ، (بيا نو) بېشكه تاسو (هم) خامخا مشركان یئ');
INSERT INTO "abulsalam" VALUES(6,122,'6:122',' ایا هغه څوك چې(مخكې) مړ و، بیا مونږ هغه ته ژوند وركړ او مونږ هغه ته رڼا عطا كړه چې د هغې په رڼا سره به هغه په خلقو كې ګرځي راګرځي؛ د هغه كس په شان دى چې د هغه حال داسې وي چې هغه په تیارو كې پروت وي، له هغو نه كله هم وتونكى نه وي، همدارنګه د كافرانو لپاره هغه عملونه ښايسته كړى شوي دي چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(6,123,'6:123',' او همدارنګه مونږ په هر كلي كې د هغه (كلي) مجرمان، مشران ګرځولي دي، د دې لپاره چې دوى په هغه (كلي) كې مكر (فريب) وكړي او دوى يواځې له خپلو ځانونو سره مكر كوي، په دې حال كې چې دوى شعور نه لري');
INSERT INTO "abulsalam" VALUES(6,124,'6:124',' او كله چې دوى ته كومه نښه راشي، دوى وايي: هیڅكله به مونږ ایمان رانه وړو تر هغه پورې چې مونږ ته راكړى شي په شان د هغو (نښو) چې د الله رسولانو ته وركړى شوي دي، الله ښه عالم دى پر هغه ځاى چې (هلته) خپل رسالت ږدي، ژر به هغو كسانو ته چې جرم يې كړى دى د الله په نیز ذلت ورسېږي او ډېر سخت عذاب، په سبب د هغه مكر چې دوى به كاوه ');
INSERT INTO "abulsalam" VALUES(6,125,'6:125',' نو هغه څوك چې الله هغهٔ ته د هدایت كولو اراده وكړي، (نو) د هغهٔ سینه د اسلام لپاره فراخه كړي او هغه څوك چې هغه يې د ګمراه كولو اراده وكړي (، نو) د هغهٔ سینه تنګه كړي، بېخي تنګه، ګویا كې هغه پورته اسمان ته په سختۍ سره خېژي، همدارنګه الله پلیتي (عذاب) اچوي په هغو كسانو باندې چې ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(6,126,'6:126',' او دا ستا د رب لار ده، چې نېغه ده، یقینًا مونږ ایتونه واضحه بیان كړي دي، د هغه قوم لپاره چې نصیحت اخلي');
INSERT INTO "abulsalam" VALUES(6,127,'6:127',' د دوى لپاره د خپل رب په نیز د سلامتۍ كور دى او هغه د دوى مددګار دى، په سبب د هغو عملونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(6,128,'6:128',' او په هغې ورځ كې چې هغه به دوى ټول راجمع كړي (او ورته وبه وايي:) اى د پېریانو ډلې! تاسو له انسانانو څخه یقینًا ډېر راخپل (او بې لارې) كړل او د دوى هغه دوستان به، چې له انسانانو ځنې دي؛ ووايي: اى زمونږه ربه! زمونږه ځینو له ځینو نورو نه فايده واخيسته او مونږ هغې نېټې ته ورسېدو كومه چې تا زمونږ لپاره مقرر كړې وه، الله به (دوى ته) ووايي: ستاسو ځاى اور دى، چې په هغه كې به همېشه یئ مګر هغه چې الله وغواړي، بېشكه ستا رب ښه حكمت والا، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(6,129,'6:129',' او همدارنګه مونږ ځینې ظالمان پر ځینو نورو مسلط كوو، په سبب د هغو عملونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(6,130,'6:130',' اى د پیریانو او انسانانو ډلې! ایا تاسو ته له هم تاسو نه رسولان نه وو راغلي چې تاسو ته به يې زما ایتونه بیانول او تاسو به يې د خپلې دې ورځې له ملاقات نه وېرولئ؟ دوى به ووايي: مونږ د خپلو ځانونو په خلاف ګواهي كوو او دوى دنيايي ژوند دوكه كړي وو او دوى به د خپلو ځانونو په خلاف ګواهي وركړي چې بېشكه دوى كافران وو');
INSERT INTO "abulsalam" VALUES(6,131,'6:131',' دا (د رسولانو لېږل) د دې لپاره (دي) چې ستا رب د ظلم په وجه د كلیو هلاكوونكى نه دى، په دې حال كې چې د هغو اوسېدونكي ناخبره وي');
INSERT INTO "abulsalam" VALUES(6,132,'6:132',' او د هر چا لپاره درجې دي له هغو اعمالو نه چې دوى كړي دي، او ستا رب له هغو عملونو نه چې دوى يې كوي؛ كله هم غافل نه دى');
INSERT INTO "abulsalam" VALUES(6,133,'6:133',' او هم ستا رب ښه بې پروا، (او) رحمت والا دى، كه چېرې هغه وغواړي (، نو) تاسو به بوځي او له تاسو وروسته به هغه (خلق) ځاى نیوونكي كړي چې يې وغواړي، لكه څنګه چې يې تاسو د بل قوم له اولادې نه پیدا كړئ');
INSERT INTO "abulsalam" VALUES(6,134,'6:134',' بېشكه هغه چې له تاسو سره يې وعده كولى شي، يقيناً راتلونكى دى او تاسو كله هم (الله لره) عاجزه كوونكي نه یئ');
INSERT INTO "abulsalam" VALUES(6,135,'6:135',' ته ووایه: اى زما قومه! تاسو په خپل حالت باندې عمل كوئ، بېشكه زه (هم) عمل كوونكى یم، پس تاسو به ژر پوه شئ چې څوك دى چې د هغه لپاره به د اخرت (ښه) انجام وي، بېشكه حقيقت دا دى چې ظالمان برى نه مومي');
INSERT INTO "abulsalam" VALUES(6,136,'6:136',' او دغو د الله لپاره په هغو څیزونو كې برخه مقرر كړه چې هغه پیدا كړي دي، چې فصل او څاروي دي، نو وايي: دا د الله لپاره دي، د دوى په عقیده، او دا زمونږ د شریكانو لپاره دي، نو هغه چې د دوى د شریكانو لپاره وي، نو هغه خو الله ته نه رسېږي او هغه چې د الله لپاره وي، نو هغه د دوى شریكانو ته رسېږي، بده ده هغه فیصله چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(6,137,'6:137',' او همدارنګه د ډېرو مشركانو لپاره د هغوى شریكانو د خپل اولاد وژل ښايسته كړي دي، د دې لپاره چې هغوى (شريكان)دوى هلاك كړي او د دې لپاره چې په دوى باندې خپل دین ګډوډ كړي او كه چېرې الله غوښتلى (،نو) دوى به دا (كار) نه و كړى، نو ته پرېږده دوى او هغه چې دوى دروغ جوړوي');
INSERT INTO "abulsalam" VALUES(6,138,'6:138',' او دوى وايي: دا ځینې څاروي او فصلونه دي، چې ممنوع دي، دا به نه خوري مګر هغه څوك چې مونږ يې وغواړو، د دوى په عقیده، او څه څاروي دي چې د هغو شاګانې منع كړى شوي دي (سورېدل او بار وړل پرې بند دي)، او څه څاروي دي چې دوى په هغو باندې د الله نوم نه یادوي،(او دا) په الله باندې د دروغو تپلو لپاره(كوي)، ضرور به الله دوى ته سزا وركړي، په سبب د هغو دروغو چې دوى به جوړول');
INSERT INTO "abulsalam" VALUES(6,139,'6:139',' او دوى وايي: هغه چې د دې څارویو په ګېډو كې دي (هغه) زمونږ د نرانو لپاره خالص دي او زمونږ پر ښځو باندې حرام دي او كه چېرې هغه مردار وي، نو دوى (نر او ښځه) په هغه كې شریكان دي، ژر به الله دوى ته د خپل دې بیان سزا وركړي، بېشكه هغه ښه حكمت والا، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(6,140,'6:140',' هغه كسان یقینًا تاوانیان شول چې خپل اولادونه يې قتل كړل، په دې حال كې چې بېوقوفان وو (او) بې پوهې وو او حرام يې كړل هغه څه چې الله دوى ته وركړي وو، په الله باندې يې دروغ وتپل، یقینًا دوى ګمراه شول او دوى هدایت موندونكي نه وو');
INSERT INTO "abulsalam" VALUES(6,141,'6:141',' او دى همغه ذات دى چې باغونه يې پیدا كړي دي، چې په څپرونو خېژول شوي دي او چې په څپرونو نه دي خېژول شوي او د كجورو ونې او فصل (يې پیدا كړي دي) چې د هغو مېوې مختلفې وي او زیتون او انار (يې پیدا كړي دي) چې(ځينې) په خپلو كې یو شان وي او ځينې غیر مُشابه هم وي، تاسو د هغو(ونو) له مېوو څخه خورئ! كله چې مېوه وكړي او د هغهٔ حق (عشر، زكات) د رېبلو په ورځ وركړئ، او تاسو اسراف مه كوئ، بېشكه الله اسراف كوونكي نه خوښوي');
INSERT INTO "abulsalam" VALUES(6,142,'6:142',' او (پیدا كړي يې دي) په څارویو كې بار وړونكي او وړوكي (څاروي، نو) تاسو له هغو ځنې خورئ چې الله تاسو ته دركړي دي او تاسو د شیطان د قدمونو پیروي مه كوئ، بېشكه هغه ستاسو ښكاره دښمن دى');
INSERT INTO "abulsalam" VALUES(6,143,'6:143',' (الله پیدا كړي دي حلال څاروي) اته جوړې (قسمونه)، له ګډو نه دوه (نر او ښځه) او له چېلو (بزو) نه دوه (نر او ښځه)، ته (دوى ته) ووایه: ایا الله دواړه نران حرام كړي دي، یا دواړه ښځې، یا هغه (بچي) چې د دواړو ښځو (ګډې او چېلۍ) رحمونه په هغو مشتمل وي، تاسو ما ته په علم سره خبر راكړئ، كه چېرې تاسو رښتیني یئ');
INSERT INTO "abulsalam" VALUES(6,144,'6:144',' او (پیدا كړي يې دي) له اوښانو نه دوه (نر او ښځه) او له غوایانو نه دوه (نر او ښځه) ته ووایه: ایا الله دواړه نران حرام كړي دي، یا دواړه ښځې، یا هغه (بچي) چې د دواړو ښځو (اوښې او غوا) رحمونه په هغو مشتمل وي، ایا تاسو حاضر وَئ كله چې الله تاسو ته د دې وصیت كاوه، نو له هغه چا نه لوى ظالم (بل) څوك دى چې په الله باندې دروغ تپي، د دې لپاره چې بې له علمه خلق ګمراه كړي، بېشكه الله ظالم قوم ته نېغه لار نه ښیي');
INSERT INTO "abulsalam" VALUES(6,145,'6:145',' (اى نبي!) دوى ته ووایه: زه نه مومم (هېڅ څیز) په هغه (شریعت) كې چې ما ته وحي كړى شوى دى په كوم خوړونكي باندې حرام كړى شوى، چې هغه يې خوري مګر دا چې هغه (څيز) مرداره وي، یا بهَول شوې وینه، یا د خنزیر غوښه، نو هغه خو پلیتي ده، یا (هغه څيز) نافرماني وي چې پر هغې د غیر الله لپاره اواز اوچت كړى شوى وي، نو هغه څوك چې ناچاره شي، چې نه زیاتي كوونكى وي او نه له حده تېرېدونكى وي، (نو د هغه لپاره يې خوړل حلال دي) نو بېشكه ستا رب ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(6,146,'6:146',' او پر هغو كسانو باندې چې یهودیان دي، مونږ ټول نوكداره څاروي حرام كړي وو او له غوایانو او ګډو چېلو نه مونږه پر هغوى باندې د هغو دواړو وازدې حرامې كړې وې، سِوا له هغو نه چې د هغو شاګانو پورته كړې وي، یا كُلمو، یا هغه چې له هډوكو سره لګېدلې وي، دا مونږ هغوى ته د خپلې سركشۍ سزا وركړه او بېشكه مونږ خامخا رښتیا ویونكي یو');
INSERT INTO "abulsalam" VALUES(6,147,'6:147',' نو كه چېرې دوى ستا تكذیب وكړي، نو ته ووایه: ستاسو رب د فراخه رحمت والا دى او د هغه عذاب له مجرم قوم نه بېرته نه ګرځول كېږي');
INSERT INTO "abulsalam" VALUES(6,148,'6:148',' ژر به هغه كسان ووايي چې شرك يې كړى دى، كه چېرې الله غوښتلى (نو) نه به مونږ شرك كړى و او نه زمونږ پلرونو او نه به مونږ څه شى حرام ګرځولى و، همدارنګه هغو كسانو (هم) تكذیب كړى و چې له دوى نه مخكې وو، تر دې چې هغوى زمونږ عذاب وڅاكه، ته ووایه: ایا تاسو سره څه علم شته چې پس مونږ ته يې راوباسئ، تاسو يواځې د ګمان پیروي كوئ او تاسو يواځې اټكل چلوونكي یئ');
INSERT INTO "abulsalam" VALUES(6,149,'6:149',' ته ووایه: نو خاص الله لره كامل دلیل دى، نو كه هغه غوښتلى تاسو ټول به يې لازمًا پر نېغه لار روان كړي وى');
INSERT INTO "abulsalam" VALUES(6,150,'6:150',' ته ووایه: تاسو خپل هغه ګواهان راولئ چې د دې خبرې ګواهي كوي چې بېشكه الله دغه (څیزونه) حرام كړي دي، نو كه هغوى ګواهي وركړي، نو ته له دوى سره ګواهي مه وركوه او ته د هغو خلقو په خواهشاتو پسې مه ځه چې زمونږ ایتونه يې دروغ ګڼلي دي او هغه كسان چې پر اخرت ایمان نه راوړي او هغوى له خپل رب سره (نور څیزونه) برابروي');
INSERT INTO "abulsalam" VALUES(6,151,'6:151',' ته ووایه: تاسو راشئ چې زه ولولم هغه څه چې ستاسو رب پر تاسو باندې حرام كړي دي، دا چې تاسو له هغه سره هېڅ شى مه شریكوئ او له مور و پلار سره ښېګڼه كوئ، او تاسو خپل اولاد د غریبۍ له وجې مه وژنئ، مونږ تاسو ته روزي دركوو او هغوى ته (هم) او تاسو د بې حیایۍ كارونو ته مه نژدې كېږئ، هغه چې په دغو كې ظاهر دي او هغه چې پټ دي او تاسو هغه نفس مه وژنئ چې الله حرام كړى دى مګر په حقه سره، دا (حكم) چې دى، هغه (الله) تاسو ته د دې كلك حكم دركړى دى، د دې لپاره چې تاسو له عقل نه كار واخلئ ');
INSERT INTO "abulsalam" VALUES(6,152,'6:152',' او تاسو د یتیم مال ته نژدې (هم) مه ورځئ مګر په هغې طریقې سره چې تر ټولو ښه وي تر دې چې هغه (یتیم) خپل پوخ والي ته ورسېږي او تاسو پېمانه او تول په انصاف سره پوره وركوئ، مونږ هېڅ یو نفس د هغه له طاقت نه زیات نه مكلف كوو او كله چې تاسو خبره كوئ، نو عدل كوئ، اګر چې هغه د خپلولۍ والا وي او د الله عهد پوره كوئ، دا (حكم) دى چې الله تاسو ته د دې كلك حكم كړى دى، د دې لپاره چې تاسو نصیحت ومنئ');
INSERT INTO "abulsalam" VALUES(6,153,'6:153',' او دا چې بېشكه دا زما لار ده، چې نېغه ده، نو تاسو د دې پیروي وكړئ او د (نورو) لارو پیروي مه كوئ، ګنې نو تاسو به (د نورو لارو پیروي) د هغهٔ له لارې نه تار په تار كړي، دا (حكم) دى چې هغه (الله) تاسو ته د دې كلك حكم كړى دى، د دې لپاره چې تاسو پرهېزګاره شئ');
INSERT INTO "abulsalam" VALUES(6,154,'6:154',' بیا مونږ موسٰی ته كتاب وركړ، د نعمت د پوره كولو لپاره پر هغه كس باندې چې نېكي يې وكړه او لپاره د هر شي د تفصیل او هدایت او رحمت، د دې لپاره چې دوى د خپل رب پر ملاقات ایمان راوړي');
INSERT INTO "abulsalam" VALUES(6,155,'6:155',' او دا كتاب دى چې مونږ نازل كړى دى، نو تاسو د ده پیروي وكړئ او پرهېزګاره شئ، د دې لپاره چې پر تاسو رحم وكړى شي');
INSERT INTO "abulsalam" VALUES(6,156,'6:156',' دا چې تاسو ونه وایئ چې همدا خبره ده چې زمونږ نه په مخكېنو دوه ډلو باندې كتاب نازل كړى شوى و، او بېشكه مونږ د هغوى له لوستلو نه یقینًا ناخبره وو');
INSERT INTO "abulsalam" VALUES(6,157,'6:157',' یا چې تاسو ونه وایئ: یقینًا كه چېرې پر مونږه كتاب نازل كړى شوى وَى نو مونږ به لازمًا له دوى نه ښه لارموندونكي وو، نو یقینًا تاسو ته د خپل رب له جانبه ښكاره دلیل راغلى دى او هدایت او رحمت، نو له هغه چا نه لوى ظالم څوك دى چې د الله ایتونه دروغ ګڼي او له هغو نه مخ اړوي؟ عنقریب به مونږ هغو كسانو ته د سخت عذاب سزا وركړو چې زمونږ له ایتونو نه مخ اړوي، په سبب د هغه چې دوى به اعراض كاوه');
INSERT INTO "abulsalam" VALUES(6,158,'6:158',' دوى انتظار نه كوي مګر د دې چې دوى ته ملايك راشي، یا ستا رب راشي، یا ستا د رب ځینې نښې راشي، په كومه ورځ كې چې ستا د رب ځینې نښې راشي (نو) هېڅ يو نفس ته به د هغه ایمان راوړل فايده ورنه كړي چې له مخكې نه يې ایمان نه وي راوړى، یا يې په خپل ایمان كې څه خیر نه وي ګټلى، ته (دوى ته) ووایه: تاسو انتظار كوئ، بېشكه مونږ (هم) انتظار كوونكي یو');
INSERT INTO "abulsalam" VALUES(6,159,'6:159',' بېشكه هغه كسان چې خپل دین يې تار په تار كړى دى، او دوى فرقې فرقې شول، ته له دوى نه په هېڅ شي كې نه يې، یقینًا خبره همدا ده چې د دوى معامله الله ته (سپارل شوې) ده، بیا به هغه دوى ته د هغو كارونو خبر وركوي چې دوى به كول');
INSERT INTO "abulsalam" VALUES(6,160,'6:160',' څوك چې یوه نېكي راوړي، نو د هغه لپاره د هغې لس چنده دي او څوك چې یوه بدي راوړي، نو هغه ته به يواځې د هغې په مثل سزا وركولى شي او پر دوى به ظلم نه كېږي');
INSERT INTO "abulsalam" VALUES(6,161,'6:161',' ته ووایه: بېشكه زه (چې یم) ما ته زما رب د نېغې لارې هدایت كړى دى، چې بېخي سم دین دى، د ابراهیم ملت دى، چې يواځې په حق پسې تلونكى و او له مشركانو څخه نه و');
INSERT INTO "abulsalam" VALUES(6,162,'6:162',' ته ووایه: بېشكه زما لمونځ او زما قرباني او زما ژوند او زما مرګ خاص د الله رب العلمین لپاره دي');
INSERT INTO "abulsalam" VALUES(6,163,'6:163',' د هغه هېڅ شریك نشته او ما ته د همدې (توحید) حكم شوى دى او زه تر ټولو مسلمانانو اول یم');
INSERT INTO "abulsalam" VALUES(6,164,'6:164',' ته ووایه: ایا زه له الله نه غیر (بل) رب ولټوم، حال دا چې هغه د هر شي رب دى، او هېڅ یو نفس (د ګناه) كسب نه كوي مګر د خپل (ځان) په ضرر يې كوي او هېڅ بار وړونكى د بل بار نه پورته كوي، بیا خاص خپل رب ته ستاسو واپس تلل دي، نو هغه به تاسو خبر كړي په هغو كارونو چې تاسو به په هغو كې اختلاف كاوه');
INSERT INTO "abulsalam" VALUES(6,165,'6:165',' او دى همغه ذات دى چې تاسو يې په ځمكه كې خلیفګان( ځاى ناستي) جوړ كړئ او ستاسو ځینې يې د ځینو (نورو) له پاسه په درجو كې پورته كړل، د دې لپاره چې تاسو وازمايي په هغه څه كې چې تاسو ته يې دركړي دي، بېشكه ستا رب ډېر ژر سزا وركوونكى دى او بېشكه هغه خامخا ښه بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(7,1,'7:1',' الف، لام، ميم، صاد');
INSERT INTO "abulsalam" VALUES(7,2,'7:2',' (دا) كتاب دى چې تا ته نازل كړى شوى دى، نو د ده په وجه دې ستا په سینه كې څه تنګي نه وي، د دې لپاره (نازل كړى شوى دى) چې ته د ده په ذرېعې سره (خلق) ووېروې او د مومنانو لپاره نصیحت دى');
INSERT INTO "abulsalam" VALUES(7,3,'7:3',' تاسو پیروي كوئ د هغه (دین) چې تاسو ته د خپل رب له جانبه نازل كړى شوى دى او تاسو له ده نه سِوا (د نورو) دوستانو پیروي مه كوئ، (خو) تاسو ډېر كم نصیحت اخلئ!');
INSERT INTO "abulsalam" VALUES(7,4,'7:4',' او څومره ډېر كلي دي چې مونږ هغه هلاك كړي دي، نو زمونږ عذاب هغو ته راغى، په دې حال كې چې دوى به د شپې ویده وو، یا به په غرمه كې ارام كوونكي وو');
INSERT INTO "abulsalam" VALUES(7,5,'7:5',' نو نه وه د دوى دعوٰی (بلنه) كله چې پر دوى زمونږ عذاب راغى، مګر دا چې دوى به ویل: بېشكه مونږ ظالمان وو');
INSERT INTO "abulsalam" VALUES(7,6,'7:6',' نو مونږ به له هغو خلقو نه خامخا لازمًا تپوس وكړو چې رسولان ورته استول شوي وو او خامخا لازمًا به مونږ له رسولانو نه (هم) تپوس وكړو ');
INSERT INTO "abulsalam" VALUES(7,7,'7:7',' نو خامخا او يقيناً به مونږ دوى ته په علم سره بیان وكړو او مونږ (څه) غايب خو نه وو');
INSERT INTO "abulsalam" VALUES(7,8,'7:8',' او په دغه ورځ (د عملونو) تول بېخي حق دى، نو هغه څوك چې تولونه (یا میزانونه) يې درانه وختل، نو همدغه خلق كامیاب دي');
INSERT INTO "abulsalam" VALUES(7,9,'7:9',' او هغه څوك چې تولونه (یا میزانونه) يې سپك وختل، نو دغه خلق هغه دي چې دوى خپلو ځانونو ته تاوان رسولى دى، په سبب د دې چې دوى به زمونږ له ایتونو سره ظلم كاوه');
INSERT INTO "abulsalam" VALUES(7,10,'7:10',' او یقینًا یقینًا مونږ تاسو ته په ځمكه كې قدرت دركړى دى او تاسو لپاره مو په هغې كې د ژوند اسباب پیدا كړي دي،(خو) تاسو بېخي ډېر لږ شكر وباسئ!');
INSERT INTO "abulsalam" VALUES(7,11,'7:11',' او یقینًا یقینًا مونږ تاسو پیدا كړئ، بیا مو ستاسو صورتونه جوړ كړل، بیا مونږ ملايكو ته وویل: ادم ته سجده وكړئ، نو هغوى سجده وكړه سِوا له ابلیس نه، هغه له سجده كوونكو ځنې نه و');
INSERT INTO "abulsalam" VALUES(7,12,'7:12',' وویل (الله): ته څه شي منع كړې، چې سجده دې ونه كړه كله چې ما تا ته حكم وكړ؟ وویل (شیطان): زه له هغه نه غوره یم، تا زه له اوره پیدا كړى یم او هغه دې له خَټې پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(7,13,'7:13',' (الله) وویل: نو ته له دې (جنت) نه كوز شه، پس تا ته دا حق نشته چې ته په دې (جنت) كې تكبر وكړې، نو ته وځه، بېشكه ته له ذلیلو څخه يې');
INSERT INTO "abulsalam" VALUES(7,14,'7:14',' وویل (ابلیس چې اى الله!) ته ما ته مهلت راكړه، تر هغې ورځې پورې چې خلق به بيا راژوندي كولى شي');
INSERT INTO "abulsalam" VALUES(7,15,'7:15',' وویل (الله): بېشكه ته له مهلت وركړى شويو څخه يې');
INSERT INTO "abulsalam" VALUES(7,16,'7:16',' (ابلیس) وویل: نو په سبب د هغه چې تا زه ګمراه كړم، زه به خامخا ضرور دوى ته ستا په نېغه لار كې كېنم ');
INSERT INTO "abulsalam" VALUES(7,17,'7:17',' بیا به خامخا لازمًا زه دوى ته راځم د دوى له مخې نه او د دوى له شا نه او د دوى له ښیو اړخونو نه او چپو اړخونو نه او ته به د دوى اكثره شكر ګذاره ونه مومې');
INSERT INTO "abulsalam" VALUES(7,18,'7:18',' وویل (الله): وُځه ته له دې ځاى نه، چې بدحاله غمجن كړى شوى، رټل شوى يې، یقینًا په دوى كې چې چا ستا پیروي وكړه (، نو) خامخا لازمًا به زه له تاسو ټولو نه جهنم ډكوم');
INSERT INTO "abulsalam" VALUES(7,19,'7:19',' او اى ادمه! ته او ستا ښځه په جنت كې اوسئ، نو تاسو خوراك كوئ له كوم ځاى نه چې مو خوښه شي، او دې ونې ته مه نژدې كېږئ (،ګنې) نو له ظالمانو نه به شئ');
INSERT INTO "abulsalam" VALUES(7,20,'7:20',' نو دوى دواړو ته شیطان وسوسه واچوله، د دې لپاره چې هغه (شیطان) دوى دواړو ته هغه څه ورښكاره كړي چې له دوى نه پټ وو، چې د دوى د شرم ځایونه دي او (ورته) ويې ويل: تاسو دواړه ستاسو رب له دې ونې نه نه یئ منع كړي مګر له دې وجې چې تاسو دواړه به ملايك شئ، یا چې شئ به له تل پاتې كېدونكو نه');
INSERT INTO "abulsalam" VALUES(7,21,'7:21',' او هغه دوى ته پوخ قسم وخوړ چې بېشكه زه تاسو لپاره یقینًا له خیر خواهي كوونكو ځنې یم ');
INSERT INTO "abulsalam" VALUES(7,22,'7:22',' نو هغه دوى دواړو ته په دوكې سره تمه وركړه، نو كله چې دوى هغه ونه وڅكله، دوى ته خپل شرم ځایونه ښكاره شول او دواړه لګیا شول چې پر خپلو ځانونو يې د جنت پاڼې نښلولې او دوى ته خپل رب اواز وكړ چې ایا ما تاسو دواړه له دې ونې نه نه وئ منع كړي او تاسو ته مې نه وو ویلي چې بېشكه شیطان ستاسو د دواړو ښكاره دښمن دى؟');
INSERT INTO "abulsalam" VALUES(7,23,'7:23',' دوى وویل: اى زمونږه ربه! مونږ پر خپلو ځانونو ظلم كړى دى او كه چېرې ته مونږ ونه بخښې او پر مونږ رحم ونه كړې (، نو) مونږ به خامخا لازمًا له تاوانیانو څخه شو');
INSERT INTO "abulsalam" VALUES(7,24,'7:24',' وویل (الله): تاسو كوز شئ، ستاسو ځینې به د ځینو نورو لپاره دښمنان وي او تاسو لپاره په ځمكه كې استوګنه ده او تر یو وخته پورې فايده اخيستل دي');
INSERT INTO "abulsalam" VALUES(7,25,'7:25',' (الله) وویل: په همدې (ځمكه) كې به تاسو ژوندون كوئ او په همدې كې به تاسو مرئ او له همدې نه به تاسو راوښكل كېږئ');
INSERT INTO "abulsalam" VALUES(7,26,'7:26',' اى د ادم اولاده! یقینًا مونږ پر تاسو باندې لباس نازل كړى دى چې ستاسو د شرم ځایونه پټوي او د زینت جامې (مو نازلې كړې دي) او د پرهېزګارۍ لباس؛ دغه ډېر غوره دي، دا د الله له ایتونو څخه دي، د دې لپاره چې دوى نصیحت حاصل كړي');
INSERT INTO "abulsalam" VALUES(7,27,'7:27',' اى د ادم اولاده! تاسو لره دې شیطان هیڅكله هم په فتنه كې وانه چوي، لكه څنګه چې يې ستاسو مور وپلار له جنت نه وويستل، چې له هغوى نه يې د هغوى لباس وښكلو، د دې لپاره چې هغوى ته خپل شرم ځایونه وښیي، بېشكه هغه او د هغهٔ قبیله تاسو له هغه ځایه ویني چې تاسو يې نه وینئ، بېشكه مونږ شیطانان د هغو خلقو لپاره دوستان جوړ كړي دي چې ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(7,28,'7:28',' او كله چې دوى څه بې حیايي وكړي (،نو) وايي: مونږ په دې باندې خپل پلرونه موندلي دي او الله مونږ ته د دې حكم كړى دى، ته (ورته) ووایه: بېشكه الله د بې حیایۍ حكم نه كوي، ایا تاسو په الله باندې هغه دروغ تپئ چې تاسو پرې نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(7,29,'7:29',' ته ووایه: زما رب د انصاف حكم كړى دى او (دا چې) تاسو خپل مخونه د هر لمانځه په وخت كې نېغ (سیده) كړئ او تاسو هغه بلئ، چې د هغه لپاره د دین (عبادت) خالص كوونكي یئ، لكه څنګه چې يې تاسو اول ځل پیدا كړي یئ (هماغه شان به) تاسو راګرځئ');
INSERT INTO "abulsalam" VALUES(7,30,'7:30',' یوې ډلې ته يې هدایت كړى دى او بله ډله (يې ګمراه كړې ده) چې په هغو باندې ګمراهي ثابته شوې ده، بېشكه دوى له الله نه سِوا شیطانان (د ځان) دوستان نیولي وو او دوى دا ګمان كاوه چې بېشكه همدوى نېغه لار موندونكي دي');
INSERT INTO "abulsalam" VALUES(7,31,'7:31',' اى د ادم اولاده! تاسو خپل زینت (لباس) راواخلئ د هر لمانځه په وخت كې او خوراك كوئ او څښاك كوئ او تاسو اسراف مه كوئ، بېشكه هغه (الله) اسراف كوونكي نه خوښوي');
INSERT INTO "abulsalam" VALUES(7,32,'7:32',' ته ووایه: هغه زینت چا حرام كړى دى چې الله د خپلو بنده ګانو لپاره راوېستلى دى او له رزقه څخه پاكیزه شیان (چا حرام كړي دي؟) ته ووایه: دا (نعمتونه) په دنيايي ژوند كې د هغو كسانو لپاره دي چې ایمان يې راوړى دى، چې (د دوى لپاره به) د قیامت په ورځ بېخي خالص وي، همدارنګه مونږ ایتونه په تفصیل سره بیانوو د هغه قوم لپاره چې پوهېږي');
INSERT INTO "abulsalam" VALUES(7,33,'7:33',' ته ووایه: یقینًا همدا خبره ده چې زما رب د بې حیایۍ كارونه حرام كړي دي، هغه چې له هغو څخه ظاهِر وي او هغه چې باطن وي او (حرامه كړې يې ده) ګناه او ناحقه ظلم كول او دا چې تاسو له الله سره هغه شى شریك كړئ چې د هغه په هكله الله هېڅ دلیل نه وي نازل كړى، او دا چې تاسو په الله باندې هغه دروغ وتپئ چې تاسو پرې نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(7,34,'7:34',' او د هر امت لپاره یوه نېټه ده، نو كله چې د دوى نېټه راشي، دوى به نه یوه ګړۍ تاخیر كوي او نه به دوى ړومبى كېږي');
INSERT INTO "abulsalam" VALUES(7,35,'7:35',' اى د ادم اولاده! كه تاسو ته له هم ستاسو نه واقعًا رسولان راشي، چې تاسو ته زما ایتونه بیانوي، نو هغه څوك چې پرهېزګاره شو او (د ځان) اصلاح يې وكړه، نو په دوى باندې به نه څه وېره وي او نه به دوى غمجن كېږي');
INSERT INTO "abulsalam" VALUES(7,36,'7:36',' او هغه كسان چې زمونږ د ایتونو تكذیب يې كړى دى او له هغو نه يې سركشي (لويي) كړې ده، دغه كسان د اور ملګري دي، دوى به په هغه كې همېشه وي');
INSERT INTO "abulsalam" VALUES(7,37,'7:37',' نو له هغه كس نه لوى ظالم څوك دى چې په الله باندې دروغ تپي، یا د هغه د ایتونو تكذیب كوي؟ دغه كسان دي چې دوى ته به له لیكل شوي (عمر، رزق وغیره) نه خپله برخه ورسېږي، تر دې چې كله دوى ته زمونږ استازي راشي، چې دوى مړه كوي (، نو ورته) وايي به: چېرته دي هغه معبودان چې تاسو به له الله نه غیر بلل؟ هغوى به ووايي: هغه له مونږ نه ورك شول، او دوى به د خپلو ځانونو په خلاف ګواهي وكړي دا چې بېشكه دوى كافران وو');
INSERT INTO "abulsalam" VALUES(7,38,'7:38',' (ورته) وبه وايي: تاسو له هغو امتونو سره یو ځاى اور ته داخل شئ چې له تاسو نه مخكې په پېریانو او انسانانو كې تېر شوي دي، هر كله چې یوه ډله ننوځي (، نو) د خپل ځان په مثل ډلې باندې به لعنت وايي، تر دې چې كله دوى ټول په دې (اور) كې راجمع شي، د دوى وروستنۍ به د خپلو ړومبنو په هكله وايي: اى زمونږه ربه! دغه هغه كسان دي، چې مونږ يې ګمراه كړي وو، نو دوى ته د اور دوه چنده عذاب وركړه، وبه وايي (الله) د هر یو لپاره دوه چنده (عذاب) دى او لېكن تاسو نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(7,39,'7:39',' د دوى ړومبني به خپلو وروستنو ته ووايي: نو تاسو لپاره پر مونږ باندې هېڅ فضیلت نشته، پس تاسو عذاب وڅَكئ د هغو عملونو په سبب چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(7,40,'7:40',' بېشكه هغه كسان چې زمونږ ایتونه يې دروغ ګڼلي دي او له هغو نه يې تكبر كړى دى، د دوى لپاره به د اسمان دروازې نه پرانستل كېږي او نه به دوى جنت ته داخلېږي تر هغه پورې چې اوښ د ستنې په سوري كې ننوځي او همدارنګه مونږ مجرمانو ته سزا وركوو');
INSERT INTO "abulsalam" VALUES(7,41,'7:41',' د دوى لپاره له جهنم څخه فرش دى او د دوى له پاسه به (د اور) اغوستنى وي او همدارنګه مونږ ظالمانو ته سزا وركوو');
INSERT INTO "abulsalam" VALUES(7,42,'7:42',' او هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، مونږ هېڅ یو نفس نه مكلف كوو مګر د هغه د وَس مطابق، دغه كسان د جنت ملګري دي، دوى به په هغه كې همېشه وي');
INSERT INTO "abulsalam" VALUES(7,43,'7:43',' او مونږ به د دوى په سينو كې پرته كينه راوباسو، د دوى له لاندې به ولې بهېږي، او دوى به وايي: ټولې ستاينې هغه الله لره دي؛ هغه چې مونږ ته يې د دې هدایت وكړ او مونږ پخپله (داسې) نه وو چې دا لار مو موندلې وَى، كه چېرې الله مونږ ته دا لار نه وه راښودلې، یقینًا یقینًا زمونږ د رب رسولان په حقه سره راغلي وو او دوى ته به اواز وكړى شي چې دا جنت دى چې تاسو د ده وارثان ګرځول شوي یئ، په سبب د هغو عملونو چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(7,44,'7:44',' او جنتیان به دوزخیانو ته اواز وكړي چې یقینًا مونږ هغه وعده چې زمونږ رب له مونږ سره كړې وه حقه وموندله، نو ایا تاسو (هم) هغه وعده حقه وموندله چې ستاسو رب (له تاسو سره) كړې وه؟ هغوى به ووايي: هو! نو د دوى په مینځ كې به یو اعلان كوونكى اعلان وكړي چې د الله لعنت په ظالمانو باندې دى');
INSERT INTO "abulsalam" VALUES(7,45,'7:45',' هغه كسان چې د الله له لارې نه منع كول كوي او په دې كې كوږوالى لټوي او هم دوى د اخرت منكر دي');
INSERT INTO "abulsalam" VALUES(7,46,'7:46',' او د دواړو په مینځ كې پرده ده او د اَعراف له پاسه به څه نور كسان وي چې دوى به هر یو (له جنتیانو او دوزخیانو نه) په خپلو علامو سره پېژني او دوى به جنتیانو ته اواز وكړي چې پر تاسو دې سلام وي، دوى لا جنت ته داخل شوي نه دي او دوى (جنت ته د ننوتلو) طمع لري');
INSERT INTO "abulsalam" VALUES(7,47,'7:47',' او كله چې د دوى سترګې د دوزخیانو په طرف واړول شي، دوى به وايي: اى زمونږه ربه! مونږ له ظالم قوم سره مه یو ځاى كوه');
INSERT INTO "abulsalam" VALUES(7,48,'7:48',' او د اَعراف ملګري به داسې سړیو ته اواز وكړي چې دوى به يې په خپلو نښو سره پېژني، دوى به (ورته) وايي: تاسو ته نه خو ستاسو ډلې فايده درورسوله او (نه) هغه چې تاسو به تكبر كاوه');
INSERT INTO "abulsalam" VALUES(7,49,'7:49',' ایا دا هغه كسان دي چې تاسو به قسمونه خوړل چې الله به دوى ته رحمت ونه رسوي (دوى ته خو ویل شوي دي چې) تاسو جنت ته داخل شئ، پر تاسو باندې هېڅ وېره نشته او نه به تاسو غمجن كېږئ');
INSERT INTO "abulsalam" VALUES(7,50,'7:50',' او دوزخیان به جنتیانو ته اواز وكړي چې تاسو پر مونږ باندې څه اوبه راتوى كړئ، یا له هغه څه نه چې الله تاسو ته دركړي دي (څه راكړئ)، وبه وايي (جنتیان) بېشكه الله دا دواړه په كافرانو باندې حرام كړي دي');
INSERT INTO "abulsalam" VALUES(7,51,'7:51',' هغه كسان چې خپل دین يې تماشه او لوبه ګرځولى دى او دوى دنيايي ژوند دوكه كړي (غولولي) دي، نو نن ورځ به يې مونږ (په سخت عذاب كې) پرېږدو، لكه څنګه چې هغوى د خپلې دې ورځې ملاقات هېر كړى (او عمل يې پرېښى) و او هغه چې دوى به زمونږ له ایتونو نه انكار كاوه');
INSERT INTO "abulsalam" VALUES(7,52,'7:52',' او یقینًا یقینًا مونږ دوى ته كتاب راوړى دى چې مونږ د علم له مخې په تفصیل سره بیان كړى دى، چې هدایت او رحمت دى د هغه قوم لپاره چې ایمان راوړي');
INSERT INTO "abulsalam" VALUES(7,53,'7:53',' دوى انتظار نه كوي مګر د هغه (قیامت) د انجام، په هغې ورځ چې د هغه انجام راشي؛ (نو) هغه كسان به وايي چې دا يې مخكې هېر كړى و: یقینًا زمونږ د رب رسولان په حقه سره راغلي وو، نو ایا زمونږ لپاره څه سفارشیان شته چې زمونږ لپاره سفارش وكړي؟ یا مونږ واپس كړى شو، نو (بیا) له هغه عمل نه سِوا نور عمل وكړو چې مونږه كاوه، یقینًا دوى خپلو ځانونو ته نقصان رسولى دى او له دوى څخه هغه دروغ ورك شول چې دوى به جوړول');
INSERT INTO "abulsalam" VALUES(7,54,'7:54',' بېشكه ستاسو رب هغه الله دى چې اسمانونه او ځمكه يې په شپږو ورځو كې پیدا كړي دي، بیا د عرش له پاسه برقراره شو، هغه په شپې سره ورځ پټوي، په دې حال كې چې دا (شپه) هغې (ورځ) لره په تلوار سره طلب كوي او (پیدا كړې يې ده) لمرسترګه، او سپوږمۍ او ستوري، چې د هغه په حكم سره مسخر كړى شوي دي، اګاه شئ! خاص هغه لره دي پیدا كول او حكم كول، ډېر بركت والا دى الله چې رب العلمین دى');
INSERT INTO "abulsalam" VALUES(7,55,'7:55',' تاسو خپل رب وبلئ په عاجزۍ سره او په پټه سره، بېشكه هغه له حد نه تېرېدونكي نه خوښوي');
INSERT INTO "abulsalam" VALUES(7,56,'7:56',' او تاسو په ځمكه كې ورانى مه كوئ، د هغې له اصلاح نه پس، او تاسو هغه (الله) رابلئ په وېرې سره او په طمع سره، بېشكه د الله رحمت نېكي كوونكو ته ډېر نژدې دى');
INSERT INTO "abulsalam" VALUES(7,57,'7:57',' او دى همغه ذات دى چې هواګانې لېږي، چې د هغه له رحمت نه وړاندې زېرى وركوونكې وي، تر دې چې كله دا (هواګانې) درنې ورېځې راپورته كړي (، نو) مونږ هغه مړ ښار ته روانوو، نو مونږ په دې سره اوبه نازلوو، نو په هغو سره هر قسمه مېوې راوباسو، همدارنګه به مونږ مړي راوباسو (راژوندي كوو)، د دې لپاره چې تاسو نصیحت ومنئ');
INSERT INTO "abulsalam" VALUES(7,58,'7:58',' او پاكیزه ښار، د هغې زرغونه د خپل رب په حكم سره راوځي او هغه (ځمكه) چې خرابه (ښوره) وي (د هغې زرغونه) نه راوځي مګر ناقصه، همدارنګه مونږ ایتونه بیا، بیا بیانوو د هغه قوم لپاره چې شكر وباسي');
INSERT INTO "abulsalam" VALUES(7,59,'7:59',' یقینًا یقینًا مونږ نوح خپل قوم ته لېږلى و، نو هغه وویل: اى زما قومه! تاسو د الله عبادت كوئ، تاسو لپاره له هغه نه سِوا (بل) هېڅ برحق معبود نشته، بېشكه زه پر تاسو د لويې ورځې له عذاب نه وېرېږم');
INSERT INTO "abulsalam" VALUES(7,60,'7:60',' د هغه د قوم سردارانو وویل: بېشكه مونږ تا لازمًا په ښكاره ګمراهۍ كې وینو');
INSERT INTO "abulsalam" VALUES(7,61,'7:61',' هغه وویل: اى زما قومه! په ما كې هېڅ ګمراهي نشته او لېكن زه د رب العلمین رسول یم');
INSERT INTO "abulsalam" VALUES(7,62,'7:62',' زه تاسو ته د خپل رب پېغامونه دررسوم او ستاسو خیرخواهي كوم او زه د الله له جانبه پر هغه څه پوهېږم چې تاسو پرې نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(7,63,'7:63',' ایا تاسو په دې تعجب كوئ چې تاسو ته د خپل رب له جانبه نصیحت راغى پر داسې سړي چې له هم ستاسو څخه دى، د دې لپاره چې تاسو ووېروي او د دې لپاره چې تاسو پرهېزګاره شئ او د دې لپاره چې پر تاسو رحم وكړى شي');
INSERT INTO "abulsalam" VALUES(7,64,'7:64',' نو هغوى دا دروغژن وګاڼه، نو مونږ هغه ته او هغو كسانو ته چې له ده سره په كشتۍ كې وو، نجات وركړ او هغه كسان مو غرق كړل چې زمونږ ایتونه يې دروغ ګڼلي وو، بېشكه هغوى ړانده خلق وو ');
INSERT INTO "abulsalam" VALUES(7,65,'7:65',' او (مونږ لېږلى و) عاد (قوم) ته د هغوى ورور هود، هغه وویل: اى زما قومه! تاسو د الله عبادت كوئ، تاسو لپاره له هغه نه سِوا (بل) هېڅ برحق معبود نشته، ایا نو تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(7,66,'7:66',' د هغه د قوم كافرو سردارانو وویل: بېشكه مونږ خامخا تا په بېوقوفۍ كې وینو او بېشكه مونږ خامخا پر تا ګمان كوو له دروغجنو څخه');
INSERT INTO "abulsalam" VALUES(7,67,'7:67',' هغه وویل: اى زما قومه! په ما كې هېڅ بېوقوفي نشته او لېكن زه د رب العلمین رسول یم ');
INSERT INTO "abulsalam" VALUES(7,68,'7:68',' زه تاسو ته د خپل رب پېغامونه دررسوم او زه ستاسو لپاره خیرخواه، امانت دار یم');
INSERT INTO "abulsalam" VALUES(7,69,'7:69',' ایا تاسو پر دې تعجب كوئ چې تاسو ته د خپل رب له جانبه نصیحت راغى پر داسې سړي باندې چې له هم ستاسو څخه دى، د دې لپاره چې تاسو ووېروي، او تاسو هغه وخت رایاد كړئ كله چې يې تاسو له نوح نه پس خلیفګان (ځاى ناستي) جوړ كړئ او تاسو ته يې په تخلیق كې زیات فراخ والى دركړ، نو تاسو د الله نعمتونه یادوئ، د دې لپاره چې تاسو كامیاب شئ');
INSERT INTO "abulsalam" VALUES(7,70,'7:70',' هغوى وویل: ایا ته د دې لپاره مونږ ته راغلى يې چې مونږ د یو الله عبادت وكړو او هغه معبودان پرېږدو چې زمونږ پلرونو يې عبادت كاوه، نو هغه (عذاب) راوله چې تهٔ مونږ په هغه سره وېروې، كه چېرې ته له رښتينو څخه يې');
INSERT INTO "abulsalam" VALUES(7,71,'7:71',' هغه وویل: یقینًا پر تاسو باندې د خپل رب له جانبه عذاب او غضب ثابت شو، ایا تاسو زما سره په هغو نومونو كې جګړه كوئ چې هغه تاسو پخپله او ستاسو پلرونو نومولي دي، الله د هغو په باره كې هېڅ دلیل نه دى نازل كړى، نو تاسو انتظار كوئ، بېشكه زه تاسو سره له انتظار كوونكو ځنې یم');
INSERT INTO "abulsalam" VALUES(7,72,'7:72',' نو مونږ هغه (هود) ته او هغو كسانو ته چې له ده سره يې ایمان راوړى و، په خپل رحمت سره نجات وركړ او د هغو كسانو جرړه (بېخ) مو غوڅه كړه چا چې زمونږ ایتونه دروغ ګڼلي وو او هغوى له سره مومنان نه وو');
INSERT INTO "abulsalam" VALUES(7,73,'7:73',' او ثمودیانو ته مو د هغوى ورور صالح (لېږلى و)، هغه وویل: اى زما قومه! تاسو د الله عبادت كوئ، تاسو لپاره له هغه نه سِوا (بل) هېڅ حق معبود نشته، یقینًا تاسو ته د خپل رب له جانبه ښكاره معجزه راغلې ده، دا د الله اوښه ده ستاسو لپاره، چې نښه ده، نو تاسو دا پرېږدئ چې د الله په ځمكه كې خوراك كوي او دې ته تكلیف مه رسوئ (ګنې) نو تاسو لره به ډېر دردوونكى عذاب ونیسي');
INSERT INTO "abulsalam" VALUES(7,74,'7:74',' او تاسو هغه وخت یاد كړئ چې هغه (الله) تاسو له عادیانو نه پس خلیفګان (ځاى ناستي، نايبان) جوړ كړئ او تاسو ته يې په ځمكه كې ځاى دركړ، چې تاسو د دې (ځمكې) په نرمو هوارو ځایونو كې هسكې ماڼۍ جوړوئ او تاسو غرونه د كورونو لپاره توږئ (كنئ)، نو تاسو د الله نعمتونه یاد كړئ او په ځمكه كې وراني كوونكي مه ګرځئ، چې فسادیان شئ');
INSERT INTO "abulsalam" VALUES(7,75,'7:75',' د هغه په قوم كې تكبر كوونكو مشرانو هغو كسانو ته وويل چې كمزوري كړى شوي وو، هغه چا ته چې له دوى ځنې يې ایمان راوړى و: ایا تاسو پوهېږئ چې بېشكه صالح د خپل رب له جانبه رالېږل شوى (رسول) دى؟ هغو (كمزورو) وویل: بېشكه مونږ پر هغه څه ایمان لرونكي یو چې په هغه سره هغه (صالح) رالېږل شوى دى');
INSERT INTO "abulsalam" VALUES(7,76,'7:76',' تكبر كوونكو كسانو وویل: بېشكه مونږ په هغه څه چې تاسو پرې ایمان راوړى دى، كفركوونكي یو');
INSERT INTO "abulsalam" VALUES(7,77,'7:77',' نو هغوى اوښه ووژله او د خپل رب له حكم نه يې سركشي وكړه او ويې ويل:اى صالحه! پر مونږ راوله هغه (عذاب) چې ته مونږ په هغه سره وېروې، كه چېرې ته له رسولانو څخه يې');
INSERT INTO "abulsalam" VALUES(7,78,'7:78',' نو دوى سختې زلزلې ونیول، نو شول دوى په خپلو كورونوكې پړمخې مړه پراته');
INSERT INTO "abulsalam" VALUES(7,79,'7:79',' نو هغه، له دوى نه وګرځېده، او ويې ويل:اى زما قومه! یقینًا یقینًا ما تاسو ته د خپل رب پېغام دروساوه او ستاسو خیرخواهي مې وكړه او لېكن تاسو نصیحت (خیرخواهي) كوونكي نه خوښوئ');
INSERT INTO "abulsalam" VALUES(7,80,'7:80',' او (لېږلى و مونږ) لوط كله چې هغه خپل قوم ته وویل: ایا تاسو داسې بد كار كوئ چې په مخلوقاتو كې هېڅ یو كس له تاسو نه دغه (بد كار) ته ړومبـى شوى نه دى');
INSERT INTO "abulsalam" VALUES(7,81,'7:81',' بېشكه تاسو د شهوت (پوره كولو) لپاره سړیو ته ورځئ، په دې حال كې چې له ښځو بېلېږئ، بلكې تاسو له حد نه تېرېدونكى قوم یئ');
INSERT INTO "abulsalam" VALUES(7,82,'7:82',' او د هغه د قوم (بل) جواب نه و سِوا له دې نه چې (ويې ويل:) دا خلق له خپل كلي نه وباسئ، بېشكه دوى داسې خلق دي چې ځان ډېر پاك ساتي');
INSERT INTO "abulsalam" VALUES(7,83,'7:83',' نو مونږ هغهٔ ته او د هغهٔ خاندان ته نجات وركړ؛ د هغهٔ له ښځې نه سِوا، هغه په پاتې كېدونكو كې وه');
INSERT INTO "abulsalam" VALUES(7,84,'7:84',' او مونږ پر هغوى باندې ناشنا باران ووراوه، نو ته وګوره چې د مجرمانو انجام څنګه و ؟');
INSERT INTO "abulsalam" VALUES(7,85,'7:85',' او (لېږلى و مونږ) مدین ته د هغوى ورور شعیب، هغه وویل: اى زما قومه! تاسو د الله عبادت كوئ، تاسو لپاره له هغه نه سِوا (بل) هېڅ حق معبود نشته، یقینًا تاسو ته د خپل رب له جانبه ښكاره دلیل راغلى دى، نو تاسو پېمانه او وزن پوره كوئ او خلقو ته د هغوى شیان كم مه وركوئ او په ځمكه كې ورانى مه كوئ، د هغې له اصلاح نه پس، دا تاسو لپاره ډېر غوره دي، كه چېرې تاسو ایمان راوړونكي یئ');
INSERT INTO "abulsalam" VALUES(7,86,'7:86',' او تاسو په هره لار كې مه كېنئ، چې تاسو (خلق) وېروئ او تاسو د الله له لارې نه هغه څوك منع كوئ چې پر الله ایمان راوړي او تاسو په هغې (لاره د الله كې) كې كوږوالى لټوئ او تاسو هغه وخت یاد كړئ كله چې تاسو ډېر لږ وئ، نو هغهٔ تاسو ډېر كړئ او تاسو وګورئ چې د ورانكارو انجام څنګه و؟');
INSERT INTO "abulsalam" VALUES(7,87,'7:87',' او كه په تاسو كې یوه ډله داسې ده چې پر هغه شي يې ایمان راوړى دى چې زه ورسره رالېږل شوى یم او بلې ډلې ایمان نه دى راوړى، نو تاسو صبر كوئ تر دې چې الله زمونږ په مینځ كې فیصله وكړي او هغه تر ټولو بهتر فیصله كوونكى دى ');
INSERT INTO "abulsalam" VALUES(7,88,'7:88','د هغه له قوم څخه كبرجنو مشرانو وویل: اى شعیبه! مونږ به تا او هغه كسان چې له تا سره يې ایمان راوړى دى؛ له خپل كلي نه خامخا لازمًا وباسو، یا به تاسو زمونږ دین ته خامخا لازمًا راګرځئ، هغه وویل: ایا اګر كه مونږ (دا) بدګڼونكي یو');
INSERT INTO "abulsalam" VALUES(7,89,'7:89',' یقینًا مونږ به په الله باندې سخت دروغ تپلي وي كه مونږ ستاسو دین ته در وګرځو وروسته له دې چې مونږ ته الله له هغه نه خلاصى راكړ، او مونږ ته دا حق نشته چې هغه ته وګرځو مګر دا چې زمونږ رب الله (څه) وغواړي، زمونږ رب هر شى په (خپل) علم كې راګېر كړى دى، خاص په الله باندې مونږ توكل كړى دى، اى زمونږه ربه! ته زمونږ په مینځ كې او زمونږ د قوم په مینځ كې په حقه سره فیصله وكړه او ته تر ټولو غوره فیصله كوونكى يې');
INSERT INTO "abulsalam" VALUES(7,90,'7:90',' او د هغه له قومه كافرو مشرانو وویل: یقینًا كه چېرې تاسو د شعیب تابعداري وكړه، بېشكه تاسو به په دغه وخت كې خامخا له تاوانیانو څخه یئ');
INSERT INTO "abulsalam" VALUES(7,91,'7:91',' نو دوى زلزلې ونیول، نو دوى شول په خپلو كورونو كې پړمخې (مړه) پراته');
INSERT INTO "abulsalam" VALUES(7,92,'7:92',' هغه كسان چې شعیب يې دروغجن ګڼلى و، (داسې هلاك شول) لكه چې دوى په دغو (كورونو) كې بېخي اباد نه وو، هغه كسان چې شعیب يې دورغجن ګڼلى و، همغوى تاوانیان وو');
INSERT INTO "abulsalam" VALUES(7,93,'7:93',' نو دى له هغوى نه وګرځېده او ويې ويل:اى زما قومه! یقینًا یقینًا ما تاسو ته د خپل رب پېغامونه درورسول او ستاسو خیرخواهي مې وكړه، نو زه څنګه په كافر قوم باندې افسوس وكړم');
INSERT INTO "abulsalam" VALUES(7,94,'7:94',' او مونږ په هېڅ یو كلي كې هېڅ یو نبي نه و لېږلى مګر دا چې مونږ به د هغه (كلي) اوسېدونكي په سختۍ او تكلیف كې ونیول، د دې لپاره چې دوى (الله ته) عاجزي او زاري وكړي');
INSERT INTO "abulsalam" VALUES(7,95,'7:95',' بیا مونږ (هغوى ته) د بدۍ په ځاى خوشحالي په بدل كې وركړه، تر دې چې دوى ډېر شول او ويې ويل: یقینًا زمونږ پلرونو ته (هم) زمونږ په شان تكلیف او خوشحالي رسېدلې وه، نو مونږه دوى ناڅاپه راونیول او دوى شعور نه لاره');
INSERT INTO "abulsalam" VALUES(7,96,'7:96',' او كه چېرې د كلیو اوسېدونكو ایمان راوړى وَى او پرهېزګاره شوي وَى (، نو) مونږ به خامخا په دوي باندې له اسمان او ځمكې نه بركتونه پرانستلي وى او لېكن دوى تكذیب وكړ، نو مونږ دوى د هغو عملونو په سبب ونیول چې دوى كول');
INSERT INTO "abulsalam" VALUES(7,97,'7:97',' ایا د كلیو اوسېدونكي له دې نه بې غمه شوي دي چې په دوى باندې زمونږ عذاب د شپې راشي، په داسې حال كې چې دوى به ویده وي');
INSERT INTO "abulsalam" VALUES(7,98,'7:98',' ایا د كلیو اوسېدونكي له دې نه بې غمه شوي دي چې په دوى باندې زمونږ عذاب په څاښت كې راشي، په داسې حال كې چې دوى به لوبې كوي');
INSERT INTO "abulsalam" VALUES(7,99,'7:99',' ایا نو دوى د الله له مكر (پټ تدبير) نه بې وېرې دي، پس د الله له مكر نه يواځې تاواني خلق بې وېرې كېږي');
INSERT INTO "abulsalam" VALUES(7,100,'7:100',' او ایا څرګنده شوې نه ده هغو كسانو ته چې د ځمكې له (مخكينو) اوسېدونكو نه وروسته دوى د هغې وارثان ګرځي دا چې كه مونږ غوښتلى (، نو) مونږ به دوى ته د دوى د ګناهونو په وجه سزا وركړې وَى او مونږ به د دوى په زړونو باندې مهر ولګوو، بیا به دوى (هېڅ) نه ویني');
INSERT INTO "abulsalam" VALUES(7,101,'7:101',' دغه كلي دي چې مونږ يې تا ته څه خبرونه بیانوو، او یقینًا یقینًا دوى ته خپلو رسولانو ښكاره معجزې راوړې وې، نو دوى كله هم داسې نه وو چې پر هغه څه ایمان راوړي د څه چې يې مخكې تكذیب كړى و، همدارنګه الله د كافرانو په زړونو باندې مهر لګوي');
INSERT INTO "abulsalam" VALUES(7,102,'7:102',' او مونږ په دوى كې د زیاترو هېڅ وعده نه ده موندلې (چې دوى پرې وفا كړې وي) او بېشكه مونږ د دوى زیاتره لازمًا نافرمان (وعده ماتوونكي) موندلي دي');
INSERT INTO "abulsalam" VALUES(7,103,'7:103',' بیا وروسته له دوى نه مونږ فرعون او د هغهٔ سردارانو ته موسٰی ولېږه له خپلو ایتونو (معجزو) سره، نو هغوى له دغو (ایتونو) سره ظلم وكړ، نو ته وګوره د ورانكارو انجام څنګه شو؟');
INSERT INTO "abulsalam" VALUES(7,104,'7:104',' او موسٰی وویل: اى فرعونه! بېشكه زه د رب العلمین له جانبه رسول یم');
INSERT INTO "abulsalam" VALUES(7,105,'7:105',' ټینګ ولاړ (یم) په دې خبره چې نه وایم پر الله مګر حقه خبره، یقینًا ما تاسو ته د خپل رب له جانبه ښكاره معجزه راوړې ده، نو ته زما سره بني اسرائیل خوشې كړه (ويې لېږه) ');
INSERT INTO "abulsalam" VALUES(7,106,'7:106',' فرعون وویل: كه چېرې تا كومه نښه راوړې وي، نو هغه راوړاندې كړه، كه چېرې ته له رښتینو څخه يې');
INSERT INTO "abulsalam" VALUES(7,107,'7:107',' نو هغه خپله اَمسا وغورځوله، نو ناڅاپه هغه ښكاره اژدها وه');
INSERT INTO "abulsalam" VALUES(7,108,'7:108',' او خپل لاس يې راوښكه، نو ناڅاپه هغه د كتونكو لپاره تك سپین و');
INSERT INTO "abulsalam" VALUES(7,109,'7:109',' د فرعون د قوم مشرانو وویل: بېشكه دا خامخا ډېر پوه جادوګر دى');
INSERT INTO "abulsalam" VALUES(7,110,'7:110',' دى غواړي چې تاسو له خپلې ځمكې (وطن) نه وباسي، نو تاسو څه حكم كوئ؟');
INSERT INTO "abulsalam" VALUES(7,111,'7:111',' خلقو وویل: ته ده ته او د ده ورور ته مهلت وركړه او په ښارونو كې راټولوونكي ولېږه');
INSERT INTO "abulsalam" VALUES(7,112,'7:112',' چې تا ته هر ښه پوه جادوګر راولي');
INSERT INTO "abulsalam" VALUES(7,113,'7:113',' او جادوګر فرعون ته راغلل، ويې ويل:بېشكه زمونږ لپاره به لازمًا اجرت وي كه چېرې همدا مونږ غلبه موندونكي شو');
INSERT INTO "abulsalam" VALUES(7,114,'7:114',' (فرعون) وویل: هو! یقینًا لازمًا تاسو به له مقربینو څخه یئ');
INSERT INTO "abulsalam" VALUES(7,115,'7:115',' (جادوګرو) وویل: اى موسٰی! یا ته (امسا) وغورځوه او یا به همدا مونږ (خپلو رسيو او امساګانو لره) غورځوونكي یو');
INSERT INTO "abulsalam" VALUES(7,116,'7:116',' ويې ويل: تاسو يې وغورځوئ، نو كله چې هغوى وغورځولې (، نو) د خلقو پر سترګو يې جادو وكړ او هغوى يې ښه ووېرول او ډېر لوى جادو يې وكړ');
INSERT INTO "abulsalam" VALUES(7,117,'7:117',' او مونږ موسٰی ته وحي وكړه چې خپله امسا وغورځوه، نو ناڅاپه هغې هغه څه ښوى تېرول چې هغوى به جوړول');
INSERT INTO "abulsalam" VALUES(7,118,'7:118',' نو حق ثابت شو او هغه څه باطل شول چې هغوى به كول');
INSERT INTO "abulsalam" VALUES(7,119,'7:119',' نو هغه ساحران په دغه ځاى كې مغلوب شول او دوى ذلیله سپك وګرځېدل');
INSERT INTO "abulsalam" VALUES(7,120,'7:120',' او (جادوګر) وغورځول شول، چې سجده كوونكي وو');
INSERT INTO "abulsalam" VALUES(7,121,'7:121',' ويې ويل:مونږ پر رب العلمین ایمان راوړ');
INSERT INTO "abulsalam" VALUES(7,122,'7:122',' د موسٰی او د هارون پر رب');
INSERT INTO "abulsalam" VALUES(7,123,'7:123',' فرعون وویل: تاسو پر ده ایمان راوړ مخكې له دې چې زه تاسو ته اجازت دركړم، بېشكه دا يقينًا یو مكر (سازش) دى چې تاسو دغه په دې ښار كې ترسره كړى دى، د دې لپاره چې تاسو له دې (ښار) نه د هغهٔ اوسېدونكي وباسئ، نو ژر به تاسو پوه شئ');
INSERT INTO "abulsalam" VALUES(7,124,'7:124',' زه به ستاسو لاسونه او ستاسو پښې ادلې بدلې خامخا لازمًا غوڅې كړم، بیا به خامخا لازمًا تاسو ټول په دار كړم');
INSERT INTO "abulsalam" VALUES(7,125,'7:125',' وویل (جادوګرو): بېشكه مونږ د خپل رب په طرف بېرته ورتلونكي یو');
INSERT INTO "abulsalam" VALUES(7,126,'7:126',' او ته بدې نه ګڼې زمونږ نه (هېڅ عمل) سوا له دې نه چې مونږ د خپل رب پر ایتونو ایمان راوړى دى، كله چې دغه (ایتونه) مونږ ته راغلل، اى زمونږه ربه! ته پر مونږ باندې صبر راتوى كړه او مونږ ته وفات راكړه، په داسې حال كې چې مسلمانان یو');
INSERT INTO "abulsalam" VALUES(7,127,'7:127',' او د فرعون د قوم مشرانو (فرعون ته) وویل: ایا ته موسٰی او د هغه قوم د دې لپاره (ازاد) پرېږدې چې په ځمكه كې ورانى وكړي او تا او ستا معبودان پرېږدي، وویل (فرعون): ژر به مونږ د دوى زامن ووژنو او د دوى ښځې (جينكۍ) به ژوندۍ پرېږدو او بېشكه مونږ د دوى له پاسه زوروَر یو');
INSERT INTO "abulsalam" VALUES(7,128,'7:128',' موسٰی خپل قوم ته وویل: تاسو له الله نه مدد وغواړئ او صبر كوئ، بېشكه ځمكه د الله لپاره ده، هغه په خپلو بنده ګانو كې هغه څوك د دې وارث ګرځوي چې يې وغواړي او ښه انجام د پرهېزګارانو لپاره دى');
INSERT INTO "abulsalam" VALUES(7,129,'7:129',' (بني اسرائیلو) وویل: مونږ ته تكلیف رسول شوى دى، مخكې له دې چې ته مونږ ته راشې او وروسته له دې چې ته مونږ ته راغلې. (موسٰی) وویل: امېد دى چې ستاسو رب به ستاسو دښمن هلاك كړي او تاسو به په ځمكه كې خلیفګان (ځاى ناستي) جوړ كړي، بیا به هغه ګوري چې تاسو څنګه عمل كوئ؟');
INSERT INTO "abulsalam" VALUES(7,130,'7:130',' او یقینًا یقینًا مونږه فرعونیان په وچكالۍ سره او د مېوو په كم والي سره نیولي (مبتلا كړي) وو، د دې لپاره چې دوى نصیحت ومني');
INSERT INTO "abulsalam" VALUES(7,131,'7:131',' نو كله چې به پر دوى خوشحالي راغله، ویل به يې: مونږ د همدې حقدار يو او كه چېرې پر دوى به څه تكلیف راغى، نو بدفالي به يې نيوله، په موسٰی او په هغه چا چې له هغه سره وو، خبردار! بېشكه خبره همدا ده چې د دوى بدفالي له الله سره ده او لېكن د دوى زیاتره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(7,132,'7:132',' او دوى به (موسٰی ته) ویل: ته چې مونږ ته هر قسمه نښه راوړې؛ د دې لپاره چې په هغې سره ته پر مونږه جادو وكړې، نو مونږ په تا باندې كله هم ایمان راوړونكي نه یو');
INSERT INTO "abulsalam" VALUES(7,133,'7:133',' نو مونږه په دوى باندې (وار په وار) طوفان، او ملخان او سپږې او چیندخې او وینه راولېږل، چې بېلې بېلې نښې وې، بيا هم هغوى تكبر وكړ او هغوى مجرم خلق وو');
INSERT INTO "abulsalam" VALUES(7,134,'7:134',' او كله چې په دوى باندې عذاب راپرېوتلو (، نو) ويې ويل: اى موسٰی: ته زمونږ لپاره له خپل رب نه دعا وغواړه په هغه څه سره چې له تا سره يې وعده كړې ده، یقینًا كه ته زمونږ نه عذاب لرې كړې، مونږ به خامخا لازمًا پر تا ایمان راوړو او خامخا لازمًا به له تا سره بني اسرائیل ولېږو (خوشې كړو)');
INSERT INTO "abulsalam" VALUES(7,135,'7:135',' نو كله چې به مونږ له دوى نه عذاب لرې كړ تر هغې مودې پورې چې دوى هغې ته رسېدونكي وو، ناڅاپه به دوى وعده ماته كړه');
INSERT INTO "abulsalam" VALUES(7,136,'7:136',' بيا مونږ له دوى نه انتقام واخيست، نو په سمندر كې مو غرق كړل، ځكه چې یقینًا هغوى زمونږ د ایتونو تكذیب كړى و او له هغو نه غافله وو');
INSERT INTO "abulsalam" VALUES(7,137,'7:137',' او مونږه هغه خلق چې ضعيفه كولى شول د ځمكې د مشرِقي اړخونو او د هغې د مغرِبي اړخونو وارثان وګرځول، هغه (اړخونه) چې مونږ په هغو كې بركت اچولى دى، او ستا د رب ډېره ښه كلمه په بني اسرائیلو باندې تمامه شوه، د دوى د صبر كولو په سبب او مونږه تباه كړې هغه (ودانۍ) چې فرعون او د هغه قوم به جوړولې او هغه چې دوى به لوړولې');
INSERT INTO "abulsalam" VALUES(7,138,'7:138',' او مونږه بني اسرائیل پر سمندر پورې اېستل، نو دوى پر داسې قوم راغلل چې د خپلو بتانو پر عبادت قايم وو، بني اسرائیلو وویل: اى موسٰی! زمونږ لپاره (هم) داسې معبود جوړ كړه، لكه څنګه چې د دوى لپاره معبودان دي، (موسٰی) وویل: بېشكه تاسو داسې قوم یئ چې جهالت كوئ');
INSERT INTO "abulsalam" VALUES(7,139,'7:139',' بېشكه دا خلق (چې دي) تباه ده هغه (تګلاره) چې دوى په هغې كې دي او باطل دي هغه عملونه چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(7,140,'7:140',' (موسٰی) وویل: ایا زه تاسو ته له الله نه سِوا معبود ولټوم، حال دا چې هغه (الله) تاسو په عالمونو باندې غوره كړي یئ');
INSERT INTO "abulsalam" VALUES(7,141,'7:141',' او كله چې مونږ تاسو ته له فرعونیانو نه نجات دركړ، چې تاسو ته به يې سخت عذاب دررساوه، چې ستاسو زامن به يې وژل او ستاسو ښځې (لوڼه) به يې ژوندۍ پرېښودلې او په دې كې ستاسو د رب له جانبه ډېر لوى ازمېښت و');
INSERT INTO "abulsalam" VALUES(7,142,'7:142',' او مونږ له موسٰی سره د دېرشو شپو وعده وكړه او مونږ هغه په لسو(نورو) سره پوره كړې، نو ستا د رب ټاكلې موده څلوېښت ورځې پوره شوه، او موسٰی خپل ورور هارون ته وویل: ته زما په قوم كې زما نايب جوړ شه او اصلاح كوه او د ورانكارو د لارې پیروي مه كوه');
INSERT INTO "abulsalam" VALUES(7,143,'7:143',' او كله چې موسٰی زمونږ ټاكلي وخت ته راغى او خپل رب يې ورسره خبرې وكړې، ويې ويل: اى زما ربه! ته ما ته (خپل ځان) وښیه چې زه تا ووینم، رب وویل: ته به ما (په دنیا كې ) له سره ونه وینې او لېكن ته غر ته وګوره، نو كه چېرې هغه پر خپل ځاى ثابت پاتې شو، نو بيا به ته ما خامخا ووینې، نو كله چې د هغهٔ رب غر ته تجلي وكړه (، نو) هغه يې ذرې ذرې وګرځاوه او موسٰی بېخوده پرېوتلو، نو كله چې په خود شو، ويې ويل: زه ستا پاكي بیانوم، زه تا ته توبې وباسم او زه تر ټولو اول ایمان راوړونكى یم');
INSERT INTO "abulsalam" VALUES(7,144,'7:144',' الله وویل: اى موسٰی! بېشكه ما تا ته په ټولو خلقو باندې په خپلو پېغامونو سره او په خپل كلام سره غوره والى دركړى دى، نو ته هغه څه ونیسه چې ما تا ته دركړي دي، او له شكر كوونكو څخه شه');
INSERT INTO "abulsalam" VALUES(7,145,'7:145',' او مونږ د هغه لپاره په تختیو كې د هر شي باره كې نصیحت او د هر شي تفصیل ولیكه، نو ورته ومو ويل: ته دغه په قوت سره ونیسه او خپل قوم ته حكم وكړه چې د دې په غوره (احكامو) عمل وكړي، ژر به زه تاسو ته د فاسقانو استوګنه وښیم');
INSERT INTO "abulsalam" VALUES(7,146,'7:146',' زه به له خپلو ایتونو نه هغه كسان لازمًا واړوم چې په ځمكه كې ناحقه تكبر كوي او كه دوى هره نښه وویني (، بیا هم) پر هغې ایمان نه راوړي او كه چېرې دوى د هدایت لار وویني (، نو) هغه خپله لار نه غوره كوي، او كه چېرې د ګمراهۍ لار وویني (، نو) هغه خپله لار ګرځوي، دا ځكه چې یقینًا دوى زمونږ د ایتونو تكذیب كړى دى، او دوى له هغو نه غافله وو');
INSERT INTO "abulsalam" VALUES(7,147,'7:147',' او هغه كسان چې زمونږ د ایتونو او د اخرت د ملاقات تكذیب يې كړى دى، د هغوى عملونه برباد شول، دوى ته به بدله نه وركول كېږي مګر د هغو كارونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(7,148,'7:148',' او د موسٰی قوم (طور ته) د هغه (له تلو) نه پس له خپلو زېوراتو نه مجسم سخى جوړكړ چې د هغه اواز د سخي و، ایا دوى نه دي كتلي چې بېشكه دا (مجسم سخى) نه له دوى سره خبرې كولى شي او نه دوى ته څه لار ښودلى شي، دوى دغه (معبود) جوړ كړى دى او دوى ظالمان وو');
INSERT INTO "abulsalam" VALUES(7,149,'7:149',' او كله چې دوى پښېمانه شول او پوه شول چې یقینًا دوى ګمراه شوي دي، ويې ويل: كه چېرې زمونږ رب پر مونږ رحم ونه كړي او مونږ ته بخښنه ونه كړي (، نو) مونږ به خامخا ضرور له تاوانیانو څخه شو');
INSERT INTO "abulsalam" VALUES(7,150,'7:150',' او كله چې موسٰی خپل قوم ته بېرته راغى، چې ډېر غصّه، افسوس كوونكى و، ويې ويل: تاسو له ما نه وروسته زما بد (او خراب) نيابت كړى دى، ایا تاسو د خپل رب له حكم نه تلوار وكړ؟ او تختۍ يې وغورځولې او د خپل ورور سر يې راونېوه، چې هغه يې خپل ځان ته راښكه، هغه وویل: اى زما د مور زویه! بېشكه دې قوم زه كمزورى كړى وم او نژدې وو چې ما ووژني، نو ته په ما باندې دښمنان مه خوشحالوه او ما له ظالم قوم سره مه ملګرى كوه');
INSERT INTO "abulsalam" VALUES(7,151,'7:151',' (موسٰی) وویل: اى زما ربه! ته ما ته بخښنه وكړه او زما ورور ته او مونږ په خپل رحمت كې داخل كړه او ته تر ټولو لوى مهربان يې');
INSERT INTO "abulsalam" VALUES(7,152,'7:152',' بېشكه هغه كسان چې سخى يې (معبود) نیولى دى، ژر به دوى ته د خپل رب له جانبه غضب ورسېږي او په دنيايي ژوند كې ذلت او همدارنګه مونږ بهتان تړونكو ته سزا وركوو');
INSERT INTO "abulsalam" VALUES(7,153,'7:153',' او هغه كسان چې بد كارونه يې كړي دي، بیا يې له هغو نه وروسته توبه وېستلې ده او ایمان يې راوړى دى، بېشكه ستا رب له دې (توبې) نه پس خامخا ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(7,154,'7:154',' او كله چې د موسٰی غصه سړه شوه، تختۍ يې راواخيستلې او د هغو په لیك كې هدایت او رحمت و د هغو كسانو لپاره چې هغوى يواځې له خپل رب نه وېرېږي');
INSERT INTO "abulsalam" VALUES(7,155,'7:155',' او موسٰی له خپل قوم نه اویا كسان زمونږ د مقرر كړي وخت لپاره غوره كړل، نو كله چې هغوى زلزلې ونیول، موسٰی وویل: اى زما ربه! كه چېرې تا غوښتلى، نو تا به دوى مخكې هلاك كړي وو او زه (هم)، ایا ته مونږه د هغه كار په وجه هلاكوې چې په مونږ كې بېوقوفو كړى دى؟ نه دي دا مګر ستا ازمایل، ته په دې سره ګمراه كوې چا لره چې ته وغواړې او پر نېغه لار روانوې چا لره چې وغواړې، ته زمونږ كارساز يې، نو ته مونږ ته بخښنه وكړه او پر مونږ رحم وكړه او ته تر ټولو غوره بخښونكى يې');
INSERT INTO "abulsalam" VALUES(7,156,'7:156',' او ته زمونږ لپاره په دې دنیا كې ښېګڼه ولیكه او په اخرت كې هم، بېشكه مونږه تا ته رجوع كړې ده، الله وویل: زه خپل عذاب رسوم چا ته چې وغواړم او زما رحمت هر شى راګېر كړى دى، نو ژر به زه ولیكم دا (رحمت) د هغو كسانو لپاره چې پرهېزګاره دي او زكات ادا كوي او هغه كسان چې هغوى زمونږ پر ایتونو ایمان لري');
INSERT INTO "abulsalam" VALUES(7,157,'7:157',' هغه كسان چې د اُمي رسول (او) نبي پیروي كوي هغه چې دوى يې له خپل ځان سره په تورات او انجیل كې لیكلى مومي، دوى ته د نېكۍ حكم كوي او دوى له بدۍ نه منع كوي او دوى ته پاكیزه څیزونه حلالوي او په دوى باندې خبیث څیزونه حراموي او له دوى نه د دوى درانه بارونه لرې كوي او هغه قیدونه هم چې پر دوى وو، نو هغه كسان چې پر ده يې ایمان راوړ او د ده ادب يې وكړ او د ده مدد يې وكړ او د هغې رڼا پیروي يې وكړه چې له ده سره نازل كړى شوې ده، همدغه كسان كامیاب دي');
INSERT INTO "abulsalam" VALUES(7,158,'7:158',' ته ووایه: اى خلقو! بېشكه زه ستاسو د ټولو په طرف د هغه الله (رالېږل شوى) رسول یم چې خاص د هغهٔ لپاره د اسمانونو او ځمكې بادشاهي ده، نشته هېڅ حق معبود مګر همدى دى، همدغه ژوند وركوي او مرګ وركوي، نو تاسو پر الله او د هغه پر رسول ایمان راوړئ چې نبي دى، امي دى، هغه چې پر الله او د هغه پر كلماتو ایمان لري او تاسو د هغه پیروي وكړئ، د دې لپاره چې تاسو هدایت ومومئ');
INSERT INTO "abulsalam" VALUES(7,159,'7:159',' او د موسٰی په قوم كې یوه ډله داسې هم ده چې په حقه سره لار ښیي او په همدې (حق) سره انصاف كوي');
INSERT INTO "abulsalam" VALUES(7,160,'7:160',' او مونږ دغه بني اسرائیل بېل بېل كړل دولس قبیلې، ډلې ډلې او مونږ موسٰی ته وحي وكړه، كله چې له هغه نه خپل قوم اوبه وغوښتې، چې ته په خپلې امسا سره كاڼى ووهه، نو له هغه نه دولس چینې جاري شوې، ټولو خلقو په يقيني ډول خپل خپل ګودر وپېژنده او مونږه پر هغوى باندې وریځ سيورى كړه او پر هغوى باندې مو ’’مَنْ‘‘ او ’’سَلْوٰی‘‘ نازل كړل، تاسو هغه پاكیزه څیزونه خورئ چې مونږ تاسو ته دركړي دي او دوى پر مونږ ظلم نه و كړى او لېكن دوى خاص پر خپلو ځانونو ظلم كاوه');
INSERT INTO "abulsalam" VALUES(7,161,'7:161',' او كله چې دوى ته وویل شول چې تاسو په دې كلي كې اوسئ او د دې له پيداوار نه خوراك كوئ كوم ځاى چې مو خوښه شي، او تاسو وایئ: وبخښه (زمونږ ګناهونه) او په دروازه كې سجده كوونكي داخل شئ، مونږ به ستاسو ګناهونه وبخښو، نېكي كوونكي به خامخا په نور زیات فضل سره ونازوو.');
INSERT INTO "abulsalam" VALUES(7,162,'7:162',' نو هغو كسانو چې ظلم يې كړى و، هغه خبره چې دوى ته ویل شوې وه په بل شان بدله كړه، نو مونږه پر هغوى باندې له اسمان نه عذاب راولېږه، په سبب د هغه ظلم چې دوى به كاوه');
INSERT INTO "abulsalam" VALUES(7,163,'7:163',' او ته له دوى نه د هغه كلي په باره كې تپوس وكړه چې د سمندر پر غاړه پروت و، كله چې به هغوى د خالي په ورځ له حد نه تېرېدل، كله چې به هغوى ته د هغوى د خالي په ورځ خپل كبان ښكاره راتلل، او په هغې ورځ كې چې دوى به په خالي كې نه وو داخل (، نو) دوى ته به نه راتلل، همدارنګه مونږه دوى ازمایو، په سبب د هغې نافرمانۍ چې دوى به كوله');
INSERT INTO "abulsalam" VALUES(7,164,'7:164',' او كله چې په دوى كې یوې ډلې وویل: ولې تاسو داسې خلقو ته نصیحت كوئ چې الله د هغو هلاكوونكى دى، یا هغو ته ډېر سخت عذاب وركوونكى دى؟ هغوى وویل: خپل رب ته د عذر وړاندې كولو لپاره او د دې لپاره چې دوى پرهېزګاره شي');
INSERT INTO "abulsalam" VALUES(7,165,'7:165',' نو كله چې دوى هغه (دین) پرېښوده چې په هغه سره ورته نصیحت شوى و، مونږ هغو كسانو ته نجات وركړ چې له بدۍ نه يې منع كوله او په ډېر سخت عذاب سره مو هغه كسان راونیول چې ظلم يې كړى و، په سبب د هغې نافرمانۍ چې دوى به كوله');
INSERT INTO "abulsalam" VALUES(7,166,'7:166',' نو كله چې دوى سركشي وكړه د هغه څه باره كې چې ترې منع شوي وو، مونږ دوى ته وویل: تاسو ذلیله شادوګان شئ');
INSERT INTO "abulsalam" VALUES(7,167,'7:167',' او كله چې ستا رب اعلان وكړ چې خامخا ضرور به په دوى باندې د قیامت د ورځې پورې داسې څوك مسلط كوي چې دوى ته به سخت عذاب رسوي، بېشكه ستا رب خامخا ډېر تېز عذاب والا دى او بېشكه هغه خامخا ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(7,168,'7:168',' او مونږه دوى په ځمكه كې ټكړې ټكړې كړل (ووېشل)، ډلې ډلې، په دوې كې ځینې نېكان دي او ځینې په كې راكم (لاندې) دي، او مونږ دوى په نعمتونو او تكلیفونو سره وازمایل، د دې لپاره چې دوى (الله ته) راوګرځي.');
INSERT INTO "abulsalam" VALUES(7,169,'7:169',' نو له دوى نه وروسته داسې نالايقه خلق د دوى ځاى ناستي شول، چې د كتاب وارثان وګرځېدل، چې د دې ډېرې خسیسې دنیا سامان اخلي او (بیا هم) وايي: مونږ ته به ضرور بخښنه وشي او كه چېرې دوى ته د دې په مثل سامان راشي (، نو) هغه (هم) اخلي، ایا له دوى نه په كتاب كې پخه وعده نه وه اخيستل شوې چې دوى به پر الله باندې نه وايي مګر حقه خبره او دوى هغه (احكام) لوستي وو چې په هغه (كتاب) كې ليك وو، او د اخرت كور د هغو كسانو لپاره ډېر غوره دى چې پرهېزګاره دي، ایا نو تاسو ولې له عقل نه كار نه اخلئ!');
INSERT INTO "abulsalam" VALUES(7,170,'7:170',' او هغه كسان چې (د الله) كتاب ښه ټینګ نیسي او لمونځ قايموي، بېشكه مونږ د نېكي كوونكو اجر نه ضايع كوو');
INSERT INTO "abulsalam" VALUES(7,171,'7:171',' او كله چې مونږ د دوى له پاسه غر راپورته كړ، ګویا كې هغه سايبان دى، او دوى یقین وكړ چې بېشكه دا پر دوى راپرېوتونكى دى، تاسو په قوت سره ونیسئ هغه (تورات) چې مونږ تاسو ته دركړى دى او یاد كړئ هغه څه چې په هغه كې دي، د دې لپاره چې تاسو پرهېزګاره شئ');
INSERT INTO "abulsalam" VALUES(7,172,'7:172',' او كله چې ستا رب د ادم له اولادې نه د هغو له شاګانو نه د هغوى اولاد راواخیست او دوى يې په خپلو ځانونو باندې ګواه جوړ كړل، (او ورته ويې ویل:) ایا زه ستاسو رب نه یم؟ دوى وویل: ولې نه! مونږ (د دې) ګواهي كوو، (دا د دې لپاره) چې تاسو د قیامت په ورځ و (نه) وایئ چې بېشكه مونږ له دې نه غافله وو');
INSERT INTO "abulsalam" VALUES(7,173,'7:173',' یا دا و(نه) وایئ چې بېشكه خبره دا ده چې شرك زمونږ پلرونو كړى و مخكې، او مونږ له هغوى نه وروسته (د هغوى) اولاد وو، ایا ته به مونږ په هغه جرم سره هلاكوې چې باطل پرستو ترسره كړى دى؟');
INSERT INTO "abulsalam" VALUES(7,174,'7:174',' او همدارنګه مونږ په تفصیل سره ایتونه بیانوو او د دې لپاره چې دوى (له تقلیده) راوګرځي');
INSERT INTO "abulsalam" VALUES(7,175,'7:175',' او ته دوى ته د هغه كس خبر ولوله چې مونږ هغه ته خپل ایتونه وركړي وو، نو دى له هغو نه ووتلو، نو شیطان په هغه پسې شو، نو هغه له ګمراهانو څخه شو');
INSERT INTO "abulsalam" VALUES(7,176,'7:176',' او كه چېرې مونږ غوښتلى (، نو) خامخا به مو هغه په دغو (ایتونو) سره پورته كړى و او لېكن هغه ځمكې ته پوخ مايله شو (هغه ځمكې ته مینه وكړه) او د خپل خواهش پیروي يې شروع كړه، نو د هغه مثال د سپي په شان دى، كه ته په هغه حمله وكړې، (نو هغه) ژبه راوباسي او تیګاه وهي او كه ته يې پرېږدې (بیا هم) ژبه راوباسي او تیګاه وهي، دا د هغو خلقو مثال دى چې زمونږ د ایتونو تكذیب يې كړى دى، نو ته (دا) قصې بیان كړه، د دې لپاره چې دوى ښه فكر وكړي');
INSERT INTO "abulsalam" VALUES(7,177,'7:177',' د مثال له مخې هغه قوم بد دى چې زمونږ د ایتونو تكذیب يې كړى دى او دوى به خاص پر خپلو ځانونو ظلم كاوه');
INSERT INTO "abulsalam" VALUES(7,178,'7:178',' چا ته چې الله هدایت په نصیب كړي، نو همغه هدایت موندونكى دى او هغه څوك چې الله يې ګمراه كړي، نو همدغه خلق تاوانیان دي');
INSERT INTO "abulsalam" VALUES(7,179,'7:179',' او یقینًا یقینًا مونږ د جهنم لپاره ډېر زیات داسې پېریان او انسانان پیدا كړي دي چې د هغوى زړونه شته (خو) په هغو سره هغوى نه پوهېږي او د هغوى سترګې شته (خو) لیدل پرې نه كوي او د هغوى غوږونه شته (خو) اورېدل پرې نه كوي، دغه خلق د څارویو په شان دي، بلكې دوى (له دغو نه هم) زیات ګمراه دي، همدغه خلق غافله دي');
INSERT INTO "abulsalam" VALUES(7,180,'7:180',' او خاص د الله لپاره تر ټولو ښكلي نومونه دي، نو تاسو الله په دغو (نومونو) سره بلئ او هغه كسان پرېږدئ چې د هغهٔ په نومونو كې الحاد (كوږوالى) كوي، ژر به هغوى ته د هغو عملونو بدله (سزا) وركړى شي چې هغوى به كول');
INSERT INTO "abulsalam" VALUES(7,181,'7:181',' او په هغو كسانو كې چې مونږ پیدا كړي دي؟ یوه ډله داسې هم شته چې د حق مطابق نېغه لار ښیي او په دې (حق) سره انصاف كوي');
INSERT INTO "abulsalam" VALUES(7,182,'7:182',' او هغه كسان چې زمونږ د ایتونو يې تكذیب كړى دى؛ ژر به مونږ دوى له داسې لوري په تدریج سره راونیسو چې دوى به نه پوهېږي');
INSERT INTO "abulsalam" VALUES(7,183,'7:183',' او زه دوى ته مهلت وركوم، بېشكه زما تدبیر (چل) ډېر مضبوط دى');
INSERT INTO "abulsalam" VALUES(7,184,'7:184',' ایا دوى هېڅ فكر نه دى كړى، چې د دوى په ملګري پورې هېڅ لیونتوب نشته، نه دى هغه مګر یو ښكاره وېروونكى');
INSERT INTO "abulsalam" VALUES(7,185,'7:185',' ایا دوى د اسمانونو او ځمكې په سلطنت (او انتظام) كې غور نه دى كړى او په هغو شيانو كې چې الله پیدا كړي دي او په دې كې چې كېداى شي چې د دوى اجل ډېر نژدې شوى وي، نو له دې (قرآن) نه وروسته به دوى پر كومه خبره ایمان راوړي؟');
INSERT INTO "abulsalam" VALUES(7,186,'7:186',' هغه څوك چې الله يې ګمراه كړي، نو د هغهٔ لپاره هېڅ هدایت وركوونكى نشته او الله دوى په خپلې سركشۍ كې پرېږدي، په دې حال كې چې سرګردان وي');
INSERT INTO "abulsalam" VALUES(7,187,'7:187',' دوى له تا نه د قیامت په باره كې پوښتنه كوي چې د دې (قیامت) پېښېدل كله دي؟ ته ووایه: بېشكه خبره همدا ده چې د هغهٔ علم زما له رب سره دى، پر خپل وخت به يې يواځې هغه ظاهروي، دغه (پېښه) په اسمانونو او ځمكه كې درنه ده، پر تاسو به نه راځي مګر ناڅاپه، دوى له تا نه (په داسې انداز كې ) تپوس كوي ګویا كې ته د ده په باره كې پوره تحقیق ته رسېدلى يې، ته ووایه: بېشكه خبره همدا ده چې د ده علم له الله سره دى او لېكن زیاتره خلق نه پوهېږي');
INSERT INTO "abulsalam" VALUES(7,188,'7:188',' ته ووایه: زه د خپل ځان لپاره نه د نفعې مالك یم او نه د نقصان مګر هغه چې الله يې وغواړي او كه چېرې زه پر غیبو عالم وَى (، نو) ما به خامخا ډېر خیر حاصل كړى و او ما ته به هېڅ تكلیف نه و رارسېدلى، زه نه یم مګر وېروونكى او زېرى وركوونكى د هغه قوم لپاره چې ایمان راوړي');
INSERT INTO "abulsalam" VALUES(7,189,'7:189',' دى همغه ذات دى چې تاسو يې له یو ځان نه پیدا كړي یئ او له هغه نه يې د هغه جوړه پیدا كړه، د دې لپاره چې د هغې ترڅنګ ارام او سكون حاصل كړي، بیا كله چې سړي (خپله جوړه) پټه كړه (جماع يې ورسره وكړه، نو) هغه حامله شوه په سپك حمل سره، نو هغه به يې ګرځاوه، نو كله چې درنه شوه، دواړو له خپل پالونكي الله نه دعا وغوښتله، (چې) كه چېرې تا مونږ ته روغ بچـى راكړ (، نو) مونږ به خامخا ضرور له شكر كوونكو څخه شو');
INSERT INTO "abulsalam" VALUES(7,190,'7:190',' نو كله چې الله دوى ته روغ بچى وركړ، دوى له الله سره په هغه څه كې شریكان جوړ كړل چې دوى ته يې وركړى و، نو الله ډېر اوچت دى له هغه څه نه چې دوى يې (ورسره) شريكوي');
INSERT INTO "abulsalam" VALUES(7,191,'7:191',' ایا دوى (له الله سره) هغه شیان شريكوي چې هېڅ شى نشي پیدا كولى، حال دا چې دوى پخپله پیدا كاوه شي ');
INSERT INTO "abulsalam" VALUES(7,192,'7:192',' او نه له دوى سره د څه مدد كولو طاقت لري او نه هم له خپلو ځانونو سره مدد كولى شي');
INSERT INTO "abulsalam" VALUES(7,193,'7:193',' او كه چېرې تهٔ دوى هدایت ته راوبلې، دوى به ستاسو پیروي ونه كړي، ستاسو لپاره يو شان ده كه تاسو دوى راوبلئ، یا تاسو خاموشه واوسئ');
INSERT INTO "abulsalam" VALUES(7,194,'7:194',' بېشكه هغه خلق چې تاسو يې له الله نه غیر بلئ، (هغوى) ستاسو په شان بنده ګان دي، نو هغوى راوبلئ، پس هغوى دې ستاسو بلنه قبوله كړي، كه چېرې تاسو رښتیني یئ');
INSERT INTO "abulsalam" VALUES(7,195,'7:195',' ایا د هغوى لپاره پښې دي چې په هغو سره تلل كوي، یا د هغوى لپاره لاسونه دي چې په هغو سره نیول كوي، یا د هغوى لپاره سترګې دي چې په هغو سره لیدل كوي، یا د هغوى لپاره غوږونه دي چې په هغو سره اورېدل كوي؟ ته ووایه: تاسو خپل شریكان راوبلئ، بیا زما په خلاف څه چل (او تدبير) وكړئ، بيا ما ته (هېڅ) مهلت مه راكوئ');
INSERT INTO "abulsalam" VALUES(7,196,'7:196',' بېشكه زما مددګار هغه الله دى چې كتاب يې نازل كړى دى او هغه د نېكانو مدد او ملګرتيا كوي');
INSERT INTO "abulsalam" VALUES(7,197,'7:197',' او هغه خلق چې تاسو يې له الله نه غیر بلئ، دوى ستاسو د مدد كولو هېڅ وَس نه لري او نه له خپلو ځانونو سره مدد كولى شي');
INSERT INTO "abulsalam" VALUES(7,198,'7:198',' او كه چېرې ته دوى هدایت ته راوبلې (، نو) دوى (ستا بلنه) نه اوري (نه يې قبلوي) او ته به دوى وینې چې (ګويا كې) تا ته ګوري، په داسې حال كې چې دوى لیدل نه كوي');
INSERT INTO "abulsalam" VALUES(7,199,'7:199',' ته معاف كول راونیسه (د عفوه كولو طريقه راخپله كړه) او د نېكۍ حكم كوه او له جاهلانو نه مخ وګرځوه');
INSERT INTO "abulsalam" VALUES(7,200,'7:200',' او كه چېرې د شیطان له طرفه كومه وسوسه تا (په ګناه كولو) ورتېز كړي، نو په الله پورې پناه ونيسه، بېشكه هغه ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(7,201,'7:201',' بېشكه هغه كسان چې پرهېزګاره شوي دي؛ كله چې هغوى ته د شیطان له طرف نه څه وسوسه ورسېږي (، نو) دوى (د الله حكم) رایاد كړي، بيا نو ناڅاپه دوى لېدونكي (او بينا) وي');
INSERT INTO "abulsalam" VALUES(7,202,'7:202',' او د دوى وروڼه دوى لره ګمراهۍ ته راكاږي، بیا نو هغوى هېڅ كمى نه كوي');
INSERT INTO "abulsalam" VALUES(7,203,'7:203',' او كله چې ته دوى ته څه نښه رانه وړې، دوى وايي: ولې دې له خپله ځانه غوره نه كړه، ته ووایه: بېشكه خبره همدا ده چې زه د هغه څه پیروي كوم چې ما ته د خپل رب له جانبه وحي كېږي، دا ستاسو د رب له جانبه د پوهنې دلايل دي او هدایت او رحمت دى د هغه قوم لپاره چې ایمان لري');
INSERT INTO "abulsalam" VALUES(7,204,'7:204',' او كله چې قرآن لوستل كېږي، نو تاسو هغه په غور سره اورئ او خاموشه اوسئ، د دې لپاره چې پر تاسو رحم وكړى شي');
INSERT INTO "abulsalam" VALUES(7,205,'7:205',' او ته خپل رب سبا او بېګاه په خپل زړه كې یادوه په عاجزۍ او وېرې سره او له اوچت اواز نه ټیټ د خبرو (په انداز) كې او له غافلانو څخه مه كېږه');
INSERT INTO "abulsalam" VALUES(7,206,'7:206',' بېشكه هغه مخلوق چې خپل رب ته نږدې دي، هغوى د هغه له عبادت نه تكبر نه كوي او د هغه پاكي بیانوي او خاص هغهٔ ته سجده كوي');
INSERT INTO "abulsalam" VALUES(8,1,'8:1',' دوى له تا نه د غنیمتونو په باره كې تپوس كوي، ته ووایه: غنیمتونه خاص د الله او رسول لپاره دي، نو تاسو له الله نه ووېرېږئ او تاسو د خپل مینځي حال اصلاح وكړئ او د الله او د هغه د رسول حكم ومنئ كه چېرې تاسو مومنان یئ');
INSERT INTO "abulsalam" VALUES(8,2,'8:2',' بېشكه خبره همدا ده چې مومنان هغه خلق دي، چې كله الله یاد كړى شي، نو د دوى زړونه ووېرېږي او كله چې دوى ته د هغه (الله) ایتونه تلاوت كړى شي (، نو) دغه (ایتونه) دوى ته ایمان ورزیات كړي او خاص په خپل رب باندې دوى توكل كوي');
INSERT INTO "abulsalam" VALUES(8,3,'8:3',' هغه كسان چې لمونځ قايموي او له هغه (مال) نه چې مونږ دوى ته وركړى دى، څه حصه خرچ كوي');
INSERT INTO "abulsalam" VALUES(8,4,'8:4',' همدغه خلق په حقه مومنان دي، د دوى لپاره د خپل رب په نېز درجې دي او بخښنه او عزتمن رزق دى');
INSERT INTO "abulsalam" VALUES(8,5,'8:5',' (د غنيمت په باره كې هم هغه شان خبره پېښه شوه) لكه څنګه (چې مخكې پېښه شوې وه كله) چې ته خپل رب له خپله كوره په حقه سره راوېستلى وې، او بېشكه یوه ډله له مومنانو څخه خامخا بد ګڼونكې وه (هغوى په دې كار ناخوښه وو)');
INSERT INTO "abulsalam" VALUES(8,6,'8:6',' دوى له تا سره په حقه خبره كې جګړه كوله وروسته له هغې چې ښه واضحه شوه، د هغوى حال داسې و ګویاكې دوى مرګ ته بېول كېږي، چې دوى يې ویني');
INSERT INTO "abulsalam" VALUES(8,7,'8:7',' او كله چې الله له تاسو سره په دوه ډلو كې د یوې وعده كوله چې بېشكه دغه تاسو لپاره ده او تاسو خوښوله چې بېشكه دغه بې وسلې ډله دې تاسو لپاره شي او الله اراده كوي چې په خپلو كلماتو سره حق ثابت كړي او د كافرانو بېخ غوڅ كړي');
INSERT INTO "abulsalam" VALUES(8,8,'8:8',' د دې لپاره چې حق ښه واضحه ثابت كړي او باطل ښه ښكاره باطل كړي (د باطل باطلوالى ښكاره كړي) اګر چې مجرمان (دا كار) بد هم وګڼي');
INSERT INTO "abulsalam" VALUES(8,9,'8:9',' كله چې تاسو له خپل رب نه مدد غوښتلو، نو هغه ستاسو دعا قبوله كړه چې بېشكه زه ستاسو مدد كوونكى یم په زرو ملايكو سره، چې په یو بل پسې به راتلونكي وي');
INSERT INTO "abulsalam" VALUES(8,10,'8:10',' او الله دا (مرسته) نه وه ګرځولې مګر زېرى او د دې لپاره چې په دې سره ستاسو زړونه مطمئن شي او مدد يواځې د الله له جانبه دى، بېشكه الله ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(8,11,'8:11',' كله چې هغه (الله) پر تاسو باندې له خپل طرف نه پركالي اچوله د امن( او اطمينان) لپاره، او پر تاسو باندې يې له بره نه اوبه نازلولې، د دې لپاره چې تاسو په دې سره ښه پاك كړي او له تاسو نه د شیطان پلیتي لرې كړي او د دې لپاره چې ستاسو زړونه مضبوط كړي او په دې سره (ستاسو) قدمونه كلك كړي');
INSERT INTO "abulsalam" VALUES(8,12,'8:12',' كله چې ستا رب ملايكو ته وحي كوله چې بېشكه زه له تاسو سره یم، نو تاسو هغه كسان چې ایمان يې راوړى دى ثابت قدم كړئ، ژر به زه د هغو كسانو په زړونو كې رعب واچوم چې كافران شوي دي، نو تاسو (د هغوى) د څټونو له پاسه وهل وكړئ، او تاسو د دوى پر (هر بند او) ټولو ګوتو باندې ګوزارونه وكړئ ');
INSERT INTO "abulsalam" VALUES(8,13,'8:13',' دا ځكه چې بېشكه دوى د الله او د هغه د رسول مخالفت كړى دى او څوك چې د الله او د هغه د رسول مخالفت كوي، نو یقینًا الله ډېر سخت عذاب والا دى');
INSERT INTO "abulsalam" VALUES(8,14,'8:14',' دا (دنيايي سزا ده) نو تاسو دا وڅَكئ او دا چې بېشكه د كافرانو لپاره د اور عذاب دى');
INSERT INTO "abulsalam" VALUES(8,15,'8:15',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې تاسو له كافرانو سره مخامخ شئ، چې تاسو (ټول) يو لښكر وئ، نو تاسو هغوى ته (خپلې) شاګانې مه وراړوئ');
INSERT INTO "abulsalam" VALUES(8,16,'8:16',' او هغه څوك چې دوى ته په دغه ورځ كې خپله شا ورواړوي، سوا له هغه نه چې د جنګ لپاره بېرته ورګرځېدونكى وي، یا له (خپلې) ډلې سره یو ځاى كېدونكى وي، نو یقینًا هغه د الله له جانبه په غضب سره راوګرځېده او د هغه استوګنه جهنم دى او هغه د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(8,17,'8:17',' نو تاسو هغوى نه دي وژلي او لېكن الله هغوى وژلي دي او تا ویشتل نه دي كړي كله چې تا ویشتل وكړل او لېكن الله (دغه) ویشتل كړي دي او د دې لپاره چې هغه له خپل جانب نه په مومنانو باندې احسان وكړي، ښه احسان، بېشكه الله ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(8,18,'8:18',' دا (احسان لازم و) او دا چې بېشكه الله د كافرانو چلونو (او سازشونو) لره ضعيفه كوونكى دى ');
INSERT INTO "abulsalam" VALUES(8,19,'8:19',' كه چېرې تاسو فتح غواړئ، نو یقینًا تاسو ته فتح راغله، او كه چېرې تاسو (له كفر نه) منع شئ، نو دا تاسو لپاره ډېر غوره دي او كه چېرې تاسو راوګرځېدئ (جنګ/كفر ته) مونږ به (هم د مومنانو مدد ته) راوګرځو او تاسو ته به ستاسو ډله د هېڅ شي فايده درونه رسولى شي اګر كه ډېره لویه هم وي او دا چې بېشكه الله د مومنانو ملګرى دى');
INSERT INTO "abulsalam" VALUES(8,20,'8:20',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د الله او د هغه د رسول حكم منئ او له دې (اطاعت) نه مه ګرځئ، حال دا چې تاسو يې اورئ');
INSERT INTO "abulsalam" VALUES(8,21,'8:21',' او تاسو د هغو خلقو په شان مه كېږئ چې ویل يې: مونږ واورېدل، حال دا چې دوى به نه اورېدل (فايده من اورېدل)');
INSERT INTO "abulsalam" VALUES(8,22,'8:22',' بېشكه د الله په نیز په حیواناتو كې تر ټولو بدتر هغه كاڼه او ګونګیان خلق دي، چې له عقل نه كار نه اخلي');
INSERT INTO "abulsalam" VALUES(8,23,'8:23',' او كه چېرې الله ته په دوى كې څه خیر (اِنابت او د حق د اورېدلو صلاحيت) معلوم وَى (، نو) خامخا به يې دوى ته اورولي وَى او كه چېرې دوى ته يې (له انابته بغير) اورولي وَى، نو دوى به خامخا ګرځېدلي وو، په دې حال كې چې دوي به اعراض كوونكي وو');
INSERT INTO "abulsalam" VALUES(8,24,'8:24',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د الله او د هغه د رسول بلنه منئ كله چې دى تاسو د هغه كار لپاره راوبلي چې تاسو ته ژوند دركوي، او پوه شئ چې بېشكه الله د انسان او د هغه د زړه تر مینځ حايل كېږي (ركاوټ جوړېږي) او دا چې بېشكه خاص د هغه په طرف به تاسو ورجمع كولى شئ');
INSERT INTO "abulsalam" VALUES(8,25,'8:25',' او تاسو له هغې فتنې نه ځان وساتئ چې په تاسو كې به يواځې خاص ظالمانو ته نه رسېږي او پوه شئ چې بېشكه الله ډېر سخت عذاب وا لا دى');
INSERT INTO "abulsalam" VALUES(8,26,'8:26',' او تاسو یاد كړئ (هغه وخت) كله چې تاسو ډېر لږ وئ، په ځمكه كې ضعيفه كړى شوي وئ، چې تاسو به وېرېدلئ له دې نه چې خلق به مو په جلتۍ سره وتښتوي، نو هغه تاسو ته ځاى دركړ او په خپل مدد سره يې تاسو مضبوط كړئ او تاسو ته يې له پاكيزه څیزونو نه رزق دركړ، د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(8,27,'8:27',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو له الله سره او له رسول سره خیانت مه كوئ او په خپلو امانتونو كې (هم) خیانت مه كوئ، په دې حال كې چې تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(8,28,'8:28',' او تاسو پوه شئ چې بېشكه خبره همدا ده چې ستاسو مالونه او ستاسو اولادونه ازمايش دي او بېشكه الله (چې دى) له هغه سره ډېر لوى اجر دى');
INSERT INTO "abulsalam" VALUES(8,29,'8:29',' اى هغو كسانو چې ایمان يې راوړى دى! كه چېرې تاسو له الله نه ووېرېږئ، هغه به تاسو لپاره فرقان (د حق او باطل په مینځ كې فرق كوونكى) مقرر كړي او له تاسو نه به ستاسو ګناهونه ختم كړي او تاسو ته به بخښنه وكړي او الله د ډېر لوى فضل والا دى');
INSERT INTO "abulsalam" VALUES(8,30,'8:30',' او كله چې له تا سره كافرانو مكر (فریب) كاوه، د دې لپاره چې تا بندي كړي، یا تا قتل كړي، یا تا وباسي، او هغوى چلونه كول او الله (هم) پټ تدبیر كاوه او الله تر ټولو بهتر تدبیر كوونكى دى');
INSERT INTO "abulsalam" VALUES(8,31,'8:31',' او كله چې دوى ته زمونږ ایتونه تلاوت كولى شي، دوى وايي: يقيناً مونږ واورېدل، كه چېرې مونږ وغواړو خامخا مونږ به هم د دې (قرآن) په مثل ووایو، نه دي دا مګر د ړومبنو خلقو باطل داستانونه ');
INSERT INTO "abulsalam" VALUES(8,32,'8:32',' او كله چې دوى وویل: اى الله! كه چېرې همدا، ستا له جانبه حق دى، نو ته پر مونږ باندې له اسمان نه كاڼي راووروه، یا مونږ ته ډېر دردوونكى عذاب راولېږه');
INSERT INTO "abulsalam" VALUES(8,33,'8:33',' او الله داسې نه دى چې دوى ته عذاب وركړي، په دې حال كې چې ته په دوى كې يې او الله دوى ته عذاب وركوونكى نه دى، په داسې حال كې چې دوى بخښنه (استغفار) غواړي');
INSERT INTO "abulsalam" VALUES(8,34,'8:34',' او دوى ته څه (امتیاز حاصل) دى چې الله به دوى ته عذاب نه وركوي، حال دا چې دوى له مسجد حرام نه (خلق) منع كوي، په دې حال كې چې دوى د دې (مسجد) جايز واكداران هم نه دي، نه دي واكداران د دې (مسجد) مګر پرهېزګاره خلق او لېكن د دغو خلقو زیاتره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(8,35,'8:35',' او د بیت الله په خوا كې د دوى لمونځ نه و مګر شپیلي وهل او لاسونه ټكول (پړقول)، نو تاسو عذاب وڅَكئ په سبب د هغه كفر چې تاسو به كاوه');
INSERT INTO "abulsalam" VALUES(8,36,'8:36',' بېشكه هغه كسان چې كفر يې كړى دى، دوى خپل مالونه د دې لپاره خرچ كوي چې د الله له لارې نه (خلق) منع كړي، نو ژر به دوى دا (مالونه) ولګوي، بیا به دغه په دوى باندې (د) حسرت (او پښېمانتیا باعث) وي، بیا به دوى مغلوب كړى شي او هغه كسان چې كفر يې كړى دى، هغوى به جهنم ته ورجمع كولى شي');
INSERT INTO "abulsalam" VALUES(8,37,'8:37',' د دې لپاره چې الله خبیث له پاك نه جدا كړي او (بیا) خبیث كېږدي ځینې يې د ځینو له پاسه، بیا يې یو د بل له پاسه ټول راجمع كړي، بیا به يې جهنم ته وغورځوي، همدغه خلق تاوانیان دي');
INSERT INTO "abulsalam" VALUES(8,38,'8:38',' ته هغو كسانو ته ووایه چې كفر يې كړى دى: كه چېرې دوى منع شي (، نو) دوى ته به د هغو (ګناهونو) بخښنه وشي چې مخكې ترې شوي دي او كه چېرې دوى (كفر ته بيا) راوګرځي، نو تحقیق د ړومبنو خلقو طریقه (قانون) تېره شوې ده. (له هغوى سره شوى عمل له دوى سره هم كېدونكى دى) ');
INSERT INTO "abulsalam" VALUES(8,39,'8:39',' او تاسو له دوى سره وجنګېږئ تر هغه پورې چې فتنه (له دين نه اړول) پاتې نشي او دین ټول خاص د الله لپاره شي، نو كه چېرې دوى (له كفر او جنګ نه) منع شي، نو بېشكه الله د هغو عملونو چې دوى يې كوي ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(8,40,'8:40',' او كه چېرې دوى (له ايمان نه) وګرځي، نو تاسو پوه شئ چې بېشكه الله ستاسو مولٰی (ساتونكى) دى، هغه ښه مولٰی دى او ښه مدد كوونكى دى');
INSERT INTO "abulsalam" VALUES(8,41,'8:41',' او تاسو پوه شئ چې بېشكه هر هغه شى چې تاسو يې په غنیمت كې حاصل كړئ، نو بېشكه د الله لپاره د هغه (غنیمت) پنځمه برخه ده او د رسول لپاره او د خپلوانو او یتیمانو او مسكینانو او مسافرو لپاره، كه چېرې یئ تاسو چې پر الله ایمان لرئ او پر هغه شي چې مونږ پر خپل بنده باندې د فیصلې په ورځ نازل كړ، هغه ورځ چې دوه ډلې یو تربله (د جنګ لپاره) مخامخ شوې او الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(8,42,'8:42',' كله چې تاسو په ډېرې نژدې غاړه كې وئ او هغوى په ډېرې لرې غاړه كې وو او قافله له تاسو نه لاندې وه او كه چېرې تاسو یو له بل سره وعده كړې وَى، نو تاسو به خامخا په وعده كې مختلف شوي وَى او لېكن د دې لپاره چې الله هغه كار پوره كړي چې مقرر شوى و، د دې لپاره چې هلاكېدونكى د روښانه دلیل (له قايمېدو) نه (پس) هلاك شي، او ژوندى پاتې كېدونكى د روښانه دلیل (له قايمېدو) نه (پس) ژوندى واوسي او بېشكه الله یقینًا ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(8,43,'8:43',' كله چې الله تا ته ستا په خوب كې هغوى ډېر لږ ښودل او كه چېرې تا ته يې هغوى ډېر زیات ښودلي وَى، (نو) تاسو به خامخا بې زړه شوي وى او تاسو به په خپل مینځ كې په دې كار كې خامخا اختلاف او جګړه كړې وَى او لېكن الله (ستاسو) ساتنه وكړه، بېشكه هغه د سینو پر خبرو ښه عالم دى');
INSERT INTO "abulsalam" VALUES(8,44,'8:44',' او كله چې يې تاسو ته هغوى، له یو بل سره د مخامخ كېدلو په وخت كې، ستاسو په سترګو كې ډېر لږ ښودل او تاسو يې د هغوى په سترګو كې لږ ښودلئ، د دې لپاره چې الله هغه كار پوره كړي چې مقرر شوى و او خاص د الله په طرف ټول كارونه بېرته ګرځولى شي');
INSERT INTO "abulsalam" VALUES(8,45,'8:45',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې تاسو د كومې (كافرې) ډلې سره مخامخ شئ، نو ثابت قدم اوسئ او الله ډېر زیات یادوئ، د دې لپاره چې تاسو كامیاب شئ');
INSERT INTO "abulsalam" VALUES(8,46,'8:46',' او تاسو د الله او د هغه د رسول اطاعت كوئ او په خپل مینځ كې جګړه (او اختلاف) مه كوئ (ګنې) نو بې زړه به شئ او ستاسو زور به لاړ شي او صبر كوئ، بېشكه الله د صبر كوونكو ملګرى دى');
INSERT INTO "abulsalam" VALUES(8,47,'8:47',' او تاسو د هغه خلقو په شان مه كېږئ چې له خپلو كورونو نه ووتل، په دې حال كې چې تكبر كوونكي او خلقو ته ځان ښودونكي وو او دوى د الله له لارې نه (خلق) منع كوي او الله د هغو كارونو احاطه كوونكى دى چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(8,48,'8:48',' او (یاد كړئ) هغه وخت چې شیطان دوى ته د دوى عملونه ښايسته كړل او ويې ويل:نن ورځ پر تاسو باندې له خلقو څخه هیڅوك غالب راتلونكى نشته او بېشكه زه ستاسو ساتونكى یم، نو كله چې دواړه ډلې یو تربله مخامخ شوې، بېرته په منډه په شا لاړ او ويې ويل:بېشكه زه له تاسو بېزاره یم، بېشكه زه هغه څه وینم چې تاسو يې نه وینئ، بېشكه زه له الله څخه وېرېږم او الله ډېر سخت عذاب والا دى');
INSERT INTO "abulsalam" VALUES(8,49,'8:49',' كله چې منافقانو ویل او هغو كسانو چې د هغوى په زړونو كې مرض و: دا خلق خپل دین دوكه كړي دي او څوك چې په الله باندې توكل وكړي، نو یقینًا الله ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(8,50,'8:50',' او كه چېرې ته ووینې هغه وخت چې ملايك د كافر شويو كسانو ساګانې قبض كوي، چې د هغوى مخونه او د هغوى شاګانې وهي او (ورته وايي:) سخت سوزونكى عذاب وڅَكئ');
INSERT INTO "abulsalam" VALUES(8,51,'8:51',' دا په سبب د هغو (بدو عملونو) چې ستاسو لاسونو مخكې لېږلي دي او په دې چې یقینًا الله پر بنده ګانو هېڅ ظلم كوونكى نه دى');
INSERT INTO "abulsalam" VALUES(8,52,'8:52',' (د دې كافرانو حال هم هغه شان دى) لكه حال د فرعونیانو او د هغو كسانو چې له دوى نه مخكې وو، د الله په ایتونو كافر شول، نو الله هغوى د خپلو ګناهونو په سبب ونیول، بېشكه الله قوي، (او) ډېر سخت عذاب والا دى');
INSERT INTO "abulsalam" VALUES(8,53,'8:53',' دا په دې چې یقینًا الله د هغه نعمت بدلوونكى نه دى چې پر كوم قوم يې پېرزو كړى وي تر هغه چې هغوى هغه (حالت) بدل كړي چې د هغوى په نفسونو كې دى او دا په دې چې یقینًا الله ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(8,54,'8:54',' لكه حال د فرعونیانو او د هغو كسانو چې له دوى نه مخكې وو، د خپل رب د ایتونو تكذیب يې وكړ، نو مونږ هغوى د خپلو ګناهونو په وجه هلاك كړل او فرعونیان مو غرق كړل او هغوى ټول ظالمان وو');
INSERT INTO "abulsalam" VALUES(8,55,'8:55',' بېشكه د الله په نیز په ځمكه باندې په ګرځېدونكو كې تر ټولو بدتر هغه كسان دي چې كفر يې كړى دى، نو هغوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(8,56,'8:56',' هغه كسان چې تا له هغوى سره مُعَاهَده كړې وه، بیا هر ځله دوى خپل عهد (تړون) ماتوي او دوى (له الله نه) نه وېرېږي');
INSERT INTO "abulsalam" VALUES(8,57,'8:57',' نو كه كله هم ته دوى په جنګ كې ومومې، نو دوى ته په سزا وركولو سره هغه كسان تیت او پرك كړه چې د دوى وروسته دي، لپاره د دې چې دغه (وروستني خلق) نصیحت ومومي');
INSERT INTO "abulsalam" VALUES(8,58,'8:58',' او كه چېرې كله هم ته له كوم قوم نه د خیانت (وعده خلافۍ) اندېښنه ولرې، نو ته دوى ته په برابرۍ سره (دغه عهد) وغورځوه (تړون على الإعلان ختم كړه)، بېشكه الله خیانت كوونكي (وعده ماتوونكي) نه خوښوي ');
INSERT INTO "abulsalam" VALUES(8,59,'8:59',' او كافر شوي كسان دې كله هم (دا) ګمان ونه كړي چې دوى بچ شول، بېشكه دوى (الله لره) نشي عاجزه كولى');
INSERT INTO "abulsalam" VALUES(8,60,'8:60',' او تاسو قوت او تړلي اسونه تیار كړئ څومره چې استطاعت لرئ، چې په دې سره به تاسو د الله دښمنان او خپل دښمنان وېروئ او له دوى نه سوا نور خلق (هم) چې تاسو هغوى نه پېژنئ، الله هغوى پېژني او تاسو چې د الله په لاره كې څه شى هم خرچ كوئ، تاسو ته به پوره (بدله) دركړى شي او پر تاسو به ظلم نه كېږي');
INSERT INTO "abulsalam" VALUES(8,61,'8:61',' او كه دغه (كفار) سولې ته مايل شي، نو ته هم دې ته مايل شه او په الله باندې توكل وكړه، بېشكه هم هغه، ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(8,62,'8:62',' او كه دوى له تا سره د دوكې كولو اراده وكړي، نو بېشكه تا لپاره الله كافي دى، دى همغه ذات دى چې ته يې په خپل مدد سره او په مومنانو سره مضبوط كړى يې ');
INSERT INTO "abulsalam" VALUES(8,63,'8:63',' او د دوى د زړونو په مینځ كې يې محبت واچاوه، كه چېرې تا هغه ټول څه لګولي وى چې په ځمكه كې دي (، نو) تا به د دوى د زړونو په مینځ كې محبت نه و اچولى او لېكن الله د دوى په مینځ كې محبت واچاوه، بېشكه هغه ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(8,64,'8:64',' اى نبي! تا لپاره او ستا پیروي كوونكو مومنانو لپاره الله كافي دى');
INSERT INTO "abulsalam" VALUES(8,65,'8:65',' اى نبي! ته مومنان (له دغو كفارو سره) په جنګ كولو باندې تېز كړه، كه چېرې په تاسو كې شل تنه صبر كوونكي وي، نو پر دوه سوه كسانو به غالبه شي او كه چېرې په تاسو كې سل تنه صبر كوونكي وي (،نو) پر هغو زرو كسانو به غالبه شي چې كافر شوي دي، ځكه چې یقینًا هغوى داسې قوم دى چې نه پوهېږي');
INSERT INTO "abulsalam" VALUES(8,66,'8:66',' اوس الله پر تاسو اساني راوستله او معلومه (څرګنده) يې كړه چې بېشكه په تاسو كې كمزوري ده، نو كه چېرې په تاسو كې سل تنه صبر كوونكي وي، (،نو) پر دوه سوه به غالبه شي او كه په تاسو كې زر تنه (صبر كوونكي) وي (،نو) پر دوه زره به غالبه شي، د الله په حكم سره، او الله د صبر كوونكو ملګرى دى');
INSERT INTO "abulsalam" VALUES(8,67,'8:67',' د نبي لپاره لايق نه دي چې د هغه دې بندیان (قيديان) وي، تر هغه چې وینې توى كړي، تاسو د دنیا اسباب غواړئ او الله (تاسو لپاره) د اخرت (د ثواب) اراده لري او الله ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(8,68,'8:68',' كه د الله له جانبه مخكینى لیك نه وى (،نو) تاسو ته به د هغې (فدیې) په بدل كې چې تاسو واخيستله، خامخا ډېر لوى عذاب دررسېدلى و');
INSERT INTO "abulsalam" VALUES(8,69,'8:69',' نو تاسو خورئ له هغه (ماله) چې تاسو په غنیمت حاصل كړى دى، چې حلال پاكیزه دى، او له الله نه ووېرېږئ، بېشكه الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(8,70,'8:70',' اى نبي! ته هغو بندیانو (قيديانو) ته ووایه چې ستاسو په لاس كې دي: كه چېرې الله ستاسو په زړونو كې خیر (ایمان) معلوم كړي (،نو) تاسو ته به له هغه (مال) نه غوره دركړي چې له تاسو اخيستل شوى دى او تاسو ته به بخښنه وكړي او الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(8,71,'8:71',' او كه دوى له تا سره د خیانت كولو اراده لري، نو یقینًا دوى له دې نه مخكې له الله سره خیانت كړى دى، نو هغه پر دوى باندې (د مومنانو) قابو (او غلبه) راوستله، او الله ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(8,72,'8:72',' بېشكه هغه كسان چې ایمان يې راوړى دى او هجرت يې كړى دى او د الله په لاره كې يې په خپلو ځانونو سره جهاد كړى دى او هغه كسان چې ځاى يې وركړى دى (دوى ته) او مرسته يې ورسره كړې ده، دغه كسان یو تر بله دوستان دي او هغه كسان چې ایمان يې راوړى دى او هجرت يې نه دى كړى (دار الاسلام ته) (، نو) ستاسو له هغوى سره د دوستۍ هېڅ شى (تعلق) نشته تر هغه پورې چې دوى هجرت وكړي او كه چېرې دوى له تاسو نه په دین كې مرسته وغواړي، نو پر تاسو باندې مدد كول لازم دي، مګر د هغه (كافر) قوم په خلاف نه چې ستاسو او د هغوى په مینځ كې معاهده وي او الله هغو كارونو لره چې تاسو يې كوئ ؛ ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(8,73,'8:73',' او هغه كسان چې كفر يې كړى دى؛ دوى یو تر بله دوستان (حمايتيان) دي، كه تاسو دا (كارونه، يعنې له مومنانو سره دوستي او د دغو كفارو سركوبي) ونه كړئ (،نو) په ځمكه كې به لویه فتنه او ډېر لوى ورانى جوړ شي');
INSERT INTO "abulsalam" VALUES(8,74,'8:74',' او هغه كسان چې ایمان يې راوړى دى او هجرت يې كړى دى او د الله په لاره كې يې جهاد كړى دى او هغه كسان چې ځاى يې وركړى دى او مدد يې كړى دى، همدغه كسان په حقه مومنان دي، د دوى لپاره بخښنه او ډېر د عزت رزق دى');
INSERT INTO "abulsalam" VALUES(8,75,'8:75',' او هغه كسان چې له دې نه پس يې ایمان راوړ او هجرت يې وكړ او ستاسو په ملګرتیا كې يې جهاد وكړ، نو دغه كسان له هم ستاسو څخه دي او د خپلولۍ والا د الله په كتاب كې د یو بل زیات حقدار دي، بېشكه الله پر هر شي ښه پوه دى');
INSERT INTO "abulsalam" VALUES(9,1,'9:1',' (دا) د الله او د هغه د رسول له جانبه بیزاري او صاف ځواب دى، په مشركانو كې هغو كسانو ته چې تاسو ورسره عهد كړى و ');
INSERT INTO "abulsalam" VALUES(9,2,'9:2',' نو (اى مشركانو!) تاسو په دغه ځمكه كې څلور میاشتې (په امن سره) ګرځئ او پوه شئ چې یقینًا تاسو الله لره عاجزه كوونكي نه یئ او دا چې یقینًا الله كافرانو لره رسوا كوونكى دى');
INSERT INTO "abulsalam" VALUES(9,3,'9:3',' او د حج اكبر په ورځ د الله او د هغه د رسول له جانبه ټولو خلقو ته (دا) اعلان (او خبرتیا) دى چې بېشكه الله او د هغه رسول له مشركانو نه بیزاره دى، نو كه تاسو توبه وباسئ، نو دا تاسو لپاره ډېر غوره دي او كه تاسو مخ وګرځوئ، نو پوه شئ چې یقینًا تاسو الله لره عاجزه كوونكي نه یئ او هغو كسانو ته چې كافران شوي دي؛ د ډېر دردوونكي عذاب زېرى وركړه');
INSERT INTO "abulsalam" VALUES(9,4,'9:4',' مګر په مشركانو كې هغه كسان چې تاسو ورسره عهد كړى دى، بیا هغوى تاسو ته هېڅ شى نقصان نه دى دررسولى او نه يې ستاسو په مقابل كې د هیچا مدد كړى دى، نو هغوى ته خپل عهد د هغوى د مودې پورې پوره كړئ، بېشكه الله (له بدعهدۍ نه) ځان ساتونكي خوښوي');
INSERT INTO "abulsalam" VALUES(9,5,'9:5',' نو كله چې حرامې میاشتې تېرې شي، بیا نو تاسو دغه مشركان وژنئ چېرته چې تاسو هغوى ومومئ او دوى نیسئ او د دوى محاصَره كوئ او د دوى لپاره په هر كمین ځاى كې كېنئ، نو كه دوى توبه وباسي او لمونځ قايم كړي او زكات ادا كړي، بیا نو تاسو د دوى لاره خوشې كړئ، بېشكه الله ډیر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(9,6,'9:6',' او كه په مشركانو كې یو تن له تا نه امان (پناه) وغواړي، نو هغه ته امان وركړه تر هغه پورې چې هغه د الله كلام واوري، بیا هغه خپل د امن ځاى (د هغهٔ وطن) ته ورسوه، دا په سبب د دې چې یقینًا دوى داسې قوم دى چې نه پوهېږي ');
INSERT INTO "abulsalam" VALUES(9,7,'9:7',' د الله په نیز او د هغه د رسول په نیز به د مشركانو لپاره څنګه عهد وي، غیر له هغو كسانو نه چې تاسو ورسره د مسجد حرام په خوا كې عهد كړى دى، نو تر څو پورې چې هغوى تاسو لپاره (پر عهد) پوره قايم وي، نو تاسو هم د هغوى لپاره (پر عهد) كلك قايم اوسئ، بېشكه الله (له بد عهدۍ نه) ځان ساتونكي خوښوي ');
INSERT INTO "abulsalam" VALUES(9,8,'9:8',' څنګه (كېدى شي د دوى لپاره عهد!) حال دا چې كه دوى پر تاسو باندې لاس بري شي (،نو بیا) دوى ستاسو په حق كې نه خپلولۍ ته ګوري او نه عهد ته، دوى تاسو په خپلو خولو سره راضي كوي او د دوى زړونه انكار كوي او د دوى اكثره فاسقان دي ');
INSERT INTO "abulsalam" VALUES(9,9,'9:9',' دوى د الله د ایتونو (د پرېښودلو) په بدل كې لږ قیمت اخيستى دى، نو منع شوي دي د هغهٔ له لارې نه، بېشكه دوى (چې دي) بد دى هغه عمل چې دوى به كاوه ');
INSERT INTO "abulsalam" VALUES(9,10,'9:10',' دوى د مومن په باره كې نه د خپلولۍ لحاظ كوي او نه د عهد، او همدغه كسان (له حد نه) تېرېدونكي دي ');
INSERT INTO "abulsalam" VALUES(9,11,'9:11',' نو كه دوى توبه وباسي او لمونځ قايم كړي او زكات ادا كړي، نو بیا ستاسو وروڼه دي په دین كې او مونږ په تفصیل سره بیانوو ایتونه د هغه قوم لپاره چې پوهېږي ');
INSERT INTO "abulsalam" VALUES(9,12,'9:12',' او كه دوى خپل قسمونه له خپل عهد نه پس مات كړل او ستاسو په دین كښی يې عیبونه راوېستل شروع كړل، بیا نو د كفر له امامانو (مشرانو) سره جنګ كوئ، بېشكه دوى (چې دي) د دوى لپاره هېڅ (د اعتبار وړ) قسمونه نشته، د دې لپاره چې دوى منع شي ');
INSERT INTO "abulsalam" VALUES(9,13,'9:13',' ولې تاسو له داسې قوم سره نه جنګېږئ چې هغوى خپل قسمونه مات كړي دي او د رسول د وېستلو اراده يې كړې ده، حال دا چې هم دوى پر تاسو شروع كړې ده اول ځلې! ایا تاسو له دوى نه وېرېږئ، نو الله تر ټولو زیات حقدار دى د دې چې له هغه نه ووېرېږئ، كه تاسو مومنان یئ ');
INSERT INTO "abulsalam" VALUES(9,14,'9:14',' له دوى سره وجنګیږئ، الله به دوى ته ستاسو په لاسونو عذاب وركړي او دوى به رسوا كړي او د دوى په مقابل كې به ستاسو مدد وكړي او د مومنو خلقو سینو ته به شفا (او يخ والى) وركړي');
INSERT INTO "abulsalam" VALUES(9,15,'9:15',' او د هغوى د زړونو غصه به لرې كړي او الله چې پر چا وغواړي په رحمت سره رجوع كوي او الله ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(9,16,'9:16',' ایا تاسو دا ګمان كړى دى چې تاسو به (همداسې) پرېښودى شئ، حال دا چې لا تر اوسه الله هغه كسان نه دي معلوم (ظاهر) كړي چې جهاد يې كړى دى له تاسو نه، او له الله او د هغه له رسول او مومنانو نه غیر يې بل هېڅ رازدار نه دى نیولى او الله ښه خبردار دى پر هغو عملونو چې تاسو يې كوئ ');
INSERT INTO "abulsalam" VALUES(9,17,'9:17',' د مشركانو لپاره دا حق نشته چې د الله جوماتونه اباد كړي، په داسي حال كې چې په خپلو ځانونو باندې د كفر ګواهي كوونكي دي، دغه خلق (چې دي) د دوى عملونه ضايع شوي دي او دوى به په اور كې تل ترتله وي ');
INSERT INTO "abulsalam" VALUES(9,18,'9:18',' د الله جوماتونه خو يواځې هغه څوك ابادوي چې پر الله او د اخرت پر ورځ يې ایمان راوړى وي او لمونځ قايموي او زكات وركوي او نه وېرېږي غیر له الله نه، نو امېد دى چې دغه كسان به له هدایت موندونكو ځنې وي ');
INSERT INTO "abulsalam" VALUES(9,19,'9:19',' ایا تاسو حاجیانو ته اوبه وركول او د مسجد حرام ابادول د هغه چا (د ایمان) په شان ګرځولي دي چې پر الله او پر ورځ د اخرت يې ایمان راوړى دى او د الله په لاره كې يې جهاد كړى دى، دوى د الله په نیز برابر نه دي او الله ظالم قوم ته هدایت نه كوي ');
INSERT INTO "abulsalam" VALUES(9,20,'9:20',' هغه كسان چې ایمان يې راوړى دى او هجرت يې كړى دى او د الله په لاره كې يې په خپلو مالونو او ځانونو سره جهاد كړى دى، د الله په نیز په درجه كې تر ټولو لوى دي، اوهمدغه كسان كامیاب دي ');
INSERT INTO "abulsalam" VALUES(9,21,'9:21',' دوى ته خپل رب له خپل جانب نه د رحمت او رضامندۍ او د داسی جنتونو زېرى وركوي چې د دوى لپاره به په هغو كې همېشني نعمتونه وي');
INSERT INTO "abulsalam" VALUES(9,22,'9:22',' په دې حال كې چې په هغو كې به تل ترتله همېشه وي، بېشكه الله (چې دى) له هغه سره ډیر لوى اجر دى ');
INSERT INTO "abulsalam" VALUES(9,23,'9:23',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو خپل پلرونه او خپل وروڼه دوستان مه نیسئ كه دوى كفر محبوب (او غوره) وګڼي په ایمان باندې، او په تاسو كې چې چا له دغو (كفارو) سره دوستي وكړه، نو همدغه كسان ظالمان دي ');
INSERT INTO "abulsalam" VALUES(9,24,'9:24',' (اى پېغمبره!) ته (دوى ته) ووایه: كه چېرې وي ستاسو پلرونه او ستاسو زامن او ستاسو وروڼه او ستاسو ښځې او ستاسو قبیله او هغه مالونه چې تاسو هغه ګټلي دي او هغه تجارت چې تاسو د هغه د بندېدو وېره كوئ او هغه استوګنې چې تاسو هغه خوښوئ، تاسو ته ډېر محبوب دي له الله او د هغه له رسول او د هغه په لاره كې له جهاد كولو نه، نو بیا انتظار كوئ، تر هغه پورې چې الله خپل حكم راولي او الله فاسقو خلقو ته هدایت نه كوي');
INSERT INTO "abulsalam" VALUES(9,25,'9:25',' یقینًا یقینًا الله په ډېرو ځایونو كې ستاسو مدد كړى دى او د حُنین په ورځ هم، كله چې خپل ډېر والي تاسو په تعجب (او غرور) كې اچولي وئ، بیا دا (ډېر والى) ستاسو هېڅ په كار رانغى او پر تاسو باندې ځمكه د هغې د ارتوالي باوجود تنګه شوه، بیا تاسو وګرځېدئ، په داسی حال كې چې شا كوونكي وئ');
INSERT INTO "abulsalam" VALUES(9,26,'9:26',' بیا الله خپل تسكین (او اطمينان) پر خپل رسول او په مومنانو باندې نازل كړ او داسې لښكرې يې نازلې كړې چې تاسو هغه نه وې لیدلې او كافر شويو خلقو ته يې عذاب وركړ او دا د كافرانو جزا ده');
INSERT INTO "abulsalam" VALUES(9,27,'9:27',' بیا به الله له دې نه پس (په رحمت سره) رجوع وكړي پر هغه چا چې يې وغواړي او الله ډېر بخښونكى، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(9,28,'9:28',' اى هغو كسانو چې ایمان يې راوړى دى! په رښتیا همدا خبره ده چې مشركان نجس دي، نو مسجد حرام ته دې له خپل دې كال نه وروسته نه نژدې كېږي او كه تاسو له كومې غریبۍ (او تنګۍ) نه وېرېږئ، نو ژر به الله تاسو له خپله فضله غنیان كړي، كه هغه وغواړي، بېشكه الله ښه پوه، ښه حكمت والا دى ');
INSERT INTO "abulsalam" VALUES(9,29,'9:29',' تاسو له هغو كسانو سره وجنګېږئ چې نه پر الله ایمان راوړي او نه د اخرت پر ورځ، او هغه څه ته حرام نه وايي چې الله او د هغه رسول حرام كړي دي او نه دینِ حق قبلوي، له هغو كسانو ځنې چې كتاب ورته وركړى شوى دى، تر دې چې دوى په خپل لاس په داسې حال كې جزیه وركړي، چې دوى ذلیل (محكوم) وي');
INSERT INTO "abulsalam" VALUES(9,30,'9:30',' او یهودیانو وویل: عُزیر د الله زوى دى او نصرانیانو وویل: مسیح د الله زوى دى، دا د دوى په خپلو خولو سره وینا ده، دوى د هغو كسانو له خبرې سره مشابهت كوي چې له دوى نه مخكې كافران وو، الله دې دوى هلاك كړي، څنګه (له سمې لارې نه) ګرځولى شي!');
INSERT INTO "abulsalam" VALUES(9,31,'9:31',' دوى خپل عالمان او خپل عابدان له الله نه غیر خدایان نیولي دي او د مریمې زوى مسیح (يې هم)، حال داچې دوى ته حكم نه و شوى مګر د دې چې دوى دې د یو حق معبود عبادت كوي چې نشته هېڅ حق معبود مګر همدى دى، هغه پاك دى له هغه څه نه چې دوى يې (ورسره) شریكوي');
INSERT INTO "abulsalam" VALUES(9,32,'9:32',' دوى غواړي چې د الله رڼا په (پوكو د) خپلو خولو سره مړه كړي او الله انكار كوي مګر دا چې خپله رڼا پوره كړي اګر كه كافران (دا كار) بد ګڼي');
INSERT INTO "abulsalam" VALUES(9,33,'9:33',' دى همغه ذات دى چې خپل رسول يې په هدایت او حق دین سره رالېږلى دى، د دې لپاره چې هغه (الله) دا (دین) په نورو ټولو دینونو باندې غالب كړي اګر كه مشركان (دا كار) بد ګڼي ');
INSERT INTO "abulsalam" VALUES(9,34,'9:34',' اى هغو كسانو چې ایمان يې راوړى دى! بېشكه ډېر عالمان (د یهودیانو) او عابدان (د نصرانیانو) د خلقو مالونه په يقيني ډول په باطلې طریقې سره خوري او د الله له لارې نه (خلق) منع كوي، او هغه كسان چې سره زر اوسپین زر خزانه كوي او هغه د الله په لاره كې نه لګوي، نو دوى ته د ډېر دردوونكي عذاب زېرى وركړه');
INSERT INTO "abulsalam" VALUES(9,35,'9:35',' په هغې ورځ چې د جهنم په اور كې به دغه (خزانې) ښې ګرمې كړى شي، بیا به په هغې سره د دوى تندي او د دوى اړخونه او د دوى شاګانې داغلى شي (او دوى ته به ویلى شي:) دا هغه څه دي چې تاسو د خپلو ځانونو لپاره خزانه كړي وو، نو تاسو وڅَكئ هغه څه چې تاسو خزانه كول');
INSERT INTO "abulsalam" VALUES(9,36,'9:36',' بېشكه د الله په نیز د میاشتو (معتبر) شمېر دولس میاشتې دى، د الله په كتاب كې، په هغه ورځ چې هغه پیدا كړل اسمانونه او ځمكه، له هغو نه څلور (میاشتی) د حرمت والا دي، همدغه سم (نېغ) دین دى، نو تاسو په دغو كې په خپلو ځانونو ظلم مه كوئ او تاسو ټول له دغو مشركانو سره جنګ كوئ، لكه څنګه چې دوى له تاسو سره جنګ كوي ټول او پوه شئ چې یقینًا الله د پرهېزګارانو مل دى ');
INSERT INTO "abulsalam" VALUES(9,37,'9:37',' اصل خبره دا ده چې تاخیر كول (د یوې میاشتې حرمت بلې میاشتې ته وروسته كول) يواځې په كفر كې ډېر والى دى، په دې سره هغه كسان ګمراه كولى شي چې كافران شوي دي، دوى دغه (حرامه میاشت) یو كال حلاله ګڼي او یو كال هغه حرامه، د دې لپاره چې دوى د هغو (میاشتو) شمېر پوره كړي چې الله حرامې كړې دي، بیا حلالې كړي هغه (میاشتې) چې الله حرامې كړې دي، دوى ته خپل ناكاره عملونه ښايسته كړى شوي دي او الله كافر قوم ته هدایت نه كوي');
INSERT INTO "abulsalam" VALUES(9,38,'9:38',' اى هغو كسانو چې ایمان يې راوړى دى! پر تاسو څه شوي دي چې كله تاسو ته وویل شي: د الله په لاره كې د غزا لپاره ووځئ (،نو) تاسو ځمكې ته درانه (او مایل) شئ؟ ایا تاسو د اخرت په مقابله كې پر دې دنيايي ژوند راضي شوي یئ؟ نو د اخرت په مقابله كې د دنیاوي ژوند فايده نه ده مګر ډېره لږه');
INSERT INTO "abulsalam" VALUES(9,39,'9:39',' كه تاسو ونه وځئ (نو) هغه (الله) به تاسو ته ډېر دردوونكى عذاب دركړي او له تاسو نه غیر به بل قوم په بدل كې راولي او تاسو به هغه ته هېڅ شى ضرر ونه رسولى شئ او الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(9,40,'9:40',' كه تاسو د دغه (رسول) مرسته ونه وكړئ، نو )هېڅ پروا نشته) یقینًا الله د هغه مدد كړى دى كله چې هغه دې كافران شويو خلقو په داسې حال كې ووېست چې دویم د دوو و، كله چې هغوى دواړه په غار كې وو، كله چې هغه خپل ملګري ته ویل: غم مه كوه، بېشكه الله زمونږ سره دى، نو الله په هغه باندې خپل تسكین نازل كړ او هغه يې په داسې لښكرو سره مضبوط كړ چې تاسو هغه نه دي لیدلي، او د كافران شويو خلقو كلمه يې تر ټولو ښكته كړه او د الله كلمه، همغه تر ټولو لوړه ده، او الله ښه غالب، ښه حكمت والا دى ');
INSERT INTO "abulsalam" VALUES(9,41,'9:41',' تاسو غزا ته وځئ، په داسی حال كې چې سپك یئ او درانه او په خپلو مالونو او ځانونو سره د الله په لار كې جهاد كوئ، دا تاسو لپاره ډېر غوره دي، كه تاسو پوهېږئ ');
INSERT INTO "abulsalam" VALUES(9,42,'9:42',' كه هغه، نژدې سامان او منځنى سفر وى (، نو) دوى به لازمًا ستا متابَعت كړى و او لېكن په دوى باندې فاصله لرې پرېوته (نو ځكه يې نافرماني وكړه) او ژر به دوى پر الله قسمونه خوري چې كه زمونږ طاقت وى (،نو) مونږ به ضرور له تاسو سره وتلي وو، دوى خپل ځانونه هلاكوي او الله پوهېږي چې بېشكه دوى یقیني دروغجن دي');
INSERT INTO "abulsalam" VALUES(9,43,'9:43',' (اى نبي!) الله ته معاف كړې، د څه لپاره (او ولې) دې دوى ته (د پاتې كېدو) اجازت وركړ؟ تر هغه پورې چې تا ته هغه كسان ښه څرګند شوي وى چې رښتیا يې ویلي او تا دروغجن (هم) معلوم كړي وى');
INSERT INTO "abulsalam" VALUES(9,44,'9:44',' له تا نه هغه كسان اجازت نه غواړي چې پر الله او د اخرت پر ورځ ایمان لري له دې نه چې په خپلو مالونو او خپلو ځانونو سره جهاد وكړي او الله پر پرهېزګارانو ښه عالم دى');
INSERT INTO "abulsalam" VALUES(9,45,'9:45',' له تا نه اجازت يواځې هغه كسان غواړي چې پر الله او د اخرت پر ورځ ایمان نه لري او د هغوى زړونه په شك كې پرېوتي دي، نو دوى په خپل شك كې حیرانه سرګردانه دي');
INSERT INTO "abulsalam" VALUES(9,46,'9:46',' او كه دوى (غزا ته) د وتلو اراده لرلى، نو ارومرو به دوى د دې (وتلو) لپاره د تیارۍ څه اسباب تیار كړي وو او لېكن الله (د جهاد لپاره) د دوى پورته كېدل نه وو خوښ كړي، نو دوى يې په سست ساتلو سره بند كړل او وویل شو: تاسو كېنئ له كېناستونكو سره');
INSERT INTO "abulsalam" VALUES(9,47,'9:47',' كه دوى ستاسو په لښكر كې وتلي وى (،نو) دوى به تاسو ته نه و زیات كړى (هیڅ شى) مګر شر او ضرور به دوى ستاسو په مینځ كې (د خرابۍ او چغلۍ لپاره) چابكي كړې وه، په داسې حال كې چې دوى به تاسو لپاره فتنه لټوله او په تاسو كې د هغوى لپاره څه جاسوسان هم شته او الله پر ظالمانو ښه پوه دى ');
INSERT INTO "abulsalam" VALUES(9,48,'9:48',' یقینًا یقینًا دوى له دې نه مخكې فتنه (خرابي) لټولې وه او تا ته يې ټول كارونه (په حیلو او دسیسو سره) اړولي وو، تر دې چې حق راغى او د الله حكم څرګند (او غالب) شو، په داسې حال كې چې دوى بد ګڼونكي وو');
INSERT INTO "abulsalam" VALUES(9,49,'9:49',' په دوى كې ځینې هغه څوك دي چې وايي: ما ته اجازت راكړه او ما په فتنه (ګمراهۍ) كې مه اچوه، خبردار شئ! هم دوى په فتنه كې غورځیدلي دي او بېشكه جهنم یقینًا كافرانو لره راګېروونكى دى');
INSERT INTO "abulsalam" VALUES(9,50,'9:50',' كه تا ته څه ښیګڼه (او برى) درورسیږي (، نو) هغه دوى خفه كوي، او كه چېرې تا ته څه مصیبت در ورسیږي (نو) دوى وايي: یقینًا مونږ خپل كار ( د احتیاط) له مخكې نه نیولى و او واپس كېږي، په داسې حال كې چې دوى (ستا په مصیبت) ډېر خوشحاله وي');
INSERT INTO "abulsalam" VALUES(9,51,'9:51',' ته ( دوى ته) ووایه: مونږ ته به له سره مصیبت راونه راسېږي مګر هغه چې الله زمونږ لپاره لیكلى دى، همغه زمونږ كارساز دى او پس لازم ده چې مومنان خاص په الله باندې توكل وكړي');
INSERT INTO "abulsalam" VALUES(9,52,'9:52',' ته (دوى ته) ووایه: تاسو زمونږ په باره كې انتظار نه كوئ مګر په دوو تر ټولو غوره څیزونو كې د یو څیز، او مونږ ستاسو په باره كې انتظار كوو د دې چې الله تاسو ته عذاب درورسوي له خپل جانب نه، یا زمونږ په لاسونو سره، پس تاسو انتظار كوئ، بېشكه مونږ هم له تاسو سره انتظار كوونكي یو');
INSERT INTO "abulsalam" VALUES(9,53,'9:53',' ته (دې منافقانو ته) ووایه: تاسو مالونه لګوئ په رضا سره، یا (د اسلام) په زور سره، له سره به له تاسو نه قبول نه كړى شي، بېشكه تاسو له شروع نه فاسقان(كافران) یئ');
INSERT INTO "abulsalam" VALUES(9,54,'9:54',' او دوى لره نه دي منع كړي چې له دوى نه دې د دوى لګولي مالونه قبول كړى شي مګر دې (خبرې) چې یقینًا دوى په الله او د هغه په رسول كافران شوي دي او لمانځه ته نه راځي مګر په داسې حال كې چې سست وي او انفاق نه كوي مګر په داسې حال كې چې ناخوښه وي');
INSERT INTO "abulsalam" VALUES(9,55,'9:55',' نو تا دې په تعجب كې وانه چوي د دوى مالونه او نه د دوى اولادونه، همدا خبره ده چې الله غواړي چې دوى ته په دغو (څیزونو) سره په دنيايي ژوند كې عذاب وركړي او د دوى روحونه په داسې حال كې ووځي چې دوى كافران وي');
INSERT INTO "abulsalam" VALUES(9,56,'9:56',' او دوى پر الله قسمونه خوري چې بېشكه دوى یقینًا له تاسو ځنې دي، حال دا چې دوى له تاسو څخه نه دي او لېكن دوى داسې قوم دى چې وېرېږي');
INSERT INTO "abulsalam" VALUES(9,57,'9:57',' كه دوى څه د پناه ځاى ومومي، یا غارونه، یا بل د ننوتلو ځاى (، نو) خامخا به دوى هغه ته وګرځي، په داسې حال كې چې دوى به منډې وهي');
INSERT INTO "abulsalam" VALUES(9,58,'9:58',' او په دوى كې ځینې هغه څوك دي چې د صدقاتو (د تقسیم) په باره كې پر تا عیب لګوي، نو كه دوى ته له دې (صدقاتو) نه (څه) وركړى شي (نو) راضي كېږي او كه له دې نه ورته څه ورنه كړى شي (نو) سمدستي هماغه وخت غصه كېږي');
INSERT INTO "abulsalam" VALUES(9,59,'9:59',' او كه حقیقتًا دوى په هغه څه راضي شوي وى چې الله او د هغه رسول دوى ته وركړي دي او ویلي يې وى: زمونږ لپاره الله كافي دى، ژر به الله مونږ ته له خپله فضله راكړي او د هغه رسول (هم له بل غنیمت نه) بېشكه مونږ خاص الله ته رغبت كوونكي یو');
INSERT INTO "abulsalam" VALUES(9,60,'9:60',' بېشكه همدا خبره ده چې صدقې (زكاتونه) يواځې د فقیرانو او مسكینانو لپاره دي او د هغو كسانو لپاره دي چې پر دغو باندې عامل (راټولوونكي) مقرر وي او د هغو لپاره دي د چا په زړونو كې چې الفت (مينه او اسلام ته رغبت) اچولى شي او په (ازادولو د) څټونو (د مریانو مكاتبانو) كې ( به صرف كولى شي) او تاوان موندونكو لپاره دي او د الله په لار كې او د مسافرو لپاره (به صرف كولى شي) دغه مقرره فریضه ده د الله له جانبه او الله ښه عالم، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(9,61,'9:61',' او په دوى كې ځینې هغه كسان دي چې نبي ته تكلیف رسوي او وايي: هغه (اورېدونكى) غوږ دى (د هر چا هر څه اوري او مني يې) ته ووایه: تاسو لپاره د خیر غوږ دى، پر الله ایمان لري او پر مومنانو باور كوي او په تاسو كې د هغو كسانو لپاره رحمت دى چې ایمان يې راوړى دى، او هغه كسان چې د الله رسول ته تكلیف رسوي؛ د هغوى لپاره ډېر دردوونكى عذاب دى ');
INSERT INTO "abulsalam" VALUES(9,62,'9:62',' تاسو لپاره دوى پر الله قسمونه خوري، د دې لپاره چې تاسو راضي كړي او الله او د هغه رسول زیات حقدار دي د دې چې دوى هغه راضي كړي، كه دوى ایمان راوړونكي وي');
INSERT INTO "abulsalam" VALUES(9,63,'9:63',' ایا دوى پر دې نه دي پوه شوي چې بېشكه شان دا دى چې څوك د الله او د هغه د رسول مخالفت كوي، نو (جزا د هغه دا ده چې) یقینًا هم د هغه لپاره د جهنم اور دى، چې په هغه كې به همېشه وي او همدغه ډېره لویه رسوايي ده');
INSERT INTO "abulsalam" VALUES(9,64,'9:64',' منافقان له دې نه وېرېږي چې په دوى باندې داسې سورت نازل كړى شي چې دې (مومنانو) ته د هغه څه خبر وركوي چې د دغو (منافقانو) په زړونو كې دي، ته (ورته) ووایه: تاسو (دغه) استهزا او مسخرې كوئ، بېشكه الله د هغه څه ښكاره كوونكى دى چې تاسو (ترې) وېرېږئ ');
INSERT INTO "abulsalam" VALUES(9,65,'9:65',' او قسم دى كه ته له دوى نه پوښتنه وكړې (، نو) خامخا به وايي: مونږ خو يواځې مشغولتیا او لوبې كولې، ته (ورته) ووایه: ایا تاسو په الله او د هغه په ایتونو او د هغه په رسول پورې استهزا او مسخرې كولې؟ ');
INSERT INTO "abulsalam" VALUES(9,66,'9:66',' عذرونه (بهانې) مه جوړوئ، یقینًا تاسو له خپل ایمان راوړو نه وروسته كافران شوي یئ، كه مونږ په تاسو كې یوې ډلې ته معافي وكړو (،نو) بلې ډلې ته به عذاب وركړو، په سبب د دې چې هغوى په رښتیا مجرمان وو ');
INSERT INTO "abulsalam" VALUES(9,67,'9:67',' منافق سړي او منافقې ښځې د دوى ځینې له ځینو (نورو) څخه دي، دوى د نارواو حكم كوي او له نېكۍ نه منع كوي او خپل لاسونه (له انفاق كولو نه) كلك بند ساتي، دوى الله پرېښوده، نو هغه (الله) دوى پرېښودل، بېشكه همدا منافقان فاسقان (د ایمان له دايرې نه وتونكي) دي ');
INSERT INTO "abulsalam" VALUES(9,68,'9:68',' الله له منافقو سړیو او منافقو ښځو او (ټولو) كفارو سره د جهنم د اور وعده كړې ده، په داسې حال كې چې دوى به په هغه كې تل ترتله وي، همدغه د دوى لپاره بس دى او الله پر دوى لعنت كړى دى او د دوى لپاره تل (دايمي) عذاب دى');
INSERT INTO "abulsalam" VALUES(9,69,'9:69',' (اى منافقانو! تاسو یئ) په شان د هغو كسانو چې له تاسو نه مخكې وو، هغوى له تاسو نه د قوت په لحاظ ډېر سخت او كلك وو او د مالونو او اولادونو په لحاظ سره ډېر زیات وو، نو هغوى له خپلې برخې نه فايده واخيستله، بیا تاسو له خپلې برخې نه فايده واخيستله، لكه څنګه چې هغو كسانو له خپلې برخې نه فايده اخيستې وه چې له تاسو نه مخكې وو او تاسو هم په باطلو خبرو كې ښه داخل شوئ په شان د هغه (داخلېدلو) چې هغوى داخل شوي وو، دغه كسان (چې دي) د دوى عملونه په دنیا او اخرت كې برباد شول او همدغه كسان تاوانیان دي ');
INSERT INTO "abulsalam" VALUES(9,70,'9:70',' ایا دوى ته د هغو كسانو (د عذاب) خبر نه دى راغلى چې له دوى نه مخكې وو (یعنې) د نوح قوم او عاد او ثمود او د ابراهیم قوم او د مدین والا او د چپه شويو كلیو (د قومِ لوط)، دوى ته خپلو رسولانو ښكاره معجزې راوړې وې، نو الله له سره داسې نه و چې پر دوى يې ظلم كړى وى او لېكن هغوى پخپله پر خپلو ځانونو ظلم كاوه ');
INSERT INTO "abulsalam" VALUES(9,71,'9:71',' او مومن سړي او مومنې ښځې، د دوى ځینې د ځینو نورو دوستان دي، دوى د نېكۍ حكم كوي او له بدۍ نه منع كوي او لمونځ قايموي او زكات وركوي او د الله او د هغه د رسول اطاعت كوي، دغه كسان (چې دي) ضرور به الله پر دوى رحم وكړي، بېشكه الله ډېر غالب، ښه حكمت والا دى ');
INSERT INTO "abulsalam" VALUES(9,72,'9:72',' الله د مومنو سړیو او مومنو ښځو سره د داسې جنتونو وعده كړې ده چې د هغو له لاندې ولې بهېږي، چې دوى به په هغو كې همېشه وي او د داسې پاكیزه اوسیدنځایونو چې د عدن (تل اوسېدنې) په جنتونو كې به وي او د الله له جانبه رضامندي تر ټولو لویه ده، همدغه ډېره لویه كامیابي ده ');
INSERT INTO "abulsalam" VALUES(9,73,'9:73',' اى نبي! ته له كفارو او منافقانو سره جهاد كوه او پر دوى سختي كوه او د دوى د ورتلو ځاى جهنم دى او (هغه) د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(9,74,'9:74',' دوى پر الله قسمونه خوري چې دوى (څه) نه دي ویلي او یقینًا یقینًا دوى د كفر الفاظ ویلي دي او له خپل اسلام راوړو نه بعد كافر شوي دي او دوى د هغه څه اراده كړې ده چې دوى (هغه ته) ونه رسېدل او دوى (پر الله او د هغه پر رسول) عیب ونه لګاوه مګر ځكه چې الله له خپله فضله دوى غنیان كړل او د هغهٔ رسول (د غنیمتونو په تقسیم سره) نو كه دوى (له نفاقه) توبه وباسي دا به د دوى لپاره ډېر غوره وي او كه دوى (له حقه) وګرځي (نو) الله به دوى ته په دنیا او اخرت (دواړو) كې ډېر دردوونكى عذاب وركړي، په دې حال كې چې د دوى لپاره به په ځمكه كې هېڅ كارساز او مددګار نه وي ');
INSERT INTO "abulsalam" VALUES(9,75,'9:75',' او په دوى كې ځینې هغه څوك دي چې له الله سره يې عهد كړى دى چې قسم دى كه هغه مونږ ته له خپله فضله څه راكړل (،نو) خامخا ضرور به خیراتونه كوو او هرو مرو لازمًا به له نېكانو څخه كېږو');
INSERT INTO "abulsalam" VALUES(9,76,'9:76',' نو كله چې هغه (الله) دوى ته له خپله فضله (مال) وركړ (، نو) په هغه (مال) سره يې بخل وكړ او وګرځېدل، په داسې حال چې اعراض كوونكي وو');
INSERT INTO "abulsalam" VALUES(9,77,'9:77',' نو هغه (الله) دوى ته د دې په نتیجه كې د دوى په زړونو كې داسې نفاق كېښود چې تر هغې ورځې پورې به په دوى كې وي چې له هغه (الله) سره به ملاقات وكړي، په سبب د هغه چې دوى له الله سره د كړې وعدې مخالفت وكړ چې دوى له هغه سره كړې وه او په سبب د دې چې دوى به دروغ ویل');
INSERT INTO "abulsalam" VALUES(9,78,'9:78',' ایا دوى پر دې نه دي پوه شوي چې بېشكه الله د دوى پر پټو (نیتونو) او د دوى پر پټو مشورو عالم دى او (دوى پر دې نه دي پوه شوي) چې بېشكه الله پر ټولو پټو خبرو ښه عالم دى');
INSERT INTO "abulsalam" VALUES(9,79,'9:79',' هغه كسان چې پر نفلي خیرات كوونكو مومنانو باندې د صدقاتو په باره كې عیب لګوي او پر هغو كسانو باندې (عیب لګوي) چې هغوى نه مومي مګر خپل وس او محنت، نو دغه (منافقان) په دوى پورې مسخرې كوي، الله به په دوى پورې مسخرې وكړي او د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(9,80,'9:80',' ته د دوى لپاره استغفار وغواړه، یا د دوى لپاره استغفار مه غواړه، كه ته د دوى لپاره اویا ځله هم استغفار وغواړې، نو الله به له سره دوى ته بخښنه ونه كړي، دا ځكه چې یقینًا دوى په الله او د هغه په رسول كافران شوي دي او الله فاسق (له حد نه وتونكي) قوم ته هدایت نه كوي');
INSERT INTO "abulsalam" VALUES(9,81,'9:81',' وروسته پاتې كړى شوي خلق، له رسول الله نه شا ته، په خپلو كېناستو باندې خوشحاله شول او دوى بد وګڼل دا چې په خپلو مالونو او ځانونو سره د الله په لاره كې جهاد وكړي او ويې ويل: تاسو په ګرمۍ كې (دې غزا ته) مه وځئ، ته ووایه: بېشكه د جهنم اور د ګرمۍ په لحاظ ډېر سخت دى، كه چېرې دوى پوهېدلى');
INSERT INTO "abulsalam" VALUES(9,82,'9:82',' نو دوى دې وخاندې (په دنیا كې) لږ او ودې ژاړي ډېر (په اخرت كې) (دا) جزا ده په سبب د هغو عملونو چې دوى به كول ');
INSERT INTO "abulsalam" VALUES(9,83,'9:83',' نو كه الله ته بېرته راوستې په دوى كې یوې ډلې ته، بیا دوى له تا نه (بلې غزا ته) د وتلو اجازت وغواړي، نو ته (ورته) ووایه: تاسو به له سره ونه وځئ (جهاد ته) زما سره، هېڅكله، او له سره به زما په ملګرتیا كې د كوم دښمن سره جنګ ونه كړئ، هېڅكله، بېشكه تاسو مخكې ځلې پر ناسته راضي شوي وئ، نو (اوس) ناست اوسئ د وروسته پاتې كېدونكو (ښځو، ماشومانو او عاجزو سړیو) سره ');
INSERT INTO "abulsalam" VALUES(9,84,'9:84',' او په دوى كې چې كوم یو مړ شي، نو ته د هغه هیڅكله هم جنازه مه كوه او مه يې (د دعا او تدفین لپاره) پر قبر ودرېږه، بېشكه دوى په الله او د هغه په رسول كافران شوي دي او مړه شوي دي، په داسې حال كې چې فاسقان وو');
INSERT INTO "abulsalam" VALUES(9,85,'9:85',' او تا دې په تعجب كې وانه چوي د دوى مالونه او نه د دوى اولادونه، یقینًا الله همدا اراده لري چې دوى ته په دغو (مالونو او اولادونو) سره عذاب وركړي او روحونه يې ووځي، په داسې حال كې چې كافران وي');
INSERT INTO "abulsalam" VALUES(9,86,'9:86',' او كله چې كوم سورت نازل كړى شي (او دا حكم په كې وي) چې پر الله ایمان راوړئ او د هغه د رسول په ملګرتیا كې جهاد وكړئ، (نو) له تا نه په دغو كې د (مالي) طاقت والا د پاتې كېدو) اجازت غواړي او وايي: ته مونږ پرېږده چې اوسو (په كورونو كې) له كېناستونكو (ښځو، ماشومانو) سره');
INSERT INTO "abulsalam" VALUES(9,87,'9:87',' دوى په دې راضي شول چې د پاتې شويو (ښځو، ماشومانو) سره وي او د دوى پر زړونو مهر لګول شوى دى، ځكه نو دوى نه پوهېږي');
INSERT INTO "abulsalam" VALUES(9,88,'9:88',' لېكن رسول او هغو كسانو چې له ده سره يې ایمان راوړى دى، هغوى په خپلو مالونو او خپلو ځانونو سره جهاد وكړ او خاص د همدغو كسانو لپاره ښېګڼې دي او همدغه كسان كامیاب دي');
INSERT INTO "abulsalam" VALUES(9,89,'9:89',' الله د دوى لپاره داسې جنتونه تیار كړي دي چې د هغو له لاندې ولې بهېږي، په دې حال كې چې همېشه اوسېدونكي به وي په هغو كې، همدغه ډېر لوى برى دى');
INSERT INTO "abulsalam" VALUES(9,90,'9:90',' او له اَعرابو (بانډېسیانو) څخه عذر جوړوونكي راغلل، د دې لپاره چې دوى ته (د پاتې كېدو) اجازت وركړى شي او هغه كسان كېناستل چې الله او د هغه رسول ته يې دروغ ویلي وو، ژر به هغو كسانو ته، چې په دوى كې كافران شوي دي ؛ ډېر دردوونكى عذاب ورسېږي ');
INSERT INTO "abulsalam" VALUES(9,91,'9:91',' هېڅ ګناه او جرم نشته پر كمزورو او نه پر مریضانو او نه پر هغو كسانو چې نه مومي هغه (مال) چې يې ولګوي، كله چې دوى د الله او د هغه د رسول لپاره اخلاص كوي، په نېكي كوونكو باندې هېڅ (د الزام) لار نشته او الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(9,92,'9:92',' او نه پر هغو كسانو (څه حرج شته) چې هر كله تا ته راغلي دي، د دې لپاره چې ته هغوى ته سورلۍ وركړې، تا وویل: زه نه مومم هغه (سورلي) چې تاسو پر هغې سواره كړم، نو هغوى وګرځي، په داسې حال كې چې د هغوى له سترګو اوښكې بهېږي، د دې غم په سبب چې دوى هغه (مال) نه مومي چې دوى يې ولګوي');
INSERT INTO "abulsalam" VALUES(9,93,'9:93',' یقینا همدا خبره ده چې لار (د ملامتۍ) خو پر هغو كسانو ده چې هغوى له تا نه (د پاتې كېدلو) اجازت غواړي، حال دا چې دوى غنیان دي، دوى راضي شوي دي پر دې چې دوى پاتې شي له وروسته پاتې كېدونكو (ښځو، جینكو) سره او الله د دوى پر زړونو مهر لګولى دى، نو دوى نه پوهېږي');
INSERT INTO "abulsalam" VALUES(9,94,'9:94',' دوى به تاسو ته عذرونه وړاندې كوي كله چې تاسو دوى ته بېرته راوګرځئ، ته (ورته) ووایه: تاسو عذرونه مه وړاندې كوئ، مونږ به له سره پر تاسو باور ونه كړو، یقینًا الله، مونږ ستاسو له ځینو احوالو نه خبر كړي یو او ژر ده چې الله او د هغه رسول به ستاسو عملونه وویني، بیا به تاسو د پټو او ښكاره وو عالِم (الله) ته ورګرځولى شئ، نو هغه به تاسو پر هغو عملونو خبر كړي چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(9,95,'9:95',' یقینًا دوى به تاسو ته پر الله قسمونه خوري، كله چې تاسو دوى ته واپس راشئ، د دې لپاره چې تاسو له دوى نه اعراض وكړئ، نو تاسو له دوى نه اعراض وكړئ، بېشكه دوى پلیت دي، او د دوى د ورتلو ځاى جهنم دى، لپاره د بدلې د هغو عملونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(9,96,'9:96',' دوى تاسو ته قسمونه خوري، د دې لپاره چې تاسو له دوى نه راضي شئ، نو كه تاسو له دوى نه راضي شئ، پس یقینًا الله له فاسق قوم نه نه راضي كېږي');
INSERT INTO "abulsalam" VALUES(9,97,'9:97',' اَعرابیان په كفر او نفاق كې ډېر سخت دي او ډېر لايق دي د دې چې د هغو (احكامو) په حدودو پوه نشي چې الله پر خپل رسول نازل كړي دي او الله ډېر پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(9,98,'9:98',' او په اَعرابیانو كې ځینې هغه دي چې (د الله په لار كې) څه چې خرچ كوي (هغه) تاوان ګڼي او ستاسو په باره كې د زمانې د ګردشونو انتظار كوي، په همدوى باندې دې د بدۍ ګردش وي او الله ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(9,99,'9:99',' او په اَعرابیانو كې ځینې هغه دي چې پر الله او پر ورځ د اخرت ایمان لري او هغه څه چې (د الله په لار كې يې) لګوي، الله ته د نږدې والي او د رسول د دعاګانو اسباب ګڼي، خبردار شئ! بېشكه دا د دوى لپاره د نږدې والي سبب دى، الله به ژر دوى په خپل رحمت كې داخل كړي، بېشكه الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(9,100,'9:100',' او له مهاجرینو او انصارو نه هغه وړاندیني اولني (ایمان لرونكي) او هغه كسان چې په نېكۍ سره يې د دوى پیروي كړې ده، الله له دوى نه راضي شوى دى او دوى له هغه نه راضي شوي دي او هغهٔ، د دوى لپاره داسې جنتونه تیار كړي دي چې د هغو له لاندې وَلې بهېږي، چې دوى به په هغو كې تل ترتله وي، همدا ډېره لویه كامیابي ده ');
INSERT INTO "abulsalam" VALUES(9,101,'9:101',' او ستاسو په شاو خوا كې چې اعرابیان دي، په هغو كې ځینې منافقان دي او ځینې د مدینې له اوسېدونكو نه هم، چې په نفاق باندې ټینګ شوي دي، ته (اى نبي!) هغوى نه پېژنې، مونږ يې پېژنو، ژر به مونږ هغوى ته دوه ځله عذاب وركړو، بیا به هغوى ډېر لوى عذاب ته ورګرځولى شي ');
INSERT INTO "abulsalam" VALUES(9,102,'9:102',' او (ځینې) نور (خلق) دي چې پر خپلو ګناهونو قايله شوي دي، دوى نېك عمل او بل بد عمل سره ګَډ (او ميلاو) كړي دي، امېد دى چې الله به پر دوى رجوع وكړي، بېشكه الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(9,103,'9:103',' (اى نبي!) ته د دوى له مالونو نه صدقه واخله، چې ته دوى پاك كړې او په دې سره يې تزكیه وكړې او دوى ته دعا وكړه، بېشكه ستا دعا د دوى لپاره د تسكین ذريعه ده او الله ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(9,104,'9:104',' ایا دوى نه پوهېدل چې یقینًا الله دى چې هغه د خپلو بنده ګانو توبه قبلوي او صدقې اخلي او دا چې بېشكه الله، هم دى ښه توبه قبلوونكى، بې حده مهربان دى ');
INSERT INTO "abulsalam" VALUES(9,105,'9:105',' او (اى نبي!) ته ووایه: تاسو عمل كوئ، پس ضرور به الله ستاسو عمل وویني او د ده رسول او مومنان (هم) او ژر به تاسو بیا بوتلى شئ د پټو او ښكاره وو عالِم (الله) ته، نو هغه به تاسو پر هغو كارونو خبر كړي چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(9,106,'9:106',' او نور كسان دي چې د الله د حكم لپاره وروسته كړى شوي دي، یا به دوى ته عذاب وركړي او یا به په دوى باندې مهرباني وكړي او الله ښه عالم، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(9,107,'9:107',' او هغه خلق چې یو جومات يې جوړ كړى دى د ضرر رسولو لپاره او د كفر كولو لپاره او د مومنانو په مینځ كې د بېلتون راوستلو لپاره او د هغو كسانو د انتظار (او كمين) لپاره چا چې له دې نه مخكې (هم) له الله او د هغه له رسول سره جنګ كړى دى او دوى به یقینًا هرومرو قسمونه خوري چې مونږ اراده نه ده كړې مګر د نېكۍ او الله شاهدي وايي چې بېشكه دوى یقینًا دروغجن دي');
INSERT INTO "abulsalam" VALUES(9,108,'9:108',' ته په دغه (جومات) كې هیڅكله هم مه ودرېږه، یقینًا هغه جومات چې بنیاد يې له اولې ورځې نه پر تقویٰ ایښودل شوى دى؛ ډېر لايق دى د دې چې ته په كې ودرېږې، په دې (جومات) كې داسې سړي دي چې ښه پاك اوسېدل خوښوي او الله ښه پاك اوسېدونكي خوښوي');
INSERT INTO "abulsalam" VALUES(9,109,'9:109',' ایا نو هغه څوك چې د خپلې ابادۍ بنیاد يې د الله په تقویٰ او رضامندۍ باندې ایښى دى، غوره دى، یا هغه څوك چې د خپلې ابادۍ بنیاد يې د نړېدونكې كندې (پانډ) په غاړه ایښى دى، بيا هغه د جهنم په اور كې له هغه سره ونړېږي او الله ظالم قوم ته هدایت نه كوي');
INSERT INTO "abulsalam" VALUES(9,110,'9:110',' د دوى هغه ابادي چې دوى جوړه كړې ده ؛ همېشه به د دوى په زړونو كې د شك سبب وي مګر دا چې د دوى زړونه ټوټې ټوټې شي او الله ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(9,111,'9:111',' بېشكه الله له مومنانو نه د هغوى ځانونه او د هغوى مالونه اخيستي دي په بدل د دې كې چې یقینًا د دوى لپاره جنت دى، دوى د الله په لاره كې جنګ كوي، نو وژل كوي او وژلى شي، دا په تورات او انجیل او قرآن كې د هغه په ذمه حقه وعده ده او له الله نه په خپلې وعدې باندې زیات ښه وفا كوونكى (بل) څوك دى؟ نو تاسو په خپلې هغې سودا باندې ښه خوشاله شئ چې تاسو له هغه (الله) سره كړې ده او همدغه ډېر لوى برى دى');
INSERT INTO "abulsalam" VALUES(9,112,'9:112',' (دوى) توبه كوونكي دي، عبادت كوونكي، ثنا ویونكي، روژه نیونكي، ركوع كوونكي، سجده كوونكي، د نېكۍ حكم كوونكي او له بدو كارونو څخه منع كوونكي دي او د الله د حدودو حفاظت كوونكي دي او ته مومنانو ته زېرى وركړه');
INSERT INTO "abulsalam" VALUES(9,113,'9:113',' د نبي او هغو كسانو لپاره چې ایمان يې راوړى دى؛ روا نه دي چې د مشركانو لپاره مغفرت وغواړي اګر كه دغه (مشركان) رشته داران هم وي، وروسته له دې نه چې دوى ته دا ښه ښكاره شوه چې یقینًا دوى د دوزخ والا دي');
INSERT INTO "abulsalam" VALUES(9,114,'9:114',' او د خپل پلار لپاره د ابراهیم مغفرت غوښتل نه و مګر د یوې وعدې له وجې نه چې د هغې وعده يې له هغه (خپل پلار) سره كړې وه، نو كله چې هغه ته ښه څرګنده شوه چې یقینًا هغه د الله دښمن دى (، نو) له هغه نه بېزاره شو، بېشكه ابراهیم خامخا ډېر نرم زړى، ډېر حِلم والا و');
INSERT INTO "abulsalam" VALUES(9,115,'9:115',' او الله هیڅكله هم داسې نه دى چې یو قوم بې لارې كړي وروسته له دې چې دوى ته يې لاره ښودلې وي تر هغه پورې چې دوى ته هغه څه بیان كړي چې دوى ترې ځان وساتي، بېشكه الله په هر شي باندې ښه پوه دى');
INSERT INTO "abulsalam" VALUES(9,116,'9:116',' بېشكه الله چې دى، د اسمانونو او ځمكې سلطنت خاص د هغه لپاره دى، هغه ژوند وركوي او هغه مرګ وركوي او تاسو لپاره له الله نه غیر نه هېڅ كار ساز شته او نه هېڅ مددګار');
INSERT INTO "abulsalam" VALUES(9,117,'9:117',' یقینًا یقینًا الله په مهربانۍ سره رجوع وكړه په خپل نبي او مهاجرو او انصارو باندې، په هغو كسانو باندې چې د تنګۍ په وخت كې يې د هغهٔ متابعت او ملګرتيا وكړه، وروسته له دې چې قریب وه چې په هغو كې د یوې ډلې زړونه كاږه شي، بیا هغه (الله) په دوى باندې مهرباني وكړه، یقینًا هغه پر دوى ډېر نرمي كوونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(9,118,'9:118',' او په هغو درې كسانو باندې چې وروسته كړى شوي وو، تر دې چې په هغوى باندې ځمكه، سره د هغې له ارت والي تنګه شوه او هغوى پر خپلو ځانونو (هم) تنګ شول (خپل ځانونه پرې بوج شول) او هغوى یقین وكړ چې د الله (له عذاب) نه د پناه هېڅ ځاى نشته مګر هم له هغهٔ سره دى، بیا هغه (الله) پر دوى مهرباني وكړه، د دې لپاره چې دوى توبه وباسي، بېشكه چې الله، هم دى ښه توبه قبلوونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(9,119,'9:119',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو له الله نه ووېرېږئ او له رښتینو سره ملګري شئ');
INSERT INTO "abulsalam" VALUES(9,120,'9:120',' د مدینې د خلقو لپاره او د دوى چاپېره د هغه چا لپاره چې اعرابیان دي، روا نه دي چې له رسول الله(صلى الله عليه وسلم) نه شا ته پاتې شي او نه دا چې خپل ځانونه د هغه له ځان نه ښه او غوره وګڼي، دا ځكه چې یقینًا دوى چې دي، دوى ته نه څه تنده رسي او نه ستړي والى او نه لوږه د الله په لاره كې او نه دوى پر كوم داسې ځاى قدمونه ږدي چې كفار په غصه كوي او نه رسي دوى دښمن ته په (هېڅ) رسېدلو سره (چې د هغوى وژل، بنديان كول او ترې غنيمت اخيستل دي) مګر دوى ته په دې سره نېك عمل لیكلى شي، بېشكه الله د نېكي كوونكو اجر نه ضايع كوي');
INSERT INTO "abulsalam" VALUES(9,121,'9:121',' او نه دوى څه نفقه لګوي، نه وړه او نه لویه او نه څه میدان قطع كوي مګر د دوى لپاره (يې ثواب) لیكلى شي، د دې لپاره چې الله دوى ته د هغو بهترینو عملونو بدله وركړي چې دوى به كول');
INSERT INTO "abulsalam" VALUES(9,122,'9:122',' او مومنان داسې نه دي چې ټول له څېرمې ووځي، نو ولې نه وځي د دوى له هرې ډلې نه یو ټولګى؟ د دې لپاره چې دوى په دین كې ژوره پوهه پیدا كړي او خپل قوم ته اِنذار وركړي كله چې دوى ته راواپس شي، د دې لپاره چې دوى ووېرېږي');
INSERT INTO "abulsalam" VALUES(9,123,'9:123',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو له هغو كفارو سره وجنګېږئ چې تاسو ته نژدې دي او لازم ده چې دوى په تاسو كې سختي ومومي او تاسو پوه شئ چې یقینًا الله د پرهېزګارو ملګرى دى ');
INSERT INTO "abulsalam" VALUES(9,124,'9:124',' او كله چې هم كوم سورت نازل كړى شي، نو په دوى كې ځینې هغه څوك دي چې وايي: په تاسو كې كوم یو دى چې دې (سورت) د هغه ایمان زیات كړ؟ نو هر چې مومنان دي، نو دې (سورت) د هغوى ایمان ورزیات كړ، په داسې حال كې چې هغوى ښه خوشالېږي');
INSERT INTO "abulsalam" VALUES(9,125,'9:125',' او پاتې شو هغه كسان چې د هغوى په زړونو كې مرض دى، نو دې (سورت) هغوى ته د هغوى له ګندګۍ سره ګندګي ور زیاته كړه او دوى مړه شول، په دې حال كې چې هغوى كافران وو');
INSERT INTO "abulsalam" VALUES(9,126,'9:126',' او ایا دوى نه ګوري چې یقینًا دوى هر كال یو ځله، یا دوه ځله ازمايلى شي، بیا نه دوى توبه وباسي او نه دوى نصیحت اخلي');
INSERT INTO "abulsalam" VALUES(9,127,'9:127',' او كله چې هم كوم سورت نازل كړى شي د دوى ځینې ځینو (نورو) ته وګوري چې ایا تاسو څوك ویني؟ بیا واپس شي، الله د دوى زړونه ګرځولي دي، په سبب د دې چې یقینًا دوى داسې قوم دى چې نه پوهېږي');
INSERT INTO "abulsalam" VALUES(9,128,'9:128',' یقینًا یقینًا تاسو ته له هم ستاسو له جنسه نه رسول راغلى دى، هغه څه چې تاسو په تكلیف كې اچوي؛ پر ده ډېر سخت (او درانه) دي، ستاسو (د هدایت) لپاره پر تاسو باندې ډېر حرصناك دى، په مومنانو باندې ډېر نرمي كوونكى، ډېر مهربان دى');
INSERT INTO "abulsalam" VALUES(9,129,'9:129',' نو كه چېرې دوى (د حق له قبلولو نه) وګرځي، نو ته ووایه: ما ته الله كافي دى، نشته دى هېڅ لايق د عبادت مګر هغه دى، خاص په هغه باندې ما توكل كړى دى او هم هغه د عظیم عرش مالك دى');
INSERT INTO "abulsalam" VALUES(10,1,'10:1',' الف، لام، را، دا له حكمت نه د ډك كتاب ایتونه دي');
INSERT INTO "abulsalam" VALUES(10,2,'10:2',' ایا د خلقو لپاره دا د تعجب خبره ده چې مونږ په دوى كې یو سړي ته دا وحي وكړه چې ته خلق ووېروه او هغو كسانو ته زېرى وركړه چې ایمان يې راوړى دى چې یقینًا د دوى لپاره د خپل رب په نیز لویه رښتینې مرتبه ده (نو) كافرانو وویل: بېشكه دا یقینًا ښكاره جادوګر دى');
INSERT INTO "abulsalam" VALUES(10,3,'10:3',' بېشكه چې ستاسو رب هم هغه الله دى چې پیدا كړي يې دي اسمانونه او ځمكه په شپږو ورځو كې، بیا دى په عرش باندې برقرار شو، دى د ټولو كارونو تدبیر كوي، هېڅ سفارشي نشته مګر د هغه له اجازت نه وروسته، دغه الله ستاسو رب دى، نو تاسو د ده عبادت كوئ، ایا نو تاسو نصیحت نه اخلئ!');
INSERT INTO "abulsalam" VALUES(10,4,'10:4',' خاص هم ده ته ستاسو د ټولو واپسي ده، الله حقه وعده كړې ده، بېشكه دى مخلوق لره اول ځل پیدا كوي، بیا يې دوباره ژوندي كوي، د دې لپاره چې هغو خلقو ته چې ایمان يې راوړى دى او نېك عملونه يې كړي دي په انصاف سره بدله وركړي، او هغه كسان چې كفر يې كړى دى، د دوى لپاره ایشېدلې ګرمې اوبه دي او بېخي دردناك عذاب دى، په دې سبب چې دوى به كفر كاوه');
INSERT INTO "abulsalam" VALUES(10,5,'10:5',' هم هغه (الله) دى چې (ټیكلى د) لمر يې تېز ځلېدونكى ګرځولى دى او (ټيكلى د) سپوږمۍ يې روښانه او هغه يې منزلونه منزلونه مقرر كړه (او منزلونه يې مقرر كړل)، د دې لپاره چې تاسو د كلونو شمېره او حساب معلوم كړئ ، الله دغه (څیزونه) نه دي پیدا كړي مګر په حقه سره، هغه ایتونه (دلیلونه) په تفصیل سره بیانوي، د هغو خلقو لپاره چې پوهېږي');
INSERT INTO "abulsalam" VALUES(10,6,'10:6',' بېشكه د شپې او ورځې په اختلاف (بدلېدو رابدلېدو) كې او په هغو (شیانو) كې چې الله په اسمانونو او ځمكه كې پیدا كړي دي، خامخا نښې دي د هغو خلقو لپاره چې وېرېږي');
INSERT INTO "abulsalam" VALUES(10,7,'10:7',' بېشكه هغه كسان چې زمونږ د ملاقات امېد نه لري او پر دنيايي ژوند راضي شوي دي او پر دې (ژوند) مطمئن دي او هغه كسان چې دوى زمونږ له ایتونو نه غافلان دي');
INSERT INTO "abulsalam" VALUES(10,8,'10:8',' دغه كسان، د دوى استوګنه اور دى، په سبب د هغو كارونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(10,9,'10:9',' بېشكه هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، د دوى رب به دوى ته د خپل ایمان په سبب لارښوونه كوي، د نعمتونو په باغونو كې به د دوى له لاندې نهرونه بهېږي');
INSERT INTO "abulsalam" VALUES(10,10,'10:10',' د دوى وینا به په دغو (جنتونو) كې سُبْحَانَكَ اللهم (اى الله! ستا پاكي بیانوو) وي او په دغو (باغونو) كې به د دوى ډالۍ سلام وي او د دوى د وینا اخر به دا وي چې ټول صفتونه خاص الله لره دي چې د ټولو عالمونو ښه پالونكى دى');
INSERT INTO "abulsalam" VALUES(10,11,'10:11',' او كه چېرې الله خلقو ته بدي (تكليف) جلت ورسوي، په شان د جلت غوښتلو د دوى خیر لره (، نو) خامخا دوى ته به د دوى موده پوره كړى شوې وى، نو مونږه هغه كسان چې زمونږ د ملاقات امېد نه لري په خپلې سركشۍ كې پرېږدو، چې سرګردانه وي');
INSERT INTO "abulsalam" VALUES(10,12,'10:12',' او كله چې انسان ته تكلیف ورسېږي (نو) په خپل اړخ باندې (پروت)، یا ناست، یا په ولاړه مونږ بلي، نو كله چې مونږ له هغه نه د هغه تكلیف لرې كړو (نو داسې) تېرېږي لكه چې ده مونږ نه وو بللي (د) هغه تكليف (لرې كولو) ته چې ده ته رسېدلى و، همدارنګه له حد نه تېرېدونكو ته هغه عملونه ښايسته كړى شول چې دوى به كول');
INSERT INTO "abulsalam" VALUES(10,13,'10:13',' او یقینًا یقینًا ستاسو نه مخكې قومونه (مخكیني نسلونه) مونږ هلاك كړل كله چې هغوى ظلم وكړ او هغوى ته خپلو رسولانو ښكاره معجزې راوړې وې او هغوى له سره داسې نه وو چې ایمان يې راوړى وى، همدارنګه مونږ مجرمو خلقو ته سزا وركوو');
INSERT INTO "abulsalam" VALUES(10,14,'10:14',' بیا مونږه له هغوى نه وروسته تاسو په ځمكه كې ځاى ناستي جوړ كړئ، د دې لپاره چې مونږ وګورو تاسو څنګه عمل كوئ');
INSERT INTO "abulsalam" VALUES(10,15,'10:15',' او كله چې په دوى باندې زمونږ ایتونه، چې واضح دي، ولوستل شي، هغه كسان ووايي چې زمونږ د ملاقات امېد نه لري: ته له ده نه غیر قرآن راوړه، یا په ده كې تبدیلي وكړه، ته (ورته) ووایه: ما ته دا حق نشته چې زه په ده كې له خپله ځانه تبدیلي وكړم، زه پیروي نه كوم مګر د هغه چې ما ته وحي كولى شي، بېشكه زه د ډېرې سترې ورځې له عذاب نه وېرېږم كه د خپل رب نافرماني وكړم');
INSERT INTO "abulsalam" VALUES(10,16,'10:16',' ته ووایه: كه چېرې الله غوښتلى (،نو) ما به دا (قرآن) پر تاسو باندې نه و لوستى او نه به هغهٔ، تاسو پر ده عالمان كړي وى، پس یقینًا ما له ده نه مخكې په تاسو كې ډېر عمر تېر كړى دى، ایا نو تاسو له عقل نه كار نه اخلئ!');
INSERT INTO "abulsalam" VALUES(10,17,'10:17',' نو له هغه چا نه لوى ظالم څوك دى چې په الله باندې دروغ تپي، یا د هغه د ایتونو تكذیب كوي، بېشكه شان دا دى چې مجرمان به خلاصى ونه مومي');
INSERT INTO "abulsalam" VALUES(10,18,'10:18',' او دوى له الله نه غير د هغو څیزونو عبادت كوي چې نه دوى ته ضرر رسولى شي او نه دوى ته نفع رسولى شي او دوى وايي: دا د الله په وړاندې زمونږ سفارشیان دي، ته ووایه: ایا تاسو الله پر هغه څه خبروئ چې هغه يې نه په اسمانونو كې پېژني او نه يې په ځمكه كې، هغه ډېر پاك دى او ډېر اوچت دى له هغو څیزونو نه چې دوى يې ورسره شریكوي ');
INSERT INTO "abulsalam" VALUES(10,19,'10:19',' او (په شروع كې) ټول خلق یو امت وو، بیا دوى سره مختلفه شول او كه چېرې نه وى هغه خبره چې ستا د رب له خوا مخكې ثابته شوې ده (، نو) خامخا د دوى په مینځ كې به د هغو خبرو فیصله شوې وى چې دوى په كې اختلاف كوي');
INSERT INTO "abulsalam" VALUES(10,20,'10:20',' او دوى وايي: ولې په ده باندې د خپل رب له جانبه معجزه نه ده نازله كړى شوې؟ نو ته (ورته) ووایه: بېشكه همدا خبره ده چې (علم د) غیب د الله لپاره خاص دى، نو تاسو انتظار كوئ، بېشكه زه له تاسو سره له انتظار كوونكو څخه یم');
INSERT INTO "abulsalam" VALUES(10,21,'10:21',' او كله چې مونږ په خلقو باندې څه رحمت وڅكو، وروسته له هغه تكلیف نه چې دوى ته رسېدلى وي (، نو) ناڅاپه دوى زمونږ په ایتونو كې مكرونه (او سازشونه) شروع كړي، ته ووایه: الله د مكر په لحاظ سره تر ټولو جلت دى، بېشكه زمونږ رسولان (ملايك) لیكي هغه څه چې تاسو كوم مكر كوئ');
INSERT INTO "abulsalam" VALUES(10,22,'10:22',' هم هغه دى چې تاسو په وچه او بحر كې ګرځوي، تر دې چې كله تاسو په كښتیو كې (سواره) شئ، او دا (كښتۍ) له دوى سره په ښې (موافِقې) هوا سره روانې شي او په دې سره دوى خوشحاله شي (،نو) په دې (كښتیو) باندې سخت تېز باد راشي او په دې (خلقو) باندې له هر ځایه چپې راشي او دوى یقین وكړي چې بېشكه دوى ګېر كړى شول (بیا) دوى الله بلي، په داسې حال كې چې خاص هغه لره دین (عبادت) خالص كوونكي وي (او وايي) یقینًا كه چېرې تا مونږ ته له دې (مصیبت) نه خلاصى راكړ(،نو) مونږ به هرومرو لازمًا له شكر كوونكو څخه شو');
INSERT INTO "abulsalam" VALUES(10,23,'10:23',' نو كله چې هغه، دوى ته نجات وركړي ناڅاپه دوى په ځمكه كې ناحقه ظلم فساد شروع كړي، اى خلقو! بېشكه همدا خبره ده چې ستاسو ظلم او سركشي د خپلو ځانونو په ضرر ده. د دنيايي ژوند فايده (واخلئ) بیا خاص مونږ ته ستاسو واپس راتله دي، نو مونږ به تاسو پر هغو عملونو خبر كړو چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(10,24,'10:24',' بېشكه همدا خبره ده چې د دنيايي ژوند مثال د هغو اوبو په شان دى چې مونږ له بره نازلې كړې، بیا په (سبب د) هغو سره د ځمكې زرغونه ښه ګډه وډه شي له هغو څیزونو ځنې چې خلق او څاروي يې خوري، تر دې چې كله ځمكه خپل زینت واخلي او ښه ښكلې شي او د هغې خاوندان یقین وكړي چې یقینًا دوى پر دې قادر دي (، نو) هغې ته زمونږ حكم د شپې راشي، یا د ورځې، نو مونږ هغه رېبل شوې كړو، ګویا كې دغه (اباده زرغونه ځمكه) پرون له سره موجود نه وه، همدارنګه مونږه ایتونه په تفصیل سره بیانوو د هغو خلقو لپاره چې ښه فكر كوي');
INSERT INTO "abulsalam" VALUES(10,25,'10:25',' او الله د سلامتۍ كور ته بلنه كوي او چا ته چې وغواړي هغه ته د نېغې لارې هدایت كوي');
INSERT INTO "abulsalam" VALUES(10,26,'10:26',' د هغو كسانو لپاره چې نېكي يې كړي ده ډېره نېكه بدله ده او زیادت (د اجر هم شته) او د دوى مخونه به نه هېڅ تور ګرد او غبار پټوي او نه څه ذلت، دغه كسان د جنت والا دي دوى به په هغه كې همېشه وي');
INSERT INTO "abulsalam" VALUES(10,27,'10:27',' او هغه كسان چې بد كارونه يې كړي دي (د دوى) د بدۍ جزا د هغې په مثل ده او ذلت به دوى لره پټوي، د دوى لپاره به د الله (له عذاب) نه هېڅ ساتونكى نه وي، ګویاكې د دوى مخونه د تكې تیارې شپې په ټكړو پټ كړى شوي دي، دغه كسان د اور والا دي، دوى به په هغه كې همېشه وي');
INSERT INTO "abulsalam" VALUES(10,28,'10:28',' او هغه ورځ چې مونږ به دوى ټول راجمع كړو، بیا به مونږ هغو كسانو ته چې شرك يې كړى دى، ووایو: تاسو او ستاسو شریكان پر خپل ځاى ټینګ ودرېږئ، نو مونږ به د دوى په مینځ كې بېلتون راولو او د دوى شریكان به ووايي: تاسو خو زمونږ عبادت نه كاوه');
INSERT INTO "abulsalam" VALUES(10,29,'10:29',' نو الله ښه كافي ګواه دى زمونږ په مینځ كې او ستاسو په مینځ كې، بېشكه شان دا دى چې مونږ ستاسو له عبادته یقینًا ناخبره وو');
INSERT INTO "abulsalam" VALUES(10,30,'10:30',' په دغه ځاى كې به هر نفس وازمايي هغه څه چې ده مخكې لېږلي دي او دوى به خپل حق مولٰی (مالك ) ته واپس بوتلى شي او له دوى نه به هغه څه ورك شي چې دوى به له ځانه دروغ جوړول');
INSERT INTO "abulsalam" VALUES(10,31,'10:31',' ته ووایه: تاسو ته له اسمان او ځمكې نه روزي څوك دركوي؟ یا د غوږونو او سترګو مالك څوك دى اوڅوك ژوندى له مړي راوباسي او مړى له ژوندي راوباسي او څوك د كارونو تدبیر كوي؟ نو یقینًا دوى به وايي چې الله دى، (نو ته ووايه:) ایا نو تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(10,32,'10:32',' نو دغه الله ستاسو په حقه رب دى، نو له حق نه بعد نشته مګر ګمراهي، نو تاسو كوم طرف ته ګرځولى شئ؟');
INSERT INTO "abulsalam" VALUES(10,33,'10:33',' همدارنګه ستا د رب كلمه په هغو كسانو باندې چې فسق يې كړى دى، ثابته شوه چې یقینًا دوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(10,34,'10:34',' ته (دوى ته) ووایه: ایا ستاسو په شریكانو كې داسې څوك شته چې مخلوق لره اول ځل پیدا كوي، بیا يې دوباره ژوندي كوي؟ ته ووایه: الله مخلوق لره اول ځل پیدا كوي، بیا به يې دوباره ژوندي كوي، نو تاسو كوم اړخ ته اړول كېږئ؟');
INSERT INTO "abulsalam" VALUES(10,35,'10:35',' ته (دوى ته) ووایه: ایا ستاسو په شریكانو كې داسې څوك شته چې د حق طرف ته هدایت كوي؟ ته ووایه: الله حق ته لار ښوونه كوي، ایا نو هغه څوك چې حق ته لار ښوونه كوي زیات حقدار دى چې متابعت يې وكړى شي، یا كه هغه څوك چې (پخپله هم) لاره نشي موندلى مګر دا چې لار ورته وښودلى شي؟ نو پر تاسو څه شوي دي، تاسو څنګه فیصله كوئ؟');
INSERT INTO "abulsalam" VALUES(10,36,'10:36',' او د دوى زیاتره پیروي نه كوي مګر د ګمان، بېشكه ګمان د حق په مقابله كې هېڅ فايده نه وركوي، بېشكه الله ښه پوه دى پر هغو كارونو چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(10,37,'10:37',' او دا قرآن هیڅكله هم داسې نه دى چې له الله نه غير دې په دروغو جوړ كړى شي او لېكن دا تصدیق دى د هغو (كتابونو) چې له ده نه مخكې دي او د رب العلمین له جانبه د كتاب تفصیل دى چې هېڅ شك په كې نشته');
INSERT INTO "abulsalam" VALUES(10,38,'10:38',' ایا دوى وايي چې ده دا له ځانه جوړ كړى دى، ته (دوى ته) ووایه: نو تاسو یو وړوكى سورت د ده په مثل راوړئ او له الله نه پرته چې څوك كولى شئ راوبلئ، كه چېرې تاسو رښتینې یئ');
INSERT INTO "abulsalam" VALUES(10,39,'10:39',' بلكې دوى د هغه (قرآن) تكذیب وكړ چې دوى د هغه د علم احاطه نه وه كړې، او دوى ته لا د ده حقیقت نه و راغلى، همدارنګه تكذیب كړى و هغو كسانو چې له دوى نه مخكې وو، نو ته وګوره د ظالمانو انجام څنګه و؟');
INSERT INTO "abulsalam" VALUES(10,40,'10:40',' او ځینې په دوى كې هغه څوك دي چې پر ده ایمان راوړي او ځینې په دوى كې هغه څوك دي چې پر ده ایمان نه راوړي، او ستا رب پر فساد كوونكو تر ټولو ښه پوه دى');
INSERT INTO "abulsalam" VALUES(10,41,'10:41',' او كه چېرې دوى ستا تكذیب وكړي، نو ته (دوى ته) ووایه: زما لپاره زما عمل دى او ستاسو لپاره ستاسو عمل دى، او تاسو له هغه عمل نه بېزاره یئ چې زه يې كوم او زه له هغه عمل نه بېزاره یم چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(10,42,'10:42',' او ځینې په دوى كې هغه كسان دي چې تا ته په غور سره غوږ نیسي، ایا نو ته كڼو ته اورول كولى شې اګر كه دوى عقل هم ونه لري؟');
INSERT INTO "abulsalam" VALUES(10,43,'10:43',' او ځینې په دوى كې هغه كسان دي چې تا ته ګوري، ایا نو ته ړندو ته لار ښودلى شې اګر كه هغوى لیدل هم نه كوي');
INSERT INTO "abulsalam" VALUES(10,44,'10:44',' بېشكه الله په خلقو باندې هېڅ شى ظلم نه كوي او لېكن همدا خلق پر خپلو ځانونو ظلم كوي');
INSERT INTO "abulsalam" VALUES(10,45,'10:45',' او هغه ورځ چې هغه (الله) به دوى راجمع كړي، په دې حال كې چې لكه دوى (په دنيا كې) نه وو اوسېدلي مګر د ورځې یوه ګړۍ، دوى به په خپل مینځ كې یو بل پېژني، یقینًا هغه كسان تاوانیان شول چې د الله د ملاقات تكذیب يې كړى و او دوى هدایت موندونكي نه وو');
INSERT INTO "abulsalam" VALUES(10,46,'10:46',' او كه كله هم مونږ تا ته ځینې هغه څه وښیو چې مونږ يې له دوى سره وعده كوو، یا تا وفات كړو، نو خاص زمونږ طرف ته د دوى واپس راتلل دي، بیا الله ښه ګواه دى پر هغو كارونو چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(10,47,'10:47',' او دهر امت لپاره یو رسول و، نو كله چې به د هغوى رسول راغى د هغوى په مینځ كې به په انصاف سره فیصله وكړى شوه، په داسې حال كې چې پر هغوى به ظلم نه كېده');
INSERT INTO "abulsalam" VALUES(10,48,'10:48',' او دوى وايي: دا وعده كله ده كه چېرې تاسو رښتیني یئ؟');
INSERT INTO "abulsalam" VALUES(10,49,'10:49',' ته (دوى ته) ووایه: زه د خپل ځان لپاره نه د هېڅ ضرر او نه د هېڅ فايدې مالك یم مګر هغه چې الله يې وغواړي، د هر امت لپاره یوه نېټه ده (،نو) كله چې د دوى نېټه راشي، نو دوى به نه یوه ګړۍ وروسته كېږي او نه مخكې كېږي');
INSERT INTO "abulsalam" VALUES(10,50,'10:50',' ته (دوى ته) ووایه: تاسو خبر راكړئ! كه چېرې پر تاسو د هغه عذاب د شپې راشي، یا د ورځې، (نو) څه دى هغه (عذاب) چې مجرمان يې په تلوار غواړي؟');
INSERT INTO "abulsalam" VALUES(10,51,'10:51',' ایا بیا به تاسو پر دې (عذاب) ایمان راوړئ كله چې واقع شي؟ ایا اوس (ایمان راوړئ چې عذاب نازل شو؟)!، حال دا چې یقینًا تاسو وئ چې د دغه (عذاب) به مو جلتي كوله');
INSERT INTO "abulsalam" VALUES(10,52,'10:52',' بیا به ظالمانو ته وویل شي: تاسو د همېشه والي عذاب وڅكئ، تاسو ته سزا نه دركولى شي مګر په سبب د هغه (عمل) چې تاسو به كاوه');
INSERT INTO "abulsalam" VALUES(10,53,'10:53',' او دوى له تا نه پوښتنه كوي چې ایا دا حق دى؟ ته (ورته) ووایه: هو! پر خپل رب مې دې قسم وي چې بېشكه دغه خامخا حق دى او تاسو (الله لره) عاجزه كوونكي نه یئ');
INSERT INTO "abulsalam" VALUES(10,54,'10:54',' او كه چېرې حقيقتًا د هر هغه نفس لپاره چې ظلم يې كړى دى، هغه څه وي چې په ځمكه كې دي، نو خامخا به يې فدیه كړي او دوى به پښیماني پټه كړي كله چې دوى عذاب وویني او د دوى په مینځ كې به فیصله په انصاف سره وكړى شي، په دې حال كې چې پر دوى به ظلم نه كېږي');
INSERT INTO "abulsalam" VALUES(10,55,'10:55',' اګاه شئ! بېشكه خاص د الله لپاره دي هغه څه چې په اسمانونو او ځمكه كې دي، اګاه شئ! بېشكه د الله وعده حقه ده او لېكن د دوى اكثره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(10,56,'10:56',' هغه ژوند وركوي او مرګ وركوي او خاص د هغهٔ په طرف به تاسو ورګرځولى شئ');
INSERT INTO "abulsalam" VALUES(10,57,'10:57',' اى خلقو! یقینًا تاسو ته د خپل رب له جانبه نصیحت راغلى دى او په سینو كې چې څه (مرضونه) دي د هغو لپاره ستره شفا ده او د مومنانو لپاره هدایت او رحمت دى');
INSERT INTO "abulsalam" VALUES(10,58,'10:58',' ته ووایه: د الله په فضل سره او د هغه په رحمت سره، لهذا په دې سره دې دوى خوشحاله شي، دا ډېر غوره دى له هغه نه چې دوى يې جمع كوي');
INSERT INTO "abulsalam" VALUES(10,59,'10:59',' ته (دوى ته) ووایه: تاسو ما ته خبر راكړئ! هغه رزق چې الله تاسو لپاره نازل كړى دى، نو تاسو له هغه نه څه حرام او څه حلال ګرځولى دى، ته (ورته) ووایه: ایا الله تاسو ته د دې اجازت (خبر) دركړى دى، یا تاسو په الله پورې دروغ تړئ ؟');
INSERT INTO "abulsalam" VALUES(10,60,'10:60',' او د هغو كسانو، چې په الله باندې له ځانه دروغ تپي، څه ګمان دى، د قیامت په ورځ كې (به ورسره څه كېږي) بېشكه الله په خلقو باندې خامخا د فضل كولو والا دى او لېكن د دوى زیاتره شكر نه وباسي');
INSERT INTO "abulsalam" VALUES(10,61,'10:61',' او ته نه يې په كوم كار كې (مشغوله) او نه ته لولې له دې نه، یعنې له قرآنه او نه تاسو څه عمل كوئ مګر مونږ پر تاسو باندې شاهدان یو كله چې تاسو په هغه كې بوختېږئ او ستا له رب نه په ځمكه كې او نه په اسمان كې د ذرې په اندازه (شى) هم نه پټېږي او نه له دې نه زیات وړوكى شى او نه له دې نه زیات غټ مګر په واضحه كتاب كې دي');
INSERT INTO "abulsalam" VALUES(10,62,'10:62',' اګاه شئ! بېشكه د الله دوستان چې دي؛ پر هغوى باندې نه څه وېره شته او نه دوى غمژن كېږي');
INSERT INTO "abulsalam" VALUES(10,63,'10:63',' هغه كسان چې ایمان يې راوړى دى او ځان (له نافرمانیو) ساتي ');
INSERT INTO "abulsalam" VALUES(10,64,'10:64',' د همدوى لپاره په دنيايي ژوند كې زېرى دى او په اخرت كې (هم). د الله كلماتو لره هېڅ تبديلي نشته، همدغه ډېر ستر برى دى');
INSERT INTO "abulsalam" VALUES(10,65,'10:65',' او د دوى وینا دې تا غمژن نه كړي، بېشكه عزت ټول خاص د الله لپاره دى، هم هغه ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(10,66,'10:66',' خبردار شئ! بېشكه خاص د الله مملوك دي هغه څوك چې په اسمانونو كې دي او هغه څوك چې په ځمكه كې دي او پیروي نه كوي هغه كسان چې له الله نه پرته شریكان بلي، دوى پیروي نه كوي مګر د ګمان او نه دي دوى مګر دا چې اټكل چلوي');
INSERT INTO "abulsalam" VALUES(10,67,'10:67',' دى همغه ذات دى چې تاسو لپاره يې شپه جوړه كړې ده، د دې لپاره چې تاسو په دې كې په ارام شئ او ورځ يې روښانه (ګرځولې ده)، بېشكه په دې كې خامخا نښې دي د هغه قوم لپاره چې اوري');
INSERT INTO "abulsalam" VALUES(10,68,'10:68',' دوى وايي: الله (د ځان لپاره) اولاد نیولى دى، هغه پاك دى، هم دى غني (بې پروا) دى، خاص ده لره دي هغه څه چې اسمانونو كې دي او هغه څه چې په ځمكه كې دي تاسو سره په دې خبره هېڅ دلیل نشته، ایا تاسو په الله باندې هغه خبره ورتپئ چې پرې نه پوهېږئ؟');
INSERT INTO "abulsalam" VALUES(10,69,'10:69',' ته ووایه: بېشكه هغه كسان چې په الله باندې له ځانه دروغ تړي؛ دوى به خلاصى ونه مومي');
INSERT INTO "abulsalam" VALUES(10,70,'10:70',' په دنیا كې لږه فايده اخيستل دي، بیا خاص مونږ ته د دوى واپس راتلل دي، بیا به مونږ پر دوى د ډېر سخت عذاب مزه وڅكو، په سبب د دې چې دوى كفر كاوه');
INSERT INTO "abulsalam" VALUES(10,71,'10:71',' او ته دوى ته د نوح خبر ولوله، كله چې هغه خپل قوم ته وویل: اى زما قومه! كه چېرې پر تاسو باندې زما اوسېدل او د الله په ایتونو سره (تاسو ته) زما نصیحت كول سخت (او ګران) وي، نو ما خاص په الله باندې توكل كړى دى، نو تاسو له خپلو شریكانو سره د خپل كار پخه اراده وكړئ، بیا دې پر تاسو باندې خپله معامله پټه نه وي، بیا ما ته ضرر راورسوئ او ما ته مهلت (هم) مه راكوئ ');
INSERT INTO "abulsalam" VALUES(10,72,'10:72',' نو كه تاسو (زما له نصيحت نه) وګرځېدئ، پس ما له تاسو نه هېڅ مزدوري نه ده غوښتې، زما اجر يواځې پر الله دى او ما ته حكم شوى دى چې زه شم له مسلمانانو څخه');
INSERT INTO "abulsalam" VALUES(10,73,'10:73',' پس هغوى د ده تكذیب وكړ، نو مونږ ده ته او هغه چا ته چې له ده سره په بېړۍ كې (سواره) وو، نجات وركړ او مونږ دوى خلیفګان (ځاى ناستي) جوړ كړل او هغه كسان مو غرق كړل چا چې زمونږ د ایتونو تكذیب كړى و، نو ته وګوره د وېرول شویو انجام څنګه و؟');
INSERT INTO "abulsalam" VALUES(10,74,'10:74',' بیا مونږ له ده نه بعد خپلو قومونو ته نور رسولان ولېږل، نو هغوى ته يې څرګند دلیلونه راوړل، پس دوى داسې نه وو چې پر هغه څه يې ایمان راوړى وى چې له دې نه مخكې يې د هغه تكذیب كړى و، همدارنګه (په دې وجه) مونږ د زیاتي كوونكو پر زړونو مهر لګوو');
INSERT INTO "abulsalam" VALUES(10,75,'10:75',' بیا مونږ له دوى نه بعد موسٰی او هارون ولېږل په ښكاره معجزو سره فرعون او د هغه سردارانو ته، نو هغوى تكبر وكړ او هغوى مجرمان خلق وو');
INSERT INTO "abulsalam" VALUES(10,76,'10:76',' پس كله چې زمونږ له خوا نه دوى ته حق راغى، دوى وویل: بېشكه دا یقینًا ښكاره جادو دى');
INSERT INTO "abulsalam" VALUES(10,77,'10:77',' موسٰی وویل: ایا تاسو حق ته (جادو) وایئ كله چې تاسو ته راغى، ایا جادو دا دى؟، حال دا چې جادوګر خو برى نه مومي ');
INSERT INTO "abulsalam" VALUES(10,78,'10:78',' دوى وویل: ایا ته مونږ ته د دې لپاره راغلى يې چې مونږ له هغې (لارې) نه واړوي چې پر هغې مونږ خپل پلرونه موندلي دي او په ځمكه كې تاسو دواړو ته لويي (غلبه) حاصله شي؟ او مونږ پر تاسو دواړو ایمان راوړونكي نه یو');
INSERT INTO "abulsalam" VALUES(10,79,'10:79',' او فرعون وویل: تاسو ما ته هر ښه پوه (ماهر)جادوګر راولئ');
INSERT INTO "abulsalam" VALUES(10,80,'10:80',' نو كله چې جادوګر راغلل، موسٰی دوى ته وویل: وغورځوئ هغه څه چې تاسو يې غورځوونكي یئ');
INSERT INTO "abulsalam" VALUES(10,81,'10:81',' پس كله چې دوى (امساګانې او پړي) وغورځول، موسٰی وویل: هغه څه چې تاسو راوړي دي، واقعي جادو دى، بېشكه الله به يې ژر تباه (بې اثره) كړي، بېشكه الله د فساد كوونكو كار نه جوړوي');
INSERT INTO "abulsalam" VALUES(10,82,'10:82',' او الله په خپلو كَلِمو سره حق ثابتوي اګر كه مجرمان يې بد ګڼي');
INSERT INTO "abulsalam" VALUES(10,83,'10:83',' نو پر موسٰی ایمان رانه وړ مګر د هغه له قومه څو ځلمیانو، د فرعون او د هغوى د سردارانو څخه په دې وېره كې چې دوى به يې په فتنه كې واچوي او بېشكه فرعون په ځمكه كې سركشي كوونكى و او بېشكه هغه له حده تېرېدونكو څخه و');
INSERT INTO "abulsalam" VALUES(10,84,'10:84',' او موسٰی وویل: اى زما قومه! كه چېرې تاسو (داسې) یئ چې پر الله مو ایمان راوړى دى، نو خاص په هغه باندې توكل وكړئ كه چېرې تاسو مسلمانان یئ');
INSERT INTO "abulsalam" VALUES(10,85,'10:85',' پس دوى وویل: خاص په الله باندې مونږ توكل كړى دى، اى زمونږه ربه! مونږ د ظالمو خلقو لپاره فتنه مه جوړوه');
INSERT INTO "abulsalam" VALUES(10,86,'10:86',' او په خپل رحمت سره مونږ ته له كافرو خلقو نه نجات راكړه');
INSERT INTO "abulsalam" VALUES(10,87,'10:87',' او مونږ موسٰی او د هغه ورور ته وحي وكړه دا چې تاسو د خپل قوم لپاره په مصر كې څه كوټې مقررې كړئ او خپلې كوټې قبله وګرځوئ او تاسو لمونځ قايموئ او ته مومنانو ته زېرى وركړه');
INSERT INTO "abulsalam" VALUES(10,88,'10:88',' او موسٰی وویل: اى زمونږ ربه! بېشكه هم تا فرعون او د هغه سردارانو ته په دنيايي ژوند كې زینت او مالونه وركړي دي، اى زمونږ ربه! د دې لپاره چې دوى ستا له لارې نه (خلق) بې لارې كړي، اى زمونږ ربه! ته د دوى مالونه محوه كړه او د دوى پر زړونو سختي راوله، پس چې دوى ایمان رانه وړي تر هغه چې ډېر دردناك عذاب وویني');
INSERT INTO "abulsalam" VALUES(10,89,'10:89',' ويې ويل: یقینًا ستاسو د دواړو دعا قبوله كړى شوه، لهذا سم ولاړ اوسئ او هيڅكله هم د هغو خلقو د لارې پیروي مه كوئ چې نه پوهېږي');
INSERT INTO "abulsalam" VALUES(10,90,'10:90',' اومونږه بني اسرائیل له سمندر نه پورې وېستل، نو فرعون او د هغه لښكر د ظلم او زیاتي كولو لپاره په دوى پسې شول، تر دې چې كله دى غرقېدو راونیوه (په ډوبېدو شو)، ده وویل: ما ایمان راوړ پر دې (خبره) چې یقینًا شان دا دى چې نشته هېڅ لايق د عبادت مګر هغه ذات چې بني اسرائیلو پر هغه ایمان راوړى دى او زه له مسلمانانو څخه یم');
INSERT INTO "abulsalam" VALUES(10,91,'10:91',' ایا اوس (ته ایمان راوړې)؟! حال دا چې یقینًا له دې نه مخكې تا نافرماني كوله او ته له فساد كوونكو څخه وې');
INSERT INTO "abulsalam" VALUES(10,92,'10:92',' پس نن ورځ به مونږ ستا بدن پر لوړ ځاى واچوو، د دې لپاره چې ته د هغه چا لپاره چې له تا نه وروسته دي، نښه جوړ شې او بېشكه په خلقو كې ډېر زمونږ له ایتونو څخه یقینًا غافلان دي');
INSERT INTO "abulsalam" VALUES(10,93,'10:93',' او یقینًا یقینًا مونږه بني اسرائیلو ته د عزت استوګنه وركړه او مونږه دوى ته له پاكیزه څیزونو څخه روزي وركړه، نو دوى اختلاف ونه كړ تر هغه پورې چې دوى ته علم راغى، بېشكه ستا رب به د دوى په مینځ كې د قیامت په ورځ فیصله كوي په هغه څیز كې چې دوى په هغه كې اختلاف كاوه');
INSERT INTO "abulsalam" VALUES(10,94,'10:94',' نو كه چېرې ته د هغه (قرآن) په باره كې په شك كې يې چې مونږ تا ته نازل كړى دى، نو ته له هغو كسانو څخه پوښتنه وكړه چې له تا نه مخكې كتاب لولي، یقینًا یقینًا ستا د رب له جانبه تا ته حق راغلى دى، نو ته هیڅكله هم له شك كوونكو څخه مه كېږه');
INSERT INTO "abulsalam" VALUES(10,95,'10:95',' او هیڅكله هم مه كېږه له هغو كسانو څخه چې د الله د ایتونو تكذیب يې كړى دى، نو ګنې شې به ته له تاوانیانو څخه');
INSERT INTO "abulsalam" VALUES(10,96,'10:96',' بېشكه هغه كسان چې ستا د رب خبره پرې ثابته شوې ده، دوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(10,97,'10:97',' اګر كه دوى ته هره نښه راشي تر هغه پورې چې دوى ډېر دردناك عذاب وویني');
INSERT INTO "abulsalam" VALUES(10,98,'10:98',' نو ولې یو داسې كلى نه و چې ایمان يې راوړى وى، بیا هغه ته د هغه ایمان فايده رسولې وى غير د یونس له قوم نه، كله چې دوى ایمان راوړ (،نو) مونږ له دوى نه په دنيايي ژوند كې د رسوايۍ عذاب لرې كړ او دوى ته مونږ تر یو وخته پورې فايده وركړه');
INSERT INTO "abulsalam" VALUES(10,99,'10:99',' او كه چېرې ستا رب غوښتلى (، نو) خامخا ایمان به راوړى و هغه چا چې په ځمكه كې دي ټول په ټولو، ایا نو ته خلق مجبوروې تر هغه چې دوى مومنان شي؟');
INSERT INTO "abulsalam" VALUES(10,100,'10:100',' او د هېڅ نفس لپاره ممكن نه دي چې ایمان راوړي مګر په اذن (ارادې) د الله سره او هغه ( الله) عذاب پر هغو كسانو اچوي چې له عقل نه كار نه اخلي');
INSERT INTO "abulsalam" VALUES(10,101,'10:101',' ته (ورته) ووایه: تاسو وګورئ په اسمانونو او ځمكه كې څه (څه دلیلونه) دي او هغه قوم ته له ایتونو او وېروونكیو څخه فايده نه رسي چې ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(10,102,'10:102',' پس دوى انتظار نه كوي مګر په شان د هغو خلقو د بدو ورځو چې له دوى نه مخكې تېر شوي دي، ته (ورته) ووایه: پس تاسو انتظار كوئ، بېشكه زه (هم) له تاسو سره له انتظار كوونكو څخه یم');
INSERT INTO "abulsalam" VALUES(10,103,'10:103',' بیا مونږ خپلو رسولانو ته نجات وركوو او همدارنګه هغو كسانو ته چې ایمان يې راوړى دى، پر مونږ ثابته ده دا چې مونږ به مومنانو ته نجات وركوو');
INSERT INTO "abulsalam" VALUES(10,104,'10:104',' ته ووایه: اى خلقو! كه چېرې تاسو زما د دین په باره كې په شك كې یئ، نو زه خو د هغو څیزونو عبادت نه كوم چې تاسو يې له الله نه غير عبادت كوئ او لېكن زه د هغه الله عبادت كوم چې تاسو وفات كوي او ما ته حكم شوى دى چې زه شم له مومنانو څخه');
INSERT INTO "abulsalam" VALUES(10,105,'10:105',' او دا چې ته خپل مخ دین ته سم ودروه، په دې حال كې چې له باطله حق ته مايله يې او ته هیڅكله هم له مشركانو څخه مه كېږه');
INSERT INTO "abulsalam" VALUES(10,106,'10:106',' او له الله نه غير هغه شى مه رابله چې تا ته نه فايده دررسولى شي او نه تا ته ضرر دررسولى شي، نو كه چېرې ته دغه (كار) وكړې، نو بېشكه ته به په دغه وخت كې له ظالمانو څخه يې');
INSERT INTO "abulsalam" VALUES(10,107,'10:107',' او كه چېرې الله تا ته څه تكلیف درورسوي، نو له هغه نه غير هیڅوك هغه (تكلیف) لره لرې كوونكى نشته او كه چېرې هغه له تا سره د خیر اراده وكړي، نو د هغه فضل لره هېڅ منع كوونكى نشته، په خپلو بنده ګانو كې چا ته چې وغواړي وررسوي يې او هماغه ښه بخښونكى، بې حده رحم كوونكى دى ');
INSERT INTO "abulsalam" VALUES(10,108,'10:108',' ته ووایه: اى خلقو! یقینًا تاسو ته د خپل رب له جانبه حق راغى، نو هغه چا چې هدایت اختيار كړ، نو بېشكه همدا خبره ده چې هغه د خپل ځان لپاره هدایت حاصلوي اوڅوك چې بې لارې شي، نو بېشكه همدا خبره ده چې هغه د خپل ځان په ضرر ګمراه كېږي او زه پر تاسو باندې نګران نه یم');
INSERT INTO "abulsalam" VALUES(10,109,'10:109',' او ته متابعت كوه د هغه څه چې تا ته وحي كولى شي او صبر كوه تر هغه پورې چې الله فیصله وكړي او هغه تر ټولو غوره فیصله كوونكى دى');
INSERT INTO "abulsalam" VALUES(11,1,'11:1',' الف، لام، را، (دا) كتاب دى چې ایتونه يې محكم كړى شوي دي، بیا د ښه حكمت والا، پوره خبردار ذات له خوا نه په تفصیل سره بیان شوي دي');
INSERT INTO "abulsalam" VALUES(11,2,'11:2',' دا چې تاسو عبادت مه كوئ مګر يواځې د الله، بېشكه زه تاسو لپاره د هغه له جانبه (رالېږل شوى) وېروونكى او زېرى وركوونكى یم');
INSERT INTO "abulsalam" VALUES(11,3,'11:3',' او دا چې تاسو له خپل رب نه مغفرت وغواړئ، بیا هغه ته توبه وباسئ (، نو) هغه به تاسو ته د یوې مقررې نېټې پورې ډېره ښكلې فايده دركړي او هر فضل والا ته به د هغه فضل وركړي او كه چېرې تاسو وګرځېدئ، نو بېشكه زه پر تاسو باندې د ډېرې سترې ورځې د عذاب وېره لرم');
INSERT INTO "abulsalam" VALUES(11,4,'11:4',' خاص الله ته ستاسو واپس ورتله دي او هغه په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(11,5,'11:5',' خبردار شئ! بېشكه دوى خپلې سینې دوه قته كوي، د دې لپاره چې له هغه نه پټ شي، خبردار شي! كله چې دوى خپلې جامې اغوندي، هغه (الله) پوهېږي پر هغه څه چې دوى (يې) پټوي او پر هغه څه چې دوى (يې) څرګندوي، بېشكه هغه د سینو پر پټو خبرو (هم) ښه پوه دى');
INSERT INTO "abulsalam" VALUES(11,6,'11:6',' او په ځمكه كې هېڅ خوځنده (ذي روح) نشته مګر د هغه رزق خاص په الله باندې دى، او هغه د هغهٔ د اوسېدلو ځاى او د هغهٔ د سپارېدلو ځاى پېژني (دا) هر څه په واضح كتاب كې (لیك) دي');
INSERT INTO "abulsalam" VALUES(11,7,'11:7',' او دى هماغه ذات دى چې اسمانونه او ځمكه يې په شپږو ورځو كې پیدا كړي دي، په دې حال كې چې د هغه عرش په اوبو باندې و، د دې لپاره چې هغه تاسو وازمايي چې په تاسو كې د عمل په لحاظ څوك ډېر ښه دى او یقینًا كه ته (دې خلقو) ووايې چې بېشكه تاسو به له مرګ نه پس ژوندي كولى شئ (نو) هغه كسان به چې كافران شوي دي هرومرو لازمًا ووايي: نه دى دا مګر ښكاره جادو (باطل) ');
INSERT INTO "abulsalam" VALUES(11,8,'11:8',' او یقینًا كه چېرې مونږ له دوى نه عذاب تر یو شمېرل شوې مودې پورې وروسته كړو (،نو) دوى به ارومرو لازمًا وايي: څه شى دغه (عذاب) ایساروي؟ خبردار شئ! په هغې ورځ كې چې دوى ته هغه (عذاب) راشي (،نو) هغه به له دوى څخه ګرځولى نشي او له دوى نه به هغه څه چاپېره شي چې دوى به په هغه پورې استهزا كولې');
INSERT INTO "abulsalam" VALUES(11,9,'11:9',' او یقینًا كه چېرې مونږ پر انسان له خپل جانبه رحمت وڅكو، بیا مونږ هغه له ده نه راكش كړو (ترې محروم يې كړو، نو) بېشكه هغه (بیا) یقینًا ډېر نا امېده، ډېر ناشكره شي');
INSERT INTO "abulsalam" VALUES(11,10,'11:10',' او یقینًا كه چېرې مونږ په ده باندې نعمت وڅكو له هغه تكلیف نه وروسته چې ده ته رسېدلى و (،نو) ارومرو لازمًا به هغه وايي: زما څخه ټولې سختۍ لاړې، (بيا نو هغه یقینًا (او) بې شكه ډېر خوشالېدونكى وي، ډېر فخر كوونكى وي ');
INSERT INTO "abulsalam" VALUES(11,11,'11:11',' مګر هغه كسان چې صبر يې وكړ او نېك عملونه يې وكړل، دغه كسان (چې دي) د همدوى لپاره مغفرت او ډېر لوى اجر دى ');
INSERT INTO "abulsalam" VALUES(11,12,'11:12',' نو ښايي چې ته پرېښودونكى يې ځینې برخې د هغه (قرآن) لره چې تا ته وحي كولى شي او په ده سره دې خپله سینه تنګېدونكې ده (له دې وېرې) چې دوى به وايي: په ده باندې خزانه ولې نه ده نازله كړى شوې، یا له ده سره مَلَك (ولې نه دى) راغلى؟ بېشكه همدا خبره ده چې ته (يواځې) وېروونكى (او خبردار كوونكى) يې او الله په هر شي باندې نګران دى');
INSERT INTO "abulsalam" VALUES(11,13,'11:13',' ایا دوى وايي چې دا يې له خپله ځانه جوړ كړى دى؟ ته (ورته) ووایه: نو تاسو د ده په مثل لس سورتونه راوړئ، چې له خپله ځانه جوړ كړى شوي وي او له الله نه غير چې څوك كولى شئ راوغواړئ، كه چېرې تاسو رښتیني یئ ');
INSERT INTO "abulsalam" VALUES(11,14,'11:14',' نو كه چېرې دوى تاسو ته جواب درنه كړي (ستاسو دعا قبوله نه كړي)، نو تاسو پوه شئ چې یقینًا هغه د الله په علم سره نازل كړى شوى دى او دا چې نشته هېڅ لايق د عبادت مګر هم دى دى، نو ایا تاسو اسلام راوړونكي یئ؟');
INSERT INTO "abulsalam" VALUES(11,15,'11:15',' هر څوك چې (داسې) وي چې دنيايي ژوند او د ده ښايست غواړي (،نو) مونږ به په همدې (دنیا) كې هغو ته د خپلو عملونو بدله پوره وركړو، په داسې حال كې چې له دوى نه به په دې (دنیا) كې څه كمولى نشي ');
INSERT INTO "abulsalam" VALUES(11,16,'11:16',' دا هغه كسان دي چې د دوى لپاره په اخرت كې نشته مګر اور او هغه څه برباد شول چې دوى په دې (دنیا) كې كړي وو او باطل دي هغه عملونه چې دوى به كول ');
INSERT INTO "abulsalam" VALUES(11,17,'11:17',' ایا نو هغه څوك چې د خپل رب له جانبه په څرګند دلیل باندې وي او د هغه له جانبه (د ده په تاييد كې) په ده پسې یو شاهد هم راغلى وي، او له ده نه مخكې د موسٰى كتاب و، چې پیشوا او رحمت و، دغه كسان پر ده ایمان لري او له ډلو څخه چې څوك په ده سره كافر شو، نو اور د هغه د وعدې ځاى دى، نو ته د ده په باره كې په هېڅ شك كې مه كېږه، بېشكه هم دا ستا د رب له جانبه حق دى او لېكن اكثره خلق ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(11,18,'11:18',' او له هغه كس نه لوى ظالم څوك دى چې په الله باندې دروغ تپي، دغه كسان به د خپل رب په وړاندې پیش كړى شي، او ګواهان به ووايي: دا هغه كسان دي چې پر خپل رب يې دروغ تړلي وو، خبردار شئ! د الله لعنت په ظالمانو باندې دى');
INSERT INTO "abulsalam" VALUES(11,19,'11:19',' هغه كسان چې د الله له لارې نه منع كول كوي او په هغې (لاره) كې كوږوالى لټوي او له اخرت نه انكار كوونكي (هم) همدوى دي');
INSERT INTO "abulsalam" VALUES(11,20,'11:20',' دغه كسان كله هم په ځمكه كې (الله لره) عاجزه كوونكي نه دي، او د دوى لپاره له الله نه سوا هیڅوك مدد كوونكي نشته، دوى ته به دوه چنده عذاب وركولى شي، نه دوى اورېدل كولى شول او نه دوى لیدل كول');
INSERT INTO "abulsalam" VALUES(11,21,'11:21',' دا هغه كسان دي چې خپلو ځانونو ته يې تاوان رسولى دى او له دوى نه هغه څه ورك شول چې دوى به دروغ تړل');
INSERT INTO "abulsalam" VALUES(11,22,'11:22',' حقه خبره دا ده چې یقینًا هم دوى په اخرت كې تر ټولو زیات زیان كاران دي');
INSERT INTO "abulsalam" VALUES(11,23,'11:23',' بېشكه هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي او خپل رب ته يې عاجزي كړې ده، دغه كسان د جنت صاحبان دي، دوى به په هغه كې همېشه وي');
INSERT INTO "abulsalam" VALUES(11,24,'11:24',' د دواړو ډلو مثال د ړانده او كاڼه او (په مقابِل كې د) لیدونكي او اورېدونكي دى، ایا دغه دواړه ډلې د حال په لحاظ سره برابرېدى شي؟ ایا نو تاسو پند نه اخلئ؟');
INSERT INTO "abulsalam" VALUES(11,25,'11:25',' او یقینًا یقینًا مونږ نوح خپل قوم ته لېږلى و، (ورته ويې ویل) بېشكه زه تاسو لپاره ښكاره وېروونكى (راغلى) یم');
INSERT INTO "abulsalam" VALUES(11,26,'11:26',' چې تاسو له الله نه غير (د هیچا) عبادت مه كوئ، بېشكه زه پر تاسو باندې د ډېرې دردوونكې ورځې له عذاب نه وېرېږم');
INSERT INTO "abulsalam" VALUES(11,27,'11:27',' نو د هغه له قومه كافر شویو مشرانو وویل: مونږ تا نه وینو مګر زمونږ په شان یو بشر او مونږ نه وینو تا چې ستا پیروي كړې ده مګر هغو كسانو چې دوى زمونږ تر ټولو لاندې خلق دي، په سرسري فكر سره، تاسو لپاره مونږ په خپل ځان باندې هېڅ بهتري (غوره والى) نه وینو، بلكې مونږ پر تاسو باندې د دروغجنو ګمان كوو');
INSERT INTO "abulsalam" VALUES(11,28,'11:28',' هغه وویل: اى زما قومه! تاسو ما ته خبر راكړئ كه زه یم پر هغه څرګند دلیل باندې چې زما د رب له جانبه دى او هغه ما ته له خپل جانبه رحمت راكړى وي، بیا پر تاسو باندې هغه پټ كړى شوى وي، ایا مونږه په هغه باندې تاسو مجبوره كولى شو، په دې حال كې چې تاسو هغه لره بد ګڼونكي یئ');
INSERT INTO "abulsalam" VALUES(11,29,'11:29',' او اى زما قومه! زه له تاسو نه په دغه (تبلیغ) باندې مال نه غواړم، زما اجر يواځې په الله باندې دى او زه د هغو كسانو شړونكى نه یم چې ایمان يې راوړى دى، بېشكه دوى له خپل رب سره ملاقات كوونكي دي او لېكن زه تاسو وینم داسې قوم چې جهالت كوئ');
INSERT INTO "abulsalam" VALUES(11,30,'11:30',' او اى زما قومه! له الله نه به ما څوك بچ كړي كه زه دوى وشړم؟ ایا نو تاسو نصیحت نه قبلوئ!');
INSERT INTO "abulsalam" VALUES(11,31,'11:31',' او زه تاسو ته (دا) نه وایم چې زما سره د الله خزانې دي او نه دا (وايم) چې زه پر غیبو پوهېږم او نه (دا) وایم چې بېشكه زه مَلك یم او نه زه د هغو كسانو په حق كې چې ستاسو سترګي يې سپك ګڼي دا وایم چې الله به دوى ته له سره څه خیر ورنه كړي، الله ښه عالم دى پر هغو (خبرو) چې د دوى په نفسونو كې دي، بېشكه زه به په دغه وخت كې خامخا له ظالمانو څخه یم');
INSERT INTO "abulsalam" VALUES(11,32,'11:32',' دوى وویل: اى نوحه! یقینًا تا زمونږ سره جګړه وكړه، نو زمونږ (سره) جګړه دې ډېره زیاته كړه، لهذا ته مونږ ته هغه (عذاب) راوله چې ته يې زمونږ سره وعده كوې (او مونږ پرې وېروې)، كه ته له صادقانو څخه يې ');
INSERT INTO "abulsalam" VALUES(11,33,'11:33',' هغه وویل: يواځې الله هغه (عذاب) راولي كه ويې غواړي او تاسو عاجزه كوونكي نه یئ (الله لره)');
INSERT INTO "abulsalam" VALUES(11,34,'11:34',' او زما نصیحت (او خير خواهي) تاسو ته نفع نه دررسوي كه زه تاسو ته د نصیحت (او خير خواهي) كولو اراده هم وكړم، كه چېرې الله ستاسو د ګمراه كولو اراده لري، هم هغه ستاسو رب دى او خاص هغه ته به ورګرځولى شئ');
INSERT INTO "abulsalam" VALUES(11,35,'11:35',' بلكې دوى وايي: ده له ځانه دا (قرآن) جوړ كړى دى، ته ووایه: كه ما دا له ځانه جوړ كړى وي، نو خاص په ما باندې دى (انجام) زما (د) جرم او زه بري یم له هغو جرمونو نه چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(11,36,'11:36',' او نوح ته وحي وكړى شوه چې بېشكه شان دا دى چې ستا له قومه به (څوك هم) ایمان رانه وړي غير له هغو نه چې ایمان يې راوړى دى، نو مه غمجن كېږه پر هغو كارونو چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(11,37,'11:37',' او زمونږ د سترګو په وړاندې او زمونږ د وحي مطابق بېړۍ جوړه كړه او د هغو كسانو په باره كې له ما سره خبرې مه كوه چې ظلم (شرك) يې كړى دى، بېشكه دوى غرقېدونكي دي');
INSERT INTO "abulsalam" VALUES(11,38,'11:38',' او هغه (نوح) بېړۍ جوړوله، په داسې حال كې چې كله به هم په ده باندې د ده له قومه څه مشران تېرېدل (نو) په ده پورې به يې مسخرې كولې، هغه وویل: كه تاسو په مونږ پورې مسخرې كوئ، نو بېشكه مونږ به هم په تاسو پورې مسخرې وكړو، لكه څنګه چې تاسو مسخرې كوئ');
INSERT INTO "abulsalam" VALUES(11,39,'11:39',' نو ژر به تاسو هغه څوك وپېژنئ چې ورته به داسې عذاب راشي چې هغه به رسوا كړي او (هغه چې) پر هغه به دايمي عذاب راكوز شي');
INSERT INTO "abulsalam" VALUES(11,40,'11:40',' تر هغه پورې چې كله زمونږ حكم راغى او تنور راوخوټېده، مونږ وویل: په دې (بېړۍ) كې له هر قسمه (حيواناتو) نه (نر او ښځه) جوړه د دوو په شمېر كې سوره كړه او خپل اهل هم، (خو) غير له هغه چا نه چې پر هغه باندې خبرې سبقت كړى دى او هغه څوك هم (سواره كړه) چې ایمان يې راوړى دى او له ده سره ایمان نه و راوړى مګر ډېرو لږو (خلقو)');
INSERT INTO "abulsalam" VALUES(11,41,'11:41',' او هغهٔ وویل: په دې كې سواره شئ (او وایئ:) د الله په نامه سره د دې روانېدل او د دې ودرېدل دي، بېشكه زما رب یقینًا ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(11,42,'11:42',' دې به دوى په داسې چپو كې وړل لكه غرونه، او نوح خپل زوى ته اواز وكړ، په داسې حال كې چې هغه په یوې غاړه كې (له دوى نه) جدا (ولاړ) و: اى زما بچوړیه! زمونږ سره راسور شه او له كافرانو سره مه كېږه');
INSERT INTO "abulsalam" VALUES(11,43,'11:43',' هغه وویل: ژر به زه د داسې غره په سر كې ځاى ونیسم چې ما به له اوبو نه بچ كړي، هغه (نوح) وویل: نن ورځ د الله له حكم نه هیڅوك ساتونكى (بچ كوونكى) نشته مګر هغه چې دغه (الله) پرې رحم وكړي، او د دواړو په مینځ كې چپه حايله شوه، نو دى شه له غرق كړى شويو نه');
INSERT INTO "abulsalam" VALUES(11,44,'11:44',' او وویل شول: اى ځمكې! خپلې اوبه ونغړه (جذب كړه) او اى اسمانه! اوبه ورول بند كړه او اوبه كمې كړى شوې او كار پوره كړى شو او هغه (بېړۍ) پر جودي غر ودرېده او وویل شول: لرې والى دې وي (له رحمت نه) د ظلم كوونكو خلقو لپاره');
INSERT INTO "abulsalam" VALUES(11,45,'11:45',' او نوح خپل رب ته اواز (سوال) وكړ، نو ويې ويل:اى زما ربه! بېشكه زما زوى زما له اهله دى او بېشكه هم ستا وعده حقه ده او ته تر ټولو ښه فیصله كوونكى يې');
INSERT INTO "abulsalam" VALUES(11,46,'11:46',' ويې ويل:اى نوحه! بېشكه هغه (ستا زوى) ستا له (مومن) اهله ځنې نه دى، بېشكه هغه (خاوند د) ناكاره عمل دى، نو له ما نه د هغه څیز سوال مه كوه چې تا ته د هغه په باره كې څه علم نه وي، بېشكه زه تا ته نصیحت كوم (او تا منع كوم) له دې نه چې ته شې له جاهلانو نه ');
INSERT INTO "abulsalam" VALUES(11,47,'11:47',' هغه وویل: اى زما ربه! بېشكه زه په تا پورې پناه نیسم له دې نه چې له تا نه د هغه څه سوال وكړم چې ما ته د هغه په باره كې څه علم نه وي او كه ته ما ته بخښنه ونه كړې او پر ما رحم ونه كړې (،نو) شم به زه له تاوانیانو نه');
INSERT INTO "abulsalam" VALUES(11,48,'11:48',' وویل شو: اى نوحه! كوز شه په سلامتۍ سره زمونږ له جانبه او سره د هغو بركتونو چې پر تا باندې شوي دي او پر څو هغو ډلو باندې شوي دي له هغو ځنې چې له تا سره دي او نورې ډلې دي چې ژر به مونږ هغوى ته نفع وركړو، بیا به هغوى ته زمونږ له جانبه ډېر دردوونكى عذاب ورسېږي ');
INSERT INTO "abulsalam" VALUES(11,49,'11:49',' دغه د غیبو له خبرونو ځنې دي چې مونږ يې تا ته وحي كوو، له دې نه مخكې نه تهٔ پر دې عالِم وې او نه ستا قوم، نو صبر كوه، بېشكه ښه انجام د پرهېزګارانو لپاره دى');
INSERT INTO "abulsalam" VALUES(11,50,'11:50',' او (مونږ لېږلى و) قومِ عاد ته د هغوى ورور هود، هغهٔ وویل: اى زما قومه! تاسو د الله عبادت كوئ، تاسو لپاره له هغه نه غیر هېڅ حق معبود نشته، نه یئ تاسو مګر دروغ تړونكي');
INSERT INTO "abulsalam" VALUES(11,51,'11:51',' اى زما قومه! زه له تاسو نه په دې (تبلیغ) باندې څه مزدوري نه غواړم، زما اجر نه دى مګر پر هغه ذات چې زه يې پیداكړى یم، ایا نو تاسو له عقل نه كار نه اخلئ!');
INSERT INTO "abulsalam" VALUES(11,52,'11:52',' او اى زما قومه! له خپل رب نه مغفرت وغواړئ، بیا هغه ته رجوع وكړئ، هغه به پر تاسو باندې باران راخوشي كړي، چې په شېبو شېبو به ورېدونكى وي او ستاسو قوت ته به (نور) قوت ورزیات كړي او تاسو (له ایمان او توبې نه) مخ مه ګرځوئ، په دې حال كې چې مجرمان یئ');
INSERT INTO "abulsalam" VALUES(11,53,'11:53',' هغوى وویل: اى هوده! تا مونږ ته څه واضح دلیل نه دى راوړى او ستا د وینا له وجې مونږ هېڅ كله هم خپل خدایان پرېښودونكي نه یو او هېڅ كله هم مونږ پر تا ایمان راوړونكي نه یو');
INSERT INTO "abulsalam" VALUES(11,54,'11:54',' (ستا په باره كې) مونږ يواځې همدا وایو چې تا ته زمونږ په خدایانو كې چا څه ضرر دررسولى دى، هغهٔ وویل: بېشكه زه الله ګواه كوم او تاسو (هم) ګواه شئ چې یقینًا زه له هغو (خدایانو) نه بېزاره یم چې تاسو يې (له الله سره) شريكوئ');
INSERT INTO "abulsalam" VALUES(11,55,'11:55',' غیر له هغه (الله) نه، نو تاسو ټول ما ته تدبیرونه جوړ كړئ، بیا ما ته مهلت مه راكوئ');
INSERT INTO "abulsalam" VALUES(11,56,'11:56',' بېشكه ما پر الله باندې توكل كړى دى چې زما رب دى او ستاسو رب دى، نشته هېڅ یو خوځنده مګر هم دغه (الله) د هغه وچولي (د تندي وېښتانو) لره نیوونكى دى، بېشكه زما رب پر نېغه لار دى');
INSERT INTO "abulsalam" VALUES(11,57,'11:57',' نو كه تاسو مخ واړوئ، نو یقینًا ما تاسو ته هغه (پېغام) درورساوه چې زه په هغه سره تاسو ته رالېږل شوى وم او زما رب به له تاسو نه غیر بل قوم ستاسو پر ځاى راولي او تاسو به هغه (الله) ته هېڅ ضرر ونه رسولى شئ، بېشكه زما رب په هر شي باندې ښه نګران او ساتونكى دى');
INSERT INTO "abulsalam" VALUES(11,58,'11:58',' او كله چې زمونږ حكم (عذاب) راغى (،نو) مونږ هود ته او هغو كسانو ته چې له هغه سره يې ایمان راوړى و؛ له خپل جانب نه په رحمت سره نجات وركړ او مونږ هغوى له ډېر سخت عذاب نه خلاص كړل');
INSERT INTO "abulsalam" VALUES(11,59,'11:59',' او دغه عادیان دي چې د خپل رب له ایتونو نه يې انكار كړى و او د هغه (الله) د رسولانو نافرماني يې كړې وه او د هر سركشه عنادي د حكم تابع شوي وو');
INSERT INTO "abulsalam" VALUES(11,60,'11:60',' او په دوى پسې په دې دنیا كې لعنت ولګول شو او د قیامت په ورځ هم، خبردار شئ! بېشكه عادیان په خپل رب سره كافران شوي دي، خبردار شئ چې (د الله له رحمت نه) لرې والى دى، عادیانو ته چې د هود قوم دى');
INSERT INTO "abulsalam" VALUES(11,61,'11:61',' او (مونږ لېږلى و) ثمودیانو ته د هغوى ورور صالح، هغه (دوى ته) وویل: اى زما قومه! تاسو د الله عبادت كوئ، ستاسو لپاره له هغه نه غیر (بل) هېڅ حق معبود نشته، هم هغه تاسو له ځمكې نه پیدا كړي یئ او په دې كې يې تاسو اوسولي یئ، نو تاسو له ده نه مغفرت وغواړئ، بیا تاسو ده ته رجوع وكړئ، بېشكه زما رب ډېر نژدې دى، د دعا قبلوونكى دى');
INSERT INTO "abulsalam" VALUES(11,62,'11:62',' دوى وویل: اى صالحه، ته په مونږ كې مخكې له دې (دعوې د نبوت) نه امېد كړى شوى وې، (تا ته زمونږ هيلې وې) ایا ته مونږ د هغو (بتانو) له عبادت نه منع كوې چې زمونږ پلرونو به يې عبادت كاوه او بېشكه مونږ د هغه (توحید) په باره كې چې ته مونږ هغهٔ ته بلې یقینًا په اضطراب اچوونكي (پريشانه كوونكي) شك كې یو ');
INSERT INTO "abulsalam" VALUES(11,63,'11:63',' هغه وویل: اى زما قومه! تاسو ما ته خبر راكړئ! كه زه د خپل رب له جانبه په ښكاره دلیل باندې یم او هغهٔ له خپله جانبه ما ته رحمت (نبوت) راكړى وي، نو له الله نه به ما څوك خلاص كړي كه زه د هغه نافرماني وكړم، نو تاسو خو ما ته له نقصان نه غیر (بل څه) نه زیاتوئ');
INSERT INTO "abulsalam" VALUES(11,64,'11:64',' اى زما قومه! دا د الله اوښه ده چې ستاسو لپاره لویه نښه ده، نو تاسو دا پرېږدئ چې د الله په ځمكه كې خوراك (څر) كوي او دې ته هېڅ تكلیف مه رسوئ (ګنې) نو تاسو به ډېر نژدې عذاب راګېر كړي');
INSERT INTO "abulsalam" VALUES(11,65,'11:65',' نو دوى د هغې پړكي پرې كړل (او ويې وژله) نو هغه (ورته) وویل: تاسو په خپلو كورونو كې درې ورځې فايده واخلئ، دا داسې وعده ده چې هېڅ دروغ په كې نشته');
INSERT INTO "abulsalam" VALUES(11,66,'11:66',' نو كله چې زمونږ (د عذاب) حكم راغى، مونږ صالح ته او هغو كسانو ته چې له هغهٔ سره يې ایمان راوړى و؛ په خپل رحمت سره خلاصى وركړ او د هغې ورځې له رسوايۍ نه هم، بېشكه ستا رب، همغه ډېر قوت والا، ډېر غالب دى');
INSERT INTO "abulsalam" VALUES(11,67,'11:67',' او هغه كسان چې ظلم يې كړى و؛ خطرناكې چغې راونیول، نو سهار مهال په خپلو كورونو كې پړمخې مړه پراتهٔ وو');
INSERT INTO "abulsalam" VALUES(11,68,'11:68',' ګویاكې دوى په دې (كورونو) كې له سره اوسېدلي نه وو، خبردار شئ! بېشكه ثمودیان په خپل رب سره كافران شوي وو، اګاه شئ! د ثمودیانو لپاره (له رحمت نه) لرې والى دى');
INSERT INTO "abulsalam" VALUES(11,69,'11:69',' او یقینًا یقینًا زمونږ استازو ابراهیم ته زېرى راوړ، ويې ويل:(پر تا) سلام (اچوو) ابراهیم وویل: (پر تاسو) سلام، نو درنګ (وخت) يې تېر نه كړ چې رايې وړ وریت (سلامت كباب شوى) سخى');
INSERT INTO "abulsalam" VALUES(11,70,'11:70',' نو كله چې يې د هغوى لاسونه ولیدل چې دې (وریتې غوښې) ته نه اوږده كېږي، نو دوى يې نا اشنا (اجنبيان) وګڼل او له دوى نه يې په زړه كې وېره محسوس كړه، دوى وویل: ته مهٔ وېرېږه، بېشكه مونږ د لوط قوم ته رالېږل شوي یو (چې هلاك يې كړو)');
INSERT INTO "abulsalam" VALUES(11,71,'11:71',' او د ده ښځه ولاړه وه، نو ويې خندل، نو مونږ دې ته د اسحق زېرى وركړ او له اسحق نه پس د (لمسي) یعقوب (زېرى مو هم وركړ)');
INSERT INTO "abulsalam" VALUES(11,72,'11:72',' هغې وویل: اى زما تعجبه (او حيرانۍ)! ایا زه به بچى راوړم، حال دا چې زه زړه (بوډۍ) یم او دا زما خاوند دى زوړ، بېشكه دا خو ډېره عجیبه خبره ده');
INSERT INTO "abulsalam" VALUES(11,73,'11:73',' هغوى وویل: ایا ته د الله په امر تعجب كوې؟ د الله رحمت او د هغهٔ بركتونه دې پر تاسو وي اى اهلِ بیتو، بېشكه هغه (الله) ډېر ستايل شوى، د ډېرې لويي والا دى');
INSERT INTO "abulsalam" VALUES(11,74,'11:74',' نو كله چې له ابراهیم نه وېره لاړه او زېرى ورته راغى، (نو بیا يې) د لوط د قوم په حق كې زمونږ (له رسولانو) سره بحث كاوه');
INSERT INTO "abulsalam" VALUES(11,75,'11:75',' بېشكه ابراهیم یقینًا ډېر حلم والا، ډېر نرم زړى (او الله ته) رجوع كوونكى و');
INSERT INTO "abulsalam" VALUES(11,76,'11:76',' اى ابراهیمه! له دې نه تېر شه، بېشكه شان دا دى چې یقینًا ستا د رب حكم راغلى دى او بېشكه دوى چې دي، پر دوى داسې عذاب راتلونكى دى چې بېرته به ګرځولى نشي');
INSERT INTO "abulsalam" VALUES(11,77,'11:77',' او كله چې زمونږ استازي (ملايك) لوط ته راغلل، نو دى پر هغوى غمجن شو او د دوى (د راتلو) په وجه يې سینه تنګه شوه او ويې ويل: دا ډېره سخته ورځ ده');
INSERT INTO "abulsalam" VALUES(11,78,'11:78',' او ده ته يې خپل قوم راغى، چې د ده په طرف يې بې اختیاره منډې وهلې او دوى مخكې له دې نه (هم) د بدو كارونو كوونكي وو، ده وویل: اى زما قومه! دا زما لوڼه دي (په نكاح يې واخلئ) دا تاسو لپاره بېخي پاكې (او حلالې) دي، نو تاسو له الله نه ووېرېږئ او زما د مېلمنو په حق كې ما مه شرموئ، ایا په تاسو كې كوم یو پوه سړى نشته؟');
INSERT INTO "abulsalam" VALUES(11,79,'11:79',' دوى وویل: یقینًا یقینًا تا ته معلومه ده چې مونږ ته ستا په لوڼو كې هېڅ حق (رغبت) نشته او بېشكه تا ته خامخا هغه څه معلوم دي چې مونږ يې غواړو');
INSERT INTO "abulsalam" VALUES(11,80,'11:80',' ويې ويل:كاشكې واقعي ما ته ستاسو په مقابله كې قوت حاصل وى یا مې ډېر مستحكم پناه ځاى (خپل قوي قوم) ته پناه وړى شوى');
INSERT INTO "abulsalam" VALUES(11,81,'11:81',' (ملايكو) وویل: اى لوطه! مونږ ستا د رب استازي یو، دوى به تا ته له سره در ونه رسي، نو ته خپل اهل (كورنۍ) د شپې په یوه حصه كې بوځه او په تاسو كې دې هیڅوك هم بېرته (شا ته) نه ګوري، خو ستا له ښځې نه غير، بېشكه شان دا دى چې هغې ته هغه عذاب رسېدونكى دى چې هغوى ته رسېږي، بېشكه د دوى د وعدې وخت سهار دى، ایا سهار ډېر نژدې نه دى؟');
INSERT INTO "abulsalam" VALUES(11,82,'11:82',' نو كله چې زمونږ حكم راغى (نو) مونږ د هغه (كلي) برنى اړخ د هغهٔ لاندې اړخ وګرځاوه او پر هغه (كلي) مو د داسې كاڼو باران وواروه چې له خټو جوړ، په اور پخ شوي وو، لاندې باندې (پرله پسې) وو');
INSERT INTO "abulsalam" VALUES(11,83,'11:83',' ستا د رب په نیز په نښه شوي وو، او له ظالمانو (د مكې له مشركانو) نه هغه (كاڼي یا ښارونه) ډېر لرې نه دي');
INSERT INTO "abulsalam" VALUES(11,84,'11:84',' او (لېږلى و مونږ) مدین ته د هغوى ورور شعیب، هغه وویل: اى زما قومه! تاسو د الله عبادت كوئ، تاسو لپاره له هغه نه غیر (بل) هېڅ حق معبود نشته او تاسو پېمانه او تله مه كموئ، بېشكه زه تاسو په خیر (ښه حال) وینم او بېشكه زه پر تاسو باندې د چا پېرېدونكې ورځې له عذاب نه وېرېږم');
INSERT INTO "abulsalam" VALUES(11,85,'11:85',' او اى زما قومه! تاسو پېمانه او تله په انصاف سره پوره وركوئ او خلقو ته د هغوى څیزونه مه كموئ او په ځمكه كې ورانى كوونكي مه ګرځئ، فساد كوونكي ');
INSERT INTO "abulsalam" VALUES(11,86,'11:86',' د الله باقي پاتې كړى (رزق) تاسو لپاره ډېر غوره دى كه تاسو مومنان یئ او زه پر تاسو باندې څه نګران خو نه یم');
INSERT INTO "abulsalam" VALUES(11,87,'11:87',' هغوى وویل: اى شعیبه! ایا ستا لمونځ تا ته د دې حكم كوي چې مونږ هغه (څیزونه) پرېږدو چې زمونږ پلرونو به يې عبادت كاوه یا دا چې په خپلو مالونو كې چې مونږ څه كول غواړو (هغه پرېږدو)، بېشكه هم ته یقینًا ډېر حِلم والا، ډېر پوه يې');
INSERT INTO "abulsalam" VALUES(11,88,'11:88',' هغهٔ وویل: اى زما قومه! تاسو ما ته خبر راكړئ! كه زه یم پر ښكاره دلیل باندې د خپل رب له جانبه او ما ته يې له خپله جانبه ډېر ښكلى رزق راكړى وي (نو زه يې ولې نافرماني وكړم) او زه نه غواړم چې ستاسو مخالفت وكړم (او راشم) هغه كار ته چې تاسو له هغهٔ نه منع كوم، زه يواځې اصلاح كول غواړم، څومره چې زما وس وي او زما توفیق يواځې دالله په مدد سره دى، خاص په هغه باندې ما توكل كړى دى او خاص هغه ته رجوع كوم');
INSERT INTO "abulsalam" VALUES(11,89,'11:89',' او اى زما قومه! زما سره دښمني دې تاسو د دې حقدار ونه ګرځوي چې پر تاسو په مثل د هغه (عذاب) راشي چې د نوح پر قوم یا د هود پر قوم یا د صالح پر قوم راغلى و او له تاسو نه د لوط قوم ډېر لرې نه دى');
INSERT INTO "abulsalam" VALUES(11,90,'11:90',' او تاسو له خپل رب نه مغفرت وغواړئ، بیا هغه ته رجوع وكړئ، بېشكه زما رب ډېر مهربان، بې حده مینه كوونكى دى');
INSERT INTO "abulsalam" VALUES(11,91,'11:91',' دوى وویل: اى شعیبه! مونږ پر ډېرو هغو خبرو نه پوهېږو چې ته يې وايې او بېشكه مونږ خو تا په مونږ كې كمزورى وینو او كه چېرې ستا دا خپلوان نه وى ( چې مونږ سره په كفر كې شریكان دي) نو مونږ به تهٔ خامخا سنګسار كړى وى او ته پر مونږ باندې هېڅ عزتناك نه يې');
INSERT INTO "abulsalam" VALUES(11,92,'11:92',' هغه وویل: اى زما قومه! ایا زما خپلوان پر تاسو باندې له الله نه ډېر عزتناك (او مخور) دي او تاسو دا (الله) ګرځولى دى خپل وروسته، شا ته غورځول شوى، بېشكه زما رب د هغو كارونو چې تاسو يې كوئ؛ راګېروونكى دى');
INSERT INTO "abulsalam" VALUES(11,93,'11:93',' او اى زما قومه! تاسو پر خپل ځاى (او طریقې) عمل كوئ، یقینًا زه هم عمل كوونكى یم، ژر به تاسو وپېژنئ هغه څوك چې پر هغه به داسې عذاب راځي چې هغه به رسواكوي او هغه څوك (به هم وپېژنئ) چې دروغجن دى، او تاسو انتظار كوئ بېشكه زه (هم) له تاسو سره انتظار كوونكى یم ');
INSERT INTO "abulsalam" VALUES(11,94,'11:94',' او كله چې زمونږ حكم (د عذاب) راغى (،نو) مونږ شعیب ته او هغو كسانو ته چې له هغه سره يې ایمان راوړى و؛ خلاصى وركړ په رحمت سره له خپل جانب نه او هغه كسان چې ظلم يې كړى و؛ خطرناكې چغې ونیول، نو شول په خپلو كورونو كې پړمخې مړه پراته');
INSERT INTO "abulsalam" VALUES(11,95,'11:95',' ګویاكې دوى په دې (كورونو) كې نه وو اوسېدلي، اګاه شئ (د الله له رحمت نه) لرې والى دى مدین ته لكه څنګه چې ثمودیان لرې (او هلاك) شول');
INSERT INTO "abulsalam" VALUES(11,96,'11:96',' او یقینًا یقینًا مونږ موسٰى په خپلو(نهو) معجزو او ښكاره دلیل سره لېږلى و');
INSERT INTO "abulsalam" VALUES(11,97,'11:97',' فرعون او د هغهٔ سردارانو ته، نو دغه (سرداران) د فرعون د حكم تابع شول، حال دا چې د فرعون حكم له سره درست نه و');
INSERT INTO "abulsalam" VALUES(11,98,'11:98',' د قیامت په ورځ به د خپل قوم په مخكې شي، نو اور ته به يې داخل كړي او بد ځاى د ورتلو دى چې (دوى هغه ته) وروستلى شي');
INSERT INTO "abulsalam" VALUES(11,99,'11:99',' او په دوى پسې په دې دنیا كې لعنت ولګول شو او د قیامت په ورځ (به هم ورپسې وي)، بد دى هغه انعام چې (دوى ته) وركولى شي');
INSERT INTO "abulsalam" VALUES(11,100,'11:100',' دا د كلیو ځينې خبرونه (او احوال) دي چې مونږ يې تا ته بیانوو، ځینې په دغو (كليو) كې ولاړ دي او (ځینې يې) رېبل شوي دي');
INSERT INTO "abulsalam" VALUES(11,101,'11:101',' او مونږ پر دوى ظلم نه و كړى او لېكن دوى (پخپله) پر خپلو ځانونو ظلم وكړ، نو د دوى هغه خدایان چې دوى به له الله نه غير بلل هېڅ شى په كار رانغلل، كله چې ستا د رب حكم راغى او دغو (خدایانو) دوى ته له هلاكت نه غیر (نور څه) ورزیات نه كړل ');
INSERT INTO "abulsalam" VALUES(11,102,'11:102',' او په شان د دغو (نیولو) نیول د رب ستا دي كله چې هغه كلي نیسي، په داسې حال كې چې هغه (كلي) ظالم وي، بېشكه د هغهٔ (الله) نیول ډېر دردوونكي، ډېر سخت دي‰ ');
INSERT INTO "abulsalam" VALUES(11,103,'11:103',' بېشكه په دغو (ذكر شويو قصو) كې خامخا (د عبرت) لویه نښه ده د هغه چا لپاره چې د اخرت له عذاب نه وېرېږي، دا داسې ورځ ده چې په دې كې به خلق جمع كولى شي او دا داسې ورځ ده چې حاضري به ورته وشي');
INSERT INTO "abulsalam" VALUES(11,104,'11:104',' او مونږ دا (ورځ) نه وروسته كوو مګر د معلومې نېټې (د پوره كېدلو) لپاره');
INSERT INTO "abulsalam" VALUES(11,105,'11:105',' په كومه ورځ چې هغه (د قیامت هیبت) راشي (نو) هېڅ نفس به د هغه (الله) له اجازت نه پرته خبرې نه كوي، نو ځینې به په دوى كې بدبخته وي او (ځینې به) نېك بخته وي');
INSERT INTO "abulsalam" VALUES(11,106,'11:106',' نو هر چې هغه كسان دي چې بدبخته شوي دي، نو هغوى به (د جهنم) په اور كې وي، د دوى لپاره به په هغه كې (د خره په شان) سختې اوچتې رمباړې او ټیټې رمباړې وي');
INSERT INTO "abulsalam" VALUES(11,107,'11:107',' په داسې حال كې چې دوى به په هغه كې تل ترتله وي، څو چې اسمانونه او ځمكه وي مګر هغه چې ستا رب (څه) وغواړي (او زَمْهَریر ته يې بوځي)، بېشكه ستا رب د هغه كار كوونكى دى چې اراده يې وكړي');
INSERT INTO "abulsalam" VALUES(11,108,'11:108',' او پاتې شو هغه كسان چې نېكبخته شوي دي، نو په جنت كې به وي، چې تل ترتله به وي، څو چې اسمانونه او ځمكه وي مګر هغه چې ستا رب اراده وكړي (چې رضوان ته او د ځان ملاقات ته يې بوځي) (دغه) نه ختمېدونكې عطا ده');
INSERT INTO "abulsalam" VALUES(11,109,'11:109',' نو تهٔ په هېڅ شك كې مه كېږه د هغو په باره كې چې دا خلق يې عبادت كوي، دوى عبادت نه كوي مګر لكه څنګه چې پخوا د دوى پلرونو عبادت كاوه او بېشكه مونږ هرومرو دوى ته د دوى برخه كامله، بې له نقصانه پوره وركوونكي یو');
INSERT INTO "abulsalam" VALUES(11,110,'11:110',' او یقینًا یقینًا مونږ موسٰى ته كتاب وركړى و، نو (د خلقو له خوا نه) په ده كې اختلاف وكړى شو، او كه هغه كلمه (حكم) نه وى چې ستا د رب له جانبه ړومبى (ليكل) شوې ده (نو) خامخا به د دوى په مینځ كې فیصله شوې وى او یقینًا دوى د دې (قرآن) په باره كې خامخا په اضطراب اچوونكي شك كې دي');
INSERT INTO "abulsalam" VALUES(11,111,'11:111',' او بېشكه هره ډله (د مومنانو او كافرانو) كله چې وخت راشي (په قيامت كې، نو) خامخا لازماً به دوى ته د دوى رب د خپلو عملونو پوره پوره جزا (بدله) وركړي، بېشكه هغه (الله) پر هغو كارونو چې دوى يې كوي ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(11,112,'11:112',' نو ته ثابت قدم اوسه لكه څنګه چې تا ته حكم شوى دى او هغه څوك دې هم (ثابت قدم اوسي) چې ستا په ملګرتيا كې يې (الله ته) رجوع كړې ده او (اى خلقو) تاسو سركشي مه كوئ، بېشكه هغه (الله) د هغو كارونو چې تاسو يې كوئ؛ ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(11,113,'11:113',' او تاسو د هغو كسانو په طرف مه مايله كېږئ چې ظلم يې كړى دى، (ګنې) نو (د دوزخ) اور به تاسو ته درورسېږي، په داسې حال كې چې تاسو لپاره به له الله نه غیر هېڅ كارسازي (دوستان) نه وي، بیا به له تاسو سره مدد نه كولى شي');
INSERT INTO "abulsalam" VALUES(11,114,'11:114',' او ته لمونځ قايموه د ورځې په دوه طرفونو كې او د شپې په څه ګړیو كې، بېشكه نېكيانې بديانې لرې كوي، دا د ذكر كوونكو (نصیحت قبلوونكو) لپاره نصیحت دى');
INSERT INTO "abulsalam" VALUES(11,115,'11:115',' او ته صبر كوه، پس بېشكه الله د نېكي كوونكو اجر نه ضايع كوي');
INSERT INTO "abulsalam" VALUES(11,116,'11:116',' نو له تاسو نه مخكې په تېر شويو امتونو كې ولې داسې د عقل او فكر خاوندان نه وو چې يې (خلق) په ځمكه كې له فساد كولو نه منع كولى (چې عذاب پرې نه وى راغلى) خو (منع كوله) لږو خلقو له هغه چا نه چې په دغو (خلقو) كې مونږ (هغو ته) نجات وركړ، او پیروي وكړه هغو كسانو چې ظلم يې كړى و د هغې (لارې) چې په هغې كې اسوده كړى شوي وو او هغوى مجرمان وو');
INSERT INTO "abulsalam" VALUES(11,117,'11:117',' او ستا رب له سره داسې نه دى چې كلي په ظلم سره (ناحقه) هلاك كړي، په دې حال كې چې د هغو اوسېدونكي اصلاح كوونكي (مومنان) وي ');
INSERT INTO "abulsalam" VALUES(11,118,'11:118',' او كه ستا رب غوښتلى خامخا به يې خلق ټول یو امت ګرځولي وو او دوى به همېشه اختلاف كوونكي وي');
INSERT INTO "abulsalam" VALUES(11,119,'11:119',' مګر هغه څوك چې ستا رب پرې رحم وكړي او د دغه (اختلاف او رحمت) لپاره يې دوى پیدا كړي دي او ستا د رب خبره پوره شوې ده چې خامخا هرومرو به زه جهنم له پېریانو او انسانانو ټولو نه ضرور ډكوم');
INSERT INTO "abulsalam" VALUES(11,120,'11:120',' او مونږ تا ته د رسولانو له خبرونو ځنې ټول هغه څه بیانوو چې په هغو سره مونږ ستا زړه كلكوو او تا ته په دې (خبرونو) كې راغلى دى حق او نصیحت او یادونه لپاره د مومنانو');
INSERT INTO "abulsalam" VALUES(11,121,'11:121',' او ته هغو كسانو ته چې ایمان نه راوړي، ووایه: تاسو پر خپل ځاى (او حال) عمل كوئ، بېشكه مونږ هم عمل كوونكي یو');
INSERT INTO "abulsalam" VALUES(11,122,'11:122',' او تاسو انتظار كوئ، بېشكه مونږ هم انتظار كوونكي یو');
INSERT INTO "abulsalam" VALUES(11,123,'11:123',' او خاص الله لره دى د اسمانونو او ځمكې (علمِ) غیب او خاص هم ده ته (د بنده ګانو) ټول كارونه بېرته ورګرځولى شي، نو تهٔ د ده عبادت كوه او پر ده توكل كوه او ستا رب له سره غافله نه دى له هغو كارونو نه چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(12,1,'12:1',' الف، لام، را، دا د واضح كتاب ایتونه دي');
INSERT INTO "abulsalam" VALUES(12,2,'12:2',' بېشكه مونږ دا نازل كړى دى، په داسې حال كې چې عربي قرآن دى، د دې لپاره چې تاسو پوه شئ');
INSERT INTO "abulsalam" VALUES(12,3,'12:3',' (اى نبي!) مونږ تا ته تر ټولو ښه بیان (قصه) اوروو، د دې قرآن په ذريعې سره چې مونږ تا ته وحي كړى دى، او بېشكه شان دا دى چې ته د دې (قرآن له نازلېدلو) نه مخكې خامخا له ناخبرو ځنې وې');
INSERT INTO "abulsalam" VALUES(12,4,'12:4',' (یاد كړه هغه وخت) كله چې یوسف خپل پلار ته وویل: اى زما پلار جانه! بېشكه ما (په خوب كې) یوولس ستوري او لمر او سپوږمۍ ولیدل، ما دوى ولېدل چې ما ته سجده كوونكي وو');
INSERT INTO "abulsalam" VALUES(12,5,'12:5',' (یعقوب) وویل: اى زما خوږه بچيه! خپل (دا) خوب خپلو وروڼو ته مه بیانوه، (ګنې) بیا به دوى تا ته څه چل جور كړي، څه چل جوړول، بېشكه شیطان د انسان لپاره ښكاره دښمن دى');
INSERT INTO "abulsalam" VALUES(12,6,'12:6',' او همدارنګه ستا رب به تا غوره كړي او تا ته به د خبرو(خوبونو) څه اصل حقیقت (تعبیر كول) وښیي او په تا باندې او د یعقوب په كورنۍ به خپل نعمت پوره كړي لكه چې هغه پوره كړى و دا (نعمت) له تا نه مخكې ستا په دوو پلرونو (نیكه او ورنیكه) ابراهیم او اسحق باندې، بېشكه ستا رب ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(12,7,'12:7',' یقینًا یقینًا په یوسف او د هغه په وروڼو كې د تپوس كوونكو لپاره ډېرې نښې دي');
INSERT INTO "abulsalam" VALUES(12,8,'12:8',' كله چې هغوى (په خپل مینځ كې) وویل: خامخا یوسف او د هغه سكنى ورور زمونږ پلار ته زمونږ نه زیات محبوب دي، حال دا چې مونږ یوه قوتناكه ډله یو، بېشكه زمونږ پلار خامخا په ښكاره خطا كې (پروت) دى');
INSERT INTO "abulsalam" VALUES(12,9,'12:9',' تاسو یوسف قتل كړئ یا هغه په كومې (لرې نامعلومه) ځمكه كې وغورځوئ چې ستاسو د پلار توجه يواځې تاسو ته خالصه شي او له دې (كاره) وروسته به تاسو نېك خلق شئ');
INSERT INTO "abulsalam" VALUES(12,10,'12:10',' په دوى كې یو ویونكي وویل: تاسو یوسف مه وژنئ او (بلكې) هغه د ګمنامي كوهي په تیاره بېخ كې وغورځوئ چې په تېرېدونكو (لارویانو) كې به يې ځینې (څوك) واخلي كه تاسو كوونكي یئ (د هغه كار چې ستاسو مقصد پرې پوره كېږي)');
INSERT INTO "abulsalam" VALUES(12,11,'12:11',' دوى وویل: اى زمونږ پلاره! څه باعث (او سبب) دى تا لره چې ته پر مونږ د یوسف باره كې اعتبار نه كوې، حال دا چې مونږ د ده یقینًا خیر غوښتونكي یو');
INSERT INTO "abulsalam" VALUES(12,12,'12:12',' ته دى له مونږ سره سبا ولېږه چې ښې ډېرې مېوې وخوري (مزې وكړي) او لوبې وكړي او بېشكه مونږ د ده خامخا ساتونكي یو');
INSERT INTO "abulsalam" VALUES(12,13,'12:13',' هغه وویل: بېشكه زه چې یم، یقینًا ما دا خبره غمجنوي چې تاسو دى بوځئ او زه له دې نه وېرېږم چې دا (یوسف) به لیوه (شرمښ) وخوري، په داسې حال كې چې تاسو به له ده نه غافلان یئ');
INSERT INTO "abulsalam" VALUES(12,14,'12:14',' هغوى وویل: قسم دى كه دى (رانه) لیوه وخوري، حال دا چې مونږ یوه قوتناكه ډله یو (،نو) بېشكه مونږ به په دغه وخت كې خامخا تاوانیان (او هلاكېدونكي) یو');
INSERT INTO "abulsalam" VALUES(12,15,'12:15',' نو كله چې دوى هغه بوته او پر دې خبره يې اتفاق وكړ چې دى د (ګمنامي) كوهي په تیاره بېخ كې وغورځوي او مونږ ده ته (یوسف ته) وحي وكړه چې قسم دى چې ته به خامخا ضرور دوى ته د دوى د دې كار خبر وركړې، په دې حال كې چې دوى به نه پوهېږي (چې ته یوسف يې)');
INSERT INTO "abulsalam" VALUES(12,16,'12:16',' او دوى خپل پلار ته بېګاه ته (په تیاره كې) راغلل چې ژړل يې (په دروغو سره)');
INSERT INTO "abulsalam" VALUES(12,17,'12:17',' ويې ويل: اى زمونږ پلاره! بېشكه مونږ لاړو مسابقه مو سره كوله او مونږ یوسف له خپلو اسبابو سره پريښى و، نو هغه شرمخ وخوړ او ته پر مونږ باندې باور كوونكى نه يې اګر كه مونږ رښتیني یو');
INSERT INTO "abulsalam" VALUES(12,18,'12:18',' او دوى د هغه د كمیس له پاسه دروغجنه وینه لګولې راوړه، یعقوب وویل: (دا دروغ دي) بلكې تاسو ته ستاسو نفسونو (دا بد) كار ښايسته كړى دى، نو (زما كار) صبر جمیل دى او د هغو خبرو په هكله خاص له الله نه مدد غوښتلى شي چې تاسو يې بیانوئ');
INSERT INTO "abulsalam" VALUES(12,19,'12:19',' او قافله راغله، نو دوى خپل اوبه راوړونكى ولېږه، نو هغه خپله بوكه (په كوهي كې) ورخوشې كړه (نو یوسف دغې بوكې پورې ونښت، چې يې ولیده، نو) ويې ويل:اى زما خوشحالۍ! دا خو (ډېر ښكلى) هلك دى او دوى هغه د تجارت د مال په توګه پټ كړ او الله پر هغو كارونو ښه عالم دى چې دوى كول');
INSERT INTO "abulsalam" VALUES(12,20,'12:20',' او دوى هغه په ډېر كم قیمت خرڅ كړ، چې څو شمېرل شوې روپۍ وې او دوى د هغه په حق كې له بې رغبتو څخه وو');
INSERT INTO "abulsalam" VALUES(12,21,'12:21',' او هغه سړي چې دى يې له مصره اخيستى و، خپلې ښځې ته وویل: د ده استوګنه عزتمنه ساته (دى په عزت سره ساته)، امېد دى چې دى به مونږ ته فايده راورسوي یا به دى مونږ زوى ونیسو او همدارنګه مونږ یوسف ته په ځمكه كې ځاى (او قدرت) وركړ او د دې لپاره چې مونږ هغه ته د خبرو څه تاویل (او تعبیر) وښیو او الله پر خپل كار غالب دى او لېكن اكثره خلق نه پوهېږي');
INSERT INTO "abulsalam" VALUES(12,22,'12:22',' او كله چې هغه خپل قوت ته ورسېده (، نو) مونږ هغهٔ ته حكم او علم وركړ او همداسې مونږ نېكي كوونكو ته بدله وركوو');
INSERT INTO "abulsalam" VALUES(12,23,'12:23',' او هغې ښځې، چې دا د هغې په كور كې و، هغې له ده نه له هغې سره د بدكارۍ مطالبه وكړه او (ټولې) دروازى يې كلكې پورې كړې او ويې ويل:هله، ته زر راشه! هغهٔ وویل: زه په الله پورې پناه نیسم، بېشكه دا (عزیز) زما مالك دى، هغه زما استوګنه ښه كړې ده، بېشكه شان دا دى چې ظالمان فلاح (او خلاصى) نه مومي');
INSERT INTO "abulsalam" VALUES(12,24,'12:24',' او یقینًا یقینًا هغې دهٔ ته قصد كړى و، او ده به هغې ته قصد كړى و كه چېرې ده د خپل رب دلیل نه وى لیدلى، همداسې (مونږ یوسف وساته) د دې لپاره چې مونږ له هغه نه بد او ناكاره (بې حیا) فعل وګرځوو (واړوو)، بېشكه دغه (یوسف) زمونږ له غوره كړى شويو بنده ګانو څخه دى');
INSERT INTO "abulsalam" VALUES(12,25,'12:25',' او دواړو دروازې ته سره منډې شروع كړې او هغې د ده كمیس له شا طرف نه څیرې كړ او دواړه د هغې له خاوند سره دروازي ته نژدې مخامخ شول، هغې وویل: نه ده سزا د هغه چا چې ستا له اهل سره يې د بدۍ اراده وكړه مګر دا چې هغه بندي (قيد) كړى شي یا ( د هغهٔ لپاره) ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(12,26,'12:26',' هغهٔ وویل: هم دې له ما نه د بدكارۍ غوښتنه كړې ده، او د هغې د كورنۍ یو شاهد شاهدي وركړه (داسې چې) كه د ده كمیس له مخامخ اړخ نه څيرې شوى وي، نو هغې رښتیا ویلي دي او دا له دروغجنانو څخه دى');
INSERT INTO "abulsalam" VALUES(12,27,'12:27',' او كه د ده كمیس له شا اړخ نه څیرې شوى وي، نو دې دروغ ویلي دي او هغه (یوسف) له رښتینو څخه دى');
INSERT INTO "abulsalam" VALUES(12,28,'12:28',' نو كله چې هغه (عزیز) د ده كمیس ولیده چې له شا اړخ نه څیرې شوى دى، ويې ويل: بېشكه (اى ښځو) دا ستاسو له فریبونو څخه دى، بېشكه ستاسو فریب (او چل) ډېر لوى دى');
INSERT INTO "abulsalam" VALUES(12,29,'12:29',' اى یوسفه! له دې خبرې نه مخ واړوه او (اى ښځې) ته د خپلې ګناه لپاره مغفرت وغواړه، بېشكه هم تهٔ له خطا كارانو ځنې وې');
INSERT INTO "abulsalam" VALUES(12,30,'12:30',' او په ښار كې ښځو وویل: د عزیز ښځه له خپل غلام نه د بدكارۍ مطالبه كوي، یقینًا د مینې په لحاظ سره هغهٔ، د دې د زړه پرده څیرې كړې ده (د دې ورسره سخته مينه ده)، بېشكه مونږ هغه خامخا په ښكاره ګمراهۍ كې وینو');
INSERT INTO "abulsalam" VALUES(12,31,'12:31',' نو كله چې هغې د دوى مكر واورېده (، نو) دوى ته يې پېغام ولېږه او د دوى لپاره يې مجلس تیار كړ او په دوى كې يې هرې یوې ته چاړه وركړه او هغې (ده ته) وویل: دوى ته راووځه، نو كله چې دوى هغه ولیده (،نو) ډېر لوى ښايسته يې ومونده او خپل لاسونه يې پرې كړل او ويې ویل الله ته پاكي ده، دا خو بشر نه دى، دا نه دى مګر ډېر عزتمن مَلك');
INSERT INTO "abulsalam" VALUES(12,32,'12:32',' هغې وویل: دا هماغه (زلمى) دى چې تاسو زه د ده (له ده سره د مینې) په باره كې ملامته كړې ومه او یقینًا یقینًا ما له ده نه د ده د ځان غوښتنه كړې وه، نو ده ځان وساته او قسم دى كه ده هغه كار ونه كړ چې زه يې ده ته حكم كوم (، نو) خامخا هرومرو به دى بندي كړى شي او خامخا هرومرو به دى شي له ذلیلانو نه');
INSERT INTO "abulsalam" VALUES(12,33,'12:33',' هغهٔ وویل: اى زما ربه! بندي خانه ما ته ډېره خوښه ده له هغه كار نه چې دوى ما هغه ته بلي او كه ته زما څخه د دوى مكر و فریب وانه ړوې (، نو) زه به دوى ته مايله شم او شم به له جاهلانو نه');
INSERT INTO "abulsalam" VALUES(12,34,'12:34',' نو خپل رب يې د ده دعا قبوله كړه، نو له ده نه يې د دوى مكر و فریب وګرځاوه، بېشكه هغه، هم هغه ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(12,35,'12:35',' بیا دوى (عزیز او د هغه كسانو) ته ښكاره شوه (صلاح يې وكړه)، وروسته له دې چې دوى (د یوسف د پاكۍ) نښې ولېدې، چې دوى به دى خامخا هرومرو تر یو وخته پورې بندي كړي');
INSERT INTO "abulsalam" VALUES(12,36,'12:36',' او له ده سره زندان ته دوه زلمي ننوتل، په دې دواړو كې یو وویل: بېشكه زه چې یم، زه ځان (په خوب كې) وینم، چې د شرابو لپاره انګور نچوړوم او بل وویل: بېشكه زه چې یم، ځان وینم، په دې حال كې چې د خپل سر له پاسه مې ډوډۍ بار كړې ده، چې له هغې نه مرغان خوراك كوي، مونږ د دې (خوبونو) پر تعبیر خبر كړه، بېشكه مونږ تا له نیكوكارانو څخه وینو');
INSERT INTO "abulsalam" VALUES(12,37,'12:37',' (یوسف) وویل: چې تاسو ته به هغه طعام لا نه وي راغلى چې تاسو ته هغه دركاوه شي مګر چې تاسو به د دې (خوبونو) پر تعبیر خبر كړم، مخكې له دې نه چې تاسو ته هغه راشي، دا (تعبیر) له هغه علم څخه دى چې زما رب ما ته ښودلى دى، بېشكه ما د هغه قوم دین پریښى دى چې په الله ایمان نه لري او په اخرت هم كافران دي');
INSERT INTO "abulsalam" VALUES(12,38,'12:38',' او ما د خپلو پلرونو د ملت پیروي كړې ده چې ابراهیم او اسحق او یعقوب دى، زمونږ لپاره له سره جايز نه دي چې له الله سره څه شى شریك كړو، دا د الله له فضله څخه دي پر مونږ باندې او په (نورو) خلقو باندې او لېكن زیاتره خلق شكر نه وباسي');
INSERT INTO "abulsalam" VALUES(12,39,'12:39',' اى د زندان دواړو ملګرو! ایا ګڼ او متفرق خدایان غوره دي یا كه هغه الله چې یو دى، ډېر قوي او غالب دى');
INSERT INTO "abulsalam" VALUES(12,40,'12:40',' تاسو له الله نه غیر عبادت نه كوئ مګر د داسې نومونو چې تاسو او ستاسو پلرونو ایښي (او نومولي) دي (او) الله د دوى (د الوهیت) په باره كې هېڅ دلیل نه دى نازل كړى، حكم يواځې الله ته خاص دى، هغه حكم كړى دى چې تاسو يواځې د هغه عبادت كوئ، همدا سم دین دى او لېكن زیاتره خلق نه پوهېږي');
INSERT INTO "abulsalam" VALUES(12,41,'12:41',' اى د زندان دواړو ملګرو! هر چي په تاسو كې یو دى، نو هغه خو به پر خپل مالك شراب څښوي او پاتې شو بل، نو هغه به په دار كولى شي، نو مرغان به د ده له سر نه (غوښې) خوري، هغه كار فیصله شوى دى چې تاسو د هغه په باره كې (له ما نه) تعبیر پوښتئ');
INSERT INTO "abulsalam" VALUES(12,42,'12:42',' او هغه كس ته يې وویل چې پر هغه يې دا یقین و چې یقینًا دى په دې دواړو كې خلاصى موندونكى دى: د خپل مالك په وړاندې ما یاد كړه، نو شیطان له هغه نه خپل مالك ته (د یوسف) یادول هېر كړل، نو هغه په زندان كې څو كاله پاتې شو');
INSERT INTO "abulsalam" VALUES(12,43,'12:43',' او بادشاه وویل: بېشكه زه چې یم، زه اوه چاغې څربه غواګانې وینم، چې اوه خوارې غواګانې يې خوري او اوه شنه وږي او نور وچ (اوه وږي وینم) اى سردارانو! تاسو ما ته زما د خوب تعبیر ووایئ، كه چېرې تاسو د خوب تعبیر كولى شئ');
INSERT INTO "abulsalam" VALUES(12,44,'12:44',' هغوى وویل: (دا) ګډوډ (تَخَیُّلي) خوبونه دي او مونږ د خیالي ګډو وډو خوبونو په تعبیر باندې عالمان نه یو');
INSERT INTO "abulsalam" VALUES(12,45,'12:45',' او هغه كس وویل چې په هغو دواړو كې هغه نجات موندلى و او له ډېرې مودې نه پس يې یاد شو: زه تاسو د دې (خوب) پر تعبیر خبروم، نو تاسو ما (یوسف ته) ولېږئ');
INSERT INTO "abulsalam" VALUES(12,46,'12:46',' اى یوسفه! اى ډېره رښتینه! ته مونږ ته تعبیر بیان كړه د اوو څربو غواګانو په باره كې چې اوه خوارې يې خوري او د اوو شنو وږو او نورو (اوو) وچو (وږو) په باره كې، د دې لپاره چې زه خلقو ته واپس لاړشم، د دې لپاره چې هغوى پوه شي');
INSERT INTO "abulsalam" VALUES(12,47,'12:47',' هغهٔ وویل: تاسو به اوه كاله پرله پسې په ډېر كوښښ سره كر كيله كوئ، نو څه چې تاسو ورېبئ هغه (غله) په خپلو وږو كې پرېږدئ، خو لږ له هغو نه چې تاسو يې خورئ');
INSERT INTO "abulsalam" VALUES(12,48,'12:48',' بیا به له دې (كلونو) نه پس ډېر سخت اوه (كاله) راځي چې هغه (غلې) به وخوري چې تاسو مخكې د دغو (كلونو) لپاره ذخیره كړي دي مګر لږ شان له هغه نه چې تاسو يې ساتئ');
INSERT INTO "abulsalam" VALUES(12,49,'12:49',' بیا به له دې (كلونو) نه پس یو كال راځي چې په هغه كې به (په باران سره) د خلقو فریاد رسي وشي او په ده كې به دوى (مېوې) نچوړي');
INSERT INTO "abulsalam" VALUES(12,50,'12:50',' او بادشاه وویل: تاسو هغه (یوسف) ما ته راولئ، نو كله چې ده ته استازى راغى، (نو) ده (ورته) وویل: خپل مالك ته بېرته لاړ شه، نو له هغه نه تپوس وكړه چې د هغو ښځو څه حال و چې خپل لاسونه يې پرې كړي وو، بېشكه زما رب د هغوى پر مكر او چل ښه پوه دى');
INSERT INTO "abulsalam" VALUES(12,51,'12:51',' هغه وویل: ستاسو څه حال (معامله) وه كله چې تاسو له یوسف نه د هغه د نفس غوښتنه كوله؟ هغوى وویل: الله ته پاكي ده، مونږ په هغه كې هېڅ بدي نه وه معلومه كړې، د عزیز ښځې وویل: اوس حقه (او رښتیا) خبره ظاهره شوه، ما له هغه نه مطالبه كړې وه او بېشكه هغه یقینًا له رښتینو څخه دى');
INSERT INTO "abulsalam" VALUES(12,52,'12:52',' دا د دې لپاره چې هغه (عزیز) پوه شي چې ما پسې شا له هغه سره خیانت نه دى كړى، او دا چې یقینًا الله د خیانتګرو مكر نه برابروي (سر ته يې نه رسوي)');
INSERT INTO "abulsalam" VALUES(12,53,'12:53',' او زه خپل نفس نه بري كوم(پاك ورته نه وايم)، یقینًا نفس خو په بدۍ سره ډېر حكم كوونكى دى، مګر هغه چې زما رب پرې رحم وكړي، یقینًا زما رب ښه، مغفرت كوونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(12,54,'12:54',' او باچا وویل: هغه ما ته راولئ چې زه يې د خپل ځان لپاره خاص مُصاحب كړم، نو كله چې يې له هغه سره خبرې وكړې، ويې ويل:بېشكه تهٔ همدا له نن ورځې نه زمونږ په نیز د مرتبې خاوند، (او) د اعتبار وړ يې');
INSERT INTO "abulsalam" VALUES(12,55,'12:55',' هغه وویل: تهٔ ما د دې ملك پر خزانو باندې مقرر كړه، بېشكه زه ښه ساتونكى، ښه پوه یم');
INSERT INTO "abulsalam" VALUES(12,56,'12:56',' او همدارنګه مونږ یوسف ته په ځمكه (د مصر) كې اقتدار وركړ، چې په دې (ملك) كې به يې چېرې چې غوښتل؛ ځاى نیوه (اوسېدلو)، مونږ خپل رحمت كوو پر چا چې مونږ وغواړو او مونږ د نېكي كوونكو اجر نه ضايع كوو');
INSERT INTO "abulsalam" VALUES(12,57,'12:57',' او یقینًا د اخرت اجر د هغو كسانو لپاره ډېر غوره دى چې ایمان يې راوړدى دى او پرهېزګاري يې كوله');
INSERT INTO "abulsalam" VALUES(12,58,'12:58',' او د یوسف وروڼه (له كِنعانه مصر ته د غلې دانې اخيستلو لپاره) راغلل، نو په ده باندې ورننوتل، نو ده هغوى وپېژندل او هغوى ده لره ناپېژندونكي وو');
INSERT INTO "abulsalam" VALUES(12,59,'12:59',' او كله چې هغهٔ دوى ته د دوى د سفر اسباب تیار كړل، ويې ويل:تاسو ما ته خپل هغه ورور راولئ چې ستاسو له پلاره دى، ایا تاسو نه وینئ چې بېشكه زه پېمانه (دانې) پوره وركوم او زه تر ټولو غوره مېلمه پال یم');
INSERT INTO "abulsalam" VALUES(12,60,'12:60',' نو كه چېرې تاسو هغه ما ته رانه وسته، بیا نو ستاسو لپاره زما سره هېڅ پېمانه نشته او (بیا) تاسو ما ته مه رانژدې كېږئ');
INSERT INTO "abulsalam" VALUES(12,61,'12:61',' هغوى وویل: مونږ به د هغهٔ په هكله د هغه له پلاره په كوښښ سره غوښتنه كوو او بېشكه مونږ خامخا (دا كار) كوونكي یو');
INSERT INTO "abulsalam" VALUES(12,62,'12:62',' او هغه خپلو ځلمیو ته وویل: د هغوى پنګه (سرمايه) د هغوى په بارونو كې كېږدئ، د دې لپاره چې دوى هغه وپېژني، كله چې دوى خپل اهل ته واپس لاړ شي، د دې لپاره چې دوى واپس راشي');
INSERT INTO "abulsalam" VALUES(12,63,'12:63',' نو كله چې دوى خپل پلار ته بېرته وګرځېدل، ويې ويل:اى زمونږ پلاره! زمونږ نه پېمانه (د بنيامين) منع كړى شوې ده، نو ته زمونږ سره زمونږ ورور ولېږه چې پېمانه راواخلو او بېشكه مونږ خامخا د ده حفاظت كوونكي یو');
INSERT INTO "abulsalam" VALUES(12,64,'12:64',' هغه وویل: ایا زه تاسو د ده باره كې پر تاسو اعتماد وكړم مګر په شان د هغه اعتماد چې ما پر تاسو له دې نه مخكې د ده د ورور باره كې كړى و، نو الله ډېر غوره ساتونكى دى او هغه تر ټولو زیات رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(12,65,'12:65',' او كله چې دوى خپل سامان پرانسته، دوى خپله پنګه وموندله چې دوى ته بېرته واپس كړى شوې ده، دوى وویل: اى زمونږ پلاره! مونږ (نور) څه غواړو؟، دا زمونږ پنګه ده چې مونږ ته بېرته واپس كړى شوې ده، او مونږ به د خپل اهل لپاره غله راوړو او خپل ورور به ساتو او د یو اوښ بار به زیات راوړو، دغه (وركړه د بادشاه لپاره يا دغه اضافي پېمانه) ډېره اسانه پېمانه ده');
INSERT INTO "abulsalam" VALUES(12,66,'12:66',' هغه وویل: هیڅكله به هم زه له تاسو سره هغه ونه لېږم تر هغه پورې چې تاسو ما ته د الله پخه وعده راكړئ چې تاسو به هغه خامخا لازمًا ما ته بېرته راولئ، مګر دا چې تاسو له هر جانب نه ګېر كړى شئ، نو كله چې هغوى ده ته خپله پخه وعده وركړه، هغه وویل: الله وكیل (او شاهد) دى پر هغه څه چې مونږ يې وایو');
INSERT INTO "abulsalam" VALUES(12,67,'12:67',' او هغه وویل: اى زما زامنو! تاسو (ټول) له یوې دروازې نه مه ورننوځئ او له بېلو بېلو دروازو نه ورننوځئ او زه له تاسو نه د الله (له فیصلې) نه هېڅ شى نشم لرې كولى، حكم نشته مګر خاص الله لره دى، خاص په هغه باندې ما توكل كړى دى اولازم دي چې توكل كوونكي يواځې په هغه باندې توكل وكړي');
INSERT INTO "abulsalam" VALUES(12,68,'12:68',' او كله چې دوى ننوتل له هغه ځایه چې دوى ته يې خپل پلار حكم كړى و، هغهٔ له دوى نه د الله (له فيصلې) نه هېڅ شى نشو لرې كولى، مګر دا د یعقوب په زړه كې یو حاجت و چې هغه يې ادا كړ، او بېشكه هغه یقینًا د علم خاوند و، په سبب د دې چې مونږ هغه ته تعلیم وركړى و او لېكن د خلقو اكثریت نه پوهېږي ');
INSERT INTO "abulsalam" VALUES(12,69,'12:69',' او كله چې دوى په یوسف باندې ورننوتل، (نو) هغه خپل ورور خپل ځان ته رانژدې كړ، ويې ويل:بېشكه زه چې یم، هم دا زه ستا ورور یم، نو ته پر هغو كارونو مه خفه كېږه چې دوى به كول');
INSERT INTO "abulsalam" VALUES(12,70,'12:70',' نو كله چې هغه، دوى ته د دوى سامانونه تیار كړل (، نو) د څښلو جام يې د خپل ورور په بار كې كېښود، بیا غږ كوونكي غږ وكړ! اى قافلې! بېشكه تاسو يقينًا غله یئ');
INSERT INTO "abulsalam" VALUES(12,71,'12:71',' هغوى وویل او دوى ته يې مخ راواړوه: تاسو څه شى ورك كړى دى؟');
INSERT INTO "abulsalam" VALUES(12,72,'12:72',' هغوى وویل: مونږ د بادشاه پېمانه وركه كړې ده، او د هغه چا لپاره چې دغه راوړي، د یو اوښ بار دى، او زه د دې ضامن یم');
INSERT INTO "abulsalam" VALUES(12,73,'12:73',' هغوى وویل: قسم پر الله، یقینًا یقینًا تاسو پوه شوي یئ چې مونږ د دې لپاره نه یو راغلي چې په دې ځمكه كې فساد وكړو، او مونږ خو كله هم غله نه یو تېر شوي');
INSERT INTO "abulsalam" VALUES(12,74,'12:74',' هغوى وویل: نو د هغه جزا به څه وي كه تاسو دروغجن وئ؟');
INSERT INTO "abulsalam" VALUES(12,75,'12:75',' دوى وویل: د هغهٔ جزا هغه كس دى د چا په بار كې چې دغه وموندل شي، نو هم دغه كس د هغه جزا دى (هغه به په كې نيولى شي)، همدارنګه مونږ ظالمانو ته جزا وركوو');
INSERT INTO "abulsalam" VALUES(12,76,'12:76',' نو د خپل ورور له بار نه مخكې يې د هغوى پر بارونو شروع وكړه، بیا يې دغه پېمانه د خپل ورور له بار نه راوښكله، همدارنګه مونږ د یوسف لپاره تدبیر وكړ، هغه (یوسف د دې حقدار) نه و چې د بادشاه په قانون كې خپل ورور راونیسي (او بندي يې كړي)، مګر دا چې الله وغواړي، چا ته چې مونږ وغواړو په درجو كې يې اوچتوو او د هر علم والا له پاسه لوى عالم دى');
INSERT INTO "abulsalam" VALUES(12,77,'12:77',' هغوى وویل: كه دا غلا كوي، نو (د حرانتيا خبره نه ده) یقینًا له ده نه مخكې د ده ورور (يوسف) هم غلا كړې ده، نو یوسف دا په خپل نفس كې پټه وساتله او دايې دوى ته ښكاره نه كړه، ويې ويل: ستاسو حال ډېر بد دى، او الله ښه عالم دى پر هغې خبره چې تاسو يې بیانوئ');
INSERT INTO "abulsalam" VALUES(12,78,'12:78',' دوى وویل: اى عزیزه! بېشكه د ده ډېر زوړ عمرخوړلى پلار دى، نو ته د ده پر ځاى په مونږ كې یو تن ونیسه، بېشكه مونږ تا له احسان كوونكو ځنې وینو');
INSERT INTO "abulsalam" VALUES(12,79,'12:79',' هغه وویل: د الله پناه غواړو (له دې خبرې نه) چې مونږ له هغه كس نه غير څوك ونيسو چې مونږ له هغه سره خپل سامان موندلى دى، بېشكه مونږ به په دغه وخت كې خامخا ظالمان یو');
INSERT INTO "abulsalam" VALUES(12,80,'12:80',' نو كله چې دوى له هغه (يوسف) نه نا امېده شول (، نو یوې خوا ته) بېل شول، په دې حال كې چې مشوره كوونكي وو، د دوى مشر وویل: ایا تاسو پر دې پوه نه یئ چې یقینًا ستاسو پلار له تاسو نه د الله پخه وعده اخيستې ده او له دې نه مخكې چې تاسو د یوسف په باره كې څه تقصیر كړى دى، نو زه خو به هیڅكله هم له دې ځمكې ونه خوځم تر هغه پورې چې ما ته زما پلار اجازت راكړي، یا الله زما لپاره فیصله وكړي او هغه تر ټولو غوره فیصله كوونكى دى');
INSERT INTO "abulsalam" VALUES(12,81,'12:81',' تاسو خپل پلار ته بېرته لاړ شئ، نو (ورته) ووایئ، اى زمونږ پلاره! بېشكه ستا زوى غلا كړې ده (ځكه نو هغه ونيول شو) او مونږ يواځې هغه څه بيانوو چې مونږ ته يې علم شوى دى او مونږ د غيبو ساتونكي (نګهبانان) خو نه وو');
INSERT INTO "abulsalam" VALUES(12,82,'12:82',' او ته له هغه كلي (والو) نه تپوس وكړه، چې مونږ په كې وو او له هغې قافلې نه (هم) چې مونږ په هغې كې راغلو او بېشكه مونږ لازمًا رښتیا ویونكي یو');
INSERT INTO "abulsalam" VALUES(12,83,'12:83',' هغه وویل: بلكې تاسو لپاره ستاسو نفسونو یو كار ښايسته كړى دى، نو (زما كار) صبر جمیل دى، امېد دى چې الله به ما ته هغوى ټول راولي، بېشكه هغه، هم هغه ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(12,84,'12:84',' او له هغوى نه يې مخ وګرځاوه او ويې ويل: اى زما غمه په (جدايۍ د) یوسف باندې! او د هغه دواړه سترګې له غمه تكې سپینې شوې، نو هغه (له غمه) ډك و');
INSERT INTO "abulsalam" VALUES(12,85,'12:85',' هغوى وویل: پر الله قسم دى چې ته به همېشه یوسف یادوې، تر هغه پورې چې ته په وېلې كېدو سره مرګ ته قریب شې یا ته له هلاكېدونكو نه شې');
INSERT INTO "abulsalam" VALUES(12,86,'12:86',' هغه وویل: بېشكه همدا خبره ده چې زه د خپلې پرېشانۍ او غم فرياد الله ته كوم، او زه د الله له جانبه پر هغه څه پوهېږم چې تاسو پرې نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(12,87,'12:87',' اى زما زامنو! تاسو لاړ شئ، نو د یوسف او د هغه د ورور لټون وكړئ او د الله له رحمت نه مه نا امېده كېږئ، بېشكه حقيقت دا دى چې د الله له رحمت نه كافر خو يواځې نا امېده كېږي');
INSERT INTO "abulsalam" VALUES(12,88,'12:88',' نو كله چې دوى پر هغه ورننوتل، دوى وویل: اى عزیزه! مونږ ته او زمونږ كورنۍ ته سختي رسېدلې ده او مونږ ناقصه (كمه) پنګه راوړې ده، نو ته مونږ ته پوره پېمانه راكړه او پر مونږ صدقه وكړه، بېشكه الله خیرات كوونكو ته بدله وركوي');
INSERT INTO "abulsalam" VALUES(12,89,'12:89',' ويې ويل: ایا تاسو پر هغه فعل عالمان یئ چې تاسو له یوسف او د هغه له ورور سره كړى و، كله چې تاسو ناپوهان وئ؟');
INSERT INTO "abulsalam" VALUES(12,90,'12:90',' ويې وویل: ایا یقینًا (او) واقعًا همدا ته یوسف يې؟ هغه وویل: هم زه یوسف یم او دغه مې سكه ورور دى، یقینًا الله پر مونږ احسان وكړ، بېشكه حقيقت دا دى چې څوك تقوىٰ اختیار كړي او صبر وكړي، نو بېشكه الله د نېكي كوونكو اجر نه ضايع كوي ');
INSERT INTO "abulsalam" VALUES(12,91,'12:91',' هغوى وویل: قسم پر الله چې یقینًا په رښتیا سره الله ته پر مونږ باندې غوره كړى يې او بېشكه مونږ خامخا خطا كار وو');
INSERT INTO "abulsalam" VALUES(12,92,'12:92',' هغه وویل: نن ورځ پر تاسو باندې هېڅ ملامتي نشته، الله دې تاسو وبخښي او هغه تر ټولو زیات رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(12,93,'12:93',' تاسو زما دا كمیس یوسئ، نو دا زما د پلار پر مخ واچوئ، هغه به بېرته بینا شي او تاسو، سره له خپل اهله ټول ما ته راشئ');
INSERT INTO "abulsalam" VALUES(12,94,'12:94',' او كله چې قافله (د مصر له حدودو) ووتله، د هغوى پلار وویل: بېشكه زه چې یم يقينًا د یوسف خوشبويي محسوسوم كه چېرې تاسو ما ته د بوډاوالي د كم عقلۍ نسبت ونه كړئ (نو زما تصدیق به وكړئ) ');
INSERT INTO "abulsalam" VALUES(12,95,'12:95',' (ده ته حاضرینو) وویل: بېشكه ته چې يې، خامخا په خپلې پخوانۍ خطا كې پروت يې');
INSERT INTO "abulsalam" VALUES(12,96,'12:96',' نو هغه وخت چې زېرى كوونكى څنګه راغى، نو د ده پر مخ يې هغه (كمیس) واچوه، نو دى بینا وګرځېده، هغه (یعقوب) وویل: ایا ما تاسو ته نه وو ویلي؟ بېشكه زه ( د الله په ښودنې سره) پر هغه څه پوهېږم چې تاسو پرې نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(12,97,'12:97',' هغوى وویل: اى زمونږ پلاره! زمونږ لپاره (له الله نه) زمونږ د ګناهونو بخښنه وغواړه، بېشكه مونږ خطا كار وو');
INSERT INTO "abulsalam" VALUES(12,98,'12:98',' هغه وویل: عنقریب به زه تاسو لپاره له خپل رب نه بخښنه وغواړم، بېشكه هغه، هم هغه، ښه بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(12,99,'12:99',' نو كله چې دوى پر یوسف ورننوتل، نو خپل مور و پلار يې خپل ځان ته رانژدې كړل او ويې ويل: تاسو مصر ته داخل شئ، په داسې حال كې چې په امن كې به يئ كه الله وغواړي');
INSERT INTO "abulsalam" VALUES(12,100,'12:100',' او خپل مور و پلار يې (له ځان سره) په تخت باندې پورته كړل، او دوى (ټول) ده ته پرېوتل سجده كوونكي او ده وویل: اى زما پلار جانه! دا زما د هغه خوب تعبیر دى چې مخكې مالېدلى و، یقینًا هغه زما رب رښتیا وګرځاوه، او یقینًا هغه پر ما احسان وكړ كله چې يې زه له جېل نه راووېستم او تاسو يې له میرې (صحرا) نه راوستئ، وروسته له دې چې شیطان زما او زما د وروڼو په مینځ كې جګړه غورځولې وه، بېشكه زما رب باریك تدبیر والا دى، د هغه (شي) لپاره چې اراده يې وكړي، بېشكه هغه، هم هغه، ښه عالم، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(12,101,'12:101',' اى زما ربه! یقینًا تا ما ته (د) څه (علاقې) بادشاهي راكړې ده، او تا ما ته د خوبونو د تعبیر علم راكړى دى، اى د اسمانونو او ځمكې پېدا كوونكیه! په دنیا او اخرت كې هم ته زما كارساز يې، ته ما ته وفات راكړه، په داسې حال كې چې مسلمان یم او ته ما له نېكانو صالحانو سره یو ځاى كړه ');
INSERT INTO "abulsalam" VALUES(12,102,'12:102',' دا د غیب له خبرونو ځنې دي چې هغه مونږ تا ته وحي كوو او ته له هغوى سره نه وې كله چې هغوى د خپل كار پخه اراده وكړه، په داسې حال كې چې دوى مكر (او فرېب) كاوه');
INSERT INTO "abulsalam" VALUES(12,103,'12:103',' او زیاتره خلق، اګر كه ته (د دوى پر ایمان راوړلو) حرص وكړې (بیا هم) هېڅكله ایمان راوړونكي نه دي');
INSERT INTO "abulsalam" VALUES(12,104,'12:104',' او ته له دوى نه په دې (تبلیغ د قرآن) باندې هېڅ اجر نه غواړې، نه دى دا مګر د ټولو خلقو لپاره (د الله له جانبه) نصیحت');
INSERT INTO "abulsalam" VALUES(12,105,'12:105',' او څومره ډېر دلیلونه دي په اسمانونو او ځمكه كې، چې دوى پرې تېرېږي، په داسې حال كې چې دوى له هغو نه مخ اړوونكي وي');
INSERT INTO "abulsalam" VALUES(12,106,'12:106',' او د دوى زیاتره پر الله ایمان نه لري، مګر په داسې حال كې چې دوى شرك كوونكي وي');
INSERT INTO "abulsalam" VALUES(12,107,'12:107',' ایا نو دوى په امن كې شوي دي له دې نه چې په دوى باندې د الله له عذاب نه پټوونكې (راګېروونكې) برخه راشي، یا په دوى باندې ناڅاپه قیامت راشي، په داسې حال كې چې دوى به نه پوهېږي');
INSERT INTO "abulsalam" VALUES(12,108,'12:108',' ته ووایه: دا زما لاره ده، د الله په طرف بلنه كوم، په ښه بیان او بصیرت سره، زه او هغه څوك چې زما پیروي كوي، او الله پاك دى، او زه له شرك كوونكو ځنې نه یم');
INSERT INTO "abulsalam" VALUES(12,109,'12:109',' او مونږ له تا نه مخكې (د رسولانو په طور) نه دي لېږلي مګر سړي چې مونږ به هغوى ته وحي كوله، چې د كلیو (ښارونو) اوسېدونكي وو، ایا نو دوى په ځمكه كې نه دي ګرځېدلي، پس چې لیدلي يې وى چې د هغو (مجرمو) كسانو انجام څنګه و چې له دوى نه مخكې وو، او یقینًا د اخرت كور د هغو كسانو لپاره ډېر غوره دى چې (له شرك او كفر نه) ځان ساتي، ایا نو تاسو له عقل نه كار نه اخلئ؟ ');
INSERT INTO "abulsalam" VALUES(12,110,'12:110',' تر هغه پورې چې رسولان به (د خلكو له ايمان راوړو نه) نا امېده شول او هغو (كافرو خلقو) به دا یقین وكړ چې بېشكه دغو (رسولان) چې دي، یقینًا دوى ته دروغ ویل شوي دي (،نو) دوى (رسولانو) ته به زمونږ مدد راغى، بیا به هغه چا ته نجات وركړى شو چې مونږ به غوښتل او زمونږ عذاب له مجرمو (كافرو) خلقو نه نشي دفع كېدلى');
INSERT INTO "abulsalam" VALUES(12,111,'12:111',' یقینًا یقینًا د دوى (د قصې) په بیان كې د خالصو عقلونو والاوو لپاره ښه عبرت دى، دغه (قرآن) داسې خبره بېخي نه ده چې له ځانه په دروغو جوړه كړى شوې وي او لېكن (دغه قرآن) د هغو (كتابونو) تصدیق دى چې له ده نه مخكې وو او د هر شي تفصیل (بیان) دى او هدایت او رحمت دى د هغه قوم لپاره چې (پر الله او د هغه پر رسول) ایمان لري');
INSERT INTO "abulsalam" VALUES(13,1,'13:1',' الف، لام، ميم، را، دا د كتاب ایتونه دي او هغه (كتاب) چې تا ته د خپل رب له جانبه نازل كړى شوى دى، حق دى او لېكن زیاتره خلق ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(13,2,'13:2',' الله هغه ذات دى چې اسمانونه يې اوچت كړي دي، بې له ستنو، چې تاسو هغه وینئ، بیا يې په عرش باندې برقرار شو، او ده لمر او سپوږمۍ مسخر كړي دي، هر یو يې تر مقررې نېټې پورې روان دى، هغه، د هر كار تدبیر كوي، دلیلونه ښه په تفصیل سره بیانوي، د دې لپاره چې تاسو د خپل رب پر ملاقات یقین وكړئ');
INSERT INTO "abulsalam" VALUES(13,3,'13:3',' او دى همغه ذات دى چې ځمكه يې غوړولې ده او په هغې كې يې پاخه غرونه ايښي او نهرونه يې روان كړي دي او له هر قسمه مېوو نه يې په دغې كې دوه دوه جوړې پېدا كړي دي، هغه په شپې سره ورځ پټوي، بېشكه په دې كې د هغو خلقو لپاره يقينًا نښې دي چې فكر (او غور) كوي');
INSERT INTO "abulsalam" VALUES(13,4,'13:4',' او په ځمكه كې څنګ په څنګ پیوستې ټوټې دي او د انګورو باغونه دي او فصلونه دي او د كجورو ونې دي چې له یوهٔ بېخه پورته شوي دي او ځینې ځان ته (جدا) بېخ لري، چې په یوهٔ قسم اوبو سره اوبه كولى شي او مونږ په هغو كې ځينو ته په نورو باندې په مېوه (او خوند) كې فضيلت وركوو، بېشكه په دې كې د هغو خلقو لپاره یقینًا نښې دي چې له عقل نه كار اخلي');
INSERT INTO "abulsalam" VALUES(13,5,'13:5',' او كه ته تعجب كوې، نو د دوى (دا) خبره ډېره عجیبه ده چې(وايي:) ایا چې كله مونږ خاورې شو، ایا نو مونږ به په رښتيا نوي تخلیق كې یو (بيا به له نوي سره پيدا كولى شو)؟ دوى همغه كسان دي چې په خپل رب سره كافران شوي دي او دغه كسان دي چې طوقونه به يې په غاړو كې وي او دغه كسان د اور ملګري دي، دوى به په هغه كې تل ترتله وي');
INSERT INTO "abulsalam" VALUES(13,6,'13:6',' او دوى له تا نه له نېكۍ (رحمت) نه مخكې بدي ژر غواړي او یقینًا له دوى نه مخكې (له عبرته ډك) عذابونه تېر شوي دي او بېشكه ستا رب د خلقو لپاره د هغوى د ظلم باوجود یقینًا د لوى مغفرت والا دى او بېشكه ستا رب یقینًا ډېر سخت عذاب والا دى');
INSERT INTO "abulsalam" VALUES(13,7,'13:7',' او هغه كسان چې كفر يې كړى دى، وايي: ولې په ده باندې د خپل رب له جانبه كومه نښه نه ده نازل كړى شوې؟ یقینًا همدا خبره ده چې ته وېروونكى يې او د هر قوم لپاره یو لار ښودونكى دى');
INSERT INTO "abulsalam" VALUES(13,8,'13:8',' الله پوهېږي پر هغه څه چې هره ښځه يې (په خپله خېټه كې) اوچتوي او پر هغه څه چې رحمونه يې كموي او پر هغه څه چې زیاتوي يې او هر شى له هغه سره په اندازې سره دى');
INSERT INTO "abulsalam" VALUES(13,9,'13:9',' د غیبو او د ښكاره و عالم دى، ډېر لوى، لوړ برتر دى');
INSERT INTO "abulsalam" VALUES(13,10,'13:10',' (د هغهٔ په وړاندې ټول) يو برابر دي، څوك چې په تاسو كې خبره ورو وكړي او څوك چې يې څرګنده وكړي او هغه څوك چې هغه په شپه كې پټېدونكى دى او په ورځ كې ظاهرېدونكى دى');
INSERT INTO "abulsalam" VALUES(13,11,'13:11',' د دغه (انسان) لپاره د ده له مخې او د ده له شا نه نوبت په نوبت په یو بل پسې راتلونكي (ملايك) دي چې د الله په امر سره د ده ساتنه كوي، بېشكه الله د هېڅ قوم حالت نه بدلوي تر هغه پورې چې هغوى (پخپله) د خپلو نفسونو حالت بدل كړي او كله چې الله یو قوم ته د بدۍ (او تكليف رسولو) اراده وكړي، نو د هغې لپاره هېڅ بېرته ګرځېدل نشته او د دوى لپاره له هغه نه غیر هیڅوك مددګار نشته');
INSERT INTO "abulsalam" VALUES(13,12,'13:12',' دى همغه ذات دى چې تاسو ته برېښنا د وېرولو او طمع دركولو لپاره درښیي او درنې ورېځې پیدا كوي');
INSERT INTO "abulsalam" VALUES(13,13,'13:13',' او رعد د هغه له حمد سره تسبیح وايي او ملايك (هم) د هغه له وېرې نه او هغه تندرونه لېږي، نو چا ته چې وغواړي دا ورته رسوي، په داسې حال كې چې دوى د الله په باره كې جګړه كوي او هغه ډېر سخت سزا والا دى');
INSERT INTO "abulsalam" VALUES(13,14,'13:14',' خاص د هغه لپاره حق بلل دي او هغه (معبودان) چې دوى يې له هغه نه پرته بلي؛ هغه د دوى هېڅ دعا نشي قبلولى مګر په شان د هغه كس چې اوبو ته خپل دواړه ورغوي (لاسونه) غځوونكى وي، د دې لپاره چې هغه د ده خولې ته راورسي، حال دا چې دغه (اوبه خولې د) ده ته رسېدونكې نه دي او د كافرانو دعا نه ده مګر بېخي په تاوان كې ده ');
INSERT INTO "abulsalam" VALUES(13,15,'13:15',' او خاص الله ته سجده كوي هغه څوك چې په اسمانونو او ځمكه كې دي په خوښه او په زور او د هغوى سيوري (هم سجده كوي) سبا او بېګاه');
INSERT INTO "abulsalam" VALUES(13,16,'13:16',' ته (دوى ته) ووایه: د اسمانونو او ځمكې رب څوك دى؟ ته ووایه: الله دى، (نو ورته) ووایه: ایا نو تاسو له هغه نه غير نور كار سازان (دوستان) جوړ كړي دي چې د خپلو ځانونو لپاره نه د نفعې اختیار لري او نه د ضرر (لرې كولو) ته ووایه: ایا ړوند او بینا سره برابر دي؟ بلكې (دا ورته ووایه:) ایا سره برابرې دي تیارې او رڼا، یا دوى د الله لپاره داسې شریكان مقرر كړي دي چې د هغه د پیدا كولو په شان پیدا كول يې كړي دي، بیا په دوى باندې پیدا كول مشتبه شوي دي؟ ته ووایه: الله د هر شي پیدا كوونكى دى او هم هغه یو دى، ډېر غالب دى');
INSERT INTO "abulsalam" VALUES(13,17,'13:17',' هغه له بره نه اوبه نازلې كړې، نو خوړونه په خپلې اندازې سره وبهېدل، نو سیلاب اوچت شوى ځګ راپورته كړ او له هغو څیزونو نه هم چې دوى پرې په اور كې د ګاڼې، یا سامان جوړولو لپاره بلول كوي، د دې په مثل ځګ وي، همدارنګه الله (مثال د) حق او باطل بیانوي، نو هر چې ځګ دى، نو هغه بې كاره لاړ شي او هر چې هغه څیز دى چې خلقو ته فايده رسوي، نو هغه په ځمكه كې پاتې كېږي، همدارنګه الله مثالونه بیانوي');
INSERT INTO "abulsalam" VALUES(13,18,'13:18',' هم د هغو كسانو لپاره نېكه بدله ده چې د خپل رب حكم يې قبول كړى دى او هغه كسان چې د هغه حكم يې نه دى قبول كړى كه له دوى سره په رښتیا هر هغه څه وي چې په ځمكه كې دي ټول او د دغو په مثل نور هم له ده سره وي (، نو) خامخا به دوى هغه په فدیه كې وركړي، دغه كسان دي چې د دوى لپاره بد حساب دى او د دوى استوګنځاى جهنم دى او هغه بد ارام ځاى دى');
INSERT INTO "abulsalam" VALUES(13,19,'13:19',' ایا نو هغه څوك چې پر دې پوهېږي چې بېشكه هغه (قرآن) چې تا ته د خپل رب له جانبه نازل كړى شوى دى، حق دى، د هغه چا په شان دى چې هغه ړوند دى؟ یقینًا همدا خبره ده چې نصیحت د خالصو عقلونو والا اخلي');
INSERT INTO "abulsalam" VALUES(13,20,'13:20',' هغه كسان چې د الله وعده پوره كوي او پخه وعده نه ماتوي');
INSERT INTO "abulsalam" VALUES(13,21,'13:21',' او هغه كسان چې هغه څه وصل كوي چې الله د هغه د پيوست ساتلو حكم كړى دى او له خپل رب څخه وېرېږي او له بد حساب نه وېره كوي');
INSERT INTO "abulsalam" VALUES(13,22,'13:22',' او هغه كسان چې د خپل رب د مخ لټونې لپاره يې صبر كړى دى او لمونځ يې قايم كړى دى او له هغه څه نه يې چې مونږ ورته وركړي دي؛ په پټه او ښكاره انفاق كړى دى او هغوى په نېكۍ سره بدي دفع كوي دغه كسان چې دي د دوى لپاره د اخرت ښه انجام دى ');
INSERT INTO "abulsalam" VALUES(13,23,'13:23',' چې د تل اوسېدلو جنتونه دي، دوى به هغو ته داخلېږي، او هغوى به هم (داخلېږي) چې صالح (صلاحيت لري) وي د دوى له پلرونو څخه او د دوى له جوړو (ښځو) څخه او د دوى له اولادونو څخه او ملايك به په دوى باندې له هرې دروازې نه ورننوځي');
INSERT INTO "abulsalam" VALUES(13,24,'13:24',' (او وايي به) ستاسو د صبر كولو په سبب دې پر تاسو باندې سلام وي، نو (دا) د اخرت ښه انجام دى ');
INSERT INTO "abulsalam" VALUES(13,25,'13:25',' او هغه كسان چې د الله عهد ماتوي د هغه (عهد) له پخولو نه وروسته او هغه څه پرېكوي چې الله د هغه د وصلولو حكم كړى دى او په ځمكه كې فساد كوي، هم دغه كسان دي چې د دوى لپاره لعنت دى او هم د دوى لپاره د اخرت بدي ده ');
INSERT INTO "abulsalam" VALUES(13,26,'13:26',' الله روزي فراخوي چا ته چې وغواړي او تنګوي يې (چا ته چې يې وغواړي) او دوى پر دنيايي ژوند خوشحاله شوي دي او دنيايي ژوند د اخرت په مقابله كې نه دى مګر لږه متاع (سامان) ');
INSERT INTO "abulsalam" VALUES(13,27,'13:27',' او وايي هغه كسان چې كفر يې كړى دى: په ده باندې د خپل رب له جانبه كومه نښه (معجزه) ولې نه ده نازل كړى شوې؟ ته (ورته) ووایه: بېشكه الله چې چا ته وغواړي ګمراه كوي يې او خپل طرف ته هغه چا ته هدایت كوي چې رجوع وكړي');
INSERT INTO "abulsalam" VALUES(13,28,'13:28',' هغه كسان چې ایمان يې راوړى دى او د هغوى زړونه د الله په ذكر سره مطمئن كېږي، خبردار شئ! يواځې د الله په ذكر (ياد) سره زړونه مطمئن كېږي ');
INSERT INTO "abulsalam" VALUES(13,29,'13:29',' هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، د دوى لپاره ښه حال دى او ښه استوګنځاى دى');
INSERT INTO "abulsalam" VALUES(13,30,'13:30',' (اى نبي) همدارنګه ته مونږ په داسې امت كې لېږلى يې چې یقینًا له هغه نه مخكې ډېر امتونه تېر شوي دي، د دې لپاره چې ته دوى ته هغه (پېغام) ولولې چې مونږ تا ته وحي كړى دى، په داسې حال كې چې دوى په رحمٰن سره كفر كوي، ته ووایه: هغه زما رب دى، له هغه نه غير د عبادت هېڅ لايق نشته، خاص پر هغه باندې ما توكل كړى دى او خاص هم هغهٔ ته زما بېرته ورتله دي ');
INSERT INTO "abulsalam" VALUES(13,31,'13:31',' او كه بېشكه (دا) قرآن چې دى (داسې شي چې) په ده سره غرونه روان كړى شي، یا په ده سره ځمكه ټوټې كړى شي، یا په ده سره مړي په خبرو راوستل شي (بیا به هم دوى ایمان رانه وړي) بلكې امر (اختیار) ټول خاص الله لپاره دى، ایا نو هغه كسان چې ایمان يې راوړو (د دغو خلقو له ایمان نه) نا امېده شوي نه دي، (حال دا چې دغه مومنان پوهېږي چې) كه الله غوښتلى، نو یقینًا ټولو خلقو ته به يې هدایت كړى و او هغه كسان چې كفر يې كړى دى، همېشه به دوى ته د خپلو كړو عملونو په سبب زړه ټكوونكى (عذاب) رسېږي، یا به د دوى كورونو ته نژدې راكوزېږي، تر هغه پورې چې د الله وعده راشي، بېشكه الله د وعدې خلاف نه كوي ');
INSERT INTO "abulsalam" VALUES(13,32,'13:32',' او یقینًا یقینًا له تا نه په مخكې رسولانو پورې استهزا (او مسخرې) شوې وې، نو ما كافران شويو خلقو ته مهلت وركړ، بیا مې هغوى ونیول، نو (بیا) زما عذاب څنګه و؟');
INSERT INTO "abulsalam" VALUES(13,33,'13:33',' ایا نو هغه ذات چې ولاړ (او نګران) دى پر هر نفس باندې سره د هغه د هغو عملونو چې كوي يې (نو بل څوك د ده برابر كېدى شي؟) او دوى د الله لپاره شریكان مقرر كړل، ته (ورته) ووایه: تاسو د دوى نومونه (او صفتونه) ذكر كړئ، یا تاسو هغه (الله) ته د هغه څه خبر وركوئ چې هغه يې په ځمكه كې نه پېژنې، بلكې تاسو په ظاهره (بې دليله) وينا سره (تش په خولې، دوى شریكان جوړوئ) بلكې كافران شويو كسانو ته خپل مكرونه (او دروغ) ښايسته كړى شوي دي او له سمې لارې نه بند كړى شوي دي او هغه څوك چې الله يې ګمراه كړي، نو د هغه لپاره هېڅ هدایت وركوونكى نشته');
INSERT INTO "abulsalam" VALUES(13,34,'13:34',' شته دى د دوى لپاره عذاب په دنيايي ژوند كې او خامخا د اخرت عذاب ډېر سخت دى، او د دوى لپاره به د الله (له عذاب) نه هېڅ ساتونكى نه وي');
INSERT INTO "abulsalam" VALUES(13,35,'13:35',' د هغه جنت حال چې له متقیانو سره يې وعده شوې ده (دا دى چې) د هغهٔ له لاندې ولې بهېږي، د هغهٔ مېوې همېشنۍ دي او د هغهٔ سیورى هم، دغه (جنت) د هغو كسانو انجام (او د رجوع ځاى) دى چې پرهېزګاري كوي او د كافرانو انجام اور دى');
INSERT INTO "abulsalam" VALUES(13,36,'13:36',' او هغه كسان چې مونږ هغوى ته (له تا نه مخكې) كتاب وركړى دى، هغوى په هغه (كتاب، قرآن) باندې خوشحالېږي چې تا ته نازل كړى شوى دى، او ( د اهل كتابو) په ډلو كې ځینې هغه دي چې د ده ځينې برخې نه مني، ته (دوى ته) ووایه: ما ته د دې حكم شوى دى چې د الله عبادت كوم او له هغه سره شریك ونه نیسم، خاص د هغهٔ طرف ته بلنه كوم او خاص هم هغهٔ ته زما ورتله دي');
INSERT INTO "abulsalam" VALUES(13,37,'13:37',' او همداسې مونږ دا (قرآن) نازل كړى دى، په عربۍ ژبه حكم كوونكى او قسم دى كه ته د دوى د خواهشاتو پېروي وكړې وروسته له دې چې تا ته علم راغى (،نو) تا لپاره به د الله په مقابله كې نه هېڅ كارساز وي او نه بچ كوونكى');
INSERT INTO "abulsalam" VALUES(13,38,'13:38',' او یقینًا یقینًا مونږ له تا نه مخكې رسولان لېږلي وو او مونږ دوى ته ښځې او اولادونه وركړي وو، او د هېڅ رسول لپاره دا اختیار نه و چې كومه معجزه راوړي مګر د الله په اذن (او حكم) سره، د هرې مودې (دور) لپاره یو لیك (، كتاب او قانون) دى');
INSERT INTO "abulsalam" VALUES(13,39,'13:39',' الله محوه كوي هغه چې اراده يې وكړي او ثابتوي يې او له هغه سره اصل د كتاب دى');
INSERT INTO "abulsalam" VALUES(13,40,'13:40',' او كه واقعًا مونږ تا ته ځینې له هغه (عذاب) نه وښیو چې دوى ته يې مونږ وعده وركوو، یا خامخا مونږ تا وفات كړو، نو پر تا خو يواځې تبلیغ كول دي او پر مونږ باندې حساب كول دي');
INSERT INTO "abulsalam" VALUES(13,41,'13:41',' ایا دوى نه ګوري چې یقینًا مونږ ځمكې ته راځو چې د هغې له اطرافو نه يې كموو او الله حكم كوي، هیڅوك د هغهٔ د حكم رد كوونكى نشته او هغه ډېر ژر حساب كوونكى دى');
INSERT INTO "abulsalam" VALUES(13,42,'13:42',' او يقينًا هغو (كافرانو) مكر كړى و چې له دې (كافرانو) نه مخكې وو، نو خاص الله لره دي پټ تدبیرونه ټول، هغه پوهېږي پر هغه څه چې هر نفس يې كوي، او كافران به ژر پوه شي چې د چا لپاره د اخرت ښه انجام دى');
INSERT INTO "abulsalam" VALUES(13,43,'13:43',' او كافران شوي خلق وايي: ته رسول نه يې، ته (ورته) ووایه: زما په مینځ كې او ستاسو په مینځ كې د ګواه په ډول الله ښه كافي دى او هغه څوك (هم) چې له هغهٔ سره د كتاب علم دى');
INSERT INTO "abulsalam" VALUES(14,1,'14:1',' الف، لام، را، (دا) كتاب دى چې مونږ تا ته نازل كړى دى، د دې لپاره چې ته خلق له تیارو نه رڼا ته وباسې د دوى د رب په اذن (او حكم) سره، د ډېر غالب، ډېر ستايل شوي ذات لارې ته');
INSERT INTO "abulsalam" VALUES(14,2,'14:2',' چې الله دى، هغه چې خاص د هغهٔ لپاره دي هغه څه چې په اسمانونو كې دي او هغه څه چې په ځمكه كې دي او كافرانو ته له ډېر سخت عذاب نه هلاكت دى');
INSERT INTO "abulsalam" VALUES(14,3,'14:3',' هغه كسان چې دنيايي ژوند په اخرت باندې غوره ګڼي او د الله له لارې نه بندول كوي او په دې كې كوږوالى لټوي، دغه كسان په ډېرې لرې ګمراهۍ كې دي');
INSERT INTO "abulsalam" VALUES(14,4,'14:4',' او مونږ هېڅ یو رسول نه دى لېږلى مګر د خپل قوم په ژبه، د دې لپاره چې دوى ته بیان (او وضاحت) وكړي، بیا نو الله ګمراه كوي هغه څوك چې يې وغواړي او هدایت وركوي هغه چا ته چې يې وغواړي، او هم دى ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(14,5,'14:5',' او یقینًا یقینًا مونږ موسٰى له خپلو معجزو سره لېږلى و (او دا مو ورته ويلي وو) چې خپل قوم له تیارو نه رڼا ته راوباسه او دوى ته د الله د ورځو یادونه وكړه، بېشكه په دغو كې خامخا د هر ښه صبر كوونكي، ډېر شكر كوونكي لپاره نښې دي');
INSERT INTO "abulsalam" VALUES(14,6,'14:6',' او كله چې موسٰى خپل قوم ته وویل: تاسو د الله هغه نعمت رایاد كړئ چې پر تاسو باندې شوى دى، كله چې يې تاسو له اٰلِ فرعون نه خلاص كړئ چې هغوى به تاسو ته ډېر سخت عذاب دررساوه او ستاسو زامن به يې حلالول او ستاسو ښځې (لوڼه) به يې ژوندۍ پرېښودلې او په دې كې ستاسو د رب له جانبه ډېر لوى ازمېښت و ');
INSERT INTO "abulsalam" VALUES(14,7,'14:7',' او كله چې ستاسو رب اعلان وكړ چې قسم دى كه تاسو شكر وباسئ (،نو) زه به خامخا هرومرو تاسو ته (په نعمتونو كې) اضافه وكړم او قسم دى كه تاسو ناشكري وكړئ، نو بېشكه زما عذاب خامخا ډېر سخت دى ');
INSERT INTO "abulsalam" VALUES(14,8,'14:8',' او موسٰى وويل: كه كافران شئ تاسو او هغه څوك چې په ځمكه كې دي، ټول، نو بېشكه الله خامخا ډېر بې پروا، ډېر ستايل شوى دى ');
INSERT INTO "abulsalam" VALUES(14,9,'14:9',' ایا تاسو ته د هغو خلقو خبر نه دى راغلى چې له تاسو نه مخكې وو، چې د نوح قوم او عادیان او ثمودیان وو او هغه كسان چې له دوى نه پس وو، چې يواځې الله پر هغو عالم دى، دوى ته خپلو رسولانو ښكاره دلیلونه راوړي وو، نو دوى خپل لاسونه خپلو خولو ته وګرځول او ويې ويل: بېشكه مونږ كافران شوي یو پر هغه څه چې تاسو په هغه سره رالېږل شوي یئ او بېشكه مونږ یقینًا د هغه څه باره كې چې تاسو مونږ هغهٔ ته بلئ په اضطراب اچوونكي شك كې یو');
INSERT INTO "abulsalam" VALUES(14,10,'14:10',' د دوى رسولانو وویل: ایا د الله په باره كې شك دى، چې د اسمانونو او ځمكې پیدا كوونكى دى؟ هغه تاسو رابلي، د دې لپاره چې تاسو لپاره ستاسو څه ګناهونه وبخښي او تاسو مقررې نېټې ته وروسته كړي، دوى وویل: نه یئ تاسو مګر زمونږ په شان انسان، تاسو غواړئ چې مونږ له هغه څه نه منع كړئ چې زمونږ پلرونو يې عبادت كاوه، نو (بيا خو) تاسو مونږ ته ښكاره دلیل(معجزه) راوړئ ');
INSERT INTO "abulsalam" VALUES(14,11,'14:11',' دوى ته خپلو رسولانو وویل: مونږ نه یو مګر ستاسو په شان انسانان، او لېكن الله په خپلو بنده ګانو كې چې پر چا وغواړي احسان كوي او مونږ ته له سره اختیار نشته چې تاسو ته كوم دلیل (معجزه) راوړو مګر د الله په حكم سره او لازم ده چې مومنان خاص په الله باندې توكل وكړي');
INSERT INTO "abulsalam" VALUES(14,12,'14:12',' او مونږ ته څه عذر دى چې مونږ به پر الله توكل نه كوو، حال دا چې هغهٔ مونږ ته د خپلو لارو هدایت كړى دى او خامخا هرومرو به مونږ پر هغو تكلیفونو صبر كوو چې تاسو يې مونږ ته راكوئ، او لازم ده چې توكل كوونكي خاص په الله باندې توكل وكړي');
INSERT INTO "abulsalam" VALUES(14,13,'14:13',' او كافران شویو خلقو خپلو رسولانو ته وویل: خامخا هرومرو به مونږ تاسو له خپلې ځمكې نه وباسو، یا به خامخا هرومرو تاسو زمونږ دین ته راګرځئ، نو دغو (رسولانو) ته خپل رب وحي وكړه چې خامخا ضرور به مونږ ظالمان هلاكوو');
INSERT INTO "abulsalam" VALUES(14,14,'14:14',' او خامخا ضرور به مونږ له دوى نه پس تاسو په دغه ځمكه كې اوسوو، دغه (د ظالمانو هلاكول او د نېكانو اوسول حق دي) د هغه چا لپاره چې زما په وړاندې له ودرېدلو نه وېرېږي او زما له وعید نه وېرېږي');
INSERT INTO "abulsalam" VALUES(14,15,'14:15',' او (بالاخر) هغو (رسولانو له الله نه) فتح وغوښته او هر متكبر سركشه نامراده شو');
INSERT INTO "abulsalam" VALUES(14,16,'14:16',' چې د هغه په وړاندې جهنم دى او پر هغه به د وینو زویو اوبه څښولى شي');
INSERT INTO "abulsalam" VALUES(14,17,'14:17',' چې هغه به په تكلیف سره ګوټ ګوټ څښي او نژدې به نه وي چې هغه تېرې كړي او ده ته به له هره ځایه مرګ راشي، حال دا چې هغه به مړ كېدونكى نه وي او د ده شا او مخې ته به ډېر سخت عذاب وي');
INSERT INTO "abulsalam" VALUES(14,18,'14:18',' حال د هغو كسانو چې په خپل رب سره كافران شوي دي، د دوى عملونه د هغه ايرو په شان دي چې په سخته بادي ورځ كې پرې تیز باد راوالوځي، دوى به له خپلو كړو عملونو نه په هېڅ شي قادره نشي، همدغه ډېره لرې ګمراهي ده ');
INSERT INTO "abulsalam" VALUES(14,19,'14:19',' ایا ته نه وینې چې بېشكه الله اسمانونه او ځمكه په حقه سره پیدا كړي دي، كه هغه وغواړي(،نو) تاسو به ختم كړي او نوي خلق به راولي');
INSERT INTO "abulsalam" VALUES(14,20,'14:20',' او دغه (كار) په الله باندې هېڅ ګران نه دى');
INSERT INTO "abulsalam" VALUES(14,21,'14:21',' او دوى ټول به په جمع د الله په وړاندې حاضر شي، نو كمزوران به هغو كسانو ته ووايي چې كبر او لويي به يې كوله: بېشكه مونږ ستاسو پیروي كوونكي وو، نو ایا تاسو زمونږ څخه د الله له عذاب نه څه شى لرې كوونكي يئ؟ هغوى به ووايي: كه الله مونږ ته هدایت كړى وى (نو) مونږ به هم تاسو ته لار ښوونه كړې وى، پر مونږ برابر دي كه مونږ شور او فریاد وكړو، یا مونږ صبر وكړو، زمونږ لپاره هېڅ د خلاصي ځاى نشته');
INSERT INTO "abulsalam" VALUES(14,22,'14:22',' او كله چې د خبرې فیصله وشي، شیطان به ووايي: بېشكه الله له تاسو سره وعده كړې وه حقه وعده او ما له تاسو سره وعده كړې وه، نو ما له تاسو سره خلاف وكړو، او زما لپاره پر تاسو باندې هېڅ غلبه نه وه غیر له دې نه چې ما تاسو راوبللئ، نو تاسو زما بلنه قبوله كړه (اوس) نو تاسو ما مه ملامتوئ او (بلكې) خپل ځانونه ملامته كړئ، نه زه ستاسو فریادرس یم او نه تاسو زما فریادرسان یئ، بېشكه زه كافر (او بېزاره) شوى یم په هغه څه چې تاسو زه (له الله سره) شریك كړى وم له دې نه مخكې، بېشكه دا ظالمان چې دي د هغوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(14,23,'14:23',' او هغه كسان به چې ایمان يې راوړى دى او نېك عملونه يې كړي دي داسې جنتونو ته داخل كړى شي چې د هغو له لاندې ولې بهېږي، په دې حال كې چې دوى به په هغو كې تل ترتله وي، د خپل رب په حكم سره او د دوى پېشكشي به په دغو (جنتونو) كې سلام وي');
INSERT INTO "abulsalam" VALUES(14,24,'14:24',' ایا ته نه وینې چې الله څنګه یو مثال بیانوي: پاكیزه كلمه (چې لا اله الا الله ده) د هغې پاكیزه ونې په شان ده چې د هغې بېخ كلك او مضبوط وي (په ځمكه كې) او د هغې څانګې په اسمان كې وي');
INSERT INTO "abulsalam" VALUES(14,25,'14:25',' خپله مېوه د خپل رب په حكم سره هر وخت وركوي او الله د خلقو لپاره مثالونه بیانوي، د دې لپاره چې دوى نصیحت واخلي');
INSERT INTO "abulsalam" VALUES(14,26,'14:26',' او د خبیثې كلمې (چې د كفر كلمه ده) مثال د خبیثې (بدبويې) ونې دى چې د ځمكې له پاسه نه وېستل شوې وي، چې د هغې لپاره هېڅ قدر قرار نه وي');
INSERT INTO "abulsalam" VALUES(14,27,'14:27',' الله هغه كسان چې ایمان يې راوړى دى په ثابته خبره باندې مضبوطوي، په دنيايي ژوند كې او په اخرت كې هم او الله ظالمان ګمراه كوي او الله چې څه وغواړي هغه كوي');
INSERT INTO "abulsalam" VALUES(14,28,'14:28',' ایا تا نه دي كتلي هغو كسانو ته چې په ناشكرۍ سره يې د الله نعمت بدل كړ او خپل قوم يې د هلاكت كور ته وركوز كړ');
INSERT INTO "abulsalam" VALUES(14,29,'14:29',' چې جهنم دى، دوى به په هغه كې داخلېږي او هغه داستوګنې بد ځاى دى');
INSERT INTO "abulsalam" VALUES(14,30,'14:30',' او دوى د الله لپاره شریكان مقرر كړل، د دې لپاره چې د هغه له لارې نه (خلق) ګمراه كړي، ته (ورته) ووایه: تاسو نفع واخلئ، پس بېشكه ستاسو بېرته ورتله (د دوزخ) اور ته دي');
INSERT INTO "abulsalam" VALUES(14,31,'14:31',' ته زما هغو بنده ګانو ته ووایه چې ایمان يې راوړى دى چې لمونځ دې قايموي او له هغه مال نه دې چې مونږ دوى ته وركړى دى؛ په پټه او ښكاره انفاق كوي، مخكې له دې نه چې هغه ورځ راشي چې په هغې كې نه اخيستل خرڅول شته او نه دوستي');
INSERT INTO "abulsalam" VALUES(14,32,'14:32',' الله هغه ذات دى چې اسمانونه او ځمكه يې پیدا كړي دي او له بره نه يې اوبه نازلې كړې دي، پس په هغو سره يې له مېوو نه تاسو لپاره رزق راويستلى دى او تاسو لپاره يې بېړۍ مُسَخَّرې كړې دي، د دې لپاره چې هغه (بېړۍ) د هغه په حكم سره په سمندر كې روانې وي، او تاسو لپاره يې نهرونه مسخر كړي دي');
INSERT INTO "abulsalam" VALUES(14,33,'14:33',' او تاسو لپاره يي لمر او سپوږمۍ په كار لګولي دي، په داسې حال كې چې د قانون مطابق ګرځي او تاسو لپاره يې شپه او ورځ په كار لګولي دي');
INSERT INTO "abulsalam" VALUES(14,34,'14:34',' او تاسو ته يې له هغو څیزونو نه چې تاسو هغه غوښتي، ځینې دركړي دي او كه تاسو د الله نعمتونه وشمېرئ (نو) تاسو به هغه ونشمېرلى شئ، بېشكه انسان خامخا ډېر ظلم كوونكى، ډېر ناشكره دى');
INSERT INTO "abulsalam" VALUES(14,35,'14:35',' او (یاد كړه) هغه وخت چې ابراهیم وویل: اى زما ربه! ته دا ښار د امن والا وګرځوه او ما او زما زامن د بتانو له عبادت نه لرې وساته');
INSERT INTO "abulsalam" VALUES(14,36,'14:36',' اى زما ربه! بېشكه دغو (بتانو) په خلقو كې ډېر زیات ګمراه كړي دي، نو چا چې زما پيروي وكړه، نو بېشكه هغه زما څخه دى او چا چې زما نافرماني وكړه، نو بېشكه ته ډېر بخښونكى، بې حده رحم كوونكى يې');
INSERT INTO "abulsalam" VALUES(14,37,'14:37',' اى زمونږه ربه! بېشكه ما واوسول خپل ځینې بچي په داسې ناو (دره) كې چې د فصل والا نه دى، ستا د عزتمن كور په خوا كې، اى زمونږ ربه! د دې لپاره چې دوى لمونځ قايم كړي، نو ته د خلقو زړونه راوګرځوه، چې په مینې سره د دوى خوا ته راځغلي او ته دوى ته له مېوو ځنې روزي وركړه، د دې لپاره چې دوى شكر وباسي');
INSERT INTO "abulsalam" VALUES(14,38,'14:38',' اى زمونږ ربه! بېشكه ته عالم يې پر هغه څه چې مونږ يې پټوو او پر هغه څه چې مونږ يې څرګندوو او په الله باندې هېڅ شى نه پټېږي، په ځمكه كې او نه په اسمان كې');
INSERT INTO "abulsalam" VALUES(14,39,'14:39',' ټوله ثنا خاص د هغه الله لپاره ده چې ما ته يې سره له زوړوالي نه اسمعیل او اسحق رابخښلې دي، بېشكه زما رب خامخا د دعا ښه اورېدونكى دى ');
INSERT INTO "abulsalam" VALUES(14,40,'14:40',' اى زما ربه! ته ما د لمونځ همېشه قايموونكى وګرځوه او زما له اولاد ځنې هم (د لمانځه قايموونكي وګرځوه)، اى زما ربه! او ته زما دعا قبوله كړې');
INSERT INTO "abulsalam" VALUES(14,41,'14:41',' اى زمونږ ربه! ته ما وبخښه و زما مور و پلار او مومنان په هغې ورځ چې حساب به قايمېږي');
INSERT INTO "abulsalam" VALUES(14,42,'14:42',' او ته په الله باندې له سره (دا) ګمان مه كوه چې هغه غافله دى له هغو كارونو نه چې ظالمان يې كوي، بېشكه همدا خبره ده چې هغه، دوى هغې ورځې ته وروسته كوي چې په هغې كې به سترګې خلاصې پاتې شي');
INSERT INTO "abulsalam" VALUES(14,43,'14:43',' چې منډې وهونكي به وي، د خپلو سرونو اوچت نیوونكي به وي، دوى ته به د خپلو سترګو رپول بېرته نه راګرځي (سترګې به يې خلاصې پاتې وي)، او د دوى زړونه به (له عقل او فهم نه) تش وي');
INSERT INTO "abulsalam" VALUES(14,44,'14:44',' او ته خلق له هغې ورځې نه ووېروه چې دوى ته به (په هغې كې) عذاب راشي، نو وايي به هغه كسان چې ظلم يې كړى دى: اى زمونږ ربه! ته مونږ تر نژدې نېټې پورې وروسته كړه (مهلت راكړه) ستا بلنه به قبوله كړو او د رسولانو پیروي به وكړو، (الله به ووايي:) ایا تاسو نه وئ چې مخكې له دې نه به مو قسم كاوه چې تاسو لپاره هېڅ زوال نشته');
INSERT INTO "abulsalam" VALUES(14,45,'14:45',' او تاسو د هغو كسانو په كورونو كې اوسېدلي وئ چې په خپلو ځانونو به يې ظلم كاوه او تاسو ته (دا خبره) ښه څرګنده شوې وه چې مونږ له هغوى سره څنګه كار كړى و، او تاسو ته مونږ مثالونه بیان كړي وو');
INSERT INTO "abulsalam" VALUES(14,46,'14:46',' او یقینًا هغوى خپل ټول مكرونه (او فریبونه) تر سره كړل، په دې حال كې چې د دوى مكرونه د الله په وړاندې دي، او د دوى مكرونه داسې نه وو چې د هغو په وجه به غرونه زايل شي');
INSERT INTO "abulsalam" VALUES(14,47,'14:47',' نو ته هیڅكله هم د الله باره كې دا ګمان مه كوه چې هغه به له خپلو رسولانو سره د وعدې خلاف وكړي، بېشكه الله ډېر غالب دى، انتقام والا دى');
INSERT INTO "abulsalam" VALUES(14,48,'14:48',' (یاده كړه) هغه ورځ چې دغه ځمكه به په بلې ځمكې سره بدله كړى شي او اسمانونه (هم) او دوى (ټول) به خاص الله ته مخامخ ودرېږي چې یو دى، ډېر غالب دى. ');
INSERT INTO "abulsalam" VALUES(14,49,'14:49',' او ته به په دغې ورځ كې مجرمان وینې چې یو له بل سره به په زولنو كې تړل شوي وي');
INSERT INTO "abulsalam" VALUES(14,50,'14:50',' د دوى قمیصونه به د نانزړو وي او د دوى مخونه به اور رالاندې (او پټ) كړي وي');
INSERT INTO "abulsalam" VALUES(14,51,'14:51',' د دې لپاره چې الله بدله وركړي هر نفس ته د هغه څه چې ده كړي دي، بېشكه الله ډېر ژر حساب والا دى');
INSERT INTO "abulsalam" VALUES(14,52,'14:52',' دا (قرآن) د خلقو لپاره پېغام دى او د دې لپاره چې په ده سره خلق خبردار كړى شي او د دې لپاره چې دوى پر دې پوه شي چې یقینًا همدا خبره ده چې هغه (الله) واحد (بې شریكه) حق معبود دى او د دې لپاره چې د خالصو عقلونو خاوندان نصیحت واخلي');
INSERT INTO "abulsalam" VALUES(15,1,'15:1',' الف، لام، را، دغه د كتاب او واضح قرآن ایتونه دي');
INSERT INTO "abulsalam" VALUES(15,2,'15:2',' ډېر ځلې به ارزو كوي هغه كسان چې كافران شوي دي چې كاش دوى مسلمانان وى!');
INSERT INTO "abulsalam" VALUES(15,3,'15:3',' ته دوى پرېږده چې خوراك كوي او فايده اخلي او امېدونه يې غافله كړي، نو ژر ده چې دوى به پوه شي');
INSERT INTO "abulsalam" VALUES(15,4,'15:4',' او مونږه هېڅ یو كلى نه دى هلاك كړى مګر په دې حال كې چې د هغه به لیك (مقرر وخت) معلوم و');
INSERT INTO "abulsalam" VALUES(15,5,'15:5',' هېڅ امت له خپل وخت نه نه مخكې كېږي او نه وروسته كېږي');
INSERT INTO "abulsalam" VALUES(15,6,'15:6',' او دوى وویل: اى هغه كسه چې په هغه باندې ذكر (قرآن) نازل شوى دى! یقینًا ته خو لیونى يې');
INSERT INTO "abulsalam" VALUES(15,7,'15:7',' ولې ته مونږ ته ملايك نه راولې! كه چېرې ته له رښتینو څخه يې');
INSERT INTO "abulsalam" VALUES(15,8,'15:8',' (اى خلقو واورئ!)مونږ ملايك نه نازلوو مګر په حقه سره او دوى به په دغه وخت كې مهلت وركړى شوي نه وي');
INSERT INTO "abulsalam" VALUES(15,9,'15:9',' بېشكه همدا مونږ پخپله ذكر نازل كړى دى او بېشكه مونږ د ده خامخا ساتونكي یو');
INSERT INTO "abulsalam" VALUES(15,10,'15:10',' او یقینًا یقینًا مونږ له تا نه مخكې د ړومبنیو خلقو په ډلو كې (رسولان) لېږلي وو');
INSERT INTO "abulsalam" VALUES(15,11,'15:11',' او دوى ته به هېڅ یو رسول نه راته مګر دوى به په هغه پورې مسخرې كولې');
INSERT INTO "abulsalam" VALUES(15,12,'15:12',' همدارنګه مونږ دا خبره د مجرمانو په زړونو كې ننباسو');
INSERT INTO "abulsalam" VALUES(15,13,'15:13',' دوى پر ده ایمان نه راوړي او یقینًا د پخوانیو (كافرو) طریقه تېره شوې ده');
INSERT INTO "abulsalam" VALUES(15,14,'15:14',' او كه مونږ په دوى باندې له اسمان نه كومه دروازه پرانستلې وى، بیا دوى په هغې كې تل ختلى');
INSERT INTO "abulsalam" VALUES(15,15,'15:15',' (نو) دوى به خامخا ویل چې همدا خبره ده چې زمونږ سترګې تړل شوې دي، بلكې پر مونږ جادو شوى دى');
INSERT INTO "abulsalam" VALUES(15,16,'15:16',' او یقینًا یقینًا مونږ په اسمان كې برجونه پیدا كړي دي او مونږ هغه د كتونكو لپاره ښايسته كړى دى');
INSERT INTO "abulsalam" VALUES(15,17,'15:17',' او مونږ هغه ساتلى دى له هر شیطان ویشتل شوي نه');
INSERT INTO "abulsalam" VALUES(15,18,'15:18',' مګر هغه (شیطان) چې اورېدل شوې خبره غلا كړي، نو په هغه پسې شي یوه ښكاره شعله');
INSERT INTO "abulsalam" VALUES(15,19,'15:19',' او ځمكه، مونږ هغه غځولې ده او مونږ په هغې كې غرونه غورځولي دي او مونږ په هغې كې هر اندازه كړى شوى شى زرغون كړى دى');
INSERT INTO "abulsalam" VALUES(15,20,'15:20',' او مونږ تاسو لپاره په هغې كې معیشتونه پیدا كړي دي او هغه هم چې تاسو هغه ته روزي وركوونكي نه یئ');
INSERT INTO "abulsalam" VALUES(15,21,'15:21',' او نشته هېڅ څیز مګر زمونږ سره د هغه خزانې دي او مونږ هغه نه نازلوو مګر په معلومې اندازې سره');
INSERT INTO "abulsalam" VALUES(15,22,'15:22',' او مونږ هواګانې لېږلې دي، چې بلاربوونكې دي، پس مونږ له بره اوبه نازلې كړې، نو مونږ هغه په تاسو وڅښولې او تاسو دغو (اوبو) لره خزانه كوونكي نه یئ');
INSERT INTO "abulsalam" VALUES(15,23,'15:23',' او بېشكه مونږ، یقینًا همدا مونږ ژوند وركوو او مرګ وركوو او همدا مونږ وارثان یو ');
INSERT INTO "abulsalam" VALUES(15,24,'15:24',' او یقینًا یقینًا مونږ په تاسو كې مخكې كېدونكي پېژندلي دي او یقینًا یقینًا مونږ وروسته كېدونكي (هم) پېژندلي دي');
INSERT INTO "abulsalam" VALUES(15,25,'15:25',' او بېشكه ستا رب، هغه به دوى راټولوي، بېشكه هغه ښه حكمت والا، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(15,26,'15:26',' او یقینًا یقینًا مونږه انسان پیدا كړى دى له كړنګېدونكې وچې خټې نه، چې له تورې سخا كړى شوې خټې ځنې وه');
INSERT INTO "abulsalam" VALUES(15,27,'15:27',' او جانّ (پلار د پېریانو) مونږ هغه له ده نه مخكې د بې لوګي اور څخه پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(15,28,'15:28',' او كله چې ستا رب وویل: بېشكه زه د یو بشر پېدا كوونكى یم د كړنګېدونكې وچې خټې نه چې له تورې سخا كړى شوې خټې ځنې ده');
INSERT INTO "abulsalam" VALUES(15,29,'15:29',' نو كله چې زه هغه برابر كړم او په هغه كې له خپله روح پُو كړم، نو تاسو هغه ته پرېوځئ، سجده كوونكي');
INSERT INTO "abulsalam" VALUES(15,30,'15:30',' نو ملايكو ټولو عامو سجده وكړه');
INSERT INTO "abulsalam" VALUES(15,31,'15:31',' له ابلیس نه غير، هغه له دې نه انكار وكړ چې له سجده كوونكو سره ملګرى شي ');
INSERT INTO "abulsalam" VALUES(15,32,'15:32',' ويې ويل: اى ابلیسه! تا ته څه عذر دى چې ته له سجده كوونكو سره نه ملګرى كېږې؟');
INSERT INTO "abulsalam" VALUES(15,33,'15:33',' هغه وویل: زه داسې نه یم چې بشر ته سجده وكړم چې تا هغه پیدا كړى دى له كړنګېدونكو وچو كودرو نه، چې له تورې سخا شوې خټې ځنې و ');
INSERT INTO "abulsalam" VALUES(15,34,'15:34',' وفرمایل: (الله) نو ته ووځه له دې ځاى نه، پس یقینًا ته رټل شوى يې');
INSERT INTO "abulsalam" VALUES(15,35,'15:35',' او بېشكه په تا باندې د قیامت د ورځې پورې لعنت دى');
INSERT INTO "abulsalam" VALUES(15,36,'15:36',' هغه وویل: اى زما ربه! نو ته ما ته د هغې ورځې پورې مهلت راكړه چې خلق به بیا ژوندي كولى شي');
INSERT INTO "abulsalam" VALUES(15,37,'15:37',' ويې ويل: پس یقینًا ته له مهلت وركړى شويو څخه يې');
INSERT INTO "abulsalam" VALUES(15,38,'15:38',' د معلوم وخت تر ورځې پورې');
INSERT INTO "abulsalam" VALUES(15,39,'15:39',' ويې ويل: اى زما ربه! په سبب د دې چې تا زه ګمراه كړم خامخا هرومرو به زه په ځمكه كې د دوى لپاره ښكلي كول كوم (ګناهونه او نافرمانۍ به ورته ښكلي كوم) او خامخا هرومرو به زه دوى ټول ګمراه كوم');
INSERT INTO "abulsalam" VALUES(15,40,'15:40',' مګر په دوى كې ستا خالص كړى شوي بنده ګان');
INSERT INTO "abulsalam" VALUES(15,41,'15:41',' ويې فرمایل: دا لار ده چې تر ما پورې نېغه (رارسېدلې) ده');
INSERT INTO "abulsalam" VALUES(15,42,'15:42',' بېشكه زما بنده ګان چې دي نشته تا لپاره په دوى باندې هېڅ غلبه مګر هغه چې ستا پیروي كوي له ګمراهانو څخه');
INSERT INTO "abulsalam" VALUES(15,43,'15:43',' او بېشكه جهنم، خامخا د دوى د ټولو د وعدې ځاى دى');
INSERT INTO "abulsalam" VALUES(15,44,'15:44',' د هغه اوه دروازې دي، د هرې دروازې لپاره به تقسیم (بېله) كړى شوې برخه وي له دوى نه');
INSERT INTO "abulsalam" VALUES(15,45,'15:45',' بېشكه متقیان به په باغونو او چینو كې وي');
INSERT INTO "abulsalam" VALUES(15,46,'15:46',' (چې ورته ويل به شي:) تاسو دغو ته په سلامتۍ سره ننوځئ، په داسې حال كې چې په امن كې به یئ');
INSERT INTO "abulsalam" VALUES(15,47,'15:47',' او مونږ به وباسو هغه كینه چې د دوى په سینو كې وي، په داسې حال كې چې وروڼه به وي، په تختونو به یو بل ته مخامخ ناست وي');
INSERT INTO "abulsalam" VALUES(15,48,'15:48',' دوى ته به په دغو (جنتونو) كې هېڅ تكلیف (ستړې والى) نه رسېږي او نه به دوى له دغو نه كله هم وېستلى شي ');
INSERT INTO "abulsalam" VALUES(15,49,'15:49',' ته زما بنده ګانو ته خبر وركړه چې بېشكه زه، همدا زه ډېر بخښونكى، بې حده رحم كوونكى یم');
INSERT INTO "abulsalam" VALUES(15,50,'15:50',' او دا چې بېشكه زما عذاب، هم هغه ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(15,51,'15:51',' او ته دوى ته د ابراهیم د مېلمنو په باره كې خبر وركړه');
INSERT INTO "abulsalam" VALUES(15,52,'15:52',' كله چې هغوى پر هغه ورننوتل، نو هغوى وویل: (مونږ سلام وایو) سلام ویل: هغه وویل: بېشكه مونږ له تاسو څخه وېرېدونكي یو');
INSERT INTO "abulsalam" VALUES(15,53,'15:53',' هغوى وویل: ته مه وېرېږه، بېشكه مونږ تا ته د ډېر پوه هلك زېرى دركوو');
INSERT INTO "abulsalam" VALUES(15,54,'15:54',' هغه وویل: ایا تاسو ما ته زېرى راكړ، سره له دې چې ما ته زوړوالى (بوډاوالى) را رسېدلى دى، نو تاسو په كومه وجه زېرى راكوئ؟');
INSERT INTO "abulsalam" VALUES(15,55,'15:55',' هغوى وویل: مونږ تا ته په حقه سره زېرى دركړى دى، نو ته له ناامېدانو نه مه كېږه');
INSERT INTO "abulsalam" VALUES(15,56,'15:56',' هغه وویل: له ګمراهانو نه غیر (بل) څوك دى چې د خپل رب له رحمت نه به ناامېده كېږي!');
INSERT INTO "abulsalam" VALUES(15,57,'15:57',' هغه وویل: نو ستاسو (نور) څه مقصد دى، اى رالېږل شویو؟');
INSERT INTO "abulsalam" VALUES(15,58,'15:58',' هغوى وویل: یقینًا مونږ د مجرم قوم په طرف رالېږل شوي یو');
INSERT INTO "abulsalam" VALUES(15,59,'15:59',' غیر له آلِ لوط نه، بېشكه مونږ خامخا هغوى ټولو ته نجات وركوونكي یو ');
INSERT INTO "abulsalam" VALUES(15,60,'15:60',' (خو) د هغه له ښځې نه غير، مونږ دا فیصله كړې ده چې یقینًا هغه به لازمًا له وروسته پاتې كېدونكو څخه وي');
INSERT INTO "abulsalam" VALUES(15,61,'15:61',' نو كله چې د لوط آل ته رالېږل شوي (ملايك) راغلل');
INSERT INTO "abulsalam" VALUES(15,62,'15:62',' هغه وویل: بېشكه تاسو یو ناپېژندګلو خلق یئ ');
INSERT INTO "abulsalam" VALUES(15,63,'15:63',' هغوى وویل: بلكې مونږ تا ته هغه (عذاب) راوړى دى چې دوى به په هغه كې شك كاوه ');
INSERT INTO "abulsalam" VALUES(15,64,'15:64',' او مونږ تا ته حق راوړى دى او بېشكه مونږ یقینًا رښتيني یو');
INSERT INTO "abulsalam" VALUES(15,65,'15:65',' نو ته خپل اهل د شپې په یوه حصه كې بوځه او ته د دوى شا ته ورپسې ځه او په تاسو كې دې هیڅوك بېرته نه ګوري او ځئ هغه ځاى ته چې تاسو ته يې حكم كېږي');
INSERT INTO "abulsalam" VALUES(15,66,'15:66',' او مونږ هغه ته د دې كار فیصله وحي كړه چې یقینًا د دې خلقو بېخ پرې كېدونكى دى، په دې حال كې چې دوى به په سبا كې داخلېدونكي وي');
INSERT INTO "abulsalam" VALUES(15,67,'15:67',' او د ښار اوسېدونكي راغلل چې ډېره خوشحالي يې كوله');
INSERT INTO "abulsalam" VALUES(15,68,'15:68',' وویل (لوط): بېشكه دا زما مېلمانه دي، نو تاسو ما مه شرموئ');
INSERT INTO "abulsalam" VALUES(15,69,'15:69',' او له الله نه ووېرېږئ او ما مه سپكوئ ');
INSERT INTO "abulsalam" VALUES(15,70,'15:70',' هغوى وویل: ایا ته مونږ خلقو (ته له پناه وركولو) نه نه وې منع كړى؟ ');
INSERT INTO "abulsalam" VALUES(15,71,'15:71',' هغه وویل: دا زما لوڼه دي كه تاسو (له دوى سره نكاح) كوونكي یئ');
INSERT INTO "abulsalam" VALUES(15,72,'15:72',' ستا په عمر مې دې قسم وي! بېشكه دوى خامخا به خپلې مستۍ (نشه) كې سرګردان دي');
INSERT INTO "abulsalam" VALUES(15,73,'15:73',' نو دوى يو هیبتناك غږ ونیول، په داسې حال كې چې د لمرختو په وخت كې داخلېدونكي وو');
INSERT INTO "abulsalam" VALUES(15,74,'15:74',' نو مونږ د هغه(ښار) پورته طرف ښكته طرف وګرځاوه او په دوى باندې مونږ د كوتكاڼو كاڼي وورول');
INSERT INTO "abulsalam" VALUES(15,75,'15:75',' بېشكه په دې كې خامخا د پوهو، عبرت اخيستونكو لپاره نښې دي');
INSERT INTO "abulsalam" VALUES(15,76,'15:76',' او بېشكه دغه (ښاریه) خامخا په سمه (چلنده) لار كې پرته ده');
INSERT INTO "abulsalam" VALUES(15,77,'15:77',' بېشكه په دې كې د مومنانو لپاره خامخا غټه نښه ده');
INSERT INTO "abulsalam" VALUES(15,78,'15:78',' او بېشكه شان دا دى چې د ځنګل والا یقینًا ظالمان وو');
INSERT INTO "abulsalam" VALUES(15,79,'15:79',' نو مونږ له هغوى نه انتقام واخيست او بېشكه دغه دواړه (ښارونه) پر لویه ښكاره لار پراته دي');
INSERT INTO "abulsalam" VALUES(15,80,'15:80',' او یقینًا یقینًا حِجر والاوو د رسولانو تكذیب كړى و ');
INSERT INTO "abulsalam" VALUES(15,81,'15:81',' او مونږ هغوى ته خپلې نښې ور (ښكاره) كړې وې، نو دوى له هغو نه مخ ګرځوونكي وو ');
INSERT INTO "abulsalam" VALUES(15,82,'15:82',' او دوى به له غرونو څخه كوټې توږلې، په دې حال كې چې په امن به وو ');
INSERT INTO "abulsalam" VALUES(15,83,'15:83',' نو دوى یو هیبتناك غږ ونیول، په داسې حال كې چې په سبا كې داخلېدونكي وو');
INSERT INTO "abulsalam" VALUES(15,84,'15:84',' نو د دوى په كار رانغلل هغه (شیان) چې دوى به حاصلول');
INSERT INTO "abulsalam" VALUES(15,85,'15:85',' او مونږ اسمانونه او ځمكه او هغه څه چې د دې دواړو په مينځ كې دي ؛ نه دي پیدا كړي مګر په حقه سره او بېشكه قیامت خامخا راتلونكى دى، نو ته (له دوى څخه) څنګ وكړه په ښكلو څنګ كولو سره');
INSERT INTO "abulsalam" VALUES(15,86,'15:86',' بېشكه ستا رب، هم دى ښه پیداكوونكى، بې حده علم والا دى ');
INSERT INTO "abulsalam" VALUES(15,87,'15:87',' او یقینًا یقینًا مونږ تا ته بیا بیا لوستل كېدونكي اوه ایتونه دركړي دي او عظیم قرآن (هم)');
INSERT INTO "abulsalam" VALUES(15,88,'15:88',' او ته خپلې سترګې هغو شیانو ته مه ور اوږدې كوه (مه پورته كوه) چې مونږ په هغو سره فايده وركړې ده دغو خلقو ته، چې قسم قسم شیان دي او په دوى باندې مه غمګین كېږه او خپل وزر د مومنانو لپاره ښكته كړه');
INSERT INTO "abulsalam" VALUES(15,89,'15:89',' او ته ووایه: بېشكه زه چې یم، هم دا زه ښكاره وېروونكى یم');
INSERT INTO "abulsalam" VALUES(15,90,'15:90',' (له داسې عذابه) لكه څنګه چې مونږ نازل كړى و په تقسیموونكو باندې');
INSERT INTO "abulsalam" VALUES(15,91,'15:91',' هغه كسان چې قرآن يې ټوټې ټوټې كړى و');
INSERT INTO "abulsalam" VALUES(15,92,'15:92',' نو قسم دى په رب ستا چې خامخا هرومرو به مونږ له دوى ټولو څخه تپوس كوو');
INSERT INTO "abulsalam" VALUES(15,93,'15:93',' د هغو كارونو په باره كې چې دوى به كول');
INSERT INTO "abulsalam" VALUES(15,94,'15:94',' نو ته ښه ښكاره بیان كړه هغه څه چې تا ته يې حكم كولى شي او له مشركانو نه مخ واړوه');
INSERT INTO "abulsalam" VALUES(15,95,'15:95',' بېشكه مونږ ستا لپاره (ستا) استهزا كوونكو ته كافي شوي یو');
INSERT INTO "abulsalam" VALUES(15,96,'15:96',' هغه كسان چې له الله سره نور معبودان مقرروي (شریكوي) نو ژر به دوى پوه شي');
INSERT INTO "abulsalam" VALUES(15,97,'15:97',' او یقینًا یقینًا مونږ ته معلوم دي چې بېشكه ستا سینه تنګېږي په هغو خبرو چې دوى يې وايي');
INSERT INTO "abulsalam" VALUES(15,98,'15:98',' نو ته د خپل رب له حمد (ستاینې) سره تسبیح وایه او ته شه له سجده كوونكو څخه ');
INSERT INTO "abulsalam" VALUES(15,99,'15:99',' او د خپل رب عبادت كوه تر هغه پورې چې تا ته یقین (مرګ) راشي');
INSERT INTO "abulsalam" VALUES(16,1,'16:1',' د الله حكم راغى، نو تاسو هغه په تلوار مه غواړئ، هغه لره پاكي ده او هغه ډېر اوچت دى له هغه څه نه چې دوى يې ورسره شریكوي');
INSERT INTO "abulsalam" VALUES(16,2,'16:2',' هغه په خپل حكم ملايك له وحي سره نازلوي په هغه چا باندې چې يې وغواړي په خپلو بنده ګانو كې، چې تاسو (خلق) خبردار كړئ، په دې خبره چې بېشكه حقيقت دا دى چې نشته هېڅ لايق د عبادت مګر زه یم، نو تاسو له ما نه ووېرېږئ');
INSERT INTO "abulsalam" VALUES(16,3,'16:3',' هغهٔ اسمانونه او ځمكه په حقه سره پیدا كړي دي، هغه اوچت دى له هغه چې دوى يې ورسره شریكوي');
INSERT INTO "abulsalam" VALUES(16,4,'16:4',' هغهٔ، انسان له نطفې نه پیدا كړى دى، بيا نو ناڅاپه دغه (انسان) ښه ښكاره سخت جګړه كوونكى دى');
INSERT INTO "abulsalam" VALUES(16,5,'16:5',' او څاروي، هغه يې پیدا كړي دي، تاسو لپاره په دوى كې د ګرمۍ اسباب او (نورې) ګټې دي او ځینې په دوى كې تاسو خورئ');
INSERT INTO "abulsalam" VALUES(16,6,'16:6',' او تاسو لپاره په دوى كې جمال (او ښايست) دى كله چې يې بېرته ماښام (كور ته) راولئ او كله چې يې د څر لپاره بوځئ');
INSERT INTO "abulsalam" VALUES(16,7,'16:7',' او دوى ستاسو درانه بارونه پورته كوي هغه ښار ته چې تاسو هغه ته رسېدونكي نه وئ مګر د ځانونو په مشقت سره، بېشكه ستاسو رب یقینًا ډېر نرمي كوونكى، بې حده مهربان دى ');
INSERT INTO "abulsalam" VALUES(16,8,'16:8',' او اسونه او قچر او خره (يې پیدا كړي دي،) د دې لپاره چې تاسو په دغو باندې سواره شئ او د زینت لپاره او پیدا كوي هغه څه چې تاسو يې نه پېژنئ');
INSERT INTO "abulsalam" VALUES(16,9,'16:9',' او خاص په الله باندې د سمې لارې بیان دى او ځینې په دغو (نورو لارو) كې كږې دي او كه هغه غوښتلى(،نو) لازمًا به يې تاسو ټولو ته هدایت كړى و ');
INSERT INTO "abulsalam" VALUES(16,10,'16:10',' دى همغه ذات دى چې تاسو لپاره يې له بره نه اوبه نازلې كړې دي، تاسو ته له هغو ځنې مشروب (څښاك) دى او له هغو ځنې ونې (واښه) هم رازرونېږي چې په هغو كې تاسو (څاروي) څروئ');
INSERT INTO "abulsalam" VALUES(16,11,'16:11',' هغهٔ په دې (اولو) سره تاسو لپاره فصل او زیتون او كجورې او انګور او هر قسمه مېوې زرغونوي، بېشكه په دغو كې خامخا لویه نښه ده د هغو خلقو لپاره چې ښه غور فكر كوي');
INSERT INTO "abulsalam" VALUES(16,12,'16:12',' او هغه مُسخَّر كړې ده تاسو لپاره شپه او ورځ او لمر او سپوږمۍ. او ستوري د هغه په حكم سره مُسَخَّر دي، بېشكه په دغو كې خامخا ډېرې نښې دي د هغو خلقو لپاره چې له عقل نه كار اخلي');
INSERT INTO "abulsalam" VALUES(16,13,'16:13',' او (مُسخر كړي يې دي) هغه څه چې تاسو لپاره يې په ځمكه كې پیدا كړي دي، په داسې حال كې چې رنګونه يې مختلف دي، بېشكه په دغو كې یقینًا لویه نښه ده د هغو خلقو لپاره چې نصیحت قبلوي');
INSERT INTO "abulsalam" VALUES(16,14,'16:14',' دى همغه ذات دى چې سمندر يې مُسخر كړى دى، د دې لپاره چې تاسو له هغه نه تازه غوښه وخورئ او له هغه نه ګاڼه (زېورات) راوباسئ چې تاسو يې اغوندئ او تهٔ په ده كې بېړۍ وینې، چې اوبه څيرونكې وي او د دې لپاره چې تاسو د هغه فضل طلب كړئ او د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(16,15,'16:15',' او هغهٔ په ځمكه كې پاخه غرونه غورځولي دي چې پر تاسو باندې ونه ښورېږي او (پیدا كړي يې دي) ولې (نهرونه) او لارې، د دې لپاره چې تاسو لار ومومئ');
INSERT INTO "abulsalam" VALUES(16,16,'16:16',' او علامې (يې مقرر كړې دي) او په ستورو سره دوى لار مومي');
INSERT INTO "abulsalam" VALUES(16,17,'16:17',' ایا نو هغه ذات چې پیدا كوي په شان د هغه چا دى چې (څه) نشي پیدا كولى؟ ایا نو تاسو پند نه اخلئ! ');
INSERT INTO "abulsalam" VALUES(16,18,'16:18',' او كه چېرې تاسو د الله نعمتونه شمېرل شروع كړئ، (، نو) هغه به ونشمېرلى شئ، بېشكه الله یقینًا ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(16,19,'16:19',' او الله ته معلوم دي هغه څه چې تاسو يې پټوئ او هغه څه چې تاسو يې څرګندوئ');
INSERT INTO "abulsalam" VALUES(16,20,'16:20',' او هغه كسان چې دوى يې له الله نه غیر بلي، هغوى هېڅ شى نشي پیدا كولى، په دې حال كې چې دوى پخپله پیداكاوه شي ');
INSERT INTO "abulsalam" VALUES(16,21,'16:21',' مړه دي، ژوندي نه دي او دوى نه پوهېږي چې كله به دوى بیا راژوندي كولى شي ');
INSERT INTO "abulsalam" VALUES(16,22,'16:22',' ستاسو حق معبود یو معبود دى، نو هغه كسان چې پر اخرت ایمان نه راوړي د هغوى زړونه مُنكِر دي او دوى تكبر كوونكي دي');
INSERT INTO "abulsalam" VALUES(16,23,'16:23',' حقیقت دا دى چې بېشكه الله پوهېږي پر هغه څه چې دوى يې پټوي او پر هغه څه چې دوى يې څرګندوي، بېشكه هغه (الله) تكبر كوونكي نه خوښوي');
INSERT INTO "abulsalam" VALUES(16,24,'16:24',' او كله چې دوى ته وویل شي: ستاسو رب څه نازل كړي دي؟ دوى وايي: د پخوانیو (خلقو) باطلې افسانې');
INSERT INTO "abulsalam" VALUES(16,25,'16:25',' د دې لپاره چې دوى خپل پېټي پوره پورته كړي د قیامت په ورځ او د هغو خلقو له پېټو نه (هم پورته كړي) چې دوى يې په جهالت سره ګمراه كوي، خبردار شئ! بد دى هغه څیز چې دوى يې باروي');
INSERT INTO "abulsalam" VALUES(16,26,'16:26',' یقینًا له دوى نه مخكې خلقو مكر كړى و، نو الله د دوى عمارتونه له بنیادونو نه ونړول، نو په دوى باندې د دوى له پاسه چتونه راپرېوتل او دوى ته عذاب له هغه ځایه راغى چې دوى يې شعور (او ګمان) هم نه لاره');
INSERT INTO "abulsalam" VALUES(16,27,'16:27',' بیا به هغه د قیامت په ورځ دوى رسوا كوي او وايي به: زما هغه شریكان چېرته دي چې تاسو به د هغوى په باره كې (له اهل حقو سره جګړي) مخالفتونه كول؟ وبه وايي هغه كسان چې علم ورته وركړ شوى دى: بېشكه په نن ورځ كې رسوايي او بدي په كافرانو باندې ده');
INSERT INTO "abulsalam" VALUES(16,28,'16:28',' هغه كسان چې ملايك يې (روح) قبضوي، په داسې حال كې چې دوى په خپلو ځانونو ظلم كوونكي وي، نو دوى به روغه (تسليمي) وړاندې كړي چې مونږ هېڅ بد عمل نه كاوه، ولې نه! بېشكه الله ښه پوه دى پر هغو عملونو چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(16,29,'16:29',' نو تاسو د جهنم په دروازه كې ننوځئ، په داسې حال كې، چې په ده كې به همېشه وئ، نو یقینًا د تكبر كوونكو استوګنه بده ده');
INSERT INTO "abulsalam" VALUES(16,30,'16:30',' او هغو كسانو ته به وویل شي چې تقوىٰ يې غوره كړې ده: ستاسو رب څه نازل كړي دي؟ هغوى به ووايي: خیر (يې نازل كړى دى). د هغو كسانو لپاره چې په دې دنیا كې يې نېكي كړې ده ؛ نېكه بدله ده او یقینًا د اخرت كور ډېر غوره دى او یقینًا ښه كور د متقیانو دى');
INSERT INTO "abulsalam" VALUES(16,31,'16:31',' چې د همېشه اوسېدلو جنتونه دي چې دوى به هغو ته داخلېږي، د هغو له لاندې ولې بهېږي، د دوى لپاره په دغو (جنتونو) كې هغه څه دي چې دوى يې غواړي، همدارنګه الله متقیانو ته بدله وركوي');
INSERT INTO "abulsalam" VALUES(16,32,'16:32',' هغه كسان چې ملايك يې (روح) قبض كوي، په دې حال كې چې هغوى پاك وي، دوى به (ورته) وايي: پر تاسو دې سلام وي، تاسو جنت ته داخل شئ، په سبب د هغو (نېكو) عملونو چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(16,33,'16:33',' دوى انتظار نه كوي مګر د دې چې دوى ته ملايك راشي، یا ستا د رب حكم راشي، همدارنګه كار كړى و هغو كسانو چې له دوى نه مخكې وو او الله پر هغوى ظلم نه و كړى او لېكن هغوى پر خپلو ځانونو ظلم كاوه');
INSERT INTO "abulsalam" VALUES(16,34,'16:34',' نو هغوى ته د خپلو عملونو بدي ورسېدله او هغوى ګېر كړل هغه څه چې دوى به ورپورې استهزا كوله');
INSERT INTO "abulsalam" VALUES(16,35,'16:35',' او وویل هغو كسانو چې شرك يې كړى و: كه چېرې الله غوښتلې (، نو) مونږ به له هغه نه غير د هېڅ شي عبادت نه و كړى او نه به زمونږ پلرونو او نه به مونږ د هغه (له حكم) نه غير څه شى حرام كړى و، همدارنګه كار كړى و هغو كسانو چې له دوى نه مخكې وو، نو د رسولانو په ذمه نشته مګر ښكاره رسَوَل ');
INSERT INTO "abulsalam" VALUES(16,36,'16:36',' او یقینًا یقینًا مونږ په هر یو امت كې یو رسول لېږلى و (په دې سره) چې تاسو د الله عبادت كوئ او له طاغوت نه ځان وساتئ، نو په دوى كې ځینو ته الله هدایت وكړ او ځینې په دوى كې هغه دي چې ګمراهي پرې ثابته شوه، نو تاسو په ځمكه كې وګرځئ، بیا وګورئ چې د تكذیب كوونكو انجام څنګه و؟');
INSERT INTO "abulsalam" VALUES(16,37,'16:37',' كه ته د دوى په هدایت راړولو باندې حرص وكړې، نو بېشكه الله هدایت نه كوي هغه چا ته چې هغه يې ګمراه كړي او د دوى لپاره هیڅوك مدد كوونكي نشته');
INSERT INTO "abulsalam" VALUES(16,38,'16:38',' او دوى په (نوم د) الله باندې قسمونه كوي، خپل پاخه قسمونه، چې الله به هغه څوك بیا راژوندي نه كړي چې وفات كېږي، ولې نه! (ژوندي كوي به يې، ځكه چې وعده يې كړې ده) وعده حقه پر هغه لازمه او لېكن اكثره خلق نه پوهېږي');
INSERT INTO "abulsalam" VALUES(16,39,'16:39',' لپاره د دې چې د دوى لپاره هغه شى ښه واضحه كړي چې دوى په هغه كې اختلاف كوي او لپاره د دې چې هغه كسان چې كفر يې كړى دى، پوه شي چې یقینًا دوى دروغژن وو');
INSERT INTO "abulsalam" VALUES(16,40,'16:40',' همدا خبره ده چې زمونږ وینا یو شي ته چې كله يې مونږ اراده وكړو؛ دا ده چې مونږ هغه ته وایو: وشه نو هغه وشي');
INSERT INTO "abulsalam" VALUES(16,41,'16:41',' او هغه كسان چې د الله لپاره يې هجرت كړى دى وروسته له دې چې ظلم پرې شو خامخا هرومرو به مونږ دوى ته په دنیا كې ښه ځاى وركړو او یقینًا د اخرت اجر تر ټولو لوى دى، كه چېرې وى دوى چې پوهېدلى');
INSERT INTO "abulsalam" VALUES(16,42,'16:42',' هغه كسان چې صبر كوي او خاص په خپل رب باندې دوى توكل كوي');
INSERT INTO "abulsalam" VALUES(16,43,'16:43',' او مونږ له تا نه مخكې نه دي لېږلي مګر سړي (پېغمبران) چې مونږ هغوى ته وحي كوله، نو تاسو له اهلِ علمو نه تپوس وكړئ كه چېرې یئ تاسو چې نه پوهېږئ ');
INSERT INTO "abulsalam" VALUES(16,44,'16:44',' په څرګندو معجزو او كتابونو سره او مونږ تا ته ذكر (وحي) نازل كړى دى، د دې لپاره چې ته خلقو ته هغه څه ښه واضحه كړې چې دوى ته نازل كړى شوي دي او د دې لپاره چې دوى ښه فكر وكړي');
INSERT INTO "abulsalam" VALUES(16,45,'16:45',' ایا نو هغه كسان چې ناكاره مكرونه يې كړي دي؛ له دې نه په امن كې شوي دي چې الله دوى په ځمكه كې ډوب كړي یا دوى ته عذاب له داسې ځایه راشي چې دوى (پرې) نه پوهېږي');
INSERT INTO "abulsalam" VALUES(16,46,'16:46',' یا دوى ونیسي د خپلو ګرځېدو په وخت كې، نو دوى هیڅكله هم (الله لره) عاجزه كوونكي نه دي');
INSERT INTO "abulsalam" VALUES(16,47,'16:47',' یا دوى په وېره ونیسي، پس یقینًا ستاسو رب خامخا ډېر نرمي كوونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(16,48,'16:48',' ایا دوى د الله پیدا كړي كوم شي ته نه دي كتلي چې د هغه سیوري له ښي اړخ او چپو اړخونو نه ګرځي، چې الله ته سجده كوونكي دي، په داسې حال كې چې دوى عاجزي كوونكي دي');
INSERT INTO "abulsalam" VALUES(16,49,'16:49',' او خاص د الله لپاره سجده كوي هغه څه چې په اسمانونو كې دي او هغه څه چې په ځمكه كې دي له خوځنده څیزونو څخه او ملايك هم، په داسې حال كې چې دوى تكبر نه كوي');
INSERT INTO "abulsalam" VALUES(16,50,'16:50',' دوى له خپل رب څخه وېرېږي چې د دوى له پاسه دى او دوى كوي هغه څه چې دوى ته حكم كاوه شي ');
INSERT INTO "abulsalam" VALUES(16,51,'16:51',' او الله وویل: تاسو دوه معبودان مه نیسئ، همدا خبره ده چې هغه یو معبود دى، نو تاسو خاص له ما نه ووېرېږئ');
INSERT INTO "abulsalam" VALUES(16,52,'16:52',' او خاص د هغهٔ لپاره دي هغه څه چې په اسمانونو او ځمكه كې دي او عبادت خاص هم د دهٔ لپاره دى، همېشه، ایا نو تاسو غير له الله نه (له بل چا نه) وېرېږئ؟');
INSERT INTO "abulsalam" VALUES(16,53,'16:53',' او پر تاسو چې هر نعمت شوى دى، نو د الله له طرفه دى، بیا چې كله تاسو ته ضرر درورسېږي، نو (بيا هم) خاص هغه ته زاري كوئ ');
INSERT INTO "abulsalam" VALUES(16,54,'16:54',' بیا كله چې هغه له تاسو نه تكلیف لرې كړي (، نو) ناڅاپه یوه ډله په تاسو كې له خپل رب سره شریكان جوړوي');
INSERT INTO "abulsalam" VALUES(16,55,'16:55',' د دې لپاره چې ناشكري وكړي د هغه څه چې مونږ دوى ته وركړي دي، نو تاسو فايده واخلئ، پس ژر به تاسو پوه شئ');
INSERT INTO "abulsalam" VALUES(16,56,'16:56',' او دوى د هغه څه لپاره چې پر هېڅ نه پوهېږي؛ برخه مقرروي په هغه څه كې چې مونږ دوى ته وركړي دي، قسم دى پر الله چې خامخا له تاسو به هرومرو پوښتنه كېږي، د هغه څه په باره كې چې تاسو به دروغ تړل');
INSERT INTO "abulsalam" VALUES(16,57,'16:57',' او دوى د الله لپاره لوڼه مقرروي، (حال دا چې) هغه (له دې نه) پاك دى، او د دوى لپاره (بيا) هغه (زامن) دي چې دوى يې خوښوي');
INSERT INTO "abulsalam" VALUES(16,58,'16:58',' او كله چې په دوى كې یو ته د لور زېرى وركړى شي د هغه مخ تك تور شي، په دې حال كې چې له قهره ډك وي');
INSERT INTO "abulsalam" VALUES(16,59,'16:59',' له خلقو څخه پټ ګرځي د هغه څه د بدۍ په وجه چې ده ته يې زېرى وركړى شوى دى (او سوچ كوي چې)، ایا دى دغه (لور) سره له ذلته وساتي، یا هغه په خاورو كې ومنډي، خبردار شئ ! بد دي هغه څه چې دوى يې فیصله كوي');
INSERT INTO "abulsalam" VALUES(16,60,'16:60',' د هغو كسانو لپاره چې پر اخرت ایمان نه راوړي بد مثال دى او خاص د الله لپاره تر ټولو لوړ مثال دى او هم هغه ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(16,61,'16:61',' او كه چېرې الله خلق د ظلم په وجه نیولى (، نو) هغهٔ به د دې (ځمكې) پر سر هېڅ خوځنده نه و پریښى او لېكن هغه دوى تر مقررې نېټې وروسته كوي، نو كله چې د دوى نېټه راشي (بيا به نو) دوى یوه ګړۍ نه وروسته كېږي او نه مخكې كېږي');
INSERT INTO "abulsalam" VALUES(16,62,'16:62',' او دوى د الله لپاره هغه څه ثابتوي چې (پخپله) يې بد ګڼي او د دوى ژبې دروغ بیانوي چې بېشكه د همدوى لپاره تر ټولو ښه حالت دى، حقیقت دا دى چې بېشكه د همدوى لپاره اور دى او بېشكه همدوى تر ټولو مخكې لېږدول شوي دي');
INSERT INTO "abulsalam" VALUES(16,63,'16:63',' قسم دى پر الله! یقینًا یقینًا مونږ امتونو ته له تا نه مخكې رسولان لېږلي وو، نو شیطان دوى ته د دوى عملونه ښكلي كړل، نو نن ورځ همغه د دوى دوست دى او د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(16,64,'16:64',' او مونږ په تا باندې كتاب نه دى نازل كړى مګر د دې لپاره چې ته دوى ته هغه (كار) ښه واضحه كړې چې دوى په هغه كې اختلاف كوي او د هغه قوم د هدایت او رحمت لپاره (مو نازل كړى دي) چې ایمان لري');
INSERT INTO "abulsalam" VALUES(16,65,'16:65',' او الله له بره نه اوبه نازلې كړې دي، بیا يې په هغو سره ځمكه راژوندۍ كړې ده د هغې له مرګ نه پس، بېشكه په دې كې خامخا لویه نښه ده د هغو خلقو لپاره چې (حق او نصيحت) اوري');
INSERT INTO "abulsalam" VALUES(16,66,'16:66',' او بېشكه تاسو لپاره په څارویو كې خامخا لوى عبرت دى، مونږ پر تاسو څښوو ځینې له هغو څیزونو څخه چې د دوى په ګېډو كې دي د غوشايیو او وینې په مینځ كې خالص شیده (پۍ) چې د څښونكو لپاره له حلق نه په اسانۍ سره تېرېدونكي دي');
INSERT INTO "abulsalam" VALUES(16,67,'16:67',' او د كجورو او انګورو له مېوو څخه، تاسو له دغو نه نشه كوونكي (څيزونه) او ښكلى رزق جوړوئ، بېشكه په دې كې خامخا لویه نښه ده د هغو خلقو لپاره چې له عقل نه كار اخلي');
INSERT INTO "abulsalam" VALUES(16,68,'16:68',' او ستا رب د شاتو مچیو ته په زړه كې واچوله چې په غرونو كې كورونه جوړ كړه او په ونو كې او په هغو (ځايونو، چوپالونو) كې چې خلق يې لوړ جوړوي');
INSERT INTO "abulsalam" VALUES(16,69,'16:69',' بیا ته خوره له هر قسمه مېوو څخه، نو ته ځه د خپل رب په لارو باندې چې اسانې دي، د دوى له خېټو نه مشروب راوځي چې رنګونه يې مختلف دي، په ده كې د خلقو لپاره شفا ده، بېشكه په دې كې لویه نښه ده د هغه قوم لپاره چې ښه فكر كوي');
INSERT INTO "abulsalam" VALUES(16,70,'16:70',' او الله تاسو پیداكړي یئ، بیا به هغه تاسو وفات كوي او په تاسو كې ځینې هغه دي چې ډېر خوار ژوندون ته ورګرځولى شي، د دې لپاره چې له علم نه بعد پر هېڅ شي پوه نشي، بېشكه الله ښه عالم، ښه قادر دى');
INSERT INTO "abulsalam" VALUES(16,71,'16:71',' او الله ستاسو ځینې په ځینو نورو باندې په رزق كې غوره كړي دي، نو هغه كسان چې غوره كړى شوي دي هغوى خپل رزق لره ورګرځوونكي (وركوونكي) نه دي هغو كسانو ته چې دوى يې مالكان شوي دي، (د دوى وينځې غلامان دي) نو دوى په دغه (رزق) كې برابر دي؟ ایا نو د الله له نعمتونو نه دوى انكار كوي');
INSERT INTO "abulsalam" VALUES(16,72,'16:72',' او الله تاسو لپاره ستاسو له جنسه جوړې پیدا كړي دي او تاسو لپاره يې ستاسو له جوړو نه زامن او لمسي (اولادونه) پیدا كړي دي او تاسو ته يې له پاكیزه څیزونو څخه روزي دركړې ده، ایا نو په باطل باندې دوى ایمان راوړي او د الله په نعمتونو سره دوى كفر كوي');
INSERT INTO "abulsalam" VALUES(16,73,'16:73',' او دوى له الله نه غیر د هغو څیزونو عبادت كوي چې د دوى لپاره اختیار نه لري د هېڅ رزق له اسمانونو او ځمكې نه د هېڅ شي او نه دوى طاقت لري');
INSERT INTO "abulsalam" VALUES(16,74,'16:74',' نو تاسو د الله لپاره مثالونه مه بیانوئ، بېشكه الله پوهېږي او تاسو نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(16,75,'16:75',' الله یو مثال بيانوي چې یو مملوك مريى دى چې پر هېڅ شي قدرت نه لري او (بل) هغه څوك دى چې مونږ هغه ته له خپل جانبه ښكلى رزق وركړى دى، نو هغه له ده نه په پټه او ښكاره انفاق كوي، ایا دوى سره برابرېږي؟ ټولې ستاينې خاص الله لره دي، بلكې د دوى زیاتره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(16,76,'16:76',' او الله یو مثال بیانوي چې دوه سړي دي، په دواړو كې یو ګونګى دى، پر هېڅ شي قدرت نه لري، په دې حال كې چې هغه په خپل بادار باندې بار (بوج) دى، چې چېرته يې هم ولېږي هېڅ خیر نه راوړي، ایا برابرېږي دغه كس او هغه څوك چې په انصاف سره حكم كوي او هغه په نېغې لارې باندې قايم دى');
INSERT INTO "abulsalam" VALUES(16,77,'16:77',' او خاص د الله لپاره دي غیب د اسمانونو او ځمكې، او د قیامت امر نه دى مګر په شان د رپ د سترګو، یا هغه زیات قریب دى (له دې نه هم) بېشكه الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(16,78,'16:78',' او الله تاسو د خپلو میندو له خیټو نه راووېستئ، په داسې حال كې چې پر هېڅ شي نه پوهېدلئ او تاسو ته يې غوږونه او سترګې او زړونه در كړل، د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(16,79,'16:79',' ایا دوى مرغانو ته نه دي كتلي چې د اسمان په فضا كې مُسَخَّر شوي دي، دوى لره نه ساتي (له غورځېدلو نه) مګر الله، بېشكه په دغو (څيزونو) كې خامخا ډېرې نښې دي د هغو خلقو لپاره چې ایمان راوړي');
INSERT INTO "abulsalam" VALUES(16,80,'16:80',' او الله تاسو لپاره ستاسو له كورونو نه د استوګنې ځایونه جوړ كړي دي او تاسو لپاره يې د څارویو له څرمنو نه كورونه جوړ كړي دي چې تاسو يې د خپل سفر په ورځ سپك مومئ او د اقامت په ورځ هم او د هغو له وړیو او د هغو له وېښتانو او د هغو له وژغنو نه يې اسبابونه او تر یو وخته پورې د نفعې سامانونه (جوړ كړي دي)');
INSERT INTO "abulsalam" VALUES(16,81,'16:81',' او الله له خپلو پيدا كړيو څیزونو نه تاسو لپاره سیوري تيار كړي دي او تاسو لپاره يې په غرونو كې پټ ځایونه جوړ كړي دي او تاسو لپاره يې داسې قمیصونه پیدا كړي دي چې تاسو له ګرمۍ نه ساتي او داسې قمیصونه (زغرې) چې تاسو له جنګ نه ساتي، همدارنګه هغه خپل نعمتونه پر تاسو باندې پوره كوي، د دې لپاره چې تاسو اسلام راوړئ');
INSERT INTO "abulsalam" VALUES(16,82,'16:82',' نو كه دوى مخ واړوي، نو همدا خبره ده چې په تا باندې يواځې ښكاره رَسَوَل (تبليغ كول) لازم دي');
INSERT INTO "abulsalam" VALUES(16,83,'16:83',' دوى د الله نعمتونه پېژني، بیا له هغو نه انكار كوي او د دوى زیاتره كافران دي');
INSERT INTO "abulsalam" VALUES(16,84,'16:84',' او هغه ورځ چې مونږ به له هر امت نه یو ګواه راپورته كړو، بیا به هغو كسانو ته چې كفر يې كړى دى، اجازت نه وركولى شي او نه به له دوى نه د راضي كولو طلب كولى شي');
INSERT INTO "abulsalam" VALUES(16,85,'16:85',' او كله چې هغه كسان چې ظلم يې كړى دى عذاب وویني، نو له دوى نه به هغه سپك (كم) نه كړى شي او نه به دوى ته مهلت وركولى شي');
INSERT INTO "abulsalam" VALUES(16,86,'16:86',' او كله چې مشركان خپل شریكان وویني، دوى به ووايي: اى زمونږ ربه! دا زمونږ هغه شریكان دي چې مونږ به له تا نه غیر بلل، نو شریكان به دوى ته خبره وروغورځوي (جواب به وركړي) چې بېشكه تاسو یقینًا دروغجنا یئ ');
INSERT INTO "abulsalam" VALUES(16,87,'16:87',' او مشركان به په دغې ورځې كې الله ته تسليمي وړاندې كړي او له دوى نه به هغه څه ورك شي چې دوى به دروغ تړل');
INSERT INTO "abulsalam" VALUES(16,88,'16:88',' هغه كسان چې كفر يې كړى دى او د الله له لارې نه يې بندول كړي دي، مونږ به هغوى ته عذاب د عذاب له پاسه ورزیات كړو، په سبب د هغه چې دوى به فساد كاوه ');
INSERT INTO "abulsalam" VALUES(16,89,'16:89',' او په هغې ورځې كې چې مونږ به په هر امت كې یو ګواه راپورته كړو د دوى په خلاف د همدوى له نفسونو نه او مونږ به تا په دوى باندې ګواه راولو او مونږ په تا باندې كتاب نازل كړى دى، په داسې حال كې چې (د دين) د هر شي لپاره (په كې) واضحه بیان دى او هدایت او رحمت او زېرى دى د مسلمانانو لپاره ');
INSERT INTO "abulsalam" VALUES(16,90,'16:90',' بېشكه الله د انصاف كولو او احسان كولو او خپلوانو ته د وركړې حكم كوي او له فحاشۍ او بدو كارونو او سركشۍ نه منع كوي، هغه تاسو ته وعظ كوي، د دې لپاره چې تاسو نصیحت قبول كړئ');
INSERT INTO "abulsalam" VALUES(16,91,'16:91',' او تاسو د الله وعده پوره كړئ كله چې تاسو وعده وكړئ او خپل قسمونه د هغو له پخولو نه بعد مه ماتوئ، حال دا چې تاسو الله پر خپلو ځانونو كفیل (ضامن) ګرځولى دى، بېشكه الله پوهېږي پر هغه څه چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(16,92,'16:92',' او تاسو د هغې ښځې په شان مه كېږئ چې خپل ورېشلي تارونه به يې له مضبوطوالي نه پس بېرته اومه كول، په دې حال كې چې ټوټې ټوټې وي، تاسو خپل قسمونه په خپل مینځ كې د ټګۍ ذریعه جوړوئ، په دې وجه چې شي یوه ډله، هغه ډېره زیاته (غالبه او فايده اخيستونكې) له بلې ډلې نه، همدا خبره ده چې الله تاسو په دې سره ازمايي او خامخا ضرور به د قیامت په ورځ تاسو ته بیانوي هغه څه چې تاسو به په هغه كې اختلاف كاوه');
INSERT INTO "abulsalam" VALUES(16,93,'16:93',' او كه چېرې الله غوښتلى (نو) خامخا تاسو به يې یو امت ګرځولي وى او لېكن هغه ګمراه كوي چا چې وغواړي او هدایت وركوي چا ته چې وغواړي او خامخا ضرور به له تاسو نه تپوس كېږي د هغو عملونو په باره كې چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(16,94,'16:94',' او تاسو خپل قسمونه په خپل مینځ كې د ټګۍ ذریعه مه جوړوئ، نو (ګنې) قدم به (پر اسلام) د هغه له ټېنګېدلو نه پس وښويېږي او تاسو به بدي وڅكئ، په سبب د هغه چې تاسو د الله له لارې نه بندول كړي دي او تاسو لپاره ډېر لوى عذاب دى');
INSERT INTO "abulsalam" VALUES(16,95,'16:95',' او تاسو د الله په عهد سره لږې پیسې (دنياوي مال و دولت) مه اخلئ، بېشكه هغه څه چې د الله په نیز دي هغه تاسو لپاره ډېر غوره دي، كه چېرې تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(16,96,'16:96',' هغه څه چې له تاسو سره دي هغه ختمېږي او څه چې له الله سره دي هغه باقي دي او خامخا ضرور به مونږ هغو كسانو ته په عوض كې د هغوى اجر وركړو چې صبر يې كړى دى د هغو تر ټولو ښو عملونو په بدل كې چې دوى به كول');
INSERT INTO "abulsalam" VALUES(16,97,'16:97',' چا چې نېك عمل وكړ، سړى وي، يا كه ښځه، په داسې حال كې چې مومن وي، نو خامخا هرومرو به مونږ هغه ته ژوند وركړو، ډېر پاكېزه ژوند او خامخا هرومرو به مونږ هغوى ته د هغوى د اجر جزا وركړو د هغو تر ټولو ښو عملونو په بدل كې چې دوى به كول');
INSERT INTO "abulsalam" VALUES(16,98,'16:98',' نو كله چې ته قرآن لولې، نو د الله پناه وغواړه د رټل شوي شیطان (له شر) نه');
INSERT INTO "abulsalam" VALUES(16,99,'16:99',' بېشكه چې دى، نشته ده ته هېڅ غلبه په هغو كسانو باندې چې ایمان يې راوړى دى او چې خاص په خپل رب باندې هغوى توكل كوي');
INSERT INTO "abulsalam" VALUES(16,100,'16:100',' همداخبره ده چې د ده غلبه په هغو كسانو باندې ده چې له ده سره دوستي كوي او په هغو كسانو باندې چې هغوى له هغه (الله) سره شریك جوړونكي دي');
INSERT INTO "abulsalam" VALUES(16,101,'16:101',' او كله چې مونږ یو ایت د بل ایت په ځاى كې تبديل كړو، او الله ښه پوه دى پر هغه څه چې هغه يې نازلوي، دوى وايي: خبره همدا ده چې ته دروغ تړونكى يې، بلكې د دوى اكثره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(16,102,'16:102',' ته ووایه: هغه ستا د رب له جانبه په حقه سره روح القدس (جبریل) نازل كړى دى، د دې لپاره چې هغه كسان ثابت او ټینګ كړي چې ایمان يې راوړى دى او د مسلمانانو د هدایت او زېري لپاره ');
INSERT INTO "abulsalam" VALUES(16,103,'16:103',' او یقینًا یقینًا مونږ پوهېږو چې بېشكه دوى وايي: خبره همدا ده چې ده ته یو انسان ښوونه كوي، د هغه كس ژبه چې دوى هغه ته نسبت كوي؛ عجمي ده او دا (قرآن) واضحه عربي ژبه ده');
INSERT INTO "abulsalam" VALUES(16,104,'16:104',' بېشكه هغه كسان چې د الله په ایتونو باندې ایمان راوړي، الله دوى ته هدایت نه كوي او دوى ته ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(16,105,'16:105',' خبره همدا ده چې دروغ هغه كسان تړي چې د الله په ایتونو باندې ایمان نه راوړي او همدغه خلق دروغجن دي ');
INSERT INTO "abulsalam" VALUES(16,106,'16:106',' هغه څوك چې په الله سره كافر شو د خپل ایمان له راوړلو نه پس، غیر له هغه كس نه چې زور پرې وكړى شي، په دې حال كې چې د هغهٔ زړهٔ په ایمان سره مطمئن وي او لېكن هغه څوك چې په كفر سره خپله سینه فراخه كړي (له زړه نه پرې راي شي)، نو په دغو خلقو باندې د الله له جانبه غضب دى او د دوى لپاره ډېر لوى عذاب دى ');
INSERT INTO "abulsalam" VALUES(16,107,'16:107',' دا په سبب د دې چې بېشكه دوى دنيايي ژوند په اخرت باندې غوره كړى دى او دا چې بېشكه الله كافر قوم ته هدایت نه كوي');
INSERT INTO "abulsalam" VALUES(16,108,'16:108',' دا همغه كسان دي چې الله د دوى پر زړونو او د دوى پر غوږونو او د دوى پر سترګو باندې مهر لګولى دى او همدغه كسان غافلان دي');
INSERT INTO "abulsalam" VALUES(16,109,'16:109',' حقیقت دا دى چې بېشكه دوى په اخرت كې، همدوى تاوانیان دي');
INSERT INTO "abulsalam" VALUES(16,110,'16:110',' بیا بېشكه ستا رب د هغو كسانو لپاره چې هجرت يې كړى دى وروسته له دې چې په فتنو كې واچول شول، بیا يې جهاد هم كړى دى او صبر يې كړى دى، بېشكه ستا رب له دې نه پس خامخا ښه مغفرت كوونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(16,111,'16:111',' هغه ورځ چې هر كس به په داسې حال كې راځي چې د خپل نفس له طرف نه به جګړه كوي او هر نفس ته به پوره وركړى شي هغه څه چې ده كړي دي، په دې حال كې چې پر دوى به ظلم ونه كړى شي');
INSERT INTO "abulsalam" VALUES(16,112,'16:112',' او الله د یو كلي مثال بیانوي چې د امن والا (او) اطمينان والا و، دغه (كلي) ته به د هغه رزق له هره ځایه پرېمانه راته، نو دغه كلي د الله د نعمتونو ناشكري وكړه، نو الله په دغه كلي باندې د لوږې او وېرې لباس وڅكاوه (لوږه او وېره يې پرې راوستله) په سبب د هغو عملونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(16,113,'16:113',' او یقینًا یقینًا دوى ته له همدوى ځنې رسول راغلى و، نو دوى د هغه تكذیب وكړ، نو دوى عذاب ونیول، په داسې حال كې چې دوى ظالمان وو');
INSERT INTO "abulsalam" VALUES(16,114,'16:114',' نو تاسو خورئ له هغو (خوراكونو) نه چې تاسو ته الله دركړي دي، چې حلال، پاكيزه دي او تاسو د الله د نعمتونو شكر وباسئ كه چېرې تاسو خاص د هغهٔ عبادت كوئ');
INSERT INTO "abulsalam" VALUES(16,115,'16:115',' همدا خبره ده چې هغه پر تاسو باندې حرامه كړې ده مرداره او وینه او د خنزیر غوښه او هغه چې په هغه باندې د غیرالله لپاره اواز پورته شوى وي، نو هغه څوك چې مجبوره شي ، په دې حال كې چې نه ظالم وي او نه (له حده) تجاوز كوونكى وي، نو بېشكه الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(16,116,'16:116',' او په دې وجه چې ستاسو ژبې دروغ بیانوې تاسو دا مه وایئ چې دا حلال دي او دا حرام دي، د دې لپاره چې تاسو په الله باندې له ځانه دروغ جوړ كړئ، بېشكه هغه كسان چې په الله باندې دروغ تپي، هغوى به خلاصى ونه مومي');
INSERT INTO "abulsalam" VALUES(16,117,'16:117',' ډېره لږه فايده ده، او د دوى لپاره ډېر دردوونكى عذاب دى ');
INSERT INTO "abulsalam" VALUES(16,118,'16:118',' او په هغو كسانو باندې چې یهودیان شوي دي، مونږ حرام كړي دي هغه څه چې مونږ تا ته له دې نه مخكې بیان كړي دي او مونږ پر هغوى باندې ظلم نه دى كړى او لېكن هغوى وو چې پر خپلو ځانونو يې ظلم كاوه ');
INSERT INTO "abulsalam" VALUES(16,119,'16:119',' بیا بېشكه ستا رب هغو كسانو ته چې بدي يې په ناپوهۍ سره كړې ده، بیا يې له دې نه بعد توبه ووېستله او اصلاح يې وكړه، بېشكه ستا رب له دې (توبې) نه بعد خامخا ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(16,120,'16:120',' بېشكه ابراهیم یو امت و، د الله فرمان منونكى، حق ته مايله او نه و هغه له مشركانو څخه');
INSERT INTO "abulsalam" VALUES(16,121,'16:121',' د هغهٔ د نعمتونو شكر كوونكى و، الله هغه غوره كړى و او هغه ته يې د نېغې لارې هدایت كړى و ');
INSERT INTO "abulsalam" VALUES(16,122,'16:122',' او مونږ هغه ته په دنیا كې نېكي (او ښېګڼه) وركړې وه او بېشكه هغه په اخرت كې خامخا له صالحانو څخه دى');
INSERT INTO "abulsalam" VALUES(16,123,'16:123',' بیا مونږ تا ته وحي وكړه چې د ابراهیم د ملت پیروي وكړه چې حنیف و او له مشركانو څخه نه و ');
INSERT INTO "abulsalam" VALUES(16,124,'16:124',' همدا خبره ده چې (ورځ د) خالي پر هغو كسانو مقرر شوې ده چې په دې كې يې اختلاف كړى و او بېشكه ستا رب به خامخا د دوى په مینځ كې د قیامت په ورځ فیصله كوي په هغه څیز كې چې دوى به په هغه كې اختلاف كاوه');
INSERT INTO "abulsalam" VALUES(16,125,'16:125',' ته د خپل رب لارې ته په حكمت سره او ښكلي نصیحت سره بلنه كوه او له دوى سره په هغې طریقې سره مجادله (مباحثه) كوه چې هغه تر ټولو ښه وي، بېشكه ستا رب (چې دى) هغه ښه پوه دى پر هغه كس چې د هغه له لارې نه ګمراه شوى دى او هغه ښه پوه دى پر لار موندونكو باندې ');
INSERT INTO "abulsalam" VALUES(16,126,'16:126',' او كه تاسو سزا (عذاب) وركوئ، نو د هغې سزا (عذاب) په مثل يې وركوئ چې تاسو ته په هغه سره عذاب دركړى شوى دى او البته كه تاسو صبر وكړئ، (نو) یقینًا دا د صبر كوونكو لپاره ډېر غوره دى ');
INSERT INTO "abulsalam" VALUES(16,127,'16:127',' او ته صبر كوه او نه دى صبر ستا مګر په توفیق د الله سره او په دوى باندې مه غمژن كېږه او په زړه تنګۍ كې مه پرېوځه د هغو مكرونو (فريبونه) په وجه چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(16,128,'16:128',' بېشكه الله د هغو كسانو ملګرى دى چې پرهېزګاري كوي او د هغو كسانو چې هغوى نېكي كوونكي دي');
INSERT INTO "abulsalam" VALUES(17,1,'17:1',' هغه ذات لره پاكي ده چې خپل بنده يې د شپې په یوه لږه برخه كې له مسجد حرام نه مسجد اقصٰى ته بوتلو، هغه (مسجد اقصٰى) چې مونږ د هغه ګېرچاپېره بركت اچولى دى، د دې لپاره چې مونږ هغه ته له خپلو نښو څخه ځینې وښیو، بېشكه هم هغه ښه اورېدونكى، ښه لېدونكى دى');
INSERT INTO "abulsalam" VALUES(17,2,'17:2',' او مونږ موسٰى ته كتاب وركړى و او هغه مو د بني اسرائیلو لپاره هدایت ګرځولى و، چې تاسو له ما نه غیر هېڅ كارساز مه نیسئ ');
INSERT INTO "abulsalam" VALUES(17,3,'17:3',' اى د هغه چا اولاده چې مونږ له نوح سره سواره كړي وو، بېشكه هغه ډېر شكر ایستونكى بنده و ');
INSERT INTO "abulsalam" VALUES(17,4,'17:4',' او مونږ بني اسرائیلو ته په كتاب كې دا قطعي خبردارى وركړى و چې تاسو به خامخا هرومرو په ځمكه كې دوه ځلې فساد كوئ او خامخا هرومرو به تاسو سركشي كوئ، ډېره لویه سركشي ');
INSERT INTO "abulsalam" VALUES(17,5,'17:5',' نو كله چې په هغو دواړو كې د اول ځلې وعده راغله (نو) مونږ پر تاسو باندې خپل داسې بنده ګان مسلط كړل چې ډېر سخت جنګي خلق وو، نو هغوى د ښارونو په مینځ كې ښه وګرځېدل او وه دا یوه وعده چې پوره كولى شوه ');
INSERT INTO "abulsalam" VALUES(17,6,'17:6',' بیا مونږ بېرته تاسو ته پر هغوى باندې غلبه دروګرځوله او ستاسو مدد مو وكړ په مالونو او زامنو سره او تاسو مو د ملاتړ (او افرادو) په لحاظ بېخي زیات كړئ');
INSERT INTO "abulsalam" VALUES(17,7,'17:7',' كه تاسو نېكي وكړئ (نو) د خپلو ځانونو لپاره به نېكي وكړئ او كه چېرې تاسو بدي وكړئ، نو د همدغو (نفسونو) لپاره ده، نو كله چې د اخري ځل وعده راغله (نو مونږ پر تاسو باندې نور بنده ګان مُسلط كړل)، د دې لپاره چې (په وهلو وهلو) ستاسو مخونه وران ويجاړه كړي او د دې لپاره چې (اقصا) جومات ته داخل شي لكه څنګه چې اول ځلې ده ته داخل شوي وو، او د دې لپاره چې هلاك او تباه كړي هر هغه شى چې دوى پرې غالب شي، هلاكول ');
INSERT INTO "abulsalam" VALUES(17,8,'17:8',' امېد دى چې ستاسو رب به پر تاسو رحم وكړي او كه تاسو بېرته وګرځېدئ (سركشۍ ته، نو) مونږ به هم راوګرځو (عذاب دركولو ته)، او مونږ جهنم د كافرانو لپاره زندان ګرځولى دى ');
INSERT INTO "abulsalam" VALUES(17,9,'17:9',' بېشكه دا قرآن هغې (لارې) ته ښودنه كوي چې تر ټولو برابره نېغه ده او هغو مومنانو ته زېرى وركوي چې نېك عملونه كوي، د دې چې يقينًا د دوى لپاره ډېر لوى اجر دى ');
INSERT INTO "abulsalam" VALUES(17,10,'17:10',' او دا چې بېشكه هغه كسان چې په اخرت باندې ایمان نه راوړي، مونږ د هغوى لپاره ډېر دردوونكى عذاب تیار كړى دى ');
INSERT INTO "abulsalam" VALUES(17,11,'17:11',' او انسان د شر دعا غواړي د خپلې خیر د دعا (په شان) او انسان له شروع نه ډېر تلوار كوونكى دى ');
INSERT INTO "abulsalam" VALUES(17,12,'17:12',' او مونږ شپه او ورځ دوه نښې جوړې كړي دي، بیا مونږ د شپې نښه تته وګرځوله او د ورځې نښه مو روښانه وګرځوله، د دې لپاره چې تاسو د خپل رب فضل طلب كړئ او د دې لپاره چې تاسو د كلونو شمېر او حساب وپېژنئ او هر شى، مونږ هغه ښه په تفصیل سره بیان كړى دى ');
INSERT INTO "abulsalam" VALUES(17,13,'17:13',' او هر انسان لره، مونږ هغه ته خپل عمل د هغه په غاړه كې لازم كړى دى او د قیامت په ورځ به ده ته یو كتاب راوباسو چې دى به ورسره مخامخ شي، په داسې حال كې چې غوړېدلى به وي');
INSERT INTO "abulsalam" VALUES(17,14,'17:14',' ته خپل كتاب ولوله، نن ورځ ستا په خلاف هم ستا خپل نفس كافي دى، حساب كوونكى ');
INSERT INTO "abulsalam" VALUES(17,15,'17:15',' چا چې نېغه لار ونیوله، نو همدا خبره ده چې هغه د خپل ځان (د فايدې) لپاره نېغه لار نیسي او څوك چې بې لارې شو، نو همدا خبره ده چې هغه د همدغه (نفس) په ضرر بې لارې كېږي او هېڅ بار اوچتونكى د بل بار نه اوچتوي او مونږ كله هم عذاب وركوونكي نه یو تر هغه پورې چې رسول ولېږو');
INSERT INTO "abulsalam" VALUES(17,16,'17:16',' او كله چې مونږ اراده وكړو چې یو كلى هلاك كړو (نو) مونږ د هغه (كلي) كبرجنو مالدارو ته حكم وكړو، نو هغوى (حكم ونه مني بلكې) په دغه (كلي) كې فسق وكړي، نو په هغه باندې خبره ثابته شي، بیا مونږ هغه تباه كړو، سخت تباه كول');
INSERT INTO "abulsalam" VALUES(17,17,'17:17',' او مونږ له نوح نه وروسته څومره ډېر قرنونه (امتونه) هلاك كړي دي! او ستا رب ښه كافي دى د خپلو بنده ګانو په ګناهونو باندې ښه خبردار، ښه لیدونكى');
INSERT INTO "abulsalam" VALUES(17,18,'17:18',' هغه څوك چې داسې وي چې دنیا غواړي (نو) مونږ به هغه ته په دې كې هغه څه په تلوار وركړو چې مونږ يې غواړو، د هغه چا لپاره چې مونږ يې اراده وكړو، بیا مونږ د هغهٔ لپاره جهنم مقرر كړو چې دى به هغه ته ننوځي، په داسې حال كې چې ملامت كړى شوى، شړل شوى به وي');
INSERT INTO "abulsalam" VALUES(17,19,'17:19',' او هغه څوك چې اخرت غواړي او د هغه لپاره كوشش كوي، د هغهٔ لايق كوشش، په دې حال كې چې مومن (هم) وي، نو دغه خلق، د دوى سعي قدر كړى شوې (او مقبوله) ده');
INSERT INTO "abulsalam" VALUES(17,20,'17:20',' له هر یو سره مونږ ستا د رب له وركړې څخه مدد كوو، له دوى سره او له دغو سره، او ستا د رب عطا (وركړه په دنیا كې) كله هم منع كړى شوې نه ده');
INSERT INTO "abulsalam" VALUES(17,21,'17:21',' ته وګوره! څرنګه مونږ د دوى ځینې، په ځینو نورو باندې غوره كړي دي او خامخا اخرت د درجو په لحاظ تر ټولو لوى او د فضیلت وركولو په لحاظ تر ټولو غټ دى ');
INSERT INTO "abulsalam" VALUES(17,22,'17:22',' ته له الله سره بل معبود مه نیسه، نو (ګنې) ملامت كړى شوى، بې یارومددګار به كېنې');
INSERT INTO "abulsalam" VALUES(17,23,'17:23',' او ستا رب حكم كړى دى چې تاسو عبادت مه كوئ (د بل هیچا) مګر يواځې د هغهٔ او له مور و پلار سره ښه احسان كوئ او كه چېرې په دوى كې یو یا دواړه له تا سره كله هم زوړوالي (بوډاوالي) ته ورسېږي، نو ته دې دواړو ته اُف (هم) مه وایه او مه دوى رټه او ته دوى ته ډېره تعظیمي خبره وایه');
INSERT INTO "abulsalam" VALUES(17,24,'17:24',' او ته د دوى لپاره له مهربانۍ نه د تواضع وزر ښكته كړه او وایه: اى زما ربه! ته پر دوى دواړو مهرباني وكړه، لكه څنګه چې دوى دواړو زما پالنه كړې ده، په دې حال كې چې زه وړوكى وم');
INSERT INTO "abulsalam" VALUES(17,25,'17:25',' ستاسو رب پر هغه څه ښه پوه دى چې ستاسو په نفسونو كې دي كه تاسو نېكان یئ، نو بېشكه هغه له شروع نه د توبه كوونكو لپاره ډېر مغفرت كوونكى دى');
INSERT INTO "abulsalam" VALUES(17,26,'17:26',' او د خپلوۍ خاوند ته د هغه حق وركړه او مسكین او مسافر ته (هم) او اسراف مه كوه، اسراف كول');
INSERT INTO "abulsalam" VALUES(17,27,'17:27',' بېشكه اسراف كوونكي د شیطانانو وروڼه دي او شیطان له همېشه د خپل رب ډېر ناشكره دى');
INSERT INTO "abulsalam" VALUES(17,28,'17:28',' او كه چېرې ته له دوى نه خامخا اعراض كوې (مخ اړوې)، د خپل رب له جانبه د رحمت د طلب لپاره چې ته يې امېد لرې، نو دوى ته ډېره اسانه خبره وایه');
INSERT INTO "abulsalam" VALUES(17,29,'17:29',' او مه كوه خپل لاس تر خپلې غاړې پورې تړل شوى او مه هغه غځوه، بېخي پوره غځول (ګنې) نو ملامت كړى شوى، پښېمانه شوى به كېنې');
INSERT INTO "abulsalam" VALUES(17,30,'17:30',' بېشكه ستا رب رزق فراخوي چا ته چې وغواړي او تنګوي يې، (چا ته چې وغواړی) بېشكه هغه پر خپلو بنده ګانو ښه خبردار، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(17,31,'17:31',' او تاسو خپل اولاد د فقر له وېرې مه وژنئ، هم دا مونږ هغوى ته روزي وركوو او تاسو ته (هم) بېشكه د دوى وژل له شروع نه ډېره لویه ګناه ده');
INSERT INTO "abulsalam" VALUES(17,32,'17:32',' او تاسو زنا ته مه نژدې كېږئ، بېشكه دا له شروع نه غټه بې حیايي ده او بده لار ده');
INSERT INTO "abulsalam" VALUES(17,33,'17:33',' او تاسو هغه نفس مه وژنئ چې الله حرام كړى دى مګر په حقه سره (د قانون او قاضي د حكم مطابق) او څوك چې ووژل شي، په دې حال كې چې مظلوم وي، نو یقینًا مونږ د هغه ولي (وارث) ته پوره غلبه وركړې ده، نو هغه دې په وژلو كې اسراف نه كوي، بېشكه دغه (وارث) مدد كړى شوى دى ');
INSERT INTO "abulsalam" VALUES(17,34,'17:34',' او تاسو د یتیم مال ته مه نژدې كېږئ مګر په هغې طریقې سره چې هغه تر ټولو ښه وي، تر هغه پورې چې هغه خپل د بلوغ حد ته ورسېږي او تاسو عهد پوره كوئ، بېشكه د عهد باره كې به پوښتنه كېږي. ');
INSERT INTO "abulsalam" VALUES(17,35,'17:35',' او تاسو پېمانه پوره وركوئ كله چې تاسو (څه شى) پېمانه كوئ او په ډېرې برابرې تلې سره تول كوئ دا (كار) تر ټولو غوره او د انجام په لحاظ تر ټولو ښه دى');
INSERT INTO "abulsalam" VALUES(17,36,'17:36',' او ته د هغه شي پیروي مه كوه چې تا ته د هغهٔ (صحيح) علم نه وي، بېشكه غوږونه او سترګې او زړه، په دغو كې د هر یو باره كې به تپوس كولى شي.');
INSERT INTO "abulsalam" VALUES(17,37,'17:37',' او په ځمكه كې په تكبر سره مه ګرځه، بېشكه چې ته به ځمكه كله هم څیرې نه كړى شې او په اوږدوالي كې به غرونو ته كله هم ونه رسېږې');
INSERT INTO "abulsalam" VALUES(17,38,'17:38',' دغه ټول كارونه (چې دي)، د دغو بدي (او خرابي) ستا د رب په نیز له شروع نه ناخوښه ده');
INSERT INTO "abulsalam" VALUES(17,39,'17:39',' دا (ټول حكمونه) له هغو د حكمت (او پوهې) له خبرو څخه دي چې ستا رب تا ته وحي كړي دي او ته له الله سره بل هېڅ معبود مه نیسه (ګنې) نو په جهنم كې به وغورځول شي، ملامت كړى شوى، رټل شوى');
INSERT INTO "abulsalam" VALUES(17,40,'17:40',' ایا ستاسو رب تاسو په زامنو سره غوره كړي یئ او (پخپله) هغه له ملايكو څخه جینكۍ نیولي دي؟ بېشكه تاسو یقینًا ډېره لویه (دروغجنه) خبره وایئ ');
INSERT INTO "abulsalam" VALUES(17,41,'17:41',' او یقینًا یقینًا مونږ په دې قرآن كې مكرر بیان كړى دى، د دې لپاره چې دوى نصیحت واخلي (خو دغه بیان) دوى ته نه زیاتوي مګر تېښته (لرې والى)');
INSERT INTO "abulsalam" VALUES(17,42,'17:42',' ته ووایه: كه له دغه (الله) سره نور معبودان وى، لكه څنګه چې دوى يې وايي (، نو) په دغه وخت كې به دوى خامخا د عرش د مالك په طرف لار لټولې وى ');
INSERT INTO "abulsalam" VALUES(17,43,'17:43',' هغه لره پاكي ده او هغه ډېر اوچت دى له هغو خبرو نه چې دوى يې وايي ډېر لوى اوچتېدل ');
INSERT INTO "abulsalam" VALUES(17,44,'17:44',' خاص د هغه لپاره تسبیح وايي اوه اسمانونه او ځمكه او هغه څوك چې په دغو كې دي، او نشته هېڅ یو شى مګر د هغه له ستاینې سره تسبیح وايي او لېكن تاسو د هغو پر تسبیح نه پوهېږئ، بېشكه هغه له ازله ډېر تحمل والا، ډېر بخښونكى دى');
INSERT INTO "abulsalam" VALUES(17,45,'17:45',' او كله چې ته قرآن لولې (، نو) مونږ ستا په مینځ كې او د هغو كسانو په مینځ كې چې پر اخرت ایمان نه راوړي یوه پټه پرده پیدا كړو');
INSERT INTO "abulsalam" VALUES(17,46,'17:46',' او مونږ د هغوى په زړونو باندې پردې واچوو له دې نه چې دوى پر ده پوه شي او د دوى په غوږونو كې كوڼوالى (پیداكړو)، او كله چې ته په قرآن كې، يواځې خپل رب یاد كړې، نو دوى په خپلو شاګانو بېرته وګرځي، په داسې حال كې چې ترهېدونكي وي');
INSERT INTO "abulsalam" VALUES(17,47,'17:47',' مونږ ښه پوه یو پر هغه (نیت) چې دوى په هغه سره اورېدل كوي، كله چې دوى تا ته غوږ ږدي او كله چې دوى پټې مشورې كوي، كله چې دا ظالمان وايي: تاسو پیروي نه كوئ مګر د داسې سړي چې جادو پرې شوى دى');
INSERT INTO "abulsalam" VALUES(17,48,'17:48',' ته وګوره! دوى ستا په باره كې مثالونه څنګه بیان كړل، نو ګمراه شول، نو د سمې لارې استطاعت نه لري');
INSERT INTO "abulsalam" VALUES(17,49,'17:49',' او دوى وايي ایا كله چې مونږ هډوكي او میده شوې خاورې شو، ایا په رښتيا به مونږ بیا راژوندي كولى شو په نويو پیدا كولو سره');
INSERT INTO "abulsalam" VALUES(17,50,'17:50',' ته (ورته) ووایه: تاسو كاڼي شئ، یا اوسپنه');
INSERT INTO "abulsalam" VALUES(17,51,'17:51',' یا كوم داسې مخلوق له هغه قسمه نه چې ستاسو په زړونو (او ذهنونو) كې لوى ښكاري، نو دوى به ژر ووايي: څوك به مونږ بیا راژوندي كوي؟ ته ووایه: هغه ذات چې تاسو يې اول كرت پیدا كړي یئ، نو ژر به دوى خپل سرونه تا ته (د استهزا په ډول) وخوځوي او وايي به: دا به كله وي؟ ته ووایه: امېد دى چې دغه به ډېر نژدې وي');
INSERT INTO "abulsalam" VALUES(17,52,'17:52',' په هغې ورځ كې چې هغه (الله) به تاسو راوبلي، نو تاسو به (د هغه بلل) قبول كړئ، په داسې حال كې چې د هغه ثنا به وایئ، او ګمان به كوئ چې تاسو (په دې حالت كې) نه یئ پاتې شوي مګر لږ شانې');
INSERT INTO "abulsalam" VALUES(17,53,'17:53',' او ته زما بنده ګانو ته ووایه چې هغه خبره دې وايي چې هغه تر ټولو ښه وي، بېشكه شیطان د دوى په مینځ كې دښمني اچوي، بېشكه شیطان د انسان لپاره له شروع نه ښكاره دښمن دى');
INSERT INTO "abulsalam" VALUES(17,54,'17:54',' ستاسو رب پر تاسو ښه پوه دى، كه هغه وغواړي (نو) پر تاسو به رحم وكړي او كه هغه وغواړي (نو) تاسو ته به عذاب دركړي او ته مونږ په دوى باندې ذمه وار نه يې لېږلى');
INSERT INTO "abulsalam" VALUES(17,55,'17:55',' او ستاسو رب ښه عالم دى پر هغه چا چې په اسمانونو او ځمكه كې دي او یقینًا یقینًا مونږ ځینو نبیانو ته په ځینو نورو باندې فضیلت وركړى دى او مونږ داود ته زبور وركړى و');
INSERT INTO "abulsalam" VALUES(17,56,'17:56',' ته ووایه: تاسو هغه (معبودان) راوبلئ چې تاسو يې له هغه (الله) نه غير عقیده لرئ، نو دوى اختیار نه لري له تاسو نه د ضرر لرې كولو او نه يې د ګرځولو');
INSERT INTO "abulsalam" VALUES(17,57,'17:57',' دغه خلق چې دوى يې بلي، هغه خلق دي چې خپل رب ته وسیله لټوي چې په دوى كې كوم یو زیات قریب دى، او د هغه د رحمت امېد لري او د هغه له عذابه وېرېږي، بېشكه ستا د رب عذاب له همېشه داسې دى چې (په كار ده چې) وېره ترې وكړى شي');
INSERT INTO "abulsalam" VALUES(17,58,'17:58',' او نشته هېڅ یو (كفر او سركښي كوونكى) كلى مګر د قیامت له ورځې نه مخكې مونږ هغه لره هلا كوونكي یو، یا هغه لره عذاب وركوونكي یو، ډېر سخت عذاب، دا له شروع نه په كتاب كې لیكل شوي دي');
INSERT INTO "abulsalam" VALUES(17,59,'17:59',' او مونږ نه یو منع كړي له دې نه چې (د دوى له غوښتنې سره سم) معجزې ولېږو مګر دې خبرې چې اولنیو خلقو د دغو (معجزو) تكذیب كړى و او مونږ ثمود ته اوښه وركړه، چې ښكاره معجزه وه، نو هغوى له هغې سره ظلم وكړ او مونږ معجزې نه رالېږو مګر د وېرولو لپاره');
INSERT INTO "abulsalam" VALUES(17,60,'17:60',' او هغه وخت (یاد كړه) چې مونږ تا ته وویل: بېشكه ستا رب ټول خلق راګېر كړي دي او هغه منظره چې مونږ تا ته وښودله مونږ نه ده ګرځولې مګر د خلقو لپاره ازمايش او هغه ونه هم (ازمايش ګرځولې) چې قرآن كې پرې لعنت شوى دى او مونږ دوى وېروو، نو (دغه وېرول) دوى ته نه زیاتوي مګر ډېره لويه سركشي');
INSERT INTO "abulsalam" VALUES(17,61,'17:61',' او كله چې مونږ ملايكو ته وویل: تاسو ادم ته سجده وكړئ، نو دوى سجده وكړه غیر له ابلیس نه، هغه وویل: ایا زه هغه چا ته سجده وكړم چې تا له خَټې (خاورې) نه پېدا كړى دى');
INSERT INTO "abulsalam" VALUES(17,62,'17:62',' هغه وویل: ما ته خبر راكړه! دا سړى چې تا هغه په ما باندې بهتر كړى دى، یقینًا كه تا زه د قیامت د ورځې پورې وروسته كړم (نو) زه به خامخا هرومرو د ده د اولادې بېخ وباسم، سوا له لږو ځنې');
INSERT INTO "abulsalam" VALUES(17,63,'17:63',' (الله) وویل چې ځه! نو په دوى كې چې چا ستا پیروي وكړه، نو بېشكه جهنم ستاسو جزا ده، ډېره پوره جزا');
INSERT INTO "abulsalam" VALUES(17,64,'17:64',' او ته په خپل اواز سره له دوى ځنې هغه څوك راپورته كړه چې ستا پرې وس رسېږي، او ته په دوى باندې خپل سواره او پیاده راجمع كړه او له دوى سره په مالونو او اولادونو كې شریك شه او دوى ته وعده وركړه، او شیطان دوى ته وعده نه وركوي مګر د دوكې');
INSERT INTO "abulsalam" VALUES(17,65,'17:65',' بېشكه زما بنده ګان چې دي، تا لره پر هغوى باندې هېڅ غلبه نشته او ستا رب ښه كافي كارساز دى');
INSERT INTO "abulsalam" VALUES(17,66,'17:66',' ستاسو رب هغه دى چې تاسو لپاره په سمندر كې بېړۍ روانوي، د دې لپاره چې تاسو د هغه فضل ولټوئ، بېشكه هغه پر تاسو له شروع نه ډېر رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(17,67,'17:67',' او كله چې تاسو ته په سمندر كې سختي درورسېږي (، نو) هغه څوك (ستاسو له فكره) ورك شي چې تاسو يې بلئ مګر يواځې هغه (الله بلئ)، نو كله چې تاسو وچې ته بچ كړي (،نو) تاسو (له توحیده) مخ اړوئ او انسان دى ډېر ناشكره');
INSERT INTO "abulsalam" VALUES(17,68,'17:68',' ایا نو تاسو له دې نه په امان كې یئ چې هغه تاسو د وچې ځمكې په یوه خوا كې ډوب (ښخ) كړي، یا پر تاسو باندې كاڼي وروونكى سخت باد راولېږي (او) بیا تاسو د خپل ځان لپاره هېڅ ساتونكى ونه مومئ');
INSERT INTO "abulsalam" VALUES(17,69,'17:69',' یا تاسو له دې نه په امان كې یئ چې په دغه (سمندر) كې مو بېرته په دويم ځلې واپس كړي، نو پر تاسو باندې (بېړۍ) ماتوونكى سخت باد راولېږي، نو تاسو د كفر كولو په سبب غرق كړي، بیا به تاسو د خپل ځان لپاره زمونږ په ضد راپسې ګرځېدونكى ونه مومئ');
INSERT INTO "abulsalam" VALUES(17,70,'17:70',' او یقینًا یقینًا مونږ د ادم اولادې ته ډېر عزت وركړى دى، او مونږ دوى ته په وچه او سمندر كې سورلۍ وركړي دي او مونږ دوى ته خوندور پاكیزه شیان وركړي دي او مونږ دوى په خپل مخلوق كې په ډېرو زیاتو باندې غوره كړي دي، غوره كول');
INSERT INTO "abulsalam" VALUES(17,71,'17:71',' او (یاده كړه) هغه ورځ چې مونږ به ټول خلق (انسانان) سره له خپل امامه (عملنامې) راوبلو، نو هغه څوك چې هغه ته خپل كتاب (عمل نامه) په خپل ښي لاس كې وركړى شو، نو دغه كسان به خپل كتاب (عمل نامه) لولي او پر دوى به د كجورې د نري تار هومره ظلم (هم) نه كېږي');
INSERT INTO "abulsalam" VALUES(17,72,'17:72',' او څوك چې په دي (دنیا) كې ړوند و، نو هغه به په اخرت كې هم ړوند وي، او (د نجات) د لارې په لحاظ به ډېر بې لارې وي');
INSERT INTO "abulsalam" VALUES(17,73,'17:73',' او بېشكه شان دا دى چې دوى نژدې وو چې تا له هغه (قرآن) نه واړوي چې مونږ تا ته وحي كړى دى، د دې لپاره چې ته پر مونږ له دې (قرآن) نه غیر (خبرې) راوتپې او په دغه وخت كې به نو ته دوى دوست نیولى وى');
INSERT INTO "abulsalam" VALUES(17,74,'17:74',' او كه ته مونږ (په حق باندې) نه وى ټینګ كړى (،نو) یقینًا یقینًا ته نژدې وې چې دوى ته لږ څه مايله شې');
INSERT INTO "abulsalam" VALUES(17,75,'17:75',' (نو) په دغه وخت كې به خامخا مونږ په تا څكلى وى دوه چند عذاب په (دې دنياوي) ژوندون او دوه چنده عذاب په مرګ (په اخرت) كې بیا به تا د خپل ځان لپاره زمونږ په خلاف مدد كوونكى نه وى موندلى');
INSERT INTO "abulsalam" VALUES(17,76,'17:76',' او بېشكه شان دا دى چې دوى قریب وو چې تا له دې ځمكې (وطن) نه واقعًا وخوځوي، د دې لپاره چې تا له دې نه وباسي او په دغه وخت كې به دوى له تا نه وروسته درنګ (وخت) نه وى تېر كړى مګر ډېر لږ ');
INSERT INTO "abulsalam" VALUES(17,77,'17:77',' (دا ده زمونږ) طریقه په هغو رسولانو كې چې مونږ له تا نه مخكې یقینًا لېږلي وو، او ته به زمونږ طریقې لره هېڅ تبدیلېدل ونه مومې');
INSERT INTO "abulsalam" VALUES(17,78,'17:78',' ته لمونځ قايموه د لمر له زواله وورسته د شپې تر تیارې پورې او د سبا قرآن (لمونځ) هم (قايموه) بېشكه د سبا قرآن، ده ته خو حاضري كولى شي');
INSERT INTO "abulsalam" VALUES(17,79,'17:79',' او د شپې په څه حصه كې خوب پرېږده، چې تا لپاره دا زیاتي عبادت دى، امېد دى چې ستا رب به تا مقامِ محمود (ستايل شوي ځاى) ته پورته كړي');
INSERT INTO "abulsalam" VALUES(17,80,'17:80',' او ته ووایه: اى زما ربه! ته ما داخل كړه په رښتیني داخلولو سره او ته ما وباسه په رښتیني وېستلو سره او ته زما لپاره له خپل جانب نه یو مددكوونكى قوت مقرر كړه');
INSERT INTO "abulsalam" VALUES(17,81,'17:81',' او ته ووایه: حق راغى او باطل په تېښته ورك شو، بېشكه باطل دى وركېدونكى');
INSERT INTO "abulsalam" VALUES(17,82,'17:82',' او مونږ قرآن نازلوو هغه چې د مومنانو لپاره شفا او رحمت دى او ظالمانو ته نه زیاتوي مګر تاوان');
INSERT INTO "abulsalam" VALUES(17,83,'17:83',' او كله چې مونږ پر (نافرمان) انسان باندې انعام وكړو (نو) هغه مخ وګرځوي او خپل ځان لرې كړي او كله چې ده ته شر (او تكلیف) ورسي (نو) ډېر ناامېده شي');
INSERT INTO "abulsalam" VALUES(17,84,'17:84',' ته ووایه: هر كس پر خپلې مناسبحالې طریقې عمل كوي، نو ستا رب ښه عالم دى پر هغه چا چې هغه د لارې په لحاظ ښه هدایت والا دى');
INSERT INTO "abulsalam" VALUES(17,85,'17:85',' او دوى له تا نه د روح په باره كې پوښتنه كوي ته (ورته) ووایه: روح زما د رب له امره ځنې دى او تاسو ته له علم ځنې څه برخه نه ده دركړ شوې مګر ډېره لږه');
INSERT INTO "abulsalam" VALUES(17,86,'17:86',' او یقینًا كه مونږ وغواړو (نو) خامخا ضرور به هغه (قرآن) بېرته یوسو چې مونږ تا ته وحي كړى دى، بیا به ته د خپل ځان لپاره په دې باره كې زمونږ په خلاف هېڅ وكیل (او كارساز) ونه مومې');
INSERT INTO "abulsalam" VALUES(17,87,'17:87',' خو ستا د رب له جانبه د مهربانۍ په وجه (مونږ دا قايم ساتلى دى، ځكه چې)، بېشكه د هغه فضل په تا باندې ډېر لوى دى');
INSERT INTO "abulsalam" VALUES(17,88,'17:88',' ته ووایه: خامخا كه انسانان او پېریان ټول پر دې خبره سره جمع شي چې د دې قرآن مِثل راوړي (،نو) د ده په مثل به رانه وړى شي اګر كه د دوى ځینې د ځینو نورو ملاتړي (هم) وي');
INSERT INTO "abulsalam" VALUES(17,89,'17:89',' او یقینًا یقینًا مونږ په دې قرآن كې هر قسمه مثال بیا بیا بیان كړى دى، نو زیاترو خلقو انكار وكړ غیر له كفر كولو نه');
INSERT INTO "abulsalam" VALUES(17,90,'17:90',' او دوى وویل: مونږ به پر تا له سره ایمان رانه وړو تر دې چې ته زمونږ لپاره په دې ځمكه كې خوټېدونكې چینه جاري كړې');
INSERT INTO "abulsalam" VALUES(17,91,'17:91',' یا چې تا لپاره باغ وي د كجورو او انګورو، بیا ته د دې (باغ) په مینځ كې ولې روانې كړې، روانول');
INSERT INTO "abulsalam" VALUES(17,92,'17:92',' یا چې لكه څنګه چې ستا خیال دى ته پر مونږ باندې اسمان ټوټې ټوټې راوغورځوې، یا چې ته الله او ملايك مخامخ (ښكاره) راولې');
INSERT INTO "abulsalam" VALUES(17,93,'17:93',' یا چې تا لپاره د سرو زرو كور وي، یا چې ته پورته اسمان ته وخېژې او مونږ به ستا پر (تشو) ختلو له سره ایمان رانه وړو تر هغه پورې چې ته پر مونږ باندې داسې یو كتاب راكوز كړې چې مونږ يې لولو، ته (دوى ته) ووایه: زما رب لپاره پاكي ده، زه نه یم مګر یو انسان (د نورو رسولانو په شان) رالېږل شوى ');
INSERT INTO "abulsalam" VALUES(17,94,'17:94',' او خلق د ایمان له راوړلو نه نه دي منع كړي، كله چې دوى ته هدایت راغى، مګر دې خبرې چې دوى به ویل: ایا الله یو انسان رسول رالېږلى دى!');
INSERT INTO "abulsalam" VALUES(17,95,'17:95',' ته (دوى ته) ووایه: كه په ځمكه كې (د انسانانو پر ځاى) ملايك وى، چې په ارام سره ګرځېدلى (نو) مونږ به خامخا په دوى باندې له اسمان نه مَلك، رسول رالېږلى وى');
INSERT INTO "abulsalam" VALUES(17,96,'17:96',' ته ووایه: زما په مینځ كې او ستاسو په مینځ كې الله ښه كافي ګواه دى، بېشكه هغه په خپلو بنده ګانو باندې ښه خبردار، (د هغو) ښه لېدونكى دى');
INSERT INTO "abulsalam" VALUES(17,97,'17:97',' او چا ته چې الله هدایت وكړي، نو هم هغه هدایت موندونكى دى او هغه څوك چې هغه (الله) يې بې لارې كړی، نو ته به د هغوى لپاره له هغه (الله) نه غیر هېڅ كارسازي (دوستان) له سره ونه مومې او د قیامت په ورځ به مونږ دوى په خپلو مخونو باندې (نسكور) راجمع كوو، په دې حال كې چې ړانده او ګونګیان او كاڼه به وي او د دوى اوسېدنځاى به جهنم وي، هر كله چې (د جهنم اور) غلى شي، (نو) مونږ به دوى ته لمبې وهونكى اور ورزیات كړو');
INSERT INTO "abulsalam" VALUES(17,98,'17:98',' „دا د دوى جزا ده، په سبب د دې چې بېشكه دوى زمونږ په ایتونو سره كفر كړى و او ویل به يې: ایا كله چې مونږ هډوكي او رېژېدلې خاورې شو، ایا په رښتیا به مونږ هرومرو بیا ژوندي كولى شو، په نوي تخلیق سره ');
INSERT INTO "abulsalam" VALUES(17,99,'17:99',' ایا دوى نه دي كتلي چې بېشكه هغه الله چې اسمانونه او ځمكه يې پیدا كړي دي پر دې قادر دى چې د دوى په مثل (نور) پیدا كړي او هغه د دوى لپاره یوه نېټه مقرر كړې ده چې په هغې كې هېڅ شك نشته، نو ظالمانو انكار وكړ مګر له كفر كولو نه');
INSERT INTO "abulsalam" VALUES(17,100,'17:100',' ته (دوى ته) ووایه: كه تاسو زما د رب د رحمت د خزانو مالكان شئ (نو) په دغه وخت كې به تاسو هرو مرو امساك (بخل) وكړئ د لګېدلو (خرچ كېدو) له وېرې نه او انسان خو دى ډېر بخیل');
INSERT INTO "abulsalam" VALUES(17,101,'17:101',' او یقینًا یقینًا مونږ موسٰى ته نهه واضحې معجزې وركړې وې، نو تهٔ له بني اسرائیلو نه پوښتنه وكړه كله چې هغه، دوى ته راغى، نو هغهٔ ته فرعون وویل: بېشكه زه خامخا ضرور پر تا د جادو شوي عقیده ساتم اى موسٰى! (پر تا جادو شوى دى)');
INSERT INTO "abulsalam" VALUES(17,102,'17:102',' ويې ويل:یقینًا یقینًا ته پوهېږې (په زړه كې) چې دا (نهه معجزې) نه دي نازلې كړې مګر د اسمانونو او ځمكې رب، په داسې حال كې چې دلیلونه دي، او بېشكه زه خامخا هرومرو په تا باندې اى فرعونه! د هلاك شوي یقین كوم');
INSERT INTO "abulsalam" VALUES(17,103,'17:103',' نو هغه (فرعون) اراده وكړه چې دوى له دغې ځمكې (وطن) نه وخوځوي (وباسي)، نو مونږ هغه او څوك چې له هغه سره وو؛ ټول غرق (ډوب) كړل ');
INSERT INTO "abulsalam" VALUES(17,104,'17:104',' او د هغه ( له هلاكت) نه وروسته مونږ بني اسرائیلو ته وویل: تاسو په دغه ځمكه (خاوره) كې اوسئ، نو كله چې د اخرت وعده (قیامت) راشي (نو) مونږ به تاسو ټول ګډ سره راولو');
INSERT INTO "abulsalam" VALUES(17,105,'17:105',' او په حقه سره مونږ دا نازل كړى دى او په حقه سره نازل شوى دى او ته مونږ نه يې لېږلى مګر زېرى وركوونكى او وېروونكى');
INSERT INTO "abulsalam" VALUES(17,106,'17:106',' او (مونږ) قرآن (نازل كړى دى) مونږ هغه ټوټه ټوټه (نازل) كړى دى، د دې لپاره چې ته دا خلقو ته ولولې په ارام (او وقفې) سره او مونږ دا نازل كړى دى (لږ لږ) نازلول');
INSERT INTO "abulsalam" VALUES(17,107,'17:107',' ته (دوى ته) ووایه چې تاسو پر ده ایمان راوړئ، یا ایمان مه راوړئ، بېشكه هغه كسان چې له دغه (قرآن) نه مخكې هغوى ته علم وركړى شوى دى، كله چې هغوى ته دا لوستى شي (نو) هغوى په (اړخ د) خپلو زنو باندې نسكور پرېوځي، چې سجده كوونكي وي');
INSERT INTO "abulsalam" VALUES(17,108,'17:108',' او وايي زمونږ رب ته پاكي ده، بېشكه زمونږ د رب وعده خامخا كېدونكې ده');
INSERT INTO "abulsalam" VALUES(17,109,'17:109',' او دوى پر خپلو زنو نسكور پرېوځي، چې ژاړي او عاجزي يې (د قرآن اورېدل) ورته ورزیاته كړي ');
INSERT INTO "abulsalam" VALUES(17,110,'17:110',' ته (دوى ته) ووایه: تاسو الله بلئ، یا تاسو رحمٰن بلئ، هر یو (نوم) يې چې وبلئ، نو خاص د هغهٔ لپاره دي تر ټولو ښكلي نومونه او ته (قراءت د) خپل لمونځ ډېر مه ظاهروه او مه يې ډېر پټوه او د دې دواړو په مینځ كې (منځنۍ) لار ولټوه');
INSERT INTO "abulsalam" VALUES(17,111,'17:111',' او ته ووایه: ټوله ثنا هغه الله لره خاص ده چې نه يې څوك اولاد نیولى دى او نه د هغه لپاره په بادشاهۍ كې څوك شریك شته او نه د هغهٔ لپاره د عاجزۍ په وجه څوك دوست شته او تهٔ د هغه لويي بیانوه، لويي كامله');
INSERT INTO "abulsalam" VALUES(18,1,'18:1',' ټولې ستاينې خاص هغه الله لره دي چې پر خپل بنده يې كتاب نازل كړى دى او په ده كې يې هېڅ كوږوالى نه دى ایښى ');
INSERT INTO "abulsalam" VALUES(18,2,'18:2',' په دې حال كې چې سم برابر دى، د دې لپاره چې هغه دوى له ډېر سخت عذاب نه ووېروي، چې د هغه (الله) له جانبه (راتلونكى) دى او مومنانو ته زېرى وركړي چې نېك عملونه كوي چې بېشكه د دوى لپاره ډېر ښكلى اجر دى');
INSERT INTO "abulsalam" VALUES(18,3,'18:3',' چې دوى به تل ترتله په هغه كې اوسېدونكي وي');
INSERT INTO "abulsalam" VALUES(18,4,'18:4',' او چې هغه كسان ووېروي چې (له ډېره جهله) وايي: الله (د ځان لپاره) اولاد نیولى دى');
INSERT INTO "abulsalam" VALUES(18,5,'18:5',' دوى ته پر دې خبره هېڅ علم نشته او نه د دوى پلرونو ته، دا د خبرې په لحاظ ډېره غټه (خبره) ده چې د دوى له خولو نه راوځي، دوى نه وايي مګر دروغ');
INSERT INTO "abulsalam" VALUES(18,6,'18:6',' نو ښايي چې ته په دوى پسې وروسته (په ګرځېدو سره) له ډېره غمه د خپل ځان وژونكى شې كه دوى پر دې خبره (قرآن) ایمان رانه وړي');
INSERT INTO "abulsalam" VALUES(18,7,'18:7',' بېشكه مونږ (چې یو) مونږ هر هغه څیز چې د ځمكې له پاسه دى د دغې لپاره زینت ګرځولى دى، د دې لپاره چې مونږ دوى و ازمایو چې په دوى كې كوم یو د عمل په لحاظ ډېر غوره دى');
INSERT INTO "abulsalam" VALUES(18,8,'18:8',' او بېشكه مونږ خامخا هغه څه چې په دې (ځمكه) باندې دي؛ وچ كلك ډاګ ګرځوونكي یو');
INSERT INTO "abulsalam" VALUES(18,9,'18:9',' ایا ته ګمان كوې چې بېشكه د غار او تختۍ والا زمونږ په نښو كې ډېر عجیبه وو؟');
INSERT INTO "abulsalam" VALUES(18,10,'18:10',' (یاد كړه) هغه وخت چې ځلمیانو غار ته رجوع وكړه (په غار كې يې پناه واخيسته)، نو ويې ويل: اى زمونږ ربه! ته مونږ ته له خپل جانب نه رحمت راكړه او زمونږ لپاره زمونږ په دې كار كې (اسباب د) هدایت برابر كړه ');
INSERT INTO "abulsalam" VALUES(18,11,'18:11',' نو مونږ د دوى پر غوږونو خوب واچاوه، په غار كې څو كاله په شمېر');
INSERT INTO "abulsalam" VALUES(18,12,'18:12',' بیا مونږ دوى له خوبه راپاڅول، د دې لپاره چې مونږ معلوم (څرګند) كړو چې په دوه ډلو كې كومې یوې هغه موده یاده ساتلې ده چې دوى تېره كړې ده');
INSERT INTO "abulsalam" VALUES(18,13,'18:13',' مونږ تا ته د دوى خبر په حقه سره بیانوو، بېشكه دوى داسې ځلمیان وو چې پر خپل رب يې ایمان راوړى و او مونږ دوى په هدایت كې ورزیات كړي وو');
INSERT INTO "abulsalam" VALUES(18,14,'18:14',' او مونږ د دوى زړونه مضبوط كړي وو، كله چې دوى ولاړ شول، نو ويې ويل: زمونږ رب د اسمانونو او ځمكې رب دى، مونږ به هیڅكله له هغه نه غیر بل معبود ونه بلو (كه مونږ له هغه نه غير بل معبود وبلو نو)، یقینًا یقینًا په دغه وخت كې به مونږ ډېره غلطه خبره كړې وي ');
INSERT INTO "abulsalam" VALUES(18,15,'18:15',' زمونږ دغې قوم له هغه (الله) نه غیر نور معبودان نيولي دي، دوى پر (عبادت د) دغو (باطلو معبودانو) باندې ښكاره دلیل ولې نه راوړي؟ نو له هغه چا نه لوى ظالم څوك دى چې په الله باندې دروغ تړي!');
INSERT INTO "abulsalam" VALUES(18,16,'18:16',' او كله چې تاسو جدا شوئ له دوى نه او له هغه څه نه چې دوى يې له الله نه غیر عبادت كوي، نو تاسو په غار كې ځاى ونیسئ، ستاسو رب به تاسو لپاره خپل رحمت خور (او پراخه) كړي او تاسو لپاره به ستاسو په كار كې اساني تیاره كړي');
INSERT INTO "abulsalam" VALUES(18,17,'18:17',' او ته به لمر وینې كله چې راخېژي، نو د دوى له غاره ښي اړخ ته میلان كوي او كله چې پرېوځي (نو) د دوى په څنګ تېر شي ګس اړخ ته او دوى له دې (غار) نه په ارت میدان كې دي، دغه د الله له نښو ځنې دي، چا ته چې الله هدایت وكړي، نو هم هغه هدایت موندونكى دى او څوك چې ګمراه كړي، نو ته به د هغه لپاره له سره ونه مومې، كوم لار ښوونكى دوست');
INSERT INTO "abulsalam" VALUES(18,18,'18:18',' او ته به پر دوى د ویښو ګمان كوې، حال دا چې دوى ویده دي او مونږ دوى په ښي اړخ او ګس اړخ اړوو او د دوى سپى (د غار) په خولې كې د خپلو څنګلو غځوونكى دى، كه ته پر دوى له بره نظر واچوې (نو) ته به خامخا له دوى نه په تېښته راوګرځې او خامخا به ته د وېرې په لحاظ ډك كړى شي');
INSERT INTO "abulsalam" VALUES(18,19,'18:19',' او همداسې مونږ دوى بیا راوېښ كړل، د دې لپاره چې دوى په خپل مینځ كې یو له بله پوښتنه وكړي، په دوى كې یو ویونكي وویل: تاسو څومره وخت تېر كړى دى؟ دوى وویل: مونږ یوه ورځ، یا د ورځې څه حصه وخت تېر كړى دى، نورو وویل: ستاسو رب ښه عالم دى پر هغې مودې چې تاسو تېره كړې ده، نو تاسو خپل یو تن له خپلو دې سپینو زرو سره ښار ته ولېږئ، نو هغه دې وګوري چې د دې (ښار) كوم یو كس د طعام په لحاظ ډېر پاكیزه دى، نو دى دې تاسو ته له هغه نه خوراك راوړي او نرمي او زیركي دې كوي او په تاسو دې هیڅوك له سره خبر نه كړي');
INSERT INTO "abulsalam" VALUES(18,20,'18:20',' بېشكه دوى كه پر تاسو خبر (او غالبه) شي (نو) دوى به تاسو سنګسار كړي، یا به تاسو هغوى خپل دین ته بېرته وګرځوي او په دغه وخت كې به تاسو له سره كامیاب نشئ، هیڅكله');
INSERT INTO "abulsalam" VALUES(18,21,'18:21',' او همداسې مونږ په دوى باندې (خلق) خبر كړل، د دې لپاره چې دغه (خلق) پوه شي چې بېشكه د الله وعده حقه ده او دا چې بېشكه قیامت (چې دى) په هغه كې هېڅ شك نشته، (مونږ خلق) هغه وخت (خبر كړل) چې دوى په خپل مینځ كې د هغوى په معامله كې جګړې كولې، نو ځينو وویل: تاسو په دوى باندې یوه ودانۍ جوړه كړئ، د دوى رب پر دوى ښه عالم دى، (خو) هغو كسانو وویل چې د دوى په معامله كې غالب وو، مونږ به خامخا هرومرو په (غار د) دوى باندې يو جومات جوړوو');
INSERT INTO "abulsalam" VALUES(18,22,'18:22',' ژر به دغه(خلق) وايي چې (دوى) درې دي، د دوى څلورم د دوى سپى دى او وايي به ځينې چې (دوى) پنځه دي، د دوى شپږم د دوى سپى دى، په داسې حال كې چې بې نښې نالیدلي ویشتل كوي او وايي به چې (دوى) اوه دي او د دوى اتم د دوى سپى دى، ته ووایه: زما رب د دوى پر شمېر ښه عالم دى، پر دوى خبر (او عالِم) نه دي مګر ډېر لږ (خلق)، نو ته د دوى په باره كې له سرسري بحث نه غیر نور بحث مه كوه او ته د دوى په باره كې په دغو (اهل كتابو) كې له هیچا نه فتوٰى مه غواړه');
INSERT INTO "abulsalam" VALUES(18,23,'18:23',' او ته د هېڅ شي په باره كې له سره داسې مه وایه چې بېشكه زه سبا ته د دغه كار كوونكى یم');
INSERT INTO "abulsalam" VALUES(18,24,'18:24',' مګر (جوخت ورسره وایه) كه الله يې وغواړي او خپل رب یاد كړه (ان شاء الله ووايه) كله چې (د هغهٔ نوم اخيستل) دې هېر شي، او ته ووایه: امېد دى چې ما ته به زما رب لارښوونه وكړي، د نېكۍ په لحاظ دې ته د قریب ترې لارې');
INSERT INTO "abulsalam" VALUES(18,25,'18:25',' او دوى په خپل غار كې درې سوه كاله تېر كړل او نهه يې (پرې) ور زیات كړل');
INSERT INTO "abulsalam" VALUES(18,26,'18:26',' ته ووایه: الله ښه عالم دى پر مقدار د هغې مودې چې دوى تېره كرې ده، خاص د هغه لپاره د اسمانونو او ځمكې غیب (پټ اَسرار) دي، هغه هر څه ښه لیدونكى او هر څه ښه اورېدونكى دى! د دوى لپاره له هغه نه غیر هېڅ كارساز (او حمایتي) نشته او هغه په خپل حكم (او قضا) كې هیڅوك نه شریكوي');
INSERT INTO "abulsalam" VALUES(18,27,'18:27',' او ته لوله هغه چې تا ته د خپل رب له كتابه وحي كړى شوى دى، د هغه (الله) كلمو لره هیڅوك بدلوونكى نشته او له هغهٔ نه غیر به ته له سره هېڅ پناه ځاى ونه مومې');
INSERT INTO "abulsalam" VALUES(18,28,'18:28',' او ته خپل ځان له هغو كسانو سره بند (او ټینګ) وساته چې خپل رب د ورځې په شروع كې او د ورځې په اخر كې بولي، دوى د هغهٔ مخ غواړي او ستا سترګې دې له دوى نه تېرې نشي، چې ته د دنيايي ژوند ښایست غوارې او ته د هغه چا اطاعت مه كوه چې مونږ له خپل ذكر نه د هغهٔ زړه غافل كړى دى او هغه د خپل خواهش تابع شوى دى او د هغهٔ كار له حد نه وتلى دى');
INSERT INTO "abulsalam" VALUES(18,29,'18:29',' او ته (دوى ته) ووایه: حق ستا د رب له جانبه دى، نو څوك چې (ایمان راوړل) وغواړي، نو هغه دې ایمان راوړي او څوك چې (كفر كول) وغواړي، نو هغه دې كفر وكړي، بېشكه مونږ د ظالمانو لپاره داسې اور تیار كړى دى چې له دغو نه د هغه پردې راچاپېره شوي دي او كه دوى (له تندې نه) فریاد وكړي (نو) په داسې اوبو سره به يې فریاد رسي وشي لكه زوې (د تیلو خټ) چې مخونه به وریتوي، بد دى دغه مشروب او بد دى دغه ځاى د اوسېدلو');
INSERT INTO "abulsalam" VALUES(18,30,'18:30',' بېشكه هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، (، نو) يقينًا مونږ د هغه چا اجر نه ضايع كوو چې ښه عمل يې كړى دى');
INSERT INTO "abulsalam" VALUES(18,31,'18:31',' دغه كسان چې دي، د دوى لپاره د تل استوګنې جنتونه دي چې د هغو له لاندې ولې بهېږي، دوى به په هغو كې د سروزرو په وښیو اراسته (او ښكلي) كاوه شي او دوى به د نري ورېښمو او پېړو ورېښمو شنې جامې اغوندي، په داسې حال كې چې په دغو (جنتونو) كې به په تختونو باندې تكیه وهونكي وي، دغه ښه بدل دى او ښه د ارام ځاى دى');
INSERT INTO "abulsalam" VALUES(18,32,'18:32',' او دوى ته یو مثال بیان كړه، دوه سړي دي، په دوى كې یو ته مونږ د انګورو دوه باغونه وركړي دي، او دغه دواړه (باغونه) مونږ په كجورو سره پټ كړي دي او د دواړو په مینځ كې مونږ فصل (كښت) پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(18,33,'18:33',' دغه دواړه باغونه خپلې مېوې كوي او هېڅ شى ترې نه كموي او مونږ د دواړو په مینځ كې ویاله بهولې ده');
INSERT INTO "abulsalam" VALUES(18,34,'18:34',' او د دغه (كس) لپاره نورې مېوې هم وې، نو ده خپل ملګري ته وویل، په دې حال كې چې له یو بل سره يې خبرې كولې: زه له تا نه په مال كې زیات یم او په ملاتړ (سړیو) كې ډېر غالب یم');
INSERT INTO "abulsalam" VALUES(18,35,'18:35',' او دى خپل باغ ته داخل شو، په داسې حال كې چې پر خپل ځان ظلم كوونكى و، ويې ويل: زه ګمان نه كوم چې دا (باغ) به كله هم هلاك (او خراب) شي');
INSERT INTO "abulsalam" VALUES(18,36,'18:36',' او زه ګمان نه كوم چې قیامت قايمېدونكى دى او قسم دى كه (بالفرض) زه خپل رب ته بوتلى شوم، نو خامخا هرومرو به زه له دې (باغ) نه غوره د بېرته ورتلو ځاى ومومم');
INSERT INTO "abulsalam" VALUES(18,37,'18:37',' ده ته خپل (مومن) ملګري وویل، په داسې حال كې چې ده ته يې جواب وركاوه: ایا ته په هغه ذات سره كفر كوې چې ته يې له خاورو پیدا كړې، بیا يې له نطفې نه، بیا يې ته سړى برابر كړې');
INSERT INTO "abulsalam" VALUES(18,38,'18:38',' لېكن زه چې یم (زه وایم چې) شان دا دى چې همدا الله زما رب دى، او زه له خپل رب سره هیڅوك نه شرېكوم');
INSERT INTO "abulsalam" VALUES(18,39,'18:39',' او كله چې تهٔ خپل باغ ته داخل شوې تا ولې ونه ویل چې مَاشَاءَ اللهُ لَا قُوَّةَ إِلَّا بِاللهِ (كېږي هغه چې الله يې اراده وكړي، هېڅ طاقت نشته مګر د الله په توفیق سره) كه ته ما له خپل ځان نه په مال او اولاد كې كم وینې');
INSERT INTO "abulsalam" VALUES(18,40,'18:40',' نو امېد دى چې ما ته به زما رب ستا له باغ نه غوره راكړي او په دغه (باغ) به له اسمانه عذاب (تندر) نازل كړي، نو هغه به وچ ښوى میدان وګرځي');
INSERT INTO "abulsalam" VALUES(18,41,'18:41',' یا به د ده اوبه وچې ډوبې شي، بیا به ته د دغو(ډوبو اوبو) په لټون له سره ونه توانېږې');
INSERT INTO "abulsalam" VALUES(18,42,'18:42',' او د ده مېوې ګېرې كړى شوې، (هلاكې شوې) نو داسې شو چې خپل ورغوي (لاسونه) يې (له افسوسه) مښل په هغه (مال) باندې چې ده په دغه (باغ) كې لګولى و، په داسې حال كې چې هغه د خپلو چتونو له پاسه راپرېوتى و، او ده ویل: اى كاشكې ما له خپل رب سره هیڅوك نه وى شریك كړى');
INSERT INTO "abulsalam" VALUES(18,43,'18:43',' او د ده هېڅ داسې ډله نه وه چې له الله نه غیر يې د ده مرسته كړې وى او نه دا پخپله بدله اخيستونكى (د ځان مدد كوونكى) و');
INSERT INTO "abulsalam" VALUES(18,44,'18:44',' په دغه وخت كې ټول د اختیار حق الله لره دى، هغه د ثواب (او بدله) وركولو په لحاظ تر ټولو غوره دى او د انجام وركولو په لحاظ تر ټولو ښه ');
INSERT INTO "abulsalam" VALUES(18,45,'18:45',' او تهٔ دوى ته د دنيايي ژوند مثال بیان كړه چې د هغو اوبو په شان دى چې مونږ له بره نه نازلې كړې دي، نو په دغو سره د ځمكې زرغونه له یو بل سره ګډه وډه شي، بیا (دغه زرغونه) وچه كلكه ماته ماته شي، چې بادونه يې الوځوي او الله په هر شي باندې قدرت لرونكى دى');
INSERT INTO "abulsalam" VALUES(18,46,'18:46',' مال او زامن خو د دنيايي ژوند زینت دى، او باقي پاتې كېدونكي نېك عملونه ستا د رب په نیز د ثواب په لحاظ تر ټولو غوره دي او د امېد په لحاظ تر ټولو ښه');
INSERT INTO "abulsalam" VALUES(18,47,'18:47',' او (یاده كړه) هغه ورځ چې مونږ به غرونه روان كړو او ته به ځمكه ښكاره (صاف میدان) وینې او مونږ به دوى (ټول) راجمع كړو، نو په دوى كې به یو تن هم پرې نږدو');
INSERT INTO "abulsalam" VALUES(18,48,'18:48',' او دوى به خپل رب ته قطار قطار وړاندې كړى شي (او وبه ویل شي:) یقینًا یقینًا تاسو مونږ ته داسې (لغړ او يواځې) راغلئ لكه چې مونږ تاسو په ړومبي ځل پیدا كړي وئ، بلكې تاسو دا ګڼله چې مونږ به تاسو لپاره له سره كوم میعاد مقرر نه كړو');
INSERT INTO "abulsalam" VALUES(18,49,'18:49',' او كتاب به كېښودل شي، نو ته به مجرمان ویني چې له هغه څه نه به وېرېدونكي وي چې په ده كې دي او وايي به: اى زمونږ هلاكته! د دې كتاب (اعمالنامې) څه حال دى چې نه وړه (ګناه) پرېږدي او نه غټه مګر هغه (هره يوه) يې حساب كړې ده او دوى به خپل ترسره كړي عملونه حاضر ومومي، او ستا رب پر هیچا ظلم نه كوي');
INSERT INTO "abulsalam" VALUES(18,50,'18:50',' او (یاد كړه) هغه وخت چې مونږ ملايكو ته وویل: تاسو ادم ته سجده وكړئ، نو دوى سجده وكړه، غیر له ابلیس نه، هغه له جناتو څخه و، نو هغه د خپل رب د حكم نافرماني يې وكړه، ایا نو تاسو له ما نه غیر هغه او د هغهٔ اولاده دوستان جوړوئ، حال دا چې هغوى ستاسو دښمنان دي، او د ظالمانو لپاره بد بدل دى');
INSERT INTO "abulsalam" VALUES(18,51,'18:51',' ما دغه (شیطانان) نه د اسمانونو او ځمكې پیدا كولو ته حاضر كړي دي او نه د دوى د ځانونو پیدا كولو ته او زه ګمراه كوونكو لره مټ (بازو) جوړوونكى (نیونكى) نه یم');
INSERT INTO "abulsalam" VALUES(18,52,'18:52',' او (یاده كړه) هغه ورځ چې وبه وايي (الله): تاسو زما هغو شریكانو ته اواز وكړئ چې تاسو به ګڼل، نو دوى به هغوى ته چغې ووهي، نو هغوى به دوى ته هېڅ جواب ورنه كړي او مونږ به د دوى تر مینځ د هلاكت يو ميدان (دوزخ) وګرځوو');
INSERT INTO "abulsalam" VALUES(18,53,'18:53',' او مجرمان به اور وویني، نو یقین به وكړي چې بېشكه دوى په دغه (اور) كې غورځېدونكي دي او دوى به له دغه (اور) نه د په څنګ كېدلو ځاى ونه مومي');
INSERT INTO "abulsalam" VALUES(18,54,'18:54',' او یقینًا یقینًا مونږ په دې قرآن كې د خلقو لپاره هر قسمه مثالونه بيا بيا بیان كړي دي، او انسان په جګړه كې تر هر شي زیات دى');
INSERT INTO "abulsalam" VALUES(18,55,'18:55',' او نه دي منع كړي خلق له دې نه چې ایمان راوړي، كله چې دوى ته هدایت راغى او له دې نه چې له خپل رب نه مغفرت وغواړي مګر (دې خبرې) چې دوى ته د ړومبنو خلقو طریقه راشي، یا دوى ته عذاب مخامخ راشي');
INSERT INTO "abulsalam" VALUES(18,56,'18:56',' او مونږ رسولان نه لېږو مګر زېرى وركوونكي او وېروونكي، او كافران شوي خلق په باطله سره جګړه كوي، د دې لپاره چې په دې سره حق ورك او لرې كړي او دوى زما ایتونه او هغه څه چې دوى پرې وېرول شوي وو؛ مسخره او ټوكې ونیول');
INSERT INTO "abulsalam" VALUES(18,57,'18:57',' او له هغه كس نه لوى ظالم څوك دى چې هغه ته د خپل رب په ایتونو سره نصیحت وركړى شي، نو دى له هغو نه مخ واړوي او هغه عملونه هېر كړي چې د ده لاسونو مخكې لېږلي دي، بېشكه مونږ د دوى پر زړونو پردې اچولې دي، د دې لپاره چې دوى پر ده پوه (نه) شي او د دوى په غوږونو كې مو كوڼوالى (اچولى دى) او كه ته دوى هدایت ته بلې، نو دوى به له سره هيڅكله هم هدایت ونه مومي');
INSERT INTO "abulsalam" VALUES(18,58,'18:58',' او هم ستا رب ډېر بخښونكى، د رحمت والا دى، كه دوى يې د خپلو كړو ګناهونو په سبب نیولي وى، (نو) دوى ته به يې عذاب خامخا ژر رالېږلى و، بلكې د دوى لپاره د وعدې وخت شته، (نو) دوى به له هغه (الله) نه غیر هېڅ پناه ځاى ونه مومي');
INSERT INTO "abulsalam" VALUES(18,59,'18:59',' او دغه كلي دي چې مونږ دوى هلاك كړل، كله چې دوى ظلم وكړ او مونږ د دوى هلاكت ته یوه نېټه ټاكلې وه');
INSERT INTO "abulsalam" VALUES(18,60,'18:60',' او (یاد كړه) هغه وخت چې موسٰى خپل ځلمي ته وویل: زه به تل ځمه، تر هغه پورې چې د دوه دریابونو د جمع كېدو ځاى ته ورسېږم، یا به ګرځمه ډېرې زمانې');
INSERT INTO "abulsalam" VALUES(18,61,'18:61',' نو كله چې دوى دواړه د هغو دواړو (دریابونو) په خپل مینځ كې د یو ځاى كېدو مقام ته ورسېدل، نو دوى خپل كب هېر كړ، نو هغه په سمندر كې (د) سرنګ (په شان) خپله لار ونیوله');
INSERT INTO "abulsalam" VALUES(18,62,'18:62',' نو كله چې دوى (له دغه ځایه) تېر شول، (موسٰى) خپل ځلمي ته وویل: ته مونږ ته زمونږ سبا نارى راوړه، بېشكه یقینًا مونږ له خپل دې سفره له ستومانۍ سره مخ شوي يو');
INSERT INTO "abulsalam" VALUES(18,63,'18:63',' هغه وویل: ایا ته خبر شوې كله چې مونږ لويې تېږې ته ځاى ونیوه (هلته مو دمه وكړه)، نو بېشكه ما هغه كب هېر كړ او هغه له ما نه هېر نه كړ مګر شیطان چې زه هغه (تا ته) دریاد كړم او هغه په سمندر كې خپله لاره ډېر عجیبه جوړه كړه');
INSERT INTO "abulsalam" VALUES(18,64,'18:64',' ويې ويل: دغه هغه څه دي چې مونږ يې طلب كاوه، پس بېرته په همغو خپلو قدمونو قدم په قدم راوګرځېدل');
INSERT INTO "abulsalam" VALUES(18,65,'18:65',' بیا نو دوى زمونږ په بنده ګانو كې یو بنده ومونده، چې مونږ هغه ته له خپله جانبه رحمت وركړى و او له خپل جانبه مو هغه ته (خاص) علم ښوولى و');
INSERT INTO "abulsalam" VALUES(18,66,'18:66',' ده ته موسٰى وویل: ایا زه ستا تابع شمه په دې شرط سره چې ته به ما ته ښیې د هغه (علم) نه چې تا ته د نېغې لار ښوول شوى دى');
INSERT INTO "abulsalam" VALUES(18,67,'18:67',' هغه وویل: بېشكه ته به زما سره هیچېرې د صبر كولو طاقت ونه لرې');
INSERT INTO "abulsalam" VALUES(18,68,'18:68',' او ته به څنګه پر هغه كار صبر وكړې چې د علم په لحاظ تا د هغهٔ احاطه نه ده كړې');
INSERT INTO "abulsalam" VALUES(18,69,'18:69',' هغه وویل: كه الله وغواړي ته به ما ضرور صبر كوونكى ومومې او زه به ستا د حكم نافرماني نه كوم');
INSERT INTO "abulsalam" VALUES(18,70,'18:70',' هغه وویل: كه ته زما متابعت كوې، نو ته له ما نه د هېڅ شي په باره كې تر هغه پورې تپوس مه كوه چې زه تا ته د هغه ذكر شروع كړم');
INSERT INTO "abulsalam" VALUES(18,71,'18:71',' نو دوى دواړه روان شول تر دې چې كله دوى په كښتۍ كې سواره شول، نو ده هغه كښتۍ سورۍ كړه، موسٰى وویل: ایا تا دا د دې لپاره سورۍ كړه چې ته د دې اهل (سورلۍ) غرق كړې، یقینًا یقینًا تا ډېر دروند ناروا كار وكړ');
INSERT INTO "abulsalam" VALUES(18,72,'18:72',' هغه وویل: ایا ما تا ته ویلي نه وو چې بېشكه ته به زما سره هیچېرې د صبر كولو طاقت ونه لرې');
INSERT INTO "abulsalam" VALUES(18,73,'18:73',' ده وویل: ته ما پر هغه كار مه نیسه چې ما په هېره وكړ او ته پر ما د دې كار په وجه سختي مه راچوه');
INSERT INTO "abulsalam" VALUES(18,74,'18:74',' نو دواړه روان شول، تر دې چې كله دوى له یو هلك سره مخامخ شول، نو ده هغه قتل كړ، (موسٰى) وویل: ایا تا یو پاكیزه (بې ګناه) نفس وواژه، بې له عوضه د بل نفس، یقینًا یقینًا تا ډېر ناكاره كار وكړ');
INSERT INTO "abulsalam" VALUES(18,75,'18:75',' هغه (خضر) وویل: ایا ما تا ته ویلي نه وو چې بېشكه ته به زما سره د صبر كولو طاقت بېخي ونه كړى شې');
INSERT INTO "abulsalam" VALUES(18,76,'18:76',' هغه (موسٰی) وویل: كه له دې نه بعد ما له تا نه د څه شي باره كې پوښتنه وكړه، نو بیا ته ما د ځان مه ملګرى كوه، یقینًا ته زما له جانبه (حد د) عذر ته ورسېدې');
INSERT INTO "abulsalam" VALUES(18,77,'18:77',' بیا دواړه روان شول تر هغه پورې چې د یو كلي اوسېدونكو ته راغلل (،نو) د هغه (كلي) له اوسېدونكو نه يې طعام (خوراك) وغوښت، نو هغو، دوى دواړو ته د مېلمستیا له وركولو نه انكار وكړ، بیا دوى په دغه (كلي) كې یو دېوال وموند چې غوښتل يې ونړېږي، نو ده (خضر) هغه سم كړ، هغه (موسٰى) وویل: كه تا غوښتلى (نو)خامخا پر دې (سازولو) به دې مزدوري اخيستې وى');
INSERT INTO "abulsalam" VALUES(18,78,'18:78',' ده وویل: دا زما په مینځ كې او ستا په مینځ كې (د) بېلتون (وخت) دى، ژر به زه تا د هغو خبرو په حقیقت خبر كړم چې په هغو باندې تا د صبر كولو طاقت ونه كړى شو');
INSERT INTO "abulsalam" VALUES(18,79,'18:79',' هر چې كښتۍ وه، نو هغه د هغو مسكینانو وه چې په بحر كې يې كار كاوه، نو ما اراده وكړه چې زه هغه عیبجنه كړم، په داسې حال كې چې د دوى په مخه كې یو داسې باچا و چې هره (روغه) كښتۍ به يې په زور سره اخيستله ');
INSERT INTO "abulsalam" VALUES(18,80,'18:80',' او هر چې هلك و، نو د هغه مور و پلار مومنان وو، نو مونږ له دې ووېرېدو چې دى به هغوى پر سركښۍ او كفر كولو مجبوره كړي');
INSERT INTO "abulsalam" VALUES(18,81,'18:81',' نومونږ وغوښتل چې دوى ته د دوى رب (داسې) بدل وركړي (چې) له دغه (هلك) نه په پاكۍ كې غوره وي او (له دغه نه) په مهربانۍ كې ډېر نږدې وي');
INSERT INTO "abulsalam" VALUES(18,82,'18:82',' او پاتې شو دېوال، نو هغه د دوه یتیمو هلكانو و، چې په ښار كې وو، او له هغه نه لاندې د هغو دواړو لپاره خزانه وه او د دغو دواړو پلار نېك (سړى) و، نو ستا رب اراده وكړه چې دوى دواړه خپلې ځوانۍ (او قوت) ته ورسي او (بیا) خپله خزانه راوباسي، ستا د رب د مهربانۍ په وجه او دغه (كار) ما (له خپله ځانه) په خپل حكم نه دى كړى، دغه حقیقت د هغه څه دى چې تا په هغو د صبر كولو طاقت ونشو كړى');
INSERT INTO "abulsalam" VALUES(18,83,'18:83',' او دوى له تا نه د ذوالقرنین باره كې تپوس كوي، ته (دوى ته) ووایه: ژر به زه تاسو ته د هغه په هكله څه احوال بيان كړم');
INSERT INTO "abulsalam" VALUES(18,84,'18:84',' بېشكه مونږ هغه ته په ځمكه كې اقتدار وركړى و او مونږ هغه ته له هر شي نه یوه لاره (او وسيله) وركړې وه');
INSERT INTO "abulsalam" VALUES(18,85,'18:85',' نو هغه لاره ونیوله (او هغه اسباب هم چې مغرب ته يې ورساوه)');
INSERT INTO "abulsalam" VALUES(18,86,'18:86',' تر هغه پورې چې كله دى د لمر پریوتو ځاى ته ورسېده (نو) ده دغه (لمر) ومونده چې د تورو خټو په لویه چینه كې ډوبېده، او ده هغې(چینې) ته نژدې یو قوم ومونده، مونږ وویل: اى ذوالقرنینه! (تا ته اختيار حاصل دى چې) یا خو تهٔ (دوى ته) عذاب وركړه او یا دا چې ته د دوى په هكله ښه لار ونیسه');
INSERT INTO "abulsalam" VALUES(18,87,'18:87',' ده وویل: هر چې هغه كس دى چې (له دې نه بعد) ظلم (شرك) وكړي، نو ژر به مونږ هغه ته (دنيايي) سزا وركړو، بیا به هغه خپل رب ته بېرته بوتللى شي، نو هغه به ده ته عذاب وركړي ډېر سخت عذاب');
INSERT INTO "abulsalam" VALUES(18,88,'18:88',' او پاتې شو هغه كس چې ایمان راوړي او نېك عمل وكړي، نو د هغه لپاره په بدله كې غوره ښېګڼه ده (چې جنت دى) او ژر ده چې مونږ به هغه ته زمونږ په كار كې پیدا كړو');
INSERT INTO "abulsalam" VALUES(18,89,'18:89',' بیا ده بله لاره ونیوله (او هغه اسباب هم چې مشرق ته يې ورساوه) ');
INSERT INTO "abulsalam" VALUES(18,90,'18:90',' تر هغه پورې چې كله دى د لمر ختو ځاى ته ورسېده (،نو) دغه (لمر) يې ومونده چې پر داسې قوم راخېژي چې مونږ هغوى ته د دغه (لمر) په وړاندې كومه پرده نه وه ګرځولې');
INSERT INTO "abulsalam" VALUES(18,91,'18:91',' همداسې (حال) و او یقینًا مونږ د خبردارۍ په لحاظ پر هغه څه احاطه كړې وه چې له ده سره وو');
INSERT INTO "abulsalam" VALUES(18,92,'18:92',' بیا ده بله لاره ونیوله (او نور اسباب يې تیار كړل)');
INSERT INTO "abulsalam" VALUES(18,93,'18:93',' تر هغه پورې چې كله دى د دوه غرونو مینځ ته ورسېده (،نو) د دغو دواړو وړاندې يې داسې قوم ومونده چې نژدې نه وو چې دوى (د ذوالقرنین) پر خبره پوه شي');
INSERT INTO "abulsalam" VALUES(18,94,'18:94',' دوى وویل: اى ذوالقرنینه! بېشكه یاجوج او ماجوج په ځمكه كې فساد كوونكي دي، نو ایا مونږ تا لپاره لګښت مقرر كړو، د دې لپاره چې ته زمونږ په مینځ كې او د دوى مینځ كې یو دېوال جوړ كړې؟');
INSERT INTO "abulsalam" VALUES(18,95,'18:95',' ده وویل: هغه څیزونه چې ما ته زما رب په (استعمال د) هغو كې قدرت راكړى دى (هغه) ډېر غوره دي، نو تاسو زما سره مرسته په محنت (او كار) سره وكړئ، زه به ستاسو په مینځ كې او د دوى په مینځ كې ښه مضبوط دېوال جوړ كړم');
INSERT INTO "abulsalam" VALUES(18,96,'18:96',' تاسو ما ته د اوسپنې تختې راكړئ، تر هغه پورې (يې ایښودلې چې) كله دغه (دېوال) د (غرونو) دوو څوكو په مینځ كې برابر شو، ويې ویل چې پو كړئ، تر هغه پورې چې كله يې دغه (اوسپنه) اور وګرځوله (،نو) ويې ويل: تاسو ما ته راوړئ چې زه پر دې (دېوال) وېلې كړى شوې تانبه وغورځوم');
INSERT INTO "abulsalam" VALUES(18,97,'18:97',' نو د دوى به طاقت نه وي چې پر ده وخېژي او نه به دوى د سوري كولو طاقت ولرلى شي');
INSERT INTO "abulsalam" VALUES(18,98,'18:98',' دغه زما د رب له جانبه یو رحمت دى، نو كله چې زما د رب وعده راشي دا به له ځمكې سره هموار وګرځوي او زما د رب وعده حقه ده');
INSERT INTO "abulsalam" VALUES(18,99,'18:99',' او مونږ به په دغه ورځ كې د دوى ځینې پرېږدو چې په ځینو نورو كې به موجونه وهي (ننوځي به) او په شپېلۍ كې به پوكى وكړى شي، نومونږ به دوى راجمع كړو، پوره جمع كول ');
INSERT INTO "abulsalam" VALUES(18,100,'18:100',' او په دغې ورځ به مونږ جهنم كافرانو ته وروړاندې كړو، وروړاندې كول');
INSERT INTO "abulsalam" VALUES(18,101,'18:101',' هغو كسانو ته چې د هغوى سترګې زما له ذكر نه په پرده كې وې او دوى اورېدل نشول كولى ');
INSERT INTO "abulsalam" VALUES(18,102,'18:102',' ایا نو كافران شویو كسانو ګمان كړى دى چې دوى به له ما نه غير زما بنده ګان كارسازوونكي ونیسي (او بیا به هم زه ورته عذاب نه وركوم، نه) بېشكه مونږ د كافرانو لپاره جهنم مېلمستیا تیار كړى دى');
INSERT INTO "abulsalam" VALUES(18,103,'18:103',' ته (دوى ته) ووایه: ایا مونږ تاسو ته د هغو كسانو باره كې خبر وركړو چې د اعمالو په لحاظ تر ټولو زیات تاواني دي؟');
INSERT INTO "abulsalam" VALUES(18,104,'18:104',' دوى هغه كسان دي چې په دنيايي ژوند كې د دوى منډې توى (او بې ځايه) تللي دي، په داسې حال كې چې دوى دا ګمان كوي چې یقیناً هم دوى ښه عملونه كوي ');
INSERT INTO "abulsalam" VALUES(18,105,'18:105',' هم دغه كسان دي چې د خپل رب په ایتونو او د هغه په ملاقات سره كافر (او انكاري) شوي دي، نو د دوى عملونه برباد شول، نو د قیامت په ورځ به مونږ د دوى لپاره هېڅ وزن ونه دروو (هېڅ حيثيت به ورنكړو)');
INSERT INTO "abulsalam" VALUES(18,106,'18:106',' دغه، د دوى بدله جهنم ده په سبب كفر وكړ او زما ایتونه او زما رسولان يې مسخره نیولي وو');
INSERT INTO "abulsalam" VALUES(18,107,'18:107',' بېشكه هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، د دوى لپاره د فردوس (اعلٰی) جنتونه مېلمستیا دي');
INSERT INTO "abulsalam" VALUES(18,108,'18:108',' چې په دغو كې به تل وي، دوى به له دغو نه ګرځېدل (او بل ځاى ته منتقل كېدل) نه غواړي');
INSERT INTO "abulsalam" VALUES(18,109,'18:109',' ته (دوى ته) ووایه: كه سمندر زما د رب د كلماتو لپاره سیاهي شي، (نو) سمندر به خامخا خلاص شي، مخكې له دې چې زما د رب كلمات تمام شي، اګر كه مونږ د دغه (سمندر) په مثل مدد (يا سياهي) هم راوړو');
INSERT INTO "abulsalam" VALUES(18,110,'18:110',' ته (دوى ته) ووایه: بېشكه همدا خبره ده چې زه يواځې ستاسو په شان بشر یم، ما ته وحي كولى شي چې بېشكه ستاسو معبود يواځې یو معبود دى، نو هغه څوك چې د خپل رب د ملاقات امېد لري، نو هغه دې نېك عمل وكړي او د خپل رب په عبادت كې دې هیڅوك نه شریكوي');
INSERT INTO "abulsalam" VALUES(19,1,'19:1',' كاف، ها، يا، عين، صاد');
INSERT INTO "abulsalam" VALUES(19,2,'19:2',' (دا) ستا د رب د رحمت ذكر دى په خپل بنده زكریا باندې');
INSERT INTO "abulsalam" VALUES(19,3,'19:3',' كله چې ده خپل رب ته اواز وكړ ډېر ورو اواز');
INSERT INTO "abulsalam" VALUES(19,4,'19:4',' ويې ويل: اى زما ربه! یقینًا زه چې یم زما هډوكي كمزوري (ضعیف) شوي دي او سر مې د سپین والي په وجه شغلې وهي، په دې حال كې چې اى زما ربه! زه هيڅكله له تا نه په سوال سره محروم (او نا امېده) نه یم پاتې شوى');
INSERT INTO "abulsalam" VALUES(19,5,'19:5',' او بېشكه زه چې یم، خپل وروسته له خپلوانو نه وېرېږم، او زما ښځه له شروع نه شنډه ده، نو ته ما ته له خپل جانب نه یو وارث راكړه');
INSERT INTO "abulsalam" VALUES(19,6,'19:6','چې وارث شي د دين زما او وارث شي د دين د اولاد د يعقوب او دى وګرځوه اى ربه غوره شوى');
INSERT INTO "abulsalam" VALUES(19,7,'19:7',' اى زكریا! بېشكه مونږ تا ته د داسې هلك زېرى دركوو چې د ده به نوم یحیٰی وي، مونږ ده ته له دې نه مخكې څوك همنامى نه دى ګرځولى');
INSERT INTO "abulsalam" VALUES(19,8,'19:8',' ده وویل: اى زما ربه! زما لپاره به هلك څنګه كېږي، په داسې حال كې چې زما ښځه له شروع نه شنډه ده اویقینًا زه د ډېر زوړوالي په وجه ډېرې كمزورۍ ته رسېدلى یم');
INSERT INTO "abulsalam" VALUES(19,9,'19:9',' (الله) ورته وویل: هم داسې دي، ستا رب فرمایلي دي چې دغه (كار) ما ته ډېر اسان دى او تحقیق ته ما پیدا كړى يې له دې نه مخكې، حال دا چې ته هېڅ شى نه وې');
INSERT INTO "abulsalam" VALUES(19,10,'19:10',' ده وویل: اى زما ربه! ته زما لپاره یوه نښه مقرر كړه (الله) وویل: ستا نښه داده چې ته به له خلقو سره درې شپې (او ورځې) خبرې نشې كولى، په داسې حال كې چې ته به ښه روغ يې');
INSERT INTO "abulsalam" VALUES(19,11,'19:11',' نو دى په خپل قوم باندې له عبادت ځاى څخه راووت، پس دوى ته يې اشاره وكړه داسې چې تاسو سبا او بېګاه تسبیح وایئ');
INSERT INTO "abulsalam" VALUES(19,12,'19:12',' اى یحیٰی! ته دغه كتاب په قوت سره ونیسه، او مونږ ده ته حكم (حكمت او نبوت) وركړ، په داسې حال كې چې دى هلك و');
INSERT INTO "abulsalam" VALUES(19,13,'19:13',' او له خپل جانب نه مو (ده ته)، مهرباني (وركړې وه) او پاكي او دى ډېر پرهېزګاره و');
INSERT INTO "abulsalam" VALUES(19,14,'19:14',' او له خپل مور و پلار سره نېكي كوونكى و، او سركشه، نافرمانه نه و');
INSERT INTO "abulsalam" VALUES(19,15,'19:15',' او پر ده دې سلام وي په هغې ورځ چې دى پيدا شو او په هغې ورځ چې دى به مري او په هغې ورځ چې دى به بیا ژوندى راپورته كړى شي');
INSERT INTO "abulsalam" VALUES(19,16,'19:16',' او ته په كتاب كې (قصه د) مریم یاده كړه كله چې هغه له خپلې كورنۍ نه هغه ځاى ته په څنګ شوه چې (د كور) مشرقي جانب و');
INSERT INTO "abulsalam" VALUES(19,17,'19:17',' نو دې د دوى په طرف پرده ونیوله، نو مونږ دې ته خپل روح (مَلَك جبريل) ولېږه، نو هغه دې ته د كامل سړي په شكل كې څرګند شو');
INSERT INTO "abulsalam" VALUES(19,18,'19:18',' (نو) دې وویل: بېشكه زه له تا نه په رحمٰن پورې پناه نیسم، كه چېرې ته پرهېزګاره يې');
INSERT INTO "abulsalam" VALUES(19,19,'19:19',' هغه وویل: یقینًا زه خو يواځې ستا د رب رسول (استاځى) یم، د دې لپاره چې (د خپل رب په حكم) تا ته یو ډېر پاك هلك دركړم');
INSERT INTO "abulsalam" VALUES(19,20,'19:20',' دې وویل: زما به هلك څنګه پیدا شي؟ حال دا چې ما ته هېڅ يو بشر لاس هم نه دى راوړى، او زه چېرې بدكاره هم نه یم');
INSERT INTO "abulsalam" VALUES(19,21,'19:21',' ويې ويل: خبره همداسې ده (لېكن) ستا رب فرمایلي دي: دغه كار ما ته ډېر اسان دى او د دې لپاره چې مونږ هغه (ماشوم) د خلقو لپاره نښه وګرځوو او له خپل جانب نه رحمت او دغه یو فیصله شوى كار دى');
INSERT INTO "abulsalam" VALUES(19,22,'19:22',' نو (مریم) په دغه (عیسٰی) سره حامله شوه، نو له دغه (حمل) سره یو لرې ځاى ته په څنګ شوه');
INSERT INTO "abulsalam" VALUES(19,23,'19:23',' نو دغه (مریم) د لنګوال درد د كجورې د ونې تنې (ډډ) ته راوسته، دې وویل: اى كاشكې زه له دې نه مخكې مړه شوې وى او زه بېخي هېره شوې، له زړونو وتلې وى');
INSERT INTO "abulsalam" VALUES(19,24,'19:24',' نو دې ته يې د دې له لاندې نه اواز وكړ چې مه خفه كېږه، یقینًا ستا رب له تا نه لاندې چینه روانه كړې ده');
INSERT INTO "abulsalam" VALUES(19,25,'19:25',' او خپل ځان ته د كجورې تنه راوخوځوه، هغه به پر تا پخې تازه لوندې كجورې راوغورځوي');
INSERT INTO "abulsalam" VALUES(19,26,'19:26',' پس (دغه كجورې) خوره او (دغه اوبه) څښه او (په عیسٰی سره) سترګې یخې كړه، نو كه خامخا تا له خلقو ځنې یو څوك ولیده، نو (ورته) وایه: بېشكه ما د رحمٰن لپاره پر ځان د روژې نذر منلى دى، پس نن ورځ به زه له سره خبرې ونه كړم له هېڅ یوه انسان سره');
INSERT INTO "abulsalam" VALUES(19,27,'19:27',' نو دې (مریمې) هغه (عیسٰی) خپل قوم ته راوړ، چې دى يې راپورته كړى و، دوى (ورته) وویل: اى مریمې! یقینًا یقینًا تا ډېر بد كار كړى دى');
INSERT INTO "abulsalam" VALUES(19,28,'19:28',' اى د هارون خورې! ستا پلار خو بد سړى نه و او نه ستا مور بدكاره (بد عمله) وه ');
INSERT INTO "abulsalam" VALUES(19,29,'19:29',' نو هغې دغه (عیسٰی) ته اشاره وكړه، دوى وویل: مونږ له هغه چا سره څنګه خبرې وكړو چې دى په غېږ كې دى، چې وړوكى دى');
INSERT INTO "abulsalam" VALUES(19,30,'19:30',' ده وویل: بېشكه زه د الله بنده یم، هغهٔ ما ته كتاب راكړى دى او زه يې نبي ګرځولى یم');
INSERT INTO "abulsalam" VALUES(19,31,'19:31',' او زه يې بركت ناك ګرځولى یم په هر ځاى كې چې اوسم او ده ما ته په لمانځه او زكات سره كلك حكم راكړى دى، تر څو چې زه ژوندى یم');
INSERT INTO "abulsalam" VALUES(19,32,'19:32',' (او زه يې ګرځولى یم) له خپلې مور سره نېكي كوونكى او زه يې سركشه، بدبخته نه یم ګرځولى');
INSERT INTO "abulsalam" VALUES(19,33,'19:33',' او پر ما دې سلام وي په هغې ورځ چې زه پيدا شوم او په هغې ورځ چې زه به مرم او په هغې ورځ چې زه به بیا ژوندى راپورته كولى شم');
INSERT INTO "abulsalam" VALUES(19,34,'19:34',' دغه عیسٰى د مریمې زوى دى (الله د ده باره كې) حقه خبره كړې ده، هغه چې دوى په هغې كې سره جګړې كوي');
INSERT INTO "abulsalam" VALUES(19,35,'19:35',' د الله لپاره نه ښايېږي چې دى دې څوك اولاد ونیسي، ده لره پاكي ده، دى چې كله د څه كار فیصله وكړي، نو بېشكه همدا خبره ده چې ده ته وايي: وشه! نو هغه (كار) وشي');
INSERT INTO "abulsalam" VALUES(19,36,'19:36',' او بېشكه چې الله زما رب دى او ستاسو رب دى، نو تاسو د ده عبادت كوئ، دا ډېره نېغه لار ده');
INSERT INTO "abulsalam" VALUES(19,37,'19:37',' نو دغه ډلې په خپل منځ كې سره مختلفې شوې، نو كافران شویو كسانو لپاره د ډېرې سترې ورځې له حاضرېدو هلاكت (او خرابي) دى');
INSERT INTO "abulsalam" VALUES(19,38,'19:38',' دوى به څه ښه اورېدونكي او څه ښه لیدونكي وي! په هغې ورځ چې مونږ ته به راځي، لېكن ظالمان نن ورځې په ښكاره ګمراهۍ كې دي');
INSERT INTO "abulsalam" VALUES(19,39,'19:39',' او ته دوى د حسرت (او ارمان) له ورځې نه ووېروه كله چې به كار فیصله كړى شي، حال دا چې دوى (اوس) په غفلت كې دي او دوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(19,40,'19:40',' بېشكه مونږ چې یو، همدا مونږ د ځمكې او د هغه چا چې په دغه (ځمكه) باندې دي؛ وارثان كېږو او خاص مونږ ته به دوى بېرته راوستى شي');
INSERT INTO "abulsalam" VALUES(19,41,'19:41',' او په كتاب كې ابراهیم یاد كړه، بېشكه دى ډېر رېښتینى (او) نبي و');
INSERT INTO "abulsalam" VALUES(19,42,'19:42',' كله چې ده خپل پلار ته وویل: اى زما پلار جانه! ته ولې د هغه څیز عبادت كوې چې نه (څه) اوري او نه ویني او له تا نه هېڅ شى (تكلیف) نشي لرې كولى؟');
INSERT INTO "abulsalam" VALUES(19,43,'19:43',' اى زما پلار جانه! بېشكه زه چې یم یقینًا ما ته (د الله له جانبه) هغه علم راغلى دى چې تا ته نه دى راغلى، نو ته زما پیروي وكړه، زه به تا ته نېغه (سمه) لار وښیم');
INSERT INTO "abulsalam" VALUES(19,44,'19:44',' اى زما پلار جانه! د شيطان بندګي مه كوه، يقينًا شيطان خود رحمان نافرمان دى');
INSERT INTO "abulsalam" VALUES(19,45,'19:45',' اى زما پلارجانه! بېشكه زه چې یم، زه له دې نه وېرېږم چې تا ته به د رحمٰن له جانبه عذاب درورسېږي، نو ته به د شیطان دوست شې');
INSERT INTO "abulsalam" VALUES(19,46,'19:46',' هغه (پلار د ابراهیم) وویل: اى ابراهیمه! ایا ته زما له معبودانو نه مخ ګرځوونكى يې، یقینًا كه ته منع نه شوې، نو زه به تا خامخا ضرور سنګسار كړم او له ما څخه ډېره موده لرې (او جدا) شه');
INSERT INTO "abulsalam" VALUES(19,47,'19:47',' ده وویل: پر تا دې سلام وي، ضرور به زه تا لپاره له خپل رب نه مغفرت غواړم، بېشكه دى په ما باندې ډېر مهربان دى');
INSERT INTO "abulsalam" VALUES(19,48,'19:48',' او زه جدا كېږم له تاسو نه او له هغو څیزونو نه چې تاسو يې له الله نه غیر بلئ، او زه خپل رب رابلم، امېد دى چې زه به د خپل رب په رابللو سره بدبخته نشم');
INSERT INTO "abulsalam" VALUES(19,49,'19:49',' نو كله چې دى له دوى نه او له هغو نه چې دغه خلق يې له الله نه غیر عبادت كوي؛ بېل شو (، نو) مونږ ده ته اسحق او یعقوب عطا كړل او دا هر یو مونږ نبیان ګرځولي وو');
INSERT INTO "abulsalam" VALUES(19,50,'19:50',' مونږ دغو ته له خپل رحمته عطا وركړه او مونږ دوى ته د صدق ډېره اوچته ژبه وركړې وه');
INSERT INTO "abulsalam" VALUES(19,51,'19:51',' او په كتاب كې موسٰی یاد كړه، بېشكه چې دى خالص كړى شوى و او رسول (او)، نبي و');
INSERT INTO "abulsalam" VALUES(19,52,'19:52',' او مونږ ده ته د طور غر له ښي اړخ نه اواز كړى و او مونږ دى قریب كړ، چې د راز خبرې مو ورسره كولې');
INSERT INTO "abulsalam" VALUES(19,53,'19:53',' او مونږ ده ته له خپل رحمته د ده ورور هارون وركړ، چې نبي و');
INSERT INTO "abulsalam" VALUES(19,54,'19:54',' او په كتاب كې اسماعیل یاد كړه، بېشكه دى د وعدې رښتینى و او رسول (او) نبي و');
INSERT INTO "abulsalam" VALUES(19,55,'19:55',' او ده به خپل اهل ته د لمانځه او زكات حكم كاوه او دى د خپل رب په نیز پسندیده و');
INSERT INTO "abulsalam" VALUES(19,56,'19:56',' او په كتاب كې ادریس یاد كړه، يقينًا دى رښتينى (او) نبي و');
INSERT INTO "abulsalam" VALUES(19,57,'19:57',' او دى مونږ ډېر اوچت ځاى ته پورته كړى دى');
INSERT INTO "abulsalam" VALUES(19,58,'19:58',' دغه خلق هغه كسان دي چې الله په دوى باندې له انبیاوو ځنې انعام كړى دى، د ادم په اولاده كې او د هغه چا (په اولاد كې) چې مونږ له نوح سره بار كړي وو او د ابراهیم او اسرائیل (یعقوب) په اولاده كې او له هغه چا نه چې مونږ (ورته) هدایت كړى و او مونږ غوره كړي وو كله چې به پر دوى د رحمٰن ایتونه ولوستى شول( نو) دوى به پرېوتل، سجده كوونكي او ژړېدونكي');
INSERT INTO "abulsalam" VALUES(19,59,'19:59',' نو له دوى نه وروسته داسې بد ځاى ناستي پېدا شول چې لمونځونه يې ضايع كړل او د شهوتونو پیروي يې وكړه، نو ژر به دوى د خپلې بدۍ له انجام سره ملاقات وكړي');
INSERT INTO "abulsalam" VALUES(19,60,'19:60',' مګر هغه څوك چې توبه وباسي او ایمان راوړي او نېك عمل وكړي، نو دغه كسان دي چې دوى به جنت ته داخلېږي او پر دوى به هېڅ شى ظلم نه كېږي');
INSERT INTO "abulsalam" VALUES(19,61,'19:61',' د تل اوسېدونې جنتونو ته، هغه چې رحمٰن يې وعده كړې ده له خپلو بنده ګانو سره په نالیدلو، بېشكه دى (الله) چې دى، د ده وعده (خامخا) راتلونكې ده');
INSERT INTO "abulsalam" VALUES(19,62,'19:62',' دوى به په دغو (جنتونو) كې لغوه خبرې نه اوري بس سلام به اوري، او د دوى لپاره په دغو (جنتونو) كې سبا او بېګاه د دوى رزق دى');
INSERT INTO "abulsalam" VALUES(19,63,'19:63',' دغه هغه جنتونه دي چې مونږ يې په خپلو بنده ګانو كې هغه څوك وارث ګرځولو چې پرهېزګاره وي');
INSERT INTO "abulsalam" VALUES(19,64,'19:64',' او (جبریل وویل) مونږ نه راكوزېږو مګر د خپل رب په امر سره، خاص د هغه لپاره دي هغه څه چې زمونږ په وړاندې دي او هغه چې زمونږ وروسته دي او هغه څه چې د دغو په مینځ كې دي او ستا رب هیڅكله هېروونكى نه دى');
INSERT INTO "abulsalam" VALUES(19,65,'19:65',' (دغه) د اسمانونو او ځمكې رب دى او د هغه څه چې د دغو دواړو په مینځ كې دي، نو ته د ده عبادت كوه او د ده پر عبادت ښه ټینګ اوسه، ایا ته د ده كوم همنام پېژنې!');
INSERT INTO "abulsalam" VALUES(19,66,'19:66',' او دغه (له بعث نه منكر) انسان وايي: ایا كله چې زه مړ شم خامخا عنقریب به زه بیا ژوندى را ایستلى شم');
INSERT INTO "abulsalam" VALUES(19,67,'19:67',' ایا دغه انسان نه یادوي چې بېشكه مونږ دى له دې نه مخكې پیدا كړى و، او دى هېڅ شى نه و');
INSERT INTO "abulsalam" VALUES(19,68,'19:68',' نو قسم دى ستا په رب چې مونږ به خامخا ضرور دوى او شیطانان ټول راجمع كړو، بیا به مونږ دوى خامخا ضرور د دوزخ چاپېره په ګونډو پراته راحاضر كړو');
INSERT INTO "abulsalam" VALUES(19,69,'19:69',' بیا به خامخا ضرور مونږ له هرې ډلې نه راوباسو هر هغه څوك چې د سركشۍ په لحاظ په رحمٰن باندې ډېر سخت دى ');
INSERT INTO "abulsalam" VALUES(19,70,'19:70',' بیا خامخا مونږ په هغو خلقو ښه عالمان یو چې هغوى دغه (دوزخ) ته د داخلولو (او سوځولو) ښه لايق دي');
INSERT INTO "abulsalam" VALUES(19,71,'19:71',' او نشته دى په تاسو كې هیڅوك مګر ده ته واردېدونكى دى دغه (واردېدل) ستا د رب په ذمه یقیني او فیصله كړى شوي دي');
INSERT INTO "abulsalam" VALUES(19,72,'19:72',' بیا به مونږ هغو كسانو ته نجات وركړو چې پرهېزګاري يې كړې ده او ظالمان (كافران) به په دغه (دوزخ) كې په ګونډو پراته پرېږدو');
INSERT INTO "abulsalam" VALUES(19,73,'19:73',' او كله چې دوى ته زمونږ ایتونه لوستى شي، چې ښه واضح دي، (، نو) كافران شوي كسان هغو كسانو ته چې ایمان يې راوړى دى؛ وايي په دغو دواړو ډلو كې كومه یوه د مقام په لحاظ ډېره غوره او د مجلس په لحاظ ډېر ښايسته ده');
INSERT INTO "abulsalam" VALUES(19,74,'19:74',' او له دوى نه مخكې مونږ څومره ډېر قرنونه (امتونه) هلاك كړي دي، چې هغوى د سامانونو او لیدنې (شكل) په لحاظ ډېر ښه وو');
INSERT INTO "abulsalam" VALUES(19,75,'19:75',' ته (دوي ته) ووایه: هر هغه څوك چې په ګمراهۍ كې وي، نو رحمٰن دې ده ته مهلت وركړي، مهلت وركول ، تر هغه پورې چې كله دوى هغه څه وویني چې له دوى سره يې وعده كولى شي، یا عذاب، یا قیامت، نو دوى به ژر پوه شي چې څوك دى د ځاى په لحاظ ډېر بد او د لښكر په لحاظ ډېر كمزورى');
INSERT INTO "abulsalam" VALUES(19,76,'19:76',' او الله هغو كسانو ته هدایت نور هم زیاتوي چې نېغه لار يې موندلې ده، او نېكۍ باقي پاتې كېدونكې ستا د رب په نیز د ثواب په لحاظ ډېرې غوره دي او د بېرته ورتللو په لحاظ ډېرې ښې دي');
INSERT INTO "abulsalam" VALUES(19,77,'19:77',' ایا تا هغه كس لیدلى چې زمونږ په ایتونو باندې كافر شوى دى او وايي چې ما ته به (په اخرت كې) خامخا ضرور مال او اولاد راكول كېږي');
INSERT INTO "abulsalam" VALUES(19,78,'19:78',' ایا دى په غیبو خبر شوى دى، یا ده د رحمٰن په نیز كومه وعده اخيستې ده');
INSERT INTO "abulsalam" VALUES(19,79,'19:79',' داسې نه ده ضرور مونږ لیكو هغه څه چې دى يې وايي او ده ته به له عذابه وغځوو، غځول');
INSERT INTO "abulsalam" VALUES(19,80,'19:80',' او په میراث به اخلو هغه څه چې دى يې وايي، او دى به مونږ ته يواځې (ځانله) راځي');
INSERT INTO "abulsalam" VALUES(19,81,'19:81',' او دوى له الله نه غیر (نور) معبودان ونیول، د دې لپاره چې دغه (معبودان) د دوى د عزت سبب شي');
INSERT INTO "abulsalam" VALUES(19,82,'19:82',' داسې نه ده، ژر به دغه (معبودان) د دوى په عبادت سره منكر شي او دغه به د دوى په ضد (او خلاف) شي');
INSERT INTO "abulsalam" VALUES(19,83,'19:83',' ایا ته نه ګورې چې مونږ شیطانان په كافرانو باندې راخوشې كوو، چې دوى تېزوي، تېزول');
INSERT INTO "abulsalam" VALUES(19,84,'19:84',' نو ته په دوى باندې (د عذاب په راتلو كې) تلوار مه كوه، بېشكه مونږ د دوى لپاره شمېرل كوو، شمېرل');
INSERT INTO "abulsalam" VALUES(19,85,'19:85',' (یاده كړه) هغه ورځ چې مونږ به متقیان راجمع كړو د رحمٰن په طرف بلل شوي سواره مېلمانه');
INSERT INTO "abulsalam" VALUES(19,86,'19:86',' او مجرمان (كافران) به د جهنم په طرف تږي پیاده شړو');
INSERT INTO "abulsalam" VALUES(19,87,'19:87',' هغوى د شفاعت اختیار نه لري، مګر هغه څوك چې د رحمٰن په نيز يې وعده اخيستې ده');
INSERT INTO "abulsalam" VALUES(19,88,'19:88',' او دغه (كافران) وايي: رحمٰن د ځان لپاره اولاد نیولى دى');
INSERT INTO "abulsalam" VALUES(19,89,'19:89',' یقینًا یقینًا تاسو ډېره لویه د ګناه خبره وكړه');
INSERT INTO "abulsalam" VALUES(19,90,'19:90',' نژدې دي چې اسمانونه له دې نه وچوي او ځمكه ټوټې ټوټې شي او غرونه راولوېږي ذرې ذرې');
INSERT INTO "abulsalam" VALUES(19,91,'19:91',' په دې خبره چې دوى د رحمٰن لپاره اولاد وباله');
INSERT INTO "abulsalam" VALUES(19,92,'19:92',' او رحمٰن ته نه ښايي چې اولاد ونیسي');
INSERT INTO "abulsalam" VALUES(19,93,'19:93',' نه دي دغه هر یو چې په اسمانونو او ځمكه كې دي مګر رحمٰن ته راتلونكي دي، په دې حال كې چې بنده به وي');
INSERT INTO "abulsalam" VALUES(19,94,'19:94',' یقینًا یقینًا ده دوى راګېر كړي دي او دوى يې شمېرلي دي، شمېرل');
INSERT INTO "abulsalam" VALUES(19,95,'19:95',' او هر يو له دوى نه ده ته راتلونكى دى په ورځ د قیامت كې يواځې');
INSERT INTO "abulsalam" VALUES(19,96,'19:96',' بېشكه هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي ژر به رحمٰن د دوى لپاره (د خلقو په زړونو كې) مینه (او محبت) پیدا كړي');
INSERT INTO "abulsalam" VALUES(19,97,'19:97',' پس بېشكه همدا خبره ده چې مونږ دغه (قرآن) ستا په ژبه آسان كړى دى، د دې لپاره چې ته په ده سره پرهېزګارانو ته زېرى وركړې او ته په ده سره ډېر جګړه كوونكى قوم ووېروې');
INSERT INTO "abulsalam" VALUES(19,98,'19:98',' او له دوى نه مخكې مونږه څومره ډېر قرنونه هلاك كړي دي! ايا ته په دوى كې يو تن محسوسوې، يا ته د دوى كوم نرم غږ (اواز) هم اورې');
INSERT INTO "abulsalam" VALUES(20,1,'20:1',' طا، ها');
INSERT INTO "abulsalam" VALUES(20,2,'20:2',' مونږ قرآن په تا باندې د دې لپاره نه دى نازل كړى چې ته په سختۍ او مشقت كې پرېوځې');
INSERT INTO "abulsalam" VALUES(20,3,'20:3',' مګر (نازل كړى دى مونږ) نصیحت د هغه چا لپاره چې وېرېږي');
INSERT INTO "abulsalam" VALUES(20,4,'20:4',' (نازل كړى شوى دى) په نازلېدلو سره د هغه (الله) له جانبه چې ځمكه او ډېر اوچت اسمانونه يې پیدا كړي دي');
INSERT INTO "abulsalam" VALUES(20,5,'20:5',' رحمٰن په عرش باندې برقراره شو');
INSERT INTO "abulsalam" VALUES(20,6,'20:6',' خاص د ده لپاره دي هغه څه چې په اسمانونو كې دي او هغه څه چې په ځمكه كې دي او هغه څه چې د دواړو په مینځ كې دي او هغه څه چې د لوندې ځمكې نه لاندې دي');
INSERT INTO "abulsalam" VALUES(20,7,'20:7',' او كه ته خبره په جهر سره كوې، نو یقینًا هغه په پټه خبره او په ډېره پټه خبره هم پوهېږي');
INSERT INTO "abulsalam" VALUES(20,8,'20:8',' الله (چې دى) نشته دى هېڅ لايق د عبادت مګر هم دى دى، خاص د ده لپاره تر ټولو ښكلي نومونه دي');
INSERT INTO "abulsalam" VALUES(20,9,'20:9',' او ایا تا ته د موسٰی قصه (خبره) راغلې ده');
INSERT INTO "abulsalam" VALUES(20,10,'20:10',' كله چې ده اور ولیده، نو خپل اهل ته يې وویل: تاسو ایسار شئ، بېشكه ما اور ولیده، كېدى شي چې زه له هغه نه تاسو ته لګولې (بله) لمبه راوړم، یا به په اور باندې څه لار ښوونه (لار ښود) ومومم');
INSERT INTO "abulsalam" VALUES(20,11,'20:11',' نو كله چې دى دغه (اور) ته راغى، ده ته اواز وكړى شو چې اى موسٰی!');
INSERT INTO "abulsalam" VALUES(20,12,'20:12',' بېشكه زه چې یم، زه ستا رب یم، نو ته خپلې پڼې وباسه، بېشكه ته په مقدسه (سپېڅلې) وادۍ (دره) طُوٰی كې يې');
INSERT INTO "abulsalam" VALUES(20,13,'20:13',' او ته ما غوره كړى يې، نو ته هغه څه ته ښه غوږ شه چې وحي كولى شي');
INSERT INTO "abulsalam" VALUES(20,14,'20:14',' بېشكه زه چې یم، زه الله یم، نشته دى هېڅ لايق د عبادت مګر همدا زه یم، نو ته زما عبادت كوه، او زما د یادولو لپاره لمونځ قايموه');
INSERT INTO "abulsalam" VALUES(20,15,'20:15',' بېشكه قیامت راتلونكى دى، زه غواړم چې دغه (قیامت) پټ وساتم، د دې لپاره چې هر نفس ته د هغو عملونو په سبب بدله وركړى شي چې دى يې كوي');
INSERT INTO "abulsalam" VALUES(20,16,'20:16',' نو تا دې هیڅكله بند نه كړې له دغه (قیامت) نه هغه څوك چې په ده ایمان نه راوړي او هغه د خپل خواهش پیروي كوي (،ګنې) نو ته به هلاك شې');
INSERT INTO "abulsalam" VALUES(20,17,'20:17',' او اى موسٰی! ستا په ښي لاس كې څه شى دى؟');
INSERT INTO "abulsalam" VALUES(20,18,'20:18',' ده وویل: دا زما امسا ده په دې زه تكیه لګوم، او په دې سره زه خپلو ګډو (بيزو) ته پاڼې څنډوهم، او زما لپاره په دې كې نور حاجتونه (او فايدې) هم دي');
INSERT INTO "abulsalam" VALUES(20,19,'20:19',' (الله) وویل: اى موسٰی! ته دا وغورځوه');
INSERT INTO "abulsalam" VALUES(20,20,'20:20',' نو ده دغه وغورځوله، نو ناڅاپه دغه (امسا) مار شو، منډې يې وهلې');
INSERT INTO "abulsalam" VALUES(20,21,'20:21',' (الله) وویل: ته دا راونیسه، او ته مه وېرېږه، ژر به مونږ دا خپل ړومبني شكل ته راوګرځوو');
INSERT INTO "abulsalam" VALUES(20,22,'20:22',' او ته خپل لاس خپل تخرګ ته پیوست كړه، دا به راووځي، چې تك سپین به وي، بې له عیب (او بیمارۍ ) نه، په داسې حال كې چې دا بله نښه (او معجزه) ده');
INSERT INTO "abulsalam" VALUES(20,23,'20:23',' د دې لپاره چې مونږ تا ته د خپلو ترټولو لویو نښو ځنې (څه) وښیو');
INSERT INTO "abulsalam" VALUES(20,24,'20:24',' ته فرعون ته لاړ شه، بېشكه ده سركشي شروع كړې ده');
INSERT INTO "abulsalam" VALUES(20,25,'20:25',' ده وویل: اى زما ربه! ته ما لره زما سینه (خلاصه) پرانیځه');
INSERT INTO "abulsalam" VALUES(20,26,'20:26',' او ما ته زما كار اسان كړه');
INSERT INTO "abulsalam" VALUES(20,27,'20:27',' او زما له ژبې نه غوټه پرانیځه');
INSERT INTO "abulsalam" VALUES(20,28,'20:28',' چې دوى زما په خبره پوه شي');
INSERT INTO "abulsalam" VALUES(20,29,'20:29',' او زما لپاره زما له اهل نه یو وزیر (مددګار) وټاكه ');
INSERT INTO "abulsalam" VALUES(20,30,'20:30',' هارون، چې زما ورور دى');
INSERT INTO "abulsalam" VALUES(20,31,'20:31',' په ده سره ته زما مَلا قوي كړه');
INSERT INTO "abulsalam" VALUES(20,32,'20:32',' او دى زما په كار كې شریك كړه');
INSERT INTO "abulsalam" VALUES(20,33,'20:33',' د دې لپاره چې مونږ ستا ډېره تسبیح ووایو');
INSERT INTO "abulsalam" VALUES(20,34,'20:34',' او ستا ډېر ذكر وكړو');
INSERT INTO "abulsalam" VALUES(20,35,'20:35',' بېشكه هم ته زمونږ حال ښه لیدونكى يې');
INSERT INTO "abulsalam" VALUES(20,36,'20:36',' (الله) وویل: اى موسٰی! یقینًا تا ته ستا سوال دركړى شو');
INSERT INTO "abulsalam" VALUES(20,37,'20:37',' او یقینًا یقینًا مونږ په تا باندې یو ځل بل احسان كړى و');
INSERT INTO "abulsalam" VALUES(20,38,'20:38',' كله چې مونږ ستا مور ته الهام وكړ هغه چې (تا ته اوس) وحي كولى شي');
INSERT INTO "abulsalam" VALUES(20,39,'20:39',' چې ته (اى مورې د موسٰی) دا (موسٰی) په تابوت (صندوق) كې واچوه، بیا دغه (صندوق) په سیند كې وغورځوه، نو دغه (صندوق) لره دې سیند په ساحل كې وغورځوي، چې دغه (موسٰی) لره ونیسي زما دښمن او د ده دښمن او ما په تا باندې له خپل جانبه محبت اچولى و، او د دې لپاره ستا تربیت زما د سترګو په وړاندې وشي');
INSERT INTO "abulsalam" VALUES(20,40,'20:40',' كله چې ستا خور (په تا پسې) روانه وه (ته يې ولیدې چې د هیچا تى (پۍ) نه اخلې) نو دې ویل: ایا زه تاسو ته هغه څوك وښیم چې د ده كفالت (او تربیت) به كوي، نو ته مونږ خپلې مور ته بېرته راوستې، د دې لپاره چې د هغې سترګې یخې شي او غمژنه نشي او تا یو كس مړ كړى و، نو ته مونږ له غم نه خلاص كړې او ته مونږ په قسما قسم ازموینو سره وازمایلې، نو تا څو كاله په اهل مدین كې تېر كړل، بيا ته په ټاكلې اندازې سره راغلې، اى موسٰی!');
INSERT INTO "abulsalam" VALUES(20,41,'20:41',' او ته ما د خپل ځان لپاره تیار كړى يې');
INSERT INTO "abulsalam" VALUES(20,42,'20:42',' ته لاړ شه او ستا ورور دې هم، زما په معجزو سره او تاسو دواړه زما په ذكر (یاد) كې سستي مه كوئ');
INSERT INTO "abulsalam" VALUES(20,43,'20:43',' تاسو دواړه فرعون ته لاړ شئ، بېشكه هغه باغي شوى دى');
INSERT INTO "abulsalam" VALUES(20,44,'20:44',' نو تاسو هغه ته ډېره نرمه خبره وكړئ، ښايي چې دى پند واخلي، یا ووېرېږي');
INSERT INTO "abulsalam" VALUES(20,45,'20:45',' دواړو وویل: اى زمونږه ربه! بېشكه مونږ له دې نه وېرېږو چې دى به زمونږ سره زیاتى وكړي، یا به سركشي شروع كړي');
INSERT INTO "abulsalam" VALUES(20,46,'20:46',' (الله) وویل: تاسو مه وېرېږئ، بېشكه زه له تاسو سره یم، زه اورم او زه وینم');
INSERT INTO "abulsalam" VALUES(20,47,'20:47',' نو تاسو ده ته ورشئ، پس (ورته) ووايئ چې بېشكه مونږ ستا د رب رسولان یو، نو ته زمونږ سره بني اسرائیل خوشې كړه، او ته دوى مه په عذابوه، په تحقیق مونږ تا ته ستا د رب له معجزې سره راغلي یو، او (له عذاب نه) سلامتي ده د هغه چا لپاره چې د هدایت پیروي وكړي');
INSERT INTO "abulsalam" VALUES(20,48,'20:48',' بېشكه مونږ چې یو، یقینًا مونږ ته وحي كړى شوې ده چې بېشكه عذاب په هغه چا دى چې (د رسولانو) تكذیب كوي او (له اسلامه) مخ ګرځوي');
INSERT INTO "abulsalam" VALUES(20,49,'20:49',' ده (فرعون) وویل: اى موسٰی ستاسو د دواړو رب څوك دى؟');
INSERT INTO "abulsalam" VALUES(20,50,'20:50',' ده وویل: زمونږ رب هغه ذات دى چې هر شي ته يې د ده (موافق) شكل وركړى دى، بیا يې لار ښودلې ده');
INSERT INTO "abulsalam" VALUES(20,51,'20:51',' (فرعون) وویل: د ړومبنو زمانو د خلقو څه حال دى؟');
INSERT INTO "abulsalam" VALUES(20,52,'20:52',' ده وویل: د هغو علم زما له رب سره دى په یو كتاب كې، زما له رب نه څه ورَكېږي او نه ترې هېرېږي');
INSERT INTO "abulsalam" VALUES(20,53,'20:53',' هغه ذات چې تاسو لپاره يې ځمكه فرش ګرځولې ده او تاسو لپاره يې په دې كې لارې چلولې دي او له بره نه يې اوبه نازلې كړې دي، پس په دغو (اوبو) سره مونږ بېلا بېل (او) قسما قسم زرغونې را ایستلي دي');
INSERT INTO "abulsalam" VALUES(20,54,'20:54',' تاسو يې خورئ او خپل څاروي (په كې) څروئ، بېشكه په دغو (شیانو) كې د عقل والاو لپاره یقینًا ډېرې نښې دي');
INSERT INTO "abulsalam" VALUES(20,55,'20:55',' له هم دې (ځمكې) نه مونږ تاسو پیدا كړي یئ او په هم دې كې به مونږ تاسو ننه باسو، او بل كرت به مونږ تاسو راوباسو');
INSERT INTO "abulsalam" VALUES(20,56,'20:56',' او یقینًا یقینًامونږ ده ته خپلې ټولې نښې (او معجزې) وروښوولې، نو ده تكذیب وكړ او انكار يې وكړ');
INSERT INTO "abulsalam" VALUES(20,57,'20:57',' وویل: (فرعون) ایا ته مونږ ته راغلى يې اى موسٰی؟، د دې لپاره چې ته په خپل سحر (جادو) سره مونږ له خپلې ځمكې نه وباسې');
INSERT INTO "abulsalam" VALUES(20,58,'20:58',' نو مونږ به خامخا ضرور تا ته د دغه (سحر) په مثل سحر راوړو، نو ته زمونږ په مینځ كې او په خپل مینځ كې یوه وعده مقرره كړه چې نه به مونږ د هغې مخالفت كوو او نه ته، داسې ځاى چې مساوي (هوار) وي');
INSERT INTO "abulsalam" VALUES(20,59,'20:59',' ده وویل: ستاسو د وعدې وخت د زینت (او جشن) ورځ ده او دا چې خلق دې د څاښت په وخت كې راجمع كړى شي');
INSERT INTO "abulsalam" VALUES(20,60,'20:60',' نو فرعون وګرځېده، نو خپل مكر (او چل) يې راجمع كړ، بیا راغى');
INSERT INTO "abulsalam" VALUES(20,61,'20:61',' (موسٰی جادوګرو ته) وویل: تاسو هلاك شئ! په الله باندې دروغ مه تړئ (ګنې) نو هغه به تاسو په عذاب سره هلاك كړي، او په تحقیق نا امېده (او نامراد) شو هغه كس چې (په الله) يې دروغ وتړل');
INSERT INTO "abulsalam" VALUES(20,62,'20:62',' نو دغو (جادوګرو) په خپل كار كې په خپل مینځ سره جګړه وكړه او دغه پټه مشوره يې خفیه وساتله');
INSERT INTO "abulsalam" VALUES(20,63,'20:63',' دوى وویل: بېشكه چې دا دواړه خامخا ساحران (جادوګر) دي، دوى دواړه غواړي چې تاسو له خپلې ځمكې (خاورې) نه په خپل سحر (جادو) سره وباسي او ستاسو ډېر غوره لار (د دين) ختمه كړي');
INSERT INTO "abulsalam" VALUES(20,64,'20:64',' نو تاسو خپل ټول چل مكر راجمع كړئ، بیا تاسو (میدان ته) راشئ صف صف، او یقینًا نن ورځ هغه څوك كامیاب دى چې غالب شو');
INSERT INTO "abulsalam" VALUES(20,65,'20:65',' ده وویل: اى موسٰی! یا به ته (امسا) غورځوې، یا به مونږ شو اول د هغه چا چې (امسا) غورځوي');
INSERT INTO "abulsalam" VALUES(20,66,'20:66',' ده وویل: بلكې تاسو وغورځوئ، نو ناڅاپه د دوى رسۍ او د دوى امساګانې د دوى د سحر په وجه د ده (موسٰى) خیال ته داسې راوستى شوې چې په رښتيا دغه منډې وهي');
INSERT INTO "abulsalam" VALUES(20,67,'20:67',' نو موسٰی په خپل نفس (زړه) كې وېره وموندله');
INSERT INTO "abulsalam" VALUES(20,68,'20:68',' مونږ (ده ته) وویل: ته مه وېرېږه، بېشكه هم ته ښه غالب يې');
INSERT INTO "abulsalam" VALUES(20,69,'20:69',' او ته هغه (امسا) وغورځوه چې ستا په ښي لاس كې ده، چې هغه څه لواړ (ښوى) تېر كړي چې دغو جوړ كړي دي، بېشكه هغه شى چې دوى تیار كړى دى د جادوګر چل مكر دى، او جادوګر برى نه مومي هر چېرته چې راشي');
INSERT INTO "abulsalam" VALUES(20,70,'20:70',' نو ساحران وغورځولى شول، سجده كوونكي، ويې ويل:مونږ د هارون او موسٰی په رب ایمان راوړ');
INSERT INTO "abulsalam" VALUES(20,71,'20:71',' (فرعون) وویل چې تاسو د ده تصدیق وكړ مخكې له دې چې زه تاسو ته اجازت دركړم، بېشكه دى یقینًا ستاسو هغه مشر دى چې تاسو ته يې سحر (جادو) ښودلى دى، نو زه به خامخا ضرور ستاسو لاسونه او ستاسو پښې ادل بدل پرې كوم او زه به خامخا ضرور تاسو د كجور په تنو كې په دار كوم او تاسو به خامخا ضرور پوه شئ چې په مونږ كې كوم یو د عذاب وركولو په لحاظ ډېر سخت دى او ډېر باقی پاتې كېدونكى دى (زه او كه موسٰی)');
INSERT INTO "abulsalam" VALUES(20,72,'20:72',' هغوى وویل: مونږ به تا ته له سره ترجيح درنه كړو په هغه باندې چې مونږ ته راغلى دى چې ښكاره معجزې دي او هغه (الله) چې مونږ يې پیدا كړي یو، نو ته هغه كار وكړه چې ته د هغه كوونكى يې، بېشكه ته يواځې د دې دنيايي ژوند فیصله كولى شې');
INSERT INTO "abulsalam" VALUES(20,73,'20:73',' بېشكه مونږ په خپل رب ایمان راوړى دى، د دې لپاره چې هغه زمونږ خطاګانې وبخښي او هغه (جرم) چې تا مونږ په هغه مجبوره كړي وو، چې سحر دى او الله ډېر غوره او تل پاتې كېدونكى دى');
INSERT INTO "abulsalam" VALUES(20,74,'20:74',' بېشكه شان دا دى چې هغه څوك چې خپل رب ته راشي، په دې حال كې چې كافر وي، نو بېشكه د ده لپاره دوزخ دى، دى به په ده كې نه مري او نه به (ښه) ژوندون كوي');
INSERT INTO "abulsalam" VALUES(20,75,'20:75',' او هغه څوك چې ده ته په داسې حال كې راشي چې مومن وي، نېك عملونه يې كړي وي (،نو) د دوى لپاره ډېرې اوچتې درجې دي');
INSERT INTO "abulsalam" VALUES(20,76,'20:76',' چې د تل اوسېدنې جنتونه دي چې د هغو له لاندې ولې بهېږي، چې دوى به په هغو كې تل ترتله وي، دغه د هغه چا بدله ده چې ځان يې (له شرك او كفر نه) پاك ساتلى وي');
INSERT INTO "abulsalam" VALUES(20,77,'20:77',' او یقینًا یقینًا موږ موسٰی ته وحي وكړه چې ته زما بنده ګان د شپې وباسه، نو ته د دوى لپاره په سمندر كې یوه وچه لاره جوړه كړه، ته به نه وېرېږې له نیول كېدو نه او نه به وېرېږې (له ډوبېدو نه) ');
INSERT INTO "abulsalam" VALUES(20,78,'20:78',' نو فرعون سره له خپلو لښكرو په دوى پسې راغى، نو دوى لره له سمندر نه هغه شي لاندې كړل چې دوى يې لاندې كړل');
INSERT INTO "abulsalam" VALUES(20,79,'20:79',' او فرعون خپل قوم لره ګمراه كړ او نېغه لار يې ور ونه ښودله ');
INSERT INTO "abulsalam" VALUES(20,80,'20:80',' اى بني اسرائیلو بېشكه مونږ تاسو ته ستاسو له دښمن نه نجات دركړ او د طور غر ښي اړخ ته مو له تاسو سره وعده وكړه او مونږ پر تاسو باندې مَن (ترنجبین) او مړزان (ډوله مرغان) نازل كړل');
INSERT INTO "abulsalam" VALUES(20,81,'20:81',' تاسو هغه پاكیزه څیزونه خورئ چې مونږ تاسو ته دركړي دي او تاسو په دغه (رزق) كې تجاوز مه كوئ (ګنې) نو په تاسو به زما غضب راكوز شي اوهغه څوك چې په هغه باندې زما غضب راكوز شي، نو یقینًا دى وغورځېده (هلاك شه)');
INSERT INTO "abulsalam" VALUES(20,82,'20:82',' او بېشكه زه خامخا ډېر بخښونكى یم هغه چا ته چې توبه وباسي او ایمان راوړي او نېك عمل كوي، بیا په نېغه لار روان اوسي');
INSERT INTO "abulsalam" VALUES(20,83,'20:83',' او ته څه شي راګړندى كړې له خپل قوم نه اى موسٰی!');
INSERT INTO "abulsalam" VALUES(20,84,'20:84',' ده وویل: دوى دغه دي په ما پسې راروان دي او زه تا ته جلت راغلم اى زما ربه! د دې لپاره چې ته راضي شې');
INSERT INTO "abulsalam" VALUES(20,85,'20:85',' (الله) وویل: نو بېشكه مونږ ستا قوم له تا نه وروسته وازمایه او سامري دوى ګمراه كړل');
INSERT INTO "abulsalam" VALUES(20,86,'20:86',' نو موسٰی خپل قوم ته راواپس شو، چې ډېر په غصه او غمجن و، ويې ويل:اى زما قومه! ایا تاسو سره ستاسو رب ډېره ښه وعده نه وه كړې، ایا نو په تاسو زمانه ډېره اوږده شوه، یا كه تاسو اراده كړې وه چې په تاسو باندې د خپل رب غضب راكوز شي، نو تاسو زما د وعدې خلاف ورزي وكړه');
INSERT INTO "abulsalam" VALUES(20,87,'20:87',' دوى وویل: مونږ ستا د وعدې مخالفت په خپل اختیار سره نه دى كړى، او لېكن پر مونږ باندې د (قبطیانو د) قوم د ښكلا زیورات بار كړى شوي وو، نو مونږ هغه وغورځول، نو همداسې سامري هغه (په اور كې) وغورځول');
INSERT INTO "abulsalam" VALUES(20,88,'20:88',' نو ده د دوى لپاره (له هغې كندې څخه) یو سخى چې جسد و؛ راووېست، چې د ده لپاره د سخي اواز و، نو دوى وویل: دا ستاسو معبود دى او د موسٰی معبود دى، پس هغه هېر كړى دى');
INSERT INTO "abulsalam" VALUES(20,89,'20:89',' (الله فرمايي) نو ایا دوى نه ګوري چې بېشكه شان دا دى چې دغه (سخى) دوى ته بېرته خبره نه راګرځوي او دى د دوى لپاره نه د ضرر اختیار لري او نه د نفعې');
INSERT INTO "abulsalam" VALUES(20,90,'20:90',' او یقینًا یقینًا هارون دوى ته د دې (موسٰی له راتلو) نه مخكې ویلي وو چې اى زما قومه! بېشكه همدا خبره ده چې تاسو په دې سره ازمایل شوي یئ، او ستاسو رب خو رحمٰن دى، نو تاسو زما پیروي وكړئ او زما د حكم اطاعت وكړئ');
INSERT INTO "abulsalam" VALUES(20,91,'20:91',' دوى وویل: مونږ به تل به په (عبادت) د ده محكم ولاړ اوسو، تر هغه پورې چې موسٰی مونږ ته بېرته راشي');
INSERT INTO "abulsalam" VALUES(20,92,'20:92',' (موسٰی چې راغى نو) ويې ويل:اى هارونه! ته څه شي منع كړى وې، كله چې تا دوى ولیدل چې ګمراه شول');
INSERT INTO "abulsalam" VALUES(20,93,'20:93',' چې تا زما پیروي ونه كړه، ایا نو تا زما د حكم نافرماني وكړه؟');
INSERT INTO "abulsalam" VALUES(20,94,'20:94',' ده وویل: اى زما د مور زویه! ته مه زما ږېره نیسه او مه زما سره، بېشكه زه ووېرېدم له دې نه چې ته به وايې چې تا د بني اسرائیلو په مینځ كې بېلتون واچاوه او زما خبرې ته دې ونه كتل');
INSERT INTO "abulsalam" VALUES(20,95,'20:95',' (موسٰی) وویل: نو ستا څه كار و اى سامري! ');
INSERT INTO "abulsalam" VALUES(20,96,'20:96',' هغه وویل: ما هغه شى ولیده چې دوى هغه نه و لیدلى نو ما یو موټى (خاوره) راوخيسته د رسول له قدمه، نو هغه ما (د دغه سخي په خوله كې) وغورځوله او همداسې ما ته زما نفس (دا كار) ښايسته كړى و');
INSERT INTO "abulsalam" VALUES(20,97,'20:97',' ده وویل: نو ته ځه! پس بېشكه تا لره په ژوندن كې دا دي چې ته به وايې: (په ما) مه لګېږئ، او بېشكه تا لپاره د وعدې یو وخت دى چې له تا سره به د هغې وعدې هېڅ خلاف ونه كړى شي، او ته خپل هغه معبود ته وګوره چې ته په (عبادت) د ده باندې محكم ولاړ وې، مونږ به دى خامخا ضرور سوځوو، بیا مونږ دى په سمندر كې بادوو، بادول');
INSERT INTO "abulsalam" VALUES(20,98,'20:98',' بېشكه همدا خبره ده چې ستاسو معبود هغه الله دى چې نشته هېڅ لايق د عبادت مګر هم دى دى، ده د علم په لحاظ هر شى، راګېر كړى');
INSERT INTO "abulsalam" VALUES(20,99,'20:99',' هم داسې مونږ تا ته بیانوو ځینې له هغو خبرونو نه چې مخكې تېر شوي دي او په تحقیق تا ته مونږ دركړى دى له خپل جانب نه ذكر (چې قرآن دى)');
INSERT INTO "abulsalam" VALUES(20,100,'20:100',' هرڅوك چې له ده نه مخ وګرځوي، پس بېشكه دغه كس به د قیامت په ورځ ( د عذاب) بار پورته كوي');
INSERT INTO "abulsalam" VALUES(20,101,'20:101',' چې تل به وي په دغه (عذاب) كې او دغه بد دى د دوى لپاره د بارولو په لحاظ');
INSERT INTO "abulsalam" VALUES(20,102,'20:102',' په هغې ورځ كې چې په شپېلۍ كې به (په دوهم ځل) وپوكل شي او په دغې ورځ كې به مونږ مجرمان (كافران) راجمع كوو چې شین سترګي (تور مخې) به وي');
INSERT INTO "abulsalam" VALUES(20,103,'20:103',' دوى به په خپل مینځ كې پټې پټې خبرې كوي چې تاسو وخت نه دى تېر كړى (په دنیا كې) مګر لس شپې');
INSERT INTO "abulsalam" VALUES(20,104,'20:104',' مونږ ښه عالمان یو په هغو خبرو چې دوى يې وايي، كله چې په دوى كې د روش په لحاظ ډېر كامل وايي: تاسو وخت نه دى تېر كړى مګر یوه ورځ');
INSERT INTO "abulsalam" VALUES(20,105,'20:105',' او دوى له تا نه د غرونو په باره كې پوښتنه كوي، نو ته (ورته) ووایه چې زما رب به دغه (غرونه) تار په تار كړي، تار تار كول');
INSERT INTO "abulsalam" VALUES(20,106,'20:106',' نو دغه (ځمكه) به تش هوار ډاګ پرېږدي');
INSERT INTO "abulsalam" VALUES(20,107,'20:107',' چې ته به په دې كې نه ښكته والى وینې او نه څه اوچت والى');
INSERT INTO "abulsalam" VALUES(20,108,'20:108',' په دغې ورځ كې به دوى د بلونكي پیروي كوي، دغې (بلنې) ته به هېڅ كوږ والى نه وي، او ټول اوازونه به رحمٰن ته چوپ شي، نو ته به نه اورې مګر كشارى (د قدمونو)');
INSERT INTO "abulsalam" VALUES(20,109,'20:109',' په دغې ورځ كې به شفاعت فايده نه رسوي مګر هغه چا ته چې د هغه په حق كې رحمٰن اجازت وكړي او دغه (الله) د ده په خبره راضي شوى وي');
INSERT INTO "abulsalam" VALUES(20,110,'20:110',' دغه (الله) پوهېږي په هر هغه څه چې د دوى په وړاندې دي او په هر هغه څه چې د دوى وروسته دي او دوى د هغه د علم احاطه نشي كولى');
INSERT INTO "abulsalam" VALUES(20,111,'20:111',' او مخونه تل ژوندي، د ټول عالم انتظام كوونكي ذات ته ټیټ شول او تحقیق نامراده شو هغه كس چې (د) ظلم (بار) يې پورته كړ');
INSERT INTO "abulsalam" VALUES(20,112,'20:112',' او هغه څوك چې نېك عمل وكړي، په دې حال كې چې مومن وي، نو دى به نه له ظلم (بې انصافۍ) نه وېرېږي او نه له نقصان نه');
INSERT INTO "abulsalam" VALUES(20,113,'20:113',' او هم داسې مونږ دغه (كتاب) نازل كړى دى عربي قرآن، او مونږ په ده كې (ایتونه) له وعید ځنې بیا بیا راوړي دي، د دې لپاره چې دوى پرهېزګاره شي یا دغه (قرآن) دوى ته څه پند پیدا كړي');
INSERT INTO "abulsalam" VALUES(20,114,'20:114',' نو اوچت دى الله حق بادشاه دى او ته په (لوستلو د) قرآن سره تلوار مه كوه مخكې له دې چې تا ته د ده وحي پوره كړى شي او ته وایه: اى زما ربه ما ته (زما) په علم كې زیاتوالى راكړې');
INSERT INTO "abulsalam" VALUES(20,115,'20:115',' او یقینًا په تحقیق سره مونږ ادم ته له دې نه مخكې حكم كړى و، نو هغه هېر كړ او مونږ د ده پخه اراده ونه مونده');
INSERT INTO "abulsalam" VALUES(20,116,'20:116',' او كله چې مونږ ملايكو ته وویل: تاسو ادم ته سجده وكړئ، نو دوى سجده وكړه، غیر له ابلیس نه، ده انكار وكړ');
INSERT INTO "abulsalam" VALUES(20,117,'20:117',' نو مونږ ادم ته وویل: بېشكه دا (ابلیس) ستا او ستا د ښځې دښمن دى، نو دى دې تاسو هرګز له جنت نه ونه باسي، (ګنې) نو په مشقت كې به پرېوځئ');
INSERT INTO "abulsalam" VALUES(20,118,'20:118',' بېشكه تا لپاره دا مقرر ده چې ته به په دې (جنت) كې نه وږى كېږې او نه به بربنډېږې');
INSERT INTO "abulsalam" VALUES(20,119,'20:119',' او دا چې بېشكه ته به نه په ده كې تږى كېږې او نه به دې ګرمي كېږي');
INSERT INTO "abulsalam" VALUES(20,120,'20:120',' نو ده ته شیطان وسوسه واچوله، ويې ويل:اى ادمه! ایا زه تا ته د تل ژوندي پاتې كېدلو ونه وښیم او داسې بادشاهي چې نه به ختمېږي');
INSERT INTO "abulsalam" VALUES(20,121,'20:121',' نو دوى دواړو (ښځې خاوند) له دغې (ونې) نه خوراك وكړ، نو دوى ته خپل د شرم ځایونه ښكاره شول او دوى شروع شول چې په خپلو ځانونو يې د جنت پاڼې نښلولې او ادم د خپل رب نافرماني وكړه، نو وښویده ');
INSERT INTO "abulsalam" VALUES(20,122,'20:122',' بیا دى خپل رب غوره كړ، نو د ده توبه يې قبوله كړه او هدایت يې وكړ (ده ته)');
INSERT INTO "abulsalam" VALUES(20,123,'20:123',' (الله) وویل: تاسو دواړه له دې (جنت) نه كوز شئ ټول، ستاسو ځینې به د ځینو نورو دښمنان وي، نو كه تاسو ته زما له جانبه هدایت راغى، نو چا چې زما د هدایت پیروي وكړه، نو دى به نه ګمراه كېږي او نه به بدبخته كېږي');
INSERT INTO "abulsalam" VALUES(20,124,'20:124',' او هغه څوك چې زما له ذكر (قرآن) نه مخ وګرځوي، نو بېشكه د ده لپاره ډېر تنګ ګذران (او ژوند) دى او دى به مونږ د قیامت په ورځ ړوند راپورته كوو');
INSERT INTO "abulsalam" VALUES(20,125,'20:125',' دى به وايي: اى زما ربه! تا زه ولې ړوند راپاڅولم، حال دا چې زه (په دنیا كې) بینا وم');
INSERT INTO "abulsalam" VALUES(20,126,'20:126',' (الله به) ووايي: همداسې و، تا ته زمونږ ایتونه راغلي وو، نو تا هغه هېر كړل، او همداسې به ته نن ورځ هېرولى شې');
INSERT INTO "abulsalam" VALUES(20,127,'20:127',' او همداسې مونږ جزا وركوو هغه چا ته چې له حده ووت او د خپل رب په ایتونو يې ایمان رانه وړ او خامخا د اخرت عذاب ډېر سخت دى او ډېر باقي پاتې كېدونكى دى');
INSERT INTO "abulsalam" VALUES(20,128,'20:128',' ایا نو دوى ته نه ده څرګنده شوې چې څومره ډېرې پېړۍ (امتونه) مونږ هلاكې كړې دي، چې دوى د هغوى په استوګنځایونو كې تېرېږي، بېشكه په دغو كې د عقل والاو لپاره ډېرې نښې دي');
INSERT INTO "abulsalam" VALUES(20,129,'20:129',' او كه چېرې هغه خبره نه وى چې ستا د رب له جانبه مخكې شوې ده (،نو) خامخا دغه (هلاكت د دوى) به لازم و، (او كه چېرې نه وى، مقرر شوې نېټه');
INSERT INTO "abulsalam" VALUES(20,130,'20:130',' نو (اى نبي!) ته په هغو خبرو صبر كوه چې دوى يې وايي او ته د خپل رب له ستاينې سره تسبیح وایه له لمر ختو نه مخكې او مخكې د پرېوتو له ده نه، او د شپې په ځینو وختونو كې، نو ته تسبیح وایه او د ورځې په غاړو كې، ښايي چې ته به خوشاله شې');
INSERT INTO "abulsalam" VALUES(20,131,'20:131',' او ته خپلې سترګې هرګز مه اوږدوه هغه څیز ته چې مونږ په هغه سره دغو ته نفع وركړې ده چې قسما قسم څیزونه دي د دنيايي ژوند ښايست، د دې لپاره چې مونږ دغه (كفار) په دې كې وازمایو او ستا د رب رزق ډېر غوره او ډېر باقي پاتې كېدونكى دى');
INSERT INTO "abulsalam" VALUES(20,132,'20:132',' او ته خپل اهل ته د لمانځه حكم كوه او ته هم په دې (لمانځه) ټینګ ولاړ اوسه، مونږ له تا نه رزق نه غواړو، مونږ تا ته رزق دركوو، او ښه انجام د پرهېزګارۍ لپاره دى');
INSERT INTO "abulsalam" VALUES(20,133,'20:133',' او دوى وايي: دى (محمد) د خپل رب له جانبه مونږ ته كومه نښه (معجزه) ولې نه راوړي؟ ایا دوى ته واضح دلیل له هغه څه نه دى راغلى چې په ړومبنیو صحیفو كې دي');
INSERT INTO "abulsalam" VALUES(20,134,'20:134',' او كه یقینًا مونږ دوى له دغه (قرآن) نه مخكې هلاك كړي وى (، نو) خامخا دوى به ویلى: اى زمونږ ربه! تا مونږ ته ولې یو رسول راونه لېږه، پس چې مونږ ستا د ایتونو پیروي كړې وى، مخكې له دې نه چې مونږ ذلیله شو او رسوا شو');
INSERT INTO "abulsalam" VALUES(20,135,'20:135',' ته (دوى ته) ووایه: هر یو انتظار كوونكى دى، نو تاسو (هم) انتظار كوئ، نو ژر ده چې تاسو به پوه شئ چې د نېغې لارې خاوندان څوك دي او څوك دى چې نېغه لار يې موندلې ده');
INSERT INTO "abulsalam" VALUES(21,1,'21:1',' خلقو ته د هغوى (د) حساب (وخت) ډېر نژدې شو، حال دا چې دوى په غفلت كې دي، مخ ګرځوونكي دي ');
INSERT INTO "abulsalam" VALUES(21,2,'21:2',' دوى ته د خپل رب له جانبه هېڅ نوى نصیحت نه راځي مګر دوى هغه اوري، په داسې حال كې چې دوى لوبې (مسخرې پرې) كوي');
INSERT INTO "abulsalam" VALUES(21,3,'21:3',' په دې حال كې چې د دوى زړونه غافله وي، او خفیه مشوره يې پټه وساتله هغو كسانو چې ظلم يې كړى دى (او ويې ویل:) دا (محمد) نه دى مګر ستاسو په شان یو انسان دى، ایا نو تاسو جادو ته راځئ، حال دا چې تاسو ګورئ');
INSERT INTO "abulsalam" VALUES(21,4,'21:4',' (رسول دوى ته) وویل: زما رب په اسمان او ځمكه كې په هره خبره پوهېږي او هم هغه ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(21,5,'21:5',' بلكې دوى وايي:(دا قرآن) ګډوډ خوبونه دي، بلكې دا يې له خپل ځانه جوړ كړى دى، بلكې دا شاعر دى، نو دا دې مونږ ته لویه نښه (معجزه) راوړي، له څنګه چې ړومبني (پېغمبران په هغو سره) لېږل شوي وو');
INSERT INTO "abulsalam" VALUES(21,6,'21:6',' ایمان نه دى راوړى له دوى نه مخكې هېڅ یو داسې كلي، چې مونږ هغه هلاك كړى، ایا نو دوى به ایمان راوړي؟');
INSERT INTO "abulsalam" VALUES(21,7,'21:7',' او مونږ له تا نه مخكې رسولان نه دي لېږلي مګر سړي چې مونږ به هغوى ته وحي كوله، نو تاسو د ذكر له خاوندانو (عالمانو) نه تپوس وكړئ كه تاسو نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(21,8,'21:8',' او مونږ هغه (رسولان داسې) جسدونه نه ووګرځولي چې طعام به نه خوري او هغوى تل ژوندي پاتې كېدونكي نه وو');
INSERT INTO "abulsalam" VALUES(21,9,'21:9',' بیا مونږ له دوى سره وعده رښتیا كړه، نو مونږ دوى ته نجات وركړ او هغه چا ته چې زمونږ اراده (د هغوى د نجات) وه او له حد نه تېرېدونكي مو هلاك كړل');
INSERT INTO "abulsalam" VALUES(21,10,'21:10',' یقینًا يقینًا مونږ تاسو ته داسې كتاب نازل كړى دى چې په هغه كې ستاسو ذكر (او عزت) دى، ایا نو تاسو عقل نه لرئ؟');
INSERT INTO "abulsalam" VALUES(21,11,'21:11',' او څومره ډېر كلي چې ظالم وو، مونږ مات (او ټوټې) كړي دي او له هغو نه پس مو نور خلق پیدا كړل');
INSERT INTO "abulsalam" VALUES(21,12,'21:12',' نو كله چې به يې زمونږ عذاب محسوس كړ، ناڅاپه به هغوى له دغو (كلیو) نه (په ډېره بيړه) تښتېدل');
INSERT INTO "abulsalam" VALUES(21,13,'21:13',' تاسو مه تښتئ، او واپس لاړ شئ هغو (نعمتونو) ته چې تاسو ته په هغو كې خوشحالي دركړى شوې وه او خپلو كورونو ته، د دې لپاره چې له تاسو نه پوښتنه وكړى شي');
INSERT INTO "abulsalam" VALUES(21,14,'21:14',' دوى وویل: اى زمونږ هلاكته! بېشكه همدا مونږ ظالمان وو');
INSERT INTO "abulsalam" VALUES(21,15,'21:15',' نو تل به د دوى دغه چغه وه تر دې چې مونږ به دوى وګرځول رېبل شوي، مړه یخ پراته');
INSERT INTO "abulsalam" VALUES(21,16,'21:16',' او مونږ اسمان او ځمكه او هغه څه چې د دواړو په مینځ كې دي (داسې) نه دي پیدا كړي، چې لوبې كوونكي یو');
INSERT INTO "abulsalam" VALUES(21,17,'21:17',' كه مونږ اراده كړې وى چې څه لوبه تماشه جوړه كړو (نو) یقینًا هغه به مو له خپلې خوا نه جوړه كړې وى كه مونږ (دا كار) كوونكي وى');
INSERT INTO "abulsalam" VALUES(21,18,'21:18',' بلكې مونږ په حق سره باطل وُلو، نو دا د هغه دماغ راوباسي، نو ناڅاپه هغه ختمېدونكى وي، او تاسو ته د هغه څه په وجه هلاكت دى چې تاسو يې بیانوئ');
INSERT INTO "abulsalam" VALUES(21,19,'21:19',' او خاص د هغه لپاره دي هغه څوك چې په اسمانونو او ځمكه كې دي، او هغه څوك چې له هغه سره دي، هغوى د هغه له عبادت نه تكبر نه كوي او نه ستومانه كېږي');
INSERT INTO "abulsalam" VALUES(21,20,'21:20',' هغوى شپه او روځ تسبیح وايي، نه څه وقفه كوي');
INSERT INTO "abulsalam" VALUES(21,21,'21:21',' ایا دوى له ځمكې نه نور معبودان نیولي دي چې هغوى ژوندي كول كوي؟');
INSERT INTO "abulsalam" VALUES(21,22,'21:22',' كه چېرې په دغو (ځمكه او اسمان) كې له الله نه پرته نور معبودان وى (نو) دواړه به خامخا وران شوي وو، نو الله د عرش مالك، پاك دى له هغو خبرو چې دوى يې بیانوي');
INSERT INTO "abulsalam" VALUES(21,23,'21:23',' له هغه نه پوښتنه نشي كېدى د هغه څه په باره كې چې هغه يې كوي او له دوى نه پوښتنه كېږي');
INSERT INTO "abulsalam" VALUES(21,24,'21:24',' ایا دوى له هغه (الله) نه پرته نور معبودان نیولي دي، ته ووایه: تاسو خپل پوخ دلیل راوړئ، دا (وحي) د هغه چا نصیحت دى چې زما سره دي او د هغه چا نصیحت دى چې له ما نه مخكې تېر شوي دي، بلكې د دوى (كافر) اكثره خلق حق نه پېژني، نو دوى مخ ګروځونكي دي');
INSERT INTO "abulsalam" VALUES(21,25,'21:25',' او مونږ له تا نه مخكې هېڅ رسول نه دى لېږلى مګر چې هغه ته به مونږ(دا) وحي كوله چې بېشكه شان دا دى چې له ما نه پرته بل هېڅ حق معبود نشته، لهذا تاسو زما عبادت كوئ');
INSERT INTO "abulsalam" VALUES(21,26,'21:26',' او دوى (كافر) وايي: الله (د ځان لپاره) اولاد نیولى دى، هغه (له دې نه) پاك دى، بلكې (ملايك خو) ډېر معزز بنده ګان دي');
INSERT INTO "abulsalam" VALUES(21,27,'21:27',' له هغه (الله) نه په خبره كولو كې ړومبى والى نه كوئ او دوى خاص د هغه په حكم عمل كوي');
INSERT INTO "abulsalam" VALUES(21,28,'21:28',' هغه ته معلوم دي څه چې د دوى په وړاندې دي او څه چې له دوى نه وروسته دي، او دوى شفاعت نشي كولى مګر د هغه چا لپاره چې هغه (الله) ترې راضي وي او دوى خاص د هغه له هیبته وېرېدونكي دي');
INSERT INTO "abulsalam" VALUES(21,29,'21:29',' او په دوى كې چې څوك ووايي چې بېشكه زه له هغه (الله) نه پرته معبود یم، نو دغه دى چې مونږ ده ته (د) جهنم جزا وركوو، همداسې مونږ ظالمانو ته جزا وركوو');
INSERT INTO "abulsalam" VALUES(21,30,'21:30',' ایا هغه كسان چې كافران شوي دي په دې نه دي خبر شوي چې یقینًا یقینًا اسمانونه او ځمكه دواړه سره نښتي وو، بیا مونږ دواړه بېل كړل، او مونږ له اوبو څخه هر ژوند شى پیدا كړى دى، ایا نو دوى ایمان نه راوړي؟');
INSERT INTO "abulsalam" VALUES(21,31,'21:31',' او مونږ په ځمكه كې پاخه غرونه پیدا كړي دي چې په دوى باندې (دغه ځمكه) ونه خوځېږي او مونږ په دې كې لارې پیدا كړې دي، چې ارتې پراخه دي، د دې لپاره چې دوى لار ومومي');
INSERT INTO "abulsalam" VALUES(21,32,'21:32',' او مونږ اسمان محفوظ چت جوړ كړى دى او دوى (كفار) د هغه (اسمان) له نخښونه مخ ګرځوونكي دي');
INSERT INTO "abulsalam" VALUES(21,33,'21:33',' او دى همغه ذات دى چې شپه او ورځ او لمر او سپوږمۍ يې پیدا كړي دي، هر یو په (خپل) فلك (دايره) كې ګرځي');
INSERT INTO "abulsalam" VALUES(21,34,'21:34',' اومونږ له تا نه مخكې هېڅ یو انسان ته تل ژوندي پاتې كېدل نه دي مقرر كړي، ایا نو كه ته مړ شې، نو دوى به تل ژوندي پاتې كېدونكي وي؟');
INSERT INTO "abulsalam" VALUES(21,35,'21:35',' هر یو نفس د مرګ (مزه) څكونكى دى، او مونږ تاسو په شر او خیر سره ازمایو، ازمایل، او تاسو به خاص مونږ ته راوستلى شئ');
INSERT INTO "abulsalam" VALUES(21,36,'21:36',' او كله چې تا هغه كسان وویني چې كافران شوي دي (نو) دوى تا نه نیسي مګر مسخره كړى شوى (او په خپل مینځ كې وايي:) ایا دا همغه كس دى چې ستاسو معبودان (په سپكه) یادوي؟ حال دا چې همدوى پخپله د رحمن له یادولو نه انكاري دي');
INSERT INTO "abulsalam" VALUES(21,37,'21:37',' انسان له تلواره پیدا كړى شوى دى، ژر به تاسو ته خپلې نښې وښيم، لهذا له ما نه ژر مطالبه مه كوئ');
INSERT INTO "abulsalam" VALUES(21,38,'21:38',' او دوى وايي: دغه وعده به كله وي، كه تاسو رښتیني یئ؟');
INSERT INTO "abulsalam" VALUES(21,39,'21:39',' كه كافران شوي كسان په هغه وخت كې پوه شي چې له خپلو مخونو نه به اور نشي منع كولى او نه له خپلو شاګانو او نه به د دوى مرسته كولى شي');
INSERT INTO "abulsalam" VALUES(21,40,'21:40',' بلكې دا (قیامت) به په دوى ناڅاپه راشي، نو دوى به حیران كړي، بیا دوى د هغه د لرې كولو طاقت نه لري او نه به دوى ته مهلت وركاوه شي');
INSERT INTO "abulsalam" VALUES(21,41,'21:41',' او یقینًا یقینًا له تا نه مخكې رسولانو پورې استهزاګانې شوې دي، نو ګېر كړل هغو كسانو لره چې په دوى پورې به يې مسخرې كولې، هغه شي چې دوى به ورپورې استهزا كوله');
INSERT INTO "abulsalam" VALUES(21,42,'21:42',' (دوى ته) ته ووایه: تاسو د رحمن (له عذاب) نه د شپې او د ورځې څوك ساتي؟ بلكې دوى دخپل رب له ذكر نه مخ ګرځوونكي دي');
INSERT INTO "abulsalam" VALUES(21,43,'21:43',' ایا د دوى لپاره نور معبودان شته چې زمونږ نه پرته دوى ساتي؟ هغوى د خپلو ځانونو د مرستې طاقت نه لري او نه دوى زمونږ (د عذاب) نه ساتل كېږي');
INSERT INTO "abulsalam" VALUES(21,44,'21:44',' بلكې مونږ دوى ته او د دوى پلرونو ته نفع وركړه تر دې چې په دوى باندې عمر اوږد شو، ایا نو دوى نه ویني چې بېشكه مونږ ځمكې ته راځو، د هغې له غاړو نه هغه كموو، ایا نو هم دوى غالب دي؟');
INSERT INTO "abulsalam" VALUES(21,45,'21:45',' ته (دوى ته) ووایه: یقینًا زه تاسو يواځې په وحي سره وېروم او كاڼه بلنه نه اوري كله چې هغوى وېرولى شي');
INSERT INTO "abulsalam" VALUES(21,46,'21:46',' او كه یقینًا دوى ته ستا د رب له عذاب نه لږ تپ ورسېږي، (نو) دوى به خامخا لازمًا وايي: اى زمونږ هلاكته! بېشكه هم مونږ ظالمان وو');
INSERT INTO "abulsalam" VALUES(21,47,'21:47',' او د قیامت په ورځ به مونږ ډېر د انصاف تلې كېږدو، نو په هېڅ نفس باندې به هېڅ شى ظلم ونه كړى شي او كه دا (عمل) د اوري د دانې په وزن وي مونږ به هغه راوړو او هم مونږ حساب كوونكي بس یو ');
INSERT INTO "abulsalam" VALUES(21,48,'21:48',' او یقینًا یقینًا مونږ موسٰى او هارون ته فرقان (حق او باطل سره بېلوونكى كتاب) او رڼا او د متقیانو لپاره ذكر (نصیحت) وركړى و');
INSERT INTO "abulsalam" VALUES(21,49,'21:49',' هغه كسان چې له خپل رب نه خشیت (وېره) كوي بې له ليدلو او دوى د قیامت څخه وېرېدونكي دي');
INSERT INTO "abulsalam" VALUES(21,50,'21:50',' او دا (قرآن) مبارك نصیحت دى چې مونږ دا نازل كړى دى، ایا نو تاسو له ده نه انكار كوونكي یئ');
INSERT INTO "abulsalam" VALUES(21,51,'21:51',' او یقینًا یقینًا مونږ ابراهیم ته د هغه هدایت له دې نه مخكې وركړى و او مونږ په هغه باندې پوه وو');
INSERT INTO "abulsalam" VALUES(21,52,'21:52',' كله چې هغه خپل پلار او خپل قوم ته وویل: دا بتان، څه شى دى هغه چې تاسو دوى ته په عبادت ټینګ ولاړ یئ؟');
INSERT INTO "abulsalam" VALUES(21,53,'21:53',' هغوى وویل: مونږ خپل پلرونه دوى ته عبادت كوونكي موندلي دي');
INSERT INTO "abulsalam" VALUES(21,54,'21:54',' هغه وویل: یقینًا یقینًا تاسو او ستاسو پلرونه په ښكاره ګمراهۍ كې یئ');
INSERT INTO "abulsalam" VALUES(21,55,'21:55',' هغوى وویل: ایا تا مونږ ته حق راوړى دى، یا ته له لوبو كوونكو څخه يې؟');
INSERT INTO "abulsalam" VALUES(21,56,'21:56',' هغه وویل: بلكې ستاسو رب د اسمانونو او ځمكې رب دى هغه (رب) چې دغه يې پیدا كړي دي او زه په دې باندې له ګواهانو ځنې یم');
INSERT INTO "abulsalam" VALUES(21,57,'21:57',' او په الله قسم دى چې هرومرو خامخا به زه ستاسو له بتانو سره یو چال چلوم وروسته له دې چې تاسو وګرځئ شاكوونكي');
INSERT INTO "abulsalam" VALUES(21,58,'21:58',' نو هغه، دغه (بتان) ټوټې ټوټې كړل د هغو له لوى نه غير (چې هغه يې پرېښود)، د دې لپاره چې هغوى همده ته رجوع وكړي');
INSERT INTO "abulsalam" VALUES(21,59,'21:59',' دوى وویل: زمونږ له معبودانو سره دا كار چا كړى دى، بېشكه دغه كس یقینًا له ظالمانو ځنې دى');
INSERT INTO "abulsalam" VALUES(21,60,'21:60',' دوى وویل: مونږ له یو زلمي نه اورېدلي وو، چې دوى به يې (په سپكه) یادول، هغه ته ابراهیم ویلى شي');
INSERT INTO "abulsalam" VALUES(21,61,'21:61',' هغوى وویل: نو هغه د خلقو د سترګو په وړاندې راولئ، د دې لپاره چې هغوى (يې وویني او) ګواهي وكړي');
INSERT INTO "abulsalam" VALUES(21,62,'21:62',' ويې ويل:اى ابراهیمه! ایا تا زمونږ له معبودانو سره دا كار كړى دى؟');
INSERT INTO "abulsalam" VALUES(21,63,'21:63',' هغه وویل: بلكې دا كار د دوى دې مشر (لوى بت) كړى دى، نو تاسو له دوى نه پوښتنه وكړئ كه دوى خبرې كوي');
INSERT INTO "abulsalam" VALUES(21,64,'21:64',' نو دوى خپلو نفسونو ته رجوع وكړه، بیا يې وویل: بېشكه هم تاسو ظالمان یئ');
INSERT INTO "abulsalam" VALUES(21,65,'21:65',' بیا دوى په خپلو سرونو نسكور كړى شول (ويې ویل:) یقینًا یقینًا ته پوه يې چې دا (بتان) خبرې نشي كولى');
INSERT INTO "abulsalam" VALUES(21,66,'21:66',' هغه وویل: ایا نو تاسو له الله نه پرته د هغه څه عبادت كوئ چې تاسو ته هېڅ نفع نشي رسولى او نه تاسو ته ضرر رسولى شي');
INSERT INTO "abulsalam" VALUES(21,67,'21:67',' اُف په تاسو او په هغو چې تاسو يې له الله نه پرته عبادت كوئ، ایا نو تاسو عقل نه لرئ');
INSERT INTO "abulsalam" VALUES(21,68,'21:68',' ويې ويل:دا وسوځوئ او د خپلو معبودانو مرسته وكړئ كه چېرې تاسو (څه) كوونكي یئ');
INSERT INTO "abulsalam" VALUES(21,69,'21:69',' مونږ وویل: اى اوره! یخ او سلامتیا والا شه په ابراهیم باندې');
INSERT INTO "abulsalam" VALUES(21,70,'21:70',' او دوى له هغه سره د ضررناك مكر كولو اراده كړې وه، نو مونږه هم دوى بې حده تاوانیان وګرځول');
INSERT INTO "abulsalam" VALUES(21,71,'21:71',' او مونږ هغه ته او لوط ته نجات وركړ د هغې ځمكې په طرف چې مونږ په هغې كې د عالَمونو لپاره بركت اچولى دى');
INSERT INTO "abulsalam" VALUES(21,72,'21:72',' او مونږ هغه ته اسحق عطا كړ او یعقوب، زیاتي، او مونږه (دغه) هر یو نېكان ګرځولي دي');
INSERT INTO "abulsalam" VALUES(21,73,'21:73',' او مونږه هغوى داسې امامان ګرځولي وو چې زمونږ په حكم سره به يې لار ښوونه كوله او مونږ دوى ته د نېكۍ كولو او د لمونځ قايمولو او د زكات وركولو وحي كړې وه او دوى خاص زمونږ عبادت كوونكي وو');
INSERT INTO "abulsalam" VALUES(21,74,'21:74',' او لوط ته، مونږ هغه ته د فیصلې كولو قوت او پوهه وركړې وه او مونږ هغه ته له هغه كلي نه نجات وركړى و چې ناپاكه خبیث عملونه يې كول، بېشكه هغوى د بدۍ خلق (او) نافرمانه وو');
INSERT INTO "abulsalam" VALUES(21,75,'21:75',' او مونږ هغه (لوط) په خپل رحمت كې داخل كړ، یقینًا هغه له نېكانو څخه دى');
INSERT INTO "abulsalam" VALUES(21,76,'21:76',' او (یاد كړه) نوح، كله چې هغه له دې نه مخكې اواز وكړ، نو مونږ د هغه سوال قبول كړ، نو مونږ هغه ته او د هغه كورنۍ ته له ډېر لوى غم نه نجات وركړ');
INSERT INTO "abulsalam" VALUES(21,77,'21:77',' او مونږ د ده لپاره له هغه قوم نه انتقام واخيست چې زمونږ د ایتونو تكذیب يې كړى و، بېشكه هغوى بد خلق وو، نو مونږ دوى ټول غرق كړل');
INSERT INTO "abulsalam" VALUES(21,78,'21:78',' او (یاد كړه) داود او سلیمان، كله چې هغوى د فصل په باره كې فیصله كوله، كله چې په هغه كې د شپې د خلقو بې شپانه ګډې بيزې څرېدلې وې او مونږ له هغوى فيصلې ته كتونكي وو');
INSERT INTO "abulsalam" VALUES(21,79,'21:79',' نو مونږه سليمان ته هغه (فیصله) وښودله او مونږ (دوى) هر یو ته حكم او علم وركړى و، او مونږ د داود سره غرونه تابع كړي وو چې تسبیح به يې ویله او مرغان (هم) او مونږ (د داسې كارونو) كوونكي وو');
INSERT INTO "abulsalam" VALUES(21,80,'21:80',' او مونږ هغه ته تاسو لپاره د زغرې جوړول وښودل، د دې لپاره چې هغه (زغره) ستاسو د (دښمنانو له) جنګ نه ستاسو حفاظت وكړي، نو ایا تاسو شكر كوونكي يئ؟');
INSERT INTO "abulsalam" VALUES(21,81,'21:81',' او (مونږ) د سلیمان لپاره هوا (تابع كړې ده)، چې ډېره تېزه وه، د هغه په حكم سره به هغې ځمكې (سيمې) ته روانېده چې مونږ په هغې كې بركت اچولى دى او مونږ له ازله په هر شي باندې پوه یو');
INSERT INTO "abulsalam" VALUES(21,82,'21:82',' او (مونږ تابع كړي وو) ځینې له شیطانانو څخه هغه چې د ده لپاره به يې غوپې وهلې او له دې نه غير نور كارونه به يې كول، او مونږ دوى لره (له وراني نه) ساتونكي وو');
INSERT INTO "abulsalam" VALUES(21,83,'21:83',' او (یاد كړه) ایوب كله چې هغه خپل رب ته اواز وكړ چې یقینًا زه (چې یم) ما ته تكلیف رارسېدلى دى او ته تر ټولو زیات رحم كوونكى يې');
INSERT INTO "abulsalam" VALUES(21,84,'21:84',' نو مونږ د هغه دعا قبوله كړه، نو مونږ هغه تكلیف لرې كړ چې هغه ته و او مونږ هغه ته د هغه كورنۍ او له هغوى سره د هغوى په مثل عطا كړل له خپل طرف نه د رحمت په وجه او د عبادت كوونكو لپاره د نصیحت په خاطر');
INSERT INTO "abulsalam" VALUES(21,85,'21:85',' او (یاد كړه) اسمعیل او ادریس او ذوالكفل، دغه هر یو له صبر كوونكو څخه دي ');
INSERT INTO "abulsalam" VALUES(21,86,'21:86',' او مونږ دوى په خپل رحمت كې داخل كړل، بېشكه دوى له صالحانو څخه دي');
INSERT INTO "abulsalam" VALUES(21,87,'21:87',' او (یاد كړه) د كب والا، كله چې هغه د غصې په حال كې لاړ، نو هغه دا خیال وكړ چې مونږ به په ده باندې له سره تنګي ونه كړو، بیا يې په تیارو كې اواز وكړ (داسې) چې نشته هېڅ لايق د عبادت مګر ته يې، ته پاك يې، بېشكه هم زه له ظالمانو څخه یم');
INSERT INTO "abulsalam" VALUES(21,88,'21:88',' نو مونږ د هغه دعا قبوله كړه او مونږ هغه له غم نه خلاص كړ او همداسې مونږ مومنانو ته نجات وركوو ');
INSERT INTO "abulsalam" VALUES(21,89,'21:89',' او (یاد كړه) زكریا كله چې هغه خپل رب ته اواز وكړ: اى زما ربه! ما يواځې (بې اولاده) مه پرېږده او ته ترټولو غوره وارث يې');
INSERT INTO "abulsalam" VALUES(21,90,'21:90',' نو مونږ د هغه دعا قبوله كړه او مونږ هغه ته یحيٰى عطا كر او د هغه لپاره مو د هغه ښځه روغه كړه، بېشكه دوى (نبیان داسې) وو چې په نېكیو كې به يې تلوار كاوه او مونږ به يې بللو، په دې حال كې چې رغبت كوونكي او وېرېدونكي وو او دوى خاص زمونږ لپاره تواضع كوونكي وو');
INSERT INTO "abulsalam" VALUES(21,91,'21:91',' او (یاده كړه) هغه (جینۍ) چې د خپل شرم ځاى حفاظت يې كړى و، نو مونږ په (ګرېوان د) هغې كې خپل ځینې روح پو كړ او مونږ هغه او د هغې زوى د خلقو لپاره لویه نښه وګرځول');
INSERT INTO "abulsalam" VALUES(21,92,'21:92',' بېشكه همدا ستاسو دین دى، چې یو دین دى او هم زه ستاسو رب یم، نو تاسو زما عبادت كوئ');
INSERT INTO "abulsalam" VALUES(21,93,'21:93',' او دوى خپل (د دين) كار په خپل مینځ كې ټوټې ټوټې كړ، دوى ټول خاص مونږ ته واپس راتلونكي دي');
INSERT INTO "abulsalam" VALUES(21,94,'21:94',' نو هغه څوك چې د نېكۍ عملونه كوي، په دې حال كې چې مومن وي، نو د ده د كوشش لپاره هېڅ ناقدري نشته او بېشكه مونږ د دغه (كوشش) لیكونكي یو');
INSERT INTO "abulsalam" VALUES(21,95,'21:95',' او حرام (ممتنع) دي په هغه كلي باندې چې مونږ هغه هلاك كړى وي دا چې په رښتیا به دوى بېرته نه راځي');
INSERT INTO "abulsalam" VALUES(21,96,'21:96',' تر هغه پورې كله چې یاجوج ماجوج پرانستل شي او دوى به له هرې لوړې نه ګړندي راكوزېږي');
INSERT INTO "abulsalam" VALUES(21,97,'21:97',' او حقه وعده به ښه رانژدې شي، نو قصه دا ده چې ناڅاپه د هغو كسانو سترګې به بوټې راوتلې پاتې وي چې كفر يې كړى دى (او وايي به:) اى زمونږ هلاكته! یقینًا مونږ له دې نه په غفلت كې وو، بلكې مونږ ظالمان وو');
INSERT INTO "abulsalam" VALUES(21,98,'21:98',' بېشكه تاسو او هغه څه چې تاسو يې له الله نه پرته عبادت كوئ؛ (ټول) د جهنم خشاك یئ، تاسو هغه ته ننوتونكي یئ');
INSERT INTO "abulsalam" VALUES(21,99,'21:99',' كه چېرې دوى (په رښتیا) معبودان وى (، نو) ده ته به نه وو داخل شوي او (دا) ټول به په هغه كې تل ترتله وي');
INSERT INTO "abulsalam" VALUES(21,100,'21:100',' په دغه (دوزخ) كې به د دوى چغې سوري وي او دوى په هغه كې (هېڅ) نه اوري');
INSERT INTO "abulsalam" VALUES(21,101,'21:101',' بېشكه هغه كسان چې هغوى ته زمونږ له جانبه سعادت مخكې فیصله شوى دى، دغه كسان له دغه (دوزخ) نه لرې كړى شوي دي');
INSERT INTO "abulsalam" VALUES(21,102,'21:102',' دوى به د هغه (دوزخ) اواز (هم) نه اوري او دوى به په هغو (نعمتونو) كې تل ترتله وي چې د دوى نفسونه يې غواړي');
INSERT INTO "abulsalam" VALUES(21,103,'21:103',' دوى به هغه تر ټولو لویه وېره نه غمجن كوئ او له دوى سره به ملايك ملاقات كوي (او وايي به:) دا ستاسو هغه ورځ ده چې تاسو ته به يې وعده دركولى شوه');
INSERT INTO "abulsalam" VALUES(21,104,'21:104',' (یاده كړه) هغه ورځ چې مونږ به اسمان راونغاړو په شان د كاغذ د رانغاړلو په لیكنې باندې، لكه څنګه چې مونږ د اول تخلیق ابتدا كړې ده، مونږ د هغه بیا اعاده كوو، دا زمونږ په ذمه وعده ده، بېشكه مونږ (دا كار) كوونكي یو');
INSERT INTO "abulsalam" VALUES(21,105,'21:105',' او یقینًا یقینًا مونږ په زبور كې له نصیحت نه پس دا لیكلي وو چې بېشكه دغه ځمكه، د دې به زما صالح (باصلاحيته) بنده ګان وارثان كېږي');
INSERT INTO "abulsalam" VALUES(21,106,'21:106',' بېشكه په دې (بیان) كې یقینًا د عبادت كوونكو خلقو لپاره كفایت دى');
INSERT INTO "abulsalam" VALUES(21,107,'21:107',' او مونږ ته نه يې لېږلې مګر د عالمونو لپاره رحمت');
INSERT INTO "abulsalam" VALUES(21,108,'21:108',' ووایه: بېشكه همدا خبره ده چې ما ته وحي كولى شي چې یقینًا هم دا خبره ده چې ستاسو حق معبود یو معبود دى، نو ایا تاسو اسلام راوړونكي یئ؟');
INSERT INTO "abulsalam" VALUES(21,109,'21:109',' نو كه دوى وګرځېدل، نو ته (ورته) ووایه: ما تاسو په برابرۍ سره خبر كړي یئ او زه نه پوهېږم چې ایا نژدې دى، یا كه لرې دى هغه چې تاسو سره يې وعده كولى شي');
INSERT INTO "abulsalam" VALUES(21,110,'21:110',' بېشكه هغه (الله) په ښكاره خبره پوهېږي او په هغې هم چې تاسو يې پټوئ');
INSERT INTO "abulsalam" VALUES(21,111,'21:111',' او زه نه پوهېږم كېدى شي چې دا (تاخیر د عذاب) تاسو لپاره یو ازمېښت وي او فايده اخيستل وي تر یو وخته پوري');
INSERT INTO "abulsalam" VALUES(21,112,'21:112',' (رسول) وویل: اى زما ربه! په حق سره فیصله وكړه او هم زمونږ رب ډېر مهربان دى، مدد ترې غوښتل شوى په هغو خبرو چې تاسو يې بیانوئ');
INSERT INTO "abulsalam" VALUES(22,1,'22:1',' اى خلقو! له خپل رب نه ووېرېږئ، بېشكه د قیامت زلزله ډېر لوى شى دى');
INSERT INTO "abulsalam" VALUES(22,2,'22:2',' په هغې ورځ چې تاسو هغه ووینئ، چې هره تى وركوونكې به غافله شي له هغه (وړوكي) نه چې هغه ته تى وركوي او هره حمل والا به خپل حمل وغورځوي او ته به خلقو لره نشه وینې، حال دا چې دوى به نشه نه وي او لېكن د الله عذاب به ډېر سخت وي');
INSERT INTO "abulsalam" VALUES(22,3,'22:3',' او په خلقو كې ځینې هغه دي چې د الله په باره كې بې له علمه جګړه كوي او د هر سركشه شیطان پیروي كوي');
INSERT INTO "abulsalam" VALUES(22,4,'22:4',' په باره د هغه كې (په ازل كې دا) لیكل شوي دي چې بېشكه شان دا دى چې هر څوك د ده سره دوستي وكړي، نو یقینًا دا (شیطان) دغه (خپل دوست) ګمراه كوي او ده ته د لمبې وهونكي عذاب لاره ښیي');
INSERT INTO "abulsalam" VALUES(22,5,'22:5',' اى خلقو! كه تاسو د بیا راژوندي كېدو په باره كې په شك كې یئ، نو یقینًا مونږ تاسو له خاورې نه پیدا كړې یئ، (ستاسو نیكه ادم) بیا (مو د هغه اولاده پیدا كړه) له یو څاڅكي (مني) نه، بیا له پرنې وینې نه، بیا له یوې غوښې بوټۍ نه چې تام الخلقت وي او ناقص الخلقت وي، د دې لپاره چې مونږ تاسو ته (خپل حكمت او قدرت) بیان كړو او مونږ په رحمونو كې ثابت پرېږدو هغه چې مونږ يې اراده وكړو تر معلومې نېټې پورې، بیا مونږ تاسو راوباسو (د میندو له خېټو نه) په داسې حال كې چې واړه بچي یئ، بیا (مونږ تاسو پالو) د دې لپاره چې تاسو خپل قوت او كمال ته ورسئ او په تاسو كې ځینې هغه دي چې وفات كولى شي او ځینې په تاسو كې هغه دي چې رد كولى (رسولى) شي تر ټولو ناكاره عمر پورې، د دې لپاره چې هغه له پوهېدلو نه وروسته په هېڅ شي پوه نشي او ته به ځمكه وچه شاړه وینې، نو كله چې مونږ په هغې باندې اوبه نازلې كړو (نو) څربه او نرمه شي او له هر قسمه ښكلو (نباتاتو) نه زرغون كړي');
INSERT INTO "abulsalam" VALUES(22,6,'22:6',' دا په سبب د دې چې یقینًا هم الله حق دى او دا چې یقینًا هغه مړي ژوندي كوي او دا چې بېشكه هغه په هر شي باندې قادر دى');
INSERT INTO "abulsalam" VALUES(22,7,'22:7',' او دا چې یقینًا قیامت راتلونكى دى، چې په (راتلو د) هغه كې هېڅ شك نشته او دا چې بېشكه الله به بیا ژوندي راپورته كوي هر هغه چې په قبرونو كې دي');
INSERT INTO "abulsalam" VALUES(22,8,'22:8',' او په خلقو كې ځینې هغه دي چې د الله په باره كې بې له علمه او بې له هدایته او بې له روښانه كتابه جګړه كوي');
INSERT INTO "abulsalam" VALUES(22,9,'22:9',' په داسې حال كې چې خپل څنګ اړوي، د دې لپاره چې د الله له لارې نه (خلق) ګمراه كړي، د ده لپاره په دنیا كې رسوايي ده او مونږ به په ده د قيامت په ورځ د سوځوونكي اور عذاب وڅكوو');
INSERT INTO "abulsalam" VALUES(22,10,'22:10',' دا په سبب د هغو عملونو دي چې ستا دواړو لاسونو مخكې لېږلي دي او دا چې بېشكه الله په خپلو بنده ګانو باندې له سره ظلم كوونكى نه دى');
INSERT INTO "abulsalam" VALUES(22,11,'22:11',' او په خلقو كې ځینې هغه دي چې د الله عبادت كوي په یوې غاړه باندې، نو كه ده ته خیر ورسي (نو) په دې (دین) مطمئن (او قايم) پاتې شي او كه ده ته څه مصیبت (ازمېښت) ورسي (، نو) بېرته په خپل مخ (كفر ته) وګرځي، ده (خپل) دنیا او اخرت ضايع كړل، همدغه ښكاره تاوان دى');
INSERT INTO "abulsalam" VALUES(22,12,'22:12',' دى له الله نه پرته هغه څه بلي چې ده ته نه ضرر رسولى شي او نه ده ته نفع رسولى شي، همدغه ډېره لرې ګمراهي ده');
INSERT INTO "abulsalam" VALUES(22,13,'22:13',' دا هغه (معبود) بلي چې یقینًا د هغه نقصان د هغه له نفع نه زیات نژدې دى، خامخا (هغه) بد دوست دى او خامخا بد ملګرى دى');
INSERT INTO "abulsalam" VALUES(22,14,'22:14',' بېشكه الله هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي؛ داسې جنتونو ته داخلوي چې د هغو له لاندې ولې بهېږي، بېشكه الله كوي هغه چې اراده يې وكړي');
INSERT INTO "abulsalam" VALUES(22,15,'22:15',' هر څوك چې دا ګمان كوي چې الله به په دنیا او اخرت كې د دغه (رسول) له سره مدد ونه كړي، نو هغه دې اسمان ته یوه رسۍ اوږده كړي، بیا دې (هغه) غوڅه كړي، نو ودې ګوري چې ایا په رښتیا به د ده چل (او تدبیر) هغه شى ختم كړي چې دا يې په غصه كړى دى');
INSERT INTO "abulsalam" VALUES(22,16,'22:16',' او همدارنګه مونږ دا (قرآن) د څرګندو ایتونو په شكل كې نازل كړى دى او دا چې بېشكه الله هدایت كوي چا ته چې اراده وكړي');
INSERT INTO "abulsalam" VALUES(22,17,'22:17',' بېشكه هغه كسان چې ایمان يې راوړى دى او هغه كسان چې یهودیان شوي او صابیان او نصرانیان او مجوس دي او هغه كسان دي چې شرك يې كړى دى، بېشكه الله به د دوى په مینځ كې د قیامت په ورځ فیصله كوي، بېشكه الله په هر شي باندې ګواه دى');
INSERT INTO "abulsalam" VALUES(22,18,'22:18',' ایا ته نه وینې چې بېشكه الله (چې دى) ده ته سجده كوي هر هغه چې په اسمانونو كې دي او هر هغه چې په ځمكه كې دي او لمر او سپوږمۍ او ستوري او غرونه او ونې او څاروي او ډېر له خلقو ځینې او (په دوى كې) ډېر داسې دي چې په هغو باندې عذاب ثابت شوى دى او هغه څوك چې الله يې سپك كړي، نو ده ته هیڅوك عزت وركوونكى نشته، بېشكه الله كوي هغه څه چې اراده يې وكړي');
INSERT INTO "abulsalam" VALUES(22,19,'22:19',' دا دوه جګړه كوونكې (ډلې) دي چې د خپل رب په باره كې يې جګړه وكړه، نو هغه كسان چې كافران شوي دي د دوى لپاره به د اور جامي پرې كړى شي، د دوى د سرونو له پاسه به ګرمې ایشېدلې اوبه اچولى شي');
INSERT INTO "abulsalam" VALUES(22,20,'22:20',' (په دغو (اوبو) سره به هغه څه (كولمې) خوړني كړى شي چې د دوى په ګېډو كې دي او څرمنې(د دوى هم) ');
INSERT INTO "abulsalam" VALUES(22,21,'22:21',' او د دوى (د وهلو) لپاره د اوسپنو څټكونه دي');
INSERT INTO "abulsalam" VALUES(22,22,'22:22',' هر كله چې دوى له هغه (دوزخ) نه، له غم نه د وتلو اراده وكړي (،نو) بېرته به په هغه كې ورګرځولى شي(او ورته ویلى به شي:) د سخت سوځوونكي (اور) عذاب وڅَكئ');
INSERT INTO "abulsalam" VALUES(22,23,'22:23',' بېشكه الله به هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي داسې جنتونه ته داخل كړي چې د هغو له لاندې ولې بهېږي، دوى ته به په هغو كې د سروزور باهوګان واچول شي او ملغلرې او د دوى لباس به په هغو (جنتونو) كې ورېښم وي');
INSERT INTO "abulsalam" VALUES(22,24,'22:24',' او دوى ته د پاكيزه خبرې ښوونه وشوه او د ستايل شوي (الله) د لارې هدایت ورته وشو');
INSERT INTO "abulsalam" VALUES(22,25,'22:25',' بېشكه هغه كسان چې كافران شوي دي او د الله له لارې نه اړول كوي او له مسجد حرام نه هغه چې مونږ هغه د خلقو لپاره داسې ګرځولى دى چې په ده كې اوسېدونكي او له بهره راتلونكي برابر دي، او څوك چې په ده كې په ظلم سره د الحاد (او بې لار توب) اراده وكړي (،نو) مونږ به په هغه له ډېر دردوونكي عذاب څخه (عذاب) وڅَكوو');
INSERT INTO "abulsalam" VALUES(22,26,'22:26',' او (یاد كړه) هغه وخت چې مونږه ابراهیم ته د بیت الله ځاى ښكاره (معین) كړ چې ته له ما سره هېڅ شى مه شریكوه، او زما كور د طواف كوونكو، او قیام كوونكو او ركوع كوونكو (او) سجده كوونكو لپاره پاك كړه');
INSERT INTO "abulsalam" VALUES(22,27,'22:27',' او ته په خلقو كې د حج اعلان وكړه دوى به تا ته پیدل راشي او په هر خوار ډنګر اوښ باندې چې دغه (اوښان) به راځي له هرې لرې ژورې لارې نه');
INSERT INTO "abulsalam" VALUES(22,28,'22:28',' د دې لپاره چې دوى خپلو فايدو ته حاضر شي او په معلومو ورځو كې د الله نوم یاد كړي، په هغو بې ژبو څارویو باندې چې هغه (الله) دوى ته وركړي دي، نو تاسو له هغو نه خورئ او په بد حال فقیر يې خورئ');
INSERT INTO "abulsalam" VALUES(22,29,'22:29',' بیا دې دوى خپل خیري لرې كړي او خپل نذرونه دې پوره كړي او د قدیم (او عزتمن) كور طوافونه دې وكړي');
INSERT INTO "abulsalam" VALUES(22,30,'22:30',' دغه (حكم بیان شو) او څوك چې د الله د حرمتونو (احكامو) تعظیم كوي، نو دغه (كار) د ده لپاره د خپل رب په نیز ډېر غوره دى او تاسو لپاره څاروي حلال ګرځول شوي دي غیر له هغو نه چې په تاسو (يې حرمت) لوستل كېږي، نو تاسو ځان وساتئ له پلیتۍ نه چې بتان دي او تاسو د دروغو له وینا څخه ځان وساتئ');
INSERT INTO "abulsalam" VALUES(22,31,'22:31',' په داسې حال كې چې الله ته په اخلاص سره غاړه ایښودونكي یئ، له ده سره شریك جوړونكي نه یئ، او هر څوك چې له الله سره شریك ونیسي، نو ګویاكې هغه له اسمان نه راوغورځېده، نو مرغان يې وتښتوي، یا يې هوا (باد) له اوچت ځاى نه لرې په ژور ځاى كې وغورځوي');
INSERT INTO "abulsalam" VALUES(22,32,'22:32',' دغه (حكم بیان شو) او څوك چې د الله د نښو تعظیم وكړي، نو بېشكه دا د زړونو له تقوىٰ څخه دى');
INSERT INTO "abulsalam" VALUES(22,33,'22:33',' تاسو لپاره په دغو (څارویو) كې تر یو مقرر وخته پورې ډېرې فايدې دي، بیا د دوى حلالځاى قدیم كور (بیت الله) ته دى');
INSERT INTO "abulsalam" VALUES(22,34,'22:34',' او د هر امت لپاره مونږ یوه قرباني مقرر كړې ده، د دې لپاره چې دوى د الله نوم یاد كړي په هغو شیانو چې دوى ته هغه (الله) وركړي دي، چې څاروي دي، نو ستاسو حق معبود یو معبود دى، نو تاسو خاص هغه ته غاړه كېږدئ او عاجزي كوونكو ته زېرى وركړه');
INSERT INTO "abulsalam" VALUES(22,35,'22:35',' هغه كسان چې كله الله یاد كړى شي (نو) د دوى زړونه ووېرېږي او (زېرى وركړه) صبر كوونكو ته په هغو (سختیو) چې دوى ته رسېږي او لمونځ قايموونكو ته او هغو ته چې له هغه مال نه څه انفاق كوي چې مونږ دوى ته وركړى دى');
INSERT INTO "abulsalam" VALUES(22,36,'22:36',' او (د هدیې) اوښان (او غوایان) مونږه هغه تاسو لپاره د الله له نښو څخه ګرځولي دي، تاسو لپاره په دغو كې خیر دى، نو تاسو په دغو باندې د الله نوم یادوئ، په داسې حال كې چې ولاړ وي، نو كله چې د دوى اړخونه پرېوځي، نو له دغو نه خورئ او په صبر كوونكي او په بې صبره(سوالګري فقیر) يې خورئ، همداسې تاسو لپاره مونږ دغه تابع كړي دي، د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(22,37,'22:37',' الله ته له سره نه رسېږي د دغو غوښې او نه د دغو وینې او لېكن هغه ته ستاسو تقوىٰ رسېږي، همداسې يې دغه تاسو لپاره تابع كړي دي، د دې لپاره چې تاسو د الله لويي بیان كړئ په هغې (طریقې) سره چې هغه تاسو ته ښوونه كړې ده او نېكي كوونكو ته زېرى وركړه');
INSERT INTO "abulsalam" VALUES(22,38,'22:38',' بېشكه الله له هغو كسانو نه دفاع كوي چې ایمان يې راوړى دى، بېشكه الله هر ډېر خیانت ګر، لوى ناشكره نه خوښوي');
INSERT INTO "abulsalam" VALUES(22,39,'22:39',' هغو كسانو ته چې ورسره قتال كاوه شي (د قتال) اجازت وركړى شو، ځكه چې یقینًا په دوى ظلم كړى شوى دى، او بېشكه الله د دوى په مدد كولو خامخا قادر دى');
INSERT INTO "abulsalam" VALUES(22,40,'22:40',' هغه كسان چې له خپلو كورونو نه ناحقه اېستل شوي دي (بې له كوم سببه) مګر دا چې دوى به ویل: زمونږ رب الله دى، او كه چېرې د الله دفع كول نه وى خلقو لره، ځینو لره په ځینو نورو باندې (، نو) خامخا به نړول شوې وى تكيې او مدرسې او عبادتخانې او هغه جوماتونه چې په هغو كې د الله نوم ډېر یادولى شي او الله به خامخا ضرور د هغه چا مدد وكړي چې د هغه (الله) مدد كوي، بېشكه الله خامخا ډېر زورور، غالب دى');
INSERT INTO "abulsalam" VALUES(22,41,'22:41',' هغه كسان دي چې كه مونږ دوى ته په ځمكه كې قدرت وركړو (نو) دوى به لمونځ قايموي او زكات به ادا كوي او د نېكۍ حكم به كوي او له بدۍ نه به منع كوي، او خاص د الله لپاره د كارونو انجام دى');
INSERT INTO "abulsalam" VALUES(22,42,'22:42',' او كه چېرې دوى ستا تكذیب وكړي، نو یقینًا له دوى نه مخكې د نوح قوم او عادیانو او ثمودیانو تكذیب كړى دى');
INSERT INTO "abulsalam" VALUES(22,43,'22:43',' او د ابراهیم قوم او د لوط قوم');
INSERT INTO "abulsalam" VALUES(22,44,'22:44',' او د مدین والاو، او د موسٰى تكذیب شوى دى، نو ما كافرانو ته مهلت وركړ، بیا ما دوى ونیول، نو زما انكار كول څنګه وو؟');
INSERT INTO "abulsalam" VALUES(22,45,'22:45',' نو څومره ډېر كلي دي چې مونږ هغه هلاك كړي دي، په داسې حال كې چې هغه ظالمان وو، نو دغه (كلي) په خپلو چتونو پراته دي، او څومره ډېر بېكاره پراته كوهیان دي او مضبوطې ماڼۍ دي');
INSERT INTO "abulsalam" VALUES(22,46,'22:46',' ایا نو دوى په ځمكه كې ګرځېدلي نه دي، نو چې دوى لپاره داسې زړونه وى چې دوى په هغو سره پوهېدلى یا داسې غوږونه چې دوى په هغو سره اورېدلى، نو بېشكه قصه دا ده چې سترګې نه ړندېږي او لېكن هغه زړونه ړندېږي چې په سینو كې دي');
INSERT INTO "abulsalam" VALUES(22,47,'22:47',' او دوى په تا په (راوستلو د) عذاب سره تلوار كوي او الله به له سره د خپلې وعدې خلاف و نه كړي، او بېشكه یوه ورځ ستا د رب په نیز د زرو كلونو په شان ده له هغو چې تاسو يې شمېرئ');
INSERT INTO "abulsalam" VALUES(22,48,'22:48',' او ډېر كلي دي چې ما هغو ته مهلت وركړى و، حال دا چې دوى ظالمان وو، بیا ما هغه ونیول، او خاص ما ته بېرته راتله دي');
INSERT INTO "abulsalam" VALUES(22,49,'22:49',' ته ووایه: اى خلقو! بېشكه همدا خبره ده چې زه تاسو ته ښكاره وېروونكى یم');
INSERT INTO "abulsalam" VALUES(22,50,'22:50',' نو هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي د دوى لپاره مغفرت او ډېر د عزت روزي ده');
INSERT INTO "abulsalam" VALUES(22,51,'22:51',' او هغه كسان چې زمونږ په ایتونو كې كوشش كوي، په داسې حال كې چې (په خپل خیال كې) عاجزه كوونكي دي، دغه كسان د دوزخ ملګري دي');
INSERT INTO "abulsalam" VALUES(22,52,'22:52',' او مونږ له تا نه مخكې نه دى لېږلى هېڅ يو رسول او نه نبي، مګر كله چې به هغه لوستل كول (،نو) شیطان به د هغه په لوستلو كې غورځول وكړل، نو الله هغه لرې كوي چې شیطان يې غورځوي، بیا الله خپل ایتونه مضبوطوي، او الله ښه عالم، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(22,53,'22:53',' د دې لپاره چې هغه (الله) هغه شى چې شیطان يې غورځوي د هغو كسانو لپاره ازمېښت وګرځوي چې د هغوى په زړونو كې مرض دى او د هغوى لپاره چې زړونه يې سخت دي او بېشكه ظالمان خامخا په ډېر لوى مخالفت كې دی');
INSERT INTO "abulsalam" VALUES(22,54,'22:54',' او د دې لپاره چې هغه كسان پوه شي چې دوى ته علم وركړى شوى دى چې بېشكه دا (قرآن) حق دى ستا د رب له جانبه، پس چې دوى په ده ایمان راوړي، بیا د دوى زړونه ده ته نرم شي او بېشكه الله خامخا هغو كسانو ته چې ایمان يې راوړى دى ؛ د نېغې لارې هدایت كوونكى دى');
INSERT INTO "abulsalam" VALUES(22,55,'22:55',' او هغه كسان چې كافران شوي دي تل به د دغه (قرآن) په هكله به شك كې وي، تر دې چې په دوى قیامت ناڅاپه راشي، یا دوى ته د شنډې ورځې عذاب راشي');
INSERT INTO "abulsalam" VALUES(22,56,'22:56',' باچاهي په دغې ورځ كې خاص د الله لپاره ده، هغه به د دوى په مینځ كې فیصله كوي نو هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي د نعمتونو په جنتونو كې دي');
INSERT INTO "abulsalam" VALUES(22,57,'22:57',' او هغه كسان چې كفر يې كړى دى او زمونږ د ایتونو تكذیب يې كړى دى، نو دغه كسان چې دي، د دوى لپاره رسوا كوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(22,58,'22:58',' او هغه كسان چې د الله په لاره كې يې هجرت كړى دى، بیا هغوى ووژل شي، یا (په خپل مرګ) مړه شي (نو) خامخا هرومرو به الله دوى ته ډېره ښكلې روزي وركړي، او بېشكه الله خامخا همغه تر ټولو غوره رزق وركوونكى دى');
INSERT INTO "abulsalam" VALUES(22,59,'22:59',' خامخا ضرور به هغه دوى داسې ځاى ته داخل كړي چې دوى به هغه خوښوي او بېشكه الله خامخا ښه عالم، ډېر تحمل والا دى');
INSERT INTO "abulsalam" VALUES(22,60,'22:60',' (خبره هم) دا ده، او هغه كس چې بدل يې واخیست په مثل د هغه (ضرر) چې دى په هغه سره مُعذب شوى و، بیا (په دویم ځل) په ده ظلم وكړى شو (نو) خامخا ضرور به الله د ده مدد كوي، بېشكه الله خامخا ډېر معافي كوونكى، ډېر بخښونكى دى');
INSERT INTO "abulsalam" VALUES(22,61,'22:61',' دغه په دې سبب چې بېشكه الله شپه په ورځ كې ننباسي او ورځ په شپه كې ننباسي او دا چې بېشكه الله ښه اورېدونكى، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(22,62,'22:62',' دغه په دې سبب چې بېشكه الله، هم دى حق دى او دا چې بېشكه هغه چې دوى يې له هغه (الله) نه غیر بولي، همغه باطل دى او دا چې بېشكه الله، هم دى ډېر اوچت، ډېر لوى دى');
INSERT INTO "abulsalam" VALUES(22,63,'22:63',' ایا تا نه دي لیدلي چې بېشكه الله له بره نه اوبه نازلې كړې، بیا (په دغو سره) ځمكه تكه شنه ګرځي، بېشكه الله ډېر مهربان (ښه باریك بین)، ډېر خبردار دى');
INSERT INTO "abulsalam" VALUES(22,64,'22:64',' خاص هم دغه (الله) لره دي هر څه چې په اسمانونو كې دي او هر څه چې په ځمكه كې دي او بېشكه الله خامخا هم دى غني ( بې پروا) ډېر ستايل شو ى دى');
INSERT INTO "abulsalam" VALUES(22,65,'22:65',' ایا تا نه دي لیدلي چې بېشكه الله تاسو لپاره تابع كړي دي هر هغه چې په ځمكه كې دي او بېړۍ چې په بحر كې د هغه په امر ګرځي او هغه اسمان ساتي له دې نه چې په ځمكه باندې راولوېږي مګر (خو) د هغه په حكم سره، بېشكه الله په خلقو باندې خامخا ډېر نرمي كوونكى، مهربان دى');
INSERT INTO "abulsalam" VALUES(22,66,'22:66',' او دغه (الله) همغه ذات دى چې تاسو يې ژوندي كړي یئ، بیا به هغه تاسو مړه كاندي، بیا به هغه تاسو ژوندي كړي، بېشكه انسان خامخا ډېر ناشكره دى');
INSERT INTO "abulsalam" VALUES(22,67,'22:67',' د هر یو امت لپاره مونږ د بندګۍ یوه لاره مقرر كړې ده چې دوى په هغې عمل كوونكي دي، نو دوى دې له تا سره په دې كار (د دین) كې له سره جګړه ونه كړي او ته خپل رب ته (د خلقو) بلنه كوه، بېشكه ته خامخا په نېغ (او برابر) هدایت باندې يې');
INSERT INTO "abulsalam" VALUES(22,68,'22:68',' او كه دوى له تاسره جګړه كوي، نو ته وایه: الله پوه دى په هغو عملونو چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(22,69,'22:69',' الله به د دوى په مینځ كې د قیامت په ورځ په هغو خبرو كې فیصله كوي چې دوى به په كې اختلاف كاوه');
INSERT INTO "abulsalam" VALUES(22,70,'22:70',' ایا ته نه يې پوه شوى چې بېشكه الله پوهېږي به هر هغه څه چې په اسمان او ځمكه كې دي، بېشكه دغه په یو كتاب كې دي، بېشكه دغه الله ته ډېر اسان دي');
INSERT INTO "abulsalam" VALUES(22,71,'22:71',' او دوى (كفار) له الله نه غیر د هغه شي عبادت كوي چې الله په (عبادت د) هغه هېڅ دلیل نه دى نازل كړى او د هغه شي چې دوى ته په هغه باندې هېڅ علم نشته، او د ظالمانو لپاره هېڅ مدد كوونكى نشته');
INSERT INTO "abulsalam" VALUES(22,72,'22:72',' او كله چې دوى ته زمونږ ایتونه ولوستل شي، چې ډېر واضح دي، (نو) ته د كافران شویو كسانو په مخونو انكار پېژنې، نژدې وي چې دوى په هغو كسانو حمله وكړي چې دوى ته زمونږ ایتونه تلاوت كوي، ته (دوى ته) ووایه: ایا نو زه تاسو ته له دغه نه د ډېر بد شي خبر دركړم (هغه) اور دى، الله د ده وعده كړې ده له هغو كسانو سره چې كافران شوي دي او هغه د بېرته ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(22,73,'22:73',' اى خلقو! یو مثال بیانولى شي، نو تاسو ده ته ښه غوږ كېږدئ، بېشكه هغه شیان چې تاسو يې له الله نه غیر بلئ دوى له سره یو مچ هم نشي پیدا كولى اګر كه دوى ده ته ټول راجمع شي او كه مچ له دوى نه یو شى وتښتوي، دوى له هغه نه دغه (شى) نشي راخلاصولى، كمزورى دى دا طالب (د غیرالله عبادت كوونكى) او دغه مطلوب (باطل معبود)');
INSERT INTO "abulsalam" VALUES(22,74,'22:74',' دوى د الله قدر ونه كړ، څنګه چې د هغه د قدر كولو حق دى، بېشكه الله خامخا ډېر زورور، ښه غالب دى');
INSERT INTO "abulsalam" VALUES(22,75,'22:75',' الله له ملايكو څخه استازي غوره كوي او له خلقو څخه، بېشكه الله ښه اورېدونكى، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(22,76,'22:76',' هغه پوهېږي په هغه څه چې د دوى په وړاندي دي او په هغه څه چې د دوى وروسته دي، او خاص الله ته ټول كارونه بېرته ورګرځول كېږي');
INSERT INTO "abulsalam" VALUES(22,77,'22:77',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو ركوع كوئ او سجده كوئ او د خپل رب عبادت كوئ او نېكي كوئ، د دې لپاره چې تاسو كامیاب شئ');
INSERT INTO "abulsalam" VALUES(22,78,'22:78',' او په (لاره د) الله كې كوشش كوئ، څنګه چې د هغه د كوشش حق دى، هغه تاسو غوره كړي یئ او په تاسو باندې يې په دین كې هېڅ حرج (مشقت) نه دى ایښى، تاسو د خپل پلار ابراهیم د ملت تابع شئ، دغه (الله) تاسو مسلمانان نومولي يئ، له دې (قرآن) نه مخكې او په ده كې، د دې لپاره چې رسول پر تاسو باندې ګواه شي او تاسو په خلقو باندې ګواهان شئ، نو تاسو لمونځ قايموئ او زكات ادا كوئ او په (دين د) الله باندې منګولې ټېنګې كلكې كړئ، هغه ستاسو مالك دى، نو هغه ښه مالك دى او ښه مدد كوونكى دى');
INSERT INTO "abulsalam" VALUES(23,1,'23:1',' یقینًا مومنان كامیاب شول');
INSERT INTO "abulsalam" VALUES(23,2,'23:2',' هغه چې هغوى په خپلو لمونځونو كې خشوع كوونكي دي');
INSERT INTO "abulsalam" VALUES(23,3,'23:3',' او هغه كسان چې دوى له لغو (خبرو او كارونو) نه مخ ګرځوونكي دي');
INSERT INTO "abulsalam" VALUES(23,4,'23:4',' او هغه چې هغوى د زكات ادا كوونكي دي');
INSERT INTO "abulsalam" VALUES(23,5,'23:5',' او هغه چې هغوى د خپلو فرجونو (د شهوت ځایونو) ساتونكي دي');
INSERT INTO "abulsalam" VALUES(23,6,'23:6',' مګر له خپلو ښځو او وینځو نه، نو بېشكه دوى (هغوى ته په ورتلو سره) ملامت كړى شوي نه دي');
INSERT INTO "abulsalam" VALUES(23,7,'23:7',' پس چا چې ولټولو سوا له دغو نه، نو دغه كسان، دوى له حد نه تېرېدونكي دي');
INSERT INTO "abulsalam" VALUES(23,8,'23:8',' او هغه چې هغوى د خپلو امانتونو او عهد رعایت كوونكي دي');
INSERT INTO "abulsalam" VALUES(23,9,'23:9',' او هغه چې هغوى په خپلو لمونځونو مداومت (او حفاظت) كوي');
INSERT INTO "abulsalam" VALUES(23,10,'23:10',' هم دغه خلق وارثان دي');
INSERT INTO "abulsalam" VALUES(23,11,'23:11',' هغه چې د فردوس وارثان كېږي به، دوى به په هغه كې همېشه وي');
INSERT INTO "abulsalam" VALUES(23,12,'23:12',' او یقینًا یقینًا مونږ انسان د خټې له خُلاصې نه پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(23,13,'23:13',' بیا مونږ هغه، نطفه (د مني یو څاڅكى) وګرځوله په مضبوط قرار ځاى كې');
INSERT INTO "abulsalam" VALUES(23,14,'23:14',' بیا مونږ دغه نطفه د وینې ټوټه وګرځوله، ورپسې مونږ دغه د وینې ټوټه د غوښې وړه بوټۍ وګرځوله، ورپسې مونږ دغه بوټۍ هډوكي وګرځوله، بیا مو هډوكو ته غوښه واغوستله، بیا مونږ هغه په بل تخلیق كې پیدا كړ، نو الله ډېر بركت والا دى چې تر ټولو ښه پیدا كوونكى دى');
INSERT INTO "abulsalam" VALUES(23,15,'23:15',' بیا بېشكه تاسو له دې (ژوند) نه بعد خامخا مړه كېدونكي یئ');
INSERT INTO "abulsalam" VALUES(23,16,'23:16',' بیا به یقینًا تاسو د قیامت په ورځ ژوندي راپاڅولى شئ');
INSERT INTO "abulsalam" VALUES(23,17,'23:17',' او یقینًا یقینًا مونږ ستاسو له پاسه اوه لارې (اسمانونه) پیدا كړي دي او مونږ د مخلوق (له ټولو حالاتو) نه غافله نه یو');
INSERT INTO "abulsalam" VALUES(23,18,'23:18',' او مونږ له بره نه په اندازې سره اوبه نازلې كړې، بیا مو هغه په ځمكه كې وساتلې او بېشكه مونږ د هغې په بېولو یقینًا قادر یو');
INSERT INTO "abulsalam" VALUES(23,19,'23:19',' نو مونږ په دې (اوبو) سره تاسو لپاره د كجورو او انګورو باغونه پیدا كړي دي، چې تاسو ته په هغو (باغونو) كې ډېرې مېوې دي او له هم دغو (باغونو) نه تاسو خوراك كوئ');
INSERT INTO "abulsalam" VALUES(23,20,'23:20',' او هغه ونه (مو پیدا كړه) چې د طور سیناء نه راوځي؟ چې د خوړونكو لپاره د غوړو او انګولي سره زرغونېږي');
INSERT INTO "abulsalam" VALUES(23,21,'23:21',' او بېشكه تاسو لپاره په څارويو كې یقینًا لوى عبرت دى، مونږ په تاسو له هغو (پیو) نه درڅښوو چې د هغو (څارويو) په ګېډو كې دي او تاسو لپاره په دوى كې ډېرې فايدې دي او له دوى نه تاسو خوړل (هم) كوئ');
INSERT INTO "abulsalam" VALUES(23,22,'23:22',' او په دغو (څارويو) باندې او په بېړیو باندې تاسو بارولى شئ');
INSERT INTO "abulsalam" VALUES(23,23,'23:23',' او یقینًا یقینًا مونږ نوح خپل قوم ته لېږلى و، نو هغه (ورته) وویل: اى زما قومه! تاسو د الله عبادت كوئ، تاسو لپاره له هغه نه غیر بل هېڅ حق معبود نشته، ایا نو تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(23,24,'23:24',' نو د هغه له قومه كافر شويو مشرانو وویل: نه دى دا (نوح) مګر هم ستاسو په شان بشر، دى غواړي چې پر تاسو باندې مشري (او برتري) حاصله كړي او كه الله غوښتلى (نو) خامخا ملايك به يې نازل كړې وو، مونږ خو دا خبره (چې رسول بني ادم وي) زمونږ په هغو پلرونو كې نه ده اورېدلې چې پخوا وو');
INSERT INTO "abulsalam" VALUES(23,25,'23:25',' نه دى دا (نوح) مګر داسې سړى چې په ده پورې لېونتوب نښتى دى، نو تاسو ده ته تر یو وخته انتظار كوئ');
INSERT INTO "abulsalam" VALUES(23,26,'23:26',' هغه وویل: اى زما ربه! ته زما مدد وكړه، په سبب د دې چې دوى زما تكذیب وكړ');
INSERT INTO "abulsalam" VALUES(23,27,'23:27',' نو مونږ هغه ته وحي وكړه داسې چې زمونږ د سترګو په وړاندې او زمونږ له وحي سره سم بېړۍ جوړه كړه، نو كله چې زمونږ حكم راغى او تنور راوخوټېده، بیا نو ته په دې (بېړۍ) كې له هر قسمه نه جوړه جوړه دوه دوه (نر او ښځه) داخل كړه او خپله كورنۍ هم (خو) له هغه چا نه غير چې په دوى كې په هغه باندې (د عذاب) خبره ړومبۍ شوې ده او ته له ما سره د هغو كسانو په باره كې خبره مه كوه چې ظلم يې كړى دى، بېشكه دوى به غرق كولى شي');
INSERT INTO "abulsalam" VALUES(23,28,'23:28',' نو كله چې وختلې ته او هغه څوك چې له تا سره دي په بېړۍ كې، نو ووایه: ټول د كمال صفتونه خاص د هغه الله لپاره دي چې مونږ يې له ظالم قوم نه خلاص كړو');
INSERT INTO "abulsalam" VALUES(23,29,'23:29',' او (د كوزېدو په وخت كې) ووایه: اى زما ربه! ته ما په یو مبارك منزل (ځاى) كې كوز كړه او ته تر ټولو كوزوونكو څخه ډېر غوره يې');
INSERT INTO "abulsalam" VALUES(23,30,'23:30',' بېشكه په دې كې یقینًا دلیلونه (نښې) دي او بېشكه مونږ خامخا ازمېښت كوونكي یو');
INSERT INTO "abulsalam" VALUES(23,31,'23:31',' بیا مونږه له دوى نه وروسته نور خلق پیدا كړل');
INSERT INTO "abulsalam" VALUES(23,32,'23:32',' نو مونږ په هغو كې له همدوى ځنې یو رسول ولېږه چې تاسو د الله عبادت كوئ، تاسو لپاره له هغه نه غیر بل هېڅ حق معبود نشته، ایا نو تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(23,33,'23:33',' او وویل د ده له قومه هغو مشرانو چې كافر شوي وو او د اخرت د ملاقات يې تكذیب كړى و او مونږ هغوى ته په دنيايي ژوند كې خوشحالي وركړې وه: نه دى دا مګر ستاسو په شان بشر، له هغه څه نه دا خوراك كوي له څه نه چې تاسو خوراك كوئ او له هغه څه نه څښاك كوي چې تاسو ترې څښاك كوئ');
INSERT INTO "abulsalam" VALUES(23,34,'23:34',' او (قسم دى) خامخا كه تاسو د خپل ځان په شان انسان اطاعت وكړئ، (نو) بېشكه چې تاسو به په دغه وخت كې یقینًا تاوانیان یئ');
INSERT INTO "abulsalam" VALUES(23,35,'23:35',' ایا دا تاسو ته وعده دركوي دا چې بېشكه كله چې تاسو مړه شئ او تاسو خاورې او هډوكي شئ (،نو) یقینًا به تاسو ژوندي راوښكل شئ؟');
INSERT INTO "abulsalam" VALUES(23,36,'23:36',' (له عقله) ډېر لرې دى، ډېر لرې دى هغه څیز چې له تاسو سره يې وعده كولى شي');
INSERT INTO "abulsalam" VALUES(23,37,'23:37',' نه دى دا (ژوندون) مګر همدا زمونږ دنيايي ژوندون، چې مرو او ژوندي كېږو او له سره به مونږ بیا راژوندي نه كړى شو');
INSERT INTO "abulsalam" VALUES(23,38,'23:38',' دى نه دى مګر (یو داسې) سړى چې په الله باندې يې دروغ ورتپلي دي او مونږ په ده باندې بېخي ایمان راوړونكي نه يو');
INSERT INTO "abulsalam" VALUES(23,39,'23:39',' هغه (نبي) وویل: اى زما ربه! ته زما مدد وكړه، په سبب د دې چې دوى زه دروغجن وګڼلم');
INSERT INTO "abulsalam" VALUES(23,40,'23:40',' (الله) وویل: له ډېر لږ وخت نه پس به دوى خامخا لازمًا پښېمانه شي');
INSERT INTO "abulsalam" VALUES(23,41,'23:41',' نو دوى یوې چغې په حقه سره ونیول، بیا نو مونږه دوى د سیلاب خس وخشاك كړل، نو د ظالم قوم لپاره (د الله له رحمت نه) لرې والى دى');
INSERT INTO "abulsalam" VALUES(23,42,'23:42',' بیا مونږ له دوى نه بعد نور قومونه (خلق) پیدا كړل');
INSERT INTO "abulsalam" VALUES(23,43,'23:43',' هېڅ یو امت د خپلې مقررې نېټې نه نه ړومبى كېدى شي او نه وروسته كېدى شي');
INSERT INTO "abulsalam" VALUES(23,44,'23:44',' بیا مونږ خپل (نور) رسولان ولېږل، په دې حال كې چې یو په بل پسې پیوست وو، هر كله چې به كوم امت ته خپل رسول راغى (نو) هغوى به د هغه تكذیب وكړ، نو مونږ به د دوى ځینې د ځینو نورو پسې (په هلاكت كې) پرله پسې كړل او مونږ دوى تشې قصې وګرځول، نو (د الله له رحمت نه) لرې والى دى د هغه قوم لپاره چې ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(23,45,'23:45',' بیا مونږ موسٰى او د هغه ورور هارون ولېږل په خپلو ایتونو (معجزو) او ښكاره دلیل سره');
INSERT INTO "abulsalam" VALUES(23,46,'23:46',' فرعون او د هغه ډلې ته، نو هغوى ډېره لويي وكړه او هغوى یو متكبر قوم وو');
INSERT INTO "abulsalam" VALUES(23,47,'23:47',' نو هغوى ویل: ایا مونږ به د خپل ځان په شان په دوه انسانانو باندې ایمان راوړو؟ حال دا چې د دې دواړو قوم زمونږ غلامان دي');
INSERT INTO "abulsalam" VALUES(23,48,'23:48',' نو هغوى د دې دواړو تكذیب وكړ، نو شول له هلاك كړى شويو نه');
INSERT INTO "abulsalam" VALUES(23,49,'23:49',' او یقینًا یقینًا مونږ موسٰى ته كتاب وركړى و، د دې لپاره چې دوى (بني اسرائیل) نېغه لار ومومي ');
INSERT INTO "abulsalam" VALUES(23,50,'23:50',' او مونږ د مریمې زوى او د هغه مور (د خپل قدرت) لویه نښه ګرځولې وه او مونږه دوى دواړه داسې هسكې (او نرمې څربې) ځمكې ته راوستل چې د قرار او پاكو صافو بهېدونكو اوبو والا وه');
INSERT INTO "abulsalam" VALUES(23,51,'23:51',' اى رسولانو! تاسو له پاكیزه څیزونو ځنې خورئ او نېك عملونه كوئ، بېشكه زه په هغو كارونو چې تاسو يې كوئ، ښه عالم یم');
INSERT INTO "abulsalam" VALUES(23,52,'23:52',' او بېشكه دا ستاسو دین دى، په داسې حال كې چې یو دین دى او زه ستاسو رب یم، نو تاسو له ما نه وېرېږئ');
INSERT INTO "abulsalam" VALUES(23,53,'23:53',' نو دوى (امتیانو) خپل (د دین) كار په خپل مينځ كې ټوټې ټوټې پرې كړ، هر یو حزب (ډله) په هغه (باطل دین) باندې چې له دوى سره دى، ډېر خوشحال دي');
INSERT INTO "abulsalam" VALUES(23,54,'23:54',' نو ته دوى تر یو وخته پورې په خپلې ګمراهۍ كې پرېږده');
INSERT INTO "abulsalam" VALUES(23,55,'23:55',' ایا دوى ګمان كوي چې بېشكه له مال او زامنو ځنې چې په څه سره مونږ د دوى مدد كوو');
INSERT INTO "abulsalam" VALUES(23,56,'23:56',' چې مونږ دوى ته په ښېګڼو (او اكرامونو) كې تلوار كوو؟ بلكې دوى نه پوهېږي');
INSERT INTO "abulsalam" VALUES(23,57,'23:57',' بېشكه هغه كسان چې دوى د خپل رب (د عذاب) له ډاره وېرېدونكي دي');
INSERT INTO "abulsalam" VALUES(23,58,'23:58',' او هغه كسان چې دوى د خپل رب په ایتونو ایمان راوړي');
INSERT INTO "abulsalam" VALUES(23,59,'23:59',' او هغه كسان چې دوى له خپل رب سره شرك نه كوي');
INSERT INTO "abulsalam" VALUES(23,60,'23:60',' او هغه كسان چې وركوي، هغه څه چې وركوي، او زړونه د دوى وېرېدونكي وي (ځكه چې) یقینًا دوى خپل رب ته واپس ورتلونكي دي');
INSERT INTO "abulsalam" VALUES(23,61,'23:61',' دغه كسان په نېكیو كې تلوار كوي او دوى دغو (نېكیو) ته ړومبى كېدونكي دي');
INSERT INTO "abulsalam" VALUES(23,62,'23:62',' مونږ هېڅ یو نفس نه مكلف كوو مګر د هغه له طاقت سره سم او زمونږ سره كتاب دى چې حق بیانوي او په دوى به هېڅ ظلم ونه كړى شي');
INSERT INTO "abulsalam" VALUES(23,63,'23:63',' بلكې د دوى (كفارو) زړونه له دې (قرآن) نه په غفلت كې دي او د دوى لپاره له دې (شرك) نه علاوه نور (ناكاره) عملونه دي، چې دوى هغه كوونكي دي');
INSERT INTO "abulsalam" VALUES(23,64,'23:64',' تر هغه پورې چې كله مونږ د دوى متكبره مالداران په عذاب سره ونیسو (،نو) ناڅاپه دوى (بیا) فریاد كوي');
INSERT INTO "abulsalam" VALUES(23,65,'23:65',' نن ورځ فریاد مه كوئ، بېشكه تاسو سره به زمونږ له جانبه مدد ونه كړى شي');
INSERT INTO "abulsalam" VALUES(23,66,'23:66',' یقینًا زما ایتونه به په تاسو تلاوت كولى شول، نو تاسو به په خپلو پوندو بېرته ګرځېدئ');
INSERT INTO "abulsalam" VALUES(23,67,'23:67',' په دې حال كې چې كبر كوونكي به وئ په دې (قرآن، محمد یا كعبې) سره، چې د شپې قصې كوونكي به وئ، تاسو به (قرآن) پرېښوده');
INSERT INTO "abulsalam" VALUES(23,68,'23:68',' ایا نو دوى په دې وینا كې (چې قرآن دى) فكر نه كوي، یا دوى ته هغه څه راغلي دي چې د دوى پلرونو ته نه دي راغلي چې له دوى نه پخوا وو');
INSERT INTO "abulsalam" VALUES(23,69,'23:69',' یا كه دوى خپل رسول نه دى پېژندلى، پس دوى له ده نه منكران دي');
INSERT INTO "abulsalam" VALUES(23,70,'23:70',' ایا دوى وايي چې په ده لېونتوب دى، (داسې نه ده) بلكې هغه دوى ته حق (دین) راوړى دى او د دوى زیاتره حق لره بدګڼونكي دي');
INSERT INTO "abulsalam" VALUES(23,71,'23:71',' او كه حق (الله) د دوى د خواهشاتو پیروي كړې وى (، نو) خامخا به تباه شوي وو اسمانونه او ځمكه او هغه څوك چې په دغو كې دي، بلكې مونږ دوى ته د دوى ذكر راوړ، نو دوى له خپل ذكر نه مخ اړوونكي دي');
INSERT INTO "abulsalam" VALUES(23,72,'23:72',' ایا ته له دوى نه مزدوري غواړې؟ نو د خپل رب مزدوري ډېره غوره ده او هغه ترټولو غوره رزق وركوونكى دى');
INSERT INTO "abulsalam" VALUES(23,73,'23:73',' او بېشكه ته یقینًا دوى نېغې لارې ته بلې');
INSERT INTO "abulsalam" VALUES(23,74,'23:74',' او بېشكه هغه كسان چې په اخرت ایمان نه راوړي له سمې لارې نه خامخا كاږه دي');
INSERT INTO "abulsalam" VALUES(23,75,'23:75',' او كه مونږ په دوى مهرباني كړې وى او هغه تكلیف مو لرې كړى وى چې په دوى دى (، نو) خامخا به دوى په خپله سركشۍ كې راټینګ شوي وو، په دې حال كې چې سرګردان به وو');
INSERT INTO "abulsalam" VALUES(23,76,'23:76',' او یقینًا یقینًا مونږ دوى په عذاب سره ونیول، نو (بیا هم) دوى نه خپل رب ته غاړه كېښوده او نه يې تضرع او زاري وكړه');
INSERT INTO "abulsalam" VALUES(23,77,'23:77',' تر دې چې كله مونږ په دوى باندې د ډېر سخت عذاب والا دروازه پرانستله ناڅاپه دوى په هغه كې نا امېده وو');
INSERT INTO "abulsalam" VALUES(23,78,'23:78',' او دغه (الله) هماغه ذات دى چې تاسو لپاره يې غوږونه او سترګې او زړونه پیدا كړل، تاسو ډېر لږ شكر وباسئ');
INSERT INTO "abulsalam" VALUES(23,79,'23:79',' او دى هماغه ذات دى چې تاسو يې په ځمكه كې خواره كړي یئ او خاص د هغه په طرف به تاسو ورجمع كولى شئ');
INSERT INTO "abulsalam" VALUES(23,80,'23:80',' او د غه (الله) هغه ذات دى چې ژوندي كول كوي او مړه كول كوي او خاص د همده په اختیار كې دى د شپې او ورځې اختلاف (بدلېدل رابدلېدل) ایا نو تاسو له عقل نه كار نه اخلئ!');
INSERT INTO "abulsalam" VALUES(23,81,'23:81',' بلكې وايي دوى په شان د هغې وینا چې پخوانیو (كافرو) ویلې وه');
INSERT INTO "abulsalam" VALUES(23,82,'23:82',' هغوى ویل: ایا كله چې مونږ مړه شو او خاورې او هډوكې شو، ایا مونږ به په رښتیا یقینًا بیا راژوندي كولى شو ');
INSERT INTO "abulsalam" VALUES(23,83,'23:83',' (بلكې نه كېږو ځكه چې) یقینًا یقینًا مونږ ته او زمونږ پلرونو ته دا وعده له دې نه مخكې (هم) راكړى شوې وه، نه دي دا خبرې مګر د ړومبنو خلقو باطلې قصې دي');
INSERT INTO "abulsalam" VALUES(23,84,'23:84',' ته (دوى ته) ووایه: دا ځمكه او څوك چې په دې كې دي، د چا دي؟ كه چېرې تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(23,85,'23:85',' دوى به یقینًا ووايي: خاص د الله لپاره دي، (نو ورته) ووایه: ایا نو تاسو نصیحت نه اخلئ!');
INSERT INTO "abulsalam" VALUES(23,86,'23:86',' ته (دوى ته) ووایه: د اوو اسمانونو او د ډېر لوى عرش رب څوك دى؟');
INSERT INTO "abulsalam" VALUES(23,87,'23:87',' دوى به ضرور ووايي: د الله لپاره دي، ته (ورته) ووایه: ایا نو تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(23,88,'23:88',' ته (دوى ته) ووایه: څوك دى هغه ذات چې د هغه په لاس كې د هر شي بادشاهي ده او هغه پناه وركوي او د هغه په خلاف پناه نشي وركول كېدى كه چېرې تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(23,89,'23:89',' خامخا به دوى ووايي: خاص د الله لپاره دي. ته (ورته) ووایه: نو څنګه په تاسو جادو كولى شي؟');
INSERT INTO "abulsalam" VALUES(23,90,'23:90',' بلكې مونږ دوى ته حق راوړى دى او بېشكه دوى خامخا دروغجنان دي');
INSERT INTO "abulsalam" VALUES(23,91,'23:91',' الله (د ځان لپاره) هېڅ اولاد نیولى نه دى او نه له هغه سره بل هېڅ معبود شته (او كه ورسره وى نو) په دغه وخت كې به خامخا هر اله بېولي وو هغه څه چې پیدا كړي يې وو، او خامخا د دوى ځینو به په ځینو نورو باندې غلبه كوله، پاك دى الله له هغو خبرو نه چې دوى يې بیانوي');
INSERT INTO "abulsalam" VALUES(23,92,'23:92',' چې په غیبو او ښكاره و عالم دى، نو ډېر اوچت دى هغه له هغو څیزونو نه چې دوى يې ورسره شریكوي');
INSERT INTO "abulsalam" VALUES(23,93,'23:93',' (اى نبي!) ته ووایه: اى زما ربه! كه چېرې خامخا ته ما ته هغه (عذاب) وښیې چې له دوى سره يې وعده كولى شي');
INSERT INTO "abulsalam" VALUES(23,94,'23:94',' اى زما ربه! نو ته ما په ظالم قوم كې مه شاملوه');
INSERT INTO "abulsalam" VALUES(23,95,'23:95',' او بېشكه مونږ خامخا قادر یو په دې چې تا ته هغه (عذاب) وښیو چې دوى ته يې مونږ وعده وركوو');
INSERT INTO "abulsalam" VALUES(23,96,'23:96',' (اى نبي!) ته بدي دفع (لرې) كوه په هغې طریقې سره چې تر ټولو ښه وي، مونږ ښه پوه یو په هغو خبرو چې دوى يې بیانوي');
INSERT INTO "abulsalam" VALUES(23,97,'23:97',' او ته ووایه: اى زما ربه! زه په تا پورې پناه نیسم د شیطانانو له وسوسو نه');
INSERT INTO "abulsalam" VALUES(23,98,'23:98',' او زه په تا پورې پناه نیسم اى زما ربه! له دې نه چې دوى ما ته حاضر شي');
INSERT INTO "abulsalam" VALUES(23,99,'23:99',' تر هغه پورې چې په دوى كې یو كس ته مرګ راشي (، نو بیا) به وايي: اى زما ربه! ته ما بېرته وګرځوه');
INSERT INTO "abulsalam" VALUES(23,100,'23:100',' د دې لپاره چې زه نېك عمل وكړم په هغه څیز كې چې ما (هلته په دنيا كې) پریښى دى، هېڅ كله هم نشي كېدى، بېشكه دا (بېرته تلل) یوه خبره ده چې دا د هغې ویونكى دى او د دوى په مخ كې برزخ دى تر هغې ورځې پورې چې دوى به بیا ژوندي راپورته كولى شي');
INSERT INTO "abulsalam" VALUES(23,101,'23:101',' نوكله چې په شپېلۍ كې پوكى وكړى شي، نو نه به وي د دوى په مینځ كې هېڅ (فايده مند) نسبونه په دغې ورځ كې او نه به دوى له یو بل نه پوښتنه كوي');
INSERT INTO "abulsalam" VALUES(23,102,'23:102',' نو هغه څوك چې، د هغه تلې درنې شوې، نو همدغه خلق كامیاب دي');
INSERT INTO "abulsalam" VALUES(23,103,'23:103',' او هغه څوك چې، د هغه تلې سپكې شوې، نو دغه هغه كسان دي چې خپلو ځانونو ته يې تاوان رسولى دى، په دوزخ كې به تل پراته وي');
INSERT INTO "abulsalam" VALUES(23,104,'23:104',' اور به د دوى مخونه سوځوي او دوى به په دغه (جهنم) كې بدمخي غونج تندي وي');
INSERT INTO "abulsalam" VALUES(23,105,'23:105',' ایا زما ایتونه به تاسو ته نه لوستل كېدل، نو تاسو به د هغو تكذیب كاوه');
INSERT INTO "abulsalam" VALUES(23,106,'23:106',' دوى به ووايي: اى زمونږه ربه! پر مونږ باندې زمونږ بدبختي غالبه شوې وه او مونږ ګمراه قوم وو');
INSERT INTO "abulsalam" VALUES(23,107,'23:107',' اى زمونږ ربه! مونږ له دې (جهنم) نه وباسه، نو كه مونږ بیا (كفر ته) راوګرځېدو، نو بیا یقینًا مونږ ظالمان یو');
INSERT INTO "abulsalam" VALUES(23,108,'23:108',' وبه فرمايي: ذلیله خوار پراته اوسئ په ده كې او زما سره خبرې مه كوئ');
INSERT INTO "abulsalam" VALUES(23,109,'23:109',' بېشكه شان دا دى چې زما په بنده ګانو كې یوه ډله وه چې ویل به يې: اى زمونږ ربه! مونږ ایمان راوړى دى، نو ته مونږ ته بخښنه وكړه او پر مونږ رحم وكړه او ته تر ټولو ډېر غوره رحم كوونكى يې');
INSERT INTO "abulsalam" VALUES(23,110,'23:110',' نو تاسو به هغوى مسخره كړى شوي نیول، تر دې چې دوى له تاسو نه زما یاد (هم) هېر كړ او تاسو به پر هغوى پورې خندل');
INSERT INTO "abulsalam" VALUES(23,111,'23:111',' بېشكه زه چې یم، ما نن ورځ هغوى ته بدله وركړه، په سبب د هغه چې هغوى صبر كړى و، بېشكه هم هغوى كامیاب دي');
INSERT INTO "abulsalam" VALUES(23,112,'23:112',' وبه وايي: تاسو په ځمكه كې د كلونو په شمېر څومره وخت تېر كړى دى؟');
INSERT INTO "abulsalam" VALUES(23,113,'23:113',' هغوى به ووايي: مونږ وخت تېر كړى دى یوه ورځ، یا د ورځې څه حصه، نو له حسابګرو نه تپوس وكړه');
INSERT INTO "abulsalam" VALUES(23,114,'23:114',' وبه فرمايي: تاسو وخت نه دى تېر كړى مګر (د اخرت د عذاب په مقابله كې) ډېر لږ، كه چېرې په رښتیا تاسو یئ چې پوهېږئ');
INSERT INTO "abulsalam" VALUES(23,115,'23:115',' ایا نو تاسو ګمان كوئ چې بېشكه مونږ تاسو بس عبث پیدا كړي یئ او دا چې بېشكه تاسو به مونږ ته نه راوستل كېږئ');
INSERT INTO "abulsalam" VALUES(23,116,'23:116',' نو ډېر اوچت دى الله چې حق بادشاه دى، نشته دى هېڅ حق معبود مګر هغه دى، د عزتناك عرش رب دى');
INSERT INTO "abulsalam" VALUES(23,117,'23:117',' او هغه څوك چې له الله سره بل داسې اله بلي چې له هغه سره په دې باندې هېڅ دلیل نشته، نو بېشكه همدا خبره ده چې د ده حساب د ده د رب په نیز دى، بېشكه شان دا دى چې كافران برى نه مومي');
INSERT INTO "abulsalam" VALUES(23,118,'23:118',' او ته ووایه: اى زما ربه! بخښنه وكړه او رحم وكړه او ته ترټولو ډېر غوره رحم كوونكى يې');
INSERT INTO "abulsalam" VALUES(24,1,'24:1',' (دا) یو سورت دى چې مونږ دا نازل كړى دى او مونږ دا فرض كړى دى او مونږ په ده كې ښكاره ایتونه نازل كړي دي، د دې لپاره چې تاسو نصیحت واخلئ');
INSERT INTO "abulsalam" VALUES(24,2,'24:2',' هغه ښځه چې زنا يې كړي وي او هغه سړى چې زنا يې كړي وي، نو تاسو (اى قاضیانو او حاكمانو) په دغو دواړو كې هر یو سل دُرې ووهئ او تاسو دې د الله په دین كې (په حكم نافذولو كې) په دغو باندې زړه سوى ونه نیسي، كه یئ تاسو چې په الله او په ورځ د اخرت ایمان لرئ او د دې دواړو سزا ته دې د مومنانو یوه ډله حاضره وي');
INSERT INTO "abulsalam" VALUES(24,3,'24:3',' زنا كار سړى نكاح نه كوي مګر له زناكارې، یا مشركې ښځې سره او زناكاره ښځه، له هغې سره نكاح نه كوي مګر زنا كار، یا مشرك سړى او په مومنانو باندې دغه (زنا او د زنا كارو سره نكاح) حرام كړى شوي دي ');
INSERT INTO "abulsalam" VALUES(24,4,'24:4',' او هغه كسان چې په پاكدامنو ښځو تهمت لګوي، بیا څلور ګواهان رانه ولي، نو تاسو دوى اتیا دُرې ووهئ او هېڅكله هم د دوى ګواهي مه قبلوئ او همدغه كسان فاسقان دي');
INSERT INTO "abulsalam" VALUES(24,5,'24:5',' مګر هغه كسان چې له دې نه بعد توبه وباسي او (د ځان) اصلاح وكړي، نو بېشكه الله ډېر بخښونكى، بې حده رحم كوونكى دى ');
INSERT INTO "abulsalam" VALUES(24,6,'24:6',' او هغه كسان چې په خپلو ښځو تهمت ولګوي او له خپلو ځانونو نه غير نور د دوى لپاره څوك ګواهان نه وي، نو د دوى د یو ګواهي څلور ځلې د الله په نامه شاهدي كول دي چې بېشكه دا خامخا له رښتينو څخه دى');
INSERT INTO "abulsalam" VALUES(24,7,'24:7',' او پنځمه (ګواهي) دا چې بېشكه په ده دې د الله لعنت وي كه دا له دروغجنو ځنې وي');
INSERT INTO "abulsalam" VALUES(24,8,'24:8',' او له هغې نه سزا دفع كوي دا چې هغه (ښځه) څلور ځلې د الله په نامه شاهدي وركړي چې بېشكه دغه (سړى) خامخا له دروغجنو ځنې دى');
INSERT INTO "abulsalam" VALUES(24,9,'24:9',' او پنځمه (ګواهي) دا چې بېشكه د الله غضب دې په دې وي كه دا (سړى په دې تهمت لګولو كې) له رښتیا ویونكو ځنې وي');
INSERT INTO "abulsalam" VALUES(24,10,'24:10',' او كه پر تاسو باندې د الله فضل او د هغه رحمت نه وى او دا چې بېشكه الله ډېر توبه قبلوونكى، ښه حكمت والا دى (نو تاسو به په دنیا كې معذب شوي وى)');
INSERT INTO "abulsalam" VALUES(24,11,'24:11',' بېشكه هغه كسان چې غټ دروغ يې راوړي دي، له هم تاسو نه یوه ډله ده، په دې (دروغو) د ځان لپاره د شر ګمان مه كوئ بلكې دا تاسو لپاره خیر دى، په دغو (بهتان ویونكو) كې د هر یو سړي لپاره هغه شى دى چې هغه ګټلى دى له ګناه نه او هغه كس چې د دغو (دروغو) لوى بار يې پورته كړى دى په دوى كې د ده لپاره ډېر لوى عذاب دى');
INSERT INTO "abulsalam" VALUES(24,12,'24:12',' كله چې تاسو دا (دروغ) واورېدل، نو ولې مومنو سړیو او مومنو ښځو په خپلو ځانونو باندې د خیر ګمان ونه كړ او دا يې ونه ویل چې دا ښكاره دروغ دي');
INSERT INTO "abulsalam" VALUES(24,13,'24:13',' ولې يې په دې (دروغو) باندې څلور ګواهان رانه وستل، نو كله چې يې څلور ګواهان رانه وستل، نو همدغه كسان د الله په نیز دروغجن دي');
INSERT INTO "abulsalam" VALUES(24,14,'24:14',' او كه چېرې پر تاسو باندې د الله فضل او د هغه رحمت نه وى په دنیا او اخرت كې (، نو) خامخا به تاسو ته د هغو (دروغو) په سبب چې تاسو په هغه كې غوپه شوي وئ ؛ ډېر لوى عذاب دررسېدلى و');
INSERT INTO "abulsalam" VALUES(24,15,'24:15',' كله چې تاسو دا (بهتان) په خپلو ژبو سره اخيسته او تاسو به په خپلو خولو سره هغه خبرې كولې چې تاسو ته د هغې هېڅ علم نه و او تاسو به دا خبره اسانه ګڼله، حال دا چې هغه د الله په نیز ډېره لویه وه');
INSERT INTO "abulsalam" VALUES(24,16,'24:16',' او كله چې تاسو دا (بهتان) واورېده (، نو) ولې تاسو ونه ویلې: مونږ ته جايز نه دي چې په دې خبره باندې خوله وخوځوو، تا لره پاكي ده (اى ربه!) دا ډېر لوى بهتان دى');
INSERT INTO "abulsalam" VALUES(24,17,'24:17',' تاسو ته الله نصیحت كوي چې تاسو د دې په مثل كار ته هيڅكله هم ونه ګرځئ كه تاسو مومنان یئ');
INSERT INTO "abulsalam" VALUES(24,18,'24:18',' او الله تاسو ته خپل ایتونه بیانوي او الله ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(24,19,'24:19',' بېشكه هغه كسان چې خوښوي چې په مومنانو كې فاحشه (بدنامي) خوره شي، د دوى لپاره په دنیا او اخرت كې ډېر دردوونكى عذاب دى، او الله پوهېږي او تاسو نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(24,20,'24:20',' او كه پر تاسو باندې د الله فضل او د هغه رحمت نه وى او دا چې بېشكه الله ډېر نرمي كوونكى، بې حده رحم كوونكى دى (نو په دنيا كې به ډېر رسوا شوي وى) ');
INSERT INTO "abulsalam" VALUES(24,21,'24:21',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د شیطان د قدمونو پیروي مه كوئ او چا چې د شیطان د قدمونو پیروي وكړه، نو بېشكه دا (شیطان) د بې حیایۍ او ناروا كارونو امر كوي او كه پر تاسو باندې د الله فضل او د هغه رحمت نه وى (نو) په تاسو كې هیڅوك هیڅكله هم ( د بهتان له ګناه نه) پاك شوى نه وى او لېكن الله پاكوي چا لره چې وغواړي او الله ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(24,22,'24:22',' او په تاسو كې دې د فضیلت او فراخۍ خاوندان له دې قسم نه خوري چې نه به وركوي (نفقه) خپلوانو او مسكینانو او د الله په لار كې هجرت كوونكو ته او دوى دې معافي وكړي او تېر دې شي، ایا تاسو دا نه خوښوئ چې الله تاسو ته بخښنه وكړي او الله ښه بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(24,23,'24:23',' بېشكه هغه كسان چې په پاك لمنو، ناخبره مومنو ښځو باندې تهمت لګوي په دوى باندې په دنیا او اخرت كې لعنت كړى شوى دى او د دوى لپاره لوى عذاب دى');
INSERT INTO "abulsalam" VALUES(24,24,'24:24',' په هغې ورځ چې په دوى باندې به خپلې ژبې او خپل لاسونه او خپلې پښې ګواهي كوي (د دوى) په هغو عملونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(24,25,'24:25',' په دغې ورځ كې به الله دوى ته د دوى حقه (لايقه) بدله پوره وركړي او دوى به پوه شي چې بېشكه الله، هم هغه ښكاره ( او ثابت) حق دى');
INSERT INTO "abulsalam" VALUES(24,26,'24:26',' ناپاكې ښځې د ناپاكو سړیو لپاره دي او ناپاك سړي د ناپاكو ښځو لپاره دي او پاكې ښځې د پاكو سړیو لپاره دي او پاك سړي د پاكو ښځو لپاره دي، دغه (پاكان خلق) پاك او بري دي له هغو خبرو نه چې دوى (دروغجن) يې وايي او د دغو (پاكو خلقو) لپاره مغفرت او عزتناكه روزي ده');
INSERT INTO "abulsalam" VALUES(24,27,'24:27',' اى هغو كسانو چې ایمان يې راوړى دى تاسو له خپلو كورونو نه غیر نورو كورونو ته مه داخلېږئ، تر دې چې تاسو اجازه وغواړئ او د هغو په اوسېدونكو سلام واچوئ، دا تاسو لپاره ډېر غوره دي، د دې لپاره چې تاسو نصیحت ومنئ');
INSERT INTO "abulsalam" VALUES(24,28,'24:28',' نو كه تاسو په دې (كورونو) كې څوك ونه مومئ، نو تاسو هغو ته مه ننوځئ، تر دې چې تاسو ته اجازت دركړى شي او كه تاسو ته وویل شي چې بېرته وګرځئ، نو تاسو بېرته وګرځئ، دا تاسو لپاره ډېر پاكیزه دي او الله په هغو كارونو چې تاسو يې كوئ ؛ ښه عالم دى');
INSERT INTO "abulsalam" VALUES(24,29,'24:29',' پر تاسو باندې هېڅ ګناه نشته چې تاسو داسې كورونو ته داخل شئ چې د اوسېدنې نه دي (او) په هغو كې ستاسو څه سامان (منفعت) وي او الله پوهېږي په هغه څه چې تاسو يې څرګندوئ او هغه چې تاسو يې پټوئ');
INSERT INTO "abulsalam" VALUES(24,30,'24:30',' ته مومنانو ته ووایه چې خپلې سترګې دې (له نامحرمو نه) ټيټې ساتي او د خپلو فرجونو (شرم ځایونو) ساتنه دې كوي، دا د دوى لپاره ډېر پاكیزه دي، بېشكه الله ښه خبردار دى په هغو كارونو چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(24,31,'24:31',' او ته مومنو ښځو ته ووایه چې خپلې سترګې دې ټیټې ساتي او د خپلو شرم ځایونو حفاظت دې كوي او خپل زینت دې نه ښكاره كوي مګر هغه چې له دې (زینت) نه (عادتًا) ظاهرېږي، او په خپلو ګرېوانونو دې پړوني واچوي او دوى دې خپل زينت نه څرګندوي مګر د خپلو خاوندانو لپاره یا د خپلو پلرونو یا د خپلو خاوندانو د پلرونو لپاره یا د خپلو زامنو لپاره يا د خپل خاوند د زامنو لپاره يا د خپلو وروڼو لپاره يا د خپلو ورېرونو لپاره يا د خپلو خورينو لپاره يا د خپلو ښځو لپاره يا هغه چا ته چې د دوى ښي لاسونه يې مالكان شوي دي، یا هغو تابعانو ته چې ښځو ته له حاجت والاوو نه وي له سړیو نه، یا هغو وړو هلكانو (ماشومانو) ته چې د ښځو په اسرارو او پټو خبرو واقف شوي نه وي او دا (ښځې) دې خپلې پښې نه وهي، د دې لپاره چې معلوم كړى شي هغه څه چې دوى يې له خپل زینت ځنې پټوي او تاسو ټول الله ته توبه وباسئ اى مومنانو! د دې لپاره چې تاسو كامیاب شئ');
INSERT INTO "abulsalam" VALUES(24,32,'24:32',' او په تاسو كې د بې واده خلقو (ښځې وي او كه سړي) نكاح كوئ، او په خپلو مریانو او وینځو كې د نېكانو صالحو (هم نكاح كوئ) كه چېرې (دغه ذكر شوي خلق) فقیران وي (نو) الله به دوى له خپله فضله غنیان كړي او الله د فراخۍ والا، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(24,33,'24:33',' او هغه كسان دې خپل ځان (او عفت) وساتي چې (اسباب د) نكاح نه مومي تر هغه پورې چې الله دوى له خپله فضله غنیان كړي او ستاسو په مریانو وینځو كې چې څوك مكاتب والى غواړي، نو تاسو له هغوى سره مكاتبت وكړئ كه تاسو په دوى كې څه خیر معلوم كړئ او تاسو دوى ته د الله له هغه مال نه وركړئ چې تاسو ته يې دركړى دى او تاسو خپلې وینځې په زنا مه مجبوروئ، كه دوى د ځان پاك ساتلو اراده لري، د دې لپاره چې تاسو د دنيايي ژوند سامان (او مال) طلب كړئ او څوك چې دوى مجبورې كړي، نو بېشكه الله(چې دى) په دوى باندې د (مالكانو له) زور نه پس ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(24,34,'24:34',' او یقینًا یقینًا مونږ تاسو ته څرګندوونكي ایتونه نازل كړي دي او حال د هغو كسانو چې له تاسو نه مخكې تېر شوي دي او نصیحت لپاره د متقیانو');
INSERT INTO "abulsalam" VALUES(24,35,'24:35',' الله د اسمانونو او ځمكې نور دى، د هغه د نور صفت داسې دى لكه یوه طاقچه ده چې په هغې كې بله ډیوه ده، بله ډیوه په شیشې (قندیل) كې ده، دغه قندیل (داسې دى) ګویاكې هغه روڼ ځلېدونكى ستورى دى، د زیتون له مباركې ونې نه بلولى شي چې (دغه ونه) نه مشرق طرف ته وي او نه مغرب طرف ته، نژدې ده چې د هغې تېل (پخپله) رڼا وكړي اګر چې دغو ته اور هم نه وي رسېدلى، رڼا له پاسه رڼا، الله د خپل نور په طرف هغه چا ته لار ښیي چې يې وغواړي او الله د خلقو لپاره مثالونه بیانوي، او الله په هر شي باندې ښه پوه دى');
INSERT INTO "abulsalam" VALUES(24,36,'24:36',' په هغو كورونو (جوماتونو) كې چې الله حكم كړى دى چې پورته دې كړل شي او په دغو كې دې د ده نوم یادوه شي او په دغو (جوماتونو) كې د هغه پاكي بیانوي، د ورځې په شروع كې او د ورځې په اخر كې');
INSERT INTO "abulsalam" VALUES(24,37,'24:37',' (داسې) سړي چې د الله له ذكر او لمونځ قايمولو او د زكات اداكولو نه يې نه تجارت غافله كوي او نه اخيستل خرڅول، دوى له هغې ورځې نه وېرېږي چې په هغې كې به زړونه او سترګې اوړي راوړي');
INSERT INTO "abulsalam" VALUES(24,38,'24:38',' د دې لپاره چې الله دوى ته بدله وركړي ډېره ښكلې بدله د هغو عملونو چې دوى كړي دي او دوى ته له خپل فضله اضافه وكړي او الله چا ته چې وغواړي، بې له حسابه رزق وركوي');
INSERT INTO "abulsalam" VALUES(24,39,'24:39',' او هغه كسان چې كافران شوي دي، د هغوى عملونه د سراب (شګې ګل) په شان دي چې په هوار ډاګ كې دي، تږى په هغه باندې د اوبو ګمان كوي، تر دې چې كله ده ته راشي، نو دغه هېڅ شى ونه مومي او په خپلې خوا كې الله ومومي، نو هغه به ده ته د ده حساب پوره وركړي، او الله ډېر ژر حساب والا دى');
INSERT INTO "abulsalam" VALUES(24,40,'24:40',' یا (د دوى عملونه) د هغو تیارو په شان دي چې په ژور سمندر كې دي، چې دغه (سمندر) یو موج (چپې) پټ كړى وي، د هغه له پاسه بل موج (چپه) وي (بیا) د هغه له پاسه ورېځ وي، تیاري دي چې د دوى ځینې د ځینو نورو له پاسه دي، كله چې دى خپل لاس راوباسي (، نو) نژدې نه دي چې دغه وویني او هر هغه څوك چې الله ده ته رڼا ورنه كړي، نو د هغه لپاره هېڅ رڼا نشته');
INSERT INTO "abulsalam" VALUES(24,41,'24:41',' ایا ته نه وینې چې بېشكه الله چې دى، د هغه لپاره پاكي بیانوي هغه څوك چې په اسمانونو او ځمكه كې دي او مرغان هم (تسبیح وايي)، په داسې حال كې چې د خپلو وزرو خواره كوونكي وي، هر یو (چې دى) یقینًا هغه (الله) د هغه په لمونځ او د هغه په تسبیح عالم دى، او الله ښه عالم دى په هغو كارونو چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(24,42,'24:42',' او خاص د الله لپاره ده د اسمانونو او ځمكې بادشاهي، او خاص الله ته بېرته ورتله دي');
INSERT INTO "abulsalam" VALUES(24,43,'24:43',' ایا تا نه دي كتلي چې بېشكه الله وریځې روانوي، بیا هغه په خپل منیځ كې یو ځاى كوي، بیا هغه لاندې باندې قط په قط كوي، بیا ته باران وینې چې د دې له مینځه راوځي او د اسمان له جانبه (د ورېځو) غرونه راكوزوي چې په هغو كې ځینې ږلۍ وي، نو هغه رسوي هغه چا ته چې يې وغواړي او اړوي يې له هغه چا نه چې يې وغواړي، نژدې وي چې د هغو د برېښنا پړق (چمك) نظرونه ختم كړي');
INSERT INTO "abulsalam" VALUES(24,44,'24:44',' الله شپه او ورځ اړوي را اړوي بېشكه په دغو كې د سترګو خاوندانو ته لوى عبرت دى');
INSERT INTO "abulsalam" VALUES(24,45,'24:45',' او په ځمكه ګرځېدونكى هر حیوان الله له اوبو څخه پیدا كړى دى، نو په دوى كې ځینې هغه دي چې په خپلې خېټې تګ كوي او په دوى كې ځېنې هغه دي چې په دوه پښو تګ كوي او په دوى كې ځینې هغه دي چې په څلورو ګرځي، الله چې څه وغواړي پیدا كوي يې، بېشكه الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(24,46,'24:46',' یقینًا یقینًا مونږه بیانوونكي ایتونه نازل كړي دي او الله چې چا ته وغواړي، نېغې لارې ته يې هدایت كوي');
INSERT INTO "abulsalam" VALUES(24,47,'24:47',' او دوى وايي: مونږ په الله باندې او په رسول باندې ایمان راوړى دى او مونږ اطاعت كړى دى، بیا په دوى كې یوه ډله له دې نه پس مخ ګرځوي او دغه خلق له سره مومنان نه دي');
INSERT INTO "abulsalam" VALUES(24,48,'24:48',' او كله چې دوى الله او د هغه رسول ته راوبلل شي، د دې لپاره چې هغه د دوى په مینځ كې فیصله وكړي، ناڅاپه، په دوى كې یوه ډله مخ ګرځوونكې وي');
INSERT INTO "abulsalam" VALUES(24,49,'24:49',' او كه حق د دوى لپاره وي (نو بیا) ده (رسول) ته راځي، چې ګړندي فرمان منونكي دي');
INSERT INTO "abulsalam" VALUES(24,50,'24:50',' ایا د دوى په زړونو كې مرض دى، یا دوى شك كوي، یا دوى وېرېږي له دې نه چې الله او د هغه رسول به په دوى ظلم وكړي، بلكې همدغه خلق ظالمان دي');
INSERT INTO "abulsalam" VALUES(24,51,'24:51',' بېشكه د مومنانو خبره خو همدا وي، كله چې دوى الله او د هغه رسول ته وبلل شي چې وايي: مونږ (حكم) واورېده او مونږ ومانه او همدغه خلق كامیاب (خلاصى موندونكي) دي');
INSERT INTO "abulsalam" VALUES(24,52,'24:52',' او هر څوك چې د الله او د هغه د رسول اطاعت وكړي او له الله نه ووېرېږي او د هغه تقوىٰ اختیار كړي، نو همدغه خلق برى موندونكي دي');
INSERT INTO "abulsalam" VALUES(24,53,'24:53',' او دوى په الله قسمونه وخوړل، سخت پاخه قسمونه: خامخا كه ته دوى ته (جهاد ته د وتلو) امر وكړې (نو) دوى به خامخا ضرور وځي، ته (دوى ته) ووایه: تاسو (د دروغو) قسمونه مه خورئ، (له تاسو مطلوب) نېك طاعت دى، بېشكه الله ښه خبردار دى په هغو كارونو چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(24,54,'24:54',' ته دوى ته ووایه: تاسو د الله اطاعت وكړئ او د رسول اطاعت وكړئ، نو كه دوى مخ واړوي، نو بېشكه همدا خبره ده چې په دغه (رسول) باندې هغه ( د تبلیغ) بار دى چې پرې ایښودل شوى دى او پر تاسو باندې هغه بار دى چې په تاسو ایښودل شوى دى او كه تاسو د هغه (رسول) اطاعت وكړئ (نو) نېغه لار به ومومئ، او د رسول په ذمه نشته مګر ښكاره پېغام رسول');
INSERT INTO "abulsalam" VALUES(24,55,'24:55',' الله وعده كړې د هغو كسانو سره چې ایمان يې راوړى دى، چې تاسو یئ او نېك عملونه يې كړي دي، چې خامخا هرومرو به دوى ته په ځمكه كې خلافت وركوي لكه چې هغو كسانو ته يې خلافت وركړى و چې له دوى نه مخكې وو او خامخا ضرور به دوى ته د دوى هغه دین ثابت (او قوي) وګرځوي چې هغه (الله) د دوى لپاره غوره كړى دى او خامخا ضرور به دوى ته د دوى له وېرې نه پس امن په بدل كې وركړي، چې دوى به زما عبادت كوي او زما سره به هېڅ شى نه شریكوي، او هر څوك چې د دغو (انعامونو) نه بعد كافر شو، نو همدغه كسان فاسقان دي');
INSERT INTO "abulsalam" VALUES(24,56,'24:56',' او تاسو لمونځ قايموئ او زكات ادا كوئ او د رسول اطاعت كوئ، د دې لپاره چې په تاسو رحم وكړى شي');
INSERT INTO "abulsalam" VALUES(24,57,'24:57',' ته له سره ګمان مه كوه په هغو كسانو چې كافران شوي دي چې په ځمكه كې (دوى مونږ لره) عاجزه كوونكي دي. او د دوى استوګنه اور دى، او هغه خامخا د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(24,58,'24:58',' اى هغو كسانو چې ایمان يې راوړى دى! له تاسو نه دې هغه كسان چې تاسو يې مالكان يئ او هغه كسان چې له تاسو (ازادو) نه بلوغ ته نه دي رسېدلي، درې ځلې دې اجازت غواړي له سبا د لمانځه نه مخكې او كله چې تاسو په غرمه كې جامې وباسئ او د ماسختن له لمانځه نه پس، تاسو لپاره درې د پردې وختونه دي، د دغو (وختونو) نه غیر نه په تاسو ګناه شته او نه پر هغوى باندې، (دوى) ډېر ګرځېدونكي دي پر تاسو باندې، ستاسو ځینې په ځینو نورو باندې، همداسې الله تاسو ته ایتونه بیانوي، او الله ښه عالم، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(24,59,'24:59',' او كله چې ستاسو (ازاد) ماشومان بلوغ ته ورسېږي، نو دوى دې اجازت غواړي، لكه چې هغه كسان اجازت غواړي چې له دوى نه مخكې (بالغ شوي) دي، همداسې الله تاسو ته خپل ایتونه بیانوي، او الله ښه عالم، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(24,60,'24:60',' او ډېرې زړې (له حیض او اولاد نه) ناستې ښځې هغه چې (د بوډاوالي په وجه) د نكاح امېد نه لري، نو پر هغوى باندې څه ګناه نشته چې خپلې جامې كېږدي، په داسې حال كې چې د زینت څرګندوونكې نه وي، او دا چې دوى (له دې نه هم) ځان وساتي، د دوى لپاره ډېره غوره دي، او الله ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(24,61,'24:61',' په ړانده باندې حرج نشته او نه په ګوډ حرج (مشقت) شته او نه په ناروغه حرج شته او نه ستاسو په خپلو ځانونو باندې (حرج شته) چې تاسو له خپلو كورونو نه خوراك وكړئ، یا د خپلو پلرونو له كورونو نه، یا د خپلو میندو له كورونو نه، یا د خپلو وروڼو له كورونو نه، یا د خپلو خویندو له كورونو نه، یا د خپلو ترونو له كورونو نه، یا د خپلو پلرنۍ تروریانو له كورونو نه یا د خپلو ماماګانو له كورونو نه، یا د خپلو مورنۍ تروریانو له كورونو نه، یا له هغو (كورونو) نه چې تاسو د هغو د چابیو اختیارمن شئ، یا د خپلو دوستانو (له كورونو) نه، په تاسو هېڅ ګناه نشته چې تاسو یو ځاى خوراك وكړئ، یا بېل بېل، نو كله چې تاسو كورونو ته ننوځئ، نو په خپلو ځانونو سلام اچوئ، (دا) د الله له جانبه مباركه پاكیزه تحفه ده، همداسې الله تاسو ته ایتونه بیانوي، د دې لپاره چې تاسو له عقل نه كار واخلئ');
INSERT INTO "abulsalam" VALUES(24,62,'24:62',' بېشكه هم دا خبره ده چې مومنان خو هغه كسان دي چې په الله او د هغه په رسول يې ایمان راوړى دى او كله چې دوى له هغه (رسول) سره په كوم جمع كوونكي كار (لكه، جمعه، جهاد، یا جرګه) وي، (نو پخپل سر) نه ځي، تر هغه پورې چې له هغه نه اجازت وغواړي، بېشكه هغه كسان چې له تا نه اجازت غواړي، دغه كسان دي چې په الله او د هغه په رسول ایمان لري، نو كله چې دوى له تا نه د خپلو ځینو كارونو لپاره اجازت وغواړي، نو په دوى كې چې چا ته وغواړې اجازت وركړه او د دوى لپاره له الله نه بخښنه وغواړه. بېشكه الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(24,63,'24:63',' تاسو په خپل مینځ كې د رسول بلنه داسې مه ګرځوئ لكه ستاسو د ځینو بلنه ځینو نورو ته، یقینًا الله هغه كسان پېژني چې له تاسو نه كشېږي (یو یو، دوه دوه، پټ دباندې وځي)، په داسې حال كې چې یو له بله ځان سره پناه كوي، نو هغه كسان دې ووېرېږي چې د هغه (رسول) له حكم نه مخالفت كوي، له دې نه چې دوى ته څه افت ورسېږي، یا دوى ته ډېر دردوونكى عذاب ورسېږي');
INSERT INTO "abulsalam" VALUES(24,64,'24:64',' خبردار شئ! بېشكه خاص د الله لپاره دي هر هغه څه دي چې په اسمانونو او ځمكه كې دي، یقینًا هغه عالم دى په هغه حال چې تاسو په هغه یئ او په هغې ورځ (هم) چې تاسو به هغه ته بېرته بېول كېږئ، نو هغه به دوى په هغو عملونو خبر كړي چې دوى كړي دي، او الله په هر شي باندې ښه عالم دى');
INSERT INTO "abulsalam" VALUES(25,1,'25:1',' ډېر بركتناك دى هغه ذات چې په خپل بنده يې فرقان (قرآن) نازل كړى دى، د دې لپاره چې دى د خلقو لپاره وېروونكى شي');
INSERT INTO "abulsalam" VALUES(25,2,'25:2',' هغه ذات چې خاص د هغه لپاره د اسمانونو او ځمكې بادشاهي ده او هېڅ اولاد يې نه دى نیولى او د ده لپاره په بادشاهۍ كې هېڅ شریك نشته، او هر شى يې پیدا كړى دى، نو هغه يې په اندازه كړى دى، اندازه كول');
INSERT INTO "abulsalam" VALUES(25,3,'25:3',' او دوى (كفارو) له هغه نه غیر نور داسې معبودان نیولي دي چې هېڅ شى نشي پیدا كولى، په داسې حال كې چې دوى پیدا كاوه شي او دوى د خپلو ځانونو لپاره نه د ضرر مالكان دي او نه د نفعې او نه د مرګ اختیار لري او نه د ژوند او نه د بیا ژوندي كولو');
INSERT INTO "abulsalam" VALUES(25,4,'25:4',' او كافران شویو خلقو وویل: نه دى دا مګر دروغ چې ده، له ځانه جوړ كړى دى او په ده باندې نورو خلقو د ده مرسته كړې ده، نو یقینًا دغو (كفارو) وكړ لوى ظلم او دروغ');
INSERT INTO "abulsalam" VALUES(25,5,'25:5',' او دوى وویل: (دا) د ړومبنیو خلقو باطلې قصې دي چې ده په چا لیكلي دي، نو هغه ده ته سبا او بېګاه لوستلى شي');
INSERT INTO "abulsalam" VALUES(25,6,'25:6',' ته (دوى ته) ووایه: دا (قرآن) هغه ذات نازل كړى دى چې عالم دى په هر پټ څیز باندې چې په اسمانونو او ځمكه كې دى، بېشكه هغه ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(25,7,'25:7',' او دوى وايي: دې رسول ته څه دي چې طعام خوري او په بازارونو كې ګرځي، ده ته ملايك ولې نه نازلاوه شي، بيا چې له ده سره دغه (ملايك) وېروونكي شي');
INSERT INTO "abulsalam" VALUES(25,8,'25:8',' یا ده ته خزانه ولې نه غورځولى كېږي، یا ولې د ده لپاره داسې باغ نه كېږي چې دا له هغه نه خوراك كوي؟ او ظالمانو وویل: تاسو پيروي نه كوئ مګر د داسې سړي چې سحر (جادو) پرې شوى دى');
INSERT INTO "abulsalam" VALUES(25,9,'25:9',' ته وګوره! دوى تا لپاره څنګه مثالونه بیانوي، نو ګمراه شول، نو دوى د (حقې) لارې (د موندلو) هېڅ طاقت نه لري');
INSERT INTO "abulsalam" VALUES(25,10,'25:10',' ډېر بركتناك دى هغه ذات چې كه وغواړي تا لپاره به د دغو (خزانو) نه غوره وګرځوي (یعنې داسې) باغونه چې د هغو له لاندې ولې بهېږي او تا لپاره به بنګلې جوړې كړي');
INSERT INTO "abulsalam" VALUES(25,11,'25:11',' بلكې دوى د قیامت تكذیب وكړ او مونږ د هغه چا لپاره چې د قیامت تكذیب كوي ؛ لمبې وهونكى اور تیار كړى دى');
INSERT INTO "abulsalam" VALUES(25,12,'25:12',' كله چې هغه (اور) دوى لره له ډېر لرې ځاى نه وویني، نو دوى به د هغه خوټكېدل او غورېدل واوري');
INSERT INTO "abulsalam" VALUES(25,13,'25:13',' او كله چې دوى په دغه (دوزخ) كې په تنګ ځاى كې وغورځول شي، په داسې حال كې چې له یو بل سره به په ځنځیرونو تړل شوي وي (، نو) دوى به په دغه ځاى كې مرګ ته چغې وهي');
INSERT INTO "abulsalam" VALUES(25,14,'25:14',' نن ورځ تاسو یو هلاكت (مرګ) ته چغې مه وهئ او (بلكې) تاسو ډېرو هلاكتونو ته چغې ووهئ');
INSERT INTO "abulsalam" VALUES(25,15,'25:15',' ته (دوى ته) ووایه: دغه (خیر) غوره دى، یا كه هغه د همېشه والي جنت چې متقیانو (پرهېزګارو) ته يې وعده وركړى شوې ده؟ دغه به د دوى لپاره بدله او د ورتلو ځاى وي');
INSERT INTO "abulsalam" VALUES(25,16,'25:16',' د دوى لپاره به په هغه كې هغه څه وي چې دوى يې غواړي، په داسې حال كې چې دوى به په كې تل وي، دا ستا د رب په ذمه غوښتل شوې وعده ده');
INSERT INTO "abulsalam" VALUES(25,17,'25:17',' او (یاده كړه) هغه ورځ چې هغه (الله) به راجمع كړي دوى او هغه څه چې دوى به يې له الله نه غیر عبادت كاوه، نو وبه وايي: ایا تاسو زما دغه بنده ګان ګمراه كړي وو، یا دوى پخپله (حقه) لاره وركه كړې وه؟');
INSERT INTO "abulsalam" VALUES(25,18,'25:18',' دغه (معبودان) به ووايي: تالره پاكي ده، مونږ ته جايز نه ده چې مونږ له تا نه غیر دوستان ونیسو او لېكن تا دوى ته او د دوى پلرونو ته فايده (او ښه زندګي) وركړې وه، تر دې چې دوى ذكر (نصیحت) هېر كړ او دوى هلاكېدونكى قوم و');
INSERT INTO "abulsalam" VALUES(25,19,'25:19',' نو یقینًا (اى مشركانو!) دوى تاسو دروغجن كړئ په هغې خبره كې چې تاسو ویله، نو تاسو نه د (عذاب د) ګرځولو طاقت لرئ او نه د مدد كولو، او په تاسو كې هر هغه څوك چې ظلم وكړي! مونږ به هغه ته ډېر لوى عذاب وڅكوو');
INSERT INTO "abulsalam" VALUES(25,20,'25:20',' او له تا نه مخكې مونږ رسولان نه دي لېږلي مګر چې یقینًا هغوى به خامخا طعام خواړه، او په بازارونو كې ګرځېدل او مونږه ستاسو ځینې د ځینو نورو لپاره ازمېښت ګرځولي دي (چې) ایا تاسو صبر كوئ (كه نه؟) او ستا رب له ازله ښه ليدونكى دى');
INSERT INTO "abulsalam" VALUES(25,21,'25:21',' او وویل هغو كسانو چې زمونږ د ملاقات امېد نه لري: پر مونږ باندې ملايك ولې نه نازلولى شي، یا (ولې) مونږ خپل رب نه وینو، یقینًا یقینًا دوى په خپلو نفسونو كې كبر وكړ، او له حده تېر شول، له حد نه ډېر لوى تېرېدل');
INSERT INTO "abulsalam" VALUES(25,22,'25:22',' (یاده كړه) هغه ورځ چې دوى به ملايك ویني، په دغې ورځ كې به د مجرمانو لپاره هېڅ زېرى (خوشالي) نه وي او وايي به (ملايك چې جنت) بند دى، ممنوع دى');
INSERT INTO "abulsalam" VALUES(25,23,'25:23',' او دوى چې څه عمل هم كړى دى هغه ته به مونږ ورسو، نو هغه به خور كړى شوى ګرد وګرځوو');
INSERT INTO "abulsalam" VALUES(25,24,'25:24',' جنتیان به په دغې ورځ كې د استوګنې په لحاظ غوره او د ارام ځاى په لحاظ ډېر ښه وي');
INSERT INTO "abulsalam" VALUES(25,25,'25:25',' او (یاده كړه) هغه ورځ چې په ورېځو سره به اسمان ټوټې ټوټې شي او ملايك به نازل كړى شي (پرله پسې) نازلول');
INSERT INTO "abulsalam" VALUES(25,26,'25:26',' حقیقي بادشاهي به په دغې ورځ كې د رحمن لپاره وي، او وي به دغه په كافرانو ډېره سخته ورځ');
INSERT INTO "abulsalam" VALUES(25,27,'25:27',' او (یاده كړه) هغه ورځ چې ظالم به په خپلو دواړو لاسونو خوله لګوي، په داسې حال كې چې وايي به: اى كاشكې! ما له رسول سره (نېغه) لار نیولې وى');
INSERT INTO "abulsalam" VALUES(25,28,'25:28',' اى زما هلاكته! كاشكې ما فلانكى سړى دوست نه وى جوړ كړى');
INSERT INTO "abulsalam" VALUES(25,29,'25:29',' یقینًا یقینًا هغه زه له ذكر (قرآن) نه ګمراه كړم، كله چې هغه (ذكر) ما ته راغى او شیطان انسان لره بې مدده پرېښودونكى دى');
INSERT INTO "abulsalam" VALUES(25,30,'25:30',' او رسول وویل: اى زما ربه! بېشكه زما قوم دا قرآن متروك ګرځولى دى');
INSERT INTO "abulsalam" VALUES(25,31,'25:31',' او همداسې مونږ د هر نبي لپاره له مجرمانو څخه دښمنان ګرځولي وو او ستا رب ډېر كافي دى، سمه لار ښوونكى، او ښه مدد كوونكى');
INSERT INTO "abulsalam" VALUES(25,32,'25:32',' او كافران شویو خلقو وویل: په ده باندې قرآن ټول یو كرت ولې نه نازلاوه شي؟ همداسې (لږ لږ يې نازلوو) د دې لپاره چې په ده سره مونږ ستا زړه مضبوط كړو او مونږ ښه په ارام لوستى دى دا (قرآن په تا باندې) په ارام لوستل');
INSERT INTO "abulsalam" VALUES(25,33,'25:33',' او دوى تا ته كوم مثال (عجیب سوال او اعتراض) نه راوړي مګر مونږ تا ته راوړو (د هغه) حق (جواب) او تر ټولو ښه تفسیر');
INSERT INTO "abulsalam" VALUES(25,34,'25:34',' هغه كسان چې جهنم ته به په خپلو مخونو جمع كولى شي، دغه كسان د ځاى په لحاظ تر ټولو بد او د لارې په لحاظ تر ټولو ګمراه دي');
INSERT INTO "abulsalam" VALUES(25,35,'25:35',' او یقینًا یقینًا مونږ موسٰى ته كتاب وركړى و او مونږ له هغه سره د هغه ورور هارون مددګار ګرځولى و');
INSERT INTO "abulsalam" VALUES(25,36,'25:36',' نو مونږ وویل: تاسو دواړه هغه قوم ته لاړ شئ چې زمونږ د ایتونو تكذیب يې كړى دى، بیا مونږ هغوى (فرعونیان) هلاك كړل، سخت هلاكول');
INSERT INTO "abulsalam" VALUES(25,37,'25:37',' او (مونږ هلاك كړ) د نوح قوم، كله چې هغوى د رسولانو تكذیب وكړ، مونږ هغوى غرق كړل او مونږه دوى د خلقو لپاره (د عبرت) نشان وګرځول او مونږه د ظالمانو لپاره ډېر دردوونكى عذاب تیار كړى دى');
INSERT INTO "abulsalam" VALUES(25,38,'25:38',' او (مونږ هلاك كړي دي) عادیان او ثمودیان او د كوهي والا او د دوى په مينځ كې ډېر زیات نور قومونه');
INSERT INTO "abulsalam" VALUES(25,39,'25:39',' او (په دوى كې) هر یو ته، مونږ هغه ته مثالونه بیان كړي وو او مونږ ټول هلاك كړل، بېخي هلاكول');
INSERT INTO "abulsalam" VALUES(25,40,'25:40',' او یقینًا یقینًا دوى په هغه كلي تېر شوي دي چې په هغه باندې د بدۍ باران ورول شوى و، ایا نو دوى هغه نه لیده ؟ بلكې دوى د بیا ژوندي پورته كېدلو امېد نه لاره');
INSERT INTO "abulsalam" VALUES(25,41,'25:41',' او كله چې دوى تا وویني (، نو) دوى تا نه نیسي مګر ځاى د مسخرې (او وايي:) ایا دا هغه سړى دى چې الله، (په طور د) رسول رالېږلى دى؟');
INSERT INTO "abulsalam" VALUES(25,42,'25:42',' بېشكه چې دى نژدې و چې مونږ خامخا له خپلو معبودانو نه بې لارې كړي كه مونږ په دوى ټینګ ودرېدلي نه وى (الله فرمايي:) دوى به ژر پوه شي كله چې دوى عذاب ویني چې څوك له (حقې) لارې نه ډېر بې لارې دى');
INSERT INTO "abulsalam" VALUES(25,43,'25:43',' ایا ته هغه څوك وینې چې خپل معبود يې خپل خواهش ګرځولى دى! ایا نو ته به په ده باندې نګران يې');
INSERT INTO "abulsalam" VALUES(25,44,'25:44',' ایا ته ګمان كوې چې په رښتیا د دوى زیاتره اوري، یا له عقل نه كار اخلي، دوى نه دي مګر په شان د څارویو بلكې دوى د لارې په لحاظ زیات ګمراه دي');
INSERT INTO "abulsalam" VALUES(25,45,'25:45',' ایا تا (د) خپل رب (قدرت) ته نه دي كتلي چې هغه سیورى څنګه غځولى دى او كه هغه غوښتلى (نو) هغه به يې خامخا ولاړ (ساكن) ګرځولى وى، بیا مونږ په هغه باندې لمر دلیل وګرځاوه');
INSERT INTO "abulsalam" VALUES(25,46,'25:46',' بیا مونږ هغه خپل طرف ته راټول كړ ډېر اسان راټولول');
INSERT INTO "abulsalam" VALUES(25,47,'25:47',' او دى هماغه ذات دى چې تاسو لپاره يې شپه لباس او خوب (سبب د) ارام ګرځولى دى او ورځ يې د ګرځېدو وخت ګرځولې ده');
INSERT INTO "abulsalam" VALUES(25,48,'25:48',' او همدى هغه ذات دى چې هواګانې يې له رحمت نه مخكې زېرى وركوونكې لېږلې دي او مونږ له بره نه پاكوونكي اوبه نازلې كړې دي');
INSERT INTO "abulsalam" VALUES(25,49,'25:49',' د دې لپاره چې مونږ په دغو (اوبو) سره مړ ښار راژوندى كړو او چې دا په څارویو او ډېر زیاتو انسانانو وڅښوو، چې زمونږ له مخلوق ځنې دي');
INSERT INTO "abulsalam" VALUES(25,50,'25:50',' او یقینًا یقینًا مونږ دا د دوى په مینځ كې په قسم قسم طریقو خورې كړې دي، لپاره د دې چې دوى نصیحت واخلي، نو زیاترو خلقو انكار وكړ مګر له كفر كولو نه');
INSERT INTO "abulsalam" VALUES(25,51,'25:51',' او كه مونږ غوښتلى (، نو) مونږ به خامخا هر كلي ته یو وېروونكى لېږلى و');
INSERT INTO "abulsalam" VALUES(25,52,'25:52',' نو ته د كافرانو اطاعت مه كوه او ته له دوى سره په دې (قرآن) سره جهاد كوه، ډېر لوى جهاد كول');
INSERT INTO "abulsalam" VALUES(25,53,'25:53',' او دى هماغه ذات دى چې دوه دریابونه يې خوشې كړې دي (له یو بل سره جوخت) دا خوږ دى، ډېر خوږ (تنده ماتوونكى) او د ا (بل) تریو دى، ښه تریو او د دواړو په مینځ كې يې یوه پرده او بند پیدا كړى دى چې منع كړى شوى دى');
INSERT INTO "abulsalam" VALUES(25,54,'25:54',' او دى همغه ذات دى چې له اوبو نه يې انسان پیدا كړى دى، بیا يې هغه (د) نسب او سخرګنۍ (والا) وګرځاوه او ستا رب ښه قادر دى');
INSERT INTO "abulsalam" VALUES(25,55,'25:55',' او دوى له الله نه غیر د هغه څه عبادت كوي چې دوى ته نه نفع رسولى شي اونه دوى ته ضرر رسولى شي او كافر د خپل رب په مخالفت كې (د شیطان) مدد كوونكى دى');
INSERT INTO "abulsalam" VALUES(25,56,'25:56',' او ته مونږ نه يې لېږلى مګر زېرى وركوونكى او وېروونكى');
INSERT INTO "abulsalam" VALUES(25,57,'25:57',' ته (دوى ته)ووایه: زه له تاسو نه په دې (تبلیغ) باندې هېڅ مزدوري (اجرت) نه غواړم خو (غواړم ایمان راوړل د) هغه كس چې غواړي چې د خپل رب په طرف لار ونیسي');
INSERT INTO "abulsalam" VALUES(25,58,'25:58',' او ته توكل وكړه په هغه تل ژوندي ذات چې نه مري او ته د هغه له ستاينې سره تسبیح بیانوه او هم الله د خپلو بنده ګانو په ګناهونو ښه كافي خبردار دى');
INSERT INTO "abulsalam" VALUES(25,59,'25:59',' هغه (الله) چې اسمانونه او ځمكه، او هغه څه چې د دواړو په مینځ كې دي، په شپږو ورځو كې پیدا كړي دي، بیا په عرش باندې برقرار شو، (دغه) رحمن دى، نو ته د ده په باره كې له ښه خبردار نه تپوس وكړه');
INSERT INTO "abulsalam" VALUES(25,60,'25:60',' او كله چې دوى ته وویل شي: تاسو رحمٰن ته سجده وكړئ، دوى وايي: رحمن څه شى دى؟ ایا مونږ به هغه څه ته سجده كوو چې ته يې مونږ ته حكم راكوې او دا (رحمٰن ته د سجده كولو حكم) دوى ته منډه تېښته ورزیاته كړي');
INSERT INTO "abulsalam" VALUES(25,61,'25:61',' ډېر بركتناك دى هغه ذات چې په اسمان كې يې برجونه پیدا كړي دي او په ده كې يې چراغ (لمر) او رڼا كوونكې سپوږمۍ پیدا كړې ده');
INSERT INTO "abulsalam" VALUES(25,62,'25:62',' او دى هماغه ذات دى چې شپه او ورځ يې یو له بل پسې راتلونكې پیدا كړي دي، د هغه چا لپاره چې اراده لري چې نصیحت واخلي یا غواړي چې شكر وباسي');
INSERT INTO "abulsalam" VALUES(25,63,'25:63',' او د رحمن بنده ګان هغه دي چې په ځمكه ورو ګرځي او كله چې له دوى سره جاهلان خطاب (خبره) كوي (نو) دوى سالمه (درسته) خبره كوي');
INSERT INTO "abulsalam" VALUES(25,64,'25:64',' او هغه كسان دي چې خپل رب ته شپه تېروي، په داسې حال كې چې سجده كوونكي او قیام كوونكي وي');
INSERT INTO "abulsalam" VALUES(25,65,'25:65',' او هغه كسان دي چې وايي: اى زمونږ ربه! ته زمونږ نه د جهنم عذاب وګرځوه، بېشكه د هغه عذاب همېشه لازمېدونكى دى');
INSERT INTO "abulsalam" VALUES(25,66,'25:66',' بېشكه دا (جهنم) د قرار او استوګنې په لحاظ بد ځاى دى');
INSERT INTO "abulsalam" VALUES(25,67,'25:67',' او هغه كسان دي چې كله انفاق كوي (مال لګوي نو) اسراف نه كوي او بخل هم نه كوي او د دغو (دواړو حالتونو) په مینځ كې میانه حاله وي');
INSERT INTO "abulsalam" VALUES(25,68,'25:68',' او هغه كسان دي چې له الله سره بل معبود نه بلي او هغه نفس چې الله حرام كړى وي؛ ناحقه نه قتلوي او زنا نه كوي او څوك چې د غه (كارونه) وكړي (نو) هغه به د خپلې ګناه له جزا سره ملاقات وكړي');
INSERT INTO "abulsalam" VALUES(25,69,'25:69',' د قیامت په ورځ به ده ته عذاب دوه چنده كولى شي او په هغه (عذاب) كې به تل وي، په داسې حال چې چې خوار او ذلیله به وي');
INSERT INTO "abulsalam" VALUES(25,70,'25:70',' مګر هغه كس چې توبه وباسي او ایمان راوړي او نېك عمل وكړي، نو دغه كسان دي چې الله به د دوى بدۍ په نېكۍ سره بدلې كړي او الله له ازله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(25,71,'25:71',' او هغه څوك چې توبه وباسي او نېك عمل وكړي، نو بېشكه دغه كس الله ته رجوع كوي، رجوع كول');
INSERT INTO "abulsalam" VALUES(25,72,'25:72',' او هغه كسان دي چې باطلو ته نه حاضرېږي او كله چې په بېكاره كار تېرېږي (نو) عزت داره تېرېږي');
INSERT INTO "abulsalam" VALUES(25,73,'25:73',' او هغه كسان دي چې كله دوى ته د خپل رب په ایتونو سره نصیحت وكړى شي (نو) په دې (ایتونو) باندې كاڼه او ړانده نه غورځېږي');
INSERT INTO "abulsalam" VALUES(25,74,'25:74',' او هغه كسان دي چې وايي: اى زمونږه ربه! ته مونږ ته زمونږ له ښځو نه او زمونږ له اولادونو نه د سترګو یخ والى راوبښه او ته مونږ د متقیانو امام وګرځوه');
INSERT INTO "abulsalam" VALUES(25,75,'25:75',' دغه كسان دي چې د صبر كولو په سبب به دوى ته د اوچتو ماڼیو بدله وركولى شي او په دې (جنت) كې به د ژوند په دعا او سلام سره د دوى استقبال كولى شي');
INSERT INTO "abulsalam" VALUES(25,76,'25:76',' په داسې حال كې چې همېشه به وي دوى په دغه (جنت) كې، د قرار او استوګنې په لحاظ دا ښه ځاى دى');
INSERT INTO "abulsalam" VALUES(25,77,'25:77',' ته (دوى ته) ووایه: زما رب ستاسو څه پروا كوي كه ستاسو دعا كول نه وى، نو یقینًا تاسو تكذیب كړى دى، نو ژر به دا (د تكذیب سزا تاسو سره) لازمه وي');
INSERT INTO "abulsalam" VALUES(26,1,'26:1',' طا، سين، ميم');
INSERT INTO "abulsalam" VALUES(26,2,'26:2',' دا د واضح كتاب ایتونه دي');
INSERT INTO "abulsalam" VALUES(26,3,'26:3',' ښايي چې ته د خپل ځان هلاكوونكى يې، په دې وجه چې دوى مومنان كېږي نه');
INSERT INTO "abulsalam" VALUES(26,4,'26:4',' كه مونږ وغواړو (نو) په دوى به له اسمان نه یو دلیل نازل كړو، بیا به د دوى څټونه دغه (دلیل) ته غاړه ایښودونكي شي');
INSERT INTO "abulsalam" VALUES(26,5,'26:5',' او دوى ته د رحمن له جانبه څه نوى ذكر (نصیحت) نه راځي مګر دوى له هغه نه مخ ګرځوونكي دي');
INSERT INTO "abulsalam" VALUES(26,6,'26:6',' نو تحقیق دوى تكذیب وكړ، پس دوى ته به ژر د هغه څه خبر (انجام) راشي چې دوى به په هغه پورې استهزا كولې');
INSERT INTO "abulsalam" VALUES(26,7,'26:7',' ایا دوى ځمكې ته نه ګوري، څومره ډېر مونږ په دې كې له هر قسمه عمده جوړو نه زرغون كړي دي');
INSERT INTO "abulsalam" VALUES(26,8,'26:8',' بېشكه په دې كې خامخا لویه نښه ده او د دوى زیاتره ایمان راوړونكي نه دي');
INSERT INTO "abulsalam" VALUES(26,9,'26:9',' او بېشكه ستا رب خامخا هم هغه ښه غالب، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(26,10,'26:10',' او (یاد كړه) هغه وخت چې ستا رب موسٰى ته اواز وكړ داسې چې ته هغه قوم ته ورشه چې ظالمان دي');
INSERT INTO "abulsalam" VALUES(26,11,'26:11',' چې د فرعون قوم دى، ایا دوى نه وېرېږي (له الله نه)!');
INSERT INTO "abulsalam" VALUES(26,12,'26:12',' (موسٰى) وویل: اى زما ربه! بېشكه زه له دې نه وېرېږم چې دوى به زما تكذیب وكړي');
INSERT INTO "abulsalam" VALUES(26,13,'26:13',' او (بل) زما سینه تنګېږي او (بل) زما ژبه نه چلېږي، نو ته هارون ته پېغام ولېږه');
INSERT INTO "abulsalam" VALUES(26,14,'26:14',' او (بل دا چې) د هغوى لپاره زما په خلاف (دعوىٰ د) ګناه ده، نو زه له دې نه وېرېږم چې دوى به ما قتل كړي');
INSERT INTO "abulsalam" VALUES(26,15,'26:15',' (الله) وفرمایل: داسې نشي كېدى، نو تاسو دواړه زما له ښكاره معجزو سره لاړ شئ، بېشكه مونږ له تاسو سره یو، اورېدونكي یو');
INSERT INTO "abulsalam" VALUES(26,16,'26:16',' نو تاسو دواړه فرعون ته ورشئ، نو (ورته) ووایئ: بېشكه مونږ د رب العلمین استازي یو');
INSERT INTO "abulsalam" VALUES(26,17,'26:17',' په دې خبره چې ته زمونږ سره بني اسرائیل خوشې كړه');
INSERT INTO "abulsalam" VALUES(26,18,'26:18',' (فرعون) وویل: ایا مونږه په خپل كاله كې ستا پالنه نه وه كړې، په داسې حال كې چې ته بچى وې او تا په مونږ كې د خپل عمر څو كاله تېر كړي وو');
INSERT INTO "abulsalam" VALUES(26,19,'26:19',' او تا خپل هغه كار كړى و چې تا كړى و، په دې حال كې چې ته له ناشكرو ځنې يې');
INSERT INTO "abulsalam" VALUES(26,20,'26:20',' (موسٰى) وویل: ما دغه كار په هغه وخت كې كړى و چې زه له ناپوهانو ځنې وم');
INSERT INTO "abulsalam" VALUES(26,21,'26:21',' نو كله چې زه له تاسو ووېرېدم (نو) له تاسو نه وتښتېدم، بیا ما ته زما رب حكم (او علم) راكړ او زه يې له رسولانو څخه وګرځولم');
INSERT INTO "abulsalam" VALUES(26,22,'26:22',' او دغه داسې احسان دى چې ته يې په ما زباتوې (هغه) دا دى چې تا بني اسرائیل غلامان ګرځولي وو');
INSERT INTO "abulsalam" VALUES(26,23,'26:23',' فرعون وویل: او رب العلمین څه شى دى؟');
INSERT INTO "abulsalam" VALUES(26,24,'26:24',' هغه وویل: رب دى د اسمانونو او ځمكې او د هغه څه چې د دې دواړو په مینځ كې دي، كه تاسو یقین كوونكي یئ');
INSERT INTO "abulsalam" VALUES(26,25,'26:25',' هغه (فرعون) خپلو ګېر چاپېره خلقو ته وویل: ایا تاسو نه اورئ؟');
INSERT INTO "abulsalam" VALUES(26,26,'26:26',' (موسٰى) وویل: (چې رب العلمین) ستاسو رب دى او ستاسو د مخكينو پلرونو رب دى');
INSERT INTO "abulsalam" VALUES(26,27,'26:27',' (فرعون) وویل: بېشكه ستاسو دغه رسول چې تاسو ته رالېږل شوى دى، یقینًا لېونى دى');
INSERT INTO "abulsalam" VALUES(26,28,'26:28',' (موسٰى) وویل: (هغه) د مشرق او مغرب رب دى او څه چې د دې دواړو په مینځ كې دي، كه تاسو له عقل نه كار اخلئ');
INSERT INTO "abulsalam" VALUES(26,29,'26:29',' (فرعون موسٰى ته) وویل: قسم دى كه تا له ما نه غیر بل معبود ونیوه (نو) زه به خامخا هرومرو تا وګرځوم له قیدیانو ځنې');
INSERT INTO "abulsalam" VALUES(26,30,'26:30',' (موسٰى) وویل: ایا اګر كه زه تا ته یو څرګندوونكى شى (دلیل) راوړم');
INSERT INTO "abulsalam" VALUES(26,31,'26:31',' (فرعون) وویل: نو ته هغه راوړه كه ته له رښتینو څخه يې');
INSERT INTO "abulsalam" VALUES(26,32,'26:32',' نو هغه (موسٰى) خپله امسا وغورځوله، نو ناڅاپه هغه وه ښكاره ښامار');
INSERT INTO "abulsalam" VALUES(26,33,'26:33',' او خپل لاس يې راواېست، نو ناڅاپه هغه د كتونكو لپاره تك سپين و');
INSERT INTO "abulsalam" VALUES(26,34,'26:34',' فرعون خپلو چاپېره سردارانو ته وویل: بېشكه دا یقینًا ډېر پوه جادوګر دى');
INSERT INTO "abulsalam" VALUES(26,35,'26:35',' دا غواړي چې په خپل جادو سره تاسو له خپلې ځمكې نه وباسي، نو تاسو د څه مشوره راكوئ؟');
INSERT INTO "abulsalam" VALUES(26,36,'26:36',' هغوى وویل: ته دا او د ده ورور وروسته كړه او په ښارونو كې راټولوونكي ولېږه');
INSERT INTO "abulsalam" VALUES(26,37,'26:37',' چې تا ته هر ښه پوه، غټ جادوګر راولي');
INSERT INTO "abulsalam" VALUES(26,38,'26:38',' نو جادوګر د معلومې ورځې میعاد ته راجمع كړى شول');
INSERT INTO "abulsalam" VALUES(26,39,'26:39',' او خلقو ته وویل شول چې ایا تاسو راجمع كېدونكي یئ؟');
INSERT INTO "abulsalam" VALUES(26,40,'26:40',' كېدى شي چې مونږ د جادوګرو پیروي وكړو كه هم دوى غالب راتلونكي شي');
INSERT INTO "abulsalam" VALUES(26,41,'26:41',' نو كله چې جادوګر راغلل، (نو) فرعون ته يې وویل: ایا یقینًا به زمونږ لپاره واقعي اجر وي كه همدا مونږ غالبه شو؟');
INSERT INTO "abulsalam" VALUES(26,42,'26:42',' ويې ويل:هو، او بېشكه تاسو به په دغه وخت كې خامخا له مقربانو څخه یئ!');
INSERT INTO "abulsalam" VALUES(26,43,'26:43',' دوى ته موسٰى وویل: تاسو هغه څه وغورځوئ چې تاسو يې غورځوونكي یئ');
INSERT INTO "abulsalam" VALUES(26,44,'26:44',' نو هغوى خپلې رسۍ او امساګانې وغورځولې او ويې ويل:د فرعون په عزت سره، بېشكه مونږ، خامخا همدا مونږ غالب یو');
INSERT INTO "abulsalam" VALUES(26,45,'26:45',' نو موسٰى خپله امسا وغورځوله، نو ناڅاپه هغې هغه څه ښوى تېرول چې دوى په دروغه جوړ كړي وو');
INSERT INTO "abulsalam" VALUES(26,46,'26:46',' نو جادوګر وغورځول شول (پړمخې)، چې سجده كوونكي وو');
INSERT INTO "abulsalam" VALUES(26,47,'26:47',' ويې ويل:مونږ په رب العلمین ایمان راوړ');
INSERT INTO "abulsalam" VALUES(26,48,'26:48',' چې د موسٰى او هارون رب دى');
INSERT INTO "abulsalam" VALUES(26,49,'26:49',' (فرعون) وویل: تاسو په ده ایمان راوړ مخكې له دې نه چې زه تاسو ته اجازت دركړم، بېشكه دا یقینًا ستاسو هغه مشر دى چې تاسو ته يې جادو ښودلى دى، نو یقینًا تاسو به ژر پوه شئ، خامخا هرومرو به زه ستاسو لاسونه او ستاسو پښې، ادل بدل پرې كړم، او زه به خامخا هرومرو تاسو ټول په دار كړم');
INSERT INTO "abulsalam" VALUES(26,50,'26:50',' هغوى وویل: هېڅ ضرر نشته، بېشكه مونږ خپل رب ته بېرته ورتلونكي یو');
INSERT INTO "abulsalam" VALUES(26,51,'26:51',' بېشكه مونږ دا طمع لرو چې زمونږ رب به مونږ ته زمونږ ګناهونه وبخښي، په دې چې مونږ (په دې حاضرینو كې) اول د ایمان راوړونكو یو');
INSERT INTO "abulsalam" VALUES(26,52,'26:52',' او مونږ موسٰى ته وحي وكړه داسې چې ته زما بنده ګان د شپې بوځه، بېشكه تاسو پسې به درتلل كېدى شي');
INSERT INTO "abulsalam" VALUES(26,53,'26:53',' نو فرعون په ښارونو كې راټولوونكي ولېږل');
INSERT INTO "abulsalam" VALUES(26,54,'26:54',' (او ويې ویل) بېشكه دغه (بني اسرائیل) ډېره لږه وړه ډله ده');
INSERT INTO "abulsalam" VALUES(26,55,'26:55',' او بېشكه دوى مونږ لره یقینًا غصه كوونكي دي');
INSERT INTO "abulsalam" VALUES(26,56,'26:56',' او بېشكه مونږ خامخا بيداره ډله یو');
INSERT INTO "abulsalam" VALUES(26,57,'26:57',' نو مونږه دوى (فرعونیان) له باغونو او چینو نه راوښكل');
INSERT INTO "abulsalam" VALUES(26,58,'26:58',' او له خزانو او ښو ځایونو نه');
INSERT INTO "abulsalam" VALUES(26,59,'26:59',' همداسې (مونږ وكړل) او مونږ د دغو (څیزونو) وارثان بني اسرائیل وګرځول');
INSERT INTO "abulsalam" VALUES(26,60,'26:60',' نو (فرعونیان) د دوى پسې شول، چې د لمر ختو په وخت كې داخلېدونكي وو');
INSERT INTO "abulsalam" VALUES(26,61,'26:61',' نو كله چې دواړو ډلو یو بل سره ولیدل، د موسٰى ملګرو وویل: بېشكه مونږ خامخا نیول كېدونكي یو');
INSERT INTO "abulsalam" VALUES(26,62,'26:62',' (موسٰى) وویل: هیڅكله داسې نشي كېدى، بېشكه زما سره زما رب دى، هغه به ضرور زما لار ښوونه كوي');
INSERT INTO "abulsalam" VALUES(26,63,'26:63',' نو مونږ موسٰى ته وحي وكړه چې ته په خپلې امسا سره سمندر ووهه، نو هغه څیرې شو، نو هره برخه د ډېر اوچت غر په شان شوله');
INSERT INTO "abulsalam" VALUES(26,64,'26:64',' او دغه ځاى ته مونږ هغه نور ښه رانژدې كړل');
INSERT INTO "abulsalam" VALUES(26,65,'26:65',' اومونږ موسٰى او هغو كسانو ته چې له هغه سره وو ؛ ټولو ته نجات وركړ');
INSERT INTO "abulsalam" VALUES(26,66,'26:66',' بیا مونږ هغه نور غرق (ډوب) كړل');
INSERT INTO "abulsalam" VALUES(26,67,'26:67',' بېشكه په دې كې خامخا لویه نښه ده، او د دوى زیاتره ایمان راوړونكي نه وو');
INSERT INTO "abulsalam" VALUES(26,68,'26:68',' او بېشكه رب ستا خامخا هم هغه ښه غالب، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(26,69,'26:69',' او ته دوى ته د ابراهیم خبر (قصه) ولوله');
INSERT INTO "abulsalam" VALUES(26,70,'26:70',' كله چې هغه خپل پلار او خپل قوم ته وویل: تاسو د څه څیز عبادت كوئ');
INSERT INTO "abulsalam" VALUES(26,71,'26:71',' هغوى وویل: مونږ د بتانو عبادت كوو، پس همېشه به دوى ته په عبادت قايم اوسو');
INSERT INTO "abulsalam" VALUES(26,72,'26:72',' هغه وویل: ایا دوى ستاسو اوري كله چې تاسو (دوى) بلئ؟');
INSERT INTO "abulsalam" VALUES(26,73,'26:73',' یا تاسو ته نفع دررسوي، یا ضرر دررسوي؟');
INSERT INTO "abulsalam" VALUES(26,74,'26:74',' هغوى وویل: بلكې مونږ خپل پلرونه موندلي دي چې همداسې به يې كول');
INSERT INTO "abulsalam" VALUES(26,75,'26:75',' هغه وویل: ایا تاسو كتلي دي (او پوه شوي يئ په) هغه چې تاسو يې عبادت كوئ');
INSERT INTO "abulsalam" VALUES(26,76,'26:76',' تاسو او ستاسو پخوا تېر شوي پلرونه');
INSERT INTO "abulsalam" VALUES(26,77,'26:77',' پس بېشكه دوى (ستاسو معبودان) زما دښمنان دي غیر له رب العلمین نه');
INSERT INTO "abulsalam" VALUES(26,78,'26:78',' هغه چې زه يې پیدا كړى یم، نو هغه زما لار ښوونه كوي');
INSERT INTO "abulsalam" VALUES(26,79,'26:79',' او هغه چې همغه په ما خوراك كوي او په ما څښاك كوي');
INSERT INTO "abulsalam" VALUES(26,80,'26:80',' او كله چې زه ناروغه شم، نو هغه ما ته شفا راكوي');
INSERT INTO "abulsalam" VALUES(26,81,'26:81',' او هغه چې ما ته به مرګ راكوي، بیا به ما ژوندى كوي');
INSERT INTO "abulsalam" VALUES(26,82,'26:82',' او هغه چې زه طمع لرم دا چې د قیامت په ورځ به هغه ما ته زما ګناه وبخښي');
INSERT INTO "abulsalam" VALUES(26,83,'26:83',' اى زما ربه! ما ته حكم (علم وحكمت) راكړه او ما له نېكانو سره پیوست كړه');
INSERT INTO "abulsalam" VALUES(26,84,'26:84',' او زما لپاره په وروستنو خلقو كې د صدق ژبه (نېكنامي) وګرځوه');
INSERT INTO "abulsalam" VALUES(26,85,'26:85',' او ما د نعمتونو د جنت له وارثانو ځنې وګرځوه');
INSERT INTO "abulsalam" VALUES(26,86,'26:86',' او زما پلار ته بخښنه وكړه، یقینًا هغه له ګمراهانو ځنې و');
INSERT INTO "abulsalam" VALUES(26,87,'26:87',' او ته ما مه شرموه په هغې ورځ كې چې خلق به بیا ژوندي كولى شي');
INSERT INTO "abulsalam" VALUES(26,88,'26:88',' په هغې ورځ چې نه به مال نفع رسوي او نه زامن');
INSERT INTO "abulsalam" VALUES(26,89,'26:89',' مګر هغه چا ته چې الله ته په ډېر سلامت زړه راغى');
INSERT INTO "abulsalam" VALUES(26,90,'26:90',' او جنت به(په هغې ورځ كې) پرهېزګارانو ته ورنژدې كړى شي');
INSERT INTO "abulsalam" VALUES(26,91,'26:91',' او جهنم به ګمراهانو ته ښه ښكاره كړى شي');
INSERT INTO "abulsalam" VALUES(26,92,'26:92',' او دوى ته به وویل شي: چېرته دي هغه (معبودان) چې تاسو به يې عبادت كاوه');
INSERT INTO "abulsalam" VALUES(26,93,'26:93',' له الله نه غیر، ایا دوى ستاسو مدد كوي، یا دوى بدله اخيستى شي؟');
INSERT INTO "abulsalam" VALUES(26,94,'26:94',' نو دا (بتان) به په دغه (جهنم) كې نسكور وغورځول شي دوى او ګمراهان هم');
INSERT INTO "abulsalam" VALUES(26,95,'26:95',' او د ابلیس لښكرې، دوى ټول');
INSERT INTO "abulsalam" VALUES(26,96,'26:96',' دوى به ووايي، په داسې حال كې چې دوى به سره جګړې كوي:');
INSERT INTO "abulsalam" VALUES(26,97,'26:97',' قسم په الله، یقینًا مونږه خامخا په ښكاره ګمراهۍ كې وو');
INSERT INTO "abulsalam" VALUES(26,98,'26:98',' كله چې مونږ تاسو له رب العلمین سره برابرولئ ');
INSERT INTO "abulsalam" VALUES(26,99,'26:99',' او مونږ نه وو ګمراه كړي مګر مجرمانو (بدو مشرانو)');
INSERT INTO "abulsalam" VALUES(26,100,'26:100',' نو زمونږ لپاره هیڅوك شفاعت كوونكي نشته');
INSERT INTO "abulsalam" VALUES(26,101,'26:101',' او نه كوم مخلص دوست');
INSERT INTO "abulsalam" VALUES(26,102,'26:102',' نو ارمان كه په رښتیا زمونږ لپاره یو ځل دنیا ته واپسي وى، نو چې شوي وى مونږ له مومنانو نه');
INSERT INTO "abulsalam" VALUES(26,103,'26:103',' بېشكه په دې كې خامخا لویه نښه ده او د دوى زیاتره مومنان نه وو');
INSERT INTO "abulsalam" VALUES(26,104,'26:104',' او بېشكه ستا رب، خامخا همغه ډېر غالب، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(26,105,'26:105',' د نوح قوم د رسولانو تكذیب وكړ');
INSERT INTO "abulsalam" VALUES(26,106,'26:106',' كله چې دوى ته د دوى ورور نوح وویل: ایا تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(26,107,'26:107',' بېشكه زه تاسو ته امانتګر رسول یم');
INSERT INTO "abulsalam" VALUES(26,108,'26:108',' نو تاسو له الله نه ووېرېږئ او زما اطاعت وكړئ');
INSERT INTO "abulsalam" VALUES(26,109,'26:109',' او زه له تاسو نه په دې (تبلیغ) باندې هېڅ اجر (عوض) نه غواړم، نه دى اجر زما مګر په رب العلمین باندې');
INSERT INTO "abulsalam" VALUES(26,110,'26:110',' نو تاسو له الله نه ووېرېږئ او زما اطاعت وكړئ');
INSERT INTO "abulsalam" VALUES(26,111,'26:111',' هغوى وویل: ایا مونږ په تا ایمان راوړو!، حال دا چې ستا پیروي تر ټولو رذیلو خوارو خلقو كړې ده');
INSERT INTO "abulsalam" VALUES(26,112,'26:112',' (نوح) وویل: او ما ته څه علم دى په هغه څه چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(26,113,'26:113',' نه دى د دوى حساب مګر زما په رب باندې، كه تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(26,114,'26:114',' او زه له سره دې مومنانو لره شړونكى نه یم');
INSERT INTO "abulsalam" VALUES(26,115,'26:115',' زه نه یم مګر ښكاره وېروونكى');
INSERT INTO "abulsalam" VALUES(26,116,'26:116',' دوى وویل: اى نوحه! قسم دى كه ته وانه وښتې (نو) خامخا هرومرو به ته له سنګسار كړى شويو ځنې شې');
INSERT INTO "abulsalam" VALUES(26,117,'26:117',' هغه وویل: اى زما ربه! بېشكه زما قوم زما تكذیب وكړ');
INSERT INTO "abulsalam" VALUES(26,118,'26:118',' نو ته زما په مینځ كې او د دوى په مینځ كې فیصله وكړه واضحه فیصله او ته ما ته نجات راكړه او هغه چا ته چې له ما سره كوم مومنان دي');
INSERT INTO "abulsalam" VALUES(26,119,'26:119',' نو مونږ نجات وركړ هغه ته او هغه چا ته چې له ده سره په ډكه كشتۍ كې وو');
INSERT INTO "abulsalam" VALUES(26,120,'26:120',' بیا مونږ له دې نه پس باقي خلق غرق (ډوب) كړل');
INSERT INTO "abulsalam" VALUES(26,121,'26:121',' بېشكه په دې كې خامخا لویه نښه ده او د دوى زیاتره مومنان نه وو');
INSERT INTO "abulsalam" VALUES(26,122,'26:122',' او بېشكه ستا رب خامخا همغه ډېر غالب، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(26,123,'26:123',' عادیانو د رسولانو تكذیب وكړ');
INSERT INTO "abulsalam" VALUES(26,124,'26:124',' كله چې دوى ته د دوى ورور هود وویل: ایا تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(26,125,'26:125',' بېشكه زه تاسو ته امانتګر رسول یم');
INSERT INTO "abulsalam" VALUES(26,126,'26:126',' نو تاسو له الله نه ووېرېږئ او زما اطاعت وكړئ');
INSERT INTO "abulsalam" VALUES(26,127,'26:127',' او زه له تاسو نه په دې (تبلیغ) باندې هېڅ اجر (عوض) نه غواړم، زمااجر نه دى مګر په رب العلمین باندې');
INSERT INTO "abulsalam" VALUES(26,128,'26:128',' ایا تاسو په هر اوچت ځاى یوه نښه (لوړه ودانۍ) جوړوئ، په داسې حال كې چې عبثیات كوئ!');
INSERT INTO "abulsalam" VALUES(26,129,'26:129',' او تاسو پاخه عمارتونه جوړوئ، ښايي چې تاسو به (په دنیا كې) تل اوسېږئ');
INSERT INTO "abulsalam" VALUES(26,130,'26:130',' او كله چې تاسو نیول كوئ (،نو) نیول كوئ، په داسې حال كې چې ظلم كوونكي يئ');
INSERT INTO "abulsalam" VALUES(26,131,'26:131',' نو تاسو له الله نه ووېږئ او زما اطاعت وكړئ');
INSERT INTO "abulsalam" VALUES(26,132,'26:132',' او تاسو له هغه ذات نه ووېرېږئ چې ستاسو مدد يې كړى دى په هغه څه سره چې تاسو پرې پوه یئ');
INSERT INTO "abulsalam" VALUES(26,133,'26:133',' هغه ستاسو مدد كړى دى په څارویو او زامنو سره');
INSERT INTO "abulsalam" VALUES(26,134,'26:134',' او باغونو او چینو سره');
INSERT INTO "abulsalam" VALUES(26,135,'26:135',' بېشكه زه پر تاسو باندې د ډېرې لويې ورځې د عذاب وېره كوم');
INSERT INTO "abulsalam" VALUES(26,136,'26:136',' هغوى وویل: مونږ ته یو شان ده، ایا كه ته نصیحت (وعظ) كوې، یا ته نه كېږې له وعظ كوونكو نه');
INSERT INTO "abulsalam" VALUES(26,137,'26:137',' نه دى دا ستا (وعظ كول) مګر عادت د پخوانیو خلقو');
INSERT INTO "abulsalam" VALUES(26,138,'26:138',' او مونږ له سره په عذاب كې مبتلا كېدونكي نه يو');
INSERT INTO "abulsalam" VALUES(26,139,'26:139',' نو دوى د هغه تكذیب وكړ، نو مونږه دوى هلاك كړل، بېشكه په دې كې خامخا لویه نښه ده او د دوى اكثره مومنان نه وو');
INSERT INTO "abulsalam" VALUES(26,140,'26:140',' او بېشكه ستا رب خامخا همغه ښه غالب، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(26,141,'26:141',' ثمودیانو د رسولانو تكذیب وكړ');
INSERT INTO "abulsalam" VALUES(26,142,'26:142',' كله چې دوى ته د دوى ورور صالح وویل: ایا تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(26,143,'26:143',' بېشكه زه تاسو ته امانتګر رسول یم');
INSERT INTO "abulsalam" VALUES(26,144,'26:144',' نو تاسو له الله نه ووېرېږئ او زما اطاعت وكړئ');
INSERT INTO "abulsalam" VALUES(26,145,'26:145',' او زه له تاسو نه په دې (تبلیغ) باندې هېڅ اجر نه غواړم، زما اجر نه دى مګر په رب العلمین باندې');
INSERT INTO "abulsalam" VALUES(26,146,'26:146',' ایا تاسو به په پرېښودل شئ په هغو څیزونو كې چې دلته دي، په امن (او بې غمۍ) سره');
INSERT INTO "abulsalam" VALUES(26,147,'26:147',' په باغونو او چینو كې');
INSERT INTO "abulsalam" VALUES(26,148,'26:148',' او په فصلونو او داسې كجورو كې چې د هغو وږي (شګوفې) ډېر نرمې پستې وي');
INSERT INTO "abulsalam" VALUES(26,149,'26:149',' او تاسو په غرونو كې كورونه توږئ (كنئ)، په داسې حال كې چې ښه ماهران یئ');
INSERT INTO "abulsalam" VALUES(26,150,'26:150',' نو تاسو له الله نه ووېرېږئ، او زما اطاعت وكړئ ');
INSERT INTO "abulsalam" VALUES(26,151,'26:151',' او تاسو د اسراف كوونكو (له حد نه تېرېدونكو كافرانو) حكم مه منئ');
INSERT INTO "abulsalam" VALUES(26,152,'26:152',' هغه چې په ځمكه كې فساد كوي او اصلاح نه كوي');
INSERT INTO "abulsalam" VALUES(26,153,'26:153',' هغوى وویل: بېشكه همدا خبره ده چې ته له جادو كړى شویو كسانو ځنې يې');
INSERT INTO "abulsalam" VALUES(26,154,'26:154',' ته نه يې مګر زمونږ په مثل بشر، نو ته كومه معجزه راوړه! كه ته له رښتینو ځنې يې');
INSERT INTO "abulsalam" VALUES(26,155,'26:155',' او هغه وویل: دا اوښه ده، د دې لپاره د اوبو وار دى او تاسو لپاره د مقررې ورځې د اوبو وار دى');
INSERT INTO "abulsalam" VALUES(26,156,'26:156',' او تاسو دې ته هېڅ تكلیف مه رسوئ، (ګنې) نو تاسو به د ډېرې لويې ورځې عذاب ونیسي ');
INSERT INTO "abulsalam" VALUES(26,157,'26:157',' نو دوى د هغې پړكي پرې كړل، بیا دوى پښېمانه شول');
INSERT INTO "abulsalam" VALUES(26,158,'26:158',' نو دوى عذاب ونیول، بېشكه په دې كې خامخا لویه نښه ده او د دوى اكثره مومنان نه وو');
INSERT INTO "abulsalam" VALUES(26,159,'26:159',' او بېشكه ستا رب خامخا همغه ډېر غالب، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(26,160,'26:160',' د لوط قوم د رسولانو تكذیب وكړ ');
INSERT INTO "abulsalam" VALUES(26,161,'26:161',' كله چې دوى ته د دوى ورور لوط وویل: ایا تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(26,162,'26:162',' بېشكه زه تاسو ته امانتګر رسول یم');
INSERT INTO "abulsalam" VALUES(26,163,'26:163',' نو تاسو له الله نه ووېرېږئ او زما اطاعت وكړئ ');
INSERT INTO "abulsalam" VALUES(26,164,'26:164',' او زه له تاسو نه په دې (تبلیغ) باندې هېڅ اجر (عوض) نه غواړم، زما اجر نه دى مګر په رب العلمین باندې');
INSERT INTO "abulsalam" VALUES(26,165,'26:165',' ایا په مخلوقاتو كې تاسو له سړیو سره بدكاري كوئ');
INSERT INTO "abulsalam" VALUES(26,166,'26:166',' او تاسو پرېږدئ هغه څه چې تاسو لپاره ستاسو رب پیدا كړي دي، یعنې ستاسو ښځې، بلكې تاسو له حده تېرېدونكي خلق یئ');
INSERT INTO "abulsalam" VALUES(26,167,'26:167',' دوى وویل: قسم دى كه ته منع نه شوې اى لوطه! (نو) خامخا هرومرو به ته له اېستل شویو ځنې شې');
INSERT INTO "abulsalam" VALUES(26,168,'26:168',' هغه وویل: بېشكه زه ستاسو له عمل سره له سخت بغض لرونكو ځنې یم');
INSERT INTO "abulsalam" VALUES(26,169,'26:169',' اى زما ربه! ته ما ته او زما اهل ته نجات راكړې له هغو كارونو نه چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(26,170,'26:170',' نو مونږ هغه ته او د هغه اهل ټولو ته نجات وركړ');
INSERT INTO "abulsalam" VALUES(26,171,'26:171',' غیر له یوې بوډۍ نه چې په باقي پاتې كېدونكو كې وه');
INSERT INTO "abulsalam" VALUES(26,172,'26:172',' بیا مونږ هغه نور هلاك كړل');
INSERT INTO "abulsalam" VALUES(26,173,'26:173',' او په دوى باندې مونږ باران ووراوه، عجیب (د كاڼو) باران، نو بد باران و د وېرول شویو (خلقو) ');
INSERT INTO "abulsalam" VALUES(26,174,'26:174',' بېشكه په دې كې خامخا لویه نښه ده، او د دوى زیاتره مومنان نه وو');
INSERT INTO "abulsalam" VALUES(26,175,'26:175',' او بېشكه ستا رب خامخا همغه ډېر غالب، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(26,176,'26:176',' د ونو ځنګل والاو د رسولانو تكذیب وكړ');
INSERT INTO "abulsalam" VALUES(26,177,'26:177',' كله چې دوى ته شعیب وویل: ایا تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(26,178,'26:178',' بېشكه زه تاسو ته امانتګر رسول یم');
INSERT INTO "abulsalam" VALUES(26,179,'26:179',' نو تاسو له الله نه ووېرېږئ او زما اطاعت وكړئ');
INSERT INTO "abulsalam" VALUES(26,180,'26:180',' او زه له تاسو نه په دې (تبلیغ) باندې هېڅ اجر نه غواړم، زما اجر نه دى مګر په رب العلمین باندې');
INSERT INTO "abulsalam" VALUES(26,181,'26:181',' تاسو پېمانه پوره وركوئ او تاسو له كم وركونكو څخه مه كېږئ');
INSERT INTO "abulsalam" VALUES(26,182,'26:182',' او تاسو په برابرې تلې سره تول كوئ');
INSERT INTO "abulsalam" VALUES(26,183,'26:183',' او تاسو خلقو ته د هغوى څیزونه مه كموئ، او مه ګرځئ په وراني سره په ځمكه كې، په دې حال چې فساد كوونكي یئ');
INSERT INTO "abulsalam" VALUES(26,184,'26:184',' او تاسو له هغه ذات نه ووېرېږئ چې تاسو او ړومبني خلق يې پیدا كړي دي');
INSERT INTO "abulsalam" VALUES(26,185,'26:185',' هغوى وویل: بېشكه همدا خبره ده چې ته له جادو كړى شویو ځنې يې');
INSERT INTO "abulsalam" VALUES(26,186,'26:186',' او ته نه يې مګر زمونږ په شان بشر، او یقینًا مونږ خامخا تا له دروغ ویونكو څخه ګڼو');
INSERT INTO "abulsalam" VALUES(26,187,'26:187',' نو ته پر مونږ باندې له اسمانه ټوټې راوغورځوه، كه ته له رښتینو ځنې يې');
INSERT INTO "abulsalam" VALUES(26,188,'26:188',' هغه وویل: زما رب ښه عالم دى په هغو كارونو چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(26,189,'26:189',' نو دوى د هغه تكذیب وكړ، پس دوى د سیوري د ورځې عذاب ونیول، بېشكه دغه (عذاب) د ډېرې لويې ورځې عذاب و');
INSERT INTO "abulsalam" VALUES(26,190,'26:190',' بېشكه په دې كې خامخا لویه نښه ده، او د دوى زیاتره مومنان نه وو');
INSERT INTO "abulsalam" VALUES(26,191,'26:191',' او بېشكه ستا رب خامخا همغه ډېر غالب، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(26,192,'26:192',' او بېشكه دا (قرآن) د رب العلمین (له جانبه) نازل كړى شوى دى');
INSERT INTO "abulsalam" VALUES(26,193,'26:193',' دا جبریل امین راكوز كړى دى');
INSERT INTO "abulsalam" VALUES(26,194,'26:194',' ستا په زړه باندې، د دې لپاره چې ته له وېروونكو څخه شې');
INSERT INTO "abulsalam" VALUES(26,195,'26:195',' په ښكاره عربي ژبه كې');
INSERT INTO "abulsalam" VALUES(26,196,'26:196',' او بېشكه دا (قرآن، یعنې د ده ذكر) د ړومبنیو خلقو په كتابونو كې دى');
INSERT INTO "abulsalam" VALUES(26,197,'26:197',' ایا دغه (كفارو) ته (د قرآن د حقانیت) دا لویه نښه نه ده چې د بني اسرائیلو عالمان په ده پوهېږي؟');
INSERT INTO "abulsalam" VALUES(26,198,'26:198',' او كه مونږ دا (قرآن) په عجمیانو كې په چا نازل كړى وى');
INSERT INTO "abulsalam" VALUES(26,199,'26:199',' بيا هغه دا (قرآن) دوى ته لوستلى وى، دوى په ده باندې ایمان راوړونكي نه وو');
INSERT INTO "abulsalam" VALUES(26,200,'26:200',' همداسې مونږ دا (د قرآن تكذیب) د مجرمانو په زړونو كې ننه ایستلى دى');
INSERT INTO "abulsalam" VALUES(26,201,'26:201',' دوى په ده ایمان نه راوړي تر هغه پورې چې دوى ډېر دردوونكى عذاب وویني');
INSERT INTO "abulsalam" VALUES(26,202,'26:202',' نو هغه (عذاب) به په دوى ناڅاپه راشي، په داسې حال كې چې دوى نه پوهېږي');
INSERT INTO "abulsalam" VALUES(26,203,'26:203',' بیا به دوى وايي: ایا مونږ ته به مهلت راكولى شي؟');
INSERT INTO "abulsalam" VALUES(26,204,'26:204',' ایا نو دوى زمونږ همدا عذاب ژر غواړي');
INSERT INTO "abulsalam" VALUES(26,205,'26:205',' نو ته ما ته خبر راكړه كه مونږ دوى ته څو كاله نفع وركړو');
INSERT INTO "abulsalam" VALUES(26,206,'26:206',' بیا په دوى هغه (عذاب) راشي چې دوى سره به يې وعده كولى شوه');
INSERT INTO "abulsalam" VALUES(26,207,'26:207',' (نو) د دوى به هېڅ په كار رانشي هغه څه چې دوى ته به (په هغو سره) نفع وركولى شوه');
INSERT INTO "abulsalam" VALUES(26,208,'26:208',' او مونږ هېڅ یو كلى نه دى هلاك كړى مګر د هغه لپاره وېروونكي وو');
INSERT INTO "abulsalam" VALUES(26,209,'26:209',' د نصیحت كولو لپاره، اومونږ ظالمان نه وو');
INSERT INTO "abulsalam" VALUES(26,210,'26:210',' او دا (قرآن) شیطانانو نه دى راكوز كړى');
INSERT INTO "abulsalam" VALUES(26,211,'26:211',' او نه دا (كار) د هغوى لپاره صحیح دى او نه دوى (د دې كار) طاقت لري');
INSERT INTO "abulsalam" VALUES(26,212,'26:212',' بېشكه دوى (شیطانان) له اورېدلو نه خامخا منع كړى شوي (لرې كړى شوي) دي');
INSERT INTO "abulsalam" VALUES(26,213,'26:213',' نو ته له الله سره بل هېڅ معبود مه بله، (ګنې) نو شې به ته له عذاب وركړى شویو نه');
INSERT INTO "abulsalam" VALUES(26,214,'26:214',' او ته خپل ډېر نژدې خپلوان ووېروه ');
INSERT INTO "abulsalam" VALUES(26,215,'26:215',' او ته خپل وزر ښكته كړه د هغه چا لپاره چې ستا پیروي يې كړې ده، چې مومنان دي');
INSERT INTO "abulsalam" VALUES(26,216,'26:216',' نو كه دغو (خپلوانو) ستا نافرماني وكړه، نو ته (ورته) ووایه: بېشكه زه د هغو كارونو نه بېزاره یم چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(26,217,'26:217',' او ته توكل وكړه په ډېر غالب، بې حده رحم كوونكي ذات باندې ');
INSERT INTO "abulsalam" VALUES(26,218,'26:218',' هغه چې تا ویني كله چې ته پاڅېږې (لمانځه ته)');
INSERT INTO "abulsalam" VALUES(26,219,'26:219',' او په سجده كوونكو كې ستا اوښتل (ویني) ');
INSERT INTO "abulsalam" VALUES(26,220,'26:220',' بېشكه هغه، هم هغه ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(26,221,'26:221',' ایا زه تاسو ته خبر دركړم چې په چا باندې شیطانان راكوزېږي؟');
INSERT INTO "abulsalam" VALUES(26,222,'26:222',' دوى په هر ډېر دروغجن، سخت مجرم باندې راكوزېږي');
INSERT INTO "abulsalam" VALUES(26,223,'26:223',' دوى غوږ ږدي (د ملايكو خبرو ته) او د دوى زیاتره دروغجنان دي');
INSERT INTO "abulsalam" VALUES(26,224,'26:224',' او شاعران (چې دي) د دوى پیروي ګمراه خلق كوي');
INSERT INTO "abulsalam" VALUES(26,225,'26:225',' ایا ته نه وینې چې بېشكه دوى په هره وادۍ (ناو) كې سرګردانه ګرځي');
INSERT INTO "abulsalam" VALUES(26,226,'26:226',' او بېشكه دوى وايي هغه څه چې دوى يې (پخپله) نه كوي');
INSERT INTO "abulsalam" VALUES(26,227,'26:227',' غیر له هغو كسانو نه چې ایمان راوړي او نېك عملونه كوي او الله ډېر زیات یادوي او بدله اخلي كله چې پر دوى ظلم وكړى شي، او هغه كسان چې ظلم يې كړى دى ژر به پوه شي چې د بېرته ورتلو كوم ځاى ته به دوى بېرته ورځي');
INSERT INTO "abulsalam" VALUES(27,1,'27:1',' طا، سين، دا د قرآن او څرګند (ښكاره بیانوونكي) كتاب ایتونه دي');
INSERT INTO "abulsalam" VALUES(27,2,'27:2',' هدایت او زېرى دى، د مومنانو لپاره');
INSERT INTO "abulsalam" VALUES(27,3,'27:3',' هغه چې لمونځ قايموي او زكات وركوي او هم دوى په اخرت یقین كوي');
INSERT INTO "abulsalam" VALUES(27,4,'27:4',' بېشكه هغه كسان چې په اخرت ایمان نه راوړي، دوى ته مونږ د دوى عملونه ښايسته كړي دي، نو دوى سرګردانه دي');
INSERT INTO "abulsalam" VALUES(27,5,'27:5',' دغه كسان دي چې د دوى لپاره بد عذاب دى، او دوى به په اخرت كې هم دوى تر ټولو تاوانیان وي');
INSERT INTO "abulsalam" VALUES(27,6,'27:6',' او بېشكه ته (چې يې) خامخا تا ته د ډېر حكمت والا، ښه علم والا (الله) له جانبه قرآن ښوولى (او عطا كولى) شي');
INSERT INTO "abulsalam" VALUES(27,7,'27:7',' (یاد كړه) هغه وخت چې موسٰى خپلې ښځې ته وویل: بېشكه ما اور ولیده، زه به تاسو ته له دغه (ځاى) نه څه خبر راوړم، یا به تاسو ته بله كړى شوې لمبه راوړم، د دې لپاره چې تاسو تاوده شئ ');
INSERT INTO "abulsalam" VALUES(27,8,'27:8',' نو كله چې دى هغه (اور) ته راغى، اواز وكړى شو، داسې چې بركتي كړى شوى دى هغه چې په اور كې دى او هغه چې د ده (اور) چاپېره دى او پاكي ده الله ته چې رب العلمین دى');
INSERT INTO "abulsalam" VALUES(27,9,'27:9',' اى موسٰى! بېشكه شان دا دى چې هم زه الله یم، ښه غالب، ښه حكمت والا');
INSERT INTO "abulsalam" VALUES(27,10,'27:10',' او ته خپله امسا وغورځوه، نو كله چې هغه دا ولیدله چې خوځېږي، ګویا كې دغه (همسا) نرى سپین تېز مار دى، نو وتېښتېده چې شاكوونكى و او بېرته راونه ګرځېده، اى موسٰى، مه وېرېږه، بېشكه زه چې یم، له ما سره رسولان نه وېرېږي');
INSERT INTO "abulsalam" VALUES(27,11,'27:11',' خو هغه څوك چې ظلم يې كړى وي، بیا له بدۍ نه پس نېكي په بدل كې راوړي، نو بېشكه زه ډېر بخښونكى، بې حده رحم كوونكى یم');
INSERT INTO "abulsalam" VALUES(27,12,'27:12',' او ته خپل لاس په خپل ګرېوان كې ننباسه، (نو) له بيمارۍ (او عیب) نه بغیر به تك سپین راووځي، (دا حساب دي) په نهو معجزو كې (چې ته ورسره لېږل شوى يې) فرعون او د هغه قوم ته، بېشكه دوى یو فاسق (نافرمانه) قوم دى');
INSERT INTO "abulsalam" VALUES(27,13,'27:13',' نو كله چې دوى ته زمونږ نښې (معجزې) راغلې، چې روښانه وې، (نو) دوى وویل: دا ښكاره جادو دى ');
INSERT INTO "abulsalam" VALUES(27,14,'27:14',' او دوى له دغو (معجزو) نه انكار وكړ، د ظلم او سركشۍ په وجه، حال دا چې د دوى نفسونو په دغو یقین كړى و، نو ته وګوره د فساد كوونكو انجام څنګه و؟');
INSERT INTO "abulsalam" VALUES(27,15,'27:15',' او یقینًا یقینًا مونږ داود او سلیمان ته (خاص) علم وركړى و او هغو دواړو وویل: ټوله ستاينه خاص د هغه الله لپاره ده چې مونږ ته يې په خپلو مومنو بنده ګانو كې په ډېرو زياتو باندې فضيلت راكړى دى');
INSERT INTO "abulsalam" VALUES(27,16,'27:16',' او سلیمان د داود (په علم، نبوت او سلطنت كې) وارث شو او هغه وویل: اى خلقو! مونږ ته د الوتونكو (مرغانو) خبرې ښوول شوې دي او مونږ ته له هر څیز نه راكړه شوې ده، بېشكه دا، خامخا هم دغه ښكاره فضل دى');
INSERT INTO "abulsalam" VALUES(27,17,'27:17',' او د سلیمان لپاره د هغه لښكرې راجمع كړى شوې، چې له پېریانو او انسانانو او مرغانو څخه وې، بیا به د دوى (په ترتیب سره) ډلې جوړولى شوې');
INSERT INTO "abulsalam" VALUES(27,18,'27:18',' تر هغه پورې چې كله دوى د مېږانو په دره راغلل، (نو) یوې مېږۍ وویل: اى مېږانو تاسو خپلو استوګنو ته ننوځئ، چې تاسو سلیمان او د هغه لښكرې مات او چیت پیت نه كړي، په داسې حال كې چې دوى به نه پوهېږي');
INSERT INTO "abulsalam" VALUES(27,19,'27:19',' نو دى د دې په خبره (اول) مسكى شو، چې خندا كوونكى و (ورپسې د خوشحالۍ په وجه) ويې ويل:اى زما ربه! ته ما ته الهام او توفیق راكړه چې زه ستا د هغو نعمتونو شكر وباسم چې تا په ما او زما په مور و پلار انعام (او پېرزو) كړي دي او دا چې زه داسې نېك عمل وكړم چې ته يې خوښوې، او ته ما په خپل رحمت سره په خپلو نېكو بنده ګانو كې داخل كړه');
INSERT INTO "abulsalam" VALUES(27,20,'27:20',' او د مرغانو لټونه يې وكړه، نو ويې ويل: ما له څه دي چې زه هدهد (ملاچرګك) نه وینم، بلكې كه هغه له غايب شویو ځنې دى؟');
INSERT INTO "abulsalam" VALUES(27,21,'27:21',' خامخا هرومرو به زه ده ته سزا وركړم، ډېره سخته سزا، یا به خامخا ضرور دى حلال كړم، یا به ضرور هرومرو ما ته ښكاره دلیل راوړي');
INSERT INTO "abulsalam" VALUES(27,22,'27:22',' نو هغه (هدهد) ایسار شو، خو ډېر نه، (او ژر راغى) نو ويې ويل:ما هغه خبره (په علم كې) ګېره كړې ده چې تا هغه ګېره كړې نه ده او زه تا ته له سبا (ښار) نه په یقیني خبر سره راغلى یم ');
INSERT INTO "abulsalam" VALUES(27,23,'27:23',' بېشكه ما یوه داسې ښځه وموندله چې په دغو (د سبا ښار په اوسېدونكو) بادشاهي كوي او هغې ته له هر څیز څخه وركړه شوې ده او د هغې یو ډېر لوى تخت دى');
INSERT INTO "abulsalam" VALUES(27,24,'27:24',' ما هغه او د هغې قوم ومونده چې له الله نه غیر لمر ته سجده كوي او دوى ته شیطان خپل عملونه ښايسته كړي دي، نو دوى يې له (حقې) لارې نه منع كړي دي، نو دوى هدایت نه مومي');
INSERT INTO "abulsalam" VALUES(27,25,'27:25',' چې دوى الله ته سجده ونه كړي، هغه چې په اسمانونو او ځمكه كې پټ څیزونه راوباسي او پوهېږي په هغه څه چې تاسو يې پټوئ او په هغه څه چې تاسو يې څرګندوئ');
INSERT INTO "abulsalam" VALUES(27,26,'27:26',' الله چې دى، نشته هېڅ لايق د عبادت مګر هم دى دى، د ډېر ستر عرش رب دى ');
INSERT INTO "abulsalam" VALUES(27,27,'27:27',' (سلیمان) وویل: ضرور به مونږ ګورو چې ایا تا رښتیا ویلي دي، یا ته له دروغ ویونكو ځنې وې؟');
INSERT INTO "abulsalam" VALUES(27,28,'27:28',' ته زما دا لیك یوسه، نو ته دا دوى ته وروغورځوه، بیا ته له دوى نه مخ وګرځوه، نو وګوره چې دوى څه واپس كوي؟');
INSERT INTO "abulsalam" VALUES(27,29,'27:29',' هغې وویل: اى سردارانو! بېشكه زه چې یم، ما ته یو ډېر عزتناك لیك راغورځول شوى دى');
INSERT INTO "abulsalam" VALUES(27,30,'27:30',' بېشكه دا د سلیمان له جانبه (راغلى) دى او بېشكه هغه (داسې) دى: د الله په نامه سره (شروع كوم) چې ډېر زیات مهربان، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(27,31,'27:31',' دا چې تاسو زما په خلاف سركشي مه كوئ او تاسو ما ته راشئ مسلمانان (غاړه ایښودونكي)');
INSERT INTO "abulsalam" VALUES(27,32,'27:32',' هغې وویل: اى سردارانو! تاسو ما ته زما په (دغه) كار كې مشوره راكړئ، زه د كوم كار فیصله كوونكې نه یم تر هغه پورې چې تاسو ما ته حاضر شئ');
INSERT INTO "abulsalam" VALUES(27,33,'27:33',' هغوى وویل: مونږه د قوت والا او د ډېر سخت جنګ والا یو، او (نور) اختیار تا ته دى، نو ته وګوره چې ته څه حكم كوې؟');
INSERT INTO "abulsalam" VALUES(27,34,'27:34',' هغې وویل: بېشكه بادشاهان (د هغوى لښكرې) چایان چې دي، كله چې دوى كوم كلي (ښار) ته داخل شي (نو) هغه خرابوي، او د هغه عزتمن اوسېدونكي ذلیله ګرځوي او دوى به هم همداسې كوي');
INSERT INTO "abulsalam" VALUES(27,35,'27:35',' او بېشكه زه دوى ته یوه ډالۍ لېږونكې یم، نو كتونكې یم چې په څه خبرې سره لېږل شوي استازي بېرته راځي');
INSERT INTO "abulsalam" VALUES(27,36,'27:36',' نو كله چې هغه (استازى) سلیمان ته راغى، (نو سلیمان ورته) وویل: ایا تاسو په مال سره زما مدد كوئ، نو هغه چې ما ته الله راكړى دى (هغه) ډېر غوره دى له هغه نه چې تاسو ته يې دركړى دى، بلكې تاسو په خپلې ډالۍ سره خوشالېږئ');
INSERT INTO "abulsalam" VALUES(27,37,'27:37',' ته هغوى ته بېرته لاړ شه، نو مونږ به خامخا هرومرو په دوى پسې داسې لښكرې بوځو، چې په دوى كې به له هغو سره د مقابلې هېڅ طاقت نه وي او مونږ به خامخا هرومرو دوى له هغه (ښاره) وباسو، په دې حال چې چې ذلیل به وي، خوار او سپك به وي');
INSERT INTO "abulsalam" VALUES(27,38,'27:38',' (سلیمان) وویل: اى (د دربار) مخورو! په تاسو كې به څوك ما ته د هغې تخت راوړي، مخكې له دې چې دوى ما ته راشي مسلمانان (غاړه اېښودونكي)؟');
INSERT INTO "abulsalam" VALUES(27,39,'27:39',' په پېریانو كې ډېر سخت زورور وویل: زه به هغه تا ته راوړم مخكې له دې چې ته له خپل ځاى نه پاڅېږي، او بېشكه زه په دغه (تخت) باندې خامخا قوي (او) امانتګر یم');
INSERT INTO "abulsalam" VALUES(27,40,'27:40',' وویل هغه كس چې له هغه سره د كتاب علم و: زه به هغه تا ته راوړم مخكې له دې چې تا ته خپل نظر بیا راګرځي، نو كله چې يې هغه خپلې مخې ته برقرار (حاضر) ولید، ويې ويل: دا زما د رب له فضله ځنې دى، د دې لپاره چې هغه ما وازمايي چې زه شكر وباسم، یا ناشكري كوم او څوك چې شكر وباسي، نو هغه يواځې د ځان لپاره شكر وباسي او څوك چې ناشكري كوي، نو بېشكه زما رب ډېر بې پروا، ډېر كرم كوونكى دى');
INSERT INTO "abulsalam" VALUES(27,41,'27:41',' ويې ويل:هغې ته خپل تخت نا اشنا كړئ چې وګورو ایا پېژني يې، یا كېږي له هغو كسانو نه چې نه يې پېژني');
INSERT INTO "abulsalam" VALUES(27,42,'27:42',' نو كله چې هغه راغله، (ورته) وویل شول: ایا ستا تخت همداسې دى؟ هغې وویل: ګویا كې دا همغه دى او مونږ ته له دې (معجزې) نه مخكې (د توحید او نبوت) علم راكړى شوى و او مونږ غاړه ايښودونكي (مسلمانان) وو');
INSERT INTO "abulsalam" VALUES(27,43,'27:43',' او دا (د الله له عبادت نه) منع كړې وه هغه څیز چې دې يې له الله نه غیر عبادت كاوه، یقینًا هغه له كافر قوم ځنې وه');
INSERT INTO "abulsalam" VALUES(27,44,'27:44',' هغې ته وویل شول: ته دې صحن ته ننوځه، نو كله چې يې هغه ولیده، نو په هغه يې د ژورو اوبو ګمان وكړ او له خپلو دواړو پنډیو نه يې كپړه پورته كړه هغه (سلیمان) وویل: بېشكه دا هوار ښویه صحن دى چې له شیشو نه (جوړ) دى، هغې وویل: بېشكه ما (مخكى) په خپل ځان ظلم كړى دى او ما له سلیمان سره خاص د الله رب العلمین لپاره اسلام راوړى دى ');
INSERT INTO "abulsalam" VALUES(27,45,'27:45',' او یقینًا یقینًا مونږ ثمودیانو ته د هغوى ورور صالح لېږلى و، په دې سره چې تاسو د الله عبادت كوئ، نو ناڅاپه هغوى داسې دوه ډلې وې چې په خپل مینځ كې جنګېدلې');
INSERT INTO "abulsalam" VALUES(27,46,'27:46',' هغه وویل: اى زما قومه! ولې تاسو په بدۍ (عذاب) سره تادي كوئ، مخكې له نېكۍ (توبې) نه، ولې تاسو له الله نه مغفرت نه غواړئ ؟ د دې لپاره چې په تاسو رحم وكړى شي');
INSERT INTO "abulsalam" VALUES(27,47,'27:47',' هغوى وویل: مونږ په تا او په هغه چا چې له تاسره دي؛ بدفالي نیولې ده، هغه وویل: ستاسو بدفالي د الله په نېز ده، بلكې تاسو داسې قوم یئ چې ازمایل كېږئ');
INSERT INTO "abulsalam" VALUES(27,48,'27:48',' او په ښار كې نهه (غټ شریر) سړي وو، چې په ځمكه كې به يې فساد كاوه او اصلاح به يې نه كوله');
INSERT INTO "abulsalam" VALUES(27,49,'27:49',' هغوى وویل: تاسو په الله قسم وخورئ چې خامخا هرومرو به په ده او د ده په ټبر د شپې حمله كوو، بیا به مونږ د ده ولي (وارث) ته خامخا ضرور (داسې) ووایو چې مونږ د ده هلاكت ته حاضر شوي نه یو او بېشكه مونږ یقینًا رښتیني یو');
INSERT INTO "abulsalam" VALUES(27,50,'27:50',' او دوى مكر وكړ، خاص مكر كول او مونږ هم تدبیر وكړ خاص تدبیر كول، په دې حال كې چې دوى نه پوهېدل');
INSERT INTO "abulsalam" VALUES(27,51,'27:51',' نو ته وګوره چې د دوى د مكر انجام څنګه و چې بېشكه مونږه دوى او د دوى قوم ټول هلاك كړل');
INSERT INTO "abulsalam" VALUES(27,52,'27:52',' نو دغه د دوى كورونه دي، چې وران شوي پراته دي، په سبب د دې چې ظلم (شرك) يې كاوه، بېشكه په دې كې خامخا لویه نښه ده د هغه قوم لپاره چې پوهېږي');
INSERT INTO "abulsalam" VALUES(27,53,'27:53',' او مونږ نجات وركړ هغو كسانو ته چې ایمان يې راوړى و او پرهېزګاري يې كوله');
INSERT INTO "abulsalam" VALUES(27,54,'27:54',' او (یاد كړه) لوط كله چې ده خپل قوم ته وویل: ایا تاسو فاحشه (د لواطت قبیح كار) كوئ، حال دا چې تاسو وینئ');
INSERT INTO "abulsalam" VALUES(27,55,'27:55',' ایا بېشكه تاسو خامخا د خپل شهوت لپاره له خپلو ښځو نه غیر له سړیو سره بدكاري كوئ! بلكې تاسو داسې قوم یئ چې جهالت كوئ');
INSERT INTO "abulsalam" VALUES(27,56,'27:56',' نو د ده د قوم جواب نه و غیر له دې نه چې دوى وویل: تاسو د لوط خېلخانه له خپل كلي نه وباسئ، بېشكه دوى داسې خلق دى چې ځانونه ډېر پاك ساتي ');
INSERT INTO "abulsalam" VALUES(27,57,'27:57',' نو مونږ نجات وركړ ده ته، او د ده كورنۍ ته د ده له ښځې نه غیر، مونږ هغه په باقي پاتې كېدونكو كې مقرر كړې وه ');
INSERT INTO "abulsalam" VALUES(27,58,'27:58',' او مونږ په دوى باندې (د كاڼو) باران ووراوه، نو د وېرول شویو باران بد و ');
INSERT INTO "abulsalam" VALUES(27,59,'27:59',' ته ووایه: ټوله ستاينه خاص د الله لپاره ده، او سلام دې وي د هغه په هغو بنده ګانو باندې چې غوره كړي يې دي، ایا الله غوره دى، یا كه هغه چې دوى يې (له الله سره) شریكوي');
INSERT INTO "abulsalam" VALUES(27,60,'27:60',' بلكې هغه ذات (بهتر دى) چې اسمانونه او ځمكه يې پیدا كړي دي او تاسو لپاره يې له بره نه اوبه نازلې كړې دي، نو هم مونږ په هغو سره د رونق (او ښايست) والا باغونه زرغون كړل، تاسو لپاره دا طاقت نشته چې تاسو د هغو (باغونو) ونې زرغونې كړئ، ایا له الله سره بل معبود شته؟ بلكې دوى یو داسې قوم دى چې (له الله سره د نورو) برابري كوي');
INSERT INTO "abulsalam" VALUES(27,61,'27:61',' بلكې هغه ذات (غوره دى) چې ځمكه يې د قرار (او ارام) ځاى جوړه كړې ده او د هغې په مینځ كې يې نهرونه جوړ كړي دي او د دې لپاره يې پاخه لوړ غرونه پیدا كړي دي، او د دوو سمندرونو په مینځ كې يې پرده پیدا كړې ده، ایا له الله سره بل معبود شته؟ بلكې د دوى اكثره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(27,62,'27:62',' بلكې هغه ذات (غوره دى) چې د مجبور ( او عاجز) سوال قبلوي، كله چې دغه (الله) وبلي او هغه تكلیف (او سختي) لرې كوي او تاسو د ځمكې خلیفه ګان جوړوي، ایا له الله سره بل معبود شته؟ تاسو ډېر لږ شانته نصیحت قبلوئ');
INSERT INTO "abulsalam" VALUES(27,63,'27:63',' بلكې هغه ذات (غوره دى) چې تاسو ته د وچې او سمندر په تیارو كې لاره ښیي او هغه ذات چې له خپل رحمت نه مخكې هواګانې لېږي، چې زېرى وركوونكې وي، ایا له الله سره بل معبود شته؟ الله ډېر اوچت دى له هغو شیانو نه چې دوى يې (له الله سره) شریكوي');
INSERT INTO "abulsalam" VALUES(27,64,'27:64',' بلكې هغه ذات چې اول ځلې مخلوق پیدا كوي، بیا به دغه (مخلوق) بېرته ژوندي كوي او هغه ذات چې تاسو ته له اسمان او ځمكې نه روزي دركوي، ایا له الله سره بل معبود شته ؟ ته (دوى ته) ووایه: تاسو خپل دلیل راوړئ، كه تاسو رښتیني یئ');
INSERT INTO "abulsalam" VALUES(27,65,'27:65',' ته (دوى ته) ووایه: هر هغه څوك چې په اسمانونو او ځمكه كې دي په غیبو نه پوهېږي غیر له الله نه او دوى نه پوهېږي چې كله به دوى بیا ژوندي كولى شي');
INSERT INTO "abulsalam" VALUES(27,66,'27:66',' بلكې د اخرت په باره كې د دوى علم بېخي ختم شوى دى، بلكې دوى د دغه (قیامت) په باره كې په شك كې دي، بلكې دوى له دغه (قیامت) نه ړانده دي');
INSERT INTO "abulsalam" VALUES(27,67,'27:67',' او كافران شویو كسانو وویل: ایا كله چې مونږه او زمونږ پلرونه خاورې شو، ایا په رښتیا به مونږ خامخا (ژوندي) راوښكلى شو؟');
INSERT INTO "abulsalam" VALUES(27,68,'27:68',' یقینًا یقینًا له دې نه مخكې هم دا وعده مونږ ته او زمونږ پلرونه ته راكړى شوې ده، نه دي دغه (خبرې) مګر د ړومبنو خلقو باطلې قصې');
INSERT INTO "abulsalam" VALUES(27,69,'27:69',' ته (دوى ته) ووایه: تاسو په ځمكه كې وګرځئ، بیا وګورئ چې د مجرمانو (كافرانو) انجام څنګه و؟');
INSERT INTO "abulsalam" VALUES(27,70,'27:70',' او ته په دوى غم مه كوه او په تنګۍ كې مه كېږه، د هغو مكرونو (فریبونو) په سبب چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(27,71,'27:71',' او دوى وايي: دا (د عذاب د نازلېدو) وعده به كله وي، كه تاسو رښتیني یئ');
INSERT INTO "abulsalam" VALUES(27,72,'27:72',' ته (دوى ته) ووایه چې امېد دى چې په تاسو پسې به پیوست شي ځینې له هغه (عذاب) نه چې تاسو يې په جلتۍ غواړئ');
INSERT INTO "abulsalam" VALUES(27,73,'27:73',' او بېشكه ستا رب په خلقو باندې خامخا د فضل كولو والا دى او لېكن د دوى اكثره شكر نه وباسي');
INSERT INTO "abulsalam" VALUES(27,74,'27:74',' او بېشكه ستا رب خامخا پوهېږي په هغه څه چې د دوى سینې يې پټوي او څه چې دوى (خلق) ظاهروي');
INSERT INTO "abulsalam" VALUES(27,75,'27:75',' او په اسمان او ځمكه كې هېڅ پټ پناه شى نشته مګر په واضح كتاب كې (لیك) دي ');
INSERT INTO "abulsalam" VALUES(27,76,'27:76',' بېشكه دا قرآن بني اسرائیلو ته د هغو خبرو زیاتره حصه بیانوي چې دوى په هغو كې اختلاف كوي');
INSERT INTO "abulsalam" VALUES(27,77,'27:77',' او بېشكه دا (قرآن) یقینًا هدایت او رحمت دى، لپاره د مومنانو');
INSERT INTO "abulsalam" VALUES(27,78,'27:78',' بېشكه ستا رب به د دوى په مینځ كې په خپل انصاف سره فیصله كوي، او همغه ډېر غالب، ښه علم والا دى');
INSERT INTO "abulsalam" VALUES(27,79,'27:79',' نو ته خاص په الله باندې توكل وكړه، بېشكه ته په ښكاره حق باندې يې');
INSERT INTO "abulsalam" VALUES(27,80,'27:80',' بېشكه ته مړو ته نشې اورولى او ته كڼو ته (هم) اواز نشې رسولى، كله چې دوى مخ واړوي، په دې حال كې چې شا كوونكي وي');
INSERT INTO "abulsalam" VALUES(27,81,'27:81',' او ته ړندو ته د هغوى له ګمراهۍ نه هدایت كوونكى نه يې، ته نشې اورولى مګر هغه چا ته چې زمونږ په ایتونو ايمان راوړي، نو هم دوى مسلمانان دي');
INSERT INTO "abulsalam" VALUES(27,82,'27:82',' او كله چې په دوى (د عذاب) خبره ثابته شي (او قیامت رانژدې شي، نو) مونږ به دوى ته له ځمكې نه يوه دابّه (ژوندى خوځنده) راوباسو، چې له دوى سره به خبرې كوي، چې بېشكه خلقو به زمونږ په ایتونو یقین نه كاوه');
INSERT INTO "abulsalam" VALUES(27,83,'27:83',' او (یاده كړه) هغه ورځ چې مونږ به له هر امت نه (د مشرانو) یوه ډله راجمع كړو، له هغو نه چې زمونږ د ایتونو تكذیب كوي، نو دوى به منع كولى(یوځاى كولى) شي');
INSERT INTO "abulsalam" VALUES(27,84,'27:84',' تر دې چې كله دوى (محشر ته) راشي، وبه وايي (الله): ایا تاسو زما د ایتونو تكذیب كړى و، حال دا چې د علم په لحاظ سره تاسو د هغو احاطه نه وه كړې، ایا څه دي هغه چې تاسو عمل كاوه؟');
INSERT INTO "abulsalam" VALUES(27,85,'27:85',' او په دوى باندې به (د عذاب) خبره ثابته شي، په سبب د هغه ظلم چې دوى كړى و، نو دوى به خبرې نشي كولى');
INSERT INTO "abulsalam" VALUES(27,86,'27:86',' ایا دوى نه دي كتلي چې بېشكه مونږ شپه پیدا كړې ده، د دې لپاره چې دوى په دې كې سكون (او ارام) وكړي او ورځ مو روښانه (ګرځولې ده) بېشكه په دغو كې خامخا ډېر دلیلونه (نښې) دی د هغه قوم لپاره چې ایمان راوړي');
INSERT INTO "abulsalam" VALUES(27,87,'27:87',' او (یاده كړه) هغه ورځ چې په شپېلۍ كې به پوكى وكړى شي، نو سخت به ووېرېږي هغه څوك چې په اسمانونو كې دي او هغه څوك چې په ځمكه كې دي غیر له هغه چا نه چې الله يې وغواړي او دوى ټول به دغه (الله) ته عاجزه (او ذلیله) راشي');
INSERT INTO "abulsalam" VALUES(27,88,'27:88',' او ته به غرونه وینې، ته به په دوى د كلكو ولاړو ګمان كوې، حال دا چې دوى به د ورېځې د تېرېدلو په شان تېرېږي، (وګورئ) كاریګري د الله هغه چې هر شى يې محكم كړى دى، بېشكه هغه ښه خبردار دى په هغو كارونو چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(27,89,'27:89',' هغه څوك چې یوه نېكي (د توحید كلمه) راوړي، نو د هغه لپاره له هغې نه غوره (بدله) ده او دوى به د دغې ورځې د سختې وېرې نه په امان (امن والا) وي');
INSERT INTO "abulsalam" VALUES(27,90,'27:90',' او څوك چې یوه بدي (شرك) راوړي، نو د دوى مخونه به په اور كې نسكور وغورځولى شي، (او ورته ویلى به شي) تاسو ته جزا نه دركولى كېږي مګر (جزا د) هغو عملونو چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(27,91,'27:91',' بېشكه ما ته حكم شوى دى چې يواځې د دې ښار د رب عبادت وكړم، هغه (الله) چې ده ته يې حرمت (عزت) وركړى دى او هر شى خاص د هغه لپاره دى او ما ته حكم راكړى شوى دى چې زه له مسلمانانو څخه شم');
INSERT INTO "abulsalam" VALUES(27,92,'27:92',' او داچې د قرآن تلاوت وكړم، نو څوك چې په نېغه لار روان شو، نو بېشكه هغه يواځې د خپل ځان د فايدې لپاره په نېغه لار ځي او څوك چې ګمراه شو، نو ته ووایه: زه خو يواځې له وېروونكو څخه یم');
INSERT INTO "abulsalam" VALUES(27,93,'27:93',' او ته ووایه: ټول د كمال صفتونه خاص د الله لپاره دي، هغه به عنقریب په تاسو خپل دلیلونه (نښې) وویني، نو تاسو به هغه وپېژنئ او ستا رب له سره غافل نه دى له هغو كارونو نه چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(28,1,'28:1',' طا، سين، ميم');
INSERT INTO "abulsalam" VALUES(28,2,'28:2',' دا د څرګند كتاب ایتونه دي');
INSERT INTO "abulsalam" VALUES(28,3,'28:3',' مونږ تا ته لولو د موسٰى او فرعون له خبرونو ځنې په حقه سره د هغه قوم لپاره چې ایمان راوړي');
INSERT INTO "abulsalam" VALUES(28,4,'28:4',' بېشكه فرعون په ځمكه كې لويي راخيستې وه او د دې اوسېدونكې يې ډلې ډلې كړي وو، په دوى كې يې یوه ډله ډېره ضعیفه كوله، د دوى زامن به يې حلالول او د دوى ښځې به يې ژوندۍ پرېښودلې، بېشكه هغه له فساد كوونكو ځنې و');
INSERT INTO "abulsalam" VALUES(28,5,'28:5',' او مونږ اراده وكړه چې په هغو كسانو احسان وكړو چې په ځمكه كې ډېر ضعیف كړى شوي دي او چې دوى امامان وګرځوو او چې مونږ دوى (د فرعون د ملك) وارثان وګرځوو');
INSERT INTO "abulsalam" VALUES(28,6,'28:6',' او چې مونږ دوى ته په ځمكه كې قدرت وركړو او چې فرعون او هامان او د دواړو لښكرو ته له دغو (بني اسرائیلو) نه هغه شى وښیو چې دوى به ترې وېرېدل');
INSERT INTO "abulsalam" VALUES(28,7,'28:7',' او مونږ د موسٰى مور ته وحي وكړه چې ته ده ته تى وركوه، نو كله چې په ده ووېرېدلې، نو دا په سیند كې وغورځوه او مه وېرېږه او غم مه كوه، بېشكه دا مونږ تا ته بېرته راوستونكي یو او مونږ دا له رسولانو له نه ګرځوونكي یو');
INSERT INTO "abulsalam" VALUES(28,8,'28:8',' نو دى ال فرعون ومونده (او اوچت يې كړ) د دې لپاره چې (عاقبتًا) د دوى لپاره دښمن او غم شي، بېشكه فرعون او هامان او د دواړو لښكرې (دوى ټول) خطا كوونكي وو');
INSERT INTO "abulsalam" VALUES(28,9,'28:9',' او د فرعون ښځې وویل: (دا هلك) زما او ستا د سترګو یخوالى دى، تاسو دا مه وژنئ، امېد دى چې دا به مونږ ته نفع راورسوي، یا به دا مونږ زوى ونیسو، په دې حال كې چې دوى نه پوهېدل');
INSERT INTO "abulsalam" VALUES(28,10,'28:10',' او د موسٰى د مور زړه (له صبره) بېخي خالي شو، بېشكه هغه قریب وه چې خامخا د ده اظهار وكړي كه چېرې مونږ د هغې زړه مضبوط كړى نه وى، د دې لپاره چې له باور كوونكو څخه شي');
INSERT INTO "abulsalam" VALUES(28,11,'28:11',' او هغې د هغه خور ته وویل: د ده (موسٰى) پسې برابره ځه، نو هغې به ده ته له لرې نه كتل، په دې حال كې چې دوى به نه پوهېدل');
INSERT INTO "abulsalam" VALUES(28,12,'28:12',' او مونږ په ده باندې دايیګانې بندې كړې وې، له مخكې نه، نو هغې (د موسٰى خور) وویل: ایا زه تاسو ته داسې كورنۍ وښیم چې ستاسو لپاره د ده كفالت (او ساتنه) وكړي، په داسې حال كې چې هغوى به له ده سره ښېګڼه كوونكي وي');
INSERT INTO "abulsalam" VALUES(28,13,'28:13',' نو مونږ هغه خپلې مور ته بېرته راوسته، د دې لپاره چې د هغې سترګې یخې شي او غم ونه كړي او د دې لپاره چې پوه شي چې بېشكه د الله وعده حقه ده او لېكن د دوى زیاتره خلق نه پوهېږي');
INSERT INTO "abulsalam" VALUES(28,14,'28:14',' او كله چې هغه خپل زور (ځوانۍ) ته ورسېده، او ښه برابر شه، مونږ هغه ته حكم (نبوت) او پوهه وركړه او همداسې مونږ نېكي كوونكو ته بدله وركوو');
INSERT INTO "abulsalam" VALUES(28,15,'28:15',' او هغه (دې) ښار ته د هغه (ښار) د اوسېدونكو د غفلت (خوب) په وخت كې داخل شو، نو ده په هغه كې دوه سړي وموندل چې له یو بل سره په جنګ وو، دا یو د ده له ډلې (بنی اسرائیلو) نه و او دا بل د ده له دښمنانو ځنې و نو، هغه كس چې د ده له ډلې څخه و، له ده نه يې مرسته وغوښته، د هغه كس په خلاف چې د ده له دښمنانو څخه و، نو موسٰى هغه په سوك وواهه، نو هغه يې قتل كړ، ده وویل: دا د شیطان له كاره څخه دى، بېشكه هغه ښكاره ګمراه كوونكى دښمن دى');
INSERT INTO "abulsalam" VALUES(28,16,'28:16',' ده وویل: اى زما ربه! بېشكه ما په خپل ځان ظلم كړى دى، نو ته ما ته بخښنه وكړې، نو هغه ورته بخښنه وكړه، بېشكه هغه، هم هغه ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(28,17,'28:17',' ويې ويل:اى زما ربه! په سبب د هغه انعام چې تا په ما وكړ، نو زه به هېچرې د مجرمانو مددګار نشم');
INSERT INTO "abulsalam" VALUES(28,18,'28:18',' نو سبا يې كړ په ښار كې، په داسې حال كې چې وېرېدونكى و، (او) انتظار يې كاوه، نو ناڅاپه هغه كس چې پرون يې له ده نه مدد غوښتى و، ده ته نارې وهي، موسٰى ده ته وویل: بېشكه ته خامخا ښكاره ګمراه يې');
INSERT INTO "abulsalam" VALUES(28,19,'28:19',' نو كله چې ده اراده وكړه چې هغه كس ونیسي چې هغه د دوى دواړو دښمن دى، هغه وویل: اى موسٰى! ایا ته غواړې چې ما (هم) قتل كړې، لكه پرون چې دې یو نفس وژلى و، ته نه غواړې مګر دا چې په ځمكه كې زورور شې او ته نه غواړې چې ته له اصلاح كوونكو څخه شې');
INSERT INTO "abulsalam" VALUES(28,20,'28:20',' او د ښار تر ټولو لرې غاړې نه یو سړى راغى چې منډې يې راوهلې، ويې ويل:اى موسٰى بېشكه چې مشران ستا په باره كې مشورې كوي، د دې لپاره چې تا قتل كړي، نو ته وځه! بېشكه زه ستا له خیر غوښتونكو ځنې یم');
INSERT INTO "abulsalam" VALUES(28,21,'28:21',' نو موسٰى له دغه (ښار) نه ووت، په دې حال كې چې وېرېدونكى و (او) انتظار يې كاوه، ويې ويل:اى زما ربه! ته ما ته له ظالم قوم نه نجات راكړې');
INSERT INTO "abulsalam" VALUES(28,22,'28:22',' او كله چې يې د مدین په طرف مخه كړه، ويې ويل:امېد دى چې زما رب به ما ته نېغه لار راوښيي');
INSERT INTO "abulsalam" VALUES(28,23,'28:23',' او كله چې د مدین اوبو ته ورسېده، نو د هغه (كوهي) په خوا كې يې د خلقو یوه ډله وموندله چې اوبه يې وركولې او له هغوى نه يې رابېرته دوه ښځې وموندلې چې خپل څاروي يې رامنع كول، ويې ويل:ستاسو څه معامله ده؟ هغوى وویل: مونږ (څارویو ته) اوبه نه وركوو تر هغه پورې چې شپانه بېرته (سره له ماله) لاړ شي او زمونږ پلار عمر خوړلى بوډا دى');
INSERT INTO "abulsalam" VALUES(28,24,'28:24',' نو (د) هغو (كنډك) ته يې اوبه وركړې، بیا سیوري ته وګرځېده، نو ويې ويل:اى زما ربه! بېشكه زه هر هغه څیز ته چې ته يې ما ته نازلوې له خیره ځنې؛ محتاج یم');
INSERT INTO "abulsalam" VALUES(28,25,'28:25',' نو په دغو دواړو كې یوه ده ته راغله چې په ډېرې حیا سره يې تګ كاوه، ويې ويل:بېشكه زما پلار تا غواړي، د دې لپاره چې تا ته اجر (حق) دركړې د هغه عمل چې تا مونږ ته (زمونږ څاروي) اوبه كړي وو، نو كله چې هغه ده ته راغى او ده ته يې (خپله) قصه بیان كړه، ويې ويل:ته مه وېرېږه (اوس) ته له ظالم قوم نه خلاص شوى يې');
INSERT INTO "abulsalam" VALUES(28,26,'28:26',' په دواړو كې یوې وویل: اى زما پلار جانه! ته دا مزدور ونیسه، بېشكه ترټولو غوره په هغه چا كې چې ته يې مزدور نیسې، هغه دى چې ښه قوتناك او امانتګر وي');
INSERT INTO "abulsalam" VALUES(28,27,'28:27',' ويې ويل:بېشكه زه غواړم په خپلو دغو دوه لوڼو كې یوه تا ته په نكاح دركړم، په دې شرط چې ته به اته كاله زما مزدوري كوې، نو كه تا لس كاله پوره كړل، نو ستا له اړخه به (احسان) وي، او زه په تا باندې مشقت اچول نه غواړم، ژر ده چې ته به ما له نېكانو څخه ومومې، كه الله وغواړي (ان شاء الله)');
INSERT INTO "abulsalam" VALUES(28,28,'28:28',' ويې ويل:دا دى (عهد مقرر) زما په مینځ كې او ستا په مینځ كې، په دغو دواړو نېټو كې چې كومه ما پوره كړه، نو په ما به (نور) هېڅ زیاتى نه وي، او څه چې مونږ وایو، الله پرې ګواه دى');
INSERT INTO "abulsalam" VALUES(28,29,'28:29',' نو كله چې موسٰى نېټه پوره كړه او له خپل اهل سره روان شو (،نو) د طور له جانبه يې اور ولیده، خپل اهل ته يې وویل: تاسو ایسار شي، بېشكه ما اور ولیده، كېدى شي چې زه تاسو ته له هغه نه څه خبر، یا د اور سكروټه (شغله) راوړم، د دې لپاره چې تاسو (پرې) تاوده شئ');
INSERT INTO "abulsalam" VALUES(28,30,'28:30',' نو كله چې دغه (اور) ته راغى (،نو) په مباركه ټوټه (قطعه) كې د ناو په ښې اړخ كې له ونې نه ورته اواز وكړى شو داسې چې اى موسٰى! بېشكه همدا زه الله رب العلمین یم');
INSERT INTO "abulsalam" VALUES(28,31,'28:31',' او داسې چې خپله امسا وغورځوه، نو كله چې يې هغه وليدله چې حركت كوي، ګویاكې نرى منګرى مار دى، (نو) مخ يې واړوه، په دې حال كې چې شاكوونكى (تښتېدونكى) و، او بېرته را ونه ګرځېده، اى موسٰى! مخ راواړوه او مه وېرېږه: بېشكه ته له امان موندونكو څخه يې');
INSERT INTO "abulsalam" VALUES(28,32,'28:32',' ته خپل لاس په خپل ګرېوان كې ننباسه، را وبه وځي، په داسي حال كې چې بې له كوم عیبه به تك سپین وي او ته خپل څانګ خپل ځان ته راپېوست كړه، د وېرې له وجې نه، نو دغه دواړه ستا د رب له جانبه دلیلونه دي فرعون او د هغه سردارانو ته، بېشكه چې دوى فاسقان خلق دي');
INSERT INTO "abulsalam" VALUES(28,33,'28:33',' ويې ويل:اى زما ربه! بېشكه ما له دوى نه یو نفس وژلى دى، نو زه وېرېږم چې دوى به ما ووژني');
INSERT INTO "abulsalam" VALUES(28,34,'28:34',' او زما ورور هارون چې دى، هغه له ما نه په ژبه كې ډېر فصیح دى، نو ته هغه زما سره مددګار ولېږه چې زما تصدیق كوي، بېشكه زه وېرېږم له دې نه چې دوى به زما تكذیب وكړي');
INSERT INTO "abulsalam" VALUES(28,35,'28:35',' ويې ويل:ژر به مونږ ستا مټ په خپل ورور سره مضبوط كړو او تاسو لپاره به غلبه وګرځوو، نو دوى به تاسو ته نشي دررسېدلى، زمونږ د نښو په وجه تاسو دواړه او څوك چې ستاسو پیروي كوي هم تاسو (ټول) به غالب وئ');
INSERT INTO "abulsalam" VALUES(28,36,'28:36',' نو كله چې دوى ته موسٰى زمونږ په څرګندو معجزو سره راغى، دوى وویل: نه دى دا مګر جادو (سحر)، چې په دروغو سره جوړ كړى شوى دى او مونږ دا خبره په خپلو پلرونو كې نه ده اورېدلې چې پخوا وو');
INSERT INTO "abulsalam" VALUES(28,37,'28:37',' او موسٰى وویل: زما رب ښه عالم دى په هغه چا چې د هغه له جانبه په هدایت سره راغلى دى او په هغه چا چې د هغه لپاره به د اخرت ښه انجام وي، بېشكه شان دا دى چې ظالمان به برى نه مومي');
INSERT INTO "abulsalam" VALUES(28,38,'28:38',' او فرعون وویل: اى سردارانو! زه تاسو لپاره له خپل ځان نه غیر بل هېڅ معبود نه پېژنم، نو اى هامانه! ته زما لپاره په خټې باندې اور بل كړه، پس زما لپاره یوه اوچته ماڼۍ جوړه كړه، كېدى شي چې زه د موسٰى معبود ته وخېژم او بېشكه زه دا له دروغجنانو ځنې ګڼم');
INSERT INTO "abulsalam" VALUES(28,39,'28:39',' او ده او د ده لښكرو په ځمكه كې ناحقه (تكبر او) اولويي وكړه او دا يې وګڼله چې یقینًا دوى به مونږ ته نه راګرځول كېږي');
INSERT INTO "abulsalam" VALUES(28,40,'28:40',' نو مونږ دا او د ده لښكرې راونیولې، نو مونږ دوى په درياب كې وغورځول، نو ته وګوره چې د ظالمانو (كافرانو) انجام څنګه و؟');
INSERT INTO "abulsalam" VALUES(28,41,'28:41',' او مونږه دوى داسې پېشوایان وګرځول چې اور ته بلنه كوي او د قیامت په ورځ به له دوى سره مرسته نه كولى شي ');
INSERT INTO "abulsalam" VALUES(28,42,'28:42',' او په دې دنیا كې مونږ د دوى پسې لعنت ولګاوه، او د قیامت په ورځ به همدوى له خراب كړى شویو (بدمخي شویو) څخه وي');
INSERT INTO "abulsalam" VALUES(28,43,'28:43',' او یقینًا یقینًا مونږ موسٰى ته كتاب وركړى و، وروسته له هغه نه چې مونږ ړومبني امتونه هلاك كړل، په دې حال كې چې هغه د خلقو لپاره د بصیرتونو والا، او هدايت او رحمت دى، د دې لپاره چې دوى نصیحت ومني');
INSERT INTO "abulsalam" VALUES(28,44,'28:44',' او (اى نبي!) ته (د موسٰى) سره د مغربي (غره) خوا ته نه وې كله چې مونږ موسٰى ته د (نبوت د) كار وحي وكړه او ته له حاضرینو څخه نه وې');
INSERT INTO "abulsalam" VALUES(28,45,'28:45',' او لېكن مونږ ډېر امتونه پیدا كړل، نو په دوى خپل عمرونه اوږده شول او ته په مدین والاو كې اوسېدونكى نه وې، چې تا هغوى ته زمونږ ایتونه لوستلى، او لېكن همدا مونږ (تا لره) لېږونكي یو');
INSERT INTO "abulsalam" VALUES(28,46,'28:46',' او ته د طور خوا ته نه وې كله چې مونږ اواز وكړ او لېكن (ته لېږلى يې مونږ) رحمت، د خپل رب له جانبه، د دې لپاره چې ته هغه قوم ووېروې چې له تا نه مخكې دوى ته كوم وېروونكى نه دى راغلى، د دې لپاره چې دوى نصیحت قبول كړي');
INSERT INTO "abulsalam" VALUES(28,47,'28:47',' او كه دا نه وى چې دوى ته به څه مصیبت ورسېږي په سبب د هغو عملونو چې د دوى لاسونو مخكې لېږلي دي، بیا به دوى وايي: اى زمونږ ربه! ته مونږ ته كوم رسول ولې نه رالېږې، پس چې مونږ ستا د ایتونو تابع شو او شو له مومنانو څخه');
INSERT INTO "abulsalam" VALUES(28,48,'28:48',' نو كله چې دوى ته زمونږ له جانبه حق راغى، ويې ويل:ده ته په مثل د هغو (معجزو) ولې نه دي وركړى شوي چې موسٰى ته وركړى شوې دي؟ ایا دوى كافران شوي نه وو په هغو (معجزو) چې له دې نه مخكې موسٰى ته وركړى شوې وې؟ دوى وویل: دوه سحره دي، چې د یو بل مدد كوي، او دوى وویل: مونږ په هر یو سره كفر كوونكي یو');
INSERT INTO "abulsalam" VALUES(28,49,'28:49',' ته ووایه: تاسو د الله له خوا نه داسې كتاب راوړئ چې هغه د دې دواړو كتابونو نه زیات لار ښوونكى وي، چې زه د هغه پیروي وكړم، كه تاسو رښتیني یئ');
INSERT INTO "abulsalam" VALUES(28,50,'28:50',' نو كه دوى ستا خبره ونه منله، نو ته پوه شه چې دوى یقینًا يواځې د خپلو خواهشاتو پيروي كوي او له هغه چا نه لوى ګمراه څوك دى، چې د خپل خواهش پيروي كوي، بې له هدایته د الله له جانبه، بېشكه الله ظالم قوم ته نېغه لار نه ښیي');
INSERT INTO "abulsalam" VALUES(28,51,'28:51',' او یقینًا یقینًا مونږ دوى ته كلام پرله پسې لېږلى دى، د دې لپاره چې دوى نصیحت واخلي');
INSERT INTO "abulsalam" VALUES(28,52,'28:52',' هغه كسان چې مونږ هغوى ته له ده نه مخكې كتاب وركړى و، هغوى په ده باندې ایمان راوړي');
INSERT INTO "abulsalam" VALUES(28,53,'28:53',' او كله چې دوى ته ولوستى شي، دوى وايي: مونږ په ده باندې ایمان راوړى دى، بېشكه همدا زمونږ د رب له جانبه حق دى، بېشكه مونږ له ده نه مخكې هم مسلمانان وو');
INSERT INTO "abulsalam" VALUES(28,54,'28:54',' دغه كسان دي چې دوى ته به د دوى اجر دوه كرته وركاوه شي، په سبب د دې چې دوى صبر وكړ او دوى په نېكۍ سره بدي لرې كوي، او ځينې له هغه مال نه چې مونږ دوى ته وركړى دى لګوي يې');
INSERT INTO "abulsalam" VALUES(28,55,'28:55',' او كله چې دوى بېكاره خبره واوري (، نو) له هغې نه مخ واړوي او وايي: زمونږ لپاره زمونږ عملونه دي او ستاسو لپاره ستاسو عملونه دي، په تاسو دې سلام وي، مونږ جاهلان نه غواړو');
INSERT INTO "abulsalam" VALUES(28,56,'28:56',' بېشكه ته هدایت نشې كولى هغه چا ته چې ته يې خوښوې، او لېكن الله هدایت وركوي چا ته چې وغواړي او هغه ښه عالم دى په هدایت موندونكو باندې');
INSERT INTO "abulsalam" VALUES(28,57,'28:57',' او دوى وايي: كه مونږ له تا سره د هدایت پیروي وكړو (نو) له خپلې ځمكې نه به وتښتول (ووېستل) شو، ایا مونږ دوى ته د داسې حرم تمكین نه دى وركړى چې د امن والا دى، ده ته د هر شي مېوې راوړلى (او وركولى) شي ښه رزق وركول، زمونږ له خوا نه، او لېكن د دوى زیاتره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(28,58,'28:58',' او مونږ څومره ډېر كلي هلاك كړي دي چې په خپل معيشت كې يې سركشي كړې وه، نو دغه د دوى كورونه دي چې له دوى نه پس په كې سكونت نه دى شوى مګر ډېر لږ او هم دا مونږ وارثان یو');
INSERT INTO "abulsalam" VALUES(28,59,'28:59',' او ستا رب له سره د كلیو هلاكوونكى نه دى، تر هغه پورې چې د هغو (كلیو) په اصل (مركزي ښار) كې یو رسول ولېږي چې دوى ته زمونږ ایتونه لولي، اومونږ د كلیو هلاكوونكي نه یو مګر په داسې حال كې چې د هغو اوسېدونكي ظالمان وي');
INSERT INTO "abulsalam" VALUES(28,60,'28:60',' او تاسو ته چې څه شى هم دركړى شوى دى، نو د دنيايي ژوند فايده او د هغه زینت دى، او هغه څه چې له الله سره دي، ډېر غوره او ښه باقي پاتې كېدونكي دي، ایا نو تاسو له عقل نه كار نه اخلئ؟');
INSERT INTO "abulsalam" VALUES(28,61,'28:61',' ایا نو هغه څوك چې له هغه سره مونږ وعده كړې ده، ښه وعده، پس دى له هغې سره ملاقات كوونكى دى، د هغه چا په شان دى، چې مونږ هغه ته فايده وركړه د دنيايي ژوند فايده، بیا به هغه د قیامت په ورځ له حاضر كړى شویو څخه وي؟');
INSERT INTO "abulsalam" VALUES(28,62,'28:62',' او (یاده كړه) هغه ورځ چې دوى ته به هغه (الله) اواز وكړي، نو وبه وايي: زما هغه شریكان چېرته دي چې تاسو به ګڼل؟');
INSERT INTO "abulsalam" VALUES(28,63,'28:63',' هغه كسان به ووايي چې پر هغوى باندې خبره ثابته شوې ده: اى زمونږه ربه! دا هغه كسان دي چې مونږ ګمراه كړي وو، مونږ دوى ګمراه كړل، لكه چې مونږ ګمراه شوي وو مونږ (له دوى نه) ستا په طرف بېزاره كېږو، دوى زمونږ عبادت نه كاوه');
INSERT INTO "abulsalam" VALUES(28,64,'28:64',' او وبه ویل شي: تاسو خپل شریكان وبلئ، نو دوى به هغوى وبلي: نو هغوى به دوى ته هېڅ جواب ورنه كړي او دوى به عذاب وویني (او تمنا به وكړي چې) كاشكې واقعتًا دوى (په دنیا كې) هدایت موندلى وى');
INSERT INTO "abulsalam" VALUES(28,65,'28:65',' او (یاده كړه) هغه ورځ چې دوى ته به (الله) اواز وكړي، نو وبه وايي: تاسو رسولانو ته څه جواب وركړى و؟');
INSERT INTO "abulsalam" VALUES(28,66,'28:66',' نو په دغې ورځ به په دوى باندې خبرونه پټ شي، نو دوى به یو تربله پوښتنه نه كوي');
INSERT INTO "abulsalam" VALUES(28,67,'28:67',' نو پاتې شو هغه كس چې توبه وباسي او ایمان راوړي او نېك عمل وكړي، نو امېد دى چې دغه به له نجات موندونكو څخه وي');
INSERT INTO "abulsalam" VALUES(28,68,'28:68',' او ستا رب پیدا كوي هغه چې وغواړي او هغه انتخاب كوي، دوى ته هېڅ اختیار نشته، الله لره پاكي ده، او هغه اوچت دى له هغه څه نه چې دوى يې (ورسره) شریكوي');
INSERT INTO "abulsalam" VALUES(28,69,'28:69',' او ستا رب پوهېږي په هغه څه چې د دوى سینې يې پټوي او څه چې دوى څرګندوي');
INSERT INTO "abulsalam" VALUES(28,70,'28:70',' او همدغه الله دى، نشته هېڅ لايق د عبادت مګر هم دى دى، خاص هم ده لره ثنا ده په دنیا او اخرت كې، او خاص هم ده لره حكم دى، او خاص ده ته به تاسو بېرته بیولى شئ');
INSERT INTO "abulsalam" VALUES(28,71,'28:71',' ته (دوى ته) ووایه: تاسو ما ته خبر راكړئ كه چېرې الله پر تاسو باندې د قیامت د ورځې پورې همېشه شپه كړي (نو) له الله نه غیر بل څوك الٰه (معبود) دى چې تاسو ته رڼا راوړي؟ ایا نو تاسو نه اورئ ؟');
INSERT INTO "abulsalam" VALUES(28,72,'28:72',' ته (دوى ته) ووایه: ما ته خبر راكړئ، كه چېرې الله پر تاسو باندې د قیامت د ورځې پورې همېشه ورځ كړي، نو له الله نه غیر بل څوك معبود دى چې تاسو ته هغه شپه راوړي چې تاسو په هغې كې ارام كوئ؟ ایا نو تاسو نه وینئ؟');
INSERT INTO "abulsalam" VALUES(28,73,'28:73',' او خاص د خپل رحمت له وجې يې تاسو لپاره شپه او ورځ پیدا كړې ده، د دې لپاره چې تاسو په دغې (شپه) كې ارام وكړئ او د دې لپاره چې تاسو (په دغې ورځ كې) د هغه له فضله (څه) ولټوئ او د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(28,74,'28:74',' او (یاده كړه) هغه ورځ چې دوى ته (الله) اواز وكړي، نو وبه وايي: چېرته دي زما هغه شریكان چې تاسو به ګڼل؟');
INSERT INTO "abulsalam" VALUES(28,75,'28:75',' او مونږ به له هر امت نه یو ګواه راوباسو، نو مونږ به (دوى ته) ووایو: تاسو خپل دلیل راوړئ، نو دوى به پوه شي چې بېشكه حق خاص د الله لپاره دى، او له دوى نه به هغه څه ورك شي چې دوى به دروغ تړل');
INSERT INTO "abulsalam" VALUES(28,76,'28:76',' بېشكه قارون د موسٰى له قومه څخه و، نو ده په دغو (بني اسرائیلو) باندې سركشي (او لويي) وكړه او مونږه ده ته له خزانو ځنې دومره مقدار وركړى و چې بېشكه د هغو چابیانې (كلیګانې) به خامخا په طاقتورې ډلې درنېدلې، (یاد كړه) هغه وخت چې ده ته د ده قوم وویل: ته (په مال خوشحالي او) تكبر مه كوه بېشكه الله (په مال خوشالېدونكي) تكبر كوونكي نه خوښوي');
INSERT INTO "abulsalam" VALUES(28,77,'28:77',' او ته په هغه (مال) كې چې تا ته الله دركړى دى ؛ د اخرت كور (جنت) لټوه، او له دنیا نه خپله برخه مه هېروه، او احسان كوه، لكه چې تا سره الله احسان كړى دى، او په ځمكه كې فساد مه غواړه، بېشكه الله فساد كوونكي نه خوښوي');
INSERT INTO "abulsalam" VALUES(28,78,'28:78',' هغه وویل: یقینًا دا خو ما ته راكړى شوى دى په هغه علم سره چې زما سره دى، ایا دى نه و پوه، چې بېشكه الله یقینًا له ده نه مخكې له امتونو څخه هغه څوك هلاك كړي دي چې هغه له ده نه په قوت كې زیات سخت وو، او په جمع (ډله) كې له ده نه ډېر وو، او له مجرمانو (كافرانو) نه به د خپلو ګناهونو په باره كې، تپوس نه كاوه شي');
INSERT INTO "abulsalam" VALUES(28,79,'28:79',' نو دى په خپل قوم باندې په زینت كې راووت، (نو) هغو كسانو وویل چې دنيايي ژوند يې غوښته: اى كاشكې چې مونږ له هم وى په شان د هغو (مالونو) چې قارون ته وركړى شوي دي، بېشكه دا خامخا د ډېرې لويې برخې خاوند دى');
INSERT INTO "abulsalam" VALUES(28,80,'28:80',' او هغو كسانو وویل چې علم ورته وركړى شوى و: په تاسو افسوس! د الله ثواب ډېر غوره دى د هغه چا لپاره چې ایمان يې راوړى دى او نېك عمل يې كړى دى، او دغه خبره ښوولى نشي مګر صبر كوونكو ته');
INSERT INTO "abulsalam" VALUES(28,81,'28:81',' نو مونږ هغه او د هغه كور په ځمكه كې ننه اېست، نو بیا د ده لپاره له الله نه غیر هېڅ ډله نه وه چې د ده مدد يې كړى وى اونه و دى (پخپله) له بدل اخيستونكو څخه');
INSERT INTO "abulsalam" VALUES(28,82,'28:82',' او شول هغه كسان چې پرون يې د ده د مرتبې تمنا كوله، چې ویل يې: تعجب دى! ځكه چې یقینًا الله رزق فراخوي چا لپاره چې وغواړي په خپلو بنده ګانو كې او تنګوي يې، كه چېرې نه وى دا چې الله پر مونږ احسان وكړ، خامخا مونږ به يې هم (په ځمكه كې) ننه ایستي (ډوب كړي) وو، تعجب دى! ځكه چې یقینًا، كافران خلاصى نه مومي');
INSERT INTO "abulsalam" VALUES(28,83,'28:83',' دغه د اخرت كور (جنت) دا مونږ د هغو كسانو لپاره مقرروو چې په ځمكه كې نه د لويي اراده لري او نه د فساد (وراني) كولو او ښه انجام د پرهېزګارانو لپاره دى');
INSERT INTO "abulsalam" VALUES(28,84,'28:84',' چا چې یوه نېكي راوړه، نو د هغه لپاره له هغې نه غوره (بدله) ده او چا چې یوه بدي راوړه، نو جزا نشي وركولى هغو كسانو ته چې بدۍ يې كړي دي، مګر هغه چې دوى به څه عمل كاوه');
INSERT INTO "abulsalam" VALUES(28,85,'28:85',' بېشكه هغه (الله) چې په تا يې قرآن فرض (نازل) كړى دى یقینًا هغه تالره د وعدې ځاى ته بېرته بېونكى دى ، ته ووایه: زما رب ښه عالم دى په هغه چا چې نېكي يې راوړې ده او په هغه چا چې هغه په ښكاره ګمراهۍ كې دى');
INSERT INTO "abulsalam" VALUES(28,86,'28:86',' او تا امېد نه لاره چې تا ته به كتاب نازل كړى شي لېكن ستا د رب له جانبه د رحمت په وجه (نازل كړى شو)، نو ته له سره مه كېږه مددګار د كافرانو');
INSERT INTO "abulsalam" VALUES(28,87,'28:87',' او دوى (كافران) دې تا د الله له ایتونو نه هیچېرې وانه ړوي، وروسته له دې چې كله تا ته نازل كړى شول، او ته خپل رب طرف ته (خلق) بله، او هیچېرې مه كېږه له مشركانو څخه');
INSERT INTO "abulsalam" VALUES(28,88,'28:88',' او ته له الله سره بل هېڅ معبود مه بله (ځكه چې) د عبادت هېڅ لايق نشته مګر هغه دى، د هغه له مخ (ذات) نه غیر هر څیز فناكېدونكى دى حكم خاص د هغه لپاره دى او خاص هغه ته به تاسو بېرته بوتلى شئ');
INSERT INTO "abulsalam" VALUES(29,1,'29:1',' الف، لام، ميم');
INSERT INTO "abulsalam" VALUES(29,2,'29:2',' ایا خلقو دا ګمان كړى دى چې دوى به په خپلې دې وینا پرېښودل شي چې: مونږ ایمان راوړى دى، حال دا چې دوى به نه ازمایل كېږي');
INSERT INTO "abulsalam" VALUES(29,3,'29:3',' او یقینًا یقینًا مونږ هغه كسان ازمایلي وو چې له دوى نه مخكې وو، نو خامخا ضرور به الله هغه كسان معلوموي (ظاهروي) چې رښتیني دي او خامخا ضرور به دروغجن معلوموي');
INSERT INTO "abulsalam" VALUES(29,4,'29:4',' بلكې ایا هغو كسانو ګمان كړى دى چې بد عملونه كوي چې دوى به زمونږ نه ړومبى (او خلاص) شي؟ بد دى هغه حكم (فیصله) چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(29,5,'29:5',' هر څوك چې د الله د ملاقات امېد لري، نو بېشكه د الله (د ملاقات) نېټه خامخا راتلونكې ده او هم هغه ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(29,6,'29:6',' او هغه څوك چې جهاد كوي، نو یقینًا هم د خپل ځان لپاره جهاد كوي، بېشكه الله خامخا غني(ډېر بې پروا) دى، له عالمونو نه');
INSERT INTO "abulsalam" VALUES(29,7,'29:7',' او هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، خامخا ضرور به مونږ له هغوى نه د هغوى ګناهونه ورژوو او خامخا ضرور به مونږ دوى ته د ترټولو غوره عملونو چې دوى به كول ؛ بدله وركړو');
INSERT INTO "abulsalam" VALUES(29,8,'29:8',' اومونږ انسان ته د خپل مور و پلار په باره كې د نېكۍ كولو كلك حكم كړى دى، او كه دوى دواړه په تا ډېر كوښښ وكړي، د دې لپاره چې ته زما سره هغه شى شریك كړې چې تا ته د هغه هېڅ علم نشته، نو ته د دوى خبره مه منه، خاص ما ته ستاسو بېرته راتلل دي، نو زه به تاسو په هغو عملونو خبر كړم چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(29,9,'29:9',' او هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، خامخا ضرور به مونږ دوى په (ډله د) صالحانو كې داخل كړو');
INSERT INTO "abulsalam" VALUES(29,10,'29:10',' او په خلقو كې ځینې هغه څوك دي چې وايي: مونږ په الله ایمان راوړى دى، نو كله چې ده ته په (لاره د) الله كې تكلیف (سختي) ورسولى شي (، نو) د خلقو (له اړخه) سختي (او فتنه) د الله له جانبه عذاب وګڼي او قسم دى خامخا كه ستا د رب له جانبه امداد راشي (، نو) خامخا ضرور به وايي چې بېشكه مونږ هم له تاسو سره وو، ایا الله ښه عالم نه دى په هغه (اخلاص او نفاق) چې د عالمونو (خلقو) په سینو كې دى');
INSERT INTO "abulsalam" VALUES(29,11,'29:11',' او خامخا ضرور به الله هغه كسان معلوموي (څرګندوي) چې ایمان يې راوړى دى او خامخا ضرور به منافقین معلوموي');
INSERT INTO "abulsalam" VALUES(29,12,'29:12',' او كافران شویو كسانو هغو خلقو ته وویل چې ایمان يې راوړى دى: تاسو زمونږ د لارې متابعت وكړئ او لازم ده چې مونږ ستاسو ګناهونه اوچت كړو، حال دا چې دوى د هغوى له ګناهونو نه هېڅ شى (ګناه) نه دي پورته كوونكي، بېشكه دوى دروغجنان دي');
INSERT INTO "abulsalam" VALUES(29,13,'29:13',' او خامخا ضرور به دوى خپل بوجونه پورته كوي او له خپلو بوجونو سره نور بوجونه (هم) او د قیامت په ورځ به خامخا ضرور له دوى نه د هغو (دروغو) تپوس كولى شي چې دوى له ځانه جوړول');
INSERT INTO "abulsalam" VALUES(29,14,'29:14',' او یقینًا یقینًا مونږه نوح خپل قوم ته (رسول) لېږلى و، نو هغه په دوى كې پنځوس كاله كم زر كاله تېر كړل، بيا دوى طوفان ونيول، په داسې حال كې چې دوى ظالمان وو ');
INSERT INTO "abulsalam" VALUES(29,15,'29:15',' نو مونږ هغه ته او د كشتۍ والاو ته نجات وركړ او مونږ د عالمونو لپاره دغه (كشتۍ، یا قصه) لویه نښه وګرځوله');
INSERT INTO "abulsalam" VALUES(29,16,'29:16',' او (یاده كړه قصه د) ابراهیم، كله چې هغه خپل قوم ته وویل: تاسو د الله عبادت كوئ او له هغه نه وېرېږئ، دا تاسو لپاره ډېر غوره دي، كه تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(29,17,'29:17',' یقینًا تاسو له الله نه غیر د بتانو عبادت كوئ او تاسو دروغ جوړوئ، بېشكه هغه (معبودان) چې تاسو يې له الله نه غیر عبادت كوئ، دوى تاسو لپاره د رزق مالكان نه دي، نو تاسو له الله سره رزق ولټوئ او د هغه عبادت كوئ او د هغه شكر وباسئ، تاسو به خاص هغه ته بېرته بیولى كېږئ');
INSERT INTO "abulsalam" VALUES(29,18,'29:18',' او (اى كافرانو!) كه تاسو تكذیب كوئ، نو یقینًا له تاسو نه مخكې امتونو هم تكذیب كړى دى، او د رسول په غاړه يواځې ښكاره پېغام رسونه ده');
INSERT INTO "abulsalam" VALUES(29,19,'29:19',' ایا دوى نه ویني چې الله څنګه اول كرت مخلوق پیدا كوي، بیا د هغو اعاده كوي، بېشكه دا (كار) الله ته ډېر اسان دى');
INSERT INTO "abulsalam" VALUES(29,20,'29:20',' ته ووایه: تاسو په ځمكه كې وګرځئ، بیا وګورئ چې هغه (الله) د مخلوق ابتدا څنګه كړې ده، بیا به الله پیدا كړي، وروستني پیدا كول، بېشكه الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(29,21,'29:21',' هغه عذاب وركوي هغه چا ته چې وغواړي او رحم كوي په هغه چا چې وغواړي او خاص هغه ته به تاسو بېرته بېولى كېږئ');
INSERT INTO "abulsalam" VALUES(29,22,'29:22',' او (اى خلقو!) نه یئ تاسو عاجزه كوونكي (د الله) په ځمكه او نه په اسمان كې او تاسو لپاره له الله نه غیر نه هېڅ دوست شته او نه مددګار ');
INSERT INTO "abulsalam" VALUES(29,23,'29:23',' او هغه كسان چې د الله په ایتونو او د هغه په ملاقات سره كافر شوي دي، دغه كسان زما له رحمت نه نا امېده شوي دي او دغه كسان دي چې د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(29,24,'29:24',' نو د هغه (ابراهیم) د قوم (بل) هېڅ جواب نه و مګر دا چې دوى وویل: تاسو دا ووژنئ یا دا وسوځوئ، نو الله ده ته له اور نه نجات وركړ، بېشكه په دې كې خامخا ډېرې نښې دي د هغه قوم لپاره چې ایمان راوړی');
INSERT INTO "abulsalam" VALUES(29,25,'29:25',' او ده وویل: یقینًا تاسو له الله نه غیر (دا) بتان يواځې د خپل مینځي دوستۍ لپاره نیولي دي، په دنيايي ژوند كې، بیا به د قیامت په ورځ ستاسو ځینې په ځینو نورو سره منكرېږي او ستاسو ځینې به په ځینو نورو لعنت وايي او ستاسو استوګنه اور دى او تاسو لپاره به هیڅوك مدد كوونكي نه وي');
INSERT INTO "abulsalam" VALUES(29,26,'29:26',' نو په ده باندې لوط ایمان راوړ او ده وویل: بېشكه زه خپل رب ته هجرت كوونكى یم، بېشكه هم هغه ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(29,27,'29:27',' او مونږ هغه ته اسحق او یعقوب وبخښل او مونږ د هغه په اولاده كې نبوت او كتاب كېښوده او مونږ هغه ته د هغه اجر په دنیا كې وركړ او بېشكه هغه په اخرت كې خامخا له صالحانو څخه دى');
INSERT INTO "abulsalam" VALUES(29,28,'29:28',' او (یاده كړه قصه د) لوط كله چې هغه خپل قوم ته وویل: بېشكه تاسو یقینًا غټه بدكاري كوئ، چې په عالمونو كې هیڅوك دې ته له تاسو نه ړومبى شوي نه دي');
INSERT INTO "abulsalam" VALUES(29,29,'29:29',' ایا خامخا تاسو یقینًا له سړیو سره بدكاري كوئ او لارې شكوئ او تاسو په خپل مجلس كې ناكاره كارونه كوئ، نو د ده د قوم (بل) هېڅ جواب نه و مګردا چې دوى وویل: ته په مونږه د الله عذاب راوله كه ته له رښتینو څخه يې');
INSERT INTO "abulsalam" VALUES(29,30,'29:30',' ده وویل: اى زما ربه! د فسادي قوم په مقابله كې ته زما مدد وكړه');
INSERT INTO "abulsalam" VALUES(29,31,'29:31',' او كله چې زمونږ استازيو ابراهیم ته زېرى راوړ (بیا يې ورته وویل:) بېشكه مونږ د دې كلي اوسېدونكو لره هلاك كوونكي یو، بېشكه د دغه (كلي) اوسېدونكي ظالمان دي');
INSERT INTO "abulsalam" VALUES(29,32,'29:32',' ده وویل: بېشكه په دغه (كلي) كې خو لوط دى، هغوى وویل: مونږ په هغه چا ښه عالمان یو چې په دغه كې دي، مونږ به خامخا ضرور هغه ته او د هغه اهل ته نجات وركوو د هغه له ښځې نه غیر، هغه په باقي پاتې كېدونكو كې ده');
INSERT INTO "abulsalam" VALUES(29,33,'29:33',' او كله چې څنګه لوط ته زمونږ استازي راغلل (، نو) هغه د دوى په وجه غمګین شو او د دوى په وجه يې زړه ډېر تنګ شو او هغوى وویل: ته مه وېرېږه او مه غمګین كېږه، بېشكه مونږ تا لره او ستا اهل لره نجات وركوونكي یو، خو ستا له ښځې نه غیر، هغه په باقي پاتې كېدونكو كې ده');
INSERT INTO "abulsalam" VALUES(29,34,'29:34',' بېشكه مونږ د دې كلي په اوسېدونكو باندې له اسمانه عذاب نازلوونكي یو، په سبب د هغې نافرمانۍ (او بدكارۍ) چې دوى به كوله');
INSERT INTO "abulsalam" VALUES(29,35,'29:35',' او یقینًا یقینًا مونږ له دغه كلي نه یوه ډېره څرګنده نښه پرېښوده، د هغه قوم لپاره چې له عقل نه كار اخلي');
INSERT INTO "abulsalam" VALUES(29,36,'29:36',' او (مونږ لېږلى و) مدین ته د هغوى ورور شعیب، نو ده وویل: اى زما قومه! تاسو د الله عبادت كوئ او د اخري ورځې امېد ساتئ او په ځمكه كې ورانكاري مه ګرځئ، په داسې حال كې چې فساد كوونكي یئ');
INSERT INTO "abulsalam" VALUES(29,37,'29:37',' نو دوى د هغه تكذیب وكړ، نو دوى سختې زلزلې ونیول، نو شول په خپلو كورونو كې پړمخې مړه پراته');
INSERT INTO "abulsalam" VALUES(29,38,'29:38',' او (هلاك كړي مو وو قوم د) عاد او ثمود او په تحقيق تاسو ته (د دوى هلاكول) د دوى له كورونو نه ښكاره شوي دياو دوى ته شیطان خپل عملونه ښايسته كړل، نو دوى يې له (حقې) لارې نه منع كړل (واړول)، حال دا چې دوى ښه بینا (او هوښیار) وو');
INSERT INTO "abulsalam" VALUES(29,39,'29:39',' او (مونږ هلاك كړ) قارون او فرعون او هامان او یقینًا یقینًا دوى ته موسٰى ښكاره معجزې راوړې وي، نو دوى په ځمكه كې لويي وكړه او دوى (زمونږ نه) ړومبى كېدونكی نه وو');
INSERT INTO "abulsalam" VALUES(29,40,'29:40',' نو (په دوى كې) هر یو مونږ په خپلې ګناه سره ونیوه، نو په دوى كې ځینې هغه دي چې مونږ پرې كاڼي وېشتونكى باد راخوشې كړ او ځینې په دوى كې هغه دي چې دوى سختې چغې ونیول او ځینې په دوى كې هغه دي چې مونږ هغوى په ځمكه كې ننه اېستل او ځینې په دوى كې هغه دي چې مونږ (په اوبو كې) غرق كړل او الله له سره داسې نه و چې په دوى يې ظلم كړى وى او لېكن دوى په خپلو ځانونو (پخپله) ظلم كاوه');
INSERT INTO "abulsalam" VALUES(29,41,'29:41',' د هغو كسانو حال چې له الله نه غیر يې دوستان (او كارسازي) نیولي دي د غڼې (جولاګي) د حال په شان دى، چې یو كور جوړ كړي، او بېشكه په كورونو كې تر ټولو كمزورى كور خامخا د جولاګي كور دى كه چېرې دوى پوهېدلى (نو د غیر الله عبادت به يې نه كاوه)');
INSERT INTO "abulsalam" VALUES(29,42,'29:42',' بېشكه الله په هر هغه شي عالم دى چې دوى يې له هغه نه غیر بلي او همغه ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(29,43,'29:43',' او دغه مثالونه، مونږ دغه د خلقو لپاره بیانوو او په دوى نه پوهېږي مګر پوه خلق');
INSERT INTO "abulsalam" VALUES(29,44,'29:44',' الله اسمانونه او ځمكه په حق سره پیدا كړي دي، بېشكه په دې كې د مومنانو لپاره خامخا لویه نښه ده');
INSERT INTO "abulsalam" VALUES(29,45,'29:45',' ته هغه لوله چې تا ته وحي كړى شوى دى، چې كتاب (او قرآن) دى او لمونځ قايموه، بېشكه لمونځ (لمونځ كوونكي لره) له بې حیایۍ او بدكار نه منع كوي او خامخا د الله یادول ډېر لوى دي او الله پوهېږي په هغو كارونو چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(29,46,'29:46',' او تاسو له اهل كتابو سره جګړه مه كوئ مګر په هغې (طریقې) سره چې هغه تر ټولو ښه وي، غیر له هغو كسانو نه چې ظلم وكړي له دوى ځنې او تاسو وايئ: مونږ په هغه (كتاب) ایمان راوړى دى چې مونږ ته نازل كړى شوى دى او ( په هغه كتاب) چې تاسو ته نازل كړى شوى دى او زمونږ معبود او ستاسو معبود یو دى او مونږ خاص هغه ته غاړه ايښودونكي یو');
INSERT INTO "abulsalam" VALUES(29,47,'29:47',' او هم داسې مونږ تا ته كتاب نازل كړ، نو هغه كسان چې مونږ هغوى ته كتاب وركړى دى هغوى په ده (قرآن) ایمان راوړي او له دغو (عربو) نه ځینې هغه دي چې په ده ایمان راوړي او زمونږ له ایتونو نه انكار نه كوي مګر كافران');
INSERT INTO "abulsalam" VALUES(29,48,'29:48',' او له دې (قرآن) نه مخكې نه تا كوم كتاب لوسته او نه تا د غه په خپل ښي لاس سره لیكه، (كه ته داسې وى نو) په دغه وخت كې به باطل پرستو شك كاوه ');
INSERT INTO "abulsalam" VALUES(29,49,'29:49',' بلكې دغه (قرآن) ښكاره ایتونه دي د هغو خلقو په سینو كې چې هغوى ته علم وركړى شوى دى او زمونږ له ایتونو نه انكار نه كوي مګر ظالمان');
INSERT INTO "abulsalam" VALUES(29,50,'29:50',' او دوى وايي: په ده باندې د خپل رب له جانبه نښې (معجزې) ولې نه نازلولى شي؟ ته (دوى ته) ووایه: بېشكه معجزې خو يواځې له الله سره دي، بېشكه زه خو بس ښكاره وېروونكى یم');
INSERT INTO "abulsalam" VALUES(29,51,'29:51',' ایا دوى ته دا كافي نه دي چې بېشكه مونږ په تاكتاب نازل كړى دى، په دې حال كې چې دوى ته لوستل كېږي، بېشكه چې په دغه (كتاب) كې خامخا رحمت او نصیحت دى د هغه قوم لپاره چې ایمان راوړي');
INSERT INTO "abulsalam" VALUES(29,52,'29:52',' ته (دوى ته) ووایه: زما په مینځ كې او ستا په مېنځ كې الله ښه كافي ګواه دى، هغه عالم دى په هر هغه شي چې په اسمانونو او ځمكه كې دي او هغه كسان چې په باطل يې ایمان راوړى دى او په الله سره كافران شوي دي همدغه كسان تاوانیان دي');
INSERT INTO "abulsalam" VALUES(29,53,'29:53',' او دوى دعذاب په راتلو سره په تا تلوار كوي، او كه چېرې ټاكل شوي نېټه نه وى (نو )خامخا به په دوى عذاب راغلى و، او خامخا ضرور به دا په دوى ناڅاپه راځي، په دې حال كې چې دوى به شعور (خبر) نه لري');
INSERT INTO "abulsalam" VALUES(29,54,'29:54',' او دوى د عذاب په راتلو سره په تا تلوار كوي، حال دا چې جهنّم خو كافرانو لره خامخا راګېروونكى دى');
INSERT INTO "abulsalam" VALUES(29,55,'29:55',' په هغې ورځ كې چې دوى لره به عذاب پټ كاندې د دوى له پاسه نه او د دوى د پښو له لاندي، نه او دوى ته به ووايي: تاسو (د) هغه عمل (سزا) وڅَكئ چې تاسو به كاوه');
INSERT INTO "abulsalam" VALUES(29,56,'29:56',' اى زما هغو بنده ګانو چې ایمان يې راوړى دى! بېشكه زما ځمكه فراخه ده، نو تاسو خاص زما عبادت كوئ');
INSERT INTO "abulsalam" VALUES(29,57,'29:57',' هر نفس د مرګ(مزه) څكونكى دى، بیا به خاص مونږ ته راګرځولى شئ');
INSERT INTO "abulsalam" VALUES(29,58,'29:58',' او هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي خامخا ضرور به مونږ دوى ته د جنت په داسې لوړو بالاخانو كې ځاى وركړو چې د هغو له لاندې ولې بهېږي، په دې حال كې چې تل به وي په دغو (بالاخانو) كې، دغه (جنت) د عمل كوونكو ښه اجر دى');
INSERT INTO "abulsalam" VALUES(29,59,'29:59',' هغه كسان چې صبر يې كړى دى او دوى خاص په خپل رب باندې توكل كوي');
INSERT INTO "abulsalam" VALUES(29,60,'29:60',' او په ځمكه ډېر خوځنده (زنده سر) دي چې خپل رزق نه پورته كوي، الله دوى ته او تاسو ته روزي دركوي او هغه ښه اورېدونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(29,61,'29:61',' او قسم دى كه ته له دوى نه پوښتنه وكړي چې اسمانونه او ځمكه چا پیدا كړي دي او (چا) لمر او سپوږمۍ مسخر كړي دي؟ دوى به خامخا ضرور ووايي چې الله (پیدا كړي او مسخر كړي دي)، نو څرنګه دوى ګرځولى شي؟');
INSERT INTO "abulsalam" VALUES(29,62,'29:62',' الله چې په خپلو بنده ګانو كې چا لره وغواړي؛ رزق فراخوي او په هغه يې تنګوي، بېشكه الله په هر شي ښه عالم دى');
INSERT INTO "abulsalam" VALUES(29,63,'29:63',' او قسم دى كه ته له دوى نه پوښتنه وكړې چې چا له اسمان نه اوبه نازلې كړې دي، بيا يې په هغو سره ځمكه د هغې له مرګ نه پس ژوندۍ كړې ده؟ (نو) خامخا ضرور به دوى ووايي چې الله (دا كار كړى دى) ته ووایه: ټول د كمال صفتونه خاص د الله لپاره دي بلكې د دوى اكثره عقل نه لري');
INSERT INTO "abulsalam" VALUES(29,64,'29:64',' او دا دنيايي ژوند نه دى مګر عبث او لوبې او بېشكه دار اخرت (جنت) خامخا همغه واقعي ژوندون دى، كه چېرې دوى پوهېدلى');
INSERT INTO "abulsalam" VALUES(29,65,'29:65',' نو كله چې دوى په كشتۍ كې سواره شي (، نو) دوى الله بولي، په داسې حال كې چې خاص هغه لره دین خالص كوونكي وي، نوكله چې هغه (الله) دوى ته وچې ته نجات وركړي (نو) ناڅاپه دوى (له الله سره) شریكان نیسي');
INSERT INTO "abulsalam" VALUES(29,66,'29:66',' د دې لپاره چې دوى په هغو (نعمتونو سره) كفر(ناشكري) وكړي چې مونږ دوى ته وركړي دي او د دې لپاره چې دوى فايده واخلي، نو ژر به دوى پوه شي');
INSERT INTO "abulsalam" VALUES(29,67,'29:67',' ایا دوى نه دي لیدلي چې بېشكه مونږ حرم امن والا ګرځولى دى، حال دا چې د دوى ګېر چاپېره خلق تښتولى شي، ایا نو دوى په باطل ایمان راوړي او د الله په نعمت سره كافران كېږي؟');
INSERT INTO "abulsalam" VALUES(29,68,'29:68',' او له هغه كس نه لوى ظالم څوك دى چې په الله باندې دروغ تړي، یا د حق تكذیب كوي، كله چې ده ته دغه (حق) راشي، ايا په جهنّم كې د كافرانو لپاره د اوسېدوځاى نشته؟');
INSERT INTO "abulsalam" VALUES(29,69,'29:69',' او هغه كسان چې په (دین) زمونږ كې كوشش كوي ( نو) مونږ به دوى ته خامخا ضرور خپلې لارې وښیو او بېشكه الله خامخا د نېكي كوونكو ملګرى دى');
INSERT INTO "abulsalam" VALUES(30,1,'30:1',' الف، لام، ميم');
INSERT INTO "abulsalam" VALUES(30,2,'30:2',' رومیان مغلوب كړى شول');
INSERT INTO "abulsalam" VALUES(30,3,'30:3',' په ډېرې نزدې ځمكه كې، او دوى به د خپل مغلوب كېدو نه وروسته ژر غالب شي');
INSERT INTO "abulsalam" VALUES(30,4,'30:4',' په څو كلونو كې خاص الله ته حكم دى له دې نه مخكې او له دې نه وروسته او په دغې ورځ به مومنان خوشاله شي');
INSERT INTO "abulsalam" VALUES(30,5,'30:5',' د الله په مدد، هغه مدد كوي چا ته چې وغواړي او همغه ښه غالب، ښه مهربان دى');
INSERT INTO "abulsalam" VALUES(30,6,'30:6',' وعده كول د الله، الله د خپلې وعدې مخالفت نه كوي او لېكن د خلقو زیاتره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(30,7,'30:7',' دوى د دنيايي ژوند په ظاهري (او سرسري شیانو) پوهېږي، په داسې حال كې چې دوى له اخرته هم دوى غافلان دي');
INSERT INTO "abulsalam" VALUES(30,8,'30:8',' ایا دوى په خپلو نفسونو كې فكر نه دى كړى، چې الله اسمانونه او ځمكه او هر هغه څه چې د دې دواړو په مینځ كې دي؛ نه دي پیدا كړي مګر په حقه سره او په ټاكلې نېټې سره او بېشكه ډېر له خلقو ځنې د خپل رب په ملاقات خامخا منكران(او كافران) دي ');
INSERT INTO "abulsalam" VALUES(30,9,'30:9',' ایا دوى په ځمكه كې نه ګرځي، نو چې وګوري چې د هغو كسانو انجام څنګه و چې له دوى نه مخكې وو هغوى له دوى نه په قوت كې زیات سخت وو او هغوى ځمكه څیروله او دا يې اباده كړې وه ډېره له هغې نه چې دوى دغه اباده كړې ده او دوى ته خپلو رسولانو ښكاره دلیلونه راوړي وو، نو الله كله هم داسې نه و چې په دوى ظلم وكړي او لېكن دوى (پخپله) په خپلو ځانونو ظلم كاوه');
INSERT INTO "abulsalam" VALUES(30,10,'30:10',' بیا د هغو كسانو انجام، چې بد يې كړي وو، ډېر بد و، په دې سبب چې دوى د الله د ایتونو تكذیب كړى و او په دغو (ایتونو) پورې به يې استهزا كولې');
INSERT INTO "abulsalam" VALUES(30,11,'30:11',' الله اول ځلې مخلوق پیدا كوي، بیا به يې اعاده كوي، بیا به خاص هغه ته ورګرځولى شي');
INSERT INTO "abulsalam" VALUES(30,12,'30:12',' او په هغې ورځ چې قیامت قايم شي، مجرمان به ناامېده شي');
INSERT INTO "abulsalam" VALUES(30,13,'30:13',' او د دوى لپاره به د دوى په شریكانو كې هېڅ شفاعت كوونكي نه وي او دوى به له خپلو شریكانو نه منكران (كافران) شي');
INSERT INTO "abulsalam" VALUES(30,14,'30:14',' او په كومه ورځ چې قیامت قايم شي، په دغې ورځ به دوى بېل بېل شي');
INSERT INTO "abulsalam" VALUES(30,15,'30:15',' نو هر چې هغه كسان دي چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، نو دوى به په باغ (جنت) كې خوشالولى شي');
INSERT INTO "abulsalam" VALUES(30,16,'30:16',' او پاتې شو هغه كسان چې كفر يې كړى دى او زمونږ د ایتونو او د اخرت د ملاقات تكذیب يې كړى دى، نو دغه كسان به په عذاب كې حاضر كړى شوي وي');
INSERT INTO "abulsalam" VALUES(30,17,'30:17',' نو (وايئ) د الله تسبیح كله چې تاسو ماښام كوئ او كله چې تاسو سبا كوئ');
INSERT INTO "abulsalam" VALUES(30,18,'30:18',' او خاص هغه لره ستاينئ دي په اسمانونو اوپه ځمكه كې او (تسبیح وايئ) د ورځې په اخره برخه كې او كله چې تاسو ماسپښین كوئ');
INSERT INTO "abulsalam" VALUES(30,19,'30:19',' هغه (الله) له مړي نه ژوندى راوباسي او له ژوندي نه مړى راوباسي او ځمكه د هغې له مرګ نه پس راژوندۍ كوي او همداسې به تاسو راویستل كېږئ');
INSERT INTO "abulsalam" VALUES(30,20,'30:20',' او یوه د هغه له نښو ځنې دا ده چې تاسو يې له خاورې پیدا كړئ، بیا ناڅاپه تاسو بشر وئ چې خورېدلئ');
INSERT INTO "abulsalam" VALUES(30,21,'30:21',' او د هغه له نښو ځنې دا دي چې تاسو لپاره يې ستاسو له نفسونو څخه جوړې پيدا كړې، د دې لپاره چې تاسو هغو ته (په مايله كېدو سره) سكون حاصل كړئ او هغه ستاسو په مینځ كې مینه او مهرباني پیدا كړه، بېشكه په دغو كې خامخا نښې دې د هغه قوم لپاره چې ښه فكر كوي');
INSERT INTO "abulsalam" VALUES(30,22,'30:22',' او د هغه له نښو ځنې د اسمانونو او ځمكې پیدا كول دي او ستاسو د ژبو او ستاسو د رنګونو اختلاف (بېل والى) دى، بېشكه په دغو كې خامخا نښې دي د عالمانو (پوهانو) لپاره');
INSERT INTO "abulsalam" VALUES(30,23,'30:23',' او د هغه له نښو نه ستاسو خوب دى، په شپه او ورځ كې او ستاسو لټول دي د هغه له فضله، بېشكه په دغو كې خامخا نښې دي د هغه قوم لپاره چې اوري');
INSERT INTO "abulsalam" VALUES(30,24,'30:24',' او د هغه له نښو ځنې(دا) دي چې هغه تاسو ته برېښنا د وېرې او طمعې لپاره ښیي او له بره نه اوبه نازلوي، بیا په دغو (اوبو) سره ځمكه راژوندۍ كوي، د هغې له مرګ نه وروسته، بېشكه په دغو كې خامخا نښې دي د هغه قوم لپاره چې له عقل نه كار اخلي');
INSERT INTO "abulsalam" VALUES(30,25,'30:25',' او د هغه له نښو څخه (دا) دي چې اسمان او ځمكه د هغه په حكم سره ولاړ دي، بیا چې كله هغه تاسو راوبلي، یوځل بلل، له ځمكې نه، (نو) ناڅاپه به تاسو (ژوندي) راوځئ');
INSERT INTO "abulsalam" VALUES(30,26,'30:26',' او خاص د هغه لپاره دي هغه څوك چې په اسمانونو او ځمكه كې دي، (دغه) ټول د همده اطاعت كوونكي دي');
INSERT INTO "abulsalam" VALUES(30,27,'30:27',' او دى همغه ذات دى چې مخلوق اول ځلې پیدا كوي، بیا به دغه دوباره راژوندي كوي او دغه (بیا راژوندي كول) هغه (الله) ته ډېر اسان دي او خاص هغه لره تر ټولو لوړ شان (او صفت) دى په اسمانونو اوځمكه كې او هغه ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(30,28,'30:28',' هغه (الله) تاسو ته ستاسو له ځانونو نه یو مثال بیانوي (هغه دا چې) ایا تاسو لپاره ستاسو له مریانو څخه په هغه (مال) كې چې مونږ تاسو ته دركړى دى، څوك شریكان شته؟ نو چې تاسو (ټول) په هغه (مال) كې سره برابر يئ چې تاسو له دوى نه داسې وېرېږئ لكه ستاسو وېره كول له خپلو ځانونو (ازادو شریكانو) نه، همداسې مونږ ایتونه په تفصیل سره بیانوو، د هغه قوم لپاره چې له عقل نه كار اخلئ');
INSERT INTO "abulsalam" VALUES(30,29,'30:29',' بلكې هغو كسانو چې ظلم يې كړى دى، بې له علمه د خپلو خواهشاتو پيروي وكړه، نو هغه چا ته به څوك هدایت وكړي چې الله ګمراه كړى دى او د دوى لپاره هېڅ مدد كوونكي نشته');
INSERT INTO "abulsalam" VALUES(30,30,'30:30',' نو ته خپل مخ دین ته نېغ (او برابر) وساته، په داسې حال كې چې حق ته مايل اوسې، (تاسو لازم ونیسئ) د الله هغه فطرت لره چې په هغه يې خلق پیدا كړي دي، د الله تخلیق لره هېڅ بدلون نشته همدغه سم(نېغ) دین دى، او لېكن د خلقو اكثریت نه پوهېږي');
INSERT INTO "abulsalam" VALUES(30,31,'30:31',' په داسې حال كې چې هغه ته رجوع كوونكي يئ او له هغه نه ووېرېږئ او لمونځ قايم كړئ او له مشركانو څخه مه كېږئ');
INSERT INTO "abulsalam" VALUES(30,32,'30:32',' له هغو كسانو څخه چې خپل دین يې تار په تار كړى دى او ډلې ډلې (پارټۍ پارټۍ) شوي دي، هره یوه ډله په هغه څه سره چې له دوى سره دي؛ ډېره خوشحاله ده');
INSERT INTO "abulsalam" VALUES(30,33,'30:33',' او كله چې خلقو ته ضرر ورسېږي (نو) دوى خپل رب بولي، په داسې حال كې چې هغه ته رجوع كوونكي وي، بیا چې كله هغه (الله) له خپل جانبه په دوى رحمت وڅكوي، ناڅاپه په دوى كې یوه ډله له خپل رب سره شریكان نیسي');
INSERT INTO "abulsalam" VALUES(30,34,'30:34',' د دې لپاره چې دوى د هغه څه ناشكري وكړي چې مونږ دوى ته وركړي دي، نوتاسو فايده واخلئ، پس ژر به تاسو پوه شئ');
INSERT INTO "abulsalam" VALUES(30,35,'30:35',' ایا مونږ په دوى باندې كوم د لیل نازل كړى دى، نو دغه (دليل دوى ته) د هغه شي خبرې كوي چې دوى به د دغه (الله) سره شريكاوه ');
INSERT INTO "abulsalam" VALUES(30,36,'30:36',' او كله چې مونږ په خلقو خپل رحمت وڅَكوو (نو) دوى په هغه خوشحال شي او كه دوى ته څه سختي ورسېږي د هغو عملونو په سبب چې د دوى لاسونو مخكې لېږلي دي (نو) ناڅاپه دوى ناامېده شي');
INSERT INTO "abulsalam" VALUES(30,37,'30:37',' ایا دوى نه دي لیدلي چې بېشكه الله رزق فراخوي، چا ته چې وغواړي او تنګوي يې، بېشكه په دغه كې خامخا د هغه قوم لپاره نښې دي چې ایمان راوړي');
INSERT INTO "abulsalam" VALUES(30,38,'30:38',' نو ته رشته دار ته د هغه حق وركړه او مسكین او مسافر ته، دغه د هغو كسانو لپاره ډېر غوره دي چې د الله مخ غواړي، اوهمدغه خلق پوره كامیاب دي');
INSERT INTO "abulsalam" VALUES(30,39,'30:39',' او هغه څه چې تاسو يې وركوئ، چې له سوده څخه وي، د دې لپاره چې د خلقو په مالونو كې زیات شي، نو د الله په نېز دغه نه زیاتېږي او هغه څه چې تاسو يې وركوئ چې له زكاته وي، په داسې حال كې چې تاسو د الله مخ غواړئ،نو همدغه كسان ډېر زیاتوونكي دي');
INSERT INTO "abulsalam" VALUES(30,40,'30:40',' الله هغه ذات دى چې تاسو يې پیدا كړئ، بیا هغه تاسو ته روزي دركړه، بیا به هغه تاسو مړه كوي، بیا به تاسو ژوندي كوي، ایا ستاسو په شریكانو كې كوم یو شته چې په دغو (كارونو) كې څه شى كولى شي؟ هغه (الله) ته پاكي ده او هغه ډېر اوچت دى له دې نه چې دوى ورسره شریك نیسي');
INSERT INTO "abulsalam" VALUES(30,41,'30:41',' په وچه او لونده (بر او بحر) كې فساد خور شو، په سبب د هغو (عملونو) چې د خلقو لاسونو كړي دي، د دې لپاره چې هغه (الله) په دوى ځینې برخه (د سزا) د هغو عملونو وڅكوي چې دوى كړي دي، د دې لپاره چې دوى راواپس شي');
INSERT INTO "abulsalam" VALUES(30,42,'30:42',' ته (دوى ته) ووایه: تاسو په ځمكه كې وګرځئ، نو وګورئ چې د هغو كسانو انجام څنګه و چې له دوى نه مخكې وو، د هغوى اكثره مشركان وو');
INSERT INTO "abulsalam" VALUES(30,43,'30:43',' نو ته خپل مخ ډېر نېغ (سم) دین ته برابر او قايم وساته له دې نه مخكې چې هغه ورځ راشي چې د الله له جانبه هغې ته بېرته ګرځېدل (ګرځول) نشته، په دغې ورځ كې به خلق تار په تار (بېل بېل) شي');
INSERT INTO "abulsalam" VALUES(30,44,'30:44',' هر څوك چې كافر شو، نو په همده باندې (عذاب د) كفر د ده دى او چا چې نېك عمل وكړ، نو دوى خاص د خپلو ځانونو لپاره فرشونه غوړوي (او ځایونه تیاروي)');
INSERT INTO "abulsalam" VALUES(30,45,'30:45',' د دې لپاره چې هغه (الله) هغو كسانو ته چې ايمان يې راوړى دى او نېك عملونه يې كړي دي؛ له خپله فضله بدله وركړي، بېشكه هغه (الله) له كافرانو سره مینه نه كوي');
INSERT INTO "abulsalam" VALUES(30,46,'30:46',' او د هغه (الله) له نښو ځنې دا دي چې هغه هواګانې رالېږي، چې زېرى وركوونكي دي، او د دې لپاره چې په تاسو له خپل رحمته ځینې (مزې)! وڅكوي او د دې لپاره چې كشتۍ د هغه په حكم سره روانې شي، او د دې لپاره چې تاسو د هغه له فضله ولټوئ، او د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(30,47,'30:47',' اویقینًا یقینًا مونږ له تا نه مخكې خپلو قومونو رسولان ته لېږلي وو، نوهغوى دوى ته په ښكاره معجزو سره راغلل، نو مونږه له هغو كسانو نه چې جرم (كفر) يې كړى و، انتقام (او بدل) واخيست او په مونږه باندې د مومنانو مدد كول حق دي');
INSERT INTO "abulsalam" VALUES(30,48,'30:48',' الله هغه ذات دى چې هواګانې لېږي، نو هغه ورېځې پورته كوي، نو هغه (الله) دغه په بره كې څنګه چې وغواړي خوروي او دغه (ورېځې) ټوټه ټوټه لاندې باندې وګرځوي، بیا ته باران وینې چې د هغې (ورېځې) له مینځ نه راوځي، نو كله چې دغه (باران) هغه چا ته ورسوي چې هغه يې په خپلو بنده ګانو كې اراده كړې وي، ناڅاپه دوى ښه خوشحالي كوي');
INSERT INTO "abulsalam" VALUES(30,49,'30:49',' او یقینًا دوى مخكې له دې چې په دوى دغه (باران) نازل كړى شي، له دې نه مخكې خامخا نا امېده وو');
INSERT INTO "abulsalam" VALUES(30,50,'30:50',' نو ته د الله د رحمت نښو (او اشارو) ته وګوره، الله ځمكه د هغې له مرګ (وچ والي) نه وروسته څنګه ژوندۍ كوي، بېشكه دغه (الله) خامخا مړو لره ژوندي كوونكى دى او هغه په هر شي باندې قادر دى');
INSERT INTO "abulsalam" VALUES(30,51,'30:51',' او قسم دى كه مونږ (د دوى په فصلونو داسې ضرري) هوا را اولېږو، بیا دوى دغه (فصل) تك زېړ وویني،(نو) خامخا دوى به له دې نه بعد شي چې كفر به كوي');
INSERT INTO "abulsalam" VALUES(30,52,'30:52',' نو بېشكه ته مړو ته نشې اورولى او نه ته كڼو ته بلنه اورولى شې، كله چې هغوى مخ واړوي، په داسې حال كې چې شا كوونكي وي');
INSERT INTO "abulsalam" VALUES(30,53,'30:53',' او ته ړندو ته د هغوى له ګمراهۍ نه هدایت كوونكى نه يې، ته نشې اورولى مګر هغه چا ته چې زمونږ په ایتونو ایمان راوړي، نو دوى مسلمانان دي');
INSERT INTO "abulsalam" VALUES(30,54,'30:54',' الله هغه ذات دى چې تاسو يې له ضعف نه پیدا كړئ، بیا يې له كمزورۍ نه بعد قوت پیدا كړ، بیا يې له قوت نه پس كمزوري او بوډاوالى (دوېښتو سپین والى) پیدا كړ، څه چې هغه وغواړي پیدا كوي، او همغه ښه عالم، ښه قدرت والا دى');
INSERT INTO "abulsalam" VALUES(30,55,'30:55',' او په هغې ورځ كې چې قیامت به قايم شي، مجرمان (كافران) به (په دې) قسم خوري چې دوى (په دنیا كې) له یو ساعت نه غیر نه دي پاتې شوي، همدارنګې به دوى ګرځولى شول');
INSERT INTO "abulsalam" VALUES(30,56,'30:56',' او هغه كسان به چې هغوى ته علم او ایمان وركړى شوى و، ووايي: یقینًا یقینًا تاسو د الله په كتاب كې د بعث (بیا راژوندي كولو) د ورځې پورې پاتې شوي یئ، نو دا د بعث، ورځ ده، او لېكن تاسو وئ چې نه پوهېدئ');
INSERT INTO "abulsalam" VALUES(30,57,'30:57',' نو په دغې ورځ به هغو كسانو ته چې ظلم (شرك) يې كړى دى، د هغوى عذر فايده نه رسوي او نه به له دوى نه د معذرت كولو (او توبه اېستلو) غوښتنه كېږي');
INSERT INTO "abulsalam" VALUES(30,58,'30:58',' او یقینًا یقینًا مونږه خلقو ته په دې قرآن كې هر قسمه مثال بیان كړى دى، او قسم دى كه ته دوى ته كومه نښه (معجزه) راوړې (نو) هغه كسان به چې كافران شوي دي؛ خامخا ضرور وايي: تاسو نه يئ مګر باطل (او ناحق) ویونكي');
INSERT INTO "abulsalam" VALUES(30,59,'30:59',' همداسې الله د هغو كسانو په زړونو مهر لګوي چې نه پوهېږي');
INSERT INTO "abulsalam" VALUES(30,60,'30:60',' نو (اى نبي) ته صبر كوه، بېشكه د الله وعده حقه ده او تا دې له سره سپك نه كړي هغه كسان چې (په اخرت) یقین نه لري');
INSERT INTO "abulsalam" VALUES(31,1,'31:1',' الف، لام، ميم');
INSERT INTO "abulsalam" VALUES(31,2,'31:2',' دغه د حكمت لرونكي كتاب ایتونه دي');
INSERT INTO "abulsalam" VALUES(31,3,'31:3',' په داسې حال كې چې نېكي كوونكو لپاره هدایت او رحمت دى');
INSERT INTO "abulsalam" VALUES(31,4,'31:4',' هغه كسان چې لمونځ قايموي او زكات ادا كوي او په اخرت باندې هم دوى یقین كوي');
INSERT INTO "abulsalam" VALUES(31,5,'31:5',' دغه كسان په هدایت باندې دي د خپل رب له جانبه او همدغه كسان پوره كامیاب دي');
INSERT INTO "abulsalam" VALUES(31,6,'31:6',' او په خلقو كې ځینې هغه څوك دي چې عبثې خبرې اخلي، د دې لپاره چې بې له علمه د الله له لارې نه (خلق) ګمراه كړي او دغه (لاره) مَسخره جوړه كړي، دغه كسان چې دي، د دوى لپاره سپكوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(31,7,'31:7',' او كله چې ده ته زمونږ ایتونه ولوستل شي (نو) مخ وګرځوي، په داسې حال كې چې ډېر تكبر كوونكى وي، ګویاكې ده دا له سره اورېدلي نه دي، ګویاكې د ده په دواړو غوږونو كې دروندوالى (كوڼوالى) دى، نو ته ده ته د ډېر دردوونكي عذاب زېرى وركړه');
INSERT INTO "abulsalam" VALUES(31,8,'31:8',' بېشكه هغه كسان چې ایمان يې راوړى اونېك عملونه يې كړي دي، د دوى لپاره د نعمتونو جنتونه دي');
INSERT INTO "abulsalam" VALUES(31,9,'31:9',' چې دوى به په هغو كې تل ترتله وي (دغه وعده كړې ده) وعده د الله حقه او همغه ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(31,10,'31:10',' هغه، اسمانونه بې له ستنو پیدا كړي دي چې تاسو هغه وینئ او په ځمكه كې يې پاخه غرونه غورځولي (پیدا كړي) دي چې دغه (ځمكه) تاسو ونه خوځوي. او په دغې (ځمكه) كې هر قسمه خوځنده (زنده سر) خواره كړي دي او مونږ له بره نه اوبه نازلې كړې دي، بیا مونږ په دغې (ځمكه) كې هر قسمه ښې عمده (غلې، ګياګانې) رازرغونې كړې');
INSERT INTO "abulsalam" VALUES(31,11,'31:11',' دغه د الله مخلوق دى، نو تاسو (اى مشركانو!) ما ته وښیئ څه شى دى هغه چې دغو (بتانو) پیدا كړى دى، چې غير له هغه (الله) نه دي، بلكې ظالمان په ښكاره ګمراهۍ كې دي');
INSERT INTO "abulsalam" VALUES(31,12,'31:12',' او یقینًا یقینًا مونږ لقمان ته حكمت وركړى و چې د الله شكر وباسه او هر چا چې شكر وكړ، نو بېشكه همدا خبره ده چې هغه د ځان لپاره شكر كوي، او هر چا چې ناشكري وكړه، نو بېشكه الله ډېر بې پروا، ښه ستايل شوى دى');
INSERT INTO "abulsalam" VALUES(31,13,'31:13',' او (یاد كړه) هغه وخت چې لقمان خپل زوى ته وویل، په دې حال كې چې ده ته يې نصیحت كاوه: اى زما خوږه زویه! له الله سره شریك مه نیسه، بېشكه شرك یقینًا ډېر لوى ظلم دى');
INSERT INTO "abulsalam" VALUES(31,14,'31:14',' اومونږ انسان ته د خپل مور و پلار (سره د نېكي كولو) په باره كې كلك حكم كړى دى، دى خپلې مور اوچت كړى و، په داسې حال كې چې سخته كمزورې وه، له پاسه د بلې كمزورۍ نه او د ده بېلول (د تي نه) په دوه كلونو كې وو، دا چې ته زما او د خپل مور و پلار شكر كوه، خاص ما ته بېرته راتله دي');
INSERT INTO "abulsalam" VALUES(31,15,'31:15',' او كه دوى (دواړه) په تا ډېر كوشش وكړي د دې چې ته زما سره هغه شى شریك كړه، چې تا ته د هغه هېڅ علم نشته، نو ته د دوى خبره مه منه، او په دنیا كې له دوى سره نېكه ملګرتیا كوه او ته د هغه چا د لارې پیروي كوه چې ما ته رجوع كوي، بیا خاص ما ته ستاسو بېرته راتله دي، نو زه به تاسو په هغو كارونو خبر كړم چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(31,16,'31:16',' اى زما خوږه زویه! بېشكه دغه(د بدۍ او نېكۍ خصلت) چې دى، كه دغه د اوري د دانې په قدر وي، بیا دغه په یو كاڼې كې وي، یا په اسمانونو كې وي، یا په ځمكه كې وي، الله به هغه راوړي، بېشكه الله ډېر باریك بین، ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(31,17,'31:17',' اى زما خوږه زویه! ته لمونځ قايموه او د نېكۍ حكم كوه او له بدۍ نه (ځان او خلق) منع كوه او صبر كوه په هغه (تكلیف) چې تا ته رسېږي. بېشكه دغه (كارونه) له اهمو كارونو څخه دي');
INSERT INTO "abulsalam" VALUES(31,18,'31:18',' او له خلقو نه خپل مخ مه ګرځوه، او په ځمكه كې په تكبر سره مه ګرځه، بېشكه الله نه خوښوي هر كبرجن (او) فخر كوونكى');
INSERT INTO "abulsalam" VALUES(31,19,'31:19',' او ته په خپل تګ كې میانه رو اوسه او خپل اواز څه ورو كړه، بېشكه چې په اوازونو كې تر ټولو بد (اواز) خامخا د خره اواز دى');
INSERT INTO "abulsalam" VALUES(31,20,'31:20',' ایا تاسو نه دي كتلي چې بېشكه الله تاسو لپاره په كار اچولي دي هر هغه څه چې په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي او پر تاسو باندې يې خپل نعمتونه، چې ظاهري دي او باطني دي ؛ پوره كړي دي او په خلقو كې ځینې هغه دي چې د الله په باره كې جګره كوي، بې له علمه او بې له هدایته او بې له روښانه كتابه');
INSERT INTO "abulsalam" VALUES(31,21,'31:21',' او كله چې دوى ته وویل شي چې تاسو د هغه (قرآن) پیروي وكړئ چې الله نازل كړى دى دوى وايي: (نه!) بلكې مونږ د هغې (لارې) پیروي كوو چې په هغې باندې مونږ خپل پلرونه موندلي دي، ایا اګر كه شیطان دوى د دوزخ عذاب ته رابلل ');
INSERT INTO "abulsalam" VALUES(31,22,'31:22',' او هغه څوك چې خپل مخ الله ته وسپاري، په دې حال كې چې نېكي كوونكى وي، نو یقینًا ده په ډېرې مضبوطې كړۍ باندې منګولې ښخې كړي دي، او خاص الله ته د ټولو كارونو انجام دى');
INSERT INTO "abulsalam" VALUES(31,23,'31:23',' او هر څوك چې كافر شو، نو تا دې د هغه كافر كېدل غمګین نه كړي، خاص مونږ ته د دوى بېرته راتله دي، نو مونږ به دوى خبر كړو په هغو كارونو چې دوى به كول، بېشكه الله ښه عالم دى په هغو خبرو چې په سینو كې دي');
INSERT INTO "abulsalam" VALUES(31,24,'31:24',' مونږ به دوى ته لږه شان نفع وركړو، بیا به مونږ په زور سره دوى ډېر سخت عذاب ته راكاږو');
INSERT INTO "abulsalam" VALUES(31,25,'31:25',' او قسم دى كه ته له دوى نه پوښتنه وكړې چې اسمانونه او ځمكه چا پیدا كړې دي (، نو) دوى به خامخا ضرور ووايي چې الله، ته ووایه: ټول د كمال صفتونه خاص الله لره دي بلكې د دوى زیاتره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(31,26,'31:26',' خاص د الله لپاره دي هر هغه څه چې په اسمانونو او ځمكه كې دي، بېشكه الله، همغه ډېر بې پروا، ښه ستايل شوى دى');
INSERT INTO "abulsalam" VALUES(31,27,'31:27',' او كه په رښتیا سره هره هغه ونه چې په ځمكه كې ده، قلمونه شي، په داسې حال كې چې(سیاهي شي) دغه (محیط) بحر، (بیا) د ده مدد نور اوه بحرونه وكړي (نو) د الله كلمات به خلاص نشي، بېشكه الله ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(31,28,'31:28',' نه دي ستاسو پیدا كول او نه بیا ژوندي راپاڅول مګر لكه د یوه نفس، بېشكه الله ښه اورېدونكى، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(31,29,'31:29',' ایا تا نه دي لیدلي چې بېشكه الله شپه په ورځ كې ننباسي او ورځ په شپه كې ننباسي او هغه لمر او سپوږمۍ مسخر كړي (په كار اچولي) دي، هر یو يې تر مقررې نېټې پورې روان دى او دا چې بېشكه هغه الله په هغو كارونو چې تاسو يې كوئ؛ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(31,30,'31:30',' دغه په دې سبب چې بېشكه الله، هم دى حق دى، او دا چې بېشكه هغه (معبودان) چې دوى يې له هغه (الله) نه غیر بولي، باطل دي او دا چې بېشكه الله، هم دى ډېر اوچت، ډېر لوى دى');
INSERT INTO "abulsalam" VALUES(31,31,'31:31',' ایا تا نه دي لیدلي چې بېشكه كشتۍ په سمندر كې د الله په نعمت سره تګ كوي، د دې لپاره چې تاسو ته له خپلو نښو ځنې وښیي، بېشكه په دغو كې خامخا نښې دي د هر ډېر صبر كوونكي، ډېر شكر كوونكي لپاره');
INSERT INTO "abulsalam" VALUES(31,32,'31:32',' او كله چې دوى لره د ورېځو په شان موج را لاندې(پټ) كړي (نو) دوى الله بولي، په داسې حال كې چې دین خاص الله ته خالص كوونكي وي، نو كله چې هغه دوى ته وچې ته نجات وركړي، بیا نو په دوى كې ځینې په نېغه لاره ولاړ وي او زمونږ له ایتونو (دلیلونو) نه انكار نه كوي مګر هر هغه چې ډېر عهد ماتوونكى، ډېر ناشكره دي');
INSERT INTO "abulsalam" VALUES(31,33,'31:33',' اى خلقو! تاسو د خپل رب تقوٰى غوره كړئ او له هغې ورځې نه ووېرېږئ چې هېڅ پلار به د خپل اولاد په كار نشي راتلى او نه كوم اولاد شته چې هغه له خپل پلار نه څه شى (عذاب) دفع كوونكى دى، بېشكه د الله وعده حقه ده، نو دنيايي ژوند دې تاسو له سره غره( دوكه) نه كړي اوتاسو دې د الله په باره كې لوى غره كوونكى (شیطان) له سره غره نه كړي');
INSERT INTO "abulsalam" VALUES(31,34,'31:34',' بېشكه الله چې دى، خاص له هغه سره د قیامت علم دى او هغه باران نازلوي او هغه (په يقيني طريقه، بې له وسايلو) پوهېږي په هر هغه څه چې په رحمونو كې دي او هېڅ یونفس نه پوهېږي چې هغه به سبا ته څه كار كوي او هېڅ یو نفس نه پوهېږي چې په كومه ځمكه (سيمه) كې به مري، بېشكه الله ښه عالم، ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(32,1,'32:1',' الف، لام، ميم');
INSERT INTO "abulsalam" VALUES(32,2,'32:2',' د دغه كتاب نازلول، چې په ده كې هېڅ شك نشته، د رب العالمین له جانبه دي');
INSERT INTO "abulsalam" VALUES(32,3,'32:3',' (بلكې) ایا دوى وايي چې ده (محمدصلى الله عليه وسلم) دا له خپله ځانه جوړ كړى دى، بلكې همدغه ستا د رب له جانبه حق دى، د دې لپاره چې ته هغه قوم ووېروې چې دوى ته له تا نه مخكې هېڅ وېروونكى نه دى راغلى، د دې لپاره چې دوى سمه لاره ومومي');
INSERT INTO "abulsalam" VALUES(32,4,'32:4',' الله هغه ذات دى چې اسمانونه او ځمكه او هر هغه څه چې د دې دواړو په مینځ كې دي، په شپږو ورځو كې پیدا كړي دي، بیا يې په عرش باندې استوا وكړه، ستاسو لپاره له هغه نه غیر نه هېڅ كارساز شته او نه شفاعت كوونكى، ایا نو تاسو پند نه اخلئ!');
INSERT INTO "abulsalam" VALUES(32,5,'32:5',' هغه د كار تدبیر كوي له اسمان نه ځمكې ته (د قیامت د ورځې پورې)، بیا به ده ته دغه (كار) په هغې ورځ كې خېژي چې د هغې مقدار زر كاله دى، له هغو (كلونو) نه چې تاسو يې شمېرئ');
INSERT INTO "abulsalam" VALUES(32,6,'32:6',' دغه (الله) د پټو او ښكاره و عالم دى، چې ښه غالب، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(32,7,'32:7',' هغه (الله) چې ښايسته كړى يې دى هر شى چې ده هغه پیدا كړى دى، او د انسان تخلیق يې له خټې نه شروع كړى دى');
INSERT INTO "abulsalam" VALUES(32,8,'32:8',' بیا يې د هغه نسل د ضعیفو حقیرو اوبو له خلاصې نه پیدا كړ');
INSERT INTO "abulsalam" VALUES(32,9,'32:9',' بیا يې هغه برابر كړ او په هغه كې يې له خپله روحه ځنې پو كړل او د هغه لپاره يې غوږونه او سترګې او زړونه پیدا كړل (خو) تاسو ډېر لږ شكر وباسئ');
INSERT INTO "abulsalam" VALUES(32,10,'32:10',' او دوى وايي: ایا كله چې مونږ په ځمكه كې ورك (او خاورې) شو، ایا مونږ به خامخا په رښتیا سره په نوي تخلیق كې یو! بلكې دوى د خپل رب له ملاقات نه انكار كوونكي دي');
INSERT INTO "abulsalam" VALUES(32,11,'32:11',' ته (دوى ته) ووایه: ستاسو روحونه هغه د مرګ مَلَك قبض كوي چې په تاسو مقرر كړى شوى دى، بیا به تاسو خپل رب ته بېرته ورګرځولى شئ');
INSERT INTO "abulsalam" VALUES(32,12,'32:12',' او كه ته ووینې كله چې مجرمان د خپل رب په وړاندې سركوزي وي (د خپلو سرونو ټیټوونكي وي او وايي به) اى زمونږه ربه! مونږ ولیده او مونږ واورېده، نو ته مونږ بېرته بوځه، چې مونږ نېك عمل وكړو، بېشكه مونږ (اوس) یقین كوونكي یو');
INSERT INTO "abulsalam" VALUES(32,13,'32:13',' او كه مونږ غوښتلى (نو) مونږ به خامخا هر نفس ته د هغه هدایت وركړى و او لېكن زما له جانبه دغه خبره ثابته (او لازمه) شوې ده چې زه به جهنّم خامخا ضرور له پېریانو او انسانانو، ټولو نه ډكوم');
INSERT INTO "abulsalam" VALUES(32,14,'32:14',' نو تاسو (دا عذاب) وڅكئ په سبب د دې چې تاسو د خپلې دغې ورځې ملاقات هېر كړى و، بېشكه مونږ (هم) تاسو هېر كړئ، نو تاسو (اوس) دغه دايمي عذاب وڅكئ، په سبب د هغو عملونو چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(32,15,'32:15',' بېشكه زمونږ په ایتونو خو يواځې هغه كسان ایمان راوړي چې كله دوى ته په دغو (ایتونو) سره پند وركړى شي، نو پرېوځي، په داسې حال كې چې سجده كوونكي وي او د خپل رب د ستاينې سره تسبیح وايي او دوى تكبر نه كوي');
INSERT INTO "abulsalam" VALUES(32,16,'32:16',' د دوى ډډې له خپلو خوب ځایونو نه لرې كېږي، دوى خپل رب بولي په وېرې او طمعې سره او له هغه (مال) نه، چې مونږ دوى ته وركړى دى ؛ څه لګوي');
INSERT INTO "abulsalam" VALUES(32,17,'32:17',' نو هېڅ یو نفس ته معلوم نه دي هغه څه چې د دوى لپاره پټ كړى شوي دي، د سترګو له یخوالي نه، لپاره د بدلې د هغو عملونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(32,18,'32:18',' ایا هغه څوك چې مومن وي، د هغه چا په شان دى چې فاسق وي؟ دوى سره نه برابرېږي');
INSERT INTO "abulsalam" VALUES(32,19,'32:19',' هر چې هغه كسان دي چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، نو د دوى لپاره د استوګنې باغونه دي، د مېلمستیا په طریقه، په سبب د هغو عملونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(32,20,'32:20',' او پاتې شو هغه كسان چې فاسقان شوي دي، نو د دوى استوګنه اور دى، هر كله چې دوى اراده وكړي چې له دغه (اور) نه ووځي (نو) بېرته به په هغه كې واپس كړى شي او دوى ته به وویل شي چې تاسو د اور هغه عذاب وڅَكئ چې تاسو به د ده تكذیب كاوه');
INSERT INTO "abulsalam" VALUES(32,21,'32:21',' او مونږ به خامخا هرومرو په دوى د نژدې (لږ) عذاب نه وڅكوو، له ډېر لوى عذاب نه مخكې، د دې لپاره چې دوى (له كفر نه اسلام ته) راوګرځي');
INSERT INTO "abulsalam" VALUES(32,22,'32:22',' او څوك دى لوى ظالم له هغه چا نه چې هغه ته د خپل رب په ایتونو سره پند وركړى شي، بیا هغه له دغو (ایتونو) نه مخ وګرځوي، بېشكه مونږ له مجرمانو نه انتقام اخيستونكي یو');
INSERT INTO "abulsalam" VALUES(32,23,'32:23',' او یقینًا یقینًا مونږ موسٰى ته كتاب وركړى و، نو ته د هغه (موسٰى) له ملاقات نه مه په شك كې كېږه او مونږ هغه د بني اسرائیلو لپاره هدایت ګرځولى و');
INSERT INTO "abulsalam" VALUES(32,24,'32:24',' او مونږ په دوى كې ځینې امامان ګرځولي وو چې زمونږ په حكم سره به يې (خلقو ته) لار ښودنه كوله كله چې دوى صبر وكړ، او دوى زمونږ په ایتونو یقین كاوه');
INSERT INTO "abulsalam" VALUES(32,25,'32:25',' بېشكه همدغه ستا رب به د قیامت په ورځ د دوى په مینځ كې فیصله كوي، په هغو خبرو كې چې دوى به په هغو كې اختلاف كاوه');
INSERT INTO "abulsalam" VALUES(32,26,'32:26',' ایا دوى ته ښكاره شوې نه ده چې مونږ له دوى نه مخكې څومره ډېرې پېړۍ هلاكې كړې دي، چې دوى د هغو په (ورانو) استوګنو كې (تل) تېرېږي، بېشكه په دغو كې خامخا نښې دي، ایا نو دوى نه اوري!');
INSERT INTO "abulsalam" VALUES(32,27,'32:27',' او ایا دوى نه دي لیدلي چې بېشكه مونږ وچې (ګیاه ختمې شوې) ځمكې ته اوبه لېږو، نو په دغو (اوبو) سره هغه فصل راوباسو چې له هغه نه د دوى څاروي او د دوى نفسونه خوراك كوي، ایا نو دوى نه ویني');
INSERT INTO "abulsalam" VALUES(32,28,'32:28',' او دوى وايي: دغه فیصله به كله وي، كه تاسو رښتیني يئ؟');
INSERT INTO "abulsalam" VALUES(32,29,'32:29',' ته (دوى ته) ووایه: د فیصلې په ورځ به كافران شویو كسانو ته د هغوى ایمان راوړل فايده ورنكړي او نه به دوى ته مهلت وركولى شي');
INSERT INTO "abulsalam" VALUES(32,30,'32:30',' نو ته له دوى نه مخ واړوه او انتظار كوه، بېشكه دوى (هم) انتظار كوونكي دي');
INSERT INTO "abulsalam" VALUES(33,1,'33:1',' اى نبي! ته له الله نه ووېرېږه او د كافرانو او منافقانو خبره مه منه، بېشكه چې الله له ازله ښه عالم، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(33,2,'33:2',' او ته د هغه (حكم) پیروي كوه چې تا ته د خپل رب له جانبه وحي كولى شي، بېشكه الله په هغو كارونو چې تاسو يې كوئ(اى خلقو!) ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(33,3,'33:3',' او ته په الله توكل وكړه او الله ښه كافي دى كار جوړوونكى ');
INSERT INTO "abulsalam" VALUES(33,4,'33:4',' الله د هېڅ یو سړي لپاره د هغه په دننه كې دوه زړونه نه دي پیدا كړي او هغه (الله) ستاسو هغه ښځې چې تاسو له هغو نه ظهار كوئ ستاسو میندې نه دي ګرځولي او هغه(الله) ستاسو په زامنو بلل شوي ستاسو (واقعي) زامن نه دي ګرځولي، دغه (درې خبرې) ستاسو (بېكاره) خبرې دي، په خپلو خولو سره، او الله حق وايي او هغه نېغه لار ښیي');
INSERT INTO "abulsalam" VALUES(33,5,'33:5',' تاسو دغه(په زامنو بلل شوي) د هغوى پلرونو ته منسوب كړئ، دغه (كار) د الله په نېز د انصاف والا دى، نو كه تاسو د هغوى پلرونه نه پېژنئ، نو دوى ستاسو وروڼه دي په دین كې او ستاسو دوستان دي، او په تاسو هېڅ ګناه نشته په هغه شي كې چې تاسو په كې خطا شئ او لېكن (ګناه شته د ) هغه (كار) چې ستاسو زړونه د هغه قصد (اراده) وكړي او الله له ازله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(33,6,'33:6',' نبي (محمدصلى الله عليه وسلم) په مومنانو باندې د دوى له ځانونو نه زیات حقدار دى او د ده ښځې د دوى میندې دي او د خپلوۍ خاوندان (رشته داران) د الله په كتاب كې د دوى ځینې په ځینو نورو باندې له مومنانو او مهاجرینو نه زیات حق لرونكي دي، مګر دا چې تاسو له خپلو دوستانو سره څه نېكي وكړئ، دغه په كتاب كې لیكل شوي دي');
INSERT INTO "abulsalam" VALUES(33,7,'33:7',' او كله چې مونږ له نبیانو نه د دوى پخه وعده واخيسته او له تا نه او له نوح او ابراهیم او موسٰى او د مریمې له زوى عیسٰى نه، او مونږ له دوى نه ډېره پخه مضبوطه وعده واخيسته');
INSERT INTO "abulsalam" VALUES(33,8,'33:8',' د دې لپاره چې هغه (الله) له صادقانو نه د هغوى د صدق (رښتیاوو) په باره كې تپوس وكړي او هغه د كافرانو لپاره ډېر دردوونكى عذاب تیار كړى دى');
INSERT INTO "abulsalam" VALUES(33,9,'33:9',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د الله هغه نعمت رایاد كړئ چې پر تاسو شوى دى، كله چې په تاسو لښكرې راغلې، نو مونږ پر هغوى باندې باد او داسې لښكرې راخوشې كړې چې تاسو نه لیدلې او الله هغو كارونو لره چې تاسو يې كوئ ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(33,10,'33:10',' كله چې دوى په تاسو راغلل، ستاسو له بره اړخ نه او ستاسو نه له لاندې اړخ نه او كله چې سترګې كږې او زړونه چنغړكو ته ورسېدل او تاسو په الله ګمانونه كول، قسم قسم ګمانونه');
INSERT INTO "abulsalam" VALUES(33,11,'33:11',' په دغه ځاى (او وخت) كې مومنان وازمایلى شول او وخوځولى شول، ډېر سخت خوځول');
INSERT INTO "abulsalam" VALUES(33,12,'33:12',' او (یاد كړه) هغه وخت چې منافقانو او هغو كسانو چې د هغوى په زړونو كې مرض و، وویل: مونږ سره الله او د هغه رسول وعده نه ده كړې مګر د دوكې(وعده) ');
INSERT INTO "abulsalam" VALUES(33,13,'33:13',' او كله چې په دوى كې یوې ډلې وویل: اى د یثرب (مدینې) اوسېدونكو! تاسو لپاره ( د استوګنې) هېڅ ځاى نشته، نو بېرته وګرځئ او په دوى كې يوه ډله له نبي نه د بېرته تللو اجازت غواړي، دوى وايي: بېشكه زمونز كورونه تش پراته دي، حال دا چې هغه تش نه دي پراته، دوى اراده نه لري مګر د تېښتې');
INSERT INTO "abulsalam" VALUES(33,14,'33:14',' او كه په دوى (دغه لښكرې) په دې (مدینه) كې راننه ایستى شي د دې له (ټولو) اړخونو نه، بیا له دوى نه د فتنې (د شرك او له مسلمانانو سره د جنګ) غوښتنه وكړى شي (، نو) دوى به خامخا دغه (كار) وكړي او دوى به په دې كې ځنډ ونه كړي مګر ډېر لږ');
INSERT INTO "abulsalam" VALUES(33,15,'33:15',' او یقینًا یقینًا دوى له دې نه مخكې له الله سره عهد كړى و، چې دوى به شاګانې وانه ړوي او د الله عهد دى چې (د ده باره كې) پوښتنه كېږي');
INSERT INTO "abulsalam" VALUES(33,16,'33:16',' ته (دوى ته) ووایه: تاسو ته به تېښته له سره نفع در ونه رسوي كه تاسو له مرګ، یا قتل نه وتښتئ او په دغه وخت كې به تاسو ته فايده درنه كړى شي مګر ډېره لږه');
INSERT INTO "abulsalam" VALUES(33,17,'33:17',' ته (دوى ته) ووایه: څوك دى هغه ذات چې تاسو به له الله نه بچ كړي كه هغه (الله) تاسو ته د تكلیف رسولو اراده وكړي، یا هغه په تاسو د مهربانۍ كولو اراده وكړي؟ او دوى به د خپل ځان لپاره له الله نه غیر نه هېڅ كارساز ومومي او نه مدد ګار');
INSERT INTO "abulsalam" VALUES(33,18,'33:18',' یقینًا الله ته په تاسو كې (له جهاد نه) منع كوونكي معلوم دي او خپلو وروڼو ته دا ویونكي چې مونږ ته راشئ! او دوى جنګ ته نه راځي مګر ډېر لږ');
INSERT INTO "abulsalam" VALUES(33,19,'33:19',' په داسې حال كې چې پر تاسو باندې بخل كوونكي دي، نو كله چې وېره راشي (، نو) ته به دوى وینې چې تا ته به ګوري، په داسې حال كې چې د دوى سترګې به (داسې) چورلي، لكه هغه كس چې په هغه د مرګ په وجه بې هوشي راوستلى شوې وي، نو كله چې وېره لاړه شي (نو) دوى تاسو ته ضرر دررسوي په خپلو تېرو ژبو سره، په داسې حال كې چې د خیر(مالِ غنیمت) ډېر حریصان وي، دغو كسانو ایمان نه دى راوړى، نو الله د دوى عملونه برباد كړل او دغه (كار) الله ته ډېر اسان دى');
INSERT INTO "abulsalam" VALUES(33,20,'33:20',' دوى په لښكرو ګمان كوي چې دوى نه دي تللي، او كه دغه لښكرې (بیا) راشي (نو) دوى خوښوي چې كاشكې په رښتیا دوى بانډېسیان وى په اعرابو (صحرايي خلقو) كې، چې ستاسو د خبرونو په باره كې يې پوښتنې كولى، او كه دوى په تاسو كې وى نو دوى به جنګ نه و كړى مګر ډېر لږ');
INSERT INTO "abulsalam" VALUES(33,21,'33:21',' یقینًا یقینًا تاسو لپاره په (كار د ) رسول الله كې ډېره ښه اقتدا ده، د هغه چا لپاره چې د الله او د وروستنۍ ورځې امېد لري او الله ډېر زیات یادوي');
INSERT INTO "abulsalam" VALUES(33,22,'33:22',' او كله چې مومنانو ډلې ولیدې، ويې ويل:دا هغه دي چې مونږ سره يې الله او د هغه رسول وعده كړې وه، او الله او د هغه رسول رښتیا ویلي وو، اودوى ته دغو زیات نه كړ مګر ایمان او (د الله حكمونو ته) تسليمېدل');
INSERT INTO "abulsalam" VALUES(33,23,'33:23',' په مومنانو كې ځینې داسې سړي دي چې هغه خبره يې رښتیا كړله چې په هغې يې له الله سره عهد كړى و نو ځينې په دوى كې هغه دي چې پوره يې كړ خپل نذر(عهد) او ځینې په دوى كې هغه دي چې انتظار كوي او دوى (په خپله خبره كې) تبدیلي نه ده كړې، هېڅ تبدیلي');
INSERT INTO "abulsalam" VALUES(33,24,'33:24',' د دې لپاره چې الله رښتینو ته د هغوى د رښتیاوو په سبب بدله وركړي او منافقانو ته عذاب وركړي كه يې وغواړي، یا په دوى رجوع وكړي، بېشكه چې الله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(33,25,'33:25',' او الله كافران سره له خپلې غصې نه بېرته وشړل، په داسې حال كې چې هېڅ خیر يې ونه موند او الله مومنان له جنګ نه وساتلاو الله ډېر زورور، ښه غالب دى');
INSERT INTO "abulsalam" VALUES(33,26,'33:26',' او هغه (الله) هغه كسان چې د دوى مرسته يې كړې وه، چې له اهل كتابو ځنې وو، له خپلو قلاګانو نه راكوز كړل او د دوى په زړونو كې يې وېره وغورځوله، (نو) یوه ډله تاسو وژله او بله ډله تاسو بندي كوله');
INSERT INTO "abulsalam" VALUES(33,27,'33:27',' او هغه، تاسو وارثان كړئ د هغوى د ځمكو او د هغوى د كورونو او د هغوى د مالونو او د هغې ځمكې چې تاسو هغه نه وه پایمال كړې، او الله په هر شي باندې له ازله ښه قادر دى');
INSERT INTO "abulsalam" VALUES(33,28,'33:28',' اى نبي! ته خپلو ښځو ته ووایه! كه تاسو يئ چې دنيايي ژوند او د ده زینت غواړئ، نو راشئ چې زه تاسو ته څه فايده درورسوم او تاسو خوشې (ازادې) كړم، ډېر ښه ازادول');
INSERT INTO "abulsalam" VALUES(33,29,'33:29',' اوكه تاسو يئ چې الله او د هغه رسول او د اخرت كور (جنت) غواړئ، نو بېشكه الله د نېكي كوونكو ښځو لپاره،چې تاسو يئ، ډېر لوى اجر تیار كړى دى');
INSERT INTO "abulsalam" VALUES(33,30,'33:30',' اى د نبي ښځو! په تاسو كې چې هر چا ښكاره بدكاري وكړه (نو) هغې ته به عذاب یو په دوه، دوچند كړى شي او دغه (كار) الله ته ډېر اسان دى');
INSERT INTO "abulsalam" VALUES(33,31,'33:31',' او په تاسو كې چې څوك د الله او د هغه د رسول اطاعت وكړي او نېك عمل وكړي(، نو) مونږ به هغې ته د هغې اجر دوه ځله وركړو او مونږ د هغې لپاره ډېر د عزت رزق تیار كړى دى');
INSERT INTO "abulsalam" VALUES(33,32,'33:32',' اى د نبي ښځو! تاسو په (ډلو د) ښځو كې د هېڅ یوې (ډلې ) په شان نه یئ كه تاسو پرهېزګاري وكړئ، نو تاسو په وینا كې نرمي مه كوئ، (ګنې) نو هغه كس به په طمع كې پرېوځي چې د هغه په زړه كې (د نفاق، یا بدكارۍ) مرض وي او تاسو هغه خبره كوئ چې مناسبه وي');
INSERT INTO "abulsalam" VALUES(33,33,'33:33',' او تاسو په خپلو كورونو كې قرار ونیسئ او تاسو د ړومبني جاهلیت د زینت څرګندونې په شان زینت مه څرګندوئ او تاسو لمونځ قايموئ او زكات ادا كوئ او د الله او د هغه د رسول اطاعت كوئ، بېشكه همدا خبره ده چې الله غواړي چې له تاسو نه، اى اهل بیتو! ګناه لرې كړي او تاسو پاك كړي، پاكول');
INSERT INTO "abulsalam" VALUES(33,34,'33:34',' او تاسو یادوئ هغه د الله ایتونه او حكمت چې ستاسو په كورونو كې لوستل كېږي، بېشكه الله دى، ډېر باریك بین، ښه خبردار');
INSERT INTO "abulsalam" VALUES(33,35,'33:35',' بېشكه مسلمانان او مسلمانانې او مومنان او مومنانې، او اطاعت كوونكي او اطاعت كوونكې او صادقان (رښتيني) او صادقانې او صبر كوونكي او صبر كوونكې اوعاجزي كوونكي او عاجزي كوونكې او خیرات كوونكي او خیرات كوونكې او روژه نیونكي او روژه نیوونكې او د خپلو شرم ځایونو حفاظت كوونكي او حفاظت كوونكې او الله لره ډېر یادوونكي او یادوونكې، الله د دوى لپاره مغفرت او ډېر لوى اجر تیار كړى دى');
INSERT INTO "abulsalam" VALUES(33,36,'33:36',' او نه دي روا د هېڅ مومن لپاره او نه د مومنې لپاره، كله چې الله او د هغه رسول د یو كار فیصله وكړي دا چې د دوى لپاره دې په خپل كار كې اختیار وي او څوك چې د الله او د هغه د رسول نافرماني كوي، نو یقینًا دغه (كس) ګمراه شو، ښكاره ګمراه كېدل');
INSERT INTO "abulsalam" VALUES(33,37,'33:37',' او (یاد كړه اى نبي!) كله چې تا هغه كس ته چې الله په هغه انعام كړى و او تا ( هم) په هغه (په ازادولو سره) انعام كړى و ؛ ویل: ته خپله ښځه له ځان سره وساته او له الله نه ووېرېږه او تا هغه خبره په خپل زړه كې پټوله چې الله د هغې ښكاره كوونكى و او ته له خلقو نه وېرېدلې او الله زیات حق دار دى چې ته له هغه نه ووېرېږې، نوكله چې زید له هغې نه غرض (حاجت) پوره كړ (نو) مونږ هغه تا ته په نكاح كړه، د دې لپاره چې په مومنانو باندې د خپلو په زامنو بلل شویو، په ښځو كې څه سختي نه وي، كله چې دغه (په زامنو بلل شوي) له دغو (ښځو) نه حاجت پوره كړي او دى د الله كار ادا كړى شوى');
INSERT INTO "abulsalam" VALUES(33,38,'33:38',' په نبي باندې هېڅ قدر حرج (او سختي) نشته په هغه كار(د نكاح) كې چې الله د ده لپاره مقرر كړى و، د الله (مقرر كړې) طریقه ده په هغو (نبیانو) كې چې له ده نه مخكې تېر شوي دي او دى د الله امر قطعي ټاكل شوې فیصله');
INSERT INTO "abulsalam" VALUES(33,39,'33:39',' هغه (نبیان) چې د الله پېغامونه رسوي او له هغه نه وېرېږي او له الله نه غیر له هیچا نه نه وېرېږي او الله ښه كافي دى حساب كوونكى');
INSERT INTO "abulsalam" VALUES(33,40,'33:40',' محمد (صلى الله عليه وسلم) ستاسو په سړیو كې د هیچا پلار نه دى او لېكن د الله رسول دى او د نبیانو (د لړۍ) ختموونكى دى او الله په هر شي ښه عالم دى');
INSERT INTO "abulsalam" VALUES(33,41,'33:41',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو الله یادوئ، ډېر زیات یادول');
INSERT INTO "abulsalam" VALUES(33,42,'33:42',' او تاسو د هغه پاكي بیانوئ د ورځې په اوله برخه او اخري برخه كې');
INSERT INTO "abulsalam" VALUES(33,43,'33:43',' دى همغه ذات دى چې په تاسو ’’صلاۃة‘‘ (شفقت) رالېږي او د هغه ملايك (هم)، د دې لپاره چې هغه تاسو له تیارو نه رڼا ته راوباسي، هغه په مومنانو ډېر رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(33,44,'33:44',' ( د الله له جانبه) دوى ته پېشكشي به، په هغې ورځ كې چې دوى به له هغه (الله) سره ملاقات كوي، سلام وي، او هغه د دوى لپاره ډېر د عزت اجر تیار كړى دى');
INSERT INTO "abulsalam" VALUES(33,45,'33:45',' اى نبي! بېشكه مونږ ته ګواه، او زېرى وركوونكى، او وېروونكى لېږلى يې');
INSERT INTO "abulsalam" VALUES(33,46,'33:46',' او الله ته د هغه په حكم سره، بلونكى او رڼا كوونكې ډیوه مولېږلى يې');
INSERT INTO "abulsalam" VALUES(33,47,'33:47',' او ته مومنانو ته زېرى وركړه په دې سره چې بېشكه د دوى لپاره د الله له جانبه ډېر لوى فضل دى');
INSERT INTO "abulsalam" VALUES(33,48,'33:48',' او ته د كافرانو او منافقانو خبره مه منه او د دوى ضرر پرېږده او په الله توكل وكړه او الله ښه كافي دى كارساز');
INSERT INTO "abulsalam" VALUES(33,49,'33:49',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې تاسو له مومنو ښځو سره نكاح وكړئ، بیا دوى طلاقې كړئ، مخكې له دې چې تاسو هغو ته لاس وروړئ، نو تاسو لپاره په دوى باندې هېڅ عدت نشته، چې تاسو به هغه شمېرئ، نوتاسو دوى ته څه فايده وركړئ او دوى خوشې كړئ، ډېر ښه خوشې كول');
INSERT INTO "abulsalam" VALUES(33,50,'33:50',' اى نبي! بېشكه مونږ تا لپاره ستا هغه ښځې حلالې كړې دي چې تا(هغو ته) خپل مهرونه وركړي دي او هغه ښځې (هم) چې ته يې مالك شوى يې له هغو (وینځو) نه چې الله تا ته (له كفارو نه) راګرځولي دي او (مونږ حلالې كړې دي) ستا د تره لوڼه او ستا د عمو (پلرنۍ تروریانو) لوڼه او ستا د ماما لوڼه او ستا د خاله ګانو (مورنۍ تروريانو) لوڼه، هغه چې له تا سره يې هجرت كړى دى، او (هغه) مومنه ښځه (هم)، كه چېرې هغه خپل ځان نبي ته وبخښي، كه نبي اراده وكړي چې دغه په نكاح واخلي، په داسې حال كې چې (دغه ښځه) له مومنانو نه غیر يواځې تا ته خاص (جايزه ) ده، یقینًا مونږ ته هغه (حكم) معلوم دى چې مونږ په دوى باندې د خپلو ښځو او خپلو وینځو په باره كې فرض كړى دى، د دې لپاره چې په تا هېڅ حرج (او سختي) نه وي او الله له ازله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(33,51,'33:51',' ته چې په دغو(ښځو) كې كومه غواړې بېرته (وروسته) كوې يې اوكومه چې غواړې ځان ته يې رانژدې كوې او هغه چې ته يې بیا غواړې له هغو نه چې تالرې كړي وي، نوپه تا هېڅ ګناه نشته دغه (حكم) ډېر نژدي دى چې د دوى سترګې به یخې شي او نه به غمجنې كېږي او دوى ټولې به په هغه راضي كېږي چې ته يې دوى ته وركوې او الله په هغو خبرو پوهېږي چې ستاسو په زړونو كې دي او الله دى ښه عالم، ښه تحمل والا');
INSERT INTO "abulsalam" VALUES(33,52,'33:52',' د دغو (نهو ښځو) نه پس تا ته (نورې) ښځې حلالې نه دي او نه دا (حلال دي) چې ته د دوى په بدل كې نورې ښځې واخلې (راولې) اګر كه د هغوى حسن تا په تعجب كې اچوي، مګر (خو روا دي درته) هغه وېنځي چې ته يې مالك شي او الله دى په هر شي باندې ښه نګران ');
INSERT INTO "abulsalam" VALUES(33,53,'33:53',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د نبي كورونو ته مه ننوځئ مګر دا چې تاسو ته طعام (خوړولو) ته اجازت دركړى شي، په داسې حال كې چې د هغه (طعام) پخلي ته انتظار كوونكي نه يئ او لېكن كله چې تاسو وبلل شئ، نو ننوځئ، بیا چې كله طعام وخورئ، نو خوراه شئ او نه دا چې په خبرو سره زړه خوشالوونكي يئ: بېشكه دغه (كار) داسې دى چې نبي ته تكلیف رسوي، نوهغه له تاسو نه حیا كوي او الله د حقې خبرې (له ویلو) نه حیا نه كوي او كله چې تاسو له دغو(پاكو بېبیانو) نه څه سامان غواړئ، نو له دوى نه يې په داسې حال كې غواړئ، چې تاسو له پردې نه د باندې يئ، دا (كار) ډېر پاك ساتونكى دى ستاسو زړونو لره او د هغوى زړونو لره او تاسو ته روا نه دي چې تاسو د الله رسول ته تكلیف ورسوئ او نه دا چې تاسو د هغه له ښځو سره له هغه نه پس نكاح وكړئ، هیڅكله، بېشكه دغه (كار) د الله په نېز ډېر لوى (جرم) دى ');
INSERT INTO "abulsalam" VALUES(33,54,'33:54',' كه چېرې تاسو كوم شى څرګند كړئ، یا هغه پټ كړئ، نو بېشكه الله په هر شي باندې ښه عالم دى');
INSERT INTO "abulsalam" VALUES(33,55,'33:55',' په دغو (ښځو) هېڅ ګناه نشته په (مخامخ كېدو او راتلو د) خپلو پلرونو كې او نه په خپلو زامنو كې او نه په خپلو وروڼو كې او نه په خپلو ورېرونو كې او نه په خپلو خورینو كې او نه په خپلو ښځو كې او نه (ګناه شته) په (مخامخ كېدو او راتلو د) هغو(وېنځو مریانو) كې چې د دغو (ښځو) ښي لاسونه يې مالكان شوي دي او (اى ښځو!) تاسو له الله نه ووېرېږئ، بېشكه الله په هر شي باندې ښه ګواه دى');
INSERT INTO "abulsalam" VALUES(33,56,'33:56',' بېشكه الله او د هغه ملايك په نبي درود وايي، اى هغو كسانو چې ایمان يې راوړى دى! تاسو په دغه(نبي) باندې درود وايئ او سلام وايئ، سلام ویل');
INSERT INTO "abulsalam" VALUES(33,57,'33:57',' بېشكه هغه كسان چې الله او د هغه رسول ته ایذا (او تكلیف) رسوي، الله په دوى لعنت كړى دى په دنیا او اخرت (دواړو) كې او د دوى لپاره يې سپكوونكى عذاب تیار كړى دى');
INSERT INTO "abulsalam" VALUES(33,58,'33:58',' او هغه كسان چې مومنو سړیو او مومنو ښځو ته په ناكړي كار(او ناكرده ګناه) تكلیف رسوي، نویقینًا دوى په خپل ځان غټ دروغ او ښكاره ګناه باروي');
INSERT INTO "abulsalam" VALUES(33,59,'33:59',' اى نبي! ته خپلو ښځو او خپلو لوڼو او د مومنانو ښځو ته ووایه چې په خپلو ځانونو دې له خپلو څادرو ځنې راخواره ( او راځوړند) كاندي، دغه (د بدن پټول) ډېر نژدې دي دې ته چې دوى به وپېژندل شي (چې پرده دارې دي) نو دوى ته به تكلیف نه رسولى شي او الله دى ډېر بخښونكى، بې حده رحم كوونكى');
INSERT INTO "abulsalam" VALUES(33,60,'33:60',' قسم دى كه دغه منافقان او هغه كسان چې د هغوى په زړونو كې (د بدكارۍ) مرض دى او په مدینه كې بدې (تشویشناكې) اوازې ګډوونكي، منع نشي (نو) مونږ به خامخا ضرور تا په دوى مسلط كړو، بیا به دوى په دې كې له تا سره هَم جواره (په ګاونډ كې) پاتې نشي مګر ډېر لږ (وخت)');
INSERT INTO "abulsalam" VALUES(33,61,'33:61',' په داسې حال كې چې رټلي ترټل شوي به وي، هر چېرې چې وموندل شي (نو) وبه نیول شي او وبه وژلى شي ډېر بد وژل كېدل');
INSERT INTO "abulsalam" VALUES(33,62,'33:62',' دغه لاره د الله (اېښودل شوې) ده د هغو (منافقو) كسانو په باره كې چې له دوى نه مخكې تېر شوي دي او ته به د الله لارې لره هېڅ تبدیلي ونه مومې');
INSERT INTO "abulsalam" VALUES(33,63,'33:63',' (اى نبي) خلق له تا نه د قیامت په باره كې تپوس كوي، ته (دوى ته)ووایه چې بېشكه د هغه ( قیامت) علم يواځې الله سره دى، او ته څه شي پوه كړى يې؟ كېدى شي چې قیامت ډېر نژدې وي');
INSERT INTO "abulsalam" VALUES(33,64,'33:64',' بېشكه الله په كافرانو لعنت كړى دى او د دوى لپاره يې سخت لمبې وهونكى اور تیار كړى دى');
INSERT INTO "abulsalam" VALUES(33,65,'33:65',' په داسې حال كې چې په ده كې به همېشه وي، تل ترتله، دوى به نه كوم دوست (كارسازى) مومي او نه مددګار');
INSERT INTO "abulsalam" VALUES(33,66,'33:66',' په هغې ورځ كې چې د دوى مخونه به په اور كې اړولى راړولى شي، دوى به وايي: اى كاشكي مونږ ! د الله اطاعت كړى وى او د رسول اطاعت مو كړى وى');
INSERT INTO "abulsalam" VALUES(33,67,'33:67',' او دوى به وايي: اى زمونږه ربه! بېشكه مونږ د خپلو مشرانو او د خپلو غټانو اطاعت كړى و، نو دوى زمونږ نه حقه لار وركه كړه');
INSERT INTO "abulsalam" VALUES(33,68,'33:68',' اى زمونږ ربه! ته دوى ته له عذابه دوه چنده وركړه او ته په دوى لعنت وكړه، ډېر لوى لعنت كول');
INSERT INTO "abulsalam" VALUES(33,69,'33:69',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د هغو كسانو په شان مه كېږئ چې موسٰى ته يې تكلیف رسولى و، نو الله هغه (موسٰى) پاك (او بري) كړ د هغه (الزام او دروغو) نه چې دوى ویل او هغه د الله په نېز ډېر مخور و');
INSERT INTO "abulsalam" VALUES(33,70,'33:70',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو له الله نه ووېرېږئ او خبره كوئ ډېره سمه (درسته) خبره');
INSERT INTO "abulsalam" VALUES(33,71,'33:71',' هغه به تاسو ته ستاسو عملونه درست (او صالح) كړي او تاسو ته به ستاسو ګناهونه وبخښي او هر څوك چې د الله او د هغه د رسول اطاعت وكړي، نو یقینًا دغه كامیاب شو، ډېر لوى كامبايېدل');
INSERT INTO "abulsalam" VALUES(33,72,'33:72',' بېشكه مونږ اسمانونو او ځمكې او غرونو ته امانت وړاندې كړ، نو دوى د ده له پورته كولو نه انكار وكړ او له ده ووېرېدل او انسان دغه (امانت) پورته كړ، بېشكه دغه (انسان) دى، ډېر ظالم، ډېر نادان');
INSERT INTO "abulsalam" VALUES(33,73,'33:73',' (دغه وړاندې كول وو) د دې لپاره چې الله منافقانو سړیو او منافقو ښځو او مشركانو سړیو او مشركو ښځو ته عذاب وركړي او چې الله په مومنانو سړیو اومومنو ښځو باندې په رحمت سره رجوع وكړي او الله له ازله ډېر بخښونكى، بې حده مهربان دى');
INSERT INTO "abulsalam" VALUES(34,1,'34:1',' ټول د كمال صفتونه يواځې هغه الله لره دي چې خاص د هغه لپاره دي هر هغه څه چې په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي او خاص همده ته دي ټولې ستاينې په اخرت كې او همدغه (الله) ښه حكمت والا، ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(34,2,'34:2',' هغه پوهېږي په هر هغه څه چې په ځمكه كې ننوځي او په هر هغه څه چې له دې نه راوځي او په په هر هغه څه چې له اسمان نه راكوزېږي او په هر هغه څه چې په ده كې پورته خېژي او همدغه (الله) بې حده مهربان، ډېر بخښونكى دى');
INSERT INTO "abulsalam" VALUES(34,3,'34:3',' او كافران شویو كسانو وویل چې مونږ ته به قیامت رانشي ته (دوى ته) ووایه: ولې نه! زما دې په خپل رب قسم وي چې خامخا لازمًا به هغه (قیامت) تاسو ته راشي(هغه رب) چې د غیبو عالم دى، له هغه نه نه پټېږي په اندازه د یوې ذرې په اسمانونو كې او نه په ځمكه كې او نه د دغې (ذرې) نه وړوكى(شى) او نه (ترې) غټ مګر (دغه هر څه) په ښكاره كتاب كې دي');
INSERT INTO "abulsalam" VALUES(34,4,'34:4',' د دې لپاره چې هغه (الله) بدله وركړي هغو كسانو ته چې ایمان يې راوړى دى اونېك عملونه يې كړي دي، دغه كسان(چې دي) د دوى لپاره مغفرت او ډېر د عزت رزق دى');
INSERT INTO "abulsalam" VALUES(34,5,'34:5',' او هغه كسان چې زمونږ په ایتونو كې (د باطلولو) كوشش كوي، په دې حال كې چې (په خپل ګمان الله لره) عاجزه كوونكي وي، دغه كسان (چې دي) د دوى لپاره له بد عذاب نه ډېره دردونكې سزا ده');
INSERT INTO "abulsalam" VALUES(34,6,'34:6',' او هغه كسان چې هغوى ته علم وركړى شوى دى، (دوى) هغه (قرآن) چې تا ته د خپل رب له جانبه نازل كړى شوى، همدغه حق ویني او دغه (قرآن) د ډېر غالب، ښه ستايل شوي (الله) لارې ته ښودنه كوي');
INSERT INTO "abulsalam" VALUES(34,7,'34:7',' او كافران شویو كسانو وویل: ایا مونږ تاسو ته داسې سړى وښیو چې تاسو ته خبر دركوي چې كله تاسو ذرې ذرې كړى شئ، پوره ذره ذره كېدل، (نو) بېشكه تاسو به خامخا په نوي تخلیق كې يئ (له نوي سره به پیدا كولى شئ)');
INSERT INTO "abulsalam" VALUES(34,8,'34:8',' ایا ده له ځانه په الله دروغ تړلي دي، یا په ده پورې لېونتوب دى(داسې نه ده اى مشركانو!) بلكې هغه كسان چې په اخرت ایمان نه لري په عذاب او ډېرې لرې ګمراهۍ كې دي');
INSERT INTO "abulsalam" VALUES(34,9,'34:9',' ایا دوى نه دې كتلي هغه څه ته چې د دوى په مخ كې دي او هغه څه ته چې له دوى نه وروسته دي له اسمانه او ځمكې نه، كه چېرې مونږ وغواړو (نو) دوى به په ځمكه كې ورډوب كړو، یا به په دوى له اسمانه ټوټې راوغورځوو، بېشكه په دغو (كارونو) كې خامخا لویه نښه ده د هر رجوع كوونكي بنده لپاره');
INSERT INTO "abulsalam" VALUES(34,10,'34:10',' او یقینًا یقینًا مونږ داود ته له خپله جانبه لوى فضیلت وركړى و، اى غرونو! او مرغانو له ده سره تسبیح راوګرځوئ او مونږ د ده لپاره اوسپنه پسته كړې وه');
INSERT INTO "abulsalam" VALUES(34,11,'34:11',' چې ته پوره ارتې زغرې جوړوه او د (كړیو) په اوبدلو (بوڼلو) كې اندازه كوه او تاسو نېك عمل كوئ، بېشكه زه هغه كارونو لره چې تاسو يې كوئ ؛ ښه لیدونكى یم');
INSERT INTO "abulsalam" VALUES(34,12,'34:12',' او د سلیمان لپاره مو هوا (مسخر كړې وه) چې د هغې د ورځې د اولې حصې مزل یوه میاشت (لاره) وه او د هغې د ورځې د اخري حصې مزل (هم) یوه میاشت (لاره) وه او د ده لپاره مونږ د ویلې كړى شوې تانبې چینه وبهوله او (مسخر كړي مو وو) له پېرانو نه هغه څوك چې د ده په مخ كې به يې د ده د رب په حكم سره كار كاوه (ورته ویل شوي وو چې) په دوى كې چې هر چا زمونږ له حكم نه سركشي وكړه (، نو) مونږ به په هغه د لمبې وهونكي اور د عذاب نه وڅَكوو');
INSERT INTO "abulsalam" VALUES(34,13,'34:13',' دوى (پېریانو) به د ده لپاره هغه څه جوړول چې ده به غوښتل، یعنې اوچتې قلاګانې، او تمثالونه او لګنونه، لكه تالابونه او په نغریو كلك پراته دېګونه، اى آل داوده! تاسو (نېك) عمل كوئ د شكر لپاره او زما په بنده ګانو كې شكر كوونكي ډېر لږ دي');
INSERT INTO "abulsalam" VALUES(34,14,'34:14',' نو كله چې مونږ په ده د مرګ حكم وكړ، نو دوى (پېریان هیچا) خبر نه كړل مګر د ځمكې چینجو چې د ده امسا يې خوړله، نو كله چې هغه (سلیمان په ځمكه) ولوېده (نو) پېریانو ته معلومه شوه چې بېشكه كه دوى په غیبو پوهېدلى( نو) په دغه سپكوونكي عذاب كې به نه و پاتې شوي');
INSERT INTO "abulsalam" VALUES(34,15,'34:15',' یقینًا یقینًا د سبا (خلقو) لپاره په خپلې استوګنې كې یوه لویه نښه وه (هغه دا چې د دوى) ښي او چپ اړخ ته دوه باغونه دي، تاسو د خپل رب له رزقه خورئ او د هغه شكر وباسئ (دا ښار) پاكیزه ښار دى او (ستاسو) رب دى ډېر بخښونكى');
INSERT INTO "abulsalam" VALUES(34,16,'34:16',' بیا دوى مخ واړلو (د نبیانو له دعوت نه) نو مونږ په دوى د بند زورور سېلاب راخلاص كړ او د دوى د دوه باغونو په ځاى مو دوى ته دوه داسې باغونه په بدل كې وركړل چې دواړه خاوندان د بې خونده مېوو او غزانو او څه لږ شى د بېرو وو');
INSERT INTO "abulsalam" VALUES(34,17,'34:17',' دغه، مونږ دوى ته سزا وركړه، په سبب د دوى د كفر كولو او مونږ جزا نه وركوو مګر سخت ناشكري ته');
INSERT INTO "abulsalam" VALUES(34,18,'34:18',' او مونږه د دوى په مینځ كې او د هغو كلیو په مینځ كې چې مونږ په هغو كې بركت اچولى و ښكاره (لارې ته نژدې) كلي جوړ كړل او د دغو (كلیو) په مینځ كې مونږ تګ راتګ په اندازه كړى و، تاسو ګرځئ په دغو (كلیو) كې، په شپو او ورځو، په داسې حال كې چې په امان به یئ');
INSERT INTO "abulsalam" VALUES(34,19,'34:19',' نو دوى وویل: اى زمونږ ربه! ته زمونږ د منزلونو په مینځ كې لرې والى پیدا كړه او دوى په خپلو ځانونو ظلم وكړ، نو مونږه دوى قصې (افسانې) وګرځول او مونږ دوى تار په تار كړل، پوره تار په تار كول، بېشكه په دغو كې خامخا نښې دي دهر ډېر صبر كوونكي، ډېر شكر كوونكي لپاره');
INSERT INTO "abulsalam" VALUES(34,20,'34:20',' او یقینًا یقینًا ابلیس د دوى په باره كې خپل خیال رښتیا ومونده، نو دوى ټولو د هغه پيروي وكړه، مګر یوې ډلې، چې هغه مومنان دي');
INSERT INTO "abulsalam" VALUES(34,21,'34:21',' او د دغه (شیطان) لپاره په دغو (اهل سباوو) باندې هېڅ تسلط (او غلبه) نه وه مګر د دې لپاره چې مونږ معلوم (او بېل) كړو هغه څوك چې په اخرت ایمان لري له هغه چا نه چې هغه د دغه (اخرت) په باره كې په شك كې دى او ستا رب د هر شي ښه نګران دى');
INSERT INTO "abulsalam" VALUES(34,22,'34:22',' ته (دې مشركانو ته) ووایه: تاسو هغه (معبودان) وبولئ چې تاسو (هغه) له الله نه غیر (معبودان) ګڼلي دي، دوى د یوې ذرې اندازه اختیار (او مِلك) نه لري په اسمانونو كې او نه په ځمكه كې او د دوى لپاره په دغو دواړو كې هېڅ برخه (شركت) نشته، او نشته دغه (الله) ته له دوى نه هېڅ مددګار ');
INSERT INTO "abulsalam" VALUES(34,23,'34:23',' او د هغه (الله) په نیز شفاعت نفع نه رسوي مګر د هغه چا لپاره چې هغه (الله) د هغه لپاره اجازت وركړي تر هغه پورې چې كله د دوى د زړونو نه وېره(هیبت) لرې كړى شي(نو) دوى وايي: ستاسو رب څه وفرمايل؟ هغوى به ووايي چې حق (يې وفرمایه) او هم دى ډېر اوچت، ډېر لوى دى');
INSERT INTO "abulsalam" VALUES(34,24,'34:24',' (اى نبي!) ته (دوى ته) ووایه چې تاسو ته له اسمانونو اوځمكې نه څوك رزق دركوي؟ ته ووایه چې ’’الله‘‘ او بېشكه مونږ، یا تاسو خامخا په هدایت باندې یو، یا په ښكاره ګمراهۍ كې یو');
INSERT INTO "abulsalam" VALUES(34,25,'34:25',' ته (دوى ته) ووایه: له تاسو نه د هغو جرمونو په باره كې تپوس نه كېږي چې مونږ كړي دي او زمونږ نه پوښتنه نه كېږي د هغو عملونو په باره كې چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(34,26,'34:26',' ته ووایه: زمونږ رب به زمونږ په مینځ كې جمع وكړي، بیا به زمونږ په مینځ كې په حقه سره فیصله وكړي او هم دى ښه فیصله كوونكى، ښه عالم دى');
INSERT INTO "abulsalam" VALUES(34,27,'34:27',' ته(دوى ته) ووایه: تاسو ما ته هغه (معبودان) راوښیئ چې تاسو له هغه (الله) سره پیوست كړي دي، چې شریكان دي (كله هم داسې نشي كېدى) بلكې همدغه الله ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(34,28,'34:28',' او ته نه يې لېږلى مونږ مګر د ټولو خلقو (انسانانو) لپاره زېرى وركوونكى او وېروونكى او لېكن اكثره خلق نه پوهېږي');
INSERT INTO "abulsalam" VALUES(34,29,'34:29',' او دوى وايي: دغه وعده به كله وي كه تاسو رښتيني یئ؟');
INSERT INTO "abulsalam" VALUES(34,30,'34:30',' ته (دوى ته) ووایه: تاسو لپاره د داسې ورځې وعده ده چې تاسو به له هغې نه نه يوه ګړۍ وروسته كېږي او نه به تاسو ړومبى كېږئ');
INSERT INTO "abulsalam" VALUES(34,31,'34:31',' او كافران شویو كسانو وویل: مونږ به له سره په دې قرآن ایمان رانه وړو او نه په هغو كتابونو چې له ده نه مخكې دي او كه ته ووینې كله چې ظالمان د خپل رب په وړاندې ودرول شوي وي، په داسې حال كې چې د دوى ځینې به ځینو نورو ته خبره اړوي (داسې) چې (په دنیا كې) ښه ضعیف كړى شوي كسان به هغو كسانو ته ووايي چې تكبر به يې كاوه: كه تاسو (مونږ لره د ایمان نه منع كوونكي) نه وى (، نو) یقینًا مونږ به مومنان وو');
INSERT INTO "abulsalam" VALUES(34,32,'34:32',' هغه كسان به ووايي چې تكبر يې كړى و، هغو كسانو ته چې ښه كمزور كړى شوي وو: ایا مونږه تاسو د هدایت نه بند كړي وئ، وروسته له دې چې كله هغه تاسو ته راغى! (داسې نه ده) بلكې تاسو (پخپله) مجرمان وئ');
INSERT INTO "abulsalam" VALUES(34,33,'34:33',' او كمزور كړى شوي كسان به هغو كسانو ته ووايي چې تكبر به يې كاوه: (داسې نه وه) بلكې(دا ستاسو) د شپې او ورځي مكر (او فریب) و، كله چې به تاسو مونږ ته حكم كاوه چې مونږ په الله سره كافران شو او د هغه لپاره مونږ شریكان پیدا كړو او دوى (ټول) به پښېماني پټه كړي كله چې دوى عذاب وویني او مونږ به ځنځيرونه د كافران شویو كسانو په غاړو (څټونو) كې واچوو، دوى ته بدله نه وركول كېږي مګر د هغو عملونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(34,34,'34:34',' او مونږ نه و لېږلى په كوم ښار كې هېڅ یو وېروونكى مګر هغه متكبره مَړو (مالدارو) خلقو به ویل: بېشكه مونږ په هغه څه چې تاسو په هغه سره رالېږل شوي يئ، كافران یو');
INSERT INTO "abulsalam" VALUES(34,35,'34:35',' او دوى به ویل: مونږ د مال او اولاد په لحاظ ډېر زیات یو او مونږ به په عذاب كولى نشو');
INSERT INTO "abulsalam" VALUES(34,36,'34:36',' ته ( دوى ته) ووایه: بېشكه زما رب رزق فراخوي چا ته چې وغواړي او تنګوي يې او لېكن د خلقو اكثریت نه پوهېږي');
INSERT INTO "abulsalam" VALUES(34,37,'34:37',' او نه دي ستاسو مالونه او نه اولاد هغه شى چې هغه تاسو مونږ ته رانژدې كړي، ښه رانژدې كول مګر هغه څوك چې ایمان يې راوړ او نېك عمل يې وكړ، نو دغه كسان دي چې د دوى لپاره دوه چنده بدله ده، په سبب د هغو عملونو چې دوى كړي دي او دوى به (د جنت) په بالاخانو كې په امن وي');
INSERT INTO "abulsalam" VALUES(34,38,'34:38',' او هغه كسان چې زمونږ په ایتونو كې كوشش كوي، چې ( په خپل خیال مونږ ه لره) عاجزه كوونكي وي، دغه كسان په عذاب كې حاضر كړى شوي دي');
INSERT INTO "abulsalam" VALUES(34,39,'34:39',' ته (دوى ته) ووایه: بېشكه زما رب رزق فراخوي چا ته چې وغواړي په خپلو بنده ګانو كې او د هغه لپاره يې تنګوي او تاسو چې څه شى هم لګوئ، نو هغه د دغه (څیز) عوض دركوي او هغه تر ټولو غوره رزق وركوونكى دى');
INSERT INTO "abulsalam" VALUES(34,40,'34:40',' او (یاد كړئ) هغه ورځ چې هغه (الله) به دوى ټول راجمع كړي، بیا به ملايكو ته ووايي: ایا دغو كسانو به خاص ستاسو عبادت كاوه؟');
INSERT INTO "abulsalam" VALUES(34,41,'34:41',' هغوى (ملايك) به ووايي: تا لره پاكي ده، ته له دوى نه غیر زمونږ مولٰى (او كارساز) يې بلكې دوى به د پېریانو عبادت كاوه، د دوى اكثره پر هغوى ایمان راوړونكي وو');
INSERT INTO "abulsalam" VALUES(34,42,'34:42',' نو نن ورځ ستاسو ځینې د ځینو نورو لپاره نه د هېڅ نفعې اختیار لري اونه د ضرر اومونږ به هغو كسانو ته چې ظلم (شرك) يې كړى دى،ووایو: تاسو د هغه اور عذاب وڅكئ چې تاسو به د هغه تكذیب كاوه');
INSERT INTO "abulsalam" VALUES(34,43,'34:43',' او كله چې دوى ته زمونږ ایتونه تلاوت كولى شي، چې ښه واضح دي (نو) دوى وايي: نه دى دا مګر یو داسې سړى دى چې غواړي چې تاسو د هغو (معبودانو له عبادت) نه منع كړي چې ستاسو پلرونو به يې عبادت كاوه او دوى وايي: دا (قرآن) نه دى مګر خالص دروغ دي چې له ځانه جوړ كړى شوي دي او كافران شوي خلق د حق(قرآن) په باره كې وايي كله چې دوى ته راغى: نه دى دا مګر ښكاره جادو');
INSERT INTO "abulsalam" VALUES(34,44,'34:44',' حال دا چې مونږ دوى ته هېڅ كتابونه نه دي وركړي چې دوى يې لولي او نه له تا نه مخكې مونږ دوى ته هېڅ وېروونكى لېږلى دى');
INSERT INTO "abulsalam" VALUES(34,45,'34:45',' او هغو كسانو تكذیب كړى و، چې له دوى نه مخكې وو، حال دا چې دوى (د مكې كافران) نه دي رسېدلي لسمې برخې ته د هغه (قوت او قدرت) چې ما هغوى (مخكينو كافرانو) ته وركړى و، نو هغوى زمونږ د رسولانو تكذيب وكړ، بيا نو زما انكار څنګه و ');
INSERT INTO "abulsalam" VALUES(34,46,'34:46',' ته (دوى ته) وایه: بېشكه زه تاسو ته يواځې دیوې خبرې وعظ كوم، دا چې تاسو خاص الله ته ودرېږئ، دوه دوه او یو یو، بیا ښه فكر وكړئ چې ستاسو په ملګري كې هېڅ لېونتوب نشته، نه دى دا مګر تاسو لره وېروونكى، د ډېر سخت عذاب نه مخكې ');
INSERT INTO "abulsalam" VALUES(34,47,'34:47',' ته (دوى ته) ووایه: هغه چې ما له تاسو نه څه اجرت غوښتى وي، نو هغه دې تاسو لپاره وي، زما اجر نشته مګر په الله باندې دى او هغه په هر شي باندې ښه ګواه (او ښه خبردار) دى');
INSERT INTO "abulsalam" VALUES(34,48,'34:48',' ته ووایه: بېشكه زما رب حق رالېږي (هغه) د پټو خبرو ښه عالم دى');
INSERT INTO "abulsalam" VALUES(34,49,'34:49',' ته ووایه: حق راغى او باطل نه اول ځلې پیدا كول كولى شي او نه دوباره ژوندي كول كولى شي');
INSERT INTO "abulsalam" VALUES(34,50,'34:50',' ته (دوى ته) ووایه: كه چېرې زه ګمراه شم، نو یقینًا يواځې د خپل ځان په ضرر ګمراه كېږم او كه ما هدایت ومونده، نو په سبب د هغه كلام چې زما رب يې ما ته وحي كوي، بېشكه هغه ښه اورېدونكى، ډېر نژدې دى');
INSERT INTO "abulsalam" VALUES(34,51,'34:51',' او كه ته ووینې كله چې دوى ووېرېږي، نو هېڅ خلاصى به نه وي او دوى به له ډېر نژدې ځایه ونیول شي');
INSERT INTO "abulsalam" VALUES(34,52,'34:52',' او دوى به ووايي: مونږ په دغه (الله) ایمان راوړى دى، او د دوى لپاره به د ډېر لرې ځاى نه نیول له كومه وي!');
INSERT INTO "abulsalam" VALUES(34,53,'34:53',' حال دا چې یقینًا دوى په ده سره له دې نه مخكې كافران شوي وو او دوى به بې له لیدلو ویشتل كول، له ډېر لرې ځایه');
INSERT INTO "abulsalam" VALUES(34,54,'34:54',' او پرده به واچول شي د دوى په مینځ كې او په مینځ د هغه شي كې چې دوى يې غواړي (او ارمان يې كوي) لكه چې د دوى له ملګرو (هم جنسو) سره(دا كار) كړى شوى و له دوى نه مخكې، بېشكه دوى په اضطراب اچوونكي شك كې وو');
INSERT INTO "abulsalam" VALUES(35,1,'35:1',' ټولې ستاينې خاص د الله لپاره دي چې د اسمانونو او ځمكې پیدا كوونكى دى، ملايكو لره رسولان ګرځوونكى دى، (داسې ملايك) چې د وزرو والا دي، دوه دوه او درې درې او څلور څلور، هغه په تخلیق كې زیاتوي چې څه وغواړي، بېشكه الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(35,2,'35:2',' هر هغه شى چې الله يې د خلقو لپاره له رحمته پرانیزي، نو د هغه (رحمت) هیڅوك بندوونكى نشته او هر شى چې هغه يې بند كړي، نو د هغه راخلاصوونكى نشته د هغه (الله) نه بعد، همغه ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(35,3,'35:3',' اى خلقو! تاسو د الله هغه نعمتونه یاد كړئ چې په تاسو شوي دي، ایا له الله نه غیر (بل) خالق شته، چې تاسو ته له اسمان او ځمكې نه رزق دركوي؟ نشته هېڅ حق معبود مګر هم دى دى، نو كوم اړخ ته تاسو ګرځولى كېږئ');
INSERT INTO "abulsalam" VALUES(35,4,'35:4',' او كه چېري دوى ستا تكذیب وكړي، نو یقینًا له تا نه د مخكینو رسولانو هم تكذیب شوى دى، او خاص الله ته ټول كارونه بېرته ګرځولى شي');
INSERT INTO "abulsalam" VALUES(35,5,'35:5',' اى خلقو! بېشكه د الله وعده حقه ده، نو تاسو دې دنيايي ژوند له سره غره (دوكه) نه كړي او تاسو دې ډېر غره (دوكه) كوونكى د الله په باره كې له سره غره نه كړي');
INSERT INTO "abulsalam" VALUES(35,6,'35:6',' بېشكه شیطان هم ستاسو دښمن دى، نو تاسو هغه دښمن وګڼئ، یقینًا همدا خبره ده چې هغه خپله ډله رابلي، د دې لپاره چې دوى له دوزخیانو ځنې شي');
INSERT INTO "abulsalam" VALUES(35,7,'35:7',' هغه كسان چې كافران شوي دي، د دوى لپاره ډېر سخت عذاب دى او هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، د دوى لپاره مغفرت او ډېر لوى اجر دى');
INSERT INTO "abulsalam" VALUES(35,8,'35:8',' ایا نو هغه كس چې هغه ته خپل بد عمل ښايسته كړى شي، پس هغه، دغه (بد عمل) ښه ویني، (ګڼي) نو بېشكه الله ګمراه كوي چا له چې وغواړي او هدایت وركوي چا له چې وغواړي، نو ستا نفس دې په (ګمراهۍ د) دوى د افسوس له وجې نه نه هلاكېږي، بېشكه چې الله ښه عالم دى په هغو كارونو چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(35,9,'35:9',' او الله هغه ذات دى چې بادونه رالېږي،پس هغه (بادونه) ورېځې راپورته كوي، بیا مونږ هغه (ورېځ) مړ ښار ته بوځو، بیا په هغې سره ځمكه راژوندۍ كړو، د هغې له مرګ نه پس، همداسې به وي بیا ژوندي پورته كول');
INSERT INTO "abulsalam" VALUES(35,10,'35:10',' هر هغه څوك چې هغه عزت غواړي، نو خاص الله ته دى ټول عزت، همدغه (الله) ته پاكیزه خبرې پورته خېژي او نېك عمل دغه (كلمات) پورته كوي او هغه كسان چې ناكاره (بد) مكرونه جوړوي، د دوى لپاره ډېر سخت عذاب دى او د دغو كسانو مكر، هم هغه باطل (او هلاك) دى');
INSERT INTO "abulsalam" VALUES(35,11,'35:11',' او الله تاسو له خاورې نه پیدا كړي يئ، بیا يې له نطفې نه، بیا يې تاسو جوړې جوړې (ښځه او خاوند) وګرځولئ او نه حامله كېږي هېڅ یوه ښځه او نه زېږوي (بچي لره) مګر د هغه (الله) په علم سره او د هېڅ عمر اوږد كړى شوي عمر نه اوږدول كېږي او نه د هغه له عمر نه كمى كولى شي مګر ( دا هر څه) په كتاب كې (ثابت) دي، بېشكه دغه (د عمر لنډول او اوږدول) الله ته ډېر اسان دي');
INSERT INTO "abulsalam" VALUES(35,12,'35:12',' او هغه دواړه بحرونه(سیندونه) سره نه برابرېږي دا یو خوږ دى، ډېر خوږ، (تنده ماتوونكى) دى، د ده اوبه په اسانۍ سره تېرېدونكې دي او دا (بل) تریو(او) سخت تریخ دى، او تاسو له هر یو ځنې تازه غوښې خورئ اوتاسو هغه زیوارت راوباسئ چې تاسو يې اغوندئ او ته په دغه (سمندر) كې كشتۍ وینې، په داسې حال كې چې (اوبو لره) څیرونكې روانې وي، د دې لپاره چې تاسو د هغه (الله) له فضله ولټوئ او د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(35,13,'35:13',' دغه (الله) شپه په ورځ كې ننباسي او ورځ په شپه كې ننباسي او هغه په كار اچولي دي لمر اوسپوږمۍ، هر یو تر مقررې نېټې پورې روان دى، دغه الله ستاسو رب دى، خاص د ده لپاره بادشاهي ده او هغه (معبودان) چې تاسو يې له دغه (الله) نه غیر بولئ، دوى د كجورې د زړي د پردې هم مالكان نه دي');
INSERT INTO "abulsalam" VALUES(35,14,'35:14',' كه تاسو دوى وبولئ، دوى ستاسو بلنه نه اوري او كه چېرې وايې وري، نو دوى ستاسو بلنه قبلولى نه شي او د قیامت په ورځ به دوى ستاسو په شریك جوړولو منكر شي او تا ته (بل څوك) خبر نشي دركولى د ښه خبرداره (الله) په شان');
INSERT INTO "abulsalam" VALUES(35,15,'35:15',' اى خلقو! هم تاسو الله ته محتاجان يئ، او الله، هم دى ښه بې پروا، ښه ستايل شوى دى');
INSERT INTO "abulsalam" VALUES(35,16,'35:16',' كه هغه اراده وكړي (نو) تاسو به ختم كړي او نور نوى مخلوق به راولي');
INSERT INTO "abulsalam" VALUES(35,17,'35:17',' او دغه الله ته هېڅ ګران (او مشكل نه دي');
INSERT INTO "abulsalam" VALUES(35,18,'35:18',' او هېڅ پورته كوونكى د بل د ګناه بار نه پورته كوي او كه دروند بارى خپل بار ته (بل څوك) راوبولي (، نو) له هغه نه به هېڅ شى وانه خستى شي اګر كه د خپلوۍ والا هم وي، بېشكه همدا خبره ده چې ته يواځې هغه كسان وېروې چې له خپل رب نه په غیبو سره خشیت (او وېره) كوي او لمونځ قايموي او هغه څوك چې پاكي حاصلوي، نو همدا خبره ده چې هغه د ځان (د فايدې) لپاره پاكي حاصلوي اوخاص الله ته بېرته ورتله دي');
INSERT INTO "abulsalam" VALUES(35,19,'35:19',' او ړوند اولیدونكى (بينا) سره برابرېدى نشي');
INSERT INTO "abulsalam" VALUES(35,20,'35:20',' او نه هم تیارې او رڼا)');
INSERT INTO "abulsalam" VALUES(35,21,'35:21',' او نه هم سېورى او ګرم باد (یا تېز لمر) ');
INSERT INTO "abulsalam" VALUES(35,22,'35:22',' او نه هم ژوندي او مړي، بېشكه الله اوروي هغه چا ته چې يې وغواړي او ته نشې اورولى هغه چا ته چې په قبرونو كې دي');
INSERT INTO "abulsalam" VALUES(35,23,'35:23',' نه يې ته مګر وېروونكى');
INSERT INTO "abulsalam" VALUES(35,24,'35:24',' بېشكه مونږ ته په حق (دین) سره لېږلى يې چې زېرى وركوونكى او وېروونكى يې او نه و هېڅ یو امت مګر په هغه كې یو وېروونكى تېر شوى دى');
INSERT INTO "abulsalam" VALUES(35,25,'35:25',' او كه چېرې دوى تا دروغجن وبولي، نو یقینًا (خپل انبیاء) دروغجن بللي وو هغو كسانو چې له دوى نه مخكې وو، دوى ته د دوى رسولان په ښكاره دلیلونو سره، او په صحیفو سره او په روښانه رڼا كوونكي كتاب سره راغلي وو');
INSERT INTO "abulsalam" VALUES(35,26,'35:26',' بیا ما هغه كسان ونیول چې كافران شول، نو زما انكار څرنګه و؟');
INSERT INTO "abulsalam" VALUES(35,27,'35:27',' ایا تا نه دي ليدلي چې بېشكه الله له بره نه اوبه نازلې كړې دي، بیا مونږ په دغو سره داسې مېوې راوېستلې دي چې د هغو رنګونه مختلف دي او په غرونو كې ځینې سپينې او سرې لارې دي، چې د دغو (لارو) رنګونه سره مختلف دي او (ځینې په دوى كې) ډېرې تكې تورې دي');
INSERT INTO "abulsalam" VALUES(35,28,'35:28',' او له خلقو او زنده سرو او څارویو څخه (داسې قسم دى چې) د دغو رنګونه سره مختلف دي هم داسې، بېشكه هم دا خبره ده چې له الله نه د هغه په بنده ګانو كې يواځې عالمان وېږېږي، بېشكه الله ښه غالب، ډېر بخښونكى دى');
INSERT INTO "abulsalam" VALUES(35,29,'35:29',' بېشكه هغه كسان چې د الله كتاب لولي او لمونځ قايموي او انفاق كوي له هغه (مال) نه چې دوى ته مونږ وركړى دى، په پټه او په ښكاره، دوى د داسې تجارت امېد لري چې له سره به هلاك نشي');
INSERT INTO "abulsalam" VALUES(35,30,'35:30',' د دې لپاره چې (الله) دوى ته د دوى اجرونه پوره وركړي او دوى ته له خپله فضله زیات وركړي، بېشكه چې دغه (الله) ډېر بخښونكى، ښه قدردان دى');
INSERT INTO "abulsalam" VALUES(35,31,'35:31',' او هغه څه چې مونږ تا ته له كتابه وحي كړي دي هم دغه حق دي، په داسې حال كې چې تصدیق كوونكى دى د هغو (كتابونو) چې له ده نه مخكې وو، بېشكه الله په خپلو بنده ګانو باندې خامخا ښه خبردار، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(35,32,'35:32',' بیا مونږ د دغه كتاب (قرآن) وارثان كړل هغه كسان چې مونږ په خپلو بنده ګانو كې غوره (او انتخاب) كړي وو، بیا په دوى كې ځینې په خپل ځان ظلم كوونكي دي او ځینې په دوى كې مینځ لاري دي او ځینې په دوى كې نېكیو ته ړومبى كېدونكي دي، د الله په حكم سره، یقینًا همدغه ډېر لوى فضل دى');
INSERT INTO "abulsalam" VALUES(35,33,'35:33',' د تل استوګنې جنتونه دي، دوى به دغو ته ننوځي، دوى به په دغو (جنتونو) كې د سرو زرو په بنګړو سره او په ملغلرو سره زیور دار (اراسته) كولى شي او د دوى جامې به په دغو (جنتونو) كې ورېښم وي');
INSERT INTO "abulsalam" VALUES(35,34,'35:34',' او دوى به ووايي: ټول د كمال صفتونه خاص هغه الله لره دي چې زمونږ نه يې غم (خفګان) ختم كړ، بېشكه زمونږ رب خامخا ډېر بخښونكى، بې حده قدردان دى');
INSERT INTO "abulsalam" VALUES(35,35,'35:35',' هغه (الله) چې مونږ ته يې له خپله فضله د اوسېدنې كور ته راوستو، چې مونږ ته په دغو كې نه څه تكلیف رارسېږي او نه مونږ ته په دغو كې ستوماني رارسېږي');
INSERT INTO "abulsalam" VALUES(35,36,'35:36',' او هغه كسان چې كافران شوي دي، د دوى لپاره د جهنّم اور دى، په دوى به نه ( د بل مرګ) فیصله كېږي، پس چې دوى مړه شي او نه به له دوى نه د دغه (جهنّم) له عذاب نه كمى كولى شي، همداسې مونږ هر ډېر ناشكره ( بې ایمانه) ته بدله وركوو');
INSERT INTO "abulsalam" VALUES(35,37,'35:37',' او دوى به په دغه (دوزخ) كې چغې سورې وهي، (وايي به:) اى زمونږه ربه! ته مونږ راوباسه، مونږ به نېك عمل وكړو غیر له هغه عمل نه چې مونږ به كاوه، او ایا مونږ تاسو ته دومره عمر نه و دركړى چې په هغه كې پند اخلي هر هغه څوك چې پند اخيستل غواړي، او تاسو ته وېروونكى هم راغلى و، نو تاسو (اوس عذاب) وڅكئ، پس د ظالمانو لپاره هېڅ مدد كوونكى نشته');
INSERT INTO "abulsalam" VALUES(35,38,'35:38',' بېشكه چې الله د اسمانونو او ځمكې د غیبو عالم دى، بېشكه هغه د سینو په خبرو ښه پوهېدونكى دى');
INSERT INTO "abulsalam" VALUES(35,39,'35:39',' دغه(الله) همغه ذات دى چې تاسو يې په ځمكه كې (د تېرو خلقو) خلیفګان جوړ كړئ، نو هغه څوك چې كافر شو، نو خاص په همده باندې د ده (د) كفر (ضرر) دى او كافرانو ته د دوى كفر د خپل رب په نیز نه ورزیاتوي مګر غضب، او كافرانو ته د دوى كفر نه وزریاتوي مګر تاوان');
INSERT INTO "abulsalam" VALUES(35,40,'35:40',' (اى نبي!) ته (دوى ته) ووایه: ایا تاسو خپل شریكان لیدلي دي هغه چې تاسو يې له الله نه غیر بلئ، تاسو ما ته وښیئ چې دوى له ځمكې څخه څه پیدا كړي دي، یا د دوى لپاره په اسمانونو كې څه شراكت (او برخه) شته؟ یا مونږ دوى ته څه كتاب وركړى دى، نو دوى له هغه نه په پاخه دلیل باندې (ولاړ) دي؟ بلكې ظالمان (چې دي) د دوى ځینې د ځینو نورو سره وعده نه كوي مګر د دوكې');
INSERT INTO "abulsalam" VALUES(35,41,'35:41',' بېشكه چې الله اسمانونه او ځمكه ساتي او (نیسي يې) چې زايل نشي او قسم د ى كه دواړه په زوال شي (نو) له هغه (الله) نه غیر به يې هیڅوك ونه ساتلى شي) (ونه نیولى) شي، بېشكه هغه (الله) دى، ښه تحمل والا، ډېر بخښونكى');
INSERT INTO "abulsalam" VALUES(35,42,'35:42',' او دوى په الله قسمونه وخوړل، خپل پاخه قسمونه، خامخا كه دوى ته كوم وېروونكى راغى (نو) دوى په خامخا ضرور په امتونو كې له هر یو نه ډېر هدایت موندونكي وي، نوكله چې دوى ته وېروونكى راغى، نو دوى ته يې زیاته نه كړه مګر تېښته');
INSERT INTO "abulsalam" VALUES(35,43,'35:43',' په ځمكه كې د لويي كولو لپاره او د ډېر بد مكر كولو لپاره، او ډېر بد مكر يواځې په خپل اهل (مكر كوونكي) چاپېره كېږي، نو دوى انتظار نه كوي مګر د ړومبنو خلقو د طریقې، نو ته به د الله طریقې لره له سره هېڅ تبدیلي ونه مومې او ته به د الله طریقې لره له سره څه ګرځول ونه مومې');
INSERT INTO "abulsalam" VALUES(35,44,'35:44',' ایا دوى په ځمكه كې نه دي ګرځېدلي، پس چې وویني چې د هغو كسانو انجام څنګه و چې له دوى نه مخكې وو، او هغوى له دوى نه په قوت كې زیات سخت و او الله كله هم داسې نه دى چې هغه لره دې عاجزه كړي هېڅ كوم شى په اسمانونوكې او په ځمكه كې، بېشكه هغه (الله) له ازله ښه پوه، ښه قادر دى');
INSERT INTO "abulsalam" VALUES(35,45,'35:45',' او كه چېرې الله خلق نیولي وى په سبب د هغو (بدو عملونو) چې دوى كړي دي، نو د دې (ځمكې) په شا به يې هېڅ زنده سر(خوځنده) نه و پرېښى او لېكن دوى تر ټاكل شوې نېټې پورې وروسته كوي، نو كله چې د دوى نېټه راشي، نو بېشكه الله خپلو بنده ګانو لره ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(36,1,'36:1',' يا، سين');
INSERT INTO "abulsalam" VALUES(36,2,'36:2',' قسم دى په قرآن، چې ډېر د حكمت والا دى');
INSERT INTO "abulsalam" VALUES(36,3,'36:3',' بېشكه ته، یقینًا له رسولانو ځنې يې');
INSERT INTO "abulsalam" VALUES(36,4,'36:4',' په نېغه لار يې');
INSERT INTO "abulsalam" VALUES(36,5,'36:5',' (دغه قرآن) د ډېر غالب، بې حده مهربان نازلول دي');
INSERT INTO "abulsalam" VALUES(36,6,'36:6',' د دې لپاره چې ته هغه قوم ووېروې چې د دوى پلرونه نه دي وېرول شوي، نو (ځكه) دوى غافلان دي');
INSERT INTO "abulsalam" VALUES(36,7,'36:7',' یقینًا یقینًا د دوى په زیاترو باندې (د عذاب) خبره ثابته شوې ده، نو (په دې وجه) دوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(36,8,'36:8',' بېشكه مونږ د دوى په غاړو (څټونو) كې طوقونه اچولي دي، نو دغه (طوقونه) تر زنو پورې دي، نو (په دې سره) دوى جګ سري كړى شوي دي');
INSERT INTO "abulsalam" VALUES(36,9,'36:9',' او مونږ د دوى په مخه كې یو دېوال كړى دى او یو دېوال د دوى شا ته، پس مونږ دوى پټ كړي دي، لهذا دوى هېڅ نه ویني');
INSERT INTO "abulsalam" VALUES(36,10,'36:10',' او دوى ته یو شان ده كه ته دوى ووېروې، یا ته دوى ونه وېروې، دوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(36,11,'36:11',' بېشكه همدا خبره ده چې ته يواځې هغه څوك وېروې چې د ذكر (قرآن) پیروي كوي او له رحمٰن نه په نالیدلو وېرېږي، نو ته دغه كس ته د مغفرت او د ډېر عزت والا اجر زېرى وركړه');
INSERT INTO "abulsalam" VALUES(36,12,'36:12',' بېشكه مونږ، همدا مونږ مړي ژوندي كوو او مونږ هغه څه لیكو چې دوى يې مخكې لېږي او د دوى پاتې نښې (هم) او هر شى، مونږ هغه په ښكاره اصل (لوح محفوظ) كې درج كړى دى');
INSERT INTO "abulsalam" VALUES(36,13,'36:13',' او ته دوى ته د دغه كلي اوسېدونكي مثال وګرځوه، كله چې دوى ته رسولان راغلل');
INSERT INTO "abulsalam" VALUES(36,14,'36:14',' كله چې مونږ دوى ته دوه پېغمبران ولېږل، نو دوى هغو دواړو ته دروغجن وویل، نو مونږ (دغه) په دریم سره مضبوط كړل، نو دغو (رسولانو) وویل: بېشكه مونږ تاسو ته رسولان (رالېږل شوي) یو');
INSERT INTO "abulsalam" VALUES(36,15,'36:15',' دوى وویل، تاسو نه يئ مګر زمونږ په شان انسانان او رحمٰن هېڅ شى هم نه دى نازل كړى، نه یئ تاسو مګر دروغ وایئ');
INSERT INTO "abulsalam" VALUES(36,16,'36:16',' دغو (رسولانو) وویل: زمونږ رب پوهېږي، بېشكه مونږ تاسو ته یقینًا (پېغمبران) راستول شوي یو ');
INSERT INTO "abulsalam" VALUES(36,17,'36:17',' او زمونږ په ذمه نشته مګر ښكاره پېغام رسونه');
INSERT INTO "abulsalam" VALUES(36,18,'36:18',' دغو خلقو وویل: بېشكه مونږ په تاسو سره بدفالي نیولې ده، قسم دى كه تاسو منع نشوئ (، نو) مونږ به خامخا ضرور تاسو سنګسار كړو او زمونږ له جانبه به تاسو ته خامخا ضرور ډېر دردوونكى عذاب درورسېږي');
INSERT INTO "abulsalam" VALUES(36,19,'36:19',' دغو(پېغمبرانو) وویل: ستاسو بدفالي له هم تاسو سره ده، ایا نو كه تاسو ته پند دركړى شي (نو تاسو دغه ته بدفالي وایئ؟) بلكې تاسو له حده تېرېدونكى قوم یئ');
INSERT INTO "abulsalam" VALUES(36,20,'36:20',' او د ښار تر ټولو لرې جانب نه یو سړى راغى، چې منډې يې وهلې، ويې ويل:اى زما قومه! تاسو د رسولانو پیروي وكړئ ');
INSERT INTO "abulsalam" VALUES(36,21,'36:21',' تاسو د هغه چا پیروي وكړئ چې له تاسو نه څه اجر (او مزدوري) نه غواړي، په داسې حال كې چې دوى (پخپله) هدایت موندونكي دي');
INSERT INTO "abulsalam" VALUES(36,22,'36:22',' او په ما څه شوي دي (څه مانع دى) چې زه به د هغه ذات عبادت نه كوم چې زه يې پیدا كړى یم او خاص هغه ته به تاسو بېرته بېول كېږئ');
INSERT INTO "abulsalam" VALUES(36,23,'36:23',' ایا زه له هغه (الله) نه غیر نور داسې معبوان ونیسم چې كه رحمٰن ما ته د ضرر رسولو اراده وكړي، (نو) له ما نه به د دوى سفارش هېڅ شى (ضرر) دفع (لرې) نه كړى شي او نه به ما دوى خلاص كړى شي');
INSERT INTO "abulsalam" VALUES(36,24,'36:24',' بېشكه زه به په دغه وخت كې خامخا په ښكاره ګمراهۍ كې یم');
INSERT INTO "abulsalam" VALUES(36,25,'36:25',' بېشكه زه چې یم، ما ستاسو په رب ایمان راوړى دى، نو تاسو (ټول) زما (دا خبره) واورئ ');
INSERT INTO "abulsalam" VALUES(36,26,'36:26',' ده ته وویل شو: ته جنت ته ننوځه! ده وویل: اى كاشكې چې زما قوم پوه شي! ');
INSERT INTO "abulsalam" VALUES(36,27,'36:27',' په هغه شي چې زما رب ما ته په هغه سره بخښنه وكړه او زه يې له عزتمنو څخه كړمه');
INSERT INTO "abulsalam" VALUES(36,28,'36:28',' او مونږ له هغه نه وروسته د ده په قوم باندې له اسمان نه هېڅ لښكر راكوز نه كړ او نه مونږ نازلوونكي وو');
INSERT INTO "abulsalam" VALUES(36,29,'36:29',' هغه (سزا) نه وه مګر یوه چغه، نو ناڅاپه دوى مړه یخ پخ پراته وو');
INSERT INTO "abulsalam" VALUES(36,30,'36:30',' اى افسوسه په بنده ګانو! دوى ته به نه راته كوم رسول مګر دوى به په هغه پورې استهزا كولې');
INSERT INTO "abulsalam" VALUES(36,31,'36:31',' ایا دوى نه ویني چې له دوى نه مخكې مونږ څومره امتونه هلاك كړي دي، چې یقینًا هغوى دوى ته بېرته نه راګرځي');
INSERT INTO "abulsalam" VALUES(36,32,'36:32',' او نه دي دا هر یو مګر ټول به زمونږ په وړاندې حاضر كړى شوي وي');
INSERT INTO "abulsalam" VALUES(36,33,'36:33',' او د دوى لپاره یوه لویه نښه مړه (وچه كلكه) ځمكه ده، چې مونږ هغه ژوندۍ كړه او له هغې نه مونږ دانې راووېستې، نو د دغو (دانو) ځنې دوى خوراك كوي');
INSERT INTO "abulsalam" VALUES(36,34,'36:34',' او مونږ په دغه (ځمكه) كې د كجورو او انګورو باغونه پیدا كړي دي او مونږه په دغه (ځمكه) كې چینې جاري كړي دي');
INSERT INTO "abulsalam" VALUES(36,35,'36:35',' د دې لپاره چې دوى د دغو (څیزونو) له مېوو نه خوراك وكړي، حال دا چې دغه (مېوې) د دوى لاسونو نه دي پیدا كړي، ایا نو دوى شكر نه وباسي');
INSERT INTO "abulsalam" VALUES(36,36,'36:36',' پاكي ده هغه ذات لره چې دغه ټولې جوړې يې پیدا كړي دي، له هغو څیزونو نه چې ځمكه يې زرغونوي او د دوى له نفسونو نه او له هغو څیزونو نه چې دوى يې نه پېژني');
INSERT INTO "abulsalam" VALUES(36,37,'36:37',' او د دوى لپاره لویه نښه شپه (هم) ده، مونږ له هغې نه ورځ راوباسو، نو ناڅاپه دوى په تیارو كې ننوتونكي وي');
INSERT INTO "abulsalam" VALUES(36,38,'36:38',' او لمر (دى چې) په خپلې ټاكلې لارې باندې روان دى، دغه د ډېر غالب، ښه پوه ذات مُقَدر (اندازه) كول دي');
INSERT INTO "abulsalam" VALUES(36,39,'36:39',' او سپوږمۍ لره، مونږ د هغې لپاره منزلونه مقرر كړي دي، تر هغه پورې چې دا شي لكه د كجورې وچ ډنډوركى');
INSERT INTO "abulsalam" VALUES(36,40,'36:40',' لمر لره ممكن نه دي چې سپوږمۍ رالاندې كړي او نه شپه په ورځ ړومبۍ كېدونكې ده او هر یو په (خپل) مدار كې لامبو وهي');
INSERT INTO "abulsalam" VALUES(36,41,'36:41',' او د دوى لپاره لویه نښه داده چې بېشكه مونږ د دوى اولاد په ډكه بېړۍ كې سواره كړي دي');
INSERT INTO "abulsalam" VALUES(36,42,'36:42',' او د دوى لپاره مو د دغې (بېړۍ) په شان هغه څیزونه پیدا كړي دي چې دوى پرې سورېږي');
INSERT INTO "abulsalam" VALUES(36,43,'36:43',' او كه مونږ وغواړو (نو) دوى به غرق كړو، بیا به دوى لپاره نه هېڅ فریادرس وي او نه به دوى له غرقېدونه خلاصول كېږي');
INSERT INTO "abulsalam" VALUES(36,44,'36:44',' مګر (نه يې غرقوو) له خپل جانب نه د رحمت په سبب او تر یو وخته پورې د فايدې اخيستلو لپاره');
INSERT INTO "abulsalam" VALUES(36,45,'36:45',' او كله چې دوى ته وویل شي: تاسو ځان وساتئ له هغه (عذاب) نه چې ستاسو په مخكې دى او له هغه نه چې ستاسو شا ته (په اخرت كې) دى، د دې لپاره چې په تاسو رحم وكړى شي ');
INSERT INTO "abulsalam" VALUES(36,46,'36:46',' او دوى ته د خپل رب له دلیلونو نه هېڅ دلیل نه راځي، مګر دوى له هغو نه مخ ګرځوونكي دي');
INSERT INTO "abulsalam" VALUES(36,47,'36:47',' او كله چې دوى ته وویل شي چې تاسو له هغه (مال) نه ځینې ولګوئ چې الله تاسو ته دركړى دى، نو كافران شوي (د الله منكر) كسان هغو خلقو ته چې ایمان يې راوړى دى؛ وايي: ایا مونږ هغه چا ته طعام وركړو چې كه الله غوښتلى (نو) هغه ته به يې طعام وركړى وى. نه یئ تاسو مګر په ښكاره ګمراهۍ كې');
INSERT INTO "abulsalam" VALUES(36,48,'36:48',' او دوى (كفار) وايي: (د قیامت) دغه وعده به كله وي كه تاسو رښتیني یئ');
INSERT INTO "abulsalam" VALUES(36,49,'36:49',' دوى انتظار نه كوي مګر د یوې داسې چغې چې دوى به ونیسي، په داسې حال كې چې دوى به یو له بله سره جګړې كوي');
INSERT INTO "abulsalam" VALUES(36,50,'36:50',' نو نه به د وصیت كولو توان ولري او نه به خپل اهل ته بېرته ورشي');
INSERT INTO "abulsalam" VALUES(36,51,'36:51',' او په شپېلۍ كې به پوكى وكړى شي، نو ناڅاپه به دوى له قبرونو نه د خپل رب په طرف منډې وهي');
INSERT INTO "abulsalam" VALUES(36,52,'36:52',' دوى به وايي: اى زمونږ هلاكته! مونږ له خپل خوب ځاى نه چا راپاڅولو؟ (جواب به وركړى شي) دا هغه (ژوندون) دى چې رحمٰن يې وعده كړې وه او رسولانو رښتیا ویلي وو');
INSERT INTO "abulsalam" VALUES(36,53,'36:53',' دغه (پوكى) به نه وي مګر یوه چغه، نو ناڅاپه به دوى ټول زمونږ په وړاندي حاضر كړى شوي وي');
INSERT INTO "abulsalam" VALUES(36,54,'36:54',' نو نن ورځ به په هېڅ یو نفس باندې هېڅ شى ظلم ونه كړى شي او تاسو ته به بدله نه دركولى كېږي مګر هغه چې تاسو څه عمل كاوه');
INSERT INTO "abulsalam" VALUES(36,55,'36:55',' بېشكه د جنت والا نن ورځ په مشغولتیا كې دي، خوند اخيستونكي دي');
INSERT INTO "abulsalam" VALUES(36,56,'36:56',' دوى او د دوى ښځې په سیورو كې (ناست) دي، په تختونو باندې تكیه وهونكي دي');
INSERT INTO "abulsalam" VALUES(36,57,'36:57',' د دوى لپاره په دغو (جنتونو) ښې مېوې دي او د دوى لپاره هغه څه دي چې دوى يې غواړي');
INSERT INTO "abulsalam" VALUES(36,58,'36:58',' سلام دى (چې دوى ته به ویلى شي) ښكاره ویل د رب له جانبه، چې ډېر مهربان دى');
INSERT INTO "abulsalam" VALUES(36,59,'36:59',' او (وبه ویل شي:) اى مجرمانو! تاسو نن ورځ بېل بېل شئ');
INSERT INTO "abulsalam" VALUES(36,60,'36:60',' اى بني ادمو! ایا ما تاسو ته كلك حكم نه و كړى، داسې چې تاسو د شیطان عبادت مه كوئ. یقینًا هغه ستاسو ښكاره دښمن دى');
INSERT INTO "abulsalam" VALUES(36,61,'36:61',' او داسې چې تاسو زما عبادت كوئ، دا نېغه لار ده؟');
INSERT INTO "abulsalam" VALUES(36,62,'36:62',' او یقینًا یقینًا ده (شیطان) په تاسو كې ډېر خلق ګمراه كړي دي، ایا نو تاسو داسې نه یئ چې عقل وچلوئ؟');
INSERT INTO "abulsalam" VALUES(36,63,'36:63',' (منكرانو ته به په اخرت كې وویل شي:) دا هغه جهنم دى چې تاسو ته به يې وعده دركول كېده');
INSERT INTO "abulsalam" VALUES(36,64,'36:64',' تاسو ده ته په نن ورځ ننوځئ، په سبب د هغه كفر چې تاسو به كاوه');
INSERT INTO "abulsalam" VALUES(36,65,'36:65',' نن ورځ به مونږ د دوى په خولو مهر ولګوو، او زمونږ سره به د دوى لاسونه خبرې كوي او د دوى پښې به ګواهي كوي په هغو كارونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(36,66,'36:66',' او كه مونږ غوښتلى (نو) د دوى سترګې به مو محوه (هوارې) كړې وى، نو دوى به (خپلې معلومې) لارې ته وړاندې شوي وى، بیا به دوى څنګه لېدله');
INSERT INTO "abulsalam" VALUES(36,67,'36:67',' او كه مونږ غوښتلى (نو) خامخا مونږ به دوى په خپلو ځایونو كې مسخ كړي وو، نو (بیا به) دوى نه د وړاندې تللو طاقت لاره او نه به بېرته ګرځېدلى وى');
INSERT INTO "abulsalam" VALUES(36,68,'36:68',' او هر هغه څوك چې مونږ هغه ته اوږد عمر وركړو (نو) هغه په تخلیق (ساخت) كې نسكور كړو، ایا نو دوى عقل مندي نه كوي');
INSERT INTO "abulsalam" VALUES(36,69,'36:69',' او مونږ ده ته شعر نه دى ښودلى او نه دا له ده سره ښايېږي، نه دى دا كتاب مګر نصیحت دى او بیانوونكى قرآن دى');
INSERT INTO "abulsalam" VALUES(36,70,'36:70',' د دې لپاره چې دغه (قرآن، یا نبي) هغه څوك ووېروي چې ژوندى وي او د دې لپاره چې په كافرانو خبره ثابته شي');
INSERT INTO "abulsalam" VALUES(36,71,'36:71',' ایا دوى نه دي كتلي چې بېشكه مونږ له هغو څیزونو نه چې زمونږ لاسونو جوړ كړي دي، دوى لپاره څاروي پیدا كړي دي، بیا دوى د هغو مالكان دي');
INSERT INTO "abulsalam" VALUES(36,72,'36:72',' او مونږ دغه (څاروي) د دوى لپاره تابع كړي دي، نو په دغو كې ځینې د دوى سورلۍ دي او په دغو كې ځینې دوى خوري');
INSERT INTO "abulsalam" VALUES(36,73,'36:73',' او د دوى لپاره په دغو (څارویو) كې فايدې او مشروبونه دي، ایا نو دوى شكر نه وباسي؟');
INSERT INTO "abulsalam" VALUES(36,74,'36:74',' او دوى له الله نه غیر نور معبودان ونیول، د دې لپاره چې دوى سره مدد وكړى شي');
INSERT INTO "abulsalam" VALUES(36,75,'36:75',' دغه (معبودان) د دوى د مدد كولو طاقت نه لري، په داسې حال كې چې دوى (مشركان) به دغو (معبودانو) ته حاضر كړى شوى لښكر وي');
INSERT INTO "abulsalam" VALUES(36,76,'36:76',' نو تا دې د دوى وینا غمژن نه كړي، بېشكه مونږ پوهېږو په هغه (حسد، كینه) چې دوى يې پټوي او په هغه (كفر، عناد) چې دوى يې څرګندوي');
INSERT INTO "abulsalam" VALUES(36,77,'36:77',' ایا انسان نه دي كتلي چې بېشكه مونږ دغه (انسان) له نطفې (یو څاڅكې اوبو) نه پیدا كړى دى، نو ناڅاپه دى ښكاره سخت جګړه كوونكى دى');
INSERT INTO "abulsalam" VALUES(36,78,'36:78',' او دى زمونږ لپاره مثال بیانوي او خپل پیدایښت يې هېر كړى دى، وايي: دغه هډوكي به څوك راژوندي كوي، په داسې حال كې چې هغه وراسته وي');
INSERT INTO "abulsalam" VALUES(36,79,'36:79',' ته (دوى ته) ووایه: دا به هغه ذات راژوندي كوي چې دا يې اول ځلې پیدا كړي دي، او هغه په خپل مخلوق باندې ښه عالم دى');
INSERT INTO "abulsalam" VALUES(36,80,'36:80',' هغه ذات چې تاسو لپاره يې له شنې ونې نه اور پیدا كړى دى، نو ناڅاپه تاسو له دغې (شنې ونې) نه اور بلوئ');
INSERT INTO "abulsalam" VALUES(36,81,'36:81',' ایا هغه ذات چې اسمانونه او ځمكه يې پیدا كړي دي، په دې نه دى قادر چې هغه د دوى په شان (نور مخلوق) پیدا كړي؟ ولې نه! او هم دى ښه پیدا كوونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(36,82,'36:82',' بېشكه د هغه امر (حكم) هم دا وي چې كله د یو شي اراده وكړي، دا چې هغه ته ووايي چې پیدا شه، نو هغه پیدا شي');
INSERT INTO "abulsalam" VALUES(36,83,'36:83',' نو پاكي ده هغه (الله) لره چې خاص د هغه په لاس كې د هر شي باچاهي ده او خاص هغه ته به بېرته بېولى كېږئ');
INSERT INTO "abulsalam" VALUES(37,1,'37:1',' قسم دى (د ملايكو) په صف جوړونكو ډلو باندې، صف جوړول');
INSERT INTO "abulsalam" VALUES(37,2,'37:2',' بیا (شیطانانو لره) په شړونكو (ملايكو دى) سخت شړل');
INSERT INTO "abulsalam" VALUES(37,3,'37:3',' بیا د ذكر په لوستونكو باندې');
INSERT INTO "abulsalam" VALUES(37,4,'37:4',' چې بېشكه ستاسو معبود یقینًا یو دى');
INSERT INTO "abulsalam" VALUES(37,5,'37:5',' چې د اسمانونو او ځمكې رب دى او د هغه څه چې د دواړو په مینځ كې دي او د مشرقونو رب دى');
INSERT INTO "abulsalam" VALUES(37,6,'37:6',' بېشكه مونږ (ځمكې ته) زیات نژدې اسمان په زینت سره ښايسته كړى دى، چې ستوري دي');
INSERT INTO "abulsalam" VALUES(37,7,'37:7',' او له هر سركښه شیطان نه مو ښه ساتلى دى');
INSERT INTO "abulsalam" VALUES(37,8,'37:8',' دغه (شیطانان) تر ټولو لوړ مجلس ته غوږ نشي نیولى او دوى له هره جانبه ویشتل شي');
INSERT INTO "abulsalam" VALUES(37,9,'37:9',' د شړلو لپاره او د دوى لپاره همېشنى عذاب دى');
INSERT INTO "abulsalam" VALUES(37,10,'37:10',' غیر له هغه (شیطان) نه چې وتښتوي یو كرت تښتونه، نو په ده پسې شي لمبه رڼا كوونكې');
INSERT INTO "abulsalam" VALUES(37,11,'37:11',' نو ته له دوى نه پوښتنه وكړه چې ایا دوى د تخلیق په لحاظ ډېر سخت دي، یا هغه چې مونږ پیدا كړي دي؟، بېشكه مونږ دوى له سرېښناكې خټې نه پیدا كړي دي');
INSERT INTO "abulsalam" VALUES(37,12,'37:12',' بلكې ته (د دوى په انكار) تعجب كوې، په داسې حال كې چې دوى (په تا پورې) مسخرې كوي');
INSERT INTO "abulsalam" VALUES(37,13,'37:13',' او كله چې دوى ته نصیحت وكړى شي، دوى پند (نصیحت) نه اخلي');
INSERT INTO "abulsalam" VALUES(37,14,'37:14',' او كله چې دوى كومه (نښه دلیل) وویني، (نو ورپورې) ډېرې مسخرې كوي');
INSERT INTO "abulsalam" VALUES(37,15,'37:15',' او وايي چې نه دى دا (چې مونږ ولید) مګر ښكاره جادو');
INSERT INTO "abulsalam" VALUES(37,16,'37:16',' ایا كله چې مونږ مړه شو او خاورې او هډوكي شو، ایا په رښتیا به مونږ خامخا بیا ژۃوندي راپاڅول كېږو؟');
INSERT INTO "abulsalam" VALUES(37,17,'37:17',' او ایا زمونږ ړومبني پلرونه هم؟');
INSERT INTO "abulsalam" VALUES(37,18,'37:18',' ته (دوى ته) ووایه: هو! په داسې حال كې چې تاسو به ذلیل هم یئ');
INSERT INTO "abulsalam" VALUES(37,19,'37:19',' پس همدا خبره ده چې هغه (قیامت) خو یوه چغه ده، نو ناڅاپه به دوى ګوري');
INSERT INTO "abulsalam" VALUES(37,20,'37:20',' او دوى به وايي: اى زمونږ هلاكته! دا د بدلې ورځ ده');
INSERT INTO "abulsalam" VALUES(37,21,'37:21',' دا د فیصلې ورځ ده، هغه ورځ چې تاسو به د دې تكذیب كاوه');
INSERT INTO "abulsalam" VALUES(37,22,'37:22',' (الله به ملايكو ته ووايي:) تاسو راټول كړئ هغه كسان چې ظلم (شرك) يې كړى دى او د دوى جوړې (د عقیدې ملګري) او هغه څه چې دوى به يې عبادت كاوه');
INSERT INTO "abulsalam" VALUES(37,23,'37:23',' له الله نه غیر، نو تاسو دوى د دوزخ په لاره روان (او برابر) كړئ');
INSERT INTO "abulsalam" VALUES(37,24,'37:24',' او دوى ودروئ، بېشكه له دوى نه پوښتنې كول شي');
INSERT INTO "abulsalam" VALUES(37,25,'37:25',' په تاسو څه دي چې یو له بله سره مرسته نه كوئ؟');
INSERT INTO "abulsalam" VALUES(37,26,'37:26',' بلكې دوى نن ورځ (پخپله) تسليمېدونكي دي');
INSERT INTO "abulsalam" VALUES(37,27,'37:27',' او د دوى ځینې به ځینو نورو ته مخ ورواړوي، یو تربله به پوښتنې كوي');
INSERT INTO "abulsalam" VALUES(37,28,'37:28',' وبه وايي (كشران مشرانو ته) بېشكه هم تاسو وئ چې مونږ ته به په زور سره راتللئ');
INSERT INTO "abulsalam" VALUES(37,29,'37:29',' وبه وايي (مشران په جواب كې) بلكې تاسو پخپله ایمان راوړونكي نه وئ');
INSERT INTO "abulsalam" VALUES(37,30,'37:30',' او زمونږ لپاره پر تاسو باندې هېڅ زور (تسلط) نه و، بلكې تاسو پخپله سركښه قوم وئ');
INSERT INTO "abulsalam" VALUES(37,31,'37:31',' نو په مونږه باندې زمونږ د رب خبره ثابته شوه، یقینًا مونږ خامخا (عذاب لره) څكونكي یو');
INSERT INTO "abulsalam" VALUES(37,32,'37:32',' نو مونږ تاسو ګمراه كړئ، بېشكه مونږ (پخپله هم) ګمراهان وو');
INSERT INTO "abulsalam" VALUES(37,33,'37:33',' پس بېشكه دوى په دغه ورځ په عذاب كې سره شریكان دي');
INSERT INTO "abulsalam" VALUES(37,34,'37:34',' بېشكه مونږ له مجرمانو سره همداسې كوو');
INSERT INTO "abulsalam" VALUES(37,35,'37:35',' بېشكه دوى (داسې) وو چې كله به دوى ته وویل شول چې له الله نه غیر هېڅ لايق د عبادت نشته (،نو) دوى به تكبر كاوه');
INSERT INTO "abulsalam" VALUES(37,36,'37:36',' او دوى وايي چې ایا یقینًا مونږ په رښتیا سره د یو لېوني شاعر له وجې نه خپل معبودان (خدایان) پرېښودونكي یو');
INSERT INTO "abulsalam" VALUES(37,37,'37:37',' (داسې نه ده) بلكې دغه (نبي) حق (دین) راوړى دى، او د رسولانو تصدیق يې كړى دى');
INSERT INTO "abulsalam" VALUES(37,38,'37:38',' بېشكه تاسو خامخا ډېر دردوونكي عذاب لره څكونكي یئ');
INSERT INTO "abulsalam" VALUES(37,39,'37:39',' او تاسو ته بدله نه دركولى كېږي مګر د هغو عملونو چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(37,40,'37:40',' لېكن د الله غوره كړى شوي بنده ګان');
INSERT INTO "abulsalam" VALUES(37,41,'37:41',' دغه كسان چې دي، د دوى لپاره معلومه روزي ده');
INSERT INTO "abulsalam" VALUES(37,42,'37:42',' چې (رنګارنګ) مېوي دي، په داسې حال كې چې دوى به عزتمن كړى شوي وي');
INSERT INTO "abulsalam" VALUES(37,43,'37:43',' د نعمتونو په باغونو كې');
INSERT INTO "abulsalam" VALUES(37,44,'37:44',' (چې ناست به وي) په تختونو یو بل ته مخامخ');
INSERT INTO "abulsalam" VALUES(37,45,'37:45',' په دوى به د روانو صافو شرابو جامونه ګرځولى شي');
INSERT INTO "abulsalam" VALUES(37,46,'37:46',' چې تك سپین به وي، د څښونكو لپاره به خوندور وي');
INSERT INTO "abulsalam" VALUES(37,47,'37:47',' نه به په هغو كې سر خوږى وي او نه به دوى د دغو (شرابو) په وجه نشه كولى شي');
INSERT INTO "abulsalam" VALUES(37,48,'37:48',' او له دوى سره به د (خپل) نظر رامنع كوونكې غټو سترګو والا حورې وي');
INSERT INTO "abulsalam" VALUES(37,49,'37:49',' ګویاكې دوى پټې ساتل شوې هګۍ دي');
INSERT INTO "abulsalam" VALUES(37,50,'37:50',' نو د دغو (جنتیانو) ځینې به ځینو نورو ته مخ راوګرځوي، چې سره تپوس به كوي');
INSERT INTO "abulsalam" VALUES(37,51,'37:51',' په دوى كې يو ویونكى به ووايي: بېشكه زما یو دوست و');
INSERT INTO "abulsalam" VALUES(37,52,'37:52',' ویل به يې: ایا یقینًا ته خامخا له تصدیق كوونكو ځنې يې؟');
INSERT INTO "abulsalam" VALUES(37,53,'37:53',' ایا كله چې مونږ مړه شو او خاورې او هډوكي شو، ایا بېشكه مونږ ته به خامخا جزا راكولى شي');
INSERT INTO "abulsalam" VALUES(37,54,'37:54',' وبه وايي: ایا تاسو كتونكي يئ؟');
INSERT INTO "abulsalam" VALUES(37,55,'37:55',' نو وبه ګوري، نو هغه به (د دوزخ) په مینځ كې وویني');
INSERT INTO "abulsalam" VALUES(37,56,'37:56',' وبه وايي (كتونكى): قسم په الله! بېشكه شان دا و چې ته نژدې وې چې ما هلاك كړې');
INSERT INTO "abulsalam" VALUES(37,57,'37:57',' او كه زما د رب نعمت نه وى (نو) خامخا زه به له حاضر كړى شویو څخه وم');
INSERT INTO "abulsalam" VALUES(37,58,'37:58',' ایا نو مونږ مړه كېدونكي نه یو');
INSERT INTO "abulsalam" VALUES(37,59,'37:59',' زمونږ له ړومبي مرګ نه غیر، او مونږ په عذاب كړى شوي نه یو');
INSERT INTO "abulsalam" VALUES(37,60,'37:60',' بېشكه دا، یقینًا همدغه ډېر لوى برى دى');
INSERT INTO "abulsalam" VALUES(37,61,'37:61',' د دغو (نعمتونو) مثل لپاره دې عمل كوونكي عمل وكړي');
INSERT INTO "abulsalam" VALUES(37,62,'37:62',' ایا دغه د مېلمستیا په لحاظ غوره دي، یا د زقوم ونه');
INSERT INTO "abulsalam" VALUES(37,63,'37:63',' بېشكه مونږ دغه (ونه) د ظالمانو (مشركانو) لپاره فتنه (او عذاب) ګرځولې ده');
INSERT INTO "abulsalam" VALUES(37,64,'37:64',' بېشكه دغه داسې ونه ده چې د دوزخ په بېخ كې راوځي');
INSERT INTO "abulsalam" VALUES(37,65,'37:65',' د دغې وږي، ګویاكې هغه د شیطانانو سرونه دي');
INSERT INTO "abulsalam" VALUES(37,66,'37:66',' پس بېشكه دوى به خامخا له هغې نه خوړونكي وي، نو له هغې نه به د خپلو خېټو ډكوونكي وي');
INSERT INTO "abulsalam" VALUES(37,67,'37:67',' بیا بېشكه دوى ته به د هغې له پاسه د ایشول شویو اوبو ګډون وي');
INSERT INTO "abulsalam" VALUES(37,68,'37:68',' بیا بېشكه د دوى ورتله خامخا لمبې وهونكي اور ته دي');
INSERT INTO "abulsalam" VALUES(37,69,'37:69',' بېشكه دوى خپل پلرونه ګمراه موندلي دي');
INSERT INTO "abulsalam" VALUES(37,70,'37:70',' نو دوى د هغوى په قدمونو پسې په ډېرې تېزۍ سره منډې وهي');
INSERT INTO "abulsalam" VALUES(37,71,'37:71',' او یقینًا یقینًا له دوى نه مخكې د ړومبنو خلقو اكثره ګمراه شوي دي');
INSERT INTO "abulsalam" VALUES(37,72,'37:72',' او یقینًا یقینًا مونږ په دوى كې وېروونكي پېغمبران لېږلي وو');
INSERT INTO "abulsalam" VALUES(37,73,'37:73',' نو ته وګوره چې د وېرول شویو انجام څنګه و؟');
INSERT INTO "abulsalam" VALUES(37,74,'37:74',' لېكن د الله غوره كړي بنډكان (له عذاب نه خلاص دي)');
INSERT INTO "abulsalam" VALUES(37,75,'37:75',' او یقینًا یقینًا مونږ ته نوح اواز وكړ، نو (مونږ) ښه جواب وركوونكي یو');
INSERT INTO "abulsalam" VALUES(37,76,'37:76',' او مونږ هغه ته او د هغه كورنۍ ته له ډېر لوى غم نه نجات وركړ');
INSERT INTO "abulsalam" VALUES(37,77,'37:77',' او مونږ د هغه اولاده، بس هم هغه باقي پاتې كېدونكې وګرځوله');
INSERT INTO "abulsalam" VALUES(37,78,'37:78',' او مونږ په ده باندې په وروستنيو خلقو كې (مدح او ثنا) پرېښودله ');
INSERT INTO "abulsalam" VALUES(37,79,'37:79',' په عالمونو كې دې په نوح سلام وي');
INSERT INTO "abulsalam" VALUES(37,80,'37:80',' بېشكه مونږ نېكانو ته همداسې بدله وركوو');
INSERT INTO "abulsalam" VALUES(37,81,'37:81',' بېشكه هغه زمونږ د مومنو بنده ګانو څخه و');
INSERT INTO "abulsalam" VALUES(37,82,'37:82',' بیا مونږ هغه نور (كافر) خلق غرق كړل');
INSERT INTO "abulsalam" VALUES(37,83,'37:83',' او بېشكه د هغه له ملګرو ځنې خامخا ابراهیم دى');
INSERT INTO "abulsalam" VALUES(37,84,'37:84',' (یاد كړه) كله چې هغه خپل رب ته په ډېر روغ زړه سره راغى');
INSERT INTO "abulsalam" VALUES(37,85,'37:85',' كله چې هغه خپل پلار او خپل قوم ته وویل: څه شى دى چې تاسو يې عبادت كوئ؟');
INSERT INTO "abulsalam" VALUES(37,86,'37:86',' ایا تاسو د دروغو (او باطلو) لپاره له الله پرته معبودان غواړئ؟');
INSERT INTO "abulsalam" VALUES(37,87,'37:87',' نو په رب العالمین مو ستاسو څه ګمان دى');
INSERT INTO "abulsalam" VALUES(37,88,'37:88',' نو په ستوریو كې نظر يې واچوه، یو نظر');
INSERT INTO "abulsalam" VALUES(37,89,'37:89',' نو ويې ويل:بېشكه زه ناجوړه یم');
INSERT INTO "abulsalam" VALUES(37,90,'37:90',' نو دوى له هغه نه وګرځېدل، په داسې حال كې چې شاكوونكي وو');
INSERT INTO "abulsalam" VALUES(37,91,'37:91',' نو د دوى معبودانو (بتانو) ته پټ راغى، نو ويې ويل: ایا تاسو (دا خوراكونه) نه خورئ؟');
INSERT INTO "abulsalam" VALUES(37,92,'37:92',' په تاسو څه شوي دي چې تاسو خبرې نه كوئ؟');
INSERT INTO "abulsalam" VALUES(37,93,'37:93',' نو په مهارت سره يې په دوى باندې راباندې كړل، په داسې حال كې چې په ښي لاس سره وهونكى و');
INSERT INTO "abulsalam" VALUES(37,94,'37:94',' نو هغوى (مشركانو) ده ته متوجه شول، چې ورپسې منډې يې وهلې');
INSERT INTO "abulsalam" VALUES(37,95,'37:95',' ده وویل: ایا تاسو د هغه څه عبادت كوئ چې تاسو يې (پخپله) تراشئ');
INSERT INTO "abulsalam" VALUES(37,96,'37:96','‚ حال دا چې الله تاسو او هغه عملونه چې تاسو يې كوئ، پیدا كړي دي');
INSERT INTO "abulsalam" VALUES(37,97,'37:97',' دوى وویل: د ده لپاره یوه ودانۍ جوړه كړئ، بیا تاسو دا په لمبې وهونكي اور كې واچوئ');
INSERT INTO "abulsalam" VALUES(37,98,'37:98',' نو دوى له ده سره د فریب اراده وكړه، نو مونږ همدوى تر ټولو زیات ذلیله كړل');
INSERT INTO "abulsalam" VALUES(37,99,'37:99',' او ده (ابراهیم) وویل: بېشكه زه خپل رب ته تلونكى یم، ژر به هغه ما ته لاره وښیي');
INSERT INTO "abulsalam" VALUES(37,100,'37:100',' اى زما ربه! ته ما ته (زوى) راكړې، چې له صالحانو ځنې وي');
INSERT INTO "abulsalam" VALUES(37,101,'37:101',' نو مونږ ده ته د ډېر حلم والا هلك زېرى وركړ');
INSERT INTO "abulsalam" VALUES(37,102,'37:102',' نو كله چې هغه (هلك) له ده (خپل پلار) سره منډې وهلو ته ورسېده، ويې ويل:اى زما خوږه زویه! بېشكه زه په خوب كې وینم داسې چې بېشكه زه تا ذبحه كوم، نو ته وګوره چې ستا څه رايه ده؟ هغه وویل: اى زما پلار جانه! ته هغه كار وكړه چې تا ته يې امر كول شي، ژر ده چې كه الله وغواړي، ته به ما له صبر كوونكو ځنې ومومې');
INSERT INTO "abulsalam" VALUES(37,103,'37:103',' نو كله چې دواړو غاړه كېښوده او هغه يې په اړخ څملاوه');
INSERT INTO "abulsalam" VALUES(37,104,'37:104',' او مونږ ده ته اواز وكړ، داسې چې اى ابراهیمه');
INSERT INTO "abulsalam" VALUES(37,105,'37:105',' یقینًا تا خوب رښتیا كړ، بېشكه مونږ نېكي كوونكو ته همداسې بدله وركوو');
INSERT INTO "abulsalam" VALUES(37,106,'37:106',' بېشكه دا، یقینًا همدغه ښكاره ازمېښت و');
INSERT INTO "abulsalam" VALUES(37,107,'37:107',' او مونږ ده (اسماعیل) ته په ډېرې لويې ذبیحې سره خلاصى وركړو');
INSERT INTO "abulsalam" VALUES(37,108,'37:108',' او مونږ باقي پرېښود په ده باندې وروستنیو خلقو كې');
INSERT INTO "abulsalam" VALUES(37,109,'37:109',' دا چې سلام دې وي په ابراهیم');
INSERT INTO "abulsalam" VALUES(37,110,'37:110',' نېكانو ته مونږ همداسې بدله وركوو');
INSERT INTO "abulsalam" VALUES(37,111,'37:111',' بېشكه هغه زمونږ له مومنو بنده ګانو ځنې دى');
INSERT INTO "abulsalam" VALUES(37,112,'37:112',' او مونږ ده ته د اسحاق زېرى وركړ، په داسې حال كې چې نبي و، له صالحانو نه');
INSERT INTO "abulsalam" VALUES(37,113,'37:113',' او بركت اچولى و مونږ په ده (اسمٰعیل) باندې او په اسحاق باندې، او د دې دواړو په اولاد كې څوك نېكې كوونكى دى او څوك په خپل ځان باندې ښكاره ظلم كوونكى دى');
INSERT INTO "abulsalam" VALUES(37,114,'37:114',' او یقینًا یقینًا مونږ په موسٰى او هارون احسان وكړ');
INSERT INTO "abulsalam" VALUES(37,115,'37:115',' او مونږ دغو دواړو ته او د دغو دواړو قوم ته له ډېر لوى غم نه نجات وركړ');
INSERT INTO "abulsalam" VALUES(37,116,'37:116',' او مونږ د دوى مدد وكړ، نو همدوى غالبه شول');
INSERT INTO "abulsalam" VALUES(37,117,'37:117',' او مونږ دغو دواړو ته ډېر واضحه كتاب وركړى و');
INSERT INTO "abulsalam" VALUES(37,118,'37:118',' او مونږ دغو دواړو ته نېغه لاره ښودلې وه');
INSERT INTO "abulsalam" VALUES(37,119,'37:119',' او مونږ په دواړو باندې په وروستنیو خلقو كې باقي پرېښوده');
INSERT INTO "abulsalam" VALUES(37,120,'37:120',' دا چې سلام دي وي په موسي او هارون');
INSERT INTO "abulsalam" VALUES(37,121,'37:121',' بېشكه مونږ نېكي كوونكو ته همداسې بدله وركوو');
INSERT INTO "abulsalam" VALUES(37,122,'37:122',' بېشكه دغه دواړه زمونږ د مومنو بنده ګانو ځنې دي');
INSERT INTO "abulsalam" VALUES(37,123,'37:123',' او بېشكه الیاس خامخا له رسولانو ځنې دى');
INSERT INTO "abulsalam" VALUES(37,124,'37:124',' (یاد كړه)، هغه وخت چې ده خپل قوم ته وویل: ولې تاسو نه وېرېږئ!');
INSERT INTO "abulsalam" VALUES(37,125,'37:125',' ایا تاسو بعل (بت) بولئ او تر ټولو ښه پیدا كوونكى ذات پرېږدئ');
INSERT INTO "abulsalam" VALUES(37,126,'37:126',' چې الله دى، چې ستاسو رب دى او ستاسو د ړومبنو پلرونو رب دى');
INSERT INTO "abulsalam" VALUES(37,127,'37:127',' نو هغوى د ده تكذیب وكړ، نو بېشكه هغوى به خامخا حاضرولى شي');
INSERT INTO "abulsalam" VALUES(37,128,'37:128',' د الله له خالص كړى شویو بنده ګانو نه غیر (چې دوى د هغه تكذیب ونه كړ)');
INSERT INTO "abulsalam" VALUES(37,129,'37:129',' او باقي پرېښود مونږ په ده باندې په وروستنیو خلقو كې');
INSERT INTO "abulsalam" VALUES(37,130,'37:130',' چې په الیاسین دې سلام وي');
INSERT INTO "abulsalam" VALUES(37,131,'37:131',' بېشكه مونږ نېكي كوونكو ته همداسي بدله وركوو');
INSERT INTO "abulsalam" VALUES(37,132,'37:132',' بېشكه هغه زمونږ له مومنو بنده ګانو ځنې دى');
INSERT INTO "abulsalam" VALUES(37,133,'37:133',' او بېشكه لوط له رسولانو ځنې دى');
INSERT INTO "abulsalam" VALUES(37,134,'37:134',' (یاد كړه) هغه وخت چې مونږ ده ته او د ده كورنۍ ته، ټولو ته نجات وركړ');
INSERT INTO "abulsalam" VALUES(37,135,'37:135',' غیر له یوې بوډۍ نه چې په باقي پاتې كېدونكو كې وه');
INSERT INTO "abulsalam" VALUES(37,136,'37:136',' بیا مونږ نور (ټول) خلق هلاك كړل');
INSERT INTO "abulsalam" VALUES(37,137,'37:137',' او بېشكه تاسو په دوى تېرېږئ، په دې حال كې چې سبا كوونكي يئ');
INSERT INTO "abulsalam" VALUES(37,138,'37:138',' او په شپه كې هم، ایا نو تاسو عقل نه كاروئ');
INSERT INTO "abulsalam" VALUES(37,139,'37:139',' او بېشكه یونس خامخا له رسولانو ځنې دى');
INSERT INTO "abulsalam" VALUES(37,140,'37:140',' (یاد كړه) هغه وخت چې هغه ډكې كشتۍ ته وتښتېده');
INSERT INTO "abulsalam" VALUES(37,141,'37:141',' نو ده قرعه واچوله، نو شو (په قرعه كې) له مغلوب كړى شویو ځنې');
INSERT INTO "abulsalam" VALUES(37,142,'37:142',' نو ښوى تېر كړ هغه لره مهي (كب)، په دې حال كې چې هغه (د خپل ځان) ملامت كوونكى و');
INSERT INTO "abulsalam" VALUES(37,143,'37:143',' نو كه دا خبره نه وى چې بېشكه هغه له تسبیح ویونكو څخه و');
INSERT INTO "abulsalam" VALUES(37,144,'37:144',' (نو) خامخا دا به د هغه (كب) په خېټه كې تر هغې ورځې پورې پاتې و چې دوى به بیا ژوندي راپاڅولى شي');
INSERT INTO "abulsalam" VALUES(37,145,'37:145',' نو مونږ هغه په ډاګه میدان كې وغورځاوه، په دې حال كې چې ناروغ و');
INSERT INTO "abulsalam" VALUES(37,146,'37:146',' او مونږ په ده باندې یو ځېلۍ داره ونه را زرغونه كړه');
INSERT INTO "abulsalam" VALUES(37,147,'37:147',' او مونږ دى لېږلى و سل زرو خلقو ته بلكې دوى زیات وو');
INSERT INTO "abulsalam" VALUES(37,148,'37:148',' نو دوى ایمان راوړ، نو مونږ دوى ته تر یو وخته پورې نفع وركړه');
INSERT INTO "abulsalam" VALUES(37,149,'37:149',' نو ته له دوى نه پوښتنه وكړه، ایا ستا د رب لپاره لوڼه دي او د دوى لپاره زامن دي؟');
INSERT INTO "abulsalam" VALUES(37,150,'37:150',' یا كه مونږ ملايك ښځې پیدا كړي دي، په داسې حال كې چې دوى حاضر وو');
INSERT INTO "abulsalam" VALUES(37,151,'37:151',' خبردار! بېشكه دوى له خپلو بدترو دروغو څخه خامخا (داسې) وايي');
INSERT INTO "abulsalam" VALUES(37,152,'37:152',' الله اولاد زېږولى دى. او بېشكه دوى یقینًا دروغجن دي');
INSERT INTO "abulsalam" VALUES(37,153,'37:153',' ایا هغه (الله د ځان لپاره) لوڼه غوره كړي دي په زامنو باندې');
INSERT INTO "abulsalam" VALUES(37,154,'37:154',' په تاسو څه شوي دي، څرنګه تاسو (دا) فیصله (او حكم) كوئ؟');
INSERT INTO "abulsalam" VALUES(37,155,'37:155',' ایا تاسو نصیحت نه اخلئ');
INSERT INTO "abulsalam" VALUES(37,156,'37:156',' ایا تاسو لپاره څه واضحه دلیل شته؟');
INSERT INTO "abulsalam" VALUES(37,157,'37:157',' نو تاسو خپل كتاب راوړئ، كه چېرې تاسو رښتیني یئ');
INSERT INTO "abulsalam" VALUES(37,158,'37:158',' او دوى د هغه (الله) په مینځ كې او د پېریانو په مینځ كې نسب ( د خپلوۍ نسبت) مقرر كړ، او يقینًا یقینًا پېریان په دې پوه شوي دي چې بېشكه دوى به خامخا حاضر كړى شي');
INSERT INTO "abulsalam" VALUES(37,159,'37:159',' الله لپاره پاكي ده له هغو (خبرو) نه چې دوى يې وايي');
INSERT INTO "abulsalam" VALUES(37,160,'37:160',' لېكن د الله خالص (غوره) كړى شوي بنده ګان (له دغو خبرو نه بچ دي)');
INSERT INTO "abulsalam" VALUES(37,161,'37:161',' پس بېشكه تاسو او هغه چې تاسو يې عبادت كوئ');
INSERT INTO "abulsalam" VALUES(37,162,'37:162',' نه یئ تاسو په دې (بت پرستۍ) سره ګمراه كوونكي (په فتنه كې اچوونكي د هیچا)');
INSERT INTO "abulsalam" VALUES(37,163,'37:163',' غیر له هغه چا نه چې هغه په دوزخ كې ننوتونكى دى');
INSERT INTO "abulsalam" VALUES(37,164,'37:164',' او په مونږ كې هیڅوك نشته مګر د هغه لپاره معلوم ځاى دى');
INSERT INTO "abulsalam" VALUES(37,165,'37:165',' او بېشكه خامخا مونږ صف تړونكي یو');
INSERT INTO "abulsalam" VALUES(37,166,'37:166',' او بېشكه مونږ خامخا تسبیح ویونكي یو');
INSERT INTO "abulsalam" VALUES(37,167,'37:167',' او بېشكه چې خامخا دوى به ویل');
INSERT INTO "abulsalam" VALUES(37,168,'37:168',' كه چېرې مونږ سره د پخوانیو خلقو (له كتابونو) نه كوم ذكر (كتاب) وى');
INSERT INTO "abulsalam" VALUES(37,169,'37:169',' (نو) خامخا مونږ به د الله خالص (او غوره) كړى شوي بنده ګان وو');
INSERT INTO "abulsalam" VALUES(37,170,'37:170',' نو دوى په دغه (قرآن) سره كافر شول، نو ژر ده چې دوى به پوه شي');
INSERT INTO "abulsalam" VALUES(37,171,'37:171',' او یقینًا یقینًا زمونږ خبره زمونږ د لېږل شویو (رسولانو) بنده ګانو لپاره صادر شوې ده');
INSERT INTO "abulsalam" VALUES(37,172,'37:172',' چې بېشكه دوى، خامخا هم دوى مدد كړى شوي دي');
INSERT INTO "abulsalam" VALUES(37,173,'37:173',' او بېشكه زمونږ لښكر، خامخا هم دوى غالب راتلونكي دي');
INSERT INTO "abulsalam" VALUES(37,174,'37:174',' نو ته له دوى نه مخ وګرځوه، تر یو وخته پورې');
INSERT INTO "abulsalam" VALUES(37,175,'37:175',' او دوى ته ګوره، نو ژر ده چې دوى به (خپل انجام) وویني');
INSERT INTO "abulsalam" VALUES(37,176,'37:176',' ایا نو دوى زمونږ د عذاب تلوار كوي');
INSERT INTO "abulsalam" VALUES(37,177,'37:177',' نو كله چې هغه د دوى په مېدان كې نازل شي، نو د وېرول شویو سبا به بد وي');
INSERT INTO "abulsalam" VALUES(37,178,'37:178',' او ته له دوى نه مخ وګرځوه تر یو وخته پورې');
INSERT INTO "abulsalam" VALUES(37,179,'37:179',' او ته ګوره، نو ژر ده چې دوى به وویني');
INSERT INTO "abulsalam" VALUES(37,180,'37:180',' ستا رب لره پاكي ده چې د عزت (او غلبې) مالك دى، له هغو خبرو نه چې دوى (مشركان) يې بیانوي');
INSERT INTO "abulsalam" VALUES(37,181,'37:181',' او سلام دې وي په رسولانو باندې');
INSERT INTO "abulsalam" VALUES(37,182,'37:182',' او ټول د كمال صفتونه خاص الله لره دي چې د عالمونو ښه پالونكى دى');
INSERT INTO "abulsalam" VALUES(38,1,'38:1',' صاد، قسم دى په قرآن چې د نصیحت والا دى');
INSERT INTO "abulsalam" VALUES(38,2,'38:2',' بلكې هغه كسان چې كافران شوي دي، په كبر او مخالفت كې دي');
INSERT INTO "abulsalam" VALUES(38,3,'38:3',' مونږه له دوى نه مخكې څومره ډېرې پېړۍ هلاكې كړې دي، نو هغوى به چغې وهلې، حال دا چې دغه وخت به د خلاصي نه و');
INSERT INTO "abulsalam" VALUES(38,4,'38:4',' او دوى په دې وجه تعجب كوي چې دوى ته له همدوى ځنې یو وېروونكى راغى او كافرانو وویل: دا (پېغمبر) جادوګر دى، سخت دروغجن دى');
INSERT INTO "abulsalam" VALUES(38,5,'38:5',' ایا ده دغه معبودان یو اله وګرځول، بېشكه دا خامخا ډېر عجیبه څیز دى');
INSERT INTO "abulsalam" VALUES(38,6,'38:6',' او له دوى څخه (د كفارو) مشران لاړل، چې (ویل يې:) ځئ او په خپلو معبودانو ټینګ اوسئ، بېشكه دغه (توحید) خامخا یو داسې شى دى چې غوښتنه يې كولى شي (زمونږ نه)');
INSERT INTO "abulsalam" VALUES(38,7,'38:7',' مونږ دغه (توحید) په وروستي دین (نصرانیت) كې نه دى اورېدلى. نه دى دا مګر له خپله ځانه نه جوړ كړى شوي دروغ');
INSERT INTO "abulsalam" VALUES(38,8,'38:8',' ایا زمونږ له مینځه په ده باندې ذكر (وحي، قرآن) نازل كړى شو! بلكې دوى زما د ذكر (قرآن) په هكله په شك كې دي بلكې لا تر اوسه دوى زما عذاب نه دى څكلى');
INSERT INTO "abulsalam" VALUES(38,9,'38:9',' ایا له دوى سره ستا د رب د رحمت خزانې دي (هغه رب) چې ډېر غالب، عطا كوونكى دى؟');
INSERT INTO "abulsalam" VALUES(38,10,'38:10',' یا د دوى لپاره بادشاهي ده د اسمانونو او ځمكې او د هغه څه چې د دواړو په مینځ كې دي، نو دوى دې په اسبابو كې وخېژي');
INSERT INTO "abulsalam" VALUES(38,11,'38:11',' (دغه) داسې لښكر ګوټى دى له لښكرو څخه، چې په دغه ځاى كې شكست وركړى شوى دى');
INSERT INTO "abulsalam" VALUES(38,12,'38:12',' تكذیب كړى و له دوى نه مخكې د نوح قوم او عادیانو او فرعون چې د مېخونو والا و');
INSERT INTO "abulsalam" VALUES(38,13,'38:13',' او ثمودیانو او د لوط قوم او د ځنګل والاوو، همدغه ډلې دي');
INSERT INTO "abulsalam" VALUES(38,14,'38:14',' نه ده هره یوه (ډله) مګر چې د رسولانو تكذیب يې كړى دى، نو زما عذاب پرې ثابت شو');
INSERT INTO "abulsalam" VALUES(38,15,'38:15',' او دوى انتظار نه كوي مګر د یوې داسې چغې چې د هغې لپاره بېرته ګرځېدل نشته (وقفه نشته)');
INSERT INTO "abulsalam" VALUES(38,16,'38:16',' او دوى ویل: اى زمونږ ربه! ته مونږ ته زمونږ برخه ژر راكړه، د حساب له ورځې نه مخكې');
INSERT INTO "abulsalam" VALUES(38,17,'38:17',' ته د دوى په دغو خبرو صبر كوه او ته زمونږ بنده داود یاد كړه چې د قوت والا و، بېشكه هغه ډېر رجوع كوونكى و');
INSERT INTO "abulsalam" VALUES(38,18,'38:18',' بېشكه مونږ له هغه سره غرونه تابع كړي وو چې د ورځې په اخره او اوله برخه كې به يې تسبیح ویله');
INSERT INTO "abulsalam" VALUES(38,19,'38:19',' او مرغان مو (تابع كړي وو) په دې حال كې چې جمع كړى شوي به وو، هر یو به ده ته ښه رجوع كوونكى و');
INSERT INTO "abulsalam" VALUES(38,20,'38:20',' اومونږ د هغه باچاهي مضبوطه كړې وه او مونږ هغه ته حكمت او د غوڅې خبرې قوت وركړى و');
INSERT INTO "abulsalam" VALUES(38,21,'38:21',' او ایا تا ته د جګړه كوونكو خبر راغلى دى، كله چې هغوى په (دېوال د) عبادت خانې راوختل');
INSERT INTO "abulsalam" VALUES(38,22,'38:22',' كله چې دوى په داود ورننوتل، نو هغه له دوى نه ووېرېده، دوى وویل: ته مه وېرېږه! (مونږ یو) دوه جګړه كوونكې ډلې، زمونږ ځینو په ځینو نورو ظلم كړى دى، نو ته زمونږ په مینځ كې په حقه سره فیصله وكړه او زیاتى مه كوه او ته نېغې لارې ته زمونږ لار ښووونه وكړه');
INSERT INTO "abulsalam" VALUES(38,23,'38:23',' بېشكه دا زما ورور دى، د ده نهه نوي (یو كم سل) ګډې (مېږې) دي او زما یوه ګډه (مېږه) ده، نو ده وویل: ته ما د دې كفیل (مالك) وګرځوه او په خبرو كې يې په ما غلبه كړې ده');
INSERT INTO "abulsalam" VALUES(38,24,'38:24',' ده (داود) وویل: یقینًا یقینًا ده په تا ظلم كړى دى، ستا د ګډې په غوښتلو (چې یو ځاى يې كړي) له خپلو ګډو سره او بېشكه په شریكانو كې ډېر زیات (داسې وي چې) خامخا ځینې د دوى په ځینو نورو ظلم كوي، غیر له هغو كسانو نه چې ایمان يې راوړى دى او نېك عملونه يې كړي دي او دوى بېخي لږ دي. او داود یقین وكړ چې بېشكه همدا خبره ده چې مونږ هغه وازمایه، نو له خپل رب نه يې بخښنه وغوښته او پرېوته، په داسې حال كې چې ركوع (او) سجده كوونكى و، او رجوع يې وكړه');
INSERT INTO "abulsalam" VALUES(38,25,'38:25',' نو مونږ ده ته دغه (خطا) وبخښله، او بېشكه د هغه لپاره زمونږ په نیز خامخا قربت دى او د ورتلو ښه ځاى دى');
INSERT INTO "abulsalam" VALUES(38,26,'38:26',' اى داوده! بېشكه مونږ ته په ځمكه كې خلیفه ګرځولى يې، نو ته د خلقو په مینځ كې په حقه سره فیصلې كوه او د خواهشاتو پيروي مه كوه، (ګنې) نو تا به د الله له لارې نه ګمراه كړي، بېشكه هغه كسان چې د الله له لارې نه ګمراه كېږي، د هغوى لپاره ډېر سخت عذاب دى، په سبب د دوى د هېرولو ورځې د حساب لره');
INSERT INTO "abulsalam" VALUES(38,27,'38:27',' او مونږ اسمان او ځمكه او هغه څه چې د دواړو په مینځ كې دي داسې نه دي پیدا كړي، چې باطل (او عبث) دي دغه (باطل پیدا كول) د كافران شویو خلقو ګمان دى، نو دا كافران شویو خلقو لپاره له اوره هلاكت (او عذاب) دى');
INSERT INTO "abulsalam" VALUES(38,28,'38:28',' ایا مونږ به هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، په ځمكه كې د فساد كوونكو په شان وګرځوو؟ ایا مونږ به متقیان د فاجرانو (بدكارانو) په شان وګرځوو؟');
INSERT INTO "abulsalam" VALUES(38,29,'38:29',' (دغه قرآن) كتاب دى چې مونږه تا ته نازل كړى دى، بركتناك دى، د دې لپاره چې دوى د ده په ایتونو كې فكر وكړي او د دې لپاره چې د خالصو عقلونو والا ترې پند واخلي');
INSERT INTO "abulsalam" VALUES(38,30,'38:30',' او مونږ داود ته سليمان عطا كړ، (هغه) ښه بنده و، بېشكه چې هغه ډېر رجوع كوونكى و');
INSERT INTO "abulsalam" VALUES(38,31,'38:31',' كله چې ده ته د ورځې په اخر كې هغه ګړندي خاص اسونه وړاندې كړى شول چې په درې پښو ودرېږي');
INSERT INTO "abulsalam" VALUES(38,32,'38:32',' نو ده ویل: بېشكه ما د دغه مال محبت محبوب ګڼلى دى د خپل رب د یاد په وجه، تر دې چې (دغه اسونه) په پرده كې پټ شول');
INSERT INTO "abulsalam" VALUES(38,33,'38:33',' تاسو دغه ما ته بېرته راولئ، نو شروع شو چې په پنډيو او څټونو يې لاس راښكه');
INSERT INTO "abulsalam" VALUES(38,34,'38:34',' او یقینًا یقینًا مونږ سلیمان وازمایه، او د ده په كرسۍ مو یو جسد وغورځاوه، بیا ده رجوع وكړه');
INSERT INTO "abulsalam" VALUES(38,35,'38:35',' ده وویل: اى زما ربه! ته ما وبخښه او ما ته داسې باچاهي راكړه چې له ما نه پس د چا لپاره هم مناسب نه وي، بېشكه هم ته ښه عطا كوونكى يې');
INSERT INTO "abulsalam" VALUES(38,36,'38:36',' نو مونږ د ده لپاره هوا مسخره كړه چې د ده په امر سره به چلېدله نرمه نرمه، چېرته چې به ده اراده وكړه');
INSERT INTO "abulsalam" VALUES(38,37,'38:37',' (مونږ تابع كړي وو د ده لپاره) پېریان هر ښه معمار او ښه غوټه وهونكى');
INSERT INTO "abulsalam" VALUES(38,38,'38:38',' او نور (پېریان) چې په ځنځيرونو كې له یو بل سره تړل شوي وو');
INSERT INTO "abulsalam" VALUES(38,39,'38:39',' دا زمونږ عطا ده، نو ته احسان كوه، يا يې وساته، بې له حسابه');
INSERT INTO "abulsalam" VALUES(38,40,'38:40',' او بېشكه د هغه لپاره زمونږ په نیز خامخا قرب دى، او (په اخرت كې) د ورتلو ښه ځاى دى');
INSERT INTO "abulsalam" VALUES(38,41,'38:41',' او ته زمونږ بنده ایوب یاد كړه، كله چې هغه خپل رب ته اواز وكړ چې بېشكه ما ته شیطان ایذا او تكلیف رارسولى دى');
INSERT INTO "abulsalam" VALUES(38,42,'38:42',' ته (ځمكه) په خپلې پښې سره ووهه، دغه یخ غسلځاى دى، او د څښلو اوبه دي');
INSERT INTO "abulsalam" VALUES(38,43,'38:43',' او مونږ ده ته د ده اهل وركړ او له دوى سره د دوى په مثل (نور هم) له خپل جانب نه د رحمت له وجې او عقلمندو ته د نصیحت لپاره');
INSERT INTO "abulsalam" VALUES(38,44,'38:44',' او ته په خپل لاس سره جارو ونیسه، نو په هغې سره وهل وكړه او مه حانث كېږه، بېشكه مونږ هغه صابر ومونده، (هغه) ښه سړى و، بېشكه هغه ډېر رجوع كوونكى و');
INSERT INTO "abulsalam" VALUES(38,45,'38:45',' او ته زمونږ بنده ګان ابراهیم، او اسحٰق او یعقوب یاد كړه چې د (عمل د) قوت او (حق ليدونكو) سترګو خاوندان وو');
INSERT INTO "abulsalam" VALUES(38,46,'38:46',' بېشكه مونږ دوى په یو خالص خوى سره خالص كړي دي چې هغه د اخرت یادول دي');
INSERT INTO "abulsalam" VALUES(38,47,'38:47',' او بېشكه دوى زمونږ، په نیز خامخا له غوره كړى شویو، بهترو خلقو ځنې دي');
INSERT INTO "abulsalam" VALUES(38,48,'38:48',' او ته اسماعیل او الیسع او ذوالكفل یاد كړه، دغه هر یو د خیر او بهترۍ والا وو');
INSERT INTO "abulsalam" VALUES(38,49,'38:49',' دا پند دى، او بېشكه د متقیانو لپاره خامخا د ورتلو ښه ځاى دى');
INSERT INTO "abulsalam" VALUES(38,50,'38:50',' چې د همېشه استوګنې جنتونه دي، په داسې حال كې چې د دوى لپاره يې دروازې ښې پرانستې دي');
INSERT INTO "abulsalam" VALUES(38,51,'38:51',' په دغو كې به تكیه وهلي وي، په دغو (جنتونو) كې به دوى ډېرې مېوې او مشروب راغواړي');
INSERT INTO "abulsalam" VALUES(38,52,'38:52',' او له دوى سره به د نظر رامنع كوونكې همځولې ښځې وي');
INSERT INTO "abulsalam" VALUES(38,53,'38:53',' دا (ثواب او نعمتونه) هغه څه دي چې تاسو ته يې د حساب د ورځې لپاره وعده دركولى شي');
INSERT INTO "abulsalam" VALUES(38,54,'38:54',' بېشكه دا خامخا زمونږ داسې رزق دى، چې ده لره هېڅ خلاصېدل نشته');
INSERT INTO "abulsalam" VALUES(38,55,'38:55',' دغه (د مومنانو حال) دى او بېشكه د سركشانو لپاره خامخا د ورتلو ډېر بد ځاى دى');
INSERT INTO "abulsalam" VALUES(38,56,'38:56',' چې جهنم دى، دوى به په هغه كې داخلېږي، نو هغه بده بستره ده');
INSERT INTO "abulsalam" VALUES(38,57,'38:57',' دغه اېشېدلې اوبه او زوې دي، نو دوى دې دا وڅَكي');
INSERT INTO "abulsalam" VALUES(38,58,'38:58',' او بل (عذاب) دى له دغه قسم نه، څو قسمه');
INSERT INTO "abulsalam" VALUES(38,59,'38:59',' دغه یوه ډله ده چې له تاسو سره په ډېر تكلیف سره ننوتونكي دي، دوى ته دې هېڅ خوشالي نه وي، بېشكه دوى اور ته داخلېدونكي دي');
INSERT INTO "abulsalam" VALUES(38,60,'38:60',' هغوى به ووايي: بلكې تاسو چې یئ، تاسو ته دې هېڅ خوشالي نه وي، تاسو دغه (عذاب) مونږ ته راوړاندې كړى دى، نو دا (دوزخ) بد قرار ځاى دى');
INSERT INTO "abulsalam" VALUES(38,61,'38:61',' دوى به ووايي: اى زمونږ ربه! چا چې مونږ ته دا راوړاندې كړى دى، نو ته هغه ته په اور كې عذاب دوه چنده ورزیات كړه');
INSERT INTO "abulsalam" VALUES(38,62,'38:62',' او دوى به ووايي: پر مونږ څه شوي دي چې مونږ هغه سړي نه وینو چې مونږ به هغوى له اشرارو (بدترو خلقو) ځنې شمېرل؟');
INSERT INTO "abulsalam" VALUES(38,63,'38:63',' ایا مونږ هغوى په مسخرو سره نیولې وو، یا كه له هغوى نه زمونږ سترګې كږې شوې دي؟');
INSERT INTO "abulsalam" VALUES(38,64,'38:64',' بېشكه دغه خامخا حق دي، یعنې (دا) د دوزخیانو خپل مینځې جګړه كول');
INSERT INTO "abulsalam" VALUES(38,65,'38:65',' ته (دوى ته) ووایه: زه خو بس وېروونكى یم، او نشته دى حق معبود غیر له الله نه چې یو او ډېر غلبې والا دى');
INSERT INTO "abulsalam" VALUES(38,66,'38:66',' چې رب دى د اسمانونو او ځمكې او د هغه څه چې د دواړو په مینځ كې دي، چې ډېر غالب، ښه بخښونكى دى');
INSERT INTO "abulsalam" VALUES(38,67,'38:67',' ته (دوى ته) ووایه: دغه (قرآن) ډېر لوى خبر دى');
INSERT INTO "abulsalam" VALUES(38,68,'38:68',' تاسو له ده نه مخ ګرځوونكي یئ');
INSERT INTO "abulsalam" VALUES(38,69,'38:69',' ما ته د ملَاِ اعلٰى (لوړ مجلس) په باره كې هېڅ علم نشته، كله چې دوى په خپل مینځ كې اختلاف كوي');
INSERT INTO "abulsalam" VALUES(38,70,'38:70',' ما ته وحي نه كېږي مګر د دې چې بېشكه همدا خبره ده چې زه ښكاره وېروونكى یم');
INSERT INTO "abulsalam" VALUES(38,71,'38:71',' كله چې ستا رب ملايكو ته وویل چې بېشكه زه له خَټې نه د یو بشر پیدا كوونكى یم');
INSERT INTO "abulsalam" VALUES(38,72,'38:72',' نو كله چې زه هغه برابر كړم او په هغه كې له خپل (پيدا كړي) روح نه پو كړم، نو تاسو هغه ته پرېوځئ، چې سجده كوونكي یئ');
INSERT INTO "abulsalam" VALUES(38,73,'38:73',' نو ملايكو ټولو سجده وكړه');
INSERT INTO "abulsalam" VALUES(38,74,'38:74',' غیر له ابلیس نه، هغه لويي وكړه او شو له كافرانو څخه');
INSERT INTO "abulsalam" VALUES(38,75,'38:75',' (الله) وویل: اى ابليسه! ته هغه كس ته له سجدې كولو نه څه شي منع كړې، چې ما پخپلو دواړو لاسونو پیدا كړى دى ؟ ایا تا لويي وكړه، یا ته (له شروع نه) د لوړو اوچتو خلقو ځنې يې؟');
INSERT INTO "abulsalam" VALUES(38,76,'38:76',' هغه وویل: زه له ده نه ډېر غوره یم، تا زه له اوره پیدا كړى یم او هغه دې له خټې نه پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(38,77,'38:77',' (الله) وویل: نو ته له دې (جنت) نه ووځه، پس بېشكه ته رټل شوى يې');
INSERT INTO "abulsalam" VALUES(38,78,'38:78',' او بېشكه په تا باندې د بدلې تر ورځې پورې زما لعنت دى');
INSERT INTO "abulsalam" VALUES(38,79,'38:79',' هغه وویل: اى زما ربه! نو ته ما ته تر هغې ورځې پورې مهلت راكړه چې دوى (خلق) به بیا راژوندي كولى شي');
INSERT INTO "abulsalam" VALUES(38,80,'38:80',' الله وویل: پس بېشكه ته له مهلت وركړى شویو څخه يې');
INSERT INTO "abulsalam" VALUES(38,81,'38:81',' د معلوم وخت تر ورځې پورې');
INSERT INTO "abulsalam" VALUES(38,82,'38:82',' ده وویل: نو قسم ستا پر عزت! چې خامخا ضرور به زه دوى ټول ګمراه كوم');
INSERT INTO "abulsalam" VALUES(38,83,'38:83',' مګر په دوى كې ستا هغه بنده ګان چې خالص كړى شوي دي');
INSERT INTO "abulsalam" VALUES(38,84,'38:84',' الله وویل: پس حقه خبره دا ده، او زه حقه خبره كوم');
INSERT INTO "abulsalam" VALUES(38,85,'38:85',' چې خامخا ضرور به زه جهنم ډكوم له تا نه او له هغه چا نه چې په دوى كې ستا پیروي وكړي، له ټولو نه');
INSERT INTO "abulsalam" VALUES(38,86,'38:86',' ته (دوى ته) ووایه: زه له تاسو نه په دې (تبلیغ) باندې هېڅ اجر نه غواړم او زه له تكلف كوونكو څخه نه یم');
INSERT INTO "abulsalam" VALUES(38,87,'38:87',' نه دى دا (قرآن) مګر د عالمونو لپاره نصیحت');
INSERT INTO "abulsalam" VALUES(38,88,'38:88',' او خامخا ضرور به تاسو څه وخت وروسته د ده په خبر عالمان شئ');
INSERT INTO "abulsalam" VALUES(39,1,'39:1',' د دې كتاب نازلول د الله له جانبه دي، چې ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(39,2,'39:2',' بېشكه مونږ تا ته كتاب په حقه سره نازل كړى دى، نو ته د الله عبادت كوه، په داسې حال كې چې دین هغه (الله) لره خالص كوونكى يې');
INSERT INTO "abulsalam" VALUES(39,3,'39:3',' خبردار شئ! خاص د الله لپاره خالص دین دى، او هغه كسان چې له هغه نه غیر يې دوستان (كارسازي) نیولي دي (وايي:) مونږ د دوى عبادت نه كوو مګر د دې لپاره چې دوى مونږ الله ته نژدې كړي، ښه نژدې كول، بېشكه الله به د دوى په مینځ كې په هغو خبرو كې فیصله كوي چې دوى په هغو كې اختلاف كوي، بېشكه الله هدایت نه كوي هغه چا ته چې هغه دروغجن، ډېر ناشكره وي');
INSERT INTO "abulsalam" VALUES(39,4,'39:4',' كه الله غوښتلى چې (د ځان لپاره) اولاد ونیسي، (نو) خامخا ده به له خپله مخلوقه هغه غوره كړي وى چې دا يې غواړي، هغه لره پاكي ده. هم دى الله دى چې یو (او) ډېر غالب دى');
INSERT INTO "abulsalam" VALUES(39,5,'39:5',' هغه اسمانونه او ځمكه په حقه سره پیدا كړي دي، هغه شپه په ورځ تاووي (نغاړي) او ورځ په شپه تاووي او هغه لمر او سپوږمۍ مسخر كړي دي، هر یو مقرر كړى شوې نېټې ته روان دى، خبردار شئ! همغه ښه غالب، ښه بخښونكى دى');
INSERT INTO "abulsalam" VALUES(39,6,'39:6',' هغه تاسو له يو نفس نه پیدا كړي يئ، بیا يې له هغه نه د هغه ښځه پیدا كړه او تاسو لپاره يې له څارویو نه اته جوړې پیدا كړې، هغه تاسو د خپلو میندو په خیټو كې پیدا كوي، یو قسم پیدا كول، وروسته له بل قسم پیدا كولو نه، په دریو تیارو كې، دغه الله ستاسو رب دى، بادشاهي خاص د هغه لپاره ده، نشته دى هېڅ حق معبود مګر هغه دى، نو كوم طرف ته تاسو ګرځولى شئ');
INSERT INTO "abulsalam" VALUES(39,7,'39:7',' كه تاسو كفر اختیار كړئ، نو بېشكه الله له تاسو نه ښه بې پروا دى، او هغه د خپلو بنده ګانو لپاره كفر نه خوښوي، او كه تاسو شكر وباسئ (نو) دى دغه (شكر) تاسو ته خوښوي، او هېڅ بار وړونكى د بل بار نه وړي، بیا خاص خپل رب ته ستاسو بېرته ورتله دي، نو هغه به تاسو په هغو عملونو خبر كړي چې تاسو به كول، بېشكه هغه د سینو په خبرو ښه عالم دى');
INSERT INTO "abulsalam" VALUES(39,8,'39:8',' او كله چې انسان ته سختي ورسېږي (، نو) خپل رب بلي، په داسې حال كې چې هغه ته رجوع كوونكى وي، بیا چې كله هغه (الله) ده ته له خپل جانب نه نعمت وركړي (، نو) هېر كړي هغه ذات چې ده به له دې (نعمت) نه مخكې دغه (تكلیف لرې كولو) ته باله، او الله لپاره شریكان مقرر كړي، د دې لپاره چې د هغه (الله) له لارې نه (خلق) ګمراه كړي، ته ووایه: ته په خپل كفر سره لږه نفعه واخله، بېشكه ته له دوزخیانو څخه يې');
INSERT INTO "abulsalam" VALUES(39,9,'39:9',' ایا هغه څوك چې هغه د شپې په ساعتونو كې اطاعت كوونكى دى، چې سجده كوونكى او قیام كوونكى وي، له اخرت نه وېرېږي او د خپل رب د رحمت امېد لري، ته ووایه: ایا سره برابرېدلى شي هغه كسان چې پوهېږي او هغه كسان چې نه پوهېږي، بېشكه همدغه د خالصو عقلونو والا نصیحت اخلي');
INSERT INTO "abulsalam" VALUES(39,10,'39:10',' ته (دوى ته د الله له جانبه) ووایه: اى زما هغو بنده ګانو چې ایمان يې راوړى دى: تاسو له خپل رب نه ووېرېږئ، د هغو كسانو لپاره چې په دنیا كې يې نېكي كړې ده ؛ لویه نېكي (او بدله) ده او د الله ځمكه فراخه ده، بېشكه يواځې صبر كوونكو ته د هغوى اجر بې حسابه پوره وركاوه شي');
INSERT INTO "abulsalam" VALUES(39,11,'39:11',' ته (دوى ته) ووایه: بېشكه ما ته حكم راكړ شوى دى چې زه د الله عبادت كوم، په داسې حال كې چې هغه (الله) لره دین خالص كوونكى يم');
INSERT INTO "abulsalam" VALUES(39,12,'39:12',' او ما ته حكم راكړى شوى دى چې زه تر ټولو اول مسلمان یم');
INSERT INTO "abulsalam" VALUES(39,13,'39:13',' ته (دوى ته) ووایه: بېشكه زه كه د خپل رب نافرماني وكړم. د ډېرې لويې ورځې له عذاب نه وېرېږم');
INSERT INTO "abulsalam" VALUES(39,14,'39:14',' ته (دوى ته) ووایه: زه خاص د الله عبادت كوم، په داسې حال كې چې هغه لره خپل دین خالص كوونكى یم');
INSERT INTO "abulsalam" VALUES(39,15,'39:15',' نو تاسو له هغه نه غیر د څه شي چې غواړئ، عبادت وكړئ، ته ووایه: بېشكه نقصان كوونكي هغه كسان دي چې د قیامت په ورځ يې خپلو ځانونو او خپل اهل ته تاوان رسولى دى. خبردار شئ، دغه (تاوان) همدغه ښكاره تاوان دى');
INSERT INTO "abulsalam" VALUES(39,16,'39:16',' د دوى لپاره به د دوى له پاسه د اور چوپالونه وي او د دوى له لاندې نه به هم چوپالونه وي. دغه (عذاب) دى چې الله په ده سره خپل بنده ګان وېروي، اى زما بنده ګانو! نو تاسو زما نه وویرېږئ');
INSERT INTO "abulsalam" VALUES(39,17,'39:17',' او هغه كسان چې له طاغوت نه يې ځان وساته، یعنې د هغو له عبادت كولو نه او الله ته يې رجوع وكړه، خاص د دوى لپاره زېرى دى، نو ته زما په بنده ګانو زېرى وكړه');
INSERT INTO "abulsalam" VALUES(39,18,'39:18',' هغه (بنده ګان) چې خبره په غور سره اوري، نو د هغې د تر ټولو ښې پیروي كوي، دا هغه كسان دي چې الله دوى ته هدایت كړى دى او همدغه كسان د خالص عقل والا دي');
INSERT INTO "abulsalam" VALUES(39,19,'39:19',' ایا نو هغه څوك چې په هغه باندې د عذاب خبره ثابته شوې ده، ایا نوته هغه څوك خلاصولى شي چې په اور كې وي');
INSERT INTO "abulsalam" VALUES(39,20,'39:20',' لېكن هغه كسان چې له خپل رب نه وېرېږي خاص د دوى لپاره داسې بالاخانې دي چې د هغو له پاسه (نورې) ښې جوړې كړى شوي بالاخانې دي، چې د هغو له لاندې ولې بهېږي، (دا) د الله كړې وعده ده، الله د وعدې مخالفت نه كوي');
INSERT INTO "abulsalam" VALUES(39,21,'39:21',' ایا تا نه دي كتلي چې بېشكه الله له بره نه اوبه نازلې كړې دي، بیا يې دغه (اوبه) په ځمكه كې په چینو كې ننه ایستي دي، بیا هغه په دغو (اوبو) سره فصل راوباسي، په دې حال كې چې د هغه رنګونه مختلف وي، بیا هغه وچ شي، نو ته هغه (شین رنګى فصل) تك زېړ وینې، بیا دغه (فصل) لره هغه رژېدلى (مات شوى) وګرځوي، بېشكه په دغه كې د خالصو عقلونو والاو لپاره خامخا نصیحت دى');
INSERT INTO "abulsalam" VALUES(39,22,'39:22',' ایا نو هغه څوك چې الله د هغه سینه د اسلام لپاره پرانستلي وي، نو دغه كس په رڼا كې وي د خپل رب له جانبه، پس هلاكت دى هغو كسانو ته چې د هغوى زړونه د الله له ذكره سخت دي، دغه كسان په ښكاره ګمراهۍ كې دي');
INSERT INTO "abulsalam" VALUES(39,23,'39:23',' الله تر ټولو ښكلى كلام نازل كړى دى، چې داسې كتاب دى چې په خپل مينځ كې مشابه دى، مكرر (او غبرګ) دى، چې له ده نه د هغو كسانو پوستكي ځیګ كېږي چې له خپل رب نه وېرېږي، بیا د دوى پوستكي او د دوى زړونه د الله ذكر ته نرم شي، دغه د الله هدایت دى، چې چا ته وغواړي په ده سره هدايت وركوي او هغه څوك چې الله يې ګمراه كړي، نو د هغه لپاره هیڅوك لار ښودونكى نشته');
INSERT INTO "abulsalam" VALUES(39,24,'39:24',' ایا نو هغه څوك چې په خپل مخ سره ځان بچ كوي له سخت عذاب نه په ورځ د قیامت كې او ظالمانو ته به وویل شي: تاسو وڅكئ (سزا د) هغو عملونو چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(39,25,'39:25',' هغو كسانو تكذیب كړى و چې له دوى نه مخكې وو، نو په دوى عذاب له هغه ځایه راغى چې دوى نه پوهېدل');
INSERT INTO "abulsalam" VALUES(39,26,'39:26',' نو الله په دوى په دنيايي ژوند كې رسوايي وڅكوله او خامخا د اخرت عذاب تر ټولو لوى دى، كه چېرې دوى پوهېدلى');
INSERT INTO "abulsalam" VALUES(39,27,'39:27',' او یقینًا یقینًا مونږ د خلقو لپاره په دې قرآن كې هر قسمه مثال بیان كړى دى، د دې لپاره چې دوى نصیحت واخلي');
INSERT INTO "abulsalam" VALUES(39,28,'39:28',' په داسې حال كې چې دغه عربي (واضح) قرآن دى، د كوږوالي والا نه دى، د دې لپاره چې دوى پرهېزګاره شي');
INSERT INTO "abulsalam" VALUES(39,29,'39:29',' الله یو مثال بیانوي، یو (غلام) سړى دى، په ده كې بدخویه په خپل مینځ كې ناسازه كسان شریكان دي او بل (غلام) سړى دى چې سالم (او پوره) د یو سړي دى، ایا دغه دواړه په حال كې سره برابر دي؟ ټول د كمال صفتونه خاص د الله لپاره دي بلكې د دوى اكثره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(39,30,'39:30',' بېشكه ته هم مړ كېدونكى يې او بېشكه دوى (هم) مړ كېدونكي دي');
INSERT INTO "abulsalam" VALUES(39,31,'39:31',' بیا بېشكه تاسو به د قیامت په ورځ د خپل رب په وړاندې، یو تربله جګړه كوئ');
INSERT INTO "abulsalam" VALUES(39,32,'39:32',' نو له هغه چا نه لوى ظالم څوك دى چې په الله دروغ تړي او رښتیا (قرآن) دروغ ګڼي، كله چې دغه (رښتیا) ده ته راشي؟ ایا په دوزخ كې د كافرانو لپاره استوګنځى نشته؟ ');
INSERT INTO "abulsalam" VALUES(39,33,'39:33',' او هغه كس چې صدق (رښتینى دین) يې راوړ او (هغه چې) د دغه (صدق) تصدیق يې وكړ (نو) هم دغه كسان پرهېزګاران دي');
INSERT INTO "abulsalam" VALUES(39,34,'39:34',' د دوى لپاره د خپل رب په نېز هغه څه دي چې دوى يې غواړي، دغه د نېكي كوونكو بدله ده');
INSERT INTO "abulsalam" VALUES(39,35,'39:35',' د دې لپاره چې الله له دوى نه هغه بدتر ګناهونه لرې (ختم) كړي چې دوى كړي دي او دوى ته د دوى اجر بدله وركړي، په سبب د هغو تر ټولو ښكلو عملونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(39,36,'39:36',' ایا الله خپل بنده ته ښه كافي كېدونكى نه دى او دوى تا په هغو (معبودانو) وېروي چې له دغه (الله) نه غیر دي او هغه څوك چې الله يې ګمراه كړي، نو د هغه لپاره هیڅوك نېغه لار ښودونكى نشته');
INSERT INTO "abulsalam" VALUES(39,37,'39:37',' او هغه څوك چې الله ورته هدایت وكړي، نو د هغه لپاره هیڅوك ګمراه كوونكى نشته. ایا الله ښه غالب او انتقام والا نه دى ؟');
INSERT INTO "abulsalam" VALUES(39,38,'39:38',' او یقینًا كه ته له دوى نه تپوس وكړې چې اسمانونه او ځمكه چا پیدا كړي دي (،نو) دوى به خامخا لازمًا ووايي چې ’’الله‘‘، ته (دوى ته) ووایه: تاسو ما ته خبر راكړئ، هغه (معبودان) چې تاسو يې له الله نه غیر بلئ. كه چېرې الله ما ته د ضرر رسولو اراده وكړي، ایا دغه (ستاسو معبودان) د هغه د ضرر لرې كوونكي دي؟ یا كه هغه (الله) ما سره د رحمت اراده وكړي، ایا دغه (معبودان) د هغه د رحمت منع كوونكي دي؟ ته (دوى ته) ووایه: زما لپاره بس الله كافي دى، خاص په ده باندې توكل كوونكي توكل كوي');
INSERT INTO "abulsalam" VALUES(39,39,'39:39',' ته (دوى ته) ووایه: اى زما قومه: تاسو په خپله طریقه (او ځاى) عمل كوئ، بېشكه زه هم عمل كوونكى یم، نو ژر به تاسو وپېژنئ');
INSERT INTO "abulsalam" VALUES(39,40,'39:40',' هغه څوك چې ده ته به داسې عذاب راشي چې دا به وشرموي او په ده به دايمي عذاب نازل شي');
INSERT INTO "abulsalam" VALUES(39,41,'39:41',' بېشكه مونږ په تا باندې كتاب نازل كړى دى د خلقو لپاره په حقه سره، نوهغه څوك چې په سمه لار روان شي، نو د خپل ځان لپاره يې نفع ده او هغه څوك چې ګمراه شي، نو بېشكه همدا خبره ده چې دى د همدغه (ځان) په ضرر ګمراه كېږي او ته په دوى باندې له سره نګران نه يې');
INSERT INTO "abulsalam" VALUES(39,42,'39:42',' الله نفسونه (ساګانې) قبضوي د هغو د مرګ په وخت كې او هغه (نفس) چې مړ نه وي د (هغه ساه) د خوب په وخت كې(قبضوي)، بیا هغه (ساه) ساتي چې په هغې يې د مرګ فیصله كړې وي او هغه بله تر مقررې نېټې پورې راخوشې كړي، بېشكه په دغو (قبض، ساتلو او پرېښودلو) كې خامخا لويې نښې دي د هغه قوم لپاره چې ښه فكر كوي');
INSERT INTO "abulsalam" VALUES(39,43,'39:43',' بلكې ایا دوى له الله نه غیر شفاعت كوونكي نیولي دي، ته (دوى ته) ووایه: ایا (دوى به شفاعت كوي!) اګر كه دوى په داسې حال كې وي چې نه د څه شي مالكان وي او نه عقل لري');
INSERT INTO "abulsalam" VALUES(39,44,'39:44',' ته (دوى ته) ووایه: شفاعت ټول خاص د الله په اختیار كې دى،خاص د هغه لپاره د اسمانونو او ځمكې بادشاهي ده، بیا به تاسو خاص هغه ته بېرته بېول كېږئ');
INSERT INTO "abulsalam" VALUES(39,45,'39:45',' او كله چې الله يواځې یاد كړى شي (، نو) د هغو كسانو زړونه سخت تنګ شي چې په اخرت ایمان نه لري او كله چې هغه (معبودان، بتان) یاد كړى شي چې له هغه(الله) نه غیر دي(، نو) ناڅاپه دوى ډېر خوشحالېږي');
INSERT INTO "abulsalam" VALUES(39,46,'39:46',' ته ووایه: اى الله د اسمانونو اوځمكې پیدا كوونكیه، د غیبو او د ښكاره وو عالِـمه، ته به د خپلو بنده ګانو په مینځ كې فیصله كوې، په هغو خبرو كې چې دوى به په هغو كې اختلاف كاوه');
INSERT INTO "abulsalam" VALUES(39,47,'39:47',' او كه په رښتیا سره د ظلم (شرك) كوونكو كسانو لپاره هغه څه چې په ځمكه كې دي، ټول مملوك شي او د دغه په مثل نور ورسره شي، نو دوى به خامخا د قیامت په ورځ كې له بدتر عذاب نه د خلاصي لپاره دغه فدیه وركړي. او دوى ته به د الله له جانبه هغه څه ښكاره شي چې دوى به يې ګمان هم نه كاوه');
INSERT INTO "abulsalam" VALUES(39,48,'39:48',' او دوى ته به د خپلو كړو عملونو بدۍ ښكاره شي او له دوى نه به هغه (عذاب) چاپېره شي چې دوى به په هغه پورې استهزا كولې');
INSERT INTO "abulsalam" VALUES(39,49,'39:49',' نو كله چې انسان ته تكلیف ورسي (، نو) مونږ بلي، بيا چې كله ده ته مونږ له خپله جانبه نعمت وركړو (، نو) دى وايي: دا خو ما ته يواځې (زما) د علم په سبب راكړ شوى دى، بلكې دغه (وركړه) ازمېښت دى، او لېكن د دوى اكثره نه پوهېږي');
INSERT INTO "abulsalam" VALUES(39,50,'39:50',' یقینًا دغه خبره هغو كسانو (هم) كړې وه چې له دوى نه مخكې وو، نو د دغو كسانو هېڅ په كار رانغلل هغه (مالونه) چې دوى به ګټل');
INSERT INTO "abulsalam" VALUES(39,51,'39:51',' نو دغو (پخوانیو خلقو) ته د خپلو كړو عملونو بدۍ ورسېدلې او هغه كسان چې ظلم يې كړى دى له دغو خلقو نه (چې قریش دي) ژر به دوى ته د دوى د كړو عملونو بدۍ ورسېږي او دوى (مونږ لره) عاجزه كوونكي نه دي');
INSERT INTO "abulsalam" VALUES(39,52,'39:52',' او ایا دوى نه پوهېږي چې بېشكه الله رزق فراخوي چا لپاره چې وغواړي او تنګوي يې، بېشكه په دغو (فراخولو اوتنګولو) كې خامخا ډېرې نښې دي د هغه قوم لپاره چې ایمان راوړي');
INSERT INTO "abulsalam" VALUES(39,53,'39:53',' ته (دوى) ته ووایه: اى زما هغو بنده ګانو چې په خپلو ځانونو يې زیاتى كړى دى! تاسو د الله له رحمت نه مه ناامېده كېږئ، بېشكه الله ټول ګناهونه بخښي، بېشكه دغه (الله)، هم دى ښه بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(39,54,'39:54',' او تاسو خپل رب ته رجوع وكړئ او هغه ته غاړه كېږدئ، مخكې له دې نه چې په تاسو عذاب راشي، بیا به تاسو سره مدد نه كولى شي ');
INSERT INTO "abulsalam" VALUES(39,55,'39:55',' او تاسو د تر ټولو ښه حكم (قرآن) پیروي وكړئ هغه چې تاسو ته د خپل رب له جانبه نازل كړى شوى دى مخكې له دې نه چې په تاسو عذاب راشي ناڅاپه، حال دا چې تاسو به نه پوهېږئ');
INSERT INTO "abulsalam" VALUES(39,56,'39:56',' (تاسو اوس خپل رب ته رجوع وكړئ) ځكه چې نفس به وايي: اى زما حسرته! په هغه چې ما د الله په حق كې تقصیر(او كمى) كړى دى او یقینًا زه خامخا د استهزا كوونكو ځنې وم');
INSERT INTO "abulsalam" VALUES(39,57,'39:57',' یا به وايي: كه حقیقتًا الله ما ته هدایت كړى وى(، نو) زه به خامخا د پرهېزګارانو ځنې وى');
INSERT INTO "abulsalam" VALUES(39,58,'39:58',' یا به وايي كله چې عذاب ویني: كاشكې په رښتیا سره ما ته بېرته تلل وى، نو زه به له نېكي كوونكو ځنې وم');
INSERT INTO "abulsalam" VALUES(39,59,'39:59',' ولې نه! یقینًا تا ته زما ایتونه راغلي وو، نو تا د هغو تكذیب كړى و او تكبر دې كړى و او ته وې له كافرانو نه ');
INSERT INTO "abulsalam" VALUES(39,60,'39:60',' او د قیامت په ورځ به ته هغه كسان ووینې چې په الله يې دروغ تړلي وو، په داسې حال كې چې د دوى مخونه به تك تور كړى شوي وي، ایا په دوزخ كې د تكبر كوونكو لپاره ځاى نشته!');
INSERT INTO "abulsalam" VALUES(39,61,'39:61',' او الله به هغو كسانو ته چې ځان يې (له شرك نه) بچ ساتلى دى، د خپلې كامیابۍ (او فلاح) په وجه نجات وركړي، په داسې حال كې چې دوى ته به نه سختي رسېږي او نه به دوى غمجن كېږي');
INSERT INTO "abulsalam" VALUES(39,62,'39:62',' الله د هر شي پیدا كوونكى دى او هغه په هر شي باندې نګران دى');
INSERT INTO "abulsalam" VALUES(39,63,'39:63',' خاص هم ده لره د اسمانونو او ځمكې چابیانې دي، او هغه كسان چې د الله په ایتونو سره كافران شوي دي، هم دغه كسان تاوانیان دي');
INSERT INTO "abulsalam" VALUES(39,64,'39:64',' ته (دوى ته) ووایه: ایا نو تاسو ما ته حكم كوئ چې زه د غیر الله عبادت وكړم؟ اى جاهلانو!');
INSERT INTO "abulsalam" VALUES(39,65,'39:65',' او یقینًا یقینًا تا ته وحي كړى شوې ده او هغو كسانو ته چې له تا نه مخكې وو (داسې چې) قسم دى كه تا شرك وكړ (نو) ستا عمل به خامخا لازمًا برباد شي او ته به خامخا لازمًا له تاوانیانو ځنې شې');
INSERT INTO "abulsalam" VALUES(39,66,'39:66',' بلكې ته خاص د الله عبادت كوه او اوسه له شكر كوونكو ځنې');
INSERT INTO "abulsalam" VALUES(39,67,'39:67',' او دغو (مشركانو) د الله تعظیم ونه كړ د هغه حق تعظیم، حال دا چې ځمكه ټوله د ده په قبضه كې ده د قیامت په ورځ كې او اسمانونه د ده په ښي لاس كې رانغاړل (راټول كړى) شوي دي، ده لره پاكي ده او هغه ډېر اوچت دى له هغو څیزونو نه چې دوى يې (له هغه سره) شریكوي');
INSERT INTO "abulsalam" VALUES(39,68,'39:68',' او په شپېلۍ كې به پو كړى شي، نو بې هوشه (بې خوده) به شي هر هغه څوك چې په اسمانونو كې دي او هر هغه څوك چې په ځمكه كې دي، غیر له هغه چا نه چې الله يې وغواړي، بیا به په دې كې بل ځلې پو كړى شي، نو ناڅاپه به دوى ولاړ وي، ګوري به (حیران حیران)');
INSERT INTO "abulsalam" VALUES(39,69,'39:69',' او ځمكه به د خپل رب په نور سره روښانه شي او كتاب (عمل نامه) به كېښودل شي او نبیان او شاهدان به راوستل شي او د دوى په مینځ كې به په حق (او انصاف) سره فیصله وكړى شي، په داسې حال كې چې په دوى به هېڅ ظلم ونه كړى شي');
INSERT INTO "abulsalam" VALUES(39,70,'39:70',' او هر نفس ته به د ده كړى عمل پوره وركړى شي او هغه (الله) ښه عالم دى په هغو عملونو چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(39,71,'39:71',' او كافران شوي كسان به دوزخ ته ډلې ډلې روان كړى شي، تر هغه پورې چې كله دوى دغه (دوزخ) ته راشي، (نو) د ده دروازې به پرانستل شي او دوى ته به د دغه (جهنم) نګرانان ووايي: ایا تاسو ته رسولان نه وو راغلي، چې له هم ستاسو نه وو، چې تاسو ته به يې ستاسو د رب ایتونه لوستل او تاسو به يې د خپلې دغې ورځې له ملاقات نه وېرولئ؟ دوى به ووايي: ولې نه! (راغلي وو) او لېكن د عذاب خبره په كافرانو باندې ثابته شوې وه');
INSERT INTO "abulsalam" VALUES(39,72,'39:72',' وبه ویل شي (دوى ته): تاسو د دوزخ په دروازو كې ننوځئ، په داسې حال كې چې په دغه (دوزخ) كې به تل يئ، نو د تكبر كوونكو استوګنه بده ده');
INSERT INTO "abulsalam" VALUES(39,73,'39:73',' او هغه كسان به چې له خپل رب نه وېرېدل، جنت ته ډلې ډلې روان كړى شي، تر هغه پورې چې كله دوى ده ته راشي، په دې حال كې چې د ده دروازې به (له مخكې نه) پرانستل شوي وي او دوى ته به د دغه (جنت) نګرانان ووايي: په تاسو دې سلام وي، تاسو پاك يئ، نو دغو ته داخل شئ، په داسې حال كې چې تل اوسېدونكي به یئ');
INSERT INTO "abulsalam" VALUES(39,74,'39:74',' او دوى به وايي: ټول د كمال صفتونه هغه الله لره خاص دي چې زمونږ سره يې خپله وعده رښتینې كړه او مونږ ته يې دغه ځمكه میراث راكړه، چې په دغه جنت كې مونږ ځاى نیسو، هر ځاى چې مونږ وغواړو، نو د (نېك) عمل كوونكو اجر ښه دى');
INSERT INTO "abulsalam" VALUES(39,75,'39:75',' او (اى نبي) ته به ملايك ووينې، چې د عرش ګېر چاپېره راتاوېدونكي وي، چې د خپل رب له حمد سره تسبیح وايي او د دغو (خلقو) په مینځ كې به په حقه سره فیصله وكړى شي او وبه ویل شي: ټول د كمال صفتونه خاص د الله لپاره دي چې د عالمونو ښه پالونكى دى');
INSERT INTO "abulsalam" VALUES(40,1,'40:1',' حا، ميم');
INSERT INTO "abulsalam" VALUES(40,2,'40:2',' د كتاب نازلول د الله له جانبه دي چې ډېر غالب، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(40,3,'40:3',' د ګناهونو بخښونكى او د توبې قبلوونكى دى، ډېر سخت عذاب والا د لوى فضل والا دى، نشته دى هېڅ حق معبود مګر هم دى دى، خاص هم ده ته بېرته ورتله دي');
INSERT INTO "abulsalam" VALUES(40,4,'40:4',' د الله په ایتونو كې جګړې نه كوي مګر هغه كسان چې كافران شوي دي، نو په ښارونو كې د دوى تګ راتګ دې تا تېر نه باسي');
INSERT INTO "abulsalam" VALUES(40,5,'40:5',' له دغو (قریشو) نه مخكې د نوح قوم تكذیب كړى و او د دوى (قوم نوح) نه وروسته نورو ډلو (هم) او هر یو امت د خپل رسول په باره كې اراده وكړه چې هغه راګېر كړي او دوى په باطله سره جګړې وكړي، د دې لپاره چې په دغه (باطل) سره حق ختم(او زايله) كړي، نو ما دوى ونیول، بیا نو زما سزا وركول څنګه وو؟');
INSERT INTO "abulsalam" VALUES(40,6,'40:6',' او همداسې ستا د رب خبره په هغو كسانو لازمه شوې ده چې كافران شوي دي، چې بېشكه دوى د اور ملګري دي');
INSERT INTO "abulsalam" VALUES(40,7,'40:7',' (له ملايكو څخه) هغه كسان چې عرش اوچتوي او هغه (ملايك) چې د دغه (عرش) ګېر چاپېره دي، دوى د خپل رب د ستاينې سره تسبیح وايي او په ده ایمان لري او د هغو كسانو لپاره مغفرت غواړي چې ایمان يې راوړى دى (او وايي:) اى زمونږه ربه! ته د رحمت په لحاظ سره هر شي ته رسېدلى يې، نو ته هغو كسانو ته بخښنه وكړې چې توبه يې ايستلې ده او ستا د لارې پیروي يې كړې ده او ته دوى د دوزخ له عذاب نه وساته');
INSERT INTO "abulsalam" VALUES(40,8,'40:8',' اى زمونږه ربه! او ته دوى د تل استوګنې هغو جنتونو ته ننباسه چې تا يې له دوى سره وعده كړې ده، او هغه څوك چې صلاحيت ولري د دوى له پلرونو او د دوى له ښځو او د دوى له اولادونو څخه (هغه هم ننباسه)، بېشكه ته، هم ته ښه غالب، ډېر حكمت والا يې');
INSERT INTO "abulsalam" VALUES(40,9,'40:9',' او ته دوى له سختیو نه بچ وساته او هغه څوك چې ته يې په دغې ورځ كې له سختیو نه بچ وساتې، نو یقینًا تا په هغه رحم وكړ او هم دغه ډېره لویه كامیابي ده');
INSERT INTO "abulsalam" VALUES(40,10,'40:10',' بېشكه هغه كسان چې كافران شوي دي، دوى ته به (په دوزخ كې) اواز وكړى شي: يقينًا (تاسو ته) د الله غصه كېدل ډېر لوى وو ستاسو خپلو ځانونو ته له غصه كېدلو نه، كله چې به تاسو ایمان ته بللى شوئ، نو تاسو به كفر كاوه');
INSERT INTO "abulsalam" VALUES(40,11,'40:11',' (نو) دوى به ووايي: اى زمونږه ربه! تا مونږ دوه ځلې مړه كړو او دوه ځل دې مونږ ژوندي كړو، نو مونږ (اوس) د خپلو ګناهونو اقرار وكړ، نو ايا (له دوزخ نه زمونږ) وتلو ته كومه لار شته؟');
INSERT INTO "abulsalam" VALUES(40,12,'40:12',' دغه (ابدي عذاب) په دې سبب دى چې بېشكه شان دا دى چې كله به الله يواځې وبلل شو(نو) تاسو به كفر كاوه او كه له ده سره به شریك ونیول شو (نو) تاسو به ایمان راوړ، نو حكم خاص هغه الله لره دى چې ډېر اوچت، ډېر لوى دى');
INSERT INTO "abulsalam" VALUES(40,13,'40:13',' دى همغه ذات دى چې تاسو ته خپلې نښې ښیي او تاسو لپاره له اسمان نه رزق نازلوي او پند نه اخلي مګر هغه څوك چې(حق ته) رجوع كوي');
INSERT INTO "abulsalam" VALUES(40,14,'40:14',' نو تاسو الله بلئ، په داسې حال كې چې همده ته د دین خالص كوونكي یئ اګر كه كافران يې بد ګڼي');
INSERT INTO "abulsalam" VALUES(40,15,'40:15',' (دغه الله) د اوچتو درجو والا (د درجو اوچتونكى) دى، د عرش مالك دى، په خپلو بنده ګانو كې په چا چې وغواړي، په خپل حكم سره وحي نازلوي، د دې لپاره چې دغه(بنده خلقو لره) د ملاقات له ورځې نه ووېروي');
INSERT INTO "abulsalam" VALUES(40,16,'40:16',' له هغې ورځې نه چې دوى به بېخي ښكاره وي، له الله نه به د دوى هېڅ شى پټ نه وي، نن ورځ د چا بادشاهي ده؟ خاص د الله (بادشاهي ده) چې یو دى، ښه غالب دى');
INSERT INTO "abulsalam" VALUES(40,17,'40:17',' نن ورځ به هر نفس ته د هغه د كړي عمل بدله وركړى شي، نن ورځ ظلم بېخي نشته، بېشكه الله ډېر ژر حساب كوونكى دى');
INSERT INTO "abulsalam" VALUES(40,18,'40:18',' او ته دوى له نژدې ورځې نه ووېروه كله چې زړونه به له چنغړكو سره وي، په داسې حال كې چې له غم نه به ډك وي، د ظالمانو لپاره به نه هیڅوك پوخ دوست وي او نه كوم داسې شفاعت كوونكى چې خبره يې ومنله شي');
INSERT INTO "abulsalam" VALUES(40,19,'40:19',' هغه د سترګو په خیانت پوهېږي او په هغو خبرو چې سینې يې پټوي (او رازونه وي)');
INSERT INTO "abulsalam" VALUES(40,20,'40:20',' او الله په حقه سره فيصله كوي او هغه (معبودان) چې دوى يې له هغه (الله) نه غیر بلي، دغه د هېڅ شي فیصله نشي كولى، بېشكه الله، هم دى ښه اورېدونكى، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(40,21,'40:21',' ایا دوى په ځمكه كې نه دي ګرځېدلي، پس چې وګوري چې د هغو كسانو انجام څنګه و، چې له دوى نه مخكې وو، هغوى له دوى نه په قوت كې او په ځمكه كې د نښو(او یادګارونو) په لحاظ ډېر سخت (او مضبوط) وو، نو الله دوى د خپلو ګناهونو په سبب ونیول او د دوى لپاره له الله نه هیڅوك بچ كوونكى نه و');
INSERT INTO "abulsalam" VALUES(40,22,'40:22',' دغه په دې سبب چې یقینًا دوى داسې وو چې دوى ته خپلو رسولانو ښكاره دلیلونه راوړل، نو (بیا هم) دوى كافران شول، نو الله دوى ونیول، بېشكه هغه، ډېر زورور، ډېر سخت عذاب والا دى');
INSERT INTO "abulsalam" VALUES(40,23,'40:23',' او یقینًا یقینًا مونږ موسٰى له خپلو معجزو او ښكاره دلیل سره لېږلى و ');
INSERT INTO "abulsalam" VALUES(40,24,'40:24',' فرعون او هامان او قارون ته، نو دوى وویل: (دا خو) جادوګر دى، سخت دروغجن دى');
INSERT INTO "abulsalam" VALUES(40,25,'40:25',' نو كله چې هغه دوى ته زمونږ له جانبه حق راوړ، دوى وویل: تاسو د هغو كسانو زامن ووژنئ چې له ده سره يې ایمان راوړى دى او د دوى ښځې (لوڼه) ژوندۍ پرېږدئ او د كافرانو چل مكر نه دى مګر په تباهۍ كې');
INSERT INTO "abulsalam" VALUES(40,26,'40:26',' او فرعون (خپلو درباریانو ته) وویل: تاسو ما پرېږدئ چې زه(دا) موسٰى ووژنم او دا دې خپل رب ته چغې ووهي، بېشكه زه له دې وېرېږم چې دا به ستاسو دین بدل كړي، یا به په ځمكه كې فساد خور كړي');
INSERT INTO "abulsalam" VALUES(40,27,'40:27',' او موسٰى وویل چې بېشكه ما د خپل رب او ستاسو د رب پناه نیولې ده له هر تكبر كوونكي نه، چې د حساب په ورځې ایمان نه لري');
INSERT INTO "abulsalam" VALUES(40,28,'40:28',' او په اٰل فرعون كې یو مومن سړي چې خپل ایمان يې پټ ساتلو، وویل: ایا تاسو یو سړى په دې وجه وژنئ چې هغه وايي: زما رب الله دى، حال دا چې یقینًا هغه تاسو ته د خپل رب له جانبه ښكاره دلیلونه هم راوړي دي او كه دى دروغجن وي، نو په همده باندې د خپلو دروغو ضرر دى او كه دى رښتینى وي(نو بیا خو به) تاسو ته د هغه(عذاب) ځینې برخه درورسېږي چې دا يې تاسو ته وعده دركوي، بېشكه الله هدایت نه كوي هغه چا ته چې هغه له حده تېرېدونكى، ډېر دروغجن وي');
INSERT INTO "abulsalam" VALUES(40,29,'40:29',' اى زما قومه! نن ورځ ستاسو بادشاهي ده، په داسې حال كې چې په ځمكه كې غالب يئ، نو مونږ به د الله له عذاب نه څوك بچ كوي كه هغه پر مونږ راشي؟ فرعون وویل: زه تاسو ته نه ښیم مګر هغه خبره چې زه يې وینم او زه تاسو ته ښودنه نه كوم مګر د سمې درستې لارې');
INSERT INTO "abulsalam" VALUES(40,30,'40:30',' او هغه كس وویل چې ایمان يې راوړى و: اى زما قومه! بېشكه زه په تاسو د (تېرو) ډلو د ورځې له مثل نه وېرېږم');
INSERT INTO "abulsalam" VALUES(40,31,'40:31',' د نوح د قوم او عادیانو او ثمودیانو او د هغو كسانو د حال له مثل نه چې له دوى نه وروسته وو او الله په بنده ګانو د هېڅ ظلم اراده نه كوي');
INSERT INTO "abulsalam" VALUES(40,32,'40:32',' او اى زما قومه! زه په تاسو یو بل ته د چيغو وهلو له ورځې نه وېرېږم');
INSERT INTO "abulsalam" VALUES(40,33,'40:33',' هغه ورځ چې تاسو به وګرځئ شا اړوونكي، په داسې حال كې چې تاسو لپاره به له الله نه هیڅوك بچ كوونكى نه وي. او هغه څوك چې الله يې ګمراه كړي، نو د هغه لپاره هیڅوك هدایت وركوونكى نشته');
INSERT INTO "abulsalam" VALUES(40,34,'40:34',' او یقینًا یقینًا تاسو ته له ده نه مخكې یوسف په واضحو دلیلونو سره راغلى و، نو تاسو د هغه څه په باره كې چې تاسو ته هغه راوړى و؛ تل په شك كې وئ، تر هغه پورې چې كله هغه مړ شو، تاسو وویل: له ده نه وروسته به الله له سره هېڅ رسول راونه لېږي، همداسې الله ګمراه كوي هغه چا لره چې هغه له حده تېرېدونكى، شك كوونكى دى');
INSERT INTO "abulsalam" VALUES(40,35,'40:35',' هغه كسان چې د الله په ایتونو كې جګړې كوي بې له كوم داسې دلیله چې دوى ته راغلى وي، (دغه) د الله په نېز او د هغو كسانو په نېز چې ایمان يې راوړى د غضب په لحاظ غټه خبره ده، همداسې الله د تكبر كوونكي او جبر كوونكي په هر(جز د) زړه باندې مهر لګوي');
INSERT INTO "abulsalam" VALUES(40,36,'40:36',' او فرعون وویل: اى هامانه! ته زما لپاره یوه ډېره اوچته ماڼۍ جوړه كړه، ښايي چې زه لارو ته ورسېږم');
INSERT INTO "abulsalam" VALUES(40,37,'40:37',' داسمانونو لارو ته، بیا د موسٰى الله له بره وګورم او بېشكه زه یقینًا دا دروغجن ګڼم او همداسې فرعون ته خپل بد عمل ښايسته كړى شوى و، او له (حقې) لارې نه منع كړى شوى و او د فرعون چل (او مكر) نه و مګر په تباهۍ كې');
INSERT INTO "abulsalam" VALUES(40,38,'40:38',' او هغه كس چې ایمان يې راوړى و، وویل: اى زما قومه ! تاسو زما پیروي وكړئ چې زه تاسو ته د هدایت لاره وښیم');
INSERT INTO "abulsalam" VALUES(40,39,'40:39',' اى زما قومه ! بېشكه همدا خبره ده چې دغه دنيايي ژوند خو لږه فايده ده او یقینًا اخرت چې دى، هم هغه د قرار كور دى');
INSERT INTO "abulsalam" VALUES(40,40,'40:40',' هر څوك چې بد كار وكړي، نو ده ته به بدله نه وركولى شي مګر په مثل د دغه (بد كار) او هر څوك چې نېك عمل وكړي، سړى وي، يا ښځه،په داسې حال كې چې مومن وي، نو دغه كسان به جنت ته داخلېږي، دوى ته به په دغه (جنت) كې بې حسابه رزق وركول كېږي');
INSERT INTO "abulsalam" VALUES(40,41,'40:41',' او اى زما قومه! په ما څه چل دى چې زه تاسو نجات (او خلاصي) ته راغواړم او تاسو ما اور ته رابلئ');
INSERT INTO "abulsalam" VALUES(40,42,'40:42',' تاسو ما رابلئ د دې لپاره چې زه په الله سره كافر شم او له هغه سره هغه شى شریك كړم چې ما ته د دغه (شي په اله كېدو) هېڅ علم نشته، حال دا چې زه تاسو ډېر غالب، ډېر مغفرت كوونكي (الله) ته رابولم');
INSERT INTO "abulsalam" VALUES(40,43,'40:43',' حقه خبره دا ده چې یقینًا هغه(معبود) چې تاسو ما(د) هغه (عبادت) ته بلئ، د هغه لپاره خو نه په دنیا كې څه بلل شته او نه په اخرت كې او دا چې بېشكه زمونږ بېرته ورتلل خاص الله ته دي او دا چې يقينًا له حده تېرېدونكي، همدغه د اور ملګري دي ');
INSERT INTO "abulsalam" VALUES(40,44,'40:44',' پس ژر ده چې تاسو به دغه خبرې يادوئ چې زه يې تاسو ته (اوس) وایم. او زه خپل كار الله ته سپارم، بېشكه الله (خپلو) بنده ګانو لره ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(40,45,'40:45',' نو الله هغه د دوى د مكرونو له بديو نه بچ وساته او (پخپله) اٰل فرعون ډېر سخت عذاب ګېركړل');
INSERT INTO "abulsalam" VALUES(40,46,'40:46',' (د دوزخ) اور(چې دى) دوى هغه ته هر سبا او هر بېګاه وړاندې كاوه شي او په هغې ورځ كې چې قیامت قايم شي (نو ملايكو ته به وویل شي:) تاسو ال فرعون لره تر ټولو سخت عذاب ته ننباسئ');
INSERT INTO "abulsalam" VALUES(40,47,'40:47',' او (یاد كړه) هغه وخت چې دوى به په خپل مینځ كې جګړې كوي، نو كمزوري به هغو كسانو ته وايي چې تكبر يې كاوه : بېشكه مونږ خو ستاسو پیروي كوونكي وو، نو ایا تاسو زمونږ نه له اور ځنې څه برخه لرې كوونكي يئ؟');
INSERT INTO "abulsalam" VALUES(40,48,'40:48',' هغه كسان به (په جواب كې) ووايي چې تكبر يې كړى و : بېشكه مونږ ټول په دغه (دوزخ) كې یو، بېشكه الله د بنده ګانو په مينځ كې فیصله كړې ده');
INSERT INTO "abulsalam" VALUES(40,49,'40:49',' او هغه كسان به چې (د دوزخ) په اور كې دي، د جهنم نګرانانو ته ووايي: تاسو له خپل رب نه دعا وغواړئ چې زمونږ په عذاب كې بس یوه ورځ تخفیف وكړي');
INSERT INTO "abulsalam" VALUES(40,50,'40:50',' دوى به (په جواب كې) ووايي: ایا تاسو ته به ستاسو رسولان په واضحو دلیلونو سره نه راتلل؟ دوى (دوزخیان) به ووايي: ولې نه (نو) دوى (د دوزخ نګرانان) به ووايي، پس تاسو (پخپله) دعا وكړئ او د كافرانو دعا نه ده مګر په ضیاع كې');
INSERT INTO "abulsalam" VALUES(40,51,'40:51',' بېشكه مونږ خامخا د خپلو رسولانو او د هغو كسانو چې ایمان يې راوړى دى، په دنيايي ژوند كې مدد كوو او په هغې ورځ كې (هم) چې ګواهان به ودرېږي');
INSERT INTO "abulsalam" VALUES(40,52,'40:52',' په هغې ورځې كې چې ظالمانو (مشركانو) ته به د هغوى معذرت هېڅ نفع نه رسوي او د همدوى لپاره لعنت دى او د همدوى لپاره بد كور (دوزخ) دى');
INSERT INTO "abulsalam" VALUES(40,53,'40:53',' او یقینًا یقینًا مونږ موسٰى ته هدایت (تورات) وركړى و او بني اسرائیلو ته مو كتاب میراث وركړ');
INSERT INTO "abulsalam" VALUES(40,54,'40:54',' په داسې حال كې چې د خالصو عقلونو والاو ته هدایت او پند و');
INSERT INTO "abulsalam" VALUES(40,55,'40:55',' نو (اى نبي) ته صبر وكړه! بېشكه د الله وعده حقه ده او د خپلې ګناه لپاره بخښنه وغواړه او د ورځې په اخره او اوله برخه كې د خپل رب له ستاينې سره تسبیح وایه');
INSERT INTO "abulsalam" VALUES(40,56,'40:56',' بېشكه هغه كسان چې د الله په ایتونو كې جګړې كوي، بې له داسې دلیله چې دوى ته راغلى وي، د دوى په سینو كې نه دى مګر كبر چې دوى دغه (د كبر مقتضا) ته رسېدونكي نه دي، نو ته په الله پورې پناه ونیسه، بېشكه هغه، هم هغه ښه اورېدونكى، ښه لیدونكى دى ');
INSERT INTO "abulsalam" VALUES(40,57,'40:57',' یقینًا د اسمانونو او ځمكې پیدا كول د خلقو له پیدا كولو نه ډېر لوى دي او لېكن د خلقو اكثریت نه پوهېږي');
INSERT INTO "abulsalam" VALUES(40,58,'40:58',' او ړوند او بینا سره نه برابرېږي او نه هم هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي او بدكاره (سره نه برابرېږي) تاسو ډېر لږ نصیحت اخلئ');
INSERT INTO "abulsalam" VALUES(40,59,'40:59',' بېشكه قیامت خامخا راتلونكى دى، په ده كې هېڅ شك نشته. او لېكن د خلقو اكثریت ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(40,60,'40:60',' او ستاسو رب فرمايي: تاسو ما وبلئ چې زه ستاسو دعا قبوله كړم. بېشكه هغه كسان چې زما له عبادت نه تكبر كوي، ژر ده چې دوى به دوزخ ته ننوځي، په داسې حال كې چې خوار او ذلیله به وي');
INSERT INTO "abulsalam" VALUES(40,61,'40:61',' الله هغه ذات دى چې تاسو لپاره يې شپه پیدا كړې ده، د دې لپاره چې تاسو په دې كې ارام حاصل كړئ او ورځ يې روښانه (ګرځولې ده)، بېشكه الله په خلقو باندې خامخا ډېر فضل كولو والا دى او لېكن د خلقو اكثریت شكر نه وباسي');
INSERT INTO "abulsalam" VALUES(40,62,'40:62',' دغه الله ستاسو رب دى چې د هر شي پیدا كوونكى دى، نشته هېڅ لايق د عبادت مګر همدى دى، نو كوم طرف ته تاسو ګرځولى شئ ');
INSERT INTO "abulsalam" VALUES(40,63,'40:63',' همداسې به (له حق نه) ګرځولى كېدل هغه كسان چې د الله له ایتونو نه به يې انكار كاوه');
INSERT INTO "abulsalam" VALUES(40,64,'40:64',' الله هغه ذات دى چې تاسو لپاره يې ځمكه قرار ځاى او اسمان چت ګرځولى دى او ستاسو صورتونه يې جوړ كړي دي، پس ستاسو صورتونه يې ښكلي جوړ كړي دي او تاسو ته يې له پاكیزه څيزونو نه رزق عطا كړى دى، دغه الله ستاسو رب دى، نو الله رب العالمین ډېر بركتي دى');
INSERT INTO "abulsalam" VALUES(40,65,'40:65',' هم دى (تل) ژوندى دى، نشته هېڅ حق معبود مګر هم دى دى، نو تاسو دا بلئ، په داسې حال كې چې هم ده لره د دین خالص كوونكي یئ، ټول د كمال صفتونه خاص د الله رب العالمین لپاره دي');
INSERT INTO "abulsalam" VALUES(40,66,'40:66',' ته (دوى ته) ووایه: بېشكه زه له دې نه منع كړى شوى یم چې د هغو (معبودانو) عبادت وكړم چې تاسو يې له الله نه غیر بلئ، كله چې ما ته زما د رب له جانبه ښكاره دلیلونه راغلل او ما ته حكم شوى دى چې زه رب العالمین ته تسلیم شم');
INSERT INTO "abulsalam" VALUES(40,67,'40:67',' دى همغه ذات دى چې تاسو يې له خاورې پیدا كړئ، بیا يې له نطفې نه، بیا يې د وینې له ټوټې نه: بیا هغه تاسو راوباسي، په داسې حال كې چې واړه (ماشومان) يئ، بیا (هغه ستاسو تربیت كوي)، د دې لپاره چې تاسو خپل قوت (او ځوانۍ) ته ورسېږئ، بیا (مو ژوندي ساتي) د دې لپاره چې زاړه (بوډاګان) شئ، او ځینې په تاسو كې هغه دي چې له دغه (وخت) نه مخكې وفات كولى شي. او (تاسو ساتي) د دې لپاره چې تاسو مقررې نېټې ته ورسېږئ او د دې لپاره چې تاسو له عقل نه كار واخلئ');
INSERT INTO "abulsalam" VALUES(40,68,'40:68',' دى همغه ذات دى چې ژوندي كول كوي او مړه كول كوي، نو كله چې د یو كار (د ایجاد) فیصله وكړي، نو بېشكه همدا خبره ده چې ده ته وايي: موجود شه! نو هغه موجود شي');
INSERT INTO "abulsalam" VALUES(40,69,'40:69',' ایا تا هغه كسان نه دي لیدلي چې د الله په ایتونو كې جګړې كوي، كوم طرف ته دوى ګرځولى شي؟');
INSERT INTO "abulsalam" VALUES(40,70,'40:70',' هغه كسان چې تكذیب كوي د كتابونو او د هغه څه چې په هغو سره مونږ خپل رسولان لېږلي وو، پس ژر به دوى پوه شي');
INSERT INTO "abulsalam" VALUES(40,71,'40:71',' كله چې به د دوى په غاړو كې طوقونه او ځنځیرونه وي، چې دوى به راښكلى كېږي');
INSERT INTO "abulsalam" VALUES(40,72,'40:72',' په ډېرو ګرمو اوبو كې، بیا به دوى (د دوزخ) په اور كې سوځولى شي');
INSERT INTO "abulsalam" VALUES(40,73,'40:73',' بیا به دوى ته ویلى شي چې چېرته دي هغه (معبودان) چې تاسو به شریكول ');
INSERT INTO "abulsalam" VALUES(40,74,'40:74',' له الله نه غیر؟ دوى به ووايي: هغه زمونږ نه ورك شول، بلكې مونږ له دې نه مخكې هېڅ شى نه باله، همداسې الله كافرانو لره ګمراه كوي');
INSERT INTO "abulsalam" VALUES(40,75,'40:75',' دغه په سبب د دې چې تاسو به په ځمكه كې ناحقه خوشحالي (اولويي) كوله او په سبب د هغه چې تاسو به تكبر كاوه');
INSERT INTO "abulsalam" VALUES(40,76,'40:76',' تاسو د جهنم په دروازو كې ننوځئ، په داسې حال چې په دغه (جهنم) كې به همېشه يئ، نو د متكبرینو استوګنځى بد دى');
INSERT INTO "abulsalam" VALUES(40,77,'40:77',' نو ته صبر كوه، بېشكه د الله وعده حقه ده، نو كه خامخا مونږ تا ته ځینې د هغه (عذاب) وښیو چې مونږ يې دوى ته وعده وركوو، یا مونږ تا وفات كړو، نو هم مونږ ته به دوى بېرته راوستلى شي');
INSERT INTO "abulsalam" VALUES(40,78,'40:78',' او یقینًا یقینًا مونږ له تا نه مخكې ډېر رسولان رالېږلي وو، په دوى كې ځينې هغه دي چې مونږ تا ته بيان كړي دي او په دوى كې ځينې هغه دي چې مونږ تا ته نه دي بیان كړي. او د هېڅ رسول لپاره دا ممكن نه وه چې هغه پخپله كومه معجزه راوړي مګر د الله په حكم سره، نو كله چې د الله حكم راشي (نو) په حق سره به فیصله وكړى شي او په دغه وخت (او ځاى) كې به باطل پرستان تاوانیان شي');
INSERT INTO "abulsalam" VALUES(40,79,'40:79',' الله هغه ذات دى چې تاسو لپاره يې څاروي پیدا كړي دي، د دې لپاره چې تاسو په دوى كې په ځینو سورلي كوئ او په دوى كې ځینې تاسو خورئ');
INSERT INTO "abulsalam" VALUES(40,80,'40:80',' او تاسو لپاره په دغو (څارویو) كې ډېرې نفعې دي او د دې لپاره چې تاسو په دوى سره هغه حاجت ته ورسېږئ چې ستاسو په سینو كې دى، او په دغو باندې او په كشتیو باندې تاسو سورول كېږئ');
INSERT INTO "abulsalam" VALUES(40,81,'40:81',' او هغه (الله) تاسو ته خپلې نښې ښیي، نو تاسو د الله له كومو نښو نه انكار كوئ؟');
INSERT INTO "abulsalam" VALUES(40,82,'40:82',' ایا نو دوى په ځمكه كې ګرځېدلي نه دي، پس چې دوى كتلي وى چې د هغو خلقو انجام څنګه و چې له دوى نه مخكې وو، هغوى له دوى نه زیات وو او د قوت او په ځمكه كې د نښو په لحاظ ډېر سخت وو، نو د دوى هېڅ په كار رانغلل هغه څه چې دوى به ګټل');
INSERT INTO "abulsalam" VALUES(40,83,'40:83',' نو كله چې دوى ته د دوى پېغمبران په ښكاره دلیلونو سره راغلل، (نو) دوى به په هغه علم خوشالي (او لويي) شروع كړه چې له دوى سره و، او ګېرچاپېره به شو هغه څه چې دوى به په هغه پورې استهزاء كولې');
INSERT INTO "abulsalam" VALUES(40,84,'40:84',' نو كله چې به دوى زمونږ عذاب ولیده، دوى به وویل: مونږ په الله ایمان راوړى دى چې ایكي يواځې دى، او مونږ كافران شولو په هغه څیز چې مونږ به د هغه په سبب مشركان وو');
INSERT INTO "abulsalam" VALUES(40,85,'40:85',' نو دغه (كار داسې) نه و چې دوى ته د دوى ایمان راوړل نفع وركړې وي كله چې دوى زمونږ عذاب ولیده، (دا) ده ايښې طريقه د الله هغه طریقه چې د هغه په بنده ګانو كې تېره شوې ده او په دغه ځاى (او وخت) كې كافران تاوانیان شول');
INSERT INTO "abulsalam" VALUES(41,1,'41:1',' حا، ميم');
INSERT INTO "abulsalam" VALUES(41,2,'41:2',' (دغه) نازلول د ډېر مهربان، بې حده رحم كوونكي له جانبه دي');
INSERT INTO "abulsalam" VALUES(41,3,'41:3',' داسې كتاب چې د ده ایتونه په تفصیل سره بیان شوي دي، په داسې حال كې چې عربي قرآن دى، د هغه قوم لپاره چې پوهېږي');
INSERT INTO "abulsalam" VALUES(41,4,'41:4',' زېرى وركوونكى او وېروونكى دى، نو د دوى زیاترو مخ وګرځاوه، پس دوى (حق) نه اوري');
INSERT INTO "abulsalam" VALUES(41,5,'41:5',' او دوى وویل: زمونږ زړونه په پرده كې دي له هغه څه نه چې ته مونږ هغه ته بلې او زمونږ په غوږونو كې كوڼوالى دى اوزمونږ له مينځه او ستا له مینځه پرده ده، نو ته(خپل) عمل كوه، بېشكه مونږ هم (خپل) عمل كوونكي یو');
INSERT INTO "abulsalam" VALUES(41,6,'41:6',' ته (دوى ته) ووایه: بېشكه هم دا خبره ده چې زه ستاسو په شان بشر یم، ما ته دا وحي كولى شي چې بېشكه همدا خبره ده چې ستاسو حق معبود یو معبود دى، نو تاسو ده ته مستقیم (او درست) اوسئ او له ده نه مغفرت غواړئ او د مشركانو لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(41,7,'41:7',' هغه كسان چې زكات نه ادا كوي او له اخرت نه هم دوى منكران (كافران) دي');
INSERT INTO "abulsalam" VALUES(41,8,'41:8',' بېشكه هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، د دوى لپاره نه قطع كېدونكى اجر دى');
INSERT INTO "abulsalam" VALUES(41,9,'41:9',' ته (دوى ته) ووایه: ایا په رښتیا تاسو خامخا كافران كېږئ په هغه ذات چې ځمكه يې په دوه ورځو كې پیدا كړه او تاسو د هغه لپاره شریكان جوړوئ. دغه خو رب العالمین دى');
INSERT INTO "abulsalam" VALUES(41,10,'41:10',' او دغه (الله) په دغې (ځمكه) كې د دې له پاسه غرونه كېښودل او په هغې كې يې بركت واچوه او په هغې كې يې د دې (د اوسېدونكو) روزي مقدره (په اندازه) كړه په څلورو ورځو كې، په داسې حال كې چې د تپوس كوونكو لپاره برابر دي');
INSERT INTO "abulsalam" VALUES(41,11,'41:11',' بیا يې د اسمان په طرف اراده وكړه، په داسې حال كې چې هغه یو لوګى و، نو هغه (الله) ده ته او ځمكې ته وویل: تاسو دواړه راشئ، په خپلې خوښې سره، يا په زور سره، دواړو وویل: مونږ په خوښې سره (فرمان منونكي) راځو');
INSERT INTO "abulsalam" VALUES(41,12,'41:12',' نو دغه يې په دوه ورځو كې اوه اسمانونه جوړ كړل او په هر اسمان كې يې د هغه د (مناسب) كار وحي وكړه او مونږ تر ټولو نژدې اسمان په ډېوو (د ستورو) سره ښكلى كړ او (دغه مو وساته) په ښې ساتنې سره، دغه د ډېر غالب، ښه پوه ذات اندازه كول دي');
INSERT INTO "abulsalam" VALUES(41,13,'41:13',' نو كه دوى مخ وګرځاوه، نو ته (دوى ته) ووایه: زه تاسو د عادیانو او ثمودیانو د سخت عذاب په مثل عذاب نه وېروم');
INSERT INTO "abulsalam" VALUES(41,14,'41:14',' كله چې دوى ته خپل رسولان د دوى له مخې او د دوى له شا نه راغلل او (ورته ويې ویل:) چې تاسو له الله نه غیر(د بل هیچا) عبادت مه كوئ. دوى وویل، كه زمونږ رب غوښتلى (نو) خامخا ملايك به يې نازل كړي وى، پس بېشكه مونږ په هغه څه سره چې تاسو ورسره رالېږل شوي يئ، كفر كوونكي یو');
INSERT INTO "abulsalam" VALUES(41,15,'41:15',' نو هر چې عادیان وو، نو دوى خو په ځمكه كې ناحقه لويي وكړه او ويې ويل:په قوت كې زمونږ نه څوك زیات سخت (او مضبوط) دي؟! ایا دوى نه ویني چې بېشكه هغه الله چې تاسو يې پیدا كړي یئ؛ هغه له تاسو نه په قوت كې ډېر سخت دى. او دغو به زمونږ له ایتونو نه انكار كاوه');
INSERT INTO "abulsalam" VALUES(41,16,'41:16',' نو مونږ په دوى باندې په څو ډېرو سپېرو ورځو كې ډېر زورور یخ باد راخوشې كړ، د دې لپاره چې مونږ په دوى باندې په دنيايي ژوند كې د رسوايي عذاب وڅكوو، او یقینًا د اخرت عذاب ډېر رسواكوونكى دى، په داسې حال كې چې د دوى به مدد نه كولى شي');
INSERT INTO "abulsalam" VALUES(41,17,'41:17',' او پاتې شول ثمودیان، نو مونږ دوى ته نېغه لار وښودله، نو دوى په هدایت باندې ړوندوالي ته ترجیح وركړه، نو دوى د سپكوونكي عذاب سختې چغې ونیول، په سبب د هغو عملونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(41,18,'41:18',' او مونږ هغو كسانو ته نجات وركړ چې ایمان يې راوړى و او پرهېزګاري به يې كوله');
INSERT INTO "abulsalam" VALUES(41,19,'41:19',' او(یاده كړه) هغه ورځ چې د الله دښمنان به اور ته راجمع كولى شي، نو دوى به ډلې كولى (او تقسیمولى) شي');
INSERT INTO "abulsalam" VALUES(41,20,'41:20',' تر دې چې كله دوى ده ته (څنګه) راشي (، نو) په دوى باندې به د دوى غوږونه او د دوى سترګې او د دوى څرمنې ګواهي وركړي، د هغو كارونو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(41,21,'41:21',' او دوى به خپلو څرمنو ته ووايي: تاسو ولې پر مونږ ګواهي وركړه؟ نو دغه (څرمنې) به ووايي: مونږ هغه الله ګویا كړو چې هر شى يې ګویا كړى دى. او دغه (الله) تاسو اول ځلې پیدا كړي یئ او خاص همده ته به بېرته ورګرځولى شئ');
INSERT INTO "abulsalam" VALUES(41,22,'41:22',' او تاسو به (د ګناه د كولو په وخت كې) ځان له دې نه نه پټولو چې په تاسو به ستاسو غوږونه او ستاسو سترګې او ستاسو څرمنې ګواهي وركړي او لېكن تاسو به دا خیال كاوه چې بېشكه الله په ډېرو هغو كارونو نه عالم كېږي چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(41,23,'41:23',' او دغه ستاسو هغه (خراب) ګمان دى چې تاسو به په خپل رب (دغه) ګمان كاوه، دغه تاسو هلاك كړئ، نو له تاوانیانو ځنې شوئ');
INSERT INTO "abulsalam" VALUES(41,24,'41:24',' پس كه دوى صبر وكړي، نو د دوى ځاى اور دى او كه دوى (دالله) رضا كول (او دنیا ته تلل) غواړي، نو دوى له قبول كړى شویو ځنې نه دي ');
INSERT INTO "abulsalam" VALUES(41,25,'41:25',' او مونږ د دوى لپاره ملګري مقرر كړي دي، نو دغو (ملګرو) دوى ته هغه څیزونه ښايسته كړي دي چې د دوى مخې ته دي او د دوى وروسته دي او په دوى (د عذاب) خبره ثابته شوې ده په هغو امتونو كې چې له دوى نه مخكې كوم پېریان او انسانان تېر شوي دي. بېشكه چې دوى تاوانیان وو');
INSERT INTO "abulsalam" VALUES(41,26,'41:26',' او كافران شوي كسان وايي : تاسو دغه قرآن ته غوږ مه ږدئ او په (تلاوت د) ده كې شور ماشور اچوئ، ښايي چې تاسو غالبه شئ');
INSERT INTO "abulsalam" VALUES(41,27,'41:27',' نو مونږ به په كافرانو شویو كسانو خامخا لازمًا ډېر سخت عذاب وڅكوو او خامخا لازمًا به مونږ دوى ته د هغو ټولو بدو عملونو بدله وركړو چې دوى به كول');
INSERT INTO "abulsalam" VALUES(41,28,'41:28',' دغه د الله د دښمنانو بدله ده،چې اور دى، د دوى لپاره په دغه (اور) كې د همېشه والي كور دى، په داسې حال كې چې (دغه) بدله ده، په سبب د هغه چې دوى زمونږ له ایتونو نه انكار كاوه');
INSERT INTO "abulsalam" VALUES(41,29,'41:29',' او كافران شوي كسان به ووايي: اى زمونږه ربه! ته مونږ ته هغه دوه تنه وښیه چې د پېریانو او انسانانو ځنې وو، چې مونږ دغه دواړه د خپلو قدمونو له لاندې كړو، د دې لپاره چې دغه دواړه د تر ټولو ښكته خلقو څخه شي');
INSERT INTO "abulsalam" VALUES(41,30,'41:30',' بېشكه هغه كسان چې (په رښتیا) يې ویلي دي: زمونږ رب الله دى، بیا ټینګ ولاړ وي (، نو) په دوى ملايك نازلېږي چې تاسو مه وېرېږئ او مه غمګین كېږئ او تاسو د هغه جنت زېرى واخلئ چې تاسو سره به يې وعده كولى شوه');
INSERT INTO "abulsalam" VALUES(41,31,'41:31',' مونږ ستاسو دوستان یو په دنيايي ژوند كې او په اخرت كې (هم) او تاسو لپاره په دغه (جنت) كې هر هغه (نعمتونه) شته چې ستاسو نفسونه يې خوښوي او تاسو لپاره په دغه (جنت) كې هر هغه څه دي چې تاسو يې غواړئ');
INSERT INTO "abulsalam" VALUES(41,32,'41:32',' په داسې حال كې چې د ډېر بخښونكي، بې حده رحم كوونكي له جانبه مېلمستیا ده');
INSERT INTO "abulsalam" VALUES(41,33,'41:33',' او د خبرې په لحاظ له هغه چا نه څوك ډېر ښه دى چې الله ته بلنه كوي او نېك عمل كوئ او وايي چې بېشكه زه له مسلمانانو ځنې یم؟');
INSERT INTO "abulsalam" VALUES(41,34,'41:34',' او نېكي او بدي هیڅكله سره نه برابرېږي. ته په هغې طریقې سره جواب وركوه چې تر ټولو ښه وي، نو ناڅاپه به هغه كس چې ستا په مینځ كې او د هغه په مینځ كې دښمني وي، داسې شي ګویا كې هغه ستا ډېر مهربان دوست دى');
INSERT INTO "abulsalam" VALUES(41,35,'41:35',' او دا (خصلت) نه وركول كېږي مګر هغو كسانو ته چې صبر كوي او دا نه وركول كېږي مګر د ډېرې لويې برخې والا ته');
INSERT INTO "abulsalam" VALUES(41,36,'41:36',' او كه خامخا تا ته د شیطان له جانبه څه وسوسه راشي، نو ته په الله پورې پناه ونیسه، بېشكه هم هغه ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(41,37,'41:37',' او د هغه له دلیلونو څخه شپه او ورځ او لمر او سپوږمۍ دي، تاسو مه لمر ته سجده كوئ او مه سپوږمۍ ته او تاسو خاص الله ته سجده كوئ چې دغه (نښې) يې پیدا كړې دي، كه چېرې تاسو خاص د هغه عبادت كوئ');
INSERT INTO "abulsalam" VALUES(41,38,'41:38',' نو كه دوى تكبر وكړ، نو هغه څوك چې له الله سره دي، هغوى شپه او ورځ د ده پاكي بیانوي، په داسې حال كې چې نه ستړي كېږي');
INSERT INTO "abulsalam" VALUES(41,39,'41:39',' او د هغه له نښو څخه دا دي چې بېشكه ته ځمكه وچه كلكه وینې، نو كله چې مونږ په دې اوبه نازلې كړو(نو) وخوځېږي او وپړسېږي، بېشكه هغه ذات چې دغه يې ژوندۍ كړې ده، خامخا د مړیو ژوندى كوونكى دى، بېشكه هغه په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(41,40,'41:40',' بېشكه هغه كسان چې زمونږ په ایتونو كې كاږه روان دي (او غلطې معناګانې كوي)، نو دوى پر مونږ پټ نه دي، ایا نو هغه څوك چې په اور كې غورځولى شي، ډېر غوره دى، یا هغه څوك چې د قیامت په ورځ (له عذاب نه) په امن كې راشي، څه چې ستاسو خوښ وي هغه كوئ، بېشكه هغه (الله) په هغو كارونو چې تاسو يې كوئ ؛ ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(41,41,'41:41',' بېشكه هغه كسان چې په ذكر (قرآن) سره كافران شوي دي، كله چې (دا ذكر) دوى ته راغى، په دې حال كې چې یقینًا دغه (ذكر) خامخا ډېر قوي كتاب دى');
INSERT INTO "abulsalam" VALUES(41,42,'41:42',' باطل ده ته نه د ده له مخې نه راتلى شي او نه د ده له شا نه، د ډېر حكمت والا، ښه ستايل شوي (الله) له جانبه رالېږل شوى دى');
INSERT INTO "abulsalam" VALUES(41,43,'41:43',' تا ته نه ویل كېږي مګر هغه خبرې چې یقینًا له تا نه مخكې رسولانو ته ويل شوي دي، بېشكه ستا رب خامخا د مغفرت والا او د ډېر دردوونكي عذاب والا دى');
INSERT INTO "abulsalam" VALUES(41,44,'41:44',' او كه مونږ دا (كتاب) عجمې قرآن ګرځولى وى، (نو) دوى به ضرور ویلى چې ولې د ده ایتونه واضح نه دي بیان شوي، ایا (كتاب) عجمي دى او (نبي يې) عربي دى، ته (دوى ته) ووایه: دغه (قرآن) د هغو كسانو لپاره چې ایمان يې راوړى دى ؛ هدایت او شفا دى او هغه كسان چې ایمان نه راوړي ؛ د هغوى په غوږونو كې دروندوالى دى، په داسې حال كې چې دغه (قرآن) په دوى ړوندوالى دى. دغه خلق (كفار ګویاكې داسې) دي چې دوى ته له ډېر لرې ځایه اواز كولى شي');
INSERT INTO "abulsalam" VALUES(41,45,'41:45',' او یقینًا یقینًا مونږ موسٰى ته كتاب وركړى و، نو په دغه (كتاب) كې اختلاف وكړى شو او كه چېرې هغه كلمه نه وى چې ستا د رب له جانبه ړومبى شوې ده،(نو) خامخا به د دوى په مینځ كې فیصله شوې وى. او بېشكه دوى د ده په هكله خامخا په اضطراب اچوونكي شك كې دي');
INSERT INTO "abulsalam" VALUES(41,46,'41:46',' هر څوك چې نېك عمل كوي، نو د خپل ځان لپاره (يې كوي) او څوك چې بدي كوي، نو ضرر يې په همده دى، او ستا رب په (خپلو) بنده ګانو د هېڅ ظلم كوونكى نه دى');
INSERT INTO "abulsalam" VALUES(41,47,'41:47',' خاص همده ته د قیامت علم ورسپارلى شي او هېڅ مېوې له خپلو غلافونو څخه نه راوځي او نه هېڅ یو ښځه حامله كېږي او نه (بچى) زېږوي مګر د دغه (الله) په علم سره او (یاده كړه) هغه ورځ چې دوى ته به (رب) اواز وكړي چې (ستاسو په عقیده) زما شریكان چېرته دي؟ دوى به ووايي: مونږ ته خبر كړې چې (اوس) پر مونږ كې هیڅوك (د دې خبرې) ګواهي كوونكى نشته');
INSERT INTO "abulsalam" VALUES(41,48,'41:48',' او له دوى نه به هغه (معبودان) ورك شي چې له دې نه مخكې به دوى بلل او دوى به یقین وكړي چې د دوى لپاره د تېښتې هېڅ ځاى نشته');
INSERT INTO "abulsalam" VALUES(41,49,'41:49',' انسان د خیر له غوښتلو نه نه ستړى كېږي او كه ده ته شر (او تكلیف) ورسېږي، نو ډېر ناهېلى، ډېر نا امېده وي');
INSERT INTO "abulsalam" VALUES(41,50,'41:50',' او كه خامخا مونږ په ده له خپل جانب نه رحمت وڅكوو، له هغه تكلیف نه وروسته چې ده ته رسېدلى وي (،نو) خامخا لازمًا به وايي: دا زما (حق) دى او زه د قیامت په هكله ګمان نه كوم چې قايم به شي او خامخا كه زه خپل رب ته بېرته وګرځولى شم، (نو) بېشكه زما لپاره به د هغه په نېز خامخا تر ټولو ښه حالت (جنت) وي، نو مونږ به خامخا لازمًا كافران شوي كسان په خپلو عملونو خبر كړو او خامخا لازمًا به مونږ دوى ته له ډېر سخت عذاب ځنې وڅكوو');
INSERT INTO "abulsalam" VALUES(41,51,'41:51',' او كله چې مونږ په انسان باندې انعام وكړو (نو) دى (له حقه) مخ واړوي او خپل جانب (ځان) لرې كړي او كله چې ده ته سختي ورسېږي، نو بیا د ډېرې اوږدې دعا والا وي');
INSERT INTO "abulsalam" VALUES(41,52,'41:52',' ته (دوى ته) ووایه: تاسو ما خبر كړئ! كه چېرې دغه (قرآن) د الله له جانبه وي، بیا تاسو په ده سره كافران شئ، (نو) له هغه چا نه لوى ګمراه څوك دى چې په ډېر لرې مخالفت كې وي');
INSERT INTO "abulsalam" VALUES(41,53,'41:53',' ژر ده چې مونږ به دوى ته خپلې نښې په افاقو (د عالم په اطرافو) كې او د دوى په ځانونو كې وښیو، تر دې چې دوى ته ښه څرګند شي چې یقینًا هم دغه (قرآن) حق دى او ایا ستا رب ښه كافي نه دى دا چې یقینًا هغه په هر شي باندې خبردار دى');
INSERT INTO "abulsalam" VALUES(41,54,'41:54',' خبردار شئ! بېشكه دوى د خپل رب له ملاقات نه په شك كې دي، خبردار شئ! بېشكه هغه په هر شي احاطه كوونكى دى');
INSERT INTO "abulsalam" VALUES(42,1,'42:1',' حٰمٓ');
INSERT INTO "abulsalam" VALUES(42,2,'42:2',' عٓسٓقٓ');
INSERT INTO "abulsalam" VALUES(42,3,'42:3',' (له شروع نه) هغه همداسې وحي كوي تا ته او هغو (پېغمبرانو) ته چې له تا نه مخكې وو، هغه الله چې ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(42,4,'42:4',' خاص د هغه لپاره هر هغه څه دي په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي او هم دغه (الله) ډېر اوچت، ډېر لوى دى');
INSERT INTO "abulsalam" VALUES(42,5,'42:5',' اسمانونه دې ته نژدې دي چې د خپل له پاسه نه وچوي او ملايك د خپل د رب ستاينې سره تسبیح وايي او د هغه چا لپاره مغفرت غواړي چې په ځمكه كې دي. خبردار شئ! بېشكه الله، هم دى ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(42,6,'42:6',' او هغه كسان چې له ده نه غیر يې نور كارسازان نیولي دي، الله په دوى ښه نګران دى او ته په دوى باندې هېڅ ذمه وار نه يې');
INSERT INTO "abulsalam" VALUES(42,7,'42:7',' او همداسې مونږ تا ته (دا) عربي قرآن وحي كړو، د دې لپاره چې ته ام القرىٰ (مكه) ووېروې او هغه څوك چې د دې ګېر چاپیره دي او ته (دوى) د جمع كېدلو له ورځې نه ووېروې چې هېڅ شك په كې نشته، یوه ډله به په جنت كې وي او بله ډله به په دوزخ كې وي');
INSERT INTO "abulsalam" VALUES(42,8,'42:8',' او كه الله غوښتلي وى (نو) دوى به يې خامخا یو امت ګرځولي وو او لېكن هغه په خپل رحمت كې چا ته چې وغواړي داخلوي يې او ظالمان چې دي، د دوى لپاره نه هېڅ كارساز شته او نه مددګار');
INSERT INTO "abulsalam" VALUES(42,9,'42:9',' ایا دوى له ده (الله) نه غیر كارسازان (او دوستان) نیولي دي، نو هم دغه الله كارساز دى او هم دى مړي راژوندي كوي او هم دى په هر څیز باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(42,10,'42:10',' او هر هغه شى چې تاسو په هغه كې اختلاف وكړئ، نو د هغه حكم (فیصله) الله ته ده، دغه الله زما رب دى، خاص په ده باندې ما توكل كړى دى او خاص هم ده ته زه رجوع كوم');
INSERT INTO "abulsalam" VALUES(42,11,'42:11',' (دغه الله) د اسمانونو او ځمكې پیدا كوونكى دى، تاسو لپاره يې له هم ستاسو له جنس (او ځانونو) نه جوړې پیدا كړي دي او له څارویو نه يې هم قسمونه (پیدا كړي دي)، هغه تاسو په دې طریقې سره خوروي، د هغه په مثل هېڅ شى نشته او هم دغه ښه اورېدونكى، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(42,12,'42:12',' خاص هم دغه (الله) لره د اسمانونو او ځمكې چابیانې دي، هغه چا لپاره چې وغواړي رزق فراخوي او (چا لپاره چې وغواړي) تنګوي يې، بېشكه هغه په هر شي باندې ښه عالم دى');
INSERT INTO "abulsalam" VALUES(42,13,'42:13',' هغه تاسو لپاره هغه دین مقرر كړى دى چې د هغه وصیت يې نوح ته كړى و او هغه چې مونږ تا ته وحي كړى دى او هغه چې مونږ د هغه وصیت ابراهیم او موسٰى او عیسٰى ته كړى و، چې تاسو دین قايم كړئ او په ده كې مه مختلف كېږئ، په مشركانو باندې هغه شى ډېر دروند دى چې ته دوى هغه ته بلې، الله غوره كوي دې (دین) ته چا ته چې وغواړي او د ده په طرف هغه چا ته هدایت كوي چې رجوع كوي');
INSERT INTO "abulsalam" VALUES(42,14,'42:14',' او دوى سره مختلف شوي نه وو مګر وروسته له هغه چې دوى ته علم راغى، په خپل مینځ كې د حسد په وجه، او كه چېرې هغه خبره نه وى چې ستا د رب له جانبه تر ټاكلې نېټې پورې ړومبى شوې ده (، نو) د دوى په مینځ كې به خامخا فیصله كړى شوې وى اوبېشكه هغه كسان چې له دوى نه وروسته ورته كتاب په میراث كې وركړى شوى دى، خامخا د دغه (قرآن) په هكله په اضطراب اچوونكي شك كې دي');
INSERT INTO "abulsalam" VALUES(42,15,'42:15',' نو ته همدغه (توحید) ته بلنه كوه او په استقامت اوسه، لكه څنګه چې تا ته حكم دركړى شوى دى او د دوى د خواهشاتو پیروي مه كوه او ته وایه: ما په هر هغه كتاب ایمان راوړى دى چې الله نازل كړى دى او ما ته حكم راكړى شوى دى چې ستاسو په مینځ كې انصاف وكړم، الله زمونږ رب دى او ستاسو رب دى، زمونږ لپاره زمونږ عملونه دي او تاسو لپاره ستاسو عملونه دي (حق ښه واضح شو، نو زمونږ په مینځ كې او ستاسو په مينځ كې هېڅ جګړه نشته، الله به مونږ ټول سره راجمع كړي او خاص همده ته بېرته ورتلل دي');
INSERT INTO "abulsalam" VALUES(42,16,'42:16',' او هغه كسان چې د الله په باره كې وروسته له دې جګړې كوي چې د هغه دعوت قبول كړى شو، د دغو كسانو جګړه كول د دوى د رب په نېز باطل دي او په دوى غضب دى او د دوى لپاره ډېر سخت عذاب دى');
INSERT INTO "abulsalam" VALUES(42,17,'42:17',' الله هغه ذات دى چې كتاب يې په حقه سره نازل كړى دى او تله (ميزان) يې هم او تا څه شى خبروي، ښايي چې قیامت ډېر نژدې وي');
INSERT INTO "abulsalam" VALUES(42,18,'42:18',' دغه (قیامت) هغه كسان په تلوار سره غواړي چې په ده ایمان نه لري او هغه كسان چې (په ده) يې ایمان راوړى دى، له ده نه وېرېدونكي دي او دوى پوهېږي چې یقینًا دا حق دى، خبردار شئ! بېشكه هغه كسان چې د قیامت په باره كې جګړې (او شكونه) كوي، یقینًا په ډېرې لرې ګمراهۍ كې دي');
INSERT INTO "abulsalam" VALUES(42,19,'42:19',' الله په خپلو بنده ګانو ډېر مهربان دى، هغه روزي وركوي چا ته چې وغواړي، او هم دى ډېر زورور، ښه غالب دى ');
INSERT INTO "abulsalam" VALUES(42,20,'42:20',' هر هغه څوك چې د اخرت فصل غواړي، نو مونږ به د ده لپاره د ده په فصل كې زیاتوالى وكړو او هر هغه څوك چې د دنیا فصل غواړي (نو) مونږ به ده ته له دغې (دنیا) نه څه وركړو او د ده لپاره به په اخرت كې هېڅ برخه نه وي');
INSERT INTO "abulsalam" VALUES(42,21,'42:21',' ایا د دوى لپاره داسې شریكان شته چې د دوى لپاره يې له دین څخه هغه لاره مقرر كړې ده چې الله د هغې اذن نه دى كړى او كه چېرې د فیصلې خبره نه وى (نو) د دوى په مینځ كې به خامخا فیصله شوې وى او بېشكه ظالمان چې دي، د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(42,22,'42:22',' ته به ظالمان (مشركان) وینې چې د خپل كړي عمل (له انجام) نه به وېرېدونكي وي، په دې حال كې چې هغه په دوى پرېوتونكى دى، او هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، د جنتونو په باغېچو كې به وي د دوى لپاره د خپل رب په نېز هغه څه دي چې دوى يې غواړي، هم دغه ډېر لوى فضل دى');
INSERT INTO "abulsalam" VALUES(42,23,'42:23',' دغه دى هغه شى چې الله يې خپلو هغو بنده ګانو ته زېرى وركوي چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، ته (دوى ته) ووایه: زه له تاسو نه په دغه (تبلیغ) باندې هېڅ اجر (مزدوري) نه غواړم، لېكن په خپلوۍ كې مینه (د خپلولۍ مينه) (غواړم) او هر هغه څوك چې نېكي وكړي مونږ به هغه ته په دغې (نېكۍ) ښايست ورزیات كړو، بېشكه اللٰه ډېر مغفرت كوونكى ډېر قدردان (شكر قبلوونكى) دى');
INSERT INTO "abulsalam" VALUES(42,24,'42:24',' ایا دوى وايي چې ده په الله باندې له ځانه دروغ تړلي دي، نو كه الله وغواړي ستا په زړه به مهر ولګوي او الله باطل محوه كوي او په خپلو كلماتو سره حق ثابتوي، بېشكه هغه د سینو په خبرو ښه عالم دى');
INSERT INTO "abulsalam" VALUES(42,25,'42:25',' او دى همغه ذات دى چې د خپلو بنده ګانو توبه قبلوي او د بدیو معافي كوي او په هغو كارونو پوهېږي چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(42,26,'42:26',' او د هغو كسانو دعاګانې قبلوي چې ایمان يې راوړى دى اونېك عملونه يې كړي دي او له خپل فضله دوى ته زیات وركوي، او كافران (چې دي) د دوى لپاره ډېر سخت عذاب دى');
INSERT INTO "abulsalam" VALUES(42,27,'42:27',' او كه چېرې الله د خپلو بنده ګانو لپاره روزي فراخه كړي، نو دوى به په ځمكه كې خامخا سركشي وكړي او لېكن هغه په یوې اندازې سره څومره چې وغواړې نازلوي يې، بېشكه هغه په خپلو بنده ګانو ښه خبردار، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(42,28,'42:28',' او دى همغه ذات دى چې باران وروي، وروسته له دې چې دوى نا امېده شي او هغه خپل رحمت خوروي او هم دى ښه كارساز، ښه ستايل شوى دى');
INSERT INTO "abulsalam" VALUES(42,29,'42:29',' او د ده له نښو څخه د اسمانونو او ځمكې پیدا كول دي او د هغو زنده سرو (خوځندهوو پیدا كول دي) چې ده په دغو دواړو كې خواره كړي دي او دغه (الله) چې كله وغواړي د دغو (څیزونو) په راجمع كولو ښه قادر دى');
INSERT INTO "abulsalam" VALUES(42,30,'42:30',' او تاسو ته چې څه مصیبت هم رسي، نو په سبب د هغو كارونو چې ستاسو لاسونو كړي دي او ډېر (ګناهونه) معاف كوي');
INSERT INTO "abulsalam" VALUES(42,31,'42:31',' او تاسو په ځمكه كې (الله لره) له سره عاجزه كوونكي نه يئ او د تاسو لپاره له الله نه غیر نه څوك دوست (او كارساز) شته او نه مددګار');
INSERT INTO "abulsalam" VALUES(42,32,'42:32',' او د هغه له نښو څخه په سمندر كې روانې بېړۍ دي، چې دغرونو په شان دي');
INSERT INTO "abulsalam" VALUES(42,33,'42:33',' كه هغه (الله) وغواړي، نو هوا به ودروي، نو دغه (بېړۍ) به د ده په شا ولاړې پاتې شي، بېشكه په دغو كې د هر صبرناك او ډېر شكر ایستونكي لپاره یقینًا ډېرې نښې دي');
INSERT INTO "abulsalam" VALUES(42,34,'42:34',' یا به دغه (بېړۍ) هلاكې (ډوبې) كړي، په سبب د هغو (ګناهونو) چې دغو (خلقو) كړي دي او (كه وغواړي) ډېر (ګناهونه) معاف كوي');
INSERT INTO "abulsalam" VALUES(42,35,'42:35',' او چې هغه كسان پوه شي چې زمونږ په ایتونو كې جګړې كوي، چې د دوى لپاره د خلاصي هېڅ ځاى نشته');
INSERT INTO "abulsalam" VALUES(42,36,'42:36',' پس تاسو ته چې څه شى هم دركړى شوى دى، نو (دغه) د دنيايي ژوند فايده ده، او هغه (ثواب) چې د الله په نېز دى (هغه) ډېر غوره او ډېر باقي پاتې كېدونكى دى د هغو كسانو لپاره چې ایمان يې راوړى دى او خاص په خپل رب باندې توكل كوي');
INSERT INTO "abulsalam" VALUES(42,37,'42:37',' او د هغو كسانو لپاره چې له غټو ګناهونو او د بې حیایۍ له كارونو نه ځان ساتي او چې كله هم په غضب شي (نو) دوى بخښنه كوي');
INSERT INTO "abulsalam" VALUES(42,38,'42:38',' او د هغو كسانو لپاره چې د خپل رب حكم مني او لمونځ قايموي او د دوى كار په خپل مینځ كې په مشورې سره وي او ځینې له هغه (مال) نه چې مونږ دوى ته وركړى دى ؛ انفاق كوي');
INSERT INTO "abulsalam" VALUES(42,39,'42:39',' او د هغو كسانو لپاره چې دوى ته (د كافرانو له خوا) ظلم ورسېږي (نو) دوى ( له هغو نه) انتقام اخلي');
INSERT INTO "abulsalam" VALUES(42,40,'42:40',' او د بدۍ بدله د هغې په شان بدي ده، نو هغه څوك چې معافي وكړي او اصلاح وكړي نو د ده اجر په الله دى، بېشكه هغه (الله) ظالمان نه خوښوي');
INSERT INTO "abulsalam" VALUES(42,41,'42:41',' او یقینًا هغه څوك چې په خپل ځان له ظلم كېدو نه پس بدل واخلي، نو دغه كسان (دي چې) په دوى (د ملامتیا) هېڅ لار نشته');
INSERT INTO "abulsalam" VALUES(42,42,'42:42',' بېشكه همدا خبره ده چې (د ملامتیا) لار خو په هغو كسانو ده چې په خلقو ظلم كوي او په ځمكه كې ناحقه سركشي كوي، دغه كسان (دي چې) د دوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(42,43,'42:43',' او يقينًا هغه څوك چې صبر يې وكړ او بخښنه يې وكړه، بېشكه دغه (صبر او بخښنه) خامخا له غوره كارونو څخه دي ');
INSERT INTO "abulsalam" VALUES(42,44,'42:44',' او هر هغه څوك چې الله يې ګمراه كړي، نو د ده لپاره له دغه (الله) نه پس هېڅ دوست (او حمایتي) نشته او ته به ظالمان وینې، كله چې دوى عذاب وویني، دوى به وايي: ایا بېرته تللو ته څه لار شته؟');
INSERT INTO "abulsalam" VALUES(42,45,'42:45',' او ته به دوى وینې چې په دغه (اور) به پیش كولى شي، په داسې حال كې چې د ذلت په وجه به وېرېدونكي وي، دوى به د سترګو له ګوټونو نه ګوري او هغه كسان به چې ایمان يې راوړى دى؛ وايي: بېشكه تاوانیان خو هغه كسان وي چې د قیامت په ورځ يې خپل ځان او خپلې كورنۍ ته تاوان ورساوه، خبردار شئ! بېشكه ظالمان به په دايمي (همېشني) عذاب كې وي');
INSERT INTO "abulsalam" VALUES(42,46,'42:46',' او د دغو لپاره به هیڅوك دوستان نه وي چې له الله نه غیر له دوى سره مدد وكړي او هغه څوك چې الله يې ګمراه كړي نو د ده لپاره هېڅ لاره نشته');
INSERT INTO "abulsalam" VALUES(42,47,'42:47',' تاسو د خپل رب حكم ومنئ مخكې له دې چې هغه ورځ راشي چې د الله له طرفه هغې لره هېڅ رد (او ګرځول) نشته، په دغه ورځ كې به تاسو لپاره هېڅ پناه ځاى نه وي او نه به څه انكار وي');
INSERT INTO "abulsalam" VALUES(42,48,'42:48',' نو كه دوى مخ وګرځوي، نو ته مونږ په دوى باندې نګران نه يې لېږلى، په تا خو يواځې تبلیغ كول دي، بېشكه مونږ كله چې په انسان له خپل جانب نه رحمت وڅكوو (نو) هغه په ده سره خوشحاله شي او كه چېرې دوى ته تكلیف ورسېږي په سبب د هغو (ګناهونو) چې د دوى لاسونو مخكې لېږلي دي بیا خو دغه انسان یقینًا ډېر ناشكره دى');
INSERT INTO "abulsalam" VALUES(42,49,'42:49',' خاص الله لره د اسمانونو او ځمكې بادشاهي ده، څه چې وغواړي هغه پیدا كوي چا ته چې وغواړي جینكۍ وركوي او چا ته چې وغواړي هلكان وركوي');
INSERT INTO "abulsalam" VALUES(42,50,'42:50',' یا دوى ته هلكان او جینكۍ جمع كوي (دواړه وركوي) او چا ته چې وغواړي شنډ (او) شنډه يې وګرځوي، بېشكه هغه، ښه عالم، ښه قادر دى');
INSERT INTO "abulsalam" VALUES(42,51,'42:51',' او د هېڅ انسان لپاره ممكن نه و چې الله ورسره خبرې وكړي مګر په وحي سره، یا له پردې نه شا ته، یا استاځى راولېږي، پس دغه د ده په حكم سره ورسوي هغه چې هغه (الله) يې غواړي، بېشكه دى، ډېر اوچت، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(42,52,'42:52',' او همداسې مونږ په خپل امر سره تا ته د روح (قرآن) وحي راولېږله، ته نه پوهېدلى چې كتاب څه شى دى او نه (پوهېدى چې) ایمان (څه شى دى) او لېكن مونږ دغه (كتاب) رڼا ګرځولى دى چې په ده سره مونږ په خپلو بنده ګانو كې چې چا ته وغواړو هدایت وركوو او بېشكه یقینًا ته نېغې لارې ته ښوونه كوي');
INSERT INTO "abulsalam" VALUES(42,53,'42:53',' د هغه الله لارې ته چې خاص هغه لره دي هر هغه څه چې په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي. اګاه شئ! خاص الله ته ټول كارونه بېرته ګرځي');
INSERT INTO "abulsalam" VALUES(43,1,'43:1',' حا، ميم');
INSERT INTO "abulsalam" VALUES(43,2,'43:2',' قسم دى، په دغه روښانه كتاب');
INSERT INTO "abulsalam" VALUES(43,3,'43:3',' بېشكه مونږ دا عربي قرآن ګرځولى دى، د دې لپاره چې تاسو له عقل نه كار واخلئ');
INSERT INTO "abulsalam" VALUES(43,4,'43:4',' او بېشكه دا په ام الكتب (لوح محفوظ) كې زمونږ په نزد خامخا ډېر اوچت، ډېر حكمت والا دى');
INSERT INTO "abulsalam" VALUES(43,5,'43:5',' ایا نو مونږ له تاسو نه دا ذكر (قرآن) واړوو (بند كړو) بېخي اړول (بندول)، په دې سبب چې تاسو له حده تېرېدونكى قوم یئ');
INSERT INTO "abulsalam" VALUES(43,6,'43:6',' او مونږ په ړومبنو خلقو كې څومره ډېر نبیان لېږلي دي');
INSERT INTO "abulsalam" VALUES(43,7,'43:7',' او دوى ته به هېڅ یو نبي نه راته مګر دوى به په هغه پورې مسخرې كولې');
INSERT INTO "abulsalam" VALUES(43,8,'43:8',' نو مونږ هغه هلاك كړل چې له دوى نه په نیولو (او قوت) كې ډېر زیات سخت وو او د ړومبنیو خلقو مثال (او حال) تېر شوى دى');
INSERT INTO "abulsalam" VALUES(43,9,'43:9',' او قسم دى كه ته له دوى نه تپوس وكړې چې اسمانونه او ځمكه چا پیدا كړي دي (، نو) دوى به خامخا لازمًا ووايي چې دغه ډېر غالب، ډېر پوه ذات پیدا كړي دي');
INSERT INTO "abulsalam" VALUES(43,10,'43:10',' هغه چې تاسو لپاره يې ځمكه فرش ګرځولې ده او تاسو لپاره يې په دې كې لارې پیدا كړي دي، د دې لپاره چې نېغه لار ومومئ');
INSERT INTO "abulsalam" VALUES(43,11,'43:11',' او هغه ذات چې له بره نه يې اوبه په اندازې سره نازلې كړې دي، پس په دغو سره مونږ مړ ښار راژوندى كړى دى، همداسې به تاسو راوېستلى شئ');
INSERT INTO "abulsalam" VALUES(43,12,'43:12',' او هغه ذات چې ټول قسمونه (انواع) يې پیدا كړي دي او تاسو لپاره يې له بېړیو او څارویو نه هغه پیدا كړي دي چې تاسو پرې سورېږئ');
INSERT INTO "abulsalam" VALUES(43,13,'43:13',' د دې لپاره چې تاسو د هغو په شاګانو ښه برابر كېنئ، بیا د خپل رب نعمت رایاد كړئ، كله چې تاسو په دغه برابر كېنئ او ووایئ: پاكي ده هغه ذات لره چې زمونږ لپاره يې دا مسخر (او تابع) كړى دى او مونږ ده لره په قابو كې راوستونكي نه یو');
INSERT INTO "abulsalam" VALUES(43,14,'43:14',' او بېشكه مونږ خاص خپل رب ته خامخا بېرته ورتلونكي یو');
INSERT INTO "abulsalam" VALUES(43,15,'43:15',' او دوى د ده په بنده ګانو كې ځينو لره د ده برخه (او اولاد) وګرځول، بېشكه انسان خامخا ډېر ښكاره ناشكره دى');
INSERT INTO "abulsalam" VALUES(43,16,'43:16',' ایا (تاسو دا وایئ چې) هغه (الله) له هغه چا نه، چې دى يې پیدا كوي، د ځان لپاره لوڼه نیولي دي او تاسو يې په زامنو سره غوره كړي يئ');
INSERT INTO "abulsalam" VALUES(43,17,'43:17',' حال دا چې كله دوى ته په هغه (لور) سره زېرى وركړى شي چې دى يې د رحمٰن لپاره مثال بیانوي (نو) د ده مخ تك تور شي، په دې حال كې چې دى له غمه ډك وي');
INSERT INTO "abulsalam" VALUES(43,18,'43:18',' او ایا (دوى د الله لپاره مقرروي) هغه چې په كالیو (زیوراتو) كې لویه ولى شي، حال دا چې په جګړه كې خبره واضحه كوونكي نه وي');
INSERT INTO "abulsalam" VALUES(43,19,'43:19',' او دوى (مشركان) ملايكو لره هغه چې دوى د رحمٰن بنده ګان دي، ښځې ګرځوي، ایا دوى د دغو پیدا كولو ته حاضر وو؟ یقینًا به د دوى ګواهي ولیكل شي او له دوى نه تپوس (هم) كېږي ');
INSERT INTO "abulsalam" VALUES(43,20,'43:20',' او دوى وايي: كه رحمٰن غوښتلي وى، (نو) مونږ به د دغو (باطلو معبودانو) عبادت نه وى كړى، دوى ته د دې (خبرې) هېڅ علم نشته، دوى يواځې اټكلونه چلوي');
INSERT INTO "abulsalam" VALUES(43,21,'43:21',' ایا دوى ته مونږ له دغه (قرآن) نه مخكې كوم كتاب وركړى دى، پس دوى په هغه منګلولې ښخونكي دي');
INSERT INTO "abulsalam" VALUES(43,22,'43:22',' بلكې دوى وايي: بېشكه مونږ خپل پلرونه په یوې لارې (طريقې) باندې موندلي دي، او بېشكه مونږ هم د دوى په قدمونو لاره موندونكي یو');
INSERT INTO "abulsalam" VALUES(43,23,'43:23',' او همداسې مونږ له تا نه مخكې په كوم كلي كې هېڅ یو وېروونكى نه دى لېږلى مګر د هغه كلي متكبره مالدارو به وویل: بېشكه مونږ خپل پلرونه په یوه لاره (طريقه) موندلي دي او بېشكه مونږ هم د دوى په قدمونو اقتدا كوونكي یو');
INSERT INTO "abulsalam" VALUES(43,24,'43:24',' (دوى ته به د دوى نبي) وویل: او ایا اګر كه ما تاسو ته له هغې (لارې) نه زیاته سمه لاره راوړې وي چې تاسو په هغې باندې خپل پلرونه موندلي دي؟ دوى به ویل: بېشكه مونږ په هغه (دین) كفر كوونكي یو چې تاسو په هغه سره رالېږل شوي يئ');
INSERT INTO "abulsalam" VALUES(43,25,'43:25',' نو مونږ له دوى نه انتقام واخيست، نو ته وګوره د تكذیب كوونكو انجام څنګه و');
INSERT INTO "abulsalam" VALUES(43,26,'43:26',' او (یاد كړه) هغه وخت چې ابراهیم خپل پلار او خپل قوم ته وویل: بېشكه زه له هغو (معبودانو) نه بېزاره یم چې تاسو يې عبادت كوئ');
INSERT INTO "abulsalam" VALUES(43,27,'43:27',' غیر له هغه ذات نه چې زه يې پیدا كړى یم، پس یقینًا هغه به ما ته خامخا هدایت وكړي');
INSERT INTO "abulsalam" VALUES(43,28,'43:28',' او هغه (ابراهیم) دغه ( د توحید كلمه) په خپل اولاد كې باقي پاتې كېدونكې وګرځوله، د دې لپاره چې راوګرځي');
INSERT INTO "abulsalam" VALUES(43,29,'43:29',' بلكې ما دوى او د دوى پلرونو ته نفع وركړه تر هغه پورې چې دوى ته حق (قرآن) او ښكاره بیانوونكى رسول راغى');
INSERT INTO "abulsalam" VALUES(43,30,'43:30',' او كله چې دوى ته حق راغى دوى وویل: دا جادو دى، او بېشكه مونږ له ده نه انكار كوونكي یو');
INSERT INTO "abulsalam" VALUES(43,31,'43:31',' او دوى وویل: دا قرآن په دوه كلیو (ښارونو) كې په یو غټ سړي ولې نه نازلاوه شي؟');
INSERT INTO "abulsalam" VALUES(43,32,'43:32',' ایا دوى ستا د رب رحمت تقسیموي؟ مونږ د دوى په مینځ كې په دنيايي ژوند كې د دوى معشیت تقسیم كړى دى او په درجو كې مو د دوى ځینې د ځینو نورو له پاسه پورته كړي دي، د دې لپاره چې د دوى ځینې ځینې نور (تابع او) كار كوونكي ونيسي او ستا د رب رحمت له هغه نه ډېر غوره دى چې دوى يې جمع كوي');
INSERT INTO "abulsalam" VALUES(43,33,'43:33',' او كه دا (خطره) نه وى چې خلق به ټول یوه ډله (كافران شي) نو مونږ به خامخا د هغه چا لپاره چې په رحمٰن كافران كېږي، د دوى كورونو ته به مو د سپینو زرو چتونه او داسې پوړۍ ګرځولې وى چې دوى به په هغو پاس ختلى');
INSERT INTO "abulsalam" VALUES(43,34,'43:34',' او د دوى د كوټو لپاره به مو (د سپینو زرو) دروازې او تختونه جوړ كړي وى چې دوى به په هغو تكیه وهلې');
INSERT INTO "abulsalam" VALUES(43,35,'43:35',' او له سرو زرو نه (دغه او نور زينتونه به مو وركړي وى) او نه دي دغه (ټول څیزونه) مګر د دنيايي ژوند سامان دى او اخرت ستا د رب په نېز د متقیانو (پرهیزګارانو) لپاره دى');
INSERT INTO "abulsalam" VALUES(43,36,'43:36',' او هر هغه څوك چې د رحمٰن له ذكر نه ځان ړوند كړي (،نو) مونږ به په ده باندې یو شیطان مسلط كړو، نو هغه به له ده سره تل ملګرى وي');
INSERT INTO "abulsalam" VALUES(43,37,'43:37',' او بېشكه دغه (شیطانان) یقینًا دوى (خپل ملګري) له حقې لارې نه منع كوي او دوى ګمان كوي چې بېشكه دوى هدایت موندونكي دي');
INSERT INTO "abulsalam" VALUES(43,38,'43:38',' تر هغه پورې چې كله دا مونږ ته راشي ( نو) وايي به: اى كاشكې! زما په مینځ كې او ستا په مینځ كې د مشرق او مغرب هومره لرې والى وى، پس (دا) بد ملګرى دى');
INSERT INTO "abulsalam" VALUES(43,39,'43:39',' او كله چې تاسو ظلم كړى دى (نو) تاسو ته به نن ورځ دا خبره له سره نفع درنـكړي چې بېشكه تاسو (او شیطانان ټول) په عذاب كې سره شریك یئ');
INSERT INTO "abulsalam" VALUES(43,40,'43:40',' ایا نو ته به كڼو ته اورول وكړې، یا به ړندو ته لار ښوونه وكړې او هغه چا ته چې په ښكاره ګمراهۍ كې دى؟');
INSERT INTO "abulsalam" VALUES(43,41,'43:41',' نو كه تا مونږ لازمًا بوځو، نو بېشكه مونږ له دوى نه انتقام اخيستونكي یو');
INSERT INTO "abulsalam" VALUES(43,42,'43:42',' ایا مونږ تا ته خامخا هغه وښیو چې مونږ يې له دوى سره وعده كړې ده، نو بېشكه مونږ په دوى پوره قدرت لرونكي یو');
INSERT INTO "abulsalam" VALUES(43,43,'43:43',' نو ته په هغه (قرآن) منګلې ولګوه چې تا ته وحي كړى شوى دى، بېشكه ته په نېغه لار يې');
INSERT INTO "abulsalam" VALUES(43,44,'43:44',' او بېشكه دا (قرآن) تا لپاره او ستا د قوم لپاره ذكر (او نصیحت) دى او ژر ده چې له تاسو نه به پوښتنه وكړى شي');
INSERT INTO "abulsalam" VALUES(43,45,'43:45',' او ته زمونږ له هغو رسولانو نه تپوس وكړه چې مونږ له تا نه مخكې لېږلي دي، ایا مونږه له رحمن نه غیر نور معبودان مقرر كړي دي چې د دوى عبادت كولى شي');
INSERT INTO "abulsalam" VALUES(43,46,'43:46',' او یقینًا یقینًا مونږ موسی زمونږ له نښو سره فرعون او د هغه سردارانو ته لېږلى و، پس ده وویل: بېشكه زه د رب العلمین رسول یم');
INSERT INTO "abulsalam" VALUES(43,47,'43:47',' نو كله چې هغه دوى ته زمونږ په واضحو نښو سره راغى، ناڅاپه دوى په هغو (نښو) پورې خندل');
INSERT INTO "abulsalam" VALUES(43,48,'43:48',' او مونږ به دوى ته هېڅ یوه نښه نه ښووله مګر چې هغه به د هغې له خور (مثل) نه ډېره لویه وه او مونږ دوى په عذاب سره راونیول، د دې لپاره چې دوى رجوع وكړي');
INSERT INTO "abulsalam" VALUES(43,49,'43:49',' او دوى (فرعونیانو) وویل: اى جادوګره! (عالمه!) ته زمونږ لپاره له خپل رب نه دعا وغواړه، د هغې وعدې په سبب چې تا سره يې كړې ده، بېشكه مونږ خامخا هدایت موندونكي یو');
INSERT INTO "abulsalam" VALUES(43,50,'43:50',' نو كله چې به مونږ له دوى نه عذاب لرې كړ، په هماغه وخت كې به دوى (وعده) ماتوله');
INSERT INTO "abulsalam" VALUES(43,51,'43:51',' او فرعون په خپل قوم كې اواز وكړ، ويې ويل:اى زما قومه! ايا د مصر باچايي زما نه ده؟ او دغه ولې چې زما له لاندې بهېږي؟ ایا نو تاسو نه وینئ');
INSERT INTO "abulsalam" VALUES(43,52,'43:52',' بلكې زه ډېر غوره یم له دغه (موسٰی) نه چې هغه سپك دى او نژدې نه دى چې خبره واضحه وكړي');
INSERT INTO "abulsalam" VALUES(43,53,'43:53',' پس په ده باندې د سرو زرو بنګړي (وښي) ولې نه غورځول كېږي، یا له ده سره ملايك صف تړلي (پیوسته) ولې) نه راځي');
INSERT INTO "abulsalam" VALUES(43,54,'43:54',' نو خپل قوم يې بې وقوفه وګرځاوه، نو دوى د ده خبره ومنله، بېشكه دوى فاسقان خلق وو');
INSERT INTO "abulsalam" VALUES(43,55,'43:55',' نو كله چې دوى مونږ خفه كړو (،نو) مونږ له دوى نه انتقام واخيست، پس مونږه دوى ټول غرق كړل');
INSERT INTO "abulsalam" VALUES(43,56,'43:56',' نومونږ دوى پیشوایان او مثال وګرځول د وروستنیو خلقو لپاره');
INSERT INTO "abulsalam" VALUES(43,57,'43:57',' او كله چې د مریمې زوى د مثال په طور بیان كړى شو، (نو) ناڅاپه ستا قوم د دغه (مثال) پورې (له خوشالۍ نه) چغې وهلې');
INSERT INTO "abulsalam" VALUES(43,58,'43:58',' او ويې ويل:ایا زمونږ معبودان غوره دي، یا كه هغه؟ دوى تا ته دا (مثال) بیان نه كړ مګر د جګړې لپاره بلكې دوى ډېر جګړه كوونكى قوم دى');
INSERT INTO "abulsalam" VALUES(43,59,'43:59',' دغه نه دى مګر یو بنده دى چې مونږ په ده انعام كړى دى او د بني اسرائیلو لپاره مو هغه (د قدرت) یو مثال ګرځولى و');
INSERT INTO "abulsalam" VALUES(43,60,'43:60',' او كه مونږ غوښتلي وى (نو) مونږ به ستاسو په ځاى په ځمكه كې ملايك پیدا كړي وى، چې خلیفګان شوي به وى');
INSERT INTO "abulsalam" VALUES(43,61,'43:61',' او بېشكه دغه (عیسٰی) د قیامت لپاره یقینًا یوه لویه نښه ده، نو تاسو د دغه (قیامت) په باره كې له سره شك مه كوئ، دا نېغه لار ده');
INSERT INTO "abulsalam" VALUES(43,62,'43:62',' او شیطان دې تا (له دې نېغې لارې نه) له سره وا نه ړوي، بېشكه هغه تاسو لپاره ښكاره دښمن دى');
INSERT INTO "abulsalam" VALUES(43,63,'43:63',' او كله چې عيسٰى له ښكاره دلیلونو سره راغى، ويې ويل:(اى بني اسرائیلو!) یقینًا ما تاسو ته حكم راوړى دى او د دې لپاره (راغلى یم) چې تاسو ته ځینې هغه (احكام) بیان كړم چې تاسو په هغو كې اختلاف كوئ، نو تاسو له الله نه ووېرېږئ او زما اطاعت وكړئ');
INSERT INTO "abulsalam" VALUES(43,64,'43:64',' بېشكه هم دا الله زما رب دى او ستاسو رب دى، لهٰذا تاسو د ده عبادت كوئ، دغه نېغه لاره ده');
INSERT INTO "abulsalam" VALUES(43,65,'43:65',' نو (د نصرانیانو) دغه ډلې په خپل منیځ كې سره مختلفې شوې، نو د هغو كسانو لپاره چې ظلم يې كړى دى، د ډېرې دردوونكي ورځې له عذاب نه هلاكت دى');
INSERT INTO "abulsalam" VALUES(43,66,'43:66',' دوى انتظار نه كوي مګر د قیامت چې په دوى به ناڅاپه راشي، او دوى به نه پوهېږي');
INSERT INTO "abulsalam" VALUES(43,67,'43:67',' په دغې ورځ كې به خواږه یاران، د دوى ځینې به د ځینو نورو دښمنان وي، غیر له متقیانو نه');
INSERT INTO "abulsalam" VALUES(43,68,'43:68',' اى زما بنده ګانو! نن ورځ په تاسو وېره نشته او نه به تاسو غمژن كېږئ');
INSERT INTO "abulsalam" VALUES(43,69,'43:69',' هغه كسان چې زمونږ په ایتونو يې ایمان راوړى دى او دوى مسلمانان وو');
INSERT INTO "abulsalam" VALUES(43,70,'43:70',' (دوى ته به ویلى شي:) تاسو جنت ته ننوځئ، تاسو او ستاسو ښځې، تاسو به خوشالولى شئ');
INSERT INTO "abulsalam" VALUES(43,71,'43:71',' په دوى به كاسې د سرو زرو او ګلاسونه ګرځول كېږي او په دغو (جنتونو) كې به هغه څه وي چې نفسونه (زړونه) د هغو خواهش كوي او سترګې (پرې) خوند اخلي او تاسو به په دغو كې تل ترتله یئ');
INSERT INTO "abulsalam" VALUES(43,72,'43:72',' او دغه هغه جنتونه دي چې تاسو ته د هغو عملونو په سبب په میراث كې دركړى شول چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(43,73,'43:73',' تاسو لپاره په دغو كې ډېرې مېوې دي چې تاسو به له هغو ځنې خوراك كوئ');
INSERT INTO "abulsalam" VALUES(43,74,'43:74',' بېشكه مجرمان به د جهنم په عذاب كې تل پاتې كېدونكي وي');
INSERT INTO "abulsalam" VALUES(43,75,'43:75',' چې دغه (عذاب) به له دوى څخه نه كمولى شي او دوى به په دغه (عذاب) كې نا امېده اوسي');
INSERT INTO "abulsalam" VALUES(43,76,'43:76',' او مونږ په دوى ظلم نه دى كړى او لېكن هم دوى پخپله ظالمان وو');
INSERT INTO "abulsalam" VALUES(43,77,'43:77',' او دغه (دوزخیان) به اواز وكړي: اى مالكه! ستا رب دې مونږ مړه كړي، هغه (مالك) به ووايي: بېشكه تاسو (تل) ژوندي پاتې كېدونكي یئ');
INSERT INTO "abulsalam" VALUES(43,78,'43:78',' یقینًا یقینًا مونږ تاسو ته حق راوړى و او لېكن ستاسو زیاتره حق لره بدګڼونكي دي');
INSERT INTO "abulsalam" VALUES(43,79,'43:79',' ایا بلكې دوى د یو كار پخه اراده كړې ده، نو بېشكه مونږ (هم) خپل (تدبیر) محكم كوونكي یو');
INSERT INTO "abulsalam" VALUES(43,80,'43:80',' ایا دوى دا ګمان كوي چې بېشكه مونږ د دوى پټې خبرې او پټې مشورې نه اورو، ولې نه! او زمونږ رسولان ليكل كوي د دوى ترڅنګه');
INSERT INTO "abulsalam" VALUES(43,81,'43:81',' ته (دوى ته) ووایه: كه چېرې د رحمٰن لپاره اولاد وى، نو زه به (د هغه) ترټولو اول عبادت كوونكى وم');
INSERT INTO "abulsalam" VALUES(43,82,'43:82',' پاكي ده د اسمانونو او ځمكې رب لره، د عرش رب لره له هغه څه نه چې دوى يې (د هغه په باره كې) بیانوي');
INSERT INTO "abulsalam" VALUES(43,83,'43:83',' نو ته دوى پرېږده چې (په باطلو خبرو كې) غوټې وهي او لوبې كوي تر هغه پورې چې دوى د خپلې هغې ورځې سره ملاقات وكړي چې دوى ته به يې وعده وركولى شوه');
INSERT INTO "abulsalam" VALUES(43,84,'43:84',' او هم دى هغه دى ذات چې په اسمان كې معبود دى او په ځمكه كې (هم) معبود دى، او هم دى ډېر حكمت والا، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(43,85,'43:85',' او ډېر بركت ناك دى هغه ذات چې خاص د هغه لپاره د اسمانونو او ځمكې باچايي ده او د هغه څه چې د دغو دواړو په مينځ كې دي او خاص له هغه سره د قیامت د علم دى او خاص هغه ته به تاسو بېرته بېولى شئ');
INSERT INTO "abulsalam" VALUES(43,86,'43:86',' او هغه معبودان چې دوى يې له دغه (الله) نه غیر بلي؛ د شفاعت هېڅ اختیار نه لري، مګر هغه څوك چې د حق ګواهي يې وركړې وي، په دې حال كې چې دوى پوهېږي');
INSERT INTO "abulsalam" VALUES(43,87,'43:87',' او قسم دى كه ته له دوى نه تپوس وكړې چې دوى چا پیدا كړي دي (نو) دوى به خامخا ضرور ووايي: الله (پیدا كړي دي) نو كوم طرف ته دوى ګرځول كېږي؟');
INSERT INTO "abulsalam" VALUES(43,88,'43:88',' او قسم دى د دغه (رسول) په دې وینا چې اى زما ربه! بېشكه زما دا قوم ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(43,89,'43:89',' نو ته له دوى نه مخ وګرځوه او ووایه: سلام، پس ژر به دوى پوه شي');
INSERT INTO "abulsalam" VALUES(44,1,'44:1',' حا، ميم');
INSERT INTO "abulsalam" VALUES(44,2,'44:2',' قسم دى په بیانوونكي كتاب');
INSERT INTO "abulsalam" VALUES(44,3,'44:3',' بېشكه مونږ دا (قرآن) په مباركه شپه كې نازل كړى دى، بېشكه مونږ وېروونكي وو');
INSERT INTO "abulsalam" VALUES(44,4,'44:4',' په دغې (شپه) كې هر له حكمته ډك كار بیانولى (او بېلولى) شي');
INSERT INTO "abulsalam" VALUES(44,5,'44:5',' زمونږ له جانبه د حكم وركولو په وجه (حكم وركول دي زمونږ له جانبه) بېشكه مونږ ( د رسولانو) لېږونكي وو');
INSERT INTO "abulsalam" VALUES(44,6,'44:6',' ستا د رب د رحمت لپاره، بېشكه هم دى ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(44,7,'44:7',' چې د اسمانونو او ځمكې رب دى او د هغه څه چې د دواړو په مینځ كې دي، كه تاسو یقین كوونكي يئ');
INSERT INTO "abulsalam" VALUES(44,8,'44:8',' نشته هېڅ یو حق معبود مګر هم دى دى، دى ژوندي كول كوي او مړه كول كوي، ستاسو رب دى او ستاسو د ړومبنیو پلرونو رب دى');
INSERT INTO "abulsalam" VALUES(44,9,'44:9',' بلكې دوى په شك كې دي، چې لوبې كوي');
INSERT INTO "abulsalam" VALUES(44,10,'44:10',' پس ته د هغې ورځې انتظار كوه چې اسمان ښكاره لوګى راوړي');
INSERT INTO "abulsalam" VALUES(44,11,'44:11',' چې خلق به پټ كړي دا ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(44,12,'44:12',' اى زمونږه ربه! ته زمونږ نه دغه عذاب لرې كړه، بېشكه مونږ ایمان راوړونكي یو');
INSERT INTO "abulsalam" VALUES(44,13,'44:13',' دوى ته به نصیحت اخيستل چېرته وي، حال دا چې دوى ته ښكاره بیانوونكى رسول راغلى و');
INSERT INTO "abulsalam" VALUES(44,14,'44:14',' بیا دوى له هغه نه مخ وګرځاوه او ويې ويل:(دا) تعلیم وركړى شوى دى، لېونى دى');
INSERT INTO "abulsalam" VALUES(44,15,'44:15',' بېشكه مونږ دغه عذاب لږ (وخت) لرې كوونكي یو، بېشكه تاسو بیا (كفر ته) راګرځېدونكي یئ');
INSERT INTO "abulsalam" VALUES(44,16,'44:16',' (یاده كړه) هغه ورځ چې مونږ نیول وكړو، تر ټولو غټ نیول، بېشكه مونږ انتقام اخيستونكي یو');
INSERT INTO "abulsalam" VALUES(44,17,'44:17',' او یقینًا یقینًا مونږ له دوى نه مخكې د فرعون قوم ازمایلى و او هغو ته ډېر عزتمند رسول راغلى و');
INSERT INTO "abulsalam" VALUES(44,18,'44:18',' چې تاسو ما ته د الله بنده ګان (بني اسرائیل) راوسپارئ، بېشكه زه تاسو لپاره امانتګر رسول یم');
INSERT INTO "abulsalam" VALUES(44,19,'44:19',' او دا چې تاسو په الله سركشي مه كوئ، بېشكه زه تاسو ته ښكاره دلیل راوړونكى یم ');
INSERT INTO "abulsalam" VALUES(44,20,'44:20',' او بېشكه ما پناه نیولې ده د خپل رب او ستاسو د رب، له دې نه چې تاسو ما سنګسار كړئ');
INSERT INTO "abulsalam" VALUES(44,21,'44:21',' او كه تاسو په ما ایمان نه راوړئ، بیا نو له ما نه په ډډه شئ');
INSERT INTO "abulsalam" VALUES(44,22,'44:22',' بیا هغه له خپل رب نه دعا وغوښته چې بېشكه دغه مجرمان خلق دى');
INSERT INTO "abulsalam" VALUES(44,23,'44:23',' نو (الله ورته وویل: چې) ته زما بنده ګان د شپې بوځه، بېشكه ستاسو به تعاقب كولى شي');
INSERT INTO "abulsalam" VALUES(44,24,'44:24',' او سمندر ولاړ (ساكن) پرېږده، بېشكه دوى دي ډوب كړى شوى لښكر');
INSERT INTO "abulsalam" VALUES(44,25,'44:25',' دوى څومره ډېر باغونه او چېنې پرېښودلې');
INSERT INTO "abulsalam" VALUES(44,26,'44:26',' او فصلونه او ډېر د عزت ځاى');
INSERT INTO "abulsalam" VALUES(44,27,'44:27',' او نور نعمتونه چې دوى به په هغو كې خوشاله وو');
INSERT INTO "abulsalam" VALUES(44,28,'44:28',' همداسې (مو دوى هلاك كړل) او دغه مو بل قوم ته په میراث وركړل');
INSERT INTO "abulsalam" VALUES(44,29,'44:29',' نو په دوى اسمان او ځمكې ونه ژړل او نه دوى مهلت وركړى شوي وو');
INSERT INTO "abulsalam" VALUES(44,30,'44:30',' او یقینًا یقینًا مونږ بني اسرائیل له سپكوونكي عذاب نه خلاص كړل');
INSERT INTO "abulsalam" VALUES(44,31,'44:31',' له فرعون نه، بېشكه هغه (فرعون) سركشه (او) له حده تېرېدونكو څخه و');
INSERT INTO "abulsalam" VALUES(44,32,'44:32',' او یقینًا یقینًا مونږ دغه (بني اسرائیل) د خپلې زمانې په خلقو باندې غوره كړي وو په علم سره ( په دې حال كې چې مونږ پرې عالمان وو ) ');
INSERT INTO "abulsalam" VALUES(44,33,'44:33',' او دوى ته مونږ هغه نښې وركړې وې، چې په هغو كې ښكاره ازمايش و');
INSERT INTO "abulsalam" VALUES(44,34,'44:34',' بېشكه دغه (كفار) خامخا وايي');
INSERT INTO "abulsalam" VALUES(44,35,'44:35',' نه دى دغه (عاقبت او د كار خاتمه) مګر زمونږ ړومبنى مرګ دى، او مونږ به له سره بیا راژوندي نه كړى شو');
INSERT INTO "abulsalam" VALUES(44,36,'44:36',' نو تاسو زمونږ پلرونو (ژوندي) راولئ! كه تاسو رښتیني یئ');
INSERT INTO "abulsalam" VALUES(44,37,'44:37',' ایا دوى (قریش) غوره دي، یا كه د تبع قوم او هغه كسان چې له دوى نه مخكې وو، مونږ هغوى هلاك كړل، بېشكه دوى مجرمان خلق وو');
INSERT INTO "abulsalam" VALUES(44,38,'44:38',' او مونږ اسمانونه او ځمكه او هر هغه څه چې د دواړو په مينځ كې دي ؛ نه دي پیدا كړي، په داسې حال كې چې عبث او لوبې كوونكي یو');
INSERT INTO "abulsalam" VALUES(44,39,'44:39',' مونږ دوى دواړه نه دي پیدا كړي مګر په حقه سره او لېكن د دوى اكثره (خلق) نه پوهېږي');
INSERT INTO "abulsalam" VALUES(44,40,'44:40',' بېشكه د فیصلې ورځ د دوى د ټولو ټاكلې وعده ده');
INSERT INTO "abulsalam" VALUES(44,41,'44:41',' هغه ورځ چې هېڅ یو دوست به د بل دوست هېڅ په كار رانشي او نه به له دوى سره مدد كولى شي');
INSERT INTO "abulsalam" VALUES(44,42,'44:42',' لېكن هغه كس چې الله پرې رحم وكړي، بېشكه دا (الله)، هم دى ښه غالب، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(44,43,'44:43',' بېشكه د زقوم ونه');
INSERT INTO "abulsalam" VALUES(44,44,'44:44',' د غټ ګناهګار خوراك دى');
INSERT INTO "abulsalam" VALUES(44,45,'44:45',' د ویلې كړى شویو مسو (د تېلو تورې خټې) په شان چې په ګېډو كې به اېشیږي ');
INSERT INTO "abulsalam" VALUES(44,46,'44:46',' د ګرمو اوبو د اېشېدلو په شان');
INSERT INTO "abulsalam" VALUES(44,47,'44:47',' تاسو دا ونیسئ، پس دى د جهنم بېخي مینځ له په سختۍ سره راكاږئ');
INSERT INTO "abulsalam" VALUES(44,48,'44:48',' بیا د ده د سر له پاسه د اېشېدلو اوبو له عذابه ورتوى كړئ');
INSERT INTO "abulsalam" VALUES(44,49,'44:49',' (ده ته به وویل شي چې دغه عذاب) وڅكه، بېشكه هم دا ته ډېر زبردست، ډېر عزتمن يې');
INSERT INTO "abulsalam" VALUES(44,50,'44:50',' بېشكه دا هغه (عذاب) دى چې تاسو به په ده كې شك كاوه');
INSERT INTO "abulsalam" VALUES(44,51,'44:51',' بېشكه متقیان به په امن ناكه ځاى كې وي');
INSERT INTO "abulsalam" VALUES(44,52,'44:52',' په جنتونو كې');
INSERT INTO "abulsalam" VALUES(44,53,'44:53',' دوى به له نریو ورېښمو او پېړو ورېښمو جامې اغوندي، په داسې حال كې چې سره مخامخ به ناست وي ');
INSERT INTO "abulsalam" VALUES(44,54,'44:54',' همداسې به وي او مونږ به دوى د غټ سترګو سپینو سرو حورو سره ملګري كړو');
INSERT INTO "abulsalam" VALUES(44,55,'44:55',' دوى به په دغو جنتونو كې هر قسمه مېوې راغواړي، په داسې حال كې چې په امن به وي');
INSERT INTO "abulsalam" VALUES(44,56,'44:56',' دوى به په دغو كې مرګ نه څكي غیر له ړومبني مرګ نه (چې په دنیا كې تېر شوى دى) او دوى به (الله) د دوزخ له عذاب نه بچ وساتي');
INSERT INTO "abulsalam" VALUES(44,57,'44:57',' د خپل رب د فضل په وجه، هم دغه ډېره لویه كامیابي ده');
INSERT INTO "abulsalam" VALUES(44,58,'44:58',' پس بېشكه هم دا خبره ده چې دا (قرآن) مونږ ستا په ژبه اسان كړى دى، د دې لپاره چې دوى نصیحت واخلي');
INSERT INTO "abulsalam" VALUES(44,59,'44:59',' نو ته انتظار كوه، بېشكه دوى هم انتظار كوونكي دي');
INSERT INTO "abulsalam" VALUES(45,1,'45:1',' حا، ميم');
INSERT INTO "abulsalam" VALUES(45,2,'45:2',' د كتاب نازلول د الله له جانبه دي چې ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(45,3,'45:3',' بېشكه په اسمانونو او ځمكه كې د مومنانو لپاره یقینًا ډېرې نښې دي');
INSERT INTO "abulsalam" VALUES(45,4,'45:4',' او ستاسو په پیدا كولو كې او په هغه څه كې چې ده هر قسمه خوځنده خواره كړي دي ؛ د هغه قوم لپاره نښې دي چې یقین كوي');
INSERT INTO "abulsalam" VALUES(45,5,'45:5',' او د شپې او ورځې په اختلاف كې او په هغه كې چې الله له اسمان (بره) نه رزق (باران) نازل كړى دى، بیا يې په هغه سره ځمكه د هغې له مرګ نه پس راژوندۍ كړې ده او د هواګانو په بدلولو رابدلولو كې د هغو خلقو لپاره نښې دي چې له عقل نه كار اخلي');
INSERT INTO "abulsalam" VALUES(45,6,'45:6',' دغه د الله دلیلونه دي چې مونږ يې په حقه سره په تا لولو، نو دوى به د الله او د هغه له ایتونو نه وروسته په كومې خبرې ایمان راوړي؟');
INSERT INTO "abulsalam" VALUES(45,7,'45:7',' د هر سخت دروغ تړونكي، سخت مجرم لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(45,8,'45:8',' د الله ایتونه اوري چې په ده لوستل كېږي، بیا ښه كلكېږي، په داسې حال كې چې تكبر كوونكى وي، ګویاكې ده دا (ایتونه) نه دي اورېدلي، نو ده ته د ډېر دردوونكي عذاب زېرى وركړه');
INSERT INTO "abulsalam" VALUES(45,9,'45:9',' او كله چې دى زمونږ له ایتونو نه څه معلوم كړي، نو هغه مسخره ونيسي، دغه كسان (چې دي) د دوى لپاره سپكوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(45,10,'45:10',' د دوى مخې ته جهنم دى او د دوى كړي عملونه به د دوى هېڅ په كار رانشي او نه هغه چې دوى له الله نه غیر كارسازان نیولي وو او د دوى لپاره ډېر لوى عذاب دى');
INSERT INTO "abulsalam" VALUES(45,11,'45:11',' دا (قرآن) هدایت دى او هغه كسان چې د خپل رب په ایتونو كافران شوي دي؛ د هغوى لپاره د ډېر دردوونكي عذاب سزا ده');
INSERT INTO "abulsalam" VALUES(45,12,'45:12',' الله هغه ذات دى چې تاسو لپاره يې سمندر تابع كړى دى، د دې لپاره چې په ده كې كشتۍ د ده په حكم سره روانې شي او د دې لپاره چې تاسو د هغه له فضله لټون وكړئ او د دې لپاره چې تاسو شكر وباسئ');
INSERT INTO "abulsalam" VALUES(45,13,'45:13',' او تاسو لپاره يې په كار لګولي دي هر هغه څه چې په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي، په داسې حال كې چې دا ټول د هغه له جانبه (مسخر) دي، بېشكه په دغو كې د هغه قوم لپاره یقینًا ډېرې نښې دي چې غور فكر كوي');
INSERT INTO "abulsalam" VALUES(45,14,'45:14',' (اى نبي!) ته هغو كسانو ته ووایه چې ایمان راوړى دى چې هغو كسانو ته دې بخښنه كوي چې د الله د ورځو (حوادثو) امېد نه لري، د دې لپاره چې دى (الله پخپله) یوې ډلې ته د هغوى د كړيو كارونو بدله وركړي');
INSERT INTO "abulsalam" VALUES(45,15,'45:15',' هر څوك چې نېك عمل وكړي، نو د خپل ځان لپاره يې دى، او څوك چې بد وكړي، نو په همده يې ضرر دى، بیا به خاص خپل رب ته تاسو بېرته بیول كېږئ');
INSERT INTO "abulsalam" VALUES(45,16,'45:16',' او یقینًا یقینًا مونږ بني اسرائیلو ته كتاب او حكم او نبوت وركړى و او دوى ته مو پاكیزه څیزونه روزي كړي وو او مونږ دوى ته (د خپلې زمانې) په خلقو فضیلت وركړى و');
INSERT INTO "abulsalam" VALUES(45,17,'45:17',' او مونږه دوى ته (د دين) په كار كې ښكاره دلیلونه وركړي وو، نو دوى اختلاف ونه كړ مګر وروسته له دې چې دوى ته علم راغى، د خپل مینځي ضد (او حسد) په وجه، بېشكه ستا رب به د قیامت په ورځ د دوى په مینځ كې په هغو خبرو كې فیصله كوي چې دوى به په هغو كې اختلاف كاوه');
INSERT INTO "abulsalam" VALUES(45,18,'45:18',' بیا مونږ ته (د دين) په معامله كې په واضحه لاره باندې ودرولې، نو ته د دې پیروي كوه او ته د هغو خلقو د خواهشاتو پیروي مه كوه چې نه پوهېږي');
INSERT INTO "abulsalam" VALUES(45,19,'45:19',' بېشكه دوى به د الله په مقابله كې ستا هېڅ په كار رانشي او بېشكه ظالمان (چې دي) د دوى ځینې د ځینو نورو دوستان دي او الله د پرهېزګارانو ملګرى دى ');
INSERT INTO "abulsalam" VALUES(45,20,'45:20',' دا (قرآن) د خلقو لپاره ښكاره دلیلونه دي او هدایت او رحمت دى د هغه قوم لپاره چې یقین كوي');
INSERT INTO "abulsalam" VALUES(45,21,'45:21',' ایا هغو كسانو چې جرمونه يې كړي دي؛ دا ګمان كړى چې مونږ به دوى د هغو كسانو په شان وګرځوو چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، په داسې حال كې چې د دوى ژوند او د دوى مرګ به یو برابر وي، بده ده هغه فیصله چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(45,22,'45:22',' او الله اسمانونه او ځمكه په حقه سره پیدا كړي دي او د دې لپاره چې هر نفس ته د هغه عمل بدله وركړى شي چې ده كړى دى، په داسې حال كې چې په دوى به ظلم نه كولى شي ');
INSERT INTO "abulsalam" VALUES(45,23,'45:23',' ایا نو تا هغه كس لیدلى دى چې خپل معبود يې خپل خواهش ګرځولى دى او الله هغه په علم باندې ګمراه كړ او د هغه په غوږونو او د هغه په زړه يې مهر ولګاوه، او د هغه په سترګو يې پرده واچوله، نو ده ته به له الله نه بعد څوك هدایت وكړي، ایا نو تاسو پند نه اخلئ!');
INSERT INTO "abulsalam" VALUES(45,24,'45:24',' او دوى وايي: دغه (ژوندون) نشته مګر زمونږ دنيايي ژوند چې مړه كېږو او ژوندي كېږو او مونږ نه هلاكوي مګر زمانه او دوى ته په دې خبره هېڅ علم نشته، دوى يواځې ګمانونه چلوي');
INSERT INTO "abulsalam" VALUES(45,25,'45:25',' او كله چې دوى ته زمونږ ایتونه تلاوت كولى شي، چې ښه واضح دي، (نو) د دوى دلیل نه وي مګر دا چې وايي: تاسو زمونږ (مړه) پلرونه راولئ كه تاسو رښتيني يئ');
INSERT INTO "abulsalam" VALUES(45,26,'45:26',' ته دوى ته ووایه: الله تاسو ژوندي كوي، بیا هغه تاسو مړه كوي بیا هغه تاسو د قیامت ورځې ته راجمع كوي چې په هغې كې هېڅ شك نشته او لېكن د خلقو اكثریت نه پوهېږي');
INSERT INTO "abulsalam" VALUES(45,27,'45:27',' او خاص الله لپاره د اسمانونو او ځمكې باچايي ده او په هغه ورځ كې چې قیامت به قايم شي، په دغه ورځ كې به باطل پرستان تاوانیان شي');
INSERT INTO "abulsalam" VALUES(45,28,'45:28',' او ته به هر امت په ځنګنونو پروت وینې، هر امت به خپل كتاب (اعمالنامې) ته راوبللى شي، نن ورځ به تاسو ته د هغو عملونو بدله دركولى شي چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(45,29,'45:29',' دا زمونږ كتاب دى چې پر تاسو باندې په حقه سره ګواهي كوي، بېشكه مونږ هغه عملونه لیكل چې تاسو به (په دنیا كې) كول');
INSERT INTO "abulsalam" VALUES(45,30,'45:30',' نو هر چې هغه كسان دي چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، نو خپل رب به دوى په خپل رحمت كې داخل كړي، هم دغه ښكاره كامیابي ده');
INSERT INTO "abulsalam" VALUES(45,31,'45:31',' او پاتې شو هغه كسان چې كافران شوي دي (نو دوى ته به وویل شي:) ایا نو تاسو ته زما ایتونه نه لوستل كېدل، بیا تاسو تكبر وكړ او تاسو مجرمان خلق وئ');
INSERT INTO "abulsalam" VALUES(45,32,'45:32',' او كله چې به (تاسو ته) وویل شو چې بېشكه د الله وعده حقه ده او قیامت (چې دى) په ده كې هېڅ شك نشته، تاسو به ویل: مونږ نه پوهېږو قیامت څه شى دى؟ مونږ خو بس یو ګمان كوو، ګمان كول او مونږ له سره یقین كوونكي نه یو');
INSERT INTO "abulsalam" VALUES(45,33,'45:33',' او دوى ته به د خپلو عملونو بدۍ ښكاره شي او دوى به ګېر كړي هغه (عذاب) چې دوى به په هغه پورې استهزا كولې');
INSERT INTO "abulsalam" VALUES(45,34,'45:34',' او وبه ویل شي نن ورځ مونږ تاسو هېروو، لكه چې تاسو د خپلې دې ورځې ملاقات هېر كړى و او ستاسو استوګنه اور دى او تاسو لپاره هیڅوك مدد كوونكي نشته');
INSERT INTO "abulsalam" VALUES(45,35,'45:35',' دا په دې سبب چې یقینًا تاسو د الله ایتونه مسخره ګرځولي وو او تاسو دنيايي ژوند غره (دوكه) كړي وئ ، نو نن ورځ به له دغه (دوزخ) نه دوى نه ایستل كېږي او نه به له دوى نه د توبې كولو مطالبه كېږي');
INSERT INTO "abulsalam" VALUES(45,36,'45:36',' نو ټول د كمال صفتونه خاص هغه الله لره دي چې د اسمانونو رب دى او د ځمكې رب دى، د عالمونو رب دى');
INSERT INTO "abulsalam" VALUES(45,37,'45:37',' او خاص ده لره په اسمانونو او ځمكه كې لويي ده او هم دى ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(46,1,'46:1',' حا، ميم');
INSERT INTO "abulsalam" VALUES(46,2,'46:2',' د كتاب نازلول د الله له جانبه دي چې ډېر غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(46,3,'46:3',' مونږ اسمانونه او ځمكه او هغه څه چې د دواړو په مینځ كې دي، نه دي پیدا كړي مګر په حقه او په ټاكلې مودې سره او كافران شوي كسان له هغه (قرآن) نه چې دوى پرې وېرولى شي، مخ ګرځوونكي دي');
INSERT INTO "abulsalam" VALUES(46,4,'46:4',' ته (دوى ته) ووایه: تاسو ما ته د هغو (معبودانو) خبر راكړئ چې تاسو يې له الله نه غیر بلئ، تاسو ما ته وښیئ دوى په ځمكه كې څه پیدا كړي دي؟ یا دوى لره په (پیدا كولو د) اسمانونو كې څه شراكت شته؟ تاسو ما ته له دغه (قرآن) نه مخكې كوم كتاب راوړئ، یا له علمه څه باقي علامه، كه تاسو رښتيني یئ');
INSERT INTO "abulsalam" VALUES(46,5,'46:5',' او له هغه كس نه زيات ګمراه څوك دى چې له الله نه غیر هغه (معبودان) بلي چې د ده دعا (سوال) تر قیامته پورې نشي قبلولى، په داسې حال كې چې دغه (معبودان) به د دوى له بلنې نه غافله (بې خبره) وي');
INSERT INTO "abulsalam" VALUES(46,6,'46:6',' او كله چې (ټول) خلق (د حساب لپاره په محشر كې) راجمع كړى شي دغه (معبودان) به د دوى دښمنان وګرځي او د دغو (عبادت كوونكو) له عبادت نه به منكر شي');
INSERT INTO "abulsalam" VALUES(46,7,'46:7',' او كله چې دوى ته زمونږ ایتونه لوستلى شي، په داسې حال كې چې ښه واضح دي (،نو) كافران شوي كسان د حق په باره كې وايي، كله چې دوى ته راغى: دا ښكاره جادو دى');
INSERT INTO "abulsalam" VALUES(46,8,'46:8',' بلكې ایا دوى وايي: ده دا له ځانه جوړ كړى دى، ته (دوى ته) ووایه: كه چېرې دا ما له ځانه جوړ كړى وي، نو تاسو زما لپاره له الله نه (د بچ كولو لپاره) د هېڅ شي اختیار نه لرئ، هغه په هغو خبرو ښه عالم دى چې تاسو په هغو كې لګیا يئ، هغه زما په مینځ كې او ستاسو په مینځ كې ښه كافي ګواه دى او همدغه ښه بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(46,9,'46:9',' ته (دوى ته) ووایه: زه په رسولانو كې اول (او نوى رسول) نه یم (بلكې له ما نه مخكې هم رسولان تېر شوي) او زه بېخي نه پوهېږم چې زما سره او تاسو سره به څه كولى شي، زه خو يواځې د هغه څه پیروي كوم چې ما ته وحي كولى شي او زه خو يواځې ښكاره وېروونكى یم');
INSERT INTO "abulsalam" VALUES(46,10,'46:10',' ته (دوى ته) ووايه: تاسو ما ته خبر راكړئ كه چېرې دا (قرآن) د الله له جانبه وي او تاسو په ده سره كافران شوي یئ او د بني اسرائیلو یو ګواه د دې په مثل سره ګواهي كړې ده، پس هغه ایمان راوړ او تاسو لويي وكړه، بېشكه الله ظالم قوم ته هدایت نه كوي');
INSERT INTO "abulsalam" VALUES(46,11,'46:11',' او كافران شویو كسانو د هغو كسانو په هكله وویل چې ایمان يې راوړى دى: كه چېرې دا (ایمان، قرآن) خیر (او غوره كار) وى، نو دوى به ده ته زمونږ نه مخكې شوي نه وو او چونكه دغو (كافرانو) په دغه (قرآن) سره نېغه لاره ونه موندله، نو ځكه دوى به ضرور وايي: دا ډېر پخواني دروغ دي');
INSERT INTO "abulsalam" VALUES(46,12,'46:12',' حال دا چې له ده نه مخكې د موسٰی كتاب و، چې لار ښوونكى او رحمت و او دا (قرآن) یو تصدیق كوونكى كتاب دى، په داسې حال كې چې په عربي ژبه دى، د دې لپاره چې هغه كسان ووېروي چې ظلم یې كړى دى او نېكي كوونكو لپاره زېرى دى');
INSERT INTO "abulsalam" VALUES(46,13,'46:13',' بېشكه هغه كسان چې وايي: زمونږ رب الله دى، بیا ټینګ ودرېږي، نو په دوى به نه څه وېره وي او نه به دوى غمګین كېږي');
INSERT INTO "abulsalam" VALUES(46,14,'46:14',' دغه كسان د جنت ملګري دي، چې په ده كې به تل ترتله وي، (دوى ته به) بدله وركولى شي د هغو (نېكو) عملونو په سبب چې دوى به كول');
INSERT INTO "abulsalam" VALUES(46,15,'46:15',' او مونږ انسان ته له خپل مور و پلار سره د نېكۍ كولو كلك حكم كړى دى، د ده مور دى د سختۍ په حالت كې (په خېټه كې) پورته كړى او دى يې د سختۍ په حالت كې زېږولى دى او د ده حمل او له تي نه د ده، پرېكولو (غوڅولو، د دواړو) موده دېرش میاشتې ده، تر هغه پورې چې كله دى خپل قوت ته ورسېده او څلوېښتو كالو ته ورسېده، ده وویل: اى زما ربه! ته ما ته (الهام او) توفیق راكړه چې زه ستا د هغه نعمت شكر ادا كړم چې تا په ما او زما په مور و پلار انعام كړى دى او دا چې زه داسې نېك عمل وكړم چې ته يې خوښوې او ته زما په اولاد كې صلاحیت (او نېكي) كېږده، بېشكه ما تا ته توبه كړې ده او بېشكه زه له مسلمانانو ځنې یم');
INSERT INTO "abulsalam" VALUES(46,16,'46:16',' دغه كسان دي چې مونږ له دوى نه د دوى په عملونو كې تر ټولو ښه قبلوو او د دوى له ګناهونو نه تېرېږو، په داسې حال كې چې د جنت په ملګرو كې به وي (الله كړې ده) هغه رښتینې وعده چې له دوى سره یې وعده كولى شوه');
INSERT INTO "abulsalam" VALUES(46,17,'46:17',' او هغه سړى چې خپل مور و پلار ته يې وویل: تاسو دواړو ته دې خرابي وي (زه له تاسو دواړو زړه تنګى یم) ایا تاسو ما ته دا وعده راكوئ چې زه به (ژوندى) راوېستلى كېږم، حال دا چې له ما نه مخكې ډېرې پېړۍ تېرې شوې دي او هغو دواړو الله ته فریاد كاوه (او ورته ویل يې:) تا لره هلاكت دى، ایمان راوړه! بېشكه د الله وعده حقه ده، نو دى (په جواب كې) وايي: دا نه دي مګر د ړومبنو خلقو باطلې قصې');
INSERT INTO "abulsalam" VALUES(46,18,'46:18',' دغه هغه كسان دي چې په دوى باندې (د عذاب) خبره ثابته شوې ده، په هغو امتونو كې چې له دوى نه مخكې تېر شوي دي له پېریانو او انسانانو ځنې، بېشكه دوى تاوانیان وو');
INSERT INTO "abulsalam" VALUES(46,19,'46:19',' او د هرې یوې (ډلې) لپاره د دوى د كړو عملونو په سبب (بېلې بېلې) مرتبې دي، او د دې لپاره چې دوى ته د دوى عملونه پوره وركړي، په داسې حال كې چې په دوى به ظلم ونه كړى شي ');
INSERT INTO "abulsalam" VALUES(46,20,'46:20',' او هغه ورځ (یاده كړه) چې كافران شوي كسان به اور ته پېش كولى شي (او ورته به وویل شي:) تاسو خپل خوندونه (او نعمتونه) په خپل دنيايي ژوند كې ختم كړي دي او تاسو له هغو نه فايده اخيستې ده، نو نن ورځ به تاسو ته د ذلت د عذاب بدله دركولى شي، په سبب د دې چې تاسو به په ځمكه كې ناحقه تكبر كاوه او په سبب د دې چې تاسو به نافرماني كوله');
INSERT INTO "abulsalam" VALUES(46,21,'46:21',' او ته د عادیانو ورور (هود) یاد كړه، كله چې ده خپل قوم ووېرولو، چې په احقاف (رېګستان) كې وو، او یقینًا له ده نه مخكې او له ده نه وروسته (نور) وېروونكي (پېغمبران) تېر شوي دي (په دې خبرې سره) چې تاسو عبادت مه كوئ غیر له الله نه، بېشكه زه په تاسو د ډېرې لويې ورځې د عذاب وېره كوم');
INSERT INTO "abulsalam" VALUES(46,22,'46:22',' دوى وویل: (اى هوده!) ایا ته مونږ ته د دې لپاره راغلى يې چې مونږ له خپلو معبودانو نه واړوې، پس ته پر مونږ هغه (عذاب) راوله چې ته يې مونږ ته وعده راكوې، كه ته له رښتینو ځنې يې');
INSERT INTO "abulsalam" VALUES(46,23,'46:23',' ده وویل: بېشكه همدا خبره ده چې (د عذاب د راتلو) علم خو له الله سره دى او زه تاسو ته هغه څه دررسوم چې په هغو سره زه رالېږل شوى یم او لېكن زه تاسو داسې قوم وینم چې له جهالت نه كار اخلئ');
INSERT INTO "abulsalam" VALUES(46,24,'46:24',' نو كله چې دوى هغه (عذاب) ولیده، چې د پلنې ورېځې په شكل كې د دوى میدانونو ته مخ كوونكى و، دوى وویل: دا پلنه ورېځ ده، پر مونږ باران وروونكې ده، (نو هود وویل) بلكې دا هغه (عذاب) دى چې تاسو د ده تلوار كاوه، باد دى چې په ده كې دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(46,25,'46:25',' چې د خپل رب په حكم سره هر شى هلاكوي، نو دوى سبا ته داسې شول چې يواځې د دوى (خالي) كورونه لیدل كېدل، مونږ همداسې مجرم قوم ته بدله وركوو');
INSERT INTO "abulsalam" VALUES(46,26,'46:26',' او یقینًا یقینًا مونږ هغوى ته په هغو څیزونو كې دومره قوت (او قدرت) وركړى و چې تاسو ته مو په هغو كې قدرت نه دى دركړى او د هغوى لپاره مونږ غوږونه او سترګې او زړونه پیدا كړي وو، نو له دوى نه د دوى غوږونو او نه د دوى سترګو او نه د دوى زړونو هېڅ شى (عذاب) دفع نه كړى شو، ځكه چې دوى د الله له ایتونو نه انكار كاوه او دوى هغه (عذاب) راګېر كړل چې دوى به په هغه پورې استهزا كولې');
INSERT INTO "abulsalam" VALUES(46,27,'46:27',' او یقینًا یقینًا مونږ هغه كلي چې ستاسو چاپېره دي، هلاك كړل او مونږه ایتونه مكرر بیان كړي دي، د دې لپاره چې دوى (له كفر نه) راوګرځي');
INSERT INTO "abulsalam" VALUES(46,28,'46:28',' نو د دوى مرسته هغو (معبودانو) ولې ونه كړه چې دغو (خلقو دوى) د تقرب لپاره له الله نه غیر معبودان ګرځولي وو، بلكې دغه (معبودان په دغه وخت كې) له دوى نه ورك شول او دغه د دوى غټ دروغ دي او هغه څه دي چې دوى به له ځانه جوړول');
INSERT INTO "abulsalam" VALUES(46,29,'46:29',' او هغه وخت (یاد كړه) چې مونږ تا ته د پېریانو یوه ډله راوګرځوله، چې قرآن به اوري، نو كله چې دوى ده (قرآن) ته حاضر شول، نو ويې ويل:چوپ شئ! نو كله چې (د قرآن تلاوت) تمام كړى شو (نو) دوى خپل قوم ته بېرته وګرځېدل، په داسې حال كې چې وېروونكي وو');
INSERT INTO "abulsalam" VALUES(46,30,'46:30',' دوى وویل: اى زمونږ قومه! بېشكه مونږ داسې كتاب واورېده چې له موسیٰ نه وروسته نازل كړى شوى دى، په داسې حال كې د هغو (كتابونو) تصدیق كوونكى دى چې له ده نه مخكې وو، حق ته او نېغې (سمې) لارې ته ښوونه كوي');
INSERT INTO "abulsalam" VALUES(46,31,'46:31',' اى زمونږ قومه! تاسو د الله د بلونكي وینا قبوله كړئ او په ده ایمان راوړئ، چې هغه (الله) تاسو ته ستاسو ځینې ګناهونه وبخښي او تاسو له ډېر دردوونكي عذاب نه خلاص (او بچ) كړي');
INSERT INTO "abulsalam" VALUES(46,32,'46:32',' او څوك چې د الله د بلونكي خبره قبوله نه كړي، نو دغه كس په ځمكه كې (الله لره) عاجزه كوونكى نه دى او د ده لپاره له دغه (الله) نه غیر هېڅ كارسازي (او مددګاران) نشته او دغه كسان په ښكاره ګمراهۍ كې دي');
INSERT INTO "abulsalam" VALUES(46,33,'46:33',' او ایا دوى نه دي كتلي چې بېشكه هغه الله چې اسمانونه او ځمكه يې پیدا كړي دي او د دغو په پیدا كولو سره نه دى ستړى شوى، یقینًا په دې قادر دى چې مړي ژوندي كړي؟ ولې نه! بېشكه هغه په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(46,34,'46:34',' او هغه ورځ (یاده كړه) چې كافران شوي كسان به اور ته وړاندې كولى شي (او ورته وبه ویل شي:) ایا دا حق (اورښتیا) نه دى، دوى به ووايي: ولې نه! زمونږ دې په خپل رب قسم وي! الله به ووايي: نو تاسو عذاب وڅَكئ، په سبب د هغه كفر چې تاسو به كاوه');
INSERT INTO "abulsalam" VALUES(46,35,'46:35',' نو (اى نبي!) ته صبر كوه، لكه چې د عزیمت والا رسولانو صبر كړى دى او د دوى په هكله (د عذاب) تلور مه كوه، ګویاكې دوى به هغه ورځ (كې) چې هغه (عذاب) وویني چې له دوى سره يې وعده كولى شوه، داسې وي چې دوى وخت نه دى تېر كړى مګر د ورځې یوه ګړۍ، (دا قرآن) پېغام دى، پس هلاك كولى نه شي مګر فاسقان خلق');
INSERT INTO "abulsalam" VALUES(47,1,'47:1',' هغه كسان چې كافران شوي دي او (خلق يې) د الله له لارې نه منع كړي دي؛ هغه (الله) د دوى (نېك) عملونه خراب (او ضايع) كړي دي');
INSERT INTO "abulsalam" VALUES(47,2,'47:2',' او هغه كسان چې ایمان يې راوړ او نېك عملونه وكړل او په هغه (قرآن) يې ایمان راوړ چې په محمد (صلى الله عليه وسلم) نازل كړى شوى دى او همدغه د دوى د رب له جانبه حق دى (نو) هغه (الله) به له دوى نه د دوى ګناهونه لرې كړي او د دوى حال به ښه كړي');
INSERT INTO "abulsalam" VALUES(47,3,'47:3',' دغه په دې سبب چې یقینًا كافران شویو كسانو د باطل پیروي كړې ده او یقینًا هغو كسانو چې ایمان يې راوړى دى د خپل رب له جانبه د حق پېروي كړې ده، همداسې الله خلقو ته د دوى مثالونه (او احوال) بیانوي');
INSERT INTO "abulsalam" VALUES(47,4,'47:4',' نو كله چې تاسو له كافران شویو خلقو سره (په جنګ كې) مخامخ شئ، نو (اول كار د دوى) د څټونو وهل دي، تر هغه پورې چې كله تاسو دوى ښه ډېر ووژنئ، نو بیا تړل ښه مضبوط كړئ (بیا رسۍ ښې مضبوطې وتړئ) بیا له دې نه بعد یا خو احسان كول دي (په دوى احسان وكړئ) او یا خو فدیه كول دي (د عوض په بدل كې يې ازاد كړئ) (له دوى سره جنګ كوئ) تر هغه پورې چې جنګ (والا) خپلې وسلې كېږدي، دغه (د الله حكم ) دى او كه الله غوښتلي وى (نو) له دوى نه به يې خامخا بدل اخيستى و او لېكن د دې لپاره (يې تاسو ته د جهاد حكم وكړ) چې ستاسو ځینې په ځینو نورو سره وازمايي، او هغه كسان چې د الله په لاره كې ووژل شي، نو هغه (الله) به د دوى عملونه له سره ضايع نه كړي');
INSERT INTO "abulsalam" VALUES(47,5,'47:5',' هغه (الله) به دوى ته خامخا لار ښوونه وكړي او د دوى حال به ښه كړي');
INSERT INTO "abulsalam" VALUES(47,6,'47:6',' او دوى به جنت ته داخل كړي چې هغه يې دوى ته ورښودلى دى');
INSERT INTO "abulsalam" VALUES(47,7,'47:7',' اى هغو كسانو چې ایمان يې راوړى دى! كه چېرې تاسو د الله مدد وكړئ، هغه به ستاسو مدد وكړئ او ستاسو قدمونه به ثابت (او ټینګ) كړي');
INSERT INTO "abulsalam" VALUES(47,8,'47:8',' او هغه كسان چې كافران شوي دي، نو د دوى لپاره هلاكوالى دى او هغه (الله) د دوى عملونه ضايع كړي دي');
INSERT INTO "abulsalam" VALUES(47,9,'47:9',' دا ځكه چې یقینًا دوى هغه (قرآن) بد ګڼلى دى چې الله نازل كړى دى، نو هغه (الله) د دوى عملونه برباد كړل');
INSERT INTO "abulsalam" VALUES(47,10,'47:10',' ایا نو دوى په ځمكه كې نه ګرځې، نو چې ووینې چې د هغو كسانو انجام څنګه و چې له دوى نه مخكې وو، الله په دوى تباهي راوسته، او د كافرانو لپاره د دغه (انجام) په مثل مثالونه شته');
INSERT INTO "abulsalam" VALUES(47,11,'47:11',' دغه ځكه چې یقینًا الله د هغو كسانو دوست (او مددګار) دى چې ایمان يې راوړى دى او دا چې یقینًا كافران (چې دي) د دوى لپاره مددګار (او دوست) نشته');
INSERT INTO "abulsalam" VALUES(47,12,'47:12',' بېشكه الله به هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، داسې جنتونه ته داخلوي چې د هغو له لاندې ولې بهېږي او هغه كسان چې كافران شوي دي، دوى فايده اخلي او خوراك كوي لكه څنګه چې څاروي خوراك كوي او اور د دوى استوګنه ده');
INSERT INTO "abulsalam" VALUES(47,13,'47:13',' او ډېر داسې كلي چې هغه ستا له هغه كلي نه چې ته يې اېستلى يې ؛ په قوت كې زیات سخت وو؛ مونږ هغوى هلاك كړل، نو د هغوى هیڅوك مددګار نه و');
INSERT INTO "abulsalam" VALUES(47,14,'47:14',' ایا نو هغه كس چې د خپل رب له جانبه په ښكاره دلیل (روان) وي د هغه كس په شان دى چې هغه ته خپل بد عمل ښايسته كړى شوى وي او دوى د خپلو خواهشاتو پیروي كړې وي');
INSERT INTO "abulsalam" VALUES(47,15,'47:15',' د هغه جنت حال (او صفت) چې پرهېزګارانو ته يې وعده وركړى شوې ده، په هغو كې د داسې اوبو ولې دي چې خرابېدونكې نه دي او د داسې شيدو (پيو) ولې دي چې خوند (او مزه) به يې تغیر نه مومي (نه خرابېږي) او د داسې شرابو ولې دي چې څښونكو ته مزه وركوونكي وي او د صفا كړى شویو شاتو ولې دي او د دوى لپاره په دغو (جنتونو) كې هر قسمه مېوې دي او د خپل رب له جانبه مغفرت دى (ایا دغه كس) د هغه چا په شان دى چې هغه په اور كې تل ترتله وي او په دوى ډېري ګرمې اوبه ورڅښولى شي، نو د دوى كولمې به ټوټې ټوټې كړي');
INSERT INTO "abulsalam" VALUES(47,16,'47:16',' او په دوى كې ځینې هغه دي چې تا ته غوږ ږدي تر دې چې كله ستا له خوا نه بهر ووځي، (نو) هغو كسانو ته ووايي چې علم ورته وركړى شوى دى: ده (محمدصلى الله عليه وسلم) اوس څه وویل؟ دغه هغه كسان دي چې الله د دوى په زړونو مهر لګولى دى او دوى د خپلو خواهشاتو پیروي كړې ده');
INSERT INTO "abulsalam" VALUES(47,17,'47:17',' او هغه كسان چې هدایت يې موندلى دى، هغه (الله) دوى ته هدایت ورزیات كړى دى او دوى ته يې د دوى پرهېزګاري وركړې ده');
INSERT INTO "abulsalam" VALUES(47,18,'47:18',' نو دوى انتظار نه كوي مګر د قیامت (یعنې) چې په دوى ناڅاپه راشي، نو یقینًا د ده نښې راغلې دي، نو د دوى لپاره به د دوى پند اخيستل څنګه (مفید) وي چې كله په دوى (قیامت) راشي');
INSERT INTO "abulsalam" VALUES(47,19,'47:19',' نو ته پوه شه چې بېشكه شان دا دى چې د عبادت هېڅ لايق نشته مګر الله دى او د خپلې ګناه مغفرت وغواړه او د مومنو سړیو او مومنو ښځو لپاره هم، او الله ستاسو په ګرځېدنځى او اوسېدنځى عالِم دى ');
INSERT INTO "abulsalam" VALUES(47,20,'47:20',' او هغه كس چې ایمان يې راوړى دى، وايي: ولې یو (داسې) سورت نه نازلول كېږي (چې د جهاد حكم په كې وي)، نو كله چې یو محكم سورت نازل كړى شي او په هغه كې جنګ یاد كړى شوى وي (نو) ته به هغه كسان وینې چې د هغوى په زړونو كې (د نفاق) مرض دى، چې تا ته به ګوري د هغه كس د كتو په شان چې په هغه د مرګ له وجې بې هوشي راغلې وي، نو د دوى لپاره دې هلاكت وي');
INSERT INTO "abulsalam" VALUES(47,21,'47:21',' (دوى وايي: زمونږ كار) اطاعت دى او نېكه خبره كول دي، نو كله چې كار (د جهاد) فرض (او لازم) شي، نو كه دوى له الله سره صدق كړى وى (نو) یقینًا دا به د دوى لپاره ډېر غوره و');
INSERT INTO "abulsalam" VALUES(47,22,'47:22',' نو ایا تاسو، كه چېرې حاكمان شئ، دې ته نژدې يئ چې تاسو به په ځمكه كې فساد وكړئ او خپل قرابتونه (خپلویانې) به پرې كړئ');
INSERT INTO "abulsalam" VALUES(47,23,'47:23',' دا هغه كسان دي چې الله په دوى لعنت كړى دى، نو دوى يې كاڼه كړي دي او د دوى سترګې يې ړندې كړې دي');
INSERT INTO "abulsalam" VALUES(47,24,'47:24',' ایا نو دوى په قرآن كې فكر نه كوي، بلكې (د دوى) په زړونو د دغو تالې پرتې دي');
INSERT INTO "abulsalam" VALUES(47,25,'47:25',' بېشكه هغه كسان چې په خپلو شاګانو (له ایمانه) بېرته ګرځېدلي دي، وروسته له دې چې دوى ته هدایت ښكاره شو، دوى ته شیطان (خپل عملونه) ښايسته كړي دي او دوى ته يې مهلت ښكاره كړى دى');
INSERT INTO "abulsalam" VALUES(47,26,'47:26',' دغه په دې سبب چې یقینًا دوى هغو كسانو ته، چې دغه (قرآن) بد ګڼي چې الله نازل كړى دى، وویل: په ځینو كارونو كې به مونږ ژر ستاسو حكم ومنو او الله د دوى په پټو خبرو پوهېږي');
INSERT INTO "abulsalam" VALUES(47,27,'47:27',' نو څنګه حال به وي، كله چې ملايك د دوى روحونه قبض كوي، په داسې حال كې چې دغه (ملايك) به د دوى مخونه او د دوى شاګانې وهي');
INSERT INTO "abulsalam" VALUES(47,28,'47:28',' دغه په دې سبب چې یقینًا دوى د هغه شي متابعت وكړ چې الله يې ناراضه كړ او دوى د هغه رضامندي بده وګڼله، نو هغه د دوى عملونه برباد كړل');
INSERT INTO "abulsalam" VALUES(47,29,'47:29',' ایا هغه كسان چې د هغوى په زړونو كې (د نفاق) بېماري ده؛ دا ګمان كړى دى چې الله به د دوى پټې كينې له سره نه څرګندوي');
INSERT INTO "abulsalam" VALUES(47,30,'47:30',' او كه مونږ وغواړو (نو) تا ته به دوى خامخا وښیو، نو ته به دوى خامخا په خپلو خاصو نښو سره وپېژنې او ته به دوى خامخا ضرور د خبرو په اړولو كې وپېژنې او الله ستاسو په عملونو عالِم دى');
INSERT INTO "abulsalam" VALUES(47,31,'47:31',' او مونږ به تاسو خامخا ضرور ازمایو تر دې چې مونږ په تاسو كې جهاد كوونكي وپېژنو (ظاهر كړو) او صبر كوونكي او ستاسو خبرونه وازمایو');
INSERT INTO "abulsalam" VALUES(47,32,'47:32',' بېشكه هغه كسان چې كافران شوي دي او د الله له لارې نه منع شوي دي او د رسول مخالفت كوي، وروسته له دې چې دوى ته هدایت ښكاره شو، دوى به الله ته له سره هېڅ شى ضرر ونه رسولى شي او هغه به ضرور د دوى عملونه برباد كړي');
INSERT INTO "abulsalam" VALUES(47,33,'47:33',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د الله اطاعت كوئ او د رسول اطاعت كوئ او خپل عملونه مه تباه كوئ');
INSERT INTO "abulsalam" VALUES(47,34,'47:34',' بېشكه هغه كسان چې كافران شوي دي او د الله له لارې نه منع كېږي، بیا دوى په داسې حال كې مړه شي چې كافران دي، نو الله به دوى ته له سره بخښنه ونه كړي');
INSERT INTO "abulsalam" VALUES(47,35,'47:35',' نو تاسو سستي مه كوئ او سولې ته بلنه مه وركوئ، په داسې حال كې چې هم تاسو به غالب یئ، او الله له تاسو سره دى او هغه به ستاسو عملونه له سره ضايع (او كم) نه كړي');
INSERT INTO "abulsalam" VALUES(47,36,'47:36',' بېشكه هم دا خبره ده چې دنيايي ژوند خو بس لوبې او مشغولتیا ده او كه تاسو ایمان راوړئ او پرهېزګاره شئ (نو) هغه به تاسو ته ستاسو اجرونه دركړي او له تاسو نه به ستاسو مالونه ونه وغواړي');
INSERT INTO "abulsalam" VALUES(47,37,'47:37',' كه هغه له تاسو نه دغه (مال) وغواړي، پس په تاسو ښه كوشش وكړي (نو) تاسو به بخل وكړئ، او هغه (الله) به ستاسو پټې كينې راوباسي');
INSERT INTO "abulsalam" VALUES(47,38,'47:38',' خبردار شئ! تاسو اى دغو كسانو! تاسو رابللى شئ، د دې لپاره چې تاسو د الله په لاره كې مال ولګوئ، نو په تاسو كې ځینې بخل كوي او څوك چې بخل وكړي، نو یقینًا هم دا خبره ده چې هغه له خپل ځان نه (د خير په منع كولو سره) بخل كوي او هم دا الله غني (ښه بې پروا) دى او هم تاسو فقیران یئ او كه تاسو وګرځېدئ (نو) هغه به له تاسو نه غیر بل قوم په بدل كې راولي، بیا به هغوى ستاسو په شان نه وي');
INSERT INTO "abulsalam" VALUES(48,1,'48:1',' بېشكه مونږ تا ته فتح دركړې ده، ښكاره فتح');
INSERT INTO "abulsalam" VALUES(48,2,'48:2',' د دې لپاره چې الله تا ته ستا هغه ګناهونه وبخښي چې مخكې تېرې شوي دي او هغه چې وروسته دي او په تا باندې خپل نعمتونه پوره كړي او تا ته نېغه لاره وښيي');
INSERT INTO "abulsalam" VALUES(48,3,'48:3',' او د دې لپاره چې الله ستا مدد وكړي، زبردست مدد كول');
INSERT INTO "abulsalam" VALUES(48,4,'48:4',' دغه (الله) هغه ذات دى چې د مومنانو په زړونو كې يې سكون (اطمینان) نازل كړ، د دې لپاره چې دوى د خپل (مخكیني) ایمان سره د ایمان په لحاظ نور ورزیات شي او خاص الله لره د اسمانونو او ځمكې لښكرې دي او الله ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(48,5,'48:5',' د دې لپاره چې هغه مومنان سړي او مومنې ښځې داسې جنتونو ته داخل كړي چې د هغو له لاندې ولې بهېږي، چې دوى به په هغو كې تل ترتله وي او (د دې لپاره چې) هغه (الله) له دوى نه د دوى ګناهونه لرې كړي، او دغه د الله په نیز ډېره لویه كامیابي ده');
INSERT INTO "abulsalam" VALUES(48,6,'48:6',' او د دې لپاره چې منافقو سړیو او منافقو ښځو او مشركو سړیو او مشركو ښځو ته عذاب وركړي، هغه چې په الله باندې ګمان كوونكي دي، بدګمان، په دوى دې د بدۍ راګرځېدل وي، او الله په دوى باندې غضب كړى دى او په دوى يې لعنت كړى دى او د دوى لپاره يې جهنم تیار كړى دى او هغه د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(48,7,'48:7',' او خاص د الله لپاره د اسمانونو او ځمكې لښكرې دي او الله له ازله ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(48,8,'48:8',' بېشكه مونږ ته شاهد او زېرى وركوونكى او وېروونكى لېږلى يې');
INSERT INTO "abulsalam" VALUES(48,9,'48:9',' د دې لپاره چې تاسو (اى خلقو!) په الله او د هغه په رسول ایمان راوړئ او د هغه (الله) مدد وكړئ او د هغه تعظیم وكړئ او سبا او بېګا د هغه تسبیح بیان كړئ');
INSERT INTO "abulsalam" VALUES(48,10,'48:10',' بېشكه هغه كسان چې له تا سره بیعت كوي، بېشكه همدا خبره ده چې دوى خو له الله سره بیعت كوي، د الله لاس د دوى د لاسونو له پاسه دى، نو هغه چا چې (دا بیعت) مات كړ، نو بېشكه همدا خبره ده چې د خپل ځان په ضرر يې ماتوي او هغه څوك چې په هغه څه وفا وكړي چې په هغه يې له الله سره عهد كړى دى، نو ده ته به هغه خامخا ډېر لوى اجر وركړي');
INSERT INTO "abulsalam" VALUES(48,11,'48:11',' ژر ده چې تا ته په اَعرابو كې وروسته پاتې كړى شوي كسان ووايي: مونږ خپلو مالونو او خپلوكورنيو مشغول كړي وو، لهٰذا ته زمونږ لپاره بخښنه وغواړه، دوى په خپلو ژبو سره هغه خبرې وايي چې د دوى په زړونو كې هغه نشته، ته (دوى ته) ووایه: نو تاسو لپاره د الله (له عذاب) نه څوك د څه شي اختیار لرلى شي، كه هغه (الله) تاسو ته د ضرر رسولو اراده وكړي، یا تاسو ته د نفعې رسولو اراده وكړي، بلكې الله په هغو كارونو چې تاسو يې كوئ ؛ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(48,12,'48:12',' بلكې (اصل خبره دا ده چې) تاسو دا ګمان كړى و چې رسول او مومنان به خپلو كورنيو ته هېڅ كله هم بېرته له سره را ونه ګرځي او دغه خبره ستاسو په زړونو كې ښايسته كړى شوه او تاسو ګمان وكړ، بدګمان، او تاسو هلاكېدونكى قوم شوئ');
INSERT INTO "abulsalam" VALUES(48,13,'48:13',' او هر څوك چې په الله او د هغه په رسول ایمان رانه وړي، نو بېشكه مونږ د كافرانو لپاره لمبې وهونكى اور تیار كړى دى');
INSERT INTO "abulsalam" VALUES(48,14,'48:14',' او خاص د الله لپاره د اسمانونو او ځمكې باچايي ده، چا ته چې وغواړي بخښنه كوي او چا ته چې وغواړي عذاب وركوي او الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(48,15,'48:15',' ژر به وروسته پاتې كړى شوي كسان ووايي، كله چې تاسو غنیمتونو ته روان شئ، د دې لپاره چې هغه راونیسئ: تاسو مونږ پرېږدئ چې تاسو پسې روان شو، دوى غواړي چې د الله كلام بدل كړي، ته (دوى ته) ووایه: تاسو به زمونږ پسې له سره رانشئ، الله همداسې مخكې له دې نه فرمایلي دي: بیا به ژر چې دوى ووايي: بلكې تاسو زمونږ سره حسد كوئ (داسې نه ده) بلكې دوى نه پوهېدل مګر ډېر لږ څه');
INSERT INTO "abulsalam" VALUES(48,16,'48:16',' په اعرابو كې وروسته پاتې كړى شویو كسانو ته ووایه: ژر ده چې تاسو به له داسې قوم سره (د جنګ كولو) لپاره راوبللى شئ، چې د ډېر سخت جنګ والا دي، تاسو به له هغوى سره جنګ كوئ، یا به هغوى مسلمانېږي، نو كه تاسو اطاعت وكړئ، الله به تاسو ته ډېر ښه اجر دركړي او كه تاسو وګرځېدئ، لكه چې له دې نه مخكې ګرځېدلي وئ (نو) هغه به تاسو ته ډېر دردوونكى عذاب دركړي');
INSERT INTO "abulsalam" VALUES(48,17,'48:17',' نه په ړانده باندې څه ګناه شته او نه په ګوډ باندې څه ګناه شته او نه په بېمار باندې څه ګناه شته او څوك چې د الله او د هغه د رسول اطاعت وكړي (نو) هغه (الله) به دغه (كس) داسې جنتونو ته داخل كړې چې د هغو له لاندې ولې بهېږي او څوك چې (د الله له احكامو نه) مخ واړوي (،نو) هغه (الله) به ده ته ډېر دردوونكى عذاب وركړي');
INSERT INTO "abulsalam" VALUES(48,18,'48:18',' یقینًا یقینًا الله له مومنانو نه راضي شو كله چې دوى له تا سره د ونې لاندې بيعت كاوه، پس ده ته هغه څه معلوم وو چې د دوى په زړونو كې وو، بیا هغه په دوى اطمینان نازل كړ او دوى ته يې په عوض كې نژدې برى وركړ');
INSERT INTO "abulsalam" VALUES(48,19,'48:19',' او ډېر غنیمتونه چې دوى به هغه راحاصل كړي او الله له همېشه ډېر غالب، ښه حكمت والا دى ');
INSERT INTO "abulsalam" VALUES(48,20,'48:20',' الله له تاسو سره د ډېرو غنیموتونو وعده كړې ده چې تاسو به هغه واخلئ، پس دا (غنیمت) يې تاسو ته ژر دركړ او د خلقو لاسونه يې له تاسو نه بند كړل او د دې لپاره چې دا (كار) د مومنانو لپاره یو نښه شي او د دې لپاره چې هغه تاسو ته نېغه لار وښیي');
INSERT INTO "abulsalam" VALUES(48,21,'48:21',' او نور داسې (غنیمتونه) دي چې تاسو لا په هغو قادر شوي نه یئ، یقینًا الله هغه راګېر كړي دي او الله په هر شي باندې ښه قادر دى ');
INSERT INTO "abulsalam" VALUES(48,22,'48:22',' او كه كافران شویو كسانو له تاسو سره جنګ كړى وى (نو) دوى به خامخا شاګانې ګرځولې وى، بیا به دوى نه څوك كارساز موندلى و او نه كوم مددګار');
INSERT INTO "abulsalam" VALUES(48,23,'48:23',' الله هغه طریقه جاري كړې ده چې له دې نه مخكې تېره شوې ده او ته به د الله طریقې لره له سره څه تبدیلې ونه مومې');
INSERT INTO "abulsalam" VALUES(48,24,'48:24',' او دى همغه ذات دى چې له تاسو نه يې د هغوى لاسونه بند كړل او له هغوى نه يې ستاسو لاسونه بند كړل، د مكې په داخل كې، وروسته له دې چې تاسو يې پر هغوى غالب كړئ، او الله هغه څه لره چې تاسو يې كوئ ؛ ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(48,25,'48:25',' دوى هغه كسان دي چې كفر يې كړى دى، او تاسو يې له مسجد حرام نه منع كړي يئ او قربانۍ (د هدي څاروي هم)، په دې حال كې چې له دې نه بندې كړى شوي دي چې خپل د حلالې ځاى ته ورسېږي او كه چېرې څه مومن سړي او څه مومنې ښځې نه وى، چې تاسو دوى نه پېژندل، دا چې تاسو به دوى پايمال كړئ، بیا به تاسو ته د دوى په وجه ګناه ورسېږي (، نو د جنګ اجازت به يې دركړى و، خو در يې نه كړ) د دې لپاره چې الله په خپل رحمت كې هغه څوك داخل كړي چې يې غواړي، كه دغه (مسلمانان له كفارو نه) جدا شوي وى، نو مونږ به خامخا په دغو (د مكې په اوسېدونكو) كسانو كې كافران شویو خلقو ته ډېر دردوونكى عذاب وركړى و');
INSERT INTO "abulsalam" VALUES(48,26,'48:26',' كله چې كافران شویو خلقو په خپلو زړونو كې غیرت واچاوه، د جاهلیت غیرت، نو الله خپل اطمینان په خپل رسول او په مومنانو باندې نازل كړ او په دوى پورې يې د تقوىٰ خبره لازمه (ټینګه) كړه او دوى د دغې ډېر حقدار او د دغې اهل وو او الله همېشه په هر شي ښه پوه دى');
INSERT INTO "abulsalam" VALUES(48,27,'48:27',' یقینًا یقینًا الله خپل رسول ته خوب په حقه سره رښتیا كړ، چې تاسو به، كه الله وغواړي، مسجد حرام ته په امن او ارام سره ننوځئ، چې خپل سرونه به خروونكي یئ او كموونكي به یئ، په داسې حال كې چې تاسو به نه وېرېږئ، پس هغه ته معلوم دي هغه څه چې تاسو ته معلوم نه دي، نو له دغې نه مخكې يې نژدې برى عطا كړ');
INSERT INTO "abulsalam" VALUES(48,28,'48:28',' دى همغه ذات دى چې خپل رسول يې په هدایت او حق دین سره رالېږلى دى، د دې لپاره چې هغه (الله) دغه (دین) په ټولو دینونو باندې غالب كړي او هم الله كافي دى ښه ګواه');
INSERT INTO "abulsalam" VALUES(48,29,'48:29',' محمد د الله رسول دى او هغه كسان چې له ده سره دي، په كافرانو باندې ډېر سخت دي، په خپل مینځ كې ډېر مهرباني كوونكي دي، ته به دوى وینې چې ركوع كوونكي، سجده كوونكي دي، له الله نه فضل او رضامندي غواړي، د دوى (د دیندارۍ) نښې د دوى په مخونو كې دي د سجدو له اثر نه، دغه په تورات كې د دوى مثال دى او په انجیل كې د دوى مثال دى، لكه هغه فصل چې خپل تېغ راوباسي، بیا هغه قوي كړي، بیا هغه (فصل، تېغ) پېړ او كلك شي بیا دغه (فصل) په خپلو پنډیو (او بېخونو) ودرېږي، په داسې حال چې زمېنداران (كروندګر) په تعجب كې اچوي، د دې لپاره چې په دغو سره كافران په غصه كړي، الله له هغو كسانو سره چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، چې دغه (اصحابان) دي؛ د بخښنې او د ډېر لوى اجر وعده كړې ده');
INSERT INTO "abulsalam" VALUES(49,1,'49:1',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د الله او د هغه د رسول په مخكې مه وړاندې كېږئ، او له الله نه ووېرېږئ، بېشكه الله ښه اورېدونكى، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(49,2,'49:2',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو خپل اوازونه د نبي د اواز له پاسه مه پورته كوئ، او مه ده ته په اوچت اواز خبره كوئ، لكه ستاسو د ځینو له ځینو نورو سره د اواز اوچتولو په شان، له دې وجې چې ستاسو عملونه به برباد شي او تاسو به پوه نه وئ');
INSERT INTO "abulsalam" VALUES(49,3,'49:3',' بېشكه هغه كسان چې د الله د رسول په وړاندې خپل اوازونه ټیټوي ؛ دغه هغه كسان دي چې الله د دوى زړونه د تقویٰ (او پرهېزګارۍ) لپاره ازمایلي دي، د دوى لپاره بخښنه او ډېر لوى اجر دى');
INSERT INTO "abulsalam" VALUES(49,4,'49:4',' بېشكه هغه كسان چې تا ته له حجرو نه د باندې چغې وهي، د دوى اكثره عقل نه لري');
INSERT INTO "abulsalam" VALUES(49,5,'49:5',' او كه یقینًا دوى صبر كړى وى، تر دې چې ته دوى ته راوتلى وى (نو) یقینًا دا به د دوى لپاره ډېره غوره و: او الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(49,6,'49:6',' اى هغو كسانو چې ایمان يې راوړى دى! كه تاسو ته كوم فاسق (بدكار كس) خبر راوړي، نو تاسو ښه تحقیق كوئ، په دې چې تاسو به یو قوم ته په ناخبري سره تكلیف ورسوئ، بیا به تاسو په خپلو كړو باندې پښېمانه شئ');
INSERT INTO "abulsalam" VALUES(49,7,'49:7',' او تاسو پوه شئ چې یقینًا په تاسو كې د الله رسول موجود دى، كه هغه په ډېرو كارونو كې ستاسو پیروي كړې وى (نو) تاسو به په مشقت كې غورځېدلي وى او لېكن الله تاسو ته ایمان محبوب وګرځاوه او هغه يې ستاسو په زړونو كې ښايسته كړ او تاسو ته يې كفر او فسق او نافرماني كول ناخوښه وګرځول، هم دغه كسان په نېغه لار دي');
INSERT INTO "abulsalam" VALUES(49,8,'49:8',' د الله له جانبه د فضل او نعمت په وجه او الله ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(49,9,'49:9',' او كه په مومنانو كې دوه ډلې په خپل مینځ كې جنګ شروع كړي، نو تاسو د دغو دواړو په مینځ كې سوله (روغه) وكړئ، نو كه په دغو دواړو كې یوې په بلې باندې زیاتى (او ظلم) وكړ، نو تاسو له هغې ډلې سره وجنګېږئ، چې زیاتى كوي، تر دې چې هغه د الله حكم ته راوګرځي، نو كه هغه (له ظلم نه) راوګرځېدله، نو تاسو د دواړو په مينځ كې په انصاف سره سوله وكړئ او تاسو انصاف كوئ، بېشكه الله انصاف كوونكي خوښوي');
INSERT INTO "abulsalam" VALUES(49,10,'49:10',' بېشكه هم دا خبره ده چې مومنان خو سره وروڼه دي، نو تاسو د خپلو دواړو وروڼو په مينځ كې سوله كوئ او له الله نه ووېرېږئ، د دې لپاره چې په تاسو رحم وكړى شي');
INSERT INTO "abulsalam" VALUES(49,11,'49:11',' اى هغو كسانو چې ایمان يې راوړى دى! هېڅ قوم دې په بل قوم پورې مسخرې نه كوي، كېدى شي چې هغوى له ده نه ډېر غوره وي، او نه (دې) ښځې په نورو ښځو پورې (مسخرې كوي)، كېدى شي چې هغوى له دوى نه ډېرې غوره وي او تاسو د خپلو ځانونو عیب جويي مه كوئ او یو بل په بدو لقبونو (او نومونو) سره مه بلئ، له ایمان راوړو نه بعد د فسق نامه بده ده او چا چې توبه ونه وېستله، نو هم دغه كسان ظالمان دي');
INSERT INTO "abulsalam" VALUES(49,12,'49:12',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو له ډېرو (بدو) ګمانونو نه ځان وساتئ، بېشكه ځینې ګمانونه ګناه ده او پټ عیبونه مه لټوئ او ستاسو ځینې دې د ځینو نورو غیبت نه كوي، ایا په تاسو كې یو كس دا خوښوي چې د خپل ورور غوښه وخوري، په دې حال كې چې هغه مړى وي، نو تاسو دغه (خوراك) بدګڼئ او له الله نه ووېرېږئ، بېشكه الله ښه توبه قبلوونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(49,13,'49:13',' اى خلقو! بېشكه مونږ تاسو له يوه سړي او له یوې ښځې پیدا كړي یئ او مونږ تاسو څانګې او قبیلې ګرځولي یئ، د دې لپاره چې تاسو یو بل سره وپېژنئ، بېشكه د الله په نیز په تاسو كې ډېر عزتمن، ستاسو زیات پرهېزګار دى، بېشكه الله ښه پوه، ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(49,14,'49:14',' بانډېسیانو (صحرايي خلقو) وویل: مونږ ایمان راوړى دى، ته (دوى ته) ووایه: تاسو ایمان نه دى راوړى او لېكن تاسو ووايئ چې مونږ اسلام راوړى دى او ایمان لا تر اوسه ستاسو په زړونو كې نه دى ننوتى او كه تاسو د الله او د هغه د رسول اطاعت وكړئ، نو هغه (الله) به ستاسو له عملونو نه هېڅ شى كم نه كړي، بېشكه الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(49,15,'49:15',' بېشكه مومنان خو هغه كسان دي چې په الله او د هغه په رسول يې ایمان راوړى دى، بیا يې شك نه دى كړى او د الله په لاره كې يې په خپلو مالونو او خپلو ځانونو سره جهاد كړى دى، هم دغه كسان رښتیني دي');
INSERT INTO "abulsalam" VALUES(49,16,'49:16',' ته (دوى ته) ووایه: ایا تاسو پر خپل دین باندې الله خبروئ، حال دا چې الله پوهېږي په هر هغه څه چې په اسمانونو كې دي او په هر هغه څه چې په ځمكه كې دي، او الله په هر شي باندې ښه پوه دى');
INSERT INTO "abulsalam" VALUES(49,17,'49:17',' دوى په تا د دې احسان كوي چې اسلام يې راوړى دى، ته (ورته)، ووایه: تاسو په ما باندې د خپل اسلام راوړلو احسان مه كوي، بلكې الله پر تاسو باندې احسان كوي دا چې تاسو ته يې د ایمان راوړلو هدایت وكړ، كه تاسو رښتیني يئ');
INSERT INTO "abulsalam" VALUES(49,18,'49:18',' بېشكه الله د اسمانونو او ځمكې په غیبو پوهېږي او الله هغه څه لره چې تاسو يې كوئ، ښه ليدونكى دى');
INSERT INTO "abulsalam" VALUES(50,1,'50:1',' قٓ، قسم دى په قرآن مجید باندې (چې ته یقینًا پېغمبر يې خو دوى ايمان نه راوړي)');
INSERT INTO "abulsalam" VALUES(50,2,'50:2',' بلكې دوى له دې نه تعجب كوي چې دوى ته له همدوى نه یو وېروونكى راغى، نو كافرانو وویل: دا خو یو عجیب شى دى');
INSERT INTO "abulsalam" VALUES(50,3,'50:3',' ایا كله چې مونږ مړه شو او مونږ خاورې شو (بیا به هم ژوندي كېږو؟) دغه بېرته راتلل (له عقل نه) ډېر لرې دي');
INSERT INTO "abulsalam" VALUES(50,4,'50:4',' یقینًا مونږ په هغه څه خبر یو چې ځمكه يې د دوى (له وجود) نه كموي او زمونږ سره یو ښه یاد ساتونكى كتاب دى');
INSERT INTO "abulsalam" VALUES(50,5,'50:5',' بلكې دوى د حق تكذیب كړى دى كله چې دوى ته راغى، نو دوى په ګډو وډو خبرو (اضطراب اچوونكي كار) كې دي');
INSERT INTO "abulsalam" VALUES(50,6,'50:6',' ایا نو دوى د خپل ځان له پاسه اسمان ته نه دي كتلي چې څنګه مونږ هغه جوړ كړى دى او (څنګه) مونږ هغه ښايسته كړى دى، او په ده كې هېڅ چاودونه نشته');
INSERT INTO "abulsalam" VALUES(50,7,'50:7',' او ځمكې لره، مونږ هغه غوړولې ده او په دې كې مو پاخه غرونه غورځولي دي او مونږ په دې كې له هر قسمه ښكلې زرغونې نه زرغون كړي دي');
INSERT INTO "abulsalam" VALUES(50,8,'50:8',' د هر رجوع كوونكي بنده د لارښوونې او نصیحت لپاره');
INSERT INTO "abulsalam" VALUES(50,9,'50:9',' او مونږ له بره نه ډېرې بركتي اوبه نازلې كړې دي، نو په دغو (اوبو) سره مونږ باغونه او د فصل دانې رازرغونې كړې');
INSERT INTO "abulsalam" VALUES(50,10,'50:10',' او د كجورو ونې، چې اوږدې لوړې دي، په دې حال ګې چې هغو لره لاندې باندې وږي دي');
INSERT INTO "abulsalam" VALUES(50,11,'50:11',' د بنده ګانو د روزۍ لپاره او مونږ په دغو (اوبو) سره مړ ښار ژوندى كړى دى، همداسې (له قبرونو نه د مړیو) راوتل دي');
INSERT INTO "abulsalam" VALUES(50,12,'50:12',' له دوى نه مخكې د نوح قوم او د كوهي والاو او ثمودیانو تكذیب كړى دى');
INSERT INTO "abulsalam" VALUES(50,13,'50:13',' او عادیانو او فرعون او د لوط وروڼو');
INSERT INTO "abulsalam" VALUES(50,14,'50:14',' او د ځنګله والاو او د تبع قوم (د دوى) هر یو د رسولانو تكذیب كړى و، نو زما عذاب (پرې) ثابت شو');
INSERT INTO "abulsalam" VALUES(50,15,'50:15',' ایا نو مونږ په اول پیدا كولو باندې ستړي شوي یو، بلكې دوى له نوي پیدا كولو نه په شك كې دي');
INSERT INTO "abulsalam" VALUES(50,16,'50:16',' او یقینًا یقینًا مونږه دا انسان پیدا كړى دى، په داسې حال كې چې مونږ په هغه څه پوهېږو چې د ده نفس په هغه سره وسوسه (او خطره) كوي او مونږ ده ته (د ده) د ورمېږ له رګ نه زیات نژدې یو');
INSERT INTO "abulsalam" VALUES(50,17,'50:17',' (یاد كړه) هغه وخت چې دوه اخيستونكي اخلي، چې یو له ښي اړخ نه او (بل) له چپ اړخ نه ناست وي');
INSERT INTO "abulsalam" VALUES(50,18,'50:18',' (دغه بنده) هېڅ خبره نه كوي مګر له ده سره حاضر ساتونكى دى');
INSERT INTO "abulsalam" VALUES(50,19,'50:19',' او د مرګ بې خودۍ (سختۍ) حق راوړ، دغه (مرګ) هغه دى چې تا به له ده نه څنګ كاوه ');
INSERT INTO "abulsalam" VALUES(50,20,'50:20',' او په شپېلۍ كې به پوكى وكړى شي دغه د وعید (وېرولو) ورځ ده');
INSERT INTO "abulsalam" VALUES(50,21,'50:21',' او هر نفس به راځي، په داسې حال كې چې له ده سره به یو شړونكى او یوګواه وي');
INSERT INTO "abulsalam" VALUES(50,22,'50:22',' یقینًا یقینًا ته له دې (ورځې) نه په غفلت كې وې، نو مونږ له تا نه ستا پرده لرې كړه، نو ستا نظر نن ورځ ډېر تېز دى');
INSERT INTO "abulsalam" VALUES(50,23,'50:23',' او د ده ملګرى (مَلَك) به ووايي: دا دى هغه شى چې زما سره حاضر دى');
INSERT INTO "abulsalam" VALUES(50,24,'50:24',' (دواړو ملايكو ته به وویل شي:) هر ډېر عناد كوونكى كافر په دوزخ كې وغورځوئ');
INSERT INTO "abulsalam" VALUES(50,25,'50:25',' چې د خیر منع كوونكى، زیاتي كوونكى، (او) شك كوونكى دى');
INSERT INTO "abulsalam" VALUES(50,26,'50:26',' هغه چې له الله سره يې بل معبود مقرر كړى دى، نو تاسو دا په ډېر سخت عذاب كې وغورځوئ');
INSERT INTO "abulsalam" VALUES(50,27,'50:27',' د ده ملګرى (شیطان) به ووايي: اى زمونږه ربه! دا ما نه دى ګمراه كړى او لېكن دا پخپله په ډېرې لرې ګمراهۍ كې و');
INSERT INTO "abulsalam" VALUES(50,28,'50:28',' (الله به) ووايي: تاسو زما په وړاندې جګړه سره مه كوئ او یقینًا ما تاسو ته وعید (وېرول) مخكې درلېږلى و');
INSERT INTO "abulsalam" VALUES(50,29,'50:29',' زما په نیز (په دې باره كې) خبره نشي بدلېدى او زه په بنده ګانو هېچرې ظلم كوونكى نه یم');
INSERT INTO "abulsalam" VALUES(50,30,'50:30',' (یاده كړه) هغه ورځ چې مونږ به جهنم ته ووایو: ایا ته ډك شوې؟ او هغه به وايي: ایا نور هم زیاتي شته؟');
INSERT INTO "abulsalam" VALUES(50,31,'50:31',' او پرهېزګارو ته به جنت ښه رانژدې كړى شي، چې لرې به نه وي');
INSERT INTO "abulsalam" VALUES(50,32,'50:32',' دا هغه دي چې له تاسو سره به يې وعده كولى شوه، د هر ډېر رجوع كوونكي، د (حدودو) ساتونكي لپاره');
INSERT INTO "abulsalam" VALUES(50,33,'50:33',' د هغه چا لپاره چې له رحمان نه په نالیدلو وېرېږي او په رجوع كوونكي زړه سره راشي');
INSERT INTO "abulsalam" VALUES(50,34,'50:34',' (دوى ته به وویل شي:) تاسو دغه (جنت) ته په سلامتۍ سره ننوځئ، دا د همېشه والي ورځ ده');
INSERT INTO "abulsalam" VALUES(50,35,'50:35',' د دوى لپاره په دغه (جنت) كې هغه څه دي چې دوى يې غواړي او زمونږ سره نور زیات هم شته');
INSERT INTO "abulsalam" VALUES(50,36,'50:36',' او له دوى نه مخكې مونږ څومره پېړۍ هلاكې كړې دي، چې هغوى له دوى نه په قوت (او نیوكه) كې ډېر سخت (او قوي) وو، نو هغوى په ښارونو كې وګرځېدل چې ایا د تېښتې (او پناه) ځاى شته؟');
INSERT INTO "abulsalam" VALUES(50,37,'50:37',' بېشكه په دغو (كارونو) كې خامخا پند دى، هغه چا ته چې هغه لره زړه وي، یا (نصیحت ته) غوږ كېږدي، په داسې حال كې چې حاضر (او متوجه) وي');
INSERT INTO "abulsalam" VALUES(50,38,'50:38',' او یقینًا یقینًا مونږ اسمانونه او ځمكه او هغه څه چې د دواړو په مینځ كې دي، په شپږو ورځو كې پیدا كړي دي، او مونږ ته هېڅ ستوماني نه ده رارسېدلې');
INSERT INTO "abulsalam" VALUES(50,39,'50:39',' نو ته په هغو خبرو صبر كوه چې دوى يې وايي او د خپل رب له ستاينې سره تسبیح وایه له لمر ختلو نه مخكې او له لمر ډوبېدلو نه مخكې');
INSERT INTO "abulsalam" VALUES(50,40,'50:40',' او د شپې په څه حصه كې، پس د هغه تسبیح وایه او (د لمانځه) له سجدو نه وروسته هم تسبیح وایه');
INSERT INTO "abulsalam" VALUES(50,41,'50:41',' او ته په هغې ورځ غوږ كېږده چې اواز كوونكى به له نژدې ځاى نه اواز وكړي');
INSERT INTO "abulsalam" VALUES(50,42,'50:42',' په هغې ورځ چې دوى به په حقه سره سخته چغه اوري، دغه د (له قبرونو نه د) راوتلو ورځ ده');
INSERT INTO "abulsalam" VALUES(50,43,'50:43',' بېشكه هم دا مونږ ژوندي كول كوو او مړه كول كوو او خاص مونږ ته بېرته راګرځېدل دي');
INSERT INTO "abulsalam" VALUES(50,44,'50:44',' په هغه ورځ چې له دوى څخه به ځمكه وچوي، په داسې حال كې چې دوى به تلوار كوونكي وي، دغه راژوندي كول مونږ ته ډېر اسان دي');
INSERT INTO "abulsalam" VALUES(50,45,'50:45',' مونږ ښه پوه یو په هغو خبرو چې دوى يې وايي، او ته په دوى باندې هېڅ زور كوونكى نه يې، نو ته په قرآن سره هغه چا ته پند وركړه چې زما له وعید (او عذاب) نه وېرېږي');
INSERT INTO "abulsalam" VALUES(51,1,'51:1',' قسم دى په الوځوونكو بادونو، الوځول');
INSERT INTO "abulsalam" VALUES(51,2,'51:2',' بیا د (اوبو د) بار په اوچتوونكو ورېځو (قسم دى)');
INSERT INTO "abulsalam" VALUES(51,3,'51:3',' بیا په اسانۍ سره تلونكو كشتیو (قسم دى)');
INSERT INTO "abulsalam" VALUES(51,4,'51:4',' بیا د كار په تقسیموونكو ملايكو(قسم دى) ');
INSERT INTO "abulsalam" VALUES(51,5,'51:5',' چې بېشكه هغه څه چې له تاسو سره يې وعده كولى شي یقینًا رښتینې (وعده) ده');
INSERT INTO "abulsalam" VALUES(51,6,'51:6',' او بېشكه (د عمل) بدله خامخا واقع كېدونكې ده');
INSERT INTO "abulsalam" VALUES(51,7,'51:7',' قسم دى په اسمان چې د لارو والا دى');
INSERT INTO "abulsalam" VALUES(51,8,'51:8',' چې بېشكه تاسو یقینًا په ګده وډه (مختلفه) خبره كې يئ');
INSERT INTO "abulsalam" VALUES(51,9,'51:9',' ګرځولى شي له دغه (قرآن، نبي) نه هغه څوك چې (د الله په علم كې مخكې) ګرځول شوى دى');
INSERT INTO "abulsalam" VALUES(51,10,'51:10',' اټكل كوونكي دې هلاك كړى شي');
INSERT INTO "abulsalam" VALUES(51,11,'51:11',' هغه كسان چې دوى په ډېر غفلت كې دي، هېروونكي دي');
INSERT INTO "abulsalam" VALUES(51,12,'51:12',' دوى پوښتنه كوي چې د بدلې ورځ به كله وي؟');
INSERT INTO "abulsalam" VALUES(51,13,'51:13',' په هغې ورځ كې چې دوى به په اور سره په عذاب كولى شي');
INSERT INTO "abulsalam" VALUES(51,14,'51:14',' (وبه ویل شي!) تاسو خپل عذاب وڅَكئ، دا هغه (عذاب) دى چې تاسو به دا جلت غوښته');
INSERT INTO "abulsalam" VALUES(51,15,'51:15',' بېشكه متقیان (پرهېزګاران) به په جنتونو او چینو كې وي');
INSERT INTO "abulsalam" VALUES(51,16,'51:16',' په داسې حال كې چې هغه څه لره به نیوونكي وي چې دوى ته يې د دوى رب وركوي، بېشكه دوى له دې نه مخكې نېكي كوونكي وو');
INSERT INTO "abulsalam" VALUES(51,17,'51:17',' دوى به د شپې په لږه برخه كې ویده كېدل');
INSERT INTO "abulsalam" VALUES(51,18,'51:18',' او په چرګ بانګونو كې به دوى استغفار كاوه');
INSERT INTO "abulsalam" VALUES(51,19,'51:19',' او د دوى په مالونو كې به د سوالګري او د محروم (مسكین) حق و ');
INSERT INTO "abulsalam" VALUES(51,20,'51:20',' او په ځمكه كې د یقین كوونكو لپاره ډېرې نښې دي');
INSERT INTO "abulsalam" VALUES(51,21,'51:21',' او ستاسو په ځانونو كې هم، ایا نو تاسو نه وینئ');
INSERT INTO "abulsalam" VALUES(51,22,'51:22',' او په اسمان كې ستاسو روزي ده او هغه څه چې تاسو سره يې وعده كولى شي');
INSERT INTO "abulsalam" VALUES(51,23,'51:23',' نو د اسمان او ځمكې په رب مې قسم دى چې بېشكه دا (خبره) یقینًا حق ده، چې د هغې په شان ده چې تاسو خبرې كوئ');
INSERT INTO "abulsalam" VALUES(51,24,'51:24',' ایا تا ته د ابراهیم د عزتمنو مېلمنو خبر رارسېدلى دى');
INSERT INTO "abulsalam" VALUES(51,25,'51:25',' كله چې دوى په ده ورننوتل، نو ويې ويل:(مونږ په تا سلام اچوو) سلام اچول، ده وویل: (په تاسو دې) سلام (وي او په زړه كې يې تېر شول چې دا) نا اشنا خلق دي');
INSERT INTO "abulsalam" VALUES(51,26,'51:26',' نو په تېزۍ سره خپلې ښځې ته لاړ، پس یو (وریت كړى) چاغ سخى يې راوړ');
INSERT INTO "abulsalam" VALUES(51,27,'51:27',' نو دغه يې دوى ته ورنژدې كړ، ويې ويل:تاسو ولې نه خورئ!');
INSERT INTO "abulsalam" VALUES(51,28,'51:28',' نو له دوى نه يې په زړه كې وېره وموندله، دوى وویل: ته مه وېرېږه او دوى ته يې د ډېر پوه هلك زېرى وركړ');
INSERT INTO "abulsalam" VALUES(51,29,'51:29',' نو د ده ښځې په چغې كولو سره مخ ورواړوه، پس خپل مخ يې وواهه او ويې ويل:(زه) زړه شنډه بوډۍ (یم)');
INSERT INTO "abulsalam" VALUES(51,30,'51:30',' دوى وویل: همداسې ستا رب ویلي دي، بېشكه هم هغه ښه حكمت والا، ښه پوه دى');
INSERT INTO "abulsalam" VALUES(51,31,'51:31',' ده وویل: نو ستاسو څه كار (او مقصد) دى اى رالېږل شویو (ملايكو)؟ ');
INSERT INTO "abulsalam" VALUES(51,32,'51:32',' دوى وویل: بېشكه مونږ د یو مجرم قوم په طرف رالېږل شوي یو');
INSERT INTO "abulsalam" VALUES(51,33,'51:33',' د دې لپاره چې مونږ په دوى باندې د خټو كاڼي راوورو');
INSERT INTO "abulsalam" VALUES(51,34,'51:34',' چې ستا د رب په نیز له حده وتونكو لپاره نشان داره كړى شوي دي');
INSERT INTO "abulsalam" VALUES(51,35,'51:35',' نو مونږ هغه څوك راووېستل چې په دغه (كلي) كې له مومنانو ځنې وو');
INSERT INTO "abulsalam" VALUES(51,36,'51:36',' پس مونږ په دغه (كلي) كې د مسلمانانو له یو كور نه غیر ونه مونده');
INSERT INTO "abulsalam" VALUES(51,37,'51:37',' او مونږ په دغه (كلي) كې د هغو كسانو لپاره یوه لویه نښه پرېښوده چې د ډېر دردوونكي عذاب نه وېرېږي');
INSERT INTO "abulsalam" VALUES(51,38,'51:38',' او په (قصه د) موسٰى كې، كله چې مونږ هغه فرعون ته په ښكاره دلیل سره ولېږه');
INSERT INTO "abulsalam" VALUES(51,39,'51:39',' نو هغه (فرعون) د خپل قوت (اولښكر) سره مخ واړوه او ويې ويل:(دا موسٰى) كوډګر (جادوګر) دى، یا لېونى دى');
INSERT INTO "abulsalam" VALUES(51,40,'51:40',' نو مونږ هغه او د هغه لښكرې راونیولې، نو مونږه دوى ټول په دریاب كې وغورځول، په داسې حال كې چې هغه د ملامتۍ كار كوونكى و');
INSERT INTO "abulsalam" VALUES(51,41,'51:41',' او په عادیانو كې، كله چې مونږ په دوى باندې (له خير نه) شنډ باد راخوشې كړ');
INSERT INTO "abulsalam" VALUES(51,42,'51:42',' ده به هېڅ داسې شى نه پرېښوده چې دا به پرې راغلى و مګر خو هغه به يې وچ زوړ رژېدلي واښه (يا هډوكي) وګرځاوه');
INSERT INTO "abulsalam" VALUES(51,43,'51:43',' او په ثمودیانو كې، كله چې دوى ته وويل شو چې تاسو تر یو وخته پورې نفع واخلئ');
INSERT INTO "abulsalam" VALUES(51,44,'51:44',' نو دوى د خپل رب له حكم نه سركشي وكړه، نو دوى (د) تندر (عذاب) ونیول، په دې حال كې چې دوى كتل');
INSERT INTO "abulsalam" VALUES(51,45,'51:45',' بیا نه دوى د ودرېدلو طاقت لاره او نه دوى بدل اخيستونكي وو');
INSERT INTO "abulsalam" VALUES(51,46,'51:46',' او له دوى نه مخكې د نوح قوم لره (مونږ هلاك كړى و) بېشكه هغوى یو فاسق قوم و');
INSERT INTO "abulsalam" VALUES(51,47,'51:47',' او اسمان مونږ هغه په قوت سره جوړ كړى دى او بېشكه مونږ خامخا پراخوونكي/ پراخه قدرت لرونكي یو');
INSERT INTO "abulsalam" VALUES(51,48,'51:48',' او ځمكه، مونږ هغه غوړولې ده، نو (مونږ) ښه غوړوونكي یو');
INSERT INTO "abulsalam" VALUES(51,49,'51:49',' او له هر څیز څخه مونږ دوه قسمه جوړې (نر او ښځه) پیدا كړي دي، د دې لپاره چې تاسو پند واخلئ');
INSERT INTO "abulsalam" VALUES(51,50,'51:50',' نو تاسو الله ته وتښتئ، بېشكه زه تاسو لپاره د هغه له جانبه ښكاره وېروونكى یم');
INSERT INTO "abulsalam" VALUES(51,51,'51:51',' او تاسو له الله سره بل معبود مه شریكوئ، بېشكه زه تاسو لپاره د هغه له جانبه ښكاره وېروونكى یم');
INSERT INTO "abulsalam" VALUES(51,52,'51:52',' همداسې، هغو كسانو ته چې له دوى نه مخكې وو، هېڅ یو رسول نه و راغلى مګر دوى به ویل: (دا) جادوګر دى، یا لېونى دى');
INSERT INTO "abulsalam" VALUES(51,53,'51:53',' ایا دوى په دې (خبرې) سره یو بل ته وصیت كړى دى، بلكې دوى سركشه قوم دى');
INSERT INTO "abulsalam" VALUES(51,54,'51:54',' نو ته له دوى نه مخ واړوه، پس ته هېڅ ملامت كړى شوى نه يې');
INSERT INTO "abulsalam" VALUES(51,55,'51:55',' او ته نصیحت كوه، پس بېشكه نصیحت كول مومنانو ته نفع رسوي');
INSERT INTO "abulsalam" VALUES(51,56,'51:56',' او ما پېریان او انسانان نه دي پیدا كړي مګر د دې لپاره چې دوى زما عبادت وكړي');
INSERT INTO "abulsalam" VALUES(51,57,'51:57',' زه له دوى نه هېڅ رزق نه غواړم او نه دا غواړم چې دوى ما ته خوراك راكړي');
INSERT INTO "abulsalam" VALUES(51,58,'51:58',' بېشكه هم دغه الله ښه روزي وركوونكى، قوت والا، (او) ډېر مضبوط دى');
INSERT INTO "abulsalam" VALUES(51,59,'51:59',' د هغو كسانو لپاره چې ظلم يې كړى دى، یقینًا د دوى د ملګرو (د عذاب) د حصې په شان حصه ده، نو دوى دې په ما تلوار نه كوي');
INSERT INTO "abulsalam" VALUES(51,60,'51:60',' پس د كافران شویو خلقو لپاره هلاكت دى، د دوى له هغې ورځې نه چې دوى سره يې وعده كولى شي');
INSERT INTO "abulsalam" VALUES(52,1,'52:1',' قسم دى په طور غر');
INSERT INTO "abulsalam" VALUES(52,2,'52:2',' او په كتاب لیكل شوي');
INSERT INTO "abulsalam" VALUES(52,3,'52:3',' په پرانستل شوې پاڼه كې');
INSERT INTO "abulsalam" VALUES(52,4,'52:4',' او (قسم دى) په ودان كړى شوي كور');
INSERT INTO "abulsalam" VALUES(52,5,'52:5',' او په پورته كړى شوي چت (چې اسمان دى)');
INSERT INTO "abulsalam" VALUES(52,6,'52:6',' او په ډك (يا بل) كړى شوي سمندر ');
INSERT INTO "abulsalam" VALUES(52,7,'52:7',' بېشكه ستا د رب عذاب خامخا نازلېدونكى دى');
INSERT INTO "abulsalam" VALUES(52,8,'52:8',' هغه لره هېڅ دفع كوونكى نشته');
INSERT INTO "abulsalam" VALUES(52,9,'52:9',' هغه ورځ چې اسمان به خوځېږي، سخت خوځېدل');
INSERT INTO "abulsalam" VALUES(52,10,'52:10',' او غرونه به روان شي، سخت روانېدل');
INSERT INTO "abulsalam" VALUES(52,11,'52:11',' پس په دغې ورځ كې د تكذیب كوونكو لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(52,12,'52:12',' هغه كسان چې دوى په بېكاره خبرو (جوړولو) كې لوبې كوي');
INSERT INTO "abulsalam" VALUES(52,13,'52:13',' هغه ورځ چې دوى به د دوزخ اور ته ټېل وهلى شي، سخت ټېل وهل');
INSERT INTO "abulsalam" VALUES(52,14,'52:14',' دا هغه اور دى چې تاسو به دا دروغ ګاڼه');
INSERT INTO "abulsalam" VALUES(52,15,'52:15',' ایا نو دا سحر(جادو) دى، یا يې تاسو نه وینئ');
INSERT INTO "abulsalam" VALUES(52,16,'52:16',' ورځئ، ده ته ننوځئ، پس تاسو صبر وكړئ، یا صبر مه كوئ، پر تاسو باندې دغه (دواړه كارونه) برابر دي، بېشكه همدا خبره ده چې تاسو ته د هغو عملونو بدله دركولى شي چې تاسو به كول ');
INSERT INTO "abulsalam" VALUES(52,17,'52:17',' بېشكه پرهېزګاران به په جنتونو او نعمتونو كې وي');
INSERT INTO "abulsalam" VALUES(52,18,'52:18',' چې په هغو (نعمتونو) به خوشالېدونكي وي چې دوى ته خپل رب وركړي دي او خپل رب دوى د دوزخ له عذاب نه بچ ساتلي دي');
INSERT INTO "abulsalam" VALUES(52,19,'52:19',' (ورته ویلى به شي:) تاسو خورئ او څښئ په خوند سره، د هغو عملونو په بدل كې چې تاسو به كول ');
INSERT INTO "abulsalam" VALUES(52,20,'52:20',' په دې حال كې چې په قطار ایښودل شويو تختونو باندې به تكیه كوونكي وي او مونږ به د دوى نكاح د ښايسته غټ سترګو سپينو سرو حورو سره وكړو');
INSERT INTO "abulsalam" VALUES(52,21,'52:21',' او هغه كسان چې ایمان يې راوړى دى او د دوى اولاد (هم) په ایمان كې د دوى متابعت كړى وي، (نو) مونږ به په دوى پورې د دوى اولاد هم پیوست كړو او مونږ به دوى ته د دوى له عملونو نه هېڅ شى كم نه كړو، هر سړى به د خپلو كړو په بدل ګرو (ګاڼه) وي ');
INSERT INTO "abulsalam" VALUES(52,22,'52:22',' او مونږ به دوى ته ور زیاتې كړو ، نورې مېوې او هغه غوښې چې د دوى زړه يې غواړي');
INSERT INTO "abulsalam" VALUES(52,23,'52:23',' دوى به په دغه(جنت) كې یو له بله (د شرابو) پیالې راكاږي، نه به په دغو كې عبثې خبرې وي او نه ګناهګار كول');
INSERT INTO "abulsalam" VALUES(52,24,'52:24',' او د دوى چاپېره به د دوى غلامان ګرځي، ګویا كې دغه (غلامان) پټې ساتل شوې ملغلرې دي');
INSERT INTO "abulsalam" VALUES(52,25,'52:25',' او د دوى ځینې به ځینو نورو ته مخ راواړوي، چې یو له بله به پوښتنې كوي');
INSERT INTO "abulsalam" VALUES(52,26,'52:26',' دوى به ووايي چې بېشكه مونږ له دې نه مخكې په خپل اهل كې وېرېدونكي وو');
INSERT INTO "abulsalam" VALUES(52,27,'52:27',' نو الله پر مونږ احسان وكړ او مونږ يې د تودوخي (ګرمباد) له عذاب نه وساتلو');
INSERT INTO "abulsalam" VALUES(52,28,'52:28',' بېشكه مونږ له دې نه مخكې هم دغه (الله) باله، بېشكه هم دى ډېر احسان كوونكى ، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(52,29,'52:29',' نو ته (خلقو ته) نصیحت كوه، پس ته د خپل رب په نعمت سره نه كاهن يې او نه لېونى');
INSERT INTO "abulsalam" VALUES(52,30,'52:30',' ایا دوى وايي چې (دى) شاعر دى، مونږ د ده په حق كې د زمانې د حوادثو انتظار كوو');
INSERT INTO "abulsalam" VALUES(52,31,'52:31',' ته (دوى ته) ووایه: تاسو انتظار كوئ، پس بېشكه زه له تاسو سره له انتظار كوونكو ځنې یم');
INSERT INTO "abulsalam" VALUES(52,32,'52:32',' ایا دوى ته د دوى عقلونه په دې (كار) حكم كوي، یا دوى سركشه (شرخوښونكى) قوم دى');
INSERT INTO "abulsalam" VALUES(52,33,'52:33',' ایا دوى وايي چې دا (قرآن) ده له ځانه جوړ كړى دى (داسې نه ده) بلكې دوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(52,34,'52:34',' نو دوى دې د ده په شان خبره راوړي كه دوى رښتیني وي');
INSERT INTO "abulsalam" VALUES(52,35,'52:35',' ایا دوى بې له څه شي پیدا كړى شوي دي، یا هم دوى، پخپله پیدا كوونكي دي؟');
INSERT INTO "abulsalam" VALUES(52,36,'52:36',' ایا دوى اسمانونه او ځمكه پیدا كړي دي؟ بلكې دوى یقین نه كوي');
INSERT INTO "abulsalam" VALUES(52,37,'52:37',' ایا له دوى سره ستا د رب خزانې دي، یا هم دوى واكدران دي');
INSERT INTO "abulsalam" VALUES(52,38,'52:38',' ایا د دوى لپاره اندرپایه (پوړۍ) ده چې دوى په هغې باندې (خېژي او) اورېدل كوي، نو د دوى اورېدونكي دې ښكاره دلیل راوړي');
INSERT INTO "abulsalam" VALUES(52,39,'52:39',' ایا د هغه (الله) لپاره لوڼه دي او تاسو لپاره زامن دي؟');
INSERT INTO "abulsalam" VALUES(52,40,'52:40',' ایا ته له دوى نه څه مزدوري غواړې، پس دوى له تاوانه درانه كړى شوي دي');
INSERT INTO "abulsalam" VALUES(52,41,'52:41',' ایا له دوى سره غیب دى (وحي راغلې ده) نو دوى يې لیكي');
INSERT INTO "abulsalam" VALUES(52,42,'52:42',' ایا دوى د مكر كولو اراده لري، نو هغه كسان چې كافران شوي دي هم دوى مكر كړى شوي دي');
INSERT INTO "abulsalam" VALUES(52,43,'52:43',' ایا د دوى لپاره له الله نه غیر بل معبود شته؟ الله لره پاكي ده له هغه شي نه چې دوى يې ورسره شریكوي');
INSERT INTO "abulsalam" VALUES(52,44,'52:44',' او كه دوى یوه ټوټه له اسمانه رالوېدونكې وویني (، نو) وبه وايي (دا) قط په قط كړى شوې ورېځ ده');
INSERT INTO "abulsalam" VALUES(52,45,'52:45',' نو ته دوى پرېږده، تر هغه پورې چې دوى له خپلې هغې ورځې سره ملاقات وكړي چې دوى به په هغې كې بې خوده كولى شي');
INSERT INTO "abulsalam" VALUES(52,46,'52:46',' هغه ورځ چې له دوى نه به د دوى مكر هېڅ شى (عذاب) لرې نه كړى شي او نه به د دوى مدد كولى شی');
INSERT INTO "abulsalam" VALUES(52,47,'52:47',' او بېشكه د هغو كسانو لپاره چې ظلم (شرك) يې كړى دى له دغه (عذاب) نه مخكې عذاب دى او لېكن د دوى زیاتره خلق نه پوهېږي');
INSERT INTO "abulsalam" VALUES(52,48,'52:48',' نو ته د خپل رب حكم ته صبر كوه، پس بېشكه ته زمونږ د سترګو په وړاندې يې او ته د خپل رب له ستاينې سره تسبیح وایه كله چې ته پاڅېږې');
INSERT INTO "abulsalam" VALUES(52,49,'52:49',' او دشپې په ځینې حصه كې، نو ته د هغه پاكي بیانوه او له ستوریو نه وروسته (د شپې په اخري حصې كې ) هم');
INSERT INTO "abulsalam" VALUES(53,1,'53:1',' قسم دى په ستوریو كله چې پرېوځي');
INSERT INTO "abulsalam" VALUES(53,2,'53:2',' ستاسو ملګرى نه ګمراه شوى دى او نه (په عقيده كې) بې لارې شوى دى');
INSERT INTO "abulsalam" VALUES(53,3,'53:3',' او دى له خپل خواهش نه خبرې نه كوي');
INSERT INTO "abulsalam" VALUES(53,4,'53:4',' نه دى دا (قرآن) مګر وحي چې (ده ته) وحي كول شي');
INSERT INTO "abulsalam" VALUES(53,5,'53:5',' ده ته (دا) د ډېرو سختو قوتونو والا (جبریل) ښودلى دى');
INSERT INTO "abulsalam" VALUES(53,6,'53:6',' چې د طاقت والا دى، بیا هغه پورته لاړ');
INSERT INTO "abulsalam" VALUES(53,7,'53:7',' او هغه په تر ټولو لوړې غاړې كې و');
INSERT INTO "abulsalam" VALUES(53,8,'53:8',' بیا هغه (جبریل محمدصلى الله عليه وسلم ته) نژدې شو، پس ښه نژدې شو');
INSERT INTO "abulsalam" VALUES(53,9,'53:9',' نو د دوه لیندو په مسافت شو بلكې (له دې نه هم) ډېر نژدې');
INSERT INTO "abulsalam" VALUES(53,10,'53:10',' نو هغه (الله د جبریل په ژبه) خپل بنده ته وحي وكړه، هغه چې وحي يې وكړه');
INSERT INTO "abulsalam" VALUES(53,11,'53:11',' د ده (نبي) زړه غلط شوى نه دى په هغه څه كې چې ده ولیدل');
INSERT INTO "abulsalam" VALUES(53,12,'53:12',' ایا تاسو له ده سره په هغه څه كې جګړه كوئ چې دى يې ویني');
INSERT INTO "abulsalam" VALUES(53,13,'53:13',' او یقینًا یقینًا ده هغه (جبریل) په یو بل ځلې كوزېدو كې لیدلى دى');
INSERT INTO "abulsalam" VALUES(53,14,'53:14',' له سدرۃة المنتهٰى سره');
INSERT INTO "abulsalam" VALUES(53,15,'53:15',' دغې سره جنة ۃالماوىٰ دى');
INSERT INTO "abulsalam" VALUES(53,16,'53:16',' كله چې دغه سدرة (د بېرې ونه) هغه څه پټوله چې دا يې پټوله');
INSERT INTO "abulsalam" VALUES(53,17,'53:17',' سترګې نه كږې شوې او نه يې له حد نه تېرى وكړ');
INSERT INTO "abulsalam" VALUES(53,18,'53:18',' یقینًا یقینًا ده د خپل رب ډېرې لويې نښې ولیدلې');
INSERT INTO "abulsalam" VALUES(53,19,'53:19',' نو تاسو ما ته خبر راكړئ د لات او عُزّٰى');
INSERT INTO "abulsalam" VALUES(53,20,'53:20',' او منات دریم، د بېخي وروستني');
INSERT INTO "abulsalam" VALUES(53,21,'53:21',' ایا تاسو لپاره هلكان دي او د هغه لپاره جینكۍ');
INSERT INTO "abulsalam" VALUES(53,22,'53:22',' دغه په دې وخت كې ډېر ظالمانه تقسیم دى');
INSERT INTO "abulsalam" VALUES(53,23,'53:23',' دغه (بتان) نه دي مګر تش نومونه چې تاسو او ستاسو پلرونو دغه ايښي دي، الله په دغو هېڅ دلیل نه دى نازل كړى دوى پیروي نه كوي مګر د تش ګمان او د هغه شي چې نفسونه يې غواړي او یقینًا یقینًا دوى ته د خپل رب له جانبه هدایت راغلى دى');
INSERT INTO "abulsalam" VALUES(53,24,'53:24',' ایا د انسان لپاره هغه څه دي چې دى يې تمنا كوي');
INSERT INTO "abulsalam" VALUES(53,25,'53:25',' نو خاص د الله لپاره اخرت او دنیا دي');
INSERT INTO "abulsalam" VALUES(53,26,'53:26',' او په اسمانونو كې څومره ډېر ملايك دي چې د دوى سفارش هېڅ شى نفع نه وركوي مګر وروسته له دې چې الله اجازت وركړي هغه چا ته چې يې وغواړي او (د هغه په شفاعت) راضي شي');
INSERT INTO "abulsalam" VALUES(53,27,'53:27',' بېشكه هغه كسان چې په اخرت ایمان نه لري، خامخا دوى د ښځو په نومونو سره ملايك نوموي');
INSERT INTO "abulsalam" VALUES(53,28,'53:28',' حال دا چې دوى ته په دې هېڅ علم نشته، دوى يواځې د تش ګمان پیروي كوي او بېشكه ګمان له حقه هېڅ شى نشي دفع كولى');
INSERT INTO "abulsalam" VALUES(53,29,'53:29',' نو ته د هغه چا نه مخ واړوه چې زمونږ له ذكر نه يې مخ ګرځولى دى او يواځې دنيايي ژوندون غواړي');
INSERT INTO "abulsalam" VALUES(53,30,'53:30',' دا د دوى د علم انتها ده، بېشكه ستا رب په هغه چا ښه عالم دى چې د هغه (الله) له لارې نه ګمراه شوى دى او هغه ښه عالم دى په هغه چا چې هدایت يې موندلى دى');
INSERT INTO "abulsalam" VALUES(53,31,'53:31',' او خاص الله لره دي هغه څه چې په اسمانونو كې دي او هغه څه چې په ځمكه كې دي، د دې لپاره چې هغو كسانو ته چې بد يې كړې دي، د خپل عمل بدله وركړي او هغو كسانو ته (هم) ډېره ښه بدله وركړي چې نېكي يې كړې ده');
INSERT INTO "abulsalam" VALUES(53,32,'53:32',' هغه كسان چې له غټو ګناهونو او د بې حیایۍ له كارونو نه ځان ساتي، غیر له وړو وړو ګناهونه، بېشكه ستا رب د بخښنې وسیع كوونكى دى، هغه په تاسو ښه عالم دى، كله چې يې تاسو له ځمكې نه راپیدا كړئ او كله چې تاسو د خپلو میندو په خېټو كې پټ بچي وئ، نو تاسو د خپلو ځانونو تزكیه (پاكوالى) مه كوئ، هغه (الله) ښه عالم دى په هغه چا چې ډېر متقي دى');
INSERT INTO "abulsalam" VALUES(53,33,'53:33',' ایا نو تا هغه كس لیدلى چې مخ يې واړوه (له ایمانه)');
INSERT INTO "abulsalam" VALUES(53,34,'53:34',' او ډېر لږ (مال) يې وركړ او منع يې كړ (نور مال) ');
INSERT INTO "abulsalam" VALUES(53,35,'53:35',' ایا له هغه سره علم غیب دى، پس دى لیدل كوي');
INSERT INTO "abulsalam" VALUES(53,36,'53:36',' بلكې ایا دى په هغه څه نه دى خبر كړى شوى چې د موسٰى په صحیفو كې دي');
INSERT INTO "abulsalam" VALUES(53,37,'53:37',' او د ابراهیم هغه چې وفا يې كړې ده');
INSERT INTO "abulsalam" VALUES(53,38,'53:38',' چې هېڅ پورته كوونكى د بل بار نه پورته كوی');
INSERT INTO "abulsalam" VALUES(53,39,'53:39',' او دا چې د انسان لپاره نشته مګر هغه څه چې ده (يې) كوشش كړى دى');
INSERT INTO "abulsalam" VALUES(53,40,'53:40',' او دا چې بېشكه د ده كوشش به عنقریب وښوول شي');
INSERT INTO "abulsalam" VALUES(53,41,'53:41',' بیا به ده ته بدله وركړى شی، ډېره پوره بدله');
INSERT INTO "abulsalam" VALUES(53,42,'53:42',' او دا چې بېشكه ستا رب ته (د ټولو مخلوقاتو) ورګرځېدل دي');
INSERT INTO "abulsalam" VALUES(53,43,'53:43',' او دا چې بېشكه هم دغه (الله) (خلق) خندوي او ژړوي');
INSERT INTO "abulsalam" VALUES(53,44,'53:44',' او دا چې هم دغه (الله) مرګ وركوي او ژوند وركوي ');
INSERT INTO "abulsalam" VALUES(53,45,'53:45',' او دا چې بېشكه دغه (الله) دوه جوړې نر او ښځه پیدا كړې دي');
INSERT INTO "abulsalam" VALUES(53,46,'53:46',' له یوه څاڅكي (نطفې) نه، كله چې وڅڅول شي');
INSERT INTO "abulsalam" VALUES(53,47,'53:47',' او دا چې بېشكه د ده په ذمه دي (له دې مرګ نه پس) بل ځلې پیدا كول');
INSERT INTO "abulsalam" VALUES(53,48,'53:48',' او دا چې بېشكه همدغه (الله) غني كول كوي او خزانې وركوي');
INSERT INTO "abulsalam" VALUES(53,49,'53:49',' او دا چې همدغه (الله) د شِعرٰى ستوري رب (او مالك) دى');
INSERT INTO "abulsalam" VALUES(53,50,'53:50',' او دا چې بېشكه همده ړومبني عادیان هلاك كړې دي');
INSERT INTO "abulsalam" VALUES(53,51,'53:51',' او ثمودیان هم، نو باقي يې پرې نه ښودل');
INSERT INTO "abulsalam" VALUES(53,52,'53:52',' او له دوى نه مخكې يې د نوح قوم هلاك كړ، بېشكه دوى، هم دوى، ډېر ظالمان او ډېر سركشان وو');
INSERT INTO "abulsalam" VALUES(53,53,'53:53',' او اړول شوي كلي يې ښكته راوغورځول');
INSERT INTO "abulsalam" VALUES(53,54,'53:54',' نو دغه هغه شي پټ كړ چې دى يې پټ كړ');
INSERT INTO "abulsalam" VALUES(53,55,'53:55',' نو (اى انسانه!) ته د خپل رب په كومو نعمتونو كې شك كوې');
INSERT INTO "abulsalam" VALUES(53,56,'53:56',' دا (محمدصلى الله عليه وسلم) له ړومبنیو وېروونكو ځنې یو وېروونكى دى');
INSERT INTO "abulsalam" VALUES(53,57,'53:57',' قیامت ډېر نژدى راغى');
INSERT INTO "abulsalam" VALUES(53,58,'53:58',' دې (قيامت) لره له الله نه غیر هېڅ څرګندوونكى نشته');
INSERT INTO "abulsalam" VALUES(53,59,'53:59',' ایا نو تاسو له دې خبرې (قرآن) نه تعجب كوئ');
INSERT INTO "abulsalam" VALUES(53,60,'53:60',' او خاندئ او نه ژاړئ');
INSERT INTO "abulsalam" VALUES(53,61,'53:61',' او تاسو غافلان يئ (لوبې كوونكي يئ) ');
INSERT INTO "abulsalam" VALUES(53,62,'53:62',' نو تاسو الله ته سجده وكړئ او (د ده) عبادت وكړئ');
INSERT INTO "abulsalam" VALUES(54,1,'54:1',' قیامت ډېر رانژدې شو او سپوږمۍ وچاودله');
INSERT INTO "abulsalam" VALUES(54,2,'54:2',' او كه چېرې دوى كوم دلیل وویني (نو) اعراض كوي او وايي: دا خو راروان (قوي) جادو دى');
INSERT INTO "abulsalam" VALUES(54,3,'54:3',' او تكذیب كوي او د خپلو خواهشاتو پیروي كوي او هر كار قرار نیونكى دى');
INSERT INTO "abulsalam" VALUES(54,4,'54:4',' او یقینًا یقینًا دوى ته له خبرونو ځنې هغه راغلي دي چې په هغو كې (د دوى لپاره) زجر او سرزنش و');
INSERT INTO "abulsalam" VALUES(54,5,'54:5',' (دغه) ډېر كامل حكمت دى، پس (دوى ته) وېروونكي نفع نه رسوي');
INSERT INTO "abulsalam" VALUES(54,6,'54:6',' نو ته له دوى نه مخ وګرځوه (او یاده كړه) هغه ورځ چې بلونكى به یو ناخوښه، (نااشنا) شي ته بلنه وكړي');
INSERT INTO "abulsalam" VALUES(54,7,'54:7',' په دې حال كې چې د دوى سترګې به خړې خوارې وي، دوى به له قبرونو نه (داسې) راوځي ګویا كې دوى خواره واره ملخان دي');
INSERT INTO "abulsalam" VALUES(54,8,'54:8',' د بلونكي په طرف به تلوار كوونكي وي، كافران به وايي: دا ډېره سخته ورځ ده');
INSERT INTO "abulsalam" VALUES(54,9,'54:9',' له دوى نه مخكې د نوح قوم تكذیب كړى دى، نو دوى زمونږ د بنده (نوح) تكذیب وكړ او ويې ويل:(دا نوح) لېونى دى او (له تبلیغه) منع كړى شو (او سخت وځورول شو)');
INSERT INTO "abulsalam" VALUES(54,10,'54:10',' نو ده له خپل رب نه دعا وغوښته، داسې چې بېشكه زه مغلوب (او عاجز) یم، نو ته (زما) انتقام (او بدله) واخله');
INSERT INTO "abulsalam" VALUES(54,11,'54:11',' نو مونږ د اسمان دروازې په ډېرو راتویدونكو اوبو سره پرانستلې');
INSERT INTO "abulsalam" VALUES(54,12,'54:12',' او له ځمكې نه مو چینې روانې كړې، نو اوبه په هغه كار باندي سره یوځاى (او ګډې) شوې چې مقرر شوى و');
INSERT INTO "abulsalam" VALUES(54,13,'54:13',' او مونږ هغه (په داسې بېړۍ) سور كړ چې د پلنو تختو او پېړو میخونو والا وه');
INSERT INTO "abulsalam" VALUES(54,14,'54:14',' چې زمونږ د سترګو په وړاندې ګرځېدله (دا طوفان راغلى و) د هغه چا د بدلې (او امداد) لپاره چې بې قدري (او ناشكري) يې كړى شوې وه');
INSERT INTO "abulsalam" VALUES(54,15,'54:15',' او یقینًا یقینًا مونږ دغه (بېړۍ) یو دلیل پرېښوده، نو ایا څوك پند اخيستونكى شته؟');
INSERT INTO "abulsalam" VALUES(54,16,'54:16',' نو زما عذاب او زما وېرول څنګه وو؟');
INSERT INTO "abulsalam" VALUES(54,17,'54:17',' او یقینًا یقینًا مونږ دا قرآن (د یادولو او پند اخيستلو لپاره) اسان كړى دى، نو ایا څوك پند اخيستونكى شته؟');
INSERT INTO "abulsalam" VALUES(54,18,'54:18',' عادیانو تكذیب وكړ، نو زما عذاب او زما وېرول څنګه وو');
INSERT INTO "abulsalam" VALUES(54,19,'54:19',' بېشكه مونږ په دوى باندې ډېر سخت باد راخوشې كړ د ډېر سخت سپېره والي په ورځ كې ');
INSERT INTO "abulsalam" VALUES(54,20,'54:20',' دغه (باد) به خلق (له ژورو پټو كندو نه) داسې را ایستل ګویا كې دوى له بېخه راوتلو د كجورو د ونو تنې دي');
INSERT INTO "abulsalam" VALUES(54,21,'54:21',' نو زما عذاب او زما وېرول څنګه وو؟');
INSERT INTO "abulsalam" VALUES(54,22,'54:22',' او یقینًا یقینًا مونږ قرآن د پند اخيستلو لپاره اسان كړى دى، نو ایا څوك پند اخيستونكى شته ؟');
INSERT INTO "abulsalam" VALUES(54,23,'54:23',' ثمودیانو د وېروونكو (نبیانو) تكذيب وكړ');
INSERT INTO "abulsalam" VALUES(54,24,'54:24',' پس دوى وویل: ایا د يو داسې بشر چې زمونږ ځنې دى (او) یو دى، مونږ به د ده پیروي كوو؟ بېشكه مونږ به په دغه وخت كې خامخا په ګمراهۍ او لېونتوب كې یو');
INSERT INTO "abulsalam" VALUES(54,25,'54:25',' ایا زمونږ له مینځه په ده باندې وحي راوغورځولى شوه؟ (داسې نه ده) بلكې دى ډېر دروغجن دى، ډېر خود پسند (متكبر) دى');
INSERT INTO "abulsalam" VALUES(54,26,'54:26',' ژر به دوى سبا ته پوه شي چې ډېر دروغجن، ډېر تكبر كوونكى څوك دى');
INSERT INTO "abulsalam" VALUES(54,27,'54:27',' بېشكه مونږ د دوى د ازمېښت لپاره اوښې لره لېږونكي یو، نو ته دوى ته منتظر اوسه او ښه صبر كوه');
INSERT INTO "abulsalam" VALUES(54,28,'54:28',' او دوى ته خبر وركړه چې بېشكه اوبه د دوى په مینځ كې تقسیم دي هر وار د اوبو ته به حاضري كول شي');
INSERT INTO "abulsalam" VALUES(54,29,'54:29',' او دوى خپل ملګري ته اواز وكړ، نو هغه توره راواخيسته، پس د هغې لېنګي يې پرې كړل (بيا يې مړه كړه) ');
INSERT INTO "abulsalam" VALUES(54,30,'54:30',' نو زما عذاب او زما وېرول څنګه وو؟');
INSERT INTO "abulsalam" VALUES(54,31,'54:31',' بېشكه مونږ پر هغوى باندې یوه چغه راخوشې كړه، نو د شپول جوړوونكي د مات (او چوره) كړى شویو وښو په شان شول');
INSERT INTO "abulsalam" VALUES(54,32,'54:32',' او یقینًا یقینًا مونږ قرآن د پند اخيستلو لپاره اسان كړى دى، نو ایا څوك پند اخيستونكى شته؟');
INSERT INTO "abulsalam" VALUES(54,33,'54:33',' د لوط قوم د وېروونكو تكذیب وكړ');
INSERT INTO "abulsalam" VALUES(54,34,'54:34',' بېشكه مونږ پر هغوى باندې كاڼي ویشتونكى باد راخوشې كړ ــ د لوط له كورنۍ نه غیر، مونږ دوى ته په پېشلمي كې نجات وركړ');
INSERT INTO "abulsalam" VALUES(54,35,'54:35',' زمونږ له جانبه د مهربانۍ په وجه، همداسې مونږ هغه چا ته بدله وركوو چې شكر وباسي');
INSERT INTO "abulsalam" VALUES(54,36,'54:36',' او یقینًا یقینًا هغه (لوط) دوى زمونږ له نیولو نه وېرولي وو، پس دوى(د هغه) په وېرولو كې شك وكړ');
INSERT INTO "abulsalam" VALUES(54,37,'54:37',' او یقینًا یقینًا دوى له ده نه د ده د مېلمنو غوښتنه وكړه، نو مونږ د دوى سترګې (محوه) ړندې كړې، پس (دوى ته وویل شو چې) تاسو زما عذاب او زما وېرول وڅَكئ');
INSERT INTO "abulsalam" VALUES(54,38,'54:38',' او یقینًا یقینًا په دوى باندې سهار وختي ثابت (او قرار نیونكى) عذاب راغى');
INSERT INTO "abulsalam" VALUES(54,39,'54:39',' نو تاسو زما عذاب او زما وېرول وڅَكئ');
INSERT INTO "abulsalam" VALUES(54,40,'54:40',' او یقینًا یقینًا مونږ قرآن د پند اخيستلو لپاره اسان كړى دى، نو ایا څوك پند اخيستونكى شته؟');
INSERT INTO "abulsalam" VALUES(54,41,'54:41',' او یقینًا یقینًا ال فرعون ته وېروونكي راغلي وو');
INSERT INTO "abulsalam" VALUES(54,42,'54:42',' (نو) دوى زمونږ د ټولو دلیلونو (معجزو) تكذیب وكړ، نو مونږ دوى ونیول، د ډېر غالب او ښه ذات په نیولو سره');
INSERT INTO "abulsalam" VALUES(54,43,'54:43',' (اى عربو!) ايا ستاسو كفار له دغو (ړومبنو كفارو) نه ډېر غوره دي، یا تاسو لپاره په كتابونو كې خلاصى (راغلى) دى؟');
INSERT INTO "abulsalam" VALUES(54,44,'54:44',' یا دوى وايي چې مونږ بدله اخيستونكې ډله یو');
INSERT INTO "abulsalam" VALUES(54,45,'54:45',' دغه جمع (ډله) به یقینًا یقینًا ماته كړى شي او دوى به شاګانې وګرځوي');
INSERT INTO "abulsalam" VALUES(54,46,'54:46',' بلكې قیامت د دوى د وعدې ځاى دى او قیامت ډېر سخت او ډېر تریخ دى');
INSERT INTO "abulsalam" VALUES(54,47,'54:47',' بېشكه مجرمان په ګمراهۍ او لمبې وهونكي اور كې دي');
INSERT INTO "abulsalam" VALUES(54,48,'54:48',' په هغې ورځ كې چې دوى به په خپلو مخونو په اور كې راښكلى شي (او ویلى به شي چې) تاسو د دوزخ رسېدل وڅكئ');
INSERT INTO "abulsalam" VALUES(54,49,'54:49',' بېشكه مونږ هر شي لره، مونږ هغه په (ازلي) اندازې سره پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(54,50,'54:50',' او زمونږ امر نه دى مګر یو ځل دى، لكه د سترګو رپول');
INSERT INTO "abulsalam" VALUES(54,51,'54:51',' او یقینًا یقینًا مونږ ستاسو مشابه (او نظاير) هلاك كړي دي، نو ایا څوك پند اخيستونكى شته؟');
INSERT INTO "abulsalam" VALUES(54,52,'54:52',' او هر هغه شى (كار) چې دوى كړى دى، په كتابونو كې دى');
INSERT INTO "abulsalam" VALUES(54,53,'54:53',' او هر وړوكى او لوى (عمل) لیكل شوى دى');
INSERT INTO "abulsalam" VALUES(54,54,'54:54',' بېشكه متقیان به په باغونو او ويالو كې وي');
INSERT INTO "abulsalam" VALUES(54,55,'54:55',' د راستۍ په مجلس كې به وي، له لوى قدرت لرونكي بادشاه سره');
INSERT INTO "abulsalam" VALUES(55,1,'55:1',' ډېر زیات مهربان ذات');
INSERT INTO "abulsalam" VALUES(55,2,'55:2',' (دا) قرآن ښودلى دى');
INSERT INTO "abulsalam" VALUES(55,3,'55:3',' هغه انسان پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(55,4,'55:4',' ده ته يې بیان (او وینا) كول ښودلي دي');
INSERT INTO "abulsalam" VALUES(55,5,'55:5',' لمر او سپوږۍ په حساب سره (روان) دي');
INSERT INTO "abulsalam" VALUES(55,6,'55:6',' او ستوري / واښه او ونې دواړه سجدې كوي');
INSERT INTO "abulsalam" VALUES(55,7,'55:7',' او اسمان لره، دا هغه پورته (اوچت) كړى دى او تله يې اېښې (مقرر كړې) ده');
INSERT INTO "abulsalam" VALUES(55,8,'55:8',' (د دې لپاره) چې تاسو په تله كې تېرى ونه كړئ');
INSERT INTO "abulsalam" VALUES(55,9,'55:9',' او تاسو په انصاف سره وزن برابروئ او په تول كې كمى مه كوئ');
INSERT INTO "abulsalam" VALUES(55,10,'55:10',' او ځمكې لره، هغه دا د ژوندیو لپاره غوړولې ده');
INSERT INTO "abulsalam" VALUES(55,11,'55:11',' په دې كې مېوې دي او د پوښونو والا د كجورو ونې دي');
INSERT INTO "abulsalam" VALUES(55,12,'55:12',' او د وښو (بوسو) والا دانې دي او خوشبویه ګلان دي');
INSERT INTO "abulsalam" VALUES(55,13,'55:13',' نو (اى انسانانو او پېریانو!) تاسو دواړه د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ (او ترې منكرېږئ)');
INSERT INTO "abulsalam" VALUES(55,14,'55:14',' انسان يې د كودرې په شان له وچې كړنګېدونكې خټې نه پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(55,15,'55:15',' او جان (د پېریانو پلار) يې د اور له خالصې (بې لوګي) شغلې نه پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(55,16,'55:16',' نو تاسو (اى انسانانو او پېریانو) دواړه د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,17,'55:17',' (هغه د ژمي او د اوړي) د دواړو مشرقونو رب دى او (د ژمي او د اوړي) د دواړو مغربونو رب دى');
INSERT INTO "abulsalam" VALUES(55,18,'55:18',' نو تاسو (اى انسانانو او پېریانو) دواړه د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,19,'55:19',' هغه دوه سمندرونه بهولي دي، په دې حال كې چې یوله بله سره میلاوېږي ');
INSERT INTO "abulsalam" VALUES(55,20,'55:20',' د دواړو په مینځ كې یوه پرده ده، چې په یو بل باندې نه ورګډېږي');
INSERT INTO "abulsalam" VALUES(55,21,'55:21',' نو تاسو (اى انسانانو او پېریانو!) دواړه د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,22,'55:22',' له دواړو (د سمندرونو) نه ملغلرې او مرجان راوځي');
INSERT INTO "abulsalam" VALUES(55,23,'55:23',' نو تاسو (اى انسانانو او پېریانو) د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,24,'55:24',' او خاص هغه لره دي په سمندر كې بادبان پورته كړى شوې بېړۍ، لكه غرونه');
INSERT INTO "abulsalam" VALUES(55,25,'55:25',' نو تاسو (اى انسانانو او پېریانو!) دواړه د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,26,'55:26',' هر هغه څوك چې په دې (ځمكې) باندې دي؛ فاني كېدونكي دي');
INSERT INTO "abulsalam" VALUES(55,27,'55:27',' او باقي به پاتې شي ستا د رب مخ (ذات) چې د جلال او د عزت خاوند دى');
INSERT INTO "abulsalam" VALUES(55,28,'55:28',' نو (اى انسانانو او پېریانو! ) تاسو دواړه د خپل رب په نعمتونو كې كوم یو دروغ ګڼئ ؟');
INSERT INTO "abulsalam" VALUES(55,29,'55:29',' له ده نه سوال كوي هر هغه څوك چې په ا سمانونو او ځمكه كې دي، هر وخت هغه په یوكار كې دى');
INSERT INTO "abulsalam" VALUES(55,30,'55:30',' نو تاسو (اى انسانانو او پېریانو!) د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,31,'55:31',' ژر ده چې مونږ به تاسو ته وزګار شو، اى دوه درنو قبیلو (انسانانو او پېریانو!)');
INSERT INTO "abulsalam" VALUES(55,32,'55:32',' نو تاسو (اى انسانانو او پېریانو!) د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,33,'55:33',' اى د پېریانو او انسانانو ډلې! كه چېرې تاسو د دې طاقت لرئ چې تاسو د اسمانونو او ځمكې له غاړو نه بهر ووځئ، نو ووځئ او تاسو به بهر ونه وتلى شئ مګر په قوت (او زور) سره');
INSERT INTO "abulsalam" VALUES(55,34,'55:34',' نو (اى پېریانو او انسانانو!) تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,35,'55:35',' په تاسو دواړو (ډلو) باندې به د اور خالصې لمبې او لوګى درخوشې كړى شي، بیا به تاسو یوله بل سره مدد نشئ كولى');
INSERT INTO "abulsalam" VALUES(55,36,'55:36',' نو تاسو دواړه (اى پېریانو او انسانانو!) د خپل رب په نعمتونو كې له كوم كوم نه انكار كوئ؟');
INSERT INTO "abulsalam" VALUES(55,37,'55:37',' بیا چې كله اسمان څیرې شي، نو سور رنګه ګل به شي، لكه د سرې څرمنې');
INSERT INTO "abulsalam" VALUES(55,38,'55:38',' نو تاسو دواړه (اى پېریانو او انسانانو!) د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,39,'55:39',' نو په دغې ورځ كې به د خپلې ګناه په باره كې نه له انسان نه پوښتنه كولى شي او نه له پېري نه');
INSERT INTO "abulsalam" VALUES(55,40,'55:40',' نو (اى پېریانو او انسانانو!) تاسو دواړه د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,41,'55:41',' مجرمان به په خپلو نښو سره پېژندلى شي، نو (د مجرمانو) وچولي او قدمونه به ونیولى شي');
INSERT INTO "abulsalam" VALUES(55,42,'55:42',' نو (اى پېريانو او انسانانو!) تاسو د خپل رب په نعمتونو كې د كوم يوه تكذيب كوئ');
INSERT INTO "abulsalam" VALUES(55,43,'55:43',' دا هغه دوزخ دى چې مجرمانو (كافرانو) به دا دروغ ګاڼه');
INSERT INTO "abulsalam" VALUES(55,44,'55:44',' دوى به د دغه (دوزخ) په مینځ كې او د ډېرو ګرمو اوبو په مینځ كې ګرځي');
INSERT INTO "abulsalam" VALUES(55,45,'55:45',' نو (اى پېریانو او انسانانو!) تاسو دواړه د خپل رب په نعمتونو كې د كوم یوه انكار كوئ؟');
INSERT INTO "abulsalam" VALUES(55,46,'55:46',' او د هغه چا لپاره چې د خپل رب په واړندې له ودرېدلو نه وېرېږي، دوه جنتونه دي');
INSERT INTO "abulsalam" VALUES(55,47,'55:47',' نو تاسو (اى پېریانو او انسانانو!) د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ؟');
INSERT INTO "abulsalam" VALUES(55,48,'55:48',' (دغه جنتونه) د ډېرو ډېرو څانګو والا دي');
INSERT INTO "abulsalam" VALUES(55,49,'55:49',' نو تاسو (اى پېریانو او انسانانو!) د خپل رب په نعمتونو كې كوم كوم دروغ ګڼئ');
INSERT INTO "abulsalam" VALUES(55,50,'55:50',' په دغو دواړو (جنتونو) كې دوه چینې دي چې بهېږي');
INSERT INTO "abulsalam" VALUES(55,51,'55:51',' نو تاسو (اى پېریانو او انسانانو!) د خپل رب په نعمتونو كې له كوم یو نه انكار كوئ');
INSERT INTO "abulsalam" VALUES(55,52,'55:52',' په دغو (جنتونو) كې له هرې مېوې نه دوه دوه قسمه دي');
INSERT INTO "abulsalam" VALUES(55,53,'55:53',' نو تاسو (اى پېریانو او انسانانو!) د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,54,'55:54',' په داسې حال كې چې دوى به په داسې فرشونو باندې تكیه وهونكي وي چې د هغو استرونه به له پېړو ورېښمو څخه وي او د دواړو جنتونو مېوې به نژدې وي');
INSERT INTO "abulsalam" VALUES(55,55,'55:55',' نو تاسو (اى انسانانو او پېریانو!) د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,56,'55:56',' په دغو (جنتونو) كې به د سترګو رامنع كوونكې حورې وي، چې له دغو (جنتي خاوندانو) نه به مخكې دوى ته نه بل انسان لاس وروړى وي او نه پېري');
INSERT INTO "abulsalam" VALUES(55,57,'55:57',' نو تاسو (اى پېریانو او انسانانو!) د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,58,'55:58',' ګویا كې دغه (حورې) یاقوت لعل دي، یا مرجان دي');
INSERT INTO "abulsalam" VALUES(55,59,'55:59',' نو تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,60,'55:60',' د احسان بدل نه دى مګر احسان (او نېكي) كول');
INSERT INTO "abulsalam" VALUES(55,61,'55:61',' نو تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,62,'55:62',' او له دغو دواړو نه غیر دوه نور جنتونه دي ');
INSERT INTO "abulsalam" VALUES(55,63,'55:63',' نو تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,64,'55:64',' تك تور دي (له ډېر شین والي او خړوب والي نه)');
INSERT INTO "abulsalam" VALUES(55,65,'55:65',' نو تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,66,'55:66',' په دغو دواړو كې دوه جوش وهونكې چینې دي');
INSERT INTO "abulsalam" VALUES(55,67,'55:67','نو تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,68,'55:68',' په دغو دواړو كې مېوې او كجورې او انار دي');
INSERT INTO "abulsalam" VALUES(55,69,'55:69','نو تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,70,'55:70',' په دغو (جنتونو) كې ډېرې غوره (نېك سیرتې) ډېرې ښكلې حورې دي');
INSERT INTO "abulsalam" VALUES(55,71,'55:71',' نو تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,72,'55:72',' په خېمو كې محفوظې ساتلې شوې حورې به وي');
INSERT INTO "abulsalam" VALUES(55,73,'55:73',' نو تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,74,'55:74',' چې له دغو (جنتي خاوندانو) نه به مخكې دوى ته نه انسان لاس وروړى وي او نه پېري');
INSERT INTO "abulsalam" VALUES(55,75,'55:75',' نو تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,76,'55:76',' په داسې حال كې چې تكیه كوونكي به وي په شین رنګه بالښتونو او په ډېرو نفیسو ښكلو فرشونو باندې');
INSERT INTO "abulsalam" VALUES(55,77,'55:77','نو تاسو د خپل رب په نعمتونو كې د كوم یوه تكذیب كوئ');
INSERT INTO "abulsalam" VALUES(55,78,'55:78',' ډېر بركتي دى ستا د رب نوم، چې د جلال او د اكرام (عزت وركولو) والا دى');
INSERT INTO "abulsalam" VALUES(56,1,'56:1',' كله چې واقع كېدونكى (قیامت) واقع شي');
INSERT INTO "abulsalam" VALUES(56,2,'56:2',' د ده پېښېدو لره هېڅ دروغجنوونكى نشته');
INSERT INTO "abulsalam" VALUES(56,3,'56:3',' (دا) ښكته كوونكى، (او) اوچتوونكى دى');
INSERT INTO "abulsalam" VALUES(56,4,'56:4',' كله چې ځمكه وخوځولى شي، خوځول');
INSERT INTO "abulsalam" VALUES(56,5,'56:5',' او غرونه ورژول شي، رژول ');
INSERT INTO "abulsalam" VALUES(56,6,'56:6',' نو دغه (غرونه) به یوه خوره شوې دوړه شي');
INSERT INTO "abulsalam" VALUES(56,7,'56:7',' اوتاسو به درې قسمونه شئ');
INSERT INTO "abulsalam" VALUES(56,8,'56:8',' نو د ښي لاس والا (خلق)، څه (ښه) دي د ښې لاس والا خلق!');
INSERT INTO "abulsalam" VALUES(56,9,'56:9',' او د چپ لاس والا خلق، څه (بد) دي د چپ لاس والا خلق؟');
INSERT INTO "abulsalam" VALUES(56,10,'56:10',' او ړومبى كېدونكي (همدوى) ړومبى كېدونكي دي');
INSERT INTO "abulsalam" VALUES(56,11,'56:11',' هم دوى نژدې كړى شوي دي (الله ته)');
INSERT INTO "abulsalam" VALUES(56,12,'56:12',' د نعمتونو په جنتونو كې به وي');
INSERT INTO "abulsalam" VALUES(56,13,'56:13',' لویه ډله به د ړومبنو خلقو ځنې وي');
INSERT INTO "abulsalam" VALUES(56,14,'56:14',' او لږ به د وروستنو خلقو ځنې وي');
INSERT INTO "abulsalam" VALUES(56,15,'56:15',' په (زرو او ملغلرو سره) بُڼل شویو تختونو باندې به (ناست) وي');
INSERT INTO "abulsalam" VALUES(56,16,'56:16',' چې په دغو باندې به یو بل ته مخامخ تكیه وهونكي وي');
INSERT INTO "abulsalam" VALUES(56,17,'56:17',' په دوى باندې به (د خدمت لپاره) تل پاتې (همېشني) هلكان ګرځي');
INSERT INTO "abulsalam" VALUES(56,18,'56:18',' د ګلاسونو او كوزو سره او د جاري (بهېدونكو) شرابو پیالو سره');
INSERT INTO "abulsalam" VALUES(56,19,'56:19',' چې له دغو (شرابو) نه به نه د دوى سر خوږېږي او نه به بې خوده (نشه) كېږي ');
INSERT INTO "abulsalam" VALUES(56,20,'56:20',' او له هغه قسمه مېوو سره چې دوى يې خوښوي');
INSERT INTO "abulsalam" VALUES(56,21,'56:21',' او د مرغانو له هغه قسمه غوښو سره چې د دوى زړونه يې غواړي');
INSERT INTO "abulsalam" VALUES(56,22,'56:22',' او (د دوى لپاره) پیمخې غټ سترګې حورې دي');
INSERT INTO "abulsalam" VALUES(56,23,'56:23',' د پټو ساتل شویو ملغلرو په شان');
INSERT INTO "abulsalam" VALUES(56,24,'56:24',' د هغو عملونو د بدلې وركولو لپاره چې دوى به كول');
INSERT INTO "abulsalam" VALUES(56,25,'56:25',' دوى به په دغه (جنت) كې نه عبث خبرې اوري او نه د ګناه (خبرې)');
INSERT INTO "abulsalam" VALUES(56,26,'56:26',' لېكن سلام، سلام ویل (به اوري)');
INSERT INTO "abulsalam" VALUES(56,27,'56:27',' او د ښي اړخ والا، څه دي د ښي اړخ والا ');
INSERT INTO "abulsalam" VALUES(56,28,'56:28',' په بې اغزیو بېرو كې به وي');
INSERT INTO "abulsalam" VALUES(56,29,'56:29',' او له بېخه تر سره ډكو كړى شویو (د) كیلو (په ونو) كې به وی');
INSERT INTO "abulsalam" VALUES(56,30,'56:30',' او په اوږد كړى شویو همېشني سيورو كې به وي');
INSERT INTO "abulsalam" VALUES(56,31,'56:31',' او په راتوى كړى شویو اوبو كې به وي');
INSERT INTO "abulsalam" VALUES(56,32,'56:32',' اوپه ډېرو زیاتو مېوو كې به وي');
INSERT INTO "abulsalam" VALUES(56,33,'56:33',' چې نه به قطع كولى شي او نه به منع كولى شي');
INSERT INTO "abulsalam" VALUES(56,34,'56:34',' او په اوچت كړى شویو فرشونو كې به وي');
INSERT INTO "abulsalam" VALUES(56,35,'56:35',' بېشكه مونږ دغه (ښځې/حورې) پیدا كړې دي، خاص پیدا كول');
INSERT INTO "abulsalam" VALUES(56,36,'56:36',' نو مونږ دغه (حورې) پېغلې ګرځولې دي');
INSERT INTO "abulsalam" VALUES(56,37,'56:37',' په خپلو خاوندانو مینانې (او) همزولې');
INSERT INTO "abulsalam" VALUES(56,38,'56:38',' د ښي اړخ والاو لپاره');
INSERT INTO "abulsalam" VALUES(56,39,'56:39',' (د ښي اړخ والا به) لویه ډله وي له ړومبنو خلقو نه');
INSERT INTO "abulsalam" VALUES(56,40,'56:40',' او بله لویه ډله به له وروستنیو خلقو نه وي');
INSERT INTO "abulsalam" VALUES(56,41,'56:41',' او د چپ اړخ (ګس) والا، څه دي د چپ اړخ والا');
INSERT INTO "abulsalam" VALUES(56,42,'56:42',' (دوى به) په ګرم باد او ډېرو تودو اوبو كې وي');
INSERT INTO "abulsalam" VALUES(56,43,'56:43',' او د تك تور لوګي په سيوري كې به وي');
INSERT INTO "abulsalam" VALUES(56,44,'56:44',' چې نه به یخ وي او نه به د عزت وي');
INSERT INTO "abulsalam" VALUES(56,45,'56:45',' بېشكه دوى له دې نه مخكې (په دنیا كې) په نعمتونو كې پالل شوي وو');
INSERT INTO "abulsalam" VALUES(56,46,'56:46',' او دوى په ډېره لویه ګناه (كفر) باندې دوام (او اصرار) كاوه');
INSERT INTO "abulsalam" VALUES(56,47,'56:47',' او دوى به ویل: ایا كله چې مونږ مړه شو او خاورې او هډوكي شو، ایا په رښتیا او یقینًا به مونږ بیا راژوندي كولى شو');
INSERT INTO "abulsalam" VALUES(56,48,'56:48',' او ایا زمونږ ړومبني پلرونه هم!');
INSERT INTO "abulsalam" VALUES(56,49,'56:49',' ته (دوى ته) ووایه چې بېشكه ړومبني او وروستني خلق');
INSERT INTO "abulsalam" VALUES(56,50,'56:50',' دوى به خامخا د معلومې ورځې مقرر وخت ته راجمع كولى شي');
INSERT INTO "abulsalam" VALUES(56,51,'56:51',' بیا به یقینًا تاسو اى ګمراهانو، تكذیب كوونكو!');
INSERT INTO "abulsalam" VALUES(56,52,'56:52',' خامخا خوړونكي به یئ د زقوم له ونې نه');
INSERT INTO "abulsalam" VALUES(56,53,'56:53',' نو له هغې نه به خېټې ډكوونكي یئ');
INSERT INTO "abulsalam" VALUES(56,54,'56:54',' بیا به تاسو د دغه (خوړل شوي زقوم) له پاسه تودې اوبه څښونكي یئ');
INSERT INTO "abulsalam" VALUES(56,55,'56:55',' نو د ډېرو تږو اوښانو د څښلو په شان به څښونكي یئ');
INSERT INTO "abulsalam" VALUES(56,56,'56:56',' دا د بدلې په ورځ د دوى مېلمستیا ده');
INSERT INTO "abulsalam" VALUES(56,57,'56:57',' مونږ تاسو پیدا كړي یئ، نو ولې تاسو تصدیق نه كوئ!');
INSERT INTO "abulsalam" VALUES(56,58,'56:58',' نو تاسو ما ته خبر راكړئ! هغه نطفه چې تاسو يې تویوئ');
INSERT INTO "abulsalam" VALUES(56,59,'56:59',' ایا تاسو (له) دغې (نطفې نه بشر) پیدا كوئ، یا هم دا مونږ يې پیدا كوونكي یو');
INSERT INTO "abulsalam" VALUES(56,60,'56:60',' بېشكه مونږ ستاسو په مینځ كې مرګ مقدر كړى دى او مونږ عاجز كړى شوي نه یو');
INSERT INTO "abulsalam" VALUES(56,61,'56:61',' له دې نه چې مونږ ستاسو په شان (نور خلق ستاسو) په بدل كې پیدا كړو او تاسو په هغو (صورتونو) سره پیدا كړو چې تاسو پرې علم نه لرئ');
INSERT INTO "abulsalam" VALUES(56,62,'56:62',' او یقینًا یقینًا تاسو په ړومبني پیدا كولو عالمان شوي یئ، نو ولې تاسو پند نه اخلئ! ');
INSERT INTO "abulsalam" VALUES(56,63,'56:63',' نو تاسو ما ته خبر راكړئ! هغه تخم چې تاسو يې كرئ');
INSERT INTO "abulsalam" VALUES(56,64,'56:64',' ایا تاسو دغه (كرل شوى) زرغونوئ، یا هم دا مونږ يې زرغونوونكي یو');
INSERT INTO "abulsalam" VALUES(56,65,'56:65',' كه مونږ وغواړو (نو) خامخا دغه به وچ او ذره ذره كړو، بیا به تاسو حیران شئ');
INSERT INTO "abulsalam" VALUES(56,66,'56:66',' (او وايئ به) بېشكه مونږ خامخا په تاوان كې غورځول شوي یو');
INSERT INTO "abulsalam" VALUES(56,67,'56:67',' بلكې مونږ محروم كړى شوي یو');
INSERT INTO "abulsalam" VALUES(56,68,'56:68',' نو تاسو ما ته خبر راكړئ! هغه اوبه چې تاسو يې څښئ');
INSERT INTO "abulsalam" VALUES(56,69,'56:69',' ایا تاسو دغه له سپینو ورېځو نه نازلې كړې دي، یا هم دا مونږ يې نازلوونكي یو؟');
INSERT INTO "abulsalam" VALUES(56,70,'56:70',' كه مونږ وغواړو، دغه به تروې ترخې وګرځوو، نو تاسو ولې شكر نه وباسئ!');
INSERT INTO "abulsalam" VALUES(56,71,'56:71',' نو تاسو ما ته خبر راكړئ! هغه اور چې تاسو يې بلوئ');
INSERT INTO "abulsalam" VALUES(56,72,'56:72',' ایا تاسو د ده ونه پیدا كړې ده، یا هم دا مونږ يې پیدا كوونكي یو؟');
INSERT INTO "abulsalam" VALUES(56,73,'56:73',' مونږه دغه (اور) تذكره (نصیحت) او د مسافرو لپاره نفعمن ګرځولى دى');
INSERT INTO "abulsalam" VALUES(56,74,'56:74',' نو ته د خپل عظیم رب د نوم پاكي بیانوه');
INSERT INTO "abulsalam" VALUES(56,75,'56:75',' نو زه د ستوریو د پرېوتو په ځایونو باندې قسم خورم');
INSERT INTO "abulsalam" VALUES(56,76,'56:76',' او بېشكه دا یقینًا ډېر لوى قسم دى كه تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(56,77,'56:77',' بېشكه دا خامخا ډېر عزتمن قرآن دى');
INSERT INTO "abulsalam" VALUES(56,78,'56:78',' په پټ ساتل شوي كتاب كې دى');
INSERT INTO "abulsalam" VALUES(56,79,'56:79',' چې ده لره نه مسه كوي مګر ښه پاك كړى شوي خلق (ملايك)');
INSERT INTO "abulsalam" VALUES(56,80,'56:80',' د رب العلمین له جانبه نازل كړى شوى دى');
INSERT INTO "abulsalam" VALUES(56,81,'56:81',' ایا نو تاسو د دې حدیث (قرآن) سپكاوى كوونكي یئ');
INSERT INTO "abulsalam" VALUES(56,82,'56:82',' او تاسو خپله برخه دا ګرځوئ چې لازمًا تاسو يې تكذیب كوئ (دروغ يې ګڼئ)');
INSERT INTO "abulsalam" VALUES(56,83,'56:83',' نو ولې يې نه (راګرځوئ) كله چې ساه ستوني ته ورسېږي');
INSERT INTO "abulsalam" VALUES(56,84,'56:84',' او په دغه وخت كې تاسو ګورئ');
INSERT INTO "abulsalam" VALUES(56,85,'56:85',' او مونږ ده ته له تاسو نه ډېر نژدې یو او لېكن تاسو نه وینئ');
INSERT INTO "abulsalam" VALUES(56,86,'56:86',' نو ولې يې نه (راګرځوئ) كه تاسو محكومان نه یئ');
INSERT INTO "abulsalam" VALUES(56,87,'56:87',' (نو ولې) دغه ساه (نه) راګرځوئ كه تاسو رښتیني یئ');
INSERT INTO "abulsalam" VALUES(56,88,'56:88',' نو كه چېرې (دغه مړى) له مقربینو څخه وي');
INSERT INTO "abulsalam" VALUES(56,89,'56:89',' نو (د ده لپاره) راحت او ښه رزق او له نعمتونو ډك جنت دى');
INSERT INTO "abulsalam" VALUES(56,90,'56:90',' او كه د ښي اړخ والاو ځنې وي');
INSERT INTO "abulsalam" VALUES(56,91,'56:91',' نو (ورته ویلى به شي:) تا ته سلام دى (اى) له ښي اړخ والاو ځنې!');
INSERT INTO "abulsalam" VALUES(56,92,'56:92',' او كه چېرې (دا مړى) د تكذیب كوونكو ګمراهانو ځنې وي');
INSERT INTO "abulsalam" VALUES(56,93,'56:93',' نو (د ده لپاره) د ډېرو اېشېدلو اوبو مېلمستیا ده');
INSERT INTO "abulsalam" VALUES(56,94,'56:94',' او دوزخ ته ننه اېستل دي');
INSERT INTO "abulsalam" VALUES(56,95,'56:95',' بېشكه دا خبره یقینًا هم دا حقه یقیني ده');
INSERT INTO "abulsalam" VALUES(56,96,'56:96',' پس ته د خپل عظیم رب د نوم پاكي بیانوه');
INSERT INTO "abulsalam" VALUES(57,1,'57:1',' د الله پاكي بیانوي هر هغه شى چې په اسمانونو او ځمكه كې دى او هم دى ښه غالب، ډېر حكمت والا دى');
INSERT INTO "abulsalam" VALUES(57,2,'57:2',' خاص هم ده لپاره د اسمانونو او ځمكې بادشاهي ده، دى ژوندي كول كوي اومړه كول كوي اوهم دى په هر شى باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(57,3,'57:3',' هم دغه (الله) اول دى (هیڅ شى له هغه نه مخكې نه و) او اخر دى (له ده نه بعد هېڅ شى نشته) او لوړ دى (په هر شي بره دى) او باطن دى (له ده په وړاندې هېڅ شى پټ نه دى) او هغه په هر شي باندې ښه پوه دى');
INSERT INTO "abulsalam" VALUES(57,4,'57:4',' دى هغه ذات دى چې اسمانونه او ځمكه يې په شپږو ورځو كې پیدا كړي دي، بیا دى په عرش باندې برقراره شو، دى عالم دى په هر شي چې په ځمكه كې ننوځي او په هر هغه شي چې له دې نه راوځي او په هر هغه شي چې له اسمانه راكوزېږي او په هر هغه شي چې په ده كې پورته خېژي او دغه (الله د خپل شان لايق) له تاسو سره دى، هر چېرې چې تاسو یئ او الله هغو كارونو لره چې تاسو يې كوئ، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(57,5,'57:5',' خاص هم ده لره د اسمانونو او ځمكې بادشاهي ده او خاص الله ته ټول كارونه ورګرځولى شي');
INSERT INTO "abulsalam" VALUES(57,6,'57:6',' دى شپه په ورځ كې ننباسي او ورځ په شپه كې ننباسي او دى د سینو په پټو خبرو ښه عالم دى');
INSERT INTO "abulsalam" VALUES(57,7,'57:7',' (اى خلقو! تاسو په الله او د هغه په رسول ایمان راوړئ او له هغه (مال) څخه څه خرچ كړئ چې هغه (الله) تاسو په دغه (مال) كې (د نورو خلقو) ځاى ناستي (خلیفه ګان) جوړ كړي یئ، نو هغه كسان چې له تاسو نه يې ایمان راوړى دى او انفاق يې كړى دى، د دوى لپاره ډېر لوى اجر دى');
INSERT INTO "abulsalam" VALUES(57,8,'57:8',' او تاسو ته څه عذر دى چې په الله ایمان نه راوړئ، حال دا چې (د الله) رسول تاسو ته بلنه دركوي دې ته چې تاسو په خپل رب ایمان راوړئ او یقینًا هغه (الله له تاسو نه) ستاسو پخه وعده اخيستې ده، كه چېرې تاسو مومنان یئ');
INSERT INTO "abulsalam" VALUES(57,9,'57:9',' دى همغه ذات دى چې په خپل بنده باندې څرګندې نښې (دلیلونه) نازلوي، د دې لپاره چې دغه (الله) تاسو له تیارو نه رڼا ته وباسي او بېشكه الله پر تاسو باندې یقینًا ډېر نرمي كوونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(57,10,'57:10',' او تاسو ته څه مانع (او عذر ) دى چې د الله په لاره كې خپل مال) نه لګوئ، حال دا چې د اسمانونو او ځمكې میراث خاص د الله لپاره دى په تاسو كې هغه څوك نه دي برابر چا چې (د الله په لاره كې) له فتحې نه مخكې انفاق كړى دى او جنګ يې كړى دى، دغه خلق د درجې په لحاظ له هغو كسانو نه ډېر لوى دي چې له دغې (فتحې) نه يې وروسته (د الله په لاره كې) انفاق كړى وي او جنګ يې كړى وي او (په دوى كې) له هر یو سره الله د ښېګڼې (جنت) وعده كړې ده او الله په هغو كارونو چې تاسو يې كوئ، ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(57,11,'57:11',' څوك دى هغه كس چې الله ته قرض وركړي، ډېر ښكلى قرض (حسن) نو چې هغه (الله) دغه (قرض) د ده لپاره دوه چنده كړي او د ده لپاره ډېر د عزت اجر دى');
INSERT INTO "abulsalam" VALUES(57,12,'57:12',' هغه ورځ (یاده كړه) چې ته به مومنان سړي او مومنې ښځې وینې چې د دوى رڼا به د دوى مخې ته او د دوى ښي اړخ ته منډې وهي، ستاسو زېرى نن ورځ داسې جنتونه دي چې د هغو له لاندې ولې بهېږي، چې په هغو كې به تل اوسېدونكي یئ، همدغه ډېره لویه كامیابي ده');
INSERT INTO "abulsalam" VALUES(57,13,'57:13',' (اى نبي! ته) هغه ورځ (یاده كړه) چې منافق سړي اومنافقې ښځې به ایمان داره كسانو ته وايي: مونږ ته وګورئ (انتظار وكړئ) چې ستاسو له رڼا نه رڼا واخلو (، نو) دوى ته به وویل شي چې خپلې شا ته وګرځئ، نو (هلته) رڼا ولټوئ، نو د دوى په مینځ كې به یو داسې دېوال وواهه شي چې د هغه به دروازه (هم) وي، د دغه (دېوال) دننه طرف، په هغه كې به رحمت وي او د دغه (دېوال) د باندې طرف، له دغه جانبه به عذاب وي');
INSERT INTO "abulsalam" VALUES(57,14,'57:14',' دوى به دغو (مومنانو) ته اواز وكړي: ایا مونږ له تاسو سره نه وو ؟ نو دوى (مومنان) به ووايي: ولې نه! او لېكن تاسو خپل ځانونه په فتنه كې اچولي وو او تاسو انتظار كاوه او شك مو كاوه او (خپلو) ارزوګانو دوكه كړي (تېرایستلي) وئ، تر هغه پورې چې د الله امر راغى او تاسو د الله په باره كې ډېر دوكه كوونكي دوكه كړي وئ');
INSERT INTO "abulsalam" VALUES(57,15,'57:15',' نو نن ورځ به نه له تاسو نه څه فدیه (معاوضه) اخيستلى شي او نه له كافران شویو كسانو نه، ستاسو استوګنه اور دى، هغه (اور) ستاسو دوست (او لايق) دى او هغه د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(57,16,'57:16',' ایا هغو كسانو ته چې ایمان يې راوړى دى (دغه) وخت نه دى راغلى چې د دوى زړونه نرم (او په وېره) شي د الله ذكر ته او هغه حق ته چې نازل شوى دى ! او د هغو كسانو په شان نشي چې له دې نه مخكې ورته كتاب وركړى شوى و، نو په دوى باندې زمانه اوږده شوه، پس د دوى زړونه سخت شول او په دوى كې ډېر فاسقان (نافران) دي');
INSERT INTO "abulsalam" VALUES(57,17,'57:17',' تاسو پوه شئ چې بېشكه الله ځمكه د هغې له مرګ (وچوالي) نه پس ژوندۍ كوي، یقینًا مونږ تاسو ته ایتونه بیان كړي دي، د دې لپاره چې تاسو له عقل نه كار واخلئ');
INSERT INTO "abulsalam" VALUES(57,18,'57:18',' بېشكه خیرات كوونكي سړي او خیرات كوونكې ښځې او هغه چې الله ته قرض وركوي، ښه قرض، د دوى لپاره (دغه قرضِ حسن) دوچند كولى شي او د دوى لپاره ډېر د عزت اجر دي');
INSERT INTO "abulsalam" VALUES(57,19,'57:19',' او هغه كسان چې په الله او د هغه په رسولانو يې ایمان راوړى دى همدغه كسان ډېر رښتني دي او د خپل رب په وړاندې شاهدان دي، د دوى لپاره د دوى اجر او د دوى رڼا ده او هغه كسان چې كافران شوي او زمونږ ایتونو ته يې دروغ ویلي دي؛ دغه كسان د دوزخ ملګري دي');
INSERT INTO "abulsalam" VALUES(57,20,'57:20',' تاسو پوه شئ چې بېشكه دنيايي ژوند يواځې لوبې او عبث او زینت او په خپل مینځ یو په بل باندې فخر كول دي او په یو بل باندې په مالونو او اولادونو كې ډېر والى كول دي د هغه باران په شان چې كرونكي (كروندګر) لره د هغه (باران) زرغونه خوشاله كړي، بیا هغه (زرغونه) وچېږي، نو ته دغه زرغونه تكه زېړه وینې، بیا هغه رېزه رېزه شي او په اخرت كې (د اخرت منكرانو ته) ډېر سخت عذاب دى او (منونكو ته) د الله له جانبه بخښنه او رضامندي ده، او دنيايي ژوند نه دى مګر د دوكې (او غولېدلو) سامان دى');
INSERT INTO "abulsalam" VALUES(57,21,'57:21',' تاسو تلوار وكړئ د الله له جانبه مغفرت ته او داسې جنت ته چې د هغه پلنوالى د اسمان او ځمكې د پلنوالي په شان دى، د هغو كسانو لپاره تیار كړى شوى دى چې په الله او د هغه په رسولانو ایمان لري، دغه د الله فضل دى، چا ته چې وغواړي دغه وركوي او الله د ډېر لوى فضل والا دى');
INSERT INTO "abulsalam" VALUES(57,22,'57:22',' نه رسېږي په ځمكه كې او نه ستاسو په ځانونو كې كوم مصیبت مګر (دا) په كتاب كې دي مخكې له دې نه چې دغه (ځانونه) پيدا كړو، بېشكه الله ته دا ډېره اسانه ده');
INSERT INTO "abulsalam" VALUES(57,23,'57:23',' لپاره د دې چې تاسو په هغه څه خفه نشئ چې له تاسو فوت شول او په هغه څه خوشاله نشئ چې تاسو ته هغه دركړل او الله نه خوښوي هېڅ كبرجن (او) فخر كوونكى');
INSERT INTO "abulsalam" VALUES(57,24,'57:24',' هغه كسان چې (پخپله) بخل كوي او (نورو) خلقو ته (هم) د بخل كولو حكم كوي او هغه څوك چې مخ يې وګرځاوه، نو بېشكه الله، هم دى ښه بې پروا، ډېر ستايل شوى دى');
INSERT INTO "abulsalam" VALUES(57,25,'57:25',' یقینًا یقینًا مونږ خپل رسولان په ښكاره دلیلونو سره لېږلي دي او له دوى سره مونږ كتاب او تله نازل كړي دي، د دې لپاره چې خلق په انصاف ودرېږي او مونږ اوسپنه نازله كړې ده، په دې كې (قوت د) سخت جنګ دى او د خلقو لپاره ګټې دي او د دې لپاره چې الله په هغه چا باندې پوه شي چې د ده او د ده د رسولانو په نالیدلو مدد كوي، بېشكه الله ډېر قوي، ښه غالب دى');
INSERT INTO "abulsalam" VALUES(57,26,'57:26',' او یقینًا یقینًا مونږ نوح او ابراهیم رسولان لېږلي وو او د دغو دواړو په اولاده كې مو نبوت او كتاب ایښى و، نو په دوى كې ځینې هدایت موندونكي دي او په دوى كې ډېر زیات فاسقان دي');
INSERT INTO "abulsalam" VALUES(57,27,'57:27',' بیا مونږ د دوى د قدمونو په اثارو پسې خپل رسولان راولېږل او ورپسې مو راوست عیسٰى زوى د مریمې او مونږ ده ته انجیل وركړى و او مونږ د ده د پېروي كوونكو په زړونو كې نرمي او مهرباني كول ایښي وو او رهبانیت چې دغه دوى له ځانه ایجاد كړى و، مونږ په دوى باندې لازم كړى نه و لېكن (دوى غوره كړى و) د الله د رضامندۍ لټولو لپاره، نو دوى د هغه (رهبانیت) رعایت ونه كړ، حق رعایت، نو په دوى كې هغو كسانو ته چې ایمان يې راوړى و ؛ مونږ د دوى اجر وركړ او په دوى كې ډېر زیات فاسقان وو');
INSERT INTO "abulsalam" VALUES(57,28,'57:28',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو له الله نه ووېرېږئ او د هغه په رسول ایمان راوړئ هغه (الله) به تاسو ته له خپل رحمت نه دوه حصې دركړي او تاسو لپاره به رڼا پیدا كړي چې تاسو به له هغې سره (په پل صراط) ځئ او تاسو به وبخښي او الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(57,29,'57:29',' د دې لپاره چې اهل كتاب (په دې) ښه پوه شي چې دوى د الله له فضله په هېڅ شي باندې قادر نه دي او دا چې بېشكه فضل د الله په لاس كې دى، چا ته چې وغواړي هغه وركوي او الله د ډېر لوى فضل والا دى');
INSERT INTO "abulsalam" VALUES(58,1,'58:1',' یقینًا الله د هغې ښځې خبره واورېدله چې له تا سره يې د خپل خاوند په باره كې بحث (او سوال و جواب) كاوه او الله ته يې شكایت كاوه او الله ستاسو د دواړو سوال او جواب اورېده، بېشكه الله ښه اورېدونكى، ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(58,2,'58:2',' هغه كسان چې په تاسو كې له خپلو ښځو نه ظهار كوي (ورته وايي چې ته په ما باندې د خپلې مور د شا په شان يې، نو) دغه (ښځې) د دوى میندې نه دي، د دوى میندې نه دي مګر هغه چې دوى يې زېږولي دي او بېشكه یقینًا دوى یوه خرابه خبره او دروغ وايي او بېشكه یقینًا الله ډېر معافي كوونكى، ډېر بخښونكى دى');
INSERT INTO "abulsalam" VALUES(58,3,'58:3',' او هغه كسان چې له خپلو ښځو نه ظهار كوي، بیا دوى (ماتولو د) هغې خبرې ته راګرځي چې ویلې وه، نو د یو مریي ازادول (لازم) دي، مخكې له دې چې دوى یو بل سره مسه كړي (كور والى وكړي) دغه (حكم) دى، تاسو ته په ده سره پند دركاوه شي او الله په هغو كارونو چې تاسو يې كوئ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(58,4,'58:4',' نو چا چې (مریى) ونه مونده، نو (په هغه) دوه میاشتې پرله پسې روژه نیول (لازم) دي، مخكې له دې چې دوى یو بل سره مسه كړي، نو هغه څوك چې (د دې) توان نه لري، نو (په ده) شپیتو مسكینانو ته خوراك وركول (لازم) دي دا (حكم) د دې لپاره دى چې تاسو په الله او د هغه په رسول ایمان راوړئ او دغه د الله (مقرر كړي) حدونه دي او د كافرانو لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(58,5,'58:5',' بېشكه هغه كسان چې د الله او د هغه د رسول مخالفت كوي، دوى به هلاك كړى شي، لكه چې هلاك كړى شوي وو هغه كسان چې له دوى نه مخكې وو او یقینًا مونږ ډېرې روښانه نښې (او دلايل) نازلې كړې دي او د كافرانو لپاره سپكوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(58,6,'58:6',' (یاده كړه) هغه ورځ چې الله به دوى ټول ژوندي راپورته كړي، پس دوى به په هغو كارونو خبر كړي چې دوى كړي دي، الله هغه محفوظ كړي دي او دوى هغه هېر كړي دي او الله په هر شي باندې ښه شاهد (ګواه) دى');
INSERT INTO "abulsalam" VALUES(58,7,'58:7',' ایا ته نه يې خبر چې بېشكه الله پوهېږي په هر هغه څه چې په اسمانونو كې دي او په هر هغه څه چې په ځمكه كې دي، نه وي هېڅ پټه مشوره د درې كسانو مګر هغه (الله) د دوى څلورم وي او نه د پنځو كسانو مګر هغه د دوى شپږم وي او نه له دې نه د كمو وي او نه (له دې نه) د زیاتو مګر هغه له دوى سره وي، دوى چې هرچېرې وي، بیا به هغه د قیامت په ورځ دوى په هغو عملونو خبر كړي چې دوى كړي دي، بېشكه الله په هر شي باندې ښه پوه دى');
INSERT INTO "abulsalam" VALUES(58,8,'58:8',' ایا تا هغه كسان نه دي كتلي چې له پټو مشورو نه منع كړى شوي وو، بیا دوى هغه څه ته راګرځي چې له هغه نه منع كړى شوي وو او دوى د ګناه او ظلم او د رسول د نافرمانۍ په هكله پټې مشورې كوي او كله چې دوى تا ته راشي (نو) تا ته په هغه (كلمې) سره سلام كوي چې په هغې سره الله تا ته سلام نه دى (مقرر) كړى او دوى په خپلو نفسونو (زړونو) كې وايي: الله مونږ ته په هغه څه ولې عذاب نه راكوي چې مونږ يې وایو، دوى ته جهنم بس دى، چې دوى به هغه ته ننوځي، پس هغه د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(58,9,'58:9',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې تاسو په خپل مینځ كې پټې خبرې (او مشورې)، كوئ، نو دګناه كولو او ظلم او د رسول د نافرمانۍ په هكله یو له بله سره پټې مشورې مه كوئ او د نیكۍ او تقویٰ په هكله یو له بل سره پټې مشورې كوئ او تاسو له هغه الله نه ووېرېږئ چې تاسو به خاص هغه ته ورجمع كولى شئ');
INSERT INTO "abulsalam" VALUES(58,10,'58:10',' بېشكه (د ګناه) پټې مشورې يواځې د شیطان له اړخه دي، د دې لپاره چې دى هغه كسان غمجن كړي چې ایمان يې راوړى دى، حال دا چې دغه (شیطان) دوى ته هېڅ شى ضرر رسوونكى نه دى مګر د الله په اذن (او ارادې) سره او لازم ده چې مومنان خاص په الله باندې توكل وكړي');
INSERT INTO "abulsalam" VALUES(58,11,'58:11',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې تاسو ته وویل شي: تاسو په مجلس كې ځاى ارت كړئ، نو تاسو ځاى ارت كړئ، الله به تاسو ته ځاى ارت كړي او كله چې تاسو ته وویل شي چې (كوم نېك كار ته) پورته شئ، نو پورته شئ، الله به په تاسو كې هغه كسان په درجو كې اوچت كړي چې ایمان يې راوړى دى او هغه كسان چې هغوى ته علم وركړى شوى دى او الله په هغو كارونو چې تاسو يې كوئ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(58,12,'58:12',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې تاسو له رسول سره پټه خبره كول غواړئ، نو تاسو له خپلې پټې مشورې نه مخكې صدقه وړاندې كړئ، دا تاسو لپاره ډېر غوره او ډېر پاكیزه دي، نو كه تاسو (صدقه) ونه مومئ، نو بېشكه الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(58,13,'58:13',' ایا تاسو له دې نه ووېرېدئ چې له خپلې پټې مشورې نه مخكې صدقې وړاندې كړئ، نو كله چې تاسو (دا كار) ونه كړ او الله پر تاسو باندې رجوع وكړه، نو تاسو لمونځ قايموئ او زكات وركوئ او د الله او د هغه د رسول اطاعت كوئ او الله په هغو عملونو چې تاسو يې كوئ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(58,14,'58:14',' ایا تا هغو كسانو ته نه دي كتلي چې له داسې قوم سره دوستي كوي چې الله پر هغوى باندې غضب كړى دى، دغه كسان (منافقان) نه ستاسو څخه دي او نه له هغوى (كافرانو) څخه دي او دوى په دروغو باندې قسمونه خوري، حال دا چې دوى پوهېږي');
INSERT INTO "abulsalam" VALUES(58,15,'58:15',' الله د دوى لپاره ډېر سخت عذاب تیار كړى دى، بېشكه دوى (چې دي) بد دى هغه (كار) چې دوى به كاوه ');
INSERT INTO "abulsalam" VALUES(58,16,'58:16',' دوى خپل قسمونه ډال ګرځولي دي، نو دوى (خلق) د الله له لارې نه منع كړل، نو د دوى لپاره سپكوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(58,17,'58:17',' د دوى لپاره به د الله په مقابله كې نه د دوى مالونونه هېڅ شى پكار راشي او نه د دوى اولادونه، دغه كسان د اور ملګري دي دوى به په هغه كې تل تر تله وي');
INSERT INTO "abulsalam" VALUES(58,18,'58:18',' (یاده كړه) هغه ورځ چې الله به دوى ټول ژوندي راپورته كړي، نو دوى به هغه (الله) ته قسمونه خوري، لكه څنګه چې تاسو ته قسمونه خوري او دوى به ګمان كوي چې یقینًا دوى په یو (نفعمن) شي (لار) باندې دي، خبردار شئ! بېشكه هم دوى دروغجنان دي');
INSERT INTO "abulsalam" VALUES(58,19,'58:19',' په دوى باندې شیطان غالب شوى دى، نو هغه له دوى نه د الله ذكر هېر كړى دى، دغه كسان د شیطان لښكر دى، خبردار شئ! بېشكه د شیطان لښكر (چې دى)، هم دوى زیان كاران دي ');
INSERT INTO "abulsalam" VALUES(58,20,'58:20',' بېشكه هغه كسان چې د الله او د هغه د رسول مخالفت كوي، دغه كسان په ډېر زیاتو ذلیلو (خلقو) كې دي');
INSERT INTO "abulsalam" VALUES(58,21,'58:21',' الله لیكلي دي چې خامخا (او) لازمًا به زه غالب كېږم او زما رسولان، بېشكه الله ډېر قوي، ښه غالب دى');
INSERT INTO "abulsalam" VALUES(58,22,'58:22',' ته به داسې قوم ونه مومې چې په الله او په ورځ د اخرت ایمان لري، چې دوى به له هغو كسانو سره دوستي كوي چې د الله او د هغه د رسول مخالفت كوي اګر چې دغه خلق د دوى پلرونه وي، یا د دوى زامن، یا د دوى وروڼه، یا د دوى قبیله وي، دغه كسان دي چې هغه (الله) د دوى په زړونو كې ایمان لیكلى دى او دوى يې په هغه روح سره قوي كړي دي چې د ده له جانبه دى او هغه به دوى داسې جنتونو ته داخل كړي چې د هغو له لاندې ولې بهېږي، چې دوى په هغه كې تل تر تله وي، الله له دوى نه راضي شو او دوى له هغه نه راضي شول، دغه كسان د الله لښكر دى اګاه شئ! بېشكه د الله لښكر، هم دوى كامیاب دي');
INSERT INTO "abulsalam" VALUES(59,1,'59:1',' د الله لپاره پاكې بیانوي هر هغه څه چې په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي او هم دى ښه غالب، ډېر حكمت والا دى');
INSERT INTO "abulsalam" VALUES(59,2,'59:2',' دى هغه ذات دى چې كتابي كافران يې له خپلو كورونو نه ووېستل په اول وېستلو كې، تاسو دا ګمان نه كاوه چې دوى به ووځي او دوى ګمان كاوه چې د دوى قلاګانې دوى لره د الله (له عذاب) نه منع كوونكې (او ساتونكې) دي، نو الله دوى ته له هغه ځایه راغى چې دوى (يې) خیال هم نه كاوه او د دوى په زړونو كې يې رعب واچاوه، دوى خپل كورونه په خپلو لاسونو او د مومنانو په لاسونو ورانول، نو اى د سترګو خاوندانو! تاسو عبرت واخلئ');
INSERT INTO "abulsalam" VALUES(59,3,'59:3',' او كه الله په دوى باندې بې كوره كېدل نه وى لیكلي (نو) یقینًا دوى ته به يې په دنیا كې عذاب وركړى و او د دوى لپاره په اخرت كې د اور عذاب دى');
INSERT INTO "abulsalam" VALUES(59,4,'59:4',' دا ځكه چې یقینًا دوى د الله او د هغه د رسول مخالفت كړى و او هغه څوك چې د الله مخالفت وكړي، نو بېشكه الله د ډېر سخت عذاب والا دى');
INSERT INTO "abulsalam" VALUES(59,5,'59:5',' د كجورو هره هغه ونه چې تاسو پرې كړه، یا مو هغه په خپلو بېخونو ولاړه پرېښوده، نو (دغه) د الله په حكم سره دي او د دې لپاره چې هغه (الله) دغو فاسقانو (كافرانو) لره رسوا (او ذلیله) كړي');
INSERT INTO "abulsalam" VALUES(59,6,'59:6',' او هغه (مال) چې الله له دوى نه په خپل رسول باندې راګرځولى دى، نو تاسو په دغه (مال) باندې نه اسونه ځغلولي دي او نه اوښان او لېكن الله خپل رسولان مسلط كوي په هر هغه چا باندې چې يې وغواړي او الله په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(59,7,'59:7',' هغه شى چې الله په خپل رسول باندې د كلیو له اوسېدونكو نه راګرځولى دى، نو (هغه) الله لره او ( د الله) رسول لره دى او (او د رسول) خپلوانو او یتیمانو او مسكینانو اومسافرو لره دى، د دې لپاره چې دغه (مال) په تاسو كې د غنیانو په مینځ كې ګردش كوونكى ونه ګرځي او رسول چې تاسو ته څه هم دركړي، نو هغه واخلئ او له څه نه چې هغه تاسو منع كړي، نو تاسو منع شئ او له الله نه ووېرېږئ، بېشكه الله ډېر سخت عذاب والا دى');
INSERT INTO "abulsalam" VALUES(59,8,'59:8',' (دغه مالونه) د هغو فقیرو هجرت كوونكو لپاره دي چې له خپلو كورونو او خپلو مالونو نه اېستل شوي دي، په داسې حال كې چې دوى د الله فضل او رضامندي لټوي او د الله او د هغه د رسول مدد كوي، همدغه كسان رښتیني دي');
INSERT INTO "abulsalam" VALUES(59,9,'59:9',' او د هغو كسانو لپاره دي چې دوى له دغو (مهاجرینو) نه مخكې په دغه كور (مدینه) كې او په ایمان كې ځاى نیولى دى، دوى له هغو كسانو سره مینه كوي چې دوى ته يې هجرت كړى دى او دوى په خپلو سینو (زړونو) كې هېڅ غصه (او حسد) نه مومي د هغه (مال) په وجه چې دغو (مهاجرینو) ته وركړى شو او دوى په خیلو ځانونو باندې (هجرت كوونكو ته) ترجیح وركوي اګر كه په دوى باندې تنګي (او حاجت مندي) وي او هر هغه څوك چې د خپل نفس له بخیلۍ نه بچ كړى شو، نو همدغه كسان كامیاب (او په مراد رسېدلي) دي');
INSERT INTO "abulsalam" VALUES(59,10,'59:10',' او د هغو كسانو لپاره دي چې له دوى (هجرت كوونكو او انصارو) نه وروسته راغلي دي؛ دوى وايي: اى زمونږه ربه: ته مونږ ته مغفرت وكړه او زمونږ هغو وروڼو ته چې زمونږ نه يې په ایمان سره سبقت (او ړومبى والى) كړى دى او ته زمونږ په زړونو كې د هغو كسانو لپاره هېڅ بغض (او كینه) مه پیدا كوه چې ایمان يې راوړى دى اى زمونږه ربه! بېشكه ته ډېر نرمي كوونكى، بې حده رحم كوونكى يې');
INSERT INTO "abulsalam" VALUES(59,11,'59:11',' ایا تا نه دي كتلي هغو كسانو ته چې منافقان شوي دي، دوى خپلو هغو وروڼو ته چې په اهل كتابو كې كافران شوي دي؛ وايي: قسم دى كه تاسو ووېستل شئ (نو) خامخا ضرور به مونږ هم له تاسو سره وځو او ستاسو په باره كې به د هیچا خبره هیڅكله هم ونه منو او كه چېرې له تاسو سره جنګ شروع كړى شي (نو) مونږ به خامخا ضرور ستاسو مدد كوو او الله ګواهي وركوي چې بېشكه دوى یقینًا دروغجن دي');
INSERT INTO "abulsalam" VALUES(59,12,'59:12',' قسم دى كه دوى وویستل شي (نو) دوى به له دغو سره نه وځي او یقینًا كه دوى سره جنګ وكړى شي (نو) دوى به د هغو امداد نه كوي او خامخا كه دوى د هغوى مدد وكړي (، نو) ضرور (او) خامخا به دوى خپلې شاګانې (په تېښته) وګرځوي، بیا به د دوى مرسته نه كېږي');
INSERT INTO "abulsalam" VALUES(59,13,'59:13',' خامخا تاسو (اى مومنانو!) د دوى په سینو (زړونو) كې د وېرې په لحاظ له الله نه زیات سخت یئ، دغه (وېره) په دې سبب ده چې بېشكه دوى داسې قوم دى چې نه پوهېږي');
INSERT INTO "abulsalam" VALUES(59,14,'59:14',' دوى به له تاسوسره ټول په ګډه جنګ ونه كړي مګر په كلابندو كلیو كې، یا د دیوالونو شا ته، د دوى په خپل مینځ كې جنګ ډېر سخت دى، ته به په دوى ګمان كوې چې سره یو (ملګري) دي، حال دا چې د دوى زړونه بېل بېل دي، دا په دې وجه چې بېشكه دوى داسې قوم دى چې له عقل نه كار نه اخلي');
INSERT INTO "abulsalam" VALUES(59,15,'59:15',' (د دوى مثال په ایمان نه راوړو كې) د هغو كسانو په شان دى چې له دوى نه مخكې په نژدې زمانه كې وو، چې هغوى د خپلو بدو چارو سزا (په دنیا كې) وڅكله او د هغوى لپاره (په اخرت كې) ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(59,16,'59:16',' (د منافقانو مثال د وعدې په مخالفت كې) د شیطان په شان دى كله چې هغه انسان ته ووايي چې كافر شه، نو كله چې هغه (انسان) كافر شي، دى (ورته) وايي: بېشكه زه له تا نه بېزاره (او جدا) یم، بېشكه زه له الله رب العالمین نه وېرېږم');
INSERT INTO "abulsalam" VALUES(59,17,'59:17',' نو د دغو دواړو عاقبت (او انجام) دا دى چې بېشكه دوى دواړه په اور كې دي، چې په ده كې به تل ترتله وي او دغه د ظالمانو بدله ده');
INSERT INTO "abulsalam" VALUES(59,18,'59:18',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو له الله نه ووېرېږئ او هر نفس دې هغه شي ته وګوري چې ده د سبا ورځې (قیامت) لپاره مخكې لېږلى دى او تاسو له الله نه ووېرېږئ ، بېشكه الله په هغو عملونو ښه خبردار دى چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(59,19,'59:19',' او تاسو د هغو كسانو په شان مه كېږئ چې الله يې هېر كړى دى، نو هغه (الله) له دوى نه خپل ځانونه هېر كړل، همدغه كسان نافرمانه دي ');
INSERT INTO "abulsalam" VALUES(59,20,'59:20',' د دوزخ والا او د جنت والا سره برابر نه دي، جنت والا چې دي؛ هم دوى كامیاب (او په مراد رسېدلي) دي');
INSERT INTO "abulsalam" VALUES(59,21,'59:21',' كه مونږ دا قرآن په یوه غره باندې نازل كړى وى (نو) تا به خامخا هغه (غر) لیدلى وى، چې وېرېدونكى او د الله له وېرې نه ټوټې ټوټې كېدونكى به وى او دغه مثالونه (دي) چې مونږ يې د خلقو لپاره بیانوو، د دې لپاره چې دوى فكر وكړي');
INSERT INTO "abulsalam" VALUES(59,22,'59:22',' دغه الله هغه ذات دى چې له ده نه غیر بل هېڅ حق معبود نشته، په غیبو او په ښكاره و عالم دى، هم دى ډېر مهربان، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(59,23,'59:23',' دغه الله هغه ذات دى چې نشته هېڅ حق معبود مګر هم دى دى، چې (حقیقي) بادشاه دى، ښه پاك دى، سلامتي وركوونكى (او) امان وركوونكى دى، نګهبان (محافظ) ښه غالب (او زورور)، ډېر عظمت والا، ډېر لويي والا دى، الله پاك دى له هغو (شیانو) نه چې دوى يې ورسره شریكوي');
INSERT INTO "abulsalam" VALUES(59,24,'59:24',' هم دغه الله پیدا كوونكى، (له نیشت نه وجود ته) راوېستونكى، صورت وركوونكى دى، خاص هم ده لپاره تر ټولو ښكلي نومونه دي، د ده پاكې بیانوي هر هغه شى چې په اسمانونو او ځمكه كې دى، او هم دى ښه غالب، ډېر حكمت والا دى');
INSERT INTO "abulsalam" VALUES(60,1,'60:1',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو زما دښمنان او خپل دښمنان دوستان مه نیسئ، چې تاسو دوى ته مینه وروړاندې كوئ، حال دا چې دوى په هغه حق باندې كافران شوي دي چې تاسو ته راغلى دى، دوى رسول او تاسو په دې سبب (له خپل وطنه) وباسي چې تاسو په خپل رب الله باندې ایمان راوړى دى ؛ كه چېرې یئ تاسو چې زما په لاره كې د جهاد لپاره او زما د رضامندۍ د غوښتلو لپاره راوتلي یئ، تاسو دوى ته په پټه سره (پېغام د) دوستي لېږئ، حال دا چې زه په هغه څه ښه عالم یم چې تاسو يې پټوئ او په هغه څه چې تاسو يې څرګندوئ او په تاسو كې چې هر څوك دغه (كار) وكړي، نو یقینًا هغه له نېغې لارې نه بې لارې شو');
INSERT INTO "abulsalam" VALUES(60,2,'60:2',' كه چېرې دوى په تاسو برى ومومي (،نو) تاسو لپاره به دښمنان شي او تاسو ته به در اوږده كړي خپل لاسونه او خپلې ژبې په بدۍ سره او دوى خوښوي چې تاسو كافران شئ');
INSERT INTO "abulsalam" VALUES(60,3,'60:3',' تاسو ته به له سره نفع در ونه رسوي ستاسو خپلویانې او نه ستاسو اولاد، د قیامت په ورځ به هغه (الله) ستاسو په مینځ كې فیصله كوي او الله هغو كارونو لره چې تاسو يې كوئ؛ ښه لیدونكى دى');
INSERT INTO "abulsalam" VALUES(60,4,'60:4',' یقینًا تاسو لپاره یوه ښكلې طریقه په ابراهیم او په هغو كسانو كې ده چې له هغه سره ملګري وو كله چې هغوى خپل قوم ته وویل: بېشكه مونږ له تاسو نه او له هغو څیزونو نه چې تاسو يې له الله نه غیر عبادت كوئ، سخت بېزاره (او جدا) یو، مونږ پر تاسو باندې منكران شوي یو او زمونږ په مینځ كې او ستاسو په منیځ كې عداوت (دښمني) او بغض د تل لپاره څرګند (او واقع) شوى دى، تر هغه پورې چې تاسو په الله باندې ایمان راوړئ چې ايكي يواځې دى خو خپل پلار ته د ابراهیم د دې (استغفار له) خبرې نه غیر چې: ’’زه به تا لپاره خامخا ضرور استغفار غواړم‘‘، او زه تا لپاره د الله په مقابله كې د هېڅ شي اختیار نه لرم ، اى زمونږه ربه! خاص په تا باندې مونږ توكل كړى دى او خاص تا ته مو رجوع كړې ده او خاص تا ته بېرته درتګ دى');
INSERT INTO "abulsalam" VALUES(60,5,'60:5',' اى زمونږه ربه! ته مونږ د كافران شویو خلقو لپاره (ځاى د) فتنې مه ګرځوه او ته مونږ ته مغفرت وكړه اى زمونږ ربه! بېشكه هم دا ته ډېر غالب، ښه حكمت والا يې');
INSERT INTO "abulsalam" VALUES(60,6,'60:6',' یقینًا یقینًا تاسو لپاره په دغو كې ډېر ښكلې (قابلِ اقتدا) طریقه ده، د هغه چا لپاره چې د الله او د اخري ورځې امېد لري او هر هغه څوك چې مخ واړوي، نو بېشكه الله، هم دى غني (ډېر بې پروا)، ښه ستايل شوى دى');
INSERT INTO "abulsalam" VALUES(60,7,'60:7',' امېد دى چې الله ستاسو په مینځ كې او د هغو كسانو په مینځ كې دوستي پیدا كړي چې تاسو ورسره دښمني كړې ده او الله ښه قادر دى، او الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(60,8,'60:8',' الله تاسو له هغو كسانو نه نه منع كوي چې له تاسو سره يې په دین كې نه جنګ كړى دى او نه يې تاسو له خپلو كورونو نه وېستلي یئ دا چې تاسو له هغوى سره نېكي وكړئ او له دوى سره انصاف وكړئ، بېشكه الله انصاف كوونكي خوښوي');
INSERT INTO "abulsalam" VALUES(60,9,'60:9',' بېشكه الله تاسو يواځې له هغو كسانو نه منع كوي چې له تاسو سره يې په دین كې جنګ كړى دى او تاسو يې له خپلو كورونو نه ایستلي یئ او ستاسو په ایستلو كې يې یو له بل سره مدد كړى دى، دا چې تاسو له دوى سره دوستي وكړئ او هر هغه څوك چې له دوى سره دوستي وكړي، نو هم دغه كسان ظالمان دي');
INSERT INTO "abulsalam" VALUES(60,10,'60:10',' اى هغو كسانوچې ایمان يې راوړى دى! كله چې تاسو ته مومنې ښځې راشي، چې هجرت كوونكې وي، نو تاسو دوى وازمایئ، الله د دوى په ایمان ښه عالم دى، نو كه تاسو دوى مومنې وپېژنئ (معلومې كړئ)، نو تاسو دوى كافرانو ته بېرته مه لېږئ، دوى د هغوى (كافرانو) لپاره حلالې نه دي او نه هغوى د دغو (مومنو ښځو) لپاره حلال دي او تاسو (د دغو مومنو ښځو) دغو (كافرو خاوندانو) ته هغه څه وركړئ چې دوى لګولي دي او پر تاسو باندې په دې كې هېڅ ګناه نشته چې تاسو له دوى سره نكاح وكړئ (خو په دې شرط) كله چې تاسو دوى ته د دوى مهرونه وركړئ او تاسو د كافرو ښځو په عصمتونو منګولې مه لګوئ (په نكاح كې يې مه ساتئ) او تاسو هغه څه وغواړئ چې تاسو (په دغو كافرو ښځو) لګولي دي او دوى (كافر خاوندان) دې هم هغه څه وغواړي چې دوى (په دغو مهاجرو ښځو باندې) لګولي وي، دغه د الله حكم دى، الله ستاسو په مینځ كې (په ده سره) فیصله كوي او الله ښه عالم، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(60,11,'60:11',' او كه چېرې ستاسو په ښځو كې كومه ښځه ستاسو له لاسه ووځي (او) كافرو ته (لاړه شي) بیا ستاسو وار راشي (غزا وكړئ نو غنیمت واخلئ) نو تاسو هغو كسانو ته چې د هغوى ښځې تللې وي هومره (مال) وركړئ چې هغوى (پر هغو ښځو) لګولى دى او تاسو له هغه الله نه ووېرېږئ چې تاسو په هغه ایمان لرونكي یئ');
INSERT INTO "abulsalam" VALUES(60,12,'60:12',' اى نبي! كله چې تا ته مومنې ښځې راشي چې له تا سره په دې خبره بیعت كوي چې له الله سره به هېڅ شى نه شریكوي او غلا به نه كوي او زنا به نه كوي او خپل اولاد به نه وژني او نه به داسې بهتان لګوي چې هغه د خپلو لاسونو او خپلو پښو په وړاندې جوړوي او په معروفو (نېكو چارو) كې به ستا نافرماني نه كوي، نو ته دوى بیعت كړه او د دوى لپاره د الله مغفرت (بخښنه) وغواړه، بېشكه الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(60,13,'60:13',' اى هغو كسانو چې ایمان يې راوړى دى! له هغه قوم سره دوستي مه كوئ چې الله پر هغوى باندې غضب كړى دى، یقینًا دوى له اخرته داسې نا امېده شوي دي لكه كفار چې د قبرونو والاو (له راژوندي كېدو) نه نا امېده شوي دي');
INSERT INTO "abulsalam" VALUES(61,1,'61:1',' د الله پاكي بیانوي هر هغه څه چې په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي او هم دى ښه غالب، ډېر حكمت والا دى');
INSERT INTO "abulsalam" VALUES(61,2,'61:2',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو هغه (خبره) ولې وایئ چې نه يې كوئ! ');
INSERT INTO "abulsalam" VALUES(61,3,'61:3',' دا (خبره) د الله په نیز د غضب په لحاظ لویه ده چې تاسو هغه (خبره) ووایئ چې نه يې كوئ');
INSERT INTO "abulsalam" VALUES(61,4,'61:4',' بېشكه الله هغه كسان خوښوي چې د هغه په لاره كې صف تړلي جنګېږي، ګویاكې دوى په قلعي سره په یو بل پورې ټینګ كړى شوى (ښه محكم) عمارت دي');
INSERT INTO "abulsalam" VALUES(61,5,'61:5',' او (یاد كړه) هغه وخت چې موسٰی خپل قوم ته وویل: اى زما قومه! تاسو ما ته ولې ضرر رارسوئ، حال دا چې تاسو پوهېږئ چې یقینًا زه تاسو ته د الله رسول یم، نو كله چې دوى (د الله له حكم نه) كاږه شول، نوالله د دوى زړونه كاږه كړل او الله نافرمانه قوم ته هدایت نه كوي');
INSERT INTO "abulsalam" VALUES(61,6,'61:6',' او (یاده كړه) هغه وخت چې د مریمې زوى عیسٰی وویل: اى بني اسرائیلو! بېشكه زه تاسو ته د الله رسول یم، چې د هغه څه تصدیق كوونكى یم چې له ما نه مخكې تېر شوى دى، چې تورات دى او د داسې رسول زېرى دركوونكى یم چې له ما نه وروسته به راځي، چې د هغه نوم احمد دى، نو كله چې دوى ته دغه (رسول) له ښكاره معجزو سره راغى (نو) دوى وویل: دا ښكاره جادو دى');
INSERT INTO "abulsalam" VALUES(61,7,'61:7',' او له هغه چا نه لوى ظالم څوك دى چې په الله پورې له ځانه دروغ تړي، حال دا چې هغه اسلام ته بللى شي او الله ظالم قوم ته هدایت نه كوي');
INSERT INTO "abulsalam" VALUES(61,8,'61:8',' دوى غواړي چې په خپلو خولو سره د الله رڼا مړه كړي او الله د خپلې رڼا پوره كوونكى دى اګر كه كافران (دا كار) بد ګڼي');
INSERT INTO "abulsalam" VALUES(61,9,'61:9',' دغه (الله) هغه ذات دى چې خپل رسول يې په هدایت او حق دین سره لېږلى دى، د دې لپاره چې دغه (الله) دغه (حق دین) ته په نورو ټولو دینونو باندې غلبه وركړي، اګر كه مشركان دغه (كار) بد ګڼي');
INSERT INTO "abulsalam" VALUES(61,10,'61:10',' اى هغو كسانو چې ایمان يې راوړى دى! ایا زه تاسو ته داسې تجارت (سوداګري) وښیم چې تاسو ته به له ډېر دردونكي عذاب نه نجات دركوي');
INSERT INTO "abulsalam" VALUES(61,11,'61:11',' تاسو په الله او د هغه په رسول ایمان لرئ او د الله په لاره كې په خپلو مالونو او خپلو ځانونو سره جهاد كوئ، دا تاسو لپاره ډېر غوره دي كه تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(61,12,'61:12',' هغه به تاسو ته ستاسو ګناهونه وبخښي او تاسو به داسې جنتونو ته داخل كړي چې د هغو له لاندې ولې بهېږي او د تل اوسېدنې په جنتونو كې په ډېرو پاكېزه استوګنو كې (به مو داخل كړي)، دغه ډېره لویه كامیابي ده');
INSERT INTO "abulsalam" VALUES(61,13,'61:13',' او بل (نعمت) چې تاسو يې خوښوئ، د الله له جانبه مرسته او نژدې برى دى او ته مومنانو ته زېرى وركړه');
INSERT INTO "abulsalam" VALUES(61,14,'61:14',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو د الله مدد كوونكي شئ، لكه چې د مریمې زوى عیسٰی حواریانو (خپلو ملګرو) ته ویلي وو: د الله طرف ته زما مدد كوونكي څوك دي؟ حواریانو وویل: مونږ د الله مدد كوونكي یو، نو په بني اسرائیلو كې یوې ډلې ایمان راوړو او بله ډله كافره شوه، نو مونږ هغو كسانو ته د خپل دښمن په خلاف قوت وركړو چې ایمان يې راوړى و، نو دوى غالبه شول');
INSERT INTO "abulsalam" VALUES(62,1,'62:1',' د الله لپاره پاكي بیانوي هر هغه څه چې په اسمانونو كې دي او هر هغه څه چې په ځمكه كې دي، (د هغه الله چې) بادشاه دى، ډېر پاك دى، ښه غالب دى، ډېر حكمت والا دى');
INSERT INTO "abulsalam" VALUES(62,2,'62:2',' دغه (الله) همغه ذات دى چې امیانو ته يې له همدوى ځنې یو رسول لېږلى دى، چې دوى ته د هغه (الله) ایتونه تلاوت كوي او د دوى تزكیه كوي او دوى ته كتاب او حكمت (سنت) ښیي او بېشكه شان دا دى چې دوى له ده نه مخكې یقینًا په ښكاره ګمراهۍ كې وو');
INSERT INTO "abulsalam" VALUES(62,3,'62:3',' او له دوى څخه نورو ته (هم) چې لا تر اوسه دوى هغو (پخوانیو) ته نه دي رسېدلي او هم دى ښه غالب، ډېر حكمت والا دى');
INSERT INTO "abulsalam" VALUES(62,4,'62:4',' دغه د الله فضل دى چې دا هغه چا ته وركوي چې يې وغواړي او الله د ډېر لوى فضل والا دى');
INSERT INTO "abulsalam" VALUES(62,5,'62:5',' د هغو كسانو حال چې په هغوى باندې تورات وربار كړى شوى و (د تورات مكلف كړى شوي وو)، بیا دوى هغه پورته نه كړ ؛ د هغه خره د حال په شان دى چې كتابونه يې په شا كړي وي، د هغو كسانو بد مثال (حال) دى چې د الله د ایتونو تكذیب كوي او الله ظالم قوم ته هدایت نه كوي');
INSERT INTO "abulsalam" VALUES(62,6,'62:6',' ته (دوى ته) ووایه: اى هغو كسانو چې یهودیان شوي دي! كه تاسو دا (غلطه) عقیده لرئ چې یقینًا تاسو له نورو خلقو نه غیر د الله دوستان یئ، نو تاسو د مرګ ارزو وكړئ، كه چېرې تاسو رښتيني یئ');
INSERT INTO "abulsalam" VALUES(62,7,'62:7',' او دوى به د دغه (مرګ) ارزو هیڅكله هم ونه كړي، په سبب د هغو (كارونو) چې د دوى لاسونو مخكې لېږلي دي او الله په ظالمانو باندې ښه پوه دى');
INSERT INTO "abulsalam" VALUES(62,8,'62:8',' ته (دوى ته) ووایه: بېشكه هغه مرګ چې تاسو له هغه نه تښتئ، پس یقینًا هغه له تاسو سره ملاقات كوونكى دى، بیا به تاسو د غیبو او ښكاره و عالم ذات ته بوتلى شئ، نو هغه به تاسو په هغو عملونو خبر كړي چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(62,9,'62:9',' اى هغو كسانو چې ایمان يې راوړى دى! كله چې د جمعې د ورځې د لمانځه لپاره اذان وویلى شي، نو تاسو د الله ذكر ته منډې وهئ او اخيستل خرڅول پرېږدئ، دا تاسو لپاره ډېر غوره دي كه تاسو پوهېږئ');
INSERT INTO "abulsalam" VALUES(62,10,'62:10',' نو كله چې لمونځ ادا كړى شي، نو تاسو په ځمكه كې خواره شئ او د الله له فضل څخه (څه) ولټوئ او تاسو الله ډېر زیات یادوئ، د دې لپاره چې تاسو كامیاب شئ');
INSERT INTO "abulsalam" VALUES(62,11,'62:11',' او كله چې دوى تجارت (سوداګري)، یا تماشه ولیدله (نو) دوى هغې ته خواره شول او ته يې ولاړ پرېښودلې، ته (دوى ته) ووایه: هغه څه چې له الله سره دي له دغې تماشې نه او له دغې سوداګرۍ نه ډېر غوره دي او الله تر ټولو ښه رزق وركوونكى دى');
INSERT INTO "abulsalam" VALUES(63,1,'63:1',' (اى نبي!) كله چې تا ته منافقان راشي (نو) دوى وايي: مونږ شاهدي وركوو چې بېشكه ته یقینًا د الله رسول يې او الله پوهېږي چې ته بېشكه (او) یقینًا د هغه رسول يې او الله شاهدي وركوي چې بېشكه منافقان یقینًا دروغ ویونكي دي');
INSERT INTO "abulsalam" VALUES(63,2,'63:2',' دوى خپل قسمونه ډال ګرځولي دي، نو دوى د الله له لارې نه منع شول، بېشكه دوى چې دي، بد دى هغه كار چې دوى يې كوي');
INSERT INTO "abulsalam" VALUES(63,3,'63:3',' دا ځكه چې یقینًا دوى ایمان راوړ، بیا كافر شول، نو د دوى په زړونو باندې مهر ولګول شو، پس ( اوس نو) دوى نه پوهېږي');
INSERT INTO "abulsalam" VALUES(63,4,'63:4',' او كله چې ته دوى ووینې (نو) تا د دوى جسمونه (تنې) په تعجب كې اچوي او كه دوى خبرې كوي، (نو) ته د دوى خبرو ته غوږ ږدې، حال دا چې ګویاكې دوى ودرول شوي وچ لرګي دي، هر اوچت غږ دخپل ځان په خلاف ګني، هم دوى دښمنان دي، لهذا ته له دوى نه محتاط اوسه، الله دې دوى هلاك كړي، څرنګه دوى ګرځولى شي');
INSERT INTO "abulsalam" VALUES(63,5,'63:5',' او كله چې دوى ته وویل شي: تاسو راشئ چې د الله رسول ستاسو لپاره مغفرت وغواړي (نو) دوى خپل سرونه تاو كړي او ته دوى وینې چې اعراض كوي، په داسې حال كې چې تكبر كوونكي وي');
INSERT INTO "abulsalam" VALUES(63,6,'63:6',' د دوى لپاره یو شان ده كه ته د دوى لپاره مغفرت (بخښنه) وغواړي، یا ته دوى لپاره مغفرت ونه غواړې، الله به دوى هېڅ كله هم ونه بخښي، بېشكه الله نافرمان قوم ته هدایت نه كوي');
INSERT INTO "abulsalam" VALUES(63,7,'63:7',' هم دوى هغه كسان دي چې وايي: تاسو په هغو كسانو باندې مال مه لګوئ چې له رسول الله سره دي، تر دې چې دوى خواره شي او خاص د الله لپاره د اسمانونو او ځمكې خزانې دي او لېكن منافقان نه پوهېږي');
INSERT INTO "abulsalam" VALUES(63,8,'63:8',' دوى وايي چې قسم دى كه مونږ مدینې ته بېرته وګرځو (نو) تر ټولو عزتمن (خلق) به له دغې (مدینې) نه تر ټولو ذلیله (خلق) خامخا ضرور وباسي او عزت (خو) خاص د الله لپاره دى او د هغه د رسول لپاره دى او د مومنانو لپاره دى او لېكن منافقان پوهه نه لري');
INSERT INTO "abulsalam" VALUES(63,9,'63:9',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو دې د الله له ذكر نه نه خپل مالونه غافله كړي او نه خپل اولادونه او هغه څوك چې دا كار وكړي، نو همدغه زیانكاران (تاوانیان) دي');
INSERT INTO "abulsalam" VALUES(63,10,'63:10',' او تاسو له هغه (مال) نه چې مونږ تاسو ته دركړى دى څه انفاق كوئ، مخكې له دې نه چې په تاسو كې یو كس ته مرګ راشي، بیا دا ووايي: اى زما ربه! ته ما تر نژدې مودې پورې ولې نه وروسته كوې، پس چې زه خیرات (صدقه) وكړم او له صالحانو (نېكانو) ځنې شم');
INSERT INTO "abulsalam" VALUES(63,11,'63:11',' او الله هېڅ یو نفس نه وروسته كوي كله چې د هغه اجل (نېټه) راشي او الله په هغو عملونو ښه خبردا دى چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(64,1,'64:1',' د الله پاكي بیانوي هر هغه څه چې په اسمانونوكې دي او هر هغه څه چې په ځمكه كې دي، خاص د هغه لپاره بادشاهي ده او خاص د هغه لپاره ټول د كمال صفتونه دي او هغه په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(64,2,'64:2',' هم دغه (الله) هغه ذات دى چې تاسو يې پیدا كړي یئ، نو په تاسو كې ځینې كافر دي او ځینې په تاسو كې مومن دي او الله هغو كارونو لره چې تاسو يې كوئ ښه ليدونكى دى');
INSERT INTO "abulsalam" VALUES(64,3,'64:3',' ده اسمانونه او ځمكه په حقه سره پیدا كړي دي او ستاسو صورتونه يې جوړ كړل، پس ستاسو صورتونه يې ډېر ښكلي كړي دي او خاص ده ته بېرته ورتګ دى');
INSERT INTO "abulsalam" VALUES(64,4,'64:4',' دى پوهېږي په هر هغه څه چې په اسمانونو اوځمكه كې دي او پوهېږي په هغه څه چې تاسو يې پټوئ او په هغه څه چې تاسويې څرګندوئ او الله د سینو په (پټو) خبرو هم ښه عالم دى');
INSERT INTO "abulsalam" VALUES(64,5,'64:5',' ایا تاسو ته د هغو خلقو خبر نه دى راغلى چې له دې نه مخكې كافران شوي وو، نو هغوى د خپل عمل سزا وڅكله او د هغوى لپاره ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(64,6,'64:6',' دا په دې سبب چې بېشكه شان دا دى چې دوى ته به د دوى رسولان له ښكاره معجزو سره راتلل، نو دوى به ویل چې ایا مونږ ته به بشر (انسانان) لاره ښیي، نو دوى كافر شول او مخ يې وګرځاوه او الله استغنا وكړه او الله ډېر بې پروا، ښه ستايل شوى دى');
INSERT INTO "abulsalam" VALUES(64,7,'64:7',' كافران شوي كسان (دا غلطه) عقیده ساتي چې دوى به له سره بیا راژوندي نه كړى شي، ته (دوى ته) ووایه: ولې نه! زما په خپل رب قسم دى چې تاسو به خامخا ضرور بیا راژوندي كړى شئ، بیا به تاسو ته خامخا ضرور د خپلو كړو عملونو خبر دركولى شي او دا (كارونه) الله ته ډېر اسان دي');
INSERT INTO "abulsalam" VALUES(64,8,'64:8',' نو تاسو په الله او د هغه په رسول او په هغې رڼا (قرآن) باندې ایمان راوړئ چې مونږ نازله كړې ده او الله په هغو كارونو چې تاسو يې كوئ؛ ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(64,9,'64:9',' (یاده كړه) هغه ورځ چې تاسو به دى (الله) د جمع كېدلو ورځې ته راجمع كړي، دغه د تاوان د ښكاره كېدو ورځ ده او هغه څوك چې په الله ایمان راوړي او نېك عمل وكړي (،نو) هغه به له هغه نه د هغه ګناهونه لرې كړي او داسې جنتونو ته به يې داخل كړي چې د هغو له لاندې ولې بهېږي، چې دوى به په هغو كې تل ترتله وي، دغه ډېره لویه كامیابي ده');
INSERT INTO "abulsalam" VALUES(64,10,'64:10',' او هغه كسان چې كافران شوي دي او زمونږ د ایتونو تكذیب يې كړى دى دغه كسان د اور والا دي، چې په ده كې به همېشه وي او هغه د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(64,11,'64:11',' هېڅ یو مصیبت نه رسېږي مګر د الله په حكم سره او هغه څوك چې په الله ایمان راوړي (، نو) هغه به د ده زړه ته هدایت وكړي او الله په هر شي باندې ښه عالم دى');
INSERT INTO "abulsalam" VALUES(64,12,'64:12',' او تاسو د الله اطاعت وكړئ او د رسول اطاعت وكړئ، نو كه تاسو وګرځېدئ، نو یقینًا زمونږ د رسول په ذمه خو يواځې ښكاره پېغام رسونه ده');
INSERT INTO "abulsalam" VALUES(64,13,'64:13',' الله (چې دى) نشته هېڅ لايق د عبادت مګر هم دى دى او لازم ده چې مومنان خاص په الله باندې توكل وكړي');
INSERT INTO "abulsalam" VALUES(64,14,'64:14',' اى هغو كسانو چې ایمان يې راوړى دى! بېشكه ستاسو په ښځو او اولادونو كې ځينې ستاسو دښمنان دي، نو تاسو له دوى نه محتاط اوسئ او كه چېرې تاسو معافي وكړئ او تېر شئ او بخښنه وكړئ، نو بېشكه چې الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(64,15,'64:15',' بېشكه هم دا خبره ده چې ستاسو مالونه او ستاسو اولادونه ازموینه (او فتنه) ده او الله (چې دى) له هغه سره ډېر لوى اجر دى');
INSERT INTO "abulsalam" VALUES(64,16,'64:16',' نو تاسو له الله نه ووېرېږئ څومره چې تاسو كولى شئ او واورئ او اطاعت وكړئ او انفاق كوئ، (دا به) ستاسو د ځانونو لپاره خیر وي او هغه څوك چې د خپل نفس له د بخیلۍ نه وساتل شو، نو هم دغه خلق كامیاب دي');
INSERT INTO "abulsalam" VALUES(64,17,'64:17',' كه چېرې تاسو الله ته قرض وركړئ، ښه قرض، (نو) دى به دغه (قرض) تاسو لپاره دوه چنده كړي او تاسو ته به بخښنه وكړي او الله ښه قدردان، ښه تحمل والا دى');
INSERT INTO "abulsalam" VALUES(64,18,'64:18',' د غیبو او ښكاره و عالم دى، ښه غالب، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(65,1,'65:1',' اى نبي! (دوى ته ووایه) كله چې تاسو (خپلې) ښځې طلاقول غواړئ، نو دوى په خپل (مناسب) وخت د عدت كې طلاقې كړئ او عدت وشمېرئ او له خپل رب الله نه ووېرېږئ (او) تاسو دغه (خپلې ښځې) د دوى له كورونو نه مه وباسئ او دوى دې (پخپله) هم نه وځي مګر په دې حال كې چې دوى ښكاره بې حیايي وكړي او دغه د الله حدونه دي او هر څوك چې د الله له حدونو نه تېر شي، نو یقینًا ده په خپل ځان ظلم وكړ، (اى طلاق وركوونكیه!) ته نه پوهېږي، كېدى شي چې الله له دغه نه وروسته كوم (ښه) كار پیدا كړي');
INSERT INTO "abulsalam" VALUES(65,2,'65:2',' نو كله چې دغه (ښځې) خپلې نېټې ته رسېږي، نو (یا) خو تاسو دوى په نېكه طریقه (په رجوع كولو سره په خپل نكاح كې) وساتئ، یا په نېكه طریقه له دوى نه جدا شئ او په تاسو كې دوه عدل والا كسان (په دې كار) ګواهان ونیسئ او تاسو (اى ګواهانو) ګواهي د الله لپاره ادا كړئ، دا (حكم چې دى) په ده سره هغه چا ته نصیحت كاوه شي چې په الله او د اخرت په ورځ ایمان لري او هغه څوك چې له الله نه ووېرېږي، (نو) هغه به د ده لپاره د وتلو څه ځاى پیدا كړي');
INSERT INTO "abulsalam" VALUES(65,3,'65:3',' او ده ته به له هغه ځایه رزق وركړي چې دى به ګمان هم نه كوي او هغه څوك چې په الله باندې توكل وكړي، نو همدى د ده لپاره بس (او كافی) دى، بېشكه الله خپل كار ته رسېدونكى دى، یقینًا الله د هر شي لپاره یوه اندازه مقرر كړې ده');
INSERT INTO "abulsalam" VALUES(65,4,'65:4',' او هغه ښځې چې ستاسو په ښځو كې د حیض له راتلو نه ناامېده شوي دي كه چېرې تاسو شك وكړئ، نو د دوى عدت درې میاشتې دى او هغه (جینكۍ) چې لا حايضه شوې نه وي او امېدوارې (حامله) ښځې (چې دي) د دوى عدت دا دى چې حمل (بچى) وزېږوي او هغه څوك چې له الله نه ووېرېږي، هغه به د ده لپاره په كار كې ډېره اساني پیدا كړي');
INSERT INTO "abulsalam" VALUES(65,5,'65:5',' دغه د الله حكم دى، چې تاسو ته يې دغه نازل كړى دى او هغه څوك چې له الله نه ووېرېږي، هغه به له ده نه د ده ګناهونه لرې كړي او د ده لپاره به اجر ډېر لوى كړي');
INSERT INTO "abulsalam" VALUES(65,6,'65:6',' تاسو دغه ښځې (په عدت كې) د خپل طاقت سره سم د هغه ځاى په څه حصه كې اوسوئ چې تاسو (په كې) اوسېږئ او تاسو دوى ته ضرر مه رسوئ، د دې لپاره چې تاسو په دوى باندې تنګسیا راولئ او كه چېرې دغه (ښځې) امېدوارې (حامله) وي، بیا نو تاسو په دوى باندې خرچه كوئ تر هغه پورې چې دوى خپل حمل وزېږوي، نو كه دوى ستاسو (اولاد) لپاره تى وركړي، نو تاسو دوى ته د دوى مزدوري وركړئ او تاسو په خپل مینځ كې په ښې طریقې سره یو له بله سره مشوره كوئ او كه تاسو یو له بل سره سختي وكړئ، نو ده (اولاد) ته به عنقریب بله ښځه تى وركړي');
INSERT INTO "abulsalam" VALUES(65,7,'65:7',' د فراخۍ والا دې له خپلې فراخۍ نه خرچه وكړي او هغه څوك چې په هغه باندې خپل رزق تنګ كړى شوى وي، نو هغه دې له هغه (مال) نه څه خرچ كړي چې الله ده ته وركړى دى، الله هېڅ یونفس نه مكلف كوي مګر هومره چې ده ته يې وركړى دى، ژر ده چې الله به له تنګۍ نه پس اساني راولي');
INSERT INTO "abulsalam" VALUES(65,8,'65:8',' او ډېر كلي وو چې د خپل رب او د هغه د رسولانو له حكم نه يې سركشي كړې وه، نو مونږ له هغوى سره حساب وكړ، ډېر سخت حساب كول او مونږ هغوى ته عذاب وركړ ډېر ناكاره عذاب وركول');
INSERT INTO "abulsalam" VALUES(65,9,'65:9',' نو هغوى د خپل كار سزا وڅكله او د دوى د كار انجام زیان (او تاوان) شو');
INSERT INTO "abulsalam" VALUES(65,10,'65:10',' الله د دوى لپاره ډېر سخت عذاب تیار كړى دى، نو تاسو له الله نه ووېرېږئ اى د خالصو عقلونو خاوندانو، هغه كسان چې ایمان يې راوړى دى، یقینًا الله تاسو ته ذكر (قرآن) نازل كړى دى');
INSERT INTO "abulsalam" VALUES(65,11,'65:11',' (او) رسول (يې رالېږلى دى) چې تاسو ته د الله ایتونه لولي، چې ښه څرګندوونكي دي، د دې لپاره چې دغه (الله) هغو كسانو لره چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، له تیارو نه رڼا ته وباسي او هغه څوك چې په الله ایمان راوړي او نېك عمل وكړي (نو) دى به دغه كس داسې جنتونو ته داخل كړي چې د هغو له لاندې ولې بهېږي، چې دوى به په هغو كې تل ترتله وي، یقینًا الله د ده لپاره رزق ډېر ښه كړى دى');
INSERT INTO "abulsalam" VALUES(65,12,'65:12',' الله هغه ذات دى چې اوه اسمانونه يې پیدا كړي دي او له ځمكې نه هم د دغو په مثل، (د الله) امر د دوى په مینځ كې نازلېږي، د دې لپاره چې تاسو پوه شي چې یقینًا الله په هر شي باندې ښه قادر دى او دا چې یقینًا الله د علم په لحاظ هر شى راګېر كړى دى');
INSERT INTO "abulsalam" VALUES(66,1,'66:1',' اى نبي! ته ولې هغه شى حراموې (بندوې) چې الله تا ته حلال كړى دى؟ ته د خپلو ښځو خوشحالول (او راضی كول) غواړې او الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(66,2,'66:2',' یقینًا الله تاسو لپاره ستاسو د قسمونو حلالوالى مقرر كړى دى او الله ستا مددګار دى، او هم دى ښه پوه، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(66,3,'66:3',' او (یاد كړه) هغه وخت چې نبي په خپلو ښځو كې كومې ته په راز كې یوه خبره وویله، نو كله چې هغې په دې سره (بلې ته) خبر وركړ او الله ده ته دغه (خبره) ښكاره كړه (نو) ده ځینې خبره وښودله او له ځینې نه يې اعراض وكړ، نو كله چې ده هغه (ښځه) په دې خبره كړه (نو) هغې وویل: ته په دې باندې چا خبر كړې؟ ده وویل: ما ته ډېر پوه (او) ښه خبردار ذات خبر راكړ');
INSERT INTO "abulsalam" VALUES(66,4,'66:4',' كه چېرې تاسو دواړه الله ته توبه وباسئ، نو یقینًا ستاسو د دواړو زړونه مايله شوي دي او كه د ده (نبي) په خلاف تاسو یوه له بلې سره مدد وكړئ، نو بېشكه چې الله، همغه د ده مددګار دى او جبریل او صالح مومنان او ملايك له دې نه پس مددګاران دي');
INSERT INTO "abulsalam" VALUES(66,5,'66:5',' كه دى تاسو ته طلاق دركړي (، نو) امېد دى چې د ده رب به ده ته په بدل كې له تاسو نه ډېرې غوره ښځې وركړي، چې مسلمانانې، مومنانې، اطاعت ګزارې، توبه اېستونكې، عبادت كوونكې، روژه نیوونكې، (هجرت كوونكې) څښتن لیدلې او پېغلې به وي');
INSERT INTO "abulsalam" VALUES(66,6,'66:6',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو خپل ځانونه او خپل اهل له داسې اور نه بچ وساتئ چې د هغه خشاك خلق او كاڼي دي، په ده باندې ډېر سخت خویه، قوتناك ملايك مقرر دي، چې دوى په هغه څه كې د الله نافرمانۍ نه كوي چې دوى ته يې حكم كړى وي او دوى هغه څه كوي چې دوى ته يې حكم كولى شي');
INSERT INTO "abulsalam" VALUES(66,7,'66:7',' اى هغو كسانو چې كافران شوي دي! تاسو نن ورځ عذرونه مه وړاندې كوئ، بېشكه همدا خبره ده چې تاسو ته به د هغه څه بدله دركولى شي چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(66,8,'66:8',' اى هغو كسانو چې ایمان يې راوړى دى! تاسو الله ته توبه وباسئ خالصه رښتینې توبه، امېد دى چې ستاسو رب به له تاسو نه ستاسو ګناهونه ورژوي او تاسو به داسې جنتونو ته داخل كړي چې د هغو له لاندې ولې بهېږي، په هغه ورځ كې چې الله به نبي او هغه كسان چې له ده سره يې ایمان راوړى دى رسوا نه كړي، د دوى رڼا به د دوى په وړاندې او د دوى په ښي اړخ منډې وهي، دوى به وايي: اى زمونږه ربه! ته مونږ ته زمونږ رڼا پوره كړه او مونږ وبخښه، بېشكه ته په هر شي باندې ښه قادر يې');
INSERT INTO "abulsalam" VALUES(66,9,'66:9',' اى نبي! ته له كافرانو او منافقانو سره جهاد كوه او په دوى باندې سختي كوه او د دوى استوګنه جهنم دى او هغه د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(66,10,'66:10',' الله د هغو كسانو لپاره مثال بیانوي چې كافران شوي دي: د نوح ښځه او د لوط ښځه، دا دواړه زمونږ په بنده ګانو كې ددوه نېكو بنده ګانو لاندې وې، نو دغو دواړو (ښځو) له هغو سره خیانت وكړ، نو هغوى د الله په مقابله كې د دوى هېڅ په كار رانغلل او دغو دواړو (ښځو) ته وویل شول چې تاسو اور ته له ننوتونكو سره ننوځئ');
INSERT INTO "abulsalam" VALUES(66,11,'66:11',' او الله د ایمان راوړونكو لپاره یو بل مثال بیانوي: د فرعون ښځه ده، كله چې هغې وویل: اى زما ربه! ته ما ته زما لپاره خپلې خوا كې په جنت كې یو كور جوړ كړه او ته ما له فرعون او د هغه له عمل نه خلاصه كړه او ته ما ته له ظالم قوم نه نجات راكړه');
INSERT INTO "abulsalam" VALUES(66,12,'66:12',' او د عمران لور مریم ده، هغه چې د خپل شرم ځاى حفاظت يې كړى و، پس مونږ له خپل روح نه په ده كې پوكى وواهه او دې د خپل رب د كلمو (خبرو) او د هغه د كتابونو تصدیق كړى و او دا له حكم منونكو ځنې وه');
INSERT INTO "abulsalam" VALUES(67,1,'67:1',' ډېر بركت والا دى هغه ذات چې خاص د هغه په لاس كې بادشاهي ده، او هغه په هر شي باندې ښه قادر دى');
INSERT INTO "abulsalam" VALUES(67,2,'67:2',' هغه ذات چې مرګ او ژوند يې پیدا كړى دى، د دې لپاره چې تاسو وازمايي چې په تاسو كې د عمل په لحاظ كوم یو ډېر ښه دى او هم دى ښه غالب، ډېر بخښونكى دى');
INSERT INTO "abulsalam" VALUES(67,3,'67:3',' هغه (الله) چې اوه اسمانونه يې طبقې طبقې (لاندې باندې) پیدا كړي دي، ته به د رحمٰن په پیداكونه كې هېڅ خلل (او فرق)، ونه وینې، پس سترګې بېرته راواړه، ایا ته كوم څیرې والى (او چاود) وینې ');
INSERT INTO "abulsalam" VALUES(67,4,'67:4',' بیا سترګې راواړه دوه ځلې، تا ته به سترګې بېرته راوګرځي، چې ذلیله به وي، په دې حال كې چې ستړې ستومانه به وي ');
INSERT INTO "abulsalam" VALUES(67,5,'67:5',' او یقینًا یقینًا مونږ دا تر ټولو نژدې اسمان په ډېوو (ستوریو) سره ښايسته كړى دى او مونږ دغه (ډېوې، یعنې ستوري) د شیطانانو د وېشتلو لپاره ذریعې جوړې كړې دي او مونږ د دوى لپاره ډېر سوځوونكى عذاب تیار كړى دى ');
INSERT INTO "abulsalam" VALUES(67,6,'67:6',' او د هغو كسانو لپاره چې په خپل رب باندې كافران شوي دي د جهنم عذاب دى او هغه د ورتلو بد ځاى دى');
INSERT INTO "abulsalam" VALUES(67,7,'67:7',' كله چې دوى په ده كې وغورځول شي دوى به د ده (لكه د خر) بد اواز واوري، په داسې حال كې چې دا به خوټكېږي ');
INSERT INTO "abulsalam" VALUES(67,8,'67:8',' نژدې به وي چې دى له غصې نه ټوټې شي، هر وارې چې په ده كې یوه ډله وغورځول شي (نو) له دوى نه به د دغه (دوزخ) څوكیداران پوښتنه وكړي چې ایا تاسو ته وېروونكى (پېغمبر) نه و راغلى؟ ');
INSERT INTO "abulsalam" VALUES(67,9,'67:9',' دوى به وايي: ولې نه! یقینًا مونږ ته وېروونكى راغلى و، پس مونږ (د هغه) تكذیب وكړ (هغه مو دروغجن وګاڼه) او مونږ وویل چې الله هېڅ شى هم نه دى نازل كړى، نه یئ تاسو مګر په ډېره لویه ګمراهۍ كې ');
INSERT INTO "abulsalam" VALUES(67,10,'67:10',' او دوى به وايي: كه چېرې مونږ اورېدل كولى، یا مو له عقل نه كار اخيستلى (نو) مونږ به (نن) د دوزخ په ملګرو كې نه وى ');
INSERT INTO "abulsalam" VALUES(67,11,'67:11',' نو دوى د خپلو ګناهونو اقرار وكړ، نو د دوزخ والاو لپاره (د الله له رحمت نه) لرې والى دى ');
INSERT INTO "abulsalam" VALUES(67,12,'67:12',' بېشكه هغه كسان چې له خپل رب نه په غیبو (نالېدلو) سره وېرېږي، د دوى لپاره مغفرت او ډېر لوى اجر دى ');
INSERT INTO "abulsalam" VALUES(67,13,'67:13',' او تاسو خپله خبره پټه وكړئ، یا تاسو هغه ښكاره وكړئ، بېشكه هغه (الله) د سینو په پټو خبرو هم ښه عالم دى ');
INSERT INTO "abulsalam" VALUES(67,14,'67:14',' ایا هغه ذات به نه پوهېږي چې يې (هر څه) پیدا كړي دي (ولې نه!) او هم دى ډېر باریك بین، ښه خبردار دى ');
INSERT INTO "abulsalam" VALUES(67,15,'67:15',' دى همغه ذات دى چې تاسو لپاره يې ځمكه تابع ګرځولې ده، نو تاسو د دې په اوږو (اطرافو، لارو) كې ګرځئ او د دغه (الله) له رزق نه خورئ او خاص ده ته (ستاسو) بیا ژوندي راپورته كېدل دي ');
INSERT INTO "abulsalam" VALUES(67,16,'67:16',' ایا تاسو په امن (او بې وېرې) شوي يئ له هغه ذات نه چې په اسمان كې دى، له دې نه چې هغه تاسو په ځمكه كې ښكته ننباسي، بیا دغه (ځمكه) ناڅاپه لړزان شروع كړي ');
INSERT INTO "abulsalam" VALUES(67,17,'67:17',' ایا تاسو له هغه ذات، نه چې په اسمان كې دى په امن (او بې وېرې) شوي یئ، له دې نه چې پر تاسو باندې د كاڼو باران راخوشې كړي، نو چې تاسو به ژر پوه شئ چې زما وېرول څنګه دي ');
INSERT INTO "abulsalam" VALUES(67,18,'67:18',' او یقینًا یقینًا هغو كسانو (د خپلو رسولانو) تكذیب كړى و چې له دغو خلقو نه مخكې وو، نو زما انكار او عذاب څنګه وو؟ ');
INSERT INTO "abulsalam" VALUES(67,19,'67:19',' او ایا دوى د خپل (سر) له پاسه مرغانو ته نه دي كتلي، په داسې حال كې چې خپل وزرونه پرانستونكي وي او ټولوي يې، دوى (له پرېوتلو نه) نه راټینګوي مګر رحمن ذات، بېشكه هغه هر شي لره ښه ليدونكى دى ');
INSERT INTO "abulsalam" VALUES(67,20,'67:20',' ایا څوك دى هغه كس (اى مشركانو!) چې هغه ستاسو لپاره لښكر دى چې له رحمٰن نه غیر به ستاسو مدد وكړي، كافران نه دي مګر په دوكه (او غولېدلو) كې ');
INSERT INTO "abulsalam" VALUES(67,21,'67:21',' ایا څوك دى هغه ذات چې تاسو ته به روزي دركړي كه چېرې هغه (الله) خپل رزق بند كړي بلكې دوى په سركشۍ او تېښته كې ننوتلي (او پاخه شوي) دي');
INSERT INTO "abulsalam" VALUES(67,22,'67:22',' ایا نو هغه څوك چې په خپل مخ باندې نسكور ځي ښه لار موندونكى دى، يا هغه څوك چې په نېغه لار سم برابر روان دى؟');
INSERT INTO "abulsalam" VALUES(67,23,'67:23',' ته (دوى ته) ووایه: دغه (الله) هغه ذات دى چې تاسو يې پیدا كړي یئ او تاسو لپاره يې غوږونه او سترګې او زړونه پیدا كړي دي (خو) تاسو ډېر لږ شكر وباسئ ');
INSERT INTO "abulsalam" VALUES(67,24,'67:24',' ته (دوى ته) ووایه چې دغه (الله) هغه ذات دى چې تاسو يې په ځمكه كې خواره كړي یئ او خاص ده ته به تاسو ورجمع كولى شئ ');
INSERT INTO "abulsalam" VALUES(67,25,'67:25',' او دوى وايي چې دغه (د قیامت) وعده به كله وي، كه تاسو رښتیني یئ؟ ');
INSERT INTO "abulsalam" VALUES(67,26,'67:26',' ته (دوى ته) ووایه چې بېشكه (د قیامت) علم خو يواځې الله ته دى او بېشكه زه خو يواځې ښكاره وېروونكى یم');
INSERT INTO "abulsalam" VALUES(67,27,'67:27',' نو كله چې دوى دغه (عذاب) ډېر نژدې وویني (نو) د هغو كسانو مخونه به بدشكله (او تور) شي چې كافران شوي وو او وبه ویل شي چې دا هغه (عذاب) دى چې تاسو به دا ژر غوښته ');
INSERT INTO "abulsalam" VALUES(67,28,'67:28',' (اى نبي!) ته (دوى ته) ووایه: تاسو ما ته خبر راكړئ! كه چېرې الله ما او هغه كسان چې زما سره دي، هلاك (او مړه) كړي، یا پر مونږ رحم وكړي، نو څوك دى چې كافران به له ډېر دردونكي عذاب نه بچ كړي؟ ');
INSERT INTO "abulsalam" VALUES(67,29,'67:29',' ته (دوى ته) ووایه: هم دغه (الله) رحمٰن دى، مونږ په ده ایمان راوړى دى او مونږ خاص په ده باندې توكل كړى دى، نو تاسو به ژر پوه شئ چې هغه څوك دى چې په ښكاره ګمراهۍ كې دى ');
INSERT INTO "abulsalam" VALUES(67,30,'67:30',' ته (دوى ته) ووایه چې تاسو ما ته خبر راكړئ! كه چېرې ستاسو اوبه (په ځمكه كې) ډېرې ښكته لاړې شي، نو څوك دى چې تاسو ته به روانې اوبه راولي؟');
INSERT INTO "abulsalam" VALUES(68,1,'68:1',' نون، قسم دى په قلم او په هغه څه چې دوى يې لیكي');
INSERT INTO "abulsalam" VALUES(68,2,'68:2',' چې ته د خپل رب په نعمت (او فضل) سره هېڅ لېونى نه يې ');
INSERT INTO "abulsalam" VALUES(68,3,'68:3',' او بېشكه چې خاص تا لره یقینًا داسې اجر دى چې قطع كولى به نشي ');
INSERT INTO "abulsalam" VALUES(68,4,'68:4','او بېشكه چې ته یقینًا په ډېرو لویو اخلاقو باندې (فايز) يې ');
INSERT INTO "abulsalam" VALUES(68,5,'68:5',' پس ژر به ته ووینې (اى نبي!) او دوى به هم وویني ');
INSERT INTO "abulsalam" VALUES(68,6,'68:6',' چې په تاسو كې كوم یو لېونى دى ');
INSERT INTO "abulsalam" VALUES(68,7,'68:7',' بېشكه ستا رب په هغه چا ښه پوه دى چې د ده له لارې نه ګمراه شوى دى او هم دى په هدایت موندونكو باندې ښه عالم دى ');
INSERT INTO "abulsalam" VALUES(68,8,'68:8',' نو ته د تكذیب كوونكو خبره مه منه ');
INSERT INTO "abulsalam" VALUES(68,9,'68:9',' دوى خوښوي چې كاشكې ته نرم شې، نو دوى به هم نرم شي ');
INSERT INTO "abulsalam" VALUES(68,10,'68:10',' او ته د هر ډېر سوګند خوړونكي، سپك، خبره مه منه ');
INSERT INTO "abulsalam" VALUES(68,11,'68:11',' چې ډېر عیب ویونكى، چغلۍ لره ډېر چلوونكى دى');
INSERT INTO "abulsalam" VALUES(68,12,'68:12',' خير لره ډېر منع كوونكى، له حده تېرېدونكى، سخت ګناهګار دى ');
INSERT INTO "abulsalam" VALUES(68,13,'68:13',' ډېر بد خویه دى (او) له دغو (عیبونو) نه پس بد اصله (حرامي هم) دى ');
INSERT INTO "abulsalam" VALUES(68,14,'68:14',' په دې وجه چې دى د مال او زامنو والا دى ');
INSERT INTO "abulsalam" VALUES(68,15,'68:15',' كله چې ده ته زمونږ ایتونه ولوستل شي (نو) دى وايي: (دا) د ړومبنو خلقو باطلې قصې دي');
INSERT INTO "abulsalam" VALUES(68,16,'68:16',' ژر ده چې مونږ به د ده په خلتم (پوزه) باندې داغ كېږدو');
INSERT INTO "abulsalam" VALUES(68,17,'68:17',' بېشكه مونږ دوى وازمایل، لكه چې مونږ د باغ خاوندان ازمایلي وو، كله چې هغوى قسمونه وخوړل چې دوى به خامخا ضرور دغه (د باغ مېوې) پرې كړي، په دې حال كې چې سبا كوونكي به وي');
INSERT INTO "abulsalam" VALUES(68,18,'68:18',' او دوى استثنا نه كوله (ان شاء الله يې ونه وایه) ');
INSERT INTO "abulsalam" VALUES(68,19,'68:19',' نو په دغه (باغ) باندې ستا د رب له جانبه یوه طواف كوونكې بلا راچاپېره شوه، په داسې حال كې چې دوى ویده وو');
INSERT INTO "abulsalam" VALUES(68,20,'68:20',' نو دغه (باغ داسې) شو، لكه رېبل شوى فصل');
INSERT INTO "abulsalam" VALUES(68,21,'68:21',' نو دوى یو بل ته اواز وكړ، په داسې حال كې چې سبا كوونكي وو');
INSERT INTO "abulsalam" VALUES(68,22,'68:22',' داسې چې خپل فصل ته سبا وختي ورشئ كه چېرې تاسو لَو كوونكي یئ ');
INSERT INTO "abulsalam" VALUES(68,23,'68:23',' نو دوى روان شول، په داسې حال كې چې یو له بل سره يې پټې پټې خبرې كولې ');
INSERT INTO "abulsalam" VALUES(68,24,'68:24',' (داسې) چې دې (باغ) ته دې پر تاسو باندې نن ورځ بېخي هېڅ مسكین داخل نه شي ');
INSERT INTO "abulsalam" VALUES(68,25,'68:25',' او دوى د نه وركولو په ارادې سره سهار لاړل، په داسې حال كې چې دوى (په خپل خیال كې) قادر وو ');
INSERT INTO "abulsalam" VALUES(68,26,'68:26',' نو دوى چې كله هغه (باغ) ولیده، ويې ويل:بېشكه مونږ یقینًا لار وركي یو ');
INSERT INTO "abulsalam" VALUES(68,27,'68:27',' بلكې مونږ محروم كړى شوي یو ');
INSERT INTO "abulsalam" VALUES(68,28,'68:28',' د دوى منځني (غوره)كس وویل: ایا ما تاسو ته نه و ویلي! ولې تاسو تسبیح نه وایئ ');
INSERT INTO "abulsalam" VALUES(68,29,'68:29',' (نو) دوى وویل: پاكي ده زمونږ رب لره، بېشكه مونږ ظالمان وو ');
INSERT INTO "abulsalam" VALUES(68,30,'68:30',' نو د دوى ځینو ځینو نورو ته مخ واړوه، چې یو بل يې ملامته كول ');
INSERT INTO "abulsalam" VALUES(68,31,'68:31',' دوى وویل: اى زمونږ هلاكته! یقینًا مونږ سركشان وو ');
INSERT INTO "abulsalam" VALUES(68,32,'68:32',' امېد دى چې زمونږ رب به مونږ ته له دې (باغ) نه غوره په بدل كې راكړي، بېشكه مونږ خپل رب ته رغبت كوونكي یو ');
INSERT INTO "abulsalam" VALUES(68,33,'68:33',' همداسې عذاب وي او یقینًا د اخرت عذاب تر ټولو لوى دى، كه چېرې دوى پوهېدلى ');
INSERT INTO "abulsalam" VALUES(68,34,'68:34',' بېشكه د پرهېزګارو لپاره د خپل رب په نیز د نعمتونو جنتونه دی');
INSERT INTO "abulsalam" VALUES(68,35,'68:35',' ايا نو مونږ به مسلمانان د مجرمانو په شان كړو؟ ');
INSERT INTO "abulsalam" VALUES(68,36,'68:36',' په تاسو څه شوي دي؟ تاسو څنګه فیصله كوئ! ');
INSERT INTO "abulsalam" VALUES(68,37,'68:37',' ایا تاسو سره كتاب دى چې په هغه كې تاسو (دا) لولئ؟ ');
INSERT INTO "abulsalam" VALUES(68,38,'68:38',' چې بېشكه تاسو لپاره په دغه (كتاب) كې هغه څه دي چې تاسو يې خوښوئ ');
INSERT INTO "abulsalam" VALUES(68,39,'68:39',' ایا تاسو لپاره زمونږ په ذمه څه قسمونه (وعدې) شته چې د قیامت د ورځې پورې رسېدونكي دي چې یقینًا تاسو لپاره به خامخا هغه څه وي چې تاسو يې فیصله كوئ ');
INSERT INTO "abulsalam" VALUES(68,40,'68:40',' ته له دوى نه پوښتنه وكړه چې په دوى كې كوم یو د دغه كار ضامن دى؟');
INSERT INTO "abulsalam" VALUES(68,41,'68:41',' ایا د دوى لپاره شریكان شته دى، نو دوى دې خپل (دغه) شریكان راولي، كه چېرې دوى رښتیني وي ');
INSERT INTO "abulsalam" VALUES(68,42,'68:42',' او (یاده كړه) هغه ورځ چې پنډۍ به بربنډه كړى شي او دوى به سجدې كولو ته راوبلل شي، نو دوى به توان ونه لري ');
INSERT INTO "abulsalam" VALUES(68,43,'68:43',' په داسې حال كې چې د دوى سترګې به ذلیله (او ټیټې) وي، دوى به ذلت راګېر كړي وي او یقینًا دوى به سجدې كولو ته رابللى شول، په داسې حال كې چې روغ رمټ به وو ');
INSERT INTO "abulsalam" VALUES(68,44,'68:44',' نو ته ما او هغه څوك پرېږده چې د دغه حدیث (قرآن) تكذیب كوي، ژر به مونږ دوى په استدراج كې واچوو (ورو به يې ورو راونيسو) له هغه ځایه چې دوى به نه پوهېږي ');
INSERT INTO "abulsalam" VALUES(68,45,'68:45',' او زه دوى ته مهلت وركوم، بېشكه زما حیله (چل) مضبوط (او محكم) دى ');
INSERT INTO "abulsalam" VALUES(68,46,'68:46',' ایا ته له دوى نه مزدوري غواړې، نو دوى له (دغه) تاوانه، درانه كړى شوي دي ');
INSERT INTO "abulsalam" VALUES(68,47,'68:47',' ایا له دوى سره علم غیب دى، نو چې دوى يې لیكي ');
INSERT INTO "abulsalam" VALUES(68,48,'68:48',' نو ته د خپل رب حكم ته صبر كوه او د كب (مهي) والا (يونس) په شان مه كېږه، كله چې ده اواز وكړ، په داسې حال كې چې له غم نه ډك و ');
INSERT INTO "abulsalam" VALUES(68,49,'68:49',' كه چېرې داسې نه وى چې ده ته د خپل رب مهرباني ورسېدله (نو) دى به خامخا په سپېره میدان كې غورځول شوى و، په داسې حال كې چې دى به مذموم (ملامت كړى شوى) و ');
INSERT INTO "abulsalam" VALUES(68,50,'68:50',' نو دى خپل رب غوره كړ، پس دى يې له صالحانو څخه وګرځاوه ');
INSERT INTO "abulsalam" VALUES(68,51,'68:51',' او بېشكه شان دا دى چې كافران شوي خلق نژدې وو چې تا په خپلو سترګو سره خامخا وښیوي، كله چې دوى ذكر (قرآن) واورېده او دوى وايي چې بېشكه دى خامخا لېونى دى');
INSERT INTO "abulsalam" VALUES(68,52,'68:52',' حال دا چې دغه (قرآن) نه دى مګر د عالمونو لپاره نصیحت');
INSERT INTO "abulsalam" VALUES(69,1,'69:1',' دغه (قیامت) خامخا واقع كېدونكى ');
INSERT INTO "abulsalam" VALUES(69,2,'69:2',' دغه لازمًا واقع كېدونكى څه شى دى ');
INSERT INTO "abulsalam" VALUES(69,3,'69:3',' او ته څه شي پوه كړې چې دغه قیامت څه شى دى ');
INSERT INTO "abulsalam" VALUES(69,4,'69:4',' ثمودیانو او عادیانو د ټكوونكي (قیامت) تكذیب كړى و ');
INSERT INTO "abulsalam" VALUES(69,5,'69:5',' نو هر چې ثمودیان وو، پس هغوى په سخت اواز سره هلاك كړى شول ');
INSERT INTO "abulsalam" VALUES(69,6,'69:6',' او هر چې عادیان وو، نو دوى په ډېر سخت اواز، له حده وتونكي باد سره هلاك كړى شول ');
INSERT INTO "abulsalam" VALUES(69,7,'69:7',' (الله) دغه (باد) په دوى باندې اوه شپې او اته ورځې متواتر (پرله پسې د دوى د هلاكت لپاره) مسخر كړى و، نو تا به دغه قوم په دغه (شپو ورځو، كورونو، باد) كې لویدلي مړه پراته لیدل، ګویاكې دوى د زړو كجورو تش ډډونه دي ');
INSERT INTO "abulsalam" VALUES(69,8,'69:8',' نو ایا ته دوى لره څه باقي پاتې شوي وینې ');
INSERT INTO "abulsalam" VALUES(69,9,'69:9',' او فرعون او هغه چا چې له ده نه مخكې وو او چپه كړى شویو كلیو غټ جرم (شرك) وكړ ');
INSERT INTO "abulsalam" VALUES(69,10,'69:10',' نو دوى د خپل رب د رسول نافرماني وكړه، نو هغه دوى ونیول، ډېر سخت نیول ');
INSERT INTO "abulsalam" VALUES(69,11,'69:11',' بېشكه مونږ، كله چې اوبه له حده ووتې، (نو) مونږ تاسو په بېړۍ كې سواره كړئ ');
INSERT INTO "abulsalam" VALUES(69,12,'69:12',' د دې لپاره چې مونږ دغه (بېړۍ) تاسو لپاره پند وګرځوو او یاد ساتونكي غوږونه دغه (پند) یاد ولري ');
INSERT INTO "abulsalam" VALUES(69,13,'69:13',' نو كله چې په شپېلۍ كې پوكى وكړى شي، یو پوكى ');
INSERT INTO "abulsalam" VALUES(69,14,'69:14',' او ځمكه او غرونه پورته كړى شي، پس دغه دواړه رېزه رېزه كړى شي، یو ځلې رېزه كول ');
INSERT INTO "abulsalam" VALUES(69,15,'69:15',' نو په دغې ورځ كې به واقع كېدونكى (قیامت) واقع شي ');
INSERT INTO "abulsalam" VALUES(69,16,'69:16',' او اسمان به وچوي، نو دغه (اسمان) به په دغې ورځ كې سست وي ');
INSERT INTO "abulsalam" VALUES(69,17,'69:17',' او ملايك به د ده د څنډو له پاسه وي او په دغې ورځ كې به ستا د رب عرش اتو (ملايكو) د دوى له پاسه اوچت كړى وي ');
INSERT INTO "abulsalam" VALUES(69,18,'69:18',' په دغې ورځ كې به تاسو وړاندې كولى شئ، ستاسو له پټو (عملونو) نه به هېڅ (شى) پټ پاتې نشي ');
INSERT INTO "abulsalam" VALUES(69,19,'69:19',' نو هر چې هغه څوك دى چې هغه ته خپل كتاب (عمل نامه) د ده په ښي لاس كې وركړى شوه، نو دى به وايي: چې واخلئ! زما عمل نامه ولولئ ');
INSERT INTO "abulsalam" VALUES(69,20,'69:20',' بېشكه ما دا یقین كاوه چې زه به خامخا له خپل حساب سره مېلاوېدونكى یم');
INSERT INTO "abulsalam" VALUES(69,21,'69:21',' نو دغه (كس) به په خوشاله ژوند كې وي ');
INSERT INTO "abulsalam" VALUES(69,22,'69:22',' په اوچت جنت كې ');
INSERT INTO "abulsalam" VALUES(69,23,'69:23',' چې د هغه مېوې به نژدې وي ');
INSERT INTO "abulsalam" VALUES(69,24,'69:24',' تاسو ښه په مزې سره خورئ او څښئ، د هغو عملونو په بدل كې چې تاسو په تېرو شویو ورځو كې مخكې لېږلي وو ');
INSERT INTO "abulsalam" VALUES(69,25,'69:25',' او هر چې هغه كس دى چې ده ته خپل كتاب د ده په چپ لاس كې وركړى شي، نو دى به وايي: اى كاشكې ما ته زما كتاب (عمل نامه) نه وى راكړى شوې ');
INSERT INTO "abulsalam" VALUES(69,26,'69:26',' او زه نه وى خبر شوى چې زما حساب څه دى ');
INSERT INTO "abulsalam" VALUES(69,27,'69:27',' اى كاشكې چې هغه (مرګ) فیصله كوونكى وى ');
INSERT INTO "abulsalam" VALUES(69,28,'69:28',' زما مال زما هېڅ په كار رانغى ');
INSERT INTO "abulsalam" VALUES(69,29,'69:29',' له ما نه زما بادشاهي برباده شوه ');
INSERT INTO "abulsalam" VALUES(69,30,'69:30',' (وبه ویل شي:) دى ونیسئ، پس دى په زلونو سره وتړئ ');
INSERT INTO "abulsalam" VALUES(69,31,'69:31',' بیا دى دوزخ ته داخل كړئ ');
INSERT INTO "abulsalam" VALUES(69,32,'69:32',' بیا په یو ځنځیر كې چې د هغه ګزونه (اوږدوالى) اویا ګزه وي، پس دى ننه باسئ ');
INSERT INTO "abulsalam" VALUES(69,33,'69:33',' بېشكه ده په ډېر لوى الله باندې ایمان نه راوړ');
INSERT INTO "abulsalam" VALUES(69,34,'69:34',' او مسكین ته په طعام وركولو يې ترغیب نه وركاوه ');
INSERT INTO "abulsalam" VALUES(69,35,'69:35',' نو د ده لپاره دلته نن ورځ هېڅ دوست نشته ');
INSERT INTO "abulsalam" VALUES(69,36,'69:36',' او نه څه طعام، سوا له غِسلین (د دوزخیانو د زخمونو د وینې او زوې) نه ');
INSERT INTO "abulsalam" VALUES(69,37,'69:37',' چې دغه نه خوري مګر (دغه) خطاكاران ');
INSERT INTO "abulsalam" VALUES(69,38,'69:38',' نو زه قسم خورم په هغو شیانو چې تاسو يې وینئ ');
INSERT INTO "abulsalam" VALUES(69,39,'69:39',' او په هغو چې تاسو يې نه وینئ ');
INSERT INTO "abulsalam" VALUES(69,40,'69:40',' بېشكه دا (قرآن) یقینًا د ډېر عزتمن رسول خبره ده ');
INSERT INTO "abulsalam" VALUES(69,41,'69:41',' او دا د كوم شاعر خبره بېخي نه ده، تاسو ډېر لږ ایمان راوړئ ');
INSERT INTO "abulsalam" VALUES(69,42,'69:42',' او نه د كاهن (ترویتي) خبره ده، تاسو ډېر لږ پند اخلئ');
INSERT INTO "abulsalam" VALUES(69,43,'69:43',' (دغه قرآن) د رب العلمین نازلول دي ');
INSERT INTO "abulsalam" VALUES(69,44,'69:44',' او كه ده پر مونږ باندې له ځانه ځینې خبرې جوړې كړې وى ');
INSERT INTO "abulsalam" VALUES(69,45,'69:45',' (نو) مونږ به د ده ښى لاس نیولى وى ');
INSERT INTO "abulsalam" VALUES(69,46,'69:46',' بیا به مونږ خامخا د ده د زړه رګ پرې كړى وى ');
INSERT INTO "abulsalam" VALUES(69,47,'69:47',' نو په تاسو كې به هیڅوك له ده نه منع كوونكي نه وو ');
INSERT INTO "abulsalam" VALUES(69,48,'69:48',' او بېشكه دا (قرآن) د پرهېزګارانو لپاره پند (او نصیحت) دى ');
INSERT INTO "abulsalam" VALUES(69,49,'69:49',' او بېشكه مونږ خامخا پوهېږو چې یقینًا په تاسو كې تكذیب كوونكي دي ');
INSERT INTO "abulsalam" VALUES(69,50,'69:50',' او بېشكه دغه (قرآن) په كافرانو باندې د پښېمانۍ سبب دى');
INSERT INTO "abulsalam" VALUES(69,51,'69:51',' او بېشكه دغه (قرآن) خامخا یقیني حق دى');
INSERT INTO "abulsalam" VALUES(69,52,'69:52',' نو ته د خپل عظیم رب د نوم تسبیح وایه');
INSERT INTO "abulsalam" VALUES(70,1,'70:1',' یو غوښتونكي هغه عذاب وغوښت چې واقع كېدونكى دى');
INSERT INTO "abulsalam" VALUES(70,2,'70:2',' په كافرانو باندې، دغه (عذاب) لره هیڅوك دفع كوونكى نشته');
INSERT INTO "abulsalam" VALUES(70,3,'70:3',' د الله له جانبه دى، چې د اوچتو زینومالك دى ');
INSERT INTO "abulsalam" VALUES(70,4,'70:4',' چې د ده په طرف پورته ملايك او روح ورخېژي، په هغې ورځ كې چې د هغې مقدار پنځوس زره كاله دى');
INSERT INTO "abulsalam" VALUES(70,5,'70:5',' نو ته صبر كوه، ډېر ښه صبر');
INSERT INTO "abulsalam" VALUES(70,6,'70:6',' بېشكه دوى (منكران) دغه (عذاب، قیامت) ډېر لرې ویني');
INSERT INTO "abulsalam" VALUES(70,7,'70:7',' او مونږه دى ډېر نژدې وینو');
INSERT INTO "abulsalam" VALUES(70,8,'70:8',' په هغې ورځ چې اسمان به د ويلې كړى شوې قلعي په شان شي');
INSERT INTO "abulsalam" VALUES(70,9,'70:9',' او غرونه به د رنګدارې وهل شوې وړۍ په شان شي ');
INSERT INTO "abulsalam" VALUES(70,10,'70:10',' او یو دوست به له بل دوست نه پوښتنه نه كوي ');
INSERT INTO "abulsalam" VALUES(70,11,'70:11',' حال دا چې دوى ته به هغوى ښودلى شي، مجرم (كافر) به ارزو كوي چې د دغې ورځې له عذابه خپل زامن په عوض كې وركړي ');
INSERT INTO "abulsalam" VALUES(70,12,'70:12',' او خپله ښځه او خپل ورور');
INSERT INTO "abulsalam" VALUES(70,13,'70:13',' او خپله هغه قبیله چې ده ته به يې ځاى وركاوه ');
INSERT INTO "abulsalam" VALUES(70,14,'70:14',' او هر هغه څوك چې په ځمكه كې دي ټول، بیا ده ته (دا كار) نجات وركړي ');
INSERT INTO "abulsalam" VALUES(70,15,'70:15',' كله هم داسې نه ده، بېشكه دغه (د دوزخ اور) لمبې وهونكى دى ');
INSERT INTO "abulsalam" VALUES(70,16,'70:16',' چې (د سر، لاسونو او پښو) پوستكي وېستونكى دى ');
INSERT INTO "abulsalam" VALUES(70,17,'70:17',' دا (دوزخ) به راغواړي هغه څوك چې شا كړې يې وي او مخ يې اړولى وي ');
INSERT INTO "abulsalam" VALUES(70,18,'70:18',' او جمع (د مال) يې كړې وي، نو ساتلى يې وي ');
INSERT INTO "abulsalam" VALUES(70,19,'70:19',' بېشكه انسان بې صبره پیدا كړى شوى دى ');
INSERT INTO "abulsalam" VALUES(70,20,'70:20',' كله چې ده ته شر (مصیبت) ورسېږي (نو) ډېر فریاد كوونكى وي ');
INSERT INTO "abulsalam" VALUES(70,21,'70:21',' او كله چې ده ته خیر (او خوشحالي) ورسېږي (نو)، ډېر منع كوونكى (او بخل كوونكى) وي ');
INSERT INTO "abulsalam" VALUES(70,22,'70:22',' (خو) له لمونځ ګزارو نه غیر ');
INSERT INTO "abulsalam" VALUES(70,23,'70:23',' هغه كسان چې دوى په خپلو لمونځونو باندې همېشه والى كوونكي وي ');
INSERT INTO "abulsalam" VALUES(70,24,'70:24',' او هغه كسان چې د دوى په مالونو كې معلومه برخه (مقرر) ده ');
INSERT INTO "abulsalam" VALUES(70,25,'70:25',' د سوالګر او محروم لپاره ');
INSERT INTO "abulsalam" VALUES(70,26,'70:26',' او هغه كسان چې د بدلې د ورځې تصدیق (او باور) كوي ');
INSERT INTO "abulsalam" VALUES(70,27,'70:27',' او هغه كسان چې دوى د خپل رب له عذاب نه وېرېدونكي دي ');
INSERT INTO "abulsalam" VALUES(70,28,'70:28',' بېشكه د دوى د رب عذاب داسې دى چې بې وېرې كېدل ترې نشته ');
INSERT INTO "abulsalam" VALUES(70,29,'70:29',' او هغه كسان چې هغوى د خپل شرم ځایونو حفاظت كوونكي دي ');
INSERT INTO "abulsalam" VALUES(70,30,'70:30',' مګر له خپلو ښځو او خپلو وینځو نه، نو بېشكه دوى ملامت كړى شوي نه دي ');
INSERT INTO "abulsalam" VALUES(70,31,'70:31',' نو هغه څوك چې له دغو نه علاوه غواړي، نو هم دغه كسان له حده تېرېدونكي دي');
INSERT INTO "abulsalam" VALUES(70,32,'70:32',' او هغه كسان چې دوى د خپلو امانتونو او خپلو وعدو لحاظ ساتونكي دي ');
INSERT INTO "abulsalam" VALUES(70,33,'70:33',' او هغه كسان چې دوى په خپلو شهادتونو (ګواهیو) باندې قايم (او ولاړ) دي');
INSERT INTO "abulsalam" VALUES(70,34,'70:34',' او هغه كسان چې دوى په خپلو لمونځونو همېشه والى (او پابندي) كوي ');
INSERT INTO "abulsalam" VALUES(70,35,'70:35',' دغه كسان به په باغونو كې وي، عزتمن كړى شوي به وي ');
INSERT INTO "abulsalam" VALUES(70,36,'70:36',' نو (اى نبي!) په دغو كافران شویو كسانو څه شوي دي چې ستا په طرف منډې راوهونكي دي ');
INSERT INTO "abulsalam" VALUES(70,37,'70:37',' له ښي اړخ نه او له چپ اړخ نه ډلې ډلې ');
INSERT INTO "abulsalam" VALUES(70,38,'70:38',' ایا په دوى كې هر یو سړى دا طمع لري چې دى به د نعمتونو جنت ته ننه اېستل شي؟ ');
INSERT INTO "abulsalam" VALUES(70,39,'70:39',' هیڅكله نه! بېشكه همدا مونږ دوى له هغه څه نه پیدا كړي دي چې دوى يې پېژني ');
INSERT INTO "abulsalam" VALUES(70,40,'70:40',' نو زه د مشرقونو او مغربونو په رب قسم خورم چې بېشكه (او) یقینًا مونږ قادر یو ');
INSERT INTO "abulsalam" VALUES(70,41,'70:41',' په دې باندې چې مونږ له دوى نه غوره (مخلوق) په بدل كې راولو اومونږ عاجز كړى شوي نه یو ');
INSERT INTO "abulsalam" VALUES(70,42,'70:42',' نو ته دوى پرېږده چې په باطلو او عبثو خبرو كې مشغولېږي او لوبې كوي تر هغه پورې چې دوى له خپلې هغې ورځې سره ملاقات وكړي چې دوى ته يې وعده وركړى شوې ده ');
INSERT INTO "abulsalam" VALUES(70,43,'70:43',' هغه ورځ چې دوى به له قبرونو نه داسې په منډه (او تېزۍ) سره راوځي ګویاكې دوى هدف ته په منډه ځغلي ');
INSERT INTO "abulsalam" VALUES(70,44,'70:44',' په داسې حال كې چې د دوى سترګې به خوارې وي، دوى به ذلت (راګېر) او پټ كړي وي، دا هغه ورځ ده چې دوى ته به د دې وعده وركول كېده');
INSERT INTO "abulsalam" VALUES(71,1,'71:1',' بېشكه مونږ نوح خپل قوم ته په دې سره لېږلى و چې ته خپل قوم ووېروه، مخكې له دې چې په دوى ډېر دردوونكى عذاب راشي');
INSERT INTO "abulsalam" VALUES(71,2,'71:2',' (نو) ده وویل: اى زما قومه! بېشكه زه ستاسو لپاره ښكاره وېروونكى یم ');
INSERT INTO "abulsalam" VALUES(71,3,'71:3',' چې تاسو د الله عبادت كوئ او له ده نه ووېرېږئ او زما اطاعت وكړئ');
INSERT INTO "abulsalam" VALUES(71,4,'71:4',' دى به تاسو ته ستاسو ځینې ګناهونه وبخښي او ترټاكلې نېټې پورې به تاسو وروسته (او باقي پاتې) كړي، بېشكه د الله نېټه چې كله راشي(نو) وروسته كېدى نشي، كه چېرې تاسو پوهېږئ ');
INSERT INTO "abulsalam" VALUES(71,5,'71:5',' ده وویل: اى زما ربه! بېشكه ما شپه او ورځ خپل قوم (توحید ته) راوباله');
INSERT INTO "abulsalam" VALUES(71,6,'71:6',' نو دوى ته زما بلنې زیاته نه كړه مګر تېښته');
INSERT INTO "abulsalam" VALUES(71,7,'71:7',' او بېشكه زه چې هر وارې دوى د دې لپاره راوبلم چې ته دوى وبخښې (نو) دوى خپلې ګوتې په خپلو غوږونو كې ننباسي او په خپلو جامو سره ځان (مخ) پټ كړي او په كفر باندې) اصرار (او همېشه والى) كوي او لويي كوي ډېر غټه لويي');
INSERT INTO "abulsalam" VALUES(71,8,'71:8',' بیا یقینًا ما دوى ښه په جار (په اوچت اواز سره) راوبلل');
INSERT INTO "abulsalam" VALUES(71,9,'71:9',' بیا یقینًا ما دوى ته څرګند (او ښكاره) دعوت وركړ او ما دوى ته په پټه هم دعوت وركړ، ښه په پټه');
INSERT INTO "abulsalam" VALUES(71,10,'71:10',' نو ما (دوى ته) وویل: تاسو له خپل رب نه بخښنه وغواړئ بېشكه دى ډېر بخښونكى دى');
INSERT INTO "abulsalam" VALUES(71,11,'71:11',' دى به په تاسو ښه ورېدونكى باران راخوشې كړي');
INSERT INTO "abulsalam" VALUES(71,12,'71:12',' او تاسو ته به مالونه او زامن درزیات كړي، او تاسو ته به باغونه پیدا كړي او تاسو ته به ولې پیدا كړي');
INSERT INTO "abulsalam" VALUES(71,13,'71:13',' په تاسو څه شوي دي چې د الله لپاره د لويي عقیده نه ساتئ؟');
INSERT INTO "abulsalam" VALUES(71,14,'71:14',' حال دا چې ده تاسو قسما قسم پیدا كړي یئ');
INSERT INTO "abulsalam" VALUES(71,15,'71:15',' ایا تاسو نه دي كتلي چې الله اوه اسمانونه لاندې باندې پیدا كړي');
INSERT INTO "abulsalam" VALUES(71,16,'71:16',' او په دغو كې يې سپوږمۍ روښانه جوړه كړه او لمر يې بَله (ځلېدونكې) ډيوه جوړه كړه');
INSERT INTO "abulsalam" VALUES(71,17,'71:17',' او الله تاسو په خاصو زرغونولو سره له ځمكې نه رازرغون كړئ');
INSERT INTO "abulsalam" VALUES(71,18,'71:18',' بیا به دى تاسو په دې كې ننباسي او راوبه موباسي په خاصو را اېستلو سره');
INSERT INTO "abulsalam" VALUES(71,19,'71:19',' او الله تاسو لپاره ځمكه فرش جوړه كړې ده');
INSERT INTO "abulsalam" VALUES(71,20,'71:20',' د دې لپاره چې تاسو د دې په ارتو لویو لارو باندې لاړ شئ');
INSERT INTO "abulsalam" VALUES(71,21,'71:21',' نوح وویل: اى زما ربه! بېشكه دوى زما نافرماني وكړه، او دوى د هغه چا پیروي وكړه چې دغه (كس) ته د ده مال او د ده اولاد زیاته نه كړه مګر زیانكاري (او تاوان)');
INSERT INTO "abulsalam" VALUES(71,22,'71:22',' او دغو خلقو ډېر لوى مكر وچلاوه');
INSERT INTO "abulsalam" VALUES(71,23,'71:23',' او ويې ویل (خپلو خلقو ته): تاسو خپل معبودان مه پرېږدئ او تاسو مه پرېږدئ (عبادت د) وَد، او نه (د) سُواع او نه (د) یغوث او یعوق اونسر');
INSERT INTO "abulsalam" VALUES(71,24,'71:24',' او یقینًا دغو (خلقو) ډېر كسان ګمراه كړي دي او ته ظالمانو ته مه زیاتوه مګر ګمراهي');
INSERT INTO "abulsalam" VALUES(71,25,'71:25',' دوى د خپلو ګناهونو په سبب غرق (ډوب) كړِى شول، نو اور ته داخل كړى شول، نو دوى د خپلو ځانونو لپاره له الله نه غیر هېڅ مدد كوونكي ونه موندل');
INSERT INTO "abulsalam" VALUES(71,26,'71:26',' او نوح وویل: اى زما ربه! ته د ځمكې له پاسه (د ځمكې په سر) په كافرانو كې هېڅ یو ګرځېدونكى مه پرېږده');
INSERT INTO "abulsalam" VALUES(71,27,'71:27',' یقینًا ته كه دوى پرېږدې، نو دوى به ستا بنده ګان ګمراه كړي او دوى نه زېږوي مګر فاجر (بدكاره)، ډېر ناشكره ');
INSERT INTO "abulsalam" VALUES(71,28,'71:28',' اى زما ربه! ته ما وبخښه او زما مور و پلار او هغه څوك (هم) چې زما كور ته داخل شي، چې ایمان لرونكى وي اومومن سړي او مومنې ښځې (وبخښه) او ظالمانو ته مه زیاتوه مګر هلاكت');
INSERT INTO "abulsalam" VALUES(72,1,'72:1',' ته (دوى ته) ووایه چې ما ته وحي شوې ده چې بېشكه شان دا دى چې په پېریانو كې یو ټولي (ډلې) (زما قراءت) په غور سره واورېده، (نو كله چې خپل قوم ته لاړل)، نو دوى وویل: بېشكه مونږ یو عجیبه قرآن واورېده');
INSERT INTO "abulsalam" VALUES(72,2,'72:2',' چې نېغې لارې ته هدایت كوي، نو مونږ په ده ایمان راوړ، او مونږ به هیڅكله له خپل رب سره هیڅوك شریك نه كړو');
INSERT INTO "abulsalam" VALUES(72,3,'72:3',' او بېشكه شان دا دى چې زمونږ د رب شان ډېر اوچت دى، ده نه ښځه نیولې ده او نه اولاد');
INSERT INTO "abulsalam" VALUES(72,4,'72:4',' او بېشكه شان دا دى چې زمونږ ناپوهو (بېوقوفو) په الله باندې زیاتي (او بې حقیقته) خبرې اړولې');
INSERT INTO "abulsalam" VALUES(72,5,'72:5',' او یقینًا مونږ دا خیال كاوه چې په الله باندې به انسانان او پېریان هیڅكله دروغ ونه وايي');
INSERT INTO "abulsalam" VALUES(72,6,'72:6',' او بېشكه شان دا دى چې په انسانانو كې ډېر داسې سړي وو چې په پېریانو كې په ځینو سړیو پورې به يې پناه نیوله، نو دغو خلقو دغو (پېریانو) ته سركشي ورزیاته كړه');
INSERT INTO "abulsalam" VALUES(72,7,'72:7',' او دا چې یقینًا دوى ګمان كړى و لكه چې تاسو ګمان كړى دى چې الله به هرګز هیڅوك بیا ژوندي راپورته نه كړي');
INSERT INTO "abulsalam" VALUES(72,8,'72:8',' او دا چې بېشكه مونږ اسمان ته ورسېدو، نو مونږ هغه ومونده چې په ډېرو سختو څوكيدارانو او سوځوونكو شغلو (ستوریو) سره ډك كړى شوى دى ');
INSERT INTO "abulsalam" VALUES(72,9,'72:9',' او دا چې بېشكه مونږ به د اورېدلو لپاره له دې (اسمان) نه د كېناستو په ځایونو كې كېناستو، نو هغه څوك چې اوس غواړي چې واوري (نو) دى به د ځان لپاره د اور لمبه منتظره ومومي');
INSERT INTO "abulsalam" VALUES(72,10,'72:10',' او دا چې یقینًا مونږ نه پوهېږو چې له هغه چا سره د شر (او بدۍ) اراده شوې ده چې په ځمكه كې دي، یا له دوى سره د دوى رب د لارښوونې (او خیر) اراده كړې ده');
INSERT INTO "abulsalam" VALUES(72,11,'72:11',' او دا چې بېشكه پر مونږ كې ځینې نېكان (او صالحه خلق) دي او پر مونږ كې له دغو نه لاندې هم شته، مونږ په مختلفو لارو روان یو');
INSERT INTO "abulsalam" VALUES(72,12,'72:12',' او دا چې بېشكه مونږ یقین وكړ چې مونږ به په ځمكه كې الله له سره عاجز نه كړى شو او مونږ به دى له سره په تېښتې سره عاجز نه كړى شو');
INSERT INTO "abulsalam" VALUES(72,13,'72:13',' او دا چې بېشكه مونږ چې كله هدایت واورېده (، نو) مونږ په ده ایمان راوړ، نو هغه څوك چې په خپل رب ایمان راوړي، نو دى به نه له نقصان نه وېرېږي او نه (د چا) له ظلم نه');
INSERT INTO "abulsalam" VALUES(72,14,'72:14',' او دا چې بېشكه په مونږ كې ځینې مسلمانان دي او ځینې په مونږ كې ظالمان دي، نو هغه چې اسلام راوړي، نو دغو خلقو د نېغې لارې قصد (او طلب) وكړ ');
INSERT INTO "abulsalam" VALUES(72,15,'72:15',' او هر چې ظالمان دي، نو دوى به د جهنم لرګي (خشاك) وي');
INSERT INTO "abulsalam" VALUES(72,16,'72:16',' او (بله وحي ما ته دا راغلې ده چې) كه دوى په نېغې لارې باندې سم ودرېږي (نو) مونږ به په دوى ډېرې اوبه وڅښوو');
INSERT INTO "abulsalam" VALUES(72,17,'72:17',' د دې لپاره چې مونږ دوى په دغه (نعمت) كې وازمایو او هغه څوك چې د خپل رب له ذكر (قرآن) نه مخ واړوي، نو هغه (رب) به دى سخت عذاب ته داخل كړي ');
INSERT INTO "abulsalam" VALUES(72,18,'72:18',' او دا چې یقینًا جوماتونه خاص د الله لپاره دي، نو تاسو له الله سره بل هیڅوك مه بلئ');
INSERT INTO "abulsalam" VALUES(72,19,'72:19',' او دا چې یقینًا شان دا دى چې كله د الله بنده (محمدصلى الله عليه وسلم لمانځه ته) ودرېده، چې دغه (الله) يې باله، (نو) دوى نژدې وو چې په ده باندې په یو بل پسې ورپرېوتونكي شي');
INSERT INTO "abulsalam" VALUES(72,20,'72:20',' ته (دوى ته) ووایه: یقینًا زه خو يواځې خپل رب بولم او زه به له ده سره هیڅوك شریك نه كړم');
INSERT INTO "abulsalam" VALUES(72,21,'72:21',' ته (دوى ته) ووایه: بېشكه زه تاسو لپاره نه د هېڅ ضرر مالك (او اختیار من) یم او نه د خیر رسولو ');
INSERT INTO "abulsalam" VALUES(72,22,'72:22',' ته (دوى ته) ووایه: بېشكه زه چې یم هیڅوك به ما له الله نه له سره خلاص نه كړى شي او زه به له ده نه غیر هېڅ پناه ځاى ونه مومم ');
INSERT INTO "abulsalam" VALUES(72,23,'72:23',' مګر د الله له جانبه د تبلیغ او د هغه د پېغام رسولو (اختیارمن یم) او هغه څوك چې د الله او د هغه د رسول نافرماني وكړي، نو بېشكه د ده لپاره د جهنم اور دى، چې دوى به په هغه كې تل ترتله وي');
INSERT INTO "abulsalam" VALUES(72,24,'72:24',' تر هغه پورې چې كله دوى هغه څه وویني چې له دوى سره يې وعده كولى شي، نو دوى به ژر پوه شي چې د مددګار په لحاظ څوك ډېر كمزورى دى او د شمېر په لحاظ څوك ډېر كم دى');
INSERT INTO "abulsalam" VALUES(72,25,'72:25',' ته (دوى ته) ووایه: زه نه پوهېږم چې ایا هغه (عذاب) نژدې دى چې تاسو سره يې وعده كولى شي، یا به د ده لپاره زما رب لرې زمانه مقرره كړي');
INSERT INTO "abulsalam" VALUES(72,26,'72:26',' دى عالم الغیب (په غیبو عالم) دى، نو دى په خپلو غیبو باندې هیڅوك نه خبروي');
INSERT INTO "abulsalam" VALUES(72,27,'72:27',' مګر هغه چې كوم رسول يې خوښ شي، نو بېشكه دغه (الله) د ده په مخ كې او د ده شا ته ساتونكي (څوكيداران) روانوي ');
INSERT INTO "abulsalam" VALUES(72,28,'72:28',' د دې لپاره چې دى په دې پوه شي چې دوى د خپل رب پېغامونه رسولي دي او ده په هغه څه احاطه كړې ده چې له دوى سره دى او ده هر شى ښه په شمېر سره ساتلى دى');
INSERT INTO "abulsalam" VALUES(73,1,'73:1',' اى په ځان پورې جامه راتاوونكیه!');
INSERT INTO "abulsalam" VALUES(73,2,'73:2',' په شپه كې لمانځه ته ودرېږه مګر لږه حصه (نه)');
INSERT INTO "abulsalam" VALUES(73,3,'73:3',' چې هغه د دغې (شپې) نیمه (ده)، یا ته له دغې (نیمې) نه لږ څه كم شه');
INSERT INTO "abulsalam" VALUES(73,4,'73:4',' یا ته په دغې (نیمې) باندې څه اضافه وكړه او قرآن ښه ښكاره واضح لوله، واضح لوستل');
INSERT INTO "abulsalam" VALUES(73,5,'73:5',' بېشكه مونږ به خامخا په تا باندې ډېر دروند قول نازلوو');
INSERT INTO "abulsalam" VALUES(73,6,'73:6',' بېشكه د شپې پورته كېدل، دغه (پاڅېدل) د (نفس) د پايمال كولو په لحاظ ډېر سخت دي او د (قرآن د) ویلو په لحاظ ډېر سموونكي دي');
INSERT INTO "abulsalam" VALUES(73,7,'73:7',' بېشكه تا لره په ورځ كې ډېره اوږده مشغولتیا (مصروفيت) وي');
INSERT INTO "abulsalam" VALUES(73,8,'73:8',' او ته د خپل رب نوم یادوه او ده ته (له هر څه نه) جدا شه، پوره جدا كېدل');
INSERT INTO "abulsalam" VALUES(73,9,'73:9',' (دغه الله) د مشرق او مغرب رب دى، نشته هېڅ لايق د عبادت مګر هم دى دى، نو ته دى (خپل) كار ساز ونیسه');
INSERT INTO "abulsalam" VALUES(73,10,'73:10',' او ته په هغو خبرو صبر كوه چې دوى يې وايي او ته دوى پرېږده ډېر ښه پرېښودل');
INSERT INTO "abulsalam" VALUES(73,11,'73:11',' او پرېږده ما او دروغ ګڼونكي چې د نعمت والا دي او دوى ته لږ مهلت وركړه');
INSERT INTO "abulsalam" VALUES(73,12,'73:12',' بېشكه زمونږ سره درانه ځنځیرونه (زولنې) او سوځوونكى اور دى');
INSERT INTO "abulsalam" VALUES(73,13,'73:13',' او داسې طعام دى چې په مرۍ كې نښتونكى دى او ډېر دردوونكى عذاب دى');
INSERT INTO "abulsalam" VALUES(73,14,'73:14',' په هغې ورځ كې چې ځمكه او غرونه به خوځېږي او غرونه به د شګو روانې ډېرۍ شي');
INSERT INTO "abulsalam" VALUES(73,15,'73:15',' بېشكه مونږ تاسو ته رسول درلېږلى دى، چې په تاسو (د قیامت په ورځ) شاهد دى، لكه څنګه چې مونږ فرعون ته رسول لېږلى و');
INSERT INTO "abulsalam" VALUES(73,16,'73:16',' نو فرعون د دغه رسول نافرماني وكړه، نو مونږ دغه (فرعون) ونیولو په سختو نیولو سره');
INSERT INTO "abulsalam" VALUES(73,17,'73:17',' كه تاسو كافر شئ، نو تاسو به ځان له هغې ورځې نه څنګه وساتئ چې (واړه) هلكان به سپین ږیري (بوډاګان) وګرځوي');
INSERT INTO "abulsalam" VALUES(73,18,'73:18',' اسمان په دغې (ورځ) كې چاودېدونكى دى، د دغه (الله) وعده كېدونكې ده');
INSERT INTO "abulsalam" VALUES(73,19,'73:19',' بېشكه دا (ایتونه) نصیحت (او پند) دى، نو هغه څوك چې غواړي چې خپل رب ته تلونكې لاره غوره كړي، (نو غوره دې كړي)');
INSERT INTO "abulsalam" VALUES(73,20,'73:20',' بېشكه ستا رب یقینًا په دې پوهېږي چې ته د شپې (په درې برخو كې) له دوو نه لږ كم (لمانحهٔ ته) پاڅېږې او (كله) د دې نیمه برخه او (كله) دریمه برخه (هم) او له هغو كسانو نه یوه ډله هم چې له تا سره دي او الله شپه او ورځ مقدار (او اندازه) كوي، دى په دې پوه دى چې بېشكه حقیقت دا دى چې تاسو به هرګز د دې طاقت ونه لرئ، نو ده پر تاسو باندې رجوع وكړه، نو تاسو له قرآنه هومره لولئ چې اسان وي. دى په دې پوه دى چې یقینًا شان دا دى چې په تاسو كې به مریضان وي او نور به په ځمكه كې سفر كوي، چې د الله فضل به لټوي او نور به د الله په لاره كې جنګ كوي، نو تاسو له دغه (قرآنه) هومره لولئ چې (درته) اسان وي او لمونځ قايموئ او زكاۃت ادا كوئ او الله ته قرض وركوئ، ډېر ښكلى قرض او هر هغه څه چې تاسو يې د خپلو ځانونو لپاره له څه خیره مخكې لېږئ (نو) هغه به تاسو له الله سره ومومئ. دغه ډېر غوره او د اجر په لحاظ ډېر لوى دي او تاسو له الله نه بخښنه وغواړئ، بېشكه الله ډېر بخښونكى، بې حده رحم كوونكى دى');
INSERT INTO "abulsalam" VALUES(74,1,'74:1',' اى په جامه كې نغښتونكیه!');
INSERT INTO "abulsalam" VALUES(74,2,'74:2',' ته پورته شه، نو (خلق) ووېروه');
INSERT INTO "abulsalam" VALUES(74,3,'74:3',' او نو د خپل رب لويي بیانوه');
INSERT INTO "abulsalam" VALUES(74,4,'74:4',' او نو خپلې جامې پاكې ساته');
INSERT INTO "abulsalam" VALUES(74,5,'74:5',' او ګناه (او بتان) نو ترك كړه');
INSERT INTO "abulsalam" VALUES(74,6,'74:6',' او (داسې) احسان مه كوه چې بدل (او ډېرول) يې غواړې');
INSERT INTO "abulsalam" VALUES(74,7,'74:7',' او خاص د خپل رب لپاره، پس صبر كوه');
INSERT INTO "abulsalam" VALUES(74,8,'74:8',' نو كله چې په شپېلۍ كې پوكى وكړى شي');
INSERT INTO "abulsalam" VALUES(74,9,'74:9',' نو په دغه وخت كې دغه ډېره سخته ورځ ده');
INSERT INTO "abulsalam" VALUES(74,10,'74:10',' په كافرانو باندې اسانه نه ده');
INSERT INTO "abulsalam" VALUES(74,11,'74:11',' ته پرېږده ما او هغه څوك چې ما دى يواځې پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(74,12,'74:12',' او (بیا مې) ده ته ډېر زیات مال وركړ');
INSERT INTO "abulsalam" VALUES(74,13,'74:13',' او زامن حاضر (باشه)');
INSERT INTO "abulsalam" VALUES(74,14,'74:14',' او ده ته مې فراخي وركړه، ډېره فراخي');
INSERT INTO "abulsalam" VALUES(74,15,'74:15',' بیا دى طمع لري چې زه به ده ته لا زیات وركړم');
INSERT INTO "abulsalam" VALUES(74,16,'74:16',' هیڅكله هم نه، بېشكه دى زمونږ له ایتونو سره ډېر عناد كوونكى (مخالف) و');
INSERT INTO "abulsalam" VALUES(74,17,'74:17',' ژر به زه ده ته ډېر سخت تكلیف وركړم');
INSERT INTO "abulsalam" VALUES(74,18,'74:18',' بېشكه ده فكر وكړ او اندازه يې وكړه');
INSERT INTO "abulsalam" VALUES(74,19,'74:19',' پس دى دې هلاك كړى شي، ده څنګه اندازه وكړه؟');
INSERT INTO "abulsalam" VALUES(74,20,'74:20',' بیا دې هلاك كړى شي، ده څنګه اندازه وكړه');
INSERT INTO "abulsalam" VALUES(74,21,'74:21',' بیا يې وكتل');
INSERT INTO "abulsalam" VALUES(74,22,'74:22',' بیا يې مخ تریو كړ او وچولى (تندى) يې بوټ كړ');
INSERT INTO "abulsalam" VALUES(74,23,'74:23',' بیا يې شا كړه او لويي يې وكړه');
INSERT INTO "abulsalam" VALUES(74,24,'74:24',' نو ده وویل: نه دى دا (قرآن) مګر داسې جادو چې رانقل كولى شي');
INSERT INTO "abulsalam" VALUES(74,25,'74:25',' نه دى دا مګر د انسان قول');
INSERT INTO "abulsalam" VALUES(74,26,'74:26',' ژر به زه دى د دوزخ اور ته داخل كړم');
INSERT INTO "abulsalam" VALUES(74,27,'74:27',' او ته څه شي پوه كړې چې سقر (د دوزخ اور) څه شى دى');
INSERT INTO "abulsalam" VALUES(74,28,'74:28',' دى به نه باقي ساتي او نه به پرېږدي');
INSERT INTO "abulsalam" VALUES(74,29,'74:29',' انسان لره ډېر سوځوونكى دى');
INSERT INTO "abulsalam" VALUES(74,30,'74:30',' په ده باندې نولس(ملايك) مقرر دي');
INSERT INTO "abulsalam" VALUES(74,31,'74:31',' او مونږ د دوزخ څوكيداران نه دي ګرځولي مګر ملايك او مونږ د دوى شمېر نه دى ګرځولى مګر ازمېښت د هغو كسانو لپاره چې كافران شوي دي، د دې لپاره چې هغه كسان یقین وكړي چې كتاب ورته وركړى شوى دى او د هغو كسانو ایمان زیات شي چې ایمان يې راوړى دى او شك ونه كړي هغه كسان چې كتاب ورته وركړى شوى دى او مومنان او د دې لپاره چې هغه كسان ووايي چې د هغوى په زړونو كې مرض دى او كافران: الله په دې سره د مثال په لحاظ څه اراده كړې ده. همداسې الله ګمراه كوي چا ته چې وغواړي او هدایت وركوي چا ته چې وغواړي او ستا د رب لښكرې نه پېژني مګر هم دى. او نه دى دا مګر د خلقو لپاره نصیحت (او پند)');
INSERT INTO "abulsalam" VALUES(74,32,'74:32',' هیڅكله داسې نه ده، قسم دى په سپوږمۍ');
INSERT INTO "abulsalam" VALUES(74,33,'74:33',' او په شپې باندې كله چې شا كړي');
INSERT INTO "abulsalam" VALUES(74,34,'74:34',' او په سبا باندې كله چې روښانه شي');
INSERT INTO "abulsalam" VALUES(74,35,'74:35',' بېشكه دغه (اور د سقر) یقینًا په لویو (بلاګانو) كې یو دى');
INSERT INTO "abulsalam" VALUES(74,36,'74:36',' چې انسان لره وېروونكى دى');
INSERT INTO "abulsalam" VALUES(74,37,'74:37',' هغه چا لره چې په تاسو كې غواړي چې وړاندې شي، یا وروسته شي');
INSERT INTO "abulsalam" VALUES(74,38,'74:38',' هر نفس به د خپلو كړو په بدل كې ګاڼه (ګرو) وي');
INSERT INTO "abulsalam" VALUES(74,39,'74:39',' غیر له ښي اړخ والاو نه');
INSERT INTO "abulsalam" VALUES(74,40,'74:40',' (دوى به) په جنتونو كې وي، یو تر بله به پوښتنې كوي');
INSERT INTO "abulsalam" VALUES(74,41,'74:41',' د مجرمانو (كافرانو) په باره كې');
INSERT INTO "abulsalam" VALUES(74,42,'74:42',' (مومنان به مجرمانو ته وايي:) تاسو په دوزخ كې څه شي داخل كړي یئ؟');
INSERT INTO "abulsalam" VALUES(74,43,'74:43',' دوى به ووايي: مونږ له لمونځ كوونكو ځنې نه وو');
INSERT INTO "abulsalam" VALUES(74,44,'74:44',' او مسكین ته به مو طعام نه وركاوه');
INSERT INTO "abulsalam" VALUES(74,45,'74:45',' او له باطل ویونكو سره به مونږ په باطلو خبرو كې ننوتلو');
INSERT INTO "abulsalam" VALUES(74,46,'74:46',' او مونږ به د بدلې ورځ دروغ ګڼله');
INSERT INTO "abulsalam" VALUES(74,47,'74:47',' تر دې پورې چې مونږ ته یقین راغى');
INSERT INTO "abulsalam" VALUES(74,48,'74:48',' نو دوى ته د سفارش كوونكو سفارش فايده نه رسوي');
INSERT INTO "abulsalam" VALUES(74,49,'74:49',' نو په دوى څه شوي دي چې له نصیحت (قرآن) نه مخ ګرځوونكي دي');
INSERT INTO "abulsalam" VALUES(74,50,'74:50',' ګویا كې دوى تښتېدونكي ځنګلي خره دي');
INSERT INTO "abulsalam" VALUES(74,51,'74:51',' چې له زمري نه تښتي');
INSERT INTO "abulsalam" VALUES(74,52,'74:52',' بلكې په دوى كې هر یو سړى غواړي چې ده ته خورې كړى شوې صحیفې (پاڼې) وركړى شي');
INSERT INTO "abulsalam" VALUES(74,53,'74:53',' هیڅكله داسې نه ده بلكې دوى له اخرت نه نه وېرېږي');
INSERT INTO "abulsalam" VALUES(74,54,'74:54',' هیڅكله داسې نه ده، بېشكه دا (قرآن) نصیحت دى');
INSERT INTO "abulsalam" VALUES(74,55,'74:55',' نو څوك چې غواړي (نو) له ده نه دې پند واخلي');
INSERT INTO "abulsalam" VALUES(74,56,'74:56',' او دوى نصیحت (او پند) نه اخلي مګر كه الله يې وغواړي. هم دى د وېرې لايق دى او د بخښنې اهل دى');
INSERT INTO "abulsalam" VALUES(75,1,'75:1',' زه د قیامت په ورځې باندې قسم خورم');
INSERT INTO "abulsalam" VALUES(75,2,'75:2',' او زه په ډېر ملامت كوونكي نفس باندې قسم خورم');
INSERT INTO "abulsalam" VALUES(75,3,'75:3',' ایا (كافر) انسان دا ګمان كوي چې مونږ به د ده هډوكي له سره راجمع نه كړو');
INSERT INTO "abulsalam" VALUES(75,4,'75:4',' ولې نه! (جمع به يې كړو)، په داسې حال كې چې مونږ په دې قادر یو چې د ده (هډوكي او) د ګوتو بندونه (هم) برابر كړو');
INSERT INTO "abulsalam" VALUES(75,5,'75:5',' بلكې (كافر) انسان اراده لري چې په خپله مخه (راتلونكې زمانه) كې هم نافرماني وكړي');
INSERT INTO "abulsalam" VALUES(75,6,'75:6',' دى تپوس كوي چې د قیامت ورځ به كله وي؟');
INSERT INTO "abulsalam" VALUES(75,7,'75:7',' نو كله چې سترګې خړې (سپینې) شي');
INSERT INTO "abulsalam" VALUES(75,8,'75:8',' او سپوږمۍ توره (بې رڼا) شي');
INSERT INTO "abulsalam" VALUES(75,9,'75:9',' او لمر او سپوږمۍ سره جمع كړى شي');
INSERT INTO "abulsalam" VALUES(75,10,'75:10',' نو (كافر) انسان به په دغه وخت كې وايي: د تېښتې ځاى چېرته دى؟');
INSERT INTO "abulsalam" VALUES(75,11,'75:11',' هیڅكله داسې نه ده، هېڅ پناه ځاى نشته');
INSERT INTO "abulsalam" VALUES(75,12,'75:12',' په دغې ورځ كې ځاى د قرار ستا د رب په طرف دى');
INSERT INTO "abulsalam" VALUES(75,13,'75:13',' په دغې ورځ كې به انسان په هغه څه خبر كړى شي چې ده مخكې لېږلي دي او وروسته يې پرېښي دي');
INSERT INTO "abulsalam" VALUES(75,14,'75:14',' بلكې انسان په خپل ځان باندې ښه دلیل دى');
INSERT INTO "abulsalam" VALUES(75,15,'75:15',' اګر كه دى خپل عذرونه وړاندې كړي');
INSERT INTO "abulsalam" VALUES(75,16,'75:16',' (اى نبي!) ته خپله ژبه په دې (قرآن) سره مه خوځوه، د دې لپاره چې ته دا په تُندۍ سره حاصل كړې');
INSERT INTO "abulsalam" VALUES(75,17,'75:17',' بېشكه پر مونږ باندې د ده جمع كول او د ده لوستل دي');
INSERT INTO "abulsalam" VALUES(75,18,'75:18',' نو كله چې مونږ دى ولولو، نو ته د ده د لوستلو پیروي كوه');
INSERT INTO "abulsalam" VALUES(75,19,'75:19',' بیا یقینًا زمونږ په ذمه د ده بیانول دي');
INSERT INTO "abulsalam" VALUES(75,20,'75:20',' هیڅكله داسې نه ده بلكې تاسو تادي كوونكې (زر تېرېدونكې فاني كېدونكې دنیا) خوښوئ');
INSERT INTO "abulsalam" VALUES(75,21,'75:21',' او تاسو اخرت پرېږدئ');
INSERT INTO "abulsalam" VALUES(75,22,'75:22',' په دغې ورځ كې به څه مخونه تازه روښانه وي');
INSERT INTO "abulsalam" VALUES(75,23,'75:23',' خپل رب ته به كتونكي وي');
INSERT INTO "abulsalam" VALUES(75,24,'75:24',' او په دغې ورځ كې به څه مخونه سخت تریو (او سپېره) وي');
INSERT INTO "abulsalam" VALUES(75,25,'75:25',' یقین به كوي چې له دوى سره به مَلا ماتوونكى عمل وشي');
INSERT INTO "abulsalam" VALUES(75,26,'75:26',' داسې نه ده كله چې ساه د ستوني هډوكي ته ورسېږي');
INSERT INTO "abulsalam" VALUES(75,27,'75:27',' او وبه ویل شي: څوك دى دم كوونكى؟');
INSERT INTO "abulsalam" VALUES(75,28,'75:28',' او دى به یقین وكړي چې بېشكه دغه (وخت د) بېلتون دى');
INSERT INTO "abulsalam" VALUES(75,29,'75:29',' او یوه پنډۍ به له بلې پنډۍ تاوېږي');
INSERT INTO "abulsalam" VALUES(75,30,'75:30',' ستا د رب په لوري به دغې ورځ كې ورتله دي');
INSERT INTO "abulsalam" VALUES(75,31,'75:31',' پس ده نه تصدیق وكړ او نه يې لمونځ وكړ');
INSERT INTO "abulsalam" VALUES(75,32,'75:32',' او لېكن دروغ يې وګاڼه (قرآن) او مخ يې وګرځاوه');
INSERT INTO "abulsalam" VALUES(75,33,'75:33',' بیا خپل اهل ته لاړ، په داسې حال كې چې تكبر يې كاوه');
INSERT INTO "abulsalam" VALUES(75,34,'75:34',' تا ته دې هلاكت (او افسوس) وي، پس هلاكت دې وي');
INSERT INTO "abulsalam" VALUES(75,35,'75:35',' بیا دې تا لپاره هلاكت وي، نو هلاكت دې وي');
INSERT INTO "abulsalam" VALUES(75,36,'75:36',' ایا(كافر) انسان ګمان كوي چې دى به مهمل (او بې كاره) پرېښودل شي');
INSERT INTO "abulsalam" VALUES(75,37,'75:37',' ایا دغه (انسان) د هغې منۍ یو څاڅكى نه و چې تویولى شي');
INSERT INTO "abulsalam" VALUES(75,38,'75:38',' بیا دى د وینې یوه ټوټه وه، نو دغه (الله دى) پیدا كړ، پس برابر يې كړ');
INSERT INTO "abulsalam" VALUES(75,39,'75:39',' نو له دغې (منۍ) نه يې دوه قسمه پیدا كړل (یعنې) سړى او ښځه');
INSERT INTO "abulsalam" VALUES(75,40,'75:40',' ایا دغه (الله) په دې قادر نه دى چې مړي راژوندي كړي؟');
INSERT INTO "abulsalam" VALUES(76,1,'76:1',' یقینًا په انسان باندې داسې وخت هم راغلى دى چې دى څه داسې د يادولو (وړ) شى نه و ');
INSERT INTO "abulsalam" VALUES(76,2,'76:2',' بېشكه مونږ انسان له ګډې وډې شوې نطفې نه پیدا كړى دى، چې مونږ به دى ازمایو، نو دى مونږ اورېدونكى (او) لیدونكى وګرځاوه');
INSERT INTO "abulsalam" VALUES(76,3,'76:3',' بېشكه مونږ ده ته د نېغې لارې ښوونه كړې ده، یا به دى شكر كوونكى وي او یا به ډېر ناشكره وي');
INSERT INTO "abulsalam" VALUES(76,4,'76:4',' بېشكه مونږ د كافرانو لپاره ځنځیرونه او طوقونه (زلونې) او لمبې وهونكى اور تیار كړى دى ');
INSERT INTO "abulsalam" VALUES(76,5,'76:5',' بېشكه نېكان خلق به د شرابو له داسې جامونو نه څښاك كوي چې د هغو ګډونى (امېزش) به كافور وي');
INSERT INTO "abulsalam" VALUES(76,6,'76:6',' چې چینه ده، د الله بنده ګان به له هغې نه څښي، دوى به هغه بهوي (چېرته چې وغواړي) ښه بهول');
INSERT INTO "abulsalam" VALUES(76,7,'76:7',' دوى نذرونه پوره كوي او له هغې ورځې نه وېرېږي چې د هغې شر ښه ښكاره دى');
INSERT INTO "abulsalam" VALUES(76,8,'76:8',' او دوى د هغه (الله) په مینه كې مسكین او یتیم او بندي ته طعام وركوي');
INSERT INTO "abulsalam" VALUES(76,9,'76:9',' (دوى وايي:) بېشكه همدا خبره ده چې مونږ تاسو ته د الله له مخه طعام دركوو، مونږ له تاسو نه څه بدله او شكر نه غواړو');
INSERT INTO "abulsalam" VALUES(76,10,'76:10',' بېشكه مونږ له خپل رب نه د تریو مخې ډېرې سختې ورځې (له عذاب) نه وېرېږو');
INSERT INTO "abulsalam" VALUES(76,11,'76:11',' نو الله به دوى د دغې ورځې له شر نه بچ وساتي او دوى ته به تازګي او خوشالي ورسوي');
INSERT INTO "abulsalam" VALUES(76,12,'76:12',' او دوى ته به د خپل صبر په سبب جنت او ورېښم په بدل كې وركړي');
INSERT INTO "abulsalam" VALUES(76,13,'76:13',' په داسې حال كې چې په تختونو باندې به تكیه وهونكي وي، دوى به په هغه (جنت) كې نه لمر ویني او نه ساړه (یخني)');
INSERT INTO "abulsalam" VALUES(76,14,'76:14',' په داسې حال كې چې په دوى باندې به د دغه (جنت د ونو) سیورى نژدې وي او د هغه د مېوو شوكول به مُسخَّر (راټیټ) كړى شوي وي، مُسخركېدل');
INSERT INTO "abulsalam" VALUES(76,15,'76:15',' او په دوى باندې به لوښي د سپينو زرو او ګلاسونه ګرځولى شي چې د شیشو به وي');
INSERT INTO "abulsalam" VALUES(76,16,'76:16',' د سپینو زرو شیشې چې دوى به دغه (لوښي) اندازه كړي وي، اندازه كول');
INSERT INTO "abulsalam" VALUES(76,17,'76:17',' او په دغه (جنت) كې به په دوى د شرابو داسې جامونه ورڅښول كېږي چې ګډونى به يې زنجبیل وي');
INSERT INTO "abulsalam" VALUES(76,18,'76:18',' چې چینه ده په دغه (جنت) كې چې نوم يې سلسبیل ایښودل شوى دى');
INSERT INTO "abulsalam" VALUES(76,19,'76:19',' او په دوى باندې به (د خدمت لپاره) همېشني هلكان ګرځي، كله چې ته دوى ووینې (نو) ته به دوى خورې كړى شوې ملغلرې ګڼې');
INSERT INTO "abulsalam" VALUES(76,20,'76:20',' او كله چې ته وګورې (نو) په دغه ځاى كې به نعمتونه او ډېره لویه بادشاهي ووینې');
INSERT INTO "abulsalam" VALUES(76,21,'76:21',' د دوى په تن باندې به د نري ورېښمو جامې وي چې شنې به وي او د پېړو ورېښمو (جامې به هم وي) او دوى ته به د سپینو زرو باهوګان واچولى شي او خپل رب به په دوى ډېر پاكيزه شراب وڅښي');
INSERT INTO "abulsalam" VALUES(76,22,'76:22',' بېشكه دا (نعمت) تاسو لپاره بدله ده. او ستاسو سعي (كوشش) قبول كړى شوى دى');
INSERT INTO "abulsalam" VALUES(76,23,'76:23',' بېشكه همدا مونږ پر تاسو باندې قرآن نازل كړى دى، ښه نازلول');
INSERT INTO "abulsalam" VALUES(76,24,'76:24',' نو ته د خپل رب حكم ته صبر كوه او په دوى كې د هېڅ ګناهګار او سخت ناشكره خبره مه منه');
INSERT INTO "abulsalam" VALUES(76,25,'76:25',' او په سبا او بېګاه كې ته د خپل رب نوم یادوه');
INSERT INTO "abulsalam" VALUES(76,26,'76:26',' او د شپې په څه حصه كې، نو ته هغه ته سجده كوه او د هغه تسبیح وایه په اوږده شپه كې');
INSERT INTO "abulsalam" VALUES(76,27,'76:27',' بېشكه دغه خلق تادي كوونكې (زر تېرېدونكي دنیا) خوښوي او له خپل ځان وروسته درنه ورځ پرېږدي');
INSERT INTO "abulsalam" VALUES(76,28,'76:28',' مونږ دوى پیدا كړي دي او مونږ د دوى بندونه مضبوط كړي دي او كله چې مونږ وغواړو د دوى مثالونه به په بدل كې راولو، په بدل كې راوستل');
INSERT INTO "abulsalam" VALUES(76,29,'76:29',' بېشكه دا (قرآن) نصیحت دى، نو هغه څوك چې غواړي چې خپل رب طرف ته لار ونیسي (نو ودې نیسي)');
INSERT INTO "abulsalam" VALUES(76,30,'76:30',' او تاسو اراده نشئ كولى مګر دا چې الله وغواړي، بېشكه الله ښه عالم، ښه حكمت والا دى');
INSERT INTO "abulsalam" VALUES(76,31,'76:31',' چا ته چې وغواړي په خپل رحمت كې يې داخلوي او ظالمان (كافران) چې دي هغهٔ، د دوى لپاره ډېر دردوونكى عذاب تیار كړى دى');
INSERT INTO "abulsalam" VALUES(77,1,'77:1',' قسم دى په هغو بادونو چې پرله پسې لېږل شوي دي!');
INSERT INTO "abulsalam" VALUES(77,2,'77:2',' بیا په هغو سختو تېزو چلېدونكو بادونو باندې، تېز تلل');
INSERT INTO "abulsalam" VALUES(77,3,'77:3',' او په خورونكو بادونو باندې، خورول');
INSERT INTO "abulsalam" VALUES(77,4,'77:4',' بیا په هغو بادونو چې جدا والى راولي، جدا كول');
INSERT INTO "abulsalam" VALUES(77,5,'77:5',' بیا په هغو بادونو باندې چې د ذكر اچونكي دي');
INSERT INTO "abulsalam" VALUES(77,6,'77:6',' د عذر لپاره او وېرولو لپاره');
INSERT INTO "abulsalam" VALUES(77,7,'77:7',' بېشكه چې له تاسو سره څه وعده كولى شي یقینًا (هغه) واقع كېدونكې ده');
INSERT INTO "abulsalam" VALUES(77,8,'77:8',' نو كله چې ستوري بې رڼا كړى شي');
INSERT INTO "abulsalam" VALUES(77,9,'77:9',' او كله چې اسمان څیرې كړى شي');
INSERT INTO "abulsalam" VALUES(77,10,'77:10',' او كله چې غرونه والوزول شي');
INSERT INTO "abulsalam" VALUES(77,11,'77:11',' او كله چې رسولانو ته وخت وټاكل شي');
INSERT INTO "abulsalam" VALUES(77,12,'77:12',' د كومې ورځې لپاره دغه (څیزونه) وروسته كړى شوي دي؟');
INSERT INTO "abulsalam" VALUES(77,13,'77:13',' د فیصلې د ورځې لپاره ');
INSERT INTO "abulsalam" VALUES(77,14,'77:14',' او ته څه شي پوه كړې چې د فیصلې ورځ كله ده ');
INSERT INTO "abulsalam" VALUES(77,15,'77:15',' د دروغ ګڼونكو لپاره په دغې ورځ كې هلاكت دى');
INSERT INTO "abulsalam" VALUES(77,16,'77:16',' ایا مونږ ړومبني (مجرمان) نه دي هلاك كړي');
INSERT INTO "abulsalam" VALUES(77,17,'77:17',' بیا مونږ په دوى پسې وروستني خلق ورلېږو');
INSERT INTO "abulsalam" VALUES(77,18,'77:18',' همداسې كار مونږ له مجرمانو (كافرانو) سره كوو');
INSERT INTO "abulsalam" VALUES(77,19,'77:19',' په دغې ورځې كې د تكذیب كوونكو (دروغ ګڼونكو) لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(77,20,'77:20',' ایا مونږ تاسو له سپكو اوبو نه نه یئ پیدا كړي');
INSERT INTO "abulsalam" VALUES(77,21,'77:21',' پس مونږ دغه (اوبه) په مضبوط ځاى كې كېښودلې');
INSERT INTO "abulsalam" VALUES(77,22,'77:22',' تر مقررې نېټې (اندازې) پورې');
INSERT INTO "abulsalam" VALUES(77,23,'77:23',' نو مونږ قادر یو، پس (مونږ) ښه قدرت لرونكي یو');
INSERT INTO "abulsalam" VALUES(77,24,'77:24',' په دغې ورځې كې د دروغ ګڼونكو لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(77,25,'77:25',' ایا مونږ ځمكه نه ده ګرځولې راجمع كوونكې');
INSERT INTO "abulsalam" VALUES(77,26,'77:26',' ژوندیو لره او مړیو لره');
INSERT INTO "abulsalam" VALUES(77,27,'77:27',' او مونږ په دې كې اوچت لوى غرونه پیدا كړي دي او په تاسو مو خوږې تنده ماتوونكې اوبه څښلي');
INSERT INTO "abulsalam" VALUES(77,28,'77:28',' په دغې ورځ كې د دروغ ګڼونكو لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(77,29,'77:29',' تاسو ځئ لاړ شئ هغه څیز ته چې تاسو به هغه دروغ ګاڼه');
INSERT INTO "abulsalam" VALUES(77,30,'77:30',' تاسو ځئ لاړ شئ هغه سیوري ته چې د دریوڅانګو والا دى');
INSERT INTO "abulsalam" VALUES(77,31,'77:31',' چې نه یخ دى او نه د اور لمبې لرې كولى شي');
INSERT INTO "abulsalam" VALUES(77,32,'77:32',' بېشكه دغه (اور) داسې سرې سكروټې ولي (غورځوي)، لكه اوچتې ماڼۍ ');
INSERT INTO "abulsalam" VALUES(77,33,'77:33',' ګویا كې دغه (سكروټې، بڅركي) زېړ تور بخن اوښان دي');
INSERT INTO "abulsalam" VALUES(77,34,'77:34',' په دغې ورځ كې د دروغ ګڼونكو لپاره هلاكت دى ');
INSERT INTO "abulsalam" VALUES(77,35,'77:35',' دغه ورځ ده چې دوى به (په كې) خبرې نشي كولى');
INSERT INTO "abulsalam" VALUES(77,36,'77:36',' او دوى ته به اجازت وركولى نشي، پس چې دوى عذر وكړي');
INSERT INTO "abulsalam" VALUES(77,37,'77:37',' په دغې ورځ كې د دروغ ګڼونكو لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(77,38,'77:38',' دغه د فیصلې ورځ ده، مونږ، تاسو او ړومبني خلق راجمع كړي دي');
INSERT INTO "abulsalam" VALUES(77,39,'77:39',' نو كه له تاسو سره څه چل مكر وي، نو زما سره (دغه) چل مكر وكړئ');
INSERT INTO "abulsalam" VALUES(77,40,'77:40',' په دغې ورځ كې د دروغ ګڼونكو لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(77,41,'77:41',' یقینًا پرهېزګاران به په سیورو او چینو كې وي');
INSERT INTO "abulsalam" VALUES(77,42,'77:42',' او په داسې مېوو كې چې دوى يې غواړي');
INSERT INTO "abulsalam" VALUES(77,43,'77:43',' (ملايك به مومنانو ته وايي:) تاسو خورئ او څښئ، په داسې حال كې چې خوند اخيستونكي يئ د هغو عملونو په بدل كې چې تاسو به كول');
INSERT INTO "abulsalam" VALUES(77,44,'77:44',' بېشكه همداسې مونږ نېكي كوونكو ته بدله وركوو');
INSERT INTO "abulsalam" VALUES(77,45,'77:45',' په دغې ورځ كې د دروغ ګڼونكو لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(77,46,'77:46',' تاسو خورئ او فايده واخلئ لږه، بېشكه تاسو مجرمان یئ');
INSERT INTO "abulsalam" VALUES(77,47,'77:47',' په دغې ورځ كې د تكذیب كوونكو (دروغ ویونكو) لپاره هلاكت او تباهي ده');
INSERT INTO "abulsalam" VALUES(77,48,'77:48',' او كله چې دوى ته وویل شي تاسو ركوع كوئ (نو) دوى ركوع نه كوي');
INSERT INTO "abulsalam" VALUES(77,49,'77:49',' په دغې ورځ كې د تكذیب كوونكو لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(77,50,'77:50',' نو له دې (قرآن) نه وروسته دوى په څه شي ایمان راوړي؟');
INSERT INTO "abulsalam" VALUES(78,1,'78:1',' دوى د څه شي په باره كې یو له بل پوښتنه كوي؟');
INSERT INTO "abulsalam" VALUES(78,2,'78:2',' د ډېر لوى خبر په باره كې');
INSERT INTO "abulsalam" VALUES(78,3,'78:3',' هغه چې دوى په هغه كې سره اختلاف كوونكي دي');
INSERT INTO "abulsalam" VALUES(78,4,'78:4',' نه دې كوي (دغه اختلاف او پوښتنې)، ژر به دوى پوه شي');
INSERT INTO "abulsalam" VALUES(78,5,'78:5',' بیا هیڅكله دې نه كوي، ژر به دوى پوه شي');
INSERT INTO "abulsalam" VALUES(78,6,'78:6',' ایا مونږ ځمكه فرش نه ده ګرځولې؟');
INSERT INTO "abulsalam" VALUES(78,7,'78:7',' او غرونه (مو) میخونه');
INSERT INTO "abulsalam" VALUES(78,8,'78:8',' او مونږ تاسو پیدا كړي یئ جوړې (نر او ښځه)');
INSERT INTO "abulsalam" VALUES(78,9,'78:9',' او مونږ ستاسو خوب (تاسو لپاره) راحت ګرځولى دى');
INSERT INTO "abulsalam" VALUES(78,10,'78:10',' او شپه مو لباس ګرځولې ده');
INSERT INTO "abulsalam" VALUES(78,11,'78:11',' او ورځ مو د روزۍ لټولو وخت ګرځولى دى');
INSERT INTO "abulsalam" VALUES(78,12,'78:12',' او ستاسو له پاسه مو اوه ډېر مضبوط اسمانونه جوړ كړي دي');
INSERT INTO "abulsalam" VALUES(78,13,'78:13',' او مونږ روښانه ځلېدونكې ډېوه پیدا كړې ده');
INSERT INTO "abulsalam" VALUES(78,14,'78:14',' او مونږ له ډكو ورېځو نه ډېرې تويېدونكې اوبه نازلې كړې دي');
INSERT INTO "abulsalam" VALUES(78,15,'78:15',' د دې لپاره چې مونږ په دغو (اوبو) سره دانې او زرغونه راوباسو');
INSERT INTO "abulsalam" VALUES(78,16,'78:16',' او باغونه یو له بل سره نژدې');
INSERT INTO "abulsalam" VALUES(78,17,'78:17',' بېشكه د فیصلې ورځ مقرر وخت دى');
INSERT INTO "abulsalam" VALUES(78,18,'78:18',' هغه ورځ چې پوكى به ووهل شي په شپېلۍ كې، نو تاسو به ډلې ډلې راځئ');
INSERT INTO "abulsalam" VALUES(78,19,'78:19',' او اسمان به پرانستل شي، نو دى به دروازې دروازې وي ');
INSERT INTO "abulsalam" VALUES(78,20,'78:20',' او غرونه به روان كړى شي، نو دوى به سراب شي');
INSERT INTO "abulsalam" VALUES(78,21,'78:21',' بېشكه دوزخ به د انتظار ځاى (كمین ځاى) شي');
INSERT INTO "abulsalam" VALUES(78,22,'78:22',' د سركشو لپاره د ورتللو ځاى');
INSERT INTO "abulsalam" VALUES(78,23,'78:23',' چې دوى به په هغه كې زمانې زمانې (همېشه) پاتې كېږي');
INSERT INTO "abulsalam" VALUES(78,24,'78:24',' دوى به په هغه كې نه څه یخوالى څَكي او نه څه اوبه');
INSERT INTO "abulsalam" VALUES(78,25,'78:25',' غیر د اېشېدلو تودو اوبو او بهېدونكو وینو زوو نه');
INSERT INTO "abulsalam" VALUES(78,26,'78:26',' بدله به وركولى شي د عملونو سره برابره موافقه');
INSERT INTO "abulsalam" VALUES(78,27,'78:27',' بېشكه دوى به د حساب امېد نه كاوه');
INSERT INTO "abulsalam" VALUES(78,28,'78:28',' او زمونږ ایتونه به يې دروغ ګڼل، سخت دروغ ګڼل');
INSERT INTO "abulsalam" VALUES(78,29,'78:29',' او هر شى، مونږ هغه په كتاب (لوح محفوظ) كې شمېرلى (او محفوظ كړى) دى');
INSERT INTO "abulsalam" VALUES(78,30,'78:30',' نو تاسو (خپله سزا) وڅَكئ، پس مونږ به تاسو ته له سره زیات نه كړو مګر عذاب');
INSERT INTO "abulsalam" VALUES(78,31,'78:31',' بېشكه خاص د متقیانو لپاره كامیابي ده');
INSERT INTO "abulsalam" VALUES(78,32,'78:32',' (چې) باغونه او انګور دي');
INSERT INTO "abulsalam" VALUES(78,33,'78:33',' او انارسینه همځولې پېغلې دي');
INSERT INTO "abulsalam" VALUES(78,34,'78:34',' او ترڅنډو پورې ډكې د شرابو پیالې دي');
INSERT INTO "abulsalam" VALUES(78,35,'78:35',' دوى په دغه (جنت) كې نه لغوه (بېكاره) خبره اوري او نه دروغ');
INSERT INTO "abulsalam" VALUES(78,36,'78:36',' د رب له جانبه به بدله وركولى شي (د عملونو) په حساب سره ډېره وركړه');
INSERT INTO "abulsalam" VALUES(78,37,'78:37',' چې د اسمانونو او ځمكې رب دى او د هغه څه چې د دغو دواړو په مینځ كې دي، چې ډېر زیات مهربان دى، دوى له هغه سره د خبرو كولو اختیار (او قدرت) نه لري');
INSERT INTO "abulsalam" VALUES(78,38,'78:38',' په هغې ورځ كې چې روح او ملايك به صفونه صفونه ودرېږي، دوى به خبرې نشي كولى مګرهغه څوك چې هغه ته رحمٰن اجازت وركړي او ده (په دنیا كې) حقه خبره (لا إله الا الله) ویلې وي');
INSERT INTO "abulsalam" VALUES(78,39,'78:39',' دغه حق ورځ ده، نو هغه څوك چې د خپل رب په طرف د ورتلو ځاى نیسي (نو ودې نیسي)');
INSERT INTO "abulsalam" VALUES(78,40,'78:40',' بېشكه مونږ تاسو له نژدې (راتلونكي) عذاب څخه ووېرولئ، په هغه ورځ كې چې هر سړى به هغه څه ته ګوري چې د ده لاسونو مخكې لېږلي دي او كافر به وايي: اى كاشكې چې زه خاورې وى');
INSERT INTO "abulsalam" VALUES(79,1,'79:1',' قسم دى په (ملايكو) راوښكونكو (روحونو د كفارو لره)، راوښكل');
INSERT INTO "abulsalam" VALUES(79,2,'79:2',' او په (ملايكو) اسان راوښكونكو (د مومنانو روحونو لره)، اسان راوښكل');
INSERT INTO "abulsalam" VALUES(79,3,'79:3',' او په تېزو تلونكو (ملايكو)، تېز تلل');
INSERT INTO "abulsalam" VALUES(79,4,'79:4',' پس په وړاندې كېدونكو (ملايكو)، وړاندې كېدل');
INSERT INTO "abulsalam" VALUES(79,5,'79:5',' بیا د كار په تدبیر جوړونكو (ملايكو) چې تاسو به خامخا بیا راژوندي كړى شئ)');
INSERT INTO "abulsalam" VALUES(79,6,'79:6',' په هغه ورځ چې لړځېدونكې به ولړځېږي');
INSERT INTO "abulsalam" VALUES(79,7,'79:7',' په دې پسې به راځي ورپسې راتلونكې');
INSERT INTO "abulsalam" VALUES(79,8,'79:8',' (د مجرمانو) زړونه به په دغه ورځ كې رپېدونكي (درزېدونكي) وي');
INSERT INTO "abulsalam" VALUES(79,9,'79:9',' د دغو سترګې به ښكته وي');
INSERT INTO "abulsalam" VALUES(79,10,'79:10',' دوى به وايي: ایا په رښتیا (او) یقینًا به مونږ ړومبني حالت ته ګرځولى شو (بیا به ژوندي كېږو؟)');
INSERT INTO "abulsalam" VALUES(79,11,'79:11',' ایا كله چې مونږ زاړه رژېدونكي هډوكي شو');
INSERT INTO "abulsalam" VALUES(79,12,'79:12',' دوى وویل: دغه (ګرځېدل) به په هغه وخت كې زیانمن ګرځېدل وي');
INSERT INTO "abulsalam" VALUES(79,13,'79:13',' پس بېشكه دغه خو بس یوه چغه ده');
INSERT INTO "abulsalam" VALUES(79,14,'79:14',' نو ناڅاپه به دوى د ځمكې په مخ وي');
INSERT INTO "abulsalam" VALUES(79,15,'79:15',' ایا تا ته د موسٰی خبره راغلې ده');
INSERT INTO "abulsalam" VALUES(79,16,'79:16',' كله چې ده ته خپل رب په پاكې دَرې (ناو) ’’طُوٰی‘‘ كې اواز وكړ ');
INSERT INTO "abulsalam" VALUES(79,17,'79:17',' ته فرعون ته لاړ شه، بېشكه ده سركشي شروع كړې ده');
INSERT INTO "abulsalam" VALUES(79,18,'79:18',' نو ته (ورته) ووایه: ایا ته دا خوښوې چې ته پاك شې');
INSERT INTO "abulsalam" VALUES(79,19,'79:19',' او دا چې زه تا ته ستا د رب په طرف ښوونه وكړم، پس چې ته ووېرېږې ');
INSERT INTO "abulsalam" VALUES(79,20,'79:20',' نو ده (موسٰی) دغه (فرعون) ته تر ټولو لویه نښه وښودله');
INSERT INTO "abulsalam" VALUES(79,21,'79:21',' نو ده تكذيب وكړ او نافراني يې وكړه ');
INSERT INTO "abulsalam" VALUES(79,22,'79:22',' بيا يې شا كړه (او) په ډېره بيړه سره لاړ');
INSERT INTO "abulsalam" VALUES(79,23,'79:23',' نو ده (جادوګر) راجمع كړل، پس اواز يې وكړ');
INSERT INTO "abulsalam" VALUES(79,24,'79:24',' نو ويې ويل:زه ستاسو رب یم، تر ټولو اوچت (رب)');
INSERT INTO "abulsalam" VALUES(79,25,'79:25',' نو دى الله راونیوه په عذاب د اخرت او دنیا سره');
INSERT INTO "abulsalam" VALUES(79,26,'79:26',' بېشكه په دغه (غرقولو او سوځولو) كې یقینًا د هغه چا لپاره عبرت دى چې (له الله نه) وېرېږي');
INSERT INTO "abulsalam" VALUES(79,27,'79:27',' ایا تاسو د پیدا كولو په لحاظ ډېر سخت یئ، یا كه اسمان، دغه هغه (الله) جوړ كړى دى ');
INSERT INTO "abulsalam" VALUES(79,28,'79:28',' د ده چت يې اوچت كړى دى، نو دغه يې ښه برابر جوړ كړى دى');
INSERT INTO "abulsalam" VALUES(79,29,'79:29',' او د ده شپه يې توره تیاره كړې ده او د ده رڼا يې راوېستلې ده');
INSERT INTO "abulsalam" VALUES(79,30,'79:30',' او ځمكه يې له ده نه وروسته غوړولې ده');
INSERT INTO "abulsalam" VALUES(79,31,'79:31',' ده له دغې (ځمكې) نه د دې اوبه او د دې واښه رااېستلي دي');
INSERT INTO "abulsalam" VALUES(79,32,'79:32',' او غرونو لره، ده دغه محكم كړي دي');
INSERT INTO "abulsalam" VALUES(79,33,'79:33',' ستاسو او ستاسو د څاریو د فايدې لپاره');
INSERT INTO "abulsalam" VALUES(79,34,'79:34',' نو كله چې تر ټولو لویه بلا (قیامت) راشي');
INSERT INTO "abulsalam" VALUES(79,35,'79:35',' په هغه ورځ چې انسان به هغه څه یادوي چې ده كړي وي');
INSERT INTO "abulsalam" VALUES(79,36,'79:36',' او دوزخ به هغه چا ته څرګند كړى شي چې يې ویني');
INSERT INTO "abulsalam" VALUES(79,37,'79:37',' نو هر چې هغه څوك دى چې سركشي يې كړې وي');
INSERT INTO "abulsalam" VALUES(79,38,'79:38',' او دنيايي ژوند ته يې ترجیح وركړې وي');
INSERT INTO "abulsalam" VALUES(79,39,'79:39',' نو بېشكه چې هم دا دوزخ (د ده) استوګنه ده');
INSERT INTO "abulsalam" VALUES(79,40,'79:40',' او هر چې هغه څوك دى چې د خپل رب په وړاندې له ودرېدلو نه وېرېدلى وي او نفس (ځان) يې له خواهشاتو نه منع كړى وي');
INSERT INTO "abulsalam" VALUES(79,41,'79:41',' نو بېشكه چې هم دا جنت (د ده) استوګنه ده');
INSERT INTO "abulsalam" VALUES(79,42,'79:42',' (اى نبي!) دوى له تا نه د قیامت په باره كې پوښتنه كوي چې د ده واقع كېدل به كله وي');
INSERT INTO "abulsalam" VALUES(79,43,'79:43',' ته د ده له یاده (علمه) په څه كې يې');
INSERT INTO "abulsalam" VALUES(79,44,'79:44',' خاص ستا رب ته د دغه (قيامت) انتها ده');
INSERT INTO "abulsalam" VALUES(79,45,'79:45',' بېشكه ته خو يواځې د هغه چا وېروونكى يې چې له ده (قیامت) نه وېرېږي');
INSERT INTO "abulsalam" VALUES(79,46,'79:46',' ګویاكې دوى به په هغې ورځې كې چې دوى دغه (قیامت) وویني (داسې به وي لكه چې) دوى (په دنیا، یا په قبر كې) نه دي ایسار شوي مګر د ورځې اخر، یا څاښت');
INSERT INTO "abulsalam" VALUES(80,1,'80:1',' ده تندى تریو كړ او مخ يې وګرځاوه');
INSERT INTO "abulsalam" VALUES(80,2,'80:2',' په دې وجه چې ده ته ړوند راغى');
INSERT INTO "abulsalam" VALUES(80,3,'80:3',' او ته څه شي پوه كړى يې، ښايي چې دى پاك شي');
INSERT INTO "abulsalam" VALUES(80,4,'80:4',' یا به دى نصیحت (او پند) واخلي، نو ده ته به پند نفع ورسوي');
INSERT INTO "abulsalam" VALUES(80,5,'80:5',' هر چې هغه څوك دى چې استغنا يې كړې ده');
INSERT INTO "abulsalam" VALUES(80,6,'80:6',' نو ته په هغه پسې كېږې');
INSERT INTO "abulsalam" VALUES(80,7,'80:7',' حال دا چې په تا هېڅ الزام نشته چې هغه پاك نشي');
INSERT INTO "abulsalam" VALUES(80,8,'80:8',' او هر چې هغه څوك دى چې تا ته په منډو راځي');
INSERT INTO "abulsalam" VALUES(80,9,'80:9',' په داسې حال كې چې دى (له الله نه) وېرېږي');
INSERT INTO "abulsalam" VALUES(80,10,'80:10',' نو ته له هغه نه مشغولېږې');
INSERT INTO "abulsalam" VALUES(80,11,'80:11',' داسې كله هم مه كوه، بېشكه دا (سورت) یو پند (او نصیحت) دى');
INSERT INTO "abulsalam" VALUES(80,12,'80:12',' نو هغه څوك چې غواړي (نو) دى دې ومني');
INSERT INTO "abulsalam" VALUES(80,13,'80:13',' (دغه) په عزتمنو پاڼو كې دى');
INSERT INTO "abulsalam" VALUES(80,14,'80:14',' چې پورته كړى شوې، ښې پاكې كړى شوې دي');
INSERT INTO "abulsalam" VALUES(80,15,'80:15',' د لیكونكو (ملايكو) په لاسونو كې');
INSERT INTO "abulsalam" VALUES(80,16,'80:16',' چې ډېر عزتمن، ډېر نېك دي');
INSERT INTO "abulsalam" VALUES(80,17,'80:17',' انسان دې هلاك كړى شي، دى څومره ډېر ناشكره دى!');
INSERT INTO "abulsalam" VALUES(80,18,'80:18',' دى دغه (الله) له څه شي نه پیدا كړى دى؟');
INSERT INTO "abulsalam" VALUES(80,19,'80:19',' له نطفې نه، دى يې پیدا كړى دى، پس دى يې اندازه كړى دى ');
INSERT INTO "abulsalam" VALUES(80,20,'80:20',' بیا يې ده ته لاره ور اسانه كړې ده ');
INSERT INTO "abulsalam" VALUES(80,21,'80:21',' بیا يې دى مړ كړ، نو دى يې په قبر كې كېښود');
INSERT INTO "abulsalam" VALUES(80,22,'80:22',' بیا چې كله وغواړي دى به ژوندى كړي');
INSERT INTO "abulsalam" VALUES(80,23,'80:23',' داسې نه ده، دغه (انسان) هغه كار نه دى كړى چې دغه (الله) ده ته حكم كړى دى');
INSERT INTO "abulsalam" VALUES(80,24,'80:24',' نو انسان دې خپل طعام ته وګوري');
INSERT INTO "abulsalam" VALUES(80,25,'80:25',' چې بېشكه مونږ اوبه راتوى كړې دي، راتویول');
INSERT INTO "abulsalam" VALUES(80,26,'80:26',' بیا مونږ ځمكه څیرې كړې ده، څیرول');
INSERT INTO "abulsalam" VALUES(80,27,'80:27',' نو مونږ په دې كې دانې را زرغونه كړي دي');
INSERT INTO "abulsalam" VALUES(80,28,'80:28',' او انګور او تركاريانې');
INSERT INTO "abulsalam" VALUES(80,29,'80:29',' او زیتون او كجورې');
INSERT INTO "abulsalam" VALUES(80,30,'80:30',' او ګڼ باغونه');
INSERT INTO "abulsalam" VALUES(80,31,'80:31',' او مېوې او ګياه (واښه)');
INSERT INTO "abulsalam" VALUES(80,32,'80:32',' ستاسو او ستاسو د څارویو د فايدې لپاره');
INSERT INTO "abulsalam" VALUES(80,33,'80:33',' نو كله چې سخت اواز راشي');
INSERT INTO "abulsalam" VALUES(80,34,'80:34',' په هغه ورځ چې سړى به له خپل وروره تښتي');
INSERT INTO "abulsalam" VALUES(80,35,'80:35',' او له خپلې مور او خپل پلاره');
INSERT INTO "abulsalam" VALUES(80,36,'80:36',' او له خپلې ښځې او خپلو زامنو نه');
INSERT INTO "abulsalam" VALUES(80,37,'80:37',' په دوى كې د هر سړي لپاره به په دغه ورځ كې یو حال وي چې دى به (له نورو نه) بې پروا (بې غوره) كوي');
INSERT INTO "abulsalam" VALUES(80,38,'80:38',' څه مخونه به په دغې ورځ كې روښانه وي');
INSERT INTO "abulsalam" VALUES(80,39,'80:39',' خندېدونكي (او) خوشاله به وي');
INSERT INTO "abulsalam" VALUES(80,40,'80:40',' او څه مخونه به په دغې ورځ كې پر هغو به ګرد پروت وي');
INSERT INTO "abulsalam" VALUES(80,41,'80:41',' دغه (مخونه) به توروالي پټ كړي وي');
INSERT INTO "abulsalam" VALUES(80,42,'80:42',' دغه (تور مخي) خلق هم دوى كافران (او) فاجران (بدكاران) دي');
INSERT INTO "abulsalam" VALUES(81,1,'81:1',' كله چې لمر ونغښتل شي');
INSERT INTO "abulsalam" VALUES(81,2,'81:2',' او كله چې ستوري خړ شي');
INSERT INTO "abulsalam" VALUES(81,3,'81:3',' او كله چې غرونه روان كړل شي');
INSERT INTO "abulsalam" VALUES(81,4,'81:4',' او كله چې د لسو میاشتو بلاربې اوښې معطلې (ازادې) پرېښودى شي');
INSERT INTO "abulsalam" VALUES(81,5,'81:5',' او كله چې وحشي ځناوران راجمع كړل شي');
INSERT INTO "abulsalam" VALUES(81,6,'81:6',' او كله چې دریابونه بل كړى شي');
INSERT INTO "abulsalam" VALUES(81,7,'81:7',' او كله چې نفسونه جوړه كړل شي');
INSERT INTO "abulsalam" VALUES(81,8,'81:8',' او كله چې ژوندۍ خښه كړى شوې جینۍ وپوښتى شي');
INSERT INTO "abulsalam" VALUES(81,9,'81:9',' چې دا په كومې ګناه وژل شوې ده');
INSERT INTO "abulsalam" VALUES(81,10,'81:10',' او كله چې اعمال نامې وغوړولى شي');
INSERT INTO "abulsalam" VALUES(81,11,'81:11',' او كله چې له اسمان نه پوستكى ووېستل شي');
INSERT INTO "abulsalam" VALUES(81,12,'81:12',' او كله چې دوزخ بلې لمبې كړى شي');
INSERT INTO "abulsalam" VALUES(81,13,'81:13',' او كله چې جنت ښه نژدې كړى شي');
INSERT INTO "abulsalam" VALUES(81,14,'81:14',' هر نفس به په هغه شي پوه شي چې ده حاضر كړى وي');
INSERT INTO "abulsalam" VALUES(81,15,'81:15',' نو زه قسم خورم په هغو (ستوریو) چې بېرته ګرځېدونكي دي');
INSERT INTO "abulsalam" VALUES(81,16,'81:16',' په روانو، پټېدونكو باندې');
INSERT INTO "abulsalam" VALUES(81,17,'81:17',' او قسم خورم په شپې كله چې شروع شي');
INSERT INTO "abulsalam" VALUES(81,18,'81:18',' او په سبا كله چې دى ساه واخلي (روښانه شي)');
INSERT INTO "abulsalam" VALUES(81,19,'81:19',' بېشكه دا (قرآن) خامخا د ډېر عزتمن رسول قول دى');
INSERT INTO "abulsalam" VALUES(81,20,'81:20',' چې د عرش د مالك په نیز د قوت والا (او) د مرتبې والا دى');
INSERT INTO "abulsalam" VALUES(81,21,'81:21',' منلى شوى دى هلته، امین دى');
INSERT INTO "abulsalam" VALUES(81,22,'81:22',' او ستاسو ملګرى (محمدصلى الله عليه وسلم) له سره لېونى نه دى');
INSERT INTO "abulsalam" VALUES(81,23,'81:23',' او یقینًا یقینًا ده دغه (جبریل د اسمان) په څرګنده غاړه كې لیدلى و');
INSERT INTO "abulsalam" VALUES(81,24,'81:24',' او دى د وحي په بیانولو كې له سره بخل كوونكى نه دى');
INSERT INTO "abulsalam" VALUES(81,25,'81:25',' او بېخي نه دى دا (قرآن) خبره د شیطان رټل شوي ');
INSERT INTO "abulsalam" VALUES(81,26,'81:26',' نو تاسو (اى كافرانو!)چېرته ځئ؟ ');
INSERT INTO "abulsalam" VALUES(81,27,'81:27',' نه دى دا (قرآن) مګر د عالمونو لپاره نصیحت (او پند)');
INSERT INTO "abulsalam" VALUES(81,28,'81:28',' د هغه چا لپاره چې په تاسو كې غواړې چې دى نېغ برابر شي');
INSERT INTO "abulsalam" VALUES(81,29,'81:29',' او تاسو د (د سمې لارې) اراده نشئ كولى مګر دا چې الله رب العلمین يې اراده وكړي');
INSERT INTO "abulsalam" VALUES(82,1,'82:1',' كله چې اسمان وچوي');
INSERT INTO "abulsalam" VALUES(82,2,'82:2',' او كله چې ستوري ګډوډ شي (او) راولوېږي');
INSERT INTO "abulsalam" VALUES(82,3,'82:3',' او كله چې دریابونه یو ځاى روان كړى شي');
INSERT INTO "abulsalam" VALUES(82,4,'82:4',' او كله چې قبرونه لاندې باندې كړى شي');
INSERT INTO "abulsalam" VALUES(82,5,'82:5',' هر نفس به په هغه څه پوه شي چې ده وړاندې لېږلي دي او وروسته يې پرېښې دي ');
INSERT INTO "abulsalam" VALUES(82,6,'82:6',' اى (كافره) انسانه! ته په خپل كریم (او سخي) رب باندې څه شي دوكه كړى يې');
INSERT INTO "abulsalam" VALUES(82,7,'82:7',' هغه چې ته يې پیدا كړې، نو ته يې ښه برابر كړې');
INSERT INTO "abulsalam" VALUES(82,8,'82:8',' په هر صورت (شكل) كې چې هغه غوښتل، ته يې پیوند (او جوړ) كړې');
INSERT INTO "abulsalam" VALUES(82,9,'82:9',' داسې بېخي نه ده، بلكې تاسو د بدلې ورځ (قیامت) دروغ ګڼئ');
INSERT INTO "abulsalam" VALUES(82,10,'82:10',' په داسې حال كې چې پر تاسو باندې ساتونكي (ملايك) مقرر دي');
INSERT INTO "abulsalam" VALUES(82,11,'82:11',' چې عزتمن لیكونكي دي');
INSERT INTO "abulsalam" VALUES(82,12,'82:12',' دوى په هغه څه پوهېږي چې تاسو يې كوئ');
INSERT INTO "abulsalam" VALUES(82,13,'82:13',' بېشكه نېكان (مومنان) به خامخا په نعمتونو كې وي');
INSERT INTO "abulsalam" VALUES(82,14,'82:14',' او بېشكه چې بدكاران (كافران) به خامخا په جهنم كې وي');
INSERT INTO "abulsalam" VALUES(82,15,'82:15',' دوى به ده ته د بدلې په ورځ ننوځي');
INSERT INTO "abulsalam" VALUES(82,16,'82:16',' او دوى به له دغه (دوزخ) نه له سره پټېدونكي نه وي');
INSERT INTO "abulsalam" VALUES(82,17,'82:17',' او ته څه شي پوه كړې چې د بدلې ورځ (قیامت) څه شى دى؟');
INSERT INTO "abulsalam" VALUES(82,18,'82:18',' بیا ته څه شي پوه كړې چې د بدلې ورځ څه شى دى');
INSERT INTO "abulsalam" VALUES(82,19,'82:19',' (یاده كړه) هغه ورځ چې هېڅ یو نفس به د بل نفس لپاره د هېڅ شي اختیار ونه لري او په دغې ورځ كې به امر (حكم) خاص الله لره وي ');
INSERT INTO "abulsalam" VALUES(83,1,'83:1',' (په تول او پېمانه كې) كمي كوونكو لره هلاكت دى');
INSERT INTO "abulsalam" VALUES(83,2,'83:2',' هغه كسان چې دوى له خلقو نه ځان ته پېمانه اخلي (، نو) پوره (يې) اخلي');
INSERT INTO "abulsalam" VALUES(83,3,'83:3',' او كله چې دغو (خلقو) ته پېمانه وركوي، یا يې دوى ته تلي (،نو) كمى كوي');
INSERT INTO "abulsalam" VALUES(83,4,'83:4',' ایا دغه خلق یقین نه كوي چې بېشكه دوى به بیا راژوندي كول شي');
INSERT INTO "abulsalam" VALUES(83,5,'83:5',' په ډېره لویه ورځ كې (د حساب لپاره)');
INSERT INTO "abulsalam" VALUES(83,6,'83:6',' هغه ورځ چې خلق به ’’رب العلمین‘‘ ته ودرېږي');
INSERT INTO "abulsalam" VALUES(83,7,'83:7',' هیڅكله داسې نه ده په كار، بېشكه د فاجرانو (كافرانو) كتاب (لیكل شوي عملونه) خامخا په سجین كې دي');
INSERT INTO "abulsalam" VALUES(83,8,'83:8',' او ته څه شي پوه كړې چې سجین څه شى دى');
INSERT INTO "abulsalam" VALUES(83,9,'83:9',' یو دفتر (كتاب) دى لیكل شوى');
INSERT INTO "abulsalam" VALUES(83,10,'83:10',' په دغې ورځ كې د دروغ ګڼونكو لپاره هلاكت دى');
INSERT INTO "abulsalam" VALUES(83,11,'83:11',' هغه كسان چې د بدلې ورځ دروغ ګڼي');
INSERT INTO "abulsalam" VALUES(83,12,'83:12',' او دغه (ورځ) دروغ نه ګڼي مګر هر زیاتي كوونكى، سخت ګناه ګار');
INSERT INTO "abulsalam" VALUES(83,13,'83:13',' كله چې ده ته زمونږ ایتونه تلاوت كولى شي؛ دى وايي: (دا) د ړومبنیو (خلقو) باطلې قصې دي');
INSERT INTO "abulsalam" VALUES(83,14,'83:14',' داسې نه ده، بلكې د دوى په زړونو باندې هغو (د ګناه عملونو) زنګ لګولى دى چې دوى به كول');
INSERT INTO "abulsalam" VALUES(83,15,'83:15',' داسې نه ده په كار، بېشكه دوى به په دغې ورځ كې له خپل رب نه یقینًا په حجاب (بند) كړى شوي وي');
INSERT INTO "abulsalam" VALUES(83,16,'83:16',' بیا بېشكه دوى یقینًا دوزخ ته ننوتونكي دي');
INSERT INTO "abulsalam" VALUES(83,17,'83:17',' بیا به (دوى ته) ویلى شي: دا هغه (دوزخ) دى چې تاسو به دا دروغ ګاڼه');
INSERT INTO "abulsalam" VALUES(83,18,'83:18',' داسې نه ده، بېشكه د نېكانو (خلقو) عملنامې (لیكلى كتاب) خامخا په ’’علِّیین‘‘ كې دي');
INSERT INTO "abulsalam" VALUES(83,19,'83:19',' او ته څه شي پوه كړې چې ’’علّیون‘‘ څه شى دى ');
INSERT INTO "abulsalam" VALUES(83,20,'83:20',' یو دفتر (كتاب) دى چې لیكل شوى دى');
INSERT INTO "abulsalam" VALUES(83,21,'83:21',' چې ده ته مقرب (بنده ګان او ملايك) حاضرېږي');
INSERT INTO "abulsalam" VALUES(83,22,'83:22',' بېشكه نېكان خلق یقینًا په نعمتونو كې دي');
INSERT INTO "abulsalam" VALUES(83,23,'83:23',' دوى به په تختونو باندې (ناست) وي، ګوري به');
INSERT INTO "abulsalam" VALUES(83,24,'83:24',' (اى كتونكیه!) ته به د دوى په مخونو كې د نعمتونو تازګي وینې');
INSERT INTO "abulsalam" VALUES(83,25,'83:25',' په دوى به مُهر (ټاپه)كړى شوي خالص شراب څښولى شي');
INSERT INTO "abulsalam" VALUES(83,26,'83:26',' چې د دغو مُهر (ټاپه) به مشك وي، او په دغه (نعمتونو) كې، پس په كار ده چې رغبت كوونكي رغبت وكړي');
INSERT INTO "abulsalam" VALUES(83,27,'83:27',' د دغو (شرابو) ګډون له تسنیم (چينې) څخه دى');
INSERT INTO "abulsalam" VALUES(83,28,'83:28',' چې یوه چینه ده چې له دې نه به (د الله) مقربین څښي');
INSERT INTO "abulsalam" VALUES(83,29,'83:29',' بېشكه هغه كسان چې جرم (د كفر) يې كړى دى، دوى به په هغو كسانو پورې خندا كوله چې ایمان يې راوړى و');
INSERT INTO "abulsalam" VALUES(83,30,'83:30',' او كله چې به د دوى (مومنانو) په خوا كې تېرېدل (نو) په خپلو منځونو كې به يې سترګكونه وهل');
INSERT INTO "abulsalam" VALUES(83,31,'83:31',' او كله چې به دوى خپلو كورونو ته واپس كېدل (نو) خوشاله به واپس كېدل');
INSERT INTO "abulsalam" VALUES(83,32,'83:32',' او كله چې به دوى دغه (مسلمانان) وليدل (نو) ويل به يې: بېشكه دغه خلق یقینًا ګمراهان دي');
INSERT INTO "abulsalam" VALUES(83,33,'83:33',' حال دا چې دوى په دغو (مومنانو) باندې نګرانان (څوكيداران) نه وو لېږل شوي');
INSERT INTO "abulsalam" VALUES(83,34,'83:34',' نو په نن ورځ به هغه كسان چې ایمان يې راوړى دى؛ په كافرانو پورې خندا كوي');
INSERT INTO "abulsalam" VALUES(83,35,'83:35',' په تختونو باندې به ناست وي، چې ګوري به');
INSERT INTO "abulsalam" VALUES(83,36,'83:36',' یقینًا كافرانو ته د هغه څه بدله وركړى شوه چې دوى به كول');
INSERT INTO "abulsalam" VALUES(84,1,'84:1',' كله چې اسمان څیرې شي');
INSERT INTO "abulsalam" VALUES(84,2,'84:2',' او خپل رب ته غوږ كېږدي او دى (اسمان) د همدې لايق دى');
INSERT INTO "abulsalam" VALUES(84,3,'84:3',' او كله چې ځمكه راښكلى شي');
INSERT INTO "abulsalam" VALUES(84,4,'84:4',' او هغه څه د باندې وغورځوي چې په دې كې دي او خالي شي');
INSERT INTO "abulsalam" VALUES(84,5,'84:5',' او خپل رب ته غوږ كېږدي او دا د همدې لايق ده');
INSERT INTO "abulsalam" VALUES(84,6,'84:6',' اى انسانه! بېشكه ته په پوره كوشش سره كار كوونكى يې تر خپل ربه (سره د ملاقات پورې)، ښه په كوشش سره كار كول، پس ته له دغه (خپل عمل) سره ملاقات كوونكى يې');
INSERT INTO "abulsalam" VALUES(84,7,'84:7',' نو هر چې هغه څوك دى چې هغه ته خپل كتاب (عمل نامه) په ښي لاس كې وركړى شي');
INSERT INTO "abulsalam" VALUES(84,8,'84:8',' نو ژر ده چې له ده سره به حساب وكړى شي، ډېر اسان حساب');
INSERT INTO "abulsalam" VALUES(84,9,'84:9',' او دى به خپل اهل ته خوشاله وګرځي');
INSERT INTO "abulsalam" VALUES(84,10,'84:10',' او هر چې هغه څوك دى چې هغه ته خپل كتاب (عمل نامه) له خپلې شا نه وروسته وركړى شي');
INSERT INTO "abulsalam" VALUES(84,11,'84:11',' نو دى به ژر هلاكت ته چغې كړي');
INSERT INTO "abulsalam" VALUES(84,12,'84:12',' او د دوزخ بل اور ته به ننوځي');
INSERT INTO "abulsalam" VALUES(84,13,'84:13',' بېشكه دى په خپل اهل كې خوشاله و');
INSERT INTO "abulsalam" VALUES(84,14,'84:14',' بېشكه ده یقین كړى و چې دى به له سره بېرته (الله ته) ونه ګرځي');
INSERT INTO "abulsalam" VALUES(84,15,'84:15',' ولې نه! بېشكه د ده رب ده لره تل ښه ليدونكى و');
INSERT INTO "abulsalam" VALUES(84,16,'84:16',' نو زه قسم خورم په شفق (له ماښام نه پس په سوروالي)');
INSERT INTO "abulsalam" VALUES(84,17,'84:17',' او په شپه او په هغه څه چې دې راجمع كړي دي');
INSERT INTO "abulsalam" VALUES(84,18,'84:18',' او په سپوږمۍ كله چې دا كامله شي');
INSERT INTO "abulsalam" VALUES(84,19,'84:19',' چې تاسو به خامخا ضرور رسېږئ یو حال ته وروسته له بل حال نه ');
INSERT INTO "abulsalam" VALUES(84,20,'84:20',' نو په دوى څه شوي دي چې دوى ایمان نه راوړي');
INSERT INTO "abulsalam" VALUES(84,21,'84:21',' او كله چې په دوى باندې قرآن لوستى شي دوى سجده نه كوي');
INSERT INTO "abulsalam" VALUES(84,22,'84:22',' بلكې هغه كسان چې كافران شوي دي؛ هغوى تكذیب كوي');
INSERT INTO "abulsalam" VALUES(84,23,'84:23',' او الله ښه عالم دى په هغه څه چې دوى يې پټ ساتي');
INSERT INTO "abulsalam" VALUES(84,24,'84:24',' نو ته دوى ته د ډېر دردوونكي عذاب زېرى وركړه');
INSERT INTO "abulsalam" VALUES(84,25,'84:25',' غیر له هغو كسانو نه چې ایمان يې راوړى دى او نېك عملونه يې كړي دي، د دوى لپاره نه ختمېدونكى اجر دى');
INSERT INTO "abulsalam" VALUES(85,1,'85:1',' قسم دى په اسمان چې خاوند د برجونو دى');
INSERT INTO "abulsalam" VALUES(85,2,'85:2',' او په وعده كړى شوې ورځې(د قيامت)');
INSERT INTO "abulsalam" VALUES(85,3,'85:3',' او په ګواه يا حاضرېدونكي او په هغې (ورځې) چې هغې ته حاضري كولى شي (چې د قيامت ورځ ده)');
INSERT INTO "abulsalam" VALUES(85,4,'85:4',' هلاك دې كړى شي خاوندان د خندق');
INSERT INTO "abulsalam" VALUES(85,5,'85:5',' چې (په كې) اور و د بلېدلو (او لرګو) والا');
INSERT INTO "abulsalam" VALUES(85,6,'85:6',' كله چې دوى په (غاړو د) هغو ناست وو');
INSERT INTO "abulsalam" VALUES(85,7,'85:7',' او دوى هغه څه ته چې دوى به په مومنانو باندې كول؛ حاضر وو');
INSERT INTO "abulsalam" VALUES(85,8,'85:8',' او دوى له دغو (مومنانو) نه هېڅ عیب نه لیده مګر دا چې دوى په الله باندې ایمان راوړى و، چې ډېر غالب، ښه ستايل شوى دى');
INSERT INTO "abulsalam" VALUES(85,9,'85:9',' هغه (الله) چې خاص د هغه لپاره د اسمانونو او ځمكې بادشاهي ده او الله په هر شي باندې ښه خبردار (او ګواه) دى');
INSERT INTO "abulsalam" VALUES(85,10,'85:10',' بېشكه هغه كسان چې مومنان سړي او مومنې ښځې يې په فتنه (عذاب) كې اچولي وي، بیا يې توبه نه وي ایستلې، نو د دوى لپاره د جهنم (دوزخ) عذاب دى او د دوى لپاره د ډېر سوځوونكي اور عذاب دى');
INSERT INTO "abulsalam" VALUES(85,11,'85:11',' بېشكه هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي د دوى لپاره داسې جنتونه دي چې د هغو له لاندې ولې بهېږي، همدغه ډېره لویه كامیابي ده');
INSERT INTO "abulsalam" VALUES(85,12,'85:12',' بېشكه ستا د رب نیول یقینًا ډېر سخت دي');
INSERT INTO "abulsalam" VALUES(85,13,'85:13',' بېشكه هم دغه (الله) اول ځلې پیدا كوي او دویم ځلې پیدا كوي');
INSERT INTO "abulsalam" VALUES(85,14,'85:14',' او هم دى ډېر بخښونكى، ډېر محبت كوونكى دى');
INSERT INTO "abulsalam" VALUES(85,15,'85:15',' د عرش مالك دى، لوى شان (او عزت) والا دى');
INSERT INTO "abulsalam" VALUES(85,16,'85:16',' ډېر كوونكى دى هغه څه لره چې دى يې اراده وكړي');
INSERT INTO "abulsalam" VALUES(85,17,'85:17',' ایا تا ته د لښكرو خبر راغلى دى؟');
INSERT INTO "abulsalam" VALUES(85,18,'85:18',' چې فرعونیان او ثمودیان وو');
INSERT INTO "abulsalam" VALUES(85,19,'85:19',' بلكې هغه كسان چې كافران شوي دي په دروغ ګڼلو كې (لګیا) دي ');
INSERT INTO "abulsalam" VALUES(85,20,'85:20',' او الله د دوى له چاپېره نه راګېروونكى دى');
INSERT INTO "abulsalam" VALUES(85,21,'85:21',' بلكې دغه د لوى قدر والا قرآن دى');
INSERT INTO "abulsalam" VALUES(85,22,'85:22',' په لوح محفوظ كې دى');
INSERT INTO "abulsalam" VALUES(86,1,'86:1',' قسم دى په اسمان او د شپې په راتلونكي');
INSERT INTO "abulsalam" VALUES(86,2,'86:2',' او ته څه شي پوه كړې چې د شپې راتلونكى څه شى دى؟');
INSERT INTO "abulsalam" VALUES(86,3,'86:3',' ځلېدونكى ستورى دى');
INSERT INTO "abulsalam" VALUES(86,4,'86:4',' نشته هېڅ یو نفس مګر په هغه باندې څارونكى (ساتونكى) شته');
INSERT INTO "abulsalam" VALUES(86,5,'86:5',' نو انسان دې وګوري چې دى له څه شي نه پیدا كړى شوى دى؟');
INSERT INTO "abulsalam" VALUES(86,6,'86:6',' دى له ټوپ وهونكو اوبو نه پیدا كړى شوى دى');
INSERT INTO "abulsalam" VALUES(86,7,'86:7',' چې د شا او له سینو د هډوكو له مینځ نه راوځي');
INSERT INTO "abulsalam" VALUES(86,8,'86:8',' بېشكه دغه (الله) د دغه (انسان) په بیا راژوندي كولو یقینًا قادر دى');
INSERT INTO "abulsalam" VALUES(86,9,'86:9',' په هغې ورځ چې د پټو څیزونو به (په كې) څېړنه (او څرګندونه) كولى شي');
INSERT INTO "abulsalam" VALUES(86,10,'86:10',' نو د دغه (انسان) لپاره به نه هېڅ قوت وي او نه څوك مدد كوونكى');
INSERT INTO "abulsalam" VALUES(86,11,'86:11',' قسم دى په اسمان چې خاوند د باران دى');
INSERT INTO "abulsalam" VALUES(86,12,'86:12',' او په ځمكه چې د (زرغونې راوتلو لپاره) چاودېدلو والا ده');
INSERT INTO "abulsalam" VALUES(86,13,'86:13',' بېشكه دا (قرآن) یقینًا (د حق او باطل) بېلونكې وینا ده');
INSERT INTO "abulsalam" VALUES(86,14,'86:14',' او دا هیڅكله عبثه (او د ټوقو) خبره نه ده');
INSERT INTO "abulsalam" VALUES(86,15,'86:15',' بېشكه دوى مكر او چل كوي، یو چل كول');
INSERT INTO "abulsalam" VALUES(86,16,'86:16',' او زه هم یو تدبیر كوم، تدبیر كول');
INSERT INTO "abulsalam" VALUES(86,17,'86:17',' نو ته كافرانو ته مهلت وركړه، ته دوى ته لږ مهلت وركړه');
INSERT INTO "abulsalam" VALUES(87,1,'87:1',' ته پاكي بیان كړه د خپل رب د نامې چې تر ټولو اوچت دى');
INSERT INTO "abulsalam" VALUES(87,2,'87:2',' هغه چې پیدا يې كړل ټول شيان، پس ښه يې برابر كړل');
INSERT INTO "abulsalam" VALUES(87,3,'87:3',' او هغه (رب) چې اندازه يې وكړه، پس لاره يې وښووله');
INSERT INTO "abulsalam" VALUES(87,4,'87:4',' او هغه ذات چې تازه ګياه يې را اېستلې ده');
INSERT INTO "abulsalam" VALUES(87,5,'87:5',' بيا يې هغه وګرځوله وچه توره ');
INSERT INTO "abulsalam" VALUES(87,6,'87:6',' ژر ده چې مونږ به په تا باندې (قرآن) ولولو، نو ته به يې نه هېروې');
INSERT INTO "abulsalam" VALUES(87,7,'87:7',' مګر هغه چې الله يې وغواړي، بېشكه دغه (الله) په ښكاره و پوهېږي او په هغه څه چې پټ وي');
INSERT INTO "abulsalam" VALUES(87,8,'87:8',' او مونږ به تا ته اسانې لارې ته اسانتیا دركوو');
INSERT INTO "abulsalam" VALUES(87,9,'87:9',' نو ته نصیحت (پند) كوه كه نصیحت كول نفع رسوي (يا بېشكه نصيحت كول نفع رسوي) ');
INSERT INTO "abulsalam" VALUES(87,10,'87:10',' ژر ده چې هغه څوك به پند واخلي چې وېرېږي');
INSERT INTO "abulsalam" VALUES(87,11,'87:11',' او له دغه (نصیحت) نه به تر ټولو بدبخته ډډه كوي');
INSERT INTO "abulsalam" VALUES(87,12,'87:12',' هغه چې تر ټولو لوى اور ته به ننوځي');
INSERT INTO "abulsalam" VALUES(87,13,'87:13',' بیا به دى په دغه (اور) كې نه مري او نه به ښه ژوند لري');
INSERT INTO "abulsalam" VALUES(87,14,'87:14',' یقینًا هغه څوك كامیاب شو چې پاك شو');
INSERT INTO "abulsalam" VALUES(87,15,'87:15',' او د خپل رب نامه يې یاده كړه، پس لمونځ يې وكړ');
INSERT INTO "abulsalam" VALUES(87,16,'87:16',' بلكې تاسو (په اخرت باندې) دنيايي ژوند غوره كوئ');
INSERT INTO "abulsalam" VALUES(87,17,'87:17',' حال دا چې اخرت ډېر غوره او تل ترتله دى');
INSERT INTO "abulsalam" VALUES(87,18,'87:18',' بېشكه دا (خبرې) یقینًا په ړومبنیو صحیفو كې هم دي');
INSERT INTO "abulsalam" VALUES(87,19,'87:19',' د ابراهیم او موسی په صحیفو كې ');
INSERT INTO "abulsalam" VALUES(88,1,'88:1',' ایا تا ته (يعنې خلقو لره) د پټوونكي (قیامت) خبر راغلى دى؟');
INSERT INTO "abulsalam" VALUES(88,2,'88:2',' څه مخونه به په دغې ورځ كې خوار (او) وېرېدونكي وي');
INSERT INTO "abulsalam" VALUES(88,3,'88:3',' سخت كار كوونكي، ستړي كېدونكي به وي');
INSERT INTO "abulsalam" VALUES(88,4,'88:4',' ډېر ګرم اور ته به ننوځي');
INSERT INTO "abulsalam" VALUES(88,5,'88:5',' په دوى به له ډېرې ګرمې چېنې نه اوبه څښولى شي');
INSERT INTO "abulsalam" VALUES(88,6,'88:6',' د دوى لپاره به طعام نه وي مګر له اغزنې ترخې ونې نه');
INSERT INTO "abulsalam" VALUES(88,7,'88:7',' چې نه څربول كوي او نه هېڅ لوږه لرې كوي');
INSERT INTO "abulsalam" VALUES(88,8,'88:8',' څه مخونه به په دغې ورځ كې تروتازه وي');
INSERT INTO "abulsalam" VALUES(88,9,'88:9',' په خپل كوشش به راضي (خوشاله) وي');
INSERT INTO "abulsalam" VALUES(88,10,'88:10',' په اوچت جنت كې به وي');
INSERT INTO "abulsalam" VALUES(88,11,'88:11',' دوى به په دغه (جنت) كې هېڅ بېكاره خبره نه اوري');
INSERT INTO "abulsalam" VALUES(88,12,'88:12',' په دغه (جنت) كې روانه چینه ده');
INSERT INTO "abulsalam" VALUES(88,13,'88:13',' په دغه (جنت) كې اوچت تختونه دي');
INSERT INTO "abulsalam" VALUES(88,14,'88:14',' او (په اندازې سره) ایښودل شوي جامونه دي');
INSERT INTO "abulsalam" VALUES(88,15,'88:15',' او په قطار كې ایښودل شوي بالښتونه دي');
INSERT INTO "abulsalam" VALUES(88,16,'88:16',' او غوړول شوي اعلی فرشونه دي');
INSERT INTO "abulsalam" VALUES(88,17,'88:17',' ایا نو دوى اوښانو ته نه ګوري چې دوى څرنګه پیدا كړى شوي دي؟');
INSERT INTO "abulsalam" VALUES(88,18,'88:18',' او اسمان ته چې دى څنګه اوچت كړى شوى دى؟');
INSERT INTO "abulsalam" VALUES(88,19,'88:19',' او غرونو ته چې دوى څنګه ودرول شوي دي ؟');
INSERT INTO "abulsalam" VALUES(88,20,'88:20',' او ځمكې ته چې څنګه غوړول شوې ده؟');
INSERT INTO "abulsalam" VALUES(88,21,'88:21',' نو ته پند وركړه، بېشكه ته خو يواځې پند وركوونكى يې');
INSERT INTO "abulsalam" VALUES(88,22,'88:22',' ته په دوى باندې مسلط كړى شوى نه يې');
INSERT INTO "abulsalam" VALUES(88,23,'88:23',' مګر هغه څوك چې (له ایمانه) وګرځي او كافر شي');
INSERT INTO "abulsalam" VALUES(88,24,'88:24',' نو الله به ده ته عذاب وركړي تر ټولو لوى عذاب');
INSERT INTO "abulsalam" VALUES(88,25,'88:25',' بېشكه خاص مونږ ته د دوى بېرته راتګ دى');
INSERT INTO "abulsalam" VALUES(88,26,'88:26',' بیا یقینًا د دوى حساب زمونږ په غاړه دى');
INSERT INTO "abulsalam" VALUES(89,1,'89:1',' قسم دى په فجر (سبا) باندې');
INSERT INTO "abulsalam" VALUES(89,2,'89:2',' او په لسو شپو باندې ');
INSERT INTO "abulsalam" VALUES(89,3,'89:3',' او په جفت او طاق باندې');
INSERT INTO "abulsalam" VALUES(89,4,'89:4',' او په شپه باندې كله چې روانه وي');
INSERT INTO "abulsalam" VALUES(89,5,'89:5',' ایا په دغو (قسمونو) كې خاوند د عقل لره (كوم قانع كوونكى) قسم شته؟');
INSERT INTO "abulsalam" VALUES(89,6,'89:6',' ایا تا نه دي كتلي چې ستا رب څه وكړل له ’’قوم عاد‘‘ سره؟');
INSERT INTO "abulsalam" VALUES(89,7,'89:7',' چې ارم و د ستنو والا');
INSERT INTO "abulsalam" VALUES(89,8,'89:8',' هغه چې په ښارونو كې د دوى په مثل نه و پیدا كړى شوي');
INSERT INTO "abulsalam" VALUES(89,9,'89:9',' او ثمودیانو سره (يې څه وكړل) هغه چې په دره كې يې تیګې غوڅې كړې (تراشلې) وې؟');
INSERT INTO "abulsalam" VALUES(89,10,'89:10',' او له میخونو والا فرعون سره (يې څه وكړل)');
INSERT INTO "abulsalam" VALUES(89,11,'89:11',' هغه كسان چې دوى په ښارونو كې سركشي كړې وه');
INSERT INTO "abulsalam" VALUES(89,12,'89:12',' نو په دغو كې يې فساد ډېر كړى و');
INSERT INTO "abulsalam" VALUES(89,13,'89:13',' نو ستا رب په دوى باندې د عذاب قمچینه (كروړه) نازله كړه');
INSERT INTO "abulsalam" VALUES(89,14,'89:14',' بېشكه ستا رب یقینًا په ځاى د انتظار كې دى');
INSERT INTO "abulsalam" VALUES(89,15,'89:15',' نو هر چې انسان دى، كله چې ده لره خپل رب وازمايي، پس ده ته عزت وركړي او ده ته نعمت وركړي، نو دى وايي: زما رب ما ته عزت راكړ');
INSERT INTO "abulsalam" VALUES(89,16,'89:16',' او خو هر كله چې دى وازمايي، پس په ده باندې د ده رزق تنګ كړي، نو دى وايي: زما رب زه سپك كړم');
INSERT INTO "abulsalam" VALUES(89,17,'89:17',' داسې نه ده، بلكې تاسو د یتیم (سره احسان او د هغه) عزت نه كوئ');
INSERT INTO "abulsalam" VALUES(89,18,'89:18',' او مسكین ته په طعام وركولو یو بل ته ترغیب نه وركوئ');
INSERT INTO "abulsalam" VALUES(89,19,'89:19',' او تاسو د میراث (پردى) مال خورئ، په خوړلو ټولو سره');
INSERT INTO "abulsalam" VALUES(89,20,'89:20',' او تاسو له مال سره مینه كوئ، ډېره زیاته مینه');
INSERT INTO "abulsalam" VALUES(89,21,'89:21',' داسې نه ده كله چې ځمكه وټكولى شي، په ټكولو ماتولو سختو سره');
INSERT INTO "abulsalam" VALUES(89,22,'89:22',' او ستا رب راشي او ملايك قطار قطار (راشي)');
INSERT INTO "abulsalam" VALUES(89,23,'89:23',' او په دغې ورځ كې به دوزخ راوستى شي، په دغې ورځ كې به انسان نصیحت (او پند) اخلي او (په دغه وخت كې) د ده لپاره نصیحت به څنګه (نفعمن) وي');
INSERT INTO "abulsalam" VALUES(89,24,'89:24',' دى به وايي: اى كاشكې ما د خپل ژوند لپاره مخكې (څه نېك عملونه) لېږلي وى');
INSERT INTO "abulsalam" VALUES(89,25,'89:25',' نو په دغې ورځ كې به د ده (يعنې د رب) د عذاب په شان هیڅوك عذاب نه وركوي');
INSERT INTO "abulsalam" VALUES(89,26,'89:26',' او د ده د تړلو په شان به هیڅوك تړل نه كوي');
INSERT INTO "abulsalam" VALUES(89,27,'89:27',' اى اطمینان نیوونكیه نفسه!');
INSERT INTO "abulsalam" VALUES(89,28,'89:28',' ته وګرځه خپل رب ته، په داسې حال كې چې خوشاله او راضي كړى شوى به يې');
INSERT INTO "abulsalam" VALUES(89,29,'89:29',' نو ته زما په بنده ګانو كې داخل شه');
INSERT INTO "abulsalam" VALUES(89,30,'89:30',' او زما جنت ته ننوځه');
INSERT INTO "abulsalam" VALUES(90,1,'90:1',' زه قسم خورم په دې ښار باندې');
INSERT INTO "abulsalam" VALUES(90,2,'90:2',' په داسې حال كې چې ته په دې ښار كې اوسېدونكى يې');
INSERT INTO "abulsalam" VALUES(90,3,'90:3',' او په زېږوونكي باندې او په هغه باندې چې ده زېږولي دي');
INSERT INTO "abulsalam" VALUES(90,4,'90:4',' یقینًا یقینًا مونږ انسان په مشقت كې پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(90,5,'90:5',' ایا دغه (كافر انسان) خیال كوي چې بېشكه شان دا دى چې په ده باندې به هیڅوك قادر نشي');
INSERT INTO "abulsalam" VALUES(90,6,'90:6',' دى وايي: ما ډېر زیات مال خرڅ كړى (لګولى) دى');
INSERT INTO "abulsalam" VALUES(90,7,'90:7',' ایا دى خیال كوي چې بېشكه دى هیچا نه دى لیدلى');
INSERT INTO "abulsalam" VALUES(90,8,'90:8',' ایا مونږ ده ته دوه سترګې نه دي وركړي');
INSERT INTO "abulsalam" VALUES(90,9,'90:9',' او یوه ژبه او دوه شونډي');
INSERT INTO "abulsalam" VALUES(90,10,'90:10',' اومونږ ده ته دوه لارې ښودلي دي');
INSERT INTO "abulsalam" VALUES(90,11,'90:11',' نو ده د غونډۍ په ختلو كې هېڅ تكلیف ونه وېست');
INSERT INTO "abulsalam" VALUES(90,12,'90:12',' او ته څه شي پوه كړې چې (ختل د) غونډۍ څه شى دى؟');
INSERT INTO "abulsalam" VALUES(90,13,'90:13',' خلاصول د څټ (ازادول د مريي) دي');
INSERT INTO "abulsalam" VALUES(90,14,'90:14',' او طعام وركول دي په داسې ورځ كې چې د لوږې والا ده');
INSERT INTO "abulsalam" VALUES(90,15,'90:15',' یتیم ته چې د خپلوۍ خاوند وي');
INSERT INTO "abulsalam" VALUES(90,16,'90:16',' یا مسكین ته چې په خاورو ككړ وي');
INSERT INTO "abulsalam" VALUES(90,17,'90:17',' بیا دغه له هغو كسانو ځنې وي چې ایمان يې راوړى وي او یو بل ته د صبر وصیت كوي او یو بل ته د مهربانۍ (او شفقت) كولو وصیت كوي');
INSERT INTO "abulsalam" VALUES(90,18,'90:18',' دغه كسان دښي اړخ والا دي');
INSERT INTO "abulsalam" VALUES(90,19,'90:19',' او هغه كسان چې زمونږ په ایتونو باندې كافران شوي دي دوى د چپ (ګس) اړخ والا دي ');
INSERT INTO "abulsalam" VALUES(90,20,'90:20',' په دوى باندې به سرپوښ كړى شوى اور وي');
INSERT INTO "abulsalam" VALUES(91,1,'91:1',' قسم دى په لمر باندې او د هغه په رڼا باندې');
INSERT INTO "abulsalam" VALUES(91,2,'91:2',' اوسپوږمۍ باندې كله چې په ده پسې وروسته راځي');
INSERT INTO "abulsalam" VALUES(91,3,'91:3',' او په ورځ باندې كله چې دغه (لمر) ښكاره كاندي');
INSERT INTO "abulsalam" VALUES(91,4,'91:4',' او په شپه باندې كله چې دغه (لمر) پټ كاندي (په خپلو تیارو كې)');
INSERT INTO "abulsalam" VALUES(91,5,'91:5',' او په اسمان باندې او په هغه ذات چې دغه يې جوړ كړى دى');
INSERT INTO "abulsalam" VALUES(91,6,'91:6',' او په ځمكه باندې او په هغه ذات چې دغه (ځمكه) يې غوړولې ده');
INSERT INTO "abulsalam" VALUES(91,7,'91:7',' او په نفس باندې او په هغه ذات چې دغه يې ښه برابر كړى دى');
INSERT INTO "abulsalam" VALUES(91,8,'91:8',' نو ده ته يې د ده بدي او د ده تقوٰی (نېكي) الهام كړې (او ورښودلې) ده');
INSERT INTO "abulsalam" VALUES(91,9,'91:9',' چې یقینًا كامیاب شو هغه څوك چې دغه (نفس) يې پاك كړ');
INSERT INTO "abulsalam" VALUES(91,10,'91:10',' او یقینًا نامراده شو هغه څوك چې دغه (نفس) يې په خاورو كې پټ كړ');
INSERT INTO "abulsalam" VALUES(91,11,'91:11',' ثمودیانو د خپلې سركشۍ په سبب (د رسولانو) تكذیب كړى و');
INSERT INTO "abulsalam" VALUES(91,12,'91:12',' كله چې د دوى لوى بدبخت راپورته شو');
INSERT INTO "abulsalam" VALUES(91,13,'91:13',' نو دوى ته د الله رسول وویل: تاسو د الله اوښه او د هغې اوبه څښل پرېږدئ');
INSERT INTO "abulsalam" VALUES(91,14,'91:14',' نو دوى د هغه تكذیب وكړ (دروغجن يې وګاڼه) نو دوى د هغې پړكي (لينګي) پرې كړل (او مړه يې كړه) نو په دوى باندې د دوى رب د خپلو ګناهونو په سبب راګېروونكى عذاب راولېږه، نو دغه (ټول) يې (په هلاكت كې) برابر كړل');
INSERT INTO "abulsalam" VALUES(91,15,'91:15',' او دى (الله) د دغه (هلاكت او عذاب) له انجام نه نه وېرېږي');
INSERT INTO "abulsalam" VALUES(92,1,'92:1',' قسم دى په شپه كله چې دا (هر شى) پټ كړي');
INSERT INTO "abulsalam" VALUES(92,2,'92:2',' او په ورځ باندې كله چې روښانه شي');
INSERT INTO "abulsalam" VALUES(92,3,'92:3',' او په هغه ذات باندې چې نر او ښځه يې پیدا كړي دي');
INSERT INTO "abulsalam" VALUES(92,4,'92:4',' چې بېشكه ستاسو سعي (او عمل) خامخا بېل بېل دي');
INSERT INTO "abulsalam" VALUES(92,5,'92:5',' نو هر چې هغه څوك دى چې وركړه كوي او وېرېږي');
INSERT INTO "abulsalam" VALUES(92,6,'92:6',' او د ډېرې ښكلې كلمې تصدیق كوي');
INSERT INTO "abulsalam" VALUES(92,7,'92:7',' نو مونږ به ضرور ده ته د اسانې لارې اسانتیا وركړو');
INSERT INTO "abulsalam" VALUES(92,8,'92:8',' او هر چې هغه څوك دى چې بخیلي كوي او بې پروايي كوي');
INSERT INTO "abulsalam" VALUES(92,9,'92:9',' او د ډېرې ښكلې كلمې تكذیب كوي (دروغ يې ګڼي)');
INSERT INTO "abulsalam" VALUES(92,10,'92:10',' نو مونږ به ضرور ده ته د سختې لارې اسانتیا وركړو');
INSERT INTO "abulsalam" VALUES(92,11,'92:11',' او كله چې دى په كنده كې غورځېږي (هلاكېږي، نو) د ده مال به د ده هېڅ په كار رانشي');
INSERT INTO "abulsalam" VALUES(92,12,'92:12',' بېشكه خاص زمونږ په ذمه خامخا لاره ښوول دي');
INSERT INTO "abulsalam" VALUES(92,13,'92:13',' او بېشكه خاص زمونږ لپاره اخرت او دنیا ده (د دواړو اختيار دى)');
INSERT INTO "abulsalam" VALUES(92,14,'92:14',' نوما تاسو له داسې اوره ووېرولئ چې لمبې وهي');
INSERT INTO "abulsalam" VALUES(92,15,'92:15',' ده ته به نه ننوځي مګر ترټولو بدبخته');
INSERT INTO "abulsalam" VALUES(92,16,'92:16',' هغه چې تكذیب يې كړى او مخ يې (له حقه) ګرځولى دى');
INSERT INTO "abulsalam" VALUES(92,17,'92:17',' او له ده نه به خامخا لرې ساتلى شي ښه وېرېدونكى');
INSERT INTO "abulsalam" VALUES(92,18,'92:18',' هغه چې خپل مال د دې لپاره وركوي چې پاك شي');
INSERT INTO "abulsalam" VALUES(92,19,'92:19',' او له ده سره (د ده په غاړه) د هیچا لپاره څه نعمت (او احسان) نشته چې بدله يې وركولى شي ');
INSERT INTO "abulsalam" VALUES(92,20,'92:20',' مګر د خپل رب د مخ لټونې لپاره چې تر ټولو اوچت دى');
INSERT INTO "abulsalam" VALUES(92,21,'92:21',' او خامخا ژر به دى راضي شي`');
INSERT INTO "abulsalam" VALUES(93,1,'93:1',' قسم دى په څاښت باندې');
INSERT INTO "abulsalam" VALUES(93,2,'93:2',' او په شپه باندې كله چې قراره شي');
INSERT INTO "abulsalam" VALUES(93,3,'93:3',' چې ته خپل رب نه پریښى يې او نه يې (درسره) بغض ساتلى دى');
INSERT INTO "abulsalam" VALUES(93,4,'93:4',' او خامخا اخرت تا لپاره له دنیا نه ډېر غوره دى');
INSERT INTO "abulsalam" VALUES(93,5,'93:5',' او خامخا ژر به تا ته ستا رب دركړي، نو ته به راضي (خوشحاله) شې');
INSERT INTO "abulsalam" VALUES(93,6,'93:6',' ایا ته يې یتیم نه وې موندلى؟ پس پناه ځاى يې دركړ');
INSERT INTO "abulsalam" VALUES(93,7,'93:7',' او ته يې ناخبره (او ناپوه) وموندلې، نو لار ښوونه يې (درته) وكړه');
INSERT INTO "abulsalam" VALUES(93,8,'93:8',' او ته يې فقیر وموندلې، نو غني يې كړې');
INSERT INTO "abulsalam" VALUES(93,9,'93:9',' نو هر چې یتیم دى، نو ته (ده ته) قهر مه كوه');
INSERT INTO "abulsalam" VALUES(93,10,'93:10',' او هر چې سوالګرى دى، نو ته يې مه رټه');
INSERT INTO "abulsalam" VALUES(93,11,'93:11',' او پاتې شو (نعمت) نو ته د خپل رب نعمت بیانوه');
INSERT INTO "abulsalam" VALUES(94,1,'94:1',' ایا مونږ تا لره ستا سینه نه ده فراخه كړې');
INSERT INTO "abulsalam" VALUES(94,2,'94:2',' او له تا نه مو ستا دروند بار كوز كړى');
INSERT INTO "abulsalam" VALUES(94,3,'94:3',' هغه (بار) چې ستا شا يې كږه (او) درنه كړې وه');
INSERT INTO "abulsalam" VALUES(94,4,'94:4',' او تا لپاره مو ستا ذكر اوچت كړ');
INSERT INTO "abulsalam" VALUES(94,5,'94:5',' نو بېشكه له سختۍ سره اساني شته');
INSERT INTO "abulsalam" VALUES(94,6,'94:6',' بېشكه له سختۍ سره اساني شته');
INSERT INTO "abulsalam" VALUES(94,7,'94:7',' نو كله چې ته فارغ شې، نو (په عبادت كې،) ځان ستړى كوه');
INSERT INTO "abulsalam" VALUES(94,8,'94:8',' او خاص خپل رب ته رغبت (او زاري) كوه');
INSERT INTO "abulsalam" VALUES(95,1,'95:1',' قسم دى په انځر او زیتون (ښوون) باندې');
INSERT INTO "abulsalam" VALUES(95,2,'95:2',' او په طور سینین (غره) باندې');
INSERT INTO "abulsalam" VALUES(95,3,'95:3',' او په دې امان وركوونكي/امن والا ښار باندې');
INSERT INTO "abulsalam" VALUES(95,4,'95:4',' چې یقینًا یقینًا مونږ انسان په ډېر ښكلي صورت (او اعتدال) كې پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(95,5,'95:5',' بیا دى مونږ وګرځاوه د ښكتو ډېر ښكته');
INSERT INTO "abulsalam" VALUES(95,6,'95:6',' مګر هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړې دي، نو د دوى لپاره نه ختمېدونكى اجر دى');
INSERT INTO "abulsalam" VALUES(95,7,'95:7',' نو (اى د قیامت منكره!) له دې نه وروسته تا څه شى د قیامت په هكله دروغ ګڼلو ته اماده كوي؟');
INSERT INTO "abulsalam" VALUES(95,8,'95:8',' ایا الله تر ټولو لوى حاكم نه دى؟');
INSERT INTO "abulsalam" VALUES(96,1,'96:1',' ته د خپل هغه رب په نامه سره ولوله چې پیدا كړي يې دي (ټول مخلوقات)');
INSERT INTO "abulsalam" VALUES(96,2,'96:2',' ده انسان له پرنې (ټوټې) وینې نه پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(96,3,'96:3',' ته ولوله، په داسې حال كې چې ستا رب تر ټولو لوى كریم دى');
INSERT INTO "abulsalam" VALUES(96,4,'96:4',' هغه چې په قلم سره يې ښوونه كړې ده');
INSERT INTO "abulsalam" VALUES(96,5,'96:5',' انسان ته يې هغه څه ښودلي دي چې دى پرې نه پوهېده');
INSERT INTO "abulsalam" VALUES(96,6,'96:6',' حقه دا خبره ده چې یقینًا انسان خامخا سركشي كوي');
INSERT INTO "abulsalam" VALUES(96,7,'96:7',' په دې وجه چې دى خپل ځان ویني چې دى بې پروا (بې حاجته) شو');
INSERT INTO "abulsalam" VALUES(96,8,'96:8',' بېشكه خاص ستا رب ته بېرته ورتلل دي');
INSERT INTO "abulsalam" VALUES(96,9,'96:9',' ته ما ته خبر راكړه د هغه كس چې منع كوي');
INSERT INTO "abulsalam" VALUES(96,10,'96:10',' بنده (محمدصلى الله عليه وسلم) لره كله چې دى لمونځ كوي');
INSERT INTO "abulsalam" VALUES(96,11,'96:11',' ته ما ته خبر راكړه كه چېرې دغه (بنده) په هدایت وي');
INSERT INTO "abulsalam" VALUES(96,12,'96:12',' یا په تقوٰی (پرهېزګارۍ) سره حكم كوي');
INSERT INTO "abulsalam" VALUES(96,13,'96:13',' ته ما ته خبر راكړه كه دغه (منع كوونكى) تكذیب كوي او (له حق نه) مخ اړوي');
INSERT INTO "abulsalam" VALUES(96,14,'96:14',' ایا دى نه پوهېږي چې بېشكه الله ویني؟');
INSERT INTO "abulsalam" VALUES(96,15,'96:15',' داسې نه ده! قسم دى كه دى منع نشو، (نو) مونږ به (دى) خامخا ضرور ډېر سخت راكاږو د تندي (وچولي) په وېښتانو سره');
INSERT INTO "abulsalam" VALUES(96,16,'96:16',' داسې تندى چې دروغجن (او) خطا كار دى');
INSERT INTO "abulsalam" VALUES(96,17,'96:17',' نو دى دې خپل مجلس والا راوبلي');
INSERT INTO "abulsalam" VALUES(96,18,'96:18',' ژر ده چې مونږ به ډېر سخت تند خویه ملايك راوبلو');
INSERT INTO "abulsalam" VALUES(96,19,'96:19',' داسې نه ده ته د ده خبره مه منه، او (الله ته) سجده كوه او (الله ته) ښه نژدې شه ');
INSERT INTO "abulsalam" VALUES(97,1,'97:1',' بېشكه مونږ دا (قرآن) د قدر په شپه كې نازل كړى دى');
INSERT INTO "abulsalam" VALUES(97,2,'97:2',' او ته څه شي پوه كړې چې د قدر شپه څه شى دى؟');
INSERT INTO "abulsalam" VALUES(97,3,'97:3',' د قدر شپه له زرو میاشتو نه غوره ده');
INSERT INTO "abulsalam" VALUES(97,4,'97:4',' په دغه (شپه) كې (نور) ملايك او روح (جبریل) د خپل رب په حكم سره نازلېږي لپاره (د پوره كولو) د هر (مقرر شوي) كار');
INSERT INTO "abulsalam" VALUES(97,5,'97:5',' (دغه شپه) سلامتیا ده، دغه (شپه) تر سبا ختلو پورې ده');
INSERT INTO "abulsalam" VALUES(98,1,'98:1',' هغه كسان چې كافران شوي دي، چې كتابیان او مشركان دي، جدا كېدونكي نه وو تر دې چې دوى ته ښكاره دلیل راشي');
INSERT INTO "abulsalam" VALUES(98,2,'98:2',' چې د الله له جانبه رسول دى، چې (دوى ته) ډېرې پاكې صحیفې لولي');
INSERT INTO "abulsalam" VALUES(98,3,'98:3',' په دغو (صحیفو) كې ډېر سم (او درست) حكمونه (او فریضې) دي');
INSERT INTO "abulsalam" VALUES(98,4,'98:4',' او اهل كتاب په خپل مینځ كې سره جدا (او مختلف) شوي نه وو مګر وروسته له دې چې دوى ته ښكاره دلیل راغى');
INSERT INTO "abulsalam" VALUES(98,5,'98:5',' حال دا چې دوى ته حكم نه و وركړى شوى مګر په دې چې دوى دې د الله عبادت كوي، په داسې حال كې چې دین (عبادت) دغه (الله) لره خالص كوونكي وي (او) حنیفان (له باطله حق ته راګرځېدونكي) وي، او (د دې چې) دوى دې لمونځ قايموي او زكاۃت دې وركوي اوهم دغه ډېر محكم دین دى');
INSERT INTO "abulsalam" VALUES(98,6,'98:6',' بېشكه هغه كسان چې كافران شوي دي، چې اهل كتاب او مشركان دي، (دوى به) د دوزخ په اور كې وي، چې په ده كې به تل ترتله وي. هم دغه كسان د خلقو بدترین دي');
INSERT INTO "abulsalam" VALUES(98,7,'98:7',' بېشكه هغه كسان چې ایمان يې راوړى دى او نېك عملونه يې كړي دي همدغه كسان د خلقو بهترین دي');
INSERT INTO "abulsalam" VALUES(98,8,'98:8',' د خپل رب په نیز د دوى بدله د تل اوسېدنې جنتونه دي چې د هغو له لاندې ولې بهېږي، چې دوى به په دغو كې تل ترتله همېشه وي، الله له دوى نه راضي شوى دى او دوى له هغه نه راضي شوي دي، دغه (وعده) د هغه چا لپاره ده چې له خپل رب نه وېرېږي');
INSERT INTO "abulsalam" VALUES(99,1,'99:1',' كله چې ځمكه وخوزلى شي په خپلو سختو خوزولو سره');
INSERT INTO "abulsalam" VALUES(99,2,'99:2',' او ځمكه خپل درانه بارونه بهر ته راوباسي');
INSERT INTO "abulsalam" VALUES(99,3,'99:3',' او انسان به ووايي چې په دې څه شوي دي');
INSERT INTO "abulsalam" VALUES(99,4,'99:4',' په دغې ورځې كې به دا ځمكه خپلې خبرې (حالات) بیانوي');
INSERT INTO "abulsalam" VALUES(99,5,'99:5',' په دې سبب چې ستا رب به دې ته وحي وكړي');
INSERT INTO "abulsalam" VALUES(99,6,'99:6',' په دغې ورځ كې به خلق بېرته راګرځي، په داسې حال كې چې بېل بېل (او مختلف) به وي، د دې لپاره چې دوى ته خپل عملونه وښودل شي');
INSERT INTO "abulsalam" VALUES(99,7,'99:7',' نو هغه څوك چې د یوې ذرې په اندازه نېك عمل وكړي (،نو) دى به هغه وویني');
INSERT INTO "abulsalam" VALUES(99,8,'99:8',' او هغه څوك چې د یوې ذرې په اندازه د شر (او بدۍ) عمل كوي (، نو) دى به هغه وویني');
INSERT INTO "abulsalam" VALUES(100,1,'100:1',' قسم دى په تېزو ځغلېدونكو اسونو، چې له سینو او ګېډو نه اواز ایستونكي دي');
INSERT INTO "abulsalam" VALUES(100,2,'100:2',' پس (قسم دى) په هغو اسونو چې په خپلو سُمانو سره تیږې وهي او اور را ایستونكي دي');
INSERT INTO "abulsalam" VALUES(100,3,'100:3',' پس (قسم دى) په هغو اسونو چې د سبا په وخت كې لوټمار كوونكي دي');
INSERT INTO "abulsalam" VALUES(100,4,'100:4',' نو په دغه وخت كې دغه (اسونه) ګرد پورته كوي');
INSERT INTO "abulsalam" VALUES(100,5,'100:5',' پس په دغه وخت كې د لويې ډلې په مینځ كې ننوځي');
INSERT INTO "abulsalam" VALUES(100,6,'100:6',' چې بېشكه انسان د خپل رب ډېر ناشكره دى');
INSERT INTO "abulsalam" VALUES(100,7,'100:7',' او بېشكه دى په دې خبره یقینًا ښه خبردار (او ګواه) دى');
INSERT INTO "abulsalam" VALUES(100,8,'100:8',' او بېشكه دى د مال په مینه كې یقینًا ډېر سخت دى');
INSERT INTO "abulsalam" VALUES(100,9,'100:9',' ایا نو دى نه پوهېږي كله چې هغه (مړي) راوښكل شي چې په قبرونو كې دي؟');
INSERT INTO "abulsalam" VALUES(100,10,'100:10',' او هغه څه راښكاره كړى شي چې په سینو كې دي');
INSERT INTO "abulsalam" VALUES(100,11,'100:11',' بېشكه د دوى رب په دغې ورځ كې په دوى باندې یقینًا ښه خبردار دى');
INSERT INTO "abulsalam" VALUES(101,1,'101:1',' (د زړونو) ټكوونكى (قیامت)');
INSERT INTO "abulsalam" VALUES(101,2,'101:2',' (د زړونو) ټكوونكى څه شى دى؟');
INSERT INTO "abulsalam" VALUES(101,3,'101:3',' او ته څه شي پوه كړې چې (د زړونو) ټكوونكى څه شى دى؟');
INSERT INTO "abulsalam" VALUES(101,4,'101:4',' (دى به زړونه ټكوي) په هغې ورځ كې چې خلق به د خواره كړى شویو پتنګانو په شان شي');
INSERT INTO "abulsalam" VALUES(101,5,'101:5',' او غرونه به د ډانډس كړى شوې رنګینې وړۍ په شان شي');
INSERT INTO "abulsalam" VALUES(101,6,'101:6',' نو هر چې هغه څوك دى چې د هغه تلې درنې شوې');
INSERT INTO "abulsalam" VALUES(101,7,'101:7',' نو دى به په ډېر ښه ژوندون كې وي');
INSERT INTO "abulsalam" VALUES(101,8,'101:8',' او پاتې شو هغه كس چې د هغه تلې سپكې شوې');
INSERT INTO "abulsalam" VALUES(101,9,'101:9',' نو د ده استوګنځى هاویه ده');
INSERT INTO "abulsalam" VALUES(101,10,'101:10',' او ته څه شي پوه كړې چې دغه (هاویه) څه شى دى؟');
INSERT INTO "abulsalam" VALUES(101,11,'101:11',' (دغه) ډېر ګرم اور دى');
INSERT INTO "abulsalam" VALUES(102,1,'102:1',' تاسو غافله كړئ فخر كولو په یو بل باندې په ډېروالي (د مال، اولاد او قام) سره ');
INSERT INTO "abulsalam" VALUES(102,2,'102:2',' تر هغه پورې چې تاسو هدېرو ته ورشئ');
INSERT INTO "abulsalam" VALUES(102,3,'102:3',' داسې نه ده په كار، ژر ده چې تاسو به پوه شئ');
INSERT INTO "abulsalam" VALUES(102,4,'102:4',' بیا بېخي داسې نه ده، ژر ده چې تاسو به پوه شئ');
INSERT INTO "abulsalam" VALUES(102,5,'102:5',' داسې نه ده كه تاسو په علم اليقين سره پوهېدلى');
INSERT INTO "abulsalam" VALUES(102,6,'102:6',' تاسو به خامخا ضرور دوزخ وینئ');
INSERT INTO "abulsalam" VALUES(102,7,'102:7',' بیا به تاسو خامخا ضرور دغه (دوزخ) د یقین په سترګو وینئ');
INSERT INTO "abulsalam" VALUES(102,8,'102:8',' بیا به په دغې ورځ كې له تاسو نه خامخا ضرور د نعمتونو په باره كې تپوس كېږي');
INSERT INTO "abulsalam" VALUES(103,1,'103:1',' قسم دى په عصر (مازیګر، یا زمانه)');
INSERT INTO "abulsalam" VALUES(103,2,'103:2',' چې بېشكه یقینًا انسان په زیان (او تاوان) كې دى');
INSERT INTO "abulsalam" VALUES(103,3,'103:3',' غیر له هغو كسانو نه چې ایمان يې راوړى دى او نېك عملونه يې كړې دي او یو بل ته يې د حق (دین) وصیت كړى دى، او یو بل ته يې د صبر وصیت كړى دى ');
INSERT INTO "abulsalam" VALUES(104,1,'104:1',' هلاكت دى د هر ډېر غیبت كوونكي، ډېر عیبت لګوونكي لپاره');
INSERT INTO "abulsalam" VALUES(104,2,'104:2',' هغه چې مال يې جمع كړى دى او دغه يې بیا بیا شمېرلى (او ذخیره كړى) دى');
INSERT INTO "abulsalam" VALUES(104,3,'104:3',' دى خیال كوي چې بېشكه د ده مال به دى تل ژوندى وساتي؟');
INSERT INTO "abulsalam" VALUES(104,4,'104:4',' داسې هیڅكله نشي كېدى، دى به خامخا ضرور په حطمه (ماتوونكي او سوځوونكي دوزخ) كې وغورځول شي');
INSERT INTO "abulsalam" VALUES(104,5,'104:5',' او ته څه شي پوه كړې چې حطمه څه شى دى؟');
INSERT INTO "abulsalam" VALUES(104,6,'104:6',' د الله هغه بل كړى شوى اور دى');
INSERT INTO "abulsalam" VALUES(104,7,'104:7',' هغه چې په زړونو راخېژي (او غالب كېږي)');
INSERT INTO "abulsalam" VALUES(104,8,'104:8',' بېشكه دغه (اور) به په دوى باندې (له هره جانبه) رابند كړى شوى وي');
INSERT INTO "abulsalam" VALUES(104,9,'104:9',' په اوږدو (راښكلى شویو) ستنو كې');
INSERT INTO "abulsalam" VALUES(105,1,'105:1',' ایا ته نه يې خبر شوى چې ستا رب له فیل والاو سره څنګه كار كړى و؟');
INSERT INTO "abulsalam" VALUES(105,2,'105:2',' ایا د هغوى چل (او مكر) يې په ګمراهۍ او زیان كې نه و ګرځولى؟');
INSERT INTO "abulsalam" VALUES(105,3,'105:3',' او په دوى باندې يې ډلې ډلې مرغان راخوشې كړل');
INSERT INTO "abulsalam" VALUES(105,4,'105:4',' چې دغو (مرغانو) به دوى لره په داسې كاڼو ویشتل چې له خټو نه جوړ، په اور پخ شوي وو');
INSERT INTO "abulsalam" VALUES(105,5,'105:5',' نو دغه (خلق) يې د خوړل شویو وښو په شان وګرځول');
INSERT INTO "abulsalam" VALUES(106,1,'106:1',' د قریشو له یو بل سره د الفت له وجې نه');
INSERT INTO "abulsalam" VALUES(106,2,'106:2',' د ژمي او اوړي په سفر كې د دوى د الفت په وجه');
INSERT INTO "abulsalam" VALUES(106,3,'106:3',' نو لازم دي چې دوى د دې كور د رب عبادت وكړي');
INSERT INTO "abulsalam" VALUES(106,4,'106:4',' هغه (رب) چې دوى ته يې له لوږې نه (د بچ كولو لپاره) طعام وركړى دى او دوى ته يې له وېرې نه (د ساتلو لپاره) امن (او بې خوفي) وركړې ده');
INSERT INTO "abulsalam" VALUES(107,1,'107:1',' ایا تا هغه كس لیدلى چې د بدلي ورځ (قیامت) دروغ ګڼي');
INSERT INTO "abulsalam" VALUES(107,2,'107:2',' نو همدغه هغه كس دى چې یتیم ته دیكې وركوي');
INSERT INTO "abulsalam" VALUES(107,3,'107:3',' او مسكین ته په طعام وركولو ترغیب نه وركوي');
INSERT INTO "abulsalam" VALUES(107,4,'107:4',' نو هلاكت دى لمونځ كوونكو لره');
INSERT INTO "abulsalam" VALUES(107,5,'107:5',' هغه كسان چې دوى له خپلو لمونځونو نه غافله (او اعراض كوونكي) وي');
INSERT INTO "abulsalam" VALUES(107,6,'107:6',' هغه كسان چې دوى ریاكاري كوي');
INSERT INTO "abulsalam" VALUES(107,7,'107:7',' او دوى ماعون (د استعمال عام څیزونه هم له یو بل نه) منع كوي');
INSERT INTO "abulsalam" VALUES(108,1,'108:1',' بېشكه مونږ تا ته كوثر دركړى دى');
INSERT INTO "abulsalam" VALUES(108,2,'108:2',' نو ته د خپل رب لپاره لمونځ كوه او قرباني كوه');
INSERT INTO "abulsalam" VALUES(108,3,'108:3',' بېشكه ستا دښمن هم هغه بې بنیاده (او لاولده) دى');
INSERT INTO "abulsalam" VALUES(109,1,'109:1',' (اى نبي!) ته (دوى ته) ووایه: اى كافرانو!');
INSERT INTO "abulsalam" VALUES(109,2,'109:2',' زه د هغو (شیانو) عبادت نه كوم چی تاسو يې عبادت كوئ');
INSERT INTO "abulsalam" VALUES(109,3,'109:3',' او نه تاسو د هغه ذات عبادت كوونكي يئ چې زه يې عبادت كوم ');
INSERT INTO "abulsalam" VALUES(109,4,'109:4',' او نه زه د هغه شي عبادت كوونكى یم چی تاسو يې عبادت كړى دى ');
INSERT INTO "abulsalam" VALUES(109,5,'109:5',' او نه تاسو عبادت كوونكي یئ د هغه ذات چې زه يې عبادت كوم ');
INSERT INTO "abulsalam" VALUES(109,6,'109:6',' تاسو لپاره ستاسو دین دى او زما لپاره زما دین دى');
INSERT INTO "abulsalam" VALUES(110,1,'110:1',' كله چې د الله مدد راشي او فتح');
INSERT INTO "abulsalam" VALUES(110,2,'110:2',' او ته خلق ووینې چې د الله په دین كې ډلې ډلې داخلېږي');
INSERT INTO "abulsalam" VALUES(110,3,'110:3',' نو ته پاكي بیانوه د خپل رب له ستاينې سره او له ده نه مغفرت غواړه، بېشكه دى تل ښه توبه قبلوونكى دى');
INSERT INTO "abulsalam" VALUES(111,1,'111:1',' د ابو لهب دواړه لاسونه دې هلاك شي او دى هلاك شو');
INSERT INTO "abulsalam" VALUES(111,2,'111:2',' د ده هېڅ په كار رانغى د ده مال او (نه) هغه څه چې ده ګټلي وو');
INSERT INTO "abulsalam" VALUES(111,3,'111:3',' ژر ده چې دى به لمبې وهونكي اور ته داخل شي');
INSERT INTO "abulsalam" VALUES(111,4,'111:4',' او د ده ښځه هم، (زه د دغې) د لرګو اوچتوونكې (مذمت كوم)');
INSERT INTO "abulsalam" VALUES(111,5,'111:5',' د دې په غاړه كې د كجورې د پټ مضبوطه رسۍ ده');
INSERT INTO "abulsalam" VALUES(112,1,'112:1',' (اى نبي!) ته (دوى ته) ووایه: شان دا دى چی الله یو دى ');
INSERT INTO "abulsalam" VALUES(112,2,'112:2',' هم دا الله بې نیاز (بې حاجته) دى');
INSERT INTO "abulsalam" VALUES(112,3,'112:3',' نه يې (څوك) زېږولى دى او نه دى (له چا) زېږول شوى دى');
INSERT INTO "abulsalam" VALUES(112,4,'112:4',' او د ده هیڅوك سیال (او) برابر نشته');
INSERT INTO "abulsalam" VALUES(113,1,'113:1',' (اى نبي) ته ووایه: زه په رب د سبا پورې پناه نيسم');
INSERT INTO "abulsalam" VALUES(113,2,'113:2',' د هر هغه شي له شر نه چې ده پیدا كړى دى');
INSERT INTO "abulsalam" VALUES(113,3,'113:3',' او د تیارې شپې له شر نه كله چې تكه توره (او خوره) شي');
INSERT INTO "abulsalam" VALUES(113,4,'113:4',' او له شره د هغو (جادوګرو) ښځو چې په غوټو كې پوكي وهونكې دي');
INSERT INTO "abulsalam" VALUES(113,5,'113:5',' او د حسد كوونكي له شر نه كله چی دى حسد (شروع او ښكاره) كړي');
INSERT INTO "abulsalam" VALUES(114,1,'114:1',' (اى نبي) ته ووایه: زه د خلقو په رب پورې پناه نیسم');
INSERT INTO "abulsalam" VALUES(114,2,'114:2',' چې د خلقو بادشاه دى');
INSERT INTO "abulsalam" VALUES(114,3,'114:3',' د خلقو (حقیقي) معبود دى ');
INSERT INTO "abulsalam" VALUES(114,4,'114:4',' له شره د وسوسې اچوونكي، تښتېدونكي');
INSERT INTO "abulsalam" VALUES(114,5,'114:5',' هغه چې د خلقو په سینو كې وسوسې اچوي');
INSERT INTO "abulsalam" VALUES(114,6,'114:6',' چی (دغه وسوسې اچوونكي) له پېریانو او انسانانو ځنې دي');
