INSERT INTO "african_dev" VALUES(1,1,'1:1','Ku lw’erinnya lya Katonda omusaasizi oweekisa ekingi, omusaasizi oweekisa ekyenjawulo.');
INSERT INTO "african_dev" VALUES(1,2,'1:2','Amatendo gonna ga Katonda Omulezi w’ebitonde byonna.');
INSERT INTO "african_dev" VALUES(1,3,'1:3','Omusaasizi oweekisa ekingi, omusaasizi oweekisa ekyenjawulo.');
INSERT INTO "african_dev" VALUES(1,4,'1:4','Nannyini buyinza yekka ku lunaku lw''okusasula (olunaku lw''enkomerero).');
INSERT INTO "african_dev" VALUES(1,5,'1:5','Gwe wekka gwe tusinza era ggwe wekka gwetusaba okutubeera.');
INSERT INTO "african_dev" VALUES(1,6,'1:6','Tulungamye mu kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(1,7,'1:7','Ekkubo ly’abo bewagabira ebyengera, abatali abo bewasunguwalira era abatali abo abaabula.');
INSERT INTO "african_dev" VALUES(2,1,'2:1','Alif-laam-miim.');
INSERT INTO "african_dev" VALUES(2,2,'2:2','Ekitabo ekyo (Kur''ani) tekiriimu kubuusabuusa, (kyassibwa ku Nabbi Muhammad) nga bulungamu eri abatya Katonda.');
INSERT INTO "african_dev" VALUES(2,3,'2:3','Abo abakkiriza ebitalabikako ne bayimirizaawo e sswala, era nga bagaba ku ebyo byetubagabirira.');
INSERT INTO "african_dev" VALUES(2,4,'2:4','Era b’ebo abakkiriza ebyakussibwako n''ebyo ebyassibwa ku babaka abaakusooka, era nga bakakasa olunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(2,5,'2:5','Abo be bali ku bulungamu okuva eri omulezi waabwe (Katonda), era abo be b’okulokoka.');
INSERT INTO "african_dev" VALUES(2,6,'2:6','Mazima abo abatakkiriza (ebyo ebyassibwa ku Nabbi Muhammad) kyenkanankana gye bali, obatiisizza oba tobatiisizza ssi ba kukkiriza.');
INSERT INTO "african_dev" VALUES(2,7,'2:7','Katonda yazibikira emitima gyabwe n’okuwulira kwabwe, nga ne ku maaso gaabwe kuliko ekibikka. Era balina e bibonerezo ebinene.');
INSERT INTO "african_dev" VALUES(2,8,'2:8','Era waliwo abantu abamu abagamba nti tukkiriza Katonda n''olunaku lw’enkomerero so nga ssi bakkiriza (bannanfuusi).');
INSERT INTO "african_dev" VALUES(2,9,'2:9','Bakwenyakwenya Katonda awamu n''abakkiriza so nga beekwenyakwenya bokka naye bambi tebakitegeera.');
INSERT INTO "african_dev" VALUES(2,10,'2:10','Balina obulwadde mu mitima gyabwe. Katonda n''abongera obulwadde, era balina n''ekibonerezo ekiruma olw''ebyo bye baalimbanga.');
INSERT INTO "african_dev" VALUES(2,11,'2:11','Bwe bagambibwa nti temwonoona mu nsi. Bagamba nti, mazima ddala ffe tuli bakozi ba bulungi.');
INSERT INTO "african_dev" VALUES(2,12,'2:12','So ng'' ate mazima ddala bo be bonoonyi naye tebakitegeera.');
INSERT INTO "african_dev" VALUES(2,13,'2:13','Era bwebaba bagambiddwa nti mukkirize nga abantu (abalala) bwe bakkiriza. Baddamu nti, tukkirize nga ababuyabuya bwe bakkiriza? abange mazima ddala bo be babuyabuya naye tebamanyi.');
INSERT INTO "african_dev" VALUES(2,14,'2:14','Bwe basisinkana n''abakkiriza bagamba nti tukkirizza, bwe badda eri abakulembeze baabwe bagamba nti mazima ddala ffe tuli wamu nammwe. Mazima bali tubaguyaaguya buguyaaguya.');
INSERT INTO "african_dev" VALUES(2,15,'2:15','Katonda abaguyaaguya era n''abongerayo mu bubuze bwabwe nga babulubuuta.');
INSERT INTO "african_dev" VALUES(2,16,'2:16','Abo b''ebo abaagula obubuze mu bulungamu. Okusuubula kwabwe tekwakola magoba, era tebaali ba kulungama.');
INSERT INTO "african_dev" VALUES(2,17,'2:17','Ekifaananyi kyabwe (abannanfuusi) kiringa eky''omuntu eyakuma omuliro, bwe gwamulisa ebimwetoolodde, Katonda n''aggyawo e kitangaala kyabwe n''abaleka mu nzikiza, nga tebakyalaba.');
INSERT INTO "african_dev" VALUES(2,18,'2:18','Bakiggala, ba kassiru, ba muzibe, tebayinza kudda (ku kkubo ggolokofu).');
INSERT INTO "african_dev" VALUES(2,19,'2:19','Oba e kifaananyi kyabwe kiringa eky''abantu abali mu nkuba etonnya ng''erimu e kizikiza e kikunidde nga mulimu n''okubwatuka, n''okumyansa, ne kibatuusa okussa ennwe zaabwe mu matu gaabwe olw''okutya okubwatuka nga batya okufa. Katonda yeetoolodde abakaafiiri.');
INSERT INTO "african_dev" VALUES(2,20,'2:20','Okumyansa (okwo) kubula okubaziba amaaso. Buli lwe kubamulisiza ne batambula, ate bwe kuzikira nebayimirira. Singa Katonda yayagala yandibaggyeeko okuwulira kwabwe, n''okulaba kwabwe. Mazima Katonda muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(2,21,'2:21','Abange (mmwe) abantu musinze omulezi wa mmwe eyabatonda n''atonda n''abo abaabakulembera mulyoke mumutye mu ngeri eyannamaddala.');
INSERT INTO "african_dev" VALUES(2,22,'2:22','(Katonda) y''oyo eyabakolera e nsi nga kyaliiro n''abakolera e ggulu nga kasolya, era n''assa amazzi (e nkuba) okuva waggulu, n''ameza nago e bibala bibeere eby''okulya bya mmwe. Kale nno Katonda temumuteekangako e bintu e birala, ate nga mumanyi (nti ye yekka y''ateekwa okusinzibwa)');
INSERT INTO "african_dev" VALUES(2,23,'2:23','Naye bwe muba mubuusabuusa mu bye twassa ku muddu waffe (Nabbi Muhammad), kale muleeteeyo E ssuula emu efaananako nga Kur''ani, muyite abajulizi ba mmwe (bemulowooza nti bayinza okubayamba) nga ssi Katonda, bwe muba nga muli b''amazima.');
INSERT INTO "african_dev" VALUES(2,24,'2:24','Bwe muba nga temukikoze, era nga temugenda kukikola, muteekwa mwerinde omuliro, nga enku zaagwo bantu n''amayinja, ogwategekerwa abawakanya amateeka ga Katonda.');
INSERT INTO "african_dev" VALUES(2,25,'2:25','Ate sanyusa abo abakkiriza ne bakola e mirimu e mirungi nti bajja kufuna e jjana ezikulukutiramu e migga. Buli lwe banaaweebwanga e kibala mu jjana banaagambanga nti ekyo kye twaweebwanga edda (nga tuli ku nsi), binabaweebwanga nga mundabika bifaanana nabiri ebyo ku nsi (naye nga mu buwoomi byanjawulo), era abasajja baliweebwa abakyala abaatukuzibwa, era ba kubeeramu lubeerera.');
INSERT INTO "african_dev" VALUES(2,26,'2:26','Mazima Katonda takwatibwa nsonyi kukuba kifaananyi newakubadde eky’ensiri n''ekintu ekirala ekigisingako. Bo abakkiriza bamanyira ddala nti ago ge mazima agava eri omulezi waabwe (Katonda), kyokka abagaana okukkiriza bagamba nti Katonda yagenderera ki mu kifaananyi kino! N''olwekyo aleka bangi ne babula olw''ekifaananyi kino so nga ate alungamya nakyo bangi. Wabula taleka muntu yenna kubula okugyako abo abamenya ebiragiro bye.');
INSERT INTO "african_dev" VALUES(2,27,'2:27','Abo abamenya e ndagaano ya Katonda oluvanyuma lw''okugikakasa, nebakutula ebyo Katonda bye yalagira okuyunga, era ne boonoona mu nsi, abo nno be b''okufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(2,28,'2:28','Mugaana mutya okukkiriza Katonda nga temwaliwo n''abawa obulamu, oluvanyuma abatta, ate waakubazuukiza era nga gyali gye mugenda okudda!.');
INSERT INTO "african_dev" VALUES(2,29,'2:29','Ye (Katonda) y''oyo eyabatondera byonna ebiri mu nsi, bwe yamaliriza n''alyoka azzaako e ggulu, n''alikola nga lya myaliiro musanvu. Katonda mumanyi wa buli kintu kyonna.');
INSERT INTO "african_dev" VALUES(2,30,'2:30','Jjukira Mukama (Katonda) wo bwe yagamba ba malayika nti mazima nja kuteeka mu nsi omusigire, ne bagamba nti, ogenda okugissaamu (omuntu) anaagyonooneramu n''ayiwa n''omusaayi, ng''ate ffe tukutendereza era nga tukugulumiza! Katonda (naddamu) n’abagamba nti nze mmanyi ebyo byemutamanyi.');
INSERT INTO "african_dev" VALUES(2,31,'2:31','(Katonda) N''ayigiriza Adamu amannya g''ebintu byonna oluvanyuma n''abiraga ba Malayika n''abagamba nti; mumbuulire amannya g''ebintu bino bwe mubeera nga mwogera mazima (Nti mwe musaanira okuba abasigire bange ku nsi).');
INSERT INTO "african_dev" VALUES(2,32,'2:32','Nebagamba nga batendereza nti; oli wanjawulo, ffe tetulina kyetumanyi okugyako ekyo kyoba otuyigirizza. Mazima ggwe muyitirivu w''okumanya omuyitirivu wa magezi.');
INSERT INTO "african_dev" VALUES(2,33,'2:33','Katonda naagamba nti; kale Adamu babuulire amannya gaabyo. Bwe yamaliriza okubattottolera amannya gaabyo, Katonda kwe kubagamba nti, saabagambye nti mazima nze mmanyi ebikusike mu nsi ne mu ggulu, era nga mmanyi bye mwolesa n''ebyo bye mukweka.');
INSERT INTO "african_dev" VALUES(2,34,'2:34','Era jjukira bwe twagamba ba Malayika nti; muvunnamire Adamu. bonna ne bavunnama okugyako Ibliisu, yagaana ne yeekuluntaza n''abeera mu bawakanya Katonda.');
INSERT INTO "african_dev" VALUES(2,35,'2:35','Netugamba nti ggwe Adamu ne mukyalawo mubeere mu Jjanna, mulye mwegazaanye wonna wemwagalira. Naye temusembereranga omuti guno ne mubeera mu balyazamaanyi.');
INSERT INTO "african_dev" VALUES(2,36,'2:36','Sitaani n''abaseereza okuva mu jjana era naabaggya mu (birungi) bye baalimu. Netubagamba nti mukke wansi nga buli omu mulabe eri munne, era ku nsi gye mujja okubeeranga n''okweyagalira e bbanga eggere.');
INSERT INTO "african_dev" VALUES(2,37,'2:37','Oluvanyuma Adamu yayiga ebigambo by''okusaba okuva eri Katonda omulezi we (nga yeenenya), era Katonda nakkiriza okwenenya kwe. Mazima yye (Katonda) yakkiriza okwenenya, omusaasizi owookusaasira okwenjawulo.');
INSERT INTO "african_dev" VALUES(2,38,'2:38','Netugamba nti, kale mwenna mugifulume (e jjana). Buli lwonna obulungamu lwe bun''abajjiranga okuva gyendi, abo abaligoberera okulungamya kwange tebaliba na kutya era tebagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(2,39,'2:39','Naye abo abaligaana okukkiriza ne balimbisa ebigambo byaffe be b''okuba mu muliro obugenderevu.');
INSERT INTO "african_dev" VALUES(2,40,'2:40','Abange abaana ba Israel, mujjukire (mwebaze) ebyengera byange bye nabagabira. Mutuukirize e ndagaano yange (ey''okunzikiriza n''okugondera amateeka gange), nja kutuukiriza e ndagaano yammwe (ey''okubayingiza e jjana). Era nze nzekka gwe muba mutya.');
INSERT INTO "african_dev" VALUES(2,41,'2:41','Era mukkirize ebyo bye nassa (ku Nabbi Muhammad) nga bikakasa ebyo bye mulina (Taurat) era temuba abasaale mu kubiwakanya. Era temutundanga ebigambo byange omuwendo omutono (olw’emigaso gy''ensi), era mutye nze nzekka.');
INSERT INTO "african_dev" VALUES(2,42,'2:42','Temutabulanga amazima n''ebitali bituufu, ne mukisa amazima nga mumanyi.');
INSERT INTO "african_dev" VALUES(2,43,'2:43','Muyimirizeewo e sswala, mutoole zzaka, era mukutame n''abo abakutama.');
INSERT INTO "african_dev" VALUES(2,44,'2:44','Mulagira mutya abantu okukola obulungi ne mwerabira emyoyo gyammwe, so nga ate mmwe musoma e kitabo (Taurat), abange temutegeera!');
INSERT INTO "african_dev" VALUES(2,45,'2:45','Mweyambise obugumiikiriza n''okusaala, wabula yo e sswala ekaluubirira nnyo abantu, okugyako abo abagondera Katonda.');
INSERT INTO "african_dev" VALUES(2,46,'2:46','Abo abakakasa nti bakusisinkana Omulezi waabwe (Katonda), era gyali gye bagenda okudda.');
INSERT INTO "african_dev" VALUES(2,47,'2:47','Abange abaana ba Israel, mujjukire ebyengera byange bye nabagabira ne mbasukkulumya ku bantu abalala bonna (olw''okubaggyamu ba Nnabbi abangi).');
INSERT INTO "african_dev" VALUES(2,48,'2:48','Era mutye olunaku omuntu lwataligasa muntu mulala mu kintu kyonna, era lwatalikkirizibwa kuwolereza wadde okuwa e nnunuzi. Era abantu abo ssi bakutaasibwa.');
INSERT INTO "african_dev" VALUES(2,49,'2:49','Era mujjukire lwe twabawonya abantu ba Firaawo ababatuusangako e bibonyoobonyo e bikakali, nga basanjaga abaana ba mmwe aboobulenzi ne balekawo aboobuwala. Ekikolwa ekyo kyalimu ekigezo kinene nnyo ekyava eri Mukama wa mmwe (Katonda).');
INSERT INTO "african_dev" VALUES(2,50,'2:50','Era mujjukire lwe twabakubira e kkubo mu nnyanja ne tubawonya, ate ne tuzikiriza abantu ba Firaawo nga nammwe mulaba.');
INSERT INTO "african_dev" VALUES(2,51,'2:51','Era mujjukire lwe twalagaanyisa Musa ebiro amakumi ana (40) (tulyoke tumuwe e kitabo Taurat) wabula kye mwakola kwe kweteerawo ennyana (ne mugisinza) oluvanyuma lwa Musa okuvaawo, ne mubeera abalyazamaanyi.');
INSERT INTO "african_dev" VALUES(2,52,'2:52','Oluvanyuma lwebyo twabasonyiwa mulyoke mwebaze.');
INSERT INTO "african_dev" VALUES(2,53,'2:53','Era mujjukire lwe twawa Musa e kitabo (Taurat), ekyawula wakati w''obutuufu n''obukyamu mulyoke mulungame.');
INSERT INTO "african_dev" VALUES(2,54,'2:54','Era mujjukire Musa, lwe yagamba abantu be; abange (mmwe) abantu bange, mazima mweryazaamanyizza mwekka olwokusinza e nnyana. Nolw’ekyo mwenenyeze omutonzi wa mmwe. Mukutuukiriza ekyo mwette mwekka. Ekyo kye kirungi gye muli ew''omutonzi wa mmwe. Bwe mwakola ekyo Katonda kwe kubasonyiwa, mazima ddala ye, yakkiriza okwenenya era omusaasizi.');
INSERT INTO "african_dev" VALUES(2,55,'2:55','Era mujjukire lwe mwagamba Musa nti; owange Musa, tetujja kukkiriza byogamba okugyako nga tulabye Katonda yennyini (amangu ago) okubwatuka okwamaanyi ne kubajjira (ne mufa) nga mutangaaliridde.');
INSERT INTO "african_dev" VALUES(2,56,'2:56','Oluvanyuma lw''okufa kwa mmwe netubazuukiza, mulyoke mwebaze.');
INSERT INTO "african_dev" VALUES(2,57,'2:57','Era twabasiikiriza n''ebire, ne tubassiza e mmere eyitibwa Manna n''enva eziyitibwa Saluwa. (Ne tubagamba nti) mulye nga e birungi mu bintu bye twabagabirira. Naye (abajeemu mu bo) tebaalyazaamaanya ffe, wabula beeryazamaanya bokka.');
INSERT INTO "african_dev" VALUES(2,58,'2:58','Era mujjukire lwe twabagamba nti muyingire e kibuga kino, nga muli mu kyo, mulye wonna wemwagala nga mwegazaanya, era muyingire omulyango gwakyo nga muvunnama nga mugamba nti, twetonze, tubasonyiwe ebisobyo bya mmwe. Era tujja kwongeza abakozi b''ebirungi.');
INSERT INTO "african_dev" VALUES(2,59,'2:59','Abaajeema (mu bo) baakyusa e kigambo ekyabalagirwa (ne boogeramu kirala). Netussa ku abo abaajeema e kibonerezo ekyava waggulu olwebyo bye baakolanga ebyobujeemu.');
INSERT INTO "african_dev" VALUES(2,60,'2:60','Era mujjukire e kiseera Musa weyasabira Katonda, awe abantu be amazzi, netumugamba nti kuba omuggo gwo ku jjinja. (Bwe yagukubako) Nemufukumukamu e nsulo kkumi nabbiri (12). Buli kika (kya bayisirayiri) ne kimanya obunywero bwakyo. (Katonda nabagamba nti) Mulye, munywe mu bigabwa bya Katonda, temwewaggulanga mu nsi ne mubeera aboonoonyi.');
INSERT INTO "african_dev" VALUES(2,61,'2:61','Era mujjukkire lwe mwagamba Musa nti, tetujja kugumiikiriza mmere ya kika kimu kyokka. Tusabire Omuleziwo (Katonda) atumereze e bimera okuva mu ttaka nga; enkoolimbo, kyukamba, katungulukyumu, ddengu n''obutungulu. (Musa) kwe kugamba nti muwanyisa ebyawansi mu kifo ky''ebirungi. Mugende awantu wonna awasobola okulimwa olwo nno munaafuna ebyo bye musabye. Nebakakasibwako obuswavu n''obunaku, era ne bakakatwako obusungu bwa Katonda. Ekyo nno lwakuba baali bawakanya ebigambo bya Katonda, era nga batta ba Nnabbi awatali nsonga ntuufu. Baakola ebyo byonna olw''obujeemu nebaba nga baasukka e nsalo z''amateeka ga Katonda.');
INSERT INTO "african_dev" VALUES(2,62,'2:62','Mazima abo abakkiriza (Nabbi Muhammad) n''abo abaali ku ddiini y''ekiyudaaya, (ku mulembe gwayo), naabo abaali ku ddiini y’ekinaswara (ku mulembe gwayo), ne ba sswabi-ina (Abo abataasinza masanamu nga Nabbi Muhammad tannatumwa), yenna akkiriza Katonda n''olunaku lw''enkomerero era naakola e mirimu e mirungi, abo nno balina e mpeera zaabwe ew''omulezi waabwe era tewaliba kutya ku bo era tebagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(2,63,'2:63','Era mujjukire (mmwe abaana ba Israel) lwe twabakozesa e ndagaano. Netuwanika waggulu wa mmwe olusozi (ne tubagamba nti) mukwate namaanyi ebyo byetubawadde (Taurat). Era mujjukire ebyo ebiri mu yo mulyoke mubeere abatya Katonda.');
INSERT INTO "african_dev" VALUES(2,64,'2:64','Oluvanyuma nemubikuba amabega singa tekyali kisa kya Katonda n''okusaasira kwe mwandibadde mu bafaafaaganiddwa.');
INSERT INTO "african_dev" VALUES(2,65,'2:65','Mazima mwamanya (ekyafaayo) kyabo abamu mu mmwe abaasukka ensalo nebavuba ku lunaku lwomukaaga. Netubagamba nti mufuuke enkobe, mubeere abanyoomebwa.');
INSERT INTO "african_dev" VALUES(2,66,'2:66','Netukifuula ekyokulabula eri abo abaaliwo n''abo abalijja oluvanyuma. Era netukifuula ekyokubuulirira eri abo abatya Katonda.');
INSERT INTO "african_dev" VALUES(2,67,'2:67','Era mujjukire Musa lwe yagamba abantu be nti; mazima Katonda abalagira musale ente. Nebaddamu nti; otufuula kyakujeeja bujeeja. Musa n''addamu nti; Nneekingiriza ne Katonda okubeera mu bantu abatategeera.');
INSERT INTO "african_dev" VALUES(2,68,'2:68','Abayudaaya nebagamba nti; tubuulize Mukamaawo atunnyonnyole ente eyo ya ngeri ki? Musa naagamba nti; mazima Katonda agamba nti; ente eyo eteekwa okuba nga si nkadde, ate nga si nto nnyo, eri wakati w''enkadde n''ento. Era muteekwa okukola ekyo kye mulagiddwa.');
INSERT INTO "african_dev" VALUES(2,69,'2:69','(Abayudaaya) ne bagamba nti; tubuulize Mukamaawo atunnyonnyole (ente eyo) ya langi ki? Naagamba nti; Katonda agamba nti ente eyo ya langi yalusanja ng’ennyikidde bulungi nnyo, nga esanyusa abalabi.');
INSERT INTO "african_dev" VALUES(2,70,'2:70','(Abayudaaya) nebagamba nti; tubuulize Mukamaawo atunnyonnyole nte ki eyo? Mazima ente zonna zezimu gye tuli. Era mazima ffe Katonda bwan''aba ayagadde, tujja kulungama.');
INSERT INTO "african_dev" VALUES(2,71,'2:71','(Musa) naagamba nti; (Katonda) agamba nti; erina kubeera nte etenyoomebwa, nga si nnimi era nga tefukirira nnimiro, nga nnamu bulungi, eteriimu bbala ddala lyonna. Nebagamba nti; kaakati otulaze amazima. Olwo ne balyoka basala ente, naye kaabula kata baleme okukikola.');
INSERT INTO "african_dev" VALUES(2,72,'2:72','Era mujjukire lwe mwatta omuntu, ensonga yokutemulwa kwe nemugikaayaniramu (ani yamusse). So nga ate Katonda yali wakuggyayo ebyo byemwali mukisizza.');
INSERT INTO "african_dev" VALUES(2,73,'2:73','Netugamba nti; mumukubeko ebimu ku bitundu by''ente. Bwatyo Katonda bwalizuukiza abafu, era bulijjo abalaga obubonero bwe, (obulaga obuyinza bwe) mulyoke mutegeere.');
INSERT INTO "african_dev" VALUES(2,74,'2:74','Oluvanyuma lw''ebyo emitima gyammwe gyakakanyala negiba ng''enjazi oba okusingawo mu bukakanyavu; anti enjazi ezimu zifukumukamu emigga, ne mubaamu ezeyasaamu ne zivaamu amazzi, n''ezimu ku zo zikka olwokutya Katonda. Era Katonda tagayaalirira byemukola.');
INSERT INTO "african_dev" VALUES(2,75,'2:75','Abaffe (abakkiriza) mwettanira (Abayudaaya) okuba nga bakkiriza eddiini yammwe, so nga ekiwayi ekimu mu bo bawulira ebigambo bya Katonda ne babikyusa oluvanyuma lw''okubitegeera. Kino nga bakikola mu bugenderevu.');
INSERT INTO "african_dev" VALUES(2,76,'2:76','(Abayudaya) bwebaasisinkananga abakkiriza nga bagamba nti tukkirizza. Kyokka bwebaba bokka na bokka, abamu bagamba bannaabwe nti muyinza mutya okubabuulira ebyo Katonda bye yababikkulira ate nga bajja kubasingisa nabyo omusango ewa mukama wa mmwe (Katonda), abaffe temutegeera.');
INSERT INTO "african_dev" VALUES(2,77,'2:77','Abaffe tebamanyi nti mazima Katonda amanyi ebyo byebakisa n''ebyo bye boolesa.');
INSERT INTO "african_dev" VALUES(2,78,'2:78','Era mu bo (Abayudaaya) mulimu abatasobola kusoma na kuwandiika, nga tebasobola kumanya biri mu kitabo (Taurat). Wabula babeera ku kwagala kwa mitima gyabwe. Era tebalina kyebaliko, okugyako bateebereza buteebereza.');
INSERT INTO "african_dev" VALUES(2,79,'2:79','Okubonaabona okwamaanyi kuli eri abo abawandiika ekitabo n''emikono gyabwe, bwebamala ne bagamba nti kino kivudde eri Katonda, nga kyebagenderera kuwaanyisaamu (byakufuna bya nsi) eby''omuwendo ogwa wansi. Bagenda kutuukwako okubonaabona olw''ebyo emikono gyabwe bye gyawandiika, ng''era bwe bagenda okutuukwako okubonaabona olw''ebyo bye baafunamu.');
INSERT INTO "african_dev" VALUES(2,80,'2:80','Era (Abayudaaya) baagamba nti, omuliro tegugenda kututuukako okugyako nnaku mbale. Bagambe; mwakola endagaano ne Katonda, Katonda alyoke abe nga tagenda ku gyawukanako? Oba mwogera ku Katonda bye mutamanyi?');
INSERT INTO "african_dev" VALUES(2,81,'2:81','Wabula omuntu bw''akola ekibi, ekyonoono kye ne kimumalawo; abantu abo baakuyingira omuliro era nga baakugutuulamu bugenderevu.');
INSERT INTO "african_dev" VALUES(2,82,'2:82','Ate bo abakkiriza ne bakola emirimu emirungi; abo be bookuyingira enyumba ey''emirembe; era bo baakutuulamu bugenderevu.');
INSERT INTO "african_dev" VALUES(2,83,'2:83','Era mujjukire lwe twakozesa abaana ba Israel endagaano nti; temusinzanga ekintu kyonna okugyako Katonda. Bo abazadde ababiri, n''abooluganda olw''okumpi, ne bamulekwa n''abanaku mubayisenga bulungi. Era mwogerenga bulungi n''abantu. Era muteekwa okuyimirizaawo e sswala, mutoole ne zzaka. Wabula (mu kifo ky''okukola ebyo) mwakyuka, okugyako batono mu mmwe ng''ate mu kukola ekyo mwali muva ku ndagaano.');
INSERT INTO "african_dev" VALUES(2,84,'2:84','Era mujjukire lwetwabakozesa endagaano nti temuuyiwenga musaayi gwa mmwe, wadde temwegobaganyenga mu mayumba. Endagaano gye mwakakasa, nga era mmwe mwennyini muli bajulizi.');
INSERT INTO "african_dev" VALUES(2,85,'2:85','Wabula ate mmwe mwennyini muttiηηana era nga mugobaganya abamu ku mmwe mu mayumba gabwe. Muyamba abamu ku bannammwe mu ngeri emenya amateeka, era mu ngeri y''obulumbaganyi. So nga ate abawambe bwebaba babajjidde mubanunula. Awamu n''okuba nga eky''okubagobaganya mu mayumba gabwe kyaziyizibwa. Abaffe ekitabo mukkirizaako kitundu ne muwakanya ekitundu ekirala? Kaakano kiki kyemusuubira okuba empeera y''omu mu mmwe akola ekikolwa ekyo, okugyako okuswazibwa mu bulamu obw''ensi ng''ate ne ku lunaku lw''enkomerero bagenda kuzzibwa eri ebibonerezo ebisinga okuba ebikakali. Katonda tasudde muguluka ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(2,86,'2:86','Abo nno b''ebo obulamu bw''enkomerero ababugulamu obw''ensi, tebagenda kukenderezebwa ku bibonerezo wadde tebagenda kutaasibwa.');
INSERT INTO "african_dev" VALUES(2,87,'2:87','Mazima twawa Musa ekitabo (Tawuraat) netumugoberezaako ababaka olvannyuma lwe. Era netuwa Issa mutabani wa Mariamu obubonero obwolwatu netumuwagira ne mwoyo mutukuvu (Malayika Jiburilu). Abaffe, buli lwonna omubaka wa Katonda lw''abajjira n''ebyo byemutayagala ng''olwo mwekuluntaza! Abamu ku babaka mubalimbisa ate abalala nga mubatta.');
INSERT INTO "african_dev" VALUES(2,88,'2:88','Ne bagamba nti emitima gyaffe mizibikivu. Wabula Katonda yabagoba mu kusaasira kwe olw''obutakkiriza bwabwe. Nekiba nga bakkiriza kitono nnyo.');
INSERT INTO "african_dev" VALUES(2,89,'2:89','Ekitabo (Kur''ani) bwe kyabajjira okuva eri Katonda nga kikakasa bye baalina. Ate nga mu kusooka baali basaba Katonda abataase ku batakkiriza (abe nga abatumira Nabbi gwe baali balindirira). Wabula bwe yabajjira oyo gwe baali bamanyi, baamuwakanya. Ekikolimo kya Katonda kibeere ku batakkiriza.');
INSERT INTO "african_dev" VALUES(2,90,'2:90','Kibi nnyo ekyo kye baagulamu emyoyo gyabwe olw''obutakkiriza Katonda byeyassa nga kiva ku nsaalwa okuba nti Katonda assa obulungi (Obwannabbi) bwe ku oyo gw''aba ayagadde mu baddu be. Ekyo kyabatuusa ku kusunguwalirwa Katonda, nga kweyongera ku kusunguwalirwa okulala. Abatakkiriza balituusibwako ebibonerezo ebibakkakkanya nga bibaswaza.');
INSERT INTO "african_dev" VALUES(2,91,'2:91','Bwe baba bagambiddwa nti mukkirize ebyo Katonda byeyabassiza, bagamba nti tukkiriza ebyo ebyakkira ku ffe. Ate nga (Kur’ani) ge mazima agakakasa ebyo byebalina.Bagambe (gwe Nabbi Muhammad); lwaki mu kusooka mwattanga bannabbi ba Katonda bwe muba nga ddala muli bakkiriza!');
INSERT INTO "african_dev" VALUES(2,92,'2:92','Mazima Musa yabajjira n''obunnyonnnyofu wabula ate bweyavaawo okugenda (okunona amateeka) nemweteerawo ennyana nemugisinza. Mu kikolwa ekyo mwali balyazamaanyi.');
INSERT INTO "african_dev" VALUES(2,93,'2:93','Era mujjukire ekiseera lwe twabakozesa endagaano, nga tuwanise olusozi waggulu wa mmwe. Ne tubagamba nti, bye tubawadde mubikwate n''amaanyi era muwulire. Baayanukula nebagamba nti, tuwulidde ne tujeema. Emitima gyabwe gyabbikira mu kusinza ennyana olw''obutakkiriza bwabwe. Bagambe nti kibi nnyo enzikiriza yammwe ky''ebalagira bwe muba nga ddala muli bakkiriza.');
INSERT INTO "african_dev" VALUES(2,94,'2:94','Bagambe nti; Enyumba ey''enkomerero bw''eba nga yeeyawulidde mmwe mmwekka ewa Katonda so si abantu balala bonna, kale mwegombe okufa bwe muba nga muli bamazima (mu bye mwogera).');
INSERT INTO "african_dev" VALUES(2,95,'2:95','Tebagenda kukwegombera ddala okusinzira ku ebyo bye baakola. Katonda amanyi nnyo abalyazamaanyi.');
INSERT INTO "african_dev" VALUES(2,96,'2:96','Era ddala ojja kubasanga nga be basinga abantu bonna okululunkanira okuwangaala. Mu kwagala okwo basukkuluma ne ku bagatta ebintu ebirala ku Katonda nga noomu ku bo yeegomba okuwangaala emyaka lukumi, so ng''ate eky''okuwangaala ebbanga eddene tekimuwonya bibonerezo. Katonda alaba byonna bye bakola.');
INSERT INTO "african_dev" VALUES(2,97,'2:97','Bagambe (ggwe Nabbi Muhammad) nti omuntu awalana Jiburilu, (ateekwa amanye nti) mazima ye Jiburilu yakuleetera Kur''ani nga asinziira ku kiragiro kya Katonda, ng''ekakasa ebitabo ebyagikulembera era nga kyakulungamya na kusanyusa eri abakkiriza.');
INSERT INTO "african_dev" VALUES(2,98,'2:98','Omuntu abeera omulabe wa Katonda ne Ba malayika be, n''ababaka be, (nga mwe muli) Jiburilu ne Miika''ilu, mazima ddala Katonda naye mulabe wa batakkiriza.');
INSERT INTO "african_dev" VALUES(2,99,'2:99','Mazima twassa gy''oli ebigambo ebinnyonnyofu (Kur''ani) era tewali abijeemera okugyako aboonoonyi.');
INSERT INTO "african_dev" VALUES(2,100,'2:100','Abaffe buli lwonna (Abayudaaya) lwe banaakolanga endagaano, abamu ku bo banaagyaawukanangako? Wabula (ekyo) bakikola (kubanga) abasinga obungi mu bo tebakkiriza.');
INSERT INTO "african_dev" VALUES(2,101,'2:101','Era omubaka (Nabbi Muhammad) bwe yabajjira okuva eri Katonda nga akakasa ebyo bye baali balina. Ekiwayi ekimu ku abo abaaweebwa ekitabo (Taurat), baasuula muguluka ekitabo (Kur''ani) nga balinga abatamanyi.');
INSERT INTO "african_dev" VALUES(2,102,'2:102','Nebagoberera ebyo sitaani bye zaayogeranga (eby''eddogo) ku mulembe gw''obufuzi bwa Sulaimani (nga babalowoozesa nti Sulaimani lye yakozesanga), so ng''ate Sulaiman tajeemeranga Katonda (alyoke akozese eddogo) wabula sitaani ze zaajeemera Katonda nga ziyigiriza abantu eddogo n''ebyo ebyassibwa ku ba malayika ababiri e Babilooni, Haaruta ne Maaruta.');
INSERT INTO "african_dev" VALUES(2,103,'2:103','Wabula singa bakkiriza (Nabbi Muhammad) era ne batya Katonda, empeera ze baalifunye okuva eri Katonda zaalibadde nnungi gye bali (okusinga eby''eddogo) singa baali bamanyi.');
INSERT INTO "african_dev" VALUES(2,104,'2:104','Abange mmwe abakkiriza, temukozesanga ekigambo Raa''ina (nga mwogera ne Nabbi Muhammad) wabula mugambenga Undhuruna. Era mubeerenga bawulize. Bo abo abajeemu balina ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(2,105,'2:105','Abo abajeemu mu bagoberezi b''ebitabo ebyasooka, n''abo abagatta ku Katonda ebintu ebirala, tebaagalira ddala kalungi konna okuba nga kassibwa gye muli okuva eri Omulezi wa mmwe (Katonda). So ng''ate Katonda assa okusaasira kwe n''akwawulira oyo gw''aba ayagadde. Era Katonda ye nannyini byengera ebingi.');
INSERT INTO "african_dev" VALUES(2,106,'2:106','Buli aya yonna gyetusangulawo oba gyetukwerabiza, tuleetamu egisingako oba efaanana nga yo. Abaffe tomanyi nti mazima ddala Katonda muyinza ku buli kintu?');
INSERT INTO "african_dev" VALUES(2,107,'2:107','Abaffe tomanyi nti mazima ddala obufuzi bw''eggulu (omusanvu) n''ensi bwa Katonda, era nga temulinaayo mukuumi yenna wadde omutaasa atali ye.');
INSERT INTO "african_dev" VALUES(2,108,'2:108','Wabula mulabika mwagala kubuuza mubaka wa mmwe nga Musa bwe yabuuzibwa olubereberye. Omuntu awaanyisa obutakkiriza mu bukkiriza aba abuze okuva ku kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(2,109,'2:109','Bangi mu bantu abaaweebwa ekitabo baagala babakyuse oluvanyuma lwo bukkiriza bwa mmwe babafuule abatakkiriza olw''ensaalwa eri mu mitima gyabwe oluvanyuma lw''amazima okubeeyoleka. Kale (mmwe abasiraamu) musonyiwe era mulekere (abo abaaweebwa ekitabo) okutuusa Katonda lw''anaaleeta ekiragiro kye, mazima Katonda muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(2,110,'2:110','Era muyimirizeewo e sswala, mutoole ne zzaka. Na buli kalungi konna kemwekolera mugenda kukasanga ewa Katonda. Mazima Katonda alaba mu bujjuvu ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(2,111,'2:111','Era nebagamba (nga bakubagana empawa) nti teri agenda kuyingira jjana okugyako abali mu ddiini y''abayudaaya oba ey''abannaswara. Okwo nno kwe kusuubira kwabwe. Bagambe (ggwe Nabbi Muhammad) nti, muleete obujulizi bwa mmwe bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(2,112,'2:112','Wabula ekituufu kiri nti omuntu eyeewaayo eri Katonda, era neyeeyisa bulungi, wakufuna empeeraye ew''omutonzi we. Abantu abo tebagenda kufuna kutya wadde okunakuwala.');
INSERT INTO "african_dev" VALUES(2,113,'2:113','Abayudaaya baagamba nti Abannaswara tebalina kyebaliko, n''abannaswara ne bagamba nti abayudaaya tebalina kyebaliko, so nga ate bonna basoma ekitabo. Bwe kityo n''abatamanyi kusoma nabo bwe bagamba, nga ekigambo kya bali. Katonda wa kulamula wakati waabwe ku lunaku lw’enkomerero ku ebyo byebaalinga baawukanamu.');
INSERT INTO "african_dev" VALUES(2,114,'2:114','Ani mulyazaamaanyi okusinga oyo aziyiza okutendereza erinnya lya Katonda mu mizikiti gye, ng''afuba okugyonoona. Tebasaana kugiyingira wabula nga bali mu kutya. Balina obuswavu mu nsi, nga ne ku lunaku lw''enkomerero bagenda kufuna ebibonerezo ebikakali.');
INSERT INTO "african_dev" VALUES(2,115,'2:115','Obuvanjuba n''obugwanjuba bya Katonda, kale yonna gyemuba mwolekedde nga musaala eyo y''eri ekibula kya Katonda, mazima Katonda mugazi mu ngeri zonna era mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(2,116,'2:116','Bagamba nti Katonda alina omwana. Katonda ali wala nnyo n''ekyo. Wabula ebiri mu ggulu ne mu nsi byonna (bitonde bye) era bimugondera.');
INSERT INTO "african_dev" VALUES(2,117,'2:117','Yeyatandikawo eggulu n''ensi. Bw''aba asazeewo ensonga yonna agamba bugambi nti ba ne kiba.');
INSERT INTO "african_dev" VALUES(2,118,'2:118','N''abo abatalina kyebamanyi bagamba nti singa Katonda ayogera naffe butereevu, oba netuwebwa ekyamagero. Ekigambo ekyo n''abo abaabasooka baakyogera olwo nebafaanagana mu ndowooza zaabwe, so nga twannyonnyola obujulizi obw''olwatu obukakasa amazima g''ababaka be tutumye eri abantu abalina amagezi agafumiitiriza.');
INSERT INTO "african_dev" VALUES(2,119,'2:119','Mazima ffe twakutuma n''amazima nga oli musanyusa era omutiisa. Togenda kubuuzibwa ku bantu ba mu muliro.');
INSERT INTO "african_dev" VALUES(2,120,'2:120','Abayudaaya yadde abakulisitaayo tebagenda kukusiima okugyako nga ogoberedde enzikiriza yaabwe, bagambe nti mazima okulungamya kwa Katonda bwe bulungamu obwannamaddala. Singa onoogoberera bo bye baagala oluvanyuma lw''okumanya okukujjidde, togenda kufuna gweweesigamirako wadde omutaasa.');
INSERT INTO "african_dev" VALUES(2,121,'2:121','Abo betwawa ekitabo ne bakisoma mu utuufu bw''okukisoma, bo bakikkiriza, wabula abo abakiwakanya abo nno be ''okufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(2,122,'2:122','Abange mmwe abaana ba Isirayiri, mujjukire ekyengera kyange kyennabawa era mazima ddala nabasukkulumya ku bantu abalala.');
INSERT INTO "african_dev" VALUES(2,123,'2:123','Era mutye n''olunaku omuntu lw''ataligasa muntu munne mu kintu kyonna, n'' ennunuzi y''omuntu bw''eterikkirizibwa, wadde tewaliba kuwolereza kumugasa. Era nga tebagenda kutaasibwa.');
INSERT INTO "african_dev" VALUES(2,124,'2:124','Jjukira mu kiseera awo Katonda weyagezesa Ibrahim n''ebiragiro (Ibrahim) n''abituukiriza, (Katonda) n''amugamba nti mazima nkufudde omukulembeze w''abantu. (Ibrahim) naagamba nti; nsaba ne mu zzadde lyange muveemu abakulembeze. (Katonda) n''amugamba nti endagaano yange tetwaliramu balyazamaanyi.');
INSERT INTO "african_dev" VALUES(2,125,'2:125','Era jjukira ekiseera lwetwafuula e nyumba (Kaaba) okuba nga bwe buddo bw''abantu era ekifo eky''emirembe, era netubalagira nti; ekifo kya Ibrahim mu kifuule awasaalirwa. Era twalagira Ibrahim ne Ismail nti mube nga mutukuza e nyumba yange ku lw''abo abagyetooloola n''abagituulamu, n''abo abakutama ne bavunnama.');
INSERT INTO "african_dev" VALUES(2,126,'2:126','Era Jjukira ekiseera Ibrahim bweyagamba nti; ayi mukama omulezi wange (Katonda) ekibuga kino kifuule kya mirembe era ogabirire abantu baamu ebibala, buli anakkirizanga Katonda n''olunaku lw''enkomerero. (Katonda) naagamba nti n''aliba takkiriza (ηŋenda kumugabiriranga) mulindirizeeko akaseera katono oluvanyuma ηŋenda kumuzza eri ebibonerezo by''omuliro. Obwo nno buddo bubi ddala.');
INSERT INTO "african_dev" VALUES(2,127,'2:127','Era jjukira ekiseera Ibrahim ne Ismail bwe baasitula omusingi gw''enyumba (Kaaba) nga bagamba nti; ayi mukama omulezi waffe (Katonda) kkiriza omulimu gwaffe guno mazima ggwe oli muwulizi era mumanyi.');
INSERT INTO "african_dev" VALUES(2,128,'2:128','Ayi mukama omulezi waffe (Katonda), tufuule ffembiriri abeewaayo gyoli era ne mu zzadde lyaffe oggyemu abalyewaayo gy''oli, era otulage engeri gye tuteekwa okukolamu emirimu gyaffe (egya Hijja). Era tusonyiwe, mazima ggwe oli musonyiyi oweekisa.');
INSERT INTO "african_dev" VALUES(2,129,'2:129','Ayi mukama omulezi waffe (Katonda) batumire omubaka ng''ava mu bo ng''abasomera abigambo byo era ng''abayigiriza ekitabo n''ebintu ebyamagezi era abatukuze. Mazima ggwe wakitiibwa asinga okuba ow''amagezi.');
INSERT INTO "african_dev" VALUES(2,130,'2:130','Ani yaalyegombye okuva ku ddiini ya Ibrahim okugyako oyo eyeesiriwaza yekka. Ate nga (Ibrahim) twamuwa ekifo eky''enkizo mu nsi era mazima ddala ye ku lunaku lw''enkomerero wakubeera mu bakozi b''obulungi.');
INSERT INTO "african_dev" VALUES(2,131,'2:131','Era jjukira ekiseera omuleziwe (Katonda) lwe yamugambira nti; weeweeyo (siramuka). (Ibrahim) naagamba nti; neewaddeyo eri Katonda omulezi w''ebitonde.');
INSERT INTO "african_dev" VALUES(2,132,'2:132','Era Ibrahim (ekigambo kyo kwewaayo) yakiraamira abaana be, nga ne Yakuubu bwe yakiraamira ababe nti; baana bange mazima ddala mukama Katonda yabasiimira eddiini, kale nno okufa tekubatuukangako okugyako nga muli basiraamu.');
INSERT INTO "african_dev" VALUES(2,133,'2:133','Abaffe mmwe (abayudaaya) muyinza okugamba nti mwaliwo mu kiseera Yakuubu bwe yali agenda okufa, naagamba abaana be nti; kiki kyemulisinza oluvanyuma lwange? Ne bagamba nti; tugenda kusinza Katonda wo era Katonda wa bakadde bo, Ibrahim, Ismail ne Ishaak. Katonda omu yekka era nga tugenda kwewaayo gyali.');
INSERT INTO "african_dev" VALUES(2,134,'2:134','Omulembe ogwo gwayita, era gw''agenda n''ebyagwo, nga nammwe mukoze ebyammwe. Era temugenda kubuuzibwa kw''ebyo byebaakola.');
INSERT INTO "african_dev" VALUES(2,135,'2:135','Era (abayudaaya n''abakurisitaayo) baagamba nti; mubeere bayudaaya oba bakurisitaayo lwemunaaluηηama. Bagambe (gwe Nabbi Muhammad) nti; nedda tujja kugoberera ddiini ya Ibrahim eragira okusinza Katonda omu yekka, era (Ibrahim) tabangako mubagatta kintu kirala ku Katonda');
INSERT INTO "african_dev" VALUES(2,136,'2:136','(Mwe abasiraamu) mugambe nti; tukkiriza Katonda, n''ebyassibwa gyetuli, n''ebyassibwa ku Ibrahim, ne Ismail, ne Ishaak, ne Yakuubu n''eri bazzukulu baabwe. N''ebyo ebyaweebwa Musa ne Issa na byonna ebyaweebwa bannabbi abalala nga biva eri mukama Katonda omulezi waabwe era tetubaawulayawulamu era nga naffe twewaddeyo gyali.');
INSERT INTO "african_dev" VALUES(2,137,'2:137','Bwe banakkiririza mu ebyo bye mukkiriza, olwo ban''aba baluηηamye. Naye bwe banaava ku nzikiriza eyo, mazima ddala bo bajja kubeera mu kuwalaaza mpaka. Era nga Katonda ajja kubakukolerako. Era ye (Katonda) awulira, era mumanyi.');
INSERT INTO "african_dev" VALUES(2,138,'2:138','(Obusiraamu) y''eddiini Katonda gyayagala munnyikiremu. Ate ani asinga Katonda okunnyikiza, era naffe oyo gwe tusinza.');
INSERT INTO "african_dev" VALUES(2,139,'2:139','Bagambe (gwe Nabbi Muhammad) nti; mutuwakanya ku bikwata ku Katonda. Ng''ate Katonda ye mulezi waffe era omulezi wa mmwe. Tukola emirimu gyaffe nga nammwe mukola egyammwe so ng''ate ffe tumusinza mu bumu bwe yekka.');
INSERT INTO "african_dev" VALUES(2,140,'2:140','Oba mugamba nti; ddala Ibrahim ne Ismail ne Ishaak ne Yakuubu ne bazzukulu baabwe baali bayudaaya oba bakurisitaayo. Bagambe (gwe Nabbi Muhammad) nti mmwe musinga okumanya, oba Katonda? Ani mubi okusinga akweka obujulizi bwalina obuva eri Katonda? Mukama Katonda bye mukola tabisudde muguluka.');
INSERT INTO "african_dev" VALUES(2,141,'2:141','Omulembe ogwo gwayita, era gwagenda n''ebyagwo, nga nammwe bwe mulina ebyammwe bye mukola era temugenda kubuuzibwa ku ebyo bye baakola. E jjuzu (2)');
INSERT INTO "african_dev" VALUES(2,142,'2:142','Ababuguyavu mu bantu bajja kugamba nti kiki ekyabaggya ku kibula kyabwe (Baitul-makdis) kyebaaliko, bagambe nti obuvanjuba n''obugwanjuba byonna bya Katonda. Aluŋŋamya gwaba ayagadde eri ekkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(2,143,'2:143','Bwe tutyo twabafuula ekibiina eky''omumakkati, mube nga mwe muwa obujulizi ku bantu, nga ate mmwe omubaka (Nabbi Muhammad) y''abawaako obujulizi. Tetwateekawo ekibula kyewasooka okwolekera okugyako lwa kwagala kulaba abagoberezi b''omubaka abannamaddala obutafaanana n''abo abakyukira ku bisinziiro byabwe. Wabula kino kyali kinene nnyo okugyako ku abo Katonda beyaluŋŋamya. Era Katonda si wabutabala bukkiriza bwa mmwe. Mazima Katonda wakisa era musaasizi ku bantu.');
INSERT INTO "african_dev" VALUES(2,144,'2:144','Tulaba nga okyuka amaaso go nga otunula ku ggulu (gwe Muhammad ng''olinda obubaka ku nsonga y''ekibula). Tujja kukwolekeza ekibula ky''oyagala. Kale yolekeza amaaso go ku luuyi oluliko omuzikiti ogw''emizizo. Na buli wonna wemubeeranga muteekeddwa okwolekeza amaaso gammwe oluuyi lwo. Mu mazima abo abaaweebwa ekitabo bakimanyidde ddala nti bino ge mazima okuva eri Katonda waabwe. Ng''ate byonna byemukola tewali kibuuka Katonda.');
INSERT INTO "african_dev" VALUES(2,145,'2:145','Abo abaaweebwa ekitabo ne bwobaleetera obujulizi obufaanana butya tebagenda kugoberera kibula kyo, nga naawe bw''otali mugoberezi wa kibula kyabwe. So nga n''abamu mu bo tebagoberera kibula ky''abalala. Kale nno singa onoogoberera bo bye baagala oluvanyuma lw''okumanya kw''ofunye, mazima ddala ggwe ojja kuba ogenze mu luse lw''abantu abalyazamaanyi.');
INSERT INTO "african_dev" VALUES(2,146,'2:146','Abo betwawa ekitabo bakimanyi bulungi nga bwe bamanyi abaana baabwe. Wabula ekibinja mu bo bakwekera ddala amazima ng''ate bamanyi.');
INSERT INTO "african_dev" VALUES(2,147,'2:147','Gano ge mazima agava eri Katonda wo. Tobeereranga ddala n''omulundi n''ogumu mu abo ababuusabuusa.');
INSERT INTO "african_dev" VALUES(2,148,'2:148','Buli bantu balina oludda lweboolekera. Kale (mmwe abakkiriza) muvuganye mu kukola ebirungi. Yonna gye muliba mwenna, Katonda agenda kubaleeta. Mazima Katonda muyinza ku buli kintu kyonna.');
INSERT INTO "african_dev" VALUES(2,149,'2:149','Na buli wonna wonoogendanga yolekeza obwanga bwo oluuyi oluliko omuzikiti ogw''emizizo. Era ddala gano ge mazima okuva eri Katonda wo. Ng''ate byonna byemukola tewali kibuuka Katonda.');
INSERT INTO "african_dev" VALUES(2,150,'2:150','Na buli wonna won''abanga ogenze yolekeza ekyenyikyo oluuyi oluliko omuzikiti ogw''emizizo. Na buli wonna wemunaabeeranga mwolekezenga ebyenyi byammwe oluuyi olwo, waleme kubaawo nsonga yonna bantu gyebeekwasa okugyako abo abalyazamaanyi mu bo. Temubatya, nze (Katonda) gwemuba mutya. Olwonno mbajjulize ebyengera byange, nammwe kibabeerere ensonga y''okulungama.');
INSERT INTO "african_dev" VALUES(2,151,'2:151','Kino kiringa bwe twabatumira omubaka ng''ava mu mmwe n''abeera ng''abasomera ebigambo byaffe, era nga abatukuza, era nga abayigiriza ekitabo n''abayigiriza n''okunnyonnyola, n''abayigiriza n''ebintu ebirala bye mwali temumanyi.');
INSERT INTO "african_dev" VALUES(2,152,'2:152','Kale nno munzijukire nga munjogerako. Nja kubajjukira. Era munneebaze, temunjeemeranga.');
INSERT INTO "african_dev" VALUES(2,153,'2:153','Abange mmwe abakkiriza mwekwate ku bugumiikiriza n''esswala ku lw''okubanga mazima Katonda ali wamu na baguminkiriza.');
INSERT INTO "african_dev" VALUES(2,154,'2:154','Era temwogeranga kwabo abattibwa mu kkubo lya Katonda mbu bafu. Go amazima baba balamu naye mmwe mutategeera (bulamu bwabwe).');
INSERT INTO "african_dev" VALUES(2,155,'2:155','Era ddala tujja kubagezesa, mu bintu nga okutya, enjala, okukendeera kw''ebyenfuna n''okufa kwa bantu, n''okukendeera kwe bibala. Wabula sanyusa abagumiikiriza.');
INSERT INTO "african_dev" VALUES(2,156,'2:156','Abo b''ebo bwe baba nga batuukiddwako obuzibu bwonna bagamba (nti) mazima ffe twava eri Katonda era ddala tuli bakudda gy''ali.');
INSERT INTO "african_dev" VALUES(2,157,'2:157','Abo nno balina okusonyiyibwa n''okusaasirwa okuva eri mukama Katonda waabwe, era abo be baluŋŋamu.');
INSERT INTO "african_dev" VALUES(2,158,'2:158','Mazima akasozi Swaffa n''akasozi Maruwa bubalibwa mu bifo ebisinzibwamu mukama Katonda. Kale nno omuntu aba azze okwetooloola e nyumba ya Katonda (Kaaba) oba ng''azze kukola mikolo gya Umura tasaanye kutya kudduka wakati wobusozi bwombiriri. N''omuntu okukola obulungi mu ngeri ya kyeyagalire, (mumanye nti) Katonda asiima nnyo era mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(2,159,'2:159','Mazima abo abakweka ebyo byetwassa nga obujulizi era nga obulungamu oluvanyuma nga tumaze okubinnyonnyola abantu mu kitabo kyabwe. Abo nno Katonda abakolimira n''abakolimi bonna ne babakolimira.');
INSERT INTO "african_dev" VALUES(2,160,'2:160','Okugyako abo abeenenya ne balongoosa, nebannyonnyola ebyo (bye baakweka). Abonno, nzikiriza okwenenya kwabwe ate nze muyitirivu w''okukkiriza okwenenya era omuyitirivu w''okusaasira.');
INSERT INTO "african_dev" VALUES(2,161,'2:161','Mazima abo abaakaafuwala ne bafiira ku bukaafiiri; Abo baliko ekikolimo okuva ewa Katonda n''okuva eri ba malayika n''abantu bonna.');
INSERT INTO "african_dev" VALUES(2,162,'2:162','(Abantu abo) Baakubeera mu muliro obugenderevu, tebagenda kukendeerezebwa ku bibonerezo ng''era bwebatagenda kulindirizibwa (kubonerezebwa).');
INSERT INTO "african_dev" VALUES(2,163,'2:163','Mazima Katonda wa mmwe ali omu, tewali kisinzibwa kyonna okugyako ye omusaasizi omuyitirivu wokusaasira aliba n''okusaasira okwenjawulo (ku lunaku lw''enkomerero).');
INSERT INTO "african_dev" VALUES(2,164,'2:164','Mazima mu kutonda eggulu ery''emiko omusanvu ne nsi, n''okukyukakyuka kw''ekiro n''emisana, n''amaato agatambulira ku nnnyanja nga geetisse ebintu ebigasa abantu, n''amazzi Katonda gassa okuva wa ggulu, n''alamusa nago ensi oluvanyuma lw''okuba nga efudde, nassa mu nsi buli kiramu ekigiriko, n''okutambuza empewo era n''ebire, ebyateekebwa wakati w''eggulu ne nsi. (Ebyo byonna) bubonero obulaga okubaawo kwa Katonda n''obuyinza bwe eri abantu abategeera.');
INSERT INTO "african_dev" VALUES(2,165,'2:165','Naye mu bantu mulimu abaleka Katonda, nebeeteerawo ebintu ebirala nebasinza ebyo, ne babyagala nga bwe baaliyagadde Katonda. So nga bo abakkiriza baagala nnyo Katonda waabwe (omu) (nga okwagala kwabwe kuli ku ye yekka). Singa abagatta ebintu ebirala ku Katonda baali basobola okulaba ebibonerezo lwebirituuka, baalibadde bamanya nti Katonda wa maanyi nnyo era nga bwe baalibadde bamanya nti ddala Katonda muyitirivu wakubonereza.');
INSERT INTO "african_dev" VALUES(2,166,'2:166','Mu kiseera abaagobererwa webalyegaanira abaabagobereranga era ne baliraba ku bibonerezo, n''ensonga ezaabagattanga ne zikutukawo.');
INSERT INTO "african_dev" VALUES(2,167,'2:167','Abaagobereranga bagenda kugamba nti; singa tubadde baakudda ku nsi naffe netubeegaana nga bwe batwegaanyi. Bwatyo Katonda agenda kubalaga okufaafaagana kw''emirimu gyabwe (gyebaakolanga ku nsi) era si bakuva mu muliro.');
INSERT INTO "african_dev" VALUES(2,168,'2:168','Abange mmwe abantu mulye ebiri mu nsi ebyakkirizibwa ebirungi, era temugobereranga amakubo ga sitaani anti ye mulabe wa mmwe wa lwatu.');
INSERT INTO "african_dev" VALUES(2,169,'2:169','Mazima sitaani abalagira kukola bibi, na by''abugwenyufu, n''okwogera ku Katonda ebyo bye mutamanyi.');
INSERT INTO "african_dev" VALUES(2,170,'2:170','Bwebaba nga bagambiddwa nti mugoberere ebyo Katonda bye yassa (ku Nabbi Muhammad) baddamu nga bagamba nti; ffe tugoberera ebyo byetwasanga ne bakadde baffe. Abaffe ne bwebabanga bakadde baabwe baalinga tebategeera kintu kyonna era nga tebaali baluŋŋamu?');
INSERT INTO "african_dev" VALUES(2,171,'2:171','Ekifaananyi ky''abantu abo abatakkiriza n''oyo abakoowoola okujja eri obukkiriza kiringa omulunzi akoowoola ensoloze ne zijja gy''ali naye nga tezitegeera ky''aziyitira wabula okuwulira amaloboozi n''okukoowoolwa. (Abatakkiriza abo) Bakiggala, bakassiru, bamuzibe, tebagenda kutegeera kintu kyonna.');
INSERT INTO "african_dev" VALUES(2,172,'2:172','Abange mmwe abakkiriza mulye ebirungi mu ebyo byetwabawa era mwebaze Katonda bwe muba nga ddala ye, yekka gwemusinza.');
INSERT INTO "african_dev" VALUES(2,173,'2:173','Mazima ddala (Katonda) yabagaana okulya ennyamanfu, n''omusaayi, n''ennyama y''embizzi, na buli kyonna ekisalibwa ku lw''ekintu ekirala ekitali Katonda. Wabula omuntu abeera mu buzibu nga si lwabujeemu, era n''atasukka kyetaago kye, oyo nno talina musango (bw''aba aliddeko). Mazima Katonda musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(2,174,'2:174','Mazima abo abakweka ebyo Katonda bye yassa mu kitabo nebabiwanyisaamu eby''okufuna ebitono, abo nno buli kyebalya guba muliro mumbuto zaabwe era Katonda tagenda kwogera nabo ku lunaku lw’enkomerero era nga bwatagenda ku batukuza. Era bagenda kussibwako ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(2,175,'2:175','Abo nno b''ebo abaawaanyisa obubuze mu bulungamu. N''okubonerezebwa mu kifo ky''okusonyiyibwa. Baliggyawa obuvumu obugumira omuliro.');
INSERT INTO "african_dev" VALUES(2,176,'2:176','Ekyo kiriba bwekityo kubanga Katonda yassa ekitabo nga kiraga amazima. Naye nno abo abatakkaanya ku biri mu kitabo okwawukana kwabwe kuli wala nnyo n''ebyo ebikirimu.');
INSERT INTO "african_dev" VALUES(2,177,'2:177','Obulungi obubalibwa eri Katonda, tebwemalidde mu kwolekera kwa mmwe ebuvanjuba oba ebugwanjuba, wabula obulungi ye muntu okukkiriza Katonda n''olunaku lw’enkomerero, ne ba malayika n''ekitabo (Kur’ani) ne bannabi bonna, n''omuntu okuba nga awaayo ebyobugaggabwe (mu kkubo lya Katonda) awamu n''okuba nga abyagala, nga abiwa abooluganda olwokumpi ne bamulekwa n''abanaku n''abatambuze abalemereddwa mu ŋŋendo zaabwe, n''abasabirizi n''abaagala okwenunula okuva mu buwambe. Era n''ayimirizaawo e sswala, n''atoola ne zzaka mu by''obugaggabwe. N abo abatuukiriza endagaano zaabwe bwebaba nga balagaanyisizza, n''abagumiikiriza mu buzibu, obubenje ne mu biseera bye ntalo. Abo nno be bakkiriza okukkiriza okwannamaddala, era abo bo be batya Katonda.');
INSERT INTO "african_dev" VALUES(2,178,'2:178','Abange mmwe abakkiriza mulaalikiddwako nti ekibonerezo ky''oyo aba atemudde kumutta; omwana waboowo attibwa olw''okutta omwana wabobwe, n''omuddu attibwa olw''okutta muddu munne, n''omukazi attibwa ku lwokutta mukazi munne. Asse, bannannyini mufu bwe baba bamusonyiye ekintu kyonna, ababanja bateekwa okubanja mu ngeri nnungi, n''abasasula basasule mu ngeri y''emu. Okwo kwe kuwewula n''okusaasira, ebyava eri Katonda wa mmwe. (Oluvanyuma lw''okusasula) oyo alumba n''atta eyasonyiyiddwa naye ateekwa okuweebwa ekibonerezo ekiruma nga ky''akuttibwa (awatali kusonyiyibwa oba okuwa engassi).');
INSERT INTO "african_dev" VALUES(2,179,'2:179','Okuwa abatemu ekibonerezo ky''okufa mwemuli obulamu bwa mmwe, abange mmwe abalina amagezi, mulyoke mutye Katonda.');
INSERT INTO "african_dev" VALUES(2,180,'2:180','Mulaalikiddwako singa (kirabika nti) omu ku mmwe atuuse okufa n''aba nga aleseewo ebintu, ateekwa okulaamira bazaddebe bombi n''aboolugandalwe olw''okumpi nga akikola mu ngeri ennungi. Ekyo kikakafu ku batya Katonda.');
INSERT INTO "african_dev" VALUES(2,181,'2:181','Wabula omuntu akyusa ekiraamo, nga amaze okukiwulira; mazima ddala ekibi ky''okukikyusa kiri kwabo abakikyusa kubanga Katonda muwulizi nnyo, era mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(2,182,'2:182','Naye omuntu bweyeekengera mu mulaami okwekubiira oba okukola ekikyamu, ye nalongoosa wakati waabwe (abalaamirwa) oyo tayina kibi. Mazima Katonda musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(2,183,'2:183','Abange mmwe abakkiriza mulaalikiddwa okusiiba nga bwe kwalaalikibwa kw''abo abaabakulembera mulyoke mutye Katonda.');
INSERT INTO "african_dev" VALUES(2,184,'2:184','Nga musiiba ennaku mbale bubazi, naye oyo an''abanga omulwadde mu mmwe, oba ng''ali ku lugendo, (akkirizibwa obutasiiba) ennaku ezo azisiibe mu nnaku endala. Bo abo abasiiba nga bazitoowererwa (bayinza obutasiiba) nebawa omutango nga baliisa abanaku. Bulijjo omuntu bw''akola ekirungi ekisinga kw''ekyo kyalagiddwa okukola, kye kirungi gyali. Wabula singa musiiba kiba kirungi gyemuli bwe muba nga mumanyi.');
INSERT INTO "african_dev" VALUES(2,185,'2:185','(Ekiseera ekibalaalikiddwako okusiiba gwe) mwezi gwa Ramadhan, Kur''ani mwe yakkira. Nga (Kur''ani) kya kulungamya eri abantu. Era nga mulimu obujulizi obunnyonnyola obulungamu era nga mulimu okulaga enjawulo wakati w''amazima n''obulimba. N''olwekyo omuntu abeerangawo ng''omwezi gwa Ramadhan gutuuse ateekwa okugusiiba. Naye oyo asangibwa nga mulwadde, oba ng''ali ku lugendo ennaku ezo azibale azisiibe mu kiseera ekirala. Bulijjo Katonda abaagaliza bwangu so si kubakaluubiriza. Kale nno mujjuze ennaku (ze mwalya mu Ramadhan) era mutendereze Katonda olw''okubaluŋŋamya, olwo mubeere abeebaza.');
INSERT INTO "african_dev" VALUES(2,186,'2:186','Abaddu bange bwe baba bakumbuuzizzaako bagambe nti mazima ndi kumpi, nnyanukula okusaba kw''omusabi wonna wansabira. Wabula bateekwa bannyanukule (nga bagondera ebiragiro byange) era banzikirize balyoke batuuke ku bulungamu.');
INSERT INTO "african_dev" VALUES(2,187,'2:187','Mukkirizibwa mu kiro ky''okusiiba okukiika eri bakyala ba mmwe, abakyala abo byambalo byammwe nga nammwe bwemuli ebyambalo by''abwe. Katonda amanyi nti mazima ddala mmwe mubadde mwebbirira, Katonda kwekukkiriza okwenenya kwa mmwe era n''abasonyiwa. Kaakati mugende gyebali era mukole ekyo Katonda kye yabakkiriza. Mulye era munywe okutuusizza ddala ewuzi enjeru (nga kye kitangaala) bwenebeeyolekera okuva mu wuzi enzirugavu (nga ye nzikiza) ng''okwo kwe kusala kw''emmambya. Olwo nno musiibe okutuusa obudde okuziba. Temukiikanga gye bali nga mutudde Itikaafu mu mizigiti. Ezo z''ensalosalo za Katonda, temuzisembereranga. Bwatyo nno Katonda bw''annyonnyola amateekage eri abantu balyoke bamutye.');
INSERT INTO "african_dev" VALUES(2,188,'2:188','Temulyanga emmaali yammwe mu bulyazaamaanyi mu nkolagana wakati wa mmwe, ne mutuuka n''okugitwala eri abafuzi musobole okulya ekitundu ku bintu by''abantu mu kibi, ate nga mumanyi.');
INSERT INTO "african_dev" VALUES(2,189,'2:189','Bakubuuza (ggwe Nabbi Muhammad) ku nkyukakyuka y''omwezi, bagambe nti ebyo bya bantu kumanyirako biseera ne Hijja. Obuntubulamu si kuyingira mayumba gammwe nga muyita mmanju, wabula obuntubulamu ye muntu okutya Katonda. Era amayumba mugayite miryango, era mutye Katonda musobole okubeera mu balirokoka.');
INSERT INTO "african_dev" VALUES(2,190,'2:190','Mulwane mu kkubo lya Katonda nga mulwanyisa abo ababa babalwanyisizza. Wabula temusosonkerezanga, mazima Katonda tayagala balumbaganyi.');
INSERT INTO "african_dev" VALUES(2,191,'2:191','Mubattenga wonna wemubanga mu basanze, era mubaggye mu bifo ebyo bye babaggyamu kubanga bulijjo effitina mbi nnyo okusinga okutta. Temubalwanyisizanga mu muzigiti ogw''emizizo okugyako nga babalwanyisizzaamu, bwe baba babalwanyisizza nammwe mubalwanyise; empeera y''abatakkiriza bw''etyo bwebeera.');
INSERT INTO "african_dev" VALUES(2,192,'2:192','Bwe baba beekomyeko mazima Katonda musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(2,193,'2:193','Mubalwanyise waleme kubaawo misanvu gyonna, olwo nno okusinza kwonna kube kwa Katonda yekka. Naye bwe baba beekomyeko olwo nno tewasaanye kubaawo bulabe okugyako ku balyazamaanyi.');
INSERT INTO "african_dev" VALUES(2,194,'2:194','Omwezi ogw''emizizo guba gwa mizizo ku buli luuyi. Omuntu atyoboola ebifo eby''emizizo aweebwa ekibonerezo ekigya mu kikolwa ky''aba akoze. Kale nno omuntu abalumbaganye mu mwolekeze obwanga nga bw''aba azze. Mutye Katonda era mumanye nti mazima ddala Katonda ali wamu n''abamutya.');
INSERT INTO "african_dev" VALUES(2,195,'2:195','Era muweeyo mu kkubo lya Katonda temwesuulanga mmwe mwennyini mu kuzikirira, era mukole ebirungi, kubanga mazima ddala Katonda ayagala abakozi b''ebirungi.');
INSERT INTO "african_dev" VALUES(2,196,'2:196','Mukole Hijja ne Umra mubujjuvu kulwa Katonda naye bwe mulemesebwanga, buli muntu ateekwa okuwaayo ekirabo ky''aba asobola, wabula temumwanga mitwe gyammwe okutuusa ekirabo nga kituuse mu kifo kyakyo. Naye oyo an''abanga omulwadde mu mmwe oba ng''alina ekizibu ekirala ku mutwe gwe oyo ateekeddwa okutoola omutango ng''asiiba oba awaayo saddaaka oba okusala ekisolo. Naye bwe mun''abanga mu mirembe, oyo yenna anaakozesanga omukisa gw''okukulembeza Umra ku Hijja atekeddwa okuwaayo ekirabo ky''aba asobodde, naye oyo an''abanga takirina ateekeddwa okusiiba ennaku ssatu ng''akyali mu Hijja nendala musanvu nga muzzeeyo, eryo nno lyekkumi (10) eryekutte. Ebyo biba bityo ku oyo amakaage nga tegali wali muzigiti gw''emizizo (Makkah). Mutye Katonda, era mumanye nti mazima ddala Katonda alina ebibonerezo ebikakali.');
INSERT INTO "african_dev" VALUES(2,197,'2:197','Hijja erina emyezi egimanyiddwa, kale nno omuntu eyeekakasangako nti mu myezi egyo ajja kukoleramu Hijja, tasaana kukola bya bufumbo, eby''obwonoonyi, wadde okuwakana ng''ali mu mikolo gya Hijja. Buli kirungi kyonna kyemukola Katonda akimanyi. Era muteekeddwa okwesibirira entanda. Nentanda esinga obulungi kwe kutya Katonda. Kale nno muntye mmwe abalina amagezi agasengejja.');
INSERT INTO "african_dev" VALUES(2,198,'2:198','Temulina kibi bwe muba nga munonyezza ku bigabwa bya Katonda wa mmwe, bwe muvanga mu Arafa mutendereze Katonda mu kifo ekyemizizo (Muzidalifah). Era mu mutendereze, anti yabaluŋŋamya newakubadde nga mu kusooka mwali mu babuze.');
INSERT INTO "african_dev" VALUES(2,199,'2:199','Mu kuva awo muyite mu kkubo abantu abalala webayita era mwegayirire Katonda, mazima Katonda musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(2,200,'2:200','Bwe mumaliriza emikolo gyammwe (egya Hijja), mw''ogerenga ku Katonda nga bwe mwogera ku bakadde ba mmwe, sinakindi n''okusingawo. Mu bantu mulimu abasaba nga bagamba; Mukama Katonda waffe tuwe ebirungi by''ensi n''aba nga talina mugabo gwonna gw''asabye mu by''enkomerero.');
INSERT INTO "african_dev" VALUES(2,201,'2:201','Naye ate mubo mulimu abasaba nga bagamba; Mukama Katonda waffe tuwe obulungi ku nsi, ne ku nkomerero otuwe obulungi, otuwonye ebibonerezo by''omuliro.');
INSERT INTO "african_dev" VALUES(2,202,'2:202','Abo nno be balina omugabo mu bye baba bakoze, era Katonda mwangu nnyo mu kubala.');
INSERT INTO "african_dev" VALUES(2,203,'2:203','Mutendereze Katonda mu nnaku embale, omuntu anaayanguwanga n''avaayo mu nnaku bbiri talina nsobi. N''oyo alinda n''amalayo (ennaku ssatu) talina nsobi. Ekyo ky''oyo atya Katonda. Mutye Katonda era mu manye nti mugenda kukuŋŋanyizibwa mudde gy''ali.');
INSERT INTO "african_dev" VALUES(2,204,'2:204','Mu bantu mulimu omuntu nga ebigambo bye bikusanyusa mu bulamu obw''ensi era nga ajuliza Katonda kw''ekyo ekiri mu mutima gwe, so nga ye mulabe asingira ddala (eri obusiraamu n''abasiraamu).');
INSERT INTO "african_dev" VALUES(2,205,'2:205','Bw''ava awo assa amaanyi mu kusaasanya obwonoonefu mu nsi, era n''azikiriza ebirime n''ebisolo, ngate Katonda tayagala bwonoonefu.');
INSERT INTO "african_dev" VALUES(2,206,'2:206','(Omuntu oyo y''omu) bwebamugamba nti tya Katonda, amawaggalige gamwongera okwonooneka. Omuliro Jahannama kye kifokye. Ekyo nno kifo kibi nnyo.');
INSERT INTO "african_dev" VALUES(2,207,'2:207','Era mu bantu mulimu oyo eyeewaayo nga anoonya okusiima kwa Katonda; (Bulijjo) Katonda musaasizi nnyo eri abaddube.');
INSERT INTO "african_dev" VALUES(2,208,'2:208','Abange mmwe abakkiriza, muyingire mu busiraamu muggweeremu. Temugobereranga makubo ga sitaani, mazima ye mulabe wa mmwe owoolwatu.');
INSERT INTO "african_dev" VALUES(2,209,'2:209','Kemunaaseerera oluvanyuma lw''okujjirwa obunnyonnyofu, mumanye nti Katonda wa buyinza, naye nga buli ky''akola akikola lwa nsonga.');
INSERT INTO "african_dev" VALUES(2,210,'2:210','Tebalina kye balinda, mpozzi nga balinda Katonda kubajjira ngali mu bisiikirize by''ebire ne ba malayika bajje. So nga ate (ebyo webiribeererawo) ebintu by''ensi byonna biriba bimaze okukoma, era eri Katonda y''eri obuddo bw''ebintu byonna.');
INSERT INTO "african_dev" VALUES(2,211,'2:211','Buuza abaana ba Yisirayiri nti, obujulizi obunnyonnyofu bumeka bwennabawa. Naye nno omuntu akyusa ekyengera kya Katonda nga amaze okukifuna, mazima ddala Katonda ebibonerezobye byamaanyi.');
INSERT INTO "african_dev" VALUES(2,212,'2:212','Abatakkiriza banyumirwa nnyo obulamu bw''ensi, era bajeeja abakkiriza; wabula abatya Katonda bagenda kubabeera waggulu ku lunaku lw’enkomerero. Katonda agabira gwaba ayagadde awatali kubalirira.');
INSERT INTO "african_dev" VALUES(2,213,'2:213','Abantu baali bumu (naye olw''ensonga emu oba endala nebaawukana) Katonda kwe kutuma ba nnabbi nga balanga essanyu (eri abo abakkiriza nebakola emirimu emirungi) era nga batiisa (ebibonerezo ebirituuka ku bajeemu). Katonda nabassaako obubaka obujjudde amazima alyoke abe ngalamula abantu mwe byo bye bayawukanamu. Naye tewali bayawukana mu bubaka okugyako abo bennyini abaabuweebwa, era nga mu kujja kw''obubaka obwo baali baafuna obunnyonnyofu. Ekyo kyali bwe kityo olw''obujeemu bwabwe. Katonda kwe kulungamya abo abakkiriza ku lw''ekisakye eri amazima ago, bali gebaayawukanamu. Era bulijjo Katonda aluŋŋamya gwaba ayagadde eri ekkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(2,214,'2:214','Oba musuubira okuyingira e jjana nga temutuukiddwako ebyo ebyatuuka ku baabakulembera! Baatuukibwako ebizibu n''okubonaabona, era nebakankanyizibwa okutuusa omubaka n''abakkiriza abaali awamu naye nebagamba nti Katonda atutaasa ddi? Naye, okutaasa kwa Katonda kuba kumpi.');
INSERT INTO "african_dev" VALUES(2,215,'2:215','Bakubuuza biki bye baba bawaayo. Bagambe nti buli kyonna kyemuwaayo mu birungi kiweebwa abazadde ababiri, abooluganda, bamulekwa, abeetaavu nabatambuze. Era (mukimanye) buli kalungi konna kemukola mazima ddala Katonda akamanyi.');
INSERT INTO "african_dev" VALUES(2,216,'2:216','Mulaalikiddwako okulwana (mu kkubo lya Katonda) awamu nokuba nga temwandikyagadde. Naye, muyinza obutayagala kintu nga ate kye kirungi gy''emuli, nga bwemuyinza okwagala ekintu naye nga kibi gy''emuli; anti Katonda amanyi ate nga mmwe temumanyi.');
INSERT INTO "african_dev" VALUES(2,217,'2:217','Bakubuuza ku nsonga y''okulwanira mu mwezi ogw''emizizo. Bagambe nti okugulwaniramu kibi kinene, wabula okulemesa abantu okukola emirimu gya Katonda n''okumujeemera n''okulemesa abantu okutuuka ku muzikiti ogw''emizizo n''okugobaganya abantu baamu, bye bisinga obubi mu maaso ga Katonda. Kubanga (bulijjo) effitina mbi nnyo okusinga okutta.');
INSERT INTO "african_dev" VALUES(2,218,'2:218','Era bulijjo bajja kubalwanyisanga okutuusa lweban''abajja ku ddiini yammwe, singa bakisobola. Naye oyo gwekanaatanda mu mmwe naava ku ddiiniye n''atuuka okufa nga mukaafiiri; abo nno bayonoonekerwa emirimu gyabwe ku nsi ne ku nkomerero, era abo be bagenda okutuula mu muliro era baakugubeeramu bugenderevu. Mazima ddala abo abakkiriza era abaasenguka ne balafuubana mu kkubo lya Katonda; abo nno be basuubira okusaasira kwa Katonda. Era Katonda musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(2,219,'2:219','Bakubuuza ebikwata ku mwenge ne zzaala, bagambe nti mu by''ombiriri mulimu ekibi kinene, nga bwe mulimu emigaso eri abantu. Naye ekibi kyabyo kye kinene okusinga emigaso egibirimu. Ate era bakubuuza nti baweeyo kigero ki mu mmaali zaabwe, bagambe nti mugabenga ekyo ekiba kifisse ku byetaago byammwe. Bwatyo nno Katonda bwa bannyonnyola amateekage mulyoke mufumitirize.');
INSERT INTO "african_dev" VALUES(2,220,'2:220','(Musobole okutegeera) ebikwata ku bulamu buno obw''okunsi n''obwoluvanyuma. Era bakubuuza ku bikwata ku bamulekwa, bagambe nti okubakolera ebyabwe nga byetongoredde kirungi, naye singa mubigatta n''ebyammwe anti baganda ba mmwe, era Katonda amanyi omwonoonyi n''omukozi w''obulungi. Singa Katonda yayagala yandibakaluubirizza. Mazima Katonda wa maanyi wa magezi.');
INSERT INTO "african_dev" VALUES(2,221,'2:221','Temuwasanga abagatta ku Katonda ebintu ebirala okutuusa lwe bakkiriza. Omuzaana omukkiriza ye mulungi okusinga oyo agatta ku Katonda ebintu ebirala newakubadde nga gwe mwandyagadde. Era temufumbizanga bawala ba mmwe eri abo abagatta ku Katonda ebintu ebirala okutuusa lwe basiramuka. Omuddu omukkiriza ye mulungi okusinga oyo agatta ku Katonda ebintu ebirala newakubadde nga gwe mwandyagadde. (Mumbeera zaabwe zonna) bakoowoola abantu nga babazza eri omuliro, so nga ate Katonda abakoowoola kubazza eri jjana n''ekisonyiwo okusinziira ku kwagala kwe, era annyonnyola abantu ebigambo bye balyoke basobole okwekuba mu kifuba.');
INSERT INTO "african_dev" VALUES(2,222,'2:222','Era bakubuuza ebikwata ku kusuulumba, bagambe nti kyo kyabulabe, kale nno mwesambe abakyala nga bali mu kusuulumba, era temubasemberera okutuusa lwe batukula, olwo nno bwe beetukuza mugende gye bali nga muyita mu kkubo eryo Katonda lye yabalagira. Mazima ddala bulijjo Katonda ayagala abeenenya era nga bw''ayagala abeetukuza.');
INSERT INTO "african_dev" VALUES(2,223,'2:223','Bakyala ba mmwe nnimiro zammwe musobola okugenda mu nnimiro zammwe nga muyita wonna wemuba mwagadde era bulijjo mwetegekere (nga mukola emirimu emirungi) era mutye Katonda mumanye nti mazima ddala mugenda kumusisinkana, era sanyusa abakkiriza.');
INSERT INTO "african_dev" VALUES(2,224,'2:224','Temufuulanga erinnya lya Katonda ekyekwaso eky''obutamenyawo birayiro byammwe, nemutuuka obuteeyisa bulungi n''obutatya Katonda nemutuuka n''obutasobola kulongoosa wakati w''abantu. Bulijjo Katonda awulira era mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(2,225,'2:225','Katonda tabavunaana mu kulayira kwa mmwe okw''olusaago, wabula abavunaana ebyo emitima gyammwe gyebikakasizza, bulijjo Katonda musonyiyi waakisa.');
INSERT INTO "african_dev" VALUES(2,226,'2:226','Abo ababa beetemye engalike nti tebagenda kuddayo kulabagana na bakyala baabwe baweebwa ebbanga lya emyezi ena, bwe baddira bakyala baabwe mu myezi egyo mazima ddala Katonda musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(2,227,'2:227','Naye bwe baba baguggubidde ku ky''okuta bakyala baabwe, mazima ddala Katonda awulira, amanyi nnyo.');
INSERT INTO "african_dev" VALUES(2,228,'2:228','Abakyala abaweereddwa talaka bateekeddwa okumala ebbanga lya ntukula ssatu era tebakkirizibwa kukweka ekyo Katonda kyaba atonze mu nnabaana waabwe bwe baba ddala nga bakkiriza Katonda n''olunaku lw’enkomerero. Era (mu kiseera ekyo) ba bbaabwe be balina okubaddira. Ekyo nno bwe baba nga bagenderera kulongoosa. Abakyala abo bateekeddwa okuweebwa nga ekyo nabo kyebateekeddwa okuwaayo mu ngeri ennungi, naye nga abasajja babalinako enkizo. Anti Katonda y''asinga okuba ow''obuyinza era nga bwa singa okuba ow''amagezi.');
INSERT INTO "african_dev" VALUES(2,229,'2:229','Okuta omukyala (nga omwami aweebwa omukisa okumuddira) kibaawo emirundi ebiri. Mu kiseera ekyo omwami ayinza okusalawo okuddira mukyalawe mu mwoyo mulungi, oba okumuta mu ngeri ennungi. Era temukkirizibwa kujja ku bakyala ba mmwe (be mutadde) kintu kyonna kwebyo bye mwabawa okugyako nga beekengedde obutatuukiriza mateeka ga Katonda mu bufumbo. Kati bwekiba nga mutidde (mwe abatabaganya) nti abafumbo bombi tebajja kusobola kutuukiriza mateeka ga Katonda mu bufumbo, tebalina musango gwonna mu kutwala ekyo omukyala kyaba yeenunula nakyo. Ago ge mateeka ga Katonda temugasukkanga. Oyo yenna amenya amateeka ga Katonda; abo nno be balyazamaanyi.');
INSERT INTO "african_dev" VALUES(2,230,'2:230','Naye omusajja bwaba atadde mukyalawe (talaka ey''okusatu) aba takyakkirizibwa kumuddira mu bufumbo oluvanyuma lwa taraka eyo okugyako nga amaze okufumbirwa omusajja omulala. Omusajja oyo omulala bw''aba amutadde tewaba musango okuddingana (N''oli eyasooka okumuta enta essatu) kasita basuubira nti bombi banaatuukiriza amateeka g''obufumbo. Ago nno gemateeka ga Katonda agannyonnyola abantu abategeera.');
INSERT INTO "african_dev" VALUES(2,231,'2:231','Bwe muba mutadde abakyala (etali Talaka esembayo) ebbanga lyebalina okutuula neliggwaako (mwe ababayinako obuyinza) temubalemesanga okuddamu okwewoowa nebabbaabwe kavuna baba bakkaanyizza mu ngeri e nnnungi (mukole ekimu kubibiri) okubaddira mu bufumbo mu ngeri e nnungi oba okwawukana nabo mu ngeri y''ekintukiramu. Temubaddiranga olw''okubalumya nga mumenya amateeka g''obufumbo. Omuntu akola ekyo aba yeereetedde yekka obuzibu. Temufuula amateeka ga Katonda ag''olusaago. Mujjukire ekyengera kya Katonda kye yabawa n''ebyo byeyabassiza mu Kur’ani ne mu njigiriza (ya Nabbi Muhammad) ebyo by''ababuulirira nabyo. Mutye Katonda mumanye nti buli kintu kyonna kyemukola akimanyi.');
INSERT INTO "african_dev" VALUES(2,232,'2:232','Bwe muba nga mutadde abakyala ebbanga lyabwe lyebatuula nga tebannafumbirwa (Idda) ne liggwaako temubagaananga okudda eri ba bbaabwe bwebaba bakkirizigannyiza mu ngeri ennungi. Etteka eryo libuulirwa oyo yenna akkiriza Katonda n''olunaku lw''enkomerero. Ekyo nno kyekisinga okuba ekyensa n''obutukuvu gyemuli. Katonda y''asinga okumanya (ekirimu omugaso gyemuli) naye mmwe temumanyi.');
INSERT INTO "african_dev" VALUES(2,233,'2:233','Abazadde (abakyala) balina okuyonsa abaana baabwe emyaka ebiri egijjudde, eri abo ababa baagadde okuyonsa okujjuvu. Kikakata ku ba taata b''abaana okubalabirira n''okubambaza mu ngeri esaana, omuntu taakakibwenga ky''atasobola. N''omukyala taayisibwenga bubi olw''omwana gw''ayonsa. Wadde kitaawe w''omwana naye tasaanidde kuyisibwa bubi olw''omwanawe. N''oyo aba asikidde taata wa baana ayisibwa mu ngeri y''emu. Bwebaba nga bakkiriziganyizza bombi okuggya omwana ku mabeere nga bayise mu kuteesa olwo tewaba namusango gwonna. Bwe muba mwagadde omukyala omulala okubayonseza omwana temulina musango gwonna kasita muba nga muwaddeyo ebyo byemukkiriziganyizzaako mu ngeri ennungi. Era mutye Katonda, era mumanye nti mazima Katonda alaba byonna bye mukola.');
INSERT INTO "african_dev" VALUES(2,234,'2:234','Ate abo ababa bafudde mu mmwe nebaleka abakyala bateekwa okwekuuma nga tebanaddamu kufumbirwa ebbanga lya myezi ena ne nnaku kumi. Bweriba nga ebbanga eryo liweddeko, temulina musango mwekyo kyebaba bakoze mu by''okwewunda (n''okukkkiriza abaagala okubawoowa) mu ngeri y''ekintukiramu (etemenya mateeka gabusiraamu). Katonda mumanyi nnyo ku byemukola.');
INSERT INTO "african_dev" VALUES(2,235,'2:235','Era temulina musango olw''ekyo kyemuba mukoze nga temwatudde nga mugenderera okwogereza abakyala (abo) oba kyemuba mukwese mu mitima gyammwe. Katonda amanyi nti mujja kwogera nabo (kunsonga y''okubawasa) naye temubalagaanyisanga mukyama mu ngeri yaakukukuta, okugyako nga mubagamba ebigambo ebirungi. Temwetantalanga kukola ndagaano y''abufumbo n''abakyala abo (abali mu Idda) okutuusa ebbanga (lya Idda) nga liweddeko. Era musaana mumanye nti mazima Katonda amanyi ebiri mu myoyo gyammwe. Mumwegendereze awamu n''ekyo mumanye nti muyitirivu waakusonyiwa era waakisa kingi.');
INSERT INTO "african_dev" VALUES(2,236,'2:236','Temulina musango singa muta bakyala ba mmwe nga temunneegatta nabo wadde nga temunnabagerekera mahare. Naye muteekwa okubasibirira. Omugagga asibirira ekigya mu mbeeraye n’omufuna mpola asibirira ekimugyamu. Okusibirira kubeera mu ngeri ya buntu bulamu. Ekyo kikakafu ku bakozi b''obulungi.');
INSERT INTO "african_dev" VALUES(2,237,'2:237','Wabula bwe muba nga mubatadde nga temunnaba kwegatta nabo naye nga mwabagerekera amahare, mubawe kitundu kyago, okugyako nga abakyala abo bagasonyiye oba nga asonyiye oyo alina obuyinza okukakasa endagaano y''obufumbo (omusajja). Naye bwe muba nga musonyiye, ekyo kye kisinga okuba okumpi n''okutya Katonda. Temwerabiranga enkolagana ennungi ebadde wakati wa mmwe. Mazima Katonda alaba ebyo byonna byemukola.');
INSERT INTO "african_dev" VALUES(2,238,'2:238','Munyiikirire nnyo e sswala zonna ettaano, naddala e sswala y''omumakkati; muyimirire mu maaso ga Katonda nga muli bakkakkamu.');
INSERT INTO "african_dev" VALUES(2,239,'2:239','Wabula bwe muba nga mutidde (omulabe) musaale nga mutambula oba nga mwebagadde. Wabula bwekuba nga okutya kuvuddewo mutendereze Katonda (nga musaala) nga bweyabayigiriza ebyo byemwali temumanyi.');
INSERT INTO "african_dev" VALUES(2,240,'2:240','Abo abatuukwako okufa mu mmwe nebaba nga balese abakyala, Katonda alagira nti abakyala abo bateekwa okulabirirwa omwaka mulamba awatali kubagobaganya mu mayumba g''aba bbaabwe. Wabula bwe bagavaamu kyeyagalire olwo nno muba temukyalina buvunaanyizibwa kw''ekyo kyebaba bakoze mu kwekolako nga beenyiriza. Bulijjo Katonda wa maanyi, ate nga agoba nsonga.');
INSERT INTO "african_dev" VALUES(2,241,'2:241','(Mu ngeri y''emu) n''abakyala ababa batalakiddwa bateekeddwa okusibirirwa mu ngeri ennungi. Ekyo kikakafu kwabo abatya Katonda.');
INSERT INTO "african_dev" VALUES(2,242,'2:242','Bwatyo nno Katonda bw''abannyonnyola ebigambo bye mulyooke mutegeere.');
INSERT INTO "african_dev" VALUES(2,243,'2:243','Tolaba abo abadduka mu mayumba gaabwe nga baali nkumi nankumi nga batya okufa, Katonda n''abagamba nti kale mufe, oluvanyuma n''abazuukiza. Mazima Katonda agabirira nnyo abantu, naye abantu abasinga obungi tebeebaza.');
INSERT INTO "african_dev" VALUES(2,244,'2:244','Mulwane mu kkubo lya Katonda era mumanye nti mazima ddala Katonda awulira era muyitirivu wa kumanya.');
INSERT INTO "african_dev" VALUES(2,245,'2:245','Ani oyo awola Katonda okuwola okulungi alyoke abe nga akumubalizaamu emirundi emingi. Era Katonda y''afunza era n''agaziya (enfuna y’omuntu) so nga era gyali gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(2,246,'2:246','Tolaba abakungu mu baana ba Israel abaaliwo oluvanyuma lwa Musa bwe baatuuka okugamba nabbi waabwe nti tuteerewo kabaka (atukulembere) tusobole okulwana mu kkubo lya Katonda. (Nabbi) naabagamba nti ‘kyemugamba nti singa mulalikiddwako okulwana nga muli ne kabaka oyo temuugaane kulwana?’ Nebagamba nti ‘Ate lwaki tetulwana mu kkubo lya Katonda nga twagobaganyizibwa dda ffe n’abaana baffe okuva munsi yaffe? Wabula bwebaalaalikibwako okulwana baakyuka okuva ku bigambo byabwe okugyako batono mubo. Wabula nga bulijjo Katonda amanyi abalyazaamanyi.');
INSERT INTO "african_dev" VALUES(2,247,'2:247','Nabbi waabwe kwekubaanukula nti mazima Katonda alonze Twaluta okuba kabaka wamwe. Bo kwekugamba nti ayinza atya okuba kabaka waffe songa ffe tusaanira obw’akabaka okumusinga, ng''era teyaweebwa bugazi bwa byanfuna. (Nabbi waabwe) n''abagamba nti Mukama Katonda amulonze mu mmwe era n''amwongera obugazi mu kumanya ne mu mubiri. Era Katonda awa obufuzi bwe oyo gwaba ayagadde. Era Katonda mugazi era mumanyi.');
INSERT INTO "african_dev" VALUES(2,248,'2:248','Nabbi waabwe n''abagamba nti mazima akabonero akakakasa obw’akabaka bwe kwekuba nti mujja kujjirwa essanduuke ng''erimu obutebenkevu okuva eri Mukama Katonda wa mmwe. Era nga mulimu ebyasigalawo kw''ebyo abantu ba Musa () n’abantu ba Haruna () bye baaleka (Essanduuko eyo) ejja kuba esituddwa ba malayika. Mazima ekyo nno ddala kirimu obujulizi gyemuli, bwe muba ddala nga muli bakkiriza.');
INSERT INTO "african_dev" VALUES(2,249,'2:249','Twaluta bweyatandika okutambula n’eggye yabagamba nti (okugenda tugenda naye mukimanye) Mazima ddala Katonda ajja kubagezesa nga akozesa omugga. Oyo yenna anaagunywako ajja kuba takyali wamuggye lyange. Naye oyo ataagekatankire oyo nno yewange ddala, mpozzi omuntu asobola okusenayo olusena lumu n’olubatulwe. Bonna baaganywa okugyako batono mubo. Bweyamala okugusomoka wamu n''abakkiriza abaali naye, nebagamba nti olwaleero luno tetuyinza kusobola Jjaluuta n''eggye lye. Bo abaakakasa nti bagenda kusisinkana Katonda kwe kugamba nti bimeka ebibiina ebitono ku lw''obuyinza bwa Katonda ebiwangula ebibiina eby''abantu abangi, era bulijjo Katonda abeera wamu n''abagumiikiriza.');
INSERT INTO "african_dev" VALUES(2,250,'2:250','Bwebaayolekaganya obwanga ne Jaaluuta n''eggye lye nebagamba nti, Ayi Katonda waffe tuwe obugumiikiriza otunyweze, era otutaase ku bantu abakaafiiri.');
INSERT INTO "african_dev" VALUES(2,251,'2:251','Ku lw''obuyinza bwa Katonda baabawangula. Dauda natta Jaaluuta era Katonda naamuwa obufuzi n''amagezi era nga bweyamuwa okumanya ebyo byeyayagala amanye. Singa tekyali nti Katonda aggyawo abantu nga akozesa abamu okugyawo bannaabwe, ensi yandiyonoonese. Naye mazima ddala Katonda ebitondebye abyagaliza bulungi.');
INSERT INTO "african_dev" VALUES(2,252,'2:252','Ebyo nno bigambo bya Katonda bye tukusomera ebyamazima, era mazima ddala ggwe (Nabbi Muhamadi) oli mu luse lw''abatume.');
INSERT INTO "african_dev" VALUES(2,253,'2:253','Abo ababaka twasukkulumya abamu ku bannaabwe, abamu ku bo Katonda yayogera nabo, nabalala naabasukkulumya mu madaala. Isa mutabani wa Mariam twamuwa obujulizi obwenkukunala era netumuwagira ne mwoyo mutukuvu (Jiburilu), Singa Katonda yayagala abaaliwo mu mirembe egyaddako tebandirwanaganye oluvanyuma lwobunnyonnyofu okubajjira naye baayawukana, abamu mu bo bakkiriza naabalala nebajeema, era singa Katonda yayagala tebandirwanaganye, wabula mazima Katonda akola ekyo kyaba ayagadde.');
INSERT INTO "african_dev" VALUES(2,254,'2:254','Abange mwe abakkiriza muweeyo kwebyo byetwabagabira, nga olunaku okutaliba kutunda wadde omukwano oba okuwolereza terunatuuka, naye Abakafiiri bebalyazamanyi (olwobutagondera mateeka g’a Katonda).');
INSERT INTO "african_dev" VALUES(2,255,'2:255','Katonda yekka, ateekwa okusinzibwa, yooyo omulamu obulamu obwolubeerera nga yeyimirizaawo yekka awatali kwetaaga buyambi bwonna, takwatibwa kusumagira wadde otulo, nannyini wa byonna ebiri mu ggulu ne mu nsi, tewali agenda kwetantala kuwolereza (munne) ewa Katonda okugyako nga amukkirizza, Katonda amanyi ebyo ebiri mumaaso gaabwe n''emabega wabwe ate bo tebasobola kumanya mubujjuvu kintu kyonna mwebyo byamanyi okugyako kyaba ayagadde, entebe ye ngazi okumalayo eggulu ne nsi, so nga ate eggulu ne nsi okubikuuma byombi tekimukaluubirira. Era ye yaasinga okuba owa waggulu, ow''ekitiibwa e kisuffu.');
INSERT INTO "african_dev" VALUES(2,256,'2:256','Tewali kuwaliriza bantu kuyingira mu ddiini anti obulungamu bweyolese okuva ku bubuze, oyo yenna awakanya Sitane nakkiriza Katonda aba yeekutte ku muguwa omugumu ogutakutuka, bulijjo Katonda awulira era amanyi byonna.');
INSERT INTO "african_dev" VALUES(2,257,'2:257','Katonda yemweyimirize wa bakkiriza, abaggya mu bizikiza naabazza eri ekitangaala, bo abakafiiri, ebintu bya Sitane ye mweyimirize waabwe, bibaggya mu kitangaala nebibazza mu nzikiza, abo be bantu b''omu muliro b''akugutuulamu olubeerera.');
INSERT INTO "african_dev" VALUES(2,258,'2:258','Tolaba oyo eyawakana ne Ibrahim ku bwomu bwa Katonda we, olwokuba nno bambi Katonda yamuwa obufuzi ekyamuletera okwekuluntaza, Ibrahim bweyamugamba nti Katonda wange yooyo awa obulamu era natta, ye naddamu nti nze mpa obulamu era nenzita, Ibrahim naagamba nti mazima Katonda enjuba agiggya buvanjuba kale ggwe giggye ebugwanjuba. Awo nno omuwakanyi oyo ebigambo nebimuggwako anti bulijjo Katonda talungamya bantu beeyisa bubi.');
INSERT INTO "african_dev" VALUES(2,259,'2:259','Oba twala ekyokulabirako kyoyo eyayita ku kyalo nga obusolya bwakyo buguddemu naagamba nti ekyalo kino Katonda agenda kukizzaawo atya nga kyafa dda! Katonda naamutta okumala emyaka kikumi oluvanyuma naamuzuukiza naamugamba nti omaze bbanga ki nga ofudde? Naamuddamu nti maze olunaku lumu oba ekitundu kyalwo, naamugamba nti nedda omaze emyaka kikumi, Ggwe tolaba emmere yo n''ebyokunywa byo tebyonoonese!!! Ate tunula ku ndogoyi yo (eweddeko ennyama neeba magumba meerere), nga ekigendererwa tukufuule ekyokulabirako eri abantu. Tunuulira olabe amagumba ge ndogoyi yo nga bwetugayunga olwo nno netugambaza ennyama, bweyamala okulaba nga bwegutyo bweguli naagamba nti kaakano nkitegedde Katonda asobola okukola buli kintu.');
INSERT INTO "african_dev" VALUES(2,260,'2:260','Jjukira ekiseera Ibrahim weyagambira nti, Ayi Katonda wange ndaga engeri gyolizuukizaamu abafu, Katonda naagamba nti tokkirizanga! Ibrahim naagamba nti nakkiriza naye ekyo nkisaba lwakwongera mutima gwange kumatira, Katonda naamugamba nti kwata ebinyonyi bina obitwale obisale, obitemeteme bwomala ku buli lusozi oteekeko ekifi kimu, oluvanyuma obikoowoole bijja kuggya gyoli mubwangu, bulijjo kiteekwa okumanyibwa nti mazima Katonda nantakubwa ku mukono ate mugoba nsonga.');
INSERT INTO "african_dev" VALUES(2,261,'2:261','Abo, abawaayo ebimu ku bintu byabwe mu kubo lya Katonda, bafaananako omuntu asiga empeke emu emera n''eweeka ebirimba musanvu nga buli kirimba kiriko empeke kikumi, bulijjo Katonda ayongeza ebigabwabye eri oyo gwaba ayagadde, anti bulijjo Katonda mugazi mu mbeera ze zonna ate nga muyitirivu wa kumanya.');
INSERT INTO "african_dev" VALUES(2,262,'2:262','Abo abawaayo e mmaali mu kkubo lya Katonda ate kyebawaddeyo nebatakigoberezaako kulaalaasa oba ebinyiiza, abo bagenda kufuna e mpeera yaabwe ewa Katonda waabwe, era tebagenda kuba nakutya kwonna mu maaso ga Katonda era nga tebagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(2,263,'2:263','Ekigambo akyobuntu bulamu n''obutayogerera bubi musabi kirungi okusinga saddaaka egoberezebwako ebigambo ebitasanyusa, bulijjo Katonda ssi mwetaavu (era) ow''ekisa ekingi.');
INSERT INTO "african_dev" VALUES(2,264,'2:264','Abange mmwe abakkiriza temwonoonanga saddaaka zammwe kulwokuziraalasa n’okuzigoberezaako ebigambo ebinyiiza gamba nga omuntu awaayo emmaali ye olwabantu okumulaba naye nga takkiriza Katonda n''olunaku lw’enkomerero, omuntu oyo alinga olwaazi oluliko ettaka namuttikwa w''enkuba naalutonnyako naaluleka nga lukalu, nabwekityo abantu abakola kulwabandabe byonna byebakola bifuumuuka nebaba nga tebabifunamu mpeera mumaaso g’a Katonda bwatyo nno Katonda talungamya bantu bajeemu.');
INSERT INTO "african_dev" VALUES(2,265,'2:265','Kyo ekifaananyi kyabo abawaayo emmaali yaabwe nga banoonya kusiima kwa Katonda n''okusinziira ku kukkiriza kwabwe Katonda okwa namaddala, baba nga ennimiro eri ku ttaka eggimu neetonnyebwamu namutikwa wenkuba ebibala byayo nebyebazaamu emirundi ebiri nebweba tefunye namutikwa wa nkuba waakiri olufuuyirize. Bulijjo Katonda alabira ddala byonna byemukola.');
INSERT INTO "african_dev" VALUES(2,266,'2:266','Abaffe omu ku mmwe yandyagadde okubeera nennimiro eyeemitende nemizabibu nga emigga gikulukutira wansi waayo ngalina mu yo buli kika kyabibala, obukadde nebumutuukako ate nga alina abaana abakyali abato, kibuyaga nga alimu n''omuliro naagikuba neggya neggwawo, bwatyo nno Katonda bwabannyonyola ebigambo ebigasa musobole okwefumiitiriza.');
INSERT INTO "african_dev" VALUES(2,267,'2:267','Abange mmwe abakkiriza muweeyo ku birungi byemuba mukoze ne mwebyo byetubaggira wansi mu ttaka, temulondangamu ebibi mu byo mube nga byemuwaayo, so singa biba biweereddwa mmwe temwandibitutte okugyako nga mwefudde abatalaba (nemumala gabitwala) era musaana mukimanye nti mazima Katonda talina bwetaavu eri kintu kyonna era yaatenderezebwa.');
INSERT INTO "african_dev" VALUES(2,268,'2:268','Obukodo n''okwagala okuwaayo ebitali birungi nga ye saddaaka, Sitane y''ebibassaamu, anti yyo Sitane ebatiisa obwavu neebalagira okukola ebibi so nga Katonda abalagaanyisa kisonyiwo okuva gyali nakubawa birungi, bulijjo Katonda mugazi nnyo mubyagaba mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(2,269,'2:269','Katonda awa okutegeera ensonga, oyo gwaba ayagadde era oyo aba aweereddwa okutegeera ensonga aba aweereddwa ebirungi ebiyitirivu. Tewali bayinza kwegasa nakino okugyako abalina amagezi agakola ennyo.');
INSERT INTO "african_dev" VALUES(2,270,'2:270','(Muteekwa mukimanye nti ) buli kyonna kyemuba muwaddeyo oba naziri yonna gyemuba mwetemye mazima Katonda abimanyi. Abeeyisa obubi nebatatuukiriza kyebateekwa kukola, tebagenda kufuna abataasa ku Katonda.');
INSERT INTO "african_dev" VALUES(2,271,'2:271','Saddaaka bwemuziwa mu lwatu kiba kirungi naye ate bwe muba muzikisizza (nemuziwa abaavu) mukyama kyekisinga obulungi, era ekyo kibasonyiyisa ebibi byammwe, bulijjo Katonda amanyi nnyo byemukola.');
INSERT INTO "african_dev" VALUES(2,272,'2:272','(Ebyo ebyogeddwa waggulu bimala okumatiza omuntu) Ssi ggwe ovunaanyizibwa ku kulungama kwaabwe, wabula Katonda alungamya oyo gwaba ayagadde, buli kirungi kyemuwaayo kigasa mmwe, (nga abakkiriza) buli kyonna kyemuwaayo mugenderera kudda eri Katonda, buli kirungi kyonna kyemuwaayo kigenda kubasasulwa mu bujjuvu era temugenda kulyazaamanyizibwa.');
INSERT INTO "african_dev" VALUES(2,273,'2:273','(Saddaaka ziweebwa) abaavu abo abeewaayo mu kuweereza mu kkubo lya Katonda nga tebasobola kutakabana nga banoonya riziki mu nsi. Olwensa zaabwe, atamanyi alowooza nti bagagga osobola okubamanya olwobubonero bwaabwe, anti tebabeeba Bantu nga basaba, (n’olwekyo abalina baweeyo) anti buli kalungi konna kemuwaayo Katonda akamanyira ddala.');
INSERT INTO "african_dev" VALUES(2,274,'2:274','Abo abawaayo e mmaali yaabwe ekiro n''emisana, mu kyama ne mu lwatu, bagenda kufuna empeera yaabwe ewa Katonda waabwe, tebagenda kutuukibwako kutya era tebagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(2,275,'2:275','Abo abalya Ribba (emaali ey''okuwola okw’okwongeramu) ku lunaku lwokuzuukira tebagenda kuyimuka wabula nga bwayimuka oyo aba akubiddwa Sitane naagwa eddalu, ekyo nno lwakuba bagamba nti okusubula ne Ribba tebirina njawulo (anti byonna kuwaanyisiganya byamaguzi na kufuna magoba) wabula ekituufu kiri nti Katonda yasalawo nti okusuubula kuli Halaali, yo Ribba eri Haramu. Kale nno oyo anaatuukwako okubuulirira okuva eri Katonda naava kunkola eya Ribba ebyo byeyakola mukusooka tebimuvunaanwa, ensonga ze zisigadde wa Katonda we, naye oyo anaddayo kunkolagana eya Ribba, abo nno, be bantu b''omu muliro ba kugubeeramu obugenderevu.');
INSERT INTO "african_dev" VALUES(2,276,'2:276','Katonda agya emikisa mu mmaali ya Ribba kyokka naayaza emmaali etoolwamu Saddaaka, Katonda tayagala mujeemu yenna awalaaza empaka (alemera ku Ribba aba agugubidde ku kibi n''okujeemera Katonda).');
INSERT INTO "african_dev" VALUES(2,277,'2:277','Mazima abo abakkiriza era nebakola emirimu emirungi nebayimirizaawo e sswala nebatoola zzaka bagenda kufuna empeera yaabwe ewa Katonda waabwe tebagenda kutuukwako kutya era tebagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(2,278,'2:278','Abange mmwe abakkiriza mutye Katonda era musazeemu enkolagana ya Ribba gyemubadde mukyalimu bwe muba nga ddala muli bakkiriza.');
INSERT INTO "african_dev" VALUES(2,279,'2:279','Bwemutaakikole mumanye nti Katonda n''omubakawe babalangiriddeko olutalo naye bwe muba mwenenyezza sente zammwe (Capital) zemwassaamu muzigyeeyo, mukino mube nga temufiiriza wadde okufiirizibwa.');
INSERT INTO "african_dev" VALUES(2,280,'2:280','Abanjibwa bwaba nga ali mu buzibu asaanidde alindirizibwe okutuusa lwanabeera nga ali mu mbeera mwasobolera okusasula, (naye mmwe ababanja) singa muba musonyiye bemubanja kyekirungi gyemuli singa mumanyi.');
INSERT INTO "african_dev" VALUES(2,281,'2:281','Mutye olunaku lwemuliddizibwa ewa Katonda oluvanyuma buli muntu asasulwe ebyo byeyakola era tebagenda kuyisibwa bubi.');
INSERT INTO "african_dev" VALUES(2,282,'2:282','Abange mmwe abakkiriza bwe muba muwolaganye nga ebbanja liriko ekiseera ekigere muwandiikenga, omuwandiisi ateekeddwa okuwandiika endagaano wakati wa mmwe mubwenkanya, omuwandiisi tasaanye kugaana kuwandiika nga Katonda bweyayigiriza, (omuwandiisi) ateekwa awandiike, gwebabanja y''ateekwa okwogera ebiwandiikwa mu ndagaano era mu kukola kino ateekeddwa okutya Katonda mukamaawe, tasaanye kulekayo kintu kyonna kikwata ku bbanja eryo, gwebabanja bwaba wantegeera nnafu oba nga mwana muto oba nga tasobola kuba nti yaayogera ebiwandiikibwa mu ndagaano olwobutasobola obuli ku mubiri gwe, amulabirira y''ateekwa okwogera ebiwandiikibwa mu ndagaano nga akikola mu bwesimbu. (Nga mukola endagaano zino) muteekengawo abajulizi babiri nga basajja mu mmwe, bwebataba basajja babiri, kale abe omusajja omu n''abakyala babiri nga mubalonda mwabo bemulabye nga basaanira okuba abajulizi, era nga singa omukyala omu obujulizi buba bumutabuseeko mu mmwe, munne asobola okumujjukiza, abajulizi tebagaananga okujja bwe baba nga bayitiddwa temwekaanyanga okuwandiika ebbanja kalibe ttono oba ddene, muwandiike nga mulaga ekiseera kyalyo werigwerayo, okuwandiika ebbanja mu ngeri eyo kiragira ddala obwenkanya obuli mu mateeka ga Katonda, era kyekisinga okuyamba obujulizi obutabula mu ngeri yeemu kyekisinga okubayamba obutabaawo kubuusabuusa (nti ebbanja lyali lyenkanawa oba lyakusasula ddi) okugyako nga kubadde kugulana okw''ekiggwerawo kwemukolaganamu mu nkola eyabulijjo mwekyo tumulina musango bwe muba nga temukoze ndagaano, era nebwe muba nga mugulaanye musseewo abajulizi (mukimanye) omuwandiisi wendagaano n''omujulizi tebasaanye kuyisibwa bubi, naye nemumala mukikola kiba kikolwa kya bugyemu okuva mu mmwe, bulijjo muteekeddwa okutya Katonda era Katonda bulijjo abayigiriza ebyo ebirimu emigaso gyammwe anti Katonda amanyi buli kintu.');
INSERT INTO "african_dev" VALUES(2,283,'2:283','Bwe muba nga muli ku lugendo olwo nno omusingo gukwatibwe naye bwe muba nga mwesiganganye nemutawandiika ndagaano oba okukwata omusingo oyo eyateekebwamu obwesige ateekwa okutuukiriza obwesigwa obwo era ateekeddwa okutya mukama omulabirizi we (bwemutwalibwa nga okuba abajulizi) temukwekanga obujulizi, oyo yenna abukweka omutima gwe guba gukoze ekibi bulijjo Katonda amanyi byonna byemukola.');
INSERT INTO "african_dev" VALUES(2,284,'2:284','Ebiri mu ggulu omusanvu ne mu nsi byonna bya Katonda, kamwolese oba mukweke ebiri mu mitima gyammwe Katonda agenda kubibabalira, olwo nno asonyiwe oyo gwaliba ayagadde era abonereze oyo gwaliba ayagadde anti Katonda muyinza wabuli kintu.');
INSERT INTO "african_dev" VALUES(2,285,'2:285','Omubaka yakkiriza naakakasa ebyo ebyassibwa ku ye nga biva ewa mukama omulabiriziwe Katonda we n''abakkiriza bwebatyo nebakakasa, buli omu ku bo yakkiriza Katonda ne ba Malayika be neebitabo bye n''ababakabe era nebagamba nti tetwawula wakati wa babakabe era nebagamba nti tuwulidde netugonda, Ayi Mukama Katonda waffe tukusaba ekisonyiwo era gyoli yeeri obuddo.');
INSERT INTO "african_dev" VALUES(2,286,'2:286','Katonda tawaliriza muntu okugyako obusobozi bwe webukoma, buli mwoyo gugenda kusasulwa ebirungi byegwakola era nga bwegugenda okuvunaanwa olwebibi byegwetikka. Ayi Mukama omulabirizi waffe totuvunaana byetukoze mukwerabira oba mu butanwa, Ayi Mukama omulabirizi waffe totutikka obuzito nga bwewatikka abo abaatukulembera, Ayi Mukama omulabirizi waffe totulagira kukola byetutasobola, tugyeko ebibi otusonyiwe otusaasire, gwe mukuumi waffe tutaase ku bantu Abaakaafuwala.');
INSERT INTO "african_dev" VALUES(3,1,'3:1','Alif Laamu Mimu.');
INSERT INTO "african_dev" VALUES(3,2,'3:2','Katonda yooyo tewali kisinzibwa mu butuufu okugyako yye, omulamu, (atendebwa n''obulamu obujjuvu obwolubeerera) eyeeyimirizaawo yekka era ayimirizaawo buli kintu kyonna.');
INSERT INTO "african_dev" VALUES(3,3,'3:3','Yassa gyoli ggwe (Nabbi Muhammad) ekitabo (Kur’ani) nga kijulira amazima geebyo e byakikulembera era ye yassa Taurat ne enjili.');
INSERT INTO "african_dev" VALUES(3,4,'3:4','Oluberyeberye lwa Kur’ani nga kyakulungamya eri abantu, era yassa ebyawula wakati wa mazima n''obulimba, mazima abo abawakanya ebigambo bya Katonda byeyassa balina ebibonerezo ebiyitirivu, Katonda ye nnantakubwa ku mukono, nannyini kwesasuza.');
INSERT INTO "african_dev" VALUES(3,5,'3:5','Katonda tewali kintu kyonna kimwekweka mu nsi ne mu ggulu.');
INSERT INTO "african_dev" VALUES(3,6,'3:6','Katonda yooyo abatondera ku bifaananyi byammwe mu nnabaana nga bwaba ayagadde. tewali kisinzibwa kyonna mu mazima, okugyako yye. nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(3,7,'3:7','Yye, yooyo eyassa ku ggwe (Muhammad) ekitabo (Kur’ani) mulimu mu kyo ebitundu nga aya zaamu nyinyonnyofu ebyo ngagwemusingi gwekitabo (Kur’ani). Era mulimu ebitundu ebirala nga Aya zaamu ziwa amakulu agafaanagana (nga okuzitegeera kwesigamye ku bitundu bya Aya ezo''musingi gwa Kur’ani) Naye abalina obubuze mu mitima gyabwe (olwebigendererwa byabwe ebibi bo) balondamu Aya eziwa amakulu agafaanagana nga tebeesigamye ku Aya zinannyini musingi gwa Kur’ani, nga ekigendererwa kyabwe kubuza bantu n''olwokwagala okuziggya ku makulu gaazo amatuufu, so nga tewali amanyi makulu gaazo amatuufu okugyako Katonda, nga abakenkufu mu kumanya bagamba nti tukkiriza Kur’ani, byonna ebigirimu byava wa Mukama omulabirizi waffe era nga tewali ategeera bigirimu okugyako ba nannyini magezi amasukkulumu.');
INSERT INTO "african_dev" VALUES(3,8,'3:8','(Era bagamba nti) Ayi Mukama omulabirizi waffe toggya mitima gyaffe ku bulungamu oluvanyuma lwokuba nti wabutuwa, anti mazima, ggwe mugabi wa byonna.');
INSERT INTO "african_dev" VALUES(3,9,'3:9','Ayi Mukama omulabirizi waffe tukkiriza era nga tukakasa nti ggwe ojja kukunganya abantu ku lunaku olutaliiko kubuusabuusa, anti mazima Katonda tayawukana kundagaano gye yalagaanyisa.');
INSERT INTO "african_dev" VALUES(3,10,'3:10','Mazima abo abaakaafuwala emmaali yaabwe n’abaana baabwe tebigenda kubagasa naakamu oba okubataasa ku bibonerezo bya Katonda era abo bennyini zeeziriba enku z''omuliro (ku lunaku olwo).');
INSERT INTO "african_dev" VALUES(3,11,'3:11','Embeera yaabwe mukulimbisa ebigambo bya Katonda efaanana ey’abantu ba farawo n''abaakulembera, Abantu ba Firawo abaalimbisa ebigambo bya Katonda, Katonda naababonererezaawo olwebyonoono byabwe, anti Katonda muyitirivu wa kubonereza.');
INSERT INTO "african_dev" VALUES(3,12,'3:12','Ggwe Nabbi, gamba abo abaakaafuwala nti mujja kuwangulwa era mukunganyizibwe mu muliro Jahannama, nabwo bwe buddo obubi ennyo.');
INSERT INTO "african_dev" VALUES(3,13,'3:13','Mwalina ekyokulabirako ekyenkukunala ku bibinja ebibiri ebyasisinkana (ku lutalo lwe Badri) ekibinja ekimu (ekya Nabbi () ne banne) nga balwana kutaasa ddiini ya Katonda, nga n''ekibinja ekirala kya baakaafiiri. Abakafiiri baalaba abakkiriza mu muwendo nga babakubisaamu emirundi ebiri, anti Katonda awagira n''okutaasa kwe oyo gwaba ayagadde, mazima mu ekyo mulimu ekyokuyiga eri ba nannyini kulaba okutuufu.');
INSERT INTO "african_dev" VALUES(3,14,'3:14','Abantu baateekebwamu okwagala ennyo ebyegombebwa nga Abakyala n''abaana ne ntuumu za zzaabu ne ffeeza ne mbalaasi e zinyirira n''ebisolo ebyawaka n''amasamba ebyo byonna bya kweyagala bya bulamu bwansi, so nga ewa Katonda yeeri obuddo obusinga obulungi.');
INSERT INTO "african_dev" VALUES(3,15,'3:15','Bagambe nti mbategeeze ekirungi okusinga ebyo. Abatya Katonda bategekerwa ewa Katonda waabwe e jjana nga emigga gikulukutira mu yo, ba kugibeeramu obugenderevu era baweebwe nabakyala abatukuvu era baakufuna okusiima kwa Katonda, bulijjo Katonda alaba abaddu be.');
INSERT INTO "african_dev" VALUES(3,16,'3:16','(Abaddu aboogerwako) beebo abagamba nti; Ayi Mukama omulabirizi waffe mazima ffe tukkirizza n''olwekyo tukusaba otusonyiwe ebibi byaffe otwesambise ebibonerezo by’omuliro.');
INSERT INTO "african_dev" VALUES(3,17,'3:17','(Era beebo) abagumiikiriza ne babeera abakkiriza abaamazima era abagonvu era abasaddaaka era nga beegayirira Katonda amatumbi budde.');
INSERT INTO "african_dev" VALUES(3,18,'3:18','Katonda yakakasa nti ddala tewali kisinzibwa (mu butuufu) okugyako yye neba malayika ne bannyini kumanya nabo bakakasa ekyo, era nga bulijjo Katonda aliwo kutuukiriza bwenkanya, tewali kisinzibwa okugyako yye nantakubwa ku mukono mugobansonga.');
INSERT INTO "african_dev" VALUES(3,19,'3:19','Mazima obusiraamu ye ddiini entuufu ewa Katonda era abaaweebwa e kitabo (Taurat ne njili) tebayawukana okugyako luvannyuma lwakufuna okumanyisibwa okuva eri Katonda kulwokwekolera ensalwa bokka nabokka n''omuntu yenna olwokwekolera eyewaggula ku bigambo bya Katonda mazima Katonda takaluubirirwa kubala.');
INSERT INTO "african_dev" VALUES(3,20,'3:20','Bwebaba bakuwakanyizza, ggwe gamba nti neewaddeyo nzenna ewa Katonda nabuli, yenna angoberera ekyo kyakola, era ogambe abo abaaweebwa ekitabo naabo abatalina kitabo (ba mushirikiina) nti nammwe mwewaddeyo bwe beelangirira nti basiramuse olwonno baba balungamye, bwebatakola batyo, anti ggwe kyoteekwa okukola kubatusaako bubaka era bulijjo Katonda alaba abaddu be (nebyebakola).');
INSERT INTO "african_dev" VALUES(3,21,'3:21','Mazima abeewaggula kubigambo bya Katonda era ne batta ba Nabbi mubukyamu era ne batta abo abalagira abantu okukola obwenkanya, basanyuse (mu kubakiina) nti bagenda kufuna ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(3,22,'3:22','Abo beebo emirimu gyabwe gyafaafaagana ku nsi ne ku nkomerero era tebagenda kufuna wa kubataasa.');
INSERT INTO "african_dev" VALUES(3,23,'3:23','Tolaba abo abaaweebwa omukisa nebafuna ekitabo nga bayitibwa okudda eri ekitabo kya Katonda abalamule, ate ekitundu ku bo, nebatakikola. Mukukola ekyo nebaba nga bavudde ku mazima gebamanyi.');
INSERT INTO "african_dev" VALUES(3,24,'3:24','Ekyo nno lwakuba nti bagamba nti omuliro tegugenda kutwokya okugyako nnaku mbale bubazi, ebigambo byebeyiyiza nebibagayaaza mu ddiini yaabwe.');
INSERT INTO "african_dev" VALUES(3,25,'3:25','Guliba gutya bwetulibakunganya ku lunaku olutaliimu kubuusabuusa! buli muntu n’asasulwa kyeyakola era tebagenda kuyisibwa bubi.');
INSERT INTO "african_dev" VALUES(3,26,'3:26','(Nabbi Muhammad) gamba nti; Ayi mukama Katonda, omufuzi w''obufuzi bwonna, owa obufuzi oyo gw’oba oyagadde era n’obugya kw’oyo gw’oba oyagadde, ositula oyo gw’oba oyagadde n’okakkanya oyo gwoba oyagadde. Obulungi bwonna buli mu mukono gwo anti ggwe muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(3,27,'3:27','Oyingiza ekiro mu musana, nga bwoyingiza omusana mu kiro. Era ogya ekiramu mu kifu, nga bwogya ekifu mu kiramu era owa riziki oyo yenna gwoba oyagadde awatali kubalirira.');
INSERT INTO "african_dev" VALUES(3,28,'3:28','Abakkiriza tebafuulanga Abakafiiri ab''emikwano nebalekawo abakkiriza. Omuntu akola bwatyo talina kintu kyonna ewa Katonda, okugyako nga mubeekengedde nti bayinza okubatusaako akabi (olwo nno musobola okukolagana ku ngulu) Katonda abalabula mmwe abakkiriza mumwegendereze, anti ewa Katonda yeeri obuddo.');
INSERT INTO "african_dev" VALUES(3,29,'3:29','Gamba ggwe (Nabbi Muhammad () nti nebwemukweka oba obutakweka ebiri mu bifuba byammwe, Katonda abimanya era amanyi ebiri mu ggulu n’ebiri mu nsi. Bulijjo Katonda mumanyi wa buli kintu.');
INSERT INTO "african_dev" VALUES(3,30,'3:30','(Lowooza) ku lunaku buli muntu lwalisanga buli kyonna kyeyakola mu birungi nga kiteekeddwa awo, era nabuli kibi kyonna kyeyakola nga kiteekeddwa awo, nga yeegomba nti singa wakati we n’ekibi ekyo waliwo ebbanga ddene, Katonda abalabula mmwe abakkiriza okumwegendereza, era Katonda musaasizi eri abamusinza.');
INSERT INTO "african_dev" VALUES(3,31,'3:31','Gamba abantu (ggwe Muhammad () nti bwe muba mwagala Katonda, kale nno mungoberere Katonda ajja kubaagala era abasonyiwe ebyonoono byammwe, bulijjo Katonda musonyoyi musaasizi.');
INSERT INTO "african_dev" VALUES(3,32,'3:32','Gamba nti mugondere Katonda n''omubaka wabula bwemutakikola mazima Katonda tayagala bakaafiiri.');
INSERT INTO "african_dev" VALUES(3,33,'3:33','Mazima Katonda yawa Adam ne Nuhu n’abo mu nyumba ya Ibrahim n’abo mu nyumba ya Imran enkizo ku bantu abalala.');
INSERT INTO "african_dev" VALUES(3,34,'3:34','Nga bazukkulu, abamu bava mu bannabwe, bulijjo Katonda awulira mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(3,35,'3:35','Jjukira mukyala wa Imran bweyagamba nti Ayi Mukama omulabirizi wange mazima nze nneeyamye ku lulwo ekiri mu lubuto lwange kibe ekiwebwayo gyoli era nkusaba okkirize ekiwebwayo kyange, anti mazima ddala ggwe ggwe awulira omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(3,36,'3:36','(Maama wa Mariam) bweyazaala omwana oyo yagamba nti ayi mukama omulabirizi wange muzadde nga muwala, wabula Katonda ye yali asinga okutegeera kyeyali azadde, omusajja talinga mukazi era mazima nze mutuumye erinnya Mariam era mazima nze mukwasa ggwe ne zzadde lye omwesambise obubi bwa Sitane eyagobwa mu kusaasira kwo.');
INSERT INTO "african_dev" VALUES(3,37,'3:37','Mukama omulabirizi we yamwanukula olwanukula olwa namaddala era naamukuza enkuza ennungi era naamukwasa Zakaria abe omukuza, buli Zakaria lweyayingiranga mu kifo Mariam weyatuulanga olwokusinza ng’amusanga alina ebyokulya. Naagamba nti owange Mariam bino obifuna kuva wa, naamuddamu nti byo biva wa Katonda, mazima Katonda agabirira oyo gwayagadde awatali kubalirira.');
INSERT INTO "african_dev" VALUES(3,38,'3:38','Awo nno Zakaria yasaba Mukama omulabirizi we nga agamba nti Ayi mukama omulabirizi wange ngabira okuva gyoli ezzadde eddungi anti mazima ggwe owulira edduwa y’omusabi.');
INSERT INTO "african_dev" VALUES(3,39,'3:39','Malayika naamukowoola bweyali ng’ayimiridde mu ssinzizo ngasaba n’amugamba nti mazima Katonda akuwa amawulire amalungi (ag''omwana) Yahaya, anajja ng’akakasa amazima g’ekigambo okuva eri Katonda era nga wa kitiibwa owensa era Nabbi ali mu luse lwa balongoofu.');
INSERT INTO "african_dev" VALUES(3,40,'3:40','Zakaria naagamba nti; Mukama wange nnaafuna ntya omwana nga nkaddiye bwenti, era nga ne mukyala wange mugumba! (Malayika) naagamba nti bwatyo Katonda akola kyaba ayagadde.');
INSERT INTO "african_dev" VALUES(3,41,'3:41','Naagamba nti Ayi Mukama omulabirizi wange mpa akabonero. (Malayika) naagamba akabonero ko kwekuba nti tojja kwogera n’abantu okumala ennaku ssatu okugyako ng’okozesa kulaza. Era jjukira Katonda wo era otendereze akawungeezi nenkya.');
INSERT INTO "african_dev" VALUES(3,42,'3:42','Era jjukira Malayika bweyagamba Mariam nti mazima Katonda akulonze naakutukuza naakwawula ku Bakyala abalala bonna.');
INSERT INTO "african_dev" VALUES(3,43,'3:43','Owange Mariam weeweyo eri Mukama omulabirizi wo ovunname era okutame ku maviivi nga abalala bwebakutama.');
INSERT INTO "african_dev" VALUES(3,44,'3:44','Ebyo by’ebimu ku bigambo ebyekusifu byetukutegeeza (Ggwe Muhammad) mu bubaka era tewali nabo web’akasukira e kalamu zaabwe ku mazzi ngabanoonya okumanya nti ani ku bo anaakuza Mariam, era tewali nabo bwe baali bakaayagana (ku nsonga eyo).');
INSERT INTO "african_dev" VALUES(3,45,'3:45','Era jjukira ekiseera Malayika bweyagamba Mariam nti; mazima Katonda akuwa amawulire agessanyu ag’ekigambo ekiva gyali (ekyokuzaala omwana) ngerinya lye ye Masiihi Isa mutabani wa Mariam aliba ow''ekitiibwa ku nsi ne ku nkomerero era nga w’amwabo abasembezebwa ewa Katonda.');
INSERT INTO "african_dev" VALUES(3,46,'3:46','Era agenda kwogera n''abantu nga ali mu kibaya ne mukusajjakula era wa mu luse lwabalongofu.');
INSERT INTO "african_dev" VALUES(3,47,'3:47','(Mariam) naagamba nti Mukama wange naafuna ntya omwana nga tewali muntu yali ankutteko (Malayika) naagamba nti bwekityo (bwekijja akuba) Katonda atonda nga bwaba ayagadde bwasalawo ekintu mazima akigamba bugambi nti ba ne kiba.');
INSERT INTO "african_dev" VALUES(3,48,'3:48','Era (Katonda) ajja kumuyigiriza amateeka n’okugoba ensonga ne Taurat ne njili.');
INSERT INTO "african_dev" VALUES(3,49,'3:49','Era agenda kubeera mubaka eri abaana ba Israel (ng’abagamba nti) mazima nze mbajjidde n’akabonero okuva eri Mukama Katonda wa mmwe nja kubakolera ekintu ekifaanana nga ekinyonyi okuva mu ttaka, nja kukifuuwamu omukka kibeere ekinyonyi ku lwobuyinza bwa Katonda, era njakuwonya abempoma n’abebigenge, nzuukize naabafu (byonna) kulwobuyinza bwa Katonda, era nja kubategeeza bwemulina okulya nebyemuteekwa okutereka mu mayumba gammwe, mazima ddala mw’ebyo (ebimenyeddwa) mulimu akabonero gyemuli bwe muba nga muli bakkiriza.');
INSERT INTO "african_dev" VALUES(3,50,'3:50','Era mbajjidde nga nkakasa amazima g’ebyo ebyakulembera ebyajjira mu Taurat. Era mbakkirize ebimu kw’ebyo ebyali byabagaanibwa, era mbajjidde n’akabonero okuva eri mukama Katonda wa mmwe nolw’ekyo mutye Katonda era mungondere.');
INSERT INTO "african_dev" VALUES(3,51,'3:51','Mazima Katonda ye Mukama wange era Mukama wa mmwe kale mumusinze. Okukola ekyo lyekkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(3,52,'3:52','Naye Isa bweyamala okulaba obukaafiiri bwabwe naagamba nti; baani abanannyamba ku mulimu gwa Katonda, Abayigirizwa nebagamba nti ffe bataasa ba Katonda, tukkiriza Katonda. Naawe julira nti mazima ffe twewaddeyo ewa Katonda.');
INSERT INTO "african_dev" VALUES(3,53,'3:53','Ayi Mukama Katonda waffe tukkiriza ebyo byewassa era netugoberera omubaka, kale tuwandiike mu bajulizi.');
INSERT INTO "african_dev" VALUES(3,54,'3:54','(Abayudaaya) baasala enkwe ne Katonda n’asalawo okulemesa enkwe zaabwe anti bulijjo Katonda mulungi okusinga abo abasala enkwe.');
INSERT INTO "african_dev" VALUES(3,55,'3:55','Jjukira ekiseera Katonda bweyagamba nti; Owange ggwe Isa nze ngenda kukutuukiririza ekyo kyenakuwandiikako, era ngenda kusitula nkuleete gyendi era nkutukuze kwabo abaakaafuwala, abo abakugoberera ngenda kubassa waggulu waabo abakafuwala okutuusa ku lunaku lw’enkomerero oluvanyuma gyendi mwenna gyemugenda okudda, olwo ndyoke mbalamule mwebyo byemwayawukanangamu.');
INSERT INTO "african_dev" VALUES(3,56,'3:56','Bbo Abaakaafuwala ngenda kubabonereza n’ebibonerezo ebikakali ku nsi ne ku nkomerero, era tebagenda kufuna baakubataasa.');
INSERT INTO "african_dev" VALUES(3,57,'3:57','Naye bbo, abo Abakkiriza nebakola emirimu emirungi (Katonda) agenda kubajjuliza empeera zaabwe, anti bulijjo Katonda tayagala bakozi ba bibi.');
INSERT INTO "african_dev" VALUES(3,58,'3:58','Ebyo tubikusomera Ggwe (Nabbi Muhammad) nga byamububonero n’okubuulirira okwamagezi.');
INSERT INTO "african_dev" VALUES(3,59,'3:59','Mazima entondebwa ya Isa ewa Katonda efaanana nentondebwa ya Adam, yamutonda nga amugya mu ttaka oluvanyuma naamugamba nti beera n’aba omuntu.');
INSERT INTO "african_dev" VALUES(3,60,'3:60','Ago nno geemazima agava eri Mukama Katondawo, kale tobeeranga mu babuusabuusa.');
INSERT INTO "african_dev" VALUES(3,61,'3:61','Singa omuntu yenna akuwakanya kukino, oluvanyuma lwa mazima agakujjidde ggwe gamba nti mujje tuyite abaana baffe n''abaana ba mmwe, bakyala baffe ne bakyala ba mmwe, naffe fennyini nammwe olwo nno tusabe ekikolimo kya Katonda kituuke ku balimba.');
INSERT INTO "african_dev" VALUES(3,62,'3:62','Mazima bino (ebyogeddwa) g’emazima (ku bikwata ku nsonga y’okuzaalibwa kwa Isa), tewali kisinzibwa kyonna (mu mazima) okugyako Katonda omu Allah, era bulijjo Katonda ye nnantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(3,63,'3:63','Bwebava ku mazima gano mazima Katonda amanyi byonna ebifa ku bonoonyi.');
INSERT INTO "african_dev" VALUES(3,64,'3:64','(Ggwe Nabbi Muhammad) gamba nti abange mmwe abaaweebwa ekitabo mujje twegatte ku kigambo ekigatta wakati waffe nammwe, nakyo kwekuba nga tetusinza okugyako Katonda, era tuleme ku mugattako kintu kyonna, era tube nga tetufuula abamu muffe ebyokusinza netuleka Katonda. Ekyo bwebakivaako, mmwe mubagambe nti (mmwe) mujulire nti mazima ffe tuli basiramu tukkiririza mu bwomu bwa Katonda.');
INSERT INTO "african_dev" VALUES(3,65,'3:65','Abange mmwe abaaweebwa ekitabo lwaki muwakana ku bikwata ku Ibrahim so nga Taurat ne njili tebyajja okugyako oluvanyumalwe. Abaffe temutegeera.');
INSERT INTO "african_dev" VALUES(3,66,'3:66','(Mwewunyisa) okuba nga mwe mwennyini mwawakana ku kyemulinako okumanya, ate kati lwaki muwakana ku kintu kyemutalinaako kumanya! so nga Katonda amanyi ate mmwe nga temumanyi.');
INSERT INTO "african_dev" VALUES(3,67,'3:67','Ibrahim tabeerangako mu yudaaya wadde omukulisitayo wabula yali mukkiriza owa nnamaddala eyewaayo ewa Katonda, era tabeerangako mu bagatta ku Katonda bintu birala.');
INSERT INTO "african_dev" VALUES(3,68,'3:68','Mazima omuntu asinga okuba okumpi ne Ibrahim, beebo abaamugoberera, noono Nabbi n’abo Abakkiriza, bulijjo Katonda abeera munne wabakkiriza.');
INSERT INTO "african_dev" VALUES(3,69,'3:69','Ekitundu ku bantu abaaweebwa ekitabo bandyagadde okubabuza, (mu kukola ekyo) tebabuza okugyako myoyo gyabwe naye tebategeera.');
INSERT INTO "african_dev" VALUES(3,70,'3:70','Abange mmwe abaaweebwa ekitabo lwaki muwakanya ebigambo bya Katonda ate nga ddala nammwe mukakasa nti g’emazima!');
INSERT INTO "african_dev" VALUES(3,71,'3:71','Abange mmwe abaaweebwa ekitabo lwaki amazima mugatabikamu obulimba! nemukweka amazima nga nammwe mumanyidde ddala (ekituufu).');
INSERT INTO "african_dev" VALUES(3,72,'3:72','Ekitundu ku bantu abaaweebwa ekitabo baagamba nti mukkirize ebyassibwa kwabo abakkiriza kumakya ate mubiwakanye olweggulo oba olyawo ekyo kinaayambako nebakyusa.');
INSERT INTO "african_dev" VALUES(3,73,'3:73','Era temukkirizanga okugyako oyo ali ku ddiini yammwe, Ggwe Nabbi Muhammad () gamba nti: mazima obulungamu obwannamaddala bulungamu bwa Katonda, abaffe ekibakozesa ekyo lwakuba nti temwagala muntu mulala yenna kuweebwa ekiringa kyemwaweebwa (obwa Nabbi)! oba lwakutya nti bayinza okuvuganya nammwe ku lwekyo mumaaso ga Katonda (Ggwe Nabbi Muhammad () gamba nti mazima ebirungi biri mu mikono gya Katonda, abiwa oyo gwaba ayagadde, anti bulijjo Katonda mugazi nnyo era mumanyi mu buli kintu.');
INSERT INTO "african_dev" VALUES(3,74,'3:74','Ayawula gwaba ayagadde mu mmwe naamuwa okusaasira kwe era Katonda ye nannyini birungi ebingi.');
INSERT INTO "african_dev" VALUES(3,75,'3:75','Mu bantu abaaweebwa ekitabo mulimu, omuntu singa oba omwesize ku mmaali ennyingi nga atuukiriza obuvunaanyizibwa, ate mu bo mulimu omuntu singa oba omwesize ku dinar emu (busente obutono) nga tatuukiriza buvunaanyizibwa, okugyako nga omwewubyeko, ekyo nno lwakuba bo bagamba nti tetufuna musango nga tulyazaamanyizza abatali basomi (abatali abaweebwa ebitabo) ne baba nga boogera ku Katonda obulimba nga nabo bamanyi ekituufu.');
INSERT INTO "african_dev" VALUES(3,76,'3:76','Wabula (omuntu asaanira okweyita omwagalwa wa Katonda yandibadde) oyo atuukiriza byeyalagaanyisa era naatya Katonda. Mazima ddala bulijjo Katonda ayagala abamutya.');
INSERT INTO "african_dev" VALUES(3,77,'3:77','Mazima ddala abo abatunda endagaano ya Katonda nebirayiro byaabwe ku muwendo omutono, tebalina mugabo ku lunaku lw’enkomerero, era Katonda tagenda kwogera nabo era tagenda kubatunulako ku lunaku lw’enkomerero era nga bwatagenda kubatukuza era bagenda kutuukwaako ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(3,78,'3:78','Era mazima mu bo mulimu ekibinja abakyusakyusa ekitabo ne nnimi zaabwe, era mazima mulimu ekibinja kyabantu abakozesa ennimi zaabwe nebakyusa ebiri mu kitabo babalowoozese nti bya mu kitabo, so nga sibyamu. Era nebagamba nti byo byava wa Katonda so nga tebivanga wa Katonda, nebaba nga boogedde ku Katonda ebyobulimba naye nga nabo bamanyi amazima.');
INSERT INTO "african_dev" VALUES(3,79,'3:79','Tekigwanira muntu yenna okuba nga Katonda amuwa ekitabo n’okumanya ensonga, n''obwa Nabbi, ate naagamba Abantu nti mubeere baddu bange (munsinze) muve ku Katonda. (kyeyandibagambye kyandibadde nti:) Naye mubeere abasinza Katonda okusinziira ku kuba nti mmwe mubadde muyigiriza abantu abalala ekitabo, era n''olwokuba nti mubadde mukisoma (n''olwekyo mumanyi ekituufu ekyokukola).');
INSERT INTO "african_dev" VALUES(3,80,'3:80','Era tabalagira kufuula ba Malayika ne ba Nabbi ba Katonda, (okiraba nti kijja) nti okuba nti abalagira ebyobukafiiri oluvanyuma lwokuba Abasiramu.');
INSERT INTO "african_dev" VALUES(3,81,'3:81','Jjukira Katonda bweyakozesa ba Nabbi endagaano nti, lwonna lwembawanga ekitabo n’okumanya ensonga, oluvanyuma omubaka (Nabbi Muhammad () naabajjira nga ava gyendi) ng’akakasa ebyo byemulina, nti ddala mugenda kumukkiriza, era mugenda kumutaasa. Katonda naagamba nti mukkirizza, era mututte endagaano eno nti ekakase nebagamba (ba Nabbi) nti tukkirizza. Katonda naagamba nti mujulire era nange ndi wamu nammwe mu bajulizi.');
INSERT INTO "african_dev" VALUES(3,82,'3:82','Oyo yenna aliyawukana kwekyo abo nno be bonoonyi.');
INSERT INTO "african_dev" VALUES(3,83,'3:83','Abaffe abo (abonoonyi) baagala etali ddiini ya Katonda (Busiramu?) ate nga kululwe byewaayo (nebisiramuka) byonna ebiri mu ggulu omusanvu ne nsi mu kugonda ne mu kukakibwa, era nga gyali gyebiggya okuzzibwa.');
INSERT INTO "african_dev" VALUES(3,84,'3:84','Ggwe Nabbi Muhammad () gamba nti tukkiriza Katonda netukkiriza n''ebyo ebyassibwa gyetuli n''ebyo ebyassibwa ku Ibrahim ne Ismail, ne Ishak ne Yakub ne ku bazzukulube, n''ebyo ebyaweebwa Musa ne Isa n''ebyo byonna ebyassibwa ku ba Nabbi okuva ewa Katonda waabwe, tetulina Nabbi yenna gwetutakkiriza naffe twewaddeyo (mu Busiramu) eri Katonda waffe.');
INSERT INTO "african_dev" VALUES(3,85,'3:85','Oyo yenna ayagala okweteerawo eddiini etali busiramu tegenda kukkirizibwa okuva gyali, era nga yye ku lunaku lw’enkomerero agenda kubeera mubafaafaganirwa.');
INSERT INTO "african_dev" VALUES(3,86,'3:86','Katonda ayinza atya okulungamya abo abawakanya (obubaka bwa Nabbi Muhammad () oluvanyuma lwokukkiriza kwabwe nebawa n''obujulizi nti omubaka (Muhammad ()) w’amazima era nga n''obujulizi obukakasa obwa Nabbi bwe bwa baggyira, mazima Katonda talungamya bantu beeyisa bubi.');
INSERT INTO "african_dev" VALUES(3,87,'3:87','Abo nno okusasulwa kwabwe kuli nti mazima Katonda yabassaako ekikolimo n''ekya ba Malayika n’ekya bantu bonna.');
INSERT INTO "african_dev" VALUES(3,88,'3:88','(Abeeyisa obubi abo) Baakubeera mu muliro olubeerera tebagenda kukendezebwa ku bibonerezo ate tebagenda kulindirizibwa.');
INSERT INTO "african_dev" VALUES(3,89,'3:89','Okugyako abo abeenenya oluvanyuma lwokukafuwala kwabwe era nebalongoosa, anti bulijjo Katonda musonyiyi nnyo era musaasizi.');
INSERT INTO "african_dev" VALUES(3,90,'3:90','Mazima abo abaakafuwala oluvanyuma lwokukkiriza kwabwe, oluvanyuma nebeeyongera okukaafuwala, okwenenya kwabwe tekugenda kukkirizibwa, era abo be babuze abannamaddala.');
INSERT INTO "african_dev" VALUES(3,91,'3:91','Mazima abo abaakafuwala nebafiira ku bukaafiiri tewali n''omu ku bo agenda kukkirizibwa kwenunula nebwaliba aleese zaabu ajjuza ensi, abo nno balina ebibonerezo ebiruma ennyo ate tebagenda kufuna mutaasa yenna.');
INSERT INTO "african_dev" VALUES(3,92,'3:92','Temuyinza kutuuka ku ddaala lyokuyitibwa abakozi b’obulungi okugyako nga muwaddeyo kwebyo byemwagala, buli Kintu kyonna kyemuwayo mazima ddala Katonda akimanyi.');
INSERT INTO "african_dev" VALUES(3,93,'3:93','Ebyokulya byonna byali Halali eri abaana ba Israil okugyako ebyo Israil (Yakubu) byeyeeziyiza nga Taurat tennaba kussibwa, bagambe nti muleete Taurat mugisome bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(3,94,'3:94','Oyo yenna anaatemerera Katonda ebigambo ebyobulimba oluvanyuma lwekyo, abo nno be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(3,95,'3:95','Gamba nti Katonda yayogera mazima, kale mugoberere eddiini ya Ibrahim, nga takkiriza kusinza kintu kirala wadde okuba mwabo abagatta ku Katonda ekintu ekirala.');
INSERT INTO "african_dev" VALUES(3,96,'3:96','Mazima ddala e nyumba eyasooka okuterwawo abantu (olwokusinza Katonda) yeeyo eri e Mekka y’amikisa era nga kya kulungamya eri abantu bonna.');
INSERT INTO "african_dev" VALUES(3,97,'3:97','Mulimu mu yo obubonero obwenkukunala, nga ekifo kya Ibrahim. Oyo yenna agiyingira abeera mirembe, era Katonda alaalise ku bantu okulambula e nyumba eyo kwoyo aba asobodde ebyetaagisa okutuuka gyeri, naye omuntu awakanya (obukakafu bwa Hijja) mazima ddala Katonda talina kyeyeeguya ku bantu.');
INSERT INTO "african_dev" VALUES(3,98,'3:98','Gamba (ggwe Nabbi Muhammad) nti abange mmwe abaaweebwa ekitabo lwaki muwakanya ebigambo bya Katonda! era bulijjo byemukola Katonda abiraba.');
INSERT INTO "african_dev" VALUES(3,99,'3:99','Gamba (ggwe Nabbi Muhammad) nti abange mmwe abaaweebwa ekitabo lwaki muziyiza abantu okukwata ekkubo lya Katonda! nemuba nga akkiriza ekikolwakye mukitwala kuba nti kyabubuze, so nga mmwe mwennyini mumanyi ekituufu, Katonda tayinza kuba nga tafaayo kubyemukola.');
INSERT INTO "african_dev" VALUES(3,100,'3:100','Abange Mmwe Abakkiriza bwemunaagondera ekibinja mwabo abaaweebwa ekitabo bajja kubazza mu bukafiiri oluvanyuma lwokuba nga mubadde bakkiriza.');
INSERT INTO "african_dev" VALUES(3,101,'3:101','Muyinza mutya okukaafuwala nga ate musomerwa ebigambo bya Katonda era nga omubaka we ali mu mmwe, oyo yenna eyeekwata ku Katonda aba alungamiziddwa mu kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(3,102,'3:102','Abange mmwe Abakkiriza mutye Katonda mubutuufu bwokumutya era temufanga okugyako nga muli Basiramu.');
INSERT INTO "african_dev" VALUES(3,103,'3:103','Mwekwate ku muguwa gwa Katonda mwenna, temwawukananga era mujjukire ekyengera kya Katonda kyeyabawa bwemwali nga muwalaggana n''atabaganya wakati w''emitima gyammwe olwekyengerakye, nemukeesa nga muli baaluganda, mwali mutuuse ku mugo gwekinnya kyomuliro n''agubatasaako, bwatyo nno Katonda bwabannyonyola ebigambo bye mube nga mulungama.');
INSERT INTO "african_dev" VALUES(3,104,'3:104','Wateekeddwa okuba mu mmwe ekibiina (Nga abakirimu) bakubiriza Abantu okudda eri obulungi era nga bakubiriza Abantu okuyisa obulungi era nga babaziyiza okweyisa obubi era abo be b''okwesiima (ku lunaku lw’enkomerero nebawona ebibonerezo bya Katonda).');
INSERT INTO "african_dev" VALUES(3,105,'3:105','Temubeera nga abo abeetemamu nebaawukana oluvanyuma lw''obunnyonnyofu okubajjira, n’abo nno balina ebibonerezo ebyamaanyi.');
INSERT INTO "african_dev" VALUES(3,106,'3:106','Ku lunaku ebyenyi lwebiritemagana ebirala nebisiiwuuka bo abo abalisiwuuka ebyenyi byabwe (baligambibwa nti ekibatuusizza kwekyo lwakuba nti) mwakafuwala oluvanyuma lwokuba nti mwali bakkiriza, n''olwekyo mukombe ku bukaawu bwebibonerezo olwebyo byemwawakanyanga.');
INSERT INTO "african_dev" VALUES(3,107,'3:107','Bo abo ebiritemagana ebyenyi byabwe baakubeera mu kusaasira kwa Katonda (jjana) bo baakubeeramu obugenderevu.');
INSERT INTO "african_dev" VALUES(3,108,'3:108','Ebyo bigambo bya Katonda tubikusomera mu butuufu bwabyo, Katonda tayagaliza bitonde kulyazamanyizibwa.');
INSERT INTO "african_dev" VALUES(3,109,'3:109','Era bya Katonda ebiri mu ggulu omusanvu n''ebiri mu nsi era eri Katonda ebintu byonna gyebizzibwa.');
INSERT INTO "african_dev" VALUES(3,110,'3:110','Mmwe kibiina ekisinga obulungi ekyaweebwa Abantu, mukubiriza okukola empisa nemuziyiza okukola ebibi, era nga mukkiriza Katonda. singa ab''aweebwa ekitabo bakkiriza kyandibadde kirungi gyebali, (naye) abamu ku bo bakkiriza nga n’abasinga obungi bagyemu.');
INSERT INTO "african_dev" VALUES(3,111,'3:111','Tebayinza kubatuusaako buzibu, okugyako ebinyiizo ebitonotono, naye bwebaba balwanaganye nammwe babakuba amabega ne badduka ate era nga tebataasibwa.');
INSERT INTO "african_dev" VALUES(3,112,'3:112','B''ateekebwako okunyomebwa wonna webaba bali okugyako nga balina obukuumi bwa Katonda n''obwa basiramu (nebaweebwa omukisa gwokuwangalira mu nsi ya Basiramu nga bali wansi wa mateeka g’obusiraamu,) nebateekebwako obusungu bwa Katonda, nebassibwako okuswazibwa ekyo nno lwakuba nti baali bawakanya ebigambo bya Katonda nebatta ne ba Nabbi mu ngeri etali ntuufu, ekyonno (baakikola nga) olw''obujeemu bwabwe era nga baali babuuka ensalo za mateeka (ga Katonda).');
INSERT INTO "african_dev" VALUES(3,113,'3:113','Bonna tebali kimu, Mu Bantu ab''aweebwa ekitabo mulimu ekibinja ekirambulukufu, abasoma ebigambo bya Katonda, mu biseera by''ekiro era nga bavunnama.');
INSERT INTO "african_dev" VALUES(3,114,'3:114','Bakkiriza Katonda n’olunaku lw’enkomerero nebakubiriza Abantu okweyisa obulungi, nebabakomako kukweyisa obubi era nga banguwa mu kukola ebirungi era abo ba mu luse lwa balongoofu.');
INSERT INTO "african_dev" VALUES(3,115,'3:115','Buli kirungi kyonna kyebakola tekiribalyazamanyizibwa. Katonda amanyidde ddala ebifa ku bamutya.');
INSERT INTO "african_dev" VALUES(3,116,'3:116','Mazima abo abaakafuwala emmaali yaabwe nabaana baabwe tebigenda kubayamba ewa Katonda naakamu, era abo be bantu b’omumuliro bo b''akugubeeramu obugenderevu.');
INSERT INTO "african_dev" VALUES(3,117,'3:117','Byebawaayo mu bulamu buno obwensi bifaanagana nga kibuyaga alimu muzira eyakuba ebirime bya bantu abeeyisa obubi naabizikiriza, Katonda si ye yabayisa obubi naye bo bennyini be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(3,118,'3:118','Abange mmwe abakkiriza, abatali ba mu mmwe temubafuulanga ab''emikwano ab''omunda, tebayinza kulekayo kubaagaliza kabi, bulijjo babaagaliza ekyo ekibazitoowereza. Obukyayi bulabika ku mimwa gyabwe (ku nnimi zaabwe) so nga byebakweka mu bifuba byabwe byebisinga obunene, mazima tubannyonnyodde ebigambo bwe muba nga mukozesa amagezi gammwe (bibamala okutegeera ekituufu).');
INSERT INTO "african_dev" VALUES(3,119,'3:119','(Buno bwe bujulizi obulaga ensobi gyemukola) Anti mmwe mubaagala sso nga bo tebabaagala, era mmwe mukkiriza ebitabo byonna! Bwe babasisinkana bagamba nti tukkiriza naye ate bwebava wemuli babalumira ennwe olw''obusungu, gamba (ggwe Muhammad) nti mufe n’obusungu bwa mmwe. mazima Katonda amanyidde ddala ebiri mu bifuba.');
INSERT INTO "african_dev" VALUES(3,120,'3:120','Bwemutuukwako ekirungi kibanakuwaza, naye bwemugwibwako akabi (olwonno) nebasanyuka nakyo, naye singa mugumiikiriza nemutya Katonda enkwe zaabwe tezigenda kubatuusaako kabi konna, anti mazima Katonda byonna byebakola abyetoolodde.');
INSERT INTO "african_dev" VALUES(3,121,'3:121','Era jjukira (Ggwe Muhammad) bwewakeera n’oleka ab''omumaka go n’ogenda otegekere abakkiriza ebifo mwebanaalwanira, bulijjo Katonda awulira era amanyi byonna.');
INSERT INTO "african_dev" VALUES(3,122,'3:122','Jjukira ebibiina ebibiri mu mmwe lwebyayagala okufotookerera, Katonda yeeyali omukuumi wa byombi, bulijjo abakkiriza bateekwa kwesiga Katonda.');
INSERT INTO "african_dev" VALUES(3,123,'3:123','Mazima Katonda yabataasa e Badr so nga ate mwali banafu, kale mutye Katonda mulyoke mube abeebaza.');
INSERT INTO "african_dev" VALUES(3,124,'3:124','Jjukira bwewagamba abakkiriza nti tekibamala Katonda wa mmwe okubadduukirira naabassiza ba Malayika enkumi ssatu.');
INSERT INTO "african_dev" VALUES(3,125,'3:125','So nno, bwemunagumiikiriza nemutya Katonda, ne babajjira mu bwangu obwo, ajja kubadduukirira ne ba Malayika enkumi ttaano abalambe nga basibye ebiremba.');
INSERT INTO "african_dev" VALUES(3,126,'3:126','Ekyo Katonda teyakibawa wabula lwakubasanyusa, era emitima gyammwe gibe nga ginyweera nakyo, naye nga bulijjo okutaasa tekuva okugyako ewa Katonda nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(3,127,'3:127','(Ekyo Katonda yakikola) abe nga ajjawo ekitundu ekimu ekya baakafiiri oba abanakuwaze (Bawangulwe) baddeyo nga baswavu.');
INSERT INTO "african_dev" VALUES(3,128,'3:128','Tekiri mu busobozi bo (ggwe Muhammad), Katonda okubasonyiwa oba okubabonereza, anti mazima bo beeyisa bubi.');
INSERT INTO "african_dev" VALUES(3,129,'3:129','Byonna ebiri mu ggulu omusanvu ne mu nsi bya Katonda. Asonyiwa gwaba ayagadde naabonereza gwaba ayagadde, bulijjo Katonda musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(3,130,'3:130','Abange Abakkiriza temulyanga Riba ekisobola okubatuusa okubazaamu emirundi n’emirundi, bulijjo mutye Katonda kibawe okulokoka ku lunaku lw’enkomerero.');
INSERT INTO "african_dev" VALUES(3,131,'3:131','Era mutye omuliro ogwo ogwateekerwateekerwa Abakafiiri.');
INSERT INTO "african_dev" VALUES(3,132,'3:132','Era mugondere Katonda n''omubaka olwo nno musaasirwe.');
INSERT INTO "african_dev" VALUES(3,133,'3:133','Era mwanguwe nga munoonya okusonyiwa kwa Katonda wa mmwe, ne jjana eyo obugazi bwayo obwenkana eggulu omusanvu ne nsi, eyateekerwateekerwa abatya Katonda.');
INSERT INTO "african_dev" VALUES(3,134,'3:134','Abo be bawaayo mu kkubo lya Katonda, mu biseera ebyessanyu ne by''obuzibu, era ababikka ku busungu, abasonyiwa abantu. bulijjo Katonda ayagala abakozi be birungi.');
INSERT INTO "african_dev" VALUES(3,135,'3:135','Era abo bwebaba bakoze ebyobuwemu oba nebaba nga beeyisizza bubi (mu ngeri endala) bajjukira Katonda nebasaba okusonyiyibwa ebyonoono byabwe ate ani yandisonyiye ebyonoono okugyako Katonda! era nebatalemera kw’ebyo byebaakola ate nga nabo bamanyi ekituufu.');
INSERT INTO "african_dev" VALUES(3,136,'3:136','Abo empeera yaabwe kisonyiwo okuva ewa Mukama Katonda waabwe ne jjana ezikulukutiramu emigga, nga bagenda kuzibeeramu bugenderevu, nga eriba mpera nnungi eri abo abakola emirimu emirongoofu.');
INSERT INTO "african_dev" VALUES(3,137,'3:137','Bwebityo ebintu bwebibadde bitambula oluberyeberye lwa mmwe, kale mutambule mu nsi mutunule, mujja kulaba engeri enkomerero yaabalimbisa bweba.');
INSERT INTO "african_dev" VALUES(3,138,'3:138','(Kur''an eno) kwe kunnyonnyola okuweereddwa eri Abantu era bulungamu era kwe kubuulirira eri abatya Katonda.');
INSERT INTO "african_dev" VALUES(3,139,'3:139','Temuterebukanga era temunakuwalanga, ng’ate mmwe muli ku ngulu kavuna muba nga muli bakkiriza abannamaddala.');
INSERT INTO "african_dev" VALUES(3,140,'3:140','Bwe muba mutuukiddwako obuvune (mu lutalo lwe Uhud) nabali baatuukwako obuvune ng’obwo (mu lutalo lwe Badr) nabwekityo bulijjo ennaku tuzikyusakyusa wakati wa bantu (olumu lw’ono, olundi lwa mulala) olwo nno Katonda ayawule abakkiriza, mu ngeri yeemu ajje mu mmwe abafiira mu kulwanirira eddiini era bulijjo Katonda tayagala beeyisa bubi.');
INSERT INTO "african_dev" VALUES(3,141,'3:141','Era Katonda alyoke asengejje ayawulemu abo Abakkiriza, era azikirize Abakafiiri.');
INSERT INTO "african_dev" VALUES(3,142,'3:142','Oba mulowooza nti muyinza okuyingira e jjana nga Katonda tannaba kwawula abo abaalwana mu kkubo lye mu mmwe, era naayawula na bagumiikiriza.');
INSERT INTO "african_dev" VALUES(3,143,'3:143','Mwalinga mwegomba okufa nga temunnaba kusisinkana (Mu lutalo lwe Uhud) naye mubutuufu kwabatuukako nga mulabira ddala.');
INSERT INTO "african_dev" VALUES(3,144,'3:144','Muhammad tali okugyako okuba nti Mubaka era nga Ababaka bonna abaamukulembera, kaakano bwaba afudde oba nattibwa olwo nga mukyukira ku bisinziiro byammwe (nemuva mu Busiraamu). Oyo yenna anaakyukira ku bisinziirobye talina kabi konna kaatuusa ku Katonda, era Katonda ajja kusasula abo abeebaza.');
INSERT INTO "african_dev" VALUES(3,145,'3:145','Era tewali muntu yenna afa okugyako nga Katonda yakkirizza, ekyo nga kiwandiike ekyagererwa ekiseera kyakyo, oyo yenna ayagala empera z’ensi tuzimuwa mu yo, n’oyo ayagala empeera z’enkomerero tulizimuwa ku nkomerero, era tujja kusasula abeebaza.');
INSERT INTO "african_dev" VALUES(3,146,'3:146','Ba Nabbi bangi, ebibinja bya bagoberezi baabwe bingi byabanga nabo mu kulwana, tebaaterebukanga olw''ebizibu ebyabatuukangako mu kulwana mu kkubo lya Katonda tebanafuwa wadde okwewaayo eri omulabe bulijjo Katonda ayagala abagumiikiriza.');
INSERT INTO "african_dev" VALUES(3,147,'3:147','Ekigambo kyabwe tekyali, okugyako okugamba nti Ayi Mukama omulabirizi waffe tusonyiwe ebyonoono byaffe, era otusonyiwe bwetuba tususse ekkomo mu nsonga zeddiini yaffe, era otunyweze mu kuyimirira kwaffe ku nsonga, era otutaase mu kibiina ku Bakafiiri.');
INSERT INTO "african_dev" VALUES(3,148,'3:148','Katonda n’abawa empeera z’okunsi, era nempeera ezisinga okuba ennungi ku lunaku lw’enkomerero, era bulijjo Katonda ayagala abakozi b’obulungi.');
INSERT INTO "african_dev" VALUES(3,149,'3:149','Abange mmwe Abakkiriza bwemunaagondera abo Abaakafuwala bajja kubazzaayo ekyennyumannyuma mukyuke okuva mu bukkiriza, mubeere abafaafaganiddwa.');
INSERT INTO "african_dev" VALUES(3,150,'3:150','Wabula Katonda yemukuumi wa mmwe era bulijjo yaasinga abataasa bonna');
INSERT INTO "african_dev" VALUES(3,151,'3:151','Tujja kussa okutya ku mitima gy’abo abaakafuwala kulw’okugatta kwabwe ku Katonda ebintu ebirala ebyo byatassaako bujulizi. Era omuliro bwebuddo bwabwe, era bulijjo obuddo bw’abeeyisa obubi buba bubi.');
INSERT INTO "african_dev" VALUES(3,152,'3:152','Mazima Katonda yatuukiriza gyemuli endagaanoye bwemwatuuka okubatirimbula olwokukkirizakwe, mpozzi bwemwatiitiira ne mukaayagana nga temukyakkaanya ku nsonga, ne mujeema (nemuva ku kasozi) bwemwalaba nti kyemwagala (ekyokuwangula omulabe) kyali kituukirira, mu mmwe mwalimu abaagala ebyokufuna bye nsi, nga bwemwalimu abagala enkomerero, oluvanyuma Katonda yaakyusa obwali obuwanguzi bwa mmwe ku bo (nebaba nga beebawangula) alyoke abe nga abagezesa ate mazima yabasonyiwa mmwe, bulijjo Katonda abakkiriza abagonnomolako ebirungi.');
INSERT INTO "african_dev" VALUES(3,153,'3:153','Jjukira ekiseera wemwayambukira olusozi, olwonno nga temukyatunula ku muntu yenna. Nga omubaka mumulese mabega abakowoola, awo nno Katonda kwekubasasula okweraliikirira okwomuddinganwa mube nga temunakuwala olwebyo byemutaafuna, oba olwobuzibu obubatuseeko Katonda amanyidde ddala byonna byemukola.');
INSERT INTO "african_dev" VALUES(3,154,'3:154','Ate oluvanyuma lwokwelariikirira Katonda yassa kummwe akalembereza kokwebakamu okwakwata abamu ku mmwe, ate abalala batiirira nnyo obulamu bwabwe nga balowooleza Katonda ebitali bituufu endowooza ya Jahiliyyah, nga bagamba nti tukyalina obusobozi mu nsonga eno? Gamba nti ebintu byonna biri mu mikono gya Katonda, (Abantu abo) bakweka mu mitima gyaabwe byebatakulaga era nga bagamba nti singa tubadde tulina okusalawo ku nsonga eno, tetwandittiddwa wano, Bagambe nti nebwemwandibadde mu mayumba gammwe ab''awandiikwako okufa bandigudde mu bifo ebyabawandiikibwako okufiiramu, olwonno Katonda alyoke agezese ebiri mu bifuba byammwe, era asengejje ebiri mu mitima gyammwe, bulijjo Katonda amanyi ebiri mu bifuba.');
INSERT INTO "african_dev" VALUES(3,155,'3:155','Mazima abo abeekyusa mu mmwe olunaku enjuyi ebbiri lwezaasisinkana, mazima Sitane yeeyabaseereza olwebimu kw’ebyo byebaakola era mazima Katonda yabasonyiwa anti mazima ddala Katonda musonyiyi w’akisa.');
INSERT INTO "african_dev" VALUES(3,156,'3:156','Abange mmwe abakkiriza temubeera ng’abo abaakafuwala nebagamba bannaabwe bwebaabanga batambudde mu nsi, oba nga bagenze ku lutalo nti singa baasigala wano naffe tebandifudde wadde okuttibwa, (baafuna endowooza eyo) Katonda alyoke akifuule ekyokwejjusa mu mitima gyabwe naye nga bulijjo Katonda y’awa obulamu era yatta era buli kyonna kyemukola Katonda akiraba.');
INSERT INTO "african_dev" VALUES(3,157,'3:157','Singa muttibwa nga muli mukuweereza mu kkubo lya Katonda, oba nemufa (enfa eyaabulijjo, mu ngeri zombi) okusonyiwa okuva eri Katonda n’okusaasira by’ebirungi okusinga ebyo byebakunganya.');
INSERT INTO "african_dev" VALUES(3,158,'3:158','(Kyekimu) okuba nti mufudde oba muttiddwa ewa Katonda yekka gyemulikunganyiizibwa.');
INSERT INTO "african_dev" VALUES(3,159,'3:159','Kulwokusaasira kwa Katonda wabagondera (Gwe Muhammad) naye singa wali waboggo omuzito w''omutima bandikudduseeko, kale basonyiwe, obeegayiririre (ewa Katonda) obebuuzengako mu nsonga zonna wabula bwobanga omaliridde (n’osalawo ekyokukola) olwo nno weekwase Katonda, mazima Katonda ayagala abamwekwasa.');
INSERT INTO "african_dev" VALUES(3,160,'3:160','Katonda bwabataasa teri ayinza kubawangula naye singa aba abavuddemu ani oyo ayinza okubataasa atali yye, bulijjo Abakkiriza bateekwa okwessa mu mikono gya Katonda mu nsonga zonna.');
INSERT INTO "african_dev" VALUES(3,161,'3:161','Tekisoboka ku Nabbi yenna okuba nga akumpanya eminyago naye omuntu yenna bwakumpanya ku minyago, agenda kujja ku lunaku lw’enkomerero n’ebyo byeyakumpanya oluvanyuma omuntu agenda kusasulwa mu bujjuvu ekyo kyeyakola, nabo tebaliyisibwa bubi.');
INSERT INTO "african_dev" VALUES(3,162,'3:162','Abaffe, oyo agoberera okusiima kwa Katonda ayinza okufaanana nooyo avuddeyo n’okusunguwalirwa Katonda! nga n’obuddo bwe muliro Jahannama ate nga buddo bubi ddala!');
INSERT INTO "african_dev" VALUES(3,163,'3:163','Abantu abo balina ebifo byanjawulo ewa Katonda, anti era bulijjo Katonda alabira ddala byebakola.');
INSERT INTO "african_dev" VALUES(3,164,'3:164','Mazima Katonda yawa Abakkiriza ekyengera bweyabatumamu omubaka nga ava mu bo, nga abasomera ebigambo by’a Katonda, era nga abatukuza era nga abayigiriza ekitabo (Kur’ani) n’ebigambo ebyamagezi (Hadith) newaakubadde nga mu kusooka baali mu bubuze obweyolefu.');
INSERT INTO "african_dev" VALUES(3,165,'3:165','Lwaki bwemwatuukwako omuswiba (mu Uhudi) nga ate bo mwabatusaako omuswiba ogwenkana ogwo emirundi ebiri (mu Badr) mwagamba nti ate lwaki kizze bwekityo, bagambe (gwe Nabbi Muhammad ()) ekyo kivudde ku mmwe mwennyini mazima Katonda bulijjo muyinza wa buli kintu .');
INSERT INTO "african_dev" VALUES(3,166,'3:166','N’ekyo ekyabatuukako olunaku ebibinja ebibiri lwebyasisinkana kyaliwo ku lwa kukkiriza kwa Katonda, era abe nga ayawula abakkiriza ab''amazima.');
INSERT INTO "african_dev" VALUES(3,167,'3:167','Mu ngeri yeemu ayawule abo abanafunsi era abaagambibwa nti mujje mulwane mukuweereza mu kkubo lya Katonda, oba waakiri mujje twetaase tusindiikirize omulabe nebaddamu nti, singa tubadde tukakasa nti waliyo okulwana twaligenze nammwe, ku lunaku olwo nga bali kumpi nnyo n’obukafiiri okusinga okubeera n’obukkiriza, boogera nemimwa gyaabwe ebitava mu mitima gyabwe, ate nga bulijjo Katonda amanyi nnyo byebakweka.');
INSERT INTO "african_dev" VALUES(3,168,'3:168','Abo (Abananfusi) abagamba bannaabwe (ab’agenda ku lutalo) bo nebasigala nga b''etuulidde eka nti singa baatuwuliriza tebandittiddwa. Bagambe (Gwe Muhammad) nti kale mwewonye okufa bwe muba nga muli b''amazima mu byemwogera.');
INSERT INTO "african_dev" VALUES(3,169,'3:169','Tolowoozanga nti abo abattirwa ku kuweereza mu kkubo lya Katonda nti bafu, nedda wabula balamu, era nga bagabirirwa ewa Mukama Katonda waabwe.');
INSERT INTO "african_dev" VALUES(3,170,'3:170','Nga bajaganya olw’ebyo Katonda byabawa mubigabwabye, era nga basanyuka olwa bannaabwe bebaleka ku nsi nti tebatuukiddwako kutya, era nga bwebatagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(3,171,'3:171','Nga basanyuka olwebyengera, n’obulungi obungi ebivudde ewa Katonda, era mazima ddala Katonda bulijjo talina bwatayinza kusasula mpeera z’abakkiriza.');
INSERT INTO "african_dev" VALUES(3,172,'3:172','Abo abaayanukula (okukowoola kwa)Katonda n''omubaka (nebakkiriza okuddayo okulwana) oluvanyuma lwokutuukibwako obulumi bwolutalo, abeeyisa obulungi mu bo nebatya Katonda bagenda kufuna empeera nnyingi.');
INSERT INTO "african_dev" VALUES(3,173,'3:173','Abo abantu (Abagatta Katonda ne bintu ebirala) bebaagamba nti mazima (Abantu bemwalwanye nabo) babeekobaanidde baagala kubaddamu nvuunula bibya musaanye mubeegendereze (ekintu ekyandibaleetedde okutya) naye ate kyabongera bukkiriza, era nebagamba nti Katonda atumala era yemukuumi asinga obulungi.');
INSERT INTO "african_dev" VALUES(3,174,'3:174','(Omulabe yabadduka awatali kulwana kwonna) ate nebadda ewaboobwe nga bafunye ebyengera okuva ewa Katonda n''obulungi obungi awatali kutuukwako kabi konna, (mukukola ebyo byonna) baali bayayaanira kusiima kwa Katonda, anti bulijjo Katonda alina obulungi bungi.');
INSERT INTO "african_dev" VALUES(3,175,'3:175','Eyali ababuzaabuza obutagenda kulwana) mazima ddala oyo ye Sitane bulijjo abatiisa mikwano gye, kale nno temubatya wabula mutye nze bwe muba muli bakkiriza abannamaddala.');
INSERT INTO "african_dev" VALUES(3,176,'3:176','Era tebakunakuwaza abo abanguwa okuyingira obukafiiri, mazima bo tebayinza kutuusa ku Katonda kabi konna, Katonda ayagala babe nga tebaba namugabo gwonna ku lunaku lw’enkomerero era balina ebibonerezo ebyamaanyi.');
INSERT INTO "african_dev" VALUES(3,177,'3:177','Mazima abo abawaanyisa obukafiiri mu bukkiriza tebagenda kutuusa kabi ku Katonda mu kintu kyonna, era balina ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(3,178,'3:178','Era abo abakaafuwala tebalowooza nti mazima ffe okubalindiriza kirungi gyebali, mazima tubalindiriza b’eyongere okukola ebibi, era bagenda kussibwako ebibonerezo ebinyomesa.');
INSERT INTO "african_dev" VALUES(3,179,'3:179','Katonda teyali w''akuleka bakkiriza kw’ekyo kyemuliko okugyako ngamaze okwawula ababi mu balungi, era Katonda teyali waakubalaga ebyo ebitalabika, naye mazima ddala Katonda alonda mu babaka be oyo gwaba ayagadde, kale mukkirize Katonda nababakabe, ate singa mukkiriza nemutya (Katonda) mujja kufuna empeera enzito.');
INSERT INTO "african_dev" VALUES(3,180,'3:180','Abo abaakodowalira ebyo Katonda byeyababawa mu bigabwabye, tebalowoozanga nti ekyo kirungi gyebali, wabula kyo kibi gyebali ku lunaku lw’enkomerero bagenda kwambazibwa mu bulago ebyo byebaakodowaliranga, bya Katonda ebiri mu ggulu omusanvu ne mu nsi era bulijjo Katonda amanyi mu bujjuvu byonna byemukola.');
INSERT INTO "african_dev" VALUES(3,181,'3:181','Mazima Katonda awulidde ekigambo kyabo abagamba nti mazima Katonda mwavu era nga ffe bagagga. Tujja kuwandiika ebyo byeboogera nga bwetujja okuwandiika okutta kwabwe ba Nabbi awatali nsonga, era tugambe nti mukombe ku bibonerezo by’omuliro.');
INSERT INTO "african_dev" VALUES(3,182,'3:182','(Ekyo kigenda kubatuukako) olwebyo emikono gyamwe byegyakola, era mazima ddala Katonda tayisa bubi baddube.');
INSERT INTO "african_dev" VALUES(3,183,'3:183','Era beebo abagamba nti mazima Katonda yatulagaanyisa okuba nga tetukkiriza mubaka yenna okugyako nga atuleetedde Saddaaka omuliro negugyokya, bagambe (Ggwe Nabbi Muhammad ()) ababaka bangi abaabajjira oluberyeberye lwange nga baleese neebyo byemugamba, lwaki ate mwabatta bwe muba nga muli b''amazima mukino kyemugamba.');
INSERT INTO "african_dev" VALUES(3,184,'3:184','(Ggwe Muhammad tobafaako) bwebakulimbisa ababaka bangi abaakulembera abaalimbisibwa, baaleeta obujulizi obwenkukunala n''ebiwandiiko n''ebitabo ebijjudde ekitangaala.');
INSERT INTO "african_dev" VALUES(3,185,'3:185','Buli muntu yenna wa kukomba ku kufa, era mazima ddala mugenda kusasulwa mu bujjuvu empeera zammwe ku lunaku lw’enkomerero oyo yenna aliwonyezebwa omuliro naayingizibwa e jjana aliba yeesiimye, obulamu bwensi tebuliimu kantu konna okugyako okweyagala okwokugayaaza.');
INSERT INTO "african_dev" VALUES(3,186,'3:186','Mujja kugezesebwa nnyo mu mmaali zammwe nemubulamu bwa mmwe, era mujja kutuukibwako ebitasanyusa bingi okuva mwabo ab''aweebwa ekitabo oluberyeberye, n''okuva mwabo abagatta ku Katonda ebintu ebirala, naye singa mugumiikiriza nemutya Katonda mazima ekyo kyekimu ku bintu ebikulu.');
INSERT INTO "african_dev" VALUES(3,187,'3:187','Era jjukira ekiseera Katonda bweyakozesa endagaano abo ab''aweebwa ekitabo nti mugenda kukinnyonnyola abantu era temugenda kukikweka wabula (endagaano) baagisuula emabega waabwe nebagiwaanyisaamu eby’okufuna ebitono, bibi nnyo byebaagula.');
INSERT INTO "african_dev" VALUES(3,188,'3:188','Tosuubiriranga abo abasanyuka olwebyo byebawaddeyo era nga baagala okutenderezebwa olwebyo byebaba tebakoze, tolowoolezanga ddala nti bagenda kuwona ebibonerezo, era bagenda kussibwako ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(3,189,'3:189','Obufuzi bwe ggulu ne nsi bwa Katonda, era Katonda muyinza ku buli kintu kyonna.');
INSERT INTO "african_dev" VALUES(3,190,'3:190','Mazima mu kutonda eggulu omusanvu ne nsi nokwawukana kwekiro ne misana ddala bubonero eri abo abalina amagezi amalamu.');
INSERT INTO "african_dev" VALUES(3,191,'3:191','Abo abatendereza Katonda nga bayimiridde oba nga batudde, oba nga bagalamidde, nebafumiitiriza mu ngeri eggulu omusanvu ne nsi gyebyatondebwamu, ne bagamba nti Ayi Mukama Katonda waffe ebintu bino tewabitonda mu bukyamu, wasukkuluma ku bintu byonna, n’olwekyo tukusaba otukingirize ebibonerezo byomuliro.');
INSERT INTO "african_dev" VALUES(3,192,'3:192','Ayi mukama Katonda waffe, mazima ggwe omuntu gwoliyingiza omuliro oliba omukkakkanyizza. Abeeyisa obubi tebagenda kufuna mutaasa yenna.');
INSERT INTO "african_dev" VALUES(3,193,'3:193','Ayi Mukama Katonda waffe tuwulidde omukoowooze ayita Abantu okujja eri obukkiriza nga agamba nti mukkirize Mukama Katonda wa mmwe, naffe netukkiriza, Ayi Mukama Katonda waffe tukusaba otusonyiwe ebibi byaffe, otusanguleko ebyonoono byaffe, otuuse okututta nga tuli mu luse lwa baddubo abalongoofu.');
INSERT INTO "african_dev" VALUES(3,194,'3:194','Ayi Mukama omulabirizi waffe tukusaba otuwe byewatulagaanyisa nga biyita ku babaka bo, totufuulanga abanyomebwa ku lunaku lw’enkomerero, mazima ddala ggwe toyawukana ku ndagaano.');
INSERT INTO "african_dev" VALUES(3,195,'3:195','Mukama omulabirizi waabwe kwekubaanukula nti, siyinza butasasula mulimu omu ku mmwe gwaba akoze kaabe musajja oba mukazi, mwenna muli kyekimu (anti muli basiraamu). Abonno abaasenguka nebagobaganyizibwa mu mayumba gaabwe, nebabonyabonyezebwa ku lwange, ne balwanagana n''omulabe kulwange, ne balwanagana n’omulabe ne battibwa nja kubajjirako ddala ebibi byaabwe era nja kubayingiriza ddala e jjana ezikulukutiramu emigga, eyonno nga mpeera okuva ewa Katonda, era bulijjo ewa Katonda yeeri empeera ennungi.');
INSERT INTO "african_dev" VALUES(3,196,'3:196','Abakafiiri okwegiriisiza mu nsi tekikuyigulanga ttama.');
INSERT INTO "african_dev" VALUES(3,197,'3:197','Ebyo byebalimu byakweyagala kutono, oluvanyuma obuddo bwaabwe muliro Jahannama, era obwo buddo bubi.');
INSERT INTO "african_dev" VALUES(3,198,'3:198','Naye mazima abo abatya mukama Katonda waabwe baakufuna e jjana ezikulukutiramu emigga, baakuzibeeramu obugenderevu nga bibanja ebyolubeerera ebiribaweebwa Katonda. Ebiri ewa Katonda byebirungi eri abaddu abalongoofu.');
INSERT INTO "african_dev" VALUES(3,199,'3:199','Mazima ddala mu Bantu ab''aweebwa ekitabo mulimu abantu abakkiriza Katonda nebakkiriza ebyassibwa gyemuli, n’ebyassibwa gyebali, bagondera Katonda ebigambo bya Katonda tebabitundamu muwendo mutono, abo balina okufuna empeera yaabwe ewa Mukama omulabirizi waabwe, mazima Katonda tagenda kukaluubirirwa kubala.');
INSERT INTO "african_dev" VALUES(3,200,'3:200','Abange mmwe abakkiriza mugumiikirize (wabula mukimanye nti n’omulabe gwe mulwana naye agumiikiriza) n’olwekyo muvuganye naye mu kugumiikiriza (mulabe nga mumuwangula nemwekyo kibasobozese okuwangula olutalo) era munyweere nga muli mu lutalo era mutye Katonda mulyoke mubeere abalyesiima ku lunaku lw’enkomerero.');
INSERT INTO "african_dev" VALUES(4,1,'4:1','Abange mmwe abantu mutye Mukama Katonda wa mmwe oyo eyabatonda nga abaggya mu muntu omu, (Adam) ate n’atonda nga agya mu ye mukyalawe (Haawa) ate mu bombi nagyamu abasajja n’abakyala bangi, era mutye Katonda oyo gwemukozesa erinnya lye nga musaba, era mutye enganda (muzikuume temuzikutula), mazima Katonda buli kiseera abataddeko eriiso egyogi.');
INSERT INTO "african_dev" VALUES(4,2,'4:2','(Bwe mukitegeera nti abataddeko eriiso gyogi kale) ne bamulekwa mubawe emmaali yaabwe, ennungi temugiwaanyisangamu embi, era emmaali yaabwe temugiryanga nga mugigatta mu yammwe, mazima ddala okukola ekyo kibi kinene.');
INSERT INTO "african_dev" VALUES(4,3,'4:3','Bwemutyanga obutakola bwenkanya eri bamulekwa (Abawala bemwasigaza okulabirira temubawasa) olwonno muwase abakyala abalala abo ababa babasanyusizza owa babiri, owa basatu, n’owabana, wabula bwemutyanga obutakola bwenkanya, kale omu, oba oyo omuzaana yenna gwemuba mulina mu mikono gyammwe, ekyo kyekirungi okuwona obuzibu bwokwekubiira.');
INSERT INTO "african_dev" VALUES(4,4,'4:4','Kikakafu muwe Abakyala Amahare gaabwe, naye bwebaba nga bbo babasonyiye ekintu kyonna ku Mahare mu mwoyo omulungi, ekyo mukirye nga mweyagala, nga muli mirembe.');
INSERT INTO "african_dev" VALUES(4,5,'4:5','Abo abatasobola kwemalirira ku kukozesa mmaali, temubawanga emmaali yammwe, eyo Katonda gyeyabateerawo nga yeetambuza obulamu bwa mmwe, era mubawenga ku yo gyebeetaaga ku byokulya era mubambazenga, era mwogerenga nabo ebigambo ebirungi.');
INSERT INTO "african_dev" VALUES(4,6,'4:6','Mugezese ba mulekwa (mpola mpola) bwebatuuka mu kiseera ekyokuwasa (oba okufumbirwa) bwe mubalabamu obusobozi (bwo kulabirira ebyabwe) olwonno mubawe emmaali yaabwe. (Mu kiseera wemubeerera nga mugibakuumira) temugiryanga mu ngeri ey’okudiibuuda, era n’okugirya eggweewo mangu nga tebannakula. Oyo anaabanga omugagga asaana yesseemu ensa (agyesonyiwe) oyo anaabanga omwavu alyengako mu ngeri y’akintu kiramu. Bwe mutuusanga okubawa emmaali yaabwe muleetengawo abajulizi, kimala okubeera nti Katonda ye mubazi.');
INSERT INTO "african_dev" VALUES(4,7,'4:7','Abaana ab’obulenzi balina omugabo gwebafuna mwebyo bakadde baabwe bombi byebalese, n’aboluganda byebaba balese, n''abaana ab''obuwala balina omugabo mwebyo bakadde baabwe bombi n''aboluganda byebaba balese, kabibe bitono oba bingi, (buli omu afuna) omugabo omugereke.');
INSERT INTO "african_dev" VALUES(4,8,'4:8','Ab’oluganda ab''okumpi ne bamulekwa n’abanaku bwebabaawo nga emmaali y’omufu egabwa mubagabirenga ko, era mubagambe ebigambo ebirungi.');
INSERT INTO "african_dev" VALUES(4,9,'4:9','(Abakuza) bateekwa okutya nti singa baba balese emabega waabwe abaana abato, nga baalibeeraliikirizza, bateekwa okutya Katonda, era boogere ebigambo ebituufu.');
INSERT INTO "african_dev" VALUES(4,10,'4:10','Mazima abo abalya emmaali y’abamulekwa mu ngeri enkyamu, mazima mu mbuto zaabwe balya muliro mwereere, era bagenda kuyingira omuliro oguyitibwa Saer.');
INSERT INTO "african_dev" VALUES(4,11,'4:11','Katonda abakalaatira ku baana ba mmwe, nga omwana omulenzi afuna emigabo gya bawala babiri, bwebaba nga baana bawala nebasukka ku babiri basikira bibiri bya kusatu (2/3 )by’emmaali omufu gyaba alese, bwaba muwala omu afuna kimu kya kubiri (1/2) olwonno bazadde b’omufu, buli omu kubo naafuna kimu kya mukaaga (1/6) ku mmaali gyaba alese bwaba nga aleseewo omwana ow’obulenzi, bwaba nga talina mwana w’abulenzi, abazadde b’ombiriri nebamusikira maamawe aba atwala kimu kya kusatu (1/3), bwaba aleseewo bagandabe olwo maamawe afuna kimu kya mukaaga (1/6), ebyo byonna bikolebwa oluvanyuma lwokutuukiriza ekiraamo kye yalaama oba ebbanja, bazadde ba mmwe n’abaana ba mmwe temumanyi ani kubonna asinga okuba ow’omugaso gyemuli, eno y’engaba ya Katonda (gye muteekeddwa okugoberera) anti bulijjo mazima ddala Katonda amanyi nnyo ate nga buli kintu akikola kusinziira ku nsonga.');
INSERT INTO "african_dev" VALUES(4,12,'4:12','(Mmwe Abasajja) mufuna kimu kya kubiri (1/2) mwebyo bakyala ba mmwe byebaba balese bwebaba nga bafudde tebalese mwana (oba abazzukulu,) bwebaba balese omwana, olwo nno mufuna kimu kyakuna (1/4) mwebyo byebaba balese, (nga bino bikolebwa (oluvanyuma lwe kiraamo kyebaba balaamye oba ebbanja. Nabo abakyala bafuna kimu kya kuna (1/4) kwebyo byemuba mulese bwe mubanga temulina mwana gwemulese. Bwe muba mulina omwana olwonno bafuna kimu kya munaana (1/8) kwebyo byemuba mulese, (Bino bikolebwa) oluvanyuma lwokutuukiriza ekiraamo kyemuba mulaamye oba ebbanja, omusajja bwaba asikirwa nga talese baana wadde bazadde, naye nga alina mugandawe oba mwannyina (gwalese) buli omu ku bombi afuna kimu kya mukaaga (1/6) bwebaba nga basukkawo olwo bonna beegattira mu kimu kyakusatu (1/3), (kino kikolebwa) oluvanyuma lwokutuukiriza ekiraamo ekiba kirekeddwa oba ebbanja, ekitaliimu kunyigiriza (eri abaserikale) bino byonna nga kiraamo gyemuli okuva eri Katonda wa mmwe, era bulijjo Katonda amanyi nnyo waakisa.');
INSERT INTO "african_dev" VALUES(4,13,'4:13','Ago mateeka ga Katonda, omuntu yenna agondera Katonda n’omubakawe alimuyingiza e jjana ezikulukutiramu emigga, baakuzibeeramu obugenderevu, era okwo kwekwesiima okusukkulumu.');
INSERT INTO "african_dev" VALUES(4,14,'4:14','N’oyo ajeemera Katonda n''omubakawe, naamenya amateekage agenda ku muyingiza omuliro, nga waakugubeeramu obugenderevu, era wa kutuusibwako ebibonerezo ebinyoomesa.');
INSERT INTO "african_dev" VALUES(4,15,'4:15','Naabo abakyala ba mmwe ababa bakoze eby’obwenzi mubateekeko abajulizi bana (4) nga bava mu mmwe, bwebabawaako obujulizi olwonno abakyala abo mubasibire mu mayumba okutuusa okufa lwekulibatuukako, oba Katonda naabateerawo enkola endala.');
INSERT INTO "african_dev" VALUES(4,16,'4:16','Naabo bombi mu mmwe ababa bakikoze mubabonerezenga, naye bwebeenenya nebeeyisa obulungi mubaleke, mazima Katonda akkiriza okwenenya musaasizi.');
INSERT INTO "african_dev" VALUES(4,17,'4:17','Mazima okwenenya okukkirizibwa ewa Katonda kwekwabantu abo abakola ebibi mu butamanya oluvanyuma nebeenenya mangu ddala abonno Katonda akkiriza okwenenya kwabwe, anti bulijjo Katonda amanyi nnyo era agoba nsonga.');
INSERT INTO "african_dev" VALUES(4,18,'4:18','Okwenenya tekukkirizibwa nga kuva mwabo abakola ebibi okutuusa omu ku bo okufa lwekumusemberera naagamba nti kaakati neenenyezza, wadde abo abafa nga bakafiiri, abo (bonna) twabategekera ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(4,19,'4:19','Abange mmwe abakkiriza temukkirizibwa kusikira bakyala lwampaka, era temubanyigirizanga mube nga mutwala ebimu kwebyo byemwabawa, mpozzi nga bakoze obwenzi obweyolefu, (bwekiba ekyo) mukolagane nabo mu ngeri ennungi, bwe muba temukyabaagala, muyinza okutamwa ekintu ate Katonda naakissaamu ebirungi bingi.');
INSERT INTO "african_dev" VALUES(4,20,'4:20','Singa mwagadde okuwaanyisa omukyala mu kifo kyomukyala omulala, omu ku bo nebwe muba nga mwamuwa endulundu ye mmaali, temubaggyangako kintu kyonna ku yo, musobole okugitwala mu ngeri yobulimba era nga kivve ekyeyolefu!!');
INSERT INTO "african_dev" VALUES(4,21,'4:21','Ate mugitwala mutya nga buli omu ku mmwe yamala dda okutuuka eri munne! (Abakyala abo) nebaggya ku mmwe endagaano ennyweevu.');
INSERT INTO "african_dev" VALUES(4,22,'4:22','Temuwasanga abakyala ba kitammwe bebaawasa, okugyako ebyo byemwakola emabega (nga temunnamanya tteeka lino) mazima ekikolwa ekyo kya buwemu, ekisunguwaza Katonda, era nga nkola mbi.');
INSERT INTO "african_dev" VALUES(4,23,'4:23','Temukkirizibwa (kiri haramu) okuwasa ba maama ba mmwe, ne bawala ba mmwe ne bannyina mmwe, neba senga mmwe, ne baganda ba bamaama ba mmwe, ne bawala ba baganda ba mmwe, ne bawala ba bannyinammwe, ne ba maama ba mmwe abo abaabayonsa, nebannyinammwe bemwayonka ku bbeere erimu, ne bamaama ba bakyala ba mmwe n’abaana abajja ne bannyabwe abo ababa bakulidde mu maka gammwe, nga bazaalibwa bakyala ba mmwe abo bemwalabagana nabo, bwe muba temwalabaganako nabo, temulina mutawaana (era temukkirizibwa) baka batabani ba mmwe abo abava ku migongo gyammwe, era temukkirizibwa kugatta wakati wa bakyala ab’oluganda okugyako ebyo byemwakola edda emabega, anti bulijjo Katonda musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(4,24,'4:24','Era temukkirizibwa kuwasa bakyala bafumbo mpozzi abo bemuba mufunye mu mikono gyammwe mu ngeri yo bufuge, bino byonna Katonda abibataddeko nga kiragiro ekiva gyali. Mukkirizibwa okuwasa abatali abo, nga mukozesa emmaali yammwe. Mubafune mu bufumbo obutukuvu so ssi mu bwenzi, abo bwe mubanga musanyuse nabo, mubawe Amahare gaabwe, nga Katonda bweyakiraalika. Temulina kibi kwekyo kyemuba mukkaanyizzaako oluvanyuma lw’okugereka Amahare. Mazima ddala Katonda bulijjo mumanyi nnyo ate agoba nsonga.');
INSERT INTO "african_dev" VALUES(4,25,'4:25','Nooyo aba talina mu mmwe busobozi okuba nga awasa abaana baboobwe abakkiriza, awase mwabo emikono gyammwe begyaafuna nga eminyago mu bawala ba mmwe abakkiriza, Katonda amanyidde ddala obukkiriza bwa mmwe, mwenna abamu bava mu bannaabwe. Wabula mubawase kasita bakama baabwe bakkiriza era mubawe Amahare gaabwe mu buntu bulamu, bateekeddwa okuba abensa nga si bamalaaya wadde ab''eteerawo baganzi baabwe, bwebaba bafumbiddwa ate nebayingira mu bwenzi, mubasseeko kitundu kya kibonerezo, ekiweebwa abakyala abaana ba''boobwe. Ekyo nno (ekyokubawasa) kyoyo aba atidde mu mmwe okugwa mu kibi kyobwenzi, naye singa muba musobodde okugumiikiriza ekyo kyekisinga obulungi gyemuli, era bulijjo Katonda musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(4,26,'4:26','Katonda ayagala okubannyonnyola era abalungamye ku nkola y’abo abaabakulembera, era akkirize okwenenya kwa mmwe. Bulijjo Katonda mumanyi nnyo ate agoba nsonga.');
INSERT INTO "african_dev" VALUES(4,27,'4:27','Era Katonda ayagala akkirize okwenenya kwa mmwe, ate bo abagoberera okwagala kwabwe baagala Katonda mumuviireko ddala.');
INSERT INTO "african_dev" VALUES(4,28,'4:28','Bulijjo Katonda ayagala kubanguyiza, anti omuntu yatondebwa nga munafu.');
INSERT INTO "african_dev" VALUES(4,29,'4:29','Abange mmwe abakkiriza, temulyanga emmaali yammwe wakati wa mmwe mu bukyamu, okugyako nga kubadde kusuubulagana nga kusinziira ku kutegeeragana wakati wa mmwe, temwetemulanga. Mazima Katonda bulijjo waakisa ku mmwe.');
INSERT INTO "african_dev" VALUES(4,30,'4:30','Omuntu akola ekintu ekyo mu bulumbaganyi n’obulyazamanyi, tugenda kumuyingiza omuliro, era ekyo ku Katonda kigenda kuba kyangu.');
INSERT INTO "african_dev" VALUES(4,31,'4:31','Bwemwewala ebibi ebinene ebibagaanwa, tujja kubasangulako ebibi ebitonotono era tubayingize ennyingiza ey’ekitiibwa.');
INSERT INTO "african_dev" VALUES(4,32,'4:32','Temuluvuwaliranga ebyo Katonda byeyasukkulumya nabyo abamu mu mmwe ku balala, Abasajja balina omugabo kw’ekyo kyebaba bakoze, nga n’abakyala bwebalina omugabo kwekyo kyebaba bakoze, era bulijjo musabe Katonda abawe ku birungibye, mazima Katonda mumanyi wa buli kintu.');
INSERT INTO "african_dev" VALUES(4,33,'4:33','Nabuli omu twamuteerawo abasikira ebyo abazadde ababiri n’aboluganda byebaba balese, n’abo bebyakakatako ebirayiro byammwe (ab''omukago), kale mubawe omugabo gwabwe, mazima ddala Katonda mujulizi ku buli kintu.');
INSERT INTO "african_dev" VALUES(4,34,'4:34','Abasajja be balabirira Abakyala olwenkizo Katonda gyeyawa abamu ku bannaabwe, ne ku lwemmaali Abasajja gyebasasaanya, kale nno abakyala abalongoofu baba bawulize ku lwa Katonda, b''ekuumira ba bbaabwe.Kwekyo Katonda kyeyabakuumisa. bo abo bemutya okuba nga bajeema mubabuulirire era mubasenguke mu buliri, era mubakubemu. Bwebaba babagondedde temulina kyemubavunaana, mazima Katonda waawaggulu nnyo wa kitiibwa.');
INSERT INTO "african_dev" VALUES(4,35,'4:35','Bwe muba nga mutidde enjawukana wakati wa bafumbo ababiri, muweerezeeyo omutabaganya okuva ku ludda lwo mwami n’omutabaganya omulala okuva ku ludda lwomukyala, bwebaba nga bombi bagenderera kulongoosa, Katonda ajja kukwanaganya wakati waabwe bombi. Mazima Katonda bulijjo amanyi nnyo ategeera buli kyonna ekikwata ku baddube.');
INSERT INTO "african_dev" VALUES(4,36,'4:36','Era musinze Katonda awatali ku mugattako kintu kyonna, n’abazadde ababiri mubayise bulungi, bwekityo n’aboluganda, ne bamulekwa n’abanaku ne muliraanwa ow’okumpi, nemuliraanwa ow''ewala, ne munno ow''okumpi n''omutambuze ali mu bwetaavu, n’abo emikono gyammwe begyafuna ng’eminyago, mazima Katonda tayagala muntu yeekuluntaza omwewulize.');
INSERT INTO "african_dev" VALUES(4,37,'4:37','Abo beebo abakodowala nebalagira na bantu abalala okuba abakodo, nebakweka Katonda byeyabawa mu birungibye. Era Abakafiiri twabategekera ebibonerezo ebinyomesa.');
INSERT INTO "african_dev" VALUES(4,38,'4:38','(Era ebyo byebibonerezo) byabo abawaayo emmaali yaabwe ku lwa bantu okubalaba, nga tebakkiriza Katonda wadde olunaku lw’enkomerero. Omuntu yenna Sitane gwabera nga ye munne, aba afunye owomukwano omubi.');
INSERT INTO "african_dev" VALUES(4,39,'4:39','Kiki ekyandibatuseeko singa bakkiriza Katonda n’olunaku lw’enkomerero, ne bagaba kwebyo Katonda bye yabawa. Anti bulijjo Katonda amanyi ebibakwatako.');
INSERT INTO "african_dev" VALUES(4,40,'4:40','Mazima Katonda talyazamaanya wadde akantu akatono ennyo, bwekiba kirungi akibazaamu anti bulijjo awa okuva gyali empeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(4,41,'4:41','Guliba gutya bwetuligya mu buli kibiina omujulizi, ggwe netukuleeta ng’omujulizi kwabo.');
INSERT INTO "african_dev" VALUES(4,42,'4:42','Olunaku olwo abo Abakaafuwala nebajeemera omubaka balyegomba singa ettaka libamira, era tebalisobola kukweka Katonda kigambo kyonna.');
INSERT INTO "african_dev" VALUES(4,43,'4:43','Abange mmwe Abakkiriza temusembereranga e sswala nga mutamidde,(okutuusa lwegubaggwako) ne muba nga mutegeera byemwogera (era temusembereranga e sswala) nga mulina Janaba okugyako ayita obuyisi mu muzigiti okutuusa nga munaabye, bwe muba abalwadde, oba nga muli ku safari, oba omu ku mmwe ng’avudde mu kumala ekyetaago (ekyobutonde) oba nga mukutte ku bakyala, ne mutafuna mazzi, olwonno mwetukuze nga mukozesa ettaka eddungi, musiige ebyenyi byammwe n’emikono gyammwe, mazima Katonda bulijjo alekera era asonyiwa.');
INSERT INTO "african_dev" VALUES(4,44,'4:44','Tolaba abo abaaweebwa omugabo mu kitabo, baasalawo okutwala obubuze era nebaagala nammwe mubulwe ekkubo.');
INSERT INTO "african_dev" VALUES(4,45,'4:45','Bulijjo Katonda amanyi nnyo ebikwata ku balabe ba mmwe, Katonda amala okuba nga ye mukuumi, era Katonda amala okuba nga ye mutaasa.');
INSERT INTO "african_dev" VALUES(4,46,'4:46','Mu Bayudaaya, mulimu abakyusa ebigambo nebabikozesa mu bukyamu, nebaba nga bagamba nti tuwulidde era tujeemye. Wulira naye Katonda aleme kukuwuliza, era siriwala. Nga bakyusakyusa ebigambo nennimi zaabwe, era nga bagezaako okussa ebituli mu ddiini. Singa bagamba nti tuwulidde era tugonze wulira era tulabirire, kyandibadde kirungi nnyo gyebali era nga kituufu, wabula Katonda yabakolimira olwobukafiiri bwabwe, tebakkiriza okugyako katono nnyo.');
INSERT INTO "african_dev" VALUES(4,47,'4:47','Abange mmwe abaaweebwa ekitabo mukkirize ebyo byetwassa nga bikakasa ebyo byemulina, nga tetunnaba kukyusa byenyi netubizza emabega, oba netubakolimira nga bwetwakolimira abantu b’olunaku lwomukaaga. Ekigambo kya Katonda bulijjo kiteekwa okutuukirira.');
INSERT INTO "african_dev" VALUES(4,48,'4:48','Mazima Katonda tasonyiwa kumugattako kintu kirala asonyiwa ebirala ebitali ekyo kwoyo gwaba ayagadde, oyo yenna agatta ku Katonda ekinti ekirala, mazima aba agunjizzaawo ekibi kinene nnyo.');
INSERT INTO "african_dev" VALUES(4,49,'4:49','Tolaba abo abeetukuza! ekituufu kiri nti Katonda atukuza oyo gwaba ayagadde, era tebagenda kulyazaamanyizibwa wadde omuwula gwentende ogumu.');
INSERT INTO "african_dev" VALUES(4,50,'4:50','Laba engeri gyebatemerera Katonda obulimba, ekyo kimala bumazi okuba nti kibi ekyolwatu.');
INSERT INTO "african_dev" VALUES(4,51,'4:51','Tolaba abo abaaweebwa omugabo mu kitabo, bakkiririza mu buli kisinzibwa ekitali Katonda era nebakkiririza mu Sitane, era nga bagamba abakafiiri nti, abo ekkubo lyabwe ly’elisinga erya bakkiriza okuba ettuufu.');
INSERT INTO "african_dev" VALUES(4,52,'4:52','Abo nno beebo Katonda beyakolimira, ate nga oyo yenna Katonda gweyakolimira, toyinza kumufunira mutaasa yenna.');
INSERT INTO "african_dev" VALUES(4,53,'4:53','Abaffe, balina omugabo gwonna mu bufuzi! bwekiba bwekityo tebandiwadde muntu yenna kantu konna nebwekandibadde kenkana akatuli k’omulamwa gwe ntende.');
INSERT INTO "african_dev" VALUES(4,54,'4:54','Oba bakwatibwa ensalwa olwebyo Katonda byeyawa abantu (Nabbi Muhammad n’abamukkiriza) so nga ate twawa aboolulyo lwa Nabbi Ibrahim ekitabo n’ebigambo ebyamagezi era netubawa obufuzi obugazi.');
INSERT INTO "african_dev" VALUES(4,55,'4:55','(Ne mwabo abaaweebwa omugabo mu kitabo) mulimu abakkiriza obubaka bwa Nabbi Muhammad, ate mulimu abaziyiza abantu okubukkiriza, kimala okuba nga Jahannama gwemuliro ogugenda okubookya.');
INSERT INTO "african_dev" VALUES(4,56,'4:56','Mazima abo abaawakanya ebigambo byaffe, ddala tujja kubayingiza omuliro, buli lwegalisiriira amaliba g’emibiri gyabwe, nga tubassaako amaliba amalala, balyoke bakombe ku bibonerezo. Mazima Katonda bulijjo nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(4,57,'4:57','Ate bbo Abakkiriza nebakola emirimu emirungi, tujja kubayingiza mu jjana ezikulukutiramu emigga, nga baakubeeramu lubeerera, balina mu zo Abakyala abatukuvu era tugenda kubakkiriza batuule mu bisiikirize ebisaakaativu.');
INSERT INTO "african_dev" VALUES(4,58,'4:58','Mazima Katonda abalagira okutuukiriza obuvunaanyizibwa eri abo bemuteekwa okubukolera, era bwemutuula mu nsonga wakati w’abantu mubalamule mu bwenkanya, mazima Katonda kisuffu kyabakalaatira, mazima Katonda bulijjo awulira ate ng’alaba.');
INSERT INTO "african_dev" VALUES(4,59,'4:59','Abange mmwe Abakkiriza mugondere Katonda mugondere n’omubaka n’abakulembeze mu mmwe, bwe mufuna obutakkaanya ku nsonga yonna mugizze eri Katonda n’omubaka, bwe muba mukkiriza Katonda n''olunaku lw’enkomerero ekyo kyekirungi era kyekivaamu ebibala ebirungi.');
INSERT INTO "african_dev" VALUES(4,60,'4:60','Tolaba abo abagamba nti bakkiriza ebyassibwa gyoli, n’ebyassibwa oluberyeberyelwo, baagala okuba nga beramuza ebintu ebikyamu ebitali mu mateeka g’a Katonda, so nga baalagirwa okubiwakanya, era bulijjo Sitane ayagala ababuze olubula olusuffu.');
INSERT INTO "african_dev" VALUES(4,61,'4:61','Bwebaba bagambiddwa nti mujje eri ebyo Katonda byeyassa, era mujje eri omubaka, olaba abananfusi nga bakwemululako olwemulula.');
INSERT INTO "african_dev" VALUES(4,62,'4:62','Butya nga batuukiddwako omuswiba olwebyo byebaakola, olwonno nebakujjira nga balayira Katonda nti, ekigendererwa kyaffe tekyabadde kirala kyonna okugyako okulongoosa n''okutereeza.');
INSERT INTO "african_dev" VALUES(4,63,'4:63','Abo nno beebo, Katonda baamanyi ebiri mu mitima gyaabwe, n''olwekyo baawukaneko era obabuulirire, era obagambe ekigambo ekiggumivu ekinaabatuuka ku mitima.');
INSERT INTO "african_dev" VALUES(4,64,'4:64','Tewali Mubaka gwe twatuma okugyako nga ateekwa kugonderwa kulwobuyinza bwa Katonda, kale singa bo lwebaba beeyisizza obubi bajja gyoli, nebeegayirira Katonda n’omubaka naabasabira ekisonyiwo ewa Katonda, baalisanze Katonda nga akkiriza okwenenya era nga wa kisa.');
INSERT INTO "african_dev" VALUES(4,65,'4:65','Ndayidde Mukama Katonda wo tebayinza kubeera bakkiriza abannamaddala okugyako nga ggwe gwebasalawo okubalamula mu buli kintu kyebaba bakaayaganyeeko, ate nebatasigaza mu mitima gyabwe kakuku ku ngeri gyoba osazeewo era nebakkiriza olukkiriza olulambulukufu.');
INSERT INTO "african_dev" VALUES(4,66,'4:66','Singa mazima ddala ffe twalaalika ku bo nti mwette, oba muve mu mayumba gammwe tebandikikoze okugyako batono ku bo, singa baakola ebyo ebibabuulirwa kyandibadde kirungi gyebali, era kyekyalisinze okubanywereza ku bukkiriza.');
INSERT INTO "african_dev" VALUES(4,67,'4:67','Olwo nno twandibawadde okuva gyetuli empeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(4,68,'4:68','Era twalibalungamizza mu kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(4,69,'4:69','Abantu abagondera Katonda n’omubaka, abo nno beebokubeera awamu naabo Katonda beyawa ebyengera mu ba Nabbi n’abakkiriza abaamazima, n''abafiira mu ntalo zeddiini n''abalongoofu. Abo balungi nnyo okuba nga be bantu boobeera nabo.');
INSERT INTO "african_dev" VALUES(4,70,'4:70','Obulungi obwo bwonna buva wa Katonda, kimala bumazi okuba nti Katonda amanyi byonna.');
INSERT INTO "african_dev" VALUES(4,71,'4:71','Abange mmwe Abakkiriza mubagalire ebyokulwanyisa byammwe, mutambulire mu bibinja, oba mutambulire wamu mwenna.');
INSERT INTO "african_dev" VALUES(4,72,'4:72','Mazima mujja kubeera mu mmwe abantu abanafuya bannaabwe, nga singa mutuukwako obuzibu mu lutalo bagamba nti Katonda yannyambye okuba nti saabadde nabo mu lutalo.');
INSERT INTO "african_dev" VALUES(4,73,'4:73','Naye ate singa muba mugonnomoddwako ebirungi okuva ewa Katonda, ajja kugamba nga walinga awatabangawo nkolagana wakati wa mmwe naye nti nga ndabye nze, singa nabadde nabo nenganyulwa oluganyulwa olunene.');
INSERT INTO "african_dev" VALUES(4,74,'4:74','Bateekeddwa okulwana mu kkubo lya Katonda abo abalekawo obulamu obwensi nebatwala obwenkomerero, oyo yenna alwanirira eddiini ya Katonda naamala attibwa, oba naawangula, tugenda kumuwa empeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(4,75,'4:75','Ngeriki bwemutalwana mu kutaasa ddiini ya Katonda, n''okutaasa abanyigirizibwa mu basajja n''abakyala n''abaana, nebatuuka okugamba nti ayi Mukama omulabirizi waffe tuggye mu nsi eno, abantu baamu abanyigiriza bannaabwe, otuteerewo okuva gyoli omukuumi era otuteerewo okuva gyoli omutaasa.');
INSERT INTO "african_dev" VALUES(4,76,'4:76','Abo abakkiriza, balwana nga bali mu kuweereza mu kkubo lya Katonda, bo abakafiiri balwana mu kuweereza mu kubo lyobubuze n''obwonoonefu, kale mulwanyise banne ba Sitane, anti bulijjo enkwe za Sitane nafu ddala.');
INSERT INTO "african_dev" VALUES(4,77,'4:77','Tolaba abo abaagambwa nti mulekere awo okulwana, era muyimirizeewo e sswala, era mutoole e Zakka, naye ate bwe baalagirwa okulwana, wagenda okulaba ng’abamu mu bo batya abantu, nga balinga abatya Katonda. Si nakindi n’okusingawo, nebagamba nti ayi Mukama Katonda waffe lwaki otulaaliseeko okulwana, singa otulindirizzaako okumala akaseera katono, gamba Ggwe (Nabbi Muhammad) nti ebyokweyagala kwensi byabutaliimu, era enkomerero yenungi eri atya Katonda, era nga temugenda kulyazamanyizibwa wadde omuwula gwe ntende.');
INSERT INTO "african_dev" VALUES(4,78,'4:78','Wonna wemuba muli okufa kugenda kubatuukako, newaakubadde nga muli mu mizimbo eminywevu (bankers), abantu abamu bwebatuukwako ekirungi bagamba nti ekyo kivudde wa Katonda, so nga bwebatuukwako ekibi bagamba nti kino kivudde ku ggwe, Gamba (Ggwe Muhammad) byonna biva wa Katonda, abantu abo bali kumpi babe nga tebategeera kintu kyonna.');
INSERT INTO "african_dev" VALUES(4,79,'4:79','Buli kirungi ekikutuukako kiva wa Katonda, ate buli kibi ekikutuukako kiva ku ggwe kennyini, era twakutuma eri abantu nga oli Mubaka, bulijjo kimala okuba nga Katonda yemujulizi.');
INSERT INTO "african_dev" VALUES(4,80,'4:80','Omuntu agondera omubaka mazima aba agondedde Katonda, n’omuntu akola ekyennyume kyekyo tetukutumanga (Ggwe Muhammad) kubera kalondoozi ku bo.');
INSERT INTO "african_dev" VALUES(4,81,'4:81','(Bwebabeera wooli) bagamba nti bawulize gyoli, naye bwebava wooli ekitundu ku bo nebasula nga basala entotto okwawukana ku byebayogedde, era Katonda awandiika entotto zonna zebasula bakola, baveeko wekwate ku Katonda, bulijjo kimala okuba nga Katonda ye mukuumi.');
INSERT INTO "african_dev" VALUES(4,82,'4:82','Abaffe, tebekkenneenya Kur’ani, singa teyava wa Katonda bandigisanzeemu okukontana kungi.');
INSERT INTO "african_dev" VALUES(4,83,'4:83','Bwebajjirwa e kigambo e kyemirembe oba e kyentiisa bakiraalaasa, so singa bakizza eri omubaka oba abo abakwatibwako ensonga mu bo, bandikitegedde abo abalina obusobozi bwokusengejja amakulu gaayo, singa tebyali birungi Katonda byabawa n’okusaasira kwe mwandigoberedde Sitane mpozzi abatono mu mmwe.');
INSERT INTO "african_dev" VALUES(4,84,'4:84','Kale nno lwaana ng’oweereza mu kkubo lya Katonda, towalirizibwa (kutwala muntu yenna ku lutalo) okugyako mwoyo gwo. Bo abakkiriza bakubirize (okulwana) olwo nno Katonda abe nga akomya akabi kaabo ab''akaafuwala, bulijjo Katonda y’asinga amaanyi era y’asinga okubonereza.');
INSERT INTO "african_dev" VALUES(4,85,'4:85','Omuntu akolerera munne afune e kirungi naye afunako omugabo, n’omuntu akolerera munne atuukweko e kibi atukwako e kibi kyekyo. Bulijjo Katonda yennyini buyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(4,86,'4:86','Bwe muba nga mulamusiddwa n''ekiramuso kyonna mwanukule nga n''ekiramuso ekisingako ku kiri obulungi, oba mwanukuze nakiri kyennyini kyebabalamusizza nakyo, bulijjo Katonda asobolera ddala okubala buli kintu.');
INSERT INTO "african_dev" VALUES(4,87,'4:87','Katonda ateekeddwa okusinzibwa ali omu tewali kintu kyonna kisaana kusinzibwa okugyako yye, mazima ddala agenda kubakunganya ku lunaku lw’enkomerero awatali kubuusabuusa, ate bulijjo ani ayogera amazima okusinga Katonda!');
INSERT INTO "african_dev" VALUES(4,88,'4:88','Lwaki Abannanfusi mubalinako e ndaba ya mirundi ebiri, so nga Katonda olwebyo byebakola yabavuunika (nebadda mu bukafiiri) kaakati mmwe mwagala kulungamya oyo Katonda gweyabuza oyo yenna Katonda gwabuza tosobola kumufunira kkubo.');
INSERT INTO "african_dev" VALUES(4,89,'4:89','Beegomba nnyo okuba nga mukaafuwala nga bwe baakaafuwala, olwo nno mufaanagane, temuggya mu bo ab''emikwano beppo nga basenguse nebadda ku kkubo lya Katonda, naye bwe batakikola mubakwate, mubatte wonna wemubagwikiriza, temuggya mu bo wa mukwano yenna wadde omutaasa.');
INSERT INTO "african_dev" VALUES(4,90,'4:90','Okugyako abo ababa baddukidde eri abantu bemulina nabo endagaano (e yobutatabaalagana), oba abo abajja gyemuli nga tebandi yagadde kubalwanyisa mmwe, oba okulwanyisa abantu baabwe, singa Katonda yayagala (abantu abo) yandi babateeredde nebabalwanyisa. Bweba nga tebabalumbaganye nebatabalwanyisa, era nebalangirira emirembe ku mmwe, nammwe Katonda mu buli ngeri yonna tabakkiriza kubalwanyisa.');
INSERT INTO "african_dev" VALUES(4,91,'4:91','Ate mujja kusanga abalala nga bo baagala babe mirembe nammwe, mu kiseera kyekimu babe mirembe n''abantu baabwe, buli wonna webaddizibwa eri obukafiiri babubbinkiramu. Bwebaba tebabavuddeeko era nebalangirira e mirembe ku mmwe, nebaziyiza e mikono gyabwe, (abo) mubakwate era mubatte wonna wemubagwikiriza, era kwabo twabawa mmwe olukusa olujjuvu okubakolako.');
INSERT INTO "african_dev" VALUES(4,92,'4:92','Tekigwanira mukkiriza yenna kutta mukkiriza, okugyako mubutanwa. Oyo yenna atta omukkiriza mubutanwa ateekwa okuta omuddu omukkiriza, nga ateekwa n''okusasula olufuubanja eri abantu b''omufu okugyako nga bamusonyiye, singa abadde wa mu bantu bemulina nabo obulabe nga ate ye mukkiriza, olwo omussi ateekwa okuta omuddu omukkiriza, naye singa abadde wa mu bantu bemulina nabo endagaano yobutatabaalagana olufubanja luteekwa okuweebwa eri abantu okwo nga ogasseeko okuta omuddu omukkiriza, omuntu aba tasobodde kufuna lufubanja lwawaayo ateekwa okusiiba emyezi ebiri nga gigoberegana, ebyo bikolebwa kulwokusaba Katonda ekisonyiwo. Bulijjo Katonda mumanyi nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(4,93,'4:93','Omuntu atta omukkiriza mu bugenderevu empeera ye muliro Jahannama, w''akugubeeramu bugenderevu, era Katonda yamusunguwalira, naamukolimira, era naamutegekera ebibonerezo ebiyitirivu.');
INSERT INTO "african_dev" VALUES(4,94,'4:94','Abange mmwe abakkiriza bwe muba nga mutambudde mu kuweereza mu kkubo lya Katonda, mwetegerezenga, omuntu bwabatooleranga ssalaamu temumugamba nti toli mukkiriza, nemululunkanira ebyokufuna byensi (bwegiba minyago) ewa Katonda weewali eminyago emingi, ebyo byemwagala okukola so nno bwemwakolanga mu kusoka, Katonda nabagonnomolako e kyengera (kyo bukkiriza) n''olwekyo muteekwa okwetegereza, anti mazima Katonda bulijjo amanyi nnyo byemukola.');
INSERT INTO "african_dev" VALUES(4,95,'4:95','Abatagenda ku lutalo mu bakkiriza nga tebalina kisonyiyisa tebayinza kwenkana naabo abalafuubana nebaweereza mu kkubo lya Katonda nga bawaayo emmaali yaabwe n’obulamu bwabwe, Katonda yasukkulumya eddaala abalafuubana mu kkubolye nga bakozesa emmaali yaabwe n’obulamu bwabwe ku batagenda mu lutalo. Nabuli bamu Katonda yabalagaanyisa okubasasula e birungi. Naye Katonda yasukkulumya abalafuubana mu kkubolye ku batagenze mu lutalo, nga agenda kubasasula empeera ensuffu.');
INSERT INTO "african_dev" VALUES(4,96,'4:96','Ebyo byonna nga kuliba kusitulwa madaala nakusonyiwa nakusaasira ebiriva eri Katonda, bulijjo Katonda musonyiyi nnyo wa kisa.');
INSERT INTO "african_dev" VALUES(4,97,'4:97','Mazima abo ba Malayika be batuusaako okufa nga bakyeyisa bubi, ba Malayika babagamba nti mubadde mweyisa mutya, bo nebaddamu nti tubadde tunyigirizibwa mu nsi (ba Malayika) nebagamba nti abaffe ensi ya Katonda teyali ngazi nemusengukako, abo nno obuddo bwabwe muliro Jahannama era nga buddo bubi ddala.');
INSERT INTO "african_dev" VALUES(4,98,'4:98','Okugyako abo abanyigirizibwa mu basajja n''a bakyala n''abaana abatalina busobozi, era nga balina obuzibu bwokumanya ekkubo lyo kukwata.');
INSERT INTO "african_dev" VALUES(4,99,'4:99','Abo nno Katonda abasonyiwa, bulijjo Katonda alekera era musonyiyi nnyo.');
INSERT INTO "african_dev" VALUES(4,100,'4:100','Oyo yenna asenguka olwokuweereza mu kkubo lya Katonda afuna ebifo bingi ebyokubudamamu n''obulamu obulungi, omuntu aleka amaka ge naasenguka ku lwa Katonda n''omubaka we naye naafa (nga tannatuuka) empeera ye eba emaze okukakata ewa Katonda, Katonda bulijjo musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(4,101,'4:101','Bwe mubanga mutambudde mu nsi, si kibi okuba nga musala ku sswala kavuna mwekengera nga abo Abaakaafuwala bayinza okubatuusaako akabi, anti bulijjo Abakafiiri balabe ba mmwe aboolwatu.');
INSERT INTO "african_dev" VALUES(4,102,'4:102','Ggwe (Nabbi Muhammad) bwobanga mu lutalo n’abakkiriza, nobakulembera mu kusaala, ekibinja ekimu ku bo kiyimiriranga naawe musaale, era nga babagalidde ebyokulwanyisa byaabwe, bwebamala okuvunnama bagende bayimirire emabega wa mmwe ate ekibinja ekirala abatannasaala bajje babe nga basaala naawe, bateekeddwa okuba obulindaala nga babagalidde ebyokulwanyisa byaabwe, abo Abakaafuwala bandyagadde singa mugayaalirira ebyokulwanyisa byammwe n''ebintu byammwe, nebabagwako ekiyiifuyiifu tewali kibi ku mmwe bwe muba nga mulina obuzibu olwenkuba oba nga mubadde balwadde okuba nga mussa wansi ebyokulwanyisa byammwe naye era mubeere bulindaala, mazima Katonda yategekera Abakafiiri ebibonerezo ebinyoomesa.');
INSERT INTO "african_dev" VALUES(4,103,'4:103','Bwemumala e sswala, mutendereze Katonda mu kuyimirira ne mu kutuula ne mu kugalamira, ate bwe muba mu mirembe musaale e sswala mu ngeri eyaabulijjo. Mazima e sswala yalaalikibwa ku Bakkiriza nga esaalibwa mu biseera ebigere.');
INSERT INTO "african_dev" VALUES(4,104,'4:104','Temunafuwa nga mulondoola omulabe, bwe muba nga mulumwa mazima nabo balumwa nga bwemulumwa, ate nga mmwe musuubira ewa Katonda bo kyebatasuubira, bulijjo Katonda amanyi nnyo era mugoba nsonga.');
INSERT INTO "african_dev" VALUES(4,105,'4:105','Mazima ffe twassa gyoli ekitabo mu butuufu, obe nga olamula wakati w’abantu nga okozesa ebyo Katonda bye yakuwa, era towolerezanga abakumpanya.');
INSERT INTO "african_dev" VALUES(4,106,'4:106','Era weegayirire Katonda, mazima Katonda musonyiyi nnyo waakisa.');
INSERT INTO "african_dev" VALUES(4,107,'4:107','Towakanira abo abeelyazaamanya, bulijjo Katonda tayagala muntu mukumpanya omwonoonefu.');
INSERT INTO "african_dev" VALUES(4,108,'4:108','Beekweka abantu nebatasonyiwalira Katonda, so nga bulijjo aba nabo bwebasula nga abayiiya ebigambo byatayagala, ate nga bulijjo Katonda yetoolodde bye bakola.');
INSERT INTO "african_dev" VALUES(4,109,'4:109','Abaffe mmwe beebo ababawakanira mu bulamu obwensi kati ani aliboogerera ewa Katonda ku lunaku lwokuzuukira! oba ani alibeera omweyimirize waabwe!');
INSERT INTO "african_dev" VALUES(4,110,'4:110','Omuntu akola ekibi oba neyeeyisa obubi naye neyeegayirira Katonda, asanga Katonda nga musonyiyi nnyo omusaasizi.');
INSERT INTO "african_dev" VALUES(4,111,'4:111','Omuntu akola ekibi akikolera mwoyo gwe, bulijjo Katonda mumanyi nnyo agoba nsonga.');
INSERT INTO "african_dev" VALUES(4,112,'4:112','Ate omuntu akola ekisobyo, oba ekyonoono ate naakissa ku muntu atakikoze, mazima aba yeetisse ekikolwa ekyobulimba era nga kibi ekyolwatu.');
INSERT INTO "african_dev" VALUES(4,113,'4:113','Singa si kisa kya Katonda n''okusaasirakwe bya kulinako, (Ggwe Muhammad) ekibiina mu bo kyaliyagadde okukubuza, byonna byebakola beebuza bokka, tebayinza kukutuusako kabi konna nga Katonda assa ku ggwe ekitabo (Kur’ani) n’ebigambo ebya magezi (Hadith) era nga yakumanyisa n''ebyo byewali tomanyi era bulijjo e birungi bya Katonda byakuwa bingi nnyo.');
INSERT INTO "african_dev" VALUES(4,114,'4:114','Tewali bulungi mu mboozi zaabwe ezisinga obungi, okugyako oyo alagira abantu okusaddaaka oba obuntu bulamu oba okutabaganya wakati w’abantu, omuntu akola ebyo nga anoonya kusiima kwa Katonda tugenda kumuwa empeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(4,115,'4:115','N''omuntu ayawukana ku Mubaka oluvanyuma lwo bulungamu okumweyoleka, naakwata ekkubo eritali lya bakkiriza tumulekera ekyo kyaaba asazeewo, era tugenda kumukasuka mu muliro Jahannama, obwo nno buddo bubi ddala.');
INSERT INTO "african_dev" VALUES(4,116,'4:116','Mazima Katonda tasonyiwa ku mugattako kintu, so nga asonyiwa ekitali ekyo eri oyo gwaba ayagadde, oyo yenna agatta ku Katonda ekintu aba abuze olubula oluyitirivu.');
INSERT INTO "african_dev" VALUES(4,117,'4:117','Buli byebasaba ebitali ye baabituuma amannya ag’ekikazi, era ddala tebasaba (mu nkola yaabwe eyo) okugyako Sitane eyeewaggula.');
INSERT INTO "african_dev" VALUES(4,118,'4:118','Katonda yagikolimira era neegamba nti nja kutwala ekitundu ekiwera mu baddubo.');
INSERT INTO "african_dev" VALUES(4,119,'4:119','Njakubabuliza ddala, era nja kubawanga ebisuubizo ebyobulimba, era nja kubalagirira ddala bawummule amatu g’ebisolo ebirundibwa era nja kubalagirira nga ddala bakyuse entonda ya Katonda. N’omuntu afuula Sitane mukwanogwe naaleka Katonda mazima aba afaafaganiddwa olufaafaganirwa olwolwatu.');
INSERT INTO "african_dev" VALUES(4,120,'4:120','(Sitane) abalagaanyisa naabawa n’ebisuubizo ebyo bulimba, Sitane byonna byabalagaanyisa tebiri okugyako byabutaliimu.');
INSERT INTO "african_dev" VALUES(4,121,'4:121','(Abagoberera Sitane) abo obuddo bwa bwe muliro Jahannama, era nga tebagenda kufuna buddukiro kuguwona.');
INSERT INTO "african_dev" VALUES(4,122,'4:122','Naabo abakkiriza nebakola emirimu emirongoofu tujja kubayingiza e jjana nga emigga jikulukutira mu zo, baakuzibeeramu obugenderevu, eyo nga ndagaano ey’amazima Katonda gyabawadde, ate ani asinga Katonda okwogera ebigambo eby’amazima.');
INSERT INTO "african_dev" VALUES(4,123,'4:123','(Okuyingira e jjana) tekigenda kusinziira ku kwagala kwa mmwe oba abaaweebwa e kitabo kye baagala, (enkola eri nti) omuntu akola ekibi kiri musasulwa, era nga oggyeko Katonda tewaliba mukuumi yenna wadde omutaasa.');
INSERT INTO "african_dev" VALUES(4,124,'4:124','N’omuntu akola emirimu emirongoofu kaabe musajja oba mukazi nga mukkiriza, abo nno baakuyingira e jjana, era tebagenda kuyisibwa bubi wadde mu kantu akatono.');
INSERT INTO "african_dev" VALUES(4,125,'4:125','Ani ali mu ddiini e nnungi okusinga oyo aba yeewadde ewa Katonda, era nga mukozi wa birungi, era n''agoberera e nkola ya Ibrahim e yobutakkiririza mu bisinzibwa byonna ebitali Katonda omu, era nga Katonda yafuula Ibrahim mukwano gwe owenjawulo.');
INSERT INTO "african_dev" VALUES(4,126,'4:126','Era bya Katonda byonna ebiri mu ggulu omusanvu ne nsi, era bulijjo Katonda amanyi ekifa ku buli kintu.');
INSERT INTO "african_dev" VALUES(4,127,'4:127','Bakubuuza ku nsonga za bakyala, bagambe nti Katonda ajja kubaanukula ebikwata ku bakyala, era nga bwajja okubategeeza ebibasomerwa mu kitabo (Kur’ani) ku nsonga zaaba mulekwa abakyala, abo bemutawa ekyo ekiteekwa okuweebwa (Amahare), ate nemwagala okubawasa era abategeeza ebikwata ku baana abato abateesobola, era nga bwajja okubategeeza nti muteekwa okukola ku nsonga z’a bamulekwa mu bwesimbu, era mukimanye nti e kirungi kyonna kyemukola Katonda akimanyidde ddala.');
INSERT INTO "african_dev" VALUES(4,128,'4:128','Omukyala bwatyanga bba okumuyisa obubi, oba okumusuulawo, si kibi ku bombi okukola ekiyinza okubatabaganya era (bulijjo) okutegeeragana kwekusinga obulungi. Naye omuntu bulijjo agwa olubege ku mulugube, naye singa mulongoosezza nemutya Katonda, bulijjo Katonda byemukola abimanyi nnyo.');
INSERT INTO "african_dev" VALUES(4,129,'4:129','Wabula temugenda kusobola kukola bwenkanya wakati wa bakyala (obuli 100%) ne bwe muba nga kye mwandi yagadde, wabula temubasuulira ngawo ddala, omukyala nemumuleka nga alengejja nga omuwanike, naye singa mutegeeraganye, era nemutya Katonda, mazima bulijjo Katonda musonyiyi nnyo musaasizi.');
INSERT INTO "african_dev" VALUES(4,130,'4:130','Naye (omwami n''omukyala abafumbo) singa baawukana buli omu Katonda ajja kumumalira ebyetaagobye nga ajja mu bigabwabye e bingi, bulijjo Katonda mugazi nnyo mu buli kimu era agoba nsonga.');
INSERT INTO "african_dev" VALUES(4,131,'4:131','Byonna ebiri mu ggulu omusanvu ne mu nsi bya Katonda, era mazima twalaamira abo abaaweebwa ekitabo oluberyeberye lwa mmwe, nammwe netubalaamira nti mutye Katonda, wabula bwemugyema mazima ddala bya Katonda ebyo ebiri mu ggulu omusanvu nebiri mu nsi. Era bulijjo Katonda talina kyeyetaaga era atenderezebwa.');
INSERT INTO "african_dev" VALUES(4,132,'4:132','Era bya Katonda ebyo ebiri mu ggulu omusanvu nebyo ebiri mu nsi. Era bulijo Katonda yaakola ku nsonga z’ebitonde byonna.');
INSERT INTO "african_dev" VALUES(4,133,'4:133','(Okukakasa ekyo) singa aba ayagadde asobola okubagyawo abange mmwe abantu naaleeta abalala, bulijjo Katonda kwekyo musobozi.');
INSERT INTO "african_dev" VALUES(4,134,'4:134','Omuntu ayagala empeera y’okunsi, Katonda yaafuga empeera y’okunsi n''e yokunkomerero bulijjo Katonda awulira era alaba nnyo.');
INSERT INTO "african_dev" VALUES(4,135,'4:135','Abange mmwe abakkiriza mubeere abalwanirira obwenkanya, abajulira ku lwa Katonda, ensonga nebweba ekwata ku mmwe mwennyini, oba abazadde bombi, n''aboluganda olwokumpi (aweebwako obujulizi) kaabe mugagga oba mwavu bombi bantu ba Katonda okusinga okuba nti ba mmwe, kale nno temugoberera okwagala kwa mmwe nemutakola bwenkanya. bwemunaakyusa amazima oba nemugaana okuwa obujulizi, mazima ddala Katonda bulijjo amanyidde ddala byemukola.');
INSERT INTO "african_dev" VALUES(4,136,'4:136','Abange mmwe abakkiriza mukkirize Katonda n’omubakawe, n’ekitabo kyeyassa ku mubakawe, era n’ekitabo kyeyassa oluberyeberye, oyo yenna atakkiririza mu Katonda ne ba Malayika be n''ebitabo bye n’ababaka be n’olunaku lw’enkomerero aba abuze olubula oluyitirivu.');
INSERT INTO "african_dev" VALUES(4,137,'4:137','Mazima abo abakkiriza, ate nebakaafuwala, ate nebakkiriza, oluvanyuma nebakaafuwala, ate nebeeyongerera ddala obwa kiwagi, Katonda si wakubasonyiwa wadde okubalungamya ku kkubo e ggolokofu.');
INSERT INTO "african_dev" VALUES(4,138,'4:138','Sanyusa abannanfusi nga obategeeza nti balina ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(4,139,'4:139','(Abannanfusi) abo abafuula abakafiiri mikwano gyabwe, nebaleka abakkiriza, abaffe baba babanonyaako kitiibwa! Mazima bulijjo ebitiibwa byonna bya Katonda.');
INSERT INTO "african_dev" VALUES(4,140,'4:140','Nga ate mazima (Katonda) yassa ku mmwe mu kitabo nti bwemuwuliranga ebigambo bya Katonda nga biwakanyizibwa, era nga bijolongwa temutuulanga wamu nabo abakola ekyo okutuusa lwebadda ku mboozi endala, (ate nga mazima ddala bulijjo kimanyiddwa nti) mazima Katonda agenda kukunganya abakafiiri n’abananfusi bonna abayingize omuliro Jahannama.');
INSERT INTO "african_dev" VALUES(4,141,'4:141','(Era Abannanfusi) beebo bulijjo abalindirira ekintu ekinaatuuka ku mmwe, bwemufuna obuwanguzi okuva ewa Katonda bagamba nti bulijjo tetuli nammwe, abakafiiri bwebaba nga beebafunyeeyo ku buwanguzi babagamba nti tetwabayamba netubataasa ku bakkiriza, wabula Katonda agenda kulamula wakati wa mmwe ku lunaku lw’enkomerero, ate nga Katonda tagenda kuwa Bakafiiri kkubo lyebayinza kuwanguliramu Bakkiriza.');
INSERT INTO "african_dev" VALUES(4,142,'4:142','Mazima Abannafunsi bakwenyakwenya Katonda, kale naye waakubakwenyakwenya, era bwebayimirira okusaala bayimirira lwabuwaze, nga bagenderera kulaga bantu era tebatendereza Katonda okugyako katono nnyo.');
INSERT INTO "african_dev" VALUES(4,143,'4:143','Ba nampa wengwa (wakati wa Bakkiriza na Bakafiiri) nga tebayinza kubalibwa ku bali oba ku bano, bulijjo omuntu Katonda gwabuza tayinza kumufunira kkubo. (lidda eri bulungamu).');
INSERT INTO "african_dev" VALUES(4,144,'4:144','Abange mmwe abakkiriza temufuula nga abakafiiri mikwano gyammwe nfiira bulago nemuleka abakkiriza, mwandiyagadde okwewaako obujulizi obwolwatu ewa Katonda.');
INSERT INTO "african_dev" VALUES(4,145,'4:145','Mazima Abannanfusi bagenda kubeera ku mwaliiro ogusemberayo ddala wansi mu muliro, ate nga togenda kubafunira mutaasa.');
INSERT INTO "african_dev" VALUES(4,146,'4:146','Okugyako abo abeenenya, nebalongoosa, nebekwata ku Katonda, nga neddiini yaabwe bagikola kulwa Katonda yekka, abo nno bagenda kuba wamu nabakkiriza. Ate nga Katonda abakkiriza agenda kubawa empeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(4,147,'4:147','Singa mwebaza Katonda, era nemukkiriza, ate olwo Katonda aliba ababonerereza lwaki! ate nga bulijjo Katonda mwebaza era mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(4,148,'4:148','Katonda tayagala kwatuukiriza kigambo kibi (ku muntu) okugyako oyo aba alangiddwa obwereere, bulijjo Katonda awulira era amanyi nnyo.');
INSERT INTO "african_dev" VALUES(4,149,'4:149','Bwemwolesa ekirungi, oba nemukikweka, oba nemusonyiwa abayisizza obubi, mazima bulijjo Katonda alekera, muyinza nnyo.');
INSERT INTO "african_dev" VALUES(4,150,'4:150','Mazima abo abajeemera Katonda n’ababakabe, nebagezaako okwawula wakati wa Katonda n’ababakabe, era nebagamba nti tukkirizaako abamu abalala tetubakkiriza, nebaagala okuteekawo ekkubo wakati weebyo ebibiri, (babe nga mwebatambulira).');
INSERT INTO "african_dev" VALUES(4,151,'4:151','Abo nno beebatakkiriza abannamaddala. Era nga abakafiiri twabategekera ebibonerezo ebinyoomesa.');
INSERT INTO "african_dev" VALUES(4,152,'4:152','Ate abo abakkiriza Katonda n''ababaka be nebatayawula wakati w’omu kubo, abo nno Katonda agenda kubawa empeera yaabwe, bulijjo Katonda musonyiyi wa kisa nnyo.');
INSERT INTO "african_dev" VALUES(4,153,'4:153','Abaaweebwa ekitabo bakusaba okuba nga bafuna ekitabo okuva mu ggulu, ate nga mazima kyebaasaba Musa kyali kinene okusinga ekyo, bwebaagamba nti tulage Katonda mubweyolefu. Olwokweyisa kwabwe okubi laddu yabakuba (ate oluvanyuma Katonda yabalamusa), ate neb’eteerawo ennyana nebagisinza, so nga obunnyonyofu bwali bwabajjira dda, ekyo nno nakyo twakisonyiwa, so nga ate twali twawa Musa obujulizi obwenkukunala (obukakasa obwa Nabbi bwa Musa).');
INSERT INTO "african_dev" VALUES(4,154,'4:154','Netuleebeseza waggulu waabwe olusozi, bwebataagoberera, endagaano gyetwabakozesa nti baali bateekwa okugoberera (enjigiriza ya Taurat) netubagamba nti muyingire omulyango gw’ekibuga (Bait Makdis) nga muvunnamye (kyebataakola) era twabalagira obutavuba ku lunaku lwa mukaaga (ekiragiro kyebaamenya) era twabakozesa endagaano enzito.');
INSERT INTO "african_dev" VALUES(4,155,'4:155','Ekyabatuusa kwebyo, kuba nti baamenya endagaano zaabwe, n’okuwakanya amateeka g’a Katonda, n’okutta kwabwe ba Nabbi ekitaali kituufu, era nekulwokugamba kwabwe nti, emitima gyaffe mizibikivu, ekituufu kiri nti Katonda yeeyagibikka olw''obukaafiiri bwabwe, tebayinza kukkiriza okugyako kitono nnyo.');
INSERT INTO "african_dev" VALUES(4,156,'4:156','Era nekulwobukafiiri bwabwe, n’okutemerera Mariam ekigambo ekinene.');
INSERT INTO "african_dev" VALUES(4,157,'4:157','Era nekulwekigambo kyabwe bwebagamba nti, mazima ffe tumaze okutta Masiya Issa Mutabani wa Mariam omubaka wa Katonda so nga tebaamutta, wadde okumukomerera ku musaalaba, wabula kyabalabikiranga abasse yye, naye mazima ddala abaayawukana mu nsonga eno bali mu kubuusabuusa ku bigikwatako, tebalina bukakafu ku yo wabula okugoberera okuteebereza, naye nga ddala tebaamutta.');
INSERT INTO "african_dev" VALUES(4,158,'4:158','Wabula Katonda yamusitula n’amutwala gyali, era bulijjo Katonda ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(4,159,'4:159','Mazima tewali n’omu mwabo ab''aweebwa e kitabo okugyako nga agenda ku mukkiriza nga tannafa, nga ne ku lunaku lw’enkomerero agenda kubawaako obujulizi.');
INSERT INTO "african_dev" VALUES(4,160,'4:160','Okusinziira kukweyisa obubi kwa ba Yudaaya, twaziza ku bo ebirungi ebyali bibakkiriziddwa, ne ku lwo kwekiika kwabwe mu kkubo lya Katonda.');
INSERT INTO "african_dev" VALUES(4,161,'4:161','Nekulwokutwalanga kwabwe Ribbah, so ng’ate baagigaanibwa, nekulwokulya kwabwe emmaali y’abantu mu bukyamu, era twateekerateekera abakafiiri mu bo ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(4,162,'4:162','Wabula abamanyi ba kakensa mu bo n’abakkiriza ebyassibwa gyoli, n''ebyassibwa oluberyeberyelwo, era abayimirizaawo e sswala, nebawaayo nezzaka, era abakkiriza Katonda n’olunaku lwe nkomerero, abo nno tugenda kubawa mpeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(4,163,'4:163','Mazima ffe twakuwa ggwe (Nabbi Muhammad) obubaka nga bwetwawa Nuhu obubaka, neba Nabbi bangi oluvanyumalwe, era twawa Ibrahim obubaka ne Ismail ne Ishaka ne Yakubu ne bazzukulu ba Yakubu, ne Isa ne Ayub ne Yunus ne Haruna ne Sulaiman, ne Dauda netumuwa Zaburi.');
INSERT INTO "african_dev" VALUES(4,164,'4:164','N’ababaka abalala twabakunyumiza dda ate nga ababaka abalala tetwabakunyumiza, era mazima Katonda yayogera ne Musa olwogera (nga tewali Mubaka wakati waabwe).');
INSERT INTO "african_dev" VALUES(4,165,'4:165','Ababaka abo bonna bajjanga nga bawa amawulire agasanyusa, nga n''abandi bawa amawulire agalabula, abantu baleme kuba na byekwaso ku Katonda kasita yamala okubatumira ababaka, bulijjo Katonda nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(4,166,'4:166','(Ekyo kimala abantu okuba nga naawe bakukkiriza naye abayudaaya n’ababafaanana bweba nga tebakukkirizza) kasita Katonda akakasa nti (ekitabo) ekyassibwa ku ggwe kyassibwa ku lwakumanya kwa Katonda n’okusalawokwe ne ba Malayika ekyo kyennyini kyebakakasa, naye nga bulijjo Katonda yeemalirira okuwa obujulizi n’okukakasa ekintu kyonna.');
INSERT INTO "african_dev" VALUES(4,167,'4:167','Mazima abo abaakaafuwala nebassa emisanvu mu kkubo lya Katonda, mazima baabula olubula oluyitirivu.');
INSERT INTO "african_dev" VALUES(4,168,'4:168','Mazima abo abaakaafuwala era nebeeyisa obubi, Katonda si waakubasonyiwa wadde okubalungamya mu kkubo.');
INSERT INTO "african_dev" VALUES(4,169,'4:169','Mpozzi ekkubo erigenda mu muliro Jahannama gwebalibeeramu obugenderevu, era bulijjo ekyo ku Katonda kyangu nnyo.');
INSERT INTO "african_dev" VALUES(4,170,'4:170','Abange mmwe abantu, omubaka yabajjira nga aleeta amazima okuva ewa Mukama omulabirizi wa mmwe, kale mukkirize, kye kirungi gyemuli, naye bwe munaakaafuwala mazima ddala ebiri mu ggulu omusanvu ne nsi bya Katonda, bulijjo Katonda mumanyi nnyo agoba nsonga.');
INSERT INTO "african_dev" VALUES(4,171,'4:171','Abange mmwe abaaweebwa ekitabo temusukkanga ekyetaagisa, nga mutuukiriza eddiini yammwe, era temwogeranga ku Katonda okugyako amazima, mazima ddala Masiya Isa mutabani wa Mariam mubaka wa Katonda, era kigambokye, kyeyawa Mariam, era mwoyo ogwava gyali. Kale temugamba nti basatu, ekyo mukikomye, kyekirungi gyemuli, mazima ddala Katonda ali Katonda omu, yayawukana (tasaana) kuba namwana, bibye byonna ebiri mu ggulu omusanvu n''ebiri mu nsi , bulijjo kimala okuba nti Katonda y’ayimiriddeko ensonga ya buli kintu.');
INSERT INTO "african_dev" VALUES(4,172,'4:172','Masiya tayinza butayagala kuba muddu wa Katonda, wadde ba Malayika abali kumwanjo nabo tebayinza butakyagala, oyo yenna atayagala kuyitibwa muddu wa Katonda neyeekuluntaza, (Katonda) ajja kubazuukiza bonna abazze gyali ku lunaku lw’enkomerero.');
INSERT INTO "african_dev" VALUES(4,173,'4:173','Kale nno abo abakkiriza nebakola emirimu emirungi, Katonda ajja kubasasula empeera y’emirimu gyaabwe mu bujjuvu, ate abongere n’ebirungi ebirala ebiva gyali, naye abo abawakanya nebawaganyala, abo wa kubabonereza ebibonerezo ebiruma ennyo, ate tebagenda kubaayo gwebafuna atali Katonda nga mukwano gwaabwe wadde nga mutaasa.');
INSERT INTO "african_dev" VALUES(4,174,'4:174','Abange mmwe abantu mazima bwabajjira obubonero obwenkukunala, era nga twassa gyemuli ekitangaala ekyeyolefu (Kur’ani).');
INSERT INTO "african_dev" VALUES(4,175,'4:175','Naye abo abakkiriza Katonda nebeenywereza ku ye, abo nno agenda kubayingiza (e jjana) nga kusaasira okuva gyali (era alibawa) ebigabwa (ebirala), era abalungamyenga eri ekkubo eribatuusa gyali, nga kkubo ggolokofu.');
INSERT INTO "african_dev" VALUES(4,176,'4:176','Bakubuuza (ggwe Nabbi Muhammad) ku muntu afa naataleka bazadde oba abaana wabula nga alese bagandabe, gamba nti Katonda ajja kubannyonnyola ebikwata ku kusikira omuntu oyo, omuntu bwafa nga talina mwana, naye nga alina mwannyina, oyo afuna kimu kyakubiri kwebyo byaba alese, era naye asikira mwannyina singa (Mwannyina afa) nga talese mwana, bannyina bwebaba babiri bafuna bibiri byakusatu kw''ebyo byaba alese, bwebaba b’aluganda nga basajja na bakazi, omusajja afuna omugabo gwabakazi babiri, Katonda abannyonnyola (amateekage) mube nga temubula, bulijjo Katonda mumanyi waabuli kintu.');
INSERT INTO "african_dev" VALUES(5,1,'5:1','Abange mmwe Abakkiriza mutuukirizenga endagaano, mukkirizibwa okulya ennyama y’ebisolo ebiriibwa byonna okugyako e binaabasomerwa, ate nga temukkirizibwa kuyigga nga muli mu mizizo gya (Hijja oba Umurah) mazima Katonda asalawo kyaaba ayagadde.');
INSERT INTO "african_dev" VALUES(5,2,'5:2','Abange mmwe abakkiriza temufuulanga Halaali ebyo Katonda byeyabalambira nti biri Haramu, era nga bwemutaasanye kukyusa myezi gya mizizo nemugifuula egyokulwaniramu, era temwekiikanga mu kkubo lyebirabo ebiba bireetebwa e Makkah wadde ensolo ennambe nga z’abirabo, wadde abantu abagenda okulambula e nyumba ey’emizizo, ababa bagenda okunoonya obulungi n’okusiima okuva ewa Katonda waabwe, naye bwe muba musumulukuse okuva mu mizizo gya (Hijja ne Umurah) olwonno muyiggenga. Olwokuba nti abantu baabaziyiza okutuuka ku muzikiti ogw’emizizo, tekibatuusanga okubayisa obubi, era muyambaganenga ku kukola obulungi n’okutya Katonda, so temuyambagananga ku kukola ebibi n’obulumbaganyi, era mutye Katonda, mazima Katonda muyitirivu w’abibonerezo.');
INSERT INTO "african_dev" VALUES(5,3,'5:3','Muziyiziddwa okulya ennyamanfu n’omusayi (Kafece) nennyama y’embizzi n’ekyo ekiba kyogereddwako eritali linnya lya Katonda nga kisalibwa, n’ekifudde olw’okukubwa, n’ekifudde olwokugwa, n’ekitomeddwa kinnakyo, n’ekiriiriddwako ensolo enkambwe, okugyako byemuba mwanguyidde nemubisala, era muziyiziddwa okulya ennyama y’ebisolo ebiba bisaliddwa olwokubiwongera atali Katonda omu ateekwa okusinzibwa, era muziyiziddwa okweraguza nga mukozesa obusaale. Okukola ekimu kwebyo, buba bugyemu, olwaleero abo abakaafuwala tebakyalina ssuubi kubalemesa ddiini yammwe, kale temubatya, mutye nze. Olwaleero mbajjulizza eddiini yammwe, era nembajjuliza ebyengera byange, era nembasalirawo obusiramu okuba nga yeddiini (yammwe) kale nno omuntu eyeesanga mu buzibu obwenjala, so ssi lwakugenderera kumenya tteeka (naamala alya ku bigaaniddwa), mazima Katonda musonyiyi wa kisa.');
INSERT INTO "african_dev" VALUES(5,4,'5:4','Bakubuuza ggwe (Nabbi Muhammad) ebibakkirizibwa (okulya), gamba nti mukkirizibwa okulya ebirungi, era (mukkirizibwa okulya) n’okulya ebyo ebikwatiddwa ebiyigga byemutendese nemuyigiriza ddala okusinziira nga Katonda bweyabayigiriza, bweguba gutyo mulye ebyo bye bibakwatira, era mubyogerereko erinnya lya Katonda (nga mubisindika okugenda okukwata ensolo) era mutye Katonda, anti mazima Katonda mwangu wa kubala.');
INSERT INTO "african_dev" VALUES(5,5,'5:5','Olwaleero mukkirizibwa okulya e birungi, nga era n''ennyama esaliddwa abaaweebwa ekitabo bwekkiriziddwa gyemuli, n''ennyama yammwe ekkiriziddwa gye bali, era mukkirizibwa okuwasa ab''ensa mu bakyala abakkiriza nga bwemukiriziddwa abakyala abensa mwabo abaaweebwa ekitabo oluberyeberye lwa mmwe, kavuna mubawa Amahare gaabwe nga mubawasa mu bufumbo obutukuvu, so ssi mu bwenzi wadde okuba nti mubatwala nga bakyala ba mmwe abebbali, n''omuntu atakolera ku mateeka g’abukkiriza emirimu gye giba gimufudde, nga naye ku lunaku lw’enkomerero agenda kubeera mu bafaafaganiddwa.');
INSERT INTO "african_dev" VALUES(5,6,'5:6','Abange mmwe abakkiriza bwe muba mugenda okusaala muteekeddwa okunaaza ebyenyi byammwe, n’emikono gyammwe okutuusa mu nkokola, era musiige ku mitwe gyammwe, munaaze n’ebigere byammwe okutuuka ku bukongovule, bwe muba nga mulina Janaba muteekeddwa okunaaba naye bwe mubanga abalwadde, oba nga muli ku safari, oba omu ku mmwe nga avudde mukumala ekyetaago ekyobutonde, oba nemukwata ku bakyala (mumbera ezo zonna) nemutafuna mazzi, kale nno, mutayammame, nga mukozesa ettaka eddungi, (ekyo mukikola) nga musiiga ku byenyi byammwe n’emikono gyammwe, Katonda tayagalangako kubateerawo bukalubo bwonna, naye ayagala kubatukuza era abajjulize ekyengerakye musobole okubeera nga mwebaza.');
INSERT INTO "african_dev" VALUES(5,7,'5:7','Era mujjukire e kyengera kya Katonda kyeyabawa n''endagaanoye gyeyabakozesa bwemwagamba nti tuwulidde era tugonze. Era mutye Katonda, anti mazima Katonda amanyidde ddala ebyo ebiri mu bifuba.');
INSERT INTO "african_dev" VALUES(5,8,'5:8','Abange mmwe abakkiriza mubeere beesimbu ku lwa Katonda, nga muwa obujulizi mubwenkanya, abantu okuba abalabe ba mmwe, tekibawugulanga nemutakola mazima, bulijjo mukole obwenkanya, kyo kyekiri okumpi n''okutya Katonda, era mutye Katonda, mazima Katonda amanyidde ddala byemukola.');
INSERT INTO "african_dev" VALUES(5,9,'5:9','Katonda yalagaanyisa abo abakkiriza nebakola emirimu emirungi, nti balina ekisonyiwo nempeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(5,10,'5:10','Ate abo ab''akaafuwala nebalimbisa ebigambo byaffe, abo nno be bantu b’omumuliro.');
INSERT INTO "african_dev" VALUES(5,11,'5:11','Abange mmwe abakkiriza mujjukire ekyengera kya Katonda kye yabawa, abantu abamu bwe baayagala okugolola e mikono gyaabwe (babalwanyise), n''aziyiza e mikono gyaabwe ku mmwe, era bulijjo mutye Katonda, Katonda yekka abakkiriza gwebateekeddwa okwesiga.');
INSERT INTO "african_dev" VALUES(5,12,'5:12','Mazima Katonda yakozesa abaana ba Israil endagaano, era netuggya mu bo abakulembeze kkumi nababiri, Katonda naagamba nti mazima njakuba wamu nammwe, bwe munaayimirizaawo e sswala, nemutoola Zzaka, nemukkiriza ababaka bange, era nemubawagira, era nemuwola Katonda oluwola olulungi, njakubasonyiwa ebyonoono byammwe, mbayingirize ddala e jjana ezikulukutiramu emigga, naye oyo yenna alikaafuwala mu mmwe oluvanyuma lwekyo, aliba abuze okuva ku kkubo ettuufu.');
INSERT INTO "african_dev" VALUES(5,13,'5:13','Kulwokumenya kwabwe endagaano twabakolimira, emitima gyaabwe netugifuula emikakanyavu nebaba nga bakyusa ebigambo okubijja ku makulu gaabyo amatuufu, nebasuula ekitundu kinene kw''ebyo ebyabayigirizibwa, bulijjo ojja kugwanga ku nkwe zaabwe okugyako abatono mu bo, kale basonyiwe era olekere, anti bulijjo mazima Katonda ayagala abakozi b’obulungi.');
INSERT INTO "african_dev" VALUES(5,14,'5:14','Era twakozesa endagaano abamu kwaabo abeeyita Abanaswara, era nabo baasuula ekitundu kinene kweebyo ebyabayigirizibwa, netussa wakati waabwe obulabe n’obukyayi okutuusa olunaku lw’enkomerero, olwo nno Katonda alyoke abategeeze byonna byebaakolanga.');
INSERT INTO "african_dev" VALUES(5,15,'5:15','Abange mmwe abaaweebwa ekitabo, mazima omubaka waffe yabajjira abategeeze bingi byemwaali mukweka mu kitabo, mu ngeri yeemu asonyiwe bingi, mazima kyabajjira ekitangaala n’ekitabo ekyeyolefu okuva eri Katonda.');
INSERT INTO "african_dev" VALUES(5,16,'5:16','Katonda alungamya nakyo mu kkubo e ry''emirembe oyo aba agoberedde okusiimakwe, era naabajja mu bizikiza nga abazza eri ekitangaala kulwokwagalakwe, era n''abalungamya ku kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(5,17,'5:17','Mazima baakaafuwala abaagamba nti ddala Masiya mutabani wa Mariam ye Katonda, gamba (Ggwe Muhammad) ani ayinza okukolawo ekintu kyonna singa Katonda aba asazeewo okuzikiriza Masiya mutabani wa Mariam ne maamawe, n’abantu bonna abali ku nsi, obufuzi bweggulu omusanvu ne nsi nabyonna ebiri wakati wa byombi, bwa Katonda, atonda kyaba ayagadde, era Katonda bulijjo muyinza wa buli kintu.');
INSERT INTO "african_dev" VALUES(5,18,'5:18','Era Abayudaaya n''aba Krisitayo bagamba nti ffe tuli baana ba Katonda era mikwanogye, bagambe ate lwaki alibabonereza olwebyonoono byammwe, ekituufu kiri nti mmwe muli bantu nga abalala beyatonda, asonyiwa gwaba ayagadde, era naabonereza gwaba ayagadde, era obufuzi bweggulu omusanvu ne nsi nabyonna ebiri wakati wa byombi bya Katonda, ate nga gyali yeeri obuddo.');
INSERT INTO "african_dev" VALUES(5,19,'5:19','Abange mmwe abaweebwa ekitabo, mazima abagyidde omubaka waffe nga abayigiriza oluvanyuma lwokuyitawo akaseera nga tewali mubaka, muleme kugamba nti tetwajjirwa atuwa mawulire ga ssanyu ag’ejjana oba okututiisa omuliro, ekituufu kiri nti abawa amawulire agessanyu, era nga abatiisa n’omuliro yabajjira. Bulijjo Katonda muyinza waabuli kintu.');
INSERT INTO "african_dev" VALUES(5,20,'5:20','Era mujjukire Musa bweyagamba abantu be nti abange, bantu bange, mujjukire ekyengera Katonda kye yabawa bwe yabassaamu ba Nabbi (abangi), era mmwe n''abawa obuyinza era n''abawa ebintu byataawa muntu mulala yenna mu bitondebye.');
INSERT INTO "african_dev" VALUES(5,21,'5:21','Abange bantu bange (mulwane) muyingire ensi entukuvu eyo Katonda gyeyabalagaanyisa, temukyukanga nemukubayo amabega (nemudduka) nemufuuka abafaafaganiddwa.');
INSERT INTO "african_dev" VALUES(5,22,'5:22','Nebagamba nti owange Musa mazima ensi eyo, erimu abantu bakirimaanyi, era mazima tetuyinza kuyingira mpozzi nga bagifulumye, bwebagifuluma olwo ffe nga tuyingira.');
INSERT INTO "african_dev" VALUES(5,23,'5:23','Abasajja babiri mwabo abatya Katonda, nga era Katonda yabawa ekyengera kyobukkiriza nebagamba nti, mubalumbe nga muyita ku mulyango omunene, kemuyingira mazima ddala mujja kuwangula, naye Katonda yekka gwemuba mwekwatako, bwe muba nga muli bakkiriza.');
INSERT INTO "african_dev" VALUES(5,24,'5:24','Nebagamba nti owange Musa, mazima ffe tetujja kukiyingira ebbanga lye banaamala nga bakyakirimu, ggwe ne Katonda wo mugende mulwane ffe tusigadde wano.');
INSERT INTO "african_dev" VALUES(5,25,'5:25','(Musa) naagamba nti ayi Mukama omulabirizi wange mazima nze sifuga okugyako omwoyo gwange ne muganda wange, lamula wakati waffe ne wakati w’abantu abonoonyi.');
INSERT INTO "african_dev" VALUES(5,26,'5:26','Katonda (n’alamula) wakati waabwe naagamba nti ekyokuyingira kibagaaniddwa okumala emyaka ana, gyebaamala nga babulubuutira mu nsi, n’olwekyo tewennyamira olwabantu abajeemu.');
INSERT INTO "african_dev" VALUES(5,27,'5:27','Era basomere mu bulambulukufu ebikwata ku baana ba Adam ababiri bwe baawa ewa Katonda Saddaka, ey’omu nekkirizibwa, ate ey’omulala n’etakkirizibwa, kwekugamba nti nja kukuttira ddala (ye oli) naagamba nti mazima ddala Katonda akkiriza mirimu gyabo abamutya.');
INSERT INTO "african_dev" VALUES(5,28,'5:28','(Naye) bwonongalulira omukono gwo onzite, nze sigenda ku kugalulira mukono gwange ku kutta, mazima nze ntya Katonda omulabirizi w’ebitonde.');
INSERT INTO "african_dev" VALUES(5,29,'5:29','Mazima nze njagala ogende n’ekibi kyange n’ekikyo (singa onzita), olwo nno obeere mu bantu bo mu muliro, era nga bulijjo eyo ye mpeera y’abantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(5,30,'5:30','Omwoyo gwe omubi negumusalirawo okutta muganda we, era n''amutta naabeera mu baafaafaganirwa.');
INSERT INTO "african_dev" VALUES(5,31,'5:31','Olwonno Katonda n’asindika Namungoona eyajja nga asima ettaka olwonno amulage engeri gyanaaziikamu omulambo gwa mugandawe, naagamba nti mbadde ki nze? nemeddwa okukola nga Namungoona bwakoze, mbe nga nziika omulambo gw’a muganda wange, awo naatandika okubeera mu bejjusa.');
INSERT INTO "african_dev" VALUES(5,32,'5:32','Okusinziira ku ekyo twassa etteeka ku baana ba Israil nti mazima oyo atta omuntu atatemudde muntu, oba atakoze bwonoonefu mu nsi, mazima ddala aba nga asse abantu bonna., ate ataasa obulamu bw''omuntu omu aba nga ataasizza obulamu bwa bantu bonna, (ebyo abayudaaya baalibadde babitegeera) anti mazima ababaka baffe babaleetera amateeka amannyonnyofu, naye ate oluvanyuma lwekyo mazima bangi ku bo baasigala beeyisa bubi mu nsi.');
INSERT INTO "african_dev" VALUES(5,33,'5:33','Mazima ddala empeera y’abo abalwanyisa Katonda n’omubaka we nebasaasaanya obwonoonefu mu nsi, (ekibonerezo kyabwe) kuttibwa, kukomererwa ku musaalaba, oba emikono na magulu gaabwe kutemwaako mu ngeri y’okuwaanyisibwa (nga bwebamutemako omukono ogwa dddyo, okugulu bamutemako kwa kkono) oba bawangangusibwe okuva mu nsi yaabwe, ekyo nno nga kubakkakkanya kuno ku nsi era nga ne ku lunaku lw’enkomerero bagenda kutuusibwako ebibonerezo ebikakali.');
INSERT INTO "african_dev" VALUES(5,34,'5:34','Okugyako abo abeenenya nga temunabakwata, era mumanye nti mazima ddala Katonda musonyiyi nnyo omusaasizi.');
INSERT INTO "african_dev" VALUES(5,35,'5:35','Abange mmwe abakkiriza mutye Katonda era munoonye ekkubo erituuka gy''ali, era munyiikire nnyo mu kuweereza mu kkubo lye, ekiribayamba okubeera mu balituuka ku buwangizi (ku lunaku lw’enkomerero).');
INSERT INTO "african_dev" VALUES(5,36,'5:36','Mazima abo abaakaafuwala singa mazima byonna ebiri mu nsi n’ebiringa byo nga obitadde wamu nebaagala okwenunula nabyo okuwona ebibonerezo by’olunaku lw’enkomerero, tebyandikkiriziddwa okuva gyebali, era baakussibwako e bibonerezo e biruma e nnyo.');
INSERT INTO "african_dev" VALUES(5,37,'5:37','Baliyagala okufuluma mu muliro ng’ate sibaakugufuluma, bagenda kutuukwako ebibonerezo e byolubeerera.');
INSERT INTO "african_dev" VALUES(5,38,'5:38','Omubbi omusajja n''omubbi omukazi mubatemeko emikono gyaabwe bombi, nga mpeera (kibonerezo) yeekyo kyebakoze, era nga kyakulabirako ekyassibwawo Katonda ekikanga abalala, bulijjo Katonda ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(5,39,'5:39','Oyo yenna eyeenenya oluvanyuma lwokweyisa kwe obubi naalongoosa, mazima Katonda akkiriza okwenenya kwe, anti bulijjo mazima Katonda musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(5,40,'5:40','Tomanyi nti mazima ddala obufuzi bweggulu omusanvu ne nsi bwa Katonda, abonereza gwaba ayagadde ate naasonyiwa gwaba ayagadde, bulijjo Katonda muyinza ku buli kintu kyonna.');
INSERT INTO "african_dev" VALUES(5,41,'5:41','Owange ggwe omubaka, tebakunakuwazanga abo abavuganya mu kuwakanya(obwannabbi bwo), mu abo abagamba n''emimwa gyaabwe nti tukkirizza so nga e mitima gyabwe tegikkiriza. Ne mu Bayudaaya abakkiriza e bigambo byobulimba, era abawuliriza kulwa bantu abalala ababa tebazze mu nkungaanazo nebakyusa e bigambo okubiggya mu bifo byabyo nga bagamba (bannaabwe nti), bwemuweebwa kino mukitwale, bwekitabaweebwa mwekeke, naye Katonda gwaba ayagadde okugezesa toyinza kumuyamba mu kintu kyonna ewa Katonda, abo beebo Katonda baatayagala kutukuza mitima gyaabwe, obuswaavu buli ku bo kuno ku nsi, era ne ku lunaku lw’enkomerero bagenda kussibwaako ebibonerezo ebikambwe.');
INSERT INTO "african_dev" VALUES(5,42,'5:42','Bawuliriza ebigambo ebyobulimba, balya emmaali gyebafuna mu makubo aga haramu, singa bajja gyoli (Ggwe Muhammad) lamula wakati waabwe oba baveeko, bwoba obavuddeko tebagenda kukutuusaako kabi, naye bwobanga osazeewo okubalamula, lamula wakati waabwe n''obwenkyanya, anti bulijjo Katonda ayagala abakola obwenkanya.');
INSERT INTO "african_dev" VALUES(5,43,'5:43','Naye lwaki basalawo ggwe obalamule, nga ate balina Taurat erimu okulamula kwa Katonda, ate oluvanyuma nebagikuba amabega, abo sibakkiriza (naakatono).');
INSERT INTO "african_dev" VALUES(5,44,'5:44','Mazima ffe twassa Taurat nga erimu obulungamu nekitangaala, bannabbi ba bayudaaya era bulijjo abamanyiddwa nti beewaayo eri Katonda, nga gyebakozesa okulamula abayudaaya, nga ne banna ddiini n''abakugu mu ddiini nga gyebalamuza olwobwesigwa obwabakwasibwa nga buva mu Kitabo kya Katonda babe nga bakuuma Taurat, era ku ekyo baali bajulizi kale nno temutyanga abantu mutye nze, ebigambo byange temubiwanyisangamu omuwendo omutono, oyo yenna atalamuza ebyo Katonda byeyassa, abo nno be bakaafiiri abannamaddala.');
INSERT INTO "african_dev" VALUES(5,45,'5:45','Era (mu Taurat eyo) twabalaalikako nti mazima omuntu attibwa olwokutta omuntu, n’eriiso liggyibwamu olw’okugyamu eriiso, nennyindo esalwako olwokusala ennyindo y''omulala, n''okutu kusalwako olwokusala okutu kw''omuntu, n''erinnyo balikulamu olwokukuula erinnyo lyomuntu, n''ebiwundu bisasulwa. Wabula omuntu akisonyiwa kimubeerera e nsonga yo kumusonyiwa e bibibye. Era bulijjo omuntu yenna atalamuza Katonda bye yassa abo nno be bantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(5,46,'5:46','Mu buwufu bwabwe (Abayudaaya) twagoberezaako Isa Mutabani wa Mariam ng’akakasa ebyo ebyamukulembera ebiri mu Taurat, era yajja nga bulungamu, era ekyokubuulirira eri abo abatya Katonda.');
INSERT INTO "african_dev" VALUES(5,47,'5:47','Kale nno abantu benjiri bateekeddwa okulamuza ebyo Katonda byeyassa mu yo, omuntu yenna atalamuza ebyo Katonda byeyassa abo nno bo be bonoonyi.');
INSERT INTO "african_dev" VALUES(5,48,'5:48','Era twassa gyoli (Ggwe Muhammad) ekitabo, mu bulambulukufu nga kikakasa e bitabo byonna e byakikulembera era nga kye kibisukkulumye. Kale lamula wakati waabwe nga okozesa e byo Katonda byeyassa togezaako okugoberera okwagala kwa bwe obe nga oleka amazima agakujjidde, buli bamu mu mmwe twabawa amateeka n''enkola singa Katonda yayagala yaalibafudde ekibiina kimu naye yayagala okubakema olwebyo byeyabawa, kale nno muvuganye mu kukola e birungi, anti eri Katonda yeeri obuddo bwa mmwe mwenna, gyalibategeereza ebyo byemwayawukanamu.');
INSERT INTO "african_dev" VALUES(5,49,'5:49','Era lamula wakati waabwe ng’okozesa ebyo Katonda byeyassa, togobereranga okwagala kwa bwe era beegendereze baleme kukuggya ku bimu ku ebyo Katonda byeyassa gyoli, bwebaba nga bakoze e kyennyume kyekyo, manya nti mazima Katonda ayagala okubavunaana olw''ebimu ku byonoono byabwe, wabula nga bulijjo abantu abasinga obungi bonoonyi.');
INSERT INTO "african_dev" VALUES(5,50,'5:50','Ennamula y’ebiseera byobutamanya gyebaagala okukozesa! ani ayinza okuwa okulamula okulungi okusinga okuva ewa Katonda eri abo abakozesa amagezi gaabwe mu butuufu!.');
INSERT INTO "african_dev" VALUES(5,51,'5:51','Abange mmwe abakkiriza temuufulanga abayudaaya n''abakrisitayo mikwano gyammwe ab''omunda, anti abamu ffa nfe w’abalala, yenna abafuula mikwano gye mu mmwe, mazima yye aba afuuse wa mu bo, anti mazima Katonda talungamya bantu beeyisa bubi.');
INSERT INTO "african_dev" VALUES(5,52,'5:52','Ojja kulaba abo abalina obulwadde mu mitima gyabwe nga babeeyunira nga bagamba nti (ekitukozesa kino) tutya okuba nga tutuukibwako ekitutuusaako obuzibu, naye lumu Katonda agenda kuleeta obuwanguzi oba ekintu kyonna okuva gyali, balikeesa enkya nga bejjusa olwebyo bye baakweeka mu mitima gyabwe.');
INSERT INTO "african_dev" VALUES(5,53,'5:53','Ate bbo abakkiriza baligamba nti abo beebo abaalayira Katonda nga bakakasa ebirayiro byabwe!, bwebaagamba nti mazima ddala bo bali wamu nammwe, byebakola byonna byafa togge, olwo nno nebaba nga bafaafaganiddwa.');
INSERT INTO "african_dev" VALUES(5,54,'5:54','Abange mmwe abakkiriza oyo yenna ava mu ddiini ye akimanye nti Katonda agenda kuleeta abantu (abalala) abamwagala nga naye abaagala, abakkakkamu mu maaso ga bakkiriza, nga beegulumiza mu maaso ga bakaafiiri, nga beewaayo mu kuweereza mu kkubo lya Katonda, era nga tebatya kunenya kwa munenyi yenna, ebyo birungi bya Katonda abiwa gwaba ayagadde, bulijjo Katonda mugazi mu buli kintu, mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(5,55,'5:55','Mazima mukwano gwa mmwe ow’omunda Katonda, n’omubaka we naabo abakkiriza, abo abayimirizaawo e sswala nebatoola zzaka, era nga bakutama ku maviivi (nga basaala).');
INSERT INTO "african_dev" VALUES(5,56,'5:56','Oyo yenna asalawo mukwano gwe ow’omunda okuba Katonda n''omubaka we, n''abo abakkiriza, anti bulijjo e kibiina kya Katonda bo be b''okuwangula.');
INSERT INTO "african_dev" VALUES(5,57,'5:57','Abange abakkiriza temufuula abemikwano egyomunda gyemuli abo abafuula eddiini yammwe e kyokusaagirako era e kyokuzannyisa, mu abo abaaweebwa e kitabo oluberyeberye lwa mmwe, n’abakaafiiri. Bulijjo bulijjo mutye Katonda bwe muba nga muli bakkiriza.');
INSERT INTO "african_dev" VALUES(5,58,'5:58','(Mmwe) bwemukowoola abantu okujja okusaala bakitwala nga kyakusaaga era nga kyamuzannyo, bakola batyo lwakuba nti ddala bbo bantu abatategeera.');
INSERT INTO "african_dev" VALUES(5,59,'5:59','Gamba nti: abange mmwe abaaweebwa e kitabo, abaffe waliwo kyemutunyiigira okugyako okuba nti tukkiriza Katonda n''ebyo e byassibwa gyetuli, n''ebyo e byassibwa oluberyeberye era nga na lwakuba nti abasinga mu mmwe boonoonyi.');
INSERT INTO "african_dev" VALUES(5,60,'5:60','Gamba nti abaffe mbategeeze oyo alifuna empeera esinga okuba embi ewa Katonda? be bantu Katonda beyakolimira nabasunguwalira, abamu ku bo nabafuula enkobe ne mbizzi n’abamu nebasinza Sitane, abo nno beebalina ekifo ekisinga obubi era beebasinga okubula okuva ku kkubo ettuufu.');
INSERT INTO "african_dev" VALUES(5,61,'5:61','Bwe bajja gyemuli bagamba nti tukkiriza naye mazima bayingira n’obukaafiiri era nebafuluma nabwo, Katonda amanyi nnyo byonna bye bakweka.');
INSERT INTO "african_dev" VALUES(5,62,'5:62','Ogenda noolaba nga bangi mu bo banguyirira nnyo okukola ekibi nobulumbaganyi n’okulya kwabwe ebya Haramu, kibi nnyo kyebakola.');
INSERT INTO "african_dev" VALUES(5,63,'5:63','Waakiri singa ba nnaddiini n''abakugu mu kumanya e ddiini babakomako ku bigambo e bibi n’okulya e mmaali eya Haramu, kibi nnyo kyebakola.');
INSERT INTO "african_dev" VALUES(5,64,'5:64','Abayudaaya baagamba nti omukono gwa Katonda mu kolige, emikono gyabwe gye gyakoligibwa, ne bakolimirwa olwebyo byebayogera, e kituufu kiri nti e mikono gye gyombiriri myanjulukufu agaba nga bwayagala, e byo ebyassibwa gyoli okuva ewa Katonda wo bijja kwongerera ddala bangi mu bo obubuze nobukafiiri, era twassa wakati waabwe obulabe n''obukyayi okutuusa ku lunaku lw''enkomerero, buli lwonna lwe bakoleeza omuliro gw’olutalo nga Katonda aguzikiza, era basaasaanya mu nsi obwonoonefu, naye bulijjo Katonda tayagala boonoonyi.');
INSERT INTO "african_dev" VALUES(5,65,'5:65','So singa ddala abaaweebwa e kitabo bakkiriza nebatya Katonda twandibasonyiye e byonoono byabwe, era twandibayingizza e jjana ey’ebyengera.');
INSERT INTO "african_dev" VALUES(5,66,'5:66','Era mazima singa bo bayimirizaawo Taurat ne Enjili na byonna e byassibwa gye bali okuva ewa Katonda waabwe, bandiridde okuva waggulu waabwe ne wansi w''ebigere byabwe, mu bo mulimu Abantu abeesimbu naye abangi mu bo byebakola bibi.');
INSERT INTO "african_dev" VALUES(5,67,'5:67','Owange Ggwe omubaka tuusa gyebali ebyo e byassibwa gyoli okuva ewa Katondawo, bwotakikole ojja kuba totuukirizza bubakabwe (totya) Katonda ajja kukuuma ku Bantu, mazima Katonda talungamya kibiina kya bakaafiiri.');
INSERT INTO "african_dev" VALUES(5,68,'5:68','Gamba (Ggwe Nabbi Muhammad) nti temulina kyemuliko okugyako nga muyimirizzaawo Taurat ne Enjili na byonna e byassibwa gyemuli okuva ewa mukama omulabirizi wa mmwe. ebyo e byassibwa gyoli okuva ewa Mukama omulabirizi wo bijja kwongera bangi mu bo obubuze n''obukaafiiri, tonakuwala olwa bantu abakaafiiri.');
INSERT INTO "african_dev" VALUES(5,69,'5:69','Mazima abo abakkiriza, na Bayudaaya ne ba Swabi-uuna (be bantu abataasinza bintu bitali Katonda omu so nga ate tebaalina ddini ya mu ggulu yonna gyebaaliko) n''abakrisitayo, oyo yenna eyakkiriza Katonda n''olunaku lw’enkomerero naakola e mirimu e mirungi (abo) tebalina kutya era tebagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(5,70,'5:70','Twakozesa abaana ba Israil endagaano era netubatumira ababaka wabula buli lweyabajjiranga omubaka n’ebyo emyoyo gyaabwe byegitayagala nga ekitundu bakirimbisa nga ate abalala babatta.');
INSERT INTO "african_dev" VALUES(5,71,'5:71','Baalowooza nti ekyo tekijja kuvaako mutawaana, naye nebafuuka ba muzibe era bakiggala, oluvanyuma Katonda yakkiriza okwenenya kwaabwe, ate bangi mu bo baddamu nebafuuka ba muzibe era bakiggala, Katonda bulijjo alaba byebakola.');
INSERT INTO "african_dev" VALUES(5,72,'5:72','Mazima baakaafuwala abo abagamba nti ddala Katonda ye Masiya mutabani wa mariam, nga ate Masiya yagamba nti abange abaana ba Israil musinze Katonda Mukama wange era nga ye Mukama wa mmwe, mazima ddala oyo agatta ku Katonda ekintu ekirala Katonda yamuziyizaako okuyingira e jjana, nga n’obuddobwe muliro. Era abeeyisa obubi tebagenda kuba namutaasa yenna.');
INSERT INTO "african_dev" VALUES(5,73,'5:73','Mazima baakaafuwala abo abagamba nti mazima ddala Katonda yoomu ku basatu (abakola Katonda omu) ekituufu kiri nti tewali Katonda yenna okugyako Katonda omu era bwe bateekomeko ku bye boogera, abo abaakafuwala mu bo bajja kutuukwako e bibonerezo e biruma.');
INSERT INTO "african_dev" VALUES(5,74,'5:74','Abaffe, tebayinza kwenenyeza Katonda nebamusaba ekisonyiwo. Ate nga bulijjo Katonda musonyiyi waakisa.');
INSERT INTO "african_dev" VALUES(5,75,'5:75','Masiya mutabani wa Mariam tali kintu kirala kyonna okugyako okuba nti Mubaka wa Katonda era nga ddala ababaka bangi abaamukulembera, ne Maama we yali Mukazi omukkiriza ow''amazima bombi baalyanga emmere, tunula olabe engeri gyetubannyonnyolamu ebigambo ate tunula olabe engeri gyebawugulwa.');
INSERT INTO "african_dev" VALUES(5,76,'5:76','Gamba (Ggwe Nabbi Muhammad) nti abaffe musinza ebintu ebitali Katonda, ebitayinza kubatuusaako kabi konna wadde ekirungi, era Katonda yye bulijjo awulira era amanyi nnyo.');
INSERT INTO "african_dev" VALUES(5,77,'5:77','Gamba (Ggwe Nabbi Muhammad) nti abange mmwe abaaweebwa ekitabo temusukkanga ekigero ekyetaagisa nga mukola eddiini yammwe ebyobutaliimu, era temugobereranga okwagala kw’abantu, mazima bo basooka nebabula era nebabuza n’abantu bangi, era nebabulira ddala okuva ku kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(5,78,'5:78','Baakolimirwa abo Abaakaafuwala mu baana ba Israil ku lulimi lwa Dauda ne Issa Mutabani wa Mariam, ekyo nno kulwakujeema kwaabw,e era nga bulijjo baasukkanga (Amateeka g’a Katonda).');
INSERT INTO "african_dev" VALUES(5,79,'5:79','Tebeekomangako ku mize gyebaakolanga, kibi ddala ekyo kyebaakolanga.');
INSERT INTO "african_dev" VALUES(5,80,'5:80','Olaba bangi mu bo nga bafuula abakaafiiri mikwano gyaabwe egyomunda, kibi nnyo emyoyo gyaabwe gyekyabasalirawo ekyatuusa Katonda okubasunguwalira era nga baakutuula bugenderevu mu bibonerezo.');
INSERT INTO "african_dev" VALUES(5,81,'5:81','Singa baali bakkiriza Katonda ne Nabbi n’ebyassibwa gyali tebandibafudde mikwano gyaabwe egyomunda, naye bangi mu bo boonoonyi.');
INSERT INTO "african_dev" VALUES(5,82,'5:82','Ojja kukirabira ddala nti abasinga okuba ab''obulabe eri abo abakkiriza be bayudaaya naabo abagatta ku Katonda ebintu ebirala era ojja kukirabira ddala nti abasinga okuba ab''omukwano ogw''okumpi eri abo abakkiriza beebo abagamba nti mazima ffe tuli ba Kristayo, ekyo nno lwakuba nti mazima ddala mu bo mulimu abayivu mu bye ddiini, na Basosorodoti era nalwakuba nti bo tebeekuluntaza.');
INSERT INTO "african_dev" VALUES(5,83,'5:83','Bwebaba bawulidde e byassibwa ku Mubaka, olaba amaaso gaabwe nga gakulukusa amaziga olwamazima gebaba bategedde nebagamba nti ayi Mukama Katonda waffe tukkirizza tuwandiike mu bajulizi.');
INSERT INTO "african_dev" VALUES(5,84,'5:84','Tuyinza tutya obutakkiriza Katonda ku mazima gano agamaze okutujjira! ate nga era tuyaayaanira okuba nga Mukama omukuumi waffe atuyingiza e jjana awamu n''abantu abalongoofu.');
INSERT INTO "african_dev" VALUES(5,85,'5:85','Olwo nno Katonda naabasasula ku lw''ebyo bye bayogera (n''abayingiza) e jjana e zikulukutiramu e migga, mwebalibeera olubeerera era eyo y''empeera y’abakozi b’obulungi.');
INSERT INTO "african_dev" VALUES(5,86,'5:86','N''abo abaakaafuwala nebalimbisa e bigambo byaffe, abo be bantu bo muliro.');
INSERT INTO "african_dev" VALUES(5,87,'5:87','Abange mmwe abakkiriza temufuulanga Haramu e birungi ebyo Katonda bye yabakkiriza, mu ngeri y''emu temusukkanga e nsalo z''ebyo Katonda bye yabagaana, bulijjo mazima Katonda tayagala basukka nsalo (z’amateekage).');
INSERT INTO "african_dev" VALUES(5,88,'5:88','Era mulye Katonda bye yabagabirira, kavuna biba nga byakkirizibwa ate nga birungi, era mutye Katonda oyo mwe gwemukkiriza.');
INSERT INTO "african_dev" VALUES(5,89,'5:89','Katonda tabavunaana olwebirayiro byammwe byemulayira mu kusaaga, naye abavunaana lwebyo byemuba mukakasizza. (E kilayiro omuntu kyalayira ng’akakasa bwakimenya), omutango gw''akyo kuliisa abanaku kkumi nga baliisibwa e mmere gyemuliisa abantu abo mu daala eryo mu makati, (mu kitundu kyammwe) oba okubambaza, oba okuta omuddu. Omuntu aba tafunye e kimu ku ebyo ateekwa okusiiba ennaku ssatu. Ogwo nno gw’emutango gw’ebirayiro byammwe byemunalayiranga, mwegendereze nnyo okulayira kwa mmwe. Bwatyo Katonda bw''abannyonnyola e bigambobye, musobole okubeera abeebaza.');
INSERT INTO "african_dev" VALUES(5,90,'5:90','Abange mmwe abakkiriza mazima ddala omwenge ne zzaala, namasanamu n’okweraguza, n’obusaale bibi bya mu bikolwa bya Sitane, muteekwa okubyewala kibayambe okuba mu balituuka ku buwanguzi (ku lunaku lw’enkomerero).');
INSERT INTO "african_dev" VALUES(5,91,'5:91','Mazima ddala Sitane ayagala okussa wakati wa mmwe obulabe n’obukyayi olw’omwenge ne zzaala, era ebawugule ku kutendereza Katonda n’okusaala, abaffe e bikolwa e byo munaabireka.');
INSERT INTO "african_dev" VALUES(5,92,'5:92','Era mugondere Katonda, mugondere n’omubaka mwewalire ddala e bikolwa e bibi, bwemutakola mutyo olwo nno mumanye nti mazima ddala omubaka waffe kyateekwa okukola, kyekyokutuusa obubaka mulwatu.');
INSERT INTO "african_dev" VALUES(5,93,'5:93','Abo abakkiriza ne bakola e mirimu e mirungi tebavunaanwa ku lwebyo bye baalya oba bye baanywa e dda, kavuna batya Katonda ne bakkiriza, ne bakola e mirimu e mirungi ate ne batya Katonda ne bakkiriza ate ne batya Katonda era ne beeyisa bulungi, bulijjo Katonda ayagala abeeyisa obulungi.');
INSERT INTO "african_dev" VALUES(5,94,'5:94','Abange mmwe abakkiriza (bwe munaabeeranga mu mikolo gya Hijja ne Umrah) Katonda ajja kubagezesa n’ensonga y’okuyigga ebisolo nga mubikwata n’emikono gyammwe, era nga mubifumita n''amafumu gammwe olwo nno Katonda alyoke amanye oyo amutya mu mmwe, awamu n’okuba nti tamulabako, oyo yenna an’amenya e tteeka lino oluvanyuma lw’okumanya bino agenda kutuusibwako e bibonerezo e biruma e nnyo.');
INSERT INTO "african_dev" VALUES(5,95,'5:95','Abange mmwe abakkiriza temuyigganga, nga muli mu mizizo gya Hijja oba Umrah, oyo yenna mu mmwe atta e kisolo kyo muttale nga agenderedde, ateekwa okuwa omutango, nga awaayo mu nsolo e zirundibwa e yenkana neeri gyeyasse, (nga ekyo okukituukako), kisalibwawo abantu babiri abeesimbu mu mmwe, ekyo nga kirabo e kiteekwa okutuusibwa ku Kaaba. Bwekitaba ekyo, ateekwa okuwa omutango gwokuliisa abanaku, oba okusiiba e nnaku ezenkana omutango ogwo, ebyo byonna biri bwebityo alyoke abe nga akomba ku bukaawu bwekikolwa kye yetantala. Byo e byakulembera Katonda yabisonyiwa, naye oyo addamu Katonda agenda kumubonereza, bulijjo Katonda nantakubwa ku mukono asobola okubonereza oyo gwaba asazeewo okubonereza.');
INSERT INTO "african_dev" VALUES(5,96,'5:96','Mukkirizibwa okuvuba n''okulya e biva mu nnyanja ku lw''okuyimirizaawo obulamu bwa mmwe, n''obwabatambuze naye mugaaniddwa okuyigga ku lukalu ebbanga lyemumala nga muli mu mizizo gya Hijja ne Umrah, bulijjo mutye Katonda oyo gyali gye mulikunganyizibwa.');
INSERT INTO "african_dev" VALUES(5,97,'5:97','Katonda yassaawo Kaaba e nyumba e y''emizizo nga y’eyimiriddeko e ntambuza y’ebintu byonna e bikwata ku bantu era nateekawo e myezi e gyemizizo n’ebisolo e bitonebwa, nebisolo e birekebwa nga birambiddwa e byo byonna bibayambe okumanya nti mazima ddala Katonda amanyi ebyo byonna e biri mu ggulu omusanvu n''ebiri mu nsi, era nti mazima ddala Katonda bulijjo amanyidde ddala ebikwata ku buli kintu.');
INSERT INTO "african_dev" VALUES(5,98,'5:98','Muteekeddwa okumanya nti mazima Katonda muyitirivu wabibonerezo era nga ddala bulijjo Katonda musonyiyi wa kisa.');
INSERT INTO "african_dev" VALUES(5,99,'5:99','Omubaka talina kyavunaanwa, okugyako okubatuusaako obubaka, era bulijjo Katonda amanyi byemukola mulwatu ne bye mukweka.');
INSERT INTO "african_dev" VALUES(5,100,'5:100','Gamba (ggwe Nabbi Muhammad nti) e kibi n''ekirungi tebifaanana newaakubadde nga oluusi osikirizibwa obungi bw''ekibi, kale mutye Katonda abange mmwe abalina amagezi agakola obulungi mulyoke mube mu balituuka ku buwanguzi (ku lunaku lw''enkomerero).');
INSERT INTO "african_dev" VALUES(5,101,'5:101','Abange mmwe abakkiriza temubuuzanga e bikwata ku bintu, nga singa bibategeezeddwa byandibasudde mu ntata, anti singa mubibuuza nga Kur’ani e kyakka bijja kubategeezebwa (ate bibakaluubirire), so nga Katonda teyabisiinyaako, anti bulijjo Katonda musonyiyi wa kisa.');
INSERT INTO "african_dev" VALUES(5,102,'5:102','Mazima abantu b''ebibiina e byabakulembera baabibuuzanga ate oluvanyuma byabakaafuwaza.');
INSERT INTO "african_dev" VALUES(5,103,'5:103','Katonda ssi ye yateekawo (e byokulondobamu ebisolo e bimu e birundibwa n’ebiweebwa amasanamu) nga okusalako okutu kw’ensolo bweba e zadde e nzaalo e ziwera, wadde eyo gyebaawulako nga y’alubaale, wadde e kyokwevuma ensolo e zaala e nkazi zokka, wadde e kyokwekwasa seddume w''e ngamiya e zaazisizza e ngamiya e nnyingi. Naye mazima ddala abo abaakaafuwala (mu kuwanuuza kwa bwe okwo), batemerera Katonda, wabula bulijjo bangi mu bo tebategeera.');
INSERT INTO "african_dev" VALUES(5,104,'5:104','Bwe baba bagambiddwa nti mujje eri e byo Katonda byeyassa, era mujje eri omubaka, bagamba nti bye twasangako bakadde baffe bitumala (abaffe bagamba batyo) wadde nga bakadde baabwe baali tebamanyi kintu kyonna era nga tebayinza kulungama.');
INSERT INTO "african_dev" VALUES(5,105,'5:105','Abange mmwe abakkiriza mwefeeko. Oyo e yabula tayinza kubatuusaako kabi kavuna mmwe mulungama, ewa Katonda mwenna yeeri obuddo bwa mmwe, olwo nno alyoke abategeeze byonna byemwali mukola.');
INSERT INTO "african_dev" VALUES(5,106,'5:106','Abange mmwe abakkiriza omu ku mmwe bwawulira, nga ayinza okuba nga asemberedde okufa, mussengawo abajulizi babiri abeesimbu mu mmwe nga akola ekiraamo, oba abalala babiri abatali ba mu mmwe. Bwe mubanga ku safari nemutuukibwako ekizibu kyokufa (abajulizi ababiri) mubatuuza oluvanyuma lw’esswala nebalayira ku linnya lya Katonda, ekyo mukituukako bwe muba nga mufunye okubuusabuusa (era bateekeddwa okugamba nti) tetugenda kukkiriza kukyusa kiraamo kino olwekintu kyonna, newaakubadde nga gwetuwaako obujulizi wa luganda lwaffe olw,okumpi, era tetugenda kukweka bujulizi bwa Katonda anti bwetukikola tubeera mu boonoonyi.');
INSERT INTO "african_dev" VALUES(5,107,'5:107','Bwekizuulwa nti abajulizi beekobanye nebakola ekibi ku nsonga y’obujulizi, olwo mangu ddala musseewo abalala babiri badde mu kifo kyabwe bombi, nga baggyibwa mu bantu bali ababiri mwe baggyibwa, nebalayira Katonda (era nebagamba nti) obujulizi bwaffe bwa maanyi okusinga obwa bali ababiri era tetugenderedde kubaweebuula anti bwetukola ekyo, tuba tugudde mu luse lwa beeyisa obubi.');
INSERT INTO "african_dev" VALUES(5,108,'5:108','Okukola ekyo, kye kisinga obulungi ku kuyamba okuba nga bawa obujulizi mu butuufu bwa bwo, oba batye e birayiro (byabwe) okugobwa oluvanyuma lw''okulayira kwabwe, mutye Katonda era muwulire, bulijjo Katonda talungamya bantu bajeemu.');
INSERT INTO "african_dev" VALUES(5,109,'5:109','Mujjukire olunaku Katonda lwalikunganya ababaka nagamba nti abantu baabaanukula batya, ne bagamba nti: tetumanyi bye baagamba anti mazima bulijjo ggwe omanyidde ddala e bikusike.');
INSERT INTO "african_dev" VALUES(5,110,'5:110','Era mufumiitirize Katonda bwaligamba nti owange Isa mutabani wa Mariam, jjukira e byengera byange bye nnakuwa ne bye nnawa Maamawo, bwe nnakuwagira ne mwoyo mutukuvu n''oyogera n’abantu ng’okyali mu kibaya, ne bwe wasajjakula, era jjukira bwennakuyigiriza okuwandiika n’okukozesa amagezi ne Taurat ne Injili, era jjukira bwe wasobola okubumba mu ttaka e kibumbe kye kinyonyi ku lw’okukkiriza kwange, n’okifuuwamu ne kiba e kinyonyi ku lw''okukkiriza kwange, era n’osobola okuwonya ba Muzibe n’abolukeke, byonna lwa kukkiriza kwa nge, era jjukira bwe wasabiranga abafu ne bazuukira olw''okukkiriza kwange, era jjukira bwe nnakuwonya abaana ba Israil bwe wabajjira n’obunnyonnyofu, abaakaafuwala mu bo ne bagamba nti bino byaleese si kirala okugyako ddogo e ryeyolefu.');
INSERT INTO "african_dev" VALUES(5,111,'5:111','Era jjukira bwe nnatumira abayigirizwabo nti munzikirize n’omubaka wange ne bagamba nti tukkirizza, era naawe julira okakase nti mazima ddala tuli basiramu.');
INSERT INTO "african_dev" VALUES(5,112,'5:112','Era jjukira abayigirizwa bwe baagamba Isa mutabani wa Mariam nti abaffe Katondawo asobola okutussiza ekijjulo okuva mu ggulu, naagamba nti mutye Katonda bwe muba nga ddala muli bakkiriza.');
INSERT INTO "african_dev" VALUES(5,113,'5:113','Nebagamba nti twagala tukiryeko, era e mitima gyaffe gitebenkere, era tumanye nti wewaawo byo tugamba byonna bya mazima, era naffe tufuuke abajulizi ku ekyo.');
INSERT INTO "african_dev" VALUES(5,114,'5:114','Isa mutabani wa Mariam naagamba nti ayi Mukama omulabirizi waffe tussize e kijjulo okuva mu ggulu, kibe e mbaga gyetuli eri abaliwo muffe n’abalijja oluvanyuma, era nga bujulizi okuva gyoli, era tukusaba otugabirire. Ggwe mugabi asinga.');
INSERT INTO "african_dev" VALUES(5,115,'5:115','Katonda naagamba nti mazima nze nja kukibassiza naye oyo yenna anaakaafuwala oluvanyuma lw’okujja kwakyo, mazima ddala ngenda kumubonereza olubonereza lwe ssigenda kubonereza muntu mulala yenna.');
INSERT INTO "african_dev" VALUES(5,116,'5:116','Era jjukira Katonda bwe yagamba nti owange Isa Mutabani wa Mariam ggwe wagamba abantu nti nze ne Mmange ffembi mutufuule ba Katonda, muve ku Katonda. Naagamba nti Mukama omulabirizi wange oli musukkulumu, siyinza kwetantala kwogera kitali kyange, mu butuufu, bwe mba nakyogera mazima wakimanya, anti omanyi byonna ebiri mu nze atenga nze simanyi biri mu ggwe, (ekireeta e njawulo eyo) anti mazima ddala ggwe omanyi e byekusifu.');
INSERT INTO "african_dev" VALUES(5,117,'5:117','Ssaabagamba okugyako kye wandagira, e ky’okubagamba nti: musinze Katonda mukama omulabirizi wange era nga ye mukama omulabirizi wa mmwe, era nali mujulizi ku byonna bye baakolanga e bbanga lye nnamala nga ndi nabo, naye bwe wantwala, ggwe wasigala omanyi byonna bye bakola. Anti bulijjo ggwe osobola okumanya e bikwata ku buli kintu.');
INSERT INTO "african_dev" VALUES(5,118,'5:118','Bwoba wa kubabonereza, mazima bo baddu bo, naye bw''obasonyiwa, mazima ddala ggwe nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(5,119,'5:119','(Ku lunaku olwo) Katonda agenda kugamba nti, luno lunaku abaayogera amazima, amazima gaabwe gagenda kubagasa, balina e jjana e zikulukutiramu e migga, bagenda kuzibeeramu olubeerera, Katonda yabasiima nabo nebeeyanza, okwo nno kwe kwesiima okusukkulumu.');
INSERT INTO "african_dev" VALUES(5,120,'5:120','Obufuzi bwe ggulu omusanvu ne nsi n''ebirimu bwa Katonda, era bulijjo Katonda musobozi wa buli kintu.');
INSERT INTO "african_dev" VALUES(6,1,'6:1','Ebitendo byonna e birungi bya Katonda oyo eyakola e ggulu omusanvu ne nsi n’assaawo e nzikiza ne kitangaala, wabula abo abagyemera Mukama omulabirizi waabwe bava ku mazima.');
INSERT INTO "african_dev" VALUES(6,2,'6:2','Yye yooyo (Katonda) e yabatonda nga abajja mu ttaka, oluvanyuma naasalawo buli omu mu mmwe e bbanga lya naawangaala, ate nassaawo e bbanga e ddala e ggere e rimanyiddwa yye yekka, ensi lwerikoma. Ate oluvanyuma mmwe mubuusabuusa.');
INSERT INTO "african_dev" VALUES(6,3,'6:3','Era yye, ye Katonda mu ggulu omusanvu ne nsi, amanya bye mukola mu kyama ne bye mukola mu lwatu, era amanya byonna bye mukola.');
INSERT INTO "african_dev" VALUES(6,4,'6:4','Tewali kabonero konna kabajjira mu bubonero bwa Katonda okugyako bwonna babwawukanako.');
INSERT INTO "african_dev" VALUES(6,5,'6:5','Mazima baalimbisa amazima bwe gaabajjira, naye luliba lumu ebigambo e bituufu e bikwata kwebyo bye baajeejanga biri bajjira.');
INSERT INTO "african_dev" VALUES(6,6,'6:6','Abaffe tebalaba e mirembe e meka agyaliwo oluberyeberye lwabwe gyetwazikiriza! twabawa okwegazaanyiza mu nsi, okusinga ku ngeri gyetwawa mmwe okwegazanyizaamu, ne tubawa e ggulu okuba nga libafukirira e nkuba mu bungi, ne tussawo e migga nga gikulukutira wansi waabwe, naye era ne tubazikiriza olwebibi byabwe ne tuleeta oluvanyuma lwabwe omulembe omulala.');
INSERT INTO "african_dev" VALUES(6,7,'6:7','Singa twassa ku ggwe e kitabo e kikoleddwa mu mpapula ne bakikwatako n’emikono gyabwe, abo abaakaafuwala bandigambye nti, bino temuli okugyako ddogo lyennyini.');
INSERT INTO "african_dev" VALUES(6,8,'6:8','Era nebagamba nti singa wassiddwa ku ye Malayika, naye singa twassa Malayika e nsonga yandiggweredde awo era ne batarindirizibwa.');
INSERT INTO "african_dev" VALUES(6,9,'6:9','Era singa twasalawo okuweereza Malayika twandimuweerezza nga muntu, era e kibatabula kyandibadde kibatabula.');
INSERT INTO "african_dev" VALUES(6,10,'6:10','Ababaka bangi baajeejebwa nga tonnatumwa, abo abaajeeja mu bo ababaka, baatuukwako e bibonerezo by''okujeeja kwabwe.');
INSERT INTO "african_dev" VALUES(6,11,'6:11','Gamba nti, mutambule mu nsi olwo nno mulabe e nkomerero y’abaalimbisa yali e tya.');
INSERT INTO "african_dev" VALUES(6,12,'6:12','Gamba nti, byani e byo e biri mu ggulu omusanvu ne nsi (bwomala) gamba nti bya Katonda, yeekakasaako okusaasira era nga agenda kubakunganyiza ddala ku lunaku lw’enkomerero, tekiriimu kubuusabuusa (wabula) abo abafaafaganirwa e myoyo gyabwe tebagenda kukkiriza.');
INSERT INTO "african_dev" VALUES(6,13,'6:13','Era bibye (Katonda) byonna e bibeerawo e kiro n’emisana, anti bulijjo yye, yaawulira era amanyi e nnyo.');
INSERT INTO "african_dev" VALUES(6,14,'6:14','Gamba nti: ndeke Katonda neeteerewo e kintu e kirala kyonna e kitali yye! nga ye mukwano gwange ow’omunda, nga ate yye (Katonda) yeeyatonda e ggulu omusanvu ne nsi, era yye, yaaliisa ng’ate talisiibwa gamba nti, mazima nze nalagirwa okuba omusaale mwabo abeewaayo era tobeeranga mu bagatta Katonda n''ebintu e birala.');
INSERT INTO "african_dev" VALUES(6,15,'6:15','Gamba, nti mazima nze ntya nnyo e bibonerezo by''olunaku oluzito singa mba njeemedde mukama omulabirizi wange.');
INSERT INTO "african_dev" VALUES(6,16,'6:16','Omuntu alyesambisibwa ku byo olunaku olwo, mazima Katonda aliba amusaasidde era e kyo bwe buwanguzi obwa nnamaddala.');
INSERT INTO "african_dev" VALUES(6,17,'6:17','Singa Katonda akutusaako akabi tewali ayinza kukakuwonya okugyako yye, singa aba akuwadde e birungi yye Katonda muyinza wa buli kintu.');
INSERT INTO "african_dev" VALUES(6,18,'6:18','Era yye, ye Nantalemwa, alina mu mikono gye e nsonga z’abaddu bonna, anti yye ye mugoba nsonga amanyi buli kimu.');
INSERT INTO "african_dev" VALUES(6,19,'6:19','Gamba nti kintu ki e kisinga obukulu okuba nga bwe bujulizi, gamba nti Katonda ye mujulizi wakati wange nammwe, era naweebwa Kur’ani eno nga bubaka mbe nga mbatiisa nayo n’abo beerituukako abatali mmwe, ate mmwe muyinza okuleeta obujulizi nti ku Katonda kweyongerako ba katonda abalala, gamba nti mazima ddala yye Katonda ali omu, era mazima ddala neesambye e byo byemugatta ku Katonda.');
INSERT INTO "african_dev" VALUES(6,20,'6:20','Abo be twaawa e kitabo bamutegeera (Nabbi Muhammad) nga bwebategeera abaana baabwe, abo abaafaafaganirwa e myoyo gyabwe (nebatategeera ntegeera ntuufu) abo nno bo tebakkiriza (byo bagamba).');
INSERT INTO "african_dev" VALUES(6,21,'6:21','Ani yeeyisa obubi okusinga oyo atemerera Katonda nga ddala amanyi nti alimba! oba n’alimbisa e bigambo bya Katonda. Anti mazima abeeyisa obubi tebagenda kwesiima.');
INSERT INTO "african_dev" VALUES(6,22,'6:22','Ku lunaku lwetulibakungaanya bonna era ne tugamba abo abaagatta ku Katonda e bintu e birala nti, ba katonda ba mmwe abo be mwagambanga baluwa.');
INSERT INTO "african_dev" VALUES(6,23,'6:23','Awo nno bwe balimala okugwa mu kizibu (olwo kubuuzibwa e kibuuzo e kyo) tebaliba na kya kukola okugyako okugaana ne bagamba nti, tulayira Katonda Mukama omulabirizi waffe tetubangako mu bagatta ku Katonda bintu birala.');
INSERT INTO "african_dev" VALUES(6,24,'6:24','Laba e ngeri gye beerimbamu ne bibaggwaako e byo bye baagunjaawo.');
INSERT INTO "african_dev" VALUES(6,25,'6:25','Era mu bo mulimu abawulira byoyogera, naye nga ku mitima gyabwe twassaako e kibikka ne batasobola kubitegeera n’amatu gaabwe ne tuteekamu e nvumbo, ne bwebalaba e kyamagero kyonna tebakikkiriza, ne batuusa okujja gyali nga bakuwakanya abo abaakaafuwala ne bagamba nti (byonna byayigiriza) tebirina kye biri okugyako okuba nti nfumo z’abedda.');
INSERT INTO "african_dev" VALUES(6,26,'6:26','Nga nabo bagaana abantu okujja eri Nabbi, era nga nabo be nnyini bamwesamba, (mweekyo) tebazikiriza okugyako myoyo gya bwe naye tebategeera.');
INSERT INTO "african_dev" VALUES(6,27,'6:27','Singa olisobola okubalaba bwe baliyimirizibwa ku muliro, olwo nno ne bagamba nti nga tulabye singa tuzzibwaayo (ku nsi) ne tutalimbisa bigambo bya Mukama omulabirizi waffe olwo nno tube mu bakkiriza.');
INSERT INTO "african_dev" VALUES(6,28,'6:28','Olwo nno biriba bye yolese gye bali e byo bye baakwekanga mu kusooka, ne bwe baaliziddwaayo baalizzeemu okukola e kya bagaanibwa, era bo baliba balimba (mu byonna bye baliba boogera).');
INSERT INTO "african_dev" VALUES(6,29,'6:29','Era bagamba nti tewali bulamu bulala, okugyako buno bwe tuwangala obw''okunsi, era tetugenda kuzuukizibwa.');
INSERT INTO "african_dev" VALUES(6,30,'6:30','Waalisaanye olabe, e mbeera gye balibeeramu nga bayimiriziddwa mu maaso ga Mukama omulabirizi waabwe, n’abagamba nti kino kye mulaba si mazima, bagenda kugamba nti bwe gutyo bwe guli ayi Mukama omulabirizi waffe naagamba nti: kale nno mukombe ku bukaawu bwe bibonerezo olwe''byo bye mwawakanya.');
INSERT INTO "african_dev" VALUES(6,31,'6:31','Mazima baafaafaganirwa abo abaalimbisa okusisinkana Katonda, okutuusiza ddala olunaku lwe nkomerero lwe lulibatuukako e kibwatukira, ne bagamba nti nga tulabye olw''ebyo bye twayonoona mu nsi, olwo nno nga beetisse e bibi bya bwe ku migongo gyabwe abange bibi ddala bye bakola.');
INSERT INTO "african_dev" VALUES(6,32,'6:32','Obulamu bwe nsi tebuli okugyako muzannyo na kusanyuka, naye mazima ddala e nyumba e yenkomerero y''esinga obulungi eri abo abatya Katonda, abaffe temutegeera.');
INSERT INTO "african_dev" VALUES(6,33,'6:33','Mazima tumanyi nti ddala bye boogera bikunyiiza, mazima ddala bo tebakulimbisa naye mazima ddala abeeyisa obubi e bigambo bya Katonda bye bawakanya.');
INSERT INTO "african_dev" VALUES(6,34,'6:34','Mazima ababaka bangi olubereberye lwo baalimbisibwa ne bagumiikiriza ku e byo byebalimbisibwa, era ne bayisibwa bubi okutuusa okutaasa kwaffe lwe kwabajjira era tewali akyusa bigambo bya Katonda. Mazima ojjiddwa e bimu ku bigambo e bikwata ku babaka.');
INSERT INTO "african_dev" VALUES(6,35,'6:35','E ky''okukwawukanako bwe kiba nga kikuyitiriddeko, kale nno bwoba nga osobodde okufuna omukwesese mu ttaka oba amadaala agakutwala waggulu obeere ng’obaleetera e kya magero kyonna (kola e kyo, naye nga tekirina makulu kubanga) singa Katonda yayagala yaali bagattidde ku bulungamu, kale (Nabbi Muhammad) tobeeranga mu batategeera.');
INSERT INTO "african_dev" VALUES(6,36,'6:36','Abo abawulira okuwulira okutuufu, mazima bajja kwanukula okukoowoola (kwo), sso abafu Katonda agenda kubazuukiza era gyali gye balizzibwa.');
INSERT INTO "african_dev" VALUES(6,37,'6:37','(Abagatta Katonda e bintu e birala) bagamba nti singa assiddwaako e kya magero kyonna okuva e wa Mukama omulabirizi we, bagambe nti mazima Katonda asobola okussa e kyamagero kyonna ,naye abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(6,38,'6:38','Tewali kintu kyonna kitambula ku nsi nga kiramu ka kibe kinyonyi e kibuuka n''ebiwawaatiro bya kyo okugyako bulijjo bikolera mu bibinja nga mmwe, tewali kintu kyonna kye twaleka okugyako nga twakiwandiika mu Lauhul Mahfuudhu (Eggwandisizo lya Katonda) oluvanyuma eri Mukama omulabirizi wa bwe gye bagenda okunganyizibwa.');
INSERT INTO "african_dev" VALUES(6,39,'6:39','Abo bonna abaalimbisa e bigambo byaffe ba kiggala ba kasiru bali mu bizikiza. Omuntu Katonda gwaba ayagadde abule amubuza, ate gwaba ayagadde okulungamya amusobozesa okulungama eri e kkubo e ggolokofu.');
INSERT INTO "african_dev" VALUES(6,40,'6:40','Bagambe ggwe (Nabbi Muhammad) nti mulaba mutya singa biba bibajjidde e bibonerezo bya Katonda oba olunaku lwe nkomerero nga lwe lubajjidde, (mu mbeera eyo muyinza) okulaajanira atali Katonda omu bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(6,41,'6:41','E kituufu kiri nti mulaajanira Katonda wa mmwe e yabakola yekka, n''abaggyirawo e kyo kye mumumulaajanidde bwaba nga ayagadde nemwerabira n''okwerabira e byo bye mubadde mumugattako.');
INSERT INTO "african_dev" VALUES(6,42,'6:42','Mazima twatuma ababaka eri e bibinja bya bantu oluberyeberyelwo, netubagezesa n''ebizibu by''obwavu n’obubenje balyoke beetowaze (basinze Katonda omu).');
INSERT INTO "african_dev" VALUES(6,43,'6:43','Kale nno singa e bizibu bwe byabajjira beetoowaliza (Katonda, kyandibayambye) naye e myoyo gyabwe gyeyongera kuwaganyala. Sitane naabawundira e byo bye baali bakola.');
INSERT INTO "african_dev" VALUES(6,44,'6:44','Kavuna baaleka e byo bye bajjukizibwa na byo, netubaggulirawo e miryango gya buli kintu mu byenfuna, okutuusiza ddala lwe baayitiriza okweyagalira mu e byo bye twabawa, netubabonereza mbagirawo nebatuuka n’okuba nti tebalina kantu konna.');
INSERT INTO "african_dev" VALUES(6,45,'6:45','Katonda yabazikiriza okutuusiza ddala ku asembayo mwabo abeeyisa obubi, bulijjo okutenderezebwa kwonna kwa Mukama omulabirizi w''ebitonde byonna.');
INSERT INTO "african_dev" VALUES(6,46,'6:46','Bagambe ggwe (Nabbi Muhammad) mulaba mutya singa Katonda abafuula ba kiggala era ba muzibe, naateeka ne nvumbo ku mitima gya mmwe, katonda ani atali Katonda omu ayinza okubibaddiza, laba e ngeri gye tunnyonnyolamu obubonero (bwange) ate bo ne babuvaako.');
INSERT INTO "african_dev" VALUES(6,47,'6:47','Gamba, mmwe mulaba nti singa e bibonerezo bya Katonda bibatuukako e kibwatukira, oba ne bijja nga mutegedde, waliwo abayinza okubonerezebwa abalala okugyako abo abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(6,48,'6:48','Bulijjo tetutuma babaka okugyako nga basanyusa abakozi b’obulungi era nga bwe batiisa abakozi b’ebibi, oyo yenna akkiriza n’akola e mirimu e mirungi abo tebalina kutya, era tebagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(6,49,'6:49','Ate abo abalimbisa e bigambo byaffe, e bibonerezo bigenda kubatuukako ol''webyo bye baakola.');
INSERT INTO "african_dev" VALUES(6,50,'6:50','Gamba ggwe (Nabbi Muhammad nti) si bagamba nti nze nnina amawanika ga Katonda, wadde okubagamba nti manyi e byekusifu, nga bwe siyinza kubagamba nti mazima nze ndi Malayika, tewali kyengoberera okugyako obubaka obutumwa gyendi. Gamba nti muzibe nooyo alaba benkana, abaffe temufumiitiriza.');
INSERT INTO "african_dev" VALUES(6,51,'6:51','Tiisa nayo (Kur’ani) abo abatya okuzuukizibwa ne bazzibwa eri Mukama omulabirizi waabwe, nga oggyeko yye, tebalina wa mukwano ffa nfe wadde omuwolereza. Olwo nno balyoke babeere mu batya Katonda.');
INSERT INTO "african_dev" VALUES(6,52,'6:52','Togobanga abo abasinza Mukama omulabirizi waabwe e nkya ne ggulo nga bakikola ku lulwe, e bibavunaanibwa tewali na kimu kikubanjibwa,nga naawe e bikuvunaanibwa tewali kye babanjibwa, olyoke obagobaganye, ekijja okukuteeka mu beeyisa obubi.');
INSERT INTO "african_dev" VALUES(6,53,'6:53','Mu ngeri yeemu abantu twabagezesa nga tukozesa bannaabwe (abamu netubafuula abagagga abalala baavu, abamu) nebatuuka okugamba nti abo mu ffe fenna Katonda beyasalawo okugonnomolako e kyengera (e kyobukkiriza), (naye okutuuka okwogera e kyo tebamanyi nti) bulijjo Katonda amanyi nnyo e bikwata ku baddu abeebaza.');
INSERT INTO "african_dev" VALUES(6,54,'6:54','Bwebajja gyoli abo abakkiriza e bigambo bya ffe, gamba nti emirembe gibeere ku mmwe, Mukama omulabirizi wa mmwe yeekakasaako okusaasira, nekiba nti oyo yenna mu mmwe akola ekibi mu butamanya oluvanyuma lwakyo ne yeenenya, era n’akola e mirimu e mirungi, mazima bulijjo Katonda musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(6,55,'6:55','Bwe tutyo bwe tunnyonnyola e bigambo byaffe kisobozese e kkubo lya boonoonyi okweyoleka.');
INSERT INTO "african_dev" VALUES(6,56,'6:56','Gamba (Ggwe Muhammad) mazima nze naganibwa okusinza e byo bye musaba nemuleka Katonda omu, gamba nti sijja kugoberera kwagala kwa mmwe, anti bwenakikola njakuba mbuze, era sijja kubeera mu balungamu.');
INSERT INTO "african_dev" VALUES(6,57,'6:57','Gamba nti mazima nze ndi ku mazima agava ewa Mukama omulabirizi wange, naye nga mmwe mugalimbisa, kye mwagala kijje amangu (e kyebibonerezo) tekiri mu mikono gyange, okusalawo tekuli wantu wonna okugyako wa Katonda, byonna byayogera mazima, era bulijjo yaasinga abalamuzi bonna.');
INSERT INTO "african_dev" VALUES(6,58,'6:58','Singa nze nnina kye mwagala kijje mangu (kyandizze) e nsonga wakati wange nammwe neggwa, era bulijjo Katonda amanyi nnyo abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(6,59,'6:59','Era yye y''alina e bisumuluzo bye by’ekusifu tewali abimanyi okugyako yye, era amanyi e biri ku lukalu ne mu nyanja, era tewali kikoola kigwa okugyako nga akimanya, era tewali mpeke eri mu bizikiza bye ttaka wadde e kibisi oba e kikalu kyonna okugyako nga kiwandiike mu kitabo e kitottola buli kintu.');
INSERT INTO "african_dev" VALUES(6,60,'6:60','Era yye yooyo abeebasa e kiro (nemuba nga abafudde) era amanyi bye mwakoze e misana, ate oluvanyuma abazza e ngulu nga bukedde (ne kifanana nga abazuukizza okuva mu kufa,) ekyo kisobozese buli kitonde okumalayo ebbanga eryakigererwa. Oluvanyuma gyali yeeri obuddo bwa mmwe, olwo nno abategeeza byonna bye mwakolanga.');
INSERT INTO "african_dev" VALUES(6,61,'6:61','Era yye yaalina obuyinza obwenkomeredde ku baddube, era abassaako (ba Malayika) abakuumi, okutuusa omu ku mmwe bwaba atuuse okufa, ababaka baffe bamuggyamu obulamu, era bulijjo tebasubwa.');
INSERT INTO "african_dev" VALUES(6,62,'6:62','Oluvanyuma ne bazzibwa ewa Katonda Mukama waabwe owanamaddala, mukimanye nti obuyinza bwonna bubwe, era nga bulijjo mwangu mu kubala okusinga ababazi bonna.');
INSERT INTO "african_dev" VALUES(6,63,'6:63','Gamba ani ayinza okubataasa ku nzikiza yo''lukalu n’enizikiza y''enyanja, nemuba nga mumusaba olw''okulaga obugonvu mu lwatu ne mu kyama nemutuuka n''okugamba nti singa (gwetusaba) atuwonya obuzibu buno tujja kubeerera ddala mu beebaza.');
INSERT INTO "african_dev" VALUES(6,64,'6:64','(Tebayinza kumufuna n’olwekyo) gamba nti Katonda yajja okubibawonya, na mu buli kizibu kyonna. E kyo bwe kiggwa ate nemugatta ku Katonda e bintu e birala.');
INSERT INTO "african_dev" VALUES(6,65,'6:65','Gamba nti yye (Katonda) yaasobola okubasindikira e kibonerezo kyonna, nga kiva wa ggulu ne wansi w''ebigere bya mmwe, n’abafuula e bibinja e byenjawulo n’akombesa abamu mu mmwe ku bukaawu bwa bannaabwe. Weetegereze engeri gyetunnyonnyolamu e bigambo balyoke babe nga bategeera.');
INSERT INTO "african_dev" VALUES(6,66,'6:66','(Kur’ani) abantu bo bagirimbisa, ate nga ddala yo mazima meereere, bagambe nti n’olwekyo si nze mbasalirawo.');
INSERT INTO "african_dev" VALUES(6,67,'6:67','Buli kintu kibaako e kkomo, (nakino) bwe kirituuka mugenda kumanyira ddala.');
INSERT INTO "african_dev" VALUES(6,68,'6:68','Era bwolabanga abo abakinaggukira mu bigambo byaffe, baviire, okutuusa lwe banaayingira mu mboozi e ndala, singa Sitane akwerabiza (n''obeerawo) wonna wojjukirira togenda mu maaso ng’otuula n’abantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(6,69,'6:69','Era abo abatya Katonda tebavunaanyizibwa ku kintu kyonna kikwata ku bantu abo, (wabula omulimu gwabwe) kubuulirira oba olyawo balyoke batye Katonda.');
INSERT INTO "african_dev" VALUES(6,70,'6:70','Vva kwabo abaafuula e ddiini yaabwe e kintu e kyomuzannyo era e kyekinyumu, obulamu bw''ensi ne bubagayaaza, naye buulirira nga okozesa Kur’ani omwoyo guleme kuzikirizibwa olw''ebyo bye yakola, tegulina nga oggyeko Katonda, w’amukwano wadde omuwolereza, nebweguliwaayo e nnunuzi e faanana etya, tegenda kukkirizibwa kuva gyeguli, abo beebo abaazikirizibwa olw''ebyo bye baakola, baliweebwa e byokunywa e by’olweje, era balissibwako e bibonerezo e biruma ennyo olw’ebyo bye baawakanyanga.');
INSERT INTO "african_dev" VALUES(6,71,'6:71','Gamba nti, tuleke Katonda tusinze e byo e bitatugasa! era e bitayinza kukutusaako kabi! tutambuzibwe e kyenyumanyuma oluvanyuma lwa Katonda okutulungamya! tufaanane nga oyo Sitane gwezibuza mu nsi, n’asoberwa naaba mu masangazira so nga alina mikwano gye abamuyita okujja eri obulungamu, (nga bagamba nti) jangu eno (ekyo bwekiba nga tekisobola kwemalirira) gamba nti mazima okulungamya kwa Katonda, bwe bulungamu bwokka, era twalagirwa okwewa ewa Mukama omulabirizi w’ebitonde.');
INSERT INTO "african_dev" VALUES(6,72,'6:72','(Era twalagirwa nti) muyimirizeewo e sswala era mutye Katonda era yye yooyo, gyali gyemulikunganyizibwa.');
INSERT INTO "african_dev" VALUES(6,73,'6:73','Era yye yooyo, eyakola e ggulu omusanvu n''ensi nga byebyo bye nnyini, era obufuzi buliba bubwe olunaku e ngombe lwelifuyibwa, omumanyi w’ebyekusifu n''ebirabikako, era yye ye mugoba nsonga, omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(6,74,'6:74','Era jjukira Ibrahim bweyagamba kitaawe Azara nti: amasanamu gofuula Katonda! mazima nze nkulaba n’abantubo nga muli mu bubuze obweyolefu.');
INSERT INTO "african_dev" VALUES(6,75,'6:75','Mu ngeri y’emu twalaga Ibrahim ebiri mu bwengula ne nsi (nengeri namateeka kwebitambulira) alyoke abeere mu baddu abannyikivu mu bukkiriza.');
INSERT INTO "african_dev" VALUES(6,76,'6:76','Ekiro bwe kyamubikka, yalaba e munyenye naagamba nti oyo ye Mukama omulabirizi wange, e munyenye bweyabula (Ibrahim) naagamba nti nze saagala ba katonda babulawo.');
INSERT INTO "african_dev" VALUES(6,77,'6:77','Bweyalaba omwezi nga gwetadde naagamba nti oyo ye Mukama omulabirizi wange, omwezi bwe gwabula (Ibrahim) naagamba nti Mukama omulabirizi wange bwatannungamye nja kubeerera ddala mu bantu ababuze.');
INSERT INTO "african_dev" VALUES(6,78,'6:78','Ate nno bweyalaba nga e njuba evuddeyo naagamba nti: ono ye Mukama omulabirizi wange, ono ye munene ddala, (Enjuba) bweyagwa naagamba nti abange abantu bange nze e byokugatta ku Katonda e bintu e birala si biriimu.');
INSERT INTO "african_dev" VALUES(6,79,'6:79','Mazima nze njolekezza e kyenyi kyange eri oyo eyatonda e ggulu omusanvu ne nsi, nga njawukanira ddala ku nsinza zonna. Siri wa mu bagatta Katonda na bintu birala.');
INSERT INTO "african_dev" VALUES(6,80,'6:80','Abantube ne bamuwakanya naagamba nti mumpakanya ku Katonda nga ate mazima yannungamya, era nga sitya e byo bye mumugattako (okuba nga bintusaako akabi), be ppo nga Mukama omulabirizi wange alina kyakkirizza, Mukama omulabirizi wange yeetooloola buli kintu mu kumanya, abaffe temwebuulirira.');
INSERT INTO "african_dev" VALUES(6,81,'6:81','Nyinza ntya okutya e byo bye mugatta ku Katonda, nga ate mmwe temutya kugatta ku Katonda bintu birala bya tabassizangako bujulizi. ku bibinja byombi (ffe nammwe) kiriwa e kisaanidde okulowooza nti kiri mirembe bwe muba nga mumanyi.');
INSERT INTO "african_dev" VALUES(6,82,'6:82','(E kituufu kiri nti) abo abakkiriza ne batatabula kweyisa bubi (Shirik), mu bukkiriza bwabwe, abo nno be balina e mirembe era abo be baalungamizibwa.');
INSERT INTO "african_dev" VALUES(6,83,'6:83','Obwo bwe bujulizi bwaffe bwe twawa Ibrahim akozese ku bantube (e kyamussa mu kifo e kyenjawulo), bulijjo tusitula amadaala gw’oyo gwetuba twagadde, anti mazima ddala Mukama omulabiriziwo agoba nsonga mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(6,84,'6:84','Era twamugabira (Ibrahim) Ishak ne Yakub, bombi twabalungamya nga n’okusooka twalungamya Nuhu, ne mu zadderye twalungamya Dauda ne Sulaiman ne Ayub ne Yusuf ne Musa ne Haruna, na bwekityo nno bwe tusasula abalongoofu.');
INSERT INTO "african_dev" VALUES(6,85,'6:85','Ne Zakariya ne Yahya ne Isa ne Eliasi, buli omu kwabo wa mu bakozi b’emirimu e mirungi.');
INSERT INTO "african_dev" VALUES(6,86,'6:86','Ne Ismail ne Iriyasa-a ne Yunus ne Luutu, buli omu ku bo twamusukkulumya ku bantu abalala.');
INSERT INTO "african_dev" VALUES(6,87,'6:87','Era (twalungamya abantu abalala) nga bava mu bakadde baabo ne bazzukulu baabwe ne baganda baabwe, era netubafuula ab''enjawulo, era netubalungamya ku kubo e ggolokofu.');
INSERT INTO "african_dev" VALUES(6,88,'6:88','Okwo kwe kulungamya kwa Katonda, alungamya na kwo oyo gwaba ayagadde mu baddube, naye singa baagatta e kintu e kirala kyonna ku Katonda, bandiyonoonekeddwa e byo bye baali bakola.');
INSERT INTO "african_dev" VALUES(6,89,'6:89','Abo beebo be twawa e kitabo n’obufuzi n’obwa Nabbi, kati bano bwe baba nga bawakanya e bintu e byo, mazima twabikwasa abantu abatagenda kubiwakanya.');
INSERT INTO "african_dev" VALUES(6,90,'6:90','Abo beebo Katonda be yalungamya, obulungamu bwabwe bwoteekwa okugoberera, ogambe nti, (kyenkola) si kibasabirako mpeera kyenkola, tekiri kyo (kintu kirala kyonna) okugyako okuba nti kujjukiza eri abantu bonna.');
INSERT INTO "african_dev" VALUES(6,91,'6:91','(Wabula Abayudaaya) tebaawa Katonda kitiibwa kimusaanira, bwe baagamba nti Katonda tassanga ku muntu yenna kintu kyonna.Gamba (gwe Nnabbi Muhammad) nti ani yassa e kitabo e kyo Musa kye yajja nakyo nga kitangaala era e kyokulungama eri abantu, (mmwe) kye mwafuula e mpapula zemulaga abantu, ate ne mukweka bingi so nga ate mwamanyisibwa bye mwali temumanyi, mmwe ne bakadde ba mmwe, bagambe nti Katonda ye yassa e kitabo e kyo, olwo nno obaleke mu bubuze bwabwe nga bazannya.');
INSERT INTO "african_dev" VALUES(6,92,'6:92','Kura’ni eno kitabo twakissa nga kya mukisa, nga kikakasa e byo e byakulembera, era okikozese nga otiisa abantu b’omu (Makkah), maama w''ebibuga, n''abantu bo''mu bitundu e bikyetoolodde. Bo abo abakkiriza olunaku lw’enkomerero bakkiriza e kitabo kino, era bulijjo e sswala zaabwe bazikuuma butiribiri.');
INSERT INTO "african_dev" VALUES(6,93,'6:93','Ani eyeeyisa obubi okusinga oyo ayogera ku Katonda e bigambo e byo bulimba, oba naagamba nti nnaweebwa obubaka so nga taweebwanga bubaka bwonna, era ani mubi okusinga oyo agamba nti njakuleeta e bigambo e bifaanana n''ebyo Katonda bye yassa! naye n’omala olaba e kiseera abeeyisa obubi bwe babeera mu bulumi bw''okufa nga ba Malayika bagolodde e mikono gyabwe (nga bwebagamba nti,) muggyemu e myoyo gyammwe, olwaleero luno mugenda kusasulwa e bibonerezo e binyoomesa olw''ebyo bye mwayogeranga ku Katonda e byobutaliimu, e kyabatuusa okwekuza nemuva ku bigambo bye.');
INSERT INTO "african_dev" VALUES(6,94,'6:94','Mutujjidde omu omu, nga bwe twabatonda mu kusooka, byonna bye twabawa mubirese mabega wa mmwe, era tulaba bwe muzze temuleese bawolereza ba mmwe, abo be mwawozanga, nti mazima bo ne Katonda bakolera wamu ku mmwe, e nkolagana ekutuseewo wakati wa mmwe nebababulako abo be mwali mulowooza nti b’akubawolereza.');
INSERT INTO "african_dev" VALUES(6,95,'6:95','Mazima Katonda y’ayasa e mpeke n’omuwula (ameza e bimera) aggya e kiramu mu kifu era yagya e kifu mu kiramu oyo nno ye Katonda wa mmwe. Ate muwugulwa mutya.');
INSERT INTO "african_dev" VALUES(6,96,'6:96','(Era ye yooyo) ayasa n’ayawula obudde ne bufuuka e misana, e kiro naakifuula e kiwummulo e njuba n’omwezi nga byombi bitambula nga bigoberera e nkola gye yabiteerawo, okwo kwekusalawo kwa nantakubwa ku mukono omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(6,97,'6:97','Era yye yooyo e yabateerawo e munyeenye mube nga mulungama nazo nga muli mu bizikiza, by’olukalu n''ebizikiza by’enyanja mazima tunnyonnyodde e bigambo olunnyonnyola olumala eri abantu abamanyi.');
INSERT INTO "african_dev" VALUES(6,98,'6:98','Era ye yooyo eyabakola nga abaggya mu muntu omu, nga musinziira mu nnabaana wa ba maama ba mmwe, era nga musinziira mu migongo gya bataata, tunnyonnyodde e bigambo (olunnyonnyola olumala) eri abantu abategeera.');
INSERT INTO "african_dev" VALUES(6,99,'6:99','Era yye yooyo atonnyesa amazzi (e nkuba) okuva waggulu, netumeza nago e bimera bya buli kintu, ne tuggya mu byo e bya kiragala, nga tuggya mu byo e mpeke ezeeweese ku zinnaazo, ne mu mitende ne mubaamu egirina e birimba e bireebeeta, n’ennimiro ez’emizabibu n’e za zaitun ne nkomamawanga, e bifaanagana n''ebitafaanagana, tunuulira e bibala bya byo bwebiba bissizza n''okwengera kwabyo mazima ddala mu ebyo mulimu obubonero eri abantu abakkiriza.');
INSERT INTO "african_dev" VALUES(6,100,'6:100','Bateeka ku Katonda ababeezi abava mu Majinni so nga ye yabatonda, wabula ate nebamupaatiikako abaana ab’obulenzi n’abobuwala ekitalina wekiva, Katonda asukkulumye era yeesambye ku ebyo bye bamutemerera.');
INSERT INTO "african_dev" VALUES(6,101,'6:101','(Yye Katonda) ye w’eggulu n’ensi nga tewali kyarabirako, ayinza atya okubeera n’omwana ate nga talina mukyala! era nga ye yatonda buli kintu era nga yye (Katonda) ku buli kintu mumanyi.');
INSERT INTO "african_dev" VALUES(6,102,'6:102','Oyo nno ye Mukama omulabirizi wa mmwe, tewali kintu kyonna kisinzibwa mu butuufu okugyako yye yekka, omutonzi wa buli kintu, kale nno ggwe mubamusinza, era bulijjo y’alina obuyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(6,103,'6:103','Amaaso tegasobola kumulaba, so nga ate ye agalaba n’amalayo e bintu byonna, anti ye Katonda alina obusobozi bw’okumanya buli kintu atalina kiyinza kumwekweka.');
INSERT INTO "african_dev" VALUES(6,104,'6:104','Mazima obujulizi bubajjidde nga buva ewa Mukama omulabirizi wa mmwe, omuntu anaalaba anaaba ye yambye yekka, ate oyo anaaziba amaaso akabi k’ekyo kadda ku ye. Nze siri wa kuddaawo kubalunda.');
INSERT INTO "african_dev" VALUES(6,105,'6:105','Bwetutyo nno bwe tunnyonnyola e bigambo, batuuke n’okugamba nti (Muhammad) wasoma (ku ba Yudaaya okutuuka okumanya bino byonna) era tube nga tubinnyonnyola eri abantu abamanyi.');
INSERT INTO "african_dev" VALUES(6,106,'6:106','(Ggwe Muhammad ky’oba okola) goberera e byo e bissibwa gyoli nga biva ewa Mukama omulabirizi wo, tewali kintu kyonna kirina kusinzibwa okugyako ye, era weesambe abagatta e bintu e birala ku Katonda.');
INSERT INTO "african_dev" VALUES(6,107,'6:107','Singa Katonda yayagala tebandimugasseeko bintu birala, era tetukussangawo obe kalondoozi ku bo, era ggwe toyinza kuba avunaanyizibwa ku nsonga zaabwe.');
INSERT INTO "african_dev" VALUES(6,108,'6:108','Temuvumanga ebyo bye basaba ne balekawo Katonda, mu kwesasuza olw''obutamanya bwabwe bajja kuvuma Katonda, bwetutyo nno buli bantu twabawa okulaba e byabwe nga bye bisinga obulungi, oluvanyuma ewa Mukama omulabirizi waabwe yeeri obuddo bwa bwe, olwo nno n’abategeeza byonna bye baakolanga.');
INSERT INTO "african_dev" VALUES(6,109,'6:109','Era balayira Katonda ne bakkaatiriza e birayiro bya bwe, ne bagamba nti singa bajjirwa e kyamagero kyonna baalikikkirizza, bagambe: e byamagero byonna bya Katonda, naye mumanyira ku ki nti mazima singa e ky’amagero kijja tebajja kukkiriza.');
INSERT INTO "african_dev" VALUES(6,110,'6:110','Tugenda kukyusa e mitima gyabwe n''okulaba kwabwe nga nabo bwe batakkiriza (Kur’ani) mu kusooka era tubaleke mu bubuze bwa bwe nga babulubuuta.');
INSERT INTO "african_dev" VALUES(6,111,'6:111','Singa twabassiza ba Malayika , era abafu ne boogeranabo, ne tubakunganyiza buli kintu ne kibaawo, tebandikkirizza okugyako Katonda nga ayagadde wabula mazima ddala abasinga obungi mu bo tebategeera.');
INSERT INTO "african_dev" VALUES(6,112,'6:112','(Nga bwetukugezesa ggwe Nabbi Muhammad n’oba n’abalabe mu bakaafiiri) ,bwe kityo buli Nabbi twamuteekako omulabe nga ali mu bantu ne mu majinni, abamu batumira bannaabwe e bigambo e biwoomu, naye nga bibi olwe kigendererwa kyo kugayaaza, naye singa Mukama omulabiriziwo aba ayagadde tebandikikoze kale nno baleke n''ebyo bye bagunjawo.');
INSERT INTO "african_dev" VALUES(6,113,'6:113','Era e mitima gy''abo abatakkiriza lunaku lw''enkomerero gibe nga gibiwuliriza, era babisiime era bagende mu maaso n’okukola e byo bye bakola.');
INSERT INTO "african_dev" VALUES(6,114,'6:114','Nyinza okuleka Katonda nenfuna omulala ansalirawo! nga ate yye Katonda y''oyo eyassa gye muli e kitabo( kuran) nga kinnyonnyola buli kimu, abo nno betwawa e kitabo( Tawraat n''enjili) bamanyi mazima ddala kyassibwa nga kiva ewa Mukama omulabiriziwo, nga kyonna mazima meereere, kale nno tobeer mu babuusabuusa.');
INSERT INTO "african_dev" VALUES(6,115,'6:115','E kigambo kya Mukama omulabirizi wo kituukiridde nga kyonna mazima na bwenkanya, tewali asobola kukyusa bigambo bye era bulijjo yye awulira era amanyi nnyo.');
INSERT INTO "african_dev" VALUES(6,116,'6:116','Singa on''ogondera abantu abasinga obungi mu nsi bajja kukubuza bakujje ku kkubo lya Katonda (byonna bye bakola temuli) okugyako okuba nti bateebereza, kye bakola tekiri okugyako kuteeba buteebi.');
INSERT INTO "african_dev" VALUES(6,117,'6:117','Mazima Mukama omulabirizi wo y''asinga okutegeera oyo eyabula ne ava ku kkubo lye, era nga bwali nti yaasinga okutegeera ebikwata ku balungama.');
INSERT INTO "african_dev" VALUES(6,118,'6:118','Kale nno mulye e byo e biba byogereddwa ku byo e rinnya lya Katonda, bwe muba nga mukkiriza e bigambo bye.');
INSERT INTO "african_dev" VALUES(6,119,'6:119','Ate muyinza mutya obutalya e byo e byogereddwako e rinnya lya Katonda nga mazima yabannyonnyola e byo bye yaziyiza ku mmwe, mpozzi e kyo kye muba mukoze olwo buwaze, naye ate mazima ddala abantu bangi babuza (abalala) nga bagoberera okwagala kwa bwe okutaliimu kwesigama ku kumanya, mazima bulijjo Mukama omulabiriziwo amanyi nnyo e bikwata ku basukka e nsaloze.');
INSERT INTO "african_dev" VALUES(6,120,'6:120','Muve ku bibi e byomulwatu ne byo''mukyama, mazima ddala abo abakola e bibi bajja kusasulwa ku lwebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(6,121,'6:121','Temulyanga e byo e bitayogereddwaako linnya lya Katonda mazima ddala okukola e kyo bwonoonyi bwennyini, era ddala Sitane ziwa mikwano gyaazo e bigambo babawakanye nabyo, singa mubagondera, nammwe olwo muba mu bagatta ku Katonda e bintu e birala.');
INSERT INTO "african_dev" VALUES(6,122,'6:122','Omuntu eyali omufu netumuwa obulamu, netumuwa e kitangaala kyakozesa nga atambula mu bantu, ayinza okufaanana ng’oyo ali mu bizikiza byatasobola kufulumamu. Na bwekityo abakaafiiri balaba nga bye bakola nga bye bisinga obulungi.');
INSERT INTO "african_dev" VALUES(6,123,'6:123','Na bwekityo twassa mu buli kitundu abakulira abagyemu baamu, babe nga bakolerera okusaasaanya obukyamu mu kyo, bye bakola tebituusa kabi okugyako ku bo bennyini naye tebamanyi.');
INSERT INTO "african_dev" VALUES(6,124,'6:124','Obujulizi obwe nkukunala bwe bubaggyira bagamba nti tetujja kukkiriza okutuusa nga tuweereddwa nga e byaweebwa ababaka ba Katonda, (e kituufu kiri nti) Katonda amanyidde ddala wassa obubaka bwe, abo abayonoona bajja kutuukwako okunyomebwa mu maaso ga Katonda n’ebibonerezo e bikakali, ku lwo kusaasanya kwabwe e bikyamu.');
INSERT INTO "african_dev" VALUES(6,125,'6:125','So nno omuntu Katonda gwaba ayagadde okulungamya ayanjuluza e kifuba kye eri obusiramu, n''oyo gwaba ayagadde okubuza afunza e kifuba kye n''atuuka okuba nga tawulira bulungi n’aba nga oyo agenda wa ggulu (space) bwatyo Katonda bwassa e kibonerezo kwabo abatakkiriza.');
INSERT INTO "african_dev" VALUES(6,126,'6:126','Lino lye kkubo lya Mukama omulabirizi wo e ggolokofu, mazima e bigambo tubinnyonnyodde eri abo abasobola okwebuulirira.');
INSERT INTO "african_dev" VALUES(6,127,'6:127','Olw''ebyo bye baali bakola ku nsi bagenda kufuna e nyumba ey''emirembe ewa Mukama omulabirizi waabwe, era yye ye mutaasa waabwe.');
INSERT INTO "african_dev" VALUES(6,128,'6:128','(Ggwe Nabbi Muhammad) lowooza olunaku Katonda lwalikungwanya (abakaafiiri) bonna, (abagambe nti) abange mmwe amajinni mwabuza abantu bangi nnyo, mikwano gya majinni mu bantu ne bagamba nti ayi Mukama omulabirizi waffe fenna twesanyusizanga wamu netutuuka ku kiseera kyaffe kye watugerera okuba ku nsi, Katonda n''abagamba nti omuliro bwe buddo bwa mmwe, muli baakugubeeramu bugenderevu okugyako oyo Katonda gwaliba yagadde, anti mazima Mukama omulabiriziwo mugoba nsonga muyitirivu wa kumanya.');
INSERT INTO "african_dev" VALUES(6,129,'6:129','Bwe tutyo bulijjo abeeyisa obubi tubafuula mikwano gy’a ba nnaabwe bwe bafaanana olw''ebyo bye bakola.');
INSERT INTO "african_dev" VALUES(6,130,'6:130','Abange mmwe amajinni n''abantu ababaka tebaabajjira nga bava mu mmwe nga babategeeza e bigambo byange era ne babatiisa, okutuuka ku lunaku lwa mmwe luno, baligamba nti twewaako obujulizi (nti byonna baabitugamba, naye e kyabaleetera okubula) obulamu bwe nsi bwabagayaaza, olwo nno nebaba nga beekakasizzaako ddala nti baali bakaafiiri.');
INSERT INTO "african_dev" VALUES(6,131,'6:131','Ekyo nno (e kyokutuma ababaka) kyaliwo lwakuba nti Mukama omulabiriziwo yali tayinza kuzirikiza bantu ba kitundu kyonna olw''okweyisa obubi nga abantu b''omukitundu e kyo tebamaze kumanyisibwa (mateekage).');
INSERT INTO "african_dev" VALUES(6,132,'6:132','Buli omu ku bo alina amadaala okusinziira ku bye yakola Mukama Katonda omulabiriziwo tayinza kumala galeka e byo bye bakola.');
INSERT INTO "african_dev" VALUES(6,133,'6:133','Mukama omulabiriziwo y’atalina kye yeetaaga, ye nannyini kusaasira, bwaba ayagadde asobola okubajjawo oluvanyuma lwa mmwe naaleeta abo baaba ayagadde, nga nammwe bwe yabassaawo nga abajja mu zzadde ly’abantu abalala.');
INSERT INTO "african_dev" VALUES(6,134,'6:134','Mazima e byo bye mulagaanyisibwa bya kutuukirira ate mmwe temugenda kusobola kubiremesa.');
INSERT INTO "african_dev" VALUES(6,135,'6:135','Gamba (Ggwe Nabbi Muhammad) abange abantu bange mubeere ku nkola ya mmwe nange, nkole e byange, kyaddaaki mujja kumanya obuddo obw''enkomerero e nnungi bwani, mazima ddala bulijjo abeeyisa obubi tebagenda kutuuka ku buwanguzi.');
INSERT INTO "african_dev" VALUES(6,136,'6:136','(Mu bibi abantu abatagenda kutuuka ku buwanguzi bye bakola kwe kuba nti) bateerawo Katonda omugabo mu birime ne mu bisolo, mu kujweteka kwabwe ne bagamba nti, guno omugabo gw’a Katonda, naguno gwa ba katonda baffe abalala, oguba ogwa ba katonda baabwe tegutuuka w’a Katonda, ate oguba ogwa Katonda, gwo gutuuka ku ba katonda baabwe abalala, e nnamula yaabwe mbi.');
INSERT INTO "african_dev" VALUES(6,137,'6:137','Mu ngeri yeemu, abagatta ku Katonda e bintu e birala, ba katonda baabwe baabalaga nti kirungi okutta abaana baabwe, nebabatuusa okukola ekivve (eky''okutta omuntu ) nga bababuza, era babe nga babuzaabuza e ddiini yaabwe, singa Katonda yayagala tebandikikoze kale baleke n''ebyo bye bagunja.');
INSERT INTO "african_dev" VALUES(6,138,'6:138','Era abagatta ku Katonda ebintu e birala mu ndaba yaabwe bagamba nti, bino e bisolo n’ebirime tebikkirizibwa kuriibwa okugyako oyo gwetuba twagadde, ate nebaba n’ebisolo bye baziza okwebagala, ate nebaba n’ebisolo e birala bye batayogererako linnya lya Katonda ( nga bagenda okubisala) byonna nga bigambo bigunje ku Katonda, Katonda ajja kubasasula olw''ebyo bye baali bagunja.');
INSERT INTO "african_dev" VALUES(6,139,'6:139','Era (abagatta ku Katonda e bintu e birala) baagamba nti amawako g’ebisolo gakkirizibwa kuliibwa basajja bokka mu ffe, era tegakkirizibwa bakyala baffe, naye (amawako ago) bwegavaamu nga gafudde olwo bonna bakkiziribwa okubirya, Katonda ajja kubasasula olw''okutetenkanya kwabwe okwo, anti mazima Katonda mugoba nsonga, muyitirizu wa kumanya.');
INSERT INTO "african_dev" VALUES(6,140,'6:140','Mazima bafaafaaganirwa abo abatta abaana baabwe mu busiru olw''obutamanya, era mu ngeri y''okutemerera Katonda ne baziza e byo Katonda bye yabagabira, mazima baabula era tebaali balungamu.');
INSERT INTO "african_dev" VALUES(6,141,'6:141','Era yye (Katonda) yooyo eyateekawo amalimiro agabalira ku ttaka naago agatabalira ku ttaka, n’emitende n''ebimera ( ebirala) nga okuwooma kwabyo kwawukamu, n''e mizayituuni n’enkoma mawanga ebifaanagana mundabika yaabyo ate nga obuwoomu bwabyo bwawukamu, mulye e bibala byabyo bwe biba nga bibaze ate mujjemu n''omugabo gwabyo (gwa Zakka) olunaku lwemuba lwe mubikungula, wabula temusukkanga e kigero kyabyo ( mu nkozesa ne mu kutoola zzaka), anti Katonda tayagala basukka kigero.');
INSERT INTO "african_dev" VALUES(6,142,'6:142','Era Katonda yateekawo e bisolo e byetikka n’ebirala nga biggyibwako eby''okwaliira, mulye mu ebyo Katonda bye yabagabira temugobereranga obuwufu bwa Sitane, anti mazima yye gye muli mulabe wa lulango.');
INSERT INTO "african_dev" VALUES(6,143,'6:143','E bisolo e birundibwa (Katonda bye yatukkiriza okulya) e mitindo gyabyo giri munaana, e biri mu ndiga, n’ebiri mu mbuzi, bagambe (ggwe Nabbi Muhammad) nti e nsajja z’ombiriri Katonda ze yaziza ku mmwe, oba e nkazi, oba amawako g’azombiriri, mumbulire nga musinziira ku kumanya bwe muba nga bye mwogera bya mazima.');
INSERT INTO "african_dev" VALUES(6,144,'6:144','Ne mu ngamiya e mitindo e biri, ne mu nte e mitindo e biri, bagambe (ggwe Nabbi Muhammad) e nsajja z’ombiriri zeyaziza oba e nkazi, oba amawako g’a zombiriri, abaffe mwaliwo nga Katonda akibalagira! olwo ani eyeeyisa obubi okusinga oyo agunja ku Katonda obulimba, atuuke okubuza abantu awatali kumanya, bulijjo Katonda talungamya bantu beeyisa bubi.');
INSERT INTO "african_dev" VALUES(6,145,'6:145','Gamba ggwe (Nabbi Muhammad) nti: mu bubaka obwampebwa temuli kiri Haramu kwoyo aba ayagadde okukirya, be ppo nga ebadde nnyamanfu oba omusaayi ogwa kafekye, oba e nnyama ye mbizzi, mazima e kikolwa e ky’okulya e bintu e byo kivve, oba (mu biri haram) y''ensolo esalibwa mu bugyemu nga eyogereddwaako e rinnya eritali lya Katonda. Wabula omuntu aba alemereddwa nga takikola lwa jjoogo wadde okumenya amateeka ga Katonda, mazima Mukama omulabiriziwo musonyiyi era musaasizi.');
INSERT INTO "african_dev" VALUES(6,146,'6:146','Era twaziza ku bayudaaya buli kisolo ekirina e njala, ate nga ente n''embuzi, twaziza ku bo amasavu gaazo okugyako ag''oku mugongo oba e byenda oba agali mu nnyama e y''okuggumba ekyo twababonereza nakyo lwabubuze bwabwe, mazima ffe buli lwe twogera kiba kyekyo.');
INSERT INTO "african_dev" VALUES(6,147,'6:147','Bwebakulimbisa gamba nti Mukama omulabirizi wa mmwe okusaasira kwe kugazi, kyokka ate e bibonerezo bye tebiyinza kuwonebwa bantu boonoonyi.');
INSERT INTO "african_dev" VALUES(6,148,'6:148','Abo abagatta ebintu e birala ku Katonda bajja kugamba nti singa Katonda yayagala ffe ne bakadde baffe tetwandigasse kintu kirala ku Katonda, era tewali kintu kye twandifudde Haramu, bwe batyo n''abaakulembera bwe baalimbisa okutuusa lwe baakomba ku bukaawu bwe bibonerezo byaffe, gamba nti abaffe (mwogera e byo) nga mulina kye musinziirako! kale mu kitulage, tewali kye mugoberera okugyako okufumiitiriza era kye mukola si kirala okugyako okuteebereza.');
INSERT INTO "african_dev" VALUES(6,149,'6:149','Gamba (Ggwe Muhammad) obujulizi obumatiza bwa Katonda. Kale nno singa yayagala mwenna yaalibalungamizza.');
INSERT INTO "african_dev" VALUES(6,150,'6:150','Gamba (Ggwe Muhammad) muleete abajulizi ba mmwe abo abajulira nti mazima Katonda yaziyiza e kintu gundi, bwe bajulira ne bakikakasa gwe toba nabo, era togoberera n''omulundi n''ogumu okwagala kw''abo abalimbisa e bigambo byaffe, n''abo abatakkiriza lunaku lwa nkomerero, era nga bo bagatta e bintu e birala ku Katonda.');
INSERT INTO "african_dev" VALUES(6,151,'6:151','Gamba nti mujje mbasomere e byo Katonda bye yafuula haramu ku mmwe, temugattanga e kintu kyonna ku Katonda, abazadde ababiri bateekeddwa okuyisibwa obulungi, temuttanga abaana ba mmwe olw’obwaavu, ffe tugabirira mmwe nabo, era temusembereranga eby''obuwemu eby''olwatu mu byo n’ebyekwese, temuttanga omuntu oyo Katonda gwe yaziza okugyako nga waliwo e nsonga. E byo nno (Katonda) yabibakalaatira kibayambe mube abategeera.');
INSERT INTO "african_dev" VALUES(6,152,'6:152','Era temusembereranga e mmaali ya ba mulekwa okugyako mu ngeri esinga okuba e nnungi okutuusa omwana oyo nga akuze mu birowoozo, era mutuukirize e bipimo n''ebigero mu bwesimbu, omuntu tetumulaalikako okugyako e kyo kyasobola, era bwe muba nga mwogera mulage amazima aweebwako obujulizi ne bwaba wa luganda, era mu ngeri e yenjawulo mutuukirize obweyamu bwa mmwe bwe mwawa Katonda. E byo nno Katonda yabibakalaatira kibayambe okujjukira (mwekube mu kifuba).');
INSERT INTO "african_dev" VALUES(6,153,'6:153','Era mazima ddala e kkubo lyange lino ggolokofu kale nno lyemuba mukwata, temugobereranga obukubo obulala ne kibakyusa okuva ku kkubo lye, e byo nno yabibakalaatira mmwe mulyoke mube abamutya.');
INSERT INTO "african_dev" VALUES(6,154,'6:154','Era (mukimanye) nti twawa Musa e kitabo nga kujjuuliriza eri oyo akola obulungi era nga kittottola buli kintu, era nga bulungamu na kusaasira, kibasobozese okukkiriza nti bagenda kusisinkana Mukama omulabirizi waabwe.');
INSERT INTO "african_dev" VALUES(6,155,'6:155','Na kino e kitabo (Kur’ani) twakissa nga kya mukisa kale nno mukigoberere era mutye Katonda olwo nno musaasirwe.');
INSERT INTO "african_dev" VALUES(6,156,'6:156','Muleme kugamba nti bwo obubaka bwa weebwa e bibinja bya mirundi e biri e byatukulembera ate nga twali tetufaayo ku bye basoma.');
INSERT INTO "african_dev" VALUES(6,157,'6:157','Oba muleme kugamba nti singa mazima ddala twassibwako e kitabo twandibadde balungamu okusinga bali, mazima obunnyonnyofu okuva ewa Mukama omulabirizi wa mmwe n''obulungamu n’okusaasira byabajjira, kaakati ani eyeeyisa obubi okusinga oyo alimbisa e bigambo bya Katonda n''abyawukanako, abo abaawukana ku bigambo byaffe tugenda kubasasula nga tubatuusaako e bibonerezo e bikakali olw''okuva kwabwe ku bigambo bya Katonda.');
INSERT INTO "african_dev" VALUES(6,158,'6:158','Abaffe balina kye balindirira mpozzi ba malayika okubajjira, oba Mukama omulabirizi wo ajje, oba obumu ku bubonero bwa Mukama omulabiriziwo bujje, (wabula) olunaku, obumu ku bubonero bwa mukama omulabiriziwo lwe burijja, omuntu tagenda kuganyulwa mu kukkiriza kwe kavuna aliba nga mu kusooka teyali mukkiriza oba obukkiriza bwe nga tebwamuyamba kukola burungi gamba (ggwe Muhammad) nti mulindirire mazima naffe tulindiridde.');
INSERT INTO "african_dev" VALUES(6,159,'6:159','Mazima ddala abo abatemaatema mu ddiini yaabwe, nebafuuka biwayi tolina kakaatwe nabo ku kintu kyonna, mazima e nsonga zaabwe ziri wa Katonda (yaagenda okubalamula), olwo nno alyoke abategeeze e byo bye baakolanga.');
INSERT INTO "african_dev" VALUES(6,160,'6:160','Omuntu alijja nga akoze e kirungi kimu, alifuna e mpeera yaakyo e mirundi kkumi, ate oyo alijja nga akoze e kibi, tagenda kusasulwa okugyako e kyenkana n''ekyo kye yakola, era nga bulijjo, nabo tebagenda kuyisibwa bubi.');
INSERT INTO "african_dev" VALUES(6,161,'6:161','Gamba mazima nze Mukama omulabirizi wange yannungamya ku kkubo e ggolokofu, nga ddiini e nnambulukufu, nga ye ddiini ya Ibrahim, eyali omwesimbu munzikiriza, era tabangako wa mubagatta ku Katonda kintu kirala.');
INSERT INTO "african_dev" VALUES(6,162,'6:162','Gamba (Ggwe Muhammad) nti mazima okusaala kwange, n’okusaddaaka kwange e bisolo, n’obulamu bwange, n’okufa kwange, byonna biri eri Mukama omulabirizi w’ebitonde.');
INSERT INTO "african_dev" VALUES(6,163,'6:163','Tewali kimugattibwako, bwentyo nno bwe nnalagirwa, era nze musaale mu beewaayo ewa Katonda.');
INSERT INTO "african_dev" VALUES(6,164,'6:164','Gamba Ggwe (Nabbi Muhammad) abaffe nyinza okuleka Katonda nenfuula e kintu e kirala kyonna okuba nga ye mulabirizi wange, so ng’ate yye ye mulabirizi w’abuli kintu, tewali kibi muntu kyakola okugyako nga kidda gyali, era tewali muntu yeetikka kibi kya mulala. Oluvanyuma eri Mukama omulabirizi wa mmwe yeeri obuddo bwa mmwe.');
INSERT INTO "african_dev" VALUES(6,165,'6:165','Era yye yooyo eyabateekawo nga mujja musikira obuvunaanyizibwa ku nsi, era naasitula abamu ku mmwe mu madaala, olwo nno abenga abagezesa mu ebyo bye yabawa, mazima Mukama omulabirizi wo mwangu wa kubonereza, era mazima ddala yye musonyiyi nnyo musaasizi.');
INSERT INTO "african_dev" VALUES(7,1,'7:1','Alif Laam Miim Swad');
INSERT INTO "african_dev" VALUES(7,2,'7:2','Kino kitabo e kyassibwa gyoli, tekisaanye kukuleetera nkeka, batiise nakyo era nga kya kwebuulirira eri abakkiriza.');
INSERT INTO "african_dev" VALUES(7,3,'7:3','Mugoberere ebyo ebyassibwa gye muli nga biva ewa Mukama omulabirizi wa mmwe, temumulekangawo nemugoberera abalala, (ekibi) mujjukira kitono.');
INSERT INTO "african_dev" VALUES(7,4,'7:4','E bitundu bimeka bye twazikiriza! E bibonerezo byaffe ne bibatuukako e kiro, nga beebase oba ne bibatuukako e misana nga bawumuddeko.');
INSERT INTO "african_dev" VALUES(7,5,'7:5','Ebibonerezo byaffe bwe byabajjira tebaalina kyakwogera okugyako okugamba nti, mazima ddala tubadde tweyisa bubi.');
INSERT INTO "african_dev" VALUES(7,6,'7:6','Tugenda kubuuliza ddala abo abaatumirwa era nga bwe tugenda okubuuliza ddala abaatumwa.');
INSERT INTO "african_dev" VALUES(7,7,'7:7','Tugenda kubatottolera ddala olw''okumanya (kwaffe), anti tetubulangako (webakolera kintu kyonna).');
INSERT INTO "african_dev" VALUES(7,8,'7:8','Okupima ku lunaku olwo kugenda kubeera kwa bwenkanya. Oyo yenna e birizitowa e bipimwa bye, abo nno be bagenda okubalibwa nti batuuse ku buwanguzi.');
INSERT INTO "african_dev" VALUES(7,9,'7:9','Oyo yenna e biriwewuka e bipimwa bye, abo nno beebo abafaafaaganirwa e myooyo gyabwe okusinziira ku ngeri gye beeyisangamu obubi ku bikwata ku bigambo byaffe.');
INSERT INTO "african_dev" VALUES(7,10,'7:10','Mazima twabawa obuyinza ku nsi ne tugibateeramu e bibabeezaawo (wabula) musiima kitono.');
INSERT INTO "african_dev" VALUES(7,11,'7:11','Era mazima twabatonda mmwe, oluvanyuma twabawa e bifaananyi bya mmwe oluvanyuma ne tugamba ba malayika muvunnamire Adam ne bavunnama okugyako Ibuliisu (Sitane) ataali mu baavunnama.');
INSERT INTO "african_dev" VALUES(7,12,'7:12','Katonda naagamba nti: kiki ekikugaanye okuvunnama bwe nkulagidde, (Sitane) naagamba nti nze mulungi okusinga yye, wantonda mu muliro, ate yye n''omutonda mu ttaka.');
INSERT INTO "african_dev" VALUES(7,13,'7:13','Katonda naagamba nti gifulume (e jjana), tolina lukusa kuwaganyaliramu, kale fuluma mazima ggwe oli mu bakkakkanyiziddwa.');
INSERT INTO "african_dev" VALUES(7,14,'7:14','(Sitane) naagamba nti nindiriza okutuusa ku lunaku lwe balizuukizibwa.');
INSERT INTO "african_dev" VALUES(7,15,'7:15','(Katonda), naagamba nti, mazima ggwe okkiriziddwa okubeera mu balindirizibwa.');
INSERT INTO "african_dev" VALUES(7,16,'7:16','(Sitane) naagamba nti, nga bwombuzizza nja kubateeganga ku kkubo lyo e ggolokofu.');
INSERT INTO "african_dev" VALUES(7,17,'7:17','Olwo nno ndyoke mbalumbire ddala nga mbava mu maaso ne mabega waabwe ne ku ddyo waabwe ne kkono, era togenda kusanga abasinga obungi mu bo nga beebaza.');
INSERT INTO "african_dev" VALUES(7,18,'7:18','(Katonda) naagamba nti gifulume ng’ovumirirwa ng’ogobeddwa, oyo yenna alikugoberera mu bo ggwe nabo mwenna ngenda kubajjuza mu muliro.');
INSERT INTO "african_dev" VALUES(7,19,'7:19','Era netugamba Adam nti ggwe ne Mukyalawo mubeere mu jjana mwembi mulye wonna wemwagala, naye temusemberera omuti guno ne kibatuusa mwembiriri okubalibwa mu beeyisa obubi.');
INSERT INTO "african_dev" VALUES(7,20,'7:20','Sitane naababuzaabuza neeba e nsonga gye yayitamu okubalaga obwereere bwabwe obwo obwali butabamanyisiddwa. (Sitane) naagamba nti Mukama omulabirizi wa mmwe mwembiriri teyabagaana muti guno okugyako lwakuba nti mwembiriri mwandibadde ba Malayika oba mwembiriri okubeera abalamu olubeerera.');
INSERT INTO "african_dev" VALUES(7,21,'7:21','(Sitane) naabalayirira nti mazima ddala nze era ku mmwe mwembiriri ndi omu kubawi ba magezi.');
INSERT INTO "african_dev" VALUES(7,22,'7:22','Naababuza bombi ng’ayita mu kubakwenyakwenya, bwe baamala okukomba ku buwoomu bw’omuti, obwereere bwabwe bwa beeyoleka, olwo nno ne batandika okunoga nga bwe beebikkako e bikoola byo mu jjana Mukama omulabirizi waabwe naabakoowoola (mu ddoboozi eryomwanguka) nti, ssaabagaana mwembiriri omuti ogwo era mwembiriri nembagamba nti mazima ddala Sitane mulabe ow''olwatu ku mmwe mwembiriri.');
INSERT INTO "african_dev" VALUES(7,23,'7:23','Nebagamba nti ayi Mukama omulabirizi waffe tweyisizza bubi, bwonooba totusonyiwe n’otusaasira tujja kubeerera ddala mu bafaafaaganiddwa.');
INSERT INTO "african_dev" VALUES(7,24,'7:24','(Katonda) naagamba nti mugende, abamu mu mmwe nga muli balabe ba bannaabwe, mujja kutuulanga ku nsi n''okweyagala okutuusa e kiseera e kigere.');
INSERT INTO "african_dev" VALUES(7,25,'7:25','(Katonda) naagamba nti mu yo mwemujja okuwangaaliranga, mu yo mwemulifiira, era nga mu yo mwemugenda okujjibwa (muzuukire).');
INSERT INTO "african_dev" VALUES(7,26,'7:26','Abange mmwe abaana ba Adam mazima twabawae byambalo e bibikka obwereere bwa mmwe, netubawa ne byambalo bye mwewunda nabyo, wabula e kyambalo ky’okutya Katonda kyo kye kisinga obulungi. Bino bye bimu ku bigambo bya Katonda (bya batumira babe nga beebuulirira).');
INSERT INTO "african_dev" VALUES(7,27,'7:27','Abange mmwe abaana ba Adam, Sitane tababuzaabuzanga, nga bweyajja bakadde ba mmwe mu jjana n''aba nga abajjako e byambalo bya bwe olwo nno abenga abalaga nti bali bwereere, anti mazima yye (Sitane) ne banne babalaba mmwe wemutabalabira, anti twafuula zi Sitane okuba mikwano ffa nfe eri abo abatakkiriza.');
INSERT INTO "african_dev" VALUES(7,28,'7:28','Bwe baba bakoze e kyobuwemu bagamba nti twasanga bakadde baffe bwe bakola, era nga Katonda ye yakitulagira, gamba ggwe (Nabbi Muhammad) nti mazima Katonda talagira bya buwemu, mutuuka okwogera ku Katonda e byo bye mutamanyi!');
INSERT INTO "african_dev" VALUES(7,29,'7:29','Ggwe (Nabbi Muhammad) gamba nti Mukama omulabirizi wange yalagira okukola obwenkanya, era mubeerenga beesimbu mu kusinza mu buli Ibaada, (okusingira ddala) buli lwemugenda mu Mizikiti, era bwe muba nga musaba musabe ye yekka, olwo nno mube nga e ddiini mugikolera ye yekka, anti nga bwe yabatandika (mu ku batonda) mugenda kuddawo (oluvanyuma lw''okufa).');
INSERT INTO "african_dev" VALUES(7,30,'7:30','E kibinja e kimu yakilungamya ate nga e kibinja e kirala baakakatwako obubuze, anti mazima bo Sitane baazifuula mikwano gyabwe ffa nfe ne baleka Katonda, ate ne beebala nti mazima bo balungamu.');
INSERT INTO "african_dev" VALUES(7,31,'7:31','Abange abaana ba Adam mwambalenga bulungi buli lwe mugenda okusaala, era mulye munywe naye temudiibudanga. Mazima Katonda tayagala badiibuuzi.');
INSERT INTO "african_dev" VALUES(7,32,'7:32','Gamba (Ggwe Nabbi Muhammad) nti ani yaziza e by’okwewunda bya Katonda e byo bye yawa abaddu be ne Riziki ng’ebadde nnungi, gamba nti byo n''abasiraamu bakkirizibwa okubikozesa mu bulamu obwensi, naye bigenda kubeera byabwe bokka ku lunaku lwe nkomerero, bwe tutyo nno bwe tutottola e bigambo eri abantu abamanyi.');
INSERT INTO "african_dev" VALUES(7,33,'7:33','Gamba ggwe Muhammad nti Mukama omulabirizi wange yaziza e by''obuwemu eby''olwaatu n’ebyenkiso era yaziza obwonoonyi n’okulumbagana abantu awatali nsonga ntuufu, era naaziza okuba nga mugatta ku Katonda e bintu e birala e kintu kyatassaako bujulizi era naaziza okuba nga mwogera ku Katonda bye mutamanyi.');
INSERT INTO "african_dev" VALUES(7,34,'7:34','Buli bantu balina e bbanga e lyabagererwa, Katonda bwasalawo e ntuuko yaabwe neetuuka, tebayinza kusaba kaseera kubongozebwayo wadde okukendezebwako (nebakifuna).');
INSERT INTO "african_dev" VALUES(7,35,'7:35','Abange mmwe abaana ba Adam ababaka bwe balibaggyira , nga bava mu mmwe, nga babatottolera e bigambo byange, oyo yenna alitya Katonda naakola e mirimu e mirungi abo nno tebalibaako kutya era tebagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(7,36,'7:36','Naabo abalimbisa e bigambo byaffe ne beekuluntaza, ne babireka abo nno be bantu bo muliro bo baakutuula bugenderevu mu gwo.');
INSERT INTO "african_dev" VALUES(7,37,'7:37','Ani eyeeyisa obubi okusinga oyo ajweteka e bigambo ku Katonda, oba naalimbisa e bigambo bye, abo nno omutemwa gwabwe ogwabagererwa gubatuukako, olwo nno ababaka baffe bwe babajjira (ba Malayika) okubajjamu e myoyo bagamba nti baluwa abo be mwasinzanga nemuleka Katonda nebagamba batubuzeeko nebeewaako obujulizi nti mazima ddala baali bakaafiiri.');
INSERT INTO "african_dev" VALUES(7,38,'7:38','Katonda aligamba nti muyingire omuliro nga muyingirira mu bibinja e byabo be mufaanana, abaabakulembera mu majinni ne mu bantu, buli kibinja ekiriyingira nga kikolimira kinnaakyo okutuusa nga bonna bamaze okusisinkana mu gwo, e kibinja e kyenkomerero kiryogera ku kyolubereberye nekigamba nti ayi Mukama omulabirizi waffe abo baatubuza bawe e bibonerezo by''omuliro nga obikubisizzaamu, Katonda naagamba nti buli bamu bongezeddwamu naye temumanyi.');
INSERT INTO "african_dev" VALUES(7,39,'7:39','Ab''olubereberye baligamba ab’enkomerero (nti ekyo kyemwogera) tekibawa nkizo yonna ku ffe, kale mukombe ku bukaawu bwe bibonerezo olw''ebyo bye mwakolanga.');
INSERT INTO "african_dev" VALUES(7,40,'7:40','Mazima abo abaalimbisa e bigambo byaffe ne babyekuluntalizaako, e miryango gye ggulu tegigenda kubaggulirwa, era tebagenda kuyingira jjana be ppo nga e ngamiya eyise mu kituli ky''empiso, bwe tutyo nno bwetusasula aboonoonyi.');
INSERT INTO "african_dev" VALUES(7,41,'7:41','Balina obuliri mu muliro Jahannama nga ne byokwebikka waggulu waabwe muliro, era bwe tutyo bwe tusasula abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(7,42,'7:42','Bo abo abakkiriza nebakola e mirimu e mirungi tetuwaliriza muntu yenna okugyako kyasobola, abo nno be bantu be jjana bo baakutuula bugenderevu mu yo.');
INSERT INTO "african_dev" VALUES(7,43,'7:43','Era tugenda kujja mu bifuba byabwe obukukuuzi bwonna, (balibeera mu jjana) nga ekulukutiramu e migga, era baligamba nti amatendo gonna ga Katonda oyo eyatulungamya eri e kkubo erituuka eri kino, tetwali baakulungama singa Katonda teyatulungamya mazima ababaka ba Mukama omulabirizi waffe bye baaleeta mazima meereere, era balikowoolwa nti eyo e jjana mugifunye olw''ebyo bye mwaali mukola.');
INSERT INTO "african_dev" VALUES(7,44,'7:44','Abantu bo Mu jjana balikowoola abantu b’o mu muliro nga bagamba nti mazima Mukama omulabirizi waffe bye yatulagaanyisa tusanze nga bituufu, nammwe Mukama omulabirizi wa mmwe bye yabalagaanyisa mubisanze nga bye byo, baligamba nti yye olwo nno omulanzi alirangirira wakati waabwe, nti e kikolimo kya Katonda kibeere ku beeyisa obubi (abantu abo abeeyisa obubi).');
INSERT INTO "african_dev" VALUES(7,45,'7:45','Beebo abaziyiza abantu okuweereza mu kkubo lya Katonda, era nebalivumirira. Anti bo bawakanya olunaku lw’enkomerero.');
INSERT INTO "african_dev" VALUES(7,46,'7:46','Era wakati w’a bantu b’o mu muliro n’a bo mu jjana wagenda kubaawo e kikomera, nga waggulu ku kikomera waliyo abasajja abaawula buli muntu okusinziira ku bulambebwe, (abantu abo) balikoowoola abantu b’o mu jjana, nebabagamba nti e mirembe gibeere ku mmwe, anti (abalibeera mu kifo e kyo), baliba tebannayingira jjana era nga amaddu gabatta.');
INSERT INTO "african_dev" VALUES(7,47,'7:47','Ate amaaso gaabwe bwe galikyusibwa negoolekera abantu b’o mu muliro baligamba nti ayi Mukama omulabirizi waffe totuteeka wamu n’abantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(7,48,'7:48','Era abalibeera ku kikomera balikoowoola abantu be balimanya olw''obulambe bwabwe ne bagamba nti bye mwakunganya tebibayambye, wadde okwekuza kwe mwalimu.');
INSERT INTO "african_dev" VALUES(7,49,'7:49','Abo beebo be mwalayirirako nti Katonda tagenda kubasaasira (mwe wamma) muyingire e jjana, temugenda kutuukibwako kutya era temugenda kunakuwala.');
INSERT INTO "african_dev" VALUES(7,50,'7:50','Era abantu bo mu muliro balikoowoola abo mu jjana nebagamba nti, mutuwe ku mazzi oba n''ekirala kyonna Katonda kyabagabidde, baligamba nti mazima byombi Katonda yabiziza ku bakaafiiri.');
INSERT INTO "african_dev" VALUES(7,51,'7:51','Abo abaafuula e ddiini yaabwe e kinyumu n''omuzannyo obulamu bwe nsi nebubagayaaza kale nno olwaleero tujja kubeerabira nga nabo bwe beerabira nti walibaayo okutuuka ku lunaku luno, era n’okuba nti baali bawakanya e bigambo byaffe.');
INSERT INTO "african_dev" VALUES(7,52,'7:52','Era mazima ddala twabawa e kitabo mwe twannyonnyolera buli kintu nga tusinziira ku kumanya, (e kitabo e kyo) bulungamu era kusaasira eri abantu abakkiriza.');
INSERT INTO "african_dev" VALUES(7,53,'7:53','Abaffe balindirira okugyako Kur’ani by''eyogerako gye biriggwera, olunaku bwe lulituuka okulaga e bintu gye biriggwera, abo abaagireka oluberyeberye baligamba nti, ababaka ba Mukama omulabirizi waffe baaleeta amazima, naye abaffe tusobola okufuna abawolereza batuwolereze, oba tuyinza okuzzibwayo (ku nsi), netukola (e mirimu e mirungi) egitali e gyo gye twali tukola, mazima bafaafaaganirwa e myoyo gyabwe n’ebyo bye baali beegunjirawo ne bibabulako.');
INSERT INTO "african_dev" VALUES(7,54,'7:54','Mazima Mukama omulabirizi wa mmwe Katonda, yooyo eyatonda e ggulu omusanvu ne nsi mu nnaku mukaaga, olwo nno naatuula ku Arish, e kiro akibikka omusana, nga kigunoonya mu bwangu obuyitirivu, era yatonda e njuba n’omwezi ne munyenye nga byonna bitambulira ku kiragiro kye, abange mukimanye nti okutonda n’ebiragiro bibye. Yayawukana Katonda omulabirizi w’ebitonde.');
INSERT INTO "african_dev" VALUES(7,55,'7:55','(kale nno) musabe Mukama omulabirizi wa mmwe nga muli bakkakkamu ( mu lwatu) ne mu kyama, anti mazima yye tayagala basukka nsalo.');
INSERT INTO "african_dev" VALUES(7,56,'7:56','Era temwonoonanga mu nsi oluvanyuma lwokugirongoosa, era Katonda mumusabenga olw''okutya e bibonerezobye, oba olw''okululunkanira e birungi bye, anti mazima okusaasira kwa Katonda kuba kumpi n’abalongoosa (e mirimu gyabwe).');
INSERT INTO "african_dev" VALUES(7,57,'7:57','Era Katonda yooyo asindika e mpewo, nga zireeta amawulire agessanyu okuva mu kusaasirakwe, olwo nno bwezisindika e bire e bizito, e birimu amazzi tubiweereza eri e kitundu e kikalu, ne tukitonnyesaamu e nkuba, olwo nno netumeza nayo e bimera, era bwe tutyo nno naabafu bwetugenda okubazuukiza,mulyoke mwebuulirire (nakyo).');
INSERT INTO "african_dev" VALUES(7,58,'7:58','E kitundu kye nsi e kirungi ebisigwamu bimera bulungi olw''obuyinza bwa Mukama omulabirizi w''akyo, kyo e kibi tebimera okugyako nga bibi, bwe tutyo nno bwe tunnyonnyola e bigambo abantu abeebaza.');
INSERT INTO "african_dev" VALUES(7,59,'7:59','Mazima twatuma Nuhu eri abantu be, naagamba nti abange bantu bange musinze Katonda yekka, tewali kirala kyonna kisaana kusinzibwa mazima nze mbatiisa e bibonerezo by''olunaku oluzito.');
INSERT INTO "african_dev" VALUES(7,60,'7:60','Abakungu mu bantu be ne bagamba nti, mazima ffe tukulaba ng’oli mu bubuze obweyolefu.');
INSERT INTO "african_dev" VALUES(7,61,'7:61','Naagamba nti bantu bange siriiko bubuze bwonna naye mazima nze ndi Mubaka okuva ewa Mukama omulabirizi w’ebitonde.');
INSERT INTO "african_dev" VALUES(7,62,'7:62','Nga mbatusaako obubaka bwa Mukama omulabirizi wange, era nga mbabuulirira, era nga mmanyi okuva ewa Katonda ebyo bye mutamanyi.');
INSERT INTO "african_dev" VALUES(7,63,'7:63','Mwewuunya okulaba nti obubaka bubajjidde okuva ewa Mukama omulabirizi wa mmwe! nga bukkira ku Musajja ava mu mmwe, n''aba nga abeekesa, era kibafuule abatya Katonda mube nga musaasirwa.');
INSERT INTO "african_dev" VALUES(7,64,'7:64','Ne bamulimbisa ne tumuwonya n''abo abaali naye mu lyato, era ne tuzikiriza abo abaalimbisa e bigambo byaffe, anti mazima bo baali ba muzibe.');
INSERT INTO "african_dev" VALUES(7,65,'7:65','N’abeekika kya Aadi twabaatumamu muganda waabwe Huud, naagamba nti abange bantu bange musinze Katonda yekka, temulinaayo kirala kitali yye kisaana kusinzibwa, abaffe temutya?');
INSERT INTO "african_dev" VALUES(7,66,'7:66','Abakungu abo abaakaafuwala mu bantu be nebagamba nti mazima ffe tukulaba ng’oli mu bya butaliimu, era mazima ffe tukulaba, ng’omu ku balimba.');
INSERT INTO "african_dev" VALUES(7,67,'7:67','Naagamba nti bantu bange siri mu bya bwe wussa, wabula mazima ddala nze ndi Mubaka atumiddwa Mukama omulabirizi w’ebitonde.');
INSERT INTO "african_dev" VALUES(7,68,'7:68','Mbatuusaako obubaka bwa Mukama omulabirizi wange nga nange gyemuli ndi mubuulirizi omwesimbu.');
INSERT INTO "african_dev" VALUES(7,69,'7:69','Mwewuunya okuba nti obubaka bubajjidde okuva ewa Mukama omulabirizi wa mmwe! Nga bukkira ku musajja ava mu mmwe, naaba nga abeekesa!. Era mwandibadde mujjukira bweyabafuula abasigire oluvanyuma lwa bantu ba Nuhu, n''abongera obugimu mu butonde, kale nno mujjukire ebigabwa bya Katonda kibasobozesa okutuuka ku buwanguzi.');
INSERT INTO "african_dev" VALUES(7,70,'7:70','Nebagamba nti, abaffe e kikuleese gye tuli lwa kusinza Katonda yekka! tuleke e byo ba jajja ffe bye baali basinza, bwe kiba bwe kityo tutuuseeko e kyo ky’otulagaanyisa, bwoba nga oli mu boogera amazima.');
INSERT INTO "african_dev" VALUES(7,71,'7:71','(Hudu) naagamba nti e bibonerezo n’obusungu bya Mukama omulabirizi wa mmwe bibakakaseeko, muyinza mutya okumpakanya ku mannya gemwetuumira mmwe, ne bakadde ba mmwe, nga Katonda tabissangako bujulizi bwonna, kale nno mulindirire, mazima nze nindirira wamu na mmwe.');
INSERT INTO "african_dev" VALUES(7,72,'7:72','Netumuwonya n''abo abaali naye olw''okusaasira kwaffe, twaggyirawo ddala e mirandira gyabo abaalimbisa e bigambo byaffe, era tebabeerangako mu bakkiriza.');
INSERT INTO "african_dev" VALUES(7,73,'7:73','Era aba Thamudu twabatumira muganda waabwe Swaleh naagamba nti abange bantu bange musinze Katonda yekka temurinaayo kirala kyemulina kusinza ekitali yye, obujulizi bubajjidde okuva ewa Mukama omulabirizi wa mmwe (eno) Ngamiya ya Katonda, nga kya magero gye muli, kale nno mugireke erye mu nsi ya Katonda wonna weeyagala, temugituusaako akabi nemutuukwaako e bibonerezo e biruma ennyo.');
INSERT INTO "african_dev" VALUES(7,74,'7:74','Era mujjukire Katonda bwe yabazza mu bigere byaba Aadi, naabatebenkeza mu nsi, nga mu bifo ebya wansi mu yo, muzimbamu amayumba amatiribona, ng’ate e nsozi muzisima nezifuuka amayumba, kale nno mujjukire e byengera bya Katonda, era temugufuula omulimu okugenda nga mwonoona ku nsi.');
INSERT INTO "african_dev" VALUES(7,75,'7:75','Abakungu abo abeekuluntaza mu bantu be kibiina kye, nebagamba eri abo abaatwalibwanga okuba aba wansi mu bo, nga bagamba abo abaali bakkiriza mu bo, nti mukakasa nti ddala Swaleh Mubaka okuva eri Mukama omulabirizi we, nebagamba nti yye, mazima ddala ffe tukkiriza byonna ebyamutumwa.');
INSERT INTO "african_dev" VALUES(7,76,'7:76','(Olwo nno) abo abeekuza nebagamba nti, mazima ffe tuwakanyizza ddala e byo bye mukkirizza.');
INSERT INTO "african_dev" VALUES(7,77,'7:77','E ngamiya n ebagifumita ne bava ku kiragiro kya Mukama omulabirizi waabwe, era nebagamba owange Swaleh bwoba nga oli mu luse lwa babaka twanguyize e byo byo tulagaanyisa.');
INSERT INTO "african_dev" VALUES(7,78,'7:78','Okukankana okwamaanyi (Musisi) nekubajjira nebakeesa e nkya nga mirambo egigangalamye mu mayumba gaabwe.');
INSERT INTO "african_dev" VALUES(7,79,'7:79','Awo nno (Swaleh) kwekubavaako nga bwabagamba nti, yye mmwe bantu bange mazima nabatuusaako obubaka bwa Mukama omulabirizi wange, era nababuulirira naye (ekituufu) temwagala babuulirira.');
INSERT INTO "african_dev" VALUES(7,80,'7:80','Era (jjukira ggwe Nabbi Muhammad) Luutu nga bwe yagamba abantu be nti abaffe mukola eby''obuwemu, nga mu nsi yonna tewali muntu yali abikoze!');
INSERT INTO "african_dev" VALUES(7,81,'7:81','Mazima mmwe obwagazi bwa mmwe mu bukola ku basajja nemulekawo abakazi wabula bantu mmwe, kyemukola muli badiibuuzi.');
INSERT INTO "african_dev" VALUES(7,82,'7:82','Tekwali okwanukula kw’a bantube okugyako, okuba nga baagamba nti, (abo) mubafulumye mu nsi ya mmwe, anti bo bantu abeetwala okuba nti bayonjo.');
INSERT INTO "african_dev" VALUES(7,83,'7:83','Wabula twamutaasa n’abantube okugyako mukyala we, yasigala nabaatuukibwako e bibonerezo.');
INSERT INTO "african_dev" VALUES(7,84,'7:84','Netubasindikira e nkuba (nga y’amayinja) kale fumiitiriza e nkomerero ya bajeemu bwe yali.');
INSERT INTO "african_dev" VALUES(7,85,'7:85','Ate abe Madyana twabatumira muganda waabwe Swaibu, naagamba nti abange bantu bange musinze Katonda, ng’oggyeko yye tewali kirala kyemusaanidde kusinza, obunnyonnyofu okuva ewa Mukama omulabirizi wa mmwe bubajjidde, kale nno mutuukirize ebirengwa n’ebipimwa, temugaananga okutuukiririza abantu ebintu byaabwe, era temwonoonanga mu nsi oluvanyuma lwokugikola nga nungi, ekyo nno mmwe kye kirungi gye muli bwe muba nga muli bakkiriza.');
INSERT INTO "african_dev" VALUES(7,86,'7:86','Era temuteeganga abantu mu kkubo lyonna nemubatiisa (olw''okwagala okubanyaga) nemuziyiza abantu abakkiriza Katonda okuweereza mu kkubo lye, ne mulifuula ebbi eri abantu, era mujjukire nga bwe mwali abatono naabayingiwaza, era mulabe e nkomerero y’abonoonyi nga bwe yali.');
INSERT INTO "african_dev" VALUES(7,87,'7:87','Era bwekiba nga e kitundu ku mmwe bakkiriza ekyo kye nnatumwa nakyo, ng’ate e kitundu e kirala tebakkiriza, kale mulinde okutuusa Katonda lwanaatulamula, anti nga bulijjo yaasinga abalamuzi.');
INSERT INTO "african_dev" VALUES(7,88,'7:88','Abakungu abo abeekuluntaza mu bantube, baagamba nti owange Swaibu tujja kukugoba mu kitundu kyaffe n’abo abakkiriza abali naawe, oba ssi ekyo, oteekeddwa okudda mu ddiini yaffe. Naagamba nti ne bwetuba nga e kyo si kyetwagala.');
INSERT INTO "african_dev" VALUES(7,89,'7:89','(Ne tumala tukikola) mazima tunaaba tugunjizza ku Katonda e bigambo e byobulimba singa tudda mu ddiini ya mmwe, oluvanyuma lwa Katonda okugituwonya, era tewali nsonga egituddisaamu, beppo nga Mukama omulabirizi waffe yaayagadde, Mukama omulabirizi waffe okumanyakwe kubuna buli kintu, ku Katonda kwe twekutte, ayi Mukama omulabirizi waffe salawo wakati waffe n’abantu baffe mu mazima, anti ggwe osinga abasalawo bonna.');
INSERT INTO "african_dev" VALUES(7,90,'7:90','Abakungu abo abaakaafuwala mu bantu be ne bagamba nti, singa mugoberera Swaibu, olwo nno mazima mmwe, mujja kubeera mu bafaafaaganiddwa.');
INSERT INTO "african_dev" VALUES(7,91,'7:91','Okukankana okw''amaanyi (Musisi) ne kubajjira ne bakeesa nga mirambo e gigangalamye mu mayumba gaabwe.');
INSERT INTO "african_dev" VALUES(7,92,'7:92','Abo abaalimbisa Swaibu ne baba nga abateeyagalirangako mu mayumba gaabwe, abo abaalimbisa Swaibu bo be bafaafaaganirwa.');
INSERT INTO "african_dev" VALUES(7,93,'7:93','Olwo nno Swaibu n’abavaako (nga takyalina kya ku bakolera) naagamba nti abange bantu bange, mazima nabatusaako obubaka bwa Mukama omulabirizi wange, era nembabuulirira. Butya bwennyinza okunyolwa olw’abantu abakaafiiri.');
INSERT INTO "african_dev" VALUES(7,94,'7:94','Tewali Nabbi gwe twatuma mu kitundu kyonna okugyako ng’abantu b’omukintu e kyo, (bwe bamulimbisa) tubatuusaako obubenje n’obuzibu, olwo nno balyoke bagonde.');
INSERT INTO "african_dev" VALUES(7,95,'7:95','Oluvanyuma mu kifo kye kibi ne tuwaanyisaamu e kirungi, okutuusa lwe baayala e mbeera yaabwe neetereera, ne bagamba nti, (si ffe tusoose) ne bakadde baffe baatuukibwako obuzibu, ate oluvanyuma ne batuukibwako obwangu. (awamu n''ekyo Katonda agamba) ne tubakwata (ne tubatta) nga nabo tebategeera.');
INSERT INTO "african_dev" VALUES(7,96,'7:96','Singa abantu b’o mu bitundu e byo bakkiriza ne batya Katonda, twandibagguliddewo e mikisa okuva mu ggulu ne mu nsi, naye baalimbisa, olwo nno netubabonereza olw’ebyo bye baakola.');
INSERT INTO "african_dev" VALUES(7,97,'7:97','Abaffe abantu b’omu bitundu e byo baalina obukakafu nti e bibonerezo byaffe tebiyinza kubatuukako kiro, nga nabo beebase.');
INSERT INTO "african_dev" VALUES(7,98,'7:98','Oba abantu b’omu bitundu e byo baalina obukakafu nti e bibonerezo byaffe tebiyinza ku batuukako misana, nga nabo bali mu kutiguka.');
INSERT INTO "african_dev" VALUES(7,99,'7:99','Abaffe baawona okusalawo kw’a Katonda, (e kituufu kiri nti) tewali ayinza kulowooza nti ayinza okuwona okusalawo kw’a Katonda okugyako abantu abafaafaaganirwa.');
INSERT INTO "african_dev" VALUES(7,100,'7:100','Abaffe tekitegeerekeka eri abo abasikira e nsi oluvanyuma lw’abagibaddemu okufa nti, singa twayagala twalibavunaanye olw’e byonoono bya bwe, ne tuteeka e nvumbo ku mitima gyabwe nebaba nga tebawulira.');
INSERT INTO "african_dev" VALUES(7,101,'7:101','E bitundu e byo tukutegeeza e bimu ku byafaayo byabyo, era mazima ababaka baabwe baabajjira n’obunnyonnyofu, baali tebayinza kukkiriza ebyo bye baalimbisa mu kusooka, bulijjo bwatyo Katonda bwazibikira e mitima gy’a bakafiiri.');
INSERT INTO "african_dev" VALUES(7,102,'7:102','Era abasinga obungi mu bo tetwabasanga na ndagaano yonna (gye baatuukiriza), wabula twasanga abasinga obungi mu bo nga boonoonyi.');
INSERT INTO "african_dev" VALUES(7,103,'7:103','Ate oluvanyuma lwabwe, twatuma Musa ewa Firawo n’abakungube, nga tumuwadde eby’amagero byaffe nebabiwakanya, kale nno, fumiitiriza olabe e nkomerero y’aboonoonyi nga bwe yali.');
INSERT INTO "african_dev" VALUES(7,104,'7:104','Musa naagamba nti, owange Firawo mazima nze ndi mubaka okuva ewa Mukama Katonda omulabirizi w’ebitonde.');
INSERT INTO "african_dev" VALUES(7,105,'7:105','Kikakafu okuba nga soogera ku Katonda okugyako amazima, mazima mbaleetedde okuva ewa Mukama omulabirizi wa mmwe obujulizi, n’olwekyo mpa abaana ba Israil (ngende nabo).');
INSERT INTO "african_dev" VALUES(7,106,'7:106','(Firawo) naagamba nti oba olina obujulizi bwozze nabwo buleete, bwoba nga ddala oli mu boogera amazima.');
INSERT INTO "african_dev" VALUES(7,107,'7:107','Awo Musa n’asuula omuggogwe, okugenda okwekanga nga gwo, musota ogwannamaddala.');
INSERT INTO "african_dev" VALUES(7,108,'7:108','Era naggyayo omukonogwe, okugenda okulaba nga mweru eri buli atunula.');
INSERT INTO "african_dev" VALUES(7,109,'7:109','Abakungu mu bantu ba Firawo nebagamba nti mazima ono, mulogo kakensa.');
INSERT INTO "african_dev" VALUES(7,110,'7:110','Ayagala ku baggya mu nsi ya mmwe, kati mmwe kiki kye mugamba.');
INSERT INTO "african_dev" VALUES(7,111,'7:111','Nebagamba nti, mugambe ne mugandawe balindeko, otume mu bitundu byonna abakunga (abalogo).');
INSERT INTO "african_dev" VALUES(7,112,'7:112','Bajja kukuleetera buli mulogo omukugu.');
INSERT INTO "african_dev" VALUES(7,113,'7:113','Era abalogo bajja ewa Firawo ne bagamba nti, mazima tuteekwa okusasulwa, kavuna tuba nga ffe tuwangudde.');
INSERT INTO "african_dev" VALUES(7,114,'7:114','Naagamba nti weewaawo, era mazima ddala mujja kubeera mu abo abambeera ku lusegere.');
INSERT INTO "african_dev" VALUES(7,115,'7:115','Nebagamba nti owange Musa ggwe osooka okwanja, oba ffe tusooka okussaawo.');
INSERT INTO "african_dev" VALUES(7,116,'7:116','Naagamba nti musseewo, bwe bassaawo baaloga amaaso g’abantu, era nebabatiisa nnyo, anti baaleeta e ddogo e pitirivu.');
INSERT INTO "african_dev" VALUES(7,117,'7:117','Netutumira Musa nti, suula omuggogwo, negutandika okumira byonna, ebyo bye baali bajingiridde.');
INSERT INTO "african_dev" VALUES(7,118,'7:118','Amazima negalabika, byonna bye baali bakola nebifa.');
INSERT INTO "african_dev" VALUES(7,119,'7:119','Bwe batyo nebawangulirwa awo wennyini, nebafuuka abakkakanyiziddwa.');
INSERT INTO "african_dev" VALUES(7,120,'7:120','Bbo abalogo beesanga nga bateekwa kuvunnama.');
INSERT INTO "african_dev" VALUES(7,121,'7:121','Nebagamba nti tukkirizza Mukama Katonda omulabirizi w’ebitonde.');
INSERT INTO "african_dev" VALUES(7,122,'7:122','Mukama omulabirizi wa Musa ne Haruna.');
INSERT INTO "african_dev" VALUES(7,123,'7:123','Firawo naagamba nti mumukkirizza nga sibawadde lukusa, (kirabika nti mazima ddala) luno lukwe lwe mwakoledde mu kitundu kino olw''ekigendererwa ky’okugobamu abantu baamu, naye muggya ku kimanya.');
INSERT INTO "african_dev" VALUES(7,124,'7:124','Nja kubatemako e mikono n’amagulu gammwe mu mpuyi ez’enjawulo (Nga bwetutema omukono ogwa ddyo, okugulu tutema kwa kkono) ate oluvanyuma mwenna nja kubakomerera ku misaalaba.');
INSERT INTO "african_dev" VALUES(7,125,'7:125','Nebagamba nti (ekyo temuli), anti mazima ddala ffe olwo tunaaba tudda eri Mukama omulabirizi waffe.');
INSERT INTO "african_dev" VALUES(7,126,'7:126','Tolina kyotuvunaana okugyako okuba nti tukkirizza e bigambo by’a Mukama omulabirizi waffe e bitujjidde, ayi Mukama omulabirizi waffe tugonnomoleko obugumiikiriza, era otuuse e ntuuko yaffe nga tuli basiraamu.');
INSERT INTO "african_dev" VALUES(7,127,'7:127','Abakungu mu bantu ba Firawo nebagamba nti, ogenda kuleka Musa n’abantube babe nga boonoona mu nsi, (era osuubire nti Musa an’akuleka) neba Katondabo, (Firawo) kwe kugamba nti, abaana baabwe ab’obulenzi tujja kubatta, tulekewo bawala baabwe, mazima ffe, nga bulijjo tubali waggulu, era tubasukkulumye mu maanyi.');
INSERT INTO "african_dev" VALUES(7,128,'7:128','Musa naagamba abantube nti, mwekwate ku Katonda era mugumiikirize, mazima ddala ensi y’aKatonda, agisikiza abo b’aba ayagadde mu baddube, era e nkomerero (e nnungi), y''abo abatya Katonda.');
INSERT INTO "african_dev" VALUES(7,129,'7:129','Nebagamba nti, mazima twabonyabonyezebwa nga tonnatujjira, n’oluvanyuma lw’okutujjira! (Musa) naagamba nti, Mukama omulabirizi wa mmwe ajja kuzikiriza omulabe wa mmwe, mu kifo kya bwe azzeewo mmwe, nga abasigire mu nsi, olwo nno atunuulire alabe bwe mukola.');
INSERT INTO "african_dev" VALUES(7,130,'7:130','Mazima twabonereza abantu ba Firawo n’emyaka gy’enjala, n’ekkekwa ly’ebibala, babe nga bajjukira.');
INSERT INTO "african_dev" VALUES(7,131,'7:131','Bwe kyabajjira nga e kirungi baagambanga nti, kino kyaffe, (ffe tukikoze) buli lwe baatukwangako e kibi, nga beekwasa Musa n’abo abaali naye, naye nga ddala e kikwa kyabwe kiri wa Katonda, wabula mazima abasinga obungi mu bo tebamanyi.');
INSERT INTO "african_dev" VALUES(7,132,'7:132','Era nebagamba nti, buli kabonero konna k’onootuleetera obe nga otuloga nako, tetugenda kukukkiriza.');
INSERT INTO "african_dev" VALUES(7,133,'7:133','Olwo nno netubasindikira amataba, n’enzige, n’ensekere, n’ebikere n’omusaayi nga bubonero obulagiddwa obulungi, wabula ne beekuluntaza, anti era bulijjo baali bantu aboonoonyi.');
INSERT INTO "african_dev" VALUES(7,134,'7:134','Ekibonerezo bwe kyamala okubatuukako nebagamba nti, owange Musa tusabire Mukama omulabiriziwo, ng’osinziira ku ebyo bye yakuwa, singa otuggyako e kibonerezo kino, ddala tujja kukukkiriza era tugenda kukuweera ddala abaana ba Israil (obatwale).');
INSERT INTO "african_dev" VALUES(7,135,'7:135','Bwe twamala okubaggyako e kibonerezo nga e bbanga eryali libagereddwa liweddeko, ekyaddirira bo kumenya ndagaano.');
INSERT INTO "african_dev" VALUES(7,136,'7:136','Olwo nno netubalaga obusungu bwaffe, netubazikiriza mu nnyanja olw''okuba nti, mazima bo baalimbisa e bigambo byaffe, era nga ku byo baali bagayaavu.');
INSERT INTO "african_dev" VALUES(7,137,'7:137','Era abantu abo abaali batwalibwa okuba nti ba wansi netubasikiza obuvanjuba n’obugwanjuba bwensi, eyo gye twassaamu e mikisa, nekituukirira e kigambo kya Mukama omulabiriziwo, e kirungi ku baana ba Israil ku lw''ebyo bye baagumiikiriza, netusaanyaawo byonna Firawo n’abantube bye baali bakola ne bye baali bazimba.');
INSERT INTO "african_dev" VALUES(7,138,'7:138','Netusomosa abaana ba Israil e nnyanja, nebayita ku bantu abaali beefunyiridde ku kusinza amasanamu gaabwe, nebagamba nti owange (Musa) tuteerewo e kisinzibwa nga nabo bwe balina e bisinzibwa, (Musa) naagamba nti mazima ddala mmwe, muli bantu abatategeera.');
INSERT INTO "african_dev" VALUES(7,139,'7:139','Mazima abo bye balimu bya kuzikirizibwa, era ebyo bye bakola bifu.');
INSERT INTO "african_dev" VALUES(7,140,'7:140','Naagamba nti, ekitali Katonda omu omutuufu kyemba mbateerawo musinze! ate nga yye yabasukkulumya ku bitonde byonna!');
INSERT INTO "african_dev" VALUES(7,141,'7:141','(Ebyogeddwa byonna mu aya ezikulembedde okubaawo kyagoberera e kyafaayo e kiddirira e kigamba nti), mujjukire bwe twabataasa ku bantu ba Firawo bwe baali babakombesa ku bukaawu bwe bibonyobonyo, nga batemula abaana ba mmwe abalenzi nebalekawo bawala ba mmwe, mu ekyo mwalimu okugezesa kunene nnyo okuva eri Mukama omulabirizi wa mmwe.');
INSERT INTO "african_dev" VALUES(7,142,'7:142','Twalagira Musa amale e biro makumi asatu nga yeetegekera okusisinkana Mukama omulabiriziwe, ate netubijjuza n’ekkumi, olwo nno Mukama omulabiriziwe kye yamugerera nekituukirira, nga z’ennaku amakumi ana, awo Musa naagamba mugandawe, Haruna nti, nsigalira mu bantu bange, era kola bulungi, togoberera n’omulundi n’ogumu e kkubo ly’aboonoonyi.');
INSERT INTO "african_dev" VALUES(7,143,'7:143','Musa bwe yajja mu kifo kye twamulagira, Mukama omulabiriziwe n’ayogera naye, (Musa) yagamba nti, ayi Mukama omulabirizi wange, nzikiriza nkutunuleko, n’amugamba nti tosobola kundaba, naye tunula ku lusozi olwo, bwe lunaasobola okusigala mu kifo kyalwo, olwo nno ojja kundaba, wabula Mukama omulabiriziwe bwe yeeyoleka eri olusozi, yalufuula e nsaano, era Musa n’agwa ku ttaka nga azirise, bwe yadda e ngulu yagamba nti, oli musukkulumu, nkwenenyerezza, era nkwata kisooka mu bakkiriza.');
INSERT INTO "african_dev" VALUES(7,144,'7:144','(Katonda) naagamba nti, owange Musa mazima nze nkuwadde e nkizo ku bantu, olw’obubaka bwange, n’okwogera kwange naawe, kale twala bye nkuwadde, era obeere mu beebaza.');
INSERT INTO "african_dev" VALUES(7,145,'7:145','Era twamuwandiikira ku mbaawo buli kintu, kibe ekyokubuulirira, era nga kunnyonnyola mu bujjuvu, ku buli kintu, kale nno bikuume butiribiri, era olagire abantubo babikwate mu ngeri esinga okuba e nnungi, oluvanyuma ngenda ku balaga e kifo ky’aboonoonyi.');
INSERT INTO "african_dev" VALUES(7,146,'7:146','Abo abeekuza mu nsi awatali nsonga ntuufu, ngenda kubeesambya e bigambo byange, buli lwe banaalabanga, akabonero konna, tebajja ku kakkirizanga, era bwe banaalabanga e kkubo ly’o bu lungamu, tebagendanga kulifuula kkubo gye bali (kulikwata), naye bwe banaalabanga e kkubo ly’obwonoonefu lye banaatwalanga okuba e kkubo, ekyonno lwa kuba nti mazima ddala bo, baalimbisa e bigambo byaffe, nebaba nga ku byo, beesuulirayo gwa naggamba.');
INSERT INTO "african_dev" VALUES(7,147,'7:147','Naabo abaalimbisa e bigambo byaffe, n’okusisinkana olunaku lw’enkomerero, e mirimu gyabwe gyonna gyafaafaagana, (ate olwo) abaffe balisasulwa okugyako ebyo bye baakolanga!');
INSERT INTO "african_dev" VALUES(7,148,'7:148','Musa bwe yagenda, abantube, baabumba e nnyana okuva mu by''okwewunda byabwe, nga erabikira ddala nga erina omubiri, n’okungonga engonga, abaffe tebaakiraba nti mazima ddala yyo tesobola kw’ogera nabo! era nga tesobola kubalungamya ku kkubo, b’agyeteerawo, era olwekikolwa ekyo nebabeera ab’omu bantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(7,149,'7:149','Bwe beefumiitiriza, nebalaba nti ddala mazima baabula olw’ekikolwa kyabwe ekyo, (beenenya) nebagamba nti, singa Mukama omulabirizi waffe tatusaasira, naatusonyiwa, tujja kubeerera ddala mu bafaafaaganiddwa.');
INSERT INTO "african_dev" VALUES(7,150,'7:150','Musa bwe yamala okudda eri abantube, nga musunguwavu, era nga asaaliddwa, yagamba nti, kibi nnyo kye mwakoze nga siriiwo, mwayaanguyirizza e kigambo ky’a Mukama omulabirizi wa mmwe! olwo nno kwe kukasuka e mbaawo, n’akwata omutwe gw’a mugandawe, nga amusika amuzza gyali, (Haruna) naagamba nti mwana wa nnyabo, mazima abantu bansinzizza amaanyi, era kumpi banzite, tontusaako kino olw''okusanyusa abalabe, era tonzisa mu bantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(7,151,'7:151','Musa naagamba nti, ayi Mukama omulabirizi wange, nsonyiwa ne muganda wange, era otuyingize mu kusaasirakwo, anti bulijjo ggwe musaasizi asinga abasaasizi bonna.');
INSERT INTO "african_dev" VALUES(7,152,'7:152','Mazima abo abeeteerawo e nnyana (bagisinze), bajja kutuukwako obusungu n’okukkakkannyizibwa ebiva ewa Mukama omulabirizi waabwe, mu bulamu obwe nsi, era bwe tutyo bwe tusasula abo abatemerera e bigambo.');
INSERT INTO "african_dev" VALUES(7,153,'7:153','Naabo abakola ebibi, ate nebeenenya oluvanyuma lwabyo, era nebakkiriza, mazima ddala Mukama omulabiriziwo, oluvanyuma lwabyo, musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(7,154,'7:154','Obusungu bwe bwaggwa ku Musa, yasitula e mbawo, nga ne mu biwandiikiddwa ku zo, mulimu obulungamu, n’okusaasira eri abo, bo abatya Mukama omulabirizi waabwe.');
INSERT INTO "african_dev" VALUES(7,155,'7:155','Musa yalonda mu bantube, abasajja nsanvu, bagende mu kifo kye twamuteerawo, wabula musisi bwe yabajjira, (Musa) yagamba nti, ayi Mukama omulabirizi wange, singa kino kye wali oyagala, wandibadde wabazikiriza olubereberye wamu nange, oyagala kutuzikiriza olw''ekyo ababuyabuya mu ffe kye baakola! kino si kintu kirala kyonna okugyako okugezesakwo, obuza nakwo oyo gwoba oyagadde, ate n’olungamya nakwo, oyo gwoba oyagadde, otulinako obuyinza, n’olwekyo tusonyiwe, era otusaasire, bulijjo ggwe asinga abasaasizi bonna.');
INSERT INTO "african_dev" VALUES(7,156,'7:156','Tuwandiikeko birungi kuno ku nsi, era ne ku nkomerero, (anti) tukwemenyedde, (Katonda) naagamba nti, e bibonerezo byange mbituusa kw’oyo gwemba njagadde, ate okusaasira kwange kwamalayo buli kintu, ngenda ku kukuwa abo abatya Katonda, nebatoola zzaka, era abo abakkiriza e bigambo byaffe.');
INSERT INTO "african_dev" VALUES(7,157,'7:157','Abo abagoberera omubaka, Nabbi, atasoma biwandiiko, oyo gwe basanga nga muwandiike ewaabwe mu Taurat ne enjili, nga abalagira e mpisa e nnungi, era n’abagaana e mpisa e mbi, era nga abakkiriza e birungi (mu biriibwa n’ebinywebwa), era naabaziyiza e bibi, era naabaggyako obuzito n’amakoligo, ebyo e byabaliko, kale nno abo abamukkiriza, nebamuwagira, ne bamutaasa, era ne bagoberera e kitangaala ekyo e kyamuweebwa, abo nno bo, be balituuka ku buwanguzi.');
INSERT INTO "african_dev" VALUES(7,158,'7:158','Gamba (ggwe Nabbi Muhammad), nti abange abantu, mazima nze ndi mubaka w’a Katonda, eri mmwe mwenna, (Katonda) oyo bubwe obufuzi bw''eggulu omusanvu ne nsi, tewali kintu kyonna kisinzibwa okugyako yye, awa obulamu, era natta, kale nno mukkirize Katonda n’omubakawe, Nabbi ataasoma biwandiiko, oyo akkiriza Katonda n’ebigambobye, era mu mugoberere, olwo nno mube nga mulungamye.');
INSERT INTO "african_dev" VALUES(7,159,'7:159','Mu bantu ba Musa, mulimu e kibiina nga bakozesa amazima, era nga gebakozesa okukola obwenkanya.');
INSERT INTO "african_dev" VALUES(7,160,'7:160','Era twabakutulamu e nnyiriri kkumi na bbiri nga bika, era twatumira Musa, abantube bwe baamusaba okubafunira amazzi, ne tumugamba nti, kuba omuggo gwo ku jjinja, olwo nno nemufubutukamu e nsulo kkumi na bbiri, buli bantu baamanya webalina okunywa, ekirala twabasiikiriza n’ekire, era twabassiza Manna, ne Saluwa (nga bwe tubagamba nti) mulye e birungi mu ebyo bye tubagabira, si ffe bebaayisa obubi, wabula beeyisa bokka obubi.');
INSERT INTO "african_dev" VALUES(7,161,'7:161','Era jjukira bwe baalagirwa nti, mutuule mu kitundu kino, mulye nga muli mu kyo, wonna wemwagala, era mugambe nti, Hittwa (tuggyeko e bibi), era muyingire omulyango gw’ekibuga nga muvunnamye, olwo nno tujja kubasonyiwa e byonoono bya mmwe, tujja kwongeza abakozi b’obulungi.');
INSERT INTO "african_dev" VALUES(7,162,'7:162','Abo abeeyisa obubi, baakyusa e kigambo ekyabagambwa, olwo nno ne tubasindikira e bibonerezo okuva mu ggulu olw''ebyo bye beeyisangamu obubi.');
INSERT INTO "african_dev" VALUES(7,163,'7:163','Babuuze ebikwata ku kitundu e kyali ku lubalama lwe nyanja, bwe baamenya e tteeka erikwata ku lunaku lw’omukaaga, olw''okuba e byennyanja byabwe bya bajjiranga, mu bungi ate nga ku lunaku olutali lw’amukaaga nga tebibajjira, bwe tutyo bwe tubakema olw''ebyo bye baakola mu bwonoonyi.');
INSERT INTO "african_dev" VALUES(7,164,'7:164','Era jjukira e kibiina mu bo bwe kyagamba nti, lwaki mubuulirira e kibiina kya bantu nga Katonda ajja kubazikiriza, oba ajja kubabonereza n’ebibonerezo e bisuffu, bo nebagamba nti tukikola lwa kweggyako musango ewa Mukama omulabirizi wa mmwe, ate sinakindi nabo bayinza okutya Katonda.');
INSERT INTO "african_dev" VALUES(7,165,'7:165','Kebaamala nebeerabira e byababuulirwa, twawonya abo abakoma ku bannaabwe okukola e bibi, ate abo abeeyisa obubi netubassaako e bibonerezo e bikambwe olw''obwonoonyi bwe baakolanga.');
INSERT INTO "african_dev" VALUES(7,166,'7:166','Bwe baalemera kw''ebyo e byabagaanibwa twabagamba nti, mubeere enkobe kibafuule abanyoomebwa.');
INSERT INTO "african_dev" VALUES(7,167,'7:167','Era jjukira Mukama omulabiriziwo bwe yalangirira nti (abayudaaya) ajja kubateerawo ddala abantu abanaabakombesanga ku bukaawu bwe bibonerezo, okutuusa ku lunaku lw’enkomerero, mazima Mukama omulabiriziwo mwangu mu kubonereza, era mazima yye. Musonyiyi nnyo wa kisa.');
INSERT INTO "african_dev" VALUES(7,168,'7:168','Era twabagabanya mu nsi, nebaba e bibiina e byenjawulo, mu bo mulimu abalongoofu, era mu bo mulimu abatali ekyo, era twabagezesa n’ebirungi n’ebibi kibayambe okudda (eri Katonda).');
INSERT INTO "african_dev" VALUES(7,169,'7:169','Newajja oluvanyuma lwabwe abaabaddira mu bigere, abaasikira e kitabo nebaba nga bakulembeza by’akufuna bya nsi ebitaliimu, era nebagamba nti tulisonyiyibwa, era singa bajjirwa e birala e bibifaanana, nabyo bandibitutte, abaffe tebaakozesebwa ndagaano y’ekitabo okuba nga teboogera ku Katonda okugyako amazima, era nga basoma ebyo ebiri mu kitabo, ate nga ekituufu kiri nti, obutuulo obw''enkomerero bwe bulungi eri abo abatya Katonda, abaffe temutegeera.');
INSERT INTO "african_dev" VALUES(7,170,'7:170','Ate abo abeekwata ku kitabo nebayimirizaawo e sswala, mazima ffe tetugenda kwonoona mpeera y’abalongoofu.');
INSERT INTO "african_dev" VALUES(7,171,'7:171','Era jjukira bwe twatengeesa olusozi waggulu waabwe, neruba nga manvuli, nebatuuka okulowooza nti mazima ddala lugenda kubagwako (awonno netubagamba nti) bye tubawadde mubikwate butiribiri, era mujjukire ebibirimu, olwo nno mube abatya Katonda.');
INSERT INTO "african_dev" VALUES(7,172,'7:172','Era jjukira Mukama omulabiriziwo bwe yajja ku migongo gy’abaana ba Adam ezzadde lyabwe, n’abalagira beeweeko obujulizi, (naagamba nti) Sinze Mukama omulabirizi wa mmwe, nebagamba nti, bwe guli, era tukiwaako obujulizi, olwo nno muleme kugamba ku lunaku lw''enkomerero nti, mazima ddala ffe, kino twali tetukimanyi.');
INSERT INTO "african_dev" VALUES(7,173,'7:173','Oba muleme kugamba nti, bakadde baffe beebaakola shiriki ffe nga tetunajja, era ffe twali zzadde eryajja oluvanyuma lwabwe. Otuzikiriza otya olw''ebyo ebyakolebwa aboonoonyi.');
INSERT INTO "african_dev" VALUES(7,174,'7:174','Era nno bwe tutyo bwe tunnyonnyola e bigambo, babe nga badda (eri okusinza Katonda yekka).');
INSERT INTO "african_dev" VALUES(7,175,'7:175','Era basomere ebikwata ku oyo gwe twawa obubonero bwaffe n''abwesamba, ne Sitane naamunoonya (n''amufuula munne) olwo nno naabeera ow''omu babuze.');
INSERT INTO "african_dev" VALUES(7,176,'7:176','Singa twayagala twandimusitudde e ddaala olw''obubonero obwo, naye mazima ddala yye yeemalira ku nsi, era n’agoberera okwagalakwe, olwo nno e kifaananyi kye alinga e mbwa bwogigoba e misinde ewejjawejja, ate nebwogireka eba ewejjawejja, ekyo nno kye kifaananyi kyabo abaalimbisa e bigambo byaffe, bategeeze ebyafaayo kibayambe ku bigambo byaffe, bategeeze ebyafaayo, kibayambe okwefumiitiriza.');
INSERT INTO "african_dev" VALUES(7,177,'7:177','Kifaananyi kibi eky’a bantu abaalimbisa e bigambo byaffe, ate nga ekituufu kiri nti emyoyo gyabwe gye baayisanga obubi.');
INSERT INTO "african_dev" VALUES(7,178,'7:178','Oyo yenna Katonda gw''aba alungamizza ye y’abeera omulungamu, ate gwabuza, abo nno be baafaafaaganirwa.');
INSERT INTO "african_dev" VALUES(7,179,'7:179','Omuliro Jahannama twagutegekera bangi mu majinni ne mu Bantu, (kyova olaba) nti, balina e mitima naye tebagikozesa kutegeera, era balina amaaso gebatalabisa, era balina amatu gebatawuliza, abo nno balinga e bisolo, sinakindi bo b’e babuze okusinga e bisolo, abo nno be beesuulirayo ogwa nnagamba (ku bigambo bye ddiini).');
INSERT INTO "african_dev" VALUES(7,180,'7:180','Era Katonda alina amannya amalungi, kale mugamusabise, era muve kw’abo abakyusakyusa amannyage, balisasulwa ebyo bye baakola.');
INSERT INTO "african_dev" VALUES(7,181,'7:181','Mu bantu betwatonda, mulimu e kibiina nga bo bakolera ku mazima, era gebakozesa okukola obwenkanya wakati w’abantu.');
INSERT INTO "african_dev" VALUES(7,182,'7:182','Naabo abaalimbisa tujja kubatwaliriza mpola, naye tubakwate nga tebamanyi.');
INSERT INTO "african_dev" VALUES(7,183,'7:183','Era mbalindiriza nebeeyagalira (mu byengera) naye nga mazima entegeka yange nnyweevu.');
INSERT INTO "african_dev" VALUES(7,184,'7:184','Abaffe tebaalowoozaako (n’omulundo n''ogumu) nti munnaabwe ono si mugwi wa ddalu, (e kituufu kiri nti) tali kintu kyonna okugyako okuba nti mutiisa ow’olwatu.');
INSERT INTO "african_dev" VALUES(7,185,'7:185','Abaffe tebaatunulako mu bufuzi bwe ggulu omusanvu ne nsi! na buli kyonna Katonda kyeyatonda! n’okuba nti kirabika nti e nkomerero yaabwe mazima yali etuuse! kaakano oluvanyuma lwa kino bigambo ki bye bayinza okukkiriza!.');
INSERT INTO "african_dev" VALUES(7,186,'7:186','Oyo yenna Katonda gwabuza tewali ayinza ku mulungamya, era abaleka mu bubuze bwa bwe nga babulubuuta.');
INSERT INTO "african_dev" VALUES(7,187,'7:187','Bakubuuza ku lunaku lw''enkomerero nti lulituuka ddi, gamba nti, mazima ebirukwatako Mukama omulabirizi wange y''abimanyi, tewali ayinza ku kubuulira mu butuufu nti lulituuka ddi? okugyako yye, ebilukwatako bizito mu ggulu omusanvu, ne ku nsi, terugenda kubajjira okugyako kibwatukira, balukubuuza ng’olinga alumanyiiko ennyo, gamba nti, mazima ebyokumanya olunaku olwo biri wa Katonda, naye ddala abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(7,188,'7:188','Gamba nti si nze neesalirawo e kirungi oba e kibi, okugyako Katonda kyaba ayagadde, era singa nnali mmanyi e byekusifu, nandiyitirizza okukola e birungi, mu ngeri y''emu tewandibadde kabi kantuukako, nze siri kintu kyonna okugyako okuba omutiisa, omusanyusa eri abo abatya Katonda.');
INSERT INTO "african_dev" VALUES(7,189,'7:189','Katonda yye yooyo eyabatonda nga abaggya mu muntu omu, ng’ate mu ye mwe yaggya mukyalawe, abeere ng’adda gyali olw''okufuna obutebenkevu, bwe yamala okulabagana naye, naafuna olubuto olwangu, n’ayita nalwo mu mitendera (olubuto mweruyita) olw’amala okukula, baasaba Mukama omulabirizi waabwe, nebagamba nti, singa otuwa omwana omulongoofu, tujja kubeerera ddala mu beebaza.');
INSERT INTO "african_dev" VALUES(7,190,'7:190','(Naye ate abantu abaddirira) Katonda bwe yabawa omwana omulongoofu, baamugattako e bintu e birala mu ekyo kye yabawala, Katonda w''anjawulo nnyo, kw''ebyo bye bamugattako.');
INSERT INTO "african_dev" VALUES(7,191,'7:191','Abaffe bagatta ku Katonda ebyo ebitasobola kutonda kintu kyonna, ng’ate byo byatondebwa.');
INSERT INTO "african_dev" VALUES(7,192,'7:192','Era tebiyinza kubafunira kutaasa wadde bo bennyini okwetaasa.');
INSERT INTO "african_dev" VALUES(7,193,'7:193','Bwe mubakoowoola okujja eri obulungamu tebabagoberera, kye kimu kamube nga mubayise, oba nga musirise.');
INSERT INTO "african_dev" VALUES(7,194,'7:194','Mazima abo bemusaba nemuva ku Katonda, baddu nga mmwe, kale mubasabe babaanukule bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(7,195,'7:195','Abaffe balina amagulu nti gebatambuza, oba balina e mikono gye bakwasa, oba balina amaaso gebalabisa, oba balina amatu gebawuliza, gamba nti muyite bye musinza ne mubigatta ku Katonda, olwo nno munneekobaanire temunnindiriza.');
INSERT INTO "african_dev" VALUES(7,196,'7:196','Mazima omukuumi wange ye Katonda, oyo eyassa e kitabo, era bulijjo yye, ye mukuumi w’abalongoofu.');
INSERT INTO "african_dev" VALUES(7,197,'7:197','Era mazima ddala be musaba ne mumulekawo, tebasobola kubataasa, era nga bwe batasobola kwetaasa.');
INSERT INTO "african_dev" VALUES(7,198,'7:198','Bwe mubakoowoola okujja eri obulungamu tebawulira, obalaba nga bakutunuulidde kyokka nga tebalaba.');
INSERT INTO "african_dev" VALUES(7,199,'7:199','Nywerera ku kusonyiwa, era olagire abantu okweyisa obulungi, era ove ku batategeera.');
INSERT INTO "african_dev" VALUES(7,200,'7:200','Buli wonna wootuukirwangako okulabankanya kwa Sitane, saba obukuumi bwa Katonda, mazima yye Katonda awulira nnyo, mumanyi.');
INSERT INTO "african_dev" VALUES(7,201,'7:201','Mazima abo abatya Katonda, bwe baba baggyiddwa okubuzibwabuzibwa okuva eri Sitane, bajjukira Katonda, olwo nno nebalaba e kituufu.');
INSERT INTO "african_dev" VALUES(7,202,'7:202','Naye nga bannaabwe bongera kubasindika mu bwonoonefu, era nga tebaweera.');
INSERT INTO "african_dev" VALUES(7,203,'7:203','Bwotabaleetera kya magero, bagamba nti, waakiri waalikyeyiiyirizza, gamba nti, mazima ngoberera ebyo ebiba bimpereddwa okuva ewa Mukama omulabirizi wange. (Kur’ani) eno, mumuli oguvudde ewa Mukama omulabirizi wa mmwe, era bulungamu na kusaasira eri abo abakkiriza.');
INSERT INTO "african_dev" VALUES(7,204,'7:204','Buli Kur’ani lwesomwanga mugiwulirize, era mugisseeko omulaka, olwo nno mulyoke musaasirwe.');
INSERT INTO "african_dev" VALUES(7,205,'7:205','Bulijjo jjukira Mukama omulabiriziwo, mu mwoyogwo, nga olaga obugonvu n’okutya, awatali kusitula ddoboozi mu bigambo (nga ekyo okikola), enkya n’eggulo, era tobeeranga mu bagayaavu.');
INSERT INTO "african_dev" VALUES(7,206,'7:206','Mazima abo abali ewa Mukama omulabiriziwo, tebeekuluntaza nebatamusinza, era ba mutendereza, era nga yye yekka, gwebavunnamira.');
INSERT INTO "african_dev" VALUES(8,1,'8:1','Bakubuuza (ggwe Nabbi Muhammad) ebikwata ku minyago, gamba nti e minyago gy’a Katonda n’omubaka, kale nno mutye Katonda, era mulongoose e nkolagana wakati wa mmwe, era mugondere Katonda n’omubakawe bwe muba nga muli bakkiriza.');
INSERT INTO "african_dev" VALUES(8,2,'8:2','Mazima ddala abakkiriza, be bo buli Katonda lwayogerwako, e mitima gy''abwe gitya, era bwe basomerwa e bigambobye, kibongera obukkiriza, era nga Mukama omulabirizi waabwe yekka gwe beekwasa.');
INSERT INTO "african_dev" VALUES(8,3,'8:3','Abo abayimirizaawo e sswala, era nga ne ku bye tubawa bagabako.');
INSERT INTO "african_dev" VALUES(8,4,'8:4','Abo b’e bakkiriza abannamaddala, balina e bifo ebya waggulu ewa Mukama omulabirizi waabwe n’ekisonyiwo n’okugabirirwa okuyitirivu.');
INSERT INTO "african_dev" VALUES(8,5,'8:5','(Kino kifaanana) nga Mukama omulabiriziwo bwe yakulagira, era nga kye kituufu ofulume (ogende ku lutalo lwe badri), so nga ettengetenge mu bakkirizza baali tebakyagadde.');
INSERT INTO "african_dev" VALUES(8,6,'8:6','Bakuwakanya ku kintu e kituufu (kyoliko), oluvanyuma lw''okuba nti kimaze okweyoleka, nebaba nga abawalulwa, nga batwalibwa eri okufa, nga batunula.');
INSERT INTO "african_dev" VALUES(8,7,'8:7','Mujjukire Katonda bwe yabalagaanyisa okufuna e kimu ku bibiri, nti mazima kyo mujja ku kifuna, wabula mmwe mwali mwagala ekitaliimu buzibu kye muba mufuna, naye nga Katonda ayagala amazima gayimirirewo okusinziira ku biragirobye, era akutulire ddala e mirandira gya bakaafiiri.');
INSERT INTO "african_dev" VALUES(8,8,'8:8','Olwo nno abe nga ayimirizaawo amazima, asaanyeewo obwonoonefu, newaakubadde nga aboonoonyi tebaakyagala.');
INSERT INTO "african_dev" VALUES(8,9,'8:9','Mujjukire bwe mwasaba Mukama omulabirizi wa mmwe obuyambi, n’abaanukula nti mazima nze, nja ku badduukirira ne ba Malayika lukumi nga bagoberagana.');
INSERT INTO "african_dev" VALUES(8,10,'8:10','Kino tewali Katonda kyeyakireetera okugyako nti kibe amawulire ag’essanyu, era e mitima gya mmwe gyeyongere okunywera ku lw''ekyo, naye nga kwo okutaasa tekulina we kuli okugyako ewa Katonda, bulijjo mazima Katonda ye nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(8,11,'8:11','Mujjukire (Mukama Katonda) bwe yabatuusaako okusumagira, nga kukkakkanya okuva gyali, n’abassiza amazzi okuva waggulu (e nkuba) abe nga abatukuza nago, era abaggyirewo e mitego gya Sitane, era agumye e mitima gyammwe, era anyweze nayo e bigere bya mmwe.');
INSERT INTO "african_dev" VALUES(8,12,'8:12','Jjukira Mukama omulabiriziwo, bwe yatumira ba Malayika nti, mazima nze ndi wamu na mmwe, mugumye abo abakkiriza, njakussa okutya mu mitima gyabo abaakaafuwala, kale muteme waggulu w’ensingo, era mubatemeko n’engalo.');
INSERT INTO "african_dev" VALUES(8,13,'8:13','Ekyo nno lwa kuba nti mazima bo beesimba mu Katonda n’o mubakawe, anti omuntu eyeesimba mu Katonda n’omubakawe, mazima Katonda muyitirivu wa kubonereza.');
INSERT INTO "african_dev" VALUES(8,14,'8:14','Ebyo nno ebibatuuseeko (bye bibasaanira), kale mukombe ku bukaawu bwabyo (nga muttibwa mu ntalo kuno ku nsi), era nga ddala abakaafiiri bagenda kussibwako e bibonerezo by’o mu muliro.');
INSERT INTO "african_dev" VALUES(8,15,'8:15','Abange mmwe abakkiriza, bwe mugasimbagananga n''abo abaakaafuwala, ababa bazze okubalwanyisa, temubaddukanga.');
INSERT INTO "african_dev" VALUES(8,16,'8:16','Oyo yenna abalaga e mabegawe (nadduka), ku lunaku olwo, mpozzi ng’ali ku kakodyo ka lutalo, oba nga adda eri kibinja kya balwanyi banne, mazima aba akakaseeko obusungu bw’a Katonda, nga n’obuddobwe muliro Jahannama, ate nga buddo bubi.');
INSERT INTO "african_dev" VALUES(8,17,'8:17','Simmwe mwabatta, naye mazima Katonda y’e yabatta, era bwe wakanyuga, si ggwe wakanyuga, naye mazima Katonda y’eyakanyuga, era abeere nga agezesa abakkiriza olugezesa olulungi n’ekikolwa ekyo, mazima Katonda awulira nnyo, mumanyi.');
INSERT INTO "african_dev" VALUES(8,18,'8:18','E byo byebyo (mubimanye), era mumanyire ddala nti, mazima Katonda anafuya enkwe z’a bakaafiiri.');
INSERT INTO "african_dev" VALUES(8,19,'8:19','(Mwe abakaafiiri) bwe muba nga musaba Katonda kusalawo, okusalawo kuzze, era bwe muneekomako, ekyo kye kirungi gye muli, naye bwe munaddamu, nga naffe tuddamu, ate ekibinja kya mmwe tekigenda kubagasa kintu kyonna, ne bwe kiba kinene kitya, anti mazima Katonda ali wamu n’abakkiriza.');
INSERT INTO "african_dev" VALUES(8,20,'8:20','Abange mmwe abakkiriza, mugondere Katonda n’omubakawe, temumuvangako, ate nga muwulira (bya bagamba).');
INSERT INTO "african_dev" VALUES(8,21,'8:21','Temubeera nga abo ab’agamba nti tuwulidde so nga tebawulira.');
INSERT INTO "african_dev" VALUES(8,22,'8:22','Mazima e kiramu e kisinga obubi ewa Katonda, be ba kiggala, ba kasiru abo abatategeera.');
INSERT INTO "african_dev" VALUES(8,23,'8:23','Singa Katonda yabamanyaamu akalungi konna, yaalibawulizza, ate ne bwe yaalibawulizza, baaliwuguse nebava ku mazima.');
INSERT INTO "african_dev" VALUES(8,24,'8:24','Abange mmwe abakkiriza, mwanukule Katonda n’omubaka bwabayita okujja eri ekyo ekibawa obulamu, era mumanye nti, mazima Katonda, assa ekyawula wakati w’omuntu n’omutimagwe, so ng’ate bulijjo, mazima gyali gye mugenda okukunganyizibwa.');
INSERT INTO "african_dev" VALUES(8,25,'8:25','Mutye e kikemo e kitagenda kukwata ku abo abeeyisa obubi mu mmwe bokka, era mumanye nti mazima Katonda muyitirivu wa kubonereza.');
INSERT INTO "african_dev" VALUES(8,26,'8:26','Mujjukire we mwali abatono, nga muyisibwa bubi mu nsi, nga mutya abantu okubagajambula, n''abawa obubudamu, n’abawagira n’okutaasakwe, n’abagabirira mu birungi, olwo nno mube nga mwebaza.');
INSERT INTO "african_dev" VALUES(8,27,'8:27','Abange mmwe abakkiriza, temukumpanyanga Katonda n’omubaka, nemukumpanya obuvunaanyizibwa bwa mmwe, ng’ate ddala mumanyi.');
INSERT INTO "african_dev" VALUES(8,28,'8:28','Era mumanye nti mazima e mmaali ya mmwe, n’abaana ba mmwe kikemo, era nga bulijjo mazima ddala Katonda, alina empeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(8,29,'8:29','Abange mmwe abakkiriza, singa mutya Katonda, ajja kubasobozesa okwawula (wakati w’ekirungi n’ekibi) era abasanguleko e byonoono bya mmwe, era abasonyiwe, era nga bulijjo Katonda ye nannyini bigabwa e bisuffu.');
INSERT INTO "african_dev" VALUES(8,30,'8:30','Era jjukira abo abaakaafuwala bwe baakukolera olukwe, bakusibe oba bakutte, oba bakugobe, nebasala olukwe, ne Katonda naayanukula olukwe olwo (n''abataasa), Katonda ali waggulu nnyo w’abasala enkwe.');
INSERT INTO "african_dev" VALUES(8,31,'8:31','Bwe basomerwa e bigambo byaffe bagamba, nti mazima ffe ebyo twabiwulira dda, era singa tuba twagadde naffe tusobola okwogera nga ebyo (byoyogera), ebyo tebirina kye biri okugyako okuba enfumo z’a bedda.');
INSERT INTO "african_dev" VALUES(8,32,'8:32','Era jjukira (ggwe Nabbi Muhammad abatakkiriza) bwe baagamba nti, ayi Mukama Katonda, gano (ebigambo by’a Muhammad )bwe biba nga g’emazima agavudde gyoli, tubonereze nenkuba nga y’amayinja okuva waggulu, oba tuleetere ebibonerezo e biruma ennyo.');
INSERT INTO "african_dev" VALUES(8,33,'8:33','Naye Katonda teyali w’akubabonereza, nga naawe okyali nabo, era Katonda teyali w’akubabonereza nga beegayirira.');
INSERT INTO "african_dev" VALUES(8,34,'8:34','Kale nga lwaki Katonda teyaalibabonerezza! ng’ate baziyiza (abantu) abakkiriza okutuuka eri omuzikiti ogw’emizizo, so nga tebabangako bakuumi baagwo, abakuumi baagwo, si balala okugyako abatya Katonda, wabula ddala abasinga obungi mu bo tebamanyi.');
INSERT INTO "african_dev" VALUES(8,35,'8:35','Okusaba kwe basabira ku Kaaba temuli, anti kubeera kufuuwa mpa na kukuba mu ngalo, kale nno mukombe ku bukaawu bwe bibonerezo, olw''ebyo bye mwawakanyanga.');
INSERT INTO "african_dev" VALUES(8,36,'8:36','Mazima abo abaakaafuwala, bawaayo e mmaali yaabwe, baggye abantu ku kkubo lya Katonda, kale bajja kugiwaayo, oluvanyuma ebafuukire e ky''okwejjusa, era bawangulwe, ng’ate abo abaakaafuwala, bagenda kukunganyizibwa batwalibwe mu muliro Jahannama.');
INSERT INTO "african_dev" VALUES(8,37,'8:37','Olwo nno Katonda abe nga ayawula omubi ku mulungi, ababi abakasuke ku bannaabwe bonna babeere entuumu. Entuumu eyo agisse mu muliro Jahannama. Abo nno be baafaafaganirwa.');
INSERT INTO "african_dev" VALUES(8,38,'8:38','Gamba abo abaakaafuwala, nti bwe baneekomako, baggya kusonyiyibwa e byakulembera, naye bwe banaddamu, ekyatuuka ku baasooka kimanyiddwa.');
INSERT INTO "african_dev" VALUES(8,39,'8:39','Era mubalwanyise waleme kubeerawo bukaafiiri, e ddiini yonna ebeere y’a Katonda. Bwe baba beekomyeko, mazima ddala Katonda alaba bye bakola.');
INSERT INTO "african_dev" VALUES(8,40,'8:40','Naye bwe batakola ekyo, mumanye nti mazima Katonda ye mukuumi wa mmwe, (era nga yye) ye mukuumi asinga era omutaasa asinga.');
INSERT INTO "african_dev" VALUES(8,41,'8:41','Era mumanye nti omunyago gwonna gwemuba muggye ku mulabe, mazima kimu kyakutaano kyagwo kya Katonda, n’omubaka, nab’oluganda olw''okumpi olwa Nabbi, ne ba mulekwa, n’a banaku, n’omutambuze, bwe muba nga mwakkiriza Katonda n''ebyo bye twassa ku muddu waffe (Muhammad) ku lunaku Katonda lwe yayawula amazima ku bulimba, olunaku e bibinja e bibiri lwe byasisinkana (ku lutalo e Badri), bulijjo Katonda w’a buyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(8,42,'8:42','Mujjukire e kiwonvu bwe mwakiri ku ludda oluli okumpi (n''ekibuga Madinah, nga ate abalabe ba mmwe bali mitala, ate nga n’a baali mu kibinja kya basuubuzi nga bali mu kifo ekiri wansi wa mmwe, singa mwali mulagaanye okusisinkana mwandiyawukanye ku ndagaano eyo, naye (mwasisinkana), Katonda alyoke atuukirize ekyo kye yasalawo okukolebwa, olwo nno oyo anaazikirira, azikirire nga azikirira lwa nsonga, n’asigadde nga mulamu asigale lwa nsonga, bulijjo mazima Katonda muyitirivu wa kuwulira, era mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(8,43,'8:43','Jjukira Katonda bwe yabakulaga (abalabebo) mu kirootokyo, nga batono, so nga singa yabakulaga nga bangi mwalinafuye ne mukubagana empawa mu kusalawo, naye mazima Katonda yabataasa, anti Katonda muyitirivu w''a kumanya ebiri mu bifuba bya mmwe.');
INSERT INTO "african_dev" VALUES(8,44,'8:44','Era jjukira Katonda bwe yababalaga nga batono mu maaso ga mmwe, bwe mwasisinkana, mmwe naabafuula batono mu maaso gaabwe, Katonda alyoke atuukirize ekyo kye yasalawo okukolebwa, era bulijjo e nsonga zonna ewa Katonda gye zizzibwa.');
INSERT INTO "african_dev" VALUES(8,45,'8:45','Abange mmwe abakkiriza bwe musisinkananga e kibinja kya balabe munywere, mwogere nnyo ku Katonda, mulyoke muwangule.');
INSERT INTO "african_dev" VALUES(8,46,'8:46','Era mugondere Katonda n’omubakawe, temukubagananga e mpawa ne kibanafuya, nemuggwamu amaanyi, era mugumiikirize, anti Katonda ali wamu n''abagumiikiriza.');
INSERT INTO "african_dev" VALUES(8,47,'8:47','Temubeera nga (abakaafiiri) abaafuluma mu mayumba gaabwe nga beekuluntaza n’okweraga eri abantu, era nga baziyiza abantu okuyingira mu ddiini y’a Katonda, nga bulijjo Katonda yeetoolodde ebyo byonna bye bakola.');
INSERT INTO "african_dev" VALUES(8,48,'8:48','Jjukira Sitane bwe yabalaga bye baali bakola nti birungi, (Sitane) n''abagamba nti tewali muntu yenna waakubawangula olwa leero, era nti mazima nze nja kubataasa, naye ate e bibinja by’ombiriri bwe byasisinkana, Sitane neyeefuula, era naagamba nti, mazima nze siri nammwe, anti mazima nze ndaba bye mutalaba, era mazima nze ntya Katonda, nga bulijjo Katonda muyitirivu wa kubonereza.');
INSERT INTO "african_dev" VALUES(8,49,'8:49','Jjukira abannanfusi n’abalina obulwadde mu mitima gyabwe bwe baagamba nti, e ddiini yaabo abakkiriza ebabuzizza, sso nga buli eyeekwasa Katonda (y’aba akoze e kituufu), anti mazima Katonda ye nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(8,50,'8:50','Singa olaba (ggwe Nabbi Muhammad) engeri malayika gye ziggyamu e myooyo gy’abakaafiiri, nga babakuba mu maaso gaabwe, ne ku migongo gyabwe, nga bwe babagamba nti, mukombe ku bukaawu bwe binorezo ebyokya.');
INSERT INTO "african_dev" VALUES(8,51,'8:51','Ekyo ekibatuuseeko, kiva ku ebyo bye mwakola (mu bulamu bwensi) era nti mazima Katonda tayisa bubi baddube.');
INSERT INTO "african_dev" VALUES(8,52,'8:52','(Ebyo ebituuse ku bakaafiiri) bifaanana n’ebyatuuka ku bantu ba Firawo, n''abo abaabakulembera, baawakanya ebigambo bya Katonda, Katonda naababonereza olw''e byonoono bya bwe, mazima ddala Katonda wa maanyi era muyitirivu wa kubonereza.');
INSERT INTO "african_dev" VALUES(8,53,'8:53','Ebyo ebituuse ku bakaafiiri (bibasaanira), anti mazima Katonda takyusa kyengera kyaba awadde bantu, okutuusa nga bo bennyini bakyusizza embeera zaabwe, era nga bulijjo mazima Katonda muyitirivu wa kuwulira, mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(8,54,'8:54','Bafaanana nga abantu ba Firawo, n''abo abaaliwo oluberyeberye lwa bwe, baalimbisa e bigambo bya Mukama omulabirizi waabwe, netubazikiriza olw''ebibi bya bwe, netusaanyaawo abantu ba Firawo, era bonna baali beeyisizza bubi.');
INSERT INTO "african_dev" VALUES(8,55,'8:55','Mazima ekiramu ekisinga obubi mu maaso g’a Katonda, beebo abaakaafuwala nekiba nti tebagenda kukkiriza.');
INSERT INTO "african_dev" VALUES(8,56,'8:56','Abo bewakozesa endagaano, ate nebamenya endagaano yaabwe buli mulundi, era tebayinza kutya Katonda.');
INSERT INTO "african_dev" VALUES(8,57,'8:57','Buli lwo bagwikirizanga mu lutalo, babonereze mu bukambwe, kibe ekyokutiisa, eri abo abalibaddirira, babe nga beebuulirira.');
INSERT INTO "african_dev" VALUES(8,58,'8:58','Bwe weekengeranga obukumpanya mu bantu, baddize e ndagaano yaabwe mu lwatu, anti mazima ddala Katonda tayagala bakumpanya.');
INSERT INTO "african_dev" VALUES(8,59,'8:59','Abo abaakaafuwala tebeeyibaalanga ne balowooza nti basimattuse ku Katonda, anti mazima bbo tebayinza kulemesa (Katonda).');
INSERT INTO "african_dev" VALUES(8,60,'8:60','Era mubeetegekere, nga mukozesa ekyo kyonna kye musobola mu by’okulwanyisa, nga e nfalaasi e ntendeke, era mutiise nakyo omulabe w’a Katonda era omulabe wa mmwe, n’abalala abatali abo be mutamanyi naye nga Katonda abamanyi, (mukutuukiriza ekyo), e kintu kyonna kye muwaayo mu kkubo lya Katonda, kigenda kubasasulwa mu bujjuvu, era temugenda kuyisibwa bubi.');
INSERT INTO "african_dev" VALUES(8,61,'8:61','Singa bayaayaanira e mirembe naawe giyaayaanire, era weekwase Katonda, anti mazima yye awulira nnyo, mumanyi.');
INSERT INTO "african_dev" VALUES(8,62,'8:62','Naye singa baba baagadde okukukwenyakwenya, akumalira byonna ye Katonda, anti yye yooyo eyakudduukirira n’akutaasa (era n’akuwagira) ng’akozesa abakkiriza.');
INSERT INTO "african_dev" VALUES(8,63,'8:63','Era naagatta wakati w’emitima gyabwe, nga singa wawaayo ebiri mu nsi byonna, tewandigasse wakati w’a mitima gyabwe, naye mazima ddala Katonda yagatta wakati waabwe, anti mazima ddala yye ye nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(8,64,'8:64','Owange ggwe Nabbi, Katonda akumalira era naamalira n’abali naawe nga bakkiriza.');
INSERT INTO "african_dev" VALUES(8,65,'8:65','Owange gwe Nabbi, kubiriza abakkiriza okugenda okulwana, kale nno mu mmwe bwe mubaamu amakumi abiri abagumiikiriza bawangula e bikumi bibiri, ate e kikumi mu mmwe bawangula lukumi, mu abo abaakaafuwala olw''okuba nti bo bantu abatategeera.');
INSERT INTO "african_dev" VALUES(8,66,'8:66','Kaakati Katonda abawewulidde, era amanyi nti mazima mu mmwe mulimu obunafu, kale mu mmwe bwe mubangamu e kikumi ekigumiikiriza, bawangula e bibiri, ate olukumi mu mmwe bawangula e nkumi bbiri ku lwo kukkiriza kwa Katonda, nga bulijjo Katonda aliwamu n''abagumiikiriza.');
INSERT INTO "african_dev" VALUES(8,67,'8:67','Tekisaanira Nabbi yenna kuba na bawambe okutuusa nga amaze okusimba amakanda mu nsi. (Bwe musalawo okutwala abawambe nga temunnanywera) muba mwagala bya kufuna byansi, ng’ate Katonda ayagala nkomerero, ate nga bulijjo Katonda ye nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(8,68,'8:68','Singa ssi kusalawo kwa Katonda oluberyeberye, mwandituusiddwako e kibonerezo e kikakali olw''ebyo bye mwatwala.');
INSERT INTO "african_dev" VALUES(8,69,'8:69','Kale nno mulye mu ebyo bye mufunye, nga minyago ebiri Halal ate nga birungi, era mutye Katonda, mazima Katonda musonyiyi, musaazizi.');
INSERT INTO "african_dev" VALUES(8,70,'8:70','Owange ggwe Nabbi gamba abawambe abali mu mikono gya mmwe nti, Katonda bwa naamanya nti mu mitima gya mmwe mulimu akalungi agenda kubawa e birungi e bisinga kw''ebyo ebyabaggyibwako era abasonyiwe, anti bulijjo Katonda musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(8,71,'8:71','Naye bwe baba baagadde ku kukumpanya, mazima ba kumpanyadda Katonda, wabula yakuyamba n’obawangula, nga bulijjo Katonda amanyi nnyo, agoba nsonga.');
INSERT INTO "african_dev" VALUES(8,72,'8:72','Mazima abo abakkiriza nebasenguka, ne balafuubana mu kkubo lya Katonda, nga bakozesa e mmaali yaabwe n’emyooyo gyabwe, era n’abo abaabudamya era ne bataasa (abaava e Mekkah) abo nno, abamu bateekwa okudduukirira bannaabwe. Ate abo abakkiriza nebatasenguka temuvunaanyizibwa ku kintu kyonna mu kubataasa, okutuusa nga basenguse. Naye bwe babakubira e nduulu ku nsonga eye ddiini, olwo nno muteekwa okubadduukirira okugyako nga bakaayagana n’abantu bemulina nabo e ndagaano. Nga bulijjo Katonda alaba bye mukola.');
INSERT INTO "african_dev" VALUES(8,73,'8:73','Ate abo abaakaafuwala abamu mikwano gya bannaabwe, bwe mutaakikole (eky''okutaasa bannammwe bemuteekwa okukikolera), olwo wajja kubaawo obuzibu mu nsi n’okwonoona kungi.');
INSERT INTO "african_dev" VALUES(8,74,'8:74','Era abo abakkiriza nebasenguka, nebalafuubana nga baweereza mu kkubo lya Katonda, n''abo abaabudamya nebataasa (abaava e Mekkah), abo nno be bakkiriza abannamaddala. Balina e kisonyiwo n’okugabirirwa okw''ettendo.');
INSERT INTO "african_dev" VALUES(8,75,'8:75','Ate abo abakkiriza, oluvanyuma ne balafuubana wamu nammwe mu kkubo lya Katonda, abo nno ba mu mmwe, naye nga ab''oluganda abamu ku bo be basinga okukwatibwako ensonga z’a bannaabwe. Ebyo nga biri mu kitabo kya Katonda. Nga bulijjo mazima Katonda mumanyi wa buli kintu.');
INSERT INTO "african_dev" VALUES(9,1,'9:1','Kino kirango ekiva ewa Katonda n’omubakawe, eri abo be mwakola nabo e ndagaano, mu bagatta e bintu e birala ku Katonda.');
INSERT INTO "african_dev" VALUES(9,2,'9:2','(Kati muweereddwa e ddembe ddembe) kale mutambule mu nsi e bbanga lya myezi ena, era mukimanye nti mazima ddala mmwe temuyinza kulemesa Katonda, era ddala, nga bulijjo Katonda aswaza abakaafiiri.');
INSERT INTO "african_dev" VALUES(9,3,'9:3','Era kuno kulangirira okuva eri Katonda n’omubakawe, okuteekwa okutuusibwa eri abantu, ku lunaku olukulu olwa Hijja, nti mazima Katonda amenyeewo e ndagaano zonna ezaakolebwa n''abo abagatta ku Katonda e bintu e birala, n’omubakawe bwatyo azimenyeewo, naye singa mwenenya ekyo kye kirungi gye muli, wabula singa mugaana mumanye nti mazima mmwe temusobola kulemesa Katonda, era (ggwe Muhammad) abo abaakaafuwala bawe amawulire ag’ebibonerezo e biruma.');
INSERT INTO "african_dev" VALUES(9,4,'9:4','Okugyako abo be mwakola nabo e ndagaano mu bagatta ku Katonda e bintu e birala, oluvanyuma ne batamenya katundu konna mu ndagaano eyo, era nebatayamba muntu yenna abalwanyisa. (abo) mubajjulize e ndagaano yaabwe okutuusa e kiseera kya bwe lwe kiggwako, anti mazima ddala Katonda ayagala nnyo abamutya.');
INSERT INTO "african_dev" VALUES(9,5,'9:5','Emyezi egy''emizizo bwegiggwako, mutte abo abagatta e birala ku Katonda, wonna wemubagwikiriza, era mubawambe, era mubataayize, era mubateege mu buli kkubo, kyokka bwe beenenya, nebayimirizaawo e sswala, nebatoola zzaka, olwo nno mubaleke, anti mazima Katonda musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(9,6,'9:6','Singa omuntu yenna mu abo abagatta ku Katonda e bintu e birala akusaba okumuwa obubudamu, bumuwe, kimuyambe okuwulira e bigambo bya Katonda, oluvanyuma mutuuse mu kifo waabeerera mu mirembe, ekyo nno lwa kuba nti mazima bbo bantu abatamanyi.');
INSERT INTO "african_dev" VALUES(9,7,'9:7','Kisoboka kitya okubaawo e ndagaano wakati w’a bagatta ku Katonda e bintu e biralala, ne wakati wa Katonda n’omubaka we! mpozzi abo be mwakola nabo e ndagaano mu kifo awali omuzikiti ogw’e mizizo, kale e bbanga lye bamala nga beesimbu gye muli nammwe muteekwa okubeera abeesimbu gye bali mazima Katonda ayagala abamutya.');
INSERT INTO "african_dev" VALUES(9,8,'9:8','Ngeriki (bwe wayinza okubaawo e ndagaano wakati wa mmwe nabo) so nga bwe baba babawangudde tebafa ku ndagaano wadde oluganda lwe mulina nabo, babasiimiikiriza n’ebigambo bya bwe, so nga e mitima gy’abwe tegiri nammwe ng’ate abasinga obungi mu bo, boonoonyi.');
INSERT INTO "african_dev" VALUES(9,9,'9:9','Batunda e bigambo bya Katonda omuwendo ogwa layisi (ku nsi) ekyabaviirako okuziyiza (abantu) ku kkubo lya Katonda, mazima bo bibi ebyo bye baali bakola.');
INSERT INTO "african_dev" VALUES(9,10,'9:10','Tebafa ku mukkiriza yenna keebe ndagaano oba oluganda, era abo be baasukka e nsalo (za Katonda).');
INSERT INTO "african_dev" VALUES(9,11,'9:11','Naye (awamu n''ekyo) bwe baba beenenyezza (nebava ku bikolwa ebyo) ne bayimirizaawo e sswala nebawa zzaka, olwo nno baba bafuuse baganda ba mmwe mu ddiini, era bulijjo tunnyonnyola mu bulambulukufu e bigambo byaffe eri abantu abamanyi (abeegasa nabyo).');
INSERT INTO "african_dev" VALUES(9,12,'9:12','(Wabula ate) bwe bamenya e birayiro bya bwe, oluvanyuma lw''endagaano gye baakola, nebajerega e ddiini ya mmwe, olwo nno mulwanyise abakungu mu bukaafiiri, anti mazima bbo, tebakuuma birayiro, balyoke beekomeko.');
INSERT INTO "african_dev" VALUES(9,13,'9:13','Kiki ekibagaana okulwanyisa abantu abamenya e ndagaano zaabwe, nebaagala n’okugoba omubaka (mu Makkah), ate nga bo bennyini b’e baabasosonkereza omulundi ogwasooka, abaffe mutya batye, Katonda yaasaanira okuba nga mumutya bwe muba nga ddala muli bakkiriza.');
INSERT INTO "african_dev" VALUES(9,14,'9:14','Mubalwanyise, Katonda ababonereze n’emikono gya mmwe, era abakkakkanye era mmwe abataase ku bo, aweweeze ekyo ekiri mu mitima gya bakkiriza.');
INSERT INTO "african_dev" VALUES(9,15,'9:15','Era amalewo obukyayi bw’emitima gya bwe, era Katonda akkirize okwenenya kw’oyo gwaba ayagadde, bulijjo Katonda muyitirivu w’a kumanya, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(9,16,'9:16','Abaffe, musuubidde okuba nga mulekebwa (nemutagezesebwa), Katonda abe nga amanya abo abaalafuubana mu mmwe (olw''ediini), nebateeteerawo wa mukwano yenna atali Katonda, oba omubakawe, oba abakkiriza, bulijjo Katonda amanyidde ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(9,17,'9:17','Abagatta ku Katonda e bintu e birala tebakkirizibwa kuzimba mizikiti gya Katonda, nga nabo bennyini beewaako obujulizi nti bakaafiiri, abo e mirimu gya bwe gyonna mifu, era nga mu muliro mwe bagenda okutuula obugenderevu.');
INSERT INTO "african_dev" VALUES(9,18,'9:18','Mazima ddala azimba e mizikiti gy’a Katonda, yooyo akkiriza Katonda n’olunaku lw''enkomerero, n’ayimirizaawo e sswala, n''atoola zzaka, era n’atatya okugyako Katonda, abo nno be bookubeera mu balungamu.');
INSERT INTO "african_dev" VALUES(9,19,'9:19','Muddira mutya okuwa ba Hajji amazzi n’okulabirira omuzikiti ogw’e mizizo ne mukyenkanya n''oyo akkiriza Katonda n’olunaku lw''enkomerero! era naalafuubana mu kuweereza mu kkubo lya Katonda, tebayinza kwenkana mu maaso g’a Katonda, era bulijjo Katonda talungamya bantu beeyisa bubi.');
INSERT INTO "african_dev" VALUES(9,20,'9:20','Abo abakkiriza, nebasenguka, era ne balafuubana mu kuweereza mu kkubo lya Katonda nga bakozesa e mmaali yaabwe n''emyoyo gyabwe, be basinga amadaala ag’a waggulu ewa Katonda, era abo, bo be b''okwesiima.');
INSERT INTO "african_dev" VALUES(9,21,'9:21','Mukama omulabirizi waabwe abasanyusa nga bwagenda okubawa okusaasira, n’okusiima ebiva gyali, era abawe e jjana, mwe balifunira e byengera e bitakoma.');
INSERT INTO "african_dev" VALUES(9,22,'9:22','Baakubeera mu zo olubeerera, mazima Katonda yekka yaalina e mpeera e nsukkulumu.');
INSERT INTO "african_dev" VALUES(9,23,'9:23','Abange mmwe abakkiriza, bakadde ba mmwe ne baganda ba mmwe, temubafuulanga mikwano gya mmwe egy''omunda, kavuna bakulembeza obukaafiiri ku bukkiriza, oyo yenna mu mmwe abafuula mikwanogye ab''omunda, abo nno be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(9,24,'9:24','Gamba nti bakadde ba mmwe n’abaana ba mmwe, ne baganda ba mmwe, ne bemwafumbiriganwa nabo, n’aboluganda lwa mmwe, n’ebyobugagga bye mukungaanya, n’ebyobusuubuzi bye mutya okudiba kwabyo, n’amayumba agabasanyusa, ebyo byonna bwe biba nga bye mwagala e nnyo okusinga Katonda n’omubakawe, n’okulafuubana okuweereza mu kkubolye, olwo nno mulindirire okutuusa Katonda lwanaaleeta e kiragirokye, era Katonda talungamya bantu boonoonyi.');
INSERT INTO "african_dev" VALUES(9,25,'9:25','Mazima Katonda yabataasa mu bifo bingi, ne ku lunaku lw''olutalo lwe Hunaini, obungi bwa mmwe bwe bwabasigula nebutabagasa kintu kyonna. Ensi yabafundirira awamu n’obugazi bwayo n’ekyaddako mwakyuka ne mussaako kakokola tondeka nnyuma.');
INSERT INTO "african_dev" VALUES(9,26,'9:26','Oluvanyuma Katonda yassa obuteefu ku mubakawe, ne ku bakkiriza era nassa amagye gemutaalaba, era naabonereza abo abaakaafuwala. Era nga bulijjo empeera y’abakafiiri bwetyo bweba.');
INSERT INTO "african_dev" VALUES(9,27,'9:27','Oluvanyuma lw''ekyo Katonda akkiriza okwenenya kw''oyo gwaba ayagadde, era bulijjo Katonda musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(9,28,'9:28','Abange mmwe abakkiriza (mukimanye nti), mazima abagatta ku Katonda e bintu e birala bajama, tebasembereranga omuzikiti ogw’emizizo oluvanyuma lw’omwaka gwabwe guno. Bwe muba mutidde bwavu, Katonda bwanaaba ayagadde ajja kubagaggawaza nga aggya mu bigabwabye, anti mazima Katonda mumanyi nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(9,29,'9:29','Mulwanyise abo abatakkiriza Katonda, wadde olunaku lw''enkomerero. Era nga tebaziza ebyo Katonda n’omubakawe bye baafuula Haramu. Era nebatagoberera ddiini entuufu, nga bava mu abo abaaweebwa e kitabo, okutuusa nga bawadde omusolo nga buli omu yaagweweera, balabike nga bakkakkanyiziddwa.');
INSERT INTO "african_dev" VALUES(9,30,'9:30','Abayudaaya baagamba nti Uzairu mwana wa Katonda, ate Abanaswara ne bagamba nti Masiya mwana w’a Katonda, ekyo kigambo kyabwe kye boogera n’emimwa gyabwe, nebifaanana e kigambo ky''abo abaakaafuwala oluberyeberye. Katonda yabakolimira, bawugulwa batya (okuva ku mazima)!.');
INSERT INTO "african_dev" VALUES(9,31,'9:31','Bafuula ba kabona baabwe n’a basosodooti okuba abasinzibwa, nebaleka Katonda. Mu ngeri y’emu ne Masiya mutabani wa Mariam baamufuula bwe batyo, so ng’ate tebaalagirwanga okugyako okusinza Katonda omu. Tewali kisinzibwa kyonna okugyako ye, musukkulumu nnyo kw''ebyo bye bamugattako.');
INSERT INTO "african_dev" VALUES(9,32,'9:32','Baagala okuzikiza e kitangaala ky’a Katonda, nga bakozesa e mimwa gyabwe, ne Katonda takkiriza okugyako okujjuza ekitangaalakye, newaakubadde nga abakafiiri tebayagala.');
INSERT INTO "african_dev" VALUES(9,33,'9:33','Katonda, yye yooyo eyatuma omubakawe nga amuwadde obulungamu n’eddiini ey’amazima, abe ng’a agiraga nga bweri waggulu we ddiini e ndala zonna newaakubadde nga abagatta ku Katonda e bintu e birala tebakyagala.');
INSERT INTO "african_dev" VALUES(9,34,'9:34','Abange mmwe abakkiriza, mazima bangi mu ba kabona, n’a basosodooti balya e mmaali y’abantu mu bukyamu, era nebaziyiza abantu okutuuka ku kkubo lya Katonda. N’abo bonna abakungaanya zaabu ne feeza ne batabiwaayo mu kkubo lya Katonda, basanyuse n’ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(9,35,'9:35','Ku lunaku (e mmaali eyo) lwe liyengerezebwa mu muliro Jahannama, e byenyi byabwe nembiriizi zaabwe n’emigongo gyabwe nebyokebwa nayo, (nga bwe bakomekkerezebwa nti) bino bye mwetegekera, mukombe ku bukaawu bw''ebyo, bye mwaterekanga.');
INSERT INTO "african_dev" VALUES(9,36,'9:36','Mazima omuwendo gw''emyezi ew’a Katonda giri emyezi kkumi n''ebiri, bwe kityo bwe kiri mu biwandiiko bya Katonda, okuva ku lunaku Katonda lwe yatonda e ggulu omusanvu ne nsi. Ena ku gyo gy’amizizo, eyo nno y’e ddiini e nnambulukufu, temweyisanga bubi mu gyo, era mulwanyise abagatta ku Katonda e bintu e birala bonna, nga nabo bwe babalwanyisa mwenna, mumanye nti mazima Katonda bulijjo abeera n’a bamutya.');
INSERT INTO "african_dev" VALUES(9,37,'9:37','Mazima okuwaanyisa e myezi egy''emizizo kyongera bukaafiiri, babuzibwa nakyo abo abaakaafuwala nga bakkiriza okugirwaniramu mu mwaka, ate omwaka omulala nebagiziza, basobole okujjuza omuwendo gwe myezi Katonda gye yaziza, mu kukola ekyo nebaba nga bafuula Halali Katonda kye yafuula Haramu. Ebikolwa byabwe e bibi bya balabikira okuba nga bye birungi, nga bulijjo Katonda talungamya bantu bakaafiiri.');
INSERT INTO "african_dev" VALUES(9,38,'9:38','Abange mmwe abakkiriza mwabaaki! bwe babagamba nti mufulume mulafuubane mu kuweereza mu kkubo lya Katonda, muwulira nga muzitoye ne musalawo kutuula ku ttaka, musazeewo kugenda na bulamu bwansi okusinga e nkomerero! naye obulamu bwensi bwe bugeraageranyizibwa ku bw''enkomerero tebuba, okugyako kantu katono nnyo.');
INSERT INTO "african_dev" VALUES(9,39,'9:39','Bwemutagenda (ku lutalo) (Katonda) agenda kubabonereza e bibonerezo e biruma ennyo, ate asseewo abantu abalala mu kifo kya mmwe, era temuyinza ku mutuusaako kabi konna, nga bulijjo Katonda muyinza wa buli kintu.');
INSERT INTO "african_dev" VALUES(9,40,'9:40','Bwe mutamutaasa (Nabbi Muhammad), mazima Katonda yamutaasa mu kiseera abakaafiiri bwe baamufulumya nga y’omu ku babiri, webaabeerera mu mpuku, mu kiseera weyagambira munne nti tonakuwala mazima Katonda ali wamu naffe, awo nno Katonda naamussaako obutebenkevubwe, era naamudduukirira neggye lye mutaalaba. E kigambo ky''abo abaakaafuwala n’akifuula ekya wansi, ssi nga ekigambo kya Katonda kyo kye kya waggulu, bulijjo Katonda ye nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(9,41,'9:41','Mufulume n’ebyokulwanyisa e bitonotono oba e bizito, mulafuubane nga muweereza mu kkubo lya Katonda, nga mukozesa e mmaali ya mmwe nemy’oyo gya mmwe, ekyo nno ky’ekirungi gye muli, bwe muba nga mumanyi.');
INSERT INTO "african_dev" VALUES(9,42,'9:42','Singa byali byanfuna nga biri kumpi, nga n’olugendo lwangu, baalikugoberedde, naye olugendo lwabayitirirako era bajja kulayira Katonda nga bagamba nti, singa twabadde tusobola twandigenze na mmwe, mukwogera ekyo nebaba nga bazikiriza myoyo gyabwe, anti Katonda amanyi nti mazima bo balimba.');
INSERT INTO "african_dev" VALUES(9,43,'9:43','Katonda akusonyiwe (Ggwe Muhammad) lwaki wabakkiriza okusigala n’otalinda kumala kumanyira ddala ab''amazima, era n’omanya abalimba.');
INSERT INTO "african_dev" VALUES(9,44,'9:44','Abo abakkiriza Katonda n’olunaku lw''enkomerero, tebayinza kukusaba kubaggyako luwalo lwa kugenda kulafuubana mu kkubo lya Katonda, nga bakozesa e mmaali yabwe, n’emyoyo gyabwe, era Katonda amanyidde ddala abo abamutya.');
INSERT INTO "african_dev" VALUES(9,45,'9:45','Mazima abo abatakkiriza Katonda n’olunaku lw''enkomerero, be bakusaba okubakkiriza obutagenda ku lutabaalo, n’emitima gyabwe gibuusabuusa, bonna mu kubuusabuusa kwabwe mwebabeera nga tebayinza kusalawo.');
INSERT INTO "african_dev" VALUES(9,46,'9:46','Singa baali baagadde kugenda ku lutalo, baalirwetegekedde okwetegeka okwetaagisa, naye Katonda teyayagala kugenda kwa bwe, n’olwekyo n’atabasobozesa, olwo nno nebalangirira nti, musigale n’abasigadde.');
INSERT INTO "african_dev" VALUES(9,47,'9:47','Singa baagendera mu mmwe, tebandibongedde okugyako bizibu, era bandibungeesezza e ngambo mu mmwe, nga babaagaliza akabi, ate nga ne mu mmwe mwandibaddemu ababawuliriza, Katonda amanyi nnyo ebikwata ku bantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(9,48,'9:48','Mazima baayagala dda okukuleetera akabi ne bakutabangulira e bintu, okutuusa amazima lwe gajja n’ekigambo kya Katonda nekyeyoleka, nga nabo tebaagala.');
INSERT INTO "african_dev" VALUES(9,49,'9:49','Mu bo mulimu omuntu agamba nti, nsaba onzikirize nneme kugenda ku lutalo, era tonteeka mu kikemo, abange abo nno mu kikemo mwennyini mwe baagwa, mazima omuliro Jahannama gwetoolodde abakaafiiri.');
INSERT INTO "african_dev" VALUES(9,50,'9:50','Bwotuukibwako e kirungi, kibanakuwaza, ate bw’otuukibwako e kibi, bagamba nti, ffe ekyo twakirabirawo ne tukyekeengera, olwo nno nebadda mu maka gaabwe nga basanyufu.');
INSERT INTO "african_dev" VALUES(9,51,'9:51','(Ggwe Muhammad) gamba nti tewali kitutuukako okugyako Katonda kye yatuwandiikako, yye, ye mutaasa waffe, abakkiriza Katonda yekka gwe bateekwa okwesiga,');
INSERT INTO "african_dev" VALUES(9,52,'9:52','Gamba (ggwe Muhammad), mulina kye musuubira okututuukako ekitali e kimu ku birungi e bibiri (okuwangula olutalo oba okuwangulwa netuyingira e jjana), ng’ate ffe tubalindiridde Katonda abatuusaako ebibonerezo okuva gyali, oba abibatuseeko ng’abiyisa ku mikono gyaffe, kale mulindirire naffe tulindirira wamu na mmwe.');
INSERT INTO "african_dev" VALUES(9,53,'9:53','Gamba (ggwe Muhammad), kamuweeyo mu mmaali ya mmwe mu ngeri y’akyeyagalire oba ey’obuwaze, (okugaba kwa mmwe okwo) tekugenda kukkirizibwa kuva gye muli, anti mazima ddala mmwe muli abantu aboonoonyi.');
INSERT INTO "african_dev" VALUES(9,54,'9:54','Tewali kyagaanyisa kuwaayo kwabwe kukkirizibwa, okugyako okuba nti, mazima ddala bo baagyemera Katonda n’omubakawe, era tewali lwe bagenda kusaala okugyako nga bagayaavu, era tebawaayo okugyako lwa buwaze.');
INSERT INTO "african_dev" VALUES(9,55,'9:55','Emmaali yaabwe n’abaana baabwe tebikuyigula ttama, anti mazima Katonda ayagala kubabonereza nabyo mu bulamu obw''ensi, era batuuke okussa omukka ogusembayo nga bakaafiiri.');
INSERT INTO "african_dev" VALUES(9,56,'9:56','Era balayira Katonda nti, mazima bo ba mu mmwe, naye ssi ba mu mmwe, mazima bo, bantu batiitiizi.');
INSERT INTO "african_dev" VALUES(9,57,'9:57','Singa bafuna obuddukiro, oba e mpuku, oba e kifo aweekukumwa, bandidduse okugenda gye kiri nga baduma.');
INSERT INTO "african_dev" VALUES(9,58,'9:58','Ate mu bo mulimu abakusongamu e mimwa ku nsonga ya saddaaka, bwe baweebwako basiima, ate bwe bataweebwa, olwo nno nebasunguwala.');
INSERT INTO "african_dev" VALUES(9,59,'9:59','Singa bamatira n''ekyo Katonda n’omubakawe kye baabawa, era ne bagamba nti Katonda waffe atumala, Katonda ajja kutuwa mu bigabwabye e bingi n’omubakawe bwatyo, anti mazima ddala ffe ewa Katonda gye tuyaayaanira okudda.');
INSERT INTO "african_dev" VALUES(9,60,'9:60','Mazima ddala saddaaka (zzaka) eweebwa abaavu, n’abanaku, n’abagikolako, naabo e mitima gyabwe egisikirizibwa, (abaakasiramuka), ne mu kununula abaddu, n’abo amabanja begatuuse mu bulago, ne mu kulafuubana mu kuweereza mu kkubo lya Katonda, n’omutambuze, ebyo byonna nga y''engereka y’a Katonda, era bulijjo Katonda amanyi nnyo, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(9,61,'9:61','Era mu bo, mulimu abo abanyiiza Nabbi nga bagamba nti, yye awuliriza buli kimu, gamba nti awuliriza lwa bulungi bwa mmwe, akkiriza Katonda, era yeesiga abakkiriza, era yye kya kusaasira eri abo abakkiriza mu mmwe, bo abo abanyiiza omubaka wa Katonda, bagenda kutuukwako e bibonerezo e biruma.');
INSERT INTO "african_dev" VALUES(9,62,'9:62','Babalayirira Katonda babasanyuse, so nga Katonda n’omubaka beebasinga okusaanira okusanyusibwa singa (abantu abo) babadde bakkiriza.');
INSERT INTO "african_dev" VALUES(9,63,'9:63','Abaffe, tebamanyi nti mazima ddala omuntu eyeesimba mu Katonda n’omubakawe, mazima wa kuyingizibwa omuliro Jahannama, nga w’akubeera mu gwo bugenderevu, obwo bwe buswavu obuyitirivu.');
INSERT INTO "african_dev" VALUES(9,64,'9:64','Abannanfunsi beekengera okussibwako e Ssura, nga ebategeeza ebiri mu mitima gyabwe, gamba (ggwe Nabbi Muhammad nti), mugira mubiyita eby''olusaago, mazima Katonda ajja kwanika ebyo bye mwekengera.');
INSERT INTO "african_dev" VALUES(9,65,'9:65','Singa obabuuza (lwaki bajerega abakkiriza), bajja kugambira ddala nti, mazima twabadde mu lwali na kuzannya, gamba (ggwe Muhammad) Katonda, n’ebigambobye n’omubakawe, bye mwabadde mujerega!.');
INSERT INTO "african_dev" VALUES(9,66,'9:66','Temwetonda mazima mukaafuwadde oluvanyuma lw’obukkiriza bwa mmwe, bwe tusonyiwa e kitundu ku mmwe (olw''okwetonda), tuba tulina okubonereza e kitundu e kirala ekiteetonze, olw''okuba nti mazima bo baali boonoonyi.');
INSERT INTO "african_dev" VALUES(9,67,'9:67','Abannanfunsi abasajja n’abannanfusi abakazi beebamu, balagira okukola e mpisa e mbi, ne bagaana (abantu) okukola e mpisa e nnungi, era bafunya e mikono gyabwe (bakodo) beerabira Katonda naye naabalekerayo, mazima abannanfusi, bo be boonoonyi.');
INSERT INTO "african_dev" VALUES(9,68,'9:68','Abannanfusi abasajja n’abannanfusi abakyala, n’abakaafiiri, Katonda ya balagaanyisa omuliro Jahannama, baakugubeeramu obugenderevu, ggwo gwe gw''okubamala era Katonda yabakolimira era balissibwako e bibonerezo eby''olubeerera.');
INSERT INTO "african_dev" VALUES(9,69,'9:69','Bafaananako nga abo abaaliwo oluberyeberye lwa mmwe, baali ba maanyi nnyo okusinga mmwe, era nga beebasinza e mmaali n’abaana, olwo nno ne beeyagala olw''ebyo bye baafuna nga nammwe bwe mweyagadde olw''ebyo bye mufunye, nga abaabakulembera bwe beeyagala olw''ebyo bye baafuna, era ne mudda mu kukuba olwali nga bali lwe baalimu, abo nno e mirimu gya bwe gyayonooneka ku nsi ne ku nkomerero, era abo be b''okufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(9,70,'9:70','Abaffe e kigambo ky''abo abaabakulembera tekyabatuukako! abantu ba Nuuhu, n’aba A’adi, n’a ba Thamud, n’abantu ba Ibrahim, n’abantu b’o mu kitundu kye Madiyana, n’abantu b’o mu bitundu ebyavuunikibwa (aba Nabbi Luutu)ababaka baabwe baabajjira n’ebigambo bya Katonda e binnyonnyofu, oluvanyuma lw''ebyo Katonda si ye yabayisa obubi, naye bbo bennyini be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(9,71,'9:71','Abakkiriza abasajja n’abakkiriza abakazi, abamu mikwano gya bannaabwe, balagira okuyisa obulungi, ne baziyiza okuyisa obubi, era ne bayimirizaawo e sswala ne batoola zzaka, era ne bagondera Katonda n’omubakawe, abo Katonda ajja kubasaasira. Anti mazima ddala Katonda nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(9,72,'9:72','Katonda yalagaanyisa abakkiriza abasajja, n’abakkiriza abakazi e jjana ezikulukutiramu e migga, b’akubeera mu zo bugenderevu, era baliweebwa e bisulo e birungi mu jjana ez’olubeerera, nga n''ekisingira ddala, bagenda kufuna okusiima okuva ewa Katonda, era ekyo kwe kwesiima okusukkulumu.');
INSERT INTO "african_dev" VALUES(9,73,'9:73','Owange ggwe Nabbi, lwanyisa abakaafiiri, n’abannanfusi era obakaluubirize, era obuddo bwa bwe muliro Jahannama, era buddo bubi nnyo.');
INSERT INTO "african_dev" VALUES(9,74,'9:74','Balayira Katonda nti tebabyogerangako, naye nga mazima baayatula e kigambo ky''obukaafiiri, olwo nno nebafuuka abakaafiiri oluvanyuma lw''okuba nti baali basiraamu, nebagezaako nnyo okukola kye baali tebasobola kutuukiriza (eky''okutta Nabbi), tewali kyabakozesa mpalana okugyako okuba nti Mukama Katonda n’omubakawe baabagonnomolako e bigabwa bya Katonda, kale nno singa beenenya kiba kirungi gye bali, naye bwe batakikola Katonda agenda ku babonereza olubonereza oluluma ennyo ku nsi, ne ku nkomerero, era nga tebalina ku nsi mukuumi yenna oba mutaasa.');
INSERT INTO "african_dev" VALUES(9,75,'9:75','Era mu bo mulimu abo abaalagaanyisa Katonda nga bagamba nti, singa atuwa mu bigabwabye, mazima tujja kusaddaakako era tujja kubeerera ddala mu balongoofu.');
INSERT INTO "african_dev" VALUES(9,76,'9:76','Bwe yabawa ku bigabwabye, baabikodowalira, nebava ku kiragaanye, mu kukola ekyo nebaba nga baava ku ndagaano.');
INSERT INTO "african_dev" VALUES(9,77,'9:77','N’olwekyo ekyaddirira Katonda nannyikiza obunnanfusi mu mitima gy’abwe, okutuusa ku lunaku lwe balimusisinkana, olw''okuba nti baayawukana ku kye baalagaanyisa Katonda, ne ku lw''ebyo bye baali balimbisa.');
INSERT INTO "african_dev" VALUES(9,78,'9:78','Abaffe tebaamanya nti mazima Katonda amanyi e byama byabwe n’emboozi zaabwe ez’olwatu, era nga mazima amanyidde ddala e byekusifu.');
INSERT INTO "african_dev" VALUES(9,79,'9:79','Abo abavumirira abakozi b’obulungi mu bakkiriza olw''okuwaayo saddaaka, era nebavumirira n''abo abatasobola kufuna okugyako akatono, nekibatuusa n’okubagyegya, Katonda abaziimuula era bagenda kussibwako e bibonerezo e biruma.');
INSERT INTO "african_dev" VALUES(9,80,'9:80','Koobeegayiririre oba tobeegayiririra, nebwonaabeegayiririra e mirundi nsanvu Katonda tagenda kubasonyiwa, ekyo nno lwa kuba nti mazima bbo, baawakanya Katonda n’omubakawe, era nga bulijjo Katonda talungamya bantu boonoonyi.');
INSERT INTO "african_dev" VALUES(9,81,'9:81','Abaayawukana ku Nabbi baasanyuka olw''okusigala nga batudde, nga baawukanye ku mubaka w’a Katonda, nebatamwa okulwana ne mmaali yaabwe, n’emyooyo gyabwe mu kkubo lya Katonda, era nebagamba bannaabwe temugezaako nemugenda mu bbugumu, bagambe nti omuliro Jahannama gwe gusinga okwokya singa babadde bategeera.');
INSERT INTO "african_dev" VALUES(9,82,'9:82','Kale nno basaana baseke kitono (mu bulamu obw''ensi), ate bakaabe nnyo (ku nkomerero), nga y''empeera gye bagenda okusasulwa olw''ebyo bye baali bakola.');
INSERT INTO "african_dev" VALUES(9,83,'9:83','Singa Katonda anaakuzza eri e kibinja mu bo (e Madinah), nebakusaba okufuluma (ku lutabaalo), bagambe nti temugenda kuddayo kufuluma nange, era temugenda kulwanyisa mulabe yenna nga muli nange, mazima mmwe mwasalawo obutagenda omulundi ogwasooka, kale mubeere naabo abaanjawukanako.');
INSERT INTO "african_dev" VALUES(9,84,'9:84','Era tosaaliranga oyo yenna aba afudde mu bo, era toyimiriranga ku kabbuliye, anti mazima bbo baagyemera Katonda n’omubakawe, era ne bafa nga boonoonyi.');
INSERT INTO "african_dev" VALUES(9,85,'9:85','Obungi bwe mmaali yaabwe n’abaana baabwe tebukwewuunyisa. Katonda kyagenderera mu byo kubabonereza kuno ku nsi, era batuuke okufa nga bali ku bukaafiiri.');
INSERT INTO "african_dev" VALUES(9,86,'9:86','Essura bwe ssibwa nga ebalagira nti mukkirize Katonda, era mulafuubane mu kuweereza mu kkubolye nga muli n’omubakawe, abagagga mu bo bakusaba obasonyiwe, nebagamba nti, tuleke tubeere n’abatagenze.');
INSERT INTO "african_dev" VALUES(9,87,'9:87','Baasiima okubeera awamu n’abaasigala. Emitima gyabwe negizibikirwa, ne baba nga tebasobola kutegeera.');
INSERT INTO "african_dev" VALUES(9,88,'9:88','Naye mazima omubaka n’abo abakkiriza abali awamu naye balafuubana mu kuweereza mu kkubo lya Katonda nga bakozesa e mmaali yaabwe, n’emyoyo gyabwe, era abo balina obulungi, era abo bbo be balituuka ku buwanguzi.');
INSERT INTO "african_dev" VALUES(9,89,'9:89','Katonda yabategekera e jjana ezikulukutiramu e migga, baakubeeramu bugenderevu, ekyo nno kwe kwesiima okusuffu.');
INSERT INTO "african_dev" VALUES(9,90,'9:90','Abawarabu ba nnamalungu ne bajja eri Nabbi nga beetonda, babe nga bakkirizibwa obutagenda ku lutalo, ate abo abaalimbisa Katonda n’omubakawe, bo baatuula butuuzi (awatali kuwa nsonga yonna), abo abaakaafuwala mu bo, bagenda kutuukwako e bibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(9,91,'9:91','Abanafu n’abalwadde, n’abo abatasobola kufuna kye bawaayo, tewali kibavunaanwa kasita baba nga bakola ku lwa Katonda n’omubakawe, abakozi b’obulungi (ng’abo) tewali wooyita kubassaako musango, nga bulijjo Katonda musonyiyi, musaasizi.');
INSERT INTO "african_dev" VALUES(9,92,'9:92','Era tewali musango ne kw''abo abakujjira obafunire engeri gy’obatwalamu ku lutalo, noogamba nti sirina ngeri gye mbatwalamu, nebava wooli ng’amaaso gaabwe gakulukuta amaziga olw''ennaku y’okuba nti tebayinza kufuna kye bawaayo.');
INSERT INTO "african_dev" VALUES(9,93,'9:93','Mazima okuvunaana, kubeera kw''abo abaakusaba obutagenda ate nga bagagga, baasalawo babe naabo abasigadde, Katonda nassa e nvumbo ku mitima gyabwe, tebayinza kumanya (nsonga ntuufu).');
INSERT INTO "african_dev" VALUES(9,94,'9:94','(Abo abaabaawukanako ku lutalo lwe Tabuk) bajja kubeetondera (nga balimba), bwe munaaba muzzeeyo gye bali bagambe (gwe Nabbi Muhammad) nti temwetonda tetujja kukkiriza kwetonda kwa mmwe, anti mazima Katonda yatutegeeza ebibakwatako, Katonda ajja kulaba ebikolwa bya mmwe n''omubakawe ajja kubiraba, oluvanyuma muli baakuzzibwa eri omumanyi w''ebyekwese n''ebyolwatu, olwo nno Katonda alyoke abategeeze ebyo bye mwali mukola (ku nsi).');
INSERT INTO "african_dev" VALUES(9,95,'9:95','(Abo abaakwawukanako ku lutalo lwe Tabuk) bajja kubalayirira Katonda bwe munaaba muzzeeyo gye bali muleme kubaako bye mubanenya, kale mubaleke anti mazima bo banyoomebwa nga n''obuddo bwa bwe muliro Jahannama nga y''empeera y''ebyo bye baali bakola.');
INSERT INTO "african_dev" VALUES(9,96,'9:96','Babalayirira balyoke babasiimiikirize naye bwe munaaba mukkiriza okusiimiikiriza kwabwe, mazima ye Katonda takkiriza kusiimiikiriza kwa bantu aboonoonyi.');
INSERT INTO "african_dev" VALUES(9,97,'9:97','Ba nnamalungu be basinga obukaafiiri n''obunnanfusi era be basinga obutamanya bikomo bya Katonda bye yassa ku Mubakawe, Katonda mumanyi nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(9,98,'9:98','Mu ba nnamalungu mulimu abo abalaba kye baba bawaddeyo kyonna mu kkubo lya Katonda nga kufiirwa era nga babaagaliza bizibu naye nga e bizibu bya kutuuka ku bo, (sso si ku mmwe), Katonda awulira nnyo era muyitirivu wa kumanya.');
INSERT INTO "african_dev" VALUES(9,99,'9:99','Era mu ba nnamalungu mulimu abakkiriza Katonda n''olunaku lw''enkomerero era nga ne bye baba bawaddeyo mu kkubo lya Katonda babibala nga bya kubatuusa ku kusiima kwa Katonda n''omubaka okubasabira (obulungi). Kituufu ebyo bye baba bawaddeyo bibatuusa ku kusiima kwa Katonda, Katonda ajja kubayingiza (e jjana) ekifo ky''okusaasirakwe.Anti mazima Katonda musonyiyi nnyo musaasizi.');
INSERT INTO "african_dev" VALUES(9,100,'9:100','N''abo abaasookera ddala okukkiriza mu baasenguka okuva e Makkah ne ba Answari (abaataasa Nabbi) n''abo abaabagoberera mu kukola obulungi, Katonda yabasiima nabo ne bamusiima, era Katonda yabateekerateekera e jjana ezikulukutiramu e migga nga baakubeera mu zo olubeerera, okwo nno kwe kwesiima okusukkulumu.');
INSERT INTO "african_dev" VALUES(9,101,'9:101','Ne mw''abo ababeetoolodde mu ba nnamalungu mulimu abannanfusi ne mu batuuze be Madinah mulimu abaagugubira ku bunnanfusi, nga ggwe tobamanyi, ffe nno tubamanyi. Tujja kubabonereza e mirundi e biri (kuno ku nsi n''okubaswaza n''ebibonerezo byo mu Kabuli oluvanyuma lw''okufa), oluvanyuma baakuzzibwa eri e bibonerezo e bisuffu.');
INSERT INTO "african_dev" VALUES(9,102,'9:102','(Waliwo) N''abalala abakkiriza e bibi byabwe, baatabula e mirimu e mirungi n''emirala e mibi. Ob''olyawo Katonda ajja kubasonyiwa (olw''okwetonda kwa bwe) anti mazima Katonda muyitirivu wa kusonyiwa era muyitirivu wa kusaasira.');
INSERT INTO "african_dev" VALUES(9,103,'9:103','(Abo abeetonda mu bo) gya mu mmaali yaabwe Saddaaka obatukuze era obalongoose nayo. Era basabire (Katonda abasonyiwe) anti mazima okusaba kwobasabira mirembe gye bali, Katonda awulira nnyo mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(9,104,'9:104','Tebamanyi nti mazima Katonda yye, yakkiriza okwenenya kwa baddube era nti yakkiriza Saddaaka era nti mazima Katonda yye yakkiriza okwenenya era omusaasizi ennyo.');
INSERT INTO "african_dev" VALUES(9,105,'9:105','Bagambe (Ggwe Nabbi Muhammad) nti mukole, Katonda ajja kulaba e mirimu gya mmwe n’omubakawe n’abakkiriza era nga muli ba kuzzibwa eri omumanyi w''ebitalabikako n''ebirabikako olwo alyoke abategeeze, ebyo bye mwali mukola.');
INSERT INTO "african_dev" VALUES(9,106,'9:106','(N''abalala mw''abo abaakwawukanako ku lutalo lwe Tabuk) baalekerwa kusalawo kwa Katonda, oba agenda kubabonereza oba alikkiriza okwenenya kwa bwe. Mazima Katonda mumanyi nnyo mugobansonga.');
INSERT INTO "african_dev" VALUES(9,107,'9:107','N''abo (Abannanfusi) abaateekawo omuzikiti ogw''obulabe n’olwobukaafiiri, era n’olwokwawulayawula mu bakkiriza n’olwokutegekera oyo eyalwanyisa Katonda n''omubakawe oluberyeberye, era bajja kulayirira ddala nti tetulina kigendererwa okugyako obulungi, so nga Katonda amanyi nti mazima bo balimbira ddala.');
INSERT INTO "african_dev" VALUES(9,108,'9:108','Bulijjo toyimiriranga (Ggwe Nabbi) okusaalira mu gwo, mazima omuzikiti ogwazimbibwa ku musingi ogw''okutya Katonda kuntandikwa gw''oteekwa okusaalirangamu, mu muzikiti ogwo mulimu abasajja abaagala okwetukuza, anti bulijjo Katonda ayagala abeetukuza.');
INSERT INTO "african_dev" VALUES(9,109,'9:109','Abaffe oyo eyatandika omusingi gw''ekizimbekye ku lw''ekigendererwa ky''okutya Katonda n''okusiimakwe yaasinga obulungi oba oyo eyatandikawo omusingi gw''ekizimbekye ku nsonda y''ekifunvu nekimusuula mu muliro Jahannama, Katonda talungamya bantu beeyisa bubi.');
INSERT INTO "african_dev" VALUES(9,110,'9:110','Ekizimbe Abannanfusi kye baazimba kijja kusigala nga kya kubuusabuusa mu mitima gya bwe okutuusa okufa kwa bwe, Katonda mumanyi nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(9,111,'9:111','Mazima Katonda yagula ku bakkiriza obulamu bwa bwe ne mmaali ya bwe nga ddala agenda kubasasulamu jjana olw''okuba nti balwana mu kkubo lya Katonda ne batta era nga nabo battibwa eyo nga ndagaano gye yeekakasaako mu Taurat mu Injil ne mu Kur’ani ate ani ayinza okutuukiriza e ndagaano ye okusinga Katonda, kale nno musanyuke n''obuguzi bwe mugulaana era okwo kwe kwesiima okusukkulumu.');
INSERT INTO "african_dev" VALUES(9,112,'9:112','(Abagwa mu kiti ekyo) beebo abeenenyeza Katonda abasinza Katonda abatendereza Katonda, abatambula mu nsi ku lw''okuweereza Katonda abakutama ku maviivi ne bavunnama ku ttaka (nga basaala) abalagira e mpisa e nnungi era abaziyiza amayisa amabi, era abeekuumira ku mateeka ga Katonda, era sanyusa abakkiriza.');
INSERT INTO "african_dev" VALUES(9,113,'9:113','Nabbi tekimugwanira wadde abakkiriza okwegayiririra abagatta ku Katonda e kintu e kirala, ne bwe baba b''aluganda olw''o kumpi kasita kimala okubeeyolekerera ddala nti mazima bo ba mu muliro.');
INSERT INTO "african_dev" VALUES(9,114,'9:114','Okwegayirira kwa Ibrahim kulwa kitaawe tekyaliwo okugyako olw''endagaano gye yamuwa, naye bwe yamala okukitegeera ddala nti mazima yye (kitaawe) mulabe wa Katonda yamwegaana, (Ibrahim yalina okugezaako nga bwasobola okwegayirira Katonda asaasire kitaawe), anti Ibrahim bulijjo yali muyitirivu wa kusaba na kuloopa nsongaze zonna ewa Katonda era nga wa kisa nnyo.');
INSERT INTO "african_dev" VALUES(9,115,'9:115','Katonda teyali waakubuza bantu oluvanyuma lw''okubalungamya okugyako nga amaze okubannyonnyola ebyo bye bateekwa okwewala. Anti mazima Katonda mumanyi wa buli kintu.');
INSERT INTO "african_dev" VALUES(9,116,'9:116','Mazima Katonda bubwe obufuzi bwe ggulu (omusanvu) ne nsi, awa obulamu era natt, ng''oggyeko Katonda temulina mukuumi wadde omutaasa.');
INSERT INTO "african_dev" VALUES(9,117,'9:117','Mazima Katonda yakkiriza okwenenya kwa Nabbi n''okwa ba Muhajiriina (abaasenguka) n''okwa ba Answar (abaabudamya) abo abaamugoberera mu kiseera ekizibu oluvanyuma lw''okusemberera emitima gya''bamu ku bo okukyuka, olwo nno n''abasonyiwa anti mazima yye bulijjo wa kisa musaasizi era yakkiriza okwenenya.');
INSERT INTO "african_dev" VALUES(9,118,'9:118','Era yakkiriza okwenenya kw''abasatu abo abataagenda ku lutabaalo okutuusa ensi lwe yabafundirira awamu n''obugazi bwayo, nabo bennyini nebafunda olw''okweraliikirira, nebatuuka okulowooza nti tewali kuwona Katonda okugyako nga omuntu azze gyali, oluvanyuma Katonda yabaddiramu basobole okwenenya. Anti mazima Katonda yye akkiriza okwenenya musaasizi.');
INSERT INTO "african_dev" VALUES(9,119,'9:119','Abange mmwe abakkiriza mutye Katonda era mubeere wamu n''a b''amazima.');
INSERT INTO "african_dev" VALUES(9,120,'9:120','Kyali tekisaanira bantu be Madinah neba nnamalungu ababeetoolodde okwawukana ku mubaka wa Katonda (ku lutabaalo) n''okuba nti beeyagala okusinga bwe bamwagala, ekyo nno lwa kuba nti mazima bo tewali nyonta wonna lwe baluma, wadde okukoowa, wadde e njala, nga bali mu kulafuubana okuweereza mu kkubo lya Katonda era tewali kigere kyonna, kye batambula ekinyiiza abakaafiiri, era tewali buzibu bwonna bwe batuusa ku mulabe, okugyako nga kibawandiikibwako nga omulimu omulungi. Mazima ddala bulijjo Katonda tayonoona mpeera ya bantu balongoosa.');
INSERT INTO "african_dev" VALUES(9,121,'9:121','Era tebawaayo kiweebwayo kyonna kitono oba kinene era tebamalaako lusenyi lwonna okugyako nga kibawandikibwakonga kirungi. Olwo nno Katonda alyoke abasasule e birungi e bisukkuluma kw''ebyo bye baakola.');
INSERT INTO "african_dev" VALUES(9,122,'9:122','Abakkiriza tebateekwa bonna kufuluma kugenda (kulafuubana mu kkubo lya Katonda) naye singa mu buli bantu muvaamu e kibinja ne bagenda bakuguka mu kumanya e ddiini olwo nno bagende babuulirire abantu baabwe nga bakomyewo gye bali ekyo nno kibasobozese okwekeka (ebitakkirizibwa).');
INSERT INTO "african_dev" VALUES(9,123,'9:123','Abange mmwe abakkiriza mulwanyise abakaafiiri abo ababaliraanye bateekwa bawulire obuzito obuva mu mmwe, era mukimanye nti mazima ddala Katonda bulijjo abeera wamu n''abamutya.');
INSERT INTO "african_dev" VALUES(9,124,'9:124','Bulijjo essuula bweba nga essiddwa, mu bo mulimu abagamba nti ani mu mmwe (e ssuula) eno gweyongedde obukkiriza, wabula bo abakkiriza eba ebongedde obukkiriza era baba bali mu ssanyu.');
INSERT INTO "african_dev" VALUES(9,125,'9:125','Naye abo abalina obulwadde mu mitima gya bwe ebongera bubi ku bubi bwa bwe nebatuuka okufa nga nabo bakaafiiri.');
INSERT INTO "african_dev" VALUES(9,126,'9:126','Abaffe tebakiraba nti mu buli mwaka bagezesebwa omulundi gumu oba ebiri, oluvanyuma ne bateenenya wadde okuba abeebuulirira.');
INSERT INTO "african_dev" VALUES(9,127,'9:127','Era e ssuula bweba essiddwa abamu batunula ku bannaabwe (nga bagamba nti) abaffe waliwo omuntu yenna abalaba olwo nno ne beemulula, Katonda yakyusa e myoyo gya bwe kubanga bo bantu abatategeera.');
INSERT INTO "african_dev" VALUES(9,128,'9:128','Omubaka yabajjira nga ava mu mmwe mwennyini nga kimuzitoowerera nnyo ekyo ekibakaluubiriza abalumirwa nnyo, era eri abakkiriza bonna wa kisa musaasizi.');
INSERT INTO "african_dev" VALUES(9,129,'9:129','Wabula kebava ku ebyo, gwe (Nabbi Muhammad) gamba nti Katonda ammala, tewali kisinzibwa kyonna (mu butuufu) okugyako yye, yye gwenneesiga era yye ye mufuzi wa Arish ey''ekitiibwa.');
INSERT INTO "african_dev" VALUES(10,1,'10:1','Aliif Laam Raa, e nnyukuta zino twazoogerako ku ntandikwa ya Sura Al Bakara, bino bye bigambo by''ekitabo (Kur’ani) eby''amagezi.');
INSERT INTO "african_dev" VALUES(10,2,'10:2','Abaffe kya kwewuunya eri abantu okuba nti twassa obubaka eri omusajja mu bo, (nga tumulagidde) nti tiisa abantu (abatakkiriza) ate owe amawulire ag''essanyu eri abo abakkiriza (obubaka) nti balina e mpeera e nnungi ewa Mukama omulabirizi waabwe (ku lw''emirimu e mirungi gye bakola). Abakaafiiri (Nabbi bwe yamala okubajjira) ne bagamba nti mazima ono mulogo mweyolefu.');
INSERT INTO "african_dev" VALUES(10,3,'10:3','Mazima Mukama omulabirizi wa mmwe ye Katonda oyo eyatonda e ggulu (omusanvu) n''ensi mu nnaku mukaaga bwe yamala naatereera ku Arish nga atambuza buli kimu, tewali agenda kwetantala kuwolereza okugyako nga Katonda amaze okukkiriza. Oyo nno mwe, ye Katonda, omulabirizi wa mmwe. Kale nno mumusinze, abaffe temwebuulirira.');
INSERT INTO "african_dev" VALUES(10,4,'10:4','Gyali y''eri obuddo bwa mmwe mwenna eyo nga ndagaano e nkakafu eyabassibwako Katonda, anti mazima yye, y’atandika e bitonde ate oluvanyuma y''agenda okubizzaawo, abe nga asasula mu bwenkanya abo abakkiriza nebakola e mirimu e mirongoofu ate abo abakaafuwala bagenda kuweebwa e byokunywa nga by''amazzi ag''olweje, era bassibweko e bibonerezo e biruma olw''ebyo bye baawakanyanga.');
INSERT INTO "african_dev" VALUES(10,5,'10:5','(Katonda) yye yooyo eyassaawo e njuba nga eyaka, nassaawo omwezi nga kitangaala, n''agugerera e bifo (mwe gubeera mu biseera eby''enjawulo) kibasobozese okumanya omuwendo gw''emyaka n''okubala, ebyo Katonda teyabikola okugyako mu butuufu bwa byo (nga bwebiteekwa okuba), bulijjo Katonda annyonnyola obubonero (obulaga obuyinza bwe n''obusobozibwe) eri abantu abamanyi.');
INSERT INTO "african_dev" VALUES(10,6,'10:6','Mazima okwawukana kw''ekiro n''emisana n''ebyo Katonda bye yatonda mu ggulu omusanvu n''ensi ddala mulimu obubonero eri abantu abatya Katonda (obubayamba okutegeera e kitiibwa n''obuyinza bwa Katonda).');
INSERT INTO "african_dev" VALUES(10,7,'10:7','Mazima abo abatasuubira kutusisinkana era ne basiima okwemalira ku bulamu bw''ensi era ne batebenkera nabwo, era abo abatassa mulaka ku bubonero bwaffe.');
INSERT INTO "african_dev" VALUES(10,8,'10:8','Abo nno obuddo bwa bwe muliro olw''ebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(10,9,'10:9','Mazima abo abakkiriza nebakola e mirimu e mirongoofu, Mukama omulabirizi waabwe abalungamya olw''obukkiriza bwa bwe, gikulukutira e migga wansi waabwe mu jjana ez''ebyengera.');
INSERT INTO "african_dev" VALUES(10,10,'10:10','Okusaba kwa bwe nga bali mu yo (baliba batendereza nga bagamba nti) wasukkuluma ayi Mukama Katonda nga ne ssalaamu, kye kiriba e kiramuso kya bwe, nga n''enkomelero y''okusaba kwa bwe baligamba nti amatendo gonna ga Katonda omulabirizi w''ebitonde.');
INSERT INTO "african_dev" VALUES(10,11,'10:11','Singa Katonda yali atuusa ku bantu akabi nga bwabatuusaako amangu obulungi, yaalibatuusizza kw''ekyo kye yabagerera, olwo nno netuleka abatasuubira kutusisinkana mu bubuze bwa bwe nga babulubuuta.');
INSERT INTO "african_dev" VALUES(10,12,'10:12','Omuntu bwatuukwakwo obuzibu atukoowoola tumuyambe kaabe nga agalamidde, atudde, oba nga ayimiridde, wonna wetumuggyirako obuzibubwe aniigiina nga atatuyitangako kujja kumutaasa mu buzibu obuba bumutuuseeko, mu ngeri nga eyo aboonoonyi ab''olutentezi bwe balaba ekibi nekiba nga e kirungi mu ebyo bye bakola.');
INSERT INTO "african_dev" VALUES(10,13,'10:13','Twazikiriza e mirembe mingi oluberyeberye lwa mmwe bwe gyamala okweyisa obubi (ewa Katonda wa bwe), so nga ababaka baabwe baabajjira n''obunnyonnyofu naye nga tebaali baakukkiriza, bulijjo bwe tutyo bwe tusasula abantu aboonoonyi.');
INSERT INTO "african_dev" VALUES(10,14,'10:14','Oluvanyuma twabafuula (mmwe) abaddira bali mu bigere oluvanyuma lwa bwe, olwo nno tulabe mmwe mukola mutya!.');
INSERT INTO "african_dev" VALUES(10,15,'10:15','Ebigambo byaffe bwe bibasomerwa ng’ate ddala binnyonnyofu, abo abatasuubira kutusisinkana bagamba nti leetayo Kur’ani endala etali eno oba eno gikyuseemu, bagambe nti tekiri mu buyinza bwange okugikyusa nga nze nnesalirawo, sirina kyengoberera okugyako ekyo ekimpebwa nga bubaka, mazima nze ntya ebibonerezo by''okulunaku oluzito singa njemera Mukama omulabirizi wange.');
INSERT INTO "african_dev" VALUES(10,16,'10:16','Bagambe nti singa Katonda yayagala sandigibasomedde (Kur’ani) era (Katonda) teyandigibamanyisizza, anti mazima nnatuula mu mmwe ebbanga ddene nga tennampebwa abaffe temutegeera!.');
INSERT INTO "african_dev" VALUES(10,17,'10:17','Ani eyeeyisa obubi okusinga oyo atemerera ku Katonda ebigambo eby''obulimba oba naalimbisa ebigambo bye mazima ddala aboonoonyi tebagenda kuwona.');
INSERT INTO "african_dev" VALUES(10,18,'10:18','Nebatuuka okusinza ebirala ebitali Katonda ebyo ebitayinza kubatuusaako kabi era ebitayinza kubagasa nebagamba nti bano, be bawolereza baffe ewa Katonda, gamba (Ggwe Muhammad nti mu kukola ekyo) mutegeeza Katonda ekyo kyatamanyi mu ggulu (omusanvu) wadde mu nsi, Musukkulumu era yeesambye nnyo kw''ebyo bye bumugattako.');
INSERT INTO "african_dev" VALUES(10,19,'10:19','Abantu tebabangako okugyako e kibiina kimu (Wddiinin y''obusiraamu) wabula oluvanyuma baayawukana, naye singa si kigambo ekyakulembera nga kiva ewa Mukama omulabiriziwo (okuba nti buli muntu alina waalifiira) ensonga zaabwe zaalimaliddwa (mangu) mw''ebyo bye baawukanamu.');
INSERT INTO "african_dev" VALUES(10,20,'10:20','Era bagamba nti singa (Muhammad) assibwako eky''amagero okuva ewa Mukama omulabiriziwe kale ggwe gamba nti mazima ebitalabwako biri mu mikono gya Katonda (bwayagala abiwa abantu bwaba tayagadde naatabawa), kale nno mulindirire, mazima nze ndiwamu na mmwe mu balindirira.');
INSERT INTO "african_dev" VALUES(10,21,'10:21','Abantu bwe tubakombya ku kusaasira oluvanyuma lw''obuzibu obuba bubatuuseeko ogenda okulaba nga balina enkwe zebakola ku by''amagero byaffe, gamba ggwe (Muhammad) nti Katonda y''asinga obwangu mu kussa mu nkola kyaba asazeewo, mazima ddala bulijjo ababaka baffe bawandiika enkwe zemusala.');
INSERT INTO "african_dev" VALUES(10,22,'10:22','(Katonda) yye yooyo abatambuza ku lukalu ne ku mazzi okutuusa bwe muba nga muli mu lyato (amaato) negatambula n''abantu abagaliko nga gaseeyeeya mu mpewo e nnungi era negabasanyusa ate nno baba balyawo e mpewo erimu kibuyaga neebajjira n''amayengo negabajjira okuva mu mpuyi zonna, nebatuuka n''okulowooza nti obwabwe bukomye, awo nno basaba Katonda ng''okusaba kwonna bakwolekeza ye yekka era nga bagamba nti n''omala otuwonya e kizibu kino tujja kubeerera ddala mubeebaza.');
INSERT INTO "african_dev" VALUES(10,23,'10:23','Naye ate bwamala okubawonya ogenda okubalaba nga bakola obwonoonefu mu nsi awatali nsonga yonna ntuufu. Abange mmwe abantu mazima obwonoonefu bwa mmwe bufiiriza mmwe, (anti bwo) kweyagala kwa bulamu bwa nsi oluvanyuma gyetuli y''eri obuddo bwa mmwe olwo nno tubamanyise byonna bye mwakolanga.');
INSERT INTO "african_dev" VALUES(10,24,'10:24','Mazima e kifaananyi ky''obulamu bw''ensi kiringa amazzi getussa okuva waggulu ne gatuuka ku bimera ebiri mu ttaka, mu ebyo abantu bye balya n''ensolo bye zirya, okutuusa ensi bwetandika okulabika obulungi nenyirira abantu abagiriko nebalowooza nti bagisobola, e kiragiro kyaffe nekibajjira ekiro oba e misana netugifuula nga enkungule neeba nga etabadde y''amugaso eggulo bwe tutyo nno bwe tunnyonnyola obubonero eri abafumiitiriza.');
INSERT INTO "african_dev" VALUES(10,25,'10:25','Ne Katonda akoowoola abantu okujja eri e nyumba ey''emirembe era alungamya oyo gwaba ayagadde eri ekkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(10,26,'10:26','Abo abakola obulungi bagenda kuweebwa obulungi n''ennyongeza. Enfuufu tegenda kubikka byenyi bya bwe wadde okulabikako obunyomoofu, abo nno b''ebantu bo mu jjana bo baakutuula bugenderevu mu yo.');
INSERT INTO "african_dev" VALUES(10,27,'10:27','Bo abakola ebibi empeera y''ekibi yenkana ekibi ekikoleddwa era obunyoomu bugenda kubabikka tebagenda kubeera n''abawonya yenna ku Katonda, bo ebyenyi bya bwe bigenda kubeera nga ebibikkiddwa ekitundu ky''ekiro eky''enzikiza abo nno b''ebantu bo mu muliro bo baakutuula mu gwo lubeerera.');
INSERT INTO "african_dev" VALUES(10,28,'10:28','Era jjukira olunaku lwe tulibakunganya bonna olwo nno netugamba eri abo abaagatta abintu ebirala ku Katonda mubeere mu bifo bya mmwe ne bannammwe (be mwagatta ku Katonda) olwo nno netubaawulamu bebaagatta ku Katonda nebagamba nti mwali temusinza ffe.');
INSERT INTO "african_dev" VALUES(10,29,'10:29','Katonda amala okuba nga ye mujulizi wakati waffe na mmwe okukakasa nti tetumanyangako nti mwali mutusinza.');
INSERT INTO "african_dev" VALUES(10,30,'10:30','Olwo nno buli muntu agenda kulagwa ebyo bye yakola era bazzibwe ewa Katonda mukama waabwe omutuufu olwo nno bye baayiiyanga biryoke bibabuleko.');
INSERT INTO "african_dev" VALUES(10,31,'10:31','Bagambe (Ggwe Muhammad) nti ani abagabirira okuva mu ggulu ne mu nsi oba ani afuga okuwulira n''okulaba era ani aggya ekiramu mu kifu era n''aggya ekifu mu kiramu era ani atambuza buli kintu? Ddala bajja kukugamba nti Katonda, bagambe nti abaffe temutya!.');
INSERT INTO "african_dev" VALUES(10,32,'10:32','Oyo nno mmwe ye Katonda Mukama omulabirizi wa mmwe omutuufu. Oluvanyuma lwa mazima gano ate kiki (omuntu) kyalinda! okugyako okubula, kale nno ate muwugulwa mutya (okuva ku Katonda).');
INSERT INTO "african_dev" VALUES(10,33,'10:33','Bwe kityo e kigambo kya Mukama omulabiriziwo, bwe kyakakata ku abo abaayonoona ne kiba nti mazima ddala bo tebagenda kukkiriza.');
INSERT INTO "african_dev" VALUES(10,34,'10:34','Gamba ggwe (Muhammad) nti abaffe mu abo bemugatta ku Katonda mulimu asobola okutandikawo ebitonde (bwamala n''abiggyawo) ate n''abizzaawo, gamba nti Katonda atandika ebitonde ate agenda kubizzaawo kale ate muwugulwa mutya!.');
INSERT INTO "african_dev" VALUES(10,35,'10:35','Bagambe nti abaffe mu abo bemugatta ku Katonda mulimu asobola okulungamya, gamba nti Katonda alungamya eri obutuufu, yaateekwa okugobererwa oba oyo atalungamya okugyako nga alungamiziddwa mwabaaki ebintu mubisalawo mutya!.');
INSERT INTO "african_dev" VALUES(10,36,'10:36','Abasinga obungi mu bo tebagoberera okugyako okuteeba, anti mazima okuteeba tekulina kintu kyonna kye kuyamba ku kulaga obutuufu, mazima ddala bulijjo Katonda amanyidde ddala ebyo bye bakola.');
INSERT INTO "african_dev" VALUES(10,37,'10:37','Kyali tekisoboka kuba nti Kur’ani eno yagunjibwawo nga teyava wa Katonda, wabula yye Katonda yagissa nga ekakasa ebyo ebitabo ebyagikulembera era nga ennyonnyola ebiri mu bitabo ebyo byonna, teriimu kubuusabuusa yava wa Mukama omulabirizi w''ebitonde byonna.');
INSERT INTO "african_dev" VALUES(10,38,'10:38','Oba bagamba nti (Muhammad) yagigunjaawo, gamba nti (bwemba nga nze nnagiyiiya) kale muleete essuula yonna egifaanana muyite bonna bemunaaba musobodde, nga oggyeko Katonda bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(10,39,'10:39','Wabula baalimbisa ebyo bye bataamanyira ddala mu bujjuvu, nga n''enzivuunula y''abyo tennabajjira bwe batyo abaabakulembera bwe baalimbisa naye tunula olabe enkomerero y''a beeyisa obubi yali etya!.');
INSERT INTO "african_dev" VALUES(10,40,'10:40','Ate mu bo mulimu abakkiriza (Kur’ani) era nga bwe mulimu abatagikkiriza era Mukama omulabiriziwo y''asinga okumanya bikwata ku boonoonyi.');
INSERT INTO "african_dev" VALUES(10,41,'10:41','Naye bwe baba nga bakulimbisizza (ggwe Nabbi Muhammad), bagambe nti nina e mirimu gyenkola (e ddiini yange) na mmwe mulina e mirimu gye mukola (mu ddiini yammwe) muli wala nnyo n''ebyo byenkola era nange ndiwala nnyo n''ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(10,42,'10:42','Ate mu bo mulimu abakuwuliriza naye abaffe ggwe osobola okuwuliza ba kiggala newaakubadde nga tebategeera.');
INSERT INTO "african_dev" VALUES(10,43,'10:43','Era mu bo mulimu akutunuulira naye abaffe ggwe osobola okulungamya ba muzibe wadde nga tebalaba!.');
INSERT INTO "african_dev" VALUES(10,44,'10:44','Mazima Katonda tayisa bubi Bantu mu kintu kyonna, naye mazima abantu bo bennyini be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(10,45,'10:45','N''olunaku (Katonda) lwali bakunganya bagenda kuba nga abataali ku nsi okugyako essaawa emu mu budde bw''emisana nga bamanyigana wakati waabwe. Mazima bafaafaaganirwa abo abalimbisa okusisinkana Katonda era tebabangako balungamu.');
INSERT INTO "african_dev" VALUES(10,46,'10:46','Naye singa tukulaga ebimu ku ebyo bye tubalagaanyisa (kuno ku nsi) oba netukutta (nga tobirabye ky''ekimu) n''olwekyo gye tuli y''eri obuddo bwabwe ate Katonda alaba ebyo byonna bye bakola.');
INSERT INTO "african_dev" VALUES(10,47,'10:47','Nabuli bantu bonna baafuna omubaka, kale nno bwalijja omubaka waabwe (naabawaako obujulizi ku lunaku lw''enkomerero) wagenda kulamulwa wakati waabwe na bwenkanya era nga tebagenda kuyisibwa bubi.');
INSERT INTO "african_dev" VALUES(10,48,'10:48','Era bagamba nti, ebyo ebitulagaanyisibwa biribaawo ddi bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(10,49,'10:49','Bagambe (Ggwe Nabbi Muhammad) sisobola kwetaasa mu kabenje konna era nga bwe siyinza kwegasa na kintu kyonna okugyako ekyo Katonda kyaba anjagalizza, buli bantu balina ebbanga Katonda lye yabagerera singa ebbanga eryo liba lituuse tebasobola kwongezaayo oba okukulembeza oba wadde essaawa emu.');
INSERT INTO "african_dev" VALUES(10,50,'10:50','Bagambe nti, mulaba mutya singa ebibonerezo bya Katonda biba bibajjidde kiro oba misana (ekyo kibagasaaki?) lwaki aboonoonyi basaba ebibonerezo okubajjira amangu!.');
INSERT INTO "african_dev" VALUES(10,51,'10:51','Abaffe oluvanyuma lw''okujja kw''ekibonerezo ebyo olwo nga mubikkirize so nga ate mwali musaba byanguyirizibwe (mu ngeri ya kujeeja).');
INSERT INTO "african_dev" VALUES(10,52,'10:52','Oluvanyuma abeeyisa obubi baligambibwa nti mukombe ku bibonerezo eby''olubeerera, abaffe muyinza okusasulwa okugyako ekyo kye mwapakasa.');
INSERT INTO "african_dev" VALUES(10,53,'10:53','Bakubuuza ggwe (Nabbi Muhammad) nti abaffe ebibonerezo by''olunaku lw''enkomerero bya mazima! bagambe nti yye ndayira Mukama omulabirizi wange nti mazima byo bya mazima, era mmwe temuli baakulemesa (Katonda).');
INSERT INTO "african_dev" VALUES(10,54,'10:54','Singa buli mwoyo (omuntu) gwonna ogweyisa obubi gulina ebyo byonna ebiri mu nsi gwalibifudde ennunuzi (okuwona ebibonerezo bya Katonda), bagenda kulyazanyizibwa. Abeeyisa obubi bwe baliraba ebibonerezo bagenda kwegeyaamu nga bejjusa, era bagenda kulamulwa mu bwenkanya era tebagenda kuyisibwa bubi.');
INSERT INTO "african_dev" VALUES(10,55,'10:55','Abange, mazima bya Katonda, ebiri mu ggulu omusanvu ne mu nsi, abange mazima endagaano ya Katonda ya mazima, naye mazima abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(10,56,'10:56','Yye Katonda y''awa obulamu era yatta ate gyali gye bagenda okuzzibwa.');
INSERT INTO "african_dev" VALUES(10,57,'10:57','Abange mmwe abantu kubajjidde okubuulirira okuvudde eri Mukama omulabirizi wa mmwe era nga kya kuvumula eri ebyo byonna ebiri mu bifuba, era nga bulungamu era nga kusaasira eri abakkiriza.');
INSERT INTO "african_dev" VALUES(10,58,'10:58','Bagambe (Ggwe Nabbi Muhammad) nti ku lw''ebigabwa bya Katonda n''okusaasirakwe (okubawa eddiini yo''busiraamu) ku lw''ebyo basaana basanyuke, ebyo nno bye bisinga obulungi mw''ebyo byonna bye mukunganya.');
INSERT INTO "african_dev" VALUES(10,59,'10:59','Bagambe (Ggwe Nabbi Muhammad) nti mumbulire ebyo Katonda bye yabassiza mu bigabwa mmwe nemweteeramu bye muziza nemulondobamu, bagambe nti mazima Katonda yeyabakkiriza (okukola mutyo) oba Katonda gwe mutemerera.');
INSERT INTO "african_dev" VALUES(10,60,'10:60','Era abo abatemerera ku Katonda eby''obulimba balowoozaaki ku biribaawo ku lunaku lw''okuzuukira, mazima Katonda agabira abantu obulungi bungi nnyo naye abasinga obungi mu bo tebeebaza.');
INSERT INTO "african_dev" VALUES(10,61,'10:61','Tolina mbeera yonna gyobeerako era tewali kintu kyonna kyosoma mu Kur’ani era tewali mulimu gwonna gwe mukola, wabula tubeera bajulizi ku mmwe nga tubalaba mu kiseera wemugutandikira, tewali wadde akantu akatono ennyo ku nsi wadde mu ggulu kasobola kubula ku Mukama omulabiriziwo, wadde akatono okusingako awo oba ekinene tewali kintu kyonna okugyako nga kyawandiikibwa mu kitabo ekinnyonnyofu.');
INSERT INTO "african_dev" VALUES(10,62,'10:62','Abange mazima emikwano gya Katonda tebalibaako kutya era be batagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(10,63,'10:63','(Mikwano gya Katonda) beebo abakkiriza nebaba nga batya Katonda.');
INSERT INTO "african_dev" VALUES(10,64,'10:64','Balina okusanyusibwa mu bulamu obw''ensi n''o bw''enkomerero, (ekyo nga ndagaano ebaweereddwa Katonda, ate nga) ebigambo bya Katonda tebikyusibwa, ekyo nno kwe kwesiima okusuffu.');
INSERT INTO "african_dev" VALUES(10,65,'10:65','Bye boogera tebikunakuwaza anti ekitiibwa n''amaanyi byonna bya Katonda, anti yye y''awulira ennyo omumanyi.');
INSERT INTO "african_dev" VALUES(10,66,'10:66','Abange mazima bya Katonda byonna ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi, abo abaleka Katonda nebasaba ebintu ebirala tebalina kye baliko, tebalina kye bagoberera okugyako ebirowoozo nga bo bwe balaba, bye bakola tebisukka okuba nti bateebereza buteebereza.');
INSERT INTO "african_dev" VALUES(10,67,'10:67','Yye yooyo Katonda eyabateerawo ekiro mube nga mukiwummuliramu, era naabateerawo obudde obw''emisana nga butangaala mazima mu ekyo mulimu obubonero (obulaga okubaawo kwa Katonda era n''obuyinzabwe), eri abantu abawulira.');
INSERT INTO "african_dev" VALUES(10,68,'10:68','Bagamba nti Katonda yeeteerawo omwana, Katonda musukkulumu (ku ebyo bye bamujwetekako anti) yye talina kye yeetaaga, ebiri mu ggulu omusanvu n''ebiri mu nsi byonna bibye, (eky''okugamba nti Katonda alina omwana) temukirinaako bujulizi bwonna, abaffe mwogera ku Katonda bye mutamanyi.');
INSERT INTO "african_dev" VALUES(10,69,'10:69','Gamba nti mazima abo abagunjaawo obulimba ku Katonda tebagenda kwesiima.');
INSERT INTO "african_dev" VALUES(10,70,'10:70','Kale beeyagale mu nsi oluvanyuma gye tuli y''egenda okuba obuddo bwa bwe olwo nno tubakombye ku bibonerezo ebikakali olw''ebyo bye baawakanyanga.');
INSERT INTO "african_dev" VALUES(10,71,'10:71','Banyumize ebyafa ku Nuhu bwe yagamba abantube nti abange bantu bange, okubeera kwange mu mmwe n''okubajjukiza e bigambo bya Katonda bwe bibeeranga bibazitooweredde, nze nneekwasizza Katonda, kale mmwe mwekobaane ne bannammwe ate kyemusazeewo kireme kuba kya kyama olwo nno musalewo kye mwagala okukola era temunnindiriza.');
INSERT INTO "african_dev" VALUES(10,72,'10:72','Bwemuva ku ekyo (nze byonna byenkola mbibakolera lwa Katonda) sibasabangako kunsasula mpeera, empeera yange teri okugyako ewa mukama Katonda, era nnalagirwa okubeera mu basiraamu.');
INSERT INTO "african_dev" VALUES(10,73,'10:73','(Awamu n''ebyo byonna) baamulimbisa netumuwonya n’abo abaali awamu naye mu lyato netubafuula abasigire (ku nsi) era netuzikiriza abo abaalimbisa ebigambo byaffe, tunula (weetegereze) enkomerero y''abo abaalabulwa.');
INSERT INTO "african_dev" VALUES(10,74,'10:74','Oluvanyuma lwe (Nuhu) twatuma ababaka buli omu eri abantube nebabaleetera obujulizi obw''enkukunala naye (bano) tebaali b''a kukkiriza bali bye baalimbisa oluberyeberye, bwe tutyo bulijjo bwe tubikka emitima gy''abo abasukka e nsalo.');
INSERT INTO "african_dev" VALUES(10,75,'10:75','Oluvanyuma lwa bwe twatuma Musa ne Haruna eri Firawo n''abantube nga tubawadde obubonero bwaffe wabula ne bawaganyala anti bulijjo baali bantu boonoonyi.');
INSERT INTO "african_dev" VALUES(10,76,'10:76','Olwo nno amazima bwe gaabajjira okuva gye tuli ne bagamba nti mazima kino ddogo eryeyolefu.');
INSERT INTO "african_dev" VALUES(10,77,'10:77','Musa naabagamba (nga bwe yeewunya) nti mugamba mutya ku mazima agabajjidde okuva ewa Katonda nti lino ddogo, so nga abalogo tebagenda kuwangula (kuno ku nsi ne ku nkomerero).');
INSERT INTO "african_dev" VALUES(10,78,'10:78','Nebagamba nti otujjidde olyoke otuwugule okutujja ku ebyo bye twasangako bakadde baffe. Obuyinza n''ekitiibwa byonna mu nsi (Ya Misiri) bibeere bya mmwe (ggwe ne Haruna) naye ffe mmwe mwembi tetujja kubakkiriza.');
INSERT INTO "african_dev" VALUES(10,79,'10:79','Olwo Firawo naagamba nti mundeetere buli mulogo omukenkufu.');
INSERT INTO "african_dev" VALUES(10,80,'10:80','Kale nno abalogo bwe baamala okujja, Musa kwe kubagamba nti mwanje buli kye mwagala.');
INSERT INTO "african_dev" VALUES(10,81,'10:81','Bwe baamala okwanja (ebyabwe) Musa kwe kubagamba nti ebyo bye muleese lye ddogo anti mazima Katonda ajja kulaga obutaliimu bwa lyo anti mazima Katonda talongoosa mulimu gwa boonoonyi.');
INSERT INTO "african_dev" VALUES(10,82,'10:82','Era Katonda ajja kunyweza obutuufu bw''ebyo bye mbajjidde nabyo n''ebigambobye (ebiragirobye) newaakubadde nga aboonoonyi tebandikyagadde.');
INSERT INTO "african_dev" VALUES(10,83,'10:83','Tewaali bakkiriza Musa (awamu n''obubonero bwe yajja nabwo) okugyako bazzukulu baabwe, nga batya Firawo n''abakungu mu bo okubatuusaako ebikemo, era naye mazima Firawo yeekuluntaza (ne yessa mu ddaala erya waggulu) kuno ku nsi era mazima yye yagenda wala nnyo mu bwonoonyi.');
INSERT INTO "african_dev" VALUES(10,84,'10:84','Musa naagamba nti abange bantu bange bwe muba nga mwakkiriza Katonda mwekwase yye bwe muba nga muli abasiraamu (abeewaayo eri Katonda).');
INSERT INTO "african_dev" VALUES(10,85,'10:85','(Abantu ba Musa) ne bamugamba nti Katonda waffe gwe twekutteko, ayi Mukama omulabirizi waffe, totufuula e kikemo eri abo abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(10,86,'10:86','Era otuwonye (otutaase) olw''okusaasirakwo ku bantu abaakaafuwala.');
INSERT INTO "african_dev" VALUES(10,87,'10:87','Netutumira Musa ne Mugandawe (Haruna) nti muteereewo abantu ba mmwe amayumba mu Misiri era mufuule amayumba ga mmwe nga bwolekero nga musaala era muyimirizeewo e sswala era wa amawulire ag''essanyu eri abakkiriza.');
INSERT INTO "african_dev" VALUES(10,88,'10:88','Musa naagamba nga asaba nti, Ayi Mukama omulabirizi waffe mazima ggwe wawa Firawo n''abakungube eby''okwewunda, n''ebyobugagga mu bulamu buno obw''ensi, ayi Mukama omulabirizi waffe ngenkomerero y''abyo kubuza (bantu) ku baggya ku kkubo lyo, ayi Mukama omulabirizi waffe saanyaawo e mmaali yaabwe era osse obukakanyavu ku mitima gyabwe olwo nno baleme kukkiriza okutuusa nga balabye ebibonerezo ebiruma (nga ate okukkiriza okwo tekukyabagasa).');
INSERT INTO "african_dev" VALUES(10,89,'10:89','(Katonda) naagamba nti mazima okusaba kwa mmwe mwembiriri kukkiriziddwa, mubeere beesimbu mwembi era temugoberera mwembi ekkubo ly''abo abatamanyi (abatategeera bukakafu bwa ndagaano za Katonda).');
INSERT INTO "african_dev" VALUES(10,90,'10:90','Netusomosa abaana ba Israil ennyanja olwo ne Firawo n''abawondera n''amagyege mu ngeri ey''obulabe n’obulumbaganyi, okutuusa lwe yatuukibwako okuzikirira mu mazzi olwo naagamba nti: nzikirizza nti mazima ddala tewali Katonda asinzibwa mu butuufu okugyako oyo abaana ba Israil gwe bakkiriza era nze ndi mu basiramuse.');
INSERT INTO "african_dev" VALUES(10,91,'10:91','(Firawo naagambibwa nti kati olwo okkiriza) so nga wajeema dda n''obeera mu boonoonyi.');
INSERT INTO "african_dev" VALUES(10,92,'10:92','Kale nno olwa leero tujja kukuwonya (kukuuma) omulambogwo (guleme kuvunda) olyoke obeere eky''okulabirako eri abalijja oluvanyumalwo, mazima abantu abasinga obungi tebafaayo (kwegasa na bubonero bwaffe).');
INSERT INTO "african_dev" VALUES(10,93,'10:93','Mazima twasenza abaana ba Israil mu kifo e kirungi ekyesiimisa netubagabirira mu bigabwa ebirungi, nebataba na njawukana (mu ddiini) okutuusa okumanya lwe kwabajjira (nti Nabbi Muhammad ajja kujja nga mubaka wa Katonda) mazima Katonda agenda kulamula wakati wa bwe ku lunaku lw''enkomerero mu ekyo kye baali baawukanamu.');
INSERT INTO "african_dev" VALUES(10,94,'10:94','Bwoba nga olimu okubuusabuusa (Ggwe Muhammad) ku ebyo bye twassa gyoli buuza abo abaasoma e kitabo oluberyeberyelwo, ddala amazima gakujjidde okuva ewa Mukama omulabiriziwo kale togeza nga n''obeera mu babuusabuusa.');
INSERT INTO "african_dev" VALUES(10,95,'10:95','Era ddala tobeeranga mu abo abalimbisa e bigambo bya Katonda n''obeera mu bafaafaganirwa.');
INSERT INTO "african_dev" VALUES(10,96,'10:96','Mazima abo abaakakatako ekigambo kya Mukama omulabiriziwo tebagenda kukkiriza.');
INSERT INTO "african_dev" VALUES(10,97,'10:97','Nebwebanajjirwa buli kabonero okutuusa lwe baliraba e bibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(10,98,'10:98','Tewali bantu ba kitundu bakkiriza obukkiriza bwa bwe nebubagasa okugyako abantu ba Yunusu anti bwe bakkiriza twabaggyako ebibonerezo ebiswaza mu bulamu buno obw''ensi netubeeyagaza okutuusa ku ntuuko zaabwe.');
INSERT INTO "african_dev" VALUES(10,99,'10:99','Singa Mukama omulabiriziwo yayagala abantu abali mu nsi bandikkirizza bonna, kaakati ggwe oyagala kukaka bantu bonna babeere bakkiriza.');
INSERT INTO "african_dev" VALUES(10,100,'10:100','Tewali muntu ayinza kukikkiriza okugyako ng’akkiriza lwa kwagala kwa Katonda, era Katonda assa ekibi ku abo abatategeera.');
INSERT INTO "african_dev" VALUES(10,101,'10:101','Bagambe nti: mwetegereze ebiri mu ggulu (omusanvu) n''ensi naye obubonero n''okutiisa tebigasa bantu batakkiriza.');
INSERT INTO "african_dev" VALUES(10,102,'10:102','Abaffe balina kye balindirira mpozzi ekifaananako ne nnaku zaabo abaabakulembera., bagambe nti: mulindirire nange ndi wamu na mmwe mu balindirira.');
INSERT INTO "african_dev" VALUES(10,103,'10:103','Oluvanyuma ne tuwonya (ne tutaasa) ababaka baffe n''abo abakkiriza bwe tutyo nno bulijjo kikakafu kuffe okutaasa abakkiriza.');
INSERT INTO "african_dev" VALUES(10,104,'10:104','Bagambe nti: abange mmwe abantu bwe muba nga mulina okubuusabuusa mu ddiini yange (mukitegeere nti) sisinza abo be musinza nemuva ku Katonda, wabula nsinza Katonda oyo abatta ,era nnalagirwa mbeere mu bakkiriza.');
INSERT INTO "african_dev" VALUES(10,105,'10:105','Era tereera mu ddiini ya Katonda n''goli mwesimbu era ddala tobeeranga mu bagatta e bintu e birala ku Katonda.');
INSERT INTO "african_dev" VALUES(10,106,'10:106','Tolekanga Katonda n''osaba e kintu e kirala ekyo ekitakugasa era ekitayinza kukutuusako kabi naye bwonookikola mazima ddala ggwe olwo nno onoobeera mu abo abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(10,107,'10:107','Singa Mukama Katonda akutuusaako ekibi tewali ayinza kukikuggyako okugyako yye, ate singa aba akwagalizza kirungi teri ayinza kuzzaayo birungibye, abifunyisa oyo gwaba ayagadde mu baddube, era yye bulijjo ye musonyiyi, omusaasizi owa nnamaddala.');
INSERT INTO "african_dev" VALUES(10,108,'10:108','Bagambe (Ggwe Muhammad) nti; abange mmwe abantu amazima gabajjidde okuva ewa Mukama omulabirizi wa mmwe oyo yenna alungama aba yeelungamidde, ate oyo abula okubulakwe kukosa yye. Nze siri mukuumi ku mmwe.');
INSERT INTO "african_dev" VALUES(10,109,'10:109','Era goberera ebyo ebitumwa gyoli era ogumiikirize okutuusa Katonda lwa naasalawo, anti bulijjo yye yaasinga abasalawo bonna.');
INSERT INTO "african_dev" VALUES(11,1,'11:1','Alif Laam Raa, ekitabo kino e bigambo by''akyo byasengekebwa oluvanyuma nebinnyonnyolwa mu bujjuvu okuva ewa Katonda akola buli kintu ng’asinziira ku nsonga ng’ate amanyi mu bujjuvu buli kintu.');
INSERT INTO "african_dev" VALUES(11,2,'11:2','Kitegeeza abantu nti temusinzanga kintu kyonna wabula Katonda yekka mazima nze gye muli ndi mutiisa (ku bibonerezo bye) era omusanyusa (olw''emperaye eri abalongoosa).');
INSERT INTO "african_dev" VALUES(11,3,'11:3','(Era kibagamba) nti mwegayirire Mukama omulabirizi wa mmwe era mumwenenyeze olwo nno ajja kubeeyagaza olweyagaza olulungi okutuusa ku kiseera ekigere era buli oyo yenna ateekeddwa okufuna obulungi agenda kumuwa obulungibwe, naye bwe muva ku ekyo mazima nze mbatiirira ebibonerezo by''olunaku oluzibu.');
INSERT INTO "african_dev" VALUES(11,4,'11:4','Eri Katonda y''eri obuddo bwa mmwe ate yye nga bulijjo muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(11,5,'11:5','Abange mazima bbo (abatakkiriza) bakweka ebifuba byabwe babe nga beekweka Katonda, abange (mukimanye nti) ne mu kiseera webeebikkira engoye zaabwe Katonda amanya ebyo bye bakweka ne bye boolesa, anti mazima yye amanyidde ddala ebiri mu bifuba.');
INSERT INTO "african_dev" VALUES(11,6,'11:6','Tewali kitonde kyonna mu bitambula ku nsi okugyako nga okugabirirwa kwa kyo kuli ku Katonda, era amanyi byonna ebikikwatako mu bulamu bw''ensi n’obwoluvanyuma, buli kimu kiri mu kitabo ekyeyolefu.');
INSERT INTO "african_dev" VALUES(11,7,'11:7','Era yye yooyo, eyatonda eggulu omusanvu n''ensi mu nnaku mukaaga, era Arish ye yali ku mazzi, olwo nno alyoke abagezese ani mu mmwe asinga okukola obulungi, singa ogamba (Ggwe Muhammad) nti mazima mmwe mujja kuzuukizibwa oluvanyuma lw''okufa abo abakaafuwala bajja kugambira ddala nti tekiri kino (Kur’ani) okugyako eddogo ery''olwatu.');
INSERT INTO "african_dev" VALUES(11,8,'11:8','Singa tulindiriza ku bo ebibonerezo okutuuka ekiseera ekigere bajja kugambira ddala nti ate ki ekibigaana okujja, abange! olunaku lwe biribajjira si bya kubaggyibwako, era biribeetoloola ebyo bye baajeejanga.');
INSERT INTO "african_dev" VALUES(11,9,'11:9','Omuntu bwe tumukombesa ku kusaasira okuva gye tuli ate oluvanyuma ne tukumuggyako mazima yye akutuka n''okusuubira n''akaafuwala.');
INSERT INTO "african_dev" VALUES(11,10,'11:10','Naye ate bwe tumukombesa ku byengera oluvanyuma lw''ebizibu ebiba bimutuuseeko ajja kugambira ddala nti obuzito bunvuddeko, mazima yye aba musanyufu eyeeraga.');
INSERT INTO "african_dev" VALUES(11,11,'11:11','Okugyako abo abagumiikiriza ne bakola emirimu emirongoofu balina ekisonyiwo n''empeera ennene.');
INSERT INTO "african_dev" VALUES(11,12,'11:12','Olabika nga oli waakuleka ebimu ebissibwa gyoli, era ekyo nga kikunyiga mu kifubakyo olw''okuba nti bagamba nti singa aweebwa eggwanika oba Malayika najja naye. Mazima ggwe oli mutiisa. Ate nga Katonda ye mweyimirize ku buli kintu.');
INSERT INTO "african_dev" VALUES(11,13,'11:13','Oba bagamba nti (Kur’ani eno Muhammad) yagigunjawo bagambe nti kale muleeteeyo essuula kkumi enjiiye ezifaanana (Kur’ani) era ng''oggyeko Katonda muyite yenna gwe musobodde (abayambeko) bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(11,14,'11:14','Bwe bataabaanukule, olwo nno mumanye nti mazima yyo (Kur’ani) yassibwa na kumanya kwa Katonda era tewali kisinzibwa kyonna okugyako yye, abaffe mmwe mwewaddeyo ewa Katonda (ne musiramuka).');
INSERT INTO "african_dev" VALUES(11,15,'11:15','Oyo yenna ayagala obulamu obw''ensi n''ebyokwewunda kwayo tubasasula mu bujjuvu emirimu gya bwe mu yo era nga bo muyo tebaseerwa.');
INSERT INTO "african_dev" VALUES(11,16,'11:16','Abo beebo abatalina ku nkomerero kintu kyonna okugyako omuliro, era byonna bye bakola ku nsi nebitabalibwa, bye baakolanga byalinga byonoonefu.');
INSERT INTO "african_dev" VALUES(11,17,'11:17','Abaffe balimbisa oyo (Muhammad) ku bunnyonnyofu obuva ewali Mukama omulabiriziwe, era nga abusoma omujulizi (Jiburilu) ava ewa Katonda nga n''oluberyeberyelwe waliwo ekitabo kya Musa nga kiraga ekkubo era nga kusaasira, abo (Muhammad n''abantube) bagikkiriza ate ebibiina byonna ebitagikkiriza omuliro y''endagaano yaabwe, tobeeranga mu kugibuusabuusaamu, mazima yyo ge mazima agavudde ewa Mukama omulabiriziwo. Naye ddala abantu abasinga obungi tebakkiriza.');
INSERT INTO "african_dev" VALUES(11,18,'11:18','Ani eyeeyisa obubi okusinga oyo agunja obulimba ku Katonda! abo bagenda kwanjulwa ewa Mukama omulabirizi waabwe nti abo baawaayiriza Mukama Katonda Omulabirizi waabwe. Abange ekibonerezo kya Katonda kibeere ku beeyisa obubi.');
INSERT INTO "african_dev" VALUES(11,19,'11:19','Abo abaziyiza abantu ku kkubo lya Katonda ne babaagaliza kukoleramu bikyamu, era nga nabo olunaku lw''enkomerero baluwakanya.');
INSERT INTO "african_dev" VALUES(11,20,'11:20','Abo tebaali ba kulemera ku nsi, era ng''oggyeko Katonda tebalinaayo ba mukwano balala bayinza kubawonya, ebibonerezo bya bwe bigenda kwongezebwamu anti baali tebasobola kuwulira era nga bwe baali tebalaba.');
INSERT INTO "african_dev" VALUES(11,21,'11:21','Abo be baafaafaganirwa emyoyo gya bwe ne bibaggwako bye baayiiyanga.');
INSERT INTO "african_dev" VALUES(11,22,'11:22','Kikakafu ddala bbo ku lunaku lw''enkomerero be balifaafaganirwa ennyo.');
INSERT INTO "african_dev" VALUES(11,23,'11:23','Mazima abo abakkiriza ne bakola emirimu emirongoofu ne bamenyekera Mukama omulabirizi waabwe abo be ba nannyini jjana, bo mu yo ba kutuulamu bugenderevu.');
INSERT INTO "african_dev" VALUES(11,24,'11:24','Ekifaananyi ky''ebibinja ebibiri (ebyogeddwako waggulu) kiringa ekya muzibe ne kiggala n''oyo alaba era nga awulira abaffe balabika nti bafaanana temwebuulirira!.');
INSERT INTO "african_dev" VALUES(11,25,'11:25','Mazima twatuma Nuhu eri abantube (n''abagamba) nti mazima nze ndi mutiisa wa mmwe omweyolefu.');
INSERT INTO "african_dev" VALUES(11,26,'11:26','(Mbategeeza) nti temusinza kintu kyonna okugyako Katonda mazima nze mbatiiriddeko e bibonerezo by''olunaku olw''okuluma obugigi.');
INSERT INTO "african_dev" VALUES(11,27,'11:27','Abakungu abo abaakaafuwala mu bantube ne bagamba nti tetukulaba ggwe okugyako okuba nti oli muntu nga ffe, era tetulaba abakugoberedde okugyako ab''awansi mu ffe ab''endowooza entono, mwenna tetubalabamu nkizo yonna ku ffe wabula tubalowooza kuba balimba.');
INSERT INTO "african_dev" VALUES(11,28,'11:28','Nuhu naagamba nti abange bantu bange mulaba mutya singa mba ndi ku bunnyonnyofu obuva ewa Mukama omulabirizi wange era nampa okusaasira okuva gyali naye mmwe ekyo ne mutakiraba, abaffe tusobola okukibawaliriza ng’ate mmwe temukyagala.');
INSERT INTO "african_dev" VALUES(11,29,'11:29','Era abange mmwe abantu bange (kye mbagamba) si kibasabirako sente, empeera yange teri wantu wonna okugyako ewa Katonda (era nnyaniriza abo bonna ababa bangoberedde) era siri nze w''akugoba abo abakkiriza (ne bwe bataba ba bitiibwa) mazima ddala bo baakusisinkana Mukama omulabirizi waabwe naye mazima ddala nze mbabala mmwe nga muli bantu abatamanyi.');
INSERT INTO "african_dev" VALUES(11,30,'11:30','Era abange bantu bange ani ayinza okuntaasa ku Katonda bwemba mbagobye, abaffe temujjukira.');
INSERT INTO "african_dev" VALUES(11,31,'11:31','Era siyinza kubagamba (mmwe) nti nina amawanika ga Katonda, era simanyi byekusifu, sigamba nti mazima nze ndi Malayika era sigamba abo amaaso ga mmwe begatawa kitiibwa nti Katonda tagenda kubawa birungi, Katonda y''amanyidde ddala ebyo ebiri mu mitima gya bwe, n''olwekyo mazima nze nemmala nkikola ddala mbeera mu beeyisa obubi.');
INSERT INTO "african_dev" VALUES(11,32,'11:32','Nebagamba nti owange Nuhu mazima otukaayanyizza n''oyitiriza okutukaayanya, kale tutuuseeko ebyo by''otulagaanyisa bwoba nga oyogera mazima.');
INSERT INTO "african_dev" VALUES(11,33,'11:33','(Nuhu) naagamba nti mazima Katonda ajja kubibatuusaako bwanaaba ayagadde, ate temuyinza kumulemesa.');
INSERT INTO "african_dev" VALUES(11,34,'11:34','Era okubuulirira kwange tekujja kubagasa singa mba njagadde okubabuulirira, ate Katonda n''aba nga ayagadde kubabuza. Yye ye Mukama omulabirizi wa mmwe era gyali gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(11,35,'11:35','Oba bagamba nti (Muhammad, Kur’ani) yagunja ngunje, bagambe bwe mba nagyegunjirawo ekyonoono ekyo kiri kunze, naye nange sirina kakwate ku ebyo bye mwonoona.');
INSERT INTO "african_dev" VALUES(11,36,'11:36','Nuhu n''aweebwa obubaka nti mazima tebagenda kukkiriza mu bantubo okugyako abo abakkiriza edda n''olwekyo tonakuwala olw''ebyo bye baali bakola.');
INSERT INTO "african_dev" VALUES(11,37,'11:37','Kale kola eryato nga naffe tukutunuulidde era okole okusinziira ku ndagirira yaffe era toyogera nange ku bikwata ku abo abeeyisa obubi, mazima ddala bo baakufiira mu mazzi.');
INSERT INTO "african_dev" VALUES(11,38,'11:38','(Nuhu) n''aba nga akola eryato. Buli lwonna abakungu mu bantube webaamuyitirangako baamusekereranga naagamba nti bwe muba nga mutusekerera naffe tujja kubasekerera nga bwe mutusekerera.');
INSERT INTO "african_dev" VALUES(11,39,'11:39','Lumu mujja kumanya ani gwe binajjira ebibonerezo ebimuswaza era najjirwa ebibonerezo eby''olubeerera.');
INSERT INTO "african_dev" VALUES(11,40,'11:40','(Baabeera bwe batyo) okutuusa ekiragiro kya ffe lwe kyajja ettanuuru neefukumuka amazzi netugamba (Nuhu) nti pakira mu lyo, ngojja mu buli mutindo gwabiramu bibiri bibiri, ekisajja n''ekikazi, era pakiramu abantu b''o mu maka go okugyako oyo eyakakatako edda ekigambo (ekyokuzikirira) era pakiramu buli yenna eyakkiriza, wabula tewaali bakkiriza baali naye okugyako batono.');
INSERT INTO "african_dev" VALUES(11,41,'11:41','(Nuhu) naagamba nti mulinnye e lyato, okutambula kwa lyo n''okuyimirira kwa lyo byonna bibeewo ku lwe linnya lya Katonda, mazima Mukama omulabirizi wange musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(11,42,'11:42','Nalyo neriseeyeeya nabo, mu mayengo agenkana ensozi, mu kiseera ekyo Nuhu yakowoola Mutabaniwe eyali awo ku mabbali g''elyato naamugamba nti mwana wange linnya ogende naffe tosigala n''abakafiiri (n''ozikirira).');
INSERT INTO "african_dev" VALUES(11,43,'11:43','(Omwana) naagamba nti njakulinnya olusozi lujja kumponya amazzi. (Nuhu) naagamba nti tewali ayinza kuwonya olwa leero ( muntu n''asimattuka ) ku kiragiro kya Katonda, okugyako Katonda gwanaaba asaasidde. Awo nno amayengo ga yawula wakati waabwe (omwana) naabeera mu baasaanawo.');
INSERT INTO "african_dev" VALUES(11,44,'11:44','(Ng''ebitonde bimaze okuzikirira) waalangirirwa nti owange ttaka mira amazzi naawe ggulu lekera awo okutonnyesa enkuba era amazzi gaakalira ensonga neekoma awo eryato neritereera ku lusozi oluyitibwa Juudi era newalangirirwa nti okwesamba okusaasira kwa Katonda kubeere ku bantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(11,45,'11:45','Nuhu n''akoowoola Mukama omulabiriziwe naagamba nti Mukama omulabirizi wange mazima Mutabani wange y''omu ku bo mu maka gange era mazima endagaano yo ya mazima era ggwe osinga abalala bonna okugoba e nsonga.');
INSERT INTO "african_dev" VALUES(11,46,'11:46','(Katonda) naagamba nti owange Nuhu mazima yye si wa mu bantubo mazima yye yakola ekikolwa ekitali kirungi, n''olwekyo tonsaba ekyo kyotalinaako kumanya, mazima nze nkubuulirira oleme kubeera mu batategeera.');
INSERT INTO "african_dev" VALUES(11,47,'11:47','(Nuhu) naagamba nti nkusaba obukuumibwo nneme kuba nga nkusaba ekyo kye sirinaako kumanya era bwotonsonyiwe n''onsaasira nja kubeera mu bafaafaaganiddwa.');
INSERT INTO "african_dev" VALUES(11,48,'11:48','Newalangirirwa nti owange Nuhu kka (ove mu lyato), ebiva gye tuli, gibe mirembe na mikisa ku ggwe, ne ku bibiina bya bantu ebiri naawe ne ku bibiina bya bantu betujja okweyagaza mu nsi, oluvanyuma ebibonerezo ebiruma ennyo bibatuukeko okuva gye tuli.');
INSERT INTO "african_dev" VALUES(11,49,'11:49','Ebyo bye bimu ku bintu ebyekusifu tubikutumira ggwe (Muhammad) tobimanyangako ggwe wadde abantubo oluberyeberye lwa kino, n''olwekyo gumiikiriza, mazima enkomerero (e nnungi) y’abatya Katonda.');
INSERT INTO "african_dev" VALUES(11,50,'11:50','Era ab''ekika kya A’di twabatumira muganda waabwe Hudu naagamba nti abange abantu bange musinze Katonda, ngoggyeko yye tewali kirala kyonna kisinzibwa, temuli mmwe okugyako abagunja obugunja bye musinza.');
INSERT INTO "african_dev" VALUES(11,51,'11:51','Abange bantu bange, si basaba mpeera ku kino, empeera yange teri okugyako ku oyo eyantonda, abaffe temutegeera.');
INSERT INTO "african_dev" VALUES(11,52,'11:52','Era abange bantu bange, mwegayirire Mukama omulabirizi wa mmwe, oluvanyuma mwenenye mudde gyali, ajja kubasindikira okuva mu bire waggulu enkuba etonnya ngeddingana, era abongere amaanyi ku maanyi ga mmwe, era temukyukanga nemuva ku ekyo ne muba aboonoonyi.');
INSERT INTO "african_dev" VALUES(11,53,'11:53','Nebagamba nti owange ggwe Hudu totuleetedde bujulizi bweyolefu (ku ebyo byogamba) ate era naffe tetuyinza kuleka ba katonda baffe olw''okuba ogambye, era ffe tetugenda kukukkiriza.');
INSERT INTO "african_dev" VALUES(11,54,'11:54','(Okusinziira ku ebyo by''oyogera) tetulina kye tugamba okugyako nti abamu ku ba katonda baffe be bakuliko mu ngeri eyo embi. (Hudu) naagamba nti era mazima nze njuliza Katonda na mmwe mujulire nti ddala nze ndi wala n''ebyo bye mugatta ku Katonda.');
INSERT INTO "african_dev" VALUES(11,55,'11:55','Nemulekawo Katonda, kale munsalire enkwe mwenna ate temunnindiriza.');
INSERT INTO "african_dev" VALUES(11,56,'11:56','Mazima nze neekwasizza Mukama Katonda omulabirizi wange era Mukama omulabirizi wa mmwe, tewali kitambula ku nsi okugyako yye y''akutte akawompo kakyo, mazima Mukama omulabirizi wange ali ku kkubo ggolokofu.');
INSERT INTO "african_dev" VALUES(11,57,'11:57','Bwe munaava ku ekyo mazima nze mmaze okubatuusaako ekyo kye nnatumwa nakyo era Mukama omulabirizi wange mu kifo kya mmwe ajja kussaawo abantu abalala abatali mmwe, era temulina kabi kemugenda kumutuusaako. Mazima Mukama omulabirizi wange buli kyonna ekikolebwa akilondoola (tewali kimubuuka).');
INSERT INTO "african_dev" VALUES(11,58,'11:58','Ekiragiro kya ffe bwe kyamala okujja ku lw''okusaasira kwa ffe, twawonya Hudu n''abo abakkiriza abaali naye ne tubawonya ebibonerezo ebizito.');
INSERT INTO "african_dev" VALUES(11,59,'11:59','Abantu abo ab''ekibiina kya A’di baawakanya ebigambo bya Mukama omulabirizi waabwe ne bajeemera ababakabe ne bagoberera ekigambo kya buli eyeekuza awalaaza empaka.');
INSERT INTO "african_dev" VALUES(11,60,'11:60','Mu nsi muno nebagoberezebwako ekikolimo, mu ngeri yeemu ne ku lunaku lw''enkomerero, abange mazima ab''ekika kya A’di baajeemera Mukama omulabirizi waabwe abange okubula okwewala ennyo kutuuke ku b''ekika kya A’di abantu ba Hudu.');
INSERT INTO "african_dev" VALUES(11,61,'11:61','Bo ab''ekika kya Thamud twabatumira Muganda waabwe Swaleh, naagamba nti abange bantu bange musinze Katonda temulinaayo kintu kyonna kigwanidde kusinzibwa okugyako yye, yye yeeyabatandikawo ng’abaggya mu nsi, ate naabakwasa obuyinza okugikulaakulanya, kale mumwegayirire, era mwenenye, mudde gyali mazima Mukama omulabirizi wange ali kumpi ayanukula (abamusaba).');
INSERT INTO "african_dev" VALUES(11,62,'11:62','Nebagamba nti owange Swaleh, mazima obadde mu ffe, nga tukulinamu essuubi, nga kino tekinnabaawo. Otugaana okusinza ebyo bakadde baffe bye basinza era mazima ffe tulina okubuusabuusa okujjudde okutankana ku ekyo ky''otuyita okujja gye kiri.');
INSERT INTO "african_dev" VALUES(11,63,'11:63','Naagamba nti abange bantu bange, mukiraba nti singa mba ndi ku bunnyonnyofu obuva ewa Mukama omulabirizi wange, era nenfuna okusaasira okuva gyali, kale ani anantaasa ku Katonda bwe mmugyemera (singa nkolera ku bya mmwe), temulina kye munnyogera okugyako okufiirwa.');
INSERT INTO "african_dev" VALUES(11,64,'11:64','Abange bantu bange eno engamiya evudde wa Katonda nga kabonero gye muli (okukakasa obwa Nabbi bwange), n''olwekyo mugireke erye ebiri mu nsi ya Katonda, temugezaako ne mugituusaako akabi konna nekibaviirako okutuukibwako ebibonerezo ebya mangu.');
INSERT INTO "african_dev" VALUES(11,65,'11:65','(Nebamuwakanya era) nebagifumita olwo nno ye (Swaleh) kwe kubagamba, mweggalire mu mayumba gammwe okumala ennaku ssatu (zokka), eyo ndagaano etalimbisibwa.');
INSERT INTO "african_dev" VALUES(11,66,'11:66','Ekiragiro kya ffe bwe kyamala okujja, twawonya Swaleh n''abo abakkiriza abaali naye olw''okusaasira okuva gye tuli era netubawonya ne mu buswavu obw''olunaku olwo, anti mazima Mukama omulabiriziwo ye wa maanyi nantakubwa ku mukono.');
INSERT INTO "african_dev" VALUES(11,67,'11:67','Abo nno abeeyisa obubi ne batuukibwako olubwatuka (olw''amanyi) olwo nno nebakeesa nga mirambo egigangalamye mu mayumba gaabwe.');
INSERT INTO "african_dev" VALUES(11,68,'11:68','(Engeri gye baazikirizibwamu) baali nga abatawangalirangako mu go, abange mazima abantu b''ekika kya Thamud baawakanya Mukama omulabirizi omulezi waabwe, abange abantu ba Thamud babeere wala nnyo n''okusaasira kwa Katonda.');
INSERT INTO "african_dev" VALUES(11,69,'11:69','Mazima ababaka baffe (Ba Malayika) bajja eri Ibrahim n''amawulire ag''essanyu, nti (Yye ne Mukyalawe bajja kuzaala omwana Ishaka) ne bamugamba nti mirembe naye naabagamba (ng’abaddamu) nti mirembe, waayitawo akaseera katono naabaleetera ennyana enjokye.');
INSERT INTO "african_dev" VALUES(11,70,'11:70','(Ibrahim) bwe yalaba emikono gya bwe nga tegituuka ku nnyama naabekengera, (Ba Malayika) bo kwe kumugamba nti totutya, mazima ffe tuli ba Malayika tutumiddwa eri abantu ba Luutu (okubazikiriza).');
INSERT INTO "african_dev" VALUES(11,71,'11:71','Nga mukyala wa Ibrahim (Sarah) ayimiridde (abawulira) naaseka (nga yeewuunya olw''ebyo bye yawulira) olwo nno ne tumuwa amawulire ag''essanyu ag''okuba nti ajja kuzaala Ishaka ate nga n’oluvanyuma lwa Ishaka wajja kuzaalibwayo Yakub (omuzzukulu).');
INSERT INTO "african_dev" VALUES(11,72,'11:72','(Omukyala Sarah bwe yamala okufuna amawulire g’okuzaala Ishaka) naagamba (nga bwe yewuunya) nti: gano nga mawano nze (Sarah) ngenda kuzaala omwana nga ndi mukadde bwe nti nga n''ono baze kinvinvi, mazima kino kintu ekye wuunyisa nnyo.');
INSERT INTO "african_dev" VALUES(11,73,'11:73','(Ba Malayika) ne bagamba nti: butya bwe weewuunya ekiragiro kya Katonda! okusaasira kwa Katonda n''emikisagye bibeere ku mmwe abantu benju (ya Ibrahim). Mazima Katonda atenderezebwa nnyo wa kitiibwa nnyo.');
INSERT INTO "african_dev" VALUES(11,74,'11:74','Ensisi bwe yamala okuggwa ku Ibrahim, ng’amaze n''okufuna amawulire ag''essanyu, yagenda mu maaso nga atukaayanya (nga awolereza) abantu ba luutu.');
INSERT INTO "african_dev" VALUES(11,75,'11:75','Mazima Ibrahim yali wa kisa nnyo ate nga asaba nnyo Katondawe era nga amwenenyeza ku buli nsonga.');
INSERT INTO "african_dev" VALUES(11,76,'11:76','(Ba Malayika nebamugamba nti) owange Ibrahim ebyo biveeko mazima kimaze okutuuka ekiragiro kya Mukama omulabiriziwo era mazima bo bagenda kujjirwa ebibonerezo ebitali bya kuzzibwayo.');
INSERT INTO "african_dev" VALUES(11,77,'11:77','Ababaka baffe bwe bajja ewa Luutu, tebaamusanyusa naawulira bubi (olw''okuba yali tasobola kubakuuma) era naagamba nti luno lunaku luzibu.');
INSERT INTO "african_dev" VALUES(11,78,'11:78','Abantube nebajja gyali nga banguwa naye nga okusooka baali bakola ebibi (Luutu) naagamba nti abange bantu bange bawala bange baabo be balungi gye muli, mutye Katonda temunswaza eri abagenyi bange, abaffe mu mmwe temuli musajja mulungamu!.');
INSERT INTO "african_dev" VALUES(11,79,'11:79','Nebagamba mazima okimanyi nti bawalabo tetubalinaako bwetaavu, era mazima ddala ggwe omanyi kye twagala.');
INSERT INTO "african_dev" VALUES(11,80,'11:80','(Luutu) naagamba nti (neegomba) singa mbadde nina amaanyi ku mmwe, oba empagi engumu gyensobola okwesigamako, (abantu abasobola okuntaasa).');
INSERT INTO "african_dev" VALUES(11,81,'11:81','(Ba Malayika) nebagamba nti owange Luutu ffe tuli babaka ba Mukama omulabiriziwo (abo) tebajja kukutuukako, tambula n''abantubo mu kitundu ky''ekiro, era takyuka omu ku mmwe (naatunula emabega, genda n''abantubo) okugyako Mukyalawo, anti mazima kiri nti ajja kutuukwako ebinaatuuka ku bali. Mazima ekiseera ekibalagaanyisiddwa bwe budde bw''oku makya, obudde bw''oku makya tebuli kumpi!.');
INSERT INTO "african_dev" VALUES(11,82,'11:82','(Ekitundu mwe baali babeera), ekiragiro kyaffe bwe kyajja, waggulu wa kyo wetwazza wansi era netukiwandagazaako amayinja amakole mu bbumba ejjokye nga gajjira kumukumu.');
INSERT INTO "african_dev" VALUES(11,83,'11:83','(Amayinja ago) nga malambe okuva ewa Mukama omulabiriziwo, ekibonerezo ng''ekyo tekiri wala ku buli abo bonna abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(11,84,'11:84','Ate abe Madiyana twabatumira Muganda waabwe Swaibu naagamba nti abange bantu bange musinze Katonda temulinaawo kirala kyonna kye mulina kusinza ekitali yye, temukendeezanga ebipimo ne minzani, mazima nze mbalaba nga musigala muli bulungi (bwe muba temukendeezezza mu bipimo ne minzani). Era mazima nze mbatiiriddeko ebibonerezo by''olunaku olugenda okwetooloola buli kimu.');
INSERT INTO "african_dev" VALUES(11,85,'11:85','Era abange bantu bange mutuukirize ebipimo ne minzani mu bwesimbu temukumpanyanga abantu ebintu bya bwe, era temukolanga ebibi mu nsi nga mugenderera okwonoona.');
INSERT INTO "african_dev" VALUES(11,86,'11:86','Ebilekeddwa Katonda (nga oggyeko byayogedde wa ggulu ebitaliimu kukumpanya bantu) bye birungi gye muli bwe muba nga muli bakkiriza. Era nze siri kalondoozi ku mmwe.');
INSERT INTO "african_dev" VALUES(11,87,'11:87','Nebagamba nti owange Swaibu okusaalakwo kwe kukulagira okuba nga tuleka bakadde baffe bye basinza, oba tukkirizibwa okukola mu mmaali yaffe bye twagala, anti ggwe oli alumirwa ow''amagezi.');
INSERT INTO "african_dev" VALUES(11,88,'11:88','Naagamba nti abange bantu bange mulaba nti singa mbadde ku bulungamu obwava ewa Mukama omulabirizi wange, nampa ne Riziki ennungi ate nnyinza okubaleka (nessibagambako) ate era saagala kubaawukanako nenzira ku ebyo bye mbagaana sirina kyengenderera kyonna okugyako okulongoosa nga bwemba nsobodde, era okulungama kwange tekuli okugyako ewa Katonda yye gwenneekwasiza era gyali gyengenda okudda.');
INSERT INTO "african_dev" VALUES(11,89,'11:89','Era abange bantu bange okwawukana kwange na mmwe tekubatwaririza nemutuukwako ebyo ebyatuuka ku bantu ba Nuhu oba abantu ba Hudu oba abantu ba Swaleh era abantu ba Luutu tebali wala na mmwe.');
INSERT INTO "african_dev" VALUES(11,90,'11:90','Era musabe Mukama omulabirizi wa mmwe ekisonyiwo olwo nno mwenenye okudda gyali mazima Mukama omulabirizi wange musaasizi mwagazi nnyo (wa bitondebye).');
INSERT INTO "african_dev" VALUES(11,91,'11:91','Nebagamba nti owange Swaibu bingi mu by''ogamba tetubitegeera ate era tukulaba ng''oli munafu muffe singa si kika kyo twandikukubye amayinja era ggwe tolina kitiibwa kyonna gye tuli.');
INSERT INTO "african_dev" VALUES(11,92,'11:92','Naagamba nti abange bantu bange ekika kyange kye ky''ekitiibwa gye muli okusinga Katonda ne mutuuka okumussa emabega wa mmwe nga mumukubye amabega mazima Mukama omulabirizi wange yeetoolodde byonna bye mukola.');
INSERT INTO "african_dev" VALUES(11,93,'11:93','Era abange bantu bange mukolere ku nkola ya mmwe nange kankole, ddala mujja kumanya ani, anajjirwa ebibonerezo ebimuswaza, era ani mulimba, naye era mulindirire mazima nze nindirira awamu na mmwe.');
INSERT INTO "african_dev" VALUES(11,94,'11:94','Ekiragiro kyaffe bwe kyamala okujja, ku lw''okusaasira kwaffe, twawonya Swaibu n''abo abakkiriza naye. Abo abeeyisa obubi nebatuukwako okubwatuka nebakeesa enkya nga mirambo egigangalamye mu mayumba ga bwe.');
INSERT INTO "african_dev" VALUES(11,95,'11:95','Nebaba nga abatagabeerangamu, abange abantu be Madiyana babeere wala nnyo n''okusaasira kwa Katonda nga abantu ba Thamud bwe beesamba okusaasira kwa Katonda.');
INSERT INTO "african_dev" VALUES(11,96,'11:96','Mazima twatuma Musa n''ebyamagero byaffe n''obujulizi obw''olwatu.');
INSERT INTO "african_dev" VALUES(11,97,'11:97','Agende ewa firawo n''eri abakungube, bo abakungu baagoberera ekigambo kya Firawo, naye nga ekigambo kya Firawo si kirungamu.');
INSERT INTO "african_dev" VALUES(11,98,'11:98','(Firawo) alikulembera abantube ku lunaku lw''enkomerero naabayingiza omuliro. Buyingiro bubi bwe baliyingira.');
INSERT INTO "african_dev" VALUES(11,99,'11:99','Ate nebagoberezebwa ekikolimo kuno ku nsi ne ku lunaku lw''enkomerero, ennyongeza mbi ne kye bongerako nakyo kibi.');
INSERT INTO "african_dev" VALUES(11,100,'11:100','Ebyo bye bimu ku bigambo ebikwata ku bitundu bye twazikiriza, tubikutegeeza ggwe (Muhammad), mu byo mulimu ebikyaliwo nga n''ebirala byasaanawo.');
INSERT INTO "african_dev" VALUES(11,101,'11:101','(Okubatuusaako ebyo) tetwabayisa bubi wabula beeyisa bokka obubi, ba Katonda baabwe abo be basaba ne bava ku Katonda omu tebaabagasa kintu kyonna, ekiragiro kya Mukama omulabiriziwo bwe kyajja, era tebaabongera okugyako okufaafaganirwa.');
INSERT INTO "african_dev" VALUES(11,102,'11:102','Bwe kutyo bwe kuba okukwata kwa Mukama omulabiriziwo. Bwakwata e bitundu nga biri mu mbeera ey''okweyisa obubi mazima okukwata kwe kuluma nnyo era kuyitirivu.');
INSERT INTO "african_dev" VALUES(11,103,'11:103','Mazima ekyo mulimu ekyokulabirako eri oyo atya ebibonerezo byo lunaku lw''enkomerero, olwo lwe lunaku ku lwalwo abantu bonna bagenda kukunganyizibwa era olwo olunaku abantu bonna baakulubaako.');
INSERT INTO "african_dev" VALUES(11,104,'11:104','Era tetululindiriza okugyako okutuuka ku kiseera kya lwo ekigere.');
INSERT INTO "african_dev" VALUES(11,105,'11:105','Olunaku bwe lulituuka teri muntu yenna agenda kwogera okugyako lwa kukkiriza kwa Katonda, nga mu bo mulibaamu abonoonefu n''abalongoofu.');
INSERT INTO "african_dev" VALUES(11,106,'11:106','Bo abo abaajeema baakuyingira muliro baliba balina mu gwo okusinda n''okukema.');
INSERT INTO "african_dev" VALUES(11,107,'11:107','Baakugubeeramu ebbanga eggulu (omusanvu) ne nsi bye birimala nga weebiri okugyako ekiseera Mukama omulabiriziwo kyaliba ayagadde mazima Mukama omulabiriziwo akola ekyo kyaba ayagadde.');
INSERT INTO "african_dev" VALUES(11,108,'11:108','Bo abo abaweebwa obulongoofu baakuyingira e jjana nga baakugibeeramu ebbanga eggulu omusanvu ne nsi bye birimala nga weebiri okugyako ebbanga Mukama omulabiriziwo lyaliba ayagadde nga okwo kuweebwa okutalibaako kutaataaganyizibwa.');
INSERT INTO "african_dev" VALUES(11,109,'11:109','Tobaamu kubuusabuusa mu ebyo bye basinza tebasinza okugyako nga ba Jjajaabwe bwe baasinzanga oluberyeberye,era mazima ffe tujja kubatuukiririza omugabo gwa bwe awatali kukendezaako.');
INSERT INTO "african_dev" VALUES(11,110,'11:110','Mazima twawa Musa ekitabo ne kyawukanwamu, singa si kigambo ekyava ewa Mukama omulabiriziwo ekyakulembera, eggoye lyandisaliddwawo (nebazikirira) era mazima bo bali mu kubuusabuusa era nga batankana (ku nsonga ya Kur’ani).');
INSERT INTO "african_dev" VALUES(11,111,'11:111','Na mazima ddala bonna Mukama omulabiriziwo agenda kubasasula emirimu gya bwe mu bujjuvu, mazima yye amanyidde ddala bye bakola.');
INSERT INTO "african_dev" VALUES(11,112,'11:112','Kale beera mwesimbu nga bwolagiddwa, n''abo abeenenyezza naawe era temubeera ba kiwagi, anti mazima ye (Katonda) bye mukola abiraba.');
INSERT INTO "african_dev" VALUES(11,113,'11:113','Era temuggweranga ku abo abeeyisa obubi, omuliro negubakwata ate nga ojjeeko Katonda temulinaayo bayambi balala, era temugenda kutaasibwa.');
INSERT INTO "african_dev" VALUES(11,114,'11:114','Era yimirizaawo e sswala ku nsalosalo ebbiri ez''emisana (ku makya n''akawungezi) n''ekiseera ekisooka ku kiro mazima ebirungi bijjawo ebibi. Okwo nno kubuulirira eri abo abajjukira.');
INSERT INTO "african_dev" VALUES(11,115,'11:115','Era gumiikiriza, bulijjo Katonda tayinza butasasula mpeera ya bakozi ba bulungi.');
INSERT INTO "african_dev" VALUES(11,116,'11:116','Singa waaliyo mu mirembe egyabakulembera abantu abalina kye baasigazaawo (mu ddiini) nga bagaana obwonoonefu mu nsi, (tebaaliyo) okugyako batono nnyo mu abo be twawonya mu bo. Wabula bo abeeyisa obubi ne batwalibwa ebirungi bye baaweebwa mu nsi nebaba aboonoonyi.');
INSERT INTO "african_dev" VALUES(11,117,'11:117','Mukama omulabiriziwo tabangako wa kuzikiriza ebitundu mu bulyazaamaanyi nga ate abantu baabyo bakozi ba bulungi.');
INSERT INTO "african_dev" VALUES(11,118,'11:118','Singa Mukama omulabiriziwo yayagala, abantu bonna yandibafudde ekibiina kimu, naye tebagenda kuva ku kwawukana.');
INSERT INTO "african_dev" VALUES(11,119,'11:119','Okugyako oyo Mukama omulabiriziwo gwaba asaasidde. N''olwekyo kyeyava abatonda, era kyakakata ekigambo kya Mukama omulabiriziwo nti ngenda kujjuza omuliro Jahannama nga nzija mu majinni n''abantu okutwalira awamu.');
INSERT INTO "african_dev" VALUES(11,120,'11:120','Era tukutegeeza buli kimu ekikwata ku babaka, ekyo kyetunyweza nakyo omutima gwo, era mu kino ojjiddwa amazima n''okubuulirira, era nga kya kujjukiza eri abakkiriza.');
INSERT INTO "african_dev" VALUES(11,121,'11:121','Era gamba abo abakkiriza nti mukolere ku nkola ya mmwe naffe katukole.');
INSERT INTO "african_dev" VALUES(11,122,'11:122','Era mulindirire naffe tulindiridde.');
INSERT INTO "african_dev" VALUES(11,123,'11:123','');
INSERT INTO "african_dev" VALUES(12,1,'12:1','Alif Laam Raa, bino bigambo by''ekitabo ekyeyolefu.');
INSERT INTO "african_dev" VALUES(12,2,'12:2','Mazima ffe twakissa (ekitabo) nga kyakusoma nga kiri mu luwarabu mube nga mutegeera.');
INSERT INTO "african_dev" VALUES(12,3,'12:3','Ffe tukutegeeza ebyafaayo ebisinga obulungi nga tukozesa obubaka bwe twakussaako (nga nabwo) ye Kur’ani eno, newaakubadde nga mu kusooka wali mu batamanyi.');
INSERT INTO "african_dev" VALUES(12,4,'12:4','Jjukira Yusuf bwe yagamba Kitaawe nti: owange Taata mazima nze nnaloose emmunyeenye kumi n''emu, n''omwezi, n''enjuba nabirabye nga binvunnamidde.');
INSERT INTO "african_dev" VALUES(12,5,'12:5','(Kitaawe Yakubu) naagamba nti owange Katabani kange, ekirooto kyo tokinyumiza bagandabo nebakukolera enkwe mazima, bulijjo Sitane ku muntu mulabe ow''olwatu.');
INSERT INTO "african_dev" VALUES(12,6,'12:6','Mu ngeri y''emu Mukama omulabiriziwo agenda ku kulonda era akuyigirize okuvvuunula endooto era ajjulize abantu ba Yakub ekyengera kye, nga mu kusooka bwe yakijjulizza bakadde bo bombi Ibrahim ne Ishaka. Mazima Mukama omulabiriziwo mumanyi nnyo era mugobansonga.');
INSERT INTO "african_dev" VALUES(12,7,'12:7','Mazima (mu kyafaayo kya) Yusuf ne bagandabe mulimu obujulizi eri ababuuza.');
INSERT INTO "african_dev" VALUES(12,8,'12:8','Jukira (baganda ba Yusuf) bwe baagamba nti kirabika Yusuf ne Mugandawe (bwe bazaalibwa mu nnyabwe) be baganzi ewa Kitaffe okusinga ffe ate nga tuli bangi, mazima Kitaffe ali mu bubuze obuyitirivu.');
INSERT INTO "african_dev" VALUES(12,9,'12:9','(Omu n''aleeta ekiteeso nti) Yusuf mumutte oba mumukasuke mu nsi (etamanyiddwa) olwo mwekomye okwagalwa Kitammwe, era oluvanyuma lw''ekyo musobola okubeera abantu abalongoofu.');
INSERT INTO "african_dev" VALUES(12,10,'12:10','Omwogezi omulala mu bo naagamba nti temutta Yusuf wabula mumusuule wansi mu luzzi abamu ku batambuze bajja kumulonda, bwe muba nga muteekwa buteekwa okubaako kye mukola.');
INSERT INTO "african_dev" VALUES(12,11,'12:11','(Ebyo nga biwedde) baagamba nti owange Kitaffe lwaki totwesiga ku Yusuf ate nga naffe tumufaako!.');
INSERT INTO "african_dev" VALUES(12,12,'12:12','Mutuwe tugende naye enkya, asanyuke, era azannye era mazima ffe tujja kumukuuma.');
INSERT INTO "african_dev" VALUES(12,13,'12:13','(Yakub) naagamba nti mazima nze kimpisa bubi okuba nga mumutwala, era ntya omusege okumulya nga mmwe mumulagajjalidde.');
INSERT INTO "african_dev" VALUES(12,14,'12:14','Ne bagamba nti singa omusege gumala ne gumulya nga ate tuli bangi mazima ffe olwo tubeera tufaafaaganiddwa.');
INSERT INTO "african_dev" VALUES(12,15,'12:15','Bwe baagenda naye era nebassa kimu okumuteeka mu kinnya wansi mu luzzi, netumutumira nti mazima ddala ogenda okubategeeza ekigambo kya bwe kino nga bo tebakyakifaako.');
INSERT INTO "african_dev" VALUES(12,16,'12:16','Olwo nebadda ewa Kitaabwe olw''eggulo nga bakaaba (naye nga kwali kwekaabya).');
INSERT INTO "african_dev" VALUES(12,17,'12:17','Nebagamba nti owange Kitaffe mazima ffe twagenze netusindana mu kudduka netuleka Yusuf awabadde ebintu byaffe omusege negumulya, tojja kutukkiriza newaakubadde nga twogera mazima.');
INSERT INTO "african_dev" VALUES(12,18,'12:18','Nebajja nga batadde ku kyambalo kye omusaayi ogw''obulimba (Yakub naagamba nti) wabula emyoyo gya mmwe gyatetenkanyizza negilungiya ekintu kino gye muli. Obugumiikiriza bulungi era Katonda ye Muyambi ku ebyo bye mwogera.');
INSERT INTO "african_dev" VALUES(12,19,'12:19','Abatambuze nebajja ne batuma abakimira amazzi nassa endobo ye olwajigyayo (wansi mu luzzi bwati naalaba omulenzi), naagamba nti “Neesiimye neefunidde omulenzi” ne bamukukusa nga bamufudde kya maguzi. Katonda mumanyi nnyo ku ebyo bye baali bakola.');
INSERT INTO "african_dev" VALUES(12,20,'12:20','Nebamutunda omuwendo omutono, Dirihamu mbale bubazi, anti baali tebamwetaaga.');
INSERT INTO "african_dev" VALUES(12,21,'12:21','Oyo eyamugula ow''emisiri naagamba mukyalawe nti mulabirire, muyise bulungi sinakindi ayinza okutugasa (mu maaso eyo) oba tumufuule omwana (Katonda kwe kugamba nti) bwe tutyo bwe twasobozesa Yusuf (A.S) okubeera mu nsi era tube nga tumuyigiriza okuvvuunula endooto. Era bulijjo Katonda ekigambokye kiwangula, wabula mazima abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(12,22,'12:22','Bwe yasajjakula twamuwa okusengeka ensonga n''okumanya, era bwe tutyo bwe tusasula abakozi b’obulungi.');
INSERT INTO "african_dev" VALUES(12,23,'12:23','(Ekyo nga kiwedde) ate muka nnyinimu oyo mu nju mweyasulanga naamwegwanyiza naggala enzigi, naamugamba nti “nzuno nkwewadde”. (Yusuf) naagamba nti: “nsaba obukuumi bwa Katonda”, ekituufu kiri nti Mukama wange (balo) ampisizza bulungi (siyinza kukikola) mazima anti abeeyisa obubi tebagenda kwesiima.');
INSERT INTO "african_dev" VALUES(12,24,'12:24','Mazima (omukyala) yamwegomba ne (Yusuf) namwegomba (omukyala), naye singa (Yusuf) teyalaba kabonero ka Mukama omulabiriziwe (yandigudde mu nsobi) twakola bwe tutyo tube nga tumuwugulako obwonoonefu n''obuwemu, anti mazima ye wa mu baddu baffe abasengejje.');
INSERT INTO "african_dev" VALUES(12,25,'12:25','Nebagobagana nga badda ku mulyango, (mu mbeera eyo) omukyala naayuza ekyambalo (kya Yusuf) emabega, nebasanga bba (w''omukyala) ku mulyango, omukyala naagamba nti kibonerezo ki ekisaanira okuweebwa omuntu ayagala okukola ku mukyalawo ekivve,okugyako okuba nga asibwa oba okumussaako e kibonerezo ekiruma?.');
INSERT INTO "african_dev" VALUES(12,26,'12:26','(Yusuf) naagamba nti ye yanneegwanyizza, omu ku bantu bo mu nyumba y''omukyala naawa obujulizi nti ekyambalokye (Yusuf) bwe kiba kiyuziddwa mu maaso gaakyo, omukyala yaanaaba ayogedde amazima ate yye (Yusuf (naaba ow''omu balimba.');
INSERT INTO "african_dev" VALUES(12,27,'12:27','Naye ekyambalokye (Yusuf) bwe kinaaba nga kiyuziddwa mabega (omukyala) anaaba alimbye, olwo nno ye naabeera mu ba mazima.');
INSERT INTO "african_dev" VALUES(12,28,'12:28','(Olwo bba w''omukyala) bwe yamala okulaba ekyambalokye nga kiyuziddwa mabega kwe kugamba nti “ezo nkwe zammwe (mwe abakyala) anti mazima bulijjo enkwe za mmwe za kabi nnyo.');
INSERT INTO "african_dev" VALUES(12,29,'12:29','Gwe (Yusuf) bino biveeko (tobinyega) ate ggwe weetonde olw''ekibikyo, mazima ddala ggwe obadde mu basobya.');
INSERT INTO "african_dev" VALUES(12,30,'12:30','Abakyala bo mu kibuga nebagamba nti Muka omukulu yeegwanyizza omuweerezaawe! mazima okwagala kumumazeemu, mazima ffe ye tumulaba nga ali mu bubuze obweyolefu.');
INSERT INTO "african_dev" VALUES(12,31,'12:31','Omukyala bwe yawulira e bigambo abakyala bye basaasaanya, yabatumira naabategekera ekifo aw''okutuula buli omu naamuwa akambe naagamba nti: (Yusuf) fuluma ogende gye bali, bwe baamulaba ne bamutendereza ne beesala engalo, era nebagamba nti: ono ssi muntu ono tali okugyako Malayika ow''ekitiibwa.');
INSERT INTO "african_dev" VALUES(12,32,'12:32','Omukyala naagamba nti oyo nno yooyo gwe mubadde munnenyeza. Yye, kituufu namwegwanyizza naagaana, wabula bwataakole ekyo kye mmulagira aggya kusibirwa ddala era abeere mu banyomebwa.');
INSERT INTO "african_dev" VALUES(12,33,'12:33','(Yusuf) naagamba nti: Ayi Mukama omulabirizi wange ekkomera lyenjagala okusinga ekyo kye bampitira, bwotamponye nkwe zaabwe nja kugwa mu mitego gya bwe, era mbe mu batategeera.');
INSERT INTO "african_dev" VALUES(12,34,'12:34','Awo nno Mukama omulabiriziwe naamwanukula naamuwugulako enkwe zaabwe anti mazima yye awulira nnyo mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(12,35,'12:35','(Bwe baamala okutunula mu bujulizi) kyabalabikira nga balina kumusiba okumala ekiseera.');
INSERT INTO "african_dev" VALUES(12,36,'12:36','Abavubuka babiri, baayingira naye ekkomera, (nga bali mu kkomera buli omu yalaba endooto) omu ku bo naagamba nti mazima nze naloose nga nsogola omubisi, omulala naagamba nti naloose nga neetisse ku mutwe gwange omugaati ng''ebinyonyi bigulyako, tubuulire bivvuunulwa bitya? Mazima ffe tukulaba nga oli mu balongoosa.');
INSERT INTO "african_dev" VALUES(12,37,'12:37','(Yusuf) naabagamba nti emmere gye muweebwa tejja kubatuukako okugyako nga mmaze okubavvuunulira (buli omu ekirootokye) nga tennaba kubatuukako, ekyo nno mmwe kye kimu ku bintu Mukama omulabirizi wange bye yanjigiriza, anti mazima nze nayawukana n''ekibiina ky''abantu abatakkiriza Katonda, nga era bo bawakanya olunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(12,38,'12:38','Era nengoberera eddiini ya bakadde bange Ibrahim ne Ishaka ne Yakub, teki kusaanirangako kugatta ku Katonda kintu kyonna. Byonna ebyo bye bimu ku birungi bya Katonda bye yatugabira era bye yagabira abantu abalala. Naye abantu abasinga obungi tebeebaza.');
INSERT INTO "african_dev" VALUES(12,39,'12:39','(Yusuf naabagamba nti) abange mmwe abasibe ababiri, ba katonda ab''enjawulo be balungi okusinga Katonda omu nantalemwa?.');
INSERT INTO "african_dev" VALUES(12,40,'12:40','Bye musinza ne muleka Katonda, tebirina kye biri okugyako okuba amannya obunnya ge mwatuuma mmwe ne bakadde ba mmwe. Katonda tabissangako bujulizi bwonna, ate nga okusalawo kwonna tekuli okugyako mu mikono gya Katonda (ate nga) yalagira mube nga temusinza okugyako ye yekka eyo nno y''eddiini ennambulukufu, wabula abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(12,41,'12:41','Abange mmwe abasibe ababiri, yye, omu ku mmwe ajja kubeera mu senero ewa Mukama we, ate yye, omulala ajja kukomererwa ku musaalaba, ebinyonyi bimulye ku mutwe, ekigambo kiwedde ekyo kye mubadde mubuuza.');
INSERT INTO "african_dev" VALUES(12,42,'12:42','(Yusuf) naagamba oyo gwe yalinamu essuubi ly''okuwona ku bombi nti: onjogererayo nga otuuse ewa Mukama wo (kabaka), wabula Sitane neemwerabiza okumwogererayo ewa Mukamaawe, olwo nno naatuula mu kkomera emyaka egiwera.');
INSERT INTO "african_dev" VALUES(12,43,'12:43','(Lumu) Kabaka naagamba nti mazima nze naloose nga ndaba ente musanvu engevvu nga ziriibwa emigogge gye nte musanvu, era nendoota ebirimba musanvu ebyakiragala n''ebikoola musanvu ngabikaze, abange mmwe abakungu munnyinnyonnyole endooto yange bwe muba nga mumanyi okuvvuunula amakulu g''ebirooto.');
INSERT INTO "african_dev" VALUES(12,44,'12:44','Nebagamba nti: okwo kulogojjana kwa birooto ate ekirala tetumanyi kuvvuunula birooto.');
INSERT INTO "african_dev" VALUES(12,45,'12:45','Omu ku basibe ababiri eyawona (e kkomera) naagamba era nga yajjukira wamaze kuyitawo kaseera nti nze njakubabuulira amakulu g''ekirooto ekyo, kale muntume.');
INSERT INTO "african_dev" VALUES(12,46,'12:46','(Bwe yatuuka mu kkomera) naagamba nti Yusuf owange ggwe omwogera mazima tubuulire amakulu g''ekirooto nga ente musanvu engevvu nga ziriibwa emigogge gy''ente musanvu, ebirimba musanvu ebyakiragala n''ebirala musanvu bikalu, olwo nno nzire eri abantu mbabuulire babe nga bamanya.');
INSERT INTO "african_dev" VALUES(12,47,'12:47','Yusuf naagamba nti mujja kulimira emyaka musanvu egiddiringanye, bye munaakungula mubireke mu birimba byabyo okugyako bitono ebyo bye munaalya.');
INSERT INTO "african_dev" VALUES(12,48,'12:48','Oluvanyuma lw''ekyo, wajja kujja emyaka musanvu egy''obuzibu egiggya okumalawo byonna bye mwagiterekera okugyako ebitono ennyo bye mulisalawo bisigalewo (nga nsigo).');
INSERT INTO "african_dev" VALUES(12,49,'12:49','Oluvanyuma lw''ebyo wagenda kujjawo omwaka abantu mwebalinywesebwa era mu mwaka ogwo balisobola okusogola ku mubisi.');
INSERT INTO "african_dev" VALUES(12,50,'12:50','Kabaka naagamba nti: mumundeetere, gwebaatuma bwe yamala okumutuukako (Yusuf) naagamba nti ddayo ewa Mukamaawo omubuuze ebikwata ku bakyala abo abeesala engalo, mazima Mukama omulabirizi wange amanyidde ddala ebikwata ku nkwe zaabwe.');
INSERT INTO "african_dev" VALUES(12,51,'12:51','(Kabaka) yagamba (abakyala abo nti) kiki ekyabatuusa kukwegwanyiza Yusuf, nebagamba nti amazima ga Katonda tetumumanyangako kibi kyonna, muka omukulu naagamba nti kaakati amazima geeyolese nze namwegwanyiza era mazima yye wa mu bantu aboogera amazima.');
INSERT INTO "african_dev" VALUES(12,52,'12:52','(Yusuf naagamba nti ekyo kyali bwekiti Kabaka) asobole okumanya nti nze sigezangako kumukumpanya nga taliiwo, bulijjo Katonda talungamya nkwe za bakumpanya.');
INSERT INTO "african_dev" VALUES(12,53,'12:53','Era sseetukuza mazima omwoyo (gw''omuntu) gulagira nnyo okwonoona, okugyako oyo Mukama omulabirizi wange gwaba asaasidde anti mazima Mukama omulabirizi wange musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(12,54,'12:54','Kabaka naagamba nti mumundeetere, mwekomye, bwe yamala okwogera naye (Kabaka) naagamba nti mazima ggwe okuva olwa leero oweereddwa obuvunaanyizibwa era oli mwesigwa.');
INSERT INTO "african_dev" VALUES(12,55,'12:55','(Yusuf) naagamba nti (bwe kiba bwe kityo) mpa obuyinza ku mawanika g’omunsi (eno Misiri) mazima nze nsobola obuwanika era omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(12,56,'12:56','Bwe tutyo nno twawa Yusuf obuvunaanyizibwa mu nsi (Misiri) nga adda eno n''eri mu yo nga bwayagala. Tussa okusaasira kwa ffe ku oyo gwe tuba twagadde era tetuyinza butasasula bakozi ba bulungi.');
INSERT INTO "african_dev" VALUES(12,57,'12:57','Empeera y''enkomerero y''esinga obulungi eri abo abakkiriza ne baba nga batya Katonda.');
INSERT INTO "african_dev" VALUES(12,58,'12:58','Awo nno baganda ba Yusuf bajja ne bayingira we yali era n''abamanya nga ate bo tebamutegeera.');
INSERT INTO "african_dev" VALUES(12,59,'12:59','Bwe yamala okubategekera bye banaatwala, naagamba nti lwe mulidda mundeeteranga muganda wa mmwe mu Kitammwe temulaba nti mazima nze ntuukiriza ebipimo era nze nsinga okuyisa obulungi abagenyi!.');
INSERT INTO "african_dev" VALUES(12,60,'12:60','Bwe mutalimundeetera temugenda kupimirwa mmere okuva gyendi era temunsembereranga.');
INSERT INTO "african_dev" VALUES(12,61,'12:61','Nebagamba nti tujja kusaba Kitaawe amutuwe era mazima ddala ffe tugenda kukikola.');
INSERT INTO "african_dev" VALUES(12,62,'12:62','(Yusuf) naagamba abavubuka be nti musse sente zaabwe mu migugu gya bwe banaakimanyira eyo nga batuuse kibenga kibasikiriza okudda.');
INSERT INTO "african_dev" VALUES(12,63,'12:63','Bwe baddayo ewa Kitaabwe, baagamba nti owange Kitaffe tugaaniddwa okupimirwa (emmere omulundi omulala bwe tutagenda ne Beniyamini) kale tukkirize tugende ne Muganda waffe tube nga tupimirwa era mazima ddala ffe tugenda kumukuuma.');
INSERT INTO "african_dev" VALUES(12,64,'12:64','Yakub naagamba nti abaffe munsuubira okubeesiga ku ye, okugyako nga bwennabeesiga ku mugandawe oluberyeberye (nemutatuukiriza), Katonda ye mukuumi asinga era yye, ye musaasizi asinga abasaasizi bonna.');
INSERT INTO "african_dev" VALUES(12,65,'12:65','Bwe baasumulula emigugu gya bwe (nga batuuse ewaabwe) ne bagwa ku sente zaabwe nga zibaddiziddwa ne bagamba nti owange Taata ate kiki ekirala kye twagala, sente zaffe ziizino zituddiziddwa, kye tulina okukola kuleetera bantu baffe mmere, era Muganda waffe tujja kumukuuma, olwo nno tuddeyo tweyongere ekipimo eky''etikkibwa engamiya, era ekyo ekipimo kyangu nnyo okufuna.');
INSERT INTO "african_dev" VALUES(12,66,'12:66','Yakub naagamba nti sijja kumubawa kugenda naye okutuusa nga mumpadde obweyamo eri Katonda nti mazima ddala mujja kumunkomezaawo, okugyako nga mwetooloddwa (ne mutasobola kumutaasa). Bwe baamala okumuwa obweyamo bwa bwe naagamba nti Katonda ye mweyimirize ku byonna bye twogera.');
INSERT INTO "african_dev" VALUES(12,67,'12:67','Era naagamba nti abange baana bange (bwe mutuukayo) temuyingirira mu mulyango gumu, wabula muyingirire mu milyango gya njawulo, (ngambye ntyo) wabula sisobola kubamalira kintu kyonna ewa Katonda anti okulamula kwonna tekulina gye kuli okugyako ewa Katonda, ye yekka gwe nneesize, anti abeesiga bonna ye yekka gwe bateekwa okwesiga.');
INSERT INTO "african_dev" VALUES(12,68,'12:68','Bwe baayingira nga Kitaabwe bwe yabalagira ekitaali kya kubagasa kintu kyonna ewa Katonda okugyako okutuukiriza ekyetaago ekyali mu mutima gwa Yakub, era mazima yye yamanyira ddala bye twamuyigiriza naye ddala abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(12,69,'12:69','Bwe baayingira awaali Yusuf yasembeza gyali Mugandawe naagamba nti: mazima ddala nze Mugandawo (eyabula) kale nno tonakuwala olw''ebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(12,70,'12:70','Bwe yamala okubategekera bye batwala yassa ekisena mu mugugu gwa Muganda we, olwo nno omulangirizi naalangirira nti, abange mmwe abatambuze abagenda mazima ddala mmwe muli babbi.');
INSERT INTO "african_dev" VALUES(12,71,'12:71','Nebagamba nga boolekedde bali (ababagamba eby''obubbi) kiki ekibabuzeeko.');
INSERT INTO "african_dev" VALUES(12,72,'12:72','Nebagamba nti ekisena kya Kabaka tetukiraba era oyo yenna anaakireeta ajja kuweebwa empeera ey''etikkibwa e ngamiya, era ekyo nze nkyeyimiridde.');
INSERT INTO "african_dev" VALUES(12,73,'12:73','Nebagamba nti tulayira Katonda mazima mukimanyi tetwajja kwonoona mu nsi, (eno) era tetubangako babbi.');
INSERT INTO "african_dev" VALUES(12,74,'12:74','Nebabagamba nti kibonerezo ki (ekimugwanira) bwe munaaba abalimba?.');
INSERT INTO "african_dev" VALUES(12,75,'12:75','Nebagamba nti ekibonerezokye, oyo yenna ekisena kya Kabaka gwe kinaasangibwa mu mugugugwe ye y''anaafuuka omutango gwa kyo, (ewaffe) bwe tutyo bwe tubonereza abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(12,76,'12:76','(Yusuf naayaza) nga atandikira ku migugu gya bwe nga tannatuuka ku mugugu gwa Mugandawe olwo naakijja mu mugugu gwa mugandawe, bwe tutyo bwe twayamba Yusuf naasalira bagandabe amagezi (anti) yali tayinza kusigaza mugandawe okusinziira ku nkola ya Kabaka (we Misiri) singa Katonda teyayagala, tusitula amadaala g''abo betuba twagadde era waggulu wa buli mumanyi waliyo omumanyi (asingako).');
INSERT INTO "african_dev" VALUES(12,77,'12:77','Nebagamba nti (Biniyamini) bwaba abbye Mugandawe yabba dda, Yusuf naakyesigaliza mu mutimagwe naatabaatulira, naagamba mmwe babi okusinga, era Katonda y''asinga okumanya ebyo bye mwogera.');
INSERT INTO "african_dev" VALUES(12,78,'12:78','Nebagamba nti owange ggwe Kamalabyonna mazima Biniyamini) aliko Kitaawe Musajja mukulu mukadde, kale mu kifo kye sigaza omu ku ffe, mazima tukutwala okuba nga oli mu bakozi b''obulungi.');
INSERT INTO "african_dev" VALUES(12,79,'12:79','(Yusuf) naagamba nti Katonda akisse wala okuba nga tukwata atali oyo gwe tukutte n''ebintu byaffe bwe kiba kityo olwo nno tuba balyazaamaanyi.');
INSERT INTO "african_dev" VALUES(12,80,'12:80','Bwe baamala okuba nga tebakyamulinamu ssuubi nebadda ebbali (begeyeemu), Mukulu waabwe naagamba nti temukimanyi nti Kitammwe yabaggyeko obweyamo eri Katonda ate nga oluberyeberye mulina engeri gye mutaayisaamu Yusuf bulungi, n''olwekyo sigenda kuva mu nsi eno okugyako nga Kitange anzikirizza oba Katonda nansalirawo. Era yye yaasinga abasalawo bonna.');
INSERT INTO "african_dev" VALUES(12,81,'12:81','Muddeeyo eri Kitammwe mumugambe nti: owange Kitaffe mazima Mutabaniwo yabba tetuwa bujulizi okugyako ku ekyo kye tumanyi, era tetubangako ba kalondoozi ku ebyo ebyekusifu.');
INSERT INTO "african_dev" VALUES(12,82,'12:82','Buuza abantu bo mu kitundu gye twali, obuuze n''ekibinja ky''abantu kye twaddiramu era mazima ddala ffe tuli b''amazima.');
INSERT INTO "african_dev" VALUES(12,83,'12:83','(Yakub) naagamba nti wabula emyoyo gya mmwe gyabayiiyirizza ekintu. okugumiikiriza kulungi, nina essuubi nti bonna Katonda agenda kubandeetera bonna. Mazima ddala y''amanyi ennyo era mugoba nsonga.');
INSERT INTO "african_dev" VALUES(12,84,'12:84','Awo nno naabaviira era naagamba nti: ndabye n''omwana wange Yusuf, amaasoge negatukula olw''obunakuwavu, era nga yye munyiikaavu.');
INSERT INTO "african_dev" VALUES(12,85,'12:85','Nebagamba nti amazima ga Katonda era okyajjukira Yusuf okutuusa lwokonzibye n''otuuka okubeera mu bagenda okufa!.');
INSERT INTO "african_dev" VALUES(12,86,'12:86','Naagamba nti mazima ebizibu byange ne nnaku yange mbiroopera Katonda era mmanyi okuva eri Katonda bye mutamanyi.');
INSERT INTO "african_dev" VALUES(12,87,'12:87','Abange baana bange mugende mugezeeko okunoonya ebifa ku Yusuf ne Mugandawe, temugwangamu ssuubi ku kusaasira kwa Katonda mazima tewali akutuka ssuubi na kususaasira kwa Katonda okugyako abantu abakaafiiri.');
INSERT INTO "african_dev" VALUES(12,88,'12:88','Bwe baayingira awaali Yusuf ne bagamba nti owange kamala byonna ekyeya kituli bubi ffe n''abantu baffe, ate bye twagala okuwaanyisaamu emmere bye tuleese ssi birungi, wabula ggwe tujjulize byotupimira era otusaddaakire (okkirize tugendere awo) mazima Katonda asasula abasaddaaka.');
INSERT INTO "african_dev" VALUES(12,89,'12:89','(Yusuf) naagamba nti: mumanyi ekyo kye mwakola Yusuf ne Mugandawe nga temumanyi (bigenda kuddirira).');
INSERT INTO "african_dev" VALUES(12,90,'12:90','Nebagamba nti mazima ddala ggwe Yusuf, naagamba nti nze Yusuf era ono Muganda wange, Katonda yatugonnomolako ebyengera era anti mazima omuntu atya Katonda era naagumiikiriza mazima Katonda tayinza butasasula bakozi ba bulungi.');
INSERT INTO "african_dev" VALUES(12,91,'12:91','Nebagamba nti tulayira Katonda nti mazima Katonda yakuwa enkizo ku ffe, era mazima mu byonna bye twakola twali basobya.');
INSERT INTO "african_dev" VALUES(12,92,'12:92','Naagamba nti olwa leero si lunaku lwa kubanenya, nsaba Katonda abasonyiwe era yye ye musaasizi asinga abasaazizi bonna.');
INSERT INTO "african_dev" VALUES(12,93,'12:93','Mutwale ekyambalo kyange kino mukisuule ku maaso ga Kitange ajja kuddamu alabe era mundeetere abantu ba mmwe bonna.');
INSERT INTO "african_dev" VALUES(12,94,'12:94','Ekibinja (kyabwe) bwe kyamala okuva (Emisiri) Kitaabwe naagamba nti mpulira akawoowo ka Yusuf singa si kutya nti munamputtawaza.');
INSERT INTO "african_dev" VALUES(12,95,'12:95','Nebagamba nti tulayira Katonda mazima ggwe oli mu bubuzeebwo obw''edda.');
INSERT INTO "african_dev" VALUES(12,96,'12:96','Eyaleeta amawulire ag''esssanyu bwe yatuuka, (ekyambalo) yakissa ku kyenyikye (ekya Yakub) awo wennyini naddamu okulaba. (Yakub) naagamba nti saabagamba nti mmanyi okuva ewa Katonda ebyo bye mutamanyi.');
INSERT INTO "african_dev" VALUES(12,97,'12:97','Nebagamba nti owange Kitaffe tusabire Katonda atusonyiwe ebyonoono byaffe mazima twali basobya.');
INSERT INTO "african_dev" VALUES(12,98,'12:98','Naagamba nti nja kubasabira Mukama omulabirizi wange abasonyiwe, mazima yye ye musonyiyi omusaasizi ennyo.');
INSERT INTO "african_dev" VALUES(12,99,'12:99','Bwe baayingira awaali Yusuf yasembeza bakaddebe gyali era naagamba nti muyingire Misiri mubeere mirembe Katonda bwanaaba ayagadde.');
INSERT INTO "african_dev" VALUES(12,100,'12:100','Era yasitula bakaddebe naabatuuza ku Ntebeye (Ey''obukulubwe) bonna ne bakutama ne bamuvunnamira era naagamba nti owange Kitange, gano ge makulu g’ekirooto kyange kye nnaloota edda, mazima Katonda wange akituukirizza, era mazima yankolera obulungi bwe yanzija mu kkomera ate na mmwe naabaleeta okubajja mu kyalo oluvanyuma lwa Sitane okuba nti yatabula wakati wange ne baganda bange. Mazima Mukama omulabirizi wange alumirwa oyo gwaba ayagadde anti mazima yye mumanyi nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(12,101,'12:101','Ayi Mukama omulabirizi wange mazima ompadde kyompadde mu bufuzi era nommanyisa okuvvuunula endooto, (ggwe) omutonzi w''eggulu omusanvu ne nsi, ggwe mukuumi wange ku nsi ne ku nkomerero. Nzita nga ndi musiraamu onteeke mu bakozi b''obulungi.');
INSERT INTO "african_dev" VALUES(12,102,'12:102','Ebyo nno bye bimu ku bigambo ebyekusifu bye tukutumira (ggwe Muhammad) era tewali nabo bwe bassa ekimu nga bakola olukwe.');
INSERT INTO "african_dev" VALUES(12,103,'12:103','Abantu abasinga obungi ne bwolulunkana otya tebayinza kubeera bakkiriza.');
INSERT INTO "african_dev" VALUES(12,104,'12:104','Sso ate tobasaba mpeera (ku kyobalungamya) tekiri (kyokola) okugyako okujjukiza eri ebitonde.');
INSERT INTO "african_dev" VALUES(12,105,'12:105','Obubonero bumeka mu ggulu omusanvu n''ensi bwe bayitako nga bbo tebabufaako.');
INSERT INTO "african_dev" VALUES(12,106,'12:106','Abasinga obungi mu bo tebakkiriza Katonda okugyako nga era bamugattako ebintu ebirala.');
INSERT INTO "african_dev" VALUES(12,107,'12:107','Abaffe baafuna obukakafu okuba nti tebayinza kujjirwa kibonerezo ky''amaanyi okuva ewa Katonda oba olunaku lw''enkomerero nerubatuukako kibwatukira nga nabo tebategedde.');
INSERT INTO "african_dev" VALUES(12,108,'12:108','Gamba nti: lino ly''ekkubo lyange nkowoola abantu okujja eri Katonda, nze n''abangoberera tukola ekyo nga tusinziira ku kulungamizibwa, era Mukama Katonda yasukkuluma, era nze siyinza kubeera mu bamugattako kintu kirala.');
INSERT INTO "african_dev" VALUES(12,109,'12:109','Tetwatumako oluberyeberye lwo okugyako basajja nga tubawa obubaka nga bantu ba mu bitundu ebyo (bye baatumwamu) abafffe tebatambula mu nsi ne balaba, yali etya enkomerero y''abo abaabasooka, e nyumba y''enkomerero y''esinga obulungi eri abo abatya Katonda, abaffe temutegeera!.');
INSERT INTO "african_dev" VALUES(12,110,'12:110','(Okutaasibwa kwa bantu tekwajjanga) okutuusa ababaka lwe baasemberanga okukutuka n''okusuubira era ne balowooza nti mazima bo baalimbisibwa (awo nno) okutaasa kwa ffe ne kubajjira, naawonyezebwa oyo gwe twayagala awonyezebwe, obukambwe bwaffe ku bantu aboonoonyi tebuwonyezebwa.');
INSERT INTO "african_dev" VALUES(12,111,'12:111','');
INSERT INTO "african_dev" VALUES(13,1,'13:1','Alif Laam Miim Raa, zino aya za kitabo (Kur’ani) ekyo ekyassibwa gyoli okuva ewa Mukama omulabiriziwo, ge mazima. Naye abantu abasinga obungi tebakkiriza.');
INSERT INTO "african_dev" VALUES(13,2,'13:2','Katonda yooyo eyasitula eggulu omusanvu awatali mpagi ze mulaba, oluvanyuma yatebenkera ku ntebe ye, era yagonza enjuba n''omwezi buli kimu kigenda kutambula okumala ebbanga eryakigererwa, atambuza buli kintu, annyonnyola ebigambo, kibasobozese okukkiriza nti mugenda kusisinkana Mukama omulabirizi wa mmwe.');
INSERT INTO "african_dev" VALUES(13,3,'13:3','Era yye yooyo eyagaziya ensi Nassa mu yo e nsozi n''emigga, ate nabuli bibala, yateeka mu yo emitendera ebiri (ekisajja n''ekikazi) obudde bw''ekiro abubikkisa emisana. Mazima ebyo birimu obubonero eri abantu abafumiitiriza.');
INSERT INTO "african_dev" VALUES(13,4,'13:4','Ate ne mu ttaka mulimu ebitundu ebiriraanye (naye ate tebifaanagana) era nga mulimu amalimiro ag''e Nzabibu era nga mulimu n''ebimera n''emitende ekikolo eky''emiti emingi n''ekikolo eky''omuti ogumu (nga byonna) binywekerezebwa ku mazzi ge gamu, era tusukkulumya ebimu ku binnaabyo mu kuwooma, mazima mu ebyo mulimu obubonero eri abantu abalina amagezi.');
INSERT INTO "african_dev" VALUES(13,5,'13:5','Bwoba oli wa kwewuunya, kyewuunyisa ekigambo kyabwe bwe bagamba nti: bwe tulimala okufuuka ettaka (nga tufudde) abaffe tuliddamu ne tutondebwa, abo nno beebo abaajeemera Mukama omulabirizi waabwe era abo enkoligo zigenda kubeera mu nsingo zaabwe era abo be bantu bo muliro bo baakutuula bugenderevu mu gwo.');
INSERT INTO "african_dev" VALUES(13,6,'13:6','Bakusaba batuusibweko ekibi mu bwangu mu kifo ky''okuba nga bandibadde bakusaba birungi, ate nga ebibonerezo bingi ebyaliwo oluberyeberye. era mazima Mukama Omulabiriziwo ajjudde okusonyiwa eri abantu ku kweyisa kwa bwe obubi, era ddala Mukama omulabiriziwo muyitirivu wa bibonerezo.');
INSERT INTO "african_dev" VALUES(13,7,'13:7','Era abo abaakaafuwala bagamba nti: singa aweebwa eky''amagero okuva ewa Mukama omulabiriziwe (wabula) mazima ddala ggwe oli mutiisa. Anti bulijjo buli bantu balina omulungamya waabwe.');
INSERT INTO "african_dev" VALUES(13,8,'13:8','Katonda amanyi buli kikazi ekyo kye kiba kirina mu lubuto lwa kyo, n''ekyo nnabaana kyayongera ne kyakendeeza, (ku bbanga olubuto lye lumala) era buli kintu kyonna ewa Mukama Katonda kikolera ku kigero (ekyakiweebwa).');
INSERT INTO "african_dev" VALUES(13,9,'13:9','Amanyi ebyekusifu n''ebirabwako, ow''ekitiibwa ennyo eyeesukulumya yekka.');
INSERT INTO "african_dev" VALUES(13,10,'13:10','Kye kimu (ewa Katonda) mu mmwe oyo aba akweese, ekigambo n''oyo akyatula, n''oyo akukuta ekiro, n''oyo assa ebibye mu musana.');
INSERT INTO "african_dev" VALUES(13,11,'13:11','(Buli muntu) aliko (ba Malayika) abakolera mu mpalo, buli kiseera babeera mu maasoge n''emabegawe nga bamukuuma nga ekyo kiragiro kya Katonda, mazima Katonda takyusa mbeera nnungi eri ku bantu okugyako nga bo bennyini be bakyusizza, Katonda bwaba ayagaliza obubi okutuuka ku bantu tewali ayinza kubuzzaayo, era tebayinza kufuna ng''oggyeko Katonda mukuumi mulala yenna.');
INSERT INTO "african_dev" VALUES(13,12,'13:12','Yye yooyo abalaga ekimyanso waggulu ne muba mu kutya ate nemuba nga muyaayaanira (enkuba), era yassaawo ebire ebizito ebitonnyesa enkuba.');
INSERT INTO "african_dev" VALUES(13,13,'13:13','Okubwatuka okuva waggulu kutendereza Katonda nga bwe kumusuuta, ba Malayika (nabo bamutendereza) olw''okumutya era asindika okubwatuka kwe ggulu naazikiriza nakwo oyo gwaba ayagadde okuzikiriza. (Awamu n''ekyo) bo abakaafiiri bagenda mu maaso nga bawakana ku bwomu bwa Katonda mazima yye Katonda amaanyige masuffu.');
INSERT INTO "african_dev" VALUES(13,14,'13:14','Okusaba okutuufu kwolekezebwa yye, bo abo abasaba atali yye tebasobola kubaanukula ku kintu kyonna, okugyako okuba nga omuntu agolodde ebibatu bye eri amazzi agaleete eri omumwa gwe naye nga tasobola kugatuuka. Era tekuli okusaba kwa bakaafiiri okugyako okubeera mu bubuze.');
INSERT INTO "african_dev" VALUES(13,15,'13:15','Ebiri mu ggulu omusanvu ne mu nsi bivunnamira Katonda mu kwagala oba mu kukakibwa nga n''ebisikirize bya byo ku makya n''olweggulo (bikola bityo).');
INSERT INTO "african_dev" VALUES(13,16,'13:16','Gamba (Ggwe Muhammad) nti ani Mukama omulabirizi w''eggulu omusanvu n''ensi bagambe nti: Katonda ate bagambe nti ate olwo mweteerawo mutya abakuumi, yye ne mumuleka, so nga ekirungi n''ekibi ebibatuukako tebiri mu mikono gya bwe, bagambe nti abaffe muzibe n''omuntu alaba benkana oba ebizikiza n''ekitangaala byenkana oba Katonda bamussaako ababeezi nga batonda nga okutondakwe nekiba nti okutonda (okw''emirundi ebiri) kwe kwabatabula, bagambe nti Katonda ye Mutonzi wa buli kintu, era yye y''oyo ali omu (obwannamunigina) nnantalemwa.');
INSERT INTO "african_dev" VALUES(13,17,'13:17','Atonnyesa enkuba okuva waggulu, amazzi negakulukutira mu nsenyi okusinziira ku bunene bwazo, Mukama naaleeta ebisejja nga bibimbye ate ekifaananyi ekirala ebintu bye bakumako omuliro olw''okwagala okufunamu eby''okwewunda oba eby''okukozesa, mu kukola ekyo namwo muvaamu ebisejja nga biri, bwa tyo Katonda bwa lambika amazima n''obwonoonefu wabula byo ebisejja bifuuka kasasiro, wabula kyo ekigasa abantu kisigala mu ttaka. Bwa tyo Katonda bwakuba ebifaananyi.');
INSERT INTO "african_dev" VALUES(13,18,'13:18','Abo abanaayanukula Mukama omulabirizi waabwe balina okufuna obulungi, naye abo abataamwanukule ebiri mu nsi yonna ne bwe byandibadde bya bwe nga balina n''ebirala ebibifaanana awamu nabiri bandyenunudde nabyo (ku lunaku lw''enkomerero), abonno balifuna okubalibwa okubi era omuliro Jahannama bwe buliba obuddo bwa bwe era bwe buliri obusinga obubi.');
INSERT INTO "african_dev" VALUES(13,19,'13:19','Abaffe oyo amanyi nti ebyassibwa ku ggwe okuva ewa Mukama omulabiriziwo ge mazima ayinza okuba nga oyo muzibe (atalaba mazima ago). Mazima aba magezi be bajjukira.');
INSERT INTO "african_dev" VALUES(13,20,'13:20','(Aba magezi) beebo abatuukiriza endagaano za Katonda nebatamenya bweyamo.');
INSERT INTO "african_dev" VALUES(13,21,'13:21','Era abo abayunga ekyo Katonda kyeyalagira okuyunga era ne batya ne babeera nga buli kiseera beeraliikirira Mukama omulabirizi waabwe, era ne batya okubalibwa okubi (okutaliimu kulekera kwonna).');
INSERT INTO "african_dev" VALUES(13,22,'13:22','Era abo abagumiikiriza ku lw''okwagala okubeera ku ludda lwa Mukama omulabirizi waabwe era nebayimirizaawo e sswala, era nebawaayo ku ebyo bye twabagabirira nga bakikola mu kyama ne mu lwatu era nga ekibi bakiggyisaawo ekirungi, abo nno balifuna ekifo ekisiimwa ku nkomerero.');
INSERT INTO "african_dev" VALUES(13,23,'13:23','(Ekifo ekyo) ze jjana ez''olubeerera zebaliyingira, nga baliba wamu n''abo abalongoosa mu bakadde babwe, ne be baali nabo mu bufumbo, ne zzadde lya bwe ba Malayika baliba bayingira gye bali, nga bayita mu buli mulyango.');
INSERT INTO "african_dev" VALUES(13,24,'13:24','(Balibagambanga) nti emirembe gibe ku mmwe olw''ebyo bye mwagumiikiriza, ekyengera ekisinga obulungi kye kifo ekyo ekirisiimwa ku nkomerero.');
INSERT INTO "african_dev" VALUES(13,25,'13:25','Ate abo abamenya endagaano za Katonda oluvanyuma lw''okuzikakasa, ne bakutula ekyo Katonda kye yalagira okuyunga era ne boonoona mu nsi abo baliko ekikolimo era bagenda kuweebwa e nyumba embi (ku lunaku lw''enkomerero).');
INSERT INTO "african_dev" VALUES(13,26,'13:26','Katonda agaziya riziki eri oyo gwaba ayagadde era agifundiza (gwaba ayagadde, bo abantu abalowoolereza mu by''ensi) badda mu kusanyuka n''obulamu obw''ensi so nga ate obulamu bw''ensi tebulina kye buli ku bulamu bw''enkomerero okugyako mpozzi lusanyuka lwa mulundi gumu.');
INSERT INTO "african_dev" VALUES(13,27,'13:27','Abo abaakaafuwala ne bagamba nti: singa assibwako (Nabbi Muhammad) eky''amagero okuva ewa Mukama omulabiriziwe. Bagambe nti mazima Katonda abuza oyo gwaba ayagadde naalungamya okudda gyali oyo yenna eyeemenya.');
INSERT INTO "african_dev" VALUES(13,28,'13:28','(Abeenenya) be bo abakkiriza Katonda, era nga emitima gya bwe gitebenkera olw''okwogera ku Katonda. Abange anti bulijjo emitima gitebenkera olw''okwogera ku Katonda.');
INSERT INTO "african_dev" VALUES(13,29,'13:29','Be bo abakkiriza ne bakola e mirimu emirongoofu, bagenda kufuna okwesiima n''obuddo obulungi.');
INSERT INTO "african_dev" VALUES(13,30,'13:30','Nabwekityo twakutuma ggwe (Nabbi Muhammad) mu bantu nga oluberyeberye lwa bwe waaliyo ebibiina bingi obe nga obasomera ekyo kye twassa gyoli, nga bali ku kuwakanya Katonda ow''ekisa ekingi, bagambe nti yye ye Mukama omulabirizi wange, tewali kisinzibwa (mu butuufu) okugyako yye, ye gwenneekwasizza era gyali gye njolekeza okwenenya kwange.');
INSERT INTO "african_dev" VALUES(13,31,'13:31','Singa mazima waliyo ekitabo ekisomwa nga ensozi zisobola okutambuzibwa nakyo, n''ensi neyeeyasaamu emiwaatwa (ne gitambuliramu amazzi), oba abafu ne boogezebwa nakyo (ekitabo ekyo kyandibadde Kur’ani eno, eby''amagero si bye birungamya) wabula obuyinza bwonna bwa Katonda, abaffe abo abakkiriza tebakutukanga na kusuubira nti singa Katonda yayagala yandirungamizza, abantu bonna, era abo abaakaafuwala tebagenda kuvaawo nga batuukwako obuzibu olw''ebyo bye baakola oba ekizibu okugwa okumpi n''amayumba ga bwe, okutuusa endagaano ya Katonda lwe lijja. Mazima Katonda tayawukana ku ndagaano.');
INSERT INTO "african_dev" VALUES(13,32,'13:32','Era mazima ababaka bangi abaagyejebwa oluberyeberyelwo, naye nennindiriza abo abaakaafuwala, oluvanyuma nnabakwata (ggwe) olaba okubonereza kwange kwali kutya.');
INSERT INTO "african_dev" VALUES(13,33,'13:33','Abaffe oyo alina obuyinza ku buli mwoyo olw''ebyo bye gukola (ayinza okufaanana nga oyo atali bwatyo). Abantu nebatuuka okugatta ku Katonda ebisinzibwa ebirala. Bagambe nti: muboogere (be baani) bwe kitaba ekyo muba nga abamutegeeza ebyo byatamanyi ku nsi, oba mumala gakozesa bigambo ku ngulu kwabyo (nga bwe mulaba), (wabula) ekituufu kiri nti abo abaakaafuwala enkwe zaabwe zaabalabikira okuba nga nnungi olwo nno ne baggyibwa ku kkubo. Oyo yenna Katonda gwaba abuzizza tayinza kubaako alungamya.');
INSERT INTO "african_dev" VALUES(13,34,'13:34','Balina ebibonerezo mu bulamu obw''ensi ate nga ebibonerezo by''enkomerero bye bisinga obukakali. Tebagenda kufuna mutaasa yenna ku Katonda.');
INSERT INTO "african_dev" VALUES(13,35,'13:35','Ekifaananyi kye jjana eyo eyalagaanyisibwa abatya Katonda (efaanana bweti) emigga gikulukutira wansi waayo, eby''okulya byamu bya lubeerera n''ekisiikirize kyamu bwe kityo, eyo y''enkomerero yaabo abatya Katonda so nga enkomerero ya bakaafiiri muliro.');
INSERT INTO "african_dev" VALUES(13,36,'13:36','N''abo be twawa ekitabo basanyuka olw''ekyo ekyassibwa gyoli ate mu bibiina byabwe (ebimu) mulimu abawakanya ebimu ku bye watumwa nabyo, bagambe nti nalagirwa okuba nga nsinza Katonda era mbe nga simugattako kintu kyonna, nkowoola abantu okudda gyali era gyali yeeri obuddo bwange.');
INSERT INTO "african_dev" VALUES(13,37,'13:37','Era bwe tutyo bwe twassa Kur’ani nga esalawo ku buli kintu nga eri mu luwarabu, singa ogoberera okwagala kwa bwe oluvanyuma lw''okumanya okukujjidde, tolina mukuumi wadde omutaasa ku Katonda.');
INSERT INTO "african_dev" VALUES(13,38,'13:38','Mazima twatuma ababaka bangi oluberyeberyelwo, netubawa abakyala n''ezadde, tewali Mubaka yenna yali asobola kuleeta kyamagero kyonna okugyako kulwa kukkiriza kwa Katonda. Buli ntuuko eba mpandiike ewa Katonda.');
INSERT INTO "african_dev" VALUES(13,39,'13:39','Katonda asangulawo kyaba ayagadde, ate ebirala naabireka nga bwe yabisalawo, ekitabo ekikulu (Llawuhul – Mahfuzu) ekiwandiikibwamu ebintu byonna yakyesigaliza.');
INSERT INTO "african_dev" VALUES(13,40,'13:40','Oba singa tukulaga ebimu ku ebyo bye tukulagaanyisa oba netukutuusa ku ntuukoyo (nga tobirabye, byonna kye kimu) ggwe kyoteekwa okukola kwe kubatuusaako obubaka, kwo okubala kuli ku ffe.');
INSERT INTO "african_dev" VALUES(13,41,'13:41','Abaffe tebalaba nti mazima ffe mpolampola tugenda tukendeeza ensi nga tutandikira gy''ekoma, kiri nti Katonda yasalawo tewali agobereza kigambo ku kusalawo kwe, era nga ye mwangu mu kubala.');
INSERT INTO "african_dev" VALUES(13,42,'13:42','Mazima abo abaabakulembera bakola enkwe naye nga enkwe zonna ziggwera eri Katonda, amanyi buli kyonna omuntu kyakola, abakaafiiri bagenda kumanya nti enkomerero ennungi eriba yaani.');
INSERT INTO "african_dev" VALUES(13,43,'13:43','Era abo abaakaafuwala bagamba nti toli mutume, bagambe nti Katonda amala okuba nga ye mujulizi wakati wange na mmwe, n''abo abalina okumanya nga kuva mu kitabo (Abayudaaya na Banaswara).');
INSERT INTO "african_dev" VALUES(14,1,'14:1','Alif Laam Raa, kino kitabo twakissa gyoli obe nga oggya abantu mu kizikiza obazze mu kitangaala, ku lw''okukkiriza kwa Mukama omulabirizi waabwe (obalungamye) ku kkubo lya nnantakubwa ku mukono atenderezebwa.');
INSERT INTO "african_dev" VALUES(14,2,'14:2','Katonda oyo nannyini wa byonna ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi. Era okubonaabona kuliba ku bakaafiiri olw''ebibonerezo ebikakali ebiribatuukako.');
INSERT INTO "african_dev" VALUES(14,3,'14:3','Abo abakulembeza obulamu bw''ensi ku bw''enkomerero, nga bagoba abantu okubaggya ku kkubo lya Katonda era nga balyagaliza okuba ekkyamu. Abo bali mu bubuze obweyolefu.');
INSERT INTO "african_dev" VALUES(14,4,'14:4','Tetutumanga Mubaka yenna okugyako mu lulimi lwa bantube, alyoke abe nga abannyonnyola (buli nsonga etegeerekeke) olwo nno Katonda abuze gwaba ayagadde era alungamye gwaba ayagadde, anti yye nga bulijjo ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(14,5,'14:5','Mazima twatuma Musa n''obubonero bwaffe (netumugamba) nti: Abantu bo baggye mu bizikiza obazze eri ekitangaala, era obajjukize ennaku za Katonda, (kwe yakolera) ebintu ebiteekwa okujjukirwa mazima mu ekyo mulimu obubonero (obulaga obuyinza bwa (Katonda) eri omugumiikiriza omwebaza owa nnamaddala.');
INSERT INTO "african_dev" VALUES(14,6,'14:6','Era jjukira Musa bwe yagamba abantube nti mujjukire ekyengera Katonda kye yabawa, bwe yabawonya abantu ba Firawo nga babakombya ku bibonyobonyo ebikakali, nga batirimbula abaana ba mmwe abalenzi nebalekawo abaana ba mmwe ab''obuwala, mu kukola ekyo mwalimu okugezesebwa kunene nnyo okuva eri Mukama omulabirizi wa mmwe.');
INSERT INTO "african_dev" VALUES(14,7,'14:7','Era mujjukire Mukama omulabirizi wa mmwe bwe yalangirira nti singa mwebaza nja kubongerera ddala naye singa mukaafuwala mazima ebibonerezo byange bikakali.');
INSERT INTO "african_dev" VALUES(14,8,'14:8','Musa naagamba nti singa mmwe mukaafuwala n''abantu bonna abali mu nsi, mazima Katonda talina kye yeetaaga eri muntu yenna era yye y''atenderezebwa.');
INSERT INTO "african_dev" VALUES(14,9,'14:9','Abaffe tegaabatuukako amawulire agakwata ku abo abaabakulembera (nga) abantu ba Nuhu, abantu ba A’di ne Thamud, n''abo abaaliwo oluvanyuma lwa bwe tewali abamanyi okugyako Katonda, ababaka baabwe baabajjira n''obunnyonnyofu, nebassa emikono gya bwe ku mimwa gya bwe, era nebabagamba nti mazima ffe tetukkirizza ebyo bye muleese, era mazima ffe tuli mu kubuusabuusa okulimu okutankana ku ebyo bye mutuyita okujja gye biri.');
INSERT INTO "african_dev" VALUES(14,10,'14:10','Ababaka baabwe nebagamba nti abaffe Katonda abuusibwa buusibwa (okuba nti ali omu) omutonzi we ggulu n''ensi, abakoowoola mujje abe nga abasonyiwa ebyonoono bya mmwe era abalindirize okutuusa ku kiseera ekigere. Nebagamba nti tewali kye muli wabula muli bantu nga ffe, mwagala kutuggya ku ebyo bakadde baffe bye baasinzanga, kale mutuleetere obujulizi obweyolefu.');
INSERT INTO "african_dev" VALUES(14,11,'14:11','Ababaka baabwe nebabagamba nti (ye wewaawo) tetuli kirala kyonna wabula tuli bantu nga mmwe, naye mazima Katonda ddala agabira oyo gwaba ayagadde mu baddube era tetusobola kubaleetera bujulizi okugyako, nga Katonda akkirizza. Katonda yekka, abakkiriza gwe bateekwa okwekwasa.');
INSERT INTO "african_dev" VALUES(14,12,'14:12','Tuyinza kwesigulaki netuteekwasa Katonda, nga ate yatulungamya mu makubo gaffe, era ddala tujja kugumiikiriza ku ngeri gye mutuyisaamu obubi, Katonda yekka abeesiga gwe bateekwa okwesiga.');
INSERT INTO "african_dev" VALUES(14,13,'14:13','Era abo abaakaafuwala nebagamba ababaka baabwe nti: tujja kubagoba mu nsi yaffe, oba ssi ekyo mujja kuddira ddala mu ddiini yaffe. Mukama omulabirizi waabwe naabatumira nti ddala tujja kuzikiriza abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(14,14,'14:14','Era ddala mmwe tujja kubatebenkeza mu nsi, oluvanyuma lwa bwe, ekyo kigenda kufunibwa oyo atya okuyimirira mu maaso gange naatya n''ebitiiso byange.');
INSERT INTO "african_dev" VALUES(14,15,'14:15','(Ababaka) nebasaba (Katonda) okubataasa, naawemuka buli mwekuza ow''amawaggali.');
INSERT INTO "african_dev" VALUES(14,16,'14:16','Oluvanyuma lw''ekyo (omwekuza oyo alindiriddwa) omuliro jahannama era agenda kunywesebwa amazzi ag''amasira ag''olusaayi saayi.');
INSERT INTO "african_dev" VALUES(14,17,'14:17','Agenda kugezaako okuganywa naye nga tagasobola, ate okufa nekumujjira nga kuva mu buli luuyi so nga ate tagenda kufa, nga ate oluvanyuma lw''ekyo alindiriddwa ebibonerezo ebizito.');
INSERT INTO "african_dev" VALUES(14,18,'14:18','Ekifaananyi ky''abo abawakanya Mukama omulabirizi waabwe, (kiri nti) emirimu gya bwe giringa vvu erikungusiddwa empewo ku lunaku olulimu kibuyaga nga mu bye baakola byonna tebalina kye balinako buyinza okwo nno kwe kubula okwewala.');
INSERT INTO "african_dev" VALUES(14,19,'14:19','Abaffe tolaba nti mazima Katonda eggulu omusanvu ne nsi yabitonda mu ngeri entuufu. Bwaba ayagadde abajjawo naaleeta ebitonde ebipya.');
INSERT INTO "african_dev" VALUES(14,20,'14:20','Era ekyo ku Mukama Katonda ssi kinene.');
INSERT INTO "african_dev" VALUES(14,21,'14:21','Bonna bagenda kweyoleka mu maaso ga Katonda. Abaali abanafu bagenda kugamba abo abeekuza mazima ffe twali bagoberezi ba mmwe abaffe mulina ekintu kye muyinza okutuyamba ku bibonerezo bya Katonda, nebagamba nti singa Katonda yatuluingamya twali balungamizza, kye kimu kuffe tutidde oba tugumiikirizza tetulina wa kuddukira.');
INSERT INTO "african_dev" VALUES(14,22,'14:22','Ekigambo nga kimaze okuggwa (mu ngeri eyo) Sitane aligamba nti mazima Katonda yabalagaanyisa endagaano ey''amazima nange nembalagaanyisa, naye nembaawukanako, era sibangako na buyinza bwonna ku mmwe okugyako okuba nti nnabayita nemunnyanukula. Kale nno temunnenya, mwenenye mwekka, nze siri wa kubataasa nga na mmwe bwe mutali ba kuntaasa, mazima nze neegaanye ebyo bye mwangattamu ne Katonda oluberyeberye. Mazima abeeyisa obubi balina ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(14,23,'14:23','Abo abakkiriza nebakola emirimu emirongoofu baliyingizibwa e jjana nga emigga gikulukutira wansi waazo, bagenda kuzibeeramu olubeerera olw''okukkiriza kwa Mukama omulabirizi waabwe, Salaamu (mirembe) kye kiriba ekiramuso kyabwe nga bali muzo.');
INSERT INTO "african_dev" VALUES(14,24,'14:24','Abaffe tolaba engeri Katonda gye yakubamu ekifaananyi ky''ekigambo ekirungi (Lla Ilaaha Illa Llahu Muhammad Rasuulu Llah), nga kifaanana omuti omulungi nga ekikolo kyagwo kinywevu nga amatabi g''agwo gali waggulu.');
INSERT INTO "african_dev" VALUES(14,25,'14:25','Guleeta eby''okulya by''agwo buli kiseera ku lw''okukkiriza kwa Mukama omulabirizi waagwo, era Katonda akubira abantu ebifaananyi kibasobozese okujjukira.');
INSERT INTO "african_dev" VALUES(14,26,'14:26','Ate ekifaananyi ky''ekigambo ekibi kiringa omuti omubi ogusindulwa (nga gwamerera) ku ngulu nga tegulina wegunywerera.');
INSERT INTO "african_dev" VALUES(14,27,'14:27','Katonda anyweza abo abakkiriza olw''ekigambo ekinywevu mu bulamu obw''ensi n''obwenkomerero (mu ngeri y''emu), era naabuza abeeyisa obubi. Bulijjo Katonda, akola ekyo kyaba ayagadde.');
INSERT INTO "african_dev" VALUES(14,28,'14:28','Abaffe tolaba abo ekyengera kya Katonda abaakiwaanyisaamu obukaafiiri (olw''ekyo), nebatwala abantu ba bwe mu nyumba ey''okuzikirira.');
INSERT INTO "african_dev" VALUES(14,29,'14:29','(Nga nayo) gwe muliro Jahannama, gwe bagenda okwesogga era obwo obuddo bubi.');
INSERT INTO "african_dev" VALUES(14,30,'14:30','Nebateeka ku Katonda ebisinzibwa ebirala babe nga babuza abantu okubaggya ku kkubolye, bagambe nti mweyagale, mazima ddala obuddo bwa mmwe buli mu muliro.');
INSERT INTO "african_dev" VALUES(14,31,'14:31','Gamba abaddu bange abo abakkiriza babe nga bayimirizaawo e sswala era baweeyo mu ebyo bye tubagabira (bakikole) mu kyama ne mu lwatu nga olunaku olutalibaamu bya maguzi wadde ab''emikwano telunnajja.');
INSERT INTO "african_dev" VALUES(14,32,'14:32','Katonda oyo eyatonda eggulu omusanvu n''ensi naatonnyesa okuva waggulu, amazzi n''ameza nago ebibala nga bya kulya bya mmwe, era yabagondeza amaato okuba nga gatambulira ku nyanja ku lw''obuyinzabwe era naabagondeza emigga.');
INSERT INTO "african_dev" VALUES(14,33,'14:33','Era naabagondeza enjuba n''omwezi nga byombi bitambula era naabagondeza ekiro n''omusana.');
INSERT INTO "african_dev" VALUES(14,34,'14:34','Era naabawa ekyo kyonna kye mumusaba, singa mugezaako okubala ebyengera bya Katonda temuyinza kubimalayo, anti mazima bulijjo omuntu yeeyisa bubi, yeewakana ebyengera.');
INSERT INTO "african_dev" VALUES(14,35,'14:35','Era Jjukira Ibrahim bwe yagamba nti Ayi Mukama omulabirizi wange ekibuga kino (Makkah) kifuule kya mirembe, onneekese n''abaana bange okuba nga tusinza amasanamu.');
INSERT INTO "african_dev" VALUES(14,36,'14:36','Ayi Mukama omulabirizi wange mazima go gabuzizza abantu bangi, oyo yenna angoberera mazima yye aba wa mu bantu bange, ate oyo yenna anjeemera, mazima ggwe oli musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(14,37,'14:37','Ayi Mukama omulabirizi waffe mazima nze nsenzezza abamu ku zzadde lyange mu lusenyi olutaliimu birime awali enyumba yo ey''emizizo, ayi Mukama omulabirizi waffe olwo babe, nga bayimirizaawo e sswala kale nno, fuula emitima gy''abantu egyagala okujja gye bali era obagabirire mu bibala basobole okwebaza.');
INSERT INTO "african_dev" VALUES(14,38,'14:38','Ayi Mukama omulabirizi waffe, mazima ggwe omanyi bye tukweka ne bye twolesa, ate nga tewali kintu kyonna kyekweka Katonda mu nsi wadde mu ggulu.');
INSERT INTO "african_dev" VALUES(14,39,'14:39','Ebitendo bya Katonda, oyo eyampa Ismail ne Ishaka mu bukadde. Mazima Mukama omulabirizi wange awulirira ddala okusaba.');
INSERT INTO "african_dev" VALUES(14,40,'14:40','Ayi Mukama omulabirizi wange nfuula ayimirizaawo e sswala, ne muzadde lyange (ojjemu abakola ekyo) ayi Mukama omulabirizi waffe nkusaba okkirize okusaba kwange.');
INSERT INTO "african_dev" VALUES(14,41,'14:41','Ayi Mukama omulabirizi waffe onsonyiwanga ne bakadde bange bombi, n''abakkiriza ku lunaku okubala lwe kuliyimirirawo.');
INSERT INTO "african_dev" VALUES(14,42,'14:42','Tosuubiranga Katonda okuba nti tafaayo ku ebyo abeeyisa obubi bye bakola, anti mazima abalindiriza okutuusa olunaku amaaso lwe galilalambala lwe lulituuka.');
INSERT INTO "african_dev" VALUES(14,43,'14:43','Baliba banguyirira nga balalambazza emitwe gya bwe, nga amaaso gaabwe tegadda gye bali (kutunula ku kintu kirala) nga n''emitima gya bwe myereere.');
INSERT INTO "african_dev" VALUES(14,44,'14:44','Era tiisa abantu olunaku ebibonerezo lwe biribajjira, abo abeeyisa obubi nebagamba nti ayi Mukama omulabirizi waffe, tulindirize okumala akaseera katono tusobole okwanukula okukoowoola kwo era tugoberere ababaka, (baligamba) abaffe temwalayira oluberyeberye nti temugenda kuva ku nsi.');
INSERT INTO "african_dev" VALUES(14,45,'14:45','Nemusenga mu bibanja byabo abeeyisa obubi, era nekibalabikira mu lwatu, ki kye twabakola. Era tubakubidde ebifaananyi (mube nga mumatira).');
INSERT INTO "african_dev" VALUES(14,46,'14:46','Mazima ddala bakola enkwe zaabwe naye nga enkwe zaabwe zonna ewa Katonda weziggwera, era ensozi tezibangako zaakuvaawo olw''enkwe zaabwe.');
INSERT INTO "african_dev" VALUES(14,47,'14:47','Tokirowoozanga nti Katonda wa kwawukana ku ndagaanoye gye yawa ababakabe (okubataasa). Mazima Katonda ye nantakubwa ku mukono alina obusobozi bw''okubonereza.');
INSERT INTO "african_dev" VALUES(14,48,'14:48','Ku lunaku ensi lwe likyusibwa neba nga tekyali nsi, nabwekityo eggulu omusanvu, (abantu bonna) ne beeyoleka ewa Katonda omu nantalemwa.');
INSERT INTO "african_dev" VALUES(14,49,'14:49','Oliraba aboonoonyi ku lunaku olwo nga bagattiddwa ku mpingu.');
INSERT INTO "african_dev" VALUES(14,50,'14:50','Engoye zaabwe ziriba zikoleddwa mu bikwata amangu omuliro, era omuliro gulibikka ebyenyi byaabwe.');
INSERT INTO "african_dev" VALUES(14,51,'14:51','Olwo nno Katonda abe nga asasula buli mwoyo ebyo bye gwakola, mazima Katonda mwangu mu kubala.');
INSERT INTO "african_dev" VALUES(14,52,'14:52','Kuno kutuusa (obubaka) eri abantu era babe nga balabulwa nabwo (ebyaziyizibwa), era babe nga bamanya nti mazima yye ali Katonda omu, era abalina amagezi beebuulirire.');
INSERT INTO "african_dev" VALUES(15,1,'15:1','Alif Laam Raa, zino ze zimu ku Aya z''ekitabo, era nga ye Kur’ani ennyinyonnyofu.');
INSERT INTO "african_dev" VALUES(15,2,'15:2','Lumu, abo abaakaafuwala balyegomba okuba nti singa baali basiraamu.');
INSERT INTO "african_dev" VALUES(15,3,'15:3','Baleke balye, era beeyagale era essuubi (mu kuwangaala) libalabankanye, kyaddaaki balimanya.');
INSERT INTO "african_dev" VALUES(15,4,'15:4','Tetwazikiriza kitundu kyonna okugyako nga kyalina ekiseera ekimanyiddwa.');
INSERT INTO "african_dev" VALUES(15,5,'15:5','Tewali bantu bakulembera ntuuko yaabwe era tebayinza kusaba kwongerwayo (okusaba kwa bwe ne kuyitamu).');
INSERT INTO "african_dev" VALUES(15,6,'15:6','Nebagamba (abakaafiiri) nti owange ggwe eyassibwako ekijjukiza (Kur’ani) mazima ddala ggwe oli mulalu.');
INSERT INTO "african_dev" VALUES(15,7,'15:7','Waakiri singa otuleetera ba Malayika bwoba nga oli mu boogera amazima.');
INSERT INTO "african_dev" VALUES(15,8,'15:8','(Katonda naagamba) nti: tetussa ba Malayika okugyako lwa nsonga n''olwekyo (singa twabaweereza ba Malayika) tebandibadde balindirizibwa.');
INSERT INTO "african_dev" VALUES(15,9,'15:9','Mazima ffe, ffe twassa ekijjukiza (Kur’ani) era mazima ffe tujja kugikuuma.');
INSERT INTO "african_dev" VALUES(15,10,'15:10','Mazima nga tonnajja twatumira ebibiina ebyakulembera.');
INSERT INTO "african_dev" VALUES(15,11,'15:11','Era tewali mubaka yenna eyabajjira okugyako baamujeejanga.');
INSERT INTO "african_dev" VALUES(15,12,'15:12','(Nga bwe twayingiza obukaafiiri mu mitima gya bakaafiiri abaakulembera) bwe tutyo bwe tubuyingiza mu mitima gy''aboonoonyi (abo mu bantubo).');
INSERT INTO "african_dev" VALUES(15,13,'15:13','(Obubaka bwo) tebabukkiriza ate nga ekyatuuka ku baabakulembera kimanyiddwa.');
INSERT INTO "african_dev" VALUES(15,14,'15:14','Era singa twabaggulirawo omulyango mu ggulu nebaba nga balinnyira mu gwo (nga bwe bakka).');
INSERT INTO "african_dev" VALUES(15,15,'15:15','Baaligambye nti mazima amaaso gaffe gatamiiziddwa si nakindi ffe tuli bantu abaloge.');
INSERT INTO "african_dev" VALUES(15,16,'15:16','Mazima twassa ku ggulu ebifo by''emmunyenye netulinyiriza eri abalabi.');
INSERT INTO "african_dev" VALUES(15,17,'15:17','Era netulikuuma ku buli Sitane eyagobwa mu kusaasira kwa Katonda.');
INSERT INTO "african_dev" VALUES(15,18,'15:18','Wabula oyo Sitane aba awulirizza mu bubba olwo nno ekitawuliro ekyeyolefu nekimufubutula emisinde.');
INSERT INTO "african_dev" VALUES(15,19,'15:19','N’ensi twagigaziya, netugisimbamu enkondo (ensozi), netumeza mu yo buli kintu nga kya kigero.');
INSERT INTO "african_dev" VALUES(15,20,'15:20','Netubateera (mmwe) mu yo ebiyimirizaawo obulamu bwa mmwe, n''ebiyimirizaawo obulamu bw''ebyo bye mutaliisa.');
INSERT INTO "african_dev" VALUES(15,21,'15:21','Tewali kintu kyonna okugyako nga ffe tulina amawanika g''akyo ate tetubissa okugyako nga kigero ekisaliddwawo.');
INSERT INTO "african_dev" VALUES(15,22,'15:22','Era tusindika empewo eyamba ebire okubaamu amazzi, olwo nno netussa enkuba okuva waggulu, netubanywekereza nayo mmwe, so nga si mmwe muba mugaterese.');
INSERT INTO "african_dev" VALUES(15,23,'15:23','Era mazima ffe tuwa obulamu, era netutta era ffe tugenda okusigalawo.');
INSERT INTO "african_dev" VALUES(15,24,'15:24','Era mazima ddala twamanya abalisooka mu mmwe (okufa) era mazima twamanya abalisembayo.');
INSERT INTO "african_dev" VALUES(15,25,'15:25','Era mazima Mukama omulabiriziwo yaali bakunganya (ku lunaku lw''enkomerero) anti mazima yye mugoba nsonga, muyitirivu wa kumanya.');
INSERT INTO "african_dev" VALUES(15,26,'15:26','Mazima twakola omuntu (Adam) mu bbumba ettabule nga liddugavu.');
INSERT INTO "african_dev" VALUES(15,27,'15:27','N''amajjini twagatonda oluberyeberye mu muliro omuyitirivu ogwokya ennyo.');
INSERT INTO "african_dev" VALUES(15,28,'15:28','Jjukira ggwe (Nabbi Muhammad) Mukama omulabiriziwo bwe yagamba ba Malayika nti: mazima nja kutonda omuntu nga muggya mu bbumba ekkaze.');
INSERT INTO "african_dev" VALUES(15,29,'15:29','Olwo nno bwe nnamala okumwenkanyankanya, nenfuuwa mu ye omwoyo oguva gyendi kale nno mugwe wansi nga mumuvunnamira.');
INSERT INTO "african_dev" VALUES(15,30,'15:30','Olwo ba Malayika bonna nebavunnama.');
INSERT INTO "african_dev" VALUES(15,31,'15:31','Okugyako Ibuliisu yagaana okubeera mu bavunnama.');
INSERT INTO "african_dev" VALUES(15,32,'15:32','(Katonda) kwe kugamba nti: owange ggwe Ibuliisu ki ekikugaanye okuvunnama n''abavunnami.');
INSERT INTO "african_dev" VALUES(15,33,'15:33','(Ibuliisu) nti: sandibadde avunnamira omuntu, gwotonze nga omuggya mu bbumba ekkaze eriddugavu.');
INSERT INTO "african_dev" VALUES(15,34,'15:34','(Katonda) kwe kumugamba nti gifulume (e jjana) anti ggwe ogobeddwa okuva mu kusaasira kwa Katonda.');
INSERT INTO "african_dev" VALUES(15,35,'15:35','Era mazima ekikolimo kibeere ku ggwe okutuusiza ddala olunaku lw''okubalibwa.');
INSERT INTO "african_dev" VALUES(15,36,'15:36','(Ibuliisu) naagamba nti: Ayi Mukama Omulabirizi wange, nnindiriza okutuusa ku lunaku lw''okusasulwa.');
INSERT INTO "african_dev" VALUES(15,37,'15:37','(Katonda) naagamba nti kale oli mubalindiriziddwa.');
INSERT INTO "african_dev" VALUES(15,38,'15:38','Okutuusa ku lunaku lw''ekiseera ekimanyiddwa (ebitonde byonna lwe birizikirira).');
INSERT INTO "african_dev" VALUES(15,39,'15:39','(Ibuliisu) naagamba nti: ayi Mukama omulabirizi wange nga bwombuzizza, mazima nja kubawundira kuno ku nsi (obujeemu n''ebibi bibe nga by''ebirungi mu maaso gaabwe) era nja kubabuliza ddala bonna.');
INSERT INTO "african_dev" VALUES(15,40,'15:40','Okugyako abaddubo mu bo abalondobemu.');
INSERT INTO "african_dev" VALUES(15,41,'15:41','(Katonda) naagamba nti lino kkubo ggolokofu.');
INSERT INTO "african_dev" VALUES(15,42,'15:42','Mazima abaddu bange tobalinaako buyinza, mpozzi abo abakugoberera mu babuze.');
INSERT INTO "african_dev" VALUES(15,43,'15:43','Era mazima omuliro Jahannama gwabalagaanyisibwa bonna.');
INSERT INTO "african_dev" VALUES(15,44,'15:44','Gulina emiryango musanvu, buli mulyango gulina mu bo omuteeko ogwagugerekerwa.');
INSERT INTO "african_dev" VALUES(15,45,'15:45','Mazima abatya Katonda balibeera mu jjana n''ensulo nga zikulukuta.');
INSERT INTO "african_dev" VALUES(15,46,'15:46','(Baligambibwa) nti mugiyingire nga muli mirembe nga temulina kutya kwonna.');
INSERT INTO "african_dev" VALUES(15,47,'15:47','Netujjawo buli kakuku akali mu bifuba bya bwe nebaba ab''oluganda. Olwo nno nga bali ku bitanda nga batunuuliganye (banyumya).');
INSERT INTO "african_dev" VALUES(15,48,'15:48','Tebagenda kutuukibwako kukoowa era nga bwe batagenda kufulumizibwamu.');
INSERT INTO "african_dev" VALUES(15,49,'15:49','Tegeeza abaddu bange nti nze musonyiyi ennyo omusaasizi.');
INSERT INTO "african_dev" VALUES(15,50,'15:50','Era nti mazima ebibonerezo byange by''ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(15,51,'15:51','Era bategeeze ebikwata ku bagenyi ba Ibrahim.');
INSERT INTO "african_dev" VALUES(15,52,'15:52','Mu kiseera bwe baayingira we yali nebagamba nti mirembe (Salaamu) naagamba (Ibrahim) mazima ffe tubatidde.');
INSERT INTO "african_dev" VALUES(15,53,'15:53','Nebagamba nti totya, mazima ffe tukuwa amawulire ag''essanyu (ag''okuzaala) omwana omulenzi omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(15,54,'15:54','(Ibrahim) naagamba nti: mumpa amawulire ag''essanyu nga n''obukadde bumaze okunzingiza olwo mawulire ga ssanyu nnabaki?.');
INSERT INTO "african_dev" VALUES(15,55,'15:55','Nebagamba nti, tukuwadde amawulire ag''essanyu mu bwesimbu, tobeeranga mu bakutuka n''okusuubira.');
INSERT INTO "african_dev" VALUES(15,56,'15:56','Naagamba nti nga ani akutuka n''okusaasira kwa Mukama omulabiriziwe mpozzi ababuze!.');
INSERT INTO "african_dev" VALUES(15,57,'15:57','Naagamba nti kiki ekibaleese abange mmwe abatumiddwa?.');
INSERT INTO "african_dev" VALUES(15,58,'15:58','Nebagamba nti: mazima ffe tusindikiddwa eri abantu aboonoonyi.');
INSERT INTO "african_dev" VALUES(15,59,'15:59','Ng''oggyeko abenju ya Luutu mazima ddala ffe bonna tujja kubawonya.');
INSERT INTO "african_dev" VALUES(15,60,'15:60','Okugyako mukyalawe, twakisalawo nti mazima yye waakubeera mu abo abataligenda (naye).');
INSERT INTO "african_dev" VALUES(15,61,'15:61','Ababaka bwe baatuuka awali abantu ba (Nabbi) Luutu.');
INSERT INTO "african_dev" VALUES(15,62,'15:62','(Luutu) naagamba nti mazima mmwe muli abantu abatamanyidddwa.');
INSERT INTO "african_dev" VALUES(15,63,'15:63','Nebagamba nti: ssi ekyo kyokka wabula tukujjidde n''ekyo kye baali babuusabuusa.');
INSERT INTO "african_dev" VALUES(15,64,'15:64','Era tukuleetedde amazima, era ddala ffe bye twogera bituufu.');
INSERT INTO "african_dev" VALUES(15,65,'15:65','Kale tambula n''abantubo mu kitundu ky''ekiro era (ggwe) baveeko emabega tewaba omu ku mmwe akyuka, era mugende eyo gye mulagiddwa.');
INSERT INTO "african_dev" VALUES(15,66,'15:66','Netumuwa ekiragiro nti abasembayo ku bo baakuggwawo nga busaasaana.');
INSERT INTO "african_dev" VALUES(15,67,'15:67','Abantu bo mu kibuga nebajja (ewa Luutu) nga bafa essanyu (olw''abagenyi abavubuka abaali bakyaddeyo).');
INSERT INTO "african_dev" VALUES(15,68,'15:68','(Luutu) naagamba nti mazima abo bemulaba bagenyi bange temunsonyiwaza.');
INSERT INTO "african_dev" VALUES(15,69,'15:69','Era mutye Katonda temunswaza.');
INSERT INTO "african_dev" VALUES(15,70,'15:70','Nebagamba nti: abaffe tetwakugaana (okuleeta wano) abantu!.');
INSERT INTO "african_dev" VALUES(15,71,'15:71','Naabagamba nti bawala bange baabo bwe muba nga mulina kukikola.');
INSERT INTO "african_dev" VALUES(15,72,'15:72','(Katonda agamba nti) ndayidde obulamu bwo (ggwe Muhammad) mazima bo mu kukola ebyo byonna, baali babuyaanira mu butamiivu bwabwe.');
INSERT INTO "african_dev" VALUES(15,73,'15:73','Okubwatuka (okwamaanyi) nekubatuukako nga enjuba evaayo.');
INSERT INTO "african_dev" VALUES(15,74,'15:74','(Netuvuunika ebyalo byabwe) netuzza waggulu waabyo wansi, netubafukirira amayinja ag''o mu bbumba eryokye.');
INSERT INTO "african_dev" VALUES(15,75,'15:75','Mazima mu ebyo mulimu obubonero eri abo abalondoozi b''obubonero.');
INSERT INTO "african_dev" VALUES(15,76,'15:76','Era mazima ebyalo ebyo (ebyazikirizibwa) biri ku kkubo erikyaliwo (agendayo asobola okubiraba).');
INSERT INTO "african_dev" VALUES(15,77,'15:77','Mazima mu ebyo mulimu akabonero (akalaga obusobozi bwa Katonda) eri abakkiriza.');
INSERT INTO "african_dev" VALUES(15,78,'15:78','Era mazima n''abantu b''omukibira baali beeyisa bubi.');
INSERT INTO "african_dev" VALUES(15,79,'15:79','Netubazikiriza era mazima abantu ab''emirundi ebiri (aba Luutu ne Swaibu) baali ku kkubo erimanyiddwa (buli ayitawo alabawo).');
INSERT INTO "african_dev" VALUES(15,80,'15:80','Mazima abantu be Hijiri baalimbisa ababaka (bwe baalimbisa Nabbi Swaleh).');
INSERT INTO "african_dev" VALUES(15,81,'15:81','Era netubaleetera obubonero bwaffe naye nebabwawukanako.');
INSERT INTO "african_dev" VALUES(15,82,'15:82','Baasimanga ensozi, nebakolamu amayumba mwe baawangaaliranga mu ddembe.');
INSERT INTO "african_dev" VALUES(15,83,'15:83','Okubwatuka nekubakwata nga bukya.');
INSERT INTO "african_dev" VALUES(15,84,'15:84','Ebyo bye baakolanga tebyabayamba.');
INSERT INTO "african_dev" VALUES(15,85,'15:85','Era tetwatonda eggulu omusanvu n''ensi n''ebyo ebiri wakati wa byombi okugyako ku lwakigendererwa, era nti mazima essaawa esembayo ejja kutuuka, kale nno lekera olulekera olulungi.');
INSERT INTO "african_dev" VALUES(15,86,'15:86','Mazima Mukama omulabiriziwo yye ye mutonzi amanyi ennyo.');
INSERT INTO "african_dev" VALUES(15,87,'15:87','Era mazima twakuwa ayaa omusanvu eziddinganwa (Surat Al Fatiha) ne Kur’ani ey''ekitiibwa (yonna).');
INSERT INTO "african_dev" VALUES(15,88,'15:88','Tokanulanga amaaso go (nga otunuulira nga weegomba) eri ebyo bye twasanyusa nabyo emitindo egimu ku bo, era tebakunakuwazanga era wekkakkanye eri abakkiriza.');
INSERT INTO "african_dev" VALUES(15,89,'15:89','Era bagambe nti mazima nze ndi mutiisa ow''olwatu.');
INSERT INTO "african_dev" VALUES(15,90,'15:90','Nga bwe twassa ku abo abagabanya (mu bubaka bwa Katonda).');
INSERT INTO "african_dev" VALUES(15,91,'15:91','Abo abafuula Kur’ani ebiwayi.');
INSERT INTO "african_dev" VALUES(15,92,'15:92','Ndayira Mukama omulabiriziwo nti mazima bonna tugenda kubabuuliza ddala.');
INSERT INTO "african_dev" VALUES(15,93,'15:93','Ku ebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(15,94,'15:94','Kale yogera mu lwatu ekyo ekikulagirwa, era oyawukane ku bagatta ku Katonda ebintu ebirala.');
INSERT INTO "african_dev" VALUES(15,95,'15:95','Mazima ffe twakumalira abajeeja.');
INSERT INTO "african_dev" VALUES(15,96,'15:96','Abo abassa ku Katonda ba katonda abalala, nebwebuliba ddi balimanya.');
INSERT INTO "african_dev" VALUES(15,97,'15:97','Tumanyidde ddala nti mazima ggwe ekifuba kyo kifunda olw''ebyo bye boogera.');
INSERT INTO "african_dev" VALUES(15,98,'15:98','(Ggwe kyoba okola) tendereza ebitendo bya Mukama omulabiriziwo era obeere mu bavunnama.');
INSERT INTO "african_dev" VALUES(15,99,'15:99','Era sinza Mukama omulabiriziwo okutuusa obukakafu (okufa) lwe bulikujjira.');
INSERT INTO "african_dev" VALUES(16,1,'16:1','Ekiragiro kya Katonda kisembedde n''olwekyo temusaba kwanguwa kwakyo, (Katonda) yasukkuluma neyeesamba ku ebyo bye bamugattako.');
INSERT INTO "african_dev" VALUES(16,2,'16:2','Assa ba Malayika nga baleeta obubaka ku lw''ekiragirokye ku oyo gwaba ayagadde mu baddube, mutiise (abantu nga mubagamba nti), mazima tewali kisinzibwa kyonna (mu butuufu) okugyako nze kale nno muntye.');
INSERT INTO "african_dev" VALUES(16,3,'16:3','Yatonda eggulu omusanvu n''ensi lwa nsonga, musukkulumu nnyo ku ebyo bye bamugattako.');
INSERT INTO "african_dev" VALUES(16,4,'16:4','Yatonda omuntu nga amuggya mu mazzi agazaala, (bwakula) naafuuka omukaayanye ow''olwatu.');
INSERT INTO "african_dev" VALUES(16,5,'16:5','N’ebisolo ebirundibwa yabibatondera mufune mu byo okubuguma, n''emigaso (emirala) nga era kwe mulya.');
INSERT INTO "african_dev" VALUES(16,6,'16:6','Era mulina mu byo okunyumirwa mu kiseera wemubiggyirayo ne mu kiseera wemubitwalirayo (ku ttale).');
INSERT INTO "african_dev" VALUES(16,7,'16:7','(Nga ebisolo ebimu) byetikka emigugu gya mmwe okugitwala eri ekitundu gye mutandituuse okugyako mu buzibu obw''ekitalo, mazima bulijjo Mukama omulabirizi wa mmwe alumirwa nnyo wa kisa.');
INSERT INTO "african_dev" VALUES(16,8,'16:8','Era (yatonda) enfalaasi, n''e nyumbu n''endogoyi mube nga mubyebagala era nga bya kubasanyusa, era atonda ne bye mutamanyi.');
INSERT INTO "african_dev" VALUES(16,9,'16:9','Katonda y''alaga ekkubo ettuufu, ate amakubo agamu makyamu, singa yali ayagadde mwenna yandibalungamizza.');
INSERT INTO "african_dev" VALUES(16,10,'16:10','Yye yooyo abatonnyeseza enkuba okuva waggulu, ku nkuba eyo kwe munywa, era ku nkuba eyo emiti kwe gimerera nga ku gyo kwe muliisa ensolo za mmwe.');
INSERT INTO "african_dev" VALUES(16,11,'16:11','Abamereza nayo ebirimwa ne Zaituni n''emitende ne nzabibu nabuli bibala byonna, mazima mu ekyo mulimu akabonero (akalaga obuyinza) bwa Katonda eri abantu abafumiitiriza.');
INSERT INTO "african_dev" VALUES(16,12,'16:12','Era yabagondeza ekiro n''emisana era enjuba n''omwezi n''emmunyeenye byonna byagonzebwa (bitambulira) ku kiragiro kye. Mazima mu ebyo byonna mulimu obubonero eri abantu abategeera.');
INSERT INTO "african_dev" VALUES(16,13,'16:13','N’ebyo bye yabateera mu nsi nga amabala g''abyo tegafaanagana, mazima mu ekyo mulimu akabonero eri abantu abejjukanya.');
INSERT INTO "african_dev" VALUES(16,14,'16:14','Era yye, yooyo eyagonza enyanja mube nga mulya muyo ennyama (ebyenyanja) ebibisi era muggye mu yo eby''okwewunda bye mwambala. Ogenda noolaba amaato nga gakola ekkubo muyo, olwo nno musobole okugenda okunoonya ebigabwabye (Katonda), era kibasobozese okwebaza.');
INSERT INTO "african_dev" VALUES(16,15,'16:15','Era yassa mu nsi enkondo (ensozi) ereme kubayuuza, era nassaamu emigga, n''amakubo musobole okulungama.');
INSERT INTO "african_dev" VALUES(16,16,'16:16','Era naateeka (mu nsi) obubonero, okwo nno bwogattako emmunyeenye basobole okumanya gye balaga.');
INSERT INTO "african_dev" VALUES(16,17,'16:17','Abange oyo atonda ayinza okuba nga atatonda! abaffe temujjukira.');
INSERT INTO "african_dev" VALUES(16,18,'16:18','Ne bwe mubala ebyengera bya Katonda temusobola kubikomekkereza, mazima Katonda musonyiyi nnyo musaasizi.');
INSERT INTO "african_dev" VALUES(16,19,'16:19','Era Katonda amanyi bye mukola mu kyama ne bye mukola mu lwatu.');
INSERT INTO "african_dev" VALUES(16,20,'16:20','Era abo bebasaba nebaleka Katonda tebatonda kintu kyonna ate nga bo batondebwa butondebwa.');
INSERT INTO "african_dev" VALUES(16,21,'16:21','(Bebasaba) bafu ssi balamu era tebamanyi ddi lwe balizuukizibwa.');
INSERT INTO "african_dev" VALUES(16,22,'16:22','Katonda wa mmwe ali Katonda omu, abo abatakkiriza lunaku lwa nkomerero emitima gyabwe gikiwakanya era nga bo beekuza.');
INSERT INTO "african_dev" VALUES(16,23,'16:23','Tewali kubuusabuusa mazima Katonda amanyi bye bakweka ne bye bakola mu lwatu, era mazima yye tayagala beekuza.');
INSERT INTO "african_dev" VALUES(16,24,'16:24','Bwe baba bagambiddwa nti Mukama omulabirizi wa mmwe kiki kyassizza bagamba nti nfumo za baasooka.');
INSERT INTO "african_dev" VALUES(16,25,'16:25','Olwo nno balyoke beetikke ebibi bya bwe mu bujjuvu ku lunaku lw''enkomerero, era beetikke ne ku bibi bya abo be baabuza awatali kumanya, abange kibi kye beetikka.');
INSERT INTO "african_dev" VALUES(16,26,'16:26','Ab’abakulembera baakola enkwe, Katonda naasitula ebizimbe byabwe nga asinziira ku musingi akasolya nekabagwira waggulu waabwe ebibonerezo nebibajjira okuva webatasuubira.');
INSERT INTO "african_dev" VALUES(16,27,'16:27','Oluvanyuma ku lunaku lw''enkomerero agenda kubaswaza, era agambe nti baliwa bannange abo bemwayawukanangamu, baligamba abo abaaweebwa okumanya nti mazima obuswavu ne nnaku olwa leero bigenda kubeera ku bakaafiiri.');
INSERT INTO "african_dev" VALUES(16,28,'16:28','Abo ba Malayika bebatuusa ku ntuuko zaabwe nga bakyeyisa bubi olwo nno nebalangirira okukkiriza Katonda (nga bwe bagamba nti) tetukolanga kibi kyonna. Nedda mazima Katonda amanyidde ddala ebyo bye mwakolanga.');
INSERT INTO "african_dev" VALUES(16,29,'16:29','Kale muyingire emiryango gyo muliro Jahannama mugenda kutuula mu gwo obugenderevu. Obuddo bwa beekuza bubi.');
INSERT INTO "african_dev" VALUES(16,30,'16:30','Abo abatya Katonda bwe baba bagambiddwa nti Mukama omulabirizi wa mmwe kiki kyassizza bagamba nti bulungi bwereere, abo abakola obulungi mu nsi muno bateekwa kusasulwa bulungi ra e nyumba ey''enkomerero ye nnungi, era nga kyengera kinene okuba nga y’enyumba y''abatya Katonda.');
INSERT INTO "african_dev" VALUES(16,31,'16:31','E jjana ey''olubeerera gye baliyingira, nga emigga gikulukutira wansi waayo nga bali mu yo balifuna ebyo bye beetaaga. Bwatyo Katonda bwasasula abamutya.');
INSERT INTO "african_dev" VALUES(16,32,'16:32','Abo ba Malayika bebatusaako entuuko zaabwe nga balungi, nga bagamba nti emirembe gibeere ku mmwe muyingire e jjana olw''ebyo bye mwakola.');
INSERT INTO "african_dev" VALUES(16,33,'16:33','Abaffe balina kye balinda mpozzi ba Malayika okubajjira oba nekijja ekyo Mukama omulabiriziwo kye yasalawo bwe batyo abaaliwo oluberyeberye lwabwe bwe baakola era Katonda teyabayisa bubi wabula bo bennyini be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(16,34,'16:34','Obubi bw''ebyo bye baakola nebubatuukako era bye baali bajeeja nebibeetoloola.');
INSERT INTO "african_dev" VALUES(16,35,'16:35','Abo abagatta ku Katonda ebintu ebirala baagamba singa Katonda yayagala ffe wadde bakadde baffe tetwandisinzizza kintu kirala netulekayo yye, era tetwandizizizza kintu kyonna kyataaziza, mu ngeri y''emu abaaliwo oluberyeberye lwabwe baakola batyo, abaffe ababaka baali balina kye bavunaanyizibwa okugyako okutuukiriza obubaka mu ngeri ennyinyonnyofu.');
INSERT INTO "african_dev" VALUES(16,36,'16:36','Mazima buli bantu twabatumira omubaka (nga agamba) nti musinze Katonda mwewale ebisinzibwa ebirala byonna, mu bo mulimu Katonda be yalungamya, nga era mu bo mulimu abo abaakakatibwako okubula, kale mutambule mu nsi, mulabe enkomerero y''abalimbisa nga bwe yali.');
INSERT INTO "african_dev" VALUES(16,37,'16:37','Bwoba olulunkanira kulungama kwa bwe, mazima Katonda tayinza kulungamya gwe yabuza, ate tebagenda kufuna wa kubadduukirira.');
INSERT INTO "african_dev" VALUES(16,38,'16:38','Era balayira Katonda nga bakakasa ebirayiro byabwe nti, Katonda tagenda kuzuukiza oyo afudde, nedda eyo ndagaano ku ye gye yeekakasaako edda (nti agenda kukikola) naye mazima abantu abasinga obungi tebamanyi (nti agenda kubazuukiza).');
INSERT INTO "african_dev" VALUES(16,39,'16:39','Olwo nno abannyonnyole ekyo kye baawukanamu era abo abaakaafuwala babe nga bamanya nti mazima bbo baali balimba.');
INSERT INTO "african_dev" VALUES(16,40,'16:40','Mazima ekigambo kyaffe eri ekintu bwe tuba tukyagadde (kibeerewo) tukigamba nti ba nekiba.');
INSERT INTO "african_dev" VALUES(16,41,'16:41','Abo abaasenguka ku lwa Katonda oluvanyuma lw''okuyisibwa obubi tujja ku batuuza mu nsi mu bifo ebirungi, naye nga empeera y''olunaku lw''enkomerero yeesinga obunene singa baali bamanyi.');
INSERT INTO "african_dev" VALUES(16,42,'16:42','(Bo) beebo abagumiikiriza era nga Mukama omulabirizi waabwe gwe beekwasa.');
INSERT INTO "african_dev" VALUES(16,43,'16:43','Tetutumanga oluberyeberyelwo okugyako basajja nga tubawa obubaka kale mubuuze ba nannyini kumanya bwe muba nga temumanyi.');
INSERT INTO "african_dev" VALUES(16,44,'16:44','(Tubatuma nga tubawa) obujulizi obweyolefu n''ebitabo, era twassa ku ggwe ekyokujjukiza osobole okunnyonnyola abantu ekyo ekyassibwa gye bali olwo babe nga beefumiitiriza.');
INSERT INTO "african_dev" VALUES(16,45,'16:45','Abo abakola enkwe, balina obwesige okuba nga Katonda tabaseeterezaako nsi oba ebibonerezo okubajjira nga biva gye batamanyi!.');
INSERT INTO "african_dev" VALUES(16,46,'16:46','(Oba Katonda) naabakwata nga bali mu kwetala kwa bwe (nga bakola emirimu) nga ate tebayinza kulemesa.');
INSERT INTO "african_dev" VALUES(16,47,'16:47','Oba naabakwata nga bali mu kutya (nti essaawa yonna ebibonerezo bibatuukako). Mazima Mukama omulabirizi wa mmwe alumirwa wa kisa.');
INSERT INTO "african_dev" VALUES(16,48,'16:48','Abaffe tebatunuulira ne balaba ebintu Katonda bye yatonda, ebisiikirize byabyo engeri gye bikyukamu ku ddyo ne ku kkono nga bivunnamira Katonda era nga bimugondera.');
INSERT INTO "african_dev" VALUES(16,49,'16:49','Katonda yekka ebyo ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi gwe bivunnamira, kabibe ebitambula ku nsi, ne ba Malayika, era byonna tebyekuza.');
INSERT INTO "african_dev" VALUES(16,50,'16:50','Batya Mukama omulabirizi waabwe ali waggulu waabwe era bakola buli ekibalagirwa.');
INSERT INTO "african_dev" VALUES(16,51,'16:51','Katonda agamba nti temugunjawo ba katonda ababiri, mazima yye ali Katonda omu kale nno nze nzekka gwe muba mutya.');
INSERT INTO "african_dev" VALUES(16,52,'16:52','Era bibye yekka ebyo ebiri mu ggulu omusanvu n''ensi era yekka yaateekwa okugonderwa, ngeriki bwe muyinza okutya atali Katonda!.');
INSERT INTO "african_dev" VALUES(16,53,'16:53','Buli kyengera kyonna kye mulina kiva eri Katonda, oluvanyuma akabi bwe kabatuukako gyali gye musaba okuyambibwa.');
INSERT INTO "african_dev" VALUES(16,54,'16:54','Ate bwabajjako obuzibu ogenda okulaba nga ekitundu ku mmwe bagatta ebintu ebirala ku Mukama omulabirizi waabwe.');
INSERT INTO "african_dev" VALUES(16,55,'16:55','Nebawakanya bye twabawa kale mweyagale kyaddaaki mujja kumanya.');
INSERT INTO "african_dev" VALUES(16,56,'16:56','Ebyo bye batamanyi (amasanamu) babiwa omugabo ku ebyo bye tubawa, ndayidde Katonda ddala mugenda kubuuzibwa ku ebyo bye mugunjaawo.');
INSERT INTO "african_dev" VALUES(16,57,'16:57','Nebateerawo Katonda abaana ab''obuwala, Katonda musukkulumu ku ekyo bo nebeewa be baagala (abalenzi).');
INSERT INTO "african_dev" VALUES(16,58,'16:58','so nga ate omu ku bo bwe bamuwa amawulire ag''okufuna omwana omuwala ekyenyikye kifuuka era n''aba munyikaavu.');
INSERT INTO "african_dev" VALUES(16,59,'16:59','Nga yeekweka abantu olw''amawulire amabi agamutuuseeko, (nga alowooza eky''okukola) amale gabeera n''ekibi ekyo mu buswavu oba amuziike mu ttaka. Abange kibi ekyo kye baasalangawo.');
INSERT INTO "african_dev" VALUES(16,60,'16:60','Abo abatakkiriza lunaku lwa nkomerero bakubirwako ekifaananyi ekisinga okuba ekibi, (mu bukaafiiri n''obutamanya) so nga ye Katonda alina ebitendo ebisukkulumu, era nga ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(16,61,'16:61','Singa Katonda yali asasulirawo abantu olw''okweyisa kwabwe obubi, teyaalirese ku nsi kitambula kyonna wabula abalindiriza okutuuka ku kiseera kyabwe ekigere, olwo nno ekiseera kyabwe ekigere bwe kiba kimaze okutuuka tebayinza kusaba nekyongezebwayo nga bwe batayinza kusaba nekireetebwa mangu.');
INSERT INTO "african_dev" VALUES(16,62,'16:62','(Era abo abatakkiriza) bateeka ku Katonda ekyo bbo kye batamwa, ennimi zaabwe zoogera eby''obulimba (mbu) balina obulungi (ewa Katonda), tewali kubuusabuusa nti mazima bo baakusasulwa muliro, era ddala bo baakulekebwa mu muliro.');
INSERT INTO "african_dev" VALUES(16,63,'16:63','Ndayira Katonda, mazima twatumira abantu abaaliwo oluberyeberyelwo (Ggwe Muhammad), ate Sitane naabawundira ebibi bye bakola, kale nno olwa leero abeere munnaabwe, era baakussibwako ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(16,64,'16:64','Tetwakussaako kitabo (kino) wabula lwa kukusobozesa kubannyonnyola ekyo kye baawukanamu, era nga (Kur’ani) bulungamu era nga kusaasira eri abantu abakkiriza.');
INSERT INTO "african_dev" VALUES(16,65,'16:65','Era Katonda yassa enkuba okuva waggulu naawa nayo ettaka obulamu, oluvanyuma lw''okukala kwalyo, mazima mu ekyo mulimu akabonero eri abantu abawulira (nebategeera).');
INSERT INTO "african_dev" VALUES(16,66,'16:66','Mazima mulina eky''okuyiga mu bisolo ebirundibwa, tubanywesa ku ebyo ebiri munda wa''byo, amata ameereere nga gava wakati w''obusa n''omusayi agawoomera abanywi.');
INSERT INTO "african_dev" VALUES(16,67,'16:67','Era ne mu bibala by''emitende ne Nzabibu mwekoleramu eby''okunywa era nemufunamu n''ebyokulya ebirungi. Mazima mu ekyo mulimu akabonero eri abantu abategeera.');
INSERT INTO "african_dev" VALUES(16,68,'16:68','Era Mukama omulabiriziwo yatumira enjuki nti mukole mu nsozi amayumba (emizinga), ne mu miti ne mu ebyo bye babazimbira.');
INSERT INTO "african_dev" VALUES(16,69,'16:69','Olwo nno (wanjuki), lya ku buli kibala, nga bwogoberera enkola ya Mukama omulabiriziwo gye yasalawo ogoberere, (enjuki bwe zikola zityo) mu mbuto zaazo muvaamu ekyokunywa eky''amabala agenjawulo nga mulimu okuwonya abantu, mazima mu ekyo mulimu akabonereo eri abantu abafumiititiza.');
INSERT INTO "african_dev" VALUES(16,70,'16:70','Era Katonda yeeyabatonda oluvanyuma y’abatta, era mu mmwe mulimu abazzibwa eri obulamu obusinga okuba obwa wansi, olwo nno (omuntu) atuuke okuba nga takyamanyi kintu kyonna oluvanyuma lw''okuba nti yali amanyi. Mazima Katonda amanyi nnyo muyinza.');
INSERT INTO "african_dev" VALUES(16,71,'16:71','Era Katonda yasukkulumya abamu mu mmwe ku balala mu by''enfuna, abo abaasukkulumizibwa ku balala tebaddiza ku bya nfuna bya bwe eri abo bebafuga, olwo nno babe nga bonna benkanankana, abaffe ate olwo ebyengera bya Katonda bye bawakanya!.');
INSERT INTO "african_dev" VALUES(16,72,'16:72','Era Katonda yabateerawo be mufumbiriganwa nabo nga abaggya mu mmwe era naabateerawo nga aggya mu bemufumbiriganwa nabo abaana n''abazzukulu, era naabagabira nga aggya mu birungi, abaffe ebitali bituufu bye bakkiriza ate ebyengera bya Katonda bo bye bawakanya!.');
INSERT INTO "african_dev" VALUES(16,73,'16:73','Nebatuuka okusinza ebitali Katonda, ebitalina buyinza bwonna ku Riziki yaabwe okuva mu ggulu omusanvu n''ensi, era nga tebiyinza kukisobola.');
INSERT INTO "african_dev" VALUES(16,74,'16:74','Kale nno Katonda temumu kubirangako bifaananyi, anti mazima Katonda amanyi ate mmwe temumanyi.');
INSERT INTO "african_dev" VALUES(16,75,'16:75','Katonda yakuba ekifaananyi eky''omuntu omufuge nga talina kintu kyonna kyasobola, n''ekifaananyi ekirala eky''oyo gwe tugabirira okuva gye tuli e riziki ennungi, nga naye (omuddu oyo) agaba ku yo mu kyama n''olwatu abaffe benkana?. Ebitendo byonna bya Katonda, wabula abasinga obungi mu bo tebamanyi.');
INSERT INTO "african_dev" VALUES(16,76,'16:76','Era Katonda yakuba ekifaananyi ekya basajja ababiri omu ku bo nga Kasiru nga tasobola kukola kintu kyonna, n''aba mugugu ku Mukamaawe, wonna waaba amutumye nga talina kirungi kyayinza kumugasa abaffe oyo yenkana n''omuntu alagira okukola obwenkanya ate nga ali ku kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(16,77,'16:77','Katonda amanyi ebikusike mu ggulu omusanvu ne mu nsi, obwangu bw''okujja kw’olunaku lw''enkomerero tebuli okugyako nga olutemya lweriiso, oba kyo kyekisinga obwangu, anti mazima Katonda ku buli kintu muyinza.');
INSERT INTO "african_dev" VALUES(16,78,'16:78','Era Katonda yabagya mu mbuto za bannyamwe nga temulina kyemumanyi, naabawa amatu, n''amaaso, n''emitima mube nga mwebaza.');
INSERT INTO "african_dev" VALUES(16,79,'16:79','Abaffe tebatunuulira ebinyonyi nga byagondezebwa (okubuuka) waggulu mubbanga tewali abiwanirira okugyako Katonda. Mazima mwekyo mulimu obubonero eri abantu abakkiriza.');
INSERT INTO "african_dev" VALUES(16,80,'16:80','Era Katonda yabateerawo mu mayumba gammwe obuwummuliro, era naabateerawo mu maliba gebisolo amayumba agabanguyira lwe muba mutambudde ne lwe muba temutambudde, ate nabaggira mu byoya bye ndiga ne bye ngamiya ne mu bye mbuzi ebyokukozesa era nga byakweyagala kwa mmwe okumala akabanga.');
INSERT INTO "african_dev" VALUES(16,81,'16:81','Era Katonda yabateerawo ebisiikirize nga bikolebwa ebimu ku ebyo bye yatonda era naabateera mu nsozi empuku (wemwekukuma) era naabateerawo ebyambalo ebibajuna ku bbugumu n''ebyambalo (ebirala) ebibakuuma mu ntalo zammwe bwatyo bwajjuliza ebyengerabye ku mmwe kibayambe okukkiriza.');
INSERT INTO "african_dev" VALUES(16,82,'16:82','Naye bwe bava ku ekyo mazima kyoteekwa okukola kwe kutuusa obubaka mu bunnyonnyofu.');
INSERT INTO "african_dev" VALUES(16,83,'16:83','Bamanya ebyengera bya Katonda, naye nebabiwakanya, era abasinga obungi mu bo bakaafiiri.');
INSERT INTO "african_dev" VALUES(16,84,'16:84','(Bajjukize) olunaku lwe tulizuukiza mu buli kibiina omujulizi, oluvanyuma abo abaakaafuwala tebagenda kukkirizibwa kwetonda, nga era bwe batagenda kusabibwa kwemenya.');
INSERT INTO "african_dev" VALUES(16,85,'16:85','Abo abeeyisa obubi kebaliraba ku bibonerezo tebigenda kukendezebwa ku bo era tebagenda kulindirizibwa.');
INSERT INTO "african_dev" VALUES(16,86,'16:86','Abo abagatta ku Katonda ebintu ebirala bwe baliraba ebyo bye baamugattangako, baligamba nti ayi Mukama omulabirizi waffe abo be bannaffe betwasabanga gwe netukuvaako, nebabaddiza ekigambo nti, mazima mmwe muli balimba.');
INSERT INTO "african_dev" VALUES(16,87,'16:87','Olunaku olwo bagenda kulangirira okwewaayo ewa Katonda era bigenda kubabulako ebyo bye bagunjawo.');
INSERT INTO "african_dev" VALUES(16,88,'16:88','Abo abaakaafuwala nebalobera abantu abalafuubana nga baweereza mu kkubo lya Katonda, tugenda kubongera ebibonerezo waggulu w''ebibonerezo ku lw''ebyo bye baayonoonanga.');
INSERT INTO "african_dev" VALUES(16,89,'16:89','Era bajjukize olunaku lwe tulizuukiza mu buli bantu oyo alibawaako obujulizi nga ava mu bo bennyini ate ggwe netukuleeta nga owa obujulizi ku abo, era twakussaako ekitabo nga kinnyonnyola buli kintu era nga bulungamu, era nga kusaasira, era nga mawulire ag''essanyu eri abasiraamu.');
INSERT INTO "african_dev" VALUES(16,90,'16:90','Mazima Katonda alagira obwenkanya, n''okuyisa, obulungi n''okugabira ab''oluganda olw''okumpi. Era aziza eby''obuwemu n''empisa embi n''okwewaggula ababuUlirira mube nga mujjukira.');
INSERT INTO "african_dev" VALUES(16,91,'16:91','Era mutuukirize endagaano ya Katonda, era temumenyanga ebirayiro oluvanyuma lw''okubinyweza, nga ate Katonda gwe mwafuula omweyimirize wa mmwe. Bulijjo mazima Katonda amanyi byonna bye mukola.');
INSERT INTO "african_dev" VALUES(16,92,'16:92','Temubeera nga omukazi oyo eyataggululanga byatunze nebifuuka obulere oluvanyuma lw''okubinyweza, nga mufuula ebirayiro bya mmwe enkwe wakati wa mwwe olw''okuba nti abantu abamu bo bagagga okusinga abalala,mazima (eby''obugagga) Katonda abagezesa nabyo bugezesa era ku lunaku lw''enkomerero agenda kubannyonnyolera ddala bye mwali mwawukanamu.');
INSERT INTO "african_dev" VALUES(16,93,'16:93','Singa Katonda yayagala yandibafudde ekibiina kimu, naye abuza oyo gwaba ayagadde era nalungamya oyo gwaba ayagadde, era ddala mugenda kubuuzibwa ebyo bye mwakolanga.');
INSERT INTO "african_dev" VALUES(16,94,'16:94','Temufuulanga ebirayiro bya mmwe okukwenyakwenya wakati wa mmwe, olwo nno ekigere nekiserera oluvanyuma lw''okunywera kwakyo, era nemukomba ku bubi (bw''obusungu bwa Katonda), kulwokuziza kwa mmwe (abantu) ku kkubo lya Katonda era mugenda kutuusibwaako ebibonerezo ebiyitirivu.');
INSERT INTO "african_dev" VALUES(16,95,'16:95','Endagaano ya Katonda temugitundanga omuwendo omutono, mazima ebiri ewa Katonda bye birungi gye muli, bwe muba nga mumanyi.');
INSERT INTO "african_dev" VALUES(16,96,'16:96','Byemulina biggwawo ate ebiri ewa Katonda bya kusigalawo, abo abagumiikiriza tugenda kubasasulira ddala empeera yaabwe nga baweebwa ekisinga obulungi ku lw''ebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(16,97,'16:97','Oyo yenna akola omulimu omulingi, kaabe musajja oba mukazi, nga naye mukkiriza, tugenda kumuwangaliza ddala mu bulamu obulungi era tugenda kubasasula empeera yaabwe nga baweebwa ekisinga obulungi ku lw''ebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(16,98,'16:98','Buli lwoyagala okusoma Kur’ani saba Katonda akukuume ku Sitane eyafulumizibwa (mu kusaasira kwa Katonda)');
INSERT INTO "african_dev" VALUES(16,99,'16:99','Mazima ye (Sitane) talina buyinza ku abo abakkiriza era nga Mukama omulabirizi waabwe gwe beekwasa.');
INSERT INTO "african_dev" VALUES(16,100,'16:100','Mazima obuyinzabwe buli ku abo abamwewa, era n''abo abagatta ku Katonda ebintu ebirala.');
INSERT INTO "african_dev" VALUES(16,101,'16:101','Bwetukyusa aya mu kifo kya aya endala, era nga Katonda yamanyi ebyo byassa, bagamba nti mazima ggwe obyeyiyiza naye (ekituufu) abasinga obungi mu bo tebamanyi.');
INSERT INTO "african_dev" VALUES(16,102,'16:102','Gamba nti yagissa (Kur’ani) mu bwesimbu mwoyo mutukuvu (Jiburilu) nga ajijja ewa Mukama omulabiriziwo, olwo nno alyoke anyweze abo abakkiriza era (Kur’ani) bulungamu n''amawulire ga ssanyu eri abasiraamu.');
INSERT INTO "african_dev" VALUES(16,103,'16:103','Mazima tukimanyi nti bo bagamba nti ddala amuyigiriza muntu (ekyo kyabulimba) anti olulimi lw''oyo gwebemulugunyako nti yamuyigiriza si Ruwarabu ate nga eno Kur’ani eri mu lulimi oluwarabu olweyolefu.');
INSERT INTO "african_dev" VALUES(16,104,'16:104','Mazima abo abatakkiriza bigambo bya Katonda, Katonda tabalungamya era baakutukibwako ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(16,105,'16:105','Mazima abagunjawo obulimba beebo abatakkiriza bigambo bya Katonda era abo bo be balimba.');
INSERT INTO "african_dev" VALUES(16,106,'16:106','Oyo yenna awakanya Katonda oluvanyuma lwokukkirizakwe (kagenda kumujjutuka), okugyako oyo akakiddwa naye nga omutima gwe gunyweredde ku bukkiriza, naye oyo ayanjuliriza obukaafiiri ekifubakye, abo baliko obusungu okuva eri Katonda, era balituusibwako ebibonerezo ebiyitirivu.');
INSERT INTO "african_dev" VALUES(16,107,'16:107','Ekyo nno lwa kuba nti mazima bbo baagala obulamu obw''ensi okusinga obw''enkomerero, era mazima Katonda talungamya bantu bakaafiiri.');
INSERT INTO "african_dev" VALUES(16,108,'16:108','Abo beebo Katonda beyazibikira emitima gyabwe n''amatu gaabwe n''amaaso gaabwe era abo bo be batafaayo.');
INSERT INTO "african_dev" VALUES(16,109,'16:109','Tewali kubuusabuusa mazima bo ku nkomerero be b''okufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(16,110,'16:110','Ate mazima Mukama omulabiriziwo ku nsonga y''abo abaasenguka oluvanyuma lw''okunyigirizibwa ate nebalafuubana nga baweereza mu kkubo lya Katonda era nebagumiikiriza, mazima Mukama omulabiriziwo oluvanyuma lw''ebyo musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(16,111,'16:111','Bajjukize olunaku omwoyo bwe gulijja nga gwerwanako gwokka, era buli mwoyo gugenda kusasulwa mu bujjuvu ekyo kye gwakola, era bo tebagenda kuyisibwa bubi.');
INSERT INTO "african_dev" VALUES(16,112,'16:112','Era Katonda yakuba ekifaananyi ky''ekitundu ekyali emirembe nga kitebenkedde, nga eby''okulya by''akyo bikijjira mu bungi, nga biva mu buli kifo, (ekitundu ekyo) nekiwakanya ebyengera bya Katonda, Katonda naakikombya ku kyambalo (kya nnawokeera) w''eenjala n''okutya, (ebyo byonna byabatuukako) olw''ebyo bye baakola.');
INSERT INTO "african_dev" VALUES(16,113,'16:113','Era mazima omubaka yabajjira nga ava mu bo nebamulimbisa, ebibonerezo nebibatuukako nga bo bali mu kweyisa bubi.');
INSERT INTO "african_dev" VALUES(16,114,'16:114','Kale mulye mu ebyo Katonda byabagabira kasita biba Halali nga ate birungi, era mwebaze ebyengera bya Katonda bwe muba nga ye yekka gwe musinza.');
INSERT INTO "african_dev" VALUES(16,115,'16:115','Mazima yabaziyiza okulya ennyamanfu n''omusaayi n''ennyama ye mbizzi, nakyonna ekiba kisaliddwa ku lw''ekintu ekirala ekitali Katonda, naye oyo abeera mu bwetaavu nga takikola lwa jjoogo, era naatasukka ekitebereka (mu kulya) mazima ddala Katonda musonyiyi wa kisa.');
INSERT INTO "african_dev" VALUES(16,116,'16:116','Temugambanga, ebyo ennimi zammwe bye zoogera mu bulimba nti kino kiri Halali nakino kiri Haramu olw''okutemerera obulimba ku Katonda. Mazima abatemerera obulimba ku Katonda tebagenda kutuuka ku buwanguzi.');
INSERT INTO "african_dev" VALUES(16,117,'16:117','Okwo kweyagala kutono era bagenda kutuukwako ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(16,118,'16:118','Ate twaziza ku abo abagamba nti bayudaaya ebyo bye twakutegeeza oluberyeberye, si ffe twabayisa obubi bo bennyini be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(16,119,'16:119','Ate mazima Mukama omulabiriziwo ku bikwata ku abo abakola ebibi mu butamanya, ate nebeenenya oluvanyuma lw''ekyo, era nebakola ebirungi (Katonda abasonyiwa), anti mazima Mukama omulabiriziwo oluvanyuma lw''ebyo musonyiyi nnyo wa kisa.');
INSERT INTO "african_dev" VALUES(16,120,'16:120','Mazima Ibrahim (ye, yekka) yali kibiina, nga mugonvu ku lwa Katonda, nga mwesimbu, tabeerangako mu bagatta ku Katonda bintu birala.');
INSERT INTO "african_dev" VALUES(16,121,'16:121','Nga yeebaza ebyengera bye, yamwawula, era naamulungamya ku kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(16,122,'16:122','Era twamuwa ku nsi ebirungi era mazima yye ku nkomerero agenda kubeera mu balongoofu.');
INSERT INTO "african_dev" VALUES(16,123,'16:123','Oluvanyuma twakutumira (Ggwe Muhamad) nti goberera eddiini ya Ibrahim nga oli mwesimbu, era (Ibrahim) tabeerangako mu bagatta bintu birala ku Katonda.');
INSERT INTO "african_dev" VALUES(16,124,'16:124','Mazima olw''omukaaga lwateerwawo abo abalwawukanamu, era mazima Mukama omulabiriziwo agenda kulamula wakati waabwe ku lunaku lw''enkomerero mu ekyo kye baayawukanamu.');
INSERT INTO "african_dev" VALUES(16,125,'16:125','Kowoola (abantu) okudda eri ekkubo lya Mukama omulabiriziwo nga okozesa amagezi n''okubuulirira okulungi, nyonnyolagana nabo nga okozesa amakubo agasinga obulungi mazima Mukama omulabiriziwo y''asinga okumanya ebikwata ku oyo eyabula okuva ku kkubolye era yaasinga okumanya ebikwata ku balungamu.');
INSERT INTO "african_dev" VALUES(16,126,'16:126','Bwe muba nga mubonereza mubonereze kyenkanyi ky''ekyo kye muba mubonerezebbwa nakyo, naye singa mugumiikiriza ekyo kye kisinga obulungi eri abagumiikiriza.');
INSERT INTO "african_dev" VALUES(16,127,'16:127','Era gumiikiriza, okugumiikirizakwo tekuyinza kubaawo okugyako ku lwa Katonda, tobanakuwalira, era tobeera mu bulumi olw''enkwe ze bakola.');
INSERT INTO "african_dev" VALUES(16,128,'16:128','Mazima Katonda ali wamu n''abo abamutya, era abo abalongoosa.');
INSERT INTO "african_dev" VALUES(17,1,'17:1','. Musukkulumu nnyo oyo eyatambuza omudduwe ekiro okumuggya ku muzikiti ogw''emizizo (Makkah) naagenda ku muzikiti ogusinga okuba ogw''ewala (Jerusalemu), ogwo ebitundu ebigwetoolodde bye twawa omukisa, olwo nno tumulage obumu ku bubonero bwaffe. Mazima yye (Katonda) y''awulira ennyo alaba.');
INSERT INTO "african_dev" VALUES(17,2,'17:2','. Era twawa Musa ekitabo era netukifuula eky''okulungamya eri abaana ba Israil (nga mbagamba) nti nga oggyeko nze temweteerangawo omukuumi omulala yenna.');
INSERT INTO "african_dev" VALUES(17,3,'17:3','. (Abange mmwe) ezadde ly''abo be twatwala ne Nuhu mu lyato (mubeere nga Nuhu), mazima ye yali muddu eyeebaza.');
INSERT INTO "african_dev" VALUES(17,4,'17:4','. Era twasalawo ku baana ba Israil mu kitabo nti mugenda kwonoonera ddala mu nsi emirundi ebiri, era mugenda kwe kuluntaza olwekuluntaza oluyiritivu.');
INSERT INTO "african_dev" VALUES(17,5,'17:5','. Bwerituuka endagaano y''omulundi ogusooka ku gyombi, tugenda kubatumira abaddu baffe ab''amaanyi amasuffu, bagenda kutuuka ku buli kanyomero ka mayumba, (nga batuusa obuzibu ku buli omu), era (eyo) neeba nga ndagaano eteekwa okutuukirizibwa.');
INSERT INTO "african_dev" VALUES(17,6,'17:6','. Oluvanyuma twabaddiza mmwe amaanyi ku bo (nemubawangula) era netubawa emmaali, n''abaana era netubafuula mmwe nga mwe musinga obungi.');
INSERT INTO "african_dev" VALUES(17,7,'17:7','. (Netubagamba nti) bwe mulongoosa mulongoseza myoyo gya mmwe, naye bwe mwonoona era gye mwonoonera, endagaano y''enkomerero bwelijja (tulibasindikira abantu) boonoone ebyenyi bya mmwe (babakkakkanye), era bagenda kuyingira omuzikiti (Jerusalemu) nga bwe baaguyingira omulundi ogwasooka, olwo nno bye baliba bawangudde babibetente olubetenta.');
INSERT INTO "african_dev" VALUES(17,8,'17:8','. Bwe muyisa obulungi kisoboka Mukama omulabirizi wa mmwe okubasaasira, naye bwe muddamu naffe nga tuddamu, ate nga omuliro Jahannama twagufuula ekkomera lya bakaafiiri.');
INSERT INTO "african_dev" VALUES(17,9,'17:9','. Mazima Kur’ani eno erungamya eri ekkubo erisinga obugolokofu, era ewa abakkiriza abo abakola emirimu emirongoofu amawulire ag''essanyu nti: mazima bo baakufuna empeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(17,10,'17:10','. Era nti mazima ddala abo abatakkiriza lunaku lwa nkomerero twabagerekera ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(17,11,'17:11','. Omuntu (olw''ebizibu ebimutuukako) yeesabira ebibi nga bwe yandyesabidde ebirungi, anti bulijjo omuntu ayanguyiriza.');
INSERT INTO "african_dev" VALUES(17,12,'17:12','. Era twassaawo ekiro n''emisana, nga bubonero bubiri, olwo nno tujjawo akabonero k''ekiro netussaawo akabonero k''emisana, nekabasobozesa okulaba mulyoke musobole okumanya ebigabwa bya Mukama omulabirizi wa mmwe, era musobole okumanya omuwendo gw''emyaka n''okubala, era buli kintu twakinnyonnyola olunnyonnyola olumala.');
INSERT INTO "african_dev" VALUES(17,13,'17:13','. Era buli muntu twamukakasaako emirimugye mu nsingoye, era ku lunaku lw''enkomerero tugenda kumuggyirayo ekitabo kyalisanga nga kibikkule.');
INSERT INTO "african_dev" VALUES(17,14,'17:14','. Olwo nno aliragirwa nti soma ekitabokyo, kimala bumazi okuba nti olwa leero ggwe wennyini ggwe ogenda okwebala.');
INSERT INTO "african_dev" VALUES(17,15,'17:15','. Oyo yenna alungama alungamira mwoyogwe, ate abula omwoyogwe gwaba aleetedde obuzibu bw''okubula, tewali mwoyo mwonoonyi gugenda kwetikka kyonoono kya mwoyo mwonoonyi mulala, era tetusobola kubonereza okugyako nga tutumye omubaka.');
INSERT INTO "african_dev" VALUES(17,16,'17:16','. Bwetuba twagadde okuzikiriza ekitundu kyonna (olw''obujeemu bwakyo) tulagira abali mu byengera (okweyisa obulungi) naye ate nebeeyisa bubi mu kyo, olwo nno ekigambo (eky''okubonerezebwa) nekikikakata, olwo nno netukisesebbula olusesebbula.');
INSERT INTO "african_dev" VALUES(17,17,'17:17','. Mirembe emeka gye twazikiriza oluvanyuma lwa Nuhu (nga mingi!), Mukama omulabiriziwo amalira ddala ku bikwata ku bibonerezo bya baddube, okuba nti amanyidde ddala alaba.');
INSERT INTO "african_dev" VALUES(17,18,'17:18','. Oyo ayagala eby''amangu (ensi) tumwanguyiza muyo ekyo kye tuba twagadde si buli omu wabula oyo gwe tuba tusazeewo, oluvanyuma netumuteerawo omuliro Jahannama agenda kugwesogga nga avumirirwa nga agobeddwa (mu kusaasira kwa Katonda).');
INSERT INTO "african_dev" VALUES(17,19,'17:19','. Ate omuntu ayagala enkomerero era naakola ebyetaagisa ku yo, era nga mukkiriza, abo nno bye bakola byebazibwa.');
INSERT INTO "african_dev" VALUES(17,20,'17:20','. Buli bamu (abalungi n''ababi ku nsi) tuwa bano nabali okuva mu bigabwa bya Mukama omulabiriziwo. Okugaba kwa Mukama omulabiriziwo tekussibwangako kkomo (ku muntu yenna kuno ku nsi).');
INSERT INTO "african_dev" VALUES(17,21,'17:21','. Tunula olabe engeri gye twasukkulumya abamu ku balala, wabula enkomerero y''erimu amadaala agasinga obukulu era y''erimu okusingana okusinga obulungi.');
INSERT INTO "african_dev" VALUES(17,22,'17:22','. Katonda tomussangako ba katonda balala, olwo nno nekikufuula avumirirwa aboolebwa.');
INSERT INTO "african_dev" VALUES(17,23,'17:23','. Mukama omulabiriziwo yasalawo okuba nga temusinza okugyako yye, yekka. Era yasalawo abazadde ababiri okuyisibwa obulungi, singa omu ku bo, oba bombi akaddiwa nga wooli, tobagambanga ekigambo eky''okubeenyinyala era tobaboggoleranga yogera nabo ebigambo eby''ensa.');
INSERT INTO "african_dev" VALUES(17,24,'17:24','. Beetoowalize era wekkakkanye gye bali olw''okubasaasira.Era gamba nti ayi Mukama omulabirizi wange basaasire bombi nga bwe bandera nga ndi muto.');
INSERT INTO "african_dev" VALUES(17,25,'17:25','. Mukama omulabirizi wa mmwe, y''asinga okumanya ebyo ebiri mu myoyo gya mmwe (bye mukwese), bwe muba nga muli abalongoosa, bulijjo musonyiyi w''abo abamwemenyera.');
INSERT INTO "african_dev" VALUES(17,26,'17:26','. Ow''olugandalwo ow''okumpi muwenga ekyo kyoteekwa okumuwa, era owe omunaku n''omutambuze, todiibuuda obudiibuuzi.');
INSERT INTO "african_dev" VALUES(17,27,'17:27','. Mazima abadiibuuzi be bayambi ba Sitane (mu bwonoonefu n''obujeemu). Ate bulijjo Sitane mujeemu eri Mukama omulabiriziwe.');
INSERT INTO "african_dev" VALUES(17,28,'17:28','. Naye bwobanga tobawadde (olw''ensonga yonna), naye ate nga oyagala era nga osuubira okusaasira kwa Mukama omulabiriziwo olwo nno bagambe ekigambo ekigonvu.');
INSERT INTO "african_dev" VALUES(17,29,'17:29','. Omukono gwo togufuulanga oguwanikiddwa ku nsingoyo (olw''obukodo), ate togwanjuluzanga n''ogumalayo (olw''okudiibuuda), n''ofuuka anenyezebwa atasigazizza.');
INSERT INTO "african_dev" VALUES(17,30,'17:30','. Mazima Mukama omulabiriziwo ayanjuluriza riziki oyo gwaba ayagadde, naawa mu ngeri y''empola (eri omulala) anti mazima yye bulijjo amanyidde ddala ebikwata ku baddube era alaba.');
INSERT INTO "african_dev" VALUES(17,31,'17:31','. Era temuttanga abaana ba mmwe olw''okutya obwavu (okubalumba), ffe tubagabira netugabira nammwe, mazima okubatta kibi kiyitirivu.');
INSERT INTO "african_dev" VALUES(17,32,'17:32','. Era temusembereranga obwenzi, mazima bwo kya buwemu era kkubo bbi.');
INSERT INTO "african_dev" VALUES(17,33,'17:33','. Era temuttanga omuntu oyo Katonda gwe yaziza okutta okugyako nga waliwo ensonga, oyo yenna attibwa nga alangibwa bwereere mazima twawa nannyiniye obuyinza (okutta eyatta omuntuwe) wabula mu kutta tosaanye kusukka kikomo anti mazima yye (attiddwa) ataasibwa (n''etteeka).');
INSERT INTO "african_dev" VALUES(17,34,'17:34','. Temusembereranga emmaali ya mulekwa, okugyako lwa kugikolako ekyo ekisinga obulungi, okutuusa lwasajjakula.Era mutuukirizenga endagaano mazima endagaano yaakukubuuzibwa.');
INSERT INTO "african_dev" VALUES(17,35,'17:35','. Era mutuukirizenga ebigero bye mugera era mupime n''ebipimo ebituufu, ekyo kye kirungi era kye kituusa ku nkomerero ennungi.');
INSERT INTO "african_dev" VALUES(17,36,'17:36','. Tewessanga ku kintu kyotalinaako kumanya, mazima okuwulira, n''okulaba, n''omutima. Ebyo byonna buli kimu kigenda kubuuzibwa.');
INSERT INTO "african_dev" VALUES(17,37,'17:37','. Totambulanga mu nsi nga weeraga, mazima ggwe togenda kwabya ttaka era togenda kwenkana nsozi buwanvu.');
INSERT INTO "african_dev" VALUES(17,38,'17:38','. Ekibi kya buli kimu ku ebyo, kitamwa ewa Mukama omulabiriziwo.');
INSERT INTO "african_dev" VALUES(17,39,'17:39','. Ebyo bye bimu ku ebyo Mukama omulabiriziwo byassa gyoli mu bigambo eby''amagezi, era tossanga ku Katonda ba katonda abalala, n''okakatibwako okukasukibwa mu muliro Jahannama, ng''ovumirirwa era nga ogobeddwa mu kusaasira kwa Katonda.');
INSERT INTO "african_dev" VALUES(17,40,'17:40','. (Abange mmwe abatakkiriza), Mukama omulabirizi wa mmwe yasalawo mmwe abawe abaana abalenzi ate yye neyeewa abawala mu ba Malayika! mazima mmwe mwogera ekigambo ekibi ennyo.');
INSERT INTO "african_dev" VALUES(17,41,'17:41','. Mazima twannyonnyola mu Kur’ani eno buli kintu (eri abantu), babe nga bajjukira (beebuulirire), wabula tekibongera okugyako okweyongera (okwesamba amazima).');
INSERT INTO "african_dev" VALUES(17,42,'17:42','. Bagambe (Ggwe Muhammad) singa Katonda aliko ba katonda abalala nga bwe bagamba, olwo nno (ba katonda abo) bandibadde beetaaga ekkubo eribatuusa ewa (Katonda) nnyini Arish.');
INSERT INTO "african_dev" VALUES(17,43,'17:43','. Yeesamba naasukkuluma ebyo bye bagamba olusukkuluma olusuffu.');
INSERT INTO "african_dev" VALUES(17,44,'17:44','. Ku lulwe eggulu omusanvu n''ensi n''ebirimu byonna bimutendereza era tewali kintu kyonna wabula nga kitendereza ebitendobye naye temutegeera ngeri ya kutendereza kwabyo, mazima yye bulijjo alumirwa musonyiyi.');
INSERT INTO "african_dev" VALUES(17,45,'17:45','. Bwosoma Kur’ani, tussa wakatiwo ne wakati w''abo abatakkiriza lunaku lwa nkomerero ekibikka ekitalabika.');
INSERT INTO "african_dev" VALUES(17,46,'17:46','. Era netussa ku mitima gya bwe ekibikka ekibagaana okubitegeera, netussa mu matu gaabwe envumbo, era bwoyogera ku Mukama omulabiriziwo yekka mu Kur’ani, bakubayo amabega nebadduka.');
INSERT INTO "african_dev" VALUES(17,47,'17:47','. Ffe tumanyi ennyo lwaki bawuliriza bwe bajja okukuwuliriza, era tumanyi kye babaako mu nkiiko zaabwe ez''ekyama, mu kiseera awo abeeyisa obubi webagambira nti mazima gwe mugoberera talina kyali okugyako okubeera omusajja omuloge.');
INSERT INTO "african_dev" VALUES(17,48,'17:48','. Tunula olabe engeri gye bakukubira ebifaananyi nebatuuka okubula nebatasobola kulaba kkubo (libazza eri amazima).');
INSERT INTO "african_dev" VALUES(17,49,'17:49','. Era bagamba nti bwe tulimala okubeera amagumba (amamerengufu) netufuuka obutundutundu obutakyayawulwa na ttaka, abaffe tulizuukizibwa nga tuli bitonde bipya!.');
INSERT INTO "african_dev" VALUES(17,50,'17:50','. Gamba nti kamube mayinja oba byuma (mujja kuzuukizibwa).');
INSERT INTO "african_dev" VALUES(17,51,'17:51','. Oba mube ekitonde kyonna mu ebyo bye mulaba nga binene mu kutegeera kwa mmwe (era mujja kuzuukizikibwa) olwo nno bagamba nti ani alituzzaawo! Bagambe nti oyo eyabatandikawo omulundi ogwasooka y''alibazzaawo, bajja kukunyeenyeza emitwe nga bwe bagamba nti kyo kyaddi?, bagambe nti ddala kiri kumpi.');
INSERT INTO "african_dev" VALUES(17,52,'17:52','. (Kigenda kubaawo) ku lunaku lwalibakoowoola nemwanukula nga mumutendereza era mugenda kulowooza nti temwamala ku nsi okugyako akaseera katono.');
INSERT INTO "african_dev" VALUES(17,53,'17:53','. Era gamba abaddu bange, babe nga boogera ebyo ebisinga okuba ebirungi. Mazima Sitane atabula wakati wa bwe anti mazima Sitane mulabe wa muntu ow''olwatu.');
INSERT INTO "african_dev" VALUES(17,54,'17:54','. Katonda wa mmwe y''asinga okubamanya, bwayagala abasaasira, oba bwaba ayagadde ababonereza era tetukutumanga kuba nga ggwe okola ku nsonga zaabwe.');
INSERT INTO "african_dev" VALUES(17,55,'17:55','. Era Mukama omulabiriziwo y''asinga okumanya abo abali mu ggulu omusanvu n''ensi, mazima twawa ba Nabbi abamu enkizo ku bannaabwe, era twawa (Nabbi) Dauda Zaburi.');
INSERT INTO "african_dev" VALUES(17,56,'17:56','. Bagambe nti kale musabe abo bemulowooza nti ba katonda nemuva ku ye, ate nga tebasobola kuggya ku mmwe kabi wadde (okukakyusa) okukazza ku mulala');
INSERT INTO "african_dev" VALUES(17,57,'17:57','. Abo bebasaba nabo banoonya ekkubo eribatuusa ewa Mukama omulabirizi waabwe, (kaakati) ku bonna ani ow''okumpi? Era baagala okusaasirakwe era batya ebibonerezobye mazima ebibonerezo bya Mukama omulabiriziwo biteekwa okwewalibwa.');
INSERT INTO "african_dev" VALUES(17,58,'17:58','. Tewali kitundu kyonna (ku nsi) okugyako okuba nga tugenda kukisanyaawo nga olunaku lw''okuzuukira terunnatuuka oba tugenda kukibonereza ebibonerezo ebikakali. Ekyo kyawandiikibwa dda mu kitabo.');
INSERT INTO "african_dev" VALUES(17,59,'17:59','. Tewali kitugaana kuweereza by''amagero, okugyako okuba nti abaasooka baabirimbisa nga bwe twaleetera abantu ba Thamud engamiya nga kyamagero eky''olwatu, nebeeyisa bubi ku yo, era tewali kituweerezesa byamagero okugyako okutiisa (abantu bave ku bujeemu).');
INSERT INTO "african_dev" VALUES(17,60,'17:60','. Era jjukira bwe twakugamba nti mazima Mukama omulabiriziwo yeetoolodde abantu (abo, n''olwekyo tobatya) era tetwaleeta ndooto eyo gye twakulaga okugyako olw''okugezesa abantu era mu ngeri y''emu omuti ogwakolimirwa mu Kur’ani nakyo kigezo era tubatiisa (nga tubabuulira) ebiribaawo ku lunaku lw''enkomerero naye tebibongera okugyako okubula okuyitirivu.');
INSERT INTO "african_dev" VALUES(17,61,'17:61','. Era jjukira bwe twagamba ba Malayika nti muvunnamire Adam, nebavunnama okugyako Ibuliisu eyagamba nti nvunnamire oyo gwe watonda mu ttaka!.');
INSERT INTO "african_dev" VALUES(17,62,'17:62','. Era (Sitaane) naagamba nti ggwe bwotyo bwolabye nti oyo omusukkulumizza ku nze. Singa onnindiriza (nemba mulamu) okutuuka ku lunaku lw''enkomerero nja kubuliza ddala ezzadderye okugyako abatono ennyo.');
INSERT INTO "african_dev" VALUES(17,63,'17:63','. Katonda naAgamba nti kale genda (ekyo nkikuwadde) naye buli alikugoberera mu bo mazima Jahannama yempeera yammwe eribasasulwa mu bujjuvu.');
INSERT INTO "african_dev" VALUES(17,64,'17:64','. Era sikiriza n''eddoboozilyo oyo yenna gwoba osobodde mu bo, batabaale nga okozesa eggyeryo eryembalaasi n''eritambuza ebigere okolagane nabo mu mmaali ne mu baana era obasuubize (buli kyoyagala) wabula Sitaane tabalagaanyisa okugyako ebigayaaza.');
INSERT INTO "african_dev" VALUES(17,65,'17:65','. Mazima abaddu bange tobalinaako buyinza era kimala okuba nga Mukama omulabiriziwo ye mukuumi.');
INSERT INTO "african_dev" VALUES(17,66,'17:66','. Mukama Katonda wa mmwe yooyo abatambuliza amaato mu nyanja musobole okunoonya ebimu ku bigabwabye, mazima yye ku mmwe musaasizi nnyo.');
INSERT INTO "african_dev" VALUES(17,67,'17:67','. Obuzibu bwe bubatuukako nga muli mu nyanja olwo nno buli kye musaba nekivaawo okugyako yye, naye bwabawonya nemudda ku lukalu ate olwo nemuddayo. Era anti bulijjo omuntu mwegaanyi.');
INSERT INTO "african_dev" VALUES(17,68,'17:68','. Abaffe mulina obukakafu (Katonda) okuba nti tayinza kulagira ludda lwansi (gye mulaze) nelubamira, oba n''abasindikira kibuyaga, nemuba nga temuyinza kufuna mukuumi yenna.');
INSERT INTO "african_dev" VALUES(17,69,'17:69','. Oba mulina obukakafu nti Katonda tayinza kubazzaayo mu yo (enyanja) omulundi omulala, olwo nno n''abasindikira omuyaga olw''ebyo bye mwawakanya ate nga temuyinza kwefunira kuffe olw''ekintu ekyo muntu alondoola.');
INSERT INTO "african_dev" VALUES(17,70,'17:70','. Mazima twawa abaana ba Adam ekitiibwa, netubawa kwe batambulira nga bali ku lukalu ne kunyanja, netubagabirira mu birungi, era netubasukkulumya olusukkulumya ku bingi mu ebyo bye twatonda.');
INSERT INTO "african_dev" VALUES(17,71,'17:71','. (Bajjukize) olunaku lwe tulikoowoola buli bantu nga tuyita linnya lya mukulembeze waabwe (mulungi oba mubi),oyo aliweebwa ekitabokye mu mukonogwe ogwa ddyo abo nno bagenda kusoma ebitabo byabwe, era tebagenda kulyazaamanyizibwa wadde akantu akatono.');
INSERT INTO "african_dev" VALUES(17,72,'17:72','. Era oyo yenna muzibe mu bulamu buno obw''ensi ku lunaku lw’enkomerero agenda kuba muzibe, era nga abuze nnyo okuva ku kkubo.');
INSERT INTO "african_dev" VALUES(17,73,'17:73','. Newaakubadde nga baabula kata bakubuze okuva ku bubaka obwo bwe twakutumira ggwe (Nabbi Muhammad) obe nga ogunja ku ffe ekintu ekirala ekitali bwo, era singa okola ekyo balikufudde ffa nfe waabwe.');
INSERT INTO "african_dev" VALUES(17,74,'17:74','. Era singa tetwakunyweza, mazima kumpi okyamemu katono nga odda gye bali.');
INSERT INTO "african_dev" VALUES(17,75,'17:75','. Olwo nno (singa obagondera) twandi kukombesezza ku bibonerezo byo ku nsi, nga bibaziddwamu era netukukombesa ne ku bibonerezo byokufa, nga nabyo bibaziddwamu era tewandifunye akututasaako.');
INSERT INTO "african_dev" VALUES(17,76,'17:76','. Newaakubadde nga basemberera okukunyenya bakuggye mu nsi (yo) olwo nno bagikugobemu, ekyo nebwekituukirira nga gwe ofulumye, tebayinza kugituulamu okugyako ebbanga ttono.');
INSERT INTO "african_dev" VALUES(17,77,'17:77','. Eyo y''enkola (yaffe) kwabo betwatuma oluberyeberyelwo mu babaka baffe, toyinza kulaba nkyukakyuka yonna mu nkola yaffe.');
INSERT INTO "african_dev" VALUES(17,78,'17:78','. Yimirizaawo e sswala enjuba nga ekyuse okutuusa ku nzikiza y''ekiro nabwekityo Kur’ani y''okumakya (Sswala ya Subuhi) mazima Kur’ani y''okumakya ba Malayika bagibaako.');
INSERT INTO "african_dev" VALUES(17,79,'17:79','. Ne mu kiro saala nga ogisoma (Kur’ani), (ekyo) nga nnyongeza gyoli olwo nno Mukama omulabiriziwo ajja kukuzuukiza mu kifo ekiweesa ekitiibwa.');
INSERT INTO "african_dev" VALUES(17,80,'17:80','. Era gamba nti ayi Mukama omulabirizi wange, nyingiza mu mulyango ogwa mazima era onfulumize mu mulyango ogwamazima, era ompe okuva gyoli obusobozi obunzirukirira.');
INSERT INTO "african_dev" VALUES(17,81,'17:81','. Era gamba nti amazima gazze obwonoonefu nebuvaawo, mazima obwonoonefu buba bwakuvaawo.');
INSERT INTO "african_dev" VALUES(17,82,'17:82','. Era tussa mu Kur’ani ebyo ebijja nga kya kuwonya era nga kya kusaasira eri abakkiriza. Wabula abeeyisa obubi tebongera okugyako okufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(17,83,'17:83','. Bwetuwa omuntu ebyengera (mu kifo ky''okwebaza) atuvaako neyeefaako yekka, so nga akabi bwe kamutuukako akutuka n''okusuubira.');
INSERT INTO "african_dev" VALUES(17,84,'17:84','. Bagambe nti buli omu akolere ku ngeriye, Mukama omulabirizi wa mmwe y''asinga okumanya, ali ku kkubo eddungamu.');
INSERT INTO "african_dev" VALUES(17,85,'17:85','. Era bakubuuza ku mwoyo, gamba nti omwoyo kye kimu ku bintu Mukama omulabirizi wange bye yeesigaliza, era temwaweebwa kumanya okugyako kutono.');
INSERT INTO "african_dev" VALUES(17,86,'17:86','. Era singa tuba twagadde tusobola okukuggyako ebyo bye twakutumira, ate toyinza kufuna olw''ebyo kuffe muwolereza.');
INSERT INTO "african_dev" VALUES(17,87,'17:87','. (Era wabisigaza ssi lwa nsonga ndala yonna) okugyako okusaasira okuva eri Mukama omulabiriziwo, anti mazima ebirungibye ku ggwe bisuffu.');
INSERT INTO "african_dev" VALUES(17,88,'17:88','. Gamba nti singa abantu n''a Majinni beegatta babe nga baleeta ekitabo ekifaanana Kur’ani eno, tebayinza kuleeta kigifaanana newaakubadde nga abamu babeera bayambi ku bannaabwe.');
INSERT INTO "african_dev" VALUES(17,89,'17:89','. Mazima twannyonnyola abantu mu Kur’ani muno buli kika kya kifaananyi, naye abantu abasinga obungi bagaana (okugyako okusigala) mu bukafiiri.');
INSERT INTO "african_dev" VALUES(17,90,'17:90','. Era nebagamba nti tetugenda kukkiriza okugyako nga otufukumulire amazzi okuva mu ttaka.');
INSERT INTO "african_dev" VALUES(17,91,'17:91','. Oba obe nga olina ennimiro erimu emitende ne Nzabibu. Olwo nno noofukumula emigga mu nnyiriri zaayo olufukumula.');
INSERT INTO "african_dev" VALUES(17,92,'17:92','. Oba otusuuleko eggulu nga bwogamba, nga liri mu bibajjo, oba oleete Katonda ne ba Malayika tubalabe maaso ku maaso.');
INSERT INTO "african_dev" VALUES(17,93,'17:93','. Oba obe n’enyumba nga yazimbwa mu Zaabu, oba olinnye mu ggulu era tetujja kukkiriza kulinnya kwo okugyako nga otuleetedde ekitabo netukisoma. Gamba ggwe (Muhammad) Mukama omulabirizi wange musukkulumu, nalimbaddeko (ekintu kyona) okugyako okubeera omuntu obuntu omubaka!.');
INSERT INTO "african_dev" VALUES(17,94,'17:94','. Tewali kyagaana bantu kukkiriza obulungamu bwe bwabajjira okugyako okuba nti bagamba nti omuntu obuntu Katonda gwatumye nga mubaka.');
INSERT INTO "african_dev" VALUES(17,95,'17:95','. Bagambe nti singa ensi yalimu ba Malayika nga batambula, nga mwe babeerera ddala, olwo nno twandibadde tubatumira Malayika okuva mu ggulu nga ye Mubaka.');
INSERT INTO "african_dev" VALUES(17,96,'17:96','. Bagambe nti Katonda amala okuba nga ye mujulizi wakati wange nammwe, anti mazima yye bulijjo amanyidde ddala era alaba ebikwata ku baddube.');
INSERT INTO "african_dev" VALUES(17,97,'17:97','. Oyo yenna Katonda gwalungamya, yye, yaaba omulungamu ate oyo gwaba abuzizza oba toyinza kubafunira bayambi batali yye, (Katonda) era tugenda kubazuukiza ku lunaku lw''enkomerero nga babutamidde ebyenyi bya bwe nga ba Muzibe ba Kasiru, ba Kiggala, obuddo bwa bwe buliba muliro Jahannama buli lwe gunaakenderanga nga tugubaseesezamu.');
INSERT INTO "african_dev" VALUES(17,98,'17:98','. Eyo y''empeera yaabwe, kubanga mazima bo baawakanya ebigambo byaffe, era ne bagamba nti bwe tuliba engumbagumba oba nga tufuuse ettaka, abaffe tulizuukizibwa netutondebwa bupya!.');
INSERT INTO "african_dev" VALUES(17,99,'17:99','. Abaffe tebalaba nti mazima Katonda oyo eyatonda eggulu omusanvu n''ensi asobola okuddamu okubatonda nga bwe baali, era nti yabassaako ekkomo mu bulamu bwa bwe eritaliimu kubuusabuusa, ate olwo nno abeeyisa obubi bagaana (buli kintu) okugyako (okukalambira) ku bukaafiiri.');
INSERT INTO "african_dev" VALUES(17,100,'17:100','. Gamba ggwe (Muhammad) nti singa mmwe mufuga amawanika g''okusaasira kwa Mukama omulabirizi wange, mwandibadde mukwatirira olw''okutya okusaasaanya bulijjo omuntu muganiriza.');
INSERT INTO "african_dev" VALUES(17,101,'17:101','. Mazima twawa Musa eby''amagero mwenda ebyeyolefu, buuza abaana ba Israil (ebyaliwo) mu kiseera weyabajjira (aba Misiri), Firawo naamugamba nti mazima nze Musa nkulabira ddala nga ggwe oli mulogo.');
INSERT INTO "african_dev" VALUES(17,102,'17:102','. (Musa) naagamba nti mazima omanyi ggwe (Firawo) tewali assizza (by''amagero) ebyo okugyako Mukama omulabirizi we ggulu omusanvu ne nsi, abireese nga bujulizi (okulaga obutuufu bwe bigambo byange) era mazima nze nkulabira ddala ggwe Firawo nga ozikiridde.');
INSERT INTO "african_dev" VALUES(17,103,'17:103','. Olwo nno (Firawo) yayagala okubagoba mu nsi (Misiri), twamuzikiriza n''abo abaali naye.');
INSERT INTO "african_dev" VALUES(17,104,'17:104','. Era oluvanyuma lw''ekyo twagamba abaana ba Israil mutuule mu nsi (eye Sham), endagaano y''olunaku lw''enkomerero bwelituuka mwenna tugenda kubaleeta nga tubakungaanya.');
INSERT INTO "african_dev" VALUES(17,105,'17:105','. (Kur’ani eno) twagissa na mazima era yaleeta mazima era tetwakutuma okugyako lwa kuba nga owa amawulire ag''essanyu era nga oli mutiisa.');
INSERT INTO "african_dev" VALUES(17,106,'17:106','. Era Kur’ani twagyawulamu ebitundu obe nga ogisomere abantu mu ngeri y''empola mpola era twagissa olussa (olw''ebitundu tundu).');
INSERT INTO "african_dev" VALUES(17,107,'17:107','. Gamba nti kamugikkirize oba temugikkiriza (kasita) mazima abo abaaweebwa okumanya nga tonajja, bwe basomerwa bagwira ku bulevu bwa bwe nga bavunnama.');
INSERT INTO "african_dev" VALUES(17,108,'17:108','. Era nga bagamba nti Mukama omulabirizi waffe musukkulumu, endagaano ya Mukama omulabirizi waffe bulijjo etuukirizibwa.');
INSERT INTO "african_dev" VALUES(17,109,'17:109','. Era bagwira ku bulevu (bwa bwe) nga bakaaba, ekibongera okugonda.');
INSERT INTO "african_dev" VALUES(17,110,'17:110','. Bagambe nti musabe Katonda oba musabe omusaasizi, erinnya (lye) mukozesa nga mumusaba likola, anti alina amannya agasinga obulungi, toleekaana nga osaba era tossa nnyo ddoboozi lyo nga osaba, wakati w''ebyo wooba onoonya enkolayo.');
INSERT INTO "african_dev" VALUES(17,111,'17:111','. Era gamba nti ebitendo byonna bya Katonda oyo ateeteerawo mwana, era atabangako na muyambi mu bufuzibwe, era tabangako na mutaasa mbu amuwonye okuswala olw''ebyandimulemye, era mugulumize olugulumiza.');
INSERT INTO "african_dev" VALUES(18,1,'18:1','. Ebitendo byonna bya Katonda oyo eyassa ku mudduwe (Muhammad) ekitabo (Kur’ani) naatakiteekamu bukyamu bwonna.');
INSERT INTO "african_dev" VALUES(18,2,'18:2','. Wabula nga kyesimbu abe nga atiisa nakyo ebibonerezo ebikakali ebiva gyali era awe nakyo amawulire ag''essanyu abakkiriza abo abakola emirimu emirongoofu (nga abategeeza) nti mazima balina empeera ennungi.');
INSERT INTO "african_dev" VALUES(18,3,'18:3','. Baakugibeeramu olubeerera.');
INSERT INTO "african_dev" VALUES(18,4,'18:4','. Era atiise abo abagamba nti Katonda yeeteerawo omwana.');
INSERT INTO "african_dev" VALUES(18,5,'18:5','. Kye batalinaako kumanya kwonna wadde bakadde baabwe, kibi nnyo ekigambo ekiva mu mimwa gya bwe. Bye boogera temuli okugyako bulimba bwereere.');
INSERT INTO "african_dev" VALUES(18,6,'18:6','. Olw''okunnyolwa, olabika nga obula kwetta ku lwa bwe olw''okuba tebakkiriza bigambo bino.');
INSERT INTO "african_dev" VALUES(18,7,'18:7','. Mazima ffe twassa ku nsi ebigiriko nga bya kuginyiriza olwo nno tubagezese ani asinga okukola ebirungi.');
INSERT INTO "african_dev" VALUES(18,8,'18:8','. Mazima ffe tugenda kufuula ebigiriko ettaka ekkalu.');
INSERT INTO "african_dev" VALUES(18,9,'18:9','. Oba olowooza nti ddala abantu bo mu mpuku, n''ekiwandiiko ekibakwatako baali baakyewunyo nnyo okusinga eby''amagero byaffe ebirala.');
INSERT INTO "african_dev" VALUES(18,10,'18:10','. Mu kiseera abavubuka bwe beewogoma mu mpuku nebagamba nti ayi Mukama omulabirizi waffe tuwe okusaasira okuva gyoli era otwanguyize ensonga yaffe gye tuliko otuwe obulungamu.');
INSERT INTO "african_dev" VALUES(18,11,'18:11','. Netukuba ku matu gaabwe (ekibikka) nga bali mu mpuku (nebeebaka) emyaka mingi.');
INSERT INTO "african_dev" VALUES(18,12,'18:12','. Oluvanyuma twabazuukusa kimanyike nti, kiriwa ku bibiina ebibiri ekisinga okwogera ekituufu ku bbanga lye baamalamu.');
INSERT INTO "african_dev" VALUES(18,13,'18:13','. Mazima ffe tukunyumiza ekyafaayo kya bwe mu butuufu, mazima bo baali bavubuka abakkiriza Mukama omulabirizi waabwe era netubongera obulungamu.');
INSERT INTO "african_dev" VALUES(18,14,'18:14','. Era twasiba ekimyu ku mitima gyabwe bwe baayimirira (mu buvumu) nebagamba nti Mukama omulabirizi waffe ye Mukama omulabirizi w''eggulu omusanvu n''ensi, n''olwekyo tetuyinza ku mulekawo ne tusaba Katonda mulala yenna era bwe tukikola mazima tuba twogedde ekigambo ekibi.');
INSERT INTO "african_dev" VALUES(18,15,'18:15','. Abo abantu baffe baamulekawo (Katonda) nebeeteerawo ba katonda abalala, kale nno singa babaleetako obujulizi obweyolefu, naye ani eyeeyisa obubi okusinga oyo agunja ku Katonda ebigambo eby''obulimba.');
INSERT INTO "african_dev" VALUES(18,16,'18:16','. Bwe mwabaawukanako nemwawukana ne ku bye basinza, nga oggyeko Katonda (Omu) olwo nno mugende mu mpuku Mukama omulabirizi wa mmwe ajja kubanjuliriza okumu ku kusaasirakwe era ajja kubateerawo okulumirwa ekigambo kya mmwe.');
INSERT INTO "african_dev" VALUES(18,17,'18:17','. Awo nno walabanga enjuba bwe yavangayo nga ekyuka nga eva ku mukono ogwa ddyo ogw''e mpuku yaabwe, ate nga bwegwa nga ebaleka ku mukono ogwa kkono nebaba nga beetaayiza mu bugazi bw''empuku ekyo kamu ku bubonero bwa Katonda (ku buyinza bwe nobusobozibwe) oyo yenna Katonda gwalungamya yye yemulungamu ate oyo gwaba abuzizza toyinza kumufunira muyambi amulungamya.');
INSERT INTO "african_dev" VALUES(18,18,'18:18','. Era (bwe wabalaba nga) nga osuubira nti tebeebase so nga beebase nga tubakyusa ku ddyo ne ku kkono nga n''embwa yaabwe egolodde emikono gyayo nga eri ku mulyango (gw''empuku) nga singa obakubako eriiso waalikyuse noobadduka era wandijjudde okubatya.');
INSERT INTO "african_dev" VALUES(18,19,'18:19','. Era bwe tutyo twabazuukusa babe nga beebuuzaganya wakati waabwe (ku kyabatuukako) omwogezi omu ku bo naagamba nti wano mumazeewo bbangaki? nebagamba nti tumazeewo lunaku oba kitundu kya lunaku nebagamba nti Mukama omulabirizi wa mmwe yaasinga okumanya ebbanga lye mumazeewo kale mutume omu ku mmwe, agende mu kibuga ne Sente zammwe zino alabe mmere ki esinga obulungi eriyo abaleetereyo ku yo eky''okulya, wabula atambule nga yeegendereza tewali muntu n''omu asaanidde kubamanya.');
INSERT INTO "african_dev" VALUES(18,20,'18:20','. Anti mazima bo bwe banaabazuula bajja kubakuba amayinja oba ssi ekyo babazzeeyo mu ddiini yaabwe olwo nno temugenda kwesiima olubeerera.');
INSERT INTO "african_dev" VALUES(18,21,'18:21','. Nabwetutyo twasobozesa abantu okubazuula olwo nno bamanye nti mazima endagaano ya Katonda ya mazima, era bamanye nti mazima essaawa (esembayo) teriimu kubuusabuusa. (Era) jjukira (abakaafiiri ) bwe baakaayana ku nsonga yaabwe (abamu), nebagamba nti mubazimbeko ekizimbe, Mukama omulabirizi waabwe y''asinga okumanya ebibakwatako, ate abalina ekigambo ekisinga obulungi nebagamba nti tujja kubazimbako omuzikiti.');
INSERT INTO "african_dev" VALUES(18,22,'18:22','. (Abantu abamu) bajja kugamba nti baali basatu, nga embwa yaabwe yaakuna, abamu bagambe nti baali bataano nga embwa yaabwe yamukaaga nga kujweteka ku byebatamanyi. Abalala bajja kugamba nti baali musanvu nga embwa yaabwe ya munaana gamba nti Mukama omulabirizi wange y''asinga okumanya omuwendo gwabwe, tewali abamanyi okugyako bantu batono, n''olwekyo towakana ku bibakwatako okugyako okuwakana okwokungulu era tewebuuzanga ku muntu yenna ku nsonga ebakwatako.');
INSERT INTO "african_dev" VALUES(18,23,'18:23','. Era togambanga ku kintu kyonna, nti mazima nze ngenda kukikola enkya.');
INSERT INTO "african_dev" VALUES(18,24,'18:24','. Okugyako nga ogasseeko nti Katonda bwanaaba ayagadde, era yogera ku Mukama omulabiriziwo wooba nga weerabidde, era gamba nti nsuubira nti Mukama omulabirizi wange ajja ku nnungamya eri obujulizi obusinga ku buno, (eky''abavubuka bo mu mpuku okulaga obwa Nabbi bwange).');
INSERT INTO "african_dev" VALUES(18,25,'18:25','. Era baatuula mu mpuku yaabwe emyaka bikumi bisatu (ku mbala ey''enjuba) ate nebeeyongera mu mwenda (kumbala ey''omwezi).');
INSERT INTO "african_dev" VALUES(18,26,'18:26','. Gamba nti Katonda y''asinga okumanya ebbanga lye baamalamu, yekka y''amanyi ebyekusifu by''omu ggulu omusanvu n''ensi, weewuunye okulaba kwe n''okuwulira kwe (okuba nti byetoolodde buli kintu, n''olwekyo) tebalina nga oggyeko yye (Katonda) mutaasa. Era mu kusalawo kwe teyeegatta na muntu mulala yenna ( wabula bulijjo asalawo yekka).');
INSERT INTO "african_dev" VALUES(18,27,'18:27','. Era soma ebyo ebyassibwa gyoli nga bya mu kitabo kya Mukama omulabiriziwo tewali akyusa bigambobye era togenda kufuna buddukiro etali wuwe.');
INSERT INTO "african_dev" VALUES(18,28,'18:28','. Wenyweze nga obeera wamu n''abo abasaba Mukama Omulabirizi waabwe enkya n''olweggulo nga baluubirira kuba ku luddalwe, tobaggyako amaaso (nootunuulira babinojjo) nga ogenderera kufuna eby''okwewunda by''obulamu bwensi, era togondera oyo yenna gwe twagayaaza omutimagwe naatatwogerako naagoberera okwagalakwe embeera ye yonna neeba bwonoonyi.');
INSERT INTO "african_dev" VALUES(18,29,'18:29','. Gamba nti gano ge mazima agavudde ewa Mukama omulabirizi wa mmwe kale ayagala akkirize n''anayagala akaafuwale, mazima ffe abeeyisa obubi twabateekerateekera omuliro, lugenda kubeetoolola olugo lwa gwo, buli lwe banaasabanga okuweebwa amazzi balinywesebwa amazzi agalinga olutabu lw''ekyuma olulyoka ebyenyi. Kiriba kya kunywa kibi era nga n''obutuulo buliba bubi.');
INSERT INTO "african_dev" VALUES(18,30,'18:30','. Mazima abo abakkiriza nebakola emirimu emirongoofu mazima ffe tetugenda butasasula mpeera y''oyo eyalongoosa emirimu.');
INSERT INTO "african_dev" VALUES(18,31,'18:31','. Abo nno bagenda kufuna e jjana ey''olubeerera nga emigga gikulukutira wansi waabwe. nga bali mu yo baliba beesigamye, nga batudde ku ntebe ensitufu, eyo nno mpeera nnungi era nga n''obutuulo buliba bulungi.');
INSERT INTO "african_dev" VALUES(18,32,'18:32','. Era bakubire ekifaananyi ky''abasajja babiri nga omu ku bo twamuwa ennimiro bbiri eze Nzabibu zetwassaako olukomera olw''emitende, ate wakati waazo netussaamu ebirime ebirala.');
INSERT INTO "african_dev" VALUES(18,33,'18:33','. Ennimiro zombi zaabala bulungi, tewali katundu kataabala, era nga twassa wakati waazo emigga.');
INSERT INTO "african_dev" VALUES(18,34,'18:34','. (Omu ku basajja ababiri) naaba nga afunye amakungula (amalungi) naagamba munne nga anyumya naye nti nze nkusinza emmaali era nga bwe nkusinza abantu.');
INSERT INTO "african_dev" VALUES(18,35,'18:35','. Olwo nno naayingira ennimiro yye nga yeeyisizza bubi yennyini, naagamba nti sirowooza nti bino biyinza okuggwawo.');
INSERT INTO "african_dev" VALUES(18,36,'18:36','. Era nga bwe ssirowooza nti essaawa ey''enkomerero egenda kubaawo (katugambe nti kibaddewo) bwe ndizzibwa ewa Mukama omulabirizi wange ngenda kufuna ebisinga obulungi ku bino.');
INSERT INTO "african_dev" VALUES(18,37,'18:37','. Munne naamuddamu nga amugamba nti owakanyizza oyo eyakutonda nga akuggya mu ttaka oluvanyuma naakuggya mu mazzi agazaala oluvanyuma naakukola nga oli Muntu omujjuvu.');
INSERT INTO "african_dev" VALUES(18,38,'18:38','. Naye nze ngamba nti mazima yye Katonda ye Mukama omulabirizi wange era siyinza kugatta ku Mukama Mulabirizi wange mulala yenna.');
INSERT INTO "african_dev" VALUES(18,39,'18:39','. Kale singa ggwe bwe wabadde oyingira ennimiroyo wagambye nti: ekyo kyonna Katonda kyaba ayagadde kyekiba, tewali maanyi okugyako nga Katonda y''agagabye, newaakubadde nga ondaba nga nze nnina emmaali ntono n''abaana batono obutenkana ggwe.');
INSERT INTO "african_dev" VALUES(18,40,'18:40','. Nina essuubi nti Mukama omulabirizi wange lumu alimpa ebirungi okusinga ennimiroyo ate naasindikira eyiyo kibuyaga okuva waggulu neekeesa enkya ttaka jjereere.');
INSERT INTO "african_dev" VALUES(18,41,'18:41','. Oba amazzi gaamu negafuuka gakkekwa n''oba nga tosobola kugazuula.');
INSERT INTO "african_dev" VALUES(18,42,'18:42','. Era ebibalabye byatuukwako obuzibu, nebukya nga akuba mu bibatubye olw''okufiirwa ebyo bye yassaamu, nga alaba ennimiro ebutamidde ku birimba byayo naagamba nti: zinsanze nze, singa saagatta kintu ku Mukama omulabirizi wange.');
INSERT INTO "african_dev" VALUES(18,43,'18:43','. Nga oggyeko Katonda yali tayinza kufuna kibinja kimutaasa nga naye bwe yali tasobola kwetaasa.');
INSERT INTO "african_dev" VALUES(18,44,'18:44','. Awo obwesige bwonna bwalina kubeera mu Katonda ow''amazima yekka, anti bulijjo yye yaasinga empeera ennungi, era y''alina enkomerero esinga obulungi.');
INSERT INTO "african_dev" VALUES(18,45,'18:45','. Era bakubire ekifaananyi eky''obulamu obw''ensi bulinga amazzi getutonnyesa okuva waggulu, nebyetabula nago ebimera byo ku nsi (olwo nno) nebikula nebikungulwa nebifuuka ebisusunku nga empewo ebikunguzza. Bulijjo Katonda ku buli kintu muyinza.');
INSERT INTO "african_dev" VALUES(18,46,'18:46','. Emmaali n''abaana byakwewunda bya bulamu bwansi, emirimu emirungi egisigalawo gye gisinga obulungi ewa Mukama omulabiriziwo, okuba nga ye mpeera era nga lye ssuubi erisinga obulungi.');
INSERT INTO "african_dev" VALUES(18,47,'18:47','. Era (fumiitiriza ku) lunaku lwe tulitambuza ensozi olwo nno oliraba ensi nga njereere era tulibakunganya bonna, tetugenda kusubwa n''omu yenna.');
INSERT INTO "african_dev" VALUES(18,48,'18:48','. Olwo nno balyanjulwa ewa Mukama Omulabiriziwo nga bali mu nnyiriri (Katonda aligamba nti) mazima muzze gye tuli nga bwe twabatonda ku mulundi ogwasooka naye mwalowooza nti tetwali baakubateerawo kiseera kigere.');
INSERT INTO "african_dev" VALUES(18,49,'18:49','. Ekitabo kya buli omu kigenda kuleetebwa olwo nno noolaba aboonoonyi nga batya olw''ebyo ebikirimu, baligamba nti: nga tulabye kitabo kya ngeriki kino! Tekirekaayo katono oba kinene okugyako nga kikikomekkereza, bagenda kusanga nga buli kye baakola weekiri. Mukama omulabiriziwo tagenda kulyazaamaanya muntu yenna.');
INSERT INTO "african_dev" VALUES(18,50,'18:50','. Era jjukira bwe twagamba ba Malayika nti muvunnamire Adam nebamuvunnamira okugyako Ibuliisu (Sitane) eyali owo mu Majinni olwo nno naayawukana ku kiragiro kya Mukama omulabiriziwe, abaffe ate mmwe oyo gwe mufuula n''ezaddelye abataasa ba mmwe (nze) Katonda nemunvaako ate nga bo balabe ba mmwe, nga okwo kuwanyisiganya kubi abeeyisa obubi kwebakola.');
INSERT INTO "african_dev" VALUES(18,51,'18:51','. Sibayitanga kubaawo nga eggulu omusanvu ne nsi bitondebwa wadde okutondebwa kwabwe era sibangako waakweteerawo ababuza nga be bayambi (bange).');
INSERT INTO "african_dev" VALUES(18,52,'18:52','. Era (mufumiitirize) olunaku (Katonda) lwalibagamba nti muyite be mwangattako abo be mwagambanga (nti nabo ba katonda) olwo nno bali bayita nebatabaanukula era netussa wakati waabwe ekibaawula.');
INSERT INTO "african_dev" VALUES(18,53,'18:53','. Aboonoonyi baliraba omuliro, olwo nno bagenda kumanyira ddala nti mazima bo baakugugwamu era tebagenda kufuna buwonero bwonna ku gwo.');
INSERT INTO "african_dev" VALUES(18,54,'18:54','. Mazima tunnyonnyodde abantu mu Kur’ani eno (nga tukozesa) buli kifaananyi mazima omuntu y''asinga ebintu byonna okuwalaaza empaka.');
INSERT INTO "african_dev" VALUES(18,55,'18:55','. Tewali kyagaana bantu kukkiriza obulungamu bwe bwabajjira, era teri kyabagaana kwegayirira Mukama omulabirizi waabwe, mpozzi nga baali balindirira kujjirwa ekyo ekyatuuka ku baasooka oba ebibonerezo bibajjire babirabe lwatu.');
INSERT INTO "african_dev" VALUES(18,56,'18:56','. Era tetwatuma babaka okugyako lwa kuwa mawulire agasanyusa (eri abakozi b''obulungi), era nga bawa amawulire agatiisa (eri abakozi b’obubi), abo abaakaafuwala bawakana mu bukyamu nga bagenderera okugyawo nabwo amazima, ebigambo byange (n''ebintu ebirala) bye batiisibwa nabyo nebabifuula eby''olusaago.');
INSERT INTO "african_dev" VALUES(18,57,'18:57','. Ani eyeeyisa obubi okusinga oyo ajjukizibwa ebigambo bya Mukama omulabiriziwe naabivaako, neyeerabira ebyo emikono gye bye gyakulembeza (okukola), mazima ffe twassa ku mitima gya bwe ekibikka, babe nga tebabitegeera ne mu matu gaabwe mulimu envumbo era nebwobayita okujja eri obulungamu tebagenda kulungama olubeerera bwe baba bwe batyo.');
INSERT INTO "african_dev" VALUES(18,58,'18:58','. Ne Mukama omulabiriziwo omusonyiyi ennyo nannyini kusaasira, singa abadde waakuvunaanirawo olw''ebyo bye bakola yandibatuusizzaako ekibonerezo mu bwangu, wabula balina ekiseera ekigere (bwe kirituuka) tebagenda kufuna buddukiro babe nga bakiwona.');
INSERT INTO "african_dev" VALUES(18,59,'18:59','. Ebitundu ebyo (bye muliraanye, abantu bamu) twabazikiriza bwe beeyisa obubi era okuzikirira kwa bwe twakuwa ekiseera kwe mwe kwalina okutuukira.');
INSERT INTO "african_dev" VALUES(18,60,'18:60','. Era jjukira Musa bwe yagamba omuweerezawe nti sijja kulekeraawo kutambula okutuusa lwe tunaatuuka enyanja ebbiri wezisisinkanira oba mmala byeya (kale mbimale).');
INSERT INTO "african_dev" VALUES(18,61,'18:61','. Olwo nno bwe baatuuka bombi ekifo wezisisinkanira beerabira ekyenyanja kyabwe nekikola ekkubo lyakyo munyanja nga kireseewo omuwulukwa.');
INSERT INTO "african_dev" VALUES(18,62,'18:62','. Bwe baayita ku kifo ekyo (Musa) yagamba omuweerezawe nti leeta eno eky''okulya kyaffe mazima olugendo lwaffe luno tulusanzeemu obukalubo.');
INSERT INTO "african_dev" VALUES(18,63,'18:63','. (Omuweerezawe) naagamba nti ojjukira wetwawummulidde ku lwazi, mazima nze neerabidde ekyenyanja tewali kyanneerabizza kukijjukira okugyako Sitane era awo wekyakoledde ekkubo mu nnyanja mu ngeri eyeewuunyisa.');
INSERT INTO "african_dev" VALUES(18,64,'18:64','. (Musa) naagamba nti ekyo kyennyini kye tubadde tunoonya, olwo nno nebadda emabega nga bwe bazze nga banoonya ekifo (ekyo).');
INSERT INTO "african_dev" VALUES(18,65,'18:65','. Nebasanga omuddu mu baddu baffe gwe twawa okusaasira okuva gye tuli, era netumuyigiriza okuva gye tuli okumanya (okw''enjawulo).');
INSERT INTO "african_dev" VALUES(18,66,'18:66','. Musa naamugamba nti abaffe (onzikiriza) nkugoberere olwo nno obe nga onjigiriza ku bulungamu bwe wayigirizibwa?.');
INSERT INTO "african_dev" VALUES(18,67,'18:67','. (Hidhiri) naamuddamu nti mazima ggwe tojja kusobola kugumiikiriza nga oli nange.');
INSERT INTO "african_dev" VALUES(18,68,'18:68','. Onoogumiikiriza otya ku bintu byotalinaako kumanya.');
INSERT INTO "african_dev" VALUES(18,69,'18:69','. (Musa) naagamab nti Katonda bwanaaba akkirizza ojja ku nsanga nga ndi mugumiikiriza era sigenda kukujeemera mu kintu kyonna.');
INSERT INTO "african_dev" VALUES(18,70,'18:70','. (Hidhiri) naagamba nti bwoba osazeewo okungoberera, tombuuza ku kintu kyonna okutuusa lwe nnaakufunira kye nkikubuulirako.');
INSERT INTO "african_dev" VALUES(18,71,'18:71','. Olwo nno nebagenda, okutuusa nga bamaze okulinnya eryato (Hidhiri) naalissaako ekituli, (Musa) naagamba nti olitaddeko ekituli ozikirize abantu abalirimu mazima okoze ekintu kibi.');
INSERT INTO "african_dev" VALUES(18,72,'18:72','. (Hidhiri) naagamba nti: saakugambye nti mazima ggwe tojja kusobola kugumiikiriza nga oli nange.');
INSERT INTO "african_dev" VALUES(18,73,'18:73','. (Musa) naagamba nti tonvunaana olw''ekyo kye nneerabidde era tontuusaako olw''ekigambo kyange (eky''okubeera naawe) buzito bwonna.');
INSERT INTO "african_dev" VALUES(18,74,'18:74','. Olwo nno nebagenda okutuusa lwe baasanga omulenzi, (Hidhiri) naamutta (Musa) naagamba nti osse omuntu atalina musango, atalina gwasse, mazima ekintu kyokoze kivve.');
INSERT INTO "african_dev" VALUES(18,75,'18:75','. (Hidhiri) naagamba nti ssaakugambye nti mazima ggwe tojja kusobola kugumiikiriza nga oli nange.');
INSERT INTO "african_dev" VALUES(18,76,'18:76','. (Musa) naagamba nti kenkubuuza ku kintu ekirala kyonna oluvanyuma lwa kino tonzikiriza kubeera naawe, mazima ofunye ensonga ekusonyiyisa ku nze.');
INSERT INTO "african_dev" VALUES(18,77,'18:77','. Nebagenda bombi okutuusa lwe batuuka ku bantu b''omukitundu, nebasaba abantu baamu okubawa emmere naye baagaana okubafuula abagenyi baabwe, wabula baasanga mu kyo (ekitundu) ekisenge nga kyagala kugwa (Hidhiri) naakiddaabiriza (Musa) naagamba nti singa oyagadde waalikisabiddeko empeera (noosasulwa).');
INSERT INTO "african_dev" VALUES(18,78,'18:78','. (Hidhiri) naagamba nti: wano nze naawe wetwawukanidde, (wabula) ngenda kukubuulira amakulu g''ebyo byotaasobodde kugumiikiriza.');
INSERT INTO "african_dev" VALUES(18,79,'18:79','. Lyo eryato lyabadde, lya ba Masikiini nga bapakasiza ku nnyanja, nagenderedde okulissaako akamogo, anti, mu maaso eyo waabaddeyo Kabaka nga atwala buli lyato eddungi mu ngeri ya bunyazi.');
INSERT INTO "african_dev" VALUES(18,80,'18:80','. Ate yye omulenzi bakaddebe bombi baali bakkiriza, netutya yye (omulenzi) okubatuusaako obuzibu bwokubula n''okukaafuwala.');
INSERT INTO "african_dev" VALUES(18,81,'18:81','. Olwo nno twayagadde Mukama Omulabirizi wa bombi abawaanyisizeemu bombi ekirungi okusinga yye mubutukuvu, era asinga okuba omusaasizi.');
INSERT INTO "african_dev" VALUES(18,82,'18:82','. Ate kyo ekisenge kyabadde kya balenzi babiri bamulekwa mu kibuga (ekyo), wansi waakyo waabaddeyo eby''ogugagga bya bombi era nga Kitaabwe wa bombi yali mulongoofu, olwo nno Mukama omulabiriziwo yayagala bombi okumala okusajjakula, olwo nno baggyeyo eby''obugagga byabwe bombi, ekyo nga kusaasira okuva eri Mukama Omulabiriziwo, era ssaakikoze lwa kusalawo kwange (wabula) bubaka obwavudde eri (Katonda), okwo nno kwe kunnyonnyola byotasobodde kugumiikiriza.');
INSERT INTO "african_dev" VALUES(18,83,'18:83','. Bakubuuza ebikwata ku Dhul Karinaini, bagambe nti nja kubayitiramu ku bimukwatako.');
INSERT INTO "african_dev" VALUES(18,84,'18:84','. Mazima ffe twamuwa obusobozi mu nsi, era twamuwa ku buli kintu okumanya engeri gye kikolebwamu.');
INSERT INTO "african_dev" VALUES(18,85,'18:85','. Olwo nno naagoberera enkola (eyo).');
INSERT INTO "african_dev" VALUES(18,86,'18:86','. Okutuusa lwe yatuuka enjuba gyegwa, naagisanga nga erabika nga egwa mu luzzi olw''ettosi, era mu kitundu ekyo naasangayo abantu, netumugamba nti owange Dhul Karinaini, bwoba oyagadde osobola okubabonereza oba oyinza okubakolamu ekirungi.');
INSERT INTO "african_dev" VALUES(18,87,'18:87','. (Dhul Karinaini) naagamba nti oyo aneeyisa obubi tujja kumubonereza, ate oluvanyuma alizzibwa ewa Mukama Omulabirizewe, naamubonereza ebibonerezo ebisuffu.');
INSERT INTO "african_dev" VALUES(18,88,'18:88','. Naye oyo akkiriza naakola emirimu emirungi, agenda kusasulwa empeera ennungi era tugenda kumugamba ebigambo ebyangu okuva gye tuli.');
INSERT INTO "african_dev" VALUES(18,89,'18:89','. Ate oluvanyuma naagoberera enkola eyo.');
INSERT INTO "african_dev" VALUES(18,90,'18:90','. Okutuusa lwe yatuuka enjuba gyeva naagisanga nga eviirayo ku bantu abo betutaawa kya kwebikka kuwona kwokya kwayo.');
INSERT INTO "african_dev" VALUES(18,91,'18:91','. Bwe tutyo bwe twakikola ate mazima byonna bye yalina, okumanya kwaffe kwali kubyetoolodde.');
INSERT INTO "african_dev" VALUES(18,92,'18:92','. Ate oluvanyuma bwe yamala naagoberera enkola y''emu.');
INSERT INTO "african_dev" VALUES(18,93,'18:93','. Okutuusa lwe yatuuka wakati w''ensozi ebbiri, naasanga wakati wa zombiriri abantu, kumpi nga tebategeera kigambo kyonna.');
INSERT INTO "african_dev" VALUES(18,94,'18:94','. Nebagamba nti owange Dhul Karinaini mazima Yajuuja ne Majuuja boonoonyi mu nsi abaffe tukuteerewo empeera (tukupakase) obe nga otuteera wakati waffe nabo ekikomera.');
INSERT INTO "african_dev" VALUES(18,95,'18:95','. Naagamba nti ekyo kyonna Mukama Omulabirizi wange kyanansobozesa kinaaba kirungi, kale munyambe n''amaanyi nja kuteeka wakati wa mmwe nabo ekikomera (ekigumu).');
INSERT INTO "african_dev" VALUES(18,96,'18:96','. Mundeetere ebitundu tundu bye byuma (bye baaleeta) okutuusa lwe yajjuza ebyuma mu bbanga eriri wakati w''ensozi ebbiri, naagamba nti mufuuwe (emivubo) okutuusa ekyuma lwe yakifuula omuliro naagamba nti mundeetere ekikomo ekisaanuuse naakifukirirako.');
INSERT INTO "african_dev" VALUES(18,97,'18:97','. (Yajuuja ne Majuuja) tebasobolangako kukirinya era nga bwe batasobolangako kukiwummulamu kituli.');
INSERT INTO "african_dev" VALUES(18,98,'18:98','. (Dhul Karinaini) naagamba nti kuno kusaasira okuva eri Mukama Omulabirizi wange, n''olwekyo endagaano ya Mukama Omulabirizi wange bwelituuka, agenda kukifuula ekimerengufu, anti bulijjo endagaano ya Mukama omulabirizi wange ya mazima.');
INSERT INTO "african_dev" VALUES(18,99,'18:99','. Tulireka abamu ku bo nebaba nga beekuluumulira mu bannaabwe nga balinga amayengo. Era engombe egenda kufuuyibwa olwo nno bonna tubakunganye.');
INSERT INTO "african_dev" VALUES(18,100,'18:100','. Ku lunaku olwo tugenda kusoosootola omuliro Jahannama eri abakaafiiri olusoosootola.');
INSERT INTO "african_dev" VALUES(18,101,'18:101','. Abo amaaso gaabwe agaali mu kubikkibwa nebatosobola kunjogerako era baali tebasobola kuwulira.');
INSERT INTO "african_dev" VALUES(18,102,'18:102','. Abaffe abo abakaafuwala basuubira okufuula abaddu bange abakuumi baabwe nze nebandekawo, nze nebansuulirira, mazima ffe twateekateeka Jahannama nga bwe butuulo bwa bakaafiiri.');
INSERT INTO "african_dev" VALUES(18,103,'18:103','. Abaffe tubabuulire abasinga okufaafaganirwa emirimu gya bwe.');
INSERT INTO "african_dev" VALUES(18,104,'18:104','. Beebo okukola kwa bwe okubeera okwonoonefu mu bulamu obw''ensi, so nga ate bo balowooza nti bakola bulungi.');
INSERT INTO "african_dev" VALUES(18,105,'18:105','. Abo beebo abawakanya ebigambo bya Mukama omulabirizi waabwe, era nebawakanya okumusisinkana, olwo nno emirimu gya bwe negifaafaagana, n''olwekyo ku lunaku lw''enkomerero, tetugenda kubalabamu buzito bwonna.');
INSERT INTO "african_dev" VALUES(18,106,'18:106','. Bwe kityo nno empeera yaabwe muliro Jahannama olw''ebyo bye baawakanya era nebafuula obubonero bwange n''ababaka bange ekisekererwa.');
INSERT INTO "african_dev" VALUES(18,107,'18:107','. Mazima abo abakkiriza era nebakola emirimu emirongoofu, e jjana eza Firidausi, bwe buliba obutuulo bwa bwe.');
INSERT INTO "african_dev" VALUES(18,108,'18:108','. Bakuzibeeramu olubeerera tebagenda kwetaaga kukyusibwa kuva mu zo.');
INSERT INTO "african_dev" VALUES(18,109,'18:109','. Gamba nti singa ennyanja zaali nga ye bwino (akozesebwa mu kuwandiika) ebigambo bya Mukama omulabirizi wange, ennyanja zandikalidde, nga ebigambo bya Mukama omulabirizi wange tebinnagwayo, nebwetandireese ennyanja ezenkana eziriwo zibe nga ziziyamba.');
INSERT INTO "african_dev" VALUES(18,110,'18:110','. Bagambe nti mazima nze ndi muntu nga mmwe, (wabula) nfuna obubaka nga bugamba nti mazima Katonda wa mmwe ali omu, kale nno, buli yenna asuubira okusisinkana Mukama omulabiriziwe ateekwa okukola emirimu emirungi era mu kusinza n’atagatta kintu kyonna ku Mukama Omulabiriziwe.');
INSERT INTO "african_dev" VALUES(19,1,'19:1','. Kaaf Haa Yaa Ain Swad.');
INSERT INTO "african_dev" VALUES(19,2,'19:2','. Okwogera ku kusaasira kwa Mukama Omulabiriziwo kwe yawa omudduwe Zakaria.');
INSERT INTO "african_dev" VALUES(19,3,'19:3','. Mu kiseera bwe yasaba Mukama Omulabiriziwe okusaba okw''ekyama.');
INSERT INTO "african_dev" VALUES(19,4,'19:4','. Naagamba nti ayi Mukama omulabirizi wange, mazima nze amagumba gannyenjebuse, n''omutwe gubunye envi, ate nga ssirekererwangako ayi Mukama Omulabirizi wange buli lwe nkusaba.');
INSERT INTO "african_dev" VALUES(19,5,'19:5','. Era mazima nze, nneekengedde ab''omusaayi gwange (bayinza okweyisa obubi) oluvanyuma lwange, ate nga ne Mukyala wange mugumba awo nno nkusaba ompe okuva gyoli omwana.');
INSERT INTO "african_dev" VALUES(19,6,'19:6','. Anaansikira, era asikire ab''olulyo lwa Yakubu, era omufuule asiimibwa ayi Mukama Omulabirizi wange.');
INSERT INTO "african_dev" VALUES(19,7,'19:7','. (Naayanukulwa nti) owange Zakaria mazima ffe tukuwa amawulire ag''essanyu nti ogenda kufuna omwana omulenzi, nga erinya lye ye Yahaya (erinnya) lye tutassangawo oluberyeberye aliyitibwa.');
INSERT INTO "african_dev" VALUES(19,8,'19:8','. (Zakaria) naagamba nti omwana omulenzi (oyo) nnaamufuna ntya, nga Mukyala wange mugumba nga n’olwo bukadde nfuuse mugogge!.');
INSERT INTO "african_dev" VALUES(19,9,'19:9','. (Malayika) naagamba nti (bwe kityo bwe kirina okuba), Mukama Omulabiriziwo agambye nti: ekyo ku nze kyangu era mazima nakutonda nga mu kusooka tewali kintu kyonna.');
INSERT INTO "african_dev" VALUES(19,10,'19:10','. (Zakaria) naagamba nti ayi Mukama Omulabirizi wange nteeraawo akabonero (akanaalaga nti omukyala ali lubuto) naamugamba nti bwe butayogera na bantu okumala ebiro bisatu ebijjudde (awamu n''okuba nga oli mulamu).');
INSERT INTO "african_dev" VALUES(19,11,'19:11','. Awo nno yafuluma, naasisinkana abantu be nga ava mu ssinzizo naabalagira (nga tayogera, wabula nga akozesa bubonero) nti muteendereze, (Katonda) enkya n''olweggulo.');
INSERT INTO "african_dev" VALUES(19,12,'19:12','. (Yahaya naalagirwa nti) owange Yahaya ekitabo (Tawurat) kikwate na maanyi, era twamuwa okumanya ensonga nga akyali muto.');
INSERT INTO "african_dev" VALUES(19,13,'19:13','. Era twamussaawo nga kulumirwa (bantu) okuva gye tuli era nga butukuvu, era yali mutya Katonda.');
INSERT INTO "african_dev" VALUES(19,14,'19:14','. Era nga yayisa bulungi bakaddebe bombi era tabangako mwekuluntaze omujeemu.');
INSERT INTO "african_dev" VALUES(19,15,'19:15','. Era emirembe gibeere ku ye olunaku lwe yazaalibwa, n''olunaku lwalifa, n''olunaku lwalizuukizibwa nga mulamu.');
INSERT INTO "african_dev" VALUES(19,16,'19:16','. Era yogera nga ojja mu kitabo (Kur’ani ebikwata ku) Mariam bwe yeesamba abantube nadda ku ludda olw''ebuvanjuba.');
INSERT INTO "african_dev" VALUES(19,17,'19:17','. Naateekawo olutimbe baleme kumulaba awo nno twamutumira mwoyo waffe (Jiburilu) naamweyoleka nga muntu ddala.');
INSERT INTO "african_dev" VALUES(19,18,'19:18','. (Mariam) naagamba nti nsaba Mukama Katonda Omusaasizi akuntaaseeko bwoba nga otya Katonda (ndeka tonsemberera).');
INSERT INTO "african_dev" VALUES(19,19,'19:19','. (Malayika) naamugamba nti mazima nze ndi mubaka okuva ewa Mukama Omulabiriziwo nkuleetedde omwana omulenzi omutukuvu.');
INSERT INTO "african_dev" VALUES(19,20,'19:20','. Mariam naagamba nti nyinza ntya okufuna omwana nga tewali muntu yankutteko, ate nga sibangako malaaya!.');
INSERT INTO "african_dev" VALUES(19,21,'19:21','. (Malayika naagamba nti) bwe kityo bwe kirina okuba, Mukama Omulabiriziwo agambye nti kyo ku nze kyangu. Era tube nga tumufuula akabonero eri abantu (akalaga obuyinza bwa Katonda), era nga kusaasira okuva gye tuli, era kyali ekigambo ekyasalibwawo (edda).');
INSERT INTO "african_dev" VALUES(19,22,'19:22','. (Mariam) n''aba olubuto lwa Issa, neyeesamba nalwo okuva mu bantu okulaga eri ekifo ekyesudde.');
INSERT INTO "african_dev" VALUES(19,23,'19:23','. Awo nno ebisa nebimusindiikiriza naasibira wansi w''omutende, naagamba nti, ndabye nze singa nnafa nga kino tekinnabaawo nemba ekyerabiddwa ekitakyafiibwako.');
INSERT INTO "african_dev" VALUES(19,24,'19:24','. (Malayika) naamukoowoola nga asinziira wansi we nti tonakuwala, mazima Mukama omulabiriziwo atadde wansi wo omwaala.');
INSERT INTO "african_dev" VALUES(19,25,'19:25','. Weenyenyeze omutende gujja kukusuulira entende embisi, ezituuse okunoga.');
INSERT INTO "african_dev" VALUES(19,26,'19:26','. Olwo nno, lya ku (ntende) era onywe ku (mwaala) otebenkere amaaso (obeere bulungi), wonna woolabira omuntu yenna, ggwe mugambe nti neeyamye ku lwa Katonda Omusaasizi okusirika, ssigenda kwogera na muntu yenna olwa leero.');
INSERT INTO "african_dev" VALUES(19,27,'19:27','. Naamuleeta eri abantube nga amusitudde nebagamba nti owange Mariam mazima okoze ekintu ekisasamaza.');
INSERT INTO "african_dev" VALUES(19,28,'19:28','. Owange mwannyina wa Haruna, Kitaawo tabangako muntu mukozi wa kibi, era ne Maamawo tabangako Malaaya!.');
INSERT INTO "african_dev" VALUES(19,29,'19:29','. (Mariam) naasonga ku mwana, (bali) nebagamba tuyinza tutya okwogera n''omwana ali ku kaali omuwere.');
INSERT INTO "african_dev" VALUES(19,30,'19:30','. (Omwana) naagamba nti mazima nze ndi muddu wa Katonda, ampadde ekitabo (enjiri) era nanfuula Nabbi.');
INSERT INTO "african_dev" VALUES(19,31,'19:31','. Era nanfuula ow''omukisa wonna wennabanga, era ankuutidde okusaala, n''okutoola Zakka, ebbanga lye nnaamala nga ndi mulamu.');
INSERT INTO "african_dev" VALUES(19,32,'19:32','. Era (ankuutidde) okuyisa obulungi Maama wange, era tanfudde eyeekuza, omwonoonefu.');
INSERT INTO "african_dev" VALUES(19,33,'19:33','. Era emirembe gibeere ku nze olunaku lwe nnazaalibwa, n''olunaku lwe ndifa, n''olunaku lwe ndizuukizibwa nemba omulamu.');
INSERT INTO "african_dev" VALUES(19,34,'19:34','. Oyo ye Issa Mutabani wa Mariam, ekigambo eky''amazima ekyo (abeeyita abagoberezibe) kye babuusabuusa.');
INSERT INTO "african_dev" VALUES(19,35,'19:35','. Katonda tekimusaanira kweteerawo mwana. Anti yye musukkulumu (ku buli kintu), bwasalawo ekintu mazima akigamba nti ba, nekiba.');
INSERT INTO "african_dev" VALUES(19,36,'19:36','. Era mazima ddala Katonda ye Mukama omulabirizi wange, era Omulabirizi wa mmwe, kale mumusinze, eryo ly''ekkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(19,37,'19:37','. Wabula ebibiina bya bantu byayawukana (ku nsonga ya Issa), n''olwekyo okubonaabona kugenda kubeera ku abo abaakaafuwala (nebabikka amazima agakwata ku Issa) ku lunaku olunene abantu bonna lwe balibaako.');
INSERT INTO "african_dev" VALUES(19,38,'19:38','. Bagenda kuba nga bawulira nnyo era nga balaba nnyo, olunaku lwe balitujjira naye abeeyisa obubi olwa leero (kuno ku nsi) bali mu bubuze obw''olwatu .');
INSERT INTO "african_dev" VALUES(19,39,'19:39','. Batiise (Ggwe Muhammad) olunaku lw''okwejjusa mu kiseera ekigambo lwe kirisalibwawo, (abalongoofu bayingire e jjana aboonoonyi bayingire omuliro), wabula bo olwa leero bali mu bugayaavu era bbo tebakkiriza.');
INSERT INTO "african_dev" VALUES(19,40,'19:40','. Mazima ffe, ffe tulisikira ensi n''abagiriko era gye tuli gye balizzibwa.');
INSERT INTO "african_dev" VALUES(19,41,'19:41','. Era yogera nga ojja mu kitabo (kino Kur’ani) ebikwata ku Ibrahim, mazima yye yali mukkiriza ow''amazima era nga Nabbi.');
INSERT INTO "african_dev" VALUES(19,42,'19:42','. Mujjukire (Ibrahim) bwe yagamba Kitaawe nti owange Taata lwaki osinza ebitawulira, era ebitalaba, era ebitayinza kukugasa mu kintu kyonna.');
INSERT INTO "african_dev" VALUES(19,43,'19:43','. Owange Taata mazima nze nzigyiddwa okumanya okutaakujjira, n''olwekyo ngoberera, nja kukulungamya ku kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(19,44,'19:44','. Owange Taata tosinzanga Sitane, mazima Sitane yajeemera Katonda omusaasizi.');
INSERT INTO "african_dev" VALUES(19,45,'19:45','. Owange Taata mazima nze ntya ebibonerezo okuva ewa Katonda omusaasizi okukutuukako, olwo n''oba mukwano gwa Sitane.');
INSERT INTO "african_dev" VALUES(19,46,'19:46','. (Kitaawe) naagamba nti owange Ibrahim oyagala kuva ku ba Katonda bange, bwotookikomye nja kukukuba amayinja, nviira mangu ssaagala kukulaba.');
INSERT INTO "african_dev" VALUES(19,47,'19:47','. Ibrahim naagamba nti emirembe gibe ku ggwe, nja kwegayirira Mukama Omulabirizi wange akusonyiwe, mazima yye bulijjo annumirwa.');
INSERT INTO "african_dev" VALUES(19,48,'19:48','. Nja kubaviira (mbaleke) n''ebyo bye musinza nemuva ku Katonda era nja kusaba Mukama Omulabirizi wange, nkakasa siyinza kuba mwonoonefu olw''okusinza Mukama Omulabirizi wange.');
INSERT INTO "african_dev" VALUES(19,49,'19:49','. Bwe yabaviira naabaleka n''ebyo bye basinza nebalekawo Katonda, twamugabira Ishak, ne Yakubu buli omu ku bombi twamufuula Nabbi.');
INSERT INTO "african_dev" VALUES(19,50,'19:50','. Era twabagabira okuva mu kusaasira kwaffe, netubateerawo okwogerwako okw''amazima okubafuula ab''awaggulu.');
INSERT INTO "african_dev" VALUES(19,51,'19:51','. Yogera mu Kitabo (Kur’ani) ebikwata ku Musa, mazima yye yali mulondemu, era yali mubaka era Nabbi.');
INSERT INTO "african_dev" VALUES(19,52,'19:52','. Twamukoowoola ku ludda olwa ddyo olw''olusozi (Sinai), netumusembeza nga twogera naye.');
INSERT INTO "african_dev" VALUES(19,53,'19:53','. Era netumuwa okuva mu kusasira kwaffe Mugandawe Haruna nga (naye) Nabbi.');
INSERT INTO "african_dev" VALUES(19,54,'19:54','. Era yogera nga ojja mu Kitabo (Kur’ani) ebikwata ku Ismail, mazima yye yali mutuukiriza wa ndagaano, era yali Mubaka nga Nabbi.');
INSERT INTO "african_dev" VALUES(19,55,'19:55','. Yalinga alagira abantu be okusaala n''okutoola Zakka. Era bulijjo nga asiimwa ewa Mukama Omulabiriziwe.');
INSERT INTO "african_dev" VALUES(19,56,'19:56','. Era yogera nga ojja mu kitabo (Kur’ani) ebikwata ku Idrisa, mazima yye yali musuffu mu mazima nga Nabbi.');
INSERT INTO "african_dev" VALUES(19,57,'19:57','. Era twamusitula netumuwa ekifo ekya waggulu.');
INSERT INTO "african_dev" VALUES(19,58,'19:58','. Abo beebo Katonda beyagabira ebyengera mu ba Nabbi nga bava mu bazzukulu ba Adam, ne mu abo betwatwala ne Nuhu mu lyato, ne mu bazzukulu ba Ibrahim, ne Israil ne mu abo betwalungamya, era netubaawula. Ebigambo bya Katonda omusaasizi buli lwe byabasomerwanga bakkanga wansi nga bavunnama era nga bakaaba.');
INSERT INTO "african_dev" VALUES(19,59,'19:59','. Olwo nno oluvanyuma lwabwe wajjayo abaabaddira mu bigere, abataafa ku sswala, era nebagoberera eby''obwagazi, luliba lumu bagenda kusisinkana okufaafaaganirwa n''okuzikirira.');
INSERT INTO "african_dev" VALUES(19,60,'19:60','. Okugyako oyo eyeenenya, era nakkiriza era naakola emirimu emirongoofu, abo bagenda kuyingira e jjana era tebagenda kulyazaamaanyizibwa kintu kyonna.');
INSERT INTO "african_dev" VALUES(19,61,'19:61','. (Baliyingira) e jjana ez''olubeerera, ezo Katonda omusaasizi ze yalagaanyisa abaddube nga tebazirabyeko, mazima endagaano ye erina kutuukirira.');
INSERT INTO "african_dev" VALUES(19,62,'19:62','. Tebagenda kuwulira mu yo (e jjana) luyoogaano wabula (baliwulira) bigambo bya mirembe byereere baliweebwa riziki yaabwe mu yo enkya n''olweggulo.');
INSERT INTO "african_dev" VALUES(19,63,'19:63','. Eyo ye jjana, eyo gye tulisikiza, abamu ku baddu baffe, oyo eyali atya Katonda.');
INSERT INTO "african_dev" VALUES(19,64,'19:64','. Era ffe (ba Malayika) tetukka okugyako lwa kiragiro kya Mukama Omulabiriziwo, bibye byonna ebiri mu maaso gaffe n''ebiri emabega waffe, n’ebyo ebiri wakati wa byombi era Mukama omulabiriziwo tabeerangako eyeerabira.');
INSERT INTO "african_dev" VALUES(19,65,'19:65','. (Katonda) ye Mukama omulabirizi w''eggulu omusanvu n''ensi, n''ebyo ebiri wakati wa byombi kale nno musinze ogumiikirize ku kumusinza (bwotokola ekyo) abaffe omanyiiyo amufaanana?.');
INSERT INTO "african_dev" VALUES(19,66,'19:66','. Omuntu agamba nti bwendiba nfudde ate ndiggyibwa (mu ntaana) nga ndi mulamu!.');
INSERT INTO "african_dev" VALUES(19,67,'19:67','. Abaffe omuntu tajjukira nti mazima ffe twamutonda omulundi ogwasooka nga yali tabangako kintu kyonna.');
INSERT INTO "african_dev" VALUES(19,68,'19:68','. N''olwekyo ndayidde Mukama omulabiriziwo, ddala tugenda kubazuukiza ne (Sitane) nazo tuzizuukize, oluvanyuma tugenda kubaleetera ddala awali omuliro Jahannama nga bakutaamiridde.');
INSERT INTO "african_dev" VALUES(19,69,'19:69','. Oluvanyuma ddala tugenda kuggya mu buli kibiina buli yenna eyali asinga okwewaggula ku Katonda ow''ekisa ekingi.');
INSERT INTO "african_dev" VALUES(19,70,'19:70','. Oluvanyuma ffe tuliba tumanyi nnyo abo abasinga okusaanira okugwesogga.');
INSERT INTO "african_dev" VALUES(19,71,'19:71','. Tewali n''omu yenna ku mmwe okugyako w''a kuguyitako, ekyo ku Mukama omulabiriziwo kikakafu ekyaggwa edda.');
INSERT INTO "african_dev" VALUES(19,72,'19:72','. Oluvanyuma tuliwonya abo abatya Katonda, olwo nno tuleke mu gwo abeeyisa obubi nga bagangalamye.');
INSERT INTO "african_dev" VALUES(19,73,'19:73','. Bwe bisomebwa ku bo ebigambo byaffe nga binnyonnyofu, abo abaakaafuwala bagamba abo abakkiriza, nti ku bibiina ebibiri kiriwa ekirina ekifo ekirungi? era abalina enkungaana ezisinga obulungi.');
INSERT INTO "african_dev" VALUES(19,74,'19:74','. Mirembe emeka gye twazikiriza oluberyeberye lwa bwe, nga abo be baali basinza ebintu ebirungi nendabika ennungi.');
INSERT INTO "african_dev" VALUES(19,75,'19:75','. Bagambe nti oyo yenna abeera mu bubuze, Katonda omusaasizi amulindiriza olulindiriza, okutuusa nga balabye bye baalagaanyisibwa, kabibe bibonerezo oba essaawa ey''enkomerero olwo nno bagenda kumanya ani alina ekifo ekibi era ani alina eggye erinafu.');
INSERT INTO "african_dev" VALUES(19,76,'19:76','. Ate abo abaalungama Katonda abongera bulungamu, byo ebisigalawo ebirungi y''empeera esinga obulungi ewa Mukama omulabiriziwo, eyo nga mpeera era nga bwe buddo obusinga obulungi.');
INSERT INTO "african_dev" VALUES(19,77,'19:77','. Abaffe olabye oyo alimbisa ebigambo byaffe, era naagamba nti (ne ku nkomerero) ngenda kuweebwa emmaali n''abaana.');
INSERT INTO "african_dev" VALUES(19,78,'19:78','. Abaffe yasobola okulaba ebyekusifu, oba yafuna ewa Katonda omusaasizi endagaano, (atuuke okugamba nti aliba ne mmaali n''abaana ku nkomerero).');
INSERT INTO "african_dev" VALUES(19,79,'19:79','. Si bwe guli, tujja kuwandiika buli kyayogera tumulindirize ku bibonerezo olulindiriza.');
INSERT INTO "african_dev" VALUES(19,80,'19:80','. Olwo nno tweddize ebyo bye yagamba, ate nga agenda kutujjira nga ali yekka nga talina kyalina.');
INSERT INTO "african_dev" VALUES(19,81,'19:81','. Era beeteerawo bye basinza nebaleka Katonda omu, mbu olwo balyoke babeere eri bano abataddewo abawi b''obuwagizi.');
INSERT INTO "african_dev" VALUES(19,82,'19:82','. Ssi bwe guli, bajja kuwakanya eky''okubasinza bafuuke ekyennyume kyabwe.');
INSERT INTO "african_dev" VALUES(19,83,'19:83','. Tolaba nti mazima ffe tusindika Sitane eri abakaafiiri nga zibawagira oluwagira.');
INSERT INTO "african_dev" VALUES(19,84,'19:84','. Toyanguya kubasabira kuzikirira, mazima ennaku zaabwe tuzibabalira bubalizi.');
INSERT INTO "african_dev" VALUES(19,85,'19:85','. (Bategeeze) olunaku lwe tulikunganya abatya Katonda, netubatwala ewa Katonda omusaasizi nga bagenyi.');
INSERT INTO "african_dev" VALUES(19,86,'19:86','. Ate tusindiikirize aboonoonyi, nga tubatwala ku muliro Jahannama nga balinga ensolo ezigenda okunywa amazzi.');
INSERT INTO "african_dev" VALUES(19,87,'19:87','. Tebaliba na buyinza bwa kuwolereza, wabula oyo eyafuna endagaano ewa Mukama Katonda ow''ekisa ekingi.');
INSERT INTO "african_dev" VALUES(19,88,'19:88','. (Abatakkiriza) bagamba nti Katonda omusaasizi yeeteerawo omwana.');
INSERT INTO "african_dev" VALUES(19,89,'19:89','. Mazima mu kwogera ekyo muleese ekintu ekibi ennyo.');
INSERT INTO "african_dev" VALUES(19,90,'19:90','. Eggulu omusanvu kumpi okweyuza olw''ekyo, n''ensi ebula okwatikayatika n''ensozi kumpi kugwa nga zimogosemogose.');
INSERT INTO "african_dev" VALUES(19,91,'19:91','. Olw''okugamba nti Mukama Katonda Omusaasizi alina omwana.');
INSERT INTO "african_dev" VALUES(19,92,'19:92','. Ate nga tekigwana ku Katonda omusaasizi kweteerawo mwana.');
INSERT INTO "african_dev" VALUES(19,93,'19:93','. Tewali kitonde kiri mu ggulu musanvu n''ensi okugyako nga kigenda kujja ewa Katonda omusaasizi nga kiri mu ngeri ya buddu.');
INSERT INTO "african_dev" VALUES(19,94,'19:94','. Mazima yabakomekkereza, era naababala olubala.');
INSERT INTO "african_dev" VALUES(19,95,'19:95','. Era bonna bagenda kumujjira olunaku lw''enkomerero kinnoomu.');
INSERT INTO "african_dev" VALUES(19,96,'19:96','. Mazima abo abakkiriza nebakola emirimu emirungi, Katonda ajja kubateerawo okwagalwa.');
INSERT INTO "african_dev" VALUES(19,97,'19:97','. Mazima (Kur’ani) twagifuula nnyangu ku lulimilwo, obe nga osanyusa nayo abo abatya Katonda, ate obe nga otiisa nayo abo abayitirivu b''okugiwakanya.');
INSERT INTO "african_dev" VALUES(19,98,'19:98','. (Abantu ba) mirembe emeka gye twazikiriza oluberyeberye lwa bwe (bano abalimbisa Kur’ani), abaffe olina gwolabako mu bo, oba gwowulizaako olukwakwayo.');
INSERT INTO "african_dev" VALUES(20,1,'20:1','. Twa-haa.');
INSERT INTO "african_dev" VALUES(20,2,'20:2','. Tetwakussaako Kur’ani obe nga ozitoowererwa.');
INSERT INTO "african_dev" VALUES(20,3,'20:3','. Wabula twagikussaako nga kya kujjukiza eri oyo atya Katonda.');
INSERT INTO "african_dev" VALUES(20,4,'20:4','. (Kur’ani eno) yassibwa okuva eri oyo eyatonda ensi n''eggulu eryewanise.');
INSERT INTO "african_dev" VALUES(20,5,'20:5','. Katonda omusaasizi ow''ekisa ekingi, yatebenkera ku Arish.');
INSERT INTO "african_dev" VALUES(20,6,'20:6','. Bibye ebyo ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi, n''ebyo ebiri wakati wa byombi, n''ebyo ebiri wansi w''ettaka.');
INSERT INTO "african_dev" VALUES(20,7,'20:7','. Singa olina ekigambo kyonna kyoyatudde (k''obe nga tokyatudde) anti mazima yye amanyi ekyekyama n''ekisinga okubeera ekikweke.');
INSERT INTO "african_dev" VALUES(20,8,'20:8','. Katonda y''oyo ewatali kisinzibwa kya mazima okugyako yye. Y''alina amannya agasinga obulungi.');
INSERT INTO "african_dev" VALUES(20,9,'20:9','. Abaffe kwakujjira okwogera (ku kyafaayo) kya Musa.');
INSERT INTO "african_dev" VALUES(20,10,'20:10','. Mu kiseera weyalabira omuliro alyoke agambe abantube nti (mutuule) wano, mazima nze nengende omuliro (kangendeyo) sikulwa nga mbaleetera okuva gye guli eryanda si nakindi nensanga awali omuliro obulungamu.');
INSERT INTO "african_dev" VALUES(20,11,'20:11','. Musa bwe yagutuukako yakoowoolwa nti: owange ggwe Musa.');
INSERT INTO "african_dev" VALUES(20,12,'20:12','. Mazima nze, nze Mukama omulabiriziwo kale ggyamu engattozo, anti mazima ggwe oli mu lusenyi olutukuvu oluyitibwa Tuwa.');
INSERT INTO "african_dev" VALUES(20,13,'20:13','. Era mazima nze nkulonze okubeera (omubaka) wange, kale nno wuliriza obubaka obukussibwako.');
INSERT INTO "african_dev" VALUES(20,14,'20:14','. Mazima nze, nze Katonda nga tewali kisinzibwa mu butuufu okugyako nze, kale nno nsinza, oyimirizeewo e sswala obeere nga onjogerako mu yo.');
INSERT INTO "african_dev" VALUES(20,15,'20:15','. Anti mazima ekiseera (ebitonde lwe birizuukizibwa) kijja kutuuka mbula kukikweka (nange mbe nga sikimanya, ate bo bakimanyira wa), olwo nno (bwe kiriba kituuse) buli mwoyo gulyoke gusasulwe ebyo bye gwatakabanira.');
INSERT INTO "african_dev" VALUES(20,16,'20:16','. Kale nno takulabankanya okukuggya ku kukolerera ekiseera ekyo, oyo yenna atakikkiriza nga agoberera kwagalakwe (nekikuviirako) okuzikirira.');
INSERT INTO "african_dev" VALUES(20,17,'20:17','. Owange Musa kiki ekiri mu mukono gwo ogwa ddyo?.');
INSERT INTO "african_dev" VALUES(20,18,'20:18','. Naagamba nti ogwo muggo gwange, gunyamba nga ntambula, era gwe nneeyambisa okukuba ebikoola embuzi zange (ziryoke zirye) era nina mugwo emigaso emirala.');
INSERT INTO "african_dev" VALUES(20,19,'20:19','. Katonda naagamba owange Musa gukasuke wansi.');
INSERT INTO "african_dev" VALUES(20,20,'20:20','. Naagukasuka, okugenda okulaba nga gwo musota ogutambula.');
INSERT INTO "african_dev" VALUES(20,21,'20:21','. Katonga naagamba gukwate, era totya, tujja kuguzza ku mbeera yaagwo ey''abulijjo.');
INSERT INTO "african_dev" VALUES(20,22,'20:22','. Yingiza omukono gwo mu nkwawazo, gugenda kuvaayo nga mweru nga ate tegutuukiddwako kabi, nakyo nga ky''amagero kirala.');
INSERT INTO "african_dev" VALUES(20,23,'20:23','. Olwo nno tulyoke tukulage obubonero bwaffe obunene.');
INSERT INTO "african_dev" VALUES(20,24,'20:24','. Genda ewa Firaawo mazima yye yabula (naafuuka kiwagi).');
INSERT INTO "african_dev" VALUES(20,25,'20:25','. (Musa) naagamba nti ayi Mukama Omulabirizi wange, nyanjululiza ekifuba kyange.');
INSERT INTO "african_dev" VALUES(20,26,'20:26','. Era onsobozese omulimu gwange.');
INSERT INTO "african_dev" VALUES(20,27,'20:27','. Era ojje okwesiba mu lulimi lwange.');
INSERT INTO "african_dev" VALUES(20,28,'20:28','. Bategeere ebigambo byange.');
INSERT INTO "african_dev" VALUES(20,29,'20:29','. Era onteerewo omuyambi nga ava mu b''oluganda lwange.');
INSERT INTO "african_dev" VALUES(20,30,'20:30','. (Nga ye) Haruna muganda wange.');
INSERT INTO "african_dev" VALUES(20,31,'20:31','. Nkusaba oggumize naye obusobozi bwange.');
INSERT INTO "african_dev" VALUES(20,32,'20:32','. Era omugatte (ku nze) mu mulimu gwange.');
INSERT INTO "african_dev" VALUES(20,33,'20:33','. Olwo nno tube nga tukutendereza nnyo.');
INSERT INTO "african_dev" VALUES(20,34,'20:34','. Era tukwogereko nnyo.');
INSERT INTO "african_dev" VALUES(20,35,'20:35','. Mazima ggwe bulijjo otulabira ddala.');
INSERT INTO "african_dev" VALUES(20,36,'20:36','. (Katonda) naagamba nti owange Musa, oweereddwa ebisabwa byo.');
INSERT INTO "african_dev" VALUES(20,37,'20:37','. Era mazima twakugabira ekyengera omulundi omulala (oluberyeberye lwa kino).');
INSERT INTO "african_dev" VALUES(20,38,'20:38','. Mu kiseera bwe twatumira eri Maamawo ekyo kye twamutumira.');
INSERT INTO "african_dev" VALUES(20,39,'20:39','. (Netumugamba) nti musse mu sanduke (bwomala) ogisse ku mazzi (g''omugga) amazzi gajja kumukasuka ku lubalama, olwo nno omulabe wange era nga mulabe we ajja kumutwala, era nakussaako ggwe (Musa) okwagala okuva gyendi, era olyoke obeere mu kulabirirwa kwange.');
INSERT INTO "african_dev" VALUES(20,40,'20:40','. Jjukira ekiseera mwannyoko bwe yatambula (naatuuka ku bantu bo mu maka ga Firaawo), naagamba nti abaffe mbalagirire omuntu anaamulabirira, olwo netukuzza ewa Maamawo, eriiso lye liryoke litebenkere (omutima gumubeere wamu), era aleme kunakuwala, (era jjukira nti lumu) watta omuntu wabula netukuwonya okweraliikirira era twakugezesa n''ebigezo bingi, nootuula emyaka egiwera mu bantu be Madiana. Oluvanyuma owange Musa bwomaze nojja wano Katonda nga bwe yakugerera.');
INSERT INTO "african_dev" VALUES(20,41,'20:41','. Era nkwawudde nga oli wange (nkuwadde obubaka).');
INSERT INTO "african_dev" VALUES(20,42,'20:42','. Genda ggwe ne mugandawo, nga mututte obubonero bwange, kyokka temukoowanga okunjogerako.');
INSERT INTO "african_dev" VALUES(20,43,'20:43','. Mugende mwembiriri ewa Firaawo, mazima yeewaggula.');
INSERT INTO "african_dev" VALUES(20,44,'20:44','. Mumugambe ebigambo ebigonvu, alyoke ajjukire era atye.');
INSERT INTO "african_dev" VALUES(20,45,'20:45','. (Musa ne Haruna) nebagamba nti, ayi Mukama omulabirizi waffe mazima ffe tutya, ayinza okutubonyaabonya ennyo, oba (yye) okweyongera okwewaggula.');
INSERT INTO "african_dev" VALUES(20,46,'20:46','. (Katonda) naagamba nti temutya mazima nze ndi wamu nammwe mpulira era ndaba.');
INSERT INTO "african_dev" VALUES(20,47,'20:47','. Kale nno mugende gyali mwembiriri mumugambe nti mazima ffe fembi tuli babaka ba Mukama omulabiriziwo, tuleke tugende n''abaana ba Israil era tobabonyaabonya. Mazima tukujjidde n''ekyamagero okuva ewa Mukama omulabiriziwo, era bulijjo emirembe gibeera ku oyo agoberera obulungamu.');
INSERT INTO "african_dev" VALUES(20,48,'20:48','. Mazima ffe tuweereddwa obubaka nti, mazima ebibonerezo bibeera ku oyo yenna alimbisa naava ku kituufu.');
INSERT INTO "african_dev" VALUES(20,49,'20:49','. (Oluvanyuma bombi baatuuka ewa Firaawo), naagamba nti owange Musa olwo ani Mukama omulabirizi wa mmwe mwembiriri?.');
INSERT INTO "african_dev" VALUES(20,50,'20:50','. (Musa) naagamba nti Mukama omulabirizi waffe y''oyo eyawa buli kintu obutonde bwakyo, oluvanyuma naakirungamya (ku ngeri gye kiteekwa okuwangaalamu).');
INSERT INTO "african_dev" VALUES(20,51,'20:51','. (Firaawo) naagamba (bwe guba bwe gutyo) emirembe egyakulembera gyo gibalibwa gitya.');
INSERT INTO "african_dev" VALUES(20,52,'20:52','. (Musa) naagamba nti ebigifaako biri wa Mukama omulabirizi wange nga biri mu kitabo (byawandiikibwa dda), Mukama omulabirizi wange takola kikyamu era teyeerabira.');
INSERT INTO "african_dev" VALUES(20,53,'20:53','. (Omulabirizi wange) oyo eyabateerawo ensi nga kyaliiro, naabateera mu yo amakubo, era nassa okuva waggulu amazzi netumeza nago emitendera ebiri mu bimera ebitali bimu.');
INSERT INTO "african_dev" VALUES(20,54,'20:54','. (Netugamba nti) mulye era mulunde ebisolo bya mmwe. Mazima mu ekyo mulimu obubonero eri ab''amagezi.');
INSERT INTO "african_dev" VALUES(20,55,'20:55','. Mulyo (ettaka) mwe twabatonda era mulyo mwe tubazza era mulyo mwe tulibaggya omulundi omulala.');
INSERT INTO "african_dev" VALUES(20,56,'20:56','. Era mazima twamulaga (Firaawo) obubonero bwaffe bwonna naalimbisa era naagaana.');
INSERT INTO "african_dev" VALUES(20,57,'20:57','. (Firaawo) naagamba nti owange Musa ekikuleese kutuggya mu nsi yaffe olw''eddogolyo.');
INSERT INTO "african_dev" VALUES(20,58,'20:58','. Kale nno naffe tujja kukuleetera eddogo eriringa lyo, teeka wakati waffe naawe endagaano, gye tutajja kwawukanako ffe wadde ggwe, (tusisinkane mu) kifo ekikiriziganyiziddwako (enjuyi zombi).');
INSERT INTO "african_dev" VALUES(20,59,'20:59','. (Musa) naagamba nti endagaano yammwe (tusisinkane) olunaku lw''okwenyiriza, era abantu balagirwanga okukungaana mu kalasa mayanzi.');
INSERT INTO "african_dev" VALUES(20,60,'20:60','. Olwo nno Firaawo yavaawo, naagenda n’agattagatta enkwe ze oluvanyuma najja (ku lunaku olulagaanye).');
INSERT INTO "african_dev" VALUES(20,61,'20:61','. Musa naabagamba (abalogo) nti: nga mulabye nnyo! Temutemerera Katonda obulimba naabasanyawo, n''ebibonerezo, era bulijjo afaafaaganirwa oyo agunjawo obulimba.');
INSERT INTO "african_dev" VALUES(20,62,'20:62','. (Abalogo), ekigambo kyabwe nebakyawukanamu wakati waabwe, wabula olukiiko lwali mu kyama.');
INSERT INTO "african_dev" VALUES(20,63,'20:63','. Nebagamba nti mazima bano bombi balogo, baagala babaggye mu nsi yammwe, olw''eddogo lyabwe bombi era baggyewo enkola ya mmwe ennungi.');
INSERT INTO "african_dev" VALUES(20,64,'20:64','. Musse kimu mu kusalawo kwa mmwe, olwo nno mulumbe nga muli lunyiriri lumu, era mazima anaawangula olwa leero y''anaaba yeesiimye.');
INSERT INTO "african_dev" VALUES(20,65,'20:65','. Nebagamba nti owange Musa ggwe (osooka) okusuula omuggo oba ffe tube abasooka okusuula.');
INSERT INTO "african_dev" VALUES(20,66,'20:66','. (Musa) naagamba nti wabula mmwe musuule, okugenda okulaba nga emiguwa gyabwe n''emiggo gyabwe, olw''eddogo lyabwe nga gimulabikira nti mazima ddala gitambula.');
INSERT INTO "african_dev" VALUES(20,67,'20:67','. Musa naafuna okutya mu mutima gwe.');
INSERT INTO "african_dev" VALUES(20,68,'20:68','. Netumugamba nti totya, mazima, ggwe ajja okuwangula.');
INSERT INTO "african_dev" VALUES(20,69,'20:69','. Era suula ekyo ekiri mu mukono gwo ogwa ddyo (nga gwe muggo), gujja kumira ebyo bye bakoze. Anti mazima ddala bye bakoze nkwe za mulogo ate nga omulogo buli waaba tawangula.');
INSERT INTO "african_dev" VALUES(20,70,'20:70','. (Bwe yakola ekyo) abalogo bonna nebagwa nga bavunnama, nebagamba nti tukkirizza Mukama omulabirizi wa Haruna ne Musa.');
INSERT INTO "african_dev" VALUES(20,71,'20:71','. (Firaawo) naagamba nti, mumukkirizza mutya nga sinnaba kubakkiriza, (kirabika) nti mazima yye ye mukulu wa mmwe oyo eyabayigiriza eddogo, n''olwekyo nja kutemerako ddala emikono gyammwe n''amagulu gammwe, (ngantema) omukono ogwa ddyo n''okugulu okwa kkono, era nja kubabamba ku ndoddo z''emitende, era mugenda kumanyira ddala nti ani ku fenna asinza ebibonerezo era ow''okusigalawo.');
INSERT INTO "african_dev" VALUES(20,72,'20:72','. (Abalogo) nebagamba tetujja kukulembeza ku mazima ameeyolefu, agamaze okutujjira, wadde okukulembeza ku oyo eyatonda, kale ggwe, salawo kyosalawo, mazima osobola kusalawo ku buno obulamu obw''ensi.');
INSERT INTO "african_dev" VALUES(20,73,'20:73','. Mazima ffe tukkirizza Mukama omulabirizi waffe, abe nga atusonyiwa ebisobyo byaffe, era atusonyiwe ebyo byotuwaliriza okukola eby''eddogo, era bulijjo Katonda y''asinga obulungi era ow''okusigalawo.');
INSERT INTO "african_dev" VALUES(20,74,'20:74','. Mazima oyo yenna alijja nga mwonoonyi ewa Mukama omulabiriziwe, mazima agenda kuyingira omuliro Jahannama tagenda kufa nga ali mu gwo wadde okubeera omulamu.');
INSERT INTO "african_dev" VALUES(20,75,'20:75','. Ate oyo alimujjira nga mukkiriza, era nga yakola emirimu emirongoofu, abo bagenda kuweebwa amadaala aga waggulu.');
INSERT INTO "african_dev" VALUES(20,76,'20:76','. (Amadaala ago) e jjana ez''olubeerera, nga emigga gikulukutira wansi waazo, baakutuula mu zo obugenderevu, era eyo y''empeera y''oyo eyeetukuza.');
INSERT INTO "african_dev" VALUES(20,77,'20:77','. Era mazima twatumira Musa nti, tambula ekiro n''abaddu bange (abaana ba Israil) obakubire ku nyanja ekkubo ekkalu, totya (mulabe) kubasanga, era totya (nyanja kubasaanyawo).');
INSERT INTO "african_dev" VALUES(20,78,'20:78','. Olwo nno Firaawo yabagoberera n''eggyerye, olwo nebabikkibwa amazzi ago (Katonda) geyasalawo gababikke.');
INSERT INTO "african_dev" VALUES(20,79,'20:79','. Bwatyo Firawo yabuza abantube era (naye) teyalungama.');
INSERT INTO "african_dev" VALUES(20,80,'20:80','. Abange abaana ba (Israil) mazima twabawonya omulabe wa mmwe, era twabalagaanyisa mu kifo ekiri ku ludda olwa ddyo ku lusozi, (wetwayogerera ne Musa era wetwamuweera Taurat) era twabassiza Manna ne Saliwa (ekisooka mmere ekirala nva).');
INSERT INTO "african_dev" VALUES(20,81,'20:81','. (okwo netwongerako nti), mulye mu buli kirungi mu ebyo bye tubagabira temusukka ekikomo mu kwo (okulya) nemukakatwako obusungu bwange, era bulijjo oyo yenna gwe bukakatako obusungu bwange aba abuze.');
INSERT INTO "african_dev" VALUES(20,82,'20:82','. Era mazima nze ndi musonyiyi, eri oyo eyeenenya naakola emirimu emirongoofu, ate bulijjo naabeera ku bulungamu.');
INSERT INTO "african_dev" VALUES(20,83,'20:83','. (Musa bwe yatuuka ku lusozi, Katonda naamugamba nti) owange Musa, kiki ekikwanguyizza nooleka abantu bo.');
INSERT INTO "african_dev" VALUES(20,84,'20:84','. Naagamba nti bo, baabo bangoberera, nze nyanguye okujja gyoli Mukama omulabirizi wange, obe nga osiima.');
INSERT INTO "african_dev" VALUES(20,85,'20:85','. (Katonda) naagamba nti, mazima twakemye abantubo nga ovuddeyo, Saamiriyyu naababuza.');
INSERT INTO "african_dev" VALUES(20,86,'20:86','. Olwo nno Musa nadda eri abantu be, nga musunguwavu nga mwennyamivu, naagamba nti abange bantu bange. Abaffe Mukama omulabirizi wa mmwe teyabalagaanyisa endagaano ennungi, ekiseera ekiragaane kye kyabawanvuwako, oba mwayagala mutuukibweko obusungu okuva ewa Mukama omulabirizi wa mmwe, olwo nno nemuva ku ndagaano yange.');
INSERT INTO "african_dev" VALUES(20,87,'20:87','. Nebagamba nti: Endagaanoyo tetwagivuddeko lwa kweyagalira kwaffe, wabula mazima ffe twatikkiddwa ebizito mu by''okwewunda bya bantu ba (Firaawo) olwo nno netubikasuka ( mu muliro) bwatyo Saamiriyyu bwe yasudde.');
INSERT INTO "african_dev" VALUES(20,88,'20:88','. Olwo (Saamiriyyu) naabakoleramu ennyana erina omubiri, era nga engonga, nebagamba nti ono ye Katonda wa mmwe, era Katonda wa Musa wabula Musa yeerabidde, (naagenda so nga Katonda yamulese wano).');
INSERT INTO "african_dev" VALUES(20,89,'20:89','. Abaffe tebaalaba nti ennyana eyo, yali tesobola kubaddamu kigambo kyonna, era nga tefuga kubatuusaako kabi wadde omugaso.');
INSERT INTO "african_dev" VALUES(20,90,'20:90','. Ate nga mazima Haruna yali amaze okubagamba mu kusooka nti abange bantu bange, mazima mukemeddwa olweyo (ennyana), era mazima Mukama omulabirizi wa mmwe, ye Katonda omusaasizi kale nno mungoberere era mugondere ekiragiro kyange.');
INSERT INTO "african_dev" VALUES(20,91,'20:91','. Nebagamba nti, tetugenda kulekayo kugisinza, okutuusa Musa lwanadda gye tuli.');
INSERT INTO "african_dev" VALUES(20,92,'20:92','. (Musa bwe yakomawo) yagamba nti owange Haruna bwe walabye nga babuze ki ekyakugaanye.');
INSERT INTO "african_dev" VALUES(20,93,'20:93','. Okungoberera (n''obanenya nga bwenkoze) abaffe wajeemedde ekiragiro kyange.');
INSERT INTO "african_dev" VALUES(20,94,'20:94','. (Haruna) naagamba nti owange mwana wa nnyabo tonsika kirevu, wadde omutwe gwange, mazima nze natidde ggwe okugamba nti, wayawukanyizza abaana ba Israil, nootalinda kigambo kyange.');
INSERT INTO "african_dev" VALUES(20,95,'20:95','. (Musa) naagamba nti ate ggwe Saamiriyyu, kiki ekyakukozesezza ekyo?.');
INSERT INTO "african_dev" VALUES(20,96,'20:96','. (Saamiruyyi) naagamba nti nalabye kye bataalabye olwo nno nenjoola olubatu lw''ettaka omubaka (Jiburilu) weyayise, nenkolamu ennyana nze bwentyo bwe nnalabye.');
INSERT INTO "african_dev" VALUES(20,97,'20:97','. (Musa) naagamba nti kale genda, mazima ossiddwako ekibonerezo mu bulamu obw''ensi, ekya tewali kukwataganako, era mazima oweereddwa ekiseera ekigere, togenda ku kyawukanako era tunula (olabe) Katondawo oyo gwe wamaliddeko ebiseera nga osinza, oluvanyuma tugenda kumumansira ddala mu mazzi olumansa.');
INSERT INTO "african_dev" VALUES(20,98,'20:98','. Mazima Katonda wa mmwe ye Katonda oyo awatali kisinzibwa kyonna (mu butuufu) okugyako yye, yamalayo buli kintu kyonna mu kumanya.');
INSERT INTO "african_dev" VALUES(20,99,'20:99','. Bwe kityo tukutegeeza amawulire agakwata ku bya kulembera era mazima twakuwa okuva gye tuli ekijjukizza (Kur’ani).');
INSERT INTO "african_dev" VALUES(20,100,'20:100','. Omuntu agyawukanako mazima agenda kwetikka ku lunaku lw''enkomerero ebibi (ayingizibwe omuliro).');
INSERT INTO "african_dev" VALUES(20,101,'20:101','. Baakutuula mu gwo olubeerera, omugugu gwabwe ku lunaku lw''enkomerero gugenda kubeera mubi.');
INSERT INTO "african_dev" VALUES(20,102,'20:102','. Olunaku engombe lwe rifuuyibwa, era ku lunaku olwo tugenda kukungaanya aboonoonyi nga amaaso gabatwaakadde.');
INSERT INTO "african_dev" VALUES(20,103,'20:103','. Baliyogeraganya mu bwama, nga bagamba nti: tetumaze ku nsi okugyako ennaku kkumi (zokka).');
INSERT INTO "african_dev" VALUES(20,104,'20:104','. Ffe tusinga okumanya bye boogera, mu kiseera abasinga okutegeera bwe baagamba nti temwatuula ku nsi okugyako olunaku lumu lwokka.');
INSERT INTO "african_dev" VALUES(20,105,'20:105','. Bakubuuza ekirituuka ku nsozi, gamba nti Mukama omulabirizi wange agenda kuzisaanyaawo olusaanyawo (zibe nga enfuufu).');
INSERT INTO "african_dev" VALUES(20,106,'20:106','. Olwo nno agireke nga museetwe nga nkalu.');
INSERT INTO "african_dev" VALUES(20,107,'20:107','. Nga tolaba mu yo makoona wadde obutunnumba.');
INSERT INTO "african_dev" VALUES(20,108,'20:108','. Olunaku olwo baligoberera omukoowooze awatali kuwalira, amaloboozi gonna negakkakkanira Katonda, togenda kuwulira mpozzi olukwakwayo (lwokka).');
INSERT INTO "african_dev" VALUES(20,109,'20:109','. Ku lunaku olwo okuwolereza tekugenda kugasa, okugyako oyo Katonda omusaasizi gwaliba akkirizza era naasiima byayogera.');
INSERT INTO "african_dev" VALUES(20,110,'20:110','. (Katonda) y’amanyi ebiri mu maaso gaabwe, n''ebiri emabega waabwe ate tebayinza kubimanya nebabimalayo.');
INSERT INTO "african_dev" VALUES(20,111,'20:111','. Ebyenyi bigenda kugondera oyo (Katonda) omulamu, eyeeyimirizaawo yekka, mazima aliba aweebuuse oyo alyetikka okweyisa obubi.');
INSERT INTO "african_dev" VALUES(20,112,'20:112','. Ate oyo akola emirimu emirongoofu nga naye mukkiriza, talitya kulyazaamaanyizibwa, era nga era bwatagenda kutya kunyigirizibwa.');
INSERT INTO "african_dev" VALUES(20,113,'20:113','. Mu ngeri bwetyo, twagissa Kur’ani nga eri mu lulimi luwarabu, netunnyonnyola mu yo buli kika kya bitiisa, olwo nno babe nga batya Katonda, oba bafune okubuulirirwa.');
INSERT INTO "african_dev" VALUES(20,114,'20:114','. Wa waggulu nnyo Katonda omufuzi owa mazima, (gwe Muhmmad) toyanguyiriza noosoma Kur’ani nga obubaka obukuweebwa tebunnagwayo, era gamba nti ayi Mukama omulabirizi wange nyongera okumanya.');
INSERT INTO "african_dev" VALUES(20,115,'20:115','. Era mazima twakozesa Adam oluberyeberye endagaano, kyokka neyeerabira era tetwamusangamu bunywevu (obutakola ebyo ebyamugaanibwa).');
INSERT INTO "african_dev" VALUES(20,116,'20:116','. Era jjukira ekiseera bwe twagamba ba Malayika nti muvunnamire Adam nebavunnama okugyako Ibuliisu (Sitane) yagaana.');
INSERT INTO "african_dev" VALUES(20,117,'20:117','. Netugamba nti owange Adam mazima ono (Sitane) mulabewo, era mulabe wa Mukyalawo, temukkirizanga naabafulumya mu jjana nemwonooneka.');
INSERT INTO "african_dev" VALUES(20,118,'20:118','. Mazima olina ggwe, obutawulira njala mu yo, (e jjana) wadde okuyita obwereere.');
INSERT INTO "african_dev" VALUES(20,119,'20:119','. Era nti mazima ggwe, togenda kuwulira nyonta, nga oli mu yo wadde okwokebwa omusana.');
INSERT INTO "african_dev" VALUES(20,120,'20:120','. Sitane yamulabankanya, naamugamba nti owange Adam, abaffe nkulagirire omuti oguwa obulamu obw''olubeerera, n''obufuzi obutaggwaawo.');
INSERT INTO "african_dev" VALUES(20,121,'20:121','. Olwo nno (Adam ne Haawa) nebalya ku muti, obwereere bwabwe nebubalabikira, nebatandika okwebikka ebikoola byo mu jjana, era nekiba nti Adam yajeemera Mukama omulabiriziwe naabula.');
INSERT INTO "african_dev" VALUES(20,122,'20:122','. Oluvanyuma Mukama omulabiriziwe, yamusembeza nakkiriza okwenenyakwe era naamulungamya.');
INSERT INTO "african_dev" VALUES(20,123,'20:123','. (Katonda) naagamba nti mwenna mugiveemu (mwe ababiri ne sitaane), abamu mu mmwe baliba balabe ba bannaabwe naye bwe buliba bubajjidde obulungamu, okuva gyendi oyo yenna aligoberera okulungamya kwange, tagenda kubula, wadde okwonooneka.');
INSERT INTO "african_dev" VALUES(20,124,'20:124','. Ate oyo ava ku kubuulirira kwange, mazima abeera mu bulamu obunyiga, ate ku lunaku lw''enkomerero tugenda kumuzuukiza nga muzibe.');
INSERT INTO "african_dev" VALUES(20,125,'20:125','. Aligamba nti ayi Mukama omulabirizi wange, lwaki onzuukizza nga ndi muzibe, nga ate mazima nali ndaba.');
INSERT INTO "african_dev" VALUES(20,126,'20:126','. (Katonda) naagamba nti bwe kityo, ebigambo byaffe byakujjira noobyerabira, mu ngeri y''emu olwa leero werabiddwa.');
INSERT INTO "african_dev" VALUES(20,127,'20:127','. Bwe kityo bwe tusasula oyo eyayonoona naatakkiriza bigambo bya Mukama omulabiriziwe, ate nga lubeerera ebibonerezo by''o ku lunaku lw''enkomerero, bye bisinga obukakali era bye by''olubeerera.');
INSERT INTO "african_dev" VALUES(20,128,'20:128','. Abaffe tekibayigiriza, eky''okuba nti emirembe emeka gye twazikiriza oluberyeberye lwabwe (bano), nga batambula mu bitundu bya bali (betwazikiriza) mazima mu ekyo mulimu eby''okuyiga eri ab''amagezi.');
INSERT INTO "african_dev" VALUES(20,129,'20:129','. Era singa si kigambo ekyakulembera nga kyava ewa Mukama omulabiriziwo, era singa tekiri nti entuuko ya buli omu ngere olwo kyandibadde kikakafu (okubazikiriza).');
INSERT INTO "african_dev" VALUES(20,130,'20:130','. Kale nno gumiikiriza ku ebyo bye boogera, era otendereze ebitendo bya Mukama omulabiriziwo, nga enjuba tennavaayo n''era nga tennagwa, era ne mu kiseera ky''ekiro mutendereze ne ku ssaawa ez''emisana olwo nno obeere mu basiima.');
INSERT INTO "african_dev" VALUES(20,131,'20:131','. Era totunuzanga amaaso go eri ebyo bye tweyagaza nabyo abaafumbiriganwa mu bo (abakaafiiri), nga bya kwewunda bya bulamu bwansi, tubakeme nabyo. Era bulijjo okugabirira kwa Mukama omulabiriziwo kwe kusinga obulungi era kwe kw''olubeerera.');
INSERT INTO "african_dev" VALUES(20,132,'20:132','. Era lagira abantu bo okusaala, era ogumiikirize ku kwo (okusaala), tetukusaba nti weegabirire, ffe tukugabirira. Enkomerero eri mu kutya Katonda.');
INSERT INTO "african_dev" VALUES(20,133,'20:133','. Era (abakaafiiri) bagamba nti singa atuleetera akabonero okuva ewa Mukama omulabiriziwe, naye abaffe tebwabajjira obunnyonnyofu, bw''ebyo obuli mu bitabo ebyasooka.');
INSERT INTO "african_dev" VALUES(20,134,'20:134','. Era singa twabazikiriza n''ebibonerezo oluberyeberyelwe, (Nabbi Muhammad nga tannatumwa) bandigambye nti ayi Mukama omulabirizi waffe, singa watutumira omubaka, olwo nno netugoberera ebigambo byo, netutatuuka ku kukkakkanyizibwa netuswaala.');
INSERT INTO "african_dev" VALUES(20,135,'20:135','. Gamba nti buli omu alindiridde, kale mulindirire, lumu mulimanya, baani abaali ku kkubo eggolokofu, era ani yalungama.');
INSERT INTO "african_dev" VALUES(21,1,'21:1','. Kusemberedde abantu okubalibwa kwa bwe nga bo bali mu butafaayo, era nga bo bakola byennyume.');
INSERT INTO "african_dev" VALUES(21,2,'21:2','. Tewali kujjukiza kwonna kubajjira okupya okuva ewa Mukama omulabirizi waabwe okugyako okuba nti bakuwuliriza nga bwe bazannya.');
INSERT INTO "african_dev" VALUES(21,3,'21:3','. Emitima gya bwe nga miragajjavu, abo abeeyisa obubi ne boogera mu kyama nga bagamba nti, abaffe ono alina kyali okugyako okuba nti muntu nga mmwe, abaffe mugenda ku ddogo nga nammwe mulabira ddala!.');
INSERT INTO "african_dev" VALUES(21,4,'21:4','. (Nabbi Muhammad) naagamba nti: Mukama omulabirizi wange amanyi ekigambo ekiri mu ggulu ne mu nsi era yye y''awulira ennyo, amanyi ennyo.');
INSERT INTO "african_dev" VALUES(21,5,'21:5','. (Wabula Abakaafiiri) baagamba nti: okwo kulogojjana mu kuloota oba ye yennyini yaabyegunjirawo, si nakindi yye mutontomi, (bwaba ayagala tukkirize byagamba) kale atuleetere akabonero nga abaasooka bwe baatumwa.');
INSERT INTO "african_dev" VALUES(21,6,'21:6','. Tewali kitundu kye twazikiriza nga abantu ba mu bakkiriza, abaffe bo be banakkiriza!.');
INSERT INTO "african_dev" VALUES(21,7,'21:7','. Oluberyeberyelwo tetwatumanga okugyako basajja nga tubawa obubaka kale mubuuze abalina okumanya bwe muba nga temumanyi.');
INSERT INTO "african_dev" VALUES(21,8,'21:8','. Era tetwafuula (ba Nabbi) mubiri nga tebalya mmere (nga ba Malayika) era tebaali ba kubeera mu nsi lubeerera.');
INSERT INTO "african_dev" VALUES(21,9,'21:9','. Oluvanyuma twabatuukiririza endagaano (gye twabawa) netubawonya awamu n''abo be twayagala era netuzikiriza abaayonoona ekisukkiridde.');
INSERT INTO "african_dev" VALUES(21,10,'21:10','. Mazima twabassiza ekitabo (Kur’ane) nga kirimu okwogera ku kitiibwa kya mmwe abaffe temutegeera!.');
INSERT INTO "african_dev" VALUES(21,11,'21:11','. Ebyalo bimeka bye twazikiriza ebyali byeyisa obubi olwo nno netuleeta abantu abalala (oluvanyuma lwa bwe).');
INSERT INTO "african_dev" VALUES(21,12,'21:12','. Bwe baamala okumanya nti ebibonerezo byaffe bijja olwo nnonebabidduka.');
INSERT INTO "african_dev" VALUES(21,13,'21:13','. (Baagambibwa) nti temudduka era mudde eri ebyo bye mubadde mweyagaliramu era mudde mu mayumba gammwe luliba lumu mulibuuzibwa.');
INSERT INTO "african_dev" VALUES(21,14,'21:14','. Nebagamba nti: zitusanze mazima ffe twali tweyisa bubi.');
INSERT INTO "african_dev" VALUES(21,15,'21:15','. Okuwanjaga kwa bwe okwo kwali bwe kutyo okutuusa lwe twabafuula nga ennimiro ekunguddwa ne ggwawo.');
INSERT INTO "african_dev" VALUES(21,16,'21:16','. Tetwatonda eggulu n''ensi n''ebiri wakati wa byombi nga bya muzannyo.');
INSERT INTO "african_dev" VALUES(21,17,'21:17','. Singa twayagala kweteerawo muzannyo, twaligukoze mu bano abali eno gye tuli singa twali ba kukikola.');
INSERT INTO "african_dev" VALUES(21,18,'21:18','. Wabula tukasuka amazima ku bulimba negabuggyawo, olwo nno bwo nebuvaawo era mugenda kubonerezebwa olw''ebyo bye mwogera.');
INSERT INTO "african_dev" VALUES(21,19,'21:19','. Bibye ebyo byonna ebiri mu ggulu ne mu nsi, n''abo abali waali tebeekuluntaza ku kumusinza era tebakaluubirirwa.');
INSERT INTO "african_dev" VALUES(21,20,'21:20','. Batendereza (Katonda) emisana n''ekiro tebakoowa.');
INSERT INTO "african_dev" VALUES(21,21,'21:21','. Oba beeteerawo ba Katonda nga ba ku nsi nga bo be balizuukiza (abafu).');
INSERT INTO "african_dev" VALUES(21,22,'21:22','. Singa (ensi n''eggulu) byalimu ba katonda abalala atali Katonda omu byombi byaliyonoonese, (ekyo nno kikulage) nti yasukkuluma Katonda Mukama omulabirizi wa Arishi kw''ebyo bye boogera.');
INSERT INTO "african_dev" VALUES(21,23,'21:23','. Tabuuzibwa ku byakola ate bo babuuzibwa.');
INSERT INTO "african_dev" VALUES(21,24,'21:24','. Abo bateekawo ba katonda ne baleka Katonda omu, gamba nti muleete obujulizi bwa mmwe (ku ekyo). Kuno kwe kubuulirira abali nange era kwe kubuulirira kw''abo abaakulembera wabula abasinga obungi mu bo (Abakaafiiri) tebamanyi mazima olwo nno bo bagakuba amabega.');
INSERT INTO "african_dev" VALUES(21,25,'21:25','. Era tetwatuma oluberyeberyelwo Mubaka yenna okugyako nga tumutumira obubaka nti tewali kisinzibwa kyonna (mu butuufu) okugyako nze (Katonda) kale munsinze.');
INSERT INTO "african_dev" VALUES(21,26,'21:26','. Era baagamba nti: Katonda ow''ekisa ekingi yeeteerawo omwana, musukkulumu (ku ebyo byonna) wabula (ba malayika bebamugattako) baddu abaweebwa ekitiibwa.');
INSERT INTO "african_dev" VALUES(21,27,'21:27','. Tebayinza kumukulembera na kigambo era bbo bakolera ku kiragirokye.');
INSERT INTO "african_dev" VALUES(21,28,'21:28','. Amanyi ebyo ebiri mu maaso gaabwe n''ebyo ebiri emabega waabwe, tebagenda kuwolereza okugyako oyo gwaliba asiimye nabo olw''okumutya beegendereza.');
INSERT INTO "african_dev" VALUES(21,29,'21:29','. Oyo yenna mubo agamba nti mazima nze ndi Katonda naaleka Katonda omu, oyo tugenda kumusasula omuliro Jahannama. Bwe tutyo bwe tusasula abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(21,30,'21:30','. Abo abaakaafuwala tebakiraba nti mazima eggulu omusanvu n''ensi byali kitole netubyabuluzaamu era netuggya mu mazzi buli kintu ekiramu abaffe tebakkiriza!.');
INSERT INTO "african_dev" VALUES(21,31,'21:31','. Era netussa mu nsi ensozi ebe nga tebayuuza era netussa mu yo amakubo kibayambe okumanya gye balaga.');
INSERT INTO "african_dev" VALUES(21,32,'21:32','. Era netufuula eggulu akasolya akakuumibwa, wabula bbo tebafaayo ku bya magero bya byo (ebya byonna ebyogeddwa waggulu).');
INSERT INTO "african_dev" VALUES(21,33,'21:33','. Era (Katonda) yye yooyo eyatonda ekiro n''emisana n''enjuba n''omwezi buli kimu kiri mu bbanga (nga byonna) biwuga.');
INSERT INTO "african_dev" VALUES(21,34,'21:34','. Era tetuwanga muntu yenna oluberyeberyelwo buwangaazi obw''olubeerera, bwoba ofudde bo be b''okubeerawo olubeerera.');
INSERT INTO "african_dev" VALUES(21,35,'21:35','. Buli muntu wa kukomba ku kufa era tubagezesa n''obubi n''obulungi nga kikemo era gye tuli gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(21,36,'21:36','. Abaakaafuwala bwe bakulaba tebakufuula okugyako eky''omuzannyo (ne bagamba) nti ono y''oyo ayogera ku bye musinza nga nabo bawakanya okwogera ku Katonda ow''ekisa ekingi.');
INSERT INTO "african_dev" VALUES(21,37,'21:37','. Omuntu yatondebwa nga ayanguyiriza nja ku balaga obubonerero bwange kale temunsaba kubanguyiriza bibonerezo.');
INSERT INTO "african_dev" VALUES(21,38,'21:38','. Era abakaafiiri bagamba nti ekiragaanyiso ekyo kiribaawo ddi bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(21,39,'21:39','. Abo abaakaafuwala singa bamanyi (ekiribaawo) mu kiseera we batalisobolera kuziyiza muliro kutuuka ku byenyi bya bwe wadde e migongo gya bwe, era nga tebagenda kutaasibwa.');
INSERT INTO "african_dev" VALUES(21,40,'21:40','. Wabula biribatuukako kibwatukira nebibakanga, tebalisobola kubizzayo era si baakulindirizibwa.');
INSERT INTO "african_dev" VALUES(21,41,'21:41','. Mazima ababaka (bangi) baagyejebwa olubereberyelwo, nebituuka ku abo abaabanyoomanga ebyo bye baalinga bajeeja. (Abo abaali babajeeja baatuukibwako ebibonerezo bye baali bajeeja)');
INSERT INTO "african_dev" VALUES(21,42,'21:42','. Bagambe nti ani abakuuma ekiro n''emisana okubawonya ekyandibatuseeko okuva ewa Katonda ow''ekisa ekingi wabula bbo abatakkiriza beesamba okwogera ku Mukama omulabirizi waabwe.');
INSERT INTO "african_dev" VALUES(21,43,'21:43','. Abaffe balina ba katonda abalibawonya ku ffe abatali ffe (ba katonda baabwe bennyini) tebagenda kusobola kwetaasa era tebagenda kufuna gwe baliba naye abawonya ku ffe.');
INSERT INTO "african_dev" VALUES(21,44,'21:44','. Wabula twabawa bo ne bakitaabwe okweyagala, okutuusa obuwangaazi lwe bwabawanvuwako, abaffe tebalaba nti mazima ffe, tuddira ensi netugikendeeza okuva ku nsalo zaayo abaffe bo be b''okulema!.');
INSERT INTO "african_dev" VALUES(21,45,'21:45','. Bagambe nti mazima mbatiisa nga nkozesa obubaka, wabula ba kiggala tebawulira kukoowoola bwe baba balabuddwa.');
INSERT INTO "african_dev" VALUES(21,46,'21:46','. So nga bwe balituukwako akatundu ku bibonerezo bya Mukama omulabiriziwo bagenda kugambira ddala nti zitusanze mazima ffe tubadde tweyisa bubi.');
INSERT INTO "african_dev" VALUES(21,47,'21:47','. Tugenda kussaawo minzane entuufu ezirikozesebwa ku lunaku lw''enkomerero, olwo nno tewali muntu aliryazaamaanyizibwa kintu kyonna, ne bwe kiriba kyenkana obuzito bwe mpeke ya khardali tugenda kukireeta. Kimala bumazi okuba nti ffe tuliba ababazi.');
INSERT INTO "african_dev" VALUES(21,48,'21:48','. Twawa Musa ne Haruna amateeka (Taurat) agaawula wakati wa mazima n''obulimba era nga kitangaaala era nga kya kubuulirira eri abatya Katonda.');
INSERT INTO "african_dev" VALUES(21,49,'21:49','. Abo abatya Mukama omulabirizi waabwe awamu n''okuba nga tebamulaba era nga nabo beeraliikirira ekiseera ky''enkomerero.');
INSERT INTO "african_dev" VALUES(21,50,'21:50','. Eno (k’urane) kubuulirira okw''omukisa kwe twassa, abaffe mugiwakanya?.');
INSERT INTO "african_dev" VALUES(21,51,'21:51','. Mazima twawa Ibrahim obulungamubwe okuva oluberyeberye era twali tumanyi ebimukwatako.');
INSERT INTO "african_dev" VALUES(21,52,'21:52','. Jjukira bwe yagamba kitaawe n''abantu be nti: bifaananyi ki bino, ebyo mmwe bye mulemeddeko!.');
INSERT INTO "african_dev" VALUES(21,53,'21:53','. Nebagamba nti twasanga bakadde baffe nga byebasinza.');
INSERT INTO "african_dev" VALUES(21,54,'21:54','. (Ibrahim) naagamba nti mazima mmwe ne bakadde ba mmwe mwali mu bubuze obw''olwatu.');
INSERT INTO "african_dev" VALUES(21,55,'21:55','. Nebagamba nti otuleetedde mazima oba oli mu ba kazannyirizi.');
INSERT INTO "african_dev" VALUES(21,56,'21:56','. Naagamba nti wabula Mukama omulabirizi wa mmwe, ye Mukama omulabirizi w''eggulu (omusanvu) n''ensi oyo eyabitonda nange ku ekyo ndi omu ku bajulizi.');
INSERT INTO "african_dev" VALUES(21,57,'21:57','. Era ndayira Katonda amasanamu ga mmwe nja kugasalira amagezi singa munaaba mugaleseewo ne mubaako wemulaga.');
INSERT INTO "african_dev" VALUES(21,58,'21:58','. (Yagabetenta) naagafuula bubajjo okugyako (essanamu) erigasinga obunene, olwo nno badde gye liri (lye baba babuuza).');
INSERT INTO "african_dev" VALUES(21,59,'21:59','. (Bwe badda) nebagamba nti ani akoze kino ku ba katonda baffe, mazima yye ali mu beeyisa obubi.');
INSERT INTO "african_dev" VALUES(21,60,'21:60','. Nebagamba nti twawulidde omuvubuka ayitibwa Ibrahim nga agoogerako.');
INSERT INTO "african_dev" VALUES(21,61,'21:61','. Nebagamba nti kale mumuleete mu maaso g''abantu bamuweeko obujulizi.');
INSERT INTO "african_dev" VALUES(21,62,'21:62','. (Bwe baamutuusa) nebamugamba nti: Gwe wakoze kino ku ba katonda baffe owange Ibrahim!.');
INSERT INTO "african_dev" VALUES(21,63,'21:63','. Naabagamba nti: nedda omukulu waabwe ono ye yakikoze, kale mubabuuze bwe baba nga baatula.');
INSERT INTO "african_dev" VALUES(21,64,'21:64','. Nebeetunulako nebagamba nti mazima mmwe, mmwe abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(21,65,'21:65','. Ate oluvanyuma (lw''ekyo) beekyusa, (mu kifo ky''okwenenya baalemera ku bukaafiiri era nebagamba nti) mazima okimanyi nti amasanamu ago tegoogera.');
INSERT INTO "african_dev" VALUES(21,66,'21:66','. (Ibrahim) naagamba nti ate muva ku Katonda nemusinza ebitabagasa kantu konna wadde okubatuusaako akabi.');
INSERT INTO "african_dev" VALUES(21,67,'21:67','. Kivve ku mmwe, lwaki musinza ebintu ebirala nemuleka Katonda abaffe temutegeera!.');
INSERT INTO "african_dev" VALUES(21,68,'21:68','. Nebagamba nti (oba bwatyo bwagamba) mumwokye mutaase ba katonda ba mmwe bwe muba nga mulina kye munaakola.');
INSERT INTO "african_dev" VALUES(21,69,'21:69','. Netugamba nti owange muliro beera bunnyogovu era mirembe ku Ibrahim.');
INSERT INTO "african_dev" VALUES(21,70,'21:70','. Baayagala okumukolera olukwe naye twabafuula abataalufunamu.');
INSERT INTO "african_dev" VALUES(21,71,'21:71','. Netumuwonya netuwonya ne Luutu netubatwala mu nsi gye twawa omukisa ku lw''ebitonde.');
INSERT INTO "african_dev" VALUES(21,72,'21:72','. Twamugabira Ishaka ne Yakubu nga nnyongeza, bonna twabafuula balongoofu.');
INSERT INTO "african_dev" VALUES(21,73,'21:73','. Era twabafuula abakulembeze nga balungamya olw''ekiragiro kyaffe, era twabatumira nti (bateekwa) okukola obulungi n''okuyimirizaawo e sswala n''okutoola zzaka era baali batusinza.');
INSERT INTO "african_dev" VALUES(21,74,'21:74','. Ne Luutu twamuwa okusengejja ensonga n''okumanya era twamuwonya netumuggya mu kitundu ekyali kikola ebyonoono mazima bo baali bantu ba kibi aboonoonyi.');
INSERT INTO "african_dev" VALUES(21,75,'21:75','. Era twamuyingiza mu kusaasira kwaffe mazima yye wa mu balongoofu.');
INSERT INTO "african_dev" VALUES(21,76,'21:76','. Era (mujjukire) Nuhu bwe yasaba oluberyeberye nga abantu (bamulemeredde) netwanukula okusaba kwe netumuwonya wamu n’abantube mu kabi akanene.');
INSERT INTO "african_dev" VALUES(21,77,'21:77','. Netumutaasa ku bantu abo abaalimbisa ebigambo byaffe, mazima bbo baali bantu ba kibi netubazikiriza bonna.');
INSERT INTO "african_dev" VALUES(21,78,'21:78','. Era mujjukire Dauda ne Sulaiman mu kiseera bombi bwe baalamula omusango ogukwata ku nnimiro, embuzi z’abantu bwe zaaliiramu, era ku kulamula kwabwe twali bajulizi.');
INSERT INTO "african_dev" VALUES(21,79,'21:79','. (Omusango ogwo) twawa Sulaiman okutegeera obulungi, naye buli omu twamuwa okugoba ensonga n’okumanya, era twagondeza Dauda ensozi nga zitendereza, n’ebinyonyi bwe bityo, (buli lwe yatenderezanga nga nabyo bitendereza). Era ebyo twali tusobolera ddala okubikola.');
INSERT INTO "african_dev" VALUES(21,80,'21:80','. Era twamuyigiriza okukola ebyambalo bya mmwe (eby''entalo) biryoke bibakuume ku buzibu bwa mmwe, abaffe mmwe mwebaza!.');
INSERT INTO "african_dev" VALUES(21,81,'21:81','. Era Sulaiman twamugondeza empewo ekungunta nga etambulira ku kiragirokye neEgenda eri ensi eyo gye twawa omukisa. Era twali tumanyi buli kintu.');
INSERT INTO "african_dev" VALUES(21,82,'21:82','. Ne mu zi Sitane mwalimu ezibbira mu nnyanja ku lulwe nga zikola n''emirimu emirala egitali ogwo era ffe twazikuumanga.');
INSERT INTO "african_dev" VALUES(21,83,'21:83','. Era (jjukira) Ayubu bwe yakoowoola Mukama omulabiriziwe nti mazima nze ntuukiddwako obuzibu, era ggwe musasizi asinga abasaasizi bonna.');
INSERT INTO "african_dev" VALUES(21,84,'21:84','. Twamwanukula (okusaba) kwe netumuggyako obuzibu obwali bumutuuseeko netumuwa abantube (abaali bafudde) netumuwa n’abalala awamu nabali, ekyo nga kusaasira okuva gye tuli era nga kya kubuulirira eri abasinza (Katonda).');
INSERT INTO "african_dev" VALUES(21,85,'21:85','. Era (jjukira) Ismail ne Idrisa ne Zulikifuli bonna ba mu bagumiikiriza.');
INSERT INTO "african_dev" VALUES(21,86,'21:86','. Era twabayingiza mu kusaasira kwaffe mazima bo ba mu balongoofu.');
INSERT INTO "african_dev" VALUES(21,87,'21:87','. Era (jjukira) ekyafaayo kya nannyini kyenyanja (Yunus) bwe yagenda nga musunguwavu naalowooza nti tetugenda kumusobola, ate bwe yamala okubeera mu kizikiza yakoowoola nti tewali kisinzibwa kyonna okugyako ggwe wasukkuluma (Mukama wange) mazima nze mbadde mu beeyisa obubi.');
INSERT INTO "african_dev" VALUES(21,88,'21:88','. Awo nno twayanukula okusabakwe ne tumuwonya okweraliikirira, era bwe tutyo bwe tuwonya abakkiriza.');
INSERT INTO "african_dev" VALUES(21,89,'21:89','. Era jjukira Zakaria bwe yakoowoola Mukama omulabiriziwe (naagamba nti) ayi Mukama omulabirizi wange tondeka bwannamunigina (ne bwompa ezzadde nsigala ndi mukkiriza) anti ggwe osinga abasigalawo bonna.');
INSERT INTO "african_dev" VALUES(21,90,'21:90','. Awo nno twayanukula okusabakwe, era netumuwa Yahaya netusobozesa mukyalawe okuzaala, mazima bbo baalinga bettanira okukola ebirungi era nga batusaba ku lw''okwagala okufuna ebirungi bye tulina ne ku lw''okutya ebibonerezo byaffe era baalinga batugondera.');
INSERT INTO "african_dev" VALUES(21,91,'21:91','. Era (jjukira) ekyafaayo ky''omukyala (Mariyamu) oyo eyakuuma obwereerebwe olwo nno netumufuuwamu omwoyo ogwava gye tuli netumufuula yye n’omwanawe nga kya kulabirako eri ebitonde byonna.');
INSERT INTO "african_dev" VALUES(21,92,'21:92','. Mazima kino ekibiina kyammwe kiri ekibiina kimu nange nze Mukama omulabirizi wa mmwe kale munsinze.');
INSERT INTO "african_dev" VALUES(21,93,'21:93','. (Naye abantu) baayawukana mu kigambo ky''eddiini yaabwe wakati waabwe (wabula) bonna gye tuli gye bagenda okudda.');
INSERT INTO "african_dev" VALUES(21,94,'21:94','. Oyo akola omulimu omulungi gwonna nga mukkiriza tayinza butasasulwa olw''okutakabanakwe era mazima tumuwandiikira (emirimugye).');
INSERT INTO "african_dev" VALUES(21,95,'21:95','. Kizira ku kitundu kyonna kye twazikiriza (okudda ku nsi) mazima bo tebayinza kudda.');
INSERT INTO "african_dev" VALUES(21,96,'21:96','. Okutuusa nga Yaajuuja ne Majuuja bagguliddwa era nga nabo bagenda kufukumuka nga bava mu buli kkubo.');
INSERT INTO "african_dev" VALUES(21,97,'21:97','. Endagaano eya mazima neba nga esembedde olwo nno oligenda okulaba nga amaaso gaabo abaakaafuwala galalambadde (olw''entiisa nebagamba nti) zitusanze ekintu kino tubadde tukigayaaliridde si kyokka wabula tubadde tweyisa bubi.');
INSERT INTO "african_dev" VALUES(21,98,'21:98','. Mazima mwe n''ebyo bye musinza nemuva ku Katonda mugenda kubeera nku za Jahannama (omuliro) mwe muli ba kuguyingira.');
INSERT INTO "african_dev" VALUES(21,99,'21:99','. Singa ebyo (bye musinza) nga baali ba Katonda ddala tebyandiguyingidde naye bonna ba kugubeeramu bugenderevu.');
INSERT INTO "african_dev" VALUES(21,100,'21:100','. Balituukibwako nga bali mu gwo okuyiriitira era tebagenda kuwulira nga bali mu gwo.');
INSERT INTO "african_dev" VALUES(21,101,'21:101','. Mazima abo be twamanya edda nti bagenda kubeera balongoofu abo nno bagenda kubeera wala n’omuliro.');
INSERT INTO "african_dev" VALUES(21,102,'21:102','. Tebagenda kuwulira lukwakwayo lwa gwo era mazima bo ku ebyo emyoyo gya bwe gye byagala ba kubeera mu byo obugenderevu.');
INSERT INTO "african_dev" VALUES(21,103,'21:103','. Ebikangabwa ebisinga okuba ebyamanyi tebigenda kubatiisa, ba Malayika balibaaniriza nga babayozaayoza nti luno lwe lunaku lwa mmwe olwo lwe mwali mulagaanyisibwa..');
INSERT INTO "african_dev" VALUES(21,104,'21:104','. Olunaku lwe tulizingako eggulu nga eggwandiikiro bwe rizingako ebiwandiiko. Mu ngeri y''emu nga bwe twatandika ebitonde bwe tugenda okubizikiriza, nga kikakafu ku ffe anti mazima ekyo tusobolera ddala okukikola.');
INSERT INTO "african_dev" VALUES(21,105,'21:105','. Mazima twawandiika mu Zaburi oluvanyuma lw''okuwandiika ku Lauhil Mahfuudhwi nti mazima ensi egenda kusikirwa abaddu bange abalongoofu.');
INSERT INTO "african_dev" VALUES(21,106,'21:106','. Mazima ebyo, (ebyogeddwako wano) kwe kubuulirira okwannamaddala eri abantu abasinza Katonda.');
INSERT INTO "african_dev" VALUES(21,107,'21:107','. Tetwakutuma ggwe (Nabbi Muhammad) okugyako nga oli kya kusaabira eri ebitonde.');
INSERT INTO "african_dev" VALUES(21,108,'21:108','. Bagambe (Ggwe Nabbi Muhammad) nti mazima mpeebwa obubaka (nga obubaka bugamba) nti: mazima Katonda wa mmwe ali Katonda omu, abaffe mmwe munaasiramuka?.');
INSERT INTO "african_dev" VALUES(21,109,'21:109','. Kale nno bwe bamala nebaawukana ku ekyo, (nebatasiramuka) olwo nno ggwe bagambe nti mwenna mbawulizza kyenkanyi (nga tewali gwe ndeseeyo) era siri nze amanyi nti bye mulagaanyisibwa biri kumpi oba biri wala.');
INSERT INTO "african_dev" VALUES(21,110,'21:110','. Mazima yye (Katonda) amanyi bye mwolesa nga mwogera era nga bwamanyi n''ebyo bye mukweka.');
INSERT INTO "african_dev" VALUES(21,111,'21:111','. Era simanyi obanga (okubalindiriza n''atabakangavvulirawo) nga kikemo gye muli era nga kubaleka kweyagala okutuusa ebbanga eggere.');
INSERT INTO "african_dev" VALUES(21,112,'21:112','. (Nabbi Muhammad) naagamba nti ayi Mukama omulabirizi wange salawo mu butuufu (wakati wange n''abo abannimbisa) era Mukama omulabirizi waffe omusaasizi ow''ekisa ekingi gwe tusaba obuyambi ku ebyo bye mwogera.');
INSERT INTO "african_dev" VALUES(22,1,'22:1','. Abange mmwe abantu, mutye Mukama omulabirizi wa mmwe anti mazima okukankana (kw''ensi) nga enkomerero etuuse kiriba kintu kinene nnyo.');
INSERT INTO "african_dev" VALUES(22,2,'22:2','. Olunaku lw''olikulaba (okukankana) buli Mukyala muzadde talissa mwoyo ku mwana (gwe yazaala) n''ayonsa, ate buli aliba alina olubuto lugenda kuvaamu, era oliraba abantu nga balinga abatamidde so nga tebatamidde (ekiribatuusa ku ekyo) lwa kuba nti mazima ebibonerezo bya Katonda biyitirivu.');
INSERT INTO "african_dev" VALUES(22,3,'22:3','. Mu ba ntu mulimu awakana ku bikwata ku Katonda nga talina kumanya kwonna, n''aba nga agoberera kyewaggula Sitane.');
INSERT INTO "african_dev" VALUES(22,4,'22:4','. (Sitane) kyamuwandiikwako nti ddala buli amwewa mazima yye amubuza era n''aba nga amuzza eri ebibonerezo by''omuliro.');
INSERT INTO "african_dev" VALUES(22,5,'22:5','. Abange mmwe abantu bwe muba nga mulina okubuusabuusa ku kuzuukira (mukimanye) nti mazima ffe twabatonda nga tubajja mu ttaka (okusinziira ku jajja mmwe Adam nga bwe yatondebwa) oluvanyuma (ne tuba nga) tubatonda mu mazzi agazaala nate oluvanyuma netubajja mu kisaayisaayi oluvanyuma ekifuuka ekinyamanyama ekituukiridde mu butonde bwakyo n''ekitatuukiridde olwo nno tube nga tubalaga (obuyinza bwaffe) olwo nno netukuumira mu nnabaana ekyo kye tuba twagadde okumala ebbanga eggere oluvanyuma tubafulumya nga muli bawere ate oluvanyuma mube nga mufuuka abakulu, ne mu mmwe mulimu abafa (nga bakyali) era mu mmwe mulimu abazzibwayo eri obuwangaazi obusinga okuba obunafu (n''akaddiwa nga takyesobola) n''aba nga takyasobola kumanya kintu kyonna oluvanyuma lw''okuba nga yali amanyi (ekirala ekiraga nti okuzuukira kulibaayo) ogenda noolaba ensi ng''ekaze naye ketugitonnyesaako enkuba edda buto n''etinta n''emeza buli mutindo gwa bimera ebirungi.');
INSERT INTO "african_dev" VALUES(22,6,'22:6','. Ekyo nno lwakuba nti mazima Katonda yye ye w''amazima era mazima yye agenda kulamusa abafu. Era nti mazima yye muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(22,7,'22:7','. Era nti mazima essaawa ey''enkomerero ejja, teriimu kubuusabuusa era nti mazima ddala Katonda agenda kuzuukiza abali mu kabbuli.');
INSERT INTO "african_dev" VALUES(22,8,'22:8','. Mu bantu mulimu oyo awakanya ebikwata ku Katonda nga tasinziira ku kumanya wadde okulungamizibwa oba ekitabo ekiraga obutangaavu.');
INSERT INTO "african_dev" VALUES(22,9,'22:9','. (Mu ngeri y’okulengezza n’okwekuza) aweta ensingoye abe nga ajja abantu ku kkubo lya Katonda, atuusibwako okuswazibwa mu nsi era tugenda kumukombesa ku lunaku lw''enkomerero ku bibonerezo ebyokya.');
INSERT INTO "african_dev" VALUES(22,10,'22:10','. (Aligambibwa nti) ekyo kikutUuseeko olw''ebyo e mikonogyo bye gyakola edda (ku nsi) era mazima Katonda talyazAamaanya baddu.');
INSERT INTO "african_dev" VALUES(22,11,'22:11','. Ne mu bantu mulimu asinza Katonda ng''ali ku ndeboolebo (ya bukkiriza) n''olwekyo bwatuukibwako ekirungi akkalira nakyo ate bwatuukibwako ekikemo oba okugesezebwa atalantuka n''agwira ku kyenyikye (naava ku ddiini) olwo nno naafiirwa ensi n''enkomerero okwo kwe kufaafaaganirwa okw''olwatu.');
INSERT INTO "african_dev" VALUES(22,12,'22:12','. Alekawo Katonda n''aasinza ebyo ebitamutuusaako kabi era ebitamutuusaako mugaso okwo kwe kubula okwewala.');
INSERT INTO "african_dev" VALUES(22,13,'22:13','. Asaba oyo akabiike nga ke kali okumpi okusinga omugasogwe, mubi nnyo asuubirwa okutaasa era mubi nnyo asuubirwa okuba nga ye w''omukwano.');
INSERT INTO "african_dev" VALUES(22,14,'22:14','. Mazima Katonda agenda kuyingiza e jjana abo abaakola emirimu emirungi, nga emigga gikulukutira wansi, waazo, mazima Katonda akola ekyo kyaba ayagadde.');
INSERT INTO "african_dev" VALUES(22,15,'22:15','. Omuntu alowooza nti Katonda tagenda kutaasa Mubakawe kuno ku nsi ne ku nkomerero kimugwanidde akasuke omuguwa waggulu ku kasolya oluvanyuma yeerippe (yeetuge) olwo nno alabe abaffe byakola bisobola okumuwonya byatayagala.');
INSERT INTO "african_dev" VALUES(22,16,'22:16','. (Katonda nga bwe yannyonnyola obuyinzabwe mu kusobola okuzuukiza abafu), mu ngeri y''emu annyonnyola engeri gye yassaamu Kur’ani naagamba nti) era bwe tutyo twagissa (Kur''ani) nga bigambo ebinnyonnyola (obulungi) era mazima Katonda alungamya gwaba ayagadde.');
INSERT INTO "african_dev" VALUES(22,17,'22:17','. Mazima abo abakkiriza (Nabbi Muhammad) n''abakkiriza eddiini ye Kiyudaaya (ku mulembe gwayo) n''abatasinza masanamu (nga Nabbi Muhammad tannatumwa) n''abakkiriza mu ddiini ye Kikurisitaayo (ku mulembe gwayo) n''abo abasinza omuliro, n''abo abagatta ku Katonda ebintu ebirala mazima Katonda agenda kusalawo wakati waabwe (ku ebyo bye baali basinza) ku lunaku lw''enkomerero. Anti mazima Katonda ku buli kintu mujulizi.');
INSERT INTO "african_dev" VALUES(22,18,'22:18','. Abaffe tolaba nti mazima Katonda bamuvunnamira abali mu ggulu omusanvu n''abo abali mu nsi n''enjuba n''omwezi n''emunyenye n''ensozi n''emiti n''ebisolo, nga n''abantu bangi nnyo (abamuvunnamira), era nga n''abantu bangi bakakatwako ebibonerezo (olwo butamuvunnamira) era omuntu Katonda gwaba ataddeko okunyoomebwa kale tayinza kufuna amussaamu kitiibwa. Anti mazima Katonda akola ekyo kyaba ayagadde.');
INSERT INTO "african_dev" VALUES(22,19,'22:19','. Bino ebibinja by''abantu ba mirundi ebiri baayawukana mu kutongoza Mukama omulabirizi waabwe, kale nekibeera nga, abakaafuwala batungirwa engoye nga za mu muliro nga bagenda kufukirirwa ku mitwe gya bwe amazzi ag''olwegye.');
INSERT INTO "african_dev" VALUES(22,20,'22:20','. Nga amazzi ago birisaanusibwa nagwo ebiri mu mbuto zaabwe n''amaliba gaabwe.');
INSERT INTO "african_dev" VALUES(22,21,'22:21','. Era nga bagenda kukubwanga ennyondo ezaakolebwa mu kyuma.');
INSERT INTO "african_dev" VALUES(22,22,'22:22','. Buli bwe baliba baagala okufuluma mu muliro ogwo ku olw''obulumi obulibatuukako mu gwo nga bazzibwa mu gwo (nga bagambibwa nti) era mukombe ku bukaawu bwe bibonerezo by''omuliro ogwokya ennyo.');
INSERT INTO "african_dev" VALUES(22,23,'22:23','. Mazima Katonda agenda kuyingiza mu jjana abo abakkiriza nebakola emirimu emirongoofu nga emigga gikulukutira wansi waazo. Nga bali mu zo, balyambazibwa ebikomo ebya zaabu ne Luulu nga n''engoye zebagenda okwambala ziriba za Siliki.');
INSERT INTO "african_dev" VALUES(22,24,'22:24','. Era nga baalungamizibwa (ku nsi) eri ekigambo ekisinga obulungi (eky''okwawula Katonda n''okumusuuta) era nga balungamizibwa eri ekkubo eritenderezebwa.');
INSERT INTO "african_dev" VALUES(22,25,'22:25','. Mazima abo abaakaafuwala era nebaziyiza abantu ku kkubo lya Katonda n’omuzikiti gw''emizizo ogwo gwe twateerawo abantu nga omutuuze weguli n''owewala bonna benkana, omuntu yenna ayagala okukoleramu obukyamu tumukombya ku bibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(22,26,'22:26','. Era jjukira ekiseera bwe twalaga Ibrahimu ekifo ky''enyumba (Kaaba), era ne mugamba nti tongattangako ekintu kyonna era tukuza e nyumba yange eri abagyetooloola n''abayimiriramu n''abakutama abavunnama (nga basaala).');
INSERT INTO "african_dev" VALUES(22,27,'22:27','. Era koowoola abantu baggye okukola Hijja, bagenda ku kujjira abatambuza ebigere, n''abalijjira ku nsolo enkovvu nga bava mu buli kkubo eryewala.');
INSERT INTO "african_dev" VALUES(22,28,'22:28','. Bagende balabe ebibagasa, era batendereze erinnya lya Katonda mu nnaku ezimanyiddwa olw''ebyo bye yabagabira mu bisolo eby''awaka (ebirundibwa) kale mulye ku byo era muliise omwetaavu omwavu.');
INSERT INTO "african_dev" VALUES(22,29,'22:29','. Ekyo nga kiwedde babe nga bamaliriza emikolo gya bwe era batuukirize bye beeyama era betooloole e nyumba kaasangwawo.');
INSERT INTO "african_dev" VALUES(22,30,'22:30','. Ebyo (babikola lwa kuwa Mukama Katonda kitiibwa) era bulijjo buli yenna agulumiza emizizo gya Katonda ekyo kye kirungi gyali mu maaso g''a Mukama omulabiriziwe. Era mwakkirizibwa okulya (ennyama y''e) bisolo ebirundibwa, okugyako ebyo ebibasomerwa mu ngeri y''emu mwewale amasanamu olw''obubi bwago, era mwewale ebigambo ebitemerere.');
INSERT INTO "african_dev" VALUES(22,31,'22:31','. Mulina kubeera abeewa Katonda yekka nga temumugattako kintu kirala kyonna, oyo yenna agatta ku Katonda ekintu ekirala aba nga awanuse waggulu ebinyonyi nebimusikula oba empewo neemusuula mu kifo eky''ewala ennyo.');
INSERT INTO "african_dev" VALUES(22,32,'22:32','. (Ebyo eby''okukola Hijja n''okusala ebisolo biragiro bya Katonda) era bulijjo omuntu agulumiza obubonero bwa Katonda mazima ekyo kibalibwa mu bikolwa by''emitima egitya Katonda.');
INSERT INTO "african_dev" VALUES(22,33,'22:33','. Mulina mu byo (ebirabo bye muwaayo mu Hijja) emigaso (nga okubyebagala n''okunywa amata gaabyo) okutuusa ebbanga eggere, oluvanyuma ekifo wezirina okusalirwa kwe kuba nga zituuka awali e nyumba kaasangwawo.');
INSERT INTO "african_dev" VALUES(22,34,'22:34','. Buli bantu twabateerawo emikolo gy''okusaddaaka ebisolo balyoke babe nga boogera ku linnya lya Katonda olw''ebyo bye yabagabira mu bisolo ebirundibwa, yye Katonda wa mmwe ali Katonda omu, yye gwe muba mu siramuka ku lulwe, era sanyusa (ggwe Muhammad) abo abatebenkedde (ku kusinza Katonda.)');
INSERT INTO "african_dev" VALUES(22,35,'22:35','. (Bo) beebo, Katonda buli lwayogerwako emitima gya bwe gitya era abagumiikiriza ku ebyo ebiba bibatuuseeko era abayimirizaawo e sswala era abawaayo ku ebyo bye tubagabira.');
INSERT INTO "african_dev" VALUES(22,36,'22:36','. Ne ngamiya twazibateerawo nga za mu bubonero bwa Katonda, mulina mu zo ebirungi, kale muzoogerereko erinnya lya Katonda nga muziteeseteese okuzisala (bwe muzisiba okugulu okumu nezisigala nga ziyimiridde ku asatu) olwo nno embuto zaazo bwe zigwa ku ttaka mukkirizibwa okuzirya, era muliiseeko atalina n''omwetaavu, bwe tutyo twazibagondeza mube nga mwebaza.');
INSERT INTO "african_dev" VALUES(22,37,'22:37','. Ennyama ya zo n''omusayi gwa zo tebituuka ku Katonda naye atuukwako okumutya okuva mu mmwe, bwatyo yazibagondeza mube nga mutendereza Katonda olw''ebyo bye yabalungamyamu era sanyusa abalongoosa.');
INSERT INTO "african_dev" VALUES(22,38,'22:38','. Mazima Katonda alwanirira abo abakkiriza, mazima Katonda tayagala oyo yenna omukumpanya omuwakanyi.');
INSERT INTO "african_dev" VALUES(22,39,'22:39','. Kikkiriziddwa (okwerwanako) eri abo abalwanyisibwa mu ngeri y''okuba nti mazima bo balyazaamaanyizibwa era mazima bulijjo Mukama Katonda asobolera ddala okubataasa.');
INSERT INTO "african_dev" VALUES(22,40,'22:40','. (Bo) beebo abaagobwa mu mayumba gaabwe awatali nsonga okugyako okuba nga bagamba nti: Katonda ye Mukama omulabirizi waffe, singa Katonda tajjawo bantu ng''akozesa abalala, amasinzizo ga bakabona, n''amakanisa ga ba Kurisitayo, n''amasinzizo ga ba Yudaaya n''emizikiti gy''abasiraamu, omwogererwa ennyo ku linnya lya Katonda byandimenyeddwa era Katonda ajja kutaasiza ddala abo abamutaasa, mazima Katonda wa maanyi Nantakubwa ku mukono.');
INSERT INTO "african_dev" VALUES(22,41,'22:41','. (Katonda baataasa) beebo bwe tuba tubatebenkezza mu nsi bayimirizaawo e sswala nebatoola zzaka nebalagira okuyisa obulungi, nebaziyiza okuyisa obubi era enkomerero y''ebintu byonna ya Katonda.');
INSERT INTO "african_dev" VALUES(22,42,'22:42','. Singa bakulimbisa mazima oluberyeberye lwa bwe abantu ba Nuhu na ba A’di na ba Thamud baalimbisa.');
INSERT INTO "african_dev" VALUES(22,43,'22:43','. Nabwekityo n''abantu ba Ibrahim n''abantu ba Luutu.');
INSERT INTO "african_dev" VALUES(22,44,'22:44','. N''abantu be Madiyana. Ne Musa yalimbisibwa nennindiriza abakaafiiri oluvanyuma nnabakwata, naye okubonereza kwange kwali kutya!.');
INSERT INTO "african_dev" VALUES(22,45,'22:45','. Ebitundu bimeka bye twazikiriza nga byo biri mu kweyisa bubi! (nga nabuli kati) obusolya bwa byo buguddemu n''enzizi ezitakyakimwako mazzi n''ebizimbe ebyali bizimbiddwa obulungi.');
INSERT INTO "african_dev" VALUES(22,46,'22:46','. Abaffe tebatambulako mu nsi nebafuna emitima gye bategeera nagyo oba amatu ge bawulira nago, mazima amaaso si ge gaziba naye emitima egiri mu bifuba gye giziba.');
INSERT INTO "african_dev" VALUES(22,47,'22:47','. Bakugamba obatuuseeko ebibonerezo mu bwangu naye Katonda tayinza kwawukana ku ndagaanoye (ajja kubibatuusaako ku lunaku lw''enkomerero) era mazima olunaku lumu ewa Mukama omulabiriziwo lulinga emyaka lukumi mu mbala ya mmwe.');
INSERT INTO "african_dev" VALUES(22,48,'22:48','. Era ebitundu bimeka bye nnalindiriza so nga byo byali mu kweyisa bubi naye oluvanyuma nembikwata era gyendi yokka y''eri obuddo.');
INSERT INTO "african_dev" VALUES(22,49,'22:49','. Gamba (Ggwe Muhammad) abange mmwe abantu mazima nze gye muli ndi mutiisa ow''olwatu.');
INSERT INTO "african_dev" VALUES(22,50,'22:50','. Bo abakkiriza ne bakola emirimu emirongoofu balina ekisonyiwo n''okugabirirwa okw''ekitiibwa.');
INSERT INTO "african_dev" VALUES(22,51,'22:51','. Naabo abeewaayo okulemesa ebigambo byaffe, abo be ba nannyini muliro Jahiimu.');
INSERT INTO "african_dev" VALUES(22,52,'22:52','. Tetwatuma oluberyeberyelwo mubaka yenna oba Nabbi okugyako nga bwayagala okusoma (obubaka obumuweereddwa) Sitaane agezaako okusonseka ebigambo mu kusomwakwe, olwo nno Katonda najjawo ebyo Sitane byagezaako okusonsekamu awo Katonda nakakasaawo ebigambobye, bulijjo Katonda mumanyi nnyo, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(22,53,'22:53','. (Ekikozesa ekyo kwe kuba nti) Katonda afuule Sitane byagezaako okusonseka (mu bubaka) ekikemo ku abo abalina obulwadde mu mitima gya bwe, n’abalina emitima emikakanyavu era mazima abeeyisa obubi beesambye nnyo Katonda.');
INSERT INTO "african_dev" VALUES(22,54,'22:54','. Bateekeddwa okutegeera abo abaaweebwa okumanya nti mazima ddala (Kur’ani) ge mazima agavudde ewa Mukama omulabiriziwo olwo nno bagakkirize, era gyewombeke ku lw''ekyo emitima gya bwe, era mazima Katonda wa kulungamya abo abakkiriza eri ekkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(22,55,'22:55','. Bulijjo abo abaakaafuwala tebagenda kulekayo kubuusabuusa ku yo, (Kur’ani) okuutuusa olunaku lwe''nkomerero lwe lulibatuukako ekibwatukira, oba ne bibatuukako ebibonerezo by''olunaku olutalibaako kulekera.');
INSERT INTO "african_dev" VALUES(22,56,'22:56','. Obufuzi ku lunaku olwo buliba bwa Katonda yekka. Agenda kulamula wakati waabwe, bo abakkiriza nebakola emirimu emirongoofu bagenda kubeera mu jjana ezijjudde ebyengera.');
INSERT INTO "african_dev" VALUES(22,57,'22:57','. Ate abo abaakaafuwala ne balimbisa ebigambo byaffe abo bagenda kussibwako ebibonerezo ebinyoomesa.');
INSERT INTO "african_dev" VALUES(22,58,'22:58','. Ate abo abaaleka amaka gaabwe ne bagenda okulafuubana mu kuweereza mu kkubo lya Katonda olwo nno nebattibwa oba ne bafa, Katonda agenda kubaweera ddala ebigabwa ebirungi. Era bulijjo mazima Katonda yye y''asinga abagabi bonna.');
INSERT INTO "african_dev" VALUES(22,59,'22:59','. Agenda kubayingiriza ddala obuyingiro bwe balisiima. Era mazima Katonda mumanyi nnyo wa kisa.');
INSERT INTO "african_dev" VALUES(22,60,'22:60','. Ekyo bwe kityo bwe kiri. Era oyo yenna asasula ekyenkana obuvune obumutuusiddwako, ate oluvanyuma naalumbaganwa, mazima Katonda wa kumutaasiza ddala mazima Katonda mulekezi musonyiyi.');
INSERT INTO "african_dev" VALUES(22,61,'22:61','. Ekyo nno lwakuba nti mazima Katonda ayingiza ekiro mu musana era naayingiza omusana mu kiro era mazima Katonda awulira alaba nnyo.');
INSERT INTO "african_dev" VALUES(22,62,'22:62','. Ekyo bwe kityo bwe kiri lwakuba nti mazima Katonda ye ye mazima, era ddala bye basinza ebitali yye bye bitali bituufu era mazima Katonda yye ye wa waggulu ow''ekitiibwa.');
INSERT INTO "african_dev" VALUES(22,63,'22:63','. Abaffe tolaba nti mazima Katonda assa okuva waggulu amazzi ensi neefuuka ya kiragala mazima Katonda alumirwa (ebitonde bye) amanyi ebyekusifu.');
INSERT INTO "african_dev" VALUES(22,64,'22:64','. Bibye yekka ebyo ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi era ddala Katonda yye y''atalina kye yeetaaga atenderezebwa.');
INSERT INTO "african_dev" VALUES(22,65,'22:65','. Abaffe tolaba nti mazima Katonda yabagondeza ebyo ebiri mu nsi, n''abagondeza n''amaato agaseeyeeya ku nnyanja olw''ekiragirokye era awanirira eggulu obutagwa ku nsi mpozzi nga ayagadde, mazima Katonda mukwata mpola musaasizi nnyo eri abantu.');
INSERT INTO "african_dev" VALUES(22,66,'22:66','. Yye yooyo abawa obulamu oluvanyuma n''abatta n''oluvanyuma alibawa obulamu mazima omuntu mwewakanyi (wa byengera).');
INSERT INTO "african_dev" VALUES(22,67,'22:67','. Buli bantu twabawa emikolo (egy''okusinza) bo gye bakola n''olwekyo (abakaafiiri) tebasaanye ku kuwakanya ku kintu kyonna, era koowoola abantu okudda ewa Mukama omulabiriziwo (okusinziira ku bye twogedde) mazima ggwe oli ku bulungamu obulambulukufu.');
INSERT INTO "african_dev" VALUES(22,68,'22:68','. Bwe bakuwakanya gamba nti Katonda y''asinga okumanya ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(22,69,'22:69','. Katonda agenda kusalawo wakati wa mmwe ku lunaku lw''enkomerero ku ebyo bye mwalinga mwawukanamu.');
INSERT INTO "african_dev" VALUES(22,70,'22:70','. Abaffe tomanyi nti mazima Katonda amanyi ebiri mu ggulu ne mu nsi mazima ekyo kiri mu kitabo (kiwandiike okuva edda) mazima ekyo kyangu nnyo ku Katonda.');
INSERT INTO "african_dev" VALUES(22,71,'22:71','. Basinza ebitali Katonda byatassangako bujulizi bwonna, era bye batalinaako kumanya kwonna, bulijjo abeeyisa obubi tebaba na mutaasa.');
INSERT INTO "african_dev" VALUES(22,72,'22:72','. Ebigambo byaffe bwe bibasomerwa nga binnyonnyofu olaba mu byenyi by''abo abaakaafuwala ekifaananyi ekitali kirungi, kumpi babuukire abo ababasomera ebigambo byaffe, bagambe nti abaffe mbabuulire ekibi okusinga abo, gwe muliro Katonda gwe yalagaanyisa abo abaakaafuwala era buddo obusinga okuba obubi.');
INSERT INTO "african_dev" VALUES(22,73,'22:73','. Abange mmwe abantu ekifaananyi kikubiddwa kale mukiwulirize mazima abo be musaba abatali Katonda tebasobola kutonda nsowera ne bwe baba nga beegasse ku lw''ekyo, era ensowera singa ebaggyako ekintu tebasobola kugikiggyako abanja munafu negwebabanja.');
INSERT INTO "african_dev" VALUES(22,74,'22:74','. Tebaawa Katonda kitiibwa kimusaanira mazima Katonda wa maanyi nnyo Nantakubwa ku mukono.');
INSERT INTO "african_dev" VALUES(22,75,'22:75','. Katonda alonda mu ba Malayika ababaka, bwatyo ne mu bantu, mazima Katonda muwulizi alaba nnyo.');
INSERT INTO "african_dev" VALUES(22,76,'22:76','. (N’olwekyo) amanyi ebiri mu maaso gaabwe n''ebiri emabega waabwe, n''eri Katonda ebintu byonna gye bizzibwa.');
INSERT INTO "african_dev" VALUES(22,77,'22:77','. Abange mmwe abakkiriza mukutame (nga mukutte ku maviivi) era muvunname, musinze Mukama omulabirizi wa mmwe, era mukole obulungi musobole okutuuka ku buwanguzi.');
INSERT INTO "african_dev" VALUES(22,78,'22:78','. Era mulafuubane nga muweereza (mu kkubo lya) Katonda mu bulambulukufu bw''okulafuubana okwo, yye (Katonda) ye yabalonda (ku lwe ddiiniye) era teyabateekera mu ddiini buzito bwonna, nga ye ddiini ya Mukadde wa mmwe Ibrahimu, yye y''eyabatuuma abasiraamu oluberyeberye (mu bitabo ebyakulembera) ne mu (Kur’ani) eno olwo nno omubaka abeere mujulizi ku mmwe nammwe mubeere bajulizi ku bantu (abalala) kaakano nno muyimirizeewo e sswala muwe ne Zakka era mwekwase Katonda yye ye mukuumi wa mmwe, kale mulungi nnyo omukuumi (oyo) era mulungi nnyo omutaasa oyo.');
INSERT INTO "african_dev" VALUES(23,1,'23:1','. Mazima abakkiriza batuuse ku buwanguzi.');
INSERT INTO "african_dev" VALUES(23,2,'23:2','. Abo bbo abeewombeka mu sswala zaabwe.');
INSERT INTO "african_dev" VALUES(23,3,'23:3','. Era abo bbo abatabeera mu bintu bitaliimu makulu.');
INSERT INTO "african_dev" VALUES(23,4,'23:4','. Era abo bbo abajjumbize mu kutoola Zakka.');
INSERT INTO "african_dev" VALUES(23,5,'23:5','. Era abo bbo abakuuma obwereere bwabwe.');
INSERT INTO "african_dev" VALUES(23,6,'23:6','. Okugyako ku be baafumbiriganwa nabo, oba abo emikono gya bwe egya ddyo be gyafuna (abazaana) anti mazima bbo, ku ekyo tebanenyezebwa.');
INSERT INTO "african_dev" VALUES(23,7,'23:7','. Naye oyo anaayiza okusukka ku ekyo abo bbo be babuuka ensalo (z''amateeka ga Katonda).');
INSERT INTO "african_dev" VALUES(23,8,'23:8','. (Abakkiriza) era beebo bbo abakuuma obutiribiri obwesigwa bwa bwe n''endagaano zaabwe.');
INSERT INTO "african_dev" VALUES(23,9,'23:9','. Era bbo beebo abeekubiriza okutuukiriza e sswala zaabwe.');
INSERT INTO "african_dev" VALUES(23,10,'23:10','. Abo nno bbo be bagenda okusikira (okuyingira e jjana).');
INSERT INTO "african_dev" VALUES(23,11,'23:11','. Abo abalisikira Firidausi (e jjana) nga bbo mu yo baakutuulamu bugenderevu.');
INSERT INTO "african_dev" VALUES(23,12,'23:12','. Mazima twatonda omuntu nga tumuggya mu nsibuko ya ttaka');
INSERT INTO "african_dev" VALUES(23,13,'23:13','. Oluvanyuma tumufuula amazzi agazaala ge tukuumira mu kifo ekinywevu (nnabaana).');
INSERT INTO "african_dev" VALUES(23,14,'23:14','. Oluvanyuma netutonda mu mazzi ago e kisaayisaayi ate e kisaayisaayi ne tukitondamu e kinyama ate e kinyama ne tukitonderamu amagumba olwo nno amagumba ne tugambaza e nnyama, ate oluvanyuma ne tumutonda nga kitonde kirala (naafuuka omuntu nga afuuyiddwamu omwoyo) n''olwekyo Katonda musukkulumu oyo asinga abakozi be bintu bonna.');
INSERT INTO "african_dev" VALUES(23,15,'23:15','. Mazima mmwe oluvanyuma lw''ekyo ddala mugenda kufa.');
INSERT INTO "african_dev" VALUES(23,16,'23:16','. Oluvanyuma mazima mmwe olunaku lw''enkomerero muli ba kuzuukizibwa.');
INSERT INTO "african_dev" VALUES(23,17,'23:17','. Era mazima twatonda waggulu wa mmwe eggulu omusanvu era tetubangako bagayaalira (kulabirira) bitonde.');
INSERT INTO "african_dev" VALUES(23,18,'23:18','. Era tussa okuva waggulu amazzi mu ngeri ya kigero netugabeeza mu ttaka era nga mazima ddala ffe tuba tusobola okugabuzaawo.');
INSERT INTO "african_dev" VALUES(23,19,'23:19','. Netubamereza nago e nnimiro ez''emitende ne nzabibu nga zirimu ebibala bingi era nga ku byo kwe mulya.');
INSERT INTO "african_dev" VALUES(23,20,'23:20','. Era netumeza nago omuti (omuzaituni) ogwo ogusibuka ku lusozi Sinaa, guvaamu omuzigo (abantu gwe beesiiga) era nga muwoomu eri abali.');
INSERT INTO "african_dev" VALUES(23,21,'23:21','. Era mazima mulina mmwe mu nsolo ezirundibwa eky''okuyiga, tubanywesa mu ebyo ebiri mu mbuto zaazo era mulina mu zo emigaso mingi era nga mulya ezimu ku zo.');
INSERT INTO "african_dev" VALUES(23,22,'23:22','. Nga era ku zo ne ku maato kwe mutwalirwa.');
INSERT INTO "african_dev" VALUES(23,23,'23:23','. Era mazima twatuma Nuhu eri a bantube naagamba nti: abange bantu bange musinze Katonda yekka temulinaayo kintu kirala kyonna kisaana kusinzibwa. Abaffe temutya (Katonda)!.');
INSERT INTO "african_dev" VALUES(23,24,'23:24','. Abakungu abo abaakaafuwala mu bantube ne bagamba nti: tali ono (Nuhu) okugyako muntu nga mmwe, ayagala kwe sukkulumya ku mmwe singa Katonda yayagala (kutuma Malayika) yaalibadde atuma ba Malayika, kino tetukiwulirangako mu bakadde baffe abaasooka.');
INSERT INTO "african_dev" VALUES(23,25,'23:25','. Talina kyali okugyako okuba nti alina ekimulinnya ku mutwe kale mumuliindirize okumala ekiseera ekigere (ajja kuvaawo oba ajja kufa).');
INSERT INTO "african_dev" VALUES(23,26,'23:26','. (Nuhu) naagamba nti ayi Mukama omulabirizi wange ntaasa ku kunnimbisa (kwa bwe).');
INSERT INTO "african_dev" VALUES(23,27,'23:27','. Awo nno twamutumira nti kola eryato nga okolera ku biragiro byaffe, era ne tumutumira nti ekigambo kyaffe bwe kinaatuuka e ttanuuru neefukumukamu amazzi olwo nno pakira mu lyo (eryato) ku buli kintu bibiri ekisajja n''ekikazi era pakiramu abantubo okugyako omuntu oyo ekigambo gwe kyakakatako edda mu bo, era toyogera nange ku abo abeeyisa obubi, mazima bbo ba kuzikirira.');
INSERT INTO "african_dev" VALUES(23,28,'23:28','. Ggwe n''abo abali naawe bwe mukkalira mu lyato olwo nno gamba nti okutenderezebwa kwonna kwa Katonda oyo atuwonyezza abantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(23,29,'23:29','. Era gamba nti ayi Mukama omulabirizi wange (bwe ntuuka okuva mu lyato lino) nzisa mu kifo eky''omukisa anti ggwe osinga bonna abasalawo ebifo (eby''okubeeramu).');
INSERT INTO "african_dev" VALUES(23,30,'23:30','. Mazima ekyo kijjuddemu obubonero (obulaga obuyinza bwa Katonda) newaakubadde nga ate tubagezesa.');
INSERT INTO "african_dev" VALUES(23,31,'23:31','. Ekyo nga kiwedde twatandikawo oluvanyuma lwa bwe omulembe omulala.');
INSERT INTO "african_dev" VALUES(23,32,'23:32','. Netubatumamu omubaka ng''ava mu bo ng''abagamba nti musinze Katonda temulina kirala kyonna kye musaanye kusinza kitali yye, abaffe temutya (Katonda)!.');
INSERT INTO "african_dev" VALUES(23,33,'23:33','. Abakungu abaakaafuwala mu bantube, era abaalimbisa okusisinkana olunaku lwe''nkomerero, era abo be twawa okweyagala mu bulamu bw''ensi, baagamba nti ono talina kyali okugyako muntu buntu nga mmwe, bye mulyako byalya, ne bye munywa byanywa.');
INSERT INTO "african_dev" VALUES(23,34,'23:34','. Ate singa mugondera omuntu obuntu nga mmwe, mazima mmwe olwo munaaba mufaafaaganiddwa.');
INSERT INTO "african_dev" VALUES(23,35,'23:35','. Mbu abalagaanyisa nti mazima mmwe singa mufa ne muba ettaka era amagumba ameereere, nti mazima mmwe mugenda kudda!.');
INSERT INTO "african_dev" VALUES(23,36,'23:36','. Tekisoboka era tekisobokera ddala ekyo kye mulagaanyisibwa.');
INSERT INTO "african_dev" VALUES(23,37,'23:37','. (Ekituufu kiri nti) tewali bulamu bulala bwonna okugyako obulamu obw''ensi abamu muffe bava mu bulamu abalala ne bayingira. Era ffe tetugenda kuzuukuzibwa.');
INSERT INTO "african_dev" VALUES(23,38,'23:38','. Tali yye okugyako okuba nti musajja agunja ku Katonda ebigambo eby''obulimba, era ffe tetujja kumukkiriza.');
INSERT INTO "african_dev" VALUES(23,39,'23:39','. (Omubaka) naagamba nti ayi Mukama omulabirizi wange ntaasa ku kunnimbisa kwa bwe.');
INSERT INTO "african_dev" VALUES(23,40,'23:40','. (Katonda) naagamba nti mu kaseera katono nnyo bajja kuba bejjusa.');
INSERT INTO "african_dev" VALUES(23,41,'23:41','. Okubwatuka ne kubatuukako era nga kye kibasaanira ne tubafuula ebisasiro. N''olwekyo babeere wala nnyo abantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(23,42,'23:42','. Ekyo nga kiwedde twatandikawo oluvanyuma lwa bwe emirembe emirala.');
INSERT INTO "african_dev" VALUES(23,43,'23:43','. Tewali mulembe guyinza kukulembera kiseera kyagugererwa, era tebayinza kwagala kwongezebwayo (nebakufuna).');
INSERT INTO "african_dev" VALUES(23,44,'23:44','. Oluvanyuma twatuma ababaka baffe nga bagoberegana, buli bantu bwe bajjirwanga omubaka waabwe nga bamulimbisa, olwo nno abamu ne tubakwasa ekkubo bannaabwe lye baakwata (mu kuzikirira) ne tubafuula eky''okwogerwako kale nno babeere wala nnyo abantu abatakkiriza.');
INSERT INTO "african_dev" VALUES(23,45,'23:45','. Oluvanyuma twatuma Musa ne Mugandawe Haruna nga tubawadde eby''amagero byaffe n''obujulizi obw''enkukunala.');
INSERT INTO "african_dev" VALUES(23,46,'23:46','. Bagende ewa Firaawo n''abakungube (wabula be twatuma bwe baagenda gye bali) ne beekuza ne babeera abantu abeetwala okuba aba waggulu.');
INSERT INTO "african_dev" VALUES(23,47,'23:47','. Nebagamba nti tukkirize abantu ababiri abafaanana nga ffe ng''ate abantu baabwe batusinza businza!.');
INSERT INTO "african_dev" VALUES(23,48,'23:48','. Ne babalimbisa bombi olwo nno ne baba mu abo abaazikirizibwa.');
INSERT INTO "african_dev" VALUES(23,49,'23:49','. Era mazima twawa Musa ekitabo babe nga balungama.');
INSERT INTO "african_dev" VALUES(23,50,'23:50','. Twafuula Mutabani wa Mariam ne Maamawe akabonero, ne tubawa obutuulo mu kifo e kisitufu e kibeerekamu nga kirimu n''amazzi agakulukuta.');
INSERT INTO "african_dev" VALUES(23,51,'23:51','. Abange mmwe ababaka mulye ebikkirizibwa okuliibwa, era mukole emirimu emirungi mazima nze manyidde ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(23,52,'23:52','. Mazima ddala e kibiina kya mmwe kino (eddiini yammwe) kiri ekibiina kimu nange nze Mukama omulabirizi wa mmwe kale muntye.');
INSERT INTO "african_dev" VALUES(23,53,'23:53','. Naye abantu beekutulamu wakati waabwe ku kigambo kyaabwe (eky''eddiini), ebibiina bingi nga buli kibinja (abantu baakyo) basanyuka n''ebyo bye balina.');
INSERT INTO "african_dev" VALUES(23,54,'23:54','. Kale baleke mu bubuze bwa bwe okutuusa ekiseera (ekyabagererwa).');
INSERT INTO "african_dev" VALUES(23,55,'23:55','. Abaffe balowooza bye tubawa mu mmaali n''abaana.');
INSERT INTO "african_dev" VALUES(23,56,'23:56','. Tuba tubanguyiza kwe yongera kufuna birungi! wabula tebategeera.');
INSERT INTO "african_dev" VALUES(23,57,'23:57','. Mazima abo abeeraliikirira okutuukiriza obuvunaanyizibwa bw''okutya Mukama omulabirizi waabwe.');
INSERT INTO "african_dev" VALUES(23,58,'23:58','. Era abo ebigambo ebyassibwa okuva ewa Mukama omulabirizi waabwe babikkiriza.');
INSERT INTO "african_dev" VALUES(23,59,'23:59','. Era abo abatagatta kintu kirala ku Mukama omulabirizi wa bwe.');
INSERT INTO "african_dev" VALUES(23,60,'23:60','. Era abo abawaayo bye baba bawaddeyo (ku lwa Katonda wa bwe) nga emitima gyabwe gitidde okuba nti mazima bbo bagenda kudda ewa Mukama omulabirizi wa bwe.');
INSERT INTO "african_dev" VALUES(23,61,'23:61','. Abo nno bayaayaanira okukola ebirungi era nga bo mu ekyo be bakulembera');
INSERT INTO "african_dev" VALUES(23,62,'23:62','. Era tetuwaliriza muntu (kukola) okugyako ekyo ekiri mu busobozibwe era tulina ekitabo ekiraga mu butuufu (ebikolebwa) nabo nga tebagenda kulyazaamaanyizibwa.');
INSERT INTO "african_dev" VALUES(23,63,'23:63','. Wabula emitima gya bwe (abakaafiiri) giri mu kugayaalirira (Kur’ani) eno, awamu n''okugatta ku Katonda ebintu ebirala, balina e mirimu emirala emibi nga Katonda abaleka ne bagikola (olwo nno ebibonerezo bya bwe byeyongere).');
INSERT INTO "african_dev" VALUES(23,64,'23:64','. Okutuusa bwe tussa ebibonerezo ku beejalabi mu bo olwo nno bo nebalajaana.');
INSERT INTO "african_dev" VALUES(23,65,'23:65','. Temulaajana leero, mazima mmwe temuli ba kututaasibwako.');
INSERT INTO "african_dev" VALUES(23,66,'23:66','. Mazima ebigambo byange byabasomerwanga naye mmwe mwalinga mukyukira ku bisinziro bya mmwe ne mubikuba amabega.');
INSERT INTO "african_dev" VALUES(23,67,'23:67','. Nga mwekuza olw''okugiwalana (Kur’ani), nga mugyogerako mu ngeri e mbi nga mutudde ku byoto bya mmwe ekiro.');
INSERT INTO "african_dev" VALUES(23,68,'23:68','. Abaffe tebeetegereza ekigambo (Kur’ani ne balaba nti ntuufu) oba (ekibabuza kwe kuba nti) bajjiddwa ebyo ebitajjira bakadde baabwe abaakulembera.');
INSERT INTO "african_dev" VALUES(23,69,'23:69','. Oba tebaamanya mubaka waabwe n''eba nga y''ensonga lwaki bamuwakanya.');
INSERT INTO "african_dev" VALUES(23,70,'23:70','. Oba bagamba nti mulalu, wabula (ekituufu kiri nti) yabajjira n''amazima, naye nga abasinga obungi mu bo batamwa amazima.');
INSERT INTO "african_dev" VALUES(23,71,'23:71','. Singa amazima gaagoberera okwagala kwa bwe, eggulu omusanvu n''ensi n''ebyo ebirimu byandi yonoonese, wabula twabawa ekyo ekibaweesa ekitiibwa (Kur’ani) naye (eky''ennaku) bbo beesamba ekitiibwa kya bwe.');
INSERT INTO "african_dev" VALUES(23,72,'23:72','. Oba obasaba musaala (ne kiba nga kye kibagaana okukkiriza naye nakyo tokikola), anti omusaala gwa Mukama omulabiriziwo gwe gusinga obulungi era yye y''asinga abagabirizi bonna.');
INSERT INTO "african_dev" VALUES(23,73,'23:73','. Era mazima ggwe obakoowoola okujja eri e kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(23,74,'23:74','. Era mazima abo abatakkiriza lunaku lwa nkomerero ddala be bava ku kkubo.');
INSERT INTO "african_dev" VALUES(23,75,'23:75','. Singa twabasaasira netubaggyako obuzibu obwabatuukako bandiremedde ku bubuze bwa bwe nga babulubuuta.');
INSERT INTO "african_dev" VALUES(23,76,'23:76','. Mazima twabatuusaako e bibonerezo naye tebeemenyera Mukama omulabirizi waabwe era tebaagonda.');
INSERT INTO "african_dev" VALUES(23,77,'23:77','. Okutuusa bwe tulibaggulira omulyango gw''ebibonerezo e bikakali oligenda okubalaba nga basobeddwa.');
INSERT INTO "african_dev" VALUES(23,78,'23:78','. (Katonda) yye, yooyo eyabatondera mmwe amatu n''amaaso n''emitima (naye) mwebaza kitono.');
INSERT INTO "african_dev" VALUES(23,79,'23:79','. Era yye yooyo (eyabatonda) n''abassa mu nsi era gyali gye mulikungaanyizibwa.');
INSERT INTO "african_dev" VALUES(23,80,'23:80','. Era yye yooyo awa obulamu era natta era ku kwe okwawukana kw''ekiro n''emisana, abaffe temutegeera!.');
INSERT INTO "african_dev" VALUES(23,81,'23:81','. Wabula (ekivaako obuzibu) bagamba nga abaabasooka kye baagamba.');
INSERT INTO "african_dev" VALUES(23,82,'23:82','. Bagamba nti bwe tulifa ne tuba e nfuufu n''amagumba abaffe tugenda kuzuukizibwa.');
INSERT INTO "african_dev" VALUES(23,83,'23:83','. Mazima ffe ne bakadde baffe kino kyatulagaanyisibwa dda, tekirina kye kiri okugyako okuba enfumo z''abasooka.');
INSERT INTO "african_dev" VALUES(23,84,'23:84','. Bagambe nti: ensi n''ebigirimu byani bwe muba nga mumanyi?.');
INSERT INTO "african_dev" VALUES(23,85,'23:85','. Bajja kugamba nti bya Katonda, bagambe nti abaffe temwebuulirira!.');
INSERT INTO "african_dev" VALUES(23,86,'23:86','. Bagambe nti ani Mukama Omulabirizi w''eggulu omusanvu era Mukama Omulabirizi wa Arishi ey''ekitiibwa?.');
INSERT INTO "african_dev" VALUES(23,87,'23:87','. Bajja kugamba nti bya Katonda, bagambe nti abaffe temutya!.');
INSERT INTO "african_dev" VALUES(23,88,'23:88','. Bagambe nti obufuzi bwa buli kintu buli mu mukono gw''ani? Alina obusobozi obuwa obukuumi (oyo aba yeekubidde enduulu gyali, ate bwaba yeetaaga omuntu) takugirwa ku ye, (mwanukule) bwe muba nga mumanyi.');
INSERT INTO "african_dev" VALUES(23,89,'23:89','. Bajja kugamba nti (obufuzi) bwa Katonda, bagambe nti mubuzibwabuzibwa mutya!.');
INSERT INTO "african_dev" VALUES(23,90,'23:90','. Wabula twabaleetera amazima naye ddala bbo balimba.');
INSERT INTO "african_dev" VALUES(23,91,'23:91','. Katonda teyeeteerangawo mwana era tawabangawo naye katonda mulala, singa bwe kyali buli katonda yandigenze ne bye yatonda era abamu baaliwambye bannaabwe. Katonda yeesambidde ddala ebyo bye boogera.');
INSERT INTO "african_dev" VALUES(23,92,'23:92','. (Katonda) mumanyi wa byekusifu n''ebirabikako ali wala nnyo ku ebyo bye bamugattako.');
INSERT INTO "african_dev" VALUES(23,93,'23:93','. Gamba nti: ayi Mukama omulabirizi wange, lwolindaga (ebibonerezo) ebyo bye balagaanyisibwa.');
INSERT INTO "african_dev" VALUES(23,94,'23:94','. Ayi Mukama omulabirizi wange tonteekanga mu bantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(23,95,'23:95','. Era mazima ffe ku ky''okukulaga bye tubalagaanyisa tusobolera ddala.');
INSERT INTO "african_dev" VALUES(23,96,'23:96','. Ggyawo ekibi nga okozesa ekyo ekisinga obulungi, ffe tusinga okumanya ebyo bye boogera.');
INSERT INTO "african_dev" VALUES(23,97,'23:97','. Era gamba nti ayi Mukama omulabirizi wange nkusaba ompe obukuumi ku kubuzaabuza kwa Sitane.');
INSERT INTO "african_dev" VALUES(23,98,'23:98','. Era Mukama omulabirizi wange nkusaba ompe obukuumi (Sitane) zireme kunsemberera.');
INSERT INTO "african_dev" VALUES(23,99,'23:99','. (Baakubeera mu bubuze) okutuusa okufa lwe kujjira omu ku bo, naagamba nti ayi Mukama omulabirizi wange munzizeeyo (ku nsi).');
INSERT INTO "african_dev" VALUES(23,100,'23:100','. Mbe nga nkola emirimu emirungi mu ebyo byessaakola, si bwe kiri mazima kyo kigambo bugambo yye kyayogera (naye nga tagenda kusobola kudda) ate era mu maaso gaabwe waliwo e kiseera (ekyawula wakati w''obulamu bw''ensi n''obw''oluvanyuma mwe balibeera) okutuusa ku lunaku lwe balizuukizibwa.');
INSERT INTO "african_dev" VALUES(23,101,'23:101','. Olwo nno engombe bwerifuuyibwa tewagenda kuba nganda wakati wa bwe ku lunaku olwo, era tebagenda kwogeraganya.');
INSERT INTO "african_dev" VALUES(23,102,'23:102','. Oyo yenna ebirizitowa ebipimwabye abo nno be b''okwesiima.');
INSERT INTO "african_dev" VALUES(23,103,'23:103','. Ate oyo ebiriwewuka ebipimwabye abo nno be bafaafaaganirwa emyoyo gya bwe nga baakutuula lubeerera mu muliro Jahannama.');
INSERT INTO "african_dev" VALUES(23,104,'23:104','. Omuliro gulisiriiza ebyenyi bya bwe, balibeera mu gwo nga banyinyibadde.');
INSERT INTO "african_dev" VALUES(23,105,'23:105','. Abaffe ebigambo byange byali tebibasomerwa ate mmwe ne muba nga mubilimbisa.');
INSERT INTO "african_dev" VALUES(23,106,'23:106','. Nebagamba nti ayi Mukama omulabirizi waffe obwonoonefu bwaffe bwatusinza amanyi, era twali abantu abaabula.');
INSERT INTO "african_dev" VALUES(23,107,'23:107','. Ayi Mukama omulabirizi waffe tuggye mu gwo (omuliro), olwo nno bwe tuddamu okwonoona mazima tujja kuba abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(23,108,'23:108','. Naagamba nti mubeere mu gwo nga munyoomebwa era temwogera nange.');
INSERT INTO "african_dev" VALUES(23,109,'23:109','. Mazima ekitundu mu baddu bange baali bagamba nti ayi Mukama omulabirizi waffe tukkirizza kale tusonyiwe era otusaasire era ggwe osinga abasaasizi bonna.');
INSERT INTO "african_dev" VALUES(23,110,'23:110','. Ne mubafuula ekisekererwa okutuusa lwe baaberabiza okunjogerako era mwali mubasekerera.');
INSERT INTO "african_dev" VALUES(23,111,'23:111','. Mazima nze olwa leero mbasasudde olw''ebyo bye baagumiikiriza mazima bbo be b''okwesiima.');
INSERT INTO "african_dev" VALUES(23,112,'23:112','. (Katonda) aligamba nti mu nsi mwatuulamu emyaka emeka?');
INSERT INTO "african_dev" VALUES(23,113,'23:113','. Baligamba nti: twamalamu lunaku oba kitundu kya lunaku, wabula buuza ababazi.');
INSERT INTO "african_dev" VALUES(23,114,'23:114','. Aligamba nti temwatuula okugyako ebbanga ttono singa mazima mmwe mwali mumanyi.');
INSERT INTO "african_dev" VALUES(23,115,'23:115','. Abaffe mwalowooza nti mazima twabatonda nga tewali kigendererwa, era nti mazima mmwe temugenda kuzzibwa gye tuli.');
INSERT INTO "african_dev" VALUES(23,116,'23:116','. Yasukkuluma Katonda Omufuzi (era yye) ye mazima tewali kisinzibwa kyonna (mu butuufu) okugyako yye, Mukama omulabirizi wa Arishi ey''ekitiibwa.');
INSERT INTO "african_dev" VALUES(23,117,'23:117','. Oyo yenna asinza ekintu ekirala awamu ne Katonda so nga ate takirinaako bujulizi, mazima okubalibwa kwe kuli wa Mukama omulabiriziwe, mazima abakaafiiri tebayinza kwesiima.');
INSERT INTO "african_dev" VALUES(23,118,'23:118','. Era gamba nti ayi Mukama omulabirizi wange sonyiwa osaasire. Era ggwe osinga abasaasizi bonna.');
INSERT INTO "african_dev" VALUES(24,1,'24:1','. (Eno) ssuula twagissa ne tugikakasa (okukozesa amateeka agagirimu) netussa mu yo ebigambo ebinnyonnyofu mube nga mujjukira.');
INSERT INTO "african_dev" VALUES(24,2,'24:2','. Omwenzi omukazi n''omwenzi omusajja buli omu ku bo mumukube emiggo kikumi, ekisa tekibakwatanga ku lwa bwe bombi, nga muli mu kutuukiriza e ddiini ya Katonda, bwe muba nga mukkiriza Katonda n’olunaku lw''enkomerero, ekibinja mu bakkiriza kiteekwa okubaawo nga babonerezebwa.');
INSERT INTO "african_dev" VALUES(24,3,'24:3','. Omusajja omwenzi tawasa okugyako omukazi omwenzi oba agatta ebintu ebirala ku Katonda, n’omukazi omwenzi tawasibwa okugyako omusajja omwenzi oba agatta ebintu ebirala ku Katonda, era ekyo kyaziyizibwa ku bakkiriza.');
INSERT INTO "african_dev" VALUES(24,4,'24:4','. N’abo abatemerera abakyala ab''ensa obwenzi ate ne bataleeta bajulizi bana mubakube emiggo kinaana era lubeerera temuddangayo okukkiriza obujulizi bwa bwe era abo be boonoonyi.');
INSERT INTO "african_dev" VALUES(24,5,'24:5','. Okugyako abo abeenenya oluvanyuma lw''ekyo, era nebakola e mirimu emirungi. Anti mazima Katonda musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(24,6,'24:6','. Ate abo abawaayiriza be baafumbiriganwa nabo obwenzi ate nga tebalina bajulizi okugyako bo bennyini, kale nno obujulizi bw''omu ku bo kwe kulayira Katonda emirundi ena (naayongerako nti) mazima yye wa mu boogera amazima.');
INSERT INTO "african_dev" VALUES(24,7,'24:7','. Ne ku mulundi ogw''okutaano (ayongerako nti) mazima ekikolimo kya Katonda kibe ku ye bwaba nga wa mu balimba.');
INSERT INTO "african_dev" VALUES(24,8,'24:8','. Olwo nno omukyala ne bamuggyako e kibonerezo bwalayira Katonda emirundi ena (naayongerako nti) mazima oli byayogera bimussa mu balimba.');
INSERT INTO "african_dev" VALUES(24,9,'24:9','. Ne ku mulundi ogw''okutaano (ayongerako nti) mazima obusungu bwa Katonda bube ku ye (bba) bwaba mu boogera amazima.');
INSERT INTO "african_dev" VALUES(24,10,'24:10','. Singa si bulungi bwa Katonda bwabawa na kusaasirakwe (mwandisanze) obuzibu mu kusalawo ensonga) era mazima Katonda akkiriza okwenenya mugoba nsonga.');
INSERT INTO "african_dev" VALUES(24,11,'24:11','. Mazima abo abaaleeta ebigambo eby''obulimba (ne batemerera Aisha obwenzi) bamu ku mmwe, temukitwala nti kibi gye muli wabula kirungi gye muli, buli omu ku bo ateekwa okufuna empeera y''ekibi kye yakola, ate oyo eyakikulembera mu bo waakufuna ebibonerezo e bikakali.');
INSERT INTO "african_dev" VALUES(24,12,'24:12','. Singa bwe mwakiwulira abakkiriza abasajja n''abakkiriza abakyala beerowooleza bulungi, era ne bagamba nti kuno kutemerera okw''olwatu.');
INSERT INTO "african_dev" VALUES(24,13,'24:13','. Singa baakireetako abajulizi bana naye kebataaleeta bajulizi olwo nno abo bbo ewa Katonda be balimba.');
INSERT INTO "african_dev" VALUES(24,14,'24:14','. Singa si bulungi bwa Katonda na kusaasirakwe byabawa ku nsi ne ku nkomerero olw''ebyo bye mweyingizaamu mwalituukiddwako ebibonerezo ebikakali.');
INSERT INTO "african_dev" VALUES(24,15,'24:15','. Mu kiseera wemwabisaasaanyiza ne nnimi za mmwe ne mwogera n''emimwa gya mmwe bye mutaalinako kumanya, mwakitwala nga ekyangu naye nga ewa Katonda kinene.');
INSERT INTO "african_dev" VALUES(24,16,'24:16','. Singa bwe mwakiwulira mwagamba nti tekitugwanira kwogera ku nsonga eno, wasukkuluma Mukama Katonda, kuno kutemerera okuyitirivu.');
INSERT INTO "african_dev" VALUES(24,17,'24:17','. Katonda ababuulirira bulijjo obutaddayo kukola kintu nga kino bwe muba nga muli bakkiriza.');
INSERT INTO "african_dev" VALUES(24,18,'24:18','. Era Katonda abannyonnyola amateeka. Era bulijjo Katonda amanyi nnyo ate nga mugoba nsonga.');
INSERT INTO "african_dev" VALUES(24,19,'24:19','. Mazima abo abaagala ebikolwa eby''obuwemu okusaasaana mu bakkiriza, balina ebibonerezo ebiruma ennyo ku nsi ne ku nkomerero. Era Katonda amanyi ate mmwe temumanyi.');
INSERT INTO "african_dev" VALUES(24,20,'24:20','. Singa si bulungi bwa Katonda na kusaasirakwe byabawa (mwandituuseeko ebizibu naye nga bulijjo) mazima Katonda wa kisa musaasizi.');
INSERT INTO "african_dev" VALUES(24,21,'24:21','. Abange mmwe abakkiriza temugobereranga amakubo ga Sitaane, oyo yenna agoberera amakubo ga Sitane anti yye (Sitaane) alagira okukola eby''obuwemu n''ebitayagalwa, era singa si bulungi bwa Katonda na kusaasirakwe byabawa olubeerera, teyanditukudde muntu yenna mu mmwe, naye mazima Katonda atukuza oyo gwaba ayagadde mazima Katonda awulira mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(24,22,'24:22','. Abeeyisa obulungi mu ddiini era abagaziyizibwa mu byenfuna tebalayiranga obutawa ab''oluganda olw''okumpi n''abanaku n''abaasenguka olw''okuweereza mu kkubo lya Katonda. Basaana basonyiwe era balekere, abaffe (mmwe) temwagala Katonda kubasonyiwa era nga bulijjo Katonda musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(24,23,'24:23','. Mazima abo abatemerera obwenzi abakyala ab''ensa abatalina musango abakkiriza, bakolimirwa ku nsi ne ku nkomerero era balissibwako ebibonerezo eby''amaanyi.');
INSERT INTO "african_dev" VALUES(24,24,'24:24','. Ku lunaku ennimi zaabwe n''emikono gya bwe lwe biribawaako obujulizi olw''ebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(24,25,'24:25','. Ku lunaku olwo Katonda agenda kubasasula mu bujjuvu empeera yaabwe ebasaanira, era bagenda kumanya nti mazima Katonda yye ye mazima owa nnamaddala.');
INSERT INTO "african_dev" VALUES(24,26,'24:26','. Abakazi ab''empisa embi ba basajja ab''empisa embi, n''abasajja ab''empisa embi ba bakazi ab''empisa embi, n''abakazi abeeyisa obulungi ba basajja abeeyisa obulungi, n''abasajja abeeyisa obulungi ba bakazi abeeyisa obulungi, abo (bebatemerera obwenzi) batukuvu ku ebyo (bali) bye boogera, balina ekisonyiwo n''okugabirirwa okw''ekitiibwa.');
INSERT INTO "african_dev" VALUES(24,27,'24:27','. Abange mmwe abakkiriza temuyingiranga amayumba agatali mayumba gammwe okugyako nga musabye era ne mutoolera abantu baamu ssalaamu ekyo kye kirungi gye muli kibayambe mujjukire.');
INSERT INTO "african_dev" VALUES(24,28,'24:28','. Bwe mutagasangamu muntu yenna temugayingiranga okutuusa nga mukkiriziddwa, bwe mugambibwanga nti muddeyo muddangayo, ekyo ky''ekisinga obutukuvu gye muli, Katonda byonna bye mukola abimanyidde ddala.');
INSERT INTO "african_dev" VALUES(24,29,'24:29','. Temulina musango bwe muyingira e nyumba ezitaliimu bantu ng''ate mulimu bye mwetaaga. Bulijjo Katonda amanyi bye mwoleesa ne bye mukweka.');
INSERT INTO "african_dev" VALUES(24,30,'24:30','. Gamba abakkiriza bakkakkanye ku maaso ga bwe, era bakuume obwereere bwa bwe, ekyo kye kisinga obutukuvu gye bali, mazima Katonda amanyidde ddala ebyo bye bakola.');
INSERT INTO "african_dev" VALUES(24,31,'24:31','. Era gamba abakyala abakkiriza bakkakkanye ku maaso gaabwe era bakuume obwereere bwa bwe, era teboolesa eby''okwewunda bya bwe okugyako ebyo ebiba biteekwa okulabika, era bateekwa beebikkirire obukaaya bwa bwe (nga butuuka) ku bifuba bya bwe, era teboolesa bya kwewunda bya bwe okugyako eri ba bbaabwe, oba ba kitaabwe, oba ba taata ba ba bbaabwe ,oba abaana baabwe, oba abaana ba ba bbaabwe, oba bannyinaabwe, oba abaana ba bannyinaabwe, oba abaana ba baganda baabwe, oba abakyala nga bbo, oba abo abaddu emikono gya bwe egyaddyo be gyafuna, oba abaweereza abalala abasajja abatakyalina bwetaavu eri bakyala, oba abaana abato abatalabanga ku bwereere bwa bakyala, era (abakyala abakkiriza bwe baba batambula) tebakuba bigere bya bwe kibe nga kituuka okumanyika ekyo kye bakusise mu by''okwewunda bya bwe. Mwenna mwenenyeze Katonda abange mmwe abakkiriza kibayambe okutuuka ku buwanguzi.');
INSERT INTO "african_dev" VALUES(24,32,'24:32','. Mufumbize mu mmwe abatali bafumbo, nabwekityo mufumbiriganye abalongoofu mu baddu ba mmwe n''abazaana ba mmwe, bwe baba baavu Katonda ajja kubagaggawaza nga aggya mu bigabwabye bulijjo Katonda mugazi (mu byalina) mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(24,33,'24:33','. Abo abatasobola kuwasa bateekwa okwessaamu ensa okutuusa Katonda lwabawa ekimala ekyetaago kya bwe nga aggya mu bigabwabye, ate abo abasaba okuweebwa ekiwandiiko (ekibakkiriza okunoonya sente beenunule) mu baddu ba mmwe abo emikono gya mmwe egya ddyo be gyafuna mukibawandiikire singa mubalabamu akalungi, (mmwe abakulembeze) mubawe nga ku mmaali ya Katonda eyo gye yabawa, era temuwalirizanga abazaana ba mmwe okukola obwa malaaya (mu kikola mutya) ng''ate bbo baagala kuba na nsa! (ekibatuusa okukola ekyo) lwa kwagala kufuna bya bulamu byansi, oyo yenna abawaliriza mazima Katonda oluvanyuma lw''okubawaliriza musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(24,34,'24:34','. Era mazima twassa gye muli ebigambo (bya Kur''an) ebinnyonnyola, era (netussa) eby''okulabirako ebikwata ku abo abaaliwo oluberyeberye lwa mmwe era ekyo nga kya kubuulirira eri abatya Katonda.');
INSERT INTO "african_dev" VALUES(24,35,'24:35','. Katonda kitangaala ky''eggulu omusanvu n''ensi ekifaananyi ky''ekitangaalakye kiringa akafo akaazimbirwa mu kisenge nga ka kuteekamu ttaala (naye nga tekayitamu kitangaala kuva bweru, ettaala bwe yaka ekitangaala kyayo kikoma ku kisenge kya kafo ako, ekiyamba abali mu kisenge kya kafo okufuna ekitangaala obulungi, akafo ako) nga kalimu ettaala nga ettaala eri mu kirawuli ekirawuli nga kiringa emmunyeenye etangalijja nga eteekebwamu amafuta agava mu muti omuzayituni ogw''omukisa nga si gwe buvanjuba wadde ebugwanjuba (mu nnimiro mwe gwakulira ne guba nga gwafunanga ekitangaala ky''enjuba ebbanga erimala), amafuta gaagwo kumpi gaaka newaakubadde nga tegatuukiddwako muliro, nekiba nga kitangaala ku kitangaala kinnaakyo (anti ekitangaala kye ttaala kyegatta ku kitangaala ky''ekirawuli ate nno bwogattako amafuta amalungi ennyo negubula asala). Katonda alungamya eri ekitangaalakye oyo gwaba ayagadde era Katonda akubira abantu ebifaananyi era Katonda bulijjo amanyi ebifa ku buli kintu.');
INSERT INTO "african_dev" VALUES(24,36,'24:36','. Mu mayumba (e mizikiti) Katonda geyakkiriza gazimbibwe era n''alagira erinnyalye libeere nga lyogerwako mu go, (omwo nno) bamutendereza mu go enkya n''olweggulo.');
INSERT INTO "african_dev" VALUES(24,37,'24:37','. Abasajja abatalabankanyizibwa bya busuubuzi wadde okutunda nebava ku kwogera ku Katonda n''okuyimirizaawo e sswala n''okutoola Zakka nga batya olunaku emitima n''amaaso lwe birikankana.');
INSERT INTO "african_dev" VALUES(24,38,'24:38','. Olwo nno Katonda abasasule ebirungi by''ebyo bye baakola era abongeze mu birungibye, bulijjo Katonda agabirira oyo gwaba ayagadde awatali kubalirira.');
INSERT INTO "african_dev" VALUES(24,39,'24:39','. Bbo abo abaakaafuwala emirimu gya bwe girirabika nga ennyanja ey''okumusenyu (so nga tebaawo) omuyonta agirabanga amazzi bwatuuka w''agirabye tasangawo kintu kyonna wabula mu kifo ekyo asangawo Katonda n''amuwa mu bujjuvu okubalibwakwe. Bulijjo Katonda mwangu mu kubala (kwe).');
INSERT INTO "african_dev" VALUES(24,40,'24:40','. (Oba abaakaafuwala emirimu gya bwe giringa) ebizikiza ebiri mu nnyanja ey''ennyanga nga ebikkiddwa amayengo ng''ate waggulu waayo waliyo amayengo amalala ng''ate waggulu waago waliyo ebire ne biba bizikiza ebiri waggulu wa binnaabyo (omuntu singa abadde mu kifo ekyo) bw''ajjayo omukonogwe aba kumpi obutagulaba oyo yenna Katonda gwatawadde kitangaala tayinza kufuna kitangaala.');
INSERT INTO "african_dev" VALUES(24,41,'24:41','. Abaffe tolaba nti mazima Katonda bimutendereza byonna ebiri mu ggulu omusanvu n''ensi n''ebinyonyi nga byanjuluzza ebiwawaatiro (mu kumutendereza) buli kimu (Katonda) amanyi okusinza kwa kyo n''okutendereza kwa kyo.Anti Katonda amanyidde ddala ebyo bye bakola.');
INSERT INTO "african_dev" VALUES(24,42,'24:42','. Bwa Katonda obufuzi bw''eggulu omusanvu n''ensi, era ewa Katonda y''eri obuddo.');
INSERT INTO "african_dev" VALUES(24,43,'24:43','. Abaffe tolaba nti mazima Katonda atambuza ebire bwamala naabikungaanya wamu oluvanyuma n''abifuula entuumu olwo n''olaba nga enkuba etonnya okuva mu byo era atonnyesa omuzira okuva waggulu mu nsozi (ez''amazzi agakutte ebitole mu bbanga) n''atuusa n''omuzira ku oyo gwaba ayagadde ate n''aguwonya oyo gwaba ayagadde. Ekitangaala ky''e kimyanso kya byo kumpi kizibe amaaso (g''abantu).');
INSERT INTO "african_dev" VALUES(24,44,'24:44','. Katonda akyusakyusa emisana n''ekiro, mazima mu ekyo mulimu e kyokuyiga eri abalina amaaso.');
INSERT INTO "african_dev" VALUES(24,45,'24:45','. Katonda yatonda buli kiramu nga akiggya mu mazzi, mu byo mulimu ekitambulira ku lubuto lwa kyo, ne mu byo mulimu ekitambulira ku magulu abiri, era mulimu ekitambulira ku ana, Katonda atonda ekyo ky''aba ayagadde. Mazima Katonda muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(24,46,'24:46','. Mazima twassa obubonero obunnyonnyola, bulijjo Katonda alungamya gwaba ayagadde ku kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(24,47,'24:47','. (Abannanfusi) bagamba nti tukkirizza Katonda n''omubaka era ne tugonda ate ekitundu ku bo ne kyekyusa oluvanyuma lw''ekyo, era abo si bakkiriza.');
INSERT INTO "african_dev" VALUES(24,48,'24:48','. Bwe bayitibwa okudda eri Katonda n''omubakawe abe nga alamula wakati wa bwe ogenda okulaba nga ekitundu ku bo bakyawukanako.');
INSERT INTO "african_dev" VALUES(24,49,'24:49','. Bwe kiba nga amazima gali ku ludda lwa bwe bajja gyali (Nabbi) nga bagonvu.');
INSERT INTO "african_dev" VALUES(24,50,'24:50','. (Ekibakozesa ekyo) abaffe mu mitima gya bwe mulimu obulwadde oba babuusabuusa (obwa Nabbi bwe), oba batya Katonda n''omubaka we okubasaliriza, si bwekiri abo bbo be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(24,51,'24:51','. Mazima kyalibadde ekigambo kya bakkiriza (kye bandigambye) wonna we babayitira okudda eri Katonda n''omubakawe abe nga alamula wakati waabwe bandigambye tuwulidde era netugonda era abo bbo be b''okutuuka ku buwanguzi.');
INSERT INTO "african_dev" VALUES(24,52,'24:52','. Oyo yenna agondera Katonda n''omubakawe n''atya Katonda ne yeeyisa nga Katonda bwayagala abo bbo be bawanguzi.');
INSERT INTO "african_dev" VALUES(24,53,'24:53','. Era (abannanfusi) balayira Katonda olw''okukkaatiriza ebirayiro bya bwe, nti singa obalagidde (okugenda ku lutabaalo naawe) ddala bajja kugenda naawe, bagambe nti temulayira, okugonda kwa mmwe (kwe mulangirira) kumanyiddwa (nga kwa bulimba). Anti Katonda amanyidde ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(24,54,'24:54','. Bagambe nti: mugondere Katonda era mugondere omubaka, bwe bagaana mazima (Nabbi) avunaanyizibwa ku ebyo ebyamutikkibwa nga nammwe bwe muvunaanyizibwa ku ebyo ebyabatikkibwa, naye bwe mumugondera mujja kulungama, era omubaka tabanjibwa okugyako okutuusa obubaka okw''olwatu.');
INSERT INTO "african_dev" VALUES(24,55,'24:55','. Katonda yalagaanyisa abo abakkiriza mu mmwe ne bakola e mirimu emirongoofu nti ajja kubawa obuyinza mu nsi, nga bwe yawa abo abaaliwo oluberyeberye lwa bwe era nti ajja kubanywereza e ddiini yaabwe eyo gye yabasiimira, era oluvanyuma lw''okuwangalira mu kutya ajja kubawaanyisizaamu emirembe, babe nga bansinza nga tebangattako kintu kirala kyonna, oyo yenna akola ekimenya amateeka oluvanyuma lw''ekyo abo nno bo be boonoonyi.');
INSERT INTO "african_dev" VALUES(24,56,'24:56','. Muyimirizeewo e sswala era mutoole zzaka mugondere n''omubaka mube nga musaasirwa.');
INSERT INTO "african_dev" VALUES(24,57,'24:57','. Abo abaakaafuwala tosuubira nti bayinza okulemesa (Katonda) mu nsi, obuddo bwa bwe muliro era buddo bubi ddala.');
INSERT INTO "african_dev" VALUES(24,58,'24:58','. Abange mmwe abakkiriza bateekeddwa okubasaba okuyingira mu biseera bya mirundi esatu abo begyafuna e mikono gya mmwe egya ddyo, n''abo abatannaba kutuuka myaka gya bukulu mu mmwe, (bwe baba nga bayingira gye muli mu biseera ebyo, nabyo) e sswala ya Subuhi nga tennatuuka, ne mu kiseera we mweyambulira engoye zammwe mu ttuntu, n''oluvanyuma lw’e sswala ya Isha, (ebyo) biseera bya mmwe bisatu okubeera obwereere, nga oggyeko ebiseera ebyo, mmwe nabo temulina musango mwena wamu bwe bayingira gye muli nga tebasabye, abantu abo babeetoololeramu ng''abamu musisinkana ba nnammwe, bwatyo (Katonda) bwa bannyonnyola e bigambo anti bulijjo Katonda mumanyi nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(24,59,'24:59','. Abato mu mmwe bwe batuuka e myaka gy''obukulu bateekwa okusaba okukkirizibwa okuyingira nga bwe basaba abo abakulembedde, bwatyo Katonda bwabannyonnyola ebigambobye, era bulijjo Katonda mumanyi nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(24,60,'24:60','. N''abannyuka eby''obufumbo mu bakyala abo abatakyalina ssuubi mu kufumbirwa, tebalina kabi obuteebikkirira naye nga tebegyagyama na bya kwenyiriza, era okwessaamu ensa kye kisinga obulungi gye bali, era bulijjo Katonda awulira nnyo mumanyi.');
INSERT INTO "african_dev" VALUES(24,61,'24:61','. Muzibe talina kibi, n''omulema talina kibi wadde omulwadde talina kibi (nga bbo) namwe temulina kibi okulya mu maka gammwe, oba mu maka ga ba kitammwe, oba mu maka ga ba nnyammwe, oba mu maka ga baganda ba mmwe, oba mu maka ga bannyinammwe, oba mu maka ga ba kitammwe abato oba mu maka ga ba senga mmwe, oba mu maka ga ba kojjammwe oba mu maka ga ba maama ba mmwe abato, oba amaka ge mufuga ebisumuluzo byago, oba amaka ga mukwano gwa mmwe temulina kabi okuliira awamu oba nga mweyawudde yawudde, bwe muyingiranga amayumba mwetoolere ‘salaamu’, ekyo nga kiramuso eky''omukisa ekirungi ekiva ewa Katonda. Bwatyo Katonda bwabannyonnyola ebigambo (bye) mulyoke mutegeere.');
INSERT INTO "african_dev" VALUES(24,62,'24:62','. Mazima abakkiriza beebo abakkiriza Katonda n''omubakawe era bwe baba naye ku nsonga ebakwatako bonna tebayinza kuvaawo okugyako nga bamaze kumusaba, mazima abo abakusaba okubakkiriza bagende, abo beebo abakkiriza Katonda n''omubakawe, kale bwe baba bakusabye olw''ezimu ku nsonga zaabwe wa olukusa oyo gwoba oyagadde mu bo era osabe Katonda abawe ekisonyiwo, mazima Katonda musonyiyi nnyo musaasizi.');
INSERT INTO "african_dev" VALUES(24,63,'24:63','. Temufuulanga engeri gye mukoowoola omubaka nga engeri gye mwekoowoolamu mwekka na mwekka, mazima Katonda amanyi abo abeemulula mu mmwe nga bebbirira, abo abaawukana ku kigambokye bateekeddwa okutya okuba nga batuukwako okugezesebwa oba ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(24,64,'24:64','. Abange, mazima bya Katonda yekka ebiri mu ggulu omusanvu n''ensi, mazima amanya ekyo kye mubaako era amanyi olunaku lwe baliddizibwa gyali olwonno abategeeze bye bakola era Katonda amanyidde ddala ebikwata ku buli kintu.');
INSERT INTO "african_dev" VALUES(25,1,'25:1','. Musukkulumu oyo eyassa Kur’ani ku mudduwe eyawula wakati w''amazima n''obulimba alyoke abeere omutiisa eri ebitonde.');
INSERT INTO "african_dev" VALUES(25,2,'25:2','. Oyo (Katonda) bubwe yekka obufuzi bw''eggulu omusanvu n''ensi era teyeeteerawo mwana era talina amwegattako mu bufuzi era yatonda buli kintu naakikola olukola olukisaanira.');
INSERT INTO "african_dev" VALUES(25,3,'25:3','. Ate (abatakkiriza) bamuvaako ne beeteerawo ba katonda abalala abatalina kintu kyonna kye batonda so ng''ate bbo baatondebwa, era nga tebalina buyinza kwetuusaako kabi oba ekirungi, era tebalina buyinza ku kufa wadde ku bulamu wadde ku kuzuukira.');
INSERT INTO "african_dev" VALUES(25,4,'25:4','. Abaakaafuwala bagamba nti ebigambo (bya Muhammad) tebirina kye biri okugyako okuba nti bulimba bwe yagunjaawo, era abantu abalala ne bamuyamba ku bwo, olwo nno ne baba nga baleese okweyisa obubi n''okutemerera.');
INSERT INTO "african_dev" VALUES(25,5,'25:5','. Era ne bagamba nti (byayogera) nfumo z''abaasooka, yasaba ne zimuwandiikirwa olwo nno zimusomerwa enkya n''eggulo.');
INSERT INTO "african_dev" VALUES(25,6,'25:6','. Bagambe nti (Kur’ani) yagissa oyo amanyi ebyama byo mu ggulu omusanvu ne nsi, mazima yye (Katonda) bulijjo musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(25,7,'25:7','. Era baagamba nti: ono mubaka wangeriki alya emmere naatambula mu butale!, singa Malayika yassibwa gyali naabeera mutiisa awamu naye (nga amuyambako).');
INSERT INTO "african_dev" VALUES(25,8,'25:8','. Oba eby''obugagga nebissibwa gyali (okuva mu ggulu) oba abe nga alina e nnimiro mwalya. Era abeeyisa ne bagamba nti temulina gwe mugoberera okugyako omusajja omuloge.');
INSERT INTO "african_dev" VALUES(25,9,'25:9','. Laba engeri gye bakukubira ebifaananyi, olwo nno ne babula, n''olwekyo tebayinza kufuna kkubo libazza (ku mazima).');
INSERT INTO "african_dev" VALUES(25,10,'25:10','. Musukkulumu oyo singa aba ayagadde yaalikuteereddewo ebirungi okusinga ebyo, (nga nabyo) g''emalimiro agakulukutiramu emigga era naakuteerawo embiri.');
INSERT INTO "african_dev" VALUES(25,11,'25:11','. Wabula (ekibaleetera obuzibu kwe kuba nti) balimbisa olunaku lw''enkomerero, ate nga twategekera abalimbisa olunaku lw''enkomerero omuliro Saira.');
INSERT INTO "african_dev" VALUES(25,12,'25:12','. (Omuliro ogwo) bwe gulibalaba okusinziira ewala baliwulira mu gwo okutokota n''okududuma.');
INSERT INTO "african_dev" VALUES(25,13,'25:13','. Bwe balisuulibwa mu gwo, nga buli omu asuulibwa mu kifo ekifunda nga balijjiddwa, olwo nno balikaaba yaaye.');
INSERT INTO "african_dev" VALUES(25,14,'25:14','. Baligambibwa nti temukaaba ‘yaaye’ mulundi gumu, mukaabe ‘yaaye’ emirundi mingi.');
INSERT INTO "african_dev" VALUES(25,15,'25:15','. Bagambe nti ekyo (eky''okuyingira omuliro) kye kirungi oba e jjana ey''olubeerera eyo eyalagaanyisibwa abatya Katonda, egenda kubeera mpeera gye bali era nga buddo.');
INSERT INTO "african_dev" VALUES(25,16,'25:16','. Muyo bagenda kufuna bye baagala baakubeeramu obugenderevu (ekyo) ku Mukama omulabiriziwo ndagaano erina okukuumwa obutiribiri.');
INSERT INTO "african_dev" VALUES(25,17,'25:17','. N''olunaku lwalibakungaanya n''ebyo bye basinza ne baleka Katonda alibagamba nti mmwe mwabuza abaddu bange abo oba be baabulwa ekkubo.');
INSERT INTO "african_dev" VALUES(25,18,'25:18','. Baligamba nti: wasukkuluma Mukama. Tekitugwanirangako kweteerawo bakuumi balala ne tukuvaako, wabula wabeeyagaza ne bakadde baabwe okutuusa lwe beerabira okutendereza ne baba abantu ab''okuzikirira.');
INSERT INTO "african_dev" VALUES(25,19,'25:19','. (Katonda alibagamba nti) mazima babalimbisizza ku ebyo bye mwogera temusobola kweggyako bibonerezo wadde okutaasibwa, oyo yenna eyeeyisa obubi mu mmwe tumukombesa ku bibonerezo ebikambwe.');
INSERT INTO "african_dev" VALUES(25,20,'25:20','. Era tetwatuma olubereberye lwo babaka okugyako nga mazima bbo baliira ddala emmere nga batambula ne mu butale, era netufuula abamu ku mmwe ekikemo eri abalala, abaffe munagumiikiriza, bulijjo Mukama omulabiriziwo alabira ddala.');
INSERT INTO "african_dev" VALUES(25,21,'25:21','. Abo abatasuubira kutusisinkana ne bagamba nti singa twassibwako ba Malayika oba netulaba Mukama omulabirizi waffe, mazima beekuza nnyo mu myoyo gya bwe ne babula olubula oluyitirivu.');
INSERT INTO "african_dev" VALUES(25,22,'25:22','. Olunaku lwe baliraba ba Malayika tewaliba mawulire ga ssanyu ku lunaku olwo eri aboonoonyi, era (ba Malayika) baligamba nti: kikafuuwe tekigenda kubaawo (omu ku mmwe okuyingira (e jjana).');
INSERT INTO "african_dev" VALUES(25,23,'25:23','. Awo nno tulidda ku buli mulimu gwe baakola ne tugufuula olufufugge olufuumuddwa.');
INSERT INTO "african_dev" VALUES(25,24,'25:24','. Abantu b''o mu jjana ku lunaku olwo be baliba n''obutuulo obulungi, era be baliba n''ebifo ebirungi ebiwummulirwamu .');
INSERT INTO "african_dev" VALUES(25,25,'25:25','. N''olunaku eggulu lwe liriyatikamu ne riba nga lijjudde ebire, era ba Malayika ne bassibwa olussibwa.');
INSERT INTO "african_dev" VALUES(25,26,'25:26','. Obufuzi obwa nnamaddala ku lunaku olwo buliba bwa Mukama Katonda ow''ekisa ekingi, lugenda kuba lunaku luzito nnyo ku bakaafiiri.');
INSERT INTO "african_dev" VALUES(25,27,'25:27','. Era olunaku eyeeyisa obubi lwaliruma engaloze aligamba nti zinsanze, singa nateekawo ekkubo erinnyunga ku Mubaka.');
INSERT INTO "african_dev" VALUES(25,28,'25:28','. Nga ndabye nze singa ssaafuula gundi w''amukwano.');
INSERT INTO "african_dev" VALUES(25,29,'25:29','. Mazima yambuza nanzigya ku kubuulirira oluvanyuma lw''okuba nti kwanzijira, anti bulijjo Sitane ayabulira omuntu.');
INSERT INTO "african_dev" VALUES(25,30,'25:30','. Omubaka aligamba nti ayi Mukama omulabirizi wange, mazima abantu bange Kur’ani eno baagifuula kifulukwa.');
INSERT INTO "african_dev" VALUES(25,31,'25:31','. Era bwe kityo buli Nabbi twamuteerawo omulabe nga ava mu boonoonyi, era Mukama omulabiriziwo amala okuba nga ye mulungamya era omutaasa.');
INSERT INTO "african_dev" VALUES(25,32,'25:32','. Abo abaakaafuwala ne bagamba nti singa Kur’ani yassibwa ku ye omulundi gumu, twagissa bwe tutyo (mu bitundu tundu) tube nga tunyweza nayo omutimagwo era ne tugikusomera olusoma (mpola mpola).');
INSERT INTO "african_dev" VALUES(25,33,'25:33','. Tebalina lwe bakuleetera kyakulabirako wabula nga tukuleetera amazima n''ennyinyonnyola esinga obulungi.');
INSERT INTO "african_dev" VALUES(25,34,'25:34','. Abo abaliwalulirwa ku byenyi bya bwe nga batwalibwa mu muliro Jahannama, abo be bali mu kifo ekisinga obubi era be basinga okubula okuva ku kkubo.');
INSERT INTO "african_dev" VALUES(25,35,'25:35','. Mazima twawa Musa ekitabo ne tumussaako Mugandawe Haruna nga muyambi.');
INSERT INTO "african_dev" VALUES(25,36,'25:36','. Netugamba nti mwembi mugende eri abantu abaalimbisa ebigambo byaffe, olwo nno ne tubazikiriza oluzikiriza.');
INSERT INTO "african_dev" VALUES(25,37,'25:37','. N''abantu ba Nuhu bwe baalimbisa ababaka twabazikiririza mu mazzi netubafuula eky''okulabirako eri abantu.Era twategekera abeeyisa obubi ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(25,38,'25:38','. Era twazikiriza abantu ba A’di, n''aba Thamud, era ne ba nannyini luzzi, era netuzikiriza n''abantu b''emirembe emirala mingi wakati w''abo.');
INSERT INTO "african_dev" VALUES(25,39,'25:39','. Buli mulembe ku egyo twabakubiranga ebifaananyi (ebyabo abaabasookawo), era buli bamu ku abo twabazikiriza oluzikirira (olw''obugyemu bwa bwe).');
INSERT INTO "african_dev" VALUES(25,40,'25:40','. (Abagatta ku Katonda ebintu ebirala ab''e Makkah baali batambula e gendo zaabwe) mazima baatuuka mu kitundu ekya fukumulwako ebibonerezo ebibi (amayinja agaakuba ab''omulembe gwa Luutu). Abaffe baali tebakiraba (ne beebuulirira) wabula baali tebasuubira kuzuukira.');
INSERT INTO "african_dev" VALUES(25,41,'25:41','. Era bwe baba bakulabye tebalina ngeri gye bakulaba okugyako ekyokujeeja obujeeja (nga bwe bagamba) nti ono yooyo Katonda gwatumye nga Mubaka!.');
INSERT INTO "african_dev" VALUES(25,42,'25:42','. Ali kumpi nnyo okutubuza nga atujja ku ba katonda baffe singa tunaaba tetunyweredde ku kusinza bbo, era bajja kumanya mu kiseera webanaalabira ebibonerezo ani eyabula okuva ku kkubo (eggolokofu).');
INSERT INTO "african_dev" VALUES(25,43,'25:43','. Abaffe olaba oyo afuula okwagalakwe nga ye Katonda we, abaffe ggwe onoobeera ku ye omweyimirize.');
INSERT INTO "african_dev" VALUES(25,44,'25:44','. Oba olowooza nti abasinga obungi mu bo bawulira ebigambo bya Katonda, oba babitegeera, tebalina kye bali ku ebyo okugyako balinga nsolo, wabula bo be baabula okuva ku kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(25,45,'25:45','. Abaffe tolaba Mukama omulabiriziwo engeri gyawanvuyaamu ekisiikirize, singa yayagala yaali kirese nga kiyimiridde mu kifo kimu, ate twafuula enjuba nga y''eraga okubaawo n''okutambula kw''ekisiikirize.');
INSERT INTO "african_dev" VALUES(25,46,'25:46','. Bwe tumala tukikwata ne tukiggyawo nga tukiggyawo mpola mpola.');
INSERT INTO "african_dev" VALUES(25,47,'25:47','. Era yye (Katonda) yooyo eyabateerawo ekiro nga kibikka, naabateerawo n''okwebaka nga kuwummula naateekawo obudde obw''emisana nga mwe musaasaanira (olw''okunoonya eky''okulya).');
INSERT INTO "african_dev" VALUES(25,48,'25:48','. Era yye yooyo asindika empewo nga zisaasaanya amawulire ag''essanyu (enkuba) nga ekyo kiri wansi wa mikono gya kusaasirakwe, era tussa okuva mu ggulu amazzi amayonjo.');
INSERT INTO "african_dev" VALUES(25,49,'25:49','. Tube nga tuzza buggya ekitundu ekikaze era tuganywese bingi mu bye twatonda (nga) ebisolo n''abantu bangi.');
INSERT INTO "african_dev" VALUES(25,50,'25:50','. Era mazima twaginnyonnyola (Kur’ani) mu bo babe nga beebuulirira wabula abantu abasinga obungi ne bagaana era nebakalambirira ku bukaafiiri.');
INSERT INTO "african_dev" VALUES(25,51,'25:51','. Singa twayagala buli kitundu twandikitumidde omutiisa, (naye twasalawo obeere omubaka eri bonna).');
INSERT INTO "african_dev" VALUES(25,52,'25:52','. N''olwekyo togondera bakaafiiri (kozesa Kur’ani) obalwanyise olulwana olw''amanyi.');
INSERT INTO "african_dev" VALUES(25,53,'25:53','. Era (Katonda) yooyo eyagatta amazzi ag''ennyanja ebbiri (ez''enjawulo), gano malungi nnyo era nga ganywebwa ate nga gali g''amunnyu teganywebwa, era yassa wakati wa gombi ekyawula era ekiziyiza ekigagaana okwetabula.');
INSERT INTO "african_dev" VALUES(25,54,'25:54','. Era yye yooyo eyatonda omuntu nga amuggya mu mazzi, (mu muntu oyo yennyini) naggyamu ab''oluganda n''abako. Bulijjo Mukama omulabiriziwo muyinza.');
INSERT INTO "african_dev" VALUES(25,55,'25:55','. (Ate abantu balekawo Katonda) ne basinza ebintu ebirala ebitali Katonda ebyo ebitabagasa era ebitayinza kubatuusaako kabi, era bulijjo omukaafiiri ayamba Sitane mu kulwanyisa Mukama omulabiriziwe.');
INSERT INTO "african_dev" VALUES(25,56,'25:56','. Era tetwakutuma okugyako nga oli omusanyusa era omutiisa.');
INSERT INTO "african_dev" VALUES(25,57,'25:57','. Gamba nti: (kino kye nkola) sikibasabaako mpeera (era sirina kye njagala) okugyako oyo asalawo okweteerawo ekkubo erimutwala eri Mukama omulabiriziwe.');
INSERT INTO "african_dev" VALUES(25,58,'25:58','. Era weekwate ku mulamu oyo atafa, era otendereze ebitendobye. Kimala okuba nti yye amanyidde ddala ebyonoono bya baddube.');
INSERT INTO "african_dev" VALUES(25,59,'25:59','. Oyo eyatonda eggulu omusanvu n''ensi n''ebiri wakati wa byombi mu nnaku mukaaga oluvanyuma naatereera ku Arishi (Katonda) omusaasizi ow''ekisa ekingi (bwoba oyagala okumumanya) buuza abakenkufu ku bimukwatako.');
INSERT INTO "african_dev" VALUES(25,60,'25:60','. (Abakaafiiri) bwe bagambibwa nti muvunnamire omusaasizi ow''ekisa ekingi (Katonda), bagamba nti omusaasizi ow''ekisa ekingi oyo yaani! Tumale gavunnamira oyo gwotulagira (nga tetumanyi) ne kibongera kwesamba (ddiini).');
INSERT INTO "african_dev" VALUES(25,61,'25:61','. Musukkulumu oyo eyassa mu ggulu ebifo ebibeeramu emmunyeenye, era nassaamu ettaala (enjuba eyaka) n''omwezi oguwa ekitangaala.');
INSERT INTO "african_dev" VALUES(25,62,'25:62','. Era yye yooyo eyassaawo ekiro n''emisana nga bigoberegana (yabiteerawo) oyo ayagala okwebuulirira oba ayagala okwebaza.');
INSERT INTO "african_dev" VALUES(25,63,'25:63','. Bo abaddu ba Katonda beebo abatambula ku nsi nga bakkakkamu. Abatategeera bwe boogera nabo (ebintu by''ekiyaaye) bo baddamu nti mirembe.');
INSERT INTO "african_dev" VALUES(25,64,'25:64','. Era beebo kulwa Mukama omulabirizi waabwe abasula nga bavunnama era abayimirira (nga basaala).');
INSERT INTO "african_dev" VALUES(25,65,'25:65','. Era beebo abagamba nti ayi Mukama omulabirizi waffe tuwuguleko ebibonerezo by''omuliro Jahannama anti mazima ebibonerezo byagwo bya lubeerera.');
INSERT INTO "african_dev" VALUES(25,66,'25:66','. Mazima ggwo kifo era butuulo bubi.');
INSERT INTO "african_dev" VALUES(25,67,'25:67','. (Abaddu ba Katonda) era beebo bwe baba nga bagabye tebadiibuuda era tebaganiriza, okuwaayo kwa bwe kuli wakati w''ebyo byombi.');
INSERT INTO "african_dev" VALUES(25,68,'25:68','. Era beebo abatasinza kintu kirala awamu ne Katonda, era abatatta muntu oyo Katonda gwe yaziza okugyako nga waliwo ensonga, era abatayenda, omuntu akola ekimu ku ebyo agenda kusisinkana (e bibonerezo) bye bibi (bye).');
INSERT INTO "african_dev" VALUES(25,69,'25:69','. (Si kye kyokka wabula) ebibonerezobye bigenda kubazibwamu ku lunaku lw''enkomerero era waakubituulamu obugenderevu mu ngeri enyoomesa.');
INSERT INTO "african_dev" VALUES(25,70,'25:70','. Okugyako oyo alyenenya nakkiriza naakola emirimu emirungi. Abo nno Katonda ebibi bya bwe agenda kubawaanyisizaamu ebirungi. Era bulijjo Katonda musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(25,71,'25:71','. Omuntu yenna ayeenenya naakola emirimu emirungi, mazima yye aba yeemenyedde Katonda olw''emenya olwannamaddala.');
INSERT INTO "african_dev" VALUES(25,72,'25:72','. (Abaddu ba Katonda) era beebo tebawa bujulizi ku bigambo bya bulimba, era bwe bayita mu bifo omukolerwa ebitali bya magezi bayitawo nga beewa ekitiibwa.');
INSERT INTO "african_dev" VALUES(25,73,'25:73','. Era beebo bwe bajjukizibwa ebigambo bya Mukama omulabirizi waabwe tebabidduka nga ba kiggala oba ba muzibe.');
INSERT INTO "african_dev" VALUES(25,74,'25:74','. Era beebo abagamba nti ayi Mukama omulabirizi waffe, tuwe nga oggya mu betwafumbiriganwa nabo era nga oggya ne mu zadde lyaffe ekitebenkeza amaaso gaffe, era otufuule abakulembeze baabo abakutya.');
INSERT INTO "african_dev" VALUES(25,75,'25:75','. Abo nno bagenda kusasulwa nga baweebwa ebifo bya waggulu olw''obugumiikiriza bwe baakola. Nga bali mu byo bagenda kuweebwa ebiramuso ne ssalaamu (eziriva mu ba Malayika).');
INSERT INTO "african_dev" VALUES(25,76,'25:76','. Baakubeeramu olubeerera nga butebenkero era butuulo obulungi.');
INSERT INTO "african_dev" VALUES(25,77,'25:77','. Bagambe (ggwe Muhammad abo abawakanya Katonda) nti Katonda talina kimutawaanya lwa kuba nti temumusaba, mazima mwalimbisa era mu bwangu mujja kutuukibwako e bibonerezo ebyakakata edda ku mmwe.');
INSERT INTO "african_dev" VALUES(26,1,'26:1','. Twaa Siin Miim.');
INSERT INTO "african_dev" VALUES(26,2,'26:2','. Ebyo bye bigambo by''ekitabo ekinnyonnyola.');
INSERT INTO "african_dev" VALUES(26,3,'26:3','. Olabika nga ayagala okwetta olw''okuba bagaanye okuba abakkiriza.');
INSERT INTO "african_dev" VALUES(26,4,'26:4','. Singa twagadde twandissizza ku bo okuva mu ggulu akabonero, ensingo zaabwe nezikugondera olubeerera.');
INSERT INTO "african_dev" VALUES(26,5,'26:5','. Tewali bubaka bwonna bupya kuva wa Katonda Omusaasizi ennyo wabula bo babuwakanya.');
INSERT INTO "african_dev" VALUES(26,6,'26:6','. Nebaba nga mazima balimbisa, bigenda kubajjira ebigambo ebyo bye baali bajeeja');
INSERT INTO "african_dev" VALUES(26,7,'26:7','. Abaffe tebatunuulira nsi ne balaba bimeka bye twameza mu yo mu buli mutindo oguvaamu emigaso.');
INSERT INTO "african_dev" VALUES(26,8,'26:8','. Mazima mu ekyo ddala mulimu akabonero (akalaga obuyinza bwaffe), wabula bangi mu bo tebakkiriza.');
INSERT INTO "african_dev" VALUES(26,9,'26:9','. Era mazima Mukama omulabiriziwo ddala yye ye nantakubwa ku mukono omusaasizi ennyo.');
INSERT INTO "african_dev" VALUES(26,10,'26:10','. Era jjukira ekiseera Mukama omulabiriziwo bwe yakoowoola Musa (naamulagira) nti: genda eri abantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(26,11,'26:11','. Abantu ba Firaawo (obagambe nti) abaffe tebatya Katonda!.');
INSERT INTO "african_dev" VALUES(26,12,'26:12','. (Musa) naagamba nti: mazima nze ntya okuba nga bannimbisa.');
INSERT INTO "african_dev" VALUES(26,13,'26:13','. Ekifuba kyange ne kifunda olulimi lwange ne rutayatula, n''olwekyo lagira Haruna (annyambeko).');
INSERT INTO "african_dev" VALUES(26,14,'26:14','. Ate banninako omusango, ntya nti bayinza okunzita.');
INSERT INTO "african_dev" VALUES(26,15,'26:15','. (Katonda) naagamba nti nedda mugende n''obubonero bwaffe mazima ffe tuli wamu na mmwe tuwulira (tugoberera buli ekigenda mu maaso).');
INSERT INTO "african_dev" VALUES(26,16,'26:16','. Mugende ewa Firaawo mumugambe nti mazima ffe tutumiddwa Mukama omulabirizi w''ebitonde byonna.');
INSERT INTO "african_dev" VALUES(26,17,'26:17','. (Atugambye nti) tuwe abaana ba Israil tugende nabo.');
INSERT INTO "african_dev" VALUES(26,18,'26:18','. (Firaawo) naagamba nti tetwakukuliza mu ffe okuva nga oli mwana muto, era nga wawangaalira mu ffe emyaka mingi ku buwangaazibwo.');
INSERT INTO "african_dev" VALUES(26,19,'26:19','. Era nookola ekikolwakyo ekyo kye wakola e kyakufuula okuba mu batasiima bye bakolerwa.');
INSERT INTO "african_dev" VALUES(26,20,'26:20','. (Musa) naagamba nti nnakikola bwe nnali nkyali mu babuze.');
INSERT INTO "african_dev" VALUES(26,21,'26:21','. Olwo nno nembadduka bwe nnabatya, awo Mukama omulabirizi wange nangabira okumanya e nsonga era nanteeka mu babaka.');
INSERT INTO "african_dev" VALUES(26,22,'26:22','. Ne''kyo e kyengera (eky''okundera n''okukulira mu mmwe) okindalaasaako naye nga ggwe abaana ba Israil wabafuula abaddu.');
INSERT INTO "african_dev" VALUES(26,23,'26:23','. Firaawo naagamba nti Mukama omulabirizi w''ebitonde byonna gwoyogerako yaani?.');
INSERT INTO "african_dev" VALUES(26,24,'26:24','. Musa naagamba nti Mukama omulabirizi w''eggulu omusanvu n''ensi n''ebiri wakati wa byombi bwe muba nga mwetegereza ebintu.');
INSERT INTO "african_dev" VALUES(26,25,'26:25','. (Firaawo) kwe kugamba abaali naye awo nti: abaffe temuwulira (nga bwabijweteka).');
INSERT INTO "african_dev" VALUES(26,26,'26:26','. (Musa) naagamba nti ye Mukama omulabirizi wa mmwe, era Mukama omulabirizi wa bakadde ba mmwe abaasooka.');
INSERT INTO "african_dev" VALUES(26,27,'26:27','. (Firaawo) naagamba nti: mazima omubaka wa mmwe oyo eyatumiddwa gye muli mulalu.');
INSERT INTO "african_dev" VALUES(26,28,'26:28','. (Musa) naagamba nti (ye) Mukama omulabirizi w''ebuvanjuba n''ebugwanjuba n''ebyo ebiri wakati wa byombi singa mubadde mutegeera.');
INSERT INTO "african_dev" VALUES(26,29,'26:29','. (Firaawo) naagamba nti mazima singa oteekawo Katonda omulala atali nze nja kukuteera ddala mu b''okusibwa.');
INSERT INTO "african_dev" VALUES(26,30,'26:30','. (Musa) naagamba nti ne bwennaaba nkuleetedde ekintu ekyeyolefu (ekiraga obutuufu bw''ebyo bye ngamba).');
INSERT INTO "african_dev" VALUES(26,31,'26:31','. (Firaawo) naagamba nti kale kireete bwoba nga oli mu boogera amazima.');
INSERT INTO "african_dev" VALUES(26,32,'26:32','. Awo nno Musa naasuula omuggogwe, okugenda okulaba nga musota omweyolefu.');
INSERT INTO "african_dev" VALUES(26,33,'26:33','. Era naggyayo omukonogwe okugenda okulaba nga ggwo gwakaayakanira abalabi.');
INSERT INTO "african_dev" VALUES(26,34,'26:34','. (Firaawo) naagamba abo abaali batudde waali nti mazima ono mulogo kafulu.');
INSERT INTO "african_dev" VALUES(26,35,'26:35','. Ayagala kubaggya mu nsi yammwe ku lw''eddogolye kati mugamba ki.');
INSERT INTO "african_dev" VALUES(26,36,'26:36','. Ne bagamba nti mulagaanyise ne Mugandawe (ddi na wwa) aw''okubasisinkana era otume mu bitundu eby''enjawulo abakungaanya abalogo.');
INSERT INTO "african_dev" VALUES(26,37,'26:37','. Bajja kukuleetera buli mulogo kakensa.');
INSERT INTO "african_dev" VALUES(26,38,'26:38','. Olwo nno abalogo ne bakungaanyizibwa mu kiseera ekiragaane ku lunaku olumanyiddwa.');
INSERT INTO "african_dev" VALUES(26,39,'26:39','. Abantu baagambibwa nti abaffe mmwe munajja.');
INSERT INTO "african_dev" VALUES(26,40,'26:40','. Sikulwa nga tunaagoberera abalogo bwe banaaba bo nga be bawangudde.');
INSERT INTO "african_dev" VALUES(26,41,'26:41','. Abalogo bwe bajja baagamba Firaawo nti mazima tuteekwa okuweebwa empeera bwe tunaaba nga ffe tuwangudde.');
INSERT INTO "african_dev" VALUES(26,42,'26:42','. (Firaawo) naagamba nti weewaawo era mazima mmwe singa kinaaba bwe kityo mujja kubeera ba ku lusegere.');
INSERT INTO "african_dev" VALUES(26,43,'26:43','. Musa naabagamba nti kale mwanje kye mwanja.');
INSERT INTO "african_dev" VALUES(26,44,'26:44','. Nebasuula e miguwa gya bwe n''emiggo gya bwe era ne bagamba nti ku lw''ekitiibwa kya Firaawo mazima ffe tujja okuwangula.');
INSERT INTO "african_dev" VALUES(26,45,'26:45','. Olwo nno Musa naasuula omuggogwe okugenda okulaba nga gwo gumira buli kye baali bagunjizzaawo.');
INSERT INTO "african_dev" VALUES(26,46,'26:46','. Awo nno abalogo ne beeyala nga bavunnama.');
INSERT INTO "african_dev" VALUES(26,47,'26:47','. Nebagamba nti tukkirizza Mukama Omulabirizi w''ebitonde.');
INSERT INTO "african_dev" VALUES(26,48,'26:48','. Mukama omulabirizi wa Musa ne Haruna.');
INSERT INTO "african_dev" VALUES(26,49,'26:49','. Firaawo naagamba nti: mukkirizza byagamba nga sinnaba kubawa lukusa, (kirabika) nti mazima yye ye mukulu wa mmwe oyo eyabayigiriza eddogo, kale nno mujja kukitegeera, nja ku bakutulako e mikono gya mmwe n''amagulu ga mmwe nga bwe nkuggyako okugulu okwa ddyo, omukono nkuggyako gwa kkono, era ddala mwenna nja ku bakomerera ku musaalaba.');
INSERT INTO "african_dev" VALUES(26,50,'26:50','. Ne bagamba nti ekyo tekiriimu buzibu, (anti) mazima ffe tujja kuba tudda eri Mukama omulabirizi waffe.');
INSERT INTO "african_dev" VALUES(26,51,'26:51','. Mazima ffe tululunkanira okuba nga Mukama omulabirizi waffe atusonyiwa ebyonoono byaffe bwe tuba nga ffe abakkiriza abasooka.');
INSERT INTO "african_dev" VALUES(26,52,'26:52','. Era netutumira Musa nti tambula ekiro n''abaddu bange, mazima mmwe mujja kulondoolwa.');
INSERT INTO "african_dev" VALUES(26,53,'26:53','. Firaawo naatuma mu bibuga abakungaanya (eggye era naagamba nti).');
INSERT INTO "african_dev" VALUES(26,54,'26:54','. Mazima abo kabiina ka bantu batono.');
INSERT INTO "african_dev" VALUES(26,55,'26:55','. Era mazima bbo batunyiizizza.');
INSERT INTO "african_dev" VALUES(26,56,'26:56','. Era nga bulijjo mazima ffe nga tukolera wamu tuli abantu abeekengera.');
INSERT INTO "african_dev" VALUES(26,57,'26:57','. (Okusinziira ku nteekateeka zaabwe ne bye bakola ate) twabaggya mu malimiro ne nsulo ezikulukuta.');
INSERT INTO "african_dev" VALUES(26,58,'26:58','. N''amawanika n''ebifo eby''ekitiibwa.');
INSERT INTO "african_dev" VALUES(26,59,'26:59','. Bwe tutyo nno ne tubisikiza abaana ba Israil.');
INSERT INTO "african_dev" VALUES(26,60,'26:60','. Olwo nno nebabagoberera nga busaasaana.');
INSERT INTO "african_dev" VALUES(26,61,'26:61','. Ebibinja byombi bwe byalengeragana abantu ba Musa baagamba nti mazima ffe bajja kutukwata (batutte).');
INSERT INTO "african_dev" VALUES(26,62,'26:62','. (Musa) naagamba nti nedda mazima Mukama omulabirizi wange ali nange ajja kundaga eky''okukola.');
INSERT INTO "african_dev" VALUES(26,63,'26:63','. Awo nno ne tutumira Musa nti kuba omuggogwo ku nnyanja, olwo neebajjukamu, buli kibajjo ne kiba nga olusozi olunene.');
INSERT INTO "african_dev" VALUES(26,64,'26:64','. Netusembeza awo abalala (Firaawo ne ggyerye nabo ne bayingira e kkubo Musa lye yakuba ku nnyanja).');
INSERT INTO "african_dev" VALUES(26,65,'26:65','. Netuwonya Musa n''abaali naye bonna.');
INSERT INTO "african_dev" VALUES(26,66,'26:66','. Oluvanyuma ne tuzikiriza abasigadde.');
INSERT INTO "african_dev" VALUES(26,67,'26:67','. Mazima mu ekyo mulimu eky''okuyiga naye abasinga obungi mu bo tebaali bakkiriza.');
INSERT INTO "african_dev" VALUES(26,68,'26:68','. Era mazima Mukama omulabiriziwo yye ye nantakubwa ku mukono omusaasizi.');
INSERT INTO "african_dev" VALUES(26,69,'26:69','. Era basomere ebigambo ebikwata ku Ibrahim.');
INSERT INTO "african_dev" VALUES(26,70,'26:70','. Bwe yagamba kitaawe n''abantu be nti musinza biki.');
INSERT INTO "african_dev" VALUES(26,71,'26:71','. Nebagamba nti tusinza amasanamu tubeera we gali era ne tugawa ebiseera bingi mu kugasinza.');
INSERT INTO "african_dev" VALUES(26,72,'26:72','. (Ibrahim) naagamba nti abaffe bwe musaba gabawulira.');
INSERT INTO "african_dev" VALUES(26,73,'26:73','. Oba gabagasa oba gasobola okubatuusaako akabi.');
INSERT INTO "african_dev" VALUES(26,74,'26:74','. Nebagamba nti nedda wabula bwe tutyo bakadde baffe bye twasanga bakola.');
INSERT INTO "african_dev" VALUES(26,75,'26:75','. Naagamba nti mulaba ebyo bye musinza.');
INSERT INTO "african_dev" VALUES(26,76,'26:76','. Mmwe ne bakadde ba mmwe abaabakulembera.');
INSERT INTO "african_dev" VALUES(26,77,'26:77','. Mazima byo balabe bange, okugyako Mukama omulabirizi w''ebitonde byonna.');
INSERT INTO "african_dev" VALUES(26,78,'26:78','. Oyo eyantonda, yye y''annungamya.');
INSERT INTO "african_dev" VALUES(26,79,'26:79','. Era yye yooyo andiisa nannyweesa.');
INSERT INTO "african_dev" VALUES(26,80,'26:80','. Era bwe ndwala yye y''amponya.');
INSERT INTO "african_dev" VALUES(26,81,'26:81','. Era yooyo anzita ate nampa obulamu.');
INSERT INTO "african_dev" VALUES(26,82,'26:82','. Era yye gwe ndulunkanira abe nga ansonyiwa ebyonoono byange ku lunaku lw''okusasulwa.');
INSERT INTO "african_dev" VALUES(26,83,'26:83','. Ayi Mukama omulabirizi ngabira okumanya ensonga era onsisinkanye abalongoofu.');
INSERT INTO "african_dev" VALUES(26,84,'26:84','. Era onteerewo okwogerwako obulungi mu balijja oluvanyuma.');
INSERT INTO "african_dev" VALUES(26,85,'26:85','. Era onteeke mu balisikira e jjana ey''ebyengera.');
INSERT INTO "african_dev" VALUES(26,86,'26:86','. Era sonyiwa Kitange mazima yye yali mu babuze.');
INSERT INTO "african_dev" VALUES(26,87,'26:87','. Era tonswazanga olunaku lwe balizuukizibwa.');
INSERT INTO "african_dev" VALUES(26,88,'26:88','. Olunaku emmaali n''abaana lwe bitaligasa.');
INSERT INTO "african_dev" VALUES(26,89,'26:89','. Okugyako (aliganyulwa) yooyo alijja ewa Katonda n''omutima omuyonjo.');
INSERT INTO "african_dev" VALUES(26,90,'26:90','. E jjana erisembezebwa eri abatya Katonda.');
INSERT INTO "african_dev" VALUES(26,91,'26:91','. N''omuliro ne gulagibwa eri ababuze.');
INSERT INTO "african_dev" VALUES(26,92,'26:92','. Era bagenda kugambibwa nti biriwa ebyo bye mwasinzanga.');
INSERT INTO "african_dev" VALUES(26,93,'26:93','. Ne muleka Katonda, abaffe bisobola okubataasa oba byo bisobola okwetaasa.');
INSERT INTO "african_dev" VALUES(26,94,'26:94','. Olwo nno ne bakasukibwa mu gwo bo n''ababuze.');
INSERT INTO "african_dev" VALUES(26,95,'26:95','. Na bonna abali muggye lya Ibuliisu.');
INSERT INTO "african_dev" VALUES(26,96,'26:96','. Baligamba nga bali mu gwo nga bakaayagana (nti).');
INSERT INTO "african_dev" VALUES(26,97,'26:97','. Tulayira Katonda nti mazima ddala twali mu bubuze obw''olwatu.');
INSERT INTO "african_dev" VALUES(26,98,'26:98','. Bwe twabenkanya (mmwe) ne Mukama omulabirizi w''ebitonde byonna.');
INSERT INTO "african_dev" VALUES(26,99,'26:99','. Era tewali yatubuza okugyako aboonoonyi.');
INSERT INTO "african_dev" VALUES(26,100,'26:100','. N''olwekyo tetulina bawolereza.');
INSERT INTO "african_dev" VALUES(26,101,'26:101','. Wadde ow''omukwano alumirwa.');
INSERT INTO "african_dev" VALUES(26,102,'26:102','. Naye singa tubadde tulina omukisa (okudda ku nsi) ne tuba mu bakkiriza.');
INSERT INTO "african_dev" VALUES(26,103,'26:103','. Mazima mu ekyo mulimu eky''okuyiga naye abasinga obungi mu bo tebakkiriza.');
INSERT INTO "african_dev" VALUES(26,104,'26:104','. Era mazima Mukama omulabiriziwo ddala yye ye nantakubwa ku mukono omusaazizi.');
INSERT INTO "african_dev" VALUES(26,105,'26:105','. Abantu ba Nuhu baalimbisa ababaka.');
INSERT INTO "african_dev" VALUES(26,106,'26:106','. Mu kiseera muganda waabwe Nuhu we yabagambira nti abaffe temutya (Katonda).');
INSERT INTO "african_dev" VALUES(26,107,'26:107','. Mazima nze gye muli ndi mubaka omwesigwa.');
