INSERT INTO "african_dev" VALUES(26,109,'26:109','. Era sikibasabirako mpeera, empeera yange teri okugyako ku Mukama omulabirizi w''ebitonde byonna.');
INSERT INTO "african_dev" VALUES(26,110,'26:110','. Kale mutye Katonda era mungondere.');
INSERT INTO "african_dev" VALUES(26,111,'26:111','. Nebagamba nti abaffe tukukkirize nga abakugoberedde be ba wansi.');
INSERT INTO "african_dev" VALUES(26,112,'26:112','. Naagamba nti: mmanya ntya bye baali bakola.');
INSERT INTO "african_dev" VALUES(26,113,'26:113','. Okubalibwa kwa bwe tekuli okugyako kuli ku Mukama omulabirizi wange singa mubadde mumanyi.');
INSERT INTO "african_dev" VALUES(26,114,'26:114','. Ate nze siri wa kugoba bakkiriza (olw''okuba ba wansi).');
INSERT INTO "african_dev" VALUES(26,115,'26:115','. Nze siri okugyako omutiisa ow''olwatu.');
INSERT INTO "african_dev" VALUES(26,116,'26:116','. Nebagamba nti: owange Nuhu bwo teekomeko ojja kubeerera ddala mu b''okukubwa amayinja.');
INSERT INTO "african_dev" VALUES(26,117,'26:117','. Naagamba nti ayi Mukama omulabirizi wange mazima abantu bange bannimbisizza.');
INSERT INTO "african_dev" VALUES(26,118,'26:118','. Kale salawo wakati wange nabo olusalawo olwannamaddala, era omponye owonye n''abakkiriza bendi nabo.');
INSERT INTO "african_dev" VALUES(26,119,'26:119','. Ne tumuwonya n''abo abaali naye mu lyato eryali lijjudde.');
INSERT INTO "african_dev" VALUES(26,120,'26:120','. Oluvanyuma twazikiriza abaasigala.');
INSERT INTO "african_dev" VALUES(26,121,'26:121','. Mazima mu ekyo mulimu eky''okuyiga naye abasinga obungi mu bo tebakkiriza.');
