INSERT INTO "african_dev" VALUES(26,129,'26:129','. Era nemweteerawo amayumba amagundiivu nga mulinga abalibeerawo olubeerera.');
INSERT INTO "african_dev" VALUES(26,130,'26:130','. Ate bwe mukwata (abalabe ba mmwe) mukwata enkwata eya ba kirimaanyi (abanyigiriza bannaabwe).');
INSERT INTO "african_dev" VALUES(26,131,'26:131','. Kale mutye Katonda era mungondere.');
INSERT INTO "african_dev" VALUES(26,132,'26:132','. Era mutye oyo abagabirira ebyo bye mumanyi.');
INSERT INTO "african_dev" VALUES(26,133,'26:133','. Abagabirira ebisolo ebirundibwa era n''abaana.');
INSERT INTO "african_dev" VALUES(26,134,'26:134','. N''amalimiro n''emigga.');
