INSERT INTO "african_dev" VALUES(26,193,'26:193','. Yakka nayo mwoyo omwesigwa (Jiburilu).');
INSERT INTO "african_dev" VALUES(26,194,'26:194','. (Naagissa) ku mutima gwo olyoke obe mu batiisa (abantu okukola ebibi).');
INSERT INTO "african_dev" VALUES(26,195,'26:195','. Nga eri mu lulimi oluwarabu olutegeerekeka.');
