INSERT INTO "african_dev" VALUES(26,213,'26:213','. (N''olwekyo) bwoba osinza Katonda tomugattako kintu kirala (anti bwokikola) obeera wa mu balibonerezebwa.');
INSERT INTO "african_dev" VALUES(26,214,'26:214','. Era tiisa ab''oluganda lwo ab''okumpi.');
INSERT INTO "african_dev" VALUES(26,215,'26:215','. Era kakkanya ekiwawaatiro kyo (weewombeke) eri oyo aba akugoberedde mu bakkiriza.');
