INSERT INTO "african_dev" VALUES(26,220,'26:220','. Mazima yye y''awulira ennyo omumanyi.');
INSERT INTO "african_dev" VALUES(26,221,'26:221','. Abaffe mbategeeze Sitane zikka ku ani.');
INSERT INTO "african_dev" VALUES(26,222,'26:222','. Zikka ku buli musazi w''abigambo omwonoonyi.');
INSERT INTO "african_dev" VALUES(26,223,'26:223','. Abo abasaasaanya bye bawulira naye nga abasinga obungi mu bo balimba.');
INSERT INTO "african_dev" VALUES(26,224,'26:224','. Era abatontomi, ababuze be babagoberera.');
INSERT INTO "african_dev" VALUES(26,225,'26:225','. Abaffe tolaba nti babulubuutira mu buli lusenyi.');
INSERT INTO "african_dev" VALUES(26,226,'26:226','. Era nti mazima bbo boogera bye batakola.');
INSERT INTO "african_dev" VALUES(26,227,'26:227','. Okugyako abo abakkiriza ne bakola emirimu emirungi ne boogera nnyo ku Katonda era ne baba nga beetaasa oluvanyuma lw''okuba nti bayisibwa bubi, era mazima abo abeeyisa obubi bagenda kumanya buddo ki gye balidda.');
INSERT INTO "african_dev" VALUES(27,1,'27:1','. waa Siin, bino bigambo bya Kur’ani era ekitabo ekinnyonnyofu.');
INSERT INTO "african_dev" VALUES(27,2,'27:2','. Kya kulungamya era mawulire ga ssanyu eri abakkiriza.');
INSERT INTO "african_dev" VALUES(27,3,'27:3','. Abo abayimirizaawo e sswala ne batoola Zakka era nga bbo bakakasa olunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(27,4,'27:4','. Mazima abo abatakkiriza lunaku lwa nkomerero twabalabisiza bulungi emirimu gya bwe, olwo nno bbo ne babulubuuta.');
INSERT INTO "african_dev" VALUES(27,5,'27:5','. Abo beebo abatuusibwako e bibonerezo ebizito (ng''okuttibwa) ate nga bbo ku lunaku lw''enkomerero be b''okufaafaaganirwa ennyo.');
INSERT INTO "african_dev" VALUES(27,6,'27:6','. Era mazima ggwe (Muhammad) oweebwa Kur’ani ng''eva (ewa Katonda) mugoba nsonga amanyi ennyo.');
INSERT INTO "african_dev" VALUES(27,7,'27:7','. Era jjukira (Musa) bwe yagamba ab''omu makage nti mazima nze nengedde omuliro (kanngendeyo) nja kubaleetera okuva we guli e kigambo oba mbaleetere ekitawuliro kibayambe okwota.');
INSERT INTO "african_dev" VALUES(27,8,'27:8','. Naye bwe yagutuukako yakoowoolwa (naagambibwa) nti wa mukisa oyo ali mu muliro n''oyo ali emabbali waagwo, era yasukkuluma Mukama omulabirizi w''ebitonde byonna.');
INSERT INTO "african_dev" VALUES(27,9,'27:9','. Owange Musa mazima nze Katonda nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(27,10,'27:10','. Era suula omuggogwo, bwe yagulaba nga gwenyeenya nga gulinga akasota akatono yakyuka nadduka era nga tayagala kudda, (Katonda kwe kumukoowoola nti) owange Musa totya mazima nze ababaka tebatya mu maaso gange.');
INSERT INTO "african_dev" VALUES(27,11,'27:11','. Okugyako oyo aba yeeyisizza obubi mu bantu abalala abatali babaka oluvanyuma obubi n''abuwaanyisaamu obulungi mazima nze ndi musonyiyi nnyo musaasizi.');
INSERT INTO "african_dev" VALUES(27,12,'27:12','. (Era Katonda naagamba Musa nti) yingiza omukonogwo mu lugoye lwo gujja kuvaayo nga mweru, so nga si mulwadde (nga ebibiri ebyo, bumu) ku bubonero omwenda bw''otwalira Firaawo n''abantube mazima bbo bantu aboonoonyi.');
INSERT INTO "african_dev" VALUES(27,13,'27:13','. Obubonero bwaffe bwe bwabatuukako nga bulagira ddala amazima baagamba nti lino ddogo eryeyolefu.');
INSERT INTO "african_dev" VALUES(27,14,'27:14','. Nebabuwakanya mu ngeri y''okweyisa obubi n''okwekuluntaza, so nga emyoyo gya bwe gya bukkiriza, kale nno tunuulira olabe enkomerero ya boonoonyi yali etya.');
INSERT INTO "african_dev" VALUES(27,15,'27:15','. Era mazima twawa Dauda ne Sulaiman okumanya era ne bagamba nti atenderezebwa Katonda oyo eyatusukkulumya ku bangi mu baddube abakkiriza.');
INSERT INTO "african_dev" VALUES(27,16,'27:16','. Era Sulaiman yasikira Dauda naagamba nti abange mmwe abantu tuyigiriziddwa olulimi lw''ebinyonyi era tuweereddwa ku buli kintu mazima kino bwe bulungi obweyolefu.');
INSERT INTO "african_dev" VALUES(27,17,'27:17','. Sulaiman naakungirwa eggyerye nga liva mu Majinni n''abantu n''ebinyonyi nga nabo batambulira ku nteekateeka eyabaweebwa.');
INSERT INTO "african_dev" VALUES(27,18,'27:18','. Okutuusa lwe baatuuka ku lusenyi lw''ensanafu, ensanafu emu kwe kugamba nti abange ba wansanafu muyingire mu bisulo bya mmwe, Sulaiman n''eggyerye tebababetenta nga nabo tebamanyi.');
INSERT INTO "african_dev" VALUES(27,19,'27:19','. Mu kiseera ekyo (Sulaiman) naamwenya ng''ate aseka olw''ekigambo kyayo era naagamba nti: ayi Mukama omulabirizi wange, nyamba mbe nga nsobola okwebaza ebyengerabyo ebyo bye wangabira era n''ebyo bye wagabira bakadde bange bombi era mbe nga nkola ebirungi byosiima era ku lw''okusaasirakwo onnyingize mu baddubo abalongoofu.');
INSERT INTO "african_dev" VALUES(27,20,'27:20','. Era Sulaiman yatunula mu binyonyi naagamba nti lwaki e fulungu siriraba oba liri mu bataliiwo.');
INSERT INTO "african_dev" VALUES(27,21,'27:21','. Nja ku libonereza e bibonerezo ebikakali si nakindi nja ku lisala oba ssi ekyo lindeetere ensonga ennambulukufu.');
INSERT INTO "african_dev" VALUES(27,22,'27:22','. (E fulungu) lyamala ebbanga eritaali ddene (oluvanyuma nerijja ewa Sulaiman) nerigamba nti nategedde ekintu kyotategeeranga era nkuleetedde okuva e Sabaa amawulire amakakafu.');
INSERT INTO "african_dev" VALUES(27,23,'27:23','. Mazima nze nnasanze nga omukyala y''abafuga ate nga yaweebwa buli kintu, era alina namulondo empitirivu.');
INSERT INTO "african_dev" VALUES(27,24,'27:24','. Namusanze (yye) n''abantube nga bavunnamira enjuba nebava ku Katonda, era Sitane yabanyiririza emirimu gya bwe, olwo nno n''ebagya ku kkubo n''olwekyo bo tebagenda kulungama.');
INSERT INTO "african_dev" VALUES(27,25,'27:25','. Nebatavunnamira Katonda oyo agyayo ebyekwese mu ggulu omusanvu ne mu nsi era amanyi ebyo bye mukweka ne bye mwolesa.');
INSERT INTO "african_dev" VALUES(27,26,'27:26','. Katonda, tewali kisinzibwa mu butuufu okugyako yye, Mukama omulabirizi wa Arishi ey''ekitiibwa.');
INSERT INTO "african_dev" VALUES(27,27,'27:27','. (Sulaiman) naagamba nti tujja kulaba oyogedde mazima oba oli mu balimba.');
INSERT INTO "african_dev" VALUES(27,28,'27:28','. Twala ebbaluwa yange eno ogibawe, bwomala baweemu akabanga olabe ki kye baddamu.');
INSERT INTO "african_dev" VALUES(27,29,'27:29','. (Balikiisi) bwe yafuna ebbaluwa naagamba nti abange mmwe abakungu, mazima nze mpeereddwa ebbaluwa ey''ekitiibwa.');
INSERT INTO "african_dev" VALUES(27,30,'27:30','. Mazima yyo eva wa Sulaiman era esoma nti ku lw''elinnya lya Katonda omusaasizi ow''ekisa ekingi omusaasizi ow''ekisa eky''enjawulo.');
INSERT INTO "african_dev" VALUES(27,31,'27:31','. Temunneekuluntalizaako mujje gyendi nga mukkirizza okugondera Katonda.');
INSERT INTO "african_dev" VALUES(27,32,'27:32','. (Balikisi) naagamba nti abange mmwe abakungu mumpe ebirowoozo ku nsonga yange eno, sisobola kusalawo nsonga okutuusa lwe mujja gyendi (ne tukyogerako).');
INSERT INTO "african_dev" VALUES(27,33,'27:33','. Nebagamba nti: ffe tuli b''amaanyi ddala era ffe mu kulwana tuli bayitirivu, naye bbwo obuyinza buli gyoli, laba ki kyolagira.');
INSERT INTO "african_dev" VALUES(27,34,'27:34','. (Balikisi) naagamba nti: mazima abafuzi bwe bayingira e kitundu (nga bakiwangudde buwanguzi) bakyonoona ne bafuula abantu ab''ekitiibwa mu kyo abanyoomebwa era bwe batyo bwe bakola.');
INSERT INTO "african_dev" VALUES(27,35,'27:35','. Era mazima nze ngenda kubaweereza ebirabo olwo nno ninde bentumye badda na ki?.');
INSERT INTO "african_dev" VALUES(27,36,'27:36','. (Abaatumwa) bwe bajja ewa Sulaiman yagamba nti: munsendasenda ne mmaali (olwo nno mbaleke) ebyo Katonda bye yampa bye birungi okusinga bye yabawa, wabula mmwe musanyuukirira nnyo olw''ebirabo bya mmwe.');
INSERT INTO "african_dev" VALUES(27,37,'27:37','. (Sulaiman naagamba) ddayo gye bali (n''ebirabo bya bwe) tugenda kubajjira n''eggye lye batalinaako busobozi, era (ensi eyo) tujja kugibafulumyamu nga ba wansi era nga banyoomebwa.');
INSERT INTO "african_dev" VALUES(27,38,'27:38','. (Sulaiman) naagamba nti abange mmwe abakungu ani mu mmwe andeetera namulondo ye nga tebannaba kunzijira nga bakkirizza Katonda.');
INSERT INTO "african_dev" VALUES(27,39,'27:39','. Ejinni ery''amaanyi nerigamba nti, nze nja kugikuleetera nga tonnava mu kifokyo, era mazima nze ku ekyo ndi wa maanyi omwesigwa.');
INSERT INTO "african_dev" VALUES(27,40,'27:40','. (Omulala) oyo alina okumanya (okw''enjawulo) okuva mu kitabo kya (Katonda) naagamba nti, nze nja kugikuleetera nga eriisolyo terinnadda gyoli (nga tonnatemya), bwe yagiraba nga eteredde waali yagamba nti, kino kya mu birungi bya Mukama omulabirizi wange abe nga angezesa, abaffe nneebaza oba sseebaza byengera, era bulijjo omuntu eyeebaza yeebaza yebaza ku lulwe, ate oyo akaafuwala mazima Mukama omulabirizi wange talina kye yeetaaga, mugabi nnyo.');
INSERT INTO "african_dev" VALUES(27,41,'27:41','. (Sulaiman) naagamba nti mukyusekyuse nnamulondoye tulabe anaasobola okugimanya oba anaabeera mu batasobola kugimanya.');
INSERT INTO "african_dev" VALUES(27,42,'27:42','. (Balikiisi) bwe yajja naagambibwa nti: eyo ye namulondoyo? naagamba nti mazima eringa yeeyo, (Sulaiman) naagamba nti: twaweebwa okumanya (Katonda) oluberyeberye lw''omukyala ono nga tanajja era tubadde basiraamu.');
INSERT INTO "african_dev" VALUES(27,43,'27:43','. Ekyamugaana (omukyala ono okubeera omusiraamu bye) ebyo bye yali asinza nga alese Katonda ali omu, mazima yye (balikiisi) yali ava mu bantu abakaafiiri.');
INSERT INTO "african_dev" VALUES(27,44,'27:44','. Naagambibwa nti: yingira olubiri (amatiribona) olwo bwe yalaba olubiri naalulabanga amayengo g''amazzi naabikkula ku ntumbweze zombiriri (olw''okutya amazzi) (Sulaiman) naamugamba nti mazima lwo lubiri (olujja lwa lwo) lwakolebwa mu birawuli nga wansi w''ebirawuli waliyo amazzi, (Balikiisi) naagamba nti Mukama omulabirizi wange, mazima nze mbadde neeyisa bubi (kaakano) nsiramuse nga ngoberera Sulaiman ku lwa Mukama omulabirizi w''ebitonde.');
INSERT INTO "african_dev" VALUES(27,45,'27:45','. Era mazima twatuma eri abantu b''ekika kya Thamud muganda waabwe Swaleh nti musinze Katonda ali omu, ekyaddirira abantu abo kwe kubaamu ebibinja bibiri ebikaayagana, (ekya bakkiriza n''abatakkiriza).');
INSERT INTO "african_dev" VALUES(27,46,'27:46','. (Swaleh) naagamba nti abange mmwe abantu bange, lwaki musaba Katonda ekibi ng''ate temunnasaba kirungi, kale nno singa musaba Katonda okubasonyiwa mube nga musaasirwa.');
INSERT INTO "african_dev" VALUES(27,47,'27:47','. Nebagamba nti tujja kufuna ebizibu ku lulwo ne ku lw''abo abali naawe. (Swaleh) naagamba nti omugabo gwa mmwe mu birungi oba mu bizibu biri eri Katonda, wabula mmwe muli abantu abalabankanyizibwa.');
INSERT INTO "african_dev" VALUES(27,48,'27:48','. Mu kibuga kya bwe mwalimu abasajja mwenda nga boonoonyi mu nsi nga tebalongoosa.');
INSERT INTO "african_dev" VALUES(27,49,'27:49','. Nebagamba (bannaabwe) mulayire Katonda nti tujja kumuteega ekiro tumutte n''abantube. (bwe tunaamala okumutta) tujja kugambira ddala eri oyo yenna amulinako obuvunaanyizibwa nti: tetwabaddewo nga omuntu waabwe attibwa era mazima twogera mazima.');
INSERT INTO "african_dev" VALUES(27,50,'27:50','. Ne bakola e nkwe ez''okutta (Swaleh n''abantube) naffe ne tuzaanukula e nkwe ez''okumuwonya nabo nga tebamanyi.');
INSERT INTO "african_dev" VALUES(27,51,'27:51','. Kale laba enkomerero eyava mu nkwe zaabwe, mazima ffe twabazikiriza n''abantu baabwe bonna.');
INSERT INTO "african_dev" VALUES(27,52,'27:52','. Olwo nno amayumba gaabwe gaalekebwa ttayo olw''enneeyisa yaabwe embi, mazima mu ekyo mulimu e kyokuyiga eri abantu abamanyi.');
INSERT INTO "african_dev" VALUES(27,53,'27:53','. Twawonya abo abakkiriza ate nga baali batya Katonda.');
INSERT INTO "african_dev" VALUES(27,54,'27:54','. Era (jjukira omubaka waffe) Luutu bwe yagamba abantube nti: mukola ekikolwa eky''obuwemu nga mulabira ddala!.');
INSERT INTO "african_dev" VALUES(27,55,'27:55','. Mazima mmwe obwagazi bwa mmwe mubukola ku basajja ne mulekawo abakazi, wabula mmwe muli abantu abakola ebikolwa eby''obubuyabuya.');
INSERT INTO "african_dev" VALUES(27,56,'27:56','. Tekwali okwanukula kw''abantube okugyako okugamba obugambi nti, muwanganguse abantu abali ne Luutu nga mu baggya mu kitundu kya mmwe anti mazima bbo bantu abeetwala okuba abatukuvu.');
INSERT INTO "african_dev" VALUES(27,57,'27:57','. Olwo nno ne tumuwonya n''abantube okugyako mukyalawe twamugerera (twamulamulira okubeera) mu b''okuzikirira.');
INSERT INTO "african_dev" VALUES(27,58,'27:58','. Netubafukirira e nkuba, n''eba nkuba mbi nnyo (eyafukirirwa) abantu abaalabubwa.');
INSERT INTO "african_dev" VALUES(27,59,'27:59','. (Gamba (ggwe Nabbi Muhammad) nti okutenderezebwa okujjuvu kwa Katonda era nti emirembe gibeere ku baddube abo beyawa enkizo (ey''okutuusa obubaka bwe eri abantu), abaffe Katonda omutonzi y''asinga obulungi oba ebyo bye bamugattako!.');
INSERT INTO "african_dev" VALUES(27,60,'27:60','. Abaffe ani yatonda eggulu n''ensi, era n''abatonyeseza okuva waggulu amazzi, ne tumeza nago amalimiro agalabika obulungi, tekibangako mu mikono gyamwe okumeza emiti gyago. Ate oluvanyuma lw''ekyo wabaawo watya ekisinzibwa kyonna nga kigattibwa ku Katonda!, wabula (ekibakozesa ekyo) lwa kuba nti bo bantu abenkanya Katonda n''ebintu ebirala.');
INSERT INTO "african_dev" VALUES(27,61,'27:61','. Abaffe ani oyo eyassaawo ensi nga nteefu naagissaamu e migga, naagiwa ensozi (nga nkondo) n''assa wakati w''ennyanja e bbiri (ey''omunnyu ne etali) ekyayawula (ate oluvanyuma lw''ekyo) wasobola okubaawo ekisinzibwa kyonna nga kigattibwa ku Katonda! wabula (ekibakozesa ekyo) lwakuba nti abasinga obungi mu bo tebamanyi.');
INSERT INTO "african_dev" VALUES(27,62,'27:62','. Abaffe ani oyo ayanukula ali mu buzibu bwaba amusabye, era naggyawo akabi, era naabafuula abasigire mu nsi (ate oluvanyuma lw''ekyo) wasobola okubaawo ekisinzibwa kyonna nga kigattibwa ku Katonda! mujjukira kitono.');
INSERT INTO "african_dev" VALUES(27,63,'27:63','. Abaffe ani oyo abalaga ekkubo mu bizikiza by''olukalu ne nnyanja era ani oyo asindika e mpewo nga zireeta essanyu (e nkuba) nga liva mu mikono gy''okusaasirakwe (ate oluvanyuma lw''ekyo) wasobola okubaawo ekisinzibwa kyonna nga kigattibwa ku Katonda!, Katonda musukkulumu nnyo ku ebyo bye bamugattako.');
INSERT INTO "african_dev" VALUES(27,64,'27:64','. Abaffe ani oyo atandikawo e bitonde ate nga agenda kubizzaawo (oluvanyuma lw''okufa kwabyo) era ani abagabirira (mmwe) okuva waggulu ne ku nsi (ate oluvanyuma lw''ekyo) wasobola okubaawo ekisinzibwa kyonna nga kigattibwa ku Katonda! bagambe nti muleete obujulizi bwa mmwe bwe muba nga muli ba mazima.');
INSERT INTO "african_dev" VALUES(27,65,'27:65','. Bagambe nti abali mu ggulu omusanvu n''ensi tewali amanyi byekusifu okugyako Katonda, (era n''olwekyo) tebamanyi ddi lwe balizuukizibwa.');
INSERT INTO "african_dev" VALUES(27,66,'27:66','. Wabula (ekibuuzo kiri nti) okumanya kwa bwe kwamalayo byonna okutuusa ku lunaku lw''enkomerero (olwo nno babe nga bakubuuza okuzuukira kulibaawo ddi) wabula (ekituufu kiri nti) bo ku bikwata ku lunaku lw''enkomerero bali mu kubuusabuusa, wabula (si ekyo kyokka) bo bamuzibe ku bikwata ku lunaku olwo.');
INSERT INTO "african_dev" VALUES(27,67,'27:67','. Abo abaakaafuwala bagamba nti bwe tulimala okufuuka ettaka ne bakadde baffe ate tulijjibwayo.');
INSERT INTO "african_dev" VALUES(27,68,'27:68','. Mazima ffe ne bakadde baffe okuva edda kino kizze kitulagaanyisibwa (nga tewali kibaawo, n''olwekyo) bino tebiri okugyako nfumo z''abaakulembera.');
INSERT INTO "african_dev" VALUES(27,69,'27:69','. Bagambe (ggwe Muhammad) nti mutambule mu nsi mulabe ngeriki enkomerero ya boonoonyi bwe yali.');
INSERT INTO "african_dev" VALUES(27,70,'27:70','. Tonakuwala ku lwabwe era towulira bubi olw''enkwe ze basala.');
INSERT INTO "african_dev" VALUES(27,71,'27:71','. Era bagamba nti endagaano eyo erituuka ddi bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(27,72,'27:72','. Bagambe nti, si kulwa nga musemberedde ebimu ku ebyo bye musaba okubatuukako mu bwangu.');
INSERT INTO "african_dev" VALUES(27,73,'27:73','. Era mazima Mukama omulabiriziwo ajjudde ebirungi byagabira abantu, naye abasinga obungi mu bo tebeebaza.');
INSERT INTO "african_dev" VALUES(27,74,'27:74','. Era mazima Mukama omulabiriziwo amanyi ebyo ebifuba bya bwe bye bikweka n''ebyo bye bakola mu lwatu.');
INSERT INTO "african_dev" VALUES(27,75,'27:75','. Era tewali kyekusifu kyonna mu ggulu ne munsi wabula nga nakyo Kiri mu kitabo ekiraga buli kimu.');
INSERT INTO "african_dev" VALUES(27,76,'27:76','. Mazima Kur’ani eno etegeeza abaana ba Israil ebisinga obungi ebyo bye baawukanamu.');
INSERT INTO "african_dev" VALUES(27,77,'27:77','. Era mazima yyo bulungamu na kusaasira eri abakkiriza.');
INSERT INTO "african_dev" VALUES(27,78,'27:78','. Era Mukama omulabiriziwo alitaawulula wakati wa bwe nga abalamula, era yye ye nantakubwa ku mukono omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(27,79,'27:79','. N''olwekyo (ggwe Muhammad) weekwate ku Katonda, mazima ggwe oli ku mazima ameeyolefu.');
INSERT INTO "african_dev" VALUES(27,80,'27:80','. (Awamu n''ekyo) mazima ggwe tosobola kuwuliza bafu era nga bwotasobola kuwuliza ba kiggala okukoowoola, kasita bakyuka nebakubayo amabega.');
INSERT INTO "african_dev" VALUES(27,81,'27:81','. Era ggwe tosobola kulungamya ba muzibe noobaggya ku bubuze bwa bwe, tosobola kuwuliza okugyako abo abakkiriza ebigambo byaffe era abo be beewaayo ewa Katonda.');
INSERT INTO "african_dev" VALUES(27,82,'27:82','. Ekigambo bwe kiribagwaako tugenda kubaggyirayo ensolo okuva mu ttaka eyogere nabo, anti mazima abantu baali tebakakasa bigambo byaffe.');
INSERT INTO "african_dev" VALUES(27,83,'27:83','. Era babuulire olunaku lwe tulikungaanya mu buli kibiina kya bantu akabinja mu abo abalimbisa ebigambo byaffe, nabo nno nga bawalulwa mu miteeko.');
INSERT INTO "african_dev" VALUES(27,84,'27:84','. Okutuusa nga bazze (mu maaso ga Katonda) aligamba nti: mwalimbisa ebigambo byange so ng''ate temwabimanya mu bujjuvu bwabyo, kaakati mwakoleranga kuki?.');
INSERT INTO "african_dev" VALUES(27,85,'27:85','. Olwo ekigambo (eky''okubonerezebwa) ne kibakakataako (olw''ebyo bye beeyisaamu obubi ne kiba nti tebagenda kwatula.');
INSERT INTO "african_dev" VALUES(27,86,'27:86','. Abaffe tebalaba nti mazima ffe twateekawo ekiro babe nga bakiwummuliramu, era (ne tuteekawo) obudde obw''emisana nga butangaala (babe nga bakoleramu emirimu egibayimirizaawo) mazima mu ekyo mulimu o bubonero eri abantu abakkiriza.');
INSERT INTO "african_dev" VALUES(27,87,'27:87','. Era (babuulire) olunaku engombe lwe lifuuyibwa, alitya oyo yenna ali mu ggulu omusanvu n''oyo ali mu nsi okugyako oyo Katonda gwaliba ayagadde, na bonna bagenda kujja nga banyoomwa.');
INSERT INTO "african_dev" VALUES(27,88,'27:88','. Oliraba ensozi ze waalirowoozezza nti ziyimiridde ziriba zidduka ng''okudduka kw''ebire, okwo kwe kukola kwa Katonda oyo eyakola buli kintu mu nkola e singa okuba eya waggulu, mazima yye amanyidde ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(27,89,'27:89','. Oyo yenna alijja n''ekirungi agenda kuweebwa ekirungi ekikisingako, era bbo baliba mirembe ku ntiisa y''olunaku olwo.');
INSERT INTO "african_dev" VALUES(27,90,'27:90','. N''oyo alijja n''ekibi ebyenyi byabwe bigenda kuvumbikibwa mu muliro, abaffe mulisasulwa okugyako ebyo bye mwali mukola.');
INSERT INTO "african_dev" VALUES(27,91,'27:91','. Gamba (ggwe Nabbi Muhammad) nti mazima nalagirwa okuba nga nsinza Mukama omulabirizi w''ekibuga kino (Makkah) oyo eyakifuula e ky''emizizo era buli kintu kyonna kikye era nnalagirwa okubeera mu beewa ewa Katonda.');
INSERT INTO "african_dev" VALUES(27,92,'27:92','. (Era nnalagirwa) okuba nga nsoma Kur’ani, kale nno oyo yenna alungama alungama ku lulwe, ate oyo abula, bagambe nti mazima nze ndi wa mu batiisa.');
INSERT INTO "african_dev" VALUES(27,93,'27:93','. Era gamba nti buli kitendo kyonna kya Mukama Katonda ajja kubalaga obubonerobwe muli bumanya (ng''ate tebukyabagasa) Mukama omulabiriziwo tabangako atafaayo ku ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(28,1,'28:1','. Twaa Siin Miim.');
INSERT INTO "african_dev" VALUES(28,2,'28:2','. Bino bigambo bya kitabo ekinnyonnyola.');
INSERT INTO "african_dev" VALUES(28,3,'28:3','. Tukusomera mu bwesimbu ebigambo bya Musa ne Firaawo (bibe bya mugaso) eri abantu abakkiriza.');
INSERT INTO "african_dev" VALUES(28,4,'28:4','. Mazima Firaawo yeekuluntariza mu nsi, abantu baamu naabateekamu obubinjabinja, nga abonyaabonya ekibinja mu bo nga asala (natta) abaana baabwe abalenzi naalekawo abawala mu bo (mu kukola ekyo) naabeera mu boonoonyi.');
INSERT INTO "african_dev" VALUES(28,5,'28:5','. Era twayagala okubunduggula ebyengera ku abo abaayisibwa obubi ku nsi era tubafuule abakulembeze era tubafuule abasikira (ebyengera bali bye baaleka).');
INSERT INTO "african_dev" VALUES(28,6,'28:6','. Era tubawe obuyinza mu nsi (era nga tukozesa bo) tulage Firaawo ne Hamana n''amagye gaabwe bombi ekyo kye baali beekeka.');
INSERT INTO "african_dev" VALUES(28,7,'28:7','. Era twatumira Maama wa Musa nti muyonse, naye bwoba nga omutiiriridde musuule mu mazzi era totya, era tonakuwala mazima ffe tujja ku mukuddiza era tujja kumuteeka mu ba baka.');
INSERT INTO "african_dev" VALUES(28,8,'28:8','. Abantu ba Firaawo ne bamulonda (naye nga) ajja kubafuukira omulabe era eky''okunakuwala. Mazima Firaawo ne Hamaana n''amagye gaabwe bombi baali basobya.');
INSERT INTO "african_dev" VALUES(28,9,'28:9','. Muka Firaawo naagamba nti (omwana ono) wa kusanyusa amaaso gange naawe, temumutta ayinza okutugasa oba ne tumufuula omwana, (naye) nga nabo tebamanyi (Musa kyagenda kubeera).');
INSERT INTO "african_dev" VALUES(28,10,'28:10','. Omutima gwa Maama wa Musa gwali gwagaana okutereera (nga yeeraliikirira) yabula kata okumulaga (nga bwe yali omwanawe) singa tetwassa bunywevu ku mutimagwe (olwo nno) alyoke abe mu bakkiriza.');
INSERT INTO "african_dev" VALUES(28,11,'28:11','. (Maama wa Musa) yagamba mwannyina (wa Musa) nti mulondoole, awo nno naamulaba nga ali wala naye nga bo tebamanyi.');
INSERT INTO "african_dev" VALUES(28,12,'28:12','. Twamulemesa okukkiriza amabeere (g''omuyonsa yenna) okuva ku ntandikwa, (Mwannyina) naabagamba nti: abaffe mbalagirire abantu b''enyumba emu abanaamubalerera era nga nabo bajja kumubuulirira.');
INSERT INTO "african_dev" VALUES(28,13,'28:13','. Olwo nno twamuzza ewa Nnyina amaasoge gabe nga gatebenkera era aleme kunakuwala, era asobole okumanya nti mazima endagaano ya Katonda ya mazima wabula abasinga obungi mu bo tebamanyi.');
INSERT INTO "african_dev" VALUES(28,14,'28:14','. (Musa) bwe yakula n''asajjakula twamuwa okusengeka ensonga n''okumanya. Era bwe tutyo bwe tusasula abalongoosa.');
INSERT INTO "african_dev" VALUES(28,15,'28:15','. (Olunaku lumu Musa) yayingira e kibuga ng''abantu baamu tebategedde naasangamu abasajja babiri nga balwanagana, nga omu wa mu bantube ate omulala wa mu balabebe (wa mu bantu ba Firaawo) ow''omu bantube naamusaba amutaase ku mulabewe, awo nno Musa naamukuba ekikonde naamutta mbagirawo, (Musa) naagamba nti kino kikolwa kya Sitane, mazima yye mulabe era mubuza ow''olwatu.');
INSERT INTO "african_dev" VALUES(28,16,'28:16','. (Musa) naagamba ayi Mukama omulabirizi wange mazima nze neeyisizza bubi nsaba onsonyiwe, (Katonda) naamusonyiwa, anti mazima yye ye musonyiyi omusaasizi ennyo.');
INSERT INTO "african_dev" VALUES(28,17,'28:17','. Naagamba nti ayi Mukama omulabirizi wange okusinziira ku byengera byongonnomoddeko sigenda kubeera muyambi wa boonoonyi.');
INSERT INTO "african_dev" VALUES(28,18,'28:18','. Okuva olwo Musa yabeera mu kibuga ng''atya nga yeekengera. Yali akyali awo nga eyamusabye jjo okumutaasa ate amukubira enduulu amudduukirire, Musa naamugamba nti: mazima ggwe mulumbaganyi ow''olwatu.');
INSERT INTO "african_dev" VALUES(28,19,'28:19','. Olwo nno (Musa) bwe yayagala okuvumbagira oyo omulabe waabwe bombi, naagamba nti owange Musa oyagala kunzita nga bwe wasse omuntu jjo, tolina kyoyagala okugyako okubeera nantagambwaako mu nsi era (olabika) toyagala kubeera wa mu bakozi ba bulungi.');
INSERT INTO "african_dev" VALUES(28,20,'28:20','. Awo nno omusajja kwe kujja nga adduka ng''ava e kibuga gye kikoma, naagamba nti owange Musa mazima abakungu bakuteesaako okukutta, kale fuluma (mu nsi eno) mazima nze ndi mu bakubuulirira.');
INSERT INTO "african_dev" VALUES(28,21,'28:21','. Awo nno naakifuluma (ekibuga) nga atidde yeekengera, naagamba nti ayi Mukama omulabirizi wange mponya abantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(28,22,'28:22','. Bwe yamala okwolekera oludda lw''e Madiyana yagamba nti nsaba Mukama omulabirizi wange annungamye ku kkubo ettuufu.');
INSERT INTO "african_dev" VALUES(28,23,'28:23','. Bwe yatuuka ku mazzi g''eMadiyana yasangawo ekibinja ky''abantu nga banywesa (ensolo zaabwe) era nga waliwo ku (bbali) abakazi babiri, nga bagaana (ensolo zaabwe okunywa amazzi) naabagamba nti ate mmwe ogubadde gwe gwaki? Nebagamba nti tetunywesa okutuusa abalunzi abalala lwe baggyawo ensolo zaabwe, ate nga ne kitaffe mukadde nnyo.');
INSERT INTO "african_dev" VALUES(28,24,'28:24','. Naabanyweseza, bwe yamala ne yeewogoma mu kisiikirize olwo naagamba nti ayi Mukama omulabirizi wange mazima nze ndi mwetaavu nnyo eri ekirungi kyonna kyoba ompadde.');
INSERT INTO "african_dev" VALUES(28,25,'28:25','. (Aba ali awo) omu ku bombi (abakazi) najja gyali mu ngeri ey''ensonyi, naagamba nti mazima Kitange akuyita abe nga akusasula empeera y''okutunyweseza, (Musa) bwe yatuuka waali (Kitaawe wa bawala) era naamunyumiza emboozi (y''ebimufaako) yamugamba nti totya omaze okuwona abantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(28,26,'28:26','. (Omuwala) omu naagamba nti owange Taata muwe omulimu mazima omupakasi asinga obulungi ye wa maanyi omwesigwa');
INSERT INTO "african_dev" VALUES(28,27,'28:27','. (Kitaawe wa Bawala) naagamba nti mazima nze njagala kukufumbiza omu ku bawala bange abo ababiri nga ojja kunkolera emyaka munaana naye bw''ojjuza e kkumi ekyo kiri gyoli, wabula saagala ku kukaluubiriza ojja kunsanga nga ndi mu bantu abakozi b''obulungi.');
INSERT INTO "african_dev" VALUES(28,28,'28:28','. (Musa) naagamba nti ekyo kiri wakati wange naawe ekimu ku biseera ebibiri kyennaaba nkoze njakuba sivunaanwa, era Katonda ye mweyimirize ku byonna bye twogera.');
INSERT INTO "african_dev" VALUES(28,29,'28:29','. Musa bwe yamala okutuukiriza ekiseera (kye baalagaana), yatambula n''abantube (nga bagenda e misiri) naalaba omuliro ku ludda lw''olusozi, naagamba abantube nti: mulindeeko wano katono, mazima nze nina wendabye omuliro, si kulwa nga mbaleetera amawulire okuva we guli oba nembaleetera e kitawuliro mulyoke mwote.');
INSERT INTO "african_dev" VALUES(28,30,'28:30','. Musa bwe yatuuka weguli, yakoowoolwa (nga eddoboozi) liva ku lubalama lwe''kisenyi olwa ddyo mu kifo eky''omukisa nga liva mu muti (nga limugamba) nti owange ggwe Musa, mazima nze Katonda Omulabirizi w''ebitonde.');
INSERT INTO "african_dev" VALUES(28,31,'28:31','. Era (Katonda naamugamba nti) kasuka omuggogwo (naye kwe kugukasuka) olwo nno bwe yagulaba nga gwenyeenya, nga gulinga omusota omwangu mu ntambula yaagwo, yakyuka nga adda emabega era n''atatunula gyava (Katonda kwe kumukoowoola nti) owange ggwe Musa komawo era totya anti mazima oli mu balina e mirembe.');
INSERT INTO "african_dev" VALUES(28,32,'28:32','. Yingiza omukono gwo mu kimeeme ky''ekyambalokyo gujja kufuluma nga mweru ng''ate tewali kabi kagutuuseeko, ssa omukonogwo ku kifubakyo. Obwo nno bwombiriri bwe bubonero okuva eri Katondawo bwotwalira Firaawo n''abakungube, anti mazima okuva emabega babadde bantu boonoonyi.');
INSERT INTO "african_dev" VALUES(28,33,'28:33','. (Musa) naagamba nti ayi Mukama omulabirizi wange mazima nze nabattamu omuntu, n''olwekyo ntya si kulwa nga banzita.');
INSERT INTO "african_dev" VALUES(28,34,'28:34','. Ate Muganda wange Haruna y''ansinga olulimi olwatufu, kale nno mutume wamu nange nga muyambi abe nga akkaatiriza amazima gange, anti mazima nze ntya okuba nga bajja kunnimbisa.');
INSERT INTO "african_dev" VALUES(28,35,'28:35','. Katonda naamugamba nti tujja kunyweeza omukonogwo (kukuwagira) ne mugandawo, era tubawe mwembiriri obusobozi babe nga tebasobola kubatuusaako kabi. olw''obubonero bwaffe, mmwe mwembiriri nabuli yenna abagoberera mmwe mujja okuwangula.');
INSERT INTO "african_dev" VALUES(28,36,'28:36','. Musa bwe yabajjira n''obubonero bwaffe nga bunnyonnyofu, baagamba nti kino tekiri okugyako okuba e ddogo e jjingirire, era tetuwuliranga ku kintu nga kino mu bakadde baffe abaasooka');
INSERT INTO "african_dev" VALUES(28,37,'28:37','. Musa naagamba nti Mukama omulabirizi wange y''asinga okumanya oyo azze n''obulungamu ng''abuggya gyali era n''oyo aliba n''enkomerero e nnungi, anti bulijjo abeeyisa obubi tebayinza kutuuka ku buwanguzi.');
INSERT INTO "african_dev" VALUES(28,38,'28:38','. Ate Firaawo naagamba nti: abange mmwe abakungu ssibamanyiddeeyo Katonda mulala atali nze, ggwe Haamaan nkumira omuliro oyokye amataffaali onzimbire omulongooti nsobole okulaba Katonda wa Musa, era mazima nze mulowooleza ddala okuba nga ali mu balimba.');
INSERT INTO "african_dev" VALUES(28,39,'28:39','. Olwo nno ne yeekuza mu nsi yye n''eggyerye, awatali nsonga yonna (kwe basinziira) ne balowooza nti mazima tebagenda kuzzibwa gye tuli.');
INSERT INTO "african_dev" VALUES(28,40,'28:40','. Netumukwata n''eggyerye ne tubanyugunya mu nnyanja, kale tunuulira olabe enkomerero ya beeyisa obubi bwe yali.');
INSERT INTO "african_dev" VALUES(28,41,'28:41','. Ne tubafuula abakulembeze nga bakoowoola abantu okuyingira omuliro, era nga ne ku lunaku lw''enkomerero tebagenda kutaasibwa.');
INSERT INTO "african_dev" VALUES(28,42,'28:42','. Era twabagobereza e kikolimo mu nsi muno nga ne ku lunaku lw''enkomerero bbo be balifuulibwa abalabika obubi.');
INSERT INTO "african_dev" VALUES(28,43,'28:43','. Mazima twawa Musa e kitabo bwe twamala okuzikiriza emirembe egyasooka nga mumuli eri abantu, era nga bulungamu era kusaasira kibayambe okwebuulirira.');
INSERT INTO "african_dev" VALUES(28,44,'28:44','. Era (ggwe Muhammad) tobangako ku ludda olw''ebugwanjuba bwe twawa Musa ekigambo (ekiragiro eky''okugenda ewa Firaawo) era tewali mu baaliwo (mu kiseera ekyo).');
INSERT INTO "african_dev" VALUES(28,45,'28:45','. Naye mazima ffe twasibulawo e mirembe ebbanga neriyitawo ddene (era nga ggwe tonnatumwa) era tobangako mutuuze mu bantu b''eMadiyana n''oba nga obasomera ebigambo byaffe naye mazima ffe bulijjo tubadde tutuma ababaka.');
INSERT INTO "african_dev" VALUES(28,46,'28:46','. Era tewaliiwo ku ludda lw''olusozi mu kiseera we twakoowolera (Musa tumuwe obubaka), wabula kwali kusaasira kwa Mukama omulabiriziwo (ggwe okumanya ebyaaliyo), obe nga weekesa abantu abatajjirwanga mutiisa yenna nga ggwe tonnajja olwo nno babe nga beebuulirira.');
INSERT INTO "african_dev" VALUES(28,47,'28:47','. (Era tetwandikutumye gye bali singa) si bantu kutuukibwako buzibu olw''ebyo e mikono gya bwe bye gyakola ate nebagamba nti ayi Mukama omulabirizi waffe singa watutumira omubaka ne tugoberera ebigambobyo era ne tubeera mu bakkiriza.');
INSERT INTO "african_dev" VALUES(28,48,'28:48','. Naye ate amazima bwe gaamala okubajjira okuva gye tuli baagamba nti singa aweereddwa nga ebyaweebwa Musa (naye) abaffe tebaawakanya ebyo ebyaweebwa Musa oluberyeberye ne bagamba nti (ebigambo bya Musa ne Muhammad) byombi ddogo, erimu liyamba linnaalyo era ne bagamba nti mazima ffe tuwakanya buli kyonna ekigwa mu kiti ky''ebyo.');
INSERT INTO "african_dev" VALUES(28,49,'28:49','. Bagambe (ggwe Muhammad) nti (bwe kiba bwe kityo) kale (mmwe) muleete ekitabo okuva ewa Katonda nga kyo kirungamu okusinga biri ebibiri nja kukigoberera bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(28,50,'28:50','. Bwe batakwanukula manya nti mazima bagoberera kwagala kwa bwe, era ani mubuze okusinga oyo agoberera okwagalakwe okutaliiko kulungamya kuva wa Katonda, mazima Katonda talungamya bantu beeyisa bubi.');
INSERT INTO "african_dev" VALUES(28,51,'28:51','. Mazima ekigambo tukibalambululidde kibayambe okwebuulirira.');
INSERT INTO "african_dev" VALUES(28,52,'28:52','. Abo betwawa ekitabo oluberyeberye lwa kino abamu (mu) bo bagikkiriza (Kur''ani).');
INSERT INTO "african_dev" VALUES(28,53,'28:53','. Bweba ebasomeddwa bagamba nti tugikkirizza, ddala yo g''emazima agava ewa Mukama omulabirizi waffe, mazima ddala ffe nga tenajja twali basiraamu.');
INSERT INTO "african_dev" VALUES(28,54,'28:54','. Abo bagenda kuweebwa empeera yaabwe emirundi ebiri olw''ebyo bye baagumiikiriza era n''olwokuba nti ekibi bakiggyisaawo ekirungi era nga bawaayo ku ebyo bye tubagabirira.');
INSERT INTO "african_dev" VALUES(28,55,'28:55','. Era bwe bawulira eby''olubalaato babyesamba era ne bagamba nti tulina enkola yaffe nammwe mulina enkola yammwe, emirembe gibeere ku mmwe, tetwagala batategeera.');
INSERT INTO "african_dev" VALUES(28,56,'28:56','. Mazima ggwe (Muhammad) tolungamya oyo gwoba oyagadde naye mazima Katonda alungamya oyo gwaba asazeewo era y''asinga okumanya abalungamu.');
INSERT INTO "african_dev" VALUES(28,57,'28:57','. Era (abatakkiriza) ne bagamba nti singa tugoberera naawe obulungamu tujja kusikulwa mu nsi yaffe (naye) abaffe tetwabatebenkereza Haram (Makkah) nga ejjudde e mirembe nga ebibala ebya buli kika bireetebwa gyeri, nga okwo kugabirira okuva gye tuli naye wabula abasinga obungi mu bo tebamanyi.');
INSERT INTO "african_dev" VALUES(28,58,'28:58','. Bitundu bimeka bye twazikiriza ebyayonoona obulamu bwa byo (olw''okufuula obulamu obw''okweyagaliramu obweyagazi) amayumba gaabwe gaago, tegaddangayo kusulwamu oluvanyuma lwa bwe okugyako matono era ffe twafuuka abaagasikira.');
INSERT INTO "african_dev" VALUES(28,59,'28:59','. Mukama Omulabiriziwo tabangako wa kuzikiriza bitundu okugyako nga atumye omubaka mu makkati gaabyo ng''abasomera ebigambo byaffe era tetubangako baakuzikiriza bitundu okugyako abantu ba mu nga beeyisa bubi.');
INSERT INTO "african_dev" VALUES(28,60,'28:60','. Buli kintu kye muweebwa bya kweyagala bya bulamu bwa nsi na kwewunda kw''ayo. So ng''ate ebiri ewa Katonda bye bisinga obulungi era bye by''okusigalawo. Abaffe temutegeera!.');
INSERT INTO "african_dev" VALUES(28,61,'28:61','. Abaffe oyo gwe twalagaanyisa endagaano ennungi era nga w''akugituukako ayinza okuba nga oyo gwe twawa eby''okweyagala by''obulamu bw''ensi, ate nga yye ku lunaku lw''enkomerero agenda kubeera mu balireetebwa okubonerezebwa.');
INSERT INTO "african_dev" VALUES(28,62,'28:62','. Era bategeeze olunaku (Katonda) lwali bakoowoola naagamba nti baliwa be mwangattangako.');
INSERT INTO "african_dev" VALUES(28,63,'28:63','. Abo abaliba bakakatiddwako ekigambo (eky''okubonerezebwa nga gubasinze) baligamba nti ayi Mukama omulabirizi waffe, abo bbo betwabuza twababuza nga bwe twabula, tubeesamudde netwemalira ku ggwe. Baali tebasinza ffe.');
INSERT INTO "african_dev" VALUES(28,64,'28:64','. Baligambibwa nti: muyite bannammwe (be mwangattangako), balibayita nebatabanukula era baliraba ebibonerezo (ne bagamba nti) singa mazima bbo baali baalungama.');
INSERT INTO "african_dev" VALUES(28,65,'28:65','. Era (bajjukize) olunaku (Katonda) lwalibakoowoola naagamba nti: kiki kye mwayanukula ababaka.');
INSERT INTO "african_dev" VALUES(28,66,'28:66','. Ku lunaku olwo ebigambo biribakalira ku matama, nga era bbo tebagenda kwewuunaganya.');
INSERT INTO "african_dev" VALUES(28,67,'28:67','. Naye oyo eyeenenya, nakkiriza naakola e mirimu e mirungi, ddala oyo y''alibeera mu bawanguzi.');
INSERT INTO "african_dev" VALUES(28,68,'28:68','. Mukama Omulabiriziwo atonda kyaba ayagadde era y''asalawo, (abantu) tebabangako na kwesalirawo, yasukkuluma Katonda era ali wala nnyo ku ebyo bye bamugattako.');
INSERT INTO "african_dev" VALUES(28,69,'28:69','. Era Mukama Omulabiriziwo amanyi ebyo ebifuba bye bikwese n''ebyo bye bassa mu lwatu.');
INSERT INTO "african_dev" VALUES(28,70,'28:70','. Era yye Katonda tewali kisinzibwa kyonna mu butuufu okugyako yye okutenderezebwa ku kwe, ku ntandikwa ne ku nkomerero era obuyinza bwonna bubwe era gyali gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(28,71,'28:71','. Bagambe nti mulaba mutya singa Katonda abassaako ekiro ne kiba kyalubeerera okutuusa ku lunaku lw''enkomerero, ani asinzibwa atali Katonda ayinza okubaleetera ekitangaala, abaffe temuwulira!.');
INSERT INTO "african_dev" VALUES(28,72,'28:72','. Bagambe nti mulaba mutya singa Katonda abassaako obudde bw''emisana nga bwa lubeerera okutuusa ku lunaku lw''enkomerero, ani asinzibwa atali Katonda ayinza okubaleetera ekiro kye muwummuliramu, abaffe temulaba!.');
INSERT INTO "african_dev" VALUES(28,73,'28:73','. Ebimu ku bibalibwa mu kusaasirakwe, kwe kuba nti yabateerawo ekiro n''emisana muwummuliremu era mube nga munoonya ebigabwabye era kibayambe okwebaza.');
INSERT INTO "african_dev" VALUES(28,74,'28:74','. Era (bategeeze) olunaku (Katonda) lwalibakoowoola naagamba nti baliwa abo bemwangattangako bemwagambanga.');
INSERT INTO "african_dev" VALUES(28,75,'28:75','. Era tuliggya mu buli kibiina omujulizi ne tugamba nti muleete abajulizi ba mmwe, awo nno balimanya nti ddala obutuufu bwa Katonda, era bigenda kubabulako ebyo bye baagunjanga.');
INSERT INTO "african_dev" VALUES(28,76,'28:76','. Mazima Karuna yali wa mu bantu ba Musa naabeewaggulako, ate nga twamuwa amawanika nga ddala e bisumuluzo byago bizitoowerera e kibinja eky''amaanyi. Jjukira abantube bwe baamugamba nti tosanyuukirira mazima Katonda tayagala basanyuukirira bitaliimu.');
INSERT INTO "african_dev" VALUES(28,77,'28:77','. Noonya e nyumba ey''enkomerero nga okozesa ebyo Katonda bye yakuwa, era teweerabiranga omugabogwo mu nsi, kolera (abalala) obulungi nga Katonda bwa kukolera obulungi, toluubiriranga okwonoona mu nsi. Mazima Katonda tayagala boonoonyi.');
INSERT INTO "african_dev" VALUES(28,78,'28:78','. (Karuna) naagamba mazima (bye nnina) nnabiweebwa lwa kumanya kwe nnina. Abaffe teyamanya nti ddala Katonda yazikiriza oluberyeberyerwe e mirembe gy''abo abaali bamusinga amaanyi era nga baakungaanya bingi okumusinga. Era aboonoonyi tebasabibwa (kunnyonnyola bikwata ku) byonoono bya bwe (anti Katonda abimanyi).');
INSERT INTO "african_dev" VALUES(28,79,'28:79','. (Olwo nno Karuna) yafuluma (naagenda) awali abantube nga ali mu byambalobye ebirungi ennyo, abo abaagala obulamu bw''ensi ne bagamba nti singa naffe tulina ng''ebyo ebyaweebwa Karuna mazima yye wa mukisa munene.');
INSERT INTO "african_dev" VALUES(28,80,'28:80','. Abo abaaweebwa okumanya ne bagamba nti nga mulabye empeera za Katonda z''ezisinga obulungi eri oyo akkiriza n''akola emirimu emirungi (kyokka ekyo) tekiweebwa okugyako abagumiikiriza.');
INSERT INTO "african_dev" VALUES(28,81,'28:81','. Olwo nno netulagira ettaka nelimumira n''enyumba ye teyalina kabinja k''abantu baamutaasa awatali Katonda era teyali wa mu bayinza kwetaasa.');
INSERT INTO "african_dev" VALUES(28,82,'28:82','. Abo abeegomba e mbeeraye olunaku lwa jjo, baatuuka okugamba nti weewaawo ddala Katonda ayanjuluza riziki kwoyo gwaba ayagadde mu baddube era nga bwasobola okugifunza. Singa Katonda teyatwagaliza kirungi, naffe yaaliragidde ettaka neritumira yye abaffe tokiraba nti abakaafiiri tebayinza kutuuka ku buwanguzi.');
INSERT INTO "african_dev" VALUES(28,83,'28:83','. Eyo e nyumba ey''enkomerero (e jjana) tugiteerawo abo abatayagala kwesukkulumya mu nsi wadde okwonoona, era enkomerero ennungi y''abatya Katonda.');
INSERT INTO "african_dev" VALUES(28,84,'28:84','. Oyo yenna akola ekirungi alisasulwa e kirungi e kikisinga, wabula oyo akola ekibi, abo abakola ebibi tebagenda kusasulwa okugyako ebyo bye baakola.');
INSERT INTO "african_dev" VALUES(28,85,'28:85','. Mazima oyo eyakukasaako Kur’ani agenda kukuddiza ddala eri obuddo obulagaanye, gamba nti Mukama omulabirizi wange y''asinga okumanya oyo aleese obulungamu, era n''oyo ali mu bubuze obw''olwatu.');
INSERT INTO "african_dev" VALUES(28,86,'28:86','. Era wali tosuubira ekitabo okuba nga kikuweebwa, (ekyo tekyali) okugyako okusaasira okuva ewa Mukama omulabiriziwo n''olwekyo tobeeranga omuyambi wa bakaafiiri.');
INSERT INTO "african_dev" VALUES(28,87,'28:87','. Era tewabangawo akuggya ku bigambo bya Katonda oluvanyuma lw''okuba nti byassibwa gyoli, era koowoola (abantu) okujja ewa Mukama omulabiriziwo era tobeeranga mu bagatta ebintu ebirala ku Katonda.');
INSERT INTO "african_dev" VALUES(28,88,'28:88','. Era tosabanga awamu ne Katonda ekisinzibwa ekirala kyonna, tewali kisinzibwa mu butuufu okugyako yye, buli kintu kyakuggwawo okugyako yye, okusalawo kwonna kukwe era gyali gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(29,1,'29:1','. Alif Laam Miim.');
INSERT INTO "african_dev" VALUES(29,2,'29:2','. Abaffe abantu basuubira okulekebwa babe nga bagamba nti tukkirizza nga nabo tebagezesebwa.');
INSERT INTO "african_dev" VALUES(29,3,'29:3','. Mazima twagezesa abo abaabakulembera, Katonda ajja kumanyira ddala abo ab''esimbu (mu bukkiriza bwa bwe) era ajja kumanyira ddala abalimba.');
INSERT INTO "african_dev" VALUES(29,4,'29:4','. Oba abo abakola ebibi basuubira nti bayinza okutulemesa (ne tutababonereza), balamula bubi.');
INSERT INTO "african_dev" VALUES(29,5,'29:5','. Oyo yenna asuubira okusisinkana Katonda mazima e kiseera kya Katonda (okumusisinkana) kijja, era yye ye muwulizi ennyo omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(29,6,'29:6','. Oyo yenna alafuubana (okuweereza Katonda) mazima yeelafuubanira ku lulwe mazima Katonda talina kye yeetaaga ku bitonde.');
INSERT INTO "african_dev" VALUES(29,7,'29:7','. Ate abo abakkiriza nebakola emirimu emirongoofu ddala tugenda kubasangulako ebibi bya bwe, era tujja kubasasulira ddala ebirungi ebisinga bye baali bakola.');
INSERT INTO "african_dev" VALUES(29,8,'29:8','. Era twalaamira omuntu okuyisa obulungi bakaddebe bombi, naye bwe bakuwaliriza obe nga ongattako ekyo kyotalinaako kumanya kwonna tobagonderanga, gyendi y''eri obuddo bwa mmwe olwo nno mbategeeze ebyo bye mwakolanga.');
INSERT INTO "african_dev" VALUES(29,9,'29:9','. Era abo abakkiriza ne bakola e mirimu e mirungi tujja kubayingiririza ddala mu balongoofu.');
INSERT INTO "african_dev" VALUES(29,10,'29:10','. Mu bantu mulimu agamba nti nzikirizza Katonda naye bwayisibwa obubi ku lwa Katonda afuula okunyigiriza kwa bantu nga e bibonerezo bya Katonda, wabula singa okutaasa kwa Mukama omulabiriziwo kuba kuzze olwo nno bagambira ddala nti tubadde wamu nammwe, abaffe Katonda si y''asinga okumanya ebyo ebiri mu bifuba bye bitonde!.');
INSERT INTO "african_dev" VALUES(29,11,'29:11','. Katonda ajja kumanyira ddala abo abakkiriza era nga bwajja okumanyira ddala abannanfusi.');
INSERT INTO "african_dev" VALUES(29,12,'29:12','. Era abo abaakaafuwala bagamba abo abakkiriza nti mugondere enkola yaffe olwo nno naffe twetikke ebibi bya mmwe so nga tebali bo ba kwetikka kintu kyonna mu bibi bya bwe, mazima bbo balimba.');
INSERT INTO "african_dev" VALUES(29,13,'29:13','. Bagenda kwetikka emigugu gyabwe n''emigugu (emirala) awamu n''emigugu gyabwe, era ddala bagenda kubuuzibwa ku lunaku lw''enkomerero ku ebyo bye baatemereranga.');
INSERT INTO "african_dev" VALUES(29,14,'29:14','. Mazima twatuma Nuuhu eri abantube naamala mu bo emyaka lukumi okugyako emyaka ataano, olwo nno amataba negabakwata nga nabo bali mu kweyisa bubi.');
INSERT INTO "african_dev" VALUES(29,15,'29:15','. Netumuwonya n''abantu abaali mu lyato ne tukifuula kya kuyiga eri abantu.');
INSERT INTO "african_dev" VALUES(29,16,'29:16','. Era (babuulire) ekyafaayo kya Ibrahim bwe yagamba abantube nti musinze Katonda era mumutye ekyo kye kirungi gye muli bwe muba nga mumanyi.');
INSERT INTO "african_dev" VALUES(29,17,'29:17','. Mazima musinza amasanamu ne muleka Katonda ne mussaawo obulimba, mazima abo be musinza ne muleka Katonda tebafuga riziki yammwe n''olwekyo ewa Katonda gye muba munoonya riziki, mumusinze era mumwebaze gyali gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(29,18,'29:18','. Bwe munaalimbisa, mazima ebibiina bingi byalimbisa oluberyeberye lwa mmwe, ate omubaka tavunaanyizibwa okugyako okutuusa (obubaka) okweyolefu.');
INSERT INTO "african_dev" VALUES(29,19,'29:19','. Abaffe tebalaba engeri Katonda gy''atandikamu ebitonde (ne bivaawo) oluvanyuma n''abizzaawo mazima ekyo kyangu nnyo ku Katonda.');
INSERT INTO "african_dev" VALUES(29,20,'29:20','. Bagambe (ggwe Muhammad) nti mutambule mu nsi mulabe engeri gye yatandika ebitonde ate Katonda agenda kusibula olusibula olw''enkomerero, mazima Katonda muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(29,21,'29:21','. Abonereza oyo gwaba ayagadde era n''asaasira oyo gwaba ayagadde era gyali gye mulikyusibwa okudda.');
INSERT INTO "african_dev" VALUES(29,22,'29:22','. Ate mmwe ku nsi ne mu ggulu temusobola kulemesa (Katonda) ate nga oggyeeko Katonda temusobola kufuna mukuumi wadde omutaasa.');
INSERT INTO "african_dev" VALUES(29,23,'29:23','. Abo abawakanya ebigambo bya Katonda n''okumusisinkana abo baakutuka n''okusuubira okusaasira kwange era abo balina ebibonerezo e biruma ennyo.');
INSERT INTO "african_dev" VALUES(29,24,'29:24','. Okwanukula kwa bantube tekwali okugyako okugamba nti: mumutte oba mumwokye awo nno Katonda naamuwonya omuliro, mazima mu ekyo mulimu obubonero obulaga obuyinza bwa Katonda eri abantu abakkiriza.');
INSERT INTO "african_dev" VALUES(29,25,'29:25','. (Ibrahim) naabagamba nti mulese Katonda ne mweteerawo ebifaananyi olw''enkolagana eri wakati wa mmwe mu bulamu bw''ensi, oluvanyuma ku lunaku lw''enkomerero abamu bagenda kwegaana bannaabwe era abamu bakolimire bannaabwe, nga n''obuddo bwa mmwe muliro, temuliba na bataasa (babadduukirira).');
INSERT INTO "african_dev" VALUES(29,26,'29:26','. Olwo nno Luutu naamukkiriza era naagamba nti mazima nze ngenda kusenguka nzire ewa Mukama omulabirizi wange, mazima yye ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(29,27,'29:27','. Era (Ibrahim) ne tumugabira Ishaaka ne Yakub era netussa mu zzadderye obwa Nabbi n''ebitabo (Taurat, Zaburi, Injiiri, Kur’ani) era twamuwa empeeraye ku nsi era mazima yye ku nkomerero wa mu bakozi ba bulungi.');
INSERT INTO "african_dev" VALUES(29,28,'29:28','. Era (bajjukize) Luutu bwe yagamba abantube nti mazima mmwe mukola ekivve awatabanga muntu n''omu yakibasookako.');
INSERT INTO "african_dev" VALUES(29,29,'29:29','. Mazima mmwe mudda ku basajja mu kifo ky''abakyala (ne mulya ebisiyaga) ne mukutula amakubo (ku bantu nga mubateega), ne mukola e bitasaana mu bifo bya mmwe wemukunganira. Okwanukula kw''abantube tekwali okugyako okugamba nti tuleetere ebibonerezo bya Katonda bwoba nga oli mu boogera amazima.');
INSERT INTO "african_dev" VALUES(29,30,'29:30','. (Luutu) naagamba nti ayi Mukama omulabirizi wange ntaasa ku bantu abOonoonyi.');
INSERT INTO "african_dev" VALUES(29,31,'29:31','. Ababaka baffe (ba Malayika) bwe bajja ewa Ibrahim nga bamuleetedde amawulire ag''essanyu (ag''okuzaala omwana Ishaka) baagamba nti mazima ffe (bwe tuva wano) tugenda kuzikiriza abantu b''omu kitundu kiri mazima abantu baamu babadde beeyisa bubi.');
INSERT INTO "african_dev" VALUES(29,32,'29:32','. (Ibrahim) naagamba nti mazima mu kyo Luutu mwabeera, ne bagamba nti ffe tusinga okumanya abaliyo, ddala tujja kumuwonya n''abantube okugyako mukyalawe ali mu abo ab''okusigala.');
INSERT INTO "african_dev" VALUES(29,33,'29:33','. Ababaka baffe bwe batuuka ewa Luutu yanakuwala ku lwab bwe, era naawulira nga talina maanyi ga kubataasa, ne bamugamba nti totya era tonakuwala, mazima ffe tujja kukuwonya n''abantubo okugyako mukyalawo yye wa mu abo abookusigala.');
INSERT INTO "african_dev" VALUES(29,34,'29:34','. Mazima ffe tugenda kussa ku bantu b''ekitundu kino e bibonerezo okuva mu ggulu olw''ebyo bye baali boonoona.');
INSERT INTO "african_dev" VALUES(29,35,'29:35','. Era mazima olw''ekikolwa ekyo twaleka a kabonero ak''enkukunala eri abantu abategeera.');
INSERT INTO "african_dev" VALUES(29,36,'29:36','. Era (jjukira) bwe twatumira abantu be Madiyana Muganda waabwe Swaibu naagamba nti abange abantu bange musinze Katonda, era mususubire (okusasulwa ku) lunaku lw''enkomerero, temukola ebikyamu mu nsi ne muba aboonoonyi.');
INSERT INTO "african_dev" VALUES(29,37,'29:37','. Baamulimbisa olwo nno olubwatuka olw''amanyi ne lubatuukako ne bafuuka emirambo egigangalamye mu mayumba gaabwe.');
INSERT INTO "african_dev" VALUES(29,38,'29:38','. Era (Jjukira) ebyatuuka ku bantu b''ekika kya A’di ne Thamud, mazima ebibakwatako bimanyiddwa gye muli okusinziira ku mayumba gaabwe nga bwe galabika kati, era Sitane yabanyiririza emirimu gya bwe emibi, olwo nno neebaggya ku kkubo so nga ate baali balaba (olwa ba Nabbi be baali bafunye).');
INSERT INTO "african_dev" VALUES(29,39,'29:39','. Era jjukira Karuna ne Firaawo ne Haamaan, mazima Musa yabajjira n''obunnyonnyofu naye ne beekuluntaliza mu nsi era tebaali ba kusimattuka.');
INSERT INTO "african_dev" VALUES(29,40,'29:40','. Buli omu twamuvunaana olw''ekibikye, mu bo mulimu betwasindikira empewo eya mayinja, ate mu bo mulimu abaatuukwako okubwatuka, era mu bo mulimu betwamiza ettaka era mu bo mulimu be twazikiriza mu mazzi naye Katonda tabangako waakubalyazaamaanya wabula bennyini be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(29,41,'29:41','. Ekifaananyi ky''abo abeeteerawo abataasa ne baleka Katonda, balinga nabbubi eyeezimbira e nyumba ate ng''enyumba esinga obunafu y’enyumba ya nabbubi singa baali bamanyi.');
INSERT INTO "african_dev" VALUES(29,42,'29:42','. Mazima Katonda amanyi nti bye basaba ne bamulekawo tebiriimu kantu, era yye ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(29,43,'29:43','. Era ebyo ebifaananyi tubikubira abantu naye tebabitegeera okugyako abamanyi.');
INSERT INTO "african_dev" VALUES(29,44,'29:44','. Katonda yatonda eggulu omusanvu, n''ensi mu ngeri entuufu, mazima mu ekyo mulimu eky''okuyiga eri abakkiriza.');
INSERT INTO "african_dev" VALUES(29,45,'29:45','. Soma ebyo ebitumiddwa gyoli ebiri mu kitabo (Kur’ani) era yimirizaawo e sswala, mazima e sswala erobera okukola eby''obuwemu n''empisa embi, era okutendereza Katonda kye kisinga obukulu, era Katonda amanyi bye mukola.');
INSERT INTO "african_dev" VALUES(29,46,'29:46','. Era temuwakananga ne ba nannyini Kitabo okugyako nga mukozesa amakubo agasinga obulungi, okugyako ku abo abeeyisa obubi mu bo era mugambe nti (ffe) tukkirizza ekyo ekyassibwa gye tuli ne kyassibwa gye muli, nga Katonda waffe era Katonda wa mmwe ali omu, nga naffe fenna yye yekka gwe tweewa.');
INSERT INTO "african_dev" VALUES(29,47,'29:47','. Era bwe tutyo twassa gyoli ekitabo (Kur’ani) abo betwawa ekitabo (nga tetunnakuwa Kur’ani) bagikkiriza ne mu abo (Abawarabu be Makkah), mulimu abo abagikkiriza era tewali awakanya bigambo byeffe (ebiri mu Kur’ani) okugyako abakafiiri.');
INSERT INTO "african_dev" VALUES(29,48,'29:48','. (Ebimu ku bya magerobyo) kwe kuba nti ggwe tosomangako kitabo kyonna oluberyeberye nga Kur’ani tennakuweebwa wadde okuwandiika n''omukonogwo ogwaddyo, singa si bwe kyali ddala aboonoonyi baali buusizzabuusizza.');
INSERT INTO "african_dev" VALUES(29,49,'29:49','. Wabula (ekituufu kiri nti) Kur’ani bigambo bya (Katonda) ebinnyonnyofu, eri mu bifuba byaabo abaaweebwa okumanya tewali awakanya bigambo byaffe (ebiri mu Kur’ani) okugyako abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(29,50,'29:50','. (Abagatta ku Katonda e bintu e birala) bagamba nti singa (Muhammad) aweereddwa obubonero okuva ewa Mukama omulabiriziwe, bagambe nti mazima obubonero bwa Katonda, naye mazima nze ndi mutiisa ow''olwatu.');
INSERT INTO "african_dev" VALUES(29,51,'29:51','. Abaffe tekibamala okuba nti twakuwa ekitabo ekibasomerwa, nga mulimu okusaasira n''okubuulirira eri abantu abakkiriza.');
INSERT INTO "african_dev" VALUES(29,52,'29:52','. Bagambe nti kimala wakati wange na mmwe okubeera nga Katonda ye mujulizi (akakasa nti ndi mubakawe), Katonda y''amanyi ebiri mu ggulu omusanvu n''ensi, naye abo abakkiriza ebitali bituufu ne bawakanya Katonda abo nno be b''okufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(29,53,'29:53','. Bakusaba (mu ngeri y''olujereegerero) obanguyize ebibonerezo, singa tewaaliwo kiseera kigere bibonerezo byandibajjidde (awo we baalibadde babisabidde) era ddala e bibonerezo bya kubajjira kibwatukira nga nabo tebategedde.');
INSERT INTO "african_dev" VALUES(29,54,'29:54','. Bakusaba obanguyirize e bibonerezo (naye mazima tebamanyi) nti mazima omuliro Jahannama ddala gulitaayiza abakaafiiri.');
INSERT INTO "african_dev" VALUES(29,55,'29:55','. Ku lunaku e bibonerezo lwe biribatuukako nga biva waggulu ne wansi w''ebigere bya bwe, era Katonda abagambe nti mukombe ku bukaawu bw''ebyo bye mwali mukola.');
INSERT INTO "african_dev" VALUES(29,56,'29:56','. Abange mmwe abaddu bange abakkiriza (bwe muba nga muli mu kunyigirizibwa) mazima ensi yange ngazi (musenguke munsinzize awalala), era nze nzekka nze gwe muba musinza.');
INSERT INTO "african_dev" VALUES(29,57,'29:57','. Buli mwoyo gwonna gwa gukomba ku kufa, oluvanyuma gye tuli gye mujja okuzzibwa.');
INSERT INTO "african_dev" VALUES(29,58,'29:58','. Era abo abakkiriza ne bakola emirimu emirongoofu tujja kubawa ebisenge eby''awaggulu mu jjana nga emigga gikulukutira wansi waabyo, baakubeera mu byo olubeerera (ebyo) birungi nnyo okuba nga y''empeera ya bakola obulungi.');
INSERT INTO "african_dev" VALUES(29,59,'29:59','. Abo abagumiikiriza era nga beesiga Katonda waabwe.');
INSERT INTO "african_dev" VALUES(29,60,'29:60','. Bimeka ebiramu ebitambula nga tebisobola kwetikka riziki yaabyo nga Katonda yekka y''abigabirira nammwe (abantu y''abgabirira) era nga yye (Katonda) ye muwulizi omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(29,61,'29:61','. Abakaafiiri bwoba nga obabuuzizza nti ani eyatonda eggulu omusanvu n''ensi n''agonza e njuba n''omwezi? bajja kukugambira ddala nti Katonda, ate olwo bakyusibwa batya okuva ku mazima!.');
INSERT INTO "african_dev" VALUES(29,62,'29:62','. Katonda agaziya ebyenfuna eri omuntu gwaba ayagadde mu baddube ate naafundiza oyo gwaba (ayagadde), mazima Katonda ku buli kintu mumanyi nnyo.');
INSERT INTO "african_dev" VALUES(29,63,'29:63','. Era bwobabuuza nti ani assa amazzi okuva waggulu n''alamusa nago ensi oluvanyuma lw''okufa kwayo? ddala bajja kugamba nti Katonda, gamba nti okutenderezebwa kwa Katonda wabula abasinga obungi mu bo tebategeera.');
INSERT INTO "african_dev" VALUES(29,64,'29:64','. Obulamu buno obw''ensi tebuli okugyako okuba eby''amasanyu era eby''omuzannyo era mazima e nyumba ey''enkomerero yo bwe bulamu obwa nnamaddala singa baali bamanyi.');
INSERT INTO "african_dev" VALUES(29,65,'29:65','. Bwe baba basaabalidde mu maato basaba Katonda, ng''ekyo bakikola ku lulwe yekka, naye bwabawonya naabatuusa ku lukalu, olwo ate ne bamugattako ebintu ebirala.');
INSERT INTO "african_dev" VALUES(29,66,'29:66','. Kale baleke beewakane bye twabawa era beeyagale, lumu balimanya.');
INSERT INTO "african_dev" VALUES(29,67,'29:67','. Abaffe tebalaba nti mazima ffe twafuula e kibuga (Makkah) eky''emizizo, e kitundu e ky''emirembe, naye ng''abantu basikulwa mu bifo ebibeetoolodde. Abaffe ebikyamu bye bakkiriza ate ne bawakanya ebyengera bya Katonda!.');
INSERT INTO "african_dev" VALUES(29,68,'29:68','. Era ani mubi okusinga oyo atemerera obulimba ku Katonda oba naalimbisa amazima bwe gaba gamujjidde abaffe mu jahannama temuli kifo kya bakaafiiri!.');
INSERT INTO "african_dev" VALUES(29,69,'29:69','. N''abo abalafuubana mu kutuweereza, ddala tujja kubalungamya ku makubo gaffe, era mazima Katonda ali wamu n''abalongoosa.');
INSERT INTO "african_dev" VALUES(30,1,'30:1','. Alif Laam Miim.');
INSERT INTO "african_dev" VALUES(30,2,'30:2','. Abaroma bawanguddwa.');
INSERT INTO "african_dev" VALUES(30,3,'30:3','. Mu nsi ey''okumpi (eriraanye Shami) ate nabo oluvanyuma lw''okuwangulwa kwa bwe, bajja kuwangula.');
INSERT INTO "african_dev" VALUES(30,4,'30:4','. Mu myaka mitono (egitasukka mwenda). Okusalawo kwa Katonda oluberyeberye n''oluvanyuma, olunaku olwo abakkiriza bagenda kukyakyankya.');
INSERT INTO "african_dev" VALUES(30,5,'30:5','. Ku lw''okutaasa kwa Katonda (Katonda) ataasa oyo gwaba ayagadde, anti bulijjo ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(30,6,'30:6','. Eyo ndagaano ya Katonda, Katonda tayawukana ku ndagaanoye, naye abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(30,7,'30:7','. (Anti) bamanyi bya kungulu ku bikwata ku bulamu bw''ensi, era nga nabo ku bikwata ku nkomerero tebalina kye bamanyi.');
INSERT INTO "african_dev" VALUES(30,8,'30:8','. Abaffe tebafumiitiriza mu myooyo gyabwe (ne bakiraba nti) Katonda teyatonda ggulu musanvu n''ensi n''ebiri wakati wa byombi, wabula mu mazima era nga birina e kiseera ekigere naye mazima bangi mu bantu bawakanya okusisinkana Mukama omulabirizi waabwe.');
INSERT INTO "african_dev" VALUES(30,9,'30:9','. Abaffe tebatambulangako mu nsi nebalaba yali etya enkomerero y''abo abaabakulembera! (ate) nga baali babasinga amaanyi era baayiikuula ensi era ne bagizimba okusinga nga bano bwe baagizimba era ababaka baabwe baabaleetera obunnyonnyofu, n''olwekyo Katonda teyali wa kubayisa bubi wabula bo bennyini be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(30,10,'30:10','. Oluvanyuma enkomerero y''abo abaakola ebivve yali nti baalimbisa e bigambo bya Katonda era baali babijeeja.');
INSERT INTO "african_dev" VALUES(30,11,'30:11','. Katonda y''atandika e bitonde oluvanyuma agenda kubizzaawo ate oluvanyuma gyali gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(30,12,'30:12','. Era olunaku e ssaawa ey''enkomerero lwe lituuka, aboonoonyi bagenda kukutuka n''okusuubira.');
INSERT INTO "african_dev" VALUES(30,13,'30:13','. Era tebagenda kufuna mu bannaabwe bawolereza, olwo nno balyegaana bannaabwe.');
INSERT INTO "african_dev" VALUES(30,14,'30:14','. Era olunaku e ssaawa ey''enkomerero lwe lituuka kw''olwo bagenda kwawukana.');
INSERT INTO "african_dev" VALUES(30,15,'30:15','. Bo abakkiriza ne bakola e mirimu emirungi, abo nno baakubeera mu bifo ebyeyagaza nga basanyusibwa.');
INSERT INTO "african_dev" VALUES(30,16,'30:16','. Ate abo abaakaafuwala ne balimbisa ebigambo byaffe ne balimbisa n''ensisinkano y’olunaku lw''enkomerero, abo bagenda kuteekebwa mu bibonerezo.');
INSERT INTO "african_dev" VALUES(30,17,'30:17','. N''olwekyo asukkulumizibwe Katonda mu kiseera wemubeerera mu biseera eby''olweggulo ne wemubeerera mu biseera eby''okumakya.');
INSERT INTO "african_dev" VALUES(30,18,'30:18','. Era okutenderezebwa ku kwe mu ggulu omusanvu n''ensi ne mu kiseera eky''akawungeezi ne wemubeerera mu ttuntu.');
INSERT INTO "african_dev" VALUES(30,19,'30:19','. (Era Katonda) aggya e kiramu mu kifu era naggya e kifu mu kiramu, era y''awa ensi obulamu oluvanyuma lw''okufa kwayo era bwe mutyo bwe muliggyibwa mu ntaana.');
INSERT INTO "african_dev" VALUES(30,20,'30:20','. Era mu bubonerobwe (obulaga obuyinzabwe), kwe kuba nti yabatonda nga abaggya mu ttaka oluvanyuma mmwe mwabeera abantu abasaasaanira e nsi.');
INSERT INTO "african_dev" VALUES(30,21,'30:21','. Era mu bubonerobwe kwe kuba nti yabatondera be mufumbiriganwa nabo nga abaggya mu mmwe, mube nga musobola okubeera nabo, era yassa wakati wa mmwe omukwano n''okusaasira , mazima ebyo mulimu obubonero eri abantu abafumiitiriza.');
INSERT INTO "african_dev" VALUES(30,22,'30:22','. Era mu bubonerobwe : okutonda eggulu omusanvu n''ensi n''okwawukana kw''ennimi zammwe n''amabala gammwe mazima mu ebyo mulimu obubonero eri abamanyi.');
INSERT INTO "african_dev" VALUES(30,23,'30:23','. Era mu bubonerobwe : okwebaka kwa mmwe ekiro n''emisana n''okunoonya kwa mmwe ebigabwabye mazima ebyo birimu obubonero eri abantu abawulira.');
INSERT INTO "african_dev" VALUES(30,24,'30:24','. Era mu bubonerobwe : kwe kuba nti abalaga ekimyanso ky''eggulu nga mukitya ate nga mululunkanira (enkuba) era assa okuva waggulu amazzi n''awa nago ettaka obulamu oluvanyuma lw''okufa kwalyo. Mazima mu ebyo mulimu obubonero eri abantu abategeera.');
INSERT INTO "african_dev" VALUES(30,25,'30:25','. Era mu bubonerobwe : eggulu n''ensi okubeerawo ku kiragirokye, oluvanyuma bwali bakoowoola olukoowoola nti (muve) mu ttaka, oligenda okulaba nga muvaayo');
INSERT INTO "african_dev" VALUES(30,26,'30:26','. Era bibye byonna ebiri mu ggulu omusanvu ne mu nsi byonna bigondera yye (yekka).');
INSERT INTO "african_dev" VALUES(30,27,'30:27','. Era yye yooyo atandikawo ebitonde era nga yaalibizzaawo (oluvanyuma lw''okufa kwabyo) nakyo (eky''okubizzaawo) kye kisinga obwangu ku ye, era kikye yekka ekifo ekisukkulumu mu ggulu omusanvu n''ensi era yye ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(30,28,'30:28','. (Katonda) yabakubira ekifaananyi nga kikwata ku mmwe mwe nnyini nga agamba nti abaffe mwebyo bye tubagabira mulina ababeegattako mwabo emikono gyamwe bye gifuga (abaddu ba mmwe) ne muba nga mwenna mwenkana mwekyo ne mutuuka okubatya nga bwe mwetya mwennyini, bulijjo bw tunnyonnyola ebigambo eri abantu abategeera.');
INSERT INTO "african_dev" VALUES(30,29,'30:29','. Wabula abo abeeyisa obubi bagoberera kwagala kwabwe awatali kusinziira ku kumanya kwonna y''ate ani ayinza okulungamya oyo Katonda gwe yabuza era abo tebagenda kufuna babataasa.');
INSERT INTO "african_dev" VALUES(30,30,'30:30','. Kale nno yolekeza ekyenyikyo eri eddiini ng''oli mwesimbu. (Olw''okuba eddiini eyo) y''enkola ya Katonda gye yatonderako abantu, tewali kukyusa mu ntonda ya Katonda, eyo ye ddiini ennambulukufu, naye abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(30,31,'30:31','. (N''olwekyo) mubeere abeemenyera Katonda era mumutye, era muyimirizeewo e sswala, era temubeeranga mu bagatta ku Katonda ebintu ebirala.');
INSERT INTO "african_dev" VALUES(30,32,'30:32','. Mu abo abaatemaatema e ddiini yaabwe ne baba bibiina nga buli kibinja kikyakyankya n''ebyo bye kirina.');
INSERT INTO "african_dev" VALUES(30,33,'30:33','. Obuzibu bwe buba butuuse ku bantu basaba Mukama omulabirizi waabwe nga bamwemenyera, oluvanyuma bwabaloza ku kusaasira okuva gyali ogenda okulaba nga ekitundu ku bo bagatta ebintu ebirala ku Mukama omulabirizi waabwe.');
INSERT INTO "african_dev" VALUES(30,34,'30:34','. Kale beegaane ebyo bye twabawa era mweyagale mmwe (nabo) lumu mulimanya.');
INSERT INTO "african_dev" VALUES(30,35,'30:35','. Abaffe oba twabawa obujulizi nga bwogera ku ebyo bye bagatta ku Katonda (nti bituufu!).');
INSERT INTO "african_dev" VALUES(30,36,'30:36','. Bwe tuba nga tulozezza abantu ku kusaasira kwaffe basanyuka nakwo, bwe baba nga batuukiddwako ekibi kyonna olw''ebyo e mikono gya bwe bye gyakola ogenda okulaba nga bakutuka n''okusuubira (okusaasira kwa) Katonda.');
INSERT INTO "african_dev" VALUES(30,37,'30:37','. Abaffe tebalaba nti mazima Katonda agaziyiza eby''enfuna eri gwaba ayagadde ate naafundiza oyo gwaba ayagadde, mazima mu ekyo mulimu obubonero obulaga obuyinza bwa Katonda eri abantu abakkiriza.');
INSERT INTO "african_dev" VALUES(30,38,'30:38','. Kale wa ow''olugandalwo olw''okumpi kyateekwa okuweebwa, n''omufuna mpola, n''omutambuze ekyo kye kirungi eri abo abaagala ebiri eri Katonda era abo bbo be bookutuuka ku kigendererwa.');
INSERT INTO "african_dev" VALUES(30,39,'30:39','. Ekyo kyonna kye muba muwaddeyo (nga muwola) mu mmaali ey''ennyongeza (Riba) nti zigende zizaalire mu mmaali y''abantu tezizaala ewa Katonda, ate ekyo kye muba muwaddeyo mu Zzaka nga mwagala ebiri eri Katonda abo nno be basaanira ennyongeza.');
INSERT INTO "african_dev" VALUES(30,40,'30:40','. Katonda yooyo eyabatonda bwamala naabagabirira bwamala naabatta, oluvanyuma agenda kubawa obulamu, abaffe mu bannammwe be mugatta ku Katonda waliwo akola ku ebyo ekintu kyonna? musukkulumu Katonda era yayawukana ku ebyo bye bamugattako.');
INSERT INTO "african_dev" VALUES(30,41,'30:41','. Obwonoonefu bweyolese ku lukalu ne ku nnyanja olw''ebyo emikono gy''abantu bye gikola, (Katonda) alyoke abakombese ku bukaawu bw''ebimu ku ebyo bye baakola, balyoke beekomeko.');
INSERT INTO "african_dev" VALUES(30,42,'30:42','. Gamba ( ggwe Muhammad) mutambule mu nsi mulabe yali etya e nkomerero y''abo abaaliwo oluberyeberye. abasinga obungi mu bo baali bagatta ku Katonda ebintu ebirala.');
INSERT INTO "african_dev" VALUES(30,43,'30:43','. Kale nno yolekeza ekyenyikyo eri e ddiini ennambulukufu, nga terunnajja olunaku olutagenda kuggyibwa ku Katonda neruzzibwayo, ku lunaku olwo abantu bagenda kubeera mu bibinja.');
INSERT INTO "african_dev" VALUES(30,44,'30:44','. Omuntu awakanya, ekibi ky''okuwakanyakwe kiri ku ye, n''oyo yenna akola emirimu emirongoofu (abo) baba bateekerateekera myoyo gya bwe.');
INSERT INTO "african_dev" VALUES(30,45,'30:45','. Katonda alyoke asasule mu bigabwabye abo abakkiriza ne bakola emirimu emirongoofu, anti mazima yye tayagala bakaafiiri.');
INSERT INTO "african_dev" VALUES(30,46,'30:46','. Era mu bubonerobwe kwe kuba nti atambuza empewo nga zireeta amawulire ag''essanyu (enkuba), era alyoke abaloze ku kusaasirakwe, era amaato galyoke gadduke ku lw''ekiragiro kye, era mube nga munoonya ebigabwa bye era mulyoke mwebaze (Katonda ku lw''ebyo).');
INSERT INTO "african_dev" VALUES(30,47,'30:47','. Era mazima twatuma oluberyeberye lwo ababaka eri abantu baabwe ne babaleetera obunnyonnyofu (ne bakaafuwala) olwo ne tuzikiriza abo abaajeema, era kyali kitukakatako okutaasa abakkiriza.');
INSERT INTO "african_dev" VALUES(30,48,'30:48','. Katonda yooyo atambuza empewo, nezifuukuula ebire naabyanjuluza mu bbanga nga bwaba ayagadde, naabifuula ebitundutundu, olwo nno n''olaba namutikkwa w''enkuba nga bivaamu, (Katonda) bwagituusa ku abo baaba ayagadde mu baddube ogenda okulaba nga bo basanyuka.');
INSERT INTO "african_dev" VALUES(30,49,'30:49','. Newaakubadde nga tennaba kubatonnyesebwa oluberyeberye lw''ekyo baali bakutuse n''okusuubira.');
INSERT INTO "african_dev" VALUES(30,50,'30:50','. Tunuulira olabe ebiva mu kusaasira kwa Katonda engeri gyawa ettaka obulamu oluvanyuma lw''okufa kwalyo. Mazima oyo ajja kulamusiza ddala abafu era yye bulijjo muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(30,51,'30:51','. Wabula singa tuba tutambuzizza empewo ne babiraba (e birime) nga bifuuse bya kyenvu olw''okuwotoka, bandibadde olw''ekyo beegaana ebyengera bya (Katonda).');
INSERT INTO "african_dev" VALUES(30,52,'30:52','. (N''olwekyo ggwe tobafaako) anti mazima ggwe tosobola kuwuliza bafu era nga bwotasobola kuwuliza bakiggala kasita bakyuka ne bakukuba amabega.');
INSERT INTO "african_dev" VALUES(30,53,'30:53','. Era toli asobola kulungamya bamuzibe n''obaggya ku bubuze bwabwe, tosobola kuwuliza okugyako abo abakkiriza ebigambo byaffe nebaba nga beewadde Katonda.');
INSERT INTO "african_dev" VALUES(30,54,'30:54','. Katonda yooyo eyatonda mmwe okutandikira mu bunafu, ate oluvanyuma lw''obunafu yabawa amaanyi, ate oluvanyuma lw''amaanyi yassaawo obunafu n''obukadde. Atonda kyaba ayagadde era yye ye mumanyi ennyo omuyinza.');
INSERT INTO "african_dev" VALUES(30,55,'30:55','. Olunaku lw''enkomerero lwe lulituuka aboonoonyi balilayira nti (Ku nsi) tebaatuulako okugyako kaseera katono, bwe batyo nno bwe baali bawugulwa.');
INSERT INTO "african_dev" VALUES(30,56,'30:56','. Ate abo abaaweebwa okumanya n''obukkiriza baligamba nti: mazima mwatuula (ku nsi) okumala ebbanga Katonda lye yabawandiikira, okutuuka ku lunaku lw''okuzuukira, kaakano luno lwe lunaku lw''okuzuukira (lutuuse) naye mazima mwali temumanyi.');
INSERT INTO "african_dev" VALUES(30,57,'30:57','. Ku lunaku olwo abo abeeyisa obubi okwetonda kwa bwe tekulibagasa era tebagenda kukubirizibwa kwenenya.');
INSERT INTO "african_dev" VALUES(30,58,'30:58','. Mazima tukubidde abantu mu Kur’ani eno buli kika kya kifaananyi, naye singa obaleetedde akabonero konna, ddala abo abakaafuwala bajja kugamba nti temuli mmwe okugyako okuba aboonoonyi.');
INSERT INTO "african_dev" VALUES(30,59,'30:59','. Bwatyo Katonda bwazibikira emitima gy''abo abatamanyi.');
INSERT INTO "african_dev" VALUES(30,60,'30:60','. Kale gumiikiriza mazima endagaano ya Katonda ya mazima, era tebakutiisanga abo abatakakasa kintu kyonna kikwata ku Katonda.');
INSERT INTO "african_dev" VALUES(31,1,'31:1','. Alif Laam Miim');
INSERT INTO "african_dev" VALUES(31,2,'31:2','. Ebyo bigambo eby''ekitabo ekijjudde amagezi.');
INSERT INTO "african_dev" VALUES(31,3,'31:3','. Nga bulungamu era kusaasira eri abalongoosa.');
INSERT INTO "african_dev" VALUES(31,4,'31:4','. Abo abayimirizaawo e sswala ne batoola Zakka, nabo nga bakakasa olunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(31,5,'31:5','. Abo bali ku bulungamu okuva ewa Mukama omulabirizi waabwe era abo bbo be b''okwesiima.');
INSERT INTO "african_dev" VALUES(31,6,'31:6','. Mu bantu mulimu agula emboozi ezitaliimu makulu, olwo nno abeere nga abuza (abantu) n''abaggya ku kkubo lya Katonda awatali kusinziira ku kumanya kwonna, era nga ebigambo (bya Katonda) abifuula bya lusaago, abo nno balina e bibonerezo ebinyoomesa.');
INSERT INTO "african_dev" VALUES(31,7,'31:7','. Ebigambo byaffe bwe biba bimusomeddwa akyuka nga yeekuza, n''aba nga atabiwulidde amatuuge negaba nga agalimu envumbo, kale musanyuse n''ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(31,8,'31:8','. Mazima abo abakkiriza ne bakola emirimu emirungi balina e jjana ezijjudde e byengera.');
INSERT INTO "african_dev" VALUES(31,9,'31:9','. Baakutuula muzo obugenderevu, nga eyo ye ndagaano ya Katonda ey''amazima, era yye ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(31,10,'31:10','. Yatonda eggulu omusanvu awatali mpagi zemulaba, ate nassa mu nsi ensozi ennywevu ereme kubayuuza, ate n''asaasaanya mu yo, buli kika kya kitambula, era netussa okuva waggulu amazzi ne tumeza mu yo mu buli mitindo ebiri ebiri (ebimera) eby''omugaso omusuffu.');
INSERT INTO "african_dev" VALUES(31,11,'31:11','. Okwo kwe kutonda kwa Katonda, kale mundage abo abatali yye bye batonda. Wabula (ekituufu kiri nti) abeeyisa obubi bali mu bubuze obweyolefu.');
INSERT INTO "african_dev" VALUES(31,12,'31:12','. Era mazima twawa Lukman okumanya ensonga (ne tumugamba) nti weebaze Katonda, era omuntu eyeebaza yeebaza ku lulwe, ate oyo akaafuwala mazima Katonda talina kye yeetaaga (eri kitonde kyonna) era atenderezebwa nnyo.');
INSERT INTO "african_dev" VALUES(31,13,'31:13','. Era jjukira Lukman bwe yagamba Mutabaniwe bw eyali amubuulirira nti: owange mwana wange togattanga ku Katonda ekintu ekirala mazima okugatta ku Katonda ekintu ekirala kwe kweyisa obubi okubi ennyo.');
INSERT INTO "african_dev" VALUES(31,14,'31:14','. Era twakalaatira omuntu okuyisa obulungi bakaddebe bombi, nnyina amusitula mu lubuto nga bunafu obweyongera ku bunafu, era nga okumuggya ku mabeere (kubaawo) mu banga lya myaka ebiri, (era ne tumugamba) nti neebaza era weebaze bakaddebo. Gyendi yokka yeeri obuddo.');
INSERT INTO "african_dev" VALUES(31,15,'31:15','. Bwe baba bakuwalirizza obe nga ongattako ekyo ky''otolinaako kumanya kwonna tobagonderanga, naye kolagana nabo mu nsi mu ngeri nnungi, era ogoberere ekkubo ly''oyo eyeemenya nadda gyendi. Ate oluvanyuma gyendi yeeri obuddo bwa mmwe olwo nno nembategeeza ebyo bye mwakolanga.');
INSERT INTO "african_dev" VALUES(31,16,'31:16','. Owange Mutabani wange mazima kyo (e kikolwa) ne bwe kiba kyenkana obuzito bwempeke ya Khardali, keebe nga eri mu lwazi oba mu ggulu omusanvu oba e nsi, Katonda agenda kukireeta (asasule e yakikola) mazima Katonda amanyidde ddala ebitalabika, ate buli kintu akimanyidde ddala okuva ku ntobo yaakyo.');
INSERT INTO "african_dev" VALUES(31,17,'31:17','. Owange Mutabani wange yimirizaawo e sswala olagire (abantu) okuyisa obulungi, obakomeko ku kweyisa obubi era ogumiikirize ku ebyo ebiba bikutuuseeko. Mazima ebyo (ebikugambiddwa) bye bimu ku bigambo e bikakafu.');
INSERT INTO "african_dev" VALUES(31,18,'31:18','. Tokyusizanga ettamalyo abantu olw''okwekuza (ng''obalengezza) era totambulanga mu nsi nga weewulira, mazima Katonda tayagala buli yenna eyeekuluntaza omwewulize.');
INSERT INTO "african_dev" VALUES(31,19,'31:19','. Pima entambulayo (tosaayirira ate totambula nga nawolovu) kakkanya e ddoboozilyo, mazima eddoboozi erisinga obubi mu maloboozi gonna ddoboozi lya ndogoyi.');
INSERT INTO "african_dev" VALUES(31,20,'31:20','. Abaffe temulaba nti mazima Katonda yabagondeza ebyo ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi, era n''abawa mu bungi ebyengerabye eby''okungulu n''ebyomunda, naye mu bantu mulimu abawalaaza empaka ku bikwata ku Katonda awatali kusinziira ku kumanya kwonna wadde obulungamu wadde e kitabo ekibamulisiza e kkubo.');
INSERT INTO "african_dev" VALUES(31,21,'31:21','. Bwe baba bagambiddwa nti mugoberere ebyo Katonda bye yassa bagamba nti nedda tugoberera ebyo bye twasangako bakadde baffe, naye abaffe (balemera ku ekyo) newaakubadde Sitane ebakoowoola kudda eri omuliro Sairi (bamala gagoberera).');
INSERT INTO "african_dev" VALUES(31,22,'31:22','. Yye, omuntu awaayo ekyenyikye ewa Katonda era nga mulongoosa, mazima aba yeekutte ku muguwa omunywevu era ewa Katonda yeeri enkomerero y''ebigambo.');
INSERT INTO "african_dev" VALUES(31,23,'31:23','. Oyo yenna akaafuwala, okukaafuwalakwe tekukunakuwaza gye tuli yeeri obuddo bwabwe, olwo nno tulibategeeza bye baakola. Mazima Katonda amanyidde ddala ebiri mu bifuba.');
INSERT INTO "african_dev" VALUES(31,24,'31:24','. Tubeeyagaza kitono, oluvanyuma tulibawalabanya okubatwala eri ebibonerezo ebizito.');
INSERT INTO "african_dev" VALUES(31,25,'31:25','. Singa obadde obabuzizza nti: ani yatonda eggulu omusanvu n''ensi? ddala bajja kugamba nti Katonda, bagambe nti (n''olwekyo) okutenderezebwa kwonna kwa Katonda. Wabula abasinga obungi ku bo tebamanyi.');
INSERT INTO "african_dev" VALUES(31,26,'31:26','. Bya Katonda ebyo ebiri mu ggulu omusanvu n''ensi, mazima Katonda yye y''atalina kye yetaaga (era) atenderezebwa.');
INSERT INTO "african_dev" VALUES(31,27,'31:27','. Era mazima singa e miti gyonna egiri mu nsi zaali kalaamu, nga n''ennyanja (eziriwo) ziyambibwako ennyanja musanvu oluvanyuma lwa zino, ebigambo bya Katonda tebyaliweddeyo, mazima Katonda nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(31,28,'31:28','. Okutondebwa kwa mmwe n''okuzuukira kwa mmwe tekuli okugyako okuba nga kulinga okutonda n''okuzuukiza omuntu omu. Mazima Katonda awulira nnyo alabira ddala.');
INSERT INTO "african_dev" VALUES(31,29,'31:29','. Abaffe tolaba nti mazima Katonda ayingiza ekiro mu budde obw''emisana, ate naayingiza obudde obw''emisana mu kiro era naagonza enjuba n''omwezi, buli kimu kigenda kudduka okutuusa ku kiseera ekigere, era mazima Katonda amanyidde ddala bye mukola.');
INSERT INTO "african_dev" VALUES(31,30,'31:30','. Ekyo kiri bwe kityo, lwa kuba nti Katonda yye ye mazima, era ddala ebyo bye basaba ne baleka Katonda bye bikyamu, era mazima Katonda yye ye wa waggulu asinga okuweebwa e kitiibwa.');
INSERT INTO "african_dev" VALUES(31,31,'31:31','. Abaffe tolaba nti mazima amaato gaseeyeeya ku nnyanja ku lw''okusaasira kwa Katonda, olwo nno abe nga abalaga mu bubonerobwe. Mazima ekyo mulimu eby''okuyiga eri buli mugumiikiriza omwebaza.');
INSERT INTO "african_dev" VALUES(31,32,'31:32','. (Abatakkiriza bwe babeera mu maato ago) amayengo bwe gababikka ne genkana ng''ensozi (olwo nno) basaba Katonda, nga eddiini yonna bagikola ku lulwe yekka, naye bwabawonya, naabatuusa ku lubalama, mu bo mu baamu abeegendereza (ne baba wakati w''okukola e mirimu e mirungi n''emibi) era tawakanya bubonero bwaffe okugyako buli wa nkwe omukaafiiri.');
INSERT INTO "african_dev" VALUES(31,33,'31:33','. Abange mmwe abantu mutye Mukama omulabirizi wa mmwe era mutye olunaku omuzadde lwataligasa mwanawe wadde omwana okuba nti yye y''aligasa mukaddewe ekintu kyonna, mazima endagaano ya Katonda ya mazima, kale nno obulamu bw''ensi tebubabulankanyanga era ebirabankanya tebibaggyanga ku Katonda.');
INSERT INTO "african_dev" VALUES(31,34,'31:34','. Mazima Katonda ewuwe yokka y''eri okumanya ebifa ku lunaku lw''enkomerero, era y''atonnyesa e nkuba, era yamanyi ekyo ekiri mu nnabaana, omuntu tayinza kumanya kiki kyaanaafuna enkya, era omuntu tayinza kumanya alifiira mu nsi ki, mazima Katonda amanyi nnyo ategeerera ddala buli kintu okuva ku ntobo yaakyo.');
INSERT INTO "african_dev" VALUES(32,1,'32:1','. Alif Laam Miim.');
INSERT INTO "african_dev" VALUES(32,2,'32:2','. Okussibwa kw''ekitabo kino (Kur''ani) tekuliimu kubuusabuusa (okuba nti) kyava ewa Mukama omulabirizi w''ebitonde.');
INSERT INTO "african_dev" VALUES(32,3,'32:3','. Oba bagamba nti (Muhammad) yakyegunjirawo, si bwe kiri, kyo g''emazima agava ewa Mukama omulabiriziwo, obe nga otiisa abantu abatajjirwa mutiisa oluberyeberyelwo olwo nno babe nga balungama.');
INSERT INTO "african_dev" VALUES(32,4,'32:4','. Katonda y''oyo eyatonda mu nnaku mukaaga eggulu omusanvu n''ensi, na byonna ebiri wakati wa byombi bwe yamala naatebenkera ku Arishi, okugyako yye temulina mukuumi wadde omuwolereza. abaffe temwebuulirira?.');
INSERT INTO "african_dev" VALUES(32,5,'32:5','. (Katonda) atambuza e bintu byonna (nga ebiragiro) biva mu ggulu ne bijja ku nsi, oluvanyuma (ebikwata ku bikoleddwa) birinnya ne byambuka gyali mu lunaku nga e kigero kyalwo kyenkana emyaka lukumi mu egyo gye mubala.');
INSERT INTO "african_dev" VALUES(32,6,'32:6','. Oyo nno ye mumanyi w''ebikusike n''ebirabwako, nantakubwa ku mukono omusaasizi.');
INSERT INTO "african_dev" VALUES(32,7,'32:7','. Oyo eyalungiya buli kintu kye yatonda, nga era yatandika okutonda omuntu nga amuggya mu ttaka.');
INSERT INTO "african_dev" VALUES(32,8,'32:8','. Ate ezaddelye (omuntu) n''aba nga alisibimbula mu mazzi aganyoomebwa.');
INSERT INTO "african_dev" VALUES(32,9,'32:9','. Bwe yamala yamussa mu kifaananyi e kimusaanira, naamufuwamu ogumu ku myoyogye (gye yatonda edda) naabassaako amatu, n''amaaso n''emitima (naye) mwebaza kitono.');
INSERT INTO "african_dev" VALUES(32,10,'32:10','. Era (abatakkiriza) baagamba nti bwe tulimala okusaanirawo mu ttaka, abaffe walibaayo okutonda okupya (ne tuddawo), ekiboogeza ekyo si kirala wabula lwa kuba nti bawakanya okusisinkana Mukama omulabirizi waabwe.');
INSERT INTO "african_dev" VALUES(32,11,'32:11','. Bagambe nti Malayika walumbe oyo eyaweebwa obuyinza ku mmwe agenda kubatta, oluvanyuma eri Mukama omulabirizi wa mmwe gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(32,12,'32:12','. Singa olisobola okulaba aboonoonyi webalikutamiza emitwe gyabwe, mu maaso ga Mukama omulabirizi waabwe, nga bagamba nti ayi Mukama omulabirizi waffe, tulabye era tuwulidde, n''olwekyo tuzzeeyo tukole e mirimu e mirungi mazima ffe tumaze okukakasa.');
INSERT INTO "african_dev" VALUES(32,13,'32:13','. Era singa twayagala buli mwoyo twaliguwadde obulungamu bwago, naye mazima kyakakata e kigambo okuva mu nze nti: nja kujjuliza ddala omuliro Jahannama okuva mu Majinni n''abantu bonna awamu.');
INSERT INTO "african_dev" VALUES(32,14,'32:14','. (Baligambibwa nti) kale mukombe (ku bibonerezo) ku lw''okwerabira kwa mmwe okusisinkana olunaku lwa mmwe luno, mazima ffe tubeerabidde era mukombe ku bibonerezo eby''olubeerera olw''ebyo bye mwali mukola.');
INSERT INTO "african_dev" VALUES(32,15,'32:15','. Mazima abakkiriza e bigambo byaffe, beebo buli lwe baba babuuliriddwa nabyo nga babutama wansi ne bavunnama era nebatendereza ebitendo bya Mukama omulabirizi waabwe, era bulijjo bbo tebeekuza.');
INSERT INTO "african_dev" VALUES(32,16,'32:16','. Nga e mbiriizi zaabwe ziva ku buliri (e kiro) ne baba mu kusaba Mukama omulabirizi waabwe olw''okutya (ebibonerezo bye) n''olwokululunkana (okufuna ebirungi bye), era bulijjo bawaayo nga baggya Mu ebyo bye tubagabirira.');
INSERT INTO "african_dev" VALUES(32,17,'32:17','. Tewali muntu amanyi ebyo ebyabateerekerwa mu kyama mu bitebenkeza amaaso (ebyabaweebwa) nga mpeera olw''ebyo bye baali bakola.');
INSERT INTO "african_dev" VALUES(32,18,'32:18','. Abaffe oyo abeera nga mukkiriza ayinza okuba nga oyo omwonoonyi! (e kituufu kiri nti) tebenkana.');
INSERT INTO "african_dev" VALUES(32,19,'32:19','. Bo abo abakkiriza ne bakola emirimu emirungi balina e jjana ezeeyagaza okubeeramu, nga bugenyi (obulibaweebwa) olw''ebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(32,20,'32:20','. Bo abo abaayonoona obuddo bwabwe muliro, lwonna lwe baliyagalanga okugufuluma nga bazzibwamu era ne bagambibwa nti mukombe ku bibonerezo by''omuliro ogwo gwe mwali mulimbisa.');
INSERT INTO "african_dev" VALUES(32,21,'32:21','. Era ddala tugenda kubakombesa ku bibonerezo ebinafu (mu bulamu obw''ensi) nga tebannatuuka ku bibonerezo ebinene (ku lunaku lw''enkomerero), oba oli awo ne badda (eri obutuufu).');
INSERT INTO "african_dev" VALUES(32,22,'32:22','. Ani eyeeyisa obubi okusinga oyo abuulirirwa ne bigambo bya Mukama omulabiriziwe ate naabivaako! Mazima ffe tugenda kubonereza aboonoonyi.');
INSERT INTO "african_dev" VALUES(32,23,'32:23','. Era mazima twawa Musa e kitabo (nga Taurat n''olwekyo naawe) tobeeramu kubuusabuusa mu ku kifuna (nga Kur''ani) era twakifuula bulungamu eri abaana ba Israil.');
INSERT INTO "african_dev" VALUES(32,24,'32:24','. Era twassaawo nga tuggya mu bo abakulembeze abalungamya (abantu) nga bakolera ku kiragiro kyaffe, kavuna bagumiikiriza era ne baba nga bakakasa obubonero bwaffe.');
INSERT INTO "african_dev" VALUES(32,25,'32:25','. Era mazima Mukama omulabiriziwo yye ajja kusalawo wakati waabwe ku lunaku lw''enkomerero mu ebyo bye baayawukanangamu.');
INSERT INTO "african_dev" VALUES(32,26,'32:26','. Abaffe tebakimanyanga mu bulambulukufu nti e mirembe emeka gye twazikiriza oluberyeberye lwabwe, ate nga batambulira mu bitundu gye baabeeranga, mazima mu ekyo mulimu obubonero bwaffe. Abaffe tebawulira!.');
INSERT INTO "african_dev" VALUES(32,27,'32:27','. Abaffe tebalaba nti mazima ffe tusundika amazzi eri ettaka ekkalu ne tumeza nago ebirimwa, e bisolo bya bwe kwe birya nabo bennyini, abaffe tebalaba!.');
INSERT INTO "african_dev" VALUES(32,28,'32:28','. Era bagamba nti okuwangula (okwo kwe mwogerako) kulibaawo ddi bwe muba nga mwogera mazima?.');
INSERT INTO "african_dev" VALUES(32,29,'32:29','. Bagambe nti olunaku lw''okuwangula (bwe lulituuka) abo abaakaafuwala obukkiriza bwa bwe, (bwe baliyagala okukola mu kiseera ekyo) tebulibagasa, era tebagenda kulindirizibwa.');
INSERT INTO "african_dev" VALUES(32,30,'32:30','. Kale nno baveeko era ggwe olindirire, mazima bbo bali mu kulindirira.');
INSERT INTO "african_dev" VALUES(33,1,'33:1','. Owange gwe Nabbi, tya Katonda era togondera abakaafiiri n''abannafunsi, mazima Katonda bulijjo mumanyi nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(33,2,'33:2','. Era goberera ebyo ebitumwa gyoli okuva ewa Mukama omulabiriziwo mazima Katonda bulijjo amanyidde ddala bye mukola.');
INSERT INTO "african_dev" VALUES(33,3,'33:3','. Era weekwate ku Katonda, Katonda amala okuba nga gweweekwatako.');
INSERT INTO "african_dev" VALUES(33,4,'33:4','. Katonda tateekanga munda wa muntu mitima ebiri, era bakyala ba mmwe abo be mulayira obuteegatta nabo Katonda tabafuulanga ba Maama ba mmwe, olw''okuba nti muba mugambye nti emigongo gyabwe giringa egya ba nnyammwe, era abaana be mufuula abaana ba mmwe (nga si mmwe mu bazaala) tabafuula baana ba mmwe, ebyo mmwe bigambo byammwe bye mwogera obwogezi n''emimwa gya mmwe. Era Katonda ayogera mazima, era yye y''alaga e kkubo (erisaana okukwata).');
INSERT INTO "african_dev" VALUES(33,5,'33:5','. Mubateekenga ku ba kitaabwe, ekyo kye kituufu ewa Katonda, bwe muba temumanyi ba kitaabwe (mu bayite baganda ba mmwe) anti baganda ba mmwe mu ddiini, era bannammwe (mu ddiini) era temulina kibi mu ekyo kye mukola mu butanwa, naye (ekibavunaanwa) kyekyo emitima gya mmwe gye kiba gikoze mu bugenderevu, era bulijjo Katonda musonyiyi nnyo musaasizi.');
INSERT INTO "african_dev" VALUES(33,6,'33:6','. Nabbi abakkiriza gwe basaanira okuwa e kitiibwa okusinga bo bennyini, era bakyalabe baba ba Maama baabwe, bo ab''oluganda abamu ku bo be basinga okusaanira bannaabwe (mu by''obusika) ekyo bwe kityo bwe kiri mu kitabo kya Katonda, okusinga bwe bandibadde eri abakkiriza n''abaasenguka, okugyako nga mukoledde ba nnammwe eky''obuntu bulamu, ebyo nno byawandiikibwa mu Kitabo.');
INSERT INTO "african_dev" VALUES(33,7,'33:7','. Era jjukira bwe twakozesa ba Nabbi endagaano yaabwe, ne ku ggwe ne ku Nuuhu ne ku Ibrahim, ne ku Musa ne ku Isa Mutabani wa Mariam, era twabakozesa e ndagaano nga nzito.');
INSERT INTO "african_dev" VALUES(33,8,'33:8','. Olwo nno (Katonda) alyoke abuuze abakkiriza ab''amazima ku mazima gaabwe, era yateekerateekera abakaafiiri e bibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(33,9,'33:9','. Abange mmwe abakkiriza, mujjukire ebyengera bya Katonda bye yabagonnomolako amagye lwe gaabalumba, ne tubasindikira kibuyaga n''amagye ge mutaalaba, era bulijjo Katonda alabira ddala bye mukola.');
INSERT INTO "african_dev" VALUES(33,10,'33:10','. Mujjukire bwe baabajjira nga bayitira engulu ne mmanga wa mmwe, era mujjukire amaaso lwe gaakanuka era emitima ne gituuka mu malokooli, nemulowooza ku Katonda ebirowoozo ebyenjawulo.');
INSERT INTO "african_dev" VALUES(33,11,'33:11','. Nga muli mu mbeera eyo, abakkiriza baagezesebwa ne bakankanyizibwa olukankana olw''amaanyi.');
INSERT INTO "african_dev" VALUES(33,12,'33:12','. Era mujjukire abannanfusi n''abo abaalina obulwadde mu mitima gyaabwe bwe baagamba nti Katonda n’omubakawe tebaatulagaanya wabula (baatulagaanya) bya kutuguyaaguya.');
INSERT INTO "african_dev" VALUES(33,13,'33:13','. Era mujjukire e kibiina mu mmwe bwe kyagamba nti, abange mmwe abantu be Yathriba (Madinah) temusobola kubeera wano, n''olwekyo muddeeyo (mu Maka gammwe), ate e kibiina ekirala mu bo baasaba Nabbi (S.A.W.) abakkirize baleme kugenda ku lutalo, nga bagamba nti, mazima amayumba gaffe meereere, so nga tegaali meereere, baali tebalina kye baagala kyonna okugyako okudduka.');
INSERT INTO "african_dev" VALUES(33,14,'33:14','. Era singa baali balumbiddwa okuva mu mpuyi zaakyo (e kibuga) oluvanyuma ne basabwa bakaafuwale (era balwanyise abasiraamu) baalikikoze era tebaalisibyemu okugyako kaseera katono.');
INSERT INTO "african_dev" VALUES(33,15,'33:15','. Ate nga mu kusooka baali baalagaanyisa Katonda nti tebagenda kukyuka kukubayo mabega (ne badduka mu lutalo), era bulijjo e ndagaano ya Katonda ebuuzibwako (okulaba oba etuukiriziddwa).');
INSERT INTO "african_dev" VALUES(33,16,'33:16','. Bagambe (gwe Muhammad) bwe muba nga mudduse kufa oba kuttibwa, okudduka tekuyinza kubagasa era ne bwe muba nga mudduse temugenda kuweebwa kweyagala okugyako akaseera katono.');
INSERT INTO "african_dev" VALUES(33,17,'33:17','. Bagambe nti, ani oyo ayinza okubawonya ku Katonda singa abadde abaagalizza akabi, oba nga abaagalizza kusaasira, era bulijjo okugyako Katonda tebayinza kufuna mukuumi oba omutaasa.');
INSERT INTO "african_dev" VALUES(33,18,'33:18','. Mazima Katonda amanyi abalemesa mu mmwe (ababa baagala okugenda mu lutalo) n''abo abagamba baganda baabwe nti mujje gyetuli) muve ku Muhammad (s.a.w.), era tebaagala kugenda ku lutalo okugyako kitono.');
INSERT INTO "african_dev" VALUES(33,19,'33:19','. Bulijjo baba bakodo ku mmwe, tebaagala (kubayamba ku lutalo). Ekiseera ekya kazigizigi bwe kijja obalaba nga bakukanulira amaaso gaabwe nga gakyuka ng’oyo azirise ng''agenda okufa, naye akaseera ka kazigizigi bwe kavaawo bakkakkana ku mmwe ne nnimi enjogi nga balulunkanira e birungi, abo tebakkirizanga, olwo nno Katonda asangula e mirimu gya bwe era ekyo ku Katonda kyangu nnyo.');
INSERT INTO "african_dev" VALUES(33,20,'33:20','. (Abannanfusi olw''obutiitiizi bwabwe) batuuka okulowooza nti ab''amagye g''omukago (abalumbaganyi) tebannagenda, singa amagye gajja (omulundi omulala), abannanfusi bandyegombye singa bbo babeera mu ba nnamalungu ne baba nga babuuza bubuuza ebikwata ku mmwe (nga bali eyo) era singa baali mu mmwe tebandirwanye okugyako kitono.');
INSERT INTO "african_dev" VALUES(33,21,'33:21','. Mazima mulina mmwe mu Mubaka wa Katonda ekyokulabirako ekirungi eri oyo abeera nga akkiriza Katonda n''olunaku lw''enkomerero, era naayogera nnyo ku Katonda nga amutendereza.');
INSERT INTO "african_dev" VALUES(33,22,'33:22','. Abakkiriza bwe baamala okulaba amagye g''omukago baagamba nti kino kyekyo Katonda n''omubakawe kye baatulagaanyisa, era Katonda n''omubakawe bayogera mazima, era (bwe baalaba) amagye g''omulabe tebaabongera okugyako obukkiriza n''okwewa Katonda.');
INSERT INTO "african_dev" VALUES(33,23,'33:23','. Mu bakkiriza mulimu abasajja abaatuukiriza ekyo kye baaweerako Katonda obweyamu (bwe baagamba nti bwe tulisisinkana omulabe bugenda ku twefuka), kale nno mu bo mulimu eyatuukiriza obweyamubwe nga bwe mulimu akyalindirira era tebakyusangako (ku ekyo) kukyusa kwonna.');
INSERT INTO "african_dev" VALUES(33,24,'33:24','. Olwo nno Katonda alyoke asasule abaatuukiriza amazima olwa mazima gaabwe, era abonereze abannanfusi bwaliba ayagadde, oba akkirize okwenenya kwa bwe(bwe baliba beenenyezza) mazima Katonda bulijjo musonyiyi nnyo musaasizi.');
INSERT INTO "african_dev" VALUES(33,25,'33:25','. Era Katonda yazzaayo abo abaakaafuwala n''obusungu bwa bwe, tebalina kalungi kebaafuna, era Katonda yamalira abakkiriza olutalo, era bulijjo Katonda wa maanyi nantakubwa ku mukono.');
INSERT INTO "african_dev" VALUES(33,26,'33:26','. Era yawanula abo abaabayamba mu ba nannyini kitabo ng''abajja mu bikomera byabwe, nassa mu mitima gya bwe okutya ne muba nga mutta e kitundu ate ne muwamba e kitundu e kirala (mu bo).');
INSERT INTO "african_dev" VALUES(33,27,'33:27','. Era naasikiza mmwe e bibanja byaabwe n''amaka gaabwe ne mmaali yaabwe n''ebitundu by''ensi ebirala bye mutatuukangamu, bulijjo Katonda muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(33,28,'33:28','. Owange ggwe Nabbi gamba bakyalabo nti: bwe muba mwagala bulamu bw''ansi na birungi byaayo, kale mujje mbasibirire (mbawe e birungi bye mwagala) era mbate oluta olulungi.');
INSERT INTO "african_dev" VALUES(33,29,'33:29','. Naye bwe muba nga mwagala Katonda n''omubakawe, ne nyumba ey''enkomerero mazima Katonda yateekerateekera abakola obulungi mu mmwe, e mpeera e nsuffu.');
INSERT INTO "african_dev" VALUES(33,30,'33:30','. Abange mmwe abakyala ba Nabbi oyo yenna mu mmwe akola e kikolwa eky''obuwemu, wekikakasibwa nti yakikoze, e kibonerezo ekimuweebwa kibazibwamu emirundi ebiri (olw''okuba Mukyala wa Nabbi) era ekyo kyangu ku Katonda.');
INSERT INTO "african_dev" VALUES(33,31,'33:31','. N''oyo yenna mu mmwe (baka Nabbi) agondera Katonda n’omubakawe naakola emirimu emirungi tumuwa empeeraye emirundi ebiri, era twamutegekera riziki eweesa ekitiibwa.');
INSERT INTO "african_dev" VALUES(33,32,'33:32','. Abange baka Nabbi temufaanana bakyala balala, bwe muba mutidde Katonda kale temugonzanga enjogera olwo nno naakwatibwa amaddu oyo alina obulwadde mu mutima gwe, era (buli lwe mwogera) mwogere ebigambo eby''ensa.');
INSERT INTO "african_dev" VALUES(33,33,'33:33','. Era mukkalire mu mayumba gammwe era temwejaajaamyanga enneejaajaamya ey''obutamanya obwaliwo mu kusooka era muyimirizeewo e sswala ne Zakka mugitoole, era mugondere Katonda n’omubakawe mazima Katonda ayagala okubaggyako ebibi abange mmwe abantu bo mu nyumba (ya Nabbi) era abatukuze olutukuza olwa nnamaddala.');
INSERT INTO "african_dev" VALUES(33,34,'33:34','. Era mujjukirenga ebyo ebisomerwa mu mayumba gammwe mu bigambo bya Katonda n’ebigambo eby''amagezi (Hadith za Nabbi), mazima bulijjo Katonda akwata bulungi (abaddu) ate nga amanyi byonna ebibafaako.');
INSERT INTO "african_dev" VALUES(33,35,'33:35','. Mazima abasajja abasiraamu n’abakazi abasiraamu, n’abasajja abakkiriza n’abakazi abakkiriza, n’abasajja abasinza Katonda n’abakazi abasinza Katonda, n’abasajja aboogera amazima n’abakazi aboogera amazima, n’abasajja abagumiikiriza n’abakazi abagumiikiriza, n’abasajja abagondera Katonda n’abakazi abagondera Katonda, n’abasajja abasaddaaka n’abakazi abasaddaaka, n’abasajja abasiiba n’abakazi abasiiba, n’abasajja abakuuma obwereere bwa bwe n’abakazi bwe batyo, n’abasajja aboogera ennyo ku Katonda n’abakazi bwe batyo, (abo bonna) Katonda yabategekera ekisonyiwo n''empeera ensuffu.');
INSERT INTO "african_dev" VALUES(33,36,'33:36','. Tekigwanirangako omukkiriza omusajja oba omukkiriza omukazi, Katonda n’omubaka we okumala okusalawo ekintu ate bo nebaba nga balina okwesalirawo ku nsonga yaabwe ate nga oyo yenna ajeemera Katonda n’omubaka we mazima aba abuze olubula olweyolefu.');
INSERT INTO "african_dev" VALUES(33,37,'33:37','. Jjukira bwe wagamba oyo Katonda gwe yawa ekyengera (n''asiramuka) naawe n''omuwa ekyengera (eky''okumuggya mu buddu) nti beera ne mukyalawo (toyawukana naye) era tya Katonda , ate ggwe nookweka mu mwoyo gwo ekyo Katonda kyanassa mu bbanga, n''oba nga otya abantu ate nga Katonda y''asinga okusaanira okuba nga gwotya, olwo nno Zaidi bwe yamala okumala obwetaavu (bwe) ku ye, twamukufumbiza olw''ensonga nti waleme kubaawo kuwulira bubi mu bakkiriza kuwasa baka baana baabwe (be batazaalira ddala) wabula bo be bayita obuyisi abaana baabwe. Wonna webaba bamalidde kye beetaaga ku bo, (nebaba nga tebakyabaagala nebabawa talaka) nga bulijjo ekiragiro kya Katonda kiteekwa kussibwa mu nkola.');
INSERT INTO "african_dev" VALUES(33,38,'33:38','. Tekisaanira Nabbi kuwulira bubi ku ekyo Katonda kyaba amusaliddewo, eyo y''enkola ya Katonda ku abo abaaliwo olubereberye. Ekigambo kya Katonda bulijjo kiba kyagerwa ekyasalibwawo edda.');
INSERT INTO "african_dev" VALUES(33,39,'33:39','. (Obutaba na nkenyera nga Katonda amaze okusalawo y''enkola ya) abo abasaasaanya obubaka bwa Katonda era nebamutya nebaba nga tebatya kintu kyonna okugyako Katonda, bulijjo kimala okuba nga Katonda y''agenda okubala.');
INSERT INTO "african_dev" VALUES(33,40,'33:40','. Muhammad tabangako kitaawe w''omu ku basajja mu mmwe wabula (yye) Mubaka wa Katonda, era ow''enkomerero wa ba Nabbi. Era bulijjo Katonda amanyidde ddala ebifa ku buli kintu.');
INSERT INTO "african_dev" VALUES(33,41,'33:41','. Abange mmwe abakkiriza mwogere ku Katonda nga mumwogerako nnyo.');
INSERT INTO "african_dev" VALUES(33,42,'33:42','. Era mumutendereze enkya n’olweggulo.');
INSERT INTO "african_dev" VALUES(33,43,'33:43','. Yye yooyo abasaasira nga ne ba Malayika be babasabira, abe nga abaggya mu bizikiza mudde eri ekitangaala. Era bulijjo musaasizi eri abakkiriza.');
INSERT INTO "african_dev" VALUES(33,44,'33:44','. Salaamu kye kiriba ekiramuso kya bwe (okuva ewa Katonda) olunaku lwe balimusisinkana, era yabategekera empeera ennungi.');
INSERT INTO "african_dev" VALUES(33,45,'33:45','. Owange ggwe Nabbi mazima ffe twakutuma nga oli mujulizi, era nga owa amawulire ag''essanyu, era nga owa amawuire agalabula.');
INSERT INTO "african_dev" VALUES(33,46,'33:46','. Era nga okoowoola (abantu) okudda eri Katonda ku lwe''kiragirokye era nga oli ttaala emulisa.');
INSERT INTO "african_dev" VALUES(33,47,'33:47','. Era sanyusa abakkiriza nti mazima bagenda kufuna okuva eri Katonda ebirungi ebisuffu.');
INSERT INTO "african_dev" VALUES(33,48,'33:48','. Era togondera abakaafiiri n''abannanfusi era tofa ku binyiizo bya bwe era weekwate bulijjo ku Katonda, anti amala okuba nga ye mukuumi.');
INSERT INTO "african_dev" VALUES(33,49,'33:49','. Abange mmwe abakkiriza bwe muwasanga abakyala abakkiriza ate nemubata nga temunnabakwatako (nga temunnatuuka gye bali), temubalinaako Idda zebatuula (nga tebannafumbirwa basajja balala), olwo nno mubasibirire ekyo ekibasanyusa era mubaleke oluleka olulungi.');
INSERT INTO "african_dev" VALUES(33,50,'33:50','. Owange ggwe Nabbi mazima ffe twakukkiriza okuba ne bakyala bo abo bewawa amahare gaabwe, n''abo bonna omukono gwo ogwa ddyo begufuga nga bajjira mu ebyo Katonda byaba akuwadde mu ntalo nga (eminyago), era twakukkiriza bawala ba Kitaawo omuto ne bawala ba ba sengabo, ne bawala ba Kojja wo ne bawala ba Maama bo abato (baganda ba nnyoko), abo abaasenguka naawe, nabwekityo omukyala yenna omukkiriza bwaba nga yeewadde Nabbi, Nabbi n''aba nga ayagadde okumuwasa ekyo nga kikyo wekka, so ssi abakkiriza abasigadde. Mazima tumanyi ebyo bye twabalaalikako (bo) ku bikwata ku bakyala baabwe n''abo emikono gya bwe egya ddyo begifuga, olwo nno obe nga towulira buzibu. Era bulijjo Katonda musonyiyi musaasizi.');
INSERT INTO "african_dev" VALUES(33,51,'33:51','. Olindiriza oyo gwoba oyagadde ku bo ate nootwala oyo gwoba oyagadde ate oyo gwoba osazeewo mu abo boolindirizza tolina kibi (singa oba osazeewo noomutwala) ekyo kye kitandikirwako mu kutebenkeza amaaso gaabwe ne batanakuwala era bonna nebasiima ebyo byoba obawadde, era Katonda amanyi ebyo ebiri mu mitima gya mmwe, anti bilijjo Katonda mumanyi nnyo wa kisa.');
INSERT INTO "african_dev" VALUES(33,52,'33:52','. Tokkirizibwa bakyala balala oluvanyuma lw''abo (boolina) wadde okubawaanyisaamu abakyala abalala, obulungi bwabwe ne bwe bukusanyusa butya okugyako abo omukono gwo ogwa ddyo begufuga, era bulijjo Katonda mulondoozi wa buli kintu.');
INSERT INTO "african_dev" VALUES(33,53,'33:53','. Abange mmwe abakkiriza temuyingiranga e nyumba za Nabbi okugyako nga mukkiriziddwa okugenda okulya (ate) nga temulinda kuyiisibwa kwa mmere, bwe mumala okulya olwo nno musaasaane era temutwalirizibwanga emboozi, mazima ekyo kibadde kinyiiza Nabbi ate nga abakwatirwa ensonyi so nga Katonda takwatibwa nsonyi kwogera mazima, era (bakyala ba Nabbi) bwe mubaako ekintu kye mubasaba mukibasabire mabega wa lutimbe, ekyo nno kyekisinga obutukuvu eri emitima gya mmwe n''emitima gyabwe. Tekibagwanira mmwe okunyiiza omubaka wa Katonda era tekibagwanira kuwasa bakyalabe oluvanyumalwe (nga ekiragiro kino) kya lubeerera. Mazima ekikolwa ekyo kinene ewa Katonda.');
INSERT INTO "african_dev" VALUES(33,54,'33:54','. Kamube nga mwolesezza ekintu oba nga mukikwese mazima Katonda bulijjo amanyidde ddala ekifa ku buli kintu.');
INSERT INTO "african_dev" VALUES(33,55,'33:55','. (Bakyala ba Nabbi) tebalina kibi (obuteebikka) nga bali ne ba kitaabwe, wadde abaana baabwe wadde bannyinaabwe, wadde abaana ba bannyinaabwe, wadde abakyala nga bbo, wadde emikono gyabwe egya ddyo begifuga, bateekeddwa okutya Katonda, mazima Katonda bulijjo aba buli kintu kyonna wekikolerwa.');
INSERT INTO "african_dev" VALUES(33,56,'33:56','. Mazima Katonda neba Malayikabe basaasira Nabbi, abange mmwe abakkiriza mumusabirenga era mumulamuse olulamusa (olumuwa ekitiibwa).');
INSERT INTO "african_dev" VALUES(33,57,'33:57','. Mazima abo abanyiiza Katonda n’omubakawe, Katonda yabakolimira ku nsi ne ku nkomerero era naabategekera ebibonerezo ebinyoomesa.');
INSERT INTO "african_dev" VALUES(33,58,'33:58','. Ate abo abanyiiza abakkiriza abasajja n’abakkiriza abakazi awatali kintu kye bakoze, mazima baba beetisse okutemerera n''ekibi eky''olwatu.');
INSERT INTO "african_dev" VALUES(33,59,'33:59','. Owange ggwe Nabbi gamba bakyalabo ne bawalabo ne bakyala ba bakkiriza babe nga beebikkirira obukaaya bwabwe, ekyo kye kyanguya okuba nga bamanyika (nti ba kitiibwa) olwo nno baleme kunyiizibwa. Bulijjo Katonda muyitirivu wa kusonyiwa era muyitirivu wa kusaasira.');
INSERT INTO "african_dev" VALUES(33,60,'33:60','. Abannanfusi n''abo abalina obulwadde mu mitima gyabwe n''abasaasaanya kalebule mu Madinah bwe banaaba tebeekomyeko tujja kukusobozesa okubakolako, n''ekinaddako tebagenda kuddayo kubeera naawe mu kyo okugyako ekiseera kitono.');
INSERT INTO "african_dev" VALUES(33,61,'33:61','. Bakolimirwa, wonna webasangibwa bakwatibwa nebattibwa olutta.');
INSERT INTO "african_dev" VALUES(33,62,'33:62','. (Tebasaasirwa) ekyo ye nkola ya Katonda ku abo abaaliwo olubereberye, ate toyinza kusanga kukyuka mu nkola ya Katonda.');
INSERT INTO "african_dev" VALUES(33,63,'33:63','. Abantu bakubuuza ku lunaku lw''enkomerero bagambe nti: mazima okumanyibwa kwalwo kuli eri Katonda, ye omanya otya oba olunaku lw''enkomerero luli kumpi!.');
INSERT INTO "african_dev" VALUES(33,64,'33:64','. Mazima Katonda yakolimira abakaafiiri era naabategekera omuliro Sai-ir.');
INSERT INTO "african_dev" VALUES(33,65,'33:65','. Baakutuula mu gwo bugenderevu, tebagenda kufuna wa mukwano oba omutaasa.');
INSERT INTO "african_dev" VALUES(33,66,'33:66','. Ku lunaku ebyenyi byabwe lwe birikyusibwakyusibwa mu muliro nga bagamba nti zitusanze singa twagondera Katonda era netugondera omubaka.');
INSERT INTO "african_dev" VALUES(33,67,'33:67','. Era ne bagamba nti: ayi Mukama omulabirizi waffe mazima ffe twagondera abakulembeze baffe n''abatutumufu mu ffe, olwo nno nebatubuza ekkubo.');
INSERT INTO "african_dev" VALUES(33,68,'33:68','. Ayi Mukama omulabirizi waffe bawe ebibonerezo byabwe nga bibaziddwamu emirundi ebiri era obakolimire olukolimira olunene.');
INSERT INTO "african_dev" VALUES(33,69,'33:69','. Abange mmwe abakkiriza temubeeranga abo abaanyiiiza Musa (bwe baagamba nti aliko obulemu ku mubirigwe obumugaana okunaaba naffe) wabula Katonda yamutukuza ku ekyo kye baagamba era bulijjo wa kitiibwa ewa Katonda.');
INSERT INTO "african_dev" VALUES(33,70,'33:70','. Abange mmwe abakkiriza mutye Katonda era mwogere ebigambo ebituufu.');
INSERT INTO "african_dev" VALUES(33,71,'33:71','. Ajja kubalongooseza emirimu gya mmwe era abasonyiwe ebyonoono bya mmwe, era bulijjo omuntu agondera Katonda n’omubakawe aba yeesiimye olwesiima olusuffu.');
INSERT INTO "african_dev" VALUES(33,72,'33:72','. Mazima ffe twayagala obuvunaanyizibwa okubuwa eggulu omusanvu n''ensi n''ensozi naye nebigaana okubwetikka nebibwesamba, ye omuntu naabwetikka (naatatuukiriza era olw''ekyo) n''abeera eyeeyisa obubi atamanyi.');
INSERT INTO "african_dev" VALUES(33,73,'33:73','. Olwo nno Katonda alyoke abe nga abonereza abannanfusi abasajja n''abannanfusi abakazi, n’abasajja abagatta ku Katonda ebintu ebirala n’abakazi abagatta ku Katonda ebintu ebirala, era abe nga asonyiwa abakkiriza abasajja n’abakkiriza abakazi. Era bulijjo Katonda muyitirivu wa kusonyiwa muyitirivu wa kusaasira.');
INSERT INTO "african_dev" VALUES(34,1,'34:1','. Ebitendo byonna bya Katonda, oyo nga bibye yekka, ebyo ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi, era ebitendo byonna bibye ku nkomerero era yye ye mugoba nsonga amanyi ennyo.');
INSERT INTO "african_dev" VALUES(34,2,'34:2','. Amanyi ebyo ebiyingira mu nsi n''ebigifuluma, n''ebyo ebikka okuva mu ggulu n''ebyambuka mu lyo, era yye ye musaasizi omusonyiyi.');
INSERT INTO "african_dev" VALUES(34,3,'34:3','. Abo abaakaafuwala bagamba nti enkomerero tegenda kutujjira, bagambe nti nedda ndayidde Mukama omulabirizi wange (enkomerero) egenda kubajjira ddala (Mukama omulabirizi wange) omumanyi w''ebyekusifu, akantu akatono ennyo tekayinza kumubulako kakabe mu ggulu omusanvu oba mu nsi wadde akatono ennyo okusinga kwako wadde ekinene (tewali kintu kyonna) okugyako nga kiri mu kitabo ekinnyonnyofu.');
INSERT INTO "african_dev" VALUES(34,4,'34:4','. (Byonna bwe biryolesebwa) olwo nno (Katonda) alyoke asasule abo abakkiriza nebakola emirimu emirongoofu. Abo balina ekisonyiwo ne riziki eweesa ekitiibwa.');
INSERT INTO "african_dev" VALUES(34,5,'34:5','. Ate abo abakola kaweefube okulwanyisa ebigambo byaffe, abo balina ebibonerezo ebiruma olw''ebyonoono (byabwe).');
INSERT INTO "african_dev" VALUES(34,6,'34:6','. Bo abo abaaweebwa okumanya ebissibwa gyoli okuva ewa Mukama omulabiriziwo baliraba nga g’emazima era nga birungamya eri ekkubo lya nantakubwa ku mukono atenderezebwa.');
INSERT INTO "african_dev" VALUES(34,7,'34:7','. Abo abaakaafuwala (mu kujereegerera) bagamba nti abaffe tubalagirire omusajja anaabategeeza bwe munaaba muyuziddwa oluyuzibwa olw''amaanyi (nga muli mu ntaana ennyama n''ebaggwa ku magumba) mbu mazima mmwe muliddayo nemutondebwa bupya.');
INSERT INTO "african_dev" VALUES(34,8,'34:8','. Abaffe (okwogera bwatyo) atemeredde bulimba ku Katonda oba agudde eddalu, wabula abo abatakkiriza nkomerero bali mu bibonerezo na bubuze obw''ewala.');
INSERT INTO "african_dev" VALUES(34,9,'34:9','. Abaffe tebalaba ebyo ebiri mu maaso gaabwe n''ebyo ebiri emabega waabwe ebiri ku ggulu ne ku nsi, singa twagadde tuyinza okubamizisa ensi oba netubasuulako ekibajjo okuva waggulu, mazima mu ebyo mulimu akabonero (eky''okuyiga) eri buli muddu eyeenenyeza Katonda we.');
INSERT INTO "african_dev" VALUES(34,10,'34:10','. Mazima twawa Dauda ebirungi okuva gye tuli, (nga bwe twagamba nti) abange mmwe nsozi mutendereze wamu naye nammwe ebinyonyi mukole bwe mutyo, era twamugondeza ekyuma.');
INSERT INTO "african_dev" VALUES(34,11,'34:11','. (Netumugamba nti) kola ebyambalo by''olutalo, amakowe gakole nga genkana, era mukole emirimu emirungi mazima nze ndabira ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(34,12,'34:12','. Ate Sulaiman twamugondeza empewo nga ekitundu kyetambula okuva kumakya okutuuka mu ttuntu kyanditambuliddwa mwezi, nga n''ekitundu kyetambula olw''eggulo kyalitambuliddwa mwezi, era twamukulukusiza ensulo z’ekikomo nga ne mu Majinni mulimu agaali mu buyinza bwe agamukolera byonna ku lw''ekiragiro kya Mukama Omulabiriziwe, ejinni lyonna eribula neriva ku kiragiro kyaffe tulikombesa ku bibonerezo ebyo muliro Sa-iri.');
INSERT INTO "african_dev" VALUES(34,13,'34:13','. N''olwekyo nga gamukolera byonna byayagala nga ebifo ebisinzibwamu n''ebifaananyi, ebibumbe ne nsiniya ennene eziringa ebidiba, n''amaseppiki (amanene ennyo) agatava mu kifo, (netumugamba nti) abange abantu ba Dauda mukole olw''okwebaza (Katonda), wabula batono abeebaza mu baddu bange.');
INSERT INTO "african_dev" VALUES(34,14,'34:14','. Bwe twamutuusaako okufa tewali kyagalaga kufa kwe okugyako ebiwuka by’omuttaka (enkuyege) ezaalya omuggo gwe gwe yatambula nga nagwo) olwo nno bwe yagwa amajinni gaakakasa nti singa gaali gamanyi ebyekusifu tegandibadde mu bibonyoobonyo ebinyoomesa.');
INSERT INTO "african_dev" VALUES(34,15,'34:15','. Mazima abantu be Saba-i mu kitundu kyabwe baalinamu eky''okuyiga (nakyo baalina) ennimiro bbiri ku ddyo ne ku kkono (netubagamba nti) mulye mu bigabwa bya Mukama omulabirizi wa mmwe era mumwebaze, (nga kino kye yabawa) kitundu kya nsi kirungi, ne Katonda mulabirizi musonyiyi.');
INSERT INTO "african_dev" VALUES(34,16,'34:16','. Baava ku kyabalagirwa netubasindikira mukoka omuyitirivu (eyawaguza zi daamu (amabibiro g''amazzi) essamba nezijjula amazzi), olwo nno nnimiro zaabwe netubawaanyisizaamu ennimiro endala bbiri ezijjudde ebyokulya ebikaawa n’emiti egitabala n’emiti gya Sidiri mitono tono.');
INSERT INTO "african_dev" VALUES(34,17,'34:17','. Ekyo kye twabasasula olw''obukaafiiri bwa bwe, ye ate tubonereza mu kusasula okw''ekyenkanyi mu ngeri eno okugyako abakaafiiri.');
INSERT INTO "african_dev" VALUES(34,18,'34:18','. Netussa wakati waabwe ne wakati w’ebitundu ebyo bye twawa omukisa ebitundu ebirala nga bikwataganye (era nga bisoboka okuwummulirwamu) okubitambuliramu netukifuula kyangu (kale) mutambule mu byo ebiro n’emisana nga muli mirembe.');
INSERT INTO "african_dev" VALUES(34,19,'34:19','. Olwo nno (mu kifo ky''okwebaza) ne bagamba nti ayi Mukama omulabirizi waffe, ssa amabanga manene (okutuuka wetusobola okuwummulira) mu ngendo zaffe. Mu kukola ekyo nebaba nga beelyazaamaanya bokka, olwo nno netubafuula eky''okwogerako era netubayuzaayuza oluyuzaayuza. Mazima mu ekyo mulimu eby''okuyiga eri buli mugumiikiriza owa nnamaddala eyeebaza ennyo.');
INSERT INTO "african_dev" VALUES(34,20,'34:20','. Mazima Ibuliisu yatuukiriza endowooza ye nga akozesa bbo, olwo nno ne bamugoberera okugyako ekitundu ku bakkiriza.');
INSERT INTO "african_dev" VALUES(34,21,'34:21','. Tabangako na buyinza ku bo wabula (ekimufunyisa abagoberezi) lwakuba twagala tumanye oyo akkiriza olunaku lw’enkomerero nga tumwawula ku oyo alulinamu okubuusabuusa, bulijjo Mukama omulabirizi wo mulondoozi wa buli kintu.');
INSERT INTO "african_dev" VALUES(34,22,'34:22','. Bagambe (gwe Nabbi Muhammad) musabe abo bemugamba nemuva ku Katonda, (wabula mukimanye nti) tebafuga kantu kenkana kanyinkuli mu ggulu omusanvu wadde mu nsi, era tebalina mugabo mu byombi era (Katonda) talina muyambi yenna ava mu bo wadde ava mu balala.');
INSERT INTO "african_dev" VALUES(34,23,'34:23','. Era mu maasoge okuwolereza tekugenda kugasa (muntu yenna) okugyako oyo gwaliba akkirizza, okutuusa okutya bwe kuliba kuggyiddwa mu mitima gya bwe baligamba nti Mukama omulabirizi wa mmwe agambyeki? (ba Malayika) baligamba nti (ayogedde) amazima, era yye wa waggulu omusukkulumu.');
INSERT INTO "african_dev" VALUES(34,24,'34:24','. Bagambe ani abagabirira okuva mu ggulu omusanvu n''e nsi, bagambe nti Katonda. Era mazima ffe oba mmwe tuli ku bulungamu oba ku bubuze obw''olwatu.');
INSERT INTO "african_dev" VALUES(34,25,'34:25','. Bagambe nti temugenda kubuuzibwa ku ebyo bye twayonoona nga bwe tutagenda kubuuzibwa ku bye mukola.');
INSERT INTO "african_dev" VALUES(34,26,'34:26','. Bagambe nti Mukama omulabirizi waffe agenda ku tukungaanya oluvanyuma asalewo wakati waffe mu mazima, anti yye ye mutaawuluza asinga, omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(34,27,'34:27','. Bagambe nti mundage abo be munnyungako ne mubangattako, nedda tebaliiyo wabula yye ye Katonda nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(34,28,'34:28','. Era tetwakutuma okugyako nga oli (mubaka) awa amawulire ag''essanyu (eri abakkiriza) era omutiisa (eri abatakkiriza) naye abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(34,29,'34:29','. Era nebagamba nti eyo endagaano eribaawo ddi bwe muba nga mwogera mazima!.');
INSERT INTO "african_dev" VALUES(34,30,'34:30','. Bagambe nti mulina endagaano y’olunaku lwe mutalisobola kusaba museeseetulwe ku lwo wadde essaawa emu era nga bwe mutalisobola kusaba lubaleeterwe mangu (ne mukifuna).');
INSERT INTO "african_dev" VALUES(34,31,'34:31','. Abo abaakaafuwala ne bagamba nti: tetujja kukkiriza Kur’ani eno wadde ebyo ebyagikulembera naye singa osobola okulaba ekiseera abeeyisa obubi lwe baliyimirizibwa mu maaso ga Mukama omulabirizi waabwe abamu ekigambo balikissa ku bannaabwe (ekyabasuula mu buzibu), abo abaali batwalibwa okuba aba wansi baligamba abo abeetwala okuba aba waggulu nti singa temwali mmwe twandibadde bakkirizza.');
INSERT INTO "african_dev" VALUES(34,32,'34:32','. Abo abaatwalibwanga okuba aba waggulu baligamba abo abaatwalibwanga okuba aba wansi nti: abaffe, ffe twabalemesa obulungamu bwe bwali nga bubajjidde (si bwe guli) wabula mwali bantu aboonoonyi.');
INSERT INTO "african_dev" VALUES(34,33,'34:33','. N''abo abaatwalibwanga okuba aba wansi baligamba abo abeetwalanga okuba aba waggulu nti (si bwe kiri), wabula ezo zaali nkwe ze mwasalanga ekiro n’emisana bwe mwatulagiranga tujeemere Katonda netumussaako ba katonda abalala, olwo nno balikweka munda okwejjusa kebaliraba ebibonerezo era tugenda kussa enjegere mu nsingo zaabo abaakaafuwala, ye ate balisasulwa okugyako ebyo bye baakola.');
INSERT INTO "african_dev" VALUES(34,34,'34:34','. Era tetutumanga mu kitundu kyonna mutiisa okugyako nga abawangaala obulamu obw''okwejalabya nga bagamba nti mazima ffe tuwakanya ebyo bye mutumiddwa nabyo.');
INSERT INTO "african_dev" VALUES(34,35,'34:35','. Era ne bagamba nti ffe tusinga (mmwe) emmaali n’abaana era ffe tetugenda kubonerezebwa.');
INSERT INTO "african_dev" VALUES(34,36,'34:36','. Bagambe (ggwe Nabbi Muhammad nti) mazima Mukama omulabirizi wange ayanjuluriza eby''enfuna oyo gwaba ayagadde ate n’abifundiza gwaba ayagadde, naye ddala abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(34,37,'34:37','. Era emmaali ya mmwe n’abaana ba mmwe si bye biyamba okusembera gye tuli okugyako (ekiyamba) ye muntu akkiriza naakola emirimu emirungi. Abo nno balifuna empeera enkubiseemu olw''ebyo bye baakola era bagenda kutuula ntende mu nyumba ensitufu.');
INSERT INTO "african_dev" VALUES(34,38,'34:38','. N''abo abassa amaanyi mu kulemesa ebigambo byaffe bagenda kuleetebwa eri bibonerezo');
INSERT INTO "african_dev" VALUES(34,39,'34:39','. Bagambe nti mazima Mukama omulabirizi wange ayanjuluza riziki eri oyo gwaba ayagadde mu baddu be ate naagifundiza (gwaba ayagadde), nabuli kintu kyonna kye muwaayo mazima yye akizzaawo era bulijjo yye yaasinga abagabirizi.');
INSERT INTO "african_dev" VALUES(34,40,'34:40','. Era mujjukire olunaku lwalibakungaanya bonna oluvanyuma naagamba ba Malayika nti: abaffe abo be baasinzanga mmwe.');
INSERT INTO "african_dev" VALUES(34,41,'34:41','. Baligamba nti wasukkuluma, ggwe Mukama waffe so ssi bo. Wabula bo baasinzanga majinni, abasinga obungi gebakkiririzangamu.');
INSERT INTO "african_dev" VALUES(34,42,'34:42','. (Katonda aligamba nti) kale nno olwa leero abamu ku mmwe tebagenda kuba na busobozi kugasa bannaabwe oba okubatuusaako akabi era tuligamba abo abeeyisa obubi nti mukombe ku bibonerezo by''omuliro ogwo gwe mwali mulimbisa.');
INSERT INTO "african_dev" VALUES(34,43,'34:43','. Ebigambo byaffe ebinnyonnyofu bwe bibasomerwa bagamba nti tali ono okugyako musajja ayagala okubajja ku ebyo bakadde ba mmwe bye baali basinza, era nebagamba nti (Kur’ani jazze nayo) eno teri okugyako bulimba obujingirire ate bo abaakaafuwala amazima bwe gamala okubajjira bagamba nti tebiri bino okugyako ddogo ery''olwatu.');
INSERT INTO "african_dev" VALUES(34,44,'34:44','. (Boogera ebyo naye nga) tetubawanga bitabo nti bye basoma era nga tetubatumiranga mutiisa yenna oluberyeberye lwo.');
INSERT INTO "african_dev" VALUES(34,45,'34:45','. Era abo abaaliwo oluberyeberye lwabwe baalimbisa (abaka baffe) ate nga (abantu b''e Makkah) tebaaweza kimu kya kkumi kye twawa bali, ate olwo nno nebalimbisa ababaka bange, naye nnababonereza ngeriki?.');
INSERT INTO "african_dev" VALUES(34,46,'34:46','. Bagambe (ggwe Nabbi Muhammad) nti mbabuulirira n’ekintu kimu kyokka nakyo kwe kuba nga muyimirira ku lwa Katonda babiri babiri oba kinnoomu oluvanyuma mulowoorereze (mujja kutegeera) nti munnammwe (Muhammad) ssi mulalu tali yye okugyako okuba nti mutiisa gye muli (nga abatiisa) ebibonerezo eby''amaanyi bye mwolekedde.');
INSERT INTO "african_dev" VALUES(34,47,'34:47','. Bagambe nti ssibasaba mpeera yonna (empeera yonna eri emu) nayo ya mmwe, empeera eyange teri okugyako ku Katonda yekka. Era yye ku buli kintu (abeerawo) nga kikolebwa.');
INSERT INTO "african_dev" VALUES(34,48,'34:48','. Bagambe nti mazima Mukama omulabirizi wange akozesa amazima najjawo obulimba amanyidde ddala ebyekusifu.');
INSERT INTO "african_dev" VALUES(34,49,'34:49','. Bagambe nti obutuufu buzze era obutali butuufu tebuyinza kutandika nate era ssi bwa kudda.');
INSERT INTO "african_dev" VALUES(34,50,'34:50','. Bagambe nti: bwemba mbuze mazima mba mbuze ku lwange naye bwennungama kiba kivudde ku bubaka Mukama omulabirizi wange bwantumira, mazima yye muwulizi nnyo ali kumpi ddala.');
INSERT INTO "african_dev" VALUES(34,51,'34:51','. Singa oliraba mu kiseera abakaafiiri bwe balitya (ku lunaku lw''enkomerero) tewaliba buddukiro era balikwatibwa nga bajjibwa mu kifo eky''okumpi ddala.');
INSERT INTO "african_dev" VALUES(34,52,'34:52','. Era baligamba nti tubikkiriza (ebyo Nabbi byagamba) naye wwa gye balijja okukkiriza nga bamaze okukuleka mu kifo eky''ewala (gye bavudde).');
INSERT INTO "african_dev" VALUES(34,53,'34:53','. Mazima baabiwakanya oluberyeberye, era baatemerera obulimba ku bigambo ebyekusifu ebiri mu kifo eky''ewala.');
INSERT INTO "african_dev" VALUES(34,54,'34:54','. Newasiikirizibwa wakati waabwe ne wakati w''ekyo kye baagala nga bwe kyakolebwa oluberyeberye (ku abo) abalinga bo mazima bo bali mu kubuusabuusa okuleeta okutankana (ne batakkiriza).');
INSERT INTO "african_dev" VALUES(35,1,'35:1','. Ebitendo byonna bya Katonda omutonzi w''eggulu omusanvu n''ensi, eyafuula ba Malayika ababaka abalina ebiwawaatiro, ow''ebibiri n''ow''ebisatu n''ow''ebina ayongera mu kutonda ekyo kyaba ayagadde mazima Katonda muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(35,2,'35:2','. Okusaasira Katonda kwaba agguliddewo abantu tewali ayinza kukulemesa, ate okwo kwalemesa tewali ayinza kukuleeta atali yye, era yye ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(35,3,'35:3','. Abange mmwe abantu mujjukire ekyengera kya Katonda kye yabawa, abaffe waliyo omutonzi atali Katonda abagabirira okuva waggulu ne mu nsi, tewali kisinzibwa kyonna okugyako yye, olwo nno ate muwugulwa mutya!.');
INSERT INTO "african_dev" VALUES(35,4,'35:4','. Bwe bakulimbisa mazima ababaka baalimbisibwa oluberyeberyelwo, era nga bulijjo ewa Katonda ensonga zonna gyezizzibwa.');
INSERT INTO "african_dev" VALUES(35,5,'35:5','. Abange mmwe abantu mazima endagaano ya Katonda ya mazima, obulamu bwensi tebubagayaazanga era ebigayaaza byonna tebibajjanga ku Katonda.');
INSERT INTO "african_dev" VALUES(35,6,'35:6','. Mazima Sitane mulabe wa mmwe kale (na mmwe) mumufuule mulabe. Anti mazima akoowoola ekibiinakye balyoke babeere mu bantu bo mu muliro Sa-iri.');
INSERT INTO "african_dev" VALUES(35,7,'35:7','. Abo abaakaafuwala balina ebibonerezo ebikakali ate abo abakkiriza nebakola emirimu emirungi balina ekisonyiwo n''empeera ensuffu.');
INSERT INTO "african_dev" VALUES(35,8,'35:8','. Abaffe oyo ogwawundibwa omulimu gwe omubi naagulaba nga mulungi (ayinza okufaanana oyo omulungamu) mazima Katonda abuza gwaba ayagadde naalungamya gwaba ayagadde, kale toggwamu mwoyo ku lw''okubasalirwa, mazima Katonda amanyidde ddala ebyo bye bakola.');
INSERT INTO "african_dev" VALUES(35,9,'35:9','. Katonda yooyo asindika empewo nezitambuza ebire (eby''enkuba, enkuba eyo) netunywekereza nayo ensi enfu (enkalu) olwo nno netuzza nayo mu bulamu ettaka eriba lifudde nabwekityo okuzuukira (bwe kuliba).');
INSERT INTO "african_dev" VALUES(35,10,'35:10','. Oyo yenna ayagala ekitiibwa, ebitiibwa byonna biri wa Katonda. Gyali ebigambo ebirungi gye byambuka n’emirimu emirongoofu agisitula ate abo abakola enkwe okukola ebibi balina ebibonerezo ebikakali era enkwe zaabo zeezigwa obutaka.');
INSERT INTO "african_dev" VALUES(35,11,'35:11','. Era Katonda yabakola (mmwe) nga abajja mu ttaka oluvanyuma abajja mu mazzi agazaala oluvanyuma yabatonda nga muli bibiri bibiri (ekisajja n’ekikazi). Tewali kikazi kifuna lubuto wadde ekizaala okugyako nga amanyi, tewali kiwangaazibwa mu biwangaazibwa era tewali kikendezebwa ku buwangaazi bwakyo okugyako nga kiri mu kitabo (nga Katonda yakisalawo dda) mazima ekyo kyangu nnyo ku Katonda.');
INSERT INTO "african_dev" VALUES(35,12,'35:12','. Amazzi ag''ennyanja ebbiri tegayinza kufaanagana, nga (emu) amazzi gaayo malungi mawoomu ganyweka, nga ate endala ya mazzi ag’omunnyu lukalabule, ate nga mulya mu buli emu ku zo ebyenyanja ebibisi era mujjamu eby''okwewunda bye mwambala era olaba amaato mu zo nga gakola amakubo olwo nno musobole okunoonya mu bigabwabye olwo nno mubeere abeebaza.');
INSERT INTO "african_dev" VALUES(35,13,'35:13','. Ayingiza ekiro mu musana era naayingiza omusana mu kiro era yagonza enjuba n''omwezi nga buli kimu kya kutambula okumala ebbanga eggere oyo nno ye Katonda Mukama omulabirizi wa mmwe nannyini buyinza bwonna ate abo bemusaba abatali yye tebalina buyinza wadde obwenkana akakuta k''empeke.');
INSERT INTO "african_dev" VALUES(35,14,'35:14','. Bwe mubasaba tebawulira kusaba kwa mmwe ate (katugambe) nti bawulidde tebasobola kubaanukula era nga ne ku lunaku lw''enkomerero bagenda kwegaana engeri gye mubagatta ku Katonda, (ayagala ategeere) anti tewali ayinza kukutegeeza kufaanana nga oyo amanyi ekintu nemunda waakyo.');
INSERT INTO "african_dev" VALUES(35,15,'35:15','. Abange mmwe abantu mmwe beetaavu eri Katonda ate yye Katonda y''atalina bwetaavu atenderezebwa ennyo.');
INSERT INTO "african_dev" VALUES(35,16,'35:16','. (N''olwekyo) singa aba ayagadde asobola okubajjawo naaleeta ebitonde ebipya.');
INSERT INTO "african_dev" VALUES(35,17,'35:17','. Era ekyo ku Katonda si kinene.');
INSERT INTO "african_dev" VALUES(35,18,'35:18','. Tewali mwoyo mwonoonyi gugenda kwetikka kibi kya mwoyo mwonoonyi mulala, era oguliba guzitoowereddwa bwe gulisaba okugwetikkirako tebaligwetikkirako kintu kyonna ,(gwe gusaba) nebwaliba wa luganda olw''okumpi, mazima olabula abo abatya Mukama omulabirizi waabwe, awamu n''obutamulabako era nebayimirizaawo e sswala. N''oyo yenna eyeetukuza mazima yeetukuza ku lulwe era eri Katonda yeeri obuddo.');
INSERT INTO "african_dev" VALUES(35,19,'35:19','. Era muzibe n''alaba tebenkana.');
INSERT INTO "african_dev" VALUES(35,20,'35:20','. Wadde ebizikiza n''ekitangaala.');
INSERT INTO "african_dev" VALUES(35,21,'35:21','. Wadde ekisiikirize tekyenkana na ppereketya wa musana.');
INSERT INTO "african_dev" VALUES(35,22,'35:22','. Era abalamu n''abafu tebenkana, mazima Katonda awuliza oyo gwaba ayagadde era ggwe (Muhammad) tosobbola kuwuliza abo abali mu kabbuli.');
INSERT INTO "african_dev" VALUES(35,23,'35:23','. Ggwe toli okugyako okuba omutiisa.');
INSERT INTO "african_dev" VALUES(35,24,'35:24','. Mazima ffe twakutuma mu butuufu nga oli musanyusa (eri abakkiriza) era nga oli mutiisa (eri abatakkiriza) era tewali bantu bonna okugyako nga omutiisa yabatuukamu.');
INSERT INTO "african_dev" VALUES(35,25,'35:25','. Naye bwe bakulimbisa mazima n''abo abaabakulembera baalimbisa ababaka baabwe baabajjira n’obunnyonnyofu n''ebiwandiiko n''ebitabo ebitangaaza.');
INSERT INTO "african_dev" VALUES(35,26,'35:26','. Oluvanyuma nnakwata abo abaakaafuwala kale okubonereza kwange kwali kutya.');
INSERT INTO "african_dev" VALUES(35,27,'35:27','. Abaffe tolaba nti Katonda assa amazzi okuva waggulu netumeza nago ebibala eby''amabala ag''enjawulo nga ne mu nsozi mulimu amakubo ameeru n''amamyufu nga amabala gaago maawukamu na''maddugavu ttibittibi.');
INSERT INTO "african_dev" VALUES(35,28,'35:28','. Mu ngeri y''emu ne mu bantu n''ensolo n’ebisolo ebirundibwa awaka amabala gaabyo ga njawulo mazima ddala abamanyi be batya Katonda mu baddube, mazima Katonda ye nantakubwa ku mukono omusonyiyi ennyo.');
INSERT INTO "african_dev" VALUES(35,29,'35:29','. Mazima abo abasoma ekitabo kya Katonda (Kur’ani) ne bayimirizaawo e sswala era ne bawaayo mu kyama ne mu lwatu ku ebyo bye tubagabirira basuubira eby''obusuubuzi ebitadiba.');
INSERT INTO "african_dev" VALUES(35,30,'35:30','. Ate alyoke abawe mu bujjuvu empeera zaabwe era abongeze mu birungibye anti mazima yye, musonyiyi nnyo omwebaza.');
INSERT INTO "african_dev" VALUES(35,31,'35:31','. N''obubaka obwo bwe twakutumira mu kitabo (Kur’ani) go ge mazima nga gakakasa ebyo ebyagakulembera. Mazima Katonda amanyidde ddala eby''omunda era mulabi nnyo.');
INSERT INTO "african_dev" VALUES(35,32,'35:32','. Oluvanyuma twasikiza ekitabo abo be twalondoba mu baddu baffe olwo nno nemuba mu bo eyeeyisa obubi yennyini nga bwe muli mu bo ow''omumakkati era nga mu bo mulimu assa essira mu kukola ebirungi (ebya Faradha ne Sunna) ku lw''obuyinza bwa Katonda obwo nno bwe bulungi obusuffu.');
INSERT INTO "african_dev" VALUES(35,33,'35:33','. (Nga) z''ejjana ez''emirembe ze baliyingira. Balinaanikibwa nga bali mu zo ebikomo ebya zzaabu ne Luulu, era ebyambalo byabwe nga bali mu yo biriba bya Sirik.');
INSERT INTO "african_dev" VALUES(35,34,'35:34','. Era ne bagamba nti ebitendo byonna bya Katonda oyo atumazeeko ennaku, mazima Mukama omulabirizi waffe musonyiyi nnyo era yeebaza.');
INSERT INTO "african_dev" VALUES(35,35,'35:35','. Oyo atutadde mu nyumba ey''olubeerera nga kugaba kwe ennaku ssi yaakututuukako nga tuli mu yo, era nga tuli mu yo bwe tutagenda kutuukwako kukoowa.');
INSERT INTO "african_dev" VALUES(35,36,'35:36','. Ate abo abaakaafuwala balifuna omuliro Jahannama, tebagenda kusalirwawo nti (bafe) olwo nno ne baba nga bafa era tebagenda kukendezebwa ku bibonerezo byagwo bwe tutyo bwe tusasula buli ntasiima.');
INSERT INTO "african_dev" VALUES(35,37,'35:37','. Nabo bagenda kuba nga baleekaanira mu gwo (nga bagamba nti) ayi Mukama omulabirizi waffe tufulumye (otuzze ku nsi) tukole emirimu emirungi egitali egyo gye twali tukola luli, (baligambibwa nti) abaffe tetwabawangaaza ebbanga lyasobola okujjukiriramu oyo ajjukira era naabajjira omutiisa (kye musaba temujja kukifuna) kale mukombe ku bukaawu bw''ebibonerezo, abeeyisa obubi tebayinza kuba na mutaasa yenna.');
INSERT INTO "african_dev" VALUES(35,38,'35:38','. Mazima Katonda amanyi ebyekusifu byo mu ggulu omusanvu n''ensi mazima ddala yye amanyidde ddala ebiri mu bifuba.');
INSERT INTO "african_dev" VALUES(35,39,'35:39','. Ye yooyo eyabafuula (mmwe) abadda mu bigere bya bannaabwe mu nsi, oyo akaafuwala obukaafiiri bwe bukosa yye, era abakaafiiri obukaafiiri bwabwe tebubongera ewa Mukama omulabirizi waabwe okugyako okukyayibwa era abakaafiiri obukaafiiri bwabwe tebubongera okugyako okufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(35,40,'35:40','. Bagambe nti mulaba abo bemungattako abo bemusaba nemuleka Katonda (kale) mundage kiki kye batonda ku nsi, oba abaffe balina omugabo mu (kukolebwa) kw''eggulu omusanvu oba (si ekyo) nga twabawa ekitabo olwo nno nebaba nga balina obujulizi okuva mu kyo (obubalagira okukola shiriki oyo) nedda abeeyisa obubi abamu mu bo tebalagaanyisa bannaabwe okugyako ebigayaaza.');
INSERT INTO "african_dev" VALUES(35,41,'35:41','. Mazima Katonda awanirira eggulu omusanvu n''ensi bibe nga tebivaawo era singa bivaawo teyandibiwaniridde omulala yenna oluvanyuma lwe anti mazima yye wa kisa musonyiyi nnyo.');
INSERT INTO "african_dev" VALUES(35,42,'35:42','. (Abakaafiiri be Makkah) baalayira Katonda mu kukakasa ebirayiro byabwe (ne bagamba nti) singa omutiisa alibajjira bagenda kubeerera ddala abasinga obulungamu okusinga (abantu bonna abaali babaddewo), awo nno omutiisa (Muhammad) bwe yabajjira tekyabongera okugyako okwesamba (omubaka).');
INSERT INTO "african_dev" VALUES(35,43,'35:43','. (Nga ekyo kiva) ku kwekuluntaza (kwa bwe) mu nsi, n''okukolala enkwe embi, enkwe embi tezituusa kabi okugyako ku ba nannyini zo olwo abaffe balina kyebalindirira okugyako enkola eyatuuka ku baabakulembera ate nga enkola ya Katonda (mu kubonereza abajeemu) tebaamu kukyusibwa era nga enkola ya Katonda toyinza kugisangamu kujjululwa.');
INSERT INTO "african_dev" VALUES(35,44,'35:44','. Abaffe tebatambula mu nsi olwo nno nebalaba enkomerero yaabo abaabakulembera nga bwe yali ate nga baali ba maanyi okubasinga, kikafuuwe ekintu kyonna mu ggulu omusanvu ne mu nsi okulemesa Katonda anti mazima yye bulijjo mumanyi nnyo muyinza.');
INSERT INTO "african_dev" VALUES(35,45,'35:45','. Singa Katonda yali avunaana abantu olw''ebyo bye baba bakoze teyaalirese ku ngulu kwayo (ensi) kitambula kyonna naye abalindiriza okutuusa ekiseera ekigere olwo nno entuuko za bwe bwe zituuka (buli muntu nazzibwa eri ekifo ekimusaanira) anti Katonda bulijjo alabira ddala ebikwata ku baddube.');
INSERT INTO "african_dev" VALUES(36,1,'36:1','. Yaasin.');
INSERT INTO "african_dev" VALUES(36,2,'36:2','. Ndayidde Kur’ani (eno) ejjudde ebigambo eby''amagezi.');
INSERT INTO "african_dev" VALUES(36,3,'36:3','. Mazima ggwe (Muhammad) oli wa mu babaka.');
INSERT INTO "african_dev" VALUES(36,4,'36:4','. (Nga oli) ku kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(36,5,'36:5','. (Kur’ani eno) yassibwa nantakubwa ku mukono omusaasizi.');
INSERT INTO "african_dev" VALUES(36,6,'36:6','. Obe nga otiisa abantu bakadde baabwe abataatiisibwa olwo nno nebaba abeesuulirayo ogwa naggamba (ku bikwata ku Katonda).');
INSERT INTO "african_dev" VALUES(36,7,'36:7','. Mazima kikakase ekigambo (eky''okubonerezebwa) ku basinga obungi mu bo nebaba nga tebagenda kukkiriza.');
INSERT INTO "african_dev" VALUES(36,8,'36:8','. Mazima ffe twassa mu nsingo zaabwe enkoligo nga nazo zituukira ddala ku bulevu, olwo nno nebaba nga balalambadde.');
INSERT INTO "african_dev" VALUES(36,9,'36:9','. Netussa mu maaso gaabwe ekiziyiza netussa n''ekiziyiza (ekirala) emabega waabwe, olwo nno netubabikka nga bo tebayinza kulaba.');
INSERT INTO "african_dev" VALUES(36,10,'36:10','. Era kyekimu ku bo obatiisizza oba tobatiisizza si baakukkiriza.');
INSERT INTO "african_dev" VALUES(36,11,'36:11','. Mazima otiisa oyo agoberera okubuulirira era n''atya (Katonda) omusaasizi ennyo awamu n''okuba nga talabika kale nno musanyuse n''amawulire g’ekisonyiwo n''empeera eyeesiimisa.');
INSERT INTO "african_dev" VALUES(36,12,'36:12','. Mazima ffe tuliramusa abafu era tuwandiika ebyo bye baakulembeza ne bye baagoberezaako nga na buli kintu twakikomeka mu kitabo e kikulu e kigenda okulaga buli kimu.');
INSERT INTO "african_dev" VALUES(36,13,'36:13','. Bakubire ekifaanayi ky''abantu bo mu kitundu ekimu bwe bajjirwa ababaka.');
INSERT INTO "african_dev" VALUES(36,14,'36:14','. Mu kiseera bwe twatumira gye bali ababaka babiri ne babalimbisa, olwo nno netubongera amaanyi n’owokusatu (bonsatule) nebagamba nti mazima ffe tutumiddwa gye muli.');
INSERT INTO "african_dev" VALUES(36,15,'36:15','. (Abo mu kitundu ekyo) ne bagamba nti: mmwe temuli okugyako (okuba) bantu bannaffe era Katonda omusaasizi ennyo tassanga kintu kyonna temuli mmwe okugyako okuba nga mulimba (bulimbi).');
INSERT INTO "african_dev" VALUES(36,16,'36:16','. (Ababaka) nebagamba nti Mukama omulabirizi waffe omanyi nti mazima ddala ffe tutumiddwa gye muli.');
INSERT INTO "african_dev" VALUES(36,17,'36:17','. Era tetuvunaanyizibwa okugyako okutuusa obubaka mu bunnyonnyofu.');
INSERT INTO "african_dev" VALUES(36,18,'36:18','. (Abo mu kitundu) nebagamba nti mazima ffe tutya okutuukibwako obuzibu ku lwa mmwe, bwe muteekomeko tujja kubakasukira amayinja era ebibonerezo ebiruma ennyo bijja kubatuukako nga biva gye tuli.');
INSERT INTO "african_dev" VALUES(36,19,'36:19','. (Ababaka) ne bagamba nti okutya kwa mmwe okutuukibwako obuzibu mwe mukyereetedde (bwe mugaanye okuwuliriza obubaka bwe muleeteddwa) wabula mmwe muli bantu abayitirivu mu kwonoona.');
INSERT INTO "african_dev" VALUES(36,20,'36:20','. olwo nno omusajja najja nga ava ku nkomerero y’ekibuga nga ayanguwa naagamba nti: abange bantu bange mugoberere ababaka.');
INSERT INTO "african_dev" VALUES(36,21,'36:21','. Mugoberere abo abatabasaba mpeera ate nga bo balungamu.');
INSERT INTO "african_dev" VALUES(36,22,'36:22','. Nina ki okuba nti sisinza oyo eyantonda, ate nga gyali gye mulizzibwa!.');
INSERT INTO "african_dev" VALUES(36,23,'36:23','. Ndeke Katonda neeteerewo ebisinzibwa ebirala, so nga singa Katonda omusaasizi ennyo anjagaliza akabi okuwolereza kwabyo tekwandingasizza kintu kyonna era tebiyinza kunziruukirira.');
INSERT INTO "african_dev" VALUES(36,24,'36:24','. (Singa nkikola) mazima nze olwo njakuba mu bubuze obweyolefu.');
INSERT INTO "african_dev" VALUES(36,25,'36:25','. Mazima nze nzikiriza Mukama omulabirizi wa mmwe kale mumpulirize.');
INSERT INTO "african_dev" VALUES(36,26,'36:26','. Naagambibwa (nga amaze okufa) nti yingira e jjana, naagamba nti kale singa abantu bange bamanyi.');
INSERT INTO "african_dev" VALUES(36,27,'36:27','. Ekyo Mukama omulabirizi wange kyansonyiyidde era nanteeka mwabo abagabulwa (olwaleero).');
INSERT INTO "african_dev" VALUES(36,28,'36:28','. Era tetwassa ku bantu be oluvanyuma lwe ggye lyonna liva mu gggulu (olw''okubazikiriza) era tetwali baakulissa.');
INSERT INTO "african_dev" VALUES(36,29,'36:29','. (Wabula okuzikirira kwabwe) tekwali okugyako lwali olubwatuka lumu, okugenda okulaba nga bo bakalu.');
INSERT INTO "african_dev" VALUES(36,30,'36:30','. Kya nnaku nnyo, ku baddu (abantu) tewali mubaka abajjira okugyako bamujeeja.');
INSERT INTO "african_dev" VALUES(36,31,'36:31','. Abaffe tebalaba nti emirembe emeka gyetwazikiriza oluberyeberye lwabwe, nga mazima ddala bo tebagenda kudda gye bali.');
INSERT INTO "african_dev" VALUES(36,32,'36:32','. Tewali nagumu ku gyonna (emirembe) okugyako nga bonna ba kuleetebwa mu maaso gaffe.');
INSERT INTO "african_dev" VALUES(36,33,'36:33','. Ensi efudde (ekaze) kabonero gye bali, tugiwa obulamu (tutonnyesa nkuba) era netumeza mu yo empeke, okwo nno kwe balya.');
INSERT INTO "african_dev" VALUES(36,34,'36:34','. Netuteeka mu yo amalimiro ag’entende n''e Mizabibu era netufukula mu yo ensulo.');
INSERT INTO "african_dev" VALUES(36,35,'36:35','. Babe nga balya ku bibala byayo ne Ku ebyo emikono gyabwe bye gikola, abaffe tebeebaza!.');
INSERT INTO "african_dev" VALUES(36,36,'36:36','. Musukkulumu oyo eyatonda ebimera mu nsi ne mubo bennyini (abantu) ne bye batamanyi, nga byonna biba ekisajja n''ekikazi.');
INSERT INTO "african_dev" VALUES(36,37,'36:37','. Era ekiro kabonero gye bali anti tukijjako obudde obw''emisana ogenda okulaba nga bali mu nzikiza.');
INSERT INTO "african_dev" VALUES(36,38,'36:38','. Ne njuba etambulira ku biragiro ebyagiweebwa, okwo kwe kugera kwa (Katonda) nantakubwa ku mukono amanyi ennyo.');
INSERT INTO "african_dev" VALUES(36,39,'36:39','. Nga n''omwezi (kabonero gye bali), twagugerera ebifo (mwe gutambulira) okutuusa lwe guddayo neguba nga ekikolokomba ekikadde.');
INSERT INTO "african_dev" VALUES(36,40,'36:40','. Tekisoboka enjuba kusisinkana mwezi era n''ekiro tekigenda kukulembera misana na buli kimu ku byombi kiri mu bbanga kiwuga.');
INSERT INTO "african_dev" VALUES(36,41,'36:41','. Era kabonero gye bali (abantu), mazima ffe twatambuliza bannaabwe mu maato agakubyeko.');
INSERT INTO "african_dev" VALUES(36,42,'36:42','. Era tubatondera agalinga go gebasaabaliramu.');
INSERT INTO "african_dev" VALUES(36,43,'36:43','. Singa twagadde tuyinza okubazikiriza ne batasobola kwekubira nduulu, era nga tebayinza kuddukirirwa.');
INSERT INTO "african_dev" VALUES(36,44,'36:44','. (Tewali kibaawo) okugyako okusaasira okuva gye tuli ne ku lw''okubeeyagaza okumala ekiseera ekigere.');
INSERT INTO "african_dev" VALUES(36,45,'36:45','. Bwe baba bagambiddwa nti mutye ebyo ebiri mu maaso ga mmwe n''ebyo ebiri emabega wa mmwe, olwo nno mube nga musaasirwa (beesuulirayo gwa naggamba).');
INSERT INTO "african_dev" VALUES(36,46,'36:46','. Anti tewali kabonero mu bubonero bwa Mukama omulabirizi waabwe kaabajjira okugyako nga baakaawukanako.');
INSERT INTO "african_dev" VALUES(36,47,'36:47','. Era bwe baba bagambiddwa nti mugabe ku ebyo Katonda bye yabawa abo abaakaafuwala bagamba abo abakkiriza nti abaffe tuliise oyo singa Katonda yayagala yaali muliisizza, temuli mmwe okugyako okuba mu bubuze obw''olwatu.');
INSERT INTO "african_dev" VALUES(36,48,'36:48','. Era bagamba nti endagaano eyo eribaawo ddi bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(36,49,'36:49','. Tebalinda okugyako olubwatuka lumu olulibakwata nga nabo bali mu kukaayana.');
INSERT INTO "african_dev" VALUES(36,50,'36:50','. Olwo nno tebagenda kulaama wadde okudda eri abantu baabwe (eka).');
INSERT INTO "african_dev" VALUES(36,51,'36:51','. Olwo engombe erifuuyibwa (olufuuwa olw''okubiri) oligenda okubalaba nga bava mu ntaana zaabwe nga bagenda eri Mukama omulabirizi waabwe nga baanguwa byansusso.');
INSERT INTO "african_dev" VALUES(36,52,'36:52','. Balyoke bagambe (nga bakuba ebiwoobe) nti nga tuzikiridde ani atuzuukizza okutujja gye tubadde twebase (mu ntaana zaffe bagenda kwanukulwa nti) ekyo kyekyo Katonda Omusaasizi ennyo kye yalagaanyisa n''ababaka baayogera mazima.');
INSERT INTO "african_dev" VALUES(36,53,'36:53','. (Okubazuukiza) tekugenda kuba okugyako olubwatuka lumu, olwo nno bonna gye tuli gye bagenda okuleetebwa.');
INSERT INTO "african_dev" VALUES(36,54,'36:54','. Olwa leero tewali muntu agenda kuyisibwa bubi mu kintu kyonna era temugenda kusasulwa okugyako ebyo bye mwali mukola.');
INSERT INTO "african_dev" VALUES(36,55,'36:55','. Mazima abantu b''omu jjana ku lunaku olwo balibeera mu kwetala olw''amasanyu gebalibeeramu.');
INSERT INTO "african_dev" VALUES(36,56,'36:56','. Bo n''abo be baafumbiriganwa nabo bagenda kubeera mu bisiikirize nga beesigamye ebitanda ebiwunde.');
INSERT INTO "african_dev" VALUES(36,57,'36:57','. Balina mu yo buli kika kya bibala era bagenda kufuna buli kye baliba basabye.');
INSERT INTO "african_dev" VALUES(36,58,'36:58','. (Bagenda kufuna ekyengera ekisinga ebyo) nakyo salaamu nga kigambo ekiriva ewa Mukama omulabirizi waabwe ow''ekisa ekingi.');
INSERT INTO "african_dev" VALUES(36,59,'36:59','. Naye bo aboonoonyi bagenda kugambibwa nti: abange mmwe aboonoonyi olwa leero mweyawule (ku bakkiriza).');
INSERT INTO "african_dev" VALUES(36,60,'36:60','. Abange mmwe ezadde lya Adam abaffe ssaabakozesa endagaano nti: temusinzanga Sitane olw''okuba yye mulabe wa mmwe omweyolefu!.');
INSERT INTO "african_dev" VALUES(36,61,'36:61','. Era (ne mbakalaatira) nti: munsinze, eryo lye kkubo ettuufu.');
INSERT INTO "african_dev" VALUES(36,62,'36:62','. Mazima (sitane) yabuza mu mmwe ebitonde bingi nnyo, abaffe mwali temutegeera.');
INSERT INTO "african_dev" VALUES(36,63,'36:63','. Guno nno gwe muliro Jahannama gwe mwalagaanyisibwanga (ku nsi).');
INSERT INTO "african_dev" VALUES(36,64,'36:64','. Muguyingire olwa leero olw''ebyo bye mwalinga muwakanya.');
INSERT INTO "african_dev" VALUES(36,65,'36:65','. Olwa leero tujja kuteeka envumbo ku mimwa gyabwe ate gyo emikono gyabwe gyogere naffe, n''amagulu gaabwe gawe obujulizi ku ebyo bye baalinga bakola.');
INSERT INTO "african_dev" VALUES(36,66,'36:66','. Singa twayagala twandizibye amaaso gaabwe olwo nno nebaba nga banoonya okudda eri ekkubo, naye nno bandirirabye batya!.');
INSERT INTO "african_dev" VALUES(36,67,'36:67','. Era singa twayagala twandikyusizza obutonde bwabwe ne babeera mu kifo awo webali, kale nno tebandisobodde kweyongerayo wadde okudda gye bavudde.');
INSERT INTO "african_dev" VALUES(36,68,'36:68','. Oyo yenna gwe tuba tuwangaazizza tumuzzaayo n''aba munafu mu butonde, abaffe tebategeera!.');
INSERT INTO "african_dev" VALUES(36,69,'36:69','. (Nabbi Muhammad) tetumuyigirizanga kutontoma era tekimugwanira, bye yajja nabyo tebiri okugyako nga bya kujjukiza era nga Kur’ani ennyonnyola.');
INSERT INTO "african_dev" VALUES(36,70,'36:70','. (Nabbi Muhmmad) alyoke atiise oyo yenna omulamu era ekigambo (eky''ebibonerezo) kikakate ku bakaafiiri.');
INSERT INTO "african_dev" VALUES(36,71,'36:71','. Abaffe tebalaba nti mu bye twabatondera ebyakolebwa e mikono gyaffe z''ensolo bo zebafuga!.');
INSERT INTO "african_dev" VALUES(36,72,'36:72','. Era twabibagondeza, mulimu bye beebagala era nga mulimu bye balya.');
INSERT INTO "african_dev" VALUES(36,73,'36:73','. Era mu byo balina emigaso (emirala) n''eby''okunywa abaffe tebeebaza!.');
INSERT INTO "african_dev" VALUES(36,74,'36:74','. Ate abakaafiiri baleka Katonda ne beeteerawo ebisinzibwa ebirala mbu babe nga bataasibwa.');
INSERT INTO "african_dev" VALUES(36,75,'36:75','. So nga tebasobola kubataasa awamu n''okuba nti bo nabyo baliba kibinja ekirireetebwa mu muliro.');
INSERT INTO "african_dev" VALUES(36,76,'36:76','. Kale nno tebikunyiiza ebigambo bya bwe mazima ffe tumanyi ebyo bye bakweka ne bye bakola mu lwatu.');
INSERT INTO "african_dev" VALUES(36,77,'36:77','. Abaffe omuntu talaba nti mazima ffe twamutonda nga tumujja mu mazzi agazaala, ogenda okulaba nga ye muyitirivu wa kukaayana okw''olwatu.');
INSERT INTO "african_dev" VALUES(36,78,'36:78','. Naatukubira ekifaananyi neyeerabira obutonde bwe, nga agamba nti ani aliramusa amagumba nga gamaze okuba amamerengufu.');
INSERT INTO "african_dev" VALUES(36,79,'36:79','. Gamba nti agenda kugalamusa oyo eyagatandika omulundi ogwasooka era nga yye amanyidde ddala ebikwata ku buli kitonde.');
INSERT INTO "african_dev" VALUES(36,80,'36:80','. Oyo eyabateerawo omuliro okuva mu muti omubisi okugenda okulaba nga mmwe mukuma omuliro okuva mu gwo (omuti).');
INSERT INTO "african_dev" VALUES(36,81,'36:81','. Abaffe oyo eyatonda eggulu omusanvu n''ensi tasobola kutonda bintu (birala) biringa byo, weewaawo (asobolera ddala) era ddala yye ye mutonzi omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(36,82,'36:82','. Mazima ekiragirokye bwaba ayagadde ekintu (kibeewo) agamba bugambi nti bba ne kiba.');
INSERT INTO "african_dev" VALUES(36,83,'36:83','. N’olwekyo musukkulumu nnyo oyo mu mikono gye mwokka mwemuli obufuzi bwa buli kintu ate nga gyali yokka gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(37,1,'37:1','Ndayidde ba Malayika abasimbye ennyiriri ennambulukufu.');
INSERT INTO "african_dev" VALUES(37,2,'37:2','Olwo nno abo abagoba ebire olugoba (bigende bitonnyese enkuba).');
INSERT INTO "african_dev" VALUES(37,3,'37:3','N''abo abasoma ebigambo ebibuulirira.');
INSERT INTO "african_dev" VALUES(37,4,'37:4','Mazima Katonda wa mmwe ddala ali omu.');
INSERT INTO "african_dev" VALUES(37,5,'37:5','Mukama omulabirizi we ggulu omusanvu n''ensi n''ebyo ebiri wakati wa byombi era Mukama omulabirizi w''ebuvanjuba (n''e bugwanjuba).');
INSERT INTO "african_dev" VALUES(37,6,'37:6','Mazima ffe twawunda eggulu ly''okunsi n''okulabika obulungi kw''e mmunyeenye (ezirijjudde).');
INSERT INTO "african_dev" VALUES(37,7,'37:7','N''okulikuuma ku buli Sitane kyewaggula.');
INSERT INTO "african_dev" VALUES(37,8,'37:8','Tezisobola kuwuliriza bitonde ebya waggulu era zikanyugirwa ebitawuliro okuva ku buli luuyi.');
INSERT INTO "african_dev" VALUES(37,9,'37:9','Nga zifuumuulwa emisinde era nga zigenda kufuna ebibonerezo ebitakoma.');
INSERT INTO "african_dev" VALUES(37,10,'37:10','Okugyako (Sitane) eyo enyakula ekigambo kyeba enyakudde olwo nno neekasukirirwa ekitawuliro ekibumbujja.');
INSERT INTO "african_dev" VALUES(37,11,'37:11','Kale nno babuuze nti bo be bazibu mu kutonda oba ebyo bye twatonda, mazima ffe (bo) twabatonda mu budongo obukwafu.');
INSERT INTO "african_dev" VALUES(37,12,'37:12','Wabula gwe weewuunya (ng’okungiriza) ate nga bo bajeeja bujeeja.');
INSERT INTO "african_dev" VALUES(37,13,'37:13','Era nga bwe baba babuuliriddwa tebeebuulirira.');
INSERT INTO "african_dev" VALUES(37,14,'37:14','Era bwe balaba akabonero konna (mu bubonero bwa Katonda) nga bajeeja.');
INSERT INTO "african_dev" VALUES(37,15,'37:15','Era nebagamba nti: kino tekiri okugyako eddogo eryeyolefu.');
INSERT INTO "african_dev" VALUES(37,16,'37:16','(Nebongerako nti) bwe tuliba tufudde netufuuka ettaka era netuba magumba ameereere abaffe tulizuukizibwa!.');
INSERT INTO "african_dev" VALUES(37,17,'37:17','Oba ba kitaffe abaakulembera.');
INSERT INTO "african_dev" VALUES(37,18,'37:18','Gamba nti yee (mulizuukizibwa) nga nammwe mukkakkanyiziddwa.');
INSERT INTO "african_dev" VALUES(37,19,'37:19','Mazima kyo (ekyo kuzuukira) luliba olufuuwa mu ngombe lumu oligenda okulaba nga bo batunula.');
INSERT INTO "african_dev" VALUES(37,20,'37:20','Era baligamba nti zitusanze luno lwe lunaku lw''okusasulwa.');
INSERT INTO "african_dev" VALUES(37,21,'37:21','(Ba Malayika baligamba nti) luno lwe lunaku lw''okwawula (wakati wa balongoofu n’aboonoonyi) olwo lwe mwali mulimbisa.');
INSERT INTO "african_dev" VALUES(37,22,'37:22','(Ba Malayika baliragirwa nti) mukungaanye abo abeeyisa obubi wamu n''ababafaanana n''ebyo bye baasinzanga.');
INSERT INTO "african_dev" VALUES(37,23,'37:23','Nebava ku Katonda olwo nno mubalage ekkubo eridda eri omuliro Jahiimu.');
INSERT INTO "african_dev" VALUES(37,24,'37:24','Era nga (temunnabatwala) mubayimirize (mu bbaliro) mazima bo balina okubuuzibwa.');
INSERT INTO "african_dev" VALUES(37,25,'37:25','(Olwo nno bagambibwe) nti: mubaddeki lwaki temwetaasa!.');
INSERT INTO "african_dev" VALUES(37,26,'37:26','Wabula bo ku lunaku olwa leero bagenda kukkiriza (nti tebalina bya kukola kuvvuunuka buzibu bwe balibeeramu).');
INSERT INTO "african_dev" VALUES(37,27,'37:27','Abamu ne boolekera bannaabwe nga bawaanyisiganya ebigambo.');
INSERT INTO "african_dev" VALUES(37,28,'37:28','(Abagoberezi) ne bagamba (bebaagoberera) nti mazima mmwe mwalinga mutujjira nga mutuyita ku ddyo.');
INSERT INTO "african_dev" VALUES(37,29,'37:29','(Abaagobererwa) baligamba nti nedda wabula temwali bakkiriza.');
INSERT INTO "african_dev" VALUES(37,30,'37:30','Era tetwabalinaako buyinza bwonna wabula mwali bantu abeewagguzi.');
INSERT INTO "african_dev" VALUES(37,31,'37:31','Olwo nno ekigambo kya Mukama omulabirizi waffe nekitukakatako (fenna) mazima ddala ffe tuteekwa kukomba (ku bibonerezo).');
INSERT INTO "african_dev" VALUES(37,32,'37:32','Twababuza era mazima ddala ffe (okugenda okubabuza) twali babuze.');
INSERT INTO "african_dev" VALUES(37,33,'37:33','Mazima bo (ab''emirundi ebiri) bagenda kwegatta mu kibonerezo.');
INSERT INTO "african_dev" VALUES(37,34,'37:34','Mazima ffe bwe tutyo bwe tukola aboonoonyi.');
INSERT INTO "african_dev" VALUES(37,35,'37:35','Mazima bo bwe baagambibwanga nti tewali kisinzibwa kyonna okugyako Katonda baali nga beekuza.');
INSERT INTO "african_dev" VALUES(37,36,'37:36','Nebagamba nti mazima ffe tuyinza okuleka ba katonda baffe ku lw''omuyimbi omulalu.');
INSERT INTO "african_dev" VALUES(37,37,'37:37','Nedda (Nabbi Muhammad talinga bwe bagamba) wabula yajja na mazima era n’akakasa ababaka (abaamukulembera).');
INSERT INTO "african_dev" VALUES(37,38,'37:38','Mazima ddala mmwe muli ba kukomba ku bibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(37,39,'37:39','Era temugenda kusasulwa wabula ebyo bye mwakolanga.');
INSERT INTO "african_dev" VALUES(37,40,'37:40','Okugyako abaddu ba Katonda abalondobemu.');
INSERT INTO "african_dev" VALUES(37,41,'37:41','Abo baliweebwa ebigabirirwa ebimanyiddwa.');
INSERT INTO "african_dev" VALUES(37,42,'37:42','(Biriba) ebibala era nga nebaba nga bo bagabulwa.');
INSERT INTO "african_dev" VALUES(37,43,'37:43','Nga bali mu jjana ez''ebyengera.');
INSERT INTO "african_dev" VALUES(37,44,'37:44','(Baliba) ku bitanda nga buli bamu boolekedde bannaabwe.');
INSERT INTO "african_dev" VALUES(37,45,'37:45','Nga babayisaamu (mu kubagabula) eggiraasi z''ebyo''kunywa eziva mu nsulo ezitakalira.');
INSERT INTO "african_dev" VALUES(37,46,'37:46','(Eby''okunywa ebyo) nga byeru ebiriwoomera abanywi.');
INSERT INTO "african_dev" VALUES(37,47,'37:47','Tebigenda kubaamu kirumya mutwe era nga bwebatagenda kutamiira olw''okubinywa.');
INSERT INTO "african_dev" VALUES(37,48,'37:48','Era baliba balina abakyala ab''amaaso amakkakkamu amanene.');
INSERT INTO "african_dev" VALUES(37,49,'37:49','Abakyala abo balibanga amagi ageegenderezebwa obutayatika.');
INSERT INTO "african_dev" VALUES(37,50,'37:50','Olwo nno abamu neboolekera bannaabwe nga bawaanyisaganya ebigambo.');
INSERT INTO "african_dev" VALUES(37,51,'37:51','Omwogezi (omu) ku bo aligamba nti mazima nze nalina ow''omukwano.');
INSERT INTO "african_dev" VALUES(37,52,'37:52','Yalinga angamba nti abaffe ggwe oli mu abo abakakasa.');
INSERT INTO "african_dev" VALUES(37,53,'37:53','Nti bwe tulifa netufuuka ettaka n’amagumba ameereere, abaffe tuli baakubalibwa.');
INSERT INTO "african_dev" VALUES(37,54,'37:54','Omukozi w’obulungi aligamba nti abaffe mulaba ekiri mu muliro.');
INSERT INTO "african_dev" VALUES(37,55,'37:55','Olwo alitunulayo n’amulaba ng’ali mu makkati g’omuliro Jahiimu.');
INSERT INTO "african_dev" VALUES(37,56,'37:56','Aligamba nti ndayidde Katonda wabula kata onzikirize.');
INSERT INTO "african_dev" VALUES(37,57,'37:57','Singa si byengera bya Mukama omulabirizi wange nnaalibadde mu baleeteddwa (eri omuliro).');
INSERT INTO "african_dev" VALUES(37,58,'37:58','Ate ffe tetukyaddayo nakufa!.');
INSERT INTO "african_dev" VALUES(37,59,'37:59','Okugyako okufa kwaffe okwasooka era nga tetuli ba kubonerezebwa.');
INSERT INTO "african_dev" VALUES(37,60,'37:60','Mazima ekyo ddala kwo kwe kwesiima okusuffu.');
INSERT INTO "african_dev" VALUES(37,61,'37:61','(N’olwekyo) ku lw’ebintu nga bino abatakabana baalitakabanye.');
INSERT INTO "african_dev" VALUES(37,62,'37:62','Abaffe obwo bwe bugugenyi obulungi oba omuti gwa Zakkuumi.');
INSERT INTO "african_dev" VALUES(37,63,'37:63','Twagussaawo nga kikemo eri abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(37,64,'37:64','Mazima gwo muti guva ku ntobo ya muliro Jahiimu.');
INSERT INTO "african_dev" VALUES(37,65,'37:65','Ebibala byagwo (okumanya bibi) biringa emitwe gya Sitane.');
INSERT INTO "african_dev" VALUES(37,66,'37:66','Mazima bo bagenda kugulyako bagenda kujjuza nagwo embuto.');
INSERT INTO "african_dev" VALUES(37,67,'37:67','Oluvanyuma bagenda kubongeza ku gwo eby''okunywa ebitabuddwa mu lweje.');
INSERT INTO "african_dev" VALUES(37,68,'37:68','Oluvanyuma mazima obuddo bwabwe buli mu muliro Jahiimu.');
INSERT INTO "african_dev" VALUES(37,69,'37:69','Mazima bo baasanga bakadde baabwe nga babuze.');
INSERT INTO "african_dev" VALUES(37,70,'37:70','N’olwekyo bo mu buwufu bwabwe batwalibwa kantindiggwa.');
INSERT INTO "african_dev" VALUES(37,71,'37:71','Era mazima oluberyeberye lwabwe abasinga obungi mu baasooka baabula.');
INSERT INTO "african_dev" VALUES(37,72,'37:72','So nga ate twatuma mu bo abatiisa.');
INSERT INTO "african_dev" VALUES(37,73,'37:73','Kale tunuulira olabe enkomerero y''abaatiisibwa yali etya.');
INSERT INTO "african_dev" VALUES(37,74,'37:74','(Tewali yawona) okugyako abaddu ba Katonda abalondobemu.');
INSERT INTO "african_dev" VALUES(37,75,'37:75','Mazima Nuhu yatukoowoola naffe nno twali abaamwanukula obulungi.');
INSERT INTO "african_dev" VALUES(37,76,'37:76','Netumuwonya n’abantube akabenje akayitirivu.');
INSERT INTO "african_dev" VALUES(37,77,'37:77','Netufuula ezzadde lye nebaba bo nga be basigaddewo.');
INSERT INTO "african_dev" VALUES(37,78,'37:78','Netumulekerawo okwogerwako okulungi mu balijja oluvanyuma.');
INSERT INTO "african_dev" VALUES(37,79,'37:79','Emirembe gibeere ku Nuhu (n’okwogerwako okulungi) mu bitonde byonna.');
INSERT INTO "african_dev" VALUES(37,80,'37:80','Mazima ffe bwe tutyo bwe tusasula abakozi b’obulungi.');
INSERT INTO "african_dev" VALUES(37,81,'37:81','Mazima (Nuhu) ali mu baddu baffe abakkiriza aba nnamaddala.');
INSERT INTO "african_dev" VALUES(37,82,'37:82','Oluvanyuma twazikiriza abasigadde (abatakkiriza Nuhu).');
INSERT INTO "african_dev" VALUES(37,83,'37:83','Era mazima ddala Ibrahim ali ku nkolaye(Nuhu).');
INSERT INTO "african_dev" VALUES(37,84,'37:84','Kubanga yajja ewa Mukama omulabiriziwe n’omutima omuyonjo.');
INSERT INTO "african_dev" VALUES(37,85,'37:85','Bwe yagamba kitaawe n’abantu be nti biki bye musinza.');
INSERT INTO "african_dev" VALUES(37,86,'37:86','Ba katonda abajingirire bemwagala nemuleka Katonda.');
INSERT INTO "african_dev" VALUES(37,87,'37:87','Olwo Mukama omulabirizi w’ebitonde byonna, ki kye mumulowoozaako?.');
INSERT INTO "african_dev" VALUES(37,88,'37:88','Olwo nno naatunula olutunula mu munyeenye (nga enkola ya bantube bwe yali).');
INSERT INTO "african_dev" VALUES(37,89,'37:89','(Naagamba nti) mazima nze ndi mulwadde.');
INSERT INTO "african_dev" VALUES(37,90,'37:90','Olwo nebamuvaako nebamulekawo.');
INSERT INTO "african_dev" VALUES(37,91,'37:91','Olwo nadda ku ba katonda baabwe naalyoka abagamba nti lwaki temulya?.');
INSERT INTO "african_dev" VALUES(37,92,'37:92','Mwabaaki, nga temwogera!.');
INSERT INTO "african_dev" VALUES(37,93,'37:93','Olwo kwe kutandika okubatema n’omukono gwe ogwa ddyo.');
INSERT INTO "african_dev" VALUES(37,94,'37:94','Olwo (abantu ba Ibrahim bwe baamanya kyakoze) bajja gyali nga banguwa.');
INSERT INTO "african_dev" VALUES(37,95,'37:95','(Ibrahim) naagamba nti, musinza ebyo bye mubajja!.');
INSERT INTO "african_dev" VALUES(37,96,'37:96','So nga Katonda yeeyabatonda mmwe n''ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(37,97,'37:97','Nebagamba nti mumuzimbire ekifo (mukikumemu omuliro) olwo nno mumukasuke mu nnabeengeya w’omuliro.');
INSERT INTO "african_dev" VALUES(37,98,'37:98','Mu kukola ekyo, baagenderera okumukolera olukwe (bamutte) wabula bbo netubafuula ab''awansi (bwe twalemesa olukwe lwabwe).');
INSERT INTO "african_dev" VALUES(37,99,'37:99','(Awo Ibrahim naasalawo okusenguka) era naagamba nti mazima nze ngenda ewa Mukama omulabirizi wange ajja kunnungamya.');
INSERT INTO "african_dev" VALUES(37,100,'37:100','Ayi Mukama omulabirizi wange ngabira (omwana) nga wa mu balongoofu.');
INSERT INTO "african_dev" VALUES(37,101,'37:101','Olwo netumusanyusa nti ajja kufuna omwana omulenzi ow''ekisa.');
INSERT INTO "african_dev" VALUES(37,102,'37:102','Omwana oyo (Ismail) bwe yatandika okutambula naye (Ibrahim) naagamba nti owange mutabani wange nkirabye lunye mu kirooto mazima nze nga nkusala kale tunula olabe, ogamba ki? (Ismail) naagamba nti owange taata kola ekyo kyolagirwa, Katonda nga ayagadde ojja kunsanga nga ndi mu bagumiikiriza.');
INSERT INTO "african_dev" VALUES(37,103,'37:103','Bombi bwe baamala okwewaayo eri (ekiragiro kya Katonda) naamugalamiza nga amuwunzise.');
INSERT INTO "african_dev" VALUES(37,104,'37:104','Netumukoowoola nti owange Ibrahim.');
INSERT INTO "african_dev" VALUES(37,105,'37:105','Mazima otuukirizza endooto, mazima ffe bwe tutyo bwe tusasula abalongoosa.');
INSERT INTO "african_dev" VALUES(37,106,'37:106','Mazima ekyo (ekyatuuka ku Ibrahim n’omwana we) ddala kwe kugezesa okw''olwatu.');
INSERT INTO "african_dev" VALUES(37,107,'37:107','Era (Ismail) twamununula n''ensolo ensuffu eyasalibwa.');
INSERT INTO "african_dev" VALUES(37,108,'37:108','Netumulekerawo okwogerwako okulungi mu balijja oluvanyuma.');
INSERT INTO "african_dev" VALUES(37,109,'37:109','Emirembe gibeere ku Ibrahim.');
INSERT INTO "african_dev" VALUES(37,110,'37:110','Bwe tutyo bwe tusasula abalongoosa.');
INSERT INTO "african_dev" VALUES(37,111,'37:111','Mazima ye wa mu baddu baffe abakkiriza (abannamaddala).');
INSERT INTO "african_dev" VALUES(37,112,'37:112','Era twamusanyusa n’okumuwa Ishaak nga nabbi nga wa mu balongoofu.');
INSERT INTO "african_dev" VALUES(37,113,'37:113','Era twamuwa omukisa netuguwa ne Ishaak era mu zadde lyabwe bombi mwalimu omulongoosa n''yeeyisa obubi ow''olwatu.');
INSERT INTO "african_dev" VALUES(37,114,'37:114','Mazima twabunduggula ebyengera ku Musa ne Haruna.');
INSERT INTO "african_dev" VALUES(37,115,'37:115','Netubawonya n’abantu baabwe bombi akabenje ak''amaanyi.');
INSERT INTO "african_dev" VALUES(37,116,'37:116','Netubataasa nebaba bbo nga bawanguzi.');
INSERT INTO "african_dev" VALUES(37,117,'37:117','Bombi twabawa ekitabo ekinnyonnyofu okutangaaza ekkubo.');
INSERT INTO "african_dev" VALUES(37,118,'37:118','Era twabalungamya mu kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(37,119,'37:119','Era bombi twabalekerawo okwogerwako okulungi mu balijja oluvanyuma.');
INSERT INTO "african_dev" VALUES(37,120,'37:120','Emirembe gibeere ku Musa ne Haruna.');
INSERT INTO "african_dev" VALUES(37,121,'37:121','Mazima ffe bwe tutyo bwe tusasula abalongoosa.');
INSERT INTO "african_dev" VALUES(37,122,'37:122','Mazima bombi ba mu baddu baffe abakkiriza (abannamaddala).');
INSERT INTO "african_dev" VALUES(37,123,'37:123','Era mazima ddala Elias wa mu babaka.');
INSERT INTO "african_dev" VALUES(37,124,'37:124','Jjukira ekiseera bwe yagamba abantu be abaffe temutya (Katonda).');
INSERT INTO "african_dev" VALUES(37,125,'37:125','Musinza ekibumbe ne muleka (Katonda) omutonzi asinga abatonzi bonna.');
INSERT INTO "african_dev" VALUES(37,126,'37:126','Katonda ye Mukama omulabirizi wa mmwe era ye Mukama omulabirizi wa bakadde ba mmwe abaasooka.');
INSERT INTO "african_dev" VALUES(37,127,'37:127','Ne bamulimbisa n’olwekyo mazima bo baakuleetebwa (babitebye).');
INSERT INTO "african_dev" VALUES(37,128,'37:128','Okugyako abaddu ba Katonda abalondemu.');
INSERT INTO "african_dev" VALUES(37,129,'37:129','Era twamulekera okwogerwako obulungi mu balijja oluvanyuma.');
INSERT INTO "african_dev" VALUES(37,130,'37:130','Emirembe gibe ku bantu ba Elias.');
INSERT INTO "african_dev" VALUES(37,131,'37:131','Mazima ffe bwe tutyo bwe tusasula abalongoosa.');
INSERT INTO "african_dev" VALUES(37,132,'37:132','Mazima ye wa mu baddu baffe abakkiriza abannamaddala.');
INSERT INTO "african_dev" VALUES(37,133,'37:133','Era mazima Luutu wa mu babaka.');
INSERT INTO "african_dev" VALUES(37,134,'37:134','(Jjukira) bwe twamuwonya n’abantube bonna.');
INSERT INTO "african_dev" VALUES(37,135,'37:135','Okugyako omukadde (Mukyalawe) eyali mu baasigala.');
INSERT INTO "african_dev" VALUES(37,136,'37:136','Oluvanyuma twazikiriza abaasigala.');
INSERT INTO "african_dev" VALUES(37,137,'37:137','Era mazima mmwe mubayitako (muyita ku matongo gaabwe) ku makya.');
INSERT INTO "african_dev" VALUES(37,138,'37:138','N''ekiro abaffe temutegeera!.');
INSERT INTO "african_dev" VALUES(37,139,'37:139','Era mazima Yunus wa mu babaka.');
INSERT INTO "african_dev" VALUES(37,140,'37:140','Jjukira (bwe yasuulawo omulimu Katonda gwe yamukwasa) naalinnya eryato eryali limaze okutikka.');
INSERT INTO "african_dev" VALUES(37,141,'37:141','(Bwe lyasemberera okusaanawo olw''okuba lyali litisse nnyo), olwo nno (Yunus) yeetaba mu kalulu (ak''okusalawo ab''okukasukibwa mu nnyanja, akalulu kaagenda okuggwa) nga ali mu be kakutte (ne bamukasuka mu nnyanja).');
INSERT INTO "african_dev" VALUES(37,142,'37:142','Olwo nno ekyennyanja kwe kumumira nga ye y''anenyezebwa.');
INSERT INTO "african_dev" VALUES(37,143,'37:143','Singa mazima ddala teyali mu batendereza (Katonda).');
INSERT INTO "african_dev" VALUES(37,144,'37:144','Yandibadde mu lubuto lwakyo okutuusa ku lunaku lwe balizuukizibwa.');
INSERT INTO "african_dev" VALUES(37,145,'37:145','Wabula twamukasuka ku lukalu naye nga yali mulwadde.');
INSERT INTO "african_dev" VALUES(37,146,'37:146','Twamumereza ekimera eky''ekiryo (kimusiikirize kimugobeko n''ensowera).');
INSERT INTO "african_dev" VALUES(37,147,'37:147','Era twamutuma eri abantu emitwalo kkumi oba okusingawo.');
INSERT INTO "african_dev" VALUES(37,148,'37:148','Bakkiriza, olwo nno netubeeyagaza okutuuka ebbanga eggere.');
INSERT INTO "african_dev" VALUES(37,149,'37:149','Kaakano ggwe babuuze (aba Kuraish) nti Mukama omulabiriziwo y''aba n''abawala bo nebaba n’abalenzi!.');
INSERT INTO "african_dev" VALUES(37,150,'37:150','Oba abaffe bo baaliwo nga tutonda ba Malayika nga bakazi.');
INSERT INTO "african_dev" VALUES(37,151,'37:151','Abange mazima mu kutemerera kwabwe batuuka n''okugamba nti.');
INSERT INTO "african_dev" VALUES(37,152,'37:152','Katonda yazaala nga ate ddala bo balimba.');
INSERT INTO "african_dev" VALUES(37,153,'37:153','(Katonda) asalawo okutwala abawala n''aleka abalenzi!.');
INSERT INTO "african_dev" VALUES(37,154,'37:154','Mwabaaki mulamula mutya (ebintu)!.');
INSERT INTO "african_dev" VALUES(37,155,'37:155','Abaffe temwebuulirira!.');
INSERT INTO "african_dev" VALUES(37,156,'37:156','Oba mulina obujulizi obweyolefu (nga kye kibakozesa ekyo).');
INSERT INTO "african_dev" VALUES(37,157,'37:157','Kale muleete ekiwandiiko kyammwe bwe muba ab''amazima.');
INSERT INTO "african_dev" VALUES(37,158,'37:158','(Abatakkiriza) nebateeka oluganda wakati we (Katonda) n''amajinni, nga ate amajinni gamanyi nti mazima ggo gaakuleetebwa (mu maaso ga Katonda gabitebye).');
INSERT INTO "african_dev" VALUES(37,159,'37:159','Katonda musukkulumu ku bye boogera.');
INSERT INTO "african_dev" VALUES(37,160,'37:160','Wabula abaddu ba Katonda abalondobemu (anti bbo tebagwa mu kiti kyabo abamwogerako eby''ensimattu).');
INSERT INTO "african_dev" VALUES(37,161,'37:161','Kale nno mazima ddala mmwe n''ebyo bye musinza.');
INSERT INTO "african_dev" VALUES(37,162,'37:162','Temusobola mmwe kufiiriza Katonda (ne mumujjako abaddu be).');
INSERT INTO "african_dev" VALUES(37,163,'37:163','Be ppo oyo y''ewokwesonseka omuliro Jahiimu (oyo musobola okumubuza).');
INSERT INTO "african_dev" VALUES(37,164,'37:164','(Ba Malayika ne bagamba nti) era tewali n''omu ku ffe okugyako nga alina ekifo ekimanyiddwa.');
INSERT INTO "african_dev" VALUES(37,165,'37:165','Era mazima ffe (bulijjo) ffe tubeera mu nnyiriri (nga tusinza Katonda).');
INSERT INTO "african_dev" VALUES(37,166,'37:166','Era mazima ffe ddala ffe abatendereza (Katonda).');
INSERT INTO "african_dev" VALUES(37,167,'37:167','Newaakubadde nga (abakaafiiri) ddala bagamba nti.');
INSERT INTO "african_dev" VALUES(37,168,'37:168','Ddala singa twalina okujjukizibwa nga kuva eri abaasooka.');
INSERT INTO "african_dev" VALUES(37,169,'37:169','Twandibadde abaddu ba Katonda abalondobemu.');
INSERT INTO "african_dev" VALUES(37,170,'37:170','(Wabula kitalo byonna ebyo bwe byajja nga ne Nabbi Muhammad kwali) baabiwakanya, kale kyaddaaki balimanya.');
INSERT INTO "african_dev" VALUES(37,171,'37:171','Mazima kyakulembera ekigambo kyaffe eri abaddu baffe abaatumwa.');
INSERT INTO "african_dev" VALUES(37,172,'37:172','Nti mazima bbo ddala be b''okutaasibwa.');
INSERT INTO "african_dev" VALUES(37,173,'37:173','Era mazima eggye lyaffe ddala bo be baba abawanguzi.');
INSERT INTO "african_dev" VALUES(37,174,'37:174','Kale baveeko okutuusa ekiseera ekigere.');
INSERT INTO "african_dev" VALUES(37,175,'37:175','Era batunuulire kyaddaaki baliraba.');
INSERT INTO "african_dev" VALUES(37,176,'37:176','Abaffe ebibonerezo byaffe bye basaba bibatuukeko mangu.');
INSERT INTO "african_dev" VALUES(37,177,'37:177','Lwe birituuka mu lujja lwabwe galiba makya mabi eri abaaweebwa amawulire agabatiisa (omuliro ne batawulira).');
INSERT INTO "african_dev" VALUES(37,178,'37:178','Era baveeko okutuusa ekiseera ekigere.');
INSERT INTO "african_dev" VALUES(37,179,'37:179','Era tunula kyaddaaki baliraba.');
INSERT INTO "african_dev" VALUES(37,180,'37:180','Mukama omulabirizi wo, nannyini buyinza musukkulumu Ku ebyo bye boogera.');
INSERT INTO "african_dev" VALUES(37,181,'37:181','Era emirembe gibeere ku babaka (ba Katonda).');
INSERT INTO "african_dev" VALUES(37,182,'37:182','Era buli kitendo kya Mukama omulabirizi w''ebitonde.');
INSERT INTO "african_dev" VALUES(38,1,'38:1','Swad. Ndayidde Kur’ani ejjudde okujjukiza.');
INSERT INTO "african_dev" VALUES(38,2,'38:2','Wabula abaakaafuwala bali mu kwekuza na kuwalaaza mpaka.');
INSERT INTO "african_dev" VALUES(38,3,'38:3','Emirembe emeka gye twazikiriza oluberyeberye lwabwe, abaatuuka okukuba ebiwoobe (nga banoonya okutaasibwa), naye nga tewakyali buwonero.');
INSERT INTO "african_dev" VALUES(38,4,'38:4','Nebeewuunya okuba nga omutiisa abajjira ng''ava mu bo, era abakaafiiri ne bagamba nti ono mulogo omulimba (awedde emirimu).');
INSERT INTO "african_dev" VALUES(38,5,'38:5','Abaffe abasinzibwa abangi abafudde omu! mazima ekyo kintu ekyewunyisa ennyo.');
INSERT INTO "african_dev" VALUES(38,6,'38:6','Ekibinja kya bakungu mu bo nekigenda (nga kikunga abantu) nti: mugende mu maaso era mugumiikirize mulemere ku ba katonda ba mmwe.');
INSERT INTO "african_dev" VALUES(38,7,'38:7','Mazima kino (eky''okubaggya ku ba katonda ba mmwe) kkobaane (Muhammad mwayagalira okuyita abaggye ku nsinza yammwe), kino tetukiwulirangako mu nzikiriza (ey''abagoberezi ba Issa) eyasembayo, kino tekiri okugyako kiyiiye (buyiiya).');
INSERT INTO "african_dev" VALUES(38,8,'38:8','Muffe fenna yye (Muhammad) yassiddwaako obubaka! wabula (ekibaleetera obuzibu) lwa kuba bo bali mu kubuusabuusa okubuulirira kwange wabula (bakola ekyo) lwa kuba tebannakomba ku bibonerezo byange.');
INSERT INTO "african_dev" VALUES(38,9,'38:9','Oba (kyebava beeyisa bwe batyo) lwakuba balina amawanika g''okusaasira kwa Mukama omulabirizi wo, nantakubwa ku mukono, omugabi.');
INSERT INTO "african_dev" VALUES(38,10,'38:10','Oba bo balina obufuzi bw''eggulu omusanvu n''ensi n''ebyo ebiri wakati wa byombi, bwe kiba bwekityo, kale baleke bakozese kyonna kyebasobola bafuneyo byebaagala.');
INSERT INTO "african_dev" VALUES(38,11,'38:11','Bo, ggye eriri awo lya kuwangulwa, libalibwa mu bibinja (ebyawangulwa).');
INSERT INTO "african_dev" VALUES(38,12,'38:12','Mazima oluberyeberye lwabwe abantu ba Nuhu ne A-adi ne Farawo nannyini nkondo baalimbisa.');
INSERT INTO "african_dev" VALUES(38,13,'38:13','Ne ba Thamud n’abantu ba Luut n’abantu b’omukibira, ebyonno bye bibinja (ebyogeddwaako mu Aya 11).');
INSERT INTO "african_dev" VALUES(38,14,'38:14','Tewali muntu yenna mwabo okugyako nga yalimbisa ababaka, olwo nno ebibonerezo byange nebikakata (ku bo).');
INSERT INTO "african_dev" VALUES(38,15,'38:15','Era abo tebalindirira okugyako lubwatuka lumu (oluliba olw''amaanyi) nga telwetaaga kuddibwamu mulundi mulala.');
INSERT INTO "african_dev" VALUES(38,16,'38:16','Era (abatakkiriza) bagamba nti ayi Mukama omulabirizi waffe twanguyirize omugabo gwaffe (ogw''ebibonerezo) ng''olunaku lw''okubalibwa terunnatuuka.');
INSERT INTO "african_dev" VALUES(38,17,'38:17','(Katonda agamba Nabbi Muhammad nti:) gumikiiriza ku bye boogera ojjukire omuddu waffe Dauda eyalina amaanyi amasuffu (awamu n''ekyo) yali adda nnyo eri Katonda.');
INSERT INTO "african_dev" VALUES(38,18,'38:18','Mazima ffe twagonza ensozi neziba nga zitendereza awamu naye olweggulo n’obudde nga busaasaana.');
INSERT INTO "african_dev" VALUES(38,19,'38:19','Era twagonza n’ebinyonyi nga biri mu bibinja (byabyo) buli kimu ku byo nga kigondera Dauda (nebitendereza nga bwatendereza).');
INSERT INTO "african_dev" VALUES(38,20,'38:20','Era twanyweza obufuzibwe era netumuwa okumanya ensonga n’okusalawo ekituufu ku bigambo ebiba byogeddwa.');
INSERT INTO "african_dev" VALUES(38,21,'38:21','Abaffe amawulire g’abaakaayana bwe baawalampa ekisenge (Dauda mweyasinzizanga) gaakutuukako.');
INSERT INTO "african_dev" VALUES(38,22,'38:22','Bwe baayingira Dauda weyali naabekanga nebagamba nti: totya (ffe) tuli abakaayana ababiri ng''omu kuffe asobezza munne, kale lamula wakati waffe n’amazima teweekubiira era otulage enkola entuufu.');
INSERT INTO "african_dev" VALUES(38,23,'38:23','Mazima muganda wange ono alina endiga kyenda mu mwenda ng’ate nze nnina endiga emu, ate angamba nti gimpe ngikulundire ate nga ansinga ebigambo.');
INSERT INTO "african_dev" VALUES(38,24,'38:24','(Dauda) naagamba nti: mazima akuyisizza bubi bwakusabye endiga yo agigatte ku ndigaze, ate nga bangi mu bagatta emirimu basobya bannaabwe, okugyako abo abakkiriza nebakola emirimu emirongoofu wabula ate abo batono, Dauda naamanya nti mazima tumugezesezza neyeegayirira Mukama omulabiriziwe naakutama ku maviivi neyeemenya era nadda eri Katonda.');
INSERT INTO "african_dev" VALUES(38,25,'38:25','Olwo nno ekyo netukimusonyiwa, era mazima alina gye tuli ekifo ekyo ku mwanjo n’obuddo obulungi.');
INSERT INTO "african_dev" VALUES(38,26,'38:26','Owange Dauda mazima ffe twakufuula omusigire ku nsi, lamula wakati wa bantu mu mazima era togobereranga okwagala (kwo) nekukubuza okuva ku kkubo lya Katonda, mazima abo abaabula nebava ku kkubo lya Katonda, balifuna ebibonerezo ebikakali olw''ebyo ebyabeerabiza olunaku lw''okubalibwa.');
INSERT INTO "african_dev" VALUES(38,27,'38:27','Eggulu n''ensi n''ebyo ebiri wakati wa byombi, tetwabitonda nga bya lusaago, eyo ndowooza yaabo abaakaafuwala, kale nno okubonaabona okw''omuliro kuliba ku abo abaakaafuwala.');
INSERT INTO "african_dev" VALUES(38,28,'38:28','Oba (basuubira nti) abo abakkiriza nebakola emirimu emirongoofu, tulibayisa nga aboonoona mu nsi, oba abatya Katonda betuliyisa nga aboonoonyi!.');
INSERT INTO "african_dev" VALUES(38,29,'38:29','(Kur’ani eno) kitabo twakissa gyoli nga kya mukisa babe nga beekenneenya ebigambo byakyo era ab''amagezi agakola babe nga beebuulirira.');
INSERT INTO "african_dev" VALUES(38,30,'38:30','Era twagabira Dauda Sulaiman, yali muddu mulungi, (anti) mazima yye yali adda nnyo eri Katonda.');
INSERT INTO "african_dev" VALUES(38,31,'38:31','Jjukira bwe yayanjulirwa mu kiseera eky''olweggulo enfaraasi ezirabika obulungi nga ziyimiridde (ate) empenyusi (bwe ziba zidduse).');
INSERT INTO "african_dev" VALUES(38,32,'38:32','Naagamba nti mazima nze nkulembezza okwagala ebirungi ku kutendereza Mukama omulabirizi wange (nenzira mu kutunuulira enfaraasi) okutuusa enjuba lwebuliddeyo!.');
INSERT INTO "african_dev" VALUES(38,33,'38:33','(Oluvanyuma yagamba nti) muzinkomezeewo, olwo nno naatandika okuzisala amagulu n''ensingo (naasaddaaka ennyama yaazo).');
INSERT INTO "african_dev" VALUES(38,34,'38:34','Era mazima twagezesa Sulaiman netuteeka omubiri (ogutaliimu bulamu) ku nnamulondo ye, (naye) oluvanyuma yeenenya.');
INSERT INTO "african_dev" VALUES(38,35,'38:35','Naagamba nti ayi Mukama omulabirizi wange nsonyiwa ongabire obwakabaka obutalisaanira mulala yenna oluvanyuma lwange, (anti) mazima ggwe mugabi.');
INSERT INTO "african_dev" VALUES(38,36,'38:36','Olwo nno netumugondeza empewo nga etambula ku lw''ekiragirokye mu ngeri ya mirembe neegenda wonna waaba asazeewo.');
INSERT INTO "african_dev" VALUES(38,37,'38:37','Era twamugondeza Sitane (nga mu zo mwalimu) enkugu mu kuzimba n''endala nga zibbira mu nnyanja.');
INSERT INTO "african_dev" VALUES(38,38,'38:38','Era nga ne (Sitane) endala zisibiddwa ku njegere (anti zo zaagaana okukola).');
INSERT INTO "african_dev" VALUES(38,39,'38:39','Netumugamba (nti) okwo kwe kugaba kwaffe, kale gabako oba omme (nga bwolaba) awatali kubuuzibwa.');
INSERT INTO "african_dev" VALUES(38,40,'38:40','Era mazima (Sulaiman) alina gye tuli ekifo kya ku mwanjo n’obuddo obulungi.');
INSERT INTO "african_dev" VALUES(38,41,'38:41','Era jjukira omuddu waffe Ayub bwe yakoowoola Mukama omulabirizi we (naagamba nti) mazima nze Sitane andeEtedde ennaku n’obuzibu (Ayi Mukama nkusaba omponye).');
INSERT INTO "african_dev" VALUES(38,42,'38:42','(Katonda naamugamba nti) samba ettaka n''ekigerekyo awo amazzi negafukula era ne bamugamba nti amazzi gano mannyogovu g''a kunaaba na kunywa (lye ddagala erikuweereddwa).');
INSERT INTO "african_dev" VALUES(38,43,'38:43','Era netumuwa (nga tuzzaawo) abantu be era awamu nabo (netumuwa) abalinga bo (ebyo byonna) nga kusaasira okuva gyetuli era nga kubuulirira eri abo abalina amagezi.');
INSERT INTO "african_dev" VALUES(38,44,'38:44','Era (n''alagirwa nti) kwata ekisaaganda ky''obuti olwo nno okimukubise (mukyalawo) anti bwotyo bwe walayira obenga tomenye kilayiro kyo, mazima ffe twamusanga (Ayub) nga mugumiikiriza (yye) muddu mulungi, anti mazima yye adda nnyo eri Katonda.');
INSERT INTO "african_dev" VALUES(38,45,'38:45','Era jjukira abaddu baffe Ibrahim ne Ishaak ne Yakub abaalina amaanyi n’okulengera ewala.');
INSERT INTO "african_dev" VALUES(38,46,'38:46','Mazima ffe twabaawula n''enkizo ey’okujjukira e nyumba ey’olubeerera (enkomerero).');
INSERT INTO "african_dev" VALUES(38,47,'38:47','Era mazima bo gye tuli ba mu baawule abalondemu.');
INSERT INTO "african_dev" VALUES(38,48,'38:48','Era jjukira Ismail ne Eliyasa-a ne Zul kifuli, nabuli omu kwabo wa mu balondemu.');
INSERT INTO "african_dev" VALUES(38,49,'38:49','Kuno kujjukiza, era mazima ddala abatya Katonda bagenda kufuna obuddo obulungi.');
INSERT INTO "african_dev" VALUES(38,50,'38:50','(Obuddo obwo buliba) Janna ez’emirembe, nga kulwabwe emiryango (gyazo) giriba miggule.');
INSERT INTO "african_dev" VALUES(38,51,'38:51','Balibeera beesigamye nga bali mu zo, nga bwe batumya mu zo ebibala ebingi nga bya njawulo n''eby''okunywa.');
INSERT INTO "african_dev" VALUES(38,52,'38:52','Era baliba balina abakyala abakkakkamu ab''amaaso ab''emyaka egyenkana.');
INSERT INTO "african_dev" VALUES(38,53,'38:53','(Baligambibwa nti) kino (kye mutuseeko) kyekyo kye mwalagaanyisibwa okuweebwa ku lunaku lw''okubalibwa.');
INSERT INTO "african_dev" VALUES(38,54,'38:54','Mazima ebyo kwe kugabiririra kwaffe tekuyinza kukalira.');
INSERT INTO "african_dev" VALUES(38,55,'38:55','Ekyo kyekyo, naye mazima abeewagguzi balifuna obuddo obubi.');
INSERT INTO "african_dev" VALUES(38,56,'38:56','Nga muliro Jahannama gwe balyesonseka naye buliba buliri bubi ddala.');
INSERT INTO "african_dev" VALUES(38,57,'38:57','Kino (kye balimu bye bibonerezo ebiruma ennyo) kale balibikombako nga nabyo mazzi agookya n''amasira ag''olusaayisaayi.');
INSERT INTO "african_dev" VALUES(38,58,'38:58','Ne (bibonerezo) ebirala ebiringa ebyo ebyengeri ez''ejawulo (nabyo biribatuukako).');
INSERT INTO "african_dev" VALUES(38,59,'38:59','(Buli aboonoonyi abapya lwe banaaleetebwanga mu muliro, nga abasookayo bagambibwa nti:) kino kibinja kyesozze omuliro awamu nammwe (olwo abalisangwamu bagambe nti) abo tebaanirizibwa (tebalina mirembe) mazima bo beesozze muliro.');
INSERT INTO "african_dev" VALUES(38,60,'38:60','(Abaagoberera) baligamba (abaasooka) nti wabula mmwe, mmwe mutaanirizibwa, mmwe mwabituleetera, bugenda kuba butuulo bubi.');
INSERT INTO "african_dev" VALUES(38,61,'38:61','Baligamba nti: ayi Mukama omulabirizi waffe oyo eyatuleetera bino mwongere ebibonerezo eby''okuba mu muliro nga bibazziddwamu.');
INSERT INTO "african_dev" VALUES(38,62,'38:62','Era (aboonoonyi) baligamba nti lwaki tetulaba bantu betwali tubalira mu babi!.');
INSERT INTO "african_dev" VALUES(38,63,'38:63','Abaffe twabafuula eky''okujeeja (so nga baali balungi) oba amaaso ge gatabalaba.');
INSERT INTO "african_dev" VALUES(38,64,'38:64','Mazima ebyo (ebyogeddwa) ddala (okwo kwe kuliba) okuyombagana kw''abantu bo mu muliro.');
INSERT INTO "african_dev" VALUES(38,65,'38:65','Bagambe (ggwe Nabbi Muhammad) mazima nze ndi mutiisa era tewali kisinzibwa kyonna mu butuufu okugyako Katonda ali omu nantalemwa.');
INSERT INTO "african_dev" VALUES(38,66,'38:66','Mukama omulabirizi w''eggulu omusanvu n''ensi n''ebyo ebiri wakati wa byombi, nantakubwa ku mukono, omusonyiyi ennyo.');
INSERT INTO "african_dev" VALUES(38,67,'38:67','Bagambe nti kyo (kye mbagamba) kigambo kikulu ddala.');
INSERT INTO "african_dev" VALUES(38,68,'38:68','(Ate) mmwe kye mwawukanako.');
INSERT INTO "african_dev" VALUES(38,69,'38:69','Sibangako na kumanya kwonna ku bikwata ku bakungu ab''awaggula webakaayanira, (ba Malayika bwe beemulugunya nga baagala Katonda baaba asindika ku nsi mu kifo kya Adam).');
INSERT INTO "african_dev" VALUES(38,70,'38:70','Ssaatumirwa kirala kyonna okugyako (okuba) nti mazima ddala nze ndi mutiisa ow''olwatu.');
INSERT INTO "african_dev" VALUES(38,71,'38:71','Jjukira ekiseera Mukama omulabirizi wo bwe yagamba ba Malayika, nti mazima nze nja kutonda omuntu nga mugya mu kadongo.');
INSERT INTO "african_dev" VALUES(38,72,'38:72','Bwe nnaamala okukola engeri nga bwanabeera era nemmufuuwamu omwoyo oguva gyendi, kale mubutame nga mumuvunnamira.');
INSERT INTO "african_dev" VALUES(38,73,'38:73','Olwo nno ba Malayika bonna nga bali wamu baavunnama.');
INSERT INTO "african_dev" VALUES(38,74,'38:74','Okugyako Ibliis yeekuluntaza naaba mu bakaafiiri.');
INSERT INTO "african_dev" VALUES(38,75,'38:75','(Katonda) naagamba nti owange Ibliis kiki ekikugaanye okuvunnamira oyo gwe nnatonda n’omukono gwange abaffe wetwala nti oli wa kitiibwa (okusinga Adam), oba lwakuba nti bulijjo oli mu ba kiwagi ku Katonda.');
INSERT INTO "african_dev" VALUES(38,76,'38:76','(Ibliis) naagamba nti: nze mmusinga, wantonda mu muliro ate (yye) noomutonda mu kadongo.');
INSERT INTO "african_dev" VALUES(38,77,'38:77','(Katonda) naagamba nti: kale gifulume (e jjana) mazima ggwe ogobeddwa (era oli mukolimire).');
INSERT INTO "african_dev" VALUES(38,78,'38:78','Era mazima okakaseeko ekikolimo kyange okutuusa ku lunaku lw''okusasulwa.');
INSERT INTO "african_dev" VALUES(38,79,'38:79','(Ibliis) naagamba nti: ayi Mukama omulabirizi wange nindiriza okutuusa ku lunaku lwe balizuukizibwa.');
INSERT INTO "african_dev" VALUES(38,80,'38:80','(Katonda) naagamba nti: mazima ggwe oli mu balindiriziddwa.');
INSERT INTO "african_dev" VALUES(38,81,'38:81','Okutuuka ku lunaku olw''ekiseera ekimanyiddwa.');
INSERT INTO "african_dev" VALUES(38,82,'38:82','(Ibliis) naagamba nti: ndayira ekitiibwa kyo njakubabuliza ddala bonna.');
INSERT INTO "african_dev" VALUES(38,83,'38:83','Okugyako abaddu bo abalondobemu mu bo.');
INSERT INTO "african_dev" VALUES(38,84,'38:84','(Katonda) naagamba nti: ago nno g''emazima, era nga bulijjo njogera mazima.');
INSERT INTO "african_dev" VALUES(38,85,'38:85','Ddala ngenda kujjuza omuliro Jahannama (nga nguteekamu) ggwe n''abo bonna abakugondera mu bo.');
INSERT INTO "african_dev" VALUES(38,86,'38:86','Gamba (ggwe Muhammad nti ekyokubatuusaako obubaka) sikibasabirako mpeera, era nga bwe ssiri mu beetumiikiriza.');
INSERT INTO "african_dev" VALUES(38,87,'38:87','(Kur’ani) yyo teri okugyako okuba nti kya kubuulirira eri ebitonde.');
INSERT INTO "african_dev" VALUES(38,88,'38:88','Era ekigambo kyayo mujja kukimanyira ddala oluvanyuma lw''ekiseera kitono.');
INSERT INTO "african_dev" VALUES(39,1,'39:1','Okussa ekitabo kino (Kur’ani) kuva ewa Katonda nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(39,2,'39:2','Mazima ffe twassa gyoli ekitabo mu butuufu. Kale sinza Katonda nga eddiini yonna ogikola ku lulwe yekka.');
INSERT INTO "african_dev" VALUES(39,3,'39:3','Abange eddiini ensengejje ya Katonda yekka bo abamuvaako ne beeteerawo abakuumi abalala (nga bagamba nti:) tetubasinza okugyako lwa kutusembeza kumpi ne Katonda, mazima Katonda agenda kulamula wakati waabwe mwebyo bo bye baawukanamu, mazima Katonda talungamya muntu oyo omulimba awalaaza empaka.');
INSERT INTO "african_dev" VALUES(39,4,'39:4','Singa Katonda yayagala kweteerawo mwana yaalibadde alonda ekyo kyayagadde mwebyo byatonda, naye wabula musukkulumu nnyo (teyeetaaga mwana) yye ye Katonda ali omu nantalemwa.');
INSERT INTO "african_dev" VALUES(39,5,'39:5','Yatonda eggulu omusanvu n''ensi mu butuufu, abikka ekiro ku misana nga bwabikka emisana ku kiro, era yagonza enjuba n''omwezi, buli kimu kyakutambula okumala ebbanga eggere, abange yye (Katonda) ye nantakubwa ku mukono, omusonyiyi ennyo.');
INSERT INTO "african_dev" VALUES(39,6,'39:6','Katonda yabatonda nga abaggya mu muntu omu oluvanyuma naggya mu ye mukyala we, era naabawa mu bisolo eby''omumaka emitindo munaana, abatondera mu mbuto za ba nnyammwe, nga kuba kutonda luvannyuma lwa kutonda, nga muli mu bizikiza bisatu, oyo nno ye Katonda, Mukama omulabirizi wa mmwe, obufuzi bwonna bubwe tewali kisinzibwa (mu butuufu) okugyako yye, kale ate mukyusibwa mutya!.');
INSERT INTO "african_dev" VALUES(39,7,'39:7','Bwemukaafuwala, mazima Katonda talina kyeyeetaaga ku mmwe era tayagaliza baddu be bukaafiiri, naye bwe mwebaza (ekyo) akibasiimako, era omwoyo omwonoonyi tegugenda kwetikka bibi bya mulala, oluvanyuma obuddo bwa mmwe buli eri Mukama omulabirizi wa mmwe, olwo nno naabamanyisa ebyo bye mwakolanga (anti) mazima yye amanyidde ddala ebiri mu bifuba.');
INSERT INTO "african_dev" VALUES(39,8,'39:8','Akabi bwe katuuka ku muntu asaba Mukama omulabiriziwe ng''amwenenyeza, oluvanyuma bwamuwa ekyengera kyonna okuva gyali, nga yeerabira oyo gwe yali asaba mu kusooka olwo nno nassa ku Katonda abasinzibwa abalala, abe nga abuza (abantu) okubaggya ku kkubo lye (Katonda). Gamba nti weeyagale katono n''obukaafiiri bwo, (anti) mazima ggwe oli wa mu bantu bo mu muliro.');
INSERT INTO "african_dev" VALUES(39,9,'39:9','Abaffe oyo asinza mu biseera by''ekiro nga avunnama era ng''ayimirira (ng''asaala) nga atya olunaku lw''enkomerero ate nga asuubira okusaasira kwa Mukama omulabiriziwe, (oyo yenkana n''atakola ekyo) gamba (ggwe Nabbi Muhammad) nti abaffe abamanyi n''abatamanyi benkana? mazima (abamanyi enjawulo eyo) era nga beebuulirira be ba nannyini magezi amatuufu.');
INSERT INTO "african_dev" VALUES(39,10,'39:10','Gamba (ggwe Nabbi Muhammad) nti abange mmwe abaddu bange abakkiriza, mutye Mukama omulabirizi wa mmwe, balina abo abakola obulungi mu bulamu buno obwo ku nsi obulungi (nga bakyali ku nsi, ne mu bulamu obw''oluvanyuma) era ensi ya Katonda ngazi (muyinza okusenguka ku lw''okusinza Katonda wa mmwe mu mirembe) so nga bo ababa bagumiikirizza (ne batasenguka) baliweebwa empeera yaabwe awatali kubalirira.');
INSERT INTO "african_dev" VALUES(39,11,'39:11','Gamba (ggwe Nabbi Muhammad) mazima nze nnalagirwa okusinza Katonda, ng''eddiini ngikola ku lulwe yekka.');
INSERT INTO "african_dev" VALUES(39,12,'39:12','Era nnalagirwa mbe nga nze nsoose okuba mwabo abasiramuse.');
INSERT INTO "african_dev" VALUES(39,13,'39:13','Gamba ggwe (Nabbi Muhammad) nti mazima nze ntya singa mba njeemedde Mukama omulabirizi wange (okutuukibwako) ebibonerezo by''olunaku oluzibu.');
INSERT INTO "african_dev" VALUES(39,14,'39:14','Gamba nti nsinza Katonda, ng''eddiini yange ngikola ku lulwe yekka.');
INSERT INTO "african_dev" VALUES(39,15,'39:15','Kale mmwe musinze ebyo bye muba mwagadde ebitali yye, era gamba nti mazima abantu ab''okufaafaaganirwa beebo abagenda okufaafaaganirwa emyoyo gyabwe n’abantu baabwe ku lunaku lw''enkomerero, abange okwo nno kwe kufaafaaganirwa okweyolefu.');
INSERT INTO "african_dev" VALUES(39,16,'39:16','Balina waggulu waabwe emizingo gy’omuliro ate nga ne wansi waabwe eriyo emizingo emirala. Ekyo nno Katonda atiisa nakyo abaddube nga abagamba nti: abange baddu bange kale muntye.');
INSERT INTO "african_dev" VALUES(39,17,'39:17','Ate abo abeesamba okusinza ebisinzibwa eby''obulimba nebadda eri Katonda balina amawulire ag''essanyu n''olwekyo sanyusa abaddu bange.');
INSERT INTO "african_dev" VALUES(39,18,'39:18','Abo abawulira ebigambo nebagoberera ebisinga obulungi mu byo, abo beebo Katonda be yawa obulungamu, era abo bo be ba nannyini magezi amatuufu.');
INSERT INTO "african_dev" VALUES(39,19,'39:19','Abaffe oyo gwekikakaseeko ekigambo ky''ebibonerezo (olw''okwonoonekakwe oyinza okumulungamya!) abaffe ggwe oyinza okutaasa oyo ali mu muliro!.');
INSERT INTO "african_dev" VALUES(39,20,'39:20','Naye abo abatya Mukama omulabirizi waabwe balina e nyumba ezaakalina nga waggulu waazo ate wazimbiddwayo e nyumba endala, ng''emigga gikulukutira mu zo, (eyo) nga ndagaano ya Katonda (ate) nga Katonda tayawukana ku ndagaano.');
INSERT INTO "african_dev" VALUES(39,21,'39:21','Abaffe tolaba nti mazima Katonda assa okuva waggulu amazzi naagakulukusiza mu mifuleggye egiri mu ttaka, oluvanyuma ameza nago ebibala eby''amabala ag''enjawulo, oluvanyuma bikala (ebibadde ebyakiragala) n''obiraba nga bifuuse bya kyenvu, oluvanyuma abifuula ebisubi ebikutusekutuse. Mazima mu ekyo mulimu okujjukiza eri abalina amagezi.');
INSERT INTO "african_dev" VALUES(39,22,'39:22','Abaffe oyo Katonda gwayanjululiza obusiraamu ekifuba kye, olwo nno yye naaba nga ali ku kitangaala ekiva eri Mukama omulabirizi we (afaanana n''oyo alina omutima omukakanyavu!) okubonaabona kuli kwabo abakakanyavu b’emitima gyabwe egiva ku kutendereza Katonda, abo bali mu bubuze obw''olwatu.');
INSERT INTO "african_dev" VALUES(39,23,'39:23','Katonda yassa ebigambo ebisinga obulungi (Kur’ani) nga kitabo ekirina ebigambo ebifaanagana (naye nga tebikontana) nga kiddingana ensonga (awatali kukooyesa awulira) gyesisiwala ku lwakyo emibiri gyabo abatya Mukama omulabirizi waabwe (bwekyogera ebigambo ebyogera ku bibonerezo) oluvanyuma emibiri gyabwe n''emitima gyabwe gikkakkana bwe giba giwulidde ebigambo ebyogera ku Katonda (n''okusaasira kwe) ekyo (ekitabo Kur’ani) kulungamya kwa Katonda alungamya nakyo oyo gwaba ayagadde, oyo yenna Katonda gwabuza tayinza kufuna mulungamya.');
INSERT INTO "african_dev" VALUES(39,24,'39:24','Abaffe oyo aligezaako okuwonya ekyenyikye ku bibonerezo by''olunaku lw''enkomerero (ayinza okufaanana n''oyo aliwona yenna olw''emirimu gye emirungi), era abeeyisa obubi baligambibwa nti: mukombe (ku bukaawu) bwebyo bye mwakolanga.');
INSERT INTO "african_dev" VALUES(39,25,'39:25','Abo abaabakulembera baalimbisa (nga nabo bwe balimbisa) olwo nno ebibonerezo nebibajjira okuva webaali tebasuubira.');
INSERT INTO "african_dev" VALUES(39,26,'39:26','Olwo nno Katonda naabakombesa ku buswavu mu bulamu obw''ensi ate nga ebibonerezo by''enkomerero bye bisinga obukakali singa baali bamanyi.');
INSERT INTO "african_dev" VALUES(39,27,'39:27','Mazima tukubidde abantu mu Kur’ani eno buli kifaananyi babe nga beebuulirira.');
INSERT INTO "african_dev" VALUES(39,28,'39:28','Kur’ani (twagissa) nga eri mu luwarabu nga teriimu bukyamu bwonna babe nga batya Katonda.');
INSERT INTO "african_dev" VALUES(39,29,'39:29','Katonda yakuba ekifaananyi ky''omusajja (omuddu) ng''aliko bakamaabe bangi abakaayana (n''atamanya ani gwasooka okuweereza) n’omusajja omulala (omuddu) nga wa muntu omu, abaffe mu kulaba bafaanana. Ebitendo byonna bya Katonda wabula abasinga obungi mu bo tebamanyi.');
INSERT INTO "african_dev" VALUES(39,30,'39:30','Mazima ggwe (Nabbi Muhammad) ojja kufa era mazima ddala nabo baakufa.');
INSERT INTO "african_dev" VALUES(39,31,'39:31','Oluvanyuma mazima mmwe (mwenna wamu) mugenda kuwozanganya ewa Mukama omulabirizi wa mmwe ku lunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(39,32,'39:32','Ani eyeeyisa obubi okusinga oyo ayogera obulimba ku Katonda! era naalimbisa amazima bwe gaamujjira (yeeyinula ki?) abaffe mu muliro Jahannama temuli nfo za bakaafiiri?.');
INSERT INTO "african_dev" VALUES(39,33,'39:33','Ate oyo eyaleeta amazima era n’agakkiririzaamu, abo bo be batya Katonda.');
INSERT INTO "african_dev" VALUES(39,34,'39:34','Balifuna bye baagala ewa Mukama omulabirizi waabwe eyo y''empeera ya balongoosa.');
INSERT INTO "african_dev" VALUES(39,35,'39:35','Katonda abe nga abasonyiwa ebibi ebyo bye baakola, era abasasule empeera yaabwe olw''ebirungi ebyo bye baali bakola.');
INSERT INTO "african_dev" VALUES(39,36,'39:36','Abaffe Katonda tamala muddu we, balyoke batuuke okukutiisatiisa n''abo abatali yye, oyo yenna Katonda gwabuza tagenda kufuna mulungamya.');
INSERT INTO "african_dev" VALUES(39,37,'39:37','Oyo yenna Katonda gwalungamya tabaako amubuza, abaffe Katonda ssi ye nantakubwa ku mukono asobola okubonereza abo (abeeyisa obubi mu baddube).');
INSERT INTO "african_dev" VALUES(39,38,'39:38','Singa obabuuza nti ani yatonda eggulu omusanvu n''ensi, ddala bajja kugamba nti Katonda, bagambe nti abaffe (mmwe) mulaba nti ebyo bye musaba nemuleka Katonda singa Katonda aba anjagalizza akabi byo bisobola okugyawo akabiike, oba singa aba anjagalizza kusaasira (kirungi) abaffe byo bisobola okuziyiza okusaasira kwe, gamba nti Katonda ammala (era bulijjo) abaagala ow''okwesiga ye yekka gwe beesiga.');
INSERT INTO "african_dev" VALUES(39,39,'39:39','Bagambe nti: abange bantu bange mukolere ku nkola yammwe, mazima nze nkola (ku nkola eyange) luliba olwo nemumanya.');
INSERT INTO "african_dev" VALUES(39,40,'39:40','Ani ebibonerezo gwe birijjira nga bimuswaza era naatuukwako ebibonerezo eby''olubeerera.');
INSERT INTO "african_dev" VALUES(39,41,'39:41','Mazima ffe twassa ku ggwe ekitabo eky''amazima nga kya bantu bonna oyo alungama aba yeerungamidde, ate oyo abula, ekibi ky''okubulakwe kidda gyali, era ggwe toli mukuumi ku bo.');
INSERT INTO "african_dev" VALUES(39,42,'39:42','Katonda yaatuusa entuuko z''emyoyo bwe gituuka okuva mu mibiri gyagyo, n''ogwo oguba tegufiiridde mu tulo twagwo (guba mu mikono gye) olwo nno gwaba asazeewo nti agwetaaga gufe naagutwala, ate omulala naaguleka okumala ebbanga eggere mazima mu ekyo mulimu eby''okuyiga eri abo abafumiitiriza.');
INSERT INTO "african_dev" VALUES(39,43,'39:43','Oba (ekibayinula) kuba nti baleka Katonda ne beeteerawo abawolereza! bagambe nti: (bwe gutyo bweguba) newaakubadde nga (abawolereza abo) tebafuga kintu kyonna era nga tebategeera.');
INSERT INTO "african_dev" VALUES(39,44,'39:44','Bagambe nti: okuwolereza kwonna kuli mu mikono gya Katonda (anti) bubwe yekka obufuzi bw''eggulu omusanvu n''ensi oluvanyuma gyali gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(39,45,'39:45','Katonda bwaba nga ayogeddwako yekka (n''atagattibwako kintu kirala) emitima gyabo abatakkiriza nkomerero gyenyinyimbwa, naye singa abatali yye boogerwako ogenda okulaba nga bo (abagatta ebintu ebirala ku Katonda) basanyuukirira.');
INSERT INTO "african_dev" VALUES(39,46,'39:46','Gamba nti ayi Mukama omutonzi w''eggulu omusanvu n''ensi, omumanyi w''ebikusike n''ebirabwako, ggwe oliramula wakati wa baddu bo mu ebyo bye baali baawukanamu.');
INSERT INTO "african_dev" VALUES(39,47,'39:47','Singa ddala ebyo ebiri mu nsi byonna n''ebirala ebiringa byo nga byabo abeeyisa obubi bandiyagadde okwenunula nabyo okuwona obuzibu bwe bibonerezo byo lunaku lw''enkomerero n''olwokuba nti baliba bamaze okulaba okuva ewa Katonda ebyo bye baali batasuubira.');
INSERT INTO "african_dev" VALUES(39,48,'39:48','Era ne bibeeyoleka ebibi by''ebyo bye baakola era ne bibeetooloola ebyo bye baalinga bajeeja.');
INSERT INTO "african_dev" VALUES(39,49,'39:49','Omuntu bwatuukibwako akabi olwo nno nga atusaba oluvanyuma bwe tumugabira ekyengera kyonna okuva gye tuli, agamba nti mazima kyampebwa lwa kumanya (kwange) wabula (si bwe kiri) kyo kikemo naye ddala abasinga obungi mu bo tebamanyi.');
INSERT INTO "african_dev" VALUES(39,50,'39:50','Mazima (ekigambo kye kimu ekyo) baakyogera abo abaabakulembera (naye) tebyabagasa ebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(39,51,'39:51','Olwo nno obubi bw''ebyo bye baakola ne bubatuukako n''abo bonna abeeyisa obubi mu bano, bajja kutuukibwako obubi bw''ebyo bye baakola ate era tebagenda kulemesa (Katonda ku bakola kyaliba ayagadde).');
INSERT INTO "african_dev" VALUES(39,52,'39:52','Abaffe tebamanyi nti mazima Katonda ayanjuluza riziki eri oyo gwaba ayagadde ate oluusi naafunza, mazima mu ekyo mulimu ebyokuyiga eri abantu abakkiriza.');
INSERT INTO "african_dev" VALUES(39,53,'39:53','Bagambe nti: abange baddu bange abo abayitiriza okwonoona ku myoyo gyabwe temuggwangamu ssuubi ku kusaasira kwa Katonda, mazima Katonda asonyiwa ebyonoono byonna, mazima yye ye musonyiyi ennyo omusaasizi ennyo.');
INSERT INTO "african_dev" VALUES(39,54,'39:54','Era mwemenye mudde ewa Mukama omulabirizi wa mmwe era mweweeyo gyali, nga ebibonerezo tebinnabajjira ate ne muba nga temuli ba kutaasibwa.');
INSERT INTO "african_dev" VALUES(39,55,'39:55','Era mugoberere ebirungi ebyo ebyassibwa gye muli nga biva ewa Mukama omulabirizi wa mmwe oluberyeberye nga ebibonerezo tebinnaba kubajjira ekibwatukira nga na mmwe temumanyi.');
INSERT INTO "african_dev" VALUES(39,56,'39:56','Omuntu aleme kutuuka kugamba nti zinsanze ku lw''ebyo bye nnayonoona ku bikwata ku Katonda era nga ddala nnali ku nsi mu banyooma.');
INSERT INTO "african_dev" VALUES(39,57,'39:57','Oba ogambe nti singa mazima Katonda yannungamya naalibadde mu batya (Katonda).');
INSERT INTO "african_dev" VALUES(39,58,'39:58','Oba bwoliraba ebibonerezo oligamba nti ddala singa nnina okuzzibwayo nembeera mu balongoosa.');
INSERT INTO "african_dev" VALUES(39,59,'39:59','(olyanukulwa nti) nedda ebigambo byange byakujjira n''obirimbisa era neweekuza nooba owo mu bakaafiiri.');
INSERT INTO "african_dev" VALUES(39,60,'39:60','Era ku lunaku lw''enkomerero abo abaayogera obulimba ku Katonda oliraba ebyenyi byabwe nga biddugadde (ate lwaki tebaliba batyo!), abaffe mu muliro Jahannama temuli nfo zaabo abeekuza?.');
INSERT INTO "african_dev" VALUES(39,61,'39:61','Era Katonda agenda kuwonya abo abaamutya nga abawa okwesiima kwabwe (olw''ejjana gyalibayingiza). Akabi (k’ebibonerezo) tekagenda kubatuukako era bo ssi baakunakuwala.');
INSERT INTO "african_dev" VALUES(39,62,'39:62','Katonda, ye mutonzi wa buli kintu era yye, ye mutambuza wa buli kintu.');
INSERT INTO "african_dev" VALUES(39,63,'39:63','Era yaalina ebiyimiriddeko entambula y''eggulu omusanvu n''ensi, ate abo abaawakanya ebigambo bya Katonda, abo bo be b''okufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(39,64,'39:64','Bagambe (ggwe Nabbi Muhammad) nti: abange mmwe abatamanyi, ebitali Katonda bye mundagira mbe nga nsinza!.');
INSERT INTO "african_dev" VALUES(39,65,'39:65','Ate nga mazima watumirwa obubaka n''eri abo abaakukulembera (nti) singa ogatta ku Katonda ebintu ebirala emirimu gyo gya kwonoonekera ddala, era ojja kubeerera ddala mu bafaafaaganiddwa.');
INSERT INTO "african_dev" VALUES(39,66,'39:66','Wabula Katonda gwoba osinza era obe mu beebaza.');
INSERT INTO "african_dev" VALUES(39,67,'39:67','(Abagatta ku Katonda ebintu ebirala) Katonda tebamussa mu kifo kimusaanira, so nga ensi yonna eriba mu kibatu kye ku lunaku lw''enkomerero, nga eggulu omusanvu gazingiddwa mu mukono gwe ogwa ddyo, musukkulumu era wa waggulu ku ebyo bye bamugattako.');
INSERT INTO "african_dev" VALUES(39,68,'39:68','Engombe erifuuyibwa, ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi nebizikirira, okugyako ekyo Katonda kyaliba ayagadde, oluvanyuma erifuuyibwa omulundi omulala oligenda okulaba nga beesiimbye (bwa ntoogo) batunula.');
INSERT INTO "african_dev" VALUES(39,69,'39:69','Ensi egenda kwakaayakana olw''ekitangaala kya Mukama omulabirizi waayo, ebitabo (by’emirimu gya baddu) bissibwewo, ba Nabbi n’abajulizi (ba Malayika abawandiika emirimu gya baddu) baleetebwe era balamulwe mu mazima era bo tebagenda kulyazaamaanyizibwa.');
INSERT INTO "african_dev" VALUES(39,70,'39:70','Buli mwoyo gulisasulwa mu bujjuvu ebyo bye gwakola, anti bulijjo Katonda amanyidde ddala ebyo bye bakola.');
INSERT INTO "african_dev" VALUES(39,71,'39:71','Abo abaakaafuwala balikunguzzibwa okutwalibwa mu muliro Jahannama nga bali mu bibinja, olwo nno bwe baligutuukako emiryango gyagwo nga giggulwawo, era abakuumi baagwo babagambe nti, abaffe tebaabajjira ababaka nga bava mu mmwe!, nga babasomera ebigambo bya Mukama omulabirizi wa mmwe era nebabatiisa okusisinkana olunaku lwa mmwe luno? baligamba nti weewaawo (batujjira naye twabalimbisa) naye ekigambo ky’ebibonerezo kimaze okukakata ku bakaafiiri.');
INSERT INTO "african_dev" VALUES(39,72,'39:72','Baligambibwa nti muyingire emiryango gy’omuliro Jahannama nga muli bakutuula mu gwo bugenderevu, nagwo kifo kibi eri abeekuza.');
INSERT INTO "african_dev" VALUES(39,73,'39:73','Abo abatya Mukama omulabirizi waabwe balitwalibwa eri e jjana nga bali mu bibinja, olwo nno bwe baligituukako n’emiryango gyayo negiggulwa, n’abakuumi baayo nebabagamba nti emirembe gibe ku mmwe mwakola bulungi (nga muli ku nsi) kale muyingire, mugenda kutuulamu, bugenderevu.');
INSERT INTO "african_dev" VALUES(39,74,'39:74','Baligamba nti ebitendo byonna bya Katonda oyo atutuukiririzza endagaano ye, naatusikiza ettaka (lyo mu jjana) nga tweyagalira mu jjana wonna wetwagala nga kyo kyengera kya mpeera ya bakozi.');
INSERT INTO "african_dev" VALUES(39,75,'39:75','Oliraba ba Malayika nga babugirizza amabbali ga Arish nga batendereza ebitendo bya Mukama omulabirizi waabwe, (ebyo byonna) abaddu baliba balamuddwa mu bwenkanya era walirangirirwa nti ebitendo byonna bya Katonda omulabirizi w''ebitonde byonna.');
INSERT INTO "african_dev" VALUES(40,1,'40:1','Ha Miim.');
INSERT INTO "african_dev" VALUES(40,2,'40:2','Okussa ekitabo (kino Kur’ani) kuva wa Katonda nantakubwa ku mukono, omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(40,3,'40:3','Omusonyiyi we byonoono, era akkiriza okwenenya, nannyini bibonerezo ebikakali, nannyini kugaba, tewali kisinzibwa okugyako yye, obuddo buli gyali.');
INSERT INTO "african_dev" VALUES(40,4,'40:4','Tewali awakana ku bigambo bya Katonda okugyako abo abaakafuwala, kale okwejalabattira kwabwe mu nsi tekukuyigula ttama.');
INSERT INTO "african_dev" VALUES(40,5,'40:5','Oluberyeberye lwabwe abantu ba Nuhu baalimbisa, n''ebibinja ebyajja oluvanyuma lwabwe (nabyo byalimbisa), buli bantu baayagala okutuusa ku mubaka waabwe akabi, baakozesa obukyamu mu kuwakana, bamenyewo amazima, awo nno nembakwata naye okubonereza kwange (olaba) kwali kutya?.');
INSERT INTO "african_dev" VALUES(40,6,'40:6','Nabwekityo, kyakakata ekigambo kya Mukama omulabirizi wo kwabo abaakafuwala nti mazima bo bantu ba mu muliro.');
INSERT INTO "african_dev" VALUES(40,7,'40:7','(Ba Malayika) abo abasitudde Arish, naabo abagibugiriza, batendereza ebitendo bya Mukama omulabirizi waabwe, era ba mukkiriza era basaba (Katonda) ekisonyiwo eri abo abakkiriza (nga bagamba nti) ayi Mukama omulabirizi waffe mu kusaasira ne mukumanya wamalayo buli kintu kyonna, kale sonyiwa abo abeenenya nebagoberera ekkubo lyo era obawonye ebibonerezo by''omuliro Jahiim.');
INSERT INTO "african_dev" VALUES(40,8,'40:8','Ayi Mukama omulabirizi waffe era bayingize e jjana ez’emirembe zewabalagaanyisa (era zewalagaanyisa) abo abaalongooka mu bakadde baabwe ne be bafumbiriganwa nabo ne zadde lyabwe. Mazima ggwe nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(40,9,'40:9','Era obesambye ebyonoono era oyo yenna gw''olyesambya ebibi ku lunaku olwo mazima oliba omusaasidde, era ekyo kwo, kwe kwesiima okusuffu.');
INSERT INTO "african_dev" VALUES(40,10,'40:10','Mazima abo abaakafuwala balikowoolwa (nga bali mu muliro nti) mazima obusungu bwa Katonda busuffu okusinga okwesunguwalira kwa mmwe, bwe mwakowolwanga okujja eri obukkiriza (wabula ate mmwe) nemuwakanya.');
INSERT INTO "african_dev" VALUES(40,11,'40:11','Baligamba nti ayi Mukama omulabirizi waffe watutta emirundi ebiri notuwa obulamu emirundi ebiri, kaakano tukkirizza ebyonoono byaffe (bitusaanidde), abaffe waliwo ekkubo lyonna eritufulumya mu muliro?.');
INSERT INTO "african_dev" VALUES(40,12,'40:12','(Baligambwa nti) ekyo mmwe (kye mulimu) buli Katonda lweyasabwanga yekka nga muwakanya, ate buli lwe yagattibwangako ekintu ekirala olwo nga mukkiriza, kale nno okulamula (olwaleero) kwa Katonda (yekka) owa waggulu owekitiibwa.');
INSERT INTO "african_dev" VALUES(40,13,'40:13','Yye yooyo abalaga obubonero bwe era abassiza amazzi okuva waggulu nga riziki (gye muli) naye teyebulirira (nabyo) okugyako oyo adda eri (Katonda).');
INSERT INTO "african_dev" VALUES(40,14,'40:14','Kale musabe Katonda ng’eddiini mugikola ku lulwe yekka newakubadde nga abakafiiri bakitamwa.');
INSERT INTO "african_dev" VALUES(40,15,'40:15','(Katonda oyo) ow''ebitiibwa ebya waggulu nannyini Arish, assa obubaka okusinziira ku kiragiro kye kwoyo gwaba ayagadde mu baddu be, abe nga atiisa (abantu ebiribaawo ku) lunaku lw''okusisinkana.');
INSERT INTO "african_dev" VALUES(40,16,'40:16','Olunaku bo (abantu) bonna lwe balyeyoleka )mu maaso ga Katonda) tewali kintu kyonna kyava mu bo kiryekweka Katonda, (balibuuzibwa nti) obufuzi bwani olwaleero? (olwo nno nebategezebwa nti) bwa Katonda ali omu nantalemwa.');
INSERT INTO "african_dev" VALUES(40,17,'40:17','Olwaleero buli mwoyo gugenda kusasulwa olwebyo bye gwakola tewali kulyazamanya olwaleero, mazima Katonda mwangu wa kubala.');
INSERT INTO "african_dev" VALUES(40,18,'40:18','Batiise (gwe nabbi Muhammad) olunaku olusembedde, ekiseera emitima lwegiribeera mu malokooli (olw''entiisa) nga banyikaavu, abeeyisa obubi tebagenda kuba na ffa nfe, wadde muwolereza ayinza okuwulirizibwa.');
INSERT INTO "african_dev" VALUES(40,19,'40:19','(Anti) Katonda amanyi amaaso agatali meesimbu era nga bwamanyi ebyo ebifuba bye bikweka.');
INSERT INTO "african_dev" VALUES(40,20,'40:20','Era Katonda alamula mazima, bo abo abasaba ebintu ebirala nebaleka Katonda, tebalina kintu kyonna kye balamula (nekiba kyo). Mazima Katonda ye yaawulira ennyo alaba.');
INSERT INTO "african_dev" VALUES(40,21,'40:21','Abaffe tebatambulako mu nsi ne balala enkomerero yaabo ababasooka nga bwe yali, bo be baali babasinga amaanyi n''ebintu bye baaleka mu nsi Katonda yabakwata olwebyonoono byabwe tewaaliwo abawonya ku Katonda.');
INSERT INTO "african_dev" VALUES(40,22,'40:22','Ekyo (kyali bwekityo) lwakuba nti mazima bo ababaka baabwe babajjiranga n''obunnyonnyofu nebajeema, olwo nno Katonda nabakwata, mazima ye wamaanyi muyitirivu wa kubonereza.');
INSERT INTO "african_dev" VALUES(40,23,'40:23','Era Mazima twatuma Musa ne bya magero byaffe n’obusobozi bwokulambika ensonga.');
INSERT INTO "african_dev" VALUES(40,24,'40:24','Eri Falawo ne Hamaana ne Karuna nebagamba nti (Musa) mulogo mulimba.');
INSERT INTO "african_dev" VALUES(40,25,'40:25','Bweyabaleetera amazima okuva gyetuli baagamba nti mutte abaana abalenzi abaabo abakkiriza abali naye, naye bawala baabwe mubaleke nga balamu, bulijjo enkwe za bakafiiri teziba okugyako mu bubuze.');
INSERT INTO "african_dev" VALUES(40,26,'40:26','Filaawo naagamba nti mundeke nzite Musa kale ayite Mukama omulabirizi we (anti) mazima nze ntidde nti ayinza okukyusa eddiini yammwe, oba ssi ekyo, ayinza okussa mu nsi obwononefu.');
INSERT INTO "african_dev" VALUES(40,27,'40:27','Musa naagamba nti mazima nze nsaba Mukama omulabirizi wange era omulabirizi wa mmwe amponye (obubi) bwa buli mwekuza atakkiriza lunaku lwa kubalibwa.');
INSERT INTO "african_dev" VALUES(40,28,'40:28','Omusajja omukkiriza mu bantu ba Filaawo (naye nga) yali akweka obukkiriza bwe, yagamba nti mutta omusajja olwokuba agamba nti Katonda ye Mukama omulabirizi wange, ate nga abaleetedde obujulizi obuva ewa Mukama omulabirizi wa mmwe, bwaba nga alimba okulimba kwe kuli gyali, naye bwaba nga ayogera mazima ebimu ku byabalaganyisa bijja kubatuukako mazima Katonda talungamya oyo ye eyegalabanja, omulimba.');
INSERT INTO "african_dev" VALUES(40,29,'40:29','Abange mmwe bantu bange olwaleero mmwe mulina obufuzi era nga mumanyiddwa mu nsi, olwo ani anatutaasa ku bibonerezo bya Katonda singa bituggira (olw''obutakkiriza Musa byagamba) Falawo nagamba nti siraba nti mwandisazeewo okugyako kye ndaba (ekyo kutta Musa), ate si balagirira okugyako ekkubo ettuufu.');
INSERT INTO "african_dev" VALUES(40,30,'40:30','Oyo eyakkiriza naagamba nti abange bantu bange (mwegendereze) mazima nze ntya mmwe okutuukibwako nga ebyo ebyatuuka ku bibiina ebyakulembera (ebyekobaana okuyisa obubi ba Nabbi babyo).');
INSERT INTO "african_dev" VALUES(40,31,'40:31','Okugeza ng''enkola y''abantu ba Nuhu ne A-adi ne Thamud naabo abaaliwo oluvanyuma lwabwe, era bulijjo Katonda tayagaliza baddu be kuyisibwa bubi.');
INSERT INTO "african_dev" VALUES(40,32,'40:32','Era abange mmwe bantu bange, mazima nze mbatiisa olunaku lw’enduulu.');
INSERT INTO "african_dev" VALUES(40,33,'40:33','Olunaku lwe mulikyuka mudduke naye nga temuliba n’abawonya yenna ku Katonda, oyo yenna Katonda gwabuza tayinza kufuna mulungamya.');
INSERT INTO "african_dev" VALUES(40,34,'40:34','Era mazima Yusuf yabajjira oluberyeberye n’obujulizi kyokka mwasigala muli mu kubuusabuusa kwebyo bye yabaleetera, okutuusa nga amaze okufa olwo nno nemugamba nti Katonda tagenda kutuma mubaka yenna luvannyuma lwe, bwatyo nno Katonda bwabuza buli oyo yenna yye eyegalabanja abuusabuusa.');
INSERT INTO "african_dev" VALUES(40,35,'40:35','Abo abawakana ku bikwata ku bigambo bya Katonda awatali bujulizi bubajjidde (ekikolwa ekyo) kibi, kinene nnyo ewa Katonda, newaabo abakkiriza, bwatyo Katonda bwazibikira ku buli mutima gwo mwekuza, kiwagi.');
INSERT INTO "african_dev" VALUES(40,36,'40:36','Firawo nagamba nti owange Hamaana (katikkiro we) nzimbira omunaala oba olyawo naatuka ku makubo.');
INSERT INTO "african_dev" VALUES(40,37,'40:37','Amakubo g''eggulu omusanvu olwo nno mbe nga ndaba Katonda wa Musa, wabula mazima mmulowooza okuba nti mulimba, bwatyo Firawo emirimu gye emibi gyamulabisibwa nga mirungi, olwo nno naggibwa ku kkubo, era enkwe za Firawo tezaali okugyako mu kugootaana.');
INSERT INTO "african_dev" VALUES(40,38,'40:38','Era oyo eyakkiriza yagamba nti mungoberere mbalagirire ekkubo lyo bulungamu.');
INSERT INTO "african_dev" VALUES(40,39,'40:39','Abange bantu bange mazima obulamu bwe nsi buno bwakiyita mu lujja, era mazima enkomerero, y’enyumba eyolubereera.');
INSERT INTO "african_dev" VALUES(40,40,'40:40','Oyo yenna akola omulimu omubi tasasulwa okugyako ekigwenkana, ate oyo akola omulimu omulongoofu kaabe musajja oba mukazi (naagukola) nga mukkiriza, abo nno baliyingira e jjana baligabirirwa mu yo awatali kubalirira.');
INSERT INTO "african_dev" VALUES(40,41,'40:41','Abange bantu bange lwaki mbayita okujja eri okuwona nga mmwe mumpita kudda eri muliro!.');
INSERT INTO "african_dev" VALUES(40,42,'40:42','Mumpita mpakanye Katonda era mugatteko ebyo bye ssirinaako kumanya! ate nga nze mbayita kudda eri Nantakubwa ku mukono, omusonyiyi ennyo.');
INSERT INTO "african_dev" VALUES(40,43,'40:43','Tewali kubuusabuusa mazima ekyo kye mumpitira tekirina buyinza ku nsi wadde ku nkomerero, ate nga mazima obuddo bwaffe buli eri Katonda era nga mazima abegalabanja bo be bantu bo mu muliro.');
INSERT INTO "african_dev" VALUES(40,44,'40:44','Lumu mulijjukira bye mbagamba era ensonga yange ngikwasa Katonda, mazima Katonda alabira ddala ebifa ku baddu.');
INSERT INTO "african_dev" VALUES(40,45,'40:45','Katonda naamuwonya obubi bwe nkwe zebasala, obubi bwe bibonorezo ne byetoloola abantu ba Falaawo.');
INSERT INTO "african_dev" VALUES(40,46,'40:46','Omuliro banjulwa gye guli (mu bufu bwabwe) enkya ne ggulo, ate olunaku lw’enkomerero lwe lulituuka walirangirirwa (nti) muyingize abantu ba Firaawo mu bibonerezo ebisinga obukakali.');
INSERT INTO "african_dev" VALUES(40,47,'40:47','Jjukira ekiseera lwe balikayana mu muliro, abanafu (abakulemberwa) bwe baligamba abo abeekuza (abaali abakulembeze nti) mazima ffe twali bagoberezi ba mmwe abaffe mmwe musobola okutukolera ekitundu (kye kibonerezo) kyo muliro?.');
INSERT INTO "african_dev" VALUES(40,48,'40:48','Abo abeekuza baligamba nti, mazima ffe tugulimu fenna wamu, mazima Katonda yamaze okulamula wakati w''abaddu.');
INSERT INTO "african_dev" VALUES(40,49,'40:49','Abaliba mu muliro baligamba abakuumi ba Jahannama nti musabe Mukama omulabirizi wa mmwe atukendeereze ku bibonerezo (waakiri) olunaku lumu.');
INSERT INTO "african_dev" VALUES(40,50,'40:50','(Abakuumi bo muliro) baligamba nti abaffe ababaka ba mmwe tebabajjira n’obujulizi obwenkukunala, baligamba nti kituufu (baatujjira), bali bagambe nti kale musabe, wabula okusaba kwa bakafiiri tekuba okugyako mu bubuze.');
INSERT INTO "african_dev" VALUES(40,51,'40:51','Mazima ffe ddala tutaasa ababaka baffe naabo abakkiriza mu bulamu obw''ensi ne ku lunaku abajulizi lwe baliyimirira (okuwa obujulizi nti ddala obubaka bwatusibwa ku bantu).');
INSERT INTO "african_dev" VALUES(40,52,'40:52','Olunaku abeeyisa obubi okwetonda kwabwe lwe kutalibagasa, era balina ekikolimo nga bwebalina obutuulo obubi (omuliro).');
INSERT INTO "african_dev" VALUES(40,53,'40:53','Mazima twawa Musa obulungamu era netusikiza abaana ba Israil ekitabo (ekyo).');
INSERT INTO "african_dev" VALUES(40,54,'40:54','Nga bulungamu era nga kya kujjukiza eri abo abalina amagezi.');
INSERT INTO "african_dev" VALUES(40,55,'40:55','Kale gumikiriza (ggwe Nabbi Muhammad) mazima endagaano ya Katonda ya mazima era saba ekisonyiwo olw''ekibi kyo, era tendereza ebitendo bya Mukama omulabirizi wo olweggulo ne kumakya.');
INSERT INTO "african_dev" VALUES(40,56,'40:56','Mazima abo abawalaaza empaka ku bubonero bwa Katonda awatali bujulizi bubajjidde, mu bifuba byabwe temuli okugyako okwekuza ate nga tekuyinza kukibatuusaako (ekye kitiibwa n''obulungi Katonda bya gonnomola ku Nabbi we) kale nno saba obukuumi ewa Katonda (anti) mazima yye yemuwulizi omulabi enyo.');
INSERT INTO "african_dev" VALUES(40,57,'40:57','Ddala, okutonda eggulu omusanvu n''ensi kinene okusinga okutonda abantu, naye ddala abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(40,58,'40:58','Era muzibe n''alaba tebenkana nga bwe batenkana abo abakkiriza ne bakola emirimu emirongoofu nomwonoonyi (wabula) mujjukira kitono nnyo.');
INSERT INTO "african_dev" VALUES(40,59,'40:59','Mazima olunaku lw’enkomerero lwa kutuuka teruliimu kubuusabuusa, naye ddala abantu abasinga obungi tebakkiriza.');
INSERT INTO "african_dev" VALUES(40,60,'40:60','Mukama omulabirizi wa mmwe yagamba nti munsabe nja kubaanukula ,mazima abo abeekuza nebatansinza bagenda kuyingira omuliro Jahannama nga baswavu.');
INSERT INTO "african_dev" VALUES(40,61,'40:61','Katonda yooyo eyabateerawo obudde obwekiro muwummuliremu nabateerawo obudde obwemisana nga butangaala (musobole okukoleramu emirimu gya mmwe). Mazima Katonda akolera abantu ebirungi bingi, naye ddala abantu abasinga obungi tebeebaza.');
INSERT INTO "african_dev" VALUES(40,62,'40:62','Oyo nno mwe ye Katonda omulabirizi wa mmwe, omutonzi wa buli kintu, tewali kisinzibwa (mu butuufu) okugyako yye ate muwugulwa mutya.');
INSERT INTO "african_dev" VALUES(40,63,'40:63','(Nga bwe muwugulwa) bwe batyo bwe bwe baawugulwanga abo abaalinga bawakanya ebigambo bya Katonda.');
INSERT INTO "african_dev" VALUES(40,64,'40:64','Katonda yooyo eyabateerawo ensi nga ntebenkevu, nabaterawo eggulu nga kasolya, era nabawa endabika ya mmwe, olwo nno nanyiriza endabika ya mmwe era nabagabirira ebintu ebirungi, oyo nno mmwe ye Katonda omulabirizi wa mmwe, kale nno musukkulumu Katonda, Mukama, omulabirizi w''ebitonde.');
INSERT INTO "african_dev" VALUES(40,65,'40:65','Yye ye mulamu tewali kisinzibwa okugyako yye, kale mumusabe ng''eddiini yonna mugikolera yye yekka, (anti) ebitendo byonna bya Katonda omulabirizi webitonde.');
INSERT INTO "african_dev" VALUES(40,66,'40:66','Bagambe (ggwe Nabbi Muhammad nti mazima nze bwe nnamala okufuna obunnyonnyofu okuva ewa Mukama omulabirizi wange) nagaanibwa okusinza ebyo bye musaba ne muleka Katonda, obunnyonnyofu bwe bwanzijira nga buva ewa Mukama omulabirizi wange, era nalagirwa okwewa ewa Mukama omulabirizi w''ebitonde.');
INSERT INTO "african_dev" VALUES(40,67,'40:67','(Anti) ye yooyo eyabatonda nga abaggya mu ttaka (omulundi ogwasooka nga atonda Adam), oluvanyuma (abatonda nga) abajja mu mazzzi agazaala, oluvanyuma abafuula ekisaayisaayi, oluvanyuma abafulumya nga muli baana, oluvanyuma (abalekawo) nemutuuka okubeera abajjuvu (mu mibiri ne mu kutegeera), oluvanyuma (abalekawo) nemutuuka okubeera abakadde, ate mu mmwe mulimu afa ng''akyali (naatamalaayo emitendera esatu egyogeddwa, (bwe kitaba ekyo abalekawo) mube nga mutuuka ekiseera ekigere (ekya buli omu) mube nga mutegeera (ebyo byonna ebibannyonnyoddwa).');
INSERT INTO "african_dev" VALUES(40,68,'40:68','(Katonda) yye yooyo awa obulamu era yatta, kale nno bwaba asazeewo ekintu akigamba nti beera nekibeera.');
INSERT INTO "african_dev" VALUES(40,69,'40:69','Abaffe tolaba abo abawalaaza empaka ku bigambo bya Katonda engeri gye bakyusibwamu (nebaleka amazima).');
INSERT INTO "african_dev" VALUES(40,70,'40:70','(Abo abaalimbisa ekitabo (Kur’ani) n''ebyo byetwatuma nabyo ababaka baffe kyaddaaki balimanya.');
INSERT INTO "african_dev" VALUES(40,71,'40:71','(Balimanya) mu kiseera enkoligo bwezirissibwa mu nsingo zaabwe era nga basibiddwa ne njegere nga bawalulwa.');
INSERT INTO "african_dev" VALUES(40,72,'40:72','Mu lweje oluvanyuma balisibirwa mu muliro.');
INSERT INTO "african_dev" VALUES(40,73,'40:73','Oluvanyuma baligambwa nti biriwa bye mwagattanga (ku Katonda).');
INSERT INTO "african_dev" VALUES(40,74,'40:74','Nemuleka Katonda, baligamba nti batubuzeeko wabula (mu butuufu) bye twasabanga oluberyeberye temwali kantu. Bwatyo Katonda bwabuza abakafiiri.');
INSERT INTO "african_dev" VALUES(40,75,'40:75','Ebyo nno mmwe (eby''omuliro gwe muyingidde) lwansonga lwebyo bye mwasanyuka nga mu nsi mu butali butuufu, era n''engeri y’obujeemu gye mweyisangamu.');
INSERT INTO "african_dev" VALUES(40,76,'40:76','Muyingire emiryango gy’omuliro Jahannama nga muli ba kutuula mu gwo bugenderevu, bubi nnyo obutuulo bwa beekuza.');
INSERT INTO "african_dev" VALUES(40,77,'40:77','Kale gumikiriza (ggwe Nabbi Muhammad) mazima endagaano ya Katonda ya mazima, kakibe nga ddala tukulaze ebimu kwebyo bye tubalaganyisa oba netukussaako okufa (nga tetubikulaze mu buli ngeri) bagenda kuzzibwa gyetuli.');
INSERT INTO "african_dev" VALUES(40,78,'40:78','Mazima twatuma ababaka oluberyeberye lwo, mu bo mulimu betwakunyumizaako, era mu bo mulimu betutakunyumizaako, era Nabbi yenna yali tayinza kuleeta kya magero okugyako nga Katonda yasazeewo, kale nno ekigambo kya Katonda bwekirijja ensonga zirilamulwa mu mazima, era awo abonoonyi webalifafaganirwa.');
INSERT INTO "african_dev" VALUES(40,79,'40:79','Katonda ye yooyo eyabassizaawo ensolo ez’omumaka ezimu ku zo muzebagale era nga neezimu kwemulya.');
INSERT INTO "african_dev" VALUES(40,80,'40:80','Era mulina mu zo emigaso (emirala ng’ate mingi olwokuzikozesa), mutuukiriza ekyetaago kye mulina mu bifuba bya mmwe (ekyo kujjulula emigugu gya mmwe okuva ebunaayira), era nga musitulirwa ku zo ne ku maato.');
INSERT INTO "african_dev" VALUES(40,81,'40:81','Era (bulijjo Katonda) abalaga obubonero bwe, kaakano bubonero ki obwa Katonda bwe muwakanya.');
INSERT INTO "african_dev" VALUES(40,82,'40:82','Abaffe tebatambulako mu nsi nebalala yali etya enkomerero yaabo ababakulembera, (abo) abaali babasinga obungi, nga babasinga amaanyi era nga be babasinza ne bye baaleka mu nsi, naye wabula tebyabagasa ebyo bye baakola.');
INSERT INTO "african_dev" VALUES(40,83,'40:83','Anti ababaka baabwe bwe baabajjira n’obunnyonnyofu, baasigala basanyuka n’okumanya kwe baali balina ne bazingwako ebyo bye baali bajeeja.');
INSERT INTO "african_dev" VALUES(40,84,'40:84','Bwe baalaba obukambwe bwe bibonerezo byaffe nebagamba nti tukkiriza Katonda yekka era netuwakanya ebyo byonna bye twali tumugattako.');
INSERT INTO "african_dev" VALUES(40,85,'40:85','Okukkiriza kwabwe tekwali kwa kubagasa (okwo kwe balangirira), bwe baamala okulaba obukambwe bwe bibonerezo byaffe (era nga eyo) yenkola ya Katonda eyo ezze ekola mu baddu be, era (ebibonerezo bya Katonda bwe bitandika okussibwa mu nkola) abakafiiri awo wennyini baba bamaze okufafaaganirwa (ne bwekuba kwenenya kuba tekukyakkirizibwa).');
INSERT INTO "african_dev" VALUES(41,1,'41:1','Ha Miim.');
INSERT INTO "african_dev" VALUES(41,2,'41:2','(Kur’ani) bubaka obussibwa okuva ewa Katonda omusaasizi ennyo era omusaasizi owenjawulo.');
INSERT INTO "african_dev" VALUES(41,3,'41:3','(Kyo) kitabo, ebigambo byakyo ebyannyonnyolwa obulungi nga bisomwa, ebiri mu luwarabu (yassibwa) eri abantu abamanyi.');
INSERT INTO "african_dev" VALUES(41,4,'41:4','(Yakka nga) ewa amawulire agessanyu eri (abakkiriza) era nga ewa amawulire agatiisa (eri abatakkiriza) naye abasinga obungi mu bo bagyesamba olwo nno bo tebawulira.');
INSERT INTO "african_dev" VALUES(41,5,'41:5','Nebagamba nti emitima gyaffe giri mu kibikka, tegiyinza kuwulira ekyo kyotuyitira, ate mu matu gaffe mulimu envumbo ate nga wakati waffe nawe waliwo ekyawula (n''olwekyo tetuyinza kukola bifaanagana) kale kola naffe katukule');
INSERT INTO "african_dev" VALUES(41,6,'41:6','Bagambe (ggwe Nabbi Muhammad nti) mazima nze ndi muntu nga mmwe, mpeebwa obubaka nti mazima Katonda wa mmwe, Katonda omu yekka, kale mudde gyali mubwesimbu, era mumwegayirire era okubonaabona kuli eri abagatta ebintu ebirala ku Katonda.');
INSERT INTO "african_dev" VALUES(41,7,'41:7','Abo abatatoola Zakka ate nga ddala nabo olunaku lw''enkomererp bo baluwakanya.');
INSERT INTO "african_dev" VALUES(41,8,'41:8','Mazima abo abakkiriza nebakola emirimu emirongoofu balina empeera etaggwawo.');
INSERT INTO "african_dev" VALUES(41,9,'41:9','Bagambe nti abaffe mmwe muwakanya oyo eyatonda ensi mu nnaku bbiri nemumuteekako ebisinzibwa ebirala, oyo (gwe mukola ebyo) ye mulezi w''ebitonde.');
INSERT INTO "african_dev" VALUES(41,10,'41:10','Era yassa mu yo (ensi) ensozi nga asinziira ku ngulu kwayo, (olwo nno nezikka wansi) era naagissaamu omukisa era yagererera mu yo ebyokulya byayo (ebiriibwa ebiramu byonna ebigirimu, ebyo byonna yabimalira) mu nnaku nnya, (ekyo kye kyokwanukula) ekituufu eri ababuuza.');
INSERT INTO "african_dev" VALUES(41,11,'41:11','Oluvanyuma yadda ku kukola eggulu nga nalyo (mu kusooka) lyali mukka, olwo nno naligamba n''ensi, nti mujje okwagala n''obutayagala, byombi nebigamba nti tuzze nga tugonze.');
INSERT INTO "african_dev" VALUES(41,12,'41:12','Olwo naagamaliriza nga gali eggulu musanvu mu nnaku bbiri, naalagira mu buli ggulu ne mussibwamu ebintu byamu era netuwunda eggulu eririraanye ensi n’amataala n’abakuumi, okwo nno kugera kwa nantakubwa ku mukono, omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(41,13,'41:13','Singa bawuguka (nebatakukkiriza) bagambe nti mbatiisizza ebibonerezo ebikakali okufaanana nga ebibonerezo ebyatuuka ku bantu ba A-adi ne Thamud.');
INSERT INTO "african_dev" VALUES(41,14,'41:14','Mu kiseera ababaka bwe baabajjira nga babava mu maaso ne mabega waabwe nga bagamba nti, temusinzanga okugyako Katonda (abatakkiriza) ne bagamba nti singa Mukama omulabirizi waffe yayagala (kututumira) yandiisizza Malayika n’olwekyo mazima ffe tuwakanya ebyo bye mutumiddwa nabyo.');
INSERT INTO "african_dev" VALUES(41,15,'41:15','Bo abantu ba A-adi anti beekuza mu nsi mu butali butuufu, era nebagamba nti ani atusinga amaanyi? abaffe baali tebakiraba nti mazima Katonda oyo eyabatonda, yye yaabasinga amaanyi. Okukola ekyo baali bawakanya obubonero bwaffe.');
INSERT INTO "african_dev" VALUES(41,16,'41:16','Netubasindikira kibuyaga owamaanyi mu nnaku ezaali zijjudde ebizibu tulyoke tubakombese ku bibonerezo ebyobuswavu mu bulamu obwensi, so nga ate ebibonerezo by''enkomerero bye bisinga okuswaza era bo tebagenda kutaasibwa.');
INSERT INTO "african_dev" VALUES(41,17,'41:17','Bo aba Thamud twabalaga ekkubo ly''obulungamu naye bo nebakulembeza obwamuzibe ku bulungamu, olwo nno okubwatuka kwe bibonerezo ebinyomesa ne kubatuukako olwebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(41,18,'41:18','Era twawonya abakkiriza era nga baali batya Katonda.');
INSERT INTO "african_dev" VALUES(41,19,'41:19','Ate olunaku abalabe ba Katonda lwe balikunganyizibwa nga batwalibwa eri omuliro awo nno nga bateereddwa mu bibinja.');
INSERT INTO "african_dev" VALUES(41,20,'41:20','Bwe baligutuukako, amatu gaabwe n’amaaso gaabwe n’amaliba gaabwe, biribawaako obujulizi kwebyo bye baakolanga.');
INSERT INTO "african_dev" VALUES(41,21,'41:21','Baligamba amaliba gaabwe (ate mmwe) lwaki mutuwaddeko obujulizi? galigamba nti Katonda atwatuzza oyo ayatuza buli kintu, era yye ye yabatonda omulundi ogwasooka era nga gyali gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(41,22,'41:22','Era (bwemwali ku nsi) temwekwekako olw''okutya amatu ga mmwe wadde amaliba ga mmwe okubawaako obujulizi, wabula mwalowooza nti mazima Katonda tamanyi bingi mwebyo bye mukola.');
INSERT INTO "african_dev" VALUES(41,23,'41:23','Okwo nno okulowooza kwa mmwe, okwo kwe mwalowooza ku Mukama omulabirizi wa mmwe, kwe kwa battattana olwo nno nemufuuka abo mu bafafaganirwa.');
INSERT INTO "african_dev" VALUES(41,24,'41:24','Ne bwe banagumikiriza, omuliro bwe butuulo bwabwe, era ne bwebanasaba okubaddiramu (mbu bazzibwe ku nsi bakole ebirungi) ssi bakuddirwamu.');
INSERT INTO "african_dev" VALUES(41,25,'41:25','Twabateerawo ba nnaabwe (abababuza) olwo nno ne babalabisiza bulungi (ebibi) ebiri mu maaso gaabwe ne mabega waabwe, ekigambo (ekyokubonerezebwa) nekibakakatako, ne bayingira mu bibiina (ebibi) ebya Majinni n’abantu ebya kulembera, mazima bo baali abafaafaganirwa.');
INSERT INTO "african_dev" VALUES(41,26,'41:26','Abo abaakafuwala nebagamba nti temuwuliriza Kur’ani eno wabula mujizannyiremu oba olyawo ne muwangula.');
INSERT INTO "african_dev" VALUES(41,27,'41:27','Ddala tugenda kukombya abo abaakafuwala ku bibonerezo ebikakali era tugenda kubasasulira ddala obubi bwebyo bye baalinga bakola.');
INSERT INTO "african_dev" VALUES(41,28,'41:28','Ogwo omuliro yempeera ya balabe ba Katonda, balina mu gwo obutuulo obw''olubeerera nga eyo yempeera (gye balifuna) olwokubanga baawakanya ebigambo byaffe.');
INSERT INTO "african_dev" VALUES(41,29,'41:29','Abo abaakafuwala baligamba nti ayi Mukama omulabirizi waffe, tulage mu Majinni n’abantu abo abaatubuza tubasse wakati w''ebigere byaffe balyoke babe mu ba wansi (ddala).');
INSERT INTO "african_dev" VALUES(41,30,'41:30','Mazima abo abagamba nti Katonda ye Mukama omulabirizi waffe oluvanyuma ne babeera beesimbu, ba Malayika bakka (nga bajja) gyebali (nga babagamba nti) temutya era temweraliikirira era musanyuke olw’ejjana eyo gye mwalinga mulagaanyisibwa.');
INSERT INTO "african_dev" VALUES(41,31,'41:31','Ffe (Ba Malayika) ffe banna mmwe mu bulamu obwensi ne mu bulamu obw''enkomerero, era mulina okufuna nga muli mu yo, ebyo emyooyo gya mmwe bye gyagala era nga bwe mulina okufuna ebyo bye musaba.');
INSERT INTO "african_dev" VALUES(41,32,'41:32','(Ebyo) nga bugenyi obuva ewa (Katonda) omusonyiyi ennyo omusaasizi.');
INSERT INTO "african_dev" VALUES(41,33,'41:33','Ani ow''ebigambo ebirungi okusinga oyo akowoola (abantu) okudda eri Katonda era naakola emirimu emirongoofu era naagamba nti mazima nze ndi mu beewa ewa Katonda?.');
INSERT INTO "african_dev" VALUES(41,34,'41:34','Era ekirungi n''ekibi tebyenkana, ggyawo (ekibi) ng’okozesa ekyo ekisinga obulungi, onogenda okulaba nga oyo awabadde wakatiiwo naye obulabe nga ddala yye ye womukwano ffa nfe.');
INSERT INTO "african_dev" VALUES(41,35,'41:35','Era (ekyo) tebayinza ku kiweebwa okugyako abo abagumikiriza era tayinza kukiweebwa okugyako ow''omukisa ogwamaanyi.');
INSERT INTO "african_dev" VALUES(41,36,'41:36','Wabula singa wabaawo ekikulabankanya nga kiva mu Sitane, oteekwa okusaba obukuumi ewa Katonda, mazima yye yaawulira ennyo, omumanyi.');
INSERT INTO "african_dev" VALUES(41,37,'41:37','Ne mu bubonero bwe (obulaga obuyinza bwe) kye kiro n’emisana ne njuba n’omwezi, temuvunnamiranga enjuba wadde omwezi, muvunnamirenga Katonda oyo eyabitonda bwe muba nga yye yekka gwe musinza.');
INSERT INTO "african_dev" VALUES(41,38,'41:38','(Naye bwe baneekuza (ssi kikulu) anti abo abali ewa Mukama omulabirizi wo bamutendereza ekiro n’emisana era nga bo tebakoowa.');
INSERT INTO "african_dev" VALUES(41,39,'41:39','Era mu bubonero bwe (kwe kuba nti) mazima ggwe olaba ensi nga nkalu ketugitonnyesaako amazzi yenyeenya (nefuuka yakiragala) era neyongera okumeza ebimera. Mazima oyo agizzaamu obulamu (neeba ngimu) ddala wa kuzuukiza abafu mazima yye muyinza wa buli kintu kyonna.');
INSERT INTO "african_dev" VALUES(41,40,'41:40','Mazima abo abawalaaza empaka mu bigambo byaffe tebatwekwese, abaffe oyo alisuulwa mu muliro yaasinga obulungi oba oyo alijja ku lunaku lw’enkomerero ng’ali mirembe, (kale) mukole bye mwagala mazima yye (Katonda) alabira ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(41,41,'41:41','Mazima abo abawakanya okubuulirira (Kur’ani) bwe kwa bajjira (kalibajjutuka) era mazima yo (Kur’ani) kitabo kya kitiibwa.');
INSERT INTO "african_dev" VALUES(41,42,'41:42','Obutali butuufu tebuyinza kujijjira kuva mu maaso gaayo, wadde mabega waayo, essibwa nga eva wa Mugoba nsonga atenderezebwa ennyo (Katonda).');
INSERT INTO "african_dev" VALUES(41,43,'41:43','Ggwe (Muhammad) tewali bikugambwa okugyako nga bya gambwa dda ababaka abakukulembera. Mazima Mukama omulabiriziwo ye nannyini kusonyiwa era ye nannyini bibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(41,44,'41:44','Singa twafuula Kur’ani eno nga ya lulimi olutali luwarabu (abawarabu) ddala baligambye nti singa ebigambo byayo byannyonnyolwa (netuba nga tubitegeera, kati ate) eri mu lumimi olutali luwarabu ate nga ekkira ku muwarabu, bagambe nti yyo ku bakkiriza kya kulungama era kya kuwonya, ate abo abatakkiriza amatu gaabwe galimu envumbo, olwo nno yo ku bo buzibe bwa maaso, abo bakowoolwa okuva mu kifo ekyewala (n''olwekyo ssi bangu ba kuwuliza).');
INSERT INTO "african_dev" VALUES(41,45,'41:45','Mazima twawa Musa ekitabo ne kyawukanwamu, singa ssi kigambo ekyakulembera nga kiva ewa Mukama omulabirizi wo walisaliddwawo wakati waabwe mubwangu, era mazima nabo (abatakukkiriza ggwe Nabbi Muhammad) bali mu kubuusabuusa okujjudde okutankana mu yo (Kur’ani).');
INSERT INTO "african_dev" VALUES(41,46,'41:46','Oyo akola omulimu omulongoofu akolera mwoyo gwe, ate oyo ayonoona akabi kakyo kadda ku gwo, ate nga Mukama omulabirizi wo si mulyazamanyi wa baddu (be).');
INSERT INTO "african_dev" VALUES(41,47,'41:47','Gyali okumanya olunaku lw''enkomerero gye kuzzibwa, ebibala tebiva mu bikuta byabyo, era ekikazi tekifuna lubuto wadde okuzaala wabula ku lwakumanya kwe, n’olunaku (Katonda) lwalibakowoola (ng''a babuuza) nti baliwa abo bemwangattako? baligamba nti tukutegeeza nti tewali nomu mu ffe ayinza kuba mujulizi (kw''ekyo ekyokuba nti olina ebikugattibwako).');
INSERT INTO "african_dev" VALUES(41,48,'41:48','Era biribabulako ebyo bye baasinzanga oluberyeberye, era bagenda kukakasa nti tebalina buddukiro.');
INSERT INTO "african_dev" VALUES(41,49,'41:49','Omuntu takoowa kusaba (okufuna) obulungi naye akabi bwe kamutuukako aggwamu essuubi naakutuka n''okusuubira.');
INSERT INTO "african_dev" VALUES(41,50,'41:50','Naye bwetumukombya ku kusaasira okuva gye tuli oluvanyuma lwakabi akaba kamutuseeko, ddala agamba nti kino nkifunye lwa kukola kwange era ssirowooza nti olunaku lw''enkomerero lulibaawo (naye) ne bwendizzibwa ewa Mukama omulabirizi wange, mazima nze ndiba nina okufuna ebirungi waali, kale nno ddala tugenda kutegeeza abo abakafuwala, ebyo bye baakola era ddala tugenda kubakombesa ku bibonerezo ebizito ennyo.');
INSERT INTO "african_dev" VALUES(41,51,'41:51','Bwetugabira omuntu ebyengera awuguka (naava ku ddiini) nadda ku bbali (nga takyayagala kubeera na bantu balala) olwokwekuza naye akabi bwe kamutuukako (olwo adda eri Katonda) era n''aba nga asaba okusaba okutakoma.');
INSERT INTO "african_dev" VALUES(41,52,'41:52','Bagambe nti mukiraba nti singa ddala (Kur’ani) yava wa Katonda oluvanyuma nemugiwakanya, ani mubuze asinga oyo ali mu kuwakana okuli ewala (n’amazima).');
INSERT INTO "african_dev" VALUES(41,53,'41:53','Tujja kubalaga obubonero bwaffe mu bwengula ne mu bo bennyini okutuusiza ddala amazima lweganabeeyoleka nti ddala yyo (Kur''ani) ge mazima, abaffe tekimala okuba nga Mukama omulabirizi wo nga ddala yye abaawo (nga kikolebwa).');
INSERT INTO "african_dev" VALUES(41,54,'41:54','Abange mazima bo bali mu kubuusabuusa okusisinkana Mukama omulabirizi waabwe, abange (mukimanye nti) mazima yye yetoolodde (mu kumanya kwe) buli kintu.');
INSERT INTO "african_dev" VALUES(42,1,'42:1','Ha Miim.');
INSERT INTO "african_dev" VALUES(42,2,'42:2','Ayin Siin Kaaf.');
INSERT INTO "african_dev" VALUES(42,3,'42:3','Bwatyo Katonda nantalemwa, mugobansonga, akutumira obubaka era nga bweyatuma eri abo abaaliwo oluberyeberye lwo.');
INSERT INTO "african_dev" VALUES(42,4,'42:4','Bibye yekka byonna ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi ,era yye ye wa waggulu ennyo owekitiibwa.');
INSERT INTO "african_dev" VALUES(42,5,'42:5','Eggulu omusanvu kumpi kweyuzaamu okutandikira waggulu waago (ku lwo kugulumiza Katonda) ne ba Malayika batendereza ebitendo bya Mukama omulabirizi waabwe, era basabira abali mu nsi ekisonyiwo, abange mazima Katonda yye ye musonyiyi ennyo omusaasizi.');
INSERT INTO "african_dev" VALUES(42,6,'42:6','Naye abo abeteerawo ebisinzibwa ebirala nebamuvaako, Katonda mulondoozi ku bo, era ggwe toli mukuumi ku bo.');
INSERT INTO "african_dev" VALUES(42,7,'42:7','Bwekityo twakuweereza Kur’ani nga eri mu luwarabu obe nga otiisa (nayo) maama w''ebibuga byonna (Makkah) n''abali mu bitundu ebikyetoolodde era otiise (abantu) olunaku lw''okukungaana olutaliimu kubuusabuusa, nga ekibinja kya kuyingira jjana ate ekibinja ekirala kya kuyingira muliro ogububuuka.');
INSERT INTO "african_dev" VALUES(42,8,'42:8','Singa Katonda yayagala (bonna) yandibafudde ekibiina kimu, naye ayingiza oyo gwaba ayagadde mu kusaasira kwe, ate abeeyisa obubi tebaliba na mukuumi wadde omutaasa.');
INSERT INTO "african_dev" VALUES(42,9,'42:9','Oba baaleka Katonda nebeeterawo ba katonda (abalala) so nno Katonda ye ye mukuumi, era yye ye wokulamusa abafu, era ye (bulijjo) muyinza ku buli kintu kyonna.');
INSERT INTO "african_dev" VALUES(42,10,'42:10','Ekintu kyonna kyemuba mwawukanyeemu, okulamula kwakyo kuli eri Katonda, oyo mwe Katonda ye Mukama omulabirizi wange. ye yekka gwe nneesiga era gyali gyenzira.');
INSERT INTO "african_dev" VALUES(42,11,'42:11','Omutandisi weggulu ne nsi, abateerawo nga agya mu mmwe bemufumbiriganwa nabo, era yabateerawo mu bisolo ebirundibwa bibiri bibiri (ekisajja n''ekikazi) nga abazalukusa mu nkola eyo, tewali kintu kiringa yye, era yye ye muwulizi ennyo omulabi ennyo.');
INSERT INTO "african_dev" VALUES(42,12,'42:12','Yekka yaalina ebitambulirwako eggulu omusanvu ne nsi, ayanjuluza riziki eri oyo gwaba ayagadde, ate nafunza (kwoyo gwaba ayagadde) mazima yye ye mumanyi ennyo wa buli kintu.');
INSERT INTO "african_dev" VALUES(42,13,'42:13','Yabalaalikako mu ddiini ebyo bye yalaamira Nuuhu n''ebyo bye twassa ku ggwe n''ebyo byetwalamira Ibrahim ne Mussa ne Issa ebigamba nti muyimirizeewo eddiini temujawukanangamu kizitoye kubagatta ku Katonda ebintu ebirala ekyo kye mubakowoola okujja gye kiri, Katonda alondobamu oyo gwaba ayagadde nadda gyali, era alungamya okudda gyali oyo eyeemenya.');
INSERT INTO "african_dev" VALUES(42,14,'42:14','Tebaayawukana okugyako luvannyuma lwa kumanya okubajjira (nga ekyabatuusa kwekyo) bwe bukyayi obwali wakati waabwe, era singa si kigambo ekyakulembera nga kiva ewa Mukama omulabirizi wo (ekyo butazikirizaawo bonoonyi) okutuusa ebbanga eggere (erya baweebwa) waalisaliddwawo wakati waabwe (mu kiseera ekyo kye nnyini) era mazima abo abasikizibwa ekitabo oluvanyuma lwabwe, mu kyo bali mu kubuusabuusa okujjudde okutankana.');
INSERT INTO "african_dev" VALUES(42,15,'42:15','N’olwekyo kale kowoola (abantu okujja eri e ddiini) era obeere mwesimbu nga bwe walagirwa, era togoberera kwagala kwabwe era gamba nti nzikiriza e bitabo ebyo Katonda bye yassa, era nalagirwa okukola obwenkanya wakati wa mmwe. Katonda ye mulabirizi waffe era omulabirizi wa mmwe ffe tulina emirimu gyaffe na mmwe mulina emirimu gya mmwe. Tewali nkayana wakati waffe na mmwe (anti amazima meyolefu), Katonda agenda kutusisinkanya (ffe na mmwe) era gyali yokka yeeri obuddo.');
INSERT INTO "african_dev" VALUES(42,16,'42:16','N''abo abawakana ku bikwata ku Katonda, oluvanyuma lwo kuba nti (omubaka Muhammad) yayanukulwa (nagobererwa), ensonga yaabwe ya butaliimu mu maaso ga Mukama omulabirizi waabwe, era baliko obusungu n''ebibonerezo ebikambwe.');
INSERT INTO "african_dev" VALUES(42,17,'42:17','Katonda yooyo eyassa ebitabo nga bya mazima n’obwenkanya. yye omanyira kuki oba olyawo olunaku lw’enkomerero luli kumpi.');
INSERT INTO "african_dev" VALUES(42,18,'42:18','Abo abatakkiriza basaba luleetebwe mangu, ate abo abakkiriza balwelaliikirira era bamanyi nti mazima ddala lwo (okutuuka kwalwo) ge mazima, abange mazima abo abawakana ku kubaawo kwo lunaku lw’enkomerero ddala bali mu bubuze obwewala.');
INSERT INTO "african_dev" VALUES(42,19,'42:19','Katonda alumirwa abaddu be, agabira oyo gwaba ayagadde era yye ye wamanyi nantakubwa ku mukono.');
INSERT INTO "african_dev" VALUES(42,20,'42:20','Oyo ayagala ebikungulwa byo bulamu obwoluvanyuma tumwongeza mu bikungulwa bye, nooyo ayagala ebikungulwa byo bulamu bwensi tubimuwaako (ebyo bye twamugerera) naye nga talina ku nkomerero mugabo gwonna.');
INSERT INTO "african_dev" VALUES(42,21,'42:21','(Ekibabuza) oba balinayo be bagatta ku Katonda nga ba bateerawo amateeka g’eddiini ago Katonda ga takkiriza, era singa si kigambo ekyokusalawo (okulibaawo ku lunaku lw’enkomerero mangu ddala) wandiramuddwa wakati waabwe. Era mazima abeeyisa obubi balina ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(42,22,'42:22','(Ku lunaku olwo) oliraba abeeyisa obubi nga beraliikirira olwebyo bye bakola, era nga ddala ekyo kigenda kubatuukako ate abo abakkiriza ne bakola emirimu emirongoofu balibeera mu bifo byo mu jjana, balifuna ebyo bye baliyagala ewa Mukama omulabirizi waabwe, ekyo kyo, bwe bulungi obusuffu.');
INSERT INTO "african_dev" VALUES(42,23,'42:23','Ekyo kyekyo Katonda kyasanyusa nakyo abaddu be abo abakkiriza ne bakola emirimu emirongoofu. (Ggwe Nabbi Muhammad) bagambe nti (ekyo kubatuusaako obubaka) si kibasabirako mpeera wabula (kye mbagalamu) kwe kukolagana mu ngeri y’omukwano nga ab’oluganda. Oyo yenna akola obulungi tubumwongezaamu obulungi obulala, mazima Katonda musonyiyi nnyo musiimi nnyo.');
INSERT INTO "african_dev" VALUES(42,24,'42:24','Oba (ekibabuza lwakuba) bagamba nti (Muhmmad) yajweteka ku Katonda ebigambo mu bulimba, Katonda bwaba ayagadde asobola okuzibikira omutima gwo (singa okola nga bwe bagamba), era Katonda naggyawo ebitali bituufu nakakasa amazima olwebigambo bye, mazima yye amanyidde ddala ebiri mu bifuba.');
INSERT INTO "african_dev" VALUES(42,25,'42:25','Era yye yooyo akkiriza okwenenya kwa baddu be era naasonyiwa ebibi, era amanyi ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(42,26,'42:26','Era ayanukula (okusaba kwabo) abakkiriza ne bakola emirimu emirongoofu era nabongera ebirungi bye, bo abakafiiri balina ebibonerezo ebikakali.');
INSERT INTO "african_dev" VALUES(42,27,'42:27','Singa Katonda yagaziya riziki ku baddu be balyonoonye mu nsi, wabula assa mu kipimo ekyo kyaba ayagadde mazima ye amanyidde ddala ebyomunda era alaba nnyo ebikwata ku baddu be.');
INSERT INTO "african_dev" VALUES(42,28,'42:28','Era ye yooyo atonnyesa enkuba oluvanyuma (lwa bantu) okukutuka n’okusuubira ,era abunyisa okusaasira kwe, era yye, ye mukuumi atenderezebwa ennyo.');
INSERT INTO "african_dev" VALUES(42,29,'42:29','Naakamu mu bubonero bwe (obulaga obuyinza bwe) kwe kutondebwa kwe ggulu omusanvu ne nsi. Nebyo ebirimu obulamu ebitambula bye yassa mu byombi. Era yye asobola okubakungaanya wayagalira.');
INSERT INTO "african_dev" VALUES(42,30,'42:30','Obuzibu bwonna obubatuukako kiba lwa nsonga yeebyo emikono gya mmwe byegiba gikoze ate nga alekera bingi.');
INSERT INTO "african_dev" VALUES(42,31,'42:31','Era mmwe temuli bayinza kulemesa (Katonda) ku nsi ate era nga ogyeko Katonda temulina mukuumi wadde omutaasa.');
INSERT INTO "african_dev" VALUES(42,32,'42:32','Era mu bubonero bwe g’emaato agaseyeeya ku nyanja nga galinga ensozi.');
INSERT INTO "african_dev" VALUES(42,33,'42:33','Bwaba ayagadde asobola okuyimiriza empewo (amaato) negabeera nga gayimiridde ku ngulu kwayo (ennyanja, nga tegabbira), mazima mwekyo mulimu obubonero (obulaga obuyinza bwa Katonda) eri buli mugumikiriza ennyo omwebaza.');
INSERT INTO "african_dev" VALUES(42,34,'42:34','Oba ayinza okugabbiza (nebazikirira) olw''ebibi bye baba bakoze, anti bulujjo alekera bingi.');
INSERT INTO "african_dev" VALUES(42,35,'42:35','Era abo abawakanya ebigambo byaffe, bateekwa bamanye nti tebagenda kufuna buddukiro.');
INSERT INTO "african_dev" VALUES(42,36,'42:36','Buli kintu kyonna kyemuba muweereddwa bya kwe yagala bya bulamu bwa nsi, so nga ebiri ewa Katonda bye bisinga obulungi era bye byo kusigalawo nga byabo abakkiriza ,era nga Mukama omulabirizi waabwe yekka gwe beesiga.');
INSERT INTO "african_dev" VALUES(42,37,'42:37','Era beebo abewala ebibi ebinene ne byo buwemu era bo bweba banyiize basonyiwa.');
INSERT INTO "african_dev" VALUES(42,38,'42:38','Era beebo abayanukula Mukama omulabirizi waabwe ne bayimirizaawo e sswala era nga bulijjo ekigambo kyabwe (kisalwawo nga kiyita mu) kuteseganya wakati waabwe era nga bagaba kwebyo bye tubagabirira.');
INSERT INTO "african_dev" VALUES(42,39,'42:39','Era beebo bwe baba batuukiddwako obulumbaganyi, bo belwanako.');
INSERT INTO "african_dev" VALUES(42,40,'42:40','Era empeera y''ekibi eba kibi ekifaanana nga kyo, kale nno oyo alekera natabagana (ne mu nne) okusasulwa kwe kuli ku Katonda, anti mazima yye (Katonda) tayagala bayisa ba nnaabwe bubi.');
INSERT INTO "african_dev" VALUES(42,41,'42:41','Omuntu eyetaasa nga abadde ayisiddwa bubi abo tebabalinaako kkubo (lya kubavunaana).');
INSERT INTO "african_dev" VALUES(42,42,'42:42','Mazima ekkubo (ly’okuvunaana) liri kwabo abayisa obubi abantu ne bonoona mu nsi awatali nsonga, abo balina ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(42,43,'42:43','Naye omuntu agumikiriza nasonyiwa, mazima ddala ekyo kya mu bintu ebikkaatirizibwa (omukkiriza byateekwa okukola)');
INSERT INTO "african_dev" VALUES(42,44,'42:44','(Awatali Katonda) oyo yenna Katonda gwabuza tayinza kuba na mukuumi, oliraba abeeyisa obubi kebaliraba ebibonerezo baligamba nti, abaffe waliwo ekkubo eriddayo (ku nsi).');
INSERT INTO "african_dev" VALUES(42,45,'42:45','Olibalaba nga baleetebwa gye biri (ebibonerezo) nga banyomebwa olwokukkakkanyizibwa (okubatusiddwaako) baliba batunuulira ku mabbali g’aliiso (eriiso nga babba libbe), abo abakkiriza baligamba nti mazima abo abafafaganirwa beebo abafafagana bo bennyini nebafafaganya abantu baabwe ku lunaku lw''enkomerero. Mazima abeeyisa obubi ba kubeera mu bibonerezo ebyolubeerera.');
INSERT INTO "african_dev" VALUES(42,46,'42:46','Era tebaalina bakuumi babataasa ku Katonda, oyo yenna Katonda gwabuza tayinza kuba na kkubo (lya kulungama).');
INSERT INTO "african_dev" VALUES(42,47,'42:47','Mwanukule Mukama omulabirizi wa mmwe nga olunaku terunnatuuka lwe wataliba buddukiro kuwona Katonda. Ku lunaku olwo temuliba na buddo, era temuliba na buwakaniro.');
INSERT INTO "african_dev" VALUES(42,48,'42:48','Bwebaba bagaanye tetukutumanga kuba kalondoozi ku bo, tovunanyizibwa okugyako okutuusa (obubaka). Era mazima ffe bwetukombesa omuntu ku kusaasira okuva gyetuli ku musanyusa, naye bwe batuukwako obuzibu olwebyo emikono gyabwe byegiba gikoze (ng’olwo bakyusa enjogera) anti bulijjo mazima omuntu tasiima.');
INSERT INTO "african_dev" VALUES(42,49,'42:49','Bwa Katonda obufuzi bwe ggulu omusanvu ne nsi, atonda ekyo kyaba ayagadde, agabira oyo gwaba ayagadde abaana ab’obuwala, era nagabira oyo gwaba ayagadde (omulala) abalenzi.');
INSERT INTO "african_dev" VALUES(42,50,'42:50','Oba abagattira ab’obuwala n’abobulenzi ate oluusi gwaba ayagadde amufuula mugumba (anti) mazima yye ye mumanyi ennyo omuyinza.');
INSERT INTO "african_dev" VALUES(42,51,'42:51','Tekisaanirangako muntu Katonda kwogera naye, okugyako nga amusaako bubaka, oba nayogera naye nga asinziira mabega wa kintu ekyawulawo, oba okutuma omubaka (Malayika), olwo nno kulwokusalawokwe namutumira kyaba ayagadde (anti) mazima yye wa waggulu nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(42,52,'42:52','Nabwekityo twakutumira (ggwe Nabbi Muhammad) omwoyo (Kur’ani eyitibwa mwoyo wano olwokuba yeewa emyooyo gy’abantu obulamu) nga ekyo kisinziira ku kiragiro kyaffe. Wali tomanyi nti ekitabo (Kur’ani) kye ki, wadde obukkiriza naye twagifuula ekitangaala nga tulungamya nayo oyo gwetuba twagadde mu baddu baffe, era mazima ggwe ddala olungamya eri ekkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(42,53,'42:53','Ekkubo lya Katonda oyo bibye yekka ebiri mu ggulu omusanvu nebyo ebiri mu nsi. Abange eri Katonda ebintu byonna gye bidda.');
INSERT INTO "african_dev" VALUES(43,1,'43:1','Ha Miim.');
INSERT INTO "african_dev" VALUES(43,2,'43:2','Ndayidde ekitabo ekinnyonnyofu.');
INSERT INTO "african_dev" VALUES(43,3,'43:3','Mazima ffe twagissa Kur’ani nga eri mu luwarabu oba olyawo ne mutegeera.');
INSERT INTO "african_dev" VALUES(43,4,'43:4','Era mazima yo eri ku nsibuko ye bitabo (Lauhul mahfudh) ddala yo ya waggulu nnyo ejjudde amagezi.');
INSERT INTO "african_dev" VALUES(43,5,'43:5','Abaffe tunabajjirako ddala okubuulirira olwokuba muli abantu abasukka ekikemo (abatagoberera mateeka ga Katonda).');
INSERT INTO "african_dev" VALUES(43,6,'43:6','Bameka ba Nabbi betwatuma mu baasooka?.');
INSERT INTO "african_dev" VALUES(43,7,'43:7','Tewali Nabbi yenna yabajjira okugyako bamujeejanga.');
INSERT INTO "african_dev" VALUES(43,8,'43:8','Olwo nno netuzikiriza abamaanyi okusinga abo (abakafiiri be Makkah) era ekyatuuka ku baakulembera kimanyiddwa (abalala bandikitutte nga ekyokuyiga).');
INSERT INTO "african_dev" VALUES(43,9,'43:9','Singa obabuuza nti ani yatonda eggulu omusanvu ne nsi, ddala bajja kugamba nti yabitonda nantakubwa ku mukono omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(43,10,'43:10','Oyo eyabateerawo ensi nga nyanjuluze nagibateeramu amakubo mulyoke mulungame (mutuuke gye mulaga).');
INSERT INTO "african_dev" VALUES(43,11,'43:11','Era oyo assa okuva waggulu amazzi nga ga kigero (ekyetaagisa) netuzza bugya nago ekitundu ekifu (olwekyeya) bwemutyo bwe mulifulumizibwa (nga muzuukira).');
INSERT INTO "african_dev" VALUES(43,12,'43:12','Era yooyo eyatonda emitindo ebiri ebiri gyonna era nabateerawo amaato n’ebisolo ebyawaka ebyo bye mwebagala.');
INSERT INTO "african_dev" VALUES(43,13,'43:13','Mube nga mutebenkera ku migongo gyazo, oluvanyuma musiime ekyengera kya Mukama omulabirizi wa mmwe wemuzituulirako, era mugambe nti, musukkulumu oyo eyatugondeza kino tetwandibadde bakisobola.');
INSERT INTO "african_dev" VALUES(43,14,'43:14','Era mazima ddala ffe tuli ba kudda ewa Mukama omulabirizi waffe.');
INSERT INTO "african_dev" VALUES(43,15,'43:15','Naye abatakkiriza bateeka ku Katonda abamu ku baddube (nti nabo ba Katonda), mazima omuntu mwewakanyi omweyolefu.');
INSERT INTO "african_dev" VALUES(43,16,'43:16','Oba mwebyo byatonda mweyasalawo okwegyira abawala ate mmwe naasalawo okubawa abalenzi.');
INSERT INTO "african_dev" VALUES(43,17,'43:17','So nga omu kubo bwaba aweereddwa amawulire nti afunye omwana agwa mu kiti kyabo be bapaatiika ku Katonda ekyenyi kye kisiiba kifumye era nga naye munyiikaavu.');
INSERT INTO "african_dev" VALUES(43,18,'43:18','Abaffe oyo akuzibwa ng’ali mu bya kwenyiriza, era nga yye mu kunnyonnyola taba na nsonga nywevu (gwemuwa Katonda!).');
INSERT INTO "african_dev" VALUES(43,19,'43:19','Ba Malayika abo, abaddu ba Katonda omusaasizi ne babafuula abakazi! abaffe baaliwo nga batondwa? Obujulizi bwabwe (bwe bepampalika okuwa) bujja kuwandiikibwa era bagenda kubuuzibwa.');
INSERT INTO "african_dev" VALUES(43,20,'43:20','Era bagamba nti singa Katonda omusaasizi ennyo yayagala tetwandibasinzizza ekyo tebakirinaako kumanya kwonna, tebali (mu kukola ekyo) okugyako bateebereza buteebereza.');
INSERT INTO "african_dev" VALUES(43,21,'43:21','Abaffe twabawa ekitabo oluberyeberye lwakino (ekikolwa kyabwe) balyoke babe nga kye bekwatako.');
INSERT INTO "african_dev" VALUES(43,22,'43:22','Wabula bagamba nti mazima ffe twasanga baganda baffe nga bali ku nkola, era mazima ffe tuli ku buwufu bwabwe kwe tulungamira.');
INSERT INTO "african_dev" VALUES(43,23,'43:23','Nabwekityo tetwatuma luberyeberyelwo mu kitundu kyonna mutiisa okugyako nga babinojjo baamu bagamba nti mazima ffe twasanga bakadde baffe nga bali ku nkola, era mazima ffe tuli ku buwufu bwabwe tugoberera.');
INSERT INTO "african_dev" VALUES(43,24,'43:24','Omubaka waabwe kwe kugamba nti (kiba bwekityo) newakubadde nga mbaleetedde ekirungamu okusinga ekyo kye mwasangako bakadde ba mmwe ne bagamba nti mazima ffe, tuwakanya ekyo kye mutumiddwa nakyo.');
INSERT INTO "african_dev" VALUES(43,25,'43:25','Olwo nno netubabonereza, kale tunuulira enkomerero ya balimbisa yali etya.');
INSERT INTO "african_dev" VALUES(43,26,'43:26','Jjukira Ibrahim bwe yagamba kitawe n’abantu be, nti mazima nze neganye ebyo bye musinza.');
INSERT INTO "african_dev" VALUES(43,27,'43:27','Okugyako (okusinza) oyo eyantonda anti mazima yye yajja okunnungamya.');
INSERT INTO "african_dev" VALUES(43,28,'43:28','(Ibrahim) yakiteekawo nga kigambo ekyokusigalawo oluvanyuma lwe, balyoke babe nga badda (eri okwawula Katonda).');
INSERT INTO "african_dev" VALUES(43,29,'43:29','Wabula nawa abo ne bakadde baabwe okweyagalira mu bulamu bwensi, okutuusiza ddala nga amazima gabajjidde (Kur’ani) n’omubaka annyonnyola (amazima ago).');
INSERT INTO "african_dev" VALUES(43,30,'43:30','Naye nno amazima bwe gaamala okubajjira nebagamba nti lino ddogo, era mazima ffe tuliwakanya.');
INSERT INTO "african_dev" VALUES(43,31,'43:31','Era nebagamba nti singa Kur’ani eno essiddwa ku musajja owekitiibwa ngava mu kimu ku bitundu ebibiri.');
INSERT INTO "african_dev" VALUES(43,32,'43:32','Abaffe bo be bagabanya okusaasira kwa Katonda, (obwa Nabbi nga bwe baagala) ffe tubagabanyiza ebibabeesaawo mu bulamu bwensi, ne tusukkulumya abamu ku balala mu madaala, abamu balyoke bafuule bannaabwe abaweereza, wabula okusaasira kwa Katonda kwe kusinga obulungi kwebyo byonna bye bakungaanya.');
INSERT INTO "african_dev" VALUES(43,33,'43:33','Singa sikuba nti abantu bonna bajja kufuuka ekibiina kimu (eky''obukafiiri), buli eyandibadde awakanya Katonda omusaasizi ennyo twalitadde ku mayumba gaabwe obusolya nga bwa ffeeza ne ku madaala gebalinnyirako (bonoonekere ddala).');
INSERT INTO "african_dev" VALUES(43,34,'43:34','Nga ne ku nyumba zaabwe twanditaddeko emiryango ne ku bitanda bye bawummulirako (nakwo twanditaddeko ffeeza).');
INSERT INTO "african_dev" VALUES(43,35,'43:35','Era (twandibawadde) ebyokwewunda nga bya zzaabu, ebyo byonna tebiri okugyako okuba nga bya kweyagala bya bulamu bwansi, so nga enkomerero ewa Mukama omulabiriziwo, ya batya Katonda.');
INSERT INTO "african_dev" VALUES(43,36,'43:36','(Nooyo yenna eyesulubabba ebigambo bya Katonda (Kur’ani) tumuteerawo Sitane olwo nno neba nga ye mukwanogwe.');
INSERT INTO "african_dev" VALUES(43,37,'43:37','Era mazima bo bannaabwe (ba Sitane) ddala babaziyiza okutuuka ku kkubo (eggolokofu) ate mazima bo ne basuubira nti balungamu.');
INSERT INTO "african_dev" VALUES(43,38,'43:38','Okutuusa (omuntu oyo eyesulubabba) lwalijja gyetuli aligamba nti nga ndabye, singa wakati wange naawe waliwo ebbanga eryenkana wakati we buvanjuba ne bugwanjuba, agenda kuba munywanyi mubi!.');
INSERT INTO "african_dev" VALUES(43,39,'43:39','Mazima mmwe ekyokuba nti mwegattidde mu bibonerezo tekijja kubagasa olwaleero okuva lwe mweyisa obubi mwekka.');
INSERT INTO "african_dev" VALUES(43,40,'43:40','Abaffe ggwe (Muhammad) osobola okuwuliza kiggala oba okulaga muzibe ekkubo nooyo ali mu bubuze obweyolefu.');
INSERT INTO "african_dev" VALUES(43,41,'43:41','Singa tukugyawo (noofa nga tetunnaba kubonereza bajeemu abo) mazima ffe tuba ba ku babonereza.');
INSERT INTO "african_dev" VALUES(43,42,'43:42','Oba tulikulaga ekyo kye twa balaganyisa anti mazima ffe tusobola.');
INSERT INTO "african_dev" VALUES(43,43,'43:43','Kale wekwate kwekyo ekikuweebwa (Kur’ani) anti mazima ggwe oli ku kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(43,44,'43:44','Era mazima yo (Kur’ani) kitiibwa gyoli neeri abantu bo era lumu mugenda kubuuzibwa (ku ngeri gye mwagigobereramu).');
INSERT INTO "african_dev" VALUES(43,45,'43:45','Buuza abo betwatuma oluberyeberye lwo mu babaka baffe nti, abaffe twaleka Katonda omusaasizi ennyo netuteekawo bu katonda obulala obusinzibwa!.');
INSERT INTO "african_dev" VALUES(43,46,'43:46','Mazima twatuma Musa n’obubonero bwaffe nti genda eri Falawo na bakungu be, olwo nno nabagamba nti mazima nze ndi mubaka w''omulabirizi w’ebitonde.');
INSERT INTO "african_dev" VALUES(43,47,'43:47','Wabula bwe yabajjira n’obubonero bwaffe wagenda okulaba nga babusekerera.');
INSERT INTO "african_dev" VALUES(43,48,'43:48','Tetwabalaga kabonero konna okugyako nga kko kaba kanene okusinga kannaako (wabula era tebakkiriza) netubatusaako ebibonerezo oba olyawo ne bekyusa (okuva ku kye babaddeko).');
INSERT INTO "african_dev" VALUES(43,49,'43:49','(Bwe balaba ebibonerezo) olwo nno ne bagamba nti owange ggwe omulogo tusabire Mukama omulabirizi wo ng’okozesa ebyo bye yakuwa (ebisabisibwa) mazima ffe ddala tujja kulungama.');
INSERT INTO "african_dev" VALUES(43,50,'43:50','Bwe twabagyako ebibonerezo amangu ago bo ne bamenya endagaano.');
INSERT INTO "african_dev" VALUES(43,51,'43:51','Era Firawo nalangirira mu bantu be naagamba nti abange bantu bange obufuzi bwa Misiri si bwange, nga nagino emigga gikulukutira wansi wange abaffe temulaba!.');
INSERT INTO "african_dev" VALUES(43,52,'43:52','Wabula nze nsinga ono (Musa) oyo yye anyomwa, era nga kumpi tasobola na kunnyonnyola (nsonga) yonna.');
INSERT INTO "african_dev" VALUES(43,53,'43:53','Singa yanaanikibwa ebikomo ebya zaabu oba ba Malayika nebajja naye nga ba muwerekera (olwo nno) twanditegedde nti mubaka wa Katonda.');
INSERT INTO "african_dev" VALUES(43,54,'43:54','(Firawo) bwatyo bwe yazannyira ku bwongo bwa bantu be, olwo nno ne bamugondera era mazima bo nebaba abantu abonoonyi.');
INSERT INTO "african_dev" VALUES(43,55,'43:55','Bwe bamala okutunyiiza twababonereza ne tubazikiriza n’amazzi bonna.');
INSERT INTO "african_dev" VALUES(43,56,'43:56','Ne tubafuula abedda abogerwako era ne tubafuula eky''okulabirako eri ab''oluvanyuma.');
INSERT INTO "african_dev" VALUES(43,57,'43:57','Mutabani wa Mariam (Issa) bwe yakubirwako ekifaananyi, ogenda okulaba nga abantu bo kibawogganya (olwessanyu).');
INSERT INTO "african_dev" VALUES(43,58,'43:58','Nebagamba nti abaffe ba katonda baffe bebasinga oba yye, tebakukubira kifaananyi ekyo okugyako lwa kuwalaaza mpaka, nedda (si ekyo kyokka kye bawakanya) bulijjo bo bantu bawakanyi.');
INSERT INTO "african_dev" VALUES(43,59,'43:59','(Isa) tali yye okugyako okuba nti muddu waffe nga abalala, twamugabira ekyengera (kyo bwa Nabbi) era netumufuula ekyokulabirako eri abaaana ba Israil.');
INSERT INTO "african_dev" VALUES(43,60,'43:60','Singa twayagala abamu mu mmwe twandibafudde ba Malayika nga basikiragana mu nsi.');
INSERT INTO "african_dev" VALUES(43,61,'43:61','Era mazima yye (Isa) kabonero akaliranga okusembera kw''olunaku lw''enkomerero, n''olwekyo temukibuusabusaamu naakamu era mungoberere, lino lye kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(43,62,'43:62','Era Sitane tabasuuliranga emisanvu (mu kkubo) anti mazima yye ku mmwe mulabe owolwatu.');
INSERT INTO "african_dev" VALUES(43,63,'43:63','(Nabbi) Isa bweyajja nobunnyonnyofu yagamba nti mazima mbajjidde nebigambo ebyamagezi era mbannyonnyole ebimu kwebyo bye muwakanamu kale mutye Katonda era mungondere.');
INSERT INTO "african_dev" VALUES(43,64,'43:64','Mazima Katonda ye Mukama omulabirizi wange era Mukama omulabirizi wa mmwe, kale mumusinze lino lye kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(43,65,'43:65','Wabula ebibiina byayawukana nga biva mu bo bennyini (ku nsonga ya Isa) kale okubonabona okwe bibonyobonyo by''olunaku oluzito kugenda kubeera kwabo abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(43,66,'43:66','Abaffe (balina kye) balindirira okugyako ekiseera eky''enkomerero okuba nga kibajjira ekibwatukira nga nabo tebamanyi.');
INSERT INTO "african_dev" VALUES(43,67,'43:67','Abomukwano ku lunaku olwo, abamu baliba balabe ba bannabwe okugyako abatya Katonda.');
INSERT INTO "african_dev" VALUES(43,68,'43:68','(Era Katonda alilangirira nti) abange baddu bange temuggya kubaako kutya olwaleero era mmwe temuli ba kunakuwala.');
INSERT INTO "african_dev" VALUES(43,69,'43:69','Abo abakkiriza ebigambo byaffe era nebaba abewaayo gyetuli (abasiramu).');
INSERT INTO "african_dev" VALUES(43,70,'43:70','Muyingire e jjana ne bakkiriza bannammwe nga mwegazaanya.');
INSERT INTO "african_dev" VALUES(43,71,'43:71','Essowani eza zzaabu n’a magiraasi biryetolozebwa mu bo, era (e jjana) eribaamu buli kintu emyoyo gye kyagala, era ekiwoomera amaaso, era nga mmwe muli ba kutuula bugenderevu mu yo.');
INSERT INTO "african_dev" VALUES(43,72,'43:72','Era eyo e jjana, eyo gye mugisikiziddwa olwebyo bye mwakola (ku nsi).');
INSERT INTO "african_dev" VALUES(43,73,'43:73','Mulina mu yo ebibala bingi nga ku byo kwemulya.');
INSERT INTO "african_dev" VALUES(43,74,'43:74','Mazima abonoonyi ba kutuula bugenderevu mu bibonerezo byo muliro Jahannama.');
INSERT INTO "african_dev" VALUES(43,75,'43:75','Tegugenda kukendezebwa ku bo, era nga bo tebalina na ssuubi lyonna lya kuguvaamu.');
INSERT INTO "african_dev" VALUES(43,76,'43:76','Tetwabayisa bubi naye bo be baali beeyisa obubi.');
INSERT INTO "african_dev" VALUES(43,77,'43:77','(Abaliba mu muliro) balikowoola nti owange Maliki (Malayika akuuma omuliro tusaba nti) Mukama omulabirizi wo atumalirewo ddala (tufe), alibagamba nti mazima mmwe muli ba kugubeeramu (awatali kufa oba okuguvaamu).');
INSERT INTO "african_dev" VALUES(43,78,'43:78','Mazima twabaleetera amazima naye abasinga obungi mu mmwe batamwanga amazima.');
INSERT INTO "african_dev" VALUES(43,79,'43:79','Bwebaba nga baasalawo ekigambo (ekyokusalira (Muhammad enkwe) naffe twasalawo (okuba nti tezigenda kuyitamu).');
INSERT INTO "african_dev" VALUES(43,80,'43:80','Oba balowooza nti mazima ffe tetuwulira ebyo bye balowooza mu mitima gyabwe oba bye bakubaganyaako ebirowoozo, nedda (tetukoma ku kubimanya bumanya, wabula webabikolera) nabatumwa baffe baba mu maaso gaabwe nga bawandiika.');
INSERT INTO "african_dev" VALUES(43,81,'43:81','Gamba nti singa Katonda omusaasizi ennyo aba alina omwana olwo nno nze mbeera musaale mu bamusinza.');
INSERT INTO "african_dev" VALUES(43,82,'43:82','Mukama omulabirizi weggulu omusanvu ne nsi (era) omulabirizi wa Arish musukkulumu nnyo kwebyo bye bamujwetekako.');
INSERT INTO "african_dev" VALUES(43,83,'43:83','Kale baleke balimbe era bazannye okutuusa lwe balisisinkanyizibwa olunaku lwabwe lwe balaganyisibwa.');
INSERT INTO "african_dev" VALUES(43,84,'43:84','Anti yye (Katonda) ye Katonda mu ggulu, era ne mu nsi ye Katonda. Era yye ye mugoba nsonga omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(43,85,'43:85','Era yatukula oyo, byonna bibye ebiri mu ggulu omusanvu ne nsi, n''ebyo ebiri wakati wa byombi, era ye yekka yalina okumanya ebikwata ku lunaku lw''enkomerero (ddi lwelituuka) era gyali gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(43,86,'43:86','Abo bebasinza nebava ku ye tebalisobola kuwolereza, be ppo oyo aliwa obujulizi mu mazima (yaliyogera) ate era bo nebaba nga baliba bamanyi amazima.');
INSERT INTO "african_dev" VALUES(43,87,'43:87','Singa obabuuza nti ani yabatonda? ddala bajja kugamba nti Katonda, kale ate bawugulwa batya!.');
INSERT INTO "african_dev" VALUES(43,88,'43:88','(Katonda amanyi) ekigambo kye (Muhammad kyayogera olutata nga awanjagira Katonda nti) ayi Mukama omulabirizi wange mazima abo (benkowoola) bantu abatakkiriza.');
INSERT INTO "african_dev" VALUES(43,89,'43:89','Kale nno baleke era ogambe nti mirembe kya ddaaki balianya.');
INSERT INTO "african_dev" VALUES(44,1,'44:1','Ha Miim.');
INSERT INTO "african_dev" VALUES(44,2,'44:2','Ndayidde ekitabo (Kur’ani) ekinnyonnyofu.');
INSERT INTO "african_dev" VALUES(44,3,'44:3','Mazima ffe twakissa mu kiro ekyomukisa (anti) mazima ffe twali tulina okutiisa (abantu).');
INSERT INTO "african_dev" VALUES(44,4,'44:4','Mu kyo (ekiro) mwekigerekerwa buli kigambo ekiba kisaliddwawo.');
INSERT INTO "african_dev" VALUES(44,5,'44:5','Nga kwo kusalawo okuva gye tuli (anti) mazima ffe twali tuteekwa okutuma ababaka.');
INSERT INTO "african_dev" VALUES(44,6,'44:6','(Ekyokutuma ababaka) kusaasira okuva ewa Mukama omulabirizi wo (anti) mazima yye yawulira ennyo amanyi ennyo.');
INSERT INTO "african_dev" VALUES(44,7,'44:7','(Yye Katonda) ye Mukama omulabirizi we ggulu omusanvu ne nsi nebyo ebiri wakati wa byombi, bwe muba muli bakkiriza abannamaddala.');
INSERT INTO "african_dev" VALUES(44,8,'44:8','Tewali kirina kusinzibwa okugyako yye, awa obulamu era yabujjawo. Mukama omulabirizi wa mmwe era omulabirizi wa bakadde ba mmwe abasooka.');
INSERT INTO "african_dev" VALUES(44,9,'44:9','Wabula bo (abatakkiriza) bali mu kubuusabuusa, bazannya.');
INSERT INTO "african_dev" VALUES(44,10,'44:10','Kale lindirira olunaku eggulu lweririreeta omukka omweyolefu (ogulabwa buli omu).');
INSERT INTO "african_dev" VALUES(44,11,'44:11','Gugenda kubikka abantu ekyo kiriba kibonerezo ekiruma ennyo.');
INSERT INTO "african_dev" VALUES(44,12,'44:12','(Baligamba nti) ayi Mukama omulabirizi waffe tugyeko ekibonerezo, mazima ffe tukkirizza.');
INSERT INTO "african_dev" VALUES(44,13,'44:13','Naye wwa gye baliggya okujjukira (batuuke okwebuulirira) ate nga mazima omubaka annyonnyola obulungi yabajjira.');
INSERT INTO "african_dev" VALUES(44,14,'44:14','Oluvannnyuma ate ne bamuvaako, era nebagamba nti (Muhmmad) ayigirizibwa buyigirizibwa era mulalu.');
INSERT INTO "african_dev" VALUES(44,15,'44:15','Mazima ffe tujja kuggyawo ekibonerezo katono (wabula) mazima mmwe mujja kuddayo (mu butakkiriza bwa mmwe).');
INSERT INTO "african_dev" VALUES(44,16,'44:16','(Wabula bajjukize) olunaku lwe tulikwata olukwata olwamaanyi (anti) mazima ffe tuli ba kubonereza.');
INSERT INTO "african_dev" VALUES(44,17,'44:17','Era mazima twageseza oluberyeberye lwabwe abantu ba Firawo, era nga yabajjira omubaka owekitiibwa.');
INSERT INTO "african_dev" VALUES(44,18,'44:18','(Naagamba nti) munzirize abaddu ba Katonda (anti) mazima nze ndi mubaka omwesigwa.');
INSERT INTO "african_dev" VALUES(44,19,'44:19','Era (ekyokubiri) mu be nga temwekuluntaza ku Katonda, mazima nze mbaleetedde obujulizi obwolwatu.');
INSERT INTO "african_dev" VALUES(44,20,'44:20','Era mazima nze nsaba obukuumi ewa Mukama omulabirizi wange era Mukama omulabirizi wa mmwe muleme kunkuba mayinja.');
INSERT INTO "african_dev" VALUES(44,21,'44:21','Bwe munaaba temukkiriza byengamba kale mundeke (temuntusaako kabi)');
INSERT INTO "african_dev" VALUES(44,22,'44:22','(Wabula bamukijjanya) olwo nno nakowoola Mukama omulabirizi we nti mazima abo bantu bonoonyi.');
INSERT INTO "african_dev" VALUES(44,23,'44:23','(Katonda naamulagira nti) tambula ekiro na baddu bange anti mazima mmwe mujja kuwonderwa.');
INSERT INTO "african_dev" VALUES(44,24,'44:24','(Bwomala okusomoka ggwe ne booli nabo) ennyanja gireke ng’erimu ekkubo (anti) mazima bbo, ggye lyakuzikirizibwa.');
INSERT INTO "african_dev" VALUES(44,25,'44:25','Amalimiro ne nsulo z’amazzi bimeka bye baaleka?.');
INSERT INTO "african_dev" VALUES(44,26,'44:26','N’ebimera, n’ebifo ebye bitiibwa.');
INSERT INTO "african_dev" VALUES(44,27,'44:27','Nebyengera ebirala bingi bye baali beyagaliramu.');
INSERT INTO "african_dev" VALUES(44,28,'44:28','Bwekityo (bwekyali nebaggwawo) netubisikiza abantu abalala.');
INSERT INTO "african_dev" VALUES(44,29,'44:29','Eggulu ne nsi tebyabakaabira era tebaali ba kulindirizibwa (nti baweebwe akaseera beenenye).');
INSERT INTO "african_dev" VALUES(44,30,'44:30','Era ddala twawonya abaana ba Israil ebibonyoobonyo ebinyoomesa.');
INSERT INTO "african_dev" VALUES(44,31,'44:31','(Bwe twabawonya) Firawo anti mazima yye yali mwekuza era nga wa mu bonoonyi ennyo.');
INSERT INTO "african_dev" VALUES(44,32,'44:32','Okusinziira ku kumanya (kwaffe) twabasukkulumya (abaana ba Israil) ku bitonde ebirala.');
INSERT INTO "african_dev" VALUES(44,33,'44:33','Era netubawa ebya magero ebyo ebyalimu okugesezebwa okwolwatu.');
INSERT INTO "african_dev" VALUES(44,34,'44:34','Mazima abo (abakafiiri bo mu Makkah) ddala bagamba nti.');
INSERT INTO "african_dev" VALUES(44,35,'44:35','Tewali kiriwo okugyako okufa kwaffe okusooka, era tetuli ba kuzuukizibwa.');
INSERT INTO "african_dev" VALUES(44,36,'44:36','(Okuzuukira bwekuba nga gye kuli) kale muleete bakadde baffe bwe muba nga mwogera mazima.');
INSERT INTO "african_dev" VALUES(44,37,'44:37','Abaffe bo be basinga obulungi oba abantu ba Tubbai (bantu be Saba-a abafuzi be Yamani) n''abo abaaliwo oluberyeberye lwabwe (bonna) twabazikiriza (anti) mazima bo baali bonoonyi.');
INSERT INTO "african_dev" VALUES(44,38,'44:38','Tetwatonda eggulu omusanvu ne nsi nebyo ebiri wakati wa byombi nga tuzannya.');
INSERT INTO "african_dev" VALUES(44,39,'44:39','Era tetwabitonda byombi wabula ku lwa nsonga, naye ddala abasinga obungi ku bo tebamanyi.');
INSERT INTO "african_dev" VALUES(44,40,'44:40','Mazima olunaku lwokusalawo bonna kye kiseera kyabwe ekya balaganyisibwa.');
INSERT INTO "african_dev" VALUES(44,41,'44:41','Olunaku owomukwano lwa taligasa wa mukwano kintu kyonna, era bo tebagenda kudduukirirwa.');
INSERT INTO "african_dev" VALUES(44,42,'44:42','Okugyako oyo Katonda gwalisaasira anti mazima yye (Katonda) ye nantakubwa ku mukono omusaasizi ennyo.');
INSERT INTO "african_dev" VALUES(44,43,'44:43','Mazima omuti (oguyitibwa) Zakkuumi.');
INSERT INTO "african_dev" VALUES(44,44,'44:44','Ye mmere ya bonoonyi.');
INSERT INTO "african_dev" VALUES(44,45,'44:45','(Obubi bwagwo ng’omuntu agulidde) guli nga ekyuma ekisaanuuse, gulyeserera mu mbuto (z’abaligulya).');
INSERT INTO "african_dev" VALUES(44,46,'44:46','Nga okwesera kwa mazzi agakyamuse.');
INSERT INTO "african_dev" VALUES(44,47,'44:47','(Olwo nno Katonda aliragira ba Malayika nti) mumukwate (omwonoonyi) olwo nno mu muwalule mu mukasuke mu makkati g’omuliro Jahiimu.');
INSERT INTO "african_dev" VALUES(44,48,'44:48','Oluvanyuma mufukirire waggulu ku mutwegwe ekibonerezo eky’amazzi ageeseze (agogeddwako).');
INSERT INTO "african_dev" VALUES(44,49,'44:49','(Bali mugamba) loza (ku kibonerezo anti) mazima ddala gwe gwe wamanyi owekitiibwa (nga bamukudaalira).');
INSERT INTO "african_dev" VALUES(44,50,'44:50','Mazima kino kyekyo kye mwali mutankana.');
INSERT INTO "african_dev" VALUES(44,51,'44:51','Mazima abatya (Katonda) baliba mu kifo ekyeddembe.');
INSERT INTO "african_dev" VALUES(44,52,'44:52','Baliba mu malimiro n’amazzi ag''ensulo.');
INSERT INTO "african_dev" VALUES(44,53,'44:53','Baliyambala engoye ezaliiri omuwewufu n’omuzito, nga batudde batunuuliganye.');
INSERT INTO "african_dev" VALUES(44,54,'44:54','Bwekityo (bwe kiriba) era tuli bafumbiza ababeezi ab’amaaso amanene.');
INSERT INTO "african_dev" VALUES(44,55,'44:55','Balisaba nga bali mu yo buli kibala nga bali mirembe.');
INSERT INTO "african_dev" VALUES(44,56,'44:56','Nga bali mu yo tebalikomba ku kufa nga ogyeko okufa okwasooka, era ali bawonya ebibonerezo ebyomuliro Jahiimu.');
INSERT INTO "african_dev" VALUES(44,57,'44:57','(Byonna) nga bigabwa ebiva ewa Mukama omulabirizi wo, okwo kwe kwesiima okusuffu.');
INSERT INTO "african_dev" VALUES(44,58,'44:58','Era ddala twagyanguya (Kur’ani) ku lulimi lwo babe nga bebuulira (nayo).');
INSERT INTO "african_dev" VALUES(44,59,'44:59','Kale lindirira mazima ddala bo balindiridde.');
INSERT INTO "african_dev" VALUES(45,1,'45:1','Ha Mim.');
INSERT INTO "african_dev" VALUES(45,2,'45:2','Okussa kwe kitabo (Kur’ani) kyava wa Katonda nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(45,3,'45:3','Mazima mu (ggulu omusanvu n''ensi mulimu obubonero obwenkukunala (obulaga obuyinza bwa Katonda) eri abakkiriza.');
INSERT INTO "african_dev" VALUES(45,4,'45:4','Era ne mu kutondebwa kwa mmwe, neebyo byasasaanya mu bitambulira (ku nsi) bubonero eri abantu abakakasa (okubeerawo kwa Katonda n’obuyinza bwe).');
INSERT INTO "african_dev" VALUES(45,5,'45:5','N’okwawukana kw''ekiro n’emisana nebyo Katonda byassa okuva waggulu (enkuba) mu bigabwabye naalamusa nabyo ensi olubvannyuma lw''okufa kwayo, n''okutambuza empewo bubonero eri abantu abategeera.');
INSERT INTO "african_dev" VALUES(45,6,'45:6','Ebigambo bya Katonda ebyo (ebiraga obubonero bwe) tubikusomera mu butuufu, olwo bigambo ki oluvanyuma lwo kuwakanya Katonda n’ebigambo bye bye balikkiriza!');
INSERT INTO "african_dev" VALUES(45,7,'45:7','Okubonabona kuli ku buli omuyitirivu w’okulimba omwonoonyi.');
INSERT INTO "african_dev" VALUES(45,8,'45:8','Awulira ebigambo bya Katonda nga bimusomerwa oluvanyuma naakalambira nga yeekuluntaza ng''alinga atabiwulidde, kale musanyuse nga (omuwa amawulire) g’ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(45,9,'45:9','Bwamanya ekintu kyonna mu bigambo byaffe akifuula kya kujeeja, abo balina ebibonerezo ebinyoomesa.');
INSERT INTO "african_dev" VALUES(45,10,'45:10','Mumaaso gaabwe omuliro Jahannama gubalinze era bye bakola tebigenda kubagasa kintu kyonna wadde ab’emikwano bebeteerawo nebava ku Katonda era balina ebibonerezo ebisuffu.');
INSERT INTO "african_dev" VALUES(45,11,'45:11','(Kur’ani) eno, kyakulungamya. era abo abawakanya ebigambo bya Mukama omulabirizi waabwe balina ebibonerezo ebimu kwebyo ebikambwe ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(45,12,'45:12','Katonda yooyo eyabagondeza ennyanja, amaato gabe nga gatambulira ku yo olwekiragiro kye, era mube nga musobola okunoonya mu bigabwa bye, era musobole okwebaza.');
INSERT INTO "african_dev" VALUES(45,13,'45:13','Era yabagondeza ebyo ebiri mu ggulu omusanvu nebyo ebiri mu nsi, nga byonna biva gyali. Mazima mwekyo mulimu obubonero (obulaga okubaayo kwa Katonda n’obuyinza bwe) eri abantu abafumitiriza.');
INSERT INTO "african_dev" VALUES(45,14,'45:14','Gamba abo abakkiriza basonyiwe abo abatasuubira (kusisinkana) naku za Katonda, alyoke asasule abantu olwebyo bye bakola.');
INSERT INTO "african_dev" VALUES(45,15,'45:15','Oyo akola emirimu emirungi akolera mwoyo gwe, ate oyo ayonoona (akabi k''okwonoona kwe) kadda eri mwoyo gwe, oluvanyuma eri Mukama omulabiririz wa mmwe gye mulizzibwa.');
INSERT INTO "african_dev" VALUES(45,16,'45:16','Mazima twawa abaana ba Israil ekitabo n’okulamula wakati wa bantu n’obwa Nabbi netubagabirira ebirungi era netubasukkulumya ku bantu abalala (abaaliwo ku mulembe gwabwe).');
INSERT INTO "african_dev" VALUES(45,17,'45:17','Era twabawa obunnyonnyofu bwe kigambo (ekikwata ku kutumwa kwa Nabbi Muhammad) olwo nno tebayawukana okugyako luvannyuma lwa kumanya kubajjira (nga ekyo baakikola) olwo bukyayi obwali wakati waabwe, mazima Mukama omulabirizi wo agenda kulamula wakati waabwe ku lunaku lw’enkomerero mwebyo bye bayawukanangamu.');
INSERT INTO "african_dev" VALUES(45,18,'45:18','Oluvanyuma twakussa (ggwe Muhammad) ku nkola ennambulukufu ku kigambo kye (ddiini) kale gigoberere, era togoberera okwagala kwabo abatamanyi.');
INSERT INTO "african_dev" VALUES(45,19,'45:19','Anti mazima bo tebayinza kukuyamba kintu kyonna kukutaasa Katonda, era mazima abeeyisa obubi abamu mu bo banywanyi ba bannaabwe, naye Katonda ye munywanyi w''abamutya.');
INSERT INTO "african_dev" VALUES(45,20,'45:20','Eno (Kur’ani) mumuli eri abantu, era bulungamu na kusaasira eri abantu abatya Katonda abannamaddala.');
INSERT INTO "african_dev" VALUES(45,21,'45:21','Abaffe oba abo abeewerebye ebibi basuubira nti tuyinza okubafuula nga abo abakkiriza ne bakola emirimu emirongoofu nga obulamu bwabwe n’okufa kwabwe bifaanana (nedda) okulamula kwabwe kubi.');
INSERT INTO "african_dev" VALUES(45,22,'45:22','Era Katonda yakola eggulu ne nsi mu butuufu era buli mwoyo gulyoke gube nga gusasulwa olwebyo, bye gwakola era bo si bakulyazamanyizibwa.');
INSERT INTO "african_dev" VALUES(45,23,'45:23','Abaffe olabye oyo okwagalakwe nga kwafuula Katonda we, Katonda naamubuza awamu n’okuba nti (omuntu oyo) amanyi ekituufu era naazibikira amatuuge n''omutimagwe, era nassa ku maasoge ekibikka, olwo nno ate ani ayinza okumulungamya oluvanyuma lwa Katonda (okumutusa awo), abaffe temwebulirira!.');
INSERT INTO "african_dev" VALUES(45,24,'45:24','Era (abakafiiri) bagamba nti (obulamu) tebuli okugyako obulamu bwaffe obw''okunsi, tufa era netuba balamu, tewali kituggya mu bulamu okugyako myaka (gya muntu) kuggwako, naye nga tebalina kumanya kwonna ku kikwatako, tebali bo okugyako bateebereza buteebereza.');
INSERT INTO "african_dev" VALUES(45,25,'45:25','Ebigambo byaffe bwe bibasomerwa mu bunnyonnyofu (bwabyo), ekyekwaso kyabwe tekiba okugyako bagamba (bweba nga eriyo okuzuukira kale) mukomyewo bakadde baffe (abaafa) mwemuba mwogera mazima.');
INSERT INTO "african_dev" VALUES(45,26,'45:26','Bagambe (ggwe Nabbi Muhammad) nti Katonda y''abawa obulamu oluvanyuma nabatta ate oluvanyuma agenda kubakungaanya nga batwala eri olunaku lw’enkomerero teruliimu kubuusabuusa naye abantu abasinga obungi tebamanyi.');
INSERT INTO "african_dev" VALUES(45,27,'45:27','Bwa Katonda yekka obufuzi bwe ggulu omusanvu ne nsi, era era bubwe olunaku lw’enkomerero lwelituuka, olunaku olwo abonoonyi balifafaganirwa.');
INSERT INTO "african_dev" VALUES(45,28,'45:28','(Kwolwo) oliraba buli kibiina nga kifukamidde (olwokutya). Buli kibiina kiriyitibwa okugenda awali ekitabo kyakyo (ekyo ekirimu emirimu gyabwe era baligambibwa nti) olwaleero muli ba kusasulwa ebyo bye mwali mukola.');
INSERT INTO "african_dev" VALUES(45,29,'45:29','(Era baligambibwa nti) ekitabo kyaffe kino kiboogerako mu mazima (anti) mazima ffe twawandikanga buli kye mwakolanga.');
INSERT INTO "african_dev" VALUES(45,30,'45:30','Olwo nno abaakola emirimu emirongoofu Mukama omulabirizi waabwe ali bayingiza mu kusaasira kwe (era) ekyo kwe kwesiima okweyolefu.');
INSERT INTO "african_dev" VALUES(45,31,'45:31','Ate abo abakafuwala (Katonda alibagamba nti) abaffe ebigambo byange tebyabasomerwa ne mwekuluntaza ne mubeera abantu abonoonyi.');
INSERT INTO "african_dev" VALUES(45,32,'45:32','Era bwe mwagambwanga nti ddala endagaano ya Katonda ya mazima era nti ekiseera ekyenkomerero tekiriimu kubuusabuusa, mwagamba nti ekiseera ekyenkomerero tetumanyi kye ki. (Ebikikwatako) tuwammanta biwammante, era ffe tetukirinaako bukakafu.');
INSERT INTO "african_dev" VALUES(45,33,'45:33','Era (awo) obubi bwebyo bye bakola buli beeyoleka era biribebungulula ebibonerezo byebyo bye baali bajeeja.');
INSERT INTO "african_dev" VALUES(45,34,'45:34','Era baligambibwa nti olwaleero tubeerabidde nga bwe mwerabira okusisinkana olunaku lwa mmwe luno, era omuliro bwe butuulo bwa mmwe era temugenda kufuna badduukirize.');
INSERT INTO "african_dev" VALUES(45,35,'45:35','Ekyo nno mmwe (ekibatuusaako) lwakuba nti mazima mmwe ebigambo bya Katonda mwabifuula bya kujeeja era obulamu bwe nsi ne bubagayaaza, n’olwekyo olwaleero si bakugujjibwamu (omuliro) era tebagenda kugambibwa kwetonda.');
INSERT INTO "african_dev" VALUES(45,36,'45:36','Ebitendo byonna bya Katonda Mukama omulabirizi we ggulu omusanvu, era Mukama omulabirizi we nsi, Mukama omulabirizi w’ebitonde byonna.');
INSERT INTO "african_dev" VALUES(45,37,'45:37','Ebitiibwa byonna mu ggulu omusanvu ne nsi bibye yekka, era yye, ye nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(46,1,'46:1','Ha Miim.');
INSERT INTO "african_dev" VALUES(46,2,'46:2','Okussa kwe kitabo (Kur’ani) kwava wa Katonda nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(46,3,'46:3','Tetwatonda eggulu omusanvu ne nsi nebyo ebiri wakati wa byombi wabula mu butuufu (nga bwe kisaana okuba nga ddala Katonda yaabikoze), era nga byakubeerawo okumala ekiseera ekigere, wabula bo abakaafuwala besamba ebyo bye batiisibwa (bye babulirirwa) nabyo.');
INSERT INTO "african_dev" VALUES(46,4,'46:4','Bagambe nti mulaba ebyo bye musaba nemuleka Katonda, kale mundage biki bye baatonda ku nsi oba balina omugabo (gw''okutonda) ebyo ebiri mu ggulu omusanvu (bwe kiba bwe kityo) kale mundeetere ekitabo ekyaliwo oluberyeberye lwa kino (Kur’ani), oba ebisigala bya yirimu yonna bwe muba nga mwogera mazima!.');
INSERT INTO "african_dev" VALUES(46,5,'46:5','Ani mubuze okusinga oyo alekawo Katonda naasaba oyo atasobola kumwanukula okutuusa ku lunaku lw’enkomerero! nga nabo (be basaba) tebalina kye bamanyi ku kusaba kwabwe.');
INSERT INTO "african_dev" VALUES(46,6,'46:6','Ng’ate abantu bwe balikunganyizibwa (be baasaba nga) bagenda kuba balabe baabwe era bagenda kwewakana okuba nti ba basinzanga.');
INSERT INTO "african_dev" VALUES(46,7,'46:7','Ebigambo byaffe bwe bibasomerwa nga binnyonnyofu, abo abawakanya amazima bwe gabajjira bagamba nti lino ddogo lyeyolefu.');
INSERT INTO "african_dev" VALUES(46,8,'46:8','Oba bagamba nti Nabbi Muhammad ye yajigunjaawo (Kur’ani)! bagambe nti bwemba najigunjaawo temulina kintu kye muyinza kumponya ku Katonda, yye yaasinga okumanya bye muwolompokamu. Ye amala okuba nga mujulizi wakati wange nammwe era yye ye musonyiyi omusaasizi ennyo.');
INSERT INTO "african_dev" VALUES(46,9,'46:9','Bagambe nti si nze nsoose mu babaka era simanyi kinankolebwa wadde kye munakolebwa, sirina kyengoberera okugyako obubaka obumpebwa era siri nze okugyako omutiisa ow''olwatu.');
INSERT INTO "african_dev" VALUES(46,10,'46:10','Bagambe nti abaffe mulaba nti singa (Kur’ani) eva wa Katonda ate mmwe nemugiwakanya, naye nga omujulizi mu baana ba Israil yajulira ku kiringa yo (nti kiva wa Katonda) olwo nno yye) nakkiriza ate mmwe ne mwekuluntaza (olwo mu mbeera eyo kiba kitya), mazima bulijjo Katonda talungamya bantu beeyisa bubi.');
INSERT INTO "african_dev" VALUES(46,11,'46:11','Abo abaakafuwala nebagamba abo abakkiriza nti (Muhammad byagamba) singa bibadde birungi (abo) tebanditusoose kugenda gyebiri, naye olwokuba tebalungama nayo, bajja kugamba nti Kur’ani kugunja okwedda.');
INSERT INTO "african_dev" VALUES(46,12,'46:12','Nga noluberyeberye lwayo (waliyo) ekitabo kya Musa nga kyagobererwa nga kya kusaasira n''ekitabo kino (Kur’ani kyajja nga) kikakasa (ebyakulembera) nga kiri mu lulimi luwarabu, kibe nga kirabula abo abeeyisa obubi era nga kya kusanyusa eri abalongoosa.');
INSERT INTO "african_dev" VALUES(46,13,'46:13','Mazima abo abagamba nti Mukama omulabirizi waffe ye Katonda, oluvanyuma ne babeera beesimbu (mu ddiini) tebagenda kutuukwako kutya era tebagenda kunakuwala.');
INSERT INTO "african_dev" VALUES(46,14,'46:14','Abo be ba nannyini jjana, ba kugituulamu obugenderevu nga yempeera olwebyo bye baali bakola.');
INSERT INTO "african_dev" VALUES(46,15,'46:15','Omuntu twamulagira okuyisa obulungi bakaddebe, nnyina yamwetikka (yali lubuto lwe) mu buzibu era namuzaala mu buzibu, nga olubuto lwe (n''okumuyonsa) n''okumujja ku mabeere emyezi makumi asatu, okutuusa lwatuuka okukakata kwe naaweza emyaka amakumi ana (omuntu mulamu) agamba nti ayi Mukama omulabirizi wange nsobozesa okwebaza ebyengera byo ebyo bye wangabira ,ne bye wagabira bakadde bange bombi era mbe nga nkola emirimu emirongoofu gyosiima, era nongooseza ezzadde lyange mazima nze nenenyezza nenzira gyoli era mazima nze ndi wa mu basiraamu.');
INSERT INTO "african_dev" VALUES(46,16,'46:16','Abo beebo betukkiriza okuva gye bali ebirungi ebyo bye baakola era netusonyiwa ebisobyo byabwe (era ba mu bantu) ba mu jjana (era eyo) ye ndagaano eyamazima gye baali baalaganyisibwa.');
INSERT INTO "african_dev" VALUES(46,17,'46:17','Nooyo yenna agamba bakadde be nti mutwale eri, muntiisatiisa nti nja kuzuukizibwa, so ng’ate emirembe mingi egiyiseewo oluberyeberye lwange (ani yali azuukidde), nga bakadde bombi basaba Katonda abayambe, era nga bamugamba (omwana) nti obula! (kyolina okukola) kkiriza, mazima endagaano ya Katonda ya mazima. Olwo nno yye (omwana) agamba nti ebyo tebiri okugyako okuba enfumo za baakulembera.');
INSERT INTO "african_dev" VALUES(46,18,'46:18','Abo beebo abaakakatwako ekigambo (ekyokubonerezebwa) mu bibiina ebyayita oluberyeberye lwabwe nga baava mu majinni ne mu bantu mazima bo be bafafaganirwa.');
INSERT INTO "african_dev" VALUES(46,19,'46:19','Era buli omu alina amadaala okusinziira kwebyo bye bakola era ddala (Katonda) agenda kubawa empeera etuukana n''emirimu gyabwe era bo ssi ba kuyisibwa bubi.');
INSERT INTO "african_dev" VALUES(46,20,'46:20','Era (mulowooze) ku lunaku abo abaakafuwala lwe balyanjulwa eri omuliro (baligambwa nti) ebirungi bya mmwe mwabimalira mu bulamu bwa mmwe obwensi, ne mweyagala nabyo, kale nno olwaleero mugenda kusasulwa ebibonerezo ebinyoomesa olwengeri gye mwali mwekuluntazaamu mu nsi awatali nsonga ne ku lwebyo bye mwali mwonoona.');
INSERT INTO "african_dev" VALUES(46,21,'46:21','Era bajjukize (ggwe Nabbi Muhammad) muganda wa A’di (Hudu) bwe yalabula abantu be abaali mu bifo ebijjuddemu entuunu z''omusenyu (gyoli nti nsozi) era nga okulabula kungi okwayita oluberyeberye lwe n''oluvanyuma lwe, (nga ababaka bonna baali bagamba nti) temusinzanga okugyako Katonda (era) mazima nze ntya okuba nga mutuukwako ebibonerezo by''olunaku oluzito.');
INSERT INTO "african_dev" VALUES(46,22,'46:22','Nebagamba nti abaffe otujjidde otuwugule ku ba Katonda baffe! (bwekiba bwe kityo), kale tutuuseeko kyotulagaanyisa bwoba nga oli mu boogera amazima.');
INSERT INTO "african_dev" VALUES(46,23,'46:23','Naabagamba nti mazima okumanya (ddi? lwe kiribatuukako) kuli eri Katonda era (nze) mbatusaako ebyo bye natumwa nabyo, naye mazima nze mbalaba nga muli bantu abatamanyi.');
INSERT INTO "african_dev" VALUES(46,24,'46:24','Naye bwe baakiraba nga (kire) ekyetimbye (ku ggulu), nga kyolekedde ensenyi zaabwe, baagamba nti ekyo ekire ekyetimbye kigenda kutuwa nkuba, wabula nedda kyo kyekyo kye mubadde musaba kibatuukeko mangu (kyo) mpewo (kikungunta) erimu e bibonerezo e biruma ennyo.');
INSERT INTO "african_dev" VALUES(46,25,'46:25','Ejja kuzikiriza buli kintu kyonna okusinziira ku kiragiro kya Mukama omulabirizi waayo, olwo nno nebatuuka okuba nga tewali kirabwa okugyako amayumba gaabwe, bwetutyo bwe tusasula abantu abonoonyi.');
INSERT INTO "african_dev" VALUES(46,26,'46:26','Mazima twabawa obusobozi (abantu ba Huudu) kwebyo bye tutaabawa mmwe (abe Makkah) busobozi ku byo, era nga twabawa amatu n''amaaso n''emitima kyokka amatu gaabwe tegaabagasa kintu kyonna wadde amaaso gaabwe wadde emitima gyabwe okuva lwe baali nga bawakanya ebigambo bya Katonda era nebibamalawo ebyo bye bajeejanga.');
INSERT INTO "african_dev" VALUES(46,27,'46:27','Era mazima twazikiriza ebitundu ebyo ebibeetolodde, era netunyonyola ebigambo oba olyawo nebadda (nebaleka okukaafuwala).');
INSERT INTO "african_dev" VALUES(46,28,'46:28','Kale singa baabataasa abo abaaleka Katonda ne beteerawo ebisinzibwa ebirala ebibasembeza ewa Katonda (ssi bwegwali) wabula baabula era (endowooza yaabwe) eyo kwali kutemerera kwa bwe era nga byali ebyo bye bagunjaawo.');
INSERT INTO "african_dev" VALUES(46,29,'46:29','Jjukira (gwe Nabbi) bwe twakuleetera ekibinja kya majinni negaba nga gawuliriza Kur’ani, bwe gaatuuka weyali esomerwa ne gagamba nti muwulirize, bwe yamala okusomwa ne gakyusa okudda eri gannaago nga gagalabula (akabi akali mu kujeemera Katonda).');
INSERT INTO "african_dev" VALUES(46,30,'46:30','Negagamba nti abange bannaffe, mazima ffe tuwulidde ekitabo ekyassibwa oluvanyuma lwa Musa nga kikakasa ebyo ebya kikulembera (nga ddala) kirungamya eri amazima n''eri ekkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(46,31,'46:31','Abange bannaffe mwanukule omukowooze wa Katonda, era mumukkirize (Katonda), agya kubasonyiwa ebyonoono bya mmwe era abawonye ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(46,32,'46:32','Oyo yenna atayanukula mukowooze wa Katonda siwakulemesa Katonda ku nsi, era talina nga oggyeko Katonda bakuumi (basobola kumuwonya) abo, bali mu bubuze obweyolefu.');
INSERT INTO "african_dev" VALUES(46,33,'46:33','Abaffe mazima Katonda, oyo eyatonda eggulu omusanvu ne nsi, era nga teyakoowa olwokubitonda tebalaba nti asoboleraddala okulamusa abafu! Wewaawo (asobolera ddala, anti) mazima yye muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(46,34,'46:34','Ate olunaku abo abakaafuwala lwe balireetebwa eri omuliro (baligambwa nti) abaffe kino ssi kya mazima!, baligamba nti wewaawo tulayidde Mukama omulabirizi waffe, (Katonda) aligamba nti kale mukombe ku bibonerezo olwebyo bye mwali muwakanya.');
INSERT INTO "african_dev" VALUES(46,35,'46:35','N’olwekyo (gwe omubaka) gumikiriza nga ababaka abavumu bwe bagumukiriza era tobasabira bibonerezo ku batuukako mangu, naye olunaku lwe baliraba bye balaganyisibwa baliba nga abatawangaala okugyako akaseera katono mu budde obwemisana, (ebyo byonna ebibabuuliddwa) bubaka obutuusiddwa gye muli ate olwo balizikirizibwa okugyako abantu abonoonyi?.');
INSERT INTO "african_dev" VALUES(47,1,'47:1','Abo abaakafuwala nebaggya abantu ku kkubo lya Katonda (Katonda) yayonoona emirimu gyabwe.');
INSERT INTO "african_dev" VALUES(47,2,'47:2','Ate abo abakkiriza nebakola emirimu emirongoofu era nebakkiriza ebyo ebyassibwa ku Muhammad, era nga g’emazima agava ewa Mukama omulabirizi waabwe (abo Katonda) yabasangulako ebisobyo byabwe era naalongoosa embeera yaabwe.');
INSERT INTO "african_dev" VALUES(47,3,'47:3','Ekyo kiri bwekityo lwakuba nti mazima bo abaakafuwala baagoberera ebitali bituufu, ate nga abo abakkiriza baagoberera amazima agava eri Mukama omulabirizi waabwe, bulijjo Katonda bwatyo akubira abantu ebifaananyi byabwe (bagerageranye).');
INSERT INTO "african_dev" VALUES(47,4,'47:4','Olwo bwe muba nga musisinkanye abo abaakafuwala (mu lutalo) muteme ensingo, okutuusiza ddala nga mubanafuyizza, munyweze empingu (zemusiba nazo abawambe) olwo nno kiri gyemuli okubata oluvanyuma (lwo kubawamba) oba okubakkiriza okwenunula, (ekyo mukikole bwemutyo) okutuusa olutalo lweruggwa. Ekyo bwekityo bwekiri, naye singa Katonda yayagala yandibewangulidde, wabula (ekimugaana okukola ekyo) lwakwagala kugezesa bamu mu mmwe ng''akozesa abalala. Era abo abattibwa nga bali mu kulwana mu kkubo lya Katonda, (Katonda) tagenda kwonoona mirimu gyabwe.');
INSERT INTO "african_dev" VALUES(47,5,'47:5','Ajja kubalungamya era alongoose nembeera yaabwe.');
INSERT INTO "african_dev" VALUES(47,6,'47:6','Era abayingize e jjana gyeyabategeeza edda.');
INSERT INTO "african_dev" VALUES(47,7,'47:7','Abange mmwe abakkiriza singa mutaasa Katonda, ajja kubataasa, era anyweze ebigere byammwe (abagumye).');
INSERT INTO "african_dev" VALUES(47,8,'47:8','N''abo abaakafuwala okuzikirira kube ku bo, era Katonda yayonoona emirimu gyabwe.');
INSERT INTO "african_dev" VALUES(47,9,'47:9','Ekyo lwakuba nti mazima bo bakyawa ebyo Katonda byeyassa, olwo nno nayonoona emirimu gyabwe.');
INSERT INTO "african_dev" VALUES(47,10,'47:10','Abaffe tebatambula mu nsi, olwo nno ne balaba nga bweyali enkomerero yaabo abaaliwo oluberyeberye lwabwe, (anti) Katonda yabazikiriza. Era abo abaakafuwala ba kutuukwako ekiringa ekyo.');
INSERT INTO "african_dev" VALUES(47,11,'47:11','Ekyo nno lwakuba nti mazima Katonda ye mukuumi wa bakkiriza era nga ddala abakafiiri tebalina mukuumi.');
INSERT INTO "african_dev" VALUES(47,12,'47:12','Mazima Katonda ajja kuyingiza abo abakkiriza nebakola emirimu emirongoofu e jjana. Ezikulukutira wansi waazo emigga, ate abo abaakafuwala beeyagala era ne balya ng''ensolo bwezirya, era omuliro bwe buddo bwabwe.');
INSERT INTO "african_dev" VALUES(47,13,'47:13','Era ebitundu bimeka nga byo byali byamaanyi okusinga ekitundu kyo (Makkah)? ekyo ekyakugoba, abantu be bitundu ebyo twabazikiriza newataba abadduukirira.');
INSERT INTO "african_dev" VALUES(47,14,'47:14','Abaffe oyo aba ali ku bunnyonnyofu obuva ewa Mukama omulabirizi we ayinza okuba ngooyo emirimu gye emibi egyamulabisibwa okuba nti mirungi!, era (abantu abo) nebagoberera okwagala kwabwe.');
INSERT INTO "african_dev" VALUES(47,15,'47:15','(Abaffe) e jjana eyo abatya Katonda gye balaganyisibwa (egenda kuba nga) erimu emigga egy''amazzi agatayonooneka nemigga gya mata agatakyuse mpoma yaago, n’emigga egy''omubisi oguliwoomera abanywi n''emigga gya asaali omusengejje, nga bali mu yo balifuna ebibala byonna, era balifuna mu yo ekisonyiwo okuva ewa Mukama omulabirizi waabwe, oyinza okugigerageranya ku oyo owokutuula mu muliro obugenderevu nga era balinywesebwa olwegye lwa mazzi, olwo nno negakutulakutula ebyenda byabwe.');
INSERT INTO "african_dev" VALUES(47,16,'47:16','Era mu bo mulimu abakuwuliriza (era nebalaga nti bagonvu) okutuusa bwe bafuluma okuva wooli, bagamba abo abaweebwa okumanya nti, abadde agambaki emabegako awo?, abo beebo Katonda beyassa envumbo ku mitima gyabwe era nebagoberera okwagala kwabwe.');
INSERT INTO "african_dev" VALUES(47,17,'47:17','Ate abo abaalungama (Katonda) abongera bulungamu era naabawa okutya kwabwe (Katonda).');
INSERT INTO "african_dev" VALUES(47,18,'47:18','Abaffe balina kyebalindirira okugyako ekiseera ekyenkomerero okuba nga kibaggyira kibwatuukira ate nga ddala (obumu) ku bubonero bwakyo bumaze okujja, baligyawa okubuulirirwa kwabwe (okulibagasa ekiseera) bwe kiribagyira!.');
INSERT INTO "african_dev" VALUES(47,19,'47:19','Kale manya nti mazima tewali kisinzibwa okugyako Katonda era osabe ekisonyiwo olwe byonoono byo, era abakkiriza abasajja n''abakkiriza abakazi obasabire ekisonyiwo, era Katonda amanyi entambula zammwe (nga mujjulukuka okuva mu kifo ne mudda mu kirala n''okuva ku kikolwa ne mudda ku kirala) era amanyi obuddo bwa mmwe.');
INSERT INTO "african_dev" VALUES(47,20,'47:20','Abo abakkiriza bagamba nti singa essura (eragira olutalo) essibwa, naye essura neemala essibwa nga eyogera mu bulambulukufu nga n''okulwana kwogerwako mu yo, olaba abo abalina obulwadde mu mitima gyabwe nga bakutunuulira entunula ey’omuntu azirise ku lwokutya okufa. Ekyalisinze obulungi gyebali.');
INSERT INTO "african_dev" VALUES(47,21,'47:21','(Kwe) kugonda, n''ebigambo ebirungi, naye ekigambo bwe kimala okukakata, kale singa batuukiriza bye balagaanyisa Katonda kye kyalisinze obulungi gye bali.');
INSERT INTO "african_dev" VALUES(47,22,'47:22','Abaffe musuubira nti singa mukulembera (ne muba abafuzi) nti munayonoona mu nsi ne mukutula enganda zammwe.');
INSERT INTO "african_dev" VALUES(47,23,'47:23','(Abakola ebyo) abo beebo Katonda beyakolimira naabaggala amatu era naaziba amaaso gaabwe.');
INSERT INTO "african_dev" VALUES(47,24,'47:24','Abaffe tebetegereza Kur’ani, oba emitima gyegiriko ekkufulu zaagyo.');
INSERT INTO "african_dev" VALUES(47,25,'47:25','Mazima abo abakyuka ne badda gyebaava (nebaleka obusiraamu) oluvanyuma lw’obulungamu okuberambikira, Sitane yeyabibalaga nti birungi era nabasuubiza ebyobulimba.');
INSERT INTO "african_dev" VALUES(47,26,'47:26','Ekyo nno, lwakuba nti mazima bo, bagamba abatayagala Katonda byeyassa, nti tujja kubagondera mu bintu ebimu. Era bulijjo Katonda amanyi ebyama byabwe.');
INSERT INTO "african_dev" VALUES(47,27,'47:27','Olwo nno baba ngeri ki Malayika bwezibatusaako entuuko zaabwe, nga zikuba ebyenyi byabwe n’obutuuliro bwabwe.');
INSERT INTO "african_dev" VALUES(47,28,'47:28','Ekyo (bwekityo bwekiriba) olwokuba nti mazima bo baagoberera ebyo ebinyiiza Katonda nebakyawa okusiima kwe, olwo nno naayonoona emirimu gyabwe.');
INSERT INTO "african_dev" VALUES(47,29,'47:29','Abaffe abo abalina obulwadde mu mitima gyabwe basuubira nti Katonda tagenda kuggyayo bukukuuzi bwabwe (bwe balina ku Nabbi Muhammad).');
INSERT INTO "african_dev" VALUES(47,30,'47:30','Singa twagala twandibakulaze, olwo nno wandibategedde olwo bubonero bwabwe, era ojja kubategeerera ddala okusinziira ku njogera yaabwe. Era Katonda amanyi emirimu gyammwe.');
INSERT INTO "african_dev" VALUES(47,31,'47:31','Era tujja kubagezesa okutuusa lwetunategeera abalafubana mu mmwe mu kuweereza mu kkubo lya Katonda nabagumikiriza, era tujja kugezesa ebigambo bya mmwe.');
INSERT INTO "african_dev" VALUES(47,32,'47:32','Mazima abo abaakafuwala nebaziyiza abantu ku kkubo lya Katonda, nebeesimba mu mubaka oluvanyuma lwo bulungamu okubeeyoleka, (abo) tebalina kabi konna kebayinza kutuusa ku Katonda, era wa kusazaamu emirimu gyabwe.');
INSERT INTO "african_dev" VALUES(47,33,'47:33','Abange mmwe abakkiriza mugondere Katonda, era mugondere omubaka, era temwononanga emirimu gya mmwe.');
INSERT INTO "african_dev" VALUES(47,34,'47:34','Mazima abo abaakafuwala era nebassa emisanvu mu kkubo lya Katonda, oluvanyuma nebafa nga bakafiiri, Katonda tagenda kubasonyiwa.');
INSERT INTO "african_dev" VALUES(47,35,'47:35','Temufotokereranga ne musaba emirembe ate nga mmwe ba waggulu, era nga Katonda ali wamu na mmwe, era nga tagenda kukendeeza mpeera ya mirimu gya mmwe.');
INSERT INTO "african_dev" VALUES(47,36,'47:36','Mazima obulamu bwe nsi muzannyo na binyumu, naye bwe mukkiriza era nemutya Katonda, ajja kubawa empeera ya mmwe, ate nga tabasabye mmali ya mmwe.');
INSERT INTO "african_dev" VALUES(47,37,'47:37','Singa agibasaba nabapeeka mujja kukodowala, olwo nno nassa mu bbanga obukukuuzi bwa mmwe.');
INSERT INTO "african_dev" VALUES(47,38,'47:38','Abaffe mmwe, mmwe beebo abakowoolwa mube nga muwaayo mu kkubo lya Katonda, olwo nno mu mmwe ne mubaamu akodowala, so nga omuntu akodowala, mazima yekodowalira yekka ng’ate Katonda yatalina kyeyetaaga ate nga mmwe muli betaavu, naye bwe munakyuka (nemuva ku ddiini) ajja kuleeta abantu abalala abatali mmwe, oluvanyuma tebagenda kuba nga mmwe.');
INSERT INTO "african_dev" VALUES(48,1,'48:1','Mazima ffe tukugguliddewo oluggulawo olweyolefu.');
INSERT INTO "african_dev" VALUES(48,2,'48:2','Katonda alyoke akusonyiwe ebyonoono byo ebyakulembera, n''ebyo ebyoluvanyuma, era ajjuze ekyengera kye kye yakuwa, era akulungamye mu kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(48,3,'48:3','Era akutaase olutaasa olwa nnamaddala.');
INSERT INTO "african_dev" VALUES(48,4,'48:4','Yye yooyo eyassa obutebenkevu mu mitima gya bakkiriza babe nga beyongera obukkiriza ku bukkiriza bwabwe (obwasooka), amagye go mu ggulu omusanvu ne nsi gonna ga Katonda, era (bulijjo) Katonda mumanyi nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(48,5,'48:5','Olwo nno ayingize abakkiriza abasajja n''abakkiriza abakazi e jjana, nga emigga gikulukutira wansi waazo, ba kutuula mu zo obugenderevu, era abagyeko ebisobyo bya bwe, ekyo ewa Katonda bwe buwanguzi obunene.');
INSERT INTO "african_dev" VALUES(48,6,'48:6','Era abonereze abananfusi abasajja n''abannanfusi abakazi, n''abasajja abagatta ebintu ebirala ku Katonda n''abakazi abagatta ebintu ebirala ku Katonda, abalowooza ku Katonda endowooza embi, batukwako enkyukakyuka embi, era Katonda yabasunguwalira nabakolimira era yabategekera omuliro Jahannama (era nga ggwo) buddo bubi.');
INSERT INTO "african_dev" VALUES(48,7,'48:7','Era amagye go mu ggulu omusanvu n''ensi gonna ga Katonda, era (bulijjo) Katonda nantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(48,8,'48:8','Era mazima ffe twakutuma (ggwe Nabbi Muhammad) nga oli mujulizi era omusanyusa (olwa mawulire agessanyu gowa abakozi bo bulungi) era omutiisa (olwa mawulire ag’ebibonerezo eri abakozi be bibi).');
INSERT INTO "african_dev" VALUES(48,9,'48:9','Mulyoke mukkirize Katonda n’omubaka we, era mu muyambe era mumuwe ekitiibwa era (Katonda) mumutendereze enkya ne ggulo.');
INSERT INTO "african_dev" VALUES(48,10,'48:10','Mazima abo abalangirira obuwagizi bwabwe gyoli mazima balangirira buwagizi bwabwe eri Katonda, omukono gwa Katonda guli waggulu wa mikono gya bwe, kale nno oyo anaamenya endagaano mazima mukugimenya aba yeefiriza yekka, nooyo atuukiriza ekyo kye yaweerako Katonda obweyamu ajja kumuwa empeera ensuffu.');
INSERT INTO "african_dev" VALUES(48,11,'48:11','Ba nnamalungu abaasigala (ne batagenda Makkah naawe) bajja kugamba nti emmaali yaffe na b’omumaka gaffe bye bya tulobera okugenda n’olwekyo tusabire ekisonyiwo, boogera ne nnimi zaabwe ebyo ebitali mu mitima gyabwe, bagambe nti olwo nno ani alina obuyinza ewa Katonda ku kintu kyonna ekibakwatako singa aba abaagalizza kabi, oba nga abaagalizza eky''omugaso, wabula Katonda amanyidde ddala ensibuko y''ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(48,12,'48:12','Wabula mwalowooza nti omubaka n’abakkiriza lubeerera tebaali ba kudda eri bantu ba mu maka gaabwe, era ekyo nekinyirizibwa eri emitima gya mmwe, nemulowooza endowooza embi, era nemuba abantu abaggweera mu kubula.');
INSERT INTO "african_dev" VALUES(48,13,'48:13','Oyo yenna atakkiriza Katonda n’omubaka we, (ka kumujjuutuka) anti mazima ffe abakafiiri twabategekera omuliro S’aira.');
INSERT INTO "african_dev" VALUES(48,14,'48:14','Obufuzi obwe ggulu omusanvu n''ensi bwa Katonda, asonyiwa gwaba ayagadde era nabonereza gwaba ayagadde era bulijjo Katonda musonyiyi nnyo musaasizi.');
INSERT INTO "african_dev" VALUES(48,15,'48:15','Abataagenda (naawe ku lutabaalo lwe Khaibara) bwe mugenda eri eminyago mube nga mugifuna bajja kugamba nti mutuleke tubagoberere, nga baagala okuba nga bakyusa ebigambo bya Katonda (okuba nga eminyago gyabo bokka ebeetaba mu lutabaalo) bagambe nti temutugoberera, bwekityo Katonda bwe yagamba oluberyeberye, olwo nno bajja kugamba nti wabula (mmwe) mutukwatirwa nsaalwa (naye nga si kyekyo) wabula baali tebategeera okugyako katono ddala.');
INSERT INTO "african_dev" VALUES(48,16,'48:16','Gamba abo abaasigala mu ba nnamalungu, nti lumu mujja kuyitibwa okugenda eri abantu abaamaanyi amayitirivu mube nga mulwanagana nabo oba basiramuke, awo nno singa muligonda (nemutuukiriza ekiragiro) Katonda ajja kubawa empeera ennungi, naye kemulimala muva kwekyo (ne mutalwana) nga bwe mwakyuka oluberyeberye (bwe mutaalwana) agenda kubabonereza ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(48,17,'48:17','Muzibe taliiko kunenyezebwa, n’omulema taliiko kunenyezebwa, wadde omulwadde naye taliiko kunenyezebwa, oyo yenna agondera Katonda n’omubaka we amuyingiza e jjana, emigga nga gikulukutira wansi waazo, ate oyo yenna akyuka (naatamugondera) agenda ku mubonereza ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(48,18,'48:18','Katonda yasiima abakkiriza bwe baalangirira obuwagizi bwabwe gyoli (bwe mwali) wansi wo muti olwo nno yamanya ebiri mu mitima gya bwe kwe kussa obutebenkevu ku bo, era nabasasula n''abawa obuwanguzi obwali okumpi.');
INSERT INTO "african_dev" VALUES(48,19,'48:19','N''eminyago mingi gye baafuna, era (bulijjo) Katonda ye nantakubwa ku mukono mugobansonga.');
INSERT INTO "african_dev" VALUES(48,20,'48:20','Katonda yabalagaanyisa eminyago mingi gye mulifuna, naye guno gwo naagubanguyiza era naaziyiza emikono gya bantu obutabatuukako (obutabalwanyisa) era (ebyo) bibeere kya kuyiga eri abakkiriza era abalungamye mu kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(48,21,'48:21','(Waliyo n''eminyago) emirala gye mutannasobola (kufuna) naye nga mazima Katonda agyetoolodde (gyonna agimanyi) era bulijjo Katonda muyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(48,22,'48:22','Era singa abo abakaafuwala babalwanyisa, ddala bandikubyeyo amabega (ne badduka) era nga tebayinza kufuna mukuumi wadde omudduukirize.');
INSERT INTO "african_dev" VALUES(48,23,'48:23','Eyo yenkola ya Katonda ebaddewo okuva oluberyeberye, era enkola ya Katonda toyinza kugisangamu kukyusibwa.');
INSERT INTO "african_dev" VALUES(48,24,'48:24','Era yye yooyo eyaziyiza emikono gyabwe ku mmwe era naziyiza emikono gya mmwe ku bo (ne mutalwanagana bwe mwasisinkana) mu makkati gomu Makkah, nga yali amaze okubawa obuwanguzi ku bo, era nga Katonda bulijjo alabira ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(48,25,'48:25','Bo beebo abaakafuwala ne babaziyiza okutuuka ku muzigiti ogwe mizizo, nga n''ebirabo biremeseddwa okutuuka mu bifo byabyo, era singa si basajja abakkiriza n''abakazi abakkiriza, be mutaamanya okuba nti mwali muyinza okubatusaako obulabe (ne mubatta), ne mutuukwako akamogo (akokunenyezebwa okubatta) mu butali bugenderevu, Katonda yandibadde akkiriza ne mwambalagana nabo, (ekyo kyali bwekityo) Katonda abe nga ayingiza mu kusaasira kwe oyo gwaba ayagadde, era singa beeyawula (abakkiriza bokka na batakkiriza bokka) era twandibonerezza abo abaakafuwala mu bo ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(48,26,'48:26','Jjukira abo abaakafuwala bwe bassa mu mitima gyabwe effutwa, effutwa lyo mu biseera eby''obutamanya, olwonno Katonda nassa obukkakkamu ku mubaka we, ne ku bakkiriza nabanywereza ku kigambo kyo kutya Katonda era nga be baali basinga okukisaanira, era nga be baali ba nannyinikyo era bulijjo Katonda mumanyi wa buli kintu.');
INSERT INTO "african_dev" VALUES(48,27,'48:27','Mazima Katonda yatuukiririza omubaka we endooto gye yamulaga eya mazima (bwe yaloota nti) ddala mujja kuyingira omuzikiti ogwemizizo Katonda bwaliba ayagadde. (muliguyingira) nga muli mirembe, nga mumwedde emitwe gya mmwe, nga (abalala) mukendezezzaako bukendeeza nga temutya. (Yamanya kye mutaamanya), nateekawo obuwanguzi obwokumpi (nga ekyo tekinnabaawo, bwe mwakola endagaano ye Hudaibiyah).');
INSERT INTO "african_dev" VALUES(48,28,'48:28','Katonda yye, yooyo eyatuma omubaka we naleeta obulungamu n''eddiini eya amzima abe ng''alaga ekifo kyaayo ekya waggulu ku madiini gonna, kimala okuba nti Katonda yemujulizi (kw''ekyo).');
INSERT INTO "african_dev" VALUES(48,29,'48:29','Muhammad Mubaka wa Katonda era abo abali naye bakambwe ku bakafiiri, basaasiragana, obalaba nga bakutamye ku maviivi, ne bwebaba bavunnamye ku ttaka, nga banoonya ebigabwa bya Katonda n’okusiima (kwe) obulambe bwabwe bulabika mu byenyi byabwe olwo mukululo gwo kuvunnama, ekyo kye kifaananyi kyabwe mu Tauraati era ekifaananyi kyabwe mu njiri, balinga ekimera ekifulumya omutunsi gwakyo negukiwanirira okutuusa lwe kikakata, olwo nno ne kyetengerera ku nduli yaakyo nekiba nga kisanyusa abalimi. (Abantu abakubiddwako ekifaananyi ekyo) balyoke babe nga abakafiiri banakuwala ku lwabwe, abo abakkiriza mu bo ne bakola emirimu emirongoofu Katonda yabalagaanyisa ekisonyiwo n''empeera ensuffu.');
INSERT INTO "african_dev" VALUES(49,1,'49:1','Abange mmwe abakkiriza temwetumikirizanga mu maaso ga Katonda n’omubaka we (nemwogera ku kintu nga omubaka tannakyogerako) era mutye Katonda mazima Katonda muwulizi nnyo mumanyi.');
INSERT INTO "african_dev" VALUES(49,2,'49:2','Abange mmwe abakkiriza temusitulanga amaloboozi ga mmwe okusinga ku ddoboozi lya Nabbi, era temumwatuliranga ebigambo nga abamu mu mmwe bwe batuulira bannabwe, emirimu gya mmwe gireme kwononeka nga temugenderedde.');
INSERT INTO "african_dev" VALUES(49,3,'49:3','Mazima abo abakkakkanya amaloboozi gaabwe awali omubaka wa Katonda, abo beebo Katonda beyatukuza emitima gyabwe babe nga bamutya. Balina ekisonyiwo ne mpeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(49,4,'49:4','Mazima abo abakukowoolera emabega w’ebisenge abasinga obungi mu bo tebategeera.');
INSERT INTO "african_dev" VALUES(49,5,'49:5','Singa mazima bo bagumikiriza okutuusa lwofuluma n''ogenda gye bali, kyandibadde kirungi gye bali, era Katonda musonyiyi waakisa nnyo.');
INSERT INTO "african_dev" VALUES(49,6,'49:6','Abange mmwe abakkiriza singa omwononefu abaleetera amawulire muteekwa okwetegereza, mube nga temutuusa kabi ku bantu mu butamanya, olwo nno nemukeesa nga mwejjusa olwekyo kye mwakoze.');
INSERT INTO "african_dev" VALUES(49,7,'49:7','Era mumanye nti mazima omubaka wa Katonda ali mu mmwe, singa abagondera mu bigambo ebisinga obungi (nga bwe mubiraba), mwandifunye obuzibu, naye ddala Katonda yabaagazisa obukkiriza nabulabisa bulungi eri emitima gya mmwe, na bakyayisa obukafiiri n’obwononefu n’obugyemu, (abo abataliimu mize egimenyeddwa waggulu) bo be balungamu.');
INSERT INTO "african_dev" VALUES(49,8,'49:8','(Ng’ebyo byonna) birungi era nga byengera ebiva ewa Katonda, era bulijjo Katonda mumanyi nnyo mugoba nsonga.');
INSERT INTO "african_dev" VALUES(49,9,'49:9','Ebibinja bibiri mu bakkiriza bwe birwanagananga muteekwa mulongoose wakati wa byombi (mu bitabaganye) ekimu bwekirumba ekirala olwo nno mulwanyise ekyo ekiba kirumbye okutuusa lwekidda eri ekigambo kya Katonda bwekiba kizze kale mutabaganye wakati wa byombi mu bwesimbu, era mukole obwenkanya mazima Katonda ayagala abakola obwenkanya.');
INSERT INTO "african_dev" VALUES(49,10,'49:10','Ddala abakkiriza ba luganda n’olwekyo mulongoose (mutabaganye) wakati wa baganda ba mmwe, era mutye Katonda mube nga musaasirwa.');
INSERT INTO "african_dev" VALUES(49,11,'49:11','Abange mmwe abakkiriza abantu (abamu) mu mmwe tebanyomanga bantu bannabwe, anti oluusi bayinza okuba nga be balungi okusinga bo, wadde abakyala okunyoma bakyala bannabwe, anti oluusi bayinza okuba nga be balungi okusinga bo, era temwerengezzanga, era temweyitanga amannya ageemize, erinnya erisinga obubi lyeryo erikakkanya omuntu oluvanyuma lwokuba nti mukkiriza. Omuntu (akola ekyo) bwatenenya, abo bo be beeyisa obubi.');
INSERT INTO "african_dev" VALUES(49,12,'49:12','Abange mmwe abakkiriza mwewale okuteebereza okusinga obungi, anti okuteebereza okumu kiba kyonoono, era temwekettanga, era abamu mu mmwe tebageyanga bannabwe, abaffe omu ku mmwe yandiyagadde okulya ennyama ya muganda we nga mufu! Ate nga mwakitamwa dda, era mutye Katonda mazima Katonda akkiriza okwenenya musaasizi nnyo.');
INSERT INTO "african_dev" VALUES(49,13,'49:13','Abange mmwe abantu mazima ffe twabatonda nga tubaggya mu musajja n’omukazi, era ne tubafuula amawanga neebika mulyoke mumanyagane, mazima abasinga ekitiibwa ewa Katonda yooyo abasinga okumutya, mazima Katonda mumanyi nnyo amanyidde ddala ebintu okuva ku nsibuko yaabyo');
INSERT INTO "african_dev" VALUES(49,14,'49:14','Ba nnamalungu baagamba nti tukkirizza, bagambe nti temunnakkiriza, wabula mugambe nti twewaddeyo eri abasiraamu (netuwona okuttibwa oba okuwambibwa) era obukkiriza tebunnayingira mu mitima gya mmwe naye singa mugondera Katonda n’omubakawe tagenda kukendeeza ku mpeera ya mirimu gya mmwe kintu kyonna. Mazima Katonda musonyiyi musaasizi nnyo.');
INSERT INTO "african_dev" VALUES(49,15,'49:15','Mazima abakkiriza abannamaddala beebo abakkiriza Katonda n’omubaka we, ate nebatabuusabuusa (ebyo bye bakkiriza) era nebalafuubana mu kuweereza mu kkubo lya Katonda nga bakozesa emmaali yaabwe ne myooyo gyabwe, abonno, bo, be bakkiriza ab’amazima.');
INSERT INTO "african_dev" VALUES(49,16,'49:16','Bagambe nti mumanyisa Katonda eddiini ya mmwe ng’ate Katonda amanyi ebyo ebiri mu ggulu omusanvu n''ensi, era bulijjo Katonda amanyidde ddala ebikwata ku buli kintu.');
INSERT INTO "african_dev" VALUES(49,17,'49:17','Bakweralaasizaako nti baasiramuka, bagambe nti temundalaasizaako busiraamu bwa mmwe, wabula Katonda abenyumiririzaako okuba nti yabalungamya eri obukkiriza bwe muba nga muli ba mazima.');
INSERT INTO "african_dev" VALUES(49,18,'49:18','Mazima Katonda amanyi ebyekusifu byo mu ggulu omusanvu n''ensi, era Katonda alabira ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(50,1,'50:1','Kaaf, ndayidde Kur’ani eyekitiibwa (mazima ggwe Muhammad oli mubaka wa Katonda era olunaku lw’enkomerero lwa kubaawo).');
INSERT INTO "african_dev" VALUES(50,2,'50:2','Wabula bewunya okuba nti omutiisa (Nabbi Muhammad) yabajjira nga ava mu bo olwo nno abakaafiiri nebagamba nti kino kintu kya kitalo.');
INSERT INTO "african_dev" VALUES(50,3,'50:3','(Nebagamba nti) abaffe bwetufa netufuuka ettaka (ate tuli bakuzzibwa!), bwekiba bwekityo okwo kudda okuli ewala ennyo.');
INSERT INTO "african_dev" VALUES(50,4,'50:4','(Ekyo kibalemera bwereere okukkiriza anti) mazima tumanyi ekyo ettaka kye libakendezaako (nga lirya emibiri gyabwe) era tulina eggwanika erikuuma (buli kintu).');
INSERT INTO "african_dev" VALUES(50,5,'50:5','Wabula (tebakoma kwekyo era) baalimbisa amazima bwe gabajjira olwo nno bo baali mu mbeera ey’ekigambo ekitali kinywevu, (olumu bagamba bati olulala ne bagamba kirala).');
INSERT INTO "african_dev" VALUES(50,6,'50:6','Abaffe tebatunula ku ggulu waggulu waabwe (ne balaba) nga bwe twalizimba era netulinyiriza nga teririna bituli.');
INSERT INTO "african_dev" VALUES(50,7,'50:7','Ne nsi twagiseeteeza era netugissaamu ensozi ennywevu, netumeza mu yo mu buli mutindo oguwa essanyu.');
INSERT INTO "african_dev" VALUES(50,8,'50:8','Bibe ekizibula amaaso era ekyokubuulirira buli muddu adda eri Katonda.');
INSERT INTO "african_dev" VALUES(50,9,'50:9','Era tussa amazzi okuva waggulu agoomukisa, olwo nno netumeza nago amalimiro n''empeke ebikungulwa.');
INSERT INTO "african_dev" VALUES(50,10,'50:10','N’emitende emitumbiivu egirina ebirimba ebyeberese ku binnabyo.');
INSERT INTO "african_dev" VALUES(50,11,'50:11','Nga byakulya bya baddu, era tulamusa nago (amazzi) ettaka effu eriba likaze, nabwekutyo okuzuukira bwe kuliba.');
INSERT INTO "african_dev" VALUES(50,12,'50:12','Oluberyeberye lwabwe (abantu be Makkah) abantu ba Nuhu baalimbisa, n’abantu ababbika Nabbi wabwe mu luzzi, n’abekika kya Thamud.');
INSERT INTO "african_dev" VALUES(50,13,'50:13','Nabekika kya Aadi ne Firaawo ne baganda ba Luutu.');
INSERT INTO "african_dev" VALUES(50,14,'50:14','N’abantu be nsi y’ebibira, n’abantu ba Tubbai, bonna baalimbisa ababaka, olwo nno nebikakata (ebibonerezo) byange bye nalagaanyisa.');
INSERT INTO "african_dev" VALUES(50,15,'50:15','Abaffe twakoowa olwokutonda okwasooka (tube nga tweganya okubazzaawo)! nedda bo bali mu kutabulwa ku bikwata ku kutonda okupya.');
INSERT INTO "african_dev" VALUES(50,16,'50:16','Ng’ate ddala twatonda omuntu era tumanyi ekyo omwoyogwe kye gumulabankanya nakyo naye nga ffe tuli okumpi naye okusinga omusuwa (gwe) ogwobulamu.');
INSERT INTO "african_dev" VALUES(50,17,'50:17','Mu kiseera we bamugyirako ebimukwatako (ba Malayika) abakwatibwako ensonga ababiri, nga bakikola ku mukono ogwa ddyo, n’ogwa kkono nga buli omu atudde ku ludda lwe.');
INSERT INTO "african_dev" VALUES(50,18,'50:18','Olwo nno (omuntu) tayatula kigambo kyonna okugyako ng’aliko omulondoozi amubeerako buli kiseera.');
INSERT INTO "african_dev" VALUES(50,19,'50:19','Olwo nno obukambwe bwokufa nebujja mu mazima (nagambibwa nti) kino bulijjo kyobadde weewala.');
INSERT INTO "african_dev" VALUES(50,20,'50:20','Era engombe nefuuyibwa olwo lwe luliba olunaku olwalagaanyisibwa.');
INSERT INTO "african_dev" VALUES(50,21,'50:21','Buli muntu alijja nga aliko (Malayika) amugoba n’omujulizi.');
INSERT INTO "african_dev" VALUES(50,22,'50:22','(Omukozi weebibi aligambwa nti) mazima wali mu bulagajjavu ku nsonga eno, kakano tukuggyeko ekibikka olwo nno eriiso lyo olwaleero lyogi nnyo.');
INSERT INTO "african_dev" VALUES(50,23,'50:23','Munne (Malayika eyamuberangako) aligamba nti, bino bye nnina ebimuwaako obujulizi binywevu nnyo.');
INSERT INTO "african_dev" VALUES(50,24,'50:24','(Katonda aliragira Malayika alijja amugoba n''alimuwaako obujulizi nti) mukasuke mu muliro Jahannama buli mukafiiri omuwalaza we mpaka.');
INSERT INTO "african_dev" VALUES(50,25,'50:25','Eyekiikanga mu kkubo lyo bulungi, eyabukanga ebikomo (bya mateeka ga Katonda) omubusabuusa.');
INSERT INTO "african_dev" VALUES(50,26,'50:26','Oyo eyassa ku Katonda ekisinzibwa ekirala, kale nno mumukasuke mu bibonerezo ebikambwe.');
INSERT INTO "african_dev" VALUES(50,27,'50:27','(Sitane) munywanyiwe aligamba nti, ayi Mukama omulabirizi waffe simubuzanga, wabula (ye yennyini) yali mu bubuze obwewala.');
INSERT INTO "african_dev" VALUES(50,28,'50:28','(Katonda) aligamba nti temukayanira mu maaso gange nabawa dda okulabula (kwange nga mukyali ku nsi).');
INSERT INTO "african_dev" VALUES(50,29,'50:29','Ekigambo tekikyusibwa gyendi (kasita mala okusalawo) era nze siyinza kuba mulyazamanyi eri abaddu.');
INSERT INTO "african_dev" VALUES(50,30,'50:30','(Mutye) olunaku lwe tuligamba omuliro Jahannama nti abaffe ojjudde, negugamba nti abaffe waliyo enyongeza?.');
INSERT INTO "african_dev" VALUES(50,31,'50:31','E jjana erisembezebwa eri abo abatya Katonda, nga tekyali wala.');
INSERT INTO "african_dev" VALUES(50,32,'50:32','Nebagambibwa nti, ekyo kye mwalaganyisibwa nga kya kufunwa oyo yenna eyadda ewa Katonda, n''akuma butiribiri (amateekage).');
INSERT INTO "african_dev" VALUES(50,33,'50:33','(Yye yooyo) eyatya Katonda omusaasizi ennyo awamu nokuba nga tamulabako, era najja (olwaleero ku lunaku lw’enkomerero) n’omutima ogwemalira ku Katonda.');
INSERT INTO "african_dev" VALUES(50,34,'50:34','(Baligambibwa nti) mugiyingire (e jjana) mu mirembe, olwo lwe lunaku olulitandika obulamu obwolubeerera.');
INSERT INTO "african_dev" VALUES(50,35,'50:35','Balifuna byonna bye baagala nga bali mu yo (e jjana), era tulina ebyo kubongeza.');
INSERT INTO "african_dev" VALUES(50,36,'50:36','Era emirembe emeka gye twazikiriza oluberyeberye lwabwe (bano abakuwakanya ggwe Nabbi Muhammad), nga bo baali bamaanyi okubasinga, nebetoloola ensi (nga bakola buli kye bagala), abaffe waliwo obuwonero.');
INSERT INTO "african_dev" VALUES(50,37,'50:37','Mazima mwekyo mulimu okubuulirira eri oyo alina omutima oba atega amatu era nga yenna waali (awuliriza).');
INSERT INTO "african_dev" VALUES(50,38,'50:38','Era mazima twatonda eggulu omusanvu ne nsi neebiri wakati wa byombi mu nnaku mukaaga era tetwatukwako kukoowa kwonna.');
INSERT INTO "african_dev" VALUES(50,39,'50:39','Kale gumikiriza ebyo bye bagamba, otendereze ebitendo bya Mukama omulabirizi wo nga enjuba tennavaayo, ne nga tennagwa.');
INSERT INTO "african_dev" VALUES(50,40,'50:40','Era omutendereze ekiro nabuli luvannyuma lwa kuvunnama (okusaala e sswala ezalaalikibwa).');
INSERT INTO "african_dev" VALUES(50,41,'50:41','Era wuliriza (ng’olinda) olunaku omukowooze lwalikowoola nga asinziira mu kifo kyo kumpi.');
INSERT INTO "african_dev" VALUES(50,42,'50:42','Olunaku lwe bali wulira okubwatuka okwa mazima, olwo nno lwe luliba olunaku lwokufuluma (okuzuukira okuva mu ntaana).');
INSERT INTO "african_dev" VALUES(50,43,'50:43','Mazima ffe, ffe tuwa obulamu era netutta, era gye tuli yokka yeeri obuddo.');
INSERT INTO "african_dev" VALUES(50,44,'50:44','Ku lunaku ettaka lweriyatika neribavaako (nga bazuukira) nebajja misinde (gye tuli), okwo kwe kukungaanya era kuliba kwangu ku ffe.');
INSERT INTO "african_dev" VALUES(50,45,'50:45','Ffe tusinga okumanya bye bagamba, era ggwe toli ku bo asobola kubawaliriza, n’olwekyo buulirira nga okozesa Kur’ani, oyo yenna atya ebitiisa byange.');
INSERT INTO "african_dev" VALUES(51,1,'51:1','Ndayidde empewo ezifumuula enfuufu olufumuula.');
INSERT INTO "african_dev" VALUES(51,2,'51:2','Olwo nno nendayira ebire ebyetikka enkuba olwetikka.');
INSERT INTO "african_dev" VALUES(51,3,'51:3','N''amaato agaseeyeeya mu mazzi mu bwangu.');
INSERT INTO "african_dev" VALUES(51,4,'51:4','Era ndayidde Malayika ezigabanya Riziki okusinziira ku kiragiro ekiba kiziweereddwa.');
INSERT INTO "african_dev" VALUES(51,5,'51:5','Mazima bye mulaganyisibwa bya kutuukirira.');
INSERT INTO "african_dev" VALUES(51,6,'51:6','Era ddala olunaku lwo kusasula lwa kubaawo.');
INSERT INTO "african_dev" VALUES(51,7,'51:7','Ndayidde eggulu eryakolebwa mu ngeri ennungi.');
INSERT INTO "african_dev" VALUES(51,8,'51:8','Mazima mmwe muli mu kigambo ekyawukamu (temussa kimu ku bikwata ku Kur’ani).');
INSERT INTO "african_dev" VALUES(51,9,'51:9','Awugulwa ku kyo oyo eyabuzibwa (natakkiriza).');
INSERT INTO "african_dev" VALUES(51,10,'51:10','Baakolimirwa abo abalimba (aboogera ebitali bituufu ku Nabbi).');
INSERT INTO "african_dev" VALUES(51,11,'51:11','Abo abali mu nnyanga ey’obukaafiiri nga babulubuuta.');
INSERT INTO "african_dev" VALUES(51,12,'51:12','Nga babuuza nti olunaku lwo kusasula luliba lwaddi?.');
INSERT INTO "african_dev" VALUES(51,13,'51:13','Lwe lunaku bo lwe balyokebwa omuliro.');
INSERT INTO "african_dev" VALUES(51,14,'51:14','(Nga bwe bagambibwa nti) mukombe ebibonerezo bya mmwe, kino kyekyo kye mwagambanga nti kibatuusibweko mu bwangu.');
INSERT INTO "african_dev" VALUES(51,15,'51:15','Mazima abatya Katonda balibeera mu masamba nga mulimu n''ensulo za mazzi.');
INSERT INTO "african_dev" VALUES(51,16,'51:16','Nga beeyanza ebyo Mukama omulabirizi waabwe byalibawa (anti) mazima bo oluberyeberye lwekyo bali balongoosa.');
INSERT INTO "african_dev" VALUES(51,17,'51:17','Bebakanga e kiseera kitono mu kiro.');
INSERT INTO "african_dev" VALUES(51,18,'51:18','Ate nga mu ttumbi babanga mu kwe gayirira.');
INSERT INTO "african_dev" VALUES(51,19,'51:19','Era nga mu maali yaabwe mulimu omugabo gwo musabi nooyo atafuna kimumala (naye nga tasaba).');
INSERT INTO "african_dev" VALUES(51,20,'51:20','Era mu nsi mulimu obubonero (obulaga okubaayo kwa Katonda n’obuyinza bwe), eri abo abakkiriza obukkiriza obwa nnamaddala.');
INSERT INTO "african_dev" VALUES(51,21,'51:21','Ne mu mmwe mwennyini (mulimu obubonero) abaffe temulaba.');
INSERT INTO "african_dev" VALUES(51,22,'51:22','Ne muggulu y''eri riziki za mmwe na byonna bye mulagaanyisibwa.');
INSERT INTO "african_dev" VALUES(51,23,'51:23','Kale ndayidde Mukama omulabirizi we ggulu n''ensi, nti ekyo bwekityo bwekiri (era nga ekyo buli muntu yenna akimanyi) nga bwe mumanyi nti mwatula.');
INSERT INTO "african_dev" VALUES(51,24,'51:24','Abaffe ebikwata ku bagenyi ba Ibrahim abekitiibwa bya kutuukako.');
INSERT INTO "african_dev" VALUES(51,25,'51:25','Bwe baayingira weyali ne bagamba nti mirembe, (naye) naagamba nti mirembe (wabula muli) bantu abatamanyiddwa.');
INSERT INTO "african_dev" VALUES(51,26,'51:26','Olwo nno nadda eri abantu be naleeta ennyana y''ente ensava (enjokye).');
INSERT INTO "african_dev" VALUES(51,27,'51:27','Nagibasembereza naagamba nti abaffe nga temuulye!.');
INSERT INTO "african_dev" VALUES(51,28,'51:28','(Bwe bataalya) yawulira nga abatiddemu, ne bagamba nti totya era nebamuwa amawulire agessanyu ag''omwana omulenzi omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(51,29,'51:29','(Awo wennyini) mukyala we naatuuka nga ayogerera waggulu nakuba ekyenyi kye, era naagamba nti (nze nga ndi) mukadde omugumba (nzaala ntya!).');
INSERT INTO "african_dev" VALUES(51,30,'51:30','(Ba Malayika) ne bagamba nti bwekityo Mukama omulabirizi wo bwagambye anti mazima yye bulijjo, ye mugoba nsonga omumanyi ennyo.');
INSERT INTO "african_dev" VALUES(51,31,'51:31','Ibrahim naagamba nti, ate mmwe ababaka nsonga ki ebaleese?.');
INSERT INTO "african_dev" VALUES(51,32,'51:32','Nebagamba nti mazima ffe tutumiddwa eri abantu abonoonyi.');
INSERT INTO "african_dev" VALUES(51,33,'51:33','Tube nga tubayiwako amayinja agomu bbumba ejjokye.');
INSERT INTO "african_dev" VALUES(51,34,'51:34','Nga malambe okuva ewa Mukama omulabirizi wo, nga ga (kuzikiriza) abonoonyi abasukkiridde.');
INSERT INTO "african_dev" VALUES(51,35,'51:35','(Katonda agamba) awo nno twagyamu oyo yenna eyakirimu (ekitundu ekyo) mu bakkiriza.');
INSERT INTO "african_dev" VALUES(51,36,'51:36','Naye tetwasangamu okugyako e nyumba emu eyabasiraamu.');
INSERT INTO "african_dev" VALUES(51,37,'51:37','Era twaleka mu kyo ekyo kuyiga eri abo abatya ebibonerezo ebiruma ennyo.');
INSERT INTO "african_dev" VALUES(51,38,'51:38','Era ne Musa (alimu ekyo kuyiga) bwe twamutuma ewa Firawo n’obujulizi obweyolefu.');
INSERT INTO "african_dev" VALUES(51,39,'51:39','Yagaana (okugoberera Musa) olwokwesigula abali emabega we era nagamba nti (Musa) mulogo oba mulalu.');
INSERT INTO "african_dev" VALUES(51,40,'51:40','Olwo nno netumukwatako ne ggye rye netubakasuka mu nnyanja nga naye anenyezebwa.');
INSERT INTO "african_dev" VALUES(51,41,'51:41','Ne mu Bantu ba Aadi (mulimu ekyokuyiga) bwe twabasindikira empewo engumba (etaliimu mukisa tereeta nkuba wadde okuwasisa ebibala).');
INSERT INTO "african_dev" VALUES(51,42,'51:42','Nga tereka kintu kyonna kyeyitako okugyako nga ekifuula nga ensaano.');
INSERT INTO "african_dev" VALUES(51,43,'51:43','Ne mu bantu ba Thamud (mulimu ekyokuyiga) bwe bagambibwa nti mweyagale okumala akaseera katono.');
INSERT INTO "african_dev" VALUES(51,44,'51:44','Wabula bo basukka ekiragiro kya Mukama omulabiririzi waabwe, olwo nno okubwatuka okwamaanyi ne kubatuukako nga nabo balaba.');
INSERT INTO "african_dev" VALUES(51,45,'51:45','Tebaasobola kusituka (badduke) era tebaali ba kuwona.');
INSERT INTO "african_dev" VALUES(51,46,'51:46','N''abantu ba Nuhu nga bano tebannabaawo (nabo balimu ekyokuyiga anti nabo baazikirizibwa era nabo kya bagwanira) anti mazima bo baali bantu abonoonyi.');
INSERT INTO "african_dev" VALUES(51,47,'51:47','(N''ebirala ebirimu ekyokuyiga) ne ggulu twalizimba n''obuyinza (bwaffe) era mazima tukyaligaziya.');
INSERT INTO "african_dev" VALUES(51,48,'51:48','N''ensi twagiseteeza, ddala nga ffe abatesiteesi abasinga.');
INSERT INTO "african_dev" VALUES(51,49,'51:49','Era twatonda mu buli kintu emitindo ebiri ebiri (ekisajja ne kikazi) oba olyawo ne mujjukira.');
INSERT INTO "african_dev" VALUES(51,50,'51:50','Kale mudduke mudde eri Katonda mazima nze ndi mutiisa owolwatu gye muli okuva gyali.');
INSERT INTO "african_dev" VALUES(51,51,'51:51','N’olwekyo temussanga ku Katonda ebisinzibwa ebirala, mazima nze ndi mutiisa owolwatu gye muli okuva gyali.');
INSERT INTO "african_dev" VALUES(51,52,'51:52','Bwekityo abo ababakulembera tewali mubaka yabajjira okugyako nga bagamba nti mulogo oba mulalu.');
INSERT INTO "african_dev" VALUES(51,53,'51:53','Abaffe bakyeramira (nedda ssi bwekiri) wabula bo bantu be wagguzi.');
INSERT INTO "african_dev" VALUES(51,54,'51:54','Kale baveeko toli ggwe anenyezebwa.');
INSERT INTO "african_dev" VALUES(51,55,'51:55','Wabula bulirira anti mazima okubulirira kugasa abakkiriza.');
INSERT INTO "african_dev" VALUES(51,56,'51:56','Era ssaatonda amajinni n’abantu wabula lwakuba nti bansinze.');
INSERT INTO "african_dev" VALUES(51,57,'51:57','Sibaagalamu Riziki era ssaagala bandiise.');
INSERT INTO "african_dev" VALUES(51,58,'51:58','Mazima Katonda yye yaagaba Riziki nannyini maanyi omusobozi.');
INSERT INTO "african_dev" VALUES(51,59,'51:59','Mazima abo abeeyisa obubi balina ebyonoono nga ebyonoono bya bannaabwe n’olwekyo tebampapya (kubatuusaako bibonerezo mu bwangu).');
INSERT INTO "african_dev" VALUES(51,60,'51:60','Okubonabona kuli eri abo abawakanya olunaku lwabwe olwo lwe baalaganyisibwa (olunaku lw’enkomerero).');
INSERT INTO "african_dev" VALUES(52,1,'52:1','Ndayidde olusozi (Sinaa).');
INSERT INTO "african_dev" VALUES(52,2,'52:2','Ndayidde ekitabo ekiwandiike.');
INSERT INTO "african_dev" VALUES(52,3,'52:3','Mu mpapula ennyanjuluze.');
INSERT INTO "african_dev" VALUES(52,4,'52:4','Era ndayidde e nyumba etaggwako basinza (ba Malayika bagyetoloola olubeerera).');
INSERT INTO "african_dev" VALUES(52,5,'52:5','Era ndayidde akasolya (k''eggulu) akasitufu.');
INSERT INTO "african_dev" VALUES(52,6,'52:6','Era ndayidde ennyanja ezijjudde amazzi.');
INSERT INTO "african_dev" VALUES(52,7,'52:7','Ddala ebibonerezo bya Mukama omulabirizi wo bya kutuukirira.');
INSERT INTO "african_dev" VALUES(52,8,'52:8','Tewali kigenda kubiremesa.');
INSERT INTO "african_dev" VALUES(52,9,'52:9','Ku lunaku eggulu lwe liri yuguuma oluyuguuma.');
INSERT INTO "african_dev" VALUES(52,10,'52:10','Ensozi nezitambula butambuzi.');
INSERT INTO "african_dev" VALUES(52,11,'52:11','N’olwekyo okubonabona ku lunaku olwo kuliba ku balimbisa.');
INSERT INTO "african_dev" VALUES(52,12,'52:12','Abo bo ababeera mu byolusaago nga bazannya.');
INSERT INTO "african_dev" VALUES(52,13,'52:13','Ku lunaku lwe bali sindikirizibwa olusindikirizibwa, okuyingira omuliro Jahannama.');
INSERT INTO "african_dev" VALUES(52,14,'52:14','Baligambibwa nti guno gwe muliro ogwo gwe mwali mulimbisa.');
INSERT INTO "african_dev" VALUES(52,15,'52:15','Abaffe lino ddogo oba mmwe temulaba.');
INSERT INTO "african_dev" VALUES(52,16,'52:16','Mugwesogge, wabula ka mugumikirize oba temugumikiriza byonna kyekimu gye muli mazima ddala musasulwa ebyo bye mwali mukola.');
INSERT INTO "african_dev" VALUES(52,17,'52:17','Mazima abatya Katonda baliba mu jjana n''ebyengera.');
INSERT INTO "african_dev" VALUES(52,18,'52:18','Nga beyagala olwebyo Mukama omulabirizi waabwe byalibawa, era Mukama omulabirizi waabwe nabawonya ebibonerezo ebyomuliro Jahiimu.');
INSERT INTO "african_dev" VALUES(52,19,'52:19','(Abeeyisa obulungi baligambibwa nti) mulye era munywe, nga muyozayozebwa olwebyo bye mwakola.');
INSERT INTO "african_dev" VALUES(52,20,'52:20','Baliba besigamye ku bitanda nga bisimbiddwa ennyiriri era tugenda ku bagatta na bakyala abo mu jjana.');
INSERT INTO "african_dev" VALUES(52,21,'52:21','Abo abakkiriza, era ezzadde lyabwe neribagoberera mu bukkiriza, twakkiriza ezzadde lyabwe okubagoberera (baleme kufuna kiwubaalo obutaba nabo) era tetulikendeeza ku mirimu gyabwe kintu kyonna, era nga buli muntu (yye) gwe musingo gwebyo bye yakola.');
INSERT INTO "african_dev" VALUES(52,22,'52:22','Era (nga ogyeko ebyogeddwa waggulu) tulibongeza ebibala n''ennyama ebyo bye baliyagala.');
INSERT INTO "african_dev" VALUES(52,23,'52:23','(Nga bali mu jjana) baliyayaanira amagiraasi ge byo kunywa, naye nga ssi kujikubiramu lwogoolo wadde ebigambo ebikyafu.');
INSERT INTO "african_dev" VALUES(52,24,'52:24','Era abalenzi abaliba balabika nga l''ulu akuumwa ennyo baliba babetoololamu nga babaweereza.');
INSERT INTO "african_dev" VALUES(52,25,'52:25','Era (abaliba mu jjana) abamu balyolekera bannabwe nga bewunaganya.');
INSERT INTO "african_dev" VALUES(52,26,'52:26','Nga bagamba nti mazima ffe mukusooka twabanga mu bantu baffe nga tutya (ebibonerezo bya Katonda).');
INSERT INTO "african_dev" VALUES(52,27,'52:27','Katonda natugonnomolako ekyengera, naatuwonya ebibonerezo byo muliro samuumu.');
INSERT INTO "african_dev" VALUES(52,28,'52:28','(Anti) mazima ffe okusooka twamusabanga (twamusinzanga), mazima yye yaayisa obulungi, omusaasizi ennyo.');
INSERT INTO "african_dev" VALUES(52,29,'52:29','Kale nno buulirira (ggwe Muhammad) anti ggwe olwe kyengera kya Mukama omulabirizi wo, toli mulaguzi wadde mulalu (nga abamu bwe babijweteka).');
INSERT INTO "african_dev" VALUES(52,30,'52:30','Oba bagamba nti (Muhammad) mutontomi gwetulekedde okukwata kwa walumbe.');
INSERT INTO "african_dev" VALUES(52,31,'52:31','Bagambe nti mulindirire, anti mazima nze ndi wamu na mmwe mu balindirira.');
INSERT INTO "african_dev" VALUES(52,32,'52:32','Abaffe amagezi gaabwe ge gabalagira kino oba bo bantu abaabula!.');
INSERT INTO "african_dev" VALUES(52,33,'52:33','Oba bagamba nti (Muhammad) yajegunjirawo (Kur’ani ssi bwekiri) wabula tebakkiriza.');
INSERT INTO "african_dev" VALUES(52,34,'52:34','(Bwekiba nga yajigunjaawo) kale baleete ebigambo ebigifaanana bwebaba nga boogera mazima.');
INSERT INTO "african_dev" VALUES(52,35,'52:35','Oba batondwa awatali kintu (nti tewali Katonda yabatonda!) oba bo be batonda (ebitonde byonna nga nabo mwebali).');
INSERT INTO "african_dev" VALUES(52,36,'52:36','Oba be baatonda eggulu omusanvu ne nsi, nedda wabula bo tebakkiriza.');
INSERT INTO "african_dev" VALUES(52,37,'52:37','Oba be balina amawanika ga Mukama omulabirizi wo, oba bo be balina obuyinza (ku buli kintu).');
INSERT INTO "african_dev" VALUES(52,38,'52:38','Oba balina amadaala nga gebakozesa okuwuliriza ebifa waggulu (bwekiba bwekityo) kale abawuliriza baabwe baleete obujulizi obweyolefu.');
INSERT INTO "african_dev" VALUES(52,39,'52:39','Oba (Katonda) alina abaana ba buwala ate nga mmwe mulina abo bulenzi.');
INSERT INTO "african_dev" VALUES(52,40,'52:40','Oba obasaba musaala olwo nno bo nebaba nga bakaluubirirwa kusasula.');
INSERT INTO "african_dev" VALUES(52,41,'52:41','Oba balina okumanya ebyekusifu ne baba nga bo bawandiika (ebyekusifu bye bamanyi nebaba nga tebetaaga ggwe kubategeeza).');
INSERT INTO "african_dev" VALUES(52,42,'52:42','Oba baagala kukola nkwe (bakulemese) wabula abo abakaafuwala be bakosebwa enkwe (ezo).');
INSERT INTO "african_dev" VALUES(52,43,'52:43','Oba balina ekisinzibwa ekitali Katonda (nga kye kibayinula), Katonda musukkulumu kwebyo bye bamugattako.');
INSERT INTO "african_dev" VALUES(52,44,'52:44','Olw''obutakkiriza bwabwe ne bwe balaba ekibajjo nga kigwa okuva waggulu (kibazikirize) bandigambye nti bire ebyetuumye.');
INSERT INTO "african_dev" VALUES(52,45,'52:45','Kale baleke okutuusa lwe balisisinkana olunaku lwabwe, olwo, ku lwo, lwe balizikirizibwa.');
INSERT INTO "african_dev" VALUES(52,46,'52:46','Olunaku enkwe zaabwe lwe zitalibagasa kintu kyonna, era nga bo tebalidduukirirwa.');
INSERT INTO "african_dev" VALUES(52,47,'52:47','Awamu n''ekyo mazima abo abeeyisa obubi batuusibwako ebibonerezo (ebirala kuno ku nsi) nga tebannatuuka ku biri, naye abasinga obungi ku bo tebamanyi.');
INSERT INTO "african_dev" VALUES(52,48,'52:48','Kale gumikiriza ku kusalawo kwa Mukama omulabirizi wo, anti mazima ggwe oli wansi wa kulabirira kwaffe. Era tendereza ebitendo bya Mukama omulabirizi wo, buli lwoliyimirira (okusaala).');
INSERT INTO "african_dev" VALUES(52,49,'52:49','Era n''ekiro mutendereze era omutendeze ekiseera we emmunyeenye wezibulira.');
INSERT INTO "african_dev" VALUES(53,1,'53:1','Ndayidde emunyeenye bweba nga egudde.');
INSERT INTO "african_dev" VALUES(53,2,'53:2','Munammwe (Muhammad) tabulanga kuva ku bulungamu era tayonoonanga.');
INSERT INTO "african_dev" VALUES(53,3,'53:3','Era tayogera nga asinziira ku kwagala (kwe).');
INSERT INTO "african_dev" VALUES(53,4,'53:4','Tebiri byo (byayogera) okugyako okuba nti bubaka obuwereezebwa (gyali nga buva ewa Katonda).');
INSERT INTO "african_dev" VALUES(53,5,'53:5','Yabimuyigiriza owamaanyi amayitirivu (Malayika Jiburilu).');
INSERT INTO "african_dev" VALUES(53,6,'53:6','Nannyini busobozi eyeyoleka.');
INSERT INTO "african_dev" VALUES(53,7,'53:7','Bweyali mu bifo ebisinga okuba ebya waggulu mu bwengula.');
INSERT INTO "african_dev" VALUES(53,8,'53:8','Oluvanyuma (Jiburilu) yasembera olwo nebaba kumpi (ne Nabbi).');
INSERT INTO "african_dev" VALUES(53,9,'53:9','Olwo nno (okusembera) nekuba nga okusemberagana kw''ensonda za kasale ebbiri oba kumpi okusingako awo.');
INSERT INTO "african_dev" VALUES(53,10,'53:10','Olwo nno (Katonda) naatumira omuddu we ekyo kye yamutumira nga akozesa Jiburilu.');
INSERT INTO "african_dev" VALUES(53,11,'53:11','Emmeme (ya Nabbi Muhammad) teyalimba kwebyo bye yalaba.');
INSERT INTO "african_dev" VALUES(53,12,'53:12','Abaffe mumuwakanya kwekyo kyalaba.');
INSERT INTO "african_dev" VALUES(53,13,'53:13','Ate nga mazima yamulaba omulundi omulala.');
INSERT INTO "african_dev" VALUES(53,14,'53:14','Mu kifo awali awali (omuti) Sidirat munitaha awakuumwa.');
INSERT INTO "african_dev" VALUES(53,15,'53:15','Weguli awo wewali e jjana ewummulirwamu (bo Malayika mwe bawummulira).');
INSERT INTO "african_dev" VALUES(53,16,'53:16','Mu kiseera omuti Asidira bwe gwabikkibwa ekya gubikka.');
INSERT INTO "african_dev" VALUES(53,17,'53:17','Amaaso (ga Nabbi) tegawuguka wadde okusukka ekyo (kye yali ateekwa okulaba).');
INSERT INTO "african_dev" VALUES(53,18,'53:18','Mazima yalaba mu bya magero ebya Mukama omulabirizi we ebinene.');
INSERT INTO "african_dev" VALUES(53,19,'53:19','Abaffe mulaba Latta ne Uzza.');
INSERT INTO "african_dev" VALUES(53,20,'53:20','Ne Mannaata ekibumbe ekirala ekyokusatu (bibumbe byali bisinzibwa nga Nabbi tannatumwa).');
INSERT INTO "african_dev" VALUES(53,21,'53:21','Abaffe mmwe muzaala abalenzi yye nazaala bawala! (mwe bemutayagala newakubadde abalenzi na bawala ku Katonda kyekimu).');
INSERT INTO "african_dev" VALUES(53,22,'53:22','Bwekiba bwekityo eyo ngabanya ejjudde kyekubiira.');
INSERT INTO "african_dev" VALUES(53,23,'53:23','(Bye musinza) tebirina kyebiri okugyako okuba amannya gemwetuumira mmwe ne bakadde ba mmwe, Katonda tabissangako bujulizi bwonna (era ababisinza) tebalina kye bagoberera okugyako okuteebereza nokugoberera ebyo emyoyo gyabwe bye jaagala, so nga ate obulungamu bwabajjira nga buva ewa Mukama omulabirizi waabwe.');
INSERT INTO "african_dev" VALUES(53,24,'53:24','Oba kiri nti omuntu asobola okufuna ekyo kye yeegomba (atuuke okwesalirawo okusinza ebibumbe ate naaba nga tavunaanwa).');
INSERT INTO "african_dev" VALUES(53,25,'53:25','Wabula nno enkomerero ne ntandikwa bya Katonda yekka (n’olwekyo yekka yaasalawo).');
INSERT INTO "african_dev" VALUES(53,26,'53:26','Era ba Malayika bameka abali mu ggulu omusanvu nga okuwolereza kwabwe tekugenda kugasa kintu kyonna okugyako oluvanyuma lwa Katonda okukkiriza gwaliba ayagadde era nasiima.');
INSERT INTO "african_dev" VALUES(53,27,'53:27','Mazima abo abatakkiriza nkomerero be bayita ba Malayika amannya ag’ekikazi.');
INSERT INTO "african_dev" VALUES(53,28,'53:28','Era nga tebakirinaako kumanya kwonna, tebagoberera okugyako okuteebereza ate nga ddala okuteebereza tekulina kye kugasa awali amazima.');
INSERT INTO "african_dev" VALUES(53,29,'53:29','N’olwekyo va kwoyo ayawukana nokubulirira kwaffe, naatayagala okugyako obulamu bw''ensi.');
INSERT INTO "african_dev" VALUES(53,30,'53:30','Ekyo kye kikomo kyabwe mu kumanya mazima Mukama omulabirizi wo yaasinga okumanya oyo abula naava ku kkubo lye era yaasinga okumanya oyo alungama.');
INSERT INTO "african_dev" VALUES(53,31,'53:31','Bya Katonda yekka ebyo ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi (era yye ye nannyini mateeka kwe bikolera) olwo nno alisasula abo abayonoona olwebyo bye bakola era nga alisasula obulungi abo abalongoosa.');
INSERT INTO "african_dev" VALUES(53,32,'53:32','Abo abeesamba ebyonoono ebinene ne byo buwemu okugyako ebitonotono, (anti) mazima Mukama omulabirizi wo mugazi wa kusonyiwa ye yaasinga okumanya ebikwata ku mmwe, okuva lwe yabatandikawo nga abagya mu ttaka ne mu kiseera we mwaberera mu mbuto za ba Maama ba mmwe, n’olwekyo temwetukuza anti yye yaasinga okumanya ani asinga okumutya.');
INSERT INTO "african_dev" VALUES(53,33,'53:33','Abaffe olabye oyo akyuka (naava ku mazima).');
INSERT INTO "african_dev" VALUES(53,34,'53:34','Era nawaayo katono bwamala ate nammira ddala.');
INSERT INTO "african_dev" VALUES(53,35,'53:35','Abaffe (ekimukozesa ekyo) lwakuba nti alina okumanya ebyekusifu, olwo nno naaba nga abiraba.');
INSERT INTO "african_dev" VALUES(53,36,'53:36','Oba tabuulirwanga ebyo ebiri mu bitabo bya Musa.');
INSERT INTO "african_dev" VALUES(53,37,'53:37','Ne Ibrahim oyo eyatuukiriza (bwe yalaganyisa Katonda).');
INSERT INTO "african_dev" VALUES(53,38,'53:38','(Ebyo ebigamba nti) tewali mwoyo gulyetikka kibi kya mulala.');
INSERT INTO "african_dev" VALUES(53,39,'53:39','Era nti omunu tagenda kufuna okugyako ekyo kye yakolerera.');
INSERT INTO "african_dev" VALUES(53,40,'53:40','Era nti mazima okukola kwe kya ddaaki kulirabwa.');
INSERT INTO "african_dev" VALUES(53,41,'53:41','Oluvanyuma alikusasulwamu empeera esinga okuba enzijuvu.');
INSERT INTO "african_dev" VALUES(53,42,'53:42','Era nti mazima ewa Mukama omulabirizi wo yeeri enkomerero (ya buli kimu).');
INSERT INTO "african_dev" VALUES(53,43,'53:43','Era nti mazima yye yaasesa era yaakaabya.');
INSERT INTO "african_dev" VALUES(53,44,'53:44','Era nti mazima yye yatta era yaawa obulamu.');
INSERT INTO "african_dev" VALUES(53,45,'53:45','Era nti mazima yatonda emitindo ebiri omusajja n''omukazi.');
INSERT INTO "african_dev" VALUES(53,46,'53:46','Abagya mu mazzi agazaala bwe gafulumizibwa (negateekebwa mu kifo ekituufu).');
INSERT INTO "african_dev" VALUES(53,47,'53:47','Era nti mazima kuli ku ye yekka okusibusa okulala (okuzuukiza).');
INSERT INTO "african_dev" VALUES(53,48,'53:48','Era nti mazima yye ya gaggawaza era naayavuwaza.');
INSERT INTO "african_dev" VALUES(53,49,'53:49','Era nti mazima yye ye Mukama omulabirizi we munyeenye eyitibwa Shiiraa (abakafiiri gye basinzanga).');
INSERT INTO "african_dev" VALUES(53,50,'53:50','Era nti mazima yye yazikiriza abe kibiina kya A''adi abasooka.');
INSERT INTO "african_dev" VALUES(53,51,'53:51','Na be kibiina kya Thamud era nga teyalekawo (wadde omu).');
INSERT INTO "african_dev" VALUES(53,52,'53:52','Na bantu ba Nuhu mu kusooka anti mazima bo be baali basinga okweyisa obubi n''okubula.');
INSERT INTO "african_dev" VALUES(53,53,'53:53','Era ye yae yazikiriza abantu ba be kitundu ekyavuunikibwa (Sodoma ne gomora).');
INSERT INTO "african_dev" VALUES(53,54,'53:54','Olwo nno ne kikibuutikira ekya kibuutikira.');
INSERT INTO "african_dev" VALUES(53,55,'53:55','Kale olwo kiriwa ku byengera bya Mukama omulabiri wo kyobuusabuusa!.');
INSERT INTO "african_dev" VALUES(53,56,'53:56','Ono (omubaka Muhammad) mutiisa ali mu luse lwa batiisa abaakulembera.');
INSERT INTO "african_dev" VALUES(53,57,'53:57','Enkomerero esembedde.');
INSERT INTO "african_dev" VALUES(53,58,'53:58','Tewali ayinza kugizzaayo bweriba ezze mpozzi Katonda.');
INSERT INTO "african_dev" VALUES(53,59,'53:59','Abaffe ebigambo bino bye mujereegerera.');
INSERT INTO "african_dev" VALUES(53,60,'53:60','Nemuba nga museka nemutakaaba.');
INSERT INTO "african_dev" VALUES(53,61,'53:61','Nga nammwe muzannya buzannyi.');
INSERT INTO "african_dev" VALUES(53,62,'53:62','Kale muvunname ku lwa Katonda era mumusinze (ekyo kye kisinga ebya mmwe bye mulimu).');
INSERT INTO "african_dev" VALUES(54,1,'54:1','Enkomerero esembedde n''omwezi ne gubejjukamu.');
INSERT INTO "african_dev" VALUES(54,2,'54:2','Bwe balaba ekya magero bakyawukanamu ne bagamba nti lino ddogo eritakoma.');
INSERT INTO "african_dev" VALUES(54,3,'54:3','Olwo nno ne balimbisa era ne bagoberera okwagala kwabwe, so nga buli kigambo kirina engeri gye kirina okubaamu (nungi oba mbi).');
INSERT INTO "african_dev" VALUES(54,4,'54:4','Ate nga ddala gaabatuukako amawulire ago agaalimu okukanga (okwandibamaze ne batya Katonda).');
INSERT INTO "african_dev" VALUES(54,5,'54:5','(Kur’ani eno) ejjudde amagezi etuukira ddala wala omuntu, (ekyekitalo) okutiisa kwokka tekumala (kwetagisaako okulungamya kwa Katonda).');
INSERT INTO "african_dev" VALUES(54,6,'54:6','Kale baveeko (olindirire) olunaku omukowooze lwalikowoola (abantu) kugenda eri ekintu ekitayagalwa.');
INSERT INTO "african_dev" VALUES(54,7,'54:7','Amaaso gaabwe nga gatunudde wansi, baliva mu kabburi nga balinga ensenene ezisasaanye.');
INSERT INTO "african_dev" VALUES(54,8,'54:8','Balisaayirira okugenda eri omukowooze nga abakafiiri bwe bagamba nti, luno lunaku luzito.');
INSERT INTO "african_dev" VALUES(54,9,'54:9','Oluberyeberye lwabwe, abantu ba Nuhu balimbisa, olwo nno nebalimbisa omuddu waffe ne bagamba mbu muguwaddalu ate naatiisibwatiisibwa.');
INSERT INTO "african_dev" VALUES(54,10,'54:10','Kyeyava asaba Mukama omulabirizi we nti mazima nze nemerdeddwa n’olwekyo nziruukirira.');
INSERT INTO "african_dev" VALUES(54,11,'54:11','Olwo nno netuggulawo emiryango gya waggulu ne tumuwa amazzi agafukumuka.');
INSERT INTO "african_dev" VALUES(54,12,'54:12','Era ettaka netulifukumulamu ensulo amazzi (ag’emirundi ebiri) ne gasisinkana okusinziira ku kiragiro ekyagerwa edda.');
INSERT INTO "african_dev" VALUES(54,13,'54:13','Netumusitulira na bonna abali naye mu lyato eryakolwa mu mbawo ne misumaali.');
INSERT INTO "african_dev" VALUES(54,14,'54:14','Nga liddukira (ku mazzi ago) ku lwa buyinza bwaffe, ekyo nga kusasula eri oyo eyali awakanyizibwa.');
INSERT INTO "african_dev" VALUES(54,15,'54:15','Mazima (ekyafaayo ekyo) twakireka nga kya kuyiga, abaffe waliwo eyeebuliridde?.');
INSERT INTO "african_dev" VALUES(54,16,'54:16','(Nebalaba nti) ebibonerezo byange n’okutiisa byali bitya!.');
INSERT INTO "african_dev" VALUES(54,17,'54:17','Mazima Kur’ani twagifuula nyangu ebeere nga ebuulirira, abaffe waliyo eyebuuliridde.');
INSERT INTO "african_dev" VALUES(54,18,'54:18','Abeekika kya Aadi baalimbisa naye ebibonerezo byange n’okutiisa byali bitya.');
INSERT INTO "african_dev" VALUES(54,19,'54:19','Mazima ffe twabasindikira kikungunta omukambwe, mu lunaku olwe kikwa ekitaasalako olunaku lwonna.');
INSERT INTO "african_dev" VALUES(54,20,'54:20','Eyasikulanga abantu (okuva ku ttaka) nga bagwa nga ebikonge by''emitende ebisiguddwa.');
INSERT INTO "african_dev" VALUES(54,21,'54:21','Naye ebibonerezo byange n’okutiisa byali bitya.');
INSERT INTO "african_dev" VALUES(54,22,'54:22','Mazima Kur’ani twagifuula nyangu ebeere nga ebuulirira, abaffe waliyo eyebuuliridde.');
INSERT INTO "african_dev" VALUES(54,23,'54:23','Abeekika kya Thamud baalimbisa okutiisa kwa (Nabbi waabwe Swaleh).');
INSERT INTO "african_dev" VALUES(54,24,'54:24','Olwo nno ne bagamba nti abaffe omuntu obuntu omu mu ffe, gwetuba tugoberera (netumala tukikola) olwo nno mazima ffe tujja kuba mu bubuze na bugwi bwa ddalu.');
INSERT INTO "african_dev" VALUES(54,25,'54:25','Mu ffe fenna ye yaweereddwa obubaka! Nedda ssi bwekiri yye mulimba alina kyalubirira.');
INSERT INTO "african_dev" VALUES(54,26,'54:26','Enkya bajja kumanya ani mulimba omwerazi.');
INSERT INTO "african_dev" VALUES(54,27,'54:27','(Anti) mazima ffe tujja kuweereza engamiya nga kikemo gye bali, olwo nno batunuulire nkaliriza era ogumikirize (ku nneeyisa yaabwe embi).');
INSERT INTO "african_dev" VALUES(54,28,'54:28','Era bategeeze nti (okuva engamiya lwezze) ddala amazzi ga kugabanyizibwa wakati waabwe nayo, nga buli luwalo lutuukirizibwa, (nyinirwo yaalina okufuna amazzi keebe ngamiya oba bantu).');
INSERT INTO "african_dev" VALUES(54,29,'54:29','Olwo nno baakowoola mu nnaabwe neyeesowolayo naafumita (engamiya).');
INSERT INTO "african_dev" VALUES(54,30,'54:30','Naye ebibonerezo byange n’okutiisa byali bitya.');
INSERT INTO "african_dev" VALUES(54,31,'54:31','(Anti) mazima twabasindikira olubwatuka lumu, olwo nno ne baba nga ebisubi ebigwa okuva ku lugo (lwo muntu agenderera okukuuma ebisolo bye).');
INSERT INTO "african_dev" VALUES(54,32,'54:32','Mazima Kur’ani twagifuula nyangu, ebeere nga ebuulirira abaffe waliyo eyebuuliridde.');
INSERT INTO "african_dev" VALUES(54,33,'54:33','Abantu ba Luutu baalimbisa obubaka.');
INSERT INTO "african_dev" VALUES(54,34,'54:34','Mazima ffe twabasindikira kibuyaga owa mayinja (eyabasanyaawo bonna) okugyako abantu b’o mu maka ga Luutu, twabawonya mu ttumbi.');
INSERT INTO "african_dev" VALUES(54,35,'54:35','Ekyo kyengera okuva gye tuli bwe tutyo nno, bwe tusasula oyo eyeebaza.');
INSERT INTO "african_dev" VALUES(54,36,'54:36','Mazima (Luutu) yabeekesa okukwata kwaffe wabula nebabuusabuusa okulabula (kwe).');
INSERT INTO "african_dev" VALUES(54,37,'54:37','Mazima baagezaako okumusendasenda abawe abagenyi be (babasiyage) olwo nno netuziba amaaso gaabwe, ne bakomba ku bibonerezo byange n''okulabula kwange.');
INSERT INTO "african_dev" VALUES(54,38,'54:38','Era mazima ebibonerezo ebitasalako bya bazinda mu matulutulu.');
INSERT INTO "african_dev" VALUES(54,39,'54:39','Olwo nno nebakomba ku bibonerezo byange n’okulabula kwange.');
INSERT INTO "african_dev" VALUES(54,40,'54:40','Mazima Kur’ani twagifuula nyangu ebeere nga ebuuririra abaffe waliyo eyebuuliridde.');
INSERT INTO "african_dev" VALUES(54,41,'54:41','Mazima okulabula kwajjira abantu ba Firawo.');
INSERT INTO "african_dev" VALUES(54,42,'54:42','Baalimbisa ebigambo byaffe byonna, olwo nno netubakwata enkwata ya Nantakubwa ku mukono, nannyini buyinza.');
INSERT INTO "african_dev" VALUES(54,43,'54:43','Abakafiiri ba mmwe be balungi okusinga abo! oba (ekibayinula) mulina akalagaane ku butavunaanwa nga kawandiikibwa mu bitabo ebitukuvu ebyasooka.');
INSERT INTO "african_dev" VALUES(54,44,'54:44','Oba (ekibayinula lwakuba) bagamba nti ffe tuli bangi nga twegasse tusobola okwetaasa.');
INSERT INTO "african_dev" VALUES(54,45,'54:45','(Zibasanze) ekibiina (ekyo) kijja kuwangulwa era bakubeyo amabega (badduke).');
INSERT INTO "african_dev" VALUES(54,46,'54:46','(Byonna bye baliko temuli) wabula olunaku lw’enkomerero ye ndagaano yaabwe era olunaku lw’enkomerero lwelusinga obukalubo n’obukaawu.');
INSERT INTO "african_dev" VALUES(54,47,'54:47','Mazima abonoonyi bali mu bubuze na bugwi bwa ddalu.');
INSERT INTO "african_dev" VALUES(54,48,'54:48','(Bandibadde bajjukira) olunaku lwe baliwalulwa ku byenyi byabwe mu muliro (nga bwe bagambibwa nti) mukombe ku bulumi bw’omuliro Sakara.');
INSERT INTO "african_dev" VALUES(54,49,'54:49','Mazima ffe buli kintu twakitonda mu ngeri nga bwe kisaana okuba.');
INSERT INTO "african_dev" VALUES(54,50,'54:50','(Ate mu kukola ebintu byonna) ekiragiro kyaffe tekiri okugyako kimu nga kiringa lutemya lwa liiso.');
INSERT INTO "african_dev" VALUES(54,51,'54:51','Era ddala twazikiriza abafaanana nga mmwe, abaffe waliyo eyebuuliridde.');
INSERT INTO "african_dev" VALUES(54,52,'54:52','Na buli kintu kyonna kye baakola kiri mu kitabo.');
INSERT INTO "african_dev" VALUES(54,53,'54:53','Era buli kitono n''ekinene kyawandiikibwa.');
INSERT INTO "african_dev" VALUES(54,54,'54:54','Mazima abatya Katonda balibeera mu jjana n’emigga (egiriba gikulukutira wansi waazo).');
INSERT INTO "african_dev" VALUES(54,55,'54:55','Mu kifo ekya mazima ew’omufuzi nannyini buyinza.');
INSERT INTO "african_dev" VALUES(55,1,'55:1','(Katonda) omusaasizi ennyo.');
INSERT INTO "african_dev" VALUES(55,2,'55:2','Yayigiriza (omuntu) Kur’ani.');
INSERT INTO "african_dev" VALUES(55,3,'55:3','Yatonda omuntu.');
INSERT INTO "african_dev" VALUES(55,4,'55:4','Yamuyizigiriza okunnyonnyola.');
INSERT INTO "african_dev" VALUES(55,5,'55:5','Enjuba n’omwezi bitambulira ku nteekateeka (eyabiweebwa).');
INSERT INTO "african_dev" VALUES(55,6,'55:6','Ebiddo n’emiti (bivunnama nga bisinza Katonda).');
INSERT INTO "african_dev" VALUES(55,7,'55:7','Era eggulu yalisitula era nassaawo obwenkanya.');
INSERT INTO "african_dev" VALUES(55,8,'55:8','Mube nga temusukka ebipimo (mu kutuukiriza obwenkanya).');
INSERT INTO "african_dev" VALUES(55,9,'55:9','Era muyirizeewo ebipimo mu bwenkanya era temukendezanga minzane.');
INSERT INTO "african_dev" VALUES(55,10,'55:10','N''ensi yagiteerawo ebitonde.');
INSERT INTO "african_dev" VALUES(55,11,'55:11','Nga erimu ebibalo n’emitende egirina ebirimba.');
INSERT INTO "african_dev" VALUES(55,12,'55:12','N''ebimera ebye mpeke eziri mu bikuta n’ebimera ebya kawoowo.');
INSERT INTO "african_dev" VALUES(55,13,'55:13','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,14,'55:14','Yatonda omuntu mu bumba eggumu eriringa ejjokye.');
INSERT INTO "african_dev" VALUES(55,15,'55:15','Ate naatonda amajinni mu bitawuliro by’omuliro.');
INSERT INTO "african_dev" VALUES(55,16,'55:16','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,17,'55:17','Mukama omulabirizi w’o buvanjuba bwombi, era Mukama omulabirizi w''obugwanjuba bwombi.');
