INSERT INTO "african_dev" VALUES(55,19,'55:19','Yagatta enyanja ebbiri (ez’amazzi agenjawulo aganyweka na gatanyweka) zesisinkana.');
INSERT INTO "african_dev" VALUES(55,20,'55:20','Wakati wa zombi waliwo ekyawula, tezigenda kwetabula.');
INSERT INTO "african_dev" VALUES(55,21,'55:21','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,22,'55:22','Ate nga mu zombi mwe muva Luulu ne Marijaani (amayinja ag’omuwendo).');
INSERT INTO "african_dev" VALUES(55,23,'55:23','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,24,'55:24','Era gatambula ku lwo buyinza bwe amaato agakolwa ku lwo kutambulira ku nyanja nga galinga ensozi.');
INSERT INTO "african_dev" VALUES(55,25,'55:25','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,26,'55:26','Buli kyonna ekigiriko (ensi) kya kuggwawo.');
INSERT INTO "african_dev" VALUES(55,27,'55:27','Olwo nno wasigalewo obwennyini bwa Mukama omulabirizi wo, nannyini kugulumizibwa era nannyini kitiibwa.');
