INSERT INTO "african_dev" VALUES(55,29,'55:29','Bimusaba ebyo ebiri mu ggulu omusanvu n''ensi (naye nga) buli lunaku aba ku ngeri ndala (mu kugera kwe).');
INSERT INTO "african_dev" VALUES(55,30,'55:30','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,31,'55:31','Tujja kubateerawo ekiseera abange mmwe ebitonde ebizito ebibiri (amajinni n’abantu, ekiseera ekyo mwe tulibalira buli omu).');
INSERT INTO "african_dev" VALUES(55,32,'55:32','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,33,'55:33','Abange mmwe ab''oluse lwa majinni n’oluse lwa bantu, bwe muba musobola okuyita mu bitundu bye ggulu omusanvu n''ensi (mube nga mudduka ku Katonda) kale muyitemu (naye) temuyinza kuyitamu okugyako nga mulina obusobozi.');
INSERT INTO "african_dev" VALUES(55,34,'55:34','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,35,'55:35','Musindikirwa mwe ab''emirundi ebiri (amajinni n’abantu) ebitawuliro byomuliro, n’ekikomo ekisaanuuse (naye temusobola kwetaasa).');
INSERT INTO "african_dev" VALUES(55,36,'55:36','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,37,'55:37','Olwo nno eggulu bwe liribajjuka nerirabika nga ekimuli ekimyufu (olwamaanyi g’omuliro) neriba nga omuzigo omusanuuse.');
INSERT INTO "african_dev" VALUES(55,38,'55:38','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,39,'55:39','Ku lunaku olwo ekibi omuntu (kye yakola) tekigenda kubuuzibwa muntu mulala oba ejinni.');
INSERT INTO "african_dev" VALUES(55,40,'55:40','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,41,'55:41','Abonoonyi balimanyibwa olwobubonero bwabwe, olwo nno nebakwatibwa ku nviiri z’o mu buwompo n’ebigere (nebamenyebwamu nebakasukwa mu muliro).');
INSERT INTO "african_dev" VALUES(55,42,'55:42','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,43,'55:43','Baligamba nti guno gwe muliro Jahannama abonoonyi gwe baali balimbisa.');
INSERT INTO "african_dev" VALUES(55,44,'55:44','Bagenda kwetoloolera wakati waagwo ne wakati w''olwegye oluyitirivu lwokwokya.');
