INSERT INTO "african_dev" VALUES(55,46,'55:46','Era oyo eyatya okuyimirira mu kifo kya Mukama omulabirizi we alina e jjana bbiri.');
INSERT INTO "african_dev" VALUES(55,47,'55:47','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
