INSERT INTO "african_dev" VALUES(55,50,'55:50','Mu zombi (e jjana) mulimu ensulo bbiri ezikulukuta.');
INSERT INTO "african_dev" VALUES(55,51,'55:51','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
