INSERT INTO "african_dev" VALUES(55,53,'55:53','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,54,'55:54','(Abalibeeramu) baliba bagalamidde ku byaliiro, nga munda mwabyo mulimu Liiri omuzito, nga ne nimiro za jjana zombi ebibala byamwo bireebeeta (byangu bya kulaba era byangu bya kunoga).');
INSERT INTO "african_dev" VALUES(55,55,'55:55','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,56,'55:56','Mu zo (e jjana) mulimu abakyala abamaaso amakkakkamu (embeerera) nga tebakwatwangako muntu yenna wadde ejinni oluberyeberye lwabwe (abo abaliba babaweereddwa mu jjana).');
INSERT INTO "african_dev" VALUES(55,57,'55:57','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,58,'55:58','(Olwo bulungi bwabwe) baliba nga Yaakut ne Marijaan (amayinja ag’omuwendo).');
INSERT INTO "african_dev" VALUES(55,59,'55:59','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,60,'55:60','Abaffe empera y’okukola obulungi (eyinza okuba ekintu ekirala kyonna) okugyako okusasulwa obulungi.');
