INSERT INTO "african_dev" VALUES(55,74,'55:74','Nga tebakwatwangako muntu yenna wadde ejinni oluberyeberye lwabwe, (abo abaliba babaweereddwa mu jjana).');
INSERT INTO "african_dev" VALUES(55,75,'55:75','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,76,'55:76','Baliba besigamye ku mitto egiriko ebibikka ebya kiragala ne byaliiro ebirungi ennyo.');
INSERT INTO "african_dev" VALUES(55,77,'55:77','Kaakano kiriwa kye mulimbisa ku byengera bya Mukama omulabirizi wa mmwe (mmwe ab’emirundi ebiri abantu na majinni).');
INSERT INTO "african_dev" VALUES(55,78,'55:78','Sukkulumu erinnya lya Mukama omulabirizi wo nannyini kugulumizibwa n’okuweebwa ekitiibwa.');
INSERT INTO "african_dev" VALUES(56,1,'56:1','Ekiseera ekitaliimu kubuusabuusa bwe kirituuka.');
INSERT INTO "african_dev" VALUES(56,2,'56:2','Okutuuka kwakyo tekuliimu kubuusabuusa.');
INSERT INTO "african_dev" VALUES(56,3,'56:3','Kirikakkanya (abeeyisa obubi era) kirisitula (abeeyisa obulungi).');
INSERT INTO "african_dev" VALUES(56,4,'56:4','Ensi bwe rikankanyizibwa olukankana.');
INSERT INTO "african_dev" VALUES(56,5,'56:5','N''ensozi nezimerengulwa olumerengulwa.');
INSERT INTO "african_dev" VALUES(56,6,'56:6','Olwo nno nezifuuka enfuufu efumuuka.');
INSERT INTO "african_dev" VALUES(56,7,'56:7','Era mmwe nemuba emitendera esatu.');
INSERT INTO "african_dev" VALUES(56,8,'56:8','Olwo nno newabaawo ogwa ba nannyini mukono ogwa ddyo, yye omanyi okubeera ku mukono ogwaddyo kye kitegeeza?.');
INSERT INTO "african_dev" VALUES(56,9,'56:9','Era newabaawo n’ogwa ba nannyini mukono ogwa kkono, yye omanyi okubeera ku mukono ogwa kkono kye kitegeeza?.');
INSERT INTO "african_dev" VALUES(56,10,'56:10','Newabaawo n’ogwabo abaakulembera, abalikulembera mu buli kimu.');
INSERT INTO "african_dev" VALUES(56,11,'56:11','Abo be bo kusembezebwa (ewa Katonda).');
INSERT INTO "african_dev" VALUES(56,12,'56:12','Nga bali mu jjana ez''ebyengera.');
INSERT INTO "african_dev" VALUES(56,13,'56:13','Bangi mu bibiina ebyasooka.');
INSERT INTO "african_dev" VALUES(56,14,'56:14','Nabatono mu kibiina kya bantu abenkomerero (abekibiina kya Nabbi Muhammad).');
INSERT INTO "african_dev" VALUES(56,15,'56:15','(Balibeera) ku bitanda ebyakolebwa mu zzaabu n''amayinja amalala agomuwendo.');
INSERT INTO "african_dev" VALUES(56,16,'56:16','Baliba bagalamidde ku byo nga boolekaganye (nga banyumya).');
INSERT INTO "african_dev" VALUES(56,17,'56:17','Abaweereza abalenzi, abolubeerera baliba babayitamu.');
INSERT INTO "african_dev" VALUES(56,18,'56:18','Nga balina ebikopo, amabinika ne giraasi ebijjudde omubisi oguva mu nsulo.');
INSERT INTO "african_dev" VALUES(56,19,'56:19','Tebagenda kulumwa mutwe olwagwo era tebagenda kutamiira.');
INSERT INTO "african_dev" VALUES(56,20,'56:20','N’ebibala (ebye njawulo) mwebyo bye baliba nga basazeewo.');
INSERT INTO "african_dev" VALUES(56,21,'56:21','Ne nnyama y’ebinyonyi eyo gye baliyagala.');
INSERT INTO "african_dev" VALUES(56,22,'56:22','N''abakyala abalungi abamaaso agatemagana.');
INSERT INTO "african_dev" VALUES(56,23,'56:23','Nga balinga Luulu akuumibwa obutiribiri.');
INSERT INTO "african_dev" VALUES(56,24,'56:24','(Ebyo byonna) nga mpeera yeebyo bye baali bakola (ebirungi ku nsi).');
INSERT INTO "african_dev" VALUES(56,25,'56:25','Tebagenda kuwulira mu yo luyogaano wadde ebigambo ebyesittaza.');
INSERT INTO "african_dev" VALUES(56,26,'56:26','Okugyako okugamba nti mirembe, mirembe.');
INSERT INTO "african_dev" VALUES(56,27,'56:27','Abantu bo ku mukono ogwa ddyo, abaffe omanyi abantu bo ku mukono ogwa ddyo baliba batya?.');
INSERT INTO "african_dev" VALUES(56,28,'56:28','Balituula (mu bittuluze) by''emiti gya Sidiri atemeddwako amaggwa.');
INSERT INTO "african_dev" VALUES(56,29,'56:29','N''ensuku ezirimu amatooke agaliko ebiwagu ebiberekaganye.');
INSERT INTO "african_dev" VALUES(56,30,'56:30','N''ebisikirize ebigazi.');
INSERT INTO "african_dev" VALUES(56,31,'56:31','N''amazzi agakulukuta awatali kusalako.');
INSERT INTO "african_dev" VALUES(56,32,'56:32','N''ebibala ebingi.');
INSERT INTO "african_dev" VALUES(56,33,'56:33','Ebitaggwawo ate ebitatekebwako kkomo.');
INSERT INTO "african_dev" VALUES(56,34,'56:34','N''ebyaliiro ebisuffu ebya waggulu.');
INSERT INTO "african_dev" VALUES(56,35,'56:35','Mazima ffe twatonda abakyala bo mu jjana olutonda.');
INSERT INTO "african_dev" VALUES(56,36,'56:36','Olwo nno twabatonda nga mbeerera.');
INSERT INTO "african_dev" VALUES(56,37,'56:37','Nga bulijjo baganzi eri ba bbaabwe nga tebakakiddwa.');
INSERT INTO "african_dev" VALUES(56,38,'56:38','Nga bo ba bantu abalibeera ku mukono ogwa ddyo.');
INSERT INTO "african_dev" VALUES(56,39,'56:39','Bangi mu bibiina ebyasooka.');
INSERT INTO "african_dev" VALUES(56,40,'56:40','Era bangi mu kibiina kya bantu ab''enkomerero (ab’ekibiina kya Nabbi Muhammad).');
INSERT INTO "african_dev" VALUES(56,41,'56:41','Ate abantu bo ku mukono ogwa kkono, abaffe omanyi abantu bo ku mukono ogwa kkono baliba batya?.');
INSERT INTO "african_dev" VALUES(56,42,'56:42','Balibeera mu kibuyaga ayokya n’amazzi ageeseze.');
INSERT INTO "african_dev" VALUES(56,43,'56:43','Nga nebisikirize mwe balyewogoma, biriba bya mu mukka omuddugavu ogwokya.');
INSERT INTO "african_dev" VALUES(56,44,'56:44','Biriba tebinnyogoga era nga tebisanyusa.');
INSERT INTO "african_dev" VALUES(56,45,'56:45','Anti mazima bo oluberyeberye lwa kino baali baamalibwawo ebyo kwe jalabya.');
INSERT INTO "african_dev" VALUES(56,46,'56:46','Era baalemeranga ku kibi ekinene (bwe baalayiranga nti tewali kuzuukira).');
INSERT INTO "african_dev" VALUES(56,47,'56:47','Era baagamba nga nti bwetufa netufuuka ettaka, era netusigala ngumba gumba abaffe tulizuukizibwa!.');
INSERT INTO "african_dev" VALUES(56,48,'56:48','Ne bakadde baffe abasooka (nabo bwe batyo)!.');
INSERT INTO "african_dev" VALUES(56,49,'56:49','Bagambe ggwe (Nabbi Muhammad) nti mazima abasooka n''aboluvanyuma.');
INSERT INTO "african_dev" VALUES(56,50,'56:50','Ddala ba kukunganyizibwa ku lunaku olwasalibwawo olumanyiddwa.');
INSERT INTO "african_dev" VALUES(56,51,'56:51','Oluvanyuma mazima mmwe, abange mmwe ababuze abalimbisa.');
INSERT INTO "african_dev" VALUES(56,52,'56:52','Ddala mugenda kulya ku muti gwa zakkumu.');
INSERT INTO "african_dev" VALUES(56,53,'56:53','Nulijjuza nagwo embuto (gujjuze embuto zammwe).');
INSERT INTO "african_dev" VALUES(56,54,'56:54','Olwo nno munywereko amazzi ageeseze.');
INSERT INTO "african_dev" VALUES(56,55,'56:55','Mugenda kunywa ng''ennywa ye ngamiya erina ennyonta.');
INSERT INTO "african_dev" VALUES(56,56,'56:56','Obwo bwe buliba obugenyi bwabwe ku lunaku lwo kusasula.');
INSERT INTO "african_dev" VALUES(56,57,'56:57','Ffe twabatonda, kale singa mukkiriza (okusinziira kwekyo).');
INSERT INTO "african_dev" VALUES(56,58,'56:58','Abaffe mulaba amazzi (agazaala) ge muteeka mu nnabaana.');
INSERT INTO "african_dev" VALUES(56,59,'56:59','Abaffe mmwe mugatonda oba ffe batonzi!.');
INSERT INTO "african_dev" VALUES(56,60,'56:60','Ffe twagera netussa wakati wa mmwe okufa, (netubakakasaako okufa) era tetuli ffe ba kulemesebwa.');
INSERT INTO "african_dev" VALUES(56,61,'56:61','Okuba nga tuwaanyisa abalinga mmwe (mu mmwe) olwo nno mmwe, netubakolamu ebirala bye mutamanyi.');
INSERT INTO "african_dev" VALUES(56,62,'56:62','Ate nga ddala mwamanya entandikwa eyasooka (engeri Katonda gye yabatondamu) kale singa mwebulirira.');
INSERT INTO "african_dev" VALUES(56,63,'56:63','Abaffe mulaba ebyo bye mulima.');
INSERT INTO "african_dev" VALUES(56,64,'56:64','Abaffe mmwe mubimeza oba ffe tumeza!.');
INSERT INTO "african_dev" VALUES(56,65,'56:65','Singa twagadde tusobola okubifuula ebikaze nemusigala nga mwewunaganya.');
INSERT INTO "african_dev" VALUES(56,66,'56:66','(Nga mugamba nti) mazima ffe tetufunye ate nga tubanjibwa.');
INSERT INTO "african_dev" VALUES(56,67,'56:67','(Si kye kyokka) wabula ffe tummiddwa.');
INSERT INTO "african_dev" VALUES(56,68,'56:68','Abaffe mulaba amazzi ago gemunywa.');
INSERT INTO "african_dev" VALUES(56,69,'56:69','Mwe mugassa okuva mu bire oba ffe tugassa.');
INSERT INTO "african_dev" VALUES(56,70,'56:70','Singa twagadde twaligafudde olukalabule kale singa mwebaza (Katonda olwo kugafuula amalungi).');
INSERT INTO "african_dev" VALUES(56,71,'56:71','Abaffe mulaba omuliro ogwo gwe mukuma.');
INSERT INTO "african_dev" VALUES(56,72,'56:72','Abaffe mmwe mwatandikawo omuti gwagwo, oba ffe twagutandika.');
INSERT INTO "african_dev" VALUES(56,73,'56:73','Ffe twagufuula kya kujjukiza, era nga gwa mugaso eri abatambuze (abataba na byoto bibakumira muliro).');
INSERT INTO "african_dev" VALUES(56,74,'56:74','Kale tendereza erinnya lya Mukama omulabirizi wo owekitiibwa.');
INSERT INTO "african_dev" VALUES(56,75,'56:75','Tewali kingaana kulayira bifo bya munyeenye.');
INSERT INTO "african_dev" VALUES(56,76,'56:76','Era mazima kwo ddala kulayira kunene singa mubadde mumanyi.');
INSERT INTO "african_dev" VALUES(56,77,'56:77','Okuba nti ddala yo Kur’ani yakitiibwa.');
INSERT INTO "african_dev" VALUES(56,78,'56:78','Eri mu kitabo ekikuumibwa obutiribiri (ku Lawuhu al mahfudhu)');
INSERT INTO "african_dev" VALUES(56,79,'56:79','Takikwatako okugyako abaatukuzibwa (ba malayika abatalina bukyafu bwa mibiri oba obwe nzikiriza).');
INSERT INTO "african_dev" VALUES(56,80,'56:80','Essibwa okuva ewa Mukama omulabirizi w’ebitonde.');
INSERT INTO "african_dev" VALUES(56,81,'56:81','Abaffe ebigambo bino (ebya Kur’ani) mmwe bye mugayaalirira.');
INSERT INTO "african_dev" VALUES(56,82,'56:82','Okugirimbisa mmwe ne mukifuula okuba nga ye Riziki yammwe.');
INSERT INTO "african_dev" VALUES(56,83,'56:83','Singa omwoyo wegutuukira mu ddokooli (musala amagezi nemugutaasa).');
INSERT INTO "african_dev" VALUES(56,84,'56:84','Ate nga mmwe mu kiseera ekyo mubaawo nga mutunula.');
INSERT INTO "african_dev" VALUES(56,85,'56:85','Era ffe tuba okumpi naye okusinga mmwe, naye temulaba.');
INSERT INTO "african_dev" VALUES(56,86,'56:86','Kale singa mwali temufugibwa (Katonda).');
INSERT INTO "african_dev" VALUES(56,87,'56:87','Muyinza okugamba nti mwandiguzizzaamu mu mubiri, bwe muba mwogera mazima.');
INSERT INTO "african_dev" VALUES(56,88,'56:88','Naye (omufu) bwaba mu bo kumpi (ne Katonda).');
INSERT INTO "african_dev" VALUES(56,89,'56:89','Aliba mu kwesiima na mirembe na jjana eye byengera.');
INSERT INTO "african_dev" VALUES(56,90,'56:90','Ate bwaba mu bantu bo ku mukono ogwa ddyo.');
INSERT INTO "african_dev" VALUES(56,91,'56:91','(Aliyozebwayozebwa nti) emirembe gibeere ku ggwe, olwokuba oli mu bantu bo ku mukono ogwa ddyo.');
INSERT INTO "african_dev" VALUES(56,92,'56:92','Wabula bwabeera nga ali mu balimbisa ababuze.');
INSERT INTO "african_dev" VALUES(56,93,'56:93','(Oyo nno) obugenyi (bwe) bwalwegye lwa mazzi.');
INSERT INTO "african_dev" VALUES(56,94,'56:94','Nakwesogga muliro Jahiimu.');
INSERT INTO "african_dev" VALUES(56,95,'56:95','Mazima ebyo ge mazima agannamaddala.');
INSERT INTO "african_dev" VALUES(56,96,'56:96','Kale nno tendereza erinnya lya Mukama omulabirizi wo owekitiibwa.');
INSERT INTO "african_dev" VALUES(57,1,'57:1','Byonna ebiri mu ggulu omusanvu ne nsi bitendereza Katonda, era yye ye nnantakubwa ku mukono mugoba nsonga.');
INSERT INTO "african_dev" VALUES(57,2,'57:2','Bubwe yekka obufuzi bwe ggulu omusanvu n''ensi,awa obulamu era natta, era yemuyinza ku buli kintu.');
INSERT INTO "african_dev" VALUES(57,3,'57:3','Yye ye yasooka era ow''enkomerero, era yye yaali waggulu wa buli kintu, era yaasooka wansi, nga wansii we tewali kintu kirala, era yye amanyi ebifa ku buli kintu.');
INSERT INTO "african_dev" VALUES(57,4,'57:4','Yye yooyo eyatonda eggulu omusanvu n''ensi mu nnaku omukaaga, oluvanyuma yatebenkera ku Arish, amanyi ebikka ku nsi nebigifuluma n''ebyo ebikka okuva mu ggulu nebiryambukamu, era yye aba wamu na mmwe wonna wemuba, era Katonda alabira ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(57,5,'57:5','Bubwe yekka obufuzi bwe ggulu omusanvu n''ensi era eri Katonda ebintu byonna gye bizzibwa.');
INSERT INTO "african_dev" VALUES(57,6,'57:6','Abuliza ekiro mu musana, era naabuliza omusana mu kiro, era yye amanyidde ddala ebiri mu bifuba.');
INSERT INTO "african_dev" VALUES(57,7,'57:7','Mukkirize Katonda n’omubaka we, era mugabe kwebyo bya yabafuula abasigire mu byo, abo nno abakkiriza mu mmwe era ne bagaba, balina empeera ensuffu.');
INSERT INTO "african_dev" VALUES(57,8,'57:8','Mwabaaki obutakkiriza Katonda ng’ate omubaka abakowoola mube nga mukkiriza Mukama omulabirizi wa mmwe, ate nga yabakozesa endagaano (kwekyo), bwe muba abakkiriza (nga mukyali ku ndagaano gyeyabakozesa).');
INSERT INTO "african_dev" VALUES(57,9,'57:9','Yye yooyo assa ku mudduwe (Muhammad) ebigambo ebinnyonnyofu, abe nga abagya mu bizikiza mudde eri ekitangaala, era mazima Katonda mukwata mpola ku mmwe, musaasizi nnyo.');
INSERT INTO "african_dev" VALUES(57,10,'57:10','Mutya bwe mutayinza kuwaayo mu kkubo lya Katonda ng’ate obusika bwe ggulu omusanvu n''ensi bwa Katonda, oyo eyawaayo mu mmwe nga obuwanguzi (bwe Makkah) tebunnabaawo, era nalwana (tayinza) kwenkana ne yakikola oluvanyuma) bali (abasoose) be be ddaala erya waggulu okusingaabo abaawaayo oluvanyuma era ne balwana. Naye nga bonna Katonda yabalagaanyisa (okubasasula) ebirungi, Katonda amanyidde ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(57,11,'57:11','Ani oyo awola Katonda oluwola olulungi, olwo nno alumubalizeemu, era nga alina empeera eweesa ekitiibwa.');
INSERT INTO "african_dev" VALUES(57,12,'57:12','Olunaku lwoliraba abakkiriza abasajja n''abakkiriza abakazi, nga ekitangaala kyabwe kibali mu maaso gaabwe, ne ku mikono gyabwe egya ddyo nga ba Malayika babagamba nti ekyessanyu kya mmwe olwaleero ze jjana nga gikulukutira wansi waazo emigg, muli ba kuzibeeramu obugenderevu, ekyo kyo kwe kwesiima okusukkulumu.');
INSERT INTO "african_dev" VALUES(57,13,'57:13','(Fumitiriza) olunaku abananfusi abasajja na bannanfusi abakazi lwe baligamba abo abakkiriza, nti mutulinde tulabise ku kitangaala kya mmwe, walirangirirwa nti mudde emabega wa mmwe (gye muvudde) munoonye ekitangaala, olwo nno waliteekwa wakati waabwe ekikomera nga kiriko omulyango, nga munda waakyo (ku ludda eribeera abakkiriza) waliyo okusaasira, ate nga ebweru waakyo (ku ludda eribeera abananfusi) ebibonerezo gyebiriba bisibuka.');
INSERT INTO "african_dev" VALUES(57,14,'57:14','Balibakowoola (nga bagamba nti mutumma ekitangaala) abaffe tetwali na mmwe, baligamba nti wewaawo, naye mazima mmwe, mwesuula mu bikemo, ne mulindiriza, ne mubuusabuusa ,essuubi ekyavaamu neribagayaaza okutuusa ekigambo kya Katonda lwekyajja era ebigayaaza nebibajja ku Katonda.');
INSERT INTO "african_dev" VALUES(57,15,'57:15','Kale nno olwaleero temuli ba kuggibwako nunuzi, wadde okugiggya kwabo abakaafuwala (mwenna) obuddo bwa mmwe muliro, ggwo gwe gubagyamu, naye nga buddo bubi.');
INSERT INTO "african_dev" VALUES(57,16,'57:16','Abaffe ekiseera tekituuse eri abo abakkiriza okuba nga emitima gyabwe gigonda ku lwokwogera ku Katonda, ne ku lwebyo ebyamazima ebisse, baleme kuba nga abo abaweebwa ekitabo (Tauraati ne Injili) oluberyeberye, olwo nno ekiseera ekyayitawo ku bo (wakati waabwe ne ba Nabbi) nekiwanvuwa, emitima gyabwe negikakanyala, era bangi mu bo bonoonyi.');
INSERT INTO "african_dev" VALUES(57,17,'57:17','Mumanye nti mazima Katonda awa obulamu ettaka oluvanyuma lwokufa (okukala) kwalyo, mazima tubannyonnyodde ebigambo (byaffe) mube nga mutegeera.');
INSERT INTO "african_dev" VALUES(57,18,'57:18','Mazima abasajja abasaddaaka, na bakyala abasaddaaka, era ne bawola Katonda oluwola olulungi, (bye bawaayo) Katonda abibakubisizaamu, era balina empeera eyekitiibwa.');
INSERT INTO "african_dev" VALUES(57,19,'57:19','Era abo abakkiriza Katonda n''ababaka be, abo bbo be bakkiriza abannamaddala, na balafuubana mu kuweereza mu kkubo lya Katonda, balina empeera yaabwe n''ekitangaala kyabwe ewa Mukama omulabirizi waabwe, ate abo abakafuwala ne balimbisa ebigambo byaffe, abo be ba nannyini muliro.');
INSERT INTO "african_dev" VALUES(57,20,'57:20','Mumanye nti mazima obulamu bwensi muzannyo na binyumo, nakwewunda nakwewaana wakati wa mmwe, nakulaga bungi bwa mmaali na baana, (wabula ebyo tebisaana kubuza muntu) anti bifaananako enkuba (emeza ebirime) ebimera byabyo nebisanyusa abalimi, oluvanyuma nebikula, olwo nno noobiraba nga byengedde, oluvanyuma nebifuuka ebisusunku naye mu bulamu obwenkomerero waliyo ebibonerezo ebikakali. (nga era bwe waliyo) ekisonyiwo n’okusiima ebiva ewa Katonda. Obulamu bwensi tebulina kye buli okugyako okuba okweyagala okugayaaza obugayaaza.');
INSERT INTO "african_dev" VALUES(57,21,'57:21','Muvuganye nga mudda eri ekisonyiwo ekiva ewa Mukama omulabirizi wa mmwe (era nga mudda), eri e jjana obugazi bwayo bwenkana obugazi bwe ggulu ne nsi nga yategekerwa abo abakkiriza Katonda nababaka be. Ebyo bigabwa bya Katonda, abiwa oyo gwaba ayagadde, era Katonda ye nnyini bigabwa ebisuffu.');
INSERT INTO "african_dev" VALUES(57,22,'57:22','Tewali kabi kabaawo mu nsi, wadde okutuuka ku mmwe okugyako nga kaali mu kitabo nga tetunnaba ku katuusaawo, ddala ekyo kyangu nnyo ku Katonda.');
INSERT INTO "african_dev" VALUES(57,23,'57:23','Muleme kukubandeka mwoyo kwekyo ekiba kibasubye, era muleme kusanyuukirira olwekyo kyaba abawadde, Katonda bulijjo tayagala mwerazi omwewulize.');
INSERT INTO "african_dev" VALUES(57,24,'57:24','(Abantu abo) beebo abakodowala ne bakalaatira n’abantu obukodo, oyo yenna akyuka (naava ku biragiro bya Katonda) mazima Katonda yye yaatali mwetaavu, atenderezebwa.');
INSERT INTO "african_dev" VALUES(57,25,'57:25','Ddala twatuma ababaka baffe nga baleeta obunnyonnyofu netubassaako ekitabo (nga kirimu amateeka) era netubalagira okukozesa Minzaani, abantu babe nga bayimirizaawo obwenkanya, era netussa (wo) ekyuma nga kirimu amaanyi amasuffu n’emigaso (nfaafa) eri abantu, olwo nno Katonda alyoke amanye oyo amutaasa era nataasa ababaka be, awamu n’okuba nti (Katonda) talabwa. Mazima Katonda wa maanyi, nantakubwa ku mukono.');
INSERT INTO "african_dev" VALUES(57,26,'57:26','Mazima twatuma Nuhu ne Ibrahim netussa muzadde lyabwe bombi obwa Nabbi n’ekitabo, olwo nno mu bo mulimu omulungamu naye abasinga obungi mu bo bonoonyi.');
INSERT INTO "african_dev" VALUES(57,27,'57:27','Oluvanyuma twagobereza ku buufu bwabwe ba Nabbi baffe, kwabo twagoberezaako Isa mutabani wa Mariam, netumuwa ne Injil, era twassa mu mitima gyabo abamugoberera okulumiriganwa n''okusaasira, era bakolera ku bu sosodooti bwe baagunjaawo, tetububawandiikangako (wabula bo bwegunjirawo) mbu nga banoonya okusiima kwa Katonda, olwo nno ate tebabukolerako nga bwekyali kyetaagisa okubukolerako, kale netuwa abo abakkiriza mu bo empeera yaabwe, era abasinga obungi mu bo bonoonyi.');
INSERT INTO "african_dev" VALUES(57,28,'57:28','Abange mmwe abakkiriza mutye Katonda era mukkirize n’omubaka we, ajja kubawa okusaasira kwe emirundi ebiri, era abateerewo ekitangaala nga kwe mutambulira, era abasonyiwe, bulijjo Katonda musonyiyi nnyo, musaasizi.');
INSERT INTO "african_dev" VALUES(57,29,'57:29','(Ebyo byonna lwakuba nti) abaweebwa ekitabo babe nga bamanya nti tebalina kintu kyonna ku bigabwa bya Katonda kye balinako buyinza, era bamanye nti ebigabwa byonna biri mu mukono gwa Katonda, abiwa gwaba ayagadde, bulijjo Katonda ye nanyini bigabwa ebisuffu.');
INSERT INTO "african_dev" VALUES(58,1,'58:1','Ddala Katonda awulidde ebigambo byo mukyala ey''emulugunyizza gyoli ku bba era n''awaaba eri Katonda era Katonda abadde awulira ebigambo bye mubadde muwanyisiganya anti Katonda bulijjo awulira era alaba.');
INSERT INTO "african_dev" VALUES(58,2,'58:2','Abo bonna mu mmwe abagamba bakyala baabwe nti emigongo gyabwe giringa egya ba nnyabwe (abo be bakigamba) tebabangako ba maama baabwe. Ba maama baabwe beebo ababazaala. (mu kwogera batyo) mazima bbo boogera ekigambo ekibi. Mu kiseera kye kimu nga kigunje. Wabula mazima ddala Katonda alekera era asonyiwa.');
INSERT INTO "african_dev" VALUES(58,3,'58:3','Abo bonna abazira bakazi baabwe olw''okulayira nti kubo balinga bannyaabwe ate oluvanyuma nebaddayo eri ekyo kye baali balayiridde (oyo akikola) ateekwa okuta omuddu nga tannaba kuddayo kulabagana namukyalawe. Ekyo nno kyemuteekwa okukola, anti Katonda amanyi nnyo byonna byemukola.');
INSERT INTO "african_dev" VALUES(58,4,'58:4','Oyo aba tasobodde kuta muddu nga tannalabagana na mukyalawe ateekwa okusiiba emyezi ebiri nga gigoberegana, wabula oyo aba tasobodde ateekwa okuliisa ba masikiini nkaaga. Ekyo kibafuule abakkiriza Katonda n''omubakawe, ago mateeka g''aKatonda era bulijjo abawakanya amateeka g''a Katonda bagenda kutuukibwako ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(58,5,'58:5','Mazima abo abawakanya Katonda n''omubakawe baswavu nga abaabasooka bwe baaswazibwa, ate nga twassa amateeka agannyonnyola (Haraamu ne Halaal) naye abo abagawakanya bajja kutuukibwako ebibonerezo ebigenda okubanyoomesa.');
INSERT INTO "african_dev" VALUES(58,6,'58:6','Olunaku Katonda lwalibazuukiza bonna n''ababuulira ebyo byebaakola, Katonda yabikunganya n''abikomekkereza. So ng''ate bbo baabyerabira. Bulijjo Katonda mujulizi ku buli kintu.');
INSERT INTO "african_dev" VALUES(58,7,'58:7','Abaffe tomanyi nti mazima Katonda amanyi ebyo ebiri mu ggulu omusanvu n''ebyo ebiri mu nsi, nga tewali kafubo konna kakabe k''abasatu okugyako nga yye waakuna, wadde ak''abataano okugyako nga yye w''amukaaga wadde abatono abatenkana basatu oba abali waggulu w''omukaaga okugyako Katonda aba nabo awo wonna webali, oluvanyuma Katonda ku lunaku lw’enkomerero agenda kubategeeza ebyo byonna byebaali bakola. Anti Katonda amanyi buli kintu kyonna.');
INSERT INTO "african_dev" VALUES(58,8,'58:8','Abaffe tolaba abo abagaanibwa okukola obufubo (obwenkwe) oluvanyuma ne baddamu ekyo kyenyini ekyabagaanibwa, nga bogeraganya wakati waabwe mu nkukutu olw''okukola ebikyamu n''obulabe n''okujeemera omubaka. Era nga bwe baba bazze gyoli bakulamusa n''ekiramuso ekitali ekyo Katonda kyeyakutekerawo, nga bwe bagamba mu mitima gyaabwe nti lwaki Katonda tatubonereza olwebyo byetwogera. Omuliro Jahannama gwebagenda okuyingira gubamala. Nayo nno nkomerero mbi.');
INSERT INTO "african_dev" VALUES(58,9,'58:9','Abange mmwe abakkiriza bwe mubanga muteesa mu kyama temuteesanga okukola ekibi kyonna oba obulabe oba okujeemera omubaka. Muteesenga byakukola bulungi nakutya Katonda. Era mutye Katonda anti gyali gyemugenda okukunganyirizibwa.');
INSERT INTO "african_dev" VALUES(58,10,'58:10','Mazima okuteeseganya mu kyama mu ngeri embi n''okukola obulabe kikolwa kya Sitaane (nga nekigendererwa kye) kunyiiza bakkiriza. Ekyo tekiyinza kubaleetera kabi konna, okugyako nga Katonda akkirizza era abakkiriza bateekwa kwesiga Katonda yekka.');
INSERT INTO "african_dev" VALUES(58,11,'58:11','Abange mmwe abakkiriza bwe muba mugambiddwa nti mwefunze mu ntuula (ne banammwe bafune ebifo) mukikole. Katonda ajja ku basobozesa okugaziwa, ate bwe muba mugambiddwa okusituka (okuva mu kifo olwensonga yonna) musituke, anti Katonda asitula amadaala mu mmwe abakkiriza n''abo abaweebwa okumanya. Bulijjo Katonda mumanyi nnyo webyo byonna byemukola.');
INSERT INTO "african_dev" VALUES(58,12,'58:12','Abange mmwe abakkiriza bwemwagalanga okwogera n''omubaka musookenga ku saddaka, nga temunayogera naye. Ekyo kye kirungi gye muli era kyekisinga era kibatukuliza ddala. Naye bwe muba temulina kyemusadaka mazima Katonda musonyiyi waakisa.');
INSERT INTO "african_dev" VALUES(58,13,'58:13','Abaffe muzitowereddwa okuwaayo saddaka nga temunnaba kwogera ne Nabbi. Kale bwekibanga kibalemye oba nga ne Katonda abasonyiye, kale nno muyimirizeewo e sswala mutoole e zzaka mugondere Katonda n''omubakawe. Mazima Katonda amanyidde ddala nnyo ebyo byemukola.');
INSERT INTO "african_dev" VALUES(58,14,'58:14','Abaffe tewamanya abo abakwanagana n''abantu Katonda beyasunguwalira, abantu abo mu butuufu si ba mu mmwe wadde si ba mu bali (Bebakwanagana nabo) nebatuuka n''okulayira ku bulimba nga ate ddala bamanyi (ekituufu).');
INSERT INTO "african_dev" VALUES(58,15,'58:15','Katonda yabategekera ebibonerezo ebikambwe, anti mazima byonna byebaakolanga bibi.');
INSERT INTO "african_dev" VALUES(58,16,'58:16','(Okulayira kwabwe nti basiramu) baakifuula ekyokuwona, olwo nno nebaziyiza abantu okukola emirimu gya Katonda, bagenda kutuukibwako ebibonerezo ebinyomesa.');
INSERT INTO "african_dev" VALUES(58,17,'58:17','Emaali yaabwe n''abaana baabwe tebigenda kubagasa naakamu mumaaso g''a Katonda. Abo be bantu bo mumuliro ab''okugubeeramu obugenderevu.');
INSERT INTO "african_dev" VALUES(58,18,'58:18','Olunaku Katonda lwalibazuukiza bonna nebamulayirira nga bwebaabalayirira mmwe ku nsi nga balowooza nti kyebakoze kyakubagasa. Abange, abo nno beebalimba bennyini.');
INSERT INTO "african_dev" VALUES(58,19,'58:19','Sitaane yabeefunza okutuusa lweyabeerabiza okwogera ku Katonda. Abo nno be bagoberezi ba Sitaane. Mukimanye nti mazima ab''ekibiina kya Sitaane be b''okufafaganirwa.');
INSERT INTO "african_dev" VALUES(58,20,'58:20','Mazima abo abawakanya Katonda n''omubakawe ba mu bantu abanyoomebwa.');
INSERT INTO "african_dev" VALUES(58,21,'58:21','(Katonda) yakakasa dda nti nze Katonda n''ababaka bange tuli baakuwangula anti Katonda wamaanyi. Nnantakubwa ku mukono.');
INSERT INTO "african_dev" VALUES(58,22,'58:22','Tewali bantu bonna abakkiriza Katonda n''olunaku lw’enkomerero baalibadde batiitiibya abo abawakanya Katonda n''omubakawe, kababe ba kitaabwe, oba baana baabwe, oba baganda baabwe, oba nga baaluganda lwabwe olwokumpi. Abo nno (abatakola ekyo) Katonda yatebenkeza obukkiriza mu mitima gyabwe, naabawagira naamanyi agava gyali era nga agenda kubayingiza e jjana egikulukutira wansi waazo emigga, nga bakubeera mu zo olubeerera. Katonda yabasiima nga nabo bwebaamusiima. Kisaana kimanyike nti mazima abo b''ebantu ba Katonda abokwesiimira ddala.');
INSERT INTO "african_dev" VALUES(59,1,'59:1','Ebiri mu ggulu ne mu nsi byonna bitendereza Katonda nantakubwa ku mukono, assa buli kintu mu ssa lyakyo.');
INSERT INTO "african_dev" VALUES(59,2,'59:2','Y''eyafulumya abo abaakafuwala mu bayudaaya (Ab''ekiika kya ba Banu Nadhiri) mu ngeri y''okubawangangusa okuva mu mayumba gaabwe olufuluma olwasooka. Temulowoozangako mmwe abakkiriza nti (ba Banu Nadhiri) bayinza okugobwa ate bo (ba Banu Nadhiri) baalowooza nti ebigo byabwe bijja kulemesa Katonda okubatuusaako ekyo kyayagala. Katonda nakibatuusaako mu kiseera webaali batakisuubirira. Era Katonda nateeka mu mitima gyabwe okutya okwensusso nga bo bennyini be bemenyera amayumba gaabwe n''emikono gyabwe, n''emikono gya bakkiriza negikola ekyo. Kale nno abategeera mumwe mugyemu ekyokuyiga.');
INSERT INTO "african_dev" VALUES(59,3,'59:3','Singa tekyali nti Katonda yabasalirawo ku bawangangusa yaalibadde ababonereza kuno ku nsi, ate nga ne ku nkomerero abategekedde ebibonerezo byomuliro.');
INSERT INTO "african_dev" VALUES(59,4,'59:4','Ekyo nno lwansonga yaakuba nti baawakanya Katonda n''omubakawe. Ate nga oyo yenna awakanya Katonda, amanye nti mazima Katonda muyitirivu waakubonereza.');
INSERT INTO "african_dev" VALUES(59,5,'59:5','Emiti gye ntende gyonna gyemwatema oba jemwaleka nga giyimiridde nga bwegyali, ebyo byonna byakolebwa lwa kiragiro kya Katonda, era alyoke aswaze abo abonoonyi.');
INSERT INTO "african_dev" VALUES(59,6,'59:6','Eyo emmaali Katonda gyeyasobozesa omubakawe okusuuza abalabe. (ba Banu Nadhiri) temwebagala nfaransi wadde engamiya okugenda gyebali. Wabula Katonda asobozesa ababakabe okuwangula oyo yenna gwaba ayagadde okuwangulwa, mazima Katonda asobola buli kintu kyonna.');
INSERT INTO "african_dev" VALUES(59,7,'59:7','Emmaali eyo yonna Katonda gyeyasobozesa omubakawe okusuuza okuva mu byalo (bya ba Banu Nadhiri ng''olutalo luggweredde mu kutegeeragana) eba y''a Katonda, n''omubakawe, n''aboluganda lwa Nabbi olwokumpi, ne ba mulekwa, ne ba masikiini, n''abatambuze naabo abafunye ebizibu mu ngendo zaabwe. Emmaali eyo ereme kuba nga abeegasa nayo bagagga bokka mu mmwe. Ebyo byonna omubaka byaba abaleetedde mubitwale. Ate ebyo byonna byaba abaziyizza mubireke mutye Katonda anti Katonda muyitirivu w''akubonereza.');
INSERT INTO "african_dev" VALUES(59,8,'59:8','Na bwe kityo abaavu abaasenguka okuva e (Makkah) ne bagobwa mu mayumba gaabwe ne balesebwa e mmaali yaabwe nga banoonya obulungi n''okusiima kwa Katonda era nga baali bataasa ddiini y''a Katonda n''omubakawe (balinako omugabo). Abo nno b''ebakkiriza abamazima.');
INSERT INTO "african_dev" VALUES(59,9,'59:9','Abatuuze b''e Madinah abaasooka okukkiriza obusiramu (ba Answar) baagala abo abaasengukira gyebali (ba Muhajiruuna), era tebalina nsaalwa yonna kwebyo ebyaweebwa bannabwe (ba Muhajiruna) era nga beeresa newakubadde nga balina obwetaavu bo bennyini. Omuntu awona omulugube, abo nno b''ebokwesiimira ddala.');
INSERT INTO "african_dev" VALUES(59,10,'59:10','Naabo abajja oluvanyuma lwabwe babasabira nga bagamba nti ayi Katonda tusonyiwe ne baganda baffe abaatukulembera mu bukkiriza, era tossa mu mitima gyaffe nsaalwa yonna eri abo abakkiriza. Ayi Katonda waffe gwe musaasizi oweekisa ekingi.');
INSERT INTO "african_dev" VALUES(59,11,'59:11','Abaffe tolaba (ggwe Nabbi Muhammad) abananfunsi abagamba bannabwe abaakafuwala mu bayudaaya nti singa munaaba mugobeddwa naffe tujja kugenda nammwe. Era tetulikkiriza kigambo kyamuntu yenna ku bikwata ku mmwe. Era bwemulirwanyisibwa tugenda kubataasa. Ne Katonda akakasa nti mazima ddala baali balimba.');
INSERT INTO "african_dev" VALUES(59,12,'59:12','Wabula singa (abayudaaya) bagobwa, abananfunsi tebagenda nabo. Ate ne bwebaba balwanyisiddwa tebabataasa. So nga ne bwebaba bagezezzaako okubataasa babaddukako, n''oluvanyuma nebatafuna kutaasibwa kwonna.');
INSERT INTO "african_dev" VALUES(59,13,'59:13','(Abakkiriza), mwe mutiibwa mu mitima gyabwe okusinga bwe batya Katonda. Ekyo nno lwakuba nti bbo kibiina ky''abantu abatategeera.');
INSERT INTO "african_dev" VALUES(59,14,'59:14','(Abayudaaya n''abananfunsi) tebayinza kubalwanyisa nga bali wamu okugyako nga bali mu byalo ebiriko ebigo, oba nga bali mabega w''abisenge. Obulabe wakati waabwe bokka buyitirivu obalowooza okuba nti bali wamu, so nga emitima gyaabwe myawukamu. Ekyo nno lwakuba nti bo kibiina kyabantu abatategeera.');
INSERT INTO "african_dev" VALUES(59,15,'59:15','(Ba Banu Nadhiri) bafaanana naabo abaabakulembera e mabegako katono (Abakafiiri be Makkah abaawangulwa obubi ennyo ku lutalo lwe Badri) baaloza ku bukaawu bwe bikolwa byabwe (ku nsi) ate nga balinayo ebibonerezo ebiruma ennyo (ku nkomerero).');
INSERT INTO "african_dev" VALUES(59,16,'59:16','(Abananfunsi mu kusendasenda abayudaaya) balinga Sitaane bwe gamba omuntu nti kaafuwala. Wabula bwakaafuwala Sitaane emugamba nti nze siri naawe, mazima nze ntya Katonda omulezi w''ebitonde.');
INSERT INTO "african_dev" VALUES(59,17,'59:17','Enkomerero y''a bombi neeba nga ddala baakubeera mu muliro olubeerera, eyo nno y''empeera y''a beeyisa obubi.');
INSERT INTO "african_dev" VALUES(59,18,'59:18','Abange mmwe abakkiriza mutye Katonda, era omuntu atunule mu byategekedde olunaku lw''enkya. Bulijjo muteekwa okutya Katonda, anti mazima Katonda amanyidde ddala buli kye mukola.');
INSERT INTO "african_dev" VALUES(59,19,'59:19','Temubeera nga abo abaava ku Katonda, ekyavaako okuba nga Katonda abeerabiza bye baali bateekwa okwekolera, abo nno be bonoonyi.');
INSERT INTO "african_dev" VALUES(59,20,'59:20','Abantu bo mu muliro tebenkana na bantu ba mu jjana, abo abaliyingira e jjana be bokwesiima.');
INSERT INTO "african_dev" VALUES(59,21,'59:21','Singa twassa Kur''ani eno ku nsozi wandizirabye nga zigonze, era nga zimerenguse olw''okutya Katonda. Ebifaananyi ng''ebyo tubikubira abantu bibasoboseze okufumitiriza (bategeere Katonda).');
INSERT INTO "african_dev" VALUES(59,22,'59:22','Yye Katonda, yooyo tewali kisinzibwa kyonna okugyako ye yekka, amanyi ebitalabwa n''ebirabwa. Yye yemusaasizi, oweekisa ekibuna kuno ku nsi, aliba n''ekisa ekyenjawulo ku lunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(59,23,'59:23','Yye Katonda, yooyo tewali kisinzibwa kyonna okugyako yye, omufuzi wa buli kintu, atuukiridde mu buli kimu, kiwamirembe, akakasa obukkiriza bwa baddube, kalondoozi wa buli kintu, nantakubwa ku mukono, kkirimaanyi, kabwejungira. Katonda wanjawulo nnyo kwe byo bye bamugattako.');
INSERT INTO "african_dev" VALUES(59,24,'59:24','Yye Katonda, yooyo omutonzi, eyatandikawo ebitonde mu kubitonda nga talina kyalabirako, eyafaananya buli kintu nga bwa kyagala, alina amannya amalungi, buli ekiri mu ggulu n''ensi kimutendereza. Era yye Katonda, ye nantakubwa ku mukono, mugoba nsonga.');
INSERT INTO "african_dev" VALUES(60,1,'60:1','Abange mmwe abakkiriza temufuulanga abalabe bange era abalabe ba mmwe mikwano gyammwe egy''omunda, so nga baawakanya amazima agaabajjira okuva ewa Katonda ne bagobaganya omubaka awamu nammwe (okuva mu Makkah), olwokuba nti mukkiriza Katonda omulezi wa mmwe. Bwe muba mufulumye olw''okulwana mu kkubo lyange era nga munoonya okusiima kwange, (temukukutanga ne mubabuulira ebyama byammwe) nga mukolagana nabo mu ngeri y''ekyama ate nga nange manyi ebyo bye mukisa ne byemwolesa. Oyo yenna akikola mu mmwe aba avudde ku kkubo ettuufu.');
INSERT INTO "african_dev" VALUES(60,2,'60:2','Bwe babagwikiriza awantu wonna boolesa obulabe gye muli ne babagalulira emikono gyabwe (mu kubalwanyisa), era ne baboolekeza ennimi zaabwe (mu kubavuma) mu ngeri embi. Era nga beegomba nti singa mukaafuwadde.');
INSERT INTO "african_dev" VALUES(60,3,'60:3','Enganda zammwe wadde abaana ba mmwe tebagenda kubagasa ku lunaku lw''enkomerero. Katonda agenda kulamula wakati wa mmwe, anti Katonda alabira ddala ebyo bye mukola.');
INSERT INTO "african_dev" VALUES(60,4,'60:4','Mazima mulina ekyokulabirako ekirungi ekya Nabbi Ibrahim n''abakkiriza abaali naye, mu kiseera we baagambira bannaabwe nti mazima ffe tubesammudde n''ebyo bye musinza ebitali Katonda (ali omu). Tubawakanyizza era waliwo wakati waffe nammwe obulabe n''obukyayi obw''olubeerera, mpozzi nga mukkirizza Katonda ali omu. Okugyako ekigambo kya Ibrahim kye yagamba kitaawe oluberyeberye nti nja kwegayirira ddala Katonda akusonyiwe, era sirina kintu kyonna kyenyinza kukugasa ewa Katonda. Ayi Katonda waffe ggwe wekka gwetwesiga. Era tukwenenyerezza. Era gyoli gyetujja okudda.');
INSERT INTO "african_dev" VALUES(60,5,'60:5','Ayi Mukama Katonda waffe totufuula kikemo eri abo abaagaana okukkiriza era tusonyiwe ayi mukama waffe, anti mazima ggwe nantakubwa ku mukono, assa buli kintu mussa lyakyo.');
INSERT INTO "african_dev" VALUES(60,6,'60:6','Mazima mulina ekyokulabirako ekirungi ku bo (Nabbi Ibrahim n''abakkiriza abaali naye) ekyo kiri eri buli asuubira okufuna e mpeera okuva ewa Katonda ku lunaku lw''enkomerero. Naye oyo yenna anaaba akyuse (naava ku kukkiriza Katonda n''olunaku lw''enkomerero), mazima ddala Katonda yye talina kye yetaaga okuva eri bitondebye, atenderezebwa bulijjo.');
INSERT INTO "african_dev" VALUES(60,7,'60:7','Wabula Katonda asobola okussa wakati wa mmwe ne be muwalaggana nabo e nkolagana ennungi. Anti Katonda muyinza, era Katonda musonyiyi omusaasizi.');
INSERT INTO "african_dev" VALUES(60,8,'60:8','Katonda tabagaana ku kolagana naabo ababa tebabalwanyisizza olw''eddiini, era abatabagobaganya mu nsi yammwe mutuuke okuba nga temubayisa bulungi n''okubakolera obwenkanya, anti mazima Katonda ayagala abenkanya.');
INSERT INTO "african_dev" VALUES(60,9,'60:9','Mazima ddala Katonda abagaana okukolagana naabo abaabalwanyisa olw''eddiini, ne babagobaganya okuva mu nsi yammwe, ne bakola kinene nnyo mu kubagobaganya. Oyo yenna akolagana nabo, abo nno be balyazamanyi.');
INSERT INTO "african_dev" VALUES(60,10,'60:10','Abange mmwe abakkiriza singa abakyala abakkiriza babajjira nga basenguse mubagezesenga. Yye Katonda obukkiriza bwabwe aba abumanyi, bwe mukakasa nti bakkiriza temubaddizanga abakafiiri, abakyala bano tebali Halali ku bakafiiri abo nga n''abakafiiri abo bwe batali halali ku bakyala abo, wabula bo abasajja (Abakafiiri) mubaddize bye baawaayo mu kubawasa, olwo nno tekiba kibi bwe mubawasa nga mubawadde amahare gaabwe. Temwesiba ku kuwoowa okwekikafiiri kwe mwakola nabo. Mubasabe babaddize byemwawaayo nga nabo bwe basobola okusaba bye bawaayo. Ebyo nno kwe kulamula kwa Katonda kwabalamuza. Anti Katonda mumanyi nnyo ateeka buli kintu mussa lyakyo.');
INSERT INTO "african_dev" VALUES(60,11,'60:11','Singa omukyala w''omu ku mmwe ava mu busiramu nadda mu bukafiiri kemulwanagana nabo ne mumala mufunayo eminyago muteekwa okusasula abo abaddukwako bakyala baabwe ekigero kyekyo kye bawaayo nga babawasa. Bulijjo muteekwa okuba nga mutya Katonda oyo gwemukkiriza.');
INSERT INTO "african_dev" VALUES(60,12,'60:12','Owange ggwe Nabbi abakkiriza abakyala bwebajja gyoli nga bakuba ekirayiro mu maasogo, nti tebagenda kugatta ku Katonda kintu kyonna, era nga tebagenda kubba, wadde okwenda oba okutta abaana baabwe, era nti tebagenda kuwaayiriza nga bajingirira wakati w''emikono gyabwe n''amagulu gaabwe, nga era bwe batagenda kukujeemera mu kukola ekirungi kyonna, naawe nno bawe obukakafu era wegayirire Katonda abasonyiwe, anti mazima ddala Katonda musonyiyi nnyo omusaasizi.');
INSERT INTO "african_dev" VALUES(60,13,'60:13','Abange mmwe abakkiriza temukwanagana n''abantu Katonda beyasunguwalira. Anti bo tebalina ssuubi ku lunaku lw''enkomerero, nga abakafiiri bwebatalina ssuubi ku bikwata ku bantu abali emagombe.');
INSERT INTO "african_dev" VALUES(61,1,'61:1','Ebiri mu ggulu ne mu nsi byonna bitenda Katonda. Yye Katonda yemuwanguzi, assa buli kintu mu ssa lyakyo.');
INSERT INTO "african_dev" VALUES(61,2,'61:2','Abange mmwe abakkiriza lwaki mwogera ebyo bye mutakola.');
INSERT INTO "african_dev" VALUES(61,3,'61:3','Kisunguwalirwa nnyo ewa Katonda okwogera bye mutakola.');
INSERT INTO "african_dev" VALUES(61,4,'61:4','Mazima Katonda ayagala abo abalwana mu kkubolye, nga basimbye ennyiriri, ne baba nga ekizimbe, nga e bitundu byakyo bikwatagana bulungi.');
INSERT INTO "african_dev" VALUES(61,5,'61:5','Mu ngeri y''emu bajjukize ekyafaayo kya Musa bwe yagamba abantube nti, lwaki munnyiiza ng''ate mumanyidde ddala nti mazima nze ndi Mubaka wa Katonda gweyabatumira. Bwe baabula, Katonda kwekubuza emitima gyabwe, anti Katonda talungamya bantu bajeemu.');
INSERT INTO "african_dev" VALUES(61,6,'61:6','Era bajjukize e kyafaayo kya Isa mutabani wa Mariam bwe yagamba abaana ba Israel nti mazima nze ndi Mubaka wa Katonda gye muli, nga nkakasa Taurat eyankulembera era nga mpa amawulire ag''essanyu ag''omubaka alijja oluvanyuma lwange, nga erinyalye ye Ahmad. Wabula omubaka (Muhammad) oyo bwe yabajjira n''obubonero obunnyonnyofu, ate bo ne bagamba nti lino ddogo eryeyolefu.');
INSERT INTO "african_dev" VALUES(61,7,'61:7','Ani eyeeyisa obubi okusinga oyo atemerera Katonda ebyobulimba, so nga bulijjo ayitibwa okujja eri obusiramu. Katonda talungamya bantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(61,8,'61:8','(Mu kukola ekyo) baagala kuzikiza kitangaala kya Katonda n''emimwa gyabwe. Sso nga Katonda waakujjuza e kitangaalakye newakubadde nga abakafiiri tebakyagala.');
INSERT INTO "african_dev" VALUES(61,9,'61:9','Katonda yooyo eyatuma omubakawe ng''amuwadde obulungamu n''eddiini entuufu. Bulyoke bukye enkya nga yeddiini ewangudde newakubadde nga abagatta ku Katonda ebintu ebirala tebakyagala.');
INSERT INTO "african_dev" VALUES(61,10,'61:10','Abange mmwe abakkiriza mwandyagadde mbalagirire ebyettunzi ebiribawonya e bibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(61,11,'61:11','(Nabyo) muteekwa okukkiriza Katonda n''omubakawe, ne mulafuubana mu kuweereza mu kkubo lya Katonda, nga muwaayo emmaali yammwe, nga nammwe mwennyini mwenyigiramu. Ekyo nno kye kirungi gye muli singa mubadde mumanyi.');
INSERT INTO "african_dev" VALUES(61,12,'61:12','(Anti bwe mukola ebyo) Katonda ajja kubasonyiwa ebyonoono byammwe, abayingize e jjana ezikulukutiramu emigga era mugenda kufuna obutuulo obulungi mu jjana ez''olubeerera, okwo nno kwe kwesiima okusuffu.');
INSERT INTO "african_dev" VALUES(61,13,'61:13','Era mufune n''ekirala nga nakyo mukyagala, nakyo kwe kutaasa kwa Katonda n''obuwanguzi obwamangu. Era sanyusa abakkiriza (nti byonna bagenda kubifuna).');
INSERT INTO "african_dev" VALUES(61,14,'61:14','Abange mmwe abakkiriza musituke mutaase e ddiini y''a Katonda. Kibe nga Issa mutabani wa Mariam bwe yagamba abagoberezibe abenjawulo nti, baani abanannyamba ku mulimu gwa Katonda. Abagoberezibe abenjawulo kwe kuddamu nti ffe twewaddeyo okutaasa e ddiini ya Katonda. Ekyavaako ogutundu ku baana ba Israel okukkiriza, n''ekitundu ekirala okukaafuwala. Naffe ne tutaasa abo abakkiriza ku mulabe waabwe, ne bukya enkya nga be bali waggulu.');
INSERT INTO "african_dev" VALUES(62,1,'62:1','Ebiri mu ggulu ne mu nsi byonna bitenda Katonda omufuzi wa buli kintu, omutukuvu mu buli kimu, nantakubwa ku mukono, assa buli kintu mussa lyakyo.');
INSERT INTO "african_dev" VALUES(62,2,'62:2','Yye yooyo eyatuma eri abo abataamanya kusoma na kuwandiika omubaka (Muhammad) nga ava mu bo ng''abasomera e bigambo bya Katonda era nga abatukuza (okubajja mu bukafiiri) era nga abayigiriza ekitabo (Kur''ani) n''ebiva mu Hadith so nga oluberyeberye (nga Nabbi tannatumwa) baali mu bubuze obw''olwatu.');
INSERT INTO "african_dev" VALUES(62,3,'62:3','Era yamutuma n''eri abalala nga ba mu bo, naye nga balijja luvannyuma lwabwe (nabo be basiramu abalala bonna abakkiriza Nabbi Muhammad abataamulabako okutuusiza ddala ku lunaku lw''enkomerero) era yye Katonda, ye nantakubwa ku mukono, assa buli kintu mussa lyakyo.');
INSERT INTO "african_dev" VALUES(62,4,'62:4','Ebyo bye birungi bya Katonda byawa oyo gwaba ayagadde. Mazima Katonda ye nannyini birungi ebisukkulumu.');
INSERT INTO "african_dev" VALUES(62,5,'62:5','Ekifaananyi kyabo abaaweebwa Taurat ne batagikozesa, balinga endogoyi ey''etikka ebitabo (nga tesobola kwe gasa nabiri mu bitabo ebyo), kibi nnyo ekifaananyi ekyo, ekyabo abalimbisa ebigambo bya Katonda. Bulijjo Katonda talungamya bantu beeyisa bubi.');
INSERT INTO "african_dev" VALUES(62,6,'62:6','Bagambe (ggwe Nabbi Muhammad) nti abange mmwe abayudaaya bwe muba nga mweyita abaagalwa ba Katonda (mwe mwekka) so ssi abantu abalala, kale nno mwegombe okufa bwe muba nga muli ba mazima.');
INSERT INTO "african_dev" VALUES(62,7,'62:7','Tebagenda kwe gombera ddala kufa, olwebyo byebaakola. Bulijjo Katonda amanyi nnyo abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(62,8,'62:8','Bagambe (ggwe Nabbi Muhammad) nti mazima okwo okufa kwe mudduka kwakubatuukako, oluvanyuma muzzibwe eri omumanyi w''ebitalabikako n''ebirabika. Ate nno olwo alyoke abategeeze ebyo byonna bye mwali mukola.');
INSERT INTO "african_dev" VALUES(62,9,'62:9','Abange mmwe abakkiriza, bwe baaziinanga olw''esswala (ye jjuma) ku lunaku lwe jjuma, mwanguwenga okugenda eri okwogera ku Katonda, ate muyimirize emirimu. Ekyo kye kirungi gye muli singa mubadde mumanyi.');
INSERT INTO "african_dev" VALUES(62,10,'62:10','E sswala bweggwa, olwo nno mugende mu nsi munoonye ebigabwa bya Katonda, mwogere nnyo ku Katonda mulyoke mwesiime.');
INSERT INTO "african_dev" VALUES(62,11,'62:11','Bwe balaba ebyo busuubuzi, oba ebya masanyu, banguwa okugenda gyebiri ne bakuleka nga oyimiridde. Bagambe nti ebya Katonda bye birungi okusinga ebya masanyu n''obusuubuzi, ate nga Katonda yaasinga abagabi bonna.');
INSERT INTO "african_dev" VALUES(63,1,'63:1','Abannanfusi bwe bajja gyoli (Ggwe Nabbi Muhammad (صلى الله عليه وسلم)) bagamba nti tukkiriza nti mazima ddala ggwe oli Mubaka wa Katonda. Ne Katonda akimanyidde ddala nti mazima gwe oli Mubakawe, ate era Katonda akakasa nti mazima abannanfusi balimba nnyo.');
INSERT INTO "african_dev" VALUES(63,2,'63:2','Ekyo kulayira kwabwe (nti basiramu) bakifuula ekyokulwanyisa ekibawonya, olwonno ne balyoka baziyiza abantu okuyingira mu ddiini y''a Katonda. Mazima bibi nnyo ebyo bye baali bakola.');
INSERT INTO "african_dev" VALUES(63,3,'63:3','Ekyo kiri bwe bityo lwa kuba baalangirira obukkiriza n''ennimi za bwe, so nga munda bakafiiri. Olwo nno e mitima gyabwe negiteekebwako envumbo ne babeera nga tebategeera.');
INSERT INTO "african_dev" VALUES(63,4,'63:4','Bwo batunulako, e mibiri gyabwe gikusanyusa, bwe boogera toyinza butatwala kigambo kyabwe, balinga ebiti ebyesigamiziddwa ku kisenge nga tebiyinza kwetengerera, buli lwonna lwe bawulira akakuba bagamba nti bubakeeredde, abo be balabe, n''olwekyo beegendereze, Katonda yabakolimira, ggwe ate bandivudde batya ku mazima.');
INSERT INTO "african_dev" VALUES(63,5,'63:5','Singa baba bagambiddwa nti mujje eri omubaka wa Katonda abasabire e kisonyiwo, banyeenya e mitwe n''obalabira ddala nga bajeemye era nga beekuluntazza.');
INSERT INTO "african_dev" VALUES(63,6,'63:6','Okubasabira e kisonyiwo n''obutabasabira byonna bifaanana, Katonda tagenda kubasonyiwa, anti Katonda talungamya bantu boononyi.');
INSERT INTO "african_dev" VALUES(63,7,'63:7','Be bamu be bo abagamba nti temuwaayo kintu kyonna eri abo abali awamu n''omubaka wa Katonda okutuusa lwe banamwabulira. So nga amawanika g''omuggulu n''ensi gonna ga Katonda. Naye mazima abananfusi tebategeera.');
INSERT INTO "african_dev" VALUES(63,8,'63:8','Baatuuka n''okugamba nti bwe tunadda e Madinah (okuva mu lutalo lwa ba Bani Al Mus-twalik) abasinga okuba ab''ekitiibwa bajja kugoba mu (Madinah) abanyoomebwa. So nga e kitiibwa n''obuwanguzi bya Katonda n''omubakawe n''abakkiriza naye mazima abananfusi tebamanyi.');
INSERT INTO "african_dev" VALUES(63,9,'63:9','Abange mmwe abakkiriza, ebyo bugagga byamwe n''abaana bamwe tebibawugulanga ne bibajja ku kwogera ku Katonda, oyo yenna akikola, abo nno be bokufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(63,10,'63:10','Era mugabe kwe byo bye twabawa walumbe nga tannatuuka ku omu ku mmwe, naatuuka okulaajana nga agamba nti, ayi mukama omulezi wange singa ondeseewo akabanga ne nsadaakako era ne mbeera mu balongoofu.');
INSERT INTO "african_dev" VALUES(63,11,'63:11','Sso nga Katonda tagenda kulindiriza mwoyo gwonna entuuko yaagwo bweriba nga etuuse, Katonda bulijjo muyitirivu w''akumanya ebyo byonna bye mukola.');
INSERT INTO "african_dev" VALUES(64,1,'64:1','Ebiri mu ggulu ne mu nsi byonna bitenda Katonda, obufuzi bwonna n''amatendo gonna bibye, anti ye muyinza wa buli kintu.');
INSERT INTO "african_dev" VALUES(64,2,'64:2','Yye yooyo eyabatonda nga mu mmwe mulimu abakafiiri na bandi bakkiriza. Lubeerera Katonda alaba bye mukola.');
INSERT INTO "african_dev" VALUES(64,3,'64:3','Yatonda eggulu n''ensi lwa nsonga. Ekifaananyi kye yabatonderako kye kisingira ddala okuba ekirungi. Era gyali gye mugenda okudda.');
INSERT INTO "african_dev" VALUES(64,4,'64:4','Amanyi ebiri mu ggulu ne mu nsi, era nga bwa manyi bye mukweka ne bye mwolesa. Anti Katonda amanyi ebiri mu bifuba.');
INSERT INTO "african_dev" VALUES(64,5,'64:5','Abaffe amawulire gaabo abaakafuwala oluberyeberye lwa mmwe tegaabatuukako, ekyabaviirako okuba nga bakomba ku bukaawu bwo bukafiri bwabwe. Era bagenda kutuukwakwo e bibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(64,6,'64:6','Olwe''nsonga nti ababaka baabwe baabaleeteranga obubaka obujjudde obunnyonnyofu, bo ne bagamba nti omuntu obuntu yajja atulungamye! ekyo ne kibakaafuwaza, ne baba nga tebakyayagala kuwulira kikwata ku bubaka, ne Katonda naaba nga takyabeetaaga. Anti Katonda bulijjo talina kye yataaga, era nga bulijjo atenderezebwa.');
INSERT INTO "african_dev" VALUES(64,7,'64:7','Abatakkiriza Katonda beerimba nga bagamba nti tebagenda kuzuukizibwa. Bagambe ggwe (Nabbi Muhammad (صلى الله عليه وسلم)) nti ssi bwekiri. Ndayira Katonda omulezi wange ddala mujja kuzuukizibwa, oluvanyuma mujja kutegeezebwa ddala ebyo bye mwakola, nekyo nno kyangu nnyo ku Katonda.');
INSERT INTO "african_dev" VALUES(64,8,'64:8','Kale nno mukkirize Katonda n''omubakawe n''ekitangaala (Kur''ani) kye twassa, ne Katonda bulijjo mumanyi nnyo weebyo bye mukola.');
INSERT INTO "african_dev" VALUES(64,9,'64:9','Mujjukire olunaku Katonda lwa libakunganya ku lunaku lwe kiyaama, olwo lwe lunaku oluliraga okufaafaganirwa (eri abakafiiri), oyo yenna akkiriza Katonda naakola e mirimu emirungi, Katonda amusangulako ebibibye, era agenda kumuyingiza e jjana ezikulukutiramu e migga, nga baakuzibeeramu lubeerera. Okwo nno kwe kwe siima okuyitirivu.');
INSERT INTO "african_dev" VALUES(64,10,'64:10','Naye ate abo abaakafuwala ne balimbisa n''ebigambo byaffe, abo nno b''ebantu bo mu muliro, ba kubeera mu gwo olubeerera, eyo nkomerero mbi.');
INSERT INTO "african_dev" VALUES(64,11,'64:11','Tewali buzibu bwonna butuuka ku muntu okugyako nga Katonda akkirizza, oyo yenna akkiriza Katonda, Katonda alungamya omutimagwe. Mazima bulijjo Katonda mumanyi nnyo wa buli kintu.');
INSERT INTO "african_dev" VALUES(64,12,'64:12','Mugondere Katonda era mugondere omubakawe. Naye bwe munaayawukana kwe kyo, ye omubaka waffe (tanenyezebwa) obuvunaanyizibwabwe, kwe kutuusa obubaka mu bujjuvu.');
INSERT INTO "african_dev" VALUES(64,13,'64:13','Katonda ye yekka asinzibwa, abakkiriza bateekwa kwesiga Katonda yekka.');
INSERT INTO "african_dev" VALUES(64,14,'64:14','Abange mmwe abakkiriza mazima mu bakyala ba mmwe n''abaana ba mmwe mulimu abalabe, n''olwekyo mubeegendereze. Naye bwe mubaddiramu ne mubalekera, ne mubasonyiwa, mazima Katonda muyitirivu waakusonyiwa asaasira ennyo.');
INSERT INTO "african_dev" VALUES(64,15,'64:15','Mazima e byobugagga bya mmwe n''abaana ba mmwe kikemo, ng''ate ewa Katonda we wali empeera ensukkulumu.');
INSERT INTO "african_dev" VALUES(64,16,'64:16','Kale nno mutye Katonda nga bwe muba musobodde, muwulire era mugonde, mu ngeri yeemu mugabe ku lwa Katonda, ekyo nno kye kirungi gye muli. Oyo yenna awonyezebwa omulugube gwo mwoyogwe, abo nno be b''okuganyulwa.');
INSERT INTO "african_dev" VALUES(64,17,'64:17','Singa muwola Katonda oluwola olulungi, alubabalizaamu era n''abasonyiwa ebyonoono bya mmwe, bulijjo Katonda asiima era wa kisa.');
INSERT INTO "african_dev" VALUES(64,18,'64:18','(Katonda oyo) amanyi ebitalabwako n''ebirabwa, nantalemwa, akola buli kintu nga asinziira ku nsonga.');
INSERT INTO "african_dev" VALUES(65,1,'65:1','Owange ggwe Nabbi, bwe muba nga muta abakyala, mubate nga bali ku mbeera eyobutukuvu (Nga tebali mu Haidh), mubalenga n''obwegendereza ebbanga lya Idda (Idda kye kiseera omukyala ateereddwa kyamala nga tannaba ku kkirizibwa kuddamu kufumbirwa). Era mutye Katonda omulezi wa mmwe, temwetantalanga okubaggya mu mayumba gaabwe (mwe babadde bawangaalira nga bali mu bufumbo bwa bwe), so nga nabo tebavanga mu mayumba ago okugyako nga bakoze ebikolobero eby''olwatu. Ezo z''ensalo z''amateeka ga Katonda. Oyo yenna abuuka ensalo z''amateeka ga Katonda, aba y''eryazaamanyizza, tomanya oba olyawo Katonda naaleetawo embeera endala oluvanyuma lw''ekyo.');
INSERT INTO "african_dev" VALUES(65,2,'65:2','Kale nno bwe baba nga basemberedde okumalako ebbanga lya Idda yaabwe mubaddirengamu, mu ngeri ennungi, oba mwawukane nabo mu ngeri ennungi (ebbanga lya Idda bwe riba liweddeko). Mubeerewo n''abajulizi babiri abeesimbu mu bantu ba mmwe, muwenga obujulizi mu bwe simbu kulwa Katonda, ebyo nno bibuulirirwa nabyo oyo yenna akkiriza Katonda n''olunaku lw''enkomerero. Bulijjo oyo yenna atya Katonda amuteerawo obwangu.');
INSERT INTO "african_dev" VALUES(65,3,'65:3','Era naamugabirira mu ngeri gyatasuubira, era oyo yenna eyeesiga Katonda, Katonda amumala, anti Katonda ekigambokye kye kyenkomeredde, mazima Katonda yategeka ekigero kya buli kintu.');
INSERT INTO "african_dev" VALUES(65,4,'65:4','Abo bonna mu bakyala ba mmwe abatakyagenda mu nsonga za Heidh, bw emutankananga ebbanga lya Idda yaabwe, balindenga emyezi esatu. Wabula abo abatanayingira mu nsonga za Haidh Idda yaabwe emyezi esatu. Naye abo abali embuto ebbanga lyabwe kumala kuzaala. Oyo yenna atya Katonda amuteerawo obwangu mu buli kintu.');
INSERT INTO "african_dev" VALUES(65,5,'65:5','Ekyo kiragiro kya Katonda kye yassa gye muli, oyo yenna atya Katonda amusangulako e bibibye ate naamwongeza e mpeera.');
INSERT INTO "african_dev" VALUES(65,6,'65:6','(Abakyala abaweereddwa ttalaka) mu basuzenga mu mayumba mwe musula okusinziira ku busobozi bwa mmwe, temubatulugunyanga, nga ekigendererwa kubanyigiriza (balemererwe), naye bwe babanga bali mbuto mubalabirirenga okutuusa lwe bazaala. Ate (bwe babanga bazadde) nebabayonseza abaana, mubasasulenga. Era mukkiriziganye mu ngeri e nnungi, naye bwe mubanga mulemaganye olwo nno munoonye omulala anaayonsa.');
INSERT INTO "african_dev" VALUES(65,7,'65:7','Omusajja alina okulabirira omukyala okusinziira ku busobozibwe, nooyo Katonda gwe yawa ebyenfuna ebinafu naye alabirira omukyala okusinziira nga Katonda bwe yamuwa, Katonda tawaliriza muntu yenna okusukka kwe byo bye yamuwa. Katonda ajja kuteekawo obwangu oluvanyuma lwo buzito.');
INSERT INTO "african_dev" VALUES(65,8,'65:8','Ebitundu bimeka nga abantu baayo bajeemera Katonda omulezi waabwe n''ababaka beyabatumiranga, ne tubibala olubala oluyitirivu era ne tubibonereza olubonereza olukambwe.');
INSERT INTO "african_dev" VALUES(65,9,'65:9','(Ebitundu ebyo) ne bikomba ku bukaawu bw''obujeemu bw''abyo, n''eba nga enkomerero y''obujeemu bwabwe kufaafaganirwa.');
INSERT INTO "african_dev" VALUES(65,10,'65:10','Katonda yabategekera ebibonerezo ebikambwe, kale nno mutye Katonda abange mmwe abalina amagezi agategeera, abange mmwe abakkiriza. Anti Katonda yassa gye muli obubaka obujjukiza (Kur''ani).');
INSERT INTO "african_dev" VALUES(65,11,'65:11','Omubaka nga abasomera ebigambo bya Katonda ebinnyonnyola, nga ekigendererwa kwe kugya mu bizikiza abakkiriza ne bakola e mirimu e mirungi n''abazza mu kitangaala. Oyo yenna akkiriza Katonda n''akola e mirimu e mirungi, Katonda agenda kumuyingiza mu jjana, nga e migga gikulukutira muzo, baakubeera muzo olubeerera. (Omuntu ow''engeri eyo) Katonda yamutebenkereza enfuna (mu bulamu obwo luvannyuma).');
INSERT INTO "african_dev" VALUES(65,12,'65:12','Katonda yooyo eyatonda eggulu omusanvu n''ensi naazitonda omuwendo gwe gumu, obubaka bwa Katonda bukka wakati we ggulu n''ensi mulyoke mumanye nti, mazima Katonda musobozi ku buli kintu, era mazima Katonda mumanyi wa buli kintu.');
INSERT INTO "african_dev" VALUES(66,1,'66:1','Owange ggwe Nabbi lwaki oziza ebyo Katonda bye yakukkiriza, nga onoonya okusiimisa bakyalabo. Bulijjo Katonda muyitirivu wa kusonyiwa era muyitirivu wa kusaasira.');
INSERT INTO "african_dev" VALUES(66,2,'66:2','Katonda yabateerawo engeri gye muva mu birayiro byemuba mukoze, anti bulijjo aba nammwe, ate nga yye muyitirivu wa kumanya, buli kyakola akikola lwa nsonga.');
INSERT INTO "african_dev" VALUES(66,3,'66:3','Era jjukira Nabbi bwe yabuulira omu ku bakyalabe (Hafuswa) ekyama, omukyala oyo bwe yamala okwogera ekyama ekyo, ate Katonda n''akitegeeza Nabbi. Nabbi n''amubuulirako ebimu ebirala n''abireka. Nabbi olwa kibuulira omukyala oyo (omukyala n''amubuuza) nti ani yakikugambye (Nabbi n''amuddamu nti) eyakintegeezezza ye muyitirivu w''okumanya omukenkufu (Katonda).');
INSERT INTO "african_dev" VALUES(66,4,'66:4','Singa munaaba mwenenyezza mwembiriri (Hafuswa ne Aisha) ewa Katonda (kye kirungi gye muli), anti emitima gya mmwe gyawaganyadde. Naye bwe munamwekalakaasizaako, (mumanye nti) mazima Katonda ali ku luddalwe, ne Jiburilu, n''abakkiriza abalongoofu, ne ba Malayika bonna oluvanyuma lw''ekyo ba kumuyambako.');
INSERT INTO "african_dev" VALUES(66,5,'66:5','Singa anaba abatadde, Katonda omuleziwe, ajja kumuwaanyisizaamu abakyala abalala abasinga ku mmwe, nga basiramu nnyo, bakkirizza, nga bagonvu nnyo, nga beenenya era nga basinza nnyo Katonda, abatambulira mu kumutya, kababe nga baafumbirwako oba nga tebafumbirwangako.');
INSERT INTO "african_dev" VALUES(66,6,'66:6','Abange mmwe abakkiriza mwewonye muwonye n''abantu ba mmwe omuliro, nga gukumirwa ku bantu na mayinja, gukuumibwa ba Malayika abakambwe abaamanyi ennyo, tebajeemera Katonda mu kyonna kyaba abalagidde, ate nga bakola ekyo kyonna kye baba balagiddwa.');
INSERT INTO "african_dev" VALUES(66,7,'66:7','(Ku lunaku lw''enkomerero abakafiiri baligambibwa nti) Abange mmwe abakafiiri temwetonda olwaleero, mazima mugenda kusasulwa ebyo bye mwakola.');
INSERT INTO "african_dev" VALUES(66,8,'66:8','Abange mmwe abakkiriza mwenenyeze Katonda olwenenya olwa namaddala. Omulezi wa mmwe Katonda kikakafu ajja ku basangulako ebyonoono bya mmwe era abayingize e jjana ezikulukutirwamu e migga. Olunaku Katonda lwataliweebuula Nabbi na bakkiriza abali naye. E kitangaala kyabwe nga kibeetoolodde mu maaso ne ku ddyo waabwe, nga bagamba nti ayi omulezi waffe tulekerewo e kitangaala kyaffe era otusonyiwe (ebyonoono byaffe) anti mazima ggwe oli musobozi ku buli kintu.');
INSERT INTO "african_dev" VALUES(66,9,'66:9','Owange ggwe Nabbi funvubira mu kulwanyisa abakafiiri n''abannanfusi era obazitoowereze. Obuddo bwabwe muliro Jahannama, ate obuddo obwo bubi nnyo.');
INSERT INTO "african_dev" VALUES(66,10,'66:10','Katonda yakubira abakafiiri ekifananyi kya muka Nuhu ne muka Luutu. Baali bakyala b''a baddu ababiri mu baddu baffe abalongoofu, naye nno ne babakumpanya (n''obutakkiriza Katonda). Nabbi Nuhu ne Luutu tebaalina kye baagasa bakyala baabwe ewa Katonda. Ne bagambwa (Abakyala bombi) nti muyingire omuliro awamu naabo abaguyingira.');
INSERT INTO "african_dev" VALUES(66,11,'66:11','Era Katonda yakubira abakkiriza e kifaananyi kya muka Firawo mu kiseera weyasabira (Katonda) nti: Ayi omulezi wange nzimbirayo e nyumba gyoli mu jjana era omponye Firawo n''ebikolwabye, omponye n''abantu abeeyisa obubi.');
INSERT INTO "african_dev" VALUES(66,12,'66:12','Era Katonda yakuba e kifananyi kya Mariyamu muwala wa Imran oyo eyeekuuma kale netufuuwa mu ye omwoyo ogwava gye tuli era Mariam nakkiriza ebigambo bya Katondawe n''ebitabobye, n''abeera nga ali mu basinza Katonda abagonvu.');
INSERT INTO "african_dev" VALUES(67,1,'67:1','Mukama Katonda nanyini buyinza bwonna musukkulumu, era nga muyinza w''abuli kintu.');
INSERT INTO "african_dev" VALUES(67,2,'67:2','Oyo eyatonda okufa n''obulamu, olwokubageseza alabe ani asinga okukola obulungi. Naye ye Nnantakubwa ku mukono omusonyiyi.');
INSERT INTO "african_dev" VALUES(67,3,'67:3','Oyo eyatonda eggulu omusanvu nga myaliro, toyinza kulaba mu kutonda kwa Katonda kamogo konna w''etegereze abaffe olabamu obukyamu bwonna.');
INSERT INTO "african_dev" VALUES(67,4,'67:4','Era zzaayo amaaso emirundi nemirundi eriiso lijja ku kuddira nga likooye era nga tolina kyofunyeeyo.');
INSERT INTO "african_dev" VALUES(67,5,'67:5','Mazima eggulu eriri okumpi n''ensi twalinyiriza nemunyeeye ezaaka, era nga bitawuliro ebigoba Sitani era Sitani ezo twazitegekera ebibonerezo byomuliro Ssair.');
INSERT INTO "african_dev" VALUES(67,6,'67:6','Ate bbo abaawakanya Katonda waabwe twabategekera ebibonerezo byamuliro Jahannama era buliba buddo bubi.');
INSERT INTO "african_dev" VALUES(67,7,'67:7','Bwe baligusuulwamu bagenda kuguwulira nga gutokota era nga gufuuwa.');
INSERT INTO "african_dev" VALUES(67,8,'67:8','Nga gubula kuyulikamu buli lwegunasulwangamu ekibinja abakuumi bagwo banababuuzanga nti temwajjirwa muntu yenna abatiisa (muliro guno)?');
INSERT INTO "african_dev" VALUES(67,9,'67:9','Baliddamu nti weewawo omutiisa yatujjira naye netumulimbisa era netumugamba nti Katonda tassanga bubaka bwonna, ebyo byemulimu bubuze obuyitirivu.');
INSERT INTO "african_dev" VALUES(67,10,'67:10','Baligamba nti singa twali tuwulira oba nga tutegeera (ebyatugambwanga) tetwandibadde bamu kwabo abayingira omuliro.');
INSERT INTO "african_dev" VALUES(67,11,'67:11','Nebeekakasaako ekyonoono kyabwe okufaafaganirwa kuli ku bantu bo mu muliro.');
INSERT INTO "african_dev" VALUES(67,12,'67:12','Mazima abo abatya Katonda wabwe awamu n''okuba nga talabika, bagenda okufuna ekisonyiwo n''empeera ensuffu.');
INSERT INTO "african_dev" VALUES(67,13,'67:13','Kyekimu mukwese ebigambo byamwe oba mubyatudde mazima yye amanyi ebiri mu bifuba');
INSERT INTO "african_dev" VALUES(67,14,'67:14','Abaffe eyatonda ayinza obutamanya nga ate ye muyitirivu w''okumanya era nga mukugu nnyo!.');
INSERT INTO "african_dev" VALUES(67,15,'67:15','Yye yoyo eyabafuulira ensi enyangu, kale mutambule mu bitundu byayo mulye mu bigabwabye era gyali gye mugenda okudda.');
INSERT INTO "african_dev" VALUES(67,16,'67:16','Musuubidde okuwona oyo ali waggulu ensi n''etabamira singa eba etabanguse.');
INSERT INTO "african_dev" VALUES(67,17,'67:17','Oba musuubidde okuwona oyo ali waggulu n''atabasindikira muyaga, wabula mugenda kumanya okulabula kwange nga bwekwali okukulu.');
INSERT INTO "african_dev" VALUES(67,18,'67:18','Abo abaaliwo oluberyeberye lwabwe baalimbisa, naye tewamanya kyennabatusaako.');
INSERT INTO "african_dev" VALUES(67,19,'67:19','Abaffe tebalaba ebinyonyi ebibuukira waggulu wabwe nga byanjuluza ebiwawaatiro ate nga n''oluusi biwumbako, tewali kibikuumira mu bbanga okugyako Katonda omusaasizi, mazima ye alaba buli kintu.');
INSERT INTO "african_dev" VALUES(67,20,'67:20','Oba ani oyo asobola okubalwanirira n''abataasa atali Katonda, abawakanyi tebalina kyebaliko okugyako mawaggali.');
INSERT INTO "african_dev" VALUES(67,21,'67:21','Ye abaffe ani oyo ayinza okubagabirira singa Katonda abajjako okugabirirakwe. Wabula baagwa mu kateebe k''okuwalaaza empaka n''okwewaggula.');
INSERT INTO "african_dev" VALUES(67,22,'67:22','Ye abaffe oyo atambula abwamiridde ku kyenyikye y''abeera kubulungamu oba oyo atambula nga yeegolodde ngali ku Kkubo eggolokofu.');
INSERT INTO "african_dev" VALUES(67,23,'67:23','Bagambe nti Katonda y''eyabatandikawo n''abassako amatu n''amaaso n''abawa n''emitima wabula mwebaza kitono.');
INSERT INTO "african_dev" VALUES(67,24,'67:24','Bagambe nti Katonda yoyo eyabassa mu nsi era nga mugenda kuzuukizibwa mudde gyali.');
INSERT INTO "african_dev" VALUES(67,25,'67:25','Bagamba mu kubalaata nti ebyo byemutulaganyisa biribaawo ddi bwe mubanga byemwogera bituufu.');
INSERT INTO "african_dev" VALUES(67,26,'67:26','Bagambe nti okumanya okujjuvu okukwata ku nsonga eyo kuli wa Katonda, naye nze ndi mutiisa owolwatu.');
INSERT INTO "african_dev" VALUES(67,27,'67:27','Bwe baliraba e bibonerezo nga bisembedde, ebyenyi byabo abakafuwala bigenda kwekaba, awo balyoke bategezebwe nti ekyo nno kye mwagambanga nti kirituukaddi.');
INSERT INTO "african_dev" VALUES(67,28,'67:28','Bagambe nti, mulaba mutya singa Katonda anzikiriza n''abo abali nange oba n''atusaasira anti obuyinza bwonna buli gyali, naye ani alitaasa abakafiiri ku bibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(67,29,'67:29','Bagambe nti yye Katonda omusaasizi gwetukkiriza era yekka gwetwesiga wabula lumu mulimanya ani ali mu bubuze obweyolefu.');
INSERT INTO "african_dev" VALUES(67,30,'67:30','Bagambe nti mulaba mutya singa amazzi gammwe bukya nga gakalidde ani ayinza okubawa amazzi agakulukuta amayonjo.');
INSERT INTO "african_dev" VALUES(68,1,'68:1','Nuni, yemu kunyukuta ezikola walifu empalabu. Ndayidde e kalamu nebyebagiwandiisa.');
INSERT INTO "african_dev" VALUES(68,2,'68:2','Mazima Gwe Muhammad ekyengera kyobwa Nabbi Katonda wo kyeyakuwa tekiyinza ku kufuula mulalu.');
INSERT INTO "african_dev" VALUES(68,3,'68:3','Era ddala oteekwa okufuna empeera etakutukawo.');
INSERT INTO "african_dev" VALUES(68,4,'68:4','Era mazima ddala Ggwe oli wampisa nungi nnyo.');
INSERT INTO "african_dev" VALUES(68,5,'68:5','Ojja kulaba nga nabo bwebagenda okulaba.');
INSERT INTO "african_dev" VALUES(68,6,'68:6','Ani kumwenna eyatukwako ekizibu kyeddalu.');
INSERT INTO "african_dev" VALUES(68,7,'68:7','Mazima Katondawo amanyidde ddala ani eyabula okuva ku kkubolye era nga bwamanyidde ddala abalungamu.');
INSERT INTO "african_dev" VALUES(68,8,'68:8','Kale nno togondera abo abalimbisa.');
INSERT INTO "african_dev" VALUES(68,9,'68:9','Bandyagadde nti singa obagondera nabo nebakugondera.');
INSERT INTO "african_dev" VALUES(68,10,'68:10','Era togeza n''ogondera buli alayiralayira ataliimu nsa.');
INSERT INTO "african_dev" VALUES(68,11,'68:11','Omugeyi atambuza olugambo.');
INSERT INTO "african_dev" VALUES(68,12,'68:12','Omukodo, omulumbaganyi omwononefu.');
INSERT INTO "african_dev" VALUES(68,13,'68:13','Mubi nnyo ate awamu n''ekyo mwana w''abwenzi.');
INSERT INTO "african_dev" VALUES(68,14,'68:14','Tomugondera olwokuba alina emmali n''abaana.');
INSERT INTO "african_dev" VALUES(68,15,'68:15','Omuntu oyo bwasomerwa ebigambo byaffe agamba nti nfumo z''abantu abedda.');
INSERT INTO "african_dev" VALUES(68,16,'68:16','Kulunaku lw’enkomerero tugenda kussa kunyindo ye akabonero akamulamba nga tanayingizibwa muliro.');
INSERT INTO "african_dev" VALUES(68,17,'68:17','Mazima ffe twagezesa abantu be Makkah bwetwabaleetera enjala nga bwetwagezesa abantu abaalina enimiro yaabwe abalayira nti baali bagenda kukungula kumakya nnyo, olwo nno baleme kugabirako bantu balala.');
INSERT INTO "african_dev" VALUES(68,18,'68:18','Wabula beerabira okugamba nti Insh''allah.');
INSERT INTO "african_dev" VALUES(68,19,'68:19','Katonda n''agisindikira nabbambula w''omuliro ogwa gizikiriza ekiro nga beebase.');
INSERT INTO "african_dev" VALUES(68,20,'68:20','Okugenda okukya ngankalu nga enkungule.');
INSERT INTO "african_dev" VALUES(68,21,'68:21','Ku lunaku olulaganye baakeera nnyo era nga buli omu agenda atemya kumunne.');
INSERT INTO "african_dev" VALUES(68,22,'68:22','Ngabagamba nti muzuukuke mugende mu nimiro yammwe bwe muba ddala nga munakungula nga bwemwagambye.');
INSERT INTO "african_dev" VALUES(68,23,'68:23','Baatambula nga bogera bwama.');
INSERT INTO "african_dev" VALUES(68,24,'68:24','Nga bwebagamba nti tebagenda kukkiriza Masikiini yenna kuyingira mu ssamba yaabwe.');
INSERT INTO "african_dev" VALUES(68,25,'68:25','Baatambula kumakya okwo nga bakakasa nti bagenda kutuukiriza kyebaali basazeewo eggulo limu.');
INSERT INTO "african_dev" VALUES(68,26,'68:26','Bwebaalaba ennimiro yaabwe b''agamba nti tubuze.');
INSERT INTO "african_dev" VALUES(68,27,'68:27','Sinakindi Katonda yagizikirizza olwe kigendererwa kyaffe ekibi ekyobutakkiriza kugabirako ba Masikiini.');
INSERT INTO "african_dev" VALUES(68,28,'68:28','Eyali abasingako eddiini n''obuntu bulamu nabagamba nti ssabagambye nti obutawaako banaku buba bulyazamanyi bwennyini, era kati kyetuteekwa okukola kwekutendereza Katonda kuba tetunamanya yatusonyiye oba nedda.');
INSERT INTO "african_dev" VALUES(68,29,'68:29','Olwo nno nebagamba nti yayawukana Katonda waffe mazima twabadde tweyisizza bubi.');
INSERT INTO "african_dev" VALUES(68,30,'68:30','Awamu nekyo baatandika okuwaanyisiganya ebigambo nga abamu banenya bannabwe.');
INSERT INTO "african_dev" VALUES(68,31,'68:31','Nebagamba nti mazima ffe tubadde bajeemu.');
INSERT INTO "african_dev" VALUES(68,32,'68:32','Tusaba Katonda waffe emmali yaffe etokomose atuwanyisizeemu ebirungi, mazima ddala ew''a Katonda waffe yokka gyetusaba obuyambi.');
INSERT INTO "african_dev" VALUES(68,33,'68:33','Bwebityo nno ebibonerezo bwebiba naye wabula ebibonerezo by''olunaku lw’enkomerero byebisinga obukakali singa baba ddala nga bamanyi.');
INSERT INTO "african_dev" VALUES(68,34,'68:34','Mazima abatya Katonda baategekerwa e jjana ez''ebyengera ewa Katonda waabwe');
INSERT INTO "african_dev" VALUES(68,35,'68:35','Gwe ate abakkiriza twalibayisizza nga abonoonyi.');
INSERT INTO "african_dev" VALUES(68,36,'68:36','Mwaba ki, e nsonga muzisalawo mutya!');
INSERT INTO "african_dev" VALUES(68,37,'68:37','Simanyi mulinayo ekitabo mwemubisoma.');
INSERT INTO "african_dev" VALUES(68,38,'68:38','Era nga mukitaba ekyo mulimu nti mwe mulyesalirawo kyemwagala ku lunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(68,39,'68:39','Oba mulina e ndagano gye twabawa nga etuukira ddala ku lunaku lw''enkomerero ngebawa ebbeetu okwesalirawo nga bwemulaba.');
INSERT INTO "african_dev" VALUES(68,40,'68:40','Babuuze ani eyesowolayo abe nga akulembera endowooza eyo.');
INSERT INTO "african_dev" VALUES(68,41,'68:41','Oba balinayo (ababeesiguza) nga babayita babeezi ba Katonda, kale nno bajjanga nabo bwebaba nga bogera mazima.');
INSERT INTO "african_dev" VALUES(68,42,'68:42','Ku lunaku olulibeera olw''okubikkula ku ntumbwe. Balikowoolwa okuvunnamira Katonda naye nga tebasobola.');
INSERT INTO "african_dev" VALUES(68,43,'68:43','Nga amaaso gaabwe makakkamu olw''obuswavu, nga bajjudde okunyomebwa so nga ate baakowolwa okuvunnamira Katonda nga bakyali balamu ne batakikola.');
INSERT INTO "african_dev" VALUES(68,44,'68:44','Kale ndeka noyo alimbisa ebigambo bino tujja kubatwaliriza mpola mpola nga tebamanyi.');
INSERT INTO "african_dev" VALUES(68,45,'68:45','Era mbalindiriza, anti entegeka zange nnywevu.');
INSERT INTO "african_dev" VALUES(68,46,'68:46','Abaffe wali obasabye empeera nebakaluubirirwa kusasula.');
INSERT INTO "african_dev" VALUES(68,47,'68:47','Oba bamanyi ebyewala nga babiwandiika (nga bebyelamuza, era nga biraga nti bagenda kubeera bulungi).');
INSERT INTO "african_dev" VALUES(68,48,'68:48','Kale nno beera mugumikiriza ku kusalawo kwa Katondawo, tobeera nga ow''ekyennyanja (Nabbi Yunus) eyatuuka okukowoola (Katondawe okumutaasa) nga ajjudde obunyiikaavu.');
INSERT INTO "african_dev" VALUES(68,49,'68:49','Singa teyafuna kyengera ekyava ewa Katondawe (ekyokukiriza okwenenyakwe) yandikasukiddwa mu nsi e nkalu (etariiko bulamu) nga naye anenyezebwa.');
INSERT INTO "african_dev" VALUES(68,50,'68:50','Wabula Katonda yamulekera obwa Nabbi era namuteeka mu bantu abalongofu.');
INSERT INTO "african_dev" VALUES(68,51,'68:51','Abakafiiri basemberera ddala okukuzikiriza n''amaaso gaabwe amabi (olw''ekkonda eriri ku myoyo gyabwe) bwe bawulira Kur''ani era ne bagamba nti mazima yye (Nabbi) mulalu.');
INSERT INTO "african_dev" VALUES(68,52,'68:52','So ng''ate (Kur''ani) teri okugyako kya kujjukiza eri e bitonde.');
INSERT INTO "african_dev" VALUES(69,1,'69:1','Olunaku olwokwolesa amazima (olunaku lw’enkomerero).');
INSERT INTO "african_dev" VALUES(69,2,'69:2','Olunaku lwokwolesa amazima kyeki.');
INSERT INTO "african_dev" VALUES(69,3,'69:3','Naye omanyi olunaku olwokwolesa amazima kyeki.');
INSERT INTO "african_dev" VALUES(69,4,'69:4','Abantu ba Thamud n''aba A''adi baalimbisa olunaku olwentiisa.');
INSERT INTO "african_dev" VALUES(69,5,'69:5','Abantu ba Thamud bazikirizibwa nakubwatuka okwamanyi.');
INSERT INTO "african_dev" VALUES(69,6,'69:6','Ate ba A''adi nebazikirizibwa nakibuyaga owamanyi ennyo eyalimu obunyogovu.');
INSERT INTO "african_dev" VALUES(69,7,'69:7','Gweyabamazaamu ebiro musanvu nenaku munaana awatali kuyimiriramu, walaba abantu nga bagudde eri nga balinga enduli zemitende ezikutuddwako amasanso.');
INSERT INTO "african_dev" VALUES(69,8,'69:8','Gwe mu kulaba waliwo eyawona.');
INSERT INTO "african_dev" VALUES(69,9,'69:9','Farawo, n''abamukulembera, naabo abali kubyalo bya Luutu bayonoona.');
INSERT INTO "african_dev" VALUES(69,10,'69:10','Ekyatuusa buli bamu okujeemera omubaka Katonda gwe yabatumira. Katonda kye yava abakwata olukwata oluyitirivu.');
INSERT INTO "african_dev" VALUES(69,11,'69:11','Aba Nuhu amazzi bwegaabooga okubawonya twabasaabaliza mulyato.');
INSERT INTO "african_dev" VALUES(69,12,'69:12','Olwo nno tulyoke tukibafuulire mmwe ekyokwebuulirira era gakitegeere amatu agategeera.');
INSERT INTO "african_dev" VALUES(69,13,'69:13','Naye engombe bwerifuyibwa olufuuwa olusooka.');
INSERT INTO "african_dev" VALUES(69,14,'69:14','Ensi nensozi birisitulwa nebimementulwa omulundi gumu.');
INSERT INTO "african_dev" VALUES(69,15,'69:15','Olwo nno olunaku lw’enkomerero luliba lutuuse.');
INSERT INTO "african_dev" VALUES(69,16,'69:16','Eggulu ligenda kweyasaamu, nga ku lunaku olwo ligenda kuba lyabirivu.');
INSERT INTO "african_dev" VALUES(69,17,'69:17','Nga ba Malayika bali kumabbali galyo, nga waggulu waabwe kulunaku olwo ba Malayika munaana beebalisitula Arishi y''amukama Katonda wo.');
INSERT INTO "african_dev" VALUES(69,18,'69:18','Ku lunaku olwo mugenda kuleetebwa mubalibwe tewali kintu kyonna kyemukola kigenda kwekweka.');
INSERT INTO "african_dev" VALUES(69,19,'69:19','Naye oyo alikwasibwa ekitabo kye mu mukono gwe ogwaddyo agenda kugamba nti abange mujje musome ekitabo kyange.');
INSERT INTO "african_dev" VALUES(69,20,'69:20','Mazima nali nkakasa nti ngenda kubalibwa.');
INSERT INTO "african_dev" VALUES(69,21,'69:21','Oyo nno agenda kubeera mu bulamu obwesiimisa.');
INSERT INTO "african_dev" VALUES(69,22,'69:22','Mu jjana ey''a waggulu.');
INSERT INTO "african_dev" VALUES(69,23,'69:23','Ebibala byayo nga birengejjera kumpi.');
INSERT INTO "african_dev" VALUES(69,24,'69:24','Kale mulye era munywe muyogeyoge olwebyo byemwakulembeza mu nnaku ezaayita.');
INSERT INTO "african_dev" VALUES(69,25,'69:25','Naye oyo alikwasibwa ekitabo kye mu mukono gwe ogwa kkono aligamba nti zinsanze singa siwereddwa kitabo kyange.');
INSERT INTO "african_dev" VALUES(69,26,'69:26','Era nessimanya kubalibwa kwange.');
INSERT INTO "african_dev" VALUES(69,27,'69:27','Waakiri singa bwennafa kyekyamala nessizuukira.');
INSERT INTO "african_dev" VALUES(69,28,'69:28','Ebyobugagga byange tebingasizza.');
INSERT INTO "african_dev" VALUES(69,29,'69:29','Sikyalina byakuwoza.');
INSERT INTO "african_dev" VALUES(69,30,'69:30','Mu kiseera ekyo Katonda agenda kulagira nti mumukwate mumulijje.');
INSERT INTO "african_dev" VALUES(69,31,'69:31','Bwemumala mumukasuke mu muliro.');
INSERT INTO "african_dev" VALUES(69,32,'69:32','Bwemumala mumuyiseemu olujegere oluwanvu bwalwo obwenkana emikono nsanvu.');
INSERT INTO "african_dev" VALUES(69,33,'69:33','Anti yali takkiriza Katonda owekitiibwa.');
INSERT INTO "african_dev" VALUES(69,34,'69:34','Era nga takubiriza kuliisa banaku.');
INSERT INTO "african_dev" VALUES(69,35,'69:35','N''olwekyo olwaleero wano talinawo muntu amulumirwa.');
INSERT INTO "african_dev" VALUES(69,36,'69:36','Nga bwatalina kyakulya okugyako masirasira.');
INSERT INTO "african_dev" VALUES(69,37,'69:37','Ebintu ebitayinza kulibwa okugyako abonoonyi.');
INSERT INTO "african_dev" VALUES(69,38,'69:38','Tewali kingana kulayira ebyo byemulaba.');
INSERT INTO "african_dev" VALUES(69,39,'69:39','Nebyo byemutalaba');
INSERT INTO "african_dev" VALUES(69,40,'69:40','Mazima Kur''ani bigambo bya Katonda byeyassa ku mubaka (Nabbi Muhammad) ow''ekitiibwa.');
INSERT INTO "african_dev" VALUES(69,41,'69:41','Ssi bigambo bya mutontomi naye Iman yammwe yentono.');
INSERT INTO "african_dev" VALUES(69,42,'69:42','Era sibigambo bya mulaguzi naye mufumitiriza kitono (nemutasobola ku byawula).');
INSERT INTO "african_dev" VALUES(69,43,'69:43','Byassibwa ku ye okuva eri omulezi w''ebitonde.');
INSERT INTO "african_dev" VALUES(69,44,'69:44','Singa yetantala natwogeza ebigambo byetutaamutuma.');
INSERT INTO "african_dev" VALUES(69,45,'69:45','Twalimukutteko n''amanyi.');
INSERT INTO "african_dev" VALUES(69,46,'69:46','Olwo nno netumukutula omusuwa gw''obulamu.');
INSERT INTO "african_dev" VALUES(69,47,'69:47','Era tewali mumwe ayinza kutulemesa kutuukiriza ekyo kuye.');
INSERT INTO "african_dev" VALUES(69,48,'69:48','Mazima yyo Kur''ani kyakwebuulirira eri abatya Katonda.');
INSERT INTO "african_dev" VALUES(69,49,'69:49','Era mazima ddala tumanyi nti mu mmwe mulimu abalimbisa (Kur''ani).');
INSERT INTO "african_dev" VALUES(69,50,'69:50','Mazima okugirimbisa (Kur''ani) kugenda kubeera kwejjusa kunene eri abakafiiri.');
INSERT INTO "african_dev" VALUES(69,51,'69:51','Mazima yo Kur''ani ge mazima aga namaddala.');
INSERT INTO "african_dev" VALUES(69,52,'69:52','Kale nno tendereza erinnya lya Mukama Katonda wo owekitiibwa.');
INSERT INTO "african_dev" VALUES(70,1,'70:1','"Mu ngeri y''okunyomerera" omu ku bakafiiri" yasaba oba ebibonerezo bijja bijje, so nga ate ddala byakujja.');
INSERT INTO "african_dev" VALUES(70,2,'70:2','Bituuke ku bakafiiri tewali agenda kubigaana kubatuukako.');
INSERT INTO "african_dev" VALUES(70,3,'70:3','Nga biva eri Katonda nnyini madaala agalinya muggulu.');
INSERT INTO "african_dev" VALUES(70,4,'70:4','Ba Malayika nga ne Jiburilu mwomutwalidde mwebayita nga bolekera eri Katonda, ekibatwalira olunaku lumu lwokka naye nga ate lwenkanankana e myaka e mitwalo etaano (egy''okunsi).');
INSERT INTO "african_dev" VALUES(70,5,'70:5','Kale nno gumikiriza olugumikiriza olulungi.');
INSERT INTO "african_dev" VALUES(70,6,'70:6','Mazima bbo balaba ebibonerezo nga biri wala.');
INSERT INTO "african_dev" VALUES(70,7,'70:7','Ate nga ffe tulaba nga biri kumpi.');
INSERT INTO "african_dev" VALUES(70,8,'70:8','Ku lunaku eggulu lwelirikulukuta nga omuzigo omusanuuse.');
INSERT INTO "african_dev" VALUES(70,9,'70:9','Ate ensozi neziba nga pamba asunsuddwa.');
INSERT INTO "african_dev" VALUES(70,10,'70:10','Era tewali wamukwano agenda kubuuza bikwata ku ffa nfe we.');
INSERT INTO "african_dev" VALUES(70,11,'70:11','Bagenda kuba nga beeraba (naye nga tebayinza kuyambagana). Omwonoonyi olwokwagala okuwona ebibonerezo byolunaku olwo, aliyagala okwenunula nga awaayo abaanabe.');
INSERT INTO "african_dev" VALUES(70,12,'70:12','Mukyalawe ne mugandawe.');
INSERT INTO "african_dev" VALUES(70,13,'70:13','N''ekika kye ekimukuuma.');
INSERT INTO "african_dev" VALUES(70,14,'70:14','Nabantu bonna abali munsi, nga ekyo akikola awone ebibonerezo bya Katonda.');
INSERT INTO "african_dev" VALUES(70,15,'70:15','Tekigenda kuba nga bwayagala, mazima ayolekedde muliro ogubumbujja.');
INSERT INTO "african_dev" VALUES(70,16,'70:16','Ogusiriiriza ddala eddiba.');
INSERT INTO "african_dev" VALUES(70,17,'70:17','Gugenda kukowoola buli eyakubayo amabega nakyuka okuva ku mazima.');
INSERT INTO "african_dev" VALUES(70,18,'70:18','Era nga yakungaanyanga emmali nagitereka "natagyamu mugabo gugitukuza".');
INSERT INTO "african_dev" VALUES(70,19,'70:19','Mazima omuntu yatondebwa nga wamulugube.');
INSERT INTO "african_dev" VALUES(70,20,'70:20','Nga bwatuukibwako akabi ayitirira okutya.');
INSERT INTO "african_dev" VALUES(70,21,'70:21','Ate bwaba afunye akalungi ayitirira obukodo.');
INSERT INTO "african_dev" VALUES(70,22,'70:22','Okugyako abo abayimirizaawo e sswala.');
INSERT INTO "african_dev" VALUES(70,23,'70:23','Abo nga okusaala kwabwe kwalubeerera.');
INSERT INTO "african_dev" VALUES(70,24,'70:24','Era abo mu by''obugagga byabwe mulimu omugabo ogumanyiddwa.');
INSERT INTO "african_dev" VALUES(70,25,'70:25','Guweebwa abasabi n''abafuna mpola.');
INSERT INTO "african_dev" VALUES(70,26,'70:26','Era bebo abakakasa o kubeerawo kw''olunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(70,27,'70:27','Era abo abatya ebibonerezo bya Mukama Katonda waabwe.');
INSERT INTO "african_dev" VALUES(70,28,'70:28','Kubanga mazima ebibonerezo byamukama Katonda sibyakuwonwa.');
INSERT INTO "african_dev" VALUES(70,29,'70:29','Era abo abakuuma obwerere bwabwe.');
INSERT INTO "african_dev" VALUES(70,30,'70:30','Okugyako ku bakyala babwe oba abazaana abali mu mikono gyabwe, anti bo tebanenyezebwa kwabo.');
INSERT INTO "african_dev" VALUES(70,31,'70:31','Omuntu asukka ku abo, abo nno be babuusi bensalo.');
INSERT INTO "african_dev" VALUES(70,32,'70:32','Era abantu abalungi abogeddwako waggulu, bebatuukiriza obwesigwa bwabwe nendagaano zaabwe.');
INSERT INTO "african_dev" VALUES(70,33,'70:33','Era abo abeesimbu mu bujulizi bwabwe.');
INSERT INTO "african_dev" VALUES(70,34,'70:34','Era abo abasaala e sswala zaabwe mu bujjuvu.');
INSERT INTO "african_dev" VALUES(70,35,'70:35','Abo nno balibeera mu jjana nga baweebwa ebitiibwa.');
INSERT INTO "african_dev" VALUES(70,36,'70:36','Lwaki abakafiiri bajja gyoli nga banguwa "so nga ate bakulimbisa".');
INSERT INTO "african_dev" VALUES(70,37,'70:37','Nga bali ku mukono gwo ogwa ddyo ne ku mukono ogwa kkono mu bibinja.');
INSERT INTO "african_dev" VALUES(70,38,'70:38','Buli omu kubo asuubira okuyingira e jjana eyebyengera.');
INSERT INTO "african_dev" VALUES(70,39,'70:39','Nedda sibwekiri, mazima ffe twabakola nga tubajja mwebyo byebamanyi.');
INSERT INTO "african_dev" VALUES(70,40,'70:40','Ndayira Katonda nannyini buvanjuba nobugwanjuba, mazima ffe tusobolera ddala.');
INSERT INTO "african_dev" VALUES(70,41,'70:41','Okubawanyisaamu abalungi ababasinga era tetuli ffe baakulemesebwa.');
INSERT INTO "african_dev" VALUES(70,42,'70:42','Ggwe baveeko babeere mu bukafiiri bwabwe nga bwebazannya okutuusa lwebalisisinkana olunaku lwabwe, olwo lwebalaganyisibwa.');
INSERT INTO "african_dev" VALUES(70,43,'70:43','Olunaku lwebalifuluma mu ntaana zaabwe nga baduma nga balinga abagenda eri ebendera ebatereddwawo.');
INSERT INTO "african_dev" VALUES(70,44,'70:44','Amaaso gabwe makakkamu, obuswavu nga bubabuutikidde, olwo lwe lunaku lwe balaganyisibwa.');
INSERT INTO "african_dev" VALUES(71,1,'71:1','Mazima ffe twatuma Nuhu eri abantu be. Nti tiisa abantubo nga ebibonerezo abiruma ennyo tebinabajjira.');
INSERT INTO "african_dev" VALUES(71,2,'71:2','Naabagamba nti mazima nze ndi mutiisa omunnyonnyozi.');
INSERT INTO "african_dev" VALUES(71,3,'71:3','Nti musinze Katonda era mu mutye nange mungondere.');
INSERT INTO "african_dev" VALUES(71,4,'71:4','Olwo nno ajja kubasonyiwa ebyonoono byammwe (bye mwakola nga temunnaba kukkiriza mubaka era alyoke abawonye okuzikirira n''ebibonerezo ebibamalawo olumu). Abalindirize abatuuse ku ntuuko ze yabagerera. Mazima entuuko za Katonda bweziba zituuse tezirindirizibwa singa muba mumanyi.');
INSERT INTO "african_dev" VALUES(71,5,'71:5','"Awamu nekyo tebakkiriza", Nuhu naagamba nti ayi Katonda wange nkowodde abantu bange ekiro n''emisana.');
INSERT INTO "african_dev" VALUES(71,6,'71:6','Wabula okukowoola kwange tekubongedde okugyako okunnesamba.');
INSERT INTO "african_dev" VALUES(71,7,'71:7','Mazima buli lwembadde mbakowoola badde eri okusonyiwa kwo bassa ennwe zaabwe mu matu gaabwe, ne beebikka engoye zaabwe, ne bakalambira era ne beekuluntaza olw''ekuluntaza.');
INSERT INTO "african_dev" VALUES(71,8,'71:8','Ate era mazima mbakowodde mulwatu.');
INSERT INTO "african_dev" VALUES(71,9,'71:9','Era njogedde nabo mu lujjudde nga bwenjogedde nabo kinnomu.');
INSERT INTO "african_dev" VALUES(71,10,'71:10','Nembagamba nti mwegayirire Katonda wa mmwe anti mazima yye bulijjo musonyiyi.');
INSERT INTO "african_dev" VALUES(71,11,'71:11','Olwonno ajja kubatonnyeseza enkuba ey''omuddiringanwa.');
INSERT INTO "african_dev" VALUES(71,12,'71:12','Era abawe mu bungi emmaali n''abaana abawe n''amasamba abateeremu n''emigga.');
INSERT INTO "african_dev" VALUES(71,13,'71:13','Naye lwaki Katonda temumuwa kitiibwa.');
INSERT INTO "african_dev" VALUES(71,14,'71:14','Sso nga yabatonda ngabayisa mumitendera egyenjawulo.');
INSERT INTO "african_dev" VALUES(71,15,'71:15','Abaffe temulaba engeri Katondawo gyeyakolamu eggulu omusanvu nga myaliiro.');
INSERT INTO "african_dev" VALUES(71,16,'71:16','Nassaamu omwezi nga kitangaala nga bweyassamu enjuba nga eyaka.');
INSERT INTO "african_dev" VALUES(71,17,'71:17','Katonda yabatandika ng''abajja muttaka nemujja nga mukula nga ebimera bwe bikula.');
INSERT INTO "african_dev" VALUES(71,18,'71:18','Oluvanyuma abazza mu ttaka nga era bwagenda okubazuukiza alibagyemu bulambalamba (nga temuyise mu mitendera nga ebimera).');
INSERT INTO "african_dev" VALUES(71,19,'71:19','Era Katonda yabateerawo ensi nga esoboka okuwangalirwako.');
INSERT INTO "african_dev" VALUES(71,20,'71:20','Mube nga musobola okutambulira muyo nga mukwata amakubo ge muba mwagadde.');
INSERT INTO "african_dev" VALUES(71,21,'71:21','Nuhu kwekugamba nti, ayi mukama wange mazima ddala abantu abo banjemedde nebasalawo okugoberera oyo yenna emmaali ye n''abaana be bebitayongera okugyako okufafaganirwa.');
INSERT INTO "african_dev" VALUES(71,22,'71:22','Nebakola olukwe sinziggu (bwebagezaako okutta Nuhu).');
INSERT INTO "african_dev" VALUES(71,23,'71:23','Era nebagamba nti temugeza nemuva ku ba Katonda ba mmwe naddala Wadda ne Suwa''a ne Yagutha ne Yauka ne Nasira.');
INSERT INTO "african_dev" VALUES(71,24,'71:24','Olwekyo baabuza abantu bangi (Nange kyenva nkusaba nti) mukama Katonda abantu abeyisa obubi tobongera okugyako kubula.');
INSERT INTO "african_dev" VALUES(71,25,'71:25','Olwobwonoonyi bwabwe baazikiziribwa nebayingizibwa omuliro baali tebayinza kufuna abataasa yenna.');
INSERT INTO "african_dev" VALUES(71,26,'71:26','Ye Nuhu kwekugamba nti, ayi Katonda wange toleka kunsi mukafiiri yenna.');
INSERT INTO "african_dev" VALUES(71,27,'71:27','Anti singa obalekawo bajja kubuza abaddu bo era buli gwebanazaala ajja kuba mwononefu omukafiiri.');
INSERT INTO "african_dev" VALUES(71,28,'71:28','Ayi Mukama Katonda wange nsonyiwa ne bakadde bange na buli yenna anaayingira mu nju yange kavuna anaaba nga mukkiriza, era nkusaba osonyiwe abakkiriza abasajja n''abakkiriza abakazi, wabula bo abeeyisa obubi tobongera okugyako okuzikirira.');
INSERT INTO "african_dev" VALUES(72,1,'72:1','Bagambe ggwe Nabbi Muhammad nti nafuna obubaka nga waliwo amajinni agamu agaawuliriza nga nsoma Kur''ani negagamba nti mazima Kur''ani tugiwulidde nga yewuunyisa.');
INSERT INTO "african_dev" VALUES(72,2,'72:2','Ekowoola oyo agiwuliriza okudda eri obulungamu era naffe tugikkirizza, era tetugenda kuddayo kugatta ku Katonda kintu kirala.');
INSERT INTO "african_dev" VALUES(72,3,'72:3','Era mazima ekitiibwa kya Mukama Katonda waffe kisukkulumu, tayinza kuba nti yeteerawo mukyala wadde omwana.');
INSERT INTO "african_dev" VALUES(72,4,'72:4','Era mazima ddala ababuyabuya muffe baayogera nga ku Katonda ebigambo ebitemerere.');
INSERT INTO "african_dev" VALUES(72,5,'72:5','Era mazima ddala ffe twalowoozanga nti abantu na majjini baali tebayinza kwogera ku Katonda byabulimba.');
INSERT INTO "african_dev" VALUES(72,6,'72:6','Waliwo abantu abamu abaasabanga obuyambi okuva eri amajinni ekyagongera okuwaganyala.');
INSERT INTO "african_dev" VALUES(72,7,'72:7','Era mazima ddala amajinni gaalowooza nga bwemwalowooza nti Katonda yali tagenda kutuma mubaka yenna.');
INSERT INTO "african_dev" VALUES(72,8,'72:8','Era mazima ffe tubadde tugezaako okugenda ku ggulu netulisanga nga lijjudde abakuumi abakambwe nebitawuliro.');
INSERT INTO "african_dev" VALUES(72,9,'72:9','Era mazima ffe waliwo enfo zetwatulangamu netuwuliriza ebifaayo, naye kati agezaako okuwuliriza asanga ebitawuliro nga bimuteeze.');
INSERT INTO "african_dev" VALUES(72,10,'72:10','Wabula tetumanyi mu kukola ekyo kwali kulumya bitonde ebiri ku nsi oba Mukama Katonda wabyo yabyagaliza bulungamu.');
INSERT INTO "african_dev" VALUES(72,11,'72:11','Mazima nemuffe mulimu abalongoofu n''abatali era tuli ebibiina ebyenjawulo.');
INSERT INTO "african_dev" VALUES(72,12,'72:12','Era ffe tuli bakakafu nti tetuyinza kulemesa Katonda ku nsi era nga bwetutayinza kumulemesa ne tumuddukako.');
INSERT INTO "african_dev" VALUES(72,13,'72:13','Era mazima ddala bwe twawulira obulungamu twabukkiriza, nga ate oyo yenna akkiriza Mukama Katonda we tayinza kutya butasasulwa oba okunyigirizibwa.');
INSERT INTO "african_dev" VALUES(72,14,'72:14','Mazima ddala muffe mulimu abasiramu nga bwemulimu abaabula. Oyo yenna asiramuka, abo nno bebaba banoonya amazima mu bwegendereza.');
INSERT INTO "african_dev" VALUES(72,15,'72:15','Wabula bo abaabula be bagenda okubeera enku z''omuliro Jahannama.');
INSERT INTO "african_dev" VALUES(72,16,'72:16','Naye singa abantu banywerera ku kkubo ettuufu twalibawadde enkuba mu bungi.');
INSERT INTO "african_dev" VALUES(72,17,'72:17','Tulyoke tubageseze ngeri ki gye be bazaamu e byengera, oyo yenna eyeesamba ebigambo bya Katondawe agenda kumubonereza n''ebibonerezo ebikambwe.');
INSERT INTO "african_dev" VALUES(72,18,'72:18','Mazima ddala emizigiti gya kusinzizamu Katonda yekka, kale nno temugatta ku Katonda ekintu kyonna.');
INSERT INTO "african_dev" VALUES(72,19,'72:19','Era mazima ddala omuddu w''a Katonda (Nabbi Muhammad) bweyayimirira nga asaala amajinni gaasembera negamwetoloola kumpi kumwetuumako nga gali mu bibinja.');
INSERT INTO "african_dev" VALUES(72,20,'72:20','Bagambe (Gwe Nabbi Muhammad) nti mazima nze nsinza Katonda wange ate si mugattako kintu kirala kyonna.');
INSERT INTO "african_dev" VALUES(72,21,'72:21','(Ggwe Nabbi Muhammad) bagambe nti tekiri mu mikono gyange okubaggyirawo obuzibu obuba bubatuuseeko nga bwe kitali mu busobozi bwange okubafunira obulungamu.');
INSERT INTO "african_dev" VALUES(72,22,'72:22','Era bagambe nti mazima nze tewali muntu yenna ayinza kumponya bibonerezo bya Katonda singa mba mujeemedde. Era sisobola kufuna buddukiro nga si bwa Katonda.');
INSERT INTO "african_dev" VALUES(72,23,'72:23','Nekiba nga ekisigadde kubatuusako bubaka obuva eri Katonda, naye abo bonna abajeemera Katonda n''omubakawe baakuyingira omuliro Jahannama, era nga b''akugubeeramu olubeerera.');
INSERT INTO "african_dev" VALUES(72,24,'72:24','Okutuusizza ddala bwe balibanga balabye ebyo ebyabalagaanyisibwa olwo nno lwe bajja okukakasa nti ani alina omutaasa omunafu neggye ettono.');
INSERT INTO "african_dev" VALUES(72,25,'72:25','Bagambe ggwe Nabbi Muhammad nti mazima nze simanyi oba e bibonerezo bye mulaganyisibwa biri kumpi oba byakujja oluvanyuma lwebbanga eggwanvu.');
INSERT INTO "african_dev" VALUES(72,26,'72:26','Ye yekka, Katonda yaamanyi ebitamanyiddwa ng''ate talina yenna gwabikkulira e byamabye.');
INSERT INTO "african_dev" VALUES(72,27,'72:27','Okugyako oyo gwaba alonze okubeera omubaka (kale nno oyo amubikkulira ebimu ku byamabye) era amuteerawo obukuumi bwa Malayika nga bamwetolodde, Sitani obutasobola kumutuukako.');
INSERT INTO "african_dev" VALUES(72,28,'72:28','Nabbi alyoke amanye nti mazima ba Nabbi baatuukiriza obubaka bwa Katonda waabwe (nga naye bwa butuukiriza) era bamanye nti Katonda amanyi ebyo byonna bye bakola nga era bwamanyi buli kintu kyonna.');
INSERT INTO "african_dev" VALUES(73,1,'73:1','Owange ggwe eyeebikkirira (olwokutya okwakutuukako bwewafuna obubaka okuva ku Malayika Jiburilu omulundi ogwasoka).');
INSERT INTO "african_dev" VALUES(73,2,'73:2','Golokoka osaale ekiro mpozzi lekayo akaseera katono.');
INSERT INTO "african_dev" VALUES(73,3,'73:3','Oyinza okusaala ekitundu kye kiro oba okukendezaako katono.');
INSERT INTO "african_dev" VALUES(73,4,'73:4','Oba oyinza okwongerako era bwoba osoma Kur''ani giwoomese.');
INSERT INTO "african_dev" VALUES(73,5,'73:5','Mazima ffe tujja kukutikka e kigambo ekizito.');
INSERT INTO "african_dev" VALUES(73,6,'73:6','Anti e sswala enzuukukire y''esinga okubuulirira omuntu (olw''okwerekereza otulo) era nga nebisomerwamu byebisinga okusomwa munsoma ennambulukufu.');
INSERT INTO "african_dev" VALUES(73,7,'73:7','Anti obudde obwemisana oleebukana nnyo (mu bintu ebirala).');
INSERT INTO "african_dev" VALUES(73,8,'73:8','Kale nno tendereza erinnya lya Mukama Katondawo, e kiseera kyonna okimuwe.');
INSERT INTO "african_dev" VALUES(73,9,'73:9','Mukama Katonda omufuziwo buvanjuba n''obugwanjuba tewali kirala kyonna kisaanye kusinzibwa okugyako yye, kale nno gwoba wesigamako.');
INSERT INTO "african_dev" VALUES(73,10,'73:10','Era ogumikirize kw''ebyo bye boogera era obaveeko mu ngeri e nnungi.');
INSERT INTO "african_dev" VALUES(73,11,'73:11','Naye ndekera abalimbisa, abagagga fugge, era balindirize akaseera katono.');
INSERT INTO "african_dev" VALUES(73,12,'73:12','Mazima tulina empingu n''omuliro Jahiim.');
INSERT INTO "african_dev" VALUES(73,13,'73:13','N''ebyokulya ebitamirika n''ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(73,14,'73:14','Ebiribaawo ku lunaku e nsi n''ensozi lw''ebirikankana e nsozi ne zifuuka entuumo z''omusenyu ogufuumuka.');
INSERT INTO "african_dev" VALUES(73,15,'73:15','(Okuwona ebyo mukozese omukisa gwokuba nti) mazima ffe twabatumira omubaka nga yajja okubawaako obujulizi okufaananako nga bwe twatumira Firawo omubaka.');
INSERT INTO "african_dev" VALUES(73,16,'73:16','Wabula Firawo yajeemera omubaka, kyetwava tumukwata olukwata olwamanyi.');
INSERT INTO "african_dev" VALUES(73,17,'73:17','Kakati bwe mujeema muliwona mutya olunaku olulifuula abaana abato abakadde.');
INSERT INTO "african_dev" VALUES(73,18,'73:18','Ate nga eggulu ligenda kuyulikamu olwebiribaawo ku lunaku olwo, nga ate Katonda kyalagaanyisa kiteekwa okutuukirira.');
INSERT INTO "african_dev" VALUES(73,19,'73:19','(Bwekiba nga bwekityo bwekiri), mazima ddala muteekwa mutwale Kur''ani nga kyakwebuulirira. N''olwekyo ayagala okuwona ateekeddwa okukwata ekkubo erimuzza eri Katonda we.');
INSERT INTO "african_dev" VALUES(73,20,'73:20','Mazima Katonda wo akimanyi nti oyimirira ekiro ng''osaala emirundi egimu kumpi bibiri byakusatu byekiro, oluusi kimu kyakubiri nga n''olundi kimu kyakusatu era nga naabamu ku bakkiriza abali naawe bwebatyo bwe bakola nga ate Katonda yaawa ebiragiro ebikola ekiro n''emisana, era nga amanyi nti temuyinza kumalayo kiro kyonna nga mukola Ibaada kye yava abawa ekisonyiwo, kale nno musome kyemuba musobodde mu Kur''ani nga musaala ekiro anti amanyi nti mu mmwe mulimu abalwadde nga n''abalala bagenda kubeera mu safari nga batambudde mu nsi olwokunoonya ebigabwa bya Katonda nga nabandi bagenda kubeera mu ntabaalo z''okuweereza mu kkubo lya Katonda n''olwekyo musome ekyo kye muba musobodde mu Kur''ani nga musaala e sswala ya tahajjudi era muyimirizeewo e sswala eza faradha mutoole ne zzaka mu ngeri y''emu muwole Katonda oluwola olulungi nga muwaayo saddaaka. Akalungi konna kemweterekera mugenda ku kasanga mu maaso ga Katonda. Okukola ekyo kirungi era kyekirimu empeera ennene era musabe Katonda ekisonyiwo mazima Katonda musonyiyi oweekisa.');
INSERT INTO "african_dev" VALUES(74,1,'74:1','Owange ggwe eyeebikiridde (olwokutya Malayika Jiburilu lwe yakuleetera obubaka okuva eri Katonda).');
INSERT INTO "african_dev" VALUES(74,2,'74:2','Golokoka otiise abantu ebibonerezo bya Katonda.');
INSERT INTO "african_dev" VALUES(74,3,'74:3','Ne Mukama Katondawo yekka gwoba ogulumiza.');
INSERT INTO "african_dev" VALUES(74,4,'74:4','N''engoyezo zitukuze.');
INSERT INTO "african_dev" VALUES(74,5,'74:5','Amasanamu geewalire ddala.');
INSERT INTO "african_dev" VALUES(74,6,'74:6','Tokoleranga abantu obulungi ng''ogenderera kufunamu bulungi kuno ku nsi.');
INSERT INTO "african_dev" VALUES(74,7,'74:7','Beeranga mugumikiriza osobole okufuna okusiima kwa Mukama Katondawo.');
INSERT INTO "african_dev" VALUES(74,8,'74:8','Engombe bwerifuuyibwa (kulwokuzuukira).');
INSERT INTO "african_dev" VALUES(74,9,'74:9','Olunaku olwo lugenda kuba lunaku luzito.');
INSERT INTO "african_dev" VALUES(74,10,'74:10','Terugenda kuba lwangu eri abakafiiri.');
INSERT INTO "african_dev" VALUES(74,11,'74:11','(Owange ggwe Nabbi Muhammad) ndeka nooyo gwe nnetondera.');
INSERT INTO "african_dev" VALUES(74,12,'74:12','Nemuwa ebyobugagga ebiyitirivu.');
INSERT INTO "african_dev" VALUES(74,13,'74:13','Nemuwa nabaana abatutumufu.');
INSERT INTO "african_dev" VALUES(74,14,'74:14','Ne mugaziyiriza olugaziya (e byenfuna obukulembeze n''ekitiibwa).');
INSERT INTO "african_dev" VALUES(74,15,'74:15','Oluvanyuma alulunkanira okumwongera okusingako awo.');
INSERT INTO "african_dev" VALUES(74,16,'74:16','Nedda sijja kumwongera kuba yawakanya ebigambo byaffe (Kur''ani).');
INSERT INTO "african_dev" VALUES(74,17,'74:17','Nja kumussaako e bibonerezo e bizito.');
INSERT INTO "african_dev" VALUES(74,18,'74:18','Anti mazima yye (Al Walid bin Mugiira) yalowooza era naasalawo okuvvoola Kur''ani.');
INSERT INTO "african_dev" VALUES(74,19,'74:19','Nakolimirwa okusinziira ku ngeri gyeyasalawo.');
INSERT INTO "african_dev" VALUES(74,20,'74:20','Era ddala yakolimirwa ku ngeri gyeyasalawo.');
INSERT INTO "african_dev" VALUES(74,21,'74:21','Oluvanyuma yakirowozaako.');
INSERT INTO "african_dev" VALUES(74,22,'74:22','Olwo nno ne yeekaba najja emitaafu.');
INSERT INTO "african_dev" VALUES(74,23,'74:23','Ne kyaddirira ebituufu yabivaako neyekuluntaza.');
INSERT INTO "african_dev" VALUES(74,24,'74:24','Olwo nno n''alangirira nti e bigambo bya Muhammad temuli wabula ddogo erijjibwa ku balogo abasooka.');
INSERT INTO "african_dev" VALUES(74,25,'74:25','Era byayogera tebisukka ku kuba bigambo bya muntu!');
INSERT INTO "african_dev" VALUES(74,26,'74:26','(Katonda naagamba nti:) nja kumuyingiza omuliro Sakara.');
INSERT INTO "african_dev" VALUES(74,27,'74:27','Naye omanyi Sakara kyeki.');
INSERT INTO "african_dev" VALUES(74,28,'74:28','Gwo muliro ogutagenda kulekawo kintu wadde okukitaliza.');
INSERT INTO "african_dev" VALUES(74,29,'74:29','Gwokera ddala negusiriiza eddiba lyomuntu.');
INSERT INTO "african_dev" VALUES(74,30,'74:30','Gukuumibwa ba Malayika kkumi na mwenda.');
INSERT INTO "african_dev" VALUES(74,31,'74:31','Ate omulimu gwokukuuma omuliro tetwaguwa okugyako ba Malayika, era omuwendo gwabwe tetwaguteekawo okugyako nga kikemo eri abatakkiriza Katonda, so nga abo abaweebwa ekitabo ekyo kibongera kukakasa (ebyo bye baalina), ate bo abakkiriza (abagoberezi ba Nabbi Muhammad) kyongera kunyweza bukkiriza bwabwe, era abaweebwa ekitabo n''abakkiriza babe nga tebasigalamu kakunkuna, wabula nga (abanafunsi) abalina obulwadde mu mitima gyabwe n''abatakkiriza bagamba nti Katonda kiki kyagenderera mu kifananyi kino, bwatyo nno Katonda abuza nalungamya oyo gwaba ayagadde, ate tewali amanyi ggye lya Mukama Katondawo okugyako yye, ebyo byonna ebyogeddwa tewali kibigendererwamu okugyako okubuulirira omuntu.');
INSERT INTO "african_dev" VALUES(74,32,'74:32','Nedda (tekiri ng''abantu abamu bwe balowooza), mazima ndayidde omwezi.');
INSERT INTO "african_dev" VALUES(74,33,'74:33','Ne ndayira n''ekiro mu kiseera we kiggwerako.');
INSERT INTO "african_dev" VALUES(74,34,'74:34','Ne ndayira n''amakya ngabusasaana.');
INSERT INTO "african_dev" VALUES(74,35,'74:35','Mazima ggwo (omuliro Sakara) kye kimu ku binene.');
INSERT INTO "african_dev" VALUES(74,36,'74:36','Ebyandibadde bibuulirira omuntu.');
INSERT INTO "african_dev" VALUES(74,37,'74:37','Eri oyo aba ayagadde mu mmwe okudda eri Katondawe oba okumwesamba.');
INSERT INTO "african_dev" VALUES(74,38,'74:38','Buli muntu agenda kubeera musingo gwa mirimugye.');
INSERT INTO "african_dev" VALUES(74,39,'74:39','Okugyako abo abalibeera ku mukono ogwaddyo.');
INSERT INTO "african_dev" VALUES(74,40,'74:40','Bagenda kubeera mu jjana nga babuuza ebifa.');
INSERT INTO "african_dev" VALUES(74,41,'74:41','Ku bonoonyi.');
INSERT INTO "african_dev" VALUES(74,42,'74:42','Nga babagamba nti ki ekibayingizza omuliro.');
INSERT INTO "african_dev" VALUES(74,43,'74:43','Baligamba nti tetubangako mu basaaze.');
INSERT INTO "african_dev" VALUES(74,44,'74:44','Era tetwaliisanga banaku.');
INSERT INTO "african_dev" VALUES(74,45,'74:45','Twabeeranga nabakubi bambekuulo.');
INSERT INTO "african_dev" VALUES(74,46,'74:46','Era twali tulimbisa olunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(74,47,'74:47','Okutuusa okufa lw''ekwatutuukako.');
INSERT INTO "african_dev" VALUES(74,48,'74:48','Kale tebagenda kuganyulwa mu kuwolereza kwa bawolereza.');
INSERT INTO "african_dev" VALUES(74,49,'74:49','Kakano kiki ekibaddusa okuva ku kubuulirira kwa (Kur''ani kuno).');
INSERT INTO "african_dev" VALUES(74,50,'74:50','Nga balinga entulege ezigobeddwa.');
INSERT INTO "african_dev" VALUES(74,51,'74:51','Ezidduka e mpologoma.');
INSERT INTO "african_dev" VALUES(74,52,'74:52','Wabula buli omu mubo ayagala aweebwe ebiwandiiko ebibikuddwa.');
INSERT INTO "african_dev" VALUES(74,53,'74:53','Nedda ekituufu kiri nti bbo tebakkiriza nkomerero.');
INSERT INTO "african_dev" VALUES(74,54,'74:54','Mazima ddala yo (Kur''ani) kyakubuulirira.');
INSERT INTO "african_dev" VALUES(74,55,'74:55','Oyo aba ayagadde yeebulirira nayo.');
INSERT INTO "african_dev" VALUES(74,56,'74:56','Wabula tebayinza kwebuulirira okugyako nga Katonda ayagadde, yye ye nanyini kutiibwa era ye nanyini kusonyiwa.');
INSERT INTO "african_dev" VALUES(75,1,'75:1','Tewali kingaana kulayira lunaku lwakuzuukira.');
INSERT INTO "african_dev" VALUES(75,2,'75:2','Era nga bwewatali kingaana kulayira omwoyo ogwejjusa.');
INSERT INTO "african_dev" VALUES(75,3,'75:3','Abaffe omuntu alowooza nti tetugenda kusobola kuzzaawo magumbage.');
INSERT INTO "african_dev" VALUES(75,4,'75:4','Nedda tukisobolera ddala nga bwetulisobola okuzzaawo ennwe zaabwe.');
INSERT INTO "african_dev" VALUES(75,5,'75:5','Wabula omuntu emirundi egisinga ayagala nnyo okuwangaalira mu kwonoona.');
INSERT INTO "african_dev" VALUES(75,6,'75:6','Ngabuuza (mu ngeri ey''okujerega n''okuwakanya) nti olunaku lwokuzuukira lulibaawo ddi.');
INSERT INTO "african_dev" VALUES(75,7,'75:7','Ddala lugenga kubaawo nga n''amaaso g''abantu galiralambala olwokutya.');
INSERT INTO "african_dev" VALUES(75,8,'75:8','Ekitangala kyomwezi kigenda kuggwawo.');
INSERT INTO "african_dev" VALUES(75,9,'75:9','Enjuba n''omwezi birigattibwa (nga tewakyasobola kubaawo kiro namisana).');
INSERT INTO "african_dev" VALUES(75,10,'75:10','Ku lunaku olwo omuntu agenda kwebuuza nti nzirukirewa!.');
INSERT INTO "african_dev" VALUES(75,11,'75:11','Kyekyo ddala, tewagenda kubaawo muntu weyekweka.');
INSERT INTO "african_dev" VALUES(75,12,'75:12','Obuddo ku lunaku olwo bujja kuba eri Mukama Katondawo.');
INSERT INTO "african_dev" VALUES(75,13,'75:13','Ku lunaku olwo omuntu agenda kutegeezebwa ebyo byonna byeyakola nebyataakola.');
INSERT INTO "african_dev" VALUES(75,14,'75:14','Wabula ate ddala omuntu ye yennyini amanyi bye yakola ne byataakola (newakubadde nga aligezaako okwogera kino nakiri ngayeetonda).');
INSERT INTO "african_dev" VALUES(75,15,'75:15','Newakubadde nga aligezaako okwogera kino nakiri ngayetonda.');
INSERT INTO "african_dev" VALUES(75,16,'75:16','Bwoba osoma Kur''ani toyanguyiriza nga olowooza nti kyekinakuyamba okuginyweeza.');
INSERT INTO "african_dev" VALUES(75,17,'75:17','Anti okugikungaanya kuli kuffe nga era bwekiri kuffe okunywereza ku lulimi lwo ensoma ennungi.');
INSERT INTO "african_dev" VALUES(75,18,'75:18','Kale nno bwetumala okugikusomera (nga tugiyisa ku Jiburilu) goberera ensoma yaayo.');
INSERT INTO "african_dev" VALUES(75,19,'75:19','Era mazima okuginnyonnyola kuli kuffe.');
INSERT INTO "african_dev" VALUES(75,20,'75:20','(Ebyo ebyogeddwa waggulu bimala omuntu okwebuulirira nabyo) wabula ebyembi mmwe mwagala ebyokufuna byensi ebyamangu.');
INSERT INTO "african_dev" VALUES(75,21,'75:21','Nemuleka ebyolunaku lw’enkomerero.');
INSERT INTO "african_dev" VALUES(75,22,'75:22','Ebyenyi bya bantu abamu ku lunaku olwo bigenda kuba nga bitemagana.');
INSERT INTO "african_dev" VALUES(75,23,'75:23','Bigenda kutunula ku Mukama Katonda waabyo.');
INSERT INTO "african_dev" VALUES(75,24,'75:24','Ate nga ebyenyi bya bantu abalala bigenda kuba binyikaavu.');
INSERT INTO "african_dev" VALUES(75,25,'75:25','Nga bimanyidde ddala nti bigenda kutuukibwako akabi akanene akamenya nenkizi.');
INSERT INTO "african_dev" VALUES(75,26,'75:26','Naye omwoyo bwegutuuka mu ddokooli.');
INSERT INTO "african_dev" VALUES(75,27,'75:27','Abantu webagambira nti ani alina eddagala eriyinza okumuwonya.');
INSERT INTO "african_dev" VALUES(75,28,'75:28','Wabula yye ngamaze okukakasa nti agenda kufa.');
INSERT INTO "african_dev" VALUES(75,29,'75:29','Entumbwe ne yeegatta ku ntumbwe.');
INSERT INTO "african_dev" VALUES(75,30,'75:30','Olwo nno obuddo buba busigadde eri Katondawo.');
INSERT INTO "african_dev" VALUES(75,31,'75:31','Naye nga omufu teyasaddaaka wadde okusaala.');
INSERT INTO "african_dev" VALUES(75,32,'75:32','Wabula yalimbisa ebigambo bya Katonda nabyesamba.');
INSERT INTO "african_dev" VALUES(75,33,'75:33','Olwo nno naatambula nadda eri abantu be nga yeeraga.');
INSERT INTO "african_dev" VALUES(75,34,'75:34','(Aligambibwa nti ekyo) ekikutuuseeko kikugwanidde era ddala kikugwanidde (owange ggwe omwonoonyi).');
INSERT INTO "african_dev" VALUES(75,35,'75:35','Ate era kikugwanidde ng''ate era ddala kikugwanidde.');
INSERT INTO "african_dev" VALUES(75,36,'75:36','Omuntu alowooza nti ayinza okulekwa neyeeyisa nga bwayagala.');
INSERT INTO "african_dev" VALUES(75,37,'75:37','Tajjukira nti yasooka kuba tondo lyamazzi agazaala agateekebwa mu nabaana.');
INSERT INTO "african_dev" VALUES(75,38,'75:38','Oluvanyuma naafuuka ekisayisayi, olwo Katonda naamutonda namussaako byonna ebimufuula omuntu.');
INSERT INTO "african_dev" VALUES(75,39,'75:39','Nga bwatyo bwakola emitendera ebiri, omusajja n''omukazi.');
INSERT INTO "african_dev" VALUES(75,40,'75:40','Abaffe oyo eyakola omuntu mu ngeri eyo tasobola kulamusa bafu!.');
INSERT INTO "african_dev" VALUES(76,1,'76:1','Omuntu tajjukira nti waayitawo ekiseera kinene nga tayogerwako (nga tannatondebwa).');
INSERT INTO "african_dev" VALUES(76,2,'76:2','Mazima ffe twatonda omuntu nga ava mu kwetabula kwamazzi (ag''omusajja n''omukazi) naye nga waakugezesebwa mu bulamu bwe. Kyetwava tumuwa okuwulira n''okulaba.');
INSERT INTO "african_dev" VALUES(76,3,'76:3','Mazima ddala twamulaga e kkubo, kiri gyali yeebaza naasinza Katonda omu oba ajeema n''awakanya.');
INSERT INTO "african_dev" VALUES(76,4,'76:4','Mazima ddala abakafiiri twabategekera enjegere n''amakoligo n''omuliro ogutuntumuka.');
INSERT INTO "african_dev" VALUES(76,5,'76:5','Mazima abakozi bobulungi bagenda kunywa ekyokunywa nga kitabuddwa mu kafuura.');
INSERT INTO "african_dev" VALUES(76,6,'76:6','Olwo nno luliba luzzi abaddu b''a Katonda abeeyisa obulungi kwebagendanga okunywa, lugenda kukulukuta nga buli muntu lumutuukako waali.');
INSERT INTO "african_dev" VALUES(76,7,'76:7','(Bagenda kutuuka kwebyo kubanga) kuno ku nsi baatuukiriza nnaziri zaabwe, era nga batya olunaku olwo obuzibu bwalwo obugenda okubuutikira e nsi n''eggulu.');
INSERT INTO "african_dev" VALUES(76,8,'76:8','Era bagabira abanaku ne bamulekwa nabasibe emmere awamu n''okuba nti bo bennyini bagyetaaga.');
INSERT INTO "african_dev" VALUES(76,9,'76:9','Nga bagamba nti mazima tubaliisa lwa Katonda tetubaagalamu mpeera wadde okwebaza.');
INSERT INTO "african_dev" VALUES(76,10,'76:10','(Ekitukozesa bino lwakuba nti) mazima ddala ffe tutya engeri gye tulisisinkanamu Katonda waffe ku lunaku e byenyi (bya bantu) lwe birijjula emitaafu nga byekabye.');
INSERT INTO "african_dev" VALUES(76,11,'76:11','N''olwekyo nno Katonda wakubawonya obuzibu bw''olunaku olwo era abawe okutangalijja kwe byenyi awamu n''essanyu.');
INSERT INTO "african_dev" VALUES(76,12,'76:12','Era wakubasasula olwobugumikiriza, nga abayingiza e jjana omuli ebyambalo bya Siliki (awamu nokuba nti kunsi abasajja baaziyizibwa okumwambala).');
INSERT INTO "african_dev" VALUES(76,13,'76:13','Balituula mu ntebe ez''omuwendo nga besigamye, tebagenda kutukwako kwokya kwanjuba wadde obutiti.');
INSERT INTO "african_dev" VALUES(76,14,'76:14','Era nga mulimu emiti egiribakolera ebisiikirize, era nga nebibala byamu bigenda kuba byangu byakunoga.');
INSERT INTO "african_dev" VALUES(76,15,'76:15','Baligabulirwa ku sowane eza feeza era bakunywera ku girasi ezitemagana.');
INSERT INTO "african_dev" VALUES(76,16,'76:16','Ziritemegana anti ziriba zaakolebwa mu feeza, ziriba zimala bumazi okubeeramu ekyokunywa ekimalawo obwetaavu bwoyo anywa.');
INSERT INTO "african_dev" VALUES(76,17,'76:17','Era bagenda kuweebwa eby''okunywa ebiri mu girasi nga bitabulwamu entangawuzi.');
INSERT INTO "african_dev" VALUES(76,18,'76:18','Eyo nno egenda kuba nsulo mu jjana eyitibwa "Salsabiila".');
INSERT INTO "african_dev" VALUES(76,19,'76:19','Bagenda kuweerezebwanga abaweereza abolubeerera, nga bwobatunulako bwoti olaba lu''ulu asasanyiziddwa.');
INSERT INTO "african_dev" VALUES(76,20,'76:20','Buli wonotunulanga mu jjana ng''olaba ebyengera ebisuffu n''obwanannyini obutaliiko kkomo.');
INSERT INTO "african_dev" VALUES(76,21,'76:21','Baliba bambadde engoye eza Siliki omuweweevu owakiragala ne Siliki omuzito, era bagenda kuba bawundiddwa nebikomo ebya feeza, era Mukama Katonda waabwe agenda kubawa ebyokunywa ebitukuvu.');
INSERT INTO "african_dev" VALUES(76,22,'76:22','(Baligambwa nti) Mazima ebyo byonna yempeera yammwe, mazima okutegana kwa mmwe kusiimiddwa.');
INSERT INTO "african_dev" VALUES(76,23,'76:23','Mazima ffe twakussaako Kur''ani (gwe Nabbi Muhammad) olussa.');
INSERT INTO "african_dev" VALUES(76,24,'76:24','Kale nno beera mugumikiriza ku kugera kwa Katondawo, togezaako okugondera omwonoonyi oba omuwakanyi yenna.');
INSERT INTO "african_dev" VALUES(76,25,'76:25','Era yogeranga kulinnya lya Mukama Katondawo enkya neggulo.');
INSERT INTO "african_dev" VALUES(76,26,'76:26','Ne mukiro muvunnamire ng''osaala era omutendereze mu ttumbi ebbanga ggwanvu.');
INSERT INTO "african_dev" VALUES(76,27,'76:27','Mazima abatakkiriza basinga kwagala birungi byansi ebyamangu, ne basuulayo gwannaggamba ku bikwata ku lunaku lw''enkomerero oluzito.');
INSERT INTO "african_dev" VALUES(76,28,'76:28','Ffe twabatonda ne tugumya e biyungo byabwe, naye nga bwe tuba twagadde (tuyinza okubazikiriza) ne tuleeta abantu abalala.');
INSERT INTO "african_dev" VALUES(76,29,'76:29','Mazima obubaka bwa Kur''ani buno kya kwebulirira era nga kyakujjukiza, n''olwekyo aba ayagadde asanye y''eteerewo ekkubo erimutuusa eri Katondawe.');
INSERT INTO "african_dev" VALUES(76,30,'76:30','Naye ate temusobola kwagala okugyako nga Katonda ayagadde, mazima Katonda lubeerera amanyi, assa buli kintu mussa lyako.');
INSERT INTO "african_dev" VALUES(76,31,'76:31','Ayingiza oyo gwaba ayagadde mu kusaasirakwe, ate bbo abalyazamanyi yabategekera ebibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(77,1,'77:1','Ndayidde empewo ekunta mu ngeri y''okugoberegana.');
INSERT INTO "african_dev" VALUES(77,2,'77:2','Era ndayidde kibuyaga akunta mu ngeri ey''amaanyi eyakabi.');
INSERT INTO "african_dev" VALUES(77,3,'77:3','Era ndayidde Malayika ezitambuza ebire olutambuza (olwokutonyesa enkuba).');
INSERT INTO "african_dev" VALUES(77,4,'77:4','Era ndayidde Malayika ezikka okuva ewa Katonda nga zireeta ebyawula wakati w''amazima n''obulimba.');
INSERT INTO "african_dev" VALUES(77,5,'77:5','Era ndayidde Malayika ezireeta ebitabo n''obubaka eri ba Nabbi ba Katonda nga byakujjukiza.');
INSERT INTO "african_dev" VALUES(77,6,'77:6','Mu ngeri yokuba nti bijjawo ebyokwekwaasa oba nga byakutiisa.');
INSERT INTO "african_dev" VALUES(77,7,'77:7','Mazima bye mulaganyisibwa ebikwata ku lunaku lw''enkomerero n''okusasulwa bya kubaawo.');
INSERT INTO "african_dev" VALUES(77,8,'77:8','Emunyeenye lweziriggibwawo (newataba kitangaala kyaazo).');
INSERT INTO "african_dev" VALUES(77,9,'77:9','Era eggulu bweririwumulwa (nerirabika nga emiryango).');
INSERT INTO "african_dev" VALUES(77,10,'77:10','Era ensozi bwezirimerengulwa.');
INSERT INTO "african_dev" VALUES(77,11,'77:11','Era ababaka b''a Katonda bwebaliweebwa akadde k''okuwa obujulizi kwabo bebaatumwamu.');
INSERT INTO "african_dev" VALUES(77,12,'77:12','(Ebintu ebyo ebyentiisa) biribaawo ddi!.');
INSERT INTO "african_dev" VALUES(77,13,'77:13','Bigenda kubaawo ku lunaku olwokusalawo n''okulamula wakati w''ebitonde.');
INSERT INTO "african_dev" VALUES(77,14,'77:14','Abaffe omanyi olunaku lw''okusalawo kyekki!.');
INSERT INTO "african_dev" VALUES(77,15,'77:15','Ku lunaku olwo okubonaabona kugenda kubeera kw''abo abalulimbisa.');
INSERT INTO "african_dev" VALUES(77,16,'77:16','Abaffe tetwazikiriza ababasokawo.');
INSERT INTO "african_dev" VALUES(77,17,'77:17','Netubagoberezaako abalala.');
INSERT INTO "african_dev" VALUES(77,18,'77:18','Bwetutyo nno bwetukola mu kubonereza abonoonyi.');
INSERT INTO "african_dev" VALUES(77,19,'77:19','Ku lunaku olwo okubonaabona kugenda kubeera kwabo abalulimbisa.');
INSERT INTO "african_dev" VALUES(77,20,'77:20','Abaffe tetwabakola nga tubajja mu mazzi aganyomebwa.');
INSERT INTO "african_dev" VALUES(77,21,'77:21','Netugateeka mukifo ekigakuuma.');
INSERT INTO "african_dev" VALUES(77,22,'77:22','Okutuusa ebbanga eggere erimanyiddwa.');
INSERT INTO "african_dev" VALUES(77,23,'77:23','Bwetutyo nno bwetwateekateeka okutondebwa kwabwe tuli bateesiteesi abasinga obulungi.');
INSERT INTO "african_dev" VALUES(77,24,'77:24','Ku lunaku olwo okubonaabona kugenda kubeera kwabo abalulimbisa.');
INSERT INTO "african_dev" VALUES(77,25,'77:25','Abaffe ensi tetwagikola nga ekungaanya.');
INSERT INTO "african_dev" VALUES(77,26,'77:26','Abalamu n''abafu (bonna ngabali muyo).');
INSERT INTO "african_dev" VALUES(77,27,'77:27','Netugiteekamu e nsozi eziginyweza entumbiivu ne tubawa amazzi amalungi.');
INSERT INTO "african_dev" VALUES(77,28,'77:28','Ku lunaku olwo okubonaabona kugenda kubeera kwabo abalulimbisa.');
INSERT INTO "african_dev" VALUES(77,29,'77:29','Ku lunaku lw''enkomerero abakafiiri baliragirwa nti mugende eri ebyo bye mwali mulimbisa.');
INSERT INTO "african_dev" VALUES(77,30,'77:30','Mugende eri ekisiikirize ekyomuliro ngakirimu ebitundu bisatu.');
INSERT INTO "african_dev" VALUES(77,31,'77:31','Wabula tebaliba na kibasiikiriza wadde e kibataasa nnimi z''amuliro.');
INSERT INTO "african_dev" VALUES(77,32,'77:32','Mazima omuliro ogwo gukasuka ebitawuliro nga biringa ebizimbe ebigulumivu.');
INSERT INTO "african_dev" VALUES(77,33,'77:33','Ebitawuliro ebyo nga biringa engamiya ez''akyenvu.');
INSERT INTO "african_dev" VALUES(77,34,'77:34','Ku lunaku olwo okubonaabona kugenda kubeera kwabo abalulimbisa.');
INSERT INTO "african_dev" VALUES(77,35,'77:35','Olwo lunaku lwebatagenda kwogera.');
INSERT INTO "african_dev" VALUES(77,36,'77:36','Wadde okukkirizibwa okwetonda.');
INSERT INTO "african_dev" VALUES(77,37,'77:37','Ku lunaku olwo okubonaabona kugenda kubeera kwabo abalulimbisa.');
INSERT INTO "african_dev" VALUES(77,38,'77:38','Luno lwe lunaku lw''okulamula tubakunganyizza mwenna n''aba bakulembera.');
INSERT INTO "african_dev" VALUES(77,39,'77:39','Bwe muba mulina engeri gyemuyinza okwebulankanyamu kale munnebulankanyeko.');
INSERT INTO "african_dev" VALUES(77,40,'77:40','Ku lunaku olwo okubonaabona kugenda kubeera kwabo abalulimbisa.');
INSERT INTO "african_dev" VALUES(77,41,'77:41','Naye mazima abatya Katonda bagenda kubeera mu bisiikirize n''emigga egikulukuta.');
INSERT INTO "african_dev" VALUES(77,42,'77:42','N''ebibala byebaliba bagala.');
INSERT INTO "african_dev" VALUES(77,43,'77:43','Nga bagambibwa nti mulye era munywe nga mweyagala.');
INSERT INTO "african_dev" VALUES(77,44,'77:44','Mazima ffe bwetutyo bwetusasula abakozi b''obulungi.');
INSERT INTO "african_dev" VALUES(77,45,'77:45','Ku lunaku olwo okubonaabona kugenda kubeera kwabo abalulimbisa.');
INSERT INTO "african_dev" VALUES(77,46,'77:46','(Kuno ku nsi abakafiiri bagambwa nti) mulye, era mweyagale kitono, wabula mumanye nti muli bonoonyi (naye mukimanye nti waliyo enkomerero).');
INSERT INTO "african_dev" VALUES(77,47,'77:47','Ku lunaku olwo okubonaabona kugenda kubeera kwabo abalulimbisa.');
INSERT INTO "african_dev" VALUES(77,48,'77:48','Bwebalagirwa okusaala tebasaala.');
INSERT INTO "african_dev" VALUES(77,49,'77:49','Ku lunaku olwo okubonaabona kugenda kubeera kwabo abalulimbisa.');
INSERT INTO "african_dev" VALUES(77,50,'77:50','Olwo bigamboki ebitali Kur''ani byebagenda okukkiriza.');
INSERT INTO "african_dev" VALUES(78,1,'78:1','Beebuuzaganya ku ki?');
INSERT INTO "african_dev" VALUES(78,2,'78:2','Ku kigambo e kinene.');
INSERT INTO "african_dev" VALUES(78,3,'78:3','Ekigambo kye baawukaniramu ddala.');
INSERT INTO "african_dev" VALUES(78,4,'78:4','Ekyo ssi bwekiri, bajja kumanya.');
INSERT INTO "african_dev" VALUES(78,5,'78:5','Oluvanyuma nedda bajja kumanyira ddala.');
INSERT INTO "african_dev" VALUES(78,6,'78:6','Abaffe tetwabafuula e nsi e kyaliiro.');
INSERT INTO "african_dev" VALUES(78,7,'78:7','N’ensozi ne tuzifuula enkondo (eziginyweza obutayuuga).');
INSERT INTO "african_dev" VALUES(78,8,'78:8','Ne tubatonda mu ngeri bbiri (omukazi n’omusajja).');
INSERT INTO "african_dev" VALUES(78,9,'78:9','Ne tufuula okwebaka kwa mmwe nga kiwummulo.');
INSERT INTO "african_dev" VALUES(78,10,'78:10','Ne tufuula ekiro ekyambalo ekibabikka (olw’enzikiza yaakyo)');
INSERT INTO "african_dev" VALUES(78,11,'78:11','Ne tufuula emisana omunoonyezebwa eky’okubabeezaawo.');
INSERT INTO "african_dev" VALUES(78,12,'78:12','Ne tuzimba waggulu wa mmwe emyaliiro emigumu musanvu.');
INSERT INTO "african_dev" VALUES(78,13,'78:13','Ne tuteekawo ettaala (enjuba) eyaka.');
INSERT INTO "african_dev" VALUES(78,14,'78:14','Ne tussa okuva mu bire by’enkuba amazzi agafukumuka.');
INSERT INTO "african_dev" VALUES(78,15,'78:15','Tulyoke tumeze nago empeke n’ebimera (ebirala).');
INSERT INTO "african_dev" VALUES(78,16,'78:16','N’amalimiro amasaakaativu.');
INSERT INTO "african_dev" VALUES(78,17,'78:17','Mazima olunaku lw’okulamula lwaweebwa e kiseera e kigere.');
INSERT INTO "african_dev" VALUES(78,18,'78:18','Olwo Lwe lunaku e ngombe lw''erifuuyibwa ne mujja nga muli bibinja.');
INSERT INTO "african_dev" VALUES(78,19,'78:19','N’eggulu ne liggulwawo ne libeerako e miryango.');
INSERT INTO "african_dev" VALUES(78,20,'78:20','N’ensozi nezikungunsibwa ne ziba nga ezitabangawo.');
INSERT INTO "african_dev" VALUES(78,21,'78:21','Mazima omuliro Jahannama weeguli gulinze.');
INSERT INTO "african_dev" VALUES(78,22,'78:22','Nga bwe buddo bw’abo abamawaggali eri Katonda.');
INSERT INTO "african_dev" VALUES(78,23,'78:23','Baakugubeeramu byeya na byeya.');
INSERT INTO "african_dev" VALUES(78,24,'78:24','Nga bali mu gwo tebagenda kufuna buweerero wadde ak''okunywa akaweweeza.');
INSERT INTO "african_dev" VALUES(78,25,'78:25','Okugyako olweje n’amasira Ag''olusaayisaayi.');
INSERT INTO "african_dev" VALUES(78,26,'78:26','Nga y’empeera ebasaanira.');
INSERT INTO "african_dev" VALUES(78,27,'78:27','Kubanga baali tebasuubira nti walibayo okubalibwa.');
INSERT INTO "african_dev" VALUES(78,28,'78:28','Ne balimbisa nnyo ebigambo byaffe.');
INSERT INTO "african_dev" VALUES(78,29,'78:29','So ng’ate buli kintu twakikuuma mu bujjuvu, mu buwandiike.');
INSERT INTO "african_dev" VALUES(78,30,'78:30','Kale mukombe ku kibabu ky''ebikolwa byammwe kubanga tetujja kubongera okugyako ebibonerezo.');
INSERT INTO "african_dev" VALUES(78,31,'78:31','Mazima abatya Katonda bajja kufuna okwesiima (okulimu buli kyonna kye baagala).');
INSERT INTO "african_dev" VALUES(78,32,'78:32','Amalimiro n’emizabbibu.');
INSERT INTO "african_dev" VALUES(78,33,'78:33','N’abawala ab’amabeere amatutu.');
INSERT INTO "african_dev" VALUES(78,34,'78:34','N’amagiraasi agajjudde eby’okunywa.');
INSERT INTO "african_dev" VALUES(78,35,'78:35','Nga bali mu yo (e jjana) tebagenda kuwulira bigambo bya butaliimu wadde eby’obulimba.');
INSERT INTO "african_dev" VALUES(78,36,'78:36','Nga y’empeera okuva eri omulezi wo, nga kuweebwa okumatiza.');
INSERT INTO "african_dev" VALUES(78,37,'78:37','Oyo nanyini Ggulu omusanvu n’ensi n’ebiri wakati wa byombi, oweekisa ekingi, tebalyetantala kwogeraganya naye.');
INSERT INTO "african_dev" VALUES(78,38,'78:38','Jiburilu ne ba malayika abalala ku lunaku lwe baliyimirira nga bali mu nnyiriri nga bonna tewali ayogera okugyako oyo yekka Katonda omusaasizi, gw’aliba akkiriza okwogera ate n''ayogera bituufu.');
INSERT INTO "african_dev" VALUES(78,39,'78:39','Olwo lwe lunaku olw’amazima. Omuntu ayagala emirembe alwetegekere afune obuddo eri Katonda we.');
INSERT INTO "african_dev" VALUES(78,40,'78:40','Mazima tubatiisizza ebibonerezo ebiri okumpi ennyo. Olunaku omuntu lw’aliraba ebyo emikono gye bye gyakola. Ku lunaku olwo omukaafiiri aligamba: ye nga zinsanze, singa nfuuse ettaka.');
INSERT INTO "african_dev" VALUES(79,1,'79:1','Ndayira Malayika ezisikambula emyoyo gy’abajeemu mu bukambwe.');
INSERT INTO "african_dev" VALUES(79,2,'79:2','Era ndayira Malayika ezisowola emyoyo gy’abakkiriza obusowozi.');
INSERT INTO "african_dev" VALUES(79,3,'79:3','Era ndayira Malayika eziwugira mu bbanga oluwuga.');
INSERT INTO "african_dev" VALUES(79,4,'79:4','Era ndayira Malayika ezivuganya oluvuganya nga zitwala emyoyo gy’abakkiriza mu jjana.');
INSERT INTO "african_dev" VALUES(79,5,'79:5','Ne ndayira ne Malayika ezissa mu nkola ebiragiro (Byange).');
INSERT INTO "african_dev" VALUES(79,6,'79:6','(Mukimanye nti ebitonde byonna bigenda kuzikirira) ku lunaku buli kintu kyonna lwe kirikankana nga engombe efuuyiddwa.');
INSERT INTO "african_dev" VALUES(79,7,'79:7','Luligobererwa olufuuwa olw''okubiri (olw''okuzuukira).');
INSERT INTO "african_dev" VALUES(79,8,'79:8','E mitima gy’abatakkiriza ku lunaku olwo giribeera mu kutya.');
INSERT INTO "african_dev" VALUES(79,9,'79:9','Amaaso gaabwe nga makkakkamu.');
INSERT INTO "african_dev" VALUES(79,10,'79:10','Bagamba (nga bajerega), nti "Mazima ddala tulizzibwawo mu bulamu nga bwetulimu kati?"');
INSERT INTO "african_dev" VALUES(79,11,'79:11','Bwe tuliba tumaze okufuuka amagumba amamerengufu?');
INSERT INTO "african_dev" VALUES(79,12,'79:12','Bongerako ne bagamba nti (okwonno bwekiriba bwekityo) kuliba kudda kwa kufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(79,13,'79:13','Mazima okuzuukira lugenda kubeera olukanga lumu lwokka.');
INSERT INTO "african_dev" VALUES(79,14,'79:14','Baligenda okulaba nga bali ku ngulu ku nsi.');
INSERT INTO "african_dev" VALUES(79,15,'79:15','Abaffe wamanya ku kyafaayo kya Nabbi Musa?');
INSERT INTO "african_dev" VALUES(79,16,'79:16','Awo Katonda we yakoowoolera Musa nga (Musa) ali mu lusenyi olutukuvu ''Tuwa''?');
INSERT INTO "african_dev" VALUES(79,17,'79:17','N''amugamba nti genda eri Firaawo kubanga ye asusse mu bujeemu ne mu buwakanyi.');
INSERT INTO "african_dev" VALUES(79,18,'79:18','Omugambe nti, Abaffe waalyetaaze okwetukuza (n''okkiriza mukama Katonda wo)?');
INSERT INTO "african_dev" VALUES(79,19,'79:19','Ndyoke mbe nga nkulungamya eri Katonda wo obeere nga omutya?');
INSERT INTO "african_dev" VALUES(79,20,'79:20','(Nabbi Musa) n’alaga Firaawo obujulizi obwenkukunala.');
INSERT INTO "african_dev" VALUES(79,21,'79:21','(Firaawo) naalimbisa era n’ajeema.');
INSERT INTO "african_dev" VALUES(79,22,'79:22','Bwe yamala n''abikuba amabega era n’akola kaweefube w''okubirwanyisa.');
INSERT INTO "african_dev" VALUES(79,23,'79:23','N’akungaanya (amagye ge, abakungu be, n’abalogo) n’alangirira.');
INSERT INTO "african_dev" VALUES(79,24,'79:24','Ng’agamba nti nze Katonda wa mmwe owa waggulu.');
INSERT INTO "african_dev" VALUES(79,25,'79:25','Katonda n’amubonereza n’okumuzikiriza olw’ekisobyo ekyasembayo n’ekyasooka.');
INSERT INTO "african_dev" VALUES(79,26,'79:26','Mazima mu ekyo (eky’okuzikiriza Firaawo) mulimu eky’okulabirako eri oyo atya Katonda.');
INSERT INTO "african_dev" VALUES(79,27,'79:27','Abaffe, mmwe be mweraba mwe musinga obuzibu okutonda okusinga e ggulu? Yalitonda nno!');
INSERT INTO "african_dev" VALUES(79,28,'79:28','(Awamu n''obugevvu bwalyo) yaliwanika n''alitereeza.');
INSERT INTO "african_dev" VALUES(79,29,'79:29','Aleeta enzikiza yaalyo n’aggyayo ekitangaala kyalyo.');
INSERT INTO "african_dev" VALUES(79,30,'79:30','N’ensi oluvanyuma lwebyo yagyaliira.');
INSERT INTO "african_dev" VALUES(79,31,'79:31','Muyo mwennyini mweyajja amazzi gaayo namalundiro gaayo.');
INSERT INTO "african_dev" VALUES(79,32,'79:32','N’ensozi yazisimba n''azinyweeza.');
INSERT INTO "african_dev" VALUES(79,33,'79:33','(Ebyo yabikola) lwa kugasa mmwe n''ensolo za mmwe.');
INSERT INTO "african_dev" VALUES(79,34,'79:34','Akabenje akanene bwe kalijja (olwo lwe luliba olunaku lw''enkomerero)');
INSERT INTO "african_dev" VALUES(79,35,'79:35','Olunaku lw’alijjukira omuntu ebyo byonna bye yakola.');
INSERT INTO "african_dev" VALUES(79,36,'79:36','Omuliro Jahiimu negulagibwa eri yenna alaba.');
INSERT INTO "african_dev" VALUES(79,37,'79:37','Oyo yenna eyasukka mu kujeema');
INSERT INTO "african_dev" VALUES(79,38,'79:38','N’asukkulumya obulamu bw’ensi (ku bw''enkomerero)');
INSERT INTO "african_dev" VALUES(79,39,'79:39','Mazima omuliro Jahiimu bwe buddo bwe.');
INSERT INTO "african_dev" VALUES(79,40,'79:40','Oyo aliba nga yatya okuyimirira mu maaso ga Mukama we (Katonda). N’aziyiza omwoyo gwe ku by’obwagazi.');
INSERT INTO "african_dev" VALUES(79,41,'79:41','Mazima e jjana, bwe buddo bwe.');
INSERT INTO "african_dev" VALUES(79,42,'79:42','Bakubuuza gwe (Nabbi Muhammad) ebikwata ku lunaku lw’enkomerero, nti lulibaawo ddi.');
INSERT INTO "african_dev" VALUES(79,43,'79:43','Wa gy''onoggya okulwogerako?');
INSERT INTO "african_dev" VALUES(79,44,'79:44','Omulezi wo Katonda yekka ya manyi ebirukwatako.');
INSERT INTO "african_dev" VALUES(79,45,'79:45','Mazima ggwe oli mutiisa eri oyo alutya.');
INSERT INTO "african_dev" VALUES(79,46,'79:46','Mazima bwe baliraba olunaku olwo balibanga abataatuula ku nsi okugyako ekiseera ky''olweggulo oba e kye nkya.');
INSERT INTO "african_dev" VALUES(80,1,'80:1','(Nabbi Muhammad) azze emitaafu, n’akubayo amabega.');
INSERT INTO "african_dev" VALUES(80,2,'80:2','Muzibe bw’amujjidde.');
INSERT INTO "african_dev" VALUES(80,3,'80:3','Ki ki ekikumanyisa ob''olyawo (muzibe oyo) aneetukuza.');
INSERT INTO "african_dev" VALUES(80,4,'80:4','Oba anaabuulirirwa okubuulira nekumugasa.');
INSERT INTO "african_dev" VALUES(80,5,'80:5','Oyo eyeegaggawaza nga yeematira.');
INSERT INTO "african_dev" VALUES(80,6,'80:6','Ate ggwe gwe weemalira?');
INSERT INTO "african_dev" VALUES(80,7,'80:7','Bw’ateetukuza kiki ekinaakubaako?');
INSERT INTO "african_dev" VALUES(80,8,'80:8','So ng’oyo akujjidde ng’ayanguwa.');
INSERT INTO "african_dev" VALUES(80,9,'80:9','Era ng’atya Katonda.');
INSERT INTO "african_dev" VALUES(80,10,'80:10','Ate ggwe gw’otofaako.');
INSERT INTO "african_dev" VALUES(80,11,'80:11','Nedda si bwe kyandibadde, mazima yyo (Kur''ani) kya kwe buulirira.');
INSERT INTO "african_dev" VALUES(80,12,'80:12','Aba ayagadde yeebuulirira nayo.');
INSERT INTO "african_dev" VALUES(80,13,'80:13','Eri mu biwandiiko eby’ekitiibwa ennyo.');
INSERT INTO "african_dev" VALUES(80,14,'80:14','Ebigulumizibwa, ebitukuvu.');
INSERT INTO "african_dev" VALUES(80,15,'80:15','Ebiri mu mikono gya Ba Malayika abawandiisi.');
INSERT INTO "african_dev" VALUES(80,16,'80:16','Ab''ebitiibwa, abatukuvu.');
INSERT INTO "african_dev" VALUES(80,17,'80:17','Omuntu yakolimirwa, ki ki ddala ekimuwakanyisa Katonda?');
INSERT INTO "african_dev" VALUES(80,18,'80:18','(Omuntu talaba) kintu ki Katonda mwe yamutonda?');
INSERT INTO "african_dev" VALUES(80,19,'80:19','Mu mazzi (e nkwaso) mwe yamutonda n''amugerera (byonna ebiri mutuukako).');
INSERT INTO "african_dev" VALUES(80,20,'80:20','Oluvanyuma n’amwanguyiza ekkubo ly’okuzaalibwa;');
INSERT INTO "african_dev" VALUES(80,21,'80:21','Bw’amala n’amutta era n’aziikibwa mu kabbuli.');
INSERT INTO "african_dev" VALUES(80,22,'80:22','Oluvanyuma alimuzuukiza bwaliba ayagadde.');
INSERT INTO "african_dev" VALUES(80,23,'80:23','Wabula omuntu tatuukiriza ebyo Katonda by’amulagira okukola.');
INSERT INTO "african_dev" VALUES(80,24,'80:24','Omuntu ateekwa atunuulire by’alya (emitendera gye biyitamu).');
INSERT INTO "african_dev" VALUES(80,25,'80:25','Mazima ffe tufuukirira amazzi olufukirira (olusaanidde.)');
INSERT INTO "african_dev" VALUES(80,26,'80:26','Ne tulyoka twasa ettaka olwasa (olusaanira ebimera).');
INSERT INTO "african_dev" VALUES(80,27,'80:27','Olwo nno ne tumeza mu lyo e mpeke.');
INSERT INTO "african_dev" VALUES(80,28,'80:28','E mizabbibu n’ebimera e bizimba omubiri.');
INSERT INTO "african_dev" VALUES(80,29,'80:29','N’emizayituni n’emitende.');
INSERT INTO "african_dev" VALUES(80,30,'80:30','N’ennimiro ezisaakaatiddemu (e miti emiwanvu)');
INSERT INTO "african_dev" VALUES(80,31,'80:31','N’ebibala n’omuddo oguliibwako e bisolo.');
INSERT INTO "african_dev" VALUES(80,32,'80:32','Bibagase n’ebisolo bya mmwe.');
INSERT INTO "african_dev" VALUES(80,33,'80:33','Naye okuboggola bwe kuliba kuzze.');
INSERT INTO "african_dev" VALUES(80,34,'80:34','Olunaku omuntu lwalidduka mugandawe,');
INSERT INTO "african_dev" VALUES(80,35,'80:35','Ne maamawe, ne kitaawe.');
INSERT INTO "african_dev" VALUES(80,36,'80:36','Ne mukyalawe, n’abaanabe.');
INSERT INTO "african_dev" VALUES(80,37,'80:37','Buli muntu ku lunaku olwo ajja kuba ku nsonga emukwatako yekka.');
INSERT INTO "african_dev" VALUES(80,38,'80:38','Ebyenyi ebimu ku lunaku olwo bigenda kuba byakaayakana.');
INSERT INTO "african_dev" VALUES(80,39,'80:39','Nga biriko akamwenyumwenyu, nga bisanyufu.');
INSERT INTO "african_dev" VALUES(80,40,'80:40','Ate ebyenyi ebirala bigenda kuba nga bigubye.');
INSERT INTO "african_dev" VALUES(80,41,'80:41','Nga ekizikiza kibibisse.');
INSERT INTO "african_dev" VALUES(80,42,'80:42','Abo nno be baawakanya Katonda, aboonoonyi.');
INSERT INTO "african_dev" VALUES(81,1,'81:1','Enjuba bw''erizingwako.');
INSERT INTO "african_dev" VALUES(81,2,'81:2','Nga n’emunyeenye zikunkumuse.');
INSERT INTO "african_dev" VALUES(81,3,'81:3','Era ensozi bwe zirikungunsibwa (ne zigibbwawo).');
INSERT INTO "african_dev" VALUES(81,4,'81:4','Nga n’ebisolo ebyo mu maka nga tebikyafiibwako (newakubadde nga biriba mawako).');
INSERT INTO "african_dev" VALUES(81,5,'81:5','N’ebisolo by’omu nsiko nabyo nga bikungaanyiziddwa.');
INSERT INTO "african_dev" VALUES(81,6,'81:6','Nga n’ennyanja zikuumiddwako omuliro (ne zibumbujja).');
INSERT INTO "african_dev" VALUES(81,7,'81:7','Nga n’emyoyo gigatiddwa buli egifaanagana nga giri wamu (Emirungi nga giri n’emirungi, e mibi nga giri n’emibi).');
INSERT INTO "african_dev" VALUES(81,8,'81:8','Era omwana omuwala eyaziikibwa nga mulamu bw''aliba abuuziddwa.');
INSERT INTO "african_dev" VALUES(81,9,'81:9','Kibi ki ekyamussa?.');
INSERT INTO "african_dev" VALUES(81,10,'81:10','Ebiwandiiko bwe biribeera nga bisasaanyiziddwa (nga buli kiwandiiko kituuka ku nnannyini kyo, omuli ebyo bye yakola ku nsi).');
INSERT INTO "african_dev" VALUES(81,11,'81:11','E ggulu bwe liriba liggyiddwawo.');
INSERT INTO "african_dev" VALUES(81,12,'81:12','Nga n’omuliro gukoleezeddwa.');
INSERT INTO "african_dev" VALUES(81,13,'81:13','Nga n’ejjana esembezeddwa.');
INSERT INTO "african_dev" VALUES(81,14,'81:14','Olwo omuntu alimanya ekyo kye yakola (kibe kirungi oba kibi).');
INSERT INTO "african_dev" VALUES(81,15,'81:15','Ndayira e munyeenye ezibulawo.');
INSERT INTO "african_dev" VALUES(81,16,'81:16','Ezibula emisana ne zirabika ekiro.');
INSERT INTO "african_dev" VALUES(81,17,'81:17','Era ndayira ekiro bwe kiba nga kyolekedde okuggwaawo.');
INSERT INTO "african_dev" VALUES(81,18,'81:18','Era ndayira n’amakya g’obudde nga busaasaana.');
INSERT INTO "african_dev" VALUES(81,19,'81:19','Mazima yyo (Kur''ani) kigambo kya mubaka (Jiburilu) ow''ekitiibwa (ky’aggya eri Katonda n’akireetera Nabbi Muhammad).');
INSERT INTO "african_dev" VALUES(81,20,'81:20','Oyo (Jiburilu) owaamaanyi era ow''ekitiibwa eri nnannyini Arishi.');
INSERT INTO "african_dev" VALUES(81,21,'81:21','Agonderwa (ba Malayika banne) eyo (mu ggulu) ng''ate mwesigwa.');
INSERT INTO "african_dev" VALUES(81,22,'81:22','Era munnammwe (Nabbi Muhammad) si mulalu.');
INSERT INTO "african_dev" VALUES(81,23,'81:23','Mazima ddala ye (Muhammad) yamulaba (Jiburilu) mu bweyolefu nga ajjudde obwengula.');
INSERT INTO "african_dev" VALUES(81,24,'81:24','Era tali (Nabbi Muhammad) ku bigambo ebyekwese (ebikwata ku bubaka obuva eri Katonda n''ebigambo by''omu ggulu) ayinza obutatuukiriza.');
INSERT INTO "african_dev" VALUES(81,25,'81:25','(Ebigambo bya Kur''ani Muhammad byayogera) si bigambo bya sitaani omukolimire.');
INSERT INTO "african_dev" VALUES(81,26,'81:26','Naye ddala mulaga wa?');
INSERT INTO "african_dev" VALUES(81,27,'81:27','Wabula Yo (Kur''ani) kyakubuulirira eri ebitonde byonna.');
INSERT INTO "african_dev" VALUES(81,28,'81:28','Eri oyo mu mmwe aba ayagadde okulungama.');
INSERT INTO "african_dev" VALUES(81,29,'81:29','Naye ate temujja kwagala okugyako nga Katonda Omulezi w’ebitonde ayagadde.');
INSERT INTO "african_dev" VALUES(82,1,'82:1','E ggulu bwe ririba lyeyasizzaamu.');
INSERT INTO "african_dev" VALUES(82,2,'82:2','N''emunyenye bwe ziriba nga zivudde waggulu ne zisaasaana.');
INSERT INTO "african_dev" VALUES(82,3,'82:3','N''ennyanja bwe ziriba nga ziwaguludde.');
INSERT INTO "african_dev" VALUES(82,4,'82:4','Ne Kabbuli bwe ziriba nga zifuukuddwa (abafu bonna ne bavaayo).');
INSERT INTO "african_dev" VALUES(82,5,'82:5','Omuntu alimanya ebyo bye yakulembeza okukola ne bye yasembyayo.');
INSERT INTO "african_dev" VALUES(82,6,'82:6','Owange ggwe omuntu kale kiki ekyakugayaaza okukuggya ku muleziwo (Katonda) Omugabi.');
INSERT INTO "african_dev" VALUES(82,7,'82:7','Oyo eyakutonda n’akutereeza era n’akwenkanyankanya.');
INSERT INTO "african_dev" VALUES(82,8,'82:8','Mu kifaananyi kyonna kye yayagala mwe yakutondera.');
INSERT INTO "african_dev" VALUES(82,9,'82:9','Nedda, wabula mulimbisa olunaku olw’okusasulwa (olw’enkomerero).');
INSERT INTO "african_dev" VALUES(82,10,'82:10','Mazima muliko abakuumi (Ba Malayika).');
INSERT INTO "african_dev" VALUES(82,11,'82:11','Abeekitiibwa abawandiisi (abawandiika ebyo byonna ebikolebwa)');
INSERT INTO "african_dev" VALUES(82,12,'82:12','Bamanyi ebyo byonna byemukola.');
INSERT INTO "african_dev" VALUES(82,13,'82:13','Mazima abakozi b’obulungi balibeera mu byengera.');
INSERT INTO "african_dev" VALUES(82,14,'82:14','Era mazima abakozi b’ebibi bagenda kubeera mu muliro oguyitibwa Jahiimu.');
INSERT INTO "african_dev" VALUES(82,15,'82:15','Bagenda kuguyingira ku lunaku olw’okusasulwa (olw''enkomerero).');
INSERT INTO "african_dev" VALUES(82,16,'82:16','Tebagenda ku gwebulankanyako.');
INSERT INTO "african_dev" VALUES(82,17,'82:17','On''omanyira ku ki olunaku olw''okusasulwa (olw’enkomerero).');
INSERT INTO "african_dev" VALUES(82,18,'82:18','Naye ddala on''omanyira ku ki olunaku olwokusasulwa (Katonda yakiddamu olwokuggumiza obukulu bw’olunaku olwo).');
INSERT INTO "african_dev" VALUES(82,19,'82:19','Olunaku omwoyo lwe gutaliba na buyinza bugasa mwoyo mulala na kintu kyonna. N’ebiragiro ku lunaku olwo biriba bya Katonda yekka.');
INSERT INTO "african_dev" VALUES(83,1,'83:1','Okuzikirira kuli ku abo abakendeeza ebipimo.');
INSERT INTO "african_dev" VALUES(83,2,'83:2','Abo bwe baba nga beebapimirwa, basaba ebipimo byabwe bijjuzibwe.');
INSERT INTO "african_dev" VALUES(83,3,'83:3','So nga bwe baba nga be balengera abalala, oba be bapima, bakendeeza (ebipimo).');
INSERT INTO "african_dev" VALUES(83,4,'83:4','Abaffe tebakakasa nti mazima ddala baakuzuukizibwa.');
INSERT INTO "african_dev" VALUES(83,5,'83:5','Ku lunaku oluzito (olw''enkomerero).');
INSERT INTO "african_dev" VALUES(83,6,'83:6','Olunaku abantu lwe baliyimirira mu maaso g''omulezi w’ebitonde.');
INSERT INTO "african_dev" VALUES(83,7,'83:7','Mazima ddala ebiwandiiko by’emirimu gy’aboonoonyi biri mu Sijjiin.');
INSERT INTO "african_dev" VALUES(83,8,'83:8','Naye omanyi Sijjiin kye ki?');
INSERT INTO "african_dev" VALUES(83,9,'83:9','Ky’ekitabo ekiwandiikibwamu ebibi by’omuntu.');
INSERT INTO "african_dev" VALUES(83,10,'83:10','Okuzikirira ku lunaku olwo, kulibeera ku abo abalimbisa.');
INSERT INTO "african_dev" VALUES(83,11,'83:11','Abo abalimbisa olunaku lw’okusasulwa (olw’enkomerero).');
INSERT INTO "african_dev" VALUES(83,12,'83:12','Tewali alimbisa lunaku olwo okugyako oyo asukka ensalo z’amateeka ga Katonda era omwonoonyi.');
INSERT INTO "african_dev" VALUES(83,13,'83:13','Bw’aba asomeddwa ebigambo byaffe (Kur''ani) agamba nti ebyo nfumo z’abaasooka.');
INSERT INTO "african_dev" VALUES(83,14,'83:14','Nedda, ssi bwekiri; wabula bye baali bakola byetuuma ku mitima gyabwe.');
INSERT INTO "african_dev" VALUES(83,15,'83:15','Mazima ddala abatakkiriza ku lunaku olwo baakuziyizibwa okulaba Omulezi waabwe (Katonda).');
INSERT INTO "african_dev" VALUES(83,16,'83:16','Mazima ddala baakuyingira omuliro Jahiimu.');
INSERT INTO "african_dev" VALUES(83,17,'83:17','Oluvanyuma bagenda kugambibwa nti ekyo kye mwalimbisanga.');
INSERT INTO "african_dev" VALUES(83,18,'83:18','Mazima ddala ebiwandiiko by’abakozi b’obulungi biri mu Illiyyiina.');
INSERT INTO "african_dev" VALUES(83,19,'83:19','Naye omanyi Illiyyiina kye ki?');
INSERT INTO "african_dev" VALUES(83,20,'83:20','Ky’ekitabo ekiwandiikibwamu ebirungi by’omuntu.');
INSERT INTO "african_dev" VALUES(83,21,'83:21','Bakisemberera Ba malayika abali okumpi ne Katonda.');
INSERT INTO "african_dev" VALUES(83,22,'83:22','Mazima abakozi b’obulungi bagenda kubeera mu byengera.');
INSERT INTO "african_dev" VALUES(83,23,'83:23','Nga bali ku bitanda ebiwunde nga batunuulira ebintu ebibeetoolodde.');
INSERT INTO "african_dev" VALUES(83,24,'83:24','Essanyu eriribeera ku maaso gaabwe ligenda ku kulaga okwesiima kwe balibaamu.');
INSERT INTO "african_dev" VALUES(83,25,'83:25','Baligabulwanga omubisi (ogw’omu jjana) omusaanikire obulungi.');
INSERT INTO "african_dev" VALUES(83,26,'83:26','(Olunaagunywangako) ng''owulira akawoowo ka misk. Ne mu kyo, abaagala okuvuganya mu kukola emirimu emirungi bavuganye.');
INSERT INTO "african_dev" VALUES(83,27,'83:27','Omubisi ogwo gugenda kutabulwa mu Tasnim.');
INSERT INTO "african_dev" VALUES(83,28,'83:28','Tasnim nsulo erinywebwangako abalibeera okumpi ne Katonda.');
INSERT INTO "african_dev" VALUES(83,29,'83:29','Mazima abantu abo abaali abajeemu baalinga ku nsi nga basekerera abakkiriza.');
INSERT INTO "african_dev" VALUES(83,30,'83:30','Bwe baabayitangako nga babageyesa amaaso.');
INSERT INTO "african_dev" VALUES(83,31,'83:31','Ate nga bwe baddanga mu bantu baabwe nga beewaana.');
INSERT INTO "african_dev" VALUES(83,32,'83:32','Bwe baabalabangako nga bagamba nti mazima abo baabulira ddala.');
INSERT INTO "african_dev" VALUES(83,33,'83:33','Naye tebatumwanga kubeera nga bebalondoola emirimu gya bali.');
INSERT INTO "african_dev" VALUES(83,34,'83:34','Wabula olwaleero (olunaku lw''enkomerero) abo abakkiriza bajja kubeera nga basekerera abatakkiriza.');
INSERT INTO "african_dev" VALUES(83,35,'83:35','(Abakkiriza) nga bali ku bitanda ebiwunde nga babakeneka.');
INSERT INTO "african_dev" VALUES(83,36,'83:36','(Nga bwe bagamba nti) abaffe abatakkiriza basasuddwa bye baalinga bakola.');
INSERT INTO "african_dev" VALUES(84,1,'84:1','Eggulu bwe liryeyasaamu.');
INSERT INTO "african_dev" VALUES(84,2,'84:2','Ne ligondera ekiragiro kya Mukama waalyo, era nga bwe kyandibadde.');
INSERT INTO "african_dev" VALUES(84,3,'84:3','Ensi bw''eriseeteezebwa.');
INSERT INTO "african_dev" VALUES(84,4,'84:4','N''ewandula ebigirimu n''esigala nga njereere.');
INSERT INTO "african_dev" VALUES(84,5,'84:5','N''egondera ekiragiro kya mukama waayo era nga bwe kyandibadde.');
INSERT INTO "african_dev" VALUES(84,6,'84:6','Owange ggwe omuntu! Otakabana olutakabana (okukola e mirimu) ng''odda eri Katondawo. Era oli wakumusisinkana.');
INSERT INTO "african_dev" VALUES(84,7,'84:7','Kale nno omuntu aliweebwa ekitabokye mu mukonogwe ogwa ddyo.');
INSERT INTO "african_dev" VALUES(84,8,'84:8','Agenda kubalwa olubala olwangu.');
INSERT INTO "african_dev" VALUES(84,9,'84:9','Era adde eri banne nga musanyufu.');
INSERT INTO "african_dev" VALUES(84,10,'84:10','Naye oyo aliweebwa ekitabokye e mabega w''omugongogwe.');
INSERT INTO "african_dev" VALUES(84,11,'84:11','Agenda kukuba e biwoobe.');
INSERT INTO "african_dev" VALUES(84,12,'84:12','Yeesogge omuliro Sa-iira.');
INSERT INTO "african_dev" VALUES(84,13,'84:13','Kubanga yabeeranga (ku nsi) mu bantube nga musanyufu (mu kujeemera amateeka ga Mukama Katonda).');
INSERT INTO "african_dev" VALUES(84,14,'84:14','Bambi! yalowooza nti tagenda kuzuukira.');
INSERT INTO "african_dev" VALUES(84,15,'84:15','So nno, mazima Katondawe yalinga amulaba.');
INSERT INTO "african_dev" VALUES(84,16,'84:16','Ndayira ekire ekimyufu (ekibaawo ng''enjuba yakagwa).');
INSERT INTO "african_dev" VALUES(84,17,'84:17','Ne ndayira n''ekiro n''ebyo bye kikungaanya.');
INSERT INTO "african_dev" VALUES(84,18,'84:18','Era ndayira omwezi bwe guba gujjudde.');
INSERT INTO "african_dev" VALUES(84,19,'84:19','Mujja kukyusibwakyusibwa (mu mbeera za mmwe nga muva mu mbeera okudda ku ndala).');
INSERT INTO "african_dev" VALUES(84,20,'84:20','Naye lwaki tebakkiriza!');
INSERT INTO "african_dev" VALUES(84,21,'84:21','Ne Kur''ani bwe basomerwa tebavunnama.');
INSERT INTO "african_dev" VALUES(84,22,'84:22','Wabula e nsonga ebagaana okuvunnama lwakuba nti abakafuuru balimbisa (Kur''ani).');
INSERT INTO "african_dev" VALUES(84,23,'84:23','So nga Katonda amannyidde ddala bye bakweka (mu mitima gyabwe).');
INSERT INTO "african_dev" VALUES(84,24,'84:24','Bawe amawulire nti bagenda kubeera mu bibonerezo ebiruma.');
INSERT INTO "african_dev" VALUES(84,25,'84:25','Okugyako abo abakkiriza ne bakola e mirimu e mirongoofu. Abo be bagenda okufuna e mpeera etakutukawo.');
INSERT INTO "african_dev" VALUES(85,1,'85:1','Ndayira e ggulu eririmu ebifo omutambulira e munyeenye (nga mwe muli e njuba n’omwezi).');
INSERT INTO "african_dev" VALUES(85,2,'85:2','Era ndayira olunaku olwalagaanyisibwa (olunaku lw’enkomerero)');
INSERT INTO "african_dev" VALUES(85,3,'85:3','Era ndayira oyo aliwa obujulizi n’oyo gwe balibuwaako (ku lunaku lw’enkomerero).');
INSERT INTO "african_dev" VALUES(85,4,'85:4','Katonda yagoba mu kusaasirakwe abo abaasima agannya.');
INSERT INTO "african_dev" VALUES(85,5,'85:5','Gebaakumamu omuliro ogubumbujja.');
INSERT INTO "african_dev" VALUES(85,6,'85:6','Bwe baatuula awo ku mabbali waagwo.');
INSERT INTO "african_dev" VALUES(85,7,'85:7','Nga bo (bennyini) balaba buli kyonna kyebakola ku bakkiriza.');
INSERT INTO "african_dev" VALUES(85,8,'85:8','Tebaalina kye baali babavunaana okugyako okukkiriza Katonda oweekitiibwa atenderezebwa.');
INSERT INTO "african_dev" VALUES(85,9,'85:9','Oyo, obufuzi bwe ggulu omusanvu n''ensi bwonna bubwe. Era Katonda alaba buli kintu.');
INSERT INTO "african_dev" VALUES(85,10,'85:10','Mazima abo abaawalagganya abakkiriza abasajja n’abakkiriza abakazi ate ne bateenenya, bakutuukibwako e bibonerezo by’omuliro Jahannama era balina e bibonerezo by’omuliro ogwokya ennyo.');
INSERT INTO "african_dev" VALUES(85,11,'85:11','Mazima abo abakkiriza ne bakola e mirimu e mirungi bajja kufuna e jjana nga mukulukutira muyo emigga. Okwo nno kwe kwesiima okwannamaddala.');
INSERT INTO "african_dev" VALUES(85,12,'85:12','Mazima okukwata kwa Mukamaawo kwamaanyi nnyo.');
INSERT INTO "african_dev" VALUES(85,13,'85:13','Kubanga yye ye mutandisi wa buli kintu era yewookubizzaawo.');
INSERT INTO "african_dev" VALUES(85,14,'85:14','Era musonyiyi nnyo, era abaagala nnyo.');
INSERT INTO "african_dev" VALUES(85,15,'85:15','Yye ye nannyini Arishi, Oweekitiibwa.');
INSERT INTO "african_dev" VALUES(85,16,'85:16','Akola buli ky’aba ayagadde.');
INSERT INTO "african_dev" VALUES(85,17,'85:17','Wali owulidde ku byafaayo by’amagye.');
INSERT INTO "african_dev" VALUES(85,18,'85:18','Aga Firawo n’abantu ba Thamuud?');
INSERT INTO "african_dev" VALUES(85,19,'85:19','Wabula abo abatakkiriza bali mu kulimbisa.');
INSERT INTO "african_dev" VALUES(85,20,'85:20','So nga Katonda abeetoolodde.');
INSERT INTO "african_dev" VALUES(85,21,'85:21','Wabula yo Kur''ani (eyassibwa ku Nabbi Muhammad) ya kitiibwa.');
INSERT INTO "african_dev" VALUES(85,22,'85:22','Eri mu ggwandiikiro erikuumwa obutiribiri.');
INSERT INTO "african_dev" VALUES(86,1,'86:1','Ndayira e ggulu ne munyeenye ezirabika e kiro.');
INSERT INTO "african_dev" VALUES(86,2,'86:2','Naye omanyi e munyeenye ezirabika ekiro kye ki?');
INSERT INTO "african_dev" VALUES(86,3,'86:3','Z’emunyeenye ezaaka ennyo ekitangaala kyazo ne kiyita mu nzikiza ne kituuka ku nsi.');
INSERT INTO "african_dev" VALUES(86,4,'86:4','Buli muntu yenna aliko abakuumi (Ba Malayika).');
INSERT INTO "african_dev" VALUES(86,5,'86:5','Kale nno omuntu yalibadde atunuulira kye yatondebwamu.');
INSERT INTO "african_dev" VALUES(86,6,'86:6','Yatondebwa ng’aggyibwa mu mazzi agasambattuka.');
INSERT INTO "african_dev" VALUES(86,7,'86:7','Nga gava wakati w’omugongo gw''omusajja n’amasaabiro g''omukazi.');
INSERT INTO "african_dev" VALUES(86,8,'86:8','Mazima ddala oyo (Katonda) asobolera ddala okumuzzaawo.');
INSERT INTO "african_dev" VALUES(86,9,'86:9','Ku lunaku ebyama byonna lwe biribikkulwa.');
INSERT INTO "african_dev" VALUES(86,10,'86:10','Omuntu tagenda kuba na maanyi gonna yadde omutaasa.');
INSERT INTO "african_dev" VALUES(86,11,'86:11','Era ndayira e ggulu ewava e nkuba etonnya nga e ddingana.');
INSERT INTO "african_dev" VALUES(86,12,'86:12','Ne ndayira n’ettaka eryeyasa olw’okumeza e bimera.');
INSERT INTO "african_dev" VALUES(86,13,'86:13','Mazima ddala yo (Kur''ani) kigambo ekyawula wakati w’amazima n’obulimba.');
INSERT INTO "african_dev" VALUES(86,14,'86:14','Kur''ani si ya lusaago.');
INSERT INTO "african_dev" VALUES(86,15,'86:15','Mazima abatakkiriza basala e nkwe.');
INSERT INTO "african_dev" VALUES(86,16,'86:16','Nange nenkola enteekateeka (zange).');
INSERT INTO "african_dev" VALUES(86,17,'86:17','Abatakkiriza balindirize, nange mbawadde akaseera katono.');
INSERT INTO "african_dev" VALUES(87,1,'87:1','Tendereza erinnya ly’omulabiriziwo owa waggulu.');
INSERT INTO "african_dev" VALUES(87,2,'87:2','Oyo eyatonda n''atereeza.');
INSERT INTO "african_dev" VALUES(87,3,'87:3','Era eyateekateeka buli kintu n''akyenkanyankanya.');
INSERT INTO "african_dev" VALUES(87,4,'87:4','Era oyo ameza omuddo oguliibwa e bisolo.');
INSERT INTO "african_dev" VALUES(87,5,'87:5','Oluvanyuma agufuula omukalu.');
INSERT INTO "african_dev" VALUES(87,6,'87:6','Tujja kukusomesa (Kur''ani) obeere nga teweerabira.');
INSERT INTO "african_dev" VALUES(87,7,'87:7','Okugyako ebyo Katonda by’anaaba ayagadde okukwerabiza. Katonda y’amanyi ebyogerwa mu lwatu ne mu kyama.');
INSERT INTO "african_dev" VALUES(87,8,'87:8','Tujja kukwanguyiza amateeka gabeere mangu.');
INSERT INTO "african_dev" VALUES(87,9,'87:9','Kale buulirira, okubuulirira bwe kuba nga kunaagasa.');
INSERT INTO "african_dev" VALUES(87,10,'87:10','Anaaganyulwa oyo atya Katonda.');
INSERT INTO "african_dev" VALUES(87,11,'87:11','Omwonoonyi ajja ku kwesamba (okubuulirira okwo).');
INSERT INTO "african_dev" VALUES(87,12,'87:12','Oyo alyesogga omuliro ogwamaanyi.');
INSERT INTO "african_dev" VALUES(87,13,'87:13','Ng’amaze (okusonsekebwa mu muliro ogwo), tagenda kufa wadde okufuna obulamu (obwesiimisa).');
INSERT INTO "african_dev" VALUES(87,14,'87:14','Mazima yeesiimye omuntu e yeetukuza.');
INSERT INTO "african_dev" VALUES(87,15,'87:15','N’ayogera ku linnya ly’omuleziwe olwo nno n’asaala.');
INSERT INTO "african_dev" VALUES(87,16,'87:16','Wabula mmwe musukkulumya obulamu obw’ensi.');
INSERT INTO "african_dev" VALUES(87,17,'87:17','So nga obulamu obw''enkomerero bwe businga obulungi era bwe bwo kusigalawo.');
INSERT INTO "african_dev" VALUES(87,18,'87:18','Mazima bino biri mu biwandiiko ebyasooka.');
INSERT INTO "african_dev" VALUES(87,19,'87:19','Ebiwandiiko bya Ibrahim ne Musa.');
INSERT INTO "african_dev" VALUES(88,1,'88:1','Abaffe wali ofunye okunyumizibwa ku kibuutikira (eky’olunaku lw''enkomerero)?');
INSERT INTO "african_dev" VALUES(88,2,'88:2','Olunaku olwo ebyenyi bigenda kuba nga biswavu.');
INSERT INTO "african_dev" VALUES(88,3,'88:3','Nga bitawaana, nga bikoowu.');
INSERT INTO "african_dev" VALUES(88,4,'88:4','Nga byesonseka omuliro ogwengeredde.');
INSERT INTO "african_dev" VALUES(88,5,'88:5','Bigenda kunywesebwa ku luzzi olweseze.');
INSERT INTO "african_dev" VALUES(88,6,'88:6','(Abantu abo) tebagenda kubeera na kya kulya kyonna okugyako e miti egy’amaggwa.');
INSERT INTO "african_dev" VALUES(88,7,'88:7','Egitaliimu kiriisa wadde okuwonya e njala.');
INSERT INTO "african_dev" VALUES(88,8,'88:8','Ebyenyi e birala bigenda kubeera mu byengera.');
INSERT INTO "african_dev" VALUES(88,9,'88:9','Bigenda kusiima olw’okutakabana kwa byo.');
INSERT INTO "african_dev" VALUES(88,10,'88:10','Nga biri mu jjana ey’awaggulu.');
INSERT INTO "african_dev" VALUES(88,11,'88:11','Togenda kuwulira mu yo bigambo bya butaliimu.');
INSERT INTO "african_dev" VALUES(88,12,'88:12','Mu jjana mulimu e nsulo e zikulukuta.');
INSERT INTO "african_dev" VALUES(88,13,'88:13','Era nga mulimu ebitanda ebisitufu (Ebiwunde).');
INSERT INTO "african_dev" VALUES(88,14,'88:14','N’ebikopo ebinywerwamu ebitegekeddwa.');
INSERT INTO "african_dev" VALUES(88,15,'88:15','N’emitto egisimbiddwa ennyiriri.');
INSERT INTO "african_dev" VALUES(88,16,'88:16','N’ebyaliiro nga byanjuluziddwa.');
INSERT INTO "african_dev" VALUES(88,17,'88:17','Abaffe tebatunuulira (olutunuulira olw’okuyiga) engamiya ngeri ki bwe yatondebwa?');
INSERT INTO "african_dev" VALUES(88,18,'88:18','N’eggulu nga bwe lyasitulwa!');
INSERT INTO "african_dev" VALUES(88,19,'88:19','N’ensozi engeri gye zaasimbwamu?');
INSERT INTO "african_dev" VALUES(88,20,'88:20','N’ensi engeri gye yaseetezebwamu.');
INSERT INTO "african_dev" VALUES(88,21,'88:21','Kale nno buulirira gwe (Nabbi Muhammad) kubanga oli mubuulirizi.');
INSERT INTO "african_dev" VALUES(88,22,'88:22','Toliiwo kubakaka.');
INSERT INTO "african_dev" VALUES(88,23,'88:23','Naye abo abanaava ku bigambobyo ne bajeema.');
INSERT INTO "african_dev" VALUES(88,24,'88:24','Katonda agenda okubabonereza e bibonerezo e bisinga okuba e bikakali.');
INSERT INTO "african_dev" VALUES(88,25,'88:25','Mazima ddala gyetuli y’eri obuddo bwabwe.');
INSERT INTO "african_dev" VALUES(88,26,'88:26','Oluvanyuma ddala okubalibwa kwabwe kuli ku ffe.');
INSERT INTO "african_dev" VALUES(89,1,'89:1','Ndayira e mambya.');
INSERT INTO "african_dev" VALUES(89,2,'89:2','N’ebiro e kkumi.');
INSERT INTO "african_dev" VALUES(89,3,'89:3','N’ebibiri bibiri n’ebyensusuuba.');
INSERT INTO "african_dev" VALUES(89,4,'89:4','N’ekiro bwekiba nga kitambula (nga kijja oba nga kivaawo).');
INSERT INTO "african_dev" VALUES(89,5,'89:5','Abaffe mu kulayira kuno temuliimu kwebuulirira eri oyo alina amagezi.');
INSERT INTO "african_dev" VALUES(89,6,'89:6','Tewamanya ebyo Omuleziwo (Katonda) bye yatuusa ku bantu b''ekika kya A''adi.');
INSERT INTO "african_dev" VALUES(89,7,'89:7','Aba-Irama abawagguufu.');
INSERT INTO "african_dev" VALUES(89,8,'89:8','Tewatondebwangayo babafaanana mu nsi.');
INSERT INTO "african_dev" VALUES(89,9,'89:9','N’ebyo bye yatuusa ku ba Thamuud abaawukuulanga e njazi mu lusenyi.');
INSERT INTO "african_dev" VALUES(89,10,'89:10','N''ebyo bye yatuusa ku Firaawo nannyini nkondo.');
INSERT INTO "african_dev" VALUES(89,11,'89:11','Abo bonna (aboogeddwako) beewaggula kuno ku nsi.');
INSERT INTO "african_dev" VALUES(89,12,'89:12','Ne bayitiriza nnyo obwonoonefu.');
INSERT INTO "african_dev" VALUES(89,13,'89:13','Omuleziwo (Katonda) kye yava abafukirira e bibonerezo e by''enjawulo.');
INSERT INTO "african_dev" VALUES(89,14,'89:14','Mazima ddala Omuleziwo (Katonda) abalindiridde.');
INSERT INTO "african_dev" VALUES(89,15,'89:15','Wabula omuntu, Omuleziwe (Katonda) bw''amugezesa n''amugabira e birungi, omuntu oyo agamba nti Katonda wange ampadde.');
INSERT INTO "african_dev" VALUES(89,16,'89:16','Ku ludda olulala bw''aba amugezesezza n''amufundiza ebyenfunaye agamba nti omulezi wange (Katonda) anzikakkanyiza.');
INSERT INTO "african_dev" VALUES(89,17,'89:17','Nedda, wabula temuyisa bulungi ba Mulekwa.');
INSERT INTO "african_dev" VALUES(89,18,'89:18','Era temwekubiriza kuliisa banaku.');
INSERT INTO "african_dev" VALUES(89,19,'89:19','Ate nga mulya emmaali ya ba Mulekwa, olulya olugimalawo.');
INSERT INTO "african_dev" VALUES(89,20,'89:20','Era nga mwagala eby''enfuna okwagala okuyitiridde.');
INSERT INTO "african_dev" VALUES(89,21,'89:21','Nedda eyo si y''endaba e ntuufu ey’ebintu, e nsi bwe ribeera nga ebetenteddwa.');
INSERT INTO "african_dev" VALUES(89,22,'89:22','Omuleziwo n''ajja nga ne Ba Malayika basimbye ennyiriri.');
INSERT INTO "african_dev" VALUES(89,23,'89:23','Era kwolwo o muliro Jahannama gulireetebwa, kwolwo nno omuntu alijjukira (bye yakola ku nsi) naye okujjukira kuno kugenda ku mugasaaki.');
INSERT INTO "african_dev" VALUES(89,24,'89:24','Agenda kugamba: zinsanze nze. Singa nategekera obulamu bwange buno.');
INSERT INTO "african_dev" VALUES(89,25,'89:25','Olunaku olwo (Katonda) e bibonerezo by''agenda okubonereza nabyo tewali n''omu yali abibonerezzaako.');
INSERT INTO "african_dev" VALUES(89,26,'89:26','Era ensiba gyalisiba ku lunaku olwo tewali yali agisibyeko.');
INSERT INTO "african_dev" VALUES(89,27,'89:27','Owange ggwe omwoyo omutebenkevu (omuntu).');
INSERT INTO "african_dev" VALUES(89,28,'89:28','Dda eri Omuleziwo ng''oli musanyufu era asiimiddwa.');
INSERT INTO "african_dev" VALUES(89,29,'89:29','Olwo nno oyingire mu baddu bange (abalongoofu).');
INSERT INTO "african_dev" VALUES(89,30,'89:30','Era oyingire mu jjana yange.');
INSERT INTO "african_dev" VALUES(90,1,'90:1','Ndayira ekibuga kino (Makkah).');
INSERT INTO "african_dev" VALUES(90,2,'90:2','Nga naawe (Muhammad) oli mutuuze mu kibuga kino.');
INSERT INTO "african_dev" VALUES(90,3,'90:3','Era ndayira omuzadde ne baazaala.');
INSERT INTO "african_dev" VALUES(90,4,'90:4','Mazima twakola omuntu nga lubeerera abeera mu kutawaana.');
INSERT INTO "african_dev" VALUES(90,5,'90:5','Alowooza nti tewali n’omu ayinza ku musobola.');
INSERT INTO "african_dev" VALUES(90,6,'90:6','Ng’agamba nti nsaasanyiza emmaali nyingi nnyo.');
INSERT INTO "african_dev" VALUES(90,7,'90:7','Alowooza nti tewali n’omu amulaba.');
INSERT INTO "african_dev" VALUES(90,8,'90:8','Tetwamuwa amaaso abiri.');
INSERT INTO "african_dev" VALUES(90,9,'90:9','N’olulimi n’emimwa ebiri.');
INSERT INTO "african_dev" VALUES(90,10,'90:10','Era ne tumulungamya okumanya amakubo abiri (ery''ekirungi n''ery''ekibi).');
INSERT INTO "african_dev" VALUES(90,11,'90:11','Kale singa yabikozesa mu kuvvuunuka obizibu (bw''olunaku lw''enkomerero n''aba nga akozesa bye yafuna naafunamu e nkomerero).');
INSERT INTO "african_dev" VALUES(90,12,'90:12','Abaffe obuzibu obumanyi (n''ekiyinza okubukuyambako)?');
INSERT INTO "african_dev" VALUES(90,13,'90:13','Kuta muddu.');
INSERT INTO "african_dev" VALUES(90,14,'90:14','Oba okuliisa mu kiseera eky’enjala;');
INSERT INTO "african_dev" VALUES(90,15,'90:15','Mulekwa, owooluganda olw’okumpi;');
INSERT INTO "african_dev" VALUES(90,16,'90:16','Oba omwavu lunkupe.');
INSERT INTO "african_dev" VALUES(90,17,'90:17','Ate era n’abeera mu abo abakkiriza. Ng’era balaamiragana obugumiikiriza nga bwe balaamiragana okusaasiragana.');
INSERT INTO "african_dev" VALUES(90,18,'90:18','Abo nno beebo abokubeera ku mukono ogwa ddyo.');
INSERT INTO "african_dev" VALUES(90,19,'90:19','Naye abo abawakanya e bigambo byaffe baakubeera ku mukono ogwa kkono.');
INSERT INTO "african_dev" VALUES(90,20,'90:20','( Bagenda kubeera mu muliro omusaanikire.');
INSERT INTO "african_dev" VALUES(91,1,'91:1','Ndayira e njuba n’okwaka kwayo.');
INSERT INTO "african_dev" VALUES(91,2,'91:2','Era ndayira o mwezi bweguba nga gugoberedde e njuba (ng’egudde)');
INSERT INTO "african_dev" VALUES(91,3,'91:3','Era ndayira obudde obw’emisana nga bwerudde enjuba.');
INSERT INTO "african_dev" VALUES(91,4,'91:4','Era ndayira ekiro bwe kiba nga kigibisse.');
INSERT INTO "african_dev" VALUES(91,5,'91:5','Era ndayira e ggulu n’eyalitonda.');
INSERT INTO "african_dev" VALUES(91,6,'91:6','Era ndayira e nsi n’oyo eyajaaliira.');
INSERT INTO "african_dev" VALUES(91,7,'91:7','Era ndayira omuntu n’eyamutonda.');
INSERT INTO "african_dev" VALUES(91,8,'91:8','N’amuwa okwawula wakati w’ekibi n’ekirungi.');
INSERT INTO "african_dev" VALUES(91,9,'91:9','Yeesiimye alongoosa omwoyogwe okuguggya mu bibi.');
INSERT INTO "african_dev" VALUES(91,10,'91:10','Aviiriddemu awo oyo agunnyika mu bibi.');
INSERT INTO "african_dev" VALUES(91,11,'91:11','Abantu ba Thamuud, olw''obwonoonefu bwabwe baalimbisa (omubaka waabwe).');
INSERT INTO "african_dev" VALUES(91,12,'91:12','Asinga obwonoonefu mu bo bwe yeesowolayo (naafumita engamiya).');
INSERT INTO "african_dev" VALUES(91,13,'91:13','Omubaka wa Katonda n’abagamba “muleke e ngamiya ya Katonda n’okunywa kwayo”.');
INSERT INTO "african_dev" VALUES(91,14,'91:14','Kyokka ne bamulimbisa ne bagifumita. Katonda omulabirizi waabwe kwe kubabuutikira olw’obwonoonefu bwabwe awatali n’omu kuwona');
INSERT INTO "african_dev" VALUES(91,15,'91:15','Era tatya nkomerero y''ekyo (eky''okubazikiriza).');
INSERT INTO "african_dev" VALUES(92,1,'92:1','Ndayira ekiro bwe kiba kibisse (e nsi).');
INSERT INTO "african_dev" VALUES(92,2,'92:2','Ndayira n''obudde bwemisana bwe buba nga bwetadde.');
INSERT INTO "african_dev" VALUES(92,3,'92:3','Ndayira oyo eyatonda ekisajja n’ekikazi.');
INSERT INTO "african_dev" VALUES(92,4,'92:4','Mazima ddala okulafuubana kwa mmwe si kwe kumu.');
INSERT INTO "african_dev" VALUES(92,5,'92:5','N''olwekyo, omuntu awaayo ekintu kyonna ng’atya Katonda.');
INSERT INTO "african_dev" VALUES(92,6,'92:6','Nakkiriza e birungi. (ebiva mu kukkiriza Katonda).');
INSERT INTO "african_dev" VALUES(92,7,'92:7','Tujja kumwanguyiza e kkubo erimutuusa eri obwangu.');
INSERT INTO "african_dev" VALUES(92,8,'92:8','Ate oyo akodowala ne yeegaggasa (ng’alowooza nti ebiri ewa Katonda tabyetaaga).');
INSERT INTO "african_dev" VALUES(92,9,'92:9','N’alimbisa obubaka obwakkira ku Nabbi Muhammad.');
INSERT INTO "african_dev" VALUES(92,10,'92:10','Tujja kumwanguyiza okutuuka eri obuzibu.');
INSERT INTO "african_dev" VALUES(92,11,'92:11','N’obugaggabwe tebugenda kumugasa ng’ayingidde okuzikirira (olw’ebikolwa bye ebibi)');
INSERT INTO "african_dev" VALUES(92,12,'92:12','Mazima kuli ku ffe okulungamya.');
INSERT INTO "african_dev" VALUES(92,13,'92:13','Era mazima ebikwata ku nkomerero ne nsi biri mu buyinza bwaffe.');
INSERT INTO "african_dev" VALUES(92,14,'92:14','Mbeekesa omuliro ogubumbujja.');
INSERT INTO "african_dev" VALUES(92,15,'92:15','Taliguyingira okugyako oyo omwonoonefu ennyo.');
INSERT INTO "african_dev" VALUES(92,16,'92:16','Oyo alimbisa (e bigambo bya Katonda) n''ayawukana (ku kugondera n''okukkiriza Katonda).');
INSERT INTO "african_dev" VALUES(92,17,'92:17','So nga oyo asinga okutya Katonda, ajja kugwesambisibwa.');
INSERT INTO "african_dev" VALUES(92,18,'92:18','Oyo awaayo ku by''alina ng’anoonya okwetukuza eri Katonda (so si bandabe oba okwatiikirira)');
INSERT INTO "african_dev" VALUES(92,19,'92:19','Nga tewali n''omu alina kiyinza ku musasulwa.');
INSERT INTO "african_dev" VALUES(92,20,'92:20','Wabula okunoonya okusiimwa ewa mukamaawe owa waggulu ennyo.');
INSERT INTO "african_dev" VALUES(92,21,'92:21','Mazima ddala ajja kusiima (ky’anaasasulwa).');
INSERT INTO "african_dev" VALUES(93,1,'93:1','Ndayira ekiseera ky’a kalasa mayanzi.');
INSERT INTO "african_dev" VALUES(93,2,'93:2','Era ndayira e kiro bwekiba nga kikutte.');
INSERT INTO "african_dev" VALUES(93,3,'93:3','Omuleziwo Katonda takwabuliranga (gwe Nabbi Muhammad) era takukyawanga.');
INSERT INTO "african_dev" VALUES(93,4,'93:4','Era ddala e nkomerero y’ennungi gy’oli okusinga entandikwa.');
INSERT INTO "african_dev" VALUES(93,5,'93:5','Era ddala Omuleziwo ajja kukuwa naawe osiime.');
INSERT INTO "african_dev" VALUES(93,6,'93:6','Teyakusanga ng’oli mulekwa n''akulabirira?');
INSERT INTO "african_dev" VALUES(93,7,'93:7','Era teyakusanga nga tomanyi n’akulungamya?');
INSERT INTO "african_dev" VALUES(93,8,'93:8','Era teyakusanga nga oli mwavu n’akugaggawaza?');
INSERT INTO "african_dev" VALUES(93,9,'93:9','Kale nno mulekwa tomukaayuukiranga.');
INSERT INTO "african_dev" VALUES(93,10,'93:10','N’omuntu asaba tomuboggoleranga.');
INSERT INTO "african_dev" VALUES(93,11,'93:11','Ate era e kyengera kyonna Omuleziwo Katonda ky’abanga akuwadde kinyumyengako.');
INSERT INTO "african_dev" VALUES(94,1,'94:1','Tetwakwanjululiza ekifubakyo (eri obwa Nabbi n’okumanya ebigambo eby’amagezi).');
INSERT INTO "african_dev" VALUES(94,2,'94:2','Ne tukuwewulira obuzito obwakuliko.');
INSERT INTO "african_dev" VALUES(94,3,'94:3','Obuzito obwo obwalibadde bukubendula omugongo.');
INSERT INTO "african_dev" VALUES(94,4,'94:4','Era ne tukuwa okwogerwako.');
INSERT INTO "african_dev" VALUES(94,5,'94:5','(Kisaana kitegerekeke nti) mazima ddala buli awabeera obuzibu, wabaawo obwangu');
INSERT INTO "african_dev" VALUES(94,6,'94:6','Mazima ddala buli awabeera obuzibu wabaawo obwangu.');
INSERT INTO "african_dev" VALUES(94,7,'94:7','Bwoba nga omaze okukola emirimu (egikuyimirizaawo ku nsi) olwo nno yimirira (osinze Katondawo).');
INSERT INTO "african_dev" VALUES(94,8,'94:8','Era eri Omuleziwo Katonda, gy’oba oteeka e ssuubilyo lyonna.');
INSERT INTO "african_dev" VALUES(95,1,'95:1','Ndayira ekibala ekiyitibwa Tiin n’ekibala ekiyitibwa Zaitun.');
INSERT INTO "african_dev" VALUES(95,2,'95:2','Era ndayira olusozi lw’e Sinai.');
INSERT INTO "african_dev" VALUES(95,3,'95:3','Nakino e kibuga (Makkah) eky’emirembe');
INSERT INTO "african_dev" VALUES(95,4,'95:4','Mazima twakola omuntu mu kifaananyi e kisinga okuba ekirungi.');
INSERT INTO "african_dev" VALUES(95,5,'95:5','Ate oluvanyuma tumuzzaayo wansi mu ddaala erisembayo.');
INSERT INTO "african_dev" VALUES(95,6,'95:6','Okugyako abo abakkiriza ne bakola e mirimu e mirungi, bagenda kufuna e mpeera etalina kkomo.');
INSERT INTO "african_dev" VALUES(95,7,'95:7','Bwekiba bw''ekityo (ate gwe omuntu) olimbisa otya olunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(95,8,'95:8','Tolaba nti Katonda ye Mulamuzi asinga okuba omwenkanya era owamazima!?.');
INSERT INTO "african_dev" VALUES(96,1,'96:1','Soma ng''otandika ne linnya lya mukamawo oyo eyatonda (buli kintu).');
INSERT INTO "african_dev" VALUES(96,2,'96:2','Yatonda omuntu nga amuggya mu kisaayisaayi.');
INSERT INTO "african_dev" VALUES(96,3,'96:3','Soma, kuba Omulezi wo (Katonda) ye mugabi asinga.');
INSERT INTO "african_dev" VALUES(96,4,'96:4','Oyo eyayigiriza omuntu okuwandiika n''ekkalaamu.');
INSERT INTO "african_dev" VALUES(96,5,'96:5','Yayigiriza omuntu ebyo (omuntu) bye yali tamanyi.');
INSERT INTO "african_dev" VALUES(96,6,'96:6','Nedda, mazima omuntu agenda neyewaggula.');
INSERT INTO "african_dev" VALUES(96,7,'96:7','Olw’okweraba nti yeemalirira (olw’ebyo by’aba alina).');
INSERT INTO "african_dev" VALUES(96,8,'96:8','Ng’ate Mazima ddala eri Omuleziwo (Katonda) y’eri obuddo.');
INSERT INTO "african_dev" VALUES(96,9,'96:9','Olabye omuntu agaana.');
INSERT INTO "african_dev" VALUES(96,10,'96:10','(Agaana) omuddu wa Katonda bw’aba ng''asaala.');
INSERT INTO "african_dev" VALUES(96,11,'96:11','Tolaba nti gwebagaana ky''akola kya bulungamu!');
INSERT INTO "african_dev" VALUES(96,12,'96:12','Oba bwaba alagidde abantu okutya Katonda.');
INSERT INTO "african_dev" VALUES(96,13,'96:13','Olaba oyo (agaana asaala) bw''alimbisa n''akyuka okuva ku mazima.');
INSERT INTO "african_dev" VALUES(96,14,'96:14','Abaffe tamanyi (oyo alimbisa) nti Mazima Katonda alaba ebyo (by’akola).');
INSERT INTO "african_dev" VALUES(96,15,'96:15','Nedda bw’anaaba teyeekomyeko (ku bikolwa ebyo) tujja kumuwalula nga tumukutte ku kawompo.');
INSERT INTO "african_dev" VALUES(96,16,'96:16','Akawompo ako akajjudde obulimba n’obusobya.');
INSERT INTO "african_dev" VALUES(96,17,'96:17','Kale ayitanga abali mu kinywi kye (eky’obujeemu bamutaase).');
INSERT INTO "african_dev" VALUES(96,18,'96:18','Naffe tujja kukoowoola ba Malayika abakambwe abakuumi b’omuliro (abatuusa ebibonerezo ku bajeemu).');
INSERT INTO "african_dev" VALUES(96,19,'96:19','Nedda (Owange Nabbi Muhammad) togonderanga omuntu ng’oyo. Vunnama (ng''osinza) Katonda era weeyongere okusembera gy''ali.');
INSERT INTO "african_dev" VALUES(97,1,'97:1','Mazima ffe twassa Kur''ani mu kiro ekiyitibwa Layila-tul-kadri (e kiro ekyokugera era ekyekitiibwa).');
INSERT INTO "african_dev" VALUES(97,2,'97:2','Ye nno omanyi Layila-tul-kadri kye ki?');
INSERT INTO "african_dev" VALUES(97,3,'97:3','Layila-tul-kadri kiro kirungi okusinga emyezi olukumi (1000).');
INSERT INTO "african_dev" VALUES(97,4,'97:4','Mu kiro ekyo, ba Mlayika bakka nga ne Jiburilu mwali ku lw’ekiragiro kya mukama waabwe (Katonda). Nga bajja na buli kiragiro.');
INSERT INTO "african_dev" VALUES(97,5,'97:5','Ekiro ekyo kya mirembe myereere, okutuusa obudde okukya.');
INSERT INTO "african_dev" VALUES(98,1,'98:1','Abajeemu mu bantu abaweebwa e kitabo, n''abo abagatta ebintu ebirala ku Katonda, tebaali baakuva ku nzikiriza yaabwe enkyamu okutuusa nga obunnyonnyofu bubajjidde.');
INSERT INTO "african_dev" VALUES(98,2,'98:2','Nga, ye Mubaka (Nabbi Muhammad) eyatumwa okuva ewa Katonda, nga asoma ekitabo ekitukuvu (Kur''ani).');
INSERT INTO "african_dev" VALUES(98,3,'98:3','Ekirimu amateeka amalambulukufu.');
INSERT INTO "african_dev" VALUES(98,4,'98:4','Era abantu abo abaweebwa e kitabo tebayawukanayawukana okutuusa nga obunnyonnyofu bumaze okubajjira.');
INSERT INTO "african_dev" VALUES(98,5,'98:5','So nga ate tebaalagirwa okugyako okusinza Katonda yekka nga buli kyebakola bakikola ku lulwe yekka, awatali ku mugattirizaako kantu konna. Era nga bateekwa okuyimirizaawo e sswala n’okutoola Zzaka. Era nga eyo nno y''eddiini ennambulukufu.');
INSERT INTO "african_dev" VALUES(98,6,'98:6','Mazima abo abaajeema mu bantu abaaweebwa e kitabo n’abo abagatta ku Katonda e bintu ebirala bakuyingira omuliro Jahannama mwe bagenda okubeera olubeerera. Abo nno be babi okusinga ebitonde ebirala byonna.');
INSERT INTO "african_dev" VALUES(98,7,'98:7','Mazima abo abakkiriza ne bakola e mirimu e mirungi be balungi okusinga ebitonde byonna.');
INSERT INTO "african_dev" VALUES(98,8,'98:8','E mpeera yaabwe ewa Katonda waabwe kubayingiza jjana eyitibwa Aden, nga emigga gikulukutira mu yo. Baakugibeeramu olubeerera. Katonda yabasiima nabo ne bamusiima. Ebyo nno bigenda kufunwa oyo eyatya omuleziwe (Katonda).');
INSERT INTO "african_dev" VALUES(99,1,'99:1','E nsi bweriba eyuguumizziddwa oluyuguuma lwayo.');
INSERT INTO "african_dev" VALUES(99,2,'99:2','E nsi n''efulumya (nga ewandula) ebyo byonna ebigirimu.');
INSERT INTO "african_dev" VALUES(99,3,'99:3','Omuntu aligamba nga yeebuuza nti! ebadde ki?');
INSERT INTO "african_dev" VALUES(99,4,'99:4','Ku lunaku olwo egenda kuttottola ebigambo byonna ebikwata ku ebyo ebyakolerwanga ku yo.');
INSERT INTO "african_dev" VALUES(99,5,'99:5','Nti mazima Katonda y''aliba agiragidde okwogera ebyo.');
INSERT INTO "african_dev" VALUES(99,6,'99:6','Abantu ku lunaku olwo baliva mu kabbuli zaabwe nga bali mu bibinja babe nga balagibwa e mirimu gyabwe.');
INSERT INTO "african_dev" VALUES(99,7,'99:7','Omuntu eyakola omulimu omulungi gwonna kagube mutono gutya agenda kugulaba.');
INSERT INTO "african_dev" VALUES(99,8,'99:8','N''omuntu eyakola omulimu omubi gwonna kagube mutono gutya agenda kugulaba.');
INSERT INTO "african_dev" VALUES(100,1,'100:1','Ndayira embalaasi ezizinda(ezidduka) nga ziweevuuma.');
INSERT INTO "african_dev" VALUES(100,2,'100:2','Nezimansula e nsasi z’omuliroolw’okukoona enjazi.');
INSERT INTO "african_dev" VALUES(100,3,'100:3','Ezizinda ku maliiri.');
INSERT INTO "african_dev" VALUES(100,4,'100:4','Nezisiikuula enfuufu, mu kutyekuula emisinde okwo.');
INSERT INTO "african_dev" VALUES(100,5,'100:5','Neziyingira mu balabe wakati.');
INSERT INTO "african_dev" VALUES(100,6,'100:6','Mazima ddala omuntu mutomi eri omuleziwe.');
INSERT INTO "african_dev" VALUES(100,7,'100:7','Era mazima ye (omuntu) ku ky''obutomi mujulizi.');
INSERT INTO "african_dev" VALUES(100,8,'100:8','Era mazima yye muyitirivu nnyo mu kwagala ebyenfuna.');
INSERT INTO "african_dev" VALUES(100,9,'100:9','Abaffe (omuntu) tamanyi nti ebiri mu kabbuli bwe biriba bizuukiziddwa.');
INSERT INTO "african_dev" VALUES(100,10,'100:10','N’ebiri mu bifuba ne byolesebwa.');
INSERT INTO "african_dev" VALUES(100,11,'100:11','Mazima Katonda omulezi waabwe (nga bulijjo ne) ku lunaku olwo agenda kuba ng''amanyi byonna ebibakwatako.');
INSERT INTO "african_dev" VALUES(101,1,'101:1','Ekikoona (ekyentiisa).');
INSERT INTO "african_dev" VALUES(101,2,'101:2','Ekikoona kye ki?');
INSERT INTO "african_dev" VALUES(101,3,'101:3','Naye omanyi ekikoona kye ki?');
INSERT INTO "african_dev" VALUES(101,4,'101:4','Lwe lunaku abantu lwe balibeera nga e biwojjolo ebisaasaanye.');
INSERT INTO "african_dev" VALUES(101,5,'101:5','N’ensozi lwe zirifuuka nga pamba omusuunsule afuumuuka.');
INSERT INTO "african_dev" VALUES(101,6,'101:6','Naye, omuntu alibeera n''emirimu e mirungi nga gizitowa (okusinga ebibibye).');
INSERT INTO "african_dev" VALUES(101,7,'101:7','Oyo agenda kubeera mu bulamu obw’okwesiima.');
INSERT INTO "african_dev" VALUES(101,8,'101:8','Ate oyo aliwewukirwa emirimu gye emirungi (ebibi bye n’ebisinga obuzito).');
INSERT INTO "african_dev" VALUES(101,9,'101:9','Oyo nno, obuddo n''obutuulo bwe muliro oguyitibwa “Haawiya”.');
INSERT INTO "african_dev" VALUES(101,10,'101:10','Naye omanyi ‘Haawiya’ kye ki?');
INSERT INTO "african_dev" VALUES(101,11,'101:11','Gwe muliro ogwengeredde (ogwokya ennyo).');
INSERT INTO "african_dev" VALUES(102,1,'102:1','Okunoonya okufuna ebingi, kubalabankanyizza (ne kubajja ku kugondera amateeka ga Katonda).');
INSERT INTO "african_dev" VALUES(102,2,'102:2','Okutuusa lwe muyingira kabbuli.');
INSERT INTO "african_dev" VALUES(102,3,'102:3','Nedda, kyaddaki mujja kumanya (mu kiseera ky''okufa)');
INSERT INTO "african_dev" VALUES(102,4,'102:4','Ate era oluvanyuma, kyaddaki mujja kumanya (nga muyingidde kabbuli)');
INSERT INTO "african_dev" VALUES(102,5,'102:5','Nedda, singa nga mubadde mumanyi mu bukakafu bw''okumanya (temwandirabankanyiziddwa na kukungaanya bingi)');
INSERT INTO "african_dev" VALUES(102,6,'102:6','Mujja kulabira ddala omuliro “Jahiim”.');
INSERT INTO "african_dev" VALUES(102,7,'102:7','Ate oluvanyuma omuliro ogwo mujja kugulaba mugutegeerere ddala.');
INSERT INTO "african_dev" VALUES(102,8,'102:8','Ate ddala oluvanyuma mugenda kubuuzibwa e byengera bye mwafuna ku nsi (mwabifuna mu makubo ki, era mwabisaasaanya mu makubo ki).');
INSERT INTO "african_dev" VALUES(103,1,'103:1','Ndayira omulembe (buli mulembe)');
INSERT INTO "african_dev" VALUES(103,2,'103:2','Mazima ddala omuntu yenna gy’afa yenkana ali mu kufaafaaganirwa.');
INSERT INTO "african_dev" VALUES(103,3,'103:3','Okugyako abo abakkiriza Katonda (mu nzikiriza entuufu) ate ne bakola e mirimu emirungi, era ne balaamirigana okukoleranga ku mazima (mu bulamu bwabwe bwonna) era ne balaamiragana okubeera abagumikiriza (mu bulamu bwabwe bwonna).');
INSERT INTO "african_dev" VALUES(104,1,'104:1','Okubonaabona okwamaanyi kugenda kubeera ku buli muntu atambuza engambo, ageya banne n''abaggya obumogo.');
INSERT INTO "african_dev" VALUES(104,2,'104:2','Oyo akungaanya ebyo bugagga n’abituuma.');
INSERT INTO "african_dev" VALUES(104,3,'104:3','Alowooza nti eby''obugaggabye byakumukuumira ku nsi obugenderevu.');
INSERT INTO "african_dev" VALUES(104,4,'104:4','Nedda, ajja kukanyugwa mu muliro oguyitibwa “Hutwama”.');
INSERT INTO "african_dev" VALUES(104,5,'104:5','Ye omanyi “Hutwama” kye ki?');
INSERT INTO "african_dev" VALUES(104,6,'104:6','(Hutwama) muliro gwa Katonda omukume, ogubumbujja.');
INSERT INTO "african_dev" VALUES(104,7,'104:7','Ogwo ogwokya negutuukira ddala ku mitima.');
INSERT INTO "african_dev" VALUES(104,8,'104:8','Mazima ddala omuliro ogwo (Hutwama) ku bantu abo (abogeddwaako) gugenda kubabuutikira.');
INSERT INTO "african_dev" VALUES(104,9,'104:9','Mu midumu emiwanvu.');
INSERT INTO "african_dev" VALUES(105,1,'105:1','Tewamanya Katonda kyeyakola eggye ly’enjovu?');
INSERT INTO "african_dev" VALUES(105,2,'105:2','Enkwe zaabwe tezaagwa butaka?');
INSERT INTO "african_dev" VALUES(105,3,'105:3','N''abasindikira e binnyonyi nga bijjira mu bibinja.');
INSERT INTO "african_dev" VALUES(105,4,'105:4','Nga bibakanyugira amayinja amookye.');
INSERT INTO "african_dev" VALUES(105,5,'105:5','Nebafuuka nga omuddo ogagayiddwa ensolo ne gufuluma nga gufuuse obusa.');
INSERT INTO "african_dev" VALUES(106,1,'106:1','Ku lw''empangaala ya ba Kuraish.');
INSERT INTO "african_dev" VALUES(106,2,'106:2','Empangaala yaabwe eyalimu okutambula engendo mu butiti ne mu kyeya.');
INSERT INTO "african_dev" VALUES(106,3,'106:3','Bateekwa basinze Katonda nannyini nyumba eno (Kaaba).');
INSERT INTO "african_dev" VALUES(106,4,'106:4','Katonda eyabawa eby''okulya, n’abawonya enjala, n’abawa e mirembe, ne bataba mu kutya.');
INSERT INTO "african_dev" VALUES(107,1,'107:1','Olabye oyo alimbisa olunaku lw''enkomerero.');
INSERT INTO "african_dev" VALUES(107,2,'107:2','Oyo nno y’oyo awalagganya ba mulekwa.');
INSERT INTO "african_dev" VALUES(107,3,'107:3','Era nga takubiriza kuliisa banaku.');
INSERT INTO "african_dev" VALUES(107,4,'107:4','Okubonaabona kuli ku abo abasaala,');
INSERT INTO "african_dev" VALUES(107,5,'107:5','Nga balagajjalira e sswala zaabwe.');
INSERT INTO "african_dev" VALUES(107,6,'107:6','Abo abakola bandabe.');
INSERT INTO "african_dev" VALUES(107,7,'107:7','Era abamma bannaabwe ebintu ebitasaana kumma.');
INSERT INTO "african_dev" VALUES(108,1,'108:1','Mazima ffe twakuwa e birungi e bingi, (mu ebyo mwe muli n''oluzzi Kauthara olw''omu jjana).');
INSERT INTO "african_dev" VALUES(108,2,'108:2','N''olwekyo wenywereze ku kusaala ku lwa Katondawo, era osale n’ebisolo, nga osaddaaka ku lwa Katondawo.');
INSERT INTO "african_dev" VALUES(108,3,'108:3','Mazima oyo akuwalagganya, (ng''akuvuma era n''okukujeeja) ye wenkuggu.');
INSERT INTO "african_dev" VALUES(109,1,'109:1','Bagambe nti, abange mmwe abatakkiriza (abawakanyi).');
INSERT INTO "african_dev" VALUES(109,2,'109:2','Ssisinza ebyo bye musinza (amasanamu, lubaale, n''ebirala).');
INSERT INTO "african_dev" VALUES(109,3,'109:3','Wadde namwe temusinza oyo gwe nsinza (ali omu mu bwa Katonda bwe).');
INSERT INTO "african_dev" VALUES(109,4,'109:4','Era siri waakusinza ebyo bye musinza.');
INSERT INTO "african_dev" VALUES(109,5,'109:5','Era nammwe temuli baakusinza oyo gwensinza.');
INSERT INTO "african_dev" VALUES(109,6,'109:6','Mulina e nkola yammwe (ey''okusinza ba Katonda abangi, nange nnina enkola yange (ey''okusinza Katonda omu yekka).');
INSERT INTO "african_dev" VALUES(110,1,'110:1','Okutaasa kwa Katonda n’obuwanguzi bwe bijjanga;');
INSERT INTO "african_dev" VALUES(110,2,'110:2','N’olaba abantu nga bayingira e ddiini ya Katonda mu bibinja;');
INSERT INTO "african_dev" VALUES(110,3,'110:3','Otenderezanga Mukama Katondawo n’ebitendobye, era omwetondere, mazima yye akkiriza okwenenya.');
INSERT INTO "african_dev" VALUES(111,1,'111:1','Mu Hadith eri mu kitabo kya Bukhari Nabbi yagenda ku Batwhahu naakuba wuuli abantu bangi bajja nga balowooza nti waliyo ebya maguzi, Nabbi bweyatandika okubabuulira ebikwata ku Katonda, Abu Lahbi naakasuka e mikono nga bwagamba nti kino kyotukunganyirizza era kyotuyitidde? Zikirira. Katonda kwe kumwanukula mu ssura eno naagamba nti: (Gizikirire e mikono gya Abu Lahbi (gyawuuba), era ddala yenna yazikirira.');
INSERT INTO "african_dev" VALUES(111,2,'111:2','Eby''obugaggabwe tebimugasizza, wadde n’ebirala bye yafuna, (nga e kitiibwa).');
INSERT INTO "african_dev" VALUES(111,3,'111:3','Ajja kwesonseka mu muliro ogubumbujja.');
INSERT INTO "african_dev" VALUES(111,4,'111:4','Ne mukyalawe omwetissi w’enku (naye bwatyo).');
INSERT INTO "african_dev" VALUES(111,5,'111:5','Nga mu nsingoye mulimu omuguwa omulange.');
INSERT INTO "african_dev" VALUES(112,1,'112:1','(Gwe Nabbi Muhammad) bagambe nti Katonda ali omu mu bwa Katondabwe (era nga yye yekka ateekeddwa okusinzibwa).');
INSERT INTO "african_dev" VALUES(112,2,'112:2','Katonda y''akola ku byetaago by''ebitonde byonna.');
INSERT INTO "african_dev" VALUES(112,3,'112:3','Tazaala, era teyazaalibwa.');
INSERT INTO "african_dev" VALUES(112,4,'112:4','Era teri kintu kyonna kimwenkana.');
INSERT INTO "african_dev" VALUES(113,1,'113:1','Gamba nti, nsaba obukuumi ewa Katonda afuga okukya kw’obudde.');
INSERT INTO "african_dev" VALUES(113,2,'113:2','Mu bubi bw’ebyo bye yatonda.');
INSERT INTO "african_dev" VALUES(113,3,'113:3','Ne mu bubi bw’ekiro nga kikutte.');
INSERT INTO "african_dev" VALUES(113,4,'113:4','Ne mu bubi bw’abakazi abalogo abasiba ebifundikwa (ne babifuuwamu nga balaamiriza).');
INSERT INTO "african_dev" VALUES(113,5,'113:5','Ne mu bubi obuva mu mukozi wensaalwa ng''akoze ensaalwa.');
INSERT INTO "african_dev" VALUES(114,1,'114:1','Gamba nti, nsaba obukuumi okuva eri Mukama omulabirizi wa bantu.');
INSERT INTO "african_dev" VALUES(114,2,'114:2','Omufuzi w''abantu');
INSERT INTO "african_dev" VALUES(114,3,'114:3','Asinzibwa abantu.');
INSERT INTO "african_dev" VALUES(114,4,'114:4','Amponye obubi bw’omulabankanya sitaani omwekwesi');
INSERT INTO "african_dev" VALUES(114,5,'114:5','Oyo alabankanya (e mitima egiri) mu bifuba by''abantu.');
INSERT INTO "african_dev" VALUES(114,6,'114:6','(Sitaani) ow’omu majiinni ne sitaani ow’omu bantu.');
