INSERT INTO "ahmeti" VALUES(1,1,'1:1','Me emrin e All-llahut, Mëshiruesit, Mëshirëbërësit!');
INSERT INTO "ahmeti" VALUES(1,2,'1:2','Falënderimi i takon All-llahut, Zotit të botërave!');
INSERT INTO "ahmeti" VALUES(1,3,'1:3','Mëshiruesit, Mëshirëbërësit!');
INSERT INTO "ahmeti" VALUES(1,4,'1:4','Sunduesit të ditës së Gjykimit!');
INSERT INTO "ahmeti" VALUES(1,5,'1:5','Ty të adhurojmë dhe prej Teje ndihmë kërkojmë!');
INSERT INTO "ahmeti" VALUES(1,6,'1:6','Udhëzona në rrugën e drejtë!');
INSERT INTO "ahmeti" VALUES(1,7,'1:7','Në rrugën e atyre ndaj të cilëve ke bekimin, e jo në të atyre që je i hidhëruar, dhe që kanë humbur!');
INSERT INTO "ahmeti" VALUES(2,1,'2:1','Elif, Lam, Mim.');
INSERT INTO "ahmeti" VALUES(2,2,'2:2','Ky është libri në të cilin nuk ka dyshim, udhëzues për ata që frikësohen (nga All-llahu).');
INSERT INTO "ahmeti" VALUES(2,3,'2:3','Të cilët e besojnë të fshehtën, e kryejnë faljen dhe prej asaj që Ne i kemi furnizuar, japin.');
INSERT INTO "ahmeti" VALUES(2,4,'2:4','Dhe ata të cilët e besojnë atë që t’u shpall Ty, dhe atë që është shpallur para teje, dhe të cilët për ahiretin (botën tjetër) janë të bindur.');
INSERT INTO "ahmeti" VALUES(2,5,'2:5','Të tillët janë të udhëzuarit nga Zoti i tyre dhe të tillët janë të shpëtuarit.');
INSERT INTO "ahmeti" VALUES(2,6,'2:6','E për ata që mohuan është njëlloj, ua tërhoqe vërejtjen apo nuk ua tërhoqe, ata nuk besojnë.');
INSERT INTO "ahmeti" VALUES(2,7,'2:7','All-llahu vëndoi vulë në zemrat e tyre dhe vëshët e tyre, ndërsa në të pamurit e tyre ka një perde, e për ta ka dënim të madh.');
INSERT INTO "ahmeti" VALUES(2,8,'2:8','Ka prej njerëzve që thonë: “Ne i besojmë All-llahut dhe jetës tjetër (ahiretit)”, por ata nuk janë besimtarë.');
INSERT INTO "ahmeti" VALUES(2,9,'2:9','(Përpiqen ta) mashtrojnë All-llahun dhe ata që besuan, por nuk mashtrojnë tjetërkë pos vetes, dhe nuk e kuptojnë!');
INSERT INTO "ahmeti" VALUES(2,10,'2:10','Në zemrat e tyre ka sëmundje, pra All-llahu ua shton sëmundjen, dhe pësojnë dënim të dhembshëm për shkak se përgënjenin.');
INSERT INTO "ahmeti" VALUES(2,11,'2:11','E kur atyre u thuhet: “Mos bëni ç’rregullira në tokë”! Ata thonë: “Ne jemi vetëm paqësues!”');
INSERT INTO "ahmeti" VALUES(2,12,'2:12','A nuk janë vërtet ata ç’rregulluesit?; Por nuk kuptojnë!');
INSERT INTO "ahmeti" VALUES(2,13,'2:13','Dhe kur atyre u thuhet: Besoni sikurse besuan njerëzit! Ata thonë: “A të besojmë ashtu siç besuan mendjelehtit?” A nuk janë vërtet mu ata mendjelehtit?; Por nuk e dinë.');
INSERT INTO "ahmeti" VALUES(2,14,'2:14','E kur i takojnë ata që besuan thonë: “Ne besojmë!” por kur veçohen me djajt e vet u thonë: “Ne jemi me ju, ne vetëm jemi tallur”.');
INSERT INTO "ahmeti" VALUES(2,15,'2:15','All-llahu tallet me ta, dhe ua shton të këqiat duke i lënë të bredhin.');
INSERT INTO "ahmeti" VALUES(2,16,'2:16','Të tillët janë ata që blenë lajthitjen me udhëzimin, pra nuk pati fitim tregëtia e tyre dhe ata nuk qenë të udhëzuar.');
INSERT INTO "ahmeti" VALUES(2,17,'2:17','Shembulli i tyre është si shembulli i atij që ndezë një zjarr dhe posa ai ndriçon vendin përreth tij, All-llahu ua shuan dritën atyre dhe i lë në errësira, (dhe aty) nuk shohin.');
INSERT INTO "ahmeti" VALUES(2,18,'2:18','(Janë) Të shurdhër, memecë dhe të verbër, ata nuk kthehen (nga rruga e shtrembër).');
INSERT INTO "ahmeti" VALUES(2,19,'2:19','Ose (Shembulli i tyre është) si ndonjë shi i rreptë prej së larti me errësirë, me bubullimë e me vetëtimë, e prej frikës së vdekjes, nga rrufeja, ata vejnë gishtat në veshët e tyre. Po All-llahu është rrethues i mosbesimtarëve (atij nuk mund t’i shpëtojë askush).');
INSERT INTO "ahmeti" VALUES(2,20,'2:20','Vetëtima gati ua merrë të pamurit dhe, saherë që ajo u bënë dritë atyre, ata ecin në te, e kur u errësohet mbesin aty. E sikur të donte All-llahu, do t’ua merrte të dëgjuarit (me krismën e bubullimës) dhe të pamurit (me shkëndijën e vetëtimës). Vërtetë, All-llahu është i plotëfuqishëm për çdo gjë.');
INSERT INTO "ahmeti" VALUES(2,21,'2:21','U ju njerëz, adhuronie Zotin tuaj, i cili ju krijoi juve ashtu që të jeni të devotshëm (të shpëtuar).');
INSERT INTO "ahmeti" VALUES(2,22,'2:22','Ai, i cili për ju bëri tokën shtrat, (vendbanim) e qiellin kulm, e prej qiellit ju lëshoi shi me të cilin ju siguroi lloje të frutave si ushqim për ju, pra mos i përshkruani All-llahut shokë, duke qenë se ju e dini (që Ai nuk ka shokë).');
INSERT INTO "ahmeti" VALUES(2,23,'2:23','E në qoftë se jeni në dyshim në atë që Ne ia shpallëm gradualisht robit tonë, atëherë sillnie ju një kaptinë të ngjashme si ai (Kur’ani) dhe thirrni (për ndihmë) dëshmitarët tuaj (zotërat) pos All-llahut, nëse jeni të sinqert (në thëniet tuaja se Kur’ani nuk është prej Zotit).');
INSERT INTO "ahmeti" VALUES(2,24,'2:24','E mos e paçit bërë (deri më tash), e as që do ta bëni kurrë (edhe në të ardhmën), atëherë ruajuni zjarrit, lëndë e të cilit janë njerëzit dhe gurët, që është i përgaditur për mosbesimtarët.');
INSERT INTO "ahmeti" VALUES(2,25,'2:25','E, përgëzoi ata që besuan dhe bënë vepra të mira se ata do të jenë në Xhennete në të cilët rrjedhin lumenj. Saherë që u jepet ndonjë ushqim nga frutat e tij, ata thonë: “Ky është që me te u ushqyem edhe më parë”. Ngase, u sillet ushqim i ngjashëm (vetëm në formë, e jo edhe në shije). Aty do të kenë ata bashkëshorte të pastra dhe aty do të jenë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(2,26,'2:26','All-llahu nuk ngurron që të marrë çfarëdo shembulli, qoftë mushkonjë a diçka edhe më e imët se ajo. Sa u përket atyre që besuan, ata e dinë se është e vërtetë nga Zoti i tyre, ndërsa ata të cilët mohuan do të thonë: “Ç’deshi All-llahu me këtë shembull?” Ai me të lajthitë shumë, dhe udhëzon me të shumë (në rrugën e drejtë), por nuk lajthitë me të (tjetërkë), pos fasikët (mëkatarët e mëdhenj).');
INSERT INTO "ahmeti" VALUES(2,27,'2:27','Ata që e thyejnë besën e dhënë All-llahut pasiqë është lidhur ajo, dhe e këpusin atë që All-llahu ka urdhëruar të jetë e kapur (sa i takon fesë së All-llahut, monoteizmit Islam, dhe të praktikohen ligjet e saj në tokë dhe gjithashtu sa i takon të mbajturit e lidhjeve të mira me farefisin dhe miqësinë) e edhe bëjnë shkatërrimie në tokë, të tillët janë ata të dështuarit.');
INSERT INTO "ahmeti" VALUES(2,28,'2:28','Si e mohoni All-llahun, e dihet se ju ishit të vdekur, e Ai ju ngjalli, mandej ju bën të vdisni e pastaj ju ringjall, e mandej te Ai do të ktheheni?');
INSERT INTO "ahmeti" VALUES(2,29,'2:29','Ai (All-llahu) është që për juve krijoi gjithçka ka në tokë, pastaj vullnetin e vet ia drejtoi qiellit dhe i bëri ata shtatë qiej. Ai është i Gjithëdijshmi për çdo gjë.');
INSERT INTO "ahmeti" VALUES(2,30,'2:30','(Përkujto Muhammed) Kur Zoti yt u tha engjëjve: “Unë po krijoj (po përcaktoj) në tokë një zëvendës”! Ata thanë: “A do të vëshë në te atë që bënë çrregullime dhe që derdh gjaqet, e ne të madhërojmë Ty me lavdërimin Tënd dhe plotësisht të adhurojmë”! Ai tha: “Unë di atë që ju nuk dini”!');
INSERT INTO "ahmeti" VALUES(2,31,'2:31','E Ai (Zoti) ia mësoi Ademit të gjithë emrat (e sendeve), pastaj ata ua prezentoi engjëjve dhe u tha: “Më tregoni për emrat e këtyre (sendeve të emërtuara), nëse jeni të drejt (çka mendoni)?”');
INSERT INTO "ahmeti" VALUES(2,32,'2:32','(Engjëjt) Thanë: “Ti je i pa të meta, ne kemi dije tjetër përveç atë që na mësove Ti. Vërtetë, Ti je i gjithdijshëmi, i urti!”');
INSERT INTO "ahmeti" VALUES(2,33,'2:33','(Zoti) Tha: “O Adem, njoftoj ata (engjëjt) me emrat e atyre (sendeve)!” E kur u rrëfeu atyre për emrat e tyre, (Zoti) tha: “A nuk u kam thënë juve se Unë, më së miri e di fshehtësinë e qiejve e të tokës dhe më së miri e di atë, që ju e publikoni dhe atë që e mbani fshehtë.”');
INSERT INTO "ahmeti" VALUES(2,34,'2:34','E kur u thamë engjëjve: përuluni (bini në sexhde) Ademit, ata menjëherë iu përulën, me përjashtim të iblisit (djallit). Ai rrefuzoi dhe u mbajtë në të madh dhe u bë pabesimtar.');
INSERT INTO "ahmeti" VALUES(2,35,'2:35','Ne i thamë: “O Adem, ti dhe bashkëshortja juaj banoni në Xhennet dhe hani lirisht nga frutat e tij kah të doni, po mos iu afroni asaj bime (peme) e të bëheni zulumqarë (të vetvetes suaj).”');
INSERT INTO "ahmeti" VALUES(2,36,'2:36','Po djalli i bëri që ata të dy të mashtrohen në atë (pemë ose Xhennetin) dhe i nxori ata nga ajo (e mirë) që ishin në te, e Ne u thamë: “Zbritni (dilni), jeni armik i njëri-tjetrit, e ju deri në një kohë në tokë keni vendbanim dhe dëfrim”.');
INSERT INTO "ahmeti" VALUES(2,37,'2:37','E Ademi prej Zotit të vet pranoi disa fjalë (lutje), prandaj Ai ia fali (gabimin), s’ka dyshim se Ai është Mëshiruesi, Pendimpranuesi.');
INSERT INTO "ahmeti" VALUES(2,38,'2:38','Ne u thamë: “Dilni prej atyhit që të gjithë, e juve gjithqysh do t’u arrijë udhëzim prej Meje, e kush pranon udhëzimin Tim, për ata nuk ka as frikë as që do të brengosen”.');
INSERT INTO "ahmeti" VALUES(2,39,'2:39','E ata që mohuan dhe përgënjeshtruan argumentet tona, të tillët janë banues të zjarrit, ata do të jenë aty përgjithmonë.');
INSERT INTO "ahmeti" VALUES(2,40,'2:40','O bijtë e israilit, kujtoni (jini mirnjohës për) të mirat e Mia, të cilat ua dhurova juve dhe zbatoni premtimin që më keni dhënë Mua. Unë zbatoj atë që u premtova dhe të më keni frikë vetëm Mua.');
INSERT INTO "ahmeti" VALUES(2,41,'2:41','Dhe besonie atë, që e shpalla (Kur’anin), e që vërteton atë e keni ju e mos u bëni mohues të parë të tij, dhe mos i ndërroni ajetet e Mia (Kur’anin) me një vlerë të paktë, por vetëm Mua të ma keni dronë.');
INSERT INTO "ahmeti" VALUES(2,42,'2:42','E mos e ngatërroni të vërtetën me të pavërtetën dhe me vetëdije të fshehni realitetin.');
INSERT INTO "ahmeti" VALUES(2,43,'2:43','Falni namazin dhe jepni zeqatin, dhe përuluni (në ruku’) me ata që përulen.');
INSERT INTO "ahmeti" VALUES(2,44,'2:44','A po i urdhëroni njerëzit për punë të mira, e veten tuaj po e harroni? Ndërsa ju e lexoni librin (Tevratin). A nuk po mendoni?');
INSERT INTO "ahmeti" VALUES(2,45,'2:45','Kërkoni ndihmë (në të gjitha çështjet) me durim dhe me namaz, vërtetë, ajo është e madhe (e vështirë), por jo edhe për ata që kanë frikë (Zotin).');
INSERT INTO "ahmeti" VALUES(2,46,'2:46','Të cilët janë të bindur se do të takojnë Zotin e vet dhe se ata do t’i kthehen Atij.');
INSERT INTO "ahmeti" VALUES(2,47,'2:47','O bijtë e israilit, përkujtone dhuntinë, Time, të cilën ua dhurova, dhe Unë ju pata (të parët tuaj) dalluar ndaj njerëzve të tjerë (të asaj kohe).');
INSERT INTO "ahmeti" VALUES(2,48,'2:48','Dhe ruajuni një ditë kur askush askujt nuk do të mund t’i kryej asgjë, kur nuk pranohet për te (jobesimtarin) ndonjë ndërmjetësim dhe nuk pranohet për te kompensim, e as që do të ndihmohen ata (fajtorët).');
INSERT INTO "ahmeti" VALUES(2,49,'2:49','Përkujtoni edhe kur ju shpëtuam prej popullit të faraonit, që nga ai shijuat dënimin më të idhët, duke ua therrur bijtë tuaj, e duke ua lënë gjallë gratë tuaja. Në këtë torturë përjetuat një sprovim të madh nga Zoti juaj.');
INSERT INTO "ahmeti" VALUES(2,50,'2:50','Dhe kur për ju e ndamë detin, e ju shpëtuam, ndërsa ithtarët e faraonit i fundosëm, e ju i shihnit (me sytë tuaj).');
INSERT INTO "ahmeti" VALUES(2,51,'2:51','E kur i premtuam Musait (t’ia japim Tevratin) dyzet netë, pas tij ju (pasi shkoi ai për Tevrat) e adhuruat viçin, ju ishit dëmtues (të vetes suaj).');
INSERT INTO "ahmeti" VALUES(2,52,'2:52','Mandej edhe pas asaj ua falëm (gabimin), ashtu që të falenderoni.');
INSERT INTO "ahmeti" VALUES(2,53,'2:53','Dhe (përkujtoni) kur ia dhamë Musait librin, dalluesin në mënyrë që të udhëzoheni në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(2,54,'2:54','Dhe kur Musai popullit të vet i tha: “O populli im, me adhurimin e viçit (në vend të Zotit), ju i bëtë zullum vetvetes, pra pendohuni para Krijuesit tuaj, dhe mbytne vetveten. Kjo për ju është më së miri te Krijuesi juaj. E Ai ua pranoi pendimin tuaj, Ai është mëshirues, ndaj pranoi shumë pendimin.');
INSERT INTO "ahmeti" VALUES(2,55,'2:55','Dhe kur i thatë: “O Musa, ne nuk të besojmë ty derisa ta shohim All-llahun haptazi, e atëherë juve u rrëmbeu rrufeja (zjarri) dhe ju e shihnit.');
INSERT INTO "ahmeti" VALUES(2,56,'2:56','Pastaj, që të jeni mirënjohës pas vdekjes suaj juve ju ngjallëm.');
INSERT INTO "ahmeti" VALUES(2,57,'2:57','Dhe Ne bëmë që retë t’u bëjnë juve hije, ju furnizuam me rrëshirë (të ëmbël -Manna) dhe me shkurtëza (Salwa). (Ju thamë) Hani nga të mirat që ju furnizuam! (Ata nuk qenë mirënjohës). Po Neve ata nuk na bënë kurrfarë dëmi, por ata dëmtuan vetveten.');
INSERT INTO "ahmeti" VALUES(2,58,'2:58','E kur ju thamë: “Hyni në këtë fshat (vendbanim), dhe hani në te lirisht ku të dëshironi, e hyni në derën (e fshatit) përulur dhe thuani: “Hittatun” - ndjesë, Ne ua falim mëkatet tuaja, e bamirësve ua shtojmë shpërblimin.');
INSERT INTO "ahmeti" VALUES(2,59,'2:59','E ata që ishin mizorë atë që u ishte thënë e ndryshuan me një fjalë tjetër, e Ne për shkak se ata kundërshtuan, lëshuam nga qielli Rizjan (lloj dënimi) kundër atyre që ishin mizorë.');
INSERT INTO "ahmeti" VALUES(2,60,'2:60','Dhe (përkujtoni) kur Musai kërkoi ujë për popullin e tij, e Ne i thamë: “Bjeri gurit me shkopin tënd”, atëherë nga ai gufuan dymbëdhjetë kroje që secili grup e dinte vendin ku do të pinte ujë. (U thamë) Hani dhe pini nga begatitë e All-llahut e mos vazhdoni të jeni çrregullues në tokë.');
INSERT INTO "ahmeti" VALUES(2,61,'2:61','Madje kur ju thatë: “O Musa, ne nuk mund të durojmë (hamë) vetëm një ushqim (të njëjtë), lute pra Zotin tënd për ne të na furnizojë me çka mbinë toka prej perimeve të saj, prej trangujve, prej hudrave, prej thjerze (groshë) dhe prej qepëve të saj”! (Musai) Tha: “A kërkoni ta ndërroni të mirën për atë që është me e thjeshtë”? Zdirgjuni pra në qytet, se aty do të keni atë që kërkoni! E mbi ta rëndoi poshtrimi dhe skamja, e ata kundër vetes shkaktuan hidhërimin e All-llahut. Kjo ndodhi ngase ata mohonin argumentet e All-llahut, mbytnin pejgamberët pa kurrfarë të drejte, dhe për shkak se kundërshtuan dhe i kalonin kufijt në të keqe.');
INSERT INTO "ahmeti" VALUES(2,62,'2:62','Vërtet, ata që besuan, ata që ishin jehudi, krishterët sabejët, kush besoi prej tyre (sinqerisht) All-llahun, dhe botën tjetër dhe bëri vepra të mira, ata e kanë shpërblimin te Zoti i tyre. Për ta nuk ka frikë as nuk kanë përse të pikëllohen.');
INSERT INTO "ahmeti" VALUES(2,63,'2:63','(Përkujtoni) Kur Ne patëm marrë prej jush besën tuaj, ngritëm mbi ju (kodrën) Turin (u thamë): veproni sipas atij (Tevratit) që ua dhamë me seriozitet, e mësoni atë që është në te, ashtuqë të ruheni.');
INSERT INTO "ahmeti" VALUES(2,64,'2:64','E pas asaj (besës) ju më vonë ia kthyet shpinën e po të mos ishte mirësia dhe mëshira e All-llahut ndaj jush, ju do të ishit prej të dëshpruarve (në të dy jetërat).');
INSERT INTO "ahmeti" VALUES(2,65,'2:65','Ju tanimë e keni të njohur çështjen e atyre nga mesi juaj që nuk respektuan (urdhërin) në të shtunën, e Ne u thamë: Shndërrohuni në majmunë të përbuzur!');
INSERT INTO "ahmeti" VALUES(2,66,'2:66','Atë (shndërrimin e tyre) e bëmë masë ndëshkuese për ata që e përjetuan (me sy) dhe për të pastajshmit, por edhe si këshillë për të devotshmit.');
INSERT INTO "ahmeti" VALUES(2,67,'2:67','(Përkutjoni) Edhe kur Musai popullit të vet i tha: “All-llahu ju urdhëron ta therrni një lopë! Ata thanë: “A bën tallje me ne?” Ai tha: “All-llahut i mbështetem të më ruaj e të mos bëhem nga injorantët!”');
INSERT INTO "ahmeti" VALUES(2,68,'2:68','Ata thanë: “Lute Zotin tënd për ne të na sqarojë çfarë është ajo? Ai tha: “Ai thotë se ajo është një lopë as e vjetër (e moshuar) as e re (mëshqerë), është e mesme, zbatoni pra atë që urdhëroheni!”');
INSERT INTO "ahmeti" VALUES(2,69,'2:69','Ata thanë: “Lute Zotin tënd për ne që të na sqarojë çfarë është ngjyra e saj? A tha: “Ai thotë se ajo është një lopë e verdhë, ngjyra e saj është e fortë që kënaqë shikuesit”.');
INSERT INTO "ahmeti" VALUES(2,70,'2:70','Ata thanë: “Lute Zotin tëndë për ne që të na sqarojë çfarë është ajo, se lopët na janë përzier (janë bërë të ngjashme) e ne do të gjejmë të vërtetën në dashtë All-llahu!');
INSERT INTO "ahmeti" VALUES(2,71,'2:71','Ai tha: “Ai thotë se ajo është lopë jo e lodhur duke lëruar tokën as duke ujitur bimët, ajo është pa të meta dhe në të nuk ka shenjë (ngjyrë tjetër)!” Ata thanë: “E tash na e sqarove saktë dhe e therrën atë, e për pak e lanë pa e kryer punën.');
INSERT INTO "ahmeti" VALUES(2,72,'2:72','(Përkujtoni) Kur e patët mbytur një njeri dhe u kundërshtuat mes veti për te, e All-llahu është zbulues i asaj që e mbanit fshehtë.');
INSERT INTO "ahmeti" VALUES(2,73,'2:73','E ne u thamë: “Mëshoni atij (të vdekurit) me një pjesë të saj (të lopës së therrur)!” Ja, kështu All-llahu ngjallë të vdekurit dhe ua sqaron argumentet e veta, ashtu që të kuptoni.');
INSERT INTO "ahmeti" VALUES(2,74,'2:74','Edhe pas (fakteve të qarta) zemrat tuaja u bënë pasandej të forta si guri, e edhe më të forta, sepse ka nga gurët prej të cilëve gufojnë lumenj, e ka disa prej tyre që çahen dhe prej tyre buron ujë, madje ka prej tyre që nga frika ndaj Zotit rrokullisen tatëpjetë (nga maja e kodrës). All-llahu nuk është i pakujdeshëm ndaj asaj që veproni ju.');
INSERT INTO "ahmeti" VALUES(2,75,'2:75','A shpresoni se do t’u besojnë ata juve (jehudët), kur dihet se një grup prej tyre dëgjuan fjalët e All-llahut, edhe pse i kishin kuptuar, e duke qenë të vetëdijshëm (ç’bënin) i ndryshuan ato.');
INSERT INTO "ahmeti" VALUES(2,76,'2:76','E kur takonin ata që kishin besuar (muslimanët) thonin: “Ne kemi besuar! E kur veçoheshin ata mes veti thonin: “A po ju tregoni atyre (muslimanëve) për atë që All-llahu ua shpalli juve (në Tevrat rreth Muhammedit a. s.) që ata para Zotit tuaj të kenë argumente kundër jush. A nuk jeni duke kuptuar.');
INSERT INTO "ahmeti" VALUES(2,77,'2:77','A thua nuk e dinë ata (jehuditë) se All-llahu e di atë çka e fshehin dhe atë çka e publikojnë?');
INSERT INTO "ahmeti" VALUES(2,78,'2:78','E ka disa prej tyre që janë analfabetë, nuk e kuptojnë librin, por jetojnë vetëm në shpresa, duke mos qenë të sigurt.');
INSERT INTO "ahmeti" VALUES(2,79,'2:79','Është shkatërrim për ata që me duart e veta e shkruajnë librin, e pastaj thonë: “Ky është nga All-llahu!, e për të arritur me te një fitim të paktë, pra është shkatërrim i madh për ta çka shkruan duart e tyre dhe është shkatërrim i madh për ta ajo çka fitojnë.');
INSERT INTO "ahmeti" VALUES(2,80,'2:80','Ata edhe thanë: “Neve nuk do të na kapë zjarri vetëm për disa ditë të numëruara!” Thuaj: “A mos keni marrë prej All-llahut ndonjë premtim, e All-llahu nuk e thyen premtimin e vet, ose jeni duke thënë për All-llahun atë që nuk e dini?');
INSERT INTO "ahmeti" VALUES(2,81,'2:81','Po, (do të ju kapë zjarri) ai që bën keq dhe që e vërshojnë gabimet e tij, ata janë banues të zjarrit, aty janë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(2,82,'2:82','E ata që besuan dhe bënë vepra të mira, ata janë banues të Xhennetit, aty janë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(2,83,'2:83','(përkujtoni) Kur Ne morëm zotimin e bijve të Israilit; mos adhuroni tjetërkë, përveç All-llahun, të silleni mirë ndaj prindërve, ndaj të afërmëve, ndaj jetimëve, ndaj miskinëve (të varfërve) dhe njerëzve u thuani fjalë të mira; faleni namazin dhe jepeni zekatin, e pastaj ju e thyet zotimin dhe përveç një pakice prej jush, ia kthyet shpinën zotimit.');
INSERT INTO "ahmeti" VALUES(2,84,'2:84','Dhe kur morëm zotimin tuaj që të mos derdhni gjakun tuaj, të mos dëboni vetën tuaj nga vendi juaj, e ju e pranuat, ndaj dëshmonie.');
INSERT INTO "ahmeti" VALUES(2,85,'2:85','Pastaj qe, ju jeni ata që mbytni njëri-tjetrin, i dëboni disa prej jush nga vendi i tyre duke bashkëpunuar kundër tyre si është mëkatë e padrejtë, e nëse ata bien te ju në robëri, ju jepni konpensim për (t’i liruar) ata, e dëbimi i tyre nga vendi është i ndaluar për ju. A e besoni një pjesë të librit, e tjetrën e mohoni? Ç’mund të jetë ndëshkimi ndaj atij që punon ashtu prej jush, pos poshtërim në jetën e kësaj bote, e në ditën e gjykimit ata hidhen në dënimin më të ashpër. All-llahu nuk është i pakujdesshëm ndaj asaj që veproni ju.');
INSERT INTO "ahmeti" VALUES(2,86,'2:86','Të tillë janë ata, që e vlerësuan jetën e kësaj bote mbi botën tjetër, andaj atyre as nuk do tu lehtësohet dënimi, e as që do të ndihmohen ata.');
INSERT INTO "ahmeti" VALUES(2,87,'2:87','Ne i patëm dhënë Musait librin dhe pas tij patëm dërguar shumë pejgamberë. Isait, birit të Merjemës i dhamë argumente (mrekulli) dhe e fuqizuam me (Xhibrilin) shpirtin e shenjtë. E saherë që u erdhi ndonjë i dërguar me çka nuk u pëlqeu juve, a nuk u bëtë kryelartë dhe disa prej tyre i përgënjeshtruat e disa i mbytët?');
INSERT INTO "ahmeti" VALUES(2,88,'2:88','E (jehuditë e kohës së Muhammedit) thonë: “Zemrat tona janë në këllëf!” Jo, por për shkak të mosbesimit të tyre, All-llahu i ka mallkuar, andaj pak janë që besojnë (ose: pak send besojnë).');
INSERT INTO "ahmeti" VALUES(2,89,'2:89','E kur u erdhi atyre prej All-llahut libri (Kur’ani) që është vërtetues i atij, që e kishin pranë, e që para se t’u vinte e kërkonin ndihmën e tij kundër mosbesimtarëve, e mohuan atë (Muhammedin) që e njihnin, kur u erdhi. Pra mallkimi i Allahut qoftë kundër mosbesimtarëve!');
INSERT INTO "ahmeti" VALUES(2,90,'2:90','E shëmtuar është ajo për çka ata e shitën vetvetën. Atë që e shpalli All-llahu të mos e besojnë nga zilia, për shkak se All-llahu nga mirësia e Tij t’i shpallë atij që dëshiron nga robët e vet. Andaj merituan zemërim mbi zemërim (gazep mbi gazep). Mosbesmtarët kanë dënim që i poshtërson.');
INSERT INTO "ahmeti" VALUES(2,91,'2:91','Dhe kur atyre u thuhet: “Besoni atë që e shpalli All-llahu”, ata thonë: “Ne besojmë atë që na u shpall neve”, kurse e mohojmë atë pas tij, edhe pse është vërtetues i atij, që e kanë ata dhe është i vërtet. Thuaj: “Nëse jeni besimtarë, pse i mbytnit më parë pejgamberët e All-llahut?”');
INSERT INTO "ahmeti" VALUES(2,92,'2:92','Juve u pat ardhur Musai me argumente, por pas tij ju e adhuruat viçin, pra ju ishit mizorë.');
INSERT INTO "ahmeti" VALUES(2,93,'2:93','(Përkujtoni) Kur morëm premtimin tuaj dhe mbi ju ngritëm kodrën Tur, (u thamë) merrni këtë që u dhamë seriozisht dhe dëgjoni (respektoni). Ata thanë: “Dëgjuam (me vesh) e kundërshtuam”. E për shkak të mosbesimit të tyre, adhurimi ndaj viçit ishte përzier me gjak në zemrat e tyre. Thuaj: “Nëse jeni besimtarë, besimi juaj është duke u udhëzuar keq”.');
INSERT INTO "ahmeti" VALUES(2,94,'2:94','Thuaj: “Nëse bota tjetër (Xhenneti) te All-llahu është vetëm për ju, edhe për njerëzit e tjerë, nëse jeni të sinqert, kërkone vdekjen (e shkoni më shpejtë).');
INSERT INTO "ahmeti" VALUES(2,95,'2:95','E për shkak të veprave të tyre (mëkateve), ata kurr nuk e dëshirojnë atë. All-llahu di për zullumqarët.');
INSERT INTO "ahmeti" VALUES(2,96,'2:96','Është e sigurtë se njerëzit më lakmues për të jetuar, ti ke për të gjetur ata (jehuditë), bile edhe më lakmues se idhujtarët. Ndonjëri prej tyre dëshiron të jetojë njëmijë vjet, por edhe sikur të jetojë, ajo (jeta e gjatë) nuk do ta shpëtoj atë prej dënimit. All-llahu sheh çka veprojnë ata.');
INSERT INTO "ahmeti" VALUES(2,97,'2:97','Thuaj: Kush është armik i Xhibrilit (është armik i All-llahut), e ai me urdhërin e All-llahut e zbriti Kur’anin në zemrën tënde, që është vërtetues i asaj që ishte më parë dhe udhërrëfyes e përgëzues për besimtarët.');
INSERT INTO "ahmeti" VALUES(2,98,'2:98','Kush është armik i All-llahut, i engjëjve të Tij, i të dërguarve të Tij, i Xhibrilit dhe i Mikailit (ai është mosbesimtarë), All-llahu është pa dyshim armik i mosbesimtarëve.');
INSERT INTO "ahmeti" VALUES(2,99,'2:99','Ne të kemi shpallur ty (Muhammed) argumente të qarta dhe ato nuk i mohon askush, përveç atyre që kanë dalë respektit.');
INSERT INTO "ahmeti" VALUES(2,100,'2:100','(nuk besojnë argumentet tona se) Saherë që ata kanë dhënë ndonjë premtim, një grup prej tyre e hodhi atë, por shumica e tyre nuk beson.');
INSERT INTO "ahmeti" VALUES(2,101,'2:101','E kur u erdhi atyre ndonjë i dërguar prej All-llahut, vërtetues i asaj që kishin ata, një grup prej tyre të cilëve iu ishte dhënë libri, e hodhi pas shpine librin e All-llahut, kinse nuk dinin (asgjë).');
INSERT INTO "ahmeti" VALUES(2,102,'2:102','(E hodhën librin e Zotit) E ndoqën atë që thonin djajt në kohën e sundimit të Sulejmanit. Por Sulejmani nuk ishte i pafe, djajt ishin të pafe, sepse u mësonin njerëzve magjinë. (Ndoqën) Edhe atë që u zbriti në Babil dy engjëjve, Harutit dhe Marutit. E ata të dy nuk i mësonin askujt (magjinë) para se t’i thonin: “Ne jemi vetëm sprovë, pra mos u bën i pa fe!” E, mësonin (njerëzit) prej atyre dyve atë (magji) me çka ndanin burrin prej gruas së vet, por pa lejën e All-llahut me atë askujt nuk mund t’i bënin dëm dhe ashtu mësonin çka u sillte dëm e nuk u sillte dobi atyre. E ata (jehuditë) e kanë ditur se ai që (hodhi librin) e zgjodhi atë (magjinë), ai në botën tjetër nuk ka ndonjë të drejtë (në mëshirën e Zotit). Po ta dinin, ata se për ç’ka e shitën vetveten, ajo është shumë e keqe.');
INSERT INTO "ahmeti" VALUES(2,103,'2:103','E sikur të kishin besuar ata dhe sikur të ishin ruajtur (prej mëkateve), po të dinin, shpërblimi prej All-llahut do të ishte shumë më i dobishëm.');
INSERT INTO "ahmeti" VALUES(2,104,'2:104','O ju që keni besuar, mos thuani: Raina - po thuani: Undhurna dhe respektoni! Mosbesimtarët kanë dënim të dhëmbshëm.');
INSERT INTO "ahmeti" VALUES(2,105,'2:105','As ata, ithtarët e librit që nuk besuan, e as idhujtarët nuk duan që juve të ju vijë ndonjë e mirë nga Zoti juaj. Mirëpo, All-llahu me mëshirën e vet veçon atë që dëshiron, All-llahu është Zot i mirësisë së madhe.');
INSERT INTO "ahmeti" VALUES(2,106,'2:106','Ne nuk abrogojmë (pezullojmë) asnjë nga argumentet tona, apo ta hedhim në harresë e të mos sjellim edhe më të dobishëm se ai, ose të ngjashëm me te. A nuk e ke ditur se All-llahu është i plotfuqishëm për çdo send?');
INSERT INTO "ahmeti" VALUES(2,107,'2:107','A nuk e ke ditur se vetëm All-llahut i takon sundimi i qiejve e i tokës, dhe se pos All-llahut nuk keni as mbrojtës as ndihmëtar.');
INSERT INTO "ahmeti" VALUES(2,108,'2:108','A doni të pyetni të dërguarin tuaj sikurse u pyet më parë Musai. Ai që e ndërron besimin me mosbesimin, ai tanimë e ka humbur rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(2,109,'2:109','Shumë ithtarë të librit (jehudi, krishterë), edhe pasiqë iu është bërë e qartë e vërteta, nga vetë zilia e tyre personale dëshiruan që pas besimit tuaj t’iu kthejnë në mosbesimtarë, pra ju lini dhe largohuni prej tyre derisa All-llahu ta sjellë urdhërin e vet. All-llahu ka mundësi për çdo send.');
INSERT INTO "ahmeti" VALUES(2,110,'2:110','Kryeni faljen (namazin) dhe jepne Zeqatin, e çfardo të mirë që e përgatitni për veten tuaj, atë e gjeni te All-llahu. S’ka dyshim All-llahu përcjell çdo veprim tuajin.');
INSERT INTO "ahmeti" VALUES(2,111,'2:111','Ata edhe thanë: “Kurrsesi nuk ka për të hyrë kush në Xhennet, përveç atij që është jehudi ose i krishterë! Ato janë fantazi të tyre! Thuaju: “Sillni argumentin tuaj (çka thoni) po qe se jeni të drejtë?');
INSERT INTO "ahmeti" VALUES(2,112,'2:112','Nuk është ashtu (si thonë ata), po ai që është dorëzuar All-llahut dhe është bamirës, ai e ka shpërblimin e vet te Zoti i tij, për ata nuk ka frikë, as nuk kanë pse të mërziten.');
INSERT INTO "ahmeti" VALUES(2,113,'2:113','Jehuditë thanë se të krishterët nuk kanë të mbështetur në asgjë (nuk kanë fe të vërtetë). Edhe të krishterët thanë se jehudit nuk janë të mbështetur në asgjë, e duke qenë se ata të dy palët e lexojnë librin. Po kështu si thëniet e tyre, thanë edhe ata që nuk dinin (idhujtarët për Muhammedin). Po në ditën e gjykimit për atë që ata nuk pajtoheshin, All-llahu gjykon ndërmjet tyre.');
INSERT INTO "ahmeti" VALUES(2,114,'2:114','E kush mundet të jetë më mizor se ai që në xhamitë e All-llahut pengon të përmendet emri i Tij (të bëhet ibadet) dhe përpiqet për shkatërrimin e tyre. Atyre ndryshe nuk u takoi të hyjnë vetë në to, vetëm duke qenë rrespektues (të tyre e jo rrënues). Ata në këtë jetë kanë nënçmim e në botën tjetër dënim të madh.');
INSERT INTO "ahmeti" VALUES(2,115,'2:115','Të All-llahut janë edhe (anët nga) lindja edhe perëndimi, dhe kahdo që të ktheheni, aty është anë e All-llahut. Vërtetë, All-llahu është i gjerë (në bujari) e i dijshëm.');
INSERT INTO "ahmeti" VALUES(2,116,'2:116','Ata (ithtarët e librit) thanë: “All-llahu ka fëmijë” I pastër është Ai nga kjo e metë! E Tij është gjithçka në qiej e në tokë, gjithçka i është nënshtruar Atij.');
INSERT INTO "ahmeti" VALUES(2,117,'2:117','Ai është shpikës i qiejve e i tokës (pa kurrfarë modeli të mëparshëm) e kur dëshiron diçka, ai vetëm i thotë: “Bëhu!” në atë moment bëhet.');
INSERT INTO "ahmeti" VALUES(2,118,'2:118','E ata që nuk dinë (kurejshitët politeistë) thanë: “Përse të mos na flasë neve All-llahu, ose të na vijë ndonjë argument!” po kështu, thëniet e tyre në mënyrë të njëjtë i patën përsëritur edhe ata që ishin para tyre. Të njëjta janë zemrat e tyre. Ne tanimë sqaruam argumentet për një popull që dëshiron të bindet.');
INSERT INTO "ahmeti" VALUES(2,119,'2:119','Ne të dërguam ty (o Muhammed a. s.) me të Vërtetën (Islamin), përgëzues dhe nedhir (vërejtës, qortues, paralajmërues), e ti nuk je përgjegjës për banuesit e Xhehennemit.');
INSERT INTO "ahmeti" VALUES(2,120,'2:120','As jehuditë, e as krishterët kurrë nuk do të jenë të kënaqur me ty deri që të pasosh fenë e tyre. Thuaju: “Udhëzimi i All-llahut është udhëzim i drejtë. E nëse pasiqë të ka ardhur ty e vërteta shkon pas mendimeve të tyre, nuk ka kush të ndihmojë e as të mbrojë nga All-llahu.');
INSERT INTO "ahmeti" VALUES(2,121,'2:121','Atyre, të cilëve u dhamë librin dhe të cilët e lexojnë drejtë ashtu si është, ata e besojnë atë (Kur’anin). E ata që e mohojnë atë, të tillët janë ata që dështuan (në dynja e në ahiret).');
INSERT INTO "ahmeti" VALUES(2,122,'2:122','O bijtë e israilit, kujtoni të mirat e Mia që ua dhurova, që ju dallova mbi njerëzit tjerë (të asaj kohe).');
INSERT INTO "ahmeti" VALUES(2,123,'2:123','Dhe ruajuni një ditë kur askush nuk do të mund ta ndihmojë tjetrin për asnjë send, nuk pranohet prej askujt kompensim, nuk do t’i bëjë dobi askujt ndonjë ndërmjetësim dhe as që do të ndihmohen (mëkatarët).');
INSERT INTO "ahmeti" VALUES(2,124,'2:124','Përkujto (O i dërguar) kur Zoti i vet, Ibrahimin e provoi me disa obligime, e ai i përmbushi ato, e Ai tha: “Unë po të bëj ty prijës (imam) të njerëzimit!” Ai tha: “(bën o Zot) Edhe nga pasardhësit e mi!” (Zoti) Tha: “Mirësinë Time nuk mund ta gëzojnë mizorët!”');
INSERT INTO "ahmeti" VALUES(2,125,'2:125','Dhe kur shtëpinë (Qaben) e bëmë vendkthim dhe vendsigurie për njerëzit, (u thamë) Vendin ku qëndroi Ibrahimi pranonie për vendfalje! Ibrahimin dhe Ismailin i urdhëruam: ju të dy pastrone shtëpinë Time për vizituesit, për ata që qëndrojnë aty dhe për ata që falen aty.');
INSERT INTO "ahmeti" VALUES(2,126,'2:126','Dhe kur Ibrahimi tha: “Zoti im, bëje këtë një qytet sigurie dhe banorët e tij, që besuan All-llahun dhe jetën tjetër, furnizoj me lloje të frutave!” Ai (All-llahu) tha: “(e furnizoj) Edhe atij që nuk besoi do t’ia mundësoj shfrytëzimin e frutave për një kohë të shkurtër, e pastaj do ta shtyej në dënimin e zjarrit, e sa përundim i shëmtuar është ai.');
INSERT INTO "ahmeti" VALUES(2,127,'2:127','Edhe kur Ibrahimi dhe Ismaili, duke i ngritur themelet e shtëpisë (Qabes luteshin): “Zoti ynë, pranoje prej nesh, se me të vërtetë Ti je që dëgjon dhe di!”');
INSERT INTO "ahmeti" VALUES(2,128,'2:128','Zoti ynë, bëna neve dyve besimtarë të sinqertë ndaj Teje dhe nga pasardhësit tanë, njerëz të bindur ndaj Teje, na e mëso manasikun (rregullat e adhurimit) dhe falna neve, vërtetë Ti je Falësi, Mëshiruesi!”');
INSERT INTO "ahmeti" VALUES(2,129,'2:129','Zoti ynë, dërgo ndër ta, nga gjiu i tyre të dërguar që t’u lexojë atyre ajetet Tua, t’u mësojë atyre librin dhe urtësinë, e t’i pastroj (prej ndytësisë së idhujtarisë) ata. S’ka dyshim se Ti je ngadhënjyesi, i dijshmi.');
INSERT INTO "ahmeti" VALUES(2,130,'2:130','E kush largohet prej fesë së Ibrahimit përveç atij që poshtron vetveten. Ne atë e bëmë të zgjedhur në këtë botë, kurse në botën tjetër ai është njëri prej të lartëve.');
INSERT INTO "ahmeti" VALUES(2,131,'2:131','Kur Zoti i vet atij i tha: “Dorëzohu”! Ai tha: “Iu kam dorëzuar Zotit të gjithësisë”!');
INSERT INTO "ahmeti" VALUES(2,132,'2:132','E Ibrahimi i porositi bijtë e tij me këtë (fe), e edhe Jakubi, (u thanë) “O bijtë e mi, All-llahu ua zgjodhi fenë (islame) juve, pra mos vdisni ndryshe, por vetëm duke qenë muslimanë!”');
INSERT INTO "ahmeti" VALUES(2,133,'2:133','A ishit ju (ithtarë të librit) dëshmitarë kur Jakubit iu afrua vdekja, e ai bijve të vet u tha: “Çka do të adhuroni pas meje”? Ata thanë: “Do të adhurojmë Zotin (Ilah) tënd, dhe Zotin e prindërve tuaj: Ibrahimit, Ismailit, Is’hakut, Një të Vetmin Zot dhe ne, vetëm Atij i jemi dorëzuar!”');
INSERT INTO "ahmeti" VALUES(2,134,'2:134','Ai ishte një popull që kaloi, atij i takoi ajo që fitoi, e juve u takon ajo që fituat, prandaj ju nuk jeni përgjegjës për atë që vepruan ata.');
INSERT INTO "ahmeti" VALUES(2,135,'2:135','Ata (ithtarët e librit) thanë: “Bëhuni jehudi ose të krishterë, e gjeni rrugën e drejtë”! Thuaj: “jo, (asnjërën) por fenë e drejtë të Ibrahimit që ai nuk ishte nga idhujtarët.');
INSERT INTO "ahmeti" VALUES(2,136,'2:136','Ju (besimtarë) thuani: “Ne i besuam All-llahut, atë që na u shpall neve, atë që iu shpall Ibrahimit, Ismailit, Is’hakut, Jakubit dhe pasardhësve (të Jakubit që ishin të ndarë në dymbedhjetë kabile), atë që i është dhënë Musait, Isait dhe atë që iu është dhënë nga Zoti i tyre pejgamberëve, ne nuk bëjmë dallim në asnjërin prej tyre dhe ne vetëm atij i jemi bindur.');
INSERT INTO "ahmeti" VALUES(2,137,'2:137','Në qoftë se ata besuan ashtu siç besuat ju ata vërtetë kanë gjetur rrugën e drejtë, e nëse refuzojnë, atëherë ata janë kundërshtarë (opozitë), po ty (Muhammed) kundër tyre do të mjaftojë All-llahu. Ai është dëgjuesi, i dijshmi.');
INSERT INTO "ahmeti" VALUES(2,138,'2:138','(kjo fe jona është) Njgyrosje e All-llahut, e kush ngjyros (me fe) më mirë se All-llahu? Ne vetëm atë e adhurojmë.');
INSERT INTO "ahmeti" VALUES(2,139,'2:139','Thuaj: “A doni të grindeni me ne për All-llahun, e Ai është Zoti ynë dhe Zoti juaj dhe ne kemi vepra (shpërblimin e veprave) tona, e ju keni veprat tuaja. Por ne jemi (besimtarë) të sinqertë ndaj Tij.');
INSERT INTO "ahmeti" VALUES(2,140,'2:140','A pretendoni se Ibrahimi, Ismaili, Is-haku, Jakubi dhe pasardhësit kanë qenë jehudi ose të krishterë? Thuaj: “A e dini ju më mirë apo All-llahu? Kush është më mizor se ai, që e ka dëshminë e All-llahut pranë veti dhe e fshehë. All-llahu nuk është i panjoftuar me veprimet tuaja”.');
INSERT INTO "ahmeti" VALUES(2,141,'2:141','Ai ishte një popull që shkoi, të cilit i takoi ajo që fitoi, e juve u takon ajo që fituat, prandaj ju nuk jeni përgjegjës për atë që ata vepruan.');
INSERT INTO "ahmeti" VALUES(2,142,'2:142','Disa mendjelehtë nga njerëzit do të thonë: “Çka i ktheu ata (muslimanët) prej kiblës (drejtimit) në të cilin ishin ata (Kudsi)? Thuaj: “Të All-llahut janë lindja dhe perëndimi, Ai e vë në rrugën e drejtë atë që do”.');
INSERT INTO "ahmeti" VALUES(2,143,'2:143','Disa ashtu (sikur u udhëzuam në fenë islame) Ne u bëmë juve një popull të drejtë (një mes të zgjedhur) për të qenë ju dëshmitarë (në ditën e gjykimit) ndaj njerëzve, dhe për të qenë i dërguari dëshmitar ndaj jush. E kiblen nga e cila ti u drejtove nuk e bëmë për tjetër, vetëm se për të provuar atë që shkon pas të dërguarit, nga ai që kthehet prapa, ndonëse kjo ka qenë vështirë (për disa), por jo edhe për ata që All-llahu i drejtoi. All-llahu nuk është që t’ua humbë besimin tuaj. S’ka dyshim se All-llahu është shumë i butë dhe mëshirues ndaj njerëzve.');
INSERT INTO "ahmeti" VALUES(2,144,'2:144','Ne shumë herë po shohim kthimin e fytyrës tënde kah qielli, e Ne gjithqysh do të drejtojmë ty në drejtim të një kibleje (Qabja) që ti e do atë. Pra kthehu anës së xhamisë së shenjtë (Qabes), dhe kudo që të jeni (o besimtarë) kthehuni kah ajo anë. E atyre që u është dhuruar libri, ata e dinë sigurisht se kjo (kthesë) është e vërtetë nga Zoti i tyre. E All-llahut nuk mund t’i fshihet ajo që veprojnë ata.');
INSERT INTO "ahmeti" VALUES(2,145,'2:145','Po edhe sikur t’u sillshe çdo lloj argumenti atyre që u është dhënë libri, ata nuk pasojnë kiblen tënde, e as ti nuk do të pasosh kiblen e tyre, po asnjëra palë nuk do ta pasojë kiblen e tjetrës. E pas dijes, e cila të ka ardhur, po e zëmë se ishe vënë pas dëshirave të tyre, atëherë ti do të ishe mizor.');
INSERT INTO "ahmeti" VALUES(2,146,'2:146','Atyre që u kemi dhënë librin, ata e njohin ate (Muhammedin) siç i njohin bijtë e vet, e një grup prej tyre edhe pse e dinë këtë, janë duke e fshehur të vërtetën.');
INSERT INTO "ahmeti" VALUES(2,147,'2:147','E vërteta është nga Zoti yt, pra kurrsesi mos u bë nga ata që dyshojnë.');
INSERT INTO "ahmeti" VALUES(2,148,'2:148','Secili (popull) ka një anë, të cilës ai i kthehet, ju shpejtoni kah punët e mbara, kudo që të jeni All-llahu ka për t’ju tubuar të gjithëve, All-llahu ka fuqi për çdo send.');
INSERT INTO "ahmeti" VALUES(2,149,'2:149','Kahdo që të shkosh ktheje fytyrën tënde kah ana e xhamisë së shenjtë, kjo është e vërtetë nga Zoti yt. All-llahu përcjell gjithçka veproni ju.');
INSERT INTO "ahmeti" VALUES(2,150,'2:150','Dhe kahdo që të dalësh, ktheje fytyrën tënde kah ana e xhamisë së shenjtë (Qabja) dhe kudo që të gjendeni ktheni fytyrat tuaja kah ana e saj, ashtu që njerëzit mos të kenë argument kundër jush, përveç atyre që janë mizorë nga ata (që nga inati nuk pranojnë kurrfarë fakti), por mos ia keni frikën atyre, frikësohuni prej Meje (e bëra këtë) që të gjeni rrugën e drejtë dhe që të plotësojë mirësinë Time ndaj jush.');
INSERT INTO "ahmeti" VALUES(2,151,'2:151','(sikurse plotësova mirësinë Time ndaj jush) Ashtu siç dërguam nga gjiu i juaj të dërguar t’u lexojë ajetet Tona, t’ju pastrojë, e t’ju mësojë librin dhe traditën, e edhe t’ju mësojë atë që nuk e dinit.');
INSERT INTO "ahmeti" VALUES(2,152,'2:152','Pra më kujtoni Mua, Unë ju kujtoj juve, Më falënderoni e mos Më mohoni.');
INSERT INTO "ahmeti" VALUES(2,153,'2:153','O ju që keni besuar, kërkoni ndihmë me durim e me të falur, se vërtetë All-llahu është me durimtarët.');
INSERT INTO "ahmeti" VALUES(2,154,'2:154','E për ata që u mbytën në rrugën e All-llahut mos thoni: “Janë të vdekur”, Jo, ata janë të gjallë, por ju nuk kuptoni.');
INSERT INTO "ahmeti" VALUES(2,155,'2:155','Ne do t’ju sprovojmë me ndonjë frikë, me uri, me ndonjë humbje nga pasuria e nga jeta e edhe nga frytet, por ti përgëzoji durimtarët.');
INSERT INTO "ahmeti" VALUES(2,156,'2:156','Të cilët, kur i godet ndonjë e pakëndëshme thonë: “Ne jemi të All-llahut dhe ne vetëm te Ai kthehemi!”');
INSERT INTO "ahmeti" VALUES(2,157,'2:157','Të tillët janë ata që kanë bekime prej Zotit të tyre dhe mëshirë dhe të tillët janë ata të udhëzuarit.');
INSERT INTO "ahmeti" VALUES(2,158,'2:158','“Safa” dhe “Merve” janë nga shenjat (për adhurim) e All-llahut, e kush e mësyen shtëpinë për haxh (Qaben për haxh), ose për umre (vizitë jashtë kohës së haxhit), nuk është mëkat për te t’i vizitojë ato dyja (të ece në ato dy vende). E kush bën ndonjë të mirë nga vullneti (jo obliguese), s’ka dyshim se All-llahu është shpërblyes i gjithdijshëm.');
INSERT INTO "ahmeti" VALUES(2,159,'2:159','Ata, të cilët fshehin argmentet dhe faktet që Ne i shpallëm, e pasi që ato ua sqaruam njerëzve në librin, të tillët i mallkon All-llahu, i mallkojnë edhe ata që mallkojnë.');
INSERT INTO "ahmeti" VALUES(2,160,'2:160','Përveç atyre që pendohen, që përmirësohen dhe që u shpjegojnë njerëzve, (të vërtetën), të tillëve ua pranoj pendimin, se Unë pranoj shumë pendimin, jam mëshirues.');
INSERT INTO "ahmeti" VALUES(2,161,'2:161','Ata, të cilët mohuan dhe vdiqën si pabesimtarë, kundër tyre është mallkimi i All-llahut, i engjëjve dhe i të gjithë njerëzve.');
INSERT INTO "ahmeti" VALUES(2,162,'2:162','Ata përgjithmonë janë aty (në zjarrë), as nuk u lehtësohet dënimi, as nuk u jepet afat.');
INSERT INTO "ahmeti" VALUES(2,163,'2:163','Zoti juaj (që meriton adhurim) është një, All-llahu, nuk ka zot pos Atij që është mëshirëplotë, gjithnjë mëshiron.');
INSERT INTO "ahmeti" VALUES(2,164,'2:164','Është fakt se në krijimin e qiejve e të tokës, në ndërrimin e natës e të ditës, të anijes që lundron në det që u sjell dobi njerëzve, në atë shi që e lëshon All-llahu prej së larti e me të ngjall tokën pas vdekjes së saj dhe përhapë në te nga çdo lloj gjallese, në qarkullimin e erërave dhe reve të nënshtruara mes qiellit e tokës, (në të gjitha këto), për një popull që ka mend ka argumente.');
INSERT INTO "ahmeti" VALUES(2,165,'2:165','E nga njerëzit ka asish që në vend të All-llahut besojnë idhujt, që i duan (i madhërojnë) ata, sikur (që besimtarët e vërtetë e duan) All-llahun, po dashuria e atyre që besuan All-llahun është shumë më e fortë. E sikur që dinin ata që bënë mizori se kur do ta shohin dënimin (në botën tjetër), do të binden se e tërë fuqia i takon vetëm All-llahut (e jo idhujve) dhe se All-llahu është ndëshkues i rreptë.');
INSERT INTO "ahmeti" VALUES(2,166,'2:166','Dhe (sikur të shihnin) kur do të largohen ata që u prinin atyre që i ndiqnin (paria largohet prej atyre që u shkuan pas), e të gjithë e shohin dënimin dhe këputen lidhjet e tyre.');
INSERT INTO "ahmeti" VALUES(2,167,'2:167','E Ata, të cilët u patën shkuar pas do të thonë: “Ah, sikur të na lejohej një kthim (në dynja) e të largohemi prej tyre (prijësve) siç u larguan ata tash prej nesh!” Kështu All-llahu do t’ju paraqesë veprat që janë dëshprim për ta, e ata nuk kanë të dalë prej zjarrit.');
INSERT INTO "ahmeti" VALUES(2,168,'2:168','O ju njerëz, hani nga ajo që është në tokë e që është e lejuar dhe e mirë, e mos shkoni hapave të djallit se ai është armik i hapët i juaji.');
INSERT INTO "ahmeti" VALUES(2,169,'2:169','Ai ju urdhëron vetëm me të këqia e turpësi, dhe ju shtyen të thoni për All-llahun atë që nuk e dini.');
INSERT INTO "ahmeti" VALUES(2,170,'2:170','E kur u thuhet atyre (idhujtarëve): “Pranoni atë që All-llahu e shpalli!” Ata thonë: “Jo, ne ndjekim atë rrugë në të cilën i gjetëm prindërit tanë!” Edhe sikur prindërit e tyre të mos jenë udhëzuar në rrugën e drejtë (ata do t’i pasonin)?”');
INSERT INTO "ahmeti" VALUES(2,171,'2:171','Shembulli i (i thirrësit të) atyre që nuk besuan është sikurse i atij (bariut) që u lëshon britmë atyre (bagëtive) që nuk dëgjojnë tjetër vetëm se britmë e zë (e nuk kuptojnë). Ata janë të shurdhër, memecë e të verbër, ata nuk kuptojnë.');
INSERT INTO "ahmeti" VALUES(2,172,'2:172','O ju që besuat, hani nga të mirat që u kemi dhënë, dhe falënderoni All-llahun, nëse jeni që vetëm Atë e adhuroni.');
INSERT INTO "ahmeti" VALUES(2,173,'2:173','(All-llahu) Ua ndaloi juve vetëm të ngordhtën, gjakun, mishin e derrit dhe atë që therret (ngritet zëri me te) jo në emër të All-llahut. E kush shtrëngohet (të hajë nga këto) duke mos tepruar, për të nuk është mëkat. Vërtetë All-llahu falë, është mëshirues.');
INSERT INTO "ahmeti" VALUES(2,174,'2:174','Ata që fshehin nga libri atë që shpalli All-llahu dhe për te fitojnë shumë të paktë, ata në barqet e tyre fusin vetëm zjarr. Atyre All-llahu nuk do t’u flasë në ditën e gjykimit dhe as nuk i shfajson ata, por për ta pason dënimi i rëndë e i padurueshëm.');
INSERT INTO "ahmeti" VALUES(2,175,'2:175','Ata janë që në vend të udhëzimit e morën humbjen dhe dënimin në vend të shpëtimit. Sa të durueshëm qenkan ata ndaj zjarrit?');
INSERT INTO "ahmeti" VALUES(2,176,'2:176','Atë (dënim) për shkak se All-llahu e zbriti librin (Tevratin), me sqarim të drejtë, e ata që bënë ndryshime në librin, janë në një përçarje të largët nga e vërteta.');
INSERT INTO "ahmeti" VALUES(2,177,'2:177','Nuk është tërë e mira (e kufizuar) të ktheni fytyrat tuaja kah lindja ose perëndimi, por mirësi e vërtetë është ajo e atij që i beson All-llahut, ditës së gjykimit, engjëjve, librit, lajmëtarëve dhe pasurinë që e do, ua jep të afërmve, bonjakëve, të vërfërve, udhëtarëve, lypësve dhe për lirimin e robërve, dhe ai që e fal namazin, e jep zeqatin, dhe ata që kur premtojnë e zbatojnë, dhe të durueshmit në skamje, në sëmundje dhe në flakën e luftës. Të tillët janë ata të sinqertit dhe të tillët janë ata të devotshmit.');
INSERT INTO "ahmeti" VALUES(2,178,'2:178','O ju që besuat, u është bërë obligim gjurmimi për dënim për mbytje: i liri për të lirin, robi për robin, femra për femrën. Kurse atij që i falet diçka nga vëllau i vet atëherë ajo le të përcillët (e atij që e fal) kuptueshëm dhe shpagimi (nga dorasi) atij le t’i bëhet me të mirë. kjo është një lehtësim dhe mëshirë prej Zotit tuaj. E kush tejkalon pas këtij (pajtim), ai ka një dënim të idhët.');
INSERT INTO "ahmeti" VALUES(2,179,'2:179','O ju të zotët e mendjes, kjo masë e dënimit është jetë për ju, ashtu që të ruheni (nga mbytja e njëri tjetrit).');
INSERT INTO "ahmeti" VALUES(2,180,'2:180','Kur ndonjërit prej jush i është afruar vdekja, nëse lë pasuri pas vete, testamenti (vasijeti) për prindërit dhe për të afërmit u është bërë juve obligim, por ashtu siç është drejtë. Për ata që janë të devotshëm kjo është detyrë që lypet kryer.');
INSERT INTO "ahmeti" VALUES(2,181,'2:181','Kush bën ndryshimin e tij pasi që ta ketë dëgjuar (ditur) atë, mëkati për te u takon atyre që ndryshojnë. All-llahu dëgjon dhe di.');
INSERT INTO "ahmeti" VALUES(2,182,'2:182','E kush frikësohet prej testamentuesit se gabimisht po largohet nga drejtësia, ose qëllimisht do të bëjë mëkat, ai që bën pajtim ndërmjet tyre, ai nuk ka mëkat. Vërtetë All-llahu shumë falë dhe shumë mëshiron.');
INSERT INTO "ahmeti" VALUES(2,183,'2:183','O ju që besuat, agjërimi (saum) u është bërë obligim sikurse që ishte obligim edhe i atyre që ishin para jush, kështu që të bëheni të devotshëm.');
INSERT INTO "ahmeti" VALUES(2,184,'2:184','(jeni të obliguar për) Ditë të caktuara, e kush është i sëmurë prej jush ose është në udhëtim (e nuk agjëroi), atëherë ai (le të agjërojë) më vonë aq ditë. E ata që i rëndon ai (nuk mund të agjërojnë), janë të obliguar për kompenzim, ushqim (ditor), i një të varfëri ai që nga vullneti jep më tepër, ajo është aqë më mirë për te. Mirëpo, po që se dini, agjërimi është më i mirë për ju.');
INSERT INTO "ahmeti" VALUES(2,185,'2:185','(ato ditë të numëruara janë) Muaji i Ramadanit që në te (filloi të) shpallet Kur’ani, që është udhërëfyes për njerëz dhe sqaruesi i rrugës së drejtë dhe dallues (i të vërtetës nga gënjeshtra). E kush e përjeton prej jush këtë muaj, le të agjërojë, ndërsa kush është i sëmurë ose në udhëtim, le të agjërojë aqë ditë nga ditët e mëvonshme. All-llahu me këtë dëshiron lehtësim për ju, e nuk dëshiron vështërsim për ju. (të agjëroni ditët e lëshuara më vonë) Që të plotësoni numrin, të madhëroni All-llahun për atë se u udhëzoi dhe që të falenderoni.');
INSERT INTO "ahmeti" VALUES(2,186,'2:186','E kur robët e Mi të pyesin ty për Mua, Unë jam afër, i përgjigjem lutjes kur lutësi më lutet, pra për të qenë ata drejt të udhëzuar, le të më përgjigjen ata Mua dhe le të më besojnë Mua.');
INSERT INTO "ahmeti" VALUES(2,187,'2:187','Natën e agjërimit u është lejuar afrimi të gratë tuaja, ato janë prehje për ju dhe ju jeni prehje për ato. All-llahu e di se ju e keni mashtruar vetveten, andaj ua pranoi pendimin tuaj dhe ua fali gabimin. Tash e tutje bashkohuni me to dhe kërkoni atë që ua ka caktuar All-llahu dhe hani e pinin derisa qartë të dallohet peri i bardhë nga peri i zi në agim, e pastaj agjërimin plotësojeni deri në mbrëmje. E kur jeni të izoluar (në i’tikafë) në xhamia, mos t’u afroheni atyre (për marëdhënie intime). Këto janë dispozitat e All-llahut, pra mos i kundërshtoni. Ja kështu, në këtë mënyrë All-llahu ua sqaron njerëzve argumentet e veta që ata të ruhen.');
INSERT INTO "ahmeti" VALUES(2,188,'2:188','Dhe mos e hani pasurinë e njëri- tjetrit në mënyrë të palejuar, e as mos u paraqitni me te (me ryshfet) te gjyakatësit për të grabitur në mënyrë të padrejtë një pjesë të pasurisë së njerëzve, kur ju e dini (se pa të drejtë po e hani atë).');
INSERT INTO "ahmeti" VALUES(2,189,'2:189','Të pyesin ty për hënën e re (dhe fazat e saj) Thuaj: “Ato janë përcaktime të kohës prë njerëzi dhe për haxh. Nuk është mirësi të hyni nga mbrapa në shtëpitë por është mirësi kush ruhet (nga të këqiat). Në shtëpia hyni kah dyert e tyre dhe kinie frikë All-llahun që ashtu të gjeni shpëtim.');
INSERT INTO "ahmeti" VALUES(2,190,'2:190','Dhe luftoni në rrugën e All-llahut kundër atyre që ju sulmojnë e mos e teproni se All-llahu nuk i do ata që e teprojnë (e fillojnë luftën).');
INSERT INTO "ahmeti" VALUES(2,191,'2:191','Dhe luftoni ata kudo që t’i zini, dëboni prej vendit ata sikurse ju përzuan ata juve. Fitneja është më e rëndë se vrasja. Mos i luftoni ata pranë xhamisë së shenjtë (Mesxhidul-Haram) deri që ata t’ju luftojnë juve aty, e nëse ju sulmojnë, atëherë sulmoni edhe ju ata. I këtillë është ndëshkimi ndaj pabesimtarëve.');
INSERT INTO "ahmeti" VALUES(2,192,'2:192','Dhe nëse heqin dorë, pra All-llahu është Mëkatfalësi, Mëshiruesi.');
INSERT INTO "ahmeti" VALUES(2,193,'2:193','Vritni ata derisa të zhduket fitneja dhe të aplikohet feja vetëm për All-llahun. E në qoftë se ndalen (nga propaganda dhe lufta), atëherë lereni armiqësinë, përveç atyre që janë zullumqarë.');
INSERT INTO "ahmeti" VALUES(2,194,'2:194','Muaji i shenjtë (i sivjetit) është për muajin e shenjtë (të vjetit), e shenjtëritë (shkelja e tyre) janë masë ndëshkuese. Pra, kush ju sulmon juve, kthenia sulmin atij edhe ju po në atë masë dhe kini frikë nga All-llahu e ta dini se All-llahu është me të devotshmit.');
INSERT INTO "ahmeti" VALUES(2,195,'2:195','Dhe jepni për një rrugë të All-llahut e mos hidhni veten në rrezik dhe bëni mirë, se me të vërtetë All-llahu i do bamirësit.');
INSERT INTO "ahmeti" VALUES(2,196,'2:196','E kryenie haxhin dhe umrën për hir të All-llahut, po në qoftë se pengoheni, atëherë (therrni për kurban) çka t’ju vijë më lehtë prej kurbanëve, e mos i rruani kokat tuaja derisa të arrijë kurbani vendin e caktuar. Po kush është prej jush i sëmurë ose ka mundim koke (e rruhet para kohe) kompensim është: agjërim, sadaka ose kurban. E kur jeni të sigurt, ai që bënë umrën para haxhit (duhet therrë) një kurban që i vjen më lehtë, mirëpo nëse nuk ka, le të agjëroj tri ditë gjatë haxhit e shtatë ditë kur të ktheheni, këto jeni dhjetë ditë të plota. Ky është rregull për ata që nuk kanë familjen pranë xhamisë së shenjtë. Pra kinie dro All-llahun dhe dine se All-llahu është ndëshkues i rreptë.');
INSERT INTO "ahmeti" VALUES(2,197,'2:197','Haxhi është në muajt e caktuar e kush bën (ia fillon të zbatojë) haxhin në këta muaj, nuk duhet afruar gruas, nuk bën të merr nëpër këmbë dispozitat e sheriatit, as nuk duhet shkaktuar grindje. All-llahu di për atë që e punoni nga e mira. Dhe përgatituni me furnizim (për rrugë), e furnizimi më i mirë është devotshmëria, e ju të zotët e mendjes keni dronë Time.');
INSERT INTO "ahmeti" VALUES(2,198,'2:198','Nuk është mëkat për ju të kërkoni begati nga Zoti juaj (të bëni ndonjë tregti gjatë haxhit). E kur të derdheni (hiqeni) prej Arafatit, përmendeni All-llahun në vend të shenjtë (Muzdelife), përmendeni Atë, ashtu si u ka udhëzuar Ai, sepse më parë ishit të humbur.');
INSERT INTO "ahmeti" VALUES(2,199,'2:199','Pastaj zdirgjuni andej kah zdirgjen njerëzit, kërkoni All-llahut falje, se All-llahu fal e është mëshirues.');
INSERT INTO "ahmeti" VALUES(2,200,'2:200','E kur t’i kryeni detyrat tuaja, përmendeni All-llahun sikurse i përkujtoni prindërit tuaj, bile përmendeni edhe më fortë. Ka disa prej njerëzve që thonë: “Zoti ynë, na jep Ti neve në këtë botë!” për të nuk ka asgjë në botën tjetër.');
INSERT INTO "ahmeti" VALUES(2,201,'2:201','E, ka prej tyre asish që thonë: “Zoti ynë na jep të mira në këtë jetë, të mira edhe në botën tjetër, dhe na ruaj prej dënimit me zjarr!”');
INSERT INTO "ahmeti" VALUES(2,202,'2:202','Të tillët e kanë shpërblimin nga ajo që e fituan. All-llahu është i shpejtë në llogari.');
INSERT INTO "ahmeti" VALUES(2,203,'2:203','All-llahun përmendeni në ditët e caktuara (në ditët e bajramit). Kush ngutet (të largohet prej Mines) për dy ditë, nuk bën mëkat, po edhe ai që e shtyen (edhe për një ditë) nuk bënë mëkat, këto rregulla janë për atë që don të jetë i përpikët. Pra, kini kujdes All-llahun dhe dine se ju, te Ai tuboheni.');
INSERT INTO "ahmeti" VALUES(2,204,'2:204','Ka ndonjë nga njerëzit që fjala e tij të mahnit, por vetëm në këtë botë (pse në botën tjetër gjykon Ai që e di të fshehtat), dhe për atë që ka në zemrën e tij, e paraqet All-llahun dëshmues, e në realitet ai është kundërshtari më i rreptë.');
INSERT INTO "ahmeti" VALUES(2,205,'2:205','E posa të kthehet, ai në tokë vepron të bëjë shkatërrim në te, të asgjësojë të korrat (mbjelljet) dhe gjallesat. E All-llahu nuk e do çrregullimin (fesadin).');
INSERT INTO "ahmeti" VALUES(2,206,'2:206','Dhe kur i thuet atij: “Kij frikë All-llahun!”, atë e kap eufori për punë mëkati. Shtrat i shëmtuar është ai që i takon atij (Xhehennemi).');
INSERT INTO "ahmeti" VALUES(2,207,'2:207','Ka nga njerëzit, i cili për hirë të All-llahut e flijojnë vetveten, e edhe All-llahu është shumë i mëshirshëm për robtë e vet.');
INSERT INTO "ahmeti" VALUES(2,208,'2:208','O ju që besuat, hyni në islamizmin e tërësishëm (Përqafoni fenë islame në tërësi), e mos ndiqni rrugën e djallit, sepse ai është armik i juaji i hapët.');
INSERT INTO "ahmeti" VALUES(2,209,'2:209','E nëse devijoni pasi u kanë ardhur argumentet e qarta, ta dini pra se All-llahu është i plotëfuqishëm, i vetëdijshëm.');
INSERT INTO "ahmeti" VALUES(2,210,'2:210','Ata nuk janë duke pritur tjetër, por vetëm t’u vijë All-llahu (urdhëri i All-llahut në ditën e gjykimit) nën hije të reve, (t’u vijnë) engjëjt dhe çështja të jetë e kryer (kush për Xhennet e kush për Xhehennem). Vetëm te All-llahu është fundi i të gjitha çështjeve.');
INSERT INTO "ahmeti" VALUES(2,211,'2:211','Pyeti bijtë e Israilit, se sa argumente të çarta u kemi dhënë atyre (dhe nuk besuan). E kush e ndërron të mirën e All-llahut pasi që t’i ka ardhur ajo, s’ka dyshim se ndëshkimi i All-llahut është i ashpër.');
INSERT INTO "ahmeti" VALUES(2,212,'2:212','Atyre që nuk besuan, u është bërë e hijshme jeta e kësaj bote dhe bëjnë tallje me ata që besuan. Po ata që u ruajtën (besimtarët) në ditën e kijametit. All-llahu i jep (shpërblim) pa masë atij që e do.');
INSERT INTO "ahmeti" VALUES(2,213,'2:213','Njerëzit ishin një popull (të fesë së natyrshme islame) e (kur u përçanë) All-llahu dërgoi pejgamberët përgëzues dhe qortues, dhe atyre Ai u zbriti edhe librin me fakte të sakta për të gjykuar në atë që u kundërshtuan vetëm ata që kishin libër (ithëtarët e librit). E përpos atyre që iu kishte dhënë ai (libri) dhe u kishin ardhur argumente të qarta, nuk kundërshtoi kush në te (në librin), po edhe atë (kundërshtim e bënë), nga zilia ndërmjet tyre, mirëpo All-llahu me mëshirën e Tij i udhëzoi ata që besuan te e vërteta e asaj për çka ishin kundërshtuar. All-llahu e vënë në rrugë të drejtë atë që dëshiron.');
INSERT INTO "ahmeti" VALUES(2,214,'2:214','Po ju menduat se do të hyni në Xhennet, pa u provuar edhe ju me shembullin e atyre që ishin para jush, të cilët i patën goditur skamjet e vuajtjet dhe qenë tronditur, sa që i dërguari thoshte, e me te edhe ata që kishin besuar: “Kur do të jetë ndihma e All-llahut?” Ja (u erdhi ndihma) vërtetë ndihma e All-llahut është afër!”');
INSERT INTO "ahmeti" VALUES(2,215,'2:215','Të pyesin se çdo të japin. Thuaj: “Atë që jepni për të afërmit, për bonjakët, për të varfërit, për gurbetçinjtë. E çdo të mirë që punoni, s’ka dyshim se All-llahu e di.');
INSERT INTO "ahmeti" VALUES(2,216,'2:216','Juve u është bërë obligim kitali (xhihadi), ndonëse ju e urreni atë. Por mund që ju ta urreni një send, e ai është shumë i dobishëm për ju, dhe mund që ju ta doni një send, e ai është dëm për ju. All-llahu di, e ju nuk dini.');
INSERT INTO "ahmeti" VALUES(2,217,'2:217','Të pyesin për luftën në muajin e shenjtë, thuaj: “Lufta në te është e madhe (mëkat i madh), por pengimi nga rruga e All-llahut, mosbesimi ndaj Tij, pengimi nga xhamia e shenjtë (Qabja) dhe dëbimi i banorëve të saj nga ajo, janë mëkate edhe më të mëdha te All-llahu. E fitneja është edhe më e madhe se mbytja. Ata do t’u luftojnë juve vazhdimisht për t’ju zbrapsur, nëse munden, nga feja juaj. E kush zbrapset prej jush nga feja e tij dhe vdes si pabesimtar, ata i kanë zhdukur veprat e veta në këtë jetë dhe në jetën tjetër. Të tillët janë banorë të zjarrit dhe në të do të qëndrojnë përjetshëm.');
INSERT INTO "ahmeti" VALUES(2,218,'2:218','Ata të cilët besuan, ata që u shpërngulën dhe luftuan në rrugën e All-llahut, ata meritojnë të shpresojnë në mëshirën e Tij. All-llahu falë shumë dhe është mëshirues.');
INSERT INTO "ahmeti" VALUES(2,219,'2:219','Të pyesin ty për verën dhe bixhozin. Thuaj: “Që të dyja janë mëkat i madh, e ka edhe dobi në to (të pakta) për njerëz, por dëmi i tyre është më i madh se dobia e tyre. Të pyesin ty edhe se ç’do të japin. Thuaj: “Tepricën!” Kështu ua sqaron All-llahu juve argumentet ashtu që të mendoni (çka është mirë e çka është keq).');
INSERT INTO "ahmeti" VALUES(2,220,'2:220','(të mendoni) Për këtë botë dhe për botën tjetër. Dhe të pyesin ty për bonjakët. Thuaj: “Po t’i ndihmoni në punët e tyre është më mirë, e nëse i përzieni (pasurin e tyre me tuajën), ata janë vëllezërit tuaj. All-llahu di të dallojë qëllimkeqin nga qëllimmiri. E sikur të dëshironte All-llahu do t’ju rëndojë. All-llahu është më i fuqishëm, më i urti.');
INSERT INTO "ahmeti" VALUES(2,221,'2:221','Mos u martoni me idhujtare deri që ato të besojnë (Zotin). Një robëresh besimtare është më e vlefshme se një idhujtare, edhe nëse ajo (idhujtarja) ju mahnitë. Mos u martoni as me idhujtarë deri që ata të besojnë (Zotin). Një rob besimtar është më i vlefshëm se idhujtari edhe nëse ai ju mahnit. Ata ju ftojnë për në zjarr, e All-llahu me mëshirën e vet ju fton për në Xhennet, për në shpëtim dhe u sqaron njerëzve argumentet e veta, ashtu që ata të përkujtojnë.');
INSERT INTO "ahmeti" VALUES(2,222,'2:222','Të pyesin ty për menstruacionin (hajdin). Thuaj: “Ajo është gjendje e neveritur, andaj largohuni prej grave gjatë menstruacionit dhe mos iu afroni atyre (për marëdhënie) derisa të pastrohen. E kur të pastrohen, atëherë afrohuni atyre ashtu siç u ka lejuar All-llahu. All-llahu i do ata që pendohen, dhe ata që ruhen prej punëve të ndyta e të neveritshme.');
INSERT INTO "ahmeti" VALUES(2,223,'2:223','Gratë tuaja janë vendmbjellje e juaj, afrohuni vendmbjelljes suaj si të dëshironi, por përgatitni për vetën tuaj, dhe kinie frikë All-llahu, e ta dini se fundi juaj është te Ai, e besimtarëve jepu myzhde.');
INSERT INTO "ahmeti" VALUES(2,224,'2:224','E mos e bëni All-llahun peng të betimeve tuaja (kur betoheni në Te), kur doni të bëni vepra të mira, të silleni mirë dhe të bëni pajtim ndërmjet njerëzve. All-llahu dëgjon të gjitha dhe i di.');
INSERT INTO "ahmeti" VALUES(2,225,'2:225','All-llahu nuk ju merr në përgjegjësi për betimet tuaja të paqëllimta, por ju merr për ato që i bëni qëllimisht me gjithë zemër. All-llahu është i butë dhe falë shumë.');
INSERT INTO "ahmeti" VALUES(2,226,'2:226','Ata që betohen se do të largohen prej grave të tyre (të mos bëjnë kontakt me to), afati i pritjes është katër muaj. Në qoftë se kthehen ata (heqin dorë nga betimi), s’ka dyshim All-llahu bën falje dhe mëshiron.');
INSERT INTO "ahmeti" VALUES(2,227,'2:227','E në qoftë se ata kanë vendosur për shkurorëzim, All-llahu dëgjon (fjalët e tyre) i di (qëllimet e tyre).');
INSERT INTO "ahmeti" VALUES(2,228,'2:228','E ato gra që janë shkurorëzuar janë të obliguara të presin tri menstruacione. Nëse ato i besojnë All-llahut dhe ditës së mbramë, atyre nuk u lejohet të fshehin atë që All-llahu krijoi në mitrat e tyre. E burrat e tyre kanë më shumë të drejta që gjatë asaj kohe, nëse duan pajtim, t’i rikthejnë ato. Edhe atyre (grave) u takon e drejta sikurse edhe përgjegjësia në bashkëshortësi, e burrave u takon një përparësi ndaj tyre. All-llahu është i gjithfuqishëm, i urtië.');
INSERT INTO "ahmeti" VALUES(2,229,'2:229','Lëshimi (pas së cilit mund të bëhet rikthimi) është dy herë, e (pastaj) ose jetë e njerëzishme (bashkëshortore) ose shkurorëzim me mirëkuptim. E juve (burrave) nuk u lejohet t’u merrni asnjë send nga ajo që u keni dhuruar (si niqah), vetëm nëse që të dy frikësoheni se nuk do të mund t’i ruajnë dispozitat e All-llahut (në bashkëshortësi). E nëse keni frikë se ata të dy nuk do të mund t’i ruajnë dispozitat e All-llahut, atëherë për atë, me çka ajo bën kompensim për shkurorëzim (hule), për ata të dy nuk ka mëkate. Këto janë dispozita të All-llahut, pra mos i kundërshtoni, sepse kush i tejkalon dispozitat e All-llahut, pikërisht të tillët janë zullumqarët.');
INSERT INTO "ahmeti" VALUES(2,230,'2:230','E në qoftë se ai (burri) e lëshon atë (për herë të tretë), pas atij (lëshimi) nuk lejohet më derisa të martohet ajo për një burrë tjetër. E nëse ai (burri i dytë) e lëshon atë, atëherë për ata dy, po qe se mendojnë se do t’i zbatojnë dispozitat e All-llahut, nuk ka pengesë të rikthehen (në bashkëshortësi). Këto janë dispozita të All-llahut që ia sqaron një populli që kupton.');
INSERT INTO "ahmeti" VALUES(2,231,'2:231','E kur t’i keni lëshuar gratë dhe ato i afrohen afatit të tyre, atëherë ose mbani si duhet, ose i lëni si duhet (të kryejnë fatin), e mos i mbani sa për t’i dëmtuar, e të bëheni të padrejtë. E kush bën atë, ai e ka dëmtuar vetveten. Dispozitat e All-llahut mos i merrni për shaka. Përkujtoni të mirat e All-llahut, ndaj jush edhe atë që ju shpalli Kur’ani dhe dispozitat e sheriatit me të cilat ju udhëzon, dhe keni frikë All-llahun e dine se All-llahu është i gjithdijshëm për çdo send.');
INSERT INTO "ahmeti" VALUES(2,232,'2:232','Dhe kur i lëshoni gratë, e ato e përmbushin afatin e tyre (të pritjes), nëse pëlqejnë mes vete ashtu si kërkojnë rregullat, mos i pengoni që të martohen për burrat e tyre. Me këtë këshillohet ai që prej jush e beson All-llahun dhe botën tjetër, kjo është më e dobishme për ju, më e pastër, se All-llahu e di, e ju nuk e dini.');
INSERT INTO "ahmeti" VALUES(2,233,'2:233','Nënat, ato që duan ta plotësojnë gjidhënien, janë të obliguara t’u japin gji fëmijëve të vet dy vjet të plota. I ati i fëmijës është i obliguar për furnizim dhe veshmbathjen e tyre (gruas) ashtu si është rregulli. Askush nuk ngarkohet më tepër, vetëm aq sa ka mundësin e tij. Asnjë nën nuk bën të dëmtohet me fëmijën e saj, e as babai me fëmijën e tij. Po ashtu është i obliguar edhe trashëgimtari (i fëmijës). E nëse pas një konsultimi dhe pëlqimi (prindërit) shfaqin dëshirën për ndërprerjen (më heret) e gjinit, nuk është ndonjë mëkat për ta. Nëse ju baballarët) për fëmijën tuaj dëshironi gjidhënëse tjetër me kusht që pagesën t’ia bëni në mënyrë të duhur, nuk është ndonjë mëkat. Kini frikë nga All-llahu dhe dine se All-llahu sheh çka punoni.');
INSERT INTO "ahmeti" VALUES(2,234,'2:234','E ata që vdesin dhe lënë gra pas vete, ato (gratë) presin katër muaj e dhjetë ditë. E kur ta kryejnë ato afatin e tyre, nuk është mëkat për ju (familja kujdestare) për atë që bëjnë ato në mënyrë të njerëzishme më veten e tyre. All-llahu hollësisht e di çka veproni.');
INSERT INTO "ahmeti" VALUES(2,235,'2:235','Paraqitja juaj për martesë ndaj grave (që kanë kryer afatin e tyre), në mënyrë të tërthortë, ose mbajtja fshehtë në veten tuaj, nuk është mëkat për ju. All-llahu e di se ju do t’ua përmendni atyre (dëshirën për martesë), por kursesi mos u premtoni atyre fshehtazi (diç tjetër), përpos t’u thoni fjalë të lejuara. Dhe mos vendosni lidhjen e kurorës derisa të përfundojë afati i caktuar. Ta dini se All-llahu e di çka fshehni në vete, pra kini frikë prej Atij, dhe dine se All-llahu shumë falë është i butë.');
INSERT INTO "ahmeti" VALUES(2,236,'2:236','Nuk është mëkat për ju nëse i lëshoni gratë pa pasur kontakt (martesë) me to dhe para se t’u caktoni atyre ndonjë caktim (kurorë-nikah), po pajisni ato (me ndonjë pajisje), pasaniku sipas mundësisë së tij dhe i varfëri sipas mundësisë së tij. Një pajisje e zakonshme është obligim për bëmirësit.');
INSERT INTO "ahmeti" VALUES(2,237,'2:237','E nëse i lëshoni ato para se të kontaktoni, por u keni pas caktuar atyre një caktim (kurorës), atëherë duhet t’u jepni gjysmën e asaj që e keni caktuar, përveç nëse ju falin ose ju falë ai në duart e të cilit është lidhja e kurorës. E të falni (burra ose gra) është më afër devotshmërisë, e mos e harroni bamirësinë ndërmjet jush. Vërtetë, All-llahu sheh atë që veproni.');
INSERT INTO "ahmeti" VALUES(2,238,'2:238','Vazhdoni rregullisht namazet (faljet), e edhe atë namazin e mesëm, dhe ndaj All-llahut të jeni respektues (në namaze).');
INSERT INTO "ahmeti" VALUES(2,239,'2:239','E nëse këni frikë (nga armiku), atëherë fauni duke ecur ose duke kaloruar, por kur të jeni të siguruar, përkujtoni (me namaz) All-llahun ashtu si u mësoi Ai për atë që ju nuk dinit.');
INSERT INTO "ahmeti" VALUES(2,240,'2:240','Ata që vdesin nga mesi i juaj dhe lënë pas vete gra, le të testamentojnë (lënë porosi) për gratë e tyre, furnizimin për një vjet, duke mos i nxjerrë prej shtëpisë. E nëse (me dëshirën e vet) dalin, nuk është mëkat për ju për atë punë të lejueshme që bëjnë ato me vetveten. All-llahu është më i fuqishmi, më i dituri.');
INSERT INTO "ahmeti" VALUES(2,241,'2:241','Për të devotshmit është obligim që edhe për gratë e lëshuara të bëjnë një furnizim të zakonshëm.');
INSERT INTO "ahmeti" VALUES(2,242,'2:242','Kështu, që t’i kuptoni, All-llahu ua shpjegon argumentet e veta.');
INSERT INTO "ahmeti" VALUES(2,243,'2:243','A nuk di për ata, të cilët nga frika prej vdekjes u larguan prej atëdheut të vet, e të cilët ishin me mijëra, e All-llahu atyre u tha: “Vdisni!” (ata vdiqën) Pastaj i ngjalli. All-llahu është shumë bamirës për njerëz, por shumica e njerëzve nuk janë mirënjohës.');
INSERT INTO "ahmeti" VALUES(2,244,'2:244','Luftoni për hirë të All-llahut, etë dini se All-llahu dëgjon, di.');
INSERT INTO "ahmeti" VALUES(2,245,'2:245','Kush është ai që i huazon All-llahut një hua të mirë, e Ai t’ia shtojë atij shumëfish Atë? All-llahu shtrëngon (varfëron) dhe çliron (begaton) dhe kthimi juaj është vetëm te Ai.');
INSERT INTO "ahmeti" VALUES(2,246,'2:246','A nuk ke marrë vesh për parinë e bijëve të israilit pas Musait? Kur i patën thënë një të dërguarit të tyre (Shem’unit): “Cakto për neve një sundues e të luftojmë në rrugën e All-llahut!” Ai (pejgamberi) tha: “A mund të ndodhë që nëse lufta u bëhet obligim ju të mos luftoni!” ata thanë: “E ç’kemi ne që të mos luftojmë në rrugën e All-llahut, kur dihet se ne jemi dëbuar nga atdheu dhe bijt tanë?” E kur u obliguan me luftë, përveç një pakice prej tyre, ata u zbrapën. All-llahu është i njohur për punët e zullumqarëve.');
INSERT INTO "ahmeti" VALUES(2,247,'2:247','Pejgamberi i tyre u tha: “All-llahu caktoi sundues tuajin Talutin” Ata thanë: “Si mund të jetë ai sundues yni, kur ne kemi më shumë meritë se ai për sundim, madje ai edhe nuk është i pasur? Ai tha: “All-llahu zgjodhi atë sundues tuajin dhe e pajisi me dituri të gjerë e me fuqi trupore!” All-llahu ia jep sundimin e vet atij që do, All-llahu është dhurues i madh, i dijshëm.');
INSERT INTO "ahmeti" VALUES(2,248,'2:248','Lajmëtari i tyre u tha: “Shenjë e sundimit të tij është që t’ju sjellë arkën, që e bartin engjëjt, dhe që në te gjeni qetësim (shpirtëror - Sekine) nga Zoti juaj e, edhe diçka që ka mbetur nga thesari i familjes së Musait dhe Harunit. S’ka dyshim se ky, po qe se jeni besimtarë, është një fakt për ju”.');
INSERT INTO "ahmeti" VALUES(2,249,'2:249','E kur doli (prej qytetit) Taluti me ushtrinë, tha: “All-llahu do t’ju sprovojë me një lumë, e ai që pi prej atij, ai nuk është me mua, e kush nuk shijon atë, ai është me mua, përveç atij që me dorën e vet e pi një grusht?” Mirëpo, me përjashtim të një pakice prej tyre, të tjerët pinë nga ai. E kur e kaloi ai (taluti) atë së bashku me te edhe ata që ishin besimtarë thanë: “Ne sot nuk kemi fuqi kundër Xhalutit dhe ushtrisë së tij!” Po ata që ishin të bindur se do ta takonin All-llahun thanë: “Sa e sa grupe të vogla me dëshirën e All-llahut kanë triumfuar ndaj grupeve të mëdha!” All-llahu është me durimtarët.');
INSERT INTO "ahmeti" VALUES(2,250,'2:250','E kur dualën përballë Xhalutit dhe ushtrisë së tij, thanë: “Zoti ynë! na dhuro durim! na i përforco këmbët tona dhe na ndihmo kundër pabesimtarëve!');
INSERT INTO "ahmeti" VALUES(2,251,'2:251','Me ndihmën e All-llahut i thyen ata, e Davudi e mbyti Xhalutin dhe All-llahu i dha atij (Davudit) sundimin dhe hikmetin (urtësinë, nubuwetin) dhe e mësoi atë për çdo gjë që deshi. Dhe sikur All-llahu të mos i mbronte njerëzit me disa prej disa të tjerëve, do të shkatërrohej toka, po All-llahu është bamirës i madh ndaj njerëzimit.');
INSERT INTO "ahmeti" VALUES(2,252,'2:252','Këta janë argumente të All-llahut, po t’i lexojmë ty me saktësi, e s’ka dyshim se ti (Muhammed) je prej të dërguarve.');
INSERT INTO "ahmeti" VALUES(2,253,'2:253','Këta (për të cilët të rrëfyem) janë të dërguarit, disa prej tyre i dalluam nga të tjerët, prej tyre pati që All-llahu u foli, disa i ngriti në shkallë më të lartë, Isait, birit të Merjemës i dhamë argumente dhe e përfocuam me shpirtin e shenjtë (Xhibrilin). E sikur të donte All-llahu, nuk do të mbyteshin ndërmjet vete ata (populli) që ishin pas tyre, pasi që atyre u patën zbritur argumentet, por ata megjithatë u përçanë. E pati prej tyre që besuan dhe pati të tillë që nuk besuan, e sikur të dëshironte All-llahu ata nuk do të mbyteshin, por All-llahu punon çka dëshiron.');
INSERT INTO "ahmeti" VALUES(2,254,'2:254','O ju që keni besuar, para se të vijë një ditë kur nuk do të ketë as shitblerje, as miqësi, e as ndërmjetësi, jepni nga ajo me çka Ne u furnizuam juve. Pabesimtarët janë mizorë.');
INSERT INTO "ahmeti" VALUES(2,255,'2:255','All-llahu - s’ka zot pos Tij. I Gjalli, Vigjiluesi. Nuk e ze të koturit, e as gjumi. E Tija është ç’ka në qiej dhe ç’ka në tokë. Kush mund të ndërmjetësojë te Ai, pos me lejën e Tij? Ai e di se ç’po u ndodhë dhe ç’do t’u ndodhë. Dhe asgjë nga dija e Tij nuk mund të përvetësojnë, pos sa të dojë Ai. Ndërsa pushteti i Tij përfshin qiejt dhe tokën. E nuk lodhet duke i ruajtur (mirëmbajtur), ngase Ai është i Larti, Madhështori. (Ky ajet quhet edhe “Ajetul-Kursij”)');
INSERT INTO "ahmeti" VALUES(2,256,'2:256','Në fe nuk ka dhunë. Është sqaruar e vërteta nga e kota. E kush nuk i beson tagutët, e i beson All-llahut, ai është kapur për lidhjen më të fortë, e cila nuk ka këputje. All-llahu është dëgjues i dijshëm.');
INSERT INTO "ahmeti" VALUES(2,257,'2:257','All-llahu është mbikëqyrës i atyre që besuan, i nxjerr ata prej errësirave në dritë. E kujdestarë të atyre që nuk besuan janë djajtë që i nxjerrin ata prej drite e i hudhin në errësira. Ata janë banues të zjarrit, ku do të qëndrojnë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(2,258,'2:258','A nuk ke arritur të dijsh për atë që përse All-llahu i kishte dhënë pushtet, ai (Nemrudi) polemizoi me Ibrahimin rreth Zotit të tij. Kur Ibrahimi tha: “Zoti im është Ai që jep jetë dhe vdekje!” Ai tha: “Edhe unë jep jetë dhe vdekje!” Ibrahimi tha: “Zoti im e sjell diellin nga lindja, sille pra ti atë nga perëndimi?” Atëherë ai që nuk besoi mbeti i hutuar. All-llahu shpie në rrugë të drejtë popullin mizor.');
INSERT INTO "ahmeti" VALUES(2,259,'2:259','Ose (nuk je i njohur) me shembullin e atij që kaloi pranë një fshati që ishte rrënuar në kulmet e tij, e tha: “Si e ngjallë All-llahu këtë pas shkatërrimit të tij?” e All-llahu e bëri të vdekur atë (pyetësin - Uzejrin) njëqind vjet, pastaj e ringjalli dhe i tha: “Sa qëndrove (i vdekur)?” Ai tha: “Një ditë, ose një pjesë të ditës!” Ai (All-llahu) Tha:” Jo, por ke ndejtur (i vdekur) njëqind vjet, shikoje ushqimin dhe pijen tënde si nuk është prishur, e shikoje edhe gomarin tënd (si i janë shkapërderdhur eshtrat). E për të bërë ty argument për njerëzit (bëmë këtë sprovë), shiko se si i kombinojmë eshtrat e pastaj i veshim me mish. E kur iu bë e qartë atij tha: “U binda se me të vërtetë All-llahu ka mundësi për çdo send!”');
INSERT INTO "ahmeti" VALUES(2,260,'2:260','“Përkujto kur Ibrahimi tha: “Zoti im, më mundëso të shoh se si i ngjallë të vdekurit?” Ai (Zoti) tha: “A nuk beson?” Ai (Ibrahimi) tha: “Po, por desha që zemra e ime të ngopet (bindje)!” Ai (Zoti) tha: “Merri katër shpezë pranë vete (therri e copëtoji), e pastaj në çdo kodër vëne nga një pjesë të tyre, mandej thirri ato, do të vijnë ty shpejt, e dije se All-llahu është i Gjithfuqishmi, i Urti.”');
INSERT INTO "ahmeti" VALUES(2,261,'2:261','Shembulli i pasurisë së atyre që e japin në rrugën e All-llahut është si një kokërr prej të cilës mbijnë shtatë kallinj, ndërsa në secilin kalli ka nga njëqind kokrra. All-llahu ia shumëfishon (shpërblimin) atij që dëshiron, All-llahu është Bujar i Madh, i di qëllimet.');
INSERT INTO "ahmeti" VALUES(2,262,'2:262','Ata që për hir të All-llahut e japin pasurinë e tyre, e pastaj atë që e dhanë nuk e përcjellin me të përmendur e me mburrje, ata e kanë shpërblimin e vet të Zoti i tyre, për ta nuk ka frikë, ata as që do të brengosen.');
INSERT INTO "ahmeti" VALUES(2,263,'2:263','Një fjalë e mirë dhe një lehtësim (që i bëhet lypësit) është më e mirë se një lëmoshë që përcillet me të keqe. All-llahu nuk ka nevojë për askë, është i Butë.');
INSERT INTO "ahmeti" VALUES(2,264,'2:264','O ju që besuat, mos i prishni lëmoshat tuaja me të krenuar e me ofendim siç bën ai që ia jep pasurinë e vet sa par sy e faqe të njerëzve, e nuk beson All-llahun dhe botën tjetër. Shembulli i tij është si një gur i madh e i lëmuar bmi të cilin ka pak dhe, e kur e godet atë një shi i madh e lë të zhveshur (lakuriq). Ata (formalistët) nuk arrijnë asgjë nga ajo që punuan. All-llahu nuk udhëzon popullin pabesimtar.');
INSERT INTO "ahmeti" VALUES(2,265,'2:265','E shembulli i atyre që pasurinë e vet e japin nga bindja e tyre e duke kërkuar kënaqësinë e All-llahut, i përngjan një kopshti në një rrafshnaltë që i bie shi i madh, e ai jep fruta të dyfishtë. Po edhe nëse nuk i bie shi i madh, i bie një rigë (që i mjafton). All-llahu sheh atë që veproni.');
INSERT INTO "ahmeti" VALUES(2,266,'2:266','A dëshiron ndonjëri prej jush ndonjë kopsht me hurma e rrush në të cilin rrjedhin lumenj, në të cilin ka nga të gjitha frutet, e atë (pronarin e kopshtit) ta ketë kapur pleqëria dhe ai të ketë pasardhës të mitur, e atë (kopshtin) ta godasë ndonjë stuhi me zjarr e ta djegë. Kështu All-llahu jua sqaron argumentet që të mendoni.');
INSERT INTO "ahmeti" VALUES(2,267,'2:267','O ju që besuat, jepni nga më e mira e asaj që e fituat dhe nga ajo që ju dhamë prej tokës, e mos nxitoni ta jepni atë më të pavlefshmen nga ajo, e që ju nuk do ta pranonit për vete pos symbyllas. Dhe dijeni se All-llahu nuk ka nevojë për ju, ngase është i Madhëruar.');
INSERT INTO "ahmeti" VALUES(2,268,'2:268','Djalli ju frikëson me varfëri dhe ju urdhëron për të këqija, e All-llahu ju garanton falje (mëkatesh) e begati; All-llahu është Dhurues i Madh, i Dijshëm.');
INSERT INTO "ahmeti" VALUES(2,269,'2:269','Ai ia dhuron hikmetin atij që do, e kujt i është dhënë hikmeti, i është dhënë mirësi e shumtë, por nuk përkujtohet askush pos të mençurve.');
INSERT INTO "ahmeti" VALUES(2,270,'2:270','All-llahu di për atë që e keni dhënë nga pasuria ose keni zbatuar ndonjë premtim (nedhër), e për mëkatarët nuk ka ndonjë ndihmës.');
INSERT INTO "ahmeti" VALUES(2,271,'2:271','Nëse lëmoshat i jepni haptazi, ajo është mirë, por nëse ato ua jepni të varfërve fshehurazi, ajo është edhe më e mirë për ju dhe Ai ua largon të këqiat. All-llahu është i njohur hollësisht për veprat e juaja.');
INSERT INTO "ahmeti" VALUES(2,272,'2:272','“Nuk është obligim yti (Muhammed) udhëzimi i tyre (në rrugë të drejtë), All-llahu e shpie në rrugë të drejtë ate që do. Çkado që të jepni nga pasuria, e keni për veten tuaj, po mos jepni për tjetërkë, por vetëm për hir të All-llahut, e çkado që t’u jepni të tjerëve nga pasuria, ajo do t’u kompensohet në mënyrë të plotë duke mos ju dëmtuar.”');
INSERT INTO "ahmeti" VALUES(2,273,'2:273','(Jepni) për të varfërit që janë të angazhuat në rrugën e All-llahut dhe nuk kanë mundësi të gjallërojnë në tokë, duke qenë se ata nuk lypin, prandaj ai që nuk e di gjendjen e tyre mendon se ata janë të pasur. Ata i njeh nga vetë pamja e tyre (të raskapitur), por nuk kërkojnë e as nuk i mërzisin njerëzit. Pra çkado që të jepni nga pasuria, s’ka dyshim se All-llahu e di atë mirë.');
INSERT INTO "ahmeti" VALUES(2,274,'2:274','Ata, të cilët pasurinë e tyre e shpërndajnë (në rrugë të Zotit) natën e ditën, fshehurazi ose haptazi, ata shpërblimin e vet e kanë të Zoti i tyre dhe për ta nuk ka as frikë, as pikëllim.');
INSERT INTO "ahmeti" VALUES(2,275,'2:275','Ata që e hanë kamatën nuk ngrehen ndryshe pos siç ngrehet i çmenduri nga të prekurit e djallit. (Bëjnë) kështu ngase thanë: “Shitblerja nuk është pos kamatë!” e All-llahu ka lejuar shitblerjen, ndërsa ka ndaluar kamatën. Atij që i ka ardhur këshillë (udhëzim) prej Zotit të tij dhe është ndalur (prej kamatës) i ka takuar e kaluara dhe çështja e saj mbetet te All-llahu, e kush e përsërit (pas ndalimit), ata janë banues të zjarrit, ku do të mbesin përgjithmonë.');
INSERT INTO "ahmeti" VALUES(2,276,'2:276','All-llahu zhduk kamatën dhe shton lëmoshën, All-llahu nuk e do asnjë besëprerë dhe mëkatar.');
INSERT INTO "ahmeti" VALUES(2,277,'2:277','S’ka dyshim se ata që besuan dhe bënë vepra të mira, falën namazin dhe dhanë zekatin i pret shpërblim i madh te Zoti i tyre, ata nuk do të kenë kurrfarë frike as brengosje.');
INSERT INTO "ahmeti" VALUES(2,278,'2:278','O ju që besuat, keni frikë All-llahun dhe nëse jeni besimtarë të sinqertë hiqni dorë prej asaj që ka mbetur nga kamata.');
INSERT INTO "ahmeti" VALUES(2,279,'2:279','E në qoftë se nuk e bëni këtë (nuk heqni dorë nga kamata), atëherë binduni se jeni në konflikt me All-llahun dhe të dërguarin e Tij. E nëse jeni penduar, atëherë juve ju takon kryet e mallit tuaj - askë nuk dëmtoni, e as vetë nuk dëmtoheni.');
INSERT INTO "ahmeti" VALUES(2,280,'2:280','Po në qoftë se ai (borxhliu) është në gjendje të vështirë, atëherë bëni një pritje deri sa të vijë në një çlirim. E t’ia falni (borxhin) në emër të lëmoshës është shumë më e mirë për ju, nëse dini.');
INSERT INTO "ahmeti" VALUES(2,281,'2:281','Dhe ruajuni ditës kur do të ktheheni te All-llahu, dhe secilit njeri i plotësohet ajo që ka fituar, dhe atyre nuk u bëhet padrejtësi.');
INSERT INTO "ahmeti" VALUES(2,282,'2:282','O besimtarë, kur merrni hua prej njëri-tjetrit për një afat të caktuar, shkruajeni atë. Dhe nga gjiu juaj le të shkruajë një shkrues i drejtë dhe të mos ngurrojë nga të shkruarit ashtu siç e ka mësuar All-llahu. Le të shkruajë, ndërsa atij le t’i diktojë ai që pranon borxhin, le t’i frikësohet All-llahut, Zotit të tij e të mos lërë mangët asnjë send nga ai. E në qoftë se ai që ngarkohet me borxh është i paaftë mentalisht, është i mitur ose nuk është në gjendje të diktojë, atëherë le të diktojë drejt kujdestari i tij. Kërkoni të dëshmojnë dy dëshmitarë burra nga mesi juaj, e në qoftë se nuk janë dy burra, atëherë një burr e dy gra, nga dëshmitarët që i pëlqeni. (Dy gra në vend të një burri) Për atë se nëse njëra prej tyre gabon, t’ia përkujtojë tjetra. Dëshmitarët të mos refuzojnë kur të thirren. Dhe mos përtoni për ta shkruar atë dhe afatin e tij, i vogël qoftë ose i madh, sepse kjo është më e drejtë te All-llahu, më e fortë për dëshmi dhe më afër mosdyshimit. Vetëm nëse është tregti e menjëherëshme (dora-doras) që e praktikoni mes jush, atëherë nuk është ndonjë mëkat të mos e shënoni. Por, kur bëni shitblerje, dëshmojeni. E ai (pronari i pasurisë) nuk dëmton as shkruesit as dëshmitarët, e nëse bëni (dëmtimin), ai është mosrespekt i juaj (ndaj dispozitave të All-llahut). Keni frikë All-llahun se All-llahu ju dhuron dituri (të jashtzako-nshme), All-llahu është i Gjithdijshëm për çdo send.');
INSERT INTO "ahmeti" VALUES(2,283,'2:283','E në qoftë se gjendeni në udhëtim dhe nuk gjeni shkrues, atëherë merrni peng (paradhënie ose kapar). E nëse i besoni njëri-tjetrit, atij që i është besuar le ta zbatojë atë (amanetin) që i është besuar, dhe le të ketë frikë All-llahun, Zotin e tij. Mos e fshehni dëshminë, sepse ai që e fsheh atë është mëkatar me zemrën e vet, All-llahu di atë që veproni.');
INSERT INTO "ahmeti" VALUES(2,284,'2:284','Të All-llahut janë ç’ka në qiell dhe ç’ka në tokë, e për atë që e keni në veten tuaj, e shfaqët haptazi ose e mbajtët fshehtë, All-llahu do t’ju marrë në llogari, e Ai ia fal atij që do dhe dënon atë që do. All-llahu ka mundësi për çdo send.');
INSERT INTO "ahmeti" VALUES(2,285,'2:285','I dërguari i besoi asaj që iu shpall prej Zotit të Tij, si dhe besimtarët. Secili i besoi All-llahut dhe melekëve të Tij dhe të dërguarve të Tij. “Ne nuk bëjmë dallim në asnjërin nga të dërguarit e Tij”, dhe thanë: “Dëgjuam dhe respektuam. Kërkojmë faljen tënde o Zoti ynë, te Ti është ardhmëria (jonë).”');
INSERT INTO "ahmeti" VALUES(2,286,'2:286','All-llahu nuk ngarkon askë përtej mundësive të veta, atij (njeriut) i takon ajo që e fitoi dhe atij i bie ajo (e keqe) që e meritoi. “Zoti ynë, mos na dëno nëse harrojmë ose gabojmë! Zoti ynë, mos na ngarko neve barrë të rëndë siç i ngarkove ata para nesh! Dhe Zoti ynë, mos na ngarko me atë për të cilën nuk kemi fuqi! Dhe na i mbulo të këqiat, na fal dhe na mëshiro. Ti je Mbrojtësi ynë, pra na ndihmo kundër popullit pabesimtar!');
INSERT INTO "ahmeti" VALUES(3,1,'3:1','Elif, Lamë, Mimë.');
INSERT INTO "ahmeti" VALUES(3,2,'3:2','All-llahu është Një, e nuk ka të adhuruar (Zot) pos tij, është i përjetshëm mbikëqyrës.');
INSERT INTO "ahmeti" VALUES(3,3,'3:3','Ai ta zbret ty (Muhammed) librin me argument që të është vërtetues i librave të mëparshme. Ai e zbriti më parë Tevratin dhe Inxhilin.');
INSERT INTO "ahmeti" VALUES(3,4,'3:4','Udhërrfim për njerëz, e zbriti edhe Furkanin (dalluesin e së vertetës nga gënjeshtra). S`ka dyshim për se ata që mohojnë argumentet e All-llahut i pret ndëshkimi i rreptë. All-llahu është ngadhënjyes, shpagimtar.');
INSERT INTO "ahmeti" VALUES(3,5,'3:5','S`ka dyshim se All-llahu nuk mund t`i fshihet asgjë në tokë e as në qiell.');
INSERT INTO "ahmeti" VALUES(3,6,'3:6','Ai është që ju krijon (formon) juve në mitra ashtu si të dojë. S`ka Zot pos Tij, e që është fuqiplotë i urti.');
INSERT INTO "ahmeti" VALUES(3,7,'3:7','Ai është që ta zbriti ty e që në të ka ajete të qarta dhe ato janë bazë e librit, e ka të tjerë që nuk janë krejtsisht të qarta (muteshabih). E ata, që në zemrat etyre kanë anim kah e shtrembëta, ata gjurmojnë atë që nuk është krejt qartë për të shkaktuar huti, e kinse kërkojnë komentin e tyre. Po, pos All-llahut askush nuk e di domethënien e tyre të saktë. Dijetarët e pajisur me dituri thonë: “Ne u kemi besuar atij (atyre që janë të paqarta), të gjitha janë nga Zoti ynë! Por këtë e kuptojnë vetëm ata që janë të zotët e mendjes.');
INSERT INTO "ahmeti" VALUES(3,8,'3:8','“Zoti ynë, mos na i lako zemrat tona pasi na i drejtove, na dhuro mëshirën Tënde, pse vetëm Ti je dhuruesi i madh”.');
INSERT INTO "ahmeti" VALUES(3,9,'3:9','“Zoti ynë Ti je ai që në një ditë do t`i tubosh njerëzit, ditë që përtë cilin nuk ka dyshim. All-llahu nuk shkel premtimin e vet.');
INSERT INTO "ahmeti" VALUES(3,10,'3:10','Është e vertetë se atyre që nuk besuan nuk do t`u vlejë asgjë para All-llahut, as pasuria e as fëmijët e tyre. Ata vetë janë lëndë e zjarrit.');
INSERT INTO "ahmeti" VALUES(3,11,'3:11','Si gjendja e pasardhësve të faraonit dhe e atyre që ishin para tyre, që përgënjeshtruan argumentet tona, e All-llahu i shkatërroi me fajet e tyre, All-llahu ndëshkon shumë ashpër.');
INSERT INTO "ahmeti" VALUES(3,12,'3:12','Thaju atyre që nuk besuan: “Do të jeni të mposhtur (ne këtë jetë) dhe do të hidheni në Xhennenem. Sa i shëmtuar është ai djep (shtrat).');
INSERT INTO "ahmeti" VALUES(3,13,'3:13','Ju (jehudi) patët një përvojë të madhe në ato dy grupet që u konfrontuat ndërmjet vete, njëri grup luftonte në rrugën e All-llahut e tjetri ishte pabesimtar, dhe me shikimin e syve të tyre i shihnin se ishin dy herë më shumë se besimtarët. Po, All-llahu me ndihmën e vet përforcon atë që do. Vërtetë, në këtë është një përvojë e madhe për ata që kanë mendje të kthjelltë.');
INSERT INTO "ahmeti" VALUES(3,14,'3:14','U është zbukuruar njerëzve dashuria ndaj të këndjeve prej grave, djemve, pasurisë së grumbulluar nga ari e argjendi, ndaj kuajve të stolisur, bagëtisë e bujqësisë. Këto janë kënaqësi të kësaj bote, po te All-llahu është e ardhmja më e mirë.');
INSERT INTO "ahmeti" VALUES(3,15,'3:15','Thuaj: “A t’ju lajmëroj për diç shumë më të mirë se ato? Për ata që frikësohen, ata kanë te Zoti i tyre Xhennete nëpër të cilat rrjedhin lumenj dhe aty do të jenë përgjithmonë, kanë bashkëshorte të pastra, dhe kënaqësi nga All-llahu. All-llahu është Basir (Ai që sheh çdogjë) për robërit.');
INSERT INTO "ahmeti" VALUES(3,16,'3:16','Të cilët thonë: “Zoti ynë, ne vërtet besuam, na i falë mëkatet tona dhe na ruaj prej ndëshkimit të zjarrit!”');
INSERT INTO "ahmeti" VALUES(3,17,'3:17','Durimtarët, të drejtit, të devotshmit, dorëdhënit, faljekërkuesit në as’har (syfyr).');
INSERT INTO "ahmeti" VALUES(3,18,'3:18','All-llahu vërtetoi se nuk ka zot tjetër përveç Tij, e dhe engjëjt e ata që kanë dijeni, se Ai është Zbatues i drejtësisë. Nuk ka zot përveç Tij, Fuqiplotit, të Urtit.');
INSERT INTO "ahmeti" VALUES(3,19,'3:19','Pa dyshim feja te All-llahu është Islami. U kundërshtuan ata që u është dhënë libri, pasi që u erdhi e vërteta, nga zilia mes vete. E kush mohon argumentet e All-llahut, (le ta dijë se) All-llahu (ia) llogaritë shpejtë.');
INSERT INTO "ahmeti" VALUES(3,20,'3:20','E, nëse të polemizojnë, thuaj: “Ia kam nënshtruar fytyrën (qenien) All-llahut, e edhe ithtarët e mij!” E thuaju edhe atyre që u është dhënë libri dhe analfabetëve: “A u nënshtruat?” Nëse janë nënshtruar, atëherë janë përudhur e nëse refuzojnë, vërtet ti ke (për obligim vetëm) kumunikimin; All-llahu është Basir (Ai që sheh) për robërit.');
INSERT INTO "ahmeti" VALUES(3,21,'3:21','Ata që mohojnë argumentet e All-llahut, mbysin pejgamberët pa farë të drejte, mbysin njerëz që këshillujnë për të drejtën, ti ata lajmëroi për një ndëshkim të dhembëshëm e pikëllues.');
INSERT INTO "ahmeti" VALUES(3,22,'3:22','Ata veprat e tyre i asgjësuan në këtë botë dhe në botën tjetër, ata nuk kanë ndonjë mbrojtës.');
INSERT INTO "ahmeti" VALUES(3,23,'3:23','A nuk i sheh ti ata, të cilëve u është dhënë mjaft nga libri, kur të thirrën që ndërmjet tyre të gjykojë libri i All-llahut, se si një grup prej tyre prapësohej. Ata janë refuzues.');
INSERT INTO "ahmeti" VALUES(3,24,'3:24','E atë (e bënin) ngase ata thonin: “Neve nuk do të na djegë zjarri vetëm për pak ditë të numëruara, e ajo që shpifën për fenë e tyre, i mashtroi keq.');
INSERT INTO "ahmeti" VALUES(3,25,'3:25','E, si do të jetë gjendja e tyre kur Ne do t’i tubojmë ata një ditë, për të cilën nuk ka dyshim dhe çdo njeriu do t’i ofrohet ajo që e ka e fituar, duke mos iu bërë atyre kurrnjë e padrejtë.');
INSERT INTO "ahmeti" VALUES(3,26,'3:26','Thuaj: “O All-llah, Sundues i çdo sendi, Ti ia jep pushtetin atij që do, Ti ia heq prej dore pushtetin atij që do dhe e përul atë që do, e lartson atë që do. çdo e mirë është në dorën Tënde, vërtet, Ti ke mundësi për çdo gjë!”');
INSERT INTO "ahmeti" VALUES(3,27,'3:27','Ti e fute natën në ditë dhe Ti e fute ditën në natë, Ti nxjerr nga i vdekuri të gjallin dhe nga i gjalli të vdekurin dhe Ti e begaton pa masë atë që do!');
INSERT INTO "ahmeti" VALUES(3,28,'3:28','Besimtarët të mos i miësojnë mosbesimtarët, e t’i lënë manash besimtarët. E kush bën atë, ai nga feja e All-llahut nuk ka asgjë, përveq për qëllim ruajtja prej të keqës së tyre. All-llahu ju tërheq vërejtjen prej (dënimit të) Tij pse vetëm te All-llahu është e ardhmja.');
INSERT INTO "ahmeti" VALUES(3,29,'3:29','Thuaj: “Edhe nëse e fshefin atë që keni në zemrat tuaja ose, e publikoni, All-llahu e di gjithçka ka në qiej e ç’ka në tokë; All-llahu është i plotëfuqishëm për çdo send.');
INSERT INTO "ahmeti" VALUES(3,30,'3:30','Ditën kur çdo njeri e gjen pran vete atë që veproi mirë ose keq, e për atë të keqe që bëri, do të dëshiroj që në mes tij dhe në mes asaj të jetë një distancë shumë më e madhe (e mos ta shohë). All-llahu u jep të frikësohen prej dënimit Tij, megjithqë All-llahu është i mëshirshëm ndaj robëve.');
INSERT INTO "ahmeti" VALUES(3,31,'3:31','Thuaj: Nëse e doni All-llahun, atëherë ejani pas meje që All-llahu të ju dojë, t’ju falë mëkatet tuaja, se All-llahu është që fal shumë, mëshiron shumë.');
INSERT INTO "ahmeti" VALUES(3,32,'3:32','Thuaj: “Binduni All-llahut dhe të dërguarit, e ata refuzojnë, atëherë All-llahu nuk i do pabesimtarët!”');
INSERT INTO "ahmeti" VALUES(3,33,'3:33','All-llahu e zgjodhi (pejgamber) Ademin, Nuhun, familjen Ibrahimit, familjen e Imranit mbi popujt tjerë (të asaj kohe).');
INSERT INTO "ahmeti" VALUES(3,34,'3:34','Që janë pasardhës njëri prej tjetrit. All-llahu dëgjon gjithçka dhe i di të gjitha.');
INSERT INTO "ahmeti" VALUES(3,35,'3:35','(Përkujtohu) Kur gruaja e Imranit pat thënë: “Zoti im, Unë këtë që është në barkun tim, vendosa ta kushtoj thjesht vetëm për shërbimin Tënd, pra pranoje këtë prej meje, vertetë Ti je Ai që dëgjon e di!”');
INSERT INTO "ahmeti" VALUES(3,36,'3:36','E kur ajo e lindi tha: “Zoti im, unë e linda femër!” Po All-llahu e di më së miri atë që ajo e lindi. E mashkulli nuk si femra.” Dhe unë e emërtova atë Merjeme, e atë dhe pasardhësit e saj po t’i lë Ty në mbrojtje prej djallit të mallkuar.');
INSERT INTO "ahmeti" VALUES(3,37,'3:37','Zoti i saj e pranoi premtimin e saj si është më mirë, e rriti me një edukatë të mirë e të plotë dhe e vuri nën kujdesin e Zekirjas. Sa herë që hynte Zekirjaja në mihrabin e saj gjente te ajo ushqimin e thonte: “Ai është nga All-llahu, se All-llahu atë që do pa masë e furnizon!”');
INSERT INTO "ahmeti" VALUES(3,38,'3:38','Aty në atë moment Zekirjaja e lut Zotin e tij e thotë: “Zoti im, falma edhe mua nga ana juaj një ppasardhës (fëmijë) të mirë, vërtet, Ti je dëgjues i lutjes”!');
INSERT INTO "ahmeti" VALUES(3,39,'3:39','E duke u falulr ai në faltore engjëjt e thërrasin: “All-llahu të përgëzon ty me Jahjanë, që do të vërtetojë fjalën (Isain) e ardhur nga All-llahu, e që do të jetë prijës i matur dhe pejgamber nga të dalluarit!”');
INSERT INTO "ahmeti" VALUES(3,40,'3:40','Ai (Zekirjaja) tha: “Zoti im, si mund të kem unë djalë kur mua më ka arritu pleqëria dhe shoqja ime beronjë (sterile)?” Tha (All-llahu): “Kështu, All-llahu punon çka të dojë!”');
INSERT INTO "ahmeti" VALUES(3,41,'3:41','Ai (Zekirjaja) tha: “Zoti im më jepë mua një shenjë!” Shenja e jote - tha Ai - është që tri ditë nuk do të mund t’u flasësh njerzve, pos me gjeste (mimikë), e ti përmende shumë Zotin tënd dhe madhëroje mbrëmje e mëngjes!”');
INSERT INTO "ahmeti" VALUES(3,42,'3:42','Përkujto kur engjujt i thanë:” Oj Mejreme, All-llahu të dalloi ty (me besim e karakter), dhe të lartësoi mbi gratë e botës.');
INSERT INTO "ahmeti" VALUES(3,43,'3:43','Oj Mejreme, vazhdoje adhurimin ndaj Zotit tënd, bën sexhde dhe falu për Zotin bashkë me ata që falen!');
INSERT INTO "ahmeti" VALUES(3,44,'3:44','Këto janë nga lajmet e fshehta (të hershme) që po t’i shpallim ty. Ti nuk ishe ndër ta kur i hidhnin shortet se kush prej tyre do të bëhej kujdestar i Mejremes, nuk ishe pranë tyre as kur ata ziheshin mes vete.');
INSERT INTO "ahmeti" VALUES(3,45,'3:45','Përkujto kur engjujt i thanë: “Oj Mejreme, All-llahu të përgëzon me fjalën e vet (me lindjen e një fëmije si rezultat i fjalës së Zotit).');
INSERT INTO "ahmeti" VALUES(3,46,'3:46','E që duke qenë në djep (foshnje) u flet njerëzve, e edhe si i rritur e që është nga të përsosurit.');
INSERT INTO "ahmeti" VALUES(3,47,'3:47','Ajo tha: “Zoti im, si mund të kemë unë djalë e mua s’më ka prekur njeriu (nuk jam e martuar)? Ai (All-llahu) tha: “Ja, kështu, All-llahu krijon çka të dojë. Kur Ai vendos për një çështe, vetëm i thotë: Bëhu! Ajo menjëherë bëhet.');
INSERT INTO "ahmeti" VALUES(3,48,'3:48','Ai (All-llahu) ia mëson atij librin (besimin), urtësinë, Tevratin dhe Inxhilin.');
INSERT INTO "ahmeti" VALUES(3,49,'3:49','Dhe, të dërguar te bijtë e Israilit: unë kam ardhur nga Zoti juaj me argument, unë nga balta ju bëj diç si shpendi, i fryej atij dhe ai me lejen e All-llahut bëhet shpend, unë i sheroj të verbërit, të sëmurit ne lëkurë, dhe unë me lejen e All-llahut ngjalli të vdekurit: unë ju tregoj për atë që e hani dhe për atë që e depononi në shtëpiat tuaja. Vertetë, kjo është fakt për ju nëse jeni besimtarë.');
INSERT INTO "ahmeti" VALUES(3,50,'3:50','Dhe (kam ardhur) që t’ju vërtetoj Tevratin që e keni para duarsh, t’ju lejoj disa që u ishin ndaluar juve, kam ardhur me argument nga Zoti juaj, pra kinie frikë All-llahun dhe më dëgjoni mua.');
INSERT INTO "ahmeti" VALUES(3,51,'3:51','All-llahu është Zoti im dhe Zoti juaj. Adhuronie Atë; kjo është më rrugë e drejtë!');
INSERT INTO "ahmeti" VALUES(3,52,'3:52','E kur e kuptoi Isai vendosmërinë e tyre në mosbesim (dhe qëllmin që ta mbysin), tha: ‘Kush janë ndihmëtarët e mij për në rrugë të All-llahut?’ Havarijunët thanë: ‘Ne jemi ndihmëtarë të fesë së All-llahut, nei besuam All-llahut, e ti dëshmo për ne se jemi muslimanë (të bindur, të dorëzuar)!');
INSERT INTO "ahmeti" VALUES(3,53,'3:53','Zoti ynë, ne e besuam atë që e zbrite (shpalljen), e pasuam të dërguarin (Isain), pra shënona bashkë me ata që dëshmojnë (besimin e drejtë)!’');
INSERT INTO "ahmeti" VALUES(3,54,'3:54','E, ata (jehudië) i kurdisën një dredhi (mbytjen e Isait), All-llahu iu kundëvu dredhisë së tyre, All-llahu është asgjësuesi më i fuqishëm kundër atyre që bëjnë dredhi.');
INSERT INTO "ahmeti" VALUES(3,55,'3:55','(Përkujto, o i dërguar) Kur All-llahu tha: ‘O Isa, Unë po të marr ty, po të ngris te Unë, po të shpëtoj prej sherrit të atyre që nuk besuan. E ata që të besuan ty, do t’i ngrisë lart mbi ata që nuk besuan deri në ditën e kijametit, pastaj vetëm te Unë është kthimi juaj, Unë gjykuj mes jush për atëqë kudështoheshit.');
INSERT INTO "ahmeti" VALUES(3,56,'3:56','Për sa u përket atyre që nuk besuan, Unë do t’i ndëshkoj me një ndëshkim të ashpër në këtë botë dhe në botën tjetër, dhe ata nuk do të kenë ndihmëtarë.');
INSERT INTO "ahmeti" VALUES(3,57,'3:57','E për sa u përket atyre që besuan dhe bënë vepra të mira, atyre u japë shpërblim të plotë. All-llahu nuk i do të padrejtit.');
INSERT INTO "ahmeti" VALUES(3,58,'3:58','Këto që po t’i lexojmë ty (o i dërguar) janë nga ajetet, janë nga Kur’ani i pa të meta (i rrezistueshëm).');
INSERT INTO "ahmeti" VALUES(3,59,'3:59','Vërtet, qështja e Isait (të lindur pa baba) te All-llahu është sikurse qështja e Ademit. Atë e krijoi Ai nga dheu, e pastaj atij i tha “Bëhu!” ai u bë.');
INSERT INTO "ahmeti" VALUES(3,60,'3:60','E vërtetëështë (kjo) nga Zoti yt, e ti kurrsesi mos u bën nga ata që dyshojnë.');
INSERT INTO "ahmeti" VALUES(3,61,'3:61','E kush të kundërshton ty në qështjen e tij (Isait) pasi të është bërë e ditur e vërteta, ti thuaj: ‘Ejani i thërrasim bijtë e tanë dhe bijtë tuaj, gratë tona dhe gratë tuaja, vetë ne dhe vet ju, mandej sinqerisht të lutemi për mallkim, dhe mallkimi nga ana e All-llahut ta hedhim kundër gënjeshtarëve.');
INSERT INTO "ahmeti" VALUES(3,62,'3:62','S’ka dyshim, ky është lajm i vërtetë. Nuk ka asnjë të adhuruar tjetër pos All-llahut. All-llahu është Ai, i plotëfuqishmi, i vetëdijshmi.');
INSERT INTO "ahmeti" VALUES(3,63,'3:63','E nëse ata refuzojnë (besimin e drejtë), ata janë ngatërrestarë, për të cilët All-llahu e di shumë mirë.');
INSERT INTO "ahmeti" VALUES(3,64,'3:64','Thuaju (o i dërguar): “‘O ithtarë të librit (Tevrat e Inxhil), ejani (të bashkohemi) te një fjalë që është e njejtë (e drejtë) mes nesh dhe mes jush: të mos adhurojmë, pos All-llahut, të mos ia bëjmë Atij asnjë send shok, të mos konsiderojmë njëri - tjetrin zotër pos All-llahut!” E në qoftë se ata refuzojnë, ju thoni: “Dëshmoni pra, se ne jemi muslimanë (besuam një Zot)!”');
INSERT INTO "ahmeti" VALUES(3,65,'3:65','(madje thuaju): “O ithtarë të librit, pse po polemizoni me ne rreth Ibrahimit? E, nuk ja në shpallur as Tevrati as Inxhilli, vetëm se pas tij?! A nuk e kuptoni (gënjeshtrën tuaj)?”');
INSERT INTO "ahmeti" VALUES(3,66,'3:66','Ja ju jeni ata që polemizuat edhe për atë që e dinit (për Isain) e përse polemizoni për atë që s’keni kurrfarë dije (ppër Ibrahimin)? All-llahu e din të vërtetën e ju nuk e dini.');
INSERT INTO "ahmeti" VALUES(3,67,'3:67','Ibrahimi nuk ka qenë as jehudi as i krishterë, por ai ishte larg besimeve të kota, ishte musliman dhe nuk ishte prej idhujtarëve.');
INSERT INTO "ahmeti" VALUES(3,68,'3:68','Njerëzit më me meritë për t’u thirrur në Ibrahimin, ishin ata që e ndoqën atë (besimin e tij) dhe ky Pejgamber me ata që besuan (ymeti i këtij). All-llahu është mbrojtës i besimtarëve.');
INSERT INTO "ahmeti" VALUES(3,69,'3:69','Një grup nga uthtarët e librit kishin dëshirë t’u shpiejnënë rrugë të gabuar por ata nuk mashtrojnëtjetërpos vetes së tyre, dhe nuk e vërejnë.');
INSERT INTO "ahmeti" VALUES(3,70,'3:70','O ithtarë të librit, përse nuk i besoni ajetet (Kur’anin) e All-llahut, e duke e ditur të vertetën?');
INSERT INTO "ahmeti" VALUES(3,71,'3:71','O ithtarët e librit, përse, duke qenë se ju e dini të vertetëne ngatërroni me gënjeshtën dhe fshihni realitetin?');
INSERT INTO "ahmeti" VALUES(3,72,'3:72','Një grup nga ithtarët e librit thanë (të vetëve): “Besoni para dite në atë që u është shpallur atyre që besuan (muslimanëve), e pasdite mos e besoni, e ndoshta edhe ata (muslimanët), do të zbrapsen (nga besimi i tyre).');
INSERT INTO "ahmeti" VALUES(3,73,'3:73','Dhe (u thonin): Mos i besoni askujt përveç atij që ështënë fenë tuaj!” Thuaj (o i dërguar):” I vetmi udhëzim është udhëzimi nga All-llahu!” (u thoni të vetëve mos besoni nga droja) Se po i jepet ndokujt (pejgamberllëku) sikurse u është dhënëjuve dhe (nga droja) se t’ju paraqesin fakte (muslimanët) para Zotit tuaj (ditën e gjykimit). Thuaju: “E tërë e mira është në duar të All-llahut, Ai ia jep atë kujt të dojë; All-llahu është dhurues i gjërë, i gjithdijshëm”');
INSERT INTO "ahmeti" VALUES(3,74,'3:74','Ai veçon me mëshirën e vet (me pejgamerllëk) kë të dojë. All-llahu është Zot i mirësisë së madhe.');
INSERT INTO "ahmeti" VALUES(3,75,'3:75','Ndër ithtarë të librit ka, që po ia besove një sasi të madhe (ari), ai ta kthen ty atë, por ka të atillë që po ia besove një dinarë, ai nuk takthen ty atë, përveç nëse gjithnjë irri gati (ia kërkon pajada). Këtë (e bëjnë) për arsye se ata thonin:” Ne nuk kemi kurrfarë përgjegjësie ndaj (pasurisë që u marrim) të të paditurëve (arabëve analfabetë). Pra duke e ditur të vertetën, ata bëjnë gënjeshtër ndaj All-llahut.');
INSERT INTO "ahmeti" VALUES(3,76,'3:76','Jo, (nuk është ashtu si thonë ata) po kush e përmbush emanetin e vet (është besnik) dhe ruhet nga mëkatet, s’ka dyshim, All-llahu i do ata që ruhen.');
INSERT INTO "ahmeti" VALUES(3,77,'3:77','Ata që për një vlerë të paktë e shesin besën e dhënë All-llahut, ndryshojnë edhe zotimet e tyrue, të tillët nuk kanë pjesë (mëshirë) në botën tjetër, dhe në ditën e kijametit All-llahu nuk u flet atyre, nuk i shikon ata dhe i pastron (prej barrës së gabimeve), ata kanë një dënim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(3,78,'3:78','Në të vertetë, një grup prej tyre janë ata që pëhtjellojnë gjuhët e tyre kur lexojnë librin (për të deformuar kuptimin) ashtuqë ju të mendoni se ajo (shprehje) është nga libri, po ajo nuk është nga (i vertetë), adje thonë: Kjo është nga All-llahu!” Po ajo nuk është nga All-llahu. Ata duke ditur thonë gënjeshtra për All-llahun.');
INSERT INTO "ahmeti" VALUES(3,79,'3:79','S’është e drejtë as nuk i takoiasnjë njeriu që t’i ketë dhënë All-llahu librin, urtësinë dhe pejgamberllëkun, e pastaj ai t’u thotë njerëzve:” Bëhuni rob të mij (adhuromëni mua) e jo të All-llahut!” por (ju thotë):” Bëhumi dijetarë të mësimeve të Zotit, ngase e keni mësuar njerëzve livrin dhe e keni studiuar atë.');
INSERT INTO "ahmeti" VALUES(3,80,'3:80','Dhe as që ju urdhëron ai (pejgamberi) që të adhuroni engjëjt, as pejgamberët për zota. Vallë, a ju urdhëoi ai juve mosbesimin pasiqë ju jeni muslimanë?');
INSERT INTO "ahmeti" VALUES(3,81,'3:81','Përkujtoni (o ithtarë të librit) kur All-llahu mori zotimin e pejgamberëve: nga ajo se juve ju dhashë libër e urtësi, e që verteton atë që ju keni pranë veti. Tha (All-llahu):” A pranuat, a e morët sipër obligimin Tim? “Ata thanë:” Ne e pranuam!” Tha (All-llahu):” Dëshmoni pra, edhe Unë dëshmoj bashkë me ju!”');
INSERT INTO "ahmeti" VALUES(3,82,'3:82','E kush shkel këtë (zotim) pas kësaj, ata janë jashtë respektit ndaj All-llahut.');
INSERT INTO "ahmeti" VALUES(3,83,'3:83','A mos kërkojnë ata (ithtarët e librit) fe, pos fesë së shpallur nga All-llahu? E Atij i është dorëzuar gjithë ç’ka në qiej e në tokë, me dashje dhe te Ai kthehen.');
INSERT INTO "ahmeti" VALUES(3,84,'3:84','Thuaj: “Ne i kemi besuar All-llahut, edhe asaj që na zbrit neve, edhe asaj që i është zbritur Ibrahimit, Ismailit, Is-hakut, Jakubit dhe pasardhësve. Edhe asaj që i është dhënë Musait dhe Isait, edhe asaj që i është zbritur të gjithë pejgamberëve nga Zoti i tyre. Ne nuk bëjmë kurrfarë dallimi në mes tyre dhe ne vetëmAtij jemidorzuar.');
INSERT INTO "ahmeti" VALUES(3,85,'3:85','E, kush kërkon fe tjetër përveç fesë islame, atij kurrsesi nuk do ti pranohet dhe ai në botën tjetër është nga të dëshpruarit.');
INSERT INTO "ahmeti" VALUES(3,86,'3:86','E, si ta udhëzojë All-llahu një popull që pas besimit të tyre u bënë pabesimtarë dhe pasi dëshmoi se i dërguari është i vërtetë, dhe pasi t’ju kenë ardhur atyre argumente të qarta? Alllahu nuk i mundëson udhëzimin e Vet popullit zullumqarë.');
INSERT INTO "ahmeti" VALUES(3,87,'3:87','Ndëshkimi i të tillëve është: mallkimi nga All-llahu, nga engjëjt dhe nga gjithë njerëzimi tok.');
INSERT INTO "ahmeti" VALUES(3,88,'3:88','Ata përgjithmonë janë në te (në zjarr), atyre as nuk ju lehtësohet dënimi dhe as nuk u jepet afat.');
INSERT INTO "ahmeti" VALUES(3,89,'3:89','Përveq atyre që pas asaj upendan dhe u përmirsuan. Vërtet, All-llahu falë shumë dhe mëshiron.');
INSERT INTO "ahmeti" VALUES(3,90,'3:90','Ata, të cilët pas besimit të tyre u bënë pabesimtarë, e pastaj e shtuan mosbesimin, atyre kurrsesi nuk du pranohet pendimi. Të tillët janë mu ata të humburit.');
INSERT INTO "ahmeti" VALUES(3,91,'3:91','Ata që nuk besuan dhe vdiqën si mosbesimtarë, anjërit prej tyre nuk do t’u pranohet ër konpensim, qoftë edhe plotë faqen e dheut ari. Ata i pret dënim dhëmshëm dhe për ta nuk ka ndihmëtarë.');
INSERT INTO "ahmeti" VALUES(3,92,'3:92','Kurrë nuk do të arrini sinqeritetin e plotë me besim (as kënaqësinë e lumtur në Xhennet) derisa të mos e jepnimë të dhëmbshmen (më të dashurën) e pasurisë suaj. çkado që jepni (për Zotin), All-llahu atë e di.');
INSERT INTO "ahmeti" VALUES(3,93,'3:93','I tërë ushqimi ishte i lejuar për bijtë e israilit, përveç atij që Israili (Jakubi) para se të s briste Tevrati, ia ndaloi vetvetës. Thuaju: “Sillne pra një Tevrat dhe lexoni atë, nëse jeni të vërtetë (në çka thoni)?');
INSERT INTO "ahmeti" VALUES(3,94,'3:94','Ai që edhe pas kësaj shpif gënjeshtra kundër All-llahut, ata vërtet janë mizorë.');
INSERT INTO "ahmeti" VALUES(3,95,'3:95','Thuaj: “Alahu e tha të vërtetën, pra ndiqne fenë e pastër të Ibrahimit, sepse ai nuk u ta konte idhujtarve.');
INSERT INTO "ahmeti" VALUES(3,96,'3:96','Shtëpia (xhamia) e parë e ndërtuar për njerëz, është ajo në Bekë (Mekë), e dobishme udhërrëfyese për mbarë njerëzimin.');
INSERT INTO "ahmeti" VALUES(3,97,'3:97','Aty ka shenja të qarta: vendi iIbrahimit, dhe kush hyn në te, ai i sigurt, Për hirë të All-llahut, vizita eshtëpisë (Qabes) ësht obligim për atë që ka mundësi udhëtimi te ajo, e kush nuk e beson (ai nuk e viziton); All-llahu nuk është i nevojshëm për (ibadetin që e bëjnë) njerëzit.');
INSERT INTO "ahmeti" VALUES(3,98,'3:98','Thuaj: “O ithtarë të librit, përse i mohoni ajetet (argumentet) e All-llahut? E All-llahu mbikëqyr atë që veproni.');
INSERT INTO "ahmeti" VALUES(3,99,'3:99','Thuaj: “O ithtarë të librit, përse e pengoni atë që besoni nga rruga e All-llahut, duke u angazhuar që ta paraqitni atë të shtrembër, ndërsa vetë ju, jeni dëshmues se është e vërtetë! Veprimi juaj nuk mund t’i shmanget mbikëqyrjes së All-llahut.');
INSERT INTO "ahmeti" VALUES(3,100,'3:100','O ju që besuat, në qoftë se ju i bindeni një grupi të atyre që iu është dhënë libri, ata, do t’ju kthejnë në mosbesimtarë.');
INSERT INTO "ahmeti" VALUES(3,101,'3:101','Si bën të mos besoni, kr juve janë duke iu lexuar (duke iu shpallur) ajetet e All-llahut dhe kur në mesin tuaj gjendet i derguari i Tij? E kush i përmbahet fesë së All-llahut, ai padyshim është i udhëzuar në rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(3,102,'3:102','O ju që besuat, kinie frikë All-llahun me sinqeritet të vërtetë dhe mos vdisni, pos vetëm duke qenë muslimanë (besimtarë)!');
INSERT INTO "ahmeti" VALUES(3,103,'3:103','Dhe kapuni që të gjithë ju për litarin (fenë dhe Kur’anin) e All-llahut, e mos u përçani! Përkujtomie nimetin e All-llahut ndaj jush, kr ju (para se ta pranonit fenë islame) ishit të armiqësuar, e Ai bashkoi zemrat tuaja dhe ashtu me dhuntitë e Tij aguat të jeni vëllezër. Madje ishit në buzë të greminës së Xhehennemit, e Ai ju shpëtoi prej tij. Po kështu All-llahu ua shqaron juve argumentet e veta që ju të gjeni të vërtetën e lumtur.');
INSERT INTO "ahmeti" VALUES(3,104,'3:104','Nga ju le të jetë grup që thërret në atë që është e dobishme, urdhëron për punë të mbara dhe ndalon nga e keqja. Të tillët janë ata të shptuarit.');
INSERT INTO "ahmeti" VALUES(3,105,'3:105','E mos u bëni si ata që u ndanë dhe u përçanë pasi u patën zbritur argumentet. Ata do të pësojnë një dënim të madh.');
INSERT INTO "ahmeti" VALUES(3,106,'3:106','Në ditën kur ka fëtyra që zbardhen dhe ka fytyra që nxihen. E për sa u përket atyre fëtyrave që janë nxirë u (thuhet): “A edhe pasbesimit tuaj u bëtë pabesimtarë? Vuanie pra, dënimin për shkak se ishit që nuk besojshit!”');
INSERT INTO "ahmeti" VALUES(3,107,'3:107','Ndërsa atyre që u janë zbardhur, ata janë në mëshirën (Xhennetin) e All-llahut dh aty janë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(3,108,'3:108','Ja, këto që po t’i lexojmë ty janë argumentet e sakta të All-llahut. E All-llahu nuk është ai që e do padrejtsinë për njerëz.');
INSERT INTO "ahmeti" VALUES(3,109,'3:109','Të All-llahu jaë ç’ka në qiej dhe ç’ka në tokë dhe vetëm te All-llahu kthehen çështjet.');
INSERT INTO "ahmeti" VALUES(3,110,'3:110','Je jeni populli më i dobishëm, i ardhur për të mirën e njerëzve, të urdhëroni për mirë, të ndaloni nga veprat e këqia dhe të besoni në All-llahun. E sikur ithtarët e librit të besonin drejt, do të ishte shumë më e mirë për ta. Disa prej tyre janë besimtarë, por shumica e tyre janë larg rrugës së Zotit.');
INSERT INTO "ahmeti" VALUES(3,111,'3:111','Ata kurrsesi nuk mund t’ju sjellin ndonjë dëm jve, përveç ndonjë shqetësimi, po edhe në ju luftofshin, ata do të zmbrapsen prej jush. Mandej ata nuk janë të ndihuar.');
INSERT INTO "ahmeti" VALUES(3,112,'3:112','Atyre (jehudive) u është vënë njollë e nënçmimit kudo që të gjenden, vetëm nëse kapen për litarin (fenë) e All-llahut dhe kthehen në besën e njerëzve (të muslimanëve), ata kanë shkaktuar kundër vetes përbuzje nga All-llahu, andaj atyre u është shtar shtypja e mjerimi. Këtë për shkak se ata i mohonin argumentet e All-llahut, i mbytnin mizorisht pejgamberët dhe për shkak se kundërshtonin (udhëzimet e Zotit) dhe tejkalonin (çdo normë njerëzore).');
INSERT INTO "ahmeti" VALUES(3,113,'3:113','Ata (ithtarët e librit) nuk janë të njejtë. Nga ithtarët e librit ka që janë në rrugë të drejtë, gjatë natës lexojnë ajetet e All-llahut duke u lutur.');
INSERT INTO "ahmeti" VALUES(3,114,'3:114','I besojnë All-llahut dhe së ditës, këshllojnë për të mirë dhe largojnë nga të dobishme, edhe ata janë prej të mirëve.');
INSERT INTO "ahmeti" VALUES(3,115,'3:115','E çfarëdo të mire që bëjnë, ajo nuk u mohohet. All-llahu i di shumë mirë të devotshmit.');
INSERT INTO "ahmeti" VALUES(3,116,'3:116','S’ka dyshim se atyre që nuk besuan, nuk do t’u bëjnë dobi ndaj (dënimit të) All-llahut as pasuria e as fëmijët e tyre. Ata janë banues të zjarrit dhe aty janë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(3,117,'3:117','Shembulli i asaj që ata e japin në këtë jetë (për t’u lavdëruar e për e përmendur) është si i një erë me breshër që godet dhe shkatërron të mbjellat e një populli që e ka lëshuar rrugën në dëm të vetes. All-llahu nuk u bëri zullum atyre, por ata ishin që i benë të padrejtë vetes së vet.');
INSERT INTO "ahmeti" VALUES(3,118,'3:118','O ju që besuat, mos i zini për miq të ngushtë të tjerët jashtë mesit tuaj, ata nuk pushojnë së bepruari në dëm tuajin, u dëshirojnë çka u mundon juve. Urrejta kundër jush duket nga gojët e tyre, por ajo që fshehin në tyre, është edhe më e madhe. Ne pra, u kemi sqaruar faktet nëse ju i kuptoni.');
INSERT INTO "ahmeti" VALUES(3,119,'3:119','Ja, ju (muslimanë) jeni që i doni ata, e ata nuk u duanjuve. Madje ju i besoni librat në tërësi (të gjitha shpalljet), e kur u takojnë thonë:” Ne kemi besuar!” e kur veçohen, ata mllefi kundër grisin majet e gishtrinjve. Thuaju: “Vdisni me atë mllef tuajin!” All-llahu i di shumë mirë se çka mbajnë (të fshehtat) zemrat e tyre.');
INSERT INTO "ahmeti" VALUES(3,120,'3:120','Nëse ju përjetoni ndonjë të mirë, ata i dëshpron ajo, e nëse u godet ndonjë e keqe, ata gëzohen për atë. Po, në qoftë se ju bëhi të durueshëm dhe ruheni (mëkateve), dinakëria e tyre nuk do të t’u dëmtojë aspak. Është e sigurt se All-llahu e ka në dorë atë që punojnë ata.');
INSERT INTO "ahmeti" VALUES(3,121,'3:121','Përkujto (O i dërguar) kur dole nga familja jote që t’ua përcaktosh besimtarëve vendet për luftë, e All-llahut është që dëgjon (fjalët) dhe që kupton (qëllimet).');
INSERT INTO "ahmeti" VALUES(3,122,'3:122','Kur dy grupe prej jush gati u treguan të dobëta, po All-llahu ishte që u ndihmoi atyre (që të mos largoheshin), andaj besimtarët le t’i mbështeten vetëm All-llahut.');
INSERT INTO "ahmeti" VALUES(3,123,'3:123','All-llahu u ndihmoi juve në Bedr kur ju ishit pak në numër, e për të qenë mirënjohës, keni frikë nga Zoti.');
INSERT INTO "ahmeti" VALUES(3,124,'3:124','Kur ti u thoshe besimtarëve: “A nuk është mjaft për ju që Zoti juaj t’ju vazhdojë ndihmën me tre mijë engjëjt e zbritur?”');
INSERT INTO "ahmeti" VALUES(3,125,'3:125','Po, nëse jeni të qëndrushëm e të matur, e ata (armiqtë) t’ju vinë tash në këtë momoent, Zoti juaj ua vazhdon juve ndihmën me pesë mijë engjëjve të ushtruar (për luftë) ose të shënuar.');
INSERT INTO "ahmeti" VALUES(3,126,'3:126','E atë (ndihmë) All-llahu nuk e bëripër tjetër por vetëm për t’ju gëzuar dhe me të të qetësojë zemrat tuaja, përndrushe ndihma vjen vetëm prej All-llahut, ngadhënjyesit, të urtit.');
INSERT INTO "ahmeti" VALUES(3,127,'3:127','(u ndihmoi) Që ta këpusë një grup nga ata që nuk besuan (duke i mbytur a zënë rovër), ose për t’i demoralizuar dhe ashtu të kthehen të dëshpruar.');
INSERT INTO "ahmeti" VALUES(3,128,'3:128','Ty nuk të takon asgjë rreth çështjes se a do t’u pranojë Ai pendimin apo do t’i dënojë ata, në të vërtetë ata janë zullumqarë.');
INSERT INTO "ahmeti" VALUES(3,129,'3:129','Vetëm e All-llahut është gjithç’ka në qiej e në tokë, Ai i fal atij që do dhe Ai dënon atë që do. All-llahu është mëkatfalës, mëshirues.');
INSERT INTO "ahmeti" VALUES(3,130,'3:130','O ju të clët besuat, mos e hani kamaatën që po e shumë fishoni dhe kini frikë (dënimin e) All-llahut ashtu që të gjeni shpëtim.');
INSERT INTO "ahmeti" VALUES(3,131,'3:131','Dhe ruajuni zjarrit që është përgaditur për pabesimtarët.');
INSERT INTO "ahmeti" VALUES(3,132,'3:132','E bindjuni All-llahut dhe të dërguarit, ashtu qëtë mëshiroheni.');
INSERT INTO "ahmeti" VALUES(3,133,'3:133','Dhe ngutuni (me punë që meritoni) në falje mëkatesh nga Zoti juaj dhe për në një Xhennet, gjerësia e të cilit është si gjerësia e qiejve dhe e tokës, i përgatitur për të devotshmit.');
INSERT INTO "ahmeti" VALUES(3,134,'3:134','Të cilët japin kur janë shlirë edhe kur janë në vështirësi dhe që e frenojnë mllefin, që u falin (keqen) njerëzve, e All-llahu i do bamirësit.');
INSERT INTO "ahmeti" VALUES(3,135,'3:135','Edhe ata të clët kur bëjnë ndonjë (mëkat) të shëmtuar ose i bëjnë zullum vetës së tyre, e përmendin All-llahun dhe kërkojnë falje për mëkatet e tyre - e kushi i falë mëkatet përveç All-llahut? - dhe që duke ditur, nuk vazhdojnë në ata që kanë punuar (në të keqen).');
INSERT INTO "ahmeti" VALUES(3,136,'3:136','Shpërblimii të tillëve është falja nga Zoti i tyre dhe Xhennetet nëpër të cilët rrjedhin lumenj e aty do rrijnë përgjithmonë. a i mirë është shpërblimi i atyre që punojnë.');
INSERT INTO "ahmeti" VALUES(3,137,'3:137','Përpara jush kanë kaluar ppopujt ndaj të cilëve janë zbatuar ligjet, ndaj, udhtoni nëpër tokë dhe shikoni si qe përfundimi i atyre që përgenjeshtruan.');
INSERT INTO "ahmeti" VALUES(3,138,'3:138','Ky (Kur’ani) është sqarim për njerëzit, është dhëzim dhe këshillë për të devotshmit.');
INSERT INTO "ahmeti" VALUES(3,139,'3:139','E mos u dobësoni (fizikisht0 dhe mos u dëshproni (shpirtërisht) derisa ju jeni më të lartit, po qe se jeni besimtarë të sinqert.');
INSERT INTO "ahmeti" VALUES(3,140,'3:140','Në qoftë se juve u preku çka u dhemb (në Uhud). edhe atë popull (armikun) e pat prekur dhembje si kjo e juaja (në Bedr. Ne, këto ditë i ndrrojmë (u japim në përdorim) mes njerëzve për t’u ditur te All-llahu ata që besuan dhe për t’i zgjedhur disa prej jush për dëshmorë (ose dëshmues). All-llahu nuk i do zullumqarët.');
INSERT INTO "ahmeti" VALUES(3,141,'3:141','Dhe që All-llahu t’i pastrojë besimtarët, ndërsa gradualisht t’i shkatërrojë mosbesimtarët.');
INSERT INTO "ahmeti" VALUES(3,142,'3:142','A mos menduat ju se do të hyni në Xhennet e All-llahu pa i ditur (pa u vërtetua në praktikëse prej jush kanë luftuar, dhe pa u ditur cilët prej jush ishin të durueshëm.');
INSERT INTO "ahmeti" VALUES(3,143,'3:143','Ju ishit ata që dëshironit vdekjen para se të ballafaqoheshit me te. Ja pra, atë e patë dhe e shikuat me sytë tuaj.');
INSERT INTO "ahmeti" VALUES(3,144,'3:144','Muhammedi nuk është tjetër vetëm se i dërguar. Edhe përpara tij pati të dërguar (që vdiqën ose u vranë). E nëse ai vdes ose mbytet, a do të ktheheshit ju prapa (nga feja ose lufta)? E kushdo që kthehet prapa, ai nuk i bën dëm All-llahut aspak, kurse All-llahu Ne do t’i shpërblejë mirënjohësit.');
INSERT INTO "ahmeti" VALUES(3,145,'3:145','Askuh nuk vdes pa vullnetin dhe lejen e All-llahut. Ai është shënim i afatit të caktuar. E kush e dëshiron shpërblimin e kësaj bote, Ne atij ia japim, e kush e dëshiron shpërblimin e botes tjetër, edhe atij do t’ia japim atë, kurse mirënjohësit Ne do t’i shpërblejmë.');
INSERT INTO "ahmeti" VALUES(3,146,'3:146','E sa pejgamberë pati qësë bashku me të luftuan turma të mëdha besimtarësh dhe për atë që i goditi në rrugën e All-llahut, ata nuk u dobësuan dhe nuk u përulën. All-llahu i do durimtarët.');
INSERT INTO "ahmeti" VALUES(3,147,'3:147','Fjala e tyre nuk ishte jetër pos vetëm: “Zoti ynë, na i falë mëkatet tona dhe lëshimet në punët tna dhe na forco në vendet tona (në luftë), nduhmona kundër popullit jobesimtar!”');
INSERT INTO "ahmeti" VALUES(3,148,'3:148','Ndaj All-llahuua u dha atyre shpërblimin e kësaj bote dhe shpërbllimin më të mirë të botës tjetër; All-llahu i do punë mirët.');
INSERT INTO "ahmeti" VALUES(3,149,'3:149','O besimtarë, nëse u nënshtroheni jobesimtarëve, ata ju kthejnë prapa aty ku ishit (në kufër) dhe atherë do të jeni të humbur.');
INSERT INTO "ahmeti" VALUES(3,150,'3:150','Sepse vetëm All-llahu është ndihmëtar i juaji dhe Ai është më imiri ndihmës.');
INSERT INTO "ahmeti" VALUES(3,151,'3:151','Ne do të mbjellim frikën në zemrat e atyre që nuk besuan sepse ata All-llahut i trillojnë e imveshin zota të tjerë, duke mos pasur për të kurrëfar fakti, ndaj vendbanimi i tyre do të jetë xhehnemi. Sa i shëmtuar është vendi i mizorëve.');
INSERT INTO "ahmeti" VALUES(3,152,'3:152','All-llahu përmbushi premtimin e vet ndaj jush, ngase me vullnetin e Tij i korrët ata (me shpata) deri kur u dobësuat dhe u përçatë ndërmjet vete, në çështjen e vendit (që u pat caktuar Pejgamberi) dhe pasi vrejtët atë që e dëshironit (prenë e luftës), e atëherë kundërshtuat (urdhërin e Pejgamberit). Pati prej jush që e deshën këtë jetë (ata që u ngutën pas presë) e pati prej jush që e deshtën botën tjetër. Pastaj, për t’ju sprovur u zmbrapi juve nga ata. Po All-llahu u fali juve; All-llahu është dhurues i madh ndaj besimtarëve.');
INSERT INTO "ahmeti" VALUES(3,153,'3:153','Dhe kur largoheshit e nuk përfillshit as njëri-tjetrin, ndërsa i dërguari ju thërriste mbrapa. Atëherë u goditi me dëshprim për dëshprim (që i shkaktuat Pejgamberit), ashtu që mos pikëlloheni për atë që u shpëtoi (preja e luftës) e as për atë që u goditi. All-llahu është i njohur hollësisht me atë që veproni.');
INSERT INTO "ahmeti" VALUES(3,154,'3:154','Mandej, pas asaj tronditjeje tërëndë, Ai u lëshoi sa për qetësim, një kotje (gjumi të lehtë) që i kaploi një grup prej jush, kurse një grup i kishte preokupuar vetëm çështja e vetvetes. Ata formuan bindje të padrejtë ndaaaaj All-llahut, bindje injorante, e thonin: “Ne nuk kemi në dorë asgjë nga kjo çështje!” Thuaj: “E tërë çështja është vetëm në duar të All-llahut!” Ata fshehin në vetvete atë që uty nuk ta shprehin. Thonin: “Sikur ne të kishim diçka në dorë në këtë çështje, ne nuk do të mbyteshim këtu!” Thuaj: “Edhe sikur të ishit në shtëpitë tuaja, atyre që është caktuar mbytja (vdekja), do të dilnin në vendin e flijimit të tyre!” Në mënyrë që All-llahu të provojë atë që është në zemrat e tuaja (sinqeritetin, ose hipokrizinë) dhe që të pastrojë atë që është në zemrat tuaja. All-llahu i di shumë mirëtë fshehtat në zemrat.');
INSERT INTO "ahmeti" VALUES(3,155,'3:155','Vërtet, ata që u zbrapsën prej jush ditën e ndeshjes së dy grupeve (besimtarë dhe idhujtarë në Uhud), ata vetë djalli i shtyri të rrëshqasin me disa punë të tyre që i bënë, po All-llahu ua fali atyre gabimin; All-llahu është mëkatfalës dhe i butë.');
INSERT INTO "ahmeti" VALUES(3,156,'3:156','O besimtarë, mos u bëni si ata që nuk besuan dhe, për vllezërit e tyre që kishin dalë në udhëtim (tregtie e gjallërimi) ose që kishin shkuar në luftë thonin: “Sikur të kishin ndejtur pranë nesh (e të mos dilnin), atë (bindje), All-llahu ua bëri një dëshprim në zemrat etyre. Pse All-llahu ë është Ai që jep jetë dhe jep vdekje dhe All-llahu sheh atë që ju punoni.');
INSERT INTO "ahmeti" VALUES(3,157,'3:157','E sikur të ishit mbytur në rrugën e All-llahut, ose t’u kikshte zënë vdekja, do të gëzonit falje e mëshirë prej All-llahut që është shumë më e dobishme se ajo çka ata grumbullojnë.');
INSERT INTO "ahmeti" VALUES(3,158,'3:158','Ti ishe ibutë ndaj atyre, ngase All-llahu të dhuroi mëshirë, e sikur të ishe i vrazhdë e zemërfortë, ata do shkapërderdheshin prej teje, andaj ti falju atyre dhe kërko ndjesë për ta, e konsultohu me ta në të gjitha çështjet, e kur të vendosësh, atherë mbështetu në Allhun, se All-llahu ido ata që mbështeten.');
INSERT INTO "ahmeti" VALUES(3,159,'3:159','Ti ishe ibutë ndaj atyre, ngase All-llahu të dhuroi mëshirë, e sikur të ishe i vrazhdë e zemërfortë, ata do shkapërderdheshin prej teje, andaj ti falju atyre dhe kërko ndjesë për ta, e konsultohu me ta në të gjitha çështjet, e kur të vendosësh, atherë mbështetu në Allhun, se All-llahu ido ata që mbështeten.');
INSERT INTO "ahmeti" VALUES(3,160,'3:160','Nëse All-llahu dëshiron t’ju ndihmojë, s’ka kush që mund t’ju mposhtë, e nëse Ai iu lë pa ndihmë, atherë kush është ai pas Tij, që dot’jundihmojë? Pra vetëm All-llahut le t’i mbështeten besimtarët.');
INSERT INTO "ahmeti" VALUES(3,161,'3:161','Asnjë pejgamberi nuk i takoi të bëjë hile dhe kush bën hile (në ndarjen e plaçkës së luftës), ai në ditën e kijametit vjen i ngarkuar me atë që ka bërë, pastaj secilit njeri i jepet ajo që e ka fituar, duke mos iu bërë e padrejtë.');
INSERT INTO "ahmeti" VALUES(3,162,'3:162','A është i njejtë ai qëiu bind All-llahut dhe kërkoi kënaqësinë e Tij, si ai që tërhoqi kundër vetes hidhërim të madh nga All-llahu dhe e ardhmja e tij është Xhehennemi, që është përfundim shumë i keq?');
INSERT INTO "ahmeti" VALUES(3,163,'3:163','Ata kanë dallim te All-llahu, e All-llahu she shumë mirë ata që veprojnë.');
INSERT INTO "ahmeti" VALUES(3,164,'3:164','Është e vërtetë se All-llahu u dha dhurataë të madhe besimtarëve, kur ndër ta nga mesi ityre dërgoi të dërguarqë atyre t’u lexojë shpalljen e Tij, t’i pastrojë ata, t’ua mësojë Kur’anin dhe sheriatin, edhepse, më parë ata ishin krejtësisht të humbur.');
INSERT INTO "ahmeti" VALUES(3,165,'3:165','E kur juve u goditi një dështim (në Uhud) e që ju ia patët dhënëatë dyfish (armikut në bedër), thatë: “Prej nga kjo?” thuaj: “Ajo është nga vetë ju!” S’ka dyshim se All-llahu është i plotëfuqishëm për çdo send.');
INSERT INTO "ahmeti" VALUES(3,166,'3:166','Ajo që ju goditi juve ditën eballafaqimit të dy grupeve (besimtarë dhe idhujtarë), ishte me lejen e All-llahut, për t’u dalluar besimtarët e sinqert.');
INSERT INTO "ahmeti" VALUES(3,167,'3:167','Dhe, për t’u ditur ata që ishin hipokritë. E atyre u është thënë: “Ejani e luftoni në rrugën e All-llahut ose mbroni vetën!” E ata thanë: “Sikur të dinim se do të luftohej, ne do të vinim pas jush!” Në atë moment ata ishin më afër mosbesimit se besimit. Flisnin me gojët e tyre atë që uk e kishin në zemrat e tyre, po All-llahu edi mirë për atë që fshehin.');
INSERT INTO "ahmeti" VALUES(3,168,'3:168','Dhe ata që nuk luftuan dhe atyre të vetëve u thanë: “Sikur të na dëgjon neve (e të ktheheshin siç u kthyem ne), nuk do të mbyteshim!” thuaju: “Largoie pra vdekjen prej vetes suaj, nëse jeni të sigurt çka thoni?”');
INSERT INTO "ahmeti" VALUES(3,169,'3:169','Kurrsesi të mos mendoni se janë të vdekur ata që ranë dëshmorë në rrugën e All-llahut. P ërkundrazi, ata janë të gjallë duke u ushqyer te Zoti ityre.');
INSERT INTO "ahmeti" VALUES(3,170,'3:170','Janë të gëzuar me atë që u dha All-llahu nga të mirat e Tij, dhe atyre që kanë mbetur ende pa i bashkuar rradhëve të tyre, u marrin myzhde se për ta nuk ka as frikë dhe as që kanë pse të brengosen.');
INSERT INTO "ahmeti" VALUES(3,171,'3:171','Ata janë të gëzuar me begati e dhurata që ua dha All-llahu, e s’ka dyshim se All-llahu nuk ua humb shpërblimin besimtërëve.');
INSERT INTO "ahmeti" VALUES(3,172,'3:172','Të cilët edhe pasi i goditi plaga iu përgjegjën All-llahut dhe të dërgurit. E për ata prej tyre që bënë, mirë dhe u rujtën, është një shpërblimi madh.');
INSERT INTO "ahmeti" VALUES(3,173,'3:173','E atyre (shokëve të pejgamberit) që dikush u tha: “Populli (idhujtaët (është tubuar t’ju sulmoja, pra kini frië!” Ajo, vetëm u a shtoi edhe më shumë besimin e thanë e thanë: “Neve na mjafton që kemi All-llahu, Ai është mbrojtësi më i mirë!”.');
INSERT INTO "ahmeti" VALUES(3,174,'3:174','Dhe atë pa i gjetur kurrfarë e keqe fituan begati e mirësi të mëdha nga Allhu dhe e arritën kënaqsinë e Tij All-llahu është dhurues i madh.');
INSERT INTO "ahmeti" VALUES(3,175,'3:175','P atë (propagndë) e bëri vetëm shejtani që dëshironte me miqtë e vet (idhujtarët), t’ju friksojë, poju mos u frikësoni prej tyre, frikësomënu Mua, nëse jeni besimtar.');
INSERT INTO "ahmeti" VALUES(3,176,'3:176','E ty të mos të brengosin ata që përpiëen për mosbesim, ata kurrsesi nuk mund të dëmtojnë asgjë All-llahun. All-llahu me urtësinë e vet dëshiron që atyre të mos u takojë kurrfarë e mire në botën tjetër dhe ata kanë një dënim të madh.');
INSERT INTO "ahmeti" VALUES(3,177,'3:177','Ata që në vend të besimit pranuan kufrin, nuk i bëjnë asgjë dëm All-llahut, ata ipret një dënim i idhët.');
INSERT INTO "ahmeti" VALUES(3,178,'3:178','Të mosmendojnë ata që nuk besuan se afatin që u dhamë Ne atyre të jetojnë, është në dobi të tyre. Ne i lamë të jetojnë vetëm që shtojnë edhe më shumë mëkate, se ata ipret një dënmim nënçmues');
INSERT INTO "ahmeti" VALUES(3,179,'3:179','All-llahu nuk ju zblon juve të fshehtat (e të dini se ç’mbajnë në zemrët e tyre). Por All-llahu është Ai që nga të dërguarit e vet zgjedh atë që do (e i zbulon ndonjë të fsehtë). Prandaj, bsoni All-llahut dhe të dërguarit të Tij, e në i besofshit dhe nëse ruheni, atëherë ju keni një shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(3,180,'3:180','Ata, të cilët bëjnë koprrraci me atë nga të mirat e veta u dha All-llahu, të mos mendojnë kurrsesi se ajo është në dobi të tyre. Jo, ajo është në dëm të tyre. Ajo e mirë me çka bënë koprraci, në ditën e kijametit do t’u mbështillet në qafën e tyre. All-llahut i mbesin trashëgim qiejt dhe toka, All-llahu është i njohur mirë më atë që veproni.');
INSERT INTO "ahmeti" VALUES(3,181,'3:181','All-llahu ka dëgjuar fjalën e tyre që thanë: “All-llahu është i varfër e ne jemi të pasur!” Ne do të shënjojmë atë që thanë ata dhe që i mbytën pejgamberët pa kurrfarë faji, e do t’ju themi: “Vuane mundimin e djegjies së zjarrit.');
INSERT INTO "ahmeti" VALUES(3,182,'3:182','Kjo është meritë e duarve tuaja, se All-llahu nuk zullumqarë ndaj robëve.');
INSERT INTO "ahmeti" VALUES(3,183,'3:183','Ata janë që thanë: “All-llahu na ka porositur që të besojmë asgjë të dërguar derisa ai të na sjellë një kurban që do ta ngrënë zjarri. Thuaju: “Para meje juve u erdhën të dërguar me argumente dhe me atë që e thatë, e përse i mbytet ata, nëse thoni të vërtetën?”');
INSERT INTO "ahmeti" VALUES(3,184,'3:184','Po nëse ata ty të përgënjeshtrojnë, janë përgënjeshtruar edhe para teje të dërguar që u erdhën me argumente të prera, me shkrime qiellore dhe me libër të nditshëm.');
INSERT INTO "ahmeti" VALUES(3,185,'3:185','Secili njeri do të shijojë vdekjen, e shpërblimet tuaja u plotësohen diten e kijametit, e kush shmanget zjarrit e futet në Xhennet, ai ka arritur shpëtim, e jeta e kësaj bote nuk është tjetër pos një përjetim mashtrues.');
INSERT INTO "ahmeti" VALUES(3,186,'3:186','Ju patjetër do të sprovoheni si në pasurinë tuaj, si në vetën tuaj, madje do të dëgjoni ofendime të shumta prej atyre që u është libri para jush, e edhe prej idhujtarëve, po në qoftë se duroni dhe ruheni, ajo është gjëja më vendimtare.');
INSERT INTO "ahmeti" VALUES(3,187,'3:187','Dhe kur All-llahu mori zotimin nga ata që i pat dhënë libri: që githqysh t’ua publikoni atë njerëzve, e të mos e fshehni, por ata e lanë pas dore atë për pak send të kësaj jete; pra, bënë punë të keqe.');
INSERT INTO "ahmeti" VALUES(3,188,'3:188','Mos mendo se janë të shpëtuar ata që gëzohen për atë që bënë (e fshehen çështjen tënde) dhe dëshirojnë të jenë të lavdëruar për atë që nuk e punuan mos llogarit shpëtimpër ta (nu e thanë të vërtetën), ata kanë njëë dënim të dhëmbshëm.');
INSERT INTO "ahmeti" VALUES(3,189,'3:189','Vetëm All-llahut i takon sundimi i qiejve e i tokës dhe All-llahu është i gjithfuqishëm për çdo send.');
INSERT INTO "ahmeti" VALUES(3,190,'3:190','Në krijimin e qiejve e të tokër, në ndyshimin e natës dhe të ditës, ka argumente të qarta për ata që kanë arsye dhe intelekt.');
INSERT INTO "ahmeti" VALUES(3,191,'3:191','Për ata që All-llahun e përmendin me përkujtim kur janë në këmbë, kur janë ulur, kur janë të shtrirë dhe thellohen në mendimie rreth krijimit të qiejve e të tokës (duke thënë):: Zoti ynë, këtë nuk e krijove kot, i lartësuar qofsh, ruanaprej dënimit të zjarrit!');
INSERT INTO "ahmeti" VALUES(3,192,'3:192','Zoti ynë, atë ti e fute në zjarr, atë e ke poshtëruar; oër mizorët nuk ka ndihëtarë!');
INSERT INTO "ahmeti" VALUES(3,193,'3:193','Zoti ynë, ne dëgjuam një thirrës që ftonte për besim (e që thoshte): Të besoni Zotin tuaj! E ne besuam! Zoti ynë, na i falë mëkatet tona, na i mbulo të metat dhe pas vdekjes na bashko me të mirët!');
INSERT INTO "ahmeti" VALUES(3,194,'3:194','Zoti ynë, jepna atë që nëpërmjet të dërgurëve Tu, na e premtove dhe në ditën e kijametit mos na turpëro; vërtet Ti je Ai që nuk e shkel premtimin!”');
INSERT INTO "ahmeti" VALUES(3,195,'3:195','Zoti i tyre iu përgjegj lutjes së tyre (e tha): Unë nuk ia humb mundin asnjërit prej jush, mashkull qoftë apo femër. Ju jeni njëri nga tjetri por ata që u dëbuan prej shtëoive të tyre dhe emigruan, u munduan vetëm pse ishin në rrrugën Time, lftuan dhe u vranë, atyre patjetërdo t; ua shlyej mëkatet e tyre dhe do t’i veje në Xhennete në të cilët burojnë lumenj. Ai është shpërblim nga ana e All-llahut, se më i miri i shpërblimeve është te All-llahu.');
INSERT INTO "ahmeti" VALUES(3,196,'3:196','Të mos mashtrojë bredhja nëpër qytete e atyre që nuk besuan.');
INSERT INTO "ahmeti" VALUES(3,197,'3:197','Ajo është një kënaqësi e pakët, e pastaj vendi i tyre është Xhehennemi që është vend mjerimi.');
INSERT INTO "ahmeti" VALUES(3,198,'3:198','Por ata që ia patën frikën Zotit të tyre, ata i kanë Xhennetet nëpër të cilat rrjedhin lumenj dhe aty janë përgjithmonë. Ajo është pritje nga ana e All-llahut, e për të mirët më e dobishmja është ajo që është te All-llahu.');
INSERT INTO "ahmeti" VALUES(3,199,'3:199','Po edhe nga ithtarët e librit ka që besojnë All-llahun, e besojnë atë që u është shpallur juve, e besojnë atë që është shpallur atyre, janë të bindur ndaj All-llahut në shpallje nuk i shesin për pak sendtë kësaj jete. Të tillët kanë shpërblimin e vet te Zoti i tyre, e All-llahu është që llogarinë e bën shpejt.');
INSERT INTO "ahmeti" VALUES(3,200,'3:200','O ju besimtarë, bëni durim, bëhuni të qëndrueshëm kundër armikut, rrini të përgaditur dhe, që të shpëtoni, ruajuni dënimit të All-llahut.');
INSERT INTO "ahmeti" VALUES(4,1,'4:1','O ju njerëz! Kinie frikë Zotin tuaj që ju ka krijuar prej një veteje (njeriu) dhe nga ajo krijoi palën (shoqën) e saj, e prej atyre dyve u shtuan burrra shumë e gra. Dhe kinie frikë All-llahun që me emrin e Tij përbetoheni, ruajeni farefisin (akraballëkun), se All-llahu është mbikëqyrës mbi ju.');
INSERT INTO "ahmeti" VALUES(4,2,'4:2','Bonjakëve (jetimëve) jepini kur të rriten pasurinë e tyre, dhe mos e ndërroni të pastërtin (mallin tuaj që e keni halall) me të ndytin (me haramin) edhe mos ehani pasurinë e tyre (të përzier) me asurinë tuaj, pse ky është mëkat i madh.');
INSERT INTO "ahmeti" VALUES(4,3,'4:3','Në qoftë se frikoheni se nuk do të jeni të drejtë ndaj bonjakeve, atëherë martohuni me ato gra që ju pëlqejnë; me dy, me tri e me katra. E nëse i frikoheni padrejtësisë (ndaj tyre), atëherë vetëm me një, ose (martohuni) me ato që i keni nën pushtetin tuaj (robëreshat). Ky (përkufizim) është më afër që të mos gaboni.');
INSERT INTO "ahmeti" VALUES(4,4,'4:4','Grave jepnu dhuratën e kurëzimit së tyre (niqahin) më të mirë, e në qoftë se ato nga vullneti i vetju falin diçka nga ajo, atëherë hane atë hallall të mirë.');
INSERT INTO "ahmeti" VALUES(4,5,'4:5','Mendjelehtësve (të papjekurve) mos u jepni pasurinë tuaj që All-llahu e bëri për ju mekamje, e ata ushqeni nga ajo, veshni dhe u thoni fjalë të mira.');
INSERT INTO "ahmeti" VALUES(4,6,'4:6','Provoni bonjakët derisa të bëhen për martesë, e nëse vëreni te ta pjekuri, atherë drzonju atyre pasurinë e tyre. Mos e hani atë duke shkapërderdhur dhe duke u ngutur para setë rrite ata. Kush është i pasur le të ruhet (shfytëzimit të pasurisë të jetimëve), e kush është ivarfër, le të hajë me maturi. E kur t’ju dorëzoni atyre pasurinë e vet, dëshmoni atë (që ju dorëzoni). Mjafton që All-llahu është llogaritës.');
INSERT INTO "ahmeti" VALUES(4,7,'4:7','Meshkujve ju takon pjesë nga pasuria që e lënë prindërit e të afërmit (pas vdekjes), edhe femrave ju takon pjesë nga ajo që lënë prindërit e të afërmit, le të jetë pak ose shumë ajo që lënë, ju takon pjesë e coaktuar (nga Zoti).');
INSERT INTO "ahmeti" VALUES(4,8,'4:8','Kur të prezentojnë (në pjestimin e pasurisë që ka lënë i vdekuri) të afërmë, bonjak dhe varfnjak (që nuk janë pjestarë në trashigim), jepnu nga ajo (pasuri e lënë) dhe atyre thuanu fjalë të mira.');
INSERT INTO "ahmeti" VALUES(4,9,'4:9','Le të frikësohen ata (në prag të vdekjes) sikur të linin pas vete pasardhës të paaftë për të cilët kanë dro (si do të jetë gjendja e tyre) le t’i ruhen allahut (dënimit të Tij) dhe le t’ju thonë (jetimëve) fjalë prindore.');
INSERT INTO "ahmeti" VALUES(4,10,'4:10','Ata që e hanë pa të drejtë pasurinë e jetimëve, në të vërtetë ata hanë atë që mbush barkun e tyre zjarr dhe do të futen në zjarrin e Xhehennemit.');
INSERT INTO "ahmeti" VALUES(4,11,'4:11','All-llahu ju urdhëron për (çështen e trashigimit) fëmijët tuaj: për mashkullin hise sa për dy femra; nëse janë (trashëgimtare) vetëm femra, dy e më shumë, atyre ju takojnë dy të tretat e pasurisë që trashëgohet; nëse është një femër, asaj i takon gjysma; për prindërit, për seilinnga ata, lu takon e gjashta nga ajo që ka lënë (i vdekuri) nëse ka fëmijë; e nëoftëse (i vdekuri) nuk ka fëmijë e atë e trashëgojnë (vetëm) prindërit, atëherë nënës së tij i takon një e treta; në qoftë se ai (i vdekuri) ka vëllezër, nënës së tij itakon vetëm një e gjashta, (kjo e drejtë në trashëgim bëhet) pasi të kryhet (vasijjeti) që ka lënë dhe pasi të lahet borgji; ju nuk dini se kush është më afër dobisë suaj. (Ky përcaktim është) Urdhër nga All-llahu. Vërtet All-llahu është më i Dijshmi, më i Urti.');
INSERT INTO "ahmeti" VALUES(4,12,'4:12','Juve ju takon gjysma e asaj (pasurie) që e lënë gratë e tuaja, nëse ato nuk kanë fëmijë, por nëse ato kanë fëmijë, juve ju takon një e katërta nga ajo që lënë ato, pasi të kryhet testamenti i tyre dhe pasi të lahet borgji. Atyre (grave) ju takon një e katërta nga ajo që lini ju, nëse nuk kini fëmijë, por nëse keni fëmijë, atyre ju takon një e teta nga ajo që leni pas kryrjes së testamentit qëkeni përcaktuar ose borxhit. Në qoftë se (i vdekuri) është mashkull ose femër, e trashëgohet nga ndnjë ilargët (pse s’ka as prindër as fëmijë) po ka hjë vëlla ose një motër (nga nëna), atëherë secilit prej tyre u takon një e gjashta, e në qoftëse sejanë më shumë (se një vëlla ose se nj motër) ata janë pjesmarrës të barabartë në të tretën (e tërë pasurisë), pas testamentit të porositur ose borxhit, e duke mos dëmtuar (trashëguesit). Ky përcaktim është porositur prej All-llahu. All-llahu është i gjithëdijshmi, jo ingutshëm.');
INSERT INTO "ahmeti" VALUES(4,13,'4:13','këto janë (dispozita) të caktuara prej All-llahut (urdhërave) dhe të dërguarit të tij, atë e dërgon në Xhennete nën të cilin burojnë lumenj. Aty do të jenë përgjithmonë. E ky është shpëtim i madh.');
INSERT INTO "ahmeti" VALUES(4,14,'4:14','Kush e kundërshton All-llahu dhe të dërguarin e Tij, dhe i shkel dispozitat e Tij, atë e shtie në zjarr të përjetëshëm. E për të është një dënim i rëndë.');
INSERT INTO "ahmeti" VALUES(4,15,'4:15','Ato nga gratë tuaja që bëjnë imoralitet (zina), kërkoni që kundër tyre të dëshmojnë katër vetë prej jush (burra); nëse vërtetohet më dëshmitarë (imoraliteti), mbyllni ato në shtëpia derisa t’i marrë vdekja ose derisa All-llahu të përcaktojë ndonjë rrugë për to.');
INSERT INTO "ahmeti" VALUES(4,16,'4:16','Ataa të dy prej jush që bëjnë atë (imoralitet-zina), ndëshkoni; e në qoftë se ata pendohen dhe përmirësohen, atëherë hiqni dorë prej tyre, pse All-llahu pranon fort pendimin dhe mëshiron shumë.');
INSERT INTO "ahmeti" VALUES(4,17,'4:17','Pendim i pranueshëm te All-llahu është vetëm ai i atyre që r bëjnë të keqen me mosdije shpejt pendohen; të tillëve All-llahu ju pranon pendimin, se All-llahu është më i dijshmi, më i urti.');
INSERT INTO "ahmeti" VALUES(4,18,'4:18','Nuk është pendim (i pranueshëm) i atyre që vazhdimisht bëjnë punë të këqia dhe vetëm atëherë kur t’i vjen vdekja ndonjërit prej tyre, të thotë: “Unë tash u pendova!” e as i atyre që vdesin duke qenë jobesimtarë. Ndaj tyre kemi përënim të ashpër.');
INSERT INTO "ahmeti" VALUES(4,19,'4:19','O ju që besuat, nuk është lejuar për ju të trashëgoni gratë (e të vdekurve) në mënyrë të dhunshme, e as t’i shtëngoni për t’u marrë diçka nga ajo që ju keni dhënë atyre, përveç nëse ato bëjnë ndonjë imoralitet të hapur. çoni jetë të mirë me to. Nëse urreni ato, bëni durim që All-llahu të japë shumë të mira në një send që ju e urreni.');
INSERT INTO "ahmeti" VALUES(4,20,'4:20','Në qoftë se dëshironi të ndërroni (ta merrni) një grua në vend të një gruaje (të lëshuares) i keni pas dhënë shumë të madhe, mos merrni prej saj snjë send. A do të merrni atë pa të drejtë e në mënyrë mizore?');
INSERT INTO "ahmeti" VALUES(4,21,'4:21','Dhe si mund të merrni atë kur njëri-tjetri iu keni bashkuar (në një shtrat) dhe kur ato kanë marrë prej jush një besë të fortë?');
INSERT INTO "ahmeti" VALUES(4,22,'4:22','Mos u martoni me ato gra me të cilat qenë të martuar prindërit tuaj, me përjashtim të asaj që ka kaluar (para islamizmit) pse ajo ishte turpësi, përbuzje e traditë e shëmtuar.');
INSERT INTO "ahmeti" VALUES(4,23,'4:23','U janë ndaluar juve (të martoheni me): nënat tuaja, bijat tuaja, motrat tuaja, hallat tuaja, tezet tuaja, bijat tuaja, bijat e motës, nënat tuaja që ju kanë dhënë gji, motrat nga gjiri, nënëat e grave tuaja (vjehrrat) dhe vajzat që janë nën kujdesin tuaj e të lindura (prej tjeër babai) nga gratë tuaja me të cilat patët kontakt, e nëse nuk ken pasur kontakt me to (me gratë), atëherë s’ka pengesë (të martoheni me ato vajza), dhe (janë të ndaluara) gratë e bijve tuaj që janë të lindjes suaj (jo të bijëve të afoptuar), dhe të bashkoni (përnjeherë në një niqah) dy motra, përpos asaj që ka kaluar. Vërtetë, All-llahu falë shumë, është mëshirues i madh.');
INSERT INTO "ahmeti" VALUES(4,24,'4:24','(Nuk u lejohet të martoheni) Edhe me gra të martuara (që kanë burrat e nuk janë të lëshuara), përveç atyre që i keni futur në pushtetin tuaj (robëreshat e lufrtës). (Ky është) obligim mbi ju nga All-llahu. U janë lejuar, pos këtyre (që u përmendën), të tjerat që t’i merrni me pasurinë tuaj (me niqah) duke pasur për qëllim bashkëshorësi e jo prostitucion (kurvëni). Epër atë që përjetuat ju nga ato (gratë me të cilat patët kurorë), jepnu shpërblimin e tyre të caktuar se është obligim. E nuk ka pengesë për ju, pas përcaktimit (te niqahut), në atë që ju pëlqeni mes vete. All-llahu është i gjithëdijshëm, i urti.');
INSERT INTO "ahmeti" VALUES(4,25,'4:25','Dhe kush nuk ka prej jush mundësi materiale që të martohet me femra të lira besimtare, le të martohet me ato besimtare tuaja që i keni nën pushtetin tuaj (robëreshat). All-llahu di më së miri për besimin tuaj. Ju jeni nga njëri- tjetri (të një origjine0. Pra, martohuni me ato (robëresha) r me lejen e të zotërve të tyre dhe jepnu kurorën në mënyrë të drejtë, (zgjidhni) të ndershme e jo prostitute të hapta e të fshehta. E kur të jenë të martuara ato, nëse bëjnë ndonjë punë të turpshme, ndëshkimi kundër tyre është sa gjysma e atij të grave të lira (jo robëresha). Kjo (martesë me robëresha) është e lejuar për ata që i frikësohen imoralitetit, por të jeni të durueshëm, është më mirë për ju. All-llahu është mirrëdashës, mëshirëplotë.');
INSERT INTO "ahmeti" VALUES(4,26,'4:26','All-llahu dëshiron t’ju sqarojë çështjet et’ju drejtojë në rrugën e atyre që ishin para jush (pejgamberët) dhe t’ju pranojë pendimin. Alahu e di më së miri gjendjen e robërve dhe është më i urti.');
INSERT INTO "ahmeti" VALUES(4,27,'4:27','All-llahu dëshiron t’ju pranojë pendimin, e ata që ndjekin dëshirat e epsheve, dëshirojnë që ju të shmangeninë tërësi (nga rruga e drejtë).');
INSERT INTO "ahmeti" VALUES(4,28,'4:28','All-llahu dëshiron t’ju lehtësoj (dispozitat), e megjithatë njeriu është i paaftë (për t’ju përballuar epsheve).');
INSERT INTO "ahmeti" VALUES(4,29,'4:29','O ju që besuat, mos e hani mallin e njëri-tjetrit në mënyrë të palejuar, përpos tregtisë në të cilën keni pajtueshmëri mes vee, dhe mos mbytni veten tuaj (duke mbytur njëri-tjetrin). Vërtet, All-llahu është i mëshirueshëm për ju.');
INSERT INTO "ahmeti" VALUES(4,30,'4:30','Kush bën këtë (që ndaloi Zoti) qëllimisht dhe tejkalon kufijtë, Ne do ta hudhim atë në një zjarr të fortë. Dhe kjo është leht për All-llahun.');
INSERT INTO "ahmeti" VALUES(4,31,'4:31','Nëse lagoheni prej mëkateve të mëdha, të cilat u janë të ndalueshme, Ne ju shlyejmë mëkatet e vogla dhe ju fusim në një vnd të ndershëm.');
INSERT INTO "ahmeti" VALUES(4,32,'4:32','Mos lakmoni në atë, që All-llahu gradoi disa nga ju mbi disa të tjerë. Burrave ju takon hise nga ajo që fituan ata dhe grve gjithashtu ju takon hise bga ajo që fituan ato. All-llahut kërkoni nga të mirat e Tij. All-llahu është i dijshëm për çdo send.');
INSERT INTO "ahmeti" VALUES(4,33,'4:33','Ne u kemi caktuar për secilin trashëgimtarë, pjesën e pasurisë që e lënë prindërit ose të afërmit. E atyre që ju keni premtuar, jepnu pjesën ë tyre. All-llahu është dëshmues mbi çdo send.');
INSERT INTO "ahmeti" VALUES(4,34,'4:34','Burrat janë përgjegjës për gratë, ngase All-llahu ka graduar disa mbi disa të tjerët dhe ngase ata kanë shpenzuar nga pasuria e tyre. Prandaj, me atë që All-llahu i bëri të ruajtura, gratë e mira janë respektuese, janë besnike ndaj të fsheftës. E ato që keni dro kryelartësisë së tyre, këshilloni, madje largohuni nga shtrati (e më në fund), edhe rrahni (lahtë, nëse nuk ndikojnë këshillat as largimi), e nëse ju respektojnë, atëherë mos u sillni keq ndaj tyre. All-llahu është më i larti, më i madhi.');
INSERT INTO "ahmeti" VALUES(4,35,'4:35','Nëse i frikësohen përçarjes mes tyre (burrit e gruas), dërgoni një pari të drejtë nga familja e tij dhe një pari të drejtë ngafamilja e saj. Nëse ata të dy (ndërmjetësuesit) kanë për qëllim pajtimin, All-llahu ju mundëson afrimin mes tyre (burrit e gruas). All-llahu është i dijëshëm, është njohës i mirë.');
INSERT INTO "ahmeti" VALUES(4,36,'4:36','Adhurone All-llahun e mos i shoqëroni Atij asnjë send, sillnu mirë ndaj prindërve, ndaj të afërmve, ndaj jetimëve, ndaj të varfërve, ndaj fqiut të afërt, ndaj fqiut të largët, ndaj shokut pranë vetes, ndaj udhëtarit të largët dhe ndaj robërve. All-llahu nuk e do atë që është kryelartë dhe atë që lavdërohet.');
INSERT INTO "ahmeti" VALUES(4,37,'4:37','Ata që vetë janë koprracë dhe urdhërojnë njerëzit për koprraci, dhe e fshehin atë që ju ka dhënë All-llahu nga mirësitë e veta. Ne kemi përgatitur dënim të turpshëm për jobesimtarët;');
INSERT INTO "ahmeti" VALUES(4,38,'4:38','Edhe ata që e japin pasurinë e tyre për sy e faqe të botës, e nuk e besojnë All-llahun as ditën e mbramë. E ai që e k shok djallin, ai pra është shok i shëmtuar.');
INSERT INTO "ahmeti" VALUES(4,39,'4:39','Dhe çka do t’i gjente ata, po ta besonin All-llahun, ditën e gjykimit dhe të jepnin nga ajo që i dhuroi All-llahu? Po All-llahu di më së miri për ta.');
INSERT INTO "ahmeti" VALUES(4,40,'4:40','S’ka dyshim se All-llahu nuk bën pa drejtë as sa grimca, e nëse ajo vepër ështëe mirë, Ai e shumëfishon atë dhe Vetë Ai i jep shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(4,41,'4:41','Dhe si do të jetë gjendja e atyre (që nuk besuan), kur Ne do të sjelli dëshmitarë për çdo popull, e ty do të sjellim dëshmitarë mbi ata (që nuk besuan)?');
INSERT INTO "ahmeti" VALUES(4,42,'4:42','Atë ditë ata që nuk besuan dhe kundërshtuan të dërguarin, do të dëshironin (të varroseshin) të rrafshohej mbi ta toka ngase nuk mund t’i fshehin All-llahut asgjë.');
INSERT INTO "ahmeti" VALUES(4,43,'4:43','O ju që besuat, mos iu afroni namazit duke qenë të dehur, deristë dini se ç’flitni, e as duke qenë xhuhubë (të papastër) derisaaa të laheni, përpos kur jeni udhëtarë. Nëse jeni të sëmurë, jeni në ndonjë udhëtim, ose ndonjeri prej jush vjen nga ultësira (nevojtorja), ose keni takuar gratë, e nuk gjeni ujë, atëherë mësyne dheun dhe fërkoni me duart (tejemmum). All-llahu shlyen e falë mëkatet.');
INSERT INTO "ahmeti" VALUES(4,44,'4:44','A nuk i vure re (Muhammed) ata që iu pate dhënë pjesë nga libri (paria fetare jehude) se si e vazhdojnë rrugën e gabuar dhe dëshirompjnë që e edhe ju të humbni!?');
INSERT INTO "ahmeti" VALUES(4,45,'4:45','All-llahu di mësë miri për armiqtë tuaj. Mjafton që All-llahu është përkrahës i juaj dhe mjafton që All-llahu është ndihëtarër i juaji.');
INSERT INTO "ahmeti" VALUES(4,46,'4:46','Një palë bga jehudite është që ndryshojnë fjalët (e Zotit) nga vendet e veta, e (kur i thërret ti) ata thonë: “Degjuam (fjalën tënde) fhe kundërshtuam (thirrjen tënde), dhe thonë: “dëgjo mos dëgjofsh!” (dhe thonë): “Raina” (këto i thonë) duke përdredhur gjuhët e tyre dhe duke atakuar fenë. E sikur të thoshin ata: “Dëgjuam, respektuam, dëgjo dhe vështrona”, do të ishte më e drejtë, por për shkak të refuzimit të tyre, All-llahu i mallkoi, prandaj besojnë vetëm pak (besim gjysmak).');
INSERT INTO "ahmeti" VALUES(4,47,'4:47','O ju që u është dhënë libri, besone atë që e zbritëm (Kur’anin) e që është vërtetues i atij që e keni (Tevratit), para se të shlyjmë fytyra (t’ua fshijmë shqisat) dhe t’i rrotullojmë ose t’i mallkojmë ata si i mallkuam ata të së shtunës. Urdhëri i All-llahut është i kryer.');
INSERT INTO "ahmeti" VALUES(4,48,'4:48','S’ka dyshim se All-llahu nuk falë (mëkatin) t’i përshkruhet Atij shok (idhujtarinë), e përpos këtij (mëkati) i falë kujt të do. Kush përshkruan All-llahut shok, ai ka trilluar një mëkat të madh.');
INSERT INTO "ahmeti" VALUES(4,49,'4:49','A nuk ke kuptuar për ata që vlerësojnë vetveten? Jo, All-llahu vlerëson atë që do dhe nuk bën padrejtë asnjë fije.');
INSERT INTO "ahmeti" VALUES(4,50,'4:50','Shiko se si shpifin kundër All-llahut! Mjafton kjo (shpifje) si mëkat i hapët.');
INSERT INTO "ahmeti" VALUES(4,51,'4:51','A nuk u vure re atyre që iy është dhënë pjesë nga libri? Besojnë idhuj e djallëzi dhe për ata që nuk besuan thonë: “Këta janë në rrugë të drejtë, se ata që besuan!”');
INSERT INTO "ahmeti" VALUES(4,52,'4:52','Ata janë që All-llahu i mallkoi, e atë që e mallkon All-llahu, për të nuk ka ndihmëtarë.');
INSERT INTO "ahmeti" VALUES(4,53,'4:53','A mos kanë ata ndonjë pjesë të pshtetit? Ata nuk do t’u jepnin njerëzve as sa grimca.');
INSERT INTO "ahmeti" VALUES(4,54,'4:54','A u kanë zili atyre njerëzve për atë që All-llahu u fha nga mirësitë e Tij? Ne u patëm dhënë pasardhësve të Ibrahimit librin e sheriatin dhe u patëm dhënë atyre pushtet të madh.');
INSERT INTO "ahmeti" VALUES(4,55,'4:55','Po disa prej tyre i besuan atij (Muhammedit), e disa e refuzuan. Për ta mjafton zjarri i Xhennemit.');
INSERT INTO "ahmeti" VALUES(4,56,'4:56','Është e vërtetë se ata që mohuan argumentet Tona, do t’i hudim në zjarr. Sa herë që u digjen lëkurat e tyre, Ne ndërrojmë lëkura të tjera që të shijojnë dënimin. All-llahu është i plotëfuqishëm, i drejtë.');
INSERT INTO "ahmeti" VALUES(4,57,'4:57','ata që besuan dhe bënë vepra të mira, Ne do t’i fusim në Xhennet nën të cilin burojnë lumenj, ku do te jenë përgjithmonë. Aty kanë edhe bashkëshorte të pastra e hije të mrekullueshme.');
INSERT INTO "ahmeti" VALUES(4,58,'4:58','All-llahu ju urdhëron që t’u jepni amanetin të zotëve të tyre dhe kur të gjykoni, ju urdhëron të gjykoni me të drejt mes njerëzve. Sa e mirë është kjo që ju këshillon. All-llahu dëgjon dhe sheh si veproni.');
INSERT INTO "ahmeti" VALUES(4,59,'4:59','O ju që besuat, nënshtrojuni All-llahut, dhe nënshtrojuni të dërguarit dhe përgjegjësve nga ju. Nëse nuk pajtoheni për ndonjë çështje, drejtojuni All-llahut dhe të Dërguarit po qe se i besoni All-llahut dhe ditës së fundit. Kjo është më e dobishmja dhe përfundimi më i mirë.');
INSERT INTO "ahmeti" VALUES(4,60,'4:60','A i vure re ata që mendojnë se besuan atë që të zbriti ty, dhe atë që zbriti para teje, se si dëshirojnë mes tyre të gjykojë djalli, e duke qenë se janë urdhëruar që të mos e besojnë atë. E djalli dëshiron t’i humbë në pafundësi.');
INSERT INTO "ahmeti" VALUES(4,61,'4:61','Kur u thuhet atyre: “Ejani (për të gjykuar) te ajo që e zbriti All-llahu dhe te i dërguari” i sheh se si dyfytyrëshit ta kthejnë shpinën.');
INSERT INTO "ahmeti" VALUES(4,62,'4:62','E qysh do të jetë (gjendja e tyre) kur t’i godasë ata ndonjë e keqe, e shkaktuar nga vetë duart e tyre, e pastaj vinë te ti (për t’u arsyetuar) dhe betohen në All-llahun: “Ne nuk patëm tjetër qëllim, vetëm afrim e pajtim” (e jo refuzimin e gjykimit tënd).');
INSERT INTO "ahmeti" VALUES(4,63,'4:63','Ata janë për të cilët e di All-llahu çka mbajnë në zemrat e tyre. Po ti hiqu tyre, tërhiqu vërejtjen (për hipokrizi) dhe thuaju fjalë që lënë përshtypje në veten e tyre.');
INSERT INTO "ahmeti" VALUES(4,64,'4:64','Ne nuk dërguam asnjë të dërguar vetëm që me urdhërin e All-llahut t’i bëhet respekt (nga njerëzit) atij. E sikur të vinin ata te ti, pasi që ta kenëdëmtuar veten e tyre (nuk kanë pranuar gjykimin tënd), e të kërkonin ata vetë ndjesë te All-llahu, e edhe i dërguari të kërkojë ndjesë për ta, ata do të kuptonin se All-llahu pranon pendimin dhe është mëshirues.');
INSERT INTO "ahmeti" VALUES(4,65,'4:65','Për Zotin tënd jo, ata nuk janë besimtarë (të asaj që të zbriti ty as të asaj para teje) derisa të mos zgjedhin ty për të gjykuar në atë konflikt mes tyre, e pastaj (pas gjykimit tënd) të mos ndiejnë pakënaqësi nga gjykimi yt dhe (derisa) të mos binden sinqerisht.');
INSERT INTO "ahmeti" VALUES(4,66,'4:66','Sikur t’i kishim obliguar ata: mbytni vetvetn tuaj, atë (obligim) nuk do ta zbatonin, me përjashtim të një pakice prej tyre. Po sikur të zbatonin atë që këshilloheshin do të ishte më mirë për ta dhe do t’i përforconte më shumë.');
INSERT INTO "ahmeti" VALUES(4,67,'4:67','Dhe atëherrë do t’u jepnim atyre nga ana Jonë shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(4,68,'4:68','Dhe do t’i udhëzonim në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(4,69,'4:69','E kushdo që i bindet All-llahut dhe të dërguarit, të tillët do të jenë së bashku me ata që All-llahu i shpërbleu: (me) pejgamberët, besnikët e dalluar, dëshmorët dhe me të mirët. Sa shokë të mirë janë ata!');
INSERT INTO "ahmeti" VALUES(4,70,'4:70','Ky shpërblim i madh është prej All-llahut. Mjafton që All-llahu di mësë miri.');
INSERT INTO "ahmeti" VALUES(4,71,'4:71','O ju që besuat, rrini të përgatitur (të armatosur) dhe dilni (për në luftë) grup pas grupi apo të gjithë së bashku.');
INSERT INTO "ahmeti" VALUES(4,72,'4:72','Ka disa prej jush që zvaritet (nuk del në luftë). E nëse ju godet juve ndonjë e papritur thotë: “Zoti më favorizoi që nuk isha i pranishëm bashkë me ta” (në luftë).');
INSERT INTO "ahmeti" VALUES(4,73,'4:73','Po nëse ju takon juve ndonjë e mirë nga All-llahu, ai (munafiku) me siguri do të thoshte: si mos të kishte ekzistuar mesh jush dhe mes tij ndonjë njoftëssi, për fat, të kisha qenë së bashku me ta e të kisha arritur një fitim të madh.');
INSERT INTO "ahmeti" VALUES(4,74,'4:74','Le të luftojnë në rrugën e Allaut, ata që e japin jetën e kësaj bote për tjetrën. Kush lufton në rrugën e All-llahut e mbytet (bie dëshmor) ose triumfon, Ne do t’i japim atij shpërblimtë madh.');
INSERT INTO "ahmeti" VALUES(4,75,'4:75','ç’keni që nuk luftoni për Zotin dhe për (t’i shpëtuar) të paaftit: nga burrat, nga gratë e nga fëmijët, të cilët luten: “Zoti ynë! nxirrna nga ky fshat (Meka), banorët e të cilit janë mizorë. Jepna nga ana Jote shpëtim e ndihmë!”');
INSERT INTO "ahmeti" VALUES(4,76,'4:76','Ata që besuan luftojnë në rrugë të All-llahut, e ata që nuk besuan luftojnë në rrugë të djallëzuar. Luftoni pra miqtë e djallit, s’ka dyshim se intriga e është e dobët.');
INSERT INTO "ahmeti" VALUES(4,77,'4:77','A nuk i vure re ata të cilëve iu pate thënë: “ndalni duart tuaja jepne zeqatin!” E kur iu bë atyre obligim lufta, që një grup prej tyre u frikësoheshin njerëzvesiç është frika ndaj All-llahut, e edhe më fort. Thonin: “Zoti ynë, pse na e bëre obligim luftën? Sikur të na e kishe shtyrë deri nënjë afat të afërt!” Thuaju: “Përjetimi ikësaj bote është pak, e për atë që është i devotshëm, bota tjetër është shumë më e dobishme. Nuk do t’ju bëhet pa drejtë as sa fija.');
INSERT INTO "ahmeti" VALUES(4,78,'4:78','Kudo që të jeni vdekja do t’ju kapë, po edhe në qoftë se jeni në pallate të fortifikuara. E nëse i qëllon ata (munafikët) ndonjë e mirë, thonë: “Kjo është nga All-llahu”. E nëse i qëllon ata ndonjë e keqe: “Kjo është nga ti (Muhammed)”. Thuaju: “Të gjitha janë nga All-llahu!” ç’është me këtë popull që nuk kupton gati asnjë send?');
INSERT INTO "ahmeti" VALUES(4,79,'4:79','çfarëdo e mire që të vjen është nga All-llahu, e çka të ndodhë nga ndonjë e keqe është nga vetë ti. Ne të dërguam ty Pejgamber për mbarë botën. Mjafton që All-llahu është dëshmues për këtë.');
INSERT INTO "ahmeti" VALUES(4,80,'4:80','Kush i bindet Pejgamberit, ai i është bindur All-llahut, e kush e refuzon, Ne nuk të dërguam roje kundër tyre.');
INSERT INTO "ahmeti" VALUES(4,81,'4:81','Dhe thonë: “jemi” Të bindur”. E kur largohen prej teje, një grup prej tyre (natën) planifikon diçka tjetër nga ajo që u thua ti. All-llahu shënon atë që e ndryshojnë. Mos u vë veshin atyre e mbështetu All-llahut.');
INSERT INTO "ahmeti" VALUES(4,82,'4:82','A nuk e përfillin ata (me vëmndje) Kur’anin? Sikur të ishte prej dikut tjetër, përvrç prej All-llahut, do të gjenin në te shumë kundërthënie.');
INSERT INTO "ahmeti" VALUES(4,83,'4:83','Madje kur u vjen atyre (munafikëve) ndonjë lajm qetësues (kur fitojnë myslimanët) ose shqetësues (kur dështojnë), ata e përhapin atë, sikurt’ia linin atë (përhapjen lajmit) Pejgamberit dhe përgjefgjësve të tyre, ata do dinin të nxjerrin përfundime (se si do të duhej shpallur). Por sikur të mos ishte dhuntia e All-llahut ndaj jush dhe mëshira e Tij, u, pos një pakice, do të ndiqnit rrugën e djallit.');
INSERT INTO "ahmeti" VALUES(4,84,'4:84','Lufto në rrugën e All-llahut1 Nuk obligohet kush pos teje. Ngacmoi besimtarët (për luftë), allahu do të zmbrapsë fuqinë e të pabesëve. All-llahu është më fuqiforti, më ndëshkuesi.');
INSERT INTO "ahmeti" VALUES(4,85,'4:85','Kush ofrin ndihmë të mirë, do të ketë pjesë nga ajo, dhe kush ofron ndihmë të keqe do të ngarkohet me të. All-llahu është i plotfuqishëm ndaj çdo sendi.');
INSERT INTO "ahmeti" VALUES(4,86,'4:86','Kush përshëndeteni me ndinjë përshëndetje, ju ktheni përshëhdetje edhe më të mirë, ose kthenie ashtu. All-llahu llogarit për çdo send.');
INSERT INTO "ahmeti" VALUES(4,87,'4:87','Për All-llahun, që s’ka tjetër pos Tij, pa tjetër Ai do t’ju tubojë në ditën e gjykimit për të cilën gjë nuk ka dyshim. E mund të jetë më i vërtetë se All-llahu në thënie?');
INSERT INTO "ahmeti" VALUES(4,88,'4:88','E çka është me ju që në çështjen e munafikëve jeni dy grupe; derisa për atë që bën, All-llahu i përmbysi ata? a mos doni ta vini në rrugë të drejtë atë që All-llahu e ka humbur? Për atë që All-llahu e ka humbur, nuk do të gjeni rrugë!');
INSERT INTO "ahmeti" VALUES(4,89,'4:89','Ata dëshirojnë që edhe ju të mos besoni sikurse nuk besuan vetë dhe bëheni të njejtë. MOs izininipër miq prej yre deisa ata të shpëngulen (si ju) për hirë të All-llahut, enëse refuzojnë, ataherë kudo që t’i gjeni, kapni dhe mbytni dhe prej tyre mos zini as mik as ndihmës.');
INSERT INTO "ahmeti" VALUES(4,90,'4:90','Përpos atyre që srtehohen te një popull, me të cilin keni marrveshje, e ju u vjen rëndë të luftojnë kundër jush ose kundër popullit të vet. Skur All-llahu të donte do t’ju jepte atyre fuqi e pushtet mbi ju dhe do t’ju luftonin. Në qoftëse ata tërhqen prej jush, nuk ju luftojnë dhe ju ofrojnë paqe, atëheë All-llahu nuk ju lejon rrugë (rrugë) kundër tyre.');
INSERT INTO "ahmeti" VALUES(4,91,'4:91','Ju do të hasni në të tjerë, që duan të sigurohen te ju (duke u paraqitur si bsimtarë) dhe të sigurohen te populli i vet (si jobesimtarë). E, saherë që thirren, (kundër jush) i përgjigjen thirrëjes. Në qoftë se nuk largohen prej jush, nuk ju ofrojnë paqe dhe nuk heqin dorë nga lufta kundër jush, atherë kapeni dhe mbytni kudo që t’i takoni. Kundër tyre u kemi dhënë fakte të qarta.');
INSERT INTO "ahmeti" VALUES(4,92,'4:92','Asnjë besimtarëi nuk i është i lejuar të mbysë besmitarin tjetër, përpos gabimisht. E kush e mbytë gabimishht një besimtarë, ai është i obliguar ta lirojë nj rob besimtarë dhe shpagimin (e gjakut). Në se ia falin (familja gjakun). Në qoftë se ai (i mbyturi gabimisht) është besimtarë, por i takon popullit që është armik ijuaji, atëherë është obligil vetëm lirimi injë robi besimtar. Në qoftëse se ai (i mbyturi) është nga ai popull që keni marrveshjen me të, atëherë është obligim shpagmi i (gjakut) që i dorëzohet familjes së tij dhe lirimi injë robi besimtar. E kush nuk ka mundësi (të lirojë një rob), letë agjëroj dy muaj rresht, si pendim (i pranuar) ndaj All-llahut. All-llahu është më idijshmi, ligjdhënësi më i drejtë.');
INSERT INTO "ahmeti" VALUES(4,93,'4:93','Kush e mbytë një besimtarë me qëllim, dënimi itij është Xhehnemi, në të cilin do të jetë përgjithmonë. All-llahu është i hidhëruar ndaj tij, e ka mallkuar dhe ika përgaditur dënim të madh.');
INSERT INTO "ahmeti" VALUES(4,94,'4:94','O ju që besuat, kur marshoni në rrugën e All-llahut (për në luftë), të jeni të matur (të mos nguteni), e mos i thoni atij që ju shpreh selamin (besimin): “Nuk je besimtarë!”, duke kërkuar me të mjet (mall0 të kësaj bote, pse te All-llahu janë begatitë e mëdha. Ashtu (jo besimtarë) ishit edhe ju më parë, e All-llahu ju dhuroi (besimin), pra sqaroni mirë! All-llahu e di në hollësi çka veproni.');
INSERT INTO "ahmeti" VALUES(4,95,'4:95','Nuk janë të barabartë prej besimtarëve ata që ndejtën (nuk luftuan) dhe ata që me pasurnë dhe me jetën e tyre luftuan në rrugën e All-llahut, me përjashtim të atyre që ishin të penguar pa vullnetin e vet. Ata që luftuan me pasurinë dhe jetën e tyre, All-llahu igradoi në një shkallë më të lartë mbi ata që ndejtën (me arsye). Por të gjithëve All-llahu u premtoi shpërblim, ndërsa ata që ndejtën (pa arsye) All-llahu gradoi luftëtarët me një shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(4,96,'4:96','Vende të larta me falje gabimesh e mëshirë nga Ai. All-llahu është që falë shumë dhe mëshiron.');
INSERT INTO "ahmeti" VALUES(4,97,'4:97','Engjëjt që ua morën shpirtine atyre që ishin mizorë të vetëvetës ju thanë: “Në çka ushit ju? - Ata thanë: “Ne ishim të paftë në atë tokë!” (engjujt ju thanë: “A nuk ishtë e gjërë toka e All-llahut e të migroni në të?” (e të praktikonit lirisht fenë e Zotit). Vendi ityre është Xhehennemi dhesa vend i keq është ai!');
INSERT INTO "ahmeti" VALUES(4,98,'4:98','Përveç atyre që ishin të paaftë nga burrat, grat e fëmijët që nuk kishin mundësi të gjenin, as mënyrë, as rrugë (për shpërngulje).');
INSERT INTO "ahmeti" VALUES(4,99,'4:99','Atyre do t’ua falë All-llahu. All-llahu është që shlyen e falë shumë (gabime).');
INSERT INTO "ahmeti" VALUES(4,100,'4:100','Kush shpërngulet për hirë të (të fesë) All-llahut, ai gjen mundësi të madhe dh begati në tokë. E kush del prej shtëpisë si migrues te All-llahu dhe i dërguari i Tij, dhe e zë vdekja (në rrugë), te All-llahu është mëshirues, mbulon të metat.');
INSERT INTO "ahmeti" VALUES(4,101,'4:101','Kur tëjeni nëudhëtim e sipër, nuk është mëkat për ju të shkurtoni namazin, nëse frigoheni se jobesimtarët dot’ju sjellin ndonjë të keqe. Jobesmtarët janë armiq tuaj të hapët.');
INSERT INTO "ahmeti" VALUES(4,102,'4:102','Kur të jesh ti (Muhammed) bashkë me ta dhe ju falë namazin, një grup prej tyre, duke i bartur armët, le të vijë e le të falet me ty (grupi tjetër në roje), ekur të bien në sexhde (të kryejnë një reqatë), këta le të të qëndrojnë mbrapa jush (në roje) e le të vijë grupi tjetër, që nuk është falur, e të falet me ty dhe le t’i bartin armët dhe të jenë në gjendje gadishmërie. Ata që nuk besojnë, e dëshirojnë moskujdesin tuaj ndaj armëve e mjeteve që t’iu vërsulen njëherë me të gjitha fuqitë. Nëse jeni të lodhur nga ndonjë shi ose jeni të sëmurë, nuk është mëkat të mos ibartni armët, por mbani gadishmërinë tuaj. All-llahu ka përgaditë dënim nënqmues për jobesimarët.');
INSERT INTO "ahmeti" VALUES(4,103,'4:103','Kur të kryeni namazin (e frikës) përmendeni All-llahun, kur jeni në këmbë, ulur, të mbështetur. E kur të qetësoheni (nga lufta), atëherë faleni namazin në mënyrë të rregullt, pse namazi është obligim (për kohë) i caktuar për besimtarët.');
INSERT INTO "ahmeti" VALUES(4,104,'4:104','Mos u tregoni të dobët në ndjekjen e armikut, pse nëse ndieni dhëmbje edhe ata ndjejnë dhëmbje sikurse ju, e ju shpresoni nga All-llahu çka ata nuk shpresjnë. All-llahu është i Dijshëm, Ligjdhënës i Matur.');
INSERT INTO "ahmeti" VALUES(4,105,'4:105','Ne të zbritëm ty (Muhammed) librin (Kur’anin) me plot të vërteta, që të gjykosh mes nerzëve me atë që të mësoi All-llahu. Mos u ngatërro për tradhtarët (mos udil në ndihmë).');
INSERT INTO "ahmeti" VALUES(4,106,'4:106','Kërko falje (për këtë) All-llahut. All-llahu falë, mëshiron.');
INSERT INTO "ahmeti" VALUES(4,107,'4:107','Mos polemizo për ata që tradhtojnë vetveten. All-llahu nuk e do atë që është gënjeshtar e mëkatar.');
INSERT INTO "ahmeti" VALUES(4,108,'4:108','Ata fshihen (turpërohen) prej njreëzve, e nuk turëprohen prej All-llahut, e Ai është me ta edhe kur planifikojnë fshehtas thënie me të cilat nuk është ikënaqur Ai. All-llahu nuk mund t’i shpëtojnë veprimet etyre.');
INSERT INTO "ahmeti" VALUES(4,109,'4:109','Ja, ju jeni ata që i mbrojtët (gënjeshtarët) në ejtën e kësaj bote, kush do të polemizojë me All-llahun për ditën e gjykimit, apo kush do ti marrë në mbrojtje?');
INSERT INTO "ahmeti" VALUES(4,110,'4:110','Kush bën ndonjë të keqe apo ngarkon veten, pastaj kërkon falje te All-llahu, ai e gjen All-llahun falës dhe mëshirues.');
INSERT INTO "ahmeti" VALUES(4,111,'4:111','Kush bën ndonjë mëkat, ai ka ngarkuar vetveten. All-llahu idi hollësitë, është gjykatës i drejtë.');
INSERT INTO "ahmeti" VALUES(4,112,'4:112','Kush bën ndonjë gabim ose mëkat, e pastaj atë ia hudh një të pafajshmit, ai ngarkohet me një shpifje e me një mëkat të hapët.');
INSERT INTO "ahmeti" VALUES(4,113,'4:113','E sikur të mos ishte dhuntia e All-llahut dhe mëshira e Tij ndaj teje, një grup prej tyre do të humbte ty, por ata nuk humbin tjetër, poos vetes së tyre. Ty nuk mund të dëmtojë asgjë. All-llahu të shpalli ty, librin dhe sheriatin, të mësoi çka nuk dije. Dhuntia e All-llahut ndaj teje është shumë e madhe.');
INSERT INTO "ahmeti" VALUES(4,114,'4:114','Në shumë biseda të tyre të fshehta nuk ka kurrfarë dobie, përveç (bisedës) kush këshillon për lëmoshë, për ndonjë të mirë ose pajtim mes njerëzve. e kush i bën këto duke pasur për qëllim vetëm kënaqësinë e All-llahut, Ne do ti japim më vonë (në botën tjetër) shpërbllim të madh.');
INSERT INTO "ahmeti" VALUES(4,115,'4:115','Kush ikundërvihet të dërguarit, pasi që iështë bërë eqatë e vërteta dhe ndjek rrugë tjetër nga ajo e e besimtarëve, Ne e lëmë në atë që ka zgjedhur (në dynja) e e fusim në Xhehennem. Përfundim i keq është ai.');
INSERT INTO "ahmeti" VALUES(4,116,'4:116','Është e vërtetë se All-llahu nuk falë (mëkatin) t’i bëhet Atij shok, pos këtij (mëkati), të tjerat i falë atij që dëshiron. Ai që i përshkruan shok All-llahut, ai ka humbur dhe bërë një largim të madh (prej të vërtetës).');
INSERT INTO "ahmeti" VALUES(4,117,'4:117','Ata nuk lusin tjetër pos Tij vetëm (emra idhujsh) femra dhe nuk tjetër pos djallit plot sherr.');
INSERT INTO "ahmeti" VALUES(4,118,'4:118','Atë e mallkoi All-llahu. Ai ka thënë: “Unë do të përfitoj një pjesë të caktuar të robërve të tu”.');
INSERT INTO "ahmeti" VALUES(4,119,'4:119','Do t’i (shmang nga e vërteta), do t’i bëjë të shpresojnë në gjëra të kota; do t’i urdhëroj dhe ata dotë shqyejnë veshët e bagëtive; do ti urdhëroj dhe ata do të shëmtojnë krijesat e All-llahut. E kush bën mik djallin, e jo All-llahun, ai ka dështuar sheshazi.');
INSERT INTO "ahmeti" VALUES(4,120,'4:120','Ai u premton atyre dhe i bën të shpresojnë, por djalli nuk premton tjetër vetëm se mashtrim.');
INSERT INTO "ahmeti" VALUES(4,121,'4:121','Të tillët (që ndjekin djallin) kanë vendin në Xhehennem dh e prej tij nuk kanë për të ikur (shpëtim).');
INSERT INTO "ahmeti" VALUES(4,122,'4:122','E ata që besuan dhe bënë vepra të mira, Ne do t’i vendosim në Xhennete, ku rrjedhin lumenj e ku do jenë përgjithmonë. Premtimi i saktë i All-llahut. E kush mund të jetë më saktë në fjalë se All-llahu?');
INSERT INTO "ahmeti" VALUES(4,123,'4:123','Nuk është (shpëtim) sipas shpresave tuaja as sipas shpresave të ithtarëve të librit. Kush bën ndonjë nga punët e mira, qoftë mashkull ose femër duke qenë besimtarë, të tillët hyjnë në Xhennet dhe nuk u bëhet farë padrejtësie.');
INSERT INTO "ahmeti" VALUES(4,124,'4:124','Kush bën ndonjë nga punët e mira, qoftë mashkull ose femër duke qrnë besimtarë, të tillët hyjnë në Xhennet dhe nuk u bëhet farë padrejtësie.');
INSERT INTO "ahmeti" VALUES(4,125,'4:125','Kush ka fe më të mirë se ai i që sinqerisht i është bindur All-llahut dhe, duke qenë bamirës, e ndjek fenë e drejtë të Ibrahimit? All-llahu e zgjodhi Ibrahimin të dashurin më të ngushtë.');
INSERT INTO "ahmeti" VALUES(4,126,'4:126','Vetëm të All-llahut janë ç’ka në qiej dhe ç’ka në tokë. All-llahu ka përfshirë çdo send.');
INSERT INTO "ahmeti" VALUES(4,127,'4:127','Kërkojnë prej teje (Muhammed) përgjigje - fetva për (çështjen) gratë. Thuaju: “All-llahu ju sqaron nga libri (Kur’ ani) përkitazi me gratë jetime, me të cilat lakmoni të martoheni me to, e nuk u jep atë që është caktuar (niqah ose miras), (ju sqaron) lidhur me të paaftit (fëmijët jetimë) dhe që të mbani drejtësi ndaj jetimëve! Dhe, çfarëdo të mire që punoni, All-llahu di shumë për të.');
INSERT INTO "ahmeti" VALUES(4,128,'4:128','Nëse ndonjë grua i frikësohet largimit pse ftohjes së burrit të vet ndaj asaj, atëherë nuk gabojnë po qe se bëjnë mes vete mbarim (pajtim). Pajtimi është më i dobishmi. Megjithëkëtë, koprracia është epranishme ndër njerëz. Por, nëse silleni mirë dhe ruheni, s’ka dyshim se All-llahu hollësisht me gjithçka veproni.');
INSERT INTO "ahmeti" VALUES(4,129,'4:129','Ju kurrsesi nuk do të mund ta mbani drejtësinë mes grave edhe nëse përpiqeni, pra mos anoni plotësisht nga ndonjëra e të lini tjetrën si të varur. E nëse përmirësoheni dhe ruheni se (padrejtësisë), All-llahu ju dhe ju mëshiron.');
INSERT INTO "ahmeti" VALUES(4,130,'4:130','Por nëse ndahen prej njëri tjetrit, All-llahu begaton me mirësinë e Tij secilin prej tyre. All-llahu është bujar i madh, i plotdijshëm.');
INSERT INTO "ahmeti" VALUES(4,131,'4:131','Vetëm e All-llahut është ç’ka në qiej e ç’ka në tokë. Ne patëm sugjeruar atyre që iu pat dhënë libri para jush, e edhe juve që të keni frikë nga All-llahu. Po në mos besofshit, (dine) e All-llahut është ç‘ka në qiej e ç’ka ka në tokë. All-llahu është i panevojë e i falënderuar.');
INSERT INTO "ahmeti" VALUES(4,132,'4:132','Të All-llahut janë gjithë ç’ka në qiej e ç’ka në tokë. Mjafton që All-llahu është ipërkujdesshëm.');
INSERT INTO "ahmeti" VALUES(4,133,'4:133','Nëse Aëshiron, ju zhduk juve or njerëz, e sjell të tjerë. Për këtë është i plotfuqishëm.');
INSERT INTO "ahmeti" VALUES(4,134,'4:134','Kush (me punënë e vet) dëshiron shpërblimin e kësaj bote, (ai gabon), shpëëëërblmi i kësaj dhe i asaj botës tjetër është te All-llahu, e All-llahu dëgjon, sheh.');
INSERT INTO "ahmeti" VALUES(4,135,'4:135','O ju që besuat, vazhdimisht të jeni dëshmues të drejtë për horë të All-llahut edhe nëse është kundër (interesit) vetvetes suaj, kundër prindërve ose kundër të afërmve, le të jetë ai (për të cilin dëshmohet) pasanik ose varfnjak, pse All-llahu di më mmirë për ta. mos ndiqni pra emocionin e t’i shmangeni drejtësisë. Nëse shtrëmbëroni ose tërhiqeni, All-llahu plotësisht di ç’ka punoni.');
INSERT INTO "ahmeti" VALUES(4,136,'4:136','O ju që besuat, besoni vazhdimisht All-llahun, të dërguarit të Tij, librit që gradualsht ia shpalli të dërguarit të Tij dhe librit që e pat zbritur më parë. Kush nuk i beson All-llahut, engjëjve të Tij, librave të Tij, të dëguarve të Tij dhe botës tjetër, ai ka humbur tepër larg.');
INSERT INTO "ahmeti" VALUES(4,137,'4:137','Vërtet, ata që besuan e pastaj tradhëtuan, përsëri besuane pastaj tradhtuan dhe e shtuan mosbesimin, All-llahu nuk u falë atyre as nuk i udhëzon në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(4,138,'4:138','Ti (Muhammed) lëjmëroj munafikët se me të vërtetë ata kanë një dënim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(4,139,'4:139','Janë ata që, përkundër besimtarëve, i miqësojnë jobesimtarët. Vallë, a mos kërkojnë fuqi te ta? S’ka dyshim, e tërë fuqia i takon All-llahut.');
INSERT INTO "ahmeti" VALUES(4,140,'4:140','Juve u është shpallur në librin (Kur’anin) që, kur të dëgjoni se po mohohet Kur’ani i All-llahut dhe po bëhet tallje me të, mos rrini me ata derisa të mos hyjnë në bisedë tjetër. Përndyshe, ju do të jeni si ata. All-llahu do t’i tubojë tradhëtarët dhe jobesimtërët që tq gjithë në Xhehennem.');
INSERT INTO "ahmeti" VALUES(4,141,'4:141','Ata janë që presin se ç’po ju ngjanë juve. Nëse me ndihmën e All-llahut ngadhënjeni, ata thonë: “A nuk ishim me ju?” E nëse jobesimtarëve u takon ndonjë fitim, ata thonë: “A nuk mbizotëruam ne mbi ju (por nuk deshtëm të mposhim0 dhe penguam prej jush besimtarët!” All-llahu do të gjykojë mes jufsh ditën e gjykimit. All-llahu krrsesi nuk iu mëndëson jbesimtarëve mbizotërim të pltë mbi besimtarët.');
INSERT INTO "ahmeti" VALUES(4,142,'4:142','Munafikët përpiqen të mashtrojnë All-llahun (duke u paraqitur rrejshëm si besimtarë), por All-llahu mbizotëron dinakëritë e tyre. Ata edhe kur falen, falen me përtaci dhe vetëm sa për t’u dukur te njerëzit dhe fare pak e përmendin All-llahun.');
INSERT INTO "ahmeti" VALUES(4,143,'4:143','Ata janë të luhatshëm ndërmjet atij dhe atij (besimit dhe mosbesimit) e nuk janë asme ata (me jobesimtarët). Po atë që e mposht All-llahu, ti nuk mund të gjesh shpëtim për te.');
INSERT INTO "ahmeti" VALUES(4,144,'4:144','O ju që besuat, mos zini miq jobesimtarët e t’i lini manash besimtarët! A doni t’i sillni allahut argument të qartë kundër vetës!');
INSERT INTO "ahmeti" VALUES(4,145,'4:145','S’ka dyshim, munafikët do të jenë në shtresën më të ulët (në fund0 të zjarrit dhe për ta nuk do të gjesh mbrojtës,');
INSERT INTO "ahmeti" VALUES(4,146,'4:146','Përveq atyre që u penduan, u përmirësuan, iu përmbajtën All-llahut (mësimeve të Tij) dhepraktikojnë sinqerisht fenë e tyre për All-llahun. Të tillët do të jenë bashkë me besimtarët, e All-llahu ka për t’iu dhënë besimtarëve shprblim të madh.');
INSERT INTO "ahmeti" VALUES(4,147,'4:147','çfarë ka All-llahu me dënimin tuaj? E falënderuat dhe i besuat? (e para nuk i pakëson sundimin e as e dyta nuk ishton) All-llahu është mirënjohës i dijshëm.');
INSERT INTO "ahmeti" VALUES(4,148,'4:148','All-llahu nuk do publikimin e shprehjeve të këqia përveç (publikimit) të atij që i bërë padrejt. All-llahu është dëgjues, i dijshëm.');
INSERT INTO "ahmeti" VALUES(4,149,'4:149','Nëse publikoni ndonjë të mirë ose e fshihni, ose i falni ndonjë të keqe (dikujt), All-llahu i plotfuqishëm falë shumë.');
INSERT INTO "ahmeti" VALUES(4,150,'4:150','S’ka dyshim se ata që nuk besojnë All-llahun dhe të dërguarin e Tij, dëhirojnë të bëjnë dallim mes All-llahut dhe të dërguarve të Tij e thonë: “Ne i besojmë disa e nuk i besojmë disa të tjerë” e mes këtij duam të marrin rrugë.');
INSERT INTO "ahmeti" VALUES(4,151,'4:151','Të tillët lanë jobesimtarët e vërtetë; e Ne kemi përgatitur dënim të fortë e nënçmues për jobesimtarët.');
INSERT INTO "ahmeti" VALUES(4,152,'4:152','E ata që besuan All-llahun dhe të dërguarin e Tij dhe nuk bënë kurrfarë dallimi në asnjë prej tyre, të tillëve do t’u jepet shpërblim i merituar. All-llahu falë shumë dhe është mëshirues.');
INSERT INTO "ahmeti" VALUES(4,153,'4:153','Ithtarët e librit (jehuditë) kërkojnë prej teje t’u sjellësh një libër (komplet) nga qielli. Po ata patën kërkuar prej Musait edhe më tepër e i patën thënë: “Dëtfona Zotin shesheas” (ta shohim me sy) e për shkak të mizorisë së tyre i kapi rrufeja. mandej edhe pasi që iu patën shfaqur atyre mrekulli të qarta, ata adhuruan. Ne u patëm falur atë, e Musait i dhamë argumente të forta.');
INSERT INTO "ahmeti" VALUES(4,154,'4:154','Ne ngritëm mbi ta (bregun) Tur’in pse ishin zotuar (ta pranojnë Tevratin)’ u patëm thënë: “Hyni në derë (të bejtul mukadesit) të përulur, dhe u patëm thënë: “Mos e thyeni të shtnën” (mos gjuani peshk). Ashtu Ne patëm marrë prej tyre besëtë fortë.');
INSERT INTO "ahmeti" VALUES(4,155,'4:155','Dhe për shkak të thyerjes së besës së dhënë, të mohimit të ajeteve (shpalljes) të All-llahut, të mbytjes së pejgamberëve pa kurrfarë të drejtë dhe thënies së tyre: “Zemrat tona janë të mbuluara” (me perde). Jo, por për shkak të mohimit të tyre All-llahu ua vulosi ato (zemrat), e nuk besojnë prej tyre vetëm pakkush.');
INSERT INTO "ahmeti" VALUES(4,156,'4:156','Edhe për shkak të mosbesimit dhe të thënies së tyre shpifëse kundër Merjemes;');
INSERT INTO "ahmeti" VALUES(4,157,'4:157','madje për shkak të thëhies së tyre: “Ne e kemi mbytur mesihun, Isain, birin e Merjemes, të dërguarin e All-llahut”. Po ata asnuk e mbytën as nuk e gozhduan (nuk e kryqëzuan në gozhda), por atyre u përngjau. Ata që nuk u pajtuan rreth (mbytjes së) tij, janë në dilemë për të (për mbytje) e nuk kanë për të kurrfarë dije të saktë, përveç që iluzojnë. E ata me siguri nuk e mbytën atë.');
INSERT INTO "ahmeti" VALUES(4,158,'4:158','Përkundrazi, All-llahu e ngriti atë pranë Vetes. All-llahu është i poltfuqishëm, i dijshëm.');
INSERT INTO "ahmeti" VALUES(4,159,'4:159','Nuk ka asnjë nga ithtarët e librit vetëm se ka për t’i besuar atij (Isait) para vdekjes së vet, e në ditën e gjykimit ai dëshmon kundër tyre.');
INSERT INTO "ahmeti" VALUES(4,160,'4:160','Dhe për shkak të mizorisë së tyre, që ishin jahudi, dhe për shkak se penguan shumë nga rruga e All-llahut, Ne ua ndaluam (ua bëmë haram) disa (lloje ushqimesh) të mira që u ishin të lejuara.');
INSERT INTO "ahmeti" VALUES(4,161,'4:161','Edhe për shkak të marrjes së kamatës edhe pse e kishin të ndaluar atë dhe ngrënies së pasurisë së njerëzve në mënyrë të padrejtë. Ne kemi përgatitur dënim të mundimshëm për ata që nuk besuan prej tyre.');
INSERT INTO "ahmeti" VALUES(4,162,'4:162','Por ata, prej tyre që kanë thesar të diturisë, dhe besimtarët, besojnë atë që tu shpallty dhe atë që u shpall para teje, besojnë edhe ata që e falin namazin dhe japin zeqatim dhe besimtarët në All-llahun e në jetën e ardhshme. Të tillëve do t’u japim shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(4,163,'4:163','Ne të frymëzuam ty me shpallje sikurse e patëm frymëzuar Nuhun dhe pejgamberët pas tij; e patëm frymëzuar Ibrahimin, Ismajlin, Is-hakun, Jakubin dhe pasardhësit e tij, Isain, Ejubin, Junusin, Harunin, Sulejmanin, e Davudit i patëm hënë Zeburin.');
INSERT INTO "ahmeti" VALUES(4,164,'4:164','Dhe (dërguam) të dërguar më parë që të rrëfyem për ta, dhe të dërguar të tjerë për të cilët nuk të rrëfyem, e Musait i foli All-llahu me fjalë.');
INSERT INTO "ahmeti" VALUES(4,165,'4:165','të dërguar që ihin lajmgëzues e kërcënues, ashtu që pas dërgimit të dërguarve njerëzit të kenë fakt (arsyetim) pars All-llahut. All-llahu është i pavarur në sundimin e vet dhe di si të veprojë.');
INSERT INTO "ahmeti" VALUES(4,166,'4:166','(Jehuditë i thanë se nuk të besojnë në shpallje Muhammedit) Por allahu dëshmon për atë që t’u zbrit ty, atë ta zbriti sipas dijes së Vet, edhe engjëjtë dëshmojnë, po mjaft është All-llahu dëshmues.');
INSERT INTO "ahmeti" VALUES(4,167,'4:167','Ata që nuk besuan dhe penguan nga rruga e All-llahut, ata kanë humbur shumë larg.');
INSERT INTO "ahmeti" VALUES(4,168,'4:168','Aa që nuk besuan dhe bënë zullum, All-llahu as nuk ka për t’i falur as nuk ka për t’i drejtuar në rrugë,');
INSERT INTO "ahmeti" VALUES(4,169,'4:169','Përpos rrugës së Xhehennemit, në të cilin do të jenë përjetë të përsosur. E për All-llahun kjo është lehtë.');
INSERT INTO "ahmeti" VALUES(4,170,'4:170','o ju njerëz, juve u erdhi i dërguari me të vërtetën (fenë e drejtë) nga Zoti juaj, pra të i besoni se është më e dobishme për ju. por nëse vazhdoni ta refuzoni, ta dini se ç’ka në qiej e tokë është vetëm e All-llahut. All-llahudi (gjendjen e robëve), është më urti.');
INSERT INTO "ahmeti" VALUES(4,171,'4:171','O ithtarët e librit, mos teproni në fenë tuaj dhe mos thuani tjetër gjë për All-llahun, përveç asaj që është e vërtetë. Mesihu Isa, bir i merjemes, ishte vetëm i dërguar i All-llahut. Ishte fjalë e Tij (bëhu) që ia drejtoi Merjemes dhe ishte frymë (shpirt) nga Ai. Besonie pra All-llahun dhe të dërguarin e Tij e mos thoni: “Tre” (trini). Pushoni (së thëni), se është më mirë për ju. All-llahu është vetëm një All-llah; larg qoftë asaj që Ai të kektë fëmijë. ç’ka ka në qiej dhe në tokë është vetëm e Tij. Mjafton që All-llahu është planifikues i pavarur.');
INSERT INTO "ahmeti" VALUES(4,172,'4:172','Mesihu nuk tërhiqet prej asaj se është rob i All-llahut, nuk tërhiqet as engjëjt më të zgjedhur. Kush tërhiqet prej adhurimit ndaj Tij dhe bën mendjemadhësi, Ai so t’i ringjallë dhe do t’i tubojë të gjithë pranë Tij.');
INSERT INTO "ahmeti" VALUES(4,173,'4:173','E përsa i përket atyre që besuan dhe vepruan mirë, Atyre do t’ju përmbushet shpërblimi i e merituar, por edhe do t’iu shtohet prej dhuntisë së Tij. Përsa i përket atyre që u tëhoqën dhe mbajtën veten kryelartë, ata do t’i ndëshkojë me një ndëshkim fort të dhëmbshëm dhe pos All-llahut nuk do t’i gjejnë vetes as mbrojtës as ndihmës.');
INSERT INTO "ahmeti" VALUES(4,174,'4:174','O ju njerëz, juve ju erdhi nga Zoti juaj argument dhe Ne ju zbritëm dritë të qartë.');
INSERT INTO "ahmeti" VALUES(4,175,'4:175','Ata që i besuan All-llahut dhe iu përmbajtën Atij (librit të Tij), Aido t’i fusë në mëshirën (Xhennetin) e Tij dhe do t’i gradojë e do t’i udhëzojë për në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(4,176,'4:176','Kërkojnë përgjigjen tënde. Thuaju: “All-llahu ju përgjigjet për çështjen e “kelale-s” (ai që nuk prindër as fëmij që e trashigojnë). Nëse vdes një njeri që nuk fëmijë, por ka motër, atëherë asaj (motrës) i takon gjysma e pasurisë së lënë. Ai (vëllau) trashëgon tërë atë (që le motra) nëse ajo nuk ka fëmijë. Në qoftë se ato janë dy (motra që trashëgojnë), atyre dyjave u takojnë dy të tretat që lë ai. Në qoftë se janë vëlezër dhe të përzier burra dhe gra, atëherë mashkullit i takon hise dy fish më shumë se sa femrës. All-llahu ju sqaron, ashtu që të mos humbni. All-llahu di për çdo send.');
INSERT INTO "ahmeti" VALUES(5,1,'5:1','O ju që besuat! Zbatoni premtimet (obligimet). U janë (lejuar t’i hani) kafshët shtëpiake, me përjashtim të atyre që po ju lexohen (në këtë kaptinë si të ndaluara) dhe përveç gjetisë, që nuk është e lejuar derisa jeni në ihram. Vërtetë, All-llahu vendos atë që dëshiron.');
INSERT INTO "ahmeti" VALUES(5,2,'5:2','O ju që besuat! Mos i shkelni simbolet e All-llahut, as muajin e shejtë (mos lejoni luftën), as kurbanin (mos e pengoni), as atë me qafore (të caktuar për kurban), as (mos pengoni) ata që duke kërkuar begati nga Zoti i tyre dhe kënaqësinë e Tij, kanë mësyrë (për vizitë) shtëpinë e shejtë (Qaben). E kur të hiqni ihramin, atëherë ghahtoni. Urrejtja ndaj një populli, që iu pat penguar nga xhamia e shenjtë, të mos iu shtyej të silleni në mënyrë të padrejtë kundër tyre. Ndihmohuni mes vete me të mira dhe në të mbara, e mosni në mëkate e armiqësi. Kini dro dënimit të All-llahut, se me të vërtetë All-llahu është ndëshkues i fortë.');
INSERT INTO "ahmeti" VALUES(5,3,'5:3','Juve u janë ndaluar (t’i hani): ngordhësira, gjaku, mishi i derrit, ajo që therret jo në emrin e All-llahut, e furmja, e mbytura, e rrëzuarja, e shparja (nga i tjetrës), ajo që e ka ngrënë egërsira, përveç asaj që arrini ta therrni (para se të ngordhë), ajo që është therrur për idhuj, dhe (u është e ndaluar) të kërkoni me short fatin. Kjo është mëkat ndaj All-llahut. Tashmë, ata që mohuan, humbën shpresën për fenë tuaj (se do të ndërroni), andaj mos u frikësonju atyre, por mua të më frikësoheni. Sot përsosa për ju fenë tuaj, plotësova ndaj jush dhuntinë Time, zgjodha për ju islamin fe. E kush detyrohet nga uria (të hajë nga të ndaluarat), duke mos anuar dhe duke mos patur qëllim mëkatin, All-llahu falë shhumë dhe është mëshirues.');
INSERT INTO "ahmeti" VALUES(5,4,'5:4','Të pyesin ty (Muhammed), çka u është lejuar atyre? Thuaju: “U janë lejuar të gjitha ushqimet e mira dhe ai (gjahu) i gjahtarëve (shtazëve) të dresuara që i mësoni ashtu si ju ka mësuar juve All-llahu. Hani, pra, atë që gjahtuan për juve dhe më parë përmendie emrin e All-llahut. Kini dro nga All-llahu pse All-llahu shpejt merr masa.');
INSERT INTO "ahmeti" VALUES(5,5,'5:5','Sot u lejuan për ju ushqimet e mira, edhe ushqimet (të therrurat) e ithtarëve të librit janë të lejuara (hallall) për ju, edhe ushqimet (të therrurat) tuaja janë të lejuara për ta (u janë lejuar). Gratë e ndershme besimtare, dhe (gra) të ndershme nga ata të cilëve u është dhënë libri para jush, kur atyre u jepni pjesën e caktuar të kurorës, por duke pasur për qëllim martesën, jo imoralitetin dhe jo si dashnore ilegale. E kush mohon (tradhton) besimin, ai ka asgjësuar veprën e vet dhe ai në botën tjetër është prej të shkatërruarve.');
INSERT INTO "ahmeti" VALUES(5,6,'5:6','O ju që besuat! Kur doni të ngriteni për të falur namazin, lani fytyrat tuaja dhe duart tuaja deri në bërryla; fërkoni kokat tuaja, e këmbët lani deri në dy zogjt. Nëse jeni xhunubë, atëherë pastrohuni (lahuni)! Në qoftë se jeni të sëmurë, ose në ndonjë udhëtim, ose ndonjëri prej jush vjen prej vendit të nevojës, ose keni kontaktuar me gratë dhe nuk gjeni ujë, atëherë mësyne (merrni tejemum) dheun e pastër dhe me të fërkoni fytyrat dhe duart tuaja. All-llahu nuk dëshiron (me obligim për abdest e larje) t’iu sjellë ndonjë vështirësi, por dëshiron t’ju pastroj të mirën e Tij ndaj jush e që t’i falënderoheni.');
INSERT INTO "ahmeti" VALUES(5,7,'5:7','Përkujtoni dhuntinë e All-llahut, që ju dha (fenë Islame) dhe zotimin e Tij, të cilin e pat marr prej jush kur thatë: “Dëgjuam e respektuam”. Kinie frikë All-llahun, pse All-llahu i di shumë mirë të fshehtat në gjoksa (në zemra).');
INSERT INTO "ahmeti" VALUES(5,8,'5:8','O ju që besuat! Bëhuni plotësisht të vendosur për hir të All-llahut, duke dëshmuar të drejtën, dhe të mos u shtyjë urrejtja ndaj një populli e t’i shmangeni drejtësisë; bëhni të drejtë se ajo është më afër devotshmërisë. Kinie dro Alahun se All-llahu di holësiht për atë që veproni.');
INSERT INTO "ahmeti" VALUES(5,9,'5:9','Atyre që besuan dhe bënë vepra të mira, All-llahu u premtoi falje për mëkate dhe shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(5,10,'5:10','E ata që mohuan dhe përgënjeshtruan argumentet Tona, ata janë banues të Xhehennemit.');
INSERT INTO "ahmeti" VALUES(5,11,'5:11','O ju që besuat! Përkujtone të mirën e All-llahut ndaj jush kur, një populltentoitë zgjasë duart e veta (për mbytje) kundër jush, krse Ai zmbrapsi duart e tyre prej jush. Respektonie All-llahun (duke zbatuar urdhërat e Tij) dhe vetëm All-llahut le t’i mbështeten besimtarët.');
INSERT INTO "ahmeti" VALUES(5,12,'5:12','në të vërtet All-llahu pat marrë zotimin e beni israilve. Dhe prej tyre dërguam (caktuam) dymbëdhjetë prijës. All-llahu tha: “Unë jamë (mbrojtës) me ju!” Në qoftë se falni namazin, e jepni zeqatin, u besoni të dërguarve të Mi dhe u ndihmoni atyre, huazoni për hirë të All-llahut hua rë mirë, Ai patjetër do t’ju shlyejmëkatet tuaja dhe do t’ju shtie në zhenete nën të cilët rrjedhin lumenj. E kush kundërshton pas këtij (zotimi) prej jush, ai e ka humbur rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(5,13,'5:13','Për shkak se ata e thyen zotimin e tyre, Ne i mallkuam ata, dhe zemrat e tyre i bëmë të forta (të shtangura). Ata i ndryshojnë fjalët (në Tevrat) nga domethënia e tyre dhe braktisën një pjesë me të cilën ishin urdhëruar. Ti vazhdimisht do të vërejsh tradhtinë e një pjese të tyre, përpos një pakice nga ata. Pra falju dhe hiqu tyre. All-llahu ido ata që bëjnë mirë.');
INSERT INTO "ahmeti" VALUES(5,14,'5:14','Ne e morëm zotimin dhe të atyre që thanë: “Ne jemi nesara” e edhe ata e harruan një pjesë të saj me të cilën ishin udhëzuar, andaj Ne kemi ndërsyer armiqësinë e urrejtjen ndërmjet tyre deri në dtën e kijametit. E më vonë All-llahu do t’injohtojë ata me atë që bënë.');
INSERT INTO "ahmeti" VALUES(5,15,'5:15','O ithtarë të librit, juve ju erdhi i dërguari Jonë që ju sqaron shumë nga ajo që fshihnit prej librit, e për shumë nuk jep sqarime. Juve ju erdhi erdhi nga All-llahu dritë, dhe libër i qartë.');
INSERT INTO "ahmeti" VALUES(5,16,'5:16','All-llahu e vë me atë (me Kur’anin) në rrugët e shpëtimit atë që ndjek kënaqësinë e tij dhe me ndihmën e Tij i nxjerr ata prej errësirave në dhe i udhëzon në një rrugë që është e drejtë.');
INSERT INTO "ahmeti" VALUES(5,17,'5:17','E mohuan (e bënë kufr) të vërtetën ata që thanë se: “Zot është ai, Mesihu bir i Merjemes” Thuaju: “Nëse dëshiron All-llahu ta shkatërrojë Mesihun, birin e Merjemes, nënën e tij dhe gjithë ç’ka në tokë, kush mund ta pengojë atë?” Vetëm Atij i takon pushteti i qiejve, i tokës dhe ç’ka mes tyre. Ai krijon çdo gjë që dëshiron. All-llahu është i gjithfuqishëm për çdo gjë.');
INSERT INTO "ahmeti" VALUES(5,18,'5:18','Jehuditë dhe të krishterët thanë: “Ne jemi bijtë e All-llahut dhe të dashurit e Tij”. Thuaju: “E pse pra, Ai ju dënon me mëkatet tuaja?” Jo, ju jeni njerëz që Ai ju krijoi. Ai i falë atij që do dhe dënon atë që do. Sundimi i qiejive, itokës dhe i githë ç’ka ka në mes tyre është vetëm e All-llahut dhe vetëm te Ai është e ardhmja.');
INSERT INTO "ahmeti" VALUES(5,19,'5:19','O ihtarë të librit, ju erdhi i dërguari i Jonë, ju sqaron (çështjet e fesë) pas ndërprerjes së të dërguarve, në mënyrë që të mos thoni: “Neve nuk na erdhi as i dërguar që të na përgëzojë, e as të na tërheqë vrejtjen “. Pra ja, ju erdhi përgëzuesi dhe kërcenuesi. All-llahu është fuqiplotë për çdo send.');
INSERT INTO "ahmeti" VALUES(5,20,'5:20','Përkujto (O i dërguar) kur Musai i tha popullit të vet: “O popull imi, përkujtonie dhuratën e All-llahut ndaj jush, kur në mesin tuaj fërgoi pejgamberë, ju mundësoi të jeni sundues dhe ju dha çka nuk idha askujt nga popujt.');
INSERT INTO "ahmeti" VALUES(5,21,'5:21','O popull imi, hyni në tokën e shejtë, të cilën All-llahu ua premtoi, dhe mos u ktheni mbrapa, pse atëherë khteheni të dëshpruar.');
INSERT INTO "ahmeti" VALUES(5,22,'5:22','Ata (populli i Musait) thanë: O Musa, aty është një popull i fuqishem (dhunues), prandaj ne nuk do të hyjmë aty kurrë derisa ata të mos dalin nga ajo (të na e lirojnë pa luftë), nëse dalin ata nga ajo, ne nuk hyjmë.');
INSERT INTO "ahmeti" VALUES(5,23,'5:23','Dy burra, prej tyre (prijësve) që All-llahu i kishte pajisur me bindje dhe kishin frikën e Tij, thanë: “Hynjy atyre nga dera, e kur t’u hyni nga ajo, atëherë ju jeni ngadhënjyes. Dhe vetëm All-llahut mbështetjuni, nëse jeni besimtarë të sinqertë.');
INSERT INTO "ahmeti" VALUES(5,24,'5:24','Ata thanë: “O Musa, ne kurrë nuk hyjmë në të derisa ata janë aty, shko pra ti dhe Zoti yt e luftoni, ne po rrimë këtu!”');
INSERT INTO "ahmeti" VALUES(5,25,'5:25','(Musai) Tha: “Zoti im! Unë nuk kam në dorë pos vetes sime dhe as vëllai im (Haruni nuk ka në dorë pos vetvetes, ose: pos vetes sime dhe vëllai tim), ndana, pra, neve prej popullit të fëlliqtë.');
INSERT INTO "ahmeti" VALUES(5,26,'5:26','(All-llahu) Tha: “Ajo (tokë) është e ndaluar (haram) për ta katërdhjetë vjet. Do të sillen nëpër tokë (të hutuar). Ti mos u keqëso për popullin e shfrenuar.');
INSERT INTO "ahmeti" VALUES(5,27,'5:27','Lexoju (Muhammed) atyre (jehudive e të tjerëve) ngjarjen e vërtetë të dy djemve të Ademit, kur të dy flijuan kurbanë, nga të cilët njërit iy pranua. Ai (që nuk iu pranua) tha: “Unë do të të mbys ty” (Kabili i tha Habilit). E ai (që iu pranua) tha: “All-llahu pranon vetëmprej tësinqertëve”,');
INSERT INTO "ahmeti" VALUES(5,28,'5:28','Dhe, nëse ti e zgjatë te unë dorën tënde për të më mbytur, unë nuk e zgjas te ti dorën për të mbytur; unë i frikësohem All-llahut, Zotit të botërave!”');
INSERT INTO "ahmeti" VALUES(5,29,'5:29','Unë (nuk dua të të mbys, por) dua që ta barësh mëkatin tim dhe mëkatin tënd, e të bëhesh nga banorët e zjarrit, e ai është dënim i zullumqarëve.');
INSERT INTO "ahmeti" VALUES(5,30,'5:30','Atëherë epshi i tij e bindi atë për mbytjen e të vëllait dhe e mbyti atë. Ai mbeti prej më të dëshpruarve (hidhëroi prindëtit, mbeti pa vëlla, u përbuz prej Zotit dhe u fut në Xhehennem).');
INSERT INTO "ahmeti" VALUES(5,31,'5:31','All-llahu dërgoi një sorrë, e cila groponte në dhe, për r’i reguar atij se si ta mbulojë trupin e të vëllait. E ai i tha: “I mjei unë, a nuk qesh i afrë të bëhem si kjo sorrë, e a mbuloja trupin e vëllait tim?” Ashtu mbeti i penduar.');
INSERT INTO "ahmeti" VALUES(5,32,'5:32','Për këtë (shkak të atij krimi) Neu shpallëm (ligji) beni israilve se kush mbyt një njeri (pa të drejtë), pa pasë mbytur ai ndonjë tjatër dhe p pasë bërë ai ndonjë shkatërrim në tokë, atëherë (krimi i tij) është si t’i kishte mbytur gjithë njerëzit. E kush e ngjall (bëhet shkak që të jetë ai gjallë) është si t’i kishte ngjallur (shpëtuar) të gjithë njerëzit. Atyre (beni israilve) u erdhën të dërguarit Tanë me argumente, mandej edhe pas këtij (vendimi) shumë prej tyre e teprojnë (kalojnë kufinjt në mbytje) në këtëtokë.');
INSERT INTO "ahmeti" VALUES(5,33,'5:33','Dënimi i atyre që luftojnë (kunddërshtojnë) All-llahun dhe të dërguarin e Tij dhe bëjnë shkatërrime në tokë, nuk është vetëm se të mbyten ose të gozhdohen, ose (të gjymtohen), t’u priten duart dhe këmbët e tyre të anëve të kundërta, ose të dëbohen nga vendi. Kjo (masë ndëshkuese) është poshtërim për ta në dynja, dhe në botën tjetër ata do të kenë dënim të madh.');
INSERT INTO "ahmeti" VALUES(5,34,'5:34','Përveç atyre që janë penduar para se tçi kapni. E, dine pra se All-llahu bën falje të madhe, është mëshirues.');
INSERT INTO "ahmeti" VALUES(5,35,'5:35','O ju që besuat! Kinie frikë All-llahun dhe kërkoni afrim te Ai, luftoni në rrugën e Tij që të gjeni shpëtim.');
INSERT INTO "ahmeti" VALUES(5,36,'5:36','Atyre që mohuan, edhe sikur të ishte e tyre e gjithë ajo që është në tokë, e edhe njëherë aq për ta dhënë shpagim (zhdëmtim) nga dënimi i ditës së kijametit, nuk u pranohet. Ata kanë dënim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(5,37,'5:37','Ata përpiqen të dalin nga zjarri, por s’kanë të dalë nga ai, ata kanë dënim të përhershëm.');
INSERT INTO "ahmeti" VALUES(5,38,'5:38','Vjedhësit dhe vjedhësit preniu duart, si shpagim i veprës që bën, (kjo masë është) dënim nga All-llahu. All-llahu është i fuqishëm, ligjdhënës i urtë.');
INSERT INTO "ahmeti" VALUES(5,39,'5:39','E kush pendohet pas punës së keqe (vjedhjes) dhe përmirësohet, s’ka dyshim, All-llah ia pranon pendimin, All-llahu është që falë shumë, është mëshirues.');
INSERT INTO "ahmeti" VALUES(5,40,'5:40','A nuk di se All-llahu është Ai që sundimi i qiejve dhe i tokës është vetëm iTij, andaj Ai dënon atë që do dhe e falë atë që do. All-llahu është i plotfuqishëm pr çdo gjë.');
INSERT INTO "ahmeti" VALUES(5,41,'5:41','O ti i dërguar! Të mos brengos vrapimi në mosbesim i tyre që me gojët e tyre thanë: “Ne kemi besuar”, e zemrat e tyre nuk kanë besuar (munafikët), e as i tyre që janë jehudi, të cilët shumë i përgjigjen gënjeshrës dhe shumë e dëgjojnë (fjalën) një populli që nuk vjen te ti, ata janë që i menjanjojnë fjalët pas vënies së tyre në vendin e vet (nga ana e All-llahut) e thonë: “Nëse u jepet kjo, pranoni, e nëse nuk u jepet, atëherë refuzoni. Po atë që All-llahu do, të humbë, ti nuk mund të bësh asgjë për te (nuk mund ta shpëtosh). Janë ata të cilëve All-llahu nuk deshi t’iu pstrojë zemrat (prej kufrit). janë ata që në këtë jetë kanë poshtërim, kurse në botën tjetër');
INSERT INTO "ahmeti" VALUES(5,42,'5:42','Janë ata që i përgjigjen të pavërteës dhe hanëshumë haramin (ryshfetin). Nëqoftë se vijnë te ti (për ndonjë gjykim), gjyko mes tyre ose hiqu tyre. nëse prapsesh prej tyre (nuk gjykon), ata nuk mund të sjellin ty kurrfarë dëmi. Po nëse gjykon ms tyre, gjyko drejtë; All-llahu i do të drejtit.');
INSERT INTO "ahmeti" VALUES(5,43,'5:43','E si kërkojnë ata gjykimin tënd, kur ata kanë Tevratin, e në të cilin është vendimi i Allauhut, e pastaj ata refuzojnë më vonë atë (vendimin tënd)? Ata nuk janë besimtarë.');
INSERT INTO "ahmeti" VALUES(5,44,'5:44','Ne e zbritëm Tevratin, në të cilin është udhëzimi i drejtë dhe drita. Sipas tij gjykuan ndaj atyre që ishin jehudi, pejgamerët që ishin të bindur, gjykuan edhe dijetarët e devotshëm e paria fetare, ngase ishin të obliguar ta ruanin librin e Allauhut dhe ishin mmbrojtës të tij. Prandaj, mos u frikësoni nga njerëzit, vetëm Mua të më frikësoheni dhe mos i ndërroni argumentet e Mia për pak send. E kush nuk gjykon me atë që zbriti All-llahu, ata janë mohues.');
INSERT INTO "ahmeti" VALUES(5,45,'5:45','Ne u caktuam atyre në të (në Tevrat) kundërmasat ndëshkuese: njeriu (mbytet) për njeriun, syri për syrin, hunda për hundën, veshi për veshin, dhëmbi për dhëmbin, por edhe plagët kanë kundër masë. E kush e falë atë (hakmarrjrrjen), ajo është shpagim (nga mëkatet) për të. E kush vendos sipas asaj që zbriti All-llahu, ata janë mizorë (të pa drejt).');
INSERT INTO "ahmeti" VALUES(5,46,'5:46','Ne vazhduam gjurmët e tyre (të pejgamberëve) me Isain, birin e Merjemes, vërtetues i Tevratit që kishin më parë. Atij i dhamë Inxhilin, që është udhëzim i drejtë dhe dritë, që është vërtetues i Tevratit që kishin pranë, që ishte udhëzues e këshillues për të devotshmit.');
INSERT INTO "ahmeti" VALUES(5,47,'5:47','(E zbritëm) Që ithtarët e Inxhilit të gjykojnë sipas asaj që All-llahu e zbriti në të. E kush nuk gjykon sipas asaj që All-llahu e zbriti, të tillët janë mëkatarë të dalë jashtë rrugës së Zotit.');
INSERT INTO "ahmeti" VALUES(5,48,'5:48','Ne edhe ty (Muhammed) ta zbritëm librin (Kur’anin) e vërtetë që është vërtetues i librave të mëparshme dhe garantues i tyre. Gjyko, pra, mes tyre me atë që All-llahu e zbriti, e mos pëlqe epshet e tyre e të largohesh nga e vërteta që i erdhi. Për secilin prej jush, Ne caktuam ligje program (të posaçëm në çështje të veprimit). Sikur të donte Alahu, do t’ju bënte një popull (në fe e sheriat), por deshi t’ju sprovojë në atë që iu dha juve, andaj ju (besimtarë) përpiquni për punë të mira. Kthimi i të gjithë juve është te All-llahu, e Ai do t’ju njoftojë me atë që kundërshtoheshi.');
INSERT INTO "ahmeti" VALUES(5,49,'5:49','(Ta zbritëm) Që edhe ti të gjykosh me atë që të zbriti All-llahu, e mos ndiq dëshirat e tyre dhe ruaju rrezikut të tyre që të mos të shmangin nga një pjesë e asaj që ta zbriti All-llahu. Nëse ata refuzojnë (gjykimin tënd), ti dije se All-llahu ka për qëllim t’i ndëshkojë për disa mëkate të tyre. Në të vërtetë, një shumicë e njerëzve janë jashtë rrugës.');
INSERT INTO "ahmeti" VALUES(5,50,'5:50','A thua mos po kërkojnë gjykimin e kohës së injorancës, po për një popull që bindshëm beson, a ka gjykim më i mirë se ai i All-llahut?');
INSERT INTO "ahmeti" VALUES(5,51,'5:51','O ju që besuat! Mos zini miq as jehuditë, e as të krishterët. Ata janë miq të njëri-tjetrit. E kush prej jush i miqëson ata, ai është prej tyre. Vërtetë All-llahu nuk vë në rrugë të drejtë popullin zullumqarë.');
INSERT INTO "ahmeti" VALUES(5,52,'5:52','E ata që në zemrat e tyre kanë sëmundje, i sheh se ngarendin te ta (për miqësi) duke thënë: “Po frikësohemi se mos po na sillet ndonjë e keqe”. Po All-llahu do të sjellë fitoren, ose diç tjetër nga ana e Tij, e atëherë do të pendohen për atë që e mbanin fsheht në shpirtin e tyre.');
INSERT INTO "ahmeti" VALUES(5,53,'5:53','Ata që besuan ju thonë: “A këta ishin që u betuan në Zotin me betimin më të fortë të tyre se ishin me ju? I shkatërruan veprat e veta dhe aguan të dëshpëruar.');
INSERT INTO "ahmeti" VALUES(5,54,'5:54','O ju që besuat! Kush largohet prej nga feja e vet (i bën dëm vetes) s’ka dyshim se All-llahu do të sjellë një popull që Ai e do atë (popull) dhe ata e duan Atë (Zotin), (një popull) që është modest e i butë ndaj besimtarëve, por i ashpër dhe i fortë ndaj mohuesve, që lufton në rrugën e All-llahut dhe që nuk i frikësohet kërcënimit të asnjë kërcënuesi. Kjo (cilës e atij populli) është dhuratë e All-llahut që i jep atij që do. All-llahu është dhurues i madh, i dijshëm.');
INSERT INTO "ahmeti" VALUES(5,55,'5:55','Mik (i afërt) juaji është vetëm All-llahu, është i dërguari i Tij dhe ata që besuan e që falin namazin dhe jalin zeqatin duke bërë rukuë (duke qenë respektues).');
INSERT INTO "ahmeti" VALUES(5,56,'5:56','E kush ka për mik All-llahun, të dërgurin e Tij dhe ata që besuan, s’ka dyshim se ana (pala-krahu) e All-llahut është ngadhënjyese.');
INSERT INTO "ahmeti" VALUES(5,57,'5:57','O ju që besuat! Mos i merrnipër miq aaata të cilëve u është dhënë libri para jush edhe as jobesimtarët që me fenë tuaj tallen dhe luajnë. Kinie dro All-llahun po qe se jeni besimtarë.');
INSERT INTO "ahmeti" VALUES(5,58,'5:58','Edhe thirrjen (ezanin) tuaj për namaz ata e marrin për tallje e lojë. Me këtë vërtetojnë se janë popull që nuk kupton.');
INSERT INTO "ahmeti" VALUES(5,59,'5:59','Thuaj: “O ithtarë të librit, a na shihni për të me të vetëm pse besuam All-llahun; besuam atë që na zbriti neve dhe atë që zbriti më parë! S’ka dyshim, shumica jush janë të shfrenuar.');
INSERT INTO "ahmeti" VALUES(5,60,'5:60','Thuaj: “A t’ju lajmëroj për një të keqe (të zezë) më të dëmshme (nga ajo e metë që na e shihni) si dënim nga All-llahu? (Ajo është) Mallkimi i atij që e mallkoi All-llahu dhe hidhërimi ndaj tij, që disa rej tyre i shdërroi në majmunë e në derra, e i bëri të djallit. Të tillët janë në pozitë më të keqe dhe më të humburit prej rrugës së drejtë.');
INSERT INTO "ahmeti" VALUES(5,61,'5:61','E kur vijnë te ju, thonë: “Kemi besuar”, ndërsa ata me (kufr) mosbesim kanë hyrë dhe me të kanë dalë. Po All-llahu di më së miri se çka fshihnin ata.');
INSERT INTO "ahmeti" VALUES(5,62,'5:62','Dhe, shumë prej tyre isheh se nguten në mëkat, në mizori e në ngrënie të haramit. Sa e shëmtuar është ajo që bënë ata.');
INSERT INTO "ahmeti" VALUES(5,63,'5:63','E, pse paria fetare (e krishterë dhe jehude) të mos i ndalin nga ato thënie të tyre të rreme dhe nga ngrënia e haramit (e ryshfetit)! Sa punë e keqe është ajo e tyre (e parisë fetare).');
INSERT INTO "ahmeti" VALUES(5,64,'5:64','Jehuditë thanë: “Dora e All-llahut është e shtrënguar!” Qofshin të shtangur duart e tyre dhe qofshin të mallkuar, pse thanë atë. Jo, duart e Tij janë të shlira. Ai furnizon ashtu si të dojë. Kjo që t’u zbrit ty do t’u shtojë disave nga ata (nga paria fetare) shumë largimin dhe mohimin. Ne ndërmjet tyre hodhëm armiqësi dhe urrejtje që do t’u vazhdojë deri në ditën e kijametit. Sa herë që ndezën zjarr për luftë, All-llahu e shuajti atë, e ata përpiqen për shkatërrime në tokë. All-llahu nuk i do ngatërrestarët..');
INSERT INTO "ahmeti" VALUES(5,65,'5:65','Sikur ithtarët e librit të besonin dhe të ruheshin (naramit), Ne do t’ua shlyenim mëkatet e tyre dhe do t’i fusnim në Xhennete me përjetime të begatshme.');
INSERT INTO "ahmeti" VALUES(5,66,'5:66','Dhe, sikur ta zbatonin Tevratin, Inxhilin dhe Kur’anin, që u zbritën nga Zoti i tyre, ata do të kishin furnizim me bollëk nga qielli dhe toka. Ështënjë grup i drejtë (i matur) prej tyre, por është e keqe ajo që punojnë shumica prej tyre.');
INSERT INTO "ahmeti" VALUES(5,67,'5:67','O ti i dërguar! Komunikoje atë që t’u zbrit prej Zotit tënd, e nëse nuk bën (kumtimin në tërësi), atëherë nuk e ke kryer detyrën (revelatën-risalen). All-llahu të garanton mbrojtjen prej njerëzve (prej armiqve). All-llahu nuk vë në rrugë të drejtë popullin që mohon.');
INSERT INTO "ahmeti" VALUES(5,68,'5:68','Thuaj: “O ithtarë të librit, junuk jeni asgjë (në fe) derisa të mos zbatoni Tevratin, Inxhilin dhe atë që ju zbriti nga Zoti i juaj. E kjo që t’uzbrit ty nga Zoti yt do të shtojë te shumica prej tyre rezistencë e mohim. Po ti, mos u keqëso për popullin që nuk beson.');
INSERT INTO "ahmeti" VALUES(5,69,'5:69','S’ka dyshim se kush e beson All-llahun (sinqerisht) e beson ditën e gjykimit dhe bën vepra të mira, qoftë prej atyre qëbesuan, prej jehudivë, prej sabiinëve (të krishterë që adhurojnë yjtë), prej të krishterëve, për ta nuk do të ketë frikë as pikëllim (me kusht që të kenë pranuar të dërguarin e fundit- Muhammedin).');
INSERT INTO "ahmeti" VALUES(5,70,'5:70','Ne patëm marrë besën prej beni israilve, atyre u dërguam pejgamberë, por sa herë që u erdhi atyre ndonjë pejgamber që (me mësime) nuk u pëlqeu epsheve të tyre, ata disa prej tyre i përgënjeshtruan, kurse disa të tjerë i mbytën.');
INSERT INTO "ahmeti" VALUES(5,71,'5:71','Ata menduan se nuk do të kenë telashe (që i mbytën pejgamberët), mirëpo ashtu u verbuan (nga rruga e drejtë) dhe u shurdhuan (nga e vërteta). Pastaj (pasi i zuri belaja) All-llahu u pranoi pendimin. Mirëpo, më vonë shumë prej tyre mbetën të verbër e të shurdhër. All-llahu di për atë që punuan.');
INSERT INTO "ahmeti" VALUES(5,72,'5:72','Bënë kufr (mohuan të vërtetën) ata që thanë: “All-llah është ai, Mesihu, bir i Merjemes”. E vetë Mesihu, (Isai) tha: “O beni israil, adhuronie All-llahun, Zotin tim dhe Zotin tuaj, sepse ai që i përshkruan Zotit shok, All-llahu ia ka ndaluar (ia ka bërë haram) atij Xhennetin dhe vendi i tij është zjarri. Për mizorët nuk ka ndihmës.');
INSERT INTO "ahmeti" VALUES(5,73,'5:73','Gjithashtu bënë kufr (mosbesim) ata që thanë: “All-llahu është i treti i treve”. S’ka në gjithësi tjetër pos një All-llahu, e nëse pushojnë nga ajo që thanë (tre zotëra), do t’i kapë dënim i dhëmbshëm, ata që nuk besuan prej tyre.');
INSERT INTO "ahmeti" VALUES(5,74,'5:74','E pse të mos pendohen dhe të kërkojnë te All-llahu falje, kur All-llahu dihet se falë shumë, është mëshires.');
INSERT INTO "ahmeti" VALUES(5,75,'5:75','Mesihu, bir i Merjemes, nuk është tjetër, vetëm se i dërguar; para tij pati shumë të dërguar. Nëna e tij ishte e drejtë (e ndershme). Që të dy ata ishin që ushqeheshin (si njerëzit tjerë). Ja, si u sqarohen atyre argumentet dhe shih pastaj sesi ikthejnë shpinën të vërtetës!');
INSERT INTO "ahmeti" VALUES(5,76,'5:76','Thuaju: A adhuroni pos All-llahut atë që nuk mund t’ju sjellë as dëm as dobi? All-llahu është që dëgjon (ç’thoni) që di (ç’veproni).');
INSERT INTO "ahmeti" VALUES(5,77,'5:77','Thuaju: “O ithtarë të librit, mos e teproni në fenë tuaj jashtë të vërtetës dhe mos shkoni pas epsheve të një populli mëparshëm që ka humbur, që ka shkakttjerëve dhe që u largua në tërsi prej rrugës së drejtë.');
INSERT INTO "ahmeti" VALUES(5,78,'5:78','Ata që mohuan të vërtetën nga beni israilët, u mallkuan prej gjuhës së Davudit dhe të Isait, të birit të Mejremës. Kshtu uveprua sepse kundërshtuan dhe e tepruan.');
INSERT INTO "ahmeti" VALUES(5,79,'5:79','Ata ishin që nuk ndalonin njëri-tjetrin nga e keja që punonin. E ajo që bënin ishte e shëmtuar.');
INSERT INTO "ahmeti" VALUES(5,80,'5:80','I sheh shumë prej tyre që i miqësojnë ata që mohuan. E keqe është ajo që i përgaditën vetës së tyre ngase All-llahu është i hidhëruar kundër tyre dhe ata përjanë në azab (në vuajtje të dënimit).');
INSERT INTO "ahmeti" VALUES(5,81,'5:81','Sikur të ishin ata që vërteëtë e besojnë All-llahun, pejgamberin dhe atë që i zbriti atij, nuk do t’i zinin për miq ata (idhujtarët), por shumica prej janë fasika (mëkatarë).');
INSERT INTO "ahmeti" VALUES(5,82,'5:82','Gjithqysh ti do të vëresh se jehuditë dhe ata që i përshkruan Zotit shok (idhujtarët) janë njerëzit me armiqësi të fortë kundër kundër besimtarëve (kundër muslimanëve). Ndërsa do të vëresh se më të afërmit si miq për besimtarët thanë: “Ne jemi nesara-krishterë”. Këtë ngase prej tyre (të krishtrëve) ka dijetarë (ulema) dhe të devotshëm, dhe se ata nuk janë kryeneçë.');
INSERT INTO "ahmeti" VALUES(5,83,'5:83','kur edëgjojnë atë që i është zbritur të dërguarit (Kur’anin), prej syve të tyre rrjedhin lotë, ngase e kanë kuptuar të vërtetën dhe thonë: “Zoti ynë, ne kemi besuar, pra regjistro ndër ata që që dëshmojnë (ndër ymetin e Muhammedit).');
INSERT INTO "ahmeti" VALUES(5,84,'5:84','(the thonë) Përse të mos besojmë All-llahun dhe atë të vërtetë që na erdhi, duke qenë se ne shpresojmë që Zoti ynë do të na qojë (në Xhennet) së bashku me njerëzit emirë.');
INSERT INTO "ahmeti" VALUES(5,85,'5:85','Për ata që thanë, All-llahu i shpërbleu me Xhennete, në të cilët rjedhin lumenj, ku do të jenë përgjithmonë. Ky është shpërblimi për njerëzit bamirës.');
INSERT INTO "ahmeti" VALUES(5,86,'5:86','Ndërkaq, ata që mohuan dhe përgenjeshtruan arumentet Tona, janë banues të zjarrit.');
INSERT INTO "ahmeti" VALUES(5,87,'5:87','O ju që besuat! Mos i ndaloni (mos i bëni haram) të mirat që për ju i lejoi (i bëri hallall) dhe mos teproni, se All-llahu nuk i do ata që teprojnë (i kalojnë kufijtë e dispozitave të Zotit).');
INSERT INTO "ahmeti" VALUES(5,88,'5:88','Hani nga ajo që All-llahu ju furnizou si hallall të mirë dhe, duke qenë se ju All-llahut i besoni, ruanu dënimit të Tij!');
INSERT INTO "ahmeti" VALUES(5,89,'5:89','All-llahu nuk ju merr në përgjegjësi për betimiet e juaja të paqëllimta (për betim), por ju merr për ato që jeni zotuar qëllimisht. E shpagimi i tij (i betimit të bërë) është duke i ushqyer dhjetë të varfër me ushqim mesatar që ushqeni familjen tuaj, ose duke i veshur ata (të dhjetë), ose duke liruar një rob. E kush nuk ka mundësi t’i bëjë këto, le të agjërojë tri ditë. Ky është shpagim për betimet tuaja kur itheni ato. Ruani betimet tuaja. Kështu All-llahu ju shqaron dispozitat e veta, ashtu që jeni mirënjohës.');
INSERT INTO "ahmeti" VALUES(5,90,'5:90','O ju që besuat, s’ka dyshim se vera, bixozi, idhujt dhe hedhja e shigjetës (për fall) janë vepra të ndyta nga shejtani. Pra, largohuni prej tyre që të jeni të shpëtuar.');
INSERT INTO "ahmeti" VALUES(5,91,'5:91','Shejtani nuk dëshiron tjetër, përveç se nëpërmjet verës dhe bixhozit të hedh armiqësi mes jush, t’ju pengojë nga të përmendurit Zotit dhe t’ju largojë nga namazi. Pra, a po i jepni fund (alkoolit e bixhozit)?');
INSERT INTO "ahmeti" VALUES(5,92,'5:92','Bindjuni All-llahut dhe të dërguarit e kini kujdes (mos kundërshtoni). E nëse refuzoni, atëherë pra, dine se obligim i të dërguarit Tonë është vetëm komunikimit i qartë.');
INSERT INTO "ahmeti" VALUES(5,93,'5:93','Ata që besuan dhe bënë punë të mira, nuk kanë mëkat për atë që u ushqyen (më herët) derisa ruhen (prej haramit), besojnë dhe bëjnë vepra të mira, mandej ruhen (nga ajo që ka qenë e lejuar e është e ndaluar) dhe besojnë dhe pastaj ruhen (nga çdo mëkat) dhe bëjnë mirë. All-llahu i do ata që bëjnë mirë.');
INSERT INTO "ahmeti" VALUES(5,94,'5:94','O ju që besuat, All-llahu do t’ju sprovojë me diçka nga gjahu që e arrijnë duart ose shigjetat tuaja, për t’u te All-llahu ai që i frikësohet Atij në heshtje (në bazë besimi). E kush i shkel (dispozitat) pas këtij (sqarimi) ai ka dënim të dhëmbshëm.');
INSERT INTO "ahmeti" VALUES(5,95,'5:95','O ju që besuat, mos e mbytni gjahun, duke qenë ju në ihram. E kush e mbyt atë me qëlim, aterë dënimi është therrorizimi injë kafshe shtëpiake të ngjashme me atë (të egrën) që ka mbytur. Për këtë çështje vendosin dy njerëz të drejtë nga mesi juaj, duke bërë at kurban për Qabe, ose (dënimi është) duke ushqyer të varfër, ose sa ai (ushqimi), agjërimi, këtë për shijuar të keqen e punës së tij. All-llahu ka falur të kaluarën. E kush bën përsëri, (gabimin) All-llahu dënon ashpër. All-llahu çshtjet), hakmerret (me dënim).');
INSERT INTO "ahmeti" VALUES(5,96,'5:96','Gjahu dhe ushqimi në det u është lejuar, si përjetim për ju dhe për udhëtarët, kurse gjahu tokësor u është ndaluar sa të jeni në ihram. Kinie dro All-llahun, te i cili do të tuboheni!');
INSERT INTO "ahmeti" VALUES(5,97,'5:97','All-llahu e bëri Qaben, shtëpinë e shenjtë, vend jetësimi (çështjet të fesë të jetës) për njerëz, dhe muajin e shenjtë, edhe kurbanin edhe (ato kafshë të shënuara për kurban) atë me qafore. Këtë (përcaktim nga Zoti) që ta dini se All-llahu e di ç’ka në qiej dhe ç’ka ka në tokë dhe se All-llahu ka përfshirë me urtësinë e dijen e vet çdo send (përcaktimi i Zotit për këto është me qëllim të caktuar, e për të mirën e njerëzve).');
INSERT INTO "ahmeti" VALUES(5,98,'5:98','Dijeni se All-llahu është ndëshkues i rreptë, dhe se All-llahu është dhurues, është mëshirues.');
INSERT INTO "ahmeti" VALUES(5,99,'5:99','I dërguari nuk ka tjetër obligim vetëm të kumtojë (shpalljen); All-llahu di ç’publikoni dhe ç’bani fshehtë.');
INSERT INTO "ahmeti" VALUES(5,100,'5:100','Thuaj: “Nuk është e njejtë e keqja dhe e mira, po edhe nëse të mahnit ty shumimi i së keqes, pra kini frikë All-llahun (e mos pranoni të keqen) o ju të zotët e mendjes, ashtu që të shpëtoni.');
INSERT INTO "ahmeti" VALUES(5,101,'5:101','O ju që besuat, mos pyetni për sende që, nëse u thuhet ajo (pyetja) haptazi, ju vjen keq juve, e nëse pyetni për to gjatë kohës kur zbritej Kur’ani, do t’ju dalë në shesh (dhe do ta ngarkoni veten pa nevojë). All-llahu u fali (pyetjet që i bëtë më herët); All-llahu falë shumë, është i bute.');
INSERT INTO "ahmeti" VALUES(5,102,'5:102','Një popull që ishte para jushpat bërë aso pyetjesh, e pastaj (nuk i zbatoi) u bë mohues i tyre.');
INSERT INTO "ahmeti" VALUES(5,103,'5:103','All-llahu nuk emëroi (për të shenjtë) Behirën, as Saibën, as Vesilën, dhe as Hamën, porata që nuk besuan trillojnë rrenë për All-llahun, (duke thënë se All-llahu i përcaktoi) dhe shumica e tyre nuk kuptojnë (se janë duke shpifur).');
INSERT INTO "ahmeti" VALUES(5,104,'5:104','E kur ju thuhet atyre: “Ejani te ajo që zbriti All-llahu dhe te (çka thotë) i dërguari, ata thonë: “Na mjafton ajo që igjetëm prindërit tanë”, (a mjafton) edhe nëse prindërit e tyre ishiin që nuk dinin asgjë dhe nuk ishin në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(5,105,'5:105','O ju që besuat, ruane veten tuaj! Ai që ka humbur, nuk ju dëmton juve kur jeni në rrugë të drejtë. Kthimi i të gjithë juve është te All-llahu dhe Ai ju njohton për atë që vepruat.');
INSERT INTO "ahmeti" VALUES(5,106,'5:106','O ju që besuat, dëshmimi me rastin e atij që lë testament (vasijjet) kur i është afruar ndonjërit prej jush vdekja, bëhet me dy dëshmitarë të drejtë nga mesi juaj, ose nga dy të tjerë pos jush, nëse jeni në udhëtim (në rrugë e s’keni të afërm) dhe ju gjen telashja e vdekjes. E nëse dyshoni (në dëshminë e tyre) i ndalni ata të dy pas namazit (të iqindisë) dhe ata betohen në All-llahun: “Ne nuk e japim betimin në All-llahun për asgjë edhe sikur të jetë (ai për të cilin betohemi) i afërm dhe nuk e fshehim dëshminë e (porositur prej) All-llahut, pse atëherë ne do të jemi mëkatarë!”');
INSERT INTO "ahmeti" VALUES(5,107,'5:107','E nëse dihet se ata të dy kanë merituar mëkat (kanë gënjyer) atëherë në vend të tyre merren dy të tjerë prej atyre që kanë meritë (meritë trashëgimi) dhe ata të dy betohen në All-llahun: “Dëshmimi ynë është më i drejtë nga dëshmimi i atyre (dy të parëve), dhe ne nuk e tejkaluam (drejtësinë), pse atëherë do të ishim nga mizorët!”');
INSERT INTO "ahmeti" VALUES(5,108,'5:108','Ky (rregull) është më afër që të bëjnë dëshmimin (dëshmitarët e testamentit) ashtu si është drejt, dhe që të frikësohen (dëshmitarët trashëgimtarë) se po thyhet betimi i tyre me një betim tjetër. Kini frikë All-llahun dhe bindjuni, se All-llahu nuk vë në rrugë të drejtë popullin shkatërrues.');
INSERT INTO "ahmeti" VALUES(5,109,'5:109','(Përkujtonie o njerëz) Ditën kur All-llahu i tubon të dërguarit e u thotë: “çfarë përgjigje (popujt) u dhanë juve?” Ata (pejgamberët) thonë: “Ne nuk dimë, vërtet Ti je që i di të gjitha të fshehtat!”');
INSERT INTO "ahmeti" VALUES(5,110,'5:110','All-llahu (atë ditë) i thotë: “O Isa, bir i merjemes, përkujtoj të mirat e Mia ndaj teje dhe ndaj nënës sate, kur të fuqizova me shpirtin e shenjtë (Xhibrilin), e ti u fole njerëzve (kur ishe) në djep dhe (kush ishe) i pjekur (si burrë), kur ta mësova ty librih e urtësinë Tevratin e Inxhilin, kur melejen Time formove nga balta si formë shpeze e i fryve asaj dhe me urdhërin Tim u bë shpezë, kur e shërove të verbërin dhe të sëmurin nga sëmundja e lëkurës me dëshirën Time, kur me urdhërin Tim i nxore (të gjallë) të vdekurit, kur i zbrapa beni israilët prej teje (që deshën të të mbysin) atëherë kur erdhe me argumente, e disa prej tyre që nuk besuan than: “Kjo (mrekullia e Isait) nuk është tjetër vetëm se magji e qartë!”');
INSERT INTO "ahmeti" VALUES(5,111,'5:111','Dhe kur i frymëzova Havarijjunët (i urdhërova): “Të më besoni Mua dhe të dërguarin Tim!” E ata thanë: “Ne besuam, e ti dëshmo se ne jemi muslimanë”.');
INSERT INTO "ahmeti" VALUES(5,112,'5:112','Dhe (përkujto) kur Havarijjunët thanë: “O Isa, bir i Merjemes, Zoti ta mund të na zbresë një tryezë nga qielli?” (Isai) tha: “Keni frikë All-llahun nëse jeni besimtarë!”');
INSERT INTO "ahmeti" VALUES(5,113,'5:113','(Ata) Thanë: “Ne dëshirojmë të hamë nga ajo dhe të na binden (edhe më shumë) zemrat tona dhe të vërtetojmë bindshëm se na e the të vërtetën e të bëhemi dëshmues të saj!”');
INSERT INTO "ahmeti" VALUES(5,114,'5:114','Isai, bir i Merjemes, tha: “O All-llah, Zoti ynë, zbritna nga qielli një tryezë, të na jetë festë (gëzim) për ne dhe për ata (që vijnë) pas nesh, të jetë argument prej Teje, dhe dhurona se Ti je furnizuesi më i mirë!”');
INSERT INTO "ahmeti" VALUES(5,115,'5:115','All-llahu tha: “Unë atë ua zbres, e kush prej jush mohon pastaj, Unë atë e dënoj me një dënim që nuk e dënoj asnjë nga njerëzit”.');
INSERT INTO "ahmeti" VALUES(5,116,'5:116','Dhe kur All-llahu tha: “O Isa, bir i Merjemes, a ti njerëzve u the: “Më besoni mua dhe nënës time dy zota pos All-llahut!?” (Isai) Tha: “Larg asaj të mete je Ti (o Zoti im). Nuk më takon mua ta them atë që s’është e vërtetë. Ta kisha thënë unë atë, ti do ta dije. Ti e di ç’ka në mua, e unë e di ç’ka në Ty. Ti je më i dijshmi i të fshehtave!”');
INSERT INTO "ahmeti" VALUES(5,117,'5:117','Unë nuk u kam thënë tjetër atyre, pos asaj që Ti më urdhërove; ta adhuroni All-llahun, Zotin tim dhe Zotin tuaj, dhe sa isha ndër ta, kam qenë përcjellës i tyre, e pasi që më more mua, Ti ishe roje (dhe dëshmues) i tyre. Ti je dëshmitar për çdo send!”');
INSERT INTO "ahmeti" VALUES(5,118,'5:118','Nëse i dënon ata, në të vërtetë ata janë robër Tu, e nëse u falë atyre, Ti je i gjithëfuqishmi, i urti.');
INSERT INTO "ahmeti" VALUES(5,119,'5:119','All-llahu tha: “kjo është dita që të drejtëve u bën dobi drejtësia e tyre. Ata kanë Xhennete në të cilët rrjedhin lumenj, janë për jetë të pasosur në ta. All-llahu është i kënaqur me ta dhe ata janë të kënaqur ndaj Tij. Ky është shpëtim i madh.');
INSERT INTO "ahmeti" VALUES(5,120,'5:120','Vetëm i All-llahut është sundimi ndaj qiejve e tokës dhe çka ka në to. Ai është i plotëfuqishëm për çdo send.');
INSERT INTO "ahmeti" VALUES(6,1,'6:1','Falenderimi i qoftë vetëm All-llahut, që krijoi qiejt dhe tokën, që formuloi errësirat e dritën, e megjithëkëtë (që Ai meriton lavdërim) ata që mohuan, i barazojnë (idhujt) me Zotin e tyre.');
INSERT INTO "ahmeti" VALUES(6,2,'6:2','Ai është që ju krijoi nga balta, e mandej ju caktoi një afat (për vdekje) dhe një afat është i caktuar pranë dijes së Tij, dhe pas (gjithë kësaj të vërtete) ju dyshoni.');
INSERT INTO "ahmeti" VALUES(6,3,'6:3','Ai është All-llahu (që adhurohet e madhërohet prej çka kanë tokë; Ai i di fshehtësitë dhe publikimet tuaja dhe Ai e di atë që bëni.');
INSERT INTO "ahmeti" VALUES(6,4,'6:4','Nuk ka argument që u vjen atyre nga argumentet e Zotit të tyre, eqë ata nuk i shmangen.');
INSERT INTO "ahmeti" VALUES(6,5,'6:5','Ata përgënjeshtruan të vërtetën kur u erdhi, e më vonë do të kuptojnë lajmin e asaj me të cilën talleshin.');
INSERT INTO "ahmeti" VALUES(6,6,'6:6','A e vrejtën ata sa gjenerata para tyre i shkatërruam që Ne u patëm mundësuar atyre (komoditet) në tokë çfarë juve nuk ju mundësuam; u patëm lëshuar nga qielli shi me bollëk dhe u patëm bërë që të rrjedhin lumenj ndër ta, e për shkak të mëkateve të tyre i shkatërruam dhe pas tyre sollëm (breza) tjerë.');
INSERT INTO "ahmeti" VALUES(6,7,'6:7','Edhe sikur të zbritnim ty një libër të shkruar në letër, e ta preknin atë me duart e tyre, ata që mohuan do të thoshin: “Kjo nuk është tjetër përveç një magji e qartë”.');
INSERT INTO "ahmeti" VALUES(6,8,'6:8','E pastaj thanë: “Pse të mos i zbret atij (Muhammedit) një engjëll (të na thotë për Muhammedin se është i dërguar)”. E sikur të zbritnim Ne një engjëll, çështja do të merrte fund duke mos u dhënë atyre një afat.');
INSERT INTO "ahmeti" VALUES(6,9,'6:9','Sikur ta bënim Ne atë (të dërguarin) engjëll, atë do ta bënim (në formë) njeriu e do t’ua përzienim (ngatërronim) atyre atë që i përzien ata vetes së tyre.');
INSERT INTO "ahmeti" VALUES(6,10,'6:10','Edhe të dërguarit të tjerë para teje janë përqeshur, e ata që u tallën pësuan (dënim) për shkak se talleshin.');
INSERT INTO "ahmeti" VALUES(6,11,'6:11','Thuaj: “Ecni nëpër tokë e mandej shikoni se si qe përfundimi i gënjeshtarëve.');
INSERT INTO "ahmeti" VALUES(6,12,'6:12','Tuaj: “E kujt është e tërë kjo në qiej e tokë?” Thuaj: “Vetëm e All-llahut”. Ai ia përaktoi mëshirën Vetes. Ai do t’ju tubojë në ditën e gjykimit, për të cilën gjë nuk ka dyshim. Ata që i shkaktuan humbje vetvetes, ata nuk besojnë.');
INSERT INTO "ahmeti" VALUES(6,13,'6:13','Vetëm e Tij është gjithëçka pushon e lëviz natën e ditën. Ai gjithëçka dëgjon dhe gjithëçka di.');
INSERT INTO "ahmeti" VALUES(6,14,'6:14','Thuaj: “A pos All-llahut, krijues i qiejvedhe i tokës, që ushqen të tjerët, ndërsa vetë nuk ushqehet, të pranoj Zot tjetër?” Thuaj: “Unë jam urdhëruar të jem i pari që bindem dhe (urdhërohem): të mos bëhem kurrsesi nga idhujtarët.');
INSERT INTO "ahmeti" VALUES(6,15,'6:15','Thuaj: “Vërtet, unë i frikësohem dënimit të ditës sëmadhe, nëse nuk i përulem Zotit tim”.');
INSERT INTO "ahmeti" VALUES(6,16,'6:16','Kush mënjanohet atë ditë nga ai (dënimi), atë Ai e ka mëshiruar, dhe ai është shpëtim i qartë.');
INSERT INTO "ahmeti" VALUES(6,17,'6:17','Nëse All-llahu të godet me ndonjë të keqe, s’ka kush që ta largojë atë pos Tij, e nëse të dhuron ndonjë të mirë, duhet ditur se Ai është i gjithëfuqishëm për çdo send.');
INSERT INTO "ahmeti" VALUES(6,18,'6:18','Ai është mbizotërues ndaj robërve të Tij. Ai hollësisht di punët e Tij dhe të robërve të Vet.');
INSERT INTO "ahmeti" VALUES(6,19,'6:19','Thuaj: “Cili send ka dëshminë më të madhe?” Thuaj: “All-llahu është dëshmues mes meje e jush, e mua më është shpallur ky Kur’an që me të t’ju tërheqë vërejtjen juve dhe atij që i komunikohet (dhe të gjithë atyre që vijnë pas jush deri në ditën e kijametit). Ju po dëshmoni se All-llahut ka edhe zota tjerë, a?” Thuaj: “Unë nuk dëshmoj!” Ai është vetëm një Zot dhe unë jam i pastër nga ajo që j u i shoqëroni!”');
INSERT INTO "ahmeti" VALUES(6,20,'6:20','Atyre që Ne u kemi dhënë librin, e njohin atë (Muhammedin) sikurse i njohin bijtë e tyre. Janë ata që asgjësuan vetveten, andaj nuk besojnë.');
INSERT INTO "ahmeti" VALUES(6,21,'6:21','Kush është më mizor se ai që ndaj All-llahut shpif gënjeshtra apo ka përgënjeshtruar faktet e Tij? S’ka dyshim, shpifësit dhe gënjeshtarëtnuk kanë shpëtim.');
INSERT INTO "ahmeti" VALUES(6,22,'6:22','Përkujto ditën kur Ne i tubojmë të gjithë dhe u themi atyre që i shoqëruan: “Ku janë ata të shoqëruarit tuaj, që i trillonit?”');
INSERT INTO "ahmeti" VALUES(6,23,'6:23','Atëherë (pas atij sprovimi) përgjigje tjetër s’u mbetetpërveç të thonë: “Për All-llahun, o zoti ynë, ne nuk ishim dhujtarë!”');
INSERT INTO "ahmeti" VALUES(6,24,'6:24','Shih, se si bëjnë gënjeshtra kundër vetes dhe si u shkoi huq ajo që trillonin!');
INSERT INTO "ahmeti" VALUES(6,25,'6:25','Ka prej atyre që ty të dëgjon (kur lexon Kur’anin). Po Ne kemi krijuar mbulesë mbi zemrat e tyre që të mos e kuptojnë atë dhe në veshët e tyre sajuam shurdhim, dhe edhe dikur t’i shohim të gjitha faktet. ata nuk besojnë, derisa kur te ti e të polemizojnë ata që mohuan thonë: “Nuk është tjetër ky (Kur’ani) vetëm se mit i hershëm.');
INSERT INTO "ahmeti" VALUES(6,26,'6:26','Ata ndalojnë (të tjerët) nga ai dhe vetë largohen prej atij (Kur’anit), e me atë nuk shkatërrojnë tjerët vetëm vetveten, dhe nuk e hetojnë.');
INSERT INTO "ahmeti" VALUES(6,27,'6:27','E sikur t’i shihje ata kur janë ndalur pranë zjarrit e thonë: “Ah sikr të ktheheshim (në dynja), të mos gënjejmë faktet e Zotit toë e të bëhemi nga besimtarët!”');
INSERT INTO "ahmeti" VALUES(6,28,'6:28','Jo, (s’është ashtu) po atyre u doli në shesh ajo që e mbanin fshehtë më parë, prandaj edhe sikur të ktheheshin ata do të përsëritnin atë që e kishin të ndaluar, e s’ka dyshim, ata janë gënjeshtarë.');
INSERT INTO "ahmeti" VALUES(6,29,'6:29','Ata thanë: “Nuk ka tjetër, vetëm kjo jeta jonë në këtë botë, dhe ne nuk do të rigjallemi.');
INSERT INTO "ahmeti" VALUES(6,30,'6:30','Dhe, sikur t’i kishte parë ata kur të ndalohen para Zotit të tyre e u thuhet atyre: “A nuk është kjo (ringjalja) e vërtetë?” Ata thonë: “Po, për zotin tonë!” thotë: “Shijoni pra dënimin për atë që mohonit!”');
INSERT INTO "ahmeti" VALUES(6,31,'6:31','Ata që përgënjeshtruan se do të takohen me All-llahun (në ditën e gjykimit) kanë humbur përderisa t’u vijë momenti (kijameti) befas e të thonë: “të mjerët ne për atë që lëshuam” (nga punët e mira në dynja), e duke i bartur gabimet e veta në shpinë, dhe, e shëmtuar është ajo që bartin.');
INSERT INTO "ahmeti" VALUES(6,32,'6:32','Jeta e kësaj bote nuk është gjë tjetër vetëm se një përjetim e mashtrim. S’ka dyshim se bota tjetër është më e dobishmja për ata që ruhen. A nuk logjikoni?');
INSERT INTO "ahmeti" VALUES(6,33,'6:33','Ne dimë se ty të brengos ajo që thonë, e ata nuk të gënjejnëty, por ata mizorë mohojnë argumentet e All-llahut.');
INSERT INTO "ahmeti" VALUES(6,34,'6:34','Janë përgënjeshtruar të dërguar para teje, duruan atë gënjeshtër, dhe u torturuan përderisa u erdhi ndihma Jonë, prandaj s’ka kush që ndryshon fjalët (premtimet) e All-llahut. E ti je i njohur me disa nga ngjarjet e të dërgarve (edhe ti do të ndihmohesh sikurse ata).');
INSERT INTO "ahmeti" VALUES(6,35,'6:35','Por, nëse refuzimi i tyre të është bërë brengë e madhe, ti kërkoje, nëse ke mundësi, ndonjë vrimë në tokë ose në qiell e t’u sjellësh atyre ndonjë argument (po ti s’mund ta bësh këtë). Të kishte dashur All-llahu i kishte tubuar në rrugë të drejtë; po ti mos u bë nga të padijshmit (dhe mos u brengos).');
INSERT INTO "ahmeti" VALUES(6,36,'6:36','vetëm ata që kanë dëgjim të shëndosh i përgjigjen (ftesës). Të vdekurit (ata që dëgjojnë dhe nuk besojnë) All-llahu i ringjall, e pastaj te Ai kthehen (u jepatë që e meritojnë).');
INSERT INTO "ahmeti" VALUES(6,37,'6:37','Dhe thanë: “Përse të mos i zbret atij (Muhammedit) një mrekulli nga Zoti i tij?” Thuaj: “S’ka dyshim se All-llahu ka fuqi t’ia zbresë një mrekulli, por shumica e tyre nuk e dinë (se çka do t’i gjente më pas).');
INSERT INTO "ahmeti" VALUES(6,38,'6:38','Nuk asnjë gjallesë në tokë dhe as shpendë që fluturojnë me dy krahë e që nuk janë të nara në grupe (të ndryshme), sikurse edhe ju (Zoti i krijoi, i pajisi si juve). Asgjë nuk kemi lënë pas dore nga evidenca. Më në fund te Zoti i tyre do të tubohen.');
INSERT INTO "ahmeti" VALUES(6,39,'6:39','E ata që i bënë të rreme faktet Tona janë shurdmemecë të mbetur në errësira. All-llahu e humb kë të dojë e vë në rrugë të drejtë atë që do.');
INSERT INTO "ahmeti" VALUES(6,40,'6:40','Thuaju: “Nëse u vjen dënimi nga All-llahu ose u vjen kijameti, më tregoni, nëse jeni të sinqertë, a do të thirrni kë pos All-llahut (për ndihmë?”');
INSERT INTO "ahmeti" VALUES(6,41,'6:41','Jo, vetëm Atij (All-llahut) do t’i luteshi, e nëse do Ai, ju heq atë për çka i luteni, e do t’i harronit ata (zota) që ia shoqëronit.');
INSERT INTO "ahmeti" VALUES(6,42,'6:42','Ne kemi dërguar (pejgamberë) edhe te popjt parateje (e derisa nuk dëgjuan), i dënuam më skamje emjerim, ashtu që të përulen.');
INSERT INTO "ahmeti" VALUES(6,43,'6:43','E pse të mos përuleshin kur ju erdhi atyre dënimijonë? Por zemrat e tyre ishin ngurosor, po edhe djalli ua hjeshoi atë që bënin.');
INSERT INTO "ahmeti" VALUES(6,44,'6:44','Meqë lanë pas dore atë me çka u këshilluan (t’i i drejtohen Zotit), Ne ua hapëm dyrt e çdo gjëje (begatie) derisa kur u gëzuan për atë që ju kishte dhënë, i kapëm befas, e ata mbetën të zhgënjyer.');
INSERT INTO "ahmeti" VALUES(6,45,'6:45','Ndaj u zhduk mbeturina e popullit mizor, pra Falënderim i qoftë Zotittë botave.');
INSERT INTO "ahmeti" VALUES(6,46,'6:46','Thuaj: “Nëse All-llahu ua merr të dëgjuarit, të pamurit dhe ua mbyll zemrat, pos All-llahut cili zot tjetër do t’ju sjellë atë?”. Shih sesi ua shqarojmë faktet e megjithkëtë ata nuk ua vënë veshin!');
INSERT INTO "ahmeti" VALUES(6,47,'6:47','Thuaj: “Më tregoni nëse dënimi nga All-llahut u vjen befas ose me parashenja, a shkatërrohet kush tjetër pos popullit mizor?”');
INSERT INTO "ahmeti" VALUES(6,48,'6:48','Ne të dërguarit nuk i dërgojmë ndryshe vetëm si përgëzues dhe qortues. E kush besoi dhe u përmirësua, ata nuk kanë as frikë as brengë.');
INSERT INTO "ahmeti" VALUES(6,49,'6:49','Ata që i përgënjeshtruan faktet tona, ata i kap dënimi ngase nuk respektuan normat.');
INSERT INTO "ahmeti" VALUES(6,50,'6:50','Thuaj: “Unë nuk u them juve se i kam në kompetencë depotë e All-llahut (e t’ju sjellë mrekulli), as nuk pretendoj se i di fshehtësitë (e t’ju tregojë se kur do t’ju vijë dënimi), as nuk u them se unë jam engjëll. Unë ndjek vetëm atë që më shpallet mua. Thuaj: “A janë baras i verbëti dhe ai që sheh?” A nuk mendoni?');
INSERT INTO "ahmeti" VALUES(6,51,'6:51','Ti tërhiqju vërejtjen me këtë (Kur’an) atyre që i frikësohen tubimit para zotit të tyre, në mënyrë që të ruhen, sepse pos Tij nuk do të ketë as mbrojtës as ndërmjetësues.');
INSERT INTO "ahmeti" VALUES(6,52,'6:52','Dhe mos i përze ata që adhurojnë Zotin e tyre pa pra mëngjes e mbrëmje, duke qenë të sinqertë ndaj Tij. Ti nukpërgjigjesh asgjë nga llogaria e tyre e as ata nuk kanë kurrfarë përgjegjësie nga llogaria jote, e po i dëbove ata do të bëhesh nga të padrejtit.');
INSERT INTO "ahmeti" VALUES(6,53,'6:53','Kështu Ne i sprovojmë disa me disa tjerë (pasanikun me varfnjakun, atë me autoritet me atë pa të) ashtu që të thonë: “A këto janë mes nesh qëAll-llahu i dhuroi?” A nuk është All-llahu më i dijshmi për ata që janë mirënjohës?!');
INSERT INTO "ahmeti" VALUES(6,54,'6:54','E kur të vijnë ty ata që i besojnë ajetet tona, thuaju: “Selamu alejkum, Zoti juaj ia ngjeshi vetes mëshirën. Kuh bën prej jush ndonjë ë keqe pa dije, e mandej pas asaj (të keqe) pehdohet dhe përmirësohet, s’ka dyshim se All-llahu është që falë shumë dhe është Mëshirues.');
INSERT INTO "ahmeti" VALUES(6,55,'6:55','Ja, kështu Ne i sqarojmë argumentet, në mënyrë që të dalë në shesh rruga e kriminelëve.');
INSERT INTO "ahmeti" VALUES(6,56,'6:56','Thuaj: “Unë jam i ndaluar të adhuroj ata që adhuroni ju, pos All-llahut! thuaj: “Unë nuk njeki dëshirat tuaja, pse do të ishte atëherë do te isha i humbur e jo prej të udhëzuarve (në rrugë të drejtë).');
INSERT INTO "ahmeti" VALUES(6,57,'6:57','Thuaj: “Unë i përmbahem të vërtetës (që më erdhi) nga Zoti im. Ju e gënjyet atë, e për atë që nguteni ju (dënimin), nuk e bëj unë, vendimi i takon vetëm All-llahut; Ai e rrëfen të vërtetën dhe Ai është më i miri i gjykatësve.');
INSERT INTO "ahmeti" VALUES(6,58,'6:58','Thuaj: “Sikur të ishte ajo për të cilën nguteni j te unë, çështja mes meje dhe jush do të ishte e përfunduar. All-llahu më së miri i di për mizorët.');
INSERT INTO "ahmeti" VALUES(6,59,'6:59','çelësat e fshehtësive janë vetëm te Ai, atë (fshehtësinë) nuk e di kush pos Tij. Ai e di çka ka në tokë dhe në det, Ai e di për çdo gjeth që bie dhe s’ka kokërr në thellësi të tokës, s’ka të njomë dhe s’ka të thatë që nuk është (shënuar) në librin e qartë (LehviMahfud).');
INSERT INTO "ahmeti" VALUES(6,60,'6:60','Ai është që ju vë në gjumë natën dhe e di çka vepruat ditën, pastaj ju ngjall-zgjon në të (ditën) për deri në afatin e caktuar (vdekje). Pastaj do të ktheheni e do t’ju njohtojë me atë që keni pas vepruar.');
INSERT INTO "ahmeti" VALUES(6,61,'6:61','Vetëm Ai është mbizotërues ndaj robërve të vet. Ndaj juve dërgon rojë (cakton engjëj) derisa kur t’i vijë ndonjërit prej jush vdekja, atij ia marrin shpirtin të dërguarit tanë (të tjerë) dhe ata nuk bëjnë kurrfarë lëshimi.');
INSERT INTO "ahmeti" VALUES(6,62,'6:62','Pastaj i kthehen All-llahut, Snduesit të vërtetë të tyre. Vetëm i tij është sundimi (gjykimi) dhe Ai është më i shpejti i llogaritësve.');
INSERT INTO "ahmeti" VALUES(6,63,'6:63','Thuaj: “Kush ju spëton prej errësirave (trishtuese) të tokës e të detit e (nga trishtimi) ju e lutni Atë haptas e fshehtas (duke thënë), nëse Ai na shpëton prej kësaj, ne do të jemi (besimtarë) mirënjohës?”');
INSERT INTO "ahmeti" VALUES(6,64,'6:64','Thuaj: “All-llahu ju shpëtin nga ajo dhe nga çdo brengosje. Megjithëkëtë ju Atij i përshkruani shok.');
INSERT INTO "ahmeti" VALUES(6,65,'6:65','Thuaj: “Ai ka fuqi (t’ju shpëtojë, por edhe) t’ju sjellë dënim prej së larti ose prej së poshti nën këmbët tuaja apo t’ju ndaj në grupe e ta luftoni njëri-tjetrin. Shih se si i sqarojmë faktet në mënyrë që të kptojnë”.');
INSERT INTO "ahmeti" VALUES(6,66,'6:66','E populli yt e konsideroi atë (Kur’anin) të rremë, porse ai (Kur’ani) është i vërtetë. Thuaj: “Unë nuk jam rojë e juaj”.');
INSERT INTO "ahmeti" VALUES(6,67,'6:67','çdo lajm e ka afatin e realizimit, e më vonë, ju do të kuptoni.');
INSERT INTO "ahmeti" VALUES(6,68,'6:68','Kur sheh ata se janë thelluar (me tallje) në çështjet Tona, largohu prej tyre derisa të kalojnë në bisedë tjetër. Nëse djalli të bën të harrosh (e rri me ta), pasi të bie ndër mend, mos rri me popullin mizor.');
INSERT INTO "ahmeti" VALUES(6,69,'6:69','Për ata që ruhen (për besimtarët) s’ka kurrfarë përgjegjësie (që tallen), por duhet t’ju përkujtojmë në mënyrë që ruhen edhe ata (që tallen).');
INSERT INTO "ahmeti" VALUES(6,70,'6:70','Hiqu atyre që e marrin fenë (në vend që ta resprktojnë) për lojë e dëfrim dhe i ka mashtruar jeta e kësaj bote. Ti përkujto me të (Kur’anin) që të mos bjerë njeriu viktimë e asaj që ka veprruar, e që s’ka mbrojtës as ndërmjetësues për te pos All-llahut. je ai (njeri) edhe nëse jep, çdo lloj shpagimi nuk i pranohet. Të tillët janë ata që ranë viktimë e asaj që punuan. Ata, për skak se mohuan, për pije kanë ujë të vluar e dënim të idhët.');
INSERT INTO "ahmeti" VALUES(6,71,'6:71','Thuaj: “A pos All-llahut të adhurojmë çka nuk na sjell as dobi as dëm dhe të kthehemi mbrapa (në kufur) pasi që All-llahu na vuri në rrugë të drejtë? Dhe atëherë të bëhemi sikur ai, të cilin djallëzit e kanë rrëmbyer (e kanë hedhur) në tokë (në një humnerë) e lënë të hutuar që, edhe pse ai ka shokë që e thërrasin në rrugë të drejtë (i thonë): “Eja te ne” (ai nuk përgjigjet). Thuaj: “I vetmi udhëzim është ai udhëzimi i All-llahut, dhe se jemi urdhëruar që t’i dorëzohemi zotit të botëve.');
INSERT INTO "ahmeti" VALUES(6,72,'6:72','(Na është thënë) Dhe falne nmazin dhe kini frikë prej Tij, se Ai është te i cili do të tubohemi.');
INSERT INTO "ahmeti" VALUES(6,73,'6:73','E Ai është që krijoi qiejt dhe tokën me qëllim të caktuar, e (ruajuni dënimit) ditën kur thotë: “Bëhu”! Ajo bëhet. Fjala e Tij është e njëmendtë dhe Atij i takon sundimi ditën kur i fryhet “Surit” (kjo është hera e dytë). Ai e di të fshehtën dhe konkretën, është më i urti që di për çdo gjë në hollësi.');
INSERT INTO "ahmeti" VALUES(6,74,'6:74','Përkujtoju (o i dërguar) kur Ibrahimi i tha vëllaut të vet Azerit: “A statuja (idhuj) adhuroni për zota? Unë po të shoh ty dhe popullin tënd në një humbje të sigurt.');
INSERT INTO "ahmeti" VALUES(6,75,'6:75','E kështu Ibrahimit ia mundësuam t’i shohë madhësitë e qiejve e të tokës për t’u bërë edhe më i bindur.');
INSERT INTO "ahmeti" VALUES(6,76,'6:76','E kur atë e mbuloi nata, ai e pa një yll e tha: “Ky është Zoti im!” E kur u zhduk ai (perëndoi) tha:” Unë nuk i dua ata që humbën”.');
INSERT INTO "ahmeti" VALUES(6,77,'6:77','Kur e pa hënën të posa tha: “Ky është Zoti im! e kur perëndoi ajo, tha: Nëse Zoti im nuk më udhëzon, unë do të jem prej njerëzve të humbur!”');
INSERT INTO "ahmeti" VALUES(6,78,'6:78','Kur e pa diellin të lindur, tha: “Ky është Zoti im, ky është i madh!” e kur ai perëndoi, tha: O populli im, unë jam i pastërnga ajo që ju i shoqëroni!”');
INSERT INTO "ahmeti" VALUES(6,79,'6:79','Unë me veten time i drejtohem Atij që krijoi qiejt e tokën, larg besimeve të tjera; unë nuk jam prej atyre që i përshkruajnë shok!');
INSERT INTO "ahmeti" VALUES(6,80,'6:80','Po atë e (me Ibrahimin) polemizoi populli i tij e ai tha: “A polemizoni me mua, rreth All-llahut e Ai më udhëzoi?” Unë nuk u frikohem atyre që ju ia bëni shok, vetëm nëse Zoti im do ndonjë send (të më godas, ai më godet). Me dijen e Tij Zoti im ka përfshirë çdo send, a nuk e merrni me mend?”');
INSERT INTO "ahmeti" VALUES(6,81,'6:81','E si t’ju frikohem atyre që ju ia shoqëruat, e ju frikoheni për atë që i shoqëruat All-llahut pa pasur kurrfarë argumenti. E cili grup, pra, është më e drejtë të jetë i sigurt, nëse jeni që kuptoni?');
INSERT INTO "ahmeti" VALUES(6,82,'6:82','Ata që besuan dhe besimin e tyre nuk e ngatërruan me besim të kotë, atyre u takon të jenë të sigurt dhe atajanë në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(6,83,'6:83','Këto janë argumentet tona që ia dhamë Ibrahimit kundër popullit të tij. Ne ngrisim në shkallë të lartë atë që duam. Zoti yt çdo send e vë në vendin e vet, asgjë nuk mund t’i fshehet.');
INSERT INTO "ahmeti" VALUES(6,84,'6:84','Ne atij (Ibrahimit) i falëm Is-hakun dhe Jakubin dhe secilin prej tyre e udhëzuam. Më parë edhe Nuhun e patëm udhëzuar. E nga pasardhësit e tij (të Ibrahimit) udhëzuam Davudin, Sulejmanin, Ejjubin, Jusufin, Musain dhe Harunin. Kështu i shpërblejmë bamirësat.');
INSERT INTO "ahmeti" VALUES(6,85,'6:85','(shpërblyem) Edhe Zekerijanë, Jahjanë, Isain, Iliajasin të gjithë prej të mirëve të përsosur.');
INSERT INTO "ahmeti" VALUES(6,86,'6:86','(shpërbylem) Edhe Ismailin, Eljesanë, Junusin dhe Lutin. Të gjithë këta i veçuam mbi njerëzit tjerë.');
INSERT INTO "ahmeti" VALUES(6,87,'6:87','Edhe disa nga prindërit, nga pasardhësit dhe nga vëllezërit e tyre i zgjodhëm (për pejgamberë) dhe i udhëzuam në rrugë të vërtetë.');
INSERT INTO "ahmeti" VALUES(6,88,'6:88','Ky është udhëzim i All-llahut, udhëzon me të kë të dojë nga robët e Tij. E sikur t’i përshkruajnë shok Zotit (edhe ndonjë nga këta) kishte për t’iu shkuar huq ajo që kanë vepuar.');
INSERT INTO "ahmeti" VALUES(6,89,'6:89','Ata shin që u patëm dhënë librin, urtësinë e pejgamberllëkun; e nëse këta (idhujtarët mekas) e refuzojnë këtë (pejgamberllëkun tënd), Ne e kemi siguruar këtë me një popull që nuk e refuzon.');
INSERT INTO "ahmeti" VALUES(6,90,'6:90','Ata (të dërguarit e përmedur) ishin që All-llahu i vuri në rrugë të drejtë, andaj ti merri shembull në udhëzim. Thuaj: “Unë nuk kërkoj për këtë (komunikimin e Kur’anit) shpërblim prej jush. Ky nuk është tjetër, përveç një këshillë për mbarë njerëzit.');
INSERT INTO "ahmeti" VALUES(6,91,'6:91','Ata (mohuesit) nuk e njohën All-llahun sa duhet njohur Atë kur thanë; “All-llahu nuk i shpalli gjë asnjë njeriu!” Thuaj: “Kush e zbriti librin, me të cilin erdhi Musai e që ishte dritë e udhërrëfyes për njerëz, e të cilin ju e bëni të shpërndarë në letra, që disa i prezentoni, kurse shumicën e fshehni. Dhe u mësuat çka nuk e dinit ju as prindërit tuaj? Thuaj: (e zbriti) All-llahu”. Mandej lëri ata që luajnë në atë kotësunë e tyre.');
INSERT INTO "ahmeti" VALUES(6,92,'6:92','Edhe ky (Kur’an) është libër që e zbritëm; është i bekuar, vërtetues i të mëparshmes, e që t’i tërheqesh vërejtjen nënës së fshatrave (të banorëve mekas) dhe atyre përrreth saj (mbarë botës). Ata që e besojnë Ahiretin, besojnë në të (Kur’anin), ata edhe e falin namazin rregullisht.');
INSERT INTO "ahmeti" VALUES(6,93,'6:93','E kush është më gabimtar i madh se sa ai që trillon rrenë ndaj Zotit, ose thotë: “Mua po më shpallet e nuk i është shpallur asgjë, ose se sa ai që thotë: “Do të thur diçka të ngjajshme me atë që e ka zbritur All-llahu”. E, sikur t’i shihje mizorët kur janë në agoni të vdekjes, e engjëjt kanë shtrirë duart e veta (me ndëshkim) e (u thonë): “Shpëtonie pra vetveten (nëse mundeni)”. “Tash përjetoni dënimin e turpshëm për shkak se e thoshit të pavërtetën për All-llahun, dhe ndaj argumrnteve të Tij ishit kryeneç.');
INSERT INTO "ahmeti" VALUES(6,94,'6:94','Në të vërtetë ju na erdhët një nga një (të vetmuar), ashtu si ju krijuam së pari; keni lënë prpa shpinës atë që u patëm dhënë, e nuk po shohim se i keni me vete ndërmjetësuesit tuaj, të cilët i mendonit se për ju janë ndihmëtarë (i shoqëronit All-llahut). S’ka dyshim, është këputur lidhja mes jush, dhe mbaroi ajo çka mendonit (ndërmjjetësues apo zotëra tjerë).');
INSERT INTO "ahmeti" VALUES(6,95,'6:95','S’ka dyshim, All-llahu është zbërthyes i farës (i kokrrës së saj) dhe i bërthamës (së pemës). Ai nxjerr të gjallin nga i vdekuri dhe Aiështë nxjerrës i të vdekurit nga i gjalli. Ky është All-llahu, e si atëherë shmangeni (nga besimi)?');
INSERT INTO "ahmeti" VALUES(6,96,'6:96','Ai është krijues i dritës së mëngjesit. Natën e bëri kohë pushimi, e diellin dhe hënën për llogaritje të kohës. Ky (rregull) është caktim i plotfuqishmit, i gjithëdijshmit.');
INSERT INTO "ahmeti" VALUES(6,97,'6:97','Ai është, që krijoi yjet për ju që me ta të orientoheni në errësira kur jeni në tokë ose në det. Vërtet Ne i shpjeguam argumentet tona për një popull që di të mendojë.');
INSERT INTO "ahmeti" VALUES(6,98,'6:98','Ai është që ju krijoi (filloi) prej një njeriu; aty (mbi tokë) jetoni (qëndroni) dhe nën te do të pushoni. Ne i sqaruam argumentet një populli që di të kuptojë.');
INSERT INTO "ahmeti" VALUES(6,99,'6:99','Ai është që lëshoi nga lartë shiun e me të nxori bimën e çdo sendi dhe prej bimës gjelbërim dhe prej tij (prej gjelbërimit) kokrra të dendura në kallinj. E nga hurmet, nga sythat tyre kalaveshë të afërt (për t’i vjelë). Edhe kopshtie me hardhi, ullinj e shegë të ngjajshme (në dukje) e të llojllojshme (në shije). Shikoni pra frutat e tyre kur i formojnë dhe kur piqen (të gjitha këto nga shiu). Edhe në këto ka fakte për njerëzit që besojnë.');
INSERT INTO "ahmeti" VALUES(6,100,'6:100','E xhinët ia bëjnë shokë All-llahut, e në të vërtetë Ai i krijoi ata (xhinët) dhe duke mos patur kurrfarë dije, ata shpifën se Ai (Zoti) ka djem e vajza. Larg (shpifjeve) është madhëria e Tij e lartë.');
INSERT INTO "ahmeti" VALUES(6,101,'6:101','Ai (All-llahu) është që krijo (pa kurrfarë shembulli) qiejt dhe tokën (e duke qenë i tillë), e si do të ketë Ai fëmijë kur nuk pati bashkëshorte? çdo send e krijoi Ai, dhe është më i dijshmi për të gjitha sendet e krijuara.');
INSERT INTO "ahmeti" VALUES(6,102,'6:102','Ky është All-llahu, Zoti juaj, nuk ka të adhuruar përveç Tij, Krijues i çdo sendi, pra adhuronie Atë; Ai është mbikëqyrës ndaj çdo sendi.');
INSERT INTO "ahmeti" VALUES(6,103,'6:103','Tëparët (e njerëzve) nuk mund ta përfshinë Atë, e Ai i ërfshinë të arët. Ai është shumë i kujdesshëm, hollësisht i njohur.');
INSERT INTO "ahmeti" VALUES(6,104,'6:104','Juve ju erdhën argumente të qarta nga Zoti i juaj e kush i sheh (kupton) ai e ka për vete, e kush verbërohet, ai e ka për të zezën e vet. E në (pejgamberi) nuk jamërojë e juaj.');
INSERT INTO "ahmeti" VALUES(6,105,'6:105','e kështu Ne i sqarojmë dëshmitë, ashtu që ata thonë: “Ke mësuar ti” (nga librat, po fjalës së tyre nuk i vihet veshi0 dhe që për t’ia bërë edhe më të qarta një populli që di të dallojë (të vërtetën nga e kota).');
INSERT INTO "ahmeti" VALUES(6,106,'6:106','Ti (Muhammed) praktoko atë që t’u shpall nga Zoti yt. S’ka Zot, përveç Tij. Largohu prej idhujtarëve.');
INSERT INTO "ahmeti" VALUES(6,107,'6:107','E sikur të donte All-llahu, ata nnuk do të ishin idhujtarë. Ne nuk të bëmë përcjellës të tyre e as që je mbikëqyrës i tyre.');
INSERT INTO "ahmeti" VALUES(6,108,'6:108','Ju mos ua shani ata (zota) që u luten (idhujtarët), pos All-llahut, e (si hakmarrje) të fyejnë All-llahun nga armiqësia, duke mos ditur (për madhërinë e Tij). Kështu Ne i kemi zbukuruar çdo populli veprimin e vet, mandej e ardhmja e tyre është te Zoti i tyre, e Ai i shpërble për atë që vepruan.');
INSERT INTO "ahmeti" VALUES(6,109,'6:109','Ata u betuan me një betim të fortë në All-llahun, se nëse uvjen atyre ndonjë mrekulli, do ta besojnë. Thuaj: “çështja e atyre mrekullive është te All-llahu”. E ku e dini ju, ndoshta kur të vijnë ato nuk u besojnë.');
INSERT INTO "ahmeti" VALUES(6,110,'6:110','Ne i rrotullojmë zemrat e të parët e tyre (prej besimit) ashtu sikundër nuk e besuan atë (Kur’anin) për herë të parë, dhe i lëmë të bredhin të hutuar në atë mashtrimin e tyre.');
INSERT INTO "ahmeti" VALUES(6,111,'6:111','E edhe sikur t’ju zbritnim Ne atyre engjëjt, t’ju flitnin të vdekurit, t’ju tubonin atyre çdo send (gjallesë) konkretisht, ata nuk kishin për të besuar, vetëm po të donte All-llahu, por shumica e tyre nuk dinë (se besimi është dhuratë nga Zoti).');
INSERT INTO "ahmeti" VALUES(6,112,'6:112','dhe kështu (sikurse edhe ty) çdo pejgamberi i bëmë armiq disa nga njerëzit dhe nga xhinët e djallëzuar, që me fjalë të shkëlqyeshme në mënyrë të fshehtë nxit njëri-tjetrin në mashtrime. E sikur të donte Zoti yt, ata nuk do të bënin atë (armiqësi), po ti lëri ata me ato trillime.');
INSERT INTO "ahmeti" VALUES(6,113,'6:113','Dhe që të anojnë (te ato fjalë mashtruese) zemrat e atyre që nuk besojnë boën e ardhshme dhe që të kënaqen e ngarkohen me atë që janë duke u ngarkuar (ty nuk të dëmtojnë asgjë).');
INSERT INTO "ahmeti" VALUES(6,114,'6:114','(Thuaj) A pos All-llahut të kërkoj unë gjykatës (mes meje dhe juve)? kur Ai është që zbriti librin në mënyrë të shkoqitur? Atyre që u dhamë librin e dinë se ai (Kur’ani) është i zbritur prej Zotit tënd saktësisht, pra mos u bë prej atyre që dyshojnë.');
INSERT INTO "ahmeti" VALUES(6,115,'6:115','Fjalët e Zotit tënd janë plot të vërteta (çka lajmërojnë) dhe plot të drejta (çka gjykojnë). S’ka kush që të ndryshoj fjalët (vendimet) e Tij. Ai është që dëgjon e di.');
INSERT INTO "ahmeti" VALUES(6,116,'6:116','Në qoftë se u bindesh shumicës (mohuese që janë) në tokë, ata do të largojnë ty nga rruga e All-llahut. Ata nuk ndjekin tjetër vetëm supozime dhe nuk janë tjetër vetëm se rrenacakë.');
INSERT INTO "ahmeti" VALUES(6,117,'6:117','S’ka dyshim, Zoti yt e di më së miri për atë që është larguar rruga e Tijdhe Ai është më i dijshmi për të udhëzuarit.');
INSERT INTO "ahmeti" VALUES(6,118,'6:118','Ju (besimtarë) hani nga ajo që (është therrur dhe) dhe është përmendur emri i All-llahut, po qe se jeni të bindur në faktet e Tij.');
INSERT INTO "ahmeti" VALUES(6,119,'6:119','ç’keni ju që të mos hani nga ajo për të cilën është përmendur emri i All-llahut, e Ai ju sqaroi juve se çka është ndaluar për ju, përveçkur jeni detyruar (atëherë edhe harami është hallall). Një shumicë (e mohusve), duke mos pasur kurrfarë dije, por vetëm nga pasionet e tyre, duan t’i largojnë nga e vërteta (njerëzit). Po Zoti yt di më së miripër ata që i shkelin dispozitat.');
INSERT INTO "ahmeti" VALUES(6,120,'6:120','Dhe mos bëni mëkate as haptas as fshehtas. Ata që bëjnë mëkatin do të ndëshkohen për mëkatin e bërë.');
INSERT INTO "ahmeti" VALUES(6,121,'6:121','Dhe mos hani nga ajo që (para therjes së saj) nuk është përmendur emri i All-llahut, vërtet ajo (ngrënia) është mëkat. Djallëzit i nxisin miqtë e vet që t’ju polemizojnë juve, e nëse i dëgjoni ata, atëherë jeni si ata (idhujtarë).');
INSERT INTO "ahmeti" VALUES(6,122,'6:122','Vallë, a është ai që qe i vdekur kurse Ne e ngjallëm dhe idhamë dritë, me të cilën ecën mes njerëzve, si ai që ka mbetur në errësirë (i humbur) dhe nuk mund të shpëtojë nga ajo? Ja, kështu (si këtij në errësirë) iu dket mirë mohusve ajo që veprojnë.');
INSERT INTO "ahmeti" VALUES(6,123,'6:123','Dhe ashtu (sikurse në Mekë) në çdo qytet kemi bërë kriminelët e tij pari, në mënyrë që të bëjnë dredhi në të, por nuk mashtrojnë tjetër kë pos veten e tyre dhe prapëseprapë nuk kuptojnë.');
INSERT INTO "ahmeti" VALUES(6,124,'6:124','E kur uvjen atyre ndonjë argument i prerë (për Muhammedin), ata thonë: “Kurrsesi nuk e besojmë atë (dërgesën e Muhammedit) derisa të mos na jepet edhe neve ngjajshëm me atë që iu pat dhënë të dërguarve të All-llahut”! Më së miri All-llahu e di ku ta vërë dërgesën (risalen) e vet. Ata që bën krim do t’igodasë poshtrimi dhe dënimi i fortë te All-llahu për shkak se vazhdimisht bënin hile.');
INSERT INTO "ahmeti" VALUES(6,125,'6:125','Atë që All-llahu dëshiron ta udhëzojë, ia zgjeron zemrën për (të pranuar) islamin. Atë që dëshiron ta lërë të humbur, zemrën e tij ia bën shumë të ngusht si tëngjitej në qiell. Kështu All-llahu lëshon dënimin mbi ata që nuk besojnë.');
INSERT INTO "ahmeti" VALUES(6,126,'6:126','Kjo është rruga e Zotit tënd, është e drejtë, Ne shpjeguam argumentet për njerëz që përkujtojnë.');
INSERT INTO "ahmeti" VALUES(6,127,'6:127','Ata e kanë vendin e shpëtimit (Xhennetin) te Zoti i tyre; Ai është mbrojtës i tyre, për atë që ata vepruan.');
INSERT INTO "ahmeti" VALUES(6,128,'6:128','Përkujto ditën kur Ai i tbon ata të gjithë (e u thotë): “O grumbull i xhinve, ju mashtruat shumë njerëz!” E nga njerëzit që ishin miq të tyre (tëxhinve) thonë: “Zoti ynë ne përfituam njëri prej tjetrit, dhe e arritëm afatin të cilin e caktove!” Tha: “(Zoti) Zjarri është vendi juaj, përgjithmonë jeni në të, pos (kohës) çka do All-llahu” Vërtet Zotit yt është më i urti, më i dijshmi.');
INSERT INTO "ahmeti" VALUES(6,129,'6:129','PO ashtu (sikur u dhamë xhinve dhe njerëzve përjetim), Ne i bëjmë sundues disa mizorë mbi mizorët e tjerë për shkak të asaj që fituan (vepruan).');
INSERT INTO "ahmeti" VALUES(6,130,'6:130','O grumbull i xhinve dhe i njerëzve! A nuk ju erdhën nga mesi juaj të dërguar t’ju rrëfejnë argumentet e Mia dhe t’ju tërheqin vërejtjen për takimin tuaj në këtë ditë? Ata thonë: “Dëshmojmëkundër vetvetes”. I pat mashtruar ata jeta e kësaj bote dhe ashtu (të detyruar) dëshmuan kundër vetvetes se me të vërtetë e refuzonin (të vërtetën).');
INSERT INTO "ahmeti" VALUES(6,131,'6:131','Këtë (dërgimin e të dërguarve) ngase Zoti yt, nuk është që për shkak të mëkatit të shkatërrojë një vend, e banorët etij të jenë të painformuar.');
INSERT INTO "ahmeti" VALUES(6,132,'6:132','Po për secili (veprues) ka shkallë (që do t’i arrijë) sipas asaj që vepruan. All-llahu nuk është i pakujdesshëm ndaj asaj që veprojnë.');
INSERT INTO "ahmeti" VALUES(6,133,'6:133','Zoti yt nuk ka nevojë për asgjë, Ai është mëshirues. Nëse do Ai, juve ju zhduk dhe sjell kë të dojë pas jush, ashtu sikurse ju solli juve nga pasardhësit e atyre që ishin para jush.');
INSERT INTO "ahmeti" VALUES(6,134,'6:134','Ajo që iu kërcënohet (kijameti, ringjallja, llogaria, përgjegjësia) pa tjetër do të vijë, e ju nuk mund ta pengoni. (Nuk mund ta menjanoni caktimin e Zotit).');
INSERT INTO "ahmeti" VALUES(6,135,'6:135','Thuaj: “O populli im (kurejshit)! veproni në atë që jeni, e unë do ta vazhdoj të veproj në atë që jam, e më vonë do ta dini se kujt do t’i takojë e ardhmja e lavdishme. Është e ditur se zullumqarët nuk gjejnë shpëtim.');
INSERT INTO "ahmeti" VALUES(6,136,'6:136','Dhe nga aj që krijoi Si prej të lashtave dhe prej kafshëve shtëpake, ata (idhujtarët) ndanë një pjesë për All-llahun dhe thanë: “Kjo është për All-llahun, si mendonin ata, e kjo është e idhujve tanë”. Ajo që ishte për idhujt, nuk shkon te All-llahu, e ajo që është për All-llahun, shkon te idhujt e tyre. sa gjykim i shëmtuar është ai që gjykojnë (pjesën e idhujve e plotësonin nga ajo e All-llahut, në rast nevoje, e të All-llahut jo).');
INSERT INTO "ahmeti" VALUES(6,137,'6:137','Kështu shumicës së idhujtarëve, idhujt ua hijeshuan atyre mbyten e fëmijëve të vet, për t’i shkatërruar (me mashtrime) dhe për ‘ua ngatërruar fenë (që e kishin pasur të Ismailit). Po sikur të donte All-llahu, ata nuk do të bënin atë, andaj hiqu tyre dhe asaj që shpifin.');
INSERT INTO "ahmeti" VALUES(6,138,'6:138','Dhe sipas bindjes së tyre ata thoshin: “Këto kafshë dhe këto bimë janë të ndaluara, nuk mund të ushqehet me to, përveç ata, të cilëvene ua lejomë; këto janë kafshë që është ndaluar t’u hipet; këto janë kafshë jatë therrjes së tyre nuk përmendin emrin e All-llahut, duke shpifur ndaj Tij. Ai do t’i ndëshkojë ata për shpifjet e tyre.');
INSERT INTO "ahmeti" VALUES(6,139,'6:139','Madje ata thonin: “çka është në barqet e këtyre kafshëve është vetëm për mashkujt tanë, e ndaluar për gratë tona. E nëse ishte e ngordhtë (fruti në bark) ata (mashkuj e femra) ishin të barabartë në të. Do të ndëshkojë Ai cilësimin ë tyre të rrejshëm. Ai është i përsosur në punët e Tij, i dijshëm për krijesat e Tij.');
INSERT INTO "ahmeti" VALUES(6,140,'6:140','S’ka dyshim se kanë dështuar keq ata që mbytën fëmijët e tyre nga mendjelehtësia e pa kurrfarë dije dhe ata që duke i shpifur Zotit, shpallën të ndaluar atë që Zoti u kishte dhuruar. Ata kanë humbur rrugën e drejtë dhe prej fillimit nuk ishin në udhëzim.');
INSERT INTO "ahmeti" VALUES(6,141,'6:141','Ai (All-llahu) është që krijoi kopshtie (bimët e të cilave) të ngritura lart (në shtylla) dhe të rrafshta (të shtrira në tokë), edhe hurmet dhe drithërat me frute (shije) të ndryshme; (krijoi) ullinjtë dhe shegët e ngjashme (nga shija). Hani frutat e tyre kur të piqen dhe ditën e korrjes (të vjeljes) së frutave jepne atë pjesë që është obligim (të varfërve e nevojlive) dhe (hani-jepni) mos teproni, pse Ai nuk i do shkapërderdhësit.');
INSERT INTO "ahmeti" VALUES(6,142,'6:142','Nga kafshët (shtëpiake krijoi) edhe aso për ngarkim (për hipje) dhe aso, prodhimi i të cilave përdoret për shtrojë (leshi i tyre ose për therrje. Haninga ajo që All-llahu ju dhuroi, e mos ndiqni gjurmët (rrugët) e shejtanit, pse ai është armik juaj i hapët.');
INSERT INTO "ahmeti" VALUES(6,143,'6:143','Ai (krijoi) tetë lloje (nga kafshët shtëpiake): prej deleve dy (dash e dele), prej dhive dy (cjap e dhi). Thuaj: “A janë të ndaluar (haram) dy meshkuj apo dy femra apo çka mbanë (pjell) mitra e dy (llojeve të tyre) femrave?” Më tregoni, pra, me fakte të ditura (e jo me trillime) nëse jeni të sinqertë.');
INSERT INTO "ahmeti" VALUES(6,144,'6:144','Ai (krijoi) edhe nga devet dy (lloje) dhe nga lopët dy (lloje). Thuaj: “A dy meshkuj i ka ndaluar (haram), a dy femrat, pse çka mban mitra e dy (llojeve të tyre) femrave?” A mos ishit të pranishëm kur All-llahu ju porositi me këtë (hallall apo haram)?” E kush është më mizor se ai që trillon rrenë për All-llahun dhe ashtu t’i humbë njerëzit në mungesë të dijes. All-llahu nuk vë në rrugë të drejtë mizorët.');
INSERT INTO "ahmeti" VALUES(6,145,'6:145','Thuaj: “Në atë që më është shpallur mua (në Kur’an) nuk po gjej diçka të ndaluar nga ushqimi, përveç në qoftë se ai (ushqimi) është: coftinë, gjak i derdhur ose mish derri, ai i ndytë, dhe pos asaj që është therrur jo në emër të All-llahut (por të ndonjë idhulli) e që është mëkat. E kush detyrohet (t’i hajë këto të ndaluara), por duke mos pasur për qëllim shijen dhe duke mos e tepruar; Zoti yt është që falë e mëshiron shumë.');
INSERT INTO "ahmeti" VALUES(6,146,'6:146','E (posaqërisht) ndaj atyre qëjanë jehudi Ne kemi ndaluar çdo (kafshë) thundrake: nga lopët dhe delet u kemi ndaluar dhjmin e tyre, pos atij (dhjami) në shpinën dhe në zorrët e tyre dhe pos atij të përzier me ndonjë asht. Këtë (masë-ndalesë) e morëm si ndëshkim ndaj mëkatit të tyre. S’ka dyshim, Ne jemi të vërtetë (në çka ju rrëfejmë).');
INSERT INTO "ahmeti" VALUES(6,147,'6:147','PO, në qoftë se (për këtë jehuditë) të përgënjeshtrojnë Thuaj: “Samëshirues i madh është ky Zoti juaj, po (mos u mashtroni) dënimi i Tij për njerëzit kriminelënuk mund të prapësohet!”');
INSERT INTO "ahmeti" VALUES(6,148,'6:148','E ata që i shoqëruan Zotit, do të thoshin: “Sikur të donte All-llahu nuk do t’i bëim shok (nuk do të ishim idhujtarë), as ne, as prindërit tanë, e as nuk do të ndalonim asnjë send.” Kështu patëngënjyer edhe ata që ishin para tyre derisa (për shkak të mëkatit) përjetuan dënimin tonë të ashpër. Thuaj: “A mos keni ndonjë fakt e të na prezentoni atë neve?” Ju i mbështeteni vetëm hamendjes, në të vërtetë, vetëm gënjeni.');
INSERT INTO "ahmeti" VALUES(6,149,'6:149','Thuaj: “All-llahu ka argumentin më të plotë dhe sikur të donte Ai do t’ju vinte në rrugë të drejtë të gjithëve.');
INSERT INTO "ahmeti" VALUES(6,150,'6:150','Thuaj: “Sillni dëshmitarët, të cilët dëshmojnë se All-llahu ndaloi (bëri haram) ato (që i ndalonin vetë). Nëseda të dëshmojnë rrejshëm, ti mos dëshmo bashkë me ta dhe mos shko pas dëshirave të atyre që argmentet Tona i bënë të rremedhe pas atyre që nuk besojnë jetën e që largohen prej Zotit të tyre (adhurojnë tjetër).');
INSERT INTO "ahmeti" VALUES(6,151,'6:151','Thuaj: “Ejani t’ju lexojë atë që me të vërtetë ju ndaloi Zoti juaj: të mos i shoqëroni Atij asnjë send, të silleni mirë me prindërit, të mos i mbytni fëmijët tuaj për shkak të varfërisë, sepse Ne u ushqejmë juve dhe ata, të mos u afroheni mëkateve të hapta apo të fshehta, mos e mbtni njeriun sepse mbytjen e tij e ndaloi All-llahu, përpos kur është me vend. Këto janë porositë e Tij, kështu të mendoni thellë.');
INSERT INTO "ahmeti" VALUES(6,152,'6:152','Mos iu afronu pasurisë së jetimit derisa ai të arrijë pjekurinë, (mund t’i afroheni) vetëm në mënyrë më të mirë, zbatoni me drejtësi masën dhe peshojën, Ne nuk ngarkojmë asnjë njeri përtej mundësive të tij. Kur të flitni (të dëshmoni), duhet të jeni të drejtë edhe është çështja për (kundër) të afërmit, dhe zotimin e dhënë All-llahut plotësonie. Këto janë me çka Ai ju porositë kështu që të përkujtoni.');
INSERT INTO "ahmeti" VALUES(6,153,'6:153','Dhe se kjoështë rruga (feja) Ime e drejtë (që e caktova për ju), pra përmbajuni kësaj, e mos ndiqni rrugë të tjerae t’ju ndajnë nga rruga e Tij. Këto janë porositë e Tij për ju, ashtu që ë ruheni.');
INSERT INTO "ahmeti" VALUES(6,154,'6:154','E Musait i dhamë librin plotësim (i të mirave) për atë që tregon bindje të mirë, dhe sqarues për çdo send, udherrëfyes e mëshirë, ashtu që të besojnë se do të takohen me Zotin e tyre.');
INSERT INTO "ahmeti" VALUES(6,155,'6:155','Dhe ky libër, dobi prurës, Ne e zbritëm, përmbanju këtij, ruajuni shtu që të mëshiroheni.');
INSERT INTO "ahmeti" VALUES(6,156,'6:156','(E zbritëm Kur’anin) Që të (mos) thoni: “Libri u zbrit vetën dy grupeve para nesh (jehudive e të krishterëve) dhe se ne ishim të panjohur me librat (me mësimet) e tyre.');
INSERT INTO "ahmeti" VALUES(6,157,'6:157','Dhe që të (mos) thoni: “Sikur të na kishte zbritur neve libri, (sikurse u zbriti atyre dy grupeve) ne do të ishim edhe në të udhëzuar se ata. Juve ju erdhi nga Zoti juaj argumenti (Kur’ani), ju erdhi udhëzimi, mëshira. E kush është më zullumqar se ai që argumentet eZOtit i bën të rreme dhe ua këthen shpinën atyre? Ne do t’i ndëshkojmë me ndëshkim më të rëndë ata që ua kthyen shpinën argumenteve Tona, për shkak të prapësimit që bënë.');
INSERT INTO "ahmeti" VALUES(6,158,'6:158','A mos janë duke pritur tjetër ata (pas këtyre fakteve) vetëm t’ju vjë engjëjt (t’ua marrin shpirtin), të vijë Zoti yt (urdhëri për shkatërrim) ose të vijnë disa nga shenjat e Zotit tënd, (në) ditën kur vijnë disa shenja të Zotit tënd, asnjë njeriu nuk ivlen besimi i Tij nëse nuk ka besuar më parë ose nuk ka bërë në besimin e tij kurfarë të mire. Thuaj: “Pritni, edhe ne jemi duke pritur!”');
INSERT INTO "ahmeti" VALUES(6,159,'6:159','Vërtet at që e përqanë fenë e tyre dhe u ndanë në grupe, ti (Muhammed) nuk ke kurfarë përgjegjësie. çështja e tyre është vetëm te All-llahu, Ai do t’i njohë me atë që punuan.');
INSERT INTO "ahmeti" VALUES(6,160,'6:160','Kush vjen me një (punë) të mirë, ai (në ditën e gjykimit) shpërblehet dhjetë fish, e kush vjen me (vepër) të keqe, ai ndëshkohet vetëm për të. Atyre nuk u bëhet e padrejtë.');
INSERT INTO "ahmeti" VALUES(6,161,'6:161','Thuaj: “Vërtet Zoti im më udhëzoi në rrugë të drejtë, që është fe e drejtë, fe e Ibrahimit, që ishte larg besimeve të kota. Ai (Ibrahimi) nuk ishte idhujtar!');
INSERT INTO "ahmeti" VALUES(6,162,'6:162','Thuaj: “Namazi im, kurbani im dhe vdekja ime janë thjesht për All-llahun, Zotin e botëve.');
INSERT INTO "ahmeti" VALUES(6,163,'6:163','Ai nuk ka shok (nuk adhuroj tjetër). Me këtë (thjeshtësi të adhurimit vetëm për Zotin) jam i urdhëruar dhe jam i pari i muslimanëve (i pari që pranoj dhe bindem)!');
INSERT INTO "ahmeti" VALUES(6,164,'6:164','Thuaj: “A të kërkojë Zot pos All-llahut, e Ai është Zot i çdo sendi (ekzistues) dhe dëmi i secilit person është kundër vetes. Askush nuk do të bartë barrën e tjetrit. Mandej, kthimi juaj është te All-llahu; e Ai ju njohton për atë që përçaheshit.');
INSERT INTO "ahmeti" VALUES(6,165,'6:165','Ai është që ju bëri sundues (zëvendësues) në tokë (pas shkatërrimit të atyre që ishin më parë) dhe lartësoi në një shkallë më të lartëdisa nga ju mbi të tjerët, për t’ju sprovuar në atë që u dha. All-llahu është ndëshkues i shpejtë, është që falë e Mëshirues.');
INSERT INTO "ahmeti" VALUES(7,1,'7:1','Elif, Lam, Mim, Sadë.');
INSERT INTO "ahmeti" VALUES(7,2,'7:2','(Ky është) Libri, që të është ty, që me të t’u tërheqësh vërejtjen dhe t’i këshillosh besmtarët, pra të mos ketë shtrëngim në gjoksin tënd (për kumtimin e tij).');
INSERT INTO "ahmeti" VALUES(7,3,'7:3','Përvetësoni atë që ju është zbritur nga zoti juaj, e mos zini miq (përkrahës) pos Tij. Pak është ajo që po merrni përvojë.');
INSERT INTO "ahmeti" VALUES(7,4,'7:4','Sa fshatra (banorë) kemi shkatërruar (me fajin e tyre) e dënimi Jonë u erdhi natën, a (ditën) kur ishin duke pushuar (duke fjetur).');
INSERT INTO "ahmeti" VALUES(7,5,'7:5','Kur u erdhi atyre dënimi Jonë, s’kishin ç’të kërkonin tjetër, vetëm të thonë: “Vërtet, ne ishim zullumqarë” (të pranojmë gabimin).');
INSERT INTO "ahmeti" VALUES(7,6,'7:6','Ne patjetërdo të marrim në përgjegjësi ata, të cilëve u është dëguar (pejgamber), e do t’i marrim në pyetje edhe të dërguarit.');
INSERT INTO "ahmeti" VALUES(7,7,'7:7','Dhe duke e ditur mirë Ne do t’u rrëfejmë atyre (për atë që punuan), se Ne nuk ishim që mungonim (ishim të pranishëm).');
INSERT INTO "ahmeti" VALUES(7,8,'7:8','Atë ditë eshimi (masa) është i drejtë. Atij që i rëndohen peshojat, ata janë të shpëtuar.');
INSERT INTO "ahmeti" VALUES(7,9,'7:9','Kujt i vijnë lehtë peshojat, ata e humben vetveten, ngase i refuzan argumentet Tona me të padrejtë.');
INSERT INTO "ahmeti" VALUES(7,10,'7:10','Ne ju vendosëm në tokë dhe ju mundësuam jeesën (mjetet për të jetuar), e pak prej jush po falënderoni.');
INSERT INTO "ahmeti" VALUES(7,11,'7:11','Ne ju krijuam pastaj ju dhamë formën, e mandej ngjëjve u thamë: “bëni sexhde për Ademin”. Ata i bënë sexhde pos Iblisit. Ai nuk qe prej atyre që bënë sexhde.');
INSERT INTO "ahmeti" VALUES(7,12,'7:12','(All-llahu) tha: “çka të pengoi ty të bësh sexhde, kur Unë të urdhërova?” Ai (Iblisi) tha: “Unë jam më i vlershëm se ai, më krijove mua nga zjarri, e atë e krijove nga balta!”');
INSERT INTO "ahmeti" VALUES(7,13,'7:13','(All-llahu) Tha: “Zbrit nga ai (Xhenneti), nuk të takon të bësh kryelartësi në të, dil jashtë, s’ka dyshim ti je i poshtëruar”.');
INSERT INTO "ahmeti" VALUES(7,14,'7:14','(Iblisi) Tha: “Më afatizogjer ditën kur ringjallen (njerëzit)!”');
INSERT INTO "ahmeti" VALUES(7,15,'7:15','(All-llahu) Tha: “Ti je i Afatizuar!”');
INSERT INTO "ahmeti" VALUES(7,16,'7:16','(Iblisi) Tha: “Për shkak se më humbe mua, unë do t’u ulem atyre (do t’u zë pusi) në rrugën Tënde të drejtë,');
INSERT INTO "ahmeti" VALUES(7,17,'7:17','Mandej, do t’ju sillem atyre para, prapa, ga edjathta dhe nga e majta e tyre, e shumicën e tyre nuk do ta gjejsh që të falënderohen (të besojnë)!”');
INSERT INTO "ahmeti" VALUES(7,18,'7:18','(All-llahu) Tha: “Dil nga ai (Xhenneti), i urrjetur, i dëbuar. Kush prej tyre vjen pas teje, Unë kam për ta mbushur Xhehennemin me të gjithë ju.');
INSERT INTO "ahmeti" VALUES(7,19,'7:19','(Ne i thamë) o Adem, ti dhe bashkëshortja jote zini vend në Xhennet, hani nga të doni, e mos iu afroni kësaj peme, pse do të bëheni prej zulumqarëve (të vetvetes suaj).');
INSERT INTO "ahmeti" VALUES(7,20,'7:20','Shejtani i nxiti ata të dy (i mashtroi), që t’ua zbulojë atyre pjesët e turpshme që u ishin të muluara dhe tha: “Zoti juaj nuk ua ndaloi ju dyve atë pemë vetëm që të mos bëhi meleqë (engjëj), ose të mos bëheni prej të përjetshmëve.');
INSERT INTO "ahmeti" VALUES(7,21,'7:21','Dhe ju bëri be atyre (duke u thënë) se: unë jam këshillues për ju.');
INSERT INTO "ahmeti" VALUES(7,22,'7:22','Atëherë me mashtrim i zbriti (në nivel të palakmueshëm). E kur e shijuan pemën u zbulua vendturpi i tyre dhe filluan të mbulojnë atë (duke vënë gjeth mbi gjeth) nga gjethet e (pemëve të) Xhennetit. Zoti i tyre i thirri (duke u thënë): “A nuk ua ndalova ju dyve atë pemë dhe a nuk ju thashë ju dyve se shejtani është armik i haptë për ju?!”');
INSERT INTO "ahmeti" VALUES(7,23,'7:23','Ata të dy thanë: “Zoti ynë, ne i bëmë të padrejtë vetvetes, në qoftë se nuk na falë dhe nuk na mëshiron, ne me siguri do të jemi prej të shkatërruarve!”');
INSERT INTO "ahmeti" VALUES(7,24,'7:24','(All-llahu) tha: “Zbritni, jeni arimk i njëri-tjetrit. Në tokë ju e keni vehdqëndrimin (vendbanimin) dhe përjetim deri në një kohë.');
INSERT INTO "ahmeti" VALUES(7,25,'7:25','Tha: “Në të (në tokë) do të jetoni (do të gjallëroni), në të do të vdesni (do të varroseni) dhe prej saj do të nxirreni (do të ringjalleni).');
INSERT INTO "ahmeti" VALUES(7,26,'7:26','O bijtë e Ademit, Ne krijuam për ju petk që ju mbulon vendturpësinë dhe petk zbukues. Po petku i devotshmërisë, ai është më i miri. Këto janë argumentet e All-llahut, ashtu që ata të përkujtojnë.');
INSERT INTO "ahmeti" VALUES(7,27,'7:27','O biktë e Ademit, të mos ju mashtrojë kurrsesi sejtani sikurse i nxori prindërit tuaj nga Xhenneti, zhveshi prej tyre petkun e tyre që t’ju dalë në shesh lakuriqësia e tyre. Vërtetë ai dhe shoqëria e tij ju sheh, ndërsa ju nuk e shihni. Ne i kemi bërë shejtanët miq të atyre që nuk besojnë.');
INSERT INTO "ahmeti" VALUES(7,28,'7:28','Kur (punojnë ata (idhujtarët) diçka të shëmtuar, thonë: “Ne i gjetëm që kështu prindërit tanë, edhe All-llahu na urdhëroi këtë (vizitën rreth Qabes lakuriq). Thuaju: “All-llahu nuk urdhëron të shëmtuarën, a thoni për All-llahun çka nuk dini?”');
INSERT INTO "ahmeti" VALUES(7,29,'7:29','Thuaj: “All-llahu më urdhëroi mua drejtësinë dhe në tërësi kthejuni Atij në çdo namaz (lutje) dhe adhuronie Atë duke qenë të sinqertë në lutje vetëm për të. Ashtu sikur ju filloi (krijoi) sëpari do të ktheheni te Ai.');
INSERT INTO "ahmeti" VALUES(7,30,'7:30','Një grup (nga ju) Ai e vuri në rrugë të drejtë, e nga një grup meritoi të jetë i hmbur, pse ata (të hmburit) shejtanët i morën për miq, e megjithatë mrndonin se ishin në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(7,31,'7:31','O bijtë e Ademit, vishuni bukur për çdo namaz (lutje), han dhe pini e mos teproni, pse Ai (All-llahu) nuk i do ata që e teprojnë (shkapërderdhin).');
INSERT INTO "ahmeti" VALUES(7,32,'7:32','Thuaj: “Kush i ndaloi bukuritë dhe ushqimet e mira që All-llahu i krijoi për robtë e vet?” Thuaj: “Ato janë në këtë botëpër ata që besuan, e në ditën e kijametitjanë të posaqme për ta. Kështu i sqaron argumentet një populli që kupton.');
INSERT INTO "ahmeti" VALUES(7,33,'7:33','Thuaj: “Zoti im i ndaloi vetëm të këqijat e turpshme, le të jenë të hapta ose të fshehta, ndaloi mëkatin, mdaloi shtypjen e tjetrit pa të drejtë, ndaloi t’i mvishni All-llahut shok pa patur për të kurrfarë argumenti, dhe ndaloi të thoni për All-llahun atë që nuk e dini se është e vërtetë.');
INSERT INTO "ahmeti" VALUES(7,34,'7:34','çdo popull (që përgënjeshtroi pejgamberët) ka afatin e vet, e kur t’u vijë afati tyre, ai nuk mund të shtyhet për një ashnjë moment, e as të përngutet më parë.');
INSERT INTO "ahmeti" VALUES(7,35,'7:35','O bijtë e Ademit, juve ju vijnë të dërguar nga mesi juaj, ju përkujtonë faktet e Mia (shkoni pas tyre). E kush ruhet dhe përmirësohet, për ta s’ka as frikë as s’kanë përse të pikëllohen.');
INSERT INTO "ahmeti" VALUES(7,36,'7:36','E ata që i konsideruan të rreme faktet tona dhe kryelartësi u larguan prej tyre, ata janë banues të zjarrit dhe në të janë përjetë.');
INSERT INTO "ahmeti" VALUES(7,37,'7:37','Kush është më mizor se ai që trilin shpifje ndaj All-llahut, apo i përgënjeshtron argumentet e Tij? Ata e arrijnë pjesën e tyre që u është caktuar (në shënime) deri kur t’u vijnë atyre të dërguarit tanë (mekaiket) t’ju marrin shpirtine u thonë: “Ku janë ata që pos Zotit i lutshit?” Ata thonë: “Kanë humbur prej nesh” dhe ashtu dëshmojnë për vete se ishin mohues (kafira).');
INSERT INTO "ahmeti" VALUES(7,38,'7:38','(All-llahu) Ju thotë: “Hyni në Xhehennem me atë popull që ishte para jush nga xhinët dhe nga njerëzit (e që ishin si ju). Sa herë që një gruphyn në të, e mallkon atë të mëparshmin derisa kur të arrijnë në të të ggjithë, grupi i fundit i tyre thotë për grupin e parë: “Zoti ynë, këta (pari) na kanë humbur neve (nga rruga e drejtë), pra shtoju dënimin me zjarr atyre!” (All-llahu) Thotë: “Për secilin (grup) është (dënimi) i shtuar, por ju nuk po dini.');
INSERT INTO "ahmeti" VALUES(7,39,'7:39','Të parët e tyre (paria) të mbramëve të tyre ju thotë: “Ju nuk keni përparësi ndaj nesh (pse vetë keni bërë kufur), shijone pra dënimin për atë që e fituat!”');
INSERT INTO "ahmeti" VALUES(7,40,'7:40','Nuk ka dyshim se ata që përgënjeshtruan argumente Tona dhe nga mendjemadhësia u larguan prej tyre, atyre nuk u hapen dyert e qiellit dhe nuk do të hyjnë në Xhennet deri të përbirojë devja vrimën e gjilpërës. Ja, kështu i shpërblejmë kriminelët.');
INSERT INTO "ahmeti" VALUES(7,41,'7:41','Për ata është përgattitur shtrat nga zjarri dhe mbulojë (mga zjarri). E kështu pra i shpërblejmë zullumqarët.');
INSERT INTO "ahmeti" VALUES(7,42,'7:42','Ata që besuan dhe bënë vepra të mira, e Ne as që obligojmë ndoken çka nuk ka mundësi (të veprojë), të tillët janë banues të Xhennetit dhe në të janë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(7,43,'7:43','Nga zemrat e tyre kemi hequr (kemi zhdukur) çdo gjë që ishte krijuar nga zilia (nga urrejtja), janë në Xhennet, ku rrjedhin lumenj, e ata thonë: “falënderojmë All-llahun që na udhëzoi për këtë (për iman, për punë të mira, na hoq zilinë, na futi në Xhennet), pse sikur të mos na drejtonte All-llahu, ne nuk do të dinim të udhëzoemi. Vërtet, të dërguarit e Zotit na e thanë të vërtetën dhe ne i besuam!” E atyre u drejtohet thirrje: “KY është Xhenneti, iu dha juve për atë që vepruat.');
INSERT INTO "ahmeti" VALUES(7,44,'7:44','Ata të Xhennetit i thërrasin (i pyesin) banuesit e zjarrit e u thonë: “Ne e gjetëm të vërtetë atë që na e pat premtuar Zoti në, e ju (banues të zjarrit) a e gjetët të vërtetën atë që pat premtuar Zoti juaj?” Ata (banuesit e zjarrit) thonë: “Po”. Atëherë në mes të tyre (mes dy grupeve) thërret një zë: “Mallkimi i All-llahut qoftë mbi zullumqarët!”');
INSERT INTO "ahmeti" VALUES(7,45,'7:45','Ata që penguan nga rruga (imani) e All-llahut dhe që kërkuan strembërimin e saj dhe ata që ishin mohues të botës tjetër.');
INSERT INTO "ahmeti" VALUES(7,46,'7:46','E mes atyre dyve (dy grupeve) është një perde (mur), e mbi Araf (lart mbi mur) janë burra që i njohin secilin (të Xhennetit dhe të Xhehennemit) me shenjat e tyre. Ata i thërrasin banuesit e Xhennetit: “Paqja (shpëtimi) qoftë mbi ju!” Ata (të Arafit) nuk kanë hyrë në të, po shpresojnë.');
INSERT INTO "ahmeti" VALUES(7,47,'7:47','E kur u shkon shikimi i tyre nga ata të Xhehennemit, Thonë: “Zoti ynë mos na bë neve me mizorët!”');
INSERT INTO "ahmeti" VALUES(7,48,'7:48','Ata të Arafit i thërrasin do burra që i njohin me shenjat e tyre dhe u thonë: “çka ju vlejti ai grumbullimi juaj (në pasuri e numër) dhe ajo që bënit kryelartësi?” (e tash jeni në Xhennenem).');
INSERT INTO "ahmeti" VALUES(7,49,'7:49','A këta janë ata (besimtarët), për të cilët betoheshi se nuk ka për t’i përfshirë mëshira e All-llahut?” (në mënyrë ironike i përqeshin kufarët, mandej u thonë besimtarëve): “Vazhdoni në Xhennet, as nuk ka frikë për ju, as nuk keni për t’u brengosur!”');
INSERT INTO "ahmeti" VALUES(7,50,'7:50','Banuesit e zjarrit i thërrasin (dhe i lusin) ata në Xhennet (duke ju thënë): “Na qitni diç nga uji apo nga ajo që u ka dhënë All-llahu (se mbaruam nga etja)!” Ata (në Xhennet) thonë: “All-llahu i ka ndaluar që të gjitha këto për jobesimtarët!”');
INSERT INTO "ahmeti" VALUES(7,51,'7:51','Ata që fenë e tyre e morrën tallje e lojë dhe të cilët i mashtroi jeta e dynjas. Sot, pra, Ne i harrojmë ata sikurse na e patën harruar takimin e kësaj dite të tyre dhe, sikurse i refonin argumentet Tona!”');
INSERT INTO "ahmeti" VALUES(7,52,'7:52','Ne u sollëm atyre (mekasve) një libër (Kur’anin) që ua shkoqitëm në baza të diturisë, e që është udhërrëfyes e mëshirë për ata që besojnë.');
INSERT INTO "ahmeti" VALUES(7,53,'7:53','Ata nuk presin tjetër, por vetëm atë që do t’u vijë. E atë ditë kur t’u vijë (dënimi i premtuar) ajo që e pritnin, ata të cilët më parë e kishin harruar thonë+. “Vërtet të dërguarit e Zotit tanë erdhën me fakte të vërteta, a kemi ndonjë ndërmjetsues që të na shpëtojë, ose të kthehemi (në dynja) e të veprojmë tjetër nga ajo që vepruam” Ata shkatërruan vetveten dhe ushkoi huq trillimi ë bënin.');
INSERT INTO "ahmeti" VALUES(7,54,'7:54','Vërtet, Zoti juaj, All-llahu është Ai që krijoi qiejt dhe tokën brenda gjashtë ditësh, pastaj qëndroi mbi Arshin, Ai e mbulon ditën me natë, që me të shpejtë e kërkon atë (mbulimin e dritës së ditës), edhe dielli, edhe hëna e edhe yjet i janë nënshtruar sundimit të Tij. Ja, vetëm Atij itakon krijimi dhe sundimi. I madhëruar është All-llahu, Zoti i botëve.');
INSERT INTO "ahmeti" VALUES(7,55,'7:55','Lutnie Zotin tuaj të përulun e në heshtje, pse Ai nuk ido ata që e teprojnë.');
INSERT INTO "ahmeti" VALUES(7,56,'7:56','Mos bëni çrregullime në tokë pas rregullimit të saj (me të ardhur të pejgamberëve) dhe lutnie Atë duke pasur frikë (dënimin) dhe duke shpresuar (mëshirën. S’ka dyshim se mëshira e All-llahut është pranë atyre të mirëve.');
INSERT INTO "ahmeti" VALUES(7,57,'7:57','Ai është që i lëshon erërat si myzhde pranë mëshirës (shiut) së Tij. E kur ato (erëra) barin re të mëdha mbi, ne i sjellim mbi një tokë të vdekur dhe lëshojmë në të ujin (shiun), dhe me të (me ujin) nxjerrim të gjithë fruta. Kështu i nxjerrim (i ngjalllim) të vdekurit, ashtu që të përkujtoni (fuqinë e Zotit).');
INSERT INTO "ahmeti" VALUES(7,58,'7:58','Me lejen e All-llahut toka e mirë mbinë bimët, e ajo që nuk është kualitative, ajo nuk mbinë vetëm (pak) me vështirësi. Kështu Ne i rradhisim argumentet Tona për ata që falenderojnë.');
INSERT INTO "ahmeti" VALUES(7,59,'7:59','Ne patëm dërguar Nuhun te populli i vet, e ai i tha: “O populli im, adhuronie vetëm All-llahun, nuk keni zot tjetër pos Tij. Unë kam frikë për dënimin Tuaj në një ditë të madhe!”');
INSERT INTO "ahmeti" VALUES(7,60,'7:60','Paria e tij tha: “Ne po të shohim (në këtë që na thërret) plotësisht të humbur!”');
INSERT INTO "ahmeti" VALUES(7,61,'7:61','(Nuhu) Tha: “O populli im, unë nuk kam kurrfarë humbje, por unë jam i dërguar prej Zotit të botëve!”');
INSERT INTO "ahmeti" VALUES(7,62,'7:62','“Unë ju kumtoj juve shpalljet e Zotit tim, ju këshilloj dhe unë di nga Zoti im çka ju nuk dini!”');
INSERT INTO "ahmeti" VALUES(7,63,'7:63','“A mos u çuditët që shpallja ju erdhi nga Zoti juaj përmes (gjuhës së) një njeriu nga mesi juaj, për t’ju tërhequr vërejtjen që të ruheni dhe ashtu të shpëtonni!”');
INSERT INTO "ahmeti" VALUES(7,64,'7:64','Po ata e përgenjeshtruan atë (Nuhun), e Ne e shpëtuam atë dhe ata që ishin me të në anije, ndërsa ata që përgenjeshtruan faktet tona i fundosëm. Vërtet, ata ishin popul i verbër.');
INSERT INTO "ahmeti" VALUES(7,65,'7:65','Edhe te (populli) Ad-i (dërguam) vëllain e tyre Hudin, e ai tha: “O populli im, adhuronie (një Zot) All-llahun, ju nuk keni zot pos Tij, a nuk po frikësoheni?!”');
INSERT INTO "ahmeti" VALUES(7,66,'7:66','Paria që nuk besoi nga populli i tij tha: “Ne po të shohim mendjelehtë dhe të konsiderojmë vërtet rrenacak!”');
INSERT INTO "ahmeti" VALUES(7,67,'7:67','Tha (Hudi): “O populli im, nuk jam mendjelehtë (nuk kam të metë mendore), por unë jam i dërguar prej Zotit të botëve”.');
INSERT INTO "ahmeti" VALUES(7,68,'7:68','(Jam idërguar (Që t’u komunikoj shpalljet e Zotit tim dhe unë jam këshillues besnik për ju.');
INSERT INTO "ahmeti" VALUES(7,69,'7:69','Amos ju erdhi çudi që ju erdhi përmes një njeriu nga mesi juaj, e për t’ju tërhequr vërejtjen. Perkujtoni kur Ai ju bëri sundues pas popullit të Nuhut dhe ju shtoi fuqinë fizike. Përkujtoni të mirat e All-llahut që të gjeni shpëtim.');
INSERT INTO "ahmeti" VALUES(7,70,'7:70','Ata thanë: “A na erdhe (të na frikësosh) që ta adhurojmë vetëm All-llahun e të braktisim atë çka adhuronin prindërit tanë? Nëse je i vërtet (çka thua) sillna atë që na premton (kërcenohesh)”.');
INSERT INTO "ahmeti" VALUES(7,71,'7:71','(Hudi) Tha: “Juve ju gjeti dënimi dhe përbuzja nga Zoti juaj. A më polemizoni mua për emra (të idhujve) që i emëruat ju dhe prindërit tuaj, e që për ta All-llahu nuk shpalli kurfarë argumenti? Pritni pra, (dënimin) edhe unë ë bashku me ju jam duke e pritur”.');
INSERT INTO "ahmeti" VALUES(7,72,'7:72','Ne më mëshirën tonë e shpëtuam atë dhe ata që ishin me të, dhe ishkulëm nga rrënja, ata që përgenjeshtruan argumentet Tona dhe nuk ishin besimtarë.');
INSERT INTO "ahmeti" VALUES(7,73,'7:73','Edhe te (populli) i Themudit u dërguam vëllain e tyre, Salihun, e ai ju tha: “O populli im, besonie All-llahun (një) nuk keni zot tjetë pos Tij. Që, ju erdhi argumenti nga Zoti juaj. Ja, kjo devja është mrekulli për ju. Linie këtë të lirë të hajë në tokën e All-llahut dhe kurrsesi mos e merrni me të keq, e t’ju kapë dënim i dhembshëm.');
INSERT INTO "ahmeti" VALUES(7,74,'7:74','Përkujtoni All-llahun kur Ai ju bëri sundues pas Adit, ju vendosi në tokë e ju në rrafshin e saj ndërtoni pallate, kurse në kodrina ngritni shtëpia, përkujtoni të mirat e All-llahut e mos u bëni shkatërrues në tokë.');
INSERT INTO "ahmeti" VALUES(7,75,'7:75','Krerët kryelartë nga populli i tij thanë atyre që ishin më të dobët e që kishin besuar: “A e dini se me të vërtetë Salihu është i dërguar nga Zoti i tij?” Ata thanë: “Vërtet, ne jemi besimtarë të asaj me çka është dërguar”.');
INSERT INTO "ahmeti" VALUES(7,76,'7:76','Ata kryelartit thanë: “Ne jemi mojues (jobesimtarë) të asaj që ju i besuat”.');
INSERT INTO "ahmeti" VALUES(7,77,'7:77','Ata e therrën devën dhe me kryelartësi shkelën dispozitëne Zotit të tyre dhe thanë: “O Salih atë me çka na u kërcenove, nëse je prej të dërguarve”.');
INSERT INTO "ahmeti" VALUES(7,78,'7:78','Atëherë ata përjetuan tërmetin dhe u gdhinë në shtëpitë e tyre kufoma të ngrira.');
INSERT INTO "ahmeti" VALUES(7,79,'7:79','Ai uzmbraps prej tyre e tha: “O populli im, unë ju komunikova dërgesën e Zotit tim, ju këshillova sa munda, por ju nuk i përfillni këshilluesit”.');
INSERT INTO "ahmeti" VALUES(7,80,'7:80','Përkujto kur popullit të vet Luti i tha: “A punoni të shëmtuaren, që asnjë nga popujt e botës nuk e bëri para jush”.');
INSERT INTO "ahmeti" VALUES(7,81,'7:81','Vërtet, ju të shtyrë nga epshet u afroheni burrave duke i lënë gratë. Po ju jeni popull i shfrenuar”.');
INSERT INTO "ahmeti" VALUES(7,82,'7:82','Përgjigjja e popullit të tij nuk ishte tjetër vetëm se të thoonë: “Dëboni ata (Lutin me besimtarë) nga vendbanimi juaj, ata janë njerëz që ruhen shumë (e i largohen të shëmtuarës)”.');
INSERT INTO "ahmeti" VALUES(7,83,'7:83','Ne shpëtuakm atë dhe familjen e tij, pos gruas së tij që mbeti aty (ndër të shkatërruarit).');
INSERT INTO "ahmeti" VALUES(7,84,'7:84','Ne lëshuam mbi ata një lloj shiu (me gurë). E shiko se si ishte fundi i kriminelëve!');
INSERT INTO "ahmeti" VALUES(7,85,'7:85','E në Medjen (dërguam) vëllain e tyre Shuajbin. Ai tha: “O popuuli im, adhuronie All-llahun (një), ju nuk keni zot tjetër pos Tij. Juve ju erdhi mrekullia nga Zoti juaj. Zbatoni drejtë matjen dhe peshojën, e mos u bëni padrejtësi njerëzve në sendet e tyre, dhe mos bëni çrregullime në tokë pas përmirësimit të saj. Këto janë më të dobishme për ju, nëse jeni besimtarë.');
INSERT INTO "ahmeti" VALUES(7,86,'7:86','Mos zini pusi në çdo rrugë e të kërcënoheni dhe ta pengoni nga rruga e All-llahut atë që i ka besuar atij (Shuajbit), e të kërkoni shtrembërimin e asaj (e rrugës). Përkujtoni kur ishit pakicë e Ai ju shumoi dhe shikoni se sifndi i çrregullusve.');
INSERT INTO "ahmeti" VALUES(7,87,'7:87','E në qoftë se një grup prej jush është që besoi asaj me të cilën unë u dërgova, enjë grup nuk, duroni deri të gjykojë All-llahu, e Ai është gjykatësi më i mirë.');
INSERT INTO "ahmeti" VALUES(7,88,'7:88','Paria, që ishte krjelartë nga populli i tij, tha: “O Shuajb, ne do të dëbojmë ty dhe së bashku me ty edhe me ata që besuan nga fshati ynë, ose pa tjetër të ktheheni në fenë tonë”. Ai (Shuajbi) tha: “A edhe nëse ne nuk e dëshirojmë atë (kthimin)?”.');
INSERT INTO "ahmeti" VALUES(7,89,'7:89','Ne do të kemi shpifur gënjeshtër ndaj All-llahut, nëse ktheheni në fenë tuaj (idhujtare), pasi që All-llahu na shpëtoi nga ajo. Nuk është për ne të kthehemi në të vetëmnëse është dëshira e All-llahut, Zotit tonë. Zoti ynë ka përfshirë me diturinë e vet çdo send, ne ju kemi mbështetur All-llahut. Zoti ynë, vendos mes nesh dhe mes popullit tonë gjykimin tënd të drejtë, se Ti je më i miri gjykatës.');
INSERT INTO "ahmeti" VALUES(7,90,'7:90','Krerët prej popullit të tij, që nuk besuan, i thanë: (Popullit i thanë) Nëse shkoni (pranoni) pas Shuajbit, (pas fesë së tij) atëherë ju jeni me siguri të dëshpëruar.');
INSERT INTO "ahmeti" VALUES(7,91,'7:91','Ata (popullin e padëgjueshëm) i kapi tërmet i fortë dhe aguan në shtëpitë e tyre kufoma të gjunjëzuara.');
INSERT INTO "ahmeti" VALUES(7,92,'7:92','Ata që përgënjestruan Shuajbin, sikur nuk ekzistuan fare aty, ata që konsideruan Shuajbin rrenacak, vërtet ishin të dështuarit.');
INSERT INTO "ahmeti" VALUES(7,93,'7:93','E ai (Shuajbi) u kthye e tha: “O populli im, vërtet unë ju kumtova porositë e Zotit tim, ju dhashë këshilla, e si të brengosem për një popull që nuk besoi”.');
INSERT INTO "ahmeti" VALUES(7,94,'7:94','Ne nuk e dërguam asnjë pejgamber në ndonjë vendbanim e që nuk e ndëshkuam atë (popullin) me skamje e vështirësi të tjera, në mënyrë që ata të përulen (të binden).');
INSERT INTO "ahmeti" VALUES(7,95,'7:95','Mandej e zqvendësuam të keqen me të mirën derisa u shumuan ato (të mirat) e thanë: “Pridërit tanë i pat goditur skamja e mjerimi” (ky është rregull natyror, po ata nuk falënderuan). Atëherëbefas i dënuam pa e vërejtur ata.');
INSERT INTO "ahmeti" VALUES(7,96,'7:96','E sikur banorët e këtyre vendbanimeve të kishin besuar dhe të ishin ruajtur, Nedo t’ju hapnim begati nga qielli e toka, por ata përgënjeshtruan, andaj i dënuam me shkatërrim për atë që merituan.');
INSERT INTO "ahmeti" VALUES(7,97,'7:97','A mos u siguruan banorët e fshatrave nga dënimi jonë kur ata ishin fjetur (natën)?');
INSERT INTO "ahmeti" VALUES(7,98,'7:98','A mos u siguruan banorët e fshatrave nga dënimi jonë para dite kur ata ishin duke luajtur?');
INSERT INTO "ahmeti" VALUES(7,99,'7:99','A mos u siguruan ata prej ndëshkimit të All-llahut? Nuk sigurohet kush prej frikës së ndëshkimit të All-llahut pos njerëzve të humbur.');
INSERT INTO "ahmeti" VALUES(7,100,'7:100','A nuk e kanë të qartë ata që trashëguan tokën pas banorëve të saj (që u shkaktërruan) se, nëse dëshirojmë Ne i godasim (i dënojmë) për mëkatet e tyre, ua mbyllim zemrat e tyre, dhe ata nuk dëgjojnë (këshillat).');
INSERT INTO "ahmeti" VALUES(7,101,'7:101','Këto janë fshatra për të cilat po të tregojmë disa nga lajmet e tyre. Atyre iu patën ardhur të dërguarit e tyre me argumente (me mrekulli), por ata nuk i besuan asaj të cilën më parë e kishin gënjyer. Ja, kështu vulos All-llahu zemrat e atyre që nuk besojnë.');
INSERT INTO "ahmeti" VALUES(7,102,'7:102','Ne te shumica e tyre nuk gjetëm zbatimin e premtimit, e gjetëm shumicën e tyre jashtë bindjes (respektit).');
INSERT INTO "ahmeti" VALUES(7,103,'7:103','Mandej pas tyre dërgam Musain me mrekulli të argumentuara te faraoni dhe rrethi i tij, e ata i refuzuan ato, e shih se si ishte përfudimi i shkatruesve?');
INSERT INTO "ahmeti" VALUES(7,104,'7:104','Musai tha: O faraon, s’ka dyshim, unë jam i dërguar prej Zotit të botëve”.');
INSERT INTO "ahmeti" VALUES(7,105,'7:105','Është dinjitet për mua ta them për All-llahun vetëm të vërtetën. Unë u kam ardhur me argumente nga Zoti jaj, lejoi pra beni israilët të vijnë me mua!”');
INSERT INTO "ahmeti" VALUES(7,106,'7:106','Ai (faraoni) tha: “Nëse ke ardhur me ndonjë argument, dhe nëse je ai që thua, na trego pra at argument”.');
INSERT INTO "ahmeti" VALUES(7,107,'7:107','Ai (Musai) e hodhi shkopin e vet, kur ja, u shfaq gjarpër i vërtetë.');
INSERT INTO "ahmeti" VALUES(7,108,'7:108','Dhe e nxori dorën e vet, kur qe, për shikuesit dritë e bardhë.');
INSERT INTO "ahmeti" VALUES(7,109,'7:109','Rrethi i parisë nga populli i faraonit tha: “Ky nuk është tjetër pos magjistar i përsosur”.');
INSERT INTO "ahmeti" VALUES(7,110,'7:110','Ai dëshiron t’ju nxjerrë prej tokës suaj: “E çka më urdhëroni (propozoni ju)?”');
INSERT INTO "ahmeti" VALUES(7,111,'7:111','Ata (krerët) thanë: “Ndale atë dhe vëllaun e tij, e na dërgo nëpër qytete tubues (të magjistarëve).');
INSERT INTO "ahmeti" VALUES(7,112,'7:112','Të sjellin çdo magjistarë të dijshëm (të aftë).');
INSERT INTO "ahmeti" VALUES(7,113,'7:113','Magjistarët erdhën te faraoni, e thanë: “ne do të kemi shpërblim, në qoftë se dalim fitues!”');
INSERT INTO "ahmeti" VALUES(7,114,'7:114','Ai (faraoni) tha: “Po, dhe ju do të jeni prej të afërmëve të mi”.');
INSERT INTO "ahmeti" VALUES(7,115,'7:115','Ata (magjistarët) thanë: “O Musa, (zgjidh)-ose do të hedhish ti ose ne po hedhim?”');
INSERT INTO "ahmeti" VALUES(7,116,'7:116','Ai (Musai) tha: “Hidhniju”! Ekur hodhën ata (shkopinjë e litarë), magjepsën sytë e njerëzve, i frikësuan ata dhe sollën një maji të madhe.');
INSERT INTO "ahmeti" VALUES(7,117,'7:117','E Ne e frymëzuam Musain (duke i thënë): “Hidhe shkopin tënd!” Kur qe, ai gëlltiste atë që kishin magjepsuar.');
INSERT INTO "ahmeti" VALUES(7,118,'7:118','Atëherë u dëshmua e vërteta dhe u zhduk ajo që kishin përgatitur.');
INSERT INTO "ahmeti" VALUES(7,119,'7:119','Aty u mundën ata (magjistarët dhe faraoni) dhe u kthyen të poshtëruar.');
INSERT INTO "ahmeti" VALUES(7,120,'7:120','E magjistarët u hodhën (u përulën) në sexhde),');
INSERT INTO "ahmeti" VALUES(7,121,'7:121','Dhe thanë: “Ne i besuam Zotit të gjithësisë,');
INSERT INTO "ahmeti" VALUES(7,122,'7:122','Zotit të Musait dhe të Harunit!”');
INSERT INTO "ahmeti" VALUES(7,123,'7:123','E faraoni tha: “I besuat atij (Musait) para se t’ju lejoja unë? Kjo është një dredhi që ju e përgatitët në qytet (Misir) për t’i ëbuar prej tij banorët e tij (kibtët), po më vonë do ta kuptoni (çka do t’ju gjejë).');
INSERT INTO "ahmeti" VALUES(7,124,'7:124','Kam për t’ua prerë duartë dhe këmbët tërthorazi, pastaj të gjithë juve do t’ju gozhdoj.');
INSERT INTO "ahmeti" VALUES(7,125,'7:125','Ata (që besuan) hanë: “S’ka dyshim, ne jemi të kthyer te Zoti ynë”.');
INSERT INTO "ahmeti" VALUES(7,126,'7:126','Ti nuk hakmerresh ndaj nsh vetëm pse besuam në argumentet e Zotit tonë, pasi që na erdhën ata. Zoti ynë, na dhuro durim (për dënimin që do të na bën faraoni) dhe na bën të vdesim muslimanë!”');
INSERT INTO "ahmeti" VALUES(7,127,'7:127','Të parët nga populli i faraonit thanë: “A do të lejosh Musain dhe popullin e tij të bëj përçarje në tokë dhe të braktisin ty dhe zotat e tu?” Ai (faraoni) tha: “Do t’ua mbysim djemtë e do t’i lëmë të gjalla gratë e tyre për shërbim, ne jemi dominues mbi ta”.');
INSERT INTO "ahmeti" VALUES(7,128,'7:128','Popullit të vet Musai i tha: “Kërkon ndihmë prej All-llahut dhe kini durim. S’ka dyshim se toka është e All-llahut, ia lë në trashëgim atij që nga robët e tij, e ardhmja e lumtur është për të devotshmit.');
INSERT INTO "ahmeti" VALUES(7,129,'7:129','ata (populli i musait) thanë: “Ne ishim të shtypur para se të na vije ti, e edhe pasi na erdhe”. Ai (Musai) tha: “Është shpresë se All-llahu do ta shkatërrojë armikun tuaj (faraonin), e ju do t’ju bëjë ta zëvendësoni në këtë vend, e do t’ju shikojë se si veproni”.');
INSERT INTO "ahmeti" VALUES(7,130,'7:130','(Për madhërinë e Zotit) Ne e provuam popullin e faraonit me skamje dhe me pakësim të frutave, në mënyrë që të marrin mësim.');
INSERT INTO "ahmeti" VALUES(7,131,'7:131','Kur u vinte atyre e mira (viti i begatshëm), ata thonin: “Kjo është e (mira) jona thjesht”. E kur i godiste ndonjë e keqe, fatin e zi ia përshkruanin Musait dhe atyre (besimtarëve) që ishin me të. Veni re, fati i tyre është te All-llahu (e jo te Musai), por shumica e tyre këtë nuk e dinin.');
INSERT INTO "ahmeti" VALUES(7,132,'7:132','Ata thanë: “Me çfarëdo argumenti të na vijshë që me te të na magjepsesh (largosh nga feja që kemi), ne nuk do të besojmë ty”.');
INSERT INTO "ahmeti" VALUES(7,133,'7:133','Atëherë (për shkak të mohimit) Ne lëshuam kundër tyre: vërshimin, karkalecat, rriqërat (insekte dëmtuese), bretkocat dhe gjakun, fakte të gjalla njëra pas tetrës, po ata mbanin kokëfortësi sepse ishin mëkatar.');
INSERT INTO "ahmeti" VALUES(7,134,'7:134','Pasi që i gjeti belaja (me ato masa dënimi) ata thanë: “O Musa, lute ër ne Zotin tënd me atë meritë që ke (si pejgamber), nëse na e largon dënimin ne do të pranojmë ty (si të dërguar) dhe do t’i lejojmë beni israilët bashkë me ty (të shkoni ku të doni).');
INSERT INTO "ahmeti" VALUES(7,135,'7:135','E kur e larguam nga ata dënimin (me lutjen e Musait) për deri në një afat që do të arrinin, ata e thyen besën.');
INSERT INTO "ahmeti" VALUES(7,136,'7:136','Atëherë ndërmorën kundër tyre dhe i fundosëm në det, ngase përgënjeshtruan argumentet Tonadhe nuk qanë kokën për to.');
INSERT INTO "ahmeti" VALUES(7,137,'7:137','E atij populli që ishte i nënshtruar i trashëgam lindje e perëndim të tokës, që Ne e bekuam (me të mira), ndërsa për durimin që patën, u plotësua fjala më e mirë (premtimi i vërtetë) e Zotit ndaj beni israilve; rrënuan atë që bënte faraoni i tij, si dhe atë që kishin ndërtuar ata.');
INSERT INTO "ahmeti" VALUES(7,138,'7:138','Bni israilët i kaluam përtej detit, e ata me një popull që adhuronte do statuja të tyre, dhe thanë: “O Musa, na e bën edhe ne një zot (statujë) si zotët që i kanë ata (ai popull)”. Ai (Musai) tha: “Ju jeni popull që nuk dini”.');
INSERT INTO "ahmeti" VALUES(7,139,'7:139','Vërtet, ai popull (që po adhuron idhuj) është i shkatërruar në atë (adhurim) dhe ajo që vepruan është e agsjësuar (s’ka dobi).');
INSERT INTO "ahmeti" VALUES(7,140,'7:140','Musai tha: “Mos deshët, pos All-llahut, të kërkoj për ju zot tjetër, kurse Ai ju vlerësoi mbi njerëzit e tjerë?”');
INSERT INTO "ahmeti" VALUES(7,141,'7:141','Përkujtoni (o beni israil) kur Ai ju shpëtoi prej popuulit të faraonit, që u shtroi më të keqen mundm, ua mbyti djemt tuaj, ua la të gjalla gratë tuaja. E ju me këto ishit në sprovë të madhe nga Zoti juaj”.');
INSERT INTO "ahmeti" VALUES(7,142,'7:142','Dhe Ne i caktuam Musait tridhjetë net (afatin për shpallje), e ato i plotësuam edhe me dhjetë dhe kështu u mbush afati për takim Zotin e tij në katërdhjetë net. Ndërsa Musai vëllait të vet Harunit, i tha: “Më zëvendëso mua te populli im dhe rregulloo, e mos shko rrugës së të këqinjve”.');
INSERT INTO "ahmeti" VALUES(7,143,'7:143','E kur Msai erdhi në kohën që i caktuam dhe i foli Zoti i vet, ai tha: “Zoti im! Më mundëso pamjen tënde e të shikoj!” Ai (Zoti) i tha: “Ti nuk ke mundësi të më shohësh, por shiko kodrën, e nëse ajo qëndron në vendin e vet, ti do të shohësh Mua. Kur u drejtua kah kodra, një pjesë e dritës nga Zoti i tij e bëri atë (kodrën) thërmi, e Musait i ra të fikët. Kur erdhi në vete, tha: “E lartë është madhëria Jote, pendohem te Ti (për atë që kërkova), dhe unë jam i pari i besimtarëve!”');
INSERT INTO "ahmeti" VALUES(7,144,'7:144','Ai (All-llahu) tha: “O Musa, Unë të gradova ty bi njerëzit me shpalljen Time dhe me të folurit tim. Merr atë që të dhash dhe bëhu mirënjohës.');
INSERT INTO "ahmeti" VALUES(7,145,'7:145','Dhe i përshkruan atij në pllaka çdo gjë (që i nevojitej), si këshillim dhe sqarim për secilin send. “Merri këto me seriozitet dhe urdhëroje popullin tënd që t’i përvetësojë më të mirat e tyre (dispozitat me shpërblim më të madh). Unë do t’ua tregoj vendbanimin mëkatarëve.');
INSERT INTO "ahmeti" VALUES(7,146,'7:146','Unë do t’i zbraps nga argumentet e Mia ata të cilët pa pasur drejtë bëjnë kryelarësi në tokë, të cilët edhe nëse shohin çdo argument nuk besojnë, dhe nëse shohin udhën e shpëtimit, nuk e marrim atë rrugë, nëse e shohin rrugën e gabuar atë e marrin rrugë. Këtë (verbërim të tyre), ngase ata i konsideruan të rreme faktet Tona dhe ngase i lanë pas dore ato.');
INSERT INTO "ahmeti" VALUES(7,147,'7:147','Ata të cilët i mohuan argumentet Tona dhe takimin në ditëne botës së ardhshme, ata asgjësuan veprat e tyre. A shpërblehen ata (më thevab ose me dënim) pos sipas asaj si kanë vepruar.');
INSERT INTO "ahmeti" VALUES(7,148,'7:148','Populli i Musait, pas tij (kur ai shkoi në kodrën Tur për pranimin e shhpalljes) e mbaroi nga stolitë e tyre një figurë viçi që kishte britmë (Pallte). A nuk e shihnin ata se ai nuk ju fliste as nuk i udhëzonte në ndonjë rrugë të drejtë. E morën atë (e adhuruan si idhull viçin), e ishin të padrejtëndaj vetes së tyre.');
INSERT INTO "ahmeti" VALUES(7,149,'7:149','E kur në duart e tyre u thye (ajo vepër e shëmtuar u penduan) dhe e kuptuan se me të vërtetë kanë gabuar thanë: “Nëse nuk na mëshiron Zoti ynë dhe nuk na falë gabimin, ne padyshim do të jemi nga më të dëshpruarit.');
INSERT INTO "ahmeti" VALUES(7,150,'7:150','E kur u kthye Musai shumë i hidhëruar te populli i vet, tha: “Sa keq më paskeni zëvendësuar pas shkuarjes sime! A e ngutët (pritjen) urdhërin e Zotit tuaj? I hodhi pllakat dhe e zuri për (flokë) koke vëllain e vet duke e tërhequr kah vetja”. Ai (vëllai Harun) tha: “O bir i nënës sime, populli më mundi dhe gati më mbyti, mos i kënaq me mua armiqtë de mos më konsidero mua me njerëzit mizorë”.');
INSERT INTO "ahmeti" VALUES(7,151,'7:151','(Musai) Tha: “Zoti im, më falë mua dhe vëllain tim dhe na dhuro mëshirën Tënde, se Ti je mëshirues i mëshiruesve.');
INSERT INTO "ahmeti" VALUES(7,152,'7:152','Nuk ka dyshim se ata që adhuruan viçin ka për t’i përfshirë përbuzja nga Zoti i tyre dhe nënçmimi në jetën e dynjasë. Kështu, i shpërblrjmë (me dënim) trilluesit e rrenës.');
INSERT INTO "ahmeti" VALUES(7,153,'7:153','E ata që ënë vepra të këqia e pastaj u penduan pas tyre, dhe besuan sinqerisht, Zoti yt ua falë gabimet dhe është mëshirues pas tij (pas pendimit të tyre).');
INSERT INTO "ahmeti" VALUES(7,154,'7:154','E kur iu ndalë hidhërimi Musait, ai mori pllakat (që i pat hedhur), e në tekstin e tyre ishin (shënuar) udhëzime e mëshirë për ata që kanë frikë ndaj Zotit të tyre.');
INSERT INTO "ahmeti" VALUES(7,155,'7:155','Musai zgjodhi nga populli i vet shtatëdhjetë veta për kohën që Ne ia caktuam takimin. E pasi që kapi ata dridhja (e tokës), ai tha: “Zoti im!, sikur të kishe dashur Ti, i kishe zhdukur më parë ata dhe mua. A po na zhduk për atë që bënë të marrët nga ne? Kjo është vetëm sprovë e Jotja, që me të e bën të humbur atë që do, dhe e vë në rrugë të drejtë atë që do. Ti je mbrojtësi ynë, pra falna e mëshirona, se Ti je më i miri që falë (gabimet).');
INSERT INTO "ahmeti" VALUES(7,156,'7:156','Dhe cakto për ne (jetë) të mirë në dynja dhe të mirë në botën tjetër, pse vërtet jemi kthyer kah Ti. Ai (Zoti) tha: “Dënimi im është ai me të cilin e godas cilindua, e mëshira Ime ka përfshirë secilin send. Atë (Mëshirën) do ta caktoj për ata të cilët i ruhen (mëkateve), e japon zeqatin dhe për ata që argumentet tona i besojnë.');
INSERT INTO "ahmeti" VALUES(7,157,'7:157','Që pranojnë të dërguarin (Muhammedin), Pejgamberin arab, (që nuk shkruan as nuk lexon), të cilin e gjejenë të cilësuar (të përshkruar me virtytet e tij), te ata në Tevrat dhe në Inxhil, e që i urdhëron ata për çdo të mirë dhe i ndalon nga çdo e keqe, u lejon ushqimet e këndëshme dhe u ndalon ato të pakëndshmet, dhe heq nga ata barrën e rëndë të tyre dhe prangat që ishin mbi ta. Pra, ata të cilët e besojnë atë, e ndërojnë dhe e ndihmonë, veprojnë me dritën që iu zbrit me të, të tillët janë të shpëtuarit.');
INSERT INTO "ahmeti" VALUES(7,158,'7:158','Thuaj (Muhammed): “O ju njerëz! Unë jam i dërgari i All-llahut te të gjithë ju. All-llahut që vetëm i Tij është sundimi i qiejve e i tokës, s’ka të adhuruar tjetër pos Tij; Ai jep jeë dhe Ai jep vdekje, pra besoni All-llahun dhe të dërguarin e Tij, pejgamberin e pashkolluar, që beson All-llahun dhe shpalljet e Tij, ndiqnie rrugën e tij që ta gjeni të vërtetën.');
INSERT INTO "ahmeti" VALUES(7,159,'7:159','Edhe prej populloit të Musait qe një grup që udhëzoi drejtë dhe iu përmbajt asaj (drejtësisë).');
INSERT INTO "ahmeti" VALUES(7,160,'7:160','Ne i ndamë ata (populln e Musait) nëdymbëdhjetë grupe fises, e kur Musait i kërkoi ujë populli i vet, Ne e udhëzuam (duke thënë): “Bjeri me shkopin tënd gurit” (ai ira) dhe prej tij (gurit) gufuan dymbëdhjetë kroje, ashtu që secili grup e dinte krojin e vet. Ne bëmë që retë t’u bëjmë hije atyre dhe u sollëm rrëshirë dhe shkutëza (dhe u thamë): “Hani nga të mirat me të cilat ju dhuruam!” (e pse ishit përbuzës). Ata nuk na dëmtuan Ne, por e dëmtuan vetveten e tyre (ngase u dënuan).');
INSERT INTO "ahmeti" VALUES(7,161,'7:161','(përkujto edhe këtë) Kur atyre iu tha: “Banoni në këtë fshat (vendbanim) dhe hani nga (frutat e tij) kah të doni e thuani: “Falje” dhe në derë hyni të përulur, se Ne do t’ua shtojmë edhe më punëmirëve!”');
INSERT INTO "ahmeti" VALUES(7,162,'7:162','E ata prej tyre q ishin mizorë ndrushuan fjalë tjetër nga ajo që iu kishte thënë, atëherë Ne zbritëm kundër tyre një dënim nga qielli për shkak se ishin zullumqarë.');
INSERT INTO "ahmeti" VALUES(7,163,'7:163','Dhe pyeti ti (Muhammed) për fshatin që ishte në breg të detit e ata e shkelën rendi e të shtunës (që e kishin të ndaluar gjuajtjen e peshqve), kur në të shtunën e tyre peshqit u vinin sheshazi mbi ujë, e në ditën që nuk festonin nuk u vinin. Ja, kështu i sprovuam ata ngase ishin mëkatarë.');
INSERT INTO "ahmeti" VALUES(7,164,'7:164','Dhe kur një grup prej tyre thanë: “Përse këshilloni një popull që All-llahu do ta shkatërrojë ose dënojë me një dënim të ashpër?” Thanë (këshilluesit): “Arsyetimi para Zotit tuaj dhe me shpresë që t’ju largohen gabimeve”.');
INSERT INTO "ahmeti" VALUES(7,165,'7:165','E kur braktisën atë për të cilën ishin këshilluar, Ne i shpëtuam ata që pengonin nga të këqiat, ndërsa ata që kundërshtuan i kapëm me një dënim të fortë, ngase ishin të shfrenuar.');
INSERT INTO "ahmeti" VALUES(7,166,'7:166','E kur ata tejkaluan me arogancë të hapët, nga ajo që ishin të ndaluar, Ne i shndërruam në majmunë të përbuzur.');
INSERT INTO "ahmeti" VALUES(7,167,'7:167','Përkujto (o i dërguar) kur Zoti Yt shpalli qartas se mbi ta do të vë, deri në ditën e kijametit, sundimin e ndinjë që ka për t’ua shijuar atyre mundimin më të shëmtuar. Vërtet Zoti yt ndërmerr shpejt dënimin, Ai edhe falë e mëshiron shumë.');
INSERT INTO "ahmeti" VALUES(7,168,'7:168','Ne i shpërndajmë ata në grupe nëpër tokë; prej tyre ka të mirë, por edhejo të tillë. Ne i provuam me të mira e me të këqia, në mënyrë që të tërhiqen nga e keqja.');
INSERT INTO "ahmeti" VALUES(7,169,'7:169','e pas tyre erdhi brezi që trashëgoi librin. Këta merrnin mjete të pavlerë të kësaj bote (lakmues që nuk dallonin të mrën a të keqen) e thonin: “Do të na falje”, e merrnin atë përsëri. A nuk është marrë prej tyre zotimi në librin (Tevratin) se nuk do të thonë ndaj All-llahut tjetër pos të vërtetës dhe ata e dinim mirë se çka në të (libër). Po, a nuk po kuptoni se bota tjetër është më e mirë (se ajo çka merrnit ju) për ata që ruhen.');
INSERT INTO "ahmeti" VALUES(7,170,'7:170','Po atyre që i përmbahen librit dhe falin namazn, Ne nuk u humbin shpërblimin të të mirëve.');
INSERT INTO "ahmeti" VALUES(7,171,'7:171','Përkujto kur ngritën kodrën mbi ta si re, e ata menduan se ajo do të bie mbi ta. (Ne u thamë): Merrni këtë që u dhamë me kujdes dhe përkujtoni ç’keni në të, e të ndaleni nga ajo që është e ndaluar.');
INSERT INTO "ahmeti" VALUES(7,172,'7:172','Përkujto kur Zoti Yt nxori nga shhpina e bijve të Ademit pasardhësit e tyre dhe i bëri dëshmues të vetes së tyre (duke u thënë): “A nuk jam Zoti juaj?” Ata thanë: “Po, dëshmuam!” Të mos thoni në ditën e kijametit: “Ne nga ky (dëshmim) ishim të panjohur.');
INSERT INTO "ahmeti" VALUES(7,173,'7:173','Ose të mos thoni: “Prindërit tanë më parë ishin idhujtarë, e ne ishim pasardhës të tyre. A do të na shkatërrosh ne për atë që bënë ata asgjësues të së vërtetës?”');
INSERT INTO "ahmeti" VALUES(7,174,'7:174','Po kështu u sqarojmë argumentet, që ata të kthehen nga e gabuarja në të vërtetën.');
INSERT INTO "ahmeti" VALUES(7,175,'7:175','Dhe lexoju atyre tregimin e atij që patëm dhënë dituritë Tona, ndërsa ai u zhvesh prej tyre dhe atëherë atë e shoqëroi shejtani dhe kështu ai u bë prej të humburëve.');
INSERT INTO "ahmeti" VALUES(7,176,'7:176','E sikur të donim Ne, do ta ngrisnim lartë me (dituri), por ai nuk iu largua tokës (dynjasë) dhe shkoi pas epshit të vet. Shembulli i tij është si ai i qeit, të cilin nëse e përzë ai e nxjerr gjuhën, po edhe nëse nuk e përzë, ai sërish e nxjerr gjuhën. Ky është shembuli i atyre që i konsideruan të rreme argumentet Tona. Ti rrëfe tregimet (ymetit tënd) në mënyrë që ata të mendojnë.');
INSERT INTO "ahmeti" VALUES(7,177,'7:177','Shembulli i keq është populli, që përgenjeshtroi faktet Tona dhe e dëmtoi vetveten.');
INSERT INTO "ahmeti" VALUES(7,178,'7:178','Atë që All-llahu e udhëzon në rrugë të drejtë, e atë që e humb, e ata janë të dëshpruarit.');
INSERT INTO "ahmeti" VALUES(7,179,'7:179','Ne krijuam shumë nga xhinët e njerëzit për Xhehennem. Ata nuk kanë zemra që me to nuk kuptojnë, ata kanë sy që me ta nuk shohin dhe ata kanë veshë që me ta nuk dëgjojnë. Ata janë si kafshët, bile edhe më të humbur, të tillët janë ata të marrët.');
INSERT INTO "ahmeti" VALUES(7,180,'7:180','All-llahu ka emrat më të mirë, andaj Atë thirrnie me ata e hiquni nga ata që shtrembërime me emrat e Tij. Ata kanë për t’u shpërblyer (me dënim) për veprimet e tyre.');
INSERT INTO "ahmeti" VALUES(7,181,'7:181','E ndër ata që krijuam Ne, ka njerëz që udhëzojnë në të drejtën e edhe veprojnë me të.');
INSERT INTO "ahmeti" VALUES(7,182,'7:182','Ndërsa ata që i konsideruan të rreme argumente Tona, Ne do t’i shpijmë në humbje në mënyrë graduale kah nuk ekuptojnë ata.');
INSERT INTO "ahmeti" VALUES(7,183,'7:183','Mirpo, atyre u jap afat, se kapja (dënimi) Ime është e fortë.');
INSERT INTO "ahmeti" VALUES(7,184,'7:184','A nuk menduan ata se ai bashkëkohaniku i tyre (Muhammedi) nuk ka farë çmendurie, ai është vetëm qortues i hapët.');
INSERT INTO "ahmeti" VALUES(7,185,'7:185','A nuk vështruan ata me vëmendje pushtetin e madh në qiej e në tokë dhe çka krijoi All-llahu prej sendeve, e edhe në atë se ndoshta u është afruar afati i tyre i vdekjes. Atëherë, cilës bisedë pos kësaj (Kur’anit) do ti besojnë?');
INSERT INTO "ahmeti" VALUES(7,186,'7:186','Për atë që All-llahu e la në humbje, s’do tëketë udhëzues, dhe ata do t’i lë të bredhin në vrazhdësinë e tyre.');
INSERT INTO "ahmeti" VALUES(7,187,'7:187','Të pyesin ty (Muhammed) për çastin (katastrofën e përgjithshme) se kur do të ndodhë ai. Thuaj: “Atë e di vetën Zoti im, kohën e tij nuk mund ta zbulojë kush pos Tij (e sjell kijametin pa e hetuar asnjë nga krijesat). çështja e tij (se kur do të ndodh) është preokupim i rëndë (për krijesat) në qie e në tokë. Ai (katastrofa) nuk ju vjen ndryshe, vetëm befas. Të pyesin ty sikur ti di për të. Thuaj: “Për të di vetëm All-llahu, por shumica e njerëzve nuk e dinë (pse është e fshehtë)”.');
INSERT INTO "ahmeti" VALUES(7,188,'7:188','Thuaj: “Unë nuk kam në dorë për veten time as ndonjë dobi, as ndonjë dëm, pos çka do All-llahu. Sikur ta dija të fshehtën, do të shumoja për vete të dobishmet, e nuk do të më prekte gjë e keqe. Unë nuk jam tjetër vetëm se qortues dhe përgëzues për njerëzit që besojnë.');
INSERT INTO "ahmeti" VALUES(7,189,'7:189','Ai (All-llahu) është Ai që ju krijoi prej një vete, e prej saj për t’u qëtësuar pranë saj. Kur e mbuloi ai (mashkulli) atë (gruan), ajo u ngarkua me një barrë të lehtë dhe ashtu vazhdoi me të, e kur u bë e rëndë, ata të dy lutën All-llahun, Zotin e tyre: “Nëse na jep një (fëmijë) të mirë, ata (pa të meta), ne do të jemi falendërues ndaj Teje”.');
INSERT INTO "ahmeti" VALUES(7,190,'7:190','Pasi që atyre të dyve ju dha (fëmijë) të mirë, ata (fëmijë e pasardhës) ipërshkruan shokë në atë që iu dha. I pastër është All-llahu nga ajo që ia përshkruajn.');
INSERT INTO "ahmeti" VALUES(7,191,'7:191','I përshkruajnë shok atë çka nuk mund të të krijojë asnjë send, e ata (idhujt (vetë janë të krijuar.');
INSERT INTO "ahmeti" VALUES(7,192,'7:192','Ata (idhujt) nuk mund t’ju sjellin atyre ndinjë ndihmë e as veten e tyre ta ndihmojnë.');
INSERT INTO "ahmeti" VALUES(7,193,'7:193','Dhe, edhe nëse i thirrni për t’i udhëzuar, ata nuk ju përgjigjen. Për ju është njejtë, i thirrët ata ose heshtët.');
INSERT INTO "ahmeti" VALUES(7,194,'7:194','S’ka dyshim se ata që po i adhuroni ju pos All-llahut, janë të krijuar sikur ju (njerëzit janë më të përsosur). Thirrni pra ata, e le t’ju përgjigjen juve nëse thuani të vërtetën.');
INSERT INTO "ahmeti" VALUES(7,195,'7:195','A kanë ata (idhujt) këmbë të ecin me to, akanë duar të rrëmbejnë me to, a kanë s të të shohin me ta? Thuaj: “Thirrni shokët (zotët) tuaj r thurrni kurtha për mua e mos pritni”.');
INSERT INTO "ahmeti" VALUES(7,196,'7:196','Mbrojtësi i im është All-llahu që zbriti librin. Ai kujdeset për të mirët.');
INSERT INTO "ahmeti" VALUES(7,197,'7:197','Mbrojtësi i im është All-llahu që zbriti librin. Ai kujdeset për të mirët.');
INSERT INTO "ahmeti" VALUES(7,198,'7:198','Dhe nëse i thirrni për udhëzim, nuk dëgjojnë, e të duken se po të shikojnë ty, po ata nuk shohin.');
INSERT INTO "ahmeti" VALUES(7,199,'7:199','Ti (Muhammed) merre të lehtën, urdhëro për të mirë dhe hiqu prej të padijshmëve.');
INSERT INTO "ahmeti" VALUES(7,200,'7:200','E nëse të godet shejtani me ndonjë vesvese, ti kërko strehim te All-llahu, sepse vërtet Ai dëgjon e di.');
INSERT INTO "ahmeti" VALUES(7,201,'7:201','Vërtet, ata që janë të ruajtur, kur i prek ndonjë iluzion nga djalli, ata përkujtojnë (All-llahun), nga djalli, ata përkujtojnë (All-llahun), dhe atëherë shohin (të vërtetën).');
INSERT INTO "ahmeti" VALUES(7,202,'7:202','E vëllezërit e tyre (jo të ruajturit) i përkrahin (shejtanët) për në humbje dhe nuk ndahen.');
INSERT INTO "ahmeti" VALUES(7,203,'7:203','Dhe, kur ti nuk u sjell atyre ndonjë mrekulli (që e kërkojnë) thonë: “Përse ti nuk e trilove vetë?” Thuaj “Unë (nuk trilloj) i përmbahem vetëm asaj që më shpallet nga Zoti im, ky (Kur’ani) është argument (me të cilin ndriçohe zemrat) nga Zoti juaj, është udhërrëfes dhe mëshirë për popullin që beson.');
INSERT INTO "ahmeti" VALUES(7,204,'7:204','Kur lexohet Kur’ani, ju dëgjoni atë (me vëmendje) dhe heshtni, në mënyrë që të fitoni mëshirë.');
INSERT INTO "ahmeti" VALUES(7,205,'7:205','Ti përmende Zotin tënd në vete (heshtas), me respekt e me dro, jo me shprehje të larta, (përmende) në mëngjes e në mbrëmje dhe mos u bërë prej atyre që nuk kanë kujdes.');
INSERT INTO "ahmeti" VALUES(7,206,'7:206','S’ka dyshim se ata që janë pranë (afër) Zotit tënd (melekët) nuk tërhiqen nga adhurimi ndaj Tij nga mendjemadhësia, Atë e madhërojnë dhe vetëm Atij i bëjnë sexhde.');
INSERT INTO "ahmeti" VALUES(8,1,'8:1','Të pyesin ty (Muhammed) për plaçkën (e fituar në luftë), thuaju: “Plaçka, (mënyra e ndarjes së saj) është çështje e All-llahut dhe të dërguarit, prandaj kini frikë All-llahun, përmirësoni gjendjen e unitetit tuaj dhe nëse jeni besimtarë, respektone All-llahun dhe të dërguarin e Tij.');
INSERT INTO "ahmeti" VALUES(8,2,'8:2','E, besimtarë të vërtetë janë vetëm ata, të të cilëve kur përmendet All-llahu u rrënqethen zemrat e tyre, kur u lexohen ajetet e Tij u shtohet besimi, dhe që janë të mbështetur vetëm te Zoti i tyre.');
INSERT INTO "ahmeti" VALUES(8,3,'8:3','Dhe, të cilët falin (rregullisht) namazin dhe nga ajo, me çka Ne i furnizuam, ata japin.');
INSERT INTO "ahmeti" VALUES(8,4,'8:4','Të tillët janë besimtarë të vërtetë dhe për këtë kanë vende të larta te Zoti i tyre, kanë falje dhe kanë furnizim në mënyrë të ndershme.');
INSERT INTO "ahmeti" VALUES(8,5,'8:5','(Mospajtimi i tyre për plaçkë është) Ashtu si ai kur të nxori Zoti yt nga shtëpia jote për të vërtetën, e një grup nga besimtarët nuk ishte i kënaqur.');
INSERT INTO "ahmeti" VALUES(8,6,'8:6','Bëjnë polemikë me ty për të vërtetën pasi që e kishin të qartë (se ti veron me lejen e Zotit) sikur me qenë se shtyheshin në vdekje të sigurt.');
INSERT INTO "ahmeti" VALUES(8,7,'8:7','Përkujtoni kur All-llahu ju premtoi njerën prej dy grupeve se është juaja, e ju dëshironit t’ju takojë ai (grup) i paarmatosur, ndërsa All-llahu dëshiroi që me premtimet e Tij ta vendosë të vërtetën, e të zhdukë në themel mbeturinën e mohusve.');
INSERT INTO "ahmeti" VALUES(8,8,'8:8','Ta forcojë të vërtetën e ta çrrënjosë të kotën, edhe pse këtë e urrejnë kriminelët.');
INSERT INTO "ahmeti" VALUES(8,9,'8:9','Përkujtoni kur kërkuat ndihmë prej Zotit tuaj, e Ai ju është përgjigjur: “Unë do t’ju ndihmoj me njëmijë engjëj që vijnë një pas një (grup pas grupi).');
INSERT INTO "ahmeti" VALUES(8,10,'8:10','All-llahu nuk e bëri atë (ndihmën) për tjetër (t’u japë myzhde) dhe për t’i forcuar (qetësuar) me të zemrat tuaja, pse ndihma në realitet është vetëm prej All-llahut. All-llahu është mbizotërues dhe i urtë.');
INSERT INTO "ahmeti" VALUES(8,11,'8:11','Dhe kur Ai ju kaploi me një kotje (gjumë) që ishte siguri për ju nga ana e Tij, ju lëshoi shi nga qielli për t’ju pastruar me të, largoi prej jush shtimet e shejtanit, e që të fuqizojë bindjen në zemrat tuaja dhe t’ju përforcoj me të (me shi) këmbët tuaja.');
INSERT INTO "ahmeti" VALUES(8,12,'8:12','Edhe kur Zoti yt u kumtoi engjëjve se: “Unë jam me ju, pra, inkurajoni ata që besuan! Unë do të hedh frikë në zemrat e atyre që nuk besuan, e ju goditni në qafë e lartë, mëshonju atyre në çdo gjymtyrë (gishtërinj).');
INSERT INTO "ahmeti" VALUES(8,13,'8:13','Këtë (ndëshkim për ta) ngase kundërshtuan All-llahun dhe të dërguarin e Tij, e kush kundërshton All-llahun dhe të dërguarn e Tj, nuk ka dyshim se All-llahu është ndëshkimfortë.');
INSERT INTO "ahmeti" VALUES(8,14,'8:14','Kështu e keni (dënimin), pra shijone, e për mohuesit është edhe dënimi i zjarrit.');
INSERT INTO "ahmeti" VALUES(8,15,'8:15','O ju që besuat! Kur të ndesheni në turmën (që lëviz ngadalë) e atyre që mohuan, mos ua ktheni shpinën.');
INSERT INTO "ahmeti" VALUES(8,16,'8:16','Kush ua kthen atyre shpinën, në atë moment veç atij që kthehet për të luftuar ose përt’iu bashkëngjitur një grupi tjetër, ai ka tërhequr kundër vetes hidhërimin e All-llahut dhe vendi i tij është Xhehennemi. E ai është përfundim i kq.');
INSERT INTO "ahmeti" VALUES(8,17,'8:17','Ju nuk i mbytët (në të vërtetë) ata, por All-llahu (me ndihmën që ua dha) i mbyti ata, dhe ti nuk i gjuajte ata, por All-llahu (të ndihmoi) i gjuajti, e (bëri këtë) për t’i shpërblyer besimtarët me një dhunti të mirë nga ana e Tij. All-llahu gjithçka dëgjon dhe di.');
INSERT INTO "ahmeti" VALUES(8,18,'8:18','Ja kjo është e vërteta. All-llahu dobëson dredhitë e mohusve (kufarëve).');
INSERT INTO "ahmeti" VALUES(8,19,'8:19','Nëse ju (idhujtarë) kërkuat fitore, ja, ku e keni fitorën, po nëse hiqni dorë (nga lufta kundër Pejgamberit), ajo do të jetë në dobinë tuaj, e nëse ju ktheheni, edhe Ne kthehemi. Ana (grumbulli) juaj nuk do t’ju vlejë asgjë edhe nëse është e madhe, sepse All-llahu është me besimtarët.');
INSERT INTO "ahmeti" VALUES(8,20,'8:20','O ju që besuat, respektoni All-llahun dhe të dërguarin e Tij, e mos e braktisni atë se ju dëgjoni.');
INSERT INTO "ahmeti" VALUES(8,21,'8:21','Dhe mos u bëni si ata që thanë: “Dëgjuam”, por nuk dëgjojnë.');
INSERT INTO "ahmeti" VALUES(8,22,'8:22','Vërtet gjallesat më të dëmshme te All-llahu janë ata të shurdhëtit, memecët, të cilët nuk logjikojnë.');
INSERT INTO "ahmeti" VALUES(8,23,'8:23','Sikur të dinte All-llahu për ndonjë të mirë prej tyre, do t’i bënte të dëgjojnë, po edhe sikur t’i bënte të dëgjojnë, ata do të zbrapseshin të shtrembër.');
INSERT INTO "ahmeti" VALUES(8,24,'8:24','O ju që besuat, përgjigjuni All-llahut dhe të dërguarit kur ju ftojnë për atë që ju jetëson (ju ngjall), dhe dijeni se All-llahu ndërhyn ndërmjet njeriut dhe zemrës së tij, dhe se te Ai do të tuboheni!');
INSERT INTO "ahmeti" VALUES(8,25,'8:25','Ruajuni nga sprovimi (fitneja) që nuk godet vetëm ata që bënë mizori vetëm prej jush, dhe ta dini se All-llahu është Ndëshkues i Rreptë.');
INSERT INTO "ahmeti" VALUES(8,26,'8:26','Përkujtoni kur ishit pakicë e të dobët në tokë, të frikësuar se do t’ju rrëmbejnë njerëzit, e Ai ju mundësoi vend të dhe ju përkrahu me ndihmën e Tij, ju furnizoi me të mira, që të jeni mirënjohës.');
INSERT INTO "ahmeti" VALUES(8,27,'8:27','O ju që besuat, mos e tradhtoni All-llahun dhe të dërguarin, që të tradhëtoni amanetet tuaja, ndërsa ju e dini.');
INSERT INTO "ahmeti" VALUES(8,28,'8:28','Dhe, dijeni se pasuria e juaj dhe fëmijët tuaj janë vetëm sprovë, dhe se te All-llahu është shpërblimi i madh.');
INSERT INTO "ahmeti" VALUES(8,29,'8:29','O ju që besuat, nëse keni frikë All-llahun, Ai do të vërë udhëzim (në zemrat tuaja) për ju, do t’ua mbulojë të këqiat, do t’ua falë mëkatet. All-llahu është dhurues i madh.');
INSERT INTO "ahmeti" VALUES(8,30,'8:30','Përkujto (o i dërguar) kur ata që nuk besuan thurnin kundër teje; të ngujojnë, të mbysin ose të dëbojnë. Ata bënin plane, e All-llahu është më i miri që asgjëson (dredhitë).');
INSERT INTO "ahmeti" VALUES(8,31,'8:31','E kur atyre u lexohen ajetet Tona (Kur’ani), thonin: “Kemi dëgjuar (këso fjalësh) dhe sikur të donim edhe ne do të thonim diçka të ngjajshme me këtëdhe se ky nuk është gjë tjetër pos mit i lashtë”.');
INSERT INTO "ahmeti" VALUES(8,32,'8:32','Dhe (përkujto) kur thanë: “O All-llah! nëse është ky (Kur’ani) vërtet prej Teje, lësho gur nga qielli kundër nesh, ose sillna ndonjë dënim të idhët.');
INSERT INTO "ahmeti" VALUES(8,33,'8:33','Po All-llahu nuk do t’i dënojë ata, derisa ti (Muhammed) je në mesin e tyre dhe All-llahu nuk do t’i dënojë, derisa ata kërkojnë falje (istigfarë).');
INSERT INTO "ahmeti" VALUES(8,34,'8:34','çka kanë ata që të mos i dënojë All-llahu, kur ata janë që pengojnë (të tjerët) nga xhamia e shenjtë (Qabja), e edhepse nuk janë mbikëqyrës të saj. Kujdestarët e saj janë vetëm ata që ruhen (të devotshmit), por shumica e tyre nuk është që di.');
INSERT INTO "ahmeti" VALUES(8,35,'8:35','Lutja e tyre pranë shtëpisë (Qabes) nuk ishe tjetër veçëse britmë dhe duartrokitje, prandaj vuane dënimin për shkak se mohonit.');
INSERT INTO "ahmeti" VALUES(8,36,'8:36','Ata që nuk besuan, shpenzojnë pasurinë e tyre për të penguar nga rruga e All-llahut. Ata do të shpenzojnë atë dhe ajo do të bëhet dëshprim i tyre, e madje ata do të mposhten. E ata që mohuan, do të përmblidhen vetëm në Xhehennem.');
INSERT INTO "ahmeti" VALUES(8,37,'8:37','(Kjo masë) Që ta dallaojë All-llahu të keqin nga i miri, dhe që të këqinjët njëri mbi tjetrin, t’i hedhë në Xhehennem. E të tillët janë më të dëshpruarit.');
INSERT INTO "ahmeti" VALUES(8,38,'8:38','Thuaju atyre që nnuk besuan nëse heqin dorë (nga rruga e tyre e gabuar dhe besojnë) do t’u falet e kaluara, po nëse vazhdojnë, ligji (i Zotit), izbatuar ndaj të parëve ëhtë i ditur (edhe ju do t’ju zë).');
INSERT INTO "ahmeti" VALUES(8,39,'8:39','Luftoni ata derisa të mos mbetet idhujtari (besim i kotë), e i tërë adhurimi të bëhet vetëm për All-llahun. Po nëse ata ndalen (i japin fund mosbesimit), All-llahu është mbikëqyrës për atë që veprojnë.');
INSERT INTO "ahmeti" VALUES(8,40,'8:40','Po nëse refuzojnë, ta dini se All-llahu është mbrojtës i juaji. E, sa mbikëqyrës e ndihmës i mirë është Ai.');
INSERT INTO "ahmeti" VALUES(8,41,'8:41','Ju (besimtarë) ta dini se një e pesta e asaj që fituat, nga ndonjë send, i takon (përkujtuesve të) All-llahut, të dërguarit të Tij, të afërmve të tij (të dërguarit), jetimëve, nevojtarëve dhe atyre në mërgim, (ky është përcaktimi i Zotit), nëse keni besuar All-llahun, dhe atë, (Kur’anin) që ia zbritëm robit Tonë (Muhammedit) ditën e furkanit (ditën e Bedrit, kur u dallaua e vërteta nga e shtrembëra), ditën e konfrontimit të dy grupeve. All-llahu është i fuqishëm për çdo gjë.');
INSERT INTO "ahmeti" VALUES(8,42,'8:42','Kur ju ishit në bregun e afërt të luginës, e ata në bregun e largët të saj (ju ishit kah Medina e ata kah Meka), ndërsa karavani ishte më poshtë prej jush (kah deti). Dhe, sikur të ishit ju ata që njëri tjetrit i keni caktuar takimin, do ta thyenit caktimin. Por All-llahu ishte Ai që zbatojë çeshtjen e kryer tanimë, e të shkatërrojë me argument atë që u shkatërrua dhe të bëjë të jetojë me argumentet atë që jetoi. All-llahu vërtet dëgjon e di.');
INSERT INTO "ahmeti" VALUES(8,43,'8:43','Dhe (përkujto) kur All-llahu t’i dëftoi ty ata në ëndërr, të paktë në numër, e sikur t’i dëftonte shumë, ju do të dobësoheshit e do të grindehsit për çështjen (e luftës), por All-llahu ju shpëtoi. All-llahu vërtet e di shumë mirë se ç’mbajnë kraharorët (zemrat).');
INSERT INTO "ahmeti" VALUES(8,44,'8:44','Përkujtoni kur u takuat (në sheshin e luftës), e Ai bëri që ata në sytë tuaj të duken pak, e po ashtu edhe ju të dukeni në sytë e tyre pak; e bëri këtë për të zbatuar All-llahu një çështje që ishte e vendosur. Vetëm te All-llahu është përfundimi i çështjeve.');
INSERT INTO "ahmeti" VALUES(8,45,'8:45','O ju që besuat, kur të konfrontoheni me ndonjë grup, përqëndrohuni dhe përmendni çdo herë All-llahun që të arrini fitoren e dëshiruar.');
INSERT INTO "ahmeti" VALUES(8,46,'8:46','Dhe respektone All-llahun e të dërguarin e Tij, e mos u përçani mes vete e të dobësoheni e ta humbni fuqinë (luftarake). Të jeni të durueshëm se All-llahu është me të durueshmit.');
INSERT INTO "ahmeti" VALUES(8,47,'8:47','Mos u bëni si ata që dolën prej shtëpive të tyre sa për krenari e për t’i parë bota, e që pengonin nga rruga e All-llahut. All-llahut nuk mund t’i shpëtojnë me atë veprim të tyre.');
INSERT INTO "ahmeti" VALUES(8,48,'8:48','Përkujto (Muhammed) kur shejtani u dha guxim për veprat e tyre dhe tha: “S’ka kush që mund t’iu mposhtë sot ju, unë jam mbrojtës Juaji!” E kur u ballafaquan të dy grupet, ai u tërhoq prapa e tha: “Unë tërhiqem prej jush, unë shoh çka nuk shihni ju, unë i frikësohem All-llahut. All-llahu ndëshkon shumë ashpër.');
INSERT INTO "ahmeti" VALUES(8,49,'8:49','Kur hipokritët dhe ata që në zemrat e tyre kishte sëmundje (dyshim) thanë: “Këta (muslimanët) i ka mashtruar feja e tyre (s’kanë fuqi të luftojnë). Po kush mbështetet në All-llahun, s’ka dyshim, se All-llahu është mgadhnjyes i urtë.');
INSERT INTO "ahmeti" VALUES(8,50,'8:50','Sikur t’i kishe parë engjëjt kur ua marrë shpirtin atyre që mohuan (do të shihje tmerr), u binin fytyrave dhe shpinave të tyre (para, prapa): “Shijoni dënimin e djegjes!”');
INSERT INTO "ahmeti" VALUES(8,51,'8:51','Këtë (dënim e morët) për shkakun e asaj që fituat. All-llahu nuk është i padrejtë për robtë e Tij.');
INSERT INTO "ahmeti" VALUES(8,52,'8:52','Edhe idhujtarët, siç e kishte traditë populli i faraonit, e dhe ata që ishin para tyre, i mohuan argumentet e All-llahut, e për shkak të mëkateve All-llahu i skatërroi. All-llahu është i plotfuqishëm, ndëshkues i ashpër.');
INSERT INTO "ahmeti" VALUES(8,53,'8:53','Këtë (masë ndëshkuese) e bëri ngase All-llahu nuk ishte ndryshues i një begatie, të cilën ia ka dhuruer një populli, derisa të ndryshojë ai vetë në vetvete (të bëhet përbuzës i së mirës) dhe ngase All-llahu dëgjon (çka thonë) dhe di (çka punojnë).');
INSERT INTO "ahmeti" VALUES(8,54,'8:54','Ashtu siç ishte traditë e popullit të faraonit dhe atyre që kishin qenë më parë, që i konsideronin të rreme faktet e Zotit të tyre, e Ne për këto të këqija të tyre i shkatërruam, e popullin e faraonit (edhe faraonin) e fundosëm, por të gjithë këta ishin dëmtues të vetes së tyre.');
INSERT INTO "ahmeti" VALUES(8,55,'8:55','Krijesat më të dëmshme te All-llahu janë ata që mohuan, nuk pritet që ata të besojnë;');
INSERT INTO "ahmeti" VALUES(8,56,'8:56','Këta janë ata prej të cilëve ti pate marrë premtimin (se nuk do të ndihmojnë idhujtarët) e të cilin ata nuk e ruajtën por si çdo herë e thyejnë premtimin e tyre.');
INSERT INTO "ahmeti" VALUES(8,57,'8:57','Po nëse i ndesh (i zë) ata në luftë, atëherë ti ata (me shkatërrimin e tyre) shpartallo ata që janë pas tyre, në mënyrë që të marrin mësim.');
INSERT INTO "ahmeti" VALUES(8,58,'8:58','Nëse ti e tradhtinë e një populli (ndaj marrëveshjes), atëherë edhe ti ua dh atyre (marrëveshjen) në mënyrë të njejtë, sepse All-llahu nuk i do ata që tradhtojnë (fshehurazi).');
INSERT INTO "ahmeti" VALUES(8,59,'8:59','Dhe, ata që mohuan, mos të mendojnë kurrsesi se shpëtuan, (se na ikën). Ata nuk mund ta bëjnë të paaftë Atë (zotin) që i ndjek.');
INSERT INTO "ahmeti" VALUES(8,60,'8:60','E ju përgatituni sa të keni mundësi force, (mjete luftarake e kuaj të caktuar për betejë kundër atyre (që tradhtojnë) e me të, (me përgatitje) ta frikësoni armikun e All-llahut, armikun tuaj dhe të tjerët, të cilët ju nuk i dini (se kush janë), e All-llahu i di ata. çkado që shpenzoni për rrugë të All-llahut, ajo do t’ju konpenzohet dhe nuk do t’ju bëhet padrejtë.');
INSERT INTO "ahmeti" VALUES(8,61,'8:61','Në qoftë se ata, anojnë kah paqja ano edhe ti kah ajo, e mbështetu në All-llahun. Ai është që dëgjon dhe di.');
INSERT INTO "ahmeti" VALUES(8,62,'8:62','Po nëse duan të mashtrojnë me të (me paqen), ty të mjafton All-llahu. Ai është që të fuqizojë ty me ndihmën e vet dhe me besimtarët.');
INSERT INTO "ahmeti" VALUES(8,63,'8:63','Dhe Ai është që bashkoi zemrat e tyre. Edhe sikur ta shpenzoshe gjithë ata që është në tokë, nuk do të mund t’i bashkoje zemrat e tyre, por All-llahu bëri bashkimin e tyre, pse Ai është i gjithëfuqishëm, i urtë.');
INSERT INTO "ahmeti" VALUES(8,64,'8:64','O Pejgamber! All-llahu të mjafton ty dhe besimtarëve që janë me ty.');
INSERT INTO "ahmeti" VALUES(8,65,'8:65','O Pejgamber! Nxiti besimtarët për luftë. Nëse prej jush jaë të durueshëm, (trima të fqishëm), do t’i mundni dyqind, e nëse janë njëqind, do t’i mundni njëmijë sish që nuk besuan, për shkak se ata janë njerëz që nuk kptojnë (pse luftojnë).');
INSERT INTO "ahmeti" VALUES(8,66,'8:66','All-llahu ju bëri lehtësim tash duke ditur se jeni dobësuar. Nëse prej jush janë janë njëqind të durueshëm, do ti mundni dyqinnd, e nëse prej jush janë njëmijë, me ndimën e Zotit do t’i mundni dy mijë. All-llahu është me ata që janë të durueshëm.');
INSERT INTO "ahmeti" VALUES(8,67,'8:67','Për asnjë pejgamber nuk ke qe me vend të ketë robër derisa ta ketë dërmuar me luftë (armikun) në tokë. Ju keni për qëllimpërjetimet e kësaj bote, ndërsa All-llahu dëshiron për ju Ahiretin. All-llahu mbizotëron çdo gjë, di çka është e mirë për robtë.');
INSERT INTO "ahmeti" VALUES(8,68,'8:68','Po të mos ishte dispozita e hershme e caktuar prej All-llahut (që të mos dënohet ai që përpiqet, po nuk qëllon), juve do t’ju kishte goditur një dënim i madh për atë që e morët.');
INSERT INTO "ahmeti" VALUES(8,69,'8:69','Pra, (është e lejuar preja e luftës) hani atë që e fituat me luftë, si të lejuar dhe të mirë, përmbajuni dispozitave të All-llahut se All-llahu është Ai që falë dhe që mëshiron.');
INSERT INTO "ahmeti" VALUES(8,70,'8:70','O Pejgamber! Thuaju atyre robërve që i keni në duart tuaja se posa të vërejë All-llahu ndonjë të mirë (besim të drejtë e të sinqertë) në zemrat tuaja, Ai u jep edhe më shumë të mira nga ajo çka është marrë nga ju, ua falë gabimet, se All-llahu falë pa masë, se është mëshirues i madh.');
INSERT INTO "ahmeti" VALUES(8,71,'8:71','E nëse (robërit) duan të tradhëtojnë ty, ata më parë tradhtuan All-llahun, (nuk besuan) e Ai (të ndihmojë), të mundësojë të ngadhnjesh ndaj tyre. Alahu i di të gjitha ndodhitë, vepron me urtësinë e Tij.');
INSERT INTO "ahmeti" VALUES(8,72,'8:72','Është e vërtetë se ata që besuan, u shpërngulën dhe luftuan me pasurinë e tyre në rrugën e All-llahut, dhe që strehuan (të shpërngulurit) dhe u ndihmuan, të tillët janë miq të njëri tjetrit (në ndihmë dhe në trashëgim). Ata që besuan por që nuk u shpërngulën, ju nuk keni përkujdes as ndihmë për ta deri sa të shpërngulen edhe ata. E nëse ata kërkojnë ndihmë prej jush për çështjen e fesë, atëherëjeni të obliguar t’u ndihmoni, përveç nëse është puna kundër një populli që me të keni marrëveshje (nuk mund t’u ndihmoni në luftë kundër atij populli). All-llahu mbikëqyr atë që veproni.');
INSERT INTO "ahmeti" VALUES(8,73,'8:73','Ata që e mohuan të vërtetën janë miq të njëri-tjetrit. E nëse nuk e bëni atë (të ndihmoni e të kujdeseni njëri-tjetrin), bëhet trazirë dhe rrëmujë e madhe në tokë.');
INSERT INTO "ahmeti" VALUES(8,74,'8:74','Po at që besuan, migruan dhe luftuan për rrugën e All-llahut, dhe ata që strehuan dhe ndimuan, janë besimtarë të vërtetë. Atyre u takon falje (e mëkateve) dhe furnizimi në mënyrë të ndershme.');
INSERT INTO "ahmeti" VALUES(8,75,'8:75','Ndërsa edhe ata që besuan më vonë, e që u shpërngulën dhe luftuan së bashku me ju, janë të njejtë me ju (në të drejta). E, (sipas dispozitave), farefisi ka më përparësi ndaj njëri-tjetrit (se sa ensarët e muhaxhirinët). All-llahu di në hollësi për çdo send.');
INSERT INTO "ahmeti" VALUES(9,1,'9:1','Denoncim nga All-llahu dhe i dërguari i Tij ndaj idhujtarëve me të cilët ju patët lidhur kontratë (kumtesë për shkeputjen e marrëveshjes).');
INSERT INTO "ahmeti" VALUES(9,2,'9:2','Pra, ju (idhujtarë) qarkulloni nëpër tokë (lirisht) katër muaj, e dine se fuqinë e All-llahut nuk mund ta bëni të paaftë dhe se All-llahu mposhtë mohuesit.');
INSERT INTO "ahmeti" VALUES(9,3,'9:3','Dhe (ky është) një kumtim nga All-llahu dhe i dërguari i Tij, drejtuar gjithë njerëzve në ditën e haxhit të madh, se All-llahu është tërhequr prej (marrëveshjes së) idhujtarëve, e njëkohësisht edhe i dërguari i Tij. Po, nëe pendoheni, do të jetë më mirë për ju, e nëse ia ktheni shpinën (rrugës së drejtë), ta dini se nuk mund t’i shpëtoni (ndjekjes së) All-llahut. E, ti përgëzoji ata që mohuan me një dënim të padurueshëm.');
INSERT INTO "ahmeti" VALUES(9,4,'9:4','Pos atyre idhujarëve me të cilët keni lidhur marrëveshje, e të cilët nuk ju kanë shmengur asgjë dhe nuk e kanë ndihmuar askënd kundër jush, pra, edhe ju përmbushni marrëveshjen e tyre deri në afatin e caktuar. S’ka dyshim se All-llahu i do të devotshmit.');
INSERT INTO "ahmeti" VALUES(9,5,'9:5','E kur të kalojnë muajtë e shenjtë, luftoni idhujtarët kudo që t’i gjeni, robëroni dhe ngujoni ata, e vinju pritë në çdo shteg. Në qoftë se pendohen, e falin namazin dhe e japin zeqatin, atëherë ua lëshoni rrugën, se vërtet All-llahu falë është mëshrues.');
INSERT INTO "ahmeti" VALUES(9,6,'9:6','E nëse ndokush prejidhujtarëve të kërkon strehim, ti strehoje në mënyrë që t’i dëgjojë fjalët e All-llahut (Kur’anin (, e mandej në vendin e tij të sigurt. Këtë ngase ata janë popull që nuk e dinë (të vërtetën e fesë islame).');
INSERT INTO "ahmeti" VALUES(9,7,'9:7','Si mund të kenë udhujtarët besë (marrëveshje) te All-llahu dhe te i dërguari i Tij, përveç atyre me të cilët keni lidhur marrëveshje pranë xhamisë së shenjtë (Qabes), e derisa ata i përmbahe (marrëveshjes) përmbahuni edhe ju. All-llahu i do ata që ruajnë besën.');
INSERT INTO "ahmeti" VALUES(9,8,'9:8','Si (mund të kenë besë) ata të cilët nëse ju mundin, nuk përfillin ndaj jush as farefisni dhe as marrëveshje. Ata ju bëjnë lajka me fjalët e tyre kundërshtojnë ngase shumica e tyre janë besëthyes.');
INSERT INTO "ahmeti" VALUES(9,9,'9:9','Ata i shkëmbyen ajetet e All-llahut për një vlerë të paktë dhe penguan nga rruga e Tij. Ata keq vepruan.');
INSERT INTO "ahmeti" VALUES(9,10,'9:10','Ata nuk respektojnë te asnjë besimtarë as farefisninë as marrëveshjen; si të tillë janë përdhunues.');
INSERT INTO "ahmeti" VALUES(9,11,'9:11','Po, nëse ata pendohen, e falin namazin dhe e japin zeqatin, atëherë i keni vëllezë në fe. Ne sqarojmë agumentet për ata njerëz që kuptojnë.');
INSERT INTO "ahmeti" VALUES(9,12,'9:12','E nëse ata thyejnë zotimet e tyre pas marrëveshjes, ofendojnë fenë tuaj, atëherë luftoni krerët e mohuesve. Ata vërtet nuk kanë besë, luftoni që të frenohen (nga krimi e fyerja).');
INSERT INTO "ahmeti" VALUES(9,13,'9:13','Përse të mos e luftoni një popull që thyen zotimete veta dhe tentuan të dëbojnë të dërguarin? Në të vërtetë ata ua filluan të parët luftën. A u frikësoheni atyre? Më e drejtë është t’i frikësoheni All-llahut, nëse jeni besimtarë.');
INSERT INTO "ahmeti" VALUES(9,14,'9:14','Luftoni ata, All-llahu i dënon dhe i mposht ata nëpërmjet jush, e juve ju ndihmon kundër tyre dhe shëron zemrat e njerëzve besimtarë.');
INSERT INTO "ahmeti" VALUES(9,15,'9:15','dhe Ai mënjanon brengat nga zemrat e tyre. All-llahu ia pranon pendimin atij që do. All-llahu i di të fshehtat, me urtësi zgjidh çështjet.');
INSERT INTO "ahmeti" VALUES(9,16,'9:16','A mos menduat se do të mbeteni anash (pa u pravuar), e pa u sqaruar te All-llahu ata që luftuanprej jush dhe, përpos All-llahut, pos të dërguarit të Tij dhe pos besimtarëve, nukmorën ndonjë të jashtëm mik intim. All-llahu e di hollësisht atë që bëni ju.');
INSERT INTO "ahmeti" VALUES(9,17,'9:17','Nuk është e drejtë e idhujtarëve të kujdesen për xhaminë e All-llahut, duke qenë se vetë dëshmojnë për veten e tyre se janë mohues. Të tillëve u shkuan kot veprat e tyre dhe ata janë përgjithmonë në zjarr.');
INSERT INTO "ahmeti" VALUES(9,18,'9:18','E drjtë e përkujdesjes së xhamive të All-llahut është vetëm e atij që i ka besuar All-llahut dhe ditës së mbramë e që e falë namazin, jep zeqatin e nuk i frikësohet askujt pos All-llahut. Të tillët do të jenë të udhëzuarit (në rrugën e drejtë).');
INSERT INTO "ahmeti" VALUES(9,19,'9:19','A mos e konsideruat dhënien e ujit haxhinjve dhe kujdestarinë ndaj xhamisë së shenjtë, si besimin e atij që i besoi All-llahut dhe ditës së fundit dhe që luftoi në rrugën e All-llahut? Jo, ato nuk janë të barabarta te All-llahu. Popullin mizor All-llahu nuk e vë në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(9,20,'9:20','Ata të cilët besuan, migruan dhe luftuan me pasurinë dhe veten e tyre në rrugën e All-llahut, ata kanë pozitë më të larët te All-llahu dhe vetëm ata janë fatlumë.');
INSERT INTO "ahmeti" VALUES(9,21,'9:21','Zoti i tyre i përgëzon ata me mëshirë nga Ai, me disponim ndaj tyre, me Xhennete ku ata do të kenë nimet (hirësi-mirësi) të pandërprerë.');
INSERT INTO "ahmeti" VALUES(9,22,'9:22','Ata aty do të jenë të pasosur e te All-llahu është shpërblim i madh.');
INSERT INTO "ahmeti" VALUES(9,23,'9:23','O ju që besuat, mos u froni miqësi (dashuri) prindërve tuaj, as vëllezërve tuaj, nëse ata vlerësojnë mosbesim kundër besimit. E kush prej jush miqësohet me ta, ata janë mizor.');
INSERT INTO "ahmeti" VALUES(9,24,'9:24','Thuaj (o i dërguar): “Në qoftë se etërit tuaj, vëllezërit tuaj, bashkëshortet tuaja, farefisi juaj, pasuria që e fituat, tregëtia që frikoheni se do të dështojë, vendbanimet me të cilat jeni të kënaqur, (të gjitha këto) janë më të dashura për ju se All-llahu, se i dërguari i Tij dhe se lufta për në rrugën e Tij, atëherë, pritni derisa All-llahu të sjellë vendimin e Tij. All-llahu nuk vë në rrugën e drejtë njerëzit e prishur.');
INSERT INTO "ahmeti" VALUES(9,25,'9:25','Nuk ka dyshim se All-llahu ju ka ndihmuar në shumë beteja, e edhe në ditën e Hunejnit, kur juve ju mahnit numri i madh, i cili nuk ju vlejti asgjë, dhe me gjerësinë e saj që e kishte toka u ngushtua për ju, pastaj ju u zbrapsët (ikët).');
INSERT INTO "ahmeti" VALUES(9,26,'9:26','Pastaj All-llahun zbriti qetësinë e vet në të dërguarin e Tij dhe në besimtarë, zbriti një ushtri që ju nuk e patë, i dënoi ata që mohuan, e ai ishte ndëshkim ndaj mosbesimtarëve.');
INSERT INTO "ahmeti" VALUES(9,27,'9:27','All-llahu pastaj i falë atij që do. All-llahu falë shumë dhe mëshiron shumë.');
INSERT INTO "ahmeti" VALUES(9,28,'9:28','O ju që besuat, vërtetë idhujtarët janë të ndyrë, andaj pas këtij viti të mos i afrohen më xhamisë së shenjtë. Nëse i frikësoheni skamjes All-llahu me dëshirën e vet do t’ju pastroj me mirësitë e Tij. All-llahu është i gjithdijshëm, është i urtë.');
INSERT INTO "ahmeti" VALUES(9,29,'9:29','Luftoni ata që nuk besojnë All-llahun e as botën tjetër, nuk e konsiderojnë të ndaluar (haram) atë që e ndaloi All-llahu dhe i dërguari i Tij, nuk besojnë fenë e vërtetë, prej atyre të cilëve u është dhënë libri, derisa ta japin xhizjen në dorë e duke qenë të mposhtur.');
INSERT INTO "ahmeti" VALUES(9,30,'9:30','E jehuditë thanë: Uzejri është djali i All-llahut, e të kristerët thanë: Mesihu është djalë i All-llahut. Ato ishim thënie të tyre me gojët e tyre (fraza të thata), që imitojnë thëniet e jobesimtarëve të mëhershëm. All-llahu i vraftë, si largohen (nga e vërteta)!');
INSERT INTO "ahmeti" VALUES(9,31,'9:31','Ata i konsideruan “ahbarët” (priftër jehudi) të tyre, “ruhbanët” (murgjit e krishterë) të tyre dhe Mesihun (Isain) birin e Merjemes, për zota pos All-llahut, ndërsa ata nuk janë urdhëruar për tjetër (nga pejgamberët) pos për adhurimin ndaj All-llahut një, e që nuk ka të adhuruar tjetër pos tij. I lartë është Ai nga çka i shoqërojnë.');
INSERT INTO "ahmeti" VALUES(9,32,'9:32','Ata përpiqen me gojët e tyre ta shuajnë dritën e All-llahut, e All-llahu nuk do tjetër, pos ta përsosë dritën e Tij ndonëse jobesimtarët e urrejnë.');
INSERT INTO "ahmeti" VALUES(9,33,'9:33','Ai (All-llahu) është që e dërgoi të dërgarin e Tij me udhëzim të drejtë e fe të vërtetë, e për ta bërë që të dominojë mbi të gjitha fetë, edhe pse e urrejtën idhujtarët.');
INSERT INTO "ahmeti" VALUES(9,34,'9:34','O ju që besuat, vërtet një shumicë e parisë fetare e jehudive dhe e të krishterëve, në mënyrë të paligjshme e hanë pasurinë e njerëzve dhe pengijnë të tjerët nga rruga e All-llahut. Ata të cilët e ruajnë arin e argjendin e nuk e japin për rrugën e All-llahut, lajmëroji për një dënim të dhëmbshëm.');
INSERT INTO "ahmeti" VALUES(9,35,'9:35','Atë ditë kur ajo (pasuri e deponuar) fërgohet në prushin e Xhehennemit, e me të (ashtu zharavë) lyhen ballët, anët dhe shpinat e tyre (do t’u thuhet): “Kjo është ajo që e dispozituat për veten tuaj, pra shijoni atë që e depozitonit!”');
INSERT INTO "ahmeti" VALUES(9,36,'9:36','Te All-llahu numri i muajve është dymbëdhjetë (sipas hënës), ashtu si është në librin e All-llahut prej ditës kur krijoi qiejt dhe tokën. Prej tyre katër janë të shenjtë. Kjo është fe e drejtë. Pra, mos e ngarkoni (me mëkat) veten tuaj ata (katër muaj). Luftoni të gjithë idhujtarët pa dallim, siç ju luftojnë ata juve pa dallm, e dinë se All-llahu është me ata që ruhen (të këqiave).');
INSERT INTO "ahmeti" VALUES(9,37,'9:37','E shtyrja (e një muaj në vend të një tjetri) nuk është tjetër vetëm se një rritje e mosbesimit, që me të edhe më shumë humbin ata që mohuan, pse në një vit e bëjnë të lejuar atë (muajin e shenjtë), e në një vit të ndaluar (të shenjtë), e për të përputhur numrin që All-llahu i bëri të shenjtë dhe me atë, e bëjnë të lejuar atë që All-llahu e ndaloi. Atyre u janë hijeshuar veprat e tyre të këqia. Po, All-llahu nuk vë në rrugë të drejtë popullin mohues.');
INSERT INTO "ahmeti" VALUES(9,38,'9:38','O ju që besuat, ç’është me ju, që kur ju thuhet: “Dilni në rrugën e All-llahut!”, ju rëndoheni përtokë? A jeni më të kënaqur me jetën e dunjasë, se sa me ahiretin? Kënaqësia e jetës së dunjasë, në krahasim me ahiretin është e vogël!');
INSERT INTO "ahmeti" VALUES(9,39,'9:39','Nëse nuk dilni, Ai do t’ju dënojë me një dënim të dhembshëm, do t’ju zëvendësojë me një popull tjetër dhe Atë nuk e dëmtoni aspak. All-llahu ka mundësi për çdo gjë.');
INSERT INTO "ahmeti" VALUES(9,40,'9:40','Nëse nuk e ndihmoni atë (Muhammedin s. a. w. s.), atë pra e ka ndihmuar All-llahu, kur e përzunë ata që nuk besuan të dytin prej dyve. Kur ishin në shpellë, ai (Muhammedi s. a. w. s.) i tha shokut të vet (Ebu Bekrit r. a.): “Mos u brengos, vërtet All-llahu është me ne!” E All-llahu zbriti qetësinë (sekinen) e Vet mbi të, dhe e fuqizoi me fuqi që ju nuk e patë dhe e bëri fjalën e atyre që nuk besuan më të ultën, kurse fjala e All-llahut është më e larta. All-llahu është i Fuqishmi, i Urti.');
INSERT INTO "ahmeti" VALUES(9,41,'9:41','Dilni me lehtësi ose vështirësi, dhe luftoni me pasurinë tuaj dhe me veten tuaj në rrugën e All-llahut; kjo është për ju më e dobishmja, nëse e dini.');
INSERT INTO "ahmeti" VALUES(9,42,'9:42','Sikur të ishte fitim i afërt dhe udhëtim mesatar, ata (hipokritët) do të vinin pas teje, por për ta ishte largësi e vështirë. Ata do të betohen në All-llahun: “Sikur të kishim mundësi, do të dilnim me ju”. E shkatërrojnë veten e tyre; All-llahu e di se ata janë rrenacakë.');
INSERT INTO "ahmeti" VALUES(9,43,'9:43','All-llahu ta fali ty (Muhammed) gabimin pse atyre ju dhe leje (të ngelin pa dalë) para se të bëhej për ty e qartë se cilët ishin të drejtë dhe t’i dije me kohë rrenacakët.');
INSERT INTO "ahmeti" VALUES(9,44,'9:44','Ata që besojnë All-llahun dhe botën tjetër, nuk të kërkojnë leje për të mos luftuar me pasurinë dhe veten e tyre. All-llahu i di të sinqertit.');
INSERT INTO "ahmeti" VALUES(9,45,'9:45','Vetëm ata që nuk besojnë All-llahun dhe botën tjetër dhe që zemrat e tyre janë të dyshimta, të kërkojnë leje (për të mos vajtur në luftë), pra ata, sillen vërdallë në dyshimin e tyre.');
INSERT INTO "ahmeti" VALUES(9,46,'9:46','E sikur të kishin dëshiruar ata të dalin, do të bënin për të (për luftë) ndonjë përgatitje, por All-llahu nuk e pëlqeu ngritjen e tyre, ndaj i zmbrapsi, dhe u është thënë: “Rrini me të paaftit (fëmijët, gratë e pleqtë).');
INSERT INTO "ahmeti" VALUES(9,47,'9:47','Edhe sikur të dilnin me ju, ata nuk do t’ju shtonin tjetër pos ngatërresës dhe shumë shpejt do të përçanin mesin tuaj, duke kërkuar t’ju turbullojnë. E ndër ju ka të tillë që i dëgjojnë ata. All-llahu i di shumë mirë hipokritët.');
INSERT INTO "ahmeti" VALUES(9,48,'9:48','Ata edhe më parë tentuan përçarjen tuaj, t’i ngatërruan ty çështjet derisa të erdhi e vërteta dhe ngadhënjeu vendimi i All-llahut, përkundër asaj që ata e urrenin.');
INSERT INTO "ahmeti" VALUES(9,49,'9:49','Dhe prej tyre ka të tillë që thonë: “Më lejo mua (të mos shko në luftë) e mos më vë në sprovë!” Ja, ata mu në sprovë kanë rënë (më parë). Xhehennemi gjithsesi i përfshin nga të gjitha anët jobesimtarët.');
INSERT INTO "ahmeti" VALUES(9,50,'9:50','Nëse ty të takon ndonjë e mirë (në luftë), ajo u vjen keq atyre, e nëse të godet ndonjë e pakëndshme, ata thonë: “Ne edhe më parë kemi ndërmarrë për çështjen tonë dhe kthehen të gëzuar.');
INSERT INTO "ahmeti" VALUES(9,51,'9:51','Thuaj: “Neve nuk na godet asgjë tjetër, përveç çka na ka caktuar All-llahu; Ai është ndihmëtar yni”. Prandaj, vetëm All-llahut le t’i mbështeten besimtarët.');
INSERT INTO "ahmeti" VALUES(9,52,'9:52','Thuaj: “ç’pritni për ne tjetër, pos njerës nga dy të mirat (o fitues, o dëshmorë)? Ndërsa ne presim për ju që nga ana e All-llahut, ose nëpërmjet duarve tona, t’ju godas me dënim. Pra, ju pritni, e bashkë me ju presim edhe ne.');
INSERT INTO "ahmeti" VALUES(9,53,'9:53','Thuaj: “Shpenzuat ju me dëshirë a me dhunë, nuk u pranohet kurrsesi, vërtet ju ishit popull i padëgjueshëm.');
INSERT INTO "ahmeti" VALUES(9,54,'9:54','Mospranimin e dhënieve të tyre nuk e pengoi tjetër gjë vetëm pse ata mohuan All-llahun dhe të dërguarin e Tij, dhe namazin e falin vetëm me përtaci, e lëmoshën nuk e japin ndryshe pos duke urrejtur.');
INSERT INTO "ahmeti" VALUES(9,55,'9:55','Prandaj, mos të mahnitë (mos e shih të mirë) pasuria e tyre e as fëmijët e tyre, All-llahu do vetëm t’i dënojë me to në jetën e kësaj bote e t’ju nxjerrë shpirtërat duke qenë ashtu qafira.');
INSERT INTO "ahmeti" VALUES(9,56,'9:56','Ata betohen në All-llahun se vërtet janë si ju, po ata nuk janë me ju, ata janë popull që frikohet.');
INSERT INTO "ahmeti" VALUES(9,57,'9:57','Sikur të gjenin ndonjë vendstrehim, ndonjë shpellë a ndonjë vrimë, ata do të shkonin aty me vrapim.');
INSERT INTO "ahmeti" VALUES(9,58,'9:58','Ka prej tyre që do të bëjnë vërejtjen në ndarjen e lëmoshës, nëse u jepet nga ajo, ata mbesin të kënaqur, e nëse nuk u jepet, ata hidhërohen.');
INSERT INTO "ahmeti" VALUES(9,59,'9:59','E sikur të kënaqeshin me atë që ua dha All-llahu dhe idërguari i Tij, e të thonin: “Ne na mjafton All-llahu, All-llahu do të na furnizojë nga të mirat e Tij, e edhe i dërguari i Tij, dhe se vetëm te All-llahu e mbështesim dëshirën (do të ishte shumë më mirë për ta)”.');
INSERT INTO "ahmeti" VALUES(9,60,'9:60','All-llahu caktoi obligim që lëmosha (zeqati etj.) t’ju takojnë vetëm: të varfëre (nevojtarëve), të ngratëve (që s’kanë fare (, punonjësve 9që e tubojnë), atyre që duhet përfituar zemrat (të dobëtve në besim), e duhet dhënë edhe për lirim nga robëria, të mbyturve në borgje, (luftëtarëve) në rrugën e All-llahut dhe atij që ka ka mbetur në rrugë. All-llahu e di gjendjen e robërve, punon sipas urtësisë së vet.');
INSERT INTO "ahmeti" VALUES(9,61,'9:61','Ka prej tyre që e ofendojnë Pejgamberin e thonë: “Ai është bërë vesh (beson çka dëgjon)”. Thuaj: “Ai është vesh itë mirës suaj (dëgjon të mirën dhe vepron e jo të keqen), ai beson All-llahun (çka i thotë), u zë besë beismtarëve dhe është mëshirues për ata që besuan nga mesi juaj, e ata që nuk e lënë të qetë të dërguarin e All-llahut, ata kanë dënim të dhëmbshëm.');
INSERT INTO "ahmeti" VALUES(9,62,'9:62','Ata ju betohen (muslimanëve) në All-llahun për t’ju bërë juve të kënaqur (se s’kanë qëllime të këqia), e me e drejtë është që ata të bëjnë të kënaqur All-llahun dhe të dërguarin r Tij (me adhurim dhe vepra të sinqerta) nëse vërtet janë besimtarë.');
INSERT INTO "ahmeti" VALUES(9,63,'9:63','A nuk e ditën ata se kush kundërshton All-llahun dhe të dërguarin e Tij, atij i është caktuar zjarri i Xhehennemit, ku do të jenë përjetë, e ky është ai poshtërimi madh.');
INSERT INTO "ahmeti" VALUES(9,64,'9:64','Hipokritët kanë dro se do të zbritet ndonjë kaptinë që do të nxjerr në shesh atë që kanë në zemrat e tyre. Thuaj: “Ani, talluni!” All-llahu do ta zbulojë atë që i frikësoheni.');
INSERT INTO "ahmeti" VALUES(9,65,'9:65','E nëse i pye ti ata (përse tallen), do të thonë: “Ne vetëm jemi mahnitur e dëfruar”. Thuaj: “A me All-llahun, librin dhe të dërguarin e Tij talleni?”');
INSERT INTO "ahmeti" VALUES(9,66,'9:66','Mos kërkoni fare ndjesë, ju pasiqë (shpallët se) besuat, keni mohuar. Nëse një grupi nga ju i falim, grupin tjetër e dënojmë, ngase ishin kriminelë.');
INSERT INTO "ahmeti" VALUES(9,67,'9:67','Hipokritët dhe hipokritet janë si njëri tjetri; urdhërojnë për të keqen e ndalojnë nga e mira dhe shrëngojnë duart e tyre. Harruan All-llahun (nuk i binden), prandaj Ai i harroi (nga mëshira). Nuk ka dyshim, hipokritët janë ata të prishurit.');
INSERT INTO "ahmeti" VALUES(9,68,'9:68','Hipokritëve dhe hipokriteve dhe jobesimtarëve All-llahu u ka përcaktuar zjarrin e Xhehennemit, ku do të jenë përgjithmonë, ai mjafton për ta, dhe ata i ka mallkuar All-llahu. Ata kanë dënim të pandërprerë.');
INSERT INTO "ahmeti" VALUES(9,69,'9:69','Ju hipokritë jeni si ata para jush, që ishin më të fuqishëm se ju, kishin më shumë pasuri e fëmijë, që përjetuan ata që u takoi, sikurse që përjetuat edhe ju pjesën tuaj, madje edhe ju u zhytët (në të këqija) siç u zhytën ata. Të tillët asgjësuan veprat e veta në këtë jetë dhe në jetën tjetër, dhe të tillët janë plotësisht të dëshpruar.');
INSERT INTO "ahmeti" VALUES(9,70,'9:70','A nuk u arrijti atyre lajmi i atyre që ishin më heret si: i popullit të Nuhut, i Adit, i Themudit, i popullit të Ibrahimit, i atyre të Medjemit dhe i të rrotulluarve (populli i Lutit). Atyre u patën ardhur të dërguarit e tyre me argumente. All-llahu (që i shkatërroi) nuk ua bëri atyre padrejtë, por ata ia bënë padrejtë vetes.');
INSERT INTO "ahmeti" VALUES(9,71,'9:71','Besimtarët dhe besimtaret janë të dashur për njëri-tjetrin, urdhërojnë për të mirë, e ndalojnë nga e keqa, e falin namazin dhe japin zeqatin, respektojnë All-llahun dhe të dërguarin e Tij. Të tillët do t’i mëshirojë All-llahu. All-llahu është ngadhnjyes, i urtë.');
INSERT INTO "ahmeti" VALUES(9,72,'9:72','Besimtarëve dhe besimtareve All-llahu u premtoi Xhennete nën të cilët rrjedhin lumenj, në to do të jenë përgjithmonë, dhe (u premtoi) vendbanime të bukura në Xhennetin e Adnit, edhe një disponim nga All-llahu që është mbi të gjitha. Ky, pra është ai shpëtimi i madh.');
INSERT INTO "ahmeti" VALUES(9,73,'9:73','O Pejgamber, lufto jobesimtarët dhe hipokritët dhe sillu rreptë ndaj tyre - vendi i tyre është Xhehennemi që është përfundimi më i keq.');
INSERT INTO "ahmeti" VALUES(9,74,'9:74','Ata betohen në All-llahun se kanë thënë (asgjë të keqe), e në të vërtetë, kanë thënë fjalë që nuk përkojnë me besimin, dhe pasi patën shprehur besimin ata e mohuan, dhe u përpoqën për atë (mbytjen e Pejgamberit) që nuk e arritën. Ata nuk urrejnë për tjetër pos pse All-llahu nga mirësitë e Tij dhe i dërguari i begatoi ata. Nëse ata pendohen do të jetë më mirë për ta, e nëse refuzojnë, All-llahui dënon me një dënim të dhembshëm si në këtë botë ashtu edhe në botën tjetër. E në këtë rruzull të tokës nuk ka për ta as mbrojtës, e as ndihmës.');
INSERT INTO "ahmeti" VALUES(9,75,'9:75','E prej tyre (hipokritëve) pati sish që i patën dhënë besën All-llahut që: “Nëse na jep (All-llahu) nga mirësia e Tij, ne do të ndajmë lëmoshë dhe do të bëhemi prej punëmirëve.');
INSERT INTO "ahmeti" VALUES(9,76,'9:76','Kur ju dha (All-llahu) nga të mirat e Veta, ata bënë koprraci me atë (që u dha), ethyen besën dhe u zbrapsen nga respekti ndaj All-llahut.');
INSERT INTO "ahmeti" VALUES(9,77,'9:77','Dhe për shkak se u thyen besën që i patën dhënë All-llahut, dhe për shkak të asaj se gënjenin, All-llahu u ngjeshi hipokrizinë në zemrat e tyre deri në ditën që e takojnë Atë.');
INSERT INTO "ahmeti" VALUES(9,78,'9:78','A nuk e ditën ata se All-llahu e di fshehtësinë e tyre (në zemra), bisedën e tyre, dhe se Ai (All-llahu) i di të gjiha të fshehtat.');
INSERT INTO "ahmeti" VALUES(9,79,'9:79','Ata (hipokritë) që i përqeshnin besimtarët, që me vullnetin e vet japin lëmoshë, i përqeshin edhe ata të cilët nuk kanë më shumë se çka është e domosdoshme për jetesën e tyre, e njëkohësisht tallen me ta, All-llahu u dha shpërblimin e taljeve të tyre - dënimin më të rëndë.');
INSERT INTO "ahmeti" VALUES(9,80,'9:80','Kërko falje për ta ose mos kërko (është njejtë), e edhe nëse kërkon falje shtatëdhjetë herë, All-llahu nuk do t’ua falë, sepse ata mohuan All-llahun dhe të dërguarin e Tij, e All-llahu nuk vë në rrugë të drejtë (të shpëtimit) ata që nuk respektojnë.');
INSERT INTO "ahmeti" VALUES(9,81,'9:81','Ata (hipokritë) që mbetën (pa shkuar në luftë), u gëzuan për ndejen e tyre pas të dërguarit të All-llahut dhe nuk dëshiruan që të luftonin me pasurinë dhe veten e tyre në rrugën e All-llahut dhe thanë: “Mos dilni (në luftë) në vapë!” Thuaju: “Zjarri i Xhehennemit ka vapën edhe më të fortë, nëse janë që kuptojnë!”');
INSERT INTO "ahmeti" VALUES(9,82,'9:82','Le të qeshin pak (në dynja) e le të qajnë shumë (në botën tjetër). Ai është shpërblim i asaj që fituan.');
INSERT INTO "ahmeti" VALUES(9,83,'9:83','Nëse All-llahu të kthen ty (prej Tebukut) te ndonjë grup i tyre (hipokritëve), e ata të kërkojnë leje për të dalë (me ty në ndonjë luftë), thuaju: “Ju nuk do të dilni kurrë me mua dhe kurrë nuk do të luftoni ndonjë armik së bashku me mua! Ju ishit të kënaqur herën e parë që nuk luftuat, pra rrini me ata që mbetën!”');
INSERT INTO "ahmeti" VALUES(9,84,'9:84','Dhe asnjërit prej tyre kur të vdesin, mos ia fal namazin (e xhenazës) kurrë, e as mos qëndro pranë varrit të tij (për lutje e vizitë), pse ata mohuan All-llahun dhe të dërguarin e Tij dhe vdiqën kryeneçë.');
INSERT INTO "ahmeti" VALUES(9,85,'9:85','Po ty të mos duket e mirë pasuria e tyre dhe fëmijët e tyre. All-llahu deshi t’i mundojë me të në këtë botë dhe t’u dalë shpirti duke qenë mohues.');
INSERT INTO "ahmeti" VALUES(9,86,'9:86','E kur zbritët një kaptinë (që thotë): të besoni All-llahun dhe luftoni së bashku me të dërguarin e Tij, ata të pasurit kërkuan leje prej teje dhe thanë: “Na le të mbesim me ata që rrinë!”');
INSERT INTO "ahmeti" VALUES(9,87,'9:87','U pajtuan të mbesin me ata që nuk vajtën (në luftë). Po zemrat e tyre janë mbyllur, andaj ata nuk kuptojnë.');
INSERT INTO "ahmeti" VALUES(9,88,'9:88','Por, i dërguari, e së bashku me të edhe ata që besuan, luftuan me pasurinë dhe veten e tyre dhe atyre u takuan të gjitha të mirat, ata janë të shpëtuarit.');
INSERT INTO "ahmeti" VALUES(9,89,'9:89','All-llahu u ka përgatitur atyre Xhennete që në të rrjedhin lumenj, ku do të qëndrojnë përgjithmonë. Ai është suksesi i madh.');
INSERT INTO "ahmeti" VALUES(9,90,'9:90','Disa nga arabët beduinë që kërkuan falje, erdhën për t’iu dhënë leje (të mos shkonin në luftë), e ata të tjerët që kishin gënjyer (me besim) All-llahun dhe të dërguarin e Tij ishin ndalë (paleje). Ata që prej tyre nuk besuan, do t’i godasë dënim i ashpër.');
INSERT INTO "ahmeti" VALUES(9,91,'9:91','Nuk është ndonjë mëkat për të dobëtit, as për të sëmurët, e as për ata që nuk kanë me çka të përgatiten, kur janë të sinqertë ndaj All-llahut dhe të dërguarit të Tij, pra për të mirët nuk ka rrugë qortimi. All-llahu falë shumë, është mëshirues.');
INSERT INTO "ahmeti" VALUES(9,92,'9:92','(nuk ka qortim) Dhe as për atë që kur erdhën te ti për t’i bartur me kafshë, u the: “Nuk kam me çka t’ju bartë. Ata u kthyen, e prej pikëllimit se nuk kishin me çka të përgatiteshin nga sytë e tyre ridhnin lotë.');
INSERT INTO "ahmeti" VALUES(9,93,'9:93','E keqja është për ata që duke qenë pasanikë kërkuan leje dhe u pajtuan të mbeteshin me ata që nuk vajtën. Po, All-llahu ka vulosur zemrat e tyre, ata nuk do të gjejnë rrugë.');
INSERT INTO "ahmeti" VALUES(9,94,'9:94','Kur të ktheheni te ata, ata do t’ju kërkojnë falje, thuanju: “Mos u arsyetoni, ne nuk ju besojmë, për gjendjen e juaj na ka njoftuar All-llahu. Veprën tuaj do t shohë All-llahu dhe i dërguari i Tij, e pastaj ju do të ktheheni te Ai që i di të fshehtat dhe të dukshmet, e Ai do t’ju lajmërojë për atë që keni vepruar.');
INSERT INTO "ahmeti" VALUES(9,95,'9:95','Ata do t’ju betohen në All-llahun, kur të ktheheni te ata, për të mos i qortuar, ju pra hiqnu tyre ata janë të ndyrë, e vendi i tyre do të jetë Xhehennemi, shpërblim për atë që fituan.');
INSERT INTO "ahmeti" VALUES(9,96,'9:96','Ata ju betohen juve që të jeni të kënaqur ndaj tyre, e nëse ju shprehni kënaqësi, All-llahu nuk është i kënaqur me popullin e paturpshëm.');
INSERT INTO "ahmeti" VALUES(9,97,'9:97','Arabët beduinë janë të pajisur me mohim e hipokrizi më të fortë, dhe janë më të përshtatshëm për të mos i njohur dispozitat që ia zbriti të dërguarit të Tij All-llahu. All-llahu i di çështjet, Ai është i urtë.');
INSERT INTO "ahmeti" VALUES(9,98,'9:98','Ka disa beduinë që për atë që e jep, e konsideron si të dështuar dhe presin kthesa kundër jush. Kundër tyre e ardhmja qoftë e keqe! All-llahu dëgjon, di.');
INSERT INTO "ahmeti" VALUES(9,99,'9:99','Po, ka edhe disa nga beduinët që besojnë All-llahun dhe botën tjetër dhe atë që e japin e konsiderojnë afrim te All-llahu dhe bekim te i dërguari. Veni re, ato janë afrime te All-llahu për ta, e All-llahu do t’i përfshijë në mëshirën e Tij. All-llahu falë dhe është mëshirues.');
INSERT INTO "ahmeti" VALUES(9,100,'9:100','All-llahu është i kënaqur me të hershmit e parë prej muhaxhirëve (migruesve) dhe prej ensarëve (vendasve-ndihmëtarë) dhe prej atyre që i pasuan ata me punë të mira, e edhe ata janë të kënaqur ndaj Tij. Atyre u ka përgatitur Xhennete, në të cilëtrrjedhin lumenj, ku do të jenë përjetë të pasosur. E ky është fitim i madh.');
INSERT INTO "ahmeti" VALUES(9,101,'9:101','Edhe rreth jush ka beduinë që janë hipokritë, e edhe nga banorët e Medinës që janë përcaktuar për hipokrizi. ti nuk i di ata, Ne i dimë, ata do t’i dënojmë dy herë, pastaj shtyhen në dënimin e madh.');
INSERT INTO "ahmeti" VALUES(9,102,'9:102','Ka edhe të tjerë që i kanë pranuar mëkatet e veta: ata përzien vepra të mira, e edhe të tjeatë këqija. Atyre do t’ua falë All-llahu, se Ai i falë atij që pendohet, e mëshiron atë që përmirësohet.');
INSERT INTO "ahmeti" VALUES(9,103,'9:103','Merr prej pasurisë së tyre (të atyre që pranuan gabimin) lëmoshë që t’i pastrosh me të dhe t’u shtosh (të mirat) dhe njëkohësisht lutu për ta, se lutja jote është qetësim për ta. All-llahu dëgjon dhe sheh.');
INSERT INTO "ahmeti" VALUES(9,104,'9:104','A nuk e ditën ata se All-llahu është ai që ranon pendimin e robëve të vet, pranon lëmoshat dhe se, vetëm All-llahu është Ai që shumë pranon pendimin dhe është mëshirues.');
INSERT INTO "ahmeti" VALUES(9,105,'9:105','Dhe thuaj: “Veproni, All-llahu do ta shohë veprën tuaj, edhe i dërguari i Tij e besimtarët, e vonë do të ktheheni te njohësi i të fshehës dhe të dukshmes, e do t’ju njoftojë për atë që vepruat.');
INSERT INTO "ahmeti" VALUES(9,106,'9:106','Ka edhe të tjerëqë janë duke pritur urdhërin e All-llahut, a do t’i dënojë apo do t’i falë pas pendimit. All-llahu e di gjendjen e tyre dhe di ç’bën me të.');
INSERT INTO "ahmeti" VALUES(9,107,'9:107','(Nga hipokritët më të shëmtuarit janë) Edhe ata që ndërtuan xhami sherri, mosbesimi e përçarjeje mes besimtarëve dhe ftuan në pritje (solemne) atë që më parë kishte luftuar kundër All-llahut dhe të dërguarit të Tij. Ata do të betohen: “Ne nuk kemi pasur tjetër qëllim, vetëm për të mirë!” Po All-llahu dëshmoi se ata vërtet janë rrenacakë.');
INSERT INTO "ahmeti" VALUES(9,108,'9:108','Ti mos u fal aty kurrë! Një xhami e cila që prej ditës së parë është themeluar në respekt ndaj All-llahut (pa hile), është më e drejtë të falesh në të, aty ka burra që dëshir të pastrohen mirë, e All-llahu i do të pastrit.');
INSERT INTO "ahmeti" VALUES(9,109,'9:109','A është më imirë ai që ndërtesën e vet e themeloi në devotshmëri dhe në kënaqësi të All-llahut, apo ai që në ndërtesën e vet e themeloi buzë bregut të shembur e bashkë me të bie në zjarrin e Xhehennemit? All-llahu nuk i udhëzon njerëzit zullumqarë.');
INSERT INTO "ahmeti" VALUES(9,110,'9:110','Ndërtesa e tyre të cilën e ndërtuan ata (e që e shembi Pejgamberi) vazhdimisht do të jetë mllef në zemrat e tyre, pos nëse pëlcasin zemrat e tyre. All-llahu di, është i urtë.');
INSERT INTO "ahmeti" VALUES(9,111,'9:111','All-llahu bleu prej besimtarëve shpirtërat dhe pasurinë e tyre me Xhennet. Luftojnë në rrugën e All-llahut, mbysin dhe mbyten. (All-llahu dha) Premtim të cilin e vërtetoi në Tevrat, Inxhill e Kur’an. E kush është më zbatues i sigurt i premtimit të vet se All-llahu? Pra, gëzonju tregtisë që bëtë me Të. Ky është sukses i madh.');
INSERT INTO "ahmeti" VALUES(9,112,'9:112','(Të Xhennetit janë) Edhe ata që pendohen, ata që sinqerisht adhurojnë, ata qqë falenderojnë, ata që agjërojnë, ata që bëjnë ruku, që bëjnë sexhde, që urdhërojnë për të mira e ndalojnë nga të këqijat, edhe përmbushin dispozitat e All-llahut. Pra, përgëzoj besimtarët.');
INSERT INTO "ahmeti" VALUES(9,113,'9:113','Nuk është e drejtë për Pejgamberin e as për besimtarët të kërkojnë falje për idhujtarët, edhe nëse janë të afërt të tyre, pasi që ta kenë të qartë se me të vërtet ata (idhujtarët) janë banues të Xhehennemit.');
INSERT INTO "ahmeti" VALUES(9,114,'9:114','Edhe lutja e Ibrahimit për babain e vet nuk ishte tjetër, pos për shkak të një premtimi që ia pat premtuar atij, e kur iu bë e qartë se ai ishte armik i All-llahut, u largua prej tij. S’ka dyshim, Ibrahimi ishte shumë i ndieshëmë dhe i butë.');
INSERT INTO "ahmeti" VALUES(9,115,'9:115','All-llahu nuk e humb një popoull pasi që ta ketë udhëzuar atë, para se t’ju sqarojë atyre se prej çka duhet ruajtur. All-llahu është i gjithdijshëm për secilin send.');
INSERT INTO "ahmeti" VALUES(9,116,'9:116','S’ka dyshim se sundimi i qiëjve e i tokës është i All-llahut, Ai jep jetë dhe vdekje, e ju, përveç All-llahut, nuk keni përkrahës tjetër.');
INSERT INTO "ahmeti" VALUES(9,117,'9:117','All-llahu ia fali Pejgamberit, edhe muhaxhirëve edhe ensarëve, të cilët në çastin e vështirë shkan pas tij, kur (nga vështirësitë) zemrat e një grupi nga ata, gati u lëkundën, por Ai ua fali (i stabilizoi zemrat e tyre) atyre. Vërtet Ai është i butë, i mëshirshëm ndaj tyre.');
INSERT INTO "ahmeti" VALUES(9,118,'9:118','(Ai u fali) Edhe atyre të treve, të cilëve iu pat shtyrë aq (pranimi i pendimit) sa që toka u bë e ngushtë për ta, përkundër gjerësisë së saj, kur u ngushtuan edhe shpirtërat e tyre sa që u bindën se nuk ka srehim prej All-llahut (tjetër ku) pos te Ai. Prandaj edhe atyre ua fali ai, në mënyrë që të pendohen. All-llahu pranon pendimin se është mëshirues.');
INSERT INTO "ahmeti" VALUES(9,119,'9:119','O ju që besuat! Ta keni në kujdes All-llahun dhe të jeni me ata të drejtit.');
INSERT INTO "ahmeti" VALUES(9,120,'9:120','Nuk ishte me vend për banorët e Medinës e as për ata që ishin rreth tyre nga beduinët, të ngelin (pa shkuar) pas të dërguarit të All-llahut e as të kursejnë veten e tyre ndaj vetes së tij (Pejgamberit). Nuk ishte me vend ngase, ata nuk i godet as rtja, as lodhja, as uria kur janë në rrugën e All-llahut, dhe nuk shkelin ndonjë vend që i hidhëron mosbesimtarët, dhe nuk arrijnë kundër armikut çkado qoftë (mbytje, robërim të tyre ose ngadhënjim mbi ta), vetëm se të gjitha ato do t’u evidentohen atyre si vepër e mirë (shpërblyese). All-llahu nuk ua humb shpërblimin veprimirëve.');
INSERT INTO "ahmeti" VALUES(9,121,'9:121','Dhe nuk japin kontribut, të vogël ose të madh, nuk kapërcejnë ndonjë luginë, e qe të mos u shenohet (për shpërblim) atyre, për t’i shpërblyer All-llahu më së miri atë që vepruan.');
INSERT INTO "ahmeti" VALUES(9,122,'9:122','Nuk është e nevojshme të dalin në luftë të gjithë besimtarët. E përse nga çdo grumbull i tyre yë mos shkojë një grup për t’u aftësuar në diturinë fetare, për ta mësuar popullin e vet kur të kthehen te ata, në mënyrë që ata ta kuptojnë (e të ruhen).');
INSERT INTO "ahmeti" VALUES(9,123,'9:123','O ju që besuat! Luftoni jobesimtarët që i keni afër jush, e le ta ndiejnë prej jush grushtin e fortë kundër tyre. E dine se All-llahu është me të devotshmit.');
INSERT INTO "ahmeti" VALUES(9,124,'9:124','E kur zbret ndonjë kaptinë, ka prej tyre që thonë: “Cilit prej jush ia shtoi kjo besimin?” Sa u përket atyre që besuan, atyre u shtohet besimi dhe gëzohen për të.');
INSERT INTO "ahmeti" VALUES(9,125,'9:125','E sa u përket atyre që kanë sëmundje në zemrat e tyre, ajo (zbritja e kaptinës) ndytësisë së tyre u shton ndytësi dhe ata vdesin si jobesimtarë.');
INSERT INTO "ahmeti" VALUES(9,126,'9:126','A nuk e shohin ata se për çdo vit sprovohen një herë apo dy herë, e përsëri nuk pendohen e as nuk përkujtohen.');
INSERT INTO "ahmeti" VALUES(9,127,'9:127','E kur zbret ndonjë sure, ata shikojnë njëri-tjetrin (dhe thonë): “A ju pa dikush?” pastaj largohen. All-llahu largoi zemrat e tyre (nga besimi) ngase janë popull që nuk kupton.');
INSERT INTO "ahmeti" VALUES(9,128,'9:128','Ju erdhi i dërguar nga mesi juaj. Atij i vie rëndë për vuajtjet tuaja. I brengosur për besimtarët, i butë dhe i mëshirëshëm.');
INSERT INTO "ahmeti" VALUES(9,129,'9:129','Po nëse refuzojnë, thuaj: “Më mjafton All-llahu, s’ka zot pos Tij, te Ai mbështetem, e Ai është Zot i Arshit të madh!”');
INSERT INTO "ahmeti" VALUES(10,1,'10:1','Elif, Lamë, Ra. Këto janë ajetet e librit të pacenueshëm.');
INSERT INTO "ahmeti" VALUES(10,2,'10:2','A ishte çudi për njerëzit, që Ne një njeriu nga mesi ityre i thamë: Tërhiqjau vërejtjen njerëzve, kurse ata që besuan gëzoi se me të vërtetë kanë pozitë të lartë te Zoti i tyre. E ata që besuan thanë: “S’ka dyshim, kjo është një magji e vërtetë!”');
INSERT INTO "ahmeti" VALUES(10,3,'10:3','Vërtet Zoti juaj është All-llahu, i cili krijoi qiejt e tokën brenda gjashtë ditësh e pastaj qëndroi mbi Arshin; Ai rregullon gjendjen. Ndërmjetësues nuk do të ketë vetëm poqese lejon Ai. Ky, madhëria e Tij është All-llahu, Zoti juaj, pra adhurojeni Atë! A nuk merrni mësim?');
INSERT INTO "ahmeti" VALUES(10,4,'10:4','kthimi i të gjithë juve është te Ai, premtimi i All-llahut, është i vërteë. Ai e filloi krijimin e pastaj e përsërit atë për t’i shpërblyer me të drejtë ata që besuan dhe bënë vepra të mira. E për ata që mohuan, ka pije të valë dhe dënim të dhembshëm, për shkak se ata mohonnin.');
INSERT INTO "ahmeti" VALUES(10,5,'10:5','Ai e bëri diellin, e hënën dritë dhe asaj (hënën) ia caktoi fazat, që ta dini numrin e viteve dhe llogarinë. All-llahu nuk krijoi këtë për tjetër, pos me qëllim të caktuar. Ai ia sqaron argument një populli që kupton.');
INSERT INTO "ahmeti" VALUES(10,6,'10:6','Në ndërrimin e natës e të ditës, dhe në çka krijoi All-llahu në qiej e në tokë, vërtet ka fakte për njerëzit që kanë droje.');
INSERT INTO "ahmeti" VALUES(10,7,'10:7','Ata që nuk presin takimin Tonë, që janë të kënaqur me jetën e dynjasë dhe kanë gjetur prehje në të, dhe ata që janë indieferentë ndaj argumenteve Tona.');
INSERT INTO "ahmeti" VALUES(10,8,'10:8','Vendi për të tillët është zjarri, për shkak të asaj që punuan.');
INSERT INTO "ahmeti" VALUES(10,9,'10:9','E ata që besuan dhe bënë vepra të mira, Zoti i tyre, për shkak të besimit të tyre i udhëzon në Xhennete të begatshme në të cilët rrjedhin lumenj.');
INSERT INTO "ahmeti" VALUES(10,10,'10:10','Lutja e tyre aty është: “I lartë je o All-llah!”, e përshëndetja e tyre është: Selam! dhe lutja e fundit e tyre: “Falënderimi i qoftë All-llahut, Zotit të botëve!”');
INSERT INTO "ahmeti" VALUES(10,11,'10:11','Sikur All-llahu t’ua shpejtonte njerëzve pranimin e kërkesës për të keqen, ashtu si e pranon shpejtimin e kërkesës për të mirën, atyre do t’u përfundonte afati (i vdekjes). Po Ne, ata që nuk janë të bindur se do të vijnë para nesh, i lëmë të bredhin në mashtrimet e tyre.');
INSERT INTO "ahmeti" VALUES(10,12,'10:12','E kur njeriun e godet dëmi, ai na lut qoftë shtrirë, ulur ose në këmbë, e kur Ne ia largojmë të keqen, ai, sikurse të mos na ishte ulur fare për dëmin që e pat goditur. Kështu kriminelëve u duket mirë ajo që veprojnë.');
INSERT INTO "ahmeti" VALUES(10,13,'10:13','Ne kemi shkatërruar gjenerata para jush (o idhujtarë), pse nuk besonin, edhe pse të dërguarit u erdhën me argumente të qarta. Kështu shpërblejmë Ne njerëzit kriminelë.');
INSERT INTO "ahmeti" VALUES(10,14,'10:14','Pastaj juve ju bëmë zëvendësues pas tyre në Tokë për t’ju parë si do të veproni.');
INSERT INTO "ahmeti" VALUES(10,15,'10:15','Dhe kur atyre u lexohen ajetet Tona të qarta, ata që nuk besojnë takimin Tonë (ringjalljen), thonë: “Sillna një Kur’an tjetër, ose ndryshoje këtë!” Thuaj: “Mua nuk më takon që ta ndrushojë vetë, unë përcjelli vetëm atë që më shpallet, unë nëse kundërshtoj Zotit tim i frikësohem dënimit të një Ditës të madhe.');
INSERT INTO "ahmeti" VALUES(10,16,'10:16','Thuaj: “Sikur të kishte dashur All-llahu, unë nuk do t’ua kisha lexuar juve atë, as nuk u kisha njoftuar me të. Unë para tij kam jetuar në mesin tuaj (as nuk ditur as nuk kam lexuar), a nuk mendoni?”');
INSERT INTO "ahmeti" VALUES(10,17,'10:17','A ka më mizor se ai, që shpif ndaj All-llahut gënjeshtër ose përgenjeshtron faktet e Tij? Mëkatarët vërtet s’kanë për të shpëtuar.');
INSERT INTO "ahmeti" VALUES(10,18,'10:18','Ata pos All-llahut adhurojnë çdo gjë që nuk bën as dobi, e thonë: “Këta janë ndërmjetësuesit tanë te All-llahu!” Thuaj: “A po e informoni All-llahun me diçka që Ai nuk e di se ç’ka në qiej dhe në tokë?” I pastër është madhëria e Tij nga ajo që shoqërojnë!');
INSERT INTO "ahmeti" VALUES(10,19,'10:19','Njerëzit nuk ishin tjetër pos të një feje, e u përçanë. E sikur të mos ishte caktim i hershëm nga Zoti yt, do të përfundohej (me dënim) mes tyre, për atë që ishin të përçarë.');
INSERT INTO "ahmeti" VALUES(10,20,'10:20','Dhe thonin: “Përse nuk i vjen atij (Muhammedit) ndonjë mrekulli nga Zoti i tij?” Po ti thuaj: “E fshehta është (çështje) vetëm e All-llahut (Ai sjell mrekulli). Prandaj, ju pritni se edhe unë së bashku me ju po pres!”');
INSERT INTO "ahmeti" VALUES(10,21,'10:21','E kur Ne ua dhurojmë njerëzve mëshirën pasi t’i ketë godotur e keqja (skamja), kur qe ata u bëjnë hile argumenteve tona. Thuaj: “All-llahu është më i shpejti në ndëshkim”. S’ka dyshim, përcjellësit tanë (melaiket) regjistrojnë atë që po thurrni ju.');
INSERT INTO "ahmeti" VALUES(10,22,'10:22','Ai (All-llahu) ua bëri të mundshëm udhëtimin në tokë e në det, deri kur jeni në anije që lundrojnë me ta (me udhëtarët) me anë të një ere të lehtë dhe janë të lumtur me të (me erën e lehtë), ia beh një erë fortë dhe nga të gjitha anët rrethohen nga valët dhe binden se janë shkatërruar, e lusin All-llahun pa farë përzierje të idhujve (duke thënë): Nëse na shpëton nga kjo (katastrofë), ne do të jemi gjithnjë falënderues!”');
INSERT INTO "ahmeti" VALUES(10,23,'10:23','E kur Ai (All-llahu) i shpëtoi ata, ja, ata veprojnë mbrapshtë në tokë, pa arsye. O ju njerëz! Kryeneçësia është vetëm kundër vetes suaj, është përjetim i jetës së kësajbote, pastaj kthimi juaj është te Ne, e Ne ju shpërblejmë për veprimet tuaja.');
INSERT INTO "ahmeti" VALUES(10,24,'10:24','Shembulli i jetës së kësaj bote është si i një shiu që e kemi zbritur nga qielli, e me anën e të cilit gërshetohen bimët e tokës (mbijnë dhe shpeshtohen të gjitha llojet) prej nga hanë njerëzit e kafshët deri kur toka të ketë marrë stolinë e vet dhe të jetë zbukuruar (me bimë, pemë e behar), e banorët e saj të mendojnë se janë të zotët e saj, e asaj i vjen urdhëri ynë, natën ose ditën, Ne e bëjmë atë (të mbjellat) të korrur sikurse të mos ekzistonte dje. Kështu Ne u sqarojmë faktet njerëzve që mendojnë.');
INSERT INTO "ahmeti" VALUES(10,25,'10:25','All-llahu thërret për në Xhennet, dhe atë që do, e vë në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(10,26,'10:26','Atyre që bëjnë vepra të mira, u takon e mira (Xhenneti) edhe më tepër (e shohin All-llahun). Fytyrat e tyre nuk i mbulon pluhuri i zi as nënçmimi, ata janë banues të Xhennetit, aty janë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(10,27,'10:27','E ata që bënë të këqia, dënimi i së keqes është aq sa është ajo, ata i mbulon poshtërimi. Ata nuk mund t’i mbrojë kush nga All-llahu, fytyrat e tyre, porsi t’i kishte mbuluar ndonjë copë e errët e natës. Ata janë banues të përjetshëm të zjarrit.');
INSERT INTO "ahmeti" VALUES(10,28,'10:28','Atë ditë i tubojmë të gjithë, ndërkaq atyre që i kanë bërë shok All-llahut u themi: “Rrini në vendin tuaj, ju dhe idhujt tuaj!” Ne ndajmë ata. E idhujt e tyre u thonë: “Ju nuk na adhuruat ne!”');
INSERT INTO "ahmeti" VALUES(10,29,'10:29','All-llahu na mjafton si dëshmues ms nesh e jush (kështu u thonë idhujt idhujtarëve). Ne nuk dimë gjë për adhurimin tuaj ndaj nesh (ne nuk dëgjojmë, nuk shohim e nuk kuptojmë).');
INSERT INTO "ahmeti" VALUES(10,30,'10:30','Aty, në atë vend, secili njeri provohet me atë që e ka bërë më parë dhe kthehen te All-llahu, Sundimtari i tyre i vërtetë dhe humb (shkon huq) prej tyre ajo që trillonin (se do t’u bëjnë shefatë).');
INSERT INTO "ahmeti" VALUES(10,31,'10:31','Thuaj: “Kush ju furnizon me ushqim nga qielli e toka, kush e ka në dorë të dëgjuarit e të pamët (tuaj), kush nxjerr të gjallin nga i vdekuri e kush nxjerr të vdekurin nga i gjalli, kush rregullon çdo çështje?” Ata do të thonë: “All-llahu”. Ti thuaj: “A nuk frikësoheni?” (dënimit).');
INSERT INTO "ahmeti" VALUES(10,32,'10:32','E ky është All-llahu, Zoti juaj i vërtetë. Pas të vërtetës nuk ka tjetër pos iluzione, e si po ia ktheni shpinën (të vërtetës)?');
INSERT INTO "ahmeti" VALUES(10,33,'10:33','Kështu është pra, vendimi i zotit tënd kundër të shfrenuarve, pse ata nuk besojnë.');
INSERT INTO "ahmeti" VALUES(10,34,'10:34','Thuaj: “A ka prej idhujve tuaj ndokush që ta fillojë krijimin e mandej atë (pas vdekjes) ta rikthejë?” Thuaj: “All-llahu e nus krijimin dhe sërish e rikthen. Si i largohen pra, së vërtetës?');
INSERT INTO "ahmeti" VALUES(10,35,'10:35','Thuaj: “A ka ndokush prej idhujve tuaj që ndonjë të humbur ta drejtojë në rrugën edrejtë? Thuaj: “Vetëm All-llahu udhëzon në të vërtetën.. Atëherë pra, a është më e drejtë të shkohet pas tij që udhëzon në rrugën e drejtë, apo pas atij që nuk udhëzon, (sepse nuk mund të udhëzojë as vetveten) vetëm nëse prej dikuj tjetër udhëzohet? ç’është me ju? Si gjykoni?');
INSERT INTO "ahmeti" VALUES(10,36,'10:36','Shumica e tyre nuk përkjnë tjetër pos supozim, e supozimi nuk është asgjë ndaj të vërtetës. All-llahu di shumë mirë për atë që punojnë.');
INSERT INTO "ahmeti" VALUES(10,37,'10:37','Nuk është e logjikshme të mendohe se ky Kur’an është i trilluar prej dikujt pos All-llahut, por është vërtetues i asaj (shpalljes) që ishte më parë, dhe sqarues e komentues i librit. Nuk ka farë dyshimi se është (i zbritur) nga Zoti i botëve.');
INSERT INTO "ahmeti" VALUES(10,38,'10:38','Përkundër kësaj, ata (idhujtarët) thonë se atë e trilloi ai (Muhammedi). Ti thuaj: “Sillne pra ju një kaptinë të ngjajshme me këtë, madje thirrni kë të doni në ndihmë, pos All-llahut, po qe se jeni të drejtë në atë që thoni”.');
INSERT INTO "ahmeti" VALUES(10,39,'10:39','Por ja, ata përgënjeshtruan atë (Kur’anin) pa e kuptuar dhe pa ju ardhur shpjegimi i tij. Po kështu, gënjenin dhe ata që ishin më përpara. Shiko si ishte përfundimi i zullumqarëve.');
INSERT INTO "ahmeti" VALUES(10,40,'10:40','Ka prej tyre (te të cilët u dërgua Muhammedi) që e besojnë atë (Kur’anin), e ka prej tyre edhe asaish që atë nuk e besojnë. Zoti i njeh ë së miri kokëfortit.');
INSERT INTO "ahmeti" VALUES(10,41,'10:41','Po nëse ata të përgënjeshtruan ty, thuaj: “Mua më takon (shpërblimi nga) vepra juaj, ju nuk përgjigjeni për atë që bëj unë, e as për atë që bëni ju.');
INSERT INTO "ahmeti" VALUES(10,42,'10:42','Ka prej tyre që të dëjojnë (kur lexon, por formalisht). A mund të bësh të shurdhëtin të dëgjojë, edhe kur ata nuk kuptojnë?');
INSERT INTO "ahmeti" VALUES(10,43,'10:43','Ka prej tyre që të shikon ty (duket se po të shikon). A mund ta udhëzosh ti të verbërin, kur ata nuk shohin (të vërtetën)?');
INSERT INTO "ahmeti" VALUES(10,44,'10:44','All-llahu nuk u bën asgjë të padrejtë njerëzve, por ata i bëjnë të padrejtë vetes së tyre.');
INSERT INTO "ahmeti" VALUES(10,45,'10:45','(Përkujto) Ditën kur i tubojnë ata, (atyre u duket) sikur nuk qëndruan (në dynja) vetëm një moment të ditës, atëherë njihen mes vete. E, vërtet, kanë dështuar ata që nuk besuan këë prezencë para All-llahut, edhe nuk gjetën udhën e drejtë.');
INSERT INTO "ahmeti" VALUES(10,46,'10:46','Nëse ta bëjmë të mundshme të shohish diçka nga (dënimi) që u premtuan atyre (mirë), ose ta marrim shpirtinty (kurse ti nuk sheh gjë), e ardhmja e tyre është vetëm te Ne. All-llahu është dëshmues i asaj që punojnë.');
INSERT INTO "ahmeti" VALUES(10,47,'10:47','Secili popull kishte të dërguarin e vet, e kur u vinte i dërguari i tyre bëhej gjykimi i drejtë mes tyre, atyre nuk u bëhet padrejtësi.');
INSERT INTO "ahmeti" VALUES(10,48,'10:48','Ata thonë: “Kur do të jetë ky premtim (dënim), nëse jeni të drejtë ç’ka thoni?');
INSERT INTO "ahmeti" VALUES(10,49,'10:49','Thuaj: “Unë nuk mund t’i sjellë vetes sime as dëm as dobi, pos atë që dëshiron All-llahu”. çdo popull ka afatin (e caktuar), e kur të vijë afati i tyre, ata nuk mund ta vonojnë as për një moment e as ta ngutin.');
INSERT INTO "ahmeti" VALUES(10,50,'10:50','Thuaj: “Më tregoni, nëse u vjen dënimi i Tij natën ose ditën, e çka i shluen të nguten për të, kriminelët?”');
INSERT INTO "ahmeti" VALUES(10,51,'10:51','A pasi që të ndodhë (dënimi) do të besoni atë? A tani!? (po besoni), e ju ishit ata që kërkonit t’u vijë më shpejt?');
INSERT INTO "ahmeti" VALUES(10,52,'10:52','Më vonë (në ditën e kijametit) atyre që bënë zullum u thuhet: “Shijoni dënimin e përjetshëm!” Mos jeni dënuar më shumë se ç’keni merituar?”');
INSERT INTO "ahmeti" VALUES(10,53,'10:53','E ata kërkojnë t’u tregosh (e do të thonë): a është e vërtetë ajo (që thua për dënimin)? Thuaj: “Po, pasha Zotin tim, ajo është më se e vërtetë, dhe ju nuk do të mund ta pengoni kurrsesi.');
INSERT INTO "ahmeti" VALUES(10,54,'10:54','Secili njeri që ka dëmtuar veten (duke mosbesuar), po të ishte e tij çdo gjë që ekziston në tokë, ai do ta flijonte atë (për të shpëtuar). E kur e shohin dënimin, aa fshehindëshprimin (nga hutia). Atëherë bëhet gjykimi i drejtë mes tyre nuk u bëhet padrejtë.');
INSERT INTO "ahmeti" VALUES(10,55,'10:55','Vini re! Nuk ka dyshim se gjithçka që gjendet në qiej e në tokë është e All-llahut! Kini kujdes. Premtimi iAll-llahut është më se i vërteë, por shumica e tyre nuk e dinë.');
INSERT INTO "ahmeti" VALUES(10,56,'10:56','Ai jep jetë dhe vdekje, dhe vetëm te Ai do të ktheheni.');
INSERT INTO "ahmeti" VALUES(10,57,'10:57','O ju njerëz! Juve ju erdhi nga zoti juaj këshilla (Kur’ani) dhe shërimi i asaj që gjendet në krahrorët tuaj (në zemra), edhe udhëzim e mëshirë për besimtarët.');
INSERT INTO "ahmeti" VALUES(10,58,'10:58','Thuaj: “Vetëm mirësisë së All-llahut dhe mëshirës së Tij le t’i gëzohen, se është shumë më e dobishme se ajo që grumbullojnë ata.');
INSERT INTO "ahmeti" VALUES(10,59,'10:59','Thuaj: “Më thuani, atë që All-llahu ju dha si ushqim (të lejuar), e ju nga ai diçka bëtë të ndaluar e diçka të lejuar?” Thuaj: “A ju lejoi All-llahu ju, ose ju i shpifni All-llahut?”');
INSERT INTO "ahmeti" VALUES(10,60,'10:60','Po çfarë është mendimi i atyre që shpifin All-llahut, për ditën e kijametit? All-llahu është dhurues i madh ndaj njerëzve, por shumica e tyre nuk falënderojnë.');
INSERT INTO "ahmeti" VALUES(10,61,'10:61','Ti nuk angazhohesh me asnjë çështje, nuk lexon nga ai pjesë nga Kur’ani dhe nuk bëni ndonjë vepër, vetëm se ne jemi prezentuesit tuaj, kur ju ndërmerrni atë. Zotit tënd nuk mund t’i fshihet as në okë e as në qiell as sa grimca e as më e vogël se ajo e as më e madhe, por vetëm sa është evidentuar në librin e sigurt.');
INSERT INTO "ahmeti" VALUES(10,62,'10:62','A nuk janë vërtet Ewlijatë (të dashurit) e All-llahut pa frikë e brengosje?');
INSERT INTO "ahmeti" VALUES(10,63,'10:63','Të cilët besuan dhe ishin (prej atyre) që frikësohen.');
INSERT INTO "ahmeti" VALUES(10,64,'10:64','Atyre u jepen myzhde në jetën e dunjasë dhe në jetën tjetër (për shpëtim në Xhennet). Nuk ndryshojnë fjalët e All-llahut. E, ai është suksesi i madh.');
INSERT INTO "ahmeti" VALUES(10,65,'10:65','Po ty të mundojnë thëniet e tyre. E tërë fuqia dhe ngadhënjimi i takon vetëm All-llahut. Ai i dëgjon dhe di të gjitha.');
INSERT INTO "ahmeti" VALUES(10,66,'10:66','Ta keni të ditur se gjithçka që është një qiej e në tokë janë në pushtetin e All-llahut (pra, edhe idhujtarët). E ata, që pos All-llahut adhurojnë, në të vërtetë nuk janë kah pasojnë zota, ata nuk janë kah pasojnë tjetër pos supozimeve dhe nuk janë tjetër vetëm se gënjejnë.');
INSERT INTO "ahmeti" VALUES(10,67,'10:67','Ai (All-llahu) bëri për ju natën që në të pushoni, e ditën të ndritshme (të shihni). Në këtë (që bëri) vërtet ka argumente për një popull që dëgjon.');
INSERT INTO "ahmeti" VALUES(10,68,'10:68','(Pas të gjitha argumenteve) Ata (jobesimtarët) thanë: “All-llahu ka fëmijë”. Larg asaj është All-llahu, Ai s’ka nevojë, e Tij është ç’ka në tokë, ju nuk argumente për këtë (që ta thoni), a thoni për All-llahun çka nuk dini?');
INSERT INTO "ahmeti" VALUES(10,69,'10:69','Thuaj: “Ata që trillojnë gënjeshtër për All-llahun, nuk kanë për të shpëtuar”.');
INSERT INTO "ahmeti" VALUES(10,70,'10:70','Ata kanë një përjetim në dynja, pastaj kthimi i tyre është vetëm te Ne, e Ne do të bëjmë që ata të shijojnë dënimin e dhembshëm për shkak se nuk besonin.');
INSERT INTO "ahmeti" VALUES(10,71,'10:71','Lexoju atyre ngjarjen e Nuhut, kur i tha popullit të vet: “O populli im, nëse u vjen rëndë qëndrimi im më mesin tuaj, përkujtimet e mia me ajetet e All-llahut, e unë iu kam mbështetur vetëm All-llahut, ju vendosni për qëllimin tuaj, thirrni në ndihmë edhe zotat tuaj, e mos e mbani fshehtë, dhe zbatoni atë kundër meje e mos pritni.');
INSERT INTO "ahmeti" VALUES(10,72,'10:72','E nëse prapësoheni (nga mësimet e mia), unë nuk u kërkoj ndonjë shpërblim, se shpërblimi im është vetëmprej All-llahut. Unë jam i urdhëruar të jam njëri prej muslimanëve.');
INSERT INTO "ahmeti" VALUES(10,73,'10:73','e ata (populli i tij) e konsideruan atë rrenacak e Ne e shpëtuam atë dhe ata që ishin bashkë në anije, dhe i bëmë që ata të jenë pasardhës (zëvendësues), kurse ata që përgënjeshtruan ajetet tona i përmbytëm. Shih pra, si ishte fundi i gënjeshtarëve.');
INSERT INTO "ahmeti" VALUES(10,74,'10:74','Mandej pas tij dërguam të dërguar te popujt e tyre, të cilët erdhën dhe u sollën atyre argumente të forta, por ata nuk i besuan asaj, të cilën më parë e kishin përgënjeshtruar. Kshtu, pra u vulosim zemrat atyre që kalojnë kufijtë.');
INSERT INTO "ahmeti" VALUES(10,75,'10:75','Pastaj, pas tyre dërguam Musai dhe Harunin me argumentet tona, te faraoni dhe paria e rrethit ta tij, po ata paraqitën kryelartësi, pse ishte popull që bënte krime.');
INSERT INTO "ahmeti" VALUES(10,76,'10:76','E kur u erdhi atyre e vërteta e sigurt nga Ne, ata thanë: “Kjo nuk është tjetër pos magji e qartë.');
INSERT INTO "ahmeti" VALUES(10,77,'10:77','Musai tha: A të vërtetës i thoni magji? A magji është kjo? Magjistarët nuk shpëtojnë?');
INSERT INTO "ahmeti" VALUES(10,78,'10:78','Ata thanë: “Mos erdhe të na largosh nga ajo (feja) që e gjetëm te prindërit tanë, për t’ju mbetur ju dyve madhështia në tokë? Po ne nuk u besojmë juve dyve.');
INSERT INTO "ahmeti" VALUES(10,79,'10:79','Faraoni tha: “Sillmëni mua të gjithë magjistarët e aftë.');
INSERT INTO "ahmeti" VALUES(10,80,'10:80','E kur erdhën magjistarët, Musai u tha: “Hidhni ç’keni për të hedhur”.');
INSERT INTO "ahmeti" VALUES(10,81,'10:81','E kur i hodhën ata, Musai u tha: “Kjo që e sollët ju tash, vërtet është magji!” Po All-llahu do ta asgjësojë këtë, sepse All-llahu nuk e përkrahë veprën e të prishurve.');
INSERT INTO "ahmeti" VALUES(10,82,'10:82','Dhe me argumentet e veta All-llahu e fuqizon të vërtetën, ndonëse e urrejnë të pabesët.');
INSERT INTO "ahmeti" VALUES(10,83,'10:83','Duke pasur frikë nga faraoni dhe nga rrethi i tij se do t’i turturojnë, Musait nuk i besoi kush pos një pjese të pasardhësve të popullit të tij. Vërtet, faraoni ishte mbizotërues në tokë dhe ai ishte prej shkatërrimtarëve.');
INSERT INTO "ahmeti" VALUES(10,84,'10:84','Musai tha: “O populli im, nëse i keni besuar All-llahut, vetëm atij mbështetuni, nëse jeni të dorëzuar (vendimit të Tij)!”');
INSERT INTO "ahmeti" VALUES(10,85,'10:85','Ata iu përgjegjën duke i thënë: “All-llahut iu kemi mbështetur, Zoti në, mos i mundëso popullit mizor të na sprovojë!”');
INSERT INTO "ahmeti" VALUES(10,86,'10:86','Dhe me mëshirën tënde, na shpëto prej popullit jobesimtarë!”');
INSERT INTO "ahmeti" VALUES(10,87,'10:87','Ne i thamë Musait dhe vëllait të tij t’i bëni vend në Egjipt popullit tuaj, shtëpitë tuaja t’i bëni faltore e ta falni namazin rregullisht. Ti (Musa) përgëzoi besimtarët!');
INSERT INTO "ahmeti" VALUES(10,88,'10:88','Musai tha: “Zoti ynë, Ti i ke dhënë faraonit dhe parisë së tij mjete të përjetimit dhe pasuri në jetën e kësaj bote, ashtu që o Zot ynë, po i largojnë njerëzit prej rrugës tënde. Zoti ynë, zhduke pasurinë e tyre, shtrëngoi zemrat e tyre, e të mos besojnë derisa të shijojnë dënimin e idhët.');
INSERT INTO "ahmeti" VALUES(10,89,'10:89','Ai (All-llahu) tha: “Lutja juaj është pranuar, prandaj ju të dy qqëndroni ashtu si jeni e mos ndiqni rrugën r atyre që nuk dinë!');
INSERT INTO "ahmeti" VALUES(10,90,'10:90','Ne i kapërcyem beni israilët përtej detit, e faraoni dhe ushtria e tij i ndoqi mizorisht dhe armiqësisht derisa e përhshiu atë përmbysja e ai tha: “Besova se nuk ka Zot tjetër pos Atij që i besuan beni israilët, edhe unë jam nga muslimanët!”');
INSERT INTO "ahmeti" VALUES(10,91,'10:91','Ti tani beson, ndërsa ma parë kundërshtove dhe ishe njëri prej shkatërruesve!');
INSERT INTO "ahmeti" VALUES(10,92,'10:92','Sot po të shpëtojmë ty (po të nxjerrim prej detit) me trupn tënd (të vdekur), që të shërbesh si argument për ata që vijnë pas teje. Vërtet, ekziston një shumicë njerëzis që nuk hulumtojnë argumentet Tona.');
INSERT INTO "ahmeti" VALUES(10,93,'10:93','Në të vërtetë, Ne i vendosëm beni israilët (pas shkatërrimit të armiqve të tyre) në një vend të mirë, i furnizuam ata me ushqime të dobishme dhe nuk ishin të përçarë (rreth fesë) vetëm pasi që u erdhi atyre dituria (Tevrati). Zoti yt, do të gjykojë mes tyre në ditën e kijametit, për atë që ishin përçarë.');
INSERT INTO "ahmeti" VALUES(10,94,'10:94','Po, (fjala vjen) nëse je në dyshim për këtë që të zbritëm ty (për ngjarjet e pejgamberëve), atëherë pyeti ata që lexojnë librin para teje. Ty të erdhi nga Zoti yt e vërteta, pra, mos u bë prej atyre që dyshojnë.');
INSERT INTO "ahmeti" VALUES(10,95,'10:95','Dhe mos u bë prej atyre që përgënjeshtruan argumentet e All-llahut, e të bëhesh nga të dështuarit.');
INSERT INTO "ahmeti" VALUES(10,96,'10:96','Kundër atyre, për të cilët ka përfunduar caktimi i Zotit tënd, ata nuk besojnë,');
INSERT INTO "ahmeti" VALUES(10,97,'10:97','Edhe sikur t’u kishin paraqitur atyre çdo lloj argumenti, ata nuk besojnë derisa të shohin dënimin e rëndë.');
INSERT INTO "ahmeti" VALUES(10,98,'10:98','Përse nuk pati vendbanim që të ketë besuar e besimi i tij të ketë ndihmuar (në shpëtim), pos popullit të Junusit, që pasi që besoi, Ne larguam prej tyre dënimin shëmtues në jetën e kësaj bote dhe i lejuam të jetojnë deri në një afat të caktuar.');
INSERT INTO "ahmeti" VALUES(10,99,'10:99','SIkur të kishte dashur Zoti yt, do t’i besonin çka janë në tokë që të gjithë. A ti di t’i detyrosh njerazit të bëhen bedsimtarë?');
INSERT INTO "ahmeti" VALUES(10,100,'10:100','Nuk është e mundur për asnjë njeri të besojë (ndryshe), pos me ndihmën e All-llahut. E dënimin u jepë atyre që nuk mendojnë.');
INSERT INTO "ahmeti" VALUES(10,101,'10:101','Thuaj: “Vështroni me vëmendje çka në (nga argumentet) në qiej e në tokë. Po argumentet dhe qortimet nuk bëjnë dobi ndaj një popull që nuk beson.');
INSERT INTO "ahmeti" VALUES(10,102,'10:102','A janë duke pritur tjetër (fat) pos ditët (e zeza) si të atyre që ekzistonin para tyre? Thuah: “Pritni, pra, se edhe unë bashkë me ju po pres!”');
INSERT INTO "ahmeti" VALUES(10,103,'10:103','E Ne pastaj i shpëtojmë të dërguarit tanë, edhe ata që besuan. Ja kështu, është detyrë e Jona, t’i shpëtojmë besimtarët.');
INSERT INTO "ahmeti" VALUES(10,104,'10:104','Thuaj: “O ju njerëz, nëse ju dyshoni në fenë time, unë nuk adhuroj ata që ju i adhuroni pos All-llahut, por adhuroj All-llahun që u jepë vdekjen (ashtu si u ka dhënë jetën), dhe jam i urdhëruar të jem besimtarë (i Zotit një).');
INSERT INTO "ahmeti" VALUES(10,105,'10:105','Dhe (jam i urdhëruar): Përqëndrohu me tërë qenien tënde në fenë e drejtë, e mos u bë nga idhujtarët!');
INSERT INTO "ahmeti" VALUES(10,106,'10:106','Dhe mos lut tjetër kë pos All-llahut, ndonjë (idhull) që nuk të sjell as dobi as dëm, e nëse bën atë, dije se i ke bërë padrejt vetvetes.');
INSERT INTO "ahmeti" VALUES(10,107,'10:107','Nëse All-llahu të provon me ndonjë të keqe, atë s’mund ta largojë kush pos Tij, po nëse dëshiron të japë ndonjë të mirë, s’ka kush që mund ta pengojë dhuntinë e Tij. E shpërblen me të, atë që do nga robët e Tij. Ai falë dhe mëshiron shumë.');
INSERT INTO "ahmeti" VALUES(10,108,'10:108','Thuaj: “O ju jerëz, juve ju erfdhi e vërteta nga Zoti juaj, e kush merr rrugën e drejtë, ai ka vetëm për veten e vet, ndërsa kush e merr të shtrembëren, ai e ka humbur veten. Unë nuk jam rojë e juaj!”');
INSERT INTO "ahmeti" VALUES(10,109,'10:109','Ti ec pas asaj që të shpallet dhe bëhu i durueshëm derisa All-llahu të gjykojë. Ai është më i miri i gjykatësve!');
INSERT INTO "ahmeti" VALUES(11,1,'11:1','Elif, Lamë, Ra, (ky është) Libër, ajetet e të cilit janë radhitur (në mënyrë të përsosur, njëkohësisht) edhe shkoqitur nga i Dijshmi i të gjitha çështjeve në hollësi.');
INSERT INTO "ahmeti" VALUES(11,2,'11:2','Që te mos adhuroni tjetër përveç All-llahut. Unë jam (dërguar) nga Ai qortues e përgëzues për ju.');
INSERT INTO "ahmeti" VALUES(11,3,'11:3','E të kërkoni falje Zotit tuaj dhe pendohuni (kthehuni) te Ai, se Ai do të ju mundësojë përjetime të mira (në këtë jetë) deri në një afat (të caktuar) dhe çdo punëmiri i jep shpërblimin e merituar. në qoftë se refuzojnë (të kërkojnë falje), unë pra kam frikë për ju, dënimin e ditës së madhe.');
INSERT INTO "ahmeti" VALUES(11,4,'11:4','Se kthimi i juaj është vetëm te All-llahu, Ai ka fuqi për çdo send.');
INSERT INTO "ahmeti" VALUES(11,5,'11:5','Ja, ata mbështjellojnë (armiqësinë në) gjoksat e tyre që ta fshehin nga Ai, po ta dini se edhe kur mbulohen me petkat e tyre, Ai e di se çka fshehin dhe çka shfaqin. S’ka dyshim se Ai e di shumë mirë çka mbajnë zemrat (qëllimet) e tyre.');
INSERT INTO "ahmeti" VALUES(11,6,'11:6','Nuk ka anjë gjallesë në tokë që All-llahu të mos ia ketë garantuar furnizimin e saj, Ai e di vendbanimin dhe vendstrehimin e saj (pas vdekjes). Të gjitha (këto) janë në librin e njohur (Levhi Mahfudhë).');
INSERT INTO "ahmeti" VALUES(11,7,'11:7','Ai është i cili krijoi qiejt e tokën brenda gjashtë dite (fazave), e Arshi (Froni) i Tij kishte qenë (më parë) mbi ujë, që t’ju sprovojë, se cili prej jush është më vepërmirë. Nëse ti u thua: “Ju do të ringjalleni pas vdekjes!” Ata që nuk besuan thonë: “Kjo (çka na thua) nuk është tjetër vetën se magji e hapur.');
INSERT INTO "ahmeti" VALUES(11,8,'11:8','Nëse Ne e vonojmë ndëshkimin për një kohë të shkurtër kundër tyre, ata thonë: “çka po e pengon atë?” Le të dijnë se ditën kur t’u vijë, ai nuk zbrapët prej tyre, ata do t’i rrethojë ai (dënimi) me të cilin talleshin.');
INSERT INTO "ahmeti" VALUES(11,9,'11:9','Po nëse Ne ia japim njeriut ndonjë begati nga ana jonë, e pastaj e tërheqim atë prej tij, ai do të jetë i dëshpruar dhe përbuzës.');
INSERT INTO "ahmeti" VALUES(11,10,'11:10','E nëse pas të keqes që e pat goditur atë, Ne dhurojmë të mira, ai do thotë: “Kaluan të këqiat prej meje!” Vërtet, ai është shfrenuar dhe mendjemadh.');
INSERT INTO "ahmeti" VALUES(11,11,'11:11','Përveç atyre, të cilët kishin durim dhe bënin vepra të mira, të tillët kanë falje (të gabimeve) dhe shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(11,12,'11:12','A mos (shpresojnë idhujtarët se) do të lëshë nga ajo që t’u shpall ty (ngase u vjen rëndë ta dëgjojnë), dhe të shtëngon zemrën tënde ajo (komunikimi i shpalljes), për shkak se ata do të thonë: “Pse të mos i ketë zbritur atij (Muhammedit) ndonjë pasuri, ose përse të mos i ketë ardhur bashkë me të ndonjë engjëll?” Ti je vetëm qortues, e All-llahu për kujdeset prër çdo send.');
INSERT INTO "ahmeti" VALUES(11,13,'11:13','Apo, pse ata thonë: “Ai (Muhammedi) e trilloi atë (Kur’anin)”. Thuaj: “Formuloni pra, dhjetë kaptina si ai (Kur’ani) ashtu të trilluara (siç thoni ju) dhe thirrni, pos All-llahit, po qe se jeni të drejtë (çka thoni), kë të mundeni për ndihmë!”');
INSERT INTO "ahmeti" VALUES(11,14,'11:14','E nëse nuk përgjigjen juve, atëherë pra ta keni të qartë se ai (Kur’ani), është i zbritur me dijen e All-llahut dhe se nuk ka zot pos Tij. A jeni pra myslimanë?”');
INSERT INTO "ahmeti" VALUES(11,15,'11:15','Kush ka për qëllim jetën e kësaj bote dhe të mirat e saj, Ne do t’u plotësojmë atyre shpërblimin e veprave të tyre në të, dhe atyre në të, dhe atyre nuk do t’u mungojë gjë.');
INSERT INTO "ahmeti" VALUES(11,16,'11:16','Të këtillëve në botën tjetër u përket vetëm Xhehennemi. Ajo që punuan dhe vepruan ata ka dështuar dhe është asgjësuar.');
INSERT INTO "ahmeti" VALUES(11,17,'11:17','A ai që është (i mbështetur) në argument nga Zoti i tij dhe atë e përforcon dëshmia nga Ai, e para tij (Kur’anit) ishte (dëshmitar) libri i Musait (Terati) që ishte udhërrëfyes e mëshirë - (është i njejtë me atë që ka për qëllim vetëm këtë jetë)? Të tillët (që janë në rrugë të drejtë) e besojnë (Kur’anin). E kush prej tyre grupeve refuzuan atë, vendi i tij është zjarri. E ti (Muhammed) mos kij dyshim në te, ai është e vërtetë nga Zoti uyt, në të s’ka dyshim, por shumica e njerëzve nuk e besojnë.');
INSERT INTO "ahmeti" VALUES(11,18,'11:18','E kush është më mizor se ai që trillon për All-llahun? Të tillët paraqiten para Zotit të tyre, e dëshmitarët thonë: “Këta ishin të cilët gënjyen ndaj Zotit të tyre, pra mallkimi i All-llahut qoftë kundër mizorëve!”');
INSERT INTO "ahmeti" VALUES(11,19,'11:19','Të cilët pengojnë nga rruga e All-llahut dhe përpiqen atë ta shtrembërojnë, mu ata janë që nnuk e besojnë botën tjetër.');
INSERT INTO "ahmeti" VALUES(11,20,'11:20','Ata nuk ishin të paprekshëm në tokë (prej dënimit të Zotit), dhe ata nuk kishin mbrojtës pos All-llahut. Atyre u shumëfishohet dënimi, sepse ata nuk ishin që mund të dëgjonin dhe as nuk shikonin.');
INSERT INTO "ahmeti" VALUES(11,21,'11:21','Ata mashtruan vetveten dhe humben prej tyre ata (idhujt) që i patën trilluar.');
INSERT INTO "ahmeti" VALUES(11,22,'11:22','Është e vërtetë se ata në botën tjetër janë më të humburit.');
INSERT INTO "ahmeti" VALUES(11,23,'11:23','Ata që nuk besuan dhe bënë vepra të mira dhe u përulën ndaj Zotit të tyre, ata janë banues të Xhennetit, në të janë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(11,24,'11:24','Shembulli i gjendjes së këtyre është (i atyre që nuk besuan) si ai i verbëri dhe i shurdhëti dhe (i atyre që besuan) si ai që sheh dhe dëgjon. A janë në pozitë të barabartë? A nuk përkujtoheni?!');
INSERT INTO "ahmeti" VALUES(11,25,'11:25','Ne patëm dërguar edhe Nuhun te populli i vet (me porosi të njejtë si Muhammedin që t’u thotë): Unë ju tërheq vërejtjenn haptazi.');
INSERT INTO "ahmeti" VALUES(11,26,'11:26','Që të mos adhuroni tjetër kë pos All-llahut, sepse unë kam frikë për dënimin tuaj në ditëm e pikëllueshme.');
INSERT INTO "ahmeti" VALUES(11,27,'11:27','Paria që nuk besoi nga populli i tij tha: “Ne nuk të shohim ndryshe vetëm si njeri, sikurse edhe ne, ne nuk po se të pasojë kush, përveç atyre që janë më të poshtërit e më mendjelehtit nga mesi ynë dhe ne nuk shohim se ju keni ndonjë vlerë mbi ne, përkundraz, ne ju konsiderojmë gënjeshtar!”');
INSERT INTO "ahmeti" VALUES(11,28,'11:28','(Nuhu) Tha: “O populli im, më thuani nëse unë jam i mbështetur në argument të qartë nga Zoti im dhe Ai më dha mëshirë nga ana e Tij, e juve u janë fshehur ato (argumentet ngase jeni dhëmë pas kësaj jete), a mos do t’ju detyrojmë për to (pranimin e tyre), kur ju jeni urrejtës të tyre!');
INSERT INTO "ahmeti" VALUES(11,29,'11:29','O populli im, unë për këtë nuk kërkoj prej jush ndonjë pasuri, shpërblimi im është vetëm te All-llahu. Dhe unë kurrsesi nuk i largoj ata që besuan, ata janë afër Zotit të tyre; por unë ju shoh si popull që nuk dini e nuk kuptoni.');
INSERT INTO "ahmeti" VALUES(11,30,'11:30','O populli im, a nuk po mendoni se nëse unë i përze ata, kush do të mund të më mbrojë mua nga All-llahu (dënimi i Tij)?');
INSERT INTO "ahmeti" VALUES(11,31,'11:31','Unë nuk u them juve se te unë janë depot e All-llahut, as nuk u them se unë e di të fshehtën, as nuk u them se unë jam engjëll, as nuk u them atyre që sytë tuaj i nënçmojnë, se All-llahu nuk u dhuroi atyre të mira. All-llahu e di më së miri se çka në shpirtrat e tyre, pse atëherë unë konsiderohem zullumqar.');
INSERT INTO "ahmeti" VALUES(11,32,'11:32','Ata thanë: “O Nuh, ti polemizove me ne dhe e zgjate polemikën tonë. Urdhëro, e nëse flet të vërtetën, sillnie pra, të na godasë ajo me çka na kërcënohesh!”');
INSERT INTO "ahmeti" VALUES(11,33,'11:33','Ai tha: “Atë ju sjell vetëm allahu, po qe se dëshiron, e ju nuk mund ta pengoni!”');
INSERT INTO "ahmeti" VALUES(11,34,'11:34','Nëse përpiqem t’ju këshilloj, po qe se All-llahu don t’ju humb, këshilla ime nuk bën dobi. Ai është Zoti juaj dhe vetëm te Ai do të ktheheni.');
INSERT INTO "ahmeti" VALUES(11,35,'11:35','A mos po thonë se ai e trilloi atë? Thuaj: “Nëse unë kam trilluar, atëherë ai është mëkati im, e unë jam larg nga krimet tuaja”.');
INSERT INTO "ahmeti" VALUES(11,36,'11:36','E, Nuhut i qe shpallur: “Nuk do të besojë më askush nga populli yt, përveç atyre që kanë besuar (deri më tash), pra mos u brengos për ata që punojnë ata”.');
INSERT INTO "ahmeti" VALUES(11,37,'11:37','E, mbaroje anijen nën mbikëqyrjen tonë dhe me mësimin tonë, e mos m’u drejto Mua për ata që bënë zullum, ata gjithqysh janë të përmbytur.');
INSERT INTO "ahmeti" VALUES(11,38,'11:38','Dhe ai ndërtonte anijen e paria e popullit të tij, saherë që kalonte pranë tij përqeshej me të. Ai u thoshte: “Nëse talleni me ne, edhe ne do të tallemi me ju, ashtu siç po talleni ju!”');
INSERT INTO "ahmeti" VALUES(11,39,'11:39','Dhe më vonë do të kuptoni se cili do të pësojë dënimin e turpshëm dhe mbi të cilin do të jetë dënim i përjetëshëm.');
INSERT INTO "ahmeti" VALUES(11,40,'11:40','E kur erdhi urdhëri ynë (i caktuar për ndëshkim) dhe gufoi (uji) prej furrës (vend ku piqet buka), Ne i thamë: “Ngarko në të çdo krijesëë nga një çift edhe familjen tënde, përveç për kë është marrë vendim i hershëm kundër tij, e (ngarkoje) edhe kush ka besuar, po përveç një pakice nuk i kishin besuar atij.');
INSERT INTO "ahmeti" VALUES(11,41,'11:41','Dhe ai tha: “Hipni në rë, me emrin e All-llahut ajo lundron dhe ndalet. Vërtet, Zoti është që falë shumë, është mëshirues.');
INSERT INTO "ahmeti" VALUES(11,42,'11:42','Ajo lundron me ta nëpër valë si, e Nuhu e thirri djjalin e vet, që ishtë në një vend të ndarë: “O djali im, hip nashkë me ne, e mos u bë me mohuesit!”');
INSERT INTO "ahmeti" VALUES(11,43,'11:43','Ai (djali) tha: “Unë do të ngjitëm në një kodër që do të më mbrojë nga uji (vërshimi)!” Tha: nuk ka sot mbrojtës prej dënimit të All-llahut, pos atë që Ai e ka mëshiruar!” Vala hyri mes tyre të dyve, e ai (djali u mbyt në ujë.');
INSERT INTO "ahmeti" VALUES(11,44,'11:44','E iu pat thënë: “Oj tokë, përbije ujin tënd, dhe o qiell, ndërpreje (shiun), uji u tërhoq, urdhëri u zbatua dhe ajo (anija) undal në (kodrën) Xhudij, e u tha: “I shkatërruar qoftë populli mizor!”');
INSERT INTO "ahmeti" VALUES(11,45,'11:45','Nuhu e luti Zotin e vet, duke thënë: “O, Zoti im, djali im është i familjes sime, e premtimi Yt është isaktë, ndërsa Ti je më idrejti i të drejtëve!”');
INSERT INTO "ahmeti" VALUES(11,46,'11:46','(Zoti) Tha: “O Nuh, ai (djali) nuk ishte nga familja jote (për të cilën të premtova se do t’i shpëtojë), ai ishte punëkeq, e ti mos më kërko Mua atë që nuk e di, Unë të këshilloj që të mos bëhesh nga injorantët!”');
INSERT INTO "ahmeti" VALUES(11,47,'11:47','(Nuhu) Tha: “Zoti im, unë mbështetem në mbrojtjen Tënde që të (mos) kërkoj prej Teje atë për çka nuk kam njohuri, e në qoftë se nuk më fal mua dhe nuk më mëshiron Ti, do të jam i humbur!”');
INSERT INTO "ahmeti" VALUES(11,48,'11:48','Iu pat thënë: “O Nuh, zbarko qofsh ishpëtuar nga ana Jonë, me begati për tu. Enjë popull (tjetër) do t’i mundësojmë përjetime, e pastaj do t’i godasë dënim i dhembshëm nga ana Jonë.');
INSERT INTO "ahmeti" VALUES(11,49,'11:49','Këto janë disa rrëfimet e panjohura (pr ty), që po t’i shpallim ty, e që para këtij (Kur’anit) nuk i ke ditur as ti as populli yt. Pra të jeshh i durueshëm se përfundimi (i lavdishëm) është për të devotshmit.');
INSERT INTO "ahmeti" VALUES(11,50,'11:50','Edhe te (populli) Adi (dërguam) nga mesi tyre Hudin që tha: “O populli im, ju nuk keni zot tjetër pos Tij, ju vetëm po hutoheni me trillime.');
INSERT INTO "ahmeti" VALUES(11,51,'11:51','O populli im, unë për këtë nuk kërkoj prej jush ndonjë shpërblim. Shpërblimi im është vetëm prej Atij që më krijoi. A nuk po kuptoni.');
INSERT INTO "ahmeti" VALUES(11,52,'11:52','O populli im, kërkoni falje prej Zotit tuaj dhe ktheniu Atij, Ai ju lëshon shi me bollëk, dhe fuqisë suaj i shton fuqi, e mos refuzoni të bëheni e të bëheni mëkatarë!”');
INSERT INTO "ahmeti" VALUES(11,53,'11:53','Ata thanë: “O Hud, ti nuk na solle ndonjë argument, e ne nuk i brakisim zotat tanë për fjalën tënde dhe ne nuk të besojmë ty”.');
INSERT INTO "ahmeti" VALUES(11,54,'11:54','Ne nuk themi tjetër vetëm se dikush prej zotave tanë të ka goditur me çmendje! Ai tha: “Unë dëshmitar e kam All-llahun, e ju dëshmoni se unë jam larg nga ajo çka ju i shoqëroni.');
INSERT INTO "ahmeti" VALUES(11,55,'11:55','(larg adhurimit) Pos Tij. Ju pra, të gjithë përpiquni kundër meje e mos më jepni afat.');
INSERT INTO "ahmeti" VALUES(11,56,'11:56','Unë iu kam mbështetur Allaut, Zotit tuaj, pse Ai nuk ka asnjë nga gjallesat, e që Ai të mos e ketë nën sundim, vërtet Zoti im është i drejtë.');
INSERT INTO "ahmeti" VALUES(11,57,'11:57','Nëse kundërshtoni, unë ju kumtova atë me tëcilën jamë dërguar te ju. Zoti im do t’ju zëvendësojë me një popull tjetër, Atij nuk mund t’i bëni kurrëfarë dëmi. Vërëtet, Zoti im është përcjellës i çdo sendi”.');
INSERT INTO "ahmeti" VALUES(11,58,'11:58','E kur arriti vendimi Ynë (për dënim), Ne me mëshirën Tonë e shpëtuam Hudin, e bashkë me të edhe ata që kishin besuar. I shpëtuam prej dënimit të rëndë.');
INSERT INTO "ahmeti" VALUES(11,59,'11:59','E ato ishin (gjurmë të gënjeshtrave të fisit) Ad-ët, që mohuan argumentet e Zotit tëtyre, kundërshtuan të dërguarin r Tij, shkuan pas urdhërit të çdo kryelarti kundërshtar.');
INSERT INTO "ahmeti" VALUES(11,60,'11:60','Ata u prcollën me mallkim si në këtë botë, ashtu edhe në ditën e gjykimit. Le të dihet, Adët mohuan Zotin e tyre. Qoftë i shkatërruar Adi, populli i Hudit!');
INSERT INTO "ahmeti" VALUES(11,61,'11:61','Edhe te (populli) Themud e patëm njërin prej tyre, Salihun, e ai u tha: “O populli im, adhuronie All-llahun, ju nuk keni zot tjetër pos Tij, Ai së pari ju krijoi nga dheu dhe ju bëri banues të tij, andaj kërkoni falje prej Tij, dhe shprehni pendim te Ai. S’ka dyshim, Zoti im është afër (me mëshirë), Ai përgjigjet (lutjeve)”!');
INSERT INTO "ahmeti" VALUES(11,62,'11:62','Ata thanë: “O Salih, ti ishe shpresë jona para kësaj. Si mund të na ndalosh të adhurojmë atë që adhuruan prindërit tanë? Vërtet, ne jemi shumë të dyshimtë e në dilemë në atë që na thërret ti!”');
INSERT INTO "ahmeti" VALUES(11,63,'11:63','“O populli im, më tregoni ju, nëse unë jam (i bazuar) në argument (të qartë) nga Zoti im, dhe Ai të më ketë dhënë mua mëshirë (gradën pejgamber) nga ana e Tij, kush do të më mbrojë mua nga All-llahu, nëse unë kundërshtoj Atë? Ju nuk më shtoni tjetër vetëm humbje.');
INSERT INTO "ahmeti" VALUES(11,64,'11:64','O populli im, kjo është devja e All-llahut, për ju është një argument. Lënie pra, të ushqehet në tokën e All-llahut e mos e prekni me ndonjë të keqe e të ju godas menjëherë dënimi!”');
INSERT INTO "ahmeti" VALUES(11,65,'11:65','E megjithatë, ata e therrën atë, e ai (Salihu) ju tha: “Përjetoni në vendin tuaj tri ditë, ky është caktim jo i rrejshëm!”');
INSERT INTO "ahmeti" VALUES(11,66,'11:66','E kur erdhi vendimi Ynë (për dënim), Ne e shpëtuam Salihun nga poshtërimi i asaj dite, e bashkë me të, edhe ata që besuan. Zoti yt është ai i fuqishmi, ngadhënjyesi.');
INSERT INTO "ahmeti" VALUES(11,67,'11:67','Ndërsa ata që bënë zullum, i kapi (në ditën e katërt) krisma dhe gëdhinë kufoma (të ngrira) në vendin e tyre.');
INSERT INTO "ahmeti" VALUES(11,68,'11:68','(gëdhinë) Sikur të mos kishin qenë fare në të. Kuptonie pra, (populli) Themud e mohoi Zotin e vet. Mallkimi qoftë kundër Themudit!');
INSERT INTO "ahmeti" VALUES(11,69,'11:69','Edhe Ibrahimit i patën ardhur të dërguarit (engjëjt) tanë me myzhde dhe i thanë: “Selam”. Ai u përgjegj: “Gjithnjë paçi selam” dhe nuk vonoi t’ju sjellë një viç të pjekur (të fërguar në gurë).');
INSERT INTO "ahmeti" VALUES(11,70,'11:70','Kur pa se duart e tyre nuk shtrihen kah ai (viç), iu dukën të pazakonshme dhe ndjeu prej tyre njëfarë frike. Ata i thanë: “Mos u frikëso, ne jemi të dërguar të populli i Lutit”.');
INSERT INTO "ahmeti" VALUES(11,71,'11:71','E gruaja (Sare) e tij (Ibrahimit) rrinte në këmbë dhe qeshi, e Ne e përgëzuam atë me Is-hakun, e pas Is-hakut Jakubin.');
INSERT INTO "ahmeti" VALUES(11,72,'11:72','Ajo tha: “E mjera unë, si do të lindë unë kr jam e vjetëruar, kurse ky burri im është i shtyer më moshë, vërtet kjo është gjë e çuditshme.');
INSERT INTO "ahmeti" VALUES(11,73,'11:73','Ata i thanë: “Si, mos po çuditesh me caktimin e Zotit?” Mëshira e All-llahut dhe bekimi i Tij qoftë me ju o familje e shtëpisë (së Ibrahimit)! Vërtet, Ai është meritues për falënderim është bamirës i madh.');
INSERT INTO "ahmeti" VALUES(11,74,'11:74','Pasi që Ibrahimit i kaloi frika dhe i erdhi myzhdeja, ai nisi dialog me të dërguarit tanë rreth popullit të Lutit..');
INSERT INTO "ahmeti" VALUES(11,75,'11:75','Vërtet, Ibrahimi ishte shumë i butë, shumë i dhembshëm dhe i kthyeshëm.');
INSERT INTO "ahmeti" VALUES(11,76,'11:76','(engjëjt i thanë) O Ibrahim lëre këtë (dialog), urdhëri i Zotit tënd tashmë ka zbritur, s’ka tjetër, ata do të përjetojnë dënim të pashmangshëm.');
INSERT INTO "ahmeti" VALUES(11,77,'11:77','E kur të dërguarit tanë i erdhën Lutit, ai u keqësua për ta dhe u ngushtua rëndë me ardhjen e tyre e tha: “Kjo është ditë e vështirë!”');
INSERT INTO "ahmeti" VALUES(11,78,'11:78','Dhe populli i tij, ë më parë bënin punë të turpshme, erdhi me ngtite ai (te Luti), e ai tha: “O populli im, qe këto bijat e mia (gratë e atij vendi), janë më të pastra për ju kinie frikë pra All-llahun e mos më turpëroni me mysafirët e mi, a nuk ka ndër ju ndonjë njeri të mençur (e të ju ndalojë nga e keqja)?”');
INSERT INTO "ahmeti" VALUES(11,79,'11:79','Ata thanë: “Po ti e ke të ditur se ne, nuk kurrfarë lakmie në bijat tua, ti e di me siguri se çka dëshirojmë ne!”');
INSERT INTO "ahmeti" VALUES(11,80,'11:80','Ai tha: “Ah, sikur të kisha fuqi kundër jush (t’ju zbrapsë), ose të kisha mbështetje në ndonjë përkrahje (të fisit) të fortë!” (do t’i mbroja mysafirët e mij).');
INSERT INTO "ahmeti" VALUES(11,81,'11:81','(engjëjt) Thanë: “O Lut, ne jemi të dërguarit e Zotit tënd, ata (populli yt) nuk kanë për t’u afruar te ti, e ti kah fundi i natës udhëto me familjen tënde dhe askushrej jush të mos vështrojë mbrapa (familha do të shpëtojë), pos gruas sate, ajo do të jetë e goditur me çka do të goditen edhe ata. Afati i tyre është mëngjesi, a nuk është mëngjesi afër?');
INSERT INTO "ahmeti" VALUES(11,82,'11:82','Kur erdhi urdhëri në, Ne përmbysëm të gjitha ato (fshatrat e tyre) duke kthyer çdo gjë nga lart poshtë, dhe mbi ta reshëm gurë të fortë pa ia nda.');
INSERT INTO "ahmeti" VALUES(11,83,'11:83','(gurë) Të shënuar te Zoti yt. Ai (vend) nuk është larg zullumqarëve (idhujtarëve kurejshitë).');
INSERT INTO "ahmeti" VALUES(11,84,'11:84','Edhe Medjenit (i dërguam) vëllain e tyre Shuajbin që ju tha: “O popull im, adhuronie All-llahun, ju nuk keni Zot tjetër pos Tij, mos matni as mos peshoni mangut, unë po shoh se jeni në gjendje të mirë jetësore, pra unë po frikësohem për ju nga dënimi që do t’ju përfshijë një ditë!”');
INSERT INTO "ahmeti" VALUES(11,85,'11:85','O populli im, veproni drejtë gjatë matjes dhe peshimit, e mos dëmtoni njerëzit në asgjë dhe mos shkaktoni rrëmujë në tokë!”');
INSERT INTO "ahmeti" VALUES(11,86,'11:86','Atë pjesë që ju lejoi All-llahu është shumë më e mirë për ju, nëse jeni besimtarë, e unë nuk jamë rojë juaj!”');
INSERT INTO "ahmeti" VALUES(11,87,'11:87','Ata thanë: “O shuajb, a namazi yt po të thotë të na urdhërosh që ta braktisim atë që adhuruan prindërit tanë, ose) po të urdhëron) për të punuar në pasurinë tonë ashtu si të dëshirojmë? Vërtet, ti qenke i butë e i mençur”. (Kjo ishte tallje e tyre).');
INSERT INTO "ahmeti" VALUES(11,88,'11:88','“O popull im, më thuani pra, nëse unë kam argumente të sigurta nga Zoti im dhe nga ana e Tij, Ai më furnizoi mua me të mira (si mund të mos ju udhëzoj në rrugë të drejtë?) Unë nuk dua t’ju kundërshtoj (duke punuar) për atë nga e cila po ju ndaloj, unë nuk dua tjetër vetëm të përmirësoj aq sa mundem, por këtë mund ta arrij vetëm me ndihmën e All-llahut, vetëm Atij iu kam mbështetur dhe vetëm te Ai jam i drejtuar!”');
INSERT INTO "ahmeti" VALUES(11,89,'11:89','O populli im, kundërshtimi ndaj meje të mos u shpie (në mosbesim) që të ju gjejë ajo që e gjeti popullin e Nuhut, populin e Hudit ose popullin e Salihut. E populli i Lutit nuk është larg prej jush.');
INSERT INTO "ahmeti" VALUES(11,90,'11:90','Kërkoni falje zotit tuaj she sinqerisht pendohuni ndaj Tij. Vërtet, Zoti im është mëshirues, shumë i dashur.');
INSERT INTO "ahmeti" VALUES(11,91,'11:91','Ata thanë: O Shuajb, ne nuk po e kuptojmë shumicën nga ajo që thua dhe ne të konsiderojmë ty të dobët në mesin tonë, dhe sikur të mos ishte ai grupi yt, ne do të gurëzonin ty, ngase ti nuk je i çmuar ndër ne”.');
INSERT INTO "ahmeti" VALUES(11,92,'11:92','Ai tha: “O populli im, a është më i çmuar te ju farefisi im se All-llahu, që e keni hedhur Atë pas shpine? Nuk ka dyshim, Zoti im i di të gjitha ato që veproni”.');
INSERT INTO "ahmeti" VALUES(11,93,'11:93','O populli im, veproni sa të mundeni, unë veproj, e më vonë do ta kuptoni se kush do të pësojë atë dënim që e poshtëron dhe kush është ai rrenacak. Pritni se edhe unë së bashku me ju jam duke pritur.');
INSERT INTO "ahmeti" VALUES(11,94,'11:94','E kur erdhi urdhëri Ynë, Ne me mëshirën tonë e shpëtuam Shuajbin dhe bashkë me të edhe ata që besuan, ndërsa zullumqarët i kapi britma e tmerri, duke gëdhirë në vendin e tyre kufoma të gjunjëzuara.');
INSERT INTO "ahmeti" VALUES(11,95,'11:95','(gëdhinë) Si të mos kishin ekzistuar fare aty. Qoftë i shkatërruar Medjeni sikurse ishte shkatërruar Themudi!');
INSERT INTO "ahmeti" VALUES(11,96,'11:96','Ne e patëm dërguar Musain me dispozitat tona dhe me mrekulli konkrete,');
INSERT INTO "ahmeti" VALUES(11,97,'11:97','Te faraoni dhe rrethi i tij, e ata iu bindën urdhërit të faraonit, po urdhëri i faraonit nuk ishte mençuri.');
INSERT INTO "ahmeti" VALUES(11,98,'11:98','Në ditën e kijametit ai (faraoni) i prinë popullit të vet dhe i fut në zjarr. Sa i shëmtuar është ai vend i ofruar.');
INSERT INTO "ahmeti" VALUES(11,99,'11:99','Edhe në këtë botë ata i përcolli mallkimi, e edhe në ditëne kijametit. Sa e keqe është ajo dhuratë e dhuruar.');
INSERT INTO "ahmeti" VALUES(11,100,'11:100','Këto janë disa nga lajmet e vendeve që po t’i rrëfejmë ty; disa prej tyre ekzistojnë ende, e disa janë shkatërruar.');
INSERT INTO "ahmeti" VALUES(11,101,'11:101','Ne nuk u bëmë padrejtë atyre, por ata vetvetes i bënë padrejtë. E kur erdhi urdhëri i Zotit tënd, atyre nuk u ndihmuan asgjë zotat e tyre, të cilëve luteshin pos All-llahut, dhe nuk fituan tjetër pos shkatërrim.');
INSERT INTO "ahmeti" VALUES(11,102,'11:102','Ja, kështu është ndëshkimi i Zotit tënd, kur dënon vendet që janë zullumqare. Vërtet, ndëshkimi i Tij është i dhëmbshëme i ashpër.');
INSERT INTO "ahmeti" VALUES(11,103,'11:103','Në këto (rrëfime) me të vërtetë ka një përvojë për atë që i frikësohet dënimit të botës tjetër. Ajo është ditë e tubimit të njerëzve dhe ajo është ditë dëshmuese.');
INSERT INTO "ahmeti" VALUES(11,104,'11:104','Dhe atë (ditë) nuk e shtyejmë vetëm deri në një afat që është i caktuar.');
INSERT INTO "ahmeti" VALUES(11,105,'11:105','E kur të vijë ajo ditë, askush nuk do të fasë, pos me lejen e Tij, e prej tyre (të tubuarëve), ka fatzi dhe fatbardhë.');
INSERT INTO "ahmeti" VALUES(11,106,'11:106','e për sa u përket atyre fatkëqinjve, ata janë në zjarr, aty ata kanë dihatje e kërhamzë të vështirë (në frymëmarrje).');
INSERT INTO "ahmeti" VALUES(11,107,'11:107','Aty janë përgjithmonë, sa të jenë qiejt dhe toka, përveç atë çka do Zoti yt, vërtet Zoti yt punon çka dëshiron.');
INSERT INTO "ahmeti" VALUES(11,108,'11:108','E për sa u përket atyre fatmirëve, ata jan në Xhennet përgjithmonë sa të jenë qiejt dhe toka, përveçatë çka do Zoti yt (All-llahu ju dhuron) shpërblim të pakëputur.');
INSERT INTO "ahmeti" VALUES(11,109,'11:109','Për adhurimin që bëjnë këta (idhujtarët), ti mos kij dyshim (se është i kotë). Nuk adhurojnë tjetër vetëm si adhuronin më parë prindërit e tyre, e Ne do t’u japim pjesën e tyre pa mangësi.');
INSERT INTO "ahmeti" VALUES(11,110,'11:110','Ne edhe Musait i patëm dhënë librin, e u bë përçarje rreth tij, e sikur të mos ishte vendim i Zotit tënd i përcaktuar më heret, me siguri do të kryhej (dënimi kundër tyre). Vërtet, ata (populli yt) kanë shprehur dyshim rreth tij.');
INSERT INTO "ahmeti" VALUES(11,111,'11:111','S’ka tjetër, vetëm se secilit prej tyre, Zoti yt do t’u përmbushë veprat e tyre, se Ai është i njohur hollësisht për atë që veprojnë.');
INSERT INTO "ahmeti" VALUES(11,112,'11:112','Ti (Muhammed) përqëndrohu vendosmërisht ashtu si je i urdhëruar, e bashkë me ty edhe ata që u penduan (prej idhujtarisë), e mos tejkaloni (kufijtë e caktuar), se më të vërtetë Ai është shikues i asaj që veproni.');
INSERT INTO "ahmeti" VALUES(11,113,'11:113','Dhe mos anoni kah ata që bënë zullum, e për atë shkak t’ju kapë zjarri, sepse përveç All-llahut nuk keni mbrojtës, e mbeteni të pa ndihmuar.');
INSERT INTO "ahmeti" VALUES(11,114,'11:114','Dhe fale namazin në dy skajet e ditës, e edhe në orët e aërta (me ditën) të natës. S’ka dyshim se vepra e mira i shlyejnë ato të këqiat. Kjo është një këshillë për ata që pranojnë këshillat.');
INSERT INTO "ahmeti" VALUES(11,115,'11:115','Dhe ti, jij i durueshëm, se All-llahu nuk ua humb shprblimin bëmirësve.');
INSERT INTO "ahmeti" VALUES(11,116,'11:116','E përse të mos ketë pasur nga breznitë që ishin para jush, të zotët e mendjes e të nderit që të ndalonin çrregullimin e kaosin në tokë, përveç një pakice, të cilët i shpëtuam (ngase frenuan nga të këqiat), e ata që ishin mizorë u dhanë pas kënaqësive si të shfrenar, duke vazhduar të jenë mëkatarë.');
INSERT INTO "ahmeti" VALUES(11,117,'11:117','E Zoti yt nuk është që të shkatërrojë mizorisht vendet, nëse banorët e tyre janë punëmirë.');
INSERT INTO "ahmeti" VALUES(11,118,'11:118','Sikur të dëshironte Zoti yt, do t’i bënte njerëzit të një feje (por nuk dëshiroi, ai e di pse). Ata vazhdimisht janë në kundërshtime (mes vete),');
INSERT INTO "ahmeti" VALUES(11,119,'11:119','Përveç atij që mëshiroi zoti yt. Po për këtë edhe i krijoi ata. Fjala e Zotit tënd: “Gjithsesi do ta bush Xhehennemin me të gjithë exhinët dhe njerëzit”, ka marrë fund (është plotësuar).');
INSERT INTO "ahmeti" VALUES(11,120,'11:120','Të gjitha këto që t’i rrëfyem ty nga lajmet e pejgamberëve, janë që të forcojnë zemrën tënde, dhe në to ka ardhur e vërteta e këshilla, si dhe përkujtime për besimtarët.');
INSERT INTO "ahmeti" VALUES(11,121,'11:121','E atyre që nuk besojnë thuaju: “Veproni sa të mundeni në atë të tuajën, edhe ne jemi duke vepruar”.');
INSERT INTO "ahmeti" VALUES(11,122,'11:122','Ju pritni (ç’do të na gjejë neve), edhe ne jemi duke pritur (se ç’do t’ju gjejë juve).');
INSERT INTO "ahmeti" VALUES(11,123,'11:123','Vetëm All-llahut i takon dija për fshehtësitë e qiejve dhe të tokës, çdo çështje i kthehet (në kompetencë) vetëm atij, pra adhuroje Atë, mbështetu te Ai, se Zoti yt, nuk është i panjohur për atë që veproni.');
INSERT INTO "ahmeti" VALUES(12,1,'12:1','Elif, Lamë, Ra. Këto (ajete që t’u zbritën) janë ajetet e librit të qartë.');
INSERT INTO "ahmeti" VALUES(12,2,'12:2','Ne e zbritëm atë Kur’an arabisht, ahtu që ta kuptoni.');
INSERT INTO "ahmeti" VALUES(12,3,'12:3','Ne me të shpallur të këtij Kur’ani po të rrëfejmë ty (Muhammed) me të bukurin rrëfim, edhe para tij ishe nga të painformuarit.');
INSERT INTO "ahmeti" VALUES(12,4,'12:4','(përkujto) Kur Jusufi, babait të vet i tha: “O baba im, pashë (në ëndërr) njëmbëdhjetë yje dhe diellin dhe hënën, i pashë duke më bërë mua sexhde!”');
INSERT INTO "ahmeti" VALUES(12,5,'12:5','Ai (Jakubi) tha: “O biri im, mos u trego ëndërrën tënde vëllezërve tu, se po të bëjnë ndonjë dredhi, s’ka dyshim, djalli për njeriun është armik i hapët”.');
INSERT INTO "ahmeti" VALUES(12,6,'12:6','Ja, keshtu Zoti yt të zgjedh ty, të mëson interpretimin e ëndërrave, plotëson dhuntinë e tij ndaj teje dhe familjes së Jakubit, sikrse pat plotësuar atë më parë ndaj prindërve tu, Ibrahimit dhe Is-hakut. Vërtet, Zoti yt është më i dijshmi, më i urti!”');
INSERT INTO "ahmeti" VALUES(12,7,'12:7','Njëmend, për ata që pyesin (interesohen) në ndodhinë e Jusufit dhe të vëllezërve të tij, pati fakte (mbi fuqunë e Zotit).');
INSERT INTO "ahmeti" VALUES(12,8,'12:8','Kur ata (vëllezërit) thanë: “Pasha Zotin, Jusufi dhe vëllai i tij, janë më të dashur te baba ynë se sa ne, ndërsa ne jemi grup (i fortë). Vërtet, babai ynë është në gabim sheshazi.');
INSERT INTO "ahmeti" VALUES(12,9,'12:9','Mbytnie Jusufin ose hidhnie diku larg në një vend e babai juaj t’ju dojë juve e pas asaj (pendohuni) bëheni njerëz të mirë.');
INSERT INTO "ahmeti" VALUES(12,10,'12:10','NJëri prej tyr etha: “Mos e bmytni Jusufin, e në oftë se jeni për të bërë diç, lëshonie atë në fund të bunarit, do ta marrë atë dikush prej dhëtarëve”.');
INSERT INTO "ahmeti" VALUES(12,11,'12:11','Ata thanë: “O baba ynë ç’ke që nuk na beson për Jusufin, e ne ia duam të mirën atij?');
INSERT INTO "ahmeti" VALUES(12,12,'12:12','Dërgoje nesër atë me ne, le të hajë pemë dhe le të luajë me siguri do ta ruajmë atë.');
INSERT INTO "ahmeti" VALUES(12,13,'12:13','Ai tha: “Unë mallëngjehem po të vijë me ju, e edhe kam frikë se do ta hajë ujku, kur mungon kjdesi juaj për të”.');
INSERT INTO "ahmeti" VALUES(12,14,'12:14','Ata thanë: “Për Zotin, nëse e ha ujku, e duke qenë në grup kaq i fortë, atëherë ne mos qofshim fare!”');
INSERT INTO "ahmeti" VALUES(12,15,'12:15','Pasi që ata shkuan me të dhe vendosën ta vënë në fund të bunarit, Ne i shpalëm: “Ti do t’i njoftosh ata për këtë vepër të tyre, e duke mos ditur ata (do të vijë koha të ju thuash për këtë vepër e ata nuk do të dinë se ti je Jusufi)”.');
INSERT INTO "ahmeti" VALUES(12,16,'12:16','E në mbrëmje erdhën duke qarë te baba i tyre.');
INSERT INTO "ahmeti" VALUES(12,17,'12:17','Thanë: “O baba në, ne shkuam të bëjmë gara, e Jusufin e lamë te teshat tona dhe atë e kishte ngrënë ujku. Ti nuk do ttë na besosh neve, edhe po të emi të vërtetë!”');
INSERT INTO "ahmeti" VALUES(12,18,'12:18','E erdhën (i sollën) e në këmishën e tj me gjak të rrejshëm. Ai tha: “Jo, (nuk e k ngrënë ujku), por epshet tuaja ua hijeshuan punën. Halli im është: durim i mirë. All-llahu është nga i cili kërkohet ndihmë për këtë që përshkruani ju”.');
INSERT INTO "ahmeti" VALUES(12,19,'12:19','Dhe atypari kaloi një gru udhëtarësh, e e dërguan ujëbartësin (për ujë), e ai e lëshoi kofën e vet (në bunar) dhe tha: “Myzhde! Qe një djalosh!” Ata e fshehën atë si mall tregtie. Po All-llahu e dinte shumë mirë seç’vepronin ata.');
INSERT INTO "ahmeti" VALUES(12,20,'12:20','Dhe e shitën atë (Jusufin (për një çmim të vogël, për disa dirhemë (masë argjendi) të numëruar, sepse në mesin e tyre pati asash që nuk lakmuan (për çmim më të lartë).');
INSERT INTO "ahmeti" VALUES(12,21,'12:21','E ai që e bleu nga Misiri (Egjipti) i tha gruas së ve: “Bëni pritje të kënaqshme, se është shpresë t’ia shohim hajrin ose ta adoptojmë për fëmijë!” Ja, kështu (sikuse e shpëtuan nga bunari), Ne i bëmë vend Jusufit në tokë (në Egjipt), e që t’ia mësojmë atij shpjegimin për disa ëndërra. All-llahu është mbizotërues i punës së vet, por shumica e njerëzve nuk e dinë (fshehtësinë e çështjeve).');
INSERT INTO "ahmeti" VALUES(12,22,'12:22','E kur e arriti pjekurinë e tij, Ne i dhamë pushtet e dituri. E kështu Ne i shpërblejmë punëmirët.');
INSERT INTO "ahmeti" VALUES(12,23,'12:23','E ajo, në shtëpinë ë së cilës ishte Jusufi, i bëri lajka atij dhe ia mbylli dyert e i tha: “Eja!” Ai (Jusufi) tha: “All-llahu më ruajtë, ai zotëriu im (e burri yt), më nderoi me vendosje të mirë (si mud t’i bëj hile në familje)”. S’ka dyshim se tradhtarët nuk kanë sukses.');
INSERT INTO "ahmeti" VALUES(12,24,'12:24','Ajo mësyni atë qëllimidht, e atij do t’i shkonte mendja ndaj saj, sikur të mos i prezentohej argumenti nga Zoti i tij. Ashtu (e bëmë të vendosur) që të larojmë nga ai të keqen dhe të ndytën. Vërtet, ai ishte nga robët tanë të zgjedhur.');
INSERT INTO "ahmeti" VALUES(12,25,'12:25','Dhe, që të dy ata u ngutën kah dera, e ajo ia grisi këmishën nga mbrapa dhe pranë dere ata të dy takuan burrin e saj, e ajo tha “çfarë mund të jetë ndëshkimi i atij që tenton të keqen në familjen tënde, përpos të burgoset, ose dënim të dhembshëm.?”');
INSERT INTO "ahmeti" VALUES(12,26,'12:26','Ai (Jusufi) tha: “Ajo m’u vërsul mua!” Një dëshmitar nga familja e saj gjykoi: nëse këmisha e tij është grisur përpara, ajo ka thënë të drejtën, kurse ai gënjen.');
INSERT INTO "ahmeti" VALUES(12,27,'12:27','E nëse këmisha e tij është grisur prapa, atëherë gënjen ajo, kurse ai është i drejtë.');
INSERT INTO "ahmeti" VALUES(12,28,'12:28','E kur e pa ai (burri i saj) këmishën e tij të grisur prapa, tha: “Kjo është dredhi juaja, vërtet dredhia juaj është e madhe!”');
INSERT INTO "ahmeti" VALUES(12,29,'12:29','“Jusuf, largohu nga kjo (mos ia përmend askujt)! E ti (gruas) kërko falje për mëkatin tënd, se vërtet ti qenke fajtore!”');
INSERT INTO "ahmeti" VALUES(12,30,'12:30','Një grup nga gratë e qytetit thanë: “Gruaja e zotëriut (e ministrit) i vërsulet marrëzisht shërbëtorit të vet, atë e ka kapluar në shpirt dashuria, e ne jemi të bindura se ajo është në humbje të hapët.');
INSERT INTO "ahmeti" VALUES(12,31,'12:31','E kur ajo dëgjoi për ato përshpëritjet e tyre, dërgoi t’i thërrasë ato, u përgaditi vend mbështetje dhe secilës prej tyre idha nga një thikë e tha: “Dilu para atyre!” Kur e panë atë, ato u tronditën dhe i prenë duart e tyre e thanë: “All-llahut i qofshim falë! Ky nuk është njeri, ky nuk është tjetër vetëm se ndonjë engjëll i lartë!”');
INSERT INTO "ahmeti" VALUES(12,32,'12:32','Ajo tha: “Qe, ky është ai për të cilin më qortuat mua, e unë e mësyna atë, po ai u ruajt fort. Po që se nuk e bën çka e urdhëroj, ai gjithqysh do të burgoset dhe do të nënçmohet”.');
INSERT INTO "ahmeti" VALUES(12,33,'12:33','Ai (Jusufi) tha: “O Zoti im, burgu është më idëshiruar për mua, se sa atë që më ofrojnë ato mua dhe nëse Ti nuk më largon prej meje dredhinë e tyre, unë mund të anoj te ato dhe të bëhem injorant.');
INSERT INTO "ahmeti" VALUES(12,34,'12:34','E Zoti i tij ia pranoi lutjen atij dhe largoi nga ai dredhinë e tyre. Vërtet, Ai është që dëgjon (lutjet) dhe di (gjendjen).');
INSERT INTO "ahmeti" VALUES(12,35,'12:35','Mandej, pasi që vertetuan argumentet (e pastërtisë së Jusufit), atyre (ministrit dhe rrethit të tij) iu paraqit ideja që ata për një kohë ta burgosin.');
INSERT INTO "ahmeti" VALUES(12,36,'12:36','Së bashku me të hynë në burg dy të rinj. Njëri prej atyre dyve tha: “Unë ëndërrova veten se po shtrydhi (rrush për) verë”. E tjetri tha “Unë ëndërrova se po bart mbi kokë një bukë nga e cila hanin shpezët”. Na trego komentin e saj, se ne të konsiderojmë nga komentuesit e mirë (të ëndërrave).');
INSERT INTO "ahmeti" VALUES(12,37,'12:37','Tha: “nuk ju vjen juve ndonjë ushqim me të cilin ushqeheni, e që unë të mos e dijë, t’ju përshkruaj atë para se t’ju vijë. Kjo është nga ajo çka më mësoi Zoti im (nuk është fall e as prognozë). Unë kam braktisur fenë e një populli që nuk e beson All-llahun as botën tjetër, e ata ishin jobesimtarë”.');
INSERT INTO "ahmeti" VALUES(12,38,'12:38','Unë ndoqa fenë e prindërve të mij: Ibrahimit, Is-hakut, Jakubit. Neve nuk na takoi t’i përshkruajmë kurrë një send shok All-llahut. Ky (besim i drejtë) është dhuratë e madhe nga All-llahu ndaj nesh dhe ndaj njerëzve, por shumica e njerëzve nuk ia dinë vlerën.');
INSERT INTO "ahmeti" VALUES(12,39,'12:39','O ju dy shokët e mij të burgut, a ësshtë më mirë të adhurohen zota të ndryshëm ose All-llahu, i vetmi ngadhënjimtar?”');
INSERT INTO "ahmeti" VALUES(12,40,'12:40','Ata që adhuroni përveq All-llahut, nuk janë tjetër vetëm se emra që i pagëzuat ju dhe prindërit tuaj. All-llahu nuk shpalli ndonjë fakt për ta, vendimi (në çështjen e adhurimit) nuk i takon kujt, pos All-llahut, e Ai urdhëroi të mos adhuroni tjetër vetëm Atë. Kjo është feja e drejtë, por shumica e njerëzve nuk e dinë.');
INSERT INTO "ahmeti" VALUES(12,41,'12:41','O shokët e mij të burgut! Njëri prej ju dyve do t’i japë të verë zotëriut të vet, e tjetri do të varet, kurse shpezët do të hanë nga koka e tij. çështja për të cilën kërkuat shpjegim, ka marrë fund (kështu).');
INSERT INTO "ahmeti" VALUES(12,42,'12:42','E atij për të cilin besonte se është i shpëtuar i tha: “Më përkujto mua te zotëriu yt”. Po djali e vuri në harresë ta përkujtonte te zotëriu i tij, e për këtë mbeti në burg disa vjet.');
INSERT INTO "ahmeti" VALUES(12,43,'12:43','E (kur u afrua lirimi i Jusufit) mbreti tha: “Kam parë ëndërr shtatë lopë të majme dhe shtatë lopë të dobëta që i hanin ato (të dobëtat, i gëlltitën ato të majmet) dhe shtatë kallinj të gjelbër (që kishin lidhur frytin) e (shtatë) të tjerë të tharë. O ju pari, nëse dini të interpretoni ëndrrën, më tregoni për ëndërrën time!”');
INSERT INTO "ahmeti" VALUES(12,44,'12:44','Ata (paria) thanë: “Ëndërra të përziera, e ne nuk dimë komentin e ëndërrave të tilla”.');
INSERT INTO "ahmeti" VALUES(12,45,'12:45','E ai prej atyre dyve që shpëtoi (që i pat thënë Jusufi ta prmendë) e përkujtoi pas një periudhe e tha: “Unë ju tregoj kuptimin e saj. Pra më lejoni të shkoj (te Jusufi)”.');
INSERT INTO "ahmeti" VALUES(12,46,'12:46','(E dërguan te burgu, e ai tha) O Jusuf, o ti i drejti, na shpjego për shtatë lopë të dobëta që i hanin shtatë lopë të majme, dhe shtatë kallinj të gjelbër eshtatë të thatë, e ndoshta po kthehem te njerëzit (me përgjigjje), ashtu që edhe ata të kuptojnë (vlerën dhe dijen tënde)!');
INSERT INTO "ahmeti" VALUES(12,47,'12:47','Ai (Jusufi) tha: “Mbillni shtatë vjet vazhdimisht, e çka keni korrur lënie në kallinj, pëveç një pakice nga e cila do të hani.');
INSERT INTO "ahmeti" VALUES(12,48,'12:48','Pastaj, do të vijnë shtatë (vjet) të vështira (me skamje) që do të hanë atë që keni ruajtur për to, përveç një pakice nga ajo që do ta ruani (në depo për farë).');
INSERT INTO "ahmeti" VALUES(12,49,'12:49','Mandej, pas asaj vjen një vit (i begatshëm) në të cilin njerëzve u bie shi dhe në të cilin do të kenë të vjela (vit i begatshëm në përgjthësi)”.');
INSERT INTO "ahmeti" VALUES(12,50,'12:50','E mbreti tha: “Më sillni mua atë (Jusufin)!” Dhe kur i shkoi atij (Jusufi) i dërguari (i mbretit), ai itha: “Kthehu te zotëriu yt dhe pyete atë: ç’ëshë puna e atyre, që i prenë duart e tyre? Vërtet, Zoti im e di shumë mirë dredhinë e tyre”.');
INSERT INTO "ahmeti" VALUES(12,51,'12:51','Ai (mbreti) tha: “çka ishte puna juaj kur ju vetë e dëshiruat (iu vërsulët) Jusufin?” Ato thanë: “Zoti na ruajt, ne nuk dijmë ndaj tij asnjë të keqe!” Gruaja e ministrit tha: “Tash doli në shesh e vërteta, e unë iu vërsula atij me dashuri, e ai është nga të drejtit!”');
INSERT INTO "ahmeti" VALUES(12,52,'12:52','Këtë (e bëra thotë Jusufi) për ta ditur ai (ministri) se unë nuk e tradhtova tinëzisht dhe së vërtet All-llahu nuk realizon dredhinë e tradhtarëve.');
INSERT INTO "ahmeti" VALUES(12,53,'12:53','Unë nuk e shfajsoj veten time, pse epshi është shumë nxitës për të keqen, përveq atë që ka mëshiruar Zoti im, se Zoti im është që fal e mëshiron shumë.');
INSERT INTO "ahmeti" VALUES(12,54,'12:54','Mbreti tha: “Me sillni muaatë, theshtë ta veçoj për veten time!” (ia sollën Jusufin) E pasi që bisedoi me të tha: “Tash ti ke pozitë dhe je i besueshëm”.');
INSERT INTO "ahmeti" VALUES(12,55,'12:55','Ai (Jusufi) tha: “Më cakto mua përggjegjës të depove të vendit, unë jam besnik i dijshëm”.');
INSERT INTO "ahmeti" VALUES(12,56,'12:56','Dhe kështu Ne Jusufit i dhamë pozitë në vend (në Egjipt) zinte vend aty ku dëshironte. Ne e pajisim me të mira tona atë që duam, e Ne nuk ua humbim shpërblimin punëmirëve.');
INSERT INTO "ahmeti" VALUES(12,57,'12:57','Po shpërblimi i botës tjetër, gjithsesi është shumë më i mirë për ata që besuan dhe ata që u frikësuan dhe u ruajtën.');
INSERT INTO "ahmeti" VALUES(12,58,'12:58','Dhe (pas një kohe) vijnë vëllezërit e Jusufit e hynë te ai, e ai i njohti, ndërsa ata nuk e njihnin.');
INSERT INTO "ahmeti" VALUES(12,59,'12:59','E pasi i furnizoi me të gjithë artikujt e nevojshëm (me barë drithi për të cilin kishin ardhur, etj.), u tha: “Më sillni një vëlla tuajin që keni nga baba, a nuk e vëreni se unë vërtet u plotësoj barrën (peshojën) dhe se unë jam mikëpritësi më mirë!”');
INSERT INTO "ahmeti" VALUES(12,60,'12:60','E nëse nuk ma sillni atë (vëllaun), Ju nuk do të merrni te unë barë drithi (për ushqim), e as mos iu afroni (vendit tim).');
INSERT INTO "ahmeti" VALUES(12,61,'12:61','Ata thanë: “Ne do të përpiqemi për të (për ta marrë) te babai i tij dhe gjithsesi ne do të bëjmë atë!”');
INSERT INTO "ahmeti" VALUES(12,62,'12:62','Ai (Jusufi) djelmoshave të tij shërbëtorë të tij u tha: “Vënie mallin e tyre (barrën që kishin sjellë për të blerë drith) në barrët e tyre, ashtu që kur të këthehen te familja e tyre (t’i hapin barrët) ta njohin, e ndoshta do të kthehen sërish te na”.');
INSERT INTO "ahmeti" VALUES(12,63,'12:63','E kur u kthye te babai i tyre thanë: “O baba ynë, po na ndalohet barra (drithi), lejonie të vijë me ne vëllau ynë (Binjamini) që të marrim ushqim, e ne do të ruajmë atë (nga çdo e keqe)”.');
INSERT INTO "ahmeti" VALUES(12,64,'12:64','Ai (Jakubi) tha: “A t’u besoj për këtë sikurse u besova më parë për vëllain e tij (Jusufin), po All-llahu është mbrojtësi më i mirë dhe Ai i mëshiruesve”.');
INSERT INTO "ahmeti" VALUES(12,65,'12:65','E kur i hapën barrët e tyre, e gjetën mallin e tyre që iu ishte kthyer dhe thanë: “O ati ynë, çka duam më tepër, qe ky është malli ynë që na u kthye, do të furnizojmë me drith familjen tonë, do ta ruajmë vëllain tonë dhe do ta shtojmë një barrë deveje, e kjo është sasi e lehtë (për sundimtarin)”.');
INSERT INTO "ahmeti" VALUES(12,66,'12:66','Ai (Jakubi) tha: “Unë kurrsesi nuk e dërgoj atë me ju, derisa të më jepni besën (e fortë) në All-llahun, se gjithsesi do të ma ktheni atë mua, përveç nëse u diktojnë rrethanat e nuk mundeni”. E kur ata ia dhanë besën (u betuan), ai tha: “All-llahu është garantues për këtë që e thamë”.');
INSERT INTO "ahmeti" VALUES(12,67,'12:67','Po ai (Jakubi) tha: “O bijtë e mij, mos hyni (në Egjipt) për një derë, po hyni nëpër dyer të ndryshme. Unë nuk mund të largoj prej jush asnjë send nga caktimi i All-llahut, vendimi nuk është i tjetërkujt vetëm i All-llahut, vetëm Atij iu kam mbështetur dhe vetëm Atij le t’i mbështeten ata që besuan.');
INSERT INTO "ahmeti" VALUES(12,68,'12:68','Dhe ata hynë ashtu si porositi babai i tyre, po ajo nuk ishte gjë që do t’u ndihmonte asgjë nga caktimi i All-llahut, përveç një dëshirë të Jakubit që e kishte në vete dhe kreu. Po ai (Jakubi) ishte i dijshëm për te, ngase Ne e kemi mësuar për atë, por shumica e njerëzve nuk e dinë.');
INSERT INTO "ahmeti" VALUES(12,69,'12:69','E kur hynë te Jusufi, aii (Jusufi) e afroi pranë vete vëllain e vet (Binjaminin) dhe tha: “Unë jam vëllai yt, e ti mos u pikëllo për atë që ata vepruan!”');
INSERT INTO "ahmeti" VALUES(12,70,'12:70','E kur i pajisi ata me pajimet e tyre e vuri tasin (gotën) në barrën e vëllait të vet, e pastaj një thirës thirri: “O ju devexhinj, ju jeni vjedhës!”');
INSERT INTO "ahmeti" VALUES(12,71,'12:71','U kthyen (devexhinjtë) dhe thanë: “çka keni humbur?”');
INSERT INTO "ahmeti" VALUES(12,72,'12:72','(shërbëtorët e sundimtarit) Thanë: “Kemi humbur tasin e sunduesit, e kush e sjell atë (tasin), ka (shpërblim) një barrë deveje (drith). Unë vetë jam për këtë garantues!”');
INSERT INTO "ahmeti" VALUES(12,73,'12:73','Ata (vëllezërit e Jusufit) thanë: “Pasha All-llahun, ju e dini se ne nuk kemi ardhur për të bërë shkatërrime në tokë dhe nuk jemi vjedhës!”');
INSERT INTO "ahmeti" VALUES(12,74,'12:74','(shërbëtorët) Thanë: “Nëse jeni gënjeshtarë, çfarë duhet të jetë ndëshkimi i atij (që ka vjedhur)?”');
INSERT INTO "ahmeti" VALUES(12,75,'12:75','Vëllezërit thanë: “Dënimi i tij është, vetë ai (të robërohet) në barrën e të cilit gjendet. Kështu ne i dënojmë të padrejtit!”');
INSERT INTO "ahmeti" VALUES(12,76,'12:76','Dhe filloi (kontrollimi) në barrët e tyre, përpara barrës së vëllait të vet, e pastaj e nxorri atë (tasin) nga barra e vëllait të vet. Kështu Ne e mësuam të planifikojë Jusufi. Sipas ligjit të mbretit, atij (Jusufit) nuk i takonte ta ndalte (peng) vëllain e vet, përveç kur është dëshira e All-llahut. Atë që duam Ne e lartësojmë shumë në dituri, e mbi secilin të dijshëm ka edhe më të dijhëm.');
INSERT INTO "ahmeti" VALUES(12,77,'12:77','(vëllezërit) Thanë: “Në pastë vjedhur ky, ka pasë vjedhur më parë një vëlla i tij. Jusufi këtë e mbajti fshehtë në vete e nuk ua zbuloi atyre dhe tha: “Ju jeni në pozitë më të keqe (këtë e tha me vetvete e jo atyre), All-llahu e di më mirë për atë që trilloni.');
INSERT INTO "ahmeti" VALUES(12,78,'12:78','Ata thanë: “O zotëri, ai ka një babë shumë të vjetër, pra, në vend të tij na merr njërin prej nesh. Bënë këtë të mirë se ti je prej atyre që bëjnë mirë!');
INSERT INTO "ahmeti" VALUES(12,79,'12:79','Ai (Jusufi) tha: “All-llahu na ruajt, të marrim tjetër pos atij te i cili e kemi gjetur teshën tonë, ne atëherë do të jemi të padrejtë!”');
INSERT INTO "ahmeti" VALUES(12,80,'12:80','E kur e humbën shpresën prej tij, u veçuan për t’u konsultuar. Më i maddhi i tyre tha: “A nuk e dini që babi juaj ka marrë prej jush besën në All-llahun, e më parë çafrë padrejtësie keni bërë ndaj Jusufit?” Unë pra nuk ndahem kurrë prej kësaj toke (të Egjiptit) deri që të më lejojë babai im, ose të vendosë All-llahu (mirë) për mua, e Ai është gjykatës më i drejtë.');
INSERT INTO "ahmeti" VALUES(12,81,'12:81','Ju kthehuni te babai juaj dhe thuani: “O at ynë, djali yt vodhi, ne dëshmojmë vetëm për atë që dijmë, e për të fshehtën ne nuk ishim roje!”');
INSERT INTO "ahmeti" VALUES(12,82,'12:82','Ti dërgo e pyeti (banorët në) qytetin ku ishim ne, po ashtu edhe (ata me) karavanin me të cilin kemi ardhur. Ne jemi të drejtë (ç’të themi).');
INSERT INTO "ahmeti" VALUES(12,83,'12:83','Ai (Jakubi) tha: “Jo, çështjen ua lehtësoi epshi juaj, (mua nuk më ka mbetur tjetër) Durimi i mirë, shpresë që All-llahu do të m’i sjellë të gjithë; Ai është më i dijshmi, më i urti!”');
INSERT INTO "ahmeti" VALUES(12,84,'12:84','Dhe, ua ktheu shpinën e tha: “O dëshprim i imi për Jusufin, hde nga pikëllimi iu zbardhën të dy sytë, po i përmbajtur (nga pikëllimi).');
INSERT INTO "ahmeti" VALUES(12,85,'12:85','Ata thanë: “Për All-llahun ti vazhdimisht e përkujton Jusufin derisa do të sëmuresh rëndë, e të shkatërrohesh”.');
INSERT INTO "ahmeti" VALUES(12,86,'12:86','Ai (Jakubi) tha: “Unë hidhërimin tim dhe pikëllimin tim, ia parashtroj All-llahut, e unë di për All-llahun atë që ju nuk e dini”.');
INSERT INTO "ahmeti" VALUES(12,87,'12:87','(Jakubi u tha të bijëve të vet) O bijtë e mi, shkoni dhe hulumtoni (në Egjipt) për Jusufin dhe vëllain e tij, e mos e humbni shpresën nga mëshira e All-llahit, pse vetëm populli jobesimtarë e humb shpresën në All-llahun.');
INSERT INTO "ahmeti" VALUES(12,88,'12:88','(shkuan) E kur hynë te ai i thanë: “O ti zotëri, neve dhe familjen tonë na ka goditur skamje e vështirë, e kemi ardhur me një mall të vjetër, e ti pra na mbush masën (barrën) dhe na dhuro, All-llahu i shpërblen ata që dhurojnë”.');
INSERT INTO "ahmeti" VALUES(12,89,'12:89','Ai (Jusufi) tha: “A e dini çka keni bërë me Jusufin dhe vëlain e tij, kur ishit injorantë?”');
INSERT INTO "ahmeti" VALUES(12,90,'12:90','Ata thanë: “A ti je vetë Jusufi?” Ai tha: “Unë jam Jusufi, e ky është vëllai im, All-llahu na dhuroi shpëtim, pse ai që ruhet dhe bën durim, s’ka dyshim All-llahu nuk e humb shpërblimin e punëmirëve”.');
INSERT INTO "ahmeti" VALUES(12,91,'12:91','Ata thanë: “Pasha All-llahun, është e vërtetë se All-llahu të ka lartësuar mbi ne, kurse ne vërtet ishim fajtorë!”');
INSERT INTO "ahmeti" VALUES(12,92,'12:92','Ai tha: “Sot nuk ka qortim kundër jush, All-llahu ju faltë juve; Ai është më mëshirues i mëshiruesve!');
INSERT INTO "ahmeti" VALUES(12,93,'12:93','Ju shkoni me këtë këmishën time, dhe hidhnia atë babit tim në fytyrë, e atij do t’i kthehen të pamët, e pastaj ejani te unë me tërë familjen time”.');
INSERT INTO "ahmeti" VALUES(12,94,'12:94','(u nisën) Dhe posa u ndanë devet (prej qytetit), i ati i tyre tha: “Po e ndiej erën e Jusufit, mos më konsideroni të matufosur.” (këtë ia tha familjes së vet në Palestinë).');
INSERT INTO "ahmeti" VALUES(12,95,'12:95','Ata (që ishin prezent) thanë: “Për All-llahun ti vërtet qëmoti në humbje”.');
INSERT INTO "ahmeti" VALUES(12,96,'12:96','E kur erdhi myzhdexhiu, ia vuri atë (këmishën) mbi fytyrën e tij, atij iu kthye të pamët dhe tha: “A nuk ju thashë se unë di nga All-llahu çka ju nuk dini?”');
INSERT INTO "ahmeti" VALUES(12,97,'12:97','Ata thanë: “O baba ynë, lutu për neve të na falen mëkatet tona, se me të vërtetë kemi qenë fajtorë!”');
INSERT INTO "ahmeti" VALUES(12,98,'12:98','“Më vonë do të kërkoj Zotit tim faljen tuaj”, tha ai (Jakubi), se vërtet, Ai është që falë, është mëshirues.');
INSERT INTO "ahmeti" VALUES(12,99,'12:99','(E morën familjen dhe shkuan) e kur hynë te Jusufi, ai i afroi ngat vete të dy prindërit r tij dhe tha: “Me dëshirën e All-llahut hyni në Egjipt të sigury!”');
INSERT INTO "ahmeti" VALUES(12,100,'12:100','Dhe ai të dy prindërit e vet i mori lart në fron (i uli pranë vete), e ata iu përulën atij, e ai tha: “O ati im, kjo është domethënia e ëndërreës sime të mëparshme. Zoti im këtë tanimë e bëri realitet, e mua më dhuroi mirësi kur më nxorri prej burgut, e juve u solli ngs fshati pasi djalli pat futur përçarje ndërmjet meje dhe vëllezërve të mij. Zoti, im me mjeshtri e butësi realizon dëshirën e vet për çka don, vërtet Ai është më i dijshmi, i urti.');
INSERT INTO "ahmeti" VALUES(12,101,'12:101','(Pasi iu plotësua dëshira Jusufit tha) Zoti im, Ti më ke dhënë mua pushtet, më mësove mua komentin e ëndërrave; o Krijues i qiejve e i tokës, Ti je kujdestar imi në dynja e në Ahiret, më bën të vdes mysliman dhe më bashko me të mirët!”');
INSERT INTO "ahmeti" VALUES(12,102,'12:102','Këto janë nga ato lajme të panjohura, e Ne po t’i shpallim ty (Muhammed), se ti nuk ke qenë pranë tyre kur ata vendosën çështjen e tyre (hudhjen e Jusufit në bunar etj.) dhe kur bënin dredhi.');
INSERT INTO "ahmeti" VALUES(12,103,'12:103','Po ti edhe pse lakmon (për besimin e tyre), shumica e njerëzve nuk do të besojnë.');
INSERT INTO "ahmeti" VALUES(12,104,'12:104','Megjithatë që ti nuk kërkon prej tyre ndonjë për të (për këshillë e udhëzim). Ai (Kur’ani) nuk është tjetër vetëm se këshillë për mbarë njerëzit.');
INSERT INTO "ahmeti" VALUES(12,105,'12:105','Sa e sa argumente ka në qiej e në tokë, të cilët i shohin, por ata nuk i vështrojnë fare.');
INSERT INTO "ahmeti" VALUES(12,106,'12:106','Dhe shumica e tyre nuk beson ndryshe All-llahun, vetëm se duke i shoqëruar (zota të tjerë).');
INSERT INTO "ahmeti" VALUES(12,107,'12:107','A mos janë siguruar ata prej dënimit që mund t’u vijë nga All-llahu e t’i mbulojë, ose befas duke mos hetuar fare t’u vijë katastrofa e përgjithshme.');
INSERT INTO "ahmeti" VALUES(12,108,'12:108','Thuaj: “Kjo është rruga ime, e vënë në fakte të qarta, e që unë dhe ai që vjen pas meje. Larg të metave është All-llahu, e unë nuk jam nga idhujtarët.”');
INSERT INTO "ahmeti" VALUES(12,109,'12:109','Ne nuk dërguam para teje vetëm se burra nga banuesit e qyteteve, të cilët i pajisëm me shpallje. A nuk udhëtuan ata nëpër tokë e të shihnin se si ishte përfundimi i atyre që ishin më parë? E s’ka dyshim se jeta e botës së ardhshme është shumë më e dobishme për ata, të cilët janë ruajtur; a nuk mendoni?');
INSERT INTO "ahmeti" VALUES(12,110,'12:110','(nuk dërguam tjetër vetëm burra e ndihma u vonua) Derisa të dërguarit gati e humbnin shpresën (se do t’i besojë populli), dhe menduan se u përgënjeshtruan (pse ende nuk i kapi dëninmi), e atëherë u erdhi atyre (tëdërguarve) ndihma Jonë. Ne i shpëtuan atë që dashtëm. E dënimi Ynë kundër popullit idhujtarë nuk zbrapset.');
INSERT INTO "ahmeti" VALUES(12,111,'12:111','Ne tregimet e tyre (të dërguarve dhe të Jusufit me vëllezër) pati mësime e përvojë për të zotët e mendjes. Ai (Kur’ani) nuk është bisedë e trilluar, por vërtetues i asaj që ishte më parë (librave të shenjta) dhe sqarues i çdo sendi, e edhe udhërrëfyes e mëshirë për një popull që beson.');
INSERT INTO "ahmeti" VALUES(13,1,'13:1','Elif, Lamë, Mimë, Ra. Këto ajete të kësaj kaptine dhe e tërë ajo që t’u shpall ty nga Zoti yt, janë realitet, por shumica e njerëzve nuk besojnë.');
INSERT INTO "ahmeti" VALUES(13,2,'13:2','All-llahu është Ai që ngriti qiejt pa ndonjë shtyllë. Ju i shihni ato. Ai u vendos mbi Arsh dhe nënshtroi diellin e hënën që çdo njëri udhëton deri në një afat të caktuar; Ai rregullon çështjen (e gjithësisë), sqaron argumentet, që të jeni të bindur për takimin (pas ringjalljes) me Zotin tuaj.');
INSERT INTO "ahmeti" VALUES(13,3,'13:3','Dhe ai është, i cili tokën e shtriu, dhe në të krijoi kodra e lumenj dhe prej secilit frut bëri dy lloj (çift), bëri që nata ta mbulojë ditën. Vërtet, në gjithë këtë ka fakte për njerëzit që thellë mendojnë.');
INSERT INTO "ahmeti" VALUES(13,4,'13:4','E në tokë, copa sipërfaqesh, te ngjitura njëra me tjetrën, kopshte të hardhisë, të bmjella (të llojllojshme) dhe hurma (bimë këto) që janë të degëzuara në shumë trupa dhe jo të degëzuara (nga një filiz) e të gjitha jiten nga një ujë, dhe Ne shijen në ngrënie të frutave të tyre e kemi dalluar njërën prej tjetrës. Edhe kjo dëshmon për argumente për njerëzit e mençur.');
INSERT INTO "ahmeti" VALUES(13,5,'13:5','E nëse ti (Muhammed) çuditesh (përse të përgënjeshtrojnë), është edhe më e çuditshme thënia e tyre: “A do të kemi krijimtë ri pasi të jemi bërë dhe?” këta janë ata që mohuan Zotin e tyre, ata që do të ndëshkohen me pranga në qafë. Pra këta janë banues të zjarrit ku do të qëndrojnë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(13,6,'13:6','E ata prej teje këkojnë ngutje zbatimit të së keqes para të së mirës edhe pse para tyre pati aso dënimesh (por nuk kanë marrë mësim). S’ka dyshim se Zoti yt u fal njerzve mëkatet e tyre, por s’ka dyshim se Zoti yt është edhe ndëshkues i rreptë.');
INSERT INTO "ahmeti" VALUES(13,7,'13:7','Ata, të cilët nuk besuan thonë: “Pse të mos i ketë zbritur atij (Muhammedit) ndonjë argument (mrekulli praktike) nga Zoti i tij?” Ti je vetëm qortues. çdo popull pati udhëzues (pejgamber).');
INSERT INTO "ahmeti" VALUES(13,8,'13:8','All-llahu e di se ç’bart çdo grua, dhe (e di) çka pakësojnë e çka shtojnë mitrat. te Ai është çdosend me masë të caktuar.');
INSERT INTO "ahmeti" VALUES(13,9,'13:9','Ai është që di të fshehtëndhe të dukshmen, Ai është i madhëruari e i latrësuari.');
INSERT INTO "ahmeti" VALUES(13,10,'13:10','Për të është njësoj (në dijen e Tij) si ai që fsheh prej jush thënien, si ai që e shpreh haptazi, si ai që vepron fshehtas natën (në errësirë të natës) dhe si ai që vepron haptazi ditën.');
INSERT INTO "ahmeti" VALUES(13,11,'13:11','Ai (njeri) ka përcjellës një pas një, para tij dhe prapa tij, që me urdhërin e All-llahut e ruajnë atë. All-llahu nuk e prish gjendjen e një populli (nuk ua largon të mirat) për derisa ata ta ndryshojnë veten e tyre. Po, kur All-llahu vendos ta ndëshkojë një popull, atë nuk ka kush që ta zbrapsë. Ata, (njerëzit) nuk kanë mbrojtës tjetër pos Tij.');
INSERT INTO "ahmeti" VALUES(13,12,'13:12','Ai është që ju shfaq vetëtimën trishtuese (nga ndonjë fatkeqësi), por edhe shpresëdhënëse (për shi), dhe formulon re të dendura.');
INSERT INTO "ahmeti" VALUES(13,13,'13:13','Ndërkaq, murmurima madhëron (Zoti një) me falënderimin (që i takon) e Tij, (e madhërojnë) edhe engjëjt nga droja ndaj Tij. E Ai dërgon rrufetë dhe me ato godt kë të dojë. Ndërsa idhujtarët polemizojnë rreth All-llahut, e ai është ndëshkues i rreptë.');
INSERT INTO "ahmeti" VALUES(13,14,'13:14','Lutje e vërtetë është vetëm ajo drejtuar Atij, e ata që u luten të tjerë pos Tij, ata nuk u përgjigjen atyre asgjë tjetër (gjendja e tyre është) vetëm si e aij që shtrin duart (prej së largu) kah uji, që ai (uji) nuk do t’i arrijë (sepse uji është send i vdekur e nuk e kupton për etjen e tij). E lutja e jobesimtarëve (drejtuar pos Zotit) nuk është tjetër vetëm se dështim (kotësi).');
INSERT INTO "ahmeti" VALUES(13,15,'13:15','Gjithçka ka në qiej e në tokë i bën sexdhe vetëm All-llahut me dëshirë ose me dhunë, (i bëjnë sexhde) ëshë hijet e tyre në mëngjes e mbrëmje.');
INSERT INTO "ahmeti" VALUES(13,16,'13:16','Thuaj: “Kush është Zoti (Krijuesi) i qiejve e i tokës?” Thuaj: “All-llahu!” Thuaj: “A keni pranuar pos tij zota që nuk kamë mundësi t’i sjellin as dobi as dëm vetvetes?” Thuaj: “A është i barabartë i verbëri me atë që sheh, ose, a janë të njëjta errësirat me dritën?” A mos i bënë All-llahut shokë (idhuj) e edhe zota krijuam si krijoi Ai dhe atyre u është bërë i ngjashëm (i paqartë) krijimi (e nuk po dinë se ciliështë krijim i Zotit, e cili i idhujve)? Thuaj: “All-llahu është krijues i çdo sendi, Ai është i vetmi ngadhënjyes!”');
INSERT INTO "ahmeti" VALUES(13,17,'13:17','Ai e lëshon ujin (shiun) nga qielli e sipas madhësisë së tyre rrjedhin përrocka, kurse vele bart mbi vete shkumë të fryer lart. Shkuma e ngjashme është edhe ajo kur shkrihet (metal) në zjarr për të përfituar ndonjë stoli ose ndonjë mjet. Kështu, All-llahu sqaron shembullin e të së vërtetës dhe të pavërtetës. Për sa i përket shkumës (së ujit apo të metaleve), ajo hidhet si mbeturinë, ndërsa ajo që u sjell dobi njerëzve, ajo mbetet në tokë. Kësisoj All-llahu i sjell shembujt.');
INSERT INTO "ahmeti" VALUES(13,18,'13:18','Atyre që i janë përgjegjur Zotit të tyre u takon shpërblimi më i mirë, ndërsa ata që nuk iu përgjigjen Atij, ata do të japin si kompensim, sikur të ishte e tyre aqë shumë sa e gjithë pasuria tokësore edhe një herë aq. Ata kanë llogarinë më të rëndë, vendstrehimi i tyre është Xhehennemi që është shtrat i keq.');
INSERT INTO "ahmeti" VALUES(13,19,'13:19','A është i njejtë ai që e di se ajo që t’u shpall ty nga Zoti yt është e vërtetë, si ai që është i verbër? Vetëm njerëzit e arsyeshëm që kanë mendje të shëndoshë e kuptojnë të vërtetën.');
INSERT INTO "ahmeti" VALUES(13,20,'13:20','Ata janë, të cilët e zbatojnë besën e dhënë All-llahut dhe nuk e thyejnë zotimin.');
INSERT INTO "ahmeti" VALUES(13,21,'13:21','Dhe ata që e mbajnë lidhjen për të cilën All-llahu ka urdhëruar të mbahet, që frikësohen nga Zoti i tyre dhe nga llogaria e rëndë e përgjegjësisë.');
INSERT INTO "ahmeti" VALUES(13,22,'13:22','Edhe ata që patën durim (ndaj të këqiave) për të fituar kënaqësinë e Zotit të tyre dhe që e falën namazin, dhe nga ajo me çka Ne i furnizuam ata kanë dhënë fshehtas e haptas dhe me të mirë e largojnë të keqen. Të tillët kanë përfundim më të mirë.');
INSERT INTO "ahmeti" VALUES(13,23,'13:23','(përfundimi i mirë është) Xhennete të Adnit (vende të përjetshme) ku do të hyjnë ata, edhe prindërit, gratë dhe fëmijët e tyre të cilët kanë qenë vepërmirë ata do t’i vizitojnë engjëjt duke hyrë në secilën derë.');
INSERT INTO "ahmeti" VALUES(13,24,'13:24','(u thonë) Selamun alejkum, me durimin tuaj gjetët shpëtimin; sa ërfundim i lavdishëm është ky vend.');
INSERT INTO "ahmeti" VALUES(13,25,'13:25','E ata që e thyejnë besën e dhënë All-llahut pasi u patën zotuar, e këpusin atë për të cilën All-llahu ka urdhëruar të jetë e pakëputur, bëjnë shkatërrime në tokë, ata janë të mallkuar dhe ata kanë përfundim të keq.');
INSERT INTO "ahmeti" VALUES(13,26,'13:26','All-llahu i ofron furnizim me bollëk atijqë do dhe ia kufizon (atij ë do). Po ata (jobesimtarët) janë të gëzuar me jetën e kësaj bote. e jeta e kësaj bote ndaj botës tjetër nuk është tjetër vetëm se një përjetim (i shkurtër).');
INSERT INTO "ahmeti" VALUES(13,27,'13:27','E ata që nuk besuan (idhujtarët mekas) thonë: Përse nuk iu shpall (Muhammedit) ndonjë mrekulli nga Zoti i tij?” Thuaj: “(Iu shpall Kur’ani, por ju u verbuat) All-llahu e le të humbur atë që do, kurse atë që pendohet nga të këqiat e shpie në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(13,28,'13:28','Ata që besuan dhe me të përmendur All-llahun, zemrat e tyre qetësohen; pra ta dini se me të përmendur All-llahun zemrat stabilizohen.');
INSERT INTO "ahmeti" VALUES(13,29,'13:29','Ata janë të cilët besuan dhe bënë vepra të mira, ata janë të lumtur dhe kanë ardhmëri të mirë.');
INSERT INTO "ahmeti" VALUES(13,30,'13:30','Kështu, Ne të dëguam te një popull, para të cilit kauluan shumë popuj, e që t’ju komunikosh atë që Ne të shpallëm ty, sepse ata jnë duke mohuar Mëshiruesin (Zotin). Thuaj: “Ai (Rrahmani) është Zoti im, nuk ka zot tjetër pos Atij. Vetëm Atij i jam mbështetur dhe vetëm te Ai është e ardhmja ime.');
INSERT INTO "ahmeti" VALUES(13,31,'13:31','Sikur të ishte ndonjë Kur’an që me të do të ecnin malet, që me të do të plasej toka, që me të do të flisnin të vdekurit (është ky)?” Jo, (ata nuk besojë) se e tërë çështja i takon vetëm All-llahut. A nuk e kanë të qartë atë që besuan se, sikur të donte All-llahu, do t’i drejtonte në rrugë të drejtë të gjithë njerëzit, por ata që nuk besuan e për shkak të asaj që bënë, do t’i godas vazhdimish fatkeqësi e kohës ose do ta rrehojë (fatkeqësia) vendin e tyre, derisa të vijë urdhëri i All-llahut (e të triumfojë islami). All-llahu nuk e thenë premtimin.');
INSERT INTO "ahmeti" VALUES(13,32,'13:32','edhe me të dërguarit para teje kanë bërë tallje. Porse Unë atyre që nuk besuan u pata dhënë afat, pastaj i ndëshkova (me dënim), e çfarë ishte dënimi Im!');
INSERT INTO "ahmeti" VALUES(13,33,'13:33','A është Ai që përcjell secilin njeri se ç’vepron (sikur idhujt që nuk kuptojnë asgjë)? e ata i përshkruajnë All-llahut shokë. Thuaj: “Përshkruani realitetin e tyre! A ju po e njoftoni Atë për atë (shok) kinse Ai nuk po ditka se ç’ka në tokë, apo vetëm po i emërtoni me fjalë (e ata as që ekzistojnë)? Jo, por ata që nuk besuan u duket e mirë dredhia e tyre, ndaj janë larguar nga rruga e drejtë, dhe atë që All-llahu e le të humbur, për te nuk ka udhëzues.');
INSERT INTO "ahmeti" VALUES(13,34,'13:34','Ata kanë dënim në jetën e kësaj bote, e s’ka dshim se dënimi i botës tjetër është shumë i rënrë. Ata nuk ka kush t’i mbrojë prej ndëshkimit të All-llahut.');
INSERT INTO "ahmeti" VALUES(13,35,'13:35','Shembulli (atributi) i Xhennetit që u është premtuar të devotshëmve është: që nën (palatet) të rrjedhin lumenj, që ushqimi në të dhe hija në të është e përhershme. ai është përjetim i lumtur i atyre që i shin ruajtur, e pëfundimi i jobesimtarëve është zjarri.');
INSERT INTO "ahmeti" VALUES(13,36,'13:36','Ata të cilëve Ne u dhamë shallje, gëzohen me atë që t’u shpall ty, e ka prej grupacioneve (të besimeve të ndryshme) që mohojnë një pjesë të tij (të Kur’anit). Thuaj: “Unë jam urdhëruar që të adhuroj vetëm All-llahun, e mos t’i bëj shok Atij. Unë vetëm te Ai thërras (njerëzit) dhe vetëm te ai është kthimi im!”');
INSERT INTO "ahmeti" VALUES(13,37,'13:37','Dhe ashtu, Ne e shpallëm atë (Kur’anin) kushtetutë në arabishte. e nëse ti pasi të kanë ardhur argumente të qarta, shkon pas dëshirave të tyre, ti nuk ke prej All-llahut as ndihmë as mbrojtje.');
INSERT INTO "ahmeti" VALUES(13,38,'13:38','Ne dërguam edhe para teje të dërguar dhe atyre u mundësuam të kenë gra e fëmijë. Asnjë të dërguari nuk i takoi të sjellë ndonjë mrekulli vetëm me lejen e All-llahut. Për secilin afat ekziston evidencë e caktuar.');
INSERT INTO "ahmeti" VALUES(13,39,'13:39','All-llahu shlyen (nga ajo evidencë) çka të dojë, e edhe forcon (çka të dojë). Vetëm te Ai është baza e librit (Levhi Mahfudhi).');
INSERT INTO "ahmeti" VALUES(13,40,'13:40','Ne mund të mundësojmë ty ta shohësh atë (dënimin) që u premtojmë atyre, ose ta marrin (shpirtin) ty. Obligim yti është vetm kumtimi, kurse Jona është llogaria.');
INSERT INTO "ahmeti" VALUES(13,41,'13:41','A nuk shohin ata se Ne i sjellim pakësimin e tokës (së tyre) nga të gjitha anët e saj. All-llahu gjykon, e s’ka kusj që mund t’i kundërvihet gjykimit të Tij. Ai ndërmerr masë të shpejtë.');
INSERT INTO "ahmeti" VALUES(13,42,'13:42','Edhe ata (jobesimtarët) para tyre (idhujtarëve) bënë intrigë, po e gjithë intriga është e nënshtruar dëshirës së All-llahut, Ai e di se çka vepron secili njeri, e mohuesit do të kuptojnë se kujt do t’i takojë ardhmëri e lumtur.');
INSERT INTO "ahmeti" VALUES(13,43,'13:43','E ata që mohuan thonë: “Ti nuk je i dërguar!” Thuaj: “Mjafton që All-llahu është dëshmitarë midis meje dhe midis jush dhe (është dëshmitar) ai që ka njohuri të librit (të shpalljeve)”.');
INSERT INTO "ahmeti" VALUES(14,1,'14:1','Elif, Lamë, Ra. (ky është) Libër; Ne ta shpallëm ty që me urdhërin e Zotit t’i nxjerrësh njerëzit prej errësirës në dritë; (t’i nxjerrësh prej errësirës) në rrugë të Fuqiplotit, të Falënderuarit.');
INSERT INTO "ahmeti" VALUES(14,2,'14:2','Të All-llahut, i të cilit është ç’ka në qiej dhe ç’ka në tokë. Është mjerim për mohuesit për dënimin e ashpër që i pret.');
INSERT INTO "ahmeti" VALUES(14,3,'14:3','Ata që i japin përparësi jetës së kësaj bote ndaj botës tjetër, pengojnë nga rruga e All-llahut dhe kërkojnë shtrembërimin e saj. Të tillët qartas janë në humbje.');
INSERT INTO "ahmeti" VALUES(14,4,'14:4','Ne asnjë nga të dërguarit nuk e dërguam ndryshe vetëm në gjuhën e popullit të vet, ashtuqë ai t’u shpjegojë atyre (në atë gjuhë). E All-llahu e lë të humbur atë që do, dhe e udhëzon në rrugë të drejtë atë që do. Ai është i fuqishmi, i urti.');
INSERT INTO "ahmeti" VALUES(14,5,'14:5','Ne e patëm dërguar Musain me argumentet tona (dhe i thamë); xnirre popullin tënd prej errësirave në dritë dhe përkujtoju atyre të mirat e All-llahut (që u dhuroi), se vërtet në ato (përkujtime) ka argumente për secilin që është shumë i durueshëm dhe shumë falënderues.');
INSERT INTO "ahmeti" VALUES(14,6,'14:6','(përkujto) Kur Musai i tha popullit të vet: “Përkujtonie dhuntinë e All-llahut ndaj jush; kur ju shpëtoi prej rrethit të faraonit që ju shijonin dënimin më të shëmtuar, ju mbytnin bijtë tuaj, e lënin gratë tuaja të jetojnë. Në ato kishit sprovë të madhe nga Zoti juaj.”');
INSERT INTO "ahmeti" VALUES(14,7,'14:7','Dhe (pëkujtoni) kur Zoti juaj njoftoi bindshëm: “Nëse falënderoni, do t’ua shtojë të mirat, e nëse përbuzni, s’ka dyshim, dënimi Im është i vështirë!”');
INSERT INTO "ahmeti" VALUES(14,8,'14:8','E Musai tha: “Nëse ju, dhe të gjithë ata që janë në tokë mohoni, All-llahu është i panevojshëm (për falënderim, i lavdishëm (vetvetiu”.');
INSERT INTO "ahmeti" VALUES(14,9,'14:9','A nuk jeni të njohur me lajmin (ndodhinë) e atyre që ishin para jush, si populli i Nuhut, i Adnit, i Themudit e edhe i atyre që erdhën pas tyre, që nuk di kush për ta poa All-llahut. Atyre u patën ardhur të dërguarit e vet me argumente, ata i vënin duart e tyre (në shenjë talljeje) në gojët e veta: “Ne e mohojmë atë me çka jeni të dërguar dhe ne dyshojmë shumë në atë që ju na thërrisni”.');
INSERT INTO "ahmeti" VALUES(14,10,'14:10','Të dërguarit e tyre u thanë: “A mos keni dyshim në All-llahun, Krijuesin e qiejve e të tokës? Ai ju thërret (në besim) për t’ju falur mëkatet dhe për t’ju vazhduar (jetën deri në një afat të caktuar!” Ata thanë: “Ju nuk jeni tjetër pos njerëz siç jemi edhe ne, ju dëshironi të na pengoni nga ajo që adhuronin prindërit tanë, pra na sillni ndonjë argument të qartë!”');
INSERT INTO "ahmeti" VALUES(14,11,'14:11','Të dërguarit e tyre ju thanë: “Ne nuk jemi tjetër vetëm se njerëz si dhe edhe ju, por All-llahu i jep dhuratë kujt të dojë nga robtë e vet. Ne nuk na takon të sjellim ndonjë argument, vetëm me dëshirën e All-llahut, pra, besimtarët le t’i mbështeten vetëm All-llahut.');
INSERT INTO "ahmeti" VALUES(14,12,'14:12','E përse të mos i mbështetemi All-llahu derisa Ai na udhëzoi në rrugën tonë (të drejt). Për Zotin, ne gjithsesi do të durojmë e do të përballojmë mundimet që na bëni, e vetëm All-llahut let’i mbështeten vazhdimisht ata që gjithnjë iu mbështeten.');
INSERT INTO "ahmeti" VALUES(14,13,'14:13','Ata që mohuan të dërgarit e vet thanë: “Për Zotin, ne do t’ju dëbojmë nga vendi ynë, ose ju domosdo do të ktheheni në fenë tonë!” E atyre (të dërguarëve) Zoti u shpalli; Ne gjithqysh do t’i shkatërrojmë zullumqarët.');
INSERT INTO "ahmeti" VALUES(14,14,'14:14','Dhe pas tyre Ne do t’ju vendosim në atë tokë. E këtë (ndihmë) për atë që i frikësohet pranisë Sime dhe i frikësohet dënimit Tim.');
INSERT INTO "ahmeti" VALUES(14,15,'14:15','Ata (të dërguarit) e kërkuan ndihmën, ndërsa çdo kryelartë idhnak pësoi dështim.');
INSERT INTO "ahmeti" VALUES(14,16,'14:16','E pas tij (kryelartit) është Xhehennemi, në të cilin i jepet ujë të ndyrë (që rrjedh prej lëkurave të djegura0,');
INSERT INTO "ahmeti" VALUES(14,17,'14:17','Përpiqet ta përbijë, po nuk mund ta gëlltis atë, atij i vjen vdekja nga të gjitha anët (sipas shenjave shkatërruese), po ai nuk vdes (e të shpëtojë prej vuajtjeve), atë e pret dënim shumë i rëndë.');
INSERT INTO "ahmeti" VALUES(14,18,'14:18','Shembulli i veprave të atyre që nuk besuan është si hiri, të cilin me puhi e shkapërderdh era në ndonjë ditë të stuhishme, e ata nuk mund të realizojnë asgjë nga veprat që kanë bërë, e ky është ai dështimi i madh.');
INSERT INTO "ahmeti" VALUES(14,19,'14:19','A nuk e ke kuptuar ti se All-llahu krijoi qiejt dhe tokën me urtësi, e nëse Ai do, juve ju zhduk e sjell krijesë (popull) të re.');
INSERT INTO "ahmeti" VALUES(14,20,'14:20','E kjo, nuk është vështirë (rëndë) për All-llahun.');
INSERT INTO "ahmeti" VALUES(14,21,'14:21','E të gjithë (njerëzit) sheshas i paraqiten All-llahut. Ata të dobëtit (masa e nënshtruar) u thonë parisë (udhëheqëse): “Ne ishim pasuesit tuaj, andaj a mund të nalehtësoni diç nga dënimi i All-llahut?” Ata (paria) thonë: “Sukur të na kishte udhëzuar All-llahu, ne do t’ju udhëzonim juve. Për ne tash është njësoj, u pikëlluam thellë ose duruam, për ne nuk ka ikje (shpëtim).');
INSERT INTO "ahmeti" VALUES(14,22,'14:22','E të kryhet çështja (të hyjnë ata të Xhennetit dhe të Xhehennemit në të, Xhehennemlinjve) djalli (u mban ligjeratën e shëmtuar) dhe u thotë: “Vërtet, All-llahu ju pat premtuar premtim të vërtetë, e unë ju pata premtuardhe, qe, nuk zbatova premtimin ndaj jush. Po unë nuk pata kurrfarë pushteti ndaj jush (që t’ju detyroj), përpos që ju thirra (në rrugë të gabuar), e ju m’u përgjigjët; atëherë, pra mos më qortoni mua, po qortnie veten. Unë nuk mund t’ju shpëtoë juve, e as ju nuk mund të më shpëtoni mua. Unë mohoj shoqërimin tuaj që më bëtë mua më parë (më adhuruat në vend të Zotit). S’ka dyshim, jobesimtarët kanë dënim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(14,23,'14:23','E ata, të cilët besuan dhe bënë vepra të mira, me urdhërin e Zotit të tyre u vendosën në Xhennete, nën pallatet e të cilëve rrjedhin lumenj, ku do të jenë përgjithmonë. Përshëndetja e tyre në të është “selam” - paqe.');
INSERT INTO "ahmeti" VALUES(14,24,'14:24','A nuk ke kuptar se si All-llahubëri shembull: fjalën e irë si pema e mirë që rrënjët e saj janë thellë (në tokë) e degët e saj janë lart,');
INSERT INTO "ahmeti" VALUES(14,25,'14:25','E që me vullnetin e Zotit, ajo e jep frutin e vet në çdo kohë. All-llahu, pra u parashtron njerëzve shembuj ashtuqë ata të mendojnë.');
INSERT INTO "ahmeti" VALUES(14,26,'14:26','Dhe shembulli i fjalës së keqe si një pemë e keqe që është shkulur mbi tokë e që nuk ka të qëndruar.');
INSERT INTO "ahmeti" VALUES(14,27,'14:27','All-llahu forcon ata që besuan në fjalëne fortë (të mirë) në jetën e kësaj bote e edhe në botën tjetër, ndërsa mizorët All-llahu i bën të humbur. All-llahu punon çka të dojë.');
INSERT INTO "ahmeti" VALUES(14,28,'14:28','A nuk i vërejte ata që dhuntinë e All-llahut e ndërruan me mosbesim dhe popullin e vet e sollën në vendin e shkatërrimit,');
INSERT INTO "ahmeti" VALUES(14,29,'14:29','(e sollën) Në Xhehennem të shijojnë djegien, e sa vend i keq është ai.');
INSERT INTO "ahmeti" VALUES(14,30,'14:30','Ata i bënin (krahasonin) shokë All-llahut për t’i shmangur (njerëzit) nga rruga e Tij. Thuaj: “Shfrytëzoni përjetimet se e srdhmja e juaj është te Zjarri!”');
INSERT INTO "ahmeti" VALUES(14,31,'14:31','Robëve të Mi, të cilët besuan thuaju: “Të falin rregullisht namazin dhe me atë që i furnizuam ata, të japin fshehtas e haptas, para se të vijë një ditë që në të nuk ka as kompensim as miqësi”.');
INSERT INTO "ahmeti" VALUES(14,32,'14:32','All-llahu është Ai që i krijoi qiejt dhe tokën, dhe lëshoi prej së larti ujë (shi), e me të nxjerr fruta si ushqim për ju, dhe për të mirën tuaj u vuri në shërbim anijet, të lundrojnë nëpër det me urdhërin e Tij, e në shërbimin tuaj i vuri edhe lumenjtë.');
INSERT INTO "ahmeti" VALUES(14,33,'14:33','Për ju nënshtroi diellin dhë hënën që në mënyrë të zakonshme vazhdimisht udhëtojnë. Për ju përshtati edhe natën e ditën.');
INSERT INTO "ahmeti" VALUES(14,34,'14:34','Dhe Ai ju dha gjithatë që e kërkuat (që kërkoi nevoja juaj) dhe, edhe në qoftë se përpiqeni t’i numëroni të mirat (në numër). Vërtet, njeriu është i padrejtë dhe shumë për përbuzës.');
INSERT INTO "ahmeti" VALUES(14,35,'14:35','(përkujto) Kur Ibrahimi tha: “Zoti im! bëne këtë qytet të sigurt dhe më mbro mua e bijtë e mi nga adhurimi i idhujve (statuja gurësh).');
INSERT INTO "ahmeti" VALUES(14,36,'14:36','Zoti im! ata vërtet i shmangin (nga rruga e drejtë) shumë njerëz. E kush më respekton mua, ai është i mi (në fe), e kush më kundërshton mua, atëherë Ti je që fal dhe mëshiron.');
INSERT INTO "ahmeti" VALUES(14,37,'14:37','Zoti ynë! unë një pjesë të familjes sime e vendosa në një luginë, ku nuk ka bimë, e pranë shtëpisë tënde të shenjtë. Zoti ynë (i vendosa aty) që të falin namazin, pra bën që zemrat e disa njerëzve të mallëngjehen për ata, dhe, për të falënderuar me mirënjohje, furnizoj ata me fruta.');
INSERT INTO "ahmeti" VALUES(14,38,'14:38','Zoti ynë! s’ka dyshim se Ti e di çka fshehin dhe çka publikojmë, se All-llahut nuk mund t’i fshihet asnjë në send në tokë e as në qiell.');
INSERT INTO "ahmeti" VALUES(14,39,'14:39','Falënderimi i qoftë All-llahut, i cili në pleqëri më fali mua Ismailin dhe Is-hakun! Vërtet, Zoi im dëgjon dhe i përgjigjet lutjes.');
INSERT INTO "ahmeti" VALUES(14,40,'14:40','O Zoti im! më bën mua nga ata që falin namazin, e edhe prej pasardhësve të mijë dhe pranoje lutjen time o Zoti ynë!');
INSERT INTO "ahmeti" VALUES(14,41,'14:41','Zoti ynë! më fal (gabimet) mua edhe prindërve të mi, fali edhe të gjithë besimtarët ditën kur jepet llogaria.');
INSERT INTO "ahmeti" VALUES(14,42,'14:42','E ti kurrsesi mos e mendo All-llahun si të pakujdesshëm ndaj asaj që veprojnë zullumqarët; Ai vetëm është duke i lënë ata përderisa një ditë në të cilën sytë shtangen (mbesin të hapur).');
INSERT INTO "ahmeti" VALUES(14,43,'14:43','(atë ditë) Ata të ngutur e duke i ngritur kokat e tyre lar, nuk lëvizin sytë e tyre (për të shikuar), e zemrat e tyre janë të zbrazura (nga frika).');
INSERT INTO "ahmeti" VALUES(14,44,'14:44','Dhe ti tërhiqju vërejtjen njerëzve për ditën kur atyre do t; ju vijë dënimi, e ata që ishin mizorë thonë: “Zoti ynë! na jep afat për një kohë të shkurtër t’i përgjigjemi thirrjes tënde (për besim) dhe t’i pasojmë të dërguarit!” A nuk e besuat ju më parë se për ju nuk ka lëkundje (prej dynjasë në botën tjetër)?');
INSERT INTO "ahmeti" VALUES(14,45,'14:45','Dhe ju u vendosët në vendbanimet e atyre që e dëmtuan vetveten (e Ne i shkatërruam) dhe e kishit të qartë se si vepruam Ne me ta, dhe ju sollëm shembuj edhe juve.');
INSERT INTO "ahmeti" VALUES(14,46,'14:46','(megjithatë) Ata përgatitën kurthin e tyre, por kurthi i tyre gjendet në duart e All-llahut, qoftë ai kurthi i tyre që shkulën edhe kodrat nga ai.');
INSERT INTO "ahmeti" VALUES(14,47,'14:47','E kurrsesi mos mendo se All-llahu e shkel premtimin e vet, që u dha të dërguarëve të tij. All-llahu është triumfues që ndërmerr masa ndëshkuese.');
INSERT INTO "ahmeti" VALUES(14,48,'14:48','(ndëshkon kundërshtarët) Ditën kur toka ndryshohet në tjetër tokë, e edhe qiejt (në tjerë qiej), e ata (njerëzit) të gjithë dallin sheshazi para All-llahut, Një, Mbizotërues.');
INSERT INTO "ahmeti" VALUES(14,49,'14:49','E idhujtarët atë ditë i sheh të lidhur në pranga.');
INSERT INTO "ahmeti" VALUES(14,50,'14:50','Petkat e tyre janë nga katrani (pezhgveja - zifti), kurse fytyrat e tyre do t’i mbulojë zjarri.');
INSERT INTO "ahmeti" VALUES(14,51,'14:51','(dalin para Zotit) Për ta shpërblyer All-llahu secilin njeri me atë që e fitoi. Vërtet, All-llahu është llogaritar i shpejtë.');
INSERT INTO "ahmeti" VALUES(14,52,'14:52','Ky (Kur’ani) është kumtesë për njerëz, që me të të këshillohen dhe të dinë se Ai është vetëm një zot, dhe që t’i këshilloj ata që kanë mend.');
INSERT INTO "ahmeti" VALUES(15,1,'15:1','Elif, Lam, Ra. Këto janë ajetet e librit, të Kur’anit të plotkuptueshëm.');
INSERT INTO "ahmeti" VALUES(15,2,'15:2','Ata që nuk besuan shpeshherë do të kishin dëshiruar të kishin qenë myslimanë.');
INSERT INTO "ahmeti" VALUES(15,3,'15:3','Leri (Muhammed) ata, të hanë, të dëfrehen dhe t’i preokupojë shpresa (se do të jetojnë shumë), e më vonë do të kuptojnë.');
INSERT INTO "ahmeti" VALUES(15,4,'15:4','Ne nuk kemi shkatërruar asnjë fshat (vendbanim) ndryshe vetëm në afatin e tij të caktuar.');
INSERT INTO "ahmeti" VALUES(15,5,'15:5','Asnjë popull nuk mund ta shpejtojë afatin evet e as ta shtyjë për më vonë.');
INSERT INTO "ahmeti" VALUES(15,6,'15:6','Dhe ata thanë: “O ti që t’u shpall përkujtimi (Kur’ani), me të vërtetë ti je çmendur!”');
INSERT INTO "ahmeti" VALUES(15,7,'15:7','Përse nuk na erdhe me enjëj (që të vërtetojnë) nëse je i sinqertë?');
INSERT INTO "ahmeti" VALUES(15,8,'15:8','Ne nuk i dërgojmë engjëjt (u përgjigjet Zoti) ndryshe vetëm kur duhet (kur e kërkon urtësia e Zotit) dhe atëherë ata nuk aftizohen.');
INSERT INTO "ahmeti" VALUES(15,9,'15:9','Ne me madhërinë Tonë e shpallëm Kur’anin dhe Ne gjithsesi jemi mbrojtës të tij.');
INSERT INTO "ahmeti" VALUES(15,10,'15:10','Ne përapara teje kemi dërguar të dërguar në grupet e popujve të hershëm.');
INSERT INTO "ahmeti" VALUES(15,11,'15:11','E atyre nuk u erdhi asnjë nga të dërguarit, e të mos talleshin me të.');
INSERT INTO "ahmeti" VALUES(15,12,'15:12','Ja kështu e fusim atë në zemrat e kriminelëve (mosbesimin - talljen).');
INSERT INTO "ahmeti" VALUES(15,13,'15:13','Ata nuk e besojnë atë (Kur’anin). Po ligji (i dënimit) ndaj popujve më të hershëm tashmë është i provuar (se si i shkatërroi Zoti).');
INSERT INTO "ahmeti" VALUES(15,14,'15:14','Edhe sikur Ne t’u hapnim atyre në qiell dhe të ngjiteshin vazhdimisht lart në të (e të shihnin engjëjt e fshehtësitë).');
INSERT INTO "ahmeti" VALUES(15,15,'15:15','Ata gjithqysh do të thonin: “Neve na janë ndalë sytë (të pamët). Jo, ne jemi njerëz të magjepsur.');
INSERT INTO "ahmeti" VALUES(15,16,'15:16','Ne në qiell kemi krijuar galaksione yjesh dhe atë (qiellin) e kemi zbukuruar për ata që e shikojnë me vëmendje.');
INSERT INTO "ahmeti" VALUES(15,17,'15:17','Dhe atë (qiellin) e kemi ruajturprej çdo djalli të mallkuar.');
INSERT INTO "ahmeti" VALUES(15,18,'15:18','Përveç atij që vjedh (përgjon) çka dëgjohet, po edhe atë e kapë ylli i zjarrtë (e djeg).');
INSERT INTO "ahmeti" VALUES(15,19,'15:19','E tokën e kemi shtruar dhe në të kemi vuar kodra dhe kemi bërë që në të të mbijnë bimë të caktuara të të gjitha llojeve.');
INSERT INTO "ahmeti" VALUES(15,20,'15:20','Dhe Ne ju krijuam juve në te jetesën (mjetet për jetë) e edhe tyre për të cilët ju nuk jeni furnizues.');
INSERT INTO "ahmeti" VALUES(15,21,'15:21','E nuk ka asnjë send që të mos ta ketë burimin te Ne, po Ne, nuk e japim atë ndryshe vetëm sipas një mse të caktuar (të nevojshme).');
INSERT INTO "ahmeti" VALUES(15,22,'15:22','Ne i lëshojmë erërat mbarësuese, e nga qielli (retë) lëshojnë shi dhe atë ju japim ta pini, e ju nuk mund ta ruani atë.');
INSERT INTO "ahmeti" VALUES(15,23,'15:23','Ne, vërtetjemi që japim jetë e vdekje dhe Ne jemi trashëgues (të qiejve e tokës).');
INSERT INTO "ahmeti" VALUES(15,24,'15:24','S’ka dyshim se Ne e dimë për ata që ishin para jush dhe edhe për ata që vijnë pas.');
INSERT INTO "ahmeti" VALUES(15,25,'15:25','E Zoti yt është Ai që i tubon të gjithë ata (të kaluarit e të ardhëshmit), vërtet Ai është shumë i urtë, shumë i dijshëm.');
INSERT INTO "ahmeti" VALUES(15,26,'15:26','Ne krijuam njeriun nga balta e argjilit, të zi e të prihsur (me erë të keqe)? (të formuar-të trajtuar-mesnun).');
INSERT INTO "ahmeti" VALUES(15,27,'15:27','E xhinët i krijuam më parë nga zjarri, nga flaka e fortë.');
INSERT INTO "ahmeti" VALUES(15,28,'15:28','Dhe (përkujto) kur Zoti yt engjëjve u tha: “Unë po krijoi njeriun nga balta e tharë, e zezë e me erë.');
INSERT INTO "ahmeti" VALUES(15,29,'15:29','E kur ta përsosë atë (në formën e njeriut) dhe do t’i japë atij shpirtin që është krijesa Imja, atëherë ju bini atij në sexhde”.');
INSERT INTO "ahmeti" VALUES(15,30,'15:30','Të gjithë engjëjt tok, bënë sexhde.');
INSERT INTO "ahmeti" VALUES(15,31,'15:31','Përpos Iblisit. Ai refuzoi të jetë me ata që bënë sexhde?”');
INSERT INTO "ahmeti" VALUES(15,32,'15:32','Ai tha: “O Iblis, ç’është ajo që ti mos jehsë me ata që bënë sexhde?”');
INSERT INTO "ahmeti" VALUES(15,33,'15:33','Ai tha: “Nuk është për mua t’i bëjë sexhde një njeriu që e krijove nga balta e terur, e zezë e me erë”.');
INSERT INTO "ahmeti" VALUES(15,34,'15:34','(All-llahu) tha: “Dil pra, prej aty, tiqofsh i mallkuar!');
INSERT INTO "ahmeti" VALUES(15,35,'15:35','Vërtet, mallkimi kundër teje qoftë deri në ditën e gjykimit”.');
INSERT INTO "ahmeti" VALUES(15,36,'15:36','(Iblisi) Tha: “Zoti im, më jep afat deri në ditën kur ata (njerëzit) të ringjallen!');
INSERT INTO "ahmeti" VALUES(15,37,'15:37','(Zoti) Tha: “Ti je prej të afatizuarve.');
INSERT INTO "ahmeti" VALUES(15,38,'15:38','deri në kohën e ditës së caktuar”.');
INSERT INTO "ahmeti" VALUES(15,39,'15:39','(Iblisi) Tha: “Zoti im, për shkak që më përzune (më largove), unë do t’ua zbukurojë (të këqiat) atyre sa të jenë në tokë (në këtë botë) dhe të gjithë ata do t’i largojë nga rruga e drejtë!”');
INSERT INTO "ahmeti" VALUES(15,40,'15:40','Përveç robërve të Tu, të cilët i ke bërë të sinqertë.');
INSERT INTO "ahmeti" VALUES(15,41,'15:41','(Madhëria e Tij) Tha: “kjo është rruga Ime e drejtë (e qartë)”.');
INSERT INTO "ahmeti" VALUES(15,42,'15:42','Se ndaj robëve të Mi (të sinqertë) nuk ke kurrëfarë force (as fizike as mendore), përveç atyre të humburve që vijnë pas teje.');
INSERT INTO "ahmeti" VALUES(15,43,'15:43','E s’ka dyshim se Xhehennemi është vendpremtimi i të gjithë atyre.');
INSERT INTO "ahmeti" VALUES(15,44,'15:44','Ai (Xhehennemi) i ka shtatë dyer, çdonjëri prej tyre ka shtegun (derën) e caktuar (nëpër të cilën do të hyjë).');
INSERT INTO "ahmeti" VALUES(15,45,'15:45','E ata, të cilët iu ruajtën (mosbesimit dhe punëve të këqija) janë në Xhennete e mes burimeve.');
INSERT INTO "ahmeti" VALUES(15,46,'15:46','(u thuhet) Hyni në to, të shpëtuar (prej çdo të keqeje) të siguruar (nga vdekja).');
INSERT INTO "ahmeti" VALUES(15,47,'15:47','Ne kemi hequr prej zemrave të tyre çfardo urrejtje, e at në mbështetëse qëndrojnë ballë për ballë njëri-tjetrit duke qenë të vëllazëruar.');
INSERT INTO "ahmeti" VALUES(15,48,'15:48','Ata aty nuk igodet kurrfarë lodhje dhe nuk do të nxirren kurrë prej tij.');
INSERT INTO "ahmeti" VALUES(15,49,'15:49','Njoftoi robët e Mi se vërtet Unë jam Ai që fal shumë dhe mëshirues i madh.');
INSERT INTO "ahmeti" VALUES(15,50,'15:50','Po (njoftojë) se edhe dënimi Im është ai i dhëmbshëm.');
INSERT INTO "ahmeti" VALUES(15,51,'15:51','Enjoftoj edhe për mysafirët e Ibrahimit.');
INSERT INTO "ahmeti" VALUES(15,52,'15:52','kur ata hynë te ai dhe thanë: “Selamen - e përshëndetën e ai (Ibrahimi) tha: “Ne po frikësohemi prej jush!”');
INSERT INTO "ahmeti" VALUES(15,53,'15:53','Ata thanë: “Mos u frikëso ne po të marrim myzhde për një djalë të zgjuar!”');
INSERT INTO "ahmeti" VALUES(15,54,'15:54','Ai (Ibrahimi) tha: “A më merrni myzhde kur më ka kapur mua pleqëria? Me çka po më përgëzoni ju?');
INSERT INTO "ahmeti" VALUES(15,55,'15:55','Ata thanë: “Ne të morëm myzhde me atë që është e sigurtë e ti mos u bë i pashpresë!”');
INSERT INTO "ahmeti" VALUES(15,56,'15:56','Tha: “Askush nuk e humb shpresën në mëshirën e Zotit të vet, përveç atyre që janë të humbur”');
INSERT INTO "ahmeti" VALUES(15,57,'15:57','Tha: “E çka është pra çështja e juaj, o të dërguar?”');
INSERT INTO "ahmeti" VALUES(15,58,'15:58','Ata thanë: “Ne jemi dërguar te një popull i prishur!”');
INSERT INTO "ahmeti" VALUES(15,59,'15:59','Me përjashtim të familjes së Lutit. Ata do t’i shpëtojmë që të gjithë.');
INSERT INTO "ahmeti" VALUES(15,60,'15:60','Përpos gruas sëtij, ne kemi vendosur, ajo të mbetet me ata të dënuarit (All-llahu ka vendosur).');
INSERT INTO "ahmeti" VALUES(15,61,'15:61','E kur i erdhën familjes së Lutit të dërguarit,');
INSERT INTO "ahmeti" VALUES(15,62,'15:62','Ai (Luti) tha: “Ju jeni njerëz të panjohur (nuk ju njoh)!');
INSERT INTO "ahmeti" VALUES(15,63,'15:63','Ata thanë: “Jo, (nuk kemi qëllim të keq ndaj teje) Të kemi ardhur ty me atë (dënim) që ata dyshonin.');
INSERT INTO "ahmeti" VALUES(15,64,'15:64','Të kemi sjellë të vërtetën, e ne jemi të drejtë (çka themi).');
INSERT INTO "ahmeti" VALUES(15,65,'15:65','Ti ec me familjen tënde pasi të kalojë një pjesë e natës dhe rri pas tyre (që të mos frkësohen) dhe asnjëri prej jush të mos kthejë mbrapa (për të shikuar) dhe shkoni andej kah urdhëroheni.');
INSERT INTO "ahmeti" VALUES(15,66,'15:66','E Ne i kumtuam atij (Lutit) atë çështje, se zhdukja e tyre deri në më të mbramin, do të jetë në mëngjes.');
INSERT INTO "ahmeti" VALUES(15,67,'15:67','E banuesit e qytetit (sadum) erdhën të gëzuar duke i lajmëruar njëri-tjetrin.');
INSERT INTO "ahmeti" VALUES(15,68,'15:68','Tha: (Luti): “Këta janë mysafirët e mi, e mos më turpëroni mua,');
INSERT INTO "ahmeti" VALUES(15,69,'15:69','Edhe kinie frikë All-llahun e mos më nënçmoni!”');
INSERT INTO "ahmeti" VALUES(15,70,'15:70','Ata thanë: “A nuk të kemi ndaluar që të mos na përzihesh në njerëzit (që ne i mësyjmë)?');
INSERT INTO "ahmeti" VALUES(15,71,'15:71','Ai (Luti) tha: “Ja, këto (gratë) bijat e mija, nëse do të bëni (martohuni me to)!”');
INSERT INTO "ahmeti" VALUES(15,72,'15:72','Pasha jetën tënde (Muhammed), s’ka dyshim se ata (Populli i Lutit) ishin të humbur në dehjen e tyre.');
INSERT INTO "ahmeti" VALUES(15,73,'15:73','E në kohën e lindjes (së diellit) ata i përfshiu ushtima e tmerrshme.');
INSERT INTO "ahmeti" VALUES(15,74,'15:74','Dhe duke e përmbysur anën e lartë të qytetit poshtë, Ne e përmbysëm dhe lëshuammbi ta shi (të dheut të pjekur në Xhehennem) si gurë.');
INSERT INTO "ahmeti" VALUES(15,75,'15:75','Në atë (ngjarje), vërtet ka argumente për ata që mendojnë.');
INSERT INTO "ahmeti" VALUES(15,76,'15:76','Dhe se ato (vendbanime të shkatërruara) ende janë të dukshme pranë rrugës.');
INSERT INTO "ahmeti" VALUES(15,77,'15:77','Për ata që besojnë, ka fakte në të.');
INSERT INTO "ahmeti" VALUES(15,78,'15:78','Edhe populli i “Ejkës” ishin zullumqarë.');
INSERT INTO "ahmeti" VALUES(15,79,'15:79','E Ne ndërrmorëm kundër tyre (i shkatërruam), dhe që të dyja ato (vendi Sadum dhe Ejke) janë në rrugë që duken.');
INSERT INTO "ahmeti" VALUES(15,80,'15:80','Edhe populli i “Hixhres” i përgënjeshtroi të dërguarit.');
INSERT INTO "ahmeti" VALUES(15,81,'15:81','Ne u patëm parashtruar atyre (mrekullitë) Tona, por ata i kundërshtonin ato.');
INSERT INTO "ahmeti" VALUES(15,82,'15:82','Ata skalisnin shtëpia në kodra shkëmbore për të qenë të sigurt.');
INSERT INTO "ahmeti" VALUES(15,83,'15:83','E ata në mëngjes i goditi gjëmim i tmerrshëm e shkatërrues.');
INSERT INTO "ahmeti" VALUES(15,84,'15:84','Dhe nuk u ndihmoi atyre asgjë ajo që vepruan.');
INSERT INTO "ahmeti" VALUES(15,85,'15:85','E Ne nuk i krijuam qiejt as tokën ndryshe vetëm me urtësi (të madhe). E s’ka dyshim se momenti i fundit (kijameti) do të vijë, e ti (Muhamme) sillu me njerëzishmëri.');
INSERT INTO "ahmeti" VALUES(15,86,'15:86','Vërtet, Zoti yt është krijuesi i përgjithshëm, më i dijshmi.');
INSERT INTO "ahmeti" VALUES(15,87,'15:87','Ne të kemi dhënë ty shtatë (ajete) që përsëriten (Fatiha përsëritet në rekate të namazit) edhe Kur’anin e madhërueshëm.');
INSERT INTO "ahmeti" VALUES(15,88,'15:88','E ty të mos shkojnë kurrsesi sytë në atë me çka Ne i pajisëm disa prej tyre, as mos u brengos për ta (pse nuk besuan), kurse ndaj besimtarëve jij i përulur e i butë.');
INSERT INTO "ahmeti" VALUES(15,89,'15:89','Dhe thuaj: “Unë jam qortues i hapët (për dënimin nga Zoti për ata që kundërshtojnë).');
INSERT INTO "ahmeti" VALUES(15,90,'15:90','(u sjellim dënim) Sikurse u sollëm atyre që bëjnë ndarjen. (e librave të mëparëshme).');
INSERT INTO "ahmeti" VALUES(15,91,'15:91','Të cilët e bënë Kur’anin të ndarë në pjesë.');
INSERT INTO "ahmeti" VALUES(15,92,'15:92','Pasha Zotin tënd, ata gjithë do t’i marrim në përgjegjësi.');
INSERT INTO "ahmeti" VALUES(15,93,'15:93','Për atë ç’vepruan.');
INSERT INTO "ahmeti" VALUES(15,94,'15:94','Publiko haptas atë për të cilën urdhërohesh, e hiqu idhujtarëve.');
INSERT INTO "ahmeti" VALUES(15,95,'15:95','S’ka dyshim se Ne të mjaftojmë ty kundër atyre që tallen,');
INSERT INTO "ahmeti" VALUES(15,96,'15:96','të cilët All-llahut, i kundërvejnë zot tjetër, e më vonë ata do ta kuptojnë (prapavinë e tyre).');
INSERT INTO "ahmeti" VALUES(15,97,'15:97','Ne e dimë mirë se ti ngushtohesh shpirtërisht për atë që thonë ata.');
INSERT INTO "ahmeti" VALUES(15,98,'15:98','Po ti madhëroje me falënderime Zotin tënd e bëhu prej atyre që luten (përulen duke u falur).');
INSERT INTO "ahmeti" VALUES(15,99,'15:99','Dhe adhuroje Zotin tënd deri të vijë ty e vërteta (vdekja).');
INSERT INTO "ahmeti" VALUES(16,1,'16:1','Erdhi (është afruar) caktimi i All-llahut (kijameti), pra mos kërkoni ngutje (ardhjen para kohës), e tij! E pstër dhe e lartë është madhëria e Tij në krahasimme atë që ata i shoqërojnë (shokë).');
INSERT INTO "ahmeti" VALUES(16,2,'16:2','Me urdhërin e Tijia zbret engjëjt me shpalljen atij që Ai dëshiron nga robtë e vet (duke thënë) t’ua tërhiqni vërejtjen (t’i njoftoni) se nuk ka të adhuruar (zot) tjetër vetëm Unë, pra të më keni frikën.');
INSERT INTO "ahmeti" VALUES(16,3,'16:3','I krijoi qiejt e tokën me urtësi precize. I lartë është Ai nga çka ata (idhujtarët) i shoqërojnë.');
INSERT INTO "ahmeti" VALUES(16,4,'16:4','E krijoi njeriun nga një pikël (uji), kur qe, ai (njeriu) kundërshtar i hapët.');
INSERT INTO "ahmeti" VALUES(16,5,'16:5','Edhe kafshët Ai krijoi dhe në saje të këtyre ju mund të mbroheni (nga të ftohtit), keni edhe dobi të tjera dhe prej tyre hani.');
INSERT INTO "ahmeti" VALUES(16,6,'16:6','Në to (në kafshët) ju shihni hijeshi (idili) kur në mbrëmje ato i ktheni (nga kullosa) dhe kur në mëngjes i lëshoni (për në kullosë).');
INSERT INTO "ahmeti" VALUES(16,7,'16:7','Ato bartin barën tuaj (të rëndë) nnë ndonjë vend (të largët) që ju do të arrinit atje me shumë vështirësi (me gjysmë shpirti). Vërtet, Zoti juaj është shumë Bamirës dhe shumë Mëshirues.');
INSERT INTO "ahmeti" VALUES(16,8,'16:8','Edhe kuajt (i krijoi), edhe mushkat edhe gomarët, për t’ju hipur atyre dhe si stoli, e Ai krijon (për hipje) çka ju (tash) nuk dini.');
INSERT INTO "ahmeti" VALUES(16,9,'16:9','Udhëzimi për në rrugë të drejtë i takon All-llahut e ka edhe rrugë që është e shtrembër. Po sikur të kishte dëshiruar Ai, do t’u kishte udhëzuar që të gjithëve.');
INSERT INTO "ahmeti" VALUES(16,10,'16:10','Ai është që për ju lëshoi nga (lart) qielli ujë, që prej tij të keni për të pirëdhe prej tij të keni bimë që në to do t’i kullotni (bagëtinë).');
INSERT INTO "ahmeti" VALUES(16,11,'16:11','Me atë (shiun) mbijnë, për të mirën tuaj, të lashtat, ullinjtë, hurmat, rushnajat dhe nga të gjitha frutat (e pemëve). Në këto (të mira) ka argumente për një popull që vështron.');
INSERT INTO "ahmeti" VALUES(16,12,'16:12','Dhe për ju nënshtroi natën e ditën dhe diellin e hënën. Edhe yjet janë të nënshtruar me urdhërin e Tij. vërtet, në këto ka argumente për një popull që mendon.');
INSERT INTO "ahmeti" VALUES(16,13,'16:13','Edhe për atë që ju krijoi në tokë lloje të ndryshme (bagëti, bimë, pemë, minerale etj.); ka fakte për një popull që di të merrë përvojë.');
INSERT INTO "ahmeti" VALUES(16,14,'16:14','Pastaj, Ai është që nënshtroi detin, që prej tij të hani mish të freskët, e prej tij të nxirrni stoli që i bani (i vishni), e i sheh anijet si çajnë (lundrojnë) në të (me ushti, ë e çajnë ujin në det). Ua nënshtroi që të shfrytëzoni nga begatitë e Tij dhe ashtu të falënderoni (Zotin).');
INSERT INTO "ahmeti" VALUES(16,15,'16:15','Dhe Ai vuri kodra (të forta) në tokë, ashtu që ajo të mos lëkundet me ju, (bëri) edhe lumenj e rrugë që të mund të orientoheni.');
INSERT INTO "ahmeti" VALUES(16,16,'16:16','Vuri edhe shenja të tjera (për orientim ditën), ndërsa (natën) ata orientohen me anën e yjeve.');
INSERT INTO "ahmeti" VALUES(16,17,'16:17','Atëherë pra, a është Ai që krijon njësoj, sikurse ai që nuk krijon? A nuk po mendoni?');
INSERT INTO "ahmeti" VALUES(16,18,'16:18','Po edhe nëse përpiqeni t’i numëroni dhuntitë (të mirat) e All-llahut, nuk do të mund të arrini t’i përcaktoni ato. Me siguri All-llahu shumë fal dhe shumë mëshiron.');
INSERT INTO "ahmeti" VALUES(16,19,'16:19','All-llahu e di atë që ju e fshihni dhe atë që e publikoni.');
INSERT INTO "ahmeti" VALUES(16,20,'16:20','Ata (idhujt) që i adhurojnë (idhujtarë) pos All-llahut, ata nuk krijojnë asnjë send, po vetë ata janë të krijuar.');
INSERT INTO "ahmeti" VALUES(16,21,'16:21','Janë të vdekur e jo të gjallë, dhe ata nuk e dinë se kur do të ringjallen (adhuruesit e tyre).');
INSERT INTO "ahmeti" VALUES(16,22,'16:22','Zoti juaj (që meriton adhurim) është një Zot, E ata që nuk e besojnë botën tjetër, zemrat e tyre janë mohuese (të njësisë së Zotit) dhe ata janë kryelartë.');
INSERT INTO "ahmeti" VALUES(16,23,'16:23','Është e vërtetë se All-llahu e di atë që e fshehin dhe atë që e shfaqën haptazi dhe se Ai nuk i do arrogantët.');
INSERT INTO "ahmeti" VALUES(16,24,'16:24','Kur atyre u thuhet (mohuesëve): ç’është ajo që shpalli Zoti juaj?”, ata thonë: “Mite të popujve të hershëm!”');
INSERT INTO "ahmeti" VALUES(16,25,'16:25','(e thonë atë shpifje) Sa për t’i bartur në ditën e gjykimit mëkatet e veta të plotadhe një pjesë të mëkateve të atyre që, pa farë arsye i mashtruan dhe i huumbën. Sa e keqe është ajo me çka ngarkohen.');
INSERT INTO "ahmeti" VALUES(16,26,'16:26','Edhe ata që ishin para tyre vënë intriga. Po All-llahu rrënoi themel ndërtimet e tyre (intrigat) dhe kulmi u shembmbi ta, dhe ashtu u erdhi dënimi kah nk kuptuan.');
INSERT INTO "ahmeti" VALUES(16,27,'16:27','Mandej, në ditën e gjykimit Ai i turpëron ata dhe thonë: “Ku janë ata që m’i bënit shokë Mua dhe për hir të tyre grindeshit?” E ata që ishin të zotët e dijës, do të thonë: “Vërtet, sot turpësia dhe e keqja i përfshiu jobesimtarët”.');
INSERT INTO "ahmeti" VALUES(16,28,'16:28','të cilët, duke qenë mizorë të së vetvetes së tyre, engjëjt ua marrin shpirtin, e ata shprehin dorëzimin (në momentin e vdekjes, duke thënë): “Ne nuk ishim që bëmë ndonjë të keqe!” (u jipet përgjigja). Po All-llahu e di shumë mirë atë që e punuat ju.');
INSERT INTO "ahmeti" VALUES(16,29,'16:29','Andaj, hyni në dyert e Xhehennemit, aty do të jeni përjetë: sa vend i keq është ai i kryelartëve!”');
INSERT INTO "ahmeti" VALUES(16,30,'16:30','Atyre që patën frikë nga (dënimi) All-llahu, u thuhet: “ç’është ajo që shpalli Zoti juaj?” Ata thoshin: “Shpalli çdo të mirë!” Ata që bënë vepra të mira, edhe në këtë jet kanë shpërblim të mirë, por shpërblimi i tyre në botën tjetër është shumë më i mirë, e sa vend i mirë është ai i të devotshëmve.');
INSERT INTO "ahmeti" VALUES(16,31,'16:31','Është Xhenneti i Adn-it, ku do të hyjnë, nën të rrjedhin lumenj, aty kanë çka të dëshirojnë, ashtu i shpërblen All-llahu të ruajturit.');
INSERT INTO "ahmeti" VALUES(16,32,'16:32','të cilvet duke qenë të pastër, engjëjt ua marrin shpirtin, duke u thënë: “Selamun alejkum” - gjetët shpëtimin, hyni në xhehet, për hir të asaj që vepruat.');
INSERT INTO "ahmeti" VALUES(16,33,'16:33','A mos presin ata (idhujtarët) diçka tjetër përveç t’u vijnë engjëjt (e t’ua marrin shpirtin) ose t’u vijë urdhëri (dënimi) i Zotit tënd. Ashtu punonin ata që ishin para tyre, pra All-llahu nuk u bëri padrejtësi atyre, por ata ishin që vetvetes i bënë padrejtë.');
INSERT INTO "ahmeti" VALUES(16,34,'16:34','Andaj, ata i goditi dënimi i të këqijave që i bënë dhe i kaploi (i përfshiu ajo e keqe) ajo me të cilën ata talleshin.');
INSERT INTO "ahmeti" VALUES(16,35,'16:35','Ata që i bënin shok zotit (idhujtarët) thanë: “Sikur të kishte dashur All-llahu, as ne, eas prindërit tanë nuk do të adhuronin asnjë send pos Tij, dhe nuk do të ndalonim (nuk do të konsideronim të ndaluar) asnjë send poa (ndalesave të) Tij. Kështu (këso gënjeshtrash) bënin edhe ata që ishin para tyre. Po a mos kanë të dërguarit obligim tjetër, përveç që t’i njoftojnë qartazi?');
INSERT INTO "ahmeti" VALUES(16,36,'16:36','Ne dërguam në çdo popull të dërguar që t’u thonë: “Adhuroni vetëm All-llahun, e largonu djave (adhurimit të tyre)!” Por, pati nga ata që All-llahu e udhëzoi dhe pati nga ata që isshte i gjykuar me humbje, pra udhëtoni nëpër botë dhe shihni se si ishte fundi i gënjeshtarëve?');
INSERT INTO "ahmeti" VALUES(16,37,'16:37','Nëse ti (Muhammed) pëpiqesh për t’i vënë në rrugë të drejtë ata (die se), All-llahu nuk e udhëzon atë që e ka humbur, dhe për ata nuk ka ndihmëtar.');
INSERT INTO "ahmeti" VALUES(16,38,'16:38','Ata u betuan në All-llahun, me betimin e tyre të fortë se “All-llahu nuk e ringjall atë që vdes!” Po, (i ringjall) ai ësjtë premtim i Tij i prerë, por shumica e njerëzve nuk dinë.');
INSERT INTO "ahmeti" VALUES(16,39,'16:39','I ringjall pë t’ua sqaruar atyre atë pë çka përçaheshin dhe, për t’ua bërë me dije atyre që nuk besuan se me të vërtetë ishin gënjeshtarë.');
INSERT INTO "ahmeti" VALUES(16,40,'16:40','Ne kur dëshirojmë një send vetëm i themi atij: “Bëhu!”, ai menjëherë bëhet.');
INSERT INTO "ahmeti" VALUES(16,41,'16:41','Ata që migruan për hir të All-llahut, e pasi që u torturuan, atyre gjithsesi do t’u mundësojmë vendbanim të mirë edhe në këtë botë, po shpërblimi i botës tjetër është edhe më i madh, sikurse njerëzit ta dinin (për atë shpërblim).');
INSERT INTO "ahmeti" VALUES(16,42,'16:42','Janë ata që bënë durim dhe vetëm Zotit të tyre iu mbështetën.');
INSERT INTO "ahmeti" VALUES(16,43,'16:43','Ne as para teje nuk dërguam tjetër, përveç burra, të cilëve u dhamë shpallje. Ju (idhujtarë) pyetni pra dijetarët (e Tevratit e të Inxhilit) nëse ju nuk dini');
INSERT INTO "ahmeti" VALUES(16,44,'16:44','(i patëm dërguar) Me argumente e me libra të shenjtë. Ty ta zbritëm Kur’anin që t’u shpjegosh njerëzve atë që u është shpallur atyre, me shpresë se do ta studiojnë (Kur’anin).');
INSERT INTO "ahmeti" VALUES(16,45,'16:45','A mos janë të sigurt ata që përgatitën kurthe, se All-llahu nk do t’i trandë ata me tokën, ose nuk do t’u vijë atyre dënimi kah nuk mendojnë?');
INSERT INTO "ahmeti" VALUES(16,46,'16:46','Ose t’i rrëmbejë ata duke qenë në udhëtimet e tyre (për jetë e tregti), e ata nuk mund t’i shpëtojnë asaj.');
INSERT INTO "ahmeti" VALUES(16,47,'16:47','Ose do t’i kapë ata duke qenë në panikë nga frika. Po, s’ka dyshim se Zoti juaj është i butë, i mëshirshëm (andaj nuk ua ngut dënimin).');
INSERT INTO "ahmeti" VALUES(16,48,'16:48','A nuk po shohin ata (keqbërësit) se çdo send që e krijoi All-llahu, e sjell hijen e vet djathtas dhe majtas duke iu perulur All-llahut (duke i bërë sexhde) dhe duke qenë respektues e përulës (e si e mbajnë veten lart disa njerëz).');
INSERT INTO "ahmeti" VALUES(16,49,'16:49','Vetëm All-llahut i bën sexhde çdo gjallesë që është në qiej e që është në tokë, e edhe engjëjt, ata nuk bëjnë kryelartësi.');
INSERT INTO "ahmeti" VALUES(16,50,'16:50','I frikësohen Zotit të tyre që është mbi ta dhe i përmbahen në përpikëri asaj me çka urdhërohen.');
INSERT INTO "ahmeti" VALUES(16,51,'16:51','E, All-llahu tha: “Mos adhuroni dy të aduruar, sepse Ai është vetëm një zot, pra vetëm Mua më keni dronë”.');
INSERT INTO "ahmeti" VALUES(16,52,'16:52','Vetëm e Tij është çka ka në qiej e në tokë, dhe vetëm Atij i takon vazhdimisht adhurimi. A mos i keni frikë dikujt tjetër pos All-llahut?');
INSERT INTO "ahmeti" VALUES(16,53,'16:53','Dhe çdo të mirë që e keni, ajo është prej All-llahut, madje edhe kur ju godet e keqja, ju vetëm te Ai e ngritni zërin (me lutje).');
INSERT INTO "ahmeti" VALUES(16,54,'16:54','Pastaj, kur Ai e largon prej jush dënimin, qe, një grup prej jush i përshkruajnë shok Zotit të tyre.');
INSERT INTO "ahmeti" VALUES(16,55,'16:55','(I bëjnë shok) Dhe ashtu ata përbuzin atë që Ne u dhamë atyre, pra shfrytëzoni (për një kohë) se më vonë do të kuptoni (rrjedhimin).');
INSERT INTO "ahmeti" VALUES(16,56,'16:56','E nga ajo që Ne i furnizuam, ata u ndajnë një pjesë atyre (zotave) që nuk kuptojnë asgjë. Pasha All-llahun, patjetër do të merreni në pëgjegjësi për atë që trillonit.');
INSERT INTO "ahmeti" VALUES(16,57,'16:57','Ata i përshkruajnë All-llahut vajzat. I pastër është Ai nga ajo!, ndërsa vetes së tyre (i përshkruajnë) çka u ka ënda (djemtë).');
INSERT INTO "ahmeti" VALUES(16,58,'16:58','Kur ndonjëri prej tyre lajmërohet me (lindjen) vajzë, fytyra e tij i prishet dhe bëhet pot mllef.');
INSERT INTO "ahmeti" VALUES(16,59,'16:59','fshihet prej njerëzve, për shkak të asaj të keqeje me të cilën u lajmërua (e konsideron bela, e jo dhuratë prej zotit). Mandej, (mendon) a do ta mbajë atë, ashtu i përulur, apo do ta mbulojë atë (të gjallë) në dhe. Sa i keq është ai gjykim i tyre.');
INSERT INTO "ahmeti" VALUES(16,60,'16:60','Shembulli i keq u takon atyre që nuk besojnë botën tjetër, ndërsa All-llahut i takon shembulli më i lartë. Ai është fuqiploti, i urti.');
INSERT INTO "ahmeti" VALUES(16,61,'16:61','Sikur All-llahu t’i dënonte njerëzit (zullumqarë) për shkak të mizorisë së tyre, nuk do të linte mbi të (tokë) asnjë gjallesë, por Ai i lë për më vonë deri në aftin e caktuar, e kur të vijë afati i tyre, për asnjë moment nuk mund ta vonojnë as ta ngutin.');
INSERT INTO "ahmeti" VALUES(16,62,'16:62','E ato *vajzat) që i urrejnë për për vete, ia mveshin All-llahut, gjuhët e tyre shpifin rrenën se, atyre do t’u takojë më e mira (Xhenneti), s’ka dyshim, i tyre është zjarri, dhe se ata janë të parët në të.');
INSERT INTO "ahmeti" VALUES(16,63,'16:63','Pasha All-llahun, Ne dërguam edhe para teje të dërguar ndër popuj, po veprat e tyre (të këqija) ua hijeshoi djalli, se ai është mik i tyre, e ata kanë një dënim të idhët.');
INSERT INTO "ahmeti" VALUES(16,64,'16:64','Ne nuk të shpallëm ty për tjetër Kur’anin, vetëm që t’u sqarosh atyre atë për çka u përçanë, (ta zbritëm) që të jetë udhëzim e mëshirë për njerëzit që besojnë.');
INSERT INTO "ahmeti" VALUES(16,65,'16:65','All-llahu lëshoi prej qiellit ujë (shi0 dhe me të ngjalli tokën pas vdekjes së saj. Në këtë është një argument i fortë për ata që më vëmendje dëgjojnë (e kuptojnë).');
INSERT INTO "ahmeti" VALUES(16,66,'16:66','Ju edhe kafshët keni mësim (përvojë). Ne nga një pjesë e asaj që kanë në barqet e tyre mes ushqimit të tyre dhe gjakut, ju japim të pinni qumësht të pastër, të shijshëm (të lehtë) për ata që e pinë.');
INSERT INTO "ahmeti" VALUES(16,67,'16:67','Edhe nga frutat e hurmës dhe të rrushit nxirrni prej tyre pije (lëngje) dhe ushqim të mirë. S’ka dyshim se edhe në këtë ka fakte (mbi fuqinë e Zotit) për njerëzit që kanë menduar.');
INSERT INTO "ahmeti" VALUES(16,68,'16:68','Zoti yt i dha instinkt bletës: “Ndërto shtëpi nëtpër kodra (male), nëpër drunj (pemë) dhe kulmet që (njerëzit) i ndërtojnë.');
INSERT INTO "ahmeti" VALUES(16,69,'16:69','Pastaj ha nga të gjitha (llojet) frutat dhe futu nëpër rrugët e nënshtruara (e të mësuara) prej Zotit tënd). Nga barqet e tyre (të bletëve) del lëng, ngjyra e të cilit është e ndryshme dhe në të cilin ka shërim (bar - ilaç) për njerëz. Edhe në këtë ka arsye për atë popull që mendon thellë.');
INSERT INTO "ahmeti" VALUES(16,70,'16:70','All-llahu ju krijoi, pastaj Ai ua merr shpirtin. E ka prej jush që shtyhet deri në jetën më të vjetër, ashtu që të mos dijë asgjë nga çka ka ditur. All-llahu është më i dijshmi, më i fuqishmi.');
INSERT INTO "ahmeti" VALUES(16,71,'16:71','All-llahu favorizoi disa prej jush mbi disa tjerë në furnizim. Atyre që u është dhënë përparësia (në furnizim), nuk janë furnizues të atyre që posedojnë (robërve), ata (të gjithë) në të janë të barabartë (furnizues kryesor është All-llahu), A mos duan të mohojnë dhuntinë e All-llahut?');
INSERT INTO "ahmeti" VALUES(16,72,'16:72','All-llahu krijoi për ju bashkëshorte nga vetë loji juaj, e prej bashkëshorteve tuaja - fëmijë e nipa dhe ju furnizoi me (ushqime) të mira. A besojnë ata të pavërtetën, e të mirat e All-llahut i mohojnë?');
INSERT INTO "ahmeti" VALUES(16,73,'16:73','Dhe pos All-llahut adhurojnë çka nuk janë në gjendje t’u sjellin asnjë furnizim nga qiejt as nga toka dhe as që kanë mundësi?');
INSERT INTO "ahmeti" VALUES(16,74,'16:74','Pra, mos i përshkruani All-llahut shembuj! All-llahu i di të gjitha e ju nuk dini.');
INSERT INTO "ahmeti" VALUES(16,75,'16:75','All-llahu sjell shembuj: një rob që është pronë e tjetrit e që nuk ka në dorë asgjë, dhe atë, të cilin ne e kemi furnizuar me një furnizim të mirë, ai jep nga ai (furnizim) fshehtas e haptas. A janë pra ata të dy të barabartë? Falënderues qoftë All-llahu, por shumica e tyre nuk dinë.');
INSERT INTO "ahmeti" VALUES(16,76,'16:76','All-llahu sjel shembull: dy njerëz, njëri prej atyre dyve është memec që nuk ka aftësi për asgjë *as për vete as për të tjerët) dhe vetë ai është barrë për kujdestrarin e tij, pse kudo që ta orientojë, a nuk sjell ndonjë dobi. A është ibarabartë me atë që udhëzon për drejtësi e edhe vetë është në rrugën e drejtë?');
INSERT INTO "ahmeti" VALUES(16,77,'16:77','Vetëm All-llahut i tokon (ta dijë) fshetësia ë qiejve dhe tokës! çështja e katastorfës (e kijametit) është (në shpejtësi) vetëm sa hedh një shikim, ose ajo është edhe më afër. All-llahu është i gjithfuqishëm për çdo send.');
INSERT INTO "ahmeti" VALUES(16,78,'16:78','All-llahu ju nxorri nga barqet e nënave tuaja (si foshnje) që nuk dinit asgjë. Ju pajisi me (shqisa për) të dëgjuar, me të parë dhe me zemër, ashtu që të jeni falënderues.');
INSERT INTO "ahmeti" VALUES(16,79,'16:79','A nuk i shohn ata shpendët fluturues në ajrin e qiellit, të cilët nuk i mban kush, përveç All-llahut. Edhe ne këto ka fakte për njerëzit që besojnë.');
INSERT INTO "ahmeti" VALUES(16,80,'16:80','All-llahu ju bëri shtëpitë tuaja vendbanim të qetë dhe nga lëkurat e kafshëve ju mndësoi të keni shtëpi që lehtë i bartni kur udhëtoni, e edhe kur vendoseni, ndërsa nga leshi dhe qimet e tyre, nga leshi i egër i tyre, petka e shtroja dhe t’i shfrytëzoni për një kohë.');
INSERT INTO "ahmeti" VALUES(16,81,'16:81','All-llahu, nga ajo që krijoi Ai, ju bëri edhe hije, e në kodra vendstrehime, edhe petka që ju mbrojnë prej të nxehtit dhe petka që ju mbrojnë në luftë. Ashtu All-llahu plotëson të mirat nfaj jush, në mënyrë që të dorëzoheni (besoni).');
INSERT INTO "ahmeti" VALUES(16,82,'16:82','Po nëse ata prapësohen (prej besimit), atëherë obligimi yt është komunikimi i qartë.');
INSERT INTO "ahmeti" VALUES(16,83,'16:83','Ata i njohin të e All-llahut, pastaj i injorojnë, e shumica e tyre janë jobesimtarë.');
INSERT INTO "ahmeti" VALUES(16,84,'16:84','E ditën kur prej secilit popull sjellim dëshmitarin, tyre që nuk besuan nuk u jepet leje për arsyetim, e as që kerkohet prej tyre (të arsyetohen).');
INSERT INTO "ahmeti" VALUES(16,85,'16:85','Dhe kur do ta shohin dënimin ata që ishin zullumqarë, atëherë as nuk do t’u lehtësohet, as nuk do t’u jepet afat.');
INSERT INTO "ahmeti" VALUES(16,86,'16:86','Ata ishin idhujtarët kur do t’i shohin idhujt e vet (zotat), thonë: “O Zoti ynë, këta, janë idhujt tanë, të cilët ne i patëm adhuruar pos Teje!” Ata (idhujt) ua kthejnë fjalën: “Ju jeni rrencakë!”');
INSERT INTO "ahmeti" VALUES(16,87,'16:87','Dhe ata (idhujtarët) i dorëzohen All-llahut, e atë çka ata e trillonin u asgjësohet.');
INSERT INTO "ahmeti" VALUES(16,88,'16:88','Atyre që nuk besuan dhe penguan nga rruga e All-llahut, u shtojmë dënim mbi dënim për shkak se bënin shkatërrime.');
INSERT INTO "ahmeti" VALUES(16,89,'16:89','Dhe (përkujtoju njerëzve) atë ditë, kur në secilin popull do të sjellim dëshmitarë kundër atyre. Ne ty të shpallëm librin sqarim për çdo send, udhëzim e mëshirë dhe myzhde për myslimanët.');
INSERT INTO "ahmeti" VALUES(16,90,'16:90','All-llahu urdhëron drejtësi, bamirësi, ndihmë të afërmve, e ndalon nga imoraliteti, nga e neveritura dhe dhuna. Ju këshillon ashtu që të merrni mësim.');
INSERT INTO "ahmeti" VALUES(16,91,'16:91','Meqë keni premtuar, pra zbatojeni premtimin e dhënë ndaj All-llahut, e mos i prishni betimet pasi i keni vërtetuar ato, duke qenë se All-llahun e bëtë garant tuajin. Vërtet, All-llahu e di se çka punoni.');
INSERT INTO "ahmeti" VALUES(16,92,'16:92','Mos u bëni (në çështjet e betimit) si ajo (grua) që e prish tjerrin e saj psi të ketë dredhur fort, e t’i bëni betimet tuaja dredhi (mahtrim) mes jush, për shkak se një popull është më i madh se tjetri. All-llahu vetëm ju sprovon me këtë, e në ditën e gjykimit Ai gjithsesi do t’ju sqarojë atë gjë rreth të cilës kundërshtoheshit.');
INSERT INTO "ahmeti" VALUES(16,93,'16:93','Sikur të dëshironte All-llahu do t’ju bënte një popull (në njëfe) por (sipas dijes së Vet) Ai e lë të humbur atë që do, dhe e udhëëzon tjetrin që do, e patjetër do të përgjigjeni për atë që vepruat.');
INSERT INTO "ahmeti" VALUES(16,94,'16:94','Mos i përdorni betimet tuaja për dredhi mes jush, e të rrëshqitni pas forcimit, e të shijoni të keqen (e dredhisë0 nga rruga e All-llahut, e ju do të keni dënim të madh.');
INSERT INTO "ahmeti" VALUES(16,95,'16:95','Mos e shitni besën e dhënë All-llahut për një vlerë të paktë, pse atë që do ta keni te All-llahu është shumë e dobishme për ju nëse jeni që e dini.');
INSERT INTO "ahmeti" VALUES(16,96,'16:96','Ajo që e keni pranë vetës është e përkohshme, e ajo që është te All-llahu është e përjetshme. E Ne do t’u japim atyre që ishin të durueshëm shpërblimin më të mirë të asaj që vepruan.');
INSERT INTO "ahmeti" VALUES(16,97,'16:97','Kush bën vepër të mirë, qoftë mashkull ose femër, e duke qenë besimtar, Ne do t’i japim atij jetë të mirë (në këtë botë), e (në botën tjetër) do t’u japim shpërblimin më të mirë për veprat e tyre.');
INSERT INTO "ahmeti" VALUES(16,98,'16:98','Kur të lexosh Kur’anin kërko mbrojtjen e All-llahut prej djallit të mallkuar.');
INSERT INTO "ahmeti" VALUES(16,99,'16:99','Vërtet, ai (shejtani) nk ka kurrfarë fuqie kundër atyre që besuan dhe i janë mbështetur Zotit të tyre.');
INSERT INTO "ahmeti" VALUES(16,100,'16:100','Mbizotërimi i tij është vetëm mbi ata që përkrahin atë dhe mbi ata që për shkak të tij u bënë idhujtarë.');
INSERT INTO "ahmeti" VALUES(16,101,'16:101','Kur një ajet e zavendësojmë me një ajet tjetër - e All-llahu di më së miri se çka shpall - ata thonë: “Ti (Muhammed) je vetëm trillues” Jo, por shumica e tyre nuk dinë.');
INSERT INTO "ahmeti" VALUES(16,102,'16:102','Thuaj; “Atë (Kur’anin) e solli “Ruhul Kudus” - shpirti ishenjtë - plot vërtetësi nga Zoti yt, për t’i përforcuar edhe më ata që që besuan, dhe për të qenë udhërrëfyes e myzhde për myslimanët.');
INSERT INTO "ahmeti" VALUES(16,103,'16:103','Ne dimë shumë mirë se ata thonë: “Atë (Muhammedin) është kah e mëson një njeri!” Mirpo, gjuha e atij nga i cili anojnë (supozojnë) ata është jo arabe (e paqartë), e kjo (e Kur’anit) është gjuhë arabe e stilit të lartë e të qartë.');
INSERT INTO "ahmeti" VALUES(16,104,'16:104','S’ka dyshim se ata që nuk besojnë ajetet (e Kur’anit) a All-llahut, ata nuk i drejto All-llahu në rrugën e drejtë dhe ata kanë një dënim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(16,105,'16:105','Gënjeshtrën e trillojnë vetëm ata që nuk i besojnë argumentet e All-llahut; të tillët janë ata gënjeshtarët.');
INSERT INTO "ahmeti" VALUES(16,106,'16:106','Ai që pas besimit të tij e mohon All-llahun, me përjashtin të atij që dhunohet (për të mohuar), e zemra e tij është e bindur plotësishtme besim, por fjala është për atë që ia hap gjoksin mosbesimit, ata i ka kapur hidhërimi nga All-llahu dhe ata kanë një dënim të madh.');
INSERT INTO "ahmeti" VALUES(16,107,'16:107','Këtë (dënim të madh) ngase ata më tepër e deshtën jetën e kësajbote se sa jetën e botës tjetër, e Alllahu nuk e udhëzon njerëzit jobesimtarë.');
INSERT INTO "ahmeti" VALUES(16,108,'16:108','Të tilët janë ata që All-llahu ua mbylli zemrat, dëgjimin dhe pamjen e tyre dhe të tillët janë ata naivët.');
INSERT INTO "ahmeti" VALUES(16,109,'16:109','Është e vërtetë se ata në botën tjetër, mu ata janë të dështuarit.');
INSERT INTO "ahmeti" VALUES(16,110,'16:110','Pastaj Zoti yt, atyre që pasi u torturuan, migruan, mandej luftuan dhe qëndruan, dhe pas të gjitha këtyre vuajtjeve, s’ka dyshim se Zoti yt atyre do t’ua falë dhe do t’i mëshirojë (Ai është që fal, është mëshirues).');
INSERT INTO "ahmeti" VALUES(16,111,'16:111','(Përkujto) Ditën që vjen kur secili njeri do të përpiqet për vetveten dhe secilit njeri do t’i plotësohet ajo që e meriton. e nuk u bëhet padrejtë.');
INSERT INTO "ahmeti" VALUES(16,112,'16:112','All-llahu sjell si shembull një fshat (vendbanim) që ishte i sigurt dhe i qetë, të cilit i vinte furnizimi nga të gjitha anët me bollëk, kurse ata (banorët) i përbuzën të mirat e All-llahut. Atëherë All-llah, për shkak të sjelljes së tyre, ua veshi petkun (ua ngjeshi) e urisë dhe të frikës.');
INSERT INTO "ahmeti" VALUES(16,113,'16:113','Atyre u pat ardhur i dërguari nga mesi i tyre, e ata e përgënjeshtruan atë, andaj i kapi dënimi që ishin zullumqarë.');
INSERT INTO "ahmeti" VALUES(16,114,'16:114','E ju (besimtarë), hani, pra. nga të mirat që ua dha All-llahu, të lejuara e të pastra, jimi mirënjohës për dhuntitë e All-llahut, nëse jeni të sinqertë në adhurim vetëm mdaj Tij.');
INSERT INTO "ahmeti" VALUES(16,115,'16:115','Ai ua ndaloi juve vetëm cofëtinën, gjakun, mishin e derrit dhe atë që theret jo në emër të All-llahut. E kush detyrohet (t’i hajë) duke mos qenë dhunues dhe duke mos e teruar (nëngrënie), All-llahu fal dhe është mëshirues.');
INSERT INTO "ahmeti" VALUES(16,116,'16:116','Mos i thoni asaj rrenës së gjuhve tuaja: “Kjo është hallall e kjo është haram” e të shpifni ndaj All-llahut rrenën. Vërtet, ata që shpifin rrenën ndaj All-llahut, nuk kanë shpëtim.');
INSERT INTO "ahmeti" VALUES(16,117,'16:117','Kanë një përjetim të vogël (në këtë jetë), e do të kenë dënim të padurueshëm.');
INSERT INTO "ahmeti" VALUES(16,118,'16:118','E atyre që ishin jehudi (çifutë) u patëm ndaluar atë që të kemi treguar më parë, po Ne nuk u bëmë padrejtë atyre, por ata vetvetes i patën bërë padrejtë.');
INSERT INTO "ahmeti" VALUES(16,119,'16:119','Pastaj Zoti yt për ata që nga mosdija bënë punë të këqija, e pastaj u penduan dhe bënë mirë, s’ka dyshim se Zoti yt është që shumë fal dhe shumë mëshiron.');
INSERT INTO "ahmeti" VALUES(16,120,'16:120','Vërtet, Ibrahimi ka qenë shembëlltyrë e të mirave, adhurues i All-llahut, besimtarë i drejtë dhe nuk a qenë nga idhujtarët.');
INSERT INTO "ahmeti" VALUES(16,121,'16:121','Falënderues për të mirat e Tij. Ai (All-llahu) e zgjodhi atë (për pejgamber) dhe e udhëzoi në rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(16,122,'16:122','Atë ne e pajisëm me të mira në këtë botë, e në botën tjetër ai është me më të mirët.');
INSERT INTO "ahmeti" VALUES(16,123,'16:123','Pastaj, ty të shpallëm: “Ndiqe fenë e drejtë të Ibrahimit, se ai nuk ka qenë nga idhujtarët.');
INSERT INTO "ahmeti" VALUES(16,124,'16:124','E shtuna (festimi ose belaja e saj) u është përcaktuar vetëm atyre që u kundërshtuan rreth saj. E Zoti yt do të gjykojë mes tyre në ditën e gjykimit për atë që ata kundërshtoheshin.');
INSERT INTO "ahmeti" VALUES(16,125,'16:125','Ti (Muhammed) thirr për në rrugën e Zotit tënd me urtësi e këshillë të mirë dhe polemizo me ata (kundërshtarët) me atë mënyrë që është më e mira. Zoti yt është Ai që e di më së miri atë që është larguar nga rruga e Tij dhe Ai di më së miri për të udhëzuarit.');
INSERT INTO "ahmeti" VALUES(16,126,'16:126','Në qoftë se doni të merrni hak, atëherë ndëshkoni në atë masë sa jeni ndëshkuar ju; e nëse duroni pa dyshim ai është më i mirë për ata që durojnë.');
INSERT INTO "ahmeti" VALUES(16,127,'16:127','Pra, të jesh i durueshëm! Durimi yt është vetëm m ndihmën e All-llahut. Ti mos u pikëllo për ata, as mos u brengos për dredhitë që bëjnë ata!');
INSERT INTO "ahmeti" VALUES(16,128,'16:128','S’ka dyshim se All-llahu është me ata që janë të devotshëm (që ruhen prej të këqijave) dhe me ata që janë bamirës.');
INSERT INTO "ahmeti" VALUES(17,1,'17:1','Pa të meta është Lartëmadhëria e Atij që robin e Vet e aloi në një pjesë të natës prej Mesxhidi Haramit (prej Qabes) gjer në Mesxhidi Aksa (Bejti Mukaddes), rrethinën e të cilës Ne e kemi bekuar, (ia bëmë këtë udhëtim) për t’ia treguar atij disa nga argumentet Tona. vërtet, Ai është dëgjuesi (i fjalëve të Mu hamedit), pamësi (i punëve të Muhammedit).');
INSERT INTO "ahmeti" VALUES(17,2,'17:2','Edhe Musai Ne i kemi dhënë librin dhe atë (librin) e bëmë udhërrëfyes për beni israilët që të mos marrin, përveç Meje, ndonjë që i besojnë çështjet.');
INSERT INTO "ahmeti" VALUES(17,3,'17:3','Pasardhësit të atyre që i batëm (ishpëtuam) së bashku me Nuhun (bëhuni mirënjohës!) Vërtet ai ishte rob shumë falënderues.');
INSERT INTO "ahmeti" VALUES(17,4,'17:4','Ne u kumtuam në libër beni israilve: “Ju do të bëni shkatërrime dy herë në tokë dhe do të tejkaloni duke bërë zullum të madh”.');
INSERT INTO "ahmeti" VALUES(17,5,'17:5','Kur erdhi koha e premtimit të parë, Ne dërguam kundër jush njerëz Tanë, që ishin të fuqishëm e të ashpër (sulmues) dhe ata hulumtuan (rreth e përqark) përreth vendit (duke mos u frikësuar). E ai (ndëshkim) ishte vendim i preë.');
INSERT INTO "ahmeti" VALUES(17,6,'17:6','Pastaj, Ne ua kthyem juve mbisundimin kundër atyre, ju përforcuam me pasuri e me djem dhe ju shtuam më së shumti.');
INSERT INTO "ahmeti" VALUES(17,7,'17:7','Nëse bëni mirë, bëni për vete, e nëse bëni keq, bëni kundër vetes. E kur vjen koha e fundit (herën e dytë Ne i dërgojmë) që t’ju shëmtojnë të fytyrat tuaja, që të hyjnë në xhaminë (Aksa) sikurse hynë heërn e parë dhe që të shkatërrojnë rrënjësisht atë që arrijnë.');
INSERT INTO "ahmeti" VALUES(17,8,'17:8','Zoti juaj do t’ju mëshirojë (nëse pendoheni), e nëse ju i ktheheni 9të keqes), kthehemi (me ndëshkim) edhe Ne. E Xhehennemin e kemi bërë burg për jobesimtarët.');
INSERT INTO "ahmeti" VALUES(17,9,'17:9','Është e vërtet se ky Kur’an udhëzon për atë rrugë që ështëmë se e vërteta, e besimtarët që bëjnë vepra të mira i përgëzon se ata pa dyshim do të kenë shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(17,10,'17:10','Ndëkaq, për ata që nuk e besojnë botën tjetër, Ne kemi përgaditur një dënim të rëndë.');
INSERT INTO "ahmeti" VALUES(17,11,'17:11','Njeriu (e tepron) lutet për keq, ashtu sikurse lutet për mirë, (po Zoti nuk e pranon lutjen e tij për keq); njeriu (nga vetë natyra e tij) është i ngutshëm.');
INSERT INTO "ahmeti" VALUES(17,12,'17:12','Ne i bëmë natën dhe ditën dy fakte (që dokumentojnë për fuqinë Tonë), e shenjën e natës e shlyem me (e errësuam), e shenjën e ditës e bëmë të ndritshme që të mund të angazhoheni për shfrytëzimin e begative të dhuruara nga Zoti juaj, dhe që të mësoni njehësimin e vjetëve dhe llogaritjen (e kohës). Ne kemi sqaruar çdo send në mënyrë të hollësishme.');
INSERT INTO "ahmeti" VALUES(17,13,'17:13','Secili njeri ia kemi njgjeshur në qafë fluturakën (shënimin - për veprimin) e tij, në ditën e gjykimit, Nee do t’ia prezentojmë atij libër të hapur,');
INSERT INTO "ahmeti" VALUES(17,14,'17:14','“Lexo librn tënd, mjafton të jesh sot llogaritës i vetvetes”.');
INSERT INTO "ahmeti" VALUES(17,15,'17:15','Kush e udhëzon veten në rrugën e drejtë, ai e ka udhëzuar vetëm vetveten e vet, e kush e ka bërë humbjen kundër vetvetes së vet, e askushnuk do ta batë barrën e tjetrit. E Ne nuk dënuam askë para se t’ia dërgojmë të dërguarin.');
INSERT INTO "ahmeti" VALUES(17,16,'17:16','kur duam të shkatërrojmë ndonjë vend (popull), i urdhërojmë pasanikët (parinë) e atyhit (të jenë në rrugë), e ata kundërshtojnë; atëherë zbatohet dënimi ei merituar kundër tyre dhe i shkatërrojmë krejtësisht.');
INSERT INTO "ahmeti" VALUES(17,17,'17:17','Sa e sa gjenerata i kemi shkatërruar pas Nuhut. Mjaft është Zoti yt vëzhgues i mëkateve të robëve të Tij.');
INSERT INTO "ahmeti" VALUES(17,18,'17:18','kush është që ka për qëllim vetëm kët botë, Ne atij që duam i japim në të aq sa duam, e pastaj atij i bëjmë të hyjë në Xhehennem i nënqmuar, i përbuzur.');
INSERT INTO "ahmeti" VALUES(17,19,'17:19','E kush e ka për qëllim botën tjetër, duke qenë ai besimtarë, përpiqet për të ashtu si i tako asaj, angazhimi i tyre do të jetë i pranishëm (te Zoti).');
INSERT INTO "ahmeti" VALUES(17,20,'17:20','Ne secilit atyre dhe atyre i japim nga të mirat e Zotit tënd dhe dhënia e Zotit tënd nuk është e kufizuar.');
INSERT INTO "ahmeti" VALUES(17,21,'17:21','Shiko se si Ne i kemi dallluar disa prej tjerëve, po dallimi në botën tjetër është më i madh dhe është përparësi më e madhe.');
INSERT INTO "ahmeti" VALUES(17,22,'17:22','E mos shoqëro me All-llahun ndonjë zot tjetër, e të bëhesh i qortuar i papërkrahur.');
INSERT INTO "ahmeti" VALUES(17,23,'17:23','Zoti yt ka dhënë urdhër të prerë që të mos adhuroni tjetër pos Tij, që të silleni në mënyrë bamirëse ndaj prindërve. Nëse njërin prej tyre, ose që të dy, i ka kapur pleqëria pranë kujdesit tënd, atëherë mos u thuaj atyre as “of - oh”, as mos u bë i vrazhdë ndaj tyre, po atyre thuaju fjalë të mira (të buta respektuese).');
INSERT INTO "ahmeti" VALUES(17,24,'17:24','Dhe në shenjë mëshire shtrije pranë tyre krahun përulës e respektues dhe thuaj: “Zoti im! mëshiroi ata të dy, sikurse më edukuan mua kur isha i vogël”.');
INSERT INTO "ahmeti" VALUES(17,25,'17:25','Zoti juaj e di më së miri atë që keni në shpirtin tuaj. Nëse keni qëllime të mira, vërtet Ai u fal atyre që pendohen.');
INSERT INTO "ahmeti" VALUES(17,26,'17:26','Dhe jepi çdo të afërmi të drejtën që i takon, edhe të varfërit, edhe atij në kurbet, po mos shpenzo tepëre pa rrugë.');
INSERT INTO "ahmeti" VALUES(17,27,'17:27','Ata që shpenzojnë tepër (mastrafxhinjtë) janë vllezër (në veprim) të djajve, e djalli është përbuzës i madh i Zotit të Tij.');
INSERT INTO "ahmeti" VALUES(17,28,'17:28','Nëse detyrohesh t’ua kthesh shpinën atyre (të afërme etj.), duke kërkuar mëshirën, të cilën e shpreson nga Zoti yt (furnizim për t’i ndihmuar ata), atëherë thuaju atyre fja; ë të mira (të lehta).');
INSERT INTO "ahmeti" VALUES(17,29,'17:29','Dhe mos e bën dorën tënde të lidur për qafe (mos u bë koprrac), e as mos e shtrij në tërësi, e të bmetesh i qortuar dhe duarthatë (nga shpenzimi i tepërt).');
INSERT INTO "ahmeti" VALUES(17,30,'17:30','S’ka dyshim se Zoti yt është Ai që furnizon me bollëk atë që do dhe ia kufizon atij që do. Ai hollësisht e di për robtë e vet dhe i vështron.');
INSERT INTO "ahmeti" VALUES(17,31,'17:31','Ju mos i mbytni fëmijët tuaj nga frika e varfërisë, se Ne ua sigurojmë furnizimin atyre dhe juve, e mbytja e tyre është mëkat i madh.');
INSERT INTO "ahmeti" VALUES(17,32,'17:32','Dhe mos iu afroni amoralitetit (zinasë), sepse vërtet ai shtë vepër e shëmtuar dhe është një rrugë e keqe.');
INSERT INTO "ahmeti" VALUES(17,33,'17:33','Dhe mos mbytni njerin që All-llahu ka ndaluar (mbytjen e tij), përveç me drejtësi (që e kërkon sheriati). E kush mbytet pa të drejtë (pa faj), kujdestarit të tij Ne i kemi dhënë të drejtë (të kërkojë drejtësinë), e ai të mos kalojë kufirin në mbytje derisa ai është i ndihmuar (pprej Zotit).');
INSERT INTO "ahmeti" VALUES(17,34,'17:34','Mos përdoroni pasurinë e jetimit ndryshe vetëm në mënyrë të mirë (për t’ia shtuar), derisa ai të arijë pjekurinë e vet. Premtimin zbatonie, pse për premtim ka përgjegjësi.');
INSERT INTO "ahmeti" VALUES(17,35,'17:35','Masën mbusheni kur matni (me enë) dhe peshoni me peshojë të drejtë (precize). Kjo është më e dobishme dhe ka përfundim më të mirë.');
INSERT INTO "ahmeti" VALUES(17,36,'17:36','Mos iu qas asaj për të cilën nuk kë njohuri, pse të dëgjuarit, të pamët dhe zemra, për të gjitha këto ka përgjegjësi.');
INSERT INTO "ahmeti" VALUES(17,37,'17:37','Dhe mos ec nëpër tokë me mburrje, pse ti as nuk mund ta çashë tokën as nuk mund ta arrishë lartësinë e maleve.');
INSERT INTO "ahmeti" VALUES(17,38,'17:38','E keqja e të gjitha këtyre është e urrejtur te Zoti yt.');
INSERT INTO "ahmeti" VALUES(17,39,'17:39','Këto janë nga urtësia që Zoti yt të shpalli ty. Mos shoqëro me All-llahun ndonjë zot tjetër e të hidhesh në Xhehennem i qortuar dhe i larguar.');
INSERT INTO "ahmeti" VALUES(17,40,'17:40','A mos u dalloi Zoti juaj me djem, e për vete zgjodhi melaike që ju i mendoni femra? Vërtet, ju jeni duke thënë fjalë të mëdha.');
INSERT INTO "ahmeti" VALUES(17,41,'17:41','Ne sqaruam në mnyrë të ndryshme (argumentet) në këtë Kur’an, ahtu që ata të nxjerrin përvojë, por kjo nuk u shtoi atyre tjetër, vetëm se largim (nga e vërteta).');
INSERT INTO "ahmeti" VALUES(17,42,'17:42','Thuaj: “sikur të kishte zota të tjerë përveç Atij, sikundër thonë ata, atëherë ata (zotat) do të kërkonin rrugë për mbizotërim ndaj të Madhëruarit (për t’ia marrë pushtetin).');
INSERT INTO "ahmeti" VALUES(17,43,'17:43','I pa të meta është Ai, Lartësia e Tijështë e madhe nga ajo që thonë ata.');
INSERT INTO "ahmeti" VALUES(17,44,'17:44','Lartmadhëri të merituar i shprehin Atij shtatë qiejt e toka dhe çka ka në to, e nuk ka asnjë send që nuk e madhëron (nuk i bënë tesbihë), duke i shprehur falënderim Atij, por ju nuk kuptoni atë madhërim të tyre (pse nuk është në gjuhën e tuaj). Ai është i butë dhe fal shumë.');
INSERT INTO "ahmeti" VALUES(17,45,'17:45','Kur ti e lexon Kur’anin, në mes teje dhe atyre që nuk e besojnë botën tjetër, Ne e vëmë një perde të padukshme.');
INSERT INTO "ahmeti" VALUES(17,46,'17:46','Ndërkaq, në zemrat e tyre vëmë një mbulesë dhe shurdhim të thellë të veshëve të tyre që nuk mund ta kuptojnë atë. E kur e përmend ti në Kur’an Zotin tënd, vetëm Atë, ata ikin mbrapa dhe largohen.');
INSERT INTO "ahmeti" VALUES(17,47,'17:47','Shih se si të mveshin ty epitete (i çmendur, poet, i magjepsur), andaj ata kanë humbur dhe nuk mund të gjejnë rrugën (e vërtetë).');
INSERT INTO "ahmeti" VALUES(17,48,'17:48','Ata thanë: “Apasi të bëhemi eshtra të kalbur, dhe e pluhur, a mos do të bëhemi krijesë e re e ringjallur?”');
INSERT INTO "ahmeti" VALUES(17,49,'17:49','Thuaj: “Edhe nëse të jeni gurë a hekur!”');
INSERT INTO "ahmeti" VALUES(17,50,'17:50','Ose ndonjë krijesë që ju duket e madhe! Ata do të thonë: “Kush do të në rikthejë neve në jetë?” Thuaj: “Ai që ju krijoi për të parën herë!?? E ata do të tundin kokat e tyre nga ti e do të thonë: “Kur është ajo (ringjallja)?” Thuaj: “Ndoshta do të jetë afër!”');
INSERT INTO "ahmeti" VALUES(17,51,'17:51','(ringjallja është) Atë ditë kur Ai j thërret, kurse ju i përgjigjeni të përulur e duke lavdëruar Atë, e ju duket se ju qëndruat (në dynja) vetëëm pak kohë.');
INSERT INTO "ahmeti" VALUES(17,52,'17:52','E ti robve të Mi thuaju: “Le ta thon ata që është më e mira, pse djalli ndësen mes tyre, e është e ditur se djalli është armik i hapët i njeriut”.');
INSERT INTO "ahmeti" VALUES(17,53,'17:53','Zoti i juaj më së miri e di gjendjen tuaj, nëse Ai do, ju mëshiron dhe nëse Ai do, ju dënon, e ty (Muhammed) nuk të kemi dërguar rojë të tyre (nuk të besuam ty punën e tyre e t’i fusësh me dhunë në besim).');
INSERT INTO "ahmeti" VALUES(17,54,'17:54','Zoti yt e di më së miri gjenden e atij që është në qiej e tokë. Ne kemi dalluar disa pejgamberë nga të tjerët, prandaj, Davudit i kemi dhënë Zebur (shënime).');
INSERT INTO "ahmeti" VALUES(17,55,'17:55','Thuaj: “Ftoni ata, të cilët i menduat (zota) pos Atij, e ata nuk mund të heqin prej jush mjerimin, as nuk mund të mënjanojnë”.');
INSERT INTO "ahmeti" VALUES(17,56,'17:56','Të tillë (zotat) që këta po lusin (pos Zotit), vetë ata (zotat e tyre) kërkojnë afrimin (te Zoti i vërtetë), shpresojnë në mëshirën e Tij dhe i frikësohen dënimit të Tij. Vërtet, dënimit të Zotit tënf ëshë për t’iu ruajtur.');
INSERT INTO "ahmeti" VALUES(17,57,'17:57','Nuk ka asnjë nga fshatrat (nga vendbanimet e kundërshtarëve) e që Ne nuk do ta shkatërrojmë ose ndëshkojmë atë me dënim të ashpër para ditës së kijametit. Kjo është theksuar në Libër (lehvi mahfudh).');
INSERT INTO "ahmeti" VALUES(17,58,'17:58','Neve nuk na pengoi asgjë që t’u sjellim argumente (mrekulli0, pos asaj që ata të parët i përgënjeshtruan ato. Ne i dhamë Themudit deven (mrekulli) konkrete, e ata e mohuan atë. E mrekullitë (ose fenomenet natyrore që shkatërrojnë), Ne nuk i dërgojmë për tjetër vetëm për frikësim.');
INSERT INTO "ahmeti" VALUES(17,59,'17:59','përkujto, kur Ne të thamë: “Zoti yt ka përfshirë (me dijen e Vet) të gjithë njerëzit (e di që këta nuk besojmë edhe në ju sjellësh mrekulli). E atë pamjen tënde që të treguam (natën e Miraxhit) dhe pemën e mallkuar (të përmendur në Kur’an), nuk ibëmë tjetër vetëm sprovë për njerëz. Ne i frikësojmë ata (idhujtarët), po nuk shton ai tjetër vetëm se rebelim të madh.');
INSERT INTO "ahmeti" VALUES(17,60,'17:60','Përkujto kur Ne u thamë engjëjve: “Bëni sexhde Ademit!” (në shenjë përshëndetjeje) e ata i bënë, me përjashtim të Iblisit. Ai tha: “A t’i bej sexhde atij që e krijove nga balta?”');
INSERT INTO "ahmeti" VALUES(17,61,'17:61','(mandej vazhdoi e) tha: “A e sheh (i tha Zotit) këtë që Ti e vlerësove mbi mua, nëse më lë të jetojë deri në ditën e kijametit, unë gjithsesi do t’i shfaros pasardhësit e tij, më përjashtim të një pakice”.');
INSERT INTO "ahmeti" VALUES(17,62,'17:62','(Zoti) tha: “Shko, ekush prej tyre vjen pas teje, shpërblimi juaj është Xhehennemi, shpërblim i plotë”.');
INSERT INTO "ahmeti" VALUES(17,63,'17:63','Dhe me atë alarmin e zërit tënd, mashtroje atë që mundesh prej tyre, me kalorësit e këmbësorët tu thirri (bërtitu) ata, përzieju me ta në pasuri dhe në fëmijë dhe premtoju atyre, po shejtani nuk u premton tjetër, përveç mashtrim.');
INSERT INTO "ahmeti" VALUES(17,64,'17:64','Në të vërtet, ti nuk ke kurrfarë fuqie ndaj robërve të Mi. Mbrojtja e Zotit tënd është e mjaftueshme (për ata).');
INSERT INTO "ahmeti" VALUES(17,65,'17:65','Zoti juaj është Ai që për ju mundëssoi lundrimin e anijeve në det në mënyrë që të kërkoni nga të mirat e Tij. Vërtet, Ai është shumë mëshirues ndaj jush.');
INSERT INTO "ahmeti" VALUES(17,66,'17:66','E kur ju kap juve paniku (frika) në det, i humbni (nga kujtesa) ata që i lutni, u mbete vetëm Ai (All-llahu), mirpo pasi që Ai t’ju shpëtojë e të arrini në tokë, ia ktheni shpinën. Ashtu, njeriu është përbuzës.');
INSERT INTO "ahmeti" VALUES(17,67,'17:67','A mos jeni të siguruar që nuk do t’u shafitë në tokë ose nuk do t’ju lëshojëndonjë shtërngatë e ju nuk do të keni mbrojtës.');
INSERT INTO "ahmeti" VALUES(17,68,'17:68','A mos jeni të sigurt se Ai nuk do t’ju kthejë njëherë tjetër në det, et’ju lëshojë ndonje furtunë të erës, e për shkak të mosbesimit tuaj t’ju fndosë, e pastaj nuk do të gjeni kiush do të hakmerrej kundër nesh për ju.');
INSERT INTO "ahmeti" VALUES(17,69,'17:69','Ne, vërtet nderuam pasardhësit e Ademit (njerëzit), u mundësuam të udhëtojnë hipur në tokë e në det, ibegatuam me ushqime të mira, i vlerësuam ata (i lartësuam) ndaj shumicës së krijesave që Ne i krijuam.');
INSERT INTO "ahmeti" VALUES(17,70,'17:70','(përkujto) Ditën kur do të thërrasim secilin njeri me prijësin (librin, pejgamberin, shënimet e veprave) e tyre. E atij që i jepet libri i tij nga e djathta e tij, të tillët i lexojnë shënimet e tyre (të mira), dhe nuk u bëhet e padrejtë asnjë fije.');
INSERT INTO "ahmeti" VALUES(17,71,'17:71','E kush ka qenë i verbër në këtë (botë), ai do të jetë edhe në botën tjetër i verbër dhe më i humburi i rrugës së drejtë.');
INSERT INTO "ahmeti" VALUES(17,72,'17:72','Në të vërtetë ata (idhujtarët) gati arritën të sprovijnë ty nga ajo që Ne ta shpallëm, e të trillosh tjetër nga çka të shpallëm, e atëherë do të zinin ty mik.');
INSERT INTO "ahmeti" VALUES(17,73,'17:73','Dhe sikur Ne të mos të kishim forcuar ty, ti gati anove diçka pak te ta.');
INSERT INTO "ahmeti" VALUES(17,74,'17:74','E atëherë Ne do ta shijonim mundimin dyfish të kësaj jete dhe dyfish të jetës tjetër, e pastaj ti nuk do të gjeje mbrojtës kundër nesh.');
INSERT INTO "ahmeti" VALUES(17,75,'17:75','E atëherë Ne do ta shijonim mundimin dyfish të kësaj jete dhe dyfish të jetës tjetër, e pastaj ti nuk do të do të gjeje mbrojtës kundër nesh.');
INSERT INTO "ahmeti" VALUES(17,76,'17:76','Ata gati të shqetësuar në tokë (në Mekë) për të përzënë prej saj, po atëherë edhe ata nuk do të mbeteshin pas teje (pas pak kohe do të shkatërroheshin).');
INSERT INTO "ahmeti" VALUES(17,77,'17:77','Ky është ligji i Tij edh me të dërguarit që i dërgoi para teje. E në ligjin Tonë ti nuk gjen ndryshim.');
INSERT INTO "ahmeti" VALUES(17,78,'17:78','Fale namazin kur zbret (nga zeniti) dielli, e deri në errësirën e natës dhe duke bërë lutjen (namazin) e agimit. Vërtet, lutja e agimit është e përcjellur.');
INSERT INTO "ahmeti" VALUES(17,79,'17:79','Dhe në një pjesë të natës zgjohu me të (me Kur’anin - namazin), është e sigurt se Zoti yt do të ngrisë ty në një vend të lavdishëm.');
INSERT INTO "ahmeti" VALUES(17,80,'17:80','Dhe thuaj: “Zoti im, më shpjer në vend të mirë dhe më nxjerr në mënyrë yë ndershme dhe nga ana Jote më dhuro fuqi ndihmuese”.');
INSERT INTO "ahmeti" VALUES(17,81,'17:81','Dhe thuaj: “Erdhi e vërteta e u zhduk e kota”. Vërtet, e kota gjithnjë ka qenë e zhdukur.');
INSERT INTO "ahmeti" VALUES(17,82,'17:82','Ne të shpallim Kur’anin që është shërim për besimtarët, kurse jobesimtarëve nuk u dhton tjetërpërpos dëshprim.');
INSERT INTO "ahmeti" VALUES(17,83,'17:83','E kur e begatojmë (me të mira) njerin, ai prapësohet dhe largohet i mashtruar, e kur e godet e keqja, ai humb shpresën.');
INSERT INTO "ahmeti" VALUES(17,84,'17:84','Thual: “Secili vepron sipas rrugës së vet, e Zoti juaj e di më së miri për atë që është në rrugë më të drejtë”.');
INSERT INTO "ahmeti" VALUES(17,85,'17:85','Të pyesin ty për shpirtin; Thuaj: “shpirti është çështëje që i përket vetëm Zoti tim, e juve ju është dhënë fort pak dije”.');
INSERT INTO "ahmeti" VALUES(17,86,'17:86','Po të kishim dëshiruar, Ne do ta shlyenim nga gjoksi yt atë që të shpallëm ty, e ti nuk do të gjeje mbrojtës që do të ruane.');
INSERT INTO "ahmeti" VALUES(17,87,'17:87','Por kjo është mëshirë nga Zoti yt dhe mirësia e Tij ndaj teje, vërtet është e madhe.');
INSERT INTO "ahmeti" VALUES(17,88,'17:88','Thuaj: “Edhe sikur të bashkoheshin njerëzit dhe xhinët për të sjellë një Kur’an të tillë, ata nuk do të mund të bënin si ky sado që do ta ndihmonin njëri - tjetrin”.');
INSERT INTO "ahmeti" VALUES(17,89,'17:89','Ne sqruam njerëzve në këtë Kur’an shëmbuj (argumente) të çdo lloji, e shumica e njerëzve nuk deshi tjetër, vetëm mohimin.');
INSERT INTO "ahmeti" VALUES(17,90,'17:90','Ata thanë: “Nuk të besojmë ty derisa që të na nxjerrësh prej tokës burime.');
INSERT INTO "ahmeti" VALUES(17,91,'17:91','Ose, (nuk të besojmë deri që) të kesh kopshte me hurma e me rrush, e të bësh të rrjedhin vazhdimisht lumenj në mesin e tyre.');
INSERT INTO "ahmeti" VALUES(17,92,'17:92','Ose, (nuk të besojmë derisa) të bjerë mbi qiell copa - copa, ashtu si po mendon ti (se do të na dënojë Zoti), apo të na sjellësh All-llahun dhe engjëjt pranë nesh.');
INSERT INTO "ahmeti" VALUES(17,93,'17:93','Ose të kesh një shtëpi prej ari, apo të ngjirtesh lart në qiell, po ne nuk të besojmë për ngjitjen tënde derisa të na sjellësh një libër që ta lexojmë?!” Thuaj “Subhanallah - i Madhërishëm është Zoti im, a mos jam unë tjetër vetëm se njeri, pejgamber?”');
INSERT INTO "ahmeti" VALUES(17,94,'17:94','Po njerëzit nuk i pengojnë tjetër që të besojnë, kur u erdhi atyre shpallja, vetëm se thoshin: “A thua All-llahu dërgoi njerin pejgamberë!”');
INSERT INTO "ahmeti" VALUES(17,95,'17:95','Thuaj: “Sikur të kishte në tokë engjëj që ecin qetë (si njerëzit), Ne do t’u çonim atyre nga qielli engjëj pejgamberë!”');
INSERT INTO "ahmeti" VALUES(17,96,'17:96','Thuaj: “All-llahu më mjafton për dëshmitar mes meje dhe mes jush, Ai hpllësisht injeh dhe i sheh robtë e vet”.');
INSERT INTO "ahmeti" VALUES(17,97,'17:97','Atë që All-llahu e drejton në rrugë të drejtë, ai është udhëzuar, e atë që e le të humbur, për ata nuk do të gjesh ndihmëtarë përveç Atij. Në ditën e kijametit Ne i tubojmë ata duke i tërhequr rrëshqanas me fytyra për dhe, të verbër, memecë të shurdhët. Vendi ti tyre është Xhehennemi, sa herë që të pushojë flaka Ne u shtojmë flakën e zjarrit.');
INSERT INTO "ahmeti" VALUES(17,98,'17:98','Ai (dënim) është meritë e tyre, ngase ata mohuan argumentet tona dhe thanë: “A pasi të bëhemi eshtra e pluhur do të ringjallemi krijesë e re?”');
INSERT INTO "ahmeti" VALUES(17,99,'17:99','A nuk e kuptuan ata se All-llahu që krijoi qiejt e tokën është i plotëfuqishëm që të krijojë ështu siç janë ata, dh Ai u ka caktuar afatin (e vdekjes e të ringjalljes) në të cilin nuk ka dyshim, po zullumqarët nuk deshën tjetër vetëm të refuzojnë.');
INSERT INTO "ahmeti" VALUES(17,100,'17:100','Thuaj: “Sikur të kishit depotë e begative të Zotit tim, nga frika se po shpenzohen, do të bëheni koprracë; njeriu është dorështrënguar”.');
INSERT INTO "ahmeti" VALUES(17,101,'17:101','Ne i patëm dhënë Musait nëntë argumente (mrekulli) të qarta. Pyeti pra ti (Muhammed) beni israilët (nëse duan të dëshmojën) kur ai u ka ardhur atyre (Musai), e atiji pat thnë faraoni: O Musa, unë mendoj ti je i magjepsur!');
INSERT INTO "ahmeti" VALUES(17,102,'17:102','Ai (Musai) tha: “Po ti e di se ato (mrekullitë) nuk i zbriti kush tjetër pos Zotit të qiejve e të tokës, i zbriti të dukshme, e unë besoj, o faraon! se ti je shkatërruar”.');
INSERT INTO "ahmeti" VALUES(17,103,'17:103','Ai (faraoni) deshi t’i dëbojë ata nga vendi (Egjipti), kurse Ne e përmbysëm atë dhe të gjithë ata që ishin me të së bashku.');
INSERT INTO "ahmeti" VALUES(17,104,'17:104','Pas tij beni israilve u thamë: “Rrini në vend, e kur të vijë koga e botës tjetër, Ne ju sjellim të gjithëve të përzier”.');
INSERT INTO "ahmeti" VALUES(17,105,'17:105','Ne me urtësi e zbritëm atë (Kur’anin) dhe me urtësi është zbritur. E ty nuk të dërguam tjetër vetëm se përgëzues dhe qortues.');
INSERT INTO "ahmeti" VALUES(17,106,'17:106','Dhe (ta shpallëm) Kur’anin që Ne e ndam pjesë - pjesë për t’ua lexuar njerëzve dalëngadalë dhe ashtu e shpallëm atë një pas një.');
INSERT INTO "ahmeti" VALUES(17,107,'17:107','Thuaj: “I besuat ju atij ose nuk i besuat (atij nuk i bëhet dëm), e atyre që u është dhënë dijeni (nga lbrat e parë) para tij, kur u lexohet atyre, ata hudhen me fytyra (përdhe) duke i bërë sexhde”.');
INSERT INTO "ahmeti" VALUES(17,108,'17:108','Dhe thonë: “I lartësuar është Zoti ynë, premtimi i Zotit tonë është realizuar”.');
INSERT INTO "ahmeti" VALUES(17,109,'17:109','Dhe duke qarë hudhen me fytyra (kur e dëgjojnë Kur’anin) dhe ai ua shton eshe më përuljen (ndaj All-llahut0.');
INSERT INTO "ahmeti" VALUES(17,110,'17:110','Thuaj: “Thërrisni: All-llah ose thërrisni Er Rrahman, me të cilindo që ta thërrisni (me këta dy emra), emrat e Tij janë më të bukurit. E ti mos e ngrit zërin (duke lexuar) në namazin tënd, po as mos e ul tepër në të, e mes kësaj kërkoje njërrugë mesatare”.');
INSERT INTO "ahmeti" VALUES(17,111,'17:111','Dhe thuaj: “Falenderuar qoftë All-llahu, i cili nuk pati fëmijë dhe nuk ka për Të shok në sundim, dhe i përket aftësisë, nuk ka nevojë për ndihmëtarë, dhe madhëroje Atë me madhërinë që i takon”.');
INSERT INTO "ahmeti" VALUES(18,1,'18:1','Falënderimi i takon vetëm All-llahut që ia shpalli robit të vet Librin dhe në të nuk lejoi ndonjë kundërthënie.');
INSERT INTO "ahmeti" VALUES(18,2,'18:2','(ia shpalli) Të saktë (pa shmangie), pët t’ua tërhequr vërejtjen (atyre që nuk besojnë) ndaj një dënimi të rëndë prej Tij, e për t’i përgëzuar besimtarët që bëjnë vepra të mira, se ata do të kenë një shpërblim të mrekullueshëm.');
INSERT INTO "ahmeti" VALUES(18,3,'18:3','Duke qenë në të përgjithmonë.');
INSERT INTO "ahmeti" VALUES(18,4,'18:4','Për t’ua tërhequr vërejtjen atyre që thanë se All-llahu ka fëmijë.');
INSERT INTO "ahmeti" VALUES(18,5,'18:5','Dhe për atë (fëmijë), as ata, e as të parët e tyre nuk kanë kurrfarë dijenie. Sa e madhe është ajo fjalë që del nga gojët e tyre, e ata nuk thonë tjetër vetëm se gënjeshtër.');
INSERT INTO "ahmeti" VALUES(18,6,'18:6','A thua ti, do ta shkatërrosh veten nga hidhërimi pas tyre, nëse ata nuk i besojnë këtij ligjërimi (Kur’anit)?');
INSERT INTO "ahmeti" VALUES(18,7,'18:7','Gjithçka që është mbi tokë, Ne e bëmë stoli të saj, për t’i provuar ata, se cili prej tyre do të jetë më vepërmirë.');
INSERT INTO "ahmeti" VALUES(18,8,'18:8','Ne kemi për ta bërë gjithçka që është mbi të dhe pa bimë (shkatërrojmë çdo gjelbërim).');
INSERT INTO "ahmeti" VALUES(18,9,'18:9','A ms mendove ti se ata të vendosurit e shpellës dhe Rekimit ishin nga mrekullitë Tona më të çuditshme?');
INSERT INTO "ahmeti" VALUES(18,10,'18:10','Kur djelmoshat u strehuan në shpellë e thanë: O Zoti ynë, na dhuro nga ana Jote mëshirë dhe na përgatit udhëzim të drejtë në tërë çështjen tonë!”');
INSERT INTO "ahmeti" VALUES(18,11,'18:11','Ne u vumë mbulojë mbi veshët e tyre (i vumë në gjumë) në shpellë për disa vite me rradhë.');
INSERT INTO "ahmeti" VALUES(18,12,'18:12','Pastaj ata i zgjuam (prej gjumit) për të parë se cili prej tyre dy grupeve është më preciz në njehësimin e kohës sa qëndruan gjatë.');
INSERT INTO "ahmeti" VALUES(18,13,'18:13','Ne po të rrëfejmë saktë çështjen e tyre. Ata ishin disa djelmosha, kishin besuar Zotin e tyre, e Ne atyre edhe më ua shtuam bindjen.');
INSERT INTO "ahmeti" VALUES(18,14,'18:14','Edhe i forcuam zemrat e tyre (i bëmë të qëndrueshëm) sa që kur ngritën thanë: “Zoti ynë është Zoti i qiejve e i tokës, nuk adhurojmë ndonjë zot tjetër pos Tij, e Ne atyre edhe më ua shtuam bindjen.');
INSERT INTO "ahmeti" VALUES(18,15,'18:15','Këta, populli ynë ka besuar zota tjerë pos Tij, pse pra nuk sjellim ndonjë argument të qartë për aa? A ka më mizor se aiqë shpif gënjeshtër ndaj All-llahut?');
INSERT INTO "ahmeti" VALUES(18,16,'18:16','Derisa jeni izoluar prej tyre dhe prej asaj që adhurojnë ata, pos All-llahut, atëherë strehonu në shpellë, e Zoti juaj ju dhuron nga mëshira e tij e gjerë dhe u lehtëson në çështjen tuaj atë që është në dobinë tuaj.');
INSERT INTO "ahmeti" VALUES(18,17,'18:17','Dhe ti e shihje diellin kur lindte bartej prej shpellës së tyre nga ana e djathtë, e kur perëndonte largohej nga ata nga ana e majtë, e ata ishin në një vend të gjerë të saj. Kjo ishte një nga argumentet e All-llahut. Atë që e udhëzon All-llahu, ai vërtet është udhëzuar, e atë që e humb, ti nuk do të gjejsh për të ndihmës që do ta udhëzonte.');
INSERT INTO "ahmeti" VALUES(18,18,'18:18','Do të mendoje se ata janë të zgjuar, ndërsa ata ishin të fjetur, e Ne i rotullonim herë në krahun e djathtë e herë në të majtin. Qeni i tyre kishte shtrirë këmbët e para pranë hyrjes. Sikur të kishe hasur në ta, do të ishe kthyer duke ikur dhe do të ishe mbushur frikë prej tyre.');
INSERT INTO "ahmeti" VALUES(18,19,'18:19','Ashtu (sikurse i vumë në gjumë) i zgjuam ata që të pyesin njëri-tjetrin (se sa kanë fjetur). Njëri prej tyre foli e tha: “Sa keni ndejur?” Ata thanë:” Kemi ndejur një ditë ose një pjesë të ditës!” Disa thanë: “Zoti juaj e di më së miri se sa keni ndejur, prandaj dërgonie njërin prej jush me këtë argjend (monedhë argjendi) në qytet, e të zgjedhë ushqim më të mirë, e t’ju sjellë atë juve dhe le të ketë shumë kujdes e të mos i japë të kuptojë askujt për ju”.');
INSERT INTO "ahmeti" VALUES(18,20,'18:20','Pse, nëse ata (të qytetit) kuptojnë për ju, do t’ju mbysin me gurë, ose do t’ju kthejnë në fenë e tyre, e atëherë kurr nuk keni për të shpëtuar.');
INSERT INTO "ahmeti" VALUES(18,21,'18:21','Dhe ashtu (sikurse i zgjuam nga gjumi) i zbuluam (t’i shohim njerëzit), e të kuptojnë se premtimi i All-llahut (për ringjallje) është i sigurt dhe se katastrofa e përgjithshme (kijameti) është e padyshimtë. (i zgjuam) Mu atëherë kur ata (populli) bisedonin për çështjen e tyre dhe thanë: “Ndëronu atyre një ndërtesë (të jetë shenjë). Zoti i tyre di më së miri për ta. E ata që ishin me shumicë thanë: “Do të ndërojmë gjithqysh në hyrjen e tyre një xhami”.');
INSERT INTO "ahmeti" VALUES(18,22,'18:22','Ata (që bisedonin për këtë) do të thonë: “Ishin tre, i katërti ishte qeni i tyre, dhe thonë: “Ishin pesë, e qeni i tyre ishte i gjashti i tyre”, kjo ishte hamendje, po edhe thonë: “Ishin shtatë, e qeni i tyre ishte i teti!” Thuaj: “Zoti im di më së miri për numrin e tyre, përveçnjë pakice nuk di kush për ta, e ti (Muhammed) mos polemizo për numrin e tyre vetëm ashtu në tërësi, dhe askë mos e pyet për ta”.');
INSERT INTO "ahmeti" VALUES(18,23,'18:23','Dhe mos thuaj kurrsesi për ndonjë çështje: “Unë do të bëj këtë nesër!”');
INSERT INTO "ahmeti" VALUES(18,24,'18:24','Vetëm (nëse i shton): “Në dashtë All-llah!” E kur të harrosh, përmende Zotin tënd dhe thuaj: “Shpresohet se Zoti im do të më japë udhëzim edhe më të afërt prej këtij (të as-habi kehfit)”.');
INSERT INTO "ahmeti" VALUES(18,25,'18:25','(dhe thonin) Ata qëndruan në shpellën e tyre treqind e nëntë vjet.');
INSERT INTO "ahmeti" VALUES(18,26,'18:26','Thuaj: “All-llahu e di më së miri sa kanë ndejur, Atij i takon fshehtësia e qiejve dhe tokës. Çfarë (i çuditshëm) është të pamët e Tij dhe çfarë është të dëgjuarit e Tij! Pos Tij ata (njerëzit) nuk kanë ndihmëtarë. Në vendimin e Tij nuk mund t’i përzihet askush.');
INSERT INTO "ahmeti" VALUES(18,27,'18:27','Lexo çka të është shpallur ty nga libri i Zotit tënd! Nuk ka kush që mund t’i ndryshojë fjalët e Tij dhe pos Tij, nuk mund të gjesh stehim.');
INSERT INTO "ahmeti" VALUES(18,28,'18:28','Përkufizoje veten tënde me ata që lusin Zotin e tyre mëngjes e mbrëmje, e që kanë për qëllim kënaqësinë (razinë) e Tij, dhe mos i hiq sytë e tu prej tyre e të kërkosh bukurinë e kësaj bote dhe mos iu bind atij që ia kemi shmangur zemrën e tij prej përkjtimit ndaj Nesh dhe i është dhënë epshit të vet, pse puna e tij ka mbaruar.');
INSERT INTO "ahmeti" VALUES(18,29,'18:29','E ti thuaj: “E vërteta është nga Zoti juaj, e kush të dojë, le të besojë, e kush të dojë, le të mohojë. Ne kemi përgaditur për jobesimtarët zjarr që muret e tij (të flakës) i rrethojnë ata, e nëse kërkojnë shpëtim, ndihmohen me një ujë si katran që përzhit fytyrat. E shëmtuar është ajo pije, e vend i keq është ai”.');
INSERT INTO "ahmeti" VALUES(18,30,'18:30','E ata që besuan dhe bënë vepra të mira, s’ka dyshim se Ne nuk humbim shpërblimin atij që është bamirës.');
INSERT INTO "ahmeti" VALUES(18,31,'18:31','Të tillët e kanë vendin në Xhennetin e Adnit, nën të cilin rrjedhin lumenj; e aty stolisen me rrathë të artë dore dhe ata veshin rrobe të gjelbërta prej mëndafshi të hollë e të trashë, janë mbështetur aty në shtretër. Sa shpërblim i mrekulleshëm është dhe sa vend i bukur është ai.');
INSERT INTO "ahmeti" VALUES(18,32,'18:32','E ti sillu atyre (që kërkuan t’i largosh varfanjakët) si shembul dy njerëz; njërit prej tyre i dhamë dy kopshte (vreshta) nga rrushi dhe ato i rrethuam me hurma, e në mes atyre djave bimë tjera.');
INSERT INTO "ahmeti" VALUES(18,33,'18:33','Të dy kopshtet jepnin frutat e veta pa munguar prej tyre asgjë, e në mesin e tyre bëmë të rrjedhë një lumë.');
INSERT INTO "ahmeti" VALUES(18,34,'18:34','Ai kishte edhe pasuro tjetër. E ai atij shokut të vet (që ishte besimtar) itha: - duke iu krenuar atij - “Unë kam pasuri më shumë se ti e, kam edhe krah më të fortë!”');
INSERT INTO "ahmeti" VALUES(18,35,'18:35','Dhe ai hyri në kopshtin e vet (e me besimtarin për dore), po duke qenë dëmtues i vetës (duke mos besuar dhe duke u krenuar) tha: “Unë nuk mendoj se zhduket kjo kurrë!”');
INSERT INTO "ahmeti" VALUES(18,36,'18:36','Dhe nuk besoj se do të ndodhë kijameti, (dita e gjykimit), por nëse bëhet që të kthehem te Zoti im, padyshim do të gjej ardhmëri edhe më të mirë se kjo.');
INSERT INTO "ahmeti" VALUES(18,37,'18:37','Atij ai shoku ivet (besimtari) itha - duke e polemizuar atë -: “A e mohove Atë që të krijoi ty nga dheu, pastaj nga një pikë uji, pastaj të bëri njeri të plotë?”');
INSERT INTO "ahmeti" VALUES(18,38,'18:38','“Por për mua, Ai All-llahu është Zoti im, e Zotit tim unë nuk ibëj shok askë!”');
INSERT INTO "ahmeti" VALUES(18,39,'18:39','E pse ti kur hyre në kopshtin tënd nuk the: “Ma shaell llah, la kuvete il-la bil lah” - (Allhu çka do, bëhet, s’ka fuqi pa ndihmën e Tij). Nëse ti më sheh mua se unë kam më pak prej teje edhe pasuri edhe fëmijë (krah më të dobët).');
INSERT INTO "ahmeti" VALUES(18,40,'18:40','Po unë shpresoj se Zoti im do të më japë edhe më të mirë nga kopshti yt, e këtij tëndit t’i shkaktojë fatkeqësi nga qielli e të gdhijë tokë e lëmuar (e zhveshur).');
INSERT INTO "ahmeti" VALUES(18,41,'18:41','Ose të gdhijë uji i shterur, e ti kurrsesi nuk mund ta kërkosh atë!”');
INSERT INTO "ahmeti" VALUES(18,42,'18:42','Dhe vërtet, u shkatërruan frutat e ti, e ai filloi tërrahë shuplakët për atë që kishte shpenzuar në të, e ajo ishte rrënuar në kulmet e saj dhe thoshte: “O i mjeri unë, të mos i kisha bërë Zotit tim akë shok!”');
INSERT INTO "ahmeti" VALUES(18,43,'18:43','E nuk pati krah (grup), pëveç All-llahut të vërtetë që t’i ndihmojë atij dhe nuk mundi ta pengojë.');
INSERT INTO "ahmeti" VALUES(18,44,'18:44','Në atë vend ndihma është vetëm e All-llahut të vërërtetë. Ai është shpërblyesi më i mirë dh te Ai ëhstë përfundimi më i mirë.');
INSERT INTO "ahmeti" VALUES(18,45,'18:45','E ti (Muhammed) paraqitjau atyre shembullin e kësaj bote që ëshë si një ujë (shi) që Ne e lëhsojmë nga qielli, e prej tij bima e tokës zhvillohet e shpeshëtohet sa që përzihet mes vete, e pas pak ajo bëhet byk (pas tharjes) që e shpërndajnë erërat. All-llahuka fuqi për çdo send.');
INSERT INTO "ahmeti" VALUES(18,46,'18:46','pasuria dhe fëmijët janë stoli e kësaj bote, kurse veprat e mira (frtyi i të cilave është i përjetshëm) janë shpërblimi më i mirë te Zoti yt dhe janë shpresa më e mirë.');
INSERT INTO "ahmeti" VALUES(18,47,'18:47','(Prkujto) Ditën kur Ne i shkulim kodrat, dhe tokën e sheh të sheshtë (të zbuluar) dhe Ne i tubojmë ata e nuk na mbete asnjë prej tyre pa e tubuar.');
INSERT INTO "ahmeti" VALUES(18,48,'18:48','Dhe vihet libri (i veprave), e i sheh mëkatarët të frikësuar nga shënimet që janë në të dh thonë: “Të mjerët ne, ç’është puna e këtij libri që nuk ka lënë as (mëkat) të vogël e as të madh pa e përfshirë?” dhe atë që vepruan e gjejnë të gatshme - prezente, e Zoti yt nuk i bën padrejt askujt.');
INSERT INTO "ahmeti" VALUES(18,49,'18:49','Dhe vihet libri (i veprave), e i sheh mëkatarët të frikësuar nga shënimet që janë në të dh thonë: “Të mjerët ne, ç’është puna e këtij libri që nuk ka lënë as (mëkat) të vogël e as të madh pa e përfshirë?” dhe atë që vepruan e gjejnë të gatshme - prezente, e Zoti yt nuk i bën padrejt askujt.');
INSERT INTO "ahmeti" VALUES(18,50,'18:50','(Përkujto) Kur u thamë engjëjve: “Përuluni Ademit, e ata iu përulën përpos Iblisit. Ai ishte nga xhinët, prandaj nuk respektoi urdhërin e Zotit të vet. Vallë, a në vend Timin do ta merrni për mik atë dhe pasardhësit e tij, ndërsa ata janë armiq tuaj?” Sa këmbim i shëmtuar është ai i jobesimtarëve!');
INSERT INTO "ahmeti" VALUES(18,51,'18:51','Unë nuk ua prezentova krijimin e qiejve e të tokës atyre që po i adhuroni e as krijimin e vetë atyre, e as nuk mora mdihmëtrë të humburit.');
INSERT INTO "ahmeti" VALUES(18,52,'18:52','E ditën kur Ai thotë: “Thirrni ata, për të cilët menduat se janë bashpuntorët e Mij!” I thërrasin, por ata nuk u përgjigjen (për ndihmë) atyre dhe ato lidhjet e tyre (në dynja) Ne i zhdukim.');
INSERT INTO "ahmeti" VALUES(18,53,'18:53','Kriminelët e shohin zjarri dhe binden se do të hudhen në të, dhe se nuk do të gjejnë shtegdalje prej tij.');
INSERT INTO "ahmeti" VALUES(18,54,'18:54','Në këtë Kur’an Ne shtruam çdo lloji shembulli për hirë të njerëzve, po njeriu (jobesimtar), më shumë se çdo tjetër është kundërshtar.');
INSERT INTO "ahmeti" VALUES(18,55,'18:55','Pasi që t’u ketë ardhur udhëzimi, njerëzit nuk i pengoi tjetër të besojnë dhe të kërkojën falje prej Zotit të tyre, vetëm se presin t’u vijë edhe atyre fatëkeqësia e popujve të mëparshëm ose t’u vijë atyre dënimi ballë për ballë.');
INSERT INTO "ahmeti" VALUES(18,56,'18:56','Ne nuk i dërgojmë ndrushe të dërguarit pos vetëm si përgëzues e qortues. E, megjithatë, ata që nuk besuan polemizojnë me të, ta mbizotërojë të vërtetën, dhe argumentet e Mia dhe atë me çka u qortuan i marrin për tallje.');
INSERT INTO "ahmeti" VALUES(18,57,'18:57','Kush është më zullumqarë se ai që është këshilluar me argumentet e Zotit të vet, e ua kthejn shpinën atyre dhe harron atë që vetë e punoi? Ne kemi vënë mbi zemrat e tyre mbulesë në mënyrë që të mos e kuptojnë atë (Kur’anin), kurse në veshët e tyre shurdhim, andaj edhe nëse i thërret ti ata nërrugë të drejtë, ata si të tillë nuk do të udhëzohen kurrë.');
INSERT INTO "ahmeti" VALUES(18,58,'18:58','Zoti yt është që flë shumë dhe bën mëshirë të madhe, sikur t’i ndëshkonte ata për atë që kanë vepruar, do t’ua ngutte atyre tash dënimin, por ata kanë momentin e caktuar, e pos Tij nuk do të gjejnë ata strehim.');
INSERT INTO "ahmeti" VALUES(18,59,'18:59','Ato janë fshatra (vendbanime) që kur bënë zullum, Ne i shkatërruam dhe për shkatërrimin e tyre u patëm caktuar kohë të sakta.');
INSERT INTO "ahmeti" VALUES(18,60,'18:60','(përkjto) Kur Musai djaloshit që e shoqëronte i tha: “Nuk do të ndalem së ecuri deri të arrijë vendtakimin e e dy deteve, ose do të udhëtoj një kohë të gjatë.');
INSERT INTO "ahmeti" VALUES(18,61,'18:61','Kur arritën ata të dy vendtakimin e atyre deteve, e harruan pehkun e tyre, e ai (peshku) mori rrugën e vet si (strofull) në det.');
INSERT INTO "ahmeti" VALUES(18,62,'18:62','Pasi ata e kaluan atë vend, ai (Musai) i tha djaloshit: “Na jep sillën tonë (ushqimin), meqë nga ky udhëtim jemi lodhur”.');
INSERT INTO "ahmeti" VALUES(18,63,'18:63','AI (djaloshi - Jushaë bin Nun) tha: “A e sheh!?” Kur u strehuam te shkëmbi, unë e harrova peshkun dhe përveç djallit, nuk më bëri ksh ta harrojë q të përkujtojë ty atë, dhe ai në mënyrë të çuditshme mori rrugën për në det!');
INSERT INTO "ahmeti" VALUES(18,64,'18:64','Ai (Musai) tha: “Ai është (vendi) që ne po e kërkojmë!” Dhe u kthyen që të dy rrugës së tyre prej nga kishin ardhur.');
INSERT INTO "ahmeti" VALUES(18,65,'18:65','Dhe e gjetën një robë Tanë, të cilit i kemi dhuruar mëshirë nga ana e Jonë dhe e kemi mësuar me një dituri të posaçme nga Ne.');
INSERT INTO "ahmeti" VALUES(18,66,'18:66','Atij (Hidrit - njeriut të mirë e të dijshëm) Musai i tha: “A pranon të vijë me ty, që të më mësosh nga ajo që je imësuar (i dhuruar) ti: dituri të drejtë e të vërtetë?');
INSERT INTO "ahmeti" VALUES(18,67,'18:67','Ai (Hidri) tha: “Sigurisht, ti nuk do të mund të kesh durim me mua!”');
INSERT INTO "ahmeti" VALUES(18,68,'18:68','Si do të durosh për atë që nuk je i njoftuar?');
INSERT INTO "ahmeti" VALUES(18,69,'18:69','(Musai) Tha: “Në dashtë All-llahu, do të shohësh se do të jem i durueshëm dhe nuk do të kundërshtoj ty asgjë!”');
INSERT INTO "ahmeti" VALUES(18,70,'18:70','Ai i tha: “Nëse më shoqëron ti mua, atëherë mos më pyet për asgjë, derisa unë vetë të të tregoj për të”.');
INSERT INTO "ahmeti" VALUES(18,71,'18:71','E ata të dy shkuan *duke ecur) deri kur hipën në anije, ai (i dishmi) e shqeu atë. Ai (Musai) tha: “A e shqeve që ti fundosësh udhëtarët e saj, vërtet bëre një punë të hatashme?”');
INSERT INTO "ahmeti" VALUES(18,72,'18:72','Tha: “A nuk të thashë se nuk do të mund të kehs durim me mua?”');
INSERT INTO "ahmeti" VALUES(18,73,'18:73','(Musai) Tha: “Mos më qorto për se harrova dhe më mundo me vështirësi në këtë shoqërim timin (lehtësoma punën)!”');
INSERT INTO "ahmeti" VALUES(18,74,'18:74','Vazhduan të shkojnë deri kur e takuan një djalë të ri, e ai (i dijshmi) e mbyti atë. (Musai) Tha: “A e mbyte njeriun e pastër, pa mbytë ai askë?! Vërtet ke bërë një pumë të shëmtuar!”');
INSERT INTO "ahmeti" VALUES(18,75,'18:75','Ai (i dijshmi) tha: “A nuk të thashë se vërtet ti nuk do të mund të kesh durim me mua?”');
INSERT INTO "ahmeti" VALUES(18,76,'18:76','(Musai) Tha: “Nëse pas kësaj të pyes (të kundërshtoj) për ndonjë gjë, atëherë mos më lejo të shoqëroj. Tashmë ndaj meje ke arsyetim (të mos më shoqërosh)!”');
INSERT INTO "ahmeti" VALUES(18,77,'18:77','Vazhduan të shkojnë deri arritën te bannorët e një qyteti që prej tyre kërkuan t’u japin ushqim, po ata nuk deshën t’i pranojnë si mysafirë (e as t’i ushqejnë) e ata të dy gjetën një mur që gati rrëzohej, e ai e drejtoi atë (murin). (Musai) Tha: “Sikur të kishe dashur do t’u merrje shpërblim për këtë!”');
INSERT INTO "ahmeti" VALUES(18,78,'18:78','Ai (i dijshmi) tha: “Tash ka ardhur koha e ndarjes mes meje e teje, e unë do të tregoj për domethënien e asaj që nuk mund të keshë durim!”');
INSERT INTO "ahmeti" VALUES(18,79,'18:79','Sa i përket anijes, ajo ishte pronë e të varfërve, që veprojnë në det, e unë desha të bëjë atë me të meta, ngas para tyre ishte një sundues që grabiste çdo anije (të aftë - pa të meta).');
INSERT INTO "ahmeti" VALUES(18,80,'18:80','Sa i përket djaloshit, dy prindërit e tij ishin besimtarë, e u frikësuam se ai do t’i shpie ata të dy në humbje e në mosbesim.');
INSERT INTO "ahmeti" VALUES(18,81,'18:81','Deshëm që Zoti t’u japë në vend të tij një më të mirë se ai dhe më të afërt në respekt dhe në mëshirë (ndaj prindërve).');
INSERT INTO "ahmeti" VALUES(18,82,'18:82','Përsa i përket murit, ai ishte i dy djelmoshave jetimë të atij qyteti, e nën të ata kishin një thesar (ari) dhe babai i tyre ka qenë njeri i mirë, e Zoti yt dëshiroi që ata dy (jetimë) ta arrijnë pjekurinë e vet dhe ta nxjerrin ata vetë thesarin e tyre. Kjo ishte mëshrë e Zotit tënd (ndaj tyre). Dhe unë nuk e pranova tërë atë sipas bindjes sime (po sipas udhëzimit të Zotit). Ky, pra, është sqarim i asaj për të cilën nuk munde të kesh durim!');
INSERT INTO "ahmeti" VALUES(18,83,'18:83','Të pyesin ty për Dhulkarnejnin, thuaju: “Do t’ju rrëfej për punën e tij me Kur’an (me shpallje)”.');
INSERT INTO "ahmeti" VALUES(18,84,'18:84','Ne i mundësuam atij forcë në tokë dhe i dhamë mundësi për çdo send që të mund të arrijë.');
INSERT INTO "ahmeti" VALUES(18,85,'18:85','Ai iu rrek një aso mundësie (dhe mori rrugën nga perëndimi).');
INSERT INTO "ahmeti" VALUES(18,86,'18:86','Deri kur vendperëndimin e diellit, e gjrti se po perëndon në njëfarë burimii me lym të zi dhe aty e gjeti një popull. E Nei thamë: “O Dhulkarnejn, ose do t’i dënosh, ose do t-i marrësh me të mirë e t’i udhëzosh!”');
INSERT INTO "ahmeti" VALUES(18,87,'18:87','Ai (Dhulkarnejni) tha: “Ai që vazhdon edhe mëtej të jetë zullunqar, atë do ta dënojmë, pastaj i kthehet Zotit të vet e Ai e dënon atë me një dënim të tmerrshëm”.');
INSERT INTO "ahmeti" VALUES(18,88,'18:88','Sa i përket atij që besoi dhe bëri vepra të mira, atij i takon shpërblimi më i mirë (Xhenneti) dhe atij nga ana Jonë do t’i bëjmë lehtësi (në jetë).');
INSERT INTO "ahmeti" VALUES(18,89,'18:89','Pastaj, ai iu rrek një aso mundësie (mori rrugën e lindjes).');
INSERT INTO "ahmeti" VALUES(18,90,'18:90','Deri kur arriti vndlindjen e diellit, e gjeti atë se po lind mbi një popull, që nuk i kemi dhënë ndonjë mbulojë prej tij (diellit).');
INSERT INTO "ahmeti" VALUES(18,91,'18:91','Ashtu (bëri edhe me këtë popull), e Ne e kemi ditur gjendjen e tij (mundësitë dhe sundimin e drejtë të tij).');
INSERT INTO "ahmeti" VALUES(18,92,'18:92','Mandej, ai iu rrek ërsëri një aso mundësie (një rrugë të tretë mes perëndimit e lindjes - nga veriu).');
INSERT INTO "ahmeti" VALUES(18,93,'18:93','Deri kur arriti mes dy kodrave (si penda) dhe mbrapa tyre gjeti një popull që thuajse nuk kuptonte asnjë gjuhë (përveç gjuhës së vet).');
INSERT INTO "ahmeti" VALUES(18,94,'18:94','Ata thanë: “O Dhulkarnejn, vërtet Jexhuxhi dhe Mexhuxhi janë duke bërë shkatërrime në tokë, a bën që ne të japim ty një kontribut (një formë tagri a tatimi), e që të bësh një pendë mes nesh dhe mes tyre?”');
INSERT INTO "ahmeti" VALUES(18,95,'18:95','Ai (Dhulkarnejni) tha: “Atë që mua ma mundësoi Zoti im është shumë më e mirë (nga ajo që më ofroni ju), po ju më ndihmoni me fuqi punëtore të bëj një pendë të fortë mes jush dhe mes atyre!”.');
INSERT INTO "ahmeti" VALUES(18,96,'18:96','Më sillni copa të hekurit! E kur e nivelizoi ai me dy anët e kodrave, tha: “Ndizni!” e kur e bëri atë (hekurin) zjarr (si zjarr nga të ndezurit) tha: “Më sillni bakër të shkrirë t’ia hedhë atij!”');
INSERT INTO "ahmeti" VALUES(18,97,'18:97','E ata as nuk arritën ta kapërcenin e as ta shponin.');
INSERT INTO "ahmeti" VALUES(18,98,'18:98','Ai (Dhulkarnejni) tha: “Kjo është një e mirë nga Zoti im, e kur të vjen caktimi i Zotit tim, Ai e bën atë rrafsh, e caktimi i Zotit tim është i sigurt.');
INSERT INTO "ahmeti" VALUES(18,99,'18:99','(Kur të vijë caktimi i Zotit, ose kur të dalin Jexhuxh Mexhuxhët) Ne bëjmë atë ditë që ata të përzihen si valët njëra me tjetrën. Dhe i fryhet Surit (për herën e dytë) e të gjithë ata i tubojmë.');
INSERT INTO "ahmeti" VALUES(18,100,'18:100','Atë ditë ua prezentojmë zhehenemin jobesimtarëve me një prezentim të hapët (trishtues).');
INSERT INTO "ahmeti" VALUES(18,101,'18:101','Atyre që sytë i kishin të mbuluar ndaj argumenteve te Mia dhe që nuk mund të dëgjonin (fjallët e All-llahut).');
INSERT INTO "ahmeti" VALUES(18,102,'18:102','A mos menduan aa që nuk besuan se përkundër Meje të marrin zota (mbrojtës) robtë e Mi? Ne kemi përgatitur për jobesimtarët Xhehennemin vendpritje.');
INSERT INTO "ahmeti" VALUES(18,103,'18:103','Thuaj: “A t’ju tregojmë për më të dëshpruarit në veprat e tyre?”');
INSERT INTO "ahmeti" VALUES(18,104,'18:104','Ata janë veprimi i të cilëve u asgjësua në jetën e kësaj bote, e megjithatë ata mendojnë se janë kah bëjnë mirë.');
INSERT INTO "ahmeti" VALUES(18,105,'18:105','Të tillët janë ata që nuk besuan argumentet e Zoit të tyre as takimin (ringjalljen) e Tij, andaj veprat e tyre shkuan huq dhe në ditën e gjykimit atyre nuk do t’u jepet kurrfarë vlere.');
INSERT INTO "ahmeti" VALUES(18,106,'18:106','Këtë, ngase shpërblimi i tyre është Xhehennemi, për shka se nuk besuan dhe argumentet e Mia e të dërguarit e Mi i morën për tallje.');
INSERT INTO "ahmeti" VALUES(18,107,'18:107','S’ka dyshim se ata që besuan dhe bënë vepra të mira, vendpritje për ta janë Xhennetet e Firdevsit.');
INSERT INTO "ahmeti" VALUES(18,108,'18:108','Aty do të jenë përgjithmonë, e nuk kërkojnë të largohen nga ai (ose t’u ndryshohet).');
INSERT INTO "ahmeti" VALUES(18,109,'18:109','Thuaj: “sikur të ishte deti ngjyrë për t’i shkruar fjalët e Zotit tim, deti (uji i tij) do të shterrej para se të përfundojnë fjalët e Zotit tim, e edhe sikur të sillnim shtesë edhe një si ai (deti)”.');
INSERT INTO "ahmeti" VALUES(18,110,'18:110','Thuaj: “Unë jam vetëm njeri, sikurse ju, mua më shpallet se vetëm një Zot është Zoti juaj, e kush është që e shpreson takimin e Zotit të vet, le të bëjë vepër të mirë, e në adhurimin ndaj Zotit të tij të mos përziejë askë.');
INSERT INTO "ahmeti" VALUES(19,1,'19:1','Kaf Ha Ja’ A’jn Sadë!');
INSERT INTO "ahmeti" VALUES(19,2,'19:2','(ky është) Përkujtim i mëshirës së Zotit tënd ndaj robit të vet Zekeriait,');
INSERT INTO "ahmeti" VALUES(19,3,'19:3','Kur ai iu lut Zotit të vet me një zë të ulët, e');
INSERT INTO "ahmeti" VALUES(19,4,'19:4','Tha: “Zoti im! Vërtet, mia më janë dobësuar eshtrat, më janë përhapur thinjat në kokën time, e me lutjen time ndaj Teje o Zoti im, asnjëherë nuk kam qenë i dëshpëruar”.');
INSERT INTO "ahmeti" VALUES(19,5,'19:5','Unë ua kam frikë pasardhësve të mi pas meje (kushërinjve se do ta humbnin fenë), ndërsa gruaja ime është sterile, pra më falë nga ajo mirësia Jote një pasardhës (një fëmijë).');
INSERT INTO "ahmeti" VALUES(19,6,'19:6','Të më trashëgojnë mua dhe t’i trashëgojë ata nga familja e Jakubit, dhe bërë atë, o Zoti, të këndshëm! (të vyeshëm)!”');
INSERT INTO "ahmeti" VALUES(19,7,'19:7','O Zekëria, Ne po të përgëzojmë ty me një djalë, emri i të cilit është Jahja, e që askush para tij nuk u emërtua me këtë emër.');
INSERT INTO "ahmeti" VALUES(19,8,'19:8','Ai tha: “Zoti im, si do të kemë unë djalë kur gruaja ime ishte që nuk lind, e unë kam arritur pleqëri të thellë?”');
INSERT INTO "ahmeti" VALUES(19,9,'19:9','Tha: “Ja, kështu Zoti yt ka thënë: se ajo për Mua është lehtë; Unë të krijova më parë ty, që nuk ishe fare!”');
INSERT INTO "ahmeti" VALUES(19,10,'19:10','Ai (Zekeriai) tha: Zoti im, më jep mua një shenjë (që të di)!” Ai (All-llahu) tha: “Shenja jote është se, (duke qenë shëndoshë e mirë), nuk do të mund t’u flasesh njerëzve tri net (e tri ditë)”.');
INSERT INTO "ahmeti" VALUES(19,11,'19:11','Dhe ai doli prej faltores para popullit të vet dhe u dha shenjë adhuroni (All-llahun) mëngjes e mbrëmje.');
INSERT INTO "ahmeti" VALUES(19,12,'19:12','O Jahja, merre librin me shumë kujdes, dhe ashtu, dhe ashtu Ne i dhamë atij urtësi kur ishte fëmijë.');
INSERT INTO "ahmeti" VALUES(19,13,'19:13','Dhe nga ana jonë i mundësuam të jetë i butë, i pastër dhe respektues (i dëgjueshëm).');
INSERT INTO "ahmeti" VALUES(19,14,'19:14','Edhe i sjellshëm ndaj prindërve të vet, nuk ishte kryelartë e i padëgjueshëm.');
INSERT INTO "ahmeti" VALUES(19,15,'19:15','Dhe selam (prej nesh) atij ditën kur u lind, ditën kur vdes dhe ditën kur do të ngritet i gjallë (e përshëndetën dhe i garantuam)!');
INSERT INTO "ahmeti" VALUES(19,16,'19:16','E, përmendju në këtë libër (tregimin për) Merjemen kur ajo u largua prej familjes së saj në një vend në lindje.');
INSERT INTO "ahmeti" VALUES(19,17,'19:17','Ajo, vuri një perde ndaj tyre, e Ne ia dërguam asaj Xhebrilin, e ai iu paraqit asaj njeri në tërësi.');
INSERT INTO "ahmeti" VALUES(19,18,'19:18','Ajo tha: “Unë i mbështetem të Gjithëmëshirëshmit prej teje, nëse je që frikësohesh Atij (pra më le të lirë)!”');
INSERT INTO "ahmeti" VALUES(19,19,'19:19','Ai (Xhibrili) tha: “Unë jam vetëm i dërguar (melek) i Zotit tënd për të dhuruar ty një djalë të pastër (pejgamber)”.');
INSERT INTO "ahmeti" VALUES(19,20,'19:20','Ajo tha: “Si do të kem unë djalë, kur mua nuk më është afruar njeri (nuk jam e martuar), e as nuk kamë qenë e pamoralshme”.');
INSERT INTO "ahmeti" VALUES(19,21,'19:21','Ai (Xhibrili) tha: “Ja, kështu ka thënë Zoti yt; ajo për Mua është lehtë, e për ta bërë atë (djalin e krijuar pa babë) argument për njerëzit e edhe mëshirë nga ana e Jonë. Kjo është çështje e kryer!”');
INSERT INTO "ahmeti" VALUES(19,22,'19:22','Ajo e barti atë (Isain), andaj (me të në bark) u izolua në një vend të largët.');
INSERT INTO "ahmeti" VALUES(19,23,'19:23','E dhembja (e lindjes) e mbështeti atë te një trup i hurmës. Ajo tha: “Ah sa mirë ka qenë për mua të kisha vdekur para kësaj e të isha e harruar që moti!”');
INSERT INTO "ahmeti" VALUES(19,24,'19:24','E prej së poshtmi atë e thirri (Xhibrili): “Mos u brengos, Zoti yt bëri pranë teje një përrockë (uji)”.');
INSERT INTO "ahmeti" VALUES(19,25,'19:25','E ti shkunde trupin e hurmës se do të bijnë ty hurma të freskëta.');
INSERT INTO "ahmeti" VALUES(19,26,'19:26','Ti pra, ha pi e qetësou, dhe nëse sheh ndonjë prej njerëzve thuaj: “Unë ka vendsur heshtje për hir të Gjitëhmëshirshmit, andaj asnjë njeriu sot nuk i flas!”');
INSERT INTO "ahmeti" VALUES(19,27,'19:27','Dhe duke e bartur ngryk shkoj me të te të afërmit e vet ata i thanë: “Oj Merjeme, ke bërë një punë shumë tëkeqe!”');
INSERT INTO "ahmeti" VALUES(19,28,'19:28','Oj motra e Harunit, babai yt nuk ishte njeri i prishur e as nëna jote nuk ka qenë e pamoralshme!');
INSERT INTO "ahmeti" VALUES(19,29,'19:29','Atëherë ajo u dha shenjë kah (Isai). Ata thanë: “Si t’i flasim atij që ëshë foshnjë në djep?”');
INSERT INTO "ahmeti" VALUES(19,30,'19:30','Ai (Isai) tha: “Unë jam rob i All-llahut, mua më ka dhënë (ka caktuar të më japë) librin dhe më ka bërë Pejgamber.');
INSERT INTO "ahmeti" VALUES(19,31,'19:31','Më ka bërë dobiprurës kudo që të jem dhe më ka porositur me namaz (falje) e zeqatë për sa të jemë gjallë!');
INSERT INTO "ahmeti" VALUES(19,32,'19:32','Më ka bërë të mirësjellshëm ndaj nënës sime, e nuk më ka bërë kryelartë as të padëgjueshëm!');
INSERT INTO "ahmeti" VALUES(19,33,'19:33','Selami (shpëtim prej All-llahut) është me mua ditën kur u linda, ditën kur të vdes dhe ditën kur të dal (prej varrit) i gjallë!”');
INSERT INTO "ahmeti" VALUES(19,34,'19:34','Kjo është (fjalë) e vërtetë, rreth Isait, birit të Merjemes në të cilin ata shkaktojnë dyshime.');
INSERT INTO "ahmeti" VALUES(19,35,'19:35','Nuk i takoi (nuk ka nevojë) All-llahu të ketë ndonjë fëmijë, i pastër është Ai, kur dëshiron një send Ai vetëm i thotë atij: Bëhu!”. Ai menjëherë bëhet.');
INSERT INTO "ahmeti" VALUES(19,36,'19:36','(Isai i tha popullit të vet) Dhe se All-llahu është Zoti im dhe Zoti juaj, pra adhuronie Atë, kjo është rrugë (fe) e drejtë.');
INSERT INTO "ahmeti" VALUES(19,37,'19:37','Po, grupet u kundërshtuan mes vete (rreth Isait). Të mjerët ata që nuk besuan se çka kanë për të parë ditën e madhe!');
INSERT INTO "ahmeti" VALUES(19,38,'19:38','Ditën kur do të na paraqiten neve, sa mirë do të dëgjojnë edhe shohin (ose njerëzit do të dëgjojnë e shohin çka i gjen ata), por tash për tash (në këtë jetë) zullumqarët janë të humbur qartë.');
INSERT INTO "ahmeti" VALUES(19,39,'19:39','Ti tërhiqu atyre vërejtjen për ditën e dëshprimit, kur të marrë fund çështja (e përgjejgësisë e dikush në xhehet, dikush në zjarr), se ata (sot) janë të painformuar, dhe ata nuk janë duke besuar.');
INSERT INTO "ahmeti" VALUES(19,40,'19:40','S’ka dyshim se Ne e trashëgojmë (na mbetet) tokën dhe çka ka në të, dhe të Ne do të jetë e ardhmja e tyre.');
INSERT INTO "ahmeti" VALUES(19,41,'19:41','Përkujtoju (lexoju idhujtarëve e të tjerëve) në Libër (në Kur’an) Ibrahimin. Ai vërtet ishte shumë i drejtë dhe pejgamber.');
INSERT INTO "ahmeti" VALUES(19,42,'19:42','Kur babait të vet i tha: “O babai im, pse adhuron atë që as nuk dëgjon, as nuk sheh, e as nuk ke asgjë prej tij?');
INSERT INTO "ahmeti" VALUES(19,43,'19:43','O babai im, mua më është dhënë nga dituria çka ty nuk të është dhënë, andaj më dëgjo se unë të udhëzoj në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(19,44,'19:44','O babai im, mos adhuro djallin, sepse djalli është kundërshtar i Mëshiruesit.');
INSERT INTO "ahmeti" VALUES(19,45,'19:45','O babai im, unë kam frikë se do të godet ndonjë dënim prej të Gjithmëshirshmit e do të jesh shok i djallit!”');
INSERT INTO "ahmeti" VALUES(19,46,'19:46','Ai (babai) tha: “A ti, Ibrahim, i refuzon zotat e mi? Nëse nuk ndalesh (së fyeri ndaj zotave të mi), unë do të gurëzoj, ndaj largohu prej meje për një kohë të gjatë!”');
INSERT INTO "ahmeti" VALUES(19,47,'19:47','Ai (Ibrahimi) tha: “Qofsh i lirë prej meje! Unë do ta lus Zotin tim për të falur ty, pse ai (babai) shte i kujdsshëm ndaj meje.');
INSERT INTO "ahmeti" VALUES(19,48,'19:48','Unë po largohem prej jush dhe prej çka adhuroni ju pos All-llahut, e shpresoj se me adhurimin ndaj Zotit tim nuk do të jem i humbur!”');
INSERT INTO "ahmeti" VALUES(19,49,'19:49','E pasi u largua prej tyre dhe prej çka adhuronin ata pos All-llahut, Ne i dhuruam atij Is-hakun dhe Jakubin dhe që të dy i bëmë pejgamberë.');
INSERT INTO "ahmeti" VALUES(19,50,'19:50','Dhe atyre (të gjithëve) u dhamë (të mira) nga mëshira Jonë, edhe i bëmë që të jenë të përmendur për të mirë (ndër njerëz).');
INSERT INTO "ahmeti" VALUES(19,51,'19:51','Përmendju në Libër (tregimin për) Musain! ai ishte i zgjedhur (prej Zotit) dhe ishte i dërguar, pejgamber.');
INSERT INTO "ahmeti" VALUES(19,52,'19:52','Dhe Ne e thirrëm nga ana e djathtë e kodrës Tur, e afruam për t’i folur (të dëgjojë bisedën tonë).');
INSERT INTO "ahmeti" VALUES(19,53,'19:53','Nga mëshira Jonë i dhamë vëllain e tij Harunin, Pejgamber.');
INSERT INTO "ahmeti" VALUES(19,54,'19:54','Përkujtoju në këtë Libër Ismailin! Ai ka qenë shumë besnik (premtoi të bëhet kurban) dhe i dërguar, pejgamber.');
INSERT INTO "ahmeti" VALUES(19,55,'19:55','Ai urdhëronte familjen e vet me faljen e namazit dhe me zeqatë, dhe ishte dhumë i pranishëm te Zoti i tij.');
INSERT INTO "ahmeti" VALUES(19,56,'19:56','Përkujtoju në libër edhe Idrisin! Ai ishte shumë i drejtë dhe pejgamber.');
INSERT INTO "ahmeti" VALUES(19,57,'19:57','Ne e ngriëm atë në një vend të Lartë (ia ngritëm lart famën).');
INSERT INTO "ahmeti" VALUES(19,58,'19:58','Këta (të përmendur) ishin që All-llahu i gradoi nga pejgamberët pasardhës të Ademit, prej pasardhësve të atyre që i patëm bartur (në anije) bashkë me Nuhun, prej pasardhësve të Ibrahimit dhe të Jakubit (Israilët), dhe prej atyre që i udhëzuam dhe i bëmë të zgjedhur; kur u lexoheshin atyre ajetet e Zotit, binin në sexhde dhe qanin.');
INSERT INTO "ahmeti" VALUES(19,59,'19:59','E pas tyre (të mirëve) erdhën pasardhës të këqij, që e lanë namazin e u dhanë pas kënaqësive (trupore), e më vonë do të hidhen në çdo gjë të keqe (ose në Gaja).');
INSERT INTO "ahmeti" VALUES(19,60,'19:60','Me pëjashtim të atij që pendohet dhe beson e bën vepra të mira, të tillët do të hyjnë në Xhennet dhe atyre nuk u bëhet padrejtë.');
INSERT INTO "ahmeti" VALUES(19,61,'19:61','Ato janë Xhennetet e Adnit. që Mëshiruesi u pat premtuar robve të vet, pa i parë ata (i besuan pa i parë), e premtimi i Tij është i kryer.');
INSERT INTO "ahmeti" VALUES(19,62,'19:62','Aty nuk dëgjojnë fjalë boshe, por vetëm përshëndetje. Aty manë ushqimin e vet (të llojllojshëm) mëngjes e mbrëmje.');
INSERT INTO "ahmeti" VALUES(19,63,'19:63','Ato janë Xhennetet që do t’ua trashëgojnë robve Tanë që ishin të ruajtur.');
INSERT INTO "ahmeti" VALUES(19,64,'19:64','Ne (engjënt, thotë Xhibrili) nuk zbresim (nuk vijmë) vetëm me urdhërin e Zotit tënd. Vetëm Atij i takon e tërë çështja e tashme e ardhme dhe mes tyre, e Zoti yt nuk është që harron.');
INSERT INTO "ahmeti" VALUES(19,65,'19:65','Ai është Zoti i qiejve dhe i tokës dhe çka ka në mes tyre, pra Atë adhuroje, e në adhurim ndaj Tij bëhu i qëndrueshëm. A di për Të ndonjë emnak (adash)!');
INSERT INTO "ahmeti" VALUES(19,66,'19:66','E njeriu thota: “A njëmed pasi të vdes do të nxirrem i gjallë (do të ringja;; en)?”');
INSERT INTO "ahmeti" VALUES(19,67,'19:67','Po, a nuk po mendon njeriu se Ne e krijuam atë më parë kur ai nuk ishte asgjë?');
INSERT INTO "ahmeti" VALUES(19,68,'19:68','Pasha Zotin tënd, Ne do t’i tubojmë (pas ringjalljes) ata bashkë më djajtë dhe do t’i afrojmë ata rreth Xhehennemit të gjunjësuar.');
INSERT INTO "ahmeti" VALUES(19,69,'19:69','Pastaj, nga secili grup do t’i kapim ata që kishin qenë më jorespektues ndaj të Gjithmëshirshmit.');
INSERT INTO "ahmeti" VALUES(19,70,'19:70','E Ne e dujmë më së miri se cili prej tyre ka më meritë të hudhet në të.');
INSERT INTO "ahmeti" VALUES(19,71,'19:71','Dhe nuk ka asnjë prej jush që nuk do t’i afrohet atij. Ky (kontaktim i Xhehennemit) është vendim i kryer i Zotit tënd.');
INSERT INTO "ahmeti" VALUES(19,72,'19:72','Pastaj, (pas kalimit pran tij) do t’i shpëtojmë ata që ishin ruajtur (mëkateve), e zullumqarët do t’i lëmë aty të gjunjëzuar.');
INSERT INTO "ahmeti" VALUES(19,73,'19:73','E kur u lexoheshin atyre ajetet tona të argumentuara, ata që nuk kshin besuar u thoshin atyre që kishin besuar: “Cili grup (ne ose ju) ka vend jete më të mirë dhe kuvend më autoritativ?”');
INSERT INTO "ahmeti" VALUES(19,74,'19:74','E, sa e sa gjenerata kemi shkatërruar para tyre e që ishin më të pajisur dhe më të dukshëm.');
INSERT INTO "ahmeti" VALUES(19,75,'19:75','Thuaj: “Atij që është në humbje (në mosbesim), le t’ia vazhdojë atij i Gjithfuqishmi për një kohë, e kur do ta shohin atë që po u premtohet: ose dënimin (në këtë jetë) ose kijametin, atëherë do ta kuptojnë se kush do të jetë në pozitë më të keqe dhe më i dobët në përkrahje!”');
INSERT INTO "ahmeti" VALUES(19,76,'19:76','E All-llahu u shton besimi atyre që u udhëzuan, e veprat e mira të pëjetshme, janë shprblim më i dobishëm te Zoti yt, dhe përfundim me i mirë.');
INSERT INTO "ahmeti" VALUES(19,77,'19:77','A je i njoftyar për atë që mohoi argumentet Tona e tha: “Mua githqysh do të më jepet pasuri e fëmijë (në botën tjetër)?”');
INSERT INTO "ahmeti" VALUES(19,78,'19:78','A je i njoftyar për atë që mohoi argumentet Tona e tha: “Mua githqysh do të më jepet pasuri e fëmijë (në botën tjetër)?”');
INSERT INTO "ahmeti" VALUES(19,79,'19:79','Jo, nuk është ashtu, po Ne do të shënojmë atë që po e thotë ai, dhe do të vazhdojmë atij dënim pas dënimi.');
INSERT INTO "ahmeti" VALUES(19,80,'19:80','Dhe Ne e trashëgojmë atë (në pasuri e fëmijë) që të thotë ai, e ai ka për të na ardhur i vetmuar.');
INSERT INTO "ahmeti" VALUES(19,81,'19:81','Dhe kur ata në vend të All-llahut adhuruan zota tjerë për t’u krenuar me ta.');
INSERT INTO "ahmeti" VALUES(19,82,'19:82','Përkundrazi, ata (idhujt) do të tërhiqen prej adhurimit të atyre dhe do të bëhen armiq të tyre.');
INSERT INTO "ahmeti" VALUES(19,83,'19:83','A nuk e di ti se Ne i kemi lëshuar djajt kundër jobesimtarëve, e ata nxisin pa ndërpreë në vepra të këqija.');
INSERT INTO "ahmeti" VALUES(19,84,'19:84','Pra, ti mos u ngut kundër tyre, se Ne jemi duke ua numëruar atyre (jetën në ditë, frymëmarrje etj.).');
INSERT INTO "ahmeti" VALUES(19,85,'19:85','Ditën kur do t’i tubojmë të devotshmit te i Gjithëmëshirshmi si musafirë të ftuar.');
INSERT INTO "ahmeti" VALUES(19,86,'19:86','Ndërsa mëkatarëve u grahim në Xhehennem të etshëm.');
INSERT INTO "ahmeti" VALUES(19,87,'19:87','Nuk ka të drejtë ndërmjetësim askush, përveç atij që ka lejuar i Gjithëfuqishmi.');
INSERT INTO "ahmeti" VALUES(19,88,'19:88','Ata thanë: “I Gjithëfuqishmi ka fëmijë”.');
INSERT INTO "ahmeti" VALUES(19,89,'19:89','Ju (jobesimtarë) vërtet sollët një fjalë shumë të shëmtuar.');
INSERT INTO "ahmeti" VALUES(19,90,'19:90','Aq të shëmtuar sa gati u copëtuan qiejt, gati pëlciti toka dhe gati u shembën kodra nga ajo (fjalë).');
INSERT INTO "ahmeti" VALUES(19,91,'19:91','Për atë se të Gjithëmëshirshmit i përshkruan fëmijë.');
INSERT INTO "ahmeti" VALUES(19,92,'19:92','E të Gjithëmëshirshmit nuk i takon të ketë fëmijë.');
INSERT INTO "ahmeti" VALUES(19,93,'19:93','Nuk ka tjeër, vetëm se githë çka është në qiej e në tokë ka për t’iu paraqitur Zotit si rob.');
INSERT INTO "ahmeti" VALUES(19,94,'19:94','Ai me diturinë e vet i ka përfshirë të githë, dhe ka numëruar e evidntuar çdo gjë të tyre në mënyrë të saktësishme.');
INSERT INTO "ahmeti" VALUES(19,95,'19:95','Dhe në ditën e kijametit secili do t’i paraqitet Atij i vetëmuar.');
INSERT INTO "ahmeti" VALUES(19,96,'19:96','Nuk ka dshim se ata që bënë vepra të mira, atyre i Gjithëmëshirshmi do t’u krijojë (në zemrat e tyre) dashuri.');
INSERT INTO "ahmeti" VALUES(19,97,'19:97','Ne e bëmë atë (Kur’anin) të lehtë me gjuhën tënde vetëm që me të t’i përgëzosh të devotshmit, dhe me të t’i tëheqish vërejtjen një populli që ëshë kryeneç.');
INSERT INTO "ahmeti" VALUES(19,98,'19:98','Sa shumë brezni kemi shkatërruar para tyre. A po vëreni ndonjë prej tyre, ose a po dëgjon zërin e ulët të tyre (nuk u ndihet zëri)?');
INSERT INTO "ahmeti" VALUES(20,1,'20:1','Ta Ha.');
INSERT INTO "ahmeti" VALUES(20,2,'20:2','Ne nuk ta shpallëm Kur’anin për të munduar ty.');
INSERT INTO "ahmeti" VALUES(20,3,'20:3','Ta shpallëm vetëm këshillë (përkujtim) për atë që frikësohet.');
INSERT INTO "ahmeti" VALUES(20,4,'20:4','Shpallje nga Ai që krijoi tokën dhe qiejt e lartë.');
INSERT INTO "ahmeti" VALUES(20,5,'20:5','Mëshiruesi që qëndron mbi Arsh.');
INSERT INTO "ahmeti" VALUES(20,6,'20:6','E Tij është çdo gjë që ekziston në qiej dhe në tokë dhe çdo gjë që gjendet në mes tyre, edhe ç’ka nën dhe.');
INSERT INTO "ahmeti" VALUES(20,7,'20:7','Andaj, nëse bën shprehjen (lutjen haptazi), Ai e di të fshehtën, madje edhe më shumë se kjo.');
INSERT INTO "ahmeti" VALUES(20,8,'20:8','Ai është All-llahu, nuk ka Zot pos Tij, Atij i takojnë emrat më të bukur.');
INSERT INTO "ahmeti" VALUES(20,9,'20:9','A të ka ardhur ndonjë njohuri për ndodhin e Musait?');
INSERT INTO "ahmeti" VALUES(20,10,'20:10','Kur ai e pa një zjarr, e i tha familjes së vet: “Rrini ku jeni, se vërejta një zjarr, e unë ndoshta do t’ju sjellë ndonjë acë prej tij, ose do të gjejë te zjarri ndonjë udhërrëfyes”.');
INSERT INTO "ahmeti" VALUES(20,11,'20:11','E kur shkoi te Ai (zjarri), u thirrë: O Musa!');
INSERT INTO "ahmeti" VALUES(20,12,'20:12','Vërtet Unë jam Zoti yt, hiq atë që ke mbathur (opingat e nallet), se je në luginën e shenjtë Tuva.');
INSERT INTO "ahmeti" VALUES(20,13,'20:13','Unë të zgjodha ty (për pejgamber), prandaj dëgjo mirë se ç’po të shpallet!');
INSERT INTO "ahmeti" VALUES(20,14,'20:14','Vërtet, vetëm Unë jam All-llahu, nuk ka zot tjetër pos Meje, pra Mua më adhuro dhe fal namazin për të më kujtuar Mua.');
INSERT INTO "ahmeti" VALUES(20,15,'20:15','S’ka dyshim se momenti (kijameti) do të vijë pa tjetër, Unë gati e kam fshehë atë; (do të vijë) për t’u shpërblyer secili njeri për atë që ka bërë.');
INSERT INTO "ahmeti" VALUES(20,16,'20:16','Prandaj, të mos shmang ty nga kjo, ai që nuk i beson atij dhe që është dhënë pas dëshirave, se atëherë shkatërrohesh.');
INSERT INTO "ahmeti" VALUES(20,17,'20:17','O Musa, ç’është ajo që e ke premtuar në të djathtën tënde?');
INSERT INTO "ahmeti" VALUES(20,18,'20:18','Ai (Musai) tha: “Ai është shkopi im që të mbahem në të, dhe me të u shkund (gjethe nga drunjtë) dhenve të mia, dhe me të kryej edhe nevoja tjera.');
INSERT INTO "ahmeti" VALUES(20,19,'20:19','Ai (All-llahu) i tha: “Hidh atë, O Musa!”');
INSERT INTO "ahmeti" VALUES(20,20,'20:20','Ai e hodhi atë, kur ja, ai një gjarpër i madh që lëvizte me shpejtësi.');
INSERT INTO "ahmeti" VALUES(20,21,'20:21','(All-llahu i) Tha: “Kape atë, e mos u frikëso, se Ne do ta kthejmë atë përsëri në gjendjen e mëparshme!”');
INSERT INTO "ahmeti" VALUES(20,22,'20:22','Dhe, fute dorën tënd e do ta nxjerrësh atë të bardhë pa asnjë të metë. Kjo është një mrekulli tjetër.');
INSERT INTO "ahmeti" VALUES(20,23,'20:23','(këtë e bëmë) Në mënyrë që të mundësojmë të shohësh disa nga argumentet Tona të mëdha.');
INSERT INTO "ahmeti" VALUES(20,24,'20:24','“Shko te faraoni se ai ka ngritur kokë (është bërë arrogant)”.');
INSERT INTO "ahmeti" VALUES(20,25,'20:25','Ai (Musai) tha: “Ma zgjëro (më ndihmo) gjoksin tim!');
INSERT INTO "ahmeti" VALUES(20,26,'20:26','Dhe më lehtëso në këtë punë timen!');
INSERT INTO "ahmeti" VALUES(20,27,'20:27','Më zgjidh nyjen e gjuhës sime!');
INSERT INTO "ahmeti" VALUES(20,28,'20:28','Që ta kuptojnë fjalën time!');
INSERT INTO "ahmeti" VALUES(20,29,'20:29','Më cakto një ndihmëtar nga familja ime,');
INSERT INTO "ahmeti" VALUES(20,30,'20:30','Harunin, vëllain tim,');
INSERT INTO "ahmeti" VALUES(20,31,'20:31','Që me të të ma forcosh fuqinë time,');
INSERT INTO "ahmeti" VALUES(20,32,'20:32','Bëma shok atë në punën time,');
INSERT INTO "ahmeti" VALUES(20,33,'20:33','Në mënyrë që të madhërojnë Ty më shumë,');
INSERT INTO "ahmeti" VALUES(20,34,'20:34','Dhe të përkujtojnë Ty shpesh.');
INSERT INTO "ahmeti" VALUES(20,35,'20:35','Vërtet, Ti je Ai që na sheh dhe na i di punët”.');
INSERT INTO "ahmeti" VALUES(20,36,'20:36','Ai (All-llahu) tha: “O Musa, t’u dha ajo që kërkove!”');
INSERT INTO "ahmeti" VALUES(20,37,'20:37','Ne edhe një herë ta dhuruam ty mirësinë Tona.');
INSERT INTO "ahmeti" VALUES(20,38,'20:38','Atëherë kur nënën e frymëzuam me atë që nuk kuptohet ndrushe pos me inspirim (me frymëzim - shpallje).');
INSERT INTO "ahmeti" VALUES(20,39,'20:39','(Ne i thamë) “Vëre atë (fëmijë, Musain) në arkë, e mandej hidhe në lumë e lumi e hjedhë në breg, atë e merre armiku Im dhe i tij. E nga ana Ime mbolla (në zemra të njerëzve) dashuri ndaj teje, e që të edukoheshe nën mbikëqyrjen Time”.');
INSERT INTO "ahmeti" VALUES(20,40,'20:40','Kur motra jote ecte (të përcillte) e thoshte: “A doni t’ju tregoj atë që do të kujdeset për të? E Ne të kthyem te nëna jote, që të gëzohej ajo e të mos mbetej e pikëlluar. Ti pate mbytur një njeri, e Ne të shpëtuam nga mërzia dhe të sprovuan me sprova të mëdha. Ti qëndrove me vite ndër banorët e Medjenit, e pastaj erdhe, o Musa, në kohën e caktuar”.');
INSERT INTO "ahmeti" VALUES(20,41,'20:41','Dhe Unë të zgodha ty për shpalljen Time.');
INSERT INTO "ahmeti" VALUES(20,42,'20:42','Shko me argumentet e Mia ti dhe vëllai yt, e mos e hiqni prej kujdesit të përmendurit ndaj Meje.');
INSERT INTO "ahmeti" VALUES(20,43,'20:43','Shkoni që të dy te faraoni, se ai vërtet e ka tepruar.');
INSERT INTO "ahmeti" VALUES(20,44,'20:44','Atij i thuani fjalë të buta, ndoshta ai mendohet a frikësohet.');
INSERT INTO "ahmeti" VALUES(20,45,'20:45','Ata të dy thanë: “Zoti ynë, ne kemi frikë se do të na ndëshkojë menjëherë ose do t’i kalojë kufijtë kundër nesh”.');
INSERT INTO "ahmeti" VALUES(20,46,'20:46','Ai (All-llahu) tha: “Mos u frikësoni, se Unë (me ndihmën Time) jam me ju, dëgjoj (ç’do t’ju thotë) dhe shoh (ç’do të bën me ju)”.');
INSERT INTO "ahmeti" VALUES(20,47,'20:47','I shkoni i thuani atij: “Ne jemi të dy dërguar të Zotit tënd, lëshoi beni israilët të vijnë më ne, e mos i mundo! Ne kemi ardhur me argumentet nga Zoti yt, e shpëtimi është për atë që ndjek rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(20,48,'20:48','Ne na është thënë se dënimi është për atë që gënjen (të dërguarit) dhe ia kthen shpinën besimit”.');
INSERT INTO "ahmeti" VALUES(20,49,'20:49','Ai (faraoni) tha: “E kush është Zoti i ju dyve, o Musa?”');
INSERT INTO "ahmeti" VALUES(20,50,'20:50','(Musai) Tha: “Zoti ynë është Ai që çdo sendi i dha formën e vet, pastaj e udhëzoi atë?”');
INSERT INTO "ahmeti" VALUES(20,51,'20:51','Ai tha: “Si është gjendja e popjve të mëparshëm?”');
INSERT INTO "ahmeti" VALUES(20,52,'20:52','Dija për ata është te Zoti im, e shënuar në një libër. Zoti im nuk gabon e as nuk harron”.');
INSERT INTO "ahmeti" VALUES(20,53,'20:53','(Zoti im) I cili për ju e bëri tokën djep, për të mirën tuaj hapi rrugë nëpër të, lëshoi shiun prej së larti, e kjo mundësoi që të mbijnë bimë të llojeve të ndryshme.');
INSERT INTO "ahmeti" VALUES(20,54,'20:54','Hani ju dhe kullotni kafshët tuaja (nga ato bimë). Vërtet, për të gjitha këto (që u përmendën) ka argumente për ata që kanë mendje të shëndodhë.');
INSERT INTO "ahmeti" VALUES(20,55,'20:55','Prej asaj (tokës) Ne u krijuam ju, e ju do t’u kthejmë përsëri në të, e prej saj do t’ju nxjerrim edhe një herë.');
INSERT INTO "ahmeti" VALUES(20,56,'20:56','Ne ia bëmë të mundshme atij t’i shohë të gjitha argumentet Ton, por ai i fënjeu ato dhe i refuzoi.');
INSERT INTO "ahmeti" VALUES(20,57,'20:57','(Atëherë faraoni) Tha: “O Musa, a mos na ke ardhur që me magjinë tënde të na nxjerrësh prej tokës tonë (prej Egjiptit)?');
INSERT INTO "ahmeti" VALUES(20,58,'20:58','Edhe ne do të kundërvihemi me një aso magjie, pra cakto një dhe një vend që do të na përshtatet, e që nuk do ta thyenim as ne as ti”.');
INSERT INTO "ahmeti" VALUES(20,59,'20:59','(Musai) Tha: “Koha e caktuar le të jetë dita e festës në mënyrë që njerëzit të tubohen para dite”.');
INSERT INTO "ahmeti" VALUES(20,60,'20:60','Faraoni u kthye dhe tuboi magjistarët e vet dhe doli në kohën (dhe vendin) e caktuar.');
INSERT INTO "ahmeti" VALUES(20,61,'20:61','Atëherë Musai u tha atyre (magjistarëve): “Mjerimi është juaji, prandaj mos shpifni ndaj All-llaht ndonjë gënjeshtër e t’ju shkatërrojë me një dënim, se pa dyshim ai që shpif ka dështuar keq”.');
INSERT INTO "ahmeti" VALUES(20,62,'20:62','Ata (magjistarët) polemizuan për çështjen e tyre, por bisedën e bënë fshehurazi (nga masa).');
INSERT INTO "ahmeti" VALUES(20,63,'20:63','Ata pastaj (pas bisedës së fshehur) thanë: “Këta të dy janë magjistarë, që me magjinë e tyre duan t’ju nxjerrin nga toka juaj dhe ta zhdukin drejtimin (ideologjinë) tuaj që padyshim është më i drejti”.');
INSERT INTO "ahmeti" VALUES(20,64,'20:64','Pra, (meqë janë magjistarë) ju vendosni seriozisht çështjen tuaj dhe dilni në shesh tok të bashkuar, e ai që trimfon sot, ai ka arritur suksesin,');
INSERT INTO "ahmeti" VALUES(20,65,'20:65','Ata thanë: “O Musa, do të hedhësh ti apo ne po hedhim të parët?”');
INSERT INTO "ahmeti" VALUES(20,66,'20:66','Ai (Musai) tha: Jo, hidhni ju! Kur, ja, atij iu dk se nga ajo magji e tyre litarët dhe shkopinjtë lëviznin (si gjarpërinj).');
INSERT INTO "ahmeti" VALUES(20,67,'20:67','E Musai ndjeu në vete njëfarë frike.');
INSERT INTO "ahmeti" VALUES(20,68,'20:68','Ne i thamë atij: “Mos ke frikë, është më se e sigurt se ti do të jesh triumfues!”');
INSERT INTO "ahmeti" VALUES(20,69,'20:69','Tani hidhe atë që e ke në dorën tëndetë djathtë (shkopin), që t’i gëlltisë ato që i bënë ata, sepse ajo që bënë ata nuk është asgjë tjetër pos mashtrim magjistari, e magistari nuk do të ketë sukses kudo qoftë.');
INSERT INTO "ahmeti" VALUES(20,70,'20:70','Atëherë (kur shkopi i Musait i ëlltiti) magjistarët u hodhën në sexhde e thanë: “Ne i besuam Zotit të Harunit dhe të Musait!”');
INSERT INTO "ahmeti" VALUES(20,71,'20:71','Ai (faraoni) tha: “Ai besuat atij para se t’u japë leje? Ai (Musai) është prijës i juaj, i cili a mësoi magjinë, unë do t’ua pres duart e këmbët tërthorazi e do t’u varë në trungujt e hurmave, e atëherë ju do a kuptoni se cili prej nesh ka dënim më të ashpër e më të vazhdueshëm?”');
INSERT INTO "ahmeti" VALUES(20,72,'20:72','Ata thanë: “Pasha Atë që na krijoi, nuk të japim përparësi ty ndaj argumenteve që na erdhën, e ti bëje atë që mendon ta bës, dhe mund ta zbatosh vetëm atë që i takonjetës së kësaj bote!”');
INSERT INTO "ahmeti" VALUES(20,73,'20:73','Ne i besuam Zotit tonë që Ai të na falë gabimet dhe magjinë, me të cilën ti na detyrove. All-llahu është më i miri (në të shpërblyerit) dhe më i përjetshmi (në të dënuar)!');
INSERT INTO "ahmeti" VALUES(20,74,'20:74','Ai që i paraqitet Zotit të vet si kriminelë, e ka Xhehennemin, në të cilin as nuk vdes as nuk jeton.');
INSERT INTO "ahmeti" VALUES(20,75,'20:75','Ndërsa ai që i paraqitet Atij besimtar e që ka bërë vepra të mira, të tillët i presin merita të larta.');
INSERT INTO "ahmeti" VALUES(20,76,'20:76','(do ta presin) Xhennete të Adnit, nëpër të cilët do të rrjedhin lumenj dhe atydo të jenë përjetë. Ky është shpërblimi i atij që është pastruar nga mosbesimi.');
INSERT INTO "ahmeti" VALUES(20,77,'20:77','Ne i kumtuam Musait: “Udhëto natën me robtë e Mij, hapu atyre me rrugë të terur në det, e mos ke frikë se do të zënë, a do të fundosësh”.');
INSERT INTO "ahmeti" VALUES(20,78,'20:78','Atyre iu vu prapa faraoni me ushtrinë e vet, po ata pësuan në det ashtu si pësuan.');
INSERT INTO "ahmeti" VALUES(20,79,'20:79','Faraoni e humbi popullin e vet, e nuk e udhëzoi.');
INSERT INTO "ahmeti" VALUES(20,80,'20:80','O bijt e israilit, Ne ju shpëtuam prej armikut tuaj, ua përcaktuam anën e djathtë të Turit (për shpalljen e Tevratit), ju furnizuam me rrëshirë (si mjalti) dhe shkurtëza.');
INSERT INTO "ahmeti" VALUES(20,81,'20:81','Hani nga të mirat që u dhuruam, e mos u bëni përbuzës se do t’u godasë hidhërimi Im, e atë që e zë hidhërimi Im, ai ka mbaruar.');
INSERT INTO "ahmeti" VALUES(20,82,'20:82','Nuk ka dyshim se Unë e fal atë që është penduar, që ka besuar, që ka bërë vepra të mira dhe që përqendrohet për në rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(20,83,'20:83','Po ty o Musa: “ç’të nguti para popullit tënd?”');
INSERT INTO "ahmeti" VALUES(20,84,'20:84','Ai u përgjigj: “Ja, ata jnë pranë meje, e unë u nguta te ti, o Zoti im, që të jeshë i kënaqur ndaj meje!”');
INSERT INTO "ahmeti" VALUES(20,85,'20:85','Ai (All-llahu) Tha: “Ne sprovuam popullin tënd pas teje, dhe ata i hodhi në humbje Samiriji!”');
INSERT INTO "ahmeti" VALUES(20,86,'20:86','Musai u kthye te populli i vet i hidhëruar e i pikëlluar dhe tha: “O populli im, a nuk u premtoi juve Zoti juaj premtim të mirë, a mos u bë kohë e gjatë e ju e harruat premtimin, ose dëshiruat të ju godasë hidhërimi nga Zoti juaj, andaj e thyet besën që ma keni dhënë?”');
INSERT INTO "ahmeti" VALUES(20,87,'20:87','Ata thanë: “Ne nuk e thyem besën tënde me dëshirën tonë, por ne ishim ngarkuar me barrë të rënda nga stolitë e popullit e i hodhëm ato (në zjarr), e ashtu i hodhi edhe Samirija”.');
INSERT INTO "ahmeti" VALUES(20,88,'20:88','E ai (Samirija) ua mbaroi atyre një viç me trup që pëlliste, e ata thanë: “Ky është zoti juaj dhe Zoti i Musait, po ai (Musai) e ka harruar”.');
INSERT INTO "ahmeti" VALUES(20,89,'20:89','A nuk e kuptonin ata se ai (viç) as nuk u përgjigjej atyre, e as nuk kishte mundësi t’ju sillte ndonjë dobi a dëm.');
INSERT INTO "ahmeti" VALUES(20,90,'20:90','E Haruni atyre u pat thënë më parë: “O populli im, ju vetëmu sprovuat me të, pse Zoti juaj është i Gjithmëshirëshmi, andaj ejani pas meje dhe më dëgjoni për këtë që po ua thëm”.');
INSERT INTO "ahmeti" VALUES(20,91,'20:91','Ata i thanë: “Ne nuk dp t’i ndahemi këtij (adhurimit të viçit) deri që të kthehet Musai!”');
INSERT INTO "ahmeti" VALUES(20,92,'20:92','Ai (Musai u kthye e) tha: “O Harun ç’të prngoi ty kur i pe se morën rrugën e gabuar,');
INSERT INTO "ahmeti" VALUES(20,93,'20:93','E ti mos u vazhdosh rrugën time? Amos e kundërshtove edhe ti porosinë time?”');
INSERT INTO "ahmeti" VALUES(20,94,'20:94','Ai (Haruni) tha: “O bir i nënës sime, mos më kap as për mjekrre as për flokë, se unë u frikësova se do të thuash: i përçave beni israilët dhe nuk e ke respektuar porosinë time?”');
INSERT INTO "ahmeti" VALUES(20,95,'20:95','Ai (Musai) tha: “E çka ishte puna jote, o Samirij?”');
INSERT INTO "ahmeti" VALUES(20,96,'20:96','Tha (Samirija): “Unë pashë atë që ata nuk panë, e mora një grusht nga gjurma e të dërguarit (nga fërkemi i kalit të Xhibrilit) dhe ia hodha atij (viçit) dhe kështu më mashtroi vetvetja”.');
INSERT INTO "ahmeti" VALUES(20,97,'20:97','Ai (Musai) tha: “Prandaj, largohu sepse sa të jeshë gjallë ke për të thënë: (këdo që e sheh) “Mos m’u afro!” E ty të pret edhe një moment (dënim) i pathyeshëm. E ti tash shikoje zotin tënd që e adhurove bindshëm, e tani atë do ta djegim e do ta bëjmë shkrumb dhe hirin e tij do ta shpërndajmë nëpër det”.');
INSERT INTO "ahmeti" VALUES(20,98,'20:98','I adhurari juaj është vetëm All-llahu, që përveç Tij nuk ka zot tjetër, e dituria e Tij ka përfshirë çdo send.');
INSERT INTO "ahmeti" VALUES(20,99,'20:99','Po kështu, Ne të rrëfejmë ty për disa ngandodhitë që kanë kaluar më heret dhe nga ana jonë të dhamë ty shpalljen plot përkujtime.');
INSERT INTO "ahmeti" VALUES(20,100,'20:100','Kush i kthen shpinën kësaj (shpalljes - Kur’anit), ai do të bartë në ditën e kijametit barrën më të rëndë.');
INSERT INTO "ahmeti" VALUES(20,101,'20:101','Nën atë do të qëndrojnë përgjithmonë. Sa barrë e keqe është për ta në ditën e kijametit.');
INSERT INTO "ahmeti" VALUES(20,102,'20:102','Ditën kur i fryhet surit (bririt), atë ditë Ne i tubojmë mëkatarët symavijosur (të verbër, të shëmtuar).');
INSERT INTO "ahmeti" VALUES(20,103,'20:103','E ata pëshpërisin mes vete: nuk qëndruat (në dynja) më shumë se nja dhjetë ditë.');
INSERT INTO "ahmeti" VALUES(20,104,'20:104','Ne më së miri e dijmë se çka flasin ata, e edhe atë kur më i mençuri prej tyre u tha: “Nuk qëndruat më shumë se një ditë!”');
INSERT INTO "ahmeti" VALUES(20,105,'20:105','Ty të pyesin edhe për kodrat, thuaj: “Zoti im mund t’i bëjë thërrime të shpërndara”.');
INSERT INTO "ahmeti" VALUES(20,106,'20:106','Dhe vendin e tyre ta bëjë rrafsh pa farë bime e ndërtese.');
INSERT INTO "ahmeti" VALUES(20,107,'20:107','E nuk do të shohësh në të as ultësirë e as lartësirë.');
INSERT INTO "ahmeti" VALUES(20,108,'20:108','Atë ditë, ata do të shkojnë pas thirrësit. e nuk do t’i largohen atij, e para të Gjithmëshirshmit ulen zërat e nuk dëgjohet tjetër pos një zë i ulët.');
INSERT INTO "ahmeti" VALUES(20,109,'20:109','Atë ditë nuk bën dobi as ndërmjetësimi, përveç atij, të cilin e ka lejuar i Gjithmëshirshmi të ndërmjetësojë dhe për të cilin e pëlqeu ndërmjetësimin.');
INSERT INTO "ahmeti" VALUES(20,110,'20:110','Ai i di ato që ishin para tyre dhe ato që vijnë pas, po dija e tyre nuk mund ta përfshij ate.');
INSERT INTO "ahmeti" VALUES(20,111,'20:111','E fytyrat (e mëkatarëve) e turpëruara, i përlen të Përjetshmit, të Gjithfuqishmit, sepse ka dështuar ai që barti padrejtësinë.');
INSERT INTO "ahmeti" VALUES(20,112,'20:112','Kush është besimtar dhe bën vepra të mira, ai nuk i frikësohet ndonjë padrejtësie e as ndonjë mungese (në shpërblim).');
INSERT INTO "ahmeti" VALUES(20,113,'20:113','Kështu Ne e shpallëm këtë Kur’an arabisht dhe përsëritëm në të vërejtjet, në mënyrë që ata të ruhen ose ai (Kur’ani) t’u sjellë atyre përvojë mësimi.');
INSERT INTO "ahmeti" VALUES(20,114,'20:114','I lartë është All-llahu, Sundue i vërtetë. Ti mos nxito me Kur’anin para se të përfundojë shpallja e tij te ti, dhe thuaj: “Zoti im, më shto dituri!”');
INSERT INTO "ahmeti" VALUES(20,115,'20:115','Na i patëm urdhëruar edhe Ademit (të mos i afrohej pemës), po ai harroi, pra re ai nuk gjetën vendosmëri.');
INSERT INTO "ahmeti" VALUES(20,116,'20:116','Përkujto kur Ne u thamë engjëjve: “Bëni sexhde ndaj Ademit, ata i bënë, pos Iblisit, i cili nuk deshi”.');
INSERT INTO "ahmeti" VALUES(20,117,'20:117','E Ne i thamë: “O Adem ky është armik yti dhe i bashkëshortes tënde, pra mos t’ju nxjerrë kurrsesi nga Xhenneti, e t’ju vë në vështirësi (për të siguruar mjetet e jetesës)”.');
INSERT INTO "ahmeti" VALUES(20,118,'20:118','Ti aty (në Xhennet) nuk ke për të qenë i uritur e as nuk ke për të mbetur i zhveshur.');
INSERT INTO "ahmeti" VALUES(20,119,'20:119','Aty nuk ke për të pasur etje e as nuk do të kesh vapë.');
INSERT INTO "ahmeti" VALUES(20,120,'20:120','Por, atë e ngacmoi djalli duke i thënë: “O Adem, a do të të treoj për pemën e pavdekshmërisë dhe të sundimit të pazhdukshëm!”');
INSERT INTO "ahmeti" VALUES(20,121,'20:121','Ata të dy hëngrën nga ajo (pemë) dhe që të dy u zhveshën, e ia nisën ta mbulojnë veten e tyre me gjethe që mbldhnin nëpër Xhennet, dh kështu Ademi theu urdhërin e Zotit të ve dhe gaboi.');
INSERT INTO "ahmeti" VALUES(20,122,'20:122','Mandej Zoti i vet atë të zgjedhur, ia pranoi pendimin dhe e vuri në rrugën e drejtë (të vendosur).');
INSERT INTO "ahmeti" VALUES(20,123,'20:123','Ai (All-llahu) u tha: “Zbritni prej aty që të gjithë, do të jeni armiq të njëri-tjetrit. Nëse u vjen na Unë udhëzim (libër e pejgamber) kush i përmbahet udhëimit Tim, ai nuk ka për të humbur (në dunja) e as nuk ka për të dështuar (në etën tjetër)”.');
INSERT INTO "ahmeti" VALUES(20,124,'20:124','E kush ia kthen shpinën udhëzimit Tim, do të ketë jetë të vështirë dhe në ditën e kijametit do ta ringjall të verbër.');
INSERT INTO "ahmeti" VALUES(20,125,'20:125','Ai (që nuk besoi) do të thotë: “Zoti im, prse më ngrite të verbër, kur unë isha me sy?”');
INSERT INTO "ahmeti" VALUES(20,126,'20:126','Ai (All-llahu) thotë: “Ashtu si harrove ti argumentet Tona që t’i ofruam, ashtu je ti i harruar sot”.');
INSERT INTO "ahmeti" VALUES(20,127,'20:127','Po kështu Ne e shpërblejmë edhe atë që zhytet në mëkate dhe nk i beson argumentet e Zotit të vet, po dënimi në botën tjetër është edhe më i ashpër dhe i përjetshëm.');
INSERT INTO "ahmeti" VALUES(20,128,'20:128','A nuk e kanë të qartë ata se sa breza para tyre i kemi zhdukur, e këta ecin nëpër ato vendbanime të tyre. Në gjurmët e atyre ka fakte për ata që kanë arsye të shëndoshë.');
INSERT INTO "ahmeti" VALUES(20,129,'20:129','E, sikur të mos ishte vendimi dhe afati i caktuar më heret nga Zoti yt, dënimi do t’i kapte ata.');
INSERT INTO "ahmeti" VALUES(20,130,'20:130','Prandaj, ti bëhu i durueshëm ndaj asaj që thonë ata, dhe duke falënderuar Zotin tënd, falu para lindjes së diellit dhe para perëndimit të tij, falu edhe në orët e matës dhe në skajet e ditës, ashtu që të gjejsh kënaqësi (me shpërblim).');
INSERT INTO "ahmeti" VALUES(20,131,'20:131','Dhe mos ia ngul sytë bukurisë së kësaj jete me të cilën i bëmë të kënaqen disa prej tyre (mosbesimtarë), e për t’i sprovuar me të, sepse shpërbimi i Zotit tënd është më i mirë dhe është i përjetshëm.');
INSERT INTO "ahmeti" VALUES(20,132,'20:132','Urdhëro familjen tënde të falë namaz, e edhe ti vetë zbatoje atë, ngase zne nuk kërkojmë prej teje ndonjë furnizim (për ty as për familjen tënde), Ne të frnizojmë ty (dhe ata), ardhmëria e mirë është e atij që ruhet.');
INSERT INTO "ahmeti" VALUES(20,133,'20:133','Ata thanë: “përse nuk na solli ai ndonjë argument nga Zoti i vet? A nuk u erdhi atyre sqarim (në Kur’an) për atë që ishte në broshurat e mëparshme”.');
INSERT INTO "ahmeti" VALUES(20,134,'20:134','Dhe sikur t’i ndëshkonim ata me ndonjë dënim para tij (para se të shpallim Kur’anin) ata do të thonin: “Zoti ynë, përse nuk na çove ndonjë të dërguar që t’iu përmbaheshim fakteve Tua para se të poshtëroheshim e të mjeroheshim”.');
INSERT INTO "ahmeti" VALUES(20,135,'20:135','Thuaj: “Secili është duke pritur, pra pritni edhe ju, se së shpejti do ta kuptoni se kush ishin ithtarët e rrugës së drejtë dhe e gjeti të vërtetën”.');
INSERT INTO "ahmeti" VALUES(21,1,'21:1','Njërezve u është afruar koha e llogarisë së tyre, e ata të hutuar në pakujdesi nuk përgatiten fare për të.');
INSERT INTO "ahmeti" VALUES(21,2,'21:2','Atyre nuk u vjen asnjë këshillë e re (në Kur’an) nga Zoti i tyre, e që ata të mos tallen me të duke e dëgjuar.');
INSERT INTO "ahmeti" VALUES(21,3,'21:3','Të shmangura nga e vërteta janë zemrat e tyre. Ata që janë zullumqarë dhe duke biseduar fshehtas (thonë): “A mos është ky (Muhammedi) diç tjetër pos njeri, sikurse edhe ju, pra a do t’ia pranoni magjinë e tij kur ju po e dini?”');
INSERT INTO "ahmeti" VALUES(21,4,'21:4','Ai (Muhammedi) tha: “Zoti im di për thënien që thuhet në qiell e në tokë (pra di edhe për bisedën tuaj), Ai është më dëgjuesi, më i dijshmi!”');
INSERT INTO "ahmeti" VALUES(21,5,'21:5','Ata madje thanë: “(për Kur’anin) Ëndërra të përziera, madje thanë se ai (Muhammedi) vet e trilloi, ai është poet! Le te na e sjellë një mrekulli ashtu si u dërguan të mëparshmit (me mrekulli)!”');
INSERT INTO "ahmeti" VALUES(21,6,'21:6','Banorët e qyteteve para tyre nuk i besuan (mrekullitë), prandaj Ne i shkatërruam, a mos do të besojnë këta?');
INSERT INTO "ahmeti" VALUES(21,7,'21:7','Ne edhe para teje nuk dërguam tjetër pos burra, të cilëve u dhamë shpallje, e nëse nuk dini këtë, atëherë pyetni njerëzit e dijshëm.');
INSERT INTO "ahmeti" VALUES(21,8,'21:8','Ne nuk i bëmë ata (pejgamberët) trupa që nuk hanë ushqim e as nuk ishin të përjetshëm (të pavdekshëm).');
INSERT INTO "ahmeti" VALUES(21,9,'21:9','Ne u plotësuam pastaj premtimin, i shpëtuam ata dhe kë dëshiruam Ne, ndërsa ata që kaluan çdo kufi, i zhdukëm.');
INSERT INTO "ahmeti" VALUES(21,10,'21:10','A nuk e kuptoni se Ne ua shpallëm librin në të cilin gjendet krenaria juaj?');
INSERT INTO "ahmeti" VALUES(21,11,'21:11','Ne shpartalluam sa vendbanime që ishin mizore, e ne vend të tyre sollëm popull tjetër.');
INSERT INTO "ahmeti" VALUES(21,12,'21:12','E ata kur e hetonin forcën e dënimit tonë, iknin me shpejtësi.');
INSERT INTO "ahmeti" VALUES(21,13,'21:13','(atëherë atyre u thuhet) Mos ikni, po kthehuni në begatitë dhe në vendbanimet tuaja se ndoshta do të mund të merreni vesh (e gjithë kjo ishte një ironi kndër tyre).');
INSERT INTO "ahmeti" VALUES(21,14,'21:14','Ata thonin: “O shkatërrimi ynë, vërtet ne ishim zullumqarë!”');
INSERT INTO "ahmeti" VALUES(21,15,'21:15','Mjerimi i tyre vazhdoi ashtu derisa i bëmë të korrur e të ftohur (të vdekur).');
INSERT INTO "ahmeti" VALUES(21,16,'21:16','Ne nuk e krijuam qiellin, tokën e çdo gjë çka mes tyre shkel e shko (pa qëllim të caktuar).');
INSERT INTO "ahmeti" VALUES(21,17,'21:17','Sikur të kishim dashur të zbavitemi dhe sikur të donim ta bënim atë, Ne do të zbaviteshim në kompetencën Tonë, por Ne nuk bëmë atë.');
INSERT INTO "ahmeti" VALUES(21,18,'21:18','Përkundrazi, Ne të pavërtetën e godasim me të vërtetën dhe ajo (e vërteta) triumfon mbi të ndërsa ajo (gënjeshtra) zhduket. E juve (jobesimtarëve) u takon shkatërrimi, për atë që i përshkruani (Zotit, si fëmijë etj.).');
INSERT INTO "ahmeti" VALUES(21,19,'21:19','Vetëm e Tij është çdo gjë që është në qiej e tokë! E ata që janë pranë TIj (melaiket), nuk shprehin mendjemadhësi në adhurim ndaj Tij, e as nuk u bëhet (ibadet) monotn.');
INSERT INTO "ahmeti" VALUES(21,20,'21:20','Ata lartësojnë (All-llahun) natë e ditë dhe nuk mërziten.');
INSERT INTO "ahmeti" VALUES(21,21,'21:21','A mos morën ata zota prej toke që mund të ringjallin (të vdekurit)?');
INSERT INTO "ahmeti" VALUES(21,22,'21:22','Sikur të kushte në to (në qiej e në tokë) zota pos Allhut, ato të dyja do të shkatërroheshin. Larg asaj që i përshkruajnë është All-llahu, Zot i Arshit.');
INSERT INTO "ahmeti" VALUES(21,23,'21:23','Ai nuk pyetet se çka punon, po ata, (njerëzit), pyeten.');
INSERT INTO "ahmeti" VALUES(21,24,'21:24','A përvetësuan ata zota tjerë pos Tij? Thuaj: “Sillne argumentin tuaj? Ky (Kur’ani) është argument për këta që janë më mua dhe për ata që ishin para meje, por shumica tyre nuk e dinë të vërtetën, andaj edhe nuk vështojnë.');
INSERT INTO "ahmeti" VALUES(21,25,'21:25','Ne nuk dërguam asnjë të dërguar para teje e të mos i kemi shpallur atij se: nuk ka zot tjetër përveç Meje, pra më adhuroni!”');
INSERT INTO "ahmeti" VALUES(21,26,'21:26','E ata thanë: “I Gjithëmëshirshmi ka fëmijë!” Larg asaj qoftë madhëria e Tij! Ja ata jnanë rob të ndershëm!');
INSERT INTO "ahmeti" VALUES(21,27,'21:27','Që nuk flasin para Tij, ata veprojnë me urdhërin e Tij.');
INSERT INTO "ahmeti" VALUES(21,28,'21:28','Ai (All-llahu) e di çka vepruan më parë dhe çka do të veprojnë, dhe ata nuk përpiqen të ndihmojnë pos për atë me të cilin është i kënaqur Ai, e ata nga frika prej tij janë të pakujdesshëm.');
INSERT INTO "ahmeti" VALUES(21,29,'21:29','Ndërsa, kush thotë prej tyre se unë jam zot pos Tij, ndëshkimi për të është Xhehennemi. Kështu i ndëshkojmë Ne zullumqarët.');
INSERT INTO "ahmeti" VALUES(21,30,'21:30','A nuk e dinë ata, të cilët nuk besuan se qiejt dhe toka ishin të ngjitura, e Ne i ndamë ato të dyja dhe ujin e bëmë bazë të jetës së çdo sendi; a nuk besojnë?');
INSERT INTO "ahmeti" VALUES(21,31,'21:31','Dhe në të (tokë) Ne kemi krijuar bjeshkë të paluhatshme që të mos i lëkundë ata, dhe nëpër to kemi bërë lugina e rrugë në mënyrë që ata të dinë të orientohen.');
INSERT INTO "ahmeti" VALUES(21,32,'21:32','Qiellin ua kemi bërë kulm të sigurt, por ata zbrapsen prej atyre argumenteve.');
INSERT INTO "ahmeti" VALUES(21,33,'21:33','Ai është që krijoi natën e ditën, diellin e hënën dhe secili prej tyre noton në orbitë.');
INSERT INTO "ahmeti" VALUES(21,34,'21:34','Ne, asnjë njeri para teje (Muhammed) nuk i dhamë jetë të përhershme, e nëse ti vdes, a mos do të mbesin ata përgjithmonë?');
INSERT INTO "ahmeti" VALUES(21,35,'21:35','çdo krijesë do ta shijojë vdekjen, e NE, në shenjë sprove ju sprovojmë me vështirësi e kënaqësi, dhe ju ktheheni te Ne.');
INSERT INTO "ahmeti" VALUES(21,36,'21:36','E kur të shohin ty ata që nuk besuan, nuk të marrin ndryshe vetëm se në tallje (duke thënë): “A ky është ai që përgojon zotët tuaj!” E ata përmendjen e Mëshiruesit (Rrahman) e mohojnë.');
INSERT INTO "ahmeti" VALUES(21,37,'21:37','Njeriu nga vet natyra e tij është i ngutshëm, e Unë do t’ua tregoj juve masën Time ndëshkuese, pra mos kërkoni t’u vie më shpejtë.');
INSERT INTO "ahmeti" VALUES(21,38,'21:38','Ata thanë: “Kur do të jetë ai premtim (me dënim) nëse thoni të vërtetën?”');
INSERT INTO "ahmeti" VALUES(21,39,'21:39','Sikur ta dinin ata që nuk besuan se atëherë nuk d të mund ta largojnë zjarrin as nga fytyrat e tyre e as nga shpinat, e as që do të ndihmohen (nuk do të ishin jobesimtarë).');
INSERT INTO "ahmeti" VALUES(21,40,'21:40','Por atyre do t’u vijë befas, do t’i tronditë ata, e atëherë as nuk mund ta largojnë dhe as nuk u jepet afat.');
INSERT INTO "ahmeti" VALUES(21,41,'21:41','Në të vërtetë, janë bërë përqeshje edhe me të dërguarit tjerë para teje, e ata që u tallën me ta, i përfshiu ajo me çka talleshin.');
INSERT INTO "ahmeti" VALUES(21,42,'21:42','Thuaj: “Kush u mbron juve prej (dënimit të) të Madhëruarit natën en ditën?” Por jo, ata i kthejnë shpinën këshillave të Zotit të tyre.');
INSERT INTO "ahmeti" VALUES(21,43,'21:43','A mos kanë ata pos Nesh zota të tjerë që i mbrojnë? Po ata nuk mund t’i ndihmojnë vetvetes (e lërë më adhuruesve), Ata (mosbesimtarët) as nuk do të pranohen prej Nesh në fqinjësi.');
INSERT INTO "ahmeti" VALUES(21,44,'21:44','Por Ne u dhamë atyre dhe prindërve të tyre jetë të gjatë me kënaqësi (e ata u mashtruan). A nuk e vërejnë ata se Ne u pakësojmë tokën nga anët e saj, atëherë a mos ata do të jenë fitues (o të humbur)?');
INSERT INTO "ahmeti" VALUES(21,45,'21:45','Thuaj: “Unë ua tërheq vërejtjen vetëm me anën e kësaj shpalljeje, po i shurdhëti nuk dëgjon thirrjen edhe kur i tërhiqet vërejtja”.');
INSERT INTO "ahmeti" VALUES(21,46,'21:46','E sikur t’i godasë pak ndonjë e keqe nga dënimi i Zotit tënd, ata do të thonë: “Të mjerët ne, i bëmë vetes padrejtësi”!');
INSERT INTO "ahmeti" VALUES(21,47,'21:47','Në ditën e gjykimit Ne do të vëmë peshoja të drejta, e askujt nuk i bëhet e padrejtë asgjë, edhe nëse është (vepra) sa peshoja e një kokrre të melit Ne do ta sjelllim atë. E mjafton që Ne jemi llogaritës.');
INSERT INTO "ahmeti" VALUES(21,48,'21:48','Ne i patëm dhënë Musait e Harunit Furkanin (dalluesin e të vërtetës e të kotës - Tevratin), që ishte dritë e këshillë për të devotshmit.');
INSERT INTO "ahmeti" VALUES(21,49,'21:49','Të cilët ia kanë frikën Zotit të tyre edhe pse edhe pse nuk e shohin dhe ata të cilët ia kanë dronë kijametit.');
INSERT INTO "ahmeti" VALUES(21,50,'21:50','Edhe ky (Kur’ani) është këshillë, është i bekuar që Ne e shpallëm. A, ju po e refuzoni.');
INSERT INTO "ahmeti" VALUES(21,51,'21:51','Ne i dhamë Ibrahimit herët të mbarën, sepse Ne kemi njohur mirë atë (ia dhamë të mbarën se e meritoi).');
INSERT INTO "ahmeti" VALUES(21,52,'21:52','Kur ai babai dhe popullit të vet u: “ç’janë këto statuja që i adhuroni?”');
INSERT INTO "ahmeti" VALUES(21,53,'21:53','Ata thanë: “I gjetëm të parët tanë, që po i adhuronin”.');
INSERT INTO "ahmeti" VALUES(21,54,'21:54','Ai u tha: “Edhe të parët tuaj e edhe ju qartë ishit të humbur!”');
INSERT INTO "ahmeti" VALUES(21,55,'21:55','Ata thanë: “Me gjithë mend e ke apo mos po tallesh!”');
INSERT INTO "ahmeti" VALUES(21,56,'21:56','Atëherë ai tha: “Jo, por Zoti juaj është Zoti i qiejve e i tokës, ështëai që i krijoi ato, ndërsa unë jam dëshmues për këtë!”');
INSERT INTO "ahmeti" VALUES(21,57,'21:57','Pasha All-llahun, posa të largoheni ju, unë kam për t’ia bërë atë që duhet statujave tuaja!');
INSERT INTO "ahmeti" VALUES(21,58,'21:58','Dhe i bëri ato copë-copë, përveç një më të madhes që e kishin ata më shpresë se atij do t’i drejtohen (për të kuptuar se kush i theu).');
INSERT INTO "ahmeti" VALUES(21,59,'21:59','(Kur u kthyen ii panë) Ata thanë: “Kush e bëri këtë me zotat tanë? Ai na qenka kriminel!”');
INSERT INTO "ahmeti" VALUES(21,60,'21:60','(pastaj) hanë: “Kemi dëgjuar për një djalosh që i përqeshte ato, të cilit i thonin Ibrahim”.');
INSERT INTO "ahmeti" VALUES(21,61,'21:61','Ata thanë: “Silleni atë këtu në sy të njerëzve që ta shohin (e ta dënijmë)”.');
INSERT INTO "ahmeti" VALUES(21,62,'21:62','I thanë: “A e bëre ti këtë me zotat tanë, o Ibrahim?”');
INSERT INTO "ahmeti" VALUES(21,63,'21:63','Ai tha: “Jo, por atë, e bëri i madhi i tytre, ju pyetni ata nëse janë që flasin?”');
INSERT INTO "ahmeti" VALUES(21,64,'21:64','Ata u ndalën e u menduan me veten, e dikush tha: “Vërtet, ju jeni ata të gabuarit (pse adhuroi gjëra të këto)”.');
INSERT INTO "ahmeti" VALUES(21,65,'21:65','Mirëpo, pastaj e shoshitën këtë gjë në kokat e tyre (u kthyen nga bindja në koëfortësi) dhe thanë: “Po ti e ke ditur se këta nuk flasin!”');
INSERT INTO "ahmeti" VALUES(21,66,'21:66','Ai tha: “A po adhuroni pra në vend të All-llahut asish që nuk u sjellin kurrfarë dobie as dëmi?”');
INSERT INTO "ahmeti" VALUES(21,67,'21:67','Medet për ju dhe për ata që i adhuroni, pos All-llahut, po a nuk po kuptoni?”');
INSERT INTO "ahmeti" VALUES(21,68,'21:68','Atëherë ata thanë: “Digjeni atë (Ibrahimin) dhe ndihmoni zotat tuaj, nëse doni t’u ndihmoni!”');
INSERT INTO "ahmeti" VALUES(21,69,'21:69','Po Ne i thamë: “O zjarr, bëhu i ftohtë dhe shpëtim për Ibrahimin!”');
INSERT INTO "ahmeti" VALUES(21,70,'21:70','Ata deshën t’i bëjnë atij (Ibrahimit) kurth, po Ne ata i bëmë më të dështuarit.');
INSERT INTO "ahmeti" VALUES(21,71,'21:71','E Ne shpëtuam atë e edhe Lutin në atë tokën që e kemi bekuar për njerëz.');
INSERT INTO "ahmeti" VALUES(21,72,'21:72','Dhe ia falën atij Is-hakun, e si dhuratë edhe Jakubin. Dhe që të gjithë i bëmë të mirë (pejgamberë).');
INSERT INTO "ahmeti" VALUES(21,73,'21:73','Dhe ata i bëmë shembëlltyrë që udhëzonin sipas urdhërit Tonë, i orientuam në punë të mira, në falljen e namazit, në dhënien e zeqatit, dhe ata ishin adhurues Tanë të sinqertë.');
INSERT INTO "ahmeti" VALUES(21,74,'21:74','Lutit Ne i dham të jetë pejgamber dhe i dijshëm, andaj edhe e shpëtuam prej atij fshati (vendbanim) që bënë punë të ndyra, ata ishin popull i dëmshëm e i prishur.');
INSERT INTO "ahmeti" VALUES(21,75,'21:75','Atë (Lutin) e shpëtuam dhe e vëmë nën mëshirën Tonë, vërtet ai ishte prej më të mirëve.');
INSERT INTO "ahmeti" VALUES(21,76,'21:76','(Përkujto) Edhe Nuhun kur më parë - pat thirrur (Zotin) e Ne ia pranuam atij (edhe luttjen) e ate dhe familjen e tij e shpëtuam nga ia tmerri i madh.');
INSERT INTO "ahmeti" VALUES(21,77,'21:77','Ne e mbrojtëm atë prej atij populli që përgënjeshtronte argumentet Tona, vërtet ai ishte popull i keq, prandaj i përmbytëm të gjithë ata.');
INSERT INTO "ahmeti" VALUES(21,78,'21:78','(Përkjto) Davudin e Sulejmanin kur pleqëronin për çëshjen e bimës (mbjelljes) të cilën delet e ati populli e kishin kullotur natën, e Ne ishim përcjellës të gjykimit të tyre.');
INSERT INTO "ahmeti" VALUES(21,79,'21:79','E sulejmanit Ne ia mësuam atë (përgjegjen e saktë), po secilit (prej) u patëm dhënë urtësi e dije. Ne e bëmë që kodrat dhe shpezët të madhërojnë (bëjnë tesbih) së bashk me Davudin. Ne kemi mundësi (ta bëjmë këtë) edhe e bëmë.');
INSERT INTO "ahmeti" VALUES(21,80,'21:80','Ne e mësuam atij (Davudit) mbarimin e petkave (të hekurta) për ju, që të ju mbrajnë në raste lufte. A jeni pra mirënjohës ju për këtë?');
INSERT INTO "ahmeti" VALUES(21,81,'21:81','Ndërsa Sulejmanit (ia nënshtruam) erën e fortë që sipas dëshirës se tuj ecte me të deri te toka të cilën e kemi bekuar Ne. Ne jemi të gjithëdijshëm për çdo gjë.');
INSERT INTO "ahmeti" VALUES(21,82,'21:82','Edhe disa nga djajtë (ia nënshtruam) që zhyteshin (në thellësi të ujit) dhe bënin për të (për Sulejmanin), por Ne i ruanim ata (të mos shmangeshin).');
INSERT INTO "ahmeti" VALUES(21,83,'21:83','(Përkujto) Edhe Ejubin kur iu drejtua Zotit të vet me lutje: “Më gjeti belaja, e Ti je më Mëshiruesi ndër Mëshiruesit!”');
INSERT INTO "ahmeti" VALUES(21,84,'21:84','Ne iu përgjegjëm atij nga mëshira e Jonë, ia hoqëm ato vështirësi që i kishte, ia dhamë familjen e tij dhe aq sa ishin ata, dhe bëmë prkujtim për të devotshmit.');
INSERT INTO "ahmeti" VALUES(21,85,'21:85','(Përkujto) Edhe Ismailin, Idrisin dhe Dhulkiflin, që të gjithë ishin të durueshëm.');
INSERT INTO "ahmeti" VALUES(21,86,'21:86','Ata i përfshimë në mëshirën Tonë, se ishin vërtet prej të mirëve.');
INSERT INTO "ahmeti" VALUES(21,87,'21:87','(Përkujto) Edhe atë të peshkut (Junusin) kur doli i hidhëruar (prej popullit) dhe mendoi se nuk do t’i vijë më ouna ngushtë, po në errësira ai tha se: “Nuk ka Zot pos Teje. Ti je i pastër, nuk ke të meta. Unë i bëra padrejt vetes!”');
INSERT INTO "ahmeti" VALUES(21,88,'21:88','Ne iu përgjigjëm atij, e shpëtuam nga tmerri. Kështu i shpëtoëmë Ne besimtarët.');
INSERT INTO "ahmeti" VALUES(21,89,'21:89','(Përkujto) Edhe Zekiriain kur e luti Zotin e vet: “Zoti im, mos më le të vetmuar se Ti je më i miri trashëgues (pas çdokujt)”.');
INSERT INTO "ahmeti" VALUES(21,90,'21:90','Ne ia pranuam lutjen e tij, ia dhuruam atij Jahjain dhe ia përmirësuam bashkëshorten atij. Ata përpiqeshin për punë të mira, na u luteshin duke shpresuar dhe duke u frikësuar, ishin respektues ndaj nesh.');
INSERT INTO "ahmeti" VALUES(21,91,'21:91','(Përkujto) Edhe atë që e ruajti nderin e saj, Ne e frymëzuam atë me shpirt (barrë) nga ana Jonë dhe atë dhe të birin e saj; bëmë mrekulli për njerëzit.');
INSERT INTO "ahmeti" VALUES(21,92,'21:92','Kjo fe është e juaja dhe është e vetmja fe (e shpallur), kurse Unë jam Zoti juaj, pra më adhuroni vetëm Mua.');
INSERT INTO "ahmeti" VALUES(21,93,'21:93','Po ata e ndanë çështjen e fesë dhe u përçanë mes vete (u ndanë në grupe: monoteistë, politeistë, jehudi, të krishterë, zjarrëputist etj.), mirëpo, që të gjithë do të vijnë te Ne.');
INSERT INTO "ahmeti" VALUES(21,94,'21:94','E kush bën ndonjë vepër të mirë dhe është besimtarë, mundi i tij nuk i mohohet, pse Ne i shënojmë ato.');
INSERT INTO "ahmeti" VALUES(21,95,'21:95','Ndërsa është e pamundur për (banorët e) një fshat të cilin e kemi shkatërruar Ne, të kthehen (në këtë jetë).');
INSERT INTO "ahmeti" VALUES(21,96,'21:96','Derisa të hapet (penda) e Jexhuxh Mexhuxhëve dhe ata do të zbresin nga çdo bregore me shpejtësi.');
INSERT INTO "ahmeti" VALUES(21,97,'21:97','Dhe derisa të jetë afruar premtimi i saktë (dita e kijametit) e ai është momenti kur mbeten të shtangët sytë e atyre që nuk besuan (në ato çaste thonë): “Të mjerët ne, vërtet ishim krejt të pavetëdijshëm për këtë (momment); por jo, është e vërtetë se ishim zullumqarë të mëdhenj.');
INSERT INTO "ahmeti" VALUES(21,98,'21:98','Ju, dhe ajo që adhuruat ju pos All-llahut, do të jeni lëndë e Xhehennemit, dhe keni për të hyrë në të.');
INSERT INTO "ahmeti" VALUES(21,99,'21:99','Sikur të kishit qenë ata zota, ata nuk do të hynin në të, po që të gjithë do të jenë aty përgjithmonë.');
INSERT INTO "ahmeti" VALUES(21,100,'21:100','Aty ata do të kenë rrënkim dhe aty ata nuk dëgjojë.');
INSERT INTO "ahmeti" VALUES(21,101,'21:101','E atyre të cilëve u priu e mbara nga ana jonë, do të jenë larg tij (Xhehennemit).');
INSERT INTO "ahmeti" VALUES(21,102,'21:102','Ata nuk nuk do të dëgjojnë as zhurmën e tij, dhe ata do të jenë përgjithmonë të kënaqur me atë që e dëshirojnë vetë.');
INSERT INTO "ahmeti" VALUES(21,103,'21:103','Ata nuk do t’i shqetësojë ajo frika e madhe (e fyerjes së gurit), dhe do t’i presin engjëjt (duke u uruar): “Kjo është dita e juaj që u premtohej”.');
INSERT INTO "ahmeti" VALUES(21,104,'21:104','(Përkujto) Atë ditë kur Ne e paloj qiellin sikurse palimi i fletëve në libër. Ashtu sikundër e kemi filluar krijimin (tuaj), e rikthejmë. Ky është obligimi yni, e Ne e bëjmë këtë.');
INSERT INTO "ahmeti" VALUES(21,105,'21:105','Ne e kemi shënuar në Zebur (në librat e shenjtë) pas shënimit (në Lehvi mahfudh), se me të vërtetë tokën do ta trashëgojnë robtë e Mij të mirë.');
INSERT INTO "ahmeti" VALUES(21,106,'21:106','Ne këtë (që u përmend) ka mjaft për një popull të dëgjueshëm.');
INSERT INTO "ahmeti" VALUES(21,107,'21:107','E Ne të dërguam ty (Muhammed) vetëm si mëshirë për të gjitha krijesat.');
INSERT INTO "ahmeti" VALUES(21,108,'21:108','Thuaj: “Mua më shpallet se Zoti i juaji është vetëm një Zot, e ju a po dorëzoheni?”');
INSERT INTO "ahmeti" VALUES(21,109,'21:109','E nëse ata refuzojnë, ti thuaju: “Unë u njoftova ju në mënyrë të barabartë, dhe se unë nuk e di se është afër ose larg ajo që po premtoheni?”');
INSERT INTO "ahmeti" VALUES(21,110,'21:110','Ai e di thënien e haptë, e di edhe atë që mbani fashehtë.');
INSERT INTO "ahmeti" VALUES(21,111,'21:111','E nuk e di, mos është kjo sprovë për ju (vonesa e dënimit), apo të kënaqeni deri në një kohë.');
INSERT INTO "ahmeti" VALUES(21,112,'21:112','Ai (Muhammedi) tha: “Zoti im, gjyko (mes meje dhe atyre gënjeshtarëve) me të vërtetën! E Zoti ynë, Mëshirues është ai prej të cilit kërkohet ndihmë kundër asaj që ju përshkruani!”');
INSERT INTO "ahmeti" VALUES(22,1,'22:1','O ju njerëz, ruanu dënimit nga Zoti juaj, sepse dridhja pranë katastrofës së kijametit është një llahtari e madhe.');
INSERT INTO "ahmeti" VALUES(22,2,'22:2','Atë ditë, kur ta përjetoni atë, secila gjidhënëse braktis atë që ka për gjini dhe secila shtatëzënë e hedh para kohe barrën e vet, ndërsa njerëzit duken të dehur, po ata nuk janë të dehur, por dënimi i All-llahut është i ashpër.');
INSERT INTO "ahmeti" VALUES(22,3,'22:3','Ka ndonjë nga njerëzit që kundërshton në çështje rreth All-llahut, pa kurrfarë dije dhe ndjek çdo djall të mbrapshtë.');
INSERT INTO "ahmeti" VALUES(22,4,'22:4','Ai (djalli) është gjykuar ta humbë dhe ta orientojë në vuajtje të zjarrit të madh, atë që i miqësohet atij.');
INSERT INTO "ahmeti" VALUES(22,5,'22:5','O ju njerëz, nëse dyshoni për ringjalljen, atëherë (mendoni krijimin tuaj) Ne u krijuam ju prej dheu, pastaj prej uji, pastaj prej një gjaku të ngjizur, pastaj prej një sa kafshatë mishi, që është krijesë e formuluar ose e paformuluar, në mënyrë që t’ju sqarojmë. Ne e përqëndrojmë në mitër deri në një afat të caktuar, e mandej u nxjerrim foshnje dhe ashtu të arrini pjekurinë tuaj. Ka që dikush prej jush vdes herët, e dikush të jetojë deri në pleqëri të thellë, në mënyrë që mos dijë asgjë nga dija që ka pasur. E ti e sheh tokën e thatë-të vdekur, e kur Ne ia lëshojmë asaj ujin ajo gjallërohet, shtohet dhe nga të gjitha llojet rritë bimë të këndshme.');
INSERT INTO "ahmeti" VALUES(22,6,'22:6','Këtë (e themi për ta ditur) se All-llahu është Ai i vërteti dhe se Ai ringjallë të vdekurit dhe Ai ka fuqi për çdo send.');
INSERT INTO "ahmeti" VALUES(22,7,'22:7','Dhe nuk ka dyshim se kijameti do të vijë patjetër dhe se All-llahu me siguri do t’i ngjallë ata të varrezave (të vdekurit).');
INSERT INTO "ahmeti" VALUES(22,8,'22:8','E ka ndonjë prej njerëzve që bën polemikë rreth çështjes së All-llahut, duke mos patur kurrfarë dije, kurrfarë udhëzimi dhe kurrfarë libri të besueshëm.');
INSERT INTO "ahmeti" VALUES(22,9,'22:9','Ai që shtrembron qafën (në shenjë mendjemadhësie), e përpiqet t’i humbë njerëzit nga rruga e All-llahut. Atij do t’i takojë poshtrimi në këtë jetë, ndërsa në ditën e kijametit atij do t’ia veshim dënimin e djegies (të zjarrit).');
INSERT INTO "ahmeti" VALUES(22,10,'22:10','Këtë për shkak të asaj ë bënë duart tua, ndërsa All-llahu nuk është zullumqar ndaj robërve.');
INSERT INTO "ahmeti" VALUES(22,11,'22:11','Ka nga njerëzit që adhuron Alahun me mëdyshje (luhatshëm) nëse e gode ndonjë e mirë aii qetësohet më të, po nëse e godet ndonjë e pamëndshme, ai kthehet në fytyrën e vet të vërtetë (të mëparshme), ai e ka humbur këtë dhe jetën tjetër e ky është ai dështimi i qartë.');
INSERT INTO "ahmeti" VALUES(22,12,'22:12','Ai në vend të All-llahut lut atë që nuk mund t’i bëjë as dëm as dobi. Edhe kjo është ajo humbja pambarim.');
INSERT INTO "ahmeti" VALUES(22,13,'22:13','Lut atë që dëmin e tij e ka më afër se dobinë e tij. Sa ndihmëtar e shok i keq qenke (do t’i thotë atij adhuruesi në ditën e kijametit).');
INSERT INTO "ahmeti" VALUES(22,14,'22:14','Ata të cilët besuan dhe bënë vepra të mira, All-llahu do t’i vejë në Xhennete, nën të cilët rrjedhin lumenj. S’ka dyshim, All-llahu punon atë që dëshiron.');
INSERT INTO "ahmeti" VALUES(22,15,'22:15','Kush mendon se All-llahu nuk do ta ndihmojë atë (Muhammedin) në këtë dhe në tjerën jetë, ai le të ngrehet me ndonjë mjet deri në qiell e le ta këputë (ndihmën), e le të shikojë se a po ia largon mjeshtëria e tij atë që po e mllefos.');
INSERT INTO "ahmeti" VALUES(22,16,'22:16','Dhe kështu Ne e zbritëm këtë (Kur’anin) plot me argumente të qarta, dhe se All-llahu vë në rrugë të drejtë atë që do.');
INSERT INTO "ahmeti" VALUES(22,17,'22:17','Është e sigurt se All-llahu do të bëjë dallimin (do të gjykojë) në ditën e kiametit në mes atyre që ishin besimtarë (myslimanë) dhe të atyre që ishin jehudi, sabejë, të krishterë, zjarrputistë, idhujtarë. All-llahu di dhe vështron çdo gjë.');
INSERT INTO "ahmeti" VALUES(22,18,'22:18','A nuk e di për All-llahun se Atij i nënshtrohet (i bën sexhde) kush është në qiej, edhe kush është në tokë, edhe në dielli, edhe kodrat, edhe bimët edhe shtazët, e edhe shumë njerëz, po shumë janë që dënimi është meritë e tyre. Atë që e poshtëron All-llahu nuk ka kush që mund të bëjë të ndershëm. All-llahu punon atë që dëshiron.');
INSERT INTO "ahmeti" VALUES(22,19,'22:19','Këta janë dy kundërshtarë (grupe kundërshtarësh: besimtarë dhe jobesimtarë) që janë zënë rreth Zotit të tyre; e atyre që nuk besuan, u qepën rroba prej zjarri e u hudhet uji i valë mbi kokat e tyre.');
INSERT INTO "ahmeti" VALUES(22,20,'22:20','Që me atë u shkrihet krejt çka në barqet e tyre e edhe lëkurat.');
INSERT INTO "ahmeti" VALUES(22,21,'22:21','Për ata janë edhe kamgjikët e hekurtë.');
INSERT INTO "ahmeti" VALUES(22,22,'22:22','Sa herë që përpiqen të dalin prej tij nga vuajtjet e padurueshme, kthehen në të përsëri (u thuhet): “Vuane dënimin me djegie!”');
INSERT INTO "ahmeti" VALUES(22,23,'22:23','E ata që besuan dhe bënë vepra të mira, All-llahu i vendosë në Xhennete, nëpër të cilin rrjedhin lumenj, aty stolisen ata me rrath të artë e me xhevahirë, e petkat e tyre janë të mëndafshta.');
INSERT INTO "ahmeti" VALUES(22,24,'22:24','Dhe ata janë që u udhëzuan me fjalët më të mira dhe u udhëzuan në rrugën e lavdishme.');
INSERT INTO "ahmeti" VALUES(22,25,'22:25','S’ka dyshim se ata të cilët nuk besuan dhe pengojnë prej rrugës së All-llahut dhe prej xhamisë së shenjtë (Qabes), të cilën Ne e bëmë të barabartë për njerëz, qofshin vendas ose të ardhur, dhe, kushdo që të përpiqet të bëjë ndonjë të keqe a ndonjë mëkat, Ne kemi për t’ua shijuar një dënim të idhët.');
INSERT INTO "ahmeti" VALUES(22,26,'22:26','Përkujtoje kur Ne e udhëzuam Ibrahimin për në vendin e shtëpisë (Qabës): të mos më përshkruaj Mua shok, pastroje shtëpinë Time për ata që e vizitojnë (bëjnë tavaf) që qëndrojnë në këmbë duke u falur, përkulur dhe që bëjnë sexhde.');
INSERT INTO "ahmeti" VALUES(22,27,'22:27','Dhe thirr ndër njerëz për haxhin, se të vijnë ty këmbësorë e edhe kalorës me deve të rraskapitura që vijnë prej rrugëve të largëta.');
INSERT INTO "ahmeti" VALUES(22,28,'22:28','(vijnë) Për të qenë të pranishëm në dobitë e tyre dhe që ta përmendin All-llahun në ato ditë të caktuara (në shenjë falënderimi) dhe për që i ka furnizuar me kafshë. Hani pra, prej tyre (kurbanave) dhe ushqeni të ngushtuarin e të varfërin.');
INSERT INTO "ahmeti" VALUES(22,29,'22:29','Pastaj le të heqin papastërtinë e tyre, le t’i zbatojnë premtimet e veta dhe le të sillën (bëjnë tavaf) rreth shtëpisë së lashtë.');
INSERT INTO "ahmeti" VALUES(22,30,'22:30','Pra, kështu: Kush madhëron atë që All-llahu e bëri të shenjtë, ajo është më e dobishme për te Zoti i vet. Juve u janë lejuar kafshët, pos atyre që u lexohen (në Kur’an) pra largohuni nga ndyesitë e idhujve dhe largohuni fjalës shpifëse.');
INSERT INTO "ahmeti" VALUES(22,31,'22:31','Duke qenë të sinqertë në besimin ndaj All-llahut, dhe duke mos i përshkruar Atij shok, e kush përshkruan shok All-llahut, ai është sikur të bjerë nga qielli e ta rrëmbejë shpendi, ose si ai të cilin e gjuan era e stuhishme në ndonjë vend të humbur.');
INSERT INTO "ahmeti" VALUES(22,32,'22:32','Kjo ëhtë kështu! E kush madhëron dispozitat e All-llahut, ajo është shenjë e devotshmërisë së zemrave.');
INSERT INTO "ahmeti" VALUES(22,33,'22:33','Ju keni dobi nga ato (shenja - kurbanë) për një kohë të caktuar, e pataj vendi i tyre është te shtëpia e lashtë.');
INSERT INTO "ahmeti" VALUES(22,34,'22:34','Ne i kemi caktuar çdo populli vendtherrjen (e kurbanit), që ta përmendin emrin e All-llahut për arsye se i furnizoi ata me kafshët. Zoti juaj është një Zot, andaj vetëm Atij dorëzohuni, e përgëzo të dëgjueshmit.');
INSERT INTO "ahmeti" VALUES(22,35,'22:35','Të cilët, kur përmendet All-llahu, u dridhen zemrat e tyre, të cilët janë të durueshëm ndaj asaj që i godet, të cilët rregullisht e falin namazin dhe të cilët japin për qëllime të dobishme nga ajo me çka furnizuam Ne.');
INSERT INTO "ahmeti" VALUES(22,36,'22:36','E devet (therrjen e tyre për kurban) ua kemi bërë prej dispozitave të All-llahut, e ju prej tyre keni dobi, andaj përmendie emrin e All-llahut duke qenë ato (të përgatitura për therrje) në këmbë, e kur të shtrihen ato në tokë (dhe t’u dalë shpirti), hani prej tyre dhe ushqeni nevojtarin dhe atë që lyp. Ashtu, ato ua vëmë në shërbimin tuaj që juu të jeni mirënjohës.');
INSERT INTO "ahmeti" VALUES(22,37,'22:37','te All-llahu nuk arrin as mishi as gjaku i tyre, por te Ai arin bindja e juaj. Ai ashtu ua nënshtroi ato juve që ta madhëroni All-llahu për udhëzimet që ua bëri. Bamirësëve merru myzhde.');
INSERT INTO "ahmeti" VALUES(22,38,'22:38','All-llahu largon dëmet e idhujtarëve ndaj atyre që besuan, se All-llahu nuk e do asnjë tradhtarë e bukëpërmbystë.');
INSERT INTO "ahmeti" VALUES(22,39,'22:39','Atyre që po sulmohen me luftë, u është dhënë leje të luftojnë, për shkak se u është bërë padrejtë, e All-llahu ka fuqi për t’u ndihmuar atyre (muslimanëve).');
INSERT INTO "ahmeti" VALUES(22,40,'22:40','(U lejuan të luftojnë) Ata, të cilët vetëm pse thanë: “All-llahu është Zoti ynë!” u dëbuan pa kurfarë të drejte. E sikur All-llahu të mos i zbrapste disa me disa të tjerë, do të rrënoheshin manastirët, kisha, havrat e edhe xhamitë që në to përmendet shumë emri i All-llahut. E All-llahu patjetër do ta ndihmojë atë që ndihmon rrugën e Tij, se All-llahu është shumë i fuqishëm dhe gjithnjë triumfues.');
INSERT INTO "ahmeti" VALUES(22,41,'22:41','(Ai ndihmon) Ata të cilët kur Ne u mundësojmë vendosjen në tokë, e falin namazin, japin zeqatin, urdhërojnë për të mirë dhe largojnë prej të keqes. All-llahut i takon përfundimi i çështjeve.');
INSERT INTO "ahmeti" VALUES(22,42,'22:42','Po edhe nëse të përgënjeshtrojnë ty, para tyre patën përgënjeshtruar edhe populli i Nuhut, Adi dhe Themudi.');
INSERT INTO "ahmeti" VALUES(22,43,'22:43','Edhe populli i Ibrahimit dhe populli i Lutit.');
INSERT INTO "ahmeti" VALUES(22,44,'22:44','Po edhe banorët e Medjenit. Qe përgënjeshtruar edhe Musai; e Unë jobesimtarëve u pata dhënë afat, më pastaj u dhashë dënim, e çfarë ishte ai dënim Imi? (Ua ndryshova gjendjen në të keq).');
INSERT INTO "ahmeti" VALUES(22,45,'22:45','E sa fshatra që ishin zullumqarë i shkatërruam Ne, e ato janë të rrënuara mbi kulmet e tyre dhe sa bunarë kanë mbetur shkretë e sa pallate të larta!');
INSERT INTO "ahmeti" VALUES(22,46,'22:46','A nuk udhëtuan ata nëpër tokë e të kenë zemra me të cilat do të kuptojnë, dhe veshë me të cilat do të dëgjojnë? pse në të vërtet sytë nuk verbërohen, por verbërohen zemrat në krahrorë.');
INSERT INTO "ahmeti" VALUES(22,47,'22:47','Ata kërkojnë prej teje t’ua ngutësh dënimin, po All-llahu nuk e thyen premtimin e Vet, pse një ditë te Zoti yt është sa njëmijë vet, si llogaritni ju.');
INSERT INTO "ahmeti" VALUES(22,48,'22:48','Shumë fshatra që ishin në rrugë të gabuar, Unë ua pata shtyrë dënimin në një kohë, e pastaj i zura me dënim dhe fundi i tyre është te Unë.');
INSERT INTO "ahmeti" VALUES(22,49,'22:49','Thuaj: “O ju njerëz, unë jam për t’ua tërhequr haptazi vërejtjen.');
INSERT INTO "ahmeti" VALUES(22,50,'22:50','E ata që besuan dhe bënë vepra të mira, do të kenë falje (të mëkateve), furnizim të begatshëm (në Xhenne).');
INSERT INTO "ahmeti" VALUES(22,51,'22:51','Ndërsa, ata që u përpoqën t’i mposhtin argumentet Tona, të tillët janë banes të Xhehennemit.');
INSERT INTO "ahmeti" VALUES(22,52,'22:52','Ne nuk dërguam para teje asnjë të dërguar (me shpallje) dhe asnjë pejgamber (të dërguar si vazhdues i shpalljes së mëparshme), e që, kur ai (i dërguari) dëshiroi diç, të mos ia pat hedhur në atë dëshirën e tij djalli, e All-llahu asgjëson atë që hedh djalli, dhe All-llahu fuqizon argumentet e Veta. All-llahu është shumë i dijshëm dhe me urtësi të madhe rregullon çështjet.');
INSERT INTO "ahmeti" VALUES(22,53,'22:53','E për a bërë atë (dyshim) që djalli e hedh si sprovë për ata që në zemrat e tyre kanë dyshim (munafikët) dhe për ata, që i kanë të zemrat e shtangëta. S’ka dyshim se jobesimtarët janë në një armiqësi të pambarim.');
INSERT INTO "ahmeti" VALUES(22,54,'22:54','E, edhe për ta vërtetuar të dijshmit se me të vërtetë ai (Kur’ani) është vërtetë prej zotit tënd dhe kështu t’i besojnë atij (Kur’anit) dhe (zemrat e tyre) të bindën ndaj tij. E All-llahu patjetër do t’i udhëzojë ata që besuan 9përqafuan) në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(22,55,'22:55','Ata që nuk besuan janë vazhdimisht në dyshim për të (për Kur’anin), derisa t’u vijë kijameti befas ose t’u vijë dënimi i ditës së pafrytshme.');
INSERT INTO "ahmeti" VALUES(22,56,'22:56','Atë ditë i tërë sundimi i takon vetëm All-llahut, Ai gjykon mes tyre, E sa u përket atyre që besuan dhe bënë vepra të mira, ata kanë kënaqësitë e tyre në xenete.');
INSERT INTO "ahmeti" VALUES(22,57,'22:57','Ndërsa, ata që nuk besuan dhe përgënjeshtruan argumentet ona, të tillët kanë dënim me nënçmim.');
INSERT INTO "ahmeti" VALUES(22,58,'22:58','E ata që për hir të All-llahut braktisën atdheun e tyre, (e duke luftuar) u vranë ose vdiçën, All-llahu do t’i shpërblej me shpërblimin më të mirë, e s’ka dyshim All-llahu është më i miri shpërblyes.');
INSERT INTO "ahmeti" VALUES(22,59,'22:59','Ata do t’i vë në një vend ku do të jenë të kënaqur. All-llahu di më së miri (kush çka merion) dhe është shumë i butë.');
INSERT INTO "ahmeti" VALUES(22,60,'22:60','Kjo është kështu! E kush ndëshkon tjetrin me të njejtën masë me të cilën ka qenë i ndëshkuar vetë, e pastaj atij përsëri i bëhet padrejtë, All-llahu do ta ndihmojë atë patjetër. All-llahu shlyen shumë të këqijat dhe falë mëkatet.');
INSERT INTO "ahmeti" VALUES(22,61,'22:61','Këtë (ndihmë) e bën ngase All-llahu (fuqiplotë) është Ai që fut natën në ditë e ditën në natë (dke shkurtuar njërën e zgjatur tjetrën). All-llahu me të vërtetë dëgjon (thëniet) e sheh (punët).');
INSERT INTO "ahmeti" VALUES(22,62,'22:62','Kjo ngase All-llahu është Ai i vërteti, dhe atë që lusin ata pos Tij është e kotë. All-llahu është vërtetë Ai i larti, i madhi.');
INSERT INTO "ahmeti" VALUES(22,63,'22:63','A nuk e di se All-llahu e lëshoi shiun prej qiellit, e toka agon e gjelbëruar, vërtet, All-llahu është i kujdesshëm, mirënjohës.');
INSERT INTO "ahmeti" VALUES(22,64,'22:64','Vetëm e Tij është çka ka në tokë, All-llahu është Ai që s’ka nevojë për asgjë, është i plotfuqishmi i falënderuari.');
INSERT INTO "ahmeti" VALUES(22,65,'22:65','A nuk sheh se All-llahu nënshtroi çdo gjë që gjendet në tokë, për të mirën tuaj, Ai nënshtroi anijet që sipias dëshirës së Tij të lundrojnë në det. Ai mban qiellin të mos bjerë në tokë po nëse dëshiron Ai. S’ka dyshim se All-llahu është mirëbërës, mëshirues ndaj njerëzve.');
INSERT INTO "ahmeti" VALUES(22,66,'22:66','Ai u dha jetën, u bën të vdisni, e pastaj ju ringjall. Vërtet, njeriu është përbuzës.');
INSERT INTO "ahmeti" VALUES(22,67,'22:67','Secilit popull ne i dhamë ligj (fetar) që ata të veprojnë sipas tij, pra le të mos polemizojnë (popujt tjerë) me ty për këtë çështje (për çështjen e shtetit islam), e ti thirri te Zoti yt, se pa dyshim ti je në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(22,68,'22:68','E nëse ata të kundërshtojnë ty, ti thuaju: “All-llahu e di më së miri për këtë që ju po veproni.');
INSERT INTO "ahmeti" VALUES(22,69,'22:69','All-llahu do të gjykojë mes jush në ditën e kijametit për atë që kundërshtoheshit.');
INSERT INTO "ahmeti" VALUES(22,70,'22:70','A nuk e ke ditur se All-llahu di çka ekziston në qiell e në tokë, e tërë ajo është e shënuar në libër, ajo për All-llahun është shumë lehtë.');
INSERT INTO "ahmeti" VALUES(22,71,'22:71','Ata (idhujtarët) adhurojnë në vend të All-llahut (idhuj) për të cilët nuk u ka ardhur kurrfarë fakti dhe nuk kanë dije, pra për idhujtarët nuk ka ndonjë ndihmëtar.');
INSERT INTO "ahmeti" VALUES(22,72,'22:72','E kur u lexohen atyre ajetet tona të qarta, në fytyrat e atyre që nk besuan u vëren urrejtje. Gati u vërsulen atyre që ua lexojnë ajetet tona. Thuaj: “A t’u tregoj për një më të keqe se kjo?” Zjarri, të cilin All-llahu e caktoi për ata që nuk besuan”. Sa i keq është ai vend ku do të shkojnë.');
INSERT INTO "ahmeti" VALUES(22,73,'22:73','O ju njerëz, ja një shembull veni veshin pra: Vërtet ata që po i adhuroni në vend të All-llahut, ata nuk mund të krijojnë asnjë mizë, edhe nëse tubohen të gjithë për të, e po ashtu, nëse miza ua rrëmben atyre ndonjë send, ata nuk do të mund ta shpëtojnë atë prej saj. I dobët është edhe lutësi edhe i luturi.');
INSERT INTO "ahmeti" VALUES(22,74,'22:74','Ata nuk madhëruan All-llahun me madhërinë e Tij të vërtetë, që meriton, All-llahu është ifuqishëm, ngadhnjyes.');
INSERT INTO "ahmeti" VALUES(22,75,'22:75','All-llahu zgjedh të dërguar prej egjëjve dhe prej njerëzve, All-llahu dëgjon e sheh.');
INSERT INTO "ahmeti" VALUES(22,76,'22:76','Ai e di atë që punuan më parë dhe atë që do të punojnë më vonë ata, dhe të gjitha çështjet i kthehen vetëm All-llahut.');
INSERT INTO "ahmeti" VALUES(22,77,'22:77','O ju që besuat, falni namazin me ruku e sexhde dhe vetëm Zotin tuaj adhuronie. Bëni punë të mira (të dobishme), se do të gjeni shpëtim.');
INSERT INTO "ahmeti" VALUES(22,78,'22:78','Luftoni me një luftë të denjë për hir të All-llahut, se Ai ju zgjodhi ju (ju përcaktoi për të luftuar për rrugën e TIj) dhe nuk ju obligoi në fe me ndonjë vështirësi, në fenë e babait tuaj, Ibrahimit. Ai edhe më parë, e edhe ky (Kur’ani) ju quajti myslimanë, për të qenë i dërguari ëshmitarë juaji dhe për të qenë ju dëshmitarë ndaj njerëzve, pra falni rregullisht namazin, jepni zeqatin, përmbahuni me All-llahun se Ai është ndihmëtari juaj. Sa mbrojtës i mirë dhe sa ndihmëtarë i madh që është.');
INSERT INTO "ahmeti" VALUES(23,1,'23:1','Është e sigurtë se kanë shpëtuar besimtarët:');
INSERT INTO "ahmeti" VALUES(23,2,'23:2','ata të cilët janë të përulur dhe janë të kujdesshëm gjatë faljes së namazit,');
INSERT INTO "ahmeti" VALUES(23,3,'23:3','dhe ata të cilët i shmangën të kotës (fjalë a punë),');
INSERT INTO "ahmeti" VALUES(23,4,'23:4','dhe ata të cilët rregullisht japin zeqatin,');
INSERT INTO "ahmeti" VALUES(23,5,'23:5','dhe ata të cilët e ruajnë nderin e vet (sa i përket jetës intime),');
INSERT INTO "ahmeti" VALUES(23,6,'23:6','me përjashtim ndaj grave të veta (me kurorë) dhe ndaj atyre (robreshave) që i kanë në pronësinë e vet, për të cilat nuk janë të qortuara,');
INSERT INTO "ahmeti" VALUES(23,7,'23:7','e kush kërkon përtej tyre (dëfrim nga të ndaluarat), të tillët janë ata që e kanë shkelë normat e caktuara,');
INSERT INTO "ahmeti" VALUES(23,8,'23:8','dhe ata të cilët me kujdes i ruajnë ato që u janë besuar (sende ose fjalë), e ruajnë edhe premtimin e dhënë,');
INSERT INTO "ahmeti" VALUES(23,9,'23:9','dhe ata të cilët i kushtojnë kujdes kohës së namazit të tyre.');
INSERT INTO "ahmeti" VALUES(23,10,'23:10','Të tillët janë mu ata trashëgimtarët,');
INSERT INTO "ahmeti" VALUES(23,11,'23:11','të cilët e trashëgojnë Firdewsin, aty janë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(23,12,'23:12','Për All-llahun, Ne krijuam njeriun prej një ajke (lëngu), e një balte.');
INSERT INTO "ahmeti" VALUES(23,13,'23:13','Pastaj atë (ajkë - baltë) e bëmë (e shëndrruam) pikë uji (farë) në një vend të sigurtë.');
INSERT INTO "ahmeti" VALUES(23,14,'23:14','Më pas, atë pikë uji e bëmë copë gjaku, e atë gjak të ngurtë e bëmë copë mishi, e atë copë mishi e shndërruam në eshtra, dhe eshtrave ua veshëm mishin, pastaj atë e bëmë krijesë tjetër (me shpirt). I lartë është All-llahu, më i miri Krijues!');
INSERT INTO "ahmeti" VALUES(23,15,'23:15','Mandej, pas kësaj (krijese) ju do të vdisni.');
INSERT INTO "ahmeti" VALUES(23,16,'23:16','E në ditën e kijametit ringjalleni.');
INSERT INTO "ahmeti" VALUES(23,17,'23:17','Ne krijuam mbi ju shtatë palë (qiej) dhe nuk jemi të pakujdesshëm ndaj asaj që krijuam.');
INSERT INTO "ahmeti" VALUES(23,18,'23:18','Ne lëshaum me masë ujë nga qielli dhe atë e përqëndrojmë në tokë. Po Ne kemi mundësi edhe ta humbim ate (ujin).');
INSERT INTO "ahmeti" VALUES(23,19,'23:19','Dhe me anën e tij Ne mundësuam pë ju kopshte hurmash e rrushi, që në to keni shumë pemë dhe hani prej tyre.');
INSERT INTO "ahmeti" VALUES(23,20,'23:20','Dhe (krijuam) trupa drunjsh që mbijnë në kodrën Sina e që japin vajëra dhe mëlmesa për ngrënësit.');
INSERT INTO "ahmeti" VALUES(23,21,'23:21','Ju ede nga kafshët keni një mësim, ngase ju freskojmë me lëng prej barqeve të tyre dhe keni shimë dobi prej tyre, e edhe prej tyre ushqeheni.');
INSERT INTO "ahmeti" VALUES(23,22,'23:22','Dhe mbi to e anije bartni (hypni).');
INSERT INTO "ahmeti" VALUES(23,23,'23:23','Ne e patëm dërguar Nuhun te populli i tij, e tha: “O populli im, adhuronie vetëm All-llahun, se nuk ka zot tjetër pos tij; a nuk i frikësoheni (dënimit)?”');
INSERT INTO "ahmeti" VALUES(23,24,'23:24','E paria e atij populli që nuk besuan thanë: “Ky nuk është tjetër pos njeri sikurse edhe ju, po dëshiron të jetë më i latrë se ju. Po të dëshironte All-llahu do të dërgonte engjëj; ne një gjë të tillë nuk e kemi dërguar as nga ta parët tanë!”');
INSERT INTO "ahmeti" VALUES(23,25,'23:25','Ai nuk është tjetër pos një njeri i sëmurë mentalisht, andaj pritni (keni durim) edhe për një kohë.');
INSERT INTO "ahmeti" VALUES(23,26,'23:26','Ai (Nuhu) tha: “Zoti im, më ndihmo për atë që më përgënjeshtrojnë.');
INSERT INTO "ahmeti" VALUES(23,27,'23:27','Ne e i shpallëm: “Ndërto anijen nën mbikëqyrjen Tonë dhe sipas urdhërit Tonë, e kur të vijë urdhëri Ynë dhe të ashpërsohet gjendja atëherë ti hgarko në të prej secilit lloj nga një qift (palë), edhe familjen tënde, me përjashtim të atij ndaj të cilit ka marrë fund vendimi, kundër tij pra ti mos kërko (falje) prej Meje për ata janë të përmbytur.');
INSERT INTO "ahmeti" VALUES(23,28,'23:28','E kur të vendosësh ti dhe kush është më ty në anije, atëherë thuaj: “Falënderuar qoftë All-llahu, i cili na shpëtoi prej popullit të prishur!”');
INSERT INTO "ahmeti" VALUES(23,29,'23:29','Dhe thuaj: “Zoti im, më zbarko në një vend të bekuar, e Ti je më i miri i atyre që bëjnë vendosjen!”');
INSERT INTO "ahmeti" VALUES(23,30,'23:30','Nuk ka dyshim se në këto (ngjarje të popujve) ekzistojnë fakte bindëse. Në të vërtetë Ne i vëmë në sprovë.');
INSERT INTO "ahmeti" VALUES(23,31,'23:31','Mandej pas tyre Ne ei sollëm popull tjetër.');
INSERT INTO "ahmeti" VALUES(23,32,'23:32','Edhe atyre u dërguam pejgamber mga mesi i tyre (që u tha): “Adhuronie All-llahun, ju nuk keni zot tjetër pos Tij, a nuk frikësoheni!”');
INSERT INTO "ahmeti" VALUES(23,33,'23:33','Edhe paria nga populli i tij e cila nuk besoi dhe e mohoi jetën tjetër dhe të cilëve Ne u patëm mundësuar rehati e begati në jetën e kësaj bote, thanë: “Ky nuk është tjetër pos njeri sikurse edhe ju, ha ashtu si hani ju dhe pi ashtu si pini ju!”');
INSERT INTO "ahmeti" VALUES(23,34,'23:34','E nëse i përuleni një njeriu që është si ju, vërtet, atëherë do të jeni të humbur e të mashtruar.');
INSERT INTO "ahmeti" VALUES(23,35,'23:35','Mos vallë ai po ju premton se pasi që të vdisni, të bëheni dhe e eshtra (të kalbur), do të nxirreni të gjallë?”');
INSERT INTO "ahmeti" VALUES(23,36,'23:36','Sa larg është sëndërtimi i asaj që premtoheni.');
INSERT INTO "ahmeti" VALUES(23,37,'23:37','Nuk ka tjetër, pos jetës sonë të kësaj bote, vdesim, lindim dhe nuk do të ringjallemi!');
INSERT INTO "ahmeti" VALUES(23,38,'23:38','E ai (Hudi (nuk është tjetër pos njeri që trillon gënjeshtra ndaj All-llahut, po ne nuk i besojmë atij.');
INSERT INTO "ahmeti" VALUES(23,39,'23:39','Ai (Hudi) tha “Zoti im, më ndihmo se ata po më përgënjeshtrojnë!”');
INSERT INTO "ahmeti" VALUES(23,40,'23:40','E Ai (Zoti) tha: “Së shpejti do të dëshprohen ata”.');
INSERT INTO "ahmeti" VALUES(23,41,'23:41','Atëherë me të drejtë ata i zuri ushtima (dënimi) dhe ashtu i bëmë sikur me qenë hedhurina (mebturina). Larg qoftë prej mëshirës së Zotit populli zullumqarë.');
INSERT INTO "ahmeti" VALUES(23,42,'23:42','Mandej pas tyre Ne sollëm brezni të reja.');
INSERT INTO "ahmeti" VALUES(23,43,'23:43','Asnjë nga popujt (e shkatërruar) nuk mundi ta shpejtojë e as ta vonojë afatin e caktuar.');
INSERT INTO "ahmeti" VALUES(23,44,'23:44','Ne më pas dërguam pejganberët tanë një pas një, mirëpo, çdo herë që një popull i erdhi i dërguari i vet, ata e përgënjeshtruan atë, prandaj Ne i përcollëm ata (me dënim) njein pas tjetrit dhe i bëmë që të përmenden në tregime. I shkatërruar qoftë populli që nuk beson!');
INSERT INTO "ahmeti" VALUES(23,45,'23:45','Më pas Ne dërguam Musain dhe vëllain e tuh Harunin me mrekulli dhe argumente tona të qarta,');
INSERT INTO "ahmeti" VALUES(23,46,'23:46','te faraoni dhe rrethi i tij, po ata u treguan kryelartë, sepse ishin popull mendjemadh.');
INSERT INTO "ahmeti" VALUES(23,47,'23:47','Dhe thanë: “Si t’u besojmë dy njerëzve që janë si ne, kur populli i tyre është në shërbim tonë?”');
INSERT INTO "ahmeti" VALUES(23,48,'23:48','Kështu, ata i përgënjeshruan që të dy, andaj me të drejtë i shkatërruam.');
INSERT INTO "ahmeti" VALUES(23,49,'23:49','Ne Musait i dhamë librin (pas shkatërrimit të faraonit) në mënyrë që ata (populli israilit) të gjejnë rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(23,50,'23:50','Edhe të birin e Merjemes dhe nënën e tij i paraq itëm si argumente (mrekulli) dhe ivendodëm që të dy në një rrafshnaltë dhe më ujë rrjedhës.');
INSERT INTO "ahmeti" VALUES(23,51,'23:51','(ne u patë thënë): “O ju të dërguar, hani atë që është e lejuar dhe mirë dhe bëni vepra të mira se Unë e di atë që ju veproni.');
INSERT INTO "ahmeti" VALUES(23,52,'23:52','Kjo feja juaj është e vetmja fe e Unë jam Zoti juaj, runju dënimit Tim.');
INSERT INTO "ahmeti" VALUES(23,53,'23:53','E ata më vonë u ndanë në grupe sipas çështjes së fesë dhe secili grup krenohej me atë që kishte përvehtësuar për vete.');
INSERT INTO "ahmeti" VALUES(23,54,'23:54','Andaj, ti (Muhammed) lëri ata edhe një kohë në atë mashtrimin e tyre.');
INSERT INTO "ahmeti" VALUES(23,55,'23:55','A mos mendojnë ata se me atë që jemi duke udhënë atyre nga pasuria dhe fëmijët,');
INSERT INTO "ahmeti" VALUES(23,56,'23:56','Nxitojmë t’u ofrojmë atyre të mirat? Jo, kurrsesi, por ata nuk janë kah e kuptojnë.');
INSERT INTO "ahmeti" VALUES(23,57,'23:57','Vërtet, ata të cilët ndaj madhërisë së Zotit të tyre kanë frikë.');
INSERT INTO "ahmeti" VALUES(23,58,'23:58','ata, të cilët u besojnë argumenteve të Zotit të vet,');
INSERT INTO "ahmeti" VALUES(23,59,'23:59','dhe ata, të cilët nuk i përshkruajnë shok Zotit të vet,');
INSERT INTO "ahmeti" VALUES(23,60,'23:60','dhe, ata të cilët japin (sadaka-zeqatë) nga ajo që u është dhënë, pse dinë se do të kthehen te Zoti i tyre, andaj zemrat e tyre i kanë të frikësuara,');
INSERT INTO "ahmeti" VALUES(23,61,'23:61','Të tillët janë duke nxituar drejt të mirave dhe të parët do t’i arrijnë ato.');
INSERT INTO "ahmeti" VALUES(23,62,'23:62','Ne nuk e obligojmë asnjë njeri përtej mundësive të tij dhe te Ne është libri (i shënive) që shënon saktësisht (veprat) dhe atyre nuk u bëhet padrejtë.');
INSERT INTO "ahmeti" VALUES(23,63,'23:63','Por zemrat e tyre (mohuasve) janë të verbëruara nga ky (libër), e përveç kësaj, ata vazhdojnë të bëjnë vepra të këqia.');
INSERT INTO "ahmeti" VALUES(23,64,'23:64','Ndërkaq, kur t’i kapim me dënim ata qëë kanë çuar jetë të shfrenuar, atëherë ata me britmë bëjnë lutje (duke kërkuar mdihmë).');
INSERT INTO "ahmeti" VALUES(23,65,'23:65','Mos e ngritni zërin tash, se ju nuk e keni ndihmën Tonë!');
INSERT INTO "ahmeti" VALUES(23,66,'23:66','Sepse, juve u lexoheshin ajatet e Mia, e ju zbrapseshit.');
INSERT INTO "ahmeti" VALUES(23,67,'23:67','Si arogant ndaj tij (ndaj Kur’anit, ose ndaj Qabës), e me fyerje kundër tij natën bisedoshit.');
INSERT INTO "ahmeti" VALUES(23,68,'23:68','A nuk menduan ata thellë fjalën (Kur’anin), apo mos u erdhi atyre diçka, që të parëve të tyre të lashtë nuk u pat ardhur,');
INSERT INTO "ahmeti" VALUES(23,69,'23:69','apo mos nuk e nnjohin të dërguarin e vet (Muhammedin), prandaj ata e refuzojnë atë?');
INSERT INTO "ahmeti" VALUES(23,70,'23:70','Apo mos thanë për të se është i çmendur? Jo, por ai ka ardhur me të vërtetën, e shumica e tyre e urren të vërteën.');
INSERT INTO "ahmeti" VALUES(23,71,'23:71','E sikur të përputhej e vërteta me dëshirat e tyre, do të shkatërroheshin qiejt e toka dhe çdo gjë që gjendet në to. E megjithatë që Ne atyre u sollëm (me këtë Kur’an) famën e tyre, ata u zbrapsën ndaj përkujtimit të vet.');
INSERT INTO "ahmeti" VALUES(23,72,'23:72','A mos u kërkon ti atyre ndonjë shpërblim? Po shpërblimi i Zotit tënd është më i miri, pse Ai është furnizues më i mirë.');
INSERT INTO "ahmeti" VALUES(23,73,'23:73','ç’është e vërteta, ti i fton ata në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(23,74,'23:74','Po ata që nuk e besojnë botën tjetër, i shmangen rrugës së drejtë.');
INSERT INTO "ahmeti" VALUES(23,75,'23:75','Edhe sikur t’i mëshironim ata (idhujtarët) dhe t’ua kishim larguar fatkeqësitë, at do të vazhdonin të zhyteshin në zhgënjimin e tyre.');
INSERT INTO "ahmeti" VALUES(23,76,'23:76','Ne i sprovuam at me dënim, po ata nuk iu përulën Zotit të tyre e as nuk iu drejtuan me lutje.');
INSERT INTO "ahmeti" VALUES(23,77,'23:77','Deri sa t’ua gapim një derë dënimi tëashpër, atëherë ata do të mbesin aty me shpresa të humbura.');
INSERT INTO "ahmeti" VALUES(23,78,'23:78','Ai është që ua krijoi të dëgjuarit, të parit dhe të kuptuarit e juaj; sa pak jeni mirënjohës.');
INSERT INTO "ahmeti" VALUES(23,79,'23:79','Dhe Ai është që ju krijoi ju në tokë dhe vetëm te Ai do të tuboheni.');
INSERT INTO "ahmeti" VALUES(23,80,'23:80','Dhe Ai është që jep jetë dhe vdekje dhe vetëm Ai bën ndryshimine natës e të ditës; si nuk e kuptoni?');
INSERT INTO "ahmeti" VALUES(23,81,'23:81','Por ja ata flasin, ashtu si flasin ata para tyre.');
INSERT INTO "ahmeti" VALUES(23,82,'23:82','Ata thanë: “Pasi të vdesim e të bëhemi dhe e eshtra të kalbur, a do të ringjallemi?');
INSERT INTO "ahmeti" VALUES(23,83,'23:83','Kjo na është premtuar ne edhe prindërve tanë më parë, po kjo nuk është gjë tjetër pos një mit i lashtë!');
INSERT INTO "ahmeti" VALUES(23,84,'23:84','Thuaj: “E kujt është toka dhe çdo gjë që ka në të, nëse jeni që e dini?”');
INSERT INTO "ahmeti" VALUES(23,85,'23:85','Ata do të thonë: “E All-llahut”. Atëherë thuaju: “Përse pra nuk mendoni?”');
INSERT INTO "ahmeti" VALUES(23,86,'23:86','Thuaj: “Kush është Zoti i shtatë qiejve dhe Zot i Arshit të madh?”');
INSERT INTO "ahmeti" VALUES(23,87,'23:87','Ata do të thonë: “All-llahu!” Thuaju: “E pse nuk frikësoheni pra?”');
INSERT INTO "ahmeti" VALUES(23,88,'23:88','Thuaj: “Në dorën e kujt është i tërë pushteti i çdo sendi, dh Ai është që mbron (kë të do), e që prej Atij nuk mund të ketë të mbrijtur; nëse jeni që e dini?”');
INSERT INTO "ahmeti" VALUES(23,89,'23:89','Ata do të thonë: “Në dorë të All-llahut!” Thuaj: “E si mashtroheni pra?”');
INSERT INTO "ahmeti" VALUES(23,90,'23:90','Por jo, Ne ua sollëm atyre të vërteën, e megjithatë, ata jetojnë me gënjeshtra (duke adhuruar idhuj).');
INSERT INTO "ahmeti" VALUES(23,91,'23:91','All-llahu nuk ka marrë për veten kurrfarë fëmijë (as engjëjt, e as nga njerëzit), nuk ka me Të ndonjë zot tjetër, pse (sikur të kishte zot tjetër), atëherë secili zot do të veçohej me atë që ka krijuar, dhe do të dominonte njëri mbi tjetrin! I lartë, i pastër është All-llahu nga ato që i përshkruajnë.');
INSERT INTO "ahmeti" VALUES(23,92,'23:92','Ai e di të fshehtën e të dukshmën, Ai është larg atyre që atë i shoqërojnë.');
INSERT INTO "ahmeti" VALUES(23,93,'23:93','Thuaj: “Zoti im, po që se patjetër do të më tregosh atë (dënim) që ua premtove atyre?”');
INSERT INTO "ahmeti" VALUES(23,94,'23:94','Zoti im, mos mëfut pra, mua te ai popull zullumqar!”');
INSERT INTO "ahmeti" VALUES(23,95,'23:95','Në të vërtetë, Ne kemi fuqi të tregojmë ty atë që u kemi premtuar atyre.');
INSERT INTO "ahmeti" VALUES(23,96,'23:96','Ti të keqen kthe me të mirën. Ne e dimë më së miri atë që shpifin ata.');
INSERT INTO "ahmeti" VALUES(23,97,'23:97','Dhe thuaj: “O Zot im, unë mbrohem me Ty prej cytjeve të djajve!”');
INSERT INTO "ahmeti" VALUES(23,98,'23:98','Dhe mbëhtetem te Ti që ata të mos më afrohen!”');
INSERT INTO "ahmeti" VALUES(23,99,'23:99','E kur ndonjërit prej tyre i vjen vdekja, ai thotë: “O Zoti im, më kthe,');
INSERT INTO "ahmeti" VALUES(23,100,'23:100','që të bëj vepra të mira e të kompensoj atë që lëshova!” Kurrsesi, (Kthim nuk ka) e kjo është vetëm fjalë që thotë ai, e ata kanë para tyre një perde (distancë periodike) der në ditën kur ringjallen.');
INSERT INTO "ahmeti" VALUES(23,101,'23:101','E kur i fryhet surit (herën e dytë), atëherë, në atë ditë nuk do të ketë lidhje familiare mes tyre e as që do të pyes kush për njëri-tjetrin.');
INSERT INTO "ahmeti" VALUES(23,102,'23:102','E kujt i peshojnë më rëndë peshojat (veprate mira), ata jamë të shpëtuarit.');
INSERT INTO "ahmeti" VALUES(23,103,'23:103','Ndërsa, atyre që u peshojnë lehtë, peshojat (veprat e mira), ata janë që humben vetveten dhe janë në Xhehennem përgjithmonë.');
INSERT INTO "ahmeti" VALUES(23,104,'23:104','Zjarri do t’ua djegë atyre fytyrat dhe do të duken shumë të shëmtuar brenda tij.');
INSERT INTO "ahmeti" VALUES(23,105,'23:105','(atyre u thuhet): A nuk u janë lexuar juve ajete e Mia; e ju nuk i besuat ato?');
INSERT INTO "ahmeti" VALUES(23,106,'23:106','Ata thonë: “O Zoti ynë, ne na patën mundur të këqiat (u dhamë pas epsheve të dynjasë) dhe si të tillë ishim popull i humbur!');
INSERT INTO "ahmeti" VALUES(23,107,'23:107','Zoti ynë, na nxjerr prej tij, e nëse gabojmë përsëri, atëherë vërtet, jemi mizor!”');
INSERT INTO "ahmeti" VALUES(23,108,'23:108','Ai thotë: “Heshtni aty e mos më folni!”');
INSERT INTO "ahmeti" VALUES(23,109,'23:109','Pse një grup prej robëve të Mi thanë: “O Zoti ynë, ne besuam, prandaj na falë dhe na mëshiro, se Ti je më i miri i mëshiruesve!”');
INSERT INTO "ahmeti" VALUES(23,110,'23:110','E ju i patët tallur ata, sa që ajo tallje bëri që të harronin përkujtimin ndaj Meje, madje edhe qeshnit me ta.');
INSERT INTO "ahmeti" VALUES(23,111,'23:111','E Unë sot i shpërbleva ata për atë që ishin të durueshëm atë janë fitimtarët.');
INSERT INTO "ahmeti" VALUES(23,112,'23:112','Ai thotë: “E sa vjet keni kaluar në tokë?”');
INSERT INTO "ahmeti" VALUES(23,113,'23:113','Ata thonë: “Kemi qëndruar një ditë ose një pjesë të ditës, pyeti ata që dinë numërimin!”');
INSERT INTO "ahmeti" VALUES(23,114,'23:114','Ai thotë: “Mirë e keni, sikur ta dishit njëmend pak keni qëndruar!”');
INSERT INTO "ahmeti" VALUES(23,115,'23:115','A menduat se Ne u krijuam kot dhe se nuk do ktheheni ju te Ne?');
INSERT INTO "ahmeti" VALUES(23,116,'23:116','I lartë është All-llahu, Sunduesi i vërtet, nuk ka zot tjetër pos Tij, Zot i Arshit bujar.');
INSERT INTO "ahmeti" VALUES(23,117,'23:117','E kush adhuron me All-llahun edhe ndonjë zot tjetër, për të cilin nuk ka kurrëfarë fakti, përgjegjësia e tij është para Zotit të vet, e mohuesit nuk do të shpëtojnë.');
INSERT INTO "ahmeti" VALUES(23,118,'23:118','E ti thuaj: “O Zoti im, më fal e më mëshiro, se Ti je me Mëshiruesi i mëshiruesve!”');
INSERT INTO "ahmeti" VALUES(24,1,'24:1','(Kjo është) Kaptinë të cilin Ne e shpallëm dhe Ne e bëmë detyrim të domosdoshëm (zbatimin e dipozitave që theksohen në të) dhe Ne parashtruam në të argumente të qarta në mënyrë që të merrni mësim.');
INSERT INTO "ahmeti" VALUES(24,2,'24:2','(dispozitë obliguese është që) Laviren dhe lavirin t’i rrahni, secilit prej tyre me nga njëqind të rëna, dhe në zbatimin e dipozitave të All-llahut mos u tregoni zemërbutë ndaj atyre dyve, nëse jeni që besoni All-llahut dhe botës tjetër. E gjatë zbatimit të ndëshkimit ndaj tyre, le të jenë të pranishëm një nmër besimtarësh.');
INSERT INTO "ahmeti" VALUES(24,3,'24:3','Laviri nuk do të duhej të martohej me tjetërkë, pos me ndonjë lavire ose idhujtare, e lavirja me të nuk do të duhen të martohej kush pos ndonjë lavir o idhujtarë. Ajo (kurvëria ose martesa më të) është e ndaluar për besimtarët.');
INSERT INTO "ahmeti" VALUES(24,4,'24:4','Edhe ata të cilët bëjnë shpifje për gratë e ndershme dhe nuk sjellin katër dëshmitarë, t’i rrahni ata me nga tetëdhjetë të rëna dhe atyre mos u pranoni dëshminë më kurrë. të tillët janë të pabesueshëm.');
INSERT INTO "ahmeti" VALUES(24,5,'24:5','Me përjashtim të atyre që më vonë pendohen dhe përmirësohen, vërtet All-llahu është që shumë falë e mëshiron.');
INSERT INTO "ahmeti" VALUES(24,6,'24:6','Ndërsa ata të cilët shpifin për gra të veta dhe nuk kanë dëshmitarë tjetër përveç vetes Dëshmimi i çdonjërit prej tyre është: të betohen katër herë në All-llahun se ai e thotë të vërtetën.');
INSERT INTO "ahmeti" VALUES(24,7,'24:7','E pesta (herë e betimit) është: mallkimi i All-llahut qoftë mbi të, nëse gënjen.');
INSERT INTO "ahmeti" VALUES(24,8,'24:8','E nga ajo (gruaja e shpifur) largohet dënimi, nëse katër herë betohet në All-llahun se ai (shpifësi) gënjen.');
INSERT INTO "ahmeti" VALUES(24,9,'24:9','E pesta herë e betimit të jetë që ta godis hidhërimi i All-llahut, atë (gruan) nëse ai, ka qenë i vërtetë.');
INSERT INTO "ahmeti" VALUES(24,10,'24:10','Dhe sikur të mos ishte dhurata dhe mëshira e All-llahut ndaj jush (ju do të mbarojshit). Pa dyshim Alahu pranon pendimin është i gjithëdijshëm.');
INSERT INTO "ahmeti" VALUES(24,11,'24:11','S’ka dyshim se ata që trilluan shpifjen, janë një grup prej jush. Ju mos e mrrni atë si ndonjë dëm për ju, përkundrazi, ajo do të jetë në dobinë tuaj. Secilit prej tyre do t’i takojë dënimi sipas pjesëmarrjes në mëkat, e atij prej tyre që e barti pjesën e madhe (të shpifjes) i takon dënim i madh.');
INSERT INTO "ahmeti" VALUES(24,12,'24:12','E përse kur e dëgjuan atë (shpifje) besimtarët dhe besimtaret të mos mendinin të mirën si për vete dhe të thonin: “Kjo është shpifje e qartë?!”');
INSERT INTO "ahmeti" VALUES(24,13,'24:13','Përse ata nuk i sollën katër dëshmitarë për këtë? E derisa nuk sollën dëshmitarë, ata pra te All-llahu janë mu gënjeshtarët.');
INSERT INTO "ahmeti" VALUES(24,14,'24:14','E sikur të mos ishte mëshira e All-llahut dhe mirësia e Tij ndaj jush, në këtë dhe në botën tjetër, ju do t’ju kapte dënim shumë i madh për shkak të asaj në të cilën u hudhët.');
INSERT INTO "ahmeti" VALUES(24,15,'24:15','Kur ju atë (shpifjen) e përcollët me gjuhën tuaj dhe për atë që nuk kishit kurrfarë dije folëshit duke menduar se ajo ishe imtëssi e parëndësishme, ndërsa te All-llahu ajo është e madhe.');
INSERT INTO "ahmeti" VALUES(24,16,'24:16','E përse, kur e dëgjuat atë, nuk thatë: “Ne nuk na takon të flasim për këtë. I lartë je Ti, kjo është shpifje e madhe!”');
INSERT INTO "ahmeti" VALUES(24,17,'24:17','All-llahu ju këshillon që një e tillë të mos përsëriet kurrë, nëse jeni besimtarë të denjë.');
INSERT INTO "ahmeti" VALUES(24,18,'24:18','All-llahu sqaron për ju argumentet, sepse Ai është i dijshëm e i urtë.');
INSERT INTO "ahmeti" VALUES(24,19,'24:19','Ata, të cilët dëshirojnë që te besimtarët të përhapet amoraliteti, ata i pret dënim i dhembshëm në këtë dhe në botën tjetër. All-llahu di (të fshehtat) e ju nuk e dini.');
INSERT INTO "ahmeti" VALUES(24,20,'24:20','Sikur të mos ishte mirësia e All-llahut ndaj jush dhe mëshira e Tij, (do ta përjetoshit menjëherë dënimin), po All-llahu është shumë i butë, i mëshirshëm.');
INSERT INTO "ahmeti" VALUES(24,21,'24:21','O ju që besuat, mo s shkoni gjurmëve të djallit, pse kush ndjek gjurmët e djallit ai urdhëron për të shëmtuara e të irituara, e sikur të mos ishte mirësia e All-llahut ndaj jush dhe mëshira e Tij, askush prej jush nuk do të pastrohej më kurrë (prej mëkatesh), por All-llahu e pastron atë që do Ai. All-llahu dëgjon e di.');
INSERT INTO "ahmeti" VALUES(24,22,'24:22','Të ndershmit dhe të pasurit nga ju, të mos betohen se nuk do t’u jain t afërmve, të varfërve dhe atyre që pr hir të All-llahut lanë vendlindje e tyre, po le t’ua falin (gabimin) dhe mos t’ua zënë për të madhe. A nukdëshironi që All-llahu t’u falë. All-llahu falë dhe mëshiron shumë.');
INSERT INTO "ahmeti" VALUES(24,23,'24:23','Vërtet, ata që akuzojnë gratë e ndershme, besimtare të pafajshme, janë të mallkuar në këtë botë dhe në botën tjetër dhe ata i pret një dënimshumë i madh.');
INSERT INTO "ahmeti" VALUES(24,24,'24:24','Në atë ditë kur dëshmojnë kundër tyre gjuhët e tyre, duart e tyre dhe këmbët e tyre për atë që ata kanë punuar.');
INSERT INTO "ahmeti" VALUES(24,25,'24:25','Atë ditë All-llahu ua plotëson shpërblimin e merituar, e ata e dinë se All-llahu është Ai i vërteti, i qarta në drejtësi.');
INSERT INTO "ahmeti" VALUES(24,26,'24:26','Të këqiat (fra ose fjalë) janë për të këqinjtë edhe të këqinjtë janë për të këqijat, ndërsa të ndershmet janë për të ndershmit edhe të ndershmit janë për të ndershmet. Ata janë të pafajshëm nga ajo që u thotë atyre. Ata kanë falje të madhe dhe furnizimtë begatshëm.');
INSERT INTO "ahmeti" VALUES(24,27,'24:27','O ju që besuat, mos hyni në shtëpi të huaja pa kërkuar leje dhe pa përshëndetur njerëzit e saj. Kjo është më mirë për ju në mënyrë që merri mësim.');
INSERT INTO "ahmeti" VALUES(24,28,'24:28','Po nëse nuk gjeni aty asnjë, atëherë mos hyni në to derisa t’u jepet leja, e nëse u thuhet kthehuni, ju pra kthenu. Kjo është më e ndershme për ju. All-llahu di çdo gjë që veproni ju.');
INSERT INTO "ahmeti" VALUES(24,29,'24:29','Nuk është për ju ndonjë mëkat, nëse hyni në shtëpi të pabanuara, e që në to keni ndonjë përfitim. All-llahu e di atë që e shfaqni haptas dhe atë që e mbani fshehtë.');
INSERT INTO "ahmeti" VALUES(24,30,'24:30','Thuaju besimtarëve të ndalin shikimet (prej haramit), të ruajnë pjesët e turpshme të trupit të tyre se kjo është më e pastër për ta. All-llahu është i njohur hollësisht për atë që bëjnë ata.');
INSERT INTO "ahmeti" VALUES(24,31,'24:31','Thuaju edhe besimtareve të ndalin shikimet e tyre, të ruajnë pjesët e turpshme të trupit të tyre. të mos zbulojnë stolitë e tyre përveç atyre që janë të dukshme, le të vejnë shamitë mbi krahrorin e tyre dhe të mos ua tregojnë bukuritë e tyre askujt përveç burrave të vet, babajve të vet ose babajve të burrave të vet, djemve të vet ose djemve të burrave të vet, vëllazërve të vet ose djemve të vëllezërve të vet, apo djemve të motrave të veta, ose grave të tyre (që u përmenden) dhe robreshave, të cilat i kanë në pronësinë e tyre, ose shërbetoreve nga medhkujt të cilët nuk ndiejnë nevojë pë femrat ose fëmijët nuk i kanë të pjekur për gra. Le të mos kërcasin me këmbët e tyre për të mos zbuluar fshehtsinë nga stolitë e tyre. Pendohuni të gjithë te All-llahu, o besimtarë, në mënyrë që të gjeni shpëtim.');
INSERT INTO "ahmeti" VALUES(24,32,'24:32','Dhe marotoni të pamartuarat (të pamartuarit) edhe robërit e robëreshat tuaja që janë të ndershëm e të ndershme. Nëse janë të varfër, All-llahu i begaton nga mirësitë e Tij, All-llahu është bujar i madh, i gjithdijshëm.');
INSERT INTO "ahmeti" VALUES(24,33,'24:33','E ata që nuk kanë mundësi martese, le të përmbahen derisa All-llahu t’i begatojë me të mirat e veta. E ata që i keni në pronësinë tuaj dhe kërkojnë prej jush t’i lironi në bazë të kompenzimit, atëherë ua bëni këtë të mundshme me marrveshje, nëse e dini se janë të besueshëm dhe jepnu nga pasuria që u ka dhënë All-llahu juve. Mo i detyroni robreshat tuaja të bëjnë imoralitet, e për shkak të ndonjë fitimi të kësaj bote, derisa ato dëshirojnë të jenë të pastra. E kush i detyron me dhunë të bëjnë atë, All-llahu i falë ato pas atij detyrimi. All-llahu falë dhe është mëshirues.');
INSERT INTO "ahmeti" VALUES(24,34,'24:34','Ne u kemi shpallë juve argumente të qarta dhe shembuj nga ata që ishin para jush, e edhe kështu për të ruajturit.');
INSERT INTO "ahmeti" VALUES(24,35,'24:35','All-llahu është dritë (ndiçues, udhëzues) i qiejve e i tokës. Shembulli i dritës së Tij i gjason kandilit të vendosur në një zgavër të errët. Kandili gjendet brenda një qelqi si të ishte yll flakërus e që ndizet prej (vajit të) një druri të bekuar, prej ullirit që nuk mund të quhet as i lidjes e as i perëndimit, e vaji i tij ndriçon pothuajsse edhe pa e prekur zjarri. Dritë mbi dritë. All-llahu udhëzon kah drita e Vet atë që do Ai. All-llahu sjell shembuj për njerëzit. All-llahu çdo gjë di shumë mirë.');
INSERT INTO "ahmeti" VALUES(24,36,'24:36','(ajo dritë) Është në shtëpitë (xhamitë) që All-llahu lejoi të ngriten, e që në to të përmendet emri i Tij, t’i bëhet lutje Atij mëngjes e mbrëmje.');
INSERT INTO "ahmeti" VALUES(24,37,'24:37','Ata janë njerëz që nuk i pengon as tregëtia e largët e as shitblerja në vend për ta përmendur All-llahun, për ta falur namazin dhe ta dhënë zeqatin, ata i frikësohen një dite kur do të tronditen zemrat dhe shikimet.');
INSERT INTO "ahmeti" VALUES(24,38,'24:38','(ata i luten) që Allhu t’i shpërblejë më së miri për atë që punuan dhe për t’ua shtuar të mirat nga Ai. All-llahu e dhuron pa masë atë që do.');
INSERT INTO "ahmeti" VALUES(24,39,'24:39','E veprat e atyre që nuk besuan janë si valët (nga rrezet e diellit) në një rrafshinë ku i etshmi mendon se është ujë derisa kur t’i afrohet atij nuk gjen asgjë, por aty e gjen All-llahun dhe ai do t’ia japë llogarinë e tij. Alahu është i shpejtë në llogaritje.');
INSERT INTO "ahmeti" VALUES(24,40,'24:40','Ose (veprat e tyre) janë si errësirat në det të thellë të cilin e mbulojnë valët mbi valë dhe mbi ato retë e dendura, pra, errësira njëra mbi tjetrën sa që nuk mund ta shohë ai as dorën e vet. Atij të cilit All-llahu nuk jep dritë, ai nuk ka për të pasur dritë.');
INSERT INTO "ahmeti" VALUES(24,41,'24:41','A nuk e di për All-llahun se Atij i lutet kush është në qiej e tokë, madje edhe shpezët krahhapura. Të gjithë e dinë dhe janë të udhëzuara në lutje ndaj Tij dhe për madhërimin e Tij. All-llahu e di ç’punojnë ata.');
INSERT INTO "ahmeti" VALUES(24,42,'24:42','Vetëm i All-llahut është i tërë sundimi i qiejve e i tokës dhe vetëm te Ai është e ardhmja.');
INSERT INTO "ahmeti" VALUES(24,43,'24:43','A nuk e ke parë se si All-llahu drejton retë, pastaj i bashkon, i bënë grumbull ato dhe atëherë e sheh shiun se si rrjedh prej tyre. Ai lëshon prej së larti, nga retë e mëdha si kodra breshër dhe me te goditë këdo, e ia largon atij që do. Shkëlqimi i vetëtimës së dritës së resë gati ses’të merr të të parit.');
INSERT INTO "ahmeti" VALUES(24,44,'24:44','All-llahu bënë rrotullimin e natës e të ditës dhe në të gjitha këto ka fakte për ata që kanë të parit të shëndoshë.');
INSERT INTO "ahmeti" VALUES(24,45,'24:45','All-llahu krijoi secilën gjallesë prej uji, e prej tyre ka që ecin rrëshqitas në barkun e vet, ka që ecin në të dy këmbët e edhe asosh që ecin në të katër këmbët. All-llahu krijon çka të dojë, pse vërtet All-llahu ka mundësi për çdo gjë.');
INSERT INTO "ahmeti" VALUES(24,46,'24:46','Ne kemi shpallur argumente të qarta, e All-llahu vë në rrugën e drejtë atë që do.');
INSERT INTO "ahmeti" VALUES(24,47,'24:47','Ata (hipokritët) thonë: “Ne i kemi besuar All-llahut, të dërguarit të Tij, dhe u jemi bindur. Por pas asaj një grup prej tyre zbrapsën. Të tillët nuk janë besimtarë.');
INSERT INTO "ahmeti" VALUES(24,48,'24:48','E kur thirrën që ndërmjet tyre të gjykojë All-llahu dhe i dërguari i tij, një grup prej tyre nuk i përgjigjen asaj.');
INSERT INTO "ahmeti" VALUES(24,49,'24:49','Po nëse është që e drejta u takon atyre (në atë gjykim), ata i shkojnë atij të bindur e me respekt.');
INSERT INTO "ahmeti" VALUES(24,50,'24:50','A thua janë të sëmurë në zemrat e tyre apo mos dyshojnë (në të dërguarin) ose mos frikësohen se All-llahu dhe i dërguari i Tij do të gjykojë padrejtë kundër tyre? Por jo, ata vet janë zullumqarë.');
INSERT INTO "ahmeti" VALUES(24,51,'24:51','Kur thirrën besimtarët për gjykim ndërmjet tyre te All-llahu dhe te i dërguari i Tij, e vetmja fjalë e tyre është të thonë: “Dëgjuam dhe respektuam!” Të tillët janë ata të shpëtuarit.');
INSERT INTO "ahmeti" VALUES(24,52,'24:52','Kush respekton All-llahun dhe të dërguarin e Tij, ia ka frikën All-llahut dhe i përmbahet udhëzimit të TIj, të tillët janë ata që do të arrijnë atë që dëshirojnë.');
INSERT INTO "ahmeti" VALUES(24,53,'24:53','Ata (hipokritët) betohen me një betim të fortë në All-llahun se sikur t’i urdhëroshe ti, ata gjithsesi do të dilnin në (për në luftë). Thuaj: “Mos u betoni” Bindja është ditur, (ju vetëm me gojë thoni e jo me zemër). S’ka dyshim, All-llahu hollësisht di çka vepron.');
INSERT INTO "ahmeti" VALUES(24,54,'24:54','Thuaj: “Respektone All-llahun, respektone të dërguarin!” Nëse ata refuzojnë, atëherë atij i takon përgjegjësia me çka është i ngarkuar ndërsa juve përgjegjësia me çka jeni ngarkuar, nëse i bindeni atij, atëherë keni gjetur të vërtetën. I dërguari ka obligim vetëm komunikimin e qartë.');
INSERT INTO "ahmeti" VALUES(24,55,'24:55','Atyre nga mesi juaj të cilët besuan dhë bënë vepra të mira All-llahu u premtoi se do t’i bëjë zotërues në atë tokë ashtu si pat bërë zotërues ata që ishin para tyre dhe fenë të cilën Ai e pëlqeu për ta, do ta forcojë, e në vend të frikës Ai do t’u dhurojë siguri. Ata më adhurojnë Mua e nuk më shoqërojnë asgjë. E kush edhe pas kësaj mohon, të tillë janë ata më të prishurit.');
INSERT INTO "ahmeti" VALUES(24,56,'24:56','Falni rregulisht namazin, jepni zeqatin dhe respektoni të dërguarin në mënyrë që të mëshiroheni.');
INSERT INTO "ahmeti" VALUES(24,57,'24:57','Kurrsesi mos mendo se janë të pathyrshëm në këtë jetë ata që nuk besuan. Vendi tyre është zjarri e sa përfundim i keq është ai.');
INSERT INTO "ahmeti" VALUES(24,58,'24:58','O ju të cilët besuat, ata të cilët i keni në pronësinë tuaj (shërbëtorët) dhe ata që nuk kanë arritur moshën e pjekurisë, duhet të kërkojnë leje prej jush (për të hyrë te ju në tri kohë: para namazit të sabahut, në kohën e drekës kur i hiqni rrobat tuaja (për të pushuar) dhe pas namazit të jacisë, që të tria këto kohë janë kur ju jeni të veshur. Pos atyre tri kohëve nuk është mëkat as për ju as për ata, të vizitoni njëri-tjerin. Kështu All-llahu ua sqaron argumentet e veta. All-llahu është i dijshëmi i urti.');
INSERT INTO "ahmeti" VALUES(24,59,'24:59','E kur fëmijët tuaj ta arrijnë moshën epjekurisë, le të kërkojnë leje (për hyrje) ashtu si kërkuan ata para rteë. All-llahu ju shpjegon dipozitat e veta, sepse Ai di më së miri dhe është më i urti.');
INSERT INTO "ahmeti" VALUES(24,60,'24:60','E gratë e moshuara, të cilat nuk dëshirojnë martesë më, nuk është mëkat ta heqin petkun mbulues të tyre, po duke mos qenë zbuluese të bukurive, megjithëqë më së miri është për to të jenë të përmbajtura. All-llahu dëgjon, di.');
INSERT INTO "ahmeti" VALUES(24,61,'24:61','Nuk është mëkat për të verbërin, as të çalin, as për të sëmurin, as për ju vetë, ose nga shtëpitë e etërve tuaj, shtëpitë e nënave tuaja, në shtëpitë e vëllezërve e të motrave tuaja, ne shtëpitë e xhaxhallarëve, të hallave, të dajëve tuaj, në shtëpitë e tezeve tuaja ose të atyre që u janë besuar çelësat e tyre, ose (në shtëpitë) e miqve tuaj. Mund të hani bashkërisht ose veçmas, nuk është mëkat. Kur të hyni në ndonjë shtëpi përshëndetni (ata që janë në to) me një përsëndetje të caktuar nga All-llahu (me selamun alejkum) që është e bekuar dhe e këndëshme. Kështu All-llahu ua sqaron argumentet që t’i kuptoni.');
INSERT INTO "ahmeti" VALUES(24,62,'24:62','Besimtarë janë ata që i besuan All-llahut dhe të dërguarit të Tij, dhe mur janë me të (të dërguarin) për ndonjë çështje të përbashkët (të rëndësishme), nuk largohen pa i kërkuar leje atij. S’ka dyshim se ata që kërkojnë leje prej teje, ata i besojnë All-llahut dhe të dërguarit të Tij. E kur të kërkojnë leje për ndonjë punë të tyre, lejo kë duash, e kërkoi falje All-llahut për ata, se vërtet, All-llahu falë shumë e ështëmëshirues.');
INSERT INTO "ahmeti" VALUES(24,63,'24:63','Thirrjen e të dërguarit mos e konsideroni si thirrjen tuaj ndaj njëri-tjetrit. All-llahu i di ata që tërhiqen prej jush tinëzisht, prandaj, le të ruhen ata që kundërshtojnë rrugën e tij (të të dërguarit) se ata do t’i zë ndonjë telashe, ose do t’i godasë dënimi i idhët.');
INSERT INTO "ahmeti" VALUES(24,64,'24:64','Veni re, se vetëm në pushtetin e All-llahut është çka ka në qiej dhe në tokë. Ai e di gjendjen tuaj (besimin apo hipokrizinë), e ditën kur i kthehen Atij, Ai do t’u kallëzojë atyre për atë që vepruan. All-llahu është i dijshëm për çdo send.');
INSERT INTO "ahmeti" VALUES(25,1,'25:1','Ai, që ia shpall Furkanin (Kur’anin, dallues të së vërtetës nga e pavërteta) robit të vet (Muhammedit) që të bëhet pejgamber i botes (këshillues), është i madhëruar.');
INSERT INTO "ahmeti" VALUES(25,2,'25:2','Ai është që vetëm Atij i takon sundimi i qiejve dhe i tokës, Ai nuk ka as fëmijë e as nuk ka shok në sundimin e Tij. Ai krijoi çdo gjë, duke e përsosur në mënyrë të qartë e të matur.');
INSERT INTO "ahmeti" VALUES(25,3,'25:3','Mirëpo, ata (idhujtarët) në vend të Tij adhuruan zota tjerë, të cilët nuk krijuan asgjë, sepse ata edhe vetë janë të krijuar. Ata nuk posedojnë për vetvete as dëm as dobi, ata nuk kanë në dorë as vdekje as jetë, e as ringjallje.');
INSERT INTO "ahmeti" VALUES(25,4,'25:4','Dhe ata që nuk besuan, thinë: “Ky (Kur’ani) nuk është tjetër vetëm se gënjeshtër, që e trilloi ai (Muhammedi), të cilit indihmuan edhe njerëz tjerë. Pra ata (idhujtarët) bënë shtrembërim e shpifje”.');
INSERT INTO "ahmeti" VALUES(25,5,'25:5','Edhe thanë: “(për Kur’anin) Janë legjenda të të parëve, që ai (Muhammedi) kërkoi t’i shkruhen ato, e i lexohen atij mëngjes e mbrëmje”.');
INSERT INTO "ahmeti" VALUES(25,6,'25:6','Thuaj: “Atë e shpall Ai, që e di të fshehtën në qiej e në tokë, Ai është që falë shumë, është mëshirues”.');
INSERT INTO "ahmeti" VALUES(25,7,'25:7','E ata thanë: “ç’është me këtë të dërguar, i cili ha ushqim dhe c nëpër tregje? ërse të mos i jetë dërguar një engjëll së bashku me te, e të jetë qortues?”');
INSERT INTO "ahmeti" VALUES(25,8,'25:8','Ose atij t’i jepet ndonjë thesar, apo të ketë ndonjë kopsht nga i cili do të ushqehej? Horrat madje thonë: “Ju vetëm jeni duke e ndjekur një njeri që e ka zënë magjia!”');
INSERT INTO "ahmeti" VALUES(25,9,'25:9','Shih se çfarë shembujsh të sjellin, andaj ata janë të humbur dhe nuk mund ta gjejmë rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(25,10,'25:10','Është i madhëruar Ai, i cili po të dëshirojë mund të të japë ty edhe më mirë se ç’thonë ata, kopshte në të cilët rrjedhin lumenj, ku ka edhe pallate.');
INSERT INTO "ahmeti" VALUES(25,11,'25:11','Por jo, ata përgënjeshtruan kiametin, e Ne për ata që nuk besojnë ditën e gjykimit kemi përgatitur zjarr të fortë.');
INSERT INTO "ahmeti" VALUES(25,12,'25:12','Që posa të duket ai prej një vendi të largët, ata ia dëgjojnë atij vëlimin edhe kërhamzën e tij (Xhehennemit).');
INSERT INTO "ahmeti" VALUES(25,13,'25:13','E kur të hudhen duarlidhur në një vend të ngushtë në të, atydo të kërkojnë shkatërrimin (vetëzhdkjen (.');
INSERT INTO "ahmeti" VALUES(25,14,'25:14','Tash mos kërkoni vetëm njëherë shkatërrimin, por atë kërkonie shumëherë.');
INSERT INTO "ahmeti" VALUES(25,15,'25:15','Thuaj: “A është më i mirë ai (zjarri i Xhehennemit), apo Xhenneti i përjetshëm, i cili u është premtuar si shpërblim dhe vend jetësimi të devotshëmve”.');
INSERT INTO "ahmeti" VALUES(25,16,'25:16','e aty, atë ditë që e dëshirojnë do ta kenë përgjithmonë. Ky premtim i Zotit tënd ka qenë dashtë kërkuar.');
INSERT INTO "ahmeti" VALUES(25,17,'25:17','Ditën kur Zoti do t’i tubonë ata, e edhe ata që adhuruan pos All-llahut dhe u thonë (zotave të adhuruar): “A i thirrët ju rbtë e Mi që t’u adhurojnë, apo ata vetë e humbën rrugën?”');
INSERT INTO "ahmeti" VALUES(25,18,'25:18','Ata (idhujt) thonë: “Je i panevojshëm për rival, o i madhëruar! Neve nuk na takoi që përveç Teje të kemi ndonjë mbrojtës tjetër, por Ti u ke dhënë të mira atyre dhe të të parëve të tyre, që ishin njerëz të prishur s që harruan të përkujtojnë!”');
INSERT INTO "ahmeti" VALUES(25,19,'25:19','Ja (All-llahu u thotë idhujtarëve), këta ju përgënjeshtruan për atë që thoni (se ishin zota), pra ju as nuk keni mundësi të shpëtoni as t’i ndihmoni (vetes). E cilido ë bën padrejtësi prej jush, Ne i japim atij të përjetojë një dënim të madh.');
INSERT INTO "ahmeti" VALUES(25,20,'25:20','Ne para teje dërguam pejgamberë të tjerë, por edhe ata ishin të dërguar që kanë ngrënë ushqim, që kanë ecur e shetitur nëpër tregje. Ne bëm njërin prej jush sprovë për tjetrin se a do të jeni të durueshëm. E Zoti yt është Ai që sheh çdo gjë.');
INSERT INTO "ahmeti" VALUES(25,21,'25:21','E ata që nuk e shpresojnë takimin Tonë, thanë: “Përse të mos na dërgohen neve engjëj, ose përse të mos shohim Zotin tonë?” Ata tejçmuan lartë veten dhe u sollën me arrogancë.');
INSERT INTO "ahmeti" VALUES(25,22,'25:22','Për kriminelët atë ditë kur i shohin engjëjt nuk ka gëzim, sepse ata (engjëjt) u thonë: “Është e ndaluar çdo e mirë (apo Xhenneti është haram për ju)!”');
INSERT INTO "ahmeti" VALUES(25,23,'25:23','E Ne i kthehemi ndonjë vepre që e bënë ata dhe e bëjmë atë për hi e pluhur.');
INSERT INTO "ahmeti" VALUES(25,24,'25:24','Atë ditë banuesit e Xhennetit do të kenë vend shumë të mirë dhe pushimore shumë të këndshme.');
INSERT INTO "ahmeti" VALUES(25,25,'25:25','Dhe (përkujto) ditën kur do të çahet qielli me anën e një reje e lëshohen engjëjt në një mënyrë madhështore.');
INSERT INTO "ahmeti" VALUES(25,26,'25:26','Atë ditë sundimi i vërtetë është i të Gjithëmëshirëshmit, e ajo ditë për jobesimtarët do të jetë ditë e vështirë.');
INSERT INTO "ahmeti" VALUES(25,27,'25:27','Atë ditë zullumqari do t’i kafshojë duart eveta dhe do të thotë: “I mjeri unë, ta kisha marrë rrugën e Pejgamberit!”');
INSERT INTO "ahmeti" VALUES(25,28,'25:28','O shkatërrimi im, ah të mos e kisha bërë filanin mik!');
INSERT INTO "ahmeti" VALUES(25,29,'25:29','Në të vërteë, pasi e gjeta rrugën e drejë, ai më largoi mua prej asaj, e djalli është ai që njeriun e humb dhe e lë të vetmuar.');
INSERT INTO "ahmeti" VALUES(25,30,'25:30','E i dërguari tha: “O Zot imi, vërtet, populli im e konsideroi këtë Kur’an si (diç) të hedhur!”');
INSERT INTO "ahmeti" VALUES(25,31,'25:31','Shi kështu, Ne secilit pejgamber ia bëmë ndonjë armik prej jobesimtarëve, por mjfton që Zoti të jetë udhërrëfyes ndihmës yti.');
INSERT INTO "ahmeti" VALUES(25,32,'25:32','E ata që nuk besuan thanë: “Përse të mos i ketë zbritur atij (Muhammedit) Kur’ani përnjëherë?” Ashtu (e zbritëm pjesë-pjesë) që me të të forcojmë zemrën tënde dhe Ne e sollëm atë ajet pas ajetit (dalëngadalë).');
INSERT INTO "ahmeti" VALUES(25,33,'25:33','Dhe ata nuk të sjellin ndonjë shembull ty, e që Ne të mos ta tregojmë të vërtetën, duke të ofruar shpjegim të qartë e të plotë.');
INSERT INTO "ahmeti" VALUES(25,34,'25:34','Ata do të tubohen, do të jenë të përmbysur me fytyrat e tyre në Xhehennem, të tillëve u përket vendi më i keq, ndaj ata janë më të humburit në rrugën e tyre.');
INSERT INTO "ahmeti" VALUES(25,35,'25:35','Ne edhe Musait i patëm dhënë librin, e bashkuam me vëllain e vet, Harunin, që takeë si ndihmës.');
INSERT INTO "ahmeti" VALUES(25,36,'25:36','U thamë: “Shkoni ju të dy te ai popull ë i përgënjeshtroi argumentet Tona!” E Ne pastaj i shkatërrruam ata shumë keq.');
INSERT INTO "ahmeti" VALUES(25,37,'25:37','Ne e përmbytëm edhe popullin e Nuhut meqë ata të dërguarit i quajtën rrenacakë, e pët njerëzit, të cilët mendojnë, le të marrin mësim nga ata. E për mizorët Ne kemi përgaditur dënim të rëndë.');
INSERT INTO "ahmeti" VALUES(25,38,'25:38','Edhe Adin, Themudin, banorët e Res-it dhe shumë gjenerata ndërmjet tyre (i shkatërruam).');
INSERT INTO "ahmeti" VALUES(25,39,'25:39','E secilit prej tyre u patëm sjellë argumente, por të gjithë i rrënuam.');
INSERT INTO "ahmeti" VALUES(25,40,'25:40','Ata (kurejshitët) kaluan buzë fshatit, i cili ishte gurëzuar me atë shiun (gurëzim prej së larti) e shëmtuar, po a nuk janë ata, të cilët e panë atë? Jo, nuk e vëhtruan, sepse ata ishin të tillë, të cilët nuk shpresonin në ringjallje.');
INSERT INTO "ahmeti" VALUES(25,41,'25:41','E kur të shohin ty, nuk të çmojnë ndryshe, por vetëm në talje e thonë: “A ky është ai që All-llahu e dërgoi pejgamber?”');
INSERT INTO "ahmeti" VALUES(25,42,'25:42','“Sikur ne të mos ishim të qëndrueshëm ndaj tyre, ai gati na pat zbrapsur prej zotave tanë!” e më vonë, kur ra shohin qartazi dënimin, do ta kuptojnë se kush ishtë më larg nga e vërteta.');
INSERT INTO "ahmeti" VALUES(25,43,'25:43','A e ke parë ti atë që, që për zot e qmon epshin e vet, a mos do t’i bëhesh ti atij mbrojtës?');
INSERT INTO "ahmeti" VALUES(25,44,'25:44','A mendon ti se shumica e tyre dëgjojnë pse kuptojnë? Ata nuk trajtohen ndryshe, por vetëm si kafshë, bile janë edhe më të humbur nga e vërteta.');
INSERT INTO "ahmeti" VALUES(25,45,'25:45','A e sheh se si Zoti yt e zgjati (andej e këndej) hijen, e sikur të kishte dashur do ta linte në një vend, pastaj diellin e bëmë rrëfyes të saj.');
INSERT INTO "ahmeti" VALUES(25,46,'25:46','Pastaj Ne e tërheqim pak nga pak.');
INSERT INTO "ahmeti" VALUES(25,47,'25:47','E ai është që natën ua bëri petk, gjumin e pushim e ditën e bëri për gjallërim.');
INSERT INTO "ahmeti" VALUES(25,48,'25:48','Edhe Ai është, që i lëshon erëra myzhde në prag të mëshirës së Tij (shiut) dhe Ne lëshuam prej qiellit ujë të pastër.');
INSERT INTO "ahmeti" VALUES(25,49,'25:49','Që me të t’i japim jetë një toke të vdekur dhe t’u japim të pijnë atyre që i krijuam, kafshë e shumë njerëz.');
INSERT INTO "ahmeti" VALUES(25,50,'25:50','Ne shumë herë ua përkujtuam atyre këto fakte që të mendojnë, por shumica e njerëzve nuk deshën tjetër pos refuzimit.');
INSERT INTO "ahmeti" VALUES(25,51,'25:51','Sikur të kishim dashur Ne, do të dërgonim pejgamber në çdo vendbanim.');
INSERT INTO "ahmeti" VALUES(25,52,'25:52','Po ti mos dëgjo jobesimtarët e luftoi ata me të (me Kur’an)');
INSERT INTO "ahmeti" VALUES(25,53,'25:53','Ai la të lirë të bashkohen dy dete, ky (njëri) i pijshëm e i shijshëm, e ky (tjetri) i njelmët e i idhët, mes tyre bëri si të jetë ndonjë pendë, pritë që të mos përzihen.');
INSERT INTO "ahmeti" VALUES(25,54,'25:54','Dhe Ai nga uji (fara) krijoi njeriun, dhe e bëri atë të ketë fis dhe të ketë miqësi. Zoti yt ka mundësi të jashtëzakonshme.');
INSERT INTO "ahmeti" VALUES(25,55,'25:55','E ata në vend të All-llahut adhurojnë atë që nuk u sjell as dobi, as dëm atyre, po jobesimtari ndihmon atë që është kundër Zotit të vet.');
INSERT INTO "ahmeti" VALUES(25,56,'25:56','E Ne nuk të dërguam ty për tjetër qëllim, por vetëm si përgëzues e si qortues.');
INSERT INTO "ahmeti" VALUES(25,57,'25:57','Thuaj: “Unë për këtë nuk kërkoj shpërblim prej jush, por vetëm kush të dojë të marrë rrugën që e afron te Zoti i vet”.');
INSERT INTO "ahmeti" VALUES(25,58,'25:58','Ti mbështetju Atij të përjetshmit që nuk vdes kurrë, madhëroje me lavdërimin që i takon Atij, Ai mjafton për njohje në hollësi të mëkateve të robve të vet.');
INSERT INTO "ahmeti" VALUES(25,59,'25:59','Ai krijoi qiejt e tokën dhe gjithçka që gjendet në mës tyre brenda gjashtë ditësh, e pastaj mbizotëroi Arshin. Ai është Mëshiruesi. Për këtë pyete ndonjë shumë të dijshëm.');
INSERT INTO "ahmeti" VALUES(25,60,'25:60','E kur u është thënë atyre: Bëni sezhde ndaj të madhërishmit, ata thanë: “E kush është Rrahmani - Mëshiruesi? A t’i bëjmë sexhde pse ti na urdhëron?” E kjo shtoi edhe më tepër largimin e tyre (nga feja).');
INSERT INTO "ahmeti" VALUES(25,61,'25:61','I madhërishëm është Ai që krijoi yje në qiell dhe vendosi në të dritë (diell) e hënë që ndriçon.');
INSERT INTO "ahmeti" VALUES(25,62,'25:62','Ai është që bëri natë e ditën zëvendsuese të njëra-tjetrës dhe për atë që dëshiron të mendojë apo të falënderojë, argument.');
INSERT INTO "ahmeti" VALUES(25,63,'25:63','E robërit e Zotit janë ata që ecin nëpër tokë të qetë, e kur atyre me fjalë u drejtohen injorantët, ata thonë: “Paqe!”');
INSERT INTO "ahmeti" VALUES(25,64,'25:64','Dhe që për hirë të Zotit të tyre natën e alojnë duke bërë sexhde dhe duke qëndruar në këmbë (falen).');
INSERT INTO "ahmeti" VALUES(25,65,'25:65','Edhe ata që thonë: “Zoti ynë, largoje prej nesh vuajtjen e Xhehennemit, e s’ka dyshim se vuajtja në të është gjëja më e rëndë”.');
INSERT INTO "ahmeti" VALUES(25,66,'25:66','Ai, vërtet është vendqëndrim i keq.');
INSERT INTO "ahmeti" VALUES(25,67,'25:67','Edhe ata që kur shpenzojnë nuk e teprojnë e as nuk janë dorështrënguar, po mbajnë mesataren e janë të matur.');
INSERT INTO "ahmeti" VALUES(25,68,'25:68','Edhe ata që pos All-llahut, nuk lusin zot tjetër dhe nuk mbysin njeriun që ka ndaluar All-llahu, por vetëm kur e meriton në bazë të drejtësisë, dhe që nuk bëjnë kurvëri, ndërsa kush i punon këto, ai gjen ndëshkimin.');
INSERT INTO "ahmeti" VALUES(25,69,'25:69','Atij i dyfishohet dënimi ditëne kijametit dhe aty mbetet i përbuzur përgjithmonë.');
INSERT INTO "ahmeti" VALUES(25,70,'25:70','Përveç ati që është penduar dhe ka bërë vepër të mirë, të tillëve All-llahu të këqiat ua shëndrron në të mira. All-llahu është mëshirues, ndaj Ai falë shumë.');
INSERT INTO "ahmeti" VALUES(25,71,'25:71','Ai që është penduar dhe ka bërë mirë, në të vërtet, ai është kthyer te All-llahu dhe është i pranishëm.');
INSERT INTO "ahmeti" VALUES(25,72,'25:72','(Robërit e Zotit janë) Edhe ata që nuk dëshmojnë rrejshëm dhe kur (rastësisht) kalojnë pranë së keqes, kalojnë duke e ruajtur karakterin e vet.');
INSERT INTO "ahmeti" VALUES(25,73,'25:73','Edhe ata që kur këshillohen me ajetet e Kur’anit të Zotit të tyre, nuk u kthejnë shpinën si të shurdhët e të verbër.');
INSERT INTO "ahmeti" VALUES(25,74,'25:74','Edhe ata që thonë: “Zoti ynë, na bën që të jemi të gëzuar me (punën) e grave tona dhe pasardhsve tanë, neve na bën shembull për të devotshmit”.');
INSERT INTO "ahmeti" VALUES(25,75,'25:75','Për shkak se duruan, të tillët shpërblehen me një shkallë të lartë (Xhennet) dhe aty priten me përshëndetje të selamit.');
INSERT INTO "ahmeti" VALUES(25,76,'25:76','Aty janë përgjithmonë, e sa vendqëndrim dhe vendbanim i mirë është ai.');
INSERT INTO "ahmeti" VALUES(25,77,'25:77','Thuaj: “zoti im as nuk do të kujdesej për ju, sikur të mos ishte lutja juaj (kur gjendeni në vështirësi, ose sikur të mos ishit të thirur të besoni), e ju përgënjeshtruat, andaj dënnimi do t’ju jetë i pandërprerë”.');
INSERT INTO "ahmeti" VALUES(26,1,'26:1','Ta, Sinë, Mimë.');
INSERT INTO "ahmeti" VALUES(26,2,'26:2','Këto janë ajete të librit të (të Kur’anit) qartë!');
INSERT INTO "ahmeti" VALUES(26,3,'26:3','A mos do të mbysësh ti veten (Muhammed) pse ata nuk bëhen besimtarë?!');
INSERT INTO "ahmeti" VALUES(26,4,'26:4','Silur të duam Ne, do të zbritnim një argument (mrekulli) nga qielli mbi ta, e që qafat e tyre do t’i rrinin përulur atij.');
INSERT INTO "ahmeti" VALUES(26,5,'26:5','Atyre nuk u vjen asnjë këshillë e re nga Mëshiruesi, e që ata mos t’ia kthejnë shpinën.');
INSERT INTO "ahmeti" VALUES(26,6,'26:6','Vërtet, ata përgënjeshtruan, por atyre do t’u vijë kobi i asaj me të cilën talleshin.');
INSERT INTO "ahmeti" VALUES(26,7,'26:7','A nuk e shikuan ata tokën se sa shumë loje bimësh të dobishme bëmë të mbijnë në të?');
INSERT INTO "ahmeti" VALUES(26,8,'26:8','Vërtet, në këto ka fakte (të forta), por megjithatë, shumica e tyre nuk kanë qenë besimtarë.');
INSERT INTO "ahmeti" VALUES(26,9,'26:9','e s’ka dyshim se Zoti yt është Ai i Gjithfuqishmi (t’i ndëshkojë) po edhe i Mëshirshmi.');
INSERT INTO "ahmeti" VALUES(26,10,'26:10','(Përkujto) Kur Zoti yt e thirri Musain: “Të shkosh te ai popull zullumqarë!”');
INSERT INTO "ahmeti" VALUES(26,11,'26:11','Populli i faraonit, a nuk janë duke u frikësuar (dënimit)?');
INSERT INTO "ahmeti" VALUES(26,12,'26:12','Ai (Musai) tha: “Zoti im, kam frikë se do të trajtojnë si gënjeshtarë.');
INSERT INTO "ahmeti" VALUES(26,13,'26:13','Do të më shtrëngohet krahrori (shpirti) im dhe nuk do të më leshohet gjuha ime (të flas rrjedhshëm), anaj dërgoje (bëre pejgamber) Harunin.');
INSERT INTO "ahmeti" VALUES(26,14,'26:14','Madje unë kam një faj ndaj tyre, e kam frikë se do të më mbysin (për hakmarrje)!”');
INSERT INTO "ahmeti" VALUES(26,15,'26:15','Ai (All-llahu) tha: “Kurrsesi (nuk do të mbysin), po shkoni ju të dy me argumentet Tona. Ne jemi me ju vështrues tuaj (ju dëgjojmë dhe ju ndihmojmë).');
INSERT INTO "ahmeti" VALUES(26,16,'26:16','Shkoni dhe faraonit thoni: “Ne jemi të dërguar nga Zoti i të gjitha botëve.');
INSERT INTO "ahmeti" VALUES(26,17,'26:17','T’i lejosh beni israilët të vijnë me ne!”');
INSERT INTO "ahmeti" VALUES(26,18,'26:18','Ai (faraoni) tha: “A nuk të rritëm ty si fëmijë në mesin e tonë, ku i kalove disa vjet të jetës sate.');
INSERT INTO "ahmeti" VALUES(26,19,'26:19','Dhe ti e bëre atë punën tënde që e bëre, prra ti je mohues (i të mirave që t’i bëmë).');
INSERT INTO "ahmeti" VALUES(26,20,'26:20','(Musai) Tha: “E bëra atë, atëherë kur isha i pavetëdijshëm.');
INSERT INTO "ahmeti" VALUES(26,21,'26:21','Andaj, edhe ika prej jush kur u frikësova nga ju, e Zoti im më dhuroi dhe më bëri nga të dërguarit.');
INSERT INTO "ahmeti" VALUES(26,22,'26:22','Më përmend atë të mirë që më bëre mua, e ti robërove beni israilët!”');
INSERT INTO "ahmeti" VALUES(26,23,'26:23','(Faraoni) Tha: “E cili është ai zot i botëve (që ju dërgoj)?”');
INSERT INTO "ahmeti" VALUES(26,24,'26:24','Ai i tha: “Zoti i qiejve dhe i tokës dhe ç’ka ndërmjet tyre, nëse bindeni!”');
INSERT INTO "ahmeti" VALUES(26,25,'26:25','Ai (faraoni) atyre që ishin përreth, u tha: “A nuk e dëgjoni?!”');
INSERT INTO "ahmeti" VALUES(26,26,'26:26','Ai (Musai) tha: “Zoti juaj dhe i prindërve tuaj të parë!”');
INSERT INTO "ahmeti" VALUES(26,27,'26:27','(Faraoni) Tha: “Vërtet, i dërguari juaj që u është dërguar juve, me siguri është i çmendur!”');
INSERT INTO "ahmeti" VALUES(26,28,'26:28','(Musai) Tha: “Zot i lindjes dhe i perëndimit dhe ç’ka ndërmjet tyr. po qe se kuptoni!”');
INSERT INTO "ahmeti" VALUES(26,29,'26:29','(faraoni) Tha: “Nëse ti beson zot tjetër pos meje, do të gjendesh në mesin e të burgosurve!”');
INSERT INTO "ahmeti" VALUES(26,30,'26:30','(Musai) Tha: “A edhe nëse të sjell ndonjë argument të qartë!”');
INSERT INTO "ahmeti" VALUES(26,31,'26:31','Ai tha: “Sille pra, nëse thua të vërtetën?”');
INSERT INTO "ahmeti" VALUES(26,32,'26:32','Atëherë, (Musai) e hodhi shkopin e vet, i cili u shndërrua në gjarpër të vërtetë.');
INSERT INTO "ahmeti" VALUES(26,33,'26:33','Nxori dorën prej xhepit, kur qe, ajo në sy të shikuesve flakëronte nga bardhësia.');
INSERT INTO "ahmeti" VALUES(26,34,'26:34','(faraoni) Parisë që e rrethonte, u tha: “Vërtet, ky qenka magjistar shumë i aftë!”');
INSERT INTO "ahmeti" VALUES(26,35,'26:35','Që me magjinë e vet dëshiron t’ju dëbojë nga toka juaj, e ç’më propozoni mua?');
INSERT INTO "ahmeti" VALUES(26,36,'26:36','Paria) Shtyje atë dhe vëlain e tij (çështjen e tyre) për më vonë, e ti dërgo nëpër qytete tubues.');
INSERT INTO "ahmeti" VALUES(26,37,'26:37','Që t’i sjellin të gjithë magjistarët e dijshëm këtu.');
INSERT INTO "ahmeti" VALUES(26,38,'26:38','Dhe, magjistarët u tubuan në kohën e ditës së caktuar.');
INSERT INTO "ahmeti" VALUES(26,39,'26:39','Edhe popullit iu tha: “Ejani e tubohuni,');
INSERT INTO "ahmeti" VALUES(26,40,'26:40','Ashtu që t’i përkrahin magjistarët, nëse ata triumfojnë”.');
INSERT INTO "ahmeti" VALUES(26,41,'26:41','E kur erdhën magjistarët i thanë faaonit: “A do të ketë shpërblim për ne, nëse dalim ngadhnjimtarë?”');
INSERT INTO "ahmeti" VALUES(26,42,'26:42','Ai tha: “Po, bile ju do të jeni atëherë prej më të afërmve!”');
INSERT INTO "ahmeti" VALUES(26,43,'26:43','Musai atyre u tha: “Hidhnie atë që keni për ta hedhur!”');
INSERT INTO "ahmeti" VALUES(26,44,'26:44','E ata i hodhën litarët dhe shkopinjtë e tyre duke thënë: “Për hir të madhërisë së faraonit, ne me sifuri do të jemi ngadhënjyes!”');
INSERT INTO "ahmeti" VALUES(26,45,'26:45','Atëherë edhe Musai e hodhi shkopin e vet, i cili menjëherë gëltiset gjithë çka kishin improvizuar ata.');
INSERT INTO "ahmeti" VALUES(26,46,'26:46','Atëherë magjistarët ranë në sexhde (u përulën).');
INSERT INTO "ahmeti" VALUES(26,47,'26:47','E thanë: “Ne i besuam Zotit të botëve,');
INSERT INTO "ahmeti" VALUES(26,48,'26:48','Zotit të Musait e të Harunit!”');
INSERT INTO "ahmeti" VALUES(26,49,'26:49','Ai (faraoni) tha: “I besuat atij, para se t’ju lejoja unë?!” S’ka dyshim se ai (Musai) është prijësi juaj që ua mësoi juve magjinë, e keni për të marrë vesh më vonë. Unë do t’ua shkurtoi (do t’ua prejë) duart dhe këmbët tuaja të anës së kundër (klithi) dhe që të gjithëve do t’u gozhdoj”.');
INSERT INTO "ahmeti" VALUES(26,50,'26:50','Ata (magjistarët) thanë: “S’ka rëndësi, ne kthehemi Zotit tonë,');
INSERT INTO "ahmeti" VALUES(26,51,'26:51','Meqë ne jemi besimtarët e parë, shpresojmë se Zoti ynë do të na i falë mëkatet tona!”');
INSERT INTO "ahmeti" VALUES(26,52,'26:52','Ne e urdhëruam (përmes shpalljes) Musain: “Të udhëtosh natan me robërit e Mij dhe se ju do të ndiqeni (prej faraonit)”.');
INSERT INTO "ahmeti" VALUES(26,53,'26:53','E faraoni dërgoi nëpër qytete për të tubuar njerëz,');
INSERT INTO "ahmeti" VALUES(26,54,'26:54','(Duke u thënë se) Ata janë një grup i vogël (të cilët ikën me Musain).');
INSERT INTO "ahmeti" VALUES(26,55,'26:55','Dhe se me të vërtetë, ata na kanë zemëruar (dhe ikën pa lejen tonë).');
INSERT INTO "ahmeti" VALUES(26,56,'26:56','E me të vërtetë, ne duhet të jemi syçelë.');
INSERT INTO "ahmeti" VALUES(26,57,'26:57','Dhe Ne (u mbushëm mendjen e) i nxorëm prej kopshteve e burimeve,');
INSERT INTO "ahmeti" VALUES(26,58,'26:58','prej pasurisë në depo dhe prej vendbanimeve të bukura.');
INSERT INTO "ahmeti" VALUES(26,59,'26:59','Ja ashtu, Ne ua bëmë atë trashëgim beni israilëve.');
INSERT INTO "ahmeti" VALUES(26,60,'26:60','E ata (të faraonit) i arritën (beni israilët) në kohën e lindjes së diellit.');
INSERT INTO "ahmeti" VALUES(26,61,'26:61','Dhe kur u panë dy grupet, shokët e Musait Thanë: “Me siguri na zunë!”');
INSERT INTO "ahmeti" VALUES(26,62,'26:62','Ai (Musai) tha: Kurrsesi, me mua është Zoti im, Ai do të më udhëzojë”.');
INSERT INTO "ahmeti" VALUES(26,63,'26:63','Ndërsa Ne Musait i thamë: “Meshkopin tënd bjeri detit”. Deti u nda dhe çdo pjesë u bë si kodër e madhe.');
INSERT INTO "ahmeti" VALUES(26,64,'26:64','E Ne i afruam atje të tjerët (ata të faraonit).');
INSERT INTO "ahmeti" VALUES(26,65,'26:65','Dhe Ne e shpëtuam Musain dhe të gjithë ata që ishin me të.');
INSERT INTO "ahmeti" VALUES(26,66,'26:66','Kurse të tjerët i përmbytëm në ujë.');
INSERT INTO "ahmeti" VALUES(26,67,'26:67','Vërtet, në këtë (ngjarje) ka fakte, por megjithatë shumica nuk besuan.');
INSERT INTO "ahmeti" VALUES(26,68,'26:68','Nuk ka dyshim se Zoti yt është i gjithfuqishëm dhe i mëshirshëm.');
INSERT INTO "ahmeti" VALUES(26,69,'26:69','Lexoju (nga ajo që të shpallëm ty Muhammed) atyre ngjarjen e Ibrahimit.');
INSERT INTO "ahmeti" VALUES(26,70,'26:70','Kur i tha babait të vet dhe popullit të vet: “çka jeni duke adhuruar?”');
INSERT INTO "ahmeti" VALUES(26,71,'26:71','Ata i thanë: “Adhurojmë idhuj, vazhdimisht u jemi besnikë atyre!”');
INSERT INTO "ahmeti" VALUES(26,72,'26:72','Ai i tha: “A ju dëgjojnë ata juve kur të luteni?”');
INSERT INTO "ahmeti" VALUES(26,73,'26:73','Ose, “a u sjellin juve dobi apo dëm?”');
INSERT INTO "ahmeti" VALUES(26,74,'26:74','Ata thanë: “Jo, por kështu i gjetëm se bënin edhe prindërit tanë!”');
INSERT INTO "ahmeti" VALUES(26,75,'26:75','Ai tha: “A po shihni se ç’po adhuroni?”');
INSERT INTO "ahmeti" VALUES(26,76,'26:76','Ju dhe prindërit tuaj të mëparshëm,');
INSERT INTO "ahmeti" VALUES(26,77,'26:77','Në të vërtetë, ata (që adhuroni ju) janë armiq të mij. përveç Zotit të botëve (nëse përveç idhujve adhuroni edhe Atë).');
INSERT INTO "ahmeti" VALUES(26,78,'26:78','Zoti që më krijoi, Ai më udhëzon mua,');
INSERT INTO "ahmeti" VALUES(26,79,'26:79','Dhe Ai që më ushqen dhe më jep të pijë,');
INSERT INTO "ahmeti" VALUES(26,80,'26:80','Dhe kur të sëmurem A më shëron,');
INSERT INTO "ahmeti" VALUES(26,81,'26:81','Ai më bën të vdesë e mandej më ngjall,');
INSERT INTO "ahmeti" VALUES(26,82,'26:82','Ai te i cili kam shpresë se do të m’i falë mëkatet e mia në ditën e gjykimit.');
INSERT INTO "ahmeti" VALUES(26,83,'26:83','Zoti im, më dhuro mua urtësi dhe më bashko me të mirët!');
INSERT INTO "ahmeti" VALUES(26,84,'26:84','Më bën përkujtim të mirë ndër ata që vijnë pas,');
INSERT INTO "ahmeti" VALUES(26,85,'26:85','Më bn prej trashëgesve të Xhennetit të begatshëm,');
INSERT INTO "ahmeti" VALUES(26,86,'26:86','Dhe fale babaim tim, se ai vërtet ishtë nga ata të humburit,');
INSERT INTO "ahmeti" VALUES(26,87,'26:87','E mos më turpëro në ditën kur do të ringjallen.');
INSERT INTO "ahmeti" VALUES(26,88,'26:88','Ditën kur nuk bën dobi as malli, as fëmijët.');
INSERT INTO "ahmeti" VALUES(26,89,'26:89','(bën dobi) Vetëm kush i paraqitet Zotit me zemër të shëndoshë.');
INSERT INTO "ahmeti" VALUES(26,90,'26:90','E Xhenneti u afrohet atyre që ishin të devotshëm.');
INSERT INTO "ahmeti" VALUES(26,91,'26:91','Ndërsa Xhehennem u dëftohet atyre që ishin të humbur.');
INSERT INTO "ahmeti" VALUES(26,92,'26:92','Dhe atyre u thuhet: “Ku janë ata që i adhuronit ju”,');
INSERT INTO "ahmeti" VALUES(26,93,'26:93','Pos All-llahut? Ata, a mund t’ju ndihmojnë juve o vetes?');
INSERT INTO "ahmeti" VALUES(26,94,'26:94','E ata (idhujt) dhe adhuruesit e tyre, njëri mbi tjetrin hudhën në të,');
INSERT INTO "ahmeti" VALUES(26,95,'26:95','Dhe të gjithë ushtria (ithtarët) r djallit.');
INSERT INTO "ahmeti" VALUES(26,96,'26:96','E duke u grindur mes vete në të, ata thonë:');
INSERT INTO "ahmeti" VALUES(26,97,'26:97','“Pasha All-llahun, njëmend ne kemi qenë krejtësisht të humbur”,');
INSERT INTO "ahmeti" VALUES(26,98,'26:98','Kur juve (idhuj) u kemi barazuar me Zotin e botëve.');
INSERT INTO "ahmeti" VALUES(26,99,'26:99','Neve nuk na humbi kush tjetër vetëm se kriminelët (prijësit).');
INSERT INTO "ahmeti" VALUES(26,100,'26:100','E tash për ne nuk ndonjë ndihmës (ndërmjetësues).');
INSERT INTO "ahmeti" VALUES(26,101,'26:101','Nuk ka as ndonjë mik të sinqertë.');
INSERT INTO "ahmeti" VALUES(26,102,'26:102','E, sikur të mnud të kthehemi (dynja) edhe njëherë,, do të bëheshim besimtarë.');
INSERT INTO "ahmeti" VALUES(26,103,'26:103','Në këtë (që u përmend) ka argumente të bindshme, po shumica e tyre nuk besuan.');
INSERT INTO "ahmeti" VALUES(26,104,'26:104','Është e vërtetë se Zoti yt është i gjithëfuqishëm dhe i mëshirshëm.');
INSERT INTO "ahmeti" VALUES(26,105,'26:105','Edhe populli i Nuhut gënjeu të dërguarit.');
INSERT INTO "ahmeti" VALUES(26,106,'26:106','Kur vëllai i tyre (nga gjaku), Nuhu u tha: “A nuk jeni kah frikësoheni?');
INSERT INTO "ahmeti" VALUES(26,107,'26:107','Unë jam për ju pejgamber besnik.');
INSERT INTO "ahmeti" VALUES(26,108,'26:108','Keni frikë (dënimit të) All-llahut e më dëgjoni mua.');
INSERT INTO "ahmeti" VALUES(26,109,'26:109','Unë për këtë nuk kërkoj prej jush ndonjë shpërblim, shpërblimi për mua është vetëm prej Zotit të botëve.');
INSERT INTO "ahmeti" VALUES(26,110,'26:110','Pra (edhe njëherë u them) frikësohuni prej All-llahut e më dëgjoni mua!');
INSERT INTO "ahmeti" VALUES(26,111,'26:111','Ata thanë: “E si të besojmë ty, kur ty të besojnë të ultit?”');
INSERT INTO "ahmeti" VALUES(26,112,'26:112','Ai tha: “Unë nuk dijë se ç’punuan ata (që ju u thoni të ulët)”.');
INSERT INTO "ahmeti" VALUES(26,113,'26:113','Përgjegjësia e tyre, nëse kuptoni, është vetëm para Zotit tim.');
INSERT INTO "ahmeti" VALUES(26,114,'26:114','E unë nuk do t’i përzë besimtarët.');
INSERT INTO "ahmeti" VALUES(26,115,'26:115','Unë nuk jamë tjetër, por vetëm sa t’u tërheq vërejtjen haptas.');
INSERT INTO "ahmeti" VALUES(26,116,'26:116','Ata i thanë: “O Nuh! Nëse nuk heq dorë (nga ajo që mëson), do të gurëzohesh (do të mbytesh me gurë)”.');
INSERT INTO "ahmeti" VALUES(26,117,'26:117','Ai tha: “O Zoti im, populli im më pandeh gënjeshtar.');
INSERT INTO "ahmeti" VALUES(26,118,'26:118','Pra Ti me drejtësinë tënde gjyko ndërmjet meje dhe atyre dhe më shpëto mua dhe besimtarët që janë me mua!”');
INSERT INTO "ahmeti" VALUES(26,119,'26:119','Andaj Ne e shpëtuam atë dhe të gjithë ata që ishin me të në anijen e mbushur plot.');
INSERT INTO "ahmeti" VALUES(26,120,'26:120','Mandej i mbytëm në ujë ata që betën pas tyre.');
INSERT INTO "ahmeti" VALUES(26,121,'26:121','Edhe në këtë (ngjarje) ka argumente, po shumica e tyre nuk ishin besimtarë.');
INSERT INTO "ahmeti" VALUES(26,122,'26:122','S’ka dyshim se Zoti yt është i plotfuqishëm i mëshirshëm.');
INSERT INTO "ahmeti" VALUES(26,123,'26:123','Edhe populli Ad nuk i besoi të dërguarit.');
INSERT INTO "ahmeti" VALUES(26,124,'26:124','Kur vëllai i tyre, Hudi u tha: “A nuk frikësoheni?”');
INSERT INTO "ahmeti" VALUES(26,125,'26:125','Unë për ju jam i dërguar besnik.');
INSERT INTO "ahmeti" VALUES(26,126,'26:126','Andaj, keni frikë (dënimit të) All-llahut dhe më dëgjoni mua.');
INSERT INTO "ahmeti" VALUES(26,127,'26:127','Unë nuk kërkoj për këtë ndonjë shpërblim prej jush, e shpërblimi është vetëm prej Zotit të botëve.');
INSERT INTO "ahmeti" VALUES(26,128,'26:128','A në çdo bregore ngritni ndonjë shtëpi (ndërtesë) sa për lojë?');
INSERT INTO "ahmeti" VALUES(26,129,'26:129','Dhe a i ndërtoni pallatet e fortifikura që të jeni përgjithmonë?');
INSERT INTO "ahmeti" VALUES(26,130,'26:130','E kur rrëmbeni, rrëmbeni mizorisht.');
INSERT INTO "ahmeti" VALUES(26,131,'26:131','Pra, kinie frikë All-llahun dhe në dëgjoni mua!');
INSERT INTO "ahmeti" VALUES(26,132,'26:132','Kinie frikë Atë që u pajisi me çka e dinit vet.');
INSERT INTO "ahmeti" VALUES(26,133,'26:133','U pajisi me mjete gjallërimi e me djem.');
INSERT INTO "ahmeti" VALUES(26,134,'26:134','Me kopshte e me burime uji.');
INSERT INTO "ahmeti" VALUES(26,135,'26:135','Vërtet, unë i frikësohem dënimit tuaj në një ditë të madhe.');
INSERT INTO "ahmeti" VALUES(26,136,'26:136','Ata thanë: “Për ne është krejt një, si na këshillove, si nuk na këshillove!”');
INSERT INTO "ahmeti" VALUES(26,137,'26:137','Kjo (që na këshillon ti) nuk është tjetër vetëm se traditë (mitesh) të popujve të lashtë.');
INSERT INTO "ahmeti" VALUES(26,138,'26:138','Se ne nuk kemi për t’u dënaur.');
INSERT INTO "ahmeti" VALUES(26,139,'26:139','Dhe ashtu ata e trajtuan atë (Hudin) gënjeshtarët, e Ne atëhere i shkatërruam. Edhe ky është një fakt, po shumiza e tyre nuk besuan.');
INSERT INTO "ahmeti" VALUES(26,140,'26:140','Vëretet, Zoti yt është i plotfuqishëm i mëshirshëm.');
INSERT INTO "ahmeti" VALUES(26,141,'26:141','Edhe kabilja Themud nuk i besoi të dërguarit.');
INSERT INTO "ahmeti" VALUES(26,142,'26:142','Kur vëllai i tyre, Salihu u tha: “A nuk frikësoheni?”');
INSERT INTO "ahmeti" VALUES(26,143,'26:143','Unë për ju jam i dërguar besnik.');
INSERT INTO "ahmeti" VALUES(26,144,'26:144','Andaj, kinie frikë (dënimin e) e All-llahut dhe mua më dëgjoni!”');
INSERT INTO "ahmeti" VALUES(26,145,'26:145','Unë nuk kërkoj për këtë ndonjë shpërblim prej jush, shpërblimi im është vetëm prej Zotit të botëve.');
INSERT INTO "ahmeti" VALUES(26,146,'26:146','A jeni të sigurt se do të mbeteni në këtë që jeni (përgjithmonë)?');
INSERT INTO "ahmeti" VALUES(26,147,'26:147','Në kopshte e në burime.');
INSERT INTO "ahmeti" VALUES(26,148,'26:148','Edhe në të mbjella, e me hurma me frutat e tyre të freskët.');
INSERT INTO "ahmeti" VALUES(26,149,'26:149','Dhe ju me mjeshtëri po ndërtoni shtëpi nëpër kodra shkëmbore.');
INSERT INTO "ahmeti" VALUES(26,150,'26:150','Pra, kini frikë All-llahun e më dëgjoni mua.');
INSERT INTO "ahmeti" VALUES(26,151,'26:151','E mos shkoni pas atyre që janë të pa frenuar (që e teprojnë me vepra të këqia).');
INSERT INTO "ahmeti" VALUES(26,152,'26:152','Të cilët në tokë bëjnë shkatërrime, nuk bëjnë mirë.');
INSERT INTO "ahmeti" VALUES(26,153,'26:153','Ata i thanë: “Ti je vetëm i magjepsur”.');
INSERT INTO "ahmeti" VALUES(26,154,'26:154','Ti nuk je tjetër vetëm se njeri sikurse edhe ne, e nëse e thua të vërtetën, sille pra ndonjë argument!”');
INSERT INTO "ahmeti" VALUES(26,155,'26:155','Ai tha: “Ja, kjo është devja, kjo do të pijë (ujë) në një ditë të caktuar, kurse ju në një ditë tjetër (jo për një ditë).');
INSERT INTO "ahmeti" VALUES(26,156,'26:156','Dhe kësaj të mos i bëni ndonjë të keqe, se do t’ju godasë dënimi i ditës së madhe!”');
INSERT INTO "ahmeti" VALUES(26,157,'26:157','Po ata e therrën atë, madje u penduan (nga frika).');
INSERT INTO "ahmeti" VALUES(26,158,'26:158','Por ata i zuri ai dënimi i premtuar. Edhe në këtë ka argument, por shumica e tyre nuk u bënë besimtarë.');
INSERT INTO "ahmeti" VALUES(26,159,'26:159','Vërtet Zoti yt është i plotfuqishëm i mëshirshëm.');
INSERT INTO "ahmeti" VALUES(26,160,'26:160','Populli i Lutit të dërguarit i konsideroi gënjeshtarë.');
INSERT INTO "ahmeti" VALUES(26,161,'26:161','Kur vëllai i tyre, Luti i tha: “A nuk i frikësoheni (All-llahut)?”');
INSERT INTO "ahmeti" VALUES(26,162,'26:162','Unë jam i dërguar i juaj, jam i besueshëm!');
INSERT INTO "ahmeti" VALUES(26,163,'26:163','Pra, kini frikë (dënimin) All-llahun dhe më dëgjoni mua!');
INSERT INTO "ahmeti" VALUES(26,164,'26:164','Unë krkoj për këtë ndonjë shprblim prej jush, shpërblimi im është te Zot i botëve.');
INSERT INTO "ahmeti" VALUES(26,165,'26:165','(Të mjerët ju) A shkoni pas meshkujve të kësaj bote.');
INSERT INTO "ahmeti" VALUES(26,166,'26:166','E gratë tuaja i leni anash, të cilat Zoti juaj i krijoi për ju! Por ju jeni një popull që kaloni çdo kufij!”');
INSERT INTO "ahmeti" VALUES(26,167,'26:167','Ata i thanë: “O Lut, nëse nuk pushon (nga kjo që na thua), me siguri do të jesh i dëbuar prej nesh!”');
INSERT INTO "ahmeti" VALUES(26,168,'26:168','Ai tha: “Unë jam kundër veprimit tuaj!');
INSERT INTO "ahmeti" VALUES(26,169,'26:169','Zoti im, më shpëto mua dhe familjen time nga ajo që ëjnë ata!”');
INSERT INTO "ahmeti" VALUES(26,170,'26:170','Ne e shpëtuam atë dhe tërë familjen e tij,');
INSERT INTO "ahmeti" VALUES(26,171,'26:171','Përpos një plake (gruas së tij) që mbeti me atë (në dënim).');
INSERT INTO "ahmeti" VALUES(26,172,'26:172','E më pas i zhdukëm ata të tjerët.');
INSERT INTO "ahmeti" VALUES(26,173,'26:173','E Ne u lëshuam atyre një shi të jashtëzakonshëm (gurë nga qielli), e sa i keq ishte ai shi për ata të cilëve iu pat tërhequr vërejtja.');
INSERT INTO "ahmeti" VALUES(26,174,'26:174','Edhe në këtë kishte argument (për të mbledhur mend), po shumica e tyre nuk qenë besimtarë.');
INSERT INTO "ahmeti" VALUES(26,175,'26:175','E Zoti yt, padyshim është Ai i plotfuqishmi dhe i mëshirëshëmi.');
INSERT INTO "ahmeti" VALUES(26,176,'26:176','Edhe populi i Ejkës të dërguarit i konsiderojnë gënjeshtarë.');
INSERT INTO "ahmeti" VALUES(26,177,'26:177','Kur Shuajbi atyre u tha: “a nuk ruheni?”');
INSERT INTO "ahmeti" VALUES(26,178,'26:178','Unë jam i dërguar te ju, jam i besueshëm!');
INSERT INTO "ahmeti" VALUES(26,179,'26:179','Pra kini frikë (dënimin nga) All-llahun dhe më dëgjoni mua!');
INSERT INTO "ahmeti" VALUES(26,180,'26:180','Unë nuk kërkoj për këtë ndonjë shprblim prej jush, shpërblimi im është prej Zotit të botëve!');
INSERT INTO "ahmeti" VALUES(26,181,'26:181','Plotësoni matjen, e mos u bëni prej atyre që lënë mangu!');
INSERT INTO "ahmeti" VALUES(26,182,'26:182','Dhe matni me peshojë (terezi) të saktë (precize).');
INSERT INTO "ahmeti" VALUES(26,183,'26:183','Dhe ata leni mangu sendet e njerëzve, e mos bëni shkatërrime në tokë.');
INSERT INTO "ahmeti" VALUES(26,184,'26:184','Frikësohuni Atij që u krijo juve dhe breznitë e para.');
INSERT INTO "ahmeti" VALUES(26,185,'26:185','Ata thanë: “Sdyshim se ty të ka zënë magjia!”');
INSERT INTO "ahmeti" VALUES(26,186,'26:186','Ti nuk je tjetër, por njeri si edhe ne, dhe ne nuk të konsiderojmë tjetër vetëm se gënjeshtarë!');
INSERT INTO "ahmeti" VALUES(26,187,'26:187','E nëse je nga të vërtetit, atëherë lëshoje mbi ne një copë nga qielli (si dënim)!');
INSERT INTO "ahmeti" VALUES(26,188,'26:188','Ai tha: “Zoti im e di më së miri për atë që ju veproni!”');
INSERT INTO "ahmeti" VALUES(26,189,'26:189','Dhe ata e përgënjeshtruan ate, andaj i goditi dënimi i hijes. Vërtet, ai ishte dënimi i një dite të llahtarshme.');
INSERT INTO "ahmeti" VALUES(26,190,'26:190','Edhe në këtë (ngjarje) ka argumente, po shumica e tyre nuk u bënë besimtarë.');
INSERT INTO "ahmeti" VALUES(26,191,'26:191','E Zoti yt, pa dyshim është Ai i plotfuqishmi dhe i mëshirshmi.');
INSERT INTO "ahmeti" VALUES(26,192,'26:192','E edhe ai (kur’ani) është shpallje (zbritje) e Zotit të botëve.');
INSERT INTO "ahmeti" VALUES(26,193,'26:193','Atë e solli shpirti besnik (Xhibrili).');
INSERT INTO "ahmeti" VALUES(26,194,'26:194','(E solli) në zemrën tënde, për të qenë ti prej atyre që tërheqin vërejtjen (pejgamber).');
INSERT INTO "ahmeti" VALUES(26,195,'26:195','(Të shpallëm) Me gjuthëtë kulluar arabe.');
INSERT INTO "ahmeti" VALUES(26,196,'26:196','Dhe se ai (Kur’ani) është i përmendur edhe në librat e mëparshëm.');
INSERT INTO "ahmeti" VALUES(26,197,'26:197','A nuk ishte për ata (për jobsimtarët mekas) argument, se atë e dinin dijetarët (ulemaja) e beni israilëve?');
INSERT INTO "ahmeti" VALUES(26,198,'26:198','E sikur t’ia shpallin atë (Kur’anin) ndonjë joarabi,');
INSERT INTO "ahmeti" VALUES(26,199,'26:199','e ai t’ua lexonte atyre, ata nuk do t’i besonin atij.');
INSERT INTO "ahmeti" VALUES(26,200,'26:200','Dhe kështu, Ne u futëm atë (dyshimin) në zemrat kriminelëve,');
INSERT INTO "ahmeti" VALUES(26,201,'26:201','E ata nuk i besojnë atij (Kur’anit) derisa ta shohin dënimin e dhembshëm,');
INSERT INTO "ahmeti" VALUES(26,202,'26:202','Dhe t’ju vijë befas, duke mos e hetuar.');
INSERT INTO "ahmeti" VALUES(26,203,'26:203','e atëherë të thonë: “A thua do të na jepet afat?”');
INSERT INTO "ahmeti" VALUES(26,204,'26:204','E si ata kërkojnë shpejtimin e dënimit tonë (kur ithanë pejgamberit sillnie dënimin).');
INSERT INTO "ahmeti" VALUES(26,205,'26:205','Po më thuaj se edhe nëse Ne u jpim të jetojnë vite me rradhë,');
INSERT INTO "ahmeti" VALUES(26,206,'26:206','e pastaj t’u vijë atyre ajo me çka u tërhiqet vërejtja,');
INSERT INTO "ahmeti" VALUES(26,207,'26:207','çka do t’u vlejë atyre ajo jetë që gëzuan?');
INSERT INTO "ahmeti" VALUES(26,208,'26:208','Ne nuk kemi ndëshkuar asnjë fshat (vendbanim), e ai të mos ketë pasur pejgamber.');
INSERT INTO "ahmeti" VALUES(26,209,'26:209','Që ju përkujtojë (përfundimin e punës së keqe). Ne nuk bëmë të padrejtë.');
INSERT INTO "ahmeti" VALUES(26,210,'26:210','Atë (Kur’anin) nuk e sollën djajt (si fallin falltorëve).');
INSERT INTO "ahmeti" VALUES(26,211,'26:211','Atyre as nuk u takon e as që kanë mundësi.');
INSERT INTO "ahmeti" VALUES(26,212,'26:212','Pse ata jnë të penguar edhe për ta dëgjuar!');
INSERT INTO "ahmeti" VALUES(26,213,'26:213','Pra, përpos All-llahut, mos lut zot tjetër e të bëhesh prej të dënuarëve.');
INSERT INTO "ahmeti" VALUES(26,214,'26:214','Dhe tërhiqju vërejtjen farefisit tënd më të afërt.');
INSERT INTO "ahmeti" VALUES(26,215,'26:215','E të jeshë i butë ndaj besimtarëve që të pranuan ty.');
INSERT INTO "ahmeti" VALUES(26,216,'26:216','E nëse ata (jobesimtarët) të kundërshtojnë, ti thuaj: “Unë jam i pastër nga ajo që punoni ju”.');
INSERT INTO "ahmeti" VALUES(26,217,'26:217','Dhe mbështetu në të plotfuqishmin, Mëshiruesin.');
INSERT INTO "ahmeti" VALUES(26,218,'26:218','I cili të sheh kur të ngritesh në këmbë.');
INSERT INTO "ahmeti" VALUES(26,219,'26:219','(E të sheh) Edhe lëvizjen tënde në mesin e atyre që falen.');
INSERT INTO "ahmeti" VALUES(26,220,'26:220','Në të vërtetë, Ai është dëgjuesi, i dijshmi.');
INSERT INTO "ahmeti" VALUES(26,221,'26:221','A t’u tregojj unë se kujt i vijnë djajt?');
INSERT INTO "ahmeti" VALUES(26,222,'26:222','Ata i vijnë çdo gënjeshtari mëkatari.');
INSERT INTO "ahmeti" VALUES(26,223,'26:223','(U vijnë) Dhe hedhin dëgjimin, shumica e tyre gënjejn.');
INSERT INTO "ahmeti" VALUES(26,224,'26:224','E për sa u përket poeteve (që Muhammedi nuk është i tillë), ata i ndjekin të humbrit (nga e vërteta).');
INSERT INTO "ahmeti" VALUES(26,225,'26:225','A nuk sheh se ata hidhen në çdo anë (sa lavdërojnë, sa përqeshin).');
INSERT INTO "ahmeti" VALUES(26,226,'26:226','Dhe ata janë që flasin atë çka nuk punojnë.');
INSERT INTO "ahmeti" VALUES(26,227,'26:227','Me përjashtim të atyre që besuan, bënë vepra të mira, që e përmendën shumë All-llahun dhe që u mbrojtën pasi që atyre iu bë e padrejtë. E ata që bënë zullum do ta kuptojnë se ç’përfundim i shkojnë (ç’dënim të ashpër ose ç’vendi në Xhehennem do t’i nënshtrohen).');
INSERT INTO "ahmeti" VALUES(27,1,'27:1','Ta, Sinë. Këto janë ajete të Kur’anit dhe të librit të kuptueshëm.');
INSERT INTO "ahmeti" VALUES(27,2,'27:2','Janë udhërrefyes dhe myzhde për besimtarët.');
INSERT INTO "ahmeti" VALUES(27,3,'27:3','Që falin namazin dhe e japin zeqatin dhe mu ata janë të bindur ndaj botës tjetër.');
INSERT INTO "ahmeti" VALUES(27,4,'27:4','E atyre që nuk bsojnë botën e ardhme, Ne ua kemi hijeshuar veprat ndaj dhe ata bredhin.');
INSERT INTO "ahmeti" VALUES(27,5,'27:5','Të tillët janë ata që u takon dënimi më i rëndë dhe mu ata do të jenë më të dëshpruarit në botën tjetër.');
INSERT INTO "ahmeti" VALUES(27,6,'27:6','E s’ka dyshim se ti (Muhammed) e pranon Kur’anin nga i Urti, i Dijshmi.');
INSERT INTO "ahmeti" VALUES(27,7,'27:7','(Përkujto) Kur Musai i tha familjessë vet: “Unë e pash një zjarr, e nga ai do t’ju sjellë ndonjë lajm ose do t’ju sjell një urë të ndezur që të ngroheni.');
INSERT INTO "ahmeti" VALUES(27,8,'27:8','E kur arriti te ai u thirr: “I bekuar është ai pranë zjarrit dhe ata përreth tij. I veçuar në madhëri është All-llahu, Zot i botëve!');
INSERT INTO "ahmeti" VALUES(27,9,'27:9','O Musa, në të vërtetë, Unë jam All-llahu, i plofuqishmi, i urti.');
INSERT INTO "ahmeti" VALUES(27,10,'27:10','E ti, hidhe shkopin tënd! E kur e pa atë që lëvizte si të ishte gjarpër, ai (nga frika) ktheu prapa duke mos patur kjdes. (I thamë) O Musa! Mos u frikës se pranë meje të dërguarit nuk frikësohen!');
INSERT INTO "ahmeti" VALUES(27,11,'27:11','Përpos kush (nuk është i dërguar) bën mëkat (ai frikësohet) e që të keqen e zëvendëson me të mirën, atëherë s’ka dyshim se Unë jam ë fali shumë dhe jam mëshirues.');
INSERT INTO "ahmeti" VALUES(27,12,'27:12','Fute dorën tënde në xhepin tënd, e ajo do të dalë e bardhë pa kurrfarë të keqe, e këto janë ndër nëntë argument për të (vajtur) faraon dhe populli i tij, se me të vërtetë ata jaë popull i prishur.');
INSERT INTO "ahmeti" VALUES(27,13,'27:13','E kur u erdhën argumentet Tona të qarta, ata thanë: “Kjo pa dyshim është magji!”');
INSERT INTO "ahmeti" VALUES(27,14,'27:14','Dhe, edhe pse bindshëm të besueshëm në to (se shin nga Zoti), i mohuan në mënyrë të padrejtë e me mendjmadhësi, pra shikoje se çfarë ishte fundi i shkatërrimtarëve.');
INSERT INTO "ahmeti" VALUES(27,15,'27:15','Ne i patëm dhënë Davudit dhe Sulejmanit dituri, e ata të d thanë: “I falënderohemi vetëm All-llahut që na dalloi (me të mira) mbi shumë robër të Tij besimtarë”.');
INSERT INTO "ahmeti" VALUES(27,16,'27:16','E Sulejmani e trashëgoi Davudin dhe tha: “O njerëz, ne kuptojmë gjuhën e shpendëve e na shtë dhuruar nga gjithçka, e, vërtet ky është shpërblim i qartë”.');
INSERT INTO "ahmeti" VALUES(27,17,'27:17','E Sulejmanit ishte tubuar ushtria e vet që përbëhej nga exhinët, nga njerëzit dhe nga shpendët dhe ata mbanin rendin.');
INSERT INTO "ahmeti" VALUES(27,18,'27:18','Deri atëherë kur ariitën mbi luginën e buburrecave, një buburrëc tha: “O ju buburreca, hyni në vendet tuaja që të mos u coptojë Sulejmani dhe ushtria e tij duke mos ju vërejtur”.');
INSERT INTO "ahmeti" VALUES(27,19,'27:19','E ai (Sulejmani) buzëqeshi i gëzuar prej fjalës së tij dhe tha: “Zoti im, më mudnëso që të falënderoj të mirat Tuaja që m’i dhurove mua dhe prindërve të mi dhe ëq të bëj vepra të mira që Ti i pëlqen, e me mëshirën tënde më shtie në mesin e robërve Tuaj të mirë!”');
INSERT INTO "ahmeti" VALUES(27,20,'27:20','Dhe vështroi shpendët e tha: “ç’është kjo që nuk e shoh pupëzën? Jo, ajo nuk qenka këtu!”');
INSERT INTO "ahmeti" VALUES(27,21,'27:21','Unë do të dënoj atë më një dënim të ashpër ose do ta therrë ose ka për të më sjellë ndonjë argument të fortë (si arsyetim).');
INSERT INTO "ahmeti" VALUES(27,22,'27:22','Ajo nuk zgjati shumë e tha: “Unë kuptova atë që ti nuk je i njohur dhe të erdha nga Sebei me një lajm të sigurt”.');
INSERT INTO "ahmeti" VALUES(27,23,'27:23','Në të vërtetë unë gjeta një grua që po i sundonte ata (Popullin e Sebe-it) dhe asaj i ishte dhënë çdo send, e ajo kishte një fron të madh.');
INSERT INTO "ahmeti" VALUES(27,24,'27:24','Madje takova atë dhe popullin e saj se adhurojnë diellin e jo All-llahun, po djalli ua kishte hijeshuar atë veprim të tyre dhe i kishte shmangur rrugës së drejtë, andaj ata nuk gjejnë udhëzim.');
INSERT INTO "ahmeti" VALUES(27,25,'27:25','(i kishte shmangur) Për të mos adhuruar All-llahun që nxjerr në shesh (e di) të fshehtën në qiej e në tokë, dhe që e di atë që fshehni dhe atë që publikoni.');
INSERT INTO "ahmeti" VALUES(27,26,'27:26','All-llahu është një, nuk ka Zot tjetër pos Tij, Zot i Arshit të madh.');
INSERT INTO "ahmeti" VALUES(27,27,'27:27','Ai (Sulejmani) tha: “Do të shohim se a e thua të vërtetën apo je nga gënjeshtarët!”');
INSERT INTO "ahmeti" VALUES(27,28,'27:28','Shko me këtë letrën time e hidhjau atyre, largohu (pak) nga ata dhe përgjo se çka bisedojnë!”');
INSERT INTO "ahmeti" VALUES(27,29,'27:29','Ajo (gruaja - Belkisa) tha: “O ju pari, mua më ka arritur një letër madhështore!”');
INSERT INTO "ahmeti" VALUES(27,30,'27:30','(e lexova) Kjo është prej Sulejmanit (mandej e hapi, kur qe, në të) “Me emrin e All-llahut, Mëshiruesit, Mëshirëbërësit!” (Bismil-lahir-Rahmanir-Rrahim!)');
INSERT INTO "ahmeti" VALUES(27,31,'27:31','(në vazhdim shkruan) “Të mos u mbani në të madh kundër meje, po të vini të dorëzuar (edhe besimtarë)!”');
INSERT INTO "ahmeti" VALUES(27,32,'27:32','Ajo tha: “O ju pari, më sugjeroni në këtë çështje timën se unë nuk do të vendosi asgjë derisa edhe ju të jepni pëlqim!”');
INSERT INTO "ahmeti" VALUES(27,33,'27:33','Ata (paria) i thanë: “Ne jemi të fuqishëm dhe jemi luftëtarë të rreptë, por puna qëndron te ti, e ti mendo se çka do të na urdhërosh!”');
INSERT INTO "ahmeti" VALUES(27,34,'27:34','Ajo tha: “Kur sundusit e pushtojnë ndonjë vend, ata e rrënojnë atë parinë më të zgjedhur të atij vendi e nënshtrojnë.. Kështu ata veprojnë!”');
INSERT INTO "ahmeti" VALUES(27,35,'27:35','Po unë do t’u dërgojë atyre një dhuratë (të madhe), e do të shohë se çka do të na sjellin të dërguarit (me dhuratë).');
INSERT INTO "ahmeti" VALUES(27,36,'27:36','E kur i erdhi ajo Sulejmanit, ai tha: “A me pasuri më ndihmoni mua? E atë që All-llahu më ka dhënë mua është shumë më e dobishme nga ajo që u ka dhënë juve! Por ju krenoheni me dhuratat tuaja!”');
INSERT INTO "ahmeti" VALUES(27,37,'27:37','Kthehu te ata (i tha kryesuesit të të deleguarëve), se për Zotin ne do t’u vvijmë atyre me një ushtri, së cilës ata nuk do të mund t’i bëjnë ballë dhe do t’’i dëbojmë prej aty të nënshtruar e të poshtëruar!');
INSERT INTO "ahmeti" VALUES(27,38,'27:38','Ai (Sulejmani) tha: O ju pari, cili prej jush do të më sjellë fronin e saj, para se të vijnë ata të dorëzuar?”');
INSERT INTO "ahmeti" VALUES(27,39,'27:39','Njëri prej xhinëve të vrazhdë (Ifriti) tha: “Unë do ta sjellë atë ty, par se të ngrites nga vendi yt, unë për të km fuqi dhe jam i sigurt!”');
INSERT INTO "ahmeti" VALUES(27,40,'27:40','E ai i cili kishte dituri nga libri tha: “Unë ta sjell atë ty sa çel e mshel sytë (këtij i tha sille)!” E kur e pa atë (fron) të vendosur pranë tij (Sulejmani) tha: Kjo është dhuntia e Zotit tim që të sprovojë mua se a do ta falënderoj, apo mos do ta përbuz. E kush falenderon, ai falenderon për të mirën e vet, e kush përbuz, Zoti im (s’ka nevojë), është i pa nevojë e bujar!”');
INSERT INTO "ahmeti" VALUES(27,41,'27:41','Ai (Sulejmani) tha: “Ndryshojani asaj fronin që ta provojmë se a po e njeh apo është nga ata që nuk dinë ta njohin!”');
INSERT INTO "ahmeti" VALUES(27,42,'27:42','E kur erdhi ajo, iu tha: “A është i tillë froni yt?” Ajo tha: “Si të ishte vetë ai?!” (Sulejmani tha) Neve na është dhënë dituria përpara saj dhe ne ishim besimtarë para saj.');
INSERT INTO "ahmeti" VALUES(27,43,'27:43','Pse atë (Belkisën) e kishte penguar ajo që adhuronte, pos All-llahut, dhe ishte nga një popull jobesimtar.');
INSERT INTO "ahmeti" VALUES(27,44,'27:44','Asaj iu tha: “Hyn në pallat, e kur mendoi se është një ujë i madh dhe i përvol këmbët”. Ai (Sulejmani) tha: “Ky është një pallat i lëmuar prej qelqi! Ajo tha: “Zoti im, unë i kam bërë krim vetes e tash i dorëzohem (pranoj besimin) All-llahut, Zotit të gjithësisë së bashku me Sulejmanin”.');
INSERT INTO "ahmeti" VALUES(27,45,'27:45','Ne ia dërguam Themudit vëllain e tyre (nga gjaku), Salihun (t’u thotë): “Adhuroni All-llahun!” Kur qe, ata du grupe që kundërshtoheshin.');
INSERT INTO "ahmeti" VALUES(27,46,'27:46','Ai (Salihu) tha: “O populli im, përse nguteni e kërkoni të keqen (dënimin) para të mirës, pse të mos kërkoni falje All-llahut që të mëshiroheni?”');
INSERT INTO "ahmeti" VALUES(27,47,'27:47','Ata thanë: “Ne parandjejmë fatkeqësi më ty dhe me ata që janë më ty!” Ai tha: “Ndjellja e fatit tuaj është te All-llahu, por vetë ju jeni një popull që ngatërroheni!”');
INSERT INTO "ahmeti" VALUES(27,48,'27:48','E në qytet ishin nëntë veta që bënin shkatërrime në tokë, e nuk përmirësonin.');
INSERT INTO "ahmeti" VALUES(27,49,'27:49','Ata që thanë: “Betohuni mes vete në All-llahu, ta mbytni atëdhe familjen e tij natë, e pastaj të afërmve të tij t’u thoni: Ne nuk morëm pjesë në mbytjen e familjes së tij, dhe se ne jemi të drejtë (të vërtetë)”');
INSERT INTO "ahmeti" VALUES(27,50,'27:50','Ata përgaditën një kurth e Ne ua shpërblyer kruhtin duke mos e hetuar ata.');
INSERT INTO "ahmeti" VALUES(27,51,'27:51','Pra, vështro se çfarë ishte përfundimi i dredhisë së tyre. Në të vërtetë, Ne zhdukëm të gjithë ata dhe popullin e tyre.');
INSERT INTO "ahmeti" VALUES(27,52,'27:52','e këto janë shtëpitë e tyre të mbetura shkret për shkak të mizorisë së tyre. E ne nuk ka dyshim se në këtë (rrënim) për një popull që kupton ka argument.');
INSERT INTO "ahmeti" VALUES(27,53,'27:53','Ne i shpëtuam ata që besuan dhe që ishin të mëtur (duke iu shmangur mëkatit).');
INSERT INTO "ahmeti" VALUES(27,54,'27:54','(Përkujto) Lutin kur ai popullit të vet i tha: “A bëni atë punë të shëmtuar që edhe vetë ju e kuptoni (se është e keqe)?”');
INSERT INTO "ahmeti" VALUES(27,55,'27:55','A vërtet, ju nga epshi kontaktoni me meshkujt, e braktisni gratë tuaja? Ju jeni popull injorantë (xhahil)');
INSERT INTO "ahmeti" VALUES(27,56,'27:56','Përgjegje e popullit të tij nuk qe tjetër vetëm se të thonë: “Përzënie familjen e Lutit nga vendi juaj, sepse ata jamë njerëz që ruajnë pastërtinë?”');
INSERT INTO "ahmeti" VALUES(27,57,'27:57','E Ne e shpëtuam atë dhe familjen e tij, përveç gruas së tij, së cilës oa caktuam të mbetet me të dënuarit.');
INSERT INTO "ahmeti" VALUES(27,58,'27:58','E kundër atyre lëshuam një shi (me gurë); i shëmtuar ishte ai shi për ata të cilëve iu kishte tërhequr vërejtja.');
INSERT INTO "ahmeti" VALUES(27,59,'27:59','Thuaj (o i dërguar): “Falënderimi i qoftë All-llahut, e shpëtimi qoftë ndaj robërve të Tij që Ai i zgjodhi (pejgamberët)! A më i mrë (për të besuar) është All-llahu, apo ata që ia bëjnë shok?”');
INSERT INTO "ahmeti" VALUES(27,60,'27:60','Ai krijoi qiejt e tokën, që për ju lëshoi shi nga qielli, e Ne me të bëmë të lulëzojnë kopshte të bukura, që përju ka qenë e pamundshme t’i bëni të mbijnë bimët në to; Amos ka ndonjë zot tjetër pos All-llahut? Jo, por ata janë popull që shtrembërojnë (të vërtetën).');
INSERT INTO "ahmeti" VALUES(27,61,'27:61','A është Ai që tokën e bëri vendqëndrim e nëpër të rrjedhin lumenj, dhe asaj (tokës) i bëri (male) përforcuese, dhe në mes dy deteve bëri ndarje. A ka përveç All-llahu zot tjetër? Jo, por shumica e tyre nuk e dinë.');
INSERT INTO "ahmeti" VALUES(27,62,'27:62','A është Ai që i përgjigjet nevojtarit (të mjerit) kur ai e thërret, duke ia larguër të keqen e juve ju bën mbizotërues të tokës. A ka zot tjetër pos All-llahut? Jo, por ju shumë pak përkujtoni.');
INSERT INTO "ahmeti" VALUES(27,63,'27:63','Ai që ju orienton në errësirat e tokës e të detit, dhe Ai që pranë mëshirës së Tij (shiut), si myzhde i lëshon erërat. Vallë, a ka tjetër zot pos All-llahut? E lartë është madhëria e Tij nga çka ia përshkruajnë shok.');
INSERT INTO "ahmeti" VALUES(27,64,'27:64','Ai që e filloi krijimin (e njeriut) e pastaj e përsëriti atë (e ringjall pas vdekjes) dhe Ai ë ju furnizn nga qielli e toka. A mos ka zot tjetër pos All-llahut? Thuaj: “Silni argmentet tuaja, nëse jeni të sinqertë!”');
INSERT INTO "ahmeti" VALUES(27,65,'27:65','Thuaj: “Askush veç All-llahut as në qiejt, as në tokë nuk e di të fshehtën, as nuk e din se kur ringjallen!”');
INSERT INTO "ahmeti" VALUES(27,66,'27:66','Por, dijenia e tyre arrihet në botën tjetër (kur të përjetojnë çka u është premtuar). Ja, ata dyshojnë për kët (për botën tjetër), bile janë të verbër ndaj saj.');
INSERT INTO "ahmeti" VALUES(27,67,'27:67','Ndërsa ata të cilët nuk besun thanë: “A pasi të (vdesim) bëhemi dhe e pluhur ne edhe prindërit tanë, (a thua) do të ringjallemi?');
INSERT INTO "ahmeti" VALUES(27,68,'27:68','Kjo na është premtuar neve e dhe prindërve tamë më përpara, por kjo nuk është gjë tjetër veçse legjendë e të parëve!”');
INSERT INTO "ahmeti" VALUES(27,69,'27:69','Thuaj: “Udhëtoni nëpër tokë dhe shikoni se si ishte përfndimi i kundërshtarëve!”');
INSERT INTO "ahmeti" VALUES(27,70,'27:70','Po ti (Muhammed) mos u trzo për ta, e as mos e ngushto veten nga ato dredhi që bëjnë!');
INSERT INTO "ahmeti" VALUES(27,71,'27:71','E ata thonë: “Po qe se jeni të sigurt në ato çka flitni, kur do të realizohet ai premtimi (me dënim)?”');
INSERT INTO "ahmeti" VALUES(27,72,'27:72','Ti thuaj: “Tanimë u është ngjeshur juve një pjesë e atij premtimi për të cilin po ju ngutet juve!”');
INSERT INTO "ahmeti" VALUES(27,73,'27:73','S’ka dyshim se Zoti yt është shumë bëmirës ndaj njerëzve, po shumica e tyre nuk e falënderojnë.');
INSERT INTO "ahmeti" VALUES(27,74,'27:74','E është e sigurt se Zoti yt di çka fshehin zemrat e tyre dhe çka shfaqin haptazi.');
INSERT INTO "ahmeti" VALUES(27,75,'27:75','Nuk ka asgjë të fshehu në qiell e as në tokë, e tëmos jetë e shënuar në librin e saktë (dijenin e Zotit).');
INSERT INTO "ahmeti" VALUES(27,76,'27:76','Vëërtet, ky Kur’ani u rrëfen beni israilve të shumtën e asj që ata ishin të përçarë.');
INSERT INTO "ahmeti" VALUES(27,77,'27:77','Dhe pamëdyshje ai është udhërrëfyes e mëshirë për besimtarët.');
INSERT INTO "ahmeti" VALUES(27,78,'27:78','E Zoti yt, vërtet do të gjykojë mes tyre me drejtësinë e Tij, e Ai është i gjithfuqishmi e dijshmi.');
INSERT INTO "ahmeti" VALUES(27,79,'27:79','E ti pra, mbështetu në All-llahun se me të vërtetë ti je në të drejtën e sigurt.');
INSERT INTO "ahmeti" VALUES(27,80,'27:80','E ti nuk mund t’i bësh të dëgjojnë të vdekurit, e as nuk mund ta bësh të shurdhëtin të dëgjojë thirrjes, e sidomos kur ata ta thejnë shpinën.');
INSERT INTO "ahmeti" VALUES(27,81,'27:81','E as që mund t’i largosh të verbërit nga humbja e tyre (zemrat e verbëra), ti nuk mund ta bësh të dëgjojë ndonjë tjetër, vetëm atë që i beson argumentet Tona, e ata janë myslimanë.');
INSERT INTO "ahmeti" VALUES(27,82,'27:82','. E kur kundër tyre do të bëhet fjala (e dënimit) realitet. Ne do t’u nxjerrim një shtazë nga toka që do t’u flasë (ose do t’i lëndojë), sepse njerëzit nuk ishin të bindur në argumentet Tona.');
INSERT INTO "ahmeti" VALUES(27,83,'27:83','E ditën kur do të tubojmë prej secilit popull grupe nga ata që kanë mohuar argumentet Tona, ata të vërë njëri pas tjetrit, do të jenë të penguar.');
INSERT INTO "ahmeti" VALUES(27,84,'27:84','Deri sa të arrijnë (në vendin e llogarisë), Ai u thotë: “A ju i mohuat argumentet e Mia duke mos i menduar dhe formuar dije për to? E çka vepronit ju ashtu (në dunja)?”');
INSERT INTO "ahmeti" VALUES(27,85,'27:85','Dhe kundër tyre zbatohet vendimi (i dënimit) për shkak se bënë krim dhe ata nuk do të flasin.');
INSERT INTO "ahmeti" VALUES(27,86,'27:86','A nuk shohin ata se Ne ua bëmë natën që të pushojnë, kurse ditën të ndritshme. Në këtë ka argument për një popull që beson.');
INSERT INTO "ahmeti" VALUES(27,87,'27:87','(përkujto) Ditën kur i fryhet surit, e të frikësohet kush është në tokë, përpos kë do All-llahu (të mos frikësohet) dhe t’i vjnë Atij të gjithë të përulur.');
INSERT INTO "ahmeti" VALUES(27,88,'27:88','E, i sheh kodrat e mendon se ato janë të palëvizshme, ndërsa ato lëvizin si retë, (kjo është) mjeshtëri e Allhut që përsosi çdo send, e Ai është hollësisht i njohur se çka punoni.');
INSERT INTO "ahmeti" VALUES(27,89,'27:89','Kush ka bërë vepër të morë, ati do t’i takojë (shpërblim) edhe më i mirë se ajo dhe ata do të jenë të siguruar prej tmerrit të asaj dite.');
INSERT INTO "ahmeti" VALUES(27,90,'27:90','E kush do të paraqitet me punë të këqia (me besim të gabuar, ata do të hidhen të përmbysur në fytyrat e tyre në zjarr, (u thuhet) Ju nuk shpërbleheni me tjetër pos me atë që punuat.');
INSERT INTO "ahmeti" VALUES(27,91,'27:91','Unë jam urdhëruar të adhuroj vetëm Zotin e këtij qyteti, të cilin Ai e bëri të shenjtë, se Atij i takon çdo send, dhe jam urdhëruar të jemë prej besimtarëve të sinqertë.');
INSERT INTO "ahmeti" VALUES(27,92,'27:92','E, edhe ta lexoj Kur’anin, e kush pranon dhëzimin e tij, ai udhëzohet vetëm për të mirën e vet, e kush ka humbur, ti thuaj: “Unë vetëm tërheq vërejtjen”.');
INSERT INTO "ahmeti" VALUES(27,93,'27:93','Dhe thuaj: “Falënderoj Allahun, e Ai do t’ua tregojë argumentet e veta dhe do t’i kuptoni ato atëherë (kur nuk u bën dobi). Zoti yt nuk është indiferent ndaj asaj që veproni ju”.');
INSERT INTO "ahmeti" VALUES(28,1,'28:1','Ta, Sinë, Mimë.');
INSERT INTO "ahmeti" VALUES(28,2,'28:2','Këto janë ajetet e librit (të Kur’anit) të plotkuptueshëm.');
INSERT INTO "ahmeti" VALUES(28,3,'28:3','Që po t’i lexojnë ty nga lajmi rreth Musait, e që është e vërtetë për një popull që beson.');
INSERT INTO "ahmeti" VALUES(28,4,'28:4','Me të vërtetë, faraoni ka ngritur kryet lart në tokë, e popullin e saj e ka grupëzuar dhe një grup prej tyre e shtyp, ashtu që djemte e tyre ua mbyt, e gratë e tyre ua lë të jetojnë. Vërtet, ai ishte prej më shkatërrimtarëve.');
INSERT INTO "ahmeti" VALUES(28,5,'28:5','E Ne duam t’i lartësojmë ata që u shtypën në tokë, t’i bëjmë udhëheqës dhe t’i bëjmë trashëgues.');
INSERT INTO "ahmeti" VALUES(28,6,'28:6','Dhe atyre t’u japim pushtet në tokë, e faraonit, Hamanit dhe ushtrisë së këtyre dyve t’ua tregojmë atë që ruheshin (ia kishin frikën).');
INSERT INTO "ahmeti" VALUES(28,7,'28:7','Ne nënën e Musait e inspiruam: t’i japë atij gji, e kur të keshë frikë për të, atëherë atë hidhe në lumë, e mos u frikëso as mos u pikëllo, se Ne do ta kthejmë atë ty dhe do ta bëjmë atë nga të dërguarit.');
INSERT INTO "ahmeti" VALUES(28,8,'28:8','E atë e gjeti familja e faraonit, ashtu që në fund ai t’u bëhet atyre armik e dëshprim. Vërtet, faraoni, Hamani dhe ushtria e tyre ishin kundërshtarë (gabonin me qëllim).');
INSERT INTO "ahmeti" VALUES(28,9,'28:9','E gruaja e faraonit tha: “Shpresë gëzimi për mua dhe për ty, mos e mbytni atë, ndoshta do të na sjellë dobi ose do ta adoptojmë për fëmijë”. Pra ata nuk e dinin rrjedhimin.');
INSERT INTO "ahmeti" VALUES(28,10,'28:10','E zemra e nënës së Musait agoi e zbrazët (kur kuptoi se ka rënë në duar të faraonit) dhe gati ta zulojë atë (fëmijën), sikur të mos ia forconim Ne zemrën e saj që të bëhet e bindur (në premtimin e All-llahut).');
INSERT INTO "ahmeti" VALUES(28,11,'28:11','E ajo i tha motrës së tij: “Gjurmoje atë”, kurse ajo e shikonte atë prej së largu dhe ata nuk e hetonin (se ishte motra e tij që e përcjell).');
INSERT INTO "ahmeti" VALUES(28,12,'28:12','Ndërsa Ne ia patëm ndaluar atij më parë thithjen e gjinit, e ajo (motra e Musait) tha: “A doni t’ju tregoj për një familje që do të kujdeset për të dhe që do të jenë të sinqertë ndaj tij?”');
INSERT INTO "ahmeti" VALUES(28,13,'28:13','Dhe ashtu atë e kthyem te nëna e vet që ajo të jetë e kënaqur e jo e pikëlluar dhe që ta kuptojë ajo se premtimi i All-llahut është i vërtetë e i sigurt, por shumica e tyre nuk e dinë.');
INSERT INTO "ahmeti" VALUES(28,14,'28:14','E pasi ai (Musai) e arriti moshën madhore dhe u bë i pjekur, Ne i dhamë urtësi e dituri. Kështu ne i shpërblejmë bamirësit.');
INSERT INTO "ahmeti" VALUES(28,15,'28:15','Dhe ai hyri në qytet në një kohë të pahetuar prej banorëve të tij, dhe në të i gjeti dy veta që po përlaheshin, njëri prej ithtarëve të tij e tjetri prej armiqve të tij, e ai i anës së tij i kërkoi ndihmë kundër armikut dhe Musai i ra grusht e ai vdiç. Ai (Musai) tha: “kjo është prej veprave të djallit, e s’kadyshim se ai është armik i hapët që shpie në humbje”.');
INSERT INTO "ahmeti" VALUES(28,16,'28:16','Ai tha: “Zoti im, unë e ngarkova veten e Ti më fal mua!” Dhe Ai ia fali, se Ai është që fal shumë, ësht mëshirues.');
INSERT INTO "ahmeti" VALUES(28,17,'28:17','Ai tha: “Zoti im, betohem në ato të mira që m’i dhurove mua se krrë nuk do të vihem në ndihmë të kriminelëve”.');
INSERT INTO "ahmeti" VALUES(28,18,'28:18','Dhe (Musai) agoi i frikësuar në qytet e duke pritur (se çka po i ngjanë), kur qe, ai i cili kishte kërkuar ndihmë dje, e thirri sërish (në ndihmë), e Musai i tha: “Vërtet ti qenke ngatërrestar i njohur!”');
INSERT INTO "ahmeti" VALUES(28,19,'28:19','E kur dëshi ta rëmbejë atë që ishte armik i të dyve, ai tha: “A do të më mbysësh mua siç e mbyte dje njeriun, ti nuk do tjetër, vetëm se do të bëhesh arrogant n tokë, e nuk dëshiron të jeshë nga përmirësuesit!”');
INSERT INTO "ahmeti" VALUES(28,20,'28:20','Dhe erdhi një njeri që ngutej nga ana më e largët e qytetit e tha: “O Musa, parësimi është duke biseduar për të mbytur ty, pra ti dil (ik), unë jam këshillues yti”.');
INSERT INTO "ahmeti" VALUES(28,21,'28:21','E ai doli prej aty i frikësuar e duke pritur (se ç’po ndodh) dhe tha: “O Zoti im, më shpëto prej popullit zullumqar”.');
INSERT INTO "ahmeti" VALUES(28,22,'28:22','Dhe kur drejtua kah Medjeni tha: “Shpresoj që Zoti im të më orientojë rrugës së drejtë”.');
INSERT INTO "ahmeti" VALUES(28,23,'28:23','E kur arriti ujin e Medjenit, aty gjeti një grumbull njerëzish që po i jepnin bagëtisë ujë, e pak më larg prej tyr vërejti d gra që o i ndalnin (kafshët e tyre) e u tha: “E çka është puna e juve dyjave?” Ato që të dyja i thanë: “Ne nuk u japim juë deri që të largohen barinjtë, kurse babai ynë është shumë i vjetër!”');
INSERT INTO "ahmeti" VALUES(28,24,'28:24','Atëherë ai u dha ujë (kafshëve) të tyre, pastaj u largua anash nën një hije e tha: “O Zoti im, unë kam nvojë për çkado që të më japësh!”');
INSERT INTO "ahmeti" VALUES(28,25,'28:25','E njëra prej tyre erdhi te ai, ecte e turpërueshme, e tha: “Babai im të thërret që të paguaj ty shpërblimin për atë që na u dhe (bagëtisë) ujë!” E Kur shkoi (Musai) te ai dh ai i tregoi atij ngjarjen, ai tha: “Mos ke frikë, paske shpëtuar nga populli zullumqar!”');
INSERT INTO "ahmeti" VALUES(28,26,'28:26','Njëra prej atyre dyjave: “O babai im, merre këtë në shërbim me pagë, pse më i miri i atij që do ta marrësh në shërbim është ai i fuqishmi, e bsniku!”');
INSERT INTO "ahmeti" VALUES(28,27,'28:27','Ai (Shujabi) tha: Unë dëshiroj të martojë ty me njërën prej këtyre dy vjzave të mia, me kusht që të shërbesh tetë vjet, e nëse i plotëson dhjetë, ai është vullneti yt, e unë nuk dëshiroj të rëndojë ty, e në dashtë All-llahu, ti do të gjejsh te unë mirëkuptim!”');
INSERT INTO "ahmeti" VALUES(28,28,'28:28','Ai (Musai) tha: “Kjo le të mbetet mes meje e teje, e cilindo afat që do ta kryej nga këta të dy, nuk do të ketë përgjegjësi për mua. Për këtë që po themi All-llahu është garant”.');
INSERT INTO "ahmeti" VALUES(28,29,'28:29','E kur Musai e kreu afatin dhe udhëtoi me familjen e vet, e vërejti kah ana e Turit një zjarr, e familjes së vet i tha: “Rrini këtu, unë kam vërejtur një zjarr, ndoshta do t’ju sjell nga ai ndonjë lajm ose një urë nga zjarri që të ngroheni”.');
INSERT INTO "ahmeti" VALUES(28,30,'28:30','Dhe kur arrit te ai (zjarr) nga ana e djathtë e luginës së atij vendi të bekuar me pemë u thirr: “O Musa, s’ka dyshim se Unë jm All-llahu, Zoti i botëve!”');
INSERT INTO "ahmeti" VALUES(28,31,'28:31','Dhe ti hidhe shkpoin tënd! E kur e pa se po lëviz si të ishte gjarpër i shpejtë, ktheu të ikë e nuk e vëstroi prapa. O Musa, ktheu ku ishe e mos u frikëso se me të vërtetë ti je i sigurt.');
INSERT INTO "ahmeti" VALUES(28,32,'28:32','Fute dorën në xhep e ajo do të dalë e bardhë pa ndonjë të metë dhe shtrëngoje për vete krahun tënd kur të frikësohesh. Këto janë dy argumente nga Zoti yt për te faraoni dhe rrethi i tij, se me të vërtetë ata janë popull i prishur.');
INSERT INTO "ahmeti" VALUES(28,33,'28:33','Ai (Musai) tha: “O Zoti im, unë kam mbytur një njeri prej tyre, e frikësohem se do të më mbysin!”');
INSERT INTO "ahmeti" VALUES(28,34,'28:34','E vëllai im, Haruni, ësht më orator se unë, andaj dërgoje atë me mua ndihmë që të vërtetojë fjalët e mia. Unë kam frikë se do të më shpallin gënjeshtar.');
INSERT INTO "ahmeti" VALUES(28,35,'28:35','(All-llahu iu përgjegj) Tha: “Ne do ta forcojmë ty krahun me vëllain tënd dhe me argumentet Tona do t’ju japim pushtet juve dyve ashtu që ata të mos mund t’u afrohen juve, andaj ju të dy dhe ata që u janë bindur juve, jeni ngadhënjimtarë”.');
INSERT INTO "ahmeti" VALUES(28,36,'28:36','E kur Musai ua solli atyre argumentet Tona të qarta, ata thanë: “Kjo nuk është tjetër vetëm se magji e trilluar dhe këtë nuk e kemi dëgjuar as ndër prindërit tanë të hershëm!”');
INSERT INTO "ahmeti" VALUES(28,37,'28:37','E Musai tha: “Zoti im e di më së miri për atë që u erdhi me udhëzim prej Tij dhe kujt do t’i takojë përfundimi i mirë, se mizorët me siguri nuk do të kenë shpëtim”.');
INSERT INTO "ahmeti" VALUES(28,38,'28:38','E faraoni tha: “O ju pari, unë nuk njoh njoh zot tjetër për ju pos meje, andaj ti o Haman, m’i pjek (tullat) nga dheu e më ndërto një kullë të lartë ndoshta do të arrijë ta shoh zotin e Musait, sepse nunë mendoj se vërtet ai është gënjeshtar”.');
INSERT INTO "ahmeti" VALUES(28,39,'28:39','Dhe ashtu ai dhe ushtëria e tij u sollën në tokë me kruenëçësi ndaj të vërtetës dhe menduan se nuk do të kthehen te Ne.');
INSERT INTO "ahmeti" VALUES(28,40,'28:40','Andaj, Ne e kapëm atë dhe ushtërinë e tij dhe e hodhëm në det, shiko pra se si përfundojnë mizorët.');
INSERT INTO "ahmeti" VALUES(28,41,'28:41','Dhe ata i bëmë prijësa që thërrasin për në zjarr dhe ditën e kijametit atje nuk do t’u ndihmohet.');
INSERT INTO "ahmeti" VALUES(28,42,'28:42','Edhe në këtë botë ikemi përcjellë ata me mallkim, kurse në ditë e kijametit ata janë të përbuzur.');
INSERT INTO "ahmeti" VALUES(28,43,'28:43','E pasi i shkatërruam popujt e mëparshëm, Ne Musait i dhamë librin që është dritë për njerëz dhe udhëzim e mëshirë që të marrin mësim.');
INSERT INTO "ahmeti" VALUES(28,44,'28:44','E ti nuk ishe në anën perëndimore (të vendit ku All-llahu i foli Musait) kur Ne Musait ia besuam shpalljën (e bëmë pejgamber dhe dërguam te faraoni), e ti as nuk ishe aty pran.');
INSERT INTO "ahmeti" VALUES(28,45,'28:45','Por Ne krijuam popuj (mes Musait e teje Muhammed), e koha ka zgjaur (ndaj, të dërguam ty). Dhe nuk banove në mesin e popullit të Medjenit e t’u lexosh këtyre argumentet Tona (lajmin për Musaln, për Shuajbin, për vvajzat e tij), por Ne të dërguam (dhe të njoftuam).');
INSERT INTO "ahmeti" VALUES(28,46,'28:46','Dhe ti nuk ke qenë pranë Kodrës Tur kur Ne e thirrëm (Musain), por (tregimi ynë) është mëshirë nga Zoti yt, për t’ia tërhequr vërejtjen një populli që para teje nuk u kishte ardhur pejgamber, dhe ashtu ata të marrin mësim.');
INSERT INTO "ahmeti" VALUES(28,47,'28:47','Dhe të mos thonë, kur t’i godasë ndonjë e papritur, të cilën e kanë merituar vetë: “Zoti ynë, përse nuk na ke dërguar ndonjë pejgamber që të pasonim argumentet Tua e të bëheshim besimtar!”');
INSERT INTO "ahmeti" VALUES(28,48,'28:48','E kur Atyre u edhi e vërteta nga ana Jonë, ata thanë: “Përse nuk iu dha atij (Muhammedit) sikurse Musait (ndonjë mrekulli materiale)! Po a nuk e mohuan atë që iu dha Musait më parë?” E, thanë: “Dy magji që përmbajnë njëra-tjetërn. Dhe thanë: çdonjëren prej tyre ne e mohojmë!”');
INSERT INTO "ahmeti" VALUES(28,49,'28:49','Thuaj: “Nëse është e vërtetë çka thoni, atëherë sillni një libër prej All-llahut që është edhe më udhëzues se këta dy (Tevrati e Kur’ani), e ti përmbahem edhe unë atij?”');
INSERT INTO "ahmeti" VALUES(28,50,'28:50','E nëse ata nuk të përgjigjen ty, atëherë dije se ata ndjekin vetëm dëshirat e veta, e kush është më humbur se ai që duke pasur fakt prej All-llahut, ndjek epshin e vet? S’ka dyshim se All-llahu nuk udhëzon popullin zullumqar.');
INSERT INTO "ahmeti" VALUES(28,51,'28:51','Ndërsa Ne pandërprerë u dërguam atyre shpalljen, ashtuqë të mendojnë.');
INSERT INTO "ahmeti" VALUES(28,52,'28:52','E atyre që Ne ua dhamë librin përpara këtij, disa prej tyre i besojnë këtij (Kur’anit).');
INSERT INTO "ahmeti" VALUES(28,53,'28:53','Dhe kur u lexohet atyre, thonë: “Ne kemi besuar atij, ajo është e vërtetë prej Zotit tonë, ne edhe para tij kemi qenë myslimanë!”');
INSERT INTO "ahmeti" VALUES(28,54,'28:54','Atyre u jepet shpërblim i dyfishtë për arsye se bënë durim, dhe ata të keqen e largojnë me të mirën, e nga ajo që Ne e furnizuam, ata japin.');
INSERT INTO "ahmeti" VALUES(28,55,'28:55','Dhe kur e dëgjojnë ndonjë llomotitje marrëzie, i kthejnë shpinën dhe thonë: “Ne kemi vepra tona e ju tuajat, qofshit larg nesh, ne nuk na duhen injorantët!”');
INSERT INTO "ahmeti" VALUES(28,56,'28:56','Është e vërtetë se ti nuk mund ta udhëzosh atë që do ti, por All-llahu udhëzon kë të dojë dhe Ai është që di më së miri për të udhëzuarit.');
INSERT INTO "ahmeti" VALUES(28,57,'28:57','Ata thanë: “Nëse ne ndjekim rrugën e drejtë së bashku me ty, ne me të shpejtë do të jemi të dëbuar prej vendit tonë!” Po a nuk u siguruam Ne atyre një vend të shenjtë e të sigurt që aty sillen frutat e çdo sendi si furnizim mga ana Jonë, po shumica e tyre nuk e dinë.');
INSERT INTO "ahmeti" VALUES(28,58,'28:58','E sa vendbanime që përbuzën jetën e vet Ne i kemi shkatërruar, e ja, ato janë shtëpitë e tyre, që pas tyre ato pak kohë janë banuar prej dokujt dhe Ne ishim trashëgues të tyre.');
INSERT INTO "ahmeti" VALUES(28,59,'28:59','Zoti yt nuk është i tillë që tq shkatërrojë venbanimet para se në kryeqendër të tyre të dërgoj pejgamber, i cili do t’u lexojë atyre argumentet Tona, dhe Ne nuk shkatërruam vendbanime, po vetëm kur banorët e tyre ishin zullumqarë.');
INSERT INTO "ahmeti" VALUES(28,60,'28:60','Dhe çdo gjë që u është dhnë juve është kënaqësi dhe shije e kësaj bote, ndërsa ajo që është te All-llahu (thevabi) është shumë më e mirë dhe përhershme, pra, a nuk mendoni?');
INSERT INTO "ahmeti" VALUES(28,61,'28:61','A është ai, të cilit Ne i kemi premtuar një premtim të mirë (për Xhennet), sikurse ai të cilit i kemi dhënë kënaqësi të kësaj jete, kurse në ditën e kijametit ai do të jetë prej të dënuarve?');
INSERT INTO "ahmeti" VALUES(28,62,'28:62','(përkujto) DItën kur i thërret ata e u thotë: “Ku janë ata shokët e Mi, të ccilët ju i pandehnit (si zota)?”');
INSERT INTO "ahmeti" VALUES(28,63,'28:63','E ata, të cilët e merituan fjfalën (dënimin)), thonë: “Zoti ynë, këta janë që ne i humbëm, i humbëm ata si hunbëm edhe vetë, ne para Teje tërhiqëm se ata nuk na adhueuan neve!”');
INSERT INTO "ahmeti" VALUES(28,64,'28:64','Dhe u thuhet: “Thirrni zotat tuaj”! Ata i thërrasin, por ata nuk u përgjigjen dot, dhe shohin dënimin. E atëherë (do të dëshironin) sikur të kishin qenë në rrugën e drejtë (e të mos përjetonin dënimin).');
INSERT INTO "ahmeti" VALUES(28,65,'28:65','Dhe ditën kur (All-llahu) i thërret ata e u thotë: “Çfarë përgjigje u keni dhënë të dërguarve? “');
INSERT INTO "ahmeti" VALUES(28,66,'28:66','Atë ditë atyre u humbin faktet dhe ata nuk konsultohen ndërmjet vete.');
INSERT INTO "ahmeti" VALUES(28,67,'28:67','E për sa i përket atij që është penduar, që ka besuar dhe ka bërë vepra të mira, ai le të shpresojë se është nga të shpëtuarit.');
INSERT INTO "ahmeti" VALUES(28,68,'28:68','Zoti yt krijon çka të dojë dhe zgjedh kë të dojë, atyre nuk u takon zgjedhja. I pastër është i lartë është All-llahu nga çka i përshkruajnë për shok.');
INSERT INTO "ahmeti" VALUES(28,69,'28:69','Dhe Zoti yt e di se ç’fshehin zemrat e tyre dhe se ç’faqin hapatzi.');
INSERT INTO "ahmeti" VALUES(28,70,'28:70','Ai është All-llahu, nuk ka Zot tjetër pos Tij, vetëm Atij i takon falënderimi në këtë botë dhe në botën tjetër dhe vetëm ATij i takon gjykimi dhe te Ai ktheheni.');
INSERT INTO "ahmeti" VALUES(28,71,'28:71','Thuaj: “Sikur All-llahu t’a bënte natën të përhershme (t’ua zgjaste) deri në ditën e kijametit, ç’mendoni, coli zot pos All-llahut do t’ju sillte juve dritë? A nuk merrni vesh”');
INSERT INTO "ahmeti" VALUES(28,72,'28:72','Thuaj: “Më tregoni, nëse All-llahu ua bën ditën të vazhdueshme deri në ditën e kijametit, cili zot pos All-llahut do t’ju sjellë natë që të pushoni në të? A nuk shihni (sa po gaboni)?”');
INSERT INTO "ahmeti" VALUES(28,73,'28:73','Po mëshira e Tij, u bëri juve natën dhe ditën për të pushuar në të dhe për të përfituar nga begatitë e Tij, prandaj, të jini mirënjohës!.');
INSERT INTO "ahmeti" VALUES(28,74,'28:74','Dhe ditën që i fton (All-llahu) e, thotë: “Ku janë ata që i menduat shokë të Mi?”');
INSERT INTO "ahmeti" VALUES(28,75,'28:75','Dhe Ne do të nxjerrim prej çdo populli dëshmitarë e u themi: “Sillni argumentin tuaj!” ATëherë do ta kuptojnë se e drejta (për Zot) është vetëm e All-llahut, dhe dështon çdo trillim i tyre.');
INSERT INTO "ahmeti" VALUES(28,76,'28:76','Karuni ishte nga populli i Musait dhe ai shtypte atë popull ngase Ne i patëm dhënë aq shumë pasuri sa që një grup i fuqishëm mezi bartin çelësat e (pasurisë së) tij, e kur populli i vet i tha: “Mos u kreno aq fortë sepse All-llahu nuk i do të shfrenuarit!”');
INSERT INTO "ahmeti" VALUES(28,77,'28:77','Dhe me atë që të ka dhënë All-llahu, kërko (ta fitosh) botën tjetër, e mos le mangu atë që të takon nga kjo botë, dhe bën mirë ashtu siç të ka bërë All-llahu ty, e mos bën të këqia në tokë, se All-llahu nuk i do çrregulluesit.');
INSERT INTO "ahmeti" VALUES(28,78,'28:78','Ai (Karuni) tha: “Më është dhënë vetëm në saje të dijes sime!” Po a nuk e di ai se All-llahu shkatërroi para tij nga popujt e lashtë asish që ishte edhe më i fuqishëm e më i pasur se ai, po mëkatarët kriminelë as që do të pyeten për faje e tyre (meqë All-llahu e di).');
INSERT INTO "ahmeti" VALUES(28,79,'28:79','Ai (Karuni) doli para popullit të vet me stolinë e vet, e ata që kishin synim jetën e kësaj bote thanë: “ah, të kishim pasur edhe ne si i është dhënë Karunit, vërtet ai është fatbardhë!”');
INSERT INTO "ahmeti" VALUES(28,80,'28:80','E ata që ishin të zotët e diturisë thanë: “Të mjerët ju, shpërblimi i All-llahut është shumë më i mirë për atë që besoi dhe bëri vepër të mirë, po atë nuk mund ta arrijë kush përveç durimtarëve!”');
INSERT INTO "ahmeti" VALUES(28,81,'28:81','Po Ne atë dhe pallatin e tij i shafuam në tokë dhe, veç All-llahut nuk pati që ta mbrojë e as vetë nuk pati mundësi të mbrohet.');
INSERT INTO "ahmeti" VALUES(28,82,'28:82','E ata që dje lakmuan të ishin në vendim e tij, filluan të thonë: “A nuk shihni se All-llahu me të vërtetë i jep begati e komoditet atij që do nga robërit e vet, e edhe ia mungon atë atij që do, e sikur All-llahu të mos bënte mëshirë ndaj nesh, do të na sharronte në tokë edhe neve; sa habi, se si jobesimtarët nuk gjejnë shpëtim!”');
INSERT INTO "ahmeti" VALUES(28,83,'28:83','Atë, vend të përjetshëm (Xhennetin) u kemi përcaktuar atyre që nuk duan as mendjemadhësi e as ngatërresë në tokë, e përfundim i këndshëm u takon atyre që i frikësohen All-llahut.');
INSERT INTO "ahmeti" VALUES(28,84,'28:84','Kush sjell me vete punë të mirë, atij do t’i takojë shpërblimi edhe më i madh, ndërsa a që paraqitet me punë të këqija, do t’i jepe dënim vetëm aq sa e ka merituar.');
INSERT INTO "ahmeti" VALUES(28,85,'28:85','S’ka dyshim se Ai që ta bëri obligim Kur’anin, Ai do të kthejë ty aty ah erdhe. Thuaj: “Zoti im e di më mirë kush është që solli udhëzim të drejtë dhe kush është në humbje të sigurt”.');
INSERT INTO "ahmeti" VALUES(28,86,'28:86','Ti as nuk ke shpresuar se do të shpallet ty libri, por kjo është mëshirë e Zotit tënd, andaj ti kurrsesi të mos jeshë ndihmëtar i jobesimtarëve.');
INSERT INTO "ahmeti" VALUES(28,87,'28:87','Dhe kurrsesi ata të mos shmangin ty nga ajetet e All-llahut meqë ato t’u kanë shpallur ty, dhe ti thirr te Zoti yt dhe kurrsesi mos u bën pasues i dëshirave të idhujtarëve.');
INSERT INTO "ahmeti" VALUES(28,88,'28:88','Veç All-llahut mos adhuro ndonjë zot tjetër, s’ka të adhuruar tjetër veç Tij. Vetëm Atij i takon gjykimi dhe te Ai do të ktheheni!');
INSERT INTO "ahmeti" VALUES(29,1,'29:1','Elif, Lamë, Mimë.');
INSERT INTO "ahmeti" VALUES(29,2,'29:2','A menduan njerëzit të thonë: “Ne kemi besuar, e të mos vihen në sprovë?”');
INSERT INTO "ahmeti" VALUES(29,3,'29:3','Ne i sprovuam ata që shin para tyre, ashtu që All-llahu gjithqysh do t’i dallojë ata që e thanë të vërtetën do t’i dallojë dhe gënjeshtarët.');
INSERT INTO "ahmeti" VALUES(29,4,'29:4','A mos menduan ata që bëjnë vepra të këqija (krime) se do të na shpëtojnë (t’i ikin dënimeve)? Sa i shmtuar është gjykimi i tyre!');
INSERT INTO "ahmeti" VALUES(29,5,'29:5','E kush shpreson takimin (shpërblimin) e All-llahut, s’ka dyshim se afati i All-llahut do të vijë pa tjetër, e Ai është dëgjuesi i dijshmi.');
INSERT INTO "ahmeti" VALUES(29,6,'29:6','E kush lufton (përpiqet për të mirë), ai bën luftë për vete, se All-llahu vërtet nuk ka nevojë për askë në tërë botën.');
INSERT INTO "ahmeti" VALUES(29,7,'29:7','Po ata që besuan dhe bënë vepra të mira, Ne me siguri do t’ua shlyejmë të këqijat do t’i shpërblejmë më së miri për atë që vepruan.');
INSERT INTO "ahmeti" VALUES(29,8,'29:8','Ne e kemi obliguar njeriun me punë të mira ndaj prindërve të vet, e nëse ata të dy përpiqen të shpiejnë ty që të më pëshkruash Mua shok (zot tjetër) për çka ti nuk di asgjë, atëherë ti mos i respekto ata. Kthimi juaj është te Unë, e Unëdo t’ju njoftoj për atë që keni vepruar.');
INSERT INTO "ahmeti" VALUES(29,9,'29:9','E ata që besn e bënë vepra të mra, ata do t’i rradhisim ndër më të mirët.');
INSERT INTO "ahmeti" VALUES(29,10,'29:10','Ka disa njerëz që thonë: “Ne i kemi besuar All-llahut, e kur goditet me ndonjë të keqe për shkak të (rrugës së) All-llahut, si sprovimin prej njerëzve e trajton si dënimin e All-llahut (prej të cilit njerëzi ikin prej mosbesimit në besim), e nëse vjen ndonjë fitore prej Zotit tënd, ata do të thonë: “Ne kemi qenë me ju!” Po a nuk është Alahu që di më së miri për atë që është në zemrat e njerëzve?');
INSERT INTO "ahmeti" VALUES(29,11,'29:11','All-llahu do t’i vërë në pah ata që besuan dhe do t’i vërë në thumb hipokritët.');
INSERT INTO "ahmeti" VALUES(29,12,'29:12','Ata që nuk bsuan u thanë atyre që besuan: “Ejani në rrugën (në fenë) tonë, e ne po i bartim mëkatet tuaja (po qe se është mëkat rruga jonë). Po ata nuk do të bartin asgjë nga mëkatet e tyre, në të vërtetë ata janë gënjeshtarë.');
INSERT INTO "ahmeti" VALUES(29,13,'29:13','Ata pa tjetër do të bartin barrën e vet, e me atë veten edhe barrë tjera, e në ditën e kijametit do të merren në përgjegjësi për atë që kanë trilluar.');
INSERT INTO "ahmeti" VALUES(29,14,'29:14','Ne e dërguam Nuhun te populli i vet, e ai kaloi ndër ata njëmijë pa pesëdjetë vjet, e ata meqë ishin horra i përfshiu vërshimi.');
INSERT INTO "ahmeti" VALUES(29,15,'29:15','Ndërsa atë vetë dhe të tjerët që ishin në anije, i shpëtuam dhe Ne atë (ngjarje) e bëmë argument për tërë botën.');
INSERT INTO "ahmeti" VALUES(29,16,'29:16','Edhe Ibrahimin (e dërguam) kur ai popullit të vet i tha: “Adhuronie All-llahun dhe keni frikë prej Atij, se kjo, nëse e kuptoni, është shumë më mirë për ju.');
INSERT INTO "ahmeti" VALUES(29,17,'29:17','Ju në vend të All-llahut jeni duke adhuruar vetëm idhuj që i trilloni vetë si gënjeshtarë. S’ka dyshim se ata që i adhuroni në vend të All-llahut, nuk posedojnë furnizimin tuaj, pra kërkonie furnizimin te All-llahu, adhuronie Atë dhe shprehni falënderimin Atij, pse te Ai do të ktheheni”.');
INSERT INTO "ahmeti" VALUES(29,18,'29:18','Po nëse ju ërgënjeshtroni, edhe popuj para jush përgënjeshtruan, e të dërguarit nuk i takon tjetër pos komunikimi i kuptueshëm.');
INSERT INTO "ahmeti" VALUES(29,19,'29:19','A nuk e kanë vërejtur ata se si All-llahu nis krijimin, e pastaj atë e përsërit, e kjo është lehtë për All-llahun.');
INSERT INTO "ahmeti" VALUES(29,20,'29:20','Thuaj: “Udhëtoni nëpër tokë e shikoni se si filloi krijimi, pastaj All-llahu e fillon krijesën tjetër (ringjalljen). Është e vërtet se All-llahu ka mundësi për çdo send.');
INSERT INTO "ahmeti" VALUES(29,21,'29:21','Ai ndëshkon atë që do dhe mshiron kë të dojë dhe te Ai do të silleni.');
INSERT INTO "ahmeti" VALUES(29,22,'29:22','Ju nuk keni mundësi ta bëni të paaftë (Zotin tuaj t’ju dënojë) as në tokë e as në qiell, dhe përpos All-llahut, nuk keni as mbrojtës as ndihmëtar”.');
INSERT INTO "ahmeti" VALUES(29,23,'29:23','E ata që nuk besuan argumentet e All-llahut dhe takimin me Të, të tillët kanë humbur shpresën nga mëshira Ime dhe aa i pret dënimi i hidhur.');
INSERT INTO "ahmeti" VALUES(29,24,'29:24','Po përgjegjja e popullit të tij nuk ishte tjetër vetëm të thotë: “Mbytnie atë (Ibrahimin) ose digjnie!” Mirëpo, All-llahu e shpëtoi atë prej zjarrit. Pë një popull që beson, këtu vërtet ka fakte bindëse.');
INSERT INTO "ahmeti" VALUES(29,25,'29:25','Ai (Ibrahimi) tha: “Ju përqafuat, veç All-llahut, idhuj vetëm sa për bashkëjetesë mes jush në jetën e kësaj bote, e në ditën e kijametit (ndryshon gjendja) ju do të refuzoni njëri-tjetrin dhe do të mallkoni njëri-tjetrin; fundi juaj është zjarri, për ju nuk do të ketë ndihmëtarë”.');
INSERT INTO "ahmeti" VALUES(29,26,'29:26','Atij (Ibrahimit) i besoi Luti, e ai (Ibrahimi) tha: “Unë shpërngulem atje ku më udhëzoi Zoti im, Ai është i gjithfuqishëmi, i urti”.');
INSERT INTO "ahmeti" VALUES(29,27,'29:27','Dhe Ne i dhuruam atij Is-hakun dhe Jakubin, e në pasardhësit e tij shpallëm pejgamberllëkun dhe librin e shenjtë (librat e shpallura), atij i dhamë shpërblimin në këtë botë, kuse në botëm tjetër do të jetë ndër të mirët e përsosur.');
INSERT INTO "ahmeti" VALUES(29,28,'29:28','(Dërguam) Edhe Lutin, i cili popullit të vet i tha: “Ju, bëni aq punë të shëmtuar sa askush para jush nga tërë bota nuk e ka bërë”.');
INSERT INTO "ahmeti" VALUES(29,29,'29:29','Ju jeni që kontaktoni meshkujt, që sulmoni njerëzit në rrugë, që në vendtubimet tuaja bëni punë të ndyta. Përgjegjja e popullit të tij nuk ishte tjetër vetëm të thonë: “Nëse e thua të vërtetën, na silldënimin nga All-llahu?”');
INSERT INTO "ahmeti" VALUES(29,30,'29:30','Ai tha: “O Zoti im, më ndihmo kundër popullit të prishur!”');
INSERT INTO "ahmeti" VALUES(29,31,'29:31','E kur të dërguarit tanë (engjëjt) i erdhën me myzhde Ibrahimit, i thanë: “Ne jemi shkatërrues të banorëve të këtij qyteti, pse banorët e tij janë mizorë!”');
INSERT INTO "ahmeti" VALUES(29,32,'29:32','Ai (Ibrahimi) tha: “Aty është Luti!” Ata i thanë: “Ne e dimë edhe më mirë se kush është aty, atë dhe familjen e tij do ta shpëtojmë përveç gruas së tij, ajo do të mbetet me të zhdukurit”.');
INSERT INTO "ahmeti" VALUES(29,33,'29:33','E pasi të dërguarit tonë i erdhën Lutit, ai për shkak të tyre u shqetësua dhe u ngushtua, po ata i thanë: “Mos ke frikë, as ms u pikëllo, ne të shpëtojmë ty dhe familjen tënde, përveç gruas sate që do të mbetet me të dënuarit”.');
INSERT INTO "ahmeti" VALUES(29,34,'29:34','Ne mbi banorët e këtij qyteti do të lëshojmë një dënim nga qielli për shkak se ata bëjnë punë të liga.');
INSERT INTO "ahmeti" VALUES(29,35,'29:35','E nga ai vend Ne kemi lënë gjurmë për një popull që mendon.');
INSERT INTO "ahmeti" VALUES(29,36,'29:36','E në Medjen (dërguam) vëllaun e tyre Shuajbin dhe si u tha: “O populli im, adhuronie vetëm All-llahun dhe kinie rikën ditën e fundit, e mos e teproni si shkatërrues në tokë!”');
INSERT INTO "ahmeti" VALUES(29,37,'29:37','Po ata e përgenjështruan, andaj i përfshiu tërmeti dhe aguan të shtangur (të ftohur e të vdekur) në shtëpitë e tyre.');
INSERT INTO "ahmeti" VALUES(29,38,'29:38','(I shkatërruam) Edhe Adin e Themudin, e venbanimet e tyre janë të qarta për ju. Djalli ua pat zbukuruarveprat e tyre dke i shmangur nga e vërteta, edhe pse e shihni atë.');
INSERT INTO "ahmeti" VALUES(29,39,'29:39','Edhe Karunin, faronin dhe Hamanin. Atyre Musai u solli fakte por ata treguan mendjemadhësi, ndaj nuk mundën t’i shptojnë dënimit.');
INSERT INTO "ahmeti" VALUES(29,40,'29:40','Secilin prej tyre e kemi dënuar për shkak të mëkatit të vet; disa prej tyre Ne i goditëm me furtunë plot rërë, disa i shkatërruam me krismë nga qielli, kurse disa të tjerë i sharrum në tokë dhe disa përmbytëm në ujë. All-llahu nuk u bëri atyre ndonjë padrejt, por ata vetës së tyre i bënë padrejtë.');
INSERT INTO "ahmeti" VALUES(29,41,'29:41','Shembulli i atyre, të cilët veç All-llahut morën mbrojtës (zota idhujsh), është si shembulli i marimangës që thur shtëpi, e sikur ta dinin ata, më e dobëta shtëpi është e shtëpia e marimangës.');
INSERT INTO "ahmeti" VALUES(29,42,'29:42','All-llahu e di se ç’adhurojnë ata pos Tij (ose nuk adhurojnë asgjë), Ai është ngadhënjyesi, i urti..');
INSERT INTO "ahmeti" VALUES(29,43,'29:43','Këta janë shembuj që Ne ua sjellim njerëzve, po këta nuk i kupton kush pos dijetarëve.');
INSERT INTO "ahmeti" VALUES(29,44,'29:44','All-llahu krijoi qiejt dhe tokën me një serozitet të caktuar. Në to më të vërtetë ka fakte për besimtarët.');
INSERT INTO "ahmeti" VALUES(29,45,'29:45','Ti lexo atë që po të shpallet nga libri (Kur’ani), fal namazin, vërtet namazi largon nga të shëmtuarat dhe të irituarat, e përmendja e All-llahut është më e madhja (e adhurimeve); Allhu e di çpunoni ju.');
INSERT INTO "ahmeti" VALUES(29,46,'29:46','Ithtarët e librit mos i polemizoni ndryshe (kurr i thirrni në fenë tuaj) vetëm ashtu si është më së miri, përveç atyre që janë mizorë dhe thuajuni: “Ne i besuam asaj që na u zbrit dhe që u zbrit juve dhe se Zoti ynë dhe Zoti juaj është një, dhe se ne i jemi dorëzuar Atij.');
INSERT INTO "ahmeti" VALUES(29,47,'29:47','Dhe po ashtu (siç i zbritëm librat e mëparshëm), Ne ta zbritëm ty librin. E ata, të cilëve Ne u kemi dhënë librin, i besojmë këtij (Kur’anit) edhe nga ata (idhujtarët arabë) ka që i besojnë këtij, kurse argumentet tona nuk i refuzon askush, përpos jobesimtarëve”.');
INSERT INTO "ahmeti" VALUES(29,48,'29:48','Ti (Muhammed) nuk ishe që lexpn ndonjë libër para këtij, e as që shkruajshe atë me dorën tënde të djathtë, pse atëherë do të dshonin ata të prishurit.');
INSERT INTO "ahmeti" VALUES(29,49,'29:49','Por jo ai është plot argumente të qarta në zenrat e atyre që u është dhënë dituria, e argumentet Tona nuk i mohon kush pos të shfrenuarëve.');
INSERT INTO "ahmeti" VALUES(29,50,'29:50','Ata thanë: “Përse të mos i ketë zbritur atij një mrekulli nga Zoti i tij?” thuaj: “çështja e mrekullive është vetëm në duar të All-llahut, e unë nuk jam tjetër pos tërheqës i vërejtjes dhe shpjeues.');
INSERT INTO "ahmeti" VALUES(29,51,'29:51','Po a nuk u mjafoti atyre që Ne ta shpallëm librin, icili u lexohet atyre, e s’ka dyshim se kjo është dhuratë e përkujtim për njerëz që duan të besojnë”.');
INSERT INTO "ahmeti" VALUES(29,52,'29:52','Thuaj: “Mjafton që All-llahu është dëshmitar ndërmjet meje dhe juve, Ai e di ç’ka në qiej e në tokë, e ata që i besuan të kotës e nuk e besuan All-llahun, ata janë të dështuarët”.');
INSERT INTO "ahmeti" VALUES(29,53,'29:53','Ata kërkojnë prej teje ngutjen e ndëshkimit, e sikur të mos ishte afati i caktuar, atyre do t’u vinte ndëshkimi, po ai patjetër do t’u vijë atyre befas ashtu që ata nuk e hetojnë.');
INSERT INTO "ahmeti" VALUES(29,54,'29:54','Po, si e kërkojnë ata ngutjen e ndëshkimit? E në të vërtetë Xhehennemi është që i ka rrethuar në të gjitha anët jobesimtarët.');
INSERT INTO "ahmeti" VALUES(29,55,'29:55','Ditën kur ata i mbulon dënimi (i zjarrit) prej së larti dhe së poshtmi nën këmbët e tyre, e u thonë: “Shijone atë që keni vepruar!”');
INSERT INTO "ahmeti" VALUES(29,56,'29:56','O robët e Mi që besuat, toka Ime është e gjërë, pra vetëm Mua më adhuroni.');
INSERT INTO "ahmeti" VALUES(29,57,'29:57','Secili njeri do të shijojë vdekjen, e pastaj do të ktheheni te Ne.');
INSERT INTO "ahmeti" VALUES(29,58,'29:58','E ata që besuan dhe bënë vepra të mira, atyre do t’u bëjmë vend të lartë në Xhennet në të cilin rrjedhin lumenj, aty janë përgjithmonë; sa i mirë është shpërblimi për ata që vepruan.');
INSERT INTO "ahmeti" VALUES(29,59,'29:59','Të cilët benë durim dhe vetëm Zotit të tyre iu mbështetën.');
INSERT INTO "ahmeti" VALUES(29,60,'29:60','Sa e sa gjallesa janë që nuk kanë mundësi për furnizimin e vet. All-llahu i furnizon ato ehe juve. Ai është dëgjuesi, i dijshmi.');
INSERT INTO "ahmeti" VALUES(29,61,'29:61','Nëse ti i pyet ata: “Kush krijoi qiejt e tokën, kush i nënshtroi (të lëvizin) diellin dhe hënën?” Ata do të thonë: “All-llahu!” E si prapsoheni, pra?');
INSERT INTO "ahmeti" VALUES(29,62,'29:62','All-llahu i jep furnizim atij që do nga robërit e vet, e ia pakëson (shtrëngon) aitj që do. Vërtet, All-llahu është i gjithdijshëm për çdo gjë.');
INSERT INTO "ahmeti" VALUES(29,63,'29:63','Dhe nëse ti pyet ata: “Kush e lëshon shiun nga qielli dhe me të e ngjall tokën pas vdekjes së saj?” Ata thonë: “All-llahu!” Thuaj: “Falënderimi i qoftë All-llahut, por shumica e tyre nuk mendojnë”.');
INSERT INTO "ahmeti" VALUES(29,64,'29:64','Kjo jetë e kësaj bote nuk është tjetër vetëm se dëfrim e lojë, e jetë e vërtetë, padyshim është ajo e botës së ardhme (Ahireti), sikur ta dinin.');
INSERT INTO "ahmeti" VALUES(29,65,'29:65','(Idhujtarët e dinë për All-llahun pse) Kur hipni ata në anije (u këputet lidhja me tokë, dhe frikësohen) i luten sinqerisht All-llahut, e kur i shpëton ata (dhe dalin) në tokë, qe, po ata të njejtit lusin idhuj!');
INSERT INTO "ahmeti" VALUES(29,66,'29:66','Po le të përbuzin ato të mira që ua dhamë dhe le të kënaqen, (e mos të besojnë), se më vonë do ta kuptojnë.');
INSERT INTO "ahmeti" VALUES(29,67,'29:67','A nuk e kanë vërejtur ata se Ne kemi bërë vendin e shenjtë (Mekën) të sigurt, e njerëzit për rreth tyre rrëmbehen (plaçkiten, mbyten). Po, a i besojnë ata të kotës, e të mirat e All-llahut i përbuzin?');
INSERT INTO "ahmeti" VALUES(29,68,'29:68','E kush është më mizor se ai që shpif gënjeshtër ndaj All-llahut, ose përgënjeshtron të vërtetën pasi t’i ketë ardhur ajo? Po, a nuk është Xhennemit vend për jobesimtarët?');
INSERT INTO "ahmeti" VALUES(29,69,'29:69','E ata, të cilët luftuan për hir Tonë, Ne, me siguri do t’i orientojmë rrugës për te Ne, e nuk ka dyshim se All-llahu është në krahun e besimtarëve.');
INSERT INTO "ahmeti" VALUES(30,1,'30:1','Elif, Lamë, Mimë.');
INSERT INTO "ahmeti" VALUES(30,2,'30:2','Bizantinët (rumët) u mundën,');
INSERT INTO "ahmeti" VALUES(30,3,'30:3','në tokën më afër (tokës arabe), po pas disfatës së tyre, ata do të ngadhënjejnë,');
INSERT INTO "ahmeti" VALUES(30,4,'30:4','brenda pak viteve. Çështja është vendim i All-llahut, fillim e mbarim (për disfatë dhe për fitore). E atë ditë (kur do të fitojnë bizantinët) besimtarët do të gëzohen,');
INSERT INTO "ahmeti" VALUES(30,5,'30:5','për ndihmën e All-llahut. Ai ndihmon atë që do dhe Ai është i gjithëfuqishëm, mëshirues.');
INSERT INTO "ahmeti" VALUES(30,6,'30:6','Premtimi i All-llahut është (ky), All-llahu nuk e thyen premtimin e Vet, por shumica e njerëzve nuk po dinë.');
INSERT INTO "ahmeti" VALUES(30,7,'30:7','Edhe atë që dinë nga jeta e kësaj bote, është dije e cekë, por ndaj jetës së përjetshme (ndaj Ahiretit) ata janë plotësisht të verbëruar (të painteresuar).');
INSERT INTO "ahmeti" VALUES(30,8,'30:8','Dhe, a nk menduan ata me vetën e tyre se All-llahu nuk krijoi qiejt dhe tokën dhe çdo gjë që gjendet ndërmjet tyre pa ndonjë qëllim të caktuar dhe për një kohë të kufizuar por një pjesë e njerëzve janë mohues të takimit me Zotin e tyre (në momentin e ringjalljes pas vdekjes).');
INSERT INTO "ahmeti" VALUES(30,9,'30:9','A nuk udhëtuan ata nëpër tokë e të shohin se si qe përfundimi i atyre që ishin para tyre!?? Ata ishin edhe më të fortë se këta, ata e lëruan tokën dhe e rindërtuan atë më shumë se sa këa, e rindërtuan, atyre u patën ardhur të dërguarit e vet me fakte të qarta. Pra, Alahu nuk ishte që t’ju bëjë të padrejtë atyre, por ata vetvetes i bënë të padrejtë.');
INSERT INTO "ahmeti" VALUES(30,10,'30:10','Mandej përfundimi i atyre që bënë punë të këqia, ishte më i keqi, për shkak se argumentet e All-llahut i përgënjeshtruan dhe talleshin me to.');
INSERT INTO "ahmeti" VALUES(30,11,'30:11','All-llahu e fillon krijimin, mandej e përsërit atë, e më në fund te Ai do të ktheheni.');
INSERT INTO "ahmeti" VALUES(30,12,'30:12','E ditën kur të ndodh kijameti, kriminelët heshtin.');
INSERT INTO "ahmeti" VALUES(30,13,'30:13','Ata nuk do të kenë ndërmjetësues (ndihmëtarë) prej shokëve (zotave) të tyre, e me shokët (zotat) e tyre ishin jobesimtarë.');
INSERT INTO "ahmeti" VALUES(30,14,'30:14','Dhe ditën kur të ndodhë kijameti, atë ditë do të ndahen (njerëzit).');
INSERT INTO "ahmeti" VALUES(30,15,'30:15','E ata, të cilët besuan dhe bënë vepra të mira, ata d të jenë të gëzuar në një kopshtë (të Xhennetit).');
INSERT INTO "ahmeti" VALUES(30,16,'30:16','E për sa u përket atyre që nuk besuan, dhe i bënë të rreme argumentet Tona dhe takimin në botën tjetër, ata do të kenë mundime (azab) të vazhdueshme.');
INSERT INTO "ahmeti" VALUES(30,17,'30:17','Atëherë, All-llahun lartësonie kur të vijë mbrëmja dhe kur të agojë mëngjesi.');
INSERT INTO "ahmeti" VALUES(30,18,'30:18','Falënderimi i takon vetëm Atij prej gjithçka ka në qiej e në tokë, edhe në mbrëmje edhe ditën.');
INSERT INTO "ahmeti" VALUES(30,19,'30:19','Ai krijon të gjallin prej të vdekurit dhe të vdekurin prej të gjallit, Ai e ngjall tokën pas vdekjes së saj, e po ashtu edhe ju do të nxirreni (të gjallë prej varrezave).');
INSERT INTO "ahmeti" VALUES(30,20,'30:20','Nga faktet (e fuqisë) e Tij është edhe ajo, se Ai ju krijoi prej dheu, e mandej ju (u zhvilluat në) njerëz që veproni të shpërndarë.');
INSERT INTO "ahmeti" VALUES(30,21,'30:21','Dhe nga faktet (e madhërisë së) e Tij është që për të mirën tuaj, Ai krijoi nga vetë lloji juaj palën (gratë), ashtu që të gjeni prehje te ato dhe në mes jush krijoi dashuri dhe mëshirë. Në këtë ka argumente për njerëzit që mendojnë.');
INSERT INTO "ahmeti" VALUES(30,22,'30:22','Nga argumentet e Tij është krijim i qiejve e i tokës, ndryshimi i gjuhëve tuaja dhe i ngjyrave tuaja. Edhe në këtë ka argumente për njerëz.');
INSERT INTO "ahmeti" VALUES(30,23,'30:23','Nga argumentet e Tij është edhe gjumi juaj natën dhe ditën, edhe përpjekja juaj për të fituar nga të mirat e Tij. Në këtë ka argumente për popullin që dëgjon.');
INSERT INTO "ahmeti" VALUES(30,24,'30:24','Nga argumentet e Tij është edhe ajo që ua dëfton vetëtimën edhe frikë e dhe si shpresë, dhe nga qielli lëshon shi e me të e ngjall tokën pas vdekjes së saj. Në këtë ka argumente për një popull që di të mendojë.');
INSERT INTO "ahmeti" VALUES(30,25,'30:25','Nga argumentet e Tij është që me fuqinë e Tij bëri të qëndrojë (pa shtyllë) qielli e Toka, mandej kur t’ju thërret juve me një thirrje prej tokës, ju menjëherë dilni.');
INSERT INTO "ahmeti" VALUES(30,26,'30:26','E Tij është çka në qiej e në tokë dhe të gjithë vetëm Atij i përulën.');
INSERT INTO "ahmeti" VALUES(30,27,'30:27','Dhe Ai është që e shpik jetën dhe pastaje përsërit atë, e ajo (përsëritja) është edhe më e lehtë oër Te (sipas mendimit tuaj). Shembëlltyra më e lartë është vetëm e Tij në qie tokë dhe Ai është mbizotëruesi, i urti.');
INSERT INTO "ahmeti" VALUES(30,28,'30:28','Ai (Zoti) ju jep një shembull nga vetë ju; a i keni shokë (ortakë) ata që janë në posedim tuaj (shërbëtorët), në atë që Ne u dhamë juve (në pronësi), që të jeni ju të barabartë në të, (në përdorimin e saj) e të frikësoheni prej tyre (se po marrin kompetencë) si frikësoheni ju në mes vete. (Ju nuk pranoni ortakë shërbëtorët tuaj, të jeni të barabartë, nuk keni frikën e tyre, e si më përshkruani Mua ortakë)? Ne kështu u sqarojmë faktet njerëzve që janë të zotët e mendjes.');
INSERT INTO "ahmeti" VALUES(30,29,'30:29','Jo (ata nuk kanë fakt), por ata që i bënë të padrejtë vetes, të cilët pa ndonjë fakt të dijes shkuan pas epshit të vet. Po kush mund ta vërë në rrugë të drejtë atë që All-llahu e ka lënë të humbur, e ata nuk do të kenë ndihmëtarë.');
INSERT INTO "ahmeti" VALUES(30,30,'30:30','Përqënrohu në vetëvetën tënde sinqerisht në fenë, ilrguar prej çdo të kote, (e ajo fe), feja e All-llahut në të cilën i krijoi njerëzit, s’ka ndryshim (mos ndryshoni) të asaj natyrshmërie të krijuar nga All-llahu, ajo është feja e drejtë por shumica e njerëzve nuk e dinë.');
INSERT INTO "ahmeti" VALUES(30,31,'30:31','(Përqëndrohu në atë fe) Të kthyer sinqerish te Ai, kini frikë prej Atij dhe falnie namazin e mos u bëni nga idhujtarët,');
INSERT INTO "ahmeti" VALUES(30,32,'30:32','Të cilët e përqanë fenë e tyre dhe u ndanë në grupe, ku secili grup i gëzohet idesë së vet.');
INSERT INTO "ahmeti" VALUES(30,33,'30:33','Kur njerëzit i prek ndonjë e keqe, ata me përulje i luten Zotit të tyre, duke iu drejtuar vetëm Atij, e kur AI nga të mirat e veta u shijon atyre, ja, që një grup prej tyre i përshkruajnë shok Zotit të vet.');
INSERT INTO "ahmeti" VALUES(30,34,'30:34','Le të mohojnë atë që Ne u kemi dhënë, pra kënaquni, e më vonë do ta kuptoni.');
INSERT INTO "ahmeti" VALUES(30,35,'30:35','A mos Ne u kemi shpallur atyre ndonjë argument, që ai vërteton (flet) për atë që ata Mua më përshkruajnë shok?');
INSERT INTO "ahmeti" VALUES(30,36,'30:36','E kur Ne u japim njerëzve begati të mira, ata u gëzohen atyre, e nëse ata i godit ndonjë e keqe për shkak të veprimit të tyre, atëherë ata e humbin shpresën.');
INSERT INTO "ahmeti" VALUES(30,37,'30:37','Vallë, po a nuk shohin ata se All-llahu ijep furnizim të mjaftueshëm atij që do Ai dhe ia pakëson. Në këtë, për një popull që beson, Ka argument.');
INSERT INTO "ahmeti" VALUES(30,38,'30:38','Andaj, jepja të afërmit atë që i takon, e ehe te varfërit edhe udhëtarit, e kjo është shumë më e dobishme për ata që vepronë për hirë të All-llahut dhe të tillët janë ata të shpëtuarit.');
INSERT INTO "ahmeti" VALUES(30,39,'30:39','Dhe çka të jepni nga pasuria për të shtuar e rritur (me kamatë), në pasurinë e njerëzve, ajo nuk do të shtohet te All-llahu, e çka të jepni nga sadakaja (bamirësia) me qëllim që t’i afroheni All-llahut, të tillët janë ata që u shumfishohet (shpërblimi).');
INSERT INTO "ahmeti" VALUES(30,40,'30:40','All-llahu është Ai që ju jrijoi, që ju furnizoi, që ju bën të vdisni e pastaj ju ngjall. A ka ndonjë prej zotave tuaj që mund të bëjë diçka nga këto? I pastër është AI i pa të meta, i madhëruar është Ai nga çka ata ia mveshin dhe trillojën.');
INSERT INTO "ahmeti" VALUES(30,41,'30:41','Për shkak të veprave (të këqia) të njerëzve, janë shaqur në tokë e në det të zeza (bela, skamje, katastrofa, humbje e bereqetit etj.), e për ta përjetuar ata një pjesë të kësaj të keqeje që e bënë, ashtu që tërhiqen (nga të këqijat).');
INSERT INTO "ahmeti" VALUES(30,42,'30:42','Thuaj: “Ecni nëpër tokë dhe shikoni s si ishte përfundimi i atyre që ishin më parë, shumica e tyre iashin jobesimtarë”.');
INSERT INTO "ahmeti" VALUES(30,43,'30:43','Ti përmbaju fuqimisht fesë së drejtë, para se të vijë dita që nuk ka kthim, pse prej All-llahut është caktuar, atë ditë ata (njerëzit) ndahen.');
INSERT INTO "ahmeti" VALUES(30,44,'30:44','Kush nuk besoi, barrën e mosbesimit të vet e ka kundër vetes, e kush e bëri punë të mirë, ata vetvetes i përgaditë vendin (Xhennetin).');
INSERT INTO "ahmeti" VALUES(30,45,'30:45','QË t’i shpërblejë Ai (All-llahu) nga të mirat e veta, ata që besuan dhe bënë vepra të mira, vërtet, Ai nuk i do jobesimtarët.');
INSERT INTO "ahmeti" VALUES(30,46,'30:46','Nga argumentet e Tij është edhe: Që Ai t’i lëshojë erërat myzhdexhinj dhe t’u shijojë juve nga meshira e vet (shiun); dhe të lundrojë anijet me vullnetin e Tij, dhe që të përfitoni nga të mirat e Tij dhe ashtu, ju jeni mirënjohës.');
INSERT INTO "ahmeti" VALUES(30,47,'30:47','Ne edhe para teje popujve të tyre u dërguam pejgamberë dhe ata u erdhën atyre me argumente të qarta, e kundër atyre që bën krim, Ne ndërmorëm masa mdëshkuese. Obligimi yni ishte të ndihmojmë besimtarët.');
INSERT INTO "ahmeti" VALUES(30,48,'30:48','All-llahu është Ai që i lëshon erërat, e ato lëkundin retë dhe Ai i shtrinë lart si të dojë e i bën ato edhe të ndara në pjesë, dhe përmes tyre e sheh se si bie shi, e kur ia lëshon atë kujt të dojë prej robërve të vet, qe, ata gëzohen.');
INSERT INTO "ahmeti" VALUES(30,49,'30:49','Edhe para se t’u binte (shiu) atyre, ata më para tij ishin të dëshpruar.');
INSERT INTO "ahmeti" VALUES(30,50,'30:50','E sikur Ne të lëshojmë një erë e ta shohin atë (të mbjellat) e zverdhura, pa dyshim ata pas kësaj do të jenë mohues (përbuzës).');
INSERT INTO "ahmeti" VALUES(30,51,'30:51','E sikur Ne të lëshojmë një erë e ta shohin atë (të mbjellat) e zverdhura, pa dyshim ata pas kësaj do të jenë mohues (përbuzës).');
INSERT INTO "ahmeti" VALUES(30,52,'30:52','Në të vërtetë, ti (Muhammed) nuk mund t’i bësh të dëgjojnë të vdekurit e as nuk mund ta bësh që të shurdhëti të dëgjojnë thirrjen, kur ata kthejnë shpinën.');
INSERT INTO "ahmeti" VALUES(30,53,'30:53','Dhe as që je ti udhëzues i rë verbërve nga humbja e tyre, ti nuk mund ta bësh të dëgjojë tjetër vetëm atë që u beson argumenteve Tona, se ata janë të bindur.');
INSERT INTO "ahmeti" VALUES(30,54,'30:54','All-llahu është Ai që ju krijoi në gjendje të dobët, pastaj pas asaj dobësie ju dha fuqi, e pas fuqisë, dobësi e pleqëri. Ai krijon çka të dojë, Ai është më i dijshmi, më i fuqishmi.');
INSERT INTO "ahmeti" VALUES(30,55,'30:55','e ditën kur të bëhet kijameti (ditën e gjykimit), kriminelët betohen se nuk qëndruan (në dynja) gjatë, por vetëm një çast të shkurtër. Kështu kanë qenë ata që i bishtëruan të së vërtetës.');
INSERT INTO "ahmeti" VALUES(30,56,'30:56','E atyre që iu është dhënë dija dhe besimi thonë: “Ju keni qëndruar aq sa u pat caktuar All-llahu deri në ditën e ringjalljes, e kjo pra është dita e ringjalljes, por ju ishit që nuk e pranonit!”');
INSERT INTO "ahmeti" VALUES(30,57,'30:57','E ata ditë për ata që bënë zullum, arsyetimi nuk u bën dobi e as që kërkohet prej tyre të justifikohen.');
INSERT INTO "ahmeti" VALUES(30,58,'30:58','Ne në këtë Kur’an u kemi sjellë të gjithë shembujt, po pasha All-llahun, edhe sikur t’u sillje çfarëdo mrekullie ti atyre, ata që nuk besuan do të thoin: Ju (Muhammedi me besimtarë) nuk jeni veçse mashtrues (gënjeshtarë).');
INSERT INTO "ahmeti" VALUES(30,59,'30:59','Në këtë mënyrë All-llahu vulos zemrat e atyre që nuk kuptojnë.');
INSERT INTO "ahmeti" VALUES(30,60,'30:60','Ti pra bën durim, pse premtimi i All-llahut është i saktë, e kurrsesi të mos luhasin ty ata që janë të dyshimtë.');
INSERT INTO "ahmeti" VALUES(31,1,'31:1','Elif, Lamë, Mimë.');
INSERT INTO "ahmeti" VALUES(31,2,'31:2','Ato janë ajetet të librit me plot urtësi të përsosur.');
INSERT INTO "ahmeti" VALUES(31,3,'31:3','E që janë udhëzim e mëshirë për ata që janë punëmirë.');
INSERT INTO "ahmeti" VALUES(31,4,'31:4','Të cilët rregullisht e falin namazin dhe japin zeqatin dhe ata, e mu ata janë të plotbindur për botën tjetër (Ahiretin).');
INSERT INTO "ahmeti" VALUES(31,5,'31:5','Të tillët janë të udhëzuar në një rrugë të qartë prej Zotit të vet dhe janë fatlumë.');
INSERT INTO "ahmeti" VALUES(31,6,'31:6','Po ka nga njerëzit që blenë tregime boshe e më qëllim që t’i largojnë njerëzit prej rrugës së All-llahut pa pasur kurrfarë fakti dhe për t’i marrë ato (ajetet e) All-llahut si tallje. Për ta me siguri është përgaditur dënim i turpshëm.');
INSERT INTO "ahmeti" VALUES(31,7,'31:7','E kur lexohen atij ajetet Tona, ai kthehet kryelartë dhe bëhet sikur nuk i ka dëgjuar ato, bëhet sikur në veshët e tij ka shurdhim të rëndë, pra, ti përgëzoje atë më një dënim të dhëmbshëm.');
INSERT INTO "ahmeti" VALUES(31,8,'31:8','E ata që besuan dhe bënë vepra të mira, për ata janë Xhennetet e begatshme,');
INSERT INTO "ahmeti" VALUES(31,9,'31:9','që në to janë përgjithmonë. Premtimi i All-llahut i prerë, e Ai është i gjithfuqishmi, i urti.');
INSERT INTO "ahmeti" VALUES(31,10,'31:10','Ai i krijoi qiejt pa shtylla, sikurse po i shihni e në tokë vendosi kodra të rënda që të mos luajë vendi bashkë me ju e nëpër të shpërndau nga të gjitha gjallesat, dhe nga qielli Ne kemi lëshuar shi e kemi bërë të mbijnë në të nga të gjitha llojet më të dobishme.');
INSERT INTO "ahmeti" VALUES(31,11,'31:11','Kjo është vepër e krijimit të All-llahut, e me trgoni pra mua se ç’krijuan ata të tjerët pos Tij. jo. asgjë, pra, idhujtarët janë në një humbje të pafund.');
INSERT INTO "ahmeti" VALUES(31,12,'31:12','Ne i patëm dhënë Llukmanit mençuri të përsosur (e i thamë): Të falënderosh All-llahun, e kush falënderon, e Mira e ati falënderimi i takon atij, e kush refuzon (edhe ai e ka për vete), në të vërtet, All-llahu nuk ka nevojë (për falënderimin e tij) pse Ai vetë është i lavdishëm.');
INSERT INTO "ahmeti" VALUES(31,13,'31:13','(prkujtoju popullit tënd) Kur Llukmani duke e këshilluar, birit të vetr i tha: “O djali im, mos i përshkruaj All-llahut shok, sepse idhujtaria është padrejtësi më e madhe!”');
INSERT INTO "ahmeti" VALUES(31,14,'31:14','Ne njeriun e kemi urdhëruar për (sjellje të mira ndaj) prindërit të vet, sepse nëna e vet atë e barti me mund pas mundi dhe pas dy viteve ia ndau gjinin. (e porositëm) Të jeshë mirënjohës ndaj Meje dhe ndaj dy prindërve tu, pse vetëm te Unë kthimi juaj.');
INSERT INTO "ahmeti" VALUES(31,15,'31:15','E nse ata të dy tentojnë ë ti të më përshkruash Mua shok, për çka ti nuk ke kurrfarë fakti, atëherë mos i respekto ata, o në çështjet e jetës së kësaj bote të keshë mirëkuptim ndaj tyre, e ti ndiqe rrugën e atij që është i kthyer kah Unë, mande kthimi juaj është te Unë, e Unë do t’ju njoftoj për atë që keni punuar.');
INSERT INTO "ahmeti" VALUES(31,16,'31:16','O djali im, s’ka dyshim se edhe ajo (vepra) peshon sa kokrra e lirit, e të jetë e fshehurnë rrasë guri, ose në qiej apo në tokë, All-llahu do ta sjellë atë, se All-llahu është i butë dhe hollësisht i informuar.');
INSERT INTO "ahmeti" VALUES(31,17,'31:17','O djali im, fale namazin, urdhëro për punë të mira, e ndalo nga të këqiat, përballo me durim çdo gjë që të godet, vërtet, këto janë nga çështjet më të preferuara.');
INSERT INTO "ahmeti" VALUES(31,18,'31:18','Dhe mos shtrembëro fytyrën tënde prej njerëzve, mos ec nëpër tokë kryelartë, se All-llahu nuk e do asnjë mendjemadh e që shumë lavdërohet.');
INSERT INTO "ahmeti" VALUES(31,19,'31:19','Të jesh i matur në ecjen tënde, ule zërin ënd, se zëri më i egër është zëri i gomarit.');
INSERT INTO "ahmeti" VALUES(31,20,'31:20','A nuk e dini j se All-llahu nënshtroi për të mirën tuaj çka në qiej e çka në tokë dhe plotësoi ndaj jush të mirat e Tij të dukshmen e të fshehta, e megjithatë prej njerëzve ka që polemizojnë çështjen e All-llahut duke mos pasur as dituri, duke mos qenë i udhëzuar dhe duke mos pasur libër të ndritshëm.');
INSERT INTO "ahmeti" VALUES(31,21,'31:21','Dhe kur atyre u thuhet: “Pasoni atë që e shpall All-llahu” ata thonë: “Jo, ne ndjekim atë që gjetëm te prindërit tanë!” E edhe sikur t’i kishte thirrur ata në dënimin e zjarrtë!');
INSERT INTO "ahmeti" VALUES(31,22,'31:22','E kush ia dorëzon veten All-llahut duke qenë edhe punëmirë, ai është kapur për lidhjen më të sigurt dhe vetëm te All-llahu është përfundimi i çështjeve.');
INSERT INTO "ahmeti" VALUES(31,23,'31:23','Ty mos të brengos mosbesimi i atij që nuk beson, kthimi i tyre është vetëm te Ne, e për atë që vepruan, Ne do t’u tregojmë. All-llahu vërtet është i dijshëm për atë që mbajnë në zemra.');
INSERT INTO "ahmeti" VALUES(31,24,'31:24','Ne u japim të jetojnë pak (në kët botë), e pastaj i shtrëngojmë ata me një dënim të rëndë.');
INSERT INTO "ahmeti" VALUES(31,25,'31:25','Nëse ti pyet ata: Kush krijoi qiejt e tokën? Me siguri do të thonë: “All-llahu!” thuaj: “All-llahut i qoftë lavdërimi!” Por shumica e tyre nuk dinë.');
INSERT INTO "ahmeti" VALUES(31,26,'31:26','Vetëm të All-llahut janë të gjitha që gjenden në qiej e në tokë; All-llahu është Ai që s’ka nevijë dhe është i lavdishëm.');
INSERT INTO "ahmeti" VALUES(31,27,'31:27','Sikur të gjithë drutë në tokë të jenë lapsa dhe sikur deti t’i shtohen edhe shtatë dete (e të jenë me ngjyrë), nuk do të mbaroni fjalët e All-llahut (do të shterroheshin detet, do të soseshin lapsat, e jo mrekulitë e Zotit). All-llahu është ngadhënjyesi i urtë.');
INSERT INTO "ahmeti" VALUES(31,28,'31:28','Krijimi dhe ringjallja juaj nuk ështëmë e rëndë se krijimi i një qenije (një njeriu); vërtet, All-llahu është që i dëgjon e isheh veprat e njerëzve.');
INSERT INTO "ahmeti" VALUES(31,29,'31:29','A nuk e di se All-llahu e fut (errësirën) natën në ditë dhe fut (dritën) ditën në natë, dhe AI i nënshtroi Diellin e Hënën (të lindin e të përendojnë) dhe secili rrjedh (noton) deri në një afat të caktuar dhe se All-llahu hollësisht është i njohur për atë që veproni.');
INSERT INTO "ahmeti" VALUES(31,30,'31:30','Kjo (dokumenton) se All-llahu është Ai (Zot) i vërtetë, dhe se ajo që adhurojnë pos Tij është gënjeshtër, e All-llahu është Ai i larti, i madhi.');
INSERT INTO "ahmeti" VALUES(31,31,'31:31','A nuk sheh se anijet lundrojnë në saje të mirësisë së All-llahut, e oër t’u reguar juve madhërinë e Tij. Vërtet, edhe në këtë ka argument për secilin që është shumë i durueshëm dhe shumë mirënjohës.');
INSERT INTO "ahmeti" VALUES(31,32,'31:32','E kur i mbulon ata ndonjë valë si reja, i drejtohen All-llahut sinqerisht me besim vetëm ndaj Tij, e kur i shpëton ata e dalin në tokë, ka disa prej tyre që i mbesin besimit të drejtë, e faktet Tona nuk i refuzon kush pos të pabeit dhe përbuzësit.');
INSERT INTO "ahmeti" VALUES(31,33,'31:33','O ju njerëz, kini frikë ditës kur prindi nuk mund t’i bëjë dobi fëmijës së vet, e as fëmija nuk mund t’i bëjë asnjë send prindit të vet. Premtimi i All-llahut (për thevab ose azab) është e sigurt, pra mos ju mashrojë jeta e kësaj bote dhe të mos ju mashrojë ndajë All-llahut djalli mashtrues.');
INSERT INTO "ahmeti" VALUES(31,34,'31:34','S’ka dyshim se vetëm All-llahu e di kur do të ndodhë kijameti, Ai e di kur e lëshon shiun. Ai e di se çka në mitra (të nënave), nuk e di kush pos Tij se çka do t’i ndodhë (çka do të punojë) nesër, dhe askush nuk e di, pos Tij, se në ç’vend (ose kohë) do të vdesë. All-llahu është më i dijshmi, më i njohuri.');
INSERT INTO "ahmeti" VALUES(32,1,'32:1','Elif, Lamë, Mimë.');
INSERT INTO "ahmeti" VALUES(32,2,'32:2','Shpallja e librit (e Kuranit), nuk ka dyshim se është (shpallje) nga Zoti i gjithësisë.');
INSERT INTO "ahmeti" VALUES(32,3,'32:3','A mos po thonë se ai (Muhammedi) e trilloi? Jo, ajo është e vërtetë prej Zotit tënd që t’i tërheqësh vërejtjen një populli që nuk u erdhi ndonjë pejgamber para teje, ashtu që ata të gjejnë rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(32,4,'32:4','All-llahu është Ai që krijoi qiejt dhe tokën dhe gjithë ç’ka ndërmjet tyre, brenda gjashtë dite, pastaj mbizotëroi Arshin. Përveç Tij nuk keni ndonjë ndihmëtar tjetër e as ndërmjetësues, pra a nuk jeni kah mendoni!');
INSERT INTO "ahmeti" VALUES(32,5,'32:5','Ai është që udhëheq çështjen (e të gjitha krijesave) prej qiellit në tokë, pastaj ajo (çështja) ngritet te Ai në një ditë që sipas llogarisë suaj është sa njëmijë vjet.');
INSERT INTO "ahmeti" VALUES(32,6,'32:6','Ai është njohës i të (gjitha) fshehtës dhe të dukshmes, i gjithëfuqishmi, Mëshirëbërësi.');
INSERT INTO "ahmeti" VALUES(32,7,'32:7','Ai që përsosi krijimin e çdo sendi, e krijimin e njeriut e filloi nga balta.');
INSERT INTO "ahmeti" VALUES(32,8,'32:8','Pastaj bëri që pasardhësit e tij të rrjedhin prej një pike uji të dobët.');
INSERT INTO "ahmeti" VALUES(32,9,'32:9','Mandej, Ai e formëson atë (në barkun e nënës) dhe nga ana e vet i jep shpirt atij dhe Ai është që juve ju pajis edhe me dëgjim, me të parit dhe me zemër, e pak send është ajoqë ju falënderoni.');
INSERT INTO "ahmeti" VALUES(32,10,'32:10','E ata thanë: “A pasi që ne të tretemi në tokë, a thua ne rishtazi do të krijohemi?” Por (çka është edhe më keq) ata nuk besojnë se do të dalin para Zotit të tyre.');
INSERT INTO "ahmeti" VALUES(32,11,'32:11','Thuaj: “Engjëlli i vdekjes, i cili është caktuar për ju, ua merr shpirtrat, e pastaj do të ktheheni te Zoti juaj”.');
INSERT INTO "ahmeti" VALUES(32,12,'32:12','E, sikur të shihje mëkatarët se si ukin kokat e veta pranë Zotit të tyre: “Zoti ynë, tash pamë dhe dëgjuam, na kthe pra edhe një herë e të bëjmë vepra të mira, se tash jemi të bindur”.');
INSERT INTO "ahmeti" VALUES(32,13,'32:13','Sikur të kishim dëshiruar Ne, scilit do t’i jepnim udhëzimin, por fjala (vendimi) Ime ka marrë fund (definitiv) se do ta mbush Xhehennemin së bashku me exhinë e njerëz.');
INSERT INTO "ahmeti" VALUES(32,14,'32:14','Prandaj shijoni këtë (dënim) për shkak se ju të e patët harruar takimin tuaj në këtë ditë, e edhe Ne tash u harruam juve, ndaj, për shkak të asaj që keni vepruar, shijoni dënimin e përjetshëm.');
INSERT INTO "ahmeti" VALUES(32,15,'32:15','Argumentet tona, i besojnë, në t vërtetë, ata që kur këshillohen me to, bien në fytyra (bëjnë sexhde), që madhërojnë Zotin e tyre në shenjë falënderimi dhe ata nuk bëjnë kryelartësi,');
INSERT INTO "ahmeti" VALUES(32,16,'32:16','I heqen trupat e tyre prej dyshekëve, duke e lutur Zotin e tyre nga frika dhe nga shpresa dhe nga ajo që Ne u kemi dhënë (pasuria) atyre, ata japin.');
INSERT INTO "ahmeti" VALUES(32,17,'32:17','Pra, për ata që kanë vepruar nuk di askush për atë kënaqësi (të zemrës e shpirtit) që u është caktuar atyre si shpërblim.');
INSERT INTO "ahmeti" VALUES(32,18,'32:18','A mos është ai që ishte besimtarë, sikurse ai që ishte jasht rrugës? Jo! Këta nuk janë të barabartë.');
INSERT INTO "ahmeti" VALUES(32,19,'32:19','Për sa u përket atyre që besuan dhe bënë vepra të mira, ata, për hir të veprave që i bënë, vendpritje e kanë Xhennetin.');
INSERT INTO "ahmeti" VALUES(32,20,'32:20','E ata që nuk respektuan All-llahun, skutë e tyre është zjarri. Sa herë që të tentojnë të dalin nga ai, kthehen në të dhe atyre u thuhet: “Shijoni dënimin e zjarrit që ju e konsideronit rrenë.');
INSERT INTO "ahmeti" VALUES(32,21,'32:21','Ne do t’u shijojmë atyre dënimin më të shpejtë (në dynja) para dënimit të madh (në Ahiret), ndoshta do të tërhiqen (prej të këqiave).');
INSERT INTO "ahmeti" VALUES(32,22,'32:22','E kush është më zullumqarë se ai që këshillohet me ajetet e Zotit të vet, e pastaj u kthyen shpinën atyre? Ne me siguri do të hakmirremi kundër kriminelëve.');
INSERT INTO "ahmeti" VALUES(32,23,'32:23','Ne i patëm dhënë edhe Musait librin, e ti (Muhammed) mos ke dyshim në çka pranon (në Kur’anin libër qiellor si Musait), kurse atë (librin e Musait) e bëmë udhërrëfyes për beni israilët.');
INSERT INTO "ahmeti" VALUES(32,24,'32:24','Dhe prej tyre Ne bëmë prijës që me urdhërin Tonë udhëzojmë, pasi që ata (që i bëmë prijësa) ishin të durueshëm dhe ndaj argumenteve Tona ishin të bindur.');
INSERT INTO "ahmeti" VALUES(32,25,'32:25','S’ka dyshim se Zoti yt është Ai që në ditën e kijametit do të gjykojë mes tyre në atë që ishin të përçarë.');
INSERT INTO "ahmeti" VALUES(32,26,'32:26','A nk e kanë të qartë ata (mekasit) se sa nga popujt para tyre i shkatërruam, që nëpër ato vendbanime të tyre janë kah kalojnë? Është e vërtetë se ato (vende të shkatërruara) ka fakte, se nuk janë duke dëgjuar me vëmendje?');
INSERT INTO "ahmeti" VALUES(32,27,'32:27','Si nuk kanë parë ata se Ne e sjellim ujn në tokën e thatë dhe me të nxjerrim bimë që prej tyre ushqehen kafshët e tyre, bile edhe ata vetë, A nuk janë duke parë (e të kuptojnë)?');
INSERT INTO "ahmeti" VALUES(32,28,'32:28','E në anën tjetër ata thonë: “e kur do të jetë ai ngadhënjim (çlirimi i Mekës ose gjykimi i Zotit), nëse jeni të drejtë çka thoni.');
INSERT INTO "ahmeti" VALUES(32,29,'32:29','Thuaj: “Ditën e kijametit, atyre që kanë qenë jobesimtarë, nuk u bën dobi besimi e as nuk u jepet afat.');
INSERT INTO "ahmeti" VALUES(32,30,'32:30','Andaj, ti largohu prej tyre dhe prit, edhe ata janë duke pritur.');
INSERT INTO "ahmeti" VALUES(33,1,'33:1','O ti Pejgamber, qëndro i fortë në besnikëri ndaj All-llahut, e mos i përfill jobesimtarët dhe hipokritët, se vërtet All-llahu ësht i gjithëdijshëm, i di çështje në hollësi.');
INSERT INTO "ahmeti" VALUES(33,2,'33:2','Dhe ti vepro sipas asaj që po të shpallet nga Zoti yt; All-llahu është i njohur shumë mirë me atë që ju veproni.');
INSERT INTO "ahmeti" VALUES(33,3,'33:3','E, mbështetu në All-llahun, se mjafton që All-llahu të jetë mbrojtës yti.');
INSERT INTO "ahmeti" VALUES(33,4,'33:4','All-llahu nuk krijoi dy zemra në gjoksin e asnjë njeriu e as nuk ua bëri gratë tuaja, prej të cilave largoheni me dhihar nëna tuaja, (duke krahasuar shpinën e gruas me atë të nënës), e as nuk ua bëri djem tuaj të adoptuarit tuaj (fëmijët e tjetërkujt që po i adoptoni si të juaj). Këto janë vetëm thënie tuaja që i shqiptoni me gojët tuaja, e All-llahu e thotë atë që është realitet, dhe Ai udhëzon në rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(33,5,'33:5','Ju thirrni (mbështetni) ata në etëri e vërtetë të tyre, kjo është më e drejtë te All-llahu, e nëse nuk ua dini (nuk dini se kush janë) etërit e tyre, atëherë ata janë vëllezërit tuaj dhe të afërmit tuaj në fe. Nuk ëshë ndonjë mëkat juaji ajo për çka keni gabuar, por (është mëkat) ajo që zemrat tuaj e bëjnë qëllimisht, po All-llahu është që falë, është mëshirues.');
INSERT INTO "ahmeti" VALUES(33,6,'33:6','Pejgamberi është më i ndishëm ndaj besimtarëve se sa ata ndaj vetë vetvetes së tyre, ndërsa gratë e tij janë në vend të nënave të tyre. E ata (të afërmit) farefisi nga barku, sipas dispozitave të All-llahut, kanë më tepër përparësi ndaj njëri tjetërit se sa besimtarët e tjerë dhe se sa muhaxhirët (të mërguarit), përveç nëse dashamirëve tuaj doni t’u bëni ndonjë të mirë (u lëni testament ose diç tjetër). Kjo është e regjistruar në librin e shquar (Lehvi mahfush, Kur’an).');
INSERT INTO "ahmeti" VALUES(33,7,'33:7','(përkujto) Kur Ne e morëm (me betim) prej pejgamberve premtimin e tyre; edhe prej teje, prej Nuhut, Ibrahimit, Musait, Isait birit të Merjemes, pra morëm prej tyre besë të fotrë.');
INSERT INTO "ahmeti" VALUES(33,8,'33:8','Për të marrë në pyetje këta të sinqertë për sinqeritetin e tyre, e për jobesimtarët Ai ka përgatitur ndëshkim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(33,9,'33:9','O ju që keni besuar, përkujtonie të mirën e All-llahut ndaj jush, kur juve u erdhi një ushtri e Ne kundër tyre lëshuam një furtunë dhe ushtri që ju nuk e shihnit, e All-llahu e shihte atë që ju vepronit.');
INSERT INTO "ahmeti" VALUES(33,10,'33:10','Kur ata u erdhën juve edhe prej së larti edhe prej së poshti, dhe kur shikimet u shtangën, e zemrat arritën në fyt, e ju sajonit mendime të llojllojta për All-llahun.');
INSERT INTO "ahmeti" VALUES(33,11,'33:11','Atje, në atë vend qenë sprovuar jobesimtarët, dhe qenë tronditur me një dridhje të fortë.');
INSERT INTO "ahmeti" VALUES(33,12,'33:12','Kur hipokritët dhe ata që në zemrat e tyre kishin sëmundje, thonin: “All-llahu dhe i dërguari Tij, nuk na premtuan tjetër vetëm se mashtrim!”');
INSERT INTO "ahmeti" VALUES(33,13,'33:13','Dhe kur një grup prej tyre thanë: “O banorë të Jethribit, nuk ka qendresë për ju, ndaj kthehuni!” E një grup prej tyre kërkonin lejen e Pejgamberit, duke thënë: “Shtëpitë tona janë të pambrojtura, në realitet ata nuk donin tjetër por vetëm të iknin.');
INSERT INTO "ahmeti" VALUES(33,14,'33:14','Dhe sikur t’u hynin atyre nga anët e saj (Medinës) dhe prej tyre të kërkohej tradhëtia, ata do të ishin të gatshëm për të dhe nuk do të vononin aspak. (të tradhëtonin).');
INSERT INTO "ahmeti" VALUES(33,15,'33:15','E, ata ishin që i patën dhënë besën All-llahut më parë se nuk do të kthehen prapa (nuk do të ikin), pra premtimi i dhënë All-llahut mban edhe përgjegjësi.');
INSERT INTO "ahmeti" VALUES(33,16,'33:16','Thuaj: “Nëse ikët prej vdekjes ose prej mbytjes, ikja nuk do t’ju bëjë dobi, sepse edhe atëherë nuk do të (shpëtoni) përjetoni vetëm për pak kohë”.');
INSERT INTO "ahmeti" VALUES(33,17,'33:17','Thuaj: “Kush do t’ju mbrojë prej All-llahut, nëse Ai ua ka caktuar ndonjë të keqe, ose (do t’ju godit me ndonjë të keqe) nëse Ai ua ka caktuar ndonjë të mirë. Po përveç All-llahut ata nuk do të gjejnë për vete ndonjë përkrahës e as ndonjë ndhmëtar”.');
INSERT INTO "ahmeti" VALUES(33,18,'33:18','All-llahu i ka njohur shumë mirë ata që ndër ju pengonin dhe ata vëllezërve të vet u thonin: “Ejani me ne!” E ata nuk vijnë në luftë, vetëm pakicë.');
INSERT INTO "ahmeti" VALUES(33,19,'33:19','Janë koprracë ndaj jush (nuk ju duan të mirën). E kur u vjen frika i sheh ata të shikojnë ty, e sytë e tyre rrotullohen si të atij nga të fiktit para vdekjes, e kur largohet frika, ata ju shpojnë juve me gjuhë ë mprehta, lakmues për pasuri (për plaçkë -ganimet). Të tillët nuk kënë besuar, andaj All-llahu ua asgësojë verat e tyre, e kjo për All-llahun është lehtë.');
INSERT INTO "ahmeti" VALUES(33,20,'33:20','Ata mendonin se grupet aleate nde nuk kishin shkuar, dhe nëse aleatët kthehen ehe njëherë, ata (hipokritët) do të dëshironin të kishin qenë beduinë diku me arabët dhe prej tyre të pyesin për çështjen tuaj, por edhe sikur të ishin në mesin tuaj, ata krejt pak do të luftonin.');
INSERT INTO "ahmeti" VALUES(33,21,'33:21','Ju e kishit shembullin më të lartë në të dërguarin e All-llahut, kuptohet, ai që shpreson në shpërblimin e All-llahut në botën jetër, ai që atë shpresë e shoqëron duke e përmendur shumë shpesh All-llahun.');
INSERT INTO "ahmeti" VALUES(33,22,'33:22','E kur myslimanët e panë ushtrinë aleate, thanë: “Kjo është ajo që All-llahu dhe i dërguari i Tij na premtuan neve, e All-llahu dhe i dërguari i Tije thanë të vërtetën. Ajo (ushtria e armikut që e panë) vetëm ua shtoi atyre besimin dhe mbështetjen.');
INSERT INTO "ahmeti" VALUES(33,23,'33:23','Prej besimtarëve kishte burra që vërtetuan bsën e dhënë All-llahut, e disa prej tyre e realizuan premtimin duke dhënë jetën, dhe ka prej tyre që janë duke pritur (ta zbatojnë) dhe ashtu nuk bënë kurrfarë ndryshimi.');
INSERT INTO "ahmeti" VALUES(33,24,'33:24','Që All-llahu t’i shpërblejë të vërtetit për sinqeritetin e tyre e hipokritët t’i ndëshkjë, nëse do, ose t’ua falë atyre; All-llahu është që falë shumë, është mëshirues.');
INSERT INTO "ahmeti" VALUES(33,25,'33:25','Dhe All-llahu i zbrapsi ata që nuk besuan me atë mllefin e tyre, duke mos arritur kurrnjë të mirë. Dhe All-llahu ua largoi luftën besimtarëve. All-llahu është i fuqishëm, ngadhënjyes.');
INSERT INTO "ahmeti" VALUES(33,26,'33:26','E ata nga ithtarët e librit (jehuditë), të cilët u ndihmuan atyre (idhujtarëve), Ai (All-llahu) i nxori prej kështjellave të veta dhe në zemrat e tyre u shtini frikën, ashtu ë një grup e mbytni, kurse grupin tjetër e robëroni.');
INSERT INTO "ahmeti" VALUES(33,27,'33:27','E juve ju la trashëgim tokën e tyre, shtëpitë e tyre, pasurinë e tyre dhe tokën që ende nuk e keni shkelur. All-llahu ëstë i gjithfuqishëm për çdo send.');
INSERT INTO "ahmeti" VALUES(33,28,'33:28','O ti Pegamber, thuaju grave tuaja: “Në qoftë se lakmoni jetën e kësaj bote dhe stolitë e saj, atëherë ejani: unë po ju jap furnizimin (për lëshim) dhe po ju lëshoj ashtu si është e rrugës.');
INSERT INTO "ahmeti" VALUES(33,29,'33:29','E në qoftë se e don All-llahun, të dërguarin e Tij dhe botën tjetër, atëherë të jeni të sigurta se All-llahu ka përgatiur shpërblim të madh për ato prej jush që bëjnë punë të mira.');
INSERT INTO "ahmeti" VALUES(33,30,'33:30','O gra të Pejgamberit, cilado prej jush që bën punë të ligë ë të shëmtuar, asaj dënimi i shtohet dyfish e kjo për All-llahun është shumë lehtë.');
INSERT INTO "ahmeti" VALUES(33,31,'33:31','E kush prej jush i përvishet adhurimit të All-llahut dhe respektimit të të dërguarit të Tij dhe bën punë të mira, asaj do t’i japim shpërblimin e dyfishtë he për të kemi përgatitur furnizim të këndshëm.');
INSERT INTO "ahmeti" VALUES(33,32,'33:32','O gratë e Pejgamberit, ju nuk jeni si asnjë grua tjetër, nëse keni kujdes e ruheni, andaj mos u llastoni në të folur e të lakmojë ai që ka sëmundje në zemrën e tij, po thuani fjalë të matura.');
INSERT INTO "ahmeti" VALUES(33,33,'33:33','Dhe rrini në shtëpitë tuaja e mos shfaqni bukurinë tuaj si shfaqej në injorancën e hershme, falnie namazin, lepnie zeqatin dhe respektonie All-llahun dhe të dërguarin e Tij. All-llahu ka për qëllim që nga ju o familje e shtëpisë (se Pejgamberit) të largojë ndytësinë e mëkateve dhe t’ju pastrojë deri në skaj.');
INSERT INTO "ahmeti" VALUES(33,34,'33:34','E, përkujtonie atë nga ajetet e All-llahut dhe rregullat e fesë që po u lexohen në shtëpitë tuaja; vërtet, All-llahu është i kujdesshëm dhe i njohur hollësisht për çdo gjë.');
INSERT INTO "ahmeti" VALUES(33,35,'33:35','Nuk ka dyshim se për myslimanet, besimtarët e besiamtaret, adhuruesit e adhurueset, të sinqertit dhe të sinqertat, durimtarët dhe durimtaret, të përvuajturit dhe të përvuajturat, sadakdhënësit dhe sadakdhënëset, agjëruesit dhe agjërueset, ruajtësit e nderit dhe ruajtëset e nderit, shumë përmendësit e All-llahut e shumë përmendëset e All-llahut, All-llahu ka përgatitur falje (mëkatesh) dhe shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(33,36,'33:36','Kur All-llahu ka vvendosur për një çështje, ose i dërguari i Tij, nuk i takon (nuk i lejohet) asnjë besimtari dhe asnjë besimtareje që në atë çështje të tyre personale të bëjnë ndonjë zgjidhje tjetërfare. E kush e kundërshton All-llahun dhe të dërguarin e Tij, ai është larguar shumë larg së vërtetës.');
INSERT INTO "ahmeti" VALUES(33,37,'33:37','(Përkujto) Kur i the atij që All-llahu e kishte shpërblyer (me besim) e edhe ti i pate bërë mirë: “Mbaje bashkëshorten tënde dhe kij frikë na All-llahu!”, e ti e mbaje fshehtë në veten tënde atë që All-llahu do ta zbulojë dhe u frikësohesh njerëzve, por më e drejtë është që t’i friksohesh All-llahut. E pasi që Zejdi e kishte vendosur atë që kishte menduar ndaj saj, Ne ta kurorëzojmë ty atë, e për të mos pasur besimtarët vështirësi (mëkat) në martesë me gratë e të adoptuarëve të tyre, kur ata heqin dorë prej tyre. E vendimi i All-llahut është kryer.');
INSERT INTO "ahmeti" VALUES(33,38,'33:38','Pejgamberit nuk i përket kurrnjë qortim për atë që All-llahu e ngarkoi me të. Ky ishte ligj i All-llahut edhe ndër ata që ishin më parë, e urdhëri i All-llahut është vendim i kryer.');
INSERT INTO "ahmeti" VALUES(33,39,'33:39','(ligj i All-llahut ndë) Ata, të cilët i kumtojnë shpalljet e All-llahut, i frikësohen vetëm All-llahut e askujt tjetër pos Tij, e All-llahu është mjaft të jetë llogatitës.');
INSERT INTO "ahmeti" VALUES(33,40,'33:40','Muhammedi nuk ka qenë babai i asnjërit prej burrave tuaj por ai ishte i dërguari i All-llahut dhe vulë e të gjithë pejgamberëve, e All-llahu është i dijshëm për çdo send.');
INSERT INTO "ahmeti" VALUES(33,41,'33:41','O ju që besuat, përkujtoni All-llahun sa më shpesh.');
INSERT INTO "ahmeti" VALUES(33,42,'33:42','Dhe madhëronie Atë mëngjes e mbrëmje.');
INSERT INTO "ahmeti" VALUES(33,43,'33:43','Ai ju mëshiron juve e edhe engjëjt e Tij, e për t’ju nxjerrë juve prej errësirave në dritë dhe Ai ndaj besimtarëve është shumë i mëshirues.');
INSERT INTO "ahmeti" VALUES(33,44,'33:44','Ditën që e takojnë Atë (Zotin), përshëndetja e tyre është Selam (paqe) dhe për ta ka përgatitur shpërblim të mirë.');
INSERT INTO "ahmeti" VALUES(33,45,'33:45','O ti Pejgamber, Ne të dërguam ty dëshmues, lajmëtar përgëzues e qortues.');
INSERT INTO "ahmeti" VALUES(33,46,'33:46','Dhe me urdhërin e All-llahut, thirrës për në rrugën e Tij dhe pshtarë ndriçues.');
INSERT INTO "ahmeti" VALUES(33,47,'33:47','E besimtarëve jepu sihariq se prej All-llahut ata kanë dhunti të madhe.');
INSERT INTO "ahmeti" VALUES(33,48,'33:48','Mos i dëgjo jobesimtarët e hipokritët dhe mos ua vë veshin mundimeve të tyre ndaj teje, e mbështetju All-llahut se All-llahu është mbrojtje e mjaftueshme.');
INSERT INTO "ahmeti" VALUES(33,49,'33:49','O ju që besuat, kur të keni lidhr kurorë me besimtaret e pastaj i lëshoni ato para se të keni kontaktuar me to, ju nuk keni të drejtë të kërkoni që ato të presin afatin e caktuar (iddetin), po jepniu diçka atyre dhe lironi në mënyrë të njerëzishme.');
INSERT INTO "ahmeti" VALUES(33,50,'33:50','O ti Pejgamber, Ne të kemi lejuar gratë, të cilave u ke dhënë shpërblimin e kurorës, dhe ato që me ndihmën e Akkahut hynë në posedimin tëned (robëreshat e luftës) dhe bijat e axhës tënd, bijat e hallave tua e dajës tënd, bijat e tezeve tua (të janë lejuar, pos grave me nuqah dhe të afërtat), të cilat kanë migruar (kanë bërë hixhret) si ti, dhe një grua besimtare nëse ajo ja falë veten Pejgamberit dhe nëse Pejgamberi dëshiron të martohet me të (një martesë e tillë pa shpërblim të niqahit) duke qenë kjo (rast) vetëm për ty e jo edhe për besimtarët. Ne e dimë se ç’u kemi bërë atyre obligim rreth grave të tyre dhe rrëth atyre që i kanë në pronësinë e vet, ashtu që ti të mos keshë vështirësi, e All-llahu është që falë shumë, është mëshirues.');
INSERT INTO "ahmeti" VALUES(33,51,'33:51','Ti je i lirë të lëshosh atë që don prej tyre dhe të mbash pranë vete atë që don, e edhe të kërkosh afrimin e asaj nga e cila ke qenë larguar, dhe nuk ke mëkat. Kjo është më së afërmi që ato të qetësohen shpirtërisht e të mos brangosen dhe të jenë të kënaqura me atë që ti u ofron të gjitha atyre. All-llahu e di çka mbani në zemrat e tuaja, All-llahu është më i dijshmi, më i buti.');
INSERT INTO "ahmeti" VALUES(33,52,'33:52','Prej tash e mbrapa tyr nuk të llejohen më gra (të tjera) e as në veend të tyre të marrësh ndonjë tjetër, po edhe sikur të mahnit bukuria e tyre, përveç atyre që ike në posedim (robëreshat), All-llahu është përcjellës ndaj çdo sendi.');
INSERT INTO "ahmeti" VALUES(33,53,'33:53','O ju që besuat, mos hyni në shtëpitë e Pejgamberit ndryshe vetëm se nëse u lejohet për ndonjë ngrënie e duke mos pritur përgatitjen e saj, por kur të thirreni, atëherë shpërndahuni, duke mos hyrë në bisedë me njëri tjetrin, vërtet këto (pritja, qëndrimi, biseda) e mundojnë Pejgamberin, por ai turpërohet prej jush; All-llahu nuk i turpërohet realitetit. E kur kërkoni prej tyre (grave të Pejgamberit) ndonjë send, atë kërkonie pas perdës, kjo është më e pastër për zemrat tuaja dhe të tyre. Juve nuk u takon ta mundoni të dërguarin e All-llahut, e as të martoheni kurrë me gratë e tij, pas (vdekjes së) tij. Këto janë te All-llahu mëkat i madh.');
INSERT INTO "ahmeti" VALUES(33,54,'33:54','E shprehët ju haptazi ose fshehtazi ndonjë send, s’ka dyshim se All-llahu çdo send e di.');
INSERT INTO "ahmeti" VALUES(33,55,'33:55','Nuk është mëkat për ato (gratë e pambuluara) ndaj etërve të tyre, as ndaj djemve të tyre, as ndaj djemve të vëllezërve të tyre, as ndaj djemve të motrave të tyre, as ndaj grave të tyre, e as ndaj robëreshave të tyre. E, frikësohuni (o ju gra) All-llahut, All-llahu është pranë çdo sendi.');
INSERT INTO "ahmeti" VALUES(33,56,'33:56','Është e vërtetë se All-llahu dhe engjëjt e Tij me madhërim e mëshirojnë Pejgamberin. O ju që keni besuar, madhëronie pra atë (duke rënë salavatë) dhe përshëndeteni me selam.');
INSERT INTO "ahmeti" VALUES(33,57,'33:57','E s’ka dyshim se ata janë, të cilët e fyejnë All-llahun dhe të dërguarin e Tij, ata i ka mallkuar All-llahu në Dynja e në Ahiret dhe për ta ka përgatitur një dënim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(33,58,'33:58','E ata që fyejnë besimtarët dhe besimtaret, për çka ata nuk janë fajtorë, ata kanë ngarkuar veten me shpifje dhe me një mëkat shumë të madh.');
INSERT INTO "ahmeti" VALUES(33,59,'33:59','O ti Pejgamber, thuaju grave tua, bijave tua dhe grave të besimtarëve le të vëjnë shamitë (mbulojë) e veta mbi trupin e tyre, pse kj është më afër që ato të njihen (se nuk janë rrugaçe) e të mos ofendohen. All-llahu fal gabimet e kaluara, Ai është mëshirues.');
INSERT INTO "ahmeti" VALUES(33,60,'33:60','Po nëse hipokritët dhe ata që kanë zemra të sëmura, dhe ata që përhapin gënjeshtra në Medinë, nuk ndalen, Ne bëjmë që ti të dominosh mbi ta e pastaj ata nuk do të jenë fqinj me ty në të, vetëm një kohë të shkurtër.');
INSERT INTO "ahmeti" VALUES(33,61,'33:61','(Do të jenë) Të mallkuar, kudo që të ndeshen do të kapen dhe do të mbyten.');
INSERT INTO "ahmeti" VALUES(33,62,'33:62','(Ky është) Ligji i All-llahut edhe ndër ata që ishin më parë, e në ligjin e All-llahut nuk mund të gjesh ndryshim.');
INSERT INTO "ahmeti" VALUES(33,63,'33:63','Njerëzit të pyesin për kijametin (katastrofën) e ti thuaj: “Për atë e di vetëm All-llahu! E, ku mund ta dish ti, ai ndoshta është afër!”');
INSERT INTO "ahmeti" VALUES(33,64,'33:64','All-llahu i ka mallkuar jobesimtarët dhe për ta ka përgatitur zjarr të ndezur fort.');
INSERT INTO "ahmeti" VALUES(33,65,'33:65','Aty do të mbesin përgjithmonë dhe për ta gjejnë kë t’i mbrojë apo t’i ndihmojë.');
INSERT INTO "ahmeti" VALUES(33,66,'33:66','Ditën kur fytyrat e tyre do të përmbysen në zjarr e thonë: “Të mjerët ne, ta kishim adhuruar All-llahun e respektuar të dërguarin!”');
INSERT INTO "ahmeti" VALUES(33,67,'33:67','Dhe thonë: “Zoti ynë, ne i dëgjuam udhëheqësit tanë dhe të parët tanë, por ata na shmangën nga rruga e drejtë”.');
INSERT INTO "ahmeti" VALUES(33,68,'33:68','Zoti ynë, jepu atyre dënim të dyfishtë dhe mallkoj ata si është më së keqi!');
INSERT INTO "ahmeti" VALUES(33,69,'33:69','O ju që keni besuar, mos u bëni si ata që e ofenduan Musain, e All-llahu e pastroi atë nga ajo që i thanë. Ai te All-llah ishte me famë.');
INSERT INTO "ahmeti" VALUES(33,70,'33:70','O ju besimtarë, pëmbajuni mësimeve të All-llahut dhe thuani fjalë të drejta.');
INSERT INTO "ahmeti" VALUES(33,71,'33:71','Ai (All-llahu) iu mundëson të bëni vepra të mira, ju shlyen juve mëkatet tuaja, e kush respekton All-llahundhe të dërguarin e Tij, ai ka arritur një sukses të madh.');
INSERT INTO "ahmeti" VALUES(33,72,'33:72','Ne u ofruam emanetin (obligimet) qiejve, tokës dhe maleve, e ato nuk deshën ta marrin përsipër atë, u frikësuan prej tij, ndërsa njeriu atë e mori mbi vete; dhe ai i bëri padrejt vetes, dhe ishte padijshëm.');
INSERT INTO "ahmeti" VALUES(33,73,'33:73','(Njeriu e mori për sipër) Ashtu që All-llahu të ndëshkojë hipokritët dhe hipokritet, idhujtarët dhe idhujtaret, e tu falë besimtarëve dhe besimtareve. All-llahu është Ai që falë shumë dhe është mëshirues.');
INSERT INTO "ahmeti" VALUES(34,1,'34:1','Falënderimi i takon All-llahut, e të cilit është ç’ka në qiej dhe ç’ka në tokë dhe i tërë falënderimi i takon Atij në botën tjetër. Ai është i urti i përsosuri në njohuri.');
INSERT INTO "ahmeti" VALUES(34,2,'34:2','Ai e di çka vihet në tokë dhe çka del prej saj dhe çka zbret prej qiellit e çka ngrihet në të; Ai është Mëshiruesi, Mëkatfalësi.');
INSERT INTO "ahmeti" VALUES(34,3,'34:3','E ata që nuk besuan thanë: “Nuk do të na vijë neve kijameti!” Thuaj: “Po. psha Zotin tim që e di të fshehtën, pa tjetër do t’ju vijë ai juve. Atij nuk mund t’i shihet as në qiell as në tokë sendi sa grimca, e as më i vogël, e as më i madh, vetëm se janë të regjistruar në librin e qartë”.');
INSERT INTO "ahmeti" VALUES(34,4,'34:4','(Janë të shënuar) Që t’i shpërblejë ata që besuan dhe bënë vepra të mira. Për të tillët ka falje dhe furnizim të mirë.');
INSERT INTO "ahmeti" VALUES(34,5,'34:5','Ata që u prërpoqën t’i mposhtin faktet Tona, ata do të kenë një dënim të keq, të dhëmbshëm.');
INSERT INTO "ahmeti" VALUES(34,6,'34:6','Ndërsa atyre që u është dhënë dijenia, e dinë se kjo që të është shpallur ty nga Zoti, shtë e vërtetë dhe se udhëzon për në rrugën e Fuqiplotit, të lavdishmit.');
INSERT INTO "ahmeti" VALUES(34,7,'34:7','Dhe ata që nuk besuan thanë: “A doni t’ju trgojmë për një njeri që do t’ju informojë juve se si, pasi që (të vdisni) të jeni të copëtuar plotësisht, ju do të krijoheni rishtazi?');
INSERT INTO "ahmeti" VALUES(34,8,'34:8','A është duke krijuar gënjeshtër ndaj All-llahut, apo e ka kapur çmendia?” Jo, por ata që nuk besojnë botën e ardhme, janë të dënuar përjetë, e tash në humbje të pambarim.');
INSERT INTO "ahmeti" VALUES(34,9,'34:9','A nuk e shohin qiellin e tokën që i ka rrethuar ata para dhe prapa? Sikur të duam, Ne e shafitim tokën me a ose shembin mbi ta copa nga qielli. S’ka dyshim se në këtë ka argumente për secilin njeri që kthen mendjen te Zoti.');
INSERT INTO "ahmeti" VALUES(34,10,'34:10','Ne i patëm dhënë Davudit dhuratë të madhe nga ana e jonë: “O male dhe shpezë, lartësoni (me tesbih) së bashku me të!” Ne ia zbutëm atij edhe hekurin (si brumë).');
INSERT INTO "ahmeti" VALUES(34,11,'34:11','(I thamë) Puno këmisha të plota (nga hekuri) dhe thurri me precizitet ato. Dhe bëni vepra të mira (o familja e Davudit), se Unë vëzhgoj atë që ju punoni.');
INSERT INTO "ahmeti" VALUES(34,12,'34:12','Edhe Sulejmanit ia nënshtruam erën që paraditja (e udhëtimit me të duke shkuar) ishte sa një muaj dhe pasditja e saj (në të kthyer) sa një muaj (udhëtimi). I bëmë që atij t’i rrjedhë burim i remit dhe me urdhërin e Zotit të tij, ia nënshtruam xhinët që punonin sipas dëshirës së tij, e kush largohet prej tyre nga urdhëri Jonë, atij do t’ia shijojmë zjarrin e fortë.');
INSERT INTO "ahmeti" VALUES(34,13,'34:13','I punonin atij çka ai dëshironte: pallate të fortifikuara, skulptura, pjata (të drunjëta) sikurse rezervuare, enë (kazana) të palëvizshme. veproni duke falnderuar, o familje e Davudit, e nga robërit e Mi, pak janë mirënjohës.');
INSERT INTO "ahmeti" VALUES(34,14,'34:14','E kur ia caktuam atij vdekjen, askush tjetër, nuk i njoftoi ata (xhinët) për vdekjen e tij, përveç krimbit që i brejti shkopin e tij, e kur u rrëzua ai, për xhinët u bë e qartë se sikur të ishin ata që e dinin të fshehtën, nuk do të vazhdonin të qëndronin në mundimin e rëndë.');
INSERT INTO "ahmeti" VALUES(34,15,'34:15','Populli Sebe pati një begati në vendbanimin e vet: kishin nga të dy anët kopshte, nga ana e djathtë (e luginës) dhe nga ana e majtë. Hani (u thamë) nga begatitë e Zotit tuaj dhe falënderonju Atij. Qytet i mirë dhe Zot mëkatfalës.');
INSERT INTO "ahmeti" VALUES(34,16,'34:16','Po ata i kthyen shpinën, e Ne e lëshuam kundër tyre rrjdhën e pendës dhe dy kopshtet e tyre i shndërram në dy kopshte me fruta të idhët, drunj të thatë dhe diçka pak bari të egër.');
INSERT INTO "ahmeti" VALUES(34,17,'34:17','Atë dënim ua dhamë atyre për shkak se nuk besuan, e Ne nuk dënojmë, përveç mohuesit.');
INSERT INTO "ahmeti" VALUES(34,18,'34:18','Dhe, në mes tyre dhe mes fshatrave të bekuara (në Sham), ju bëmë fshatra të njëpasnjëshme dhe ju bëmë udhëtim të parshtatshëm. Udhëtoni të sigurt nëpër to natën dhe ditën.');
INSERT INTO "ahmeti" VALUES(34,19,'34:19','Po ata thanë: “Zoti ynë, na i largo udhëtimet tona!” E ashtu e dëmtuan veten dhe i bëmë ata ngjarje tregimesh, i ndamë plotësisht. Në këtë ka argumente për secilin durimtar e mirënjohës.');
INSERT INTO "ahmeti" VALUES(34,20,'34:20','Në të vërtetë, djalli realizoi mendimin e vet ndaj tyre, ndaj edhe e dëgjuan atë, përpos një grupi besimtarësh.');
INSERT INTO "ahmeti" VALUES(34,21,'34:21','E ai (djalli) nuk pati kurrfarë pushteti nda tyre, përveç që (dija e Jonë t dal në shesh) të dihet ai që beson në botën tjetër, prej atij që është në dyshim për të. E Zoti yt është përcjellës ndaj çdo gjëje.');
INSERT INTO "ahmeti" VALUES(34,22,'34:22','Thuaj: “Thirrni ata, të cilët i menduat për zota pos All-llahut!” Ata nuk posedojnë sa një grimcë as në qiej e as në tokë dhe as që kanë në to nonjë pjesë, dhe Ai nuk ka prej tyre ndonjë përkrahje.');
INSERT INTO "ahmeti" VALUES(34,23,'34:23','Dhe ndërmjetësimi nuk bën dobi ek Ai, përveç atij të cilit i jep leje, e kur hiqe frika nga zemrat e tyre (të ndërmjetësuesve), ata thonë: “çka tha Zoti juaj!” (rreth shefatit). Ata (engjëjt e lartë) thonë: “Të vërtetën!” (u dha leje). Ai është më i larti, më i madhi.');
INSERT INTO "ahmeti" VALUES(34,24,'34:24','Thuaj: “Kush ju furnizon nga qiejt e nga toka?” Thuaj: “All-llahu”. E atëherë, ose ne ose ju jemi në rrugë të drejtë, po në një humbje të dukshme!');
INSERT INTO "ahmeti" VALUES(34,25,'34:25','Thuaj: “Ju nuk jeni përgjegjës për gabimet tona, e as ne nuk përgjigjemi për atë që veproni ju!”');
INSERT INTO "ahmeti" VALUES(34,26,'34:26','Thuaj: “Zoti ynë n tubon neve dhe juve, e pastaj me drejtësi gjykon mes nesh, se Ai është gjykatës i drejtë, i dijshëm.');
INSERT INTO "ahmeti" VALUES(34,27,'34:27','Thuaj: “M’i tregoni ata (idhuj) që ia shoqëroni Atij si rivalë!” Jo, kurrsesi nuk ka shok, por Ai është All-llahu, i gjithfuqishmi, i urti!”');
INSERT INTO "ahmeti" VALUES(34,28,'34:28','Ne nuk të dërguam ty ndryshe vetëm se për të gjithë njerëzit, myzhdedhënëa dh tërheqës i vërejtjes, por shumica e njerëzve nuk e dinë.');
INSERT INTO "ahmeti" VALUES(34,29,'34:29','E thonë: “Kur është ai premtim, nëse jeni të vërtetë?”');
INSERT INTO "ahmeti" VALUES(34,30,'34:30','Thuaj: “Ju keni afatin e një dite që për asnjë moment nuk do të shtyheni për më vonë, e as nuk do të nguteni më para.');
INSERT INTO "ahmeti" VALUES(34,31,'34:31','Dhe ata që nuk besuan thanë: “Ne nuk i besojmë këtij Kur’ani, e as atij që idhte para tij, (librave të tjerë)?!” E sikur t’i shihje zullumqarët kur ë dalin para Zotit të tyre të ndalen, kthejnë fjalën (fyese) njëri-tjerit, atyre që ishin pari, u thonë: “Sikur të mos ishit ju, ne do të kishim qenë besimtarë!”');
INSERT INTO "ahmeti" VALUES(34,32,'34:32','E ata që ishin pari, atyre që kishin qenë të dobët u thonë: “A ne ju penguam prej udhëzimit të drejtë pasi që u pat ardhur juve? Jo, por ju vetë ishit kriminelë!”');
INSERT INTO "ahmeti" VALUES(34,33,'34:33','E ata që kishin qenë të shtypur, atyre që ishin krerë u thonin: “Jo, por dredhia juaj natë e ditë (na largoi prej besimit), kur ju na thërrisnit të mos e besojmë All-llahun dhe t’i bëjmë Atij shokë. E kur e shohim dënimin, e fshehin dëshpërimin e vet dhe Ne u vëmë prangat në qafat e tyre që nuk besuan. Ata nuk dënohen për tjetër pos për atë që punuan.');
INSERT INTO "ahmeti" VALUES(34,34,'34:34','Ne nuk dërguam në asnjë vendbanim ndonjë nga pejgamberët e që pasanikët e tij të mos i thonin: “Ne nuk i besojmë asaj me çka jeni dërguar”.');
INSERT INTO "ahmeti" VALUES(34,35,'34:35','Ata thoshin: “Ne kemi më shumë pasuri e fëmijë, e ne nuk do të jemi të ndëshkuar”.');
INSERT INTO "ahmeti" VALUES(34,36,'34:36','Thuaj: “All-llahu ia shumon begatinë atij që do, e edhe ia pakëson, por shumica e njerëzve nuk dinë”.');
INSERT INTO "ahmeti" VALUES(34,37,'34:37','Nuk është pasuria juaj, e as fëmijët tuaj ajo që ju afro pranë Nesh, është vetëm besimi dhe veprat e mira; të tillët shpërblehen shumëfish për atë që vepruan dhe ata janë në dhoma të larta të shpëtuar.');
INSERT INTO "ahmeti" VALUES(34,38,'34:38','Ndërsa, ata që përpiqen t’i mposhtin argumentet Tona, të tillët janë të dënuar.');
INSERT INTO "ahmeti" VALUES(34,39,'34:39','Thuaj: “S’ka dyshim se Zoti im është Ai që jep furnizim të begatshëm atij që do nga robërit e vet dhe Ai ia pakëson atij, e çkado që të jepni, Ai e kompenson atë dhe Ai është dhuruesi më i madh.');
INSERT INTO "ahmeti" VALUES(34,40,'34:40','Dhe, (përkujto) ditën kur i tubon ata të gjithë e pastaj engjëjve u thotë: “A këta ishin që etëm juve u adhuronin?”');
INSERT INTO "ahmeti" VALUES(34,41,'34:41','Ata (engjëjt) thonë: “I pa të meta je o i madhëruar! Ti je Zoti ynë, larg asaj që ata thonë! Por ata kanë qenë që adhuronin xhinët (djajtë) dhe shumica sish u besonin atyre”.');
INSERT INTO "ahmeti" VALUES(34,42,'34:42','E sot pra, Nuk keni gjë në dorë t’i bën dobi, as dënim njëri-tjetrit, dhe atyre që ishin zullumqarë u themi: “Shijoni dënimin e zjarrit, të cilin e konsideruat gënjeshtër!”');
INSERT INTO "ahmeti" VALUES(34,43,'34:43','E kur u lexohen atyre ajetet tonaa të qarta, ata thoshin: “Ky nuk është tjetër vetëm se njeri, i cili dëshiron t’ju shmangë nga ajo që adhuronin prindërit tuaj” dhe thoshin: “Ku (Kur’ani) nuk është tjetër vetëm se gënjeshtër e trilluar!” Madje, ata që nuk besuan të vërteën, pasi ajo u erdhi, i thanë: “Kjo (Muhammed, feja islame, Kur’ani) nuk është tjetër vetëm se maxhi e qartë!”');
INSERT INTO "ahmeti" VALUES(34,44,'34:44','Po Ne nuk kemi u dhënë atyre ndonjë libër që të mësojnë dhe as nuk dërguam te ata ndonjë pejgamber para teje (pra ata nuk dinë si është pejgamberi as si është shpallja).');
INSERT INTO "ahmeti" VALUES(34,45,'34:45','Edhe ata që ishin para këtyre (kurejshitëve) patën përgënjeshtruar, dhe këta nuk kanë (kurejshitët) as një të dhjetën e asaj, që Ne u patëm dhënë atyre, e konsideruan rrenacakë të dërguarit e Mi, ja si ishte dënimi Im.');
INSERT INTO "ahmeti" VALUES(34,46,'34:46','Thuaj: “Unë ju këshilloj vetëm me një gjë: Për hir të All-llahut të angazhoheni sinqerisht dy nga dy ose një nga një, e pastaj të mendoni thellë (që ta kuptoni) se shoku juaj (Muhammedi) nuk ka ndonjë çmendje. Ai nuk është tjetër vetëm t’ju tërheqë vërejtjen për një dënim të ashpër (nëse nuk besoni)”.');
INSERT INTO "ahmeti" VALUES(34,47,'34:47','Thuaj “Unë nuk kërkova prej jush ndonjë shpërblim, nëse kam kërkuar, ai le të, mbetet juve, shpërbilmi është vetëm prej All-llahut, e Ai është dëshmitarë për çdo send”.');
INSERT INTO "ahmeti" VALUES(34,48,'34:48','Thuaj: “Zoti im, Njohës i të fshehtave, sjell të vërtetën”.');
INSERT INTO "ahmeti" VALUES(34,49,'34:49','Thuaj: “Erdhi e vërteta, e kota u zhduk pa fillim dhe pa kthim!”');
INSERT INTO "ahmeti" VALUES(34,50,'34:50','Thuaj: “Nëse unë kam humbur, atëherë e keqja e humbjes është vetëm në dëm timin, e nëse e kam gjetur të vërtetën, atëherë ajo është me ndihmën e Zotit tim; Ai është dëgjues, i afërt!”');
INSERT INTO "ahmeti" VALUES(34,51,'34:51','E sikur t’i shohësh ata kur i kap frika e nuk kanë shpëtim ehe të kapen prej një vendi afër (do të shihje tmerr të madh).');
INSERT INTO "ahmeti" VALUES(34,52,'34:52','Dhe thonë: “Ne i kemi besuar atij (Kur’anit, Muhammedit)!” Po si arrijnë ata beimin prej së largu?');
INSERT INTO "ahmeti" VALUES(34,53,'34:53','Kur ata më parë e mohuan atë dhe prej së largu thoshin me hamendje (s’ka ringjallje, s’ka përgjegjësi etj.).');
INSERT INTO "ahmeti" VALUES(34,54,'34:54','Dhe ndërhyhet (ndërhyn All-llahu) ndërmjet tyre dhe ndërmjet asaj që dëshirojnë ata, ashtu sikurse është vepruar me parë me të ngjashmit e tyre (partinë e tyre); vërtet, ata ishin ë një dyshim të fortë.');
INSERT INTO "ahmeti" VALUES(35,1,'35:1','Falënderimi i qoftë Allauht, Krijuesit të qiejve e tokës Sajuesit të engjëjve me mga dy palë, tri palë e katër krahë, ndërmjetsues (në mes Tij dhe pejgamberëve). Ai shton në krijimin atë që do, vërtet, All-llahu ka fuqi për çdo send.');
INSERT INTO "ahmeti" VALUES(35,2,'35:2','Atë që All-llahu ua dhuron njerëzve nga mëshira e Tij, nuk ka kush që mund ta pengojnë, e atë që Ai e ndal, nuk ka kush që mund ta lirojë pos Tij; Ai është ngadhnjyesi, i gjithdijshmi.');
INSERT INTO "ahmeti" VALUES(35,3,'35:3','O ju njerëz, ruani me mirënjohje të mirat që All-llahu u dhuroi! A ka ndonjë zot tjetër që u furnizon juve nga qielli e toka? Nuk ka zot tjetër përveç Tij, e si pra, e ktheni shpinën?');
INSERT INTO "ahmeti" VALUES(35,4,'35:4','Po, nëse ty të konsiderojnë rrenacak, edhe të dërguarit para teje kanë qenë të përgënjeshtruar, po vetëm te All-llahu kthehen çështjet.');
INSERT INTO "ahmeti" VALUES(35,5,'35:5','O ju njerëz, s’ka dyshim se premtimi i All-llahut është i vërtetë, pra të mos u mashtrojë juve jeta e kësaj bote dhe të mos u mashtrojë juve ndaj All-llahut ai i mashtruari (shejtani).');
INSERT INTO "ahmeti" VALUES(35,6,'35:6','Djalli është armik i juaji, pra edhe ju konsideronie armik, ai e thërret atë grupin e vet, vetëm për t’i bërë banues të zjarrit.');
INSERT INTO "ahmeti" VALUES(35,7,'35:7','Ata që nuk besuan, kanë një dënim të ashpër, e ata që besuan dhe bën vepra të mira, ata kanë falje (të mëkateve) dhe shpërblilm të madh).');
INSERT INTO "ahmeti" VALUES(35,8,'35:8','A atij që vepra e vet e keqe i është hijeshuar dhe e sheh si të mirë (a është i njejtë me atë që i largohet asaj)? Në të vërtetë, All-llahu e largon nga e vërteta atë që do dhe Ai e udhëzon atë që do, andaj ti (Muhammed) mos shkatërro veten me dëshpërim për ta, All-llahu e di shumë mirë atë që bëjnë ata.');
INSERT INTO "ahmeti" VALUES(35,9,'35:9','All-llahu është Ai që i lëshon erërat, e ato i lëkundin retë dhe Ne i derdhim ato në një vend të thatë dhe me atë ujitje e ngjallim tokën pas vdekjes së saj. Ja, kështu është ringjallja.');
INSERT INTO "ahmeti" VALUES(35,10,'35:10','Kush e dëshiron krenarinë (le ta dijë) e tërë krenaria i takon All-llahut (pra le të kërkojë prej Tij), te Ai ngritet fjala e mirë (besimi) dhe veprimi i mirë, e Ai i pranon. E ata që përgatitin dredhira të këqia, për ata është një dënim i rëndë dhe dredhia e tyre është e zhdukur.');
INSERT INTO "ahmeti" VALUES(35,11,'35:11','All-llahu ju krijoi prej dheu, mandej prej një pike uji, e pastaj ju bëri çift. E asnjë femër nuk bart e as nuk lind ndryshe, vetëm se me dijen e Tij, dhe nuk i jepet kujt të jetojë gjatë ose t’i shkurtohet jeta e tij, vetëm se ajo është e shënuar në Libër (Lehvi mahvudh), e kjo për All-llahun është e lehtë.');
INSERT INTO "ahmeti" VALUES(35,12,'35:12','E nuk janë të njejtë dy dete; ky i ëmbël shuan etjen dhe pirja e tij është e lehtë, e ky tjetri i njelmët djeg. E prej secili ju hani mish të freskët, nxirrni stoli që i bani; e, i sheh anijet se si çajnë ujin për të kërkuar të mirat e Tij. Ashtu që ju falënderoni.');
INSERT INTO "ahmeti" VALUES(35,13,'35:13','Ai është që zgjat natën në pjesë të ditës dhe zgjat ditën në pjesë të natës, i nënshtroi diellin dhe hënë që secili lëviz (udhëton) deri në afatin e caktuar. Ky është All-llahu, Zoti juaj, i tërë sundimin është i Tij, e ata që i lutni në vend të Tij, nuk posedojnë as sa një cipë (e hurmave, fije).');
INSERT INTO "ahmeti" VALUES(35,14,'35:14','Nëse ata i thëritni, ata nuk dëgjojnë thirrjen tuaj, po të zëmë se dëgjojnë nuk mund t’ju përgjigjen juve në ditën e kijametit, ata do të mohojnë adhurimin tuaj ndaj tyre. E kush nuk të informon ty si i Dijshmi (All-llahu).');
INSERT INTO "ahmeti" VALUES(35,15,'35:15','O ju njerëz, ju keni nevojë All-llahun e All-llahu nuk ka nevojë për ju; Ai është i falënderuari.');
INSERT INTO "ahmeti" VALUES(35,16,'35:16','Po të dojë Ai, juve ju zhduk e sjellë krijesë të re.');
INSERT INTO "ahmeti" VALUES(35,17,'35:17','E për All-llahun ajo nuk është e vështirë.');
INSERT INTO "ahmeti" VALUES(35,18,'35:18','dhe asnjë mëkatar nuk do të bartë mëkatin e tjetërit, po edhe nëse mëkatarin thërret ndonjë për t’ia bartur atë, ajo nuk do t’i bartet atij, edhe nëse (ai që thërret) është i afërt i tij. Ti mund t’ua tërhequr vërejtjen vetëm atyre që i kanë frikën Zotit të tyre edhe pse nuk shohin dhe që e falin namazin. E kush pastron veten, ai ka bërë pastrimin për të mirën e vet, se vetëm te All-llahu është e ardhmja.');
INSERT INTO "ahmeti" VALUES(35,19,'35:19','Nuk janë të barabartë i verbëri dhe ai që sheh.');
INSERT INTO "ahmeti" VALUES(35,20,'35:20','Dhe as errësirat e drita.');
INSERT INTO "ahmeti" VALUES(35,21,'35:21','E as hija me vapën.');
INSERT INTO "ahmeti" VALUES(35,22,'35:22','E nuk janë të njejtë as të gjallit e të vdekurit, All-llahu bën të dëgjojë atë që do, e ti nuk mund të bësh të dëgjojë ai që është në varr.');
INSERT INTO "ahmeti" VALUES(35,23,'35:23','Ti nuk je tjetër vetëm i dërguar që tërheq vërejtjen.');
INSERT INTO "ahmeti" VALUES(35,24,'35:24','Ne të dërguam ty me atë që është e vërtetë, lajmgëzues e qortues, e nuk pati asnjë nga popujt që nuk pati të dërguar.');
INSERT INTO "ahmeti" VALUES(35,25,'35:25','Edhe nëse të përgënjeshtrojnë ty, përgënjeshtruan edhe ata që ishin para tyre, që atyre u patën ardhur argumente të qarta, me broshura dhe me Libër të ndritshëm.');
INSERT INTO "ahmeti" VALUES(35,26,'35:26','Pastaj (pasi refuzuan) i shkatërrova ata që nuk besuan, e sa i tmershëm ishte ndëshkimi Im!');
INSERT INTO "ahmeti" VALUES(35,27,'35:27','A nuk e sheh se All-llahu lëshon nga qielli ujë, e Ne nxjerrim me të fruta, ngjyra e të cilave është e ndryshme, e edhe nëpër kodra ka vija të bardha e të kuqe ngjyrash të ndryshme, e ka edhe shumë të zeza.');
INSERT INTO "ahmeti" VALUES(35,28,'35:28','Edhe nga njerëzit, nga gjallesat, nga kafshët po ashtu ka të ngjyrave të ndryshme. Po All-llahut ia kanë frikën nga robërit e Tij, vetëm dijetarët, All-llahu është mbi gjithçka është mëkatfalës.');
INSERT INTO "ahmeti" VALUES(35,29,'35:29','Ata që lexojnë librin e All-llahut, e falin namazin dhe nga begatitë që Ne u kemi dhënë japin fshehtazi e haptazi, ata e shpresojnë në një fitim që kurrë nuk humbet.');
INSERT INTO "ahmeti" VALUES(35,30,'35:30','Që Ai (All-llahu) do t’ju plotësojë shpërblimin e tyre, e edhe do t’ju shtojë nga mirësia e Tij, vërtet Ai është mëkatfalës dhe shumë dhe shumë mirënjohës.');
INSERT INTO "ahmeti" VALUES(35,31,'35:31','Dhe ajo që Ne të shpallëm ty nga libri, është e vërtetë e saktë, ë vërteton edhe për ato që ishin para tij. Vëetet, All-llahu është hollësisht i njohur ndaj robërve të vet, isheh të gjitha.');
INSERT INTO "ahmeti" VALUES(35,32,'35:32','Pastaj Ne u lamë në trashëgim librin robërve Tanë që Ne i kemi zgjedhur; e prej tyre ka që janë dëmtues të vetëvetës, ka që janë mesatarë, e ka prej tyre që janë më ndihmën e All-llahut, të parët në punë të mira, kjo është ajo mirësia e madhe.');
INSERT INTO "ahmeti" VALUES(35,33,'35:33','Xhennetet e Adnit janë që do të hyjnë në to, aty do të stollisen me rrethdorëza nga ari e margaritari, e petkat e tyre janë të mëndafshta.');
INSERT INTO "ahmeti" VALUES(35,34,'35:34','E ata thonë: “Falënderuar qoftë All-llahu që largoi prej nesh brengat; vërtet, Zoti ynë është që falë shumë dhe është bamirës.');
INSERT INTO "ahmeti" VALUES(35,35,'35:35','I cili nga mirësia e Tij na vendosi në vendin e përjetshëm, ku nukk do të na prekë ndonjë mundim fizik dhe ku nuk na prek ndonjë molisje.');
INSERT INTO "ahmeti" VALUES(35,36,'35:36','Po për ata që nuk besuan është zjarri i Xhehennemit. Ata as nuk gjykohen që të vdesin (të rehatohen) e as nuk u lehtësohe ndëshkimi. Kështu e ndëshkojmë secilin që është shumë i pabesim.');
INSERT INTO "ahmeti" VALUES(35,37,'35:37','Dhe ata do të klithin aty: “O Zot ynë, nxirrna e të bëjmë vepra të mira, e jo si ato që i bënim!” Po a nuk u dhamë juve jetë aq sa që ai ka dashur të mendojë, ka mundur të mendojë gjatë asaj kohe, madje juve u ka ardhur edhe pejgamberi, pra shijoni, se për zullumqarët nuk ka ndonjë ndihmëtar.');
INSERT INTO "ahmeti" VALUES(35,38,'35:38','Nuk ka dyshim, se All-llahu e di të fshehtën e qiejve e të tokës dhe Ai është që e di se çka mbajnë gjoksat.');
INSERT INTO "ahmeti" VALUES(35,39,'35:39','Ai është që juve ju bëri zëvendësues në Tokë, e kush nuk besoi të keqen e mosbesimit e ka kundër vetes; dhe mosbesimi i jobesimtarëve nuk u shton tjetër te Zoti i tyre vetëm se urrejtje të fortë dhe jobesimtarëve nuk u shton tjetër, mosbesimi i tyre vetëm se dëshpërim.');
INSERT INTO "ahmeti" VALUES(35,40,'35:40','Thuaj: “Më tregoni për zotat tuaj që i adhuroni pos All-llahut, më bëni të shohë se ç’krijuan ata në tokë; a mos kanë pjesë ata në qiej, a mos u kemi dhënë atyre ndonjë libër, e ata kanë argument në të?” Jo, por mizorët nuk i premtojnë njëri-tjetrit diçka tjetër vetëm se mashtrim.');
INSERT INTO "ahmeti" VALUES(35,41,'35:41','All-llahu i mban qiejt dhe tokën që të mos zhduken, e nëse zhduken, s’ka askush pos Tij që mund t’i mbajë; Ai është që nuk ngutet, është që falë.');
INSERT INTO "ahmeti" VALUES(35,42,'35:42','Ata me betimin më të fortë të tyre janë betuar në All-llahun se, nëse u vjen atyre ndonjë pejgamber, do të jenë më të udhëzuarit (më besimtarët) nga njeri i atyre popujve (jahudi ose i krishterë), e kur u erdh atyre pejgamberi, atyre nuk u shtoi tjetër vetëm se largim.');
INSERT INTO "ahmeti" VALUES(35,43,'35:43','(Largim) Për shkak të mendjemadhësisë dhe dredhisë së keqe, por dredhia e keqe nuk godit tjetër vetëm ata që kurdisën. Pra ata nuk janë duke pritur tjetër vetëm gjurmët e të parëve, e në ligjin e All-llahut kurrë nuk do të hasësh devijim.');
INSERT INTO "ahmeti" VALUES(35,44,'35:44','A nuk udhëtuan ata nëpër tokë e të sohin se si ishte përfundimi i atyre që ishin para tyre, ndonëse ata ishin edhe më të fuqishëm. All-llahu nuk është i tillë që ndonjë send në qiej e as në tokë të mund ta bëjë të paaftë Atë; Ai është i dijshëm, i fuqishëm.');
INSERT INTO "ahmeti" VALUES(35,45,'35:45','E sikur All-llahu t’i kapte njerëzit sipas veprave (të këqia) të tyre, nuk doheut asnjë gjallesë, por Ai i afatizon deri në momentin e caktuar, e kur të vijë afati i tyre, s’ka dyshim se All-llahu i ka parasysh robërit e vet.');
INSERT INTO "ahmeti" VALUES(36,1,'36:1','Ja, Sinë!');
INSERT INTO "ahmeti" VALUES(36,2,'36:2','Pasha Kur’anin e pacenueshëm në urtësinë e tij të lartë.');
INSERT INTO "ahmeti" VALUES(36,3,'36:3','S’ka dyshim se ti (Muhammed) je prej të dërguarve.');
INSERT INTO "ahmeti" VALUES(36,4,'36:4','Je në një rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(36,5,'36:5','(Kur’ani është) Zbritje e plotfuqishmit, e Mëshiruesit.');
INSERT INTO "ahmeti" VALUES(36,6,'36:6','Për t’ia tërhequr një populli, që të parët e tyre nuk u është tërhequr, e për atë shkak ata janë të hutuar.');
INSERT INTO "ahmeti" VALUES(36,7,'36:7','Për Zotin tashmë ka marrë fund (thënia) shumicës së tyre, andaj ata edhe nuk besojnë.');
INSERT INTO "ahmeti" VALUES(36,8,'36:8','Ne u kemi varë në qafat e tyre pranga e ato u arrijnë deri në nofulla, andaj ata mbesin me koka lart.');
INSERT INTO "ahmeti" VALUES(36,9,'36:9','Ne u kemi vënë edhe para tyre pendë edhe mbrapa tyre pendë dhe ua kemi mbuluar sytë, prandaj ata nuk shohin.');
INSERT INTO "ahmeti" VALUES(36,10,'36:10','Dhe për ata është e njejtë, a ua tërhoqe vërejtjen a nuk ua tërhoqe, ata nuk besojnë.');
INSERT INTO "ahmeti" VALUES(36,11,'36:11','Ti ia tërheq vërejtjen vetëm atij që e përvetson Kur’anin dhe i frikësohet Mëshiruesit dhe kur është vetëm (i papashëm prej njerëzve), pra jepi myzhde atij prë falje dhe shpërblim të mirë.');
INSERT INTO "ahmeti" VALUES(36,12,'36:12','Vërtet, Ne i ngjallim të vdekurit dhe i shkruajmë veprat; gjurmët e tyre dhe çdo gjë kemi regjistruar në librin e ruajtur (lehvi mahvudh).');
INSERT INTO "ahmeti" VALUES(36,13,'36:13','Përmendju atyre një shembull të banorëve të fshatit, kur atyre u patë ardhur të dërguarit.');
INSERT INTO "ahmeti" VALUES(36,14,'36:14','Kur Ne dërguam te ata dy, e ata i përgënjeshtruan që të dy, atëherë i përforcum me një të tretë dhe u thanë: “Ne jemi të dërguar te ju.');
INSERT INTO "ahmeti" VALUES(36,15,'36:15','Ata (fshatarët) thanë: “Ju nuk jeni tjetër, vetëm se njerëz sikurse ne dhe se All-llahu nuk u ka shpallur asgjë; ju nuk jeni tjetër vetëm se rrenacakë.');
INSERT INTO "ahmeti" VALUES(36,16,'36:16','Ata thanë: “Zoti ynë e di se me të vërtetë ne jemi të dërguar te ju.');
INSERT INTO "ahmeti" VALUES(36,17,'36:17','Dhe nuk jemi të aobliguar për tjetër, përveç t’ju kumtojmë ashtu qartë.');
INSERT INTO "ahmeti" VALUES(36,18,'36:18','Ata (fshatarët) thanë: “Ne parandiejmë kob me ju dhe nëse nuk tërhiqeni, ne do t’ju gyrëzojmë dhe do të përjetoni ndëshkim të idhët prej nesh.');
INSERT INTO "ahmeti" VALUES(36,19,'36:19','Ata (të dërguarit) thanë: “Fati i keq është i juaji, a pse u këshilluat, a (na kërcënoheni)? Jo, por ju jeni popull i shfrenuar!”');
INSERT INTO "ahmeti" VALUES(36,20,'36:20','Dhe prej skajit më të largët të qytetit erdhi një njeri qëngutej e tha: “O populli im, dëgjoni të dërguarit!”');
INSERT INTO "ahmeti" VALUES(36,21,'36:21','Dëgjoni atë që nuk kërkon prej jush ndonjë shpërblim dhe janë udhërrëfyes!');
INSERT INTO "ahmeti" VALUES(36,22,'36:22','E, ç’kam unë që të mos e adhuroj atë që më krijoi dhe, te Ai ktheheni.');
INSERT INTO "ahmeti" VALUES(36,23,'36:23','A mos do të pranoj zota tjerë pos Tij, e nëse do Mëshiruesi të më godasë me ndonjë të keqe, ndërmjetësimi i tyre nuk do të më vlejë asgjë, e as që do të më shpëtjnë.');
INSERT INTO "ahmeti" VALUES(36,24,'36:24','Unë do të jem atëherë në një humbje të hapët.');
INSERT INTO "ahmeti" VALUES(36,25,'36:25','Unë i kam besuar Zotit tuaj, pra më dëgjoni!”');
INSERT INTO "ahmeti" VALUES(36,26,'36:26','Atij i është thënë: “Hyn në Xhennet! e ai tha: “Ah, sikur ta dinte populli im,');
INSERT INTO "ahmeti" VALUES(36,27,'36:27','për çka më fali Zoti im dhe më bëri prej të nderuarve!”');
INSERT INTO "ahmeti" VALUES(36,28,'36:28','Pas tij, Ne nuk zbritëm ndonjë ushtri nga qieeli kundër popullit të tij, e as që ishte vendimi yni të zbresim tjetër,');
INSERT INTO "ahmeti" VALUES(36,29,'36:29','përveç një britmë të tmerrshme, kur qe, ata të ftohur (të vdekur).');
INSERT INTO "ahmeti" VALUES(36,30,'36:30','O sa dëshprim i madh për robërit që nuk u erdhi ndonjë i dërguar, vetëm se ata u tallën me të.');
INSERT INTO "ahmeti" VALUES(36,31,'36:31','A nuk e panë ata se sa gjenerata kemi zhdukur para tyre dhe ata nuk u kthyen.');
INSERT INTO "ahmeti" VALUES(36,32,'36:32','Dhe të gjithë, të tubuar, do të paraqiten pranë Nesh.');
INSERT INTO "ahmeti" VALUES(36,33,'36:33','Dhe për ata është argument (për fuqinë e Zotit) toka e vdekur, të cilën Ne e ngjallim, nxjerrim prej saj drith nga i cili ata hanë.');
INSERT INTO "ahmeti" VALUES(36,34,'36:34','Dhe në të Ne u kemi bërë kpshte hurmash e rrushi dhe në të u kemi dhënë burime (uji).');
INSERT INTO "ahmeti" VALUES(36,35,'36:35','Ashtu që ata të hanë nga ata fruta dhe nga çka prodhojnë vetë duart e tyre. A i gëzojnë këto dhe nik falënderojnë.');
INSERT INTO "ahmeti" VALUES(36,36,'36:36','I pastër nga të metat është Ai që krijoi të gjitha llojet (çiftet) nga çka mban toka, nga vetë ata dhe nga çka ata nuk dinë.');
INSERT INTO "ahmeti" VALUES(36,37,'36:37','Për ata është argument edhe nata, prej të cilës largojmë ditën, kurse ata mbesin në terr.');
INSERT INTO "ahmeti" VALUES(36,38,'36:38','Edhe dielli udhëton për në kufirin e vet (në cakun përfundimtar). Ai është (udhëtim) përcaktim i ngadhnjyesit, të dijshmit.');
INSERT INTO "ahmeti" VALUES(36,39,'36:39','Edhe hënës i kemi caktuar (pozicionet) derisa të kthehet në trajtën e harku (rrem i hurmës së tharë).');
INSERT INTO "ahmeti" VALUES(36,40,'36:40','As dielli nuk mund ta arrijë hënën, e as nata para ditës po secili noton në një galaksion.');
INSERT INTO "ahmeti" VALUES(36,41,'36:41','Argument për ta është edhe ajo se Ne pasardhësit e tyre (të Ademit) i vartëm në anije të mbushur përplot.');
INSERT INTO "ahmeti" VALUES(36,42,'36:42','Dhe ngjashëm me të, u krijuam atyre diçka që t’i hipin.');
INSERT INTO "ahmeti" VALUES(36,43,'36:43','E sikur të duam Ne, i i përmbytim ata, e nuk ka efekt as lutja e tyre dhe as që do të shpëtonin.');
INSERT INTO "ahmeti" VALUES(36,44,'36:44','Përveç nga mëshira Jonë ndaj tyre, dhe që të përjetojnë të mirat deri në një kohë të caktuar.');
INSERT INTO "ahmeti" VALUES(36,45,'36:45','E kur u thuhet atyre: “Kini frikë asaj që ngjau para jush dhe asaj çka do t’ju ngjajë më vonë, e që të mëshiroheni (ata nuk dëgjojnë).');
INSERT INTO "ahmeti" VALUES(36,46,'36:46','Nuk ka asnjë argument që u erdhi atyre nga argumentet e Zotit, e që ata nuk ia kthyen shpinën.');
INSERT INTO "ahmeti" VALUES(36,47,'36:47','Dhe kur u thuhej: “Jepni nga ajo që All-llahu u begatoi, ata që nuk besuan, besimtarëve u thanë: “A ta ushqejmë atë që sikur të donte All-llahu do ta ushqente? Ju nuk jeni tjetër vetëm se të humbur qartë!”.');
INSERT INTO "ahmeti" VALUES(36,48,'36:48','Dhe thoshin: “Po qe se jeni të vërtetë, kur do të jetë ai premtim?”');
INSERT INTO "ahmeti" VALUES(36,49,'36:49','(All-llahu u përgjigjet) Nuk janë duke pritur tjetër vetëm se një britmë që i rrëmben ata kur janë duke u zënë mes vete.');
INSERT INTO "ahmeti" VALUES(36,50,'36:50','E nuk do të mund të lënë as porosi (vasijet-testament) as të kthehen në familjet e tyre.');
INSERT INTO "ahmeti" VALUES(36,51,'36:51','Dhe i fryhet Surit, kur qe, duke u ngutur prej varrezave paraqiten te Zoti i tyre.');
INSERT INTO "ahmeti" VALUES(36,52,'36:52','E thonë: “Të mjerët ne! Po kush na ngriti prej ku ishim të shtrirë në varre?” E, kjo është ajo që premtoi Zoti dhe vëtetuan të dërguarit.');
INSERT INTO "ahmeti" VALUES(36,53,'36:53','Ajo nuk është tjetër, përveç një zë i fuqishëm dhe ata të gjithë të paraqitur para Nesh.');
INSERT INTO "ahmeti" VALUES(36,54,'36:54','Sot pra, akujt nuk i bëhet e padrejtë diçka dhe nuk shpërbleheni me tjeër vetëm për atë që vepruat.');
INSERT INTO "ahmeti" VALUES(36,55,'36:55','Ata të Xhennetit tash janë të angazhuar me kënaqësi.');
INSERT INTO "ahmeti" VALUES(36,56,'36:56','Ata dhe shoqet e tyre janë nën hijet të mbështetur në kolltukë.');
INSERT INTO "ahmeti" VALUES(36,57,'36:57','Aty kanë pemë dhe çka të duan.');
INSERT INTO "ahmeti" VALUES(36,58,'36:58','Kanë “Selam”, thënie e Zotit mëshirues!');
INSERT INTO "ahmeti" VALUES(36,59,'36:59','E tash, ju kriminelë, ndahuni!');
INSERT INTO "ahmeti" VALUES(36,60,'36:60','O bijtë e Ademit (kriminelë), po a nuk u dërgova porosinë që të mos e dëgjoni djallin, se me të vërtetë ai është armiku juaj i hapët!?');
INSERT INTO "ahmeti" VALUES(36,61,'36:61','*Ju porosita) Të më adhuroni Mua, se kjo është rruga e sigurt!');
INSERT INTO "ahmeti" VALUES(36,62,'36:62','Vërtet, ai ka humbur shumë njerëz prej jush, a nuk mblodhët mend?');
INSERT INTO "ahmeti" VALUES(36,63,'36:63','Ky është Xhehennemi që juve u premtohe.');
INSERT INTO "ahmeti" VALUES(36,64,'36:64','Hyni tash në të, për shkak se mohuaat çdo të vërtetë!');
INSERT INTO "ahmeti" VALUES(36,65,'36:65','Sot Ne ua mbyllim gojët e atyre, Neve na flasin duart e tyre, kurse këmbët e tyre dëshmojën për atë që punuan.');
INSERT INTO "ahmeti" VALUES(36,66,'36:66','E sikur të duam Ne do t’ua verbonim ytë e tyre, e ata do ta mësynin rrugën, po si do të dhihnin?');
INSERT INTO "ahmeti" VALUES(36,67,'36:67','Dhe sikur të duam, do t’i kishim gjymtuar ata në vend, e nuk do tëmund të shkoninas para as prapa.');
INSERT INTO "ahmeti" VALUES(36,68,'36:68','E atij që i japim të jetojë gjatë, e kthejmë prapa në krijimin e tij. A nuk janë duke menduar?');
INSERT INTO "ahmeti" VALUES(36,69,'36:69','Ne as nuk ia mësuam atij (Muhammedit) poezinë, e as që i takon ajo atij, ai (Kur’ani) nuk është tjetër vetëm se këshillë dhe Kur’an i qartë.');
INSERT INTO "ahmeti" VALUES(36,70,'36:70','Për t’ia tërhequr vërejtjen ati që është gjallë (me mend e zemër) dhe dënimi të bëhet meritë për jobesimtarët.');
INSERT INTO "ahmeti" VALUES(36,71,'36:71','A nuk e shohin ata se nga ajo që Ne vetë e shpikëm, u krijuam atyre kafshët që i kanë.');
INSERT INTO "ahmeti" VALUES(36,72,'36:72','Dhe ua bëmë ato që t’u biden atyre, e disave prej tyre u hipnin, ndërsa prej disave ushqehen.');
INSERT INTO "ahmeti" VALUES(36,73,'36:73','Ata kanë edhe dobi të tjera në to, e edhe pinë (qumësht) prej tyre. A nuk duhet falënderojnë?');
INSERT INTO "ahmeti" VALUES(36,74,'36:74','Po, shkojnë e në vend të All-llahut adhurojnë zota të tjerë me shpresë se do të ndihmohen prej tyre.');
INSERT INTO "ahmeti" VALUES(36,75,'36:75','Ata nuk mund t’u ndihmojnë atyre, por këta (idhujtarët) u janë bërë ushtri e gatshme e tyre.');
INSERT INTO "ahmeti" VALUES(36,76,'36:76','E ty mos të brengosin thëniet e tyre, Ne dimë çka mbajnë ata fshehtë dhe çka publikojnë.');
INSERT INTO "ahmeti" VALUES(36,77,'36:77','A nuk mendon njeriu se Ne e krijuam atë prej një pike uji (fare), kur qe, ai kundërshtarë i rreptë.');
INSERT INTO "ahmeti" VALUES(36,78,'36:78','Ai na solli Neve shembull, e harroi krijimin e vet e tha: “Kush i ngjall eshtrat duke qenë ata të kalbur?');
INSERT INTO "ahmeti" VALUES(36,79,'36:79','Thuaj: “I ngjall Ai që i krijoi për herë të parë, e Ai është shumë i dijshëm për çdo krijim.');
INSERT INTO "ahmeti" VALUES(36,80,'36:80','Ai që prej drurit të gjelbër ju bëri zjarrin, e ju prej tij ndezni.');
INSERT INTO "ahmeti" VALUES(36,81,'36:81','A nuk është i fuqishëm Ai, që kerijoi qiejt e tokën, të krijojë njerëz sikundër që i krijoi ata? Po Ai është krijuesi, i dijshmi.');
INSERT INTO "ahmeti" VALUES(36,82,'36:82','Kur Ai dëshiron ndonjë send, urdhëri i Tij është vetëm t’i thotë: “Bëhu!” Ai menjëherë bëhet.');
INSERT INTO "ahmeti" VALUES(36,83,'36:83','I lartë është Ai ë në dorën e Tij është pushteti mbi çdo send dhe vetëm te Ai ktheheni.');
INSERT INTO "ahmeti" VALUES(37,1,'37:1','Pasha të rradhiturit (melaiket), që në rreshta (safa) qëndrojnë.');
INSERT INTO "ahmeti" VALUES(37,2,'37:2','Dhe nxituesit, që me nxitim shtyjnë.');
INSERT INTO "ahmeti" VALUES(37,3,'37:3','Dhe pasha lexuesit, që lexojnë përkujtimin.');
INSERT INTO "ahmeti" VALUES(37,4,'37:4','Është e vërtetë se Zoti juaj është vetëm Një.');
INSERT INTO "ahmeti" VALUES(37,5,'37:5','Zot i qiejve dhe i tokës e çka ka mes tyre dhe Zot i lindjeve (të yjeve).');
INSERT INTO "ahmeti" VALUES(37,6,'37:6','Vërtetë, Ne kemi stolisur qiellin më të afërt (të dynjasë) me bukurinë e yjeve.');
INSERT INTO "ahmeti" VALUES(37,7,'37:7','Dhe me mbrojtje prej çdo djalli të prishur.');
INSERT INTO "ahmeti" VALUES(37,8,'37:8','Ashtu që nuk mund të përgjojnë parinë më të lartë (engjëjt më të zgjedhur), pse gjuhen me shkëndija nga të gjitha anët.');
INSERT INTO "ahmeti" VALUES(37,9,'37:9','Ata janë të përzënë dhe do të kenë një dënim të përhershëm.');
INSERT INTO "ahmeti" VALUES(37,10,'37:10','Përveç atij që rrëmben vrullshëm, po atë e ndjek ylli që e djeg.');
INSERT INTO "ahmeti" VALUES(37,11,'37:11','Ti pra, pyeti ata (idhujtarët): a janë ata krijesë më e fortë, apo çka Ne krijuam. Ne i krijuam ata prej një balte që ngjitet.');
INSERT INTO "ahmeti" VALUES(37,12,'37:12','Por ti je i habitur, e ata tallen.');
INSERT INTO "ahmeti" VALUES(37,13,'37:13','E kur këshillohen, ata nuk marrin parasysh këshillën.');
INSERT INTO "ahmeti" VALUES(37,14,'37:14','E kur shohin ndonjë mrekulli, ata nxitin në përqeshje.');
INSERT INTO "ahmeti" VALUES(37,15,'37:15','Dhe thanë: “Ky (Kur’ani) nuk është tjetër vetëmn se magji e kulluar.');
INSERT INTO "ahmeti" VALUES(37,16,'37:16','A, pasi të vdesim ne, të bëhemi dhe e eshtra të kalbur a do të ringjallemi?');
INSERT INTO "ahmeti" VALUES(37,17,'37:17','A edhe të parët tanë të herëshëm?');
INSERT INTO "ahmeti" VALUES(37,18,'37:18','thuaj: “Po, bile ju do të jeni të nënçmuar!”');
INSERT INTO "ahmeti" VALUES(37,19,'37:19','Ajo do të jetë vetëm një britmë, kur që, ata të ngritur shikojnë.');
INSERT INTO "ahmeti" VALUES(37,20,'37:20','E thonë: “O, të mjerët ne, kjo është dita e gjykimit!”');
INSERT INTO "ahmeti" VALUES(37,21,'37:21','Kjo është dita e ndasisë që ju e konsideruat rrenë.');
INSERT INTO "ahmeti" VALUES(37,22,'37:22','Tuboni ata që ishin zullumqarë, shoqërinë e tyre dhe ata që i adhuruan.');
INSERT INTO "ahmeti" VALUES(37,23,'37:23','(adhuruan) Pos All-llahut, orientoni rrugës së xhehimit!');
INSERT INTO "ahmeti" VALUES(37,24,'37:24','Ndalni ata, sepse do të merren në përgjegjësi.');
INSERT INTO "ahmeti" VALUES(37,25,'37:25','Çka keni që nuk ndihmoni njëri-tjetrin?');
INSERT INTO "ahmeti" VALUES(37,26,'37:26','E, ata sot janë dorëzuar në tërësi.');
INSERT INTO "ahmeti" VALUES(37,27,'37:27','E kthehen e ia hedhin përgjegjësinë njëri-tjetrit.');
INSERT INTO "ahmeti" VALUES(37,28,'37:28','(Të shtypurit) u thonë (atyre të parëve): “Ju ishit që na vinit neve nga ana e djathtë (na pengonit pre së vërtetës).');
INSERT INTO "ahmeti" VALUES(37,29,'37:29','Ata (paria) u thonë: “Jo, ju vetë nuk ishit besimtarë.');
INSERT INTO "ahmeti" VALUES(37,30,'37:30','Ne nuk kemi pasur ndonjë pushtet ndaj jush, por ju vetë ishit që nuk respektuat (porositë e Zotit).');
INSERT INTO "ahmeti" VALUES(37,31,'37:31','E, ajo thënia (premtimi) e Zotit tonë u vërtetua kundër nesh, e s’ka dyshim se ne po e shijojmë (dënimin).');
INSERT INTO "ahmeti" VALUES(37,32,'37:32','Ne u ofruam juve rrugën e humbjes, ashtu sikurse edhe vetë ishim të humbur.');
INSERT INTO "ahmeti" VALUES(37,33,'37:33','Dhe atë ditë ata do të jenë në dënim të përbashkët.');
INSERT INTO "ahmeti" VALUES(37,34,'37:34','Kështu Ne veprojmë me kriminelët.');
INSERT INTO "ahmeti" VALUES(37,35,'37:35','Për arsye se kur u thuhej atyre: “Nuk ka Zottjetër përveç All-llahut, ata e mbanin veten lart.');
INSERT INTO "ahmeti" VALUES(37,36,'37:36','Dhe thoshin: “A do t’i braktisim ne zotat tanë për një poet të çmendur?”');
INSERT INTO "ahmeti" VALUES(37,37,'37:37','Jo, (nuk është çka thonë ata) por ai u solli të vërtetën dhe vërtetoi të dërguarit e parë.');
INSERT INTO "ahmeti" VALUES(37,38,'37:38','Ju, pa tjetër do të përjetoni dënimin më të ashpër.');
INSERT INTO "ahmeti" VALUES(37,39,'37:39','Dhe nuk ndëshkoheni për tjetër, përveç për atë që vepruat.');
INSERT INTO "ahmeti" VALUES(37,40,'37:40','Me përjashtim të robëve të All-llahut që ishin të sinqertë,');
INSERT INTO "ahmeti" VALUES(37,41,'37:41','Të tillët janë ata që kanë furnizim të dalluar,');
INSERT INTO "ahmeti" VALUES(37,42,'37:42','Pemë të llojllojshme, ata janë të nderuar,');
INSERT INTO "ahmeti" VALUES(37,43,'37:43','Në Xhennete të begatshme,');
INSERT INTO "ahmeti" VALUES(37,44,'37:44','Të mbështetur në kolltukë me fytyrë nga njëri-tjetri,');
INSERT INTO "ahmeti" VALUES(37,45,'37:45','Atyre u bëhrt shërbim me gota (me verë) nga burmi,');
INSERT INTO "ahmeti" VALUES(37,46,'37:46','E barshë (vera), që ka shije për ata që e pinë,');
INSERT INTO "ahmeti" VALUES(37,47,'37:47','Prej asaj nuk ka dhembje koke, e as që ata do të dehen nga ajo,');
INSERT INTO "ahmeti" VALUES(37,48,'37:48','E pranë tyre janë (hyritë) symëdhatë me shikim të pëulur,');
INSERT INTO "ahmeti" VALUES(37,49,'37:49','Sikur ato të jenë inxhi e paprekur (ve e ruajtur).');
INSERT INTO "ahmeti" VALUES(37,50,'37:50','I qasen njëri-tjetrit e bisedojnë,');
INSERT INTO "ahmeti" VALUES(37,51,'37:51','Prej tyre njëri flet e thotë: “Unë kam pasur njëfarë miku,');
INSERT INTO "ahmeti" VALUES(37,52,'37:52','thoshte: “A je ti pre atyre që besojnë se');
INSERT INTO "ahmeti" VALUES(37,53,'37:53','Kur të jemi të vdekur, të jemi bërë dhe e eshtra të kalbur, do të jemi të shpërblyer për vepra?”');
INSERT INTO "ahmeti" VALUES(37,54,'37:54','Ai (besimtari në Xhennet) thotë: “A vini ju të shikojmë?”');
INSERT INTO "ahmeti" VALUES(37,55,'37:55','Ai shikon dhe e vëren atë (mikun) në mes të Xhehennemit.');
INSERT INTO "ahmeti" VALUES(37,56,'37:56','Ai thotë: “Pasha All-llahun, për pak më rrëzove (në Xhehennem) edhe mua.');
INSERT INTO "ahmeti" VALUES(37,57,'37:57','Dhe sikur të mos ishte dhuntia e Zotit tim, unë do të isha bashkë me ty në zjarr,');
INSERT INTO "ahmeti" VALUES(37,58,'37:58','Dhe ne nuk do të vdesim më,');
INSERT INTO "ahmeti" VALUES(37,59,'37:59','Përveç asaj vdekjes sonë të parë dhe ne nuk do të dënohemi më!”');
INSERT INTO "ahmeti" VALUES(37,60,'37:60','Vërtet, ky është ai suksesi i madh,');
INSERT INTO "ahmeti" VALUES(37,61,'37:61','Për një shpërblim të këtillë le të veprojnë vepruesit!');
INSERT INTO "ahmeti" VALUES(37,62,'37:62','A kjo pritje (me shpërbllim të All-llahut) është më e mirë, apo pema e “Zekumë-it”?');
INSERT INTO "ahmeti" VALUES(37,63,'37:63','Ne atë e kemi bërë sprovë për zullumqarët.');
INSERT INTO "ahmeti" VALUES(37,64,'37:64','Ajo është një pemë që mbin në fund të Xhehennemit.');
INSERT INTO "ahmeti" VALUES(37,65,'37:65','Pema (fruti) e saj është sikurse kok a dreqërish.');
INSERT INTO "ahmeti" VALUES(37,66,'37:66','E ata do të hanë nga ajo dhe do të mbushin barqet prej saj.');
INSERT INTO "ahmeti" VALUES(37,67,'37:67','Pastaj, ata do të kenë kundrejt atij ushqimi edhe ujë të valë.');
INSERT INTO "ahmeti" VALUES(37,68,'37:68','Mandej kthimi i tyre është në Xhehennem.');
INSERT INTO "ahmeti" VALUES(37,69,'37:69','Ata i gjetën dhe shkuan pas prindërve të tyre të humbur.');
INSERT INTO "ahmeti" VALUES(37,70,'37:70','Ata u ngutën dhe shkelën hapave të tyre (pa menduar).');
INSERT INTO "ahmeti" VALUES(37,71,'37:71','Po, edhe para këtyre (popullit tënd) shunica e popujve të kaluar ishin të humbur.');
INSERT INTO "ahmeti" VALUES(37,72,'37:72','Ne atyre u patëm dërguar pejgamberë.');
INSERT INTO "ahmeti" VALUES(37,73,'37:73','E shif se si ishte përfundimi i atyre, të cilëve u qe tërhequr vërejtja.');
INSERT INTO "ahmeti" VALUES(37,74,'37:74','Me përjashtim të robëve të All-llahut që ishin të sinqertë.');
INSERT INTO "ahmeti" VALUES(37,75,'37:75','Për Zotin Neve na pat thirr në ndihmë Nuhu, Ne jemi përgjegjës të mirë.');
INSERT INTO "ahmeti" VALUES(37,76,'37:76','Dhe Ne e shpëtuam atë dhe familjen e tij prej asaj të keqe të madhe.');
INSERT INTO "ahmeti" VALUES(37,77,'37:77','E pasardhësit e tij i bëmë ata që vazhduan jetën.');
INSERT INTO "ahmeti" VALUES(37,78,'37:78','Dhe Ne ua lamë atë kujtim për njrëzit e ardhshëm.');
INSERT INTO "ahmeti" VALUES(37,79,'37:79','Selam i qoftë Nuhut (prej All-llahut e prej krijesave) në mbarë botën (si kujtim ndaj tij).');
INSERT INTO "ahmeti" VALUES(37,80,'37:80','Ne kështu i shpërblejmë të mirët.');
INSERT INTO "ahmeti" VALUES(37,81,'37:81','Vërtet, ai ishte prej robëve tanë që janë besimtarë.');
INSERT INTO "ahmeti" VALUES(37,82,'37:82','E pastaj i përmbytëm në një ujë të tjerët (që nuk besuan).');
INSERT INTO "ahmeti" VALUES(37,83,'37:83','Edhe Ibrahimi ishte i grupit të tij.');
INSERT INTO "ahmeti" VALUES(37,84,'37:84','Kur Zotit të vet iu bind me zemër të pastër.');
INSERT INTO "ahmeti" VALUES(37,85,'37:85','Kur babait të vet dhe popullit të vet i tha: “çka është ajo që ju adhuroni?”');
INSERT INTO "ahmeti" VALUES(37,86,'37:86','A në vend të All-llahut doni zota të trilluar?');
INSERT INTO "ahmeti" VALUES(37,87,'37:87','çka është mendimi juaj ndaj Zotit të botëce?');
INSERT INTO "ahmeti" VALUES(37,88,'37:88','Dhe atëherë u lëshoi një shikim yjeve,');
INSERT INTO "ahmeti" VALUES(37,89,'37:89','E tha: “Unë jam i sëmurë!”');
INSERT INTO "ahmeti" VALUES(37,90,'37:90','Ata u kthyen dhe u larguan prek tij.');
INSERT INTO "ahmeti" VALUES(37,91,'37:91','Ai u drejtua kah zotat e tyre dhe u tha: “A nuk po hani ju?”');
INSERT INTO "ahmeti" VALUES(37,92,'37:92','çka keni pra, që nuk po flitni?');
INSERT INTO "ahmeti" VALUES(37,93,'37:93','Iu afrua atyre ngadalë, duke u mëshuar me të djathtën (me fuqi).');
INSERT INTO "ahmeti" VALUES(37,94,'37:94','Ata (populli) iu afruan atij me të shpejtë (sigurisht e qortuan).');
INSERT INTO "ahmeti" VALUES(37,95,'37:95','Ai (Ibrahimi) tha: “A adhuroni atë që vetë e keni gdhendur?”');
INSERT INTO "ahmeti" VALUES(37,96,'37:96','E All-llahu ju krijoi juve edhe atë që e punoni.');
INSERT INTO "ahmeti" VALUES(37,97,'37:97','Ata thanë: “Ndërtonja atij një vend dhe hudhne atë në zjarr!');
INSERT INTO "ahmeti" VALUES(37,98,'37:98','Ata i menduan atij një kurth, kurse Ne i mposhtëm ata të nënçmuar”.');
INSERT INTO "ahmeti" VALUES(37,99,'37:99','Ai tha: “Unë po shkoj aty ku më urdhëroi Zoti im, e Ai më udhëzon!”');
INSERT INTO "ahmeti" VALUES(37,100,'37:100','Zoti im, më dhuro mua (një fëmijë) prej të mirëve!');
INSERT INTO "ahmeti" VALUES(37,101,'37:101','Ne e gëzuam atë me një djalë që do të jetë i butë (i sjëllshëm).');
INSERT INTO "ahmeti" VALUES(37,102,'37:102','Dhe kur arriti ai (djalli) që së bashku me të (me Ibrahimin) të angazhohet në punë, ai (Ibrahimi) tha: “O djalli im, unë kam parë (jam urdhëruar) në ëndërr të pres ty. Shiko pra, çka mendon ti?” Ai tha: “O babai im, punoje atë që urdhërohesh, e ti do të më gjesh mua, nëse do All-llahu, prej të durueshmëve!”');
INSERT INTO "ahmeti" VALUES(37,103,'37:103','E kur ata të dy iu dorëzuan urdhërit të Zotit dhe përmbysi atë në fytyrë (në ballë).');
INSERT INTO "ahmeti" VALUES(37,104,'37:104','Ne e thirrëm atë: “O Ibrahim!”');
INSERT INTO "ahmeti" VALUES(37,105,'37:105','Ti tashmë e zbatove ëndrrën! Ne kështu i shpërblejmë të mirët!');
INSERT INTO "ahmeti" VALUES(37,106,'37:106','Vërtet, kjo ishte sprovë e qartë.');
INSERT INTO "ahmeti" VALUES(37,107,'37:107','Ne e shpaguam atë me një të therrur (kurban) të rëndësishëm.');
INSERT INTO "ahmeti" VALUES(37,108,'37:108','Dhe ndaj tij Ne lamë përkujtim të mirë ndër popujt e ardhshëm.');
INSERT INTO "ahmeti" VALUES(37,109,'37:109','Selam (shpëtim e paqe) pastë Ibrahimi!');
INSERT INTO "ahmeti" VALUES(37,110,'37:110','Kështu, në këtë mënyrë Ne i shpërblejmë bamirësit.');
INSERT INTO "ahmeti" VALUES(37,111,'37:111','Vërtet, ai ishte nga robërit Tanë besimtarë.');
INSERT INTO "ahmeti" VALUES(37,112,'37:112','Dhe Ne e gëzuam atë me (një djalë tjetër) Is-hakun, pejgamber prej të mirëve.');
INSERT INTO "ahmeti" VALUES(37,113,'37:113','Dhe Ne i dhuruan bekim atij dhe Is-hakut e prej pasardhësve të atyre dyve do të kenë punëmirë e të ndershëm, e edhe dëmtues t hapët të vetvetes.');
INSERT INTO "ahmeti" VALUES(37,114,'37:114','Pasha madhërinë Tonë, Ne u dhamë të mira Musait e Harunit.');
INSERT INTO "ahmeti" VALUES(37,115,'37:115','I shpëtuam ata dhe popullin e tyre prej një mjerimi të madh.');
INSERT INTO "ahmeti" VALUES(37,116,'37:116','Ne u ndihmuam atyre dhe ata ngadhënjyen.');
INSERT INTO "ahmeti" VALUES(37,117,'37:117','Atyre dyve u hamë librin e përsosur e të qartë.');
INSERT INTO "ahmeti" VALUES(37,118,'37:118','Dhe të dy ata i udhëzuam rrugës së drejtë.');
INSERT INTO "ahmeti" VALUES(37,119,'37:119','Përkujtim të këndshëm ndaj të dyve kem lënë në popujt e më vonshëm.');
INSERT INTO "ahmeti" VALUES(37,120,'37:120','(Përkujtimin) “Selamun” - qofshin të mëshiruar Musai dhe Haruni.');
INSERT INTO "ahmeti" VALUES(37,121,'37:121','Kështu, në këtë mënyrë Ne i shpërblejmë bamirësit.');
INSERT INTO "ahmeti" VALUES(37,122,'37:122','Vërtet, ata të dy ishin besimtarë nga robërit Tanë.');
INSERT INTO "ahmeti" VALUES(37,123,'37:123','Edhe Iljasi ishte prej të dërguarve Tanë.');
INSERT INTO "ahmeti" VALUES(37,124,'37:124','Kur ai, popullit të vet i tha: “A nuk jeni kah frikësoheni?”');
INSERT INTO "ahmeti" VALUES(37,125,'37:125','A e adhuroni “Ba’ël-in” (emër i një statuje) e braktisni adhurimin ndaj ë mirit që është Krijues?');
INSERT INTO "ahmeti" VALUES(37,126,'37:126','All-llahun, Zotin tuaj e të prindërve tuaj të hershëm!!”');
INSERT INTO "ahmeti" VALUES(37,127,'37:127','Ata e përgënjeshtruan, andaj ata medoemos janë të sjellë në Xhehennem.');
INSERT INTO "ahmeti" VALUES(37,128,'37:128','Me përjashtim të robërve besnikë ndaj All-llahut.');
INSERT INTO "ahmeti" VALUES(37,129,'37:129','Edhe ndaj tij kemi lënë përkujtim të mirë në të ardhshmit.');
INSERT INTO "ahmeti" VALUES(37,130,'37:130','“Selamun” - qoftë i mëshiruar Iljasi (ose edhe besimtarët e Iljasit).');
INSERT INTO "ahmeti" VALUES(37,131,'37:131','Kështu, në këtë mënyrë Ne i shpërblejmë bamirësit.');
INSERT INTO "ahmeti" VALUES(37,132,'37:132','S’ka dyshim, ai ishte besimtar nga robërit Tanë.');
INSERT INTO "ahmeti" VALUES(37,133,'37:133','Edhe Luti, pa mëdyshje ishte prej të dërguarve Tanë.');
INSERT INTO "ahmeti" VALUES(37,134,'37:134','Kur Ne e shpëtuam atë dhe tërë familjen e tij bashkë.');
INSERT INTO "ahmeti" VALUES(37,135,'37:135','Përveç një plake (grua e tij) që mbeti me të dënuarit.');
INSERT INTO "ahmeti" VALUES(37,136,'37:136','Pastaj të tjerët i rrënuam.');
INSERT INTO "ahmeti" VALUES(37,137,'37:137','E ju (mekas) me siguri kaloni atypari mëngjes (ditën)');
INSERT INTO "ahmeti" VALUES(37,138,'37:138','e mbrëmje (natën). Pra, a nuk mbildhni mend?');
INSERT INTO "ahmeti" VALUES(37,139,'37:139','Edhe Junusi ishte një nga të dërguarit Tanë.');
INSERT INTO "ahmeti" VALUES(37,140,'37:140','kur iku te anija që ishte e mbushur plot (udhëtarë).');
INSERT INTO "ahmeti" VALUES(37,141,'37:141','E ai mori pjesë në short, po humbi (i ra sorti atij).');
INSERT INTO "ahmeti" VALUES(37,142,'37:142','Dhe atë e kafshoi (e gëlltit) peshku, zatën ai ishte që e meritoi qortimin.');
INSERT INTO "ahmeti" VALUES(37,143,'37:143','E sikur të mos ishte ajo që ai kishte qenë prej atyre që shumë e përmendin Zotin.');
INSERT INTO "ahmeti" VALUES(37,144,'37:144','Ai do të mbetej në barkun e tij deri në ditën e ringjalljes.');
INSERT INTO "ahmeti" VALUES(37,145,'37:145','E Ne e hodhëm në një tokë pa bimë (shkretëtirë), ndërsa ai ishte i sëmurë.');
INSERT INTO "ahmeti" VALUES(37,146,'37:146','Dhe Ne bëmë që për të të mbijë një bimë (t’i bëjë hije) nga kungulli.');
INSERT INTO "ahmeti" VALUES(37,147,'37:147','Ne (pastaj) e dërguam atë te njëqindmijë e më shumë.');
INSERT INTO "ahmeti" VALUES(37,148,'37:148','E ata i besuan, e Ne ua vazhduam atyre të përjetojnë për deri në një kohë.');
INSERT INTO "ahmeti" VALUES(37,149,'37:149','E ti (Muhammed) pyeti ata (idhujtarët): “A të Zotit tënd janë vajzat, kurse të tyre djemtë?”');
INSERT INTO "ahmeti" VALUES(37,150,'37:150','Apo, Ne i krijuam endjëjt femra, e ata ishin dëshmitarë (kur Ne i krijuam engjëjt femra)?!');
INSERT INTO "ahmeti" VALUES(37,151,'37:151','Vini re se si ata nga trillimet e tyre thonë:');
INSERT INTO "ahmeti" VALUES(37,152,'37:152','“All-llahu ka lindë!” S’ka dyshim se ata janë gënjeshtarë (kur thonë se engjëjt janë bijat e Zotit).');
INSERT INTO "ahmeti" VALUES(37,153,'37:153','A thua vajzat Ai i ka bërë më të zgjedhura se djemt?');
INSERT INTO "ahmeti" VALUES(37,154,'37:154','Po ç’keni ju kështu, si po gjykoni ashtu?');
INSERT INTO "ahmeti" VALUES(37,155,'37:155','A nuk jeni duke menduar?');
INSERT INTO "ahmeti" VALUES(37,156,'37:156','A mos keni ju ndonjë argument të sigurt?');
INSERT INTO "ahmeti" VALUES(37,157,'37:157','Sillnie pra, librin tuaj, nëse është si thoni ju!');
INSERT INTO "ahmeti" VALUES(37,158,'37:158','Ata (idhujtarët) pohuan mes Tij e mes engjëjve (xhinëve) lidhmëri farefisnore, po xhinët e dinë se ata (idhujtarët) janë të hedhur në zjarr.');
INSERT INTO "ahmeti" VALUES(37,159,'37:159','All-llahu është i pastër nga ajo çka i përshkruanë.');
INSERT INTO "ahmeti" VALUES(37,160,'37:160','Ata (engjëjt) janë vetëm robër të sinqertë të Zotit (ata nuk i përshkruajnë gjë).');
INSERT INTO "ahmeti" VALUES(37,161,'37:161','E, as ju dhe as ata që i adhuroni,');
INSERT INTO "ahmeti" VALUES(37,162,'37:162','Nuk mund ta vini në sprovë askë ndaj Atij (besimit në Zotin),');
INSERT INTO "ahmeti" VALUES(37,163,'37:163','Përveç atij që është i gjykuar për Xhehennem.');
INSERT INTO "ahmeti" VALUES(37,164,'37:164','E, nuk ka prej nesh (grupit të engjëjve) që nuk e ka vendin (detyrë, pozitën) e vet të njohur.');
INSERT INTO "ahmeti" VALUES(37,165,'37:165','Edhe ne (engjëjt) jemi të rreshtuar (në adhurim),');
INSERT INTO "ahmeti" VALUES(37,166,'37:166','Dhe ne jemi që i bëjmë tesbih (mohojmë se ai ka të meta).');
INSERT INTO "ahmeti" VALUES(37,167,'37:167','Po edhe pse ata ishin që thoshin:');
INSERT INTO "ahmeti" VALUES(37,168,'37:168','Sikur të gjendej te ne ndonjë libër nga të parët,');
INSERT INTO "ahmeti" VALUES(37,169,'37:169','Ne do të ishim robër të All-llahut, të sinqertë.');
INSERT INTO "ahmeti" VALUES(37,170,'37:170','Po (kur u erdhi libri), ata e mohuan atë (Kur’anin), e më vonë do të kuptojnë.');
INSERT INTO "ahmeti" VALUES(37,171,'37:171','E tashmë fjala (premtimi) e jonë u është dhënë më parë robërve tanë të dërguar,');
INSERT INTO "ahmeti" VALUES(37,172,'37:172','se ata, pa dyshim do të jenë të ndihmuar.');
INSERT INTO "ahmeti" VALUES(37,173,'37:173','dhe se ushtria jonë do të jenë ata ngadhënjyesit.');
INSERT INTO "ahmeti" VALUES(37,174,'37:174','Prandaj, për një kohë, ti (Muhammed) hiqu tyre.');
INSERT INTO "ahmeti" VALUES(37,175,'37:175','E ti vështroji ata se edhe ata do ta shohin (ndihmën tonë ndaj juve besimtarëve).');
INSERT INTO "ahmeti" VALUES(37,176,'37:176','A mos po kërkojnë shpejtimin e ndëshkimit tonë?');
INSERT INTO "ahmeti" VALUES(37,177,'37:177','E kur të vjen ai (dënimi) në territorin e tyre, mëngjes i shëmtuar do të jetë për ata që u ishte tërhrqur vërejtja.');
INSERT INTO "ahmeti" VALUES(37,178,'37:178','E ti largohu për një kohë prej tyre,');
INSERT INTO "ahmeti" VALUES(37,179,'37:179','Dhe ti vështroi se edhe ata më vonë do të shohin.');
INSERT INTO "ahmeti" VALUES(37,180,'37:180','I Lartësuar është Zoti yt, Zot i fuqisë nga ajo që ia përshkruajnë.');
INSERT INTO "ahmeti" VALUES(37,181,'37:181','Qoftë paqja mbi të dërguarit.');
INSERT INTO "ahmeti" VALUES(37,182,'37:182','Dhe falënderimi i takon All-llahut, Zotit të botërave!');
INSERT INTO "ahmeti" VALUES(38,1,'38:1','Sad. Pasha Kur’anin që është përplot përkujtime (është i famshëm dhe s’ka dyshim se është mrekulli).');
INSERT INTO "ahmeti" VALUES(38,2,'38:2','Por ata që nuk besuan janë në kryeneçsi e kundërshtim.');
INSERT INTO "ahmeti" VALUES(38,3,'38:3','Sa gjenerata para tyre i kemi asgjësuar, të cilët lutën për shpëtim, po nuk ishte koha e shpëtimit.');
INSERT INTO "ahmeti" VALUES(38,4,'38:4','Ata (idhujtarët) habiten, ngase u erdhi pejgamberi nga mesi i tyre, e mosbesimtarët thanë: “Ky është magjistar, rrenës”.');
INSERT INTO "ahmeti" VALUES(38,5,'38:5','A mendon ai t’i bëjë zotat Një Zot? Vërtet, kjo është gjë shumë e çuditshme!');
INSERT INTO "ahmeti" VALUES(38,6,'38:6','Paria nga mesi i tyre shkoi duke i thënë njëri-tjetrit: vazhdoni të jeni të qëndrueshëm në adhurimin e zotave tuaj, pse kjo është një gjë e kurdisur.');
INSERT INTO "ahmeti" VALUES(38,7,'38:7','Ne nuk kemi dëgjuar diçka të këtillë në popullin (në fenë) e fundit; kjo nuk është tjetër, pos një trillim!');
INSERT INTO "ahmeti" VALUES(38,8,'38:8','A mos atij, nga mesi ynë, iu zbrit Kur’ani? Jo, por ata janë në dyshimndaj Kur’anit Tim, por për shkak se ende nuk e kanë shijuar dënimin Tim.');
INSERT INTO "ahmeti" VALUES(38,9,'38:9','A mos ata i kanë depotë e mëshirës së Zotit tënd, Ngadhënjyesit, Dhuruesit!');
INSERT INTO "ahmeti" VALUES(38,10,'38:10','A mos është i tyre pushteti i qiejve e i tokës dhe çka ka ndërmjet tyre? Pra, le të rreken me shkaqet (deri në qiell e të komandojnë)!');
INSERT INTO "ahmeti" VALUES(38,11,'38:11','(Ata janë) Një ushtri e grupacioneve që është aty, ajo është e thyer.');
INSERT INTO "ahmeti" VALUES(38,12,'38:12','Përgënjedhtruan para tyre populli i Nuhut, Adi dhe Firavni i ngrehinave të mëdha.');
INSERT INTO "ahmeti" VALUES(38,13,'38:13','(Përgënjeshtruan) Edhe Themudi, edhe populli i Lutit dhe banorët e Ejkes. Ato ishin grupacione (kundërshtare)');
INSERT INTO "ahmeti" VALUES(38,14,'38:14','Secili prej tyre përgënjeshtroi të dërguarit, andaj i gjeti dënimi Im.');
INSERT INTO "ahmeti" VALUES(38,15,'38:15','Po edhe këta nuk janë duke pritur tjetër vetëm se një ushtimë që nuk ka të përsëritur.');
INSERT INTO "ahmeti" VALUES(38,16,'38:16','Ata edhe thanë: “Zoti ynë, ngutna pjesën tonë para ditës së përgjegjësisë (të gjykimit)!”');
INSERT INTO "ahmeti" VALUES(38,17,'38:17','Ti duro ndaj asaj që thonë ata, dhe përkujtorobin Tonë Davudin, të fuqishmin (në fe e trup), vërtet ai gjithnjë i drejtohej All-llahut.');
INSERT INTO "ahmeti" VALUES(38,18,'38:18','Ne ia nënshtruam kodrat që së bashku që së bashku me të bënin tesbihë mbrëmje e mëngjes,');
INSERT INTO "ahmeti" VALUES(38,19,'38:19','Edhe shpezët e tubuara, të gjitha, vetëm Atij i ishin drejtuar.');
INSERT INTO "ahmeti" VALUES(38,20,'38:20','Ne ia forcuam edhe sundimin atij, i dhamë mençuri dhe aftësi në gjykime.');
INSERT INTO "ahmeti" VALUES(38,21,'38:21','A të ka arritur lajmi i palëve ndërgjykuese, kur kaluan prej së larti në dhomën ku lutej.');
INSERT INTO "ahmeti" VALUES(38,22,'38:22','Kur hynë te Davudi, ai u frikësua prej tyre, po ata i thanë: “Mos ke frikë, ne jemi dy palë në kundërshtim që i kemi bërë padrejtë njëri-tjetrit, ndaj gjyko me drejtësi mes nesh e mos shmang, dhe udhëzona në rrugë të drejtë”.');
INSERT INTO "ahmeti" VALUES(38,23,'38:23','Ky miku im i ka nëntëdhjetë e nëntë dele, ndërsa un ë kam vetëm një dhe dhe më ka thënë: “Ma ler mua ta posedoj atë! “Dhe më rëndoi e më mundi me fjalë!”');
INSERT INTO "ahmeti" VALUES(38,24,'38:24','Ai (Davudi) tha: “Ai ka bërë padrejtë ndaj teje me kërkimin e deles tënde (për t’ia bashkuar) te delet e veta. Është e vërtetë se shumica prej otakëve i bëjnë padrejtë njëri-tjetrit, me përjashtim të atyre që kanë besuar dhe punuar vepra të mira, por të tillë janë pak!” e Davudi mendoi se Ne kemi vënë në sprovë atë, andaj kërkoi falje nga Zoti i vet, ra i përkulur dhe u pendua.');
INSERT INTO "ahmeti" VALUES(38,25,'38:25','Ne atë ia falë, atijdhe ai është i afërt te Ne dhe ka përfundim (ardhmëri) të mirë.');
INSERT INTO "ahmeti" VALUES(38,26,'38:26','O Davud: “Ne të kemi bërë sundimtar në tokë, e ti pra gjyko me drejtësi mes njerëzve, e mos shko pas dëshirave se ato ë shmangin prej rrugës së All-llahu. Ata që largohen prej rrugës së All-llahut i pret dënim i rëndë për shkak se e harruan ditën e përgjegjësisë”.');
INSERT INTO "ahmeti" VALUES(38,27,'38:27','Ne nuk e krijuam qiellin e as tokën dhe çka ka në mes tyre, pa qëlllim (shkel e shko), ai është mendim i atyre që nuk besuan, pra dënimi me zjarr është mjerim për ata që nuk besuan.');
INSERT INTO "ahmeti" VALUES(38,28,'38:28','A mos do t’i barazojmë ata që besuan dhe bënë vepra të mira me ata që bënë shkatërrime në tokë, apo do t’i konsiderojnë njësoj si të ruajturit prej të këqiave, si ata që janë mëkatarë?');
INSERT INTO "ahmeti" VALUES(38,29,'38:29','(Ky është) Libër i begatshëm, Ne ta shpallëm ty këtë, që t’i studiojnë argumentet e tij dhe që të marrin mësim prej tij ata që kanë mend.');
INSERT INTO "ahmeti" VALUES(38,30,'38:30','Ne Davudit i falëm Sulejmanin, rob shumë i mirë dhe shumë i kthyer nga Zoti.');
INSERT INTO "ahmeti" VALUES(38,31,'38:31','Kur në një parambrëmje iu shfaqën atij kuaj që, kur ishin të ndalur, rrinin gatitu në tri këmbë, e ishin edhe shumë të shpejtë.');
INSERT INTO "ahmeti" VALUES(38,32,'38:32','E ai tha: “Unë i dhashë përparësi dashurisë së kuajve, ndaj Zotit tim, derisa (dielli) e fsheh dhe perëndoi”.');
INSERT INTO "ahmeti" VALUES(38,33,'38:33','“M’i ktheni ata mua!” Atëherë filloi t’u mësjojë këmbëve e qafave.');
INSERT INTO "ahmeti" VALUES(38,34,'38:34','Ne e sprovuam Sulejmanin dhe e mbajtëm në postin e tij si një trup, e pastaj u rikthye n gjendjen në jendjen e parë.');
INSERT INTO "ahmeti" VALUES(38,35,'38:35','Tha: “Zoti im, më fal (gabimin), më dhuro asi pushteti që askush pas meje nuk do të ketë; vërtet, Ti je dhuruesi më i madh.');
INSERT INTO "ahmeti" VALUES(38,36,'38:36','Ne ia nënshtruam erën që, sipas urdhërit të tij, ajo të ecën lehtë dhe nga të dëshirojë ai.');
INSERT INTO "ahmeti" VALUES(38,37,'38:37','Ndërsa djajtë (ia nënshtruam) për çdo ndërtim dhe zhytje në ujë.');
INSERT INTO "ahmeti" VALUES(38,38,'38:38','Dhe të tjerët (djaj) që ishin të lidhur në panga.');
INSERT INTO "ahmeti" VALUES(38,39,'38:39','Ky është shpërblimi Ynë, e ti dhuro ose mos dhuro, për këtë nuk përgjigjesh.');
INSERT INTO "ahmeti" VALUES(38,40,'38:40','Ai vërtet ka vend të lartë te Ne dhe ardhmëri të mirë.');
INSERT INTO "ahmeti" VALUES(38,41,'38:41','Përkujto edhe robin tonë Ejubin, kur me lutje iu drejtua Zotit të vet: “Djalli më ka goditur me mundim e dhembje!”');
INSERT INTO "ahmeti" VALUES(38,42,'38:42','Bjeri me këmbën tënde tokës! Ky është (ujë) i ftohtë, lahesh dhe pi.');
INSERT INTO "ahmeti" VALUES(38,43,'38:43','E nga mëshira Jonë dhe mësim për ata që kanë menduar, Ne ia falëm familjen e tij dhe po aq sa ishin ata.');
INSERT INTO "ahmeti" VALUES(38,44,'38:44','E, merre me dorën tënde njëdeng thupra dhe bjeri me të, e mos e thyej betimin! Vërtet, Ne e gjetëm atë të durueshëm. Sa rob shumë i mirë ishte ai dhe i kthyer te Zoti.');
INSERT INTO "ahmeti" VALUES(38,45,'38:45','Përkujto robërit tanë Ibrahimin, Is-hakun, Jakubin që ishin të fortë në zbatimin e detyrave dhe largpamës në fe.');
INSERT INTO "ahmeti" VALUES(38,46,'38:46','ne i pajisëm ata me një virtyt të posaçëm, me përkujtim ndaj botës tjetër.');
INSERT INTO "ahmeti" VALUES(38,47,'38:47','S’ka dyshim se ata ishin te Ne prej të zgjedhurve më të mirë.');
INSERT INTO "ahmeti" VALUES(38,48,'38:48','Përkujto Ismajlin, Eljesan, Dhelkiflin, që të gjithë prej të zgjedhurve.');
INSERT INTO "ahmeti" VALUES(38,49,'38:49','Ky është një përkujtim. E është e sigurt se ata që janë të ruajtur, kanë një ardhmëri të mirë.');
INSERT INTO "ahmeti" VALUES(38,50,'38:50','Xhennetet e Adnit janë me dyer të hapura për ata.');
INSERT INTO "ahmeti" VALUES(38,51,'38:51','Aty do të jenë të mbështetur në kolltukë dhe të kërkojnë emë e pije të llojeve të ndryshme.');
INSERT INTO "ahmeti" VALUES(38,52,'38:52','Ata kanë pranë vetes (hyri) sypërulura të një moshe.');
INSERT INTO "ahmeti" VALUES(38,53,'38:53','Këto janë ato që premtoheshit për ditën e llogarisë.');
INSERT INTO "ahmeti" VALUES(38,54,'38:54','Ky është furnizimi Ynë, i cili nuk ka të mbaruar.');
INSERT INTO "ahmeti" VALUES(38,55,'38:55','Kjo është kështu, E, sa u përket atyre që nuk besuan, ata kanë një ardhmëri shumë të keqe,');
INSERT INTO "ahmeti" VALUES(38,56,'38:56','Xhehennemin që do të hudhen në të, e sa djep i shëmtuar është ai.');
INSERT INTO "ahmeti" VALUES(38,57,'38:57','Ky është ujë i valë dhe i ndyrë; le ta shijojnë atë!');
INSERT INTO "ahmeti" VALUES(38,58,'38:58','I presin edhe dënime të tjera të llojllojta sikurse ai.');
INSERT INTO "ahmeti" VALUES(38,59,'38:59','Ky është një grumbull që bashkohet me ju (u thonë engjëjt parisë): “Mos paqin komoditet as mirëseardhje (thonë paria)!” Ata janë që do të digjen në zjarr.');
INSERT INTO "ahmeti" VALUES(38,60,'38:60','Ata (të shtypurit) thonë: “Jo, juve mos u qoftë as mirëseardhja, as komoditeti; ju jeni që na e përgatitët këtë!” Sa vendqëndrim i keq është!');
INSERT INTO "ahmeti" VALUES(38,61,'38:61','O Zoti ynë, ata thonë: “Atij që na e bëri këtë, shtoja dyfish dënimin në zjarr!”');
INSERT INTO "ahmeti" VALUES(38,62,'38:62','Dhe thonë: “ç’ëshë që nuk po i shohim disa burra që ne i konsideronim prej të këqijve.');
INSERT INTO "ahmeti" VALUES(38,63,'38:63','E që i kemi pas marrë ata ër tallje, a mos na u ka larguar shikimi prej tyre?”');
INSERT INTO "ahmeti" VALUES(38,64,'38:64','Kjo armiqësi në mes banuesve të zjarrit është e vërtetë.');
INSERT INTO "ahmeti" VALUES(38,65,'38:65','Thuaj: “Unë jam vetëm këshillues, e nuk ka ndonjë zot tjetër përpos All-llahut Një, Dërmuesit.');
INSERT INTO "ahmeti" VALUES(38,66,'38:66','Zot i qiejve e i tokës dhe ç’ka mes tyre, i plotfuqishmi, Mëkatfalësi”.');
INSERT INTO "ahmeti" VALUES(38,67,'38:67','Thusj: “Ky (Kur’ani) është një kumtesë e madhe!”');
INSERT INTO "ahmeti" VALUES(38,68,'38:68','Të cilës ju ia ktheni shpinën.');
INSERT INTO "ahmeti" VALUES(38,69,'38:69','Unë nuk kam pasur kurrfarë dije për eliten e lartë (melaike) kur ata bën polemikë (rreth Ademit).');
INSERT INTO "ahmeti" VALUES(38,70,'38:70','Mua nuk më shpalet tjetër vetëm se, unë nuk jam tjetër pos një tërheqës i hapët i vërejtjes!');
INSERT INTO "ahmeti" VALUES(38,71,'38:71','Kur Zoti yt u tha engjëjve: “Unë po krijoi një njeri nga balta,');
INSERT INTO "ahmeti" VALUES(38,72,'38:72','dhe kur ta kem përsosur atë dhe t’i kem dhënë nga ana Ime shpirt, ju menjëherë përuljunju atij (në sexhde)”.');
INSERT INTO "ahmeti" VALUES(38,73,'38:73','Engjëjt, të gjithë së bashku iu përulën,');
INSERT INTO "ahmeti" VALUES(38,74,'38:74','përveç Iblisit që ishte kryelartë dhe që u bë prej jobesimtarëve.');
INSERT INTO "ahmeti" VALUES(38,75,'38:75','(Zoti) Tha: “O Iblis, çka të pengoi ty t’i përulësh atij që Unë vetë e krijova? A bëre kryeneçësi, apo ke qenë prej atyre që shesin fodullëk?”');
INSERT INTO "ahmeti" VALUES(38,76,'38:76','Ai (Iblisi) tha: “Unë jam më i miri prej tij, mua më ke krijuar nga zjarri, e atë e krijove nga balta!”');
INSERT INTO "ahmeti" VALUES(38,77,'38:77','(Zoti) Tha: “dil pra prej tij (prej Xhennetit), ti je i mallkuar.');
INSERT INTO "ahmeti" VALUES(38,78,'38:78','Dhe largimi prej mëshirës Sime ka për të përcjellë deri në ditën e gjykimit!”');
INSERT INTO "ahmeti" VALUES(38,79,'38:79','Ai tha: “Zoti im, më jep afat deri në ditën e ringjalljes!”');
INSERT INTO "ahmeti" VALUES(38,80,'38:80','(Zoti) Tha: “Po, je prej atyre të afatizuarëve,');
INSERT INTO "ahmeti" VALUES(38,81,'38:81','deri në kohën e ditës së caktuar!”');
INSERT INTO "ahmeti" VALUES(38,82,'38:82','Ai tha: “Pasha madhërinë Tënde, kam për t’i shmangur pre rrugës së drejtë që të gjithë,');
INSERT INTO "ahmeti" VALUES(38,83,'38:83','përveç atyre që janë të sinqertë nga robërit e Tu!”');
INSERT INTO "ahmeti" VALUES(38,84,'38:84','(Zoti) Tha: “Pasha të vërtetën, e Unë e flas vetëm të vërtetën:');
INSERT INTO "ahmeti" VALUES(38,85,'38:85','Unë do ta mbushi Xhehennemin me ty dhe të gjithë ata që vijnë pas teje!”');
INSERT INTO "ahmeti" VALUES(38,86,'38:86','Thuaj (Muhammed): “Unë nuk kërkoj pre jush ndonjë shpërblim, dhe unë nuk jam prej atyre që bëjnë trillime”.');
INSERT INTO "ahmeti" VALUES(38,87,'38:87','Ky (Kur’ani) nuk është tjetër vetëm se këshillë për botët.');
INSERT INTO "ahmeti" VALUES(38,88,'38:88','E ju gjithsesi do ta kuptoni pas pak kohe vërtetësinë e tij.');
INSERT INTO "ahmeti" VALUES(39,1,'39:1','Shpallja e librit është prej All-llahut, të gjithfuqishmit, të urtit.');
INSERT INTO "ahmeti" VALUES(39,2,'39:2','Ne ta shpallëm ty librin për hir të së vërtetës, andaj ti adhuroje All-llahun duke qenë i sinqertë në adhurimin e Tij!');
INSERT INTO "ahmeti" VALUES(39,3,'39:3','Vini re! Adhurim i sinqertë është vetëm ai për All-llahun! Ndërsa ata që në vend të tij adhurojnë miq të tjerë (duke thënë): Ne nuk i adhurojmë ata për tjetër, vetëm që të na afrojnë sa më afër All-llahut, s’ka dyshim se All-llahu do të gjykojë mes tyre për atë që ata ishin në kundërshtim. E, është e vërtetë se All-llahu nuk udhëzon në rrugë të drejtë, atë që është rrenës, jobesimtarë.');
INSERT INTO "ahmeti" VALUES(39,4,'39:4','Sikur të kishte dashur All-llahu të ketë fëmijë, do të zgjidhte atë që dëshiron nga çka Ai vetë krijon. I pastër është Ai! Ai është All-llahu, i vetmi, i fuqishmi!');
INSERT INTO "ahmeti" VALUES(39,5,'39:5','Ai krijoi qiejt e tokën me qëllim të caktuar; Ai natën mbështjell (vendi të) ditës dhe ditës ia mbështjell natës; Ai nënshtroi diellin dhe hënën, që ssecili lëviz deri në një afat të caktuar, pra Ai është ngadhënjyesi, mëkatfalësi.');
INSERT INTO "ahmeti" VALUES(39,6,'39:6','Ai ju krijoi juve prej një njeriu, mandej prej tij e krijoi palën (çiftin) e tij dhe Ai krijoi për juve tetë nga qiftet e kafshëve. Ai u krijoi në barqet e nënave tuaja, krijim (etapë) pas krijimit në tri errësira. Ky është All-llahu, Zoti juaj, vetëm i Tij është pushteti, nuk ka tjetër pos Tij. E, si pra, i shmangeni (adhurimit të Tij)?');
INSERT INTO "ahmeti" VALUES(39,7,'39:7','Nëse ju nuk besoni, All-llahu nuk është nevojtarë për ju; megjithatë, Ai nuk është i kënaqur me mosbesimin e robërve të Vet, e nëse jeni mirënjohës ndaj Tij, Ai e pëlqen atë për ju. Ndërkaq, nuk do ta bartë asnjë mëkatarë barrën e tjetrit. Pastaj, e ardhmja juaj është vetëm te Zoti juaj, e AI do t’ju njoftojë me atë që keni vepruar. Vërtet, Ai e di shumë mirë se çka mbajnë zemrat.');
INSERT INTO "ahmeti" VALUES(39,8,'39:8','E kur e godit njeriun ndonjë e keqe (ndonjë dëm), ai e lut Zotin e vet duke kërkuar ndihmë vetëm prej Tij, e kur nga ana e TIj i je ndonjë të mira (ia largon të keqen), ai e harron atë që më parë është lutur Atij, dhe i përshkruan shokë All-llahut për të larguar (njerëzit) nga rruga e Tij. Thuaj: “Kënaqu për pak kohë më mosbesimin tënd, se ti pa dyshim je nga banuesit e zjarrit!”');
INSERT INTO "ahmeti" VALUES(39,9,'39:9','(A jobesimtari e ka gjendjen më të mirë) Apo ai që kohën e natë e kalon në adhurim, duke bërëb sexhde, duke qëndruar në këmbë, i ruhet (dënimit të) botës tjetër dhe shpreson në mëshirën e Zotit të vet? Thuaj: “A janë të barabartë ata që dinë dhe ata që nuk dinë?” Po, vetëm të zotët e mendjes marrin mësim.');
INSERT INTO "ahmeti" VALUES(39,10,'39:10','Thuaj: “O robërit e Mi që keni besuar, kini frikë ndaj Zotit tuaj. Ata që bënë mirë në këtë jetë, kanë të mirë të madhe, e Toka e All-llahut është e gjërë, ndërsa të durueshmive u jepet shpërblimi i tyre pa masë!”');
INSERT INTO "ahmeti" VALUES(39,11,'39:11','Thuaj: “Unë jam i urdhëruar ta adhuroj All-llahun, të jem i snqertë në adhurimin ndaj Tij!”');
INSERT INTO "ahmeti" VALUES(39,12,'39:12','Dhe jam i urdhëruar të jem i pari i myslimanëve!');
INSERT INTO "ahmeti" VALUES(39,13,'39:13','Thuaj: “Unë i frikësohem dënimit të një dite të madhe, nëse kundërshtoj Zotin tim”.');
INSERT INTO "ahmeti" VALUES(39,14,'39:14','thuaj: “vetëm All-llahun e adhuroj, sinqerisht ndaj Tij e bëj adhurimin tim.');
INSERT INTO "ahmeti" VALUES(39,15,'39:15','E ju pra, adhuroni pos Tij çka të doni!” Thuaj: “Të dështuar në ditën e kijametit janë ata që e kanë hmbur veten dhe familjen e vet. Vini re: ky është dështimi i njëmendët.');
INSERT INTO "ahmeti" VALUES(39,16,'39:16','Se ata do të kenë shtresa të zjarrit edhe sipër edhe prfundi. Me këtë All-llahu i frikëson robërit e Vet: “O robërit e Mi, kinie frikë (dënimin)!”');
INSERT INTO "ahmeti" VALUES(39,17,'39:17','Ndërkaq, ata që u larguan prej adhurimit të idhujve dhe ju drejtuan All-llahut, ata kanë gëzim të madh, e ti përgëzoj robërit e Mi!');
INSERT INTO "ahmeti" VALUES(39,18,'39:18','Të cilët i dëgjojnë fjalët dhe pasojnë atë më të mirën prej tyre. Të tillët janë ata që All-llahu i udhëzoi në rrugën e drejtë dhe të tillët janë ata të mençurit.');
INSERT INTO "ahmeti" VALUES(39,19,'39:19','E, atij që i është përcaktuar vendimi për dënim, a mund ta shpëtosh ti prej zjarrit?');
INSERT INTO "ahmeti" VALUES(39,20,'39:20','Ndërkaq, ata që u ruajtën për hir të Zotit të tyre, për ta do të ketë dhoma të ndërtuara njëra mbi tjetrën, nën të cilat rrjedhin lumenjtë. Premtimi i All-llahut, e All-llahu nuk e thyen premtimin.');
INSERT INTO "ahmeti" VALUES(39,21,'39:21','A nuk e sheh se All-llahu lëshon ujë nga qielli dhe atë e shpërndanë në tokë nëpër burime, mandej me të mbijnë bimë të llojeve dhe të ngjyrave të ndryshme, pastaj ajo thahet dhe ti e sheh ata të zverdhurar dhe pastaj e bën atë të llomitr. S’ka dyshim se në këtë ka argument për të zotët e mendjes.');
INSERT INTO "ahmeti" VALUES(39,22,'39:22','Atij që All-llahu ia ka zgjruar kraharorin për besim islam, ai është në një dritë të sigurt prej Zotit të vet. Të mjrët janë ata që nga zemërfortësia e tyre nuk përmendin All-llahun, të tillët janë në humbje të qartë.');
INSERT INTO "ahmeti" VALUES(39,23,'39:23','All-llahu e shpalli të folmën më të mirë, librin, të ngjashëm në mrekulli, të përsëritur herë pas herë (me këshilla e dispozita), që prej (dëgjimit të) tij rrënqethen lëkurat dhe zemrat e tyre. Ky (libër) është udhëzim i All-llahut, me të udhëzon atë që do. E atë që All-llahu e le të humbur, për të nuk ka ndonjë udhëzues.');
INSERT INTO "ahmeti" VALUES(39,24,'39:24','E ai që me fytyrën e vet mbrohet nga dënimi në ditën e kijametit (a është i njejtë me atë të shpëtuarin)? Kur mizorëve u thuhet: “Përjetoni të keqen e asaj që punuat!”');
INSERT INTO "ahmeti" VALUES(39,25,'39:25','Edhe ata para tyre përgënjeshtruan, por u erdhi ndëshkim nga nuk e menduan.');
INSERT INTO "ahmeti" VALUES(39,26,'39:26','All-llahu u bëri të përjetojnë në këtë jetë poshëtrimin, por dënimi i botës tjetër është edhe më i madh, sikur ta dinin ata.');
INSERT INTO "ahmeti" VALUES(39,27,'39:27','Ne u kemi sjellë njerëzve në këtë Kur’an shembuj nga çdo lëmi e nevojshme, ashtu që të marrin përvojë, (mësim),');
INSERT INTO "ahmeti" VALUES(39,28,'39:28','Duke qenë Kur’an arabisht që nuk ka kundërthënie, me qëllim që të ruhen prej rrugës së gabuar.');
INSERT INTO "ahmeti" VALUES(39,29,'39:29','All-llahu solli një shembull: Një njeri (rob) në posedimin e të cilit ishin ortakë pa marrëveshje mes vete dhe njëri (rob) që është thjesht në posedimin e një njeriu. A janë ata të dy të një lloji (të një gjendjeje)? Lavdërimi i takon vetëm All-llahut, por shumica e tyre, nuk e dinë.');
INSERT INTO "ahmeti" VALUES(39,30,'39:30','Ti do të jesh i vdekur, e edhe ata do të jenë të vdekur.');
INSERT INTO "ahmeti" VALUES(39,31,'39:31','E pastaj, në ditën e kijametit pranë Zotit tuaj do të grindeni mes vete.');
INSERT INTO "ahmeti" VALUES(39,32,'39:32','E kush është më mëkatar se ai që flet të pavërtetën ndaj All-llahut dhe se ai që kur i erdhi e vërteta e përgënjeshtroi? A nuk është në Xhehennem vendqëndrimi për jobesimtarët?');
INSERT INTO "ahmeti" VALUES(39,33,'39:33','E ai që e solli të vërtetën dhe ai që e vërtetoi atë, të tillët janë të ruajturit.');
INSERT INTO "ahmeti" VALUES(39,34,'39:34','Ata te Zoti i tyre kanë çkado që dëshirojnë, e ai është shpërblimi i bamirësve.');
INSERT INTO "ahmeti" VALUES(39,35,'39:35','All-llahu do t’ua shlyej atyre edhe më të keqen që e punuan dhe do t’u jepë shpërblime më të mira për ata që punuan.');
INSERT INTO "ahmeti" VALUES(39,36,'39:36','A nuk i mjafton All-llahu robit të vet? E ata të friksojnë ty me të tjerët pos Tij. Po atë që e ka humbur All-llahu, për të nuk ka udhëzues!');
INSERT INTO "ahmeti" VALUES(39,37,'39:37','Atë që All-llahu e drejton, atë nuk ka kush e humbë; a nuk është All-llahu i gjithëfuqishëm që ndërmerr ndëshkime?');
INSERT INTO "ahmeti" VALUES(39,38,'39:38','Po nëse i pyet ata se kush i krijoi qiejt e tokën, sigurisht ata do të thonë: “All-llahu!” Ti thuaju: “Më tregoni pra, për ata që i adhuroni, pos All-llahut, nëse All-llahu më godit mua me ndonjë të keqe, a munden ta largojnë ata atë të keqe, ose, nëse All-llahu dëshiron ndonjë të mirë ndaj meje, a munden ta pengojnë ata të mirën e Tij?” Thuaju: “Mua më mjafton All-llahu. Vetëm Atij i mbështeten të mbështeturit”.');
INSERT INTO "ahmeti" VALUES(39,39,'39:39','Thuaj: “O populli im, veproni sipas gjendjes suaj, e edhe unë veproj sipas times e më vonë do ta dini,');
INSERT INTO "ahmeti" VALUES(39,40,'39:40','se cilit do t’i vijë dënimi që e poshtëron dhe do ta goditë dënim i përjetshëm!”');
INSERT INTO "ahmeti" VALUES(39,41,'39:41','Ne ta zbritëm ty librin me argumente për njerëz, e kush orientohet në rrugën e vërtetë, ai e ka për vete e kush e humb, ai i bën dëm vetëm vetes, e ti nuk je garantues i tyre.');
INSERT INTO "ahmeti" VALUES(39,42,'39:42','All-llahu i merr shpirtrat kur është momenti i vdekjes së tyre (i vdekjes së trupave të tyre), e edhe atë që është në gjumë e nuk ka vdekur, e atij që i është caktuar vdekja e mban (nuk e kthen), e atë tjetrin (që nuk i është caktuar vdekja, por është në gjumë), e lëshon (të kthehet) deri në afatin e caktuar. Vërtet, në këto ka argumente për një popull që mendon.');
INSERT INTO "ahmeti" VALUES(39,43,'39:43','A mos kanë zgjedhur ndërmjetësues përveç All-llahut? Thuaj: “A edhe pse janë që nuk posedojnë asgjë e as nuk kuptojnë”.');
INSERT INTO "ahmeti" VALUES(39,44,'39:44','Thuaj: “I tërë shefaati (ndërmjetësimi) i takon vetëm All-llahut, i Tij është pushteti i qiejve dhe i tokës, e më vonë vetëm te Ai ktheheni”.');
INSERT INTO "ahmeti" VALUES(39,45,'39:45','Kur përmendet vetëm All-llahu, zemrat e atyre që nuk besojnë botën tjetër, neveriten, e kur përmenden, përveç Atij, të tjerët, ata gëzohen.');
INSERT INTO "ahmeti" VALUES(39,46,'39:46','Thuaj: “O All-llah, Krijues i qiejve e i tokës, Njohës i të fshehtës edhe i të dukshmës, Ti je që gjykon mes robërve Tu për atë që ata kundërshtoheshin”.');
INSERT INTO "ahmeti" VALUES(39,47,'39:47','E sikur të ishte e atyre që nuk besuan, e tërë ajo çka ka në tokë e edhe njëherë aq, do ta jepnin kompensim për t’i shpëtuar dënimit të tmerrshëm në ditën e kijametit. E prej All-llahut do t’u prezentohet (lloji i dënimit) çka as nuk kanë mund të mendojnë.');
INSERT INTO "ahmeti" VALUES(39,48,'39:48','Dhe do t’ua dalin në shesh të këqiat që i kanë punuar dhe i përfshin ata ajo me të cilën talleshin.');
INSERT INTO "ahmeti" VALUES(39,49,'39:49','Kur e godit njeriun ndonjë e keqe, ai na lutet Neve, e kur nga ana Jonë ia shëndërrojmë atë në ndonjë të mirë, ai thotë: “Kjo më është dhënë në bazë të dijes sime!” Jo, por ajo është një sprovë, por shumica e tyre nuk e dinë.');
INSERT INTO "ahmeti" VALUES(39,50,'39:50','Ashtu patën thënë edhe ata që ishin para tyre, por atyre nuk u bëri dobi ajo çka kishin fituar.');
INSERT INTO "ahmeti" VALUES(39,51,'39:51','Ata i goditën të këqijat që i kishin punuar. Edhe prej këtyre, ata që bënë zullum, do t’i godasin të këqiat e veprave të tyre dhe nuk mund t’i shpëtojnë asaj (goditjeje).');
INSERT INTO "ahmeti" VALUES(39,52,'39:52','A nuk e dinë ata, se All-llahu i jep furnizim të plotë atij që do. Edhe në këto ekzistojnë fakte për një popull që beson.');
INSERT INTO "ahmeti" VALUES(39,53,'39:53','Thuaj: “O robërit e Mi, të cilët e keni ngarkuar me shumë gabime veten tuaj, mos e humbni shpresën ndaj mëshirës së All-llahut, pse All-llahu i falë të gjitha mëkatet, Ai është që shumë falë dhe është mëshirues!”');
INSERT INTO "ahmeti" VALUES(39,54,'39:54','Dhe, kthehuni te Zoti juaj dhe përuljuni Atij para se t’u vijë dënimi, se pastaj nuk do të ketë kush t’ju ndihmojë.');
INSERT INTO "ahmeti" VALUES(39,55,'39:55','Dhe përmbajuni asaj më të mirës që u është shpallur nga Zoti juaj, para se t’ju vijë dënimi befas e ju të mos dini.');
INSERT INTO "ahmeti" VALUES(39,56,'39:56','Dhe të mos thotë ndokush: “O i mjeri unë që kam lënë mangu respektimin ndaj All-llahut dhe ë kam qenë prej atyre që talleshin!”');
INSERT INTO "ahmeti" VALUES(39,57,'39:57','Ose të mos thotë: “Ah, sikur të më kishte udhëzuar All-llahu (në rrugë të drejtë) e të kisha qenë prej atyre që janë të ruajtur!”');
INSERT INTO "ahmeti" VALUES(39,58,'39:58','Apo, kur ta shohë dënimin të thotë: “Sikur të isha kthyer edhe njeherë e të isha bërë prej atyre bamirësve!”');
INSERT INTO "ahmeti" VALUES(39,59,'39:59','Jo, ty të patën ardhur argumentet e Mia, e ti i përgënjeshtrove ato, u bëre kryeneç dhe ishe prej atyre që nuk besuan.');
INSERT INTO "ahmeti" VALUES(39,60,'39:60','E ata që bënë gënjeshtër ndaj All-llahut, do t’i shohësh në ditën e kijametit, fytyrat e tyre të nxira. A nuk është në Xhehennem vendi i Kryelartëve?');
INSERT INTO "ahmeti" VALUES(39,61,'39:61','Ndërsa All-llahu do t’i shpëtojë me atë suksesin e tyre ata, që kishin qenë të ruajtur. Ata nuk dot’i kapë e keqja e as nuk do të jenë të shqetësuar.');
INSERT INTO "ahmeti" VALUES(39,62,'39:62','All-llahu është krijues i çdo sendi dhe Ai ëshë mbikëqyrës ndaj çdo gjëje.');
INSERT INTO "ahmeti" VALUES(39,63,'39:63','Vetëm te Ai janë çelësat e qiejve dhe tokës, ndërsa ata që nuk i besuan argumentet e All-llahut, të tillët janë ata të dështuarit.');
INSERT INTO "ahmeti" VALUES(39,64,'39:64','Thuaj: “O ju injorantë, a mos më thirrni të adhuroj tjetër, në vend të All-llahut?”');
INSERT INTO "ahmeti" VALUES(39,65,'39:65','Pasha Alahun, ty të është shpallur, e edhe atyre para teje: “Nëse i bën shok (All-llahut), veprat tua janë të asgjësuara dhe ti do të jeshë prej të humburve.');
INSERT INTO "ahmeti" VALUES(39,66,'39:66','Prandaj, vetëm All-llahun adhroje dhe bëhu mirënjohës!”');
INSERT INTO "ahmeti" VALUES(39,67,'39:67','Ata nuk e çmuan All-llahun me atë madhështinë që i takon, ndërsa, në ditën e kijametit e tërë toka është në grushtin e Tij, e qiejt të mbështjellë në të djathtën e Tij. Ai është i pastër nga të metat dhe Ai është i lartë nga çka ata i shoqërojnë!');
INSERT INTO "ahmeti" VALUES(39,68,'39:68','Dhe i fryhet Surit dhe bie i vdekur çka ka në qiej dhe në tokë, përveç atyre që do All-llahu (të mos vdesin), pastaj i fryhet atij herën tjetër, kur qe, të gjithë aa të ngritur e presin (urdhërin e Zotit).');
INSERT INTO "ahmeti" VALUES(39,69,'39:69','Toka është ndriçuar me dritën e Zotit të vet, libri (shënimet mbi veprat) është vënë pranë dhe sillen pejgamberët e dëshmitarët, e kryhet mes tyre gjykimi me drejtësi, e atyre nuk u bëhet padrejtë.');
INSERT INTO "ahmeti" VALUES(39,70,'39:70','Dhe, secili njeri shpërblehet me atë që ka vepruar, e Ai më së miri e di se ç’punuan.');
INSERT INTO "ahmeti" VALUES(39,71,'39:71','E ata që nuk besuan sillen në grupe te Xhehennemi, e kur arrijnë te ai, dyert e tij hapen e roja e tij u thotë atyre: “A nuk u patën ardhur juve të dërguar nga mesi juaj t’ju lexojnë shpalljet e Zotit tuaj, t’ju tërheqin vërejtjen për ballafaqimin tuaj në këtë ditë?” Po, (na kanë ardhur...) thonë ata, por fjala (vendimi) e dënimit domosdo është bërë realitet kundër jobesimtarëve!');
INSERT INTO "ahmeti" VALUES(39,72,'39:72','U thuhet: “Hyni nëpër dyer të Xhehennemit, aty do të jeni përgjithmonë, sa vend i keq është për kryelartët!”');
INSERT INTO "ahmeti" VALUES(39,73,'39:73','E ata që ishin të devotshëm ndaj Zotit të tyre, sillen në grupe te Xhenneti, e kur arrijnë aty, dyert e tij i gjejnë të hapura dhe roja e tij u thotë atyre: “Selamun alejkum” - qofshi të shpëtuar, ishit të pastër, andaj hyni në të, aty do të jeni përgjithmonë.');
INSERT INTO "ahmeti" VALUES(39,74,'39:74','E ata (të Xhennetit) thanë: “Falënderuar qoftë All-llahu, i cili premtimin e Vet e bëri realitet ndaj nesh dhe na e la në disponim tokën e Xhennetit që të vendosemi aty ku të duam! Sa shpërblim i mirë është i atyre që vepruan drejtë”.');
INSERT INTO "ahmeti" VALUES(39,75,'39:75','E do t’i shohësh engjëjt të rreshtuar përreth Arshit, e madhërojnë me falënderim Zotin e vet. E në mes tyre (njerëzve) kryhet gjykimi me drejtësi dhe thuhet: “(prej të gjithëve) Falënderuar qoftë vetëm All-llahu, Zoti i botëve!”');
INSERT INTO "ahmeti" VALUES(40,1,'40:1','Ha, Mimë.');
INSERT INTO "ahmeti" VALUES(40,2,'40:2','Shpallja e librit është prej All-llahut, Fuqiplotit, Gjithdijshmit.');
INSERT INTO "ahmeti" VALUES(40,3,'40:3','I cili fal mëkatin dhe pranon pendimin, dhe që është ndëshkues i ashpër, por edhe bamirës. Nuk ka zot tjetër përveç Atij. Te Ai është e ardhmja e çdokujt.');
INSERT INTO "ahmeti" VALUES(40,4,'40:4','Rreth argumenteve të All-llahut nuk kundërshton kush, pos atyre që nuk besuan; pra ty të mos te mashtrojë lëvizja e tyre nëpër qytete.');
INSERT INTO "ahmeti" VALUES(40,5,'40:5','Populli i Nuhut dhe grupacione pas tyre patën përgënjeshtruar përpara këtyre (idhujtarëve), dhe secili popull (që përgënjeshtroi), u përpoq ta asgjësojë të dërguarin e vet dhe luftoi me çështje të kota, ashtu me ato deshi ta likuidojë të vërtetën, e Unë i ndëshkova ata, e çfarë ishte ndëshkimi Im ndaj tyre?');
INSERT INTO "ahmeti" VALUES(40,6,'40:6','Po ashtu, ka marrë fund vendimi i Zotit tënd (për dënim) kundër atyre që nuk besuan, se ata janë banues të zjarrit.');
INSERT INTO "ahmeti" VALUES(40,7,'40:7','Ndërsa ata (engjëjt) që e bartin Arshin dhe ata që janë përreth tij, lartësojnë me falënderim Zotin e tyre, i besojnë Atij dhe i luten Atij t’i falë ata që besuan (duke thënë): “Zoti yna, Ti me mëshirën dhe me diturinë Tënde ke përfshirë çdo send, andaj falu atyre që u penduan dhe ndoqën rrugën tënde, e edhe ruaj ata nga dënimi i Xhehennemit!');
INSERT INTO "ahmeti" VALUES(40,8,'40:8','Zoti ynë, shtiji në Xhennete të Adnit, të cilët ua ke premtuar, ata edhe kush ishte i mirë, prej etërve të tyre, grave të tyre dhe pasardhësve të tyre. Vërtet, Ti je ngadhnjesi, i urti!');
INSERT INTO "ahmeti" VALUES(40,9,'40:9','dhe i mbron ata prej të këqiave, pse atë që Ti e mbron atë ditë prej të këqiave, Ti e ke mëshiruar atë, e ai është shpëtimi i madh”.');
INSERT INTO "ahmeti" VALUES(40,10,'40:10','S’ka dyshim e ata që nuk besuan do të thirren (do t’u thuhet); “Hidhërimit i All-llahut pse u ftuat të besoni e ju nuk besuat (në dynja), është më i madh se urrejtja juaj ndaj vetes”.');
INSERT INTO "ahmeti" VALUES(40,11,'40:11','Ata (jobesimtarët) thonë: “Zoti ynë, na bëre të vdesim dy herë dhe na ngjalle dy herë, e ne pranuam për mëkatet tona, pra a ka ndonjë rrugëdalje?”');
INSERT INTO "ahmeti" VALUES(40,12,'40:12','ky (dënimi) është, pse kur bëhet thirrja për të besuar All-llahun Një, ju e mohuat, e nëse i përshkruhej shok Atij, ju besuat, pra i tërë sundimi është në duart e All-llahut, të lartit, të madhëruarit!');
INSERT INTO "ahmeti" VALUES(40,13,'40:13','Ai (All-llahu) është që po ua dëfton juve argumentet e veta, që ju sjell furnizim prej qiellit (shiun), por përveç atyre që janë të kthyer te Zoti, të tjerët nuk marrin mësim.');
INSERT INTO "ahmeti" VALUES(40,14,'40:14','Pra, adhuronie All-llahun me një adhurim të sinqertë ndaj Tij, edhe pse jobesimtarët e urrejnë atë (adhurimin).');
INSERT INTO "ahmeti" VALUES(40,15,'40:15','Ai (All-llahu) është i pozitës më të lartë, zoti i Arshit; Ai me urdhërin e vet ia sjell shpirtin (shpalljen) atij që do nga robërit e vet, për t’ua tërhequr vërejtjen ndaj ditës së takimit (të kijametit).');
INSERT INTO "ahmeti" VALUES(40,16,'40:16','Ditës kur ata do të dalin (prej varrezave) në shesh, e All-llahut nuk mund t’i fshiheet asnjë gjë e tyrre. I kujt është pushteti sot? (bëhet pyetja). I All-llahut, i Atij që është një, i fuqiplotit (është përgjigjja)!');
INSERT INTO "ahmeti" VALUES(40,17,'40:17','Sot shpërblehet secili njeri me atë që ka vepruar. Sot nuk ka padrejtësi, All-llahu llogarit shpejt.');
INSERT INTO "ahmeti" VALUES(40,18,'40:18','ti tërhiqu atyre vërejtjen për ditën e kijametit, kur zemrat, të mllefosura arrijnë në fyt. Për zullumqarët nuk ka as mik e as ndërmjetsues që i shkon fjala.');
INSERT INTO "ahmeti" VALUES(40,19,'40:19','Ai di për shikimin me cep të syve, e edhe për atë që e fshehin në zemra.');
INSERT INTO "ahmeti" VALUES(40,20,'40:20','All-llahu gjykon me drejtësi, ndërsa ata që adhuronin tjetër po Atij, ata nuk kanë në dorë të gjykojnë asgjë. All-llahu është Ai që dëgjon dhe sheh.');
INSERT INTO "ahmeti" VALUES(40,21,'40:21','A nuk udhëtuan ata nëpër tokë të shohin se si ishte përfundimi i atyre që patën qenë para tyre? Ata ishin edhe më fuqishëm se këta e edhe lanë më shumë gjurmë më tokë, po All-llahu i dënoi për shkak të mëkateve të tyre dhe nuk patë ndonjë që t’i mbrojë prej dënimit të All-llahut.');
INSERT INTO "ahmeti" VALUES(40,22,'40:22','E ai (dënimi ishte) për arsye se ata i mohuan argumentet e qarta, të cilat ua sillnin të dërguarit e tyre, andaj All-llahu i shkatërroi. AI vërtetë është i fuqishëm, ndëshkues i ashpër.');
INSERT INTO "ahmeti" VALUES(40,23,'40:23','Ne e kemi pas dërguar Musain me argumente e fakte të qarta,');
INSERT INTO "ahmeti" VALUES(40,24,'40:24','te faraoni dhe te Hamanit e Karuni, e ata i thanë: “Magjistar e gënjshtar!”');
INSERT INTO "ahmeti" VALUES(40,25,'40:25','Kur ua solli ia të vërtetën nga ana Jonë, ata thanë: “Mbytni djemtë e atyre që i besuan atij, e lini të jetojnë gratë e tyre, por dredhitë e tyre nuk qenë tjetër pos dështim”.');
INSERT INTO "ahmeti" VALUES(40,26,'40:26','Faraoni tha: “më lini mua ta mbys Musain, e ia le të thërrasë Zotin e vet, pse unë kam frikë se po ua ndryshon fenë tuaj, ose po nxit trazira në vend!”');
INSERT INTO "ahmeti" VALUES(40,27,'40:27','Musai tha: “Unë iu kam mbështëtur Zotit tim dhe Zotit tuaj që të më mbrojë prej çdo kryeneçi, që nuk i beson ditës së përgjrgjësisë!”');
INSERT INTO "ahmeti" VALUES(40,28,'40:28','Një njeri besimtar nga familja e faraonit që e fshehte besimin e vet, tha: “A doni ta mbytni një njeri vetëm pse thotë Zoti im është All-llahu, derisa prej Zotit tuaj u ka sjellë argmente? Nëse ai është rrenës, për vete e ka rrenën, e nëse është i drejtë, juve do t’ju godit diçka nga ajo që ai ju premton. S’ka dyshim se All-llahu nuk udhëzon në rrugë atë që e tejkalon dhe është rrenës.');
INSERT INTO "ahmeti" VALUES(40,29,'40:29','O populli im, sot pushteti është juaji në vend, po kush do të na mbrojë prej dënimit të All-llahut nëse ia na përfshin? “Faraoni tha: “Unë nuk kam mendim tjetër t’ju japë pos atij që ju thashë dhe nuk jam duke ju udhëzuar tjetër pos në rrugën e drejtë!”');
INSERT INTO "ahmeti" VALUES(40,30,'40:30','Ndërkaq, ai që kishte besuar tha: “O populli im, unë kam frikë për ju se po u gjen si i gjeti popujt e mëparshëm (grupet e mëparshme)!”');
INSERT INTO "ahmeti" VALUES(40,31,'40:31','Si rasti i popullit të Nuhut, të Adit, të Themudit dhe të atyre që ishin pas. All-llahu nuk bën pdrejtë ndaj rbërve.');
INSERT INTO "ahmeti" VALUES(40,32,'40:32','O populli im, unë kam frikë për ju ditën e britmës (të mjerimit),');
INSERT INTO "ahmeti" VALUES(40,33,'40:33','Ditën, kur nga frika ktheheni të ikni, e nuk ka kush që të prapësojë prej jush dënimin e All-llahut, Atë që All-llahu e lë të humbur, për të nuk ka ndonjë që e drejton.');
INSERT INTO "ahmeti" VALUES(40,34,'40:34','Edhe Jusufi, qush herët ju pat ardhur me argumente të qarta e ju gjithnjë ishit në dyshim ndaj asaj që ju pat sjellë, derisa edhe kur vdiq ai, ju patët thënë: “All-llahu nuk do të dërgojë pas tij pejgamber!” Ashtu All-llahu e bën të humbur atë që është i shfrennuar, i dyshimtë.');
INSERT INTO "ahmeti" VALUES(40,35,'40:35','Ata që duke mos pasur kurrfarë fakti pranë vetes, polemizojmë rreth argumenteve të All-llahut. Kjo (polemikë) shton urrejtje të madhe te All-llahu dhe te ata që kanë besuar. Po kështu All-llahu vulos çdo zemër të arrogantit, të zullumqarit.');
INSERT INTO "ahmeti" VALUES(40,36,'40:36','E faraoni tha: “O Haman, ndërtoma një pyrg, e ndoshta do t’i gjejë rrugët.');
INSERT INTO "ahmeti" VALUES(40,37,'40:37','Rrugët e qiejve, e ta shoh Zotin e Musait, pse unë mendoj se ai është rrenës”. Dhe ashtu, faraonit iu dukë e mirë vepra etij e keqe dhe u shmang prej rrugës së drejtë, kështu që parashikimet e faraonit ishin të asgjësuara.');
INSERT INTO "ahmeti" VALUES(40,38,'40:38','Ai që kishte besuar tha: “O populli im, ejani pas meje, e unë t’ju udhëzoj në rrugë të drejtë!');
INSERT INTO "ahmeti" VALUES(40,39,'40:39','O populli im, kjo jetë e kësaj bote nuk është tjetër vetëm se një përjetim i përkohshëm, ndërsa bota tjetër është ajo e përhershmja.');
INSERT INTO "ahmeti" VALUES(40,40,'40:40','Kush bën ndonjë vepër të keqe, ndëshkohet vetëm për aq sa është ajo, e kush bën ndonjë vepër të mirë, qoftë mashkull a femër, por duke qenë besimtar, të tillët hyjnë në Xhennet dhe aty shpërblehen pa masë.');
INSERT INTO "ahmeti" VALUES(40,41,'40:41','Dhe, o populli im, ç’është që unë ju thërras në shpëtim, e ju më thirrni për në zjarm?');
INSERT INTO "ahmeti" VALUES(40,42,'40:42','Më thirrni ta mohoj All-llahun dh adhuroj atë që nuk di asgjë për të i gjithfuqishmi, më katfalësi!');
INSERT INTO "ahmeti" VALUES(40,43,'40:43','Është e vërtetë se atij te icili më thirrni ju mua, nuk i takon adhurimi as në dynja as në botën tjetër dhe se e ardhmja jonë është te All-llahu, s’ka dyshim se të larguarit prej rrugës së drejtë, ata janë banus të zjarrit.');
INSERT INTO "ahmeti" VALUES(40,44,'40:44','Juve do t’ju bjerë ndërmend çka po u them, e çështjen tme ia besoj All-llahut; vërtet, All-llahu mbikëqyr robërit!”');
INSERT INTO "ahmeti" VALUES(40,45,'40:45','Dhe All-llahu e shpëtoi atë (besimtarin) prej të këqijavë që i kurdisën, ndërsa ithtarët e faraonit i përfshiu dënimi më i keq.');
INSERT INTO "ahmeti" VALUES(40,46,'40:46','Ata i nënshtroi zjarrit mëngjes e mbrëmje, ditën e kijametit (u thuhet engjëjve): “Ithtarët e faraonit futni në dënimin më të rëndë!”');
INSERT INTO "ahmeti" VALUES(40,47,'40:47','Dhe (përkujto) ur duke qenë në zjarr, ata grinden në mes vete, e të dobëtit u thonë atyre të mëdhenjve: “Ne kemi qenë ithtarë tuaj (në dynja), a mund të na largoni ndonjë pjesë të dënimit me zjarr?”');
INSERT INTO "ahmeti" VALUES(40,48,'40:48','Ata që kishin qenë pari thonë: “Ne të gjithë jëmi në të!” All-llahu ka vendosr në mënyrë të prerë në mes njerëzve”.');
INSERT INTO "ahmeti" VALUES(40,49,'40:49','Dhe ata, që janë në në zjarr, rojës së Xhehennemit i thonë: “Lutnie Zotin tuaj të na e lehtësoj dënimin, bile një ditë!”');
INSERT INTO "ahmeti" VALUES(40,50,'40:50','Ata u thonë: “A nuk u patën arritur juve të dërguar tuaj me argumente të qarta? Ata përgjigjen: “Po (na kanë ardhur)!” E pra, (u thotë roja) lutnu ju vetë, po lutja e jobesimtarëve është asgjë!”');
INSERT INTO "ahmeti" VALUES(40,51,'40:51','Ne patjetër do të ndihmojmë të dërguarit tanë në jetën e kësaj bote, edhe ata që besuan, e edhe në ditën e prezentimit të dëshmive.');
INSERT INTO "ahmeti" VALUES(40,52,'40:52','Në ditën kur zullumqarëve nuk u bën dobi arsyetimi i tyre, ata janë të mallkuar dhe ata e kanë vendin e keq.');
INSERT INTO "ahmeti" VALUES(40,53,'40:53','Për Zotin, Ne Musait i dhamë udhëzimin, e beni israilëve u lamë në trashëgim librin.');
INSERT INTO "ahmeti" VALUES(40,54,'40:54','Ua lamë (librin) udhërrëfyes e përkujtues për të zotët e mendjes.');
INSERT INTO "ahmeti" VALUES(40,55,'40:55','Ti bën durim, se premtimi i All-llahut është vërtetë, kërko falje për mëkatin tënd, lartëso me falënderim Zotin tënd mbrëmje e mëngjes.');
INSERT INTO "ahmeti" VALUES(40,56,'40:56','Ata që kundërshtojnë shpalljen e All-llahut pa pasur argument, nuk kanë tjetër në zemrat e tyre vetëm se një mendjemadhësi, me të cilën nuk do t’ia arrijnë qëllimit, e ti kërko mbrojtje te All-llahu, se Ai të gjitha i dëgjon dhe isheh.');
INSERT INTO "ahmeti" VALUES(40,57,'40:57','S’ka dyshim se krijimi i qiejve dhe tokës është më i madh se krijimi i njerëzve, por shumica e njerëzve nuk e dinë.');
INSERT INTO "ahmeti" VALUES(40,58,'40:58','Nuk është e barabartë i verbëri dhe ai që sheh, e as ata që besuan dhe bënë vepra të mira, nuk janë të barabartë me të këqijtë, por pak jeni duke marrë mësim.');
INSERT INTO "ahmeti" VALUES(40,59,'40:59','Kijameti gjithsesi do të vijë, aty nuk ka dyshim, por shumca e njerëzve nuk besojnë.');
INSERT INTO "ahmeti" VALUES(40,60,'40:60','Zoti juaj ka thënë: “Më thirrni Mua, Unë ju përgjigjem, e ata që nga mendjemadhësia i shmangen adhurimit i shmangeb adhurimit ndaj Meje, do të hyjnë të nënçmuar në Xhehennem”.');
INSERT INTO "ahmeti" VALUES(40,61,'40:61','All-llahu është Ai, që juve ua bëri natën të pushoni në të, e ditën të ndritëshme. All-llahu ëhtë dhurues ndaj njerëzve, por shumica e njerëzve nuk falënderojnë.');
INSERT INTO "ahmeti" VALUES(40,62,'40:62','Ky është All-llahu, Zoti, Krijuesi i çdo sendi, nuk ka zot pos Tij, e si prapësoheni?');
INSERT INTO "ahmeti" VALUES(40,63,'40:63','Kështu prapësohen ata që argumentet e All-llahut i mohojnë.');
INSERT INTO "ahmeti" VALUES(40,64,'40:64','All-llahu është Ai që ua bëri tokën vendbanim e qiellin kulm, dhe ju formësoi, e formën tuaj e bëri më të mirë dhe ju pajisi me të mira. Ky është All-llahu, Zoti juaj, i lartë, pra është All-llahu, Zot i botëve!');
INSERT INTO "ahmeti" VALUES(40,65,'40:65','Ai është i përjetshmi, s’ka zot, vetëm Ai, pra adhuronie Atë me adhurim të sinqertë ndaj Tij, falënderimi i qoftë vetëm All-llahut, Zotit të gjithësisë.');
INSERT INTO "ahmeti" VALUES(40,66,'40:66','Thuaj: “Unë jam ndaluar të adhuroj ata që ju i adhuroni pos All-llahut, pasi mua më kanë ardhur argumente të qarta nga Zoti im, dhe jam urdhëruar t’i dorëzohem Zotit të çdo krijese”.');
INSERT INTO "ahmeti" VALUES(40,67,'40:67','Ai (All-llahu) është që ju krijoi juve (të parin tuaj) prej dheu, pastaj prej një pike uji, pastaj prej gjakut të ngjizur, mandej ju bën të lindeni si foshnjë e më vonë të arrini pjekurinë, e pastaj të plakeni, e ka prej jush që vdes më herët, e që të gjithë të arrini afatin e caktuar dhe ashtu të kuptoni realitetin.');
INSERT INTO "ahmeti" VALUES(40,68,'40:68','Ai është që jep jetë dhe sjellë vdekje, e kur dëshiron një gjë, Ai vetëm thotë: “bëhu”, e ajo menjëherë bëhet.');
INSERT INTO "ahmeti" VALUES(40,69,'40:69','A nuk i sheh ata që kundërshtojën argumentet e All-llahut se si largohen nga e vërteta?');
INSERT INTO "ahmeti" VALUES(40,70,'40:70','Ata janë që përgënjeshtruan librin (Kur’anin) dhe atë me çka Ne i dërguam të dërguarit tanë, e më vonë ata do të kuptojnë.');
INSERT INTO "ahmeti" VALUES(40,71,'40:71','Ata janë që përgënjeshtruan librin (Kur’anin) dhe atë me çka Ne i dërguam të dërguarit tanë, e më vonë ata do të kuptojnë.');
INSERT INTO "ahmeti" VALUES(40,72,'40:72','Atyre do t’u vihen prangat dhe zingjirët në qafat e tyre e do të zhyten.');
INSERT INTO "ahmeti" VALUES(40,73,'40:73','Mandej atyre u thuhet: “Ku janë ata që i adhuronit,');
INSERT INTO "ahmeti" VALUES(40,74,'40:74','pos All-llahut?” Ata thonë: “Na kanë humbur sysh, por jo ne nuk kemi qenë që kemi adhuruar më parë ndonjë send!” Ja, kështu All-llahu i humb jobesimtarët.');
INSERT INTO "ahmeti" VALUES(40,75,'40:75','Këtë (dënim) e keni për shkak se gëzoheshit padrejt në dynja (duke bërë mëkate etj.) edhe për shkakse krenoheshit (me mendjemadhësi).');
INSERT INTO "ahmeti" VALUES(40,76,'40:76','Hyni në dyert e Xhehennemit (nëpër shtatë dyer); aty do të jeni përgjithmonë. E, sa vend i shëmtuar është ai i kryeneçëve!');
INSERT INTO "ahmeti" VALUES(40,77,'40:77','Ti pra, bëhu i durueshëm; premtimi i All-llahut është i vërtetë. E Neose do të bëjmë të shohishdiçka nga ajo që u premtojmë atyre, ose do ta marrim jetën (pa parë asgjë), e ata vetëm te Ne do të kthehen.');
INSERT INTO "ahmeti" VALUES(40,78,'40:78','Ne kemi dërguar pejgamberë para teje, për disa tyre të kemi njohtuar me rrëfimet e tyre, e për disa sish nuk të kemi njoftuar, e asnjë pejgamberi nuk i takoi të sjellë ndonjë argument, vetëm se me urdhërin e All-llahut. E kru të vijë koha e caktuar nga All-llahu, zbatohet gjykimi me drejtësi dhe aty atëherë dështojnë ata të kotit.');
INSERT INTO "ahmeti" VALUES(40,79,'40:79','All-llahu është Ai që për ju i krijoi kafshëët që disave t’u hipni prej disave të ushqeheni.');
INSERT INTO "ahmeti" VALUES(40,80,'40:80','Ju prej tyre keni edhe dobi të tjera, dhe përmes tyre t’i plotësoni nevojat tuaja, duke hipur në to me anë të anijeve barteni (udhëtoni).');
INSERT INTO "ahmeti" VALUES(40,81,'40:81','Dhe Ai është që ju mundësoi t’i shihni argumentet e Tij; e cilin, pra prej argmenteve të All-llahut e mohoni?');
INSERT INTO "ahmeti" VALUES(40,82,'40:82','A nuk udhëtuan ata nëpër tokë e të shikojnë se si prfunduan ata që ishin më parë, të cilët ishin edhe më të fortë për nga forca e ndikimi në tokë. por ajo që fituan, nuk u ndihmoi asgjë.');
INSERT INTO "ahmeti" VALUES(40,83,'40:83','Kur u erdhën atyre të dërgiarit me argumente të qarta, ata iu gëzuan dijenisë së vet dhe atëherë i përfshiu ajo që e tallnin.');
INSERT INTO "ahmeti" VALUES(40,84,'40:84','E kur e panë dënimin tonë, thanë: “Ne i kemi besuar vetëm All-llahut dhe i kemi mohuar ata që i ia shoqëronim”.');
INSERT INTO "ahmeti" VALUES(40,85,'40:85','Por besimi i tyre, kur e vërejtën dënimin tonë, nuk u bëri dobi. Ky është ligj i All-llahut (i kheshëm) ndaj robërve të Tij dhe aty, në atë çast pësuan disfatë jobesimtarët.');
INSERT INTO "ahmeti" VALUES(41,1,'41:1','Ha, Mimë.');
INSERT INTO "ahmeti" VALUES(41,2,'41:2','Zbritje prej Mëshiruesit, Mëshirëbërësit!');
INSERT INTO "ahmeti" VALUES(41,3,'41:3','Libër që ajetet e tij janë të shkoqitura, duke qenë Kur’an arabisht për një popull që di ta kuptojë.');
INSERT INTO "ahmeti" VALUES(41,4,'41:4','Është përgëzues dhe qortues, po shumica e tyre ia kthyen shpinën, andaj ata nuk dëgjojnë.');
INSERT INTO "ahmeti" VALUES(41,5,'41:5','Ata thanë: “Zemrat tona janë të mbyllura nga ajo që ti na thërret dhe në veshët tanë kemi shurdhim të rëndë, kështu që nddërmjet nesh edhe ndërmjet teje ekziston një perde, prandaj, ti vepro sipas tëndës e ne vazhdojmë tonën!');
INSERT INTO "ahmeti" VALUES(41,6,'41:6','Thuaj: “Unë jam vetëm njeri sikurse edhe ju, mua më shpallet se Zoti juaj është vetëm Zoti një, pra drejtonju Atj dhe kërkoni falje prej Tij. Për idhujtarët është mjerim i madh.');
INSERT INTO "ahmeti" VALUES(41,7,'41:7','të cilët nuk ejapin zeqatin dhe mu ata e mohojnë botën tjetër.');
INSERT INTO "ahmeti" VALUES(41,8,'41:8','Nuk ka dyshim se ata që besuan dhe bënë vepra të mira, kanë shpërblim të përhershëm.');
INSERT INTO "ahmeti" VALUES(41,9,'41:9','thuaj: “A ju jeni ata që nuk i besoni Atij që e krijoi tokën për dy ditë dhe Atij i përshkruani shok, e Ai është Zoti i botëve!');
INSERT INTO "ahmeti" VALUES(41,10,'41:10','Ai që në të vuri kodra të forta, ia shtoi të mirat asaj dhe brenda katër ditëve të plota caktoi të gjitha kushtet për të jetuar në të. Pra, për ata që pyesin (për kohën e krijimit të saj)”');
INSERT INTO "ahmeti" VALUES(41,11,'41:11','Mandej e mësyu qiellin (krijim e qiellit) e ai ishte tym (mmjegullinë që ishte si materie e parë) dhe atij e edhe tokës i tha: “Qasuni urdhërit Tim me dëshirë ose me dhunë!” Ato të dyja thanë: “Po i qasmi me dëshirë!”');
INSERT INTO "ahmeti" VALUES(41,12,'41:12','Dhe ata i krijoi shtatë qiej brenda dy ditëve dhe secilit qiell icaktoi atë që i nvojitej. Qiellin më të afërt (të dynjasë) Ne e stolisë me yje ndriçuese dhe e bëmë të mbrojtur. Ky është caktm i plotfuqishmit, i të dijshmit.');
INSERT INTO "ahmeti" VALUES(41,13,'41:13','Në qoftë se ata nuk përgjigjen, ti thuaj: “Unë tërhoqa vërejtjen e një rufeje (dënimi), si rrufeja e Adit dhe Themudit!”');
INSERT INTO "ahmeti" VALUES(41,14,'41:14','Kur atyre u erdhën të dërguar nga të gjitha anët, i këshilluan: “Të mos adhroni tjetër po vetëm All-llahun”, ata thanë: “Sikur Zoti ynë të kishte dëshiruar (ta dëgjonim) do të dërgonte engjëll, e kësaj m çka jeni dërguar ju, ne nuk i besojmë!”');
INSERT INTO "ahmeti" VALUES(41,15,'41:15','Sa i përket Adit, ata pa pasur kurrfarë të drejte, u treguan mendjemdhënj ndaj banorëve në tokë dhe thanë: “Kush ka fuqi më të fortë se ne?” A nuk e diten ata se All-llahu, i cili i krijoi ka fuqi më të fortë se ata?! Ata mohuan argumentet tona.');
INSERT INTO "ahmeti" VALUES(41,16,'41:16','Andaj Ne kundër tyre lëshuan një erë shumë të ftohtë gjatë disa ditëve fatzeza për t’u dhënë të pëjetojnë dënimin nënçmuesnë jetë e kësaj bote, ndërsa dënimi në botën tjetër do të jetë edhe më i shëmtuar dhe ata nuk do të ndihmohen.');
INSERT INTO "ahmeti" VALUES(41,17,'41:17','Sa i përket Themudit. Ne atyre u patëm treguar rrugën e drejtë, por ata megjithatë më shumë e deshën verbërinë, andaj ata i kapi tmerri i dënimit të turpshëm për shkak të asaj që vepruan.');
INSERT INTO "ahmeti" VALUES(41,18,'41:18','Ndërkaq, Ne ishpëtuam ata që kishin besuar dhe që ishin të ruajtur.');
INSERT INTO "ahmeti" VALUES(41,19,'41:19','E ditën kur armiqtë e All-llahut tubohen për në zjarr dhe ata janë të rrethuar.');
INSERT INTO "ahmeti" VALUES(41,20,'41:20','E derisa afrohen atij, kundër tyre dëshmojnë: të dëgjuarit e tyre, të parit e tyre dhe lëkurat e tyre për çdo gjë që ata kanë punuar.');
INSERT INTO "ahmeti" VALUES(41,21,'41:21','Ata lëkurave të tyre u thonë: “Përse dëshmuat kundër nesh?” Ato thonë: “All-llahu që çdo sendi ia ka dhënë të folur na dha edhe neve, e Ai është që u krijoi juve herën e parë dhe vetëm te Ai do të ktheheni”.');
INSERT INTO "ahmeti" VALUES(41,22,'41:22','Ju nuk fshiheshit (kur bënit mëkate) se do të dëshmojnë kundër jush të dëgjuarit tuaj, të parit tuaj dhe lëkurat tuaja, bile ju menduat se All-llahu nuk di shumë gjëra që ju bënit.');
INSERT INTO "ahmeti" VALUES(41,23,'41:23','mendimi juaj i gabuar, të cilin e patët ndaj Zotit tuaj, është ai që ju shkatërroi dhe tani jeni më të dëshpruarit.');
INSERT INTO "ahmeti" VALUES(41,24,'41:24','Nëse bëjnë durim, zjarri është vend i tyre, e nëse kërkojën të kthehen në atë që e dëshironë (në kënaqësinë e Zotit), atë nuk do të arrijnë.');
INSERT INTO "ahmeti" VALUES(41,25,'41:25','Ne u patëm mundësuar atyre të kenë asi shokësh që ua hijeshojnë atë që e bënin në atë kohë (në dynja) dhe atë që do t’u vijë më vonë dhe ashtu dënimi ndaj tyre u bë me vend si ndaj popujve që ishin para tyre nga xhinët dhe njerëzit, se vërtet ata ishin të humbur.');
INSERT INTO "ahmeti" VALUES(41,26,'41:26','Ata që nuk besuan thanë: “mos dëgjoni këtë Kur; an dhe kur të lexohet ai, ju bëni zhurmë (bërtitni) ashtu që ta pengoni!”');
INSERT INTO "ahmeti" VALUES(41,27,'41:27','E, Ne atyre që nuk besuan pa tjetër do t’u japim të përjetojnë dënim të ashpër dhe do t’i shpërblejmë me më të keqen e asaj që punuan.');
INSERT INTO "ahmeti" VALUES(41,28,'41:28','Dhe ai ndëshkim i armiqve të All-llahut është zjarri, aty është vendi i tyre i përhershëm, si shpërblim për shkak se ata refuzuan argumente tona.');
INSERT INTO "ahmeti" VALUES(41,29,'41:29','Ata që nuk besuan thonë: “O Zoti ynë, na itrego ata të dy nga xhinët dhe jerëzit, të cilët na bënë të jemi të humbur, e t’i shkelim me këmbët tona dhe le të jenë prej më të poshtërve!”');
INSERT INTO "ahmeti" VALUES(41,30,'41:30','S’ka dyshim se ata që thanë: “Zoti ynë është All-llahu”, pastaj nuk u luhatën, atyre u zbresin melaiket (në prag të vdekjes dhe u thonë): “Mos u frikësoni dhe mos u pikëlloni, keni myzhde Xhennetin që ju premtohej!');
INSERT INTO "ahmeti" VALUES(41,31,'41:31','Ne jemi miqtë e juaj në jetën e dunjasë dhe në ahiret, ku do të keni atë që dëshironi dhe gjithçka kërkoni.');
INSERT INTO "ahmeti" VALUES(41,32,'41:32','Pritje nga Falësi, Mëshiruesi!”');
INSERT INTO "ahmeti" VALUES(41,33,'41:33','E kush ka fjalën më të mirë prej atij që thërret te All-llahu, vepron (punë) të mira dhe thotë: “Unë jam prej muslimanëve?”');
INSERT INTO "ahmeti" VALUES(41,34,'41:34','Nuk është e barabartë e mira dhe e keqja. Andaj, (të keqen) ktheje me mirësi, se atëherë ai, mes teje dhe të cilit ka armiqësi, do të bëhet (si) mik i afërt.');
INSERT INTO "ahmeti" VALUES(41,35,'41:35','Mirëpo, këtë nuk e arrin kush, pos atyre të durueshmëve, dhe nuk e arrin kush, pos atyre me virtyt të lartë.');
INSERT INTO "ahmeti" VALUES(41,36,'41:36','E nëse ty të ngacmon ndonjë ngacmim prej shejtanit, kërko mbrojtje në All-llahun, vërtet Ai është Dëgjuesi, i Dijshmi!');
INSERT INTO "ahmeti" VALUES(41,37,'41:37','Nga faktet e madhështisë së Tij janë nata, dita, dielli e hëna. Mos i bëni sexhde as diellit, as hënës! Bëni sexhde vetëm All-llahut, i cili krijoi ato, nëse adhuroni vetëm Atë!');
INSERT INTO "ahmeti" VALUES(41,38,'41:38','Po nëse ata (kufarët) janë kryeneç (e nuk bëjnë sexhde), atëherë ata që janë pranë Zotit tënd (engjëjt më të lartë), Atij i bëjnë tesbih natën e ditën dhe ata nuk mërziten prej adhurimit.');
INSERT INTO "ahmeti" VALUES(41,39,'41:39','Nga argumentet e Tij është edhe toka që ti e sheh atë të thatë (shkretëtirë), e kur Ne i lëshojmë asaj shiun, ajo gjallërohet e shtohet. E, s’ka dyshim se Ai që e ngjalli atë, do t’i ringjallë të vdekurit, pse Ai ka mundësi për ‘do gjë.');
INSERT INTO "ahmeti" VALUES(41,40,'41:40','Ata që sulmojnë argumente Tona, nuk mund të na fshihen. Pra, a më mirë i ka punët ai që do të hudhet në zjarr, apo ai që në në ditë e kijametit vjen i sigurt? E ju pra veproni si të dëshironi, e ta dini se Ai sheh atë që punoni.');
INSERT INTO "ahmeti" VALUES(41,41,'41:41','Ata që e mohuan Kur’anin kur u erdhi, janë të marrë; ai është një libër ngadhnjyes (i pashoq).');
INSERT INTO "ahmeti" VALUES(41,42,'41:42','Atij nuk mund t’i mvishet e pavërteta nga asnjë anë; është i zbritur prej të Urtit, të lavdishmit.');
INSERT INTO "ahmeti" VALUES(41,43,'41:43','Ty nuk po të thuhet tjetër përveç asaj që u është thënë të dërguarve para teje. Vërtet, Zoti yt është ai që falë, po është edhe Ai që ndëshkon rëndë.');
INSERT INTO "ahmeti" VALUES(41,44,'41:44','Sikur Ne tabënim Kur’anin në gjuhë të huaj, ata do të thoshin: “Përse nuk janë ajetet e tij të kuptueshme (të shkoqitura), a është ai (Kur’ani) në gjuhë të huaj, kurse ai (pejgamberi) është arab?” Thuaj: “Ai është për besimtarët udhëzues e shërues. E ata që nuk besojnë në veshët e tyre kanë shurdhim, dhe ai për ta është verbërim. Të tillët janë sikurse thirren prej një vendi të largët e nuk dëgjojnë”.');
INSERT INTO "ahmeti" VALUES(41,45,'41:45','Ne edhe Musait i patëm dhënë librin, lidhur me të cilin pati kundërshtime. Sikur të mos ishte vendimi më parë prej Zotit tënd (të shtyhet ndëshkimi), puna mes tyre do të kryhej, pse ata janë në një dyshim ndaj tij.');
INSERT INTO "ahmeti" VALUES(41,46,'41:46','Kush bën mirë, ai e ka për vete, e kush bën keq, ai vepron kundër vetes, e Zoti yt nuk u bën padrejtësi robërve.');
INSERT INTO "ahmeti" VALUES(41,47,'41:47','Vetëm Ai e di për katastrofën (për kijametin). Asnjë frut nuk del prej lëvozhgës së vet dhe asnjë femër nuk mbart e as nuk lind pa dijen e Tij. E ditën kur Ai thërret ata se ku janë ata qëm’i bënit shok? Ata thonë: “Ne të kemi njohur Ty, nuk na ndonjë prej nesh që dëshmon se ke shok!”');
INSERT INTO "ahmeti" VALUES(41,48,'41:48','Dhe humb prej tyre ajo që më parë adhuronini, e binden se ata nuk kanë shpëtim.');
INSERT INTO "ahmeti" VALUES(41,49,'41:49','Njeriu nuk lodhet prej kërkesës për mirë, e kur atë e godit e keqja, ai keqësohet shumë dhe humb shpresën.');
INSERT INTO "ahmeti" VALUES(41,50,'41:50','E nëse pas të këqiave që e goditë, Ne i japim të mira nga ana Jonë, ai me këmbëngulje do të thotë: “Këtë e kam arritur vet (këtë do ta kemë gjithnjë), e nuk mendoj se do të bëhet kijameti, po, edhe nëse do të kthehem te Zoti im, unë te Ai do të kemë edhe më mirë!” Ne pa tjetër do t’i njoftjmë ata që nuk besuan për atë që punuan dhe do t’u japim të përjetojnë dënim të rëndë.');
INSERT INTO "ahmeti" VALUES(41,51,'41:51','Ne kur e begatojmë njeriun, ai nuk falënderon dhe largohet, e kur e godet e keqja, ai përulet dhe lutet shumë.');
INSERT INTO "ahmeti" VALUES(41,52,'41:52','Thuaj: “Më tregoni, nëse ai (Kur’ani) është prej All-llahut, e ju e mohuat atë, kush është më i humbur se ai që është në kundërshtim të fortë?”');
INSERT INTO "ahmeti" VALUES(41,53,'41:53','Ne do t’u bëjmë atyre të mundshme që të shohin argumentet Tona në horizonte dhe në veten e tyre deri që t’u bëhet e qartë se ai (Kur’ani) është i vërtetë. A nuk mjfton që Zoti yt është dëshmitar për çdo gjë?');
INSERT INTO "ahmeti" VALUES(41,54,'41:54','Vini re! Ata janë në dyshim për takimin me Zotin e tyre (me botën tjetër), e ta dini se Ai ka përfshirë me dijen e vet çdo send.');
INSERT INTO "ahmeti" VALUES(42,1,'42:1','Ha, Mimë.');
INSERT INTO "ahmeti" VALUES(42,2,'42:2','Ajn, Sinë, Kaf.');
INSERT INTO "ahmeti" VALUES(42,3,'42:3','All-llahu, fuqiploti, i gjithëdijshmi kështu (këto ajete) të shpall ty dhe atyre që ishin para teje.');
INSERT INTO "ahmeti" VALUES(42,4,'42:4','Vetëm e Tij është ç’ka në qiej dhe ç’ka në tokë dhe Ai është Mbizotëruesi, i Madhëruari!');
INSERT INTO "ahmeti" VALUES(42,5,'42:5','Qiejt e lartë mbi ta (ose qiejt njëri mbi tjetrin) gati pëlcasin (nga madhëria e Zotit ose nga thëniet e çoroditura të idhujtarëve). Ndërkaq, engjëjt vazhdimisht madhërojnë (bëjnë tesbih) duke falënderuar Zotin e tyre dhe kërkojnë falje të mëkateve për ata (besimtarët) që janë në tokë. Ta dini se All-llahu është Ai mëkatfalësi, Mëshiruesi!');
INSERT INTO "ahmeti" VALUES(42,6,'42:6','All-llahu është përcjellës i atyre që, pos Tij, adhurojnë zota të tjerë. Ti (o i dërguar), nuk je i obliguar ndaj tyre.');
INSERT INTO "ahmeti" VALUES(42,7,'42:7','Prandaj, Ne të shpallëm ty Kur’an arabisht që ta këshillosh kryefshatin (Mekën) dhe ata përreth saj, dhe t’u tërheqih vërejtjen për ditën e tubimit (kijametit), për të cilën nuk ka dyshim. Një grup për në Xhennet, kurse nj grup për në zjarr.');
INSERT INTO "ahmeti" VALUES(42,8,'42:8','Po sikur të kishte dashur All-llahu, do t’i bënte ata (njerëzit) një popull (të një feje), por Ai shtie në mëshirën e vet atë që do (atë që me vullnetin e vet pranon rrugën e drejtë), e jobesimtarët nuk kanë mbrojtës, as ndihhmës.');
INSERT INTO "ahmeti" VALUES(42,9,'42:9','Përkundrazi, ata zgjodhën mbrojtjës pos Atij, po vetëm All-llahu është Ai mbrojtësi dhe Ai ngjall të vdekurit dhe Ai ka mundësi për çdo send.');
INSERT INTO "ahmeti" VALUES(42,10,'42:10','Për çdo send që nuk pajtoheni, gjykimi për të është te All-llahu. Ai (gjykatësi i famshëm) është All-llahu, Zoti im, vetëm Atij i jam mbështetur, dhe vetëm Atij i drejtohem.');
INSERT INTO "ahmeti" VALUES(42,11,'42:11','Ai është krijues i qiejve e i tokës. Ai nga lloji juaj krijoi për ju bashkëshorte, edhe nga kafshët krijoi çifte, ashtu që t’ju shumojë. Asnjë send nuk është si Ai; Ai është dëgjuesi, shikuesi.');
INSERT INTO "ahmeti" VALUES(42,12,'42:12','Të Tij janë çelësat e qiejve e të tokës. Ai begaton shumë atë që do dhe nuk begaton (atë që gjithashtu do). Ai është i gjithdijshëm për çdo send.');
INSERT INTO "ahmeti" VALUES(42,13,'42:13','Ai u përcaktoi juve fe atë që i pat përcaktuar Nuhut dhe atë që Ne ta shpallëm ty dhe atë me çka e patëm porositur Ibrahimin, Musain dhe Isain. (I porositëm) Ta praktikoni fenë e drejtë e mos u përçani në të. Për idhujtarët është rëndë kjo në çka ju i thirrni ata. All-llahu veçon për të (për besim të drejtë) atë që do dhe e udhëzon atë që i drejtohet Atij.');
INSERT INTO "ahmeti" VALUES(42,14,'42:14','Po ata, vetëm pasi që u erdhi e vërteta, nuk u përçanë për tjetër, por nga zilia mes tyre. e sikur të mos ishte fjalë (vendimi) e hershme nga Zoti yt për deri në një afat të caktuar do të kryhej (dënimi) me ta. Nuk ka dyshim se ata që e trashëguan librin prej tyre (prej të parëve) janë në dyshim të thellë ndaj tij (ndaj librit Tevratë e Inxhil).');
INSERT INTO "ahmeti" VALUES(42,15,'42:15','E për këtë shkak (të përçarjes së tyre), ti thirr dhe përqëndrohu ashtu si të është urdhëruar dhe mos shko pas sëshirave e thuaj: “Unë kam besuar në librat që i shpalli All-llahu, jam urdhëruar të mbajë drejtësi mes jush, All-llahu është Zoti ynë dhe Zoti juaj; Ne kemi përgjegjësinë e veprave tona e ju të veprave tuaja, nuk ka polemikë mes nesh dhe jush; All-llahu bën tubimin mes nesh dhe vetëm te Ai është përfundimi.');
INSERT INTO "ahmeti" VALUES(42,16,'42:16','Ata që kundërshtojnë fenë e All-llahut pasi që asaj i janë përgjigjur (njerëzit), faktet e tyre janë të asgjësuara te Zoti i tyre, ata janë të përbuzur dhe ata do të kenë dënim të rëndë.');
INSERT INTO "ahmeti" VALUES(42,17,'42:17','All-llahu është Ai që e zbriti librin e vërtetë dhe drejtësinë. Ti nuk mund ta dish, ndishta çasti i shkatërrimit është afër.');
INSERT INTO "ahmeti" VALUES(42,18,'42:18','Ata që nuk i besojnë atij, kërkojnë ngutjen e tij, ndërsa ata që i besojnë, i frokësohen dhe e dinë se ajo është e vërtetë. Ta dini se ata që bëjnë polemikë, duke dyshuar në momentin e katastrofës së përgjithshme, janë në një humbje të thellë.');
INSERT INTO "ahmeti" VALUES(42,19,'42:19','All-llahu është shumë bamirës për robërit e vet; Ai e begaton atë që do; Ai është i gjithfuqishmi, ngadhënjyesi.');
INSERT INTO "ahmeti" VALUES(42,20,'42:20','Kush është që dëshiron fitimin e botës tjetër (ahiretin), Ne do t’ia shtojmë fitimin e tij, e kush e dëshiron vetëm fitimin e kësaj bote, Ne ia japim, po në botën tjetër ai nuk ka hise.');
INSERT INTO "ahmeti" VALUES(42,21,'42:21','A mos kanë ata ortakë (zota ose idhuj) që u përcaktuan atyre fe, të cilën nuk e urdhëroi All-llahu? Po, sikur të mos ishte fjala vendimtare (e All-llahut që shpërblimi dhe ndëshkimi të jenë në ahiret), dhe do të kryhej dënimi mbi ta, e megjithatë, mohuesit do të kenë dënimi të dhembshëm.');
INSERT INTO "ahmeti" VALUES(42,22,'42:22','Do t’i shohësh jobesimtarët të frikësuar nga ajo që vepruan, po, ajo do t’u ndodhë atyre, kurse ata që besuan dhe bënë vepra të mira, do të jenë në kopshtet e Xhenneteve dhe ata kanë te Zoti i tyre çka të dëshirojnë; e kjo është ajo dhuntia e madhe.');
INSERT INTO "ahmeti" VALUES(42,23,'42:23','Ajo (dhunti) konsistonnë atë që All-llahu u jep myzhde robërve të vet, të cilët besuan dhe bënë vepra të mira. Thuaj: “Unë nuk kërkoj prej jush ndonjë shpërblim për thirrjen time vetëm respektin e dashurisë për hir të farefisnisë (akraballëkut)”. Kush bën ndonjë të mirë, Ne ia shumëfishojmë të mira; vërtet, All-llahu falë mëkatet, është mirënjohës.');
INSERT INTO "ahmeti" VALUES(42,24,'42:24','A mos thonë ata se ai (Muhammedi) shpifi gënjeshtër ndaj All-llahut, e nëse dëshiron All-llahu ta mbyllë zemrën tënde, pse All-llahu eliminon të pavërtetën dhe me fjalë e veta e forcon të vërtetën. Ai është që e di çka ka në zemra.');
INSERT INTO "ahmeti" VALUES(42,25,'42:25','Ai është që pranon pendimin e robërve të vet dhe shlyen të këqijat dhe e di çka punoni.');
INSERT INTO "ahmeti" VALUES(42,26,'42:26','Ai është që u përgjigjet (lutjeve të) atyre që besuan dhe bënë punë të mira dhe, nga mirësia e vet, ua shton atyre të mirat. Ndërsa, jobesimtarët kanë dënim të rreptë.');
INSERT INTO "ahmeti" VALUES(42,27,'42:27','Sikur All-llahu t’ua shumonte begatinë robërve të vet, ata do të kalonin kufijtë, por Ai i furnizon me masë që dëshiron, sepse Ai është i njohur hollësisht dhe i dijshëm për robërit e vet.');
INSERT INTO "ahmeti" VALUES(42,28,'42:28','Dhe Ai është që e lëshon shiun, pasi që ata t’i kenë humbur shpresat dhe Ai shtrin mëshirën e vet; Ai është mbikëqyrësi i lavdëruari.');
INSERT INTO "ahmeti" VALUES(42,29,'42:29','Nga argumentet e Tij është krijimi i qiejve dhe i tokës dhe shpërndarja e gjallesave në të dyjat dhe Ai me fuqinë e Tij mund t’i bashkojë kurdo që të dëshirojë.');
INSERT INTO "ahmeti" VALUES(42,30,'42:30','çfarëdo e keqe që mund t’ju godasë, ajo është pasojë e veprave tuaja (të këqia), e për shumë të tjera Ai u falë.');
INSERT INTO "ahmeti" VALUES(42,31,'42:31','E ju nuk mund t’i ikni (ta mposhtni) dënimit në tokë dhe përveç All-llahut nuk keni as mbrojtës e as ndihmës.');
INSERT INTO "ahmeti" VALUES(42,32,'42:32','Dhe nga argumentet (që dokumentojnë fuqinë) e Tij, janë anijet lunduese nëpër det si të ishin kodra.');
INSERT INTO "ahmeti" VALUES(42,33,'42:33','Nëse do Ai, e ndal erën dhe ato ngelin të palëvizshme mbi sipërfaqen e ujit. S’ka dyshim se këto fakte ekzistojnë për secilin durimtarë e mirënjohës.');
INSERT INTO "ahmeti" VALUES(42,34,'42:34','Ose i përmbyt ata për shkak të asaj që vepruan, e për shumë të tjera Ai i falë.');
INSERT INTO "ahmeti" VALUES(42,35,'42:35','Ata që i kontestojnë argumenetet Tona, le ta dinë se nuk kanë shpëtim (nuk mund t’i shmangin dënimit).');
INSERT INTO "ahmeti" VALUES(42,36,'42:36','çka u është dhënë nga ndonjë send, ajo është kënaqësi në këtë botë, e ajo që është te All-llahu është shumë më e mirë dhe e përjetshme, por pë ata që besuan dhe që vetëm Zotit të tyre i mbështeten:');
INSERT INTO "ahmeti" VALUES(42,37,'42:37','Dhe ata që u shmangën mëkateve të mëdha e të shëmtuara, dhe kur hidhërohen, ata falin,');
INSERT INTO "ahmeti" VALUES(42,38,'42:38','Edhe ata që i përgjigjen thirrjes së Zotit të tyre dhe e falin namazin rregullisht dhe ata që konsultohen mes vete për punë të përbashkëta, e nga ajo që Ne ua japim ata e shpërndajnë,');
INSERT INTO "ahmeti" VALUES(42,39,'42:39','Edhe ata që kur i godit e padrejta, i kundërvihen.');
INSERT INTO "ahmeti" VALUES(42,40,'42:40','Ndëshkimi i të keqes, bëhet në të njejtën mas, e kush fal e bën pajtim, shpërblimi i tij është te All-llahu. Vërtet, Ai nuk i do zullumqarët.');
INSERT INTO "ahmeti" VALUES(42,41,'42:41','E kush hakmirret për padrejtësitë i janë bërë, ndaj të tillëve nuk ka ndonjë përgjegjësi.');
INSERT INTO "ahmeti" VALUES(42,42,'42:42','Përgjegjësia (ndëshkimi) është vetëm kundër atyre që u bëjnë padrejtë dhe kundër atyre që pa farë arsye bëjnë çrregullime në tokë. Për të tillët është një dënim i dhembshëm.');
INSERT INTO "ahmeti" VALUES(42,43,'42:43','Kush bën durim dhe fal, s; ka dyshim se ajo është virtyti më i lartë (i lavdishëm).');
INSERT INTO "ahmeti" VALUES(42,44,'42:44','Atë që All-llahu e lë të humbur, për të nuk ka ndonjë ndihmës tjetër, pos Tij. Do t’i shohësh zullumqarët që, kur të vërejnë dënimin, do të thonë: “A ka ndonjë rrugë për rikthim (në dynja)?”');
INSERT INTO "ahmeti" VALUES(42,45,'42:45','Do t’i shohësh ata duke iu afruar atij (zjarrit) të frikësuar nga nënshtrimi, se si e shikojnë me bisht të syrit tinëzisht (me një shikim të vjedhur). Ata që besuan do të thonë: “Vërtet, të humbur në ditën e kijametit janë ata që e humbën vetveten dhe familjen e vet!” Ta dini pra, se mizorët janë në dënim të përjetshëm.');
INSERT INTO "ahmeti" VALUES(42,46,'42:46','Ata nuk kanë mbrojtës që t’u nduhmojë, përveç All-llahut, sepse atë që All-llahu e ka humbur, për të nuk ka rrugëdalje (shpëtim).');
INSERT INTO "ahmeti" VALUES(42,47,'42:47','Përgjigjuni thirrjes së Zotit tuaj para se të vijë një ditë, që All-llahu nuk e kthen më (pasi ta ketë caktuar). Atë ditë ju nuk do të gjeni strehim dhe as nuk do të mund t’i mohoni (mëkatet).');
INSERT INTO "ahmeti" VALUES(42,48,'42:48','Nëse ata (idhujtarët) refuzojnë, Ne nuk të kemi dëruar ty rojë të tyre, ti ke për obligim vetëm komunikimin. Vërtet, kur Ne i dhurojmë njeriut nga ana Jonë ndonjë të mirë, ai gëzohet për të, e kur i godet ndonjë e keqe, që e ka merituar vetë, atëherë njeriu është përbuzës.');
INSERT INTO "ahmeti" VALUES(42,49,'42:49','Vetëm i All-llahut është pushteti i qiejve e i tokës; Ai krijon çka të dojë; Ai i falë vetën femra atij që do; e i falë vetëm meshkuj atij që do.');
INSERT INTO "ahmeti" VALUES(42,50,'42:50','Ose u falë çift, meshkuj e femra, por atë që do e lë pa fëmijë (steril); Ai është i dijshëm, i fuqishëm.');
INSERT INTO "ahmeti" VALUES(42,51,'42:51','Nuk ka asnjë njeri që t’i ketë folur All-llahut ndryshe, vetëm se me anën e frymëzimit, ose pas ndonjë perdeje, ose t’i dërgojë të dërguar (melek), e ai t’i shpallë me lejen e Tij atë që do Ai. Vërtet, Ai është më i larti, më i urti.');
INSERT INTO "ahmeti" VALUES(42,52,'42:52','Po kështu me urdhërin tonë Ne te shpallëm edhe ty shpirtin (Kur’anin). Ti nuk ke ditur çka është libri (Kur’ani) as ç’është besimi, por Ne atë e bëmë dritë me të cilën e vëmë në rrugë të drejtë atë që dëshirojmë prej robërve tanë. Në të vërtetë, edhe ti udhëzon për në rrugë të drejtë,');
INSERT INTO "ahmeti" VALUES(42,53,'42:53','Në rrugën e All-llahut, të cilit i takon çka ka në qiej dhe çka në tokë, e ta dini se të gjitha çështjet janë në vullnetin e All-llahut.');
INSERT INTO "ahmeti" VALUES(43,1,'43:1','Ha, Mimë.');
INSERT INTO "ahmeti" VALUES(43,2,'43:2','Pasha librin (Kur’anin) sqarues!');
INSERT INTO "ahmeti" VALUES(43,3,'43:3','S’ka dyshim se Ne e bëmë atë Kur’an arabisht, në mënyrë që ju ta kuptoni.');
INSERT INTO "ahmeti" VALUES(43,4,'43:4','Dhe se në librin amzë (Lehvi mahfudh) te Ne, gëzon famë të lartë dhe është plot urtësi.');
INSERT INTO "ahmeti" VALUES(43,5,'43:5','A thua Ne ta lëmë këshillimin ndaj ju e t’ju braktisim, pse ju jeni popull i shfrenuar?');
INSERT INTO "ahmeti" VALUES(43,6,'43:6','E, sa pejgamberë kemi dërguar te popujt e lashtë?!');
INSERT INTO "ahmeti" VALUES(43,7,'43:7','Dhe nuk u erdhi atyre asnjë pejgamber, e të mos tallen me të.');
INSERT INTO "ahmeti" VALUES(43,8,'43:8','Andaj, Ne i zhdukëm ata që ishin më të fuqishëm se këta dhe shembulli i atyre të mëparshmive është përmendur më parë (në Kur’an).');
INSERT INTO "ahmeti" VALUES(43,9,'43:9','Nëse ti i pyet ata: “Kush i krijoi qiejt dhe tokën?” Me siguri do të thonë: “Ato i krijoi i gjithfuqishmi, i dijshmi!”');
INSERT INTO "ahmeti" VALUES(43,10,'43:10','Ai që tokën e bëri djep për ju dhe u mundësoi të keni rrugë nëpër të, e të mund të orientoheni (për qëllimet tuaja).');
INSERT INTO "ahmeti" VALUES(43,11,'43:11','Dhe Ai që lëshon nga qielli ujë (shi) me masë. Ne i japim me të jetë një vendi të vdekur. Ja, kështu edhe ju do të nxirreni (të gjallë nga varrezat);');
INSERT INTO "ahmeti" VALUES(43,12,'43:12','Dhe Ai që krijoi të gjitha llojet (çiftet) dhe ju mundësoi të udhëtoni hipur në anije ose në kafshë.');
INSERT INTO "ahmeti" VALUES(43,13,'43:13','Të uleni mbi to dhe atëherë, pasi të jeni vendosur mbi to, ta përkujtoni të mirën që u dha Zoti juaj e të thoni: “Falënderuar qoftë Ai që i nënshtroi këto për neve, sepse ne nuk do të kishim mundësi ta bënim këtë.');
INSERT INTO "ahmeti" VALUES(43,14,'43:14','Dhe ne me siguri do të kthehemi te Zoti ynë!”');
INSERT INTO "ahmeti" VALUES(43,15,'43:15','Megjithatë (pranuan se Zoti është i vetmi krijues), ata i përshkruan Atij pjesë (fëmijë) nga robërit e Tij. Njeriu i tilë njëmend është mohues i hapët.');
INSERT INTO "ahmeti" VALUES(43,16,'43:16','A mos Ai nga ato që krijoi, për vete përcaktoi vajza, kurse juve u dalloi me djem?');
INSERT INTO "ahmeti" VALUES(43,17,'43:17','E kur ndonjëri prej jush lajmërohet me (lindje të vajzës) atë që ia pat përshkruar Zotit shembull, fytyra e tij, nxihet dhe i zihet fryma.');
INSERT INTO "ahmeti" VALUES(43,18,'43:18','A, atë që rritet me stoli, e në dialog është i paqartë (ia përshkruajnë Zotit)?');
INSERT INTO "ahmeti" VALUES(43,19,'43:19','Edhe engjëjt që janë adhurues të Zotit i quajnë femra? A prezentuan ata krijimin e tyre (melekëve)? Dëshmia e tyre do të regjistrohet dhe ata do të merren në pyetje.');
INSERT INTO "ahmeti" VALUES(43,20,'43:20','Ata pastaj thanë: “Sikur të kishte dashur All-llahu, ne nuk do t’i adhuronim ata (as idhujt e as melaiket). Ata nuk kanë për këtë (që thonë) ndonjë fakt, ata vetëm gënjejnë”.');
INSERT INTO "ahmeti" VALUES(43,21,'43:21','A mos u kemi dhënë ndonjë libër para tij (para Kur’anit), e ata i përmbahen atij?');
INSERT INTO "ahmeti" VALUES(43,22,'43:22','Jo, por ata thanë: “Ne i kemi gjetur të parët tanë në këtë fe dhe vazhdojmë gjurmëve të tyre”.');
INSERT INTO "ahmeti" VALUES(43,23,'43:23','Ja pra, Ne nuk kemi dërguar para teje pejgamber në ndonjë vendbanim, që të mos i ketë thënë paria e begatshme e tij: “Ne i gjetëm të parët tanë në këtë fe dhe ne jemi të orientuar gjurmëve të tyre”.');
INSERT INTO "ahmeti" VALUES(43,24,'43:24','Ai tha: “A edhe nëse ju kam sjell rrugë më të mirë nga ajo që i gjetët të parët tuaj?” Ata thanë: “Ne nuk i besojmë asaj me çka ju jeni dërguar!”');
INSERT INTO "ahmeti" VALUES(43,25,'43:25','Atëherë Ne ndërmorrëm ndëshkime kundër tyre, e shih si ishte përfundimi i gënjeshtarëve.');
INSERT INTO "ahmeti" VALUES(43,26,'43:26','(Pëërkujto, o i dërguar) Kur Ibrahimi babait të vet dhe popullit të tij i tha: “Unë jam i larguar prej asaj që adhroni ju,');
INSERT INTO "ahmeti" VALUES(43,27,'43:27','përveç Atij që më krijoi, dhe që Ai do të më drejtojë!”');
INSERT INTO "ahmeti" VALUES(43,28,'43:28','Dhe ai (Ibrahimi) e la të përjetshme atë fjalë (besimn në një Zot) ndër pasardhësit e vet me shpresë që ata të kthehen prej rrugës së gabuar në rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(43,29,'43:29','Por, Unë u dhashë mundësi këtyre dhe të parëve të tyre të kënaqen derisa nuk u erdhi e vërteta dhe i dërguari me fakte.');
INSERT INTO "ahmeti" VALUES(43,30,'43:30','Po, kur u erdhi atyre e vërteta, ata thanë: “Kjo është magji dhe se ne kësaj nuk besojmë!”');
INSERT INTO "ahmeti" VALUES(43,31,'43:31','Pastaj thanë: “Përse të mos ketë zbritur ky Kur’an një njeriu të madh nga dy qytete?”');
INSERT INTO "ahmeti" VALUES(43,32,'43:32','A thua ata e përcaktojnë mëshirën (pejgamberllëkun) e Zotit tënd? Ne kemi përcaktuar ndër ta gjendjen e jetës në këtë botë; Ne kemi dalluar disa në shkallë më të lartë se të tjerët, që të shfrytëzojnë njëri-tjetrin për shërbime. E mëshira (caktimi për pejgamber) e Zotit tënd është shumë më e dobishme se ajo që ata grumbullojnë.');
INSERT INTO "ahmeti" VALUES(43,33,'43:33','E sikur të mos ishte që njerëzit (do të lakmonin) të jenë që nuk feje (jobesimtarë), Ne atyre që nuk e besojnë Zotin do t’ua bënim ppulazet e shtëpive të tyre nga argjendi si dhe shkallët nga argjendi, mbi të cilat ata do të ngriteshin.');
INSERT INTO "ahmeti" VALUES(43,34,'43:34','E edhe dyert e shtëpive të tyre nga argjendi edhe kolltukët, mbi të cilët do të mbështeteshin.');
INSERT INTO "ahmeti" VALUES(43,35,'43:35','Edhe stoli të ndryshme (ari etj.). E, të gjitha këto nuk janë gjë tjetër pos kënaqësi e jetës së kësaj bote, kurse bota e ardhshme te Zoti është për besimtarët e ruajtur.');
INSERT INTO "ahmeti" VALUES(43,36,'43:36','Kush mbyll sytë para këshillave të Zotit, atij ia shoqërojmë një djall që nuk i ndahet kurrë.');
INSERT INTO "ahmeti" VALUES(43,37,'43:37','E ata (djajt) do t’i shmangin nga rruga e drejtë, kurse (jobesimtarët) mendojnë se janë duke i udhëzuar.');
INSERT INTO "ahmeti" VALUES(43,38,'43:38','E kur të vijë ai (jobesimtari) para Nesh, do të thotë: “Ah, të kishim qenë larg mes vete sa lindja me perëndimin; sa shok i keq je ti!”');
INSERT INTO "ahmeti" VALUES(43,39,'43:39','Dhe sot, për shkak se ishit zullumqarë, shoqërimi juaj nuk do t’ju bëjë dobi në vuajtjet tuaja.');
INSERT INTO "ahmeti" VALUES(43,40,'43:40','A mos ti do ta bësh të dëgjojë i shurdhëti, ose ta drejtosh të verbërin, apo atë që është zhytur në humbje të thellë?');
INSERT INTO "ahmeti" VALUES(43,41,'43:41','E, nëse të tërheqim ty (të marrim jetën ose të shpërngulim), ngase Nr kemi fuqi kundër tyre.');
INSERT INTO "ahmeti" VALUES(43,42,'43:42','Ose, mund të bëjmë që ti ta shohësh atë që ne u premtuam (dënimin), ngase Ne kemi fuqi kundër tyre.');
INSERT INTO "ahmeti" VALUES(43,43,'43:43','Andaj, ti përmbaju asaj që të shap;; et, e s’ka dyshim se ti je në rrugë të drejtë.');
INSERT INTO "ahmeti" VALUES(43,44,'43:44','Dhe se ajo (shpallja) është lëvdatë e madhe për ty dhe për popullin tënd dhe për këtë (lëvdatë) më vonë do të përgjigjeni.');
INSERT INTO "ahmeti" VALUES(43,45,'43:45','E ti, pyeti ata të dërguarit që i dërguam para teje, a kemi lejuar që në vend të All-llahut të adhurohen zota tjerë?');
INSERT INTO "ahmeti" VALUES(43,46,'43:46','Ne e patëm dërguar Musain me argumentet tona te faraoni dhe rrethi i tij, e ai tha: “Unë jam i dërguari i Zotit të gjithësisë!”');
INSERT INTO "ahmeti" VALUES(43,47,'43:47','E kur u solli ai argumentet Tona, ata u tallën me to.');
INSERT INTO "ahmeti" VALUES(43,48,'43:48','Dhe Ne nuk u treguam asnjë mreklli (ndëshkuese) që nuk ishin të mëdha se njëra-tjetra dhe ashtu i ndëshkuam në mënyrë që të tërhiqeshin nga rruga që praktikonin.');
INSERT INTO "ahmeti" VALUES(43,49,'43:49','De ata thanë: “O ti magjistar, lute për ne Zotin tënd që të ka dhënë (të na largojë dënimin) se ne po besojmë!”');
INSERT INTO "ahmeti" VALUES(43,50,'43:50','E kur ua hoqëm atyre dënimin, qe, ata e thyen besën.');
INSERT INTO "ahmeti" VALUES(43,51,'43:51','Ndërsa, faraoni thirri popullin e vet e tha: “O popull imi, a nuk është i imi pushteti i Egjiptit (i Misirit) dhe i këtyre lumenjve që rrjedhin nën pallatin tim, a nuk po shihni?”');
INSERT INTO "ahmeti" VALUES(43,52,'43:52','Pra, unë jam më i mirë se ky qyqar që mëzi flet!');
INSERT INTO "ahmeti" VALUES(43,53,'43:53','përse nuk i janë atij bylyzykë nga ari, ose të kenë të kenë ardhur bashkë me të engjëjt shoqërues?');
INSERT INTO "ahmeti" VALUES(43,54,'43:54','Dhe ashtu ai e frikësoi popullin e vet, e ata e respektuan, por ata ishin vërtet popull i shkatërruar.');
INSERT INTO "ahmeti" VALUES(43,55,'43:55','Kur ata nxitën hidhërimin Tonë, Ne iu hakmorëm atyre dhe i përmbytëm të gjithë.');
INSERT INTO "ahmeti" VALUES(43,56,'43:56','Dhe i bëmë ata shembull e përvojë për të tjerët.');
INSERT INTO "ahmeti" VALUES(43,57,'43:57','Kur iu përmend popullit tënd si shembull biri i Merjemes, ata brohoritën.');
INSERT INTO "ahmeti" VALUES(43,58,'43:58','Dhe thanë: “A janë më të mirë zotat tanë apo ai?” Ata nuk të thanë atë, vetëm si polemikë, por ata janë njerëz ngatrrestarë.');
INSERT INTO "ahmeti" VALUES(43,59,'43:59','Ai (Isai) ka qenë vetëm një rob, të cilin e bëmë pejgamber dhe e bëmë shembull të jashtëzakonshëm si përvojë për beni israilët.');
INSERT INTO "ahmeti" VALUES(43,60,'43:60','Po sikur të duam Ne, do të bënim prej jush engjëjt që do t’ju zëvendësonin në tokë.');
INSERT INTO "ahmeti" VALUES(43,61,'43:61','E ai (Isai) është parashenjë e momentit (e kijametit), pra kurrsesi mos dyshoni në të (në katastrofë) dhe eni pas meje kjo është rrugë e drejtë.');
INSERT INTO "ahmeti" VALUES(43,62,'43:62','E të mos u pengojë djalli, se ai për ju është armik i hapët.');
INSERT INTO "ahmeti" VALUES(43,63,'43:63','Po kur Isai erdhi me argumente tha: “Erdha te ju me pejgamberllëk dhe erfha t’ju sqaroj atë pjesë që e kundërshtonit, pra kini frikë All-llahun dhe më respektoni mua!');
INSERT INTO "ahmeti" VALUES(43,64,'43:64','S’ka dyshim, se All-llahu është Ai Zoti juaj, andajAtë adhuronie! Kjo është rrugë e drejtë!”');
INSERT INTO "ahmeti" VALUES(43,65,'43:65','Dhe grupet u përçanë mes vete, e të mjerët ata që janë zullumqarë nga dënimi i ditës së dhembshme.');
INSERT INTO "ahmeti" VALUES(43,66,'43:66','Nuk presim tjetër ato (grupe) përveç katastrofë t’u vijë befas duke mos e hetuar fare.');
INSERT INTO "ahmeti" VALUES(43,67,'43:67','Atë ditë shokët e ngushtë do të jenë armiq të njeri-tjetrit, përveç atyre që ishin të sinqertë në miqësi.');
INSERT INTO "ahmeti" VALUES(43,68,'43:68','(Atyre besimtarëve, që ishin shoqëruar për hir të Zotit, u thuhet): “O adhuruesit e Mi, sot nuk ka as frikë për ju, e as që do të jeni të pikëlluar!”');
INSERT INTO "ahmeti" VALUES(43,69,'43:69','(Robërit e Mi) të cilët besuan argumentet dhe ishin myslimanë.');
INSERT INTO "ahmeti" VALUES(43,70,'43:70','Hyni në Xhennete, ju she gratë tuaja, të gëzuar!');
INSERT INTO "ahmeti" VALUES(43,71,'43:71','Atyre u shërbejnë me enë e gastare nga ari, aty do të kenë çka t’u dëshirojnë shpirti dhe t’u kënaqet syri. Ju do të jeni aty përgjithmonë.');
INSERT INTO "ahmeti" VALUES(43,72,'43:72','E ky është Xhenneti që u është dhënë për atë që keni punar.');
INSERT INTO "ahmeti" VALUES(43,73,'43:73','Aty keni shumë pemë prej të cilave do të hani.');
INSERT INTO "ahmeti" VALUES(43,74,'43:74','Ndërkaq, kriminelët janë në vuajtje të përjetshme të Xhehennemit.');
INSERT INTO "ahmeti" VALUES(43,75,'43:75','Atyre as nuk u lehtësohet (vuajtja) dhe aty janë të dëshpruar.');
INSERT INTO "ahmeti" VALUES(43,76,'43:76','Ne nuk u bëmë atyre padrejtësi, por ata vetë kanë qenë horra.');
INSERT INTO "ahmeti" VALUES(43,77,'43:77','Dhe ata thërrasin: “O malik, le të në e marrë shpirtin Zoti yt!” ai thitë: “Ju do të jeni aty përgjithmonë!”');
INSERT INTO "ahmeti" VALUES(43,78,'43:78','Ne u patëm sjellë të vërteën, por shumica prej jush ishit urrejtës të së vëtetës.');
INSERT INTO "ahmeti" VALUES(43,79,'43:79','A mos vëndosët për ndonjë çështje (grackë kundër Muhammedit), por edhe Ne kemivendosur (ta ndihmojmë).');
INSERT INTO "ahmeti" VALUES(43,80,'43:80','A mendojnë se Ne nuk dërguam ndjenjën e fshehtë të tyre dhe bisedën mes tyre? Po, e dëgjomë dhe të dërguarit tonë (engjëjt përcjellës) që janë pranë tyre, shkruajnë.');
INSERT INTO "ahmeti" VALUES(43,81,'43:81','Thuaj: “Sikurse Mëshiruesi (All-llahu) të kishte fëmijë, unë do të jem i pari adhurues (pse unë e di më së miri se ajo është gënjeshtër)!”');
INSERT INTO "ahmeti" VALUES(43,82,'43:82','Larg asaj është Zoti i qiejve e i tokës, Zoti i Arshit, nga aji që ata i përshkruajnë.');
INSERT INTO "ahmeti" VALUES(43,83,'43:83','Po ti, lëri ata të zhyten edhe më thellë derisa të arrijnë në ditën që u është përcaktuar.');
INSERT INTO "ahmeti" VALUES(43,84,'43:84','AI është që në qiell është All-llahu dhe në tokë All-llah, Ai është i urti, i dijshmi.');
INSERT INTO "ahmeti" VALUES(43,85,'43:85','I lartësuar qoftë Ai, që në sundimin e qiejve e i tokës dhe çka ka ndërmjet tyre dhe vetëm Ai di për momentin e kijametit dhe te Ai ktheheni.');
INSERT INTO "ahmeti" VALUES(43,86,'43:86','Ata që i adhuruan pos Tij, nuk mund të ndërmjetsojnë (të bëjnë shefaat), përveç kush dëshmoi të vërtetën, e ata e dinë.');
INSERT INTO "ahmeti" VALUES(43,87,'43:87','Po nëse ti i pyet: Kush i krijoi ata, me siguri do të thonë: “All-llahu!” E si pra, ia kthejnë shpinën?');
INSERT INTO "ahmeti" VALUES(43,88,'43:88','(Zoti e di) Edhe thënien e tij (të Muhammedit): “O Zoti im, këta janë një popull që nuk beson!”');
INSERT INTO "ahmeti" VALUES(43,89,'43:89','(E, Zoti iu përgjegj): Hiqu tyre pra, thuaj: “Qofshin braktisur, e më vonë do ta kuptojnë!”');
INSERT INTO "ahmeti" VALUES(44,1,'44:1','Ha, Mimë.');
INSERT INTO "ahmeti" VALUES(44,2,'44:2','Pasha librin sqarues (të së drejtës nga e kota)!');
INSERT INTO "ahmeti" VALUES(44,3,'44:3','Ne e zbritëm ate në një natë të bekuar (në natën e begatshme të kadrit). Ne dëshiruam t’u tërheqim vërejtjen, e njerëzit të jenë të gatshëm.');
INSERT INTO "ahmeti" VALUES(44,4,'44:4','Në atë (natë) zgjidhet çdo çshtje në mënyrë të prerë.');
INSERT INTO "ahmeti" VALUES(44,5,'44:5','Urdhër i përcaktuar nga Vetë Ne. S’ka dyshim se Ne dërguam të dërguar.');
INSERT INTO "ahmeti" VALUES(44,6,'44:6','(E zbeitëm) Nga Mëshira e Zotit tënd; Ai është dëgjuesi, i dijshmi.');
INSERT INTO "ahmeti" VALUES(44,7,'44:7','Zoti i qiejve e i tokës dhe çka ka ndërmjet tyre, nëse jeni të bindur.');
INSERT INTO "ahmeti" VALUES(44,8,'44:8','Nuk ka Zot tjetër, vetëm Ai që jep jetë dhe vdekje, Zoti juaj dhe Zoti i prindërve tuaj të lashtë.');
INSERT INTO "ahmeti" VALUES(44,9,'44:9','Por jo, ata (idhujtarët) janë në dyshim, janë duke luajtur.');
INSERT INTO "ahmeti" VALUES(44,10,'44:10','Ti pra, prite ditën kur qielli sjell një tym konkret.');
INSERT INTO "ahmeti" VALUES(44,11,'44:11','Ai (tymi) i përfdhinë njerëzit. Ky është një ndëshkim i mundimshëm.');
INSERT INTO "ahmeti" VALUES(44,12,'44:12','(Ata thonë) Zoti ynë, largoje prej nesh dënimin, ne do të besojmë.');
INSERT INTO "ahmeti" VALUES(44,13,'44:13','Prej nga atyre ajo këshillë (ai mësim), kur atyre u pat ardhur pejgamber me argument (e nuk besuan)?');
INSERT INTO "ahmeti" VALUES(44,14,'44:14','Po ia kthyen shpinën atij (pejgamberit) dhe thanë: “I mësuar prej dikujt, i çmendur”.');
INSERT INTO "ahmeti" VALUES(44,15,'44:15','Ne do t’ua largojmë dënimin për pak kohë, por ju do t’i ktheheni kotësisë.');
INSERT INTO "ahmeti" VALUES(44,16,'44:16','(Përkujto) Ditën kur do t’i kapim me atë rrëmbimin Tonë të fuqishëm e do t’u hakmerremi.');
INSERT INTO "ahmeti" VALUES(44,17,'44:17','Ne përpara tyre e vumë në sprovë popullin e faraonit, dhe i pat ardhur i dërguar i ndershëm.');
INSERT INTO "ahmeti" VALUES(44,18,'44:18','(U pat thënë): “Të m’i dorëzoni robërit e All-llahut, unë sjell argument të sigurt.');
INSERT INTO "ahmeti" VALUES(44,19,'44:19','E mos bëni mendjemadhësi ndaj All-llahut, unë sjell argument të sigurt.');
INSERT INTO "ahmeti" VALUES(44,20,'44:20','Unë i jam mbështetur Zotit tim dhe Zotit tuaj nga (Kërcënimi) që të më gurëzoni (më mbytni).');
INSERT INTO "ahmeti" VALUES(44,21,'44:21','E nëse nuk më besoni atëherë hiqnu meje (më lini të lirë)!”');
INSERT INTO "ahmeti" VALUES(44,22,'44:22','E thirri Zotin e vet: “Këta janë popull kriminelë”.');
INSERT INTO "ahmeti" VALUES(44,23,'44:23','(Ne i thamë) Tërhiqu me robërit e Mi natën, se do të jeni të ndjekur.');
INSERT INTO "ahmeti" VALUES(44,24,'44:24','Dhe lëre detin ashtu të qetë (të hapur) se ata janë ushtri që do të përmbytet.');
INSERT INTO "ahmeti" VALUES(44,25,'44:25','Sa kopshte e kroje kanë lënë!');
INSERT INTO "ahmeti" VALUES(44,26,'44:26','Edhe ara të mbjella e vende të bukura.');
INSERT INTO "ahmeti" VALUES(44,27,'44:27','dhe sa të mira që i kanë përjetuar.');
INSERT INTO "ahmeti" VALUES(44,28,'44:28','Ja, ashtu atë ua lëmë në trashëgim një populli tjetër.');
INSERT INTO "ahmeti" VALUES(44,29,'44:29','Prë ta nuk qajtën as qielli e as toka dhe atyre nuk iu dha afat.');
INSERT INTO "ahmeti" VALUES(44,30,'44:30','Ndërsa Ne i shpëtuam beni israilët prej vuajtjeve nënqmuese,');
INSERT INTO "ahmeti" VALUES(44,31,'44:31','Prej faraonit. Vërtet ai ishte mizor i pakufishëm.');
INSERT INTO "ahmeti" VALUES(44,32,'44:32','Ne i patëm zgjedhur ata (besimtarët e beni israilëve) me vetëdije ndër njerëzit e asaj kohe.');
INSERT INTO "ahmeti" VALUES(44,33,'44:33','Dhe u dhamë argumente (mrekulli) në të cilat patën përvojë të qartë.');
INSERT INTO "ahmeti" VALUES(44,34,'44:34','E këta (populli yt) me siguri do të thonë:');
INSERT INTO "ahmeti" VALUES(44,35,'44:35','“S’ka tjetër, vetëm ajo vdekja jonë e parë dhe ne nuk do të ringjallemi!');
INSERT INTO "ahmeti" VALUES(44,36,'44:36','Nëse jeni të vërtetë çka thoni (u thonë Muhammedit e besimtarëve), na sillni pra (në jetë) etërit tanë (të vdekur)!');
INSERT INTO "ahmeti" VALUES(44,37,'44:37','A janë ata (idhujtarët) më të fortë, apo populli i Tubeit dhe ata që ishin përpara tyre. Ne ata i zhdukëm, sepse ishin kriminelë.');
INSERT INTO "ahmeti" VALUES(44,38,'44:38','Ne nuk krijuam qiejt e tokën dhe çka ndërmjet tyre, pa një qëllim.');
INSERT INTO "ahmeti" VALUES(44,39,'44:39','Por, Ne i krijuam të dyja me qëllim të caktuar, mirëpo shumica e tyre nuk dinë.');
INSERT INTO "ahmeti" VALUES(44,40,'44:40','S’ka dyshim se dita e kijametit ku kryhet gjykimi, është e caktim i të gjithë atyre.');
INSERT INTO "ahmeti" VALUES(44,41,'44:41','Në atë ditë nuk bën dobi asgjë miku për mikun, e as nuk mund të ndihmohen.');
INSERT INTO "ahmeti" VALUES(44,42,'44:42','Përjashtim bën ai që e mëshiron All-llahu, se Ai është ngadhnjyesi, i mëshirshmi.');
INSERT INTO "ahmeti" VALUES(44,43,'44:43','Është e vërtetë se pema e Zekumit,');
INSERT INTO "ahmeti" VALUES(44,44,'44:44','Do të jetë ushqim i mëkatarëve.');
INSERT INTO "ahmeti" VALUES(44,45,'44:45','vlon si katran (si pezhgeja) në barqet.');
INSERT INTO "ahmeti" VALUES(44,46,'44:46','Ashtu si vlon uji i valë.');
INSERT INTO "ahmeti" VALUES(44,47,'44:47','(U thuhet engjëjve për mëkatarin) Rrëmbene e grahne ne mes të Xhehennemit.');
INSERT INTO "ahmeti" VALUES(44,48,'44:48','Dhe hudhni mbi kokën e tij ujin e valë e shtonja mundimin.');
INSERT INTO "ahmeti" VALUES(44,49,'44:49','(I thuhet): Shijoje! se ti je ai forti, i autoritetshmi.');
INSERT INTO "ahmeti" VALUES(44,50,'44:50','E ky është ai (dënimi) për të cilin dyshonit.');
INSERT INTO "ahmeti" VALUES(44,51,'44:51','Vërtet, ata që i patën frikë Zotit, janë në vende të qeta.');
INSERT INTO "ahmeti" VALUES(44,52,'44:52','Janë në Xhennete e në burime që rrjedhin.');
INSERT INTO "ahmeti" VALUES(44,53,'44:53','Veshin petka nga mëndafshi e kadifeja, ulur ballë për ballë.');
INSERT INTO "ahmeti" VALUES(44,54,'44:54','Ja kështu, edhe e martojmë më bardhoshe symëdha (me hyrija).');
INSERT INTO "ahmeti" VALUES(44,55,'44:55','aty kërkojnë t’u sillen çdo lloj peme dhe atyjanë të sigurt.');
INSERT INTO "ahmeti" VALUES(44,56,'44:56','Aty nuk do të përjetojnë vdekjen, përveç asaj të parës në dynja. Ata i shpëtoi (All-llahu) prej vuajtjeve të Xhehennemit.');
INSERT INTO "ahmeti" VALUES(44,57,'44:57','(Ato të mira) Janë dhuratë nga Zoti yt, e ai është shpëtim i madh.');
INSERT INTO "ahmeti" VALUES(44,58,'44:58','Ne ua bëmë të lehtë atë (Kur’anin) në gjuhën tënde ashtu që ata të marin mësim.');
INSERT INTO "ahmeti" VALUES(44,59,'44:59','(Meqë nuk po marrim mësim) Ti pra, prit se edhe ata janë duke pritur.');
INSERT INTO "ahmeti" VALUES(45,1,'45:1','Ha, Mimë.');
INSERT INTO "ahmeti" VALUES(45,2,'45:2','Zbritja e librit është prej All-llahut, të gjithëfuqishmit, të urtit.');
INSERT INTO "ahmeti" VALUES(45,3,'45:3','Për besimtarët ekzistojnë argumentet në qiej dhe në tokë.');
INSERT INTO "ahmeti" VALUES(45,4,'45:4','Për një popull që sinqerisht është bindur ka argumente edhe në krijimin tuaj dhe na krijimin e shtazëve të përhapura.');
INSERT INTO "ahmeti" VALUES(45,5,'45:5','Edhe në ndërrimin e natës e të ditës, në atë furnnizim që All-llahu e lëshon prej qiellit dhe me te, pasi të ketë vdekur e ngjall tokën; edhe në qarkullimin e erërave për një popull që logjikon ka argumente.');
INSERT INTO "ahmeti" VALUES(45,6,'45:6','Këto janë fakte të All-llahut që po t’i lexojmë ty në mënyrë të saktë; cilës fjallë pra, pos fjalës së All-llahut dhe argumenteve të Tij, ata i besojnë?');
INSERT INTO "ahmeti" VALUES(45,7,'45:7','Ç‘do gënjeshtar e shumë mëkatarë është i shkatërruar.');
INSERT INTO "ahmeti" VALUES(45,8,'45:8','Që i dëgjon ajetet e All-llahut, të cilat lexohen, e pastaj vazhdo prapë si mendjemadh sikur nuk i ka dëgjuar ato. Atë lajmroje për një vuajtje të dhembshme.');
INSERT INTO "ahmeti" VALUES(45,9,'45:9','Edhe kur të mëson diçka prej ajeteve Tona, ai i merr ato për tallje. Për të tillët pason një dënim shtypës.');
INSERT INTO "ahmeti" VALUES(45,10,'45:10','Përpara tyre e kanë Xhehennemin (që i pret) dhe nuk do t’u bëjë dobi asgjë ajo që kanë fituar, e as ndihmësit që i adhuruan, pos All-llahut. Ata do të përjetjnë një dënim të madh.');
INSERT INTO "ahmeti" VALUES(45,11,'45:11','Ky (Kur’ani) është një udhërrëfyes i plotë, e ata që nuk i pranuan ajetet e Zotit të tyre, i pret një dëmim më i mundimshëm.');
INSERT INTO "ahmeti" VALUES(45,12,'45:12','All-llahu është Ai, që për ju e nënshtroi detin që me lejen e tij të lundrojnë anijet nëpër të, që të kërkojnë begatitë e tij; ndaj, falëderonie.');
INSERT INTO "ahmeti" VALUES(45,13,'45:13','Dhe për ju nënshtroi gjithë ç’ka në qie dhe ç’ka në tokë, njëmend për njerëzit që mendojnë thellë, në to ekzistojnë argumete.');
INSERT INTO "ahmeti" VALUES(45,14,'45:14','Thuaju atyre që besuan: Le t’ua falin atyre që nuk presin ndëshkimet e All-llahut (pse nuk besojnë), ashtu që Ai vetë ta dënojë një popull për shkak të veprave që bëjnë.');
INSERT INTO "ahmeti" VALUES(45,15,'45:15','Kush bën vepra të mira, bën për vete, e kush bën keq, bën kundër vetes, pastaj do të ktheheni te Zoti juaj.');
INSERT INTO "ahmeti" VALUES(45,16,'45:16','Ne u patëm dhënë beni israilëve librin dhe pejgamberllukn dhe aftësi për të gjykuar në mes të njerëzve, i patëm furnizuar ata me të mira dhe i patëm dalluar nga njerëzit e tjerë.');
INSERT INTO "ahmeti" VALUES(45,17,'45:17','U patëm dhënë edhe fakte te qarta për çështjen e fesë. Mirëpo, për shkak të zilisë që kishin ndërmjet vete, ata u përçanë vetëm atëherë kur u erdhi dija që ta kuptojnë, s’ka dyshim se Zoti yt në ditën e kijametit do të gjykojë në mes tyre për shkaçet e përçarjes së tyre.');
INSERT INTO "ahmeti" VALUES(45,18,'45:18','Pastaj, Ne të vumë ty në një rrugë të drejtë të fesë, pra ti ndiqe atë e mos ndiq dëshirat e atyre që nuk dinë.');
INSERT INTO "ahmeti" VALUES(45,19,'45:19','Ata nuk mund të mbrojnë ty për asgjë te All-llahu. Zullumqarët janë miq të njërit-tjetrit, kurse All-llahu është mbrojtës i besimtarëve të devotshëm.');
INSERT INTO "ahmeti" VALUES(45,20,'45:20','Ky (Kur’ani) është dritë e dijes për njerëz, është udhëzues e mëshirë për një popull që beson bindshëm.');
INSERT INTO "ahmeti" VALUES(45,21,'45:21','A menduan ata, të cilët vepruan në të këqija, se në jetën e tyre dhe në vdejken e tyre do t’i bëjmë të barabartë me ata që besuan dhe bënë vepra të mira? Sa i shëmtuar është gjykimi ti tyre!');
INSERT INTO "ahmeti" VALUES(45,22,'45:22','All-llahu krijoi edhe qiejt edhe tokën me një drejtësi precize, e për t’u shpërblyer secilit njeri me veprat e veta, atyre nuk u bëhet e padrejtë.');
INSERT INTO "ahmeti" VALUES(45,23,'45:23','A e ke parë ti (Muhammed) atë që duke e ditur, dëshirën e vet e respekton si zot të vetin, atë All-llahu e ka humbur, ia ka mbyllur të dëgjuarit dhe zenrën e tij, i ka vënë perde mbi të parit e tij, më thuaj, pos All-llahut, kush mund ta udhëzojë atë? A nuk merrni mësim?');
INSERT INTO "ahmeti" VALUES(45,24,'45:24','Ata edhe thanë: “Nuk ka tjetër, vetëm se kjo jetë jona në këtë botë, po vdesim dhe po lindemi dhe asgjë nuk na shkatërron tjetër pos kohës. Ata për këtë nuk dinë asjë, ata vetëm fantazojnë.');
INSERT INTO "ahmeti" VALUES(45,25,'45:25','E kur atyre u lexohen ajetet Tona, të qarta ata nuk kanë fakt tjetër, pos të thonë: “Nese jeni të saktë na sillni të gjallë prindërit tanë!”');
INSERT INTO "ahmeti" VALUES(45,26,'45:26','Thuaj: “All-llahu juve ju jep jetën dhe ju bën të vdisni (e jo koha); mandej, do t’ju tubojë në ditë e gjykimit për të cilën nuk ka dyshim, por shumica e njerëzve nuk dinë (përfuqinë e Zotit).');
INSERT INTO "ahmeti" VALUES(45,27,'45:27','Sundimi i qiejve e i tokës është vetëm i All-llahut, e ditën kur ndodh kijameti, atë ditë mohuesit dëshprohen.');
INSERT INTO "ahmeti" VALUES(45,28,'45:28','Dhe (atë ditë) e sheh secilin popull thirret te libri i vet (shënimet e veprave). (U thuhet): “Sot shpërbleheni me atë që e keni vepruar”.');
INSERT INTO "ahmeti" VALUES(45,29,'45:29','Ky është libri ynë (shënimet e veprave tuaja) që dëshmon të vërtetën (të saktën), Ne kemi kërkuar të shkruhet se ç’vepronit.');
INSERT INTO "ahmeti" VALUES(45,30,'45:30','Sa u përket atyre që besuan dhe bënë vepra të mira, Zoti i tyre i merr ata në mëshirën e vet (në Xhennet), e ai është shpëtim i madh.');
INSERT INTO "ahmeti" VALUES(45,31,'45:31','E, sa u përket atyre që nuk besuan (u thuhet): “A nuk ju patën lexuar argumentet e Mia; e ju bëtë mendjemadhësi de ishit popull i zhytur në mëkate!”');
INSERT INTO "ahmeti" VALUES(45,32,'45:32','Edhe kur u thuhej se premtimi i All-llahut është dhe se do të vijë kijameti për të cilin s’ka dyshim, ju thatë: “Ne nuk dimë se ç’është kijameti, e ne me dyshim menduan për te dhe ne nuk jemi të bindur!”');
INSERT INTO "ahmeti" VALUES(45,33,'45:33','Dhe u dalin në shesh të këqijat që i kanë punuar, dhe i përfshinë ajo me të cilën talleshin.');
INSERT INTO "ahmeti" VALUES(45,34,'45:34','E u thuhet: “Sot po u lëmë të harruar, ashtu sikurse ju e harruat takimin e kësaj dite, vendi juaj është zjarri, për ju nuk ka ndihmëtarë”.');
INSERT INTO "ahmeti" VALUES(45,35,'45:35','Këtë (dënim) për shkak se ju u tallët me ajetet e All-llahut, juve u mashtroi jeta e dynjasë. Sot, pra, nuk do të nxirreni prej tij (zjarrit), e as nuk kërkohet që ata t’i kthehen pendimit të All-llahut.');
INSERT INTO "ahmeti" VALUES(45,36,'45:36','Pra, falënderimi i qoftë vetëm All-llahut, Zotit të qiejve, Zotit të tokës, Zotit të gjithësisë.');
INSERT INTO "ahmeti" VALUES(45,37,'45:37','Vetëm Atij i takon lartëmadhëria në qiej e n tokë dhe Ai është ngadhënjyesi, më i urti.');
INSERT INTO "ahmeti" VALUES(46,1,'46:1','Ha, Mimë.');
INSERT INTO "ahmeti" VALUES(46,2,'46:2','(Ky) Libër i shpallur prej All-llahut, të fuqishmit, të urtit.');
INSERT INTO "ahmeti" VALUES(46,3,'46:3','Ne nuk i krijuam qiejt e tokën dhe atë që ekziston ndërmjet tyre, përpos me qëllim dhe për një afat të caktuar, kurse ata që nuk besuan nuk i vunë veshin asaj me çka u tërhiqet vërejtja.');
INSERT INTO "ahmeti" VALUES(46,4,'46:4','Thuaj: “Më thuani për ata që pos All-llahut i adhuroni, më tregoni se çka krijuan ata në tokë dhe a kanë pjesëmarrje në (krijimin në) qiejtë? Nëse e thoni të vërtetën, më sillni ndonjë libër para këtij ose ndonjë gjurmë të mbetur prej diturisë”.');
INSERT INTO "ahmeti" VALUES(46,5,'46:5','Kush është më i humbur se ai që pos All-llahut lut diç që nuk i përgjigjen atij deri në kijamet, pse ata (që luten) janë gafilë ndaj lutjes së tyre.');
INSERT INTO "ahmeti" VALUES(46,6,'46:6','E kur do të tubohen njerëzit, ata (zotat e tyre) do të jenë armiq të tyre (të adhuruesve) dhe do ta mohojnë adhurimin e tyre (të idhujtarëve).');
INSERT INTO "ahmeti" VALUES(46,7,'46:7','Dhe kur atyre u lexoheshin ajetet Tona të qarta, ata të vërtetës (Kur’anit) që u erdhi, i thanë: “Kjo është magji e hapët!”');
INSERT INTO "ahmeti" VALUES(46,8,'46:8','Ose i thonë se ai (Muhammedi) e trilloi. Thuaj: “Nëse unë kam trilluar atë, ju nuk keni mundësi asgjë të më mbroni prej All-llahut. Ai e di më së miri se çka i mveshni ju atij, por mjafton që Ai është dëshmitarë ndërmet meje e ndërmjet jush, Ai është mëkatfalësi, Mëshiruesi”.');
INSERT INTO "ahmeti" VALUES(46,9,'46:9','Thuaj: “Unë nuk jamë risimtar prej të dërguarve, e nuk e di se çka do të bëhet me mua e as me ju, unë nuk ndjek tjetër vetëm atë që më shpallet, unë nuk jam tjetër pos i dërguar që ju trheq qartas”.');
INSERT INTO "ahmeti" VALUES(46,10,'46:10','Thuaj: “Më tregoni mua se nëse ai (Kur’ani) është rej All-llahut, e ju e mohua (si do të jetë puna e juaj), ndërsa një dëshmitar nga beni israilët e dëshoi si të tillë (të zbritur prej All-llahut) dhe i besoi, kurse ju bëtë kryelartësi (a nuk jeni zulllumqarë)?” E, s’ka dyshim se All-llahu një popull që është mizor, nuk e udhëzon në rrugën e shpëtimit.');
INSERT INTO "ahmeti" VALUES(46,11,'46:11','E ata që nuk besuan, atyre që besuani thanë: “Sikur të ishte ai (Kur’ani - feja) ndonjë e mirë, ata nuk do ta përqafonin para nesh!” E dersa nuk drejtohen me të (Kur’anin), ata do të thonë: “Ky është trillim i kahershëm”.');
INSERT INTO "ahmeti" VALUES(46,12,'46:12','E para tij ishte libri i Musait, prijës dhe mëshirë, e edhe ky është libër që vërteton (librin e Musait), është në gjuhën arabe për t’ia tërhequr vërejtjen atyre që nuk besuan, kurse myzhde për besimtarët.');
INSERT INTO "ahmeti" VALUES(46,13,'46:13','Ata që thanë: “All-llahu është Zoti ynë dhe qëndruan besnikrisht, për ta nuk ka frikë dhe ata nuk do të pikëllohen.');
INSERT INTO "ahmeti" VALUES(46,14,'46:14','Të tillët janë banues të Xhennetit, aty do të jenë përgjithonë, atë e kanë shpërblim pë veprat që i bënë.');
INSERT INTO "ahmeti" VALUES(46,15,'46:15','Ne e urdhëruam njeriun t’u bëjëmirë prindërve të vet, ngase nëna e vet me mundim e barti dhe me vështirësi e lndi, e bartja e tij dhe gjidhënia e tij zgjat tridjetë muaj (e ai vazhdon të jetojë) derisa të arrijë pjekurinë e vet dhe kur t’i mbush dyzet vjet ai thotë: “Zoti im, më inspiro mua që të falënderoj për të mirën tënde që ma dhurove mua dhe prindërve të mij, që të bëj vepra të mira që i pëlqen Ti dhe m’i bën të mirë pasardhësit e mij, unë pendohem te Ti dhe unë jam me myslimanët.');
INSERT INTO "ahmeti" VALUES(46,16,'46:16','Të tillët jan ata që Ne ua pranojmë në mënyrë më të mirë atë që punuan, ua kapërcejmë të këqijat e tyre duke rradhitur me banuesit e Xhennetit. (Ky është) Premtim i vërtetë që u është premtuar.');
INSERT INTO "ahmeti" VALUES(46,17,'46:17','E ai që prindërve të vet u thotë: “Oh, përr ju!, a më premtoni mua se do të ringjallem, kur sa e sa gjenerata kanë kluar para meje (e nuk u ringjallën)?” E ata të dy e lusin All-llahun ta udhëzojë (duke i thënë atij) “I mjeri ti, beso, se premtimi i All-llahut është i saktë!” po ai thotë: “Kjo nuk është tjetër vetëm se legjendë e të lashtëve!”');
INSERT INTO "ahmeti" VALUES(46,18,'46:18','Ata janë të tillë, kundër të cilëve a marrë fund vendimi (të jenë banues të zjarrit) si në popujt nga exhinët dhe njerëzit që kaluan para tyre, sepse ata ishin të humbur.');
INSERT INTO "ahmeti" VALUES(46,19,'46:19','E, secilit sipas veprave që i takon shkalla, e shpërblimi për veprat e tyre do t’u plotësohet e nuk u bëhet padrejtë.');
INSERT INTO "ahmeti" VALUES(46,20,'46:20','E në ditën kur ata që nuk besuan paraqiten pranë zjarrit (e u thonë): ju i shfryëzuat të mirat në jetën e dynjasë dhe i përjetuat ato, e sot, për shkak se keni bërë mendjemadhësi në tokë pa të drejtë dhe për shkak se nuk respektuan urdhërat e Zotit, do të shpërbleheni me dënim nënçmues.');
INSERT INTO "ahmeti" VALUES(46,21,'46:21','Përkjto vëllain e Adit (Hudin) kur popullin e vet në Ahkafë e qortoi, megjithatë qortime pati edhe para edhe pas tij, (u tha): Të mos adhuroni tjetër vetëm se All-llahun, pse unë i kam frikë dënimit tuaj në ditën e madhe!');
INSERT INTO "ahmeti" VALUES(46,22,'46:22','Ata i thanë: “A ke ardhur të na largosh prej zotave taë?! Nëse je i sigurt, na sjell atë me çka na kërcënohesh.');
INSERT INTO "ahmeti" VALUES(46,23,'46:23','Ai tha: “VEtëm All-llahu di atë, ndërsa unë po ju kumtoj atë për çka jam i dërguar, por unë po ju shoh se jeni popull injorant.');
INSERT INTO "ahmeti" VALUES(46,24,'46:24','E kur e panë të paraqitur (renë) të drejtuar kah luginat e tyre thanë: “Kjo re do t na sjellë shi!” jo, kjo është ajo që ju e kërkuat sa më shpejt, një erë (shtrëngatë) me një dënim të dhemshëm.');
INSERT INTO "ahmeti" VALUES(46,25,'46:25','(Erë) që me lejen e Zotit të saj, rrënon çdo send. Dhe aguan ashtuqë nuk shihej tjetër përveç banesave të tyre. Ashtu, Ne e ndëshkojmë popullin zullumqarë.');
INSERT INTO "ahmeti" VALUES(46,26,'46:26','Ne u patëm mundësuar atyre (fuqi, pasuri, jetë të gjatë), atë që nuk u mundësuam juve, u patëm dhënë të dëgjuar, të parë e edhe zemra (të munduar), por atyre nuk u bëri dobi asgjë, as të dëgjuarit e tyre, e as të parit e tyre e as zemrat e tyre, pse ata ishin që i mohonin argumentet e All-llahut, andaj, i përfshiu ajo me të cilën talleshin.');
INSERT INTO "ahmeti" VALUES(46,27,'46:27','Ne i zhdukëm disa vendbanime përreth jush, u përsëritëm argumentet ashtu që të kthehen (në rrugë të drejtë).');
INSERT INTO "ahmeti" VALUES(46,28,'46:28','Përse atyre nuk u ndihmuan zotat, të cilët i adhuruan pos All-llahut, në shenjë të afrimit me ta (te All-llahu)? Por, ata humbën prej syve të tyre. Ajo ishte gënjeshtër e tyre dhe ajo që trillonin ata vetë.');
INSERT INTO "ahmeti" VALUES(46,29,'46:29','(Përkujto) Kur disa prej exhinëve i drejtuam te ti që të dëgjojnë Kur’anin dhe kur u afruan dhe e dëgjuan atë, thanë: “Heshtni!” dhe kur u krye, u kthyen te populli i vet dhe e këshilluan.');
INSERT INTO "ahmeti" VALUES(46,30,'46:30','Thanë: “O populli ynë, në dëgjuam një libër të shpallur pas Musait, që vërteton atë para tij, që udhëzon në të vërtetën dhe në rrugën drejtë!”');
INSERT INTO "ahmeti" VALUES(46,31,'46:31','O populli ynë, përjigjuni thirrësut të All-llahut dhe besoni atij! Ai ju falë mëkatet tuaja dhe ju shpëton prej një dënimi plot vuajtje.');
INSERT INTO "ahmeti" VALUES(46,32,'46:32','E kush nuk i përgjigjet atij që thërret në rrugën e All-llahut, ai nuk është i pamposhtur në tokë dhe pos Atij, ai nuk ka mbrojtës, të tillët janë në një humbje të hapët!”');
INSERT INTO "ahmeti" VALUES(46,33,'46:33','a nuk e dinë ata se All-llahu që krijoi qiejt dhe tokën dhe nuk u lodh në krijimin e tyre, Ai ka fuqi t’i ngjallë të vdekurit. Po, Ai është i plotfuqishëm për çdo send.');
INSERT INTO "ahmeti" VALUES(46,34,'46:34','E ditën kur ata që nuk besuan paraqiten pranë zjarrit (e u thuhet): “A nuk është ky (dënimi) i vërtetë?” Ata thonë: “Po pasha Zotin tonë!” Ai thotë: “Për shkak se nuk besuat, pra vuanie dënimin!”');
INSERT INTO "ahmeti" VALUES(46,35,'46:35','Ti (Muahamed) duro, ashu sikurse duruan të dërguarit e vendosur dhe mos ërko ngutjen e dënimit për ta, sepse ditën kur do ta përjetojnë atë (dënimin) që u është premtuar, atyre u duket sikur nuk kanë jetuar vetëm se në një moment të shkurtër të ditës. Kumtesë e mjaftueshme! A mos shkatërrohet kush, pos popullit të shfrenuar.');
INSERT INTO "ahmeti" VALUES(47,1,'47:1','Ata të cilët vetë nuk besuan dhe penguan edhe të tjerët nga rruga e All-llahut, Ai ua ka asgjesuar veprat e tyre.');
INSERT INTO "ahmeti" VALUES(47,2,'47:2','Ndërsa atyre që besuan, bënë vepra të mira dhe atë që iu shpall Muhammedit e besuan, e ajo është e vërtetë prej Zotit të tyre, Ai atyre ua shlyen mëkatet dhe ua përmirëson gjendjen.');
INSERT INTO "ahmeti" VALUES(47,3,'47:3','Kjo është kështu, ngase ata që nuk besuan ndoqën të pavërtetën, e ata që besuan ndoqën të vërtetën që u erdhi prej Zotit të tyre. Kështu All-llahu u sjell njerëzve shebmujt e tyre.');
INSERT INTO "ahmeti" VALUES(47,4,'47:4','Pra, kur t’i takoni në (luftë) ata që nuk besuan mëshojuni në qafë (mbytni) derisa t’i rraskapitni, atëherë lidhni, e pastaj, ose lidhni ose kërkoni dëmshpërblim përderisa lufta të mos pushojë. Kështu pra, po sikur të dojë All-llahu, Ai do të hakmerrej ndaj tyre, por Ai dëshiron t’ju sprovojë njërin me tjetrin. E ata që ranë dëshmorë në rrugën e All-llahut, atyre kurrsesi nuk ua humb veprat (por ua shton).');
INSERT INTO "ahmeti" VALUES(47,5,'47:5','Ata do t’i udhëzojë dhe dp të përmirësojë gjendjen e tyre.');
INSERT INTO "ahmeti" VALUES(47,6,'47:6','Dhe do t’i shtie në Xhennetin, të cilin ua ka bërë të njohur atyre.');
INSERT INTO "ahmeti" VALUES(47,7,'47:7','O besimtarë, nëse ju ndihmoni (fenë) All-llahun, Ai u ndihmon juve dhe u forzcon këmbët tuaja.');
INSERT INTO "ahmeti" VALUES(47,8,'47:8','E atyre që nuk besuan, ata qofshin të përmbysur, Ai atyre ua ka asgjësuar veprat.');
INSERT INTO "ahmeti" VALUES(47,9,'47:9','Këtë për shkak se ata e urrejtën atë që e zbriti All-llahu, prandaj atyre ua zhduk veprat.');
INSERT INTO "ahmeti" VALUES(47,10,'47:10','A nuk udhëtuan ata nëpër tokë e të shohin se si ishte mbarimi i atyre që ishin përpara tyre e që All-llahu ata i rrënoi, e edhe mosbesimtarët (mekas) i pret shembulli i tyre.');
INSERT INTO "ahmeti" VALUES(47,11,'47:11','Këtë ngase All-llahu është mbrojtës i atyre që besuan, kurse për jobesimtarët nuk ka mbrojtje.');
INSERT INTO "ahmeti" VALUES(47,12,'47:12','Ata që besuan dhe bënë vepra të mira All-llahu do t’i shtie në Xhennete nëpër të cilat rrjedhin lumenj, e ata që nuk besuan, përjetojnë kënaqësi (në këtë jetë) dhe hanë ashtu si hanë kafshët, e veni i tyre ëshrtë zjarri.');
INSERT INTO "ahmeti" VALUES(47,13,'47:13','Se sa (popullatë) qytetesh që ishin shumë më fortë se qyteti nga i cili të dëbuam ty, Ne i kemi shkatërruar dhe për ta nuk pati ndihmëtar.');
INSERT INTO "ahmeti" VALUES(47,14,'47:14','A është i njejtë ai, që është i mbëhtetur në argument të qartë prej Zotit të vet, si ai që veprat e tij të këqia i janë hijeshuar dhe ndjekin epshet e veta?');
INSERT INTO "ahmeti" VALUES(47,15,'47:15','Shembulli i Xhennetit, i cili u është premtuar atyre që janë ruajtur (të devotshëm) në të cilin ka lumenju me ujë për pije, lumenj mga qumështi me shije të paprishur, lumenj nga vera e shijshme për njerëz, lumenj nga mjalti i kulluar, ata kanë aty edhe gjithfarë lloj pemësh, kanë edhe falje nga Zoti i tyre (a është i njejtë) a si ai që është përgjithmonë në zjarr dhe u shuhet etja me ujë të valë që ua copëton zorrët e tyre?');
INSERT INTO "ahmeti" VALUES(47,16,'47:16','Prej tyre ka asish që vijnë të dëgjojnë ty, por kur largohen prej teje, u drejtohen atyre që kanë dije e u thonë: “çka tha ai (Muhammedi) më parë?” Ata janë zemrat e të cilëve ua ka vulosur All-llahu e ata udhëhiqen pas dëshirave të veta.');
INSERT INTO "ahmeti" VALUES(47,17,'47:17','Kurse ata që e gjetën rrugën e drejtë, Ai atyre u shton edhe më udhëzimin në rrugën e drejtë dhe u ofron mundësi të jenë edhe me të matur.');
INSERT INTO "ahmeti" VALUES(47,18,'47:18','Po ata a presin tjetër, pos kijametit, i cili do t’u vijë befas, e parashenjat e tij tashmë kanë ardhur. E prej nga atyre këshillimi (pendimi) kur t’u vijë kijameti?');
INSERT INTO "ahmeti" VALUES(47,19,'47:19','Atëherë, dije se nuk ka Zot tjeter pos All-llahut, kërko falje për mëkatin tënd, për të besimtarëve e të besimtareve, All-llahu edi për lëshimet tuaja (për vepra) dhe për vendin tuaj.');
INSERT INTO "ahmeti" VALUES(47,20,'47:20','E ata që besuan po thonë: “Përse të mos shpallet një sure (që na e obligon luftën)?” E kur u shpall sureja e qartë dhe në të u përmed lufta, i sheh ata, të cilët kanë zemra të sëmura (të dyshimta), të shikojnë me një shikim si të ishin në agoni të vdekjes. I gjetë e keqja!');
INSERT INTO "ahmeti" VALUES(47,21,'47:21','Një respekt dhe një fjalë e mirë (do të ishte më e mirë për ta). E kur çështja (e luftës) të jetë vendosur, do të ishte mirë për ata të jenë besnik ndaj All-llahut.');
INSERT INTO "ahmeti" VALUES(47,22,'47:22','A pritet prej jush (hipokritëve) që në nëse merrni sundimin (ose zbrapseni prej fesë islame) të bëni trazira në tokë dhe të ndërpreni lidhjet e akraballëkut?');
INSERT INTO "ahmeti" VALUES(47,23,'47:23','Të tillët janë që All-llahu i mallkoi, i bëri të shurdhët dhe ua verbëroi të parit e tyre.');
INSERT INTO "ahmeti" VALUES(47,24,'47:24','A nuk e studiojnë me vëmendje Kur’anin? Por jo, ata janëzemra që kanë drynat e vet!');
INSERT INTO "ahmeti" VALUES(47,25,'47:25','Nuk ka dyshim se ata që u kthyen prapa (në mosbesim) pasi që u ishte sqaruar rruga e drejtë, djalli ua hijeshoi dhe shpresë të rrejshme u premtoi.');
INSERT INTO "ahmeti" VALUES(47,26,'47:26','Këtë ngase atyre që e urrejtën atë që e shpalli All-llahu (jehudive), u than: “Ne do t’ju dëgjojmë juve për disa çështje!” Po All-llahu i di fshehtësitë e tyre.');
INSERT INTO "ahmeti" VALUES(47,27,'47:27','E si do të jetë atëherë puna e tyre engjëjt t’ua marrin shpirtin duke i rrahur fytyrave dhe shpinave të tyre?');
INSERT INTO "ahmeti" VALUES(47,28,'47:28','E atë (dënim) për shkak se ata ndoqën rrugë të cilë All-llahu e urren, kurse e urrejtën atë që Ai e pëlqen, prandaj ua asgjësoi veprat e tyre.');
INSERT INTO "ahmeti" VALUES(47,29,'47:29','A menduan atë që në zemrat e tyre kanë dyshim, se All-llahu nuk do të zbulojë mllefin e tyre?');
INSERT INTO "ahmeti" VALUES(47,30,'47:30','E sikur të kishim dashur Ne, do t’i tregonim ty se kush janë ata, e do ti njihje konkret po me siguri do t’i njohësh përme mënyrës së fjalës së tyre, e All-llahui di vepra tuaja.');
INSERT INTO "ahmeti" VALUES(47,31,'47:31','Ne do t’u sprovojmë juve, derisa t’i njohim (të dihen konkretisht) luftëtarët dhe ata që ishin të qënrueshëm nga mesi juaj, po edhe do të sprovojmë veprat tuaja.');
INSERT INTO "ahmeti" VALUES(47,32,'47:32','Ata që nuk besuan dhe penguan të tjerët nga rruga e All-llahut edhe pse u ishte sqaruar rruga e drejtë, e kundërshtuan të dërguarin, ata asgjë nuk mund t’i bëjnë dëm All-llahut dhe Ai do t’ua zhduk veprat e tyre.');
INSERT INTO "ahmeti" VALUES(47,33,'47:33','O ju besimtarë, respektonie All-llahun dhe respektonie të dërguarin, e mos i çoni kot veprat tuaja!');
INSERT INTO "ahmeti" VALUES(47,34,'47:34','Ata që nuk besuan dhe penguan të tjerët nga rruga e All-llahut, e pastaj vdiqën duke qenë jobesimtarë, All-llahu kurrsesi nuk do t’ju falë atyre.');
INSERT INTO "ahmeti" VALUES(47,35,'47:35','Pra, mos u dobësoni e të kërkoni pajtim (armëpushim), kur ju jeni ngadhnjyes dhe All-llahu është me ju dhe nuk u pakëson veprat tuaja.');
INSERT INTO "ahmeti" VALUES(47,36,'47:36','Vërtet, jeta e kësaj bote është vetëm lojë e dëfrim, e nëse besoni dhe ruheni prej të këqijave, Ai u jep shpërblimin tuaj dhe nuk kërkon prej jush tërë pasurinë tuaj (por një pjesë).');
INSERT INTO "ahmeti" VALUES(47,37,'47:37','Pse sikur ta kërkojë tërë atë dhe t’ju shtrëngojë, ju do të bëni koprraci dhe do të dalë në shesh ajo që keni në zemra.');
INSERT INTO "ahmeti" VALUES(47,38,'47:38','Ja, ju jeni ata që ftoheni për të dhënë në rrugën e All-llahut (për luftë kundër armikut tuaj), e dikush prej jush bën koprraci, e kush bën koprraci, ai bën kundër vetës, All-llahu s’ka nevojë, e ju jeni të varfër (keni nevojë). Po nëse ju i ktheni shpinën, Ai do t’ju zëvendesojë me një popull tjetër, që nuk do të jetë si ju.');
INSERT INTO "ahmeti" VALUES(48,1,'48:1','Ne ty të dhamë (vendosëm për) një fitore të sigurt.');
INSERT INTO "ahmeti" VALUES(48,2,'48:2','Ashtu që All-llahu (pas fitores) të liroi ty prej mëkateve (që t’i mbathën) të mëparëshme dhe prej atyre të mëvonshme (kur njerëzit do të pranojnë fenë islame dhe nuk do të jenë më mëkatarë) dhe ashtu të plotëson mirësinë e Vet ndaj teje në rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(48,3,'48:3','Dhe që All-llahu të ndihmon ty me një ndihmë të fortë.');
INSERT INTO "ahmeti" VALUES(48,4,'48:4','Ai është që në zemrat ë besimtarëve dhuroi qetësinë për ta shtuar ata bindjen në besimin e vet që kishin. Ushtritë e qiejve e të tokës janë vetëm të All-llahut, e All-llahut është shumë i dijshëm dhe shumë preciz.');
INSERT INTO "ahmeti" VALUES(48,5,'48:5','(E urdhëroi luftën) Edhe për t’i shtie besimtarët dhe besimtaret në Xhennete nëpër të cilët rrjedhin lumenj e ku do të jenë përgjithmonë, dhe për t’u shlyer të këqijat e tyre, e kjo është fitore e madhe te All-llahu.');
INSERT INTO "ahmeti" VALUES(48,6,'48:6','E në anën tjetër, për t’i ndëshkuar hipokritët dhe hipokritet, idhujtarët dhe idhujtaret, mendimi i të cilëve ndaj All-llahut ishte mendim i keq. Atyre u raftë e keqja! All-llahu shprehu urrejtje kundër tyre dhe i mallkoi e Xhehennemin e përgatiti për ta, që është vednbanim i keq.');
INSERT INTO "ahmeti" VALUES(48,7,'48:7','Edhe ushtritë (ndëshkuese) e qiejve e të tokës janë vetëm të All-llahut dhe All-llahu është i fuqishëm e i urtë.');
INSERT INTO "ahmeti" VALUES(48,8,'48:8','Ne të dërguam ty (Muhammed) dëshmitar, përgëzues dhe qortues.');
INSERT INTO "ahmeti" VALUES(48,9,'48:9','Që ju (njerëz) t’i besoni All-llahut dhë të dërguarit të Tij dhe atë ta përkrahni e ta respeketoni, e (All-llahun) ta madhëroni për çdo mngjes e mbrëmje.');
INSERT INTO "ahmeti" VALUES(48,10,'48:10','Ata që të zotohen ty, në të vërtetë, ata i zotohen All-llahut, se dora e All-llahut është mbi duart e tyre. E kush e thyen (zotimin), ai e thyen vetëm në dëm të vetin, e kush e znaton atë që i është zotuar All-llahut, Ai do t’i japë atij shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(48,11,'48:11','Ata beduinë që mbetën pas (nuk erdhën me ty) do të thonë: “Neve na penguan pasurië tona familjet tona, andaj ti kërko falje për neve!” Ata flasin flasin me gjuhët e veta atë që nuk e kanë në zemrat e tyre. Thuaj: “Kush mund ta pengojë dëshirën e All-llahut, nëse Ai dëshiron t’ju përfshijë juve ngonjë dëm, ose nëse Ai dëshiron t’ju gjejë ndonjë e mirë?” Jo, por All-llahu holësisht di për atë që punoni ju.');
INSERT INTO "ahmeti" VALUES(48,12,'48:12','Por ju menduat se i dërguari dhe besimtarët kurrë nuk do të kthehen të familjet e veta dhe kjo (bindje) në zemrat e tuaja duket e mirë dhe patët mendim të keq, ju ishit popull i shkatërruar.');
INSERT INTO "ahmeti" VALUES(48,13,'48:13','E kush nuk i besoi All-llahut dhe të dërguarit të Tij, Ne pra, për jobesimtarët kemi përgatitur zjarr shumë të fortë.');
INSERT INTO "ahmeti" VALUES(48,14,'48:14','Vetëm i All-llahut është pushteti i qiejve dhe i tokës, Ai falë kë të dojë dhe ndëshkon kë të do, All-llahu është mëkatfalës Mëshirues.');
INSERT INTO "ahmeti" VALUES(48,15,'48:15','Kur të shkoni ju për të mbledhur prenë e luftës, ata që ngelën pas, do të thonë: “Na lejoni edhe ne të vijmë me ju!” Ata duan ta ndryshojnë vendimin e All-llahut! Ti thuaj: “Kurrsesi, ju nuk keni për të ardhur me ne, kështu ka thënë më parë All-llahu!” E ata do t’ju thonë: “Jo, por ju na keni zili!” Po ata janë që kuptojnë pak gjë.');
INSERT INTO "ahmeti" VALUES(48,16,'48:16','Atyre nga beduinët që ngelën pas, thuaju: “Ju do të ftoheni te një popull luftarak e i fuqishëm, t’i luftoni ata ose të dorëzohen (të pranojnë fenë islame), e nëse përgjigjeni (në thirrje), All-llahu ju jep shpërblim të mirë, e nëse nuk dilni sikurse ngelët pa dalë më parë, Ai ju ndëshkon me një dënim të padurueshëm”,');
INSERT INTO "ahmeti" VALUES(48,17,'48:17','Nuk është mëkat (të ngelë pa dalë) për të verbërin, për të çalin, e as për të sëmurin. E kush respekton All-llahun dhe të dëruarin e Tij, Ai atë e shtie në Xhennete ku rrjedhin lunenj, e kush ngel prapa, Ai atë e ndëshkon me një dënim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(48,18,'48:18','Vërtet, All-llahu qe i kënaqur me besimtarët kur ata nën hijen e atij druri zotoheshin ty dhe Ai e dinte se ç’kishin zemrat e tyre, andaj u dhuroi qetësimin dhe së shpejti i shpërbleu me një fitore (çlirimin e Hajberit).');
INSERT INTO "ahmeti" VALUES(48,19,'48:19','Dhe me pre të tjera të mëdha, të cilat i merrni, All-llahu është i gjithfuqishëm i urtë.');
INSERT INTO "ahmeti" VALUES(48,20,'48:20','All-llahu u premtoi juve pre të shumta që do t’i merrni, e këtë (të Hajberit) ua shpejtoi dhe pengoi duart e njerëzve kudër jush, e që kjo të jetë një shenjë e mirë për besimtarët e edhe që Ai t’ju drejtojë në rrugën e fortë.');
INSERT INTO "ahmeti" VALUES(48,21,'48:21','E edhe (pre) të tjera, të cilat ju nuk keni pasur fuqi t’i merrni, po All-llahu mbizotëroi atë, e All-llahu ka fuqi për çdo gjë.');
INSERT INTO "ahmeti" VALUES(48,22,'48:22','Edhe sikur ata që nuk besuan t’ju luftonin juve, ata do të munden dhe do të kthejnë prapa, pastaj nuk do të gjejnë as mbrojtës, as ndihmës.');
INSERT INTO "ahmeti" VALUES(48,23,'48:23','(ky është) Ligji i All-llahut ë është i kahershëm edhe ndër të parët, në ligjin e All-llahut nuk do të gjesh ndryshime.');
INSERT INTO "ahmeti" VALUES(48,24,'48:24','Dhe Ai është që pengoi duart e tyre prej jush dhe duart e tuaja prej tyre brenda në Mekë, pasi që u dha fitoren kundër tyre, e All-llahu mbikëqyr atë që ju punoni.');
INSERT INTO "ahmeti" VALUES(48,25,'48:25','Ata janë që nuk besuan, ju penguan t’i afroheni xhamisë së shenjtë (Qabes), duke i mbajtur kurbanet të penguar të arrini në vendin e vet. E sikur të mos kishte burra besimtarë dhe gra besimtare, që ju nuk i dinit, e t’i sulmonit duke mos ditur, dhe me mbytjen e ndonjërit prej tyre të turpëroheni (zoti do t’ju jepte leje të hyni në Mekë). (E bëri atë) Për të marrë All-llahu në mëshirën e vet atë që do. Dhe sikur të ishin të ndarë, Ne do t’i dënonim me një dënim të rëndë ata prej tyre të cilët nuk besuan.');
INSERT INTO "ahmeti" VALUES(48,26,'48:26','Kur ata që nuk besuan ndezën në zemrat e tyre euforinë, dhe atë kryelartësi injorante (pagane), po All-llahu dhuroi qetësimin e Vet dhe ndaj të dërguarit të vet dhe ndaj besimtarëve dhe për ata zgjodhi besimin e drejtë (fjalinë: la ilahe il lell llah), pse ata ishin më me meritë për të dhe më të zotët e saj, e All-llahu është i gjithdijshëm për çdo send.');
INSERT INTO "ahmeti" VALUES(48,27,'48:27','All-llahu ëndrrën e të dërguarit të Vet e vërtetoi me realitet: se me vullnetin e All-llahut do të hyni në xhaminë e shenjtë (në Qabe) të siguruar, të rruani flokët e kokave tuaja, t’i shkurtoni e nuk do të keni frikë! Pra Ai ka ditur atë që ju nk e dinit, para kësaj ju dha një fitore të shpejtë (Hajberin).');
INSERT INTO "ahmeti" VALUES(48,28,'48:28','Ai është që e dërgoi të dërguarin e Vet me udhëzim të plotë dhe me fenë e vërtetë, që të triumfojë mbi çdo fe, e mjafton All-llahu dëshmitar.');
INSERT INTO "ahmeti" VALUES(48,29,'48:29','Muhammedi është i dërguar i All-llahut, e ata që janë me të (sahabët) janë të ashpër kundër jobesimtarëve, janë të mëshirshëm ndërmjet vete, ti i shehkah përulen (në rukuë), duke rënë me fytyrë në tokë (në sexhde), e kërkojnë pre All-llahut që të ketë mëshir dhe kënaqësinë e Tij ndaj tyre. Në fytyrat e tyre shihen shenjat e gjurmës së sexhdes. Përshkrimi i cilësive të tyre është në Tevrat dhe po ky përshkrim është edhe në Inxhil. Ata janë si një farë e mbjellë ku mbin filizi i vet, e ai trashet, përforcohet dhe qëndron në trungun e vet, ajo e mahnit mbjellësin. (All-llahu i shumoi) Për t’ua shtuar me ta mllefin jobesimtarëve. All-llahu atyre që besuan dhe bënë vepra të mira u premtoi falje të mëkateve dhe shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(49,1,'49:1','O besimtarë, mos bëni asgjë para (se të orientoheni në udhëzimet e) Zotit dhe të dërguarit të Tij, keni kujdes All-llahun, se me të vërtetë All-llahu i dëgjon të gjitha dhe i di të gjitha.');
INSERT INTO "ahmeti" VALUES(49,2,'49:2','O ju që keni besuar, mos ngritni zërin tuaj mbi zërin e Pejgamberit dhe mos iu ngërmoni atij si i ngërmoheni njëri-tjetrit, e të zhduken veprat tuaja duke mos ditur ju.');
INSERT INTO "ahmeti" VALUES(49,3,'49:3','Ata që ulin zërat e tyre paranë të dërguarit të All-llahut. All-llahu zemrat e tyre i ka përshtatur për devotshmëri, ata kanë falje mëkatesh dhe shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(49,4,'49:4','Ata që thërrasin përtej mureve, shumica e tyre janë që nuk kuptojnë.');
INSERT INTO "ahmeti" VALUES(49,5,'49:5','E sikur ata të bënin durim derisa të kishe dalë ti vetë, do të ishte më edukative për ta, po All-llahu është mëkatfalës, mëshirues.');
INSERT INTO "ahmeti" VALUES(49,6,'49:6','O ju që keni besuar, nëse ndonjë i pandërgjegjshëm u sjell ndonjë lajm, ju shqyrtonie mirë, ashtu që të mos e goditni ndonjë popull pa e ditur realitetin, e pastaj të pendoheni për atë që keni bërë.');
INSERT INTO "ahmeti" VALUES(49,7,'49:7','E, dijeni se në mesin tuaj e keni të dërguarin e All-llahut. sikur ai t’ju dëgjonte juve në shu, ë çështje, ju do të pësonit vuajtje, por All-llahu juve ua bëri të dashur besimin dhe ua zbukuroi atë në zemrat tuaja, ndërsa mosbesimin, shfrenimin dhe kundërshtimin, ua bëri që t’i uureni. Të tillë janë ata që gjetën rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(49,8,'49:8','Kjo është dhuratë dhe begati prej All-llahut, e All-llahu është i dijshëmdhe i urtë.');
INSERT INTO "ahmeti" VALUES(49,9,'49:9','Nëse dy grupe besimtarësh tentojnë të luftojnë ndërmjet vete, ju pajtoni ata, e në qoftë se ndonjëri prej tyre e sulmon tjetrin, atëherë luftonie atë grup që vërsulet me pa të drejtë, derisa t’i bindet udhëzimit të All-llahut, e nëse ktheheni, atëherë me drejtësi bëni pajtimin ndërmjet tyre, mbanie drejtësinë, se vërtet All-llahu i do të drejtit.');
INSERT INTO "ahmeti" VALUES(49,10,'49:10','S’ka dyshim se besimtarët janë vëllezër, pra bëni pajtim ndërmjet vëllezërve tuaj dhe kinie frikë All-llahun, që të jeni të mëshiruar (na Zoti).');
INSERT INTO "ahmeti" VALUES(49,11,'49:11','O ju që besuat, nuk bën të tallet një popull me një popull tjetër, meqë të përqeshurit mund të jenë më të mirë nga ata të cilët përqeshin dikë tjetër, e as gratë me gratë e tjera, sepse mund të ndodhë që gratë e tjera të jenë më të mira se ato që përqeshin (duke nënçmuar njëri-tjetrin) dhe mos etiketoni njëri-tjetrin me llagape. Pas besimit është keq të përhapet llagapi i keq. E ata që nuk pendohen, janë mizorë.');
INSERT INTO "ahmeti" VALUES(49,12,'49:12','O ju që keni besuar, largohuni prej dyshimeve të shumta, meqë disa dyshime janë mëkat dhe mos hulumtoni për zbulimin e të metave të njeri tjetrit, dhe mos përgjoni njëri tjetrin; a mos ndonjëri prej jush dëshiron të hajë mishin e vëllait të vet të vdekur? Atë pra e urreni! Kini frikë nga ndëshkimi i All-llahut, e All-llahu është mëshrues, Ai pranon shumë pendimin.');
INSERT INTO "ahmeti" VALUES(49,13,'49:13','O ju njerëz, vërtetë Ne ju krijuam ju prej një mashkulli dhe një femre, ju bëmë popuj e fise që të njiheni ndërmjet vete, e s’ka dyshim se te All-llahu më i ndershmi ndër ju është ai që më tepër është ruajtur (këqijat) e All-llahu është i dijshëm dhe hollësisht i njohur për çdo gjë.');
INSERT INTO "ahmeti" VALUES(49,14,'49:14','Beduinët thanë: “Ne kemi besuar!” Thuaj: “Ju nuk keni besuar ende, por thuani: ne jemi dorëzuar, e ende nuk po hyn besimi në zemrat tuaja. E nëse e respektoni All-llahun dhe të dërguarin e Tij, Ai nuk ju pakëson asgjë nga veprat tuaja, se All-llahu është shumë mëkatfalës, shumë mëshirues”.');
INSERT INTO "ahmeti" VALUES(49,15,'49:15','Besimtarë janë vetëm ata që besuan All-llahut, të dërguarit të Tij, e mandej nuk dyshuan dhe për hirë të All-llahut luftuan me pasurinë dhe me jetën e tyre. Të tillë janë ata të vërtetit.');
INSERT INTO "ahmeti" VALUES(49,16,'49:16','Thuaj: “A ju ia bëni me dije All-llahut fenë tuaj (duke thënë: ne besuam), kur All-llahu është Ai që e di ç’ka në qiej dhe ç’ka në tokë dhe All-llahu është i dijshëm për çdo send”.');
INSERT INTO "ahmeti" VALUES(49,17,'49:17','Ata të shprehin ty mirënjohës (ta përmendin) që u bënë myslimanë. Thuaj: “Pranimin tuaj të fesë islame mos ma njihni (mos ma përmendni) mua, por nëse jeni të sinqertë All-llahu juve ju bëri mirë, kur ju udhëzoi për besim”.');
INSERT INTO "ahmeti" VALUES(49,18,'49:18','S’ka dyshim se All-llahu e di fshehtësinë e qiejve e të tokës, All-llahu sheh atë që punoni ju.');
INSERT INTO "ahmeti" VALUES(50,1,'50:1','Kaf, pasha Kur’anin e lavdishëm (do të ringjalleni)!');
INSERT INTO "ahmeti" VALUES(50,2,'50:2','Por jo, ata u çuditën që u erdhi i dërguari (qortues) nga mesi tyre, andaj jobesimtarët thanë: “Kjo është diçka shumë e çuditshme.');
INSERT INTO "ahmeti" VALUES(50,3,'50:3','A pasi të vdesim e të bëhemi dhe (do të kthehemi në jetë)? Ai kthim është larg (mendjes e mundësisë)!”');
INSERT INTO "ahmeti" VALUES(50,4,'50:4','Ne dimë se ç’pakësohet (ç’tret) toka prej tyre. Te Ne është edhe libri që ruan çdo gjë (numrin e tyre, emrat, pjesët e trupit).');
INSERT INTO "ahmeti" VALUES(50,5,'50:5','Ata përgënjeshtruan të vërtetën kur ajo u erdhi dhe ata janë në gjendje të luhatshme.');
INSERT INTO "ahmeti" VALUES(50,6,'50:6','A nuk shikojnë ata me vëmendje kah qielli se si mbi ta kemi ndërtuar atë, e kemi zbukuruar atë duke mos pasur në të ndonjë zbrazëti.');
INSERT INTO "ahmeti" VALUES(50,7,'50:7','Edhe tokën se si e kemi shtrirë, e në të kemi vendosur kodra përforcuese dhe kemi bërë që në të të mbijnë gjithfarë bimësh të bukura.');
INSERT INTO "ahmeti" VALUES(50,8,'50:8','(I bëmë ashtu) Dëshmi të dukshme dhe përkujtuese për secilin njeri që ka drejtuar mendjen (te Zoti).');
INSERT INTO "ahmeti" VALUES(50,9,'50:9','Ne, nga qielli lëshuam shiun e dobishëm dhe më të bëmë që të kultivohen kopshte e drithëra të korren.');
INSERT INTO "ahmeti" VALUES(50,10,'50:10','Dhe rritëm trungje të gjata hurmash me frua të paluara njëri mbi tjetrin.');
INSERT INTO "ahmeti" VALUES(50,11,'50:11','ushqim për njerëz, dhe me të, Ne e ngjallëm tokën e vdekur, e kështu do të jetë edhe ringjallja.');
INSERT INTO "ahmeti" VALUES(50,12,'50:12','Para tyre përgënjeshtruan populli i Nuhut, banorët e Bunarit (të pusit) dhe Themudi.');
INSERT INTO "ahmeti" VALUES(50,13,'50:13','Edhe Adi, edhe Faraoni dhe vëllezërit e Lutit.');
INSERT INTO "ahmeti" VALUES(50,14,'50:14','Edhe banorët e Ejkes, populli i Tubbeit. Të gjithë i përgënjeshtruan të dërguarit, atëherë e merituan dënimin Tim.');
INSERT INTO "ahmeti" VALUES(50,15,'50:15','A mos u lodhëm Ne me krijimin e parë? Jo, por ata janë në një huti rreth një krijimi të ri.');
INSERT INTO "ahmeti" VALUES(50,16,'50:16','Ne e kemi krijuar njeriun dhe dimë se ç’pëshpëritë ai në vetvete dhe Ne jemi më afër tij se damari (që rrah) i qafës së tij.');
INSERT INTO "ahmeti" VALUES(50,17,'50:17','Dhe se kur dy engjëjt qëndrojnë pranë tij në të djathtë dhe në të majtë.');
INSERT INTO "ahmeti" VALUES(50,18,'50:18','Që ai nuk hedh ndonjë fjalë e të mos jetë pranë tij përcjellësi i gatshëm.');
INSERT INTO "ahmeti" VALUES(50,19,'50:19','Agonia e vdekjes i vjen me atë të vërtetën (i zbulohet çështja e ahiretit); kjo është ajo prej së cilës ke ikur.');
INSERT INTO "ahmeti" VALUES(50,20,'50:20','Dje i fryhet surit, e ajo është dita e premtuar (për dënim).');
INSERT INTO "ahmeti" VALUES(50,21,'50:21','E do të vijë secili njeri bashkë me të dhe grahësi edhe dëshmitari.');
INSERT INTO "ahmeti" VALUES(50,22,'50:22','(I thuhet) Ti ishe në një huti nga kjo (ditë) e NE ta tërhoqëm perdën tënde dhe tash ti sheh shumë mprehtë.');
INSERT INTO "ahmeti" VALUES(50,23,'50:23','E shoku (përcjellësi) i tij do të thotë: “Kjo që është te unë (regjistri i veprave) është gati.');
INSERT INTO "ahmeti" VALUES(50,24,'50:24','Ju të dy (engjëjt) hidhinie në Xhehennem secilin mohues kryeneç.');
INSERT INTO "ahmeti" VALUES(50,25,'50:25','Secilin pengues të veprave, mizor e të dyshimtë në fe.');
INSERT INTO "ahmeti" VALUES(50,26,'50:26','I cili All-llahut i shoqëroi Zot tjetër, pra hidhnie atë në vuajtjet më të rënda”.');
INSERT INTO "ahmeti" VALUES(50,27,'50:27','E shoku i tij (djalli) thotë: “Zoti ynë, unë nuk u shmanga prej rrugës së drejtë, por ai vetë ka qenë i humbur larg”.');
INSERT INTO "ahmeti" VALUES(50,28,'50:28','Ai (All-llahu) thotë: “Mos u gridni tash para Meje, se Unë më parë ju tërhoqa vërejtjen.');
INSERT INTO "ahmeti" VALUES(50,29,'50:29','te Unë nuk ndryshon fjala (vendimi) dhe Unë nuk jam zullumqar për robërit”.');
INSERT INTO "ahmeti" VALUES(50,30,'50:30','(Përkujto) Ditën kur Ne Xhehennemit i themi: “A je mbushur?” E, ai thotë: “A ka ende?”');
INSERT INTO "ahmeti" VALUES(50,31,'50:31','Ndërsa besimtarëve të ruajtur Xhenneti u afrohet krejt afër.');
INSERT INTO "ahmeti" VALUES(50,32,'50:32','Kjo është ajo që u premtohet për secilin që pendohet dhe që e ruan besën e dhënë.');
INSERT INTO "ahmeti" VALUES(50,33,'50:33','Për secilin që i është frikësuar Zotit pa e parë dhe ka qenë i kthyer te Ai me zemër të sinqertë.');
INSERT INTO "ahmeti" VALUES(50,34,'50:34','(U thuhet) Hyni në te, të shpëtuar, se kjo është dita e përjetshme.');
INSERT INTO "ahmeti" VALUES(50,35,'50:35','Ata aty kanë çka të dëshirojnë, e te Ne ka edhe më shumë.');
INSERT INTO "ahmeti" VALUES(50,36,'50:36','E sa e sa brezni para tyre kemi shkatërruar e që ishin më të fortë e më të shkathët se këta (idhujtarët kurejshitë). Ata brodhën andej e këndej nëpër tokë (për t’i ikur vdekjes). Por, a mos gjetën rrugëdalje?');
INSERT INTO "ahmeti" VALUES(50,37,'50:37','Në të gjitha këto, për atë që ka mendje të shëndoshë dhe që i ka vënë me vëmendje, ka argumente.');
INSERT INTO "ahmeti" VALUES(50,38,'50:38','Ne krijuam qiejt e tokën dhe gjithçka ka ndërmjet tyre brenda gjashtë ditesh dhe Ne nuk ndiem lodhje.');
INSERT INTO "ahmeti" VALUES(50,39,'50:39','Po ti (Mumahed) me përballo atë që ata të thonë, para lindjes së diellit dhe para përendimit madhëro e falënderim Zotin tënd.');
INSERT INTO "ahmeti" VALUES(50,40,'50:40','Falu për të Tij edhe në një pjesë të natës edhe pas sexhdes (pas namazit farz).');
INSERT INTO "ahmeti" VALUES(50,41,'50:41','Dhe vëni veshin thirrësit kur thërret nga ndonjë vend i afërt.');
INSERT INTO "ahmeti" VALUES(50,42,'50:42','Ditën kur e dëgjojnë thirrjen për të vërtetën (ringjalljen), e ajo është dita e daljes (prej varrezave).');
INSERT INTO "ahmeti" VALUES(50,43,'50:43','S’ka dyshim se Ne japim vdekje dhe vetëm te Ne është kthimi i tyre.');
INSERT INTO "ahmeti" VALUES(50,44,'50:44','Ditën kur me ata hapet toka e ata nguten (për vendtubim), e ai është një tubim i lehtë për Ne.');
INSERT INTO "ahmeti" VALUES(50,45,'50:45','Ne dimë më së miri çka thonë ata, e ti ndaj tyre nuk je ndonjë dhunues, ti këshilloje me këtë Kur’an atë që i ka frikë kërcënimit Tim.');
INSERT INTO "ahmeti" VALUES(51,1,'51:1','Për erërat që ngrehin dheun dhe shpërndajnë.');
INSERT INTO "ahmeti" VALUES(51,2,'51:2','Për retë që e bartin shiun e rëndë.');
INSERT INTO "ahmeti" VALUES(51,3,'51:3','Për anijet që lundrojnë lehtë.');
INSERT INTO "ahmeti" VALUES(51,4,'51:4','Për engjëjt që bëjnë ndarjen e çështjeve.');
INSERT INTO "ahmeti" VALUES(51,5,'51:5','S’ka dyshim se ajo qe u premtohet është e vërtetë e sigurt.');
INSERT INTO "ahmeti" VALUES(51,6,'51:6','Dhe se shpërblimi (gjykimi për vepra) do të ndodhë pa tjetër.');
INSERT INTO "ahmeti" VALUES(51,7,'51:7','Për qiellin plot rrugë.');
INSERT INTO "ahmeti" VALUES(51,8,'51:8','Ju gjendeni në nje thënie kontradiktore,');
INSERT INTO "ahmeti" VALUES(51,9,'51:9','Nga ai (besimi) zbrapset ai që ishte zmbrapsur.');
INSERT INTO "ahmeti" VALUES(51,10,'51:10','Mallkuar qofshin gënjeshtarët!');
INSERT INTO "ahmeti" VALUES(51,11,'51:11','Ata të cilët janë gafilë në injorancë (në verbëri, në padituri).');
INSERT INTO "ahmeti" VALUES(51,12,'51:12','E pyesin: “Kur është dita e gjykimit?”');
INSERT INTO "ahmeti" VALUES(51,13,'51:13','Dita kur ata digjen në zjarr.');
INSERT INTO "ahmeti" VALUES(51,14,'51:14','Përjetonie dënimin tuaj, ky është ai që kërkonit t’ju shpejtohet.');
INSERT INTO "ahmeti" VALUES(51,15,'51:15','Të devotshmit janë në kopshte e burime.');
INSERT INTO "ahmeti" VALUES(51,16,'51:16','Të kënaqur se kanë shtënë në dorë atë që u dha Zoti i tyre, ata edhe më parë (në dynja) ishin mirëbërës.');
INSERT INTO "ahmeti" VALUES(51,17,'51:17','Ata qenë të cilët pak flinin natën.');
INSERT INTO "ahmeti" VALUES(51,18,'51:18','Dhe në syfyr (kah mbarimi i natës) ata kërkonin falje për mëkata.');
INSERT INTO "ahmeti" VALUES(51,19,'51:19','Dhe në pasurinë e tyre kishin përcaktuar të drejtë për lypësin dhe për të ngratin (që ka nevojë por nuk lyp).');
INSERT INTO "ahmeti" VALUES(51,20,'51:20','Edhe në tokë ka argumente për ata të bindurit.');
INSERT INTO "ahmeti" VALUES(51,21,'51:21','Po edhe në veten tuaj. A nuk jeni kah e shihni?');
INSERT INTO "ahmeti" VALUES(51,22,'51:22','E në qiell është furnizimi juaj dhe ajo që ju premtohet.');
INSERT INTO "ahmeti" VALUES(51,23,'51:23','Pasha Zotin e qiellit e të tokës, kjo është një e vërtetë, ashtu siç është se ju flisni.');
INSERT INTO "ahmeti" VALUES(51,24,'51:24','A ke arritur të dëgjosh ti për rrëfimin e musafirëve të ndershëm të Ibrahimit?');
INSERT INTO "ahmeti" VALUES(51,25,'51:25','Kur patën hyrë te Ai dhe thanë: “Të përshendesin me paqe!” (E më vete tha): “Njerëz të panjohur!”');
INSERT INTO "ahmeti" VALUES(51,26,'51:26','Dhe ai pa u vërejtur shkoi te familja e vet dhe solli një viç të majmë të pjekur.');
INSERT INTO "ahmeti" VALUES(51,27,'51:27','Atë ua afroi atyre e tha: “Përse nuk po hani?”');
INSERT INTO "ahmeti" VALUES(51,28,'51:28','Dhe ndjeu njëfarë frike prej atyre, po ata thanë: “Mos kij frikë!” Mandej i dhanë myzhde për një djalë të dijshëm.');
INSERT INTO "ahmeti" VALUES(51,29,'51:29','E gruaja e tij nisi të bërtasë e ri ra shuplakë fyyrës së vet e tha: “Unë jam një plakë që nuk lind!”');
INSERT INTO "ahmeti" VALUES(51,30,'51:30','Ata (engjëjt) thanë: “Keshtu ka thënë Zoti yt, e Ai është i urti, i gjithedijshmi”.');
INSERT INTO "ahmeti" VALUES(51,31,'51:31','Ai (Ibrahimi) tha: “E ç’është puna juaj, o ju të dërguar?”');
INSERT INTO "ahmeti" VALUES(51,32,'51:32','Ata thanë: “Ne jemi të dërguar te një popull kriminel!”');
INSERT INTO "ahmeti" VALUES(51,33,'51:33','Që të hedhim mbi ta gurë nga balta (e pjekur)!');
INSERT INTO "ahmeti" VALUES(51,34,'51:34','(gurë) Të shënuar te Zoti yt, për ata që i kaluan kufijtë (në mëkate).');
INSERT INTO "ahmeti" VALUES(51,35,'51:35','Dhe kush ishte aty nga besimtarët, Ne i nxorrëm (i larguam).');
INSERT INTO "ahmeti" VALUES(51,36,'51:36','Po nuk gjetëm aty pos një shtëpie myslimane.');
INSERT INTO "ahmeti" VALUES(51,37,'51:37','Dhe Ne kemi lënë aty shenjë dënimit të hidhur.');
INSERT INTO "ahmeti" VALUES(51,38,'51:38','(Kemi lënë shenjë) Edhe në Musain kur Ne e dërguam te faraoni me fakte të forta.');
INSERT INTO "ahmeti" VALUES(51,39,'51:39','E ai, me tërë anën (fuqinë) e vet, ia ktheu shpinën dhe tha: “Është magjistar, ose është i çmendur!”');
INSERT INTO "ahmeti" VALUES(51,40,'51:40','E Ne e kapëm atë dhe ushtrinë e tij dhe e hodhëm në det, dhe ai e bëri veten të jetë i sharë.');
INSERT INTO "ahmeti" VALUES(51,41,'51:41','Edhe në Adin (kemi lënë shenjë) kur Ne lëshuam kundër tyre erën rrënuese,');
INSERT INTO "ahmeti" VALUES(51,42,'51:42','E cila nuk la send pa e shënderruar në mbeturinë.');
INSERT INTO "ahmeti" VALUES(51,43,'51:43','Edhe Themudin kur atyre iu pat thënë: kënaquni deri në një kohë.');
INSERT INTO "ahmeti" VALUES(51,44,'51:44','E ata nga kryelartësia nuk respektuan urdhërin e Zotit të tyre, andaj i përfshiu rrufeja me krismë, e ata e shihnin.');
INSERT INTO "ahmeti" VALUES(51,45,'51:45','Dhe nuk mundën as të ngriten e as t’i kundërvihen.');
INSERT INTO "ahmeti" VALUES(51,46,'51:46','Edhe popullin e Nuhut (e dënuam) që ishte më parë. Ai ishte një popull i prishur.');
INSERT INTO "ahmeti" VALUES(51,47,'51:47','Ne, me forcën tonë e ngritëm qiellin dhe Ne e zgjërojmë atë.');
INSERT INTO "ahmeti" VALUES(51,48,'51:48','Edhe tokën Ne e kemi shtruar, sa shtrues të mirë jemi.');
INSERT INTO "ahmeti" VALUES(51,49,'51:49','Dhe Ne krijuam prej çdo sendi dy lloje (mashkull e femër) që ju të përkujtoni (madhështnë e Zotit).');
INSERT INTO "ahmeti" VALUES(51,50,'51:50','Pra, ikni e mbështetuni te All-llahu, unë prej Tij jam një qortues i hapët.');
INSERT INTO "ahmeti" VALUES(51,51,'51:51','E mos i shoqëroni All-llahut edhe ndonjë zot tjetër, edhe për këtë unë jam i dërguar prej Tij që t’ju tërheqë haptazi vërejtjen.');
INSERT INTO "ahmeti" VALUES(51,52,'51:52','Ja, ashtu pra, edhe atyre që ishin më parë nuk u erdhi i dëguar e që nuk i thanë: “Është magjistar ose është i çmendur!”');
INSERT INTO "ahmeti" VALUES(51,53,'51:53','A mos e porositën njëri-tjetrin me këtë? Jo, por ata janë popull renegat.');
INSERT INTO "ahmeti" VALUES(51,54,'51:54','Andaj ti larohu prej tyre, ti nuk je i qoruar.');
INSERT INTO "ahmeti" VALUES(51,55,'51:55','Vazhdo me keshillë, sepse këshilla besimtarëve u bën dobi.');
INSERT INTO "ahmeti" VALUES(51,56,'51:56','Unë nuk i krijova xhinët dhe njerëzit për tjetër pos që të më adhurojnë.');
INSERT INTO "ahmeti" VALUES(51,57,'51:57','Unë nuk kërkoj prej tyre ndonjë furnizim e as dëshiroj të më ushqejnë ata.');
INSERT INTO "ahmeti" VALUES(51,58,'51:58','All-llahu është furnizues i madh, Ai fuqiforti.');
INSERT INTO "ahmeti" VALUES(51,59,'51:59','S’ka dyshim se atyre që bënë mizori u përket dënimi si pjesa e dënimit të shokëve të tyre, andaj, të mos e kërkojnë shpejtimin e tij.');
INSERT INTO "ahmeti" VALUES(51,60,'51:60','Të mjerët ata që nuk besuan për atë ditën e tyre të premtuar!');
INSERT INTO "ahmeti" VALUES(52,1,'52:1','Pasha Turin.');
INSERT INTO "ahmeti" VALUES(52,2,'52:2','Dhe librin e shkruar në rreshta.');
INSERT INTO "ahmeti" VALUES(52,3,'52:3','Në lëkurë të shtrirë.');
INSERT INTO "ahmeti" VALUES(52,4,'52:4','Pasha shtëpinë (Qaben) e vizituar (ose Bejti Mamurin në qiell).');
INSERT INTO "ahmeti" VALUES(52,5,'52:5','Pasha kulmin e ngritur (qiellin).');
INSERT INTO "ahmeti" VALUES(52,6,'52:6','Pasha detin e mbushur (ose detin e ndezur flakë në kijamet).');
INSERT INTO "ahmeti" VALUES(52,7,'52:7','Është e vërtetë se dënimi i Zotit tënd pa tjetër da të ndodhë.');
INSERT INTO "ahmeti" VALUES(52,8,'52:8','Atë nuk ka kush që mund ta largojë.');
INSERT INTO "ahmeti" VALUES(52,9,'52:9','Ditën kur qielli bën një tronditje të fortë.');
INSERT INTO "ahmeti" VALUES(52,10,'52:10','Dhe kodrat të shkulen nga vendi e të ecin.');
INSERT INTO "ahmeti" VALUES(52,11,'52:11','Atë ditë është mjerim i madh për gënjeshtarët.');
INSERT INTO "ahmeti" VALUES(52,12,'52:12','Të cilët luajnë të zhytur në besime të kota.');
INSERT INTO "ahmeti" VALUES(52,13,'52:13','Ditën kur me rrëmbim shtyhen në zjarrin e Xhehennemit.');
INSERT INTO "ahmeti" VALUES(52,14,'52:14','Ky është ai zjarr, që ju e keni përgënjeshtruar.');
INSERT INTO "ahmeti" VALUES(52,15,'52:15','A mos është kjo ndonjë magji, apo nuk po shihni (jeni vebëruar siç u verbëruan në dynja).');
INSERT INTO "ahmeti" VALUES(52,16,'52:16','Hyni në të! Për juve është njesoj, bëtë durim ose nuk bëtë. Shpërbleheni me atë që me veprat tuaja e merituat.');
INSERT INTO "ahmeti" VALUES(52,17,'52:17','Ata të devitshmit janë në kopshte e në begati.');
INSERT INTO "ahmeti" VALUES(52,18,'52:18','Të kënaqur me atë që u dha Zoti i tyre dhe që i ruajti Zoti i tyre nga vuajtja e Xhehennemit.');
INSERT INTO "ahmeti" VALUES(52,19,'52:19','(U thuhet) Me të mirëhani e pini, për atë që vepruat mirë.');
INSERT INTO "ahmeti" VALUES(52,20,'52:20','Ata janë të mbështetur në koltukë të renditur dhe Ne u shoqëruam atyre hyri symëdha.');
INSERT INTO "ahmeti" VALUES(52,21,'52:21','E ata që vetë besuan, e edhe pasardhësit e tyre ishin me besim, Ne atyre do t’ua shoqërojmë pasardhsësit e tyre dhe asgjë nuk u pakësojmë nga veprat e tyre (prindërve). Secili njeri është peng i asaj që ka punuar.');
INSERT INTO "ahmeti" VALUES(52,22,'52:22','Ne atyre u shtojmë të mirat me pemë e me mish që ata e dëshirojnë.');
INSERT INTO "ahmeti" VALUES(52,23,'52:23','Aty njëri-tjetrit ia zgjasim gotën, aty nuk ka fjalë të kota e as mëkat.');
INSERT INTO "ahmeti" VALUES(52,24,'52:24','Aty njëri-tjetrit ia zgjasin gotën, aty nuk ka fjalë të kota e as mëkat.');
INSERT INTO "ahmeti" VALUES(52,25,'52:25','Dhe njëri-tjetrit i qasen duke i pyetur (për punët e tyre në dynja).');
INSERT INTO "ahmeti" VALUES(52,26,'52:26','(Këta) Thonë: “Ne edhe kur ishim në familjet tona, ishim ata që frikësoheshim.');
INSERT INTO "ahmeti" VALUES(52,27,'52:27','E All-llahu na dhuroi të mira dhe na ruajti prej dënimit të erës (flakës) së nxehtë të zjarrit.');
INSERT INTO "ahmeti" VALUES(52,28,'52:28','Ne më parë ishim ndër ata që lutëm Atë, e Ai është bamirës, meshirues”.');
INSERT INTO "ahmeti" VALUES(52,29,'52:29','Pra ti përkujto (me Kur’an) se me dhuntimë e Zotiti tënd ti nuk je as falltor, as i çmendur.');
INSERT INTO "ahmeti" VALUES(52,30,'52:30','E ata thonë: “Ai është poet, po presim kohën ta zhdukë”.');
INSERT INTO "ahmeti" VALUES(52,31,'52:31','Thuaj: “Pritni, se edhe unë do të pres me ju”.');
INSERT INTO "ahmeti" VALUES(52,32,'52:32','A mendjet (iluzionet) e tyre po u urdhërojën për këtë, apo ata janë popull renegat?');
INSERT INTO "ahmeti" VALUES(52,33,'52:33','A mo ata thonë: “Ai (Muhammedi) e trilloi!” Jo, por ata nuk besojnë.');
INSERT INTO "ahmeti" VALUES(52,34,'52:34','Pra, ata janë drejt, le ta sjellin një ligjërim si ai.');
INSERT INTO "ahmeti" VALUES(52,35,'52:35','A mos u krjuam prej kurgjësë, apo ata vetë janë krijues?');
INSERT INTO "ahmeti" VALUES(52,36,'52:36','A mos ata i krijuan qiejt e tokën, Jo, por ata nuk janë të bindur.');
INSERT INTO "ahmeti" VALUES(52,37,'52:37','A mos te ata ata janë depot e Zotit tënd, apo ata i mbizotërojnë?');
INSERT INTO "ahmeti" VALUES(52,38,'52:38','A mos kanë ata shkallë të larta që në to të përgjojnë? Prra, përgjuesit e tyre le të sjellin fakt të sigurt!');
INSERT INTO "ahmeti" VALUES(52,39,'52:39','A mos vajzat janë të Atij, kurse djemtë tuaj?');
INSERT INTO "ahmeti" VALUES(52,40,'52:40','A mos ti u kërkon atyre shpërblim, e ata janë të ngarkuar mga tagri?');
INSERT INTO "ahmeti" VALUES(52,41,'52:41','A mos te ata janë fshehtësitë, e ata i shkruajnë?');
INSERT INTO "ahmeti" VALUES(52,42,'52:42','A mos po të kurdisin ndonjë kurt, po ata që nuk beuan bijnë vetë në kurth.');
INSERT INTO "ahmeti" VALUES(52,43,'52:43','A mos kanë ndonjë zot pos All-llahut? I lartë është All-llahu nga ai që ata i shoqërojnë.');
INSERT INTO "ahmeti" VALUES(52,44,'52:44','Edhe sikur ta shihnin që u bie nga qielli ndonjë copë, ata do të thoshin: “Është e re e dendur!”');
INSERT INTO "ahmeti" VALUES(52,45,'52:45','Po ti lëri ata derisa të ballafaqohen në ditën e tyre, kur do të shtangen.');
INSERT INTO "ahmeti" VALUES(52,46,'52:46','Ditën kur dredhia e tyre nuk do t’u bëjë dobi sgjë e as do të ndihmohen.');
INSERT INTO "ahmeti" VALUES(52,47,'52:47','Është e vërtetë se ata që bënë zullum, kanë një dënim më të afërt se sa si (kijameti), por pjesa dërmuesse e tyre nuk dinë.');
INSERT INTO "ahmeti" VALUES(52,48,'52:48','Ti bën durim në vendimin e Zotit tënd, se ti je nën mbikëqyrjen Tonë dhe kur të ngrihesh madhëroje me falënderim Zotitn tënd!');
INSERT INTO "ahmeti" VALUES(52,49,'52:49','Edhe gjatë natës adhuroje Atë, si dhe kur yjet nuk nuk duken më.');
INSERT INTO "ahmeti" VALUES(53,1,'53:1','Pasha yllin kur bie!');
INSERT INTO "ahmeti" VALUES(53,2,'53:2','Shoku juaj nuk ka humbur, e as që ka devijuar');
INSERT INTO "ahmeti" VALUES(53,3,'53:3','Dhe ai nuk flet nga dëshira');
INSERT INTO "ahmeti" VALUES(53,4,'53:4','Ajo nuk është tjetër pos shpalljes që i shpallet');
INSERT INTO "ahmeti" VALUES(53,5,'53:5','Atë ia mësoi fuqiforti (Xhibrili)');
INSERT INTO "ahmeti" VALUES(53,6,'53:6','Që ka mendje precize dhe që u përqëndrua në formën e vet');
INSERT INTO "ahmeti" VALUES(53,7,'53:7','Dhe ai (Xhibrili) ishte në horizontin e lartë');
INSERT INTO "ahmeti" VALUES(53,8,'53:8','Pastaj u lëshua dhe iu afrua');
INSERT INTO "ahmeti" VALUES(53,9,'53:9','E ishte afër sa dy harqe, apo edhe më afër');
INSERT INTO "ahmeti" VALUES(53,10,'53:10','Dhe i shpalli robit të Tij (All-llahut) atë që ia shpalli.');
INSERT INTO "ahmeti" VALUES(53,11,'53:11','Zemra nuk mohoi atë që pa');
INSERT INTO "ahmeti" VALUES(53,12,'53:12','A po i bëni polemikë atij për atë që ka parë?');
INSERT INTO "ahmeti" VALUES(53,13,'53:13','Ai e ka parë (Xhibrilin) edhe herën tjetër');
INSERT INTO "ahmeti" VALUES(53,14,'53:14','Te Sidretul Munteha');
INSERT INTO "ahmeti" VALUES(53,15,'53:15','Pranë së cilës është xhennetul Me’va');
INSERT INTO "ahmeti" VALUES(53,16,'53:16','Atëherë kur Sidrën e mbuloi ajo që e mbuloi');
INSERT INTO "ahmeti" VALUES(53,17,'53:17','Shikimi i tij nuk lakoi e as nuk tejkaloi');
INSERT INTO "ahmeti" VALUES(53,18,'53:18','Ai vërtet pa disa nga shenjat më të mëdha të Zotit të vet');
INSERT INTO "ahmeti" VALUES(53,19,'53:19','E ju, a shihni Latin dhe Uzanë?');
INSERT INTO "ahmeti" VALUES(53,20,'53:20','Dhe atë të tretin, të pavlefshmin?');
INSERT INTO "ahmeti" VALUES(53,21,'53:21','A mendoni se për ju janë meshkujt, e për Të fe-mrat?');
INSERT INTO "ahmeti" VALUES(53,22,'53:22','Atëherë ajo është një ndarje e padrejtë!');
INSERT INTO "ahmeti" VALUES(53,23,'53:23','Ata (që i adhuroni) nuk janë tjetër, vetëm se emra që ju dhe prindërit tuaj i emërtuat; All-llahu nuk zbriti për ta ndonjë fakt. Por ata (idhujtarët), nuk ndjekin tjetër vetëm se paragjykime dhe çka duan vetë, megji-thqë prej Zotit të tyre u pat ardhur udhëzimi');
INSERT INTO "ahmeti" VALUES(53,24,'53:24','A mos i takon njeriut ajo që dëshiron?');
INSERT INTO "ahmeti" VALUES(53,25,'53:25','E dihet se vetëm All-llahut i takon bota tjetër dhe kjo botë');
INSERT INTO "ahmeti" VALUES(53,26,'53:26','E sa engjëj ka në qiej, ndërmjetësimi i të cilëve nuk bën dobi vetëm pasi që All-llahu të japë leje për atë që dëshiron dhe me të cilën është i kënaqur');
INSERT INTO "ahmeti" VALUES(53,27,'53:27','Ata që nuk e besojnë jetën tjetër, engjëjt i emërto-jnë me emra femrash');
INSERT INTO "ahmeti" VALUES(53,28,'53:28','Ata nuk dinë asgjë për atë (emërtim) dhe nuk ndjekin tjetër vetëm paragjykime, e paragjykimi nuk është asgjë ndaj së vërtetës');
INSERT INTO "ahmeti" VALUES(53,29,'53:29','Andaj ti largohu prej atij që ia ka kthyer shpinën Kur’anit dhe që nuk do tjetër pos jetës së kësaj bote!');
INSERT INTO "ahmeti" VALUES(53,30,'53:30','Ajo është e tërë dituria që kanë arritur, e Zoti yt di më së miri për atë që është larguar nga rruga e Tij dhe Ai di më së miri për atë që është në rrugën e drejtë');
INSERT INTO "ahmeti" VALUES(53,31,'53:31','Dhe vetëm të All-llahut janë ç’ka në qiej dhe në tokë - për t’i shpërblyer ata që bënë keq me të keqen e tyre dhe për t’i shpërblyer ata që bënë mirë me të mirën e tyre');
INSERT INTO "ahmeti" VALUES(53,32,'53:32','Ata që largohen prej mëkateve të mëdha dhe prej punëve të ndyta, përpos ndonjë pune të imët, s’ka dy-shim se Zoti yt gjerësisht përfshin me falje, se Ai di më së miri për ju edhe para se t’ju krijojë, kur filloi krijimin tuaj nga dheu ndërsa ju ishit të fshehur në barqet e nënave tuaja. Pra mos lavdëroni veten, se Ai e di kush është më i ruajtur');
INSERT INTO "ahmeti" VALUES(53,33,'53:33','A e ke parë atë që u zmbrap?');
INSERT INTO "ahmeti" VALUES(53,34,'53:34','Që dha pak e pastaj e ndërpreu?');
INSERT INTO "ahmeti" VALUES(53,35,'53:35','A mos ka ai njohuri për të fshehtën e po e sheh?');
INSERT INTO "ahmeti" VALUES(53,36,'53:36','A nuk është informuar me atë që gjendet në fletushkat e Musait');
INSERT INTO "ahmeti" VALUES(53,37,'53:37','Dhe të Ibrahimit, që gjithnjë plotësonte obligimet');
INSERT INTO "ahmeti" VALUES(53,38,'53:38','(Në të cilat shkruan) Se askush nuk e bartë barrën e tjetrit');
INSERT INTO "ahmeti" VALUES(53,39,'53:39','Dhe se njeriut nuk i takon tjetër pos asaj që ka punuar');
INSERT INTO "ahmeti" VALUES(53,40,'53:40','Dhe se mundi i tij më vonë do të shihet');
INSERT INTO "ahmeti" VALUES(53,41,'53:41','Pastaj ai shpërblehet ose dënohet më plotësisht');
INSERT INTO "ahmeti" VALUES(53,42,'53:42','Dhe se kthimi përfundimtar është te Zoti yt');
INSERT INTO "ahmeti" VALUES(53,43,'53:43','Dhe se Ai jep qeshjen dhe Ai jep të qarit');
INSERT INTO "ahmeti" VALUES(53,44,'53:44','Dhe se Ai jep vdekjen dhe Ai jep jetën');
INSERT INTO "ahmeti" VALUES(53,45,'53:45','Dhe Ai krijon llojet - mashkullin dhe femrën');
INSERT INTO "ahmeti" VALUES(53,46,'53:46','Nga pika e farës që hidhet');
INSERT INTO "ahmeti" VALUES(53,47,'53:47','Dhe se Ai ringjallë');
INSERT INTO "ahmeti" VALUES(53,48,'53:48','Dhe se Ai jep pasurinë dhe varfërinë');
INSERT INTO "ahmeti" VALUES(53,49,'53:49','Dhe se Ai është Zoti i Shi’rasë (ylli polar)');
INSERT INTO "ahmeti" VALUES(53,50,'53:50','Dhe Ai shkatërroi Adin e lashtë');
INSERT INTO "ahmeti" VALUES(53,51,'53:51','Edhe Themudin, të cilët nuk i kurseu (nga dënimi)');
INSERT INTO "ahmeti" VALUES(53,52,'53:52','Edhe popullin e Nuhut më herët, vërtet ata ishin edhe më shkatërrues dhe renegatë');
INSERT INTO "ahmeti" VALUES(53,53,'53:53','Edhe Mu’tefikën (venbanimin e popullit të Lutit) e lëshoi prej së larti');
INSERT INTO "ahmeti" VALUES(53,54,'53:54','E i përfshiu ata ajo që i përfshiu');
INSERT INTO "ahmeti" VALUES(53,55,'53:55','Pra cilës së mirë të Zotit tënd i dyshon?');
INSERT INTO "ahmeti" VALUES(53,56,'53:56','Ky është një qortues i ngjashëm me qortuesit e parë');
INSERT INTO "ahmeti" VALUES(53,57,'53:57','E afërta është afruar');
INSERT INTO "ahmeti" VALUES(53,58,'53:58','All-llahut është i vetmi që do ta tregojë');
INSERT INTO "ahmeti" VALUES(53,59,'53:59','A këtij ligjërimi po i çuditeni?');
INSERT INTO "ahmeti" VALUES(53,60,'53:60','E po qeshni dhe nuk po qani?!');
INSERT INTO "ahmeti" VALUES(53,61,'53:61','E madje jeni edhe të pakujdesshëm!');
INSERT INTO "ahmeti" VALUES(53,62,'53:62','Pra, bini në sexhde për All-llahun dhe Adhurojeni!');
INSERT INTO "ahmeti" VALUES(54,1,'54:1','Momenti (i katastrofës së përgjigjthshme) është afruar, e hëna është çarë (në dysh).');
INSERT INTO "ahmeti" VALUES(54,2,'54:2','Po ata (idhujtarët) edhe nëse shohin ndonjë argument (mrekulli) zprapsen e thonë: “Kjo është magji e vazhdueshme!”');
INSERT INTO "ahmeti" VALUES(54,3,'54:3','Dhe përgënjeshtruan, e ndoqën dëshirat e veta, por çdo çështje arrinë cakun e fundit.');
INSERT INTO "ahmeti" VALUES(54,4,'54:4','Atyre u patën ardhur aso lajmesh (të popujve të lashtë), ku ka mjaft qortime, (për ata që kuptojnë).');
INSERT INTO "ahmeti" VALUES(54,5,'54:5','Urtësi e përsosur (është Kur’ani)! Po sa bëjnë dobi qortimet!');
INSERT INTO "ahmeti" VALUES(54,6,'54:6','Pra, largohu prej tyre. ditën kur thirrësi (Israfili) kumton një send të tmerrshëm.');
INSERT INTO "ahmeti" VALUES(54,7,'54:7','Dalin prej varreve, e si karkaleca të shpërndarë e me shikim ë përulur.');
INSERT INTO "ahmeti" VALUES(54,8,'54:8','Duke shpejtuar drejt atij që i thërret, e, jobesimtarët thonë: “Kjo është ditë e vështirë”.');
INSERT INTO "ahmeti" VALUES(54,9,'54:9','Populli i Nuhut që ishte para tyre përgënjeshtroi, dhe robin Tonë (Nuhun) e quajti rrenacak dhe i thanë: “I marrë!” dhe iu bë kërcënim.');
INSERT INTO "ahmeti" VALUES(54,10,'54:10','E ai iu drejtua Zotit të vet: “Unë jam i mundur, prandaj më ndihmo!”');
INSERT INTO "ahmeti" VALUES(54,11,'54:11','Atëherë Ne me një shi të vrullshëm i hapëm dyert e qiellit.');
INSERT INTO "ahmeti" VALUES(54,12,'54:12','Dhe Ne tokën e zbërthyem në burime uji, kurse uji u bashkua siç ishte e caktuar.');
INSERT INTO "ahmeti" VALUES(54,13,'54:13','E atë (Nuhun) e bartëm në (anije) dërrasash e shtyllash të gojëzuara.');
INSERT INTO "ahmeti" VALUES(54,14,'54:14','Që lundronte në mbikëqyrje Tonë. (I fundosëm) Si ndëshkim për atë (Nuhun) që ishte mohuar (përbuzur).');
INSERT INTO "ahmeti" VALUES(54,15,'54:15','Atë (ngjarje) Ne e lamë kujtim, po a ka ndokush që merr mësim?');
INSERT INTO "ahmeti" VALUES(54,16,'54:16','E çfarë ishte ndëshkimi Im dhe kërcënimi Im!');
INSERT INTO "ahmeti" VALUES(54,17,'54:17','Ne Kur’anin e bëmë të lehtë për mësim, po a ndokush që merr mësim?');
INSERT INTO "ahmeti" VALUES(54,18,'54:18','Adi përgënjeshtroi, e çfarë ishte ndëshkimi Im dhe kërcënimi?');
INSERT INTO "ahmeti" VALUES(54,19,'54:19','Atyre Nenjë ditë fatkeqe të përjetshme u lëshuamnjë erë të fortë.');
INSERT INTO "ahmeti" VALUES(54,20,'54:20','I ngriste njerëzit si të ishin trupa hurmash të shkulura.');
INSERT INTO "ahmeti" VALUES(54,21,'54:21','Dhe çfarë ishte ndëshkimi Im dhe vërejtja Ime?');
INSERT INTO "ahmeti" VALUES(54,22,'54:22','Ne Kur’anin e bëmë të lehtë për të kuptuar, a ka ndokush që merr këshillë?');
INSERT INTO "ahmeti" VALUES(54,23,'54:23','Themudi përgënjeshtroi të dërguarit.');
INSERT INTO "ahmeti" VALUES(54,24,'54:24','E thamë: “A të shkojmë pas një të vetmit njeri që doli nga mesi ynë, po Ne atëherë do të jemi të humbur në një marrësi!”');
INSERT INTO "ahmeti" VALUES(54,25,'54:25','A, atij nga mesi jonë iu dha shpallja? Jo, por ai është një gënjeshtar mendjemadh.');
INSERT INTO "ahmeti" VALUES(54,26,'54:26','Mirëpo ata nesër do të kuptojmë se kush është gënjeshtari, mendjemadhi!');
INSERT INTO "ahmeti" VALUES(54,27,'54:27','Ne atyre do t’ua dërgojmë deven si sprovë e ti vetëm dhe bën durim.');
INSERT INTO "ahmeti" VALUES(54,28,'54:28','Dhe lajmëroji ata se uji është i ndarë për ta veç e veç, secili do të paraqitet për të pirë ujë në rendin e vet.');
INSERT INTO "ahmeti" VALUES(54,29,'54:29','Po ata e ftuan shokun e vet, e ai mori dhe e theri atë.');
INSERT INTO "ahmeti" VALUES(54,30,'54:30','E çfarë ishte ndëshkimi ndëshkimi Im dhe kërcënimi Im?');
INSERT INTO "ahmeti" VALUES(54,31,'54:31','Ne lëshuam kundër tyre një ushtimë, e ata u bënë si shtrojë vathi. (Mbeturinë e ushqimit të kafshëve që u shtohet).');
INSERT INTO "ahmeti" VALUES(54,32,'54:32','Ne Kur’anin e bëmë të lehtë për këshillë, po a ka ndokush që merr mësim?');
INSERT INTO "ahmeti" VALUES(54,33,'54:33','Populli i Lutit i përgënjeshtroi vërejtjet e pejgamberit të vet.');
INSERT INTO "ahmeti" VALUES(54,34,'54:34','Ne atyre, përveç famijes së Lutit të cilën e shpëtuam para se të agonte, u lëshuam një erë që solli gurë mbi ta.');
INSERT INTO "ahmeti" VALUES(54,35,'54:35','(Shpëtimi ishte) Një mirësi nga ana Jonë, ashtu Ne shpërblejmë atë që falënderon.');
INSERT INTO "ahmeti" VALUES(54,36,'54:36','Ai (Luti) u pat tërhequr vërejtjen atyre për dënimin Tonë të rëndë, por ata dyshuan në ato kërcënime.');
INSERT INTO "ahmeti" VALUES(54,37,'54:37','Ata deshën t’u afrohen mysafirëve të tij e Ne ua verbëruam sytë atyre, pra përjetoni ndëshkimin Tim dhe qortimet e Mia!');
INSERT INTO "ahmeti" VALUES(54,38,'54:38','E, atyre në një mëngjes të hershëm u erdhi dënimi i përhershëm.');
INSERT INTO "ahmeti" VALUES(54,39,'54:39','Pra, vuanie dënimin Tim dhe qortimin Tim!');
INSERT INTO "ahmeti" VALUES(54,40,'54:40','Ne Kur’anin e bëmë të lehtë për të studiuar, a ka ndokush që merr përvojë nga ai?');
INSERT INTO "ahmeti" VALUES(54,41,'54:41','Edhe rrethit të faraonit i patëm ardhur shumë vërejtje.');
INSERT INTO "ahmeti" VALUES(54,42,'54:42','Ata i përgënjeshtruan të gjitha argumentet Tona, prandaj Ne i dënuam ashtu siç është dënimi i një ngadhënjyesi, i një fuqiploti.');
INSERT INTO "ahmeti" VALUES(54,43,'54:43','A jobesimtarët tuaj janë më të vlefshëm se ata që u përmendën, apo ju keni ndonjë kontratë në librat e qiellit?');
INSERT INTO "ahmeti" VALUES(54,44,'54:44','A mos po thonë: “Ne jemi një grup ngadhënjyes tok të bashkuar?”');
INSERT INTO "ahmeti" VALUES(54,45,'54:45','Grupi do të pësojë disfatë dhe ata do të kthehen prapa.');
INSERT INTO "ahmeti" VALUES(54,46,'54:46','Por jo, afati i tyre është kijameti, e kijameti është edhe më i vështirë, edhe më i hidhur.');
INSERT INTO "ahmeti" VALUES(54,47,'54:47','S’ka dyshim se idhujtarët kriminelë, janë edhe në një huti edhe në zjarr të madh.');
INSERT INTO "ahmeti" VALUES(54,48,'54:48','Ditën kur me fytyrat e tyre do të tërhiqen zvarrë në zjarr. “Vuanie dënimin Sekar”. (emër i një Xhehennemi).');
INSERT INTO "ahmeti" VALUES(54,49,'54:49','Ne çdo send kemi krijuar me masë të caktuar.');
INSERT INTO "ahmeti" VALUES(54,50,'54:50','Puna Jonë (në krijim) është e shpejtë sa çel e mshel sytë.');
INSERT INTO "ahmeti" VALUES(54,51,'54:51','Ne, ata që ishin si ju i shkatërruam, a ka ndokush që merr mësim?');
INSERT INTO "ahmeti" VALUES(54,52,'54:52','Dhe çdo gjë që ata e punuan, gjendet (e shkruar) në shënime.');
INSERT INTO "ahmeti" VALUES(54,53,'54:53','Dhe çdo vepër, qoftë e vogël dhe qoftë e madhe është rradhitur (evidentuar në Lehvi Mahfudh).');
INSERT INTO "ahmeti" VALUES(54,54,'54:54','Është e vërtetë se të devotshmit do të jenë në Xhennete e në lumenj.');
INSERT INTO "ahmeti" VALUES(54,55,'54:55','Në një vend të kënaqshëm, te Sunduesi i plotfuqishëm (te All-llahu).');
INSERT INTO "ahmeti" VALUES(55,1,'55:1','Rrahmani - Mëshiruesi (Zoti ynë),');
INSERT INTO "ahmeti" VALUES(55,2,'55:2','Ai ia mësoi Kur’anin');
INSERT INTO "ahmeti" VALUES(55,3,'55:3','E krijoi njeriun.');
INSERT INTO "ahmeti" VALUES(55,4,'55:4','Ia mësoi atij të folurit (të shprehurit të shqiptuarit).');
INSERT INTO "ahmeti" VALUES(55,5,'55:5','Dielli dhe hëna udhëtojnë sipas një përcaktimi të saktë.');
INSERT INTO "ahmeti" VALUES(55,6,'55:6','Edhe yjet dhe bimët i bëjnë përulje (dëshirës së Rrahmanit).');
INSERT INTO "ahmeti" VALUES(55,7,'55:7','Ai e ngriti qiellin dhe Ai vuri drejtësinë.');
INSERT INTO "ahmeti" VALUES(55,8,'55:8','Që të mos kaloni kufirin në drejtësi.');
INSERT INTO "ahmeti" VALUES(55,9,'55:9','Edhe ju mbani me drejtësi peshojën, e mos leni mangu në peshojë!');
INSERT INTO "ahmeti" VALUES(55,10,'55:10','Ai edhe tokën e bëri të shtrirë për krijesat.');
INSERT INTO "ahmeti" VALUES(55,11,'55:11','Në të ka pemë të llojllojta, ka edhe hurma ma shporta të mbështjella.');
INSERT INTO "ahmeti" VALUES(55,12,'55:12','Edhe drithi me kashtën e tij edhe bimët aromatike (ose ushqyese).');
INSERT INTO "ahmeti" VALUES(55,13,'55:13','E, cilën të mirë të Zotit tuaj po e mohoni (ju o njerëz dhe ju o xhinë)?');
INSERT INTO "ahmeti" VALUES(55,14,'55:14','Ai e krijoi njeriun nga balta e terur si (enë balte) e pjekur.');
INSERT INTO "ahmeti" VALUES(55,15,'55:15','Dhe Ai krijoi xhinët nga flaka (pa tym0 e zjarrit.');
INSERT INTO "ahmeti" VALUES(55,16,'55:16','E, cilën begati të Zotit tuaj e mohoni?');
INSERT INTO "ahmeti" VALUES(55,17,'55:17','Zot i dy lindeve dhe Zot i dy perëndimeve.');
INSERT INTO "ahmeti" VALUES(55,18,'55:18','E, cilën të mirë të Zotit tuaj e mohoni.');
INSERT INTO "ahmeti" VALUES(55,19,'55:19','Ai e lejoi dy detet të puqen ndërmjet vete.');
INSERT INTO "ahmeti" VALUES(55,20,'55:20','Ndërmjet atyre të dyve është një pengues që ata të dy nuk kapërcejnë.');
INSERT INTO "ahmeti" VALUES(55,21,'55:21','E, cilë të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,22,'55:22','Prej atyre të dyve nxirren margaritarë e diamantë.');
INSERT INTO "ahmeti" VALUES(55,23,'55:23','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,24,'55:24','Dhe të Atij janë anijet lundruese të ngritura lart si kodra në det.');
INSERT INTO "ahmeti" VALUES(55,25,'55:25','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,26,'55:26','çdo gjë që është në të (në tokë) është zhdukur.');
INSERT INTO "ahmeti" VALUES(55,27,'55:27','E dotë mbetet vetëm Zoti yt që është i madhëruar e i nderuar!');
INSERT INTO "ahmeti" VALUES(55,28,'55:28','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,29,'55:29','(Ai mbetet) Atij i drejtohen me lutje kush është në qiej e në tokë dhe Ai në çdo moment është i angazhuar në çështje të reja (falë mëkate, largon brengosje, jep jetë, jep vdekje, krijon gjendje, zhduk tëtjera etj.).');
INSERT INTO "ahmeti" VALUES(55,30,'55:30','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,31,'55:31','Ne do të merremi me (llogarinë për ju) ju, o ju dy të rëndësishmit (njerëz dhe xhinë).');
INSERT INTO "ahmeti" VALUES(55,32,'55:32','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,33,'55:33','O turmë e xhinëve dhe e njerëzve, nëse keni mundësi të dilni përtej kufijve të qiejve e të tokës, depërtoni pra, po nuk mundeni vetëm me ndonjë fuqi të fortë.');
INSERT INTO "ahmeti" VALUES(55,34,'55:34','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,35,'55:35','Juve dy grumbujve u derdhet përmbi flakë zjarri dhe do t’uderdhen rem i shkrirë e nuk do të keni mundësi të mbroheni.');
INSERT INTO "ahmeti" VALUES(55,36,'55:36','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,37,'55:37','E kur të çahet qielli e bëhet si vaji i shkrirë.');
INSERT INTO "ahmeti" VALUES(55,38,'55:38','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,39,'55:39','Atë ditë nuk pyetet për mëkatin e tij, as njeriu as xhini.');
INSERT INTO "ahmeti" VALUES(55,40,'55:40','E, cilën të mirë të Zotit tuaj po e mohonni?');
INSERT INTO "ahmeti" VALUES(55,41,'55:41','Kriminelët njhen me tiparet e tyre, andaj me rrëmbim kapen për flok dhe për këmbët e tyre.');
INSERT INTO "ahmeti" VALUES(55,42,'55:42','E, cilën të mirë të Zotit tuaj poe mohoni?');
INSERT INTO "ahmeti" VALUES(55,43,'55:43','Ja, ky është Xhehennemi, të cilin kriminelët e mohonin.');
INSERT INTO "ahmeti" VALUES(55,44,'55:44','Ata do të sillen vërdallë ndërmjet atij (zjarrit) dhe ujit të valë deri në kulminacion.');
INSERT INTO "ahmeti" VALUES(55,45,'55:45','E, cilën të mirë të Zotit tuaj po e Mohoni?');
INSERT INTO "ahmeti" VALUES(55,46,'55:46','E, për atë që i pat frikë paraqitjes para Zotit të vet, janë dyXhennete.');
INSERT INTO "ahmeti" VALUES(55,47,'55:47','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,48,'55:48','(Xhennete) plot degë me gjethe e pemë.');
INSERT INTO "ahmeti" VALUES(55,49,'55:49','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,50,'55:50','Në ata të dy janë dy kroje që rrjedhin.');
INSERT INTO "ahmeti" VALUES(55,51,'55:51','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,52,'55:52','Në ata të dy, prej të gjtitha pemëve ka nga dy lloje.');
INSERT INTO "ahmeti" VALUES(55,53,'55:53','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,54,'55:54','Të mbështëetur në kolltukë që i kanë mbulesat e kadifesë, dhe pemët e atyre dy Xhenneteve janë krejt afër.');
INSERT INTO "ahmeti" VALUES(55,55,'55:55','E, cilën të mirë të Zotit tuaj po e Mohoni?');
INSERT INTO "ahmeti" VALUES(55,56,'55:56','Aty janë ato që përqëndrojnë shikimet e tyre (vetëm në burrat e vet) e që nuk i ka prekur kush para tyre, as njerëz, as xhin.');
INSERT INTO "ahmeti" VALUES(55,57,'55:57','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,58,'55:58','Ato janë si xhevahiri e diamanti (të kuqrremta në të bardhë.');
INSERT INTO "ahmeti" VALUES(55,59,'55:59','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,60,'55:60','A mund të jetë shpërblimi i veprës së mirë diç tjetër, pos të së mirës!');
INSERT INTO "ahmeti" VALUES(55,61,'55:61','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,62,'55:62','E nën ato dy (Xhennete) janë edhe dy Xhennete.');
INSERT INTO "ahmeti" VALUES(55,63,'55:63','Po cilën të mirë nga të mirat e Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,64,'55:64','Nga gjelbërimi i shumtë duken të mbyllura në të zi.');
INSERT INTO "ahmeti" VALUES(55,65,'55:65','E, cilën nga të mirat e Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,66,'55:66','Aty janë dy burime që gjithnjë gurgullojnë.');
INSERT INTO "ahmeti" VALUES(55,67,'55:67','E, cilën të mirë të Zoti tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,68,'55:68','Aty, në ata të dy, ka hurma dhe shegë.');
INSERT INTO "ahmeti" VALUES(55,69,'55:69','E cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,70,'55:70','Ato janë hyri që e kufizojnë ndejën vetëm në shtëpinë (tënde).');
INSERT INTO "ahmeti" VALUES(55,71,'55:71','Ato janë hyri që e kufizojnë ndejën vetëm në shtëpinë (tënde).');
INSERT INTO "ahmeti" VALUES(55,72,'55:72','Ato janë hyri që e kufizojnë ndejën vetëm në shtëpinë (tënde).');
INSERT INTO "ahmeti" VALUES(55,73,'55:73','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,74,'55:74','Ato nuk i ka prekur kush para tyre, as njeriu as xhini.');
INSERT INTO "ahmeti" VALUES(55,75,'55:75','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,76,'55:76','Aty rrinë të mbështetura në mbështetëse të gjelbra e shtroja të bukura.');
INSERT INTO "ahmeti" VALUES(55,77,'55:77','E, cilën të mirë të Zotit tuaj po e mohoni?');
INSERT INTO "ahmeti" VALUES(55,78,'55:78','I lartësuar është emri i Zotit tënd, të madhëruar e të nderuar!');
INSERT INTO "ahmeti" VALUES(56,1,'56:1','E kur të ngjajë ndodhia (kijameti).');
INSERT INTO "ahmeti" VALUES(56,2,'56:2','Realitetin e asaj ndodhie nuk a kush që do ta përgënjeshtrojë.');
INSERT INTO "ahmeti" VALUES(56,3,'56:3','Ajo ëshë që rrëzon, është që grit (lart).');
INSERT INTO "ahmeti" VALUES(56,4,'56:4','(ajo ndodh) Kur toka tundet me dridhje të forta.');
INSERT INTO "ahmeti" VALUES(56,5,'56:5','E kodrat shkapërderdhën e bëhen copë e thërmi.');
INSERT INTO "ahmeti" VALUES(56,6,'56:6','E bëhen pluhur i shpërndarë.');
INSERT INTO "ahmeti" VALUES(56,7,'56:7','Dhe ju të ndaheni në tri grupe.');
INSERT INTO "ahmeti" VALUES(56,8,'56:8','E ata të djathtit, kush janë ata të djathtit se?');
INSERT INTO "ahmeti" VALUES(56,9,'56:9','Po të majtit, kush janë ata të majtit?');
INSERT INTO "ahmeti" VALUES(56,10,'56:10','Ndërsa të përparmit janë të dalluar (janë në ballë).');
INSERT INTO "ahmeti" VALUES(56,11,'56:11','Ata pra, janë më të afruarit (te Zoti).');
INSERT INTO "ahmeti" VALUES(56,12,'56:12','Janë në Xhennetet e begatshme.');
INSERT INTO "ahmeti" VALUES(56,13,'56:13','(ata janë) Shumë prej (popujve) të hershmëve.');
INSERT INTO "ahmeti" VALUES(56,14,'56:14','E janë pak prej të mëvonshmëve.');
INSERT INTO "ahmeti" VALUES(56,15,'56:15','Janë në shtretër të qendisur me ar.');
INSERT INTO "ahmeti" VALUES(56,16,'56:16','Të mbështetur në to përballë njëri-tjetrit.');
INSERT INTO "ahmeti" VALUES(56,17,'56:17','Atyre u shërbejnë djelmosha të gjithmonshëm.');
INSERT INTO "ahmeti" VALUES(56,18,'56:18','Me gastare, me ibrigë dhe me gota të mbushura plot prej burimit.');
INSERT INTO "ahmeti" VALUES(56,19,'56:19','Prej së cilës pije (vere) as nuk i dhemb koka, as nuk dehen.');
INSERT INTO "ahmeti" VALUES(56,20,'56:20','Edhe pemë sipas dëshirës së tyre.');
INSERT INTO "ahmeti" VALUES(56,21,'56:21','Edhe mish shpezësh, cilin ta duan.');
INSERT INTO "ahmeti" VALUES(56,22,'56:22','Dhe (gratë e tyre janë) hyri symëdha.');
INSERT INTO "ahmeti" VALUES(56,23,'56:23','Si të ishim inxhi të ruajtura.');
INSERT INTO "ahmeti" VALUES(56,24,'56:24','Shpërblim për atë, që ata punuan.');
INSERT INTO "ahmeti" VALUES(56,25,'56:25','Aty nuk dëgjojnë fjalë të këqija as që janë mëkat (gënjeshtër).');
INSERT INTO "ahmeti" VALUES(56,26,'56:26','Vetëm thënie: selam pas selami.');
INSERT INTO "ahmeti" VALUES(56,27,'56:27','E për sa u përket atyre të djathtëve, çka është gjendja e të djathtëve?');
INSERT INTO "ahmeti" VALUES(56,28,'56:28','Janë nën drunj të pemëve pa therra (pa gjemba).');
INSERT INTO "ahmeti" VALUES(56,29,'56:29','Të bananeve me fruta palë-palë.');
INSERT INTO "ahmeti" VALUES(56,30,'56:30','Nën hije të përhershme.');
INSERT INTO "ahmeti" VALUES(56,31,'56:31','Pranë ujit rrjedhës.');
INSERT INTO "ahmeti" VALUES(56,32,'56:32','Edhe ndër pemë shumë llojesh.');
INSERT INTO "ahmeti" VALUES(56,33,'56:33','Që as nuk ndërprehen, as nuk janë të ndaluara.');
INSERT INTO "ahmeti" VALUES(56,34,'56:34','Edhe shtretër të lartë (ose gra të larta).');
INSERT INTO "ahmeti" VALUES(56,35,'56:35','Ne i kemi krijuar ato në një krijim të ri (formë të re).');
INSERT INTO "ahmeti" VALUES(56,36,'56:36','Dhe ato i kemi bërë virgjëresha.');
INSERT INTO "ahmeti" VALUES(56,37,'56:37','Të dashuruara (për burrat e vet), të një moshe.');
INSERT INTO "ahmeti" VALUES(56,38,'56:38','(Ato janë) për ata të djathtët.');
INSERT INTO "ahmeti" VALUES(56,39,'56:39','(Të djathtët) Janë shumë nga të parët (të hershmit).');
INSERT INTO "ahmeti" VALUES(56,40,'56:40','Janë shmë nga më të vonshmit.');
INSERT INTO "ahmeti" VALUES(56,41,'56:41','E të majtit, ç’është puna e të majtëve?');
INSERT INTO "ahmeti" VALUES(56,42,'56:42','Janë në vapë të zjarrit dhe në ujë të valë.');
INSERT INTO "ahmeti" VALUES(56,43,'56:43','Dhe në errësirë tymi.');
INSERT INTO "ahmeti" VALUES(56,44,'56:44','As e freskët e as e këndshme.');
INSERT INTO "ahmeti" VALUES(56,45,'56:45','Ata përpara kësajkanë qenë të dhënë pas komoditetit (qejfeve).');
INSERT INTO "ahmeti" VALUES(56,46,'56:46','Dhe ata ishin vazhdimisht në mëkatin e madh.');
INSERT INTO "ahmeti" VALUES(56,47,'56:47','Dhe ishin që thonin: “A pasi të vdesim, të bëhemi dhe e eshtra të kalbur, njëmend do të ringjallemi?');
INSERT INTO "ahmeti" VALUES(56,48,'56:48','A edhe prindërit tanë të hershëm?');
INSERT INTO "ahmeti" VALUES(56,49,'56:49','Thuaj: “Edhe të parët edhe të fundit!”');
INSERT INTO "ahmeti" VALUES(56,50,'56:50','Kemi për t’u tubuar në një ditë të caktuar!');
INSERT INTO "ahmeti" VALUES(56,51,'56:51','Pastaj ju, o të humbur e gënjeshtarë!');
INSERT INTO "ahmeti" VALUES(56,52,'56:52','Keni për të ngrënë prej pemës Zekumë!');
INSERT INTO "ahmeti" VALUES(56,53,'56:53','Prej saj keni për t’i mbushur barqet!');
INSERT INTO "ahmeti" VALUES(56,54,'56:54','E menjëherë pas do të pini pre ujit t vluar!');
INSERT INTO "ahmeti" VALUES(56,55,'56:55','Do të pini si deveja e etshme!”');
INSERT INTO "ahmeti" VALUES(56,56,'56:56','Kjo është mirëseardhja e tyre ditën e gjykimit.');
INSERT INTO "ahmeti" VALUES(56,57,'56:57','Ne u kemi krijuar juve, e përse nuk pranoni (ringjalljen)?');
INSERT INTO "ahmeti" VALUES(56,58,'56:58','A më tregoni për farën që e dredhni,');
INSERT INTO "ahmeti" VALUES(56,59,'56:59','A ju e krijoni atë, apo Ne jemi që e krijojmë?');
INSERT INTO "ahmeti" VALUES(56,60,'56:60','Ne e kemi caktuar (dhe barazuar) ndërmjet jush vdekjen dhe Ne nuk ka kush që mund të na pengojë.');
INSERT INTO "ahmeti" VALUES(56,61,'56:61','Që t’ju zëvendësojë me ë tjerë si ju dhe t’ju krijojmë rishtazi siç nuk dini ju.');
INSERT INTO "ahmeti" VALUES(56,62,'56:62','Ju e keni të njohur zanafillën e parë, e përse nuk mendoni.');
INSERT INTO "ahmeti" VALUES(56,63,'56:63','A nuk e shihni atë që e mblidhni?');
INSERT INTO "ahmeti" VALUES(56,64,'56:64','A ju e bëni të mbijë ajo, apo Ne e bëjmë të mbijë?');
INSERT INTO "ahmeti" VALUES(56,65,'56:65','Sikur Ne të dëshironim, do ta bënim atë të thyer (të shkatërruar) e ju do të mbeteshit gjithmonë të habitur (duke thënë).');
INSERT INTO "ahmeti" VALUES(56,66,'56:66','S’ka dyshim se ne mbetëm të dëmtuar.');
INSERT INTO "ahmeti" VALUES(56,67,'56:67','Po jo, ne mbetëm pa të (të varfëruar).');
INSERT INTO "ahmeti" VALUES(56,68,'56:68','A e shihni ujin që po e pini?');
INSERT INTO "ahmeti" VALUES(56,69,'56:69','A ju e lëshoni atë prej reve, apo Ne jemi që e lëshojmë?');
INSERT INTO "ahmeti" VALUES(56,70,'56:70','Sikur Ne të dëshirojmë, e bëjmë atë të njelmët, përse nuk falënderoni?');
INSERT INTO "ahmeti" VALUES(56,71,'56:71','A e keni parë zjarrin që ju e ndizni?');
INSERT INTO "ahmeti" VALUES(56,72,'56:72','A ju krijuat drurin e tij, apo Ne jemi krijues?');
INSERT INTO "ahmeti" VALUES(56,73,'56:73','Ne e bëmë atë si përkujtim (për zjarrin e Xhehennemit) dhe të nevojshëm për ata në vende të pabanuara (në shkretërirë).');
INSERT INTO "ahmeti" VALUES(56,74,'56:74','Pra, ti lartësoje emrin e Zoti tënd të madh.');
INSERT INTO "ahmeti" VALUES(56,75,'56:75','Betohem në shuarjen (përëndimin) e yjeve.');
INSERT INTO "ahmeti" VALUES(56,76,'56:76','E ai është betim imadh, sikur ta dini!');
INSERT INTO "ahmeti" VALUES(56,77,'56:77','Se me të vërtetë ai është Kur’an i famshëm.');
INSERT INTO "ahmeti" VALUES(56,78,'56:78','Është në një libër të ruajtur.');
INSERT INTO "ahmeti" VALUES(56,79,'56:79','Atë nuk e prek kush, vetëm të pastrit (melaiket).');
INSERT INTO "ahmeti" VALUES(56,80,'56:80','Është i zbritur prej Zotit të botëve.');
INSERT INTO "ahmeti" VALUES(56,81,'56:81','A, ndaj këtij ligjërimi (Kur’ani) ju jeni që e gënjeni.');
INSERT INTO "ahmeti" VALUES(56,82,'56:82','Dhe falënderimi për furnizimin tuaj, ju jeni që e gënjeni.');
INSERT INTO "ahmeti" VALUES(56,83,'56:83','Përse, pra kur arrin shpirti në fyt.');
INSERT INTO "ahmeti" VALUES(56,84,'56:84','E ju në atë moment shikoni (se ç’po i ngjet).');
INSERT INTO "ahmeti" VALUES(56,85,'56:85','E Ne jemi më afër te se ju, por ju nuk shihni.');
INSERT INTO "ahmeti" VALUES(56,86,'56:86','Dhe përse, nëse ju nuk jeni përgjegjës (për vepra),');
INSERT INTO "ahmeti" VALUES(56,87,'56:87','(Përse) Nuk e ktheni atë (shpirtin të mos dalë).');
INSERT INTO "ahmeti" VALUES(56,88,'56:88','E nëse ai (i vdekuri) është prej të afërmëve (të Zotit).');
INSERT INTO "ahmeti" VALUES(56,89,'56:89','Ai ka (te Zoti) kënaqësi, furnizim të mirë dhe Xhennet të begatshëm.');
INSERT INTO "ahmeti" VALUES(56,90,'56:90','Po në qoftë se është nga të djathtët?');
INSERT INTO "ahmeti" VALUES(56,91,'56:91','Pra, ty të qoftë se selam prej të djathtëve (i thuhet).');
INSERT INTO "ahmeti" VALUES(56,92,'56:92','E në qoftë se është prej gënjeshtarëve të humbur,');
INSERT INTO "ahmeti" VALUES(56,93,'56:93','Mirëseardhja e tyre është pritje me ujë të valë.');
INSERT INTO "ahmeti" VALUES(56,94,'56:94','Dhe djegie nga zjarri i Xhehennemit.');
INSERT INTO "ahmeti" VALUES(56,95,'56:95','E s’ka dyshim se kjo është ajo e vërteta e sigurt.');
INSERT INTO "ahmeti" VALUES(56,96,'56:96','Pra ti lartëso Zotin tënd të madhëruar');
INSERT INTO "ahmeti" VALUES(57,1,'57:1','All-llahun e lartësoi me adhurim ç’ka në qiej e në tokë dhe Ai është i gjithfuqishmi, i urti.');
INSERT INTO "ahmeti" VALUES(57,2,'57:2','I Tij është pushteti në qiej e në tokë, Ai jep jetë dhe Ai jep vdekje dhe Ai ka fuqi për çdo send.');
INSERT INTO "ahmeti" VALUES(57,3,'57:3','Ai është i pari që s’ka mbarim, i dukshmi dhe i padukshmi, dhe Ai është më i dijshmi për çdo gjë.');
INSERT INTO "ahmeti" VALUES(57,4,'57:4','Ai është që krijoi qiejt dhe tokën vetëm për gjashtë ditë, pastaj qëndroi mbi Arsh. Ai e di çka futet në tokë dhe çka del prej saj, çka zbret nga qielli dhe çka ngritet në të, dhe Ai është me ju kudo që të jeni, All-llahu është përcjellës i asaj që punoni.');
INSERT INTO "ahmeti" VALUES(57,5,'57:5','Vetëm i Tij është sundimi i qiejve dhe i tokës dhe vetëm te All-llahu kthehen të gjitha çështjet.');
INSERT INTO "ahmeti" VALUES(57,6,'57:6','Ai e ngërthen (e zgjaton) natën në ditë dhe e ngërthen (zgjaton) ditën në natë dhe Ai është që i di të fshehtat në gjoksa.');
INSERT INTO "ahmeti" VALUES(57,7,'57:7','Besoni All-llahut dhe të dërguarit të Tij dhe jepni nga ajo që Ai u bëri trashëgues në të, se ata që besuan prej jush dhanë për hir të Zotit, ata kanë shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(57,8,'57:8','ç’keni ju që nuk i besoni All-llahut, kue i dërguari i Tij ju thërret t’i besoni Zotit tuaj dhe kur Ai ka marrë besën tuaj, nëse jeni besimtarë (o sot o kurrë).');
INSERT INTO "ahmeti" VALUES(57,9,'57:9','Ai robit të vet i shpall argumente të qarta për t’iu nxjerrë juve prej errësirave ne dritë. E, s’ka dyshim se All-llahu është i butë e i mëshirshëm ndaj jush.');
INSERT INTO "ahmeti" VALUES(57,10,'57:10','E, ç’keni ju që nuk jepni për rrugën e All-llahut, kur dihet se All-llahut i mbesin trashëgim qiejt dhe toka? Nuk janë të barabartë prej jush ata që dhanë nga pasuria e tyre dhe luftuan para çlirimit, sepse të tillët kanë vlerë të madhe nga ata që dhanë dhe luftuan pas. Por të gjithve All-llahu u premyoi të mirat; All-llahu di çka punoni.');
INSERT INTO "ahmeti" VALUES(57,11,'57:11','Kush është ai që i huazon All-llahut një hua të mirë e që Ai t’ia shumëfishojë atë dhe ai ka një shpërbllim të mirë.');
INSERT INTO "ahmeti" VALUES(57,12,'57:12','(Përkujto) Ditën kur i sheh besimtarët dhe besimtaret që drita e tyre flakëron para tyre dhe në djathtë të tyre. (U thuhet) Myzhde e juaj sot janë Xhennetet nën të cilat rrjedhin lumenj, aty do të jeni përgjithmonë. E ajo është fitore e madhe.');
INSERT INTO "ahmeti" VALUES(57,13,'57:13','Ditën kur hipokritët dhe hipokritet atyre që besuan u thonë: “Na pritni (ose na shikoni) të ndriçoheni prej dritës suaj!” U thuhet: “Kthehuni prapa jush (në dynja) e kërkoni ndonjë dritë!” Atëherë vihet ndërmjet tyre njëfarë muri që ka një derë, e brendia e tij është mëshirë (Xhenneti), e ana e jashtme e tij është dënimi (zjarri).');
INSERT INTO "ahmeti" VALUES(57,14,'57:14','(Hipokritët e mbetur në errësirë) I thërrasin ata (besimtarët): “A nuk kemi qenë edhe ne së bashku me ju!? (Besimtarët përgjigjen): “Po, por ju e shkatërruat veten (me hipokrizi), ju prisnit kob të zi për besimtarët, ju dyshonit çëshjtet e fesë dhe ju mashtruan shpresat e kota deri kur ju erdhi caktimi i All-llahut (vdekja) dhe ashtu, mashtruesi ju mashtroi.');
INSERT INTO "ahmeti" VALUES(57,15,'57:15','E sot, prej jush nuk pranohet ndonjë kompensim e as prej atyre që nuk besuan, vendi juaj është zjarri, ai është ndihma e juaj: sa përfundim i keq është ai”.');
INSERT INTO "ahmeti" VALUES(57,16,'57:16','A nuk është koha që zemrat të zbuten me këshillat e All-llahut dhe ne atë të vërtetën që zbriti (me Kur’an), e të mos bëhen si ata, të cilëve u është dhënë libri më parë e zhgjati koha dhe zemrat e tyre u shtangën e shumë prej tyre janë jashtë rrugës.');
INSERT INTO "ahmeti" VALUES(57,17,'57:17','Dijeni se All-llahu e ngjall tokën pas vdekjes së saj. Ne ua kemi sqaruar argumentet ashtu që të kuptoni.');
INSERT INTO "ahmeti" VALUES(57,18,'57:18','S’ka dyshim se ata që japin lëmoshë (sadaka) dhe sinqerisht japin për hirë të All-llahut, atyre u shumëfishohet dhe ata kanë shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(57,19,'57:19','Ata që besuan All-llahun dhe të dërguarin e Tij, ata janë të sinqertit dhe dëshmorët te Zoti i tyre, ata kanë shpërblimin e vet, ndërsa ata që e mohuan dhe përgënjeshtruan faktet Tona, ata janë banues të Xhehennemit.');
INSERT INTO "ahmeti" VALUES(57,20,'57:20','Ju njerëz dijeni se jeta e kësaj bote nuk është tjetër vetëm se lojë, kalim kohe në argëtim, stoli, krenari mes jush dhe përpjekje në shtimin e pasurisë dhe të fëmijve, e që është shembull i një përpjekje në shtimin e pasurisë dhe të fëmijve, e që është shembull i një shiu prej të cilit bima habit bujqit, e pastaj ajo thahet dhe e sheh atë t verdhë, mandej të thyer e llomitur, e në botën tjetër është dënimi i rëndë, por edhe falje mëkatesh dhe dhurim i kënaqësisë nga All-llahu; pra jeta e kësaj bote nuk është tjetër vetëm se përjetim mashtrues.');
INSERT INTO "ahmeti" VALUES(57,21,'57:21','Shpejtoni në atë që e fitoni falje prej Zotit tuaj dhe Xhennetit që gjërsia e qiellit e tokës, i përgaditur për ata që i besuan All-llahut dhe të dërguarve të Tij. Ajo është dhunti e All-llahut ë ia jep kujt të dojë, All-llahu është dhurues i madh.');
INSERT INTO "ahmeti" VALUES(57,22,'57:22','Nuk ndodh asnjë fatkeqësi në tokë e as në trupin tuaj, e që të mos jetë në shënime (libër - Lehvi Mahfudh) para se të ngjajë ajo, e kjo për All-llahun është e lehtë.');
INSERT INTO "ahmeti" VALUES(57,23,'57:23','Ashtu që të mos dëshproheni tepër për atë që u ka kaluar, e as të mos gëzohi tepër me atë që Ai u ka dhënë, pse All-llahu nuk e do asnjë arrogant që u lavdërohet të tjerëve.');
INSERT INTO "ahmeti" VALUES(57,24,'57:24','Të cilët vetë bëjnë koprraci dhe me koprraci i këshillojnë njerzit. E kush largohet prej dhënies, le ta dijë se All-llahu është i begatshëm, i lavdishëm.');
INSERT INTO "ahmeti" VALUES(57,25,'57:25','Ne i dërguam të dërguarit Tonë me dokumente të qarta dhe Ne zbritëm me ata librin dhe drejtësinë që t’i përmbahen njerëzit të së drejtës. Ne e kemi zbritur edhe hekurin që në të ka forcë të fortë dhe dobi për njerz, e edhe për t’u ditur te All-llahu se kush ndihmon Atë (rrugën e Tij) dhe të dërguarit e Tij duke i besuar të fshehtës. S’ka dyshim se All-llahu është furnizues, mbizotërues.');
INSERT INTO "ahmeti" VALUES(57,26,'57:26','Ne Nuhun dhe Ibrahimin i zgjodhëm të dërguar e pasardhësve të atyre dyve ua dhamë pejgamberllëkun dhe librin, e disa prej tyre po shumë prej tyre mbetën jashtë rrugës.');
INSERT INTO "ahmeti" VALUES(57,27,'57:27','Pastaj vazhduam pas tyre me të dërguarit Tanë, e pas tyre dërguam Isain të birin e Merjemes dhe atij ia dhamë Ingjillin, e në zemrat e ithtarëve të tij dhuruam butësi e mëshirë, ndërsa murgësinë ata vetë e shpikën. Ne atë nuk ua bëmë obligim atyre, mirëpo edhe pse kishin për qëllim me të vetëm të arrijnë kënaqësinë e All-llahut, ata nuk iu përmbajtën asaj si duhet përmbajtur, prandaj atyre që besuan, Ne u dhamë shpërblimin e tyre, po shumica prej tyre janë mëkatarë (jobesimtarë).');
INSERT INTO "ahmeti" VALUES(57,28,'57:28','O ju që keni besuar, kinie frikë All-llahun dhe vazhdoni me besim ndaj të dërguarit të Tij, se Ai ju jep dyfish nga mëshira e tij, ju dhuron dritë që ecni me të, ju falë mëkatet tuaja All-llahu është mekatfalës, mëshirues.');
INSERT INTO "ahmeti" VALUES(57,29,'57:29','Le ta dinë ithtarët e librit se ata nuk kanë asgjë në dorë ng mirësia e All-llahut dhe se të gjithë e mira (pejgamberllëku, shpallja) është në dorë të All-llahut, e Ai ia jep kujt të dojë, All-llahu është dhurues i madh.');
INSERT INTO "ahmeti" VALUES(58,1,'58:1','Vërtet, All-llahu dëgjoi fjalët e asaj e cila bisedoi me ty lidhur më burrin e vet, që u ankua te All-llahu; prandaj All-llahu e dëgjon bisedën tuaj, All-llahu dëgjon çdo bisedë dhe sheh çdo punë.');
INSERT INTO "ahmeti" VALUES(58,2,'58:2','Ata prej jush që bëjnë “Dhihar” (epërsia ime ndaj teje është haram si epërsia ndaj nënës sime) ndaj grave të veta, në të vërtetë ato nuk janë nënat e tyre, nënat e tyre janë vetëm ato që i kanë lindur ata, ndërsa ata janë janë duke thënë fjalë të irituara dhe gënjeshtra, por All-llahu është që lë shumë pa marrë në përgjegjësi dhe shumë falë.');
INSERT INTO "ahmeti" VALUES(58,3,'58:3','Ata që krahasojnë (me nëna) dhe largohen nga gratë e veta, e pastaj zmbrapsen nga ajo që kanë thënë, janë të obliguar që para se të kontaktojnë ndërmjet vete ta lirojnë një rob. Kjo është dispozitë me të cilën porositeni hollësisht me atë që ju e punoni.');
INSERT INTO "ahmeti" VALUES(58,4,'58:4','E kush nuk gjen (rob për lirim), atëherë le të agjërojë dy muaj rresht para se të kontaktojnë, e kush nuk mundet, atëherë le t’i ushqejë gjashtëdhjetë të varfër. Kjo (dispozitë ju shërben) për të vërtetën ju se besoni All-llahun dhe të gërguarin e Tij (e jo zakonet injorante). Këto, pra janë përcaktimet e All-llahut. ndërkaq, mohuesit kanë dënim të rëndë.');
INSERT INTO "ahmeti" VALUES(58,5,'58:5','Është e vërtetë se ata që i kundërvhen All-llahut dhe të dërguarit të Tij, janë të mposhtur, ashtu siç u mposhtën ata përpara tyre. Mirëpo, Ne u kemi shpallur fakte të qarta, e për mohuesit është një dënim i turpshëm.');
INSERT INTO "ahmeti" VALUES(58,6,'58:6','Në ditën e kur All-llahu i ringjall ata të gjithë, i njofton me atë që kanë punuar, pse All-llahu ka mbajtur ato shënime, dhe pse ata i kanë harruar. All-llahu është Ai që çdo send esheh dhe e përcjell.');
INSERT INTO "ahmeti" VALUES(58,7,'58:7','A nuk e di ti (o dëgjues) se All-llahu di çka në qiej dhe çka në tokë?! Nuk bëhet bisedë e fshehtë mes tre vetave e të mos jetë Ai i katërti; e as mes pesë vetave e të mos jetë Ai i gjashti, e as mes pak vetave dhe as në shumë vetave, e të mos jetë Ai me ta, kudo që të jenë. Pastaj, në ditën e kijametit i njofton ata me atë që kanë punuar. All-llahu ka përshirë çdo send në dijen e vet.');
INSERT INTO "ahmeti" VALUES(58,8,'58:8','A nuk i sheh ata që ishin të ndaluar të bisedojnë fshehtë, se si i kthehen përsëri asaj na e cila ishin të ndaluar dhe pëshpërisin për çka është mëkat, për çka është mizori çka është në kundërshti të të dërguarit. Ata kur vjnë të ti, të përshënesin me atë që All-llahu nuk të përshëndet dhe vetëmevete thonë: “Përse All-llahu nuk na dënon për kët që po e themi?” Por, mjaft është për ta Xhehennemi në të cilin hidhen. Sa vend i shëmtuar është ai.');
INSERT INTO "ahmeti" VALUES(58,9,'58:9','O ju që keni besuar, kur bisedoni mes vete fshehurazi, mos bisedoni për çka është mëkat, armiqësi dhe kundërshti ndaj pejgamberit, por bisedoni për punë të ndershme e të matura dhe kinia frikën All-llahut, te i cili do të tuboheni!');
INSERT INTO "ahmeti" VALUES(58,10,'58:10','Bisedën e fshehtë e nxit vetëm djalli për t’i shqetësuar ata që besuan, ndonëse ajo nuk mund t’i dëmtojë fare pa dëshirën e All-llahut le t’i mbështetën besimtarët!');
INSERT INTO "ahmeti" VALUES(58,11,'58:11','O ju që besuat, kur t’ju thuhet: zgjerohuni (bëni vend) në vend ndeje, ju zgjërohuni, se All-llahu bën zgjërim për ju, e kur t’ju thuhet: ngrituni! Atëherë ju ngrihuni, All-llahu lartëson ata që besuan prej jush, i lartëson në shkallë të lartë ata të cilëve u është dhënë dituri. All-llahu është i njohur mirë me atë që punoni.');
INSERT INTO "ahmeti" VALUES(58,12,'58:12','O ju që keni besuar, kur dëshironi të bisedoni me të dërguarin, jepne një sadaka para bisedës suaj. Kjo është për ju më e pastër; e nëse nuk keni çka të jepni, All-llahu është i njohur mirë me atë që punoni.');
INSERT INTO "ahmeti" VALUES(58,13,'58:13','A u frikësuat (varfërisë) të jepni lëmoshë në prag të bisedës suaj? E kur ju nuk e bëtë dhe All-llahu ju fali, atëherë pra, falnie namazin, jepnie zeqatin, respekonie All-llahun dhe të dërguarin e Tij, All-llahu është i njohur me punën tuaj.');
INSERT INTO "ahmeti" VALUES(58,14,'58:14','A e dini për atë që kanë miqësuar një popull që All-llahu ka shprehur mallkim kundër tyre? Ata nuk janë as prej jush as prej atyre dhe me vetëdije betohen rrejshëm.');
INSERT INTO "ahmeti" VALUES(58,15,'58:15','All-llahu për ta ka përgatitur një ndëshkim të ashpër. Vërtet, atë që bëjnë është shumë e keqe.');
INSERT INTO "ahmeti" VALUES(58,16,'58:16','Ata betimin e tyre e kanë marrë si mbrojtje dhe kanë penguar prej rrugës së All-llahut, andaj ata i pret një dënim dënim poshtrues.');
INSERT INTO "ahmeti" VALUES(58,17,'58:17','Atyre nuk do t’u bëjë kurrfarë dobie pasuria e tyre as fëmijët e tyre para All-llahut. Ata janë banorë të zjarrit, aty janë përgjithmonë.');
INSERT INTO "ahmeti" VALUES(58,18,'58:18','Ditën kur All-llahu i ringjall të gjithë, e ata i betohen Atij, sikurse u betoheshin juve, dhe mendojnë (me atë betim) të arrijnë diçka. Pra ta dini, se ata janë me të vërtetë rrenacakët.');
INSERT INTO "ahmeti" VALUES(58,19,'58:19','Ata i ka nënshtruar djalli dhe ua ka qitur në harresë porositë e All-llahut. Ata janë shoqëri e djallit, e dinë se ithtarët e djallit janë më të dëshpruarit.');
INSERT INTO "ahmeti" VALUES(58,20,'58:20','S’ka dyshim se ata që e kundërshtojnë All-llahun dhe të dërguarin e Tij, të tillët janë ndër më të poshtëruarit.');
INSERT INTO "ahmeti" VALUES(58,21,'58:21','All-llahu ka përcaktuar (shkruar në Levhi Mahvudh): “Unë dhe të dërguarit e Mi, pa tjetër do të ngadhnjejmë!” All-llahu është i fortë, ngadhnjyes.');
INSERT INTO "ahmeti" VALUES(58,22,'58:22','Nuk gjen popull që e beson All-llahun dhe ditën e gjykimit, e ta dojë atë që e kundërshton All-llahun dhe të dërguarin e Tij, edhe sikur të jenë ata (kundërshtarët) prindërit e tyre, ose fëmijët e tyre, ose vëllezërit e tyre, ose farefisi i tyre. Ata janë që në zemrat e tyre (Ai) ka skalitur besimin dhe ka forcuar me shpirt nga ana e Tij dhe ata do t’i shpjerë në Xhennete, nëpër të cilin rrjedhin lumenj. Aty janë përgjithmonë. All-llahu ua ka pëlqyer punën e tyre dhe ata janë të kënaqur me shpërblimin e Tij. Të tillët janë palë (grup, parti) e All-llahut ta dini pra, se ithtarët e All-llahut janë ata të shpëtuarit.');
INSERT INTO "ahmeti" VALUES(59,1,'59:1','All-llahun e madhëron me tesbih (duke i mohuar ato që nuk i takojnë atij) gjithçka ka në tokë, e Ai është i fuqishmi, i urti.');
INSERT INTO "ahmeti" VALUES(59,2,'59:2','Ai është që në dëbimin e parë i nxoru prej shtëpive të tyre ata nga ithtarët e librit, të cilët nuk besuan. Ju nuk menduat se fortifikatat e tyre do t’i mbronin prej ndëshkimit të All-llahut, po All-llahu u erdhi atyre nga nuk e kishin menduar dhe në zemrat e tyre hodhi frikën ashu që me duart e veta dhe me duart të besimtarëve të rrëzonin shtëpitë e veta; pra merrni përvojë o ju të zotët e mendjes.');
INSERT INTO "ahmeti" VALUES(59,3,'59:3','Sikur All-llahu të mos u kishte caktuar atyre shpërnguljen, ata do t’i ndëshkonte në këtë jetë, kurse në jetën tjetër ata e kanë dënimin e zjarrit.');
INSERT INTO "ahmeti" VALUES(59,4,'59:4','Këtë për shkak se e kundërshtuan All-llahun dhe të dërguarin e Tij. Pra, kush e kundërshton All-llahun, s’ka dyshim se All-llahu është ndëshkimfortë.');
INSERT INTO "ahmeti" VALUES(59,5,'59:5','Ju nuk këputët asnjë nga hurmat e buta (fidanët) ose atë e keni lënë të ngriur në rrënjët e tye, vetëm se me lejen e All-llahut e për t’i shtypur kundërshtarët.');
INSERT INTO "ahmeti" VALUES(59,6,'59:6','E atë (Pasuri) që prej tyre All-llahu ia ktheu të dërguarit të vet, ju për të nuk i keni ngarë as kuajt, as devetë, por All-llahu i bën mbizotëruesit të dërguarit e vet kundër kujt të dojë, All-llahu ka fuqi mbi çdo send.');
INSERT INTO "ahmeti" VALUES(59,7,'59:7','Atë që All-llahu nga banorët e vendeve (jobesimtare) ia dha pa luftë të dërguarit të vet, ajo i takon All-llahut, të dërguarit, të afërmve, jetimëve, të varfërve, kurbetçinjve. (Kështu veprohet) Që ajo të mos ndahet ndërrmjet pasanikëve tuaj. Çka t’ju jep i Pejgamberi, atë merrmie e çka t’ju ndalojë, përmbanju dhe kinie frikë All-llahun, se All-llahu është ndëshkues i ashpër.');
INSERT INTO "ahmeti" VALUES(59,8,'59:8','(Ajo pronë) U takon muhaxhirëve të varfër, të cilët u dëbuan prej shtëpive të tyre dhe prej pasurisë së tyre, duke kërkuar mirasinë dhe kënaqëainë e All-llahut, dhe që ndihmojnë All-llahun dhe të dërguarin e Tij, të tillët janë ata të sinqertit.');
INSERT INTO "ahmeti" VALUES(59,9,'59:9','Edhe ata që përgatitën vendin (Medinën) dhe besimin para tyre, i duan ata që shpërnguleshin te ata dhe nuk ndiejnë në gjoksat e tyre ndonjë nevojë (për zili a tjetër) nga ajo që u jepej atyre (muhaxhirëve), madje edhe sikur të kishin vetë nevojë për të, ata u jepnin përparësi atyre para vetvetes. Kush është i ruajtur prej lakmisë së vet, të tillët janë të shpëtuar.');
INSERT INTO "ahmeti" VALUES(59,10,'59:10','Edhe ata që kanë ardhur pas tyre e thonë: “Zoti ynë, falna ne dhe vëllezërit tanë që para nesh u pajisën me besim dhe mos lejo në zemrat farë urrejtjeje ndaj atyre që besuan. Zoti ynë Ti je i butë, mëshirues!”');
INSERT INTO "ahmeti" VALUES(59,11,'59:11','A nuk e ke ditur se ata që u bënë hipokritë, u thoshin vëllezërve të vet nga ithtarët e librit që nuk kishin besuar: nëse ju dëboheni, edhe ne do të dalim me ju, për çështjen tuaj, ne kurrë nuk do t’i bindemi askujt (t’ju luftojmë ose t’ju nënshtrojmë), e nëse luftoheni prej dikujt, ne gjithqysh do t; u ndihmojmë. All-llahu dëshmon se ata janë rrenacakë!');
INSERT INTO "ahmeti" VALUES(59,12,'59:12','Sikur të dëbohen ata (ithtarët e librit), nuk do të dilnin me ta edhe sikur të luftoheshin, nuk do t’u ndihmojnë atyre, po edhe sikur t’u ndihmonin atyre, ata do të iknin prapa, e ata do të mbesin pa ndihmën e tyre.');
INSERT INTO "ahmeti" VALUES(59,13,'59:13','Ju (besimtarët) në zemrat e tyre (ta munafikëve) jeni frikë më e fortë se All-llahu (frika nga All-llahu). Kjo për shkak se ata janë popull që nuk kuptojnë.');
INSERT INTO "ahmeti" VALUES(59,14,'59:14','Ata të gjithë së bashku nuk kanë guxim t’ju luftojnë, vetëm kur gjenden në ndonjë vend të fortifikuar ose kur janë pas ndonjë muri. Armiqësia ndërmjet tyre është e ashpër. Mund të mendohet për ta se janë të bashkuar, e në reallitet zemrat e tyre janë të përçara, sepse janë njerëz që nuk logjikojnë.');
INSERT INTO "ahmeti" VALUES(59,15,'59:15','Si shembull i atyre jo fort moti që përjetuan të keqen e punës së vet, edhe ata kanë një dënim të rëndë.');
INSERT INTO "ahmeti" VALUES(59,16,'59:16','Ata (hipokritët) janë shembull si djalli, kur i thotë njeriut: “Refuzo besimin!”, e kur ai të ketë refuzuar, ai (djalli) tërhiqet prej teje; unë i frikohem All-llahut, Zotit të gjithësisë!”');
INSERT INTO "ahmeti" VALUES(59,17,'59:17','Dhe ashtu, ata të dy do të përfundojnë në zjarr përgjithmonë. Ai është ndëshkim i zullumqarëve.');
INSERT INTO "ahmeti" VALUES(59,18,'59:18','O ju që besuat, frikësojuni All-llahut dhe le të shikojë çdonjëri se çka ka bërë për nesër. Dhe frikësojuni All-llahut! S’ka dyshim se All-llahu është i Njohtuar (Habir) për atë që punoni!');
INSERT INTO "ahmeti" VALUES(59,19,'59:19','E mos u bëni si ata që harruan All-llahun, pra (All-llahu bëri që ata) harruan vetveten! Të tillët janë të Fasikët.');
INSERT INTO "ahmeti" VALUES(59,20,'59:20','Nuk janë të barabartë banorët e Xhennetit dhe banorët e Xhehennemit. Banorët e Xhennetit janë të suksesshmit (janë ata që ia kanë arritur qëllimit).');
INSERT INTO "ahmeti" VALUES(59,21,'59:21','Sikur Ne ta zbritnim këtë Kur’an mbi ndonjë kodër, do të shihje atë të strukur e të çarë prej frikës nga All-llahu. Këta janë shembuj që ua sjellim njerëzve, që ata të mendojnë.');
INSERT INTO "ahmeti" VALUES(59,22,'59:22','Ai është All-llahu, nuk ka zot tjetër pos Tij. Dijetari i të fshehtës dhe të dukshmes, Ai është Mëshiruesi, Mëshirëbërësi!');
INSERT INTO "ahmeti" VALUES(59,23,'59:23','Ai është All-llahu, pos të Cilit nuk ka zot tjetër, Sunduesi, i Shenjti, i Pastërti (prej të metave që i mvishen), Siguruesi, Mbikëqyrësi, i Plotfuqishmi, Mbizotëruesi, i Madhërishmi. I lartësuar është All-llahu nga ajo që ia shoqërojnë!');
INSERT INTO "ahmeti" VALUES(59,24,'59:24','Ai është All-llahu, Krijuesi, Shpikësi, Formësuesi. Të tij janë emrat më të bukur. Atë e lartëson çka ka në qiej e në tokë. Dhe Ai është i Fuqishmi, i Urti!');
INSERT INTO "ahmeti" VALUES(60,1,'60:1','O ju që besuat, nëse keni dalë (prej vendlindjes) për hirë të luftës për rrugën Time, duke kërkuar kënaqësinë Time ndaj jush, mos e zini mik armikun Tim dhe armikun tuaj, duke shprehur ndaj tyre dashuri, kur dihet se ata mohuan të vërtetën që u erdhi juve. Ata e dëbojnë të dërguarin dhe ju, sespe i besoni All-llahut, Zotit tuaj, e ju fshehurazi u ofroni miqësi, po Unë më së miri e di atë që e keni fshehur dhe atë që e keni publikuar. Ai që punon ashtu nga pala juaj, ia e ka humbur rrugën e drejtë.');
INSERT INTO "ahmeti" VALUES(60,2,'60:2','Nëse ata ia dalin t’ju mindin, ata do të jenë armiq tuaj, do të zgjasin duart e veta dhe gjuhët e veta kundër jush, duke ju përbuzur, sepse ata dëshironin të mos besonit.');
INSERT INTO "ahmeti" VALUES(60,3,'60:3','Në ditën e kijametit All-llahu gjykon mes jush, e nuk do t’ju bëjnë dobi as të aërmit tuaj e as fëmijët tuaj. All-llahu sheh atë që punoni.');
INSERT INTO "ahmeti" VALUES(60,4,'60:4','Ju e keni shembullin më të mirë te Ibrahimi dhe ata që ishin me të, kur i thanë popullit të vet: “Ne tërhiqemi prej jush dhe prej asaj që adhuroni, pos All-llahut, nuk besojnë tuajën, randaj ndërmjet nesh e jush është e hapët armiqësia e urrejtja derisa ta besoni vetëm All-llahun Një!” (Nuk e kemi shembull) Me përjashtim të fjalës së Ibrahimit thënë babait të vet: “Unë do të kërkoj falje për ty, po unë nuk kam në dorë asgjë për ty te All-llahu!” Zoti ynë, vetëm Ty të jemi mbështetur, vetëm nga Ti jemi kthyer dhe vetëm te Ti është e ardhmja!');
INSERT INTO "ahmeti" VALUES(60,5,'60:5','Zoti ynë, mos na bën sprovë në duar të atyre që nuk besuan dhe falna, Zoti ynë, Ti je ngadhënjyesi, i urti.');
INSERT INTO "ahmeti" VALUES(60,6,'60:6','Vërtet, për atë që ka shpresë në All-llahun dhe në ditën e gjykimit, ju keni mostër të mirë (te Ibrahimi dhe besimtarët), e kush i bie prapa, le ta dijë se All-llahu është Ai i panevojshmi, i vetvetiu i lavdëruari.');
INSERT INTO "ahmeti" VALUES(60,7,'60:7','Pritet që All-llahu të verë miqësi ndërmjet jush dhe atyre që kishit armiqësi, All-llahu është i fuqishëm, All-llahu falë mëkatet, është mëshirues.');
INSERT INTO "ahmeti" VALUES(60,8,'60:8','All-llahu nuk ju ndalon të bëni mirë dhe të mbani drejtësi me ata që nuk ju luftuan për shkak të fesë, e as nuk ju dëbuan prej shtëpive tuaja; All-llahu i do ata që mbajnë drejtësinë.');
INSERT INTO "ahmeti" VALUES(60,9,'60:9','All-llahu ju ndalon t’u afroheni vetëm atyre që ju luftuan për shkak të fesë, që ju nxorën prej shtëpive tuaja dhe që ndihmuan dëbimin tuaj; ju ndalon t miqësoheni me ta. Kush miqësohet me ta, të tillët janë dëmtues të vetvetës.');
INSERT INTO "ahmeti" VALUES(60,10,'60:10','O ju që besuat, kur t’ju vijnë besimtaret e shpërngulura (prej Mekes), provoni besnikërinë e tyre, ndonëse All-llahu di më së miri për besimin e tyre; nëse vërtetoni se ato janë besimtare, atëherë, mos i ktheni ato te jobesimtarët, pse as ato nuk janë të lejuara për ta e as ata nuk janë të lejuar për to, e ju jepnu atyre (jobesimtarëve) atë që kanë shpenzuar për to (në emër të kororëzimit). Ju nuk keni pengesë të martoheni me to, pasi t’ua jepni vlerën e kurorës. Mos i mbani nën kurorë, idhujtaret por kërkoni (prej idhujtarëve mekas) atë që keni shpenzuar për to, dhe ata le të kërkojnë (prej jush besimtarë) atë që kanë shpenzuar. Këto janë dispozita të All-llahut me të cilat Ai gjykon ndërmjet jush; All-llahu është më i dijshmi, më i informuari.');
INSERT INTO "ahmeti" VALUES(60,11,'60:11','Në qoftë se u humbet diçka (nga shpenzimi) i ndonjë gruaje tuaj që ikën te idhujtarët, e juve u vjen rasti t’i luftoni dhe të merrni pre të luftës, atëherë atyre që u kanë shkuar gratë, jepnu aq sa kanë shpenzuar; e, kinie frikë All-llahun, të cilin ju e besoni!');
INSERT INTO "ahmeti" VALUES(60,12,'60:12','O Pejgamber, kur të vijnë besimtaret që të japin besën se: nuk do t’i shoqërojnë asnjë send All-llahut; se nuk do të vjedhin; se nuk do të bëjnë kurvëri; se nuk do t’i mbysin fëmijët e tyre; se nuk do të gënjejnë me ndonjë shpifje për (fëmijën e huaj që kanë marrë) se është i tyre; dhe se nuk do të të kundërshtojnë në atë që i urdhëron, atëherë, prano zotimin e tyre dhe lute All-llahun t’i falë ato, se All-llahu është mëkatfalës dhe mëshirues.');
INSERT INTO "ahmeti" VALUES(60,13,'60:13','O ju që keni besuar, mos u miqësoni me një popull që All-llahu ka shprehur hidhërim kundër tij, popull që ka humbur shpresën ndaj botës së ardhshme, ashtu si nuk kanë shpresë jobesimtarët prej të vdekurve (që do të ngjallen) në këtë botë, ose në ringjallje (në botën tjetër).');
INSERT INTO "ahmeti" VALUES(61,1,'61:1','All-llahun e madhëroi çka ka në qiej dhe çka ka në tokë, e Ai është mbizotëruesi, i urti.');
INSERT INTO "ahmeti" VALUES(61,2,'61:2','O ju që besuat, pse po e thoni atë që nuk e punoni?');
INSERT INTO "ahmeti" VALUES(61,3,'61:3','te All-llahu është shumë e urrejtur ta thoni atë që nuk e punoni!');
INSERT INTO "ahmeti" VALUES(61,4,'61:4','All-llahu i do ata që luftojnë në rrugën e Tij të rreshtuar si të jetë ndërtesë e fortifikuar.');
INSERT INTO "ahmeti" VALUES(61,5,'61:5','Dhe, (rikujto) kur Musai popullit të vet i tha: “O populli imi, pse më shqetësoni kur e dini se me të vërtetë unë jam i dërguar i All-llahut te ju!” E kur ata u shmangën (nga e vërteta), All-llahui largoi zemrat e tyre, pse All-llahu nuk vë në rrugë të drejtë popullin e prishur.');
INSERT INTO "ahmeti" VALUES(61,6,'61:6','Dhe, kur Isai, biri i Merjemes tha: “O beni israilë, unë jam i dërguar i All-llahut te ju, jam vërtetues i Tevratit që ishte para meje dhe jam përgëzues për një të dërguar që do të vijë pas meje, emri i të cilit është Ahmed!” E kur ai u erdhi atyre me argumente të qarta, ata thanë: “Kjo është magji e hapët”.');
INSERT INTO "ahmeti" VALUES(61,7,'61:7','E kush është më mizor se ai që për All-llahun shpif gënjeshtër për fenë islame; po All-llahu nuk i ofron udhëzimin për në rrugën e drejtë popullin zullumqarë.');
INSERT INTO "ahmeti" VALUES(61,8,'61:8','Ata duan ta shuajnë dritën e All-llahut me gojët e tyre, po All-llahu e plotëson (e përhap) dritën e vet, edhe pse e urrejnë jobesimtarët.');
INSERT INTO "ahmeti" VALUES(61,9,'61:9','Ai (All-llahu) është që e dërgoi të dërguarin e vet me udhëzim të qartë e fe të vërtetë për ta bërë mbizotërues mbi të gjitha fetë, edhe pse idhujtarët e urrejnë.');
INSERT INTO "ahmeti" VALUES(61,10,'61:10','O ju që keni besuar, a t’ju tregoj për jë tregti të bujshme që ju shpëton prej jë dënimi të dhembshë:');
INSERT INTO "ahmeti" VALUES(61,11,'61:11','T’i besoni All-llahut dhe të dërguarit të Tij, të luftoni në rrugën e All-llahut me pasurinë tuaj dhe veten tuaj, e kjo është shumë më e dobishme për ju, nëse jeni që e dini.');
INSERT INTO "ahmeti" VALUES(61,12,'61:12','Ai (All-llahu) iu falë mëkatet tuaja, ju shpie në Xhennete nëpër të cilat rrjedhin dhe në pallate të bukura në Xhennetet e amshueshme, e këto janë ai suksesi më i madh.');
INSERT INTO "ahmeti" VALUES(61,13,'61:13','Edhe të tjera që ju i dëshironi: ndihmë prej All-llahut dhe fitore e afërt, e pra, përgëzoji besimtarët!');
INSERT INTO "ahmeti" VALUES(61,14,'61:14','O ju që besuat, bëhuni ndihmëtarë të All-llahut, si Isai biri i Merjemes havarijunëve (besimtarëve të vet të sinqertë) u pat thënë: “Kush është ndihmëtarë imi për në rrugën e All-llahut?” Havarijunët i thanë: “ne jemi ndihmëtarë të rrugës së All-llahut!” E një grup prej beni israilëve besoi, e një grup refuzoi, e Ne, ata që besuan i përkrahëm kundër armikut të tyre dhe ashtu u bënë ngadhënjyes.');
INSERT INTO "ahmeti" VALUES(62,1,'62:1','Ç''farë ka në qiej dhe ç''ka në tokë bën tesbih All-llahun, Sunduesin, të Pastërin nga çdo e metë, të Fuqishmin e të Urtin.');
INSERT INTO "ahmeti" VALUES(62,2,'62:2','Ai është që arabve të pashkolluar u dërgoi Pejgamberin nga mesi tityre që t’ju lexojë ajetet e Tij, t’i pastrojë ata, t’ua mësojë librin dhe sheriatin, edhe pse më parë ata ishin në një humbje të dukshme.');
INSERT INTO "ahmeti" VALUES(62,3,'62:3','(U dërgoi) edhe të tjerëve që nuk i arritën ata, Ai është ngadhnjyesi');
INSERT INTO "ahmeti" VALUES(62,4,'62:4','Kjo është mirësi e All-llahut, që ia jep atij që do, e All-llahu është Zoti i dhuntisë së përsosur.');
INSERT INTO "ahmeti" VALUES(62,5,'62:5','Shembulli i atyre, që janë obliguar me Tevrat, dhe nuk e zbatojnë atë, është shembulli indonjë gomari që bart libra. Shembulli i keq, është shembull i popullit që përgënjeshtroi ajetet e All-llahut, e All-llahu nuk udhëzon në rrugë të drejtë popullin jobesimtarë.');
INSERT INTO "ahmeti" VALUES(62,6,'62:6','Thuaj: “O ju që i përmbaheni jehudizmit, nëse e pretendoni se jeni më të afërmit e All-llahut e jo njerëzit e tjerë, atëherë kërkonie vdekjen, po qe se jeni të sinqertë (në atë që thoni)”.');
INSERT INTO "ahmeti" VALUES(62,7,'62:7','Po, ata nuk dëshirojnë kurrë atë, për shkak të asaj që e punuan me duart e veta, e All-llahu e di më së miri se kush janë mizorët.');
INSERT INTO "ahmeti" VALUES(62,8,'62:8','Thuaj: “s’ka dyshim se vdekja prejsë cilës po ikni, ka për t’ju zën, e mande do të silleni te Ai që e di të padukshmën dhe të dukshëm, dhe atëherë Ai do t’ju njoftojë me atë që keni punuar”.');
INSERT INTO "ahmeti" VALUES(62,9,'62:9','O ju që besuat, kur bëhet thirrja për namaz, ditën e zhumasë, ecni shpejt për aty ku përmendet All-llahu (dëgjojeni hutben, falnie namazin), e lini shitblerjen, kjo është më e dobishme për ju nëse jeni që e dini.');
INSERT INTO "ahmeti" VALUES(62,10,'62:10','E, kur të kryhet namazi, atëherë shërndanu në tokë dhe kërkoni begatitë e All-llahut, por edhe përmendni shpeshherë All-llahun, ashtu që të gjeni shpëtim.');
INSERT INTO "ahmeti" VALUES(62,11,'62:11','Po, kur ata shohin ndonjë tregti, ose ndonjë aheng mësyhen atje, kurse ty të lënë në këmbë. Thuaju: “Ajo që është te All-llahu është shumë më e mira se dëfrimi dhe tregëtia, e All-llahu është furnizuesi më i mirë!”');
INSERT INTO "ahmeti" VALUES(63,1,'63:1','Kur të erdhën ty hipokritët, të thanë: “Ne dëshmojmë se vërtet ti je i dërguar i All-llahut!” E, All-llahu e di se ti je i dërguar i Tij, por All-llahu dëshmon se hipokritët janë rrenacakë (kur thonë ashtu).');
INSERT INTO "ahmeti" VALUES(63,2,'63:2','Ata betimin e tyre e bëjnë për t’u mbrojtur, andaj ata penguan nga rruga e All-llahut. Veprimi që ata bënë është shumë keq.');
INSERT INTO "ahmeti" VALUES(63,3,'63:3','Ngase ata besuan (me gojë) e pastaj mohuan, andaj zemrat e tyre u mbyllën dhe ata nuk kuptojnë.');
INSERT INTO "ahmeti" VALUES(63,4,'63:4','E kur t’i shohish ata, trupat e tyre të mahnisin, e kur të flasin, fjalës së tyre i vë veshin. Po ata janë si trungujt e zgavruar e të mbështetur, e çdo zë e mendojnë se është kundër tyre. Ata janë armiq, pra ruaju prej tyre! All-llahu i vraftë, si shmangen nga e vërteta!');
INSERT INTO "ahmeti" VALUES(63,5,'63:5','E kur u thuhet: “Ejani te i dërguari i All-llahut që ai të kërkojë prej Tij falje për ju”, ata tundin kokat e tyre dhe i sheh se si ta kthejnë shpinën duke e mbajtur veten të madh.');
INSERT INTO "ahmeti" VALUES(63,6,'63:6','Sa u përket atyre, është krejt njësoj: si kërkove për ta falje, si nuk kërkove për ta falje (nga Zoti), sepse All-llahu kurrsesi nuk do t’i falë dhe është e sigurt se All-llahu nuk ia ofron udhëzimin popullit arrogant.');
INSERT INTO "ahmeti" VALUES(63,7,'63:7','Ata janë që u thonë (vendasve të Medinës): “Mos u jepni atyre që janë me të dërguarin e All-llahut (muhaxhirëve), ashtu që të shpërndahen!. Po, te All-llahu janë pasuritë (depot) e iqejve e të tokës, por hipokritët nuk janë duke kuptuar”.');
INSERT INTO "ahmeti" VALUES(63,8,'63:8','Ata thonë: “Nëse do të kthehemi (prej gjuhës me beni Mustalikët) në Medine, ai më i forti pa tjetër prej aty ka për ta përzënë atë më të dobëtin!” (ashtu thanë) Ndërsa e tërë krenaria i takon All-llahut, të dërguarit të Tij dhe besimtarëve, por hipokritët këtë nuk e dinë.');
INSERT INTO "ahmeti" VALUES(63,9,'63:9','O ju që besuat, as pasuria juaj e as fëmijët tuaj të mos u shmangin prej adhurimit të All-llahut, e kush bën ashtu të tillët janë mu ata të humburit.');
INSERT INTO "ahmeti" VALUES(63,10,'63:10','Dhe jepni nga ajo që Ne u kemi dhënë juve, para se ndonjërit prej jush t’i vijë vdekja, e atëherë të thotë: “O Zoti im, përse nuk më shtyve edhe pak afatin (e vdekjes), që të jepja lëmoshë e të bëhesha prej të mira!”');
INSERT INTO "ahmeti" VALUES(63,11,'63:11','Po, All-llahu kurrsesi askë nuk e shtyn për më vonë, kur atij t’i vijë afati i vet. All-llahu hollësisht është i njohur me atë që ju punoni.');
INSERT INTO "ahmeti" VALUES(64,1,'64:1','Vetëm All-llahut i shprehin madhërinë e lartësimin çka ka në qiej dhe çka në tokë, dhe se vetëm e Tij është i tërë pushteti dhe vetëm Atij i takon falënderimi, e Ai është i gjithfuqishëm për çdo send.');
INSERT INTO "ahmeti" VALUES(64,2,'64:2','Ai është që u krijoi juve, e nga ju ka mohues dhe ka besimtarë. All-llahu e sheh shumë mirë atë që e punoni.');
INSERT INTO "ahmeti" VALUES(64,3,'64:3','Ai krijoi qiejt dhe tokën me urtësi të përsosur dhe Ai juve u dha formën, e formën tuaj e bëri të bukur dhe vetëm te Ai është e ardhmja.');
INSERT INTO "ahmeti" VALUES(64,4,'64:4','Ai e di ç’ka në qiej e në tokë, e di atë që e fshihni dhe atë që e shfaqni haptazi, sepse All-llahu di edhe atë që mbajnë në zemrat.');
INSERT INTO "ahmeti" VALUES(64,5,'64:5','A nuk ju erdhi lajmi i atyre që ishin më parë e nuk besuan, ndaj edhe përjetuan barrën e rëndë të punës së vet dhe ata kanë një dënim të idhët.');
INSERT INTO "ahmeti" VALUES(64,6,'64:6','Atë (dënim e marrin) për shkak se atyre u vinin të dërguarit e vet me argumente të qarta, e ata thoshin: “A njeriu i thjeshtë do të na udhëzojë neve?” Dhe ashtu nuk besuan dhe u zmbrapsën, po All-llahu nuk ka nevojë për ta; All-llahu është begatshëm, e i falënderuar nga vetvetiu.');
INSERT INTO "ahmeti" VALUES(64,7,'64:7','Ata që nuk besuan, menduan se kurrsesi nuk do të ringjallen. Thuaj: “Po, pasha Zotin tim, pa tjetër do të ringjalleni dhe do të njiheni me atë që keni punuar, e ajo për All-llahun është lehtë!”');
INSERT INTO "ahmeti" VALUES(64,8,'64:8','Atëherë pra, pranonie All-llahun, të dërguarin e Tij dhe dritën që e zbritëm Ne. All-llahu është i njohur për atë që veproni.');
INSERT INTO "ahmeti" VALUES(64,9,'64:9','Ditën e kijametit, kur Ai u tubon juve, ajo është ditë e mashtrimit (ditën e kijametit duket se kush është mashtruar). kush beson All-llahun dhe bën vepra të mira. Ai ia shlyen të këqijat e tij dhe atë e fut në Xhennete, nëpër të cilat rrjedhin lumenj; aty do të jenë përgjthmonë, e ai është shpëtimi i madh.');
INSERT INTO "ahmeti" VALUES(64,10,'64:10','E ata që nuk besuan dhe përgënjeshtruan argumentet Tona, të tillët janë banorët e zjarrit; aty do të jenë përgjithmonë, e ai është përfundim i keq.');
INSERT INTO "ahmeti" VALUES(64,11,'64:11','çfarëdo e keqe që ndodh (godet), nuk mund të jetë ndryshe, vetëm sipas caktimit të All-llahut, e kush i beson All-llahut, Ai ia udhëzon zemrën e tij; All-llahu është i gjithdijshëm për çdo send.');
INSERT INTO "ahmeti" VALUES(64,12,'64:12','Pra, respektonie All-llahun dhe respektonie të dërguarin, po nëse nuk e respektoni, atëherë detyrë e të dërguarit tonë është vetëm kumtimi i qartë.');
INSERT INTO "ahmeti" VALUES(64,13,'64:13','All-llahu është Ai që pos Tij nuk ka zot tjetër, prandaj vetëm All-llahut le t’i mbështeten besimtarët.');
INSERT INTO "ahmeti" VALUES(64,14,'64:14','O ju që besuat, vërtet, disa nga bashkëshortet tuaja dhe nga fëmojët tuaj janë armiq tuaj, andaj kini kujdes ndaj tyre, e nëse nuk merrni masa, nuk ua vini veshin dhe i falni, ta dini se edhe All-llahu është mëkatfalës, mëshirues.');
INSERT INTO "ahmeti" VALUES(64,15,'64:15','S’ka dyshim se pasuria juaj dhe fëmijët tuaj janë sprovë, ndërsa ajo që është te All-llahu, është shpërblim i madh.');
INSERT INTO "ahmeti" VALUES(64,16,'64:16','Andaj, sa të keni mundësi ruanu prej dënimit të All-llahut, dëgjoni, respektoni dhe jepni për të mirën tuaj, e kush i ruhet lakmisë së vetvetes, të tillët janë të shpëtuarit.');
INSERT INTO "ahmeti" VALUES(64,17,'64:17','Nëse All-llahut i huazoni diçka të mirë, Ai juve ua shumëfishon atë dhe ju falë; All-llahu është shumë mirënjohës, i butë.');
INSERT INTO "ahmeti" VALUES(64,18,'64:18','Është i gjithdijshmi i të fshehtës e i të dukshmes, është ngadhënjyesi, i urti.');
INSERT INTO "ahmeti" VALUES(65,1,'65:1','O ti pejgamber, kur t’i lëshoni gratë, lëshoni ato në vigjilje të fillimit të pritjes së tyre (në kohën e pastërtisë së tyre) dhe kohën e pritjes mbanie mend mirë e kini frikë All-llahut, Zotit tuaj, dhe mos i nxirrni ato prej shtëpive të tyre, e edhe ato të mos dalin ndryshe vetëm nëse bëjnë ndonjë vepër të keqe të vërtetuar. Këto janë dispozitat e All-llahut, e kush del jashtë dispozitave të All-llahut, ai e ka dëmtuar vetveten. Ti nuk e di, All-llahu pas asaj mund të të jap diçka rishtazi.');
INSERT INTO "ahmeti" VALUES(65,2,'65:2','E kur t’i afrohen ato mbarimit ta afatit të tyre, atëherë ose mbani (i ktheni në jetën bashkëshortore) si duhet, ose ndanu prej tyre si është e udhës, e këtë do ta dëshmojnë dy dëshmitarë të drejtë nga mesi juaj dhe dëshmitë zbatojeni për hirë të All-llahut. Kështu këshillohet ai që i beson All-llahut dhe ditës së fundit, e kush iu përmbahet dispozitave të All-llahut, atij Ai i hap rrugë,');
INSERT INTO "ahmeti" VALUES(65,3,'65:3','dhe e furnizon atë prej nga nuk kujton fare. Kështu i mbështetet All-llahut, Ai i mjafton atij, All-llahu realizon dëshirën e vet dhe All-llahu çdo gjëje ia ka caktuar kohën (afatin).');
INSERT INTO "ahmeti" VALUES(65,4,'65:4','E ato nga gratë tuaja të cilave u është ndërprerë cikli mujor (menstruacioni) nëse nuk e keni ditur, koha e pritjes së tyre është tre muaj, e ashtu edhe ato që ende nuk kanë pasur menstruacion. Ndërkaq, për shtatzënat afati i pritjes së tyre është derisa të lindin. E kush i frikësohet All-llahut, Ai atij ia lehtëson punën.');
INSERT INTO "ahmeti" VALUES(65,5,'65:5','Ky është vendim i All-llahut që ua zbriti juve, e kush e ka frikë All-llahun, Ai atij ia shlyen të këqijat dhe ia shton shpërblimin.');
INSERT INTO "ahmeti" VALUES(65,6,'65:6','Ato (gratë e lëshuara), sipas mundësisë suaj i vendosni në ndonjë vend ku banoni ju, e mos i ngushtoni ato për t’i detyruar (të dalin). Në qoftë se ato janë shtatzëna, atëherë furnizoni ato derisa ta heqin barrën e tyre, e nëse ato u japin gji fëmijëve tuaj, atëherë jepnu atyre shpërblimin që u takon dhe kini mirëkuptim në mes jush ashtu si duhet, e në qoftë se hasni në vështirësi ndër vete, atëherë le të kërkojnë për të gjidhënëse tjetër.');
INSERT INTO "ahmeti" VALUES(65,7,'65:7','Ai që është i pasur, le të shpenzojë sipas mundësisë së vet, e ai që është ngusht nga pasuria, le të japë nga ajo që i ka dhënë All-llahu, e All-llahu nuk ngarkon askë, vetëm aq sa i ka dhënë; mirëpo pas vështirësisë All-llahu sjell çlirim (begati).');
INSERT INTO "ahmeti" VALUES(65,8,'65:8','Sa shumë banorë të fshatrave, që ishin kryeneçë ndaj urdhërit të Zotit të vet dhe ndaj të dërguarëve të Tij, Ne i kemi shpërblyer me një llogari të ashpër, i kemi ndëshkuar me një dënim të papërshkrueshëm.');
INSERT INTO "ahmeti" VALUES(65,9,'65:9','She ata e përjetuan barrën e rëndë të punës së vet, kështu që fundi i çështjes së tyre ishte dështimi.');
INSERT INTO "ahmeti" VALUES(65,10,'65:10','All-llahu u ka përgatitur dënim të rëndë, atyre pra, kinie frikë All-llahun o ju të zotët e mendjes. E ju që keni besuar, All-llahu u zbriti juve shpalljen (Kur’anin).');
INSERT INTO "ahmeti" VALUES(65,11,'65:11','Të dërguarit që ua lexon ajetet e qarta të All-llahut për t’i nxjerrë ata që besuan dhe bënë cepra të mira prej errësirave në dritë. Kush i beson All-llahut dhe bën vepra të mira, Ai e shpie atë në Xhennete nëpër të cilat rrjedhin lumenj; aty do të jenë përgjthmonë e kurrë pakëputur. All-llahu i ka dhënë atij furnizim të mirë...');
INSERT INTO "ahmeti" VALUES(65,12,'65:12','All-llahu është Ai që krijoi shtatë palë qiej dhe prej tokës aq sa ata; në to zbatohet vendimi i Tij për ta ditur ju se All-llahu ka fuqi për çdo send dhe që me të vërtetë All-llahu ka përfshirë çdo send me dijen e vet.');
INSERT INTO "ahmeti" VALUES(66,1,'66:1','O ti pejgamber (o ti, që është sjellë kumtesa nga qielli), pse ndalon vetes atë që All-llahu ta lejoni? A kërkon me të pajtim e grave tuaja? All-llahu është mëkatfalës, mëshirues.');
INSERT INTO "ahmeti" VALUES(66,2,'66:2','All-llahu u ka përcaktuar juve zgjidhjen e betimeve tuaj; All-llahu është kujdestari juaj; Ai është i gjithdjshmi, i urti.');
INSERT INTO "ahmeti" VALUES(66,3,'66:3','(Përkujto) kur Pejgamberi ndonjerës prej grave të veta i kumtoi fshehurazi një lajm, e kur ajo tregoi atë (lajm), e All-llahu (Pejgamberit) i zbuloi atij për të (kallzimin e lajmit nga ajo grua), ai ia bëri me (asaj) me dije një pjesë të trgimit, kurse për pjesën tjetër heshti. E kur ai (pejgamberi) e njohtoi atë (gruan) me atë (që kishte treguar), ajo tha: “E kush të tregoi ty këtë?” Ai tha: “Më tregoi i gjithdijshmi, njohësi i të fshehtave!”');
INSERT INTO "ahmeti" VALUES(66,4,'66:4','Nëse ju të dyja pendoheni te All-llahu (është më mirë për ju), sepse se zemrat tuaja tanimë kanë rrëshqitur, e nëse bashkveproni kundër tij, ta dini se All-llahu është Ai mbrojtës i tij si dhe Xhibrili dhe besimtarët e ndershëm; e kur është kështu, edhe engjëjt i dalin në ndihmë.');
INSERT INTO "ahmeti" VALUES(66,5,'66:5','Nëse ai u ka lëshuar juve, është detyrë e Zotit të tij t’i bëjë zëvendsim me gra edhe më të mira se ju; myslimane, besmtarë, respektuese, penduese, adhuruese, emigruese që kanë qenë të martuara ose virgjiresha.');
INSERT INTO "ahmeti" VALUES(66,6,'66:6','O ju që besuat, ruane veten dhe familjen tuaj prej zjarrit, lënda djegëse e të cilit janë njerëzit dhe gurët. Atë (zjarrin) e mbikëqyrin engjëjt të rreptë e të ashpër që nuk e kndërshtojnë All-llahun për asgjë që Ai i urdhëron dhe punojnë atë që janë të urdhëruara.');
INSERT INTO "ahmeti" VALUES(66,7,'66:7','O ju që mohuat, sot mos u arsyetoni; ju shpërbleni veten për atë që keni vepruar.');
INSERT INTO "ahmeti" VALUES(66,8,'66:8','O ju që keni besuar, pendohuni te All-llahu me një pendim të sinqertë në mënyrë që Zoti juaj t’i largojë prej jush të këqijat, t’u shpie në Xhennete, nën të cilat rrjedhin lumenj ditën kur All-llahu nuk e turpëron Pejgamberin e së bashku me të as atta që kanë besuar. Drita e tyre ndriçon para tyre dhe në të djathtë të të tyre, e ata thonë: “Zoti ynë, vazhdona dritën tonë, falna neve. Vërtet, Ti je i plotfuqishëm për çdo send”.');
INSERT INTO "ahmeti" VALUES(66,9,'66:9','O ti pejgamber, luftonju jobesimtarët e hipokritët dhe qëndro ashëpër kundër tyre; vendi i tyre është Xhehennemi që është një vend i shëmtuar!');
INSERT INTO "ahmeti" VALUES(66,10,'66:10','Atyre që mohuan, All-llahu u sjell një shembull gruan e Nuhut dhe gruan e Lutit. Ato të dyja ishin në kurorë të dy robërve të mirë nga robërit tanë, por ato të dyja i tradhtuan (në fe) ata të dy dhe këta të dy nuk mund t’i mbrojnë ato fare te All-llahu, e atyre dyjave u thuhet: “Hyni në zjarr së bashku me ata që hyjnë!”');
INSERT INTO "ahmeti" VALUES(66,11,'66:11','E atyre që besuan, All-llahun u solli shembull gruan e faraonit kur ajo tha: “Zoti im, më bën një vend pranë mëshirës sate në Xhennet dhe më shpëto prej faraonit e brutalitetit të tij dhe më shpëto prej popullit mizor!”');
INSERT INTO "ahmeti" VALUES(66,12,'66:12','Edhe Merjemen të bijën e Imranit, që e ruajti nderin e vet, e Ne prej anës sonë i frymëzuam një shpirt e ajo i besoi fjalët e Zotit të saj dhe librat e Tij dhe ishte e devotshme.');
INSERT INTO "ahmeti" VALUES(67,1,'67:1','I madhëruar është Ai, që në dorën e fuqisë së Tij është i tërë sundimi dhe Ai ka fuqi mbi çdo send.');
INSERT INTO "ahmeti" VALUES(67,2,'67:2','Ai është që krijoi vdekjen dhe jetën, për t’ju sprovuar se cili prej jush është më vepërmirë. Ai është ngadhnjyesi, mëkatëfalësi.');
INSERT INTO "ahmeti" VALUES(67,3,'67:3','Ai është që krijoi shtatë qiej palë mbi palë. Në krijimin e Mëshiruesit nuk mund të shohësh ndonjë kontrast, andaj, drejto shikimin se a sheh ndonjë çarje?');
INSERT INTO "ahmeti" VALUES(67,4,'67:4','Mandej, herë pas here drejto shikimin, e shikimi do të kthehet të ti i përulur dhe i molisur.');
INSERT INTO "ahmeti" VALUES(67,5,'67:5','Ne, qiellin më të afërt e kemi zbukuruar me kandila (yje ndriquese) dhe ata ikemi bërë gjuajtës kundër djajve, të cilëve u kemi përgatitur dënim me zjarr shumë të madh.');
INSERT INTO "ahmeti" VALUES(67,6,'67:6','Dënimi në Xhehennem është edhe për ata që nuk besuan Zotit të tyre; sa vend i keq është ai!');
INSERT INTO "ahmeti" VALUES(67,7,'67:7','Kur të hidhen në të, ata do t’ia dëgjojnë atij ushtimën e ai vlon.');
INSERT INTO "ahmeti" VALUES(67,8,'67:8','Nga zemërimi, ai gati copëtohet. Sa herë që hidhet në të ndonjë turmë, roja i pyet: “A nuk ju pat ardhur juve ndonjë pejgamber (qortues)?”');
INSERT INTO "ahmeti" VALUES(67,9,'67:9','Ata thonë: “Po, na erdhi pejgamberi, por ne e përgënjeshtruam dhe ne u thamë: “All-llahu nuk shpalli asgjë, e ju nukjeni tjetër vetëm se në një humbje të nadhe!”');
INSERT INTO "ahmeti" VALUES(67,10,'67:10','Dhe thonë: “Sikur të kishim dëgjuar dhe pasur mend, ne nuk do të ishim ndër banuesit e zjarrit”.');
INSERT INTO "ahmeti" VALUES(67,11,'67:11','Pra, i pranojmë mëkatet e veta; banuesit e zjarrit qofshin larg mëshirës.');
INSERT INTO "ahmeti" VALUES(67,12,'67:12','Ata, që pa parë i frikësoheshin Zotit të vet, për ta ka falje dhe shpërblim të madh.');
INSERT INTO "ahmeti" VALUES(67,13,'67:13','E, ju fshihnie ose thhonie fjalën tuaj haptas; Ai s’ka dyshim se e di çka fshehni në zemrat tuaja.');
INSERT INTO "ahmeti" VALUES(67,14,'67:14','A nuk e di Ai që ka krijuar, kur dihet se Ai dëpërton në thellësi të sekreteve, i njh hollësitë.');
INSERT INTO "ahmeti" VALUES(67,15,'67:15','Ai është që juve tokën ua bëri të përshtatshme, andaj, ecni nëpër pjesë të saj dhe shfrytëzoni begatitë e Tij, meqë vetëm te Ai është e ardhmja.');
INSERT INTO "ahmeti" VALUES(67,16,'67:16','A u garantuat j prej Atij që është në qiell, që të mos ju shafit (të mos u lëshoj) toka kur të dridhet.');
INSERT INTO "ahmeti" VALUES(67,17,'67:17','A u garantuat ju prej Atij që është në qiell, që të mos lëshojë kundër jush ndonjë stuhi me rrebesh gurësh. E pra, do ta kuptoni se si është ndëshkimi Im!');
INSERT INTO "ahmeti" VALUES(67,18,'67:18','Edhe ata që ishin para tyre përgënjeshtruan, por çfarë ishte dënimi Im?');
INSERT INTO "ahmeti" VALUES(67,19,'67:19','A nuk i shikuan ata shpezët përmbi ta krahëhapur dhe kur krahët i palojnë, ato nuk i mban në ajër pos Fuqiplotit. Ai është që çdo send e sheh dhe e di.');
INSERT INTO "ahmeti" VALUES(67,20,'67:20','Kush është ajo ushtëri juaja që do t’ju ndihmojë, pos Mëshiruesit? Është e vërtetë se jobesimtarët gjenden në një mashtrim të madh.');
INSERT INTO "ahmeti" VALUES(67,21,'67:21','Kush është ai që ju ushqen, po që se Ai ua ndërpret furnizi, im e vet? Jo, askush, por ata vazhdojnë me arrogancë e kundërvënie.');
INSERT INTO "ahmeti" VALUES(67,22,'67:22','A është më i udhëzyar ai që ecën i përmbysur me fytyrë në tokë, apo ai që ecën në pozicion qëndrues rrugës së drejtë?');
INSERT INTO "ahmeti" VALUES(67,23,'67:23','Thuaj: “Ai është q ju krijoi, ju dhuroi të dëgjuarit, të parit dhe mendjen, kurse pak po falënderoni”');
INSERT INTO "ahmeti" VALUES(67,24,'67:24','Thuaj: “Ai është që ju krijoi e ju shumoi në tokë dhe vetëm te Ai do të tuboheni!”');
INSERT INTO "ahmeti" VALUES(67,25,'67:25','E, ata po thonë: “Kur do të sëndertohet ky premtim, po qe se jeni të vërtetë?”');
INSERT INTO "ahmeti" VALUES(67,26,'67:26','. Thuaj: “Atë e di vetëm All-llahu, e unë jam një qortues që ju bën me dije!”');
INSERT INTO "ahmeti" VALUES(67,27,'67:27','Kur ta shohin atë (Xhehennemin) se po u afrohet, fytyrat e atyre që nuk besuan u shëmtohendhe u thuhet: “Ky është ai që ju e kërkonit sa më shpejt!”');
INSERT INTO "ahmeti" VALUES(67,28,'67:28','Thuaj: “Më tregoni pra, nëse All-llahu më merr shpirtin mua dhe atyre që janë me mua, ose na mëshiron, kush do t’i mbrojë jobesimtarët prej një dënimi të mundimshëm?”');
INSERT INTO "ahmeti" VALUES(67,29,'67:29','Thuaj: “Ai është Mëshiruesi, Atij i kemi besuar dhe vetë te Ai jemi mbështetur, kurse ju do takuptoni se kush njëmend është ai i humburi!”');
INSERT INTO "ahmeti" VALUES(67,30,'67:30','Thuaj: “Më tregoni, nëse uji juaj humbet në thellësi, kush do t’ju sjell ujë mbitokësor (burimor)?”');
INSERT INTO "ahmeti" VALUES(68,1,'68:1','Nun, betohem në pendën dhe në atë çka shkruajnë!');
INSERT INTO "ahmeti" VALUES(68,2,'68:2','Ti, me dhuratën pejgamber) e Zotit tënd, nuk je i çmendur!');
INSERT INTO "ahmeti" VALUES(68,3,'68:3','Dhe se ti, padyshim do të kesh shpërblim të pandërprerë!');
INSERT INTO "ahmeti" VALUES(68,4,'68:4','Vërtet, ti je në një shkallë të lartë morali!');
INSERT INTO "ahmeti" VALUES(68,5,'68:5','Më vonë ti do të shohësh, edhe ata do të shohin.');
INSERT INTO "ahmeti" VALUES(68,6,'68:6','Se cili prej jush është i çmendur?');
INSERT INTO "ahmeti" VALUES(68,7,'68:7','S’ka dyshim se Zoti yt është Ai që e di më së miri se kush është ai që ka humbur prej rrugës së Tij dhe Ai e di më së miri për të udhëzuarit.');
INSERT INTO "ahmeti" VALUES(68,8,'68:8','Andaj, ti mos u pajto me gënjeshtarët!');
INSERT INTO "ahmeti" VALUES(68,9,'68:9','Ata kanë dëshirë që ti të bësh lajka.');
INSERT INTO "ahmeti" VALUES(68,10,'68:10','Mos e respekto askë që betohet shumë dhe është i poshtër!');
INSERT INTO "ahmeti" VALUES(68,11,'68:11','që është përgojues e ban fjalë ndër njerëz.');
INSERT INTO "ahmeti" VALUES(68,12,'68:12','Shumë koprrac ndaj rrugës së mbarë, i pafrenueshëm, mëkatarë,');
INSERT INTO "ahmeti" VALUES(68,13,'68:13','që është shumë i vrazhdë, më në fund edhe kopil (nuk dihet baba).');
INSERT INTO "ahmeti" VALUES(68,14,'68:14','(Mos e respekto) Vetëm pse ka pasuri e djem!');
INSERT INTO "ahmeti" VALUES(68,15,'68:15','Kur atij i lexohen ajetet Tona, ai thotë: “Legjenda të të parëve!”');
INSERT INTO "ahmeti" VALUES(68,16,'68:16','Ne do ti dëmkosim atë në turi (në hundë).');
INSERT INTO "ahmeti" VALUES(68,17,'68:17','Ne i sprovuam ata (mekasit) sikurse i kemi pas sprovuar pronarët e kopshtit. Kur u betuan se do të vjelin atë herët në agim.');
INSERT INTO "ahmeti" VALUES(68,18,'68:18','E nuk thanë (kur u betuan): “Në dashtë All-llahu - inshaell -llahu!”');
INSERT INTO "ahmeti" VALUES(68,19,'68:19','E, sa qenë ata fjetur, atë (kopshtin) e goditi një bela nga Zoti yt.');
INSERT INTO "ahmeti" VALUES(68,20,'68:20','Dhe ai gdhiu si të ishte i vjelë.');
INSERT INTO "ahmeti" VALUES(68,21,'68:21','Ata në mëngjes thërritnin njëri-tjetrin.');
INSERT INTO "ahmeti" VALUES(68,22,'68:22','Ngrihuni herët te prodhimet e tuaja, nëse doni t’i vilni!');
INSERT INTO "ahmeti" VALUES(68,23,'68:23','Dhe ata vajtënduke pëshpëritur në mes vete (të mos i dëgjojnë kush).');
INSERT INTO "ahmeti" VALUES(68,24,'68:24','Të mos lejojnë të afrohet sot në të asnjë i varfër.');
INSERT INTO "ahmeti" VALUES(68,25,'68:25','Të mos lejojnë të afrohet sot në të asnjë i varfër.');
INSERT INTO "ahmeti" VALUES(68,26,'68:26','E kur e panë atë, thanë: “S’ka dyshim, ne kemi humbur (rrugën)”.');
INSERT INTO "ahmeti" VALUES(68,27,'68:27','Jo, (ne nuk kemi humbur rrugën) por pakemi mbetur pa të!');
INSERT INTO "ahmeti" VALUES(68,28,'68:28','Ai me maturi prej tyre tha: “A nuk ju thahë përse nuk e përmendi Zotin?”');
INSERT INTO "ahmeti" VALUES(68,29,'68:29','Atëherë ata thanë: “I pafajshëm është Zoti ynë, ne ishim të padrejtë”');
INSERT INTO "ahmeti" VALUES(68,30,'68:30','Dhe u kthyen e po e qortojën njëri-tjetrin.');
INSERT INTO "ahmeti" VALUES(68,31,'68:31','Thanë: “Të mjerët ne, vërtet ne ishim jashtë rrugës!”');
INSERT INTO "ahmeti" VALUES(68,32,'68:32','Shpresohet se Zoti yt do na zëvendsojë me ndonjë edhe më të mirë se ai; ne në të vetëm te Zoti ynë mbajmë shpresën.');
INSERT INTO "ahmeti" VALUES(68,33,'68:33','Ja, kështu do të jenë dënimi (i mekasve), ndërsa dënimi i botës tjetër do të jetë shumë më i ashpër, sikur të jenë që e kuptojnë.');
INSERT INTO "ahmeti" VALUES(68,34,'68:34','Është e sigurt se për të devotshmit do të ketë te Zoti i tyre kopshte të begatshme.');
INSERT INTO "ahmeti" VALUES(68,35,'68:35','A do ti bëjmë krminelët të barabartë me mylimanët?');
INSERT INTO "ahmeti" VALUES(68,36,'68:36','ç’është me ju, si gjykoni ashtu?');
INSERT INTO "ahmeti" VALUES(68,37,'68:37','A mos keni ndonjë libër, e prej saj mësoni?');
INSERT INTO "ahmeti" VALUES(68,38,'68:38','E ju do të keni atje çka të dëshironi?');
INSERT INTO "ahmeti" VALUES(68,39,'68:39','Amos keni ndonjë zotim të fortë prej Nesh që vazhdon deri në kijamet, se do ta rrini atë që dëshironi?');
INSERT INTO "ahmeti" VALUES(68,40,'68:40','Pyeti ata se cili është prej tyre që u garanton atë?');
INSERT INTO "ahmeti" VALUES(68,41,'68:41','Apo ata kanë ortakë (zota) që u garantojnë, pra le t’i sjellin ortakët e vet, nëse e thonë të vëtetën?');
INSERT INTO "ahmeti" VALUES(68,42,'68:42','(Përkujto) Ditën kur ashpërsohet çshtja deri në kulm (në kijamet), e ata do të ftohen për të bërë sexhde, po nuk mundën.');
INSERT INTO "ahmeti" VALUES(68,43,'68:43','Shikimet e tyre janë të përulura dhe ata i kaplon poshtërimi, kur dihet se ata kanë qenë të ftuar të bëjnë sexhde sa ishin të shëndoshë (e ata - talleshin).');
INSERT INTO "ahmeti" VALUES(68,44,'68:44','Pra më lë Mua dhe ata që e mohojnë këtë Kur’an. Ne do t’i afrojmë ata dalëngadalë te dënimi prej nga nuk presin.');
INSERT INTO "ahmeti" VALUES(68,45,'68:45','Dhe atyre Unë u jap afat, sepse ndëshkimi Im ëstë shumë i fortë.');
INSERT INTO "ahmeti" VALUES(68,46,'68:46','A mos kërkon ti prej tyre shpërblim, e ata janë të ngarkuar me dhëniet?');
INSERT INTO "ahmeti" VALUES(68,47,'68:47','A mos te ta është e fshehta (e Lehvi Mahfudhit), e ata prej atu trumbetojnë (se janë të mirë)?');
INSERT INTO "ahmeti" VALUES(68,48,'68:48','Po ti, bëhu i durueshëm ndaj caktimit të Zotit tënd, e mos u bë si ai peshkut në kohën kur pat thirrur (Zotin) dhe ishte i mllefosur.');
INSERT INTO "ahmeti" VALUES(68,49,'68:49','Sikur të mos i afrohej atij mëshirë nga Zoti i tij, do të hidhej në shkretërirë i demoralizuar.');
INSERT INTO "ahmeti" VALUES(68,50,'68:50','Po Zoti i tij, e bëri atë të zgjedhur prej të mirëve.');
INSERT INTO "ahmeti" VALUES(68,51,'68:51','E ata që nuk besuan gati sa të zhdukin me shikimet e tyre, kur dëgjuan Kur’anin, e (nga inati) thanë: “Ai është i marrë!”');
INSERT INTO "ahmeti" VALUES(68,52,'68:52','Po ai (Kur’ani) nuk është tjetër vetëm se këshillë për botën (e njerëzve e të xhinëve).');
INSERT INTO "ahmeti" VALUES(69,1,'69:1','Momenti i katastrofës së përgjithshme (kijameti).');
INSERT INTO "ahmeti" VALUES(69,2,'69:2','ç’është kijameti?');
INSERT INTO "ahmeti" VALUES(69,3,'69:3','çka të mësoi ty se ç’është kijameti?');
INSERT INTO "ahmeti" VALUES(69,4,'69:4','Themudi dhe Adi përgënjeshtruan tronditjen (kijametin).');
INSERT INTO "ahmeti" VALUES(69,5,'69:5','Sa i përket Themudit, ata u shkatërruan me krismë të tmerrshme.');
INSERT INTO "ahmeti" VALUES(69,6,'69:6','Ndërsa Adi është shkatërruar me një erë të fortë, të pakufishme.');
INSERT INTO "ahmeti" VALUES(69,7,'69:7','Atë Ai e lëshoi kundër tyre shtatë net e tetë ditë rresht, kur shihje njerëzit në to të rrëzuar si të ishi trupa hurmash të zgavëruara.');
INSERT INTO "ahmeti" VALUES(69,8,'69:8','A e sheh prej tyre ndonjë që ka mbetur?');
INSERT INTO "ahmeti" VALUES(69,9,'69:9','Erdhi faraoni dhe ata që ishin para tij si dhe të përmbysurit (fshatrat e popullit të Lutit), me punë të gabuara.');
INSERT INTO "ahmeti" VALUES(69,10,'69:10','Ata kundërshtuan të dërguarin e Zotit të tyre, e Ai i dënoi ata me një dënim edhe më të madh (prej dënimeve të mëparshëm).');
INSERT INTO "ahmeti" VALUES(69,11,'69:11','Pasi që uji pat vërshuar, Ne ju bartëm në anije.');
INSERT INTO "ahmeti" VALUES(69,12,'69:12','Për ta bërë atë (ngjarje) përkujtim për ju dhe për ta ruajtur atë veshët që janë përkujtues.');
INSERT INTO "ahmeti" VALUES(69,13,'69:13','E kur të fryhet në surë një e fryeme.');
INSERT INTO "ahmeti" VALUES(69,14,'69:14','Dhe të ngrihen toka e kodrat e t’i mëshojnë njëra-tjetrës me një të goditur.');
INSERT INTO "ahmeti" VALUES(69,15,'69:15','Në aë kohë ndodh e vërteta (kijameti).');
INSERT INTO "ahmeti" VALUES(69,16,'69:16','E qielli çahet, pse ai atë ditë është i raskapitur.');
INSERT INTO "ahmeti" VALUES(69,17,'69:17','Dhe engjëjt qëndrojnë në skajet (anët) e tij, e atë ditë, tetë (engjëj) e bartin mbi vete Arshin e Zotit tënd.');
INSERT INTO "ahmeti" VALUES(69,18,'69:18','Atë ditë ju do të ekspozoheni (para Zotit), duke mos mbetur fshehtë asnjë sekret juaji.');
INSERT INTO "ahmeti" VALUES(69,19,'69:19','Kujt t’i jepet libri i vet nga e djathta e tij, ai do të thotë: “O ju, qe, lexonie librin tim!”');
INSERT INTO "ahmeti" VALUES(69,20,'69:20','Unë kam qenë i bindur se do të jap llogarinë time.');
INSERT INTO "ahmeti" VALUES(69,21,'69:21','Dhe ai atëherë është në një jetë të kënaqshme.');
INSERT INTO "ahmeti" VALUES(69,22,'69:22','Në një Xhennet të lartë.');
INSERT INTO "ahmeti" VALUES(69,23,'69:23','Pemët e tij i ka krejt afër.');
INSERT INTO "ahmeti" VALUES(69,24,'69:24','(E thuhet) Hani e pini shijshëm, ngase në ditët e kaluara ju e përgatitët këtë.');
INSERT INTO "ahmeti" VALUES(69,25,'69:25','Ndërkaq, kujt i jepen librat e veta nga e majta, ai thotë: “O i mjeri unë, të mos më jepej fare libri im.');
INSERT INTO "ahmeti" VALUES(69,26,'69:26','Dhe të mos dijsha fare se çka është llogaria ime.');
INSERT INTO "ahmeti" VALUES(69,27,'69:27','Ah, sikur të kishte qenë ajo (vdekja e parë) mbarim i amshueshëm për mua.');
INSERT INTO "ahmeti" VALUES(69,28,'69:28','Pasuria ime nuk më bëri fare dobi.');
INSERT INTO "ahmeti" VALUES(69,29,'69:29','U hoq prej meje çdo kompetencë imja.');
INSERT INTO "ahmeti" VALUES(69,30,'69:30','U thuhet zebanive: Kapnie atë, vënja prangat!');
INSERT INTO "ahmeti" VALUES(69,31,'69:31','Pastaj atë shtinie në Xhehennem...');
INSERT INTO "ahmeti" VALUES(69,32,'69:32','Mandej, lidhne atë me një zingjirë të gjatë shtatëdhjetë kutë.');
INSERT INTO "ahmeti" VALUES(69,33,'69:33','Pse ai ka qenë që nuk besoi All-llahun e madhëruar.');
INSERT INTO "ahmeti" VALUES(69,34,'69:34','Ai nuk nxiste për t’i ushqyer të varfërit.');
INSERT INTO "ahmeti" VALUES(69,35,'69:35','Ai sot nuk ka këtu ndonjë mik.');
INSERT INTO "ahmeti" VALUES(69,36,'69:36','As ushqim tjetër përveç të të shplarave.');
INSERT INTO "ahmeti" VALUES(69,37,'69:37','Që atë nuk e ha kush, pos mëkatarëve.');
INSERT INTO "ahmeti" VALUES(69,38,'69:38','Pra Unë betohem në atë që e shihni!');
INSERT INTO "ahmeti" VALUES(69,39,'69:39','Edhe në atë që nuk e shihni!');
INSERT INTO "ahmeti" VALUES(69,40,'69:40','Se me të vërtetë ai (Kur’ani) është fjalë (e Zotit) që e lexon i dërguari i ndershëm.');
INSERT INTO "ahmeti" VALUES(69,41,'69:41','Ai nuk është fjalë e ndonjë poeti, po ju nuk besoni.');
INSERT INTO "ahmeti" VALUES(69,42,'69:42','Nuk është as fjalë e ndonji falltori, po ju nuk besoni.');
INSERT INTO "ahmeti" VALUES(69,43,'69:43','Është zbritje prej Zotit të botëve!');
INSERT INTO "ahmeti" VALUES(69,44,'69:44','Sikur të trillonte ai (Muhammedi) për Ne ndonjë fjalë!');
INSERT INTO "ahmeti" VALUES(69,45,'69:45','Ne do ta kapim atë me fuqinë Tonë.');
INSERT INTO "ahmeti" VALUES(69,46,'69:46','E pastaj do t’ia këputnim atij arterien e zemrës.');
INSERT INTO "ahmeti" VALUES(69,47,'69:47','Askush prej jush nuk do të mund të ndërhynte për mbrojtjen e tij.');
INSERT INTO "ahmeti" VALUES(69,48,'69:48','Ai (kur’ani) është këshillë për të ruajturit.');
INSERT INTO "ahmeti" VALUES(69,49,'69:49','S’ka dyshim se Ne dijmë që prej jush ka qe e përgënjeshtrojnë.');
INSERT INTO "ahmeti" VALUES(69,50,'69:50','Ai (Kur’ani) njëmend do të jetë dëshprim për jobesimtarët.');
INSERT INTO "ahmeti" VALUES(69,51,'69:51','Ai është e vërtetë bindëse!');
INSERT INTO "ahmeti" VALUES(69,52,'69:52','Andaj, ti lartësoje nga të metat Zotin tënd të madhëruar!');
INSERT INTO "ahmeti" VALUES(70,1,'70:1','Një lutës e kerkoi dënimin e pashmangshëm,');
INSERT INTO "ahmeti" VALUES(70,2,'70:2','për jobesimtarët. Atë (dënim) s’ka kush që mund ta ndalë.');
INSERT INTO "ahmeti" VALUES(70,3,'70:3','Ai dënim vjen nga All-llahu, pronari ishkallëve të larta (në qiej).');
INSERT INTO "ahmeti" VALUES(70,4,'70:4','Atje ngjiten engjëjt dhe shpirti (Xhibrili) në një ditë që zgjatë pesëdhjetë mijë vjet (ose lartësia e atyre shkallëve është pesëdhjetë mijë vjet).');
INSERT INTO "ahmeti" VALUES(70,5,'70:5','Ti, pra, duro me një durim pa ankesë (të mirë).');
INSERT INTO "ahmeti" VALUES(70,6,'70:6','Atyre u duket ai larg,');
INSERT INTO "ahmeti" VALUES(70,7,'70:7','Kurse Neve ai na duket afër.');
INSERT INTO "ahmeti" VALUES(70,8,'70:8','Dita kur qielli të bëhet si kallaji i shkrirë.');
INSERT INTO "ahmeti" VALUES(70,9,'70:9','Kodrat të bëhen si leshi i lënurur.');
INSERT INTO "ahmeti" VALUES(70,10,'70:10','Dhe asnjë mik nuk pyet për mikun.');
INSERT INTO "ahmeti" VALUES(70,11,'70:11','Edhe pse ata shihen ndërmjet vete (edhe njihen, por ikin prej njëri-tjetrit). Krimineli dëshiron sikur të kishte paguar dënimin e asaj dite me bijtë e vet.');
INSERT INTO "ahmeti" VALUES(70,12,'70:12','Dhe me gruan e vet dhe me vëllain e vet.');
INSERT INTO "ahmeti" VALUES(70,13,'70:13','Edhe me të afërmit e tij që ai te ata mbështetej.');
INSERT INTO "ahmeti" VALUES(70,14,'70:14','Edhe me krejt çka ka në tokë, e vetëm të kishte shpëtuar!');
INSERT INTO "ahmeti" VALUES(70,15,'70:15','Jo, kurrsesi, ajo (para) është Ledhdha - flaka (emër i Xhehennemit).');
INSERT INTO "ahmeti" VALUES(70,16,'70:16','Është që heq kapakët e kokës.');
INSERT INTO "ahmeti" VALUES(70,17,'70:17','Që e thërret (tërheq prej vetes) atë që është zbbrapsur e larguar (prej besimit).');
INSERT INTO "ahmeti" VALUES(70,18,'70:18','Dhe që ka tubuar (pasuri) dhe e ka ruajtur (fshehur).');
INSERT INTO "ahmeti" VALUES(70,19,'70:19','Është e vërtetë se njeriu është i prirur të jetë i padurueshëm.');
INSERT INTO "ahmeti" VALUES(70,20,'70:20','Pse kur e godit ndonjë e keqe, ai ankohet së tepërmi.');
INSERT INTO "ahmeti" VALUES(70,21,'70:21','Ndërkaq, kur e gjen e mira, ai bëhet tepër koprac.');
INSERT INTO "ahmeti" VALUES(70,22,'70:22','Përveç atyre që falen,');
INSERT INTO "ahmeti" VALUES(70,23,'70:23','të cilët janë të rregullt në faljen e namazit të tyre.');
INSERT INTO "ahmeti" VALUES(70,24,'70:24','Dhe ata që në pasurinë e vet kanë ndarë në një pjesë të caktuar.');
INSERT INTO "ahmeti" VALUES(70,25,'70:25','Për lypësin dhe për nevojtarin që nuk lyp.');
INSERT INTO "ahmeti" VALUES(70,26,'70:26','Edhe ata që e besojnë bindshëm ditën e gjykimit.');
INSERT INTO "ahmeti" VALUES(70,27,'70:27','Edhe ata që i frikësohen dënimit nga Zoti i tyre.');
INSERT INTO "ahmeti" VALUES(70,28,'70:28','Sepse është e vërtetë që nuk ka shpëtim prej dënimit nga Zoti i tyre.');
INSERT INTO "ahmeti" VALUES(70,29,'70:29','Edhe ata që janë ruajtur prej punëve të ndyra (amorale).');
INSERT INTO "ahmeti" VALUES(70,30,'70:30','Me përjashtim ndaj grave të veta dhe ndaj robëreshave që i kanë në posedim, ata nuk qortohen për to.');
INSERT INTO "ahmeti" VALUES(70,31,'70:31','E kush kërkon përveç tyre, të tillët janë të shfrenuar (meritojnë dënim).');
INSERT INTO "ahmeti" VALUES(70,32,'70:32','Edhe ata që janë besnikë ndaj amanetit që u është besuar dhe ndaj premtimit të dhënë.');
INSERT INTO "ahmeti" VALUES(70,33,'70:33','Edhe ata që dëshminë e vet e zbatojnë (nuk e mohojnë, nuk e fshehin).');
INSERT INTO "ahmeti" VALUES(70,34,'70:34','Edhe ata që janë të kujdesshëm ndaj rregullave të namazit të tyre.');
INSERT INTO "ahmeti" VALUES(70,35,'70:35','Të tillët janë në Xhennete dhe janë të nderuar.');
INSERT INTO "ahmeti" VALUES(70,36,'70:36','ç’është me ata që nuk besuan dhe zgjasin qafat nga ti?');
INSERT INTO "ahmeti" VALUES(70,37,'70:37','Ulën në grupe në të djathtë e në të majtë teje?');
INSERT INTO "ahmeti" VALUES(70,38,'70:38','A dëshiron secili prej tyre të hyjë në Xhennetin r begatshëm?');
INSERT INTO "ahmeti" VALUES(70,39,'70:39','Kurrsesi! Ne i krijuam ata nga ajo çka ata e dinë.');
INSERT INTO "ahmeti" VALUES(70,40,'70:40','Pra betohem në Zotin e lindjeve e të perëndimeve, se Ne kemi fuqi.');
INSERT INTO "ahmeti" VALUES(70,41,'70:41','të zëvendësojmë me më të mirë se ata dhe Ne nuk mund të na dalë kush para.');
INSERT INTO "ahmeti" VALUES(70,42,'70:42','Po ti lëri ata të zhyten edhe më thellë në të kota dhe të dëfrehen deri që të ballafaqohen në ditën e tyre që po u premtohet.');
INSERT INTO "ahmeti" VALUES(70,43,'70:43','Ditën kur duke u ngutur dalin prej varreve, sikur nguteshin te idhujt,');
INSERT INTO "ahmeti" VALUES(70,44,'70:44','Me shikimet e tyre të përulura i ka kapluar poshtërimi. Ajo është dita që ka qenë premtuar.');
INSERT INTO "ahmeti" VALUES(71,1,'71:1','Ne e dërguam Nuhun e populli i vet (dhe i thamë): “Tërhiqja vërejtjen popullit tënd para se ata t’i godasë dënimi i rëndë!”');
INSERT INTO "ahmeti" VALUES(71,2,'71:2','Ai tha: “O populli im, unë jam i dëruar te ju, jam i qartë.');
INSERT INTO "ahmeti" VALUES(71,3,'71:3','Adhuronie All-llahun, kini frikë prej Tij dhe mua më dëgjoni.');
INSERT INTO "ahmeti" VALUES(71,4,'71:4','Ai u falë juve nga mëkatet tuaja dhe ua vazhdon jetën deri në një afat të caktuar (pa u dënuar), e kur të vijë afati i caktuar prej All-llahut, ai nuk shtyhet për më vonë, nëse jeni që e dini”.');
INSERT INTO "ahmeti" VALUES(71,5,'71:5','Ai tha: “O Zoti im, unë e thirra popullin tim natën e ditën.');
INSERT INTO "ahmeti" VALUES(71,6,'71:6','Por thirrja ime vetëm ua shtoi ikjen.');
INSERT INTO "ahmeti" VALUES(71,7,'71:7','Dhe sa herë që unë i thirritja ata për t’u falur Ti mëkatet, ata i vënin gishtat e tyre në veshë dhe e mbulonin kokat me teshat e tyre dhe vazhdonin në atë të tyren me një mendjemadhësi të fortë.');
INSERT INTO "ahmeti" VALUES(71,8,'71:8','Pastaj, unë i kam thirrur ata haptazi.');
INSERT INTO "ahmeti" VALUES(71,9,'71:9','Pastaj unë thirrjen ua drejtoja atyre haptazi, e shumë herë edhe fshehurazi”.');
INSERT INTO "ahmeti" VALUES(71,10,'71:10','Unë u thashë: “Kërkoni falje Zotit tuaj, se Ai vërtet falë shumë;');
INSERT INTO "ahmeti" VALUES(71,11,'71:11','Ai ju lëshon nga qielli shi me bollëk,');
INSERT INTO "ahmeti" VALUES(71,12,'71:12','Ju shumon pasurinë dhe fëmijët, ju bën të keni kopshte dhe ju jep lumenj.');
INSERT INTO "ahmeti" VALUES(71,13,'71:13','ç’është me ju që All-llahut nuk i shprehni madhërinë që e meriton?');
INSERT INTO "ahmeti" VALUES(71,14,'71:14','Kur Ai ju krijoi në disa etapa.');
INSERT INTO "ahmeti" VALUES(71,15,'71:15','A nuk e keni parë se si All-llahu krijoi shtatë palë qiej (në kate).');
INSERT INTO "ahmeti" VALUES(71,16,'71:16','Dhe në ta hënën e bëri dritë, diellin e bëri ndriçues.');
INSERT INTO "ahmeti" VALUES(71,17,'71:17','Dhe All-llahu u shpiku juve prej tokës si bimë.');
INSERT INTO "ahmeti" VALUES(71,18,'71:18','Pastaj ju kthen në të pa dyshim ju nxjerr sërish.');
INSERT INTO "ahmeti" VALUES(71,19,'71:19','All-llahu juve ua bëri tokën të sheshtë.');
INSERT INTO "ahmeti" VALUES(71,20,'71:20','Që nëpër të të ecni rrugëve të gjera”.');
INSERT INTO "ahmeti" VALUES(71,21,'71:21','Nuhu tha: “Zoti im, ata më kundërshtuan, ata shkuan pas atyre (pasanikëve), të cilëve pasuria dhe fëmijët e tyre vetëm ua shtuan dëshpërimin.');
INSERT INTO "ahmeti" VALUES(71,22,'71:22','Dhe ata (pasanikët) u kurdisën atyre kurthe shumë të mëdha”.');
INSERT INTO "ahmeti" VALUES(71,23,'71:23','Dhe u thanë: Mos braktisni zotat tuaj kurrsesi, mos braktisni Vedda-në, as Suvva-në, e Jeguth-in, Jeukë-n e Nesre-n.');
INSERT INTO "ahmeti" VALUES(71,24,'71:24','Dhe ata i bënë të humbur shumë sish, e Ti zullumqarëve mos u jep tjetër, por vetëm humbje.');
INSERT INTO "ahmeti" VALUES(71,25,'71:25','Për shkak të mëkateve të tyre ata u përmbytën dhe menjëherë u futën në zjarr dhe ata, përveç All-llahut, nuk gjetën ndihmëtarë.');
INSERT INTO "ahmeti" VALUES(71,26,'71:26','E Nuhu tha: “O Zoti im, mos lë mbi tokë asnjë nga jobesimtarët!');
INSERT INTO "ahmeti" VALUES(71,27,'71:27','Sepse, nëse Ti i lë ata, robërit Tu do t’i humbin dhe prej tyre nuk lind tjetër, vetëm të prishur e jobesimtarë.');
INSERT INTO "ahmeti" VALUES(71,28,'71:28','Zoti im, më falë mua dhe prindërit e mi, dhe ata që hynë në shtëpinë time, duke qenë besimtarë. edhe besimtarët e besimtaret, ndërsa jobesimtarëve mos u shto tjetër, vetëm dëshpërim”.');
INSERT INTO "ahmeti" VALUES(72,1,'72:1','Thuaj: “Mua më shpalllet se një grup i vogël nga xhinët i vuri veshin dhe dëgjoi (Kur’anin) dhe (kur u kthyen te të vetët) thanë: “Ne kemi dëgjuar një Kur’an që mahnit,');
INSERT INTO "ahmeti" VALUES(72,2,'72:2','që udhëzon në të vërtetën, andaj ne i besuam atij dhe Zotit tonë, kurrsesi nuk do t’ia shoqërojmë më akë”');
INSERT INTO "ahmeti" VALUES(72,3,'72:3','Dhe se latësua e madhëruar e Zotit tonë, nuk është që ka as grua, as fëmijë.');
INSERT INTO "ahmeti" VALUES(72,4,'72:4','S’ka dshim se ai mendjelehti jonë fliste të pavërteta ndaj All-llahut.');
INSERT INTO "ahmeti" VALUES(72,5,'72:5','Ndërsa, ne kemi pas menduar se as njerëzit, as xhinët nuk flasin gënjeshtra përkitazi me All-llahun,');
INSERT INTO "ahmeti" VALUES(72,6,'72:6','Dhe se ka pasur burra prej njerëzve, që kërkonin ndihmë prej disa xhinëve dhe ashtu ua shtonin atyre edhe më shumë arrogancën.');
INSERT INTO "ahmeti" VALUES(72,7,'72:7','Dhe se ata (njerëzit) kanë menduar, siç keni menduar edhe ju, se All-llahu nuk ka për të ringjallur askë.');
INSERT INTO "ahmeti" VALUES(72,8,'72:8','Dhe vërtet, ne kemi hulumtuar ta arrijnë qiellin, po atë e gjetëm përplot roje të forta dhe me shkëndija (zjarri që djeg),');
INSERT INTO "ahmeti" VALUES(72,9,'72:9','Dhe ne më parë hulumtonim në vendëvështrim për të dëgjuar, e kush përpiqet të dëgjojë tash, ai has në pusinë e shkëndijës së zjarrtë.');
INSERT INTO "ahmeti" VALUES(72,10,'72:10','Ne nuk e dimë se me këtë (përgaditje të qiellit) është për qëllim ndonjë e keqe për ata që janë në tokë, apo Zoti i tyre dëshiron që ata t’i udhëzojë.');
INSERT INTO "ahmeti" VALUES(72,11,'72:11','Është e vërtetë se prej nesh ka të mirë, por ka edhe që nuk janë aq të mirë, sepse ne kemi qenë në drejtime të ndryshme.');
INSERT INTO "ahmeti" VALUES(72,12,'72:12','Dhe ne kemi qenë të bindur se All-llahut nuk mund t’i shmangeni në tokë e as nuk mund t’i shpëtojmë Atij duke ikur.');
INSERT INTO "ahmeti" VALUES(72,13,'72:13','Dhe ne, sapo e dëgjuam udhëzimin (Kur’anin), i besuam atij, e kush e beson Zotit të vet, ai nuk i frikësohet ndonjë dënimi e as ndonjë padrejtësie.');
INSERT INTO "ahmeti" VALUES(72,14,'72:14','Është e vërtetë se prej nesh ka besimtarë (myslimanë) dhe prej nesh ka që janë jashtë rrugës (jobesimtarë), e kush e pranoi islamin, të tillët mësynë rrugën e shpëtimit.');
INSERT INTO "ahmeti" VALUES(72,15,'72:15','Ndërsa, ata që lëshuan rrugën, ata u bënë lëndë e Xhehennemit!');
INSERT INTO "ahmeti" VALUES(72,16,'72:16','sikur ata (idhujtarët) t’i përmbaheshin rrugës së drejtë, Ne do t’iu lëshonim atyre ujë me bollëk,');
INSERT INTO "ahmeti" VALUES(72,17,'72:17','Për të sprovuar me të. E kush ia kthen shpinën adhurimit ndaj Zotit të vet, Ai e shpie atë në vuajtje të padurueshme.');
INSERT INTO "ahmeti" VALUES(72,18,'72:18','Dhe (mua më shpallet) e vërtetë xhamitë janë veçant për të adhuruar All-llahun, e mos adhuroni në to askë tjetër me All-llahun!');
INSERT INTO "ahmeti" VALUES(72,19,'72:19','Dhe se kur u ngrit robi i All-llahut ta adhurojë Atë, ata (xhinët ose idhujtarët) gati hipnin njëri mbi tjetërin për të dagjuar.');
INSERT INTO "ahmeti" VALUES(72,20,'72:20','Thuaj: “Unë dëshiroj vetëm Zotin tim dhe Atij nuk i bëj asnjë shok!”');
INSERT INTO "ahmeti" VALUES(72,21,'72:21','Thuaj: “Unë nuk kam në dorë që t’ju largojë dëmin, as t’ju sjell dobi!”');
INSERT INTO "ahmeti" VALUES(72,22,'72:22','Thuaj: “Është e vërtetë se mua nuk më mbron askush prej (dënimit) të All-llahut dhe përveç Tij, unë nuk gjej mbështetje”.');
INSERT INTO "ahmeti" VALUES(72,23,'72:23','(Nuk kam në dorë tjetër), përveç kumtimin prej All-llahut dhe përveç shpalljeve të Tij. Kush e kundërshton All-llahun dhe të dërguarin e tij, i tij është zjarri i Xhehennemit, aty do të jenë përgjithmonë, pambarim.');
INSERT INTO "ahmeti" VALUES(72,24,'72:24','Derisa kur ta shohin atë që u premtohet, atëherë do të kuptojnë se kush është më i dobët në ndihmë dhe më i vogël në numër.');
INSERT INTO "ahmeti" VALUES(72,25,'72:25','Thuaj: “Unë nuk e di a do të jetë afër ajo që u premtohet, apo Zoti im do ta vonojë atë shumë?”');
INSERT INTO "ahmeti" VALUES(72,26,'72:26','Ai është që e di të fshehtën, por fshehtësinë e vet nuk ia zbulon askujt.');
INSERT INTO "ahmeti" VALUES(72,27,'72:27','Me përjashtim të ndonjë të dërguari që Ai do. Atëbotë Ai vë roje para edhe prapa tij (kur i shpall fshehtësinë),');
INSERT INTO "ahmeti" VALUES(72,28,'72:28','Për ta ditur (për t’u dukur) se ata kanë kumtuar shpalljet e Zotit të tyre, dhe se Ai ka përfshi atë te ata, dhe Ai e di sasinë e çdo gjëje që ekziston.');
INSERT INTO "ahmeti" VALUES(73,1,'73:1','O ti i mbështjellë!');
INSERT INTO "ahmeti" VALUES(73,2,'73:2','Ngrihu për falje natën, përveç një pjese të vogël të saj.');
INSERT INTO "ahmeti" VALUES(73,3,'73:3','Gjysmën e saj ose diç më pak se gjysmën.');
INSERT INTO "ahmeti" VALUES(73,4,'73:4','Ose shto diç prej saj dhe Kur’anin lexoje me vëmendje.');
INSERT INTO "ahmeti" VALUES(73,5,'73:5','Ne do të shpallim ty fjalë të rëndë (të madhe).');
INSERT INTO "ahmeti" VALUES(73,6,'73:6','E s’ka dyshim se akiviteti i natës është fuqizues dhe fjlë më e qartë.');
INSERT INTO "ahmeti" VALUES(73,7,'73:7','Vërtet, Ti gjatë ditës ke angazhim të gjatë (të madh).');
INSERT INTO "ahmeti" VALUES(73,8,'73:8','Po ti përmende Zotin tënd dhe plotësisht përkushtoju Atij!');
INSERT INTO "ahmeti" VALUES(73,9,'73:9','Zot i lindjes dhe i perëndimit, nuk ka zot tjetër vetëm Ai, pra Atë merrë për mbështetje.');
INSERT INTO "ahmeti" VALUES(73,10,'73:10','Dhe kij durim ndaj asaj që të thonë dhe largohu atyre i matur.');
INSERT INTO "ahmeti" VALUES(73,11,'73:11','Dhe më lë Mua për ata gënjeshtarët pasanikë, dhe jepu atyre pak afat.');
INSERT INTO "ahmeti" VALUES(73,12,'73:12','Se me të vërtetë te Ne ka pranga e zjarr.');
INSERT INTO "ahmeti" VALUES(73,13,'73:13','Edhe ushqim që nuk gëlltitet edhe dënim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(73,14,'73:14','Atë ditë kur toka e kodrat të dridhen, e kodrat bëhen rërë rrëshqitëse.');
INSERT INTO "ahmeti" VALUES(73,15,'73:15','Ne dërguam te ju të dërguar që dëshmon kundër jush, ashtu siç patëm dërguar edhe te faraoni të dërguar.');
INSERT INTO "ahmeti" VALUES(73,16,'73:16','E faraoni e kundërshtoi të dërguarin, andaj Ne i dhamë atij një dënim të rëndë.');
INSERT INTO "ahmeti" VALUES(73,17,'73:17','Nëse ju mohoni, si do të mbroheni në një ditë që fëmijët do t’i bëjë pleq?');
INSERT INTO "ahmeti" VALUES(73,18,'73:18','Atë ditë qielli do të çahet dhe premtimi i Tij do të realizohet.');
INSERT INTO "ahmeti" VALUES(73,19,'73:19','Këto janë një përkujtim qortues, e kush do, ai e zgjedh rrugën për te Zoti i vet.');
INSERT INTO "ahmeti" VALUES(73,20,'73:20','Zoti yt e di se ti dhe një grup i atyre që janë me ty, kaloni në adhurim më pak se dy të tretat e natës ose gjysmën e saj a një të tretën e saj. All-llahu e di gjatësinë e natës dhe të ditës dhe Ai e diti që ju nuk do mund t’i përmbaheni asaj, andaj u lehtësoi juve, pra falnu duke lexuar Kur’an sa t’ju vijë më lehtë prej namazit. Ai e diti se nga ju do të ketë të sëmurë dhe të tjerët që gjallërojnë nëpër tokë dke kërkuar nga begatitë e All-llahut, e dhe të tjerë që luftojnë në rrugën e All-llahut, pra falnu dhe lexoni sa të jetë më lehtë për tij dhe kryene me rregull faljen, jepne zeqatin, huazoni (jepni sadaka) për hir të All-llahut hua të mirë, se çka të përgatitni për veten tuaj prej të mirave, atë do ta gjeni te All-llahu edhe më të mirë, dhe shpërblim më të madh, e kërkoni falje prej All-llahut, se All-llahu është mëkatfalës, mëshirues!');
INSERT INTO "ahmeti" VALUES(74,1,'74:1','O ti i mbuluar!');
INSERT INTO "ahmeti" VALUES(74,2,'74:2','Ngrihu dhe tërhiqu vërejtjen (duke i thirrur).');
INSERT INTO "ahmeti" VALUES(74,3,'74:3','Dhe madhëroje Zotin tënd!');
INSERT INTO "ahmeti" VALUES(74,4,'74:4','Dhe rrobat tua pastroji!');
INSERT INTO "ahmeti" VALUES(74,5,'74:5','Dhe të keqës së ndyrë largohu!');
INSERT INTO "ahmeti" VALUES(74,6,'74:6','Dhe mos u mburr me atë që jep e të duket shumë!');
INSERT INTO "ahmeti" VALUES(74,7,'74:7','Dhe për hir të Zotit tënd duro!');
INSERT INTO "ahmeti" VALUES(74,8,'74:8','E, kur të fryhet në Sur,');
INSERT INTO "ahmeti" VALUES(74,9,'74:9','Atëherë ajo është ditë e vështirë.');
INSERT INTO "ahmeti" VALUES(74,10,'74:10','Për jobesimtarët është jo e lehtë.');
INSERT INTO "ahmeti" VALUES(74,11,'74:11','Ti, më le Mua dhe atë që e krijova të vetmuar.');
INSERT INTO "ahmeti" VALUES(74,12,'74:12','Dhe bëra këtë pasuri të madhe.');
INSERT INTO "ahmeti" VALUES(74,13,'74:13','E bëra edhe me djem që i ka me vete.');
INSERT INTO "ahmeti" VALUES(74,14,'74:14','Dhe Unë i lash në disponim të gjitha mundësitë.');
INSERT INTO "ahmeti" VALUES(74,15,'74:15','E ai lakmon që t’i shtoj edhe më tepër.');
INSERT INTO "ahmeti" VALUES(74,16,'74:16','E jo, se ai vërtet ishte mohues i fakteve tona!');
INSERT INTO "ahmeti" VALUES(74,17,'74:17','Atë do ta ndrydhi me një dënim të rëndë e të padurueshëm.');
INSERT INTO "ahmeti" VALUES(74,18,'74:18','Pse ai shumë mendonte, radhitte e përgatiste (për Kur’anin).');
INSERT INTO "ahmeti" VALUES(74,19,'74:19','Qoftë i shkatërruar, po si përceptonte?');
INSERT INTO "ahmeti" VALUES(74,20,'74:20','Prapë qoftë i shkatërruar, po si përceptonte?');
INSERT INTO "ahmeti" VALUES(74,21,'74:21','Ai sërish thellohej në mendime.');
INSERT INTO "ahmeti" VALUES(74,22,'74:22','Pastaj mrrolej dhe bëhej edhe i vrazhdë.');
INSERT INTO "ahmeti" VALUES(74,23,'74:23','Pastaj ktheu shpinën me mendjemadhësi.');
INSERT INTO "ahmeti" VALUES(74,24,'74:24','E tha: “Ky (Kur’ani) nuk është tjetër, vetëm se magji që përcillet prej të tjerëve.');
INSERT INTO "ahmeti" VALUES(74,25,'74:25','Ky nuk është tjetër, përveç fjalë njerëzish!”');
INSERT INTO "ahmeti" VALUES(74,26,'74:26','E Unë atë do ta hedhë në Sekar!');
INSERT INTO "ahmeti" VALUES(74,27,'74:27','E, ku e di ti se çka është Sekar?');
INSERT INTO "ahmeti" VALUES(74,28,'74:28','Ai nuk lë send të mbetet pa e djegur.');
INSERT INTO "ahmeti" VALUES(74,29,'74:29','Ai është që ua prish dhe ua nxin lëkurat.');
INSERT INTO "ahmeti" VALUES(74,30,'74:30','Mbikëqyrës mbi të janë nëntëmbëdhjetë.');
INSERT INTO "ahmeti" VALUES(74,31,'74:31','E mbiqëkyrës të zjarrit, Ne nuk bëjmë tjetër, përveç engjëjve dhe numrin e tyre (të përmendur në Kur’an) nuk e bëmë për tjetër, por vetëm si sprovë për ata që nuk besuan, e që të binden ata që u është dhënë libri, e atyre që besuan t’u shtohet besimi, e atyre që u është dhëmë libri dhe besimtarët të mos kenë dyshim, dhe ata që zemrat i kanë të sëmura dhe jobesimtarët të thonë: “çka dashti All-llahu me këtë shembull?” Ja ashtu, All-llahu e lë të humbur atë që do, e ushtrinë e Zotit tënd nuk e di kush pos Atij, e ai (Sekari), nuk është tjetër vetëm se një përkujtim për njerëz.');
INSERT INTO "ahmeti" VALUES(74,32,'74:32','Jo, nuk është ashtu, Pasha hënën!');
INSERT INTO "ahmeti" VALUES(74,33,'74:33','Edhe natën kur tërhiqet!');
INSERT INTO "ahmeti" VALUES(74,34,'74:34','Edhe agimin kur agon!');
INSERT INTO "ahmeti" VALUES(74,35,'74:35','S’ka dyshim se ai (Sekari) është një nga belatë më të mëdha.');
INSERT INTO "ahmeti" VALUES(74,36,'74:36','Është vërejtje për njerëz!');
INSERT INTO "ahmeti" VALUES(74,37,'74:37','Për atë nga mesi juaj që dëshiron të përparojë (në të mira) apo të ngecë.');
INSERT INTO "ahmeti" VALUES(74,38,'74:38','Secili njeri është pengë i veprës së vet.');
INSERT INTO "ahmeti" VALUES(74,39,'74:39','Përveç atyre të djathtëve.');
INSERT INTO "ahmeti" VALUES(74,40,'74:40','Që janë në Xhennete e i bëjnë pyetje njëri-tjetrit,');
INSERT INTO "ahmeti" VALUES(74,41,'74:41','Përkitazi me kriminelët (e u thonë):');
INSERT INTO "ahmeti" VALUES(74,42,'74:42','“çka u solli juve në Sekar?”');
INSERT INTO "ahmeti" VALUES(74,43,'74:43','Ata thonë: “Nuk kemi qenë prej atyre që faleshin (që bënin namaz);');
INSERT INTO "ahmeti" VALUES(74,44,'74:44','Nuk kemi qenë që ushqyenim të varfërit;');
INSERT INTO "ahmeti" VALUES(74,45,'74:45','Dhe kemi qenë që përziheshim me të tjerët në punë të kota.');
INSERT INTO "ahmeti" VALUES(74,46,'74:46','Dhe kemi qenë që nuk e besonim ditën e gjykimit.');
INSERT INTO "ahmeti" VALUES(74,47,'74:47','Derisa na erdhi e vërteta (vdekja)!”');
INSERT INTO "ahmeti" VALUES(74,48,'74:48','E tanimë, atyre nuk u bën dobi ndërmjetësimi i ndërmjetësuesve,');
INSERT INTO "ahmeti" VALUES(74,49,'74:49','Po çka kishin ata që largohesin prej këshillës (Kur’ait)?');
INSERT INTO "ahmeti" VALUES(74,50,'74:50','Ata ishin si gomarë të trembur (të egër).');
INSERT INTO "ahmeti" VALUES(74,51,'74:51','Që ikin prej luanit (ose prej gjahtarit).');
INSERT INTO "ahmeti" VALUES(74,52,'74:52','Veç kësaj, secili prej tyre dëshironte t’i jepej libër i posaçëm (i hapët, i qartë).');
INSERT INTO "ahmeti" VALUES(74,53,'74:53','E jo! Por ata janë që nuk frikësohen botës tjetër.');
INSERT INTO "ahmeti" VALUES(74,54,'74:54','Dhe jo! Se ai (Kur’ani) është këshillë e lartë.');
INSERT INTO "ahmeti" VALUES(74,55,'74:55','E kush do, këshillohet me të.');
INSERT INTO "ahmeti" VALUES(74,56,'74:56','Po ata nuk mund të marrin mësim prej tij, vetëm nëse All-llahu di, Ai është i denjë për t’iu ruajtur dhe i denjë për falje mëkatesh!');
INSERT INTO "ahmeti" VALUES(75,1,'75:1','Betohem në ditën e kijametit;');
INSERT INTO "ahmeti" VALUES(75,2,'75:2','Betohem në shpirtin që është shumë qortues!');
INSERT INTO "ahmeti" VALUES(75,3,'75:3','A mendon njeriu se nuk do t’i tubojmë eshtrat e tij?');
INSERT INTO "ahmeti" VALUES(75,4,'75:4','Po, do t’ia tubojmë! Duke qenë se Ne jemi të zotët t’ia rikrijojmë si kanë qenë edhe majat e gishtave të tij!');
INSERT INTO "ahmeti" VALUES(75,5,'75:5','Por, njeriu dëshiron të vazhdojë edhe mëtej në mëkate.');
INSERT INTO "ahmeti" VALUES(75,6,'75:6','Andaj edhe pyet: “Kur është dita e kijametit?”');
INSERT INTO "ahmeti" VALUES(75,7,'75:7','E, kur të merren sytë (të parët)?');
INSERT INTO "ahmeti" VALUES(75,8,'75:8','E të zëhet hëna (errësohet),');
INSERT INTO "ahmeti" VALUES(75,9,'75:9','Dhe të bashkohet dielli e hëna.');
INSERT INTO "ahmeti" VALUES(75,10,'75:10','Atë ditë njeriu do të thotë: “Nga të iket!”');
INSERT INTO "ahmeti" VALUES(75,11,'75:11','Jo, nuk ka strehim!');
INSERT INTO "ahmeti" VALUES(75,12,'75:12','Atë ditë vetëm te Zoti yt është caku!');
INSERT INTO "ahmeti" VALUES(75,13,'75:13','Atë ditë njeriu do të njihet me atë që çoi para dhe me atë që la prapa.');
INSERT INTO "ahmeti" VALUES(75,14,'75:14','Po njeriu është dëshmitar i vetvetes.');
INSERT INTO "ahmeti" VALUES(75,15,'75:15','Edhe nëse i paraqet arsyetimet e veta.');
INSERT INTO "ahmeti" VALUES(75,16,'75:16','Ti (Muhammed) mos shqipto atë (Kur’anin) me gjuhën tënde për ta nxënë atë me të shpejtë!');
INSERT INTO "ahmeti" VALUES(75,17,'75:17','Sepse Ne e kemi për detyrë tubimin dhe leximin e tij!');
INSERT INTO "ahmeti" VALUES(75,18,'75:18','E kur ta lexojnë atë ty, ti përcille me të dëgjuar leximin e tij.');
INSERT INTO "ahmeti" VALUES(75,19,'75:19','Dhe pastaj është obligim i yni që ta shkoqisim atë.');
INSERT INTO "ahmeti" VALUES(75,20,'75:20','Jo, nuk është ashtu! Por ju jeni që e doni të ngutshmen (dynjanë).');
INSERT INTO "ahmeti" VALUES(75,21,'75:21','Dhe e lini pas shpine atë të ardhmen (ahiretin).');
INSERT INTO "ahmeti" VALUES(75,22,'75:22','Atë ditë do të ketë fytyra të shkëlqyera (të gëzuara).');
INSERT INTO "ahmeti" VALUES(75,23,'75:23','Që Zotin e tyre e shikojnë.');
INSERT INTO "ahmeti" VALUES(75,24,'75:24','Atë ditë ka fytyra edhe të vrazhda.');
INSERT INTO "ahmeti" VALUES(75,25,'75:25','Që presin t’u thyhet kurrizi (të shkatërrohen).');
INSERT INTO "ahmeti" VALUES(75,26,'75:26','Jo dhe Jo! Po kur të arrijë (shpirti) në gropë të fytit (të gjoksit),');
INSERT INTO "ahmeti" VALUES(75,27,'75:27','Dhe thuhet (nga familja e të afërmit): “Kush do ta shërojë?”');
INSERT INTO "ahmeti" VALUES(75,28,'75:28','Dhe ai bindet se ai po ndahet.');
INSERT INTO "ahmeti" VALUES(75,29,'75:29','E t’i puqet kofsha për kofshe (t’i vështirësohet gjendja).');
INSERT INTO "ahmeti" VALUES(75,30,'75:30','Atë ditë vetëm te Zoti yt shkohet.');
INSERT INTO "ahmeti" VALUES(75,31,'75:31','E ai as nuk vërtetoi atë që duhej, as nuk u falë.');
INSERT INTO "ahmeti" VALUES(75,32,'75:32','Por përgënjeshtroi dhe ktheu shpinën.');
INSERT INTO "ahmeti" VALUES(75,33,'75:33','Dhe shkonte te familja e tij me fodullëk.');
INSERT INTO "ahmeti" VALUES(75,34,'75:34','Të është afruar ty e keqja e t’u afroftë! (ose i mjeri ti i mjeri).');
INSERT INTO "ahmeti" VALUES(75,35,'75:35','Edhe nje herë t’u afrua ty e keqja; t’u afroftë!');
INSERT INTO "ahmeti" VALUES(75,36,'75:36','A mos mendon njeriu se do të lihet duke mos zënë asgjë (pa kurrfarë përgjegjësie).');
INSERT INTO "ahmeti" VALUES(75,37,'75:37','A nuk ka qenë ai në një pikë ujë që derdhet.');
INSERT INTO "ahmeti" VALUES(75,38,'75:38','E pastaj u bë gjak i trashë, e Ai e krijoi dhe e përsosi?');
INSERT INTO "ahmeti" VALUES(75,39,'75:39','Dhe prej tij Ai i bëri dy lloje: mashkullin dhe femrën.');
INSERT INTO "ahmeti" VALUES(75,40,'75:40','A nuk është Ai (Zot) i fuqishëm që i ngjall të vdekurit?');
INSERT INTO "ahmeti" VALUES(76,1,'76:1','Vërtetë ka kaluar një periudhë kohore, që njeriu nuk ekzistonnte fare si një diçka i përmendur.');
INSERT INTO "ahmeti" VALUES(76,2,'76:2','Ne e krijuam prej një uji të bashkëdyzuar për të sprovuar atë, andaj e bëmë të dëgjojë e të shohë.');
INSERT INTO "ahmeti" VALUES(76,3,'76:3','Ne e udhëzuam atë në rrugën të drejtë, e ai do të jetë: mirënjohës ose përbuzës.');
INSERT INTO "ahmeti" VALUES(76,4,'76:4','Ne për jobesimtërët kemi përgatitur zinxhirë, pranga e zjarr.');
INSERT INTO "ahmeti" VALUES(76,5,'76:5','S’ka dyshim se të devotshmit do të pijnë nga gota që përziejra brenda saj është nga kafuri (aromatik).');
INSERT INTO "ahmeti" VALUES(76,6,'76:6','Është një burim prej të cilit pijnë robërit e All-llahut dhe e bartin atë ku të duan.');
INSERT INTO "ahmeti" VALUES(76,7,'76:7','Ata janë që zbatojnë premtimet e tyre dhe i frikësohen një dite dëmi i së cilës ka përmasa të mëdha.');
INSERT INTO "ahmeti" VALUES(76,8,'76:8','Ata janë që për hir të Tij u japin ushqim të varvfërve, jetimmëve dhe të zënëve robër.');
INSERT INTO "ahmeti" VALUES(76,9,'76:9','Ne po ju ushqejmë vetëm për hir të All-llahut dhe prej jush nuk kërkojmë ndonjë shpërblim e as falënderim.');
INSERT INTO "ahmeti" VALUES(76,10,'76:10','Ne i frikësohemi (dënimit) Zotit tonë në një ditë që fytyrat i bën të zymta dhe është sshumë vështirë.');
INSERT INTO "ahmeti" VALUES(76,11,'76:11','Po All-llahu i ruajti ata prej sherrit të asaj dite dhe u dhuroi shkëlqim në fytyra e gëzim të madh.');
INSERT INTO "ahmeti" VALUES(76,12,'76:12','Dhe për shkak se ata duran, i shpërbleu me Xhennet dhe me petka mëndafshi.');
INSERT INTO "ahmeti" VALUES(76,13,'76:13','Aty janë të mbështetur në koltukë dh aty nk shohin as diell (vapë) e as të ftohtë.');
INSERT INTO "ahmeti" VALUES(76,14,'76:14','Hijet e tyre (të pemëve) janë afër mbi ta dhe kalaveshët e pemëve janë të qaur shumë afër.');
INSERT INTO "ahmeti" VALUES(76,15,'76:15','Dhe atyre u bëhet shërbim me enë dhe me gota të tejdukshme.');
INSERT INTO "ahmeti" VALUES(76,16,'76:16','Të tejdukshme nga argjenti që ata (shërbëtorët) i përcaktuan të jenë sa duhet (të mëdha o të vogla).');
INSERT INTO "ahmeti" VALUES(76,17,'76:17','Dhe u jepet të pijnë aty gota të verës të përzier zenxhebilë (bimë aromatike).');
INSERT INTO "ahmeti" VALUES(76,18,'76:18','(nga) burimi aty që queht selsebil (i lehtë në të pimë).');
INSERT INTO "ahmeti" VALUES(76,19,'76:19','Dhe atyre u sillen për shërbim djelmosha që përherë janë të tillë sa që kur t’i kundrosh të duken si margairtarë të dërdhur.');
INSERT INTO "ahmeti" VALUES(76,20,'76:20','Dhe kur të shikosh sty, sheh begati të mëdha e zotërim (pronë, pauri) të madh.');
INSERT INTO "ahmeti" VALUES(76,21,'76:21','Kanë të veshura petka mëndafshi të hollë e të gjelbër (atllas) dhe të mëndafshit të trashë (brokat); janë të stoliur me bylyzykë të argjentë dhe Zoti i tyre ujep të pijnë pije të pastër.');
INSERT INTO "ahmeti" VALUES(76,22,'76:22','Ky është shpërblimi juaji, sepse angazhimi juaji ka qenë i pranishëm.');
INSERT INTO "ahmeti" VALUES(76,23,'76:23','Ne të shpallëm ty Kur’anin në intervale (pjesë-pjesë).');
INSERT INTO "ahmeti" VALUES(76,24,'76:24','Andaj ti bëhu i durueshëm deri në vendimin e Zotit tënd e mos dëgjo as mëkatarë, as jobesimtarë.');
INSERT INTO "ahmeti" VALUES(76,25,'76:25','Adhuroje Zotin tënd mëngjes e mbrëmje.');
INSERT INTO "ahmeti" VALUES(76,26,'76:26','Dhe falu për hirë të Tij në një kohë të natës, mandej adhuroje Atë edhe natën më gjatë.');
INSERT INTO "ahmeti" VALUES(76,27,'76:27','Vërtet, ata (idhujtarët) e duan shumë këtë jetë dhe e zënë asgjë ditën e rëndë që i pret.');
INSERT INTO "ahmeti" VALUES(76,28,'76:28','Ne i krijuam ata dhe Ne e përsosëm krijimin e tyre, e sikur të duam, Ne i zhdukim ata e sjellim të tjerë më të mirë se ata.');
INSERT INTO "ahmeti" VALUES(76,29,'76:29','Vërtet, këto janë një këshillë, e kush do e merr rrugën që e çon te Zoti i tij.');
INSERT INTO "ahmeti" VALUES(76,30,'76:30','Po ju nuk mund të doni gjë, pos nëse do All-llahu, e All-llahu është shumë i dijshëm, shumë i urtë.');
INSERT INTO "ahmeti" VALUES(76,31,'76:31','Ai atë që do, e shtie në mëshirën e Vet, kurse për zullumqarët ka përgatitur dënim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(77,1,'77:1','Pasha erërat që fryejnë njëpasnjë (pandërprerë)!');
INSERT INTO "ahmeti" VALUES(77,2,'77:2','Që janë shumë të stuhishme e shkaktojnë furtunë.');
INSERT INTO "ahmeti" VALUES(77,3,'77:3','Pasha engjëjt që u është besuar shpërndarja e reve dhe që i shpërndajnë.');
INSERT INTO "ahmeti" VALUES(77,4,'77:4','Dhe që ndajnë qartë (të vërtetën nga e pavërteta).');
INSERT INTO "ahmeti" VALUES(77,5,'77:5','Dhe ata që sjellin shpallje (libra të Zotit, te pejgamberët).');
INSERT INTO "ahmeti" VALUES(77,6,'77:6','Si arsyetim ose si vërejtje.');
INSERT INTO "ahmeti" VALUES(77,7,'77:7','S’ka dyshim se ajo që premtoheni ka për të ndodh pa tjetër.');
INSERT INTO "ahmeti" VALUES(77,8,'77:8','Kur yjet të shuhen.');
INSERT INTO "ahmeti" VALUES(77,9,'77:9','Dhe kur qielli të hapet,');
INSERT INTO "ahmeti" VALUES(77,10,'77:10','Dhe kur kodrat të copëtohen,');
INSERT INTO "ahmeti" VALUES(77,11,'77:11','Dhe kur të dërguarve u është caktuar koha?');
INSERT INTO "ahmeti" VALUES(77,12,'77:12','E për cilën ditë atyre u është caktuar koha?');
INSERT INTO "ahmeti" VALUES(77,13,'77:13','Për ditën e gjykimit (kur drejtësia e Zotit ndan të mirën nga e keqja).');
INSERT INTO "ahmeti" VALUES(77,14,'77:14','E ku e di ti se çka është dita e gjykimit?');
INSERT INTO "ahmeti" VALUES(77,15,'77:15','Atë ditë është shkatërrim për ata që përgënjeshtruan.');
INSERT INTO "ahmeti" VALUES(77,16,'77:16','A nuk i kemi është shkatërrim Ne popujt më të hershëm?');
INSERT INTO "ahmeti" VALUES(77,17,'77:17','Dhe pastaj pas tyre ua shoqëruam të tjerë më të vonshëm.');
INSERT INTO "ahmeti" VALUES(77,18,'77:18','Ne ashtu bëjmë me kriminelët.');
INSERT INTO "ahmeti" VALUES(77,19,'77:19','Atë ditë është mjerim i madh për ata që përgënjeshtruan.');
INSERT INTO "ahmeti" VALUES(77,20,'77:20','A nuk u kemi krijuar juve nga një lëng i dobët?');
INSERT INTO "ahmeti" VALUES(77,21,'77:21','Dhe atë e vendosëm në një vend të sigurt.');
INSERT INTO "ahmeti" VALUES(77,22,'77:22','Për deri në një kohë të caktuar.');
INSERT INTO "ahmeti" VALUES(77,23,'77:23','Dhe Ne bëmë përcaktimin e krijimit. Sa përcaktues të mirë jemi!');
INSERT INTO "ahmeti" VALUES(77,24,'77:24','Atë ditë është shkatërrim për ata që nuk e njohën fuqinë tonë.');
INSERT INTO "ahmeti" VALUES(77,25,'77:25','A nuk e bëmë Ne tokën që në gjirin e vet mban,');
INSERT INTO "ahmeti" VALUES(77,26,'77:26','Të gjallë dhe të vdekur?');
INSERT INTO "ahmeti" VALUES(77,27,'77:27','Dhe në të kemi vënë kodra të forta e të larta dhe ju dhamë të pini ujë që ua shuan etjen.');
INSERT INTO "ahmeti" VALUES(77,28,'77:28','Atë ditë është mjerim për ata që përgënjeshtruan,.');
INSERT INTO "ahmeti" VALUES(77,29,'77:29','(u thuhet) Shkoni te ai (Xhehennemi) që ju e konsuderuat rrenë.');
INSERT INTO "ahmeti" VALUES(77,30,'77:30','Shkoni te ajo hije (mjegull) që ka tri degëzime.');
INSERT INTO "ahmeti" VALUES(77,31,'77:31','Që nuk bën as hije, as nuk mbron prej flakës.');
INSERT INTO "ahmeti" VALUES(77,32,'77:32','Ai (Xhehennemi) hedh gaca të mëdha si ndonjë kështjellë.');
INSERT INTO "ahmeti" VALUES(77,33,'77:33','E që janë si të ishin deve të verdha (shumë të shpejta).');
INSERT INTO "ahmeti" VALUES(77,34,'77:34','Atë ditë është shkatërrim për mohuesit.');
INSERT INTO "ahmeti" VALUES(77,35,'77:35','Kjo është një ditë që ata nuk flasin.');
INSERT INTO "ahmeti" VALUES(77,36,'77:36','Atyre as nuk u lejohet që të arsyetohen.');
INSERT INTO "ahmeti" VALUES(77,37,'77:37','Atë ditë, mjerë ata që përgënjeshtruan!');
INSERT INTO "ahmeti" VALUES(77,38,'77:38','Kjo është dita e gjykimit, u kemi tubuar juve dhe të mëparshmit.');
INSERT INTO "ahmeti" VALUES(77,39,'77:39','Dhe nëse keni ndonjë dredhi, atëherë bëni dredhira!');
INSERT INTO "ahmeti" VALUES(77,40,'77:40','Atë ditë është shkatërrim për ata që nuk besuan.');
INSERT INTO "ahmeti" VALUES(77,41,'77:41','Dhe, nuk ka dyshim se të devotshmit do të jenë nën hije pranë burimeve,');
INSERT INTO "ahmeti" VALUES(77,42,'77:42','Dhe kanë pemë çfarë të dëshirojnë.');
INSERT INTO "ahmeti" VALUES(77,43,'77:43','Hani e pini shijshëm e me ëndje, për atë e keni vepruar.');
INSERT INTO "ahmeti" VALUES(77,44,'77:44','Ne kështu i shpërblejmë mirëbërësit.');
INSERT INTO "ahmeti" VALUES(77,45,'77:45','Atë ditë është mjerim për përgënjeshtuesit.');
INSERT INTO "ahmeti" VALUES(77,46,'77:46','Ju (jobesimtarë në dynja) hani e kënaquni përpak kohë, sepse vërtet ju jeni rrebelë.');
INSERT INTO "ahmeti" VALUES(77,47,'77:47','Atë ditë është mjerim për ata që mohuan të mirat e Zotit.');
INSERT INTO "ahmeti" VALUES(77,48,'77:48','Dhe kur atyre u thuhej: “Përkuluni!”, ata nuk përkuleshin.');
INSERT INTO "ahmeti" VALUES(77,49,'77:49','Atë ditë është mjerim për ata që përgënjeshtuan.');
INSERT INTO "ahmeti" VALUES(77,50,'77:50','Pra, cilës fjale (libër) pas këtij (Kur’anit) i besojnë?');
INSERT INTO "ahmeti" VALUES(78,1,'78:1','Për çka ata i bëjnë pyetje njëri-tjetri?');
INSERT INTO "ahmeti" VALUES(78,2,'78:2','Për lajmin e madh e tronditës.');
INSERT INTO "ahmeti" VALUES(78,3,'78:3','Në të cilin ata janë në kundërshti.');
INSERT INTO "ahmeti" VALUES(78,4,'78:4','Jo, të mos pyesin, sepse ata do ta kuptojnë!');
INSERT INTO "ahmeti" VALUES(78,5,'78:5','Përsëri Jo, se ata do ta kuptojnë!');
INSERT INTO "ahmeti" VALUES(78,6,'78:6','A nuk e bëmë Ne tokën të përshtatshme (për jetë),');
INSERT INTO "ahmeti" VALUES(78,7,'78:7','Ndërsa kodrat shtylla?');
INSERT INTO "ahmeti" VALUES(78,8,'78:8','Dhe Ne u krijuam juve në çifte.');
INSERT INTO "ahmeti" VALUES(78,9,'78:9','Dhe gjumin tuaj ua bëmë për pushim.');
INSERT INTO "ahmeti" VALUES(78,10,'78:10','Dhe natën ua bëmë mbulesë (si rrobet).');
INSERT INTO "ahmeti" VALUES(78,11,'78:11','Ndërsa ditën e bëmë për gjallërim.');
INSERT INTO "ahmeti" VALUES(78,12,'78:12','Dhe Ne kemi ndërtuar mbi ju një shtatëshe të fortë.');
INSERT INTO "ahmeti" VALUES(78,13,'78:13','Dhe kemi vënë ndriçuesin që flakëron.');
INSERT INTO "ahmeti" VALUES(78,14,'78:14','Dhe prej reve të shtrydhura kemi lëshuar shi të madh.');
INSERT INTO "ahmeti" VALUES(78,15,'78:15','Që me të të rrisim drithëra e bimë.');
INSERT INTO "ahmeti" VALUES(78,16,'78:16','Edhe kopshte të dendura palë mbi palë.');
INSERT INTO "ahmeti" VALUES(78,17,'78:17','S’ka dyshim se dita e gjykimit është caktuar.');
INSERT INTO "ahmeti" VALUES(78,18,'78:18','Është dita që i fryhet surit, e ju vini grupe-grupe.');
INSERT INTO "ahmeti" VALUES(78,19,'78:19','Dhe qielli hapet e bëhet dyer-dyer.');
INSERT INTO "ahmeti" VALUES(78,20,'78:20','Dhe kodrat shkulen dhe bëhen pluhur (si valë rrezesh).');
INSERT INTO "ahmeti" VALUES(78,21,'78:21','E Xhehennemi është në pritë (në pusi).');
INSERT INTO "ahmeti" VALUES(78,22,'78:22','Është vendstrehim i arrogantëve.');
INSERT INTO "ahmeti" VALUES(78,23,'78:23','Aty do të mbesin për tërë kohën (pa mbarim).');
INSERT INTO "ahmeti" VALUES(78,24,'78:24','Aty nuk do të shijojnë as freskim, as ndonjë pije.');
INSERT INTO "ahmeti" VALUES(78,25,'78:25','Përveç ujë të valë e kalbësirë.');
INSERT INTO "ahmeti" VALUES(78,26,'78:26','Si ndëshkim i merituar.');
INSERT INTO "ahmeti" VALUES(78,27,'78:27','Ata ishin që nuk pritnin se do të jepin llogari.');
INSERT INTO "ahmeti" VALUES(78,28,'78:28','Dhe argumentet Tona i përgënjeshtruan më këmbëngulje.');
INSERT INTO "ahmeti" VALUES(78,29,'78:29','Ndërsa Ne çdo send e kemi ruajtur me shkrim të saktë.');
INSERT INTO "ahmeti" VALUES(78,30,'78:30','E ju pra, vuani, se Ne nuk do t’u shtojmë tjetër vetëm se vuajtje.');
INSERT INTO "ahmeti" VALUES(78,31,'78:31','S’ka dyshim se të devotshmit kanë fitime të mëdha.');
INSERT INTO "ahmeti" VALUES(78,32,'78:32','Kopshte e vreshta rrushi.');
INSERT INTO "ahmeti" VALUES(78,33,'78:33','Edhe vasha të një moshe.');
INSERT INTO "ahmeti" VALUES(78,34,'78:34','Edhe gota të mbushura plot verë.');
INSERT INTO "ahmeti" VALUES(78,35,'78:35','Aty nuk dëgjojnë fjalë të kota, as gënjeshtra.');
INSERT INTO "ahmeti" VALUES(78,36,'78:36','Shpërblime e dhurata të shumta dhënë nga Zoti yt.');
INSERT INTO "ahmeti" VALUES(78,37,'78:37','Zoti i qiejve e i tokës, dhe çka ka ndërmjet tyre, që është mëshirues, të cilit nuk kanë të drejtë t’i bëjnë vërejtje.');
INSERT INTO "ahmeti" VALUES(78,38,'78:38','Atë ditë Xibrili dhe engjëjt qëndrojnë të rreshtuar, askush nuk flet pos atij që e lejon Mëshiruesi, dhe i cili e thotë të vërtetën.');
INSERT INTO "ahmeti" VALUES(78,39,'78:39','Ajo është dita e vërtetë, e kush dëshiron le të kthehet rrugës te Zoti i vet.');
INSERT INTO "ahmeti" VALUES(78,40,'78:40','Ne ua tërhoqëm juve (idhujtarëve) vërejtjen për një dënim të afërt, në ditën kur njeriu (besimtar) shikon se çka i kanë sjellë duart e veta, e jobesimtari thotë: “Ah, sa mirë do të ishte për mua sikur të isha dhe"!');
INSERT INTO "ahmeti" VALUES(79,1,'79:1','Pasha ata (engjëj) që marrin shpirtrat me rrëmbim (përmbytshëm)!');
INSERT INTO "ahmeti" VALUES(79,2,'79:2','Dhe ata që marrin shpirtra me lehtësim!');
INSERT INTO "ahmeti" VALUES(79,3,'79:3','Dhe ata që notojnë me shpejtësi!');
INSERT INTO "ahmeti" VALUES(79,4,'79:4','Dhe nxitësit të cilët me shpejtësi në vendin e vet i çojnë!');
INSERT INTO "ahmeti" VALUES(79,5,'79:5','Dhe pasha ata që çdo çështje rregullojnë!');
INSERT INTO "ahmeti" VALUES(79,6,'79:6','Ditën që vrullshëm bëhet dridhja.');
INSERT INTO "ahmeti" VALUES(79,7,'79:7','Që pas saj pason tjetra.');
INSERT INTO "ahmeti" VALUES(79,8,'79:8','Atë ditë zemrat janë të tronditura.');
INSERT INTO "ahmeti" VALUES(79,9,'79:9','Shikimet e tyre janë të poshtruara (frikësuara).');
INSERT INTO "ahmeti" VALUES(79,10,'79:10','Ata (idhujtarët në dynja) thonë: “A thua do të bëhemi përsëri kështu si jemi?”');
INSERT INTO "ahmeti" VALUES(79,11,'79:11','A edhe pasi të jemi bërë eshtra të kalbur?”');
INSERT INTO "ahmeti" VALUES(79,12,'79:12','Ata thanë: “Atëherë, ai kthim do të jetë dëshpërues për ne!”');
INSERT INTO "ahmeti" VALUES(79,13,'79:13','Po ajo (ringjallja) nuk do të jetë tjetër pos një thirrje (klithje).');
INSERT INTO "ahmeti" VALUES(79,14,'79:14','Dhe ja, ata mbi sipërfaqen e tokës.');
INSERT INTO "ahmeti" VALUES(79,15,'79:15','A të ka ardhur ty rrëfimi i Musait?');
INSERT INTO "ahmeti" VALUES(79,16,'79:16','Kur Zoti i tij e pat thirrur në luginën e bekuar Tuva (rrënzë kodrës Turi Sina).');
INSERT INTO "ahmeti" VALUES(79,17,'79:17','Shko te faraoni se vërtet ai e ka tepruar.');
INSERT INTO "ahmeti" VALUES(79,18,'79:18','Dhe thuaj: “E ke ndër mend të pastrohesh?”');
INSERT INTO "ahmeti" VALUES(79,19,'79:19','Unë të udhëzoi te Zoti yt e ti t’ia keshë frikën Atij!');
INSERT INTO "ahmeti" VALUES(79,20,'79:20','Dhe ai ia tregoi mrekullinë e madhe.');
INSERT INTO "ahmeti" VALUES(79,21,'79:21','Po ai e përgënjeshtroi dhe e kundërshtoi.');
INSERT INTO "ahmeti" VALUES(79,22,'79:22','Pastaj u kthye prapa dhe iku me të shpejtë.');
INSERT INTO "ahmeti" VALUES(79,23,'79:23','I tuboi (të gjithë të vetët) dhe u mbajti fjalim.');
INSERT INTO "ahmeti" VALUES(79,24,'79:24','E u tha: “Unë jam zoti juaj më i lartë!”');
INSERT INTO "ahmeti" VALUES(79,25,'79:25','Atëherë tmerrshëm e dënoi atë, për këtë të fundit dhe për atë të parën.');
INSERT INTO "ahmeti" VALUES(79,26,'79:26','Në këtë ndëshkim ka përvojë mësimi për atë që frikësohet.');
INSERT INTO "ahmeti" VALUES(79,27,'79:27','A është më i rëndë rëndë krijimi juaj apo ai i qiellit? E Ai e ngriti atë!');
INSERT INTO "ahmeti" VALUES(79,28,'79:28','Ngriti kuoën e tij dhe e përsosi atë.');
INSERT INTO "ahmeti" VALUES(79,29,'79:29','Natën ia errësoi e ditën ia ndriçoi.');
INSERT INTO "ahmeti" VALUES(79,30,'79:30','E pastaj tokën e sheshoi.');
INSERT INTO "ahmeti" VALUES(79,31,'79:31','Dhe prej saj nxori ujin e saj dhe kullosat e saj.');
INSERT INTO "ahmeti" VALUES(79,32,'79:32','Kurse kodrat ia përforcoi.');
INSERT INTO "ahmeti" VALUES(79,33,'79:33','Si furnizim për ju dhe për kafshët tuaja.');
INSERT INTO "ahmeti" VALUES(79,34,'79:34','E kur të vijë vala e madhe (kijameti),');
INSERT INTO "ahmeti" VALUES(79,35,'79:35','Atë ditë njeriu përkujton se çka ka punuar.');
INSERT INTO "ahmeti" VALUES(79,36,'79:36','E Xhehennemi shfaqet sheshazi për atë që e sheh.');
INSERT INTO "ahmeti" VALUES(79,37,'79:37','E për sa u përket atij që ka tepruar.');
INSERT INTO "ahmeti" VALUES(79,38,'79:38','Dhe i dha përparësi jetës së kësaj bote,');
INSERT INTO "ahmeti" VALUES(79,39,'79:39','Xhehennemi do të jetë vendi i tij.');
INSERT INTO "ahmeti" VALUES(79,40,'79:40','E kush iu frikësua paraqitjes para Zotit të vet dhe ndaloi veten prej epsheve,');
INSERT INTO "ahmeti" VALUES(79,41,'79:41','Xhenneti është vendi i tij.');
INSERT INTO "ahmeti" VALUES(79,42,'79:42','Ty të pyesin për kijametin: “Kur do të ndodhë ai?!”');
INSERT INTO "ahmeti" VALUES(79,43,'79:43','Po, në çfarë gjendje je ti t’ua përkutosh atë?');
INSERT INTO "ahmeti" VALUES(79,44,'79:44','Vetëm te Zoti yt është dijenia për të.');
INSERT INTO "ahmeti" VALUES(79,45,'79:45','Ti je vetëm qortues i atij që ia ka frikën Atij.');
INSERT INTO "ahmeti" VALUES(79,46,'79:46','Ditën kur ta përjetojnë atë, atyre (idhujtarëve) do t’u duket sikur nuk kan jetuar më tepër se një mbrëmje ose një mëngjes të saj.');
INSERT INTO "ahmeti" VALUES(80,1,'80:1','Ai vrenjti (fytyrën) dhe u kthye.');
INSERT INTO "ahmeti" VALUES(80,2,'80:2','Ngase atij i erdh i verbëri.');
INSERT INTO "ahmeti" VALUES(80,3,'80:3','E ku mund ta dish ti, ndoshta ai do të pastrohet.');
INSERT INTO "ahmeti" VALUES(80,4,'80:4','Apo do të këshillohet dhe këshilla do t’i bëjë dobi!');
INSERT INTO "ahmeti" VALUES(80,5,'80:5','E ai që nuk ndien nevojë (për Zotin) pse ka pasuri,');
INSERT INTO "ahmeti" VALUES(80,6,'80:6','Ti atij i vë veshin (i drejtohesh)!');
INSERT INTO "ahmeti" VALUES(80,7,'80:7','Po ti nuk ke përgjegjësi, pse ai nuk pastrohet.');
INSERT INTO "ahmeti" VALUES(80,8,'80:8','Ndërsa ai që nxiton dhe vjen te ti,');
INSERT INTO "ahmeti" VALUES(80,9,'80:9','Dhe meqë ai frikësohet,');
INSERT INTO "ahmeti" VALUES(80,10,'80:10','E ti nuk e zë asgjë (dhe nuk ia vë veshin fjalës së tij),');
INSERT INTO "ahmeti" VALUES(80,11,'80:11','Jo ashtu! Në të vërtetë këto janë këshilla.');
INSERT INTO "ahmeti" VALUES(80,12,'80:12','Kush do merr mesim nga kjo (nga Kur’ani).');
INSERT INTO "ahmeti" VALUES(80,13,'80:13','Është këshillë në flet të çmueshme.');
INSERT INTO "ahmeti" VALUES(80,14,'80:14','Me vlerë të lartë e të ruajtur.');
INSERT INTO "ahmeti" VALUES(80,15,'80:15','Në duar të udhëtuesve (engjëjve udhëtues mes All-llahut e njerëzve).');
INSERT INTO "ahmeti" VALUES(80,16,'80:16','Të nderuar e të ruajtur.');
INSERT INTO "ahmeti" VALUES(80,17,'80:17','Qoftë mallkuar njeriu, sa mohues i fortë është ai!');
INSERT INTO "ahmeti" VALUES(80,18,'80:18','Po prej çkafi e krijoi Ai atë?');
INSERT INTO "ahmeti" VALUES(80,19,'80:19','Atë e krijoi prej një pike uji dhe e përgatiti.');
INSERT INTO "ahmeti" VALUES(80,20,'80:20','Pastaj atij ia lehtësoi rrugën.');
INSERT INTO "ahmeti" VALUES(80,21,'80:21','Mandej atë e bëri të vdesë dhe atij i bëri varr (të varrosët).');
INSERT INTO "ahmeti" VALUES(80,22,'80:22','Pastaj kur të dojë Ai e ringjallë atë.');
INSERT INTO "ahmeti" VALUES(80,23,'80:23','Jo! Ai nuk zbatoi atë që urdhëroi Ai.');
INSERT INTO "ahmeti" VALUES(80,24,'80:24','Njeriu le të shikoje ushqimin e vet.');
INSERT INTO "ahmeti" VALUES(80,25,'80:25','Ne lëshuam shi të mjaftueshëm.');
INSERT INTO "ahmeti" VALUES(80,26,'80:26','Pastaj e çamë tokën sipas nevojës së bimës,');
INSERT INTO "ahmeti" VALUES(80,27,'80:27','Dhe bëmë që në të të mbijnë drithëra');
INSERT INTO "ahmeti" VALUES(80,28,'80:28','Edhe rrush e perime,');
INSERT INTO "ahmeti" VALUES(80,29,'80:29','Edhe ullinj e hurma,');
INSERT INTO "ahmeti" VALUES(80,30,'80:30','Edhe kopshte të dendura,');
INSERT INTO "ahmeti" VALUES(80,31,'80:31','Dhe pemë e kullosa,');
INSERT INTO "ahmeti" VALUES(80,32,'80:32','Si mjete gjallërimi për ju dhe për bagëtinë tuaj.');
INSERT INTO "ahmeti" VALUES(80,33,'80:33','E kur të vijë ushtima (krisma e kijametit);');
INSERT INTO "ahmeti" VALUES(80,34,'80:34','Atë ditë njeriu ikën prej vëllait të vet,');
INSERT INTO "ahmeti" VALUES(80,35,'80:35','Prej nënës dhe prej babait të vet,');
INSERT INTO "ahmeti" VALUES(80,36,'80:36','Prej gruas dhe prej fëmjëve të vet.');
INSERT INTO "ahmeti" VALUES(80,37,'80:37','Atë ditë secilit njeri i mjafton çështja e vet.');
INSERT INTO "ahmeti" VALUES(80,38,'80:38','Atë ditë do të ketë fytyra të shndritura,');
INSERT INTO "ahmeti" VALUES(80,39,'80:39','Të buzëqeshura e të gëzuara.');
INSERT INTO "ahmeti" VALUES(80,40,'80:40','Atë ditë do të ketë edhe fytyra të pluhëruara,');
INSERT INTO "ahmeti" VALUES(80,41,'80:41','Që i ka mbuluar errësira e zezë,');
INSERT INTO "ahmeti" VALUES(80,42,'80:42','E të tillët janë ata mohuesit, mëkatarët.');
INSERT INTO "ahmeti" VALUES(81,1,'81:1','Kur dielli të jetë mbështjellur (dhe errësohet),');
INSERT INTO "ahmeti" VALUES(81,2,'81:2','Dhe kur yjet të kenë rënë (e shkapërderdhur),');
INSERT INTO "ahmeti" VALUES(81,3,'81:3','Dhe kur kodrat të kenë udhëtuar (e bërë pluhur në ajër),');
INSERT INTO "ahmeti" VALUES(81,4,'81:4','Dhe kur devetë e shtrenjta të lihen pa bari në fushë,');
INSERT INTO "ahmeti" VALUES(81,5,'81:5','Dhe kur egërsirat të jenë bashkuar (tubuar).');
INSERT INTO "ahmeti" VALUES(81,6,'81:6','Dhe kur detet të vlojnë si zjarr i flakruar,');
INSERT INTO "ahmeti" VALUES(81,7,'81:7','Dhe kur shpirtrat të jenë bashkuar.');
INSERT INTO "ahmeti" VALUES(81,8,'81:8','Dhe kur të pyeten ato vajza të varrosura të gjalla,');
INSERT INTO "ahmeti" VALUES(81,9,'81:9','Për çfarë mëkati ato janë mbytur,');
INSERT INTO "ahmeti" VALUES(81,10,'81:10','Dhe kur fletushkat të jenë shpaluar,');
INSERT INTO "ahmeti" VALUES(81,11,'81:11','Dhe kur qielli të jetë hequr,');
INSERT INTO "ahmeti" VALUES(81,12,'81:12','Dhe kur Xhehennemi të jetë ndezur fort,');
INSERT INTO "ahmeti" VALUES(81,13,'81:13','Dhe kur Xhenneti të jetë afruar,');
INSERT INTO "ahmeti" VALUES(81,14,'81:14','Atëbotë njeriu do të dijë se çka ka ofruar (të mirë ose të keqe).');
INSERT INTO "ahmeti" VALUES(81,15,'81:15','Betohem fuqimisht në yjet që tërhiqen (ditën, e ndriçojnë natën),');
INSERT INTO "ahmeti" VALUES(81,16,'81:16','Që udhëtojnë e fshihen (si dreni në strofkë),');
INSERT INTO "ahmeti" VALUES(81,17,'81:17','Pasha natën kur ajo ia beh me errësirë.');
INSERT INTO "ahmeti" VALUES(81,18,'81:18','Pasha agimin kur ia shkrep!');
INSERT INTO "ahmeti" VALUES(81,19,'81:19','S’ka dyshim se ai (Kur’ani) është thënie e të dërguarit fisnik (që e pranoi prej Zotit).');
INSERT INTO "ahmeti" VALUES(81,20,'81:20','Që është i fortë dhe që ka vend të lartë te Zoti i Arshit.');
INSERT INTO "ahmeti" VALUES(81,21,'81:21','Që është i respektuar atje (në mesin e engjëjve) dhe besnik.');
INSERT INTO "ahmeti" VALUES(81,22,'81:22','Nuk është i marrë shoku juaj (Muhammedi)!');
INSERT INTO "ahmeti" VALUES(81,23,'81:23','Dhe se ai e pat parë atë (Xhibrilin) në horizontin e qartë lindor.');
INSERT INTO "ahmeti" VALUES(81,24,'81:24','Dhe ai nuk është që në të fshehtën (shpalljen) ka dyshim (ose ua lë mangu).');
INSERT INTO "ahmeti" VALUES(81,25,'81:25','Dhe ai (Kur’ani) nuk është thënie e djallit të mallkuar!');
INSERT INTO "ahmeti" VALUES(81,26,'81:26','Pra, ku jeni, kah shkoni?');
INSERT INTO "ahmeti" VALUES(81,27,'81:27','Ai (Kur’ani) s’është tjetër pos këshillë për njerëzit!');
INSERT INTO "ahmeti" VALUES(81,28,'81:28','Për atë nga mesi juaj që dëshiron të gjendet në të vërtetën.');
INSERT INTO "ahmeti" VALUES(81,29,'81:29','Po ju nuk mundeni, por vetëm nëse dëshiron All-llahu, Zoti i botëve!');
INSERT INTO "ahmeti" VALUES(82,1,'82:1','E kur të çahet qielli.');
INSERT INTO "ahmeti" VALUES(82,2,'82:2','Dhe kur të shkapërderdhen yjet.');
INSERT INTO "ahmeti" VALUES(82,3,'82:3','Dhe kur të përzihen detet.');
INSERT INTO "ahmeti" VALUES(82,4,'82:4','Dhe kur të trazohen varret.');
INSERT INTO "ahmeti" VALUES(82,5,'82:5','Atëbotë secili njeri e din se çka punuar dhe çka ka lënë prapa!');
INSERT INTO "ahmeti" VALUES(82,6,'82:6','O ti njeri po ç’të mashtroi ty kundrejt Zotit tënd që është bujar e i urtë?');
INSERT INTO "ahmeti" VALUES(82,7,'82:7','I cili të krijoi, të përsosi dhe të drejtoi.');
INSERT INTO "ahmeti" VALUES(82,8,'82:8','Të formësoi në formën që Ai dëshiroi!');
INSERT INTO "ahmeti" VALUES(82,9,'82:9','Jo, nuk është ashtu. Ju përgënjeshtroni ditën e përgjegjësisë.');
INSERT INTO "ahmeti" VALUES(82,10,'82:10','Kurse ndaj jush janë përcjellësit!');
INSERT INTO "ahmeti" VALUES(82,11,'82:11','Janë shkrues të ndershëm te All-llahu.');
INSERT INTO "ahmeti" VALUES(82,12,'82:12','Ata e dinë se çka punoni.');
INSERT INTO "ahmeti" VALUES(82,13,'82:13','E s’ka dyshim se të mirët janë në kënaqësinë e përjetimeve (në Xhennet).');
INSERT INTO "ahmeti" VALUES(82,14,'82:14','Dhe se mëkatarët janë në Xhehennem.');
INSERT INTO "ahmeti" VALUES(82,15,'82:15','Aty futen ata ditën e gjykimit (të cilën nuk e besonin).');
INSERT INTO "ahmeti" VALUES(82,16,'82:16','Dhe nuk kanë të larguar prej tij.');
INSERT INTO "ahmeti" VALUES(82,17,'82:17','E ç’të mësoi ty se ç’është dita e gjykimit?');
INSERT INTO "ahmeti" VALUES(82,18,'82:18','Përsëri, ç’të mësoi ty se ç’është dita e gjykimit?');
INSERT INTO "ahmeti" VALUES(82,19,'82:19','Është dita kur askush, askujt nuk do të mund t’i ndihmojë asgjë; atë ditë e tërë çështja i takon vetëm All-llahut!');
INSERT INTO "ahmeti" VALUES(83,1,'83:1','Të mjerët ata që masin e peshojnë.');
INSERT INTO "ahmeti" VALUES(83,2,'83:2','Ata që kur matin prej njerëzve prej njerëzve, për vete e plotësojnë,');
INSERT INTO "ahmeti" VALUES(83,3,'83:3','E kur u matin të tjerëve ose u peshojnë, u lënë mangu.');
INSERT INTO "ahmeti" VALUES(83,4,'83:4','A nuk e donë të tillët se kanë për t’u ringjallur?');
INSERT INTO "ahmeti" VALUES(83,5,'83:5','Në një ditë të madhe,');
INSERT INTO "ahmeti" VALUES(83,6,'83:6','Në ditën kur njerëzit ngriten (prej varrezave) për të dalë para Zotit të botëve.');
INSERT INTO "ahmeti" VALUES(83,7,'83:7','Jo, të mos rrinë gafil! Se shënimet e veprave të mëkatarëve janë në Sixhin (në një libër shënimesh të të këqijave më të dëmshme).');
INSERT INTO "ahmeti" VALUES(83,8,'83:8','E, ku e di ti se ç’është sixhini?');
INSERT INTO "ahmeti" VALUES(83,9,'83:9','Ai është një libër i qendisur (në një qeli në fund të burgut).');
INSERT INTO "ahmeti" VALUES(83,10,'83:10','Atë ditë është shkatërrimi për gënjeshtarët.');
INSERT INTO "ahmeti" VALUES(83,11,'83:11','Të cilët nuk e besojnë ditën e përgjegjësisë.');
INSERT INTO "ahmeti" VALUES(83,12,'83:12','E atë ditë nuk e mohon kush, përveç atij që ka sharruar tepër në mosbesim e në punë të këqija.');
INSERT INTO "ahmeti" VALUES(83,13,'83:13','E që kur i lexohen atij argumentet Tona, thonë: “Legjenda të të parëve!”');
INSERT INTO "ahmeti" VALUES(83,14,'83:14','Jo, nuk është ashtu! Por të këqijat që i punuan, zemrat e tyre ua mbuluan.');
INSERT INTO "ahmeti" VALUES(83,15,'83:15','Jo dhe Jo! Atë ditë do të jenë të penguar prej (ta shohin) Zotit të tyre.');
INSERT INTO "ahmeti" VALUES(83,16,'83:16','Pastaj ata do të hyjnë në Xhehennem.');
INSERT INTO "ahmeti" VALUES(83,17,'83:17','Dhe do t’u thuhet: “Ky është ai të cilin e keni përgënjeshtruar!”');
INSERT INTO "ahmeti" VALUES(83,18,'83:18','Jo, nuk janë të njejtë! Se libri i të mirave gjendet në Il-lijjinë!');
INSERT INTO "ahmeti" VALUES(83,19,'83:19','E ku e di ti se çka është Ilijunë?');
INSERT INTO "ahmeti" VALUES(83,20,'83:20','Është një llibër i qendisur qartë.');
INSERT INTO "ahmeti" VALUES(83,21,'83:21','Të cilin e dëshmojnë engjëjt që janë më të të afërmit.');
INSERT INTO "ahmeti" VALUES(83,22,'83:22','E s’ka dyshim se vepërmirët janë në përjetime e kënaqësi (Xhennet).');
INSERT INTO "ahmeti" VALUES(83,23,'83:23','Të mbështetur në kolltukë vështrojnë.');
INSERT INTO "ahmeti" VALUES(83,24,'83:24','Në fytyrat e tyre mund të kuptosh kënaqësinë e përjetimeve.');
INSERT INTO "ahmeti" VALUES(83,25,'83:25','U jepet të pijnë pije në enë të mbyllura.');
INSERT INTO "ahmeti" VALUES(83,26,'83:26','Pije që në fund asaj i vjen era misk! E për shpërblim të tillë le të garojnë ata që lakmojnë të mirën.');
INSERT INTO "ahmeti" VALUES(83,27,'83:27','Dhe përzierja e asaj pije është në tensim (uji më i mirë).');
INSERT INTO "ahmeti" VALUES(83,28,'83:28','Krua nga i cili do të pijnë më të afërmit e Zotit.');
INSERT INTO "ahmeti" VALUES(83,29,'83:29','Mëkatarët ishin ata të cilët i përqeshnin ata që besuan.');
INSERT INTO "ahmeti" VALUES(83,30,'83:30','Dhe kur kalonin (besimtarët) pranë (idhujtarëve), ata ia bënin me sy njëri-tjetrit.');
INSERT INTO "ahmeti" VALUES(83,31,'83:31','E kur ktheheshin te familjet e veta, ktheheshin të kënaqur.');
INSERT INTO "ahmeti" VALUES(83,32,'83:32','Dhe kur i shihnin ata (besimtarët) janë të humbur!”');
INSERT INTO "ahmeti" VALUES(83,33,'83:33','Po Ne, nuk i caktuam ata (idhujtarët) si rojë të atyre (besimtarëve).');
INSERT INTO "ahmeti" VALUES(83,34,'83:34','E sot, (në ditën e gjykimit), ata që besuan do të tallen me jobesimtarët.');
INSERT INTO "ahmeti" VALUES(83,35,'83:35','Duke qëndruar të mbështetur (besimtarët) në kolltukë e duke shikuar.');
INSERT INTO "ahmeti" VALUES(83,36,'83:36','A thua a u shpërblyen jobesimtarët për atë që punuan? (Po)');
INSERT INTO "ahmeti" VALUES(84,1,'84:1','Kur të pëlcet qielli.');
INSERT INTO "ahmeti" VALUES(84,2,'84:2','T’i bindet urdhërit të Zotit të vet, ashtu si takoi.');
INSERT INTO "ahmeti" VALUES(84,3,'84:3','Dhe kur toka të shtrihet në gjerësinë e saj.');
INSERT INTO "ahmeti" VALUES(84,4,'84:4','E të nxjerr çka ka brenda e të çlirohet,');
INSERT INTO "ahmeti" VALUES(84,5,'84:5','Dhe t’i bindet Zotit të saj, ashtu si takoi.');
INSERT INTO "ahmeti" VALUES(84,6,'84:6','O ti njeri, vërtet ti përpiqesh me një përpjekje të madhe që çon te Zoti yt, e atë (përpjekje) do ta gjesh.');
INSERT INTO "ahmeti" VALUES(84,7,'84:7','E përsa i përket atij që libri i vet i jepet nga e djathta,');
INSERT INTO "ahmeti" VALUES(84,8,'84:8','Ai do të llogaritet me një llogari të lehtë,');
INSERT INTO "ahmeti" VALUES(84,9,'84:9','Do të kthehet te familja e vet i gëzuar.');
INSERT INTO "ahmeti" VALUES(84,10,'84:10','E përsa i përket atij që libri i vet i jepet pas shpinës,');
INSERT INTO "ahmeti" VALUES(84,11,'84:11','Ai do t’i ndjellë vetes shkatërrim,');
INSERT INTO "ahmeti" VALUES(84,12,'84:12','Dhe do të hyjë në zjarr të ndezur fort.');
INSERT INTO "ahmeti" VALUES(84,13,'84:13','Ai ka qenë i gëzuar në familjen e vet,');
INSERT INTO "ahmeti" VALUES(84,14,'84:14','Dhe ka menduar se nuk ka për t’u kthyer.');
INSERT INTO "ahmeti" VALUES(84,15,'84:15','Po, se, vërtet Zoti i tij gjithnjë e ka parë atë.');
INSERT INTO "ahmeti" VALUES(84,16,'84:16','Betohem në kuqërrimin e horizontit pas perëndimit,');
INSERT INTO "ahmeti" VALUES(84,17,'84:17','Dhe në natën dhe çka ajo tubon,');
INSERT INTO "ahmeti" VALUES(84,18,'84:18','Dhe në hënën kur ajo është e plotë.');
INSERT INTO "ahmeti" VALUES(84,19,'84:19','Ju patjetër do të përjetoni gjendje pas gjendjeje.');
INSERT INTO "ahmeti" VALUES(84,20,'84:20','E, çka kanë ata që nuk besojnë?');
INSERT INTO "ahmeti" VALUES(84,21,'84:21','Dhe kur u lexohet atyre Kur’ani, nuk përulen (nuk bien në sexhde)?');
INSERT INTO "ahmeti" VALUES(84,22,'84:22','Përkundrazi, ata që nuk besuan edhe përgënjeshtrojnë?');
INSERT INTO "ahmeti" VALUES(84,23,'84:23','E All-llahu e di më së miri se çka mbajnë fshehtë ata!');
INSERT INTO "ahmeti" VALUES(84,24,'84:24','Lajmëroji, pra me një dënim të dhembshëm.');
INSERT INTO "ahmeti" VALUES(84,25,'84:25','Me përjashtim të atyre që besuan e bënë vepra të mira, të cilët kanë shpërblim të vazhdueshëm!');
INSERT INTO "ahmeti" VALUES(85,1,'85:1','Pasha qiellin me plot yje,');
INSERT INTO "ahmeti" VALUES(85,2,'85:2','Pasha ditën e premtuar,');
INSERT INTO "ahmeti" VALUES(85,3,'85:3','Pasha atë që dëshmon dhe atë që dëshmohet!');
INSERT INTO "ahmeti" VALUES(85,4,'85:4','Mallkuar qofshin ata të zotët e hendeqeve,');
INSERT INTO "ahmeti" VALUES(85,5,'85:5','Të zjarrit më plot lëndë djegëse,');
INSERT INTO "ahmeti" VALUES(85,6,'85:6','Kur ata rrinin ulur rreth tij,');
INSERT INTO "ahmeti" VALUES(85,7,'85:7','Dhe dëshmonin atë që bënin me besimtarët.');
INSERT INTO "ahmeti" VALUES(85,8,'85:8','E nuk patën pse t’i urrejë, përveç që e besuan All-llahun, ngadhënjyesin, të lavdëruarin,');
INSERT INTO "ahmeti" VALUES(85,9,'85:9','Të cilit i takon sundimi i qiejve e i tokës;, e All-llahu është dëshmitar i çdo sendi.');
INSERT INTO "ahmeti" VALUES(85,10,'85:10','Është i sigurt se ata që i sprovuan besimtarët dhe besimtaret dhe nuk u penduan; ata kanë dënimin e Xhehennemit dhe dënimin e djegies.');
INSERT INTO "ahmeti" VALUES(85,11,'85:11','Ata që besuan dhe bënë vepra të mira, ata kanë Xhennete nën të cilët burojnë lumenj. e ai është suksesi më i madh.');
INSERT INTO "ahmeti" VALUES(85,12,'85:12','Është e vërtetë se goditja e Zotit tënd është e fortë.');
INSERT INTO "ahmeti" VALUES(85,13,'85:13','Në të vërtetë, Ai është që e fillon (krijimin) dhe e rikthen.');
INSERT INTO "ahmeti" VALUES(85,14,'85:14','Ai është që falë mëkatet, është i dashur.');
INSERT INTO "ahmeti" VALUES(85,15,'85:15','Është i zoti i Arshit, të madhëruar!');
INSERT INTO "ahmeti" VALUES(85,16,'85:16','E punon atë që dëshiron.');
INSERT INTO "ahmeti" VALUES(85,17,'85:17','A të ka ardhur ty lajmi i ushtrive,');
INSERT INTO "ahmeti" VALUES(85,18,'85:18','Të faraonit dhe të Thmeudit?');
INSERT INTO "ahmeti" VALUES(85,19,'85:19','Por, këta që mohuan (idhujtarët), vazhdojnë me gënjeshtër.');
INSERT INTO "ahmeti" VALUES(85,20,'85:20','Po All-llahu ata i ka rrethuar (nuk mund t’i shpëtojnë)!');
INSERT INTO "ahmeti" VALUES(85,21,'85:21','Përkundrazi, (ata që gënjejnë), ai është Kur’an i famshëm.');
INSERT INTO "ahmeti" VALUES(85,22,'85:22','Në një pllakë të ruajtur mirë (në Levhi Mahfudhë).');
INSERT INTO "ahmeti" VALUES(86,1,'86:1','Pasha qiellin dhe yllin ndriçues (të natës)!');
INSERT INTO "ahmeti" VALUES(86,2,'86:2','E ku e di tise çka është tariku - trokitësi?');
INSERT INTO "ahmeti" VALUES(86,3,'86:3','Ai është ylli që shpon (errësirën me dritë).');
INSERT INTO "ahmeti" VALUES(86,4,'86:4','Nuk ka asnjëri njeri që ndaj tij të mos ketë përcjellës.');
INSERT INTO "ahmeti" VALUES(86,5,'86:5','Le të shikojë njeriu se prej çkahit është krijuar?');
INSERT INTO "ahmeti" VALUES(86,6,'86:6','Ai është krijuar prej një uji që hidhet fuqishëm,');
INSERT INTO "ahmeti" VALUES(86,7,'86:7','E që del nga kurrizi i shpinës dhe dërrasa e gjoksit.');
INSERT INTO "ahmeti" VALUES(86,8,'86:8','E s’ka dyshim se Ai ka fuqi ta rikthejë në jetë atë.');
INSERT INTO "ahmeti" VALUES(86,9,'86:9','Ditën kur do të gjurmohen të fshehtat.');
INSERT INTO "ahmeti" VALUES(86,10,'86:10','E ai (njeriu) nuk ka forcë as ndihmëtarë.');
INSERT INTO "ahmeti" VALUES(86,11,'86:11','Pasha qiellin që kohë pas kohe kthehet me shi.');
INSERT INTO "ahmeti" VALUES(86,12,'86:12','Edhe kohën që qahet për bimë.');
INSERT INTO "ahmeti" VALUES(86,13,'86:13','Është e vërtetë se ai (Kur’ani) është fjalë që dallon - gjykon.');
INSERT INTO "ahmeti" VALUES(86,14,'86:14','Nuk është ai i parëndësishëm (lojë e kotë)!');
INSERT INTO "ahmeti" VALUES(86,15,'86:15','Ata vërtetë bëjnë kurtha.');
INSERT INTO "ahmeti" VALUES(86,16,'86:16','Po edhe Unë përgatis dështimin e kurtheve të tyre!');
INSERT INTO "ahmeti" VALUES(86,17,'86:17','E ti jobesimtarët jepju afat, afatizoi ata pak.');
INSERT INTO "ahmeti" VALUES(87,1,'87:1','Madhëroje emrin e Zotin tënd të Lartë!');
INSERT INTO "ahmeti" VALUES(87,2,'87:2','I cili krijoi dhe përsosi,');
INSERT INTO "ahmeti" VALUES(87,3,'87:3','Dhe i cili përcaktoi e orientoi,');
INSERT INTO "ahmeti" VALUES(87,4,'87:4','Dhe i cili mundësoi mbirjen e kullosës;');
INSERT INTO "ahmeti" VALUES(87,5,'87:5','E pastaj atë e bëri mbeturinë të zezë.');
INSERT INTO "ahmeti" VALUES(87,6,'87:6','Ne do të lexojmë ty e nuk do të harrosh (Kur’anin).');
INSERT INTO "ahmeti" VALUES(87,7,'87:7','Përveç asaj që All-llahu dëshiron. Ai e di të haptën e të fshehtën.');
INSERT INTO "ahmeti" VALUES(87,8,'87:8','Dhe Ne do ta lehtësojmë ty më të lehtën.');
INSERT INTO "ahmeti" VALUES(87,9,'87:9','Pra këshillo aty ku këshilla bën dobi.');
INSERT INTO "ahmeti" VALUES(87,10,'87:10','Do të këshillohet ai që ka frikë,');
INSERT INTO "ahmeti" VALUES(87,11,'87:11','Ndërsa do të shmanget më i keqi,');
INSERT INTO "ahmeti" VALUES(87,12,'87:12','I cili do të hyjë në zjarrin e madh.');
INSERT INTO "ahmeti" VALUES(87,13,'87:13','Pastaj nuk do të vdesë në të, por as nuk do të jetojë.');
INSERT INTO "ahmeti" VALUES(87,14,'87:14','Ka shpëtuar ai që është patruar.');
INSERT INTO "ahmeti" VALUES(87,15,'87:15','Që e përkujton emrin e Zotit të vet dhe falet.');
INSERT INTO "ahmeti" VALUES(87,16,'87:16','Po ju i jepni përparësi jetës së dunjasë,');
INSERT INTO "ahmeti" VALUES(87,17,'87:17','Ndërsa ahireti (amshimi) është më i mirë dhe e përjetshëm.');
INSERT INTO "ahmeti" VALUES(87,18,'87:18','Vërtet kjo ishte në broshurat e lashta:');
INSERT INTO "ahmeti" VALUES(87,19,'87:19','në broshurat e Ibrahimit dhe të Musait.');
INSERT INTO "ahmeti" VALUES(88,1,'88:1','A të erdhi ty lajmi për belanë që kaplon (kijameti)?');
INSERT INTO "ahmeti" VALUES(88,2,'88:2','Atë ditë sheh fytyra të përulura,');
INSERT INTO "ahmeti" VALUES(88,3,'88:3','Të lodhura e të rraskapitura (nga mundimi).');
INSERT INTO "ahmeti" VALUES(88,4,'88:4','Ato hyjnë në zjarrin të ndezur fort,');
INSERT INTO "ahmeti" VALUES(88,5,'88:5','U jepet të pijnë prej një kroi të valë.');
INSERT INTO "ahmeti" VALUES(88,6,'88:6','Ata kanë ushqim tjetër pos një barishte me ferra helmuese.');
INSERT INTO "ahmeti" VALUES(88,7,'88:7','Që as nuk jep fuqi, as nuk largon uri.');
INSERT INTO "ahmeti" VALUES(88,8,'88:8','Atë ditë dheh fytyra të ndritshme,');
INSERT INTO "ahmeti" VALUES(88,9,'88:9','Të kënaqura për veprimet e veta,');
INSERT INTO "ahmeti" VALUES(88,10,'88:10','Në Xhennet të shkallës së lartë,');
INSERT INTO "ahmeti" VALUES(88,11,'88:11','Aty nuk dëgjojnë fjalë të pakëndshme,');
INSERT INTO "ahmeti" VALUES(88,12,'88:12','Aty ka kroje që rrjedhin,');
INSERT INTO "ahmeti" VALUES(88,13,'88:13','Aty kanë shtretër të lartë,');
INSERT INTO "ahmeti" VALUES(88,14,'88:14','Aty kanë shtretër të lartë,');
INSERT INTO "ahmeti" VALUES(88,15,'88:15','Edhe jastekë të renduar për mbështetje,');
INSERT INTO "ahmeti" VALUES(88,16,'88:16','Edhe qilima të shtruar.');
INSERT INTO "ahmeti" VALUES(88,17,'88:17','A nuk i shikojnë devet se si janë krijuar?');
INSERT INTO "ahmeti" VALUES(88,18,'88:18','Edhe qiellin se si është ngritur lart!');
INSERT INTO "ahmeti" VALUES(88,19,'88:19','Edhe kodrat se si janë venduar!');
INSERT INTO "ahmeti" VALUES(88,20,'88:20','Edhe tokën se si është shtruar!');
INSERT INTO "ahmeti" VALUES(88,21,'88:21','E ti pra, këshillo, se je vetëm përkujtues,');
INSERT INTO "ahmeti" VALUES(88,22,'88:22','Ti ndaj tyre nuk je mbizotërues!');
INSERT INTO "ahmeti" VALUES(88,23,'88:23','Përveç atij zbrapset e nuk beson,');
INSERT INTO "ahmeti" VALUES(88,24,'88:24','All-llahu atë me vuajtje e dënon.');
INSERT INTO "ahmeti" VALUES(88,25,'88:25','Vetëm te Ne kthimin e kanë.');
INSERT INTO "ahmeti" VALUES(88,26,'88:26','Dhe vetëm detyrë Jona është llogaira e tyre.');
INSERT INTO "ahmeti" VALUES(89,1,'89:1','Pasha agimin!');
INSERT INTO "ahmeti" VALUES(89,2,'89:2','Pasha dhjetë netët!');
INSERT INTO "ahmeti" VALUES(89,3,'89:3','Pasha çiftin dhe pasha tekun!');
INSERT INTO "ahmeti" VALUES(89,4,'89:4','Pasha natën që shkon!');
INSERT INTO "ahmeti" VALUES(89,5,'89:5','A janë këto betime për të mençurin?');
INSERT INTO "ahmeti" VALUES(89,6,'89:6','A nuk e i ti se ç’bëri Zoti yt me Ad-in?');
INSERT INTO "ahmeti" VALUES(89,7,'89:7','Me banorët e Iremit me ndërtesa të larta?');
INSERT INTO "ahmeti" VALUES(89,8,'89:8','Që si ata (populli Ad) nuk është krijuar askush në tokë!');
INSERT INTO "ahmeti" VALUES(89,9,'89:9','Dhe me Themudin, të cilët shpuan shkëmbinj në luginën (kura),');
INSERT INTO "ahmeti" VALUES(89,10,'89:10','Dhe me faraonin i fortifikuar me tenda (ushtarake)!');
INSERT INTO "ahmeti" VALUES(89,11,'89:11','Të cilët e tepruan me krime në tokë.');
INSERT INTO "ahmeti" VALUES(89,12,'89:12','Dhe në të shtuan shkatërrimin.');
INSERT INTO "ahmeti" VALUES(89,13,'89:13','E All-llahu kundër tyre lëshoi lloj-lloj dënimesh.');
INSERT INTO "ahmeti" VALUES(89,14,'89:14','Pse Zoti yt është që përcjell (u rri në pritë).');
INSERT INTO "ahmeti" VALUES(89,15,'89:15','E njeriu, kur e sprovon Zoti i tij, e nderon dhe e begaton, ai thotë: “Zoti im më ka nderuar!”');
INSERT INTO "ahmeti" VALUES(89,16,'89:16','Por, kur për ta sprovuar ia pakëson furnizimin (e varfëron), ai thotë: “Zoti im më ka nënçmuar!”');
INSERT INTO "ahmeti" VALUES(89,17,'89:17','Jo, nuk është ashtu! Por (punoni edhe më zi) ju nuk përfillnit bonjakun,');
INSERT INTO "ahmeti" VALUES(89,18,'89:18','Nuk cytni njëri-tjetrin për ta ushqyer të varfërin,');
INSERT INTO "ahmeti" VALUES(89,19,'89:19','Dhe ju e hani me të madhe trashëgimin (padrejtë).');
INSERT INTO "ahmeti" VALUES(89,20,'89:20','Dhe pasurinë e doni së tepërmi.');
INSERT INTO "ahmeti" VALUES(89,21,'89:21','Jo, mosni ashtu! Kur të dridhet toka njëpasnjëshëm!');
INSERT INTO "ahmeti" VALUES(89,22,'89:22','Dhe kur të vijë Zoti yt dhe engjëjt qëndrojnë rradhë-rradhë!');
INSERT INTO "ahmeti" VALUES(89,23,'89:23','Dhe atë ditë sillet Xhehennemi, ditën kur përkujtohet njeriu, e ç’dobi i bën atij përkujtimi?');
INSERT INTO "ahmeti" VALUES(89,24,'89:24','Thotë: “O, i gjori unë, sikur të isha i parapërgatitur për jetën time!”');
INSERT INTO "ahmeti" VALUES(89,25,'89:25','Pra, ditën kur nuk dënon si Ai askush.');
INSERT INTO "ahmeti" VALUES(89,26,'89:26','Dhe nuk do të farkojë pranga si Ai askush.');
INSERT INTO "ahmeti" VALUES(89,27,'89:27','O shpirt i qetësuar!');
INSERT INTO "ahmeti" VALUES(89,28,'89:28','Kthehu te Zoti yt i kënaqur (për vehte) e kënaqës (për Zotin)!');
INSERT INTO "ahmeti" VALUES(89,29,'89:29','Hyr mes robërve të Mi!');
INSERT INTO "ahmeti" VALUES(89,30,'89:30','Dhe hyr në Xhennetin Tim!');
INSERT INTO "ahmeti" VALUES(90,1,'90:1','Betohem në këtë qytet!');
INSERT INTO "ahmeti" VALUES(90,2,'90:2','E ti je që gjendesh në këtë qytet!');
INSERT INTO "ahmeti" VALUES(90,3,'90:3','Betohem në prindin dhe në atë që lind prej tij!');
INSERT INTO "ahmeti" VALUES(90,4,'90:4','Vërtet, Ne e krijuam njeriun në vështirësi të njëpasnjëshme.');
INSERT INTO "ahmeti" VALUES(90,5,'90:5','A mendon ai, se atë nuk mund ta mposhtë askush?');
INSERT INTO "ahmeti" VALUES(90,6,'90:6','E thotë: “Unë kam shpenzuar shumë pasuri!”');
INSERT INTO "ahmeti" VALUES(90,7,'90:7','A mendon ai se atë askush nuk e ka parë?');
INSERT INTO "ahmeti" VALUES(90,8,'90:8','A nuk i dhamë Ne atij dy sy?');
INSERT INTO "ahmeti" VALUES(90,9,'90:9','Edhe gjuhë e dy buzë?');
INSERT INTO "ahmeti" VALUES(90,10,'90:10','Dhe i sqaruam atij për të dy rrugët.');
INSERT INTO "ahmeti" VALUES(90,11,'90:11','E ai nuk u hodh në përpjetëzen (rrugë e vështirë - qafëmalit).');
INSERT INTO "ahmeti" VALUES(90,12,'90:12','E ç’gjë të mësoi ty se ç’është Akabe (rruga e vështirë)?');
INSERT INTO "ahmeti" VALUES(90,13,'90:13','Është lirimi i një skllavi,');
INSERT INTO "ahmeti" VALUES(90,14,'90:14','Ose dhënia e ushqimit në kohën kur mbretëron uria:');
INSERT INTO "ahmeti" VALUES(90,15,'90:15','Ndonjë jetimi që është i afërt,');
INSERT INTO "ahmeti" VALUES(90,16,'90:16','Ose ndonjë të varfëri që e ka molisur varfëria.');
INSERT INTO "ahmeti" VALUES(90,17,'90:17','E pastaj të bëhej prej atyre që besuan, që këshilluan njëri-tjetrin për durim dhe që këshillluan për mëshirë (për ndihmë).');
INSERT INTO "ahmeti" VALUES(90,18,'90:18','Të tillët janë të zotët e anës së djathtë.');
INSERT INTO "ahmeti" VALUES(90,19,'90:19','Ndërsa ata që nuk i besuan argumentet Tona, ata jaanë të të majtës.');
INSERT INTO "ahmeti" VALUES(90,20,'90:20','Kundër tyre është zjarri i mbyllur.');
INSERT INTO "ahmeti" VALUES(91,1,'91:1','Pasha diellin dhe dritën e tij!');
INSERT INTO "ahmeti" VALUES(91,2,'91:2','Pasha hënën kur vjen pas tij!');
INSERT INTO "ahmeti" VALUES(91,3,'91:3','Pasha ditën kur ai (dielli) e shndrit!');
INSERT INTO "ahmeti" VALUES(91,4,'91:4','Pasha natën kur ajo e mbulon atë (dritën e diellit)!');
INSERT INTO "ahmeti" VALUES(91,5,'91:5','Pasha qiellin dhe Atë që e ndërtoi atë!');
INSERT INTO "ahmeti" VALUES(91,6,'91:6','Pasha tokën dhe Atë që e shtriu atë (e sheshoi)!');
INSERT INTO "ahmeti" VALUES(91,7,'91:7','Pasha njeriun dhe Atë që e krijoi atë!');
INSERT INTO "ahmeti" VALUES(91,8,'91:8','Dhe ia mësoi se cilat janë të këqijat dhe të mirat e tij.');
INSERT INTO "ahmeti" VALUES(91,9,'91:9','Pra, ka shpëtuar ai që e pastroi vetveten.');
INSERT INTO "ahmeti" VALUES(91,10,'91:10','E ka dështuar ai që e poshtëroi vetveten.');
INSERT INTO "ahmeti" VALUES(91,11,'91:11','Themudi përgënjeshtroi për shkak të arrogancës së vet.');
INSERT INTO "ahmeti" VALUES(91,12,'91:12','Kur më i keqi i tyre u ngrit me të shpejtë.');
INSERT INTO "ahmeti" VALUES(91,13,'91:13','e Pejgamberi atyre u pat thënë: “Kinie kujdes devenë e All-llahut dhe ndaj pengimit të saj që të pijë ujë.');
INSERT INTO "ahmeti" VALUES(91,14,'91:14','Ata nuk i besuan atij dhe atë (devenë) e therrën dhe Zoti i tyre për shkak të mëkatit të tyre ua rreshtoi ndëshkimet dhe i rrafshoi,');
INSERT INTO "ahmeti" VALUES(91,15,'91:15','Duke mos iu frikësuar prapavisë së atij (ndëshkimi).');
INSERT INTO "ahmeti" VALUES(92,1,'92:1','Pasha natën që me errësirë mbulon gjithësinë!');
INSERT INTO "ahmeti" VALUES(92,2,'92:2','Pasha ditën kur ajo shkrepëtin!');
INSERT INTO "ahmeti" VALUES(92,3,'92:3','Pasha Atë që krijoi mashkullin e femrën!');
INSERT INTO "ahmeti" VALUES(92,4,'92:4','Vërtet, veprimi juaj është i lloj-llojtë.');
INSERT INTO "ahmeti" VALUES(92,5,'92:5','E sa i përket atij që jep dhe ruhet,');
INSERT INTO "ahmeti" VALUES(92,6,'92:6','Dhe vërteton bindshëm për më të mirën,');
INSERT INTO "ahmeti" VALUES(92,7,'92:7','Ne do ta përgatisim atë për më të lehtën.');
INSERT INTO "ahmeti" VALUES(92,8,'92:8','E sa i përket atij që bën koprraci dhe ndien veten të pavarur (nga Zoti),');
INSERT INTO "ahmeti" VALUES(92,9,'92:9','Dhe që mohon atë më të mirën,');
INSERT INTO "ahmeti" VALUES(92,10,'92:10','Ne do ta përgatisim për më të vështirën.');
INSERT INTO "ahmeti" VALUES(92,11,'92:11','E pasuria e tij nuk do t’i bëjë dobi, kur ai të zhduket.');
INSERT INTO "ahmeti" VALUES(92,12,'92:12','Detyrë e jonë është vetëm të udhëzojmë.');
INSERT INTO "ahmeti" VALUES(92,13,'92:13','Dhe vetëm e Jona është bota tjetër si dhe kjo.');
INSERT INTO "ahmeti" VALUES(92,14,'92:14','Unë ua kam tërhequr vërejtjen për zjarrin të ndezur fort.');
INSERT INTO "ahmeti" VALUES(92,15,'92:15','Që aty nuk hyn tjëtërkush, pos atij që është më i prishuri.');
INSERT INTO "ahmeti" VALUES(92,16,'92:16','I cili përgënjeshtroi dhe u zmbraps.');
INSERT INTO "ahmeti" VALUES(92,17,'92:17','Ndërsa ai që është më i devotshëm do të jetë larg tij.');
INSERT INTO "ahmeti" VALUES(92,18,'92:18','Ai që e jep pasurinë e vet e pastrohet,');
INSERT INTO "ahmeti" VALUES(92,19,'92:19','E jo për ta shpërblyer ndokë që i ka bërë mirë më parë.');
INSERT INTO "ahmeti" VALUES(92,20,'92:20','Por vetëm për të fituar kënaqësinë e Zotit të vet, më të lartit,');
INSERT INTO "ahmeti" VALUES(92,21,'92:21','Për Zotin, ai do të gëzojë atë kënaqësi!');
INSERT INTO "ahmeti" VALUES(93,1,'93:1','Pasha paraditën!');
INSERT INTO "ahmeti" VALUES(93,2,'93:2','Pasha natën kur shtrinë errësirën!');
INSERT INTO "ahmeti" VALUES(93,3,'93:3','Zoti yt nuk të ka lënë, as nuk të ka përbuzur.');
INSERT INTO "ahmeti" VALUES(93,4,'93:4','Dhe se bota e ardhshme është shumë më e mirë për ty se e para.');
INSERT INTO "ahmeti" VALUES(93,5,'93:5','E Zoti yt do të jepë ty, e ti do të kënaqesh.');
INSERT INTO "ahmeti" VALUES(93,6,'93:6','A nuk të gjeti ty jetim, e Ai të bëri vend (të dha përkrahje).');
INSERT INTO "ahmeti" VALUES(93,7,'93:7','Dhe të gjeti të paudhëzuar e Ai të udhëzojë.');
INSERT INTO "ahmeti" VALUES(93,8,'93:8','dhe të gjeti të varfër, e Ai të begatoi.');
INSERT INTO "ahmeti" VALUES(93,9,'93:9','Pra, mos e përul jetimin!');
INSERT INTO "ahmeti" VALUES(93,10,'93:10','As lypësin mos e përzë!');
INSERT INTO "ahmeti" VALUES(93,11,'93:11','E me të mirat që të dha Zoti yt, trego (udhëzo njerëz)!');
INSERT INTO "ahmeti" VALUES(94,1,'94:1','A nuk ta hapëm ne gjoksin tënd?');
INSERT INTO "ahmeti" VALUES(94,2,'94:2','Dhe Ne hoqëm prej teje barrën tënde,');
INSERT INTO "ahmeti" VALUES(94,3,'94:3','E cila shtypte shpinën tënde.');
INSERT INTO "ahmeti" VALUES(94,4,'94:4','Dhe Ne, ta ngritëm lartë famën tënde?');
INSERT INTO "ahmeti" VALUES(94,5,'94:5','E, pa dyshim se pas vështirësisë është lehtësimi.');
INSERT INTO "ahmeti" VALUES(94,6,'94:6','Vërtet, pas vështirësisë vjen lehtësimi.');
INSERT INTO "ahmeti" VALUES(94,7,'94:7','E kur ta kryesh (obligimin), atëbotë mundohu me adhurim (All-llahut).');
INSERT INTO "ahmeti" VALUES(94,8,'94:8','Dhe, vetëm te Zoti yt përqëndro synimin!');
INSERT INTO "ahmeti" VALUES(95,1,'95:1','Pasha fikun dhe ullirin!');
INSERT INTO "ahmeti" VALUES(95,2,'95:2','Dhe (kodrën) Turi Sina-en!');
INSERT INTO "ahmeti" VALUES(95,3,'95:3','Dhe këtë qytet të sigurisë!');
INSERT INTO "ahmeti" VALUES(95,4,'95:4','Vërtet, Ne e krijuam njerin në formën më të bukur.');
INSERT INTO "ahmeti" VALUES(95,5,'95:5','Pastaj e zbritëm atë në më të ultin e të ultëve.');
INSERT INTO "ahmeti" VALUES(95,6,'95:6','Përveç atyre që besuan dhe bënë vepra të mira; ata kanë shpërblim të pandërprerë.');
INSERT INTO "ahmeti" VALUES(95,7,'95:7','E pas gjithë kësaj, ç’të shtyri të përgënjeshtrosh ditën e shpërblimit (përgjegjësisë)?');
INSERT INTO "ahmeti" VALUES(95,8,'95:8','A nuk është All-llahu më i urti gjykuesve?');
INSERT INTO "ahmeti" VALUES(96,1,'96:1','Lexo me emrin e Zotit tënd, i cili krijoi.');
INSERT INTO "ahmeti" VALUES(96,2,'96:2','Krijoi njeriun prej gjakut të ngjizur.');
INSERT INTO "ahmeti" VALUES(96,3,'96:3','Lexo për Zotin tënd Bujar!');
INSERT INTO "ahmeti" VALUES(96,4,'96:4','Është Ai që ua mësoi pendën.');
INSERT INTO "ahmeti" VALUES(96,5,'96:5','Ia mësoi njeriut atë që nuk e dinte.');
INSERT INTO "ahmeti" VALUES(96,6,'96:6','Jo, është e vërtetë se ai i kalon kufintë,');
INSERT INTO "ahmeti" VALUES(96,7,'96:7','Për shkak se e ndien veten të pavarur.');
INSERT INTO "ahmeti" VALUES(96,8,'96:8','Po kthimi është vetëm te Zoti yt.');
INSERT INTO "ahmeti" VALUES(96,9,'96:9','A më tregon atë që pengon,');
INSERT INTO "ahmeti" VALUES(96,10,'96:10','një rob kur ai falet?');
INSERT INTO "ahmeti" VALUES(96,11,'96:11','ç’mendon kur ai (robi) është në rrugë të drejtë?');
INSERT INTO "ahmeti" VALUES(96,12,'96:12','Ose ai (rob) urdhëroi për sinqeritet.');
INSERT INTO "ahmeti" VALUES(96,13,'96:13','A më trego nëse ai përgënjeshtron dhe zmbrapset (nga besimi)?');
INSERT INTO "ahmeti" VALUES(96,14,'96:14','A nuk e di ai se All-llahu e sheh?');
INSERT INTO "ahmeti" VALUES(96,15,'96:15','Jo, Jo! Nëse nuk tërhiqet, vërtet do ta kapim për flokësh mbi ballin.');
INSERT INTO "ahmeti" VALUES(96,16,'96:16','Floku rrenacak, mëkatar.');
INSERT INTO "ahmeti" VALUES(96,17,'96:17','E ai le t’i thërret ata të vetët.');
INSERT INTO "ahmeti" VALUES(96,18,'96:18','Ne do t’i thërrasim zebanitë.');
INSERT INTO "ahmeti" VALUES(96,19,'96:19','Jo, Jo! Ti mos iu bind atij, vazhdo me sexhde dhe afroju Zotit me të.');
INSERT INTO "ahmeti" VALUES(97,1,'97:1','Ne e zbritëm atë (Kur’anin) në natën e Kadrit.');
INSERT INTO "ahmeti" VALUES(97,2,'97:2','E ç’të bëri ty të dijsh se ç’është nata e Kadrit?');
INSERT INTO "ahmeti" VALUES(97,3,'97:3','Nata e Kadrit është më e rëndësishme se një mijë muaj!');
INSERT INTO "ahmeti" VALUES(97,4,'97:4','Me lejen e Zotit të tyre në (atë natë) të zbresin engjëj dhe shpirti (Xhibrili) për secilën çështje.');
INSERT INTO "ahmeti" VALUES(97,5,'97:5','Ajo (që përcakton Zoti) është paqe deri në agim të mëngjesit.');
INSERT INTO "ahmeti" VALUES(98,1,'98:1','Ata nga ithtarët e librit dhe idhujtarët, të cilët nuk besuan, nuk iu ndanë (besimit të tyre) derisa atyre u erdhi argumenti i qartë,');
INSERT INTO "ahmeti" VALUES(98,2,'98:2','I dërguari prej All-llahut, që lexon fletë të pastra,');
INSERT INTO "ahmeti" VALUES(98,3,'98:3','Në të cilat ka vetëm dispozita të drejta.');
INSERT INTO "ahmeti" VALUES(98,4,'98:4','Dhe ata të cilëve u është dhënë libri, nuk u ndanë vetëm pasi që u erdhi argumenti.');
INSERT INTO "ahmeti" VALUES(98,5,'98:5','E duke qenë se ata nuk ishin të urdhëruar me tjetër, pos që ta adhuronin All-llahun me një adhurim të sinqertë ndaj Tij, që të largohen prej çdo besimi të kotë, ta falin namazin, të japin zeqatin, se ajo është feja e drejtë.');
INSERT INTO "ahmeti" VALUES(98,6,'98:6','S’ka dyshim se ata, të cilët nuk besuan nga ithtarët e librit dhe nga idhujtarët, do të jenë në zjarrin e Xhehennemit, aty do të jenë përgjithmonë. Të tilët janë krijesa më e dëmshme.');
INSERT INTO "ahmeti" VALUES(98,7,'98:7','Ndërsa, ata që besuan dhe bënë vepra të mira, të tillët janë krijesa më e dobishme.');
INSERT INTO "ahmeti" VALUES(98,8,'98:8','Shpërblimi për ta te Zoti i tyre janë: vendet ne Xhennete nëpër të cilët burojnë lumenj, e aty janë përgjithmonë, pakëputur. All-llahu është i kënaqur ndaj tyre, e edhe ata të kënaqur prej Tij. E ky është për atë, që i ka pasur frikën Zotit të vet.');
INSERT INTO "ahmeti" VALUES(99,1,'99:1','Kur të dridhet toka me dridhjen e saj të fuqishme,');
INSERT INTO "ahmeti" VALUES(99,2,'99:2','Dhe të nxjerrë toka atë që ka në mbrendinë e saj (barrën nga brenda),');
INSERT INTO "ahmeti" VALUES(99,3,'99:3','Dhe njeriu të thotë: “ç’ka kjo (që bën këtë dridhje)?”');
INSERT INTO "ahmeti" VALUES(99,4,'99:4','Atë ditë, ajo i rrëfen tregimet e veta,');
INSERT INTO "ahmeti" VALUES(99,5,'99:5','Ngase Zoti yt e ka urdhëruar atë.');
INSERT INTO "ahmeti" VALUES(99,6,'99:6','Atë ditë njerëzit shfaqen të ndarë në grupe që të shpërblehen për veprat e tyre.');
INSERT INTO "ahmeti" VALUES(99,7,'99:7','E kushe punoi ndonjë të mirë, që pëshon sa grimca, atë do ta gjejë.');
INSERT INTO "ahmeti" VALUES(99,8,'99:8','Dhe kush punoi ndonjë të keqe sa grimca, atë do ta gjejë.');
INSERT INTO "ahmeti" VALUES(100,1,'100:1','Pasha vrapuesit e shpejtë (kuajt) që hingëllojnë!');
INSERT INTO "ahmeti" VALUES(100,2,'100:2','E që me thundrat e tyre nxjerrin xixëllima zjarri nga gurët,');
INSERT INTO "ahmeti" VALUES(100,3,'100:3','Dhe të cilët vërsulen herët në agim.');
INSERT INTO "ahmeti" VALUES(100,4,'100:4','E me të (vërsulje) atëherë çojnë pluhur.');
INSERT INTO "ahmeti" VALUES(100,5,'100:5','Dhe ashtu hidhen në mesin e grumbullit (të armikut).');
INSERT INTO "ahmeti" VALUES(100,6,'100:6','Vërtet, njeriu është përbuzës ndaj Zotit të vet.');
INSERT INTO "ahmeti" VALUES(100,7,'100:7','Edhe vetë është dëshmues i asaj (përbuzje).');
INSERT INTO "ahmeti" VALUES(100,8,'100:8','Dhe ai është që shumë e do pasurinë.');
INSERT INTO "ahmeti" VALUES(100,9,'100:9','A nuk e di ai se kur të nxirret çka ëstë në varreza?');
INSERT INTO "ahmeti" VALUES(100,10,'100:10','Dhe të tubohet (të dalë në shesh) ajo që ishte në gjoksa.');
INSERT INTO "ahmeti" VALUES(100,11,'100:11','E atë ditë, pa dyshim Zoti i tyre është i njohur për punën e tyre.');
INSERT INTO "ahmeti" VALUES(101,1,'101:1','Krisma e kijametit.');
INSERT INTO "ahmeti" VALUES(101,2,'101:2','Ç''ka është krisma?');
INSERT INTO "ahmeti" VALUES(101,3,'101:3','Dhe ç’të mësoi ty se ç’është krisma?');
INSERT INTO "ahmeti" VALUES(101,4,'101:4','Ajo është ditë kur njerëzit bëhen si insekte të shpërndara.');
INSERT INTO "ahmeti" VALUES(101,5,'101:5','Dhe kodrat bëhen si leshi i shprishur.');
INSERT INTO "ahmeti" VALUES(101,6,'101:6','E sa i përket atij që i rëndohet peshoja e veprave të tij,');
INSERT INTO "ahmeti" VALUES(101,7,'101:7','Ai është në një jetë të këndshme.');
INSERT INTO "ahmeti" VALUES(101,8,'101:8','Ndërsa, atij që ka peshojën e lehtë të veprave të tij,');
INSERT INTO "ahmeti" VALUES(101,9,'101:9','Vendi i tij do të jetë në Havije.');
INSERT INTO "ahmeti" VALUES(101,10,'101:10','E ç’është ajo që të njoftoi ç’është ai (Havije)?');
INSERT INTO "ahmeti" VALUES(101,11,'101:11','Është zjarri me nxehtësi shumë të lartë.');
INSERT INTO "ahmeti" VALUES(102,1,'102:1','Juve u preokupoi përpjekja për shumimin (e pasurirë, të fëmijve, të pozitës)!');
INSERT INTO "ahmeti" VALUES(102,2,'102:2','Derisa të mos vizitoni varrezat (të bëheni banues të tyre - të vdisni).');
INSERT INTO "ahmeti" VALUES(102,3,'102:3','Jo, nuk është ashtu! Gjithsesi këtë do ta kuptoni më vonë!');
INSERT INTO "ahmeti" VALUES(102,4,'102:4','Përsëri jo, jeni gabim! Më vonë do ta kuptoni!');
INSERT INTO "ahmeti" VALUES(102,5,'102:5','Jo, pse, sikur ta dinit me një dije të sigurt (nuk do të bënit ashtu).');
INSERT INTO "ahmeti" VALUES(102,6,'102:6','Ju pa tjetër do ta shihni Xhehennemin.');
INSERT INTO "ahmeti" VALUES(102,7,'102:7','Madje atë do ta shihni të bindur plotësisht.');
INSERT INTO "ahmeti" VALUES(102,8,'102:8','Pastaj në atë ditë do të pyeteni për të mirat (e dynjas).');
INSERT INTO "ahmeti" VALUES(103,1,'103:1','Pasha kohën!');
INSERT INTO "ahmeti" VALUES(103,2,'103:2','Nuk ka dyshim njeriu është në një humbje të sigurt.');
INSERT INTO "ahmeti" VALUES(103,3,'103:3','Me përjashtim të atyre që besuan, që bënë vepra të mira, që porositën njëri-tjetrin t’i përmbahen të vërtetës dhe që këshilluan njëri-tjetrin të jenë të durueshëm.');
INSERT INTO "ahmeti" VALUES(104,1,'104:1','Mjerë për secilin që e ofendon e përqesh (njerëzit),');
INSERT INTO "ahmeti" VALUES(104,2,'104:2','Që ka tubuar pasuri dhe që atë e ruan të mos i pakësohet.');
INSERT INTO "ahmeti" VALUES(104,3,'104:3','E mendon se pasuria e tij do të bëjë të përjetshëm.');
INSERT INTO "ahmeti" VALUES(104,4,'104:4','Jo, të mos medojë ashtu! Se ai pa tjetër do të hidhet në Hutame.');
INSERT INTO "ahmeti" VALUES(104,5,'104:5','E çka dini ti se ç’është Hutame?');
INSERT INTO "ahmeti" VALUES(104,6,'104:6','Është zjarri i All-llahut i ndezur fort.');
INSERT INTO "ahmeti" VALUES(104,7,'104:7','Që depërton deri në loçkë të zemrës.');
INSERT INTO "ahmeti" VALUES(104,8,'104:8','Ai i mbyll ata, ua zë frymën.');
INSERT INTO "ahmeti" VALUES(104,9,'104:9','Ata janë të lidhur në pranga');
INSERT INTO "ahmeti" VALUES(105,1,'105:1','A nuk e ke parë se ç’bëri Zoti yt me poseduesi e elefantit?');
INSERT INTO "ahmeti" VALUES(105,2,'105:2','A nuk ua bëri përpjekjen e tyre të dështuar?');
INSERT INTO "ahmeti" VALUES(105,3,'105:3','Dhe Ai kundër tyre lëshoi shpendë që vinin tufë-tufë!');
INSERT INTO "ahmeti" VALUES(105,4,'105:4','Dhe i gjuanin ata me gurë nga balta e gurëzuar!');
INSERT INTO "ahmeti" VALUES(105,5,'105:5','Dhe ata i bëri si gjeth i grimcuar (i përtypur)!');
INSERT INTO "ahmeti" VALUES(106,1,'106:1','Për hirë të garantimit që e gëzojnë kurejshitët!');
INSERT INTO "ahmeti" VALUES(106,2,'106:2','Garantimin e udhëtimit të tyre të lirë dimërit dhe verës!');
INSERT INTO "ahmeti" VALUES(106,3,'106:3','Pra, le ta adhurojnë Zotin e kësaj shtëpie,');
INSERT INTO "ahmeti" VALUES(106,4,'106:4','I cili i ushqeu pas urisë dhe i siguroi prej çdo frike!');
INSERT INTO "ahmeti" VALUES(107,1,'107:1','A e ke parë ti (a e sheh) atë, që përgënjeshtron përgjegjësinë dhe llogarinënë botën tjetër?');
INSERT INTO "ahmeti" VALUES(107,2,'107:2','Po ai është që e përzë në mënyrë të vrazhdë bonjakun.');
INSERT INTO "ahmeti" VALUES(107,3,'107:3','Dhe që nuk nxit për të ushqyer të varfërin.');
INSERT INTO "ahmeti" VALUES(107,4,'107:4','Pra shkatërrim është për ata që falen,');
INSERT INTO "ahmeti" VALUES(107,5,'107:5','të cilët ndaj namazit të tyre janë të pakujdesshëm.');
INSERT INTO "ahmeti" VALUES(107,6,'107:6','Ata që vetëm shtiren (sa për sy e faqe).');
INSERT INTO "ahmeti" VALUES(107,7,'107:7','Dhe nuk japin as sendin më të vogël (as hua).');
INSERT INTO "ahmeti" VALUES(108,1,'108:1','Ne, vërtet, të dhamë ty shumë mira.');
INSERT INTO "ahmeti" VALUES(108,2,'108:2','Andaj, ti falu dhe prej kurban për hurë të Zotit tënd!');
INSERT INTO "ahmeti" VALUES(108,3,'108:3','E s’ka dyshim se urrejtësi yt është farësosur.');
INSERT INTO "ahmeti" VALUES(109,1,'109:1','Thuaj: “O ju jobesimtarë!');
INSERT INTO "ahmeti" VALUES(109,2,'109:2','Unë nuk adhuroj atë që ju e adhuroni!');
INSERT INTO "ahmeti" VALUES(109,3,'109:3','As ju nuk jeni adhurues të Atij që unë e adhuroj!');
INSERT INTO "ahmeti" VALUES(109,4,'109:4','Dhe unë kurr nuk do të jem adhurues i asaj që ju adhuroni!');
INSERT INTO "ahmeti" VALUES(109,5,'109:5','Por edhe ju nuk do të jeni adhurues të Atij që unë adhuroj!');
INSERT INTO "ahmeti" VALUES(109,6,'109:6','Ju keni fenë tuaj (që i përmbaheni), e unë kam fenë time (që i përmbahem)!');
INSERT INTO "ahmeti" VALUES(110,1,'110:1','Kur erdhi ndihma e All-llahut dhe çlirimi (ngadhënjimi),');
INSERT INTO "ahmeti" VALUES(110,2,'110:2','Dhe i pe njerëzit që po hyjnë turma-turma në fenë e All-llahut.');
INSERT INTO "ahmeti" VALUES(110,3,'110:3','Ti, pra, lartësoje Zotin tënd duke falënderuar dhe kërko nga Ai falje. Ai vërtet pranon shumë pendimin.');
INSERT INTO "ahmeti" VALUES(111,1,'111:1','Qoftë i shkatërruar Ebi Lehebi, e ai më është shkatërruar!');
INSERT INTO "ahmeti" VALUES(111,2,'111:2','Atij nuk i bëri dobi pasuria e vet, as ajo çka fitoi!');
INSERT INTO "ahmeti" VALUES(111,3,'111:3','Ai do të hyjë në një zjarr të ndezur flakë.');
INSERT INTO "ahmeti" VALUES(111,4,'111:4','E edhe gruaja e tij, ajo që barti dru (ferra),');
INSERT INTO "ahmeti" VALUES(111,5,'111:5','E në qafën e saj ajo ka një litar të përdredhur.');
INSERT INTO "ahmeti" VALUES(112,1,'112:1','Thuaj: Ai, All-llahu është Një!');
INSERT INTO "ahmeti" VALUES(112,2,'112:2','All-llahu është mbështetja (Atij i mbështetet çdo krijesë).');
INSERT INTO "ahmeti" VALUES(112,3,'112:3','As s’ka lindur kë, as nuk është i lindur.');
INSERT INTO "ahmeti" VALUES(112,4,'112:4','Dhe Atij askush nuk i është i barabartë.');
INSERT INTO "ahmeti" VALUES(113,1,'113:1','Thuaj: I mbështetem Zotit të agimit,');
INSERT INTO "ahmeti" VALUES(113,2,'113:2','Prej dëmit të çdo krijese, që Ai e krijoi.');
INSERT INTO "ahmeti" VALUES(113,3,'113:3','Dhe prej errësirës së natës kur ngryset plotësisht.');
INSERT INTO "ahmeti" VALUES(113,4,'113:4','Dhe prej dëmit të atyre që lidhin dhe fryejnë nyja.');
INSERT INTO "ahmeti" VALUES(113,5,'113:5','Edhe prej dëmit të smirëkeqit kur sipas smirës vepron.');
INSERT INTO "ahmeti" VALUES(114,1,'114:1','Thuaj: “Mbështetem në Zotin e njerëzve!');
INSERT INTO "ahmeti" VALUES(114,2,'114:2','Sunduesin e njerëzve,');
INSERT INTO "ahmeti" VALUES(114,3,'114:3','Të adhuruarin e njerëzve,');
INSERT INTO "ahmeti" VALUES(114,4,'114:4','Prej të keqes së cytësit që fshihet.');
INSERT INTO "ahmeti" VALUES(114,5,'114:5','I cili hedh dyshime në zemrat e njerëzve,');
INSERT INTO "ahmeti" VALUES(114,6,'114:6','Qoftë ai nga xhinët ose nga njerëzit”.');
