INSERT INTO "al_hind" VALUES(1,1,'1:1','شروع اللہ کے نام سے جو بڑا مہربان نہایت رحم والا ہے ');
INSERT INTO "al_hind" VALUES(1,2,'1:2','سب تعریفیں اللہ کے لئے ہیں جو پالنے والا سارے جہان کا ');
INSERT INTO "al_hind" VALUES(1,3,'1:3','بےحد مہربان نہایت رحم والا');
INSERT INTO "al_hind" VALUES(1,4,'1:4','مالک روز جزا کا ');
INSERT INTO "al_hind" VALUES(1,5,'1:5','تیری ہی ہم بندگی کرتے ہیں اور تجھی سے مدد چاہتے ہیں ');
INSERT INTO "al_hind" VALUES(1,6,'1:6','بتلا ہم کو راہ سیدھی');
INSERT INTO "al_hind" VALUES(1,7,'1:7','راہ ان لوگوں کی جن پر تو نے فضل فرمایا جن پر نہ تیرا غصہ ہوا اور نہ وہ گمراہ ہوئے ');
INSERT INTO "al_hind" VALUES(2,1,'2:1','الم ');
INSERT INTO "al_hind" VALUES(2,2,'2:2','اس کتاب میں کچھ شک نہیں راہ بتلاتی ہے ڈرنے والوں کو ');
INSERT INTO "al_hind" VALUES(2,3,'2:3','جو کہ یقین کرتے ہیں بن دیکھی چیزوں کا اور قائم رکھتے ہیں نماز کو اور جو ہم نے روزی دی ہے ان کو اسمیں سے خرچ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(2,4,'2:4','اور وہ لوگ جو ایمان لائے اس پر کہ جو کچھ نازل ہوا تیری طرف اور اس پر کہ جو کچھ نازل ہوا تجھ سے پہلے اور آخرت کو وہ یقینی جانتے ہیں ');
INSERT INTO "al_hind" VALUES(2,5,'2:5','وہی لوگ ہیں ہدایت پر اپنے پروردگار کی طرف سے اور وہی ہیں مراد کو پہنچنے والے ');
INSERT INTO "al_hind" VALUES(2,6,'2:6','بیشک جو لوگ کافر ہو چکے برابر ہے ان کو تو ڈرائے یا نہ ڈرائے وہ ایمان نہیں لائیں گے ');
INSERT INTO "al_hind" VALUES(2,7,'2:7','مہر کر دی اللہ نے ان کےدلوں پر اور انکے کانوں پر اور ان کی آنکھوں پر پردہ ہے اور ان کے لئے بڑا عذاب ہے');
INSERT INTO "al_hind" VALUES(2,8,'2:8','اور لوگوں میں کچھ ایسے بھی ہیں جو کہتے ہیں ہم ایمان لائے اللہ پر اور دن قیامت پر اور وہ ہر گز مومن نہیں ');
INSERT INTO "al_hind" VALUES(2,9,'2:9','دغابازی کرتے ہیں اللہ سے اور ایمان والوں سے اور دراصل کسی کو دغا نہیں دیتے مگر اپنے آپ کو اور نہیں سوچتے ');
INSERT INTO "al_hind" VALUES(2,10,'2:10','ان کے دلوں میں بیماری ہے پھر بڑھا دی اللہ نے انکی بیماری اور ان کے لئے عذاب دردناک ہے اس بات پر کہ جھوٹ کہتےتھے ');
INSERT INTO "al_hind" VALUES(2,11,'2:11','اور جب کہا جاتا ہے ان کو فساد نہ ڈالو ملک میں تو کہتے ہیں ہم تو اصلاح کرنے والے ہیں ');
INSERT INTO "al_hind" VALUES(2,12,'2:12','جان لو وہی ہیں خرابی کرنے والے لیکن نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(2,13,'2:13','اور جب کہا جاتا ہے ان کو ایمان لاؤ جس طرح ایمان لائے سب لوگ تو کہتے ہیں کیا ہم ایمان لائیں جس طرح ایمان لائے بیوقوف جان لو وہی ہیں بیوقوف لیکن نہیں جانتے ');
INSERT INTO "al_hind" VALUES(2,14,'2:14','اور جب ملاقات کرتے ہیں مسلمانوں سے تو کہتے ہیں ہم ایمان لے آئے ہیں اور جب تنہا ہوتے ہیں اپنے شیطانوں کے پاس تو کہتےہیں کہ بیشک ہم تمہارے ساتھ ہیں ہم تو ہنسی کرتے ہیں (یعنی مسلمانوں سے )');
INSERT INTO "al_hind" VALUES(2,15,'2:15','اللہ ہنسی کرتا ہے ان سے اور ترقی دیتا ہے انکو ان کی سرکشی میں(اور) حالت یہ ہے کہ وہ عقل کے اندھے ہیں ');
INSERT INTO "al_hind" VALUES(2,16,'2:16','یہ وہی ہیں جنہوں نے مول لی گمراہی ہدایت کے بدلے سو نافع نہ ہوئی ان کی سوداگری اور نہ ہوئے راہ پانے والے ');
INSERT INTO "al_hind" VALUES(2,17,'2:17','ان کی مثال اس شخص کی سی ہے جس نے آگ جلائی پھر جب روشن کر دیا آگ نے اس کے آس پاس کو تو زائل کر دی اللہ نے ان کی روشنی اور چھوڑا ان کو اندھیروں میں کہ کچھ نہیں دیکھتے ');
INSERT INTO "al_hind" VALUES(2,18,'2:18','بہرے ہیں گونگے ہیں اندھے ہیں سو وہ نہیں لوٹیں گے ');
INSERT INTO "al_hind" VALUES(2,19,'2:19','یا ان کی مثال ایسی ہے جیسے زور سے مینہ پڑ رہا ہو آسمان سے اس میں اندھیرے ہیں اور گرج اور بجلی دیتے ہیں انگلیاں اپنے کانوں میں مارے کڑک کے موت کے ڈر سے اور اللہ احاطہ کرنے والا ہے کافروں کا ');
INSERT INTO "al_hind" VALUES(2,20,'2:20','قریب ہے کہ بجلی اچک لے ان کی آنکھیں جب چمکتی ہے ان پر تو چلنے لگے ہیں اس کی روشنی میں اور جب اندھیرا ہوتا ہے تو کھڑے رہ جاتے ہیں اور اگر چاہے اللہ تو لیجائے ان کے کان اور آنکھیں بیشک اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(2,21,'2:21','اے لوگو بندگی کرو اپنے رب کی جس نے پیدا کیا تم کو اور ان کو جو تم سے پہلے تھے تاکہ تم پرہیز گار بن جاؤ');
INSERT INTO "al_hind" VALUES(2,22,'2:22','جس نے بنایا واسطے تمہارے زمین کو بچھونا اور آسمان کو چھت اور اتار آسمان سے پانی پھر نکالے اس سے میوے تمہارے کھانے کے واسطے سو نہ ٹھہراؤ کسی کو اللہ کے مقابل اور تم تو جانتے ہو ');
INSERT INTO "al_hind" VALUES(2,23,'2:23','اور اگر تم شک میں ہو اس کلام سےجو اتارا ہم نے اپنے بندہ پر تو لے آؤ ایک سورت اس جیسی اور بلاؤ اس کو جو تمہارا مددگار ہو اللہ کے سوا اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(2,24,'2:24','پھر اگر ایسا نہ کرسکو اور ہر گز نہ کر سکو گے تو پھر بچو اس آگ سے جس کا ایندھن آدمی اور پتھر ہیں تیار کی ہوئی ہے کافروں کے واسطے ');
INSERT INTO "al_hind" VALUES(2,25,'2:25','اور خوشخبری دے ان لوگوں کو جو ایمان لائے اور اچھے کام کئے کہ ان کے واسطے باغ ہیں کہ بہتی ہیں ان کے نیچے نہریں جب ملے گا ان کو وہاں کا کوئی پھل کھانے کو تو کہیں گے یہ تو وہی ہے جو ملا تھا ہم کو اس سے پہلے اور دیئے جائیں گے ان کو پھل ایک صورت کے اور انکے لئے وہاں عورتیں ہوں گی پاکیزہ اور وہ وہیں ہمیشہ رہیں گے ');
INSERT INTO "al_hind" VALUES(2,26,'2:26','بیشک اللہ شرماتا نہیں اس بات سے کہ بیان کرے کوئی مثال مچھر کی یا اس چیز کی جو اس سے بڑھکر ہے سو جو لوگ مومن ہیں وہ یقینًا جانتےہیں کہ یہ مثال ٹھیک ہے جو نازل ہوئی انکے رب کی طرف سے اور جو کافر ہیں سو کہتے ہیں کیا مطلب تھا اللہ کا اس مثال سے گمراہ کرتا ہے خدائے تعالیٰ اس مثال سے بہتیروں کو اور ہدایت کرتا ہے اس سے بہتیروں کو اور گمراہ نہیں کرتا اس مثل سے مگر بدکاروں کو');
INSERT INTO "al_hind" VALUES(2,27,'2:27','جو توڑتے ہیں خدا کے معاہدہ کو مضبوط کرنے کے بعد اور قطع کرتے ہیں اس چیز کو جس کو اللہ نے فرمایا ملانے کو اور فساد کرتے ہیں ملک میں وہی ہیں ٹوٹے والے ');
INSERT INTO "al_hind" VALUES(2,28,'2:28','کس طرح کافر ہوتے ہو خدا تعالیٰ سے حالانکہ تم بیجان تھے پھر جلایا تم کو پھر مارے گا تم کو پھر جلائے گا تم کو پھر اسی کی طرف لوٹائے جاؤ گے ');
INSERT INTO "al_hind" VALUES(2,29,'2:29','وہی ہے جس نے پیدا کیا تمہارے واسطے جو کچھ زمین میں ہے سب پھر قصد کیا آسمان کی طرف سو ٹھیک کر دیا انکو سات آسمان اور خدا تعالیٰ ہر چیز سے خبردار ہے ');
INSERT INTO "al_hind" VALUES(2,30,'2:30','اور جب کہا تیرے رب نے فرشتوں کو کہ میں بنانیوالا ہوں زمین میں ایک نائب کہا فرشتوں نے کیا قائم کرتا ہے تو زمین میں اسکو جو فساد کرے اس میں اور خون بہائے اور ہم پڑھتے رہتے ہیں تیری خوبیاں اور یاد کرتے ہیں تیری پاک ذات کو فرمایا بیشک مجھ کو معلوم ہے جو تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(2,31,'2:31','اور سکھلا دیے اللہ نے آدم کو نام سب چیزوں کے پھر سامنے کیا ان سب چیزوں کو فرشتوں کے پھر فرمایا بتاؤ مجھ کو نام ان کے اگر تم سچے ہو');
INSERT INTO "al_hind" VALUES(2,32,'2:32','بولے پاک ہے تو ہم کو معلوم نہیں مگر جتنا تو نے ہم کو سکھایا بیشک تو ہی ہے اصل جاننے والا حکمت والا ');
INSERT INTO "al_hind" VALUES(2,33,'2:33','فرمایا اے آدم بتا دے فرشتوں کو ان چیزوں کے نام پھر جب بتا دیے اس نے ان کے نام فرمایا کیا نہ کہا تھا میں نے تم کو کہ میں خوب جانتا ہوں چھپی ہوئی چیزیں آسمانوں کی اور زمین کی اور جانتا ہوں جو تم ظاہر کرتے ہو اور جو چھپاتے ہو ');
INSERT INTO "al_hind" VALUES(2,34,'2:34','اور جب ہم نے حکم دیا فرشتوں کو کہ سجدہ کرو آدم کو تو سب سجدہ میں گر پڑے مگر شیطان اس نے نہ مانا اور تکبر کیا اور تھا وہ کافروں میں کا ');
INSERT INTO "al_hind" VALUES(2,35,'2:35','اور ہم نے کہا اے آدم رہا کر تو اور تیری عورت جنت میں اور کھاؤ اس میں جو چاہو جہاں کہیں سے چاہو اور پاس مت جانا اس درخت کے پھر تم ہو جاؤ گے ظالم ');
INSERT INTO "al_hind" VALUES(2,36,'2:36','پھر ہلا دیا انکو شیطان نے اس جگہ سے پھر نکالا انکو اس عزت و راحت سے کہ جس میں تھے اور ہم نے کہاتم سب اترو تم ایک دوسرے کے دشمن ہو گے اور تمہارے واسطے زمین میں ٹھکانا ہے اور نفع اٹھانا ہے ایک وقت تک ');
INSERT INTO "al_hind" VALUES(2,37,'2:37','پھر سیکھ لیں آدم نے اپنے رب سے چند باتیں پھر متوجہ ہو گیا اللہ اس پر بیشک وہی ہے توبہ قبول کرنے والا مہربان ');
INSERT INTO "al_hind" VALUES(2,38,'2:38','ہم نے حکم دیا نیچے جاؤ یہاں سے تم سب پھر اگر تم کو پہنچے میری طرف سے کوئی ہدایت تو جو چلا میری ہدایت پر نہ خوف ہو گا ان پر اور نہ وہ غمگین ہوں گے ');
INSERT INTO "al_hind" VALUES(2,39,'2:39','اور جو لوگ منکر ہوئے اور جھٹلایا ہماری نشانیوں کو وہ ہیں دوزخ میں جانے والے وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "al_hind" VALUES(2,40,'2:40','اے بنی اسرائیل یاد کرو میرے وہ احسان جو میں نے تم پر کئے اور تم پورا کرو میرا اقرار تو میں پورا کروں تمہارا اقرار اور مجھ ہی سے ڈرو ');
INSERT INTO "al_hind" VALUES(2,41,'2:41','اور مان لو اس کتاب کو جو میں نے اتاری ہے سچ بتانے والی ہے اس کتاب کو جو تمہارے پاس ہے اور مت ہو سب میں اول منکر اس کے اور نہ لو میری آیتوں پر مول تھوڑا اور مجھ ہی سے بچتے رہو۔');
INSERT INTO "al_hind" VALUES(2,42,'2:42','اور مت لاؤ صحیح میں غلط اور مت چھپاؤ سچ کو جان بوجھ کر');
INSERT INTO "al_hind" VALUES(2,43,'2:43','اور قائم کرو نماز اور دیا کرو زکوٰۃ اور جھکو نماز میں جھکنے والوں کے ساتھ ');
INSERT INTO "al_hind" VALUES(2,44,'2:44','کیا حکم کرتے ہو لوگوں کو نیک کام کا اور بھولتے ہو اپنے آپ کو اور تم تو پڑھتے ہو کتاب پھر کیوں نہیں سوچتے ہو ');
INSERT INTO "al_hind" VALUES(2,45,'2:45','اور مدد چاہو صبر سے اور نماز سے اور البتہ وہ بھاری ہے مگر انہی عاجزوں پر');
INSERT INTO "al_hind" VALUES(2,46,'2:46','جن کو خیال ہے کہ وہ روبرو ہونے والے ہیں اپنے رب کے اور یہ کہ انکو اسی کی طرف لوٹ کر جانا ہے ');
INSERT INTO "al_hind" VALUES(2,47,'2:47','اے بنی اسرائیل یاد کرو میرے احسان جو میں نے تم پر کئے اور اسکو کہ میں نے تم کو بڑائی دی تمام عالم پر ');
INSERT INTO "al_hind" VALUES(2,48,'2:48','اور ڈرو اس دن سے کہ کام نہ آئے کوئی شخص کسی کے کچھ بھی اور قبول نہ ہو اسکی طرف سے سفارش اور نہ لیا جائے اس کی طرف سے بدلہ اور نہ ان کو مدد پہنچے ');
INSERT INTO "al_hind" VALUES(2,49,'2:49','اور یاد کرو اس وقت کو جبکہ رہائی دی ہم نے تم کو فرعون کے لوگوں سے جو کرتےتھے تم پر بڑا عذاب ذبح کرتے تھے تمہارے بیٹوں کو اور زندہ چھوڑتے تھے تمہاری عورتوں کو اور اس میں آزمائش تھی تمہارے رب کی طرف سے بڑی ');
INSERT INTO "al_hind" VALUES(2,50,'2:50','اور جب پھاڑ دیا ہم نے تمہاری وجہ سے دریا کو پھر بچا دیا ہم نے تم کو اور ڈبا دیا فرعون کے لوگوں کو اور تم دیکھ رہے تھے ');
INSERT INTO "al_hind" VALUES(2,51,'2:51','اور جب ہم نے وعدہ کیا موسٰی سے چالیس رات کا پھر تم نے بنا لیا بچھڑا موسٰی کے بعد اور تم ظالم تھے ');
INSERT INTO "al_hind" VALUES(2,52,'2:52','پھر معاف کیا ہم نے تم کو اس پر بھی تاکہ تم احسان مانو ');
INSERT INTO "al_hind" VALUES(2,53,'2:53','اور جب ہم نے دی موسٰی کو کتاب اور حق کو ناحق سے جدا کرنے والے احکام تاکہ تم سیدھی راہ پاؤ ');
INSERT INTO "al_hind" VALUES(2,54,'2:54','اور جب کہا موسٰی نے اپنی قوم سے اے قوم تم نے نقصان کیا اپنایہ بچھڑا بنا کر سو اب توبہ کرو اپنے پیدا کرنے والے کی طرف اور مار ڈالو اپنی اپنی جان یہ بہتر ہے تمہارے لئے تمہارے خالق کے نزدیک پھر متوجہ ہوا تم پر بیشک وہی ہے معاف کرنے والا نہایت مہربان');
INSERT INTO "al_hind" VALUES(2,55,'2:55','اور جب تم نے کہا اے موسٰی ہم ہرگز یقین نہ کریں گے تیرا جب تک کہ نہ دیکھ لیں اللہ کو سامنے پھر آلیا تم کو بجلی نے اور تم دیکھ رہے تھے');
INSERT INTO "al_hind" VALUES(2,56,'2:56','پھر اٹھا کھڑا کیا ہم نے تم کو مر گئے پیچھے تاکہ تم احسان مانو ');
INSERT INTO "al_hind" VALUES(2,57,'2:57','اور سایہ کیا ہم نے تم پر ابر کا اور اتارا تم پر من اور سلویٰ کھاؤ پاکیزہ چیزیں جو ہم نے تم کو دیں اور انہوں نے ہمارا کچھ نقصان نہ کیا بلکہ اپنا ہی نقصان کرتے رہے ');
INSERT INTO "al_hind" VALUES(2,58,'2:58','اور جب ہم نے کہا داخل ہو اس شہر میں اور کھاتے پھرو اسمیں جہاں چاہو فراغت سے اور داخل ہو دروازے میں سجدہ کرتے ہوئے اور کہتے جاؤ بخشدے تو معاف کر دیں گے ہم تمہارے قصور اور زیادہ بھی دیں گے نیکی والوں کو ');
INSERT INTO "al_hind" VALUES(2,59,'2:59','پھر بدل ڈالا ظالموں نے بات کو خلاف اسکے جو کہہ دی گئی تھی ان سے پھر اتارا ہم نے ظالموں پر عذاب آسمان سے ان کی عدول حکمی پر ');
INSERT INTO "al_hind" VALUES(2,60,'2:60','اور جب پانی مانگا موسٰی نے اپنی قوم کے واسطے تو ہم نے کہا مار اپنے عصا کو پتھر پر سو بہہ نکلے اس سے بارہ چشمے پہچان لیا ہر قوم نے اپنا گھاٹ کھاؤ اور پیو اللہ کی روزی اور نہ پھرو ملک میں فساد مچاتے ');
INSERT INTO "al_hind" VALUES(2,61,'2:61','اور جب کہا تم نے اے موسٰی ہم ہر گز صبر نہ کریں گے ایک ہی کھانے پر سو دعا مانگ ہمارے واسطے اپنے پروردگار سے کہ نکال دے ہمارے واسطے جو اگتا ہے زمین سے ترکاری اور ککڑی اور گیہوں اور مسور اور پیاز کہا موسٰی نے کیا لینا چاہتے وہ چیز جو ادنیٰ ہے اس کے بدلہ میں جو بہتر ہے اترو کسی شہر میں تو تم کو ملے جو مانگتے ہو اور ڈالی گئ ان پر ذلت اور محتاجی اور پھرے اللہ کا غضب لے کر یہ اس لئے ہوا کہ نہیں مانتے تھے احکام خداوندی کو اور خون کرتے تھے پیغمبروں کا ناحق یہ اس لئے کہ نافرمان تھے اور حد پر نہ رہتے تھے ');
INSERT INTO "al_hind" VALUES(2,62,'2:62','بیشک جو لوگ مسلمان ہوئے اور جو لوگ یہودی ہوئے اور نصاریٰ اور صائبین جو ایمان لایا (ان میں سے) اللہ پر اور روز قیامت پر اور کام کئے نیک تو انکے لئے ہے انکا ثواب انکے رب کے پاس اور نہیں ان پر کچھ خوف اور نہ وہ غمگین ہوں گے ');
INSERT INTO "al_hind" VALUES(2,63,'2:63','اور جب لیا ہم نے تم سے قرار اور بلند کیا تمہارے اوپر کوہ طور کو کہ پکڑو جو کتاب ہم نے تم کو دی زور سے اور یاد رکھو جو کچھ اس میں ہے تاکہ تم ڈرو ');
INSERT INTO "al_hind" VALUES(2,64,'2:64','پھر تم پھر گئے اس کے بعد سو اگر نہ ہوتا اللہ کا فضل تم پر اور اسکی مہربانی تو ضرور تم تباہ ہوتے ');
INSERT INTO "al_hind" VALUES(2,65,'2:65','اور تم خوب جان چکے ہو جنہوں نے کہ تم میں سے زیادتی کی تھی ہفتہ کے دن میں تو ہم نے کہا ان سے ہو جاؤ بندر ذلیل ');
INSERT INTO "al_hind" VALUES(2,66,'2:66','پھر کیا ہم نے اس واقعہ کو عبرت ان لوگوں کے لئے جو وہاں تھے اور جو پیچھے آنے والے تھے اور نصیحت ڈرنے والوں کے واسطے ');
INSERT INTO "al_hind" VALUES(2,67,'2:67','اور جب کہا موسٰی نے اپنی قوم سے اللہ تعالیٰ فرماتا ہے تم کو ذبح کرو ایک گائے وہ بولے کیا تو ہم سے ہنسی کرتا ہے کہا پناہ خدا کی کہ ہوں میں جاہلوں میں ');
INSERT INTO "al_hind" VALUES(2,68,'2:68','بولے کہ دعا کرو ہمارے واسطے اپنے رب سے کہ بتا دے ہم کو کہ وہ گائے کیسی ہے کہا وہ فرماتا کہ وہ ایک گائے ہے نہ بوڑھی اور نہ بن بیاہی درمیان میں ہے بڑھاپے اور جوانی کے اب کر ڈالو جو تم کو حکم ملا ہے ');
INSERT INTO "al_hind" VALUES(2,69,'2:69','بولے کہ دعا کر ہمارے واسطے اپنے رب سے کہ بتا دے ہم کو کیسا ہے اس کا رنگ کہا وہ فرماتا ہےکہ وہ ایک گائے ہے زرد خوب گہری ہے اس کی زردی خوش آتی ہے دیکھنے والوں کو');
INSERT INTO "al_hind" VALUES(2,70,'2:70','بولے دعا کر ہمارے واسطے اپنے رب سے کہ بتا دے ہم کو کس قسم میں ہے وہ کیونکہ اس گائے میں شبہہ پڑا ہے ہم کو اور ہم اگر اللہ نے چاہا تو ضرور راہ پا لیں گے');
INSERT INTO "al_hind" VALUES(2,71,'2:71','کہا وہ فرماتا ہے کہ وہ ایک گائے ہے محنت کرنے والی نہیں کہ جوتتی ہو زمین کو یا پانی دیتی ہو کھیتی کو بے عیب ہے کوئی داغ اسمیں نہیں بولے اب لایا تو ٹھیک بات پھر اس کو ذبح کیا اور وہ لگتے نہ تھے کہ ایسا کر لیں گے ');
INSERT INTO "al_hind" VALUES(2,72,'2:72','اور جب مار ڈالا تھا تم نے ایک شخص کو پھر لگے ایک دوسرے پر دھرنے اور اللہ کو ظاہر کرنا تھا جو تم چھپاتے تھے ');
INSERT INTO "al_hind" VALUES(2,73,'2:73','پھر ہم نے کہا مارو اس مردہ پر اس گائے کا ایک ٹکڑا اسی طرح زندہ کرے گا اللہ مردو ں کو اور دکھاتا ہے تم کو اپنی قدرت کے نمونے تاکہ تم غور کرو ');
INSERT INTO "al_hind" VALUES(2,74,'2:74','پھر تمہارے دل سخت ہو گئے اس سب کے بعد سو وہ ہو گئے جیسے پتھر یا ان سے بھی سخت اور پتھروں میں تو ایسے بھی ہیں جن سے جاری ہوتی ہیں نہریں اور ان میں ایسے بھی ہیں جو پھٹ جاتے ہیں اور نکلتا ہے ان سے پانی اور ان میں ایسے بھی ہیں جو گر پڑتے ہیں اللہ کے ڈر سے اور اللہ بیخبر نہیں تمہارے کاموں سے ');
INSERT INTO "al_hind" VALUES(2,75,'2:75','اب کیا تم اے مسلمانوں توقع رکھتے ہو کہ وہ مانیں تمہاری بات اور ان میں ایک فرقہ تھا کہ سنتا تھا اللہ کا کلام پھر بدل ڈالتے تھے اس کو جان بوجھ کر اور وہ جانتے تھے ');
INSERT INTO "al_hind" VALUES(2,76,'2:76','اور جب ملتے ہیں مسلمانوں سے کہتے ہیں ہم مسلمان ہوئے اور جب تنہا ہوتے ہیں ایک دوسرے کے پاس تو کہتے ہیں تم کیوں کہہ دیتے ہو ان سے جو ظاہر کیا ہے اللہ نے تم پر تاکہ جھٹلائیں تم کو اس سے تمہارے رب کے آگے کیا تم نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(2,77,'2:77','کیا اتنا بھی نہیں جانتے کہ اللہ کو معلوم ہے جو کچھ چھپاتے ہیں اور جو کچھ ظاہر کرتے ہیں ');
INSERT INTO "al_hind" VALUES(2,78,'2:78','اور بعض ان میں بے پڑھے ہیں کہ خبر نہیں رکھتے کتاب کی سوائے جھوٹی آرزوؤں کے اور انکے پاس کچھ نہیں مگر خیالات ');
INSERT INTO "al_hind" VALUES(2,79,'2:79','سو خرابی ہے ان کو جو لکھتے ہیں کتاب اپنے ہاتھ سے پھر کہہ دیتے ہیں یہ خدا کی طرف سے ہے تاکہ لیویں اس پر تھوڑا سا مول سو خرابی ہے ان کو اپنے ہاتھوں کے لکھے سے اور خرابی ہے ان کو اپنی اس کمائی سے ');
INSERT INTO "al_hind" VALUES(2,80,'2:80','اور کہتے ہیں ہم کو ہر گز آگ نہ لگے گی مگر چند روز گنے چنے کہدو کیا تم لے چکے ہو اللہ کے یہاں سے قرار کہ اب ہرگز خلاف نہ کرے گا اللہ اپنے قرار کے یا جوڑتے ہو اللہ پر جو تم نہیں جانتے کیوں نہیں ');
INSERT INTO "al_hind" VALUES(2,81,'2:81','جس نے کمایا گناہ اور گھیر لیا اس کو اس کے گناہ نے سو وہی ہیں دوزخ کے رہنے والے وہ اسی میں ہمیشہ رہیں گے');
INSERT INTO "al_hind" VALUES(2,82,'2:82','اور جو ایمان لائے اور عمل نیک کئے نیک وہی ہیں جنت کے رہنے والے وہ اسی میں ہمیشہ رہیں گے');
INSERT INTO "al_hind" VALUES(2,83,'2:83','اور جب ہم نے لیا قرار بنی اسرائیل سے کہ عبادت نہ کرنا مگر اللہ کی اور ماں باپ سے سلوک نیک کرنا اور کنبہ والوں سے اور یتیموں اور محتاجوں سے او رکہیو سب لوگوں سے نیک بات اور قائم رکھیو نماز اور دیتے رکھیو زکوٰۃ پھر تم پھر گئے مگر تھوڑے سے تم میں اور تم ہو ہی پھرنے والے ');
INSERT INTO "al_hind" VALUES(2,84,'2:84','اور جب لیا ہم نے وعدہ تمہارا کہ نہ کرو گے خون آپس میں اور نہ نکال دو گے اپنوں کو اپنے وطن سے پھر تم نے اقرار کر لیا اور تم مانتے ہو ');
INSERT INTO "al_hind" VALUES(2,85,'2:85','پھر تم وہ لوگ ہو کہ ایسے ہی خون کرتے ہو آپس میں اور نکال دیتے ہو اپنے ایک فرقہ کو ان کے وطن سے چڑھائی کرتے ہو ان پر گناہ اور ظلم سے اور اگر وہی آویں تمہارے پاس کسی کے قیدی ہو کر تو ان کا بدلا دیکر چھڑاتے ہو حالانکہ حرام ہے تم پر انکا نکال دینا بھی تو کیا مانتے ہو بعض کتاب کو اور نہیں مانتے بعض کو سو کوئی سزا نہیں اس کی جو تم میں یہ کام کرتا ہے مگر رسوائی دنیا کی زندگی میں اور قیامت کے دن پہنچائے جاویں سخت سےسخت عذاب میں اور اللہ بے خبر نہیں تمہارے کاموں سے ');
INSERT INTO "al_hind" VALUES(2,86,'2:86','یہ وہی ہیں جنہوں نے مول لی دنیا کی زندگی آخرت کے بدلے سو نہ ہلکا ہو گا ان پر عذاب اور نہ ان کو مدد پہنچے گی ');
INSERT INTO "al_hind" VALUES(2,87,'2:87','اور بیشک دی ہم نےموسٰی کو کتاب اور پے درپے بھیجے اس کے پیچھے رسول اور دئے ہم نے عیسیٰ مریم کے بیٹے کو معجزے صریح اور قوت دی اس کو روح پاک سے پھر بھلا کیا جب پاس لایا کوئی رسول وہ حکم جو نہ بھایا تمہارے جی کو تو تم تکبر کرنے لگے پھر ایک جماعت کو جھٹلایا اور ایک جماعت کو تم نے قتل کر دیا ');
INSERT INTO "al_hind" VALUES(2,88,'2:88','اور کہتے ہیں ہمارے دلوں پر غلاف ہے بلکہ لعنت کی ہے اللہ نے انکے کفر کے سبب سو بہت کم ایمان لاتے ہیں ');
INSERT INTO "al_hind" VALUES(2,89,'2:89','اور جب پہنچی انکے پاس کتاب اللہ کی طرف سے جو سچا بتاتی ہے اس کتاب کو جو انکے پاس ہے اور پہلے سے فتح مانگتے تھے کافروں پر پھر جب پہنچا ان کو جسکو پہچان رکھا تھا تو اس سے منکر ہو گئے سو لعنت ہے اللہ کی منکروں پر ');
INSERT INTO "al_hind" VALUES(2,90,'2:90','بری چیز ہے وہ جسکے بدلے بیچا انہوں نے اپنے آپکو کہ منکر ہوئے اس چیز کے جو اتاری اللہ نے اس ضد پر کہ اتارے اللہ اپنے فضل سے جس پر چاہے اپنے بندوں میں سے سو کما لائے غصہ پر غصہ اور کافروں کے واسطے عذاب ہے ذلت کا ');
INSERT INTO "al_hind" VALUES(2,91,'2:91','اور جب کہا جاتا ہے ان سے مانو اس کو جو اللہ نے بھیجا ہے تو کہتے ہیں ہم مانتے ہیں جو اترا ہے ہم پر اور نہیں مانتے اس کو جو سوا اس کے ہے حالانکہ وہ کتاب سچی ہے تصدیق کرتی ہے اس کتاب کی جو انکے پاس ہے کہدو پھر کیوں قتل کرتے رہے ہو اللہ کے پیغمبروں کو پہلے سے اگر تم ایمان رکھتے تھے ');
INSERT INTO "al_hind" VALUES(2,92,'2:92','اور آچکا تمہارے پاس موسٰی صریح معجزے لیکر پھر بنا لیا تم نے بچھڑا اس کے گئے پیچھے اور تم ظالم ہو ');
INSERT INTO "al_hind" VALUES(2,93,'2:93','اور جب ہم نے لیا قرار تمہارا اور بلند کیا تمہارے اوپر کوہ طور کو پکڑو جو ہم نے تم کو دیا زور سے اور سنو بولے سنا ہم نے اور نہ مانا اور پلائی گئ انکے دلوں میں محبت اسی بچھڑے کی بسبب انکے کفر کے کہدے کہ بری باتیں سکھاتا ہے تم کو ایمان تمہارا اگر تم ایمان والے ہو');
INSERT INTO "al_hind" VALUES(2,94,'2:94','کہدے کہ اگر ہے تمہارے واسطے آخرت کا گھر اللہ کے ہاں تنہا سوا اور لوگوں کے تو تم مرنے کی آرزو کرو اگر تم سچ کہتے ہو ');
INSERT INTO "al_hind" VALUES(2,95,'2:95','اور ہر گز آرزو نہ کریں گے موت کی کبھی بسبب ان گناہوں کے کہ بھیج چکے ہیں انکے ہاتھ اور اللہ خوب جانتا ہے گنہگاروں کو');
INSERT INTO "al_hind" VALUES(2,96,'2:96','اور تو دیکھے گا ان کو سب لوگوں سے زیادہ حریص زندگی پر اور زیادہ حریص مشرکوں سے بھی چاہتا ہے ایک ایک ان میں کا کہ عمر پاوے ہزار برس اور نہیں اس کو بچانے والا عذاب سے اس قدر جینا اور اللہ دیکھتا ہے جو کچھ وہ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(2,97,'2:97','تو کہدے جو کوئی ہووے دشمن جبریل کا سو اس نے تو اتارا ہے یہ کلام تیرے دل پر اللہ کے حکم سے کہ سچا بتانے والا ہے اس کلام کو جو اسکے پہلے ہے اور راہ دکھاتا ہے اور خوش خبری سناتا ہے ایمان والوں کو');
INSERT INTO "al_hind" VALUES(2,98,'2:98','جو کوئی ہووے دشمن اللہ کا اور اسکے فرشتوں کا اور اسکے پیغمبروں کا اور جبریل اور میکائیل کا تو اللہ دشمن ہے ان کافروں کا ');
INSERT INTO "al_hind" VALUES(2,99,'2:99','اور ہم نے اتاریں تیری طرف آیتیں روشن اور انکار نہ کریں گے ان کا مگر وہی جو نافرمان ہیں');
INSERT INTO "al_hind" VALUES(2,100,'2:100','کیا جب کبھی باندھیں گے کوئی قرار تو پھینک دے گی اس کو ایک جماعت ان میں سے بلکہ ان میں اکثر یقین نہیں کرتے ');
INSERT INTO "al_hind" VALUES(2,101,'2:101','اور جب پہنچا انکے پاس رسول اللہ کی طرف سے تصدیق کرنے والا اس کتاب کی جو انکے پاس ہے تو پھینک دیا ایک جماعت نے اہل کتاب سے کتاب اللہ کو اپنی پیٹھ کے پیچھے گویا کہ وہ جانتے ہی نہیں ');
INSERT INTO "al_hind" VALUES(2,102,'2:102','اور پیچھے ہو لئے اس علم کے جو پڑتے تھے شیطان سلیمان کی بادشاہت کے وقت اور کفر نہیں کیا سلیمان نے لیکن شیطانوں نے کفر کیا کہ سکھلاتے تھے لوگوں کو جادو اور اس علم کے پیچھے ہو لئے جو اترا دو فرشتوں پر شہر بابل میں جن کا نام ہاروت اور ماروت ہے اور نہیں سکھاتے تھے وہ دونوں فرشتے کسی کو جب تک یہ نہ کہدیتے کہ ہم تو آزمائش کے لئے ہیں سو تو کافر مت ہو پھر ان سے سیکھتے وہ جادو جس سے جدائی ڈالتے ہیں مرد میں اور اسکی عورت میں اور وہ اس سے نقصان نہیں کر سکتے کس کا بغیر حکم اللہ کے اور سیکھتے ہیں وہ چیز جو نقصان کرے ان کا اور فائدہ نہ کرے اور وہ خوب جان چکے ہیں کہ جس نے اختیار کیا جادو کو نہیں اس کے لئے آخرت میں کچھ حصہ اور بہت ہی بری چیز ہے جسکے بدلے بیچا انہوں نے اپنے آپکو اگر ان کو سمجھ ہوتی');
INSERT INTO "al_hind" VALUES(2,103,'2:103','اور اگر وہ ایمان لاتے اور تقویٰ کرتے تو بدلا پاتے اللہ کے ہاں سے بہتر اگر ان کو سمجھ ہوتی ');
INSERT INTO "al_hind" VALUES(2,104,'2:104','اے ایمان والو تم نہ کہو راعنا اور کہو انظرنا اور سنتے رہو اور کافروں کو عذاب ہے دردناک ');
INSERT INTO "al_hind" VALUES(2,105,'2:105','دل نہیں چاہتا ان لوگوں کا جو کافر ہیں اہل کتاب میں اور نہ مشرکوں میں اس بات کو کہ اترے تم پر کوئی نیک بات تمہارے رب کی طرف سے اور اللہ خاص کر لیتا ہے اپنی رحمت کے ساتھ جسکو چاہے اور اللہ بڑے فضل والا ہے ');
INSERT INTO "al_hind" VALUES(2,106,'2:106','جو منسوخ کرتے ہیں ہم کوئی آیت یا بھلا دیتے ہیں تو بھیج دیتے ہیں اس سے بہتر یا اسکے برابر کیا تجھکو معلوم نہیں کہ اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(2,107,'2:107','کیا تجھ کو معلوم نہیں کہ اللہ ہی کے لئے ہے سلطنت آسمان اور زمین کی اور نہیں تمہارے واسطے اللہ کے سوا کوئی حمایتی اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(2,108,'2:108','کیا تم مسلمان بھی چاہتے ہو کہ سوال کرو اپنے رسول سے جیسے سوال ہو چکے ہیں موسٰی سے اس سے پہلے اور جو کوئی کفر لیوے بدلے ایمان کے تو وہ بہکا سیدھی راہ سے ');
INSERT INTO "al_hind" VALUES(2,109,'2:109','دل چاہتا ہے بہت سے اہل کتاب کا کہ کسی طرح تم کو پھیر کر مسلمان ہوئے پیچھے کافر بنا دیں بسبب اپنے دلی حسد کے بعد اس کے کہ ظاہر ہو چکا ان پر حق سو تم در گذر کرو اور خیال میں نہ لاؤ جبتک بھیجے اللہ اپنا حکم بیشک اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(2,110,'2:110','اور قائم رکھو نماز اور دیتے رہو زکوٰۃ اور جو کچھ آگے بھیج دو گے اپنے واسطے بھلائی پاؤ گے اس کو اللہ کے پاس بیشک اللہ جو کچھ تم کرتے ہو سب دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(2,111,'2:111','اور کہتے ہیں کہ ہرگز نہ جاویں گے جنت میں مگر جو ہوں گے یہودی یا نصرانی یہ آرزؤیں باندھ لی ہیں انہوں نے کہدے لے آؤ سند اپنی اگر تم سچے ہو');
INSERT INTO "al_hind" VALUES(2,112,'2:112','کیوں نہیں جس نے تابع کر دیا منہ اپنا اللہ کے اور وہ نیک کام کرنے والا ہےتو اس کے لئے ہے ثواب اس کا اپنے رب کے پاس اور نہ ڈر ہے ان پر اور نہ وہ غمگین ہو نگے ');
INSERT INTO "al_hind" VALUES(2,113,'2:113','اور یہود تو کہتے ہیں کہ نصاریٰ نہیں کسی راہ پر اور نصاریٰ کہتے ہیں کہ یہود نہیں کسی راہ پر باوجودیکہ وہ سب پڑھتے ہیں کتاب اسی طرح کہا ان لوگوں نے جو جاہل ہیں ان ہی کی سی بات اب اللہ حکم کرے گا ان میں قیامت کے دن جس بات میں جھگڑتے تھے ');
INSERT INTO "al_hind" VALUES(2,114,'2:114','اور اس سے بڑا ظالم کون جس نے منع کیا اللہ کی مسجدوں میں کہ لیا جاوے وہاں نام اس کا اور کوشش کی انکے اجاڑنے میں ایسوں کو لائق نہیں کہ داخل ہوں ان میں مگر ڈرتے ہوئے انکے لئےدنیا میں ذلت ہے اور ان کے لئے آخرت میں بڑا عذاب ہے');
INSERT INTO "al_hind" VALUES(2,115,'2:115','اور اللہ ہی کا ہے مشرق اور مغرب سو جس طرف تم منہ کرو وہاں ہی متوجہ ہے اللہ بیشک اللہ بے انتہا بخشش کرنے والا سب کچھ جاننے والا ہے ');
INSERT INTO "al_hind" VALUES(2,116,'2:116','اور کہتے ہیں کہ اللہ رکھتا ہے اولاد وہ تو سب باتوں سے پاک ہے بلکہ اسی کا ہے جو کچھ ہے آسمان اور زمین میں سب اسی کے تابعدار ہیں');
INSERT INTO "al_hind" VALUES(2,117,'2:117','نیا پیدا کرنے والا ہے آسمان اور زمین کا اور جب حکم کرتا ہے کسی کام کو تو یہی فرماتا ہے اس کو کہ ہو جا پس وہ ہو جاتا ہے ');
INSERT INTO "al_hind" VALUES(2,118,'2:118','اور کہتے ہیں وہ لوگ جو کچھ نہیں جانتے کیوں نہیں بات کرتا ہم سے اللہ یا کیوں نہیں آتی ہمارے پاس کوئی آیت اسی طرح کہ چکے ہیں وہ لوگ جو ان سے پہلے تھے انہی کی سی بات ایک سے ہیں دل ان کے بیشک ہم نے بیان کر دیں نشانیاں ان لوگوں کے واسطے جو یقین لاتے ہیں ');
INSERT INTO "al_hind" VALUES(2,119,'2:119','بیشک ہم نے تجھ کو بھیجا ہے سچا دین دیکر خوشخبری دینے والا اور ڈرانے والا اور تجھ سے پوچھ نہیں دوزخ میں رہنے والوں کی ');
INSERT INTO "al_hind" VALUES(2,120,'2:120','اور ہر گرز راضی نہ ہوں گے تجھ سے یہود او ر نصاریٰ جب تک تو تابع نہ ہو ان کے دین کا تو کہدے جو راہ اللہ بتلاوے وہی راہ سیدھی ہے اور اگر بالفرض تو تابعداری کرے انکی خواہشوں کی بعد اس علم کے جو تجھ کو پہنچا تو تیرا کوئی نہیں اللہ کے ہاتھ سے حمایت کرنے والا اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(2,121,'2:121','وہ لوگ جن کو دی ہم نے کتاب وہ اسکو پڑھتے ہیں جو حق ہے اسکے پڑھنے کا وہی اس پر یقین لاتے ہیں اور جو کوئی منکر ہو گا اس سے تو وہی لوگ نقصان پانے والے ہیں ');
INSERT INTO "al_hind" VALUES(2,122,'2:122','اے بنی اسرائیل یاد کرو احسان ہمارے جو ہم نے تم پر کئے اور اس کو کہ ہم نے تم کو بڑائی دی اہل عالم پر');
INSERT INTO "al_hind" VALUES(2,123,'2:123','اور ڈرو اس دن سے کہ نہ کام آوے کوئی شخص کسی کی طرف سے ذرا بھی اور نہ قبول کیا جاوے گا اس کی طرف سے بدلہ اور نہ کام آوے اس کو سفارش اور نہ ان کو مدد پہنے ');
INSERT INTO "al_hind" VALUES(2,124,'2:124','اور جب آزمایا ابراہیم کو اسکے رب نے کئ باتوں میں پھر اسنے وہ پوری کیں تب فرمایا میں تجھ کو کروں گا سب لوگوں کا پیشوا بولا اور میری اولاد میں سے بھی فرمایا نہیں پہنچے گا میرا قرار ظالموں کو ');
INSERT INTO "al_hind" VALUES(2,125,'2:125','اور جب مقرر کیا ہم نے خانہ کعبہ کو اجتماع کی جگہ لوگوں کے واسطے اور جگہ امن کی اور بناؤ ابراہیم کے کھڑے ہونے کی جگہ کو نماز کی جگہ اور حکم کیا ہم نے ابراہیم اور اسمٰعیل کو کہ پاک کر رکھو میرے گھر کو واسطے طواف کرنے والوں کے اور اعتکاف کرنے والوں کے اور رکوع او رسجدہ کرنے والوں کے');
INSERT INTO "al_hind" VALUES(2,126,'2:126','اور جب کہا ابراہیم نے اے میرے رب بنا اس کو شہر امن کا اور روزی دے اسکے رہنے والوں کو میوے جو کوئی ان میں سے ایمان لاوے اللہ پر اور قیامت کے دن پر فرمایا اور جو کفر کریں اس کو بھی نفع پہنچاؤں گا تھوڑے دنوں پھر اسکو جبرًا بلاؤں گا دوزخ کے عذاب میں اور وہ بری جگہ ہے رہنے کی ');
INSERT INTO "al_hind" VALUES(2,127,'2:127','اور یاد کرو جب اٹھاتے تھے ابراہیم بنیادیں خانہ کعبہ کی اور اسمٰعیل اور دعا کرتے تھے اے پروردگار ہمارے قبول کر ہم سے بیشک تو ہی ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(2,128,'2:128','اے پروردگار ہمارے اور کر ہم کو حکم بردار اپنا اور ہماری اولاد میں بھی کر ایک جماعت فرمانبردار اپنی اور بتلا ہم کو قاعدے حج کرنے کے اور ہم کو معاف کر بیشک تو ہی ہے توبہ قبول کرنے والا مہربان');
INSERT INTO "al_hind" VALUES(2,129,'2:129','اے پروردگار ہمارے اور بھیج ان میں ایک رسول انہی میں کا کہ پڑھے ان پر تیری آیتیں اور سکھلاوے ان کو کتاب اور تہ کی باتیں اور پاک کرے ان کو بیشک تو ہی ہے بہت زبردست بڑی حکمت والا ');
INSERT INTO "al_hind" VALUES(2,130,'2:130','اور کون ہے جو پھرے ابراہیم کے مذہب سے مگر وہی کہ جس نے احمق بنایا اپنے آپ کو اور بیشک ہم نےانکو منتخب کیا دنیا میں اور وہ آخرت میں نیکوں میں ہیں');
INSERT INTO "al_hind" VALUES(2,131,'2:131','یاد کرو جب اس کو کہا اس کے رب نے کہ حکم برداری کر تو بولا کہ میں حکم بردار ہوں تمام عالم کے پروردگار کا');
INSERT INTO "al_hind" VALUES(2,132,'2:132','اور یہی وصیت کر گیا ابراہیم اپنے بیٹوں کو اور یعقوب بھی کہ اے بیٹو بیشک اللہ نے چن کر دیا ہے تم کو دین سو تم ہرگز نہ مرنا مگر مسلمان ');
INSERT INTO "al_hind" VALUES(2,133,'2:133','کیا تم موجود تھے جس وقت قریب آئی یعقوب کے موت جب کہا اپنے بیٹوں کو تم کس کی عبادت کرو گے میرے بعد بولے ہم بندگی کریں گے تیرے رب کی اور تیرے باپ دادوں کے رب کی جو کہ ابراہیم اور اسمٰعیل اور اسحٰق ہیں وہی ایک معبود ہے اور ہم سب اسی کے فرمانبردار ہیں ');
INSERT INTO "al_hind" VALUES(2,134,'2:134','وہ ایک جماعت تھی جو گذر چکی انکے واسطے ہے جو انہوں نے کیا اور تمہارے واسطے ہے جو تم نے کیا اور تم سے پوچھ نہیں ان کے کاموں کی ');
INSERT INTO "al_hind" VALUES(2,135,'2:135','اور کہتے ہیں کہ ہو جاؤ یہودی یا نصرانی تو تم پالو گے راہ راست کہدے کہ ہر گز نہیں بلکہ ہم نے اختیار کی راہ ابراہیم کی جو ایک ہی طرف کا تھا اور نہ تھا شرک کرنے والوں میں ');
INSERT INTO "al_hind" VALUES(2,136,'2:136','تم کہہ دو کہ ہم ایمان لائے اللہ پر اور جو اترا ہم پر اور جو اترا ابراہیم اور اسمٰعیل اور اسحٰق اور یعقوب اور اس کی اولاد پر اور جو ملا موسٰی کو اور عیسٰی کو اور جو ملا دوسرے پیغمبروں کو انکے رب کی طرف سے ہم فرق نہیں کرتے ان سب میں سے ایک میں بھی اور ہم اسی پروردگار کے فرمانبردار ہیں ');
INSERT INTO "al_hind" VALUES(2,137,'2:137','سو اگر وہ ابھی ایمان لاویں جس طرح پر تم ایمان لائے ہدایت پائی انہوں نے بھی اور اگر پھر جاویں تو پھر وہی ہیں ضد پر سو اب کافی ہے تیری طرف سے ان کو اللہ اور وہی ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(2,138,'2:138','ہم نے قبول کر لیا رنگ اللہ کا اور کس کا رنگ بہتر ہے اللہ کے رنگ سے اور ہم اسی کی بندگی کرتے ہیں ');
INSERT INTO "al_hind" VALUES(2,139,'2:139','کہدے کیا تم جھگڑا کرتے ہو ہم سے اللہ کی نسبت حالانکہ وہی ہےرب ہمارا اور رب تمہارا اور ہمارےلئے ہیں عمل ہمارے اور تمہارے لئے ہیں عمل تمہارے اور ہم تو خالص اسی کے ہیں ');
INSERT INTO "al_hind" VALUES(2,140,'2:140','کیا تم کہتے ہو کہ ابراہیم اور اسمٰعیل اور اسحٰق او ریعقوب اور اس کی اولاد تو یہودی تھے یا نصرانی کہدے کہ تم کو زیادہ خبر ہے یا اللہ کو اور اس سے بڑا ظالم کون جس نے چھپائی وہ گواہی جو ثابت ہو چکی اس کو اللہ کی طرف سے اور اللہ بیخبر نہیں تمہارے کاموں سے ');
INSERT INTO "al_hind" VALUES(2,141,'2:141','وہ ایک جماعت تھی جو گذر چکی ان کے واسطے ہے جو انہوں نے کیا اور تمہارے واسطے ہے جو تم نے کیا اور تم سے کچھ پوچھ نہیں ان کے کاموں کی ');
INSERT INTO "al_hind" VALUES(2,142,'2:142','اب کہیں گے بیوقوف لوگ کہ کس چیز نے پھیر دیا مسلمانوں کو انکے قبلہ سےجس پر وہ تھے تو کہہ اللہ ہی کا ہے مشرق اور مغرب چلائے جس کو چاہے سیدھی راہ ');
INSERT INTO "al_hind" VALUES(2,143,'2:143','اور اسی طرح کیا ہم نے تم کو امت معتدل تاکہ ہو تم گواہ لوگوں پر اور ہو رسول تم پر گواہی دینے والا اور نہیں مقرر کیا تھا ہم نے وہ قبلہ کہ جس پر تو پہلے تھا مگر اس واسطے کہ معلوم کریں کون تابع رہے گا رسول کا اور کون پھر جائے گا الٹے پاؤں اور بیشک یہ بات بھاری ہوئی مگر ان پر جن کو راہ دکھائی اللہ نے اور اللہ ایسا نہیں کہ ضائع کرے تمہارا ایمان بیشک اللہ لوگوں پر بہت شفیق نہایت مہربان ہے ');
INSERT INTO "al_hind" VALUES(2,144,'2:144','بیشک ہم دیکھتے ہیں باربار اٹھنا تیرے منہ کا آسمان کی طرف سو البتہ پھیر یں گے ہم تجھ کو جس قبلہ کی طرف تو راضی ہے اب پھیر منہ اپنا طرف مسجد الحرام کے اور جس جگہ تم ہوا کرو پھیرو منہ اسی کی طرف اور جن کو ملی ہے کتاب البتہ جانتے ہیں کہ یہ ہی ٹھیک ہے انکے رب کی طرف سے اور اللہ بے خبر نہیں ان کاموں سے جو وہ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(2,145,'2:145','اور اگر تو لائے اہل کتاب کےپاس ساری نشانیاں تو بھی نہ مانیں گے تیرے قبلہ کو اور نہ تو مانے ان کا قبلہ اور نہ ان میں ایک مانتا ہے دوسرے کا قبلہ اور اگر تو چلا ان کی خواہشوں پر بعد اس علم کے جو تجھ کو پہنچا تو بیشک تو بھی ہوا بے انصافوں میں ');
INSERT INTO "al_hind" VALUES(2,146,'2:146','جن کو ہم نے دی ہے کتاب پہچانتے ہیں اس کو جیسے پہچانتے ہیں اپنے بیٹوں کو اور بیشک ایک فرقہ ان میں سے البتہ چھپاتے ہیں حق کو جان کر');
INSERT INTO "al_hind" VALUES(2,147,'2:147','حق تو وہی ہے جو تیرا رب کہے پھر تو نہ ہو شک لانے والا ');
INSERT INTO "al_hind" VALUES(2,148,'2:148','اور ہر کسی کے واسطے ایک جانب ہے یعنی قبلہ کہ وہ منہ کرتا ہے اس طرف سو تم سبقت کرو نیکیوں میں جہاں کہیں تم ہو گے کر لائے گا تم کو اللہ اکٹھا بیشک اللہ ہر چیز کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(2,149,'2:149','اور جس جگہ سے تو نکلے سو منہ کر اپنا مسجدالحرام کی طرف اور بیشک یہ حق ہے تیرے رب کی طرف سے اور اللہ بیخبر نہیں تمہارےکاموں سے');
INSERT INTO "al_hind" VALUES(2,150,'2:150','اور جہاں سے تو نکلے منہ کر اپنا مسجدالحرام کی طرف اور جس جگہ تم ہوا کرو منہ کرو اسی کی طرف تاکہ نہ رہے لوگوں کو تم سے جگھڑنے کا موقع مگر جو ان میں بے انصاف ہیں سو ان سے [یعنی انکے اعتراضوں سے] مت ڈرو اور مجھ سے ڈرو اور اس واسطے کہ کامل کروں تم پر فضل اپنا اور تاکہ تم پاؤ راہ سیدھی ');
INSERT INTO "al_hind" VALUES(2,151,'2:151','جیسا کہ بھیجا ہم نے تم میں رسول تم ہی میں کا پڑھتا ہے تمہارے آگے آیتیں ہماری اور پاک کرتا ہے تم کو اور سکھلاتا ہے تم کو کتاب اور اس کے اسرار اور سکھلاتا ہے تم کو جو تم نہ جانتے تھے ');
INSERT INTO "al_hind" VALUES(2,152,'2:152','سو تم یاد رکھو مجھ کو میں یاد رکھوں تم کو اور احسان مانو میرا اور ناشکری مت کرو ');
INSERT INTO "al_hind" VALUES(2,153,'2:153','اے مسلمانو مدد لو صبر اور نماز سے بیشک اللہ صبر کرنے والوں کے ساتھ ہے ');
INSERT INTO "al_hind" VALUES(2,154,'2:154','اور نہ کہو ان کو جو مارے گئے خدا کی راہ میں کہ مردے ہیں بلکہ وہ زندے ہیں لیکن تم کو خبر نہیں ');
INSERT INTO "al_hind" VALUES(2,155,'2:155','اور البتہ ہم آزمائیں گے تم کو تھوڑے سے ڈر سے اور بھوک سے اور نقصان سے مالوں کے اور جانوں کے اور میووں کے اور خوشخبری دے ان صبر کرنے والوں کو');
INSERT INTO "al_hind" VALUES(2,156,'2:156','کہ جب پہنچے ان کو کچھ مصیبت تو کہیں ہم تو اللہ ہی کا مال ہیں اور ہم اسی کی طرف لوٹ کر جانے والے ہیں');
INSERT INTO "al_hind" VALUES(2,157,'2:157','ایسے ہی لوگوں پر عنایتیں ہیں اپنے رب کی اور مہربانی اور وہی ہیں سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(2,158,'2:158','بیشک صفا اور مروہ نشانیوں میں سے ہیں اللہ کی سو جو کوئی حج کرے بیت اللہ کا یا عمرہ تو کچھ گناہ نہیں اس کو کہ طواف کرے ان دونوں میں اور جو کوئی اپنی خوشی سےکرےکچھ نیکی تو اللہ قدردان ہے سب کچھ جاننے والا ');
INSERT INTO "al_hind" VALUES(2,159,'2:159','بیشک جو لوگ چھپاتے ہیں جو کچھ ہم نے اتارے صاف حکم اور ہدایت کی باتیں بعد اس کے کہ ہم انکو کھول چکے لوگوں کے واسطے کتاب میں ان پر لعنت کرتا ہے اللہ اور لعنت کرتے ہیں ان پر لعنت کرنے والے ');
INSERT INTO "al_hind" VALUES(2,160,'2:160','مگر جنہوں نے توبہ کی اور درست کیا اپنے کام کو اور بیان کر دیا حق بات کو تو انکو معاف کرتا ہوں اور میں ہوں بڑا معاف کرنے والا نہایت مہربان');
INSERT INTO "al_hind" VALUES(2,161,'2:161','بیشک جو لوگ کافر ہوئے اور مر گئے کافر ہی انہی پر لعنت ہے اللہ کی اور فرشتوں کی اور لوگوں کی سب کی ');
INSERT INTO "al_hind" VALUES(2,162,'2:162','ہمیشہ رہیں گے اسی لعنت میں نہ ہلکا ہو گا ان پر سے عذاب اور نہ انکو مہلت ملے گی ');
INSERT INTO "al_hind" VALUES(2,163,'2:163','اور معبود تم سب کا ایک ہی معبود ہے کوٹی معبود نہیں اس کے سوا بڑا مہربان ہے نہایت رحم والا ');
INSERT INTO "al_hind" VALUES(2,164,'2:164','بیشک آسمان اور زمین کے پیدا کرنے میں اور رات اور دن کے بدلتے رہنے میں اور کشتیوں میں جو کہ لے کر چلتی ہیں دریا میں لوگوں کے کام کی چیزیں اور پانی میں جس کو کہ اتارا اللہ نے آسمان سے پھر جلایا اس سے زمین کو اس کے مر گئے پیچھے اور پھیلائے اس میں سب قسم کے جانور اور ہواؤں کے بدلنے میں اور بادل میں جو کہ تابعدار ہے اس کے حکم کا درمیان آسمان و زمین کے بیشک ان سب چیزوں میں نشانیاں ہیں عقلمندوں کے لئے ');
INSERT INTO "al_hind" VALUES(2,165,'2:165','اور بعضے لوگ وہ ہیں جو بناتے ہیں اللہ کے برابر اوروں کو انکی محبت ایسی رکھتے ہیں جیسی محبت اللہ کی اور ایمان والوں کو اس سے زیادہ تر ہے محبت اللہ کی اور اگر دیکھ لیں یہ ظالم اس وقت کو جبکہ دیکھیں گے عذاب کہ قوۃ ساری اللہ ہی کے لئے ہے اور یہ کہ اللہ کا عذاب سخت ہے ');
INSERT INTO "al_hind" VALUES(2,166,'2:166','جبکہ بیزار ہو جاویں گے وہ کہ جن کی پیروی کی تھی ان سے کہ جو انکے پیرو ہوٹے تھے اور دیکھیں گے عذاب اور منقطع ہو جائیں گے ان کے سب علاقے ');
INSERT INTO "al_hind" VALUES(2,167,'2:167','اور کہیں گے پیرو کیا اچھا ہوتا جو ہم کو دنیا کی طرف لوٹ جانا مل جاتا تو پھر ہم بھی بیزار ہو جاتے ان سے جیسے یہ ہم سے بیزار ہو گئے اسی طرح پر دکھلائے گا اللہ ان کو ان کے کام حسرت دلانے کو اور وہ ہرگز نکلنے والے نہیں مار سے ');
INSERT INTO "al_hind" VALUES(2,168,'2:168','اے لوگو کھاؤ زمین کی چیزوں میں سے حلال پاکیزہ اور پیروی نہ کرو شیطان کی بیشک وہ تمہارا دشمن ہے صریح');
INSERT INTO "al_hind" VALUES(2,169,'2:169','وہ تو یہی حکم کرے گا تم کو کہ برے کام اور بیحیائی کرو اور جھوٹ لگاؤ اللہ پر وہ باتیں جنکو تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(2,170,'2:170','اور جب کوئی ان سےکہے کہ تابعداری کرو اس حکم کی جو کہ نازل فرمایا اللہ نے تو کہتے ہیں ہرگز نہیں ہم تو تابعداری کریں گے اس کی جس پر دیکھا ہم نے اپنے باپ دادوں کو بھلا اگرچہ ان کے باپ دادے نہ سمجھتے ہوں کچھ ہی اور نہ جانتے ہوں سیدھی راہ [۲۴۰/۱]');
INSERT INTO "al_hind" VALUES(2,171,'2:171','اور مثال ان کافروں کی ایسی ہے جیسے پکارے کوئی شخص ایک چیز کو جو کچھ نہ سنے سوا پکارنے اور چلانے کے [۲۴۰/۲] بہرے گونگے اندھے ہیں سو وہ کچھ نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(2,172,'2:172','اے ایمان والو کھاؤ پاکیزہ چیزیں جو روزی دی ہم نے تم کو اور شکر کرو اللہ کا اگر تم اسی کےبندے ہو ');
INSERT INTO "al_hind" VALUES(2,173,'2:173','اس نے تو تم پر یہی حرام کیا ہے مردہ جانور اور لہو اور گوشت سور کا اور جس جانور پر نام پکارا جائے اللہ کے سوا کسی اور کا پھر جو کوئی بے اختیار ہو جائے نہ تو نافرمانی کرے اور نہ زیادتی تو اس پر کچھ گناہ نہیں بیشک اللہ ہے بڑا بخشنے والا نہایت مہربان ');
INSERT INTO "al_hind" VALUES(2,174,'2:174','بیشک جو لوگ چھپاتے ہیں جو کچھ کیا نازل اللہ نے کتاب اور لیتے ہیں اس پر تھوڑا سا مول وہ نہیں بھرتے اپنے پیٹ میں مگر آگ اور نہ بات کرے گا ان سے اللہ قیامت کے دن اور نہ پاک کرے گا ان کو اور انکے لئے ہے عذاب دردناک ');
INSERT INTO "al_hind" VALUES(2,175,'2:175','یہی ہیں جنہوں نے خریدا گمراہی کو بدلے ہدایت کے اور عذاب بدلے بخشش کے [۲۵۴/۱] سو کس قدر صبر کرنے والے ہیں وہ دوزخ پر [۲۵۴/۲]');
INSERT INTO "al_hind" VALUES(2,176,'2:176','یہ اس واسطے کہ اللہ نے نازل فرمائی کتاب سچی اور جنہوں نے اختلاف ڈالا کتاب میں وہ بیشک ضد میں دور جا پڑے ');
INSERT INTO "al_hind" VALUES(2,177,'2:177','نیکی کچھ یہی نہیں کہ منہ کرو اپنا مشرق کی طرف یا مغرب کی لیکن بڑی نیکی تو یہ ہے جو کوئی ایمان لائے اللہ پر اور قیامت کے دن پر اور فرشتوں پر اور سب کتابوں پر اور پیغمبروں پر اور دے مال اس کی محبت پر رشتہ داروں کو اور یتیموں کو اور محتاجوں کو اور مسافروں کو اور مانگنے والوں کو اور گردنیں چھڑانے میں اور قائم رکھے نماز اور دیا کرے زکوٰۃ اور پورا کرنے والے اپنے اقرار کو جب عہد کریں اور صبر کرنے والے سختی میں اور تکلیف میں اور لڑائی کے وقت یہی لوگ ہیں سچے اور یہی ہیں پرہیزگار ');
INSERT INTO "al_hind" VALUES(2,178,'2:178','اے ایمان والو فرض ہوا تم پر [قصاص] برابری کرنا مقتولوں میں آزاد کے بدلے آزاد اور غلام کے بدلے غلام اور عورت کےبدلے عورت پھر جس کو معاف کیا جائے اس کے بھائی کی طرف سے کچھ بھی تو تابعداری کرنی چاہیئے موافق دستور کے اور ادا کرنا چاہئے اس کو خوبی کے ساتھ یہ آسانی ہوئی تمہارے رب کی طرف سے اور مہربانی پھر جو زیادتی کرے اس فیصلہ کے بعد تو اس کے لئے ہے عذاب دردناک ');
INSERT INTO "al_hind" VALUES(2,179,'2:179','اور تمہارے واسطے قصاص میں بڑی زندگی ہے اے عقلمندو تاکہ تم بچتے رہو ');
INSERT INTO "al_hind" VALUES(2,180,'2:180','فرض کر دیا گیا تم پر جب حاضر ہو کسی کو تم میں موت بشرطیکہ چھوڑے کچھ مال وصیت کرنا ماں باپ کے واسطے اور رشتہ داروں کےلئے انصاف کے ساتھ یہ حکم لازم ہے پرہیز گاروں پر ');
INSERT INTO "al_hind" VALUES(2,181,'2:181','پھر جو کوئی بدل ڈالے وصیت کو بعد اس کے جو سن چکا تو اس کا گناہ انہی پر جنہوں نے اس کو بدلا بیشک اللہ سننے والا جاننے والا ہے ');
INSERT INTO "al_hind" VALUES(2,182,'2:182','پھر جو کوئی خوف کرے وصیت کرنے والے سے طرفداری کا یا گناہ کا پھر ان میں باہم صلح کرا دے تو اس پر کچھ گناہ نہیں بیشک اللہ بڑا بخشنے والا نہایت مہربان ہے ');
INSERT INTO "al_hind" VALUES(2,183,'2:183','اے ایمان والو فرض کیا گیا تم پر روزہ جیسے فرض کیا گیا تھا تم سے اگلوں پر تاکہ تم پرہیز گار ہو جاؤ ');
INSERT INTO "al_hind" VALUES(2,184,'2:184','چند روز ہیں گنتی کے پھر جو کوئی تم میں سے بیمار ہو یا مسافر تو اس پر ان کی گنتی ہے اور دنوں سے اور جن کو طاقت ہے روزہ کی انکے ذمہ بدلا ہے ایک فقیر کا کھانا پھر جو کوئی خوشی سے کرے نیکی تو اچھا ہےاسکے واسطے اور روزہ رکھو تو بہتر ہے تمہارے لئے اگر تم سمجھ رکھتے ہو ');
INSERT INTO "al_hind" VALUES(2,185,'2:185','مہینہ رمضان کا ہے جس میں نازل ہوا قرآن ہدایت ہے واسطے لوگوں کو اور دلیلیں روشن راہ پانے کی اور حق کو باطل سے جدا کرنے کی سو جو کوئی پائے تم میں سے اس مہینہ کو تو ضرور روزے رکھے اسکے اور جو کوئی ہو بیمار یا مسافر تو اس کو گنتی پوری کرنی چاہیئے اور دنوں سے اللہ چاہتا ہے تم پر آسانی اور نہیں چاہتا تم پر دشواری اور اس واسطے کہ تم پوری کرو گنتی اور تاکہ بڑائی کرو اللہ کی اس بات پر کہ تم کو ہدایت کی اور تاکہ تم احسان مانو ');
INSERT INTO "al_hind" VALUES(2,186,'2:186','اور جب تجھ سے پوچھیں میرے بندے مجھ کو سو میں تو قریب ہوں قبول کرتا ہوں دعا مانگنے والے کی دعا کو جب مجھ سے دعا مانگے تو چاہئے کہ وہ حکم مانیں میرا اور یقین لائیں مجھ پر تاکہ نیک راہ پر آئیں ');
INSERT INTO "al_hind" VALUES(2,187,'2:187','حلال ہوا تم کو روزہ کی رات میں بے حجاب ہونا اپنی عورتوں سے وہ پوشاک ہیں تمہاری اور تم پوشاک ہو انکی اللہ کو معلوم ہے کہ تم خیانت کرتے تھے اپنی جانوں سے سو معاف کیا تم کو اور درگذر کی تم سے پھر ملو اپنی عورتوں سے اور طلب کرو اس کو جو لکھ دیا ہے اللہ نے تمہارے لئے اور کھاؤ اور پیو جب تک کہ صاف نظر آئے تم کو دھاری سفید صبح کی جدا دھاری سیاہ سے پھر پورا کرو روزہ کو رات تک اور نہ ملو عورتوں سے جب تک تم اعتکاف کرو مسجدوں میں یہ حدیں باندھی ہوئی ہیں اللہ کی سو انکے نزدیک نہ جاؤ اسی طرح بیان فرماتا ہے اللہ اپنی آیتیں لوگوں کے واسطے تاکہ وہ بچتے رہیں ');
INSERT INTO "al_hind" VALUES(2,188,'2:188','اور نہ کھاؤ مال ایک دوسرے کا آپس میں ناحق اور نہ پہنچاؤ انکو حاکموں تک کہ کھا جاؤ کوئی حصہ لوگوں کےمال میں سے ظلم کر کے [ناحق] اور تم کو معلوم ہے ');
INSERT INTO "al_hind" VALUES(2,189,'2:189','تجھ سے پوچھتےہیں حال نئے چاند کا کہہ دے کہ یہ اوقات مقررہ ہیں لوگوں کے واسطے اور حج کے واسطے اور نیکی یہ نہیں کہ گھروں میں آؤ انکی پشت کی طرف سے اور لیکن نیکی یہ ہے کہ جو کوئی ڈرے اللہ سے اور گھروں میں آؤ دروازوں سے اور اللہ سے ڈرتے رہو تاکہ تم اپنی مراد کو پہنچو ');
INSERT INTO "al_hind" VALUES(2,190,'2:190','اور لڑو اللہ کی راہ میں ان لگوں سے جو لڑتے ہیں تم سے اور کسی پر زیادتی مت کرو بیشک اللہ تعالیٰ ناپسند کرتا ہے زیادتی کرنے والوں کو');
INSERT INTO "al_hind" VALUES(2,191,'2:191','اور مار ڈالو ان کو جس جگہ پاؤ اور نکال دو ان کو جہاں سے انہوں نے تم کو نکالا اور دین سے بچلانا مار ڈالنے سے بھی زیادہ سخت ہے اور نہ لڑو ان سے مسجدالحرام کے پاس جب تک کہ وہ نہ لڑیں تم سے اس جگہ پھر اگر وہ خود ہی لڑیں تم سےتو ان کو مارو یہی ہے سزا کافروں کی ');
INSERT INTO "al_hind" VALUES(2,192,'2:192','پھر اگر وہ باز آئیں تو بیشک اللہ بہت بخشنے والا نہایت مہربان ہے ');
INSERT INTO "al_hind" VALUES(2,193,'2:193','اور لڑو ان سے یہاں تک کہ نہ باقی رہے فساد اور حکم رہے خدا تعالیٰ ہی کا پھر اگر وہ باز آئیں تو کسی پر زیادتی نہیں مگر ظالموں پر ');
INSERT INTO "al_hind" VALUES(2,194,'2:194','حرمت والا مہینہ بدلا (مقابل) ہے حرمت والے مہینے کا اور ادب رکھنے میں بدلا ہے پھر جس نے تم پر زیادتی کی تم اس پر زیادتی کرو جیسی اس نے زیادتی کی تم پر اور ڈرتے رہو اللہ سے اور جان لو کہ اللہ ساتھ ہے پرہیزگاروں کے ');
INSERT INTO "al_hind" VALUES(2,195,'2:195','اور خرچ کرو اللہ کی راہ میں اور نہ ڈالو اپنی جان کو ہلاکت میں اور نیکی کرو بیشک اللہ دوست رکھتا ہے نیکی کرنے والوں کو');
INSERT INTO "al_hind" VALUES(2,196,'2:196','اورپورا کرو حج اور عمرہ اللہ کے واسطے پھر اگر تم روک دیے جاؤ تو تم پر ہے جو کچھ کہ میسر ہو قربانی سے اور حجامت نہ کرو اپنے سروں کی جب تک پہنچ نہ چکے قربانی اپنے ٹھکانے پر پھر جو کوئی تم میں سے بیمار ہو یا اس کو تکلیف ہو سر کی تو بدلا دیوے روزے یا خیرات یا قربانی پھر جب تمہاری خاطر جمع ہو تو جو کوئی فائدہ اٹھائے عمرہ کو ملا کر حج کے ساتھ تو اس پر ہے جو کچھ میسر ہو قربانی سے پھر جس کو قربانی نہ ملے تو روزے رکھے تین حج کےدنوں میں اور سات روزے جب لوٹو یہ دس روزےہو ئے پورے یہ حکم اس کے لئے ہے جسکے گھر والے نہ رہتے ہوں مسجد الحرام کے پاس اور ڈرتے رہو اللہ سے اور جان لو کہ بیشک اللہ کا عذاب سخت ہے');
INSERT INTO "al_hind" VALUES(2,197,'2:197','حج کے چند مہینے ہیں معلوم پھر جس نے لازم کر لیا ان میں حج تو بے حجاب ہونا جائز نہیں عورت سے اور نہ گناہ کرنا اور نہ جھگڑا کرنا حج کے زمانہ میں اور جو کچھ تم کرتے ہو نیکی اللہ اس کو جانتا ہے اور زاد راہ لے لیا کرو کہ بیشک بہتر فائدہ زاد راہ کا بچنا ہے سوال سے اور مجھ سے ڈرتے رہو اے عقلمندو ');
INSERT INTO "al_hind" VALUES(2,198,'2:198','کچھ گناہ نہیں تم پر کہ تلاش کرو فضل اپنے رب کا پھر جب طواف کے لئے لوٹو عرفات سے تو یاد کرو اللہ کو نزدیک مشعر الحرام کے اور اس کو یاد کرو جس طرح تم کو سکھلایا اور بیشک تم تھے اس سے پہلے ناواقف ');
INSERT INTO "al_hind" VALUES(2,199,'2:199','پھر طواف کے لئے پھرو جہاں سے سب لوگ پھریں اور مغفرت چاہو اللہ سے بیشک اللہ تعالیٰ بخشنے والا ہے مہربان ');
INSERT INTO "al_hind" VALUES(2,200,'2:200','پھر جب پورے کر چکو اپنے حج کے کام کو تو یاد کرو اللہ کو جیسے تم یاد کرتے تھے اپنے باپ دادوں کو بلکہ اس سے بھی زیادہ یاد کرو پھر کوئی آدمی تو کہتا ہے اے رب ہمارے دے ہم کو دنیا میں اور اس کے لئے آخرت میں کچھ حصہ نہیں');
INSERT INTO "al_hind" VALUES(2,201,'2:201','اور کوئی ان میں کہتا ہے اے رب ہمارے دے ہم کو دنیا میں خوبی اور آخرت میں خوبی اور بچا لے ہم کو دوزخ کے عذاب سے');
INSERT INTO "al_hind" VALUES(2,202,'2:202','انہی لوگوں کے واسطے حصہ ہے اپنی کمائی سے اور اللہ جلد حساب لینے والا ہے ');
INSERT INTO "al_hind" VALUES(2,203,'2:203','اور یاد کرو اللہ کو گنتی کے چند دنوں میں پھر جو کوئی جلد چلا گیا دو ہی دن میں تو اس پر گناہ نہیں اور جو کوئی رہ گیا تو اس پر بھی گناہ نہیں جو کہ ڈرتا ہے اور ڈرتے رہو اللہ سے اور جان لو بیشک تم سب اسکے پاس جمع ہو گے ');
INSERT INTO "al_hind" VALUES(2,204,'2:204','اور بعضا آدمی وہ ہے کہ پسند آتی ہے تجھ کو اس کی بات دنیا کی زندگانی کے کاموں میں اور گواہ کرتا ہے اللہ کو اپنے دل کی بات پر اور وہ سخت جھگڑالو ہے');
INSERT INTO "al_hind" VALUES(2,205,'2:205','اور جب پھرے تیرے پاس سے تو دوڑتا پھرے ملک میں تاکہ اس میں خرابی ڈالے اور تباہ کرے کھیتیاں اور جانیں اور اللہ ناپسند کرتا ہے فساد کو');
INSERT INTO "al_hind" VALUES(2,206,'2:206','اور جب اس سے کہا جائے کہ اللہ سے ڈرو تو آمادہ کرےاس کو غرور گناہ پر سو کافی ہے اس کو دوزخ اور وہ بیشک برا ٹھکانہ ہے ');
INSERT INTO "al_hind" VALUES(2,207,'2:207','اور لوگوں میں ایک شخص وہ ہے کہ بیچتا ہے اپنی جان کو اللہ کی رضا جوئی میں اور اللہ نہایت مہربان ہے اپنے بندوں پر ');
INSERT INTO "al_hind" VALUES(2,208,'2:208','اے ایمان والو داخل ہو جاؤ اسلام میں پورے اور مت چلو قدموں پر شیطان کے بیشک وہ تمہارا صریح دشمن ہے ');
INSERT INTO "al_hind" VALUES(2,209,'2:209','پھر اگر تم بچلنے لگو اس کے بعد اس کہ پہنچ چکے تم کو صاف حکم تو جان رکھو کہ بیشک اللہ زبردست ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(2,210,'2:210','کیا وہ اسی کی راہ دیکھتے ہیں کہ آوے ان پر اللہ ابر کے سائبانوں میں اور فرشتے اور طے ہو جاوے قصہ اور اللہ ہی کی طرف لوٹیں گے سب کام ');
INSERT INTO "al_hind" VALUES(2,211,'2:211','پوچھ بنی اسرائیل سے کس قدر عنایت کیں ہم نے انکو نشانیاں کھلی ہوئیں اور جو کوئی بدل ڈالے اللہ کی نعمت بعد اس کے کہ پہنچ چکی ہو وہ نعمت اس کو تو اللہ کا عذاب سخت ہے ');
INSERT INTO "al_hind" VALUES(2,212,'2:212','فریفتہ کا ہے کافروں کو دنیا کی زندگی پر اور ہنستے ہیں ایمان والوں کو اور جو پرہیزگار ہیں وہ ان کافروں سے بالاتر ہو ں گے قیامت کے دن اور اللہ روزی دیتا ہے جس کو چاہے بے شمار ');
INSERT INTO "al_hind" VALUES(2,213,'2:213','تھے سب لوگو ایک دین پر پھر بھیجے اللہ نے پیغمبر خوشخبری سنانے والے اور ڈرانے والے اور اتاری انکےساتھ کتاب سچی کہ فیصلہ کرے لوگوں میں جس بات میں وہ جھگڑا کریں اور نہیں جھگڑا ڈالا کتاب میں مگر انہی لوگوں نے جن کو کتاب ملی تھی اس کے بعد کہ ان کو پہنچ چکے صاف حکم آپس کی ضد سے پھر اب ہدایت کی اللہ نے ایمان والوں کو اس سچی بات کی جس میں وہ جھگڑ رہے تھے اپنے حکم سے اور اللہ بتلاتا ہے جس کو چاہے سیدھا راستہ ');
INSERT INTO "al_hind" VALUES(2,214,'2:214','کیا تم کو یہ خیال ہے کہ جنت میں چلے جاؤ گے حالانکہ تم پر نہیں گذرے حالات ان لوگوں جیسے جو ہو چکے تم سے پہلے کہ پہنچی انکو سختی اور تکلیف اور جھڑ جھڑائے گئے یہاں تک کہ کہنے لگا رسول اور جو اس کے ساتھ ایمان لائے کب آوے گی اللہ کی مدد سن رکھو اللہ کی مدد قریب ہے ');
INSERT INTO "al_hind" VALUES(2,215,'2:215','تجھ سے پوچھتے ہیں کہ کیا چیز خرچ کریں کہہ دو کہ جو کچھ تم خرچ کرو مال سو ماں باپ کے لئے اور قرابت والوں کے اور یتیموں کے اور محتاجوں کے اور مسافروں کے اور جو کچھ کرو گے تم بھلائی سو وہ بیشک اللہ کو خوب معلوم ہے ');
INSERT INTO "al_hind" VALUES(2,216,'2:216','فرض ہوئی تم پر لڑائی اور وہ بری لگتی ہے تم کو اور شاید کہ تم کو بری لگے ایک چیز اور وہ بہتر ہو تمہارے حق میں اور شاید تم کو بھلی لگے ایک چیز اور وہ بری ہو تمہارے حق میں اور اللہ جانتا ہے اور تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(2,217,'2:217','تجھ سے پوچھتے ہیں مہینہ حرام کو کہ اس میں لڑنا کیسا کہہ دے لڑائی اس میں بڑا گناہ ہے اور روکنا اللہ کی راہ سے اور اس کو نہ ماننا اور مسجد الحرام سے روکنا اور نکال دینا اسکے لوگوں کو وہاں سے اس سے بھی زیادہ گناہ ہے اللہ کے نزدیک اور لوگوں کو دین سے بچلانا قتل سے بھی بڑھ کر ہے اور کفار تو ہمیشہ تم سے لڑتے ہی رہیں گے یہاں تک کہ تم کو پھیر دیں تمہارے دین سے اگر قابو پاویں اور جو کوئی پھرے تم میں سے اپنے دین سے پھر مر جاوے حالت کفر ہی میں تو ایسوں کے ضائع ہوئے عمل دنیا اور آخرت میں اور وہ لوگ رہنے والے ہیں دوزخ میں وہ اس میں ہمیشہ رہیں گے ');
INSERT INTO "al_hind" VALUES(2,218,'2:218','بیشک جو لوگ ایمان لائے اور جنہوں نے ہجرت کی اور لڑے اللہ کی راہ میں وہ امیدوار ہیں اللہ کی رحمت کے اور اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(2,219,'2:219','تجھ سے پوچھتے ہیں حکم شراب کا اور جوئے کا کہدے ان دونوں میں بڑا گناہ ہے اور فائدے بھی ہیں لوگوں کو اور ان کا گناہ بہت بڑا ہے ان کے فائدے سے اور تجھ سے پوچھتے ہیں کہ کیا خرچ کریں کہدے جو بچے اپنے خرچ سے اسی طرح بیان کرتا ہے اللہ تمہارے واسطے حکم تا کہ تم فکر کرو');
INSERT INTO "al_hind" VALUES(2,220,'2:220','دنیا و آخرت کی باتوں میں اور تجھ سے پوچھتے ہیں یتیموں کا حکم کہدے سنوارنا ان کے کام کا بہتر ہے اور اگر ان کا خرچ ملا لو تو وہ تمہارے بھائی ہیں اور اللہ جانتا ہے خرابی کرنے والے اور سنوارنے والے کو اور اگر اللہ چاہتا تو تم پر مشقت ڈالتا بیشک اللہ زبردست ہے تدبیر والا ');
INSERT INTO "al_hind" VALUES(2,221,'2:221','اور نکاح مت کرو مشرک عورتوں سے جب تک ایمان نہ لے آئیں اور البتہ لونڈی مسلمان بہتر ہے مشرک بی بی سے اگرچہ وہ تم کو بھلی لگے اور نکاح نہ کر دو مشرکین سے جب تک وہ ایمان نے لے آویں اور البتہ غلام مسلمان بہتر ہے مشرک سے اگرچہ وہ تم کو بھلا لگے وہ بلاتےہیں دوزخ کی طرف اور اللہ بلاتا ہے جنت کی اور بخشش کی طرف اپنے حکم سے اور بتلاتا ہے اپنے حکم لوگوں کو تاکہ وہ نصیحت قبول کریں');
INSERT INTO "al_hind" VALUES(2,222,'2:222','اور تجھ سے پوچھتے ہیں حکم حیض کا کہدے وہ گندگی ہے سو تم الگ رہو عورتوں سے حیض کے وقت اور نزدیک نہ ہو ان کے جب تک پاک نہ ہوویں پھر جب خوب پاک ہو جاویں تو جاؤ ان کے پاس جہاں سے حکم دیا تم کو اللہ نے بیشک اللہ کو پسند آتے ہیں توبہ کرنے والے اور پسند آتے ہیں گندگی سے بچنے والے ');
INSERT INTO "al_hind" VALUES(2,223,'2:223','تمہاری عورتیں تمہاری کھیتی ہیں سو جاؤ اپنی کھیتی میں جہاں سے چاہو اور آگے کی تدبیر کرو اپنے واسطے اور ڈرتے رہو اللہ سے اور جان رکھو کہ تم کو اس سے ملنا ہے اور خوشخبری سنا ایمان والوں کو');
INSERT INTO "al_hind" VALUES(2,224,'2:224','اور مت بناؤ اللہ کے نام کو نشانہ اپنی قسمیں کھانے کے لئے کہ سلوک کرنے سے اور پرہیزگاری سے اور لوگوں میں صلح کرانے سے بچ جاؤ اور اللہ سب کچھ سنتا جانتا ہے ');
INSERT INTO "al_hind" VALUES(2,225,'2:225','نہیں پکڑتا تم کو اللہ بیہودہ قسموں پر تمہاری لیکن پکڑتا ہے تم کو ان قسموں پر کہ جن کا قصد کیا تمہارے دلوں نے اور اللہ بخشنے والا تحمل کرنے والا ہے ');
INSERT INTO "al_hind" VALUES(2,226,'2:226','جو لوگ قسم کھا لیتے ہیں اپنی عورتوں کے پاس جانے سے انکے لئے مہلت ہے چار مہینے کی پھر اگر باہم مل گئے تو اللہ بخشنے والا مہربان ہے');
INSERT INTO "al_hind" VALUES(2,227,'2:227','اور اگر ٹھہرا لیا چھوڑ دینے کو تو بیشک اللہ سننے والا جاننے والا ہے ');
INSERT INTO "al_hind" VALUES(2,228,'2:228','اور طلاق والی عورتیں انتظار میں رکھیں اپنے آپ کو تین حیض تک اور ان کو حلال نہیں کہ چھپا رکھیں جو پیدا کیا اللہ نے ان کے پیٹ میں اگر وہ ایمان رکھتی ہیں اللہ پر اور پچھلے دن پر اور ان کے خاوند حق رکھتے ہیں انکے لوٹا لینے کا اس مدت میں اگر چاہیں سلوک سے رہنا اور عورتوں کا بھی حق ہے جیسا کہ مردوں کا ان پر حق ہے دستور کے موافق اور مردوں کو عورتوں پر فضیلت ہے اور اللہ زبردست ہے تدبیر والا');
INSERT INTO "al_hind" VALUES(2,229,'2:229','طلاق رجعی ہے دو بار تک اسکے بعد رکھ لینا موافق دستور کے یا چھوڑ دینا بھلی طرح سے اور تم کو روا نہیں کہ لے لو کچھ اپنا دیا ہوا عورتوں سے مگر جبکہ خاوند عورت دونوں ڈریں اس بات سے کہ قائم نہ رکھ سکیں گے حکم اللہ کا پھر اگر تم لوگ ڈرو اس بات سے کہ وہ دونوں قائم نہ رکھ سکیں گے اللہ کا حکم تو کچھ گناہ نہیں دونوں پر اس میں کہ عورت بدلہ دیکر چھوٹ جاوے یہ اللہ کی باندھی ہوئی حدیں ہیں سو ان سے آگے مت بڑھو اور جو کوئی بڑھ چلے اللہ کی باندھی ہوئی حدوں سےسو وہی لوگ ہیں ظالم ');
INSERT INTO "al_hind" VALUES(2,230,'2:230','پھر اگر اس عورت کو طلاق دی یعنی تیسری بار تو اب حلال نہیں اس کو وہ عورت اس کے بعد جب تک نکاح نہ کرے کسی خاوند سے اسکے سوا پھر اگر طلاق دیدے دوسرا خاوند تو کچھ گناہ نہیں ان دونوں پر کہ پھر باہم مل جاویں اگر خیال کریں کہ قائم رکھیں گے اللہ کا حکم اور یہ حدیں باندھی ہوئی ہیں اللہ کی بیان فرماتا ہے ان کو واسطے جاننے والوں کے ');
INSERT INTO "al_hind" VALUES(2,231,'2:231','اور جب طلاق دی تم نے عورتوں کو پھر پہنچیں اپنی عدت تک تو رکھ لو ان کو موافق دستور کے یا چھوڑ دو ان کو بھلی طرف سے اور نہ روکے رکھو ان کو ستانے کے لئے تاکہ ان پر زیادتی کرو اور جو ایسا کرے گا وہ بیشک اپنا ہی نقصان کرے گا اور مت ٹھہراؤ اللہ کے احکام کو ہنسی اور یاد کرو اللہ کا احسان جو تم پر ہے اور اس کو کہ جو اتاری تم پر کتاب اور علم کی باتیں کہ تم کو نصیحت کرتا ہے اسکے ساتھ اور ڈرتے رہو اللہ سے اور جان رکھو کہ اللہ سب کچھ جانتا ہے ');
INSERT INTO "al_hind" VALUES(2,232,'2:232','اور جب طلاق دی تم نے عورتوں کو پھر پورا کر چکیں اپنی عدت کو تو آپ نہ روکو ان کو اس سے کہ نکاح کر لیں اپنے انہی خاوندوں سے جبکہ راضی ہو جاویں آپس میں موافق دستور کے یہ نصیحت اس کو کی جاتی ہے جو کہ تم میں سے ایمان رکھتا ہے اللہ پر اور قیامت کے دن پر اس میں تمہارے واسطے بڑی ستھرائی ہے اور بہت پاکیزگی اور اللہ جانتا ہے اور تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(2,233,'2:233','اور بچے والی عورتیں دودھ پلاویں اپنے بچوں کو دو برس پوری جو کوئی چاہے کہ پوری کرے دودھ کی مدت اور لڑکے والے یعنی باپ پر ہے کھانا اور کپڑا ان عورتوں کا موافق دستور کے تکلیف نہیں دی جاتی کسی کو مگر اس کی گنجائش کے موافق نہ نقصان دیا جاوے ماں کو اس کے بچہ کی وجہ سے اور نہ اس کو کہ جس کا وہ بچہ ہے یعنی باپ کو اسکے بچہ کی وجہ سے اور وارثوں پر بھی یہ لازم ہے پھر اگر ماں باپ چاہیں کہ دودھ چھڑا لیں یعنی دو برس کے اندر ہی اپنی رضا اور مشورہ سے تو ان پر کچھ گناہ نہیں اور اگر تم لوگ چاہو کہ دودھ پلواؤ کسی دایہ سے اپنی اولاد کو تو بھی تم پر کچھ گناہ نہیں جبکہ حوالہ کر دو جو تم نے دینا ٹھہرایا تھا موافق دستور کے اور ڈرو اللہ سے اور جان رکھو کہ اللہ تمہارے سب کاموں کو خوب دیکھتا ہے');
INSERT INTO "al_hind" VALUES(2,234,'2:234','اور جو لوگ مر جاویں تم میں سے اور چھوڑ جاویں اپنی عورتیں تو چاہئے کہ وہ عورتیں انتظار میں رکھیں اپنے آپ کو چار مہینے اور دس دن پھر جب پورا کر چکیں اپنی عدت کو تو تم پر کچھ گناہ نہیں اس بات میں کہ کریں وہ اپنے حق میں قاعدہ کے موافق اور اللہ کو تمہارے تمام کاموں کی خبر ہے');
INSERT INTO "al_hind" VALUES(2,235,'2:235','اور کچھ گناہ نہیں تم پر اس میں کہ اشارہ میں کہو پیغام نکاح ان عورتوں کا یا پوشیدہ رکھو اپنے دل میں اللہ کو معلوم ہے کہ تم البتہ ان عورتوں کا ذکر کرو گے لیکن ان سے نکاح کا وعدہ نہ کر رکھو چھپ کر مگر یہی کہ کہدو کوئی بات رواج شریعت کے موافق اور نہ ارادہ کرو نکاح کا یہاں تک کہ پہنچ جاوے عدت مقررہ اپنی انتہا کو اور جان رکھو کہ اللہ کو معلوم ہے جو کچھ تمہارے دل میں ہے سو اس سے ڈرتے رہو اور جان رکھو کہ اللہ بخشنے والا اور تحمل کرنے والا ہے ');
INSERT INTO "al_hind" VALUES(2,236,'2:236','کچھ گناہ نہیں تم پر اگر طلاق دو تم عورتوں کو اس وقت کہ ان کو ہاتھ بھی نہ لگایا ہو اور نہ مقرر کیا ہو ان کے لئے کچھ مہر اور ان کو کچھ خرچ دو مقدور والے پر اس کے موافق ہے اور تنگی والے پر اس کے موافق جو خرچ کہ قاعدہ کے موافق ہے لازم ہے نیکی کرنے والوں پر ');
INSERT INTO "al_hind" VALUES(2,237,'2:237','اور اگر طلاق دو ان کو ہاتھ لگانے سے پہلے اور ٹھہرا چکے تھے تم ان کے لئے مہر تو لازم ہوا آدھا اس کا کہ تم مقرر کر چکے تھے مگر یہ کہ درگذر کریں عورتیں یا درگذر کرے وہ شخص کہ اس کے اختیار میں ہے گرہ نکاح کی یعنی خاوند اور تم مرد درگذر کرو تو قریب ہے پرہیزگاری سے اور نہ بھلا دو احسان کرنا آپس میں بیشک اللہ جو کچھ تم کرتے ہو خوب دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(2,238,'2:238','خبردار رہو سب نمازوں سے اور بیچ والی نماز سے اور کھڑے رہو اللہ کے آگے ادب سے ');
INSERT INTO "al_hind" VALUES(2,239,'2:239','پھر اگر تم کو ڈر ہو کسی کا تو پیادہ پڑھ لو یا سوار پھر جس وقت تم امن پاؤ تو یاد کرو اللہ کو جس طرح کہ تم کو سکھایا ہے جس کو تم نہ جانتےتھے ');
INSERT INTO "al_hind" VALUES(2,240,'2:240','اور جو لوگ تم میں سے مر جاویں اور چھوڑ جاویں اپنی عورتیں تو وہ وصیت کر دیں اپنی عورتوں کے واسطے خرچ دینا ایک برس تک بغیر نکالنے کے گھر سے پھر اگر وہ عورتیں آپ نکل جاویں تو کچھ گناہ نہیں تم پر اس میں کہ کریں وہ عورتیں اپنے حق میں بھلی بات اور اللہ زبردست ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(2,241,'2:241','اور طلاق دی ہوئی عورتوں کے واسطے خرچ دینا ہے قاعدہ کے موافق لازم ہے پرہیزگاروں پر ');
INSERT INTO "al_hind" VALUES(2,242,'2:242','اسی طرح بیان فرماتا ہے اللہ تمہارے واسطے اپنے حکم تاکہ تم سمجھ لو ');
INSERT INTO "al_hind" VALUES(2,243,'2:243','کیا نہ دیکھا تو نے ان لوگوں کو جو کہ نکلے اپنے گھروں سے اور وہ ہزاروں تھے موت کے ڈر سے پھر فرمایا ان کو اللہ نے کہ مر جاؤ پھر ان کو زندہ کر دیا بیشک اللہ فضل کرنے والا ہے لوگوں پر لیکن اکثر لوگ شکر نہیں کرتے ');
INSERT INTO "al_hind" VALUES(2,244,'2:244','اور لڑو اللہ کی راہ میں اور جان لو کہ اللہ بیشک خوب سنتا جانتا ہے');
INSERT INTO "al_hind" VALUES(2,245,'2:245','کو ن شخص ہے ایسا جو کہ قرض دے اللہ کو اچھا قرض پھر دوگنا کر دے اللہ اس کو کئ گنا اور اللہ ہی تنگی کردیتا ہے اور وہی کشائش کرتا ہے اور اسی کی طرف تم لوٹائے جاؤ گے ');
INSERT INTO "al_hind" VALUES(2,246,'2:246','کیا نہ دیکھا تو نے ایک جماعت بنی اسرائیل کو موسٰیؑ کے بعد جب انہوں نےکہا اپنےنبی سے مقرر کر دو ہمارے لئے ایک بادشاہ تا کہ ہم لڑیں اللہ کی راہ میں پیغمبر نے کہا کیا تم سے یہ بھی توقع ہے کہ اگر حکم ہو تم کو لڑائی کا تو تم اس وقت نہ لڑو وہ بولے ہم کو کیا ہوا کہ ہم نہ لڑیں اللہ کی راہ میں اور ہم تو نکال دیے گئے اپنےگھروں سے اور بیٹوں سے پھر جب حکم ہوا ان کو لڑائی کا تو وہ سب پھر گئے مگر تھوڑے سے ان میں کے اور اللہ تعالیٰ خوب جانتا ہےگنہگاروں کو ');
INSERT INTO "al_hind" VALUES(2,247,'2:247','اور فرمایا ان سے ان کے نبی نے بیشک اللہ نے مقرر فرما دیا تمہارے لئے طالوت کو بادشاہ کہنےلگے کیونکر ہو سکتی ہے اس کو حکومت ہم پر اور ہم زیادہ مستحق ہیں سلطنت کے اس سے اور اس کو نہیں ملی کشائش مال میں پیغمبر نے کہا بیشک اللہ نے پسند فرمایا اس کو تم پر اور زیادہ فراخی دی اس کو علم اور جسم میں اور اللہ دیتا ہے ملک اپنا جس کو چاہے اور اللہ ہے فضل کرنے والا سب کچھ جاننے والا ');
INSERT INTO "al_hind" VALUES(2,248,'2:248','اور کہا بنی اسرائیل سے ان کے نبی نے کہ طالوت کی سلطنت کی نشانی یہ ہے کہ آوے تمہارےپاس ایک صندوق کہ جس میں تسلی خاطر ہے تمہارے رب کی طرف سے اور کچھ بچی ہوئی چیزیں ہیں ان میں سے جو چھوڑ گئ تھی موسٰیؑ اور ہارونؑ کی اولاد اٹھا لاویں گے اس صندوق کو فرشتے بیشک اس میں پوری نشانی ہے تمہارے واسطے اگر تم یقین رکھتے ہو ');
INSERT INTO "al_hind" VALUES(2,249,'2:249','پھر جب باہر نکلا طالوت فوجیں لے کر کہا بیشک اللہ تمہاری آزمائش کرتا ہے ایک نہر سے سو جس نے پانی پیا اس نہر کا تو وہ میرا نہیں اور جس نے اس کو نہ چکھا تو وہ بیشک میرا ہے مگر جو کوئی بھرےایک چلو اپنے ہاتھ سے پھر پی لیا سب نے اس کا پانی مگر تھوڑوں نے ان میں سے پھر جب پار ہوا طالوت اور ایمان والے ساتھ اس کے تو کہنےلگے طاقت نہیں ہم کو آج جالوت اور اس کے لشکروں سے لڑنے کی کہنے لگے وہ لوگ جن کو خیال تھا کہ ان کو اللہ سےملنا ہے بارہا تھوڑی جماعت غالب ہوئی ہے بڑی جماعت پر اللہ کے حکم سے اور اللہ صبر کرنے والوں کے ساتھ ہے ');
INSERT INTO "al_hind" VALUES(2,250,'2:250','اور جب سامنے ہوئے جالوت کے اور اس کی فوجوں کے تو بولے اے رب ہمارےڈال دے ہمارے دلوں میں صبر اور جمائے رکھ ہمارے پاؤں اور مدد کر ہماری اس کافر قوم پر');
INSERT INTO "al_hind" VALUES(2,251,'2:251','پھر شکست دی مومنوں نے جالوت کے لشکر کو اللہ کے حکم سے اور مار ڈالا داؤد نے جالوت کو اور دی داؤد کو اللہ نے سلطنت اور حکمت اور سکھایا ان کو جو چاہاا اور اگر نہ ہوتا دفع کرا دینا اللہ کا ایک کو دوسرے سے تو خراب ہو جاتا ملک لیکن اللہ بہت مہربان ہے جہان کے لوگوں پر ');
INSERT INTO "al_hind" VALUES(2,252,'2:252','یہ آیتیں اللہ کی ہیں ہم تجھ کو سناتے ہیں ٹھیک ٹھیک اور تو بیشک ہمارے رسولوں میں ہے ');
INSERT INTO "al_hind" VALUES(2,253,'2:253','یہ سب رسول فضیلت دی ہم نے ان میں بعض کو بعض سے کوئی تو وہ ہے کہ کلام فرمایا اس سے اللہ نے اور بلند کئے بعضوں کے درجے اور دیے ہم نے عیسٰیؑ مریم کے بیٹے کو معجزے صریح اور قوت دی اس کو روح القدس یعنی جبریل سے اور اگر اللہ چاہتا تو نہ لڑتے وہ لوگ جو ہوئے ان پیغمبروں کے پیچھے بعد اس کے کہ پہنچ چکے ان کے پاس صاف حکم لیکن ان میں اختلاف پڑ گیا پھر کوئی تو ان میں ایمان لایا اور کوئی کافر رہا اور اگر چاہتا اللہ تو وہ باہم نہ لڑتے لیکن اللہ کرتا ہے جو چاہے ');
INSERT INTO "al_hind" VALUES(2,254,'2:254','اے ایمان والو خرچ کرو اس میں سے جو ہم نے تم کو روزی دی پہلے اس دن کے آنے سے کہ جس میں نہ خرید و فرخت ہے اور نہ آشنائی اور نہ سفارش اور جو کافر ہیں وہی ہیں ظالم ');
INSERT INTO "al_hind" VALUES(2,255,'2:255','اللہ اس کے سوا کوئی معبود نہں زندہ ہے سب کا تھامنے والا نہیں پکڑ سکتی اس کو اونگھ اور نہ نیند اسی کا ہے جو کچھ آسمانوں اور زمین میں ہے ایسا کون ہے جو سفارش کرے اس کے پاس مگر اجازت سے جانتا ہے جو کچھ خلقت کے روبرو ہے اور جو کچھ انکے پیچھے ہے اور وہ سب احاطہ نہیں کر سکتے کسی چیز کا اس کی معلومات میں سے مگر جتنا کہ وہی چاہے گنجائش ہے اس کی کرسی میں تمام آسمانوں اور زمین کو اور گراں نہیں اس کو تھامنا ان کا اور وہی ہے سب سے برتر عظمت والا ');
INSERT INTO "al_hind" VALUES(2,256,'2:256','زبردستی نہں دین کے معاملہ میں بیشک جدا ہو چکی ہے ہدیات گمراہی سے اب جو کوئی نہ مانےگمراہ کرنےوالوں کو اور یقین لاوےاللہ پر تو اس نے پکڑ لیا حلقہ مضبوط جو ٹوٹنے والا نہیں اور اللہ سب کچھ سنتا جانتا ہے ');
INSERT INTO "al_hind" VALUES(2,257,'2:257','اللہ مددگار ہے ایمان والوں کا نکالتا ہے ان کو اندھیروں سے روشنی کی طرف اور جو لوگ کافر ہوئے ان کے رفیق ہیں شیطان نکالتے ہیں ان کو روشنی سے اندھیروں کی طرف یہی لوگ ہیں دوزخ میں رہنے والے وہ اسی میں ہمیشہ رہیں گے');
INSERT INTO "al_hind" VALUES(2,258,'2:258','کیا نہ دیکھا تو نے اس شخص کو جس نے جھگڑا کیا ابراہیم سے اس کے رب کی بابت اسی وجہ سے کہ دی تھی اللہ نے اس کو سلطنت جب کہا ابراہیم نے میرا رب وہ ہے جو زندہ کرتا ہے اور مارتا ہے وہ بولا میں بھی جلاتا اور مارتا ہوں کہا ابراہیم نے کہ بیشک اللہ تو لاتا ہے سورج کو مشرق سے اب تو لے آ اس کو مغرب کی طرف سے تب حیران رہ گیا وہ کافر اور اللہ سیدھی راہ نہیں دکھاتا بے انصافوں کو ');
INSERT INTO "al_hind" VALUES(2,259,'2:259','یا نہ دیکھا تو نے اس شخص کو کہ گذرا وہ ایک شہر پر اور وہ گرا پڑا تھا اپنی چھتوں پر بولا کیونکر زندہ کرے گا اس کو اللہ مر گئے پیچھے پھر مردہ رکھا اس شخص کو اللہ نے سو برس پھر اٹھایا اس کو کہا تو کتنی دیر یہاں رہا بولا میں رہا ایک دن یا ایک دن سے کچھ کم کہا نہیں بلکہ تو رہا سو برس اب دیکھ اپنا کھانا اور پینا سڑ نہیں گیا اور دیکھ اپنے گدھے کو اور ہم نے تجھ کو نمونہ بنانا چاہا لوگوں کے واسطے اور دیکھ ہڈیوں کی طرف کہ ہم ان کو کس طرح ابھار کر جوڑ دیتےہیں پھر ان پر پہناتے ہیں گوشت پھر جب اس پر ظاہر ہوا یہ حال تو کہہ اٹھا کہ مجھ کو معلوم ہے کہ بیشک اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(2,260,'2:260','اور یاد کر جب کہا ابراہیم نے اے پروردگار میرے دکھلا دے مجھ کو کہ کیونکر زندہ کرے گا تو مردے فرمایا کیا تو نے یقین نہیں کیا کہا کیوں نہیں لیکن اس واسطے چاہتا ہوں کہ تسکین ہو جاوے میرے دل کو فرمایا تو پکڑ لے چار جانور اڑنے والے پھر انکو ہلا لے اپنے ساتھ پھر رکھ دے ہر پہاڑ پر ان کے بدن کا ایک ایک ٹکڑا پھر ان کو بلا چلے آویں گے تیرے پاس دوڑتے اور جان لے کہ بیشک اللہ زبردست ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(2,261,'2:261','مثال ان لوگوں کی جو خرچ کرتے ہیں اپنے مال اللہ کی راہ میں ایسی ہے کہ جیسے ایک دانہ اس سے اگیں سات بالیں ہر بال میں سو سو دانے اور اللہ بڑھاتا ہے جس کے واسطے چاہے اور اللہ بینہایت بخشش کرنے والا ہے سب کچھ جانتا ہے ');
INSERT INTO "al_hind" VALUES(2,262,'2:262','جو لوگ خرچ کرتے ہیں اپنے مال اللہ کی راہ میں پھر خرچ کرنے کے بعد نہ احسان رکھتے ہیں اور نہ ستاتے ہیں انہی کے لئے ہے ثواب ان کا اپنے رب کے یہاں اور نہ ڈر ہے ان پر اور نہ غمگین ہوں گے ');
INSERT INTO "al_hind" VALUES(2,263,'2:263','جواب دینا نرم اور درگذر کرنا بہتر ہے اس خیرات سے جس کے پیچھے ہو ستانا اور اللہ بے پروا ہے نہایت تحمل والا ');
INSERT INTO "al_hind" VALUES(2,264,'2:264','اے ایمان والو مت ضائع کرو اپنی خیرات احسان رکھ کر اور ایذا دے کر اس شخص کی طرح جو خرچ کرتا ہے اپنا مال لوگوں کے دکھانے کو اور یقین نہیں رکھتا ہے اللہ پر اور قیامت کے دن پر سو اس کی مثال ایسی ہے جیسے صاف پتھر کہ اس پر پڑی ہے کچھ مٹی پھر برسا اس پر زور کا مینہ تو کر چھوڑا اس کو بالکل صاف کچھ ہاتھ نہیں لگتا ایسے لوگوں کے ثواب اس چیز کا جو انہوں نےکمایا اور اللہ نہیں دکھاتا سیدھی راہ کافروں کو ');
INSERT INTO "al_hind" VALUES(2,265,'2:265','اور مثال ان کی جو خرچ کرتے ہیں اپنے مال اللہ کی خوشی حاصل کرنے کو اور اپنے دلوں کو ثابت کر کر ایسی ہے جیسے ایک باغ ہے بلند زمین پر اس پر پڑا زور کا مینہ تو لایا وہ باغ اپنا پھل دو چند اور اگر نہ پڑا اس پر مینہ تو پھورا ہی کافی ہے اور اللہ تمہارے کاموں کو خوب دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(2,266,'2:266','کیا پسند آتا ہے تم میں سے کسی کو یہ کہ ہووے اس کا ایک باغ کھجور اور انگور کا بہتی ہوں نیچے اس کے نہریں اس کو اس باغ میں اور بھی سب طرح کا میوہ حاصل ہو اور آ گیا اس پر بڑھاپا اور اس کی اولاد ہیں ضعیف تب آ پڑا اس باغ پر ایک بگولا جس میں آگ تھی جس سے وہ باغ جل گیا یوں سمجھاتا ہے تم کو اللہ آیتیں تاکہ تم غور کرو ');
INSERT INTO "al_hind" VALUES(2,267,'2:267','اے ایمان والو خرچ کرو ستھری چیزیں اپنی کمائی میں سے اور اس چیز میں سے کہ جو ہم نے پیدا کیا تمہارے واسطے زمین سے اور قصد نہ کرو گندی چیز کا اس میں سے کہ اس کو خرچ کرو حالانکہ تم اس کو کبھی نہ لو گے مگر یہ کہ چشم پوشی کر جاؤ اور جان رکھو کہ اللہ بے پروا ہے خوبیوں والا ');
INSERT INTO "al_hind" VALUES(2,268,'2:268','شیطان وعدہ دیتا ہے تم کو تنگدستی کا اور حکم کرتا ہے بے حیائی کا اور اللہ وعدہ دیتا ہے تم کو اپنی بخشش اور فضل کا اور اللہ بہت کشائش والا ہے سب کچھ جانتا ہے ');
INSERT INTO "al_hind" VALUES(2,269,'2:269','عنایت کرتا ہے سمجھ جس کسی کو چاہے اور جس کو سمجھ ملی اس کو بڑی خوبی ملی اور نصیحت وہی قبول کرتے ہیں جو عقل والے ہیں ');
INSERT INTO "al_hind" VALUES(2,270,'2:270','اور جو خرچ کرو گے تم خیرات یا قبول کرو گے کو ئی منت تو بیشک اللہ کو سب معلوم ہے اور ظالموں کا کوئی مدگار نہیں ');
INSERT INTO "al_hind" VALUES(2,271,'2:271','اگر ظاہر کر کے دو خیرات تو کیا اچھی بات اور اگر اس کو چھپاؤ اور فقیروں کو پہنچاؤ تو وہ بہتر ہے تمہارے حق میں اور دور کرے گا کچھ گناہ تمہارے اور اللہ تمہارے کاموں سے خوب خبردار ہے ');
INSERT INTO "al_hind" VALUES(2,272,'2:272','تیرا ذمہ نہیں انکو راہ پر لانا اور لیکن اللہ راہ پر لاوے جس کو چاہے اور جو کچھ خرچ کرو گے تم مال سو اپنے ہی واسطے جب تک کہ خرچ کرو گے اللہ ہی کی رضاجوئی میں اور جو کچھ خرچ کرو گے خیرات سو پوری ملے گی تم کو اور تمہارا حق نہ رہے گا ');
INSERT INTO "al_hind" VALUES(2,273,'2:273','خیرات ان فقیروں کے لئے ہے جو رکے ہوئے ہیں اللہ کی راہ میں چل پھر نہیں سکتے ملک میں سمجھے ان کو ناواقف مالدار ان کے سوال نہ کرنے سے تو پہچانتا ہے ان کو انکے چہرہ سے نہیں سوال کرتے لوگوں سے لپٹ کر اور جو کچھ خرچ کرو گے کام کی چیز وہ بیشک اللہ کو معلوم ہے ');
INSERT INTO "al_hind" VALUES(2,274,'2:274','جو لوگ خرچ کرتے ہیں اپنے مال اللہ کی راہ میں رات کو اور دن کو چھپا کر اور ظاہر میں تو انکے لئے ہے ثواب ان کا اپنے رب کے پاس اور نہ ڈر ہے ان پر اور نہ وہ غمگین ہوں گے ');
INSERT INTO "al_hind" VALUES(2,275,'2:275','جو لوگ کھاتےہیں سود نہیں اٹھیں گے قیامت کو مگر جس طرح اٹھتا ہے وہ شخص کہ جس کے حواس کھو دیے ہوں جن نے لپٹ کر یہ حالت انکی اس واسطے ہو گی کہ انہوں نے کہا کہ سوداگری بھی تو ایسی ہی ہے جیسے سود لینا حالانکہ اللہ نے حلال کیا ہے سوداگری اور حرام کیا ہے سود کو پھر جس کو پہنچی نصیحت اپنے رب کی طرف سے اور وہ باز آ گیا تو اس کے واسطے ہے جو پہلے ہو چکا اور معاملہ اس کا اللہ کے حوالہ ہے اور جو کوئی پھر سود لیوے تو وہی لوگ ہیں دوزخ والے وہ اس میں ہمیشہ رہیں گے ');
INSERT INTO "al_hind" VALUES(2,276,'2:276','مٹاتا ہے اللہ سود کو اور بڑھاتا ہے خیرات کو اور اللہ خوش نہیں کسی نا شکر گنہگار سے ');
INSERT INTO "al_hind" VALUES(2,277,'2:277','جو لوگ ایمان لائے اور عمل نیک کئے اور قائم رکھا نماز کو اور دیتے رہے زکوٰۃ انکے لئے ہے ثواب ان کا اپنے رب کے پاس اور نہ ان کو خوف ہے اور نہ وہ غمگین ہوں گے ');
INSERT INTO "al_hind" VALUES(2,278,'2:278','اے ایمان والو ڈرو اللہ سے اور چھوڑ دو جو کچھ باقی رہ گیا ہے سود اگر تم کو یقین ہے اللہ کے فرمانے کا ');
INSERT INTO "al_hind" VALUES(2,279,'2:279','پھر اگر نہیں چھوڑتے تو تیار ہو جاؤ لڑنے کو اللہ سے اور اس کے رسول سے اور اگر توبہ کرتے ہو تو تمہارے واسطے ہے اصل مال تمہارا نہ تم کسی پر ظلم کرو اور نہ کوئی تم پر ');
INSERT INTO "al_hind" VALUES(2,280,'2:280','اور اگر ہے تنگدست تو مہلت دینی چاہیئے کشائش ہونے تک اور بخش دو تو بہت بہتر ہے تمہارے لئے اگر تم کو سمجھ ہو ');
INSERT INTO "al_hind" VALUES(2,281,'2:281','اور ڈرتے رہو اس دن سے کہ جس دن لوٹائے جاؤ گے اللہ کی طرف پھر پورا دیا جائے گا ہر شخص کو جو کچھ اس نےکمایا اور ان پر ظلم نہ ہو گا ');
INSERT INTO "al_hind" VALUES(2,282,'2:282','اے ایمان والو جب تم آپس میں معاملہ کرو ادھار کا کسی وقت مقرر تک تو اس کو لکھ لیا کرو اور چاہئے کہ لکھ دے تمہارے درمیان کوئی لکھنے والا انصاف سے اور انکار نہ کرے لکھنے والا اس سے کہ لکھ دیوے جیسا سکھایا اس کو اللہ نے سو اس کو چاہئے کہ لکھ دے اور بتلاتا جاوے وہ شخص کہ جس پر قرض ہے اور ڈرے اللہ سے جو اس کا رب ہے اور کم نہ کرے اس میں سےکچھ پھر اگر وہ شخص کہ جس پر قرض ہے بے عقل ہے یا ضعیف ہے یا آپ نہیں بتلا سکتا تو بتلاوے کارگذار اس کا انصاف سے اور گواہ کرو دو شاہد اپنے مردوں میں سے پھر اگر نہ ہوں دو مرد تو ایک مرد اور دو عورتیں ان لوگوں میں سے کہ جن کو تم پسند کرتے ہو گواہوں میں تاکہ اگر بھول جائے ایک ان میں سے تو یاد دلاوے اس کو وہ دوسری اور انکار نہ کریں گواہ جس وقت بلائے جاویں اور کاہلی نہ کرو اس کے لکھنے سے چھوٹا ہو معاملہ یا بڑا اس کی میعاد تک اس میں پورا انصاف ہے اللہ کے نزدیک اور بہت درست رکھنے والا ہے گواہی کو اور نزدیک ہے کہ شبہ میں نہ پڑو مگر یہ کہ سودا ہو ہاتھوں ہاتھ لیتے دیتے ہو اس کو آپس میں تو تم پر کچھ گناہ نہیں اگر اس کو نہ لکھو اور گواہ کر لیا کرو جب تم سودا کرو اور نقصان نہ کرےلکھنے والا اور نہ گواہ اور اگر ایسا کرو تو یہ گناہ کی بات ہے تمہارے اندر اور ڈرتے رہو اللہ سے اور اللہ تم کو سکھلاتا ہے اور اللہ ہر ایک چیز کو جانتا ہے');
INSERT INTO "al_hind" VALUES(2,283,'2:283','اور اگر تم سفر میں ہو اور نہ پاؤ کوئی لکھنے والا تو گرو ہاتھ میں رکھنی چاہیئے پھر اگر اعتبار کرے ایک دوسرے کا تو چاہئے کہ پورا ادا کرے وہ شخص کہ جس پر اعتبار کیا اپنی امانت کو اور ڈرتا رہے اللہ سے جو رب ہے اس کا اور مت چھپاؤ گواہی کو اور جو شخص اس کو چھپاوے تو بیشک گنہگار ہے دل اس کا اور اللہ تمہارے کاموں کو خوب جانتا ہے ');
INSERT INTO "al_hind" VALUES(2,284,'2:284','اللہ ہی کا ہے جو کچھ کہ آسمانوں اور زمین میں ہے اور اگر ظاہر کرو گے اپنے جی کی بات یا چھپاؤ گے اس کو حساب لے گا اس کا تم سے اللہ پھر بخشے گا جس کو چاہے اور عذاب کرے گا جس کو چاہے اور اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(2,285,'2:285','مان لیا رسول نے جو کچھ اترا اس پر اس کے رب کی طرف سے اور مسلمانوں نے بھی سب نے مانا اللہ کو اور اس کے فرشتوں کو اور اس کی کتابوں کو اور اس کے رسولوں کو کہتے ہیں کہ ہم جدا نہیں کرتے کسی کو اس کے پیغمبروں میں سے اور کہہ اٹھے کہ ہم نےسنا اور قبول کیا تیری بخشش چاہتے ہیں اے ہمارے رب اور تیری ہی طرف لوٹ کر جانا ہے ');
INSERT INTO "al_hind" VALUES(2,286,'2:286','اللہ تکلیف نہیں دیتا کسی کو مگر جس قدر اس کی گنجائش ہے اس کو ملتا ہے جو اس نے کمایا اور اسی پر پڑتا ہے جو اس نے کیا اے رب ہمارے نہ پکڑ ہم کو اگر ہم بھولیں یا چوکیں اے رب ہمارے اور نہ رکھ ہم پر بوجھ بھاری جیسا رکھا تھا ہم سے اگلے لوگوں پر اے رب ہمارے اور نہ اٹھوا ہم سے وہ بوجھ کہ جس کی ہم کو طاقت نہیں اور درگذر کر ہم سے اور بخش ہم کو اور رحم کر ہم پر تو ہی ہمارا رب ہے مدد کر ہماری کافروں پر ');
INSERT INTO "al_hind" VALUES(3,1,'3:1','الم');
INSERT INTO "al_hind" VALUES(3,2,'3:2','اللہ اس کے سوا کوئی معبود نہیں زندہ ہے سب کا تھامنے والا ');
INSERT INTO "al_hind" VALUES(3,3,'3:3','اتاری تجھ پر کتاب سچی تصدیق کرتی ہے اگلی کتابوں کی اور اتارا توریت اور انجیل کو');
INSERT INTO "al_hind" VALUES(3,4,'3:4','اس کتاب سے پہلے لوگوں کی ہدایت کے لئے اور اتارے فیصلے بیشک جو منکر ہوئے اللہ کی آیتوں سے انکے واسطے سخت عذاب ہے اور اللہ زبردست ہے بدلا لینے والا ');
INSERT INTO "al_hind" VALUES(3,5,'3:5','اللہ پر چھپی نہیں کوئی چیز زمین میں اور نہ آسمان میں ');
INSERT INTO "al_hind" VALUES(3,6,'3:6','وہی تمہارا نقشہ بناتا ہے ماں کے پیٹ میں جس طرح چاہے کسی کی بندگی نہیں اسکے سوا زبردست ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(3,7,'3:7','وہی ہے جس نے اتاری تجھ پر کتاب اس میں بعض آیتیں ہیں محکم یعنی انکے معنیٰ واضح ہیں وہ اصل ہیں کتاب کی اور دوسری ہیں متشابہ یعنی جنکے معنیٰ معلوم یا معین نہیں سو جن کے دلوں میں کجی ہے وہ پیروی کرتے ہیں متشابہات کی گمراہی پھیلانے کی غرض سے اور مطلب معلوم کرنے کی وجہ سے اور ان کا مطلب کوئی نہیں جانتا سوا اللہ کے اور مضبوط علم والے کہتےہیں ہم اس پر یقین لائے سب ہمارے رب کی طرف سے اتری ہیں اور سمجھانے سے وہی سمجھتے ہیں جن کو عقل ہے ');
INSERT INTO "al_hind" VALUES(3,8,'3:8','اے رب نہ پھیر ہمارے دلوں کو جب تو ہم کو ہدایت کر چکا اور عنایت کر ہم کو اپنے پاس سے رحمت تو ہی ہے سب کچھ دینے والا ');
INSERT INTO "al_hind" VALUES(3,9,'3:9','اے رب تو جمع کرنے والا ہے لوگوں کو ایک دن جس میں کچھ شبہ نہیں بیشک اللہ خلاف نہیں کرتا اپنا وعدہ ');
INSERT INTO "al_hind" VALUES(3,10,'3:10','بیشک جو لوگ کافر ہیں ہر گز کام نہ آویں گے ان کو ان کے مال اور نہ ان کی اولاد اللہ کے سامنے کچھ اور وہی ہیں ایندھن دوزخ کے ');
INSERT INTO "al_hind" VALUES(3,11,'3:11','جیسے دستور فرعون والوں کا اور جو ان سے پہلے تھے جھٹلایا انہوں نے ہماری آیتوں کو پھر پکڑا ان کو اللہ نے ان کے گناہوں پر اور اللہ کا عذاب سخت ہے ');
INSERT INTO "al_hind" VALUES(3,12,'3:12','کہہ دے کافروں کو کہ اب تم مغلوب ہو گے اور ہانکے جاؤ گے دوزخ کی طرف اور کیا برا ٹھکانہ ہے ');
INSERT INTO "al_hind" VALUES(3,13,'3:13','ابھی گذر چکا ہے تمہارے سامنے ایک نمونہ دو فوجوں میں جن میں مقابلہ ہوا ایک فوج ہے کہ لڑتی ہے اللہ کی راہ میں اور دوسری فوج کافروں کی ہے دیکھتے ہیں یہ ان کو اپنے سے دو چند صریح آنکھوں سے اور اللہ زور دیتا ہے اپنی مدد کا جس کو چاہے اسی میں عبرت ہے دیکھنے والوں کو ');
INSERT INTO "al_hind" VALUES(3,14,'3:14','فریفتہ کیا ہے لوگوں کو مرغوب چیزوں کی محبت نے جیسے عورتیں اور بیٹے اور خزانے جمع کئے ہوئے سونے اور چاندی کے اور گھوڑے نشان لگائے ہوئے اور مویشی اور کھیتی یہ فائدہ اٹھانا ہے دنیا کی زندگی میں اور اللہ ہی کے پاس ہے اچھا ٹھکانہ ');
INSERT INTO "al_hind" VALUES(3,15,'3:15','کہدے کیا بتاؤں میں تمکو اس سے بہتر پرہیزگاروں کے لئے اپنے رب کے ہاں باغ ہیں جن کے نیچے جاری ہیں نہریں ہمیشہ رہیں گے ان میں اور عورتیں ہیں ستھری اور رضامندی اللہ کی اور اللہ کی نگاہ میں ہیں بندے ');
INSERT INTO "al_hind" VALUES(3,16,'3:16','وہ جو کہتے ہیں اے رب ہمارے ہم ایمان لائے ہیں سو بخش دے ہم کو گناہ ہمارے اور بچا ہم کو دوزخ کے عذب سے ');
INSERT INTO "al_hind" VALUES(3,17,'3:17','وہ صبر کرنے والے ہیں اور سچے اور حکم بجا لانے والے اور خرچ کرنے والے اور گناہ بخشوانے والے پچھلی رات میں ');
INSERT INTO "al_hind" VALUES(3,18,'3:18','اللہ نے گواہی دی کہ کسی کی بندگی نہیں اس کے سوا اور فرشتوں نے اور علم والوں نے بھی وہی حاکم انصاف کا ہے کسی کی بندگی نہیں سوا اس کے زبردست ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(3,19,'3:19','بیشک دین جو ہے اللہ کے ہاں سو یہی مسلمانی حکمبرداری ہے اور مخالف نہیں ہوئے کتاب والے مگر جب ان کو معلوم ہو چکا آپس کی ضد اور حسد سے اور جو کوئی انکار کرے اللہ کے حکموں کا تو اللہ جلدی حساب لینے والا ہے ');
INSERT INTO "al_hind" VALUES(3,20,'3:20','پھر بھی اگر تجھ سے جھگڑیں تو کہہ دے میں نے تابع کیا اپنا منہ اللہ کے حکم پر اور انہوں نے بھی کہ جو میرے ساتھ ہیں اور کہہ دے کتاب والوں کو اور ان پڑھوں کو کہ تم بھی تابع ہوتے ہو پھر اگر وہ تابع ہوئے تو انہوں نے راہ پائی سیدھی اور اگر منہ پھریں تو تیرے ذمہ صرف پہنچا دینا ہے اور اللہ کی نگاہ میں ہیں بندے ');
INSERT INTO "al_hind" VALUES(3,21,'3:21','جو لوگ انکار کرتے ہیں اللہ کے حکموں کا اور قتل کرتے ہیں پیغمبروں کو ناحق اور قتل کرتے ہیں ان کو جو حکم کرتے ہیں انصاف کرنے کا لوگوں میں سے سو خوشخبری سنا دے انکو عذاب دردناک کی');
INSERT INTO "al_hind" VALUES(3,22,'3:22','یہی ہیں جن کی محنت ضائع ہوئی دنیا میں اور آخرت میں اور کوئی نہیں ان کا مددگار ');
INSERT INTO "al_hind" VALUES(3,23,'3:23','کیا نہ دیکھا تو نے ان لوگوں کو جن کو ملا کچھ ایک حصہ کتاب کا ان کو بلاتے ہیں اللہ کی کتاب کی طرف تاکہ وہ کتاب ان میں حکم کرے پھر منہ پھیرتےہیں بعضے ان میں سےتغافل کر کے ');
INSERT INTO "al_hind" VALUES(3,24,'3:24','یہ اس واسطے کہ کہتے ہیں وہ ہم کو ہرگز نہ لگے گی آگ دوزخ کی مگر چند دن گنتی کے اور بہکے ہیں اپنے دین میں اپنی بنائی باتوں پر ');
INSERT INTO "al_hind" VALUES(3,25,'3:25','پھر کیا ہو گا حال جب ہم انکو جمع کریں گے ایک دن کہ اسکے آنے میں کچھ شبہ نہیں اور پورا پاوے گا ہر کوئی اپنا کیا اور انکی حق تلفی نہ ہو گی ');
INSERT INTO "al_hind" VALUES(3,26,'3:26','تو کہہ یا اللہ مالک سلطنت کے تو سلطنت دیوے جس کو چاہے اور سلطنت چھین لیوے جس سے چاہے اور عزت دیوے جس کو چاہے اور ذلیل کرے جس کو چاہے تیرے ہاتھ ہے سب خوبی بیشک تو ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(3,27,'3:27','تو داخل کرتا ہے رات کو دن میں اور داخل کرے دن کو رات میں اور تو نکالے زندہ مردہ سے اور نکالے مردہ زندہ سے اور تو رزق دے جس کو چاہے بے شمار ');
INSERT INTO "al_hind" VALUES(3,28,'3:28','نہ بناویں مسلمان کافروں کو دوست مسلمانوں کو چھوڑ کر اور جو کوئی یہ کام کرے تو نہیں اس کو اللہ سے کوئی تعلق مگر اس حالت میں کہ کرنا چاہو تم ان سے بچاؤ اور اللہ تم کو ڈراتا ہے اپنے سے اور اللہ ہی کی طرف لوٹ کر جانا ہے ');
INSERT INTO "al_hind" VALUES(3,29,'3:29','تو کہہ اگر تم چھپاؤ گے اپنے جی کی بات یا اسے ظاہر کرو گے جانتا ہے اس کو اللہ اور اس کو معلوم ہے جو کچھ کہ ہے آسمانوں میں اور جو کچھ ہے زمین میں اور اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(3,30,'3:30','جس دن موجود پاوے گا ہر شخص جو کچھ کہ کی ہے اس نے نیکی اپنےسامنے اور جو کچھ کہ کی ہے اس نے برائی آرزو کرے گا کہ مجھ میں اور اس میں فرق پڑ جاوے دور کا اور اللہ ڈراتا ہے تم کو اپنے سے اور اللہ بہت مہربان ہے بندوں پر ');
INSERT INTO "al_hind" VALUES(3,31,'3:31','تو کہہ اگر تم محبت رکھتے ہو اللہ کی تو میری راہ چلو تاکہ محبت کرے تم سے اللہ اور بخشے گناہ تمہارے اور اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(3,32,'3:32','تو کہہ حکم مانو اللہ کا اور رسول کا پھر اگر اعراض کریں تو اللہ کو محبت نہیں ہے کافروں سے ');
INSERT INTO "al_hind" VALUES(3,33,'3:33','بیشک اللہ نے پسند کیا آدم کو اور نوح کو اور ابراہیم کے گھر کو اور عمران کے گھر کو سارے جہان سے');
INSERT INTO "al_hind" VALUES(3,34,'3:34','جو اولاد تھے ایک دوسرے کی اور اللہ سننے والا جاننے والا ہے ');
INSERT INTO "al_hind" VALUES(3,35,'3:35','جب کہا عمران کی عورت نے کہ اے رب میں نے نذر کیا تیرے جو کچھ میرے پیٹ میں ہے سب سے آزاد رکھ کر سو تو مجھ سے قبول کر بیشک تو ہی ہے اصل سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(3,36,'3:36','پھر جب اس کو جنا بولی اے رب میں نے تو اس کو لڑکی جنی اور اللہ کو خوب معلوم ہے جو کچھ اس نے جنا اور بیٹا نہ ہو جیسی وہ بیٹی اور میں نے اس کا نام رکھا مریم اور میں تیری پناہ میں دیتی ہوں اس کو اور اس کی اولاد کو شیطان مردود سے ');
INSERT INTO "al_hind" VALUES(3,37,'3:37','پھر قبول کیا اس کو اس کے رب نے اچھی طرح کا قبول اور بڑھایا ا سکو اچھی طرح بڑھانا اور سپرد کی زکریا کو جس وقت آتے اس کے پاس زکریا حجرے میں پاتے اس کے پاس کچھ کھانا کہا اے مریم کہاں سے آیا تیرے پاس یہ کہنے لگی یہ اللہ کے پاس سے آتا ہے اللہ رزق دیتا ہے جس کو چاہے بے قیاس ');
INSERT INTO "al_hind" VALUES(3,38,'3:38','وہیں دعا کی زکریا نے اپنے رب سے کہا اے رب میرے عطا کر مجھ کو اپنے پاس سے اولاد پاکیزہ بیشک تو سننے والا ہے دعا کا ');
INSERT INTO "al_hind" VALUES(3,39,'3:39','پھر اس کو آواز دی فرشتوں نے جب وہ کھڑے تھے نماز میں حجرے کے اندر کہ اللہ تجھکو خوشخبری دیتا ہے یحیٰی کی جو گواہی دے گا اللہ کے ایک حکم کی اور سردار ہو گا اور عورت کے پاس نہ جائے گا اور نبی ہو گا صالحین سے ');
INSERT INTO "al_hind" VALUES(3,40,'3:40','کہا اے رب کہاں سے ہو گا میرے لڑکا اور پہنچ چکا مجھ کو بڑھاپا اور عورت میری بانجھ ہے فرمایا اسی طرح اللہ کرتا ہے جو چاہے ');
INSERT INTO "al_hind" VALUES(3,41,'3:41','کہا اے رب مقرر کر میرے لئے کچھ نشانی فرمایا نشانی تیرے لئے یہ ہے کہ نہ بات کرے گا تو لوگوں سے تین دن مگراشارہ سے اور یاد کر اپنے رب کو بہت اور تسبیح کر شام اور صبح ');
INSERT INTO "al_hind" VALUES(3,42,'3:42','اور جب فرشتے بولے اے مریم اللہ نے تجھ کو پسند کیا اور ستھرا بنایا اور پسند کیا تجھ کو سب جہان کی عورتوں پر ');
INSERT INTO "al_hind" VALUES(3,43,'3:43','اے مریم بندگی کر اپنے رب کی اور سجدہ کر اور رکوع کر ساتھ رکوع کرنےوالوں کے ');
INSERT INTO "al_hind" VALUES(3,44,'3:44','یہ خبریں غیب کی ہیں جو ہم بھیجتے ہیں تجھکو اور تو نہ تھا ان کے پاس جب ڈالنے لگے اپنے قلم کہ کون پرورش میں لے مریم کو اور تو نہ تھا ان کے پاس جب وہ جھگڑتے تھے ');
INSERT INTO "al_hind" VALUES(3,45,'3:45','جب کہا فرشتوں نے اے مریم اللہ تجھ کو بشارت دیتا ہے ایک اپنے حکم کی جس کا نام مسیح ہے عیسیٰ مریم کا بیٹا مرتبہ والا دنیا میں اور آخرت میں اور اللہ کے مقربوں میں ');
INSERT INTO "al_hind" VALUES(3,46,'3:46','اور باتیں کرے گا لوگوں سے جبکہ ماں کی گود میں ہو گا اور جبکہ پوری عمر کا ہو گا اور نیک بختوں میں ہے ');
INSERT INTO "al_hind" VALUES(3,47,'3:47','بولی اے رب کہاں سے ہو گا میرے لڑکا اور مجھ کو ہاتھ نہیں لگایا کسی آدمی نے فرمایا اسی طرح اللہ پیدا کرتا ہے جو چاہے جب ارادہ کرتا ہے کسی کام کا تو یہی کہتا ہے اس کو کہ ہو جا سو وہ ہو جاتا ہے ');
INSERT INTO "al_hind" VALUES(3,48,'3:48','اور سکھاوےگا اس کو کتاب اور تہ کی باتیں اور تورات اور انجیل ');
INSERT INTO "al_hind" VALUES(3,49,'3:49','اور کرے گا اس کو پیغمبر بنی اسرائیل کی طرف بیشک میں آیا ہوں تمہارےپاس نشانیاں لے کر تمہارے رب کی طرف سے کہ میں بنا دیتا ہوں تم کو گارے سے پرندہ کی شکل پھر اس میں پھونک مارتا ہوں تو ہو جاتا ہے وہ اڑتا جانور اللہ کے حکم سے اور اچھا کرتا ہوں مادر زاد اندھے کو اور کوڑھی کو اور جلاتا ہوں مردے اللہ کے حکم سے اور بتا دیتا ہوں تم کو جو کھا کر آؤ اور جو رکھ آؤ اپنے گھر میں اس میں نشانی پوری ہے تم کو اگر تم یقین رکھتے ہو');
INSERT INTO "al_hind" VALUES(3,50,'3:50','اور سچا بتاتا ہوں اپنےسے پہلی کتاب کو جو توریت ہے اور اس واسطے کہ حلال کر دوں تم کو بعضی وہ چیزیں جو حرام تھیں تم پر اور آیا ہوں تمہارے پاس نشانی لے کر تمہارے رب کی سو ڈرو اللہ سے اور میرا کہا مانو ');
INSERT INTO "al_hind" VALUES(3,51,'3:51','بیشک اللہ ہے رب میرا اور رب تمہارا سو اس کی بندگی کرو یہی راہ سیدھی ہے ');
INSERT INTO "al_hind" VALUES(3,52,'3:52','پھر جب معلوم کیا عیسٰی نے بنی اسرائیل کا کفر بولا کون ہے کہ میری مدد کرے اللہ کی راہ میں کہا حواریوں نے ہم ہیں مدد کرنے والے اللہ کی ہم یقین لائے اللہ پر اور تو گواہ رہ کہ ہم نے حکم قبول کیا ');
INSERT INTO "al_hind" VALUES(3,53,'3:53','اے رب ہم نے یقین کیا اس چیز کا جو تو نے اتاری اور ہم تابع ہوئے رسول کے سو تو لکھ لے ہم کو ماننے والوں میں ');
INSERT INTO "al_hind" VALUES(3,54,'3:54','اور مکر کیا ان کافروں نے اور مکر کیا اللہ نے اور اللہ کا داؤ سب سے بہتر ہے ');
INSERT INTO "al_hind" VALUES(3,55,'3:55','جس وقت کہا اللہ نے اے عیسٰی میں لے لوں گا تجھ کو اور اٹھا لوں گا اپنی طرف اور پاک کروں گا تجھ کو کافروں سے اور رکھوں گا ان کو جو تیرے تابع ہیں غالب ان سے جو انکار کرتے ہیں قیامت کے دن تک پھر میری طرف ہے تم سب کو پھر آنا پھر فیصلہ کر دوں گا تم میں جس بات میں تم جھگڑتے تھے');
INSERT INTO "al_hind" VALUES(3,56,'3:56','سو وہ لوگ جو کافر ہوئے ان کو عذاب کروں گا سخت عذاب دنیا میں اور آخرت میں اور کوئی نہیں ان کا مددگار');
INSERT INTO "al_hind" VALUES(3,57,'3:57','اور وہ لوگ جو ایمان لائے اور کام نیک کئے سو انکو پورا دے گا ان کا حق اور اللہ کو خوش نہیں آتے بے انصاف ');
INSERT INTO "al_hind" VALUES(3,58,'3:58','یہ پڑھ کو سناتے ہیں ہم تجھ کو آیتیں اور بیان تحقیقی');
INSERT INTO "al_hind" VALUES(3,59,'3:59','بیشک عیسٰی کی مثال اللہ کے نزدیک جیسے مثال آدم کی بنایا اس کو مٹی سے پھر کہا اس کو کہ ہو جا وہ ہو گیا ');
INSERT INTO "al_hind" VALUES(3,60,'3:60','حق وہ ہے جو تیرا رب کہے پھر تو مت رہ شک لانے والوں سے ');
INSERT INTO "al_hind" VALUES(3,61,'3:61','پھر جو کوئی جھگڑا کرے تجھ سے اس قصہ میں بعد اس کے کہ آ چکی تیرے پاس خبر سچی تو تو کہدے آؤ بلادیں ہم اپنے بیٹے اور تمہارے بیٹے اور اپنی عورتیں اور تمہاری عورتیں اور اپنی جان اور تمہاری جان پھر التجا کریں ہم سب اور لعنت کریں اللہ کی ان پر کہ جو جھوٹے ہیں ');
INSERT INTO "al_hind" VALUES(3,62,'3:62','بیشک یہی ہے بیان سچا اور کسی کی بندگی نہیں ہے سوا اللہ کے اور اللہ جو ہے وہی ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(3,63,'3:63','پھر اگر قبول نہ کریں تو اللہ کو معلوم ہے فساد کرنے والے ');
INSERT INTO "al_hind" VALUES(3,64,'3:64','تو کہہ اے اہل کتاب آؤ ایک بات کی طرف جو برابر ہے ہم میں اور تم میں کہ بندگی نہ کریں ہم مگر اللہ کی اور شریک نہ ٹھہراویں اس کا کسی کو اور نہ بناوے کوئی کسی کو رب سوا اللہ کے پھر اگر وہ قبول نہ کریں تو کہہ دو گواہ رہو کہ ہم تو حکم کے تابع ہیں ');
INSERT INTO "al_hind" VALUES(3,65,'3:65','اے اہل کتاب کیوں جھگڑتے ہو ابراہیم کی بابت اور توریت اور انجیل تو اتریں اس کے بعد کیا تم کو عقل نہیں');
INSERT INTO "al_hind" VALUES(3,66,'3:66','سنتے ہو تم لوگ جھگڑ چکے جس بات میں تم کو کچھ خبر تھی اب کیوں جھگڑتے ہو جس بات میں تم کو کچھ خبر نہیں اور اللہ جانتا ہے اور تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(3,67,'3:67','نہ تھا ابراہیم یہودی اور نہ تھا نصرانی لیکن تھا حنیف یعنی سب جھوٹے مذہبوں سے بیزار اور حکم بردار اور نہ تھا مشرک ');
INSERT INTO "al_hind" VALUES(3,68,'3:68','لوگوں میں زیادہ مناسبت ابراہیم سے ان کو تھی جو ساتھ اس کے تھے اور اس نبی کو اور جو ایمان لائے اس نبی پر اور اللہ والی ہے مسلمانوں کا ');
INSERT INTO "al_hind" VALUES(3,69,'3:69','آرزو ہے بعضے اہل کتاب کو کہ کسی طرح گمراہ کریں تم کو اور گمراہ نہیں کرتے مگر اپنے آپ کو اور نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(3,70,'3:70','اے اہل کتاب کیوں انکار کرتے ہو اللہ کے کلام کا اور تم قائل ہو ');
INSERT INTO "al_hind" VALUES(3,71,'3:71','اے اہل کتاب کیوں ملاتے ہو سچ میں جھوٹ اور چھپاتے ہو سچی بات جان کر ');
INSERT INTO "al_hind" VALUES(3,72,'3:72','اور کہا بعضے اہل کتاب نے مان لو جو کچھ اترا مسلمانوں پر دن چڑھے اور منکر ہو جاؤ آخر دن میں شاید وہ پھر جاویں ');
INSERT INTO "al_hind" VALUES(3,73,'3:73','اور نہ مانیو مگر اسی کی جو چلے تمہارےدین پر کہدے کہ بیشک ہدایت وہی ہے جو اللہ ہدایت کرے اور یہ سب کچھ اس لئے ہے کہ اور کسی کو بھی کیوں مل گیا جسیا کچھ تم کو ملا تھا یا وہ غالب کیوں آ گئے تم پر تمہارے رب کے آگے تو کہہ بڑائی اللہ کے ہاتھ میں ہے دیتا ہے جس کو چاہے اور اللہ بہت گنجائش والا ہے خبردار');
INSERT INTO "al_hind" VALUES(3,74,'3:74','خاص کرتا ہے اپنی مہربانی جس پر چاہے اور اللہ کا فضل بڑا ہے ');
INSERT INTO "al_hind" VALUES(3,75,'3:75','اور بعضے اہل کتاب میں وہ ہیں کہ اگر تو ان کے پاس امانت رکھے ڈھیر مال کا تو ادا کر دیں تجھ کو اور بعضے ان میں وہ ہیں کہ اگر تو انکے پاس امانت رکھے ایک اشرفی تو ادا نہ کریں تجھ کو مگر جب تک کہ تو رہے اس کے سر پر کھڑا یہ اس واسطے کہ انہوں کے کہہ رکھا ہے کہ نہیں ہے ہم پر امی لوگوں کے حق لینے میں کچھ گناہ اور جھوٹ بولتے ہیں اللہ پر اور وہ جانتے ہیں ');
INSERT INTO "al_hind" VALUES(3,76,'3:76','کیوں نہیں جو کوئی پورا کرے اپنا قرار اور پرہیز گار ہے تو اللہ کو محبت ہے پرہیزگاروں سے ');
INSERT INTO "al_hind" VALUES(3,77,'3:77','جو لوگ مول لیتے ہیں اللہ کے قرار پر اور اپنی قسموں پر تھوڑا سا مول ان کا کچھ حصہ نہیں آخرت میں اور نہ بات کرے گا ان سے اللہ اور نہ نگاہ کرے گا انکی طرف قیامت کے دن اور نہ پاک کرے گا ان کو اور انکے واسطے عذاب ہے دردناک ');
INSERT INTO "al_hind" VALUES(3,78,'3:78','اور ان میں ایک فریق ہے کہ زبان مڑوڑ کر پڑھتے ہیں کتاب تاکہ تم جانو کہ وہ کتاب میں ہے اور وہ نہیں کتاب میں اور کہتے ہیں وہ اللہ کا کہا ہے اور وہ نہیں اللہ کا کہا اور اللہ پر جھوٹ بولتے ہیں جان کر');
INSERT INTO "al_hind" VALUES(3,79,'3:79','کسی بشر کا کام نہیں کہ اللہ اس کو دیوے کتاب اور حکمت اور پیغمبر کرے پھر وہ کہے لوگوں کو کہ تم میرے بندے ہو جاؤ اللہ کو چھوڑ کر لیکن یوں کہے کہ تم اللہ والے ہو جاؤ جیسے کہ تم سکھلاتے تھے کتاب اور جیسے کہ تم آپ بھی پڑھتے تھے اسے ');
INSERT INTO "al_hind" VALUES(3,80,'3:80','اور نہ یہ کہے تم کو کہ ٹھہرا لو فرشتوں کو اور نبیوں کو رب کیا تم کو کفر سکھائے گا بعد اس کے کہ تم مسلمان ہو چکے ہو ');
INSERT INTO "al_hind" VALUES(3,81,'3:81','اور جب لیا اللہ نے عہد نبیوں سے کہ جو کچھ میں نے تم کو دیا کتب اور علم پھر آوے تمہارے پاس کوئی رسول کہ سچا بتاوے تمہارے پاس والی کتاب کو تو اس رسول پر ایمان لاؤ گے اور اس کی مدد کرو گے فرمایا کہ کیا تم نے اقرار کیا اور اس شرط پر میرا عہد قبول کیا بولے ہم نے اقرار کیا فرمایا تو اب گواہ رہو اور میں بھی تمہارے ساتھ گواہ ہوں ');
INSERT INTO "al_hind" VALUES(3,82,'3:82','پھر جو کوئی پھر جاوے اس کے بعد تو وہی لوگ ہیں نافرمان ');
INSERT INTO "al_hind" VALUES(3,83,'3:83','اب کوئی اور دین ڈھونڈھتے ہیں سوا دین اللہ کے اور اسی کے حکم میں ہے جو کوئی آسمان اور زمین میں ہے خوشی سے یا لاچاری سے اور اسی کی طرف سب پھر جاویں گے ');
INSERT INTO "al_hind" VALUES(3,84,'3:84','تو کہہ ہم ایمان لائے اللہ پر اور جو کچھ اترا ہم پر اور جو کچھ اترا ابراہیم پر اور اسمٰعیل پر اور اسحٰق پر اور یعقوب پر اور اس کی اولاد پر اور جو ملا موسٰی کو اور عیسٰی کو اور جو ملا سب نبیوں کو انکے پروردگار کی طرف سے ہم جدا نہیں کرتے ان میں کسی کو اور ہم اسی کے فرمانبردار ہیں ');
INSERT INTO "al_hind" VALUES(3,85,'3:85','اور جو کوئی چاہے سوا دین اسلام کے اور کوئی دین سو اس سے ہر گز قبول نہ ہو گا اور وہ آخرت میں خراب ہے ');
INSERT INTO "al_hind" VALUES(3,86,'3:86','کیونکر راہ دے گا للہ ایسے لوگوں کو کہ کافر ہو گئے ایمان لا کر اور گواہی دے کر کہ بیشک رسول سچا ہے اور آئیں ان کے پاس نشانیاں روشن اور اللہ راہ نہیں دیتا ظالم لوگوں کو ');
INSERT INTO "al_hind" VALUES(3,87,'3:87','ایسے لوگوں کی سزا یہ ہے کہ ان پر لعنت ہے اللہ کی اور فرشتوں کی اور لوگوں کی سب کی ');
INSERT INTO "al_hind" VALUES(3,88,'3:88','ہمیشہ رہیں گے اس میں نہ ہلکا ہو گا ان سے عذاب اور نہ ان کو فرصت ملے ');
INSERT INTO "al_hind" VALUES(3,89,'3:89','مگر جنہوں نے توبہ کی اس کےبعد اور نیک کام کئے تو بیشک اللہ غفور رحیم ہے ');
INSERT INTO "al_hind" VALUES(3,90,'3:90','جو لوگ منکر ہوئے مان کر پھر بڑھتے رہے انکار میں ہر گز قبول نہ ہوگی ان کی توبہ اور وہی ہیں گمراہ ');
INSERT INTO "al_hind" VALUES(3,91,'3:91','جو لوگ کافر ہوئے اور مر گئے کافر ہی تو ہر گز قبول نہ ہوگا کسی ایسے سے زمین بھر کو سونا اور اگرچہ بدلا دیوے اس قدر سونا ان کو عذاب دردناک ہے اور کوئی نہیں ان کا مددگار ');
INSERT INTO "al_hind" VALUES(3,92,'3:92','ہر گز حاصل نہ کر سکو گے نیکی میں کمال جب تک نہ خرچ کرو اپنی پیاری چیز سے کچھ اور جو چیز خرچ کرو گے سو اللہ کو معلوم ہے ');
INSERT INTO "al_hind" VALUES(3,93,'3:93','سب کھانے کی چیزیں حلال تھیں بنی اسرائیل کو مگر وہ جو حرام کر لی تھی اسرائیل نے اپنے اوپر توریت نازل ہونے سے پہلے تو کہہ لاؤ توریت اور پڑھو اگر سچے ہو ');
INSERT INTO "al_hind" VALUES(3,94,'3:94','پھر جو کوئی جوڑے اللہ پر جھوٹ اس کے بعد تو وہی ہیں بڑے بے انصاف ');
INSERT INTO "al_hind" VALUES(3,95,'3:95','تو کہہ سچ فرمایا اللہ نے اب تابع ہو جاؤ دین ابراہیم کے جو ایک ہی کا ہو رہا تھا اور نہ تھا شرک کرنے والا ');
INSERT INTO "al_hind" VALUES(3,96,'3:96','بیشک سب سے پہلا گھر جو مقرر ہوا لوگوں کے واسطے یہی ہے جو مکہ میں ہے برکت والا اور ہدایت جہان کے لوگوں کو');
INSERT INTO "al_hind" VALUES(3,97,'3:97','اس میں نشانیاں ہیں ظاہر جیسے مقام ابراہیم اور جو اس کے اندر آیا اس کو امن ملا اور اللہ کا حق ہے لوگوں پر حج کرنا اس گھر کا جو شخص قدرت رکھتا ہو اسکی طرف راہ چلنے کی اور جو نہ مانے تو پھر اللہ پروا نہیں رکھتا جہان کے لوگوں کی ');
INSERT INTO "al_hind" VALUES(3,98,'3:98','تو کہہ اے اہل کتاب کیوں منکر ہوتے ہو اللہ کے کلام سے اور اللہ کے روبرو ہے جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(3,99,'3:99','تو کہہ اے اہل کتاب کیوں روکتے ہو اللہ کی راہ سے ایمان لانے والوں کو کہ ڈھونڈتے ہو اس میں عیب اور تم خود جانتے ہو اور اللہ بے خبر نہیں تمہارے کام سے ');
INSERT INTO "al_hind" VALUES(3,100,'3:100','اے ایمان والو اگر تم کہا مانو گے بعضے اہل کتاب کا تو پھر کر دیں گے وہ تم کو ایمان لائے پیچھے کافر ');
INSERT INTO "al_hind" VALUES(3,101,'3:101','اور تم کس طرح کافر ہوتے ہو اور تم پر پڑھی جاتی ہیں آیتیں اللہ کی اور تم میں اس کا رسول ہے اور جو کوئی مضبوط پکڑے اللہ کو تو اس کو ہدایت ہوئی سیدھے رستہ کی ');
INSERT INTO "al_hind" VALUES(3,102,'3:102','اے ایمان والو ڈرتے رہو اللہ سے جیسا چاہیئے اس سے ڈرنا اور نہ مریو مگر مسلمان ');
INSERT INTO "al_hind" VALUES(3,103,'3:103','اور مضبوط پکڑو رسی اللہ کی سب مل کر اور پھوٹ نہ ڈالو اور یاد کرو احسان اللہ کا اپنے اوپر جب کہ تھے تم آپس میں دشمن پھر الفت دی تمہارے دلوں میں اب ہو گئے اس کے فضل سے بھائی اور تم تھے کنارے پر ایک آگ کے گڑھے کے پھر تم کو اس سے نجات دی اسی طرح کھولتا ہے اللہ تم پر آیتیں تاکہ تم راہ پاؤ ');
INSERT INTO "al_hind" VALUES(3,104,'3:104','اور چاہیئے کہ رہے تم میں ایک جماعت ایسی جو بلاتی رہے نیک کام کی طرف اور حکم کرتی رہے اچھے کاموں کا اور منع کریں برائی سے اور وہی پہنچے اپنی مراد کو ');
INSERT INTO "al_hind" VALUES(3,105,'3:105','اور مت ہو ان کی طرح جو متفرق ہو گئے اور اختلاف کرنے لگے بعد اس کے کہ پہنچ چکے ان کو حکم صاف اور ان کو بڑا عذاب ہے ');
INSERT INTO "al_hind" VALUES(3,106,'3:106','جس دن کہ سفید ہوں گے بعضے منہ اور سیاہ ہوں گے بعضے منہ سو وہ لوگ کہ سیاہ ہوئے منہ ان کے ان سے کہا جائے گا کیا تم کافر ہو گئے ایمان لا کر اب چکھو عذاب بدلا اس کفر کرنے کا');
INSERT INTO "al_hind" VALUES(3,107,'3:107','اور وہ لوگ کہ سفید ہوئے منہ ان کے سو رحمت میں ہیں اللہ کی وہ اس میں ہمیشہ رہیں گے ');
INSERT INTO "al_hind" VALUES(3,108,'3:108','یہ حکم ہیں اللہ کے ہم سناتے ہیں تجھ کو ٹھیک ٹھیک اور اللہ ظلم کرنا نہیں چاہتا خلقت پر ');
INSERT INTO "al_hind" VALUES(3,109,'3:109','اور اللہ ہی کا ہے جو کچھ کہ ہے آسمانوں میں اور جو کچھ کہ ہے زمین میں اور اللہ کی طرف رجوع ہے ہر کام کا ');
INSERT INTO "al_hind" VALUES(3,110,'3:110','تم ہو بہتر سب امتوں سے جو بھیجی گئ عالم میں حکم کرتے ہو اچھے کاموں کا اور منع کرتے ہو برے کاموں سے اور ایمان لاتے ہو اللہ پر اور اگر ایمان لاتے اہل کتاب تو ان کے لئے بہتر تھا کچھ تو ان میں سے ہیں ایمان پر اور اکثر ان میں نافرمان ہیں ');
INSERT INTO "al_hind" VALUES(3,111,'3:111','وہ کچھ نہ بگاڑ سکیں گے تمہارا مگر ستانا زبان سے اور اگر تم سے لڑیں گے تو پیٹھ دیں گے پھر ان کی مدد نہ ہو گی ');
INSERT INTO "al_hind" VALUES(3,112,'3:112','ماری گئ ان پر ذلت جہاں دیکھے جائیں سوائے دست آویز اللہ کے اور دست آویز لوگوں کے اور کمایا انہوں نے غصہ اللہ کا اور لازم کر دی گئ انکے اوپر حاجت مندی یہ اس واسطے کہ وہ انکار کرتے رہے ہیں اللہ کی آیتوں سے اور قتل کرتے رہے ہیں پیغمبروں کو ناحق یہ اس واسطے کہ نافرمانی کی انہوں نے اور حد سے نکل گئے ');
INSERT INTO "al_hind" VALUES(3,113,'3:113','وہ سب برابر نہیں اہل کتاب میں ایک فرقہ ہے سیدھی راہ پر پڑھتے ہیں آیتیں اللہ کی راتوں کے وقت اور وہ سجدے کرتے ہیں');
INSERT INTO "al_hind" VALUES(3,114,'3:114','ایمان لاتے ہیں اللہ پر اور قیامت کے دن پر اور حکم کرتے ہیں اچھی بات کا اور منع کرتے ہیں برے کاموں سے اور دوڑتے ہیں نیک کاموں پر اور وہی لوگ نیک بخت ہیں ');
INSERT INTO "al_hind" VALUES(3,115,'3:115','اور جو کچھ کریں گے وہ لوگ نیک کام اس کی ہرگز ناقدری نہ ہو گی اور اللہ کو خبر ہے پرہیزگاروں کی ');
INSERT INTO "al_hind" VALUES(3,116,'3:116','وہ لوگ جو کافر ہیں ہرگز کام نہ آویں گے ان کو ان کےمال اور نہ اولاد اللہ کے آگے کچھ اور وہی لوگ رہنے والے ہیں آگ میں دوزخ کی وہ اس آگ میں ہمیشہ رہیں گے');
INSERT INTO "al_hind" VALUES(3,117,'3:117','جو کچھ خرچ کرتے ہیں اس دنیا کی زندگی میں اس کی مثال جیسے ایک ہوا کہ اس میں ہو پالا جا لگی کھیتی کو اس قوم کی کہ انہوں نے اپنے حق میں برا کیا تھا پھر اس کو نابود کر گئ اور اللہ نے ان پر ظلم نہیں کیا لیکن وہ اپنے اوپر ظلم کرتے ہیں ');
INSERT INTO "al_hind" VALUES(3,118,'3:118','اے ایمان والو نہ بناؤ بھیدی کسی کو اپنوں کےسوا وہ کمی نہیں کرتےتمہاری خرابی میں انکی خوشی ہےتم جس قدر تکلیف میں رہو نکلی پڑتی ہےدشمنی انکی زبان سےاور جو کچھ مخفی ہے انکے جی میں وہ اس سے بہت زیادہ ہے ہم نےبتا دیےتم کو پتے اگر تم کو عقل ہے ');
INSERT INTO "al_hind" VALUES(3,119,'3:119','سن لو تم لوگ ان کے دوست ہو اور وہ تمہارےدوست نہیں اور تم سب کتابوں کو مانتے ہو اور جب تم سے ملتے ہیں کہتے ہیں ہم مسلمان ہیں اور جب اکیلے ہوتے ہیں تو کاٹ کاٹ کھاتے ہیں تم پر انگلیاں غصہ سے تو کہہ مرو تم اپنے غصہ میں اللہ کو خوب معلوم ہیں دلوں کی باتیں ');
INSERT INTO "al_hind" VALUES(3,120,'3:120','اگر تم کو ملے کچھ بھلائی تو بری لگی ہے ان کو اور اگر تم پر پہنچے کوئی برائی تو خوش ہو ں اس سے اور اگر تم صبر کرو اور بچتے رہو تو کچھ نہ بگڑے گا تمہارا ان کے فریب سے بیشک جو کچھ وہ کرتے ہیں سب اللہ کے بس میں ہے ');
INSERT INTO "al_hind" VALUES(3,121,'3:121','اور جب صبح کو نکلا تو اپنے گھر سے بٹھلانے لگا مسلمانوں کو لڑائی کے ٹھکانوں پر اور اللہ سب کچھ سنتا جانتا ہے');
INSERT INTO "al_hind" VALUES(3,122,'3:122','جب قصد کیا دو فرقوں نے تم میں سے کہ نامردی کریں اور اللہ مددگار تھا ان کا اور اللہ ہی پر چاہیئے بھروسہ کریں مسلمان');
INSERT INTO "al_hind" VALUES(3,123,'3:123','اور تمہاری مدد کر چکا ہے اللہ بدر کی لڑائی میں اور تم کمزور تھے سو ڈرتے رہو اللہ سے تاکہ تم احسان مانو ');
INSERT INTO "al_hind" VALUES(3,124,'3:124','جب تو کہنے لگا مسلمانوں کو کیا تم کو کافی نہیں کہ تمہاری مدد کو بھیجے رب تمہارا تین ہزار فرشتے آسمان سے اترنے والے ');
INSERT INTO "al_hind" VALUES(3,125,'3:125','البتہ اگر تم صبر کرو اور بچتے رہو اور وہ آئیں تم پر اسی دم تو مدد بھیجے تمہارا رب پانچ ہزار فرشتے نشان دار گھوڑوں پر ');
INSERT INTO "al_hind" VALUES(3,126,'3:126','اور یہ تو اللہ نے تمہارےدل کی خوشی کی اور تاکہ تسکین ہو تمہارے دلوں کو اس سے اور مدد ہے صرف اللہ ہی کی طرف سے جو کہ زبردست ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(3,127,'3:127','تاکہ ہلاک کرے بعضے کافروں کو یا انکو ذلیل کرے تو پھر جاویں محروم ہو کر ');
INSERT INTO "al_hind" VALUES(3,128,'3:128','تیرا اختیار کچھ نہیں یا انکو توبہ دیوے خدائے تعالیٰ یا انکو عذاب کرے کہ وہ ناحق پر ہیں ');
INSERT INTO "al_hind" VALUES(3,129,'3:129','اور اللہ ہی کا مال ہے جو کچھ آسمانوں میں ہے اور جو کچھ کہ زمین میں ہے بخش دے جس کو چاہے اور عذاب کرے جس کو چاہے اور اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(3,130,'3:130','اے ایمان والو مت کھاؤ سود دونے پر دونا اور ڈرو اللہ سے تاکہ تمہارا بھلا ہو ');
INSERT INTO "al_hind" VALUES(3,131,'3:131','اور بچو اس آگ سے جو تیار ہوئی کافروں کے واسطے ');
INSERT INTO "al_hind" VALUES(3,132,'3:132','اور حکم مانو اللہ کا اور رسول کا تاکہ تم پر رحم ہو ');
INSERT INTO "al_hind" VALUES(3,133,'3:133','اور دوڑو بخشش کی طرف اپنے رب کی اور جنت کی طرف جس کا عرض ہے آسمان اور زمین تیار ہوئی ہے واسطے پرہیزگاروں کے');
INSERT INTO "al_hind" VALUES(3,134,'3:134','جو خرچ کئے جاتے ہیں خوشی میں اور تکلیف میں اور دبا لیتےہیں غصہ اور معاف کرتے ہیں لوگوں کو اور اللہ چاہتا ہے نیکی کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(3,135,'3:135','اور وہ لوگ کہ جب کر بیٹھیں کچھ کھلا گناہ یا برا کام کریں اپنے حق میں تو یاد کریں اللہ کو اور بخشش مانگیں اپنے گناہوں کی اور کون ہے گناہ بخشنے والا سوا اللہ کے اور اڑتے نہیں اپنے کئے پر اور وہ جانتے ہیں');
INSERT INTO "al_hind" VALUES(3,136,'3:136','انہی کی جزاء ہے بخشش ان کے رب کی اور باغ جن کے نیچے نہریں بہتی ہیں ہمیشہ رہیں گے وہ لوگ ان باغوں میں اور کیا خوب مزدوری ہے کام کرنے والوں کی ');
INSERT INTO "al_hind" VALUES(3,137,'3:137','ہو چکے ہیں تم سے پہلے واقعات سو پھرو زمین میں اور دیکھو کہ کیا ہوا انجام جھٹلانے والوں کا ');
INSERT INTO "al_hind" VALUES(3,138,'3:138','یہ بیان ہے لوگوں کے واسطے اور ہدایت اور نصیحت ہے ڈرنے والوں کو ');
INSERT INTO "al_hind" VALUES(3,139,'3:139','اور سست نہ رہو اور نہ غم کھاؤ اور تم ہی غالب رہو گے اگر تم ایمان رکھتے ہو ');
INSERT INTO "al_hind" VALUES(3,140,'3:140','اگر پہنچا تم کو زخم تو پہنچ چکا ہے ان کو بھی زخم ایسا ہی اور یہ دن باری باری بدلتے رہتے ہیں ہم ان کو لوگوں میں اور اس لئے کہ معلوم کرے اللہ جن کو ایمان ہے اور کرے تم میں سےشہید اور اللہ کو محبت نہیں ظلم کرنے والوں سے ');
INSERT INTO "al_hind" VALUES(3,141,'3:141','اور اس واسطے کہ پاک صاف کرے اللہ ایمان والوں کو اور مٹا دیوے کافروں کو ');
INSERT INTO "al_hind" VALUES(3,142,'3:142','کیا تم کو خیال ہے کہ داخل ہو جاؤ گے جنت میں اور ابھی تک معلوم نہیں کیا اللہ نے جو لڑنے والے ہیں تم میں اور معلوم نہیں کیا ثابت رہنے والوں کو ');
INSERT INTO "al_hind" VALUES(3,143,'3:143','اور تم تو آرزو کرتے تھے مرنے کی اس کی ملاقات سے پہلے سو اب دیکھ لیا تم نے اس کو آنکھوں کے سامنے ');
INSERT INTO "al_hind" VALUES(3,144,'3:144','اور محمد تو ایک رسول ہے ہو چکے اس سے پہلے بہت رسول پھر کیا اگر وہ مر گیا یا مارا گیا تو تم پھر جاؤ گے الٹے پاؤں اور جو کوئی پھر جائے گا الٹے پاؤں تو ہر گز نہ بگاڑے گا اللہ کا کچھ اور اللہ ثواب دے گا شکر گذاروں کو ');
INSERT INTO "al_hind" VALUES(3,145,'3:145','اور کوئی مر نہیں سکتا بغیر حکم اللہ کے لکھا ہوا ہے ایک وقت مقرر اور جو کوئی چاہے گا بدلہ دنیا کا دیویں گے ہم اس کو دنیا ہی سے اور جو کوئی چاہے گا بدلہ آخرت کا اس میں سےدیویں گے ہم اسکو اور ہم ثواب دیں گے احسان ماننے والوں کو ');
INSERT INTO "al_hind" VALUES(3,146,'3:146','اور بہت نبی ہیں جن کے ساتھ ہو کر لڑے ہیں بہت خدا کے طالب پھر نہ ہارے ہیں کچھ تکلیف پہنچنے سے اللہ کی راہ میں اور نہ سست ہوئے ہیں اور نہ دب گئے ہیں اور اللہ محبت کرتا ہے ثابت قدم رہنے والوں سے ');
INSERT INTO "al_hind" VALUES(3,147,'3:147','اور کچھ نہیں بولے مگر یہی کہا کہ اے رب ہمارےبخش ہمارے گناہ اور جو ہم سےزیادتی ہوئی ہمارےکام میں اور ثابت رکھ قدم ہمارے اور مدد دے ہم کو قوم کفار پر ');
INSERT INTO "al_hind" VALUES(3,148,'3:148','پھر دیا اللہ نے ان کو ثواب دنیا کا اور خوب ثواب آخرت کا اور اللہ محبت رکھتا ہے نیک کام کرنے والوں سے ');
INSERT INTO "al_hind" VALUES(3,149,'3:149','اے ایمان والو اگر تم کہا مانو گے کافروں کا تو وہ تم کو پھیر دیں گے الٹے پاؤں پھر جا پڑو گے تم نقصان میں ');
INSERT INTO "al_hind" VALUES(3,150,'3:150','بلکہ اللہ تمہارا مددگار ہے اور اس کی مدد سب سے بہتر ہے ');
INSERT INTO "al_hind" VALUES(3,151,'3:151','اب ڈالیں گے ہم کافروں کے دل میں ہیبت اس واسطے کہ انہوں نے شریک ٹھہرایا اللہ کا جس کی اس نے کوئی سند نہیں اتاری اور ان کا ٹھکانہ دوزخ ہے اور وہ برا ٹھکانا ہے ظالموں کا ');
INSERT INTO "al_hind" VALUES(3,152,'3:152','اور اللہ تو سچا کر چکا تم سے اپنا وعدہ جب تم قتل کرنے لگے انکو اسکے حکم سے یہاں تک کہ جب تم نے نامردی کی اور کام میں جھگڑا ڈالا اور نافرمانی کی بعد اس کے کہ تم کو دکھا چکا تمہاری خوشی کی چیز کوئی تم میں سے چاہتا تھا دنیا اور کوئی تم میں چاہتا تھا آخرت پھر تم کو الٹ دیا ان پر سے تاکہ تم کو آزماوے اور وہ تو تم کو معاف کر چکا اور اللہ کا فضل ہے ایمان والوں پر ');
INSERT INTO "al_hind" VALUES(3,153,'3:153','جب تم چڑھے چلے جاتے تھے اور پیچھے پھر کر نہ دیکھتے تھے کسی کو اور رسول پکارتا تھا تم کو تمہارے پیچھے سے پھرپہنچا تم کو غم عوض میں غم کے تاکہ تم غم نہ کیا کرو اس پر جو ہاتھ سے نکل جاوے اور نہ اس پر کہ جو کچھ پیش آ جاوے اور اللہ کو خبر ہے تمہارے کام کی ');
INSERT INTO "al_hind" VALUES(3,154,'3:154','پھر تم پر اتارا تنگی کے بعد امن کو جو اونگھ تھی کہ ڈھانک لیا اس اونگھ نے بعضوں کو تم میں سے اور بعضوں کو فکر پڑ رہا تھا اپنی جان کا خیال کرتےتھے اللہ پر جھوٹے خیال جاہلوں جیسے کہتے تھے کچھ بھی کام ہے ہمارے ہاتھ میں تو کہہ سب کام ہے اللہ کے ہاتھ وہ اپنے جی میں چھپاتے ہیں جو تجھ سے ظاہر نہیں کرتے کہتے ہیں اگر کچھ کام ہوتا ہمارے ہاتھ تو ہم مارےنہ جاتے اس جگہ تو کہہ اگر تم ہوتے اپنے گھروں میں البتہ باہر نکلتےجن پر لکھ دیا تھا مارا جانا اپنے پڑاؤ پر اور اللہ کو آزمانا تھا جو کچھ تمہارےجی میں ہے اور صاف کرنا تھا اس کا جو تمہارے دل میں ہے اور اللہ جانتا ہے دلوں کے بھید ');
INSERT INTO "al_hind" VALUES(3,155,'3:155','جو لوگ تم میں سے ہٹ گئے جس دن لڑیں دو فوجیں سو انکو بہکا دیا شیطان نے ان کے گناہ کی شامت سے اور ان کو بخش چکا اللہ اللہ بخشنے والا ہے تحمل کرنے والا ');
INSERT INTO "al_hind" VALUES(3,156,'3:156','اے ایمان والو تم نہ ہو ان کی طرح جو کافر ہوئے اور کہتےہیں اپنے بھائیوں کو جب وہ سفر کو نکلیں ملک میں یا ہوں جہاد میں اگر رہتے ہمارے پاس تو نہ مرتے اور نہ مارے جاتے تاکہ اللہ ڈالے اس گمان سے افسوس ان کے دلوں میں اور اللہ ہی جلاتا ہے اور مارتا ہے اور اللہ تمہارے سب کام دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(3,157,'3:157','اور اگر تم مارے گئے اللہ کی راہ میں یا مر گئے تو بخشش اللہ کی اور مہربانی اسکی بہتر ہے اس چیز سے جو وہ جمع کرتے ہیں');
INSERT INTO "al_hind" VALUES(3,158,'3:158','اور اگر تم مر گئے یا مارے گئے تو البتہ اللہ ہی کے آگے اکٹھے ہو گے تم سب ');
INSERT INTO "al_hind" VALUES(3,159,'3:159','سو کچھ اللہ ہی کی رحمت ہے جو تو نرم دل مل گیا ان کو اور اگر تو ہوتا تندخو سخت دل تو متفرق ہو جاتے تیرے پاس سے سو تو ان کو معاف کر اور ان کے واسطے بخشش مانگ اور ان سے مشورہ لے کام میں پھر جب قصد کر چکا تو اس کام کا تو پھر بھروسہ کر اللہ پر اللہ کو محبت ہے توکل والوں سے ');
INSERT INTO "al_hind" VALUES(3,160,'3:160','اگر اللہ تمہاری مدد کرے گا تو کوئی تم پر غالب نہ ہو سکے گا اور اگر مدد نہ کرے تمہاری تو پھر ایسا کون ہے جو مدد کر سکے تمہاری اس کے بعد اور اللہ ہی پر بھروسا چاہئے مسلمانوں کو ');
INSERT INTO "al_hind" VALUES(3,161,'3:161','اور نبی کا کام نہیں کہ کچھ چھپا رکھے اور جو کوئی چھپاوے گا وہ لائے گا اپنی چھپائی چیز دن قیامت کے پھر پورا پاوے گا ہر کوئی جو اس نے کمایا اور ان پر ظلم نہ ہو گا ');
INSERT INTO "al_hind" VALUES(3,162,'3:162','کیا ایک شخص جو تابع ہے اللہ کی مرضی کا برابر ہو سکتا ہے اسکے جس نے کمایا غصہ اللہ کا اور اس کا ٹھکانا دوزخ ہے اور کیا ہی بری جگہ پہنچا ');
INSERT INTO "al_hind" VALUES(3,163,'3:163','لوگوں کے مختلف درجے ہیں اللہ کے ہاں اور اللہ دیکھتا ہے جو کچھ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(3,164,'3:164','اللہ نے احسان کیا ایمان والوں پر جو بھیجا ان میں رسول انہی میں کا پڑھتا ہے ان پر آیتیں اس کی اور پاک کرتا ہے انکو یعنی شرک وغیرہ سے اور سکھلاتا ہے انکو کتاب اور کام کی بات اور وہ تو پہلے سے صریح گمراہی میں تھے ');
INSERT INTO "al_hind" VALUES(3,165,'3:165','کیا جس وقت پہنچی تم کو ایک تکلیف کہ تم پہنچا چکے ہو اس سے دوچند تو کہتے ہو یہ کہاں سے آئی تو کہہ دے یہ تکلیف تم کو پہنچی تمہاری ہی طرف سے بیشک اللہ ہر چیز پر قادر ہے');
INSERT INTO "al_hind" VALUES(3,166,'3:166','اور جو کچھ تم کو پیش آیا اس دن کہ ملیں دو فوجیں سو اللہ کے حکم سے اور اس واسطے کہ معلوم کرے ایمان والوں کو');
INSERT INTO "al_hind" VALUES(3,167,'3:167','اور تاکہ معلوم کرے ان کو جو منافق تھے اور کہا گیا ان کو کہ آؤ لڑو اللہ کی راہ میں یا دفع کرو دشمن کو بولے اگر ہم کو معلوم ہو لڑائی تو البتہ تمہارےساتھ رہیں وہ لوگ اس دن کفر کے قریب ہیں بہ نسبت ایمان کے کہتےہیں اپنے منہ سے جو نہیں ان کے دل میں اور اللہ خوب جانتا ہے جو کچھ چھپاتے ہیں ');
INSERT INTO "al_hind" VALUES(3,168,'3:168','وہ لوگ ہیں جو کہتے ہیں اپنے بھائیوں کو اور آپ بیٹھ رہے ہیں اگر وہ ہماری بات مانتے تو مارے نہ جاتے تو کہہ دے اب ہٹا دیجو اپنے اوپر سے موت کو اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(3,169,'3:169','اور تو نہ سمجھ ان لوگوں کو جو مارے گئے اللہ کی راہ میں مردے بلکہ وہ زندہ ہیں اپنے رب کے پاس کھاتے پیتے');
INSERT INTO "al_hind" VALUES(3,170,'3:170','خوشی کرتے ہیں اس پر جو دیا ان کو اللہ نے اپنے فضل سے اور خوش وقت ہوتے ہیں انکی طرف سے جو ابھی تک نہیں پہنچے ان کے پاس ان کے پیچھے سے اس واسطے کہ نہ ڈر ہے ان پر اور نہ انکو غم');
INSERT INTO "al_hind" VALUES(3,171,'3:171','خوش وقت ہوتے ہیں اللہ کی نعمت اور فضل سے اور اس بات سے کہ اللہ ضائع نہیں کرتا مزدوری ایمان والوں کی ');
INSERT INTO "al_hind" VALUES(3,172,'3:172','جن لوگوں نے حکم مانا اللہ کا اور رسول کا بعد اس کے کہ پہنچ چکے تھے ان کو زخم جو ان میں نیک ہیں اور پرہیزگار ان کو ثواب بڑا ہے');
INSERT INTO "al_hind" VALUES(3,173,'3:173','جن کو کہا لوگوں نے کہ مکہ والے آدمیوں نے جمع کیا ہے سامان تمہارے مقابلہ کو سو تم ان سے ڈرو تو اور زیادہ ہوا ان کا ایمان اور بولے کافی ہے ہم کو اللہ اور کیا خوب کارساز ہے ');
INSERT INTO "al_hind" VALUES(3,174,'3:174','پھر چلے آئے مسلمان اللہ کے احسان اور فضل کے ساتھ کچھ نہ پہنچی ان کو برائی اور تابع ہوئے اللہ کی مرضی کے اور اللہ کا فضل بڑا ہے ');
INSERT INTO "al_hind" VALUES(3,175,'3:175','یہ جو ہے سو شیطان ہے کہ ڈراتا ہے اپنے دوستوں سے سو تم ان سے مت ڈرو اور مجھ سے ڈرو اگر ایمان رکھتے ہو ');
INSERT INTO "al_hind" VALUES(3,176,'3:176','اور غم میں نہ ڈالیں تجھ کو وہ لوگ جو دوڑتے ہیں کفر کی طرف وہ نہ بگاڑیں گے اللہ کا کچھ اللہ چاہتا ہے کہ ان کو فائدہ نہ دے آخرت میں اور ان کے لئے عذاب ہے بڑا ');
INSERT INTO "al_hind" VALUES(3,177,'3:177','جنہوں نے مول لیا کفر کو ایمان کے بدلے وہ نہ بگاڑیں گے اللہ کا کچھ اور انکے لئے عذاب ہے دردناک ');
INSERT INTO "al_hind" VALUES(3,178,'3:178','اور یہ نہ سمجھیں کافر کہ ہم جو مہلت دیتے ہیں ان کو کچھ بھلا ہے ان کے حق میں ہم تو مہلت دیتے ہیں ان کو تاکہ ترقی کریں وہ گناہ میں اور ان کے لئے عذاب ہے خوار کرنے والا ');
INSERT INTO "al_hind" VALUES(3,179,'3:179','اللہ وہ نہیں کہ چھوڑ دےمسلمانوں کو اس حالت پر جس پر تم ہو جب تک کہ جدا نہ کر دے ناپاک کو پاک سے اور اللہ نہیں ہے کہ تم کو خبر دے غیب کی لیکن اللہ چھانٹ لیتا ہے اپنے رسولوں میں جس کو چاہے سو تم یقین لاؤ اللہ پر اور اسکے رسولوں پر اور اگر تم یقین پر رہو اور پرہیزگاری پر تو تم کو بڑا ثواب ہے ');
INSERT INTO "al_hind" VALUES(3,180,'3:180','اور نہ خیال کریں وہ لوگ جو بخل کرتے ہیں اس چیز پر جو اللہ نے انکو دی ہے اپنے فضل سے کہ یہ بخل بہتر ہے انکے حق میں بلکہ یہ بہت برا ہے انکے حق میں طوق بنا کر ڈالا جائے گا انکے گلوں میں وہ مال جس میں بخل کیا تھا قیامت کے دن اور اللہ وارث ہے آسمان اور زمین کا اور اللہ جو کرتے ہو سو جانتا ہے ');
INSERT INTO "al_hind" VALUES(3,181,'3:181','بیشک اللہ نے سنی ان کی بات جنہوں نے کہا کہ اللہ فقیر ہے اور ہم مالدار اب لکھ رکھیں گے ہم انکی بات اور جو خون کئے ہیں انہوں نے انبیاء کے ناحق اور کہیں گے چکھو عذاب جلتی آگ کا ');
INSERT INTO "al_hind" VALUES(3,182,'3:182','یہ بدلہ اس کا ہے جو تم نے اپنے ہاتھوں آگے بھیجا اور اللہ ظلم نہیں کرتا بندوں پر ');
INSERT INTO "al_hind" VALUES(3,183,'3:183','وہ لوگ جو کہتے ہیں کہ اللہ نے ہم کو کہہ رکھا ہے کہ یقین نہ کریں کسی رسول کا جب تک نہ لاوے ہمارے پاس قربانی کہ کھا جائے اس کو آگ تو کہہ تم میں آ چکے کتنے رسول مجھ سے پہلے نشانیاں لے کر اور یہ بھی جو تم نے کہا پھر ان کو کیوں قتل کیا تم نے اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(3,184,'3:184','پھر اگر یہ تجھ کو جھٹلاویں تو پہلے تجھ سے جھٹلائے گئے بہت رسول جو لائے نشانیاں اور صحیفے اور کتاب روشن ');
INSERT INTO "al_hind" VALUES(3,185,'3:185','ہر جی کو چکھنی ہے موت اور تم کو پورے بدلے ملیں گے قیامت کے دن پھر جو کوئی دور کیا گیا دوزخ سے اور داخل کیا گیا جنت میں اس کا کام تو بن گیا اور نہیں زندگانی دنیا کی مگر پونجی دھوکے کی ');
INSERT INTO "al_hind" VALUES(3,186,'3:186','البتہ تمہاری آزمائش ہو گی مالوں میں اور جانوں میں اور البتہ سنو گے تم اگلی کتاب والوں سے اور مشرکوں سے بدگوئی بہت اور اگر تم صبر کرو اور پرہیزگاری کرو تو یہ ہمت کے کام ہیں ');
INSERT INTO "al_hind" VALUES(3,187,'3:187','اور جب اللہ نے عہد لیا کتاب والوں سے کہ اس کو بیان کرو گے لوگوں سے اور نہ چھپاؤ گے پھر پھینک دیا انہوں نے وہ عہد اپنی پیٹھ کے پیچھے اور خرید کیا اس کے بدلے تھوڑا سا مول سو کیا برا ہے جو خریدتے ہیں ');
INSERT INTO "al_hind" VALUES(3,188,'3:188','تو نہ سمجھ کہ جو لوگ خوش ہوتے ہیں اپنے کئے پر اور تعریف چاہتے ہیں بن کئے پر سو مت سمجھ ان کو کہ چھوٹ گئے عذاب سے اور ان کے لئےعذاب ہے دردناک ');
INSERT INTO "al_hind" VALUES(3,189,'3:189','اور اللہ ہی کے لئے ہے سلطنت آسمان اور زمین کی اور اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(3,190,'3:190','بیشک آسمان اور زمین کا بنانا اور رات اور دن کا آنا جانا اس میں نشانیاں ہیں عقل والوں کو ');
INSERT INTO "al_hind" VALUES(3,191,'3:191','وہ جو یاد کرتے ہیں اللہ کو کھڑے اور بیٹھے اور کروٹ پر لیٹے اور فکر کرتے ہیں آسمان اور زمین کی پیدائش میں کہتے ہیں اے رب ہمارے تو نے یہ عبث نہیں بنایا تو پاک ہے سب عیبوں سے ہم کو بچا دوزخ کے عذاب سے ');
INSERT INTO "al_hind" VALUES(3,192,'3:192','اے رب ہمارے جس کو تو نے دوزخ میں ڈالا سو اس کو رسوا کر دیا اور نہیں کوئی گنہگاروں کا مددگار ');
INSERT INTO "al_hind" VALUES(3,193,'3:193','اے رب ہمارے ہم نے سنا کہ ایک پکارنے والا پکارتا ہے ایمان لانے کو کہ ایمان لاؤ اپنے رب پر سو ہم ایمان لے آئے اے رب ہمارے اب بخش دے گناہ ہمارے اور دور کر دے ہم سے برائیاں ہماری اور موت دے ہم کو نیک لوگوں کے ساتھ ');
INSERT INTO "al_hind" VALUES(3,194,'3:194','اے رب ہمارے اور دے ہم کو جو وعدہ کیا تو نے ہم سے اپنے رسولوں کے واسطہ سے اور رسوا نہ کر ہم کو قیامت کے دن بیشک تو وعدہ کے خلاف نہیں کرتا ');
INSERT INTO "al_hind" VALUES(3,195,'3:195','پھر قبول کی ان کی دعا انکے رب نے کہ میں ضائع نہیں کرتا محنت کسی محنت کرنے والے کی تم میں سے مرد ہو یا عورت تم آپس میں ایک ہو پھر وہ لوگ کہ ہجرت کی انہوں نے اور نکالے گئے اپنے گھروں سے اور ستائے گئے میری راہ میں اور لڑے اور مارے گئے البتہ دور کروں گا میں ان سے برائیاں ان کی اور داخل کروں گا ان کو باغوں میں جن کے نیچے بہتی ہیں نہریں یہ بدلہ ہے اللہ کےہاں سے اور اللہ کے ہاں ہے اچھا بدلہ ');
INSERT INTO "al_hind" VALUES(3,196,'3:196','تجھ کو دھوکا نہ دے چلنا پھرنا کافروں کا شہروں میں');
INSERT INTO "al_hind" VALUES(3,197,'3:197','یہ فائدہ ہے تھوڑا سا پھر ان کا ٹھکانا دوزخ ہے اور وہ بہت برا ٹھکانا ہے ');
INSERT INTO "al_hind" VALUES(3,198,'3:198','لیکن جو لوگ ڈرتے رہے اپنے رب سے انکے لئے باغ ہیں جن کے نیچے بہتی ہیں نہریں ہمیشہ رہیں گے ان میں مہمانی ہے اللہ کے ہاں سے اور جو اللہ کے ہاں ہے سو بہتر ہے نیک بختوں کے واسطے');
INSERT INTO "al_hind" VALUES(3,199,'3:199','اور کتاب والوں میں بعضے وہ بھی ہیں جو ایمان لاتے ہیں اللہ پر اور جو اترا تمہاری طرف اور جو اترا انکی طرف عاجزی کرتےہیں اللہ کے آگے نہیں خریدتے اللہ کی آیتوں پر مول تھوڑا یہی ہیں جن کے لئے مزدوری ہے ان کے رب کے ہاں بیشک اللہ جلد لیتا ہے حساب ');
INSERT INTO "al_hind" VALUES(3,200,'3:200','اے ایمان والو صبر کرو اور مقابلہ میں مضبوط رہو اور لگے رہو اور ڈرتے رہو اللہ سے تاکہ تم اپنی مراد کو پہنچو ');
INSERT INTO "al_hind" VALUES(4,1,'4:1','اے لوگو ڈرتے رہو اپنے رب سے جس نے پیدا کیا تم کو ایک جان سے اور اسی سے پیدا کیا اس کا جوڑا اور پھیلائے ان دونوں سے بہت مرد اور عورتیں اور ڈرتے رہو اللہ سے جس کے واسطے سے سوال کرتے ہو آپس میں اور خبردار رہو قرابت والوں سے بیشک اللہ تم پر نگہبان ہے ');
INSERT INTO "al_hind" VALUES(4,2,'4:2','اور دے ڈالو یتیموں کو ان کا مال اور بدل نہ لو برے مال کو اچھے مال سے اور نہ کھاؤ ان کے مال اپنے مالوں کے ساتھ یہ ہے بڑا وبال ');
INSERT INTO "al_hind" VALUES(4,3,'4:3','اور اگر ڈرو کہ انصاف نہ کر سکو گے یتیم لڑکیوں کے حق میں تو نکاح کر لو جو اور عورتیں تم کو خوش آویں دو دو تین تین چار چار پھر اگر ڈرو کہ ان میں انصاف نہ کر سکو گے تو ایک ہی نکاح کرو یا لونڈی جو اپنا مال ہے اس میں امید ہے کہ ایک طرف نہ جھک پڑو گے ');
INSERT INTO "al_hind" VALUES(4,4,'4:4','اور دے ڈالو عورتوں کو مہر ان کے خوشی سے پھر اگر وہ اس میں سے کچھ چھوڑ دیں تم کو اپنی خوشی سے تو اس کو کھاؤ رچتا پچتا ');
INSERT INTO "al_hind" VALUES(4,5,'4:5','اور مت پکڑا دو بے عقلوں کو اپنے وہ مال جن کو بنایا ہے اللہ نے تمہارےگذران کا سبب اور ان کو اس میں سے کھلاتے اور پہناتے رہو اور کہو ان سے بات معقول ');
INSERT INTO "al_hind" VALUES(4,6,'4:6','اور سدھاتے رہو یتیموں کو جب تک پہنچیں نکاح کی عمر کو پھر اگر دیکھو ان میں ہوشیاری تو حوالہ کر دو ان کے مال ان کو اور کھا نہ جاؤ یتیموں کا مال ضرورت سے زیادہ اور حاجت سے پہلے کہ یہ بڑے نہ ہو جائیں اور جس کو حاجت نہ ہو تو مال یتیم سے بچتا رہے اور جو کوڑی محتاج ہو تو کھاوے موافق دستور کے پھر جب ان کو حوالہ کرو ان کے مال تو گواہ کر لو اس پر اور اللہ کافی ہے حساب لینے کو ');
INSERT INTO "al_hind" VALUES(4,7,'4:7','مردوں کا بھی حصہ ہے اس میں جو چھوڑ مریں ماں باپ او رقرابت والے اور عورتوں کا بھی حصہ ہے اس میں جو چھوڑ مریں ماں باپ اور قرابت والے تھوڑا ہو یا بہت ہو حصہ مقرر کیا ہوا ہے ');
INSERT INTO "al_hind" VALUES(4,8,'4:8','اور جب حاضر ہوں تقسیم کے وقت رشتہ دار اور یتیم اور محتاج تو ان کو کچھ کھلا دو اس میں سے اور کہہ دو ان کو بات معقول ');
INSERT INTO "al_hind" VALUES(4,9,'4:9','اور چاہئے کہ ڈریں وہ لوگ کہ اگر چھوڑی ہے اپنے پیچھے اولاد ضعیف تو ان پر اندیشہ کریں یعنی ہمارے پیچھے ایسا ہی حال انکا ہو گا تو چاہئے کہ ڈریں اللہ سے اور کہیں بات سیدھی ');
INSERT INTO "al_hind" VALUES(4,10,'4:10','جو لوگ کہ کھاتے ہیں مال یتیموں کا ناحق وہ لوگ اپنے پیٹوں میں آگ ہی بھر رہے ہیں اور عنقریب داخل ہوں گے آگ میں ');
INSERT INTO "al_hind" VALUES(4,11,'4:11','حکم کرتا ہے تم کو اللہ تمہاری اولاد کے حق میں کہ ایک مرد کا حصہ ہے برابر ہے دو عورتوں کے پھر اگر صرف عورتیں ہی ہوں دو سے زیادہ تو ان کے لئے ہے دو تہائی اس مال سےجو چھوڑ مرا اور اگر ایک ہی ہو تو اس کے لئے آدھا ہے اور میت کے ماں باپ کو ہر ایک کے لئے دونوں میں سے چھٹا حصہ ہے اس مال سے جو کہ چھوڑ مرا اگر میت کے اولاد ہے اور اگر اس کے اولاد نہیں اور وارث ہیں اسکے ماں باپ تو اس کی ماں کا ہے تہائی پھر اگر میت کے کئ بھائی ہیں تو اس کی ماں کا ہے چھٹا حصہ بعد وصیت کے جو کر مرا یا بعد ادائے قرض کے تمہارے باپ اور بیٹے تم کو معلوم نہیں کون نفع پہنچائے تم کو زیادہ حصہ مقرر کیا ہوا اللہ کا ہے بیشک اللہ خبردار ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(4,12,'4:12','اور تمہارا ہے آدھا مال جو کہ چھوڑ مریں تمہاری عورتیں اگر نہ ہو ان کے اولاد اور اگر ان کےاولاد ہے تو تمہارے واسطے چوتھائی ہے اس میں سے جو چھوڑ گئیں بعد وصیت کے جو کر گئیں یا بعد قرض کے اور عورتوں کے لئے چوتھائی مال ہے اس میں سے جو چھوڑ مرو تم اگر نہ ہو تمہارے اولاد اور اگر تمہارے اولاد ہے تو ان کے لئے آٹھواں حصہ ہے اس میں سے کہ جو کچھ تم نے چھوڑا بعد وصیت کے جو تم کر مرو یا قرض کے اور اگر وہ مرد کہ جس کی میراث ہے باپ بیٹا کچھ نہیں رکھتا یا عورت ہو ایسی ہی اور اس میت کے ایک بھائی ہے یا بہن ہے تودونوں میں سے ہر ایک کا چھٹا حصہ ہے اور اگر زیادہ ہوں اس سے تو سب شریک ہیں ایک تہائی میں بعد وصیت کے جو ہو چکی ہے یا قرض کے جب اوروں کا نقصان نہ کیا ہو یہ حکم ہے اللہ کا اور اللہ ہی سب کچھ جاننے والا تحمل کرنے والا ');
INSERT INTO "al_hind" VALUES(4,13,'4:13','یہ حدیں باندھی ہوئی اللہ کی ہیں اور جو کوئی حکم پر چلے اللہ کے اور سول کے اس کو داخل کرے گا جنتوں میں جن کے نیچے بہتی ہیں نہریں ہمیشہ رہیں گے ان میں اور یہ ہے بڑی مراد ملنی');
INSERT INTO "al_hind" VALUES(4,14,'4:14','اور جو کوئی نافرمانی کرے اللہ کی اور اسکے رسول کی اور نکل جاوے اس کی حدوں سے ڈالے گا اس کو آگ میں ہمیشہ رہے گا اس میں اور اس کے لئے ذلت کا عذاب ہے ');
INSERT INTO "al_hind" VALUES(4,15,'4:15','اور جو کوئی بدکاری کرے تمہاری عورتوں میں سے تو گواہ لاؤ ان پر چار مرد اپنوں میں سے پھر اگر وہ گواہی دے دیویں تو بند رکھو ان عورتوں کو گھروں میں یہاں تک کہ اٹھا لیوے ان کو موت یا مقرر کر دے اللہ ان کے لئے کوئی راہ ');
INSERT INTO "al_hind" VALUES(4,16,'4:16','اور جو دو مرد کریں تم میں سے وہی بدکاری تو ان کو ایذا دو پھر اگر وہ دونوں توبہ کریں اور اپنی اصلاح کر لیں تو ان کا خیال چھوڑ دو بیشک اللہ توبہ قبول کرنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(4,17,'4:17','توبہ قبول کرنی اللہ کو ضرور تو انکی ہے جو کرتے ہیں برا کام جہالت سے پھر توبہ کرتے ہیں جلدی سے تو ان کو اللہ معاف کر دیتا ہے اور اللہ سب کچھ جاننے والا ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(4,18,'4:18','اور ایسوں کی توبہ نہیں جو کئے جاتے ہیں برے کام یہاں تک کہ جب سامنے آ جائے ان میں سے کسی کے موت تو کہنے لگا میں توبہ کرتا ہوں اب اور نہ ایسوں کی توبہ جو کہ مرتے ہیں حالت کفر میں ان کے لئے تو ہم نے تیار کیا ہے عذاب دردناک ');
INSERT INTO "al_hind" VALUES(4,19,'4:19','اے ایمان والو حلال نہیں تم کو کہ میراث میں لے لو عورتوں کو زبردستی اور نہ روکے رکھو ان کو اس واسطے کہ لے لو ان سے کچھ اپنا دیا ہوا مگر کہ وہ کریں بے حیائی صریح اور گذران کرو عورتوں کے ساتھ اچھی طرح پھر اگر وہ تم کو نہ بھاویں تو شاید تم کو پسند نہ آوے ایک چیز اور اللہ نے رکھی ہو اس میں بہت خوبی ');
INSERT INTO "al_hind" VALUES(4,20,'4:20','اور اگر بدلنا چاہو ایک عورت کی جگہ دوسری عورت کو اور دے چکے ہو ایک کو بہت سا مال تو مت پھیر لو اس میں سے کچھ کیا لیا چاہتے ہو اس کو ناحق اور صریح گناہ سے ');
INSERT INTO "al_hind" VALUES(4,21,'4:21','اور کیونکہ اس کو لے سکتے ہو اور پہنچ چکا ہے تم میں کا ایک دوسرے تک اور لے چکیں وہ عورتیں تم سے عہد پختہ ');
INSERT INTO "al_hind" VALUES(4,22,'4:22','اور نکاح میں نہ لاؤ جن عورتوں کو نکاح میں لائے تمہارے باپ مگر جو پہلے ہو چکا یہ بے حیائی ہے اور کام ہے غضب کا اور برا چلن ہے ');
INSERT INTO "al_hind" VALUES(4,23,'4:23','حرام ہوئی ہیں تم پر تمہاری مائیں اور بیٹیاں اور بہنیں اور پھوپھیاں اور خالائیں اور بیٹیاں بھائی کی اور بہن کی اور جن ماؤں نے تم کو دودھ پلایا اور دودھ کی بہنیں اور تمہاری عورتوں کی مائیں اور ان کی بیٹیاں جو تمہاری پرورش میں ہیں جن کو کہ جنا ہے تمہاری ان عورتوں نے جن سے تم نے صحبت کی اور اگر تم نے ان سے صحبت نہیں کی تو تم پر کچھ گناہ نہیں اس نکاح میں اور عورتیں تمہارے بیٹوں کی جو تمہاری پشت سے ہیں اور یہ کہ اکٹھا کرو دو بہنوں کو مگر جو پہلے ہو چکا بیشک اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(4,24,'4:24','اور خاوند والی عورتیں مگر جن کےمالک ہو جائیں تمہارے ہاتھ حکم ہوا اللہ کا تم پر اور حلال ہیں تم کو سب عورتیں ان کےسوا بشرطیکہ طلب کرو انکو اپنے مال کے بدلے قید میں لانے کو نہ مستی نکالنے کو پھر جس کو کام میں لائے تم ان عورتوں میں سے تو ان کو دو ان کے حق جو مقرر ہوئے اور گناہ نہیں تم کو اس بات میں کہ ٹھہرا لو تم دونوں آپس کی رضا سے مقرر کیے پیچھے بیشک اللہ ہے خبردار حکمت والا ');
INSERT INTO "al_hind" VALUES(4,25,'4:25','اور جو کوئی نہ رکھے تم میں مقدور اس کا کہ نکاح میں لائے بیبیاں مسلمان تو نکاح کر لے ان سے جو تمہارے ہاتھ کا مال ہیں جو کہ تمہارے آپس کی لونڈیاں ہیں مسلمان اور اللہ کو خوب معلوم ہے تمہاری مسلمانی تم آپس میں ایک ہو سو ان سے نکاح کرو ان کے مالکوں کی اجازت سے اور دو ان کے مہر موافق دستور کے قید میں آنے والیاں ہوں نہ مستی نکالنے والیاں اور نہ چھپی یاری کرنے والیاں پھر جب قید نکاح میں آ چکیں تو اگر کریں بے حیائی کا کام تو ان پر آدھی سزا ہے بیبیوں کی سزا سے یہ اس کے واسطے ہے جو کوئی تم میں ڈرے تکلیف میں پڑنے سے اور صبر کرو تو بہتر ہے تمہارے حق میں اور اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(4,26,'4:26','اللہ چاہتا ہے کہ بیان کرے تمہارے واسطے اور چلائے تم کو پہلوں کی راہ اور معاف کرے تم کو اور اللہ جاننے والا ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(4,27,'4:27','اور اللہ چاہتا ہے کہ تم پر متوجہ ہووے اور چاہتے ہیں وہ لوگ جو لگے ہوئے ہیں اپنے مزوں کے پیچھے کہ تم پھر جاؤ راہ سے بہت دور ');
INSERT INTO "al_hind" VALUES(4,28,'4:28','اللہ چاہتا ہے کہ تم سے بوجھ ہلکا کرے اور انسان بنا ہے کمزور ');
INSERT INTO "al_hind" VALUES(4,29,'4:29','اے ایمان والو نہ کھاؤ مال ایک دوسرے کے آپس میں ناحق مگر یہ کہ تجارت ہو آپس کی خوشی سے اور نہ خون کرو آپس میں بیشک اللہ تم پر مہربان ہے ');
INSERT INTO "al_hind" VALUES(4,30,'4:30','اور جو کوئی یہ کام کرے تعدی سے اور ظلم سے تو ہم اس کو ڈالیں گے آگ میں اور یہ اللہ پر آسان ہے ');
INSERT INTO "al_hind" VALUES(4,31,'4:31','اگر تم بچتے رہو گے ان چیزوں سے جو گناہوں میں بڑی ہیں تو ہم معاف کر دیں گے تم سے چھوٹے گناہ تمہارے اور داخل کریں گے تم کو عزت کے مقام میں ');
INSERT INTO "al_hind" VALUES(4,32,'4:32','اور ہوس مت کرو جس چیز میں بڑائی دی اللہ نے ایک کو ایک پر مردوں کو حصہ ہے اپنی کمائی سے اور عورتوں کو حصہ ہے اپنی کمائی سے اور مانگو اللہ سے اس کا فضل بیشک اللہ کو ہر چیز معلوم ہے ');
INSERT INTO "al_hind" VALUES(4,33,'4:33','اور ہر کسی کے لئے ہم نے مقرر کر دیے ہیں وارث اس مال کے کہ چھوڑ مریں ماں باپ اور قرابت والے اور جن سے معاہدہ ہوا تمہارا ان کو دے دو ان کا حصہ بیشک اللہ کے روبرو ہے ہر چیز ');
INSERT INTO "al_hind" VALUES(4,34,'4:34','مرد حاکم ہیں عورتوں پر اس واسطے کہ بڑائی دی اللہ نے ایک کو ایک پر اور اس واسطے کہ خرچ کیے انہوں نے اپنے مال پھر جو عورتیں نیک ہیں سو تابعدار ہیں نگہبانی کرتی ہیں پیٹھ پیچھے اللہ کی حفاظت سے اور جن کی بدخوئی کا ڈر ہو تم کو تو ان کو سمجھاؤ اور جدا کرو سونے میں اور مارو پھر اگر کہا مانیں تمہارا تو مت تلاش کرو ان پر راہ الزام کی بیشک اللہ ہے سب سے اوپر بڑا ');
INSERT INTO "al_hind" VALUES(4,35,'4:35','اور اگر تم ڈرو کہ وہ دونوں آپس میں ضد رکھتے ہیں تو کھڑ اکرو ایک منصف مرد والوں میں سے اور ایک منصف عورت والوں میں سے اگر یہ دونوں چاہیں گے کہ صلح کرا دیں تو اللہ موافقت کر دے گا ان دونوں میں بیشک اللہ سب کچھ جاننے والا خبردار ہے ');
INSERT INTO "al_hind" VALUES(4,36,'4:36','اور بندگی کرو اللہ کی اور شریک نہ کرو اس کا کسی کو اور ماں باپ کے ساتھ نیکی کرو اور قرابت والوں کے ساتھ اور یتیموں اور فقیروں اور ہمسایہ قریب اور ہمسایہ اجنبی اور پاس بیٹھنے والےاور مسافر کے ساتھ اور اپنے ہاتھ کے مال یعنی غلام باندیوں کے ساتھ بیشک اللہ کو پسند نہیں آتا اترانے والا بڑائی کرنے والا ');
INSERT INTO "al_hind" VALUES(4,37,'4:37','جو کہ بخل کرتے ہیں اور سکھاتے ہیں لوگوں کو بخل اور چھپاتے ہیں جو ان کو دیا اللہ نے اپنے فضل سے اور تیار کر رکھا ہے ہم نے کافروں کے لئے عذاب ذلت کا ');
INSERT INTO "al_hind" VALUES(4,38,'4:38','اور وہ لوگ جو کہ خرچ کرتے ہیں اپنے مال لوگوں کے دکھانے کو اور ایمان نہیں لاتے اللہ پر اور نہ قیامت کے دن پر اور جس کا ساتھی ہوا شیطان تو وہ بہت برا ساتھی ہے ');
INSERT INTO "al_hind" VALUES(4,39,'4:39','اور کیا نقصان تھا ان کا اگر ایمان لاتے اللہ پر اور قیامت کے دن پر اور خرچ کرتے اللہ کے دیے ہوئے میں سے اور اللہ کو ان کی خوب خبر ہے ');
INSERT INTO "al_hind" VALUES(4,40,'4:40','بیشک اللہ حق نہیں رکھتا کسی کا ایک ذرہ برابر اور اگر نیکی ہو تو اس کو دونا کر دیتا ہے اور دیتا ہے اپنے پاس سے بڑا ثواب ');
INSERT INTO "al_hind" VALUES(4,41,'4:41','پھر کیا حال ہو گا جب بلاویں گے ہم ہر امت میں سے احوال کہنے والا اور بلاویں گے تجھ کو ان لوگوں پر احوال بتانے والا ');
INSERT INTO "al_hind" VALUES(4,42,'4:42','اس دن آرزو کریں گے وہ لوگ جو کافر ہوئے تھے اور رسول کی نافرمانی کی تھی کہ برابر ہو جاویں زمین کے اور نہ چھپا سکیں گے اللہ سے کوئی بات ');
INSERT INTO "al_hind" VALUES(4,43,'4:43','اے ایمان والو نزدیک نہ جاؤ نماز کے جس وقت کہ تم نشہ میں ہو یہاں تک کہ سمجھنے لگو جو کہتے ہو اور نہ اس وقت کہ غسل کی حاجت ہو مگر راہ چلتے ہوئے یہاں تک کہ غسل کر لو اور اگر تم مریض ہو یا سفر میں یا آیا ہے کوئی شخص جائے ضرور سے یا پاس گئے ہو عورتوں کے پھر نہ ملا تم کو پانی تو ارادہ کرو زمین پاک کا پھر ملو اپنے منہ کو اور ہاتھوں کو بیشک اللہ ہے معاف کرنے والا بخشنے والا ');
INSERT INTO "al_hind" VALUES(4,44,'4:44','کیا تو نے نہ دیکھا ان کو جن کو ملا ہے کچھ حصہ کتاب سے خرید کرتے ہیں گمراہی اور چاہتے ہیں کہ تم بھی بہک جاؤ اپنی راہ سے');
INSERT INTO "al_hind" VALUES(4,45,'4:45','اور اللہ خوب جانتا ہے تمہارے دشمنوں کو اور اللہ کافی ہے حمایتی اور اللہ کافی ہے مددگار ');
INSERT INTO "al_hind" VALUES(4,46,'4:46','بعضے لوگ یہودی پھیرتے ہیں بات کو اس کے ٹھکانے سے اور کہتے ہیں ہم نے سنا اور نہ مانا اور کہتے ہیں کہ سن نہ سنایا جائیو اور کہتے ہیں راعنا موڑ کر اپنی زبان کو اور عیب لگانے کو دین میں اور اگر وہ کہتے ہم نے سنا اور مانا اور سن اور ہم پر نظر کر تو بہتر ہوتا ان کے حق میں اور درست لیکن لعنت کی ان پر اللہ نے ان کے کفر کے سبب سو وہ ایمان نہیں لاتے مگر بہت کم ');
INSERT INTO "al_hind" VALUES(4,47,'4:47','اے کتاب والو ایمان لاؤ اس پر جو ہم نے نازل کیا تصدیق کرتا ہے اس کتاب کی جو تمہارے پاس ہے پہلے اس سے کہ ہم مٹا ڈالیں بہت سے چہروں کو پھر الٹ دیں انکو پیٹھ کی طرف یا لعنت کریں ان پر جیسے ہم نےلعنت کی ہفتہ کے دن والوں پر اور اللہ کا حکم تو ہو کر ہی رہتا ہے ');
INSERT INTO "al_hind" VALUES(4,48,'4:48','بیشک اللہ نہیں بخشتا اس کو جو اس کا شریک کرے اور بخشتا ہے اس سے نیچے کے گناہ جس کے چاہے اور جس نے شریک ٹھہرایا اللہ کا اس نے بڑا طوفان باندھا ');
INSERT INTO "al_hind" VALUES(4,49,'4:49','کیا تو نے نہ دیکھا ان کو جو اپنے آپ کو پاکیزہ کہتے ہیں بلکہ اللہ ہی پاکیزہ کرتا ہے جس کو چاہے اور ان پر ظلم نہ ہو گا تاگے برابر ');
INSERT INTO "al_hind" VALUES(4,50,'4:50','دیکھ کیسا باندھتے ہیں اللہ پر جھوٹ اور کافی ہے یہی گناہ صریح ');
INSERT INTO "al_hind" VALUES(4,51,'4:51','کیا تو نے نہ دیکھا ان کو جن کو ملا ہے کچھ حصہ کتاب کا جو مانتے ہیں بتوں کو اور شیطان کو اور کہتے ہیں کافروں کو کہ یہ لوگ زیادہ راہ راست پر ہیں مسلمانوں سے ');
INSERT INTO "al_hind" VALUES(4,52,'4:52','یہ وہی ہیں جن پر لعنت کی ہے اللہ نے اور جس پر لعنت کرے اللہ نہ پاوے گا تو اس کا کوئی مددگار ');
INSERT INTO "al_hind" VALUES(4,53,'4:53','کیا ان کا کچھ حصہ ہےسلطنت میں پھر تو یہ نہ دیں گے لوگوں کو ایک تل برابر ');
INSERT INTO "al_hind" VALUES(4,54,'4:54','یا حسد کرتے ہیں لوگوں کا اس پر جو دیا ہے انکو اللہ نے اپنے فضل سےسو ہم نے تو دی ہے ابراہیم کے خاندان میں کتاب اور علم اور انکو دی ہے ہم نے بڑی سلطنت ');
INSERT INTO "al_hind" VALUES(4,55,'4:55','پھر ان میں سے کسی نے اس کو مانا اور کوئی اس سے ہٹا رہا اور کافی ہے دوزخ کی بھڑکتی آگ ');
INSERT INTO "al_hind" VALUES(4,56,'4:56','بیشک جو منکر ہوئے ہماری آیتوں سے ان کو ہم ڈالیں گے آگ میں جس وقت جل جائے گی کھال ان کی تو ہم بدل دیویں گے ان کو اور کھال تاکہ چکھتے رہیں عذاب بیشک اللہ ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(4,57,'4:57','اور جو لوگ ایمان لائے اور کام کئے نیک البتہ انکو ہم داخل کریں گے باغوں میں جن کے نیچے بہتی ہیں نہریں رہا کریں ان میں ہمیشہ ان کے لئے وہاں عورتیں ہیں ستھری اور انکو ہم داخل کریں گے گھنی چھاؤں میں ');
INSERT INTO "al_hind" VALUES(4,58,'4:58','بیشک اللہ تم کو فرماتا ہے کہ پہنچا دو امانتیں امانت والوں کو اور جب فیصلہ کرنے لگو لوگوں میں تو فیصلہ کرو انصاف سے اللہ اچھی نصیحت کرتا ہے تم کو بیشک اللہ ہے سننے والا دیکھنے والا ');
INSERT INTO "al_hind" VALUES(4,59,'4:59','اے ایمان والو حکم مانو اللہ کا اور حکم مانو رسول کا اور حاکموں کا جو تم میں سے ہوں پھر اگر جھگڑ پڑو کسی چیز میں تو اس کو رجوع کرو طرف اللہ کے اور رسول کے اگر یقین رکھتے ہو اللہ پر اور قیامت کے دن پر یہ بات اچھی ہے اور بہت بہتر ہے اس کا انجام ');
INSERT INTO "al_hind" VALUES(4,60,'4:60','کیا تو نے نہ دیکھا ان کو جو دعویٰ کرتے ہیں کہ ایمان لائے ہیں اس پر جو اترا تیری طرف اور جو اترا تجھ سے پہلے چاہتے ہیں کہ قضیہ لے جائیں شیطان کی طرف اور حکم ہو چکا ہے انکو کہ اس کو نہ مانیں اور چاہتا ہےشیطان کہ ان کو بہلا کر دور جا ڈالے ');
INSERT INTO "al_hind" VALUES(4,61,'4:61','اور جب ان کو کہے کہ آؤ اللہ کے حکم کی طرف جو اس نے اتارا اور رسول کی طرف تو دیکھے تو منافقوں کو کہ ہٹتے ہیں تجھ سے رک کر ');
INSERT INTO "al_hind" VALUES(4,62,'4:62','پھر کیا ہو کہ جب ان کو پہنچے مصیبت اپنے ہاتھوں کے کئے ہوئے سے پھر آویں تیرے پاس قسمیں کھاتے ہوئے اللہ کی کہ ہم کو غرض نہ تھی مگر بھلائی اور ملاپ ');
INSERT INTO "al_hind" VALUES(4,63,'4:63','یہ وہ لوگ ہیں کہ اللہ جانتا ہے جو ان کے دل میں ہے سو تو ان سے تغافل کر اور ان کو نصیحت کر اور ان سےکہہ ان کے حق میں بات کام کی ');
INSERT INTO "al_hind" VALUES(4,64,'4:64','اور ہم نے کوئی رسول نہیں بھیجا مگر اسی واسطے کہ اس کا حکم مانیں اللہ کے فرمانے سے اور اگر وہ لوگ جس وقت انہوں نے اپنا برا کیا تھا آتے تیرے پاس پھر اللہ سے معافی چاہتے اور رسول بھی ان کو بخشواتا تو البتہ اللہ کو پاتے معاف کرنے والا مہربان ');
INSERT INTO "al_hind" VALUES(4,65,'4:65','سو قسم ہے تیرے رب کی وہ مومن نہ ہوں گے یہاں تک کہ تجھ کو ہی منصف جانیں اس جھگڑے میں جو ان میں اٹھے پھر نہ پاویں اپنے جی میں تنگی تیرے فیصلہ سے اور قبول کریں خوشی سے ');
INSERT INTO "al_hind" VALUES(4,66,'4:66','اور اگر ہم ان پر حکم کرتے کہ ہلاک کرو اپنی جان یا چھوڑ نکلو اپنے گھر تو ایسا نہ کرتے مگر تھوڑے ان میں سے اور اگر یہ لوگ کریں وہ جو ان کو نصیحت کی جاتی ہے تو البتہ انکے حق میں بہتر ہو اور زیادہ ثبات رکھنے والا ہو دین میں');
INSERT INTO "al_hind" VALUES(4,67,'4:67','اور اس وقت البتہ دیں ہم انکو اپنے پاس سے بڑا ثواب');
INSERT INTO "al_hind" VALUES(4,68,'4:68','اور چلاویں ان کو سیدھی راہ ');
INSERT INTO "al_hind" VALUES(4,69,'4:69','اور جو کوئی حکم مانے اللہ کا اور اس کے رسول کا سو وہ ان کے ساتھ ہیں جن پر اللہ نے انعام کیا کہ وہ نبی اور صدیق اور شہید اور نیک بخت ہیں اور اچھی ہے ان کی رفاقت ');
INSERT INTO "al_hind" VALUES(4,70,'4:70','یہ فضل ہے اللہ کی طرف سے اور اللہ کافی ہے جاننے والا ');
INSERT INTO "al_hind" VALUES(4,71,'4:71','اے ایمان والو لے لو اپنے ہتھیار پھر نکلو جدی جدی فوج ہو کر یا سب اکھٹے ');
INSERT INTO "al_hind" VALUES(4,72,'4:72','اور تم میں بعضا ایسا ہے کہ البتہ دیر لگا دے گا پھر اگر تم کو کوئی مصیبت پہنچے تو کہے اللہ نے مجھ پر فضل کیا کہ میں نہ ہوا ان کے ساتھ ');
INSERT INTO "al_hind" VALUES(4,73,'4:73','اور اگر تم کو پہنچا فضل اللہ کی طرف سے تو اس طرح کہنے لگے گا کہ گویا نہ تھی تم میں اور اس میں کچھ دوستی اے کاش کہ میں ہوتا انکے ساتھ تو پاتا بڑی مراد ');
INSERT INTO "al_hind" VALUES(4,74,'4:74','سو چاہئے لڑیں اللہ کی راہ میں وہ لوگ جو بیچتے ہیں دنیا کی زندگی آخرت کے بدلے اور جو کوئی لڑے اللہ کی راہ میں پھر مارا جاوے یا غالب ہووے تو ہم دیں گے اس کو بڑا ثواب ');
INSERT INTO "al_hind" VALUES(4,75,'4:75','اور تم کو کیا ہوا کہ نہیں لڑتے اللہ کی راہ میں اور ان کے واسطے جو مغلوب ہیں مرد اور عورتیں اور بچے جو کہتے ہیں اے رب ہمارے نکال ہم کو اس بستی سے کہ ظالم ہیں یہاں کےلوگ اور کر دے ہمارے واسطے اپنے پاس سےکوئی حمایتی اور کر دے ہمارے واسطے اپنے پاس سے مددگار ');
INSERT INTO "al_hind" VALUES(4,76,'4:76','جو لوگ ایمان والے ہیں سو لڑتے ہیں اللہ کی راہ میں اور جو کافر ہیں سو لڑتے ہیں شیطان کی راہ میں سو لڑو تم شیطان کے حمایتیوں سے بیشک فریب شیطان کا سست ہے ');
INSERT INTO "al_hind" VALUES(4,77,'4:77','کیا تو نے نہ دیکھا ان لوگوں کو جن کو حکم ہوا تھا کہ اپنے ہاتھ تھامے رکھو اور قائم رکھو نماز اور دیتے رہو زکوٰۃ پھر جب حکم ہوا ان پر لڑائی کا اسی وقت ان میں ایک جماعت ڈرنےلگی لوگوں سے جیسا ڈر ہو اللہ کا یا اس سے بھی زیادہ ڈر اور کہنے لگے اے رب ہمارے کیوں فرض کی ہم پر لڑائی کیوں نہ چھوڑے رکھا ہم کو تھوڑی مدت تک کہہ دےکہ فائدہ دنیا کا تھوڑا ہے اور آخرت بہتر ہے پرہیزگار کو اور تمہارا حق نہ رہے گا ایک تاگے برابر ');
INSERT INTO "al_hind" VALUES(4,78,'4:78','جہاں کہیں تم ہو گے موت تم کو آ پکڑے گی اگرچہ تم ہو مضبوط قلعوں میں اور اگر پہنچے لوگوں کو کچھ بھلائی تو کہیں یہ اللہ کی طرف سے ہے اور اگر ان کو پہنچے کچھ برائی تو کہیں یہ تیری طرف سے ہے کہدے کہ سب اللہ کی طرف سے ہے سو کیا حال ہے ان لوگوں کا ہرگز نہیں لگتے کہ سمجھیں کوئی بات ');
INSERT INTO "al_hind" VALUES(4,79,'4:79','جو پہنچے تجھ کو کوئی بھلائی سو اللہ کی طرف سے ہے اور جو تجھ کو برائی پہنچے سو تیرے نفس کی طرف سے ہے اور ہم نے تجھ کو بھیجا پیغام پہنچانے والا لوگوں کو اور اللہ کافی ہے سامنے دیکھنے والا ');
INSERT INTO "al_hind" VALUES(4,80,'4:80','جس نے حکم مانا رسول کا اس نے حکم مانا اللہ کا اور جو الٹا پھرا تو ہم نے تجھ نہیں بھیجا ان پر نگہبان ');
INSERT INTO "al_hind" VALUES(4,81,'4:81','اور کہتے ہیں کہ قبول ہے پھر جب باہر گئے تیرے پاس سے تو مشورہ کرتے ہیں بعضے بعضے ان میں سے رات کو اس کےخلاف جو تجھ سےکہہ چکے تھے اور اللہ لکھتا ہے جو وہ مشورہ کرتے ہیں سو تو تغافل کر ان سے اور بھرسہ کر اللہ پر اور اللہ کافی ہے کارساز ');
INSERT INTO "al_hind" VALUES(4,82,'4:82','کیا غور نہیں کرتے قرآن میں اور اگر یہ ہوتا کسی اور کا سوائے اللہ کے تو ضرور پاتےاس میں بہت تفاوت ');
INSERT INTO "al_hind" VALUES(4,83,'4:83','اور جب ان کے پاس پہنچتی ہے کوئی خبر امن کی یا ڈر کی تو اسکو مشہور کر دیتے ہیں اور اگر اسکو پہنچا دیتے رسول تک اور اپنے حکموں تک تو تحقیق کرتے اس کو جو ان میں تحقیق کرنے والے ہیں اس کی اور اگر نہ ہوتا فضل اللہ کا تم پر اور اس کی مہربانی تو البتہ تم پیچھے ہو لیتے شیطان کے مگر تھوڑے ');
INSERT INTO "al_hind" VALUES(4,84,'4:84','سو تو لڑ اللہ کی راہ میں تو ذمہ دار نہیں مگر اپنی جان کا اور تاکید کر مسلمانوں کو قریب ہے کہ اللہ بند کر دے لڑائی کافروں کی اور اللہ بہت سخت ہے لڑائی میں اور بہت سخت ہے سزا دینے میں ');
INSERT INTO "al_hind" VALUES(4,85,'4:85','جو کوئی سفارش کرے نیک بات میں اس کو بھی ملے گا اس میں سے ایک حصہ اور جو کوئی سفارش کرے بری بات میں اس پر بھی ہے ایک بوجھ اس میں سے اور اللہ ہے ہر چیز پر قدرت رکھنے والا ');
INSERT INTO "al_hind" VALUES(4,86,'4:86','اور جب تم کو دعا دیوے کوئی تو تم بھی دعا دو اس سے بہتر یا وہی کہو الٹ کر بیشک اللہ ہے ہر چیز کا حساب کرنے والا ');
INSERT INTO "al_hind" VALUES(4,87,'4:87','اللہ کے سوا کسی کی بندگی نہیں بیشک تم کو جمع کرے گا قیامت کے دن اس میں کچھ شبہ نہیں اور اللہ سے سچی کس کی بات ');
INSERT INTO "al_hind" VALUES(4,88,'4:88','پھر تم کو کیا ہوا کہ منافقوں کے معاملہ میں دو فریق ہو رہے ہو اور اللہ نے انکو الٹ دیا بسبب ان کے اعمال کے کیا تم چاہتے ہو کہ راہ پر لاؤ جس کو گمراہ کیا اللہ نے اور جس کو گمراہ کرےاللہ ہر گز نہ پاوے گا تو اس کے لئے کوئی راہ ');
INSERT INTO "al_hind" VALUES(4,89,'4:89','چاہتے ہیں کہ تم بھی کافر ہو جاؤ جیسے وہ کافر ہوئے تو پھر تم سب برابر ہو جاؤ سو تم ان میں سےکسی کو دوست مت بناؤ یہاں تک کہ وطن چھوڑ آویں اللہ کی راہ میں پھر اگر اس کو قبول نہ کریں تو ان کو پکڑو اور مار ڈالو جہاں پاؤ اور نہ بناؤ ان میں سے کسی کو دوست اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(4,90,'4:90','مگر وہ لوگ جو ملاپ رکھتے ہیں ایک قوم سے کہ تم میں اور ان میں عہد ہے یا آئے ہیں تمہارے پاس کہ تنگ ہو گئے ہیں دل ان کے تمہاری لڑائی سے اور اپنی قوم کی لڑائی سے بھی اور اگر اللہ چاہتا تو ان کو تم پر زور دے دیتا تو ضرور لڑتے تم سےسو اگر یکسو رہیں وہ تم سے پھر تم سے نہ لڑیں اور پیش کریں تم پر صلح تو اللہ نے نہیں دی تم کو ان پر راہ ');
INSERT INTO "al_hind" VALUES(4,91,'4:91','اب تم دیکھو گے ایک اور قوم کو جو چاہتے ہیں کہ امن میں رہیں تم سے بھی اور اپنی قوم سے بھی جب کبھی لوٹائے جاتے ہیں وہ فساد کی طرف تو اسکی طرف لوٹ جاتے ہیں پھر اگر وہ تم سے یکسو نہ رہیں اور نہ پیش کریں تم پر صلح اور اپنے ہاتھ نہ روکیں تو ان کو پکڑو اور مار ڈالو جہاں پاؤ اور ان پر ہم نے تم کو دی ہے کھلی سند ');
INSERT INTO "al_hind" VALUES(4,92,'4:92','اور مسلمان کا کام نہیں کہ قتل کرے مسلمان کو مگر غلطی سے اور جو قتل کرے مسلمان کو غلطی سے تو آزاد کرے گردن ایک مسلمان کی اور خون بہا پہنچائے اسکے گھر والوں کو مگر یہ کہ وہ معاف کر دیں پھر اگر مقتول تھا ایسی قوم میں سےکہ وہ تمہارے دشمن ہیں اور خود وہ مسلمان تھا تو آزاد کرے گردن ایک مسلمان کی اور اگر وہ تھا ایسی قوم میں سےکہ تم میں اور ان میں عہد ہے تو خون بہا پہنچائے اس کےگھر والوں کو اور آزاد کرے گردن ایک مسلمان کی پھر جس کو میسر نہ ہو تو روزے رکھے دو مہینے کے برابر گناہ بخشوانے کو اللہ سے اور اللہ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(4,93,'4:93','اور جو کوئی قتل کرے مسلمان کو جان کر تو اس کی سزا دوزخ ہے پڑا رہے گا اسی میں اور اللہ کا اس پر غضب ہوا اور اس کو لعنت کی اور اسکے واسطے تیار کیا بڑا عذاب ');
INSERT INTO "al_hind" VALUES(4,94,'4:94','اے ایمان والو جب سفر کرو اللہ کی راہ میں تو تحقیق کر لیا کرو اور مت کہو اس شخص کو جو تم سے سلام علیک کرے کہ تو مسلمان نہیں تم چاہتے ہو اسباب دنیا کی زندگی کا سو اللہ کے ہاں بہت غنیمتیں ہیں تم بھی تو ایسے ہی تھے اس سے پہلے پھر اللہ نے تم پر فضل کیا سو اب تحقیق کر لو بیشک اللہ تمہارے کاموں سے خبردار ہے ');
INSERT INTO "al_hind" VALUES(4,95,'4:95','برابر نہیں بیٹھ رہنے والے مسلمان جن کو کوئی عذر نہیں اور وہ مسلمان جو لڑنے والے ہیں اللہ کی راہ میں اپنے مال سے اور جان سے اللہ نے بڑھا دیا لڑنے والوں کا اپنے مال اور جان سے بیٹھ رہنے والوں پر درجہ اور ہر ایک سے وعدہ کیا اللہ نے بھلائی کا اور زیادہ کیا اللہ نے لڑنے والوں کو بیٹھ رہنے والوں سے اجر عظیم میں');
INSERT INTO "al_hind" VALUES(4,96,'4:96','جو کہ درجے ہیں اللہ کی طرف سے اور بخشش ہے اور مہربانی ہے اور اللہ ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(4,97,'4:97','وہ لوگ کہ جن کی جان نکالتے ہیں فرشتے اس حالت میں کہ وہ برا کر رہے ہیں اپنا کہتے ہیں ان سے فرشتے تم کس حال میں تھے وہ کہتے ہیں ہم تھے بے بس اس ملک میں کہتے ہیں فرشتے کیا نہ تھی زمین اللہ کی کشادہ جو چلے جاتے وطن چھوڑ کر وہاں سو ایسوں کا ٹھکانا ہے دوزخ اور وہ بہت بری جگہ پہنچے');
INSERT INTO "al_hind" VALUES(4,98,'4:98','مگر جو ہیں بےبس مردوں اور عورتوں اور بچوں میں سے جو نہیں کر سکتے کوئی تدبیر اور نہ جانتےہیں کہیں کا راستہ');
INSERT INTO "al_hind" VALUES(4,99,'4:99','سو ایسوں کو امید ہے کہ اللہ معاف کرے اور اللہ ہے معاف کرنے والا بخشنے والا ');
INSERT INTO "al_hind" VALUES(4,100,'4:100','اور جو کوئی وطن چھوڑے اللہ کی راہ میں پاوے گا اس کے مقابلہ میں جگہ بہت اور کشائش اور جو کوئی نکلے اپنے گھر سے ہجرت کر کے اللہ اور رسول کی طرف پھر آ پکڑے اس کو موت تو مقرر ہو چکا اس کا ثواب اللہ کے ہاں اور ہے اللہ بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(4,101,'4:101','اور جب تم سفر کرو ملک میں تو تم پر گناہ نہیں کہ کچھ کم کرو نماز میں سےاگر تم کو ڈر ہو کہ ستاویں گے تم کو کافر البتہ کافر تمہارے صریح دشمن ہیں ');
INSERT INTO "al_hind" VALUES(4,102,'4:102','اور جب تو ان میں موجود ہو پھر نماز میں کھڑا کرے تو چاہئے ایک جماعت ان کی کھڑی ہو تیرے ساتھ اور ساتھ لے لیویں اپنے ہتھیار پھر جب یہ سجدہ کریں تو ہٹ جاویں تیرے پاس سے اور آوے دوسری جماعت جس نے نماز نہیں پڑھی وہ نماز پڑھیں تیرے ساتھ اور ساتھ لیویں اپنا بچاؤ اور ہتھیار کافر چاہتے ہیں کسی طرح تم بے خبر ہو اپنے ہتھیاروں سے اور اسباب سے تاکہ تم پر حملہ کریں یکبارگی اور تم پر کچھ گناہ نہیں اگر تم کو تکلیف ہو مینہ سے یا تم بیمار ہو کہ اتار رکھو اپنے ہتھیار اور ساتھ لے لو اپنا بچاؤ بیشک اللہ نے تیار کر رکھا ہے کافروں کے واسطے عذاب ذلت کا ');
INSERT INTO "al_hind" VALUES(4,103,'4:103','پھر جن تم نماز پڑھ چکو تو یاد کرو اللہ کو کھڑے اور بیٹھے اور لیٹے پھر جب خوف جاتا رہے تو درست کرو نماز کو بیشک نماز مسلمانوں پر فرض ہے اپنے مقررہ وقتوں میں ');
INSERT INTO "al_hind" VALUES(4,104,'4:104','اور ہمت نہ ہارو ان کا پیچھا کرنے سے اگر تم بے آرام ہوتے ہو تو وہ بھی بے آرام ہوتے ہیں جس طرح تم ہوتے ہو اور تم کو اللہ سے امید ہے جو ان کو نہیں اور اللہ سب کچھ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(4,105,'4:105','بیشک ہم نے اتاری تیری طرف کتاب سچی کہ تو انصاف کرے لوگوں میں جو کچھ سمجھاوے تجھ کو اللہ اور تو مت ہو دغابازوں کی طرف سے جھگڑنے والا ');
INSERT INTO "al_hind" VALUES(4,106,'4:106','اور بخشش مانگ اللہ سے بیشک اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(4,107,'4:107','اور مت جھگڑ ان کی طرف سے جو اپنے جی میں دغا رکھتے ہیں اللہ کو پسند نہیں جو کوئی ہو دغاباز گنہگار');
INSERT INTO "al_hind" VALUES(4,108,'4:108','شرماتےہیں لوگوں سے اور نہیں شرماتے اللہ سے اور وہ انکے ساتھ ہے جبکہ مشورہ کرتے ہیں رات کو اس بات کا جس سے اللہ راضی نہیں اور جو کچھ وہ کرتے ہیں سب اللہ کے قابو میں ہے ');
INSERT INTO "al_hind" VALUES(4,109,'4:109','سنتے ہو تم لوگ جھگڑا کرتے ہو ان کی طرف سے دنیا کی زندگی میں پھر کون جھگڑا کرے گا ان کے بدلے اللہ سے قیامت کے دن یا کون ہو گا ان کا کارساز ');
INSERT INTO "al_hind" VALUES(4,110,'4:110','اور جو کوئی کرے گناہ یا اپنا برا کرے پھر اللہ سے بخشوا دے تو پاوے اللہ کو بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(4,111,'4:111','اور جو کوئی کرے گناہ سو کرتا ہے اپنے ہی حق میں اور اللہ سب کچھ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(4,112,'4:112','اور جو کوئی کرے خطا یا گناہ پھر تمہت لگاوے کسی بے گناہ پر تو اس نے اپنے سر دھرا طوفان اور گناہ صریح ');
INSERT INTO "al_hind" VALUES(4,113,'4:113','اور اگر نہ ہوتا تجھ پر اللہ کا فضل اور اس کی رحمت تو قصد کر ہی چکی تھی ان میں ایک جماعت کہ تجھ کو بہکاویں اور بہکا نہیں سکتے مگر اپنے آپ کو اور تیرا کچھ نہیں بگاڑ سکتے اور اللہ نےاتاری تجھ پر کتاب اور حکمت اور تجھ کو سکھائیں وہ باتیں جو تو نہ جانتا تھا اور اللہ کا فضل تجھ پر بہت بڑا ہے ');
INSERT INTO "al_hind" VALUES(4,114,'4:114','کچھ اچھے نہیں ان کےاکثر مشورے مگر جو کوئی کہ کہے صدقہ کرنے کو یا نیک کام کو یا صلح کرانے کو لوگوں میں اور جو کوئی یہ کام کرے اللہ کی خوشی کے لئے تو ہم اس کو دیں گے بڑا ثواب ');
INSERT INTO "al_hind" VALUES(4,115,'4:115','اور جو کوئی مخالفت کرے رسول کی جبکہ کھل چکی اس پر سیدھی راہ اور چلے سب مسلمانوں کے رستہ کے خلاف تو ہم حوالہ کریں گے اس کو وہی طرف جو اس نے اختیار کی اور ڈالیں گے ہم اس کو دوزخ میں اور وہ بہت بری جگہ پہنچا ');
INSERT INTO "al_hind" VALUES(4,116,'4:116','بیثک اللہ نہیں بخشتا اس کو جو اس کا شریک کرے کسی کو اور بخشتا ہے اس کے سوا جس کو چاہے اور جس نے شریک ٹھرایا اللہ کا وہ بہک کر دور جا پڑا ');
INSERT INTO "al_hind" VALUES(4,117,'4:117','اللہ کے سوا نہیں پکارتے مگر عورتوں کو اور نہیں پکارتے مگر شیطان سرکش کو');
INSERT INTO "al_hind" VALUES(4,118,'4:118','جس پر لعنت کی اللہ نے اور کہا شیطان نے کہ میں البتہ لوں گا تیرے بندوں سےحصہ مقررہ ');
INSERT INTO "al_hind" VALUES(4,119,'4:119','اور انکو بہکاؤں گا اور انکو امیدیں دلاؤں گا اور انکو سکھلاؤں گا کہ چیریں جانوروں کے کان اور انکو سکھلاؤں گا کہ بدلیں صورتیں بنائی ہوئی اللہ کی اور جو کوئی بناوے شیطان کو دوست اللہ کو چھوڑ کر تو وہ پڑا صریح نقصان میں');
INSERT INTO "al_hind" VALUES(4,120,'4:120','انکو وعدہ دیتا ہے اور انکو امیدیں دلاتا ہے اور جو کچھ وعدہ دیتا ہے انکو شیطان سو سب فریب ہے');
INSERT INTO "al_hind" VALUES(4,121,'4:121','ایسوں کا ٹھکانا ہے دوزخ اور نہ پاویں گے وہاں سے کہیں بھاگنے کو جگہ ');
INSERT INTO "al_hind" VALUES(4,122,'4:122','اور جو لوگ ایمان لائے اور عمل کئے اچھے انکو ہم داخل کریں گے باغوں میں کہ جن کے نیچے بہتی ہیں نہریں رہا کریں ان میں ہی ہمیشہ وعدہ ہے اللہ کا سچا اور اللہ سےسچا کون ہے ');
INSERT INTO "al_hind" VALUES(4,123,'4:123','نہ تمہاری امیدوں پر مدار ہے اور نہ اہل کتاب کی امیدوں پر جو کوئی برا کام کرے گا اس کی سزا پاوے گا اور نہ پاوے گا اللہ کے سوا اپنا کوئی حمایتی اور نہ کوئی مددگار');
INSERT INTO "al_hind" VALUES(4,124,'4:124','اور جو کوئی کام کرے اچھے مرد ہو یا عورت اور وہ ایمان رکھتا ہو سو وہ لوگ داخل ہوں گے جنت میں اور ان کا حق ضائع نہ ہو گا تل بھر ');
INSERT INTO "al_hind" VALUES(4,125,'4:125','اور اس سے بہتر کس کا دین ہو گا جس نے پیشانی رکھی اللہ کے حکم پر اور نیک کاموں میں لگا ہوا ہے اور چلا دین ابراہیم پر جو ایک ہی طرف کا تھا اور اللہ نے بنا لیا ابراہیم کو خالص دوست ');
INSERT INTO "al_hind" VALUES(4,126,'4:126','اور اللہ ہی کا ہے جو کچھ ہے آسمانوں میں اور زمین میں اور سب چیزیں اللہ کے قابو میں ہیں ');
INSERT INTO "al_hind" VALUES(4,127,'4:127','اور تجھ سے رخصت مانگے ہیں عورتوں کے نکاح کی کہہ دے اللہ تم کو اجازت دیتا ہے ان کی اور وہ جو تم کو سنایا جاتا ہے قرآن میں سو حکم ہے ان یتیم عورتوں کا جن کو تم نہیں دیتے جو ان کے لئے مقرر کیا ہے اور چاہتے ہو کہ ان کو نکاح میں لے آؤ اور حکم ہے ناتوان لڑکوں کا اور یہ کہ قائم رہو یتیموں کےحق میں انصاف پر اور جو کرو گے بھلائی سو وہ اللہ کو معلوم ہے ');
INSERT INTO "al_hind" VALUES(4,128,'4:128','اور اگر کوئی عورت ڈرے اپنے خاوند کے لڑنے سے یا جی پھر جانے سو تو کچھ گناہ نہیں دونوں پر کہ کر لیں آپس میں کسی طرح صلح اور صلح خوب چیز ہے اور دلوں کے سامنے موجود ہے حرص اور اگر تم نیکی کرو اور پرہیزگاری کرو تو اللہ کو تمہارے سب کاموں کی خبر ہے ');
INSERT INTO "al_hind" VALUES(4,129,'4:129','اور تم ہرگز برابر نہ رکھ سکو گے عورتوں کو اگرچہ اس کی حرص کرو سو بالکل پھر بھی نہ جاؤ کہ ڈال رکھو ایک عورت کو جیسے ادھر میں لٹکتی اور اگر اصلاح کرتے رہو اور پرہیزگاری کرتے رہو تو للہ بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(4,130,'4:130','اور اگر دونوں جدا ہو جاویں تو اللہ ہر ایک کو بے پروا کر دے گا اپنی کشائش سے اور اللہ کشائش والا تدبیر جاننے والا ہے ');
INSERT INTO "al_hind" VALUES(4,131,'4:131','اور اللہ کا ہے جو کچھ ہے آسمانوں میں اور جو کچھ ہے زمین میں اور ہم نے حکم دیا ہے پہلے کتاب والوں کو اور تم کو کہ ڈرتے رہو اللہ سے اور اگر نہ مانو گے تو اللہ کا ہے جو کچھ ہے آسمانوں میں اور جو کچھ کہ ہے زمین میں اور اللہ ہے بے پروا سب خوبیوں والا');
INSERT INTO "al_hind" VALUES(4,132,'4:132','اور اللہ کا ہے جو کچھ ہے آسمانوں میں اور جو کچھ ہے زمین میں اور اللہ کافی ہے کارساز ');
INSERT INTO "al_hind" VALUES(4,133,'4:133','اگر چاہے تو تم کو دور کر دے اے لوگو اور لے آئے اور لوگوں کو اور اللہ کو یہ قدرت ہے ');
INSERT INTO "al_hind" VALUES(4,134,'4:134','جو کوئی چاہتا ہو ثواب دنیا کا سو اللہ کے یہاں ہے ثواب دنیا کا اور آخرت کا اور اللہ سب کچھ سنتا دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(4,135,'4:135','اے ایمان والو قائم رہو انصاف پر گواہی دو اللہ کی طرف کی اگرچہ نقصان ہو تمہارا یا ماں باپ کا یا قرابت والوں کا اگر کوئی مالدار ہے یا محتاج ہے تو اللہ ان کا خیرخواہ تم سے زیادہ ہے سو تم پیروی نہ کرو دل کی خواہش کی انصاف کرنے میں اور اگر تم زبان ملو گے یا بچا جاؤ گے تو اللہ تمہاے سب کاموں سے واقف ہے ');
INSERT INTO "al_hind" VALUES(4,136,'4:136','ایمان والو یقین لاؤ اللہ پر اور اسکے رسول پر اور اس کتاب پر جو نازل کی ہے اپنے رسول پر اور اس کتاب پر جو نازل کی تھی پہلے اور جو کوئی یقین نہ رکھے اللہ پر اور اسکے فرشتوں پر اور کتابوں پر اور رسولوں پر اور قیامت کےدن پر وہ بہک کر دور جا پڑا ');
INSERT INTO "al_hind" VALUES(4,137,'4:137','جو لوگ مسلمان ہوئے پھر کافر ہو گئے پھر مسلمان ہوئے پھر کافر ہو گئے پھر بڑھتے رہے کفر میں تو اللہ ان کو ہرگز بخشنے والا نہیں اور نہ دکھلاوے ان کو راہ ');
INSERT INTO "al_hind" VALUES(4,138,'4:138','خوشخبری سنا دے منافقوں کو کہ ان کے واسطے ہے عذاب دردناک');
INSERT INTO "al_hind" VALUES(4,139,'4:139','وہ جو بناتے ہیں کافروں کو اپنارفیق مسلمانوں کو چھوڑ کر کیا ڈھونڈھتے ہیں ان کے پاس عزت سو عزت تو اللہ ہی کے واسطے ہے ساری ');
INSERT INTO "al_hind" VALUES(4,140,'4:140','اور حکم اتار چکا تم پر قرآن میں کہ جب سنو اللہ کی آیتوں پر انکار ہوتے اور ہنسی ہوتے تو نہ بیٹھو ان کےساتھ یہاں تک کہ مشغول ہوں کسی دوسری بات میں نہیں تو تم بھی انہی جیسے ہو گئے اللہ اکٹھا کرے گا منافقوں کو اور کافروں کو دوزخ میں ایک جگہ ');
INSERT INTO "al_hind" VALUES(4,141,'4:141','وہ منافق جو تمہاری تاک میں ہیں پھر اگر تم کو فتح ملے اللہ کی طرف سے تو کہیں کیا ہم نہ تھےتمہارے ساتھ اور اگر نصیب ہو کافروں کو تو کہیں کیا ہم نےگھیر نہ لیا تھا تم کو اور بچا دیا تم کو مسلمانوں سے سو اللہ فیصلہ کرے گا تم میں قیامت کےدن اور ہرگز نہ دے گا اللہ کافروں کو مسلمانوں پر غلبہ کی راہ ');
INSERT INTO "al_hind" VALUES(4,142,'4:142','البتہ منافق دغابازی کرتےہیں اللہ سے اور وہی ان کو دغا دے گا اور جب کھڑے ہوں نماز کو تو کھڑے ہوں ہارے جی سے لوگوں کے دکھانے کو اور یاد نہ کریں اللہ کو مگر تھوڑا سا ');
INSERT INTO "al_hind" VALUES(4,143,'4:143','ادھر میں لٹکتے ہیں دونوں کے بیچ نہ ان کی طرف اور نہ اُن کی طرف اور جس کو گمراہ کرے اللہ تو ہر گز نہ پاوے گا تو اسکے واسطے کہیں راہ ');
INSERT INTO "al_hind" VALUES(4,144,'4:144','اے ایمان والو نہ بناؤ کافروں کو اپنا رفیق مسلمانوں کو چھوڑ کر کیا لیا چاہتے ہو اپنے اوپر اللہ کا الزام صریح');
INSERT INTO "al_hind" VALUES(4,145,'4:145','بیشک منافق ہیں سب سے نیچے درجہ میں دوزخ کے اور ہر گز نہ پاوے گا تو ان کے واسطے کوئی مددگار ');
INSERT INTO "al_hind" VALUES(4,146,'4:146','مگر جنہوں نے توبہ کی اور اپنی اصلاح کی اور مضبوط پکڑا اللہ کو اور خالص حکم بردار ہوئے اللہ کے سو وہ ہیں ایمان والوں کےساتھ اور جلد دے گا اللہ ایمان والوں کو بڑا ثواب ');
INSERT INTO "al_hind" VALUES(4,147,'4:147','کیا کرے گا اللہ تم کو عذاب کر کے اگر تم حق کو مانو اور یقین رکھو اور اللہ قدردان ہے سب کچھ جاننے والا ');
INSERT INTO "al_hind" VALUES(4,148,'4:148','اللہ کو پسند نہیں کسی کی بری بات کا ظاہر کرنا مگر جس پر ظلم ہوا ہو اور اللہ ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(4,149,'4:149','اگر تم کھول کر کرو کوئی بھلائی یا اسکو چھپاؤ یا معاف کرو برائی کو تو اللہ بھی معاف کرنے والا بڑی قدرت والا ہے ');
INSERT INTO "al_hind" VALUES(4,150,'4:150','جو لوگ منکر ہیں اللہ سے اور اسکے رسولوں سے اور چاہتے ہیں کہ فرق نکالیں اللہ میں اور اسکے رسولوں میں اور کہتے ہیں ہم مانتے ہیں بعضوں کو اور نہیں مانتے بعضوں کو اور چاہتے ہیں کہ نکالیں اس کے بیچ میں ایک راہ');
INSERT INTO "al_hind" VALUES(4,151,'4:151','ایسے لوگ وہی ہیں اصل کافر اور ہم نےتیار کر رکھا ہے کافروں کے واسطے ذلت کا عذاب ');
INSERT INTO "al_hind" VALUES(4,152,'4:152','اور جو لوگ ایمان لائے اللہ پر اور اسکے رسولوں پر اور جدا نہ کیا ان میں سے کسی کو ان کو جلد دے گا انکے ثواب اور اللہ ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(4,153,'4:153','تجھ سےدرخواست کرتے ہیں اہل کتاب کہ تو ان پر اتار لاوے لکھی ہوئی کتاب آسمان سے سو مانگ چکے ہیں موسٰی سے اس سے بھی بڑی چیز اور کہا ہم کو دکھا دے اللہ کو بالکل سامنے سو آ پڑی ان پر بجلی انکے گناہ کے باعث پھر بنا لیا بچھڑےکو بہت کچھ نشانیاں پہنچ چکنے کے بعد پھر ہم نے وہ بھی معاف کیا اور دیا ہم نے موسٰی کو غلبہ صریح ');
INSERT INTO "al_hind" VALUES(4,154,'4:154','اور ہم نے اٹھایا ان پر پہاڑ قرار لینے کے واسطے اور ہم نے کہا داخل ہو دروازہ میں سجدہ کرتے ہوئے اور ہم نے کہا کہ زیادتی مت کرو ہفتہ کے دن میں اور ہم نے ان سے لیا قول مضبوط ');
INSERT INTO "al_hind" VALUES(4,155,'4:155','ان کو جو سزا ملی سو ان کی عہد شکنی پر اور منکر ہونے پر اللہ کی آیتوں سے اور خون کرنے پر پیغمبروں کا ناحق اور اس کہنے پر کہ ہمارے دل پر غلاف ہے سو یہ نہیں بلکہ اللہ نے مہر کر دی انکے دل پر کفر کے سبب سو ایمان نہیں لاتے مگر کم ');
INSERT INTO "al_hind" VALUES(4,156,'4:156','اور ان کے کفر پر اور مریم پر بڑا طوفان باندھنے پر');
INSERT INTO "al_hind" VALUES(4,157,'4:157','اور انکے اس کہنے پر کہ ہم نے قتل کیا مسیح عیسٰی مریم کے بیٹے کو جو رسول تھا اللہ کا اور انہوں نے نہ اس کو مارا اور نہ سولی پر چڑھایا و لیکن وہی صورت بن گئ انکے آگے اور جو لوگ اس میں مختلف باتیں کرتے ہیں تو وہ لوگ اس جگہ شبہ میں پڑے ہوئے ہیں کچھ نہیں ان کو اس کی خبر صرف اٹکل پر چل رہے ہیں اور اس کو قتل نہیں کیا بیشک');
INSERT INTO "al_hind" VALUES(4,158,'4:158','بلکہ اس کو اٹھا لیا اللہ نے اپنی طرف اور اللہ ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(4,159,'4:159','اور جتنے فرقے ہیں اہل کتاب کے سو عیسٰی پر یقین لاویں گے اس کی موت سے پہلے اور قیامت کے دن ہو گا ان پر گواہ ');
INSERT INTO "al_hind" VALUES(4,160,'4:160','سو یہود کے گناہوں کی وجہ سے ہم نے حرام کیں ان پربہت سے پاک چیزیں جو ان پر حلال تھیں اور اس وجہ سے کہ روکتے تھے اللہ کی راہ سے بہت');
INSERT INTO "al_hind" VALUES(4,161,'4:161','اور اس وجہ سے کہ سود لیتے تھے اور ان کو اس کی ممانعت ہو چکی تھی اور اس وجہ سے کہ لوگوں کا مال کھاتے تھے ناحق اور تیار کر رکھا ہے ہم نے کافروں کے واسطے جو ان میں ہیں عذاب دردناک ');
INSERT INTO "al_hind" VALUES(4,162,'4:162','لیکن جو پختہ ہیں علم میں ان میں اور ایمان والے سو مانتے ہیں اسکو جو نازلہوا تجھ پر اور جو نازل ہوا تجھ سے پہلےاور آفریں ہے نماز پر قائم رہنے والوں کو اور جو دینے والے ہیں زکوٰۃ کے اور یقین رکھنے والے ہیں اللہ پر اور قیامت کے دن پر سو ایسوں کو ہم دیں گے بڑا ثواب ');
INSERT INTO "al_hind" VALUES(4,163,'4:163','ہم نے وحی بھیجی تیری طرف جیسے وحی بھیجی نوح پر اور ان نبیوں پر جو اسکے بعد ہوئے اور وحی بھیجی ابراہیم پر اور اسمٰعیل پر اور اسحٰق پر اور یعقوب پر اور اسکی اولاد پراور عیسٰی پر اور ایوب پر اور یونس پر اور ہارون پر اور سلیمان پر اور ہم نے دی داؤد کو زبور');
INSERT INTO "al_hind" VALUES(4,164,'4:164','اور بھیجے ایسے رسول کہ جن کا احوال ہم نے سنایا تجھ کو اس سے پہلےاور ایسے رسول جن کا احوال نہیں سنایا تجھ کو اور بایتں کیں اللہ نے موسٰی سے بول کر ');
INSERT INTO "al_hind" VALUES(4,165,'4:165','بھیجے پیغمبر خوشخبری اور ڈر سنانے والے تاکہ باقی نہ رہے لوگوں کو اللہ پر الزام کا موقع رسولوں کے بعد اور اللہ زبردست ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(4,166,'4:166','لیکن اللہ شاہد ہے اس پر جو تجھ پر نازل یا کہ یہ نازل کیا ہے اپنے علم کے ساتھ اور فرشتے بھی گواہ ہیں اور اللہ کافی ہےحق ظاہر کرنے والا ');
INSERT INTO "al_hind" VALUES(4,167,'4:167','جو لوگ کافر ہوئے اور روکا اللہ کی راہ سے وہ بہک کر دور جا پڑے');
INSERT INTO "al_hind" VALUES(4,168,'4:168','جو لوگ کافر ہوئے اور حق دبا رکھا ہر گز اللہ بخشنے والا نہیں ان کو اور نہ دکھلا دے گا ان کو سیدھی راہ');
INSERT INTO "al_hind" VALUES(4,169,'4:169','مگر راہ دوزخ کی رہا کریں اس میں ہمیشہ اور یہ اللہ پر آسان ہے ');
INSERT INTO "al_hind" VALUES(4,170,'4:170','اے لوگو تمہارے پاس رسول آ چکا ٹھیک بات لیکر تمہارے رب کی سو مان لو تاکہ بھلا ہو تمہارا اور اگر نہ مانو گے تو اللہ کا ہے جو کچھ ہے آسمانوں میں اور زمین میں اور ہے اللہ سب کچھ جاننے والا حکمت والا ');
INSERT INTO "al_hind" VALUES(4,171,'4:171','اے کتاب والو مت مبالغہ کرو اپنے دین کی بات میں اور مت کہو اللہ کی شان میں مگر پکی بات بیشک مسیح جو ہے عیسٰی مریم کا بیٹا وہ رسول ہے اللہ کا اور اس کا کلام ہے جس کو ڈالا مریم کی طرف اور روح ہے اس کے ہاں کی سو مانو اللہ کو اور اسکے رسولوں کو اور نہ کہو کہ خدا تین ہیں اس بات کو چھوڑ دو بہتر ہو گا تمہارے واسطے بیشک اللہ معبود ہے اکیلا اسکے لائق نہیں ہے کہ اس کے اولاد ہو ]۲۳۲] اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور کافی ہے اللہ کارساز ');
INSERT INTO "al_hind" VALUES(4,172,'4:172','مسیح کو اس سے ہرگز عار نہیں کہ وہ بندہ ہو اللہ کا اور نہ فرشتوں کو جو مقرب ہیں اور جس کو عار آوے اللہ کی بندگی سے اور تکبر کرے سو وہ جمع کرے گا ان سب کو اپنے پاس اکٹھا');
INSERT INTO "al_hind" VALUES(4,173,'4:173','پھر جو لوگ ایمان لائے اور عمل کئے انہوں نے اچھے تو انکو پورا دے گا ان کا ثواب اور زیادہ دے گا اپنے فضل سے اور جنہوں نے عار کی اور تکبر کیا سو انکو عذاب دے گا عذاب دردناک اور نہ پاویں گے اپنے واسطے اللہ کے سوا کوئی حمایتی اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(4,174,'4:174','اے لوگو تمہارے پاس پہنچ چکی تمہارے رب کی طرف سےسند اور اتاری ہم نے تم پر روشنی واضح');
INSERT INTO "al_hind" VALUES(4,175,'4:175','سو جو لوگ ایمان لائے اللہ پر اور اس کو مضبوط پکڑا تو ان کو داخل کرے گا اپنی رحمت میں اور فضل میں اور پہنچاوے گا ان کو اپنی طرف سیدھے راستہ پر ');
INSERT INTO "al_hind" VALUES(4,176,'4:176','حکم پوچھتے ہیں تجھ سے سو کہہ دے اللہ حکم بتاتا ہے تم کو کلالہ کا اگر کوئی مرد مر گیا اور اس کے بیٹا نہیں اور اس کے ایک بہن ہے تو اس کو پہنچے آدھا اس کا جو چھوڑ مرا اور وہ بھائی وارث ہے اس بہن کا اگر نہ ہو اس کے بیٹا پھر اگر بہنیں دو ہوں تو انکو پہنچے دو تہائی اس مال کا جو چھوڑ مرا اور اگر کئ شخص ہوں اسی رشتہ کے کچھ مرد اور کچھ عورتیں تو ایک مرد کا حصہ ہے برابر دو عورتوں کے بیان کرتا ہے اللہ تمہارے واسطے تاکہ تم گمراہ نہ ہو اور اللہ ہر چیز سےواقف ہے ');
INSERT INTO "al_hind" VALUES(5,1,'5:1','اے ایمان والو پورا کرو عہدوں کو حلا ل ہوئے تمہارے لئے چوپائے مویشی سو اے انکے جو تم کو آگے سنائےجاویں گے مگر حلال نہ جانو شکار کو احرام کی حالت میں اللہ حکم کرتا ہے جو چاہے ');
INSERT INTO "al_hind" VALUES(5,2,'5:2','اے ایمان والو حلال نہ سمجھو اللہ کی نشانیوں کو اور نہ ادب والے مہینہ کو اور نہ اس جانور کو جو نیاز کعبہ کی ہو اور نہ جنکے گلے میں پٹا ڈال کر لیجاویں کعبہ کو اور نہ آنے والوں کو حرمت والے گھر کی طرف جو ڈھونڈتے ہیں فضل اپنے رب کا اور اسکی خوشی اور جب احرام سے نکلو تو شکار کر لو اور باعث نہ ہو تم کو اس قوم کی دشمنی جو کہ تم کو روکتی تھی حرمت والی مسجد سے اس پر کہ زیادتی کرنے لگو اور آپس میں مدد کرو نیک کام پر اور پرہیزگاری پر اور مدد نہ کرو گناہ پر اور ظلم پر اور ڈرتے رہو اللہ سے بیشک اللہ کا عذاب سخت ہے ');
INSERT INTO "al_hind" VALUES(5,3,'5:3','حرام ہوا تم پر مردہ جانور اور لہو اور گوشت سور کا اور جس جانور پر نام پکارا جائے اللہ کے سوا کسی اور کا اور جو مر گیا ہو گلا گھونٹنے سے یاچوٹ سے یا اونچے سے گر کر یا سینگ مارنے سے اور جس کو کھایا ہو درندہ نے مگر جس کو تم نےذبح کر لیا اور حرام ہے جو ذبح ہوا کسی تھان پر اور یہ کہ تقسیم کرو جوئے کے تیروں سے یہ گناہ کا کام ہے آج ناامید ہو گئے کافر تمہارے دین سے سو ان سے مت ڈرو اور مجھ سے ڈرو آج میں پورا کر چکا تمہارے لئے دین تمہارا اور پورا کیا تم پر میں نے احسان اپنا اور پسند کیا میں نے تمہارے واسطےاسلام کو دین پھر جو کوئی لاچار ہو جاوے بھوک میں لیکن گناہ پر مائل نہ ہو تو اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(5,4,'5:4','تجھ سے پوچھتے ہیں کہ کیا چیز ان کےلئے حلال ہے کہہ دے تم کو حلال ہیں ستھری چیزیں اور جو سدھاؤ شکاری جانور شکار پر دوڑانے کو کہ ان کو سکھاتے ہو اس میں سےجو اللہ نے تم کو سکھایا ہے سو کھاؤ اس میں سے جو پکڑ رکھیں تمہارےواسطے اور اللہ کا نام لو اس پر اور ڈرتے رہو اللہ سے بیشک اللہ جلد لینے والا ہے حساب ');
INSERT INTO "al_hind" VALUES(5,5,'5:5','آج حلال ہوئیں تم کو سب ستھری چیزیں اور اہل کتاب کا کھانا تم کو حلال ہے اور تمہارا کھانا ان کو حلال ہے اور حلال ہیں تم کو پاکدامن عورتیں مسلمان اور پاکدامن عورتیں ان میں سےجن کو دی گئ کتاب تم سے پہلےجب دو ان کو مہر ان کے قید میں لانے کو نہ مستی نکالنے کو اور نہ چھپی آشنائی کرنے کو اور جو منکر ہوا ایمان سے تو ضائع ہوئی محنت اس کی اور آخرت میں وہ ٹوٹے والوں میں ہے ');
INSERT INTO "al_hind" VALUES(5,6,'5:6','اے ایمان والو جب تم اٹھو نماز کو تو دھو لو اپنے منہ اور ہاتھ کہنیوں تک اور مل لو اپنے سر کو اور پاؤں ٹخنوں تک اور اگر تم کو جنابت ہو تو خوب طرح پاک ہو اور اگر تم بیمار ہو یا سفر میں یا کوئی تم میں آیا ہے جائے ضرور سے یا پاس گئے ہو عورتوں کے پھر نہ پاؤ تم پانی تو قصد کرو مٹی پاک کا اور مل لو اپنے منہ اور ہاتھ اس سے اللہ نہیں چاہتا کہ تم پر تنگی کرے لیکن چاہتا ہے کہ تم کو پاک کرے اور پورا کرے اپنا احسان تم پر تاکہ تم احسان مانو ');
INSERT INTO "al_hind" VALUES(5,7,'5:7','اور یاد کرو احسان اللہ کا اپنے اوپر اور عہد اس کا جو تم سے ٹھہرایا تھا جب تم نے کہا تھا کہ ہم نے سنا اور مانا اور ڈرتے رہو اللہ سے اللہ خوب جانتا ہے دلوں کی بات ');
INSERT INTO "al_hind" VALUES(5,8,'5:8','اے ایمان والو کھڑے ہو جایا کرو اللہ کے واسطے گواہی دینے کو انصاف کی اور کسی قوم کی دشمنی کے باعث انصاف کو ہر گز نہ چھوڑو عدل کرو یہ بات زیادہ نزدیک ہے تقویٰ سے اور ڈرتے رہو اللہ سے اللہ کو خوب خبر ہے جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(5,9,'5:9','وعدہ کیا اللہ نے ایمان والوں سے اور جو نیک عمل کرتے ہیں کہ انکے واسطے بخشش اور بڑا ثواب ہے ');
INSERT INTO "al_hind" VALUES(5,10,'5:10','اور جن لوگوں نے کفر کیا اور جھٹلائیں ہماری آیتیں وہ ہیں دوزخ والے ');
INSERT INTO "al_hind" VALUES(5,11,'5:11','اے ایمان والو یاد رکھو احسان اللہ کا اپنے اوپر جب قصد کیا لوگوں نے کہ تم پر ہاتھ چلاویں پھر روک دیے تم سے ان کے ہاتھ اور ڈرتے رہو اللہ سے اور اللہ ہی پر چاہئے بھروسہ ایمان والوں کو ');
INSERT INTO "al_hind" VALUES(5,12,'5:12','اور لے چکا ہے اللہ عہد بنی اسرائیل سے اور مقرر کئے ہم نے ان میں بارہ سردار اور کہا اللہ نے میں تمہارے ساتھ ہو اگر تم قائم رکھو گے نماز اور دیتے رہو گے زکوٰۃ اور یقین لاؤ گے میرے رسولوں پر اور مدد کرو گے ان کی اور قرض دو گے اللہ کو اچھی طرح کا قرض تو البتہ دور کروں گا میں تم سے گناہ تمہارے اور داخل کروں گا تم کو باغوں میں کہ جن کے نیچے بہتی ہیں نہریں پھر جو کوئی کافر رہا تم میں سے اس کے بعد تو وہ بیشک گمراہ ہوا سیدھے راستہ سے ');
INSERT INTO "al_hind" VALUES(5,13,'5:13','سو ان کے عہد توڑنے پر ہم نے ان پر لعنت کی اور کر دیا ہم نے ان کے دلوں کو سخت پھیرتے ہیں کلام کو اسکے ٹھکانے سے اور بھول گئے نفع اٹھانا اس نصیحت سے جو انکو کی گئ تھی اور ہمیشہ تو مطلع ہوتا رہتا ہے ان کی کسی دغا پر مگر تھوڑے لوگ ان میں سے سو معاف کر اور درگذر کر ان سے اللہ دوست رکھتا ہے احسان کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(5,14,'5:14','اور وہ جو کہتے ہیں اپنے کو نصاریٰ ان سے بھی لیا تھا ہم نے عہد ان کا پھر بھول گئے نفعع اٹھانا اس نصیحت سے جو انکو کی گئ تھی پھر ہم نے لگا دی آپس میں انکی دشمنی اور کینہ قیامت کے دن تک اور آخر جتا دے گا ان کو اللہ جو کچھ کرتے تھے ');
INSERT INTO "al_hind" VALUES(5,15,'5:15','اے کتاب والو تحقیق آیا ہے تمہارے پاس رسول ہمارا ظاہر کرتا ہے تم پر بہت سی چیزیں جن کو تم چھپاتے تھے کتاب میں سے اور درگزر کرتا ہے بہت چیزوں سے بیشک تمہارے پاس آئی ہے اللہ کی طرف سے روشنی اور کتاب ظاہر کرنے والی');
INSERT INTO "al_hind" VALUES(5,16,'5:16','جس سے اللہ ہدایت کرتا ہے اس کو جو تابع ہوا اسکی رضا کا سلامتی کی راہیں اور ان کو نکالتا ہے اندھیروں سے روشنی میں اپنے حکم سے اور ان کو چلاتا ہے سیدھی راہ ');
INSERT INTO "al_hind" VALUES(5,17,'5:17','بیشک کافر ہوئے جنہوں نے کہا کہ اللہ تو وہی مسیح ہے مریم کا بیٹا تو کہہ دے پھر کس کا بس چل سکتا ہے اللہ کے آگے اگر وہ چاہے کہ ہلاک کرے مسیح مریم کے بیٹے کو اور اس کی ماں کو اور جتنے لوگ ہیں زمین میں سب کو اور اللہ ہی کے واسطے ہے سلطنت آسمانوں اور زمین کی اور جو کچھ درمیان ان دونوں کے ہے پیدا کرتا ہے جو چاہے اور اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(5,18,'5:18','اور کہتے ہیں یہود اور نصاریٰ ہم بیٹے ہیں اللہ کے اور اسکے پیارے تو کہہ پھر کیوں عذاب کرتا ہے تم کو تمہارے گناہوں پر کوئی نہیں بلکہ تم بھی ایک آدمی ہو اسکی مخلوق میں بخشے جس کو چاہے اور عذاب کرے جس کو چاہے اور اللہ ہی کے لئے ہے سلطنت آسمان اور زمین کی اور جو کچھ دونوں کے بیچ میں ہے اور اسکی کی طرف لوٹ کر جانا ہے ');
INSERT INTO "al_hind" VALUES(5,19,'5:19','اے کتاب والو آیا ہے تمہارے پاس رسول ہمارا کھولتا ہے تم پر رسولوں کے انقطاع کے بعد کبھی تم کہنے لگو کہ ہمارے پاس نہ آیا کوئی خوشی یا ڈر سنانے والا سو آ چکا تمہارے پاس خوشی اور ڈر سنانے والا اور اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(5,20,'5:20','اور جب کہا موسٰی نے اپنی قوم کو اے قوم یاد کرو احسان اللہ کا اپنے اوپر جب پیدا کئے تم میں نبی اور کر دیا تم کو بادشاہ اور دیا تم کو جو نہیں دیا تھا کسی کو جہان میں ');
INSERT INTO "al_hind" VALUES(5,21,'5:21','اے قوم داخل ہو زمین پاک میں جو مقرر کر دی ہے اللہ نے تمہارے واسطے اور نہ لوٹو اپنی پیٹھ کی طرف پھر جا پڑو گے نقصان میں ');
INSERT INTO "al_hind" VALUES(5,22,'5:22','بولے اے موسٰی وہاں ایک قوم ہے زبردست اور ہم ہر گز وہاں نہ جاویں گے یہاں تک کہ وہ نکل جاویں اس میں سے پھر اگر وہ نکل جاویں گے اس میں سےتو ہم ضرور داخل ہوں گے ');
INSERT INTO "al_hind" VALUES(5,23,'5:23','کہا دو مردوں نے اللہ سے ڈرنے والوں میں سے کہ خدا کی نوازش تھی ان دو پر گھس جاؤ ان پرحملہ کر کے دروازہ میں پھر جب تم اس میں گھس جاؤ گے تو تم ہی غالب ہو گے اور اللہ پر بھروسہ کرو اگر یقین رکھتے ہو ');
INSERT INTO "al_hind" VALUES(5,24,'5:24','بولے اے موسٰی ہم ہرگز نہ جاویں گے ساری عمر جب تک وہ رہیں گے اس میں سو تو جا اور تیرا رب اور تم دونوں لڑو ہم تو یہیں بیٹھے ہیں ');
INSERT INTO "al_hind" VALUES(5,25,'5:25','بولا اے رب میرے میرے اختیار میں نہیں مگر میری جان اور میرا بھائی سو جدائی کر دے تو ہم میں اور اس نافرمان قوم میں ');
INSERT INTO "al_hind" VALUES(5,26,'5:26','فرمایا تحقیق وہ زمین حرام کی گئ ہے ان پر چالیس برس سر مارتے پھریں گے ملک میں سو تو افسوس نہ کر نافرمان لوگوں پر');
INSERT INTO "al_hind" VALUES(5,27,'5:27','اور سنا ان کو حال واقعی آدم کے دو بیٹوں کا جب نیاز کی دونوں نے کچھ نیاز اور مقبول ہوئی ایک کی اور نہ مقبول ہوئی دوسرے کی کہا میں تجھ کو مار ڈالوں گا وہ بولا اللہ قبول کرتا ہے تو پرہیزگاروں سے ');
INSERT INTO "al_hind" VALUES(5,28,'5:28','اگر تو ہاتھ چلادے گا مجھ پر مارنے کو میں نہ ہاتھ چلاؤں گا تجھ پر مارنے کو میں ڈرتا ہوں اللہ سے جو پروردگار ہے سب جہان کا ');
INSERT INTO "al_hind" VALUES(5,29,'5:29','میں چاہتا ہوں کہ تو حاصل کرے میرا گناہ اور اپنا گناہ پھر ہو جاوے تو دوزخ والوں میں اور یہی ہے سزا ظالموں کی ');
INSERT INTO "al_hind" VALUES(5,30,'5:30','پھر اس کو راضی کیا اسکے نفس نے خون پر اپنے بھائی کے پھر اسکو مار ڈالا سو ہو گیا نقصان اٹھانے والوں میں ');
INSERT INTO "al_hind" VALUES(5,31,'5:31','پھر بھیجا اللہ نے ایک کوا جو کریدتا تھا زمین کو تاکہ اس کو دکھلا دے کس طرح چھپاتا ہے لاش اپنے بھائی کی بولا اے افسوس مجھ سے اتنا نہ ہو سکا کہ ہوں برابر اس کوے کی کہ میں چھپاؤں لاش اپنے بھائی کی پھر لگا پچتانے ');
INSERT INTO "al_hind" VALUES(5,32,'5:32','اسی سبب سے لکھا ہم نے بنی اسرائیل پر کہ جو کوئی قتل کرے ایک جان کو بلاعوض جان کے یا بغیر فساد کرنے کے ملک میں تو گویا قتل کر ڈالا اس نے سب لوگوں کو اور جس نے زندہ رکھا ایک جان کو تو گویا زندہ کر دیا سب لوگوں کو اور لا چکے ہیں انکے پاس رسول ہمارے کھلے ہوئے حکم پھر بہت لوگ ان میں سے اس پر بھی ملک میں دست درازی کرتے ہیں ');
INSERT INTO "al_hind" VALUES(5,33,'5:33','یہی سزا ہے ان کی جو لڑائی کرتے ہیں اللہ سے اور اسکے رسول سے اور دوڑتے ہیں ملک میں فساد کرنے کو ان کو قتل کیا جائےیا سولی چڑھائے جاویں یا کاٹے جاویں ان کے ہاتھ اور پاؤں مخالف جانب سے یا دور کر دیے جاویں اس جگہ سے یہ ان کی رسوائی ہے دنیا میں اور ان کے لئے آخرت میں بڑا عذاب ہے ');
INSERT INTO "al_hind" VALUES(5,34,'5:34','مگر جنہوں نے توبہ کی تمہارے قابو پانے سے پہلے تو جان لو کہ اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(5,35,'5:35','اے ایمان والو ڈرتے رہو اللہ سے اور ڈھونڈو اس تک وسیلہ اور جہاد کرو اس کی راہ میں تاکہ تمہارا بھلا ہو ');
INSERT INTO "al_hind" VALUES(5,36,'5:36','جو لوگ کافر ہیں اگر ان کے پاس ہو جو کچھ زمین میں ہے سارا اور اس کے ساتھ اتنا ہی اور ہو تاکہ بدلہ میں دیں اپنے قیامت کے عذاب سے تو ان سےقبول نہ ہو گا اور ان کے واسطے عذاب دردناک ہے ');
INSERT INTO "al_hind" VALUES(5,37,'5:37','چاہیں گے کہ نکل جاویں آگ سے اور وہ اس سے نکلنے والے نہیں اور ان کے لئے عذاب دائمی ہے ');
INSERT INTO "al_hind" VALUES(5,38,'5:38','اور چوری کرنے والا مرد اور چوری کرنے والی عورت کاٹ ڈالو انکے ہاتھ سزا میں ان کی کمائی کی تنبیہ ہے اللہ کی طرف سے اور اللہ غالب ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(5,39,'5:39','پھر جس نے توبہ کہ اپنے ظلم کے پیچھے اور اصلاح کی تو اللہ قبول کرتا ہے اس کی توبہ بیشک اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(5,40,'5:40','تجھ کو معلوم نہیں کہ اللہ ہی کے واسطے ہے سلطنت آسمان اور زمین کی عذاب کرے جس کو چاہے اور بخشے جس کو چاہے اور اللہ سب چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(5,41,'5:41','اے رسول غم نہ کر ان کو جو دوڑ کر گرتے ہیں کفر میں وہ لوگ جو کہتے ہیں ہم مسلمان ہیں اپنے منہ سے اور ان کے دل مسلمان نہیں اور وہ جو یہودی ہیں جاسوسی کرتے ہیں جھوٹ بولنے کے لئے وہ جاسوس ہیں دوسری جماعت کے جو تجھ تک نہیں آئے بدل ڈالتے ہیں بات کو اس کا ٹھکانہ چھوڑ کر کہتے ہیں اگر تم کو یہ حکم ملے تو قبول کر لینا اور یہ حکم نہ ملے تو بچتے رہنا اور جس اللہ نے گمراہ کرنا چاہا سو تو اس کے لئے کچھ نہیں کر سکتا اللہ کے ہاں یہ وہی لوگ ہیں جن کو اللہ نےنہ چاہا کہ دل پاک کرےان کے ان کو دنیا میں ذلت ہے اور ان کو آخرت میں بڑا عذاب ہے');
INSERT INTO "al_hind" VALUES(5,42,'5:42','جاسوسی کرنے والے جھوٹ بولنے کے لئے اور بڑے حرام کھانے والے سو اگر آویں وہ تیرے پاس تو فیصلہ کر دے ان میں یا منہ پھیر لے ان سے اور اگر تو منہ پھیر لے گا ان سے تو وہ تیرا کچھ نہ بگاڑ سکیں گے اور اگر تو فیصلہ کرے تو فیصلہ کر ان میں انصاف سے بیشک اللہ دوست رکھتا ہے انصاف کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(5,43,'5:43','اور وہ تجھ کو کس طرح منصف بنائیں گے اور ان کے پاس تو توریت ہے جس میں حکم ہے اللہ کا پھر اس کے پیچھے پھرے جاتے ہیں اور وہ ہر گز ماننے والے نہیں ہیں ');
INSERT INTO "al_hind" VALUES(5,44,'5:44','ہم نےنازل کی توریت کہ اس میں ہدایت اور روشنی ہے اس پر حکم کرتے تھے پیغمبر جو کہ حکم بردار تھے اللہ کے یہود کو اور حکم کرتے تھے درویش اور عالم اس واسطے کہ وہ نگہبان ٹھہرائے گئے تھے اللہ کی کتاب پر اور اس کی خبر گیری پر مقرر تھے سو تم نہ ڈرو لوگوں سے اور مجھ سے ڈرو اور مت خریدو میری آیتوں پر مول تھوڑا اور جو کوئی حکم نہ کرے اس کے موافق جو کہ اللہ نے اتارا سو وہی لوگ ہیں کافر ');
INSERT INTO "al_hind" VALUES(5,45,'5:45','اور لکھ دیا ہم نے ان پر اس کتاب میں کہ جی کے بدلے جی اور آنکھ کے بدلے آنکھ اور ناک کے بدلے ناک اور کان کے بدلے کان اور دانت کے بدلے دانت اور زخموں کا بدلا انکے برابر پھر جس نے معاف کر دیا تو وہ گناہ سے پاک ہو گیا اور جو کوئی حکم نہ کرے اس کے موافق جو کہ اللہ نے اتارا سو وہی لوگ ہیں ظالم ');
INSERT INTO "al_hind" VALUES(5,46,'5:46','اور پیچھے بھیجا ہم نے انہی کےقدموں پر عیسٰی مریم کے بیٹے کو تصدیق کرنے والا توریت کی جو آگے سے تھے اور اس کو دی ہم نے انجیل جس میں ہدایت اور روشنی تھی اور تصدیق کرتی تھی اپنے سے اگلی کتاب توریت کی اور راہ بتلانے والی اور نصیحت تھی ڈرنے والوں کو ');
INSERT INTO "al_hind" VALUES(5,47,'5:47','اور چاہئے کہ حکم کریں انجیل والے موافق اس کے جو کہ اتارا اللہ نے اس میں اور جو کوئی حکم نہ کرے موافق اس کے جو کہ اتارا اللہ نےسو وہی لوگ ہیں نافرمان ');
INSERT INTO "al_hind" VALUES(5,48,'5:48','اور تجھ پر اتاری ہم نے کتاب سچی تصدیق کرنے والی سابقہ کتابوں کی اور ان کے مضامیں پر نگہبان سو تو حکم کر ان میں موافق اسکے جو کہ اتارا اللہ نے اور انکی خوشی پر مت چل چھوڑ کر سیدھا راستہ جو تیرے پاس آیا ہر ایک کو تم میں سے دیا ہم نے ایک دستور اور راہ اور اللہ چاہتا تو تم کو ایک دین پر کر دیتا لیکن تم کو آزمانا چاہتا ہے اپنے دیے ہوئے حکموں میں سو تم دوڑ کر لو خوبیاں اللہ کے پاس تم سب کو پہنچنا ہے پھر جتا دے گا جس بات میں تم کو اختلاف تھا ');
INSERT INTO "al_hind" VALUES(5,49,'5:49','اور یہ فرمایا کہ حکم کر ان میں موافق اس کے جو کہ اتارا اللہ نے اور مت چل ان کی خوشی پر اور بچتا رہ ان سے کہ تجھ کو بہکا نہ دیں کسی اسےحکم سے جو اللہ نے اتارا تجھ پر پھر اگر نہ مانیں تو جان لے کہ اللہ نے یہی چاہا ہے کہ پہنچا دے انکو کچھ سزا ان کے گناہوں کی اور لوگوں میں بہت ہیں نافرمان ');
INSERT INTO "al_hind" VALUES(5,50,'5:50','اب کیا حکم چاہتے ہیں کفر کے وقت کا اور اللہ سے بہتر کون ہے حکم کرنے والا یقین کرنے والوں کے واسطے ');
INSERT INTO "al_hind" VALUES(5,51,'5:51','اے ایمان والو مت بناؤ یہود اور نصاریٰ کو دوست وہ آپس میں دوست ہیں ایک دوسرے کے اور جو کوئی تم میں سے دوستی کرے ان سے تو وہ انہی میں ہے اللہ ہدایت نہیں کرتا ظالم لوگوں کو ');
INSERT INTO "al_hind" VALUES(5,52,'5:52','اب تو دیکھے گا ان کو جن کے دل میں بیماری ہے دوڑ کر ملتے ہیں ان میں کہتے ہیں کہ ہم کو ڈر ہے کہ نہ آجائے ہم پر گردش زمانہ کی سو قریب ہے کہ اللہ جلد ظاہر فرماوے فتح یا کوئی حکم اپنے پاس سے تو لگیں اپنے جی کی چھپی بات پر پچتانے ');
INSERT INTO "al_hind" VALUES(5,53,'5:53','اور کہتے ہیں مسلمان کیا یہ وہی لوگ ہیں جو قسمیں کھاتے تھے اللہ کی تاکید سے کہ ہم تمہارے ساتھ ہیں برباد گئے ان کےعمل پھر رہ گئے نقصان میں');
INSERT INTO "al_hind" VALUES(5,54,'5:54','اے ایمان والو جو کوئی تم میں پھرے گا اپنے دین سے تو اللہ عنقریب لاوے گا ایسی قوم کو کہ اللہ انکو چاہتا ہے اور وہ اس کو چاہتے ہیں نرم دل ہیں مسلمانوں پر زبردست ہیں کافروں پر لڑتے ہیں اللہ کی راہ میں اور ڈرتے نہیں کسی کے الزام سے یہ فضل ہے اللہ کا دے گا جس کو چاہے اور اللہ کشائش والا ہے خبردار ');
INSERT INTO "al_hind" VALUES(5,55,'5:55','تمہارا رفیق تو وہی اللہ ہے اور اس کا رسول اور جو ایمان والے ہیں جو کہ قائم ہیں نماز پر اور دیتے ہیں زکوٰۃ اور وہ عاجزی کرنے والے ہیں ');
INSERT INTO "al_hind" VALUES(5,56,'5:56','اور جو کوئی دوست رکھے اللہ کو اور اس کے رسول کو اور ایمان والوں کو تو اللہ کی جماعت وہی سب پر غالب ہے ');
INSERT INTO "al_hind" VALUES(5,57,'5:57','اے ایمان والو مت بناؤ ان لوگوں کو جو ٹھہراتے ہیں تمہارے دین کو ہنسی اور کھیل وہ لوگ جو کتاب دیے گئے تم سے پہلے اور نہ کافروں کو اپنا دوست اور ڈرو اللہ سے اگر ہو تم ایمان والے ');
INSERT INTO "al_hind" VALUES(5,58,'5:58','اور جب تم پکارتے ہو نماز کے لئے تو وہ ٹھہراتے ہیں اس کو ہنسی اور کھیل یہ اس واسطے کہ وہ لوگ بے عقل ہیں ');
INSERT INTO "al_hind" VALUES(5,59,'5:59','تو کہہ اے کتاب والو کیا ضد ہے تم کو ہم سے مگر یہی کہ ہم ایمان لائے اللہ پر اور جو نازل ہوا ہم پر اور جو نازل ہو چکا پہلے اور یہی کہ تم میں اکثر نافرمان ہیں ');
INSERT INTO "al_hind" VALUES(5,60,'5:60','تو کہہ میں تم کو بتلاؤں ان میں کس کی بری جزا ہے اللہ کے ہاں وہی جس پر اللہ نے لعنت کی اور اس پر غضب نازل کیا اور ان میں سے بعضوں کو بندر کر دیا اور بعضوں کو سور اور جنہوں نے بندگی کی شیطان کی وہی لوگ بدتر ہیں درجہ میں اور بہت بہکے ہوئے ہیں سیدھی راہ سے ');
INSERT INTO "al_hind" VALUES(5,61,'5:61','اور جب تمہارے پاس آتے ہیں تو کہتے ہیں ہم ایمان لائے اور حالت یہ ہے کہ کافر ہی آئے تھے اور کافر ہی چلے گئے اور اللہ خوب جانتا ہے جو کچھ چھپائے ہوئے تھے ');
INSERT INTO "al_hind" VALUES(5,62,'5:62','اور تو دیکھے گا بہتوں کو ان میں سے کہ دوڑتے ہیں گناہ پر اور ظلم اور حرام کھانے پر بہت برے کام ہیں جو کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(5,63,'5:63','کیوں نہیں منع کرتے انکے درویش اور علماء گناہ کی بات کہنے سے اور حرام کھانے سے بہت ہی برے عمل ہیں جو کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(5,64,'5:64','اور یہود کہتے ہیں اللہ کا ہاتھ بند ہو گیا انہی کے ہاتھ بند ہو جاویں اور لعنت ہےانکو اس کہنے پر بلکہ اس کے تو دونوں ہاتھ کھلے ہوئے ہیں خرچ کرتا ہے جس طرح چاہے اور ان میں بہتوں کو بڑھے گی اس کلام سے جو تجھ پر اترا تیرے رب کی طرف سے شرارت اور انکار اور ہم نے ڈال رکھی ہے ان میں دشمنی اور بیر قیامت کے دن تک جب کبھی آگ سلگاتے ہیں لڑائی کے لئے اللہ اس کو بجھا دیتا ہے اور دوڑتے ہیں ملک میں فساد کرتے ہوئے اور اللہ پسند نہیں کرتا فساد کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(5,65,'5:65','اور اگر اہل کتاب ایمان لاتے اور ڈرتے تو ہم دور کر دیتے ان سے ان کی برائیاں اور ان کو داخل کرتے نعمت کے باغوں میں ');
INSERT INTO "al_hind" VALUES(5,66,'5:66','اور اگر وہ قائم رکھتے توریت اور انجیل کو اور اس کو جو کہ نازل ہوا ان پر انکے رب کی طرف سے تو کھاتے اپنے اوپر سے اور اپنے پاؤں کے نیچے سے کچھ لوگ ان میں ہیں سیدھی راہ پر اور بہت سے ان میں برے کام کر رہے ہیں');
INSERT INTO "al_hind" VALUES(5,67,'5:67','اے رسول پہنچا دے جو تجھ پر اترا تیرے رب کی طرف سے اور اگر ایسا نہ کیا تو تو نے کچھ نہ پہنچایا اس کا پیغام اور اللہ تجھ کو بچا لے گا لوگوں سے بیشک اللہ راستہ نہیں دکھلاتا قوم کفار کو ');
INSERT INTO "al_hind" VALUES(5,68,'5:68','کہہ دے اے کتاب والو تم کسی راہ پر نہیں جب تک نہ قائم کرو توریت اور انجیل کو اور جو تم پر اترا تمہارےرب کی طرف سے اور ان میں بہتوں کو بڑھے گی اس کلام سے جو تجھ پر اترا تیرے رب کی طرف سے شرارت اور کفر سو تو افسوس نہ کر اس قوم کفار پر ');
INSERT INTO "al_hind" VALUES(5,69,'5:69','بیشک جو مسلمان ہیں اور جو یہودی ہیں اور فرقہ صابی اور نصاریٰ جو کوئی ایمان لاوے اللہ پر اور روز قیامت پر اور عمل کرے نیک نہ ان پر ڈر ہے نہ وہ غمگین ہوں گے ');
INSERT INTO "al_hind" VALUES(5,70,'5:70','ہم نے لیا تھا پختہ قول بنی اسرائیل سے اور بھیجے ان کی طرف رسول جب لایا ان کے پاس کوئی رسول وہ حکم جو خوش نہ آیا ان کے جی کو تو بہتوں کو جھٹلایا اور بہتوں کو قتل کر ڈالتےتھے ');
INSERT INTO "al_hind" VALUES(5,71,'5:71','اور خیال کیا کہ کچھ خرابی نہ ہو گی سو وہ اندھے ہو گئے اور بہرے پھر توبہ قبول کی اللہ نے ان کی پھر اندھے اور بہرے ہوئے ان میں سےبہت اور اللہ دیکھتا ہے جو کچھ وہ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(5,72,'5:72','بیشک کافر ہوئے جنہوں نے کہا اللہ وہی مسیح ہے مریم کا بیٹا اور میسح نے کہا ہے کہ اے بنی اسرائیل بندگی کرو اللہ کی رب ہے میرا اور تمہارا بیشک جس نے شریک ٹھہرایا اللہ کا سو حرام کی اللہ نے اس پر جنت اور اس کا ٹھکانہ دوزخ ہے اور کوئی نہیں گنہگاروں کی مدد کرنے والا ');
INSERT INTO "al_hind" VALUES(5,73,'5:73','بیشک کافر ہوئے جنہوں نے کہا اللہ ہے تین میں کا ایک حالانکہ کوئی معبود نہیں بجز ایک معبود کے اور اگر نہ باز آویں گے اس بات سے کہ کہتے ہیں تو بیشک پہنچے گا ان میں سےکفر پر قائم رہنے والوں کو عذاب دردناک');
INSERT INTO "al_hind" VALUES(5,74,'5:74','کیوں نہیں توبہ کرتے اللہ کے آگے اور گناہ بخشواتے اس سے اور اللہ ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(5,75,'5:75','نہیں ہے مسیح مریم کا بیٹا مگر رسول گذر چکے اس سے پہلے بہت رسول اور اسکی ماں ولی ہے دونوں کھاتےتھے کھانا دیکھ ہم کیسے بتلاتے ہیں ان کو دلیلیں پھر دیکھ وہ کہاں الٹے جارہے ہیں ');
INSERT INTO "al_hind" VALUES(5,76,'5:76','تو کہہ دے کیا تم ایسی چیز کی بندگی کرتے ہو اللہ کو چھوڑ کر جو مالک نہیں تمہارے برے کی اور نہ بھلے کی اور اللہ وہی ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(5,77,'5:77','تو کہہ اے اہل کتاب مت مبالغہ کرو اپنے دین کی بات میں ناحق کا اور مت چلو خیالات پر ان لوگوں کے جو گمراہ ہو چکے پہلے اور گمراہ کر گئے بہتوں کو اور بہک گئے سیدھی راہ سے ');
INSERT INTO "al_hind" VALUES(5,78,'5:78','ملعون ہوئے کافر بنی اسرائیل میں کے داؤد کی زبان پر اور عیسٰی بیٹے مریم کے یہ اس لئے کہ وہ نافرمان تھے اور حد سے گذر گئےتھے ');
INSERT INTO "al_hind" VALUES(5,79,'5:79','آپس میں منع نہ کرتے برے کام سے جو وہ کر رہے تھے کیا ہی برا کام ہے جو کرتے تھے');
INSERT INTO "al_hind" VALUES(5,80,'5:80','تو دیکھتا ہے ان میں کہ بہت سے لوگ دوستی کرتے ہیں کافروں سے کیا ہی برا سامان بھیجا انہوں نے اپنے واسطے وہ یہ کہ اللہ کا غضب ہوا ان پر اور وہ ہمیشہ عذاب میں رہنے والے ہیں ');
INSERT INTO "al_hind" VALUES(5,81,'5:81','اور اگر وہ یقین رکھتے اللہ پر اور نبی پر اور جو نبی پر اترا تو کافروں کو دوست نہ بناتے لیکن ان میں بہت سے لوگ نافرمان ہیں ');
INSERT INTO "al_hind" VALUES(5,82,'5:82','تو پاوے گا سب لگوں سے زیادہ دشمن مسلمانوں کا یہودیوں کو اور مشرکوں کو اور تو پاوے گا سب سے نزدیک محبت میں مسلمانوں کے ان لوگوں کو جو کہتے ہیں کہ ہم نصاریٰ ہیں یہ اس واسطے کہ نصاریٰ میں عالم ہیں اور درویش ہیں اور اس واسطے کہ وہ تکبر نہیں کرتے');
INSERT INTO "al_hind" VALUES(5,83,'5:83','اور جب سنتے ہیں اس کو جو اترا رسول پر تو دیکھے تو ان کی آنکھوں کو کہ ابلتی ہیں آنسوؤں سے اس وجہ سے کہ انہوں نے پہچان لیا حق بات کو کہتے ہیں اے رب ہمارے ہم ایمان لائے سو تو لکھ ہم کو ماننے والوں کے ساتھ');
INSERT INTO "al_hind" VALUES(5,84,'5:84','اور ہم کو کیا ہوا کہ یقین نہ لاویں اللہ پر اور اس چیز پر جو پہنچی ہم کو حق سےاور توقع رکھیں اسکی کہ داخل کرے ہم کو رب ہمارا ساتھ نیک بختوں کے');
INSERT INTO "al_hind" VALUES(5,85,'5:85','پھر انکو بدلے میں دیے اللہ نے اس کہنے پر ایسے باغ کہ جن کے نیچے بہتی ہیں نہریں رہا کریں ان میں ہی اور یہ ہے بدلا نیکی کرنے والوں کا');
INSERT INTO "al_hind" VALUES(5,86,'5:86','اور جو لوگ منکر ہوئے اور جھٹلانےلگے ہمای آیتوں کو وہ ہیں دوزخ کے رہنے والے ');
INSERT INTO "al_hind" VALUES(5,87,'5:87','اے ایمان والو مت حرام ٹھہراؤ وہ لذیذ چیزیں جو اللہ نے تمہارے لئےحلال کردیں اور حد سے نہ بڑھو بیشک اللہ پسند نہیں کرتا حد سے بڑھنے والوں کو');
INSERT INTO "al_hind" VALUES(5,88,'5:88','اور کھاؤ اللہ کے دیے ہوئے میں سے جو چیز حلال پاکیزہ ہو اور ڈرتے رہو اللہ سے جس پر تم ایمان رکھتے ہو ');
INSERT INTO "al_hind" VALUES(5,89,'5:89','نہیں پکڑتا اللہ تم کو تمہاری بیہودہ قسموں پر لیکن پکڑتا ہے اس پر جس قسم کو تم نے مضبوط باندھا سو اس کا کفارہ کھانا دینا ہے دس محتاجوں کو اوسط درجہ کا کھانا جو دیتے ہو اپنے گھر والوں کو یا کپڑا پہنا دینا دس محتاجوں کو یا ایک گردن آزاد کرنی پھر جس کو میسر نہ ہوتو روزے رکھنے ہیں تین دن کے یہ کفارہ ہے تمہاری قسموں کا جب قسم کھا بیٹھو اور حفاطت رکھو اپنی قسموں کی اسی طرح بیان کرتا ہے اللہ تمہارے لئے اپنے حکم تاکہ تم احسان مانو ');
INSERT INTO "al_hind" VALUES(5,90,'5:90','اے ایمان والو یہ جو ہے شراب اور جوا اور بت اور پانسے سب گندے کام ہیں شیطان کے سو ان سے بچتے رہو تاکہ تم نجات پاؤ ');
INSERT INTO "al_hind" VALUES(5,91,'5:91','شیطان تو یہی چاہتا ہے کہ ڈالے تم میں دشمنی اور بیر بذریعہ شراب اور جوئے کے اور روکےتم کو اللہ کی یاد سے اور نماز سے سو اب بھی تم باز آؤ گے ');
INSERT INTO "al_hind" VALUES(5,92,'5:92','اور حکم مانو اللہ کا اور حکم مانو رسول کا اور بچتے رہو پھر اگر تم پھر جاؤ گے تو جان لو کہ ہمارے رسول کا ذمہ صرف پہنچا دینا ہے کھول کر ');
INSERT INTO "al_hind" VALUES(5,93,'5:93','جو لوگ ایمان لائے اور کام نیک کئے ان پر گناہ نہیں اس میں جو کچھ پہلے کھا چکے جب کہ آئندہ کو ڈر گئے اور ایمان لائے اور عمل نیک کئے پھر ڈرتے رہے اور یقین کیا پھر ڈرتے رہے اور نیکی کی اور اللہ دوست رکھتا ہےنیکی کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(5,94,'5:94','اے ایمان والو البتہ تم کو آزماوے گا اللہ ایک بات سے اس شکار میں کہ جس پر پہنچے ہیں ہاتھ تمہارے اور نیزےتمہارے تاکہ معلوم کرے اللہ کون اس سے ڈرتا ہے بن دیکھے پھر جس نے زیادتی کی اس کے بعد تو اس کے لئے عذاب دردناک ہے');
INSERT INTO "al_hind" VALUES(5,95,'5:95','اے ایمان والو نہ مارو شکار جس وقت تم ہو احرام میں اور جو کوئی تم میں اس کو مارے جان کر تو اس پر بدلا ہے اس مارے ہوئے کے برابر مویشی میں سے جو تجویز کریں دو آدمی معتبر تم میں سے اس طرح سے کہ وہ جانور بدلے کا بطور نیاز پہنچایا جاوے کعبہ تک یا اس پر کفارہ ہے چند محتاجوں کو کھلانا یا اس کےبرابر روزے تاکہ چکھے سزا اپنے کام کی اللہ نے معاف کیا جو کچھ ہو چکا اور جو کوئی پھر کرے گا اس سے بدلا لے گا اللہ اور اللہ زبردست ہے بدلا لینے والا ');
INSERT INTO "al_hind" VALUES(5,96,'5:96','حلال ہوا تمہارے لئے دریا کا شکار اور دریا کا کھانا تمہارے فائدہ کے واسطے اور سب مسافروں کے اور حرام ہوا تم پر جنگل کا شکار جب تک تم احرام میں رہو اور ڈرتے رہو اللہ سےجس کے پاس تم جمع ہو گے ');
INSERT INTO "al_hind" VALUES(5,97,'5:97','اللہ نے کر دیا کعبہ کو جو کہ گھر ہے بزرگی والا قیام کا باعث لوگوں کے لئے اور بزرگی والے مہینوں کو اور قربانی کو جو نیاز کعبہ کی ہو اور جنکے گلے میں پٹہ ڈال کر لیجاویں کعبہ کو یہ اس لئے کہ تم جان لو کہ بیشک اللہ کو معلوم ہے جو کچھ کہ ہے آسمان اور زمین میں اور اللہ ہر چیز سے خوب واقف ہے ');
INSERT INTO "al_hind" VALUES(5,98,'5:98','جان لو کہ بیشک اللہ کا عذاب سخت ہے اور بیشک اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(5,99,'5:99','رسول کے ذمہ نہیں مگر پہنچا دینا اور اللہ کو معلوم ہے جو تم ظاہر میں کرتے ہو اور جو چھپا کر کرتے ہو ');
INSERT INTO "al_hind" VALUES(5,100,'5:100','تو کہہ دے کہ برابر نہیں ناپاک اور پاک اگرچہ تجھ کو بھلی لگے ناپاک کی کثرت سو ڈرتے رہو اللہ سے اے عقلمندو تاکہ تمہاری نجات ہو ');
INSERT INTO "al_hind" VALUES(5,101,'5:101','اے ایمان والو مت پوچھو ایسی باتیں کہ اگر تم پر کھولی جاویں تو تم کو بری لگیں اور اگر پوچھو گے یہ باتیں ایسے وقت میں کہ قرآن نازل ہو رہا ہے تو تم پر ظاہر کر دی جاویں گی اللہ نے ان سے درگذر کی ہے اور اللہ بخشنے والا تحمل والا ہے');
INSERT INTO "al_hind" VALUES(5,102,'5:102','ایسی باتیں پوچھ چکی ہے ایک جماعت تم سے پہلے پھر ہو گئے ان باتوں سے منکر ');
INSERT INTO "al_hind" VALUES(5,103,'5:103','نہیں مقرر کیا اللہ نے بحیرہ اور نہ سائبہ اور نہ وصیلہ اور نہ حامی و لیکن کافر باندھتے ہیں اللہ پر بہتان اور ان میں اکثروں کو عقل نہیں ');
INSERT INTO "al_hind" VALUES(5,104,'5:104','اور جب کہا جاتا ہے ان کو آؤ اسکی طرف جو کہ اللہ نے نازل کیا اور رسول کی طرف تو کہتے ہیں ہم کو کافی ہے وہ جس پر پایا ہم نے اپنے باپ دادوں کو بھلا اگر انکے باپ دادے نہ کچھ علم رکھتے ہوں اور نہ راہ جانتے ہوں تو بھی ایسا ہی کریں گے ');
INSERT INTO "al_hind" VALUES(5,105,'5:105','اے ایمان والو تم پر لازم ہے فکر اپنی جان کا تمہارا کچھ نہیں بگاڑتا جو کوئی گمراہ ہوا جبکہ تم ہوئے راہ پر اللہ کے پاس لوٹ کر جانا ہے تم سب کو پھر وہ جتلاوے گا تمکو جو کچھ تم کرتےتھے ');
INSERT INTO "al_hind" VALUES(5,106,'5:106','اے ایمان والو گواہ درمیان تمہارے جبکہ پہنچے کسی کو تم میں موت وصیت کے وقت دو شخص معتبر ہونے چاہئیں تم میں سے یا دو شاہد اور ہوں تمہارے سوا اگر تم نے سفر کیا ہو ملک میں پھرپہنچے تم کو مصیبت موت کی تو کھڑا کرو ان دونوں کو بعد نماز کے وہ دونوں قسم کھاویں اللہ کی اگر تم کو شبہ پڑے کہیں کہ ہم نہیں لیتے قسم کے بدلے مال اگرچہ کسی کو ہم سے قرابت بھی ہو اور ہم نہیں چھپاتے اللہ کی گواہی نہیں تو ہم بیشک گنہگار ہیں ');
INSERT INTO "al_hind" VALUES(5,107,'5:107','پھر اگر خبر ہو جاوے کہ وہ دونوں حق بات دبا گئے تو دو گواہ اور کھڑے ہوں ان کی جگہ ان میں سے کہ جن کا حق دبا ہے جو سب سے زیادہ قریب ہوں میت کے پھر قسم کھاویں اللہ کی کہ ہماری گواہی تحقیقی ہے پہلوں کی گواہی سے اور ہم نے زیادتی نہیں کی نہیں تو ہم بیشک ظالم ہیں ');
INSERT INTO "al_hind" VALUES(5,108,'5:108','اس میں امید ہے کہ ادا کریں شہادت کو ٹھیک طرح پر اور ڈریں کہ الٹی پڑے گی قسم ہماری ان کی قسم کے بعد اور ڈرتے رہو اللہ سے اور سن رکھو اور اللہ نہیں چلاتا سیدھی راہ پر نافرمانوں کو ');
INSERT INTO "al_hind" VALUES(5,109,'5:109','جس دن اللہ جمع کرے گا سب پیغمبروں کو پھر کہے گا تم کو کیا جواب ملا تھا وہ کہیں گے ہم کو خبر نہیں تو ہی ہے چھپی باتوں کو جاننے والا');
INSERT INTO "al_hind" VALUES(5,110,'5:110','جب کہے گا اللہ اے عیسٰی مریم کے بیٹے یاد کر میرا احسان جو ہوا ہے تجھ پر اور تیری ماں پر جب مدد کی میں نے تیری روح پاک سے تو کلام کرتا تھا لوگوں سے گود میں اور بڑی عمر میں اور جب سکھائی میں نے تجھ کو کتاب اور تہہ کی باتیں اور توریت اور انجیل اور جب تو بناتا تھا گارے سے جانور کی صورت میرے حکم سے پھر پھونک مارتا تھا اسمیں تو ہو جاتا اڑنے والا میرے حکم سے اور اچھا کرتا تھا مادرزاد اندھے کو اور کوڑھی کو میرے حکم سے او رجب نکال کھڑا کرتا تھا مردوں کو میرے حکم سے اور جب روکا میں نے بنی اسرائیل کو تجھ سے جب تو لے کر آیا انکے پاس نشانیاں تو کہنے لگے جو کافر تھے ان میں اور کچھ نہیں یہ تو جادو ہے صریح ');
INSERT INTO "al_hind" VALUES(5,111,'5:111','اور جب میں نے دل میں ڈال دیا حواریوں کے کہ ایمان لاؤ مجھ پر اور میرے رسول پر تو کہنے لگے ہم ایمان لائے اور تو گواہ رہ کہ ہم فرمانبردار ہیں');
INSERT INTO "al_hind" VALUES(5,112,'5:112','جب کہا حواریوں نے اے عیسٰی مریم کے بیٹے تیرا رب کر سکتا ہے کہ اتارے ہم پر خوان بھرا ہوا آسمان سے بولا ڈرو اللہ سے اگر ہو تم ایمان والے ');
INSERT INTO "al_hind" VALUES(5,113,'5:113','بولے کہ ہم چاہتے ہیں کہ کھاویں اس میں سے اور مطمئن ہو جاویں ہمارے دل اور جان لیں کہ تو نے ہم سے سچ کہا او رہیں ہم اس پر گواہ ');
INSERT INTO "al_hind" VALUES(5,114,'5:114','کہا عیسٰی مریم کے بیٹے نے اے اللہ رب ہمارے اتار ہم پر خوان بھرا ہوا آسمان سے کہ وہ دن عید رہے ہماری پہلوں او رپچھلوں کے واسطے اور نشانی ہو تیری طرف سے اور روزی دے ہم کو اور تو ہی ہے سب سے بہتر روزی دینے والا ');
INSERT INTO "al_hind" VALUES(5,115,'5:115','کہا اللہ نے میں بیشک اتاروں گا وہ خوان تم پر پھر جو کوئی تم میں ناشکری کرے گا اس کے بعد تو میں اس کو وہ عذاب دوں گا جو کسی کو نہ دوں گا جہان میں ');
INSERT INTO "al_hind" VALUES(5,116,'5:116','اور جب کہے گا اللہ اے عیسٰی مریم کے بیٹے تو نے کہا لوگوں کو کہ ٹھہرا لو مجھ کو اور میری ماں کو دو معبود سوا اللہ کے کہا تو پاک ہے مجھ کو لائق نہیں کہ کہوں ایسی بات جس کا مجھ کو حق نہیں اگر میں نے یہ کہا ہوگا تو تجھ کو ضرور معلوم ہو گا تو جانتا ہے جو میرے جی میں ہے اور میں نہیں جانتا جو تیرے جی میں ہے بیشک تو ہی ہے جاننے والا چھپی باتوں کا ');
INSERT INTO "al_hind" VALUES(5,117,'5:117','میں نے کچھ نہیں کہا انکو مگر جو تو نے حکم کیا کہ بندگی کرو اللہ کی جو رب ہے میرا اور تمہارا اور میں ان سے خبردار تھا جب تک ان میں رہا پھر جب تو نے مجھ کو اٹھا لیا تو تو ہی تھا خبر رکھنے والا ان کی اور تو ہر چیز سے خبردار ہے ');
INSERT INTO "al_hind" VALUES(5,118,'5:118','اگر تو انکو عذاب دے تو وہ بندے ہیں تیرے اور اگر تو ان کو معاف کر دے تو تو ہی ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(5,119,'5:119','فرمایا اللہ نے یہ دن ہے کہ کام آوے گا سچوں کے ان کا سچ انکے لئے ہیں باغ جن کے نیچے بہتی ہیں نہریں رہا کریں گے انہی میں ہمیشہ اللہ راضی ہوا ان سے اور وہ راضی ہوئے اس سے یہی ہے بڑی کامیابی ');
INSERT INTO "al_hind" VALUES(5,120,'5:120','اللہ ہی کے لئے سلطنت ہے آسمانوں کی اور زمین کی اور جو کچھ ان کے بیچ میں ہے اور وہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(6,1,'6:1',' سب تعریفیں اللہ کےلئے ہیں جس نے پیدا کئے آسمان اور زمین اور بنایا اندھیرا اور اجالا پھر بھی یہ کافر اپنے رب کےساتھ اوروں کو برابر کئے دیتے ہیں ');
INSERT INTO "al_hind" VALUES(6,2,'6:2','وہی ہے جس نے پیدا کیا تم کو مٹی سے پھر مقرر کر دیا ایک وقت اور ایک مدت مقرر ہے اللہ کے نزدیک پھر بھی تم شک کرتے ہو ');
INSERT INTO "al_hind" VALUES(6,3,'6:3','اور وہی ہے اللہ آسمانوں میں اور زمین میں جانتا ہے تمہارا چھپا اور کھلا اور جانتا ہے جو کچھ تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(6,4,'6:4','اور نہیں آتی انکے پاس کوئی نشانی ان کے رب کی نشانیوں میں سے مگر کرتے ہیں اس سے تغافل ');
INSERT INTO "al_hind" VALUES(6,5,'6:5','سو بیشک جھٹلایا انہوں نے حق کو جب ان تک پہنچا سو اب آئی جاتی ہے انکے آگے حقیقت اس بات کی جس پر ہنستے تھے ');
INSERT INTO "al_hind" VALUES(6,6,'6:6','کیا دیکھتے نہیں کہ کتنی ہلاک کر دیں ہم نے ان سے پہلے امتیں جن کو جما دیا تھا ہم نے ملک میں اتنا کہ جتنا تم کو نہیں جمایا اور چھوڑ دیا ہم نے ان پر آسمان کو لگاتار برستا ہوا اور بنا دیں ہم نے نہریں بہتی ہوئی ان کے نیچے پھر ہلاک کیا ہم نے انکو انکے گناہوں پر اور پیدا کیا ہم نے ان کے بعد اور امتوں کو ');
INSERT INTO "al_hind" VALUES(6,7,'6:7','اور اگر اتاریں ہم تجھ پر لکھا ہوا کاغذ میں پھر چھو لیویں وہ اس کو اپنے ہاتھوں سے البتہ کہیں گے کافر یہ نہیں ہے مگر صریح جادو ');
INSERT INTO "al_hind" VALUES(6,8,'6:8','اور کہتے ہیں کیوں نہیں اترا اس پر کوئی فرشتہ اور اگر ہم اتاریں فرشتہ تو طے ہو جاوے قصہ پھر ان کو مہلت بھی نہ ملے ');
INSERT INTO "al_hind" VALUES(6,9,'6:9','اور اگر ہم رسول بنا کر بھیجتے کسی فرشتہ کو تو وہ بھی آدمی ہی کی صورت میں ہوتا اور انکو اسی شبہہ میں ڈالتے جس میں اب پڑ رہے ہیں ');
INSERT INTO "al_hind" VALUES(6,10,'6:10','اور بلاشبہہ ہنسی کرتے رہے ہیں رسولوں سے تجھ سے پہلے پھرگھیر لیا ان سے ہنسی کرنے والوں کو اس چیز نے کہ جس پر ہنسا کرتے تھے ');
INSERT INTO "al_hind" VALUES(6,11,'6:11','تو کہہ دے کہ سیر کرو ملک میں پھر دیکھو کیا انجام ہوا جھٹلانے والوں کا ');
INSERT INTO "al_hind" VALUES(6,12,'6:12','پوچھ کہ کس کا ہے جو کچھ کہ ہے آسمانوں اور زمین میں کہہ دے اللہ کا ہے اس نے لکھی ہے اپنے ذمہ مہربانی البتہ تم کو اکٹھا کر دے گا قیامت کے دن تک کہ اس میں کچھ شک نہیں جو لوگ نقصان میں ڈال چکے اپنی جانوں کو وہی ایمان نہیں لاتے ');
INSERT INTO "al_hind" VALUES(6,13,'6:13','اور اللہ ہی کا ہے جو کچھ کہ آرام پکڑتا ہے رات میں اور دن میں اور وہی ہے سب کچھ سننے والا جاننے والا');
INSERT INTO "al_hind" VALUES(6,14,'6:14','تو کہہ دے کیا اور کسی کو بناؤں اپنا مددگار اللہ کے سوا جو بنانے والا ہے آسمانوں اور زمین کا اور وہ سب کو کھلاتا ہے اور اسکو کوئی نہیں کھلاتا کہہ دے مجھ کو حکم ہوا ہے کہ سب سے پہلے حکم مانوں اور تو ہرگز نہ ہو شرک والا');
INSERT INTO "al_hind" VALUES(6,15,'6:15','تو کہہ میں ڈرتا ہوں اگر نافرمانی کروں اپنے رب کی ایک بڑے دن کے عذاب سے ');
INSERT INTO "al_hind" VALUES(6,16,'6:16','جس پر سے ٹل گیا وہ عذاب اس دن تو اس پر رحم کر دیا اللہ نے اور یہی ہے بڑی کامیابی ');
INSERT INTO "al_hind" VALUES(6,17,'6:17','اور اگر پہنچاوے تجھ کو اللہ کچھ سختی تو کوئی اسکو دور کرنے والا نہیں سوا اسکے اور اگر تجھ کو پہنچاوےبھلائی تو وہ ہر چیز پر قادر ہے');
INSERT INTO "al_hind" VALUES(6,18,'6:18','اور اسی کا زور ہے اپنے بندوں پر اور وہی ہے بڑی حکمت والا سب کی خبر رکھنے والا ');
INSERT INTO "al_hind" VALUES(6,19,'6:19','تو پوچھ سب سے بڑا کون ہے کہہ دے اللہ گواہ ہے میرے اور تمہارے درمیان اور اترا ہے مجھ پر یہ قرآن تاکہ تم کو اس سے خبردار کر دوں اور جس کو یہ پہنچے کیا تم گواہی دیتے ہو کہ اللہ کے ساتھ معبود اور بھی ہیں تو کہہ دے میں تو گواہی نہ دوں گا کہہ دے وہی ہے معبود ایک اور میں بیزار ہوں تمہارے شرک سے ');
INSERT INTO "al_hind" VALUES(6,20,'6:20','جن کو ہم نے دی ہے کتاب وہ پہچانتے ہیں اسکو جیسے پہچانتے ہیں اپنے بیٹوں کو جو لوگ نقصان میں ڈال چکے اپنی جانوں کو وہی ایمان نہیں لاتے ');
INSERT INTO "al_hind" VALUES(6,21,'6:21','اور اس سے زیادہ ظالم کون جو بہتان باندھے اللہ پر یا جھٹلا دے اسکی آیتوں کو بلاشک بھلائی نصیب نہیں ہوتی ظالموں کو ');
INSERT INTO "al_hind" VALUES(6,22,'6:22','اور جس دن ہم جمع کریں گے ان سب کو پھر کہیں گے ان لوگوں کو جنہوں نے شرک کیا تھا کہاں ہیں شریک تمہارے جن کا تم کو دعویٰ تھا ');
INSERT INTO "al_hind" VALUES(6,23,'6:23','پھر نہ رہے گا انکے پاس کوئی فریب مگر یہی کہ کہیں گے قسم ہے اللہ کی جو ہمارا رب ہے ہم نہ تھے شرک کرنے والے ');
INSERT INTO "al_hind" VALUES(6,24,'6:24','دیکھو تو کیسا جھوٹ بولے اپنے اوپر اور کھوئی گئیں ان سے وہ باتیں جو بنایا کرتے تھے ');
INSERT INTO "al_hind" VALUES(6,25,'6:25','اور بعضے ان میں کان لگائے رہتے ہیں تیری طرف اور ہم نے ان کے دلوں پر ڈال رکھے ہیں پردے تاکہ اس کو نہ سمجھیں اور رکھ دیا انکےکانوں میں بوجھ اور اگر دیکھ لیں تمام نشانیاں تو بھی ایمان نہ لاویں ان پر یہاں تک کہ جب آتے ہیں تیرے پاس تجھ سےجھگڑنے کو تو کہتے ہیں وہ کافر نہیں ہے یہ مگر کہانیاں پہلے لوگوں کی');
INSERT INTO "al_hind" VALUES(6,26,'6:26','اور یہ لوگ روکتے ہیں اس سے اور بھاگتے ہیں اس سے اور نہیں ہلاک کرتے مگر اپنے آپ کو اور نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(6,27,'6:27','اور اگر تو دیکھے جس وقت کہ کھڑے کئے جاویں گے وہ دوزخ پر پس کہیں گے اے کاش ہم پھر بھیجدئے جاویں اور ہم نہ جھٹلائیں اپنے رب کی آیتوں کو اور ہو جاویں ہم ایمان والوں میں ');
INSERT INTO "al_hind" VALUES(6,28,'6:28','کوئی نہیں بلکہ ظاہر ہو گیا جو چھپاتے تھے پہلے اور اگر پھر بھیجے جاویں تو پھر بھی وہی کام کریں جس سے منع کئے گئے تھے اور وہ بیشک جھوٹے ہیں ');
INSERT INTO "al_hind" VALUES(6,29,'6:29','اور کہتے ہیں ہمارےلئے زندگی نہیں مگر یہی دنیا کی اور ہم کو پھر نہیں زندہ ہونا ');
INSERT INTO "al_hind" VALUES(6,30,'6:30','اور کاش کہ تو دیکھے جس وقت وہ کھڑے کئے جاویں گے اپنے رب کے سامنے فرمائے گا کیا یہ سچ نہیں کہیں گے کیوں نہیں قسم ہے اپنے رب کی فرمائے گا تو چکھو عذاب بدلے میں اپنے کفر کے ');
INSERT INTO "al_hind" VALUES(6,31,'6:31','تباہ ہوئے وہ لوگ جنہوں نے جھوٹ جانا ملنا اللہ کا یہاں تک کہ جب آ پہنچے گی ان پر قیامت اچانک تو کہیں گے اے افسوس کیسی کوتاہی ہم نے اس میں کی اور وہ اٹھاویں گے اپنے بوجھ اپنی پیٹھوں پر خبردار ہو جاؤ کہ برا بوجھ ہے جس کو وہ اٹھاویں گے ');
INSERT INTO "al_hind" VALUES(6,32,'6:32','اور نہیں ہے زندگانی دنیا کی مگر کھیل اور جی بھلانا اور آخرت کا گھر بہتر ہے پرہیزگاروں کے لئے کیا تم نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(6,33,'6:33','ہم کو معلوم ہے کہ تجھ کو غم میں ڈالتی ہیں ان کی باتیں سو وہ تجھ کو نہیں جھٹلاتے لیکن یہ ظالم تو اللہ کی آیتوں کا انکار کرتے ہیں');
INSERT INTO "al_hind" VALUES(6,34,'6:34','اور جھٹلائے گئے ہیں بہت سے رسول تجھ سے پہلے پس صبر کرتے رہے جھٹلانے پر اور ایذا پر یہاں تک کہ پہنچی ان کو مدد ہماری اور کوئی نہیں بدل سکتا للہ کی باتیں اور تجھ کو پہنچ چکے ہیں کچھ حالات رسولوں کے ');
INSERT INTO "al_hind" VALUES(6,35,'6:35','اور اگر تجھ پر گراں ہے ان کا منہ پھیرنا تو اگر تجھ سے ہو سکے کہ ڈھونڈ نکالے کوئی سرنگ زمین میں یا کوئی سیڑھی آسمان میں پھر لاوے انکے پاس ایک معجزہ اور اگر اللہ چاہتا تو جمع کر دیتا سب کو سیدھی راہ پر سو تو مت ہو نادانوں میں ');
INSERT INTO "al_hind" VALUES(6,36,'6:36','مانتے وہی ہیں جو سنتے ہیں اور مردوں کو زندہ کرے گا اللہ پھر اسکی طرف لائے جاویں گے ');
INSERT INTO "al_hind" VALUES(6,37,'6:37','اور کہتےہیں کیوں نہیں اتری اس پر کوئی نشانی اسکے رب کی طرف سے کہہ دے کہ اللہ کو قدرت ہے اس بات پر کہ اتارے نشانی لیکن ان میں اکثر نہیں جانتے ');
INSERT INTO "al_hind" VALUES(6,38,'6:38','اور نہیں ہے کوئی چلنے والا زمین میں اور نہ کوئی پرندہ کہ اڑتا ہے اپنے دو بازوؤں سے مگر ہر ایک امت ہے تمہاری طرح ہم نےنہیں چھوڑی لکھنے میں کوئی چیز پھر سب اپنے رب کے سامنے جمع ہوں گے ');
INSERT INTO "al_hind" VALUES(6,39,'6:39','اور جو جھٹلاتے ہیں ہماری آیتوں کو وہ بہرے اور گونگے ہیں اندھیروں میں جسکو چاہے اللہ گمراہ کرے اور جس کو چاہے ڈال دے سیدھی راہ پر');
INSERT INTO "al_hind" VALUES(6,40,'6:40','تو کہہ دیکھو تو اگر آوے تم پر عذاب اللہ کا یا آوے تم پر قیامت کیا اللہ کے سوا کسی اور کو پکارو گے بتاؤ اگر تم سچے ہو');
INSERT INTO "al_hind" VALUES(6,41,'6:41','بلکہ اسی کو پکارتے ہو پھر دور کر دیتا ہے اس مصیبت کو جس کے لئے اسکو پکارتے ہو اگر چاہتا ہے اور تم بھول جاتے ہو جنکو شریک کرتے تھے ');
INSERT INTO "al_hind" VALUES(6,42,'6:42','اور ہم نے رسول بھیجے تھے بہت سی امتوں پر تجھ سے پہلے پھر ان کو پکڑا ہم نے سختی میں اور تکلیف میں تاکہ وہ گڑگڑاویں');
INSERT INTO "al_hind" VALUES(6,43,'6:43','پھر کیوں نہ گڑگڑائے جب آیا ان پر عذاب ہمارا لیکن سخت ہو گئے دل انکے اور بھلے کر دکھلائے ان کو شیطان نے جو کام وہ کر رہے تھے');
INSERT INTO "al_hind" VALUES(6,44,'6:44','پھر جب وہ بھول گئے اس نصیحت کو جو انکو کی گئ تھی کھول دیے ہم نے ان پر دروازے ہر چیز کے یہاں تک کہ جب وہ خوش ہوئے ان چیزوں پر جو انکو دی گئیں پکڑ لیا ہم نے انکو اچانک پس اس وقت وہ رہ گئے نا امید ');
INSERT INTO "al_hind" VALUES(6,45,'6:45','پھر کٹ گئ جڑ ان ظالموں کی اور سب تعریفیں اللہ ہی کے لئے ہیں جو پالنے والا ہے سارے جہان کا ');
INSERT INTO "al_hind" VALUES(6,46,'6:46','تو کہہ دیکھو تو اگر چھین لے اللہ تمہارےکان اور آنکھیں اور مہر کر دے تمہارے دلوں پر تو کون ایسا رب ہے اللہ کے سوا جو تم کو یہ چیزیں لا دیوے دیکھ ہم کیونکر طرح طرح سے بیان کرتے ہیں باتیں پھر بھی وہ کنارہ کرتے ہیں');
INSERT INTO "al_hind" VALUES(6,47,'6:47','تو کہہ دیکھو تو اگر آوے تم پر عذاب اللہ کا اچانک یا ظاہر ہو کر تو کون ہلاک ہو گا ظالم لوگوں کےسوا ');
INSERT INTO "al_hind" VALUES(6,48,'6:48','اور ہم رسول نہیں بھیجتے مگر خوشی اور ڈر سنانے کو پھر جو کوئی ایمان لایا اور سنور گیا تو نہ ڈر ہے ان پر اور نہ وہ غمگین ہوں');
INSERT INTO "al_hind" VALUES(6,49,'6:49','اور جنہوں نے جھٹلایا ہماری آیتوں کو انکو پہنچے گا عذاب اس لئے کہ وہ نافرمانی کرتے تھے ');
INSERT INTO "al_hind" VALUES(6,50,'6:50','تو کہہ میں نہیں کہتا تم سے کہ میرے پاس ہیں خزانے اللہ کے اور نہ میں جانوں غیب کی بات اور نہ میں کہوں تم سے کہ میں فرشتہ ہوں میں تو اسی پر چلتا ہوں جو میرے پاس اللہ کا حکم آتا ہے تو کہہ دے کب برابر ہو سکتا ہے اندھا اور دیکھنے والا سو کیا تم غور نہیں کرتے ');
INSERT INTO "al_hind" VALUES(6,51,'6:51','اور خبردار کر دے اس قرآن سے ان لوگوں کو جنکو ڈر ہے اسکا کہ وہ جمع ہونگے اپنے رب کے سامنے اس طرح پر کہ اللہ کے سوا نہ کوئی انکا حمایتی ہو گا اور نہ سفارش کرنے والا تاکہ وہ بچتے رہیں ');
INSERT INTO "al_hind" VALUES(6,52,'6:52','اور مت دور کر ان لوگوں کو جو پکارتے ہیں اپنے رب کو صبح اور شام اور چاہتے ہیں اسی کی رضا تجھ پر نہیں ہے ان کے حساب میں سے کچھ اور نہ تیرے حساب میں سے ان پر ہے کچھ کہ تو ان کو دور کرنے لگے پس ہو جاوے گا تو بے انصافوں میں ');
INSERT INTO "al_hind" VALUES(6,53,'6:53','اور اسی طرح ہم نے آزمایا ہے بعضے لوگوں کو بعضوں سے تاکہ کہیں کیا یہی لوگ ہیں جن پر اللہ نے فضل کیا ہم سب میں کیا نہیں ہے اللہ خوب جاننے والا شکر کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(6,54,'6:54','اور جب آویں تیرے پاس ہماری آیتوں کے ماننے والے تو کہہ دے تو سلام ہے تم پر لکھ لیا ہے تمہارے رب نے اپنے اوپر رحمت کو کہ جو کوئی کرے تم میں سے برائی ناواقفیت سے پھر اس کے بعد توبہ کر لے اور نیک ہو جائے تو بات یہ ہےکہ وہ ہے بخشنے والا مہربان');
INSERT INTO "al_hind" VALUES(6,55,'6:55','اور اسی طرح ہم تفصیل سے بیان کرتے ہیں آیتوں کو اور تاکہ کھل جاوےطریقہ گنہگاروں کا ');
INSERT INTO "al_hind" VALUES(6,56,'6:56','تو کہہ دے مجھ کو روکا گیا ہے اس سے کہ بندگی کروں انکی جن کو تم پکارتے ہو اللہ کے سوا تو کہہ میں نہیں چلتا تمہاری خوشی پر بیشک اب تو میں بہک جاؤں گا اور نہ رہوں گا ہدایت پانے والوں میں ');
INSERT INTO "al_hind" VALUES(6,57,'6:57','تو کہہ دے کہ مجھ کو شہادت پہنچی میرے رب کی اور تم نے اسکو جھٹلایا میرے پاس نہیں جس چیز کی تم جلدی کر رہے ہو حکم کسی کا نہیں سوا اللہ کے بیان کرتا ہے حق بات اور وہ سب سے اچھا فیصلہ کرنے والا ہے');
INSERT INTO "al_hind" VALUES(6,58,'6:58','تو کہہ اگر ہوتی میرے پاس وہ چیز جس کی تم جلدی کر رہے ہو تو طے ہو چکا ہوتا جھگڑا درمیان میرے اور درمیان تمہارے اور اللہ خوب جانتا ہے ظالموں کو');
INSERT INTO "al_hind" VALUES(6,59,'6:59','اور اُسی کے پاس کنجیاں ہیں غیب کی کہ انکو کوئی نہیں جانتا اسکے سوا اور وہ جانتا ہے جو کچھ جنگل اور دریا میں ہے اور نہیں جھڑتا کوئی پتا مگر وہ جانتا ہے اسکو اور نہیں گرتا کوئی دانہ زمین کے اندھیروں میں اور نہ کوئی ہری چیز اور نہ کوئی سوکھی چیز مگر وہ سب کتاب مبین میں ہے ');
INSERT INTO "al_hind" VALUES(6,60,'6:60','اور وہ ہی ہے کہ قبضہ میں لے لیتا ہے تم کو رات میں اور جانتا ہے جو کچھ کہ تم کر چکے ہو دن میں پھر تم کو اٹھا دیتا ہے اُس میں تاکہ پورا ہو وہ وعدہ جو مقرر ہو چکا ہے پھر اسی کی طرف تم لوٹائے جاؤ گے پھر خبر دے گا تم کو اس کی جو کچھ تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(6,61,'6:61','اور وہی غالب ہے اپنے بندوں پر اور بھیجتا ہے تم پر نگہبان یہاں تک کہ جب آپہنچے تم میں سے کسی کو موت تو قبضہ میں لے لیتے ہیں اسکو ہمارے بھیجے ہوئےفرشتے اور وہ کوتاہی نہیں کرتے ');
INSERT INTO "al_hind" VALUES(6,62,'6:62','پھر پہنچائے جاویں گے اللہ کی طرف جو مالک انکا ہے سچا سن رکھو حکم اسی کا ہے اور وہ بہت جلد حساب لینے والا ہے ');
INSERT INTO "al_hind" VALUES(6,63,'6:63','تو کہہ کون تم کو بچا لاتا ہے جنگل کے اندھیروں سے اور دریا کے اندھیروں سے اس وقت میں کہ پکارتے ہو تم اسکو گڑا گڑا کر اور چپکے سے کہ اگر ہم کو بچا لیوے اس بلا سے تو البتہ ہم ضرور احسان مانیں گے');
INSERT INTO "al_hind" VALUES(6,64,'6:64','تو کہہ دے اللہ تم کو بچاتا ہے اس سے اور ہر سختی سے پھر بھی تم شرک کرتے ہو ');
INSERT INTO "al_hind" VALUES(6,65,'6:65','تو کہہ اسی کو قدرت ہے اس پر کہ بھیجے تم پر عذاب اوپر سے یا تمہارے پاؤں کے نیچے سے یا بھڑا دے تم کو مختلف فرقے کر کے اور چکھاوے ایک کو لڑائی ایک کی دیکھ کس کس طرح ہم بیان کرتے ہیں آیتوں کو تاکہ وہ سمجھ جاویں ');
INSERT INTO "al_hind" VALUES(6,66,'6:66','اور اسکو جھوٹ بتلایا تیری قوم نے حالانکہ وہ حق ہے تو کہہ دے کہ میں نہیں تم پر داروغہ');
INSERT INTO "al_hind" VALUES(6,67,'6:67','ہر ایک خبر کا ایک وقت مقرر ہے اور قریب ہے کہ اس کو جان لو گے ');
INSERT INTO "al_hind" VALUES(6,68,'6:68','اور جب تو دیکھے ان لوگوں کہ کہ جھگڑتے ہیں ہماری آیتوں میں تو ان سے کنارہ کر یہاں تک کہ مشغول ہو جاویں کسی اور بات میں اور اگر بھلا دے تجھ کو شیطان تو مت بیٹھ یاد آجانے کے بعد ظالموں کےساتھ ');
INSERT INTO "al_hind" VALUES(6,69,'6:69','اور پرہیزگاروں پر نہیں ہے جھگڑنے والوں کے حساب میں سے کوئی چیز لیکن انکے ذمہ نصیحت کرنی ہے تاکہ وہ ڈریں ');
INSERT INTO "al_hind" VALUES(6,70,'6:70','اور چھوڑ دے انکو جنہوں نے بنا رکھا ہے اپنے دین کو کھیل اور تماشا اور دھوکا دیا انکو دنیا کی زندگی نے اور نصیحت کر انکو قرآن سے تاکہ گرفتار نہ ہو جاوے کوئی اپنے کئے میں کہ نہ ہو اس کے لئے اللہ کے سوا کوئی حمایتی اور نہ سفارش کرنے والا اور اگر بدلے میں دے سارے بدلے تو قبول نہ ہوں اس سے وہی لوگ ہیں جو گرفتار ہوئے اپنے کئے میں انکو پینا ہے گرم پانی اور عذاب ہے دردناک بدلے میں کفر کے ');
INSERT INTO "al_hind" VALUES(6,71,'6:71','تو کہہ دے کیا ہم پکاریں اللہ کےسوا ان کو جو نہ نفع پہنچا سکیں ہم کو اور نہ نقصان اور کیا پھر جاویں ہم الٹے پاؤں اسکے بعد کہ اللہ سیدھی راہ دکھا چکا ہم کو مثل اس شخص کے کہ رستہ بھلا دیا ہو اسکو جنوں نے جنگل میں جبکہ وہ حیران ہے اس کے رفیق بلاتے ہیں اسکو رستہ کی طرف کہ چلا آ ہمارے پاس تو کہہ دے کہ اللہ نے جو راہ بتلائی وہی سیدھی راہ ہے اور ہم کو حکم ہوا ہے کہ تابع رہیں پروردگار عالم کے');
INSERT INTO "al_hind" VALUES(6,72,'6:72','اور یہ کہ قائم رکھو نماز کو اور ڈرتے رہو اللہ سے اور وہی ہے جس کے سامنے تم سب اکھٹے ہو گے');
INSERT INTO "al_hind" VALUES(6,73,'6:73','اور وہی ہے جس نے پیدا کیا آسمانوں اور زمین کو ٹھیک طور پر اور جس دن کہے گا کہ ہو جا تو وہ ہو جائے گا اسی کی بات سچی ہے اور اسی کی سلطنت ہے جس دن پھونکا جائے گا صور جاننے والا چھپی اور کھلی باتوں کا اور وہی ہے حکمت والا جاننے والا ');
INSERT INTO "al_hind" VALUES(6,74,'6:74','اور یاد کرو جب کہا ابراہیم نے اپنے باپ آزر کو تو کیا مانتا ہے بتوں کو خدا میں دیکھتا ہوں کہ تو اور تیری قوم صریح گمراہ ہیں ');
INSERT INTO "al_hind" VALUES(6,75,'6:75','اور اسی طرح ہم دکھانے لگے ابراہیم کو عجائبات آسمانوں اور زمینوں کے اور تاکہ اس کو یقین آ جاوے ');
INSERT INTO "al_hind" VALUES(6,76,'6:76','پھر جب اندھیرا کر لیا اس پر رات نے دیکھا اس نے ایک ستارہ بولا یہ ہے رب میرا پھر جب وہ غائب ہو گیا تو بولا میں پسند نہیں کرتا غائب ہو جانے والوں کو ');
INSERT INTO "al_hind" VALUES(6,77,'6:77','پھر جب دیکھا چاند چمکتا ہوا بولا یہ ہے رب میرا پھر جب وہ غائب ہو گیا بولا اگر نہ ہدایت کرے گا مجھ کو رب میرا تو بیشک میں رہوں گا گمراہ لوگوں میں ');
INSERT INTO "al_hind" VALUES(6,78,'6:78','پھر جب دیکھا سورج جھلکھتا ہوا بولا یہ ہے رب میرا یہ سب سے بڑا ہے پھر جب وہ غائب ہو گیا بولا اے میری قوم میں بیزار ہوں ان سے جن کو تم شریک کرتے ہو ');
INSERT INTO "al_hind" VALUES(6,79,'6:79','میں نے متوجہ کر لیا اپنے منہ کو اسی کی طرف جس نے بنائے آسمان اور زمین سب سے یکسو ہو کر اور میں نہیں ہوں شرک کرنے والا ');
INSERT INTO "al_hind" VALUES(6,80,'6:80','اور اس سے جھگڑا کیا اسکی قوم نے بولا کیا تم مجھ سے جھگڑا کرتے ہو اللہ کے ایک ہونے میں اور وہ مجھ کو سمجھا چکا اور میں ڈرتا نہیں ہوں ان سے جنکو تم شریک کرتے ہو اس کا مگر یہ کہ میرا رب ہی کوئی تکلیف پہنچانی چاہے احاطہ کر لیا ہے میرے رب کے علم نے سب چیزوں کا کیا تم نہیں سوچتے ');
INSERT INTO "al_hind" VALUES(6,81,'6:81','اور میں کیونکر ڈروں تمہارے شریکوں سے اور تم نہیں ڈرتے اس بات سے کہ شریک کرتے ہو اللہ کا انکو جسکی نہیں اتاری اس نے تم پر کوئی دلیل اب دونوں فرقوں میں کون مستحق ہے دل جمعی کا بولو اگر تم سمجھ رکھتے ہو');
INSERT INTO "al_hind" VALUES(6,82,'6:82','جو لوگ یقین لے آئے اور نہیں ملا دیا انہوں نے اپنے یقین میں کوئی نقصان انہی کے واسطے ہے دل جمعی اور وہی ہیں سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(6,83,'6:83','اور یہ ہماری دلیل ہے کہ ہم نے دی تھی ابراہیم کو اس کی قوم کے مقابلہ میں درجے بلندکرتے ہیں ہم جس کے چاہیں تیرا رب حکمت والا ہے جاننے والا ');
INSERT INTO "al_hind" VALUES(6,84,'6:84','اور بخشا ہم نے ابراہیم کو اسحٰق اور یعقوب سب کو ہم نے ہدایت دی اور نوح کو ہدایت کی ہم نے ان سب سے پہلے اور اسکی اولاد میں سے داؤد اور سلیمان کو اور ایوب اور یوسف کو اور موسٰی اور ہارون کو اور ہم اسی طرح بدلہ دیا کرتےہیں نیک کام والوں کو');
INSERT INTO "al_hind" VALUES(6,85,'6:85','اور زکریا اور یحیٰی اور عیسٰی اور الیاس کو سب ہیں نیک بختوں میں');
INSERT INTO "al_hind" VALUES(6,86,'6:86','اور اسمٰعیل اور الیسع کو اور یونس کو اور لوط کو اور سب کو ہم نے بزرگی دی سارے جہان والوں پر ');
INSERT INTO "al_hind" VALUES(6,87,'6:87','اور ہدایت کی ہم نے بعضوں کو انکے باپ دادوں میں سے اور انکی اولاد میں سےاور بھائیوں میں سے اور انکو ہم نے پسند کیا اور سیدھی راہ چلایا');
INSERT INTO "al_hind" VALUES(6,88,'6:88','یہ اللہ کی ہدایت ہے اس پر چلاتا ہے جسکو چاہے اپنے بندوں میں سے اور اگر یہ لوگ شرک کرتے تو البتہ ضائع ہو جاتا جو کچھ انہوں نے کیا تھا ');
INSERT INTO "al_hind" VALUES(6,89,'6:89','یہ لوگ تھے جن کو دی ہم نے کتاب اور شریعت اور نبوت پھر اگر ان باتوں کو نہ مانیں مکہ والے تو ہم نے ان باتوں کے لئے مقرر کر دیے ہیں ایسے لوگ جو ان سے منکر نہیں ');
INSERT INTO "al_hind" VALUES(6,90,'6:90','یہ وہ لوگ تھے جن کو ہدایت کی اللہ نے سو تو چل ان کے طریقہ پر تو کہہ دے کہ میں نہیں مانگتا تم سے اس پر کچھ مزدوری یہ تو محض نصیحت ہے جہان کے لوگوں کو ');
INSERT INTO "al_hind" VALUES(6,91,'6:91','اور نہیں پہچانا انہوں نے اللہ کو پورا پہچاننا جب کہنے لگے کہ نہیں اتاری اللہ نے کسی انسان پر کوئی چیز پوچھ تو کس نے اتاری وہ کتاب جو موسٰی لے کر آیا تھا روشن تھی اور ہدایت تھی لوگوں کے واسطے جس کو تم نے ورق ورق کر کے لوگوں کو دکھلایا اور بہت سی باتوں کو تم نے چھپا رکھا اور تم کو سکھلا دیں جن کو نہ جانتے تھے تم اور نہ تمہارے باپ دادے تو کہہ دے کہ اللہ نےاتاری پھر چھوڑ دے ان کو اپنی خرافات میں کھیلتے رہیں ');
INSERT INTO "al_hind" VALUES(6,92,'6:92','اور یہ قرآن وہ کتاب ہے جو کہ ہم نے اتاری برکت والی تصدیق کرنے والی ان کی جو اس سے پہلی ہیں اور تاکہ تو ڈرا دے مکہ والوں کو اور اس کے آس پاس والوں کو اور جن کو یقین ہے آخرت کا وہ اس پر ایمان لاتے ہیں اور وہ ہیں اپنی نماز سے خبردار ');
INSERT INTO "al_hind" VALUES(6,93,'6:93','اور اس سے زیادہ ظالم کون جو باندھے اللہ پر بہتان یا کہے مجھ پر وحی اتری اور اس پر وحی نہیں اتری کچھ بھی اور جو کہے کہ میں بھی اتارتا ہوں مثل اسکے جو اللہ نے اتارا اور اگر تو دیکھے جس وقت کہ ظالم ہوں موت کی سختیوں میں اور فرشتے اپنے ہاتھ بڑھا رہے ہیں کہ نکالو اپنی جانیں آج تم کو بدلے میں ملے گا ذلت کا عذاب اس سبب سے کہ تم کہتےتھے اللہ پر جھوٹی باتیں اور اس کی آیتوں سے تکبر کرتے تھے ');
INSERT INTO "al_hind" VALUES(6,94,'6:94','اور البتہ تم ہمارے پاس آ گئے ایک ایک ہو کر جیسے ہم نے پیدا کیا تھا تم کو پہلی بار اور چھوڑ آئے تم جو کچھ اسباب ہم نے تم کو دیا تھا اپنی پیٹھ کے پیچھے اور ہم نہیں دیکھتے تمہارے ساتھ سفارش والوں کو جن کو تم بتلایا کرتے تھے کہ ان کا تم میں ساجھا ہے البتہ منقطع ہو گیا تمہاا علاقہ اور جاتے رہے جو دعوے کہ تم کیا کرتے تھے ');
INSERT INTO "al_hind" VALUES(6,95,'6:95','اللہ ہے کہ پھوڑ نکالتا ہے دانہ اور گٹھلی نکالتا ہے مردہ سے زندہ اور نکالنے والا ہے زندہ سے مردہ یہ ہے اللہ پھر تم کدھر بہکے جاتے ہو ');
INSERT INTO "al_hind" VALUES(6,96,'6:96','پھوڑ نکالنے والا صبح کی روشنی کا اور اسی نے رات بنائی آرام کو اور سورج اور چاند حساب کے لئے یہ اندازہ رکھا ہوا ہے زورآور خبردار کا ');
INSERT INTO "al_hind" VALUES(6,97,'6:97','اور اسی نے بنا دیے تمہارے واسطے ستارے کہ انکے وسیلہ سے راستے معلوم کرو اندھیروں میں جنگل اور دریا کے البتہ ہم نے کھول کر بیان کر دیے پتے ان لوگوں کے لئے جو جانتے ہیں');
INSERT INTO "al_hind" VALUES(6,98,'6:98','اور وہی ہے جس نے تم کو سب کو پیدا کیا ایک شخص سے پھر ایک تو تمہارا ٹھکانہ ہے اور ایک امانت رکھے جانے کی جگہ البتہ ہم نے کھول کر سنا دیے پتے اس کو جو سوچتے ہیں');
INSERT INTO "al_hind" VALUES(6,99,'6:99','اور اسی نے اتارا آسمان سے پانی پھر نکالی ہم نے اس سے اگنے والی ہر چیز پھر نکالی اس میں سے سبز کھیتی جس سے ہم نکالتے ہیں دانے ایک پر ایک چڑھا ہوا اور کھجور کے گابھے میں سے پھل کے گچھے جھکے ہوئے اور باغ انگور کے اور زیتون کے اور انار کے آپس میں ملتے جلتے اور جدا جدا بھی دیکھو ہر ایک درخت کے پھل کو جب وہ پھل لاتا ہے اور اسکے پکنے کو ان چیزوں میں نشانیاں ہیں واسطے ایمان والوں کے ');
INSERT INTO "al_hind" VALUES(6,100,'6:100','اور ٹھہراتے ہیں اللہ کے شریک جنوں کو حالانکہ اس نے انکو پیدا کیا ہے اور تراشتے ہیں اسکے واسطے بیٹے اور بیٹیاں جہالت سے وہ پاک ہے اور بہت دور ہے ان باتوں سے جو یہ لوگ بیان کرتے ہیں ');
INSERT INTO "al_hind" VALUES(6,101,'6:101','نئی طرح پر بنانے والا آسمان اور زمین کا کیونکر ہو سکتا ہے اسکے بیٹا حالانکہ اس کی کوئی عورت نہیں اور اس نے بنائی ہر چیز اور وہ ہر چیز سے واقف ہے ');
INSERT INTO "al_hind" VALUES(6,102,'6:102','یہ اللہ تمہارا رب ہے نہیں ہے کوئی معبود سوا اسکے پیدا کرنے والا ہر چیز کا سو تم اسی کی عبادت کرو اور وہ ہرچیز پر کارساز ہے ');
INSERT INTO "al_hind" VALUES(6,103,'6:103','نہیں پا سکتیں اس کو آنکھیں اور وہ پا سکتا ہے آنکھوں کو اور وہ نہایت لطیف اور خبردار ہے ');
INSERT INTO "al_hind" VALUES(6,104,'6:104','تمہارے پاس آ چکیں نشانیاں تمہارے رب کی طرف سے پھر جس نے دیکھ لیا سو اپنے واسطے اور جو اندھا رہا سو اپنے نقصان کو اور میں نہیں تم پر نگہبان ');
INSERT INTO "al_hind" VALUES(6,105,'6:105','اور یوں طرح طرح سے سمجھاتے ہیں ہم آیتیں اور تاکہ وہ کہیں کہ تو نے کسی سے پڑھا ہے اور تاکہ واضح کر دیں ہم اسکو واسطے سمجھ والوں کے ');
INSERT INTO "al_hind" VALUES(6,106,'6:106','تو چل اس پر جو حکم تجھ کو آوے تیرے رب کا کوئی معبود نہیں سوا اسکے اور منہ پھیر لے مشرکوں سے ');
INSERT INTO "al_hind" VALUES(6,107,'6:107','اور اگر اللہ چاہتا تو وہ لوگ شرک نہ کرتے اور ہم نے نہیں کیا تجھ کو ان پر نگہبان اور نہیں ہے تو ان پر داروغہ ');
INSERT INTO "al_hind" VALUES(6,108,'6:108','اور تم لوگو برا نہ کہو ان کو جنکی یہ پرستش کرتے ہیں اللہ کے سوا پس وہ برا کہنے لگیں گے اللہ کو بے ادبی سے بدون سمجھے اسی طرح ہم نے مزین کر دیا ہر ایک فرقہ کی نظر میں انکے اعمال کو پھر ان سب کو اپنے رب کے پاس پہنچنا ہے تب وہ جتلاوے گا انکو جو کچھ وہ کرتے تھے ');
INSERT INTO "al_hind" VALUES(6,109,'6:109','اور وہ قسمیں کھاتےہیں اللہ کی تاکید سے کہ اگر آوے انکے پاس کوئی نشانی تو ضرور اس پر ایمان لاویں گے تو کہہ دے کہ نشانیاں تو اللہ کے پاس ہیں اور تم کو اے مسلمانو کیا خبر ہے کہ وہ نشانیاں آئیں گی تو یہ لوگ ایمان لے ہی آویں گے ');
INSERT INTO "al_hind" VALUES(6,110,'6:110','اور ہم الٹ دیں گے ان کے دل اور ان کی آنکھیں جیسے کہ ایمان نہیں لائے نشانیوں پر پہلی بار اور ہم چھوڑے رکھیں گے انکو انکی سرکشی میں بہکتے ہوئے ');
INSERT INTO "al_hind" VALUES(6,111,'6:111','اور اگر ہم اتاریں ان پر فرشتے اور باتیں کریں ان سے مردے اور زندہ کر دیں ہم ہر چیز کو ان کےسامنے تو بھی یہ لوگ ہرگز ایمان لانےوالے نہیں مگر یہ کہ چاہے اللہ لیکن ان میں اکثر جاہل ہیں ');
INSERT INTO "al_hind" VALUES(6,112,'6:112','اور اسی طرح کر دیا ہم نے ہر نبی کے لئے دشمن شریر آدمیوں کو اور جنوں کو جو کہ سکھلاتے ہیں ایک دوسرے کو ملمع کی ہوئی باتیں فریب دینے کے لئے اور اگر تیرا رب چاہتا تو وہ لوگ یہ کام نہ کرتے سو تو چھوڑ دے وہ جانیں اور انکا جھوٹ ');
INSERT INTO "al_hind" VALUES(6,113,'6:113','اور اس لئے کہ مائل ہوں ان کی ملمع کی باتوں کی طرف ان لوگوں کے دل جنکو یقین نہیں آخرت کا اور وہ اسکو پسند بھی کر لیں اور کئے جاویں جو کچھ برے کام کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(6,114,'6:114','سو کیا اب اللہ کے سوا کسی اور کو منصف بناؤں حالانکہ اسی نے اتاری تم پر کتاب واضح اور جن لوگوں کو ہم نے کتاب دی ہے وہ جانتے ہیں کہ یہ نازل ہوئی ہے تیرے رب کی طرف سے ٹھیک سو تو مت ہو شک کرنے والوں میں سے');
INSERT INTO "al_hind" VALUES(6,115,'6:115','اور تیرے رب کی بات پوری سچی ہے اور انصاف کی کوئی بدلنے والا نہیں اس کی بات کو اور وہی ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(6,116,'6:116','اور اگر تو کہنا مانے گا ان لوگوں کا جو دنیا میں ہیں تو تجھ کو بہکا دیں گے اللہ کی راہ سے وہ سب تو چلتے ہیں اپنے خیال پر اور سب اٹکل ہی دوڑاتے ہیں ');
INSERT INTO "al_hind" VALUES(6,117,'6:117','تیرا رب خوب جاننے والا ہے اس کو جو بہکتا ہے اس کی راہ سے اور وہی خوب جاننے والا ہے انکو جو اس کی راہ پر ہیں');
INSERT INTO "al_hind" VALUES(6,118,'6:118','سو تم کھاؤ اس جانور میں سے جس پر نام لیا گیا ہے اللہ کا اگر تم کو اس کے حکموں پر ایمان ہے ');
INSERT INTO "al_hind" VALUES(6,119,'6:119','اور کیا سبب کہ تم نہیں کھاتے اس جانور میں سے کہ جس پر نام لیا گیا ہے اللہ کا اور وہ واضح کر چکا ہے جو کچھ کہ اس نے تم پر حرام کیا ہے مگر جب کہ مجبور ہو جاؤ اسکے کھانے پر اور بہت لوگ بہکاتے پھرتے ہیں اپنے خیالات پر بغیر تحقیق تیرا رب ہی خوب جانتا ہے حد سے بڑھنے والوں کو ');
INSERT INTO "al_hind" VALUES(6,120,'6:120','اور چھوڑ دو کھلا ہوا گناہ اور چھپا ہوا جو لوگ گناہ کرتے ہیں عنقریب سزا پاویں گے اپنے کئے کی ');
INSERT INTO "al_hind" VALUES(6,121,'6:121','اور اس میں سے نہ کھاؤ جس پر نام نہیں لیا گیا اللہ کا اور یہ کھانا گناہ ہے اور شیطان دل میں ڈالتے ہیں اپنے رفیقوں کے تاکہ وہ تم سے جھگڑا کریں اور اگر تم نے انکا کہا مانا تو تم بھی مشرک ہوئے ');
INSERT INTO "al_hind" VALUES(6,122,'6:122','بھلا ایک شخص جو کہ مردہ تھا پھر ہم نے اس کو زندہ کر دیا اور ہم نے اس کو دی روشنی کہ لئے پھرتا ہے اس کو لوگوں میں برابر ہو سکتا ہےاسکے کہ جس کا حال یہ ہے کہ پڑا ہے اندھیروں میں وہاں سے نکل نہیں سکتا اسی طرح مزین کر دیے کافروں کی نگاہ میں ان کے کام ');
INSERT INTO "al_hind" VALUES(6,123,'6:123','اور اسی طرح کئے ہیں ہم نے ہر بستی میں گنہگاروں کے سردار کہ حیلے کیا کریں وہاں اور جو حیلہ کرتے ہیں سو اپنی ہی جان پر اور نہیں سوچتے ');
INSERT INTO "al_hind" VALUES(6,124,'6:124','اور جب آتی ہے انکے پاس کوئی آیت تو کہتے ہیں کہ ہم ہرگز نہ مانیں گےجب تک کہ نہ دیا جاوے ہم کو جیسا کچھ کہ دیا گیا ہے اللہ کے رسولوں کو اللہ خوب جانتا ہے اس موقع کو کہ جہاں بھیجے اپنے پیغام عنقریب پہنچے گی گنہگاروں کو ذلت اللہ کے ہاں اور عذاب سخت اس وجہ سےکہ وہ مکر کرتے تھے ');
INSERT INTO "al_hind" VALUES(6,125,'6:125','سو جس کو اللہ چاہتا ہے کہ ہدایت کرے تو کھول دیتا ہے اسکے سینہ کو واسطے قبول کرنے اسلام کے اور جس کو چاہتا ہے کہ گمراہ کرے کر دیتا ہے اس کےسینہ کو تنگ بے نہایت تنگ گویا وہ زور سے چڑھتا ہے آسمان پر اسی طرح ڈالے گا اللہ عذاب کو ایمان نہ لانے والوں پر');
INSERT INTO "al_hind" VALUES(6,126,'6:126','اور یہ رستہ ہے تیرے رب کا سیدھا ہم نے واضح کر دیا نشانیوں کو غور کرنے والوں کے واسطے ');
INSERT INTO "al_hind" VALUES(6,127,'6:127','انہی کے لئےہے سلامتی کا گھر اپنے رب کے ہاں اور وہ انکا مددگار ہے بسبب انکے اعمال کے ');
INSERT INTO "al_hind" VALUES(6,128,'6:128','اور جس دن جمع کرے گا ان سب کو فرمائے گا اے جماعت جنات کی تم نے بہت کچھ تابع کر لئے اپنے آدمیوں میں سے اور کہیں گے ان کے دوستدار آدمیوں میں سے اے رب ہمارے کام نکالا ہم میں ایک نے دوسرے سے اور ہم پہنچے اپنے اس وعدہ کو جو تو نے ہمارے لئے مقرر کیا تھا فرماوے گا آگ ہے گھر تمہارا رہا کرو گے اسی میں مگر جب چاہے اللہ البتہ تیرا رب حکمت والا خبردار ہے ');
INSERT INTO "al_hind" VALUES(6,129,'6:129','اور اسی طرح ہم ساتھ ملاویں گے گنہگاروں کو ایک کو دوسرے سے ان کے اعمال کے سبب ');
INSERT INTO "al_hind" VALUES(6,130,'6:130','اے جماعت جنوں کی اور انسانوں کی کیا نہیں پہنچے تھے تمہارے پاس رسول تمہی میں سے کہ سناتے تھے تم کو میرے حکم اور ڈراتے تھے تم کو اس دن کے پیش آنے سے کہیں گے کہ ہم نے اقرار کر لیا اپنے گناہ کا اور ان کو دھوکا دیا دنیا کی زندگی نے اور قائل ہو گئے اپنے اوپر اس بات کے کہ وہ کافر تھے ');
INSERT INTO "al_hind" VALUES(6,131,'6:131','یہ اس واسطے کہ تیرا رب ہلاک کرنے والا نہیں بستیوں کو انکے ظلم پر اور وہاں کے لوگ بے خبر ہوں');
INSERT INTO "al_hind" VALUES(6,132,'6:132','اور ہر ایک کے لئے درجے ہیں انکے عمل کے اور تیرا رب بیخبر نہیں ان کے کام سے ');
INSERT INTO "al_hind" VALUES(6,133,'6:133','اور تیرا رب بے پروا ہے رحمت والا اگر چاہے تو تم کو لے جاوے اور تمہارے پیچھے قائم کر دے جس کو چاہے جیسا تم کو پیدا کیا اوروں کی اولاد سے');
INSERT INTO "al_hind" VALUES(6,134,'6:134','جس چیز کا تم سے وعدہ کیا جاتا ہے وہ ضرور آنے والا ہے اور تم عاجز نہیں کر سکتے ');
INSERT INTO "al_hind" VALUES(6,135,'6:135','تو کہہ دے اے لوگو تم کام کرتے رہو اپنی جگہ پر میں بھی کام کرتا ہوں سو عنقریب جان لو گے تم کہ کس کو ملتا ہے عاقبت کا گھر بالیقین بھلا نہ ہوگا ظالموں کا ');
INSERT INTO "al_hind" VALUES(6,136,'6:136','اور ٹھہراتے ہیں اللہ کا اس کی پیدا کی ہوئی کھیتی او رمواشی میں ایک حصہ پھر کہتے ہیں یہ حصہ اللہ کا ہے اپنے خیال میں اور یہ ہمارے شریکوں کا ہے سو جو حصہ ان کے شریکوں کا ہے وہ تو نہیں پہنچتا اللہ کی طرف اور جو اللہ کا ہے وہ پہنچ جاتا ہے ان کے شریکوں کی طرف کیا ہی برا انصاف کرتے ہیں ');
INSERT INTO "al_hind" VALUES(6,137,'6:137','اور اسی طرح مزین کر دیا بہت سے مشرکوں کی نگاہ میں ان کی اولاد کے قتل کو انکے شریکوں نے تاکہ ان کو ہلاک کریں اور رلا ملا دیں ان پر انکے دین کو اور اللہ چاہتا تو وہ یہ کام نہ کرتے سو چھوڑ دے وہ جانیں اور انکا جھوٹ ');
INSERT INTO "al_hind" VALUES(6,138,'6:138','اور کہتے ہیں کہ یہ مواشی اور کھیتی ممنوع ہے اس کو کوئی نہ کھاوے مگر جس کو ہم چاہیں انکے خیال کے موافق اور بعضے مواشی کی پیٹھ پر چڑھنا حرام کیا اور بعض مواشی کے ذبح کے وقت نام نہیں لیتے اللہ کا اللہ پر بہتان باندھ کر عنقریب وہ سزا دے گا انکو اس جھوٹ کی ');
INSERT INTO "al_hind" VALUES(6,139,'6:139','اور کہتے ہیں جو بچہ ان مویشی کے پیٹ میں ہے اس کو تو خاص ہمارے مرد ہی کھاویں اور وہ حرام ہے ہماری عورتوں پر اور جو بچہ مردہ ہو تو اس کے کھانے میں سب برابر ہیں وہ سزا دے گا ان کو ان تقریروں کی وہ حکمت والا جاننے والا ہے ');
INSERT INTO "al_hind" VALUES(6,140,'6:140','بیشک خراب ہوئے جنہوں نے قتل کیا اپنی اولاد کو نادانی سے بغیر سمجھے اور حرام ٹھہرا لیا اس رزق کو جو اللہ نے انکو دیا بہتان باندھ کر اللہ پر بیشک وہ گمراہ ہوئے اور نہ آئے سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(6,141,'6:141','اور اسی نے پیدا کئے باغ جو ٹٹیوں پر چڑھائے جاتے ہیں اور جو ٹٹیوں پر نہیں چڑھائے جاتے اور کھجور کے درخت اور کھیتی کہ مختلف ہیں انکے پھل اور پیدا کیا زیتون کو اور انار کو ایک دوسرے کے مشابہ اور جدا جدا بھی کھاؤ انکے پھل میں سے جس وقت پھل لاویں اور ادا کرو ان کا حق جس دن ان کو کاٹو اور بیجا خرچ نہ کرو اس کو خوش نہیں آتے بیجا خرچ کرنے والے ');
INSERT INTO "al_hind" VALUES(6,142,'6:142','اور پیدا کئے مواشی میں بوجھ اٹھانے والے اور زمین سے لگے ہوئے کھاؤ اللہ کے رزق میں سے اور مت چلو شیطان کے قدموں پر وہ تمہارا دشمن ہے صریح ');
INSERT INTO "al_hind" VALUES(6,143,'6:143','پیدا کئے آٹھ نر اور مادہ بھیڑ میں سے دو اور بکری میں سے دو پوچھ تو کہ دونوں نر اللہ نے حرام کئے ہیں یا دونوں مادہ یا وہ بچہ کہ اس پر مشتمل ہیں بچہ دان دونوں مادہ کے بتلاؤ مجھ کو سند اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(6,144,'6:144','اور پیدا کئے اونٹ میں سے دو اور گائے میں سے دو پوچھ تو دونوں نر حرام کئے ہیں یا دونوں مادہ یا وہ بچہ کہ اس پر مشتمل ہیں بچہ دان دونوں مادہ کے کیا تم حاضر تھے جس وقت تم کو اللہ نے یہ حکم دیا تھا پھر اس سے زیادہ ظالم کون جو بہتان باندھے اللہ پر جھوٹا تاکہ لوگوں کو گمراہ کرے بلاتحقیق بیشک اللہ ہدایت نہیں کرتا ظالم لوگوں کو ');
INSERT INTO "al_hind" VALUES(6,145,'6:145','تو کہہ دے کہ میں نہیں پاتا اس وحی میں کہ مجھ کو پہنچی ہے کسی چیز کو حرام کھانے والے پر جو اس کو کھاوے مگر یہ کہ وہ چیز مردار ہو یا بہتا ہوا خون یا گوشت سور کا کہ وہ ناپاک ہے یا ناجائز ذبیحہ جس پر نام پکارا جاوے اللہ کے سوا کسی اور کا پھر جو کوئی بھوک سے بے اختیار ہو جاوے نہ نافرمانی کرے اور نہ زیادتی تو تیرا رب بڑا معاف کرنے والا ہے نہایت مہربان ');
INSERT INTO "al_hind" VALUES(6,146,'6:146','اور یہود پر ہم نے حرام کیا تھا ہر ایک ناخن والا جانور اور گائے اور بکری میں سے حرام کی تھی ان کی چربی مگر جو لگی ہو پشت پر یا انتڑیوں پر یا جو چربی کہ ملی ہو ہڈی کے ساتھ یہ ہم نے انکو سزا دی تھی انکی شرارت پر اور ہم سچ کہتے ہیں ');
INSERT INTO "al_hind" VALUES(6,147,'6:147','پھر اگر تجھ کو جھٹلاویں تو کہہ دے کہ تمہارے رب کی رحمت میں بڑی وسعت ہے اور نہیں ٹلے گا اس کا عذاب گنہگار لوگوں سے ');
INSERT INTO "al_hind" VALUES(6,148,'6:148','اب کہیں گے مشرک اگر اللہ چاہتا تو شرک نہ کرتے ہم اور نہ ہمارےباپ دادے اور نہ ہم حرام کر لیتے کوئی چیز اسی طرح جھٹلایا کئے ان سےاگلے یہاں تک کہ انہوں نے چکھا ہمارا عذاب تو کہہ کچھ علم بھی ہے تمہارے پاس کہ اس کو ہمارے آگے ظاہر کرو تم تو نری اٹکل پر چلتے ہو اور صرف تخمینے ہی کرتے ہو');
INSERT INTO "al_hind" VALUES(6,149,'6:149','تو کہہ دے بس اللہ کا الزام پورا ہے سو اگر وہ چاہتا تو ہدایت کر دیتا تم سب کو ');
INSERT INTO "al_hind" VALUES(6,150,'6:150','تو کہہ کہ لاؤ اپنے گواہ جو گواہی دیں اس بات کی اللہ نے حرام کیا ہے ان چیزوں کو پھر اگر وہ ایسی گواہی دیں بھی تو تو نہ اعتبار کر ان کا اور نہ چل ان کی خوشی پر جنہوں نے جھٹلایا ہمارے حکموں کو اور جو یقین نہینں کرتے آخرت کا اور وہ اپنے رب کے برابر کرتے ہیں اوروں کو ');
INSERT INTO "al_hind" VALUES(6,151,'6:151','تو کہہ تم آؤ میں سنا دوں جو حرام کیا ہے تم پر تمہارے رب نے کہ شریک نہ کرو اسکے ساتھ کسی چیز کو اور ماں باپ کے ساتھ نیکی کرو اور مار نہ ڈالو اپنی اولاد کو مفلسی سے ہم رزق دیتے ہیں تم کو اور ان کو اور پاس نہ جاؤ بیحیائی کے کام کے جو ظاہر ہو اس میں سے اور جو پوشیدہ ہو اور مار نہ ڈالو اس جان کو جس کو حرام کیا ہے اللہ نے مگر حق پر تم کو یہ حکم کیا ہے تاکہ تم سمجھو ');
INSERT INTO "al_hind" VALUES(6,152,'6:152','اور پاس نہ جاؤ یتیم کے مال کے مگر اس طرح سے کہ بہتر ہو یہاں تک کہ پہنچ جاوے اپنی جوانی کو اور پورا کرو ماپ اور تول کو انصاف سے ہم کسی کے ذمہ وہی چیز لازم کرتے ہیں جسکی اسکو طاقت ہو اور جب بات کہو تو حق کی کہو اگرچہ وہ اپنا قریب ہی ہو اور اللہ کا عہد پورا کرو تم کو یہ حکم کر دیا ہے تاکہ تم نصیحت پکڑو');
INSERT INTO "al_hind" VALUES(6,153,'6:153','اور حکم کیا کہ یہ راہ ہے میری سیدھی سو اس پر چلو اور مت چلو اور رستوں پر کہ وہ تم کو جدا کر دیں گے اللہ کے راستہ سے یہ حکم کر دیا ہے تم کو تاکہ تم بچتے رہو');
INSERT INTO "al_hind" VALUES(6,154,'6:154','پھر دی ہم نے موسٰی کو کتاب واسطے پورا کرنے نعمت کے نیک کام والوں پر اور واسطے تفصیل ہر شے کے اور ہدایت اور رحمت کے لئے تاکہ وہ لوگ اپنے رب کے ملنے کا یقین کریں ');
INSERT INTO "al_hind" VALUES(6,155,'6:155','اور ایک یہ کتاب ہے کہ ہم نے اتاری برکت والی سو اس پر چلو اور ڈرتے رہو تاکہ تم پر رحمت ہو ');
INSERT INTO "al_hind" VALUES(6,156,'6:156','اس واسطے کہ کبھی تم کہنے لگو کہ کتاب جو اتری تھی سو ان ہی دو فرقوں پر جو ہم سے پہلے تھے اور ہم کو تو ان کے پڑھنے پڑھانے کی خبر ہی نہ تھی ');
INSERT INTO "al_hind" VALUES(6,157,'6:157','یا کہنے لگو کہ اگر ہم پر اترتی کتاب تو ہم تو راہ پر چلتے ان سے بہتر سو آ چکی تمہارے پاس حجت تمہارے رب کی طرف سے اور ہدایت اور رحمت اب اس سے زیادہ ظالم کون جو جھٹلاوے اللہ کی آیتوں کو اور ان سے کتراوے ہم سزا دیں گے ان کو جو ہماری آیتوں سے کتراتے ہیں برا عذاب بدلے میں اس کترانے کے ');
INSERT INTO "al_hind" VALUES(6,158,'6:158','کاہے کی راہ دیکھتے ہیں لوگ مگر یہی کہ ان پر آئیں فرشتے یا آئے تیرا رب یا آئے کوئی نشانی تیرے رب کی جس دن آئے گی ایک نشانی تیرے رب کی کام نہ آئے گا کسی کے اس کا ایمان لانا جو کہ پہلے سے ایمان نہ لایاتھا یا اپنے ایمان میں کچھ نیکی نہ کی تھی تو کہہ دے تم راہ دیکھو ہم بھی راہ دیکھتے ہیں ');
INSERT INTO "al_hind" VALUES(6,159,'6:159','جنہوں نے راہیں نکالیں اپنے دین میں اور ہو گئے بہت سے فرقے تجھ کو ان سےکچھ سروکار نہیں ان کا کام اللہ ہی کے حوالے ہے پھر وہی جتلائے گا ان کو جو کچھ وہ کرتے تھے ');
INSERT INTO "al_hind" VALUES(6,160,'6:160','جو کوئی لاتا ہے ایک نیکی تو اسکے لئے اس کا دس گنا ہے اور جو کوئی لاتا ہے ایک برائی سو سزا پائے گا اسی کے برابر اور ان پر ظلم نہ ہو گا ');
INSERT INTO "al_hind" VALUES(6,161,'6:161','تو کہہ دے مجھ کو سجھائی میرے رب نے راہ سیدھی دین صحیح ملت ابراہیم کی جو ایک ہی طرف کا تھا اور نہ تھا شرک والوں میں ');
INSERT INTO "al_hind" VALUES(6,162,'6:162','تو کہہ کہ میری نماز اور میری قربانی اور میرا جینا اور مرنا اللہ ہی کے لئے ہے جو پالنے والا سارے جہان کا ہے');
INSERT INTO "al_hind" VALUES(6,163,'6:163','کوئی نہیں اس کا شریک اور یہی مجھ کو حکم ہوا اور میں سب سے پہلے فرمانبردار ہوں ');
INSERT INTO "al_hind" VALUES(6,164,'6:164','تو کہہ کیا اب میں اللہ کے سوا تلاش کروں کوئی رب اور وہی ہے رب ہر چیز کا اور جو کوئی گناہ کرتا ہے سو وہ اس کے ذمہ پر ہے اور بوجھ نہ اٹھائے گا ایک شخص دوسرے کا پھر تمہارے رب کے پاس ہی تم سب کو لوٹ کر جانا ہے سو وہ جتلائے گا جس بات میں تم جھگڑتے تھے ');
INSERT INTO "al_hind" VALUES(6,165,'6:165','اور اسی نے تم کو نائب کیا ہے زمین میں اور بلند کر دیے تم میں درجے ایک کے ایک پر تاکہ آزمائے تم کو اپنی دیئے ہوئے حکموں میں تیرا رب جلد عذاب کرنے والا ہے اور وہی بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(7,1,'7:1','المص');
INSERT INTO "al_hind" VALUES(7,2,'7:2','یہ کتاب اتری ہے تجھ پر سو چاہئے کہ تیرا جی تنگ نہ ہو اس کے پہنچانے سے تاکہ تو ڈرائے اس سے اور نصیحت ہو ایمان والوں کو ');
INSERT INTO "al_hind" VALUES(7,3,'7:3','چلو اسی پر جو اترا تم پر تمہارے رب کی طرف سے اور نہ چلو اسکے سوا اور رفیقوں کے پیچھے تم بہت کم دھیان کرتے ہو ');
INSERT INTO "al_hind" VALUES(7,4,'7:4','اور کتنی بستیاں ہم نے ہلاک کر دیں کہ پہنچا ان پر ہمارا عذاب راتوں رات یا دوپہر کو سوتے ہوئے');
INSERT INTO "al_hind" VALUES(7,5,'7:5','پھر یہی تھی ان کی پکار جس وقت کہ پہنچا ان پر ہمارا عذاب کہ کہنے لگے بیشک ہمی تھے گنہگار ');
INSERT INTO "al_hind" VALUES(7,6,'7:6','سو ہم کو ضرور پوچھنا ہے ان سے جن کے پاس رسول بھیجے گئے تھے اور ہم کو ضرور پوچھنا ہے رسولوں سے ');
INSERT INTO "al_hind" VALUES(7,7,'7:7','پھر ہم انکو احوال سنائیں گے اپنے علم سے اور ہم کہیں غائب نہ تھے ');
INSERT INTO "al_hind" VALUES(7,8,'7:8','اور تول اس دن ٹھیک ہو گی پھر جس کی تولیں بھاری ہوئیں سو وہی ہیں نجات پانے والے');
INSERT INTO "al_hind" VALUES(7,9,'7:9','اور جس کی تولیں ہلکی ہوئیں سو وہی ہیں جنہوں نے اپنا نقصان کیا اس واسطے کہ ہماری آیتوں کا انکار کرتے تھے ');
INSERT INTO "al_hind" VALUES(7,10,'7:10','اور ہم نے تم کو جگہ دی زمین میں اور مقرر کر دیں اس میں تمہارے لئے روزیاں تم بہت کم شکر کرتے ہو ');
INSERT INTO "al_hind" VALUES(7,11,'7:11','اور ہم نے تم کو پیدا کیا پھر صورتیں بنائیں تمہاری پھر حکم کیا فرشتوں کو کہ سجدہ کرو آدم کو پس سجدہ کیا سب نے مگر ابلیس نہ تھا سجدہ والوں میں');
INSERT INTO "al_hind" VALUES(7,12,'7:12','کہا تجھ کو کیا مانع تھا کہ تو نے سجدہ نہ کیا جب میں نے حکم دیا بولا میں اس سے بہتر ہوں مجھ کو تو نے بنایا آگ سے اور اس کو بنایا مٹی سے');
INSERT INTO "al_hind" VALUES(7,13,'7:13','کہا تو اتر یہاں سے تو اس لائق نہیں کہ تکبر کرے یہاں پس باہر نکل تو ذلیل ہے ');
INSERT INTO "al_hind" VALUES(7,14,'7:14','بولا کہ مجھے مہلت دے اس دن تک کہ لوگ قبروں سے اٹھائے جائیں');
INSERT INTO "al_hind" VALUES(7,15,'7:15','فرمایا تجھ کو مہلت دی گئ ');
INSERT INTO "al_hind" VALUES(7,16,'7:16','بولا تو جیسا تو نے مجھے گمراہ کیا ہے میں بھی ضرور بیٹھوں گا انکی تاک میں تیری سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(7,17,'7:17','پھر ان پر آؤں گا ان کے آگے سے اور پیچھے سے اور دائیں سے اور بائیں سے اور نہ پائے گا تو اکثروں کو ان میں شکرگذار ');
INSERT INTO "al_hind" VALUES(7,18,'7:18','کہا نکل یہاں سے برے حال سے مردود ہو کر جو کوئی ان میں سے تیری راہ پر چلے گا تو میں ضرور بھر دوں گا دوزخ کو تم سب سے ');
INSERT INTO "al_hind" VALUES(7,19,'7:19','اور اے آدم رہ تو اور تیری عورت جنت میں پھر کھاؤ جہاں سے چاہو اور پاس نہ جاؤ اس درخت کے پھر تم ہو جاؤ گے گنہگار ');
INSERT INTO "al_hind" VALUES(7,20,'7:20','پھر بہکایا ان کو شیطان نے تاکہ کھول دے ان پر وہ چیز کہ انکی نظر سے پوشیدہ تھی انکی شرمگاہوں سے اور وہ بولا کہ تم کو نہیں روکا تمہارے رب نے اس درخت سے مگر اسی لئے کہ کبھی تم ہو جاؤ فرشتہ یا ہو جاؤ ہمیشہ رہنے والے');
INSERT INTO "al_hind" VALUES(7,21,'7:21','اور انکے آگے قسم کھائی کہ میں البتہ تمہارا دوست ہوں');
INSERT INTO "al_hind" VALUES(7,22,'7:22','پھر مائل کر لیا انکو فریب سے پھر جب چکھا ان دونوں نے درخت کو تو کھل گئیں ان پر شرمگاہیں انکی اور لگے جوڑنے اپنے اوپر بہشت کے پتے اور پکارا انکو انکے رب نے کیا میں نے منع نہ کیا تھا تم کو اس درخت سے اور نہ کہہ دیا تھا تم کو کہ شیطان تمہارا کھلا دشمن ہے');
INSERT INTO "al_hind" VALUES(7,23,'7:23','بولے وہ دونوں اے رب ہمارے ظلم کیا ہم نے اپنی جان پر اور اگر تو ہم کو نہ بخشے اور ہم پر رحم نہ کرے تو ہم ضرور ہو جائیں گے تباہ');
INSERT INTO "al_hind" VALUES(7,24,'7:24','فرمایا تم اترو تم ایک دوسرے کے دشمن ہو گے اور تمہارے واسطے زمین میں ٹھکانا اور نفع اٹھانا ہے ایک وقت تک');
INSERT INTO "al_hind" VALUES(7,25,'7:25','فرمایا اسی میں تم زندہ رہو گے اور اسی میں تم مرو گے اور اسی سے تم نکالے جاؤ گے');
INSERT INTO "al_hind" VALUES(7,26,'7:26','اےاولاد آدم کی ہم نے اتاری تم پر پوشاک جو ڈھانکے تمہاری شرمگاہیں اور اتارے آرائش کے کپڑے اور لباس پرہیزگاری کا وہ سب سے بہتر ہے یہ نشانیاں ہیں اللہ کی قدرت کی تاکہ وہ لوگ غور کریں ');
INSERT INTO "al_hind" VALUES(7,27,'7:27','اے اولاد آدم کی نہ بہکائے تم کو شیطان جیسا کہ اس نے نکال دیا تمہارے ماں باپ کو بہشت سے اتروائے ان سے ان کے کپڑے تاکہ دکھلائے انکو شرمگاہیں انکی وہ دیکھتا ہے تم کو اور اسکی قوم جہاں سے تم انکو نہیں دیکھتے ہم نےکر دیا شیطانوں کو رفیق ان لوگوں کا جو ایمان نہیں لاتے ');
INSERT INTO "al_hind" VALUES(7,28,'7:28','اور جب کرتے ہیں کوئی برا کام تو کہتے ہیں کہ ہم نے دیکھا اسی طرح کرتے اپنے باپ دادوں کو اور اللہ نے بھی ہم کو یہ حکم کیا ہے تو کہہ دے کہ اللہ حکم نہیں کرتا برے کام کا کیوں لگاتے ہو اللہ کے ذمہ وہ باتیں جو تم کو معلوم نہیں ');
INSERT INTO "al_hind" VALUES(7,29,'7:29','تو کہہ دے کہ میرے رب نے حکم کر دیا ہے انصاف کا اور سیدھے کرو اپنے منہ ہر نماز کے وقت اور پکارو اسکو خالص اس کے فرمانبردار ہو کر جیسا تم کو پہلے پیدا کیا دوسری بار بھی پیدا ہو گے ');
INSERT INTO "al_hind" VALUES(7,30,'7:30','ایک فرقہ کو ہدایت کی اور ایک فرقہ پر مقرر ہو چکی گمراہی انہوں نے بنایا شیطانوں کو رفیق اللہ کو چھوڑ کر اور سمجھتے ہیں کہ وہ ہدایت پر ہیں ');
INSERT INTO "al_hind" VALUES(7,31,'7:31','اے اولاد آدم کی لے لو اپنی آرائش ہر نماز کے وقت اور کھاؤ اور پیئو اور بیجا خرچ نہ کرو اس کو خوش نہیں آتے بیجا خرچ کرنے والے ');
INSERT INTO "al_hind" VALUES(7,32,'7:32','تو کہہ کس نے حرام کیا اللہ کی زینت کو جو اس نے پیدا کی اپنے بندوں کے واسطے اور ستھری چیزیں کھانے کی تو کہہ یہ نعمتیں اصل میں ایمان والوں کے واسطے ہیں دنیا کی زندگی میں خالص انہی کے واسطے ہیں قیامت کے دن اسی طرح مفصل بیان کرتے ہیں ہم آیتیں انکے لئے جو سمجھتے ہیں ');
INSERT INTO "al_hind" VALUES(7,33,'7:33','تو کہہ دے میرے رب نے حرام کیا ہے صرف بیحیائی کی باتوں کو جو ان میں کھلی ہوئی ہیں اور جو چھپی ہوئی ہیں اور گناہ کو اور ناحق زیادتی کو اور اس بات کو کہ شریک کرو اللہ کا ایسی چیز کو کہ جس کی اس نے سند نہیں اتاری اور اس بات کو کہ لگاؤ اللہ کے ذمہ وہ باتیں جو تم کو معلوم نہیں ');
INSERT INTO "al_hind" VALUES(7,34,'7:34','اور ہر فرقےکے واسطے ایک وعدہ ہے پھر جب آ پہنچے گا ان کا وعدہ نہ پیچھے سرک سکیں گے ایک گھڑی اور نہ آگے سرک سکیں گے ');
INSERT INTO "al_hind" VALUES(7,35,'7:35','اے اولاد آدم کی اگر آئیں تمہارے پاس رسول تم میں کے کہ سنائیں تم کو آیتیں میری تو جو کوئی ڈرے اور نیکی پکڑے تو نہ خوف ہو گا ان پر اور نہ وہ غمگین ہوں گے');
INSERT INTO "al_hind" VALUES(7,36,'7:36','اور جنہوں نے جھٹلایا ہماری آیتوں کو اور تکبر کیا ان سے وہی ہیں دوزخ میں رہنے والے وہ اسی میں ہمیشہ رہیں گے ');
INSERT INTO "al_hind" VALUES(7,37,'7:37','پھر اس سے زیادہ ظالم کون جو بہتان باندھے اللہ پر جھوٹا یا جھٹلائے اسکے حکموں کو وہ لوگ ہیں کہ ملے گا ان کو جو انکا حصہ لکھا ہوا ہے کتاب میں یہاں تک کہ جب پہنچیں ان کے پاس ہمارے بھیجے ہوئے انکی جان لینے کو تو کہیں کیا ہوئے وہ جن کو تم پکارا کرتے تھے سوا اللہ کے بولیں گے وہ ہم سےکھوئے گئے اور اقرار کر لیں گے اپنے اوپر کہ بیشک وہ کافر تھے ');
INSERT INTO "al_hind" VALUES(7,38,'7:38','فرمائے گا داخل ہو جاؤ ہمراہ اور امتوں کے جو تم سے پہلے ہو چکی ہیں جن اور آدمیوں میں سے دوزخ کے اندر جب داخل ہو گی ایک امت تو لعنت کرے گی دوسری امت کو یہاں تک کہ جب گر چکیں گے اس میں سارے تو کہیں گے انکے پچھلے پہلوں کو اے رب ہمارے ہم کو انہی نے گمراہ کیا سو تو انکو دے دونا عذاب آگ کا فرمائے گا کہ دونوں کو دو گنا ہے لیکن تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(7,39,'7:39','اور کہیں گے ان کے پہلے پچھلوں کو پس کچھ نہ ہوئی تم کو ہم پر بڑائی اب چکھو عذاب بسبب اپنی کمائی کے ');
INSERT INTO "al_hind" VALUES(7,40,'7:40','بیشک جنہوں نے جھٹلایا ہماری آیتوں کو اور ان کے مقابلہ میں تکبر کیا نہ کھولے جائیں گے انکے لئےدروازے آسمان کے اور نہ داخل ہوں گے جنت میں یہاں تک کہ گھس جائے اونٹ سوئی کے ناکے میں اور ہم یوں بدلا دیتے ہیں گنہگاروں کو');
INSERT INTO "al_hind" VALUES(7,41,'7:41','انکے واسطے دوزخ کا بچھونا ہے اور اوپر سے اوڑھنا اور ہم یوں بدلا دیتے ہیں ظالموں کو');
INSERT INTO "al_hind" VALUES(7,42,'7:42','اور جو ایمان لائے اور کیں نیکیاں ہم بوجھ نہیں رکھتے کسی پر مگر اس کی طاقت کے موافق وہی ہیں جنت میں رہنے والے وہ اسی میں ہمیشہ رہیں گے ');
INSERT INTO "al_hind" VALUES(7,43,'7:43','اور نکال لیں گے ہم جو کچھ ان کے دلوں میں خفگی تھی بہتی ہوں گی ان کے نیچے نہریں اور وہ کہیں گے شکر اللہ کا جس نے ہم کو یہاں تک پہنچا دیا اور ہم نہ تھے راہ پانے والے اگر نہ ہدایت کرتا ہم کو اللہ بیشک لائے تھے رسول ہمارے رب کے سچی بات اور آواز آئے گی کہ یہ جنت ہے وارث ہوئے تم اس کے بدلے میں اپنے اعمال کے ');
INSERT INTO "al_hind" VALUES(7,44,'7:44','اور پکاریں گے جنت والے دوزخ والوں کو کہ ہم نے پایا جو ہم سے وعدہ کیا تھا ہمارے رب نے سچا سو تم نے بھی پایا اپنے رب کے وعدہ کو سچا وہ کہیں گے کہ ہاں پھر پکارے گا ایک پکارنے والا انکے بیچ میں کہ لعنت ہے اللہ کی ان ظالموں پر');
INSERT INTO "al_hind" VALUES(7,45,'7:45','جو روکتے تھے اللہ کی راہ سے اور ڈھونڈتے تھے اس میں کجی اور وہ آخرت سے منکر تھے ');
INSERT INTO "al_hind" VALUES(7,46,'7:46','اور دونوں کے بیچ میں ہو گی ایک دیوار اور اعراف کے اوپر مرد ہوں گے کہ پہچان لیں گے ہر ایک کو اس کی نشانی سے اور وہ پکاریں گے جنت والوں کو کہ سلامتی ہے تم پر وہ ابھی جنت میں داخل نہیں ہوئے اور وہ امیدوار ہیں ');
INSERT INTO "al_hind" VALUES(7,47,'7:47','اور جب پھرے گی ان کی نگاہ دوزخ والوں کی طرف تو کہیں گے اے رب ہمارے مت کر ہم کو گنہگار لوگوں کے ساتھ ');
INSERT INTO "al_hind" VALUES(7,48,'7:48','اور پکاریں گے اعراف والے ان لوگوں کو کہ انکو پہچانتے ہیں انکی نشانی سے کہیں گے نہ کام آئی تمہارے جماعت تمہاری اور جو تم تکبر کیا کرتے تھے ');
INSERT INTO "al_hind" VALUES(7,49,'7:49','اب یہ وہی ہیں کہ تم قسم کھایا کرتے تھے کہ نہ پہنچے گی ان کو اللہ کی رحمت چلے جاؤ جنت میں نہ ڈر ہےتم پر اور نہ تم غمگین ہو گے ');
INSERT INTO "al_hind" VALUES(7,50,'7:50','اور پکاریں گے دوزخ والے جنت والوں کو کہ بہاؤ ہم پر تھوڑا سا پانی یا کچھ اس میں سے جو روزی تم کو دی اللہ نےکہیں گے اللہ نے ان دونوں کو روک دیا ہے کافروں سے');
INSERT INTO "al_hind" VALUES(7,51,'7:51','جنہوں نے ٹھہرایا اپنا دین تماشا اور کھیل اور دھوکے میں ڈالا ان کو دنیا کی زندگی نے سو آج ہم انکو بھلا دیں گے جیسا انہوں نے بھلا دیا اس دن کے ملنے کو اور جیسا کہ وہ ہماری آیتوں سےمنکر تھے ');
INSERT INTO "al_hind" VALUES(7,52,'7:52','اور ہم نے ان لوگوں کے پاس پہنچا دی ہے کتاب جس کو مفصل بیان کیا ہے ہم نے خبرداری سے راہ دکھانے والی اور رحمت ہے ایمان والوں کے لئے ');
INSERT INTO "al_hind" VALUES(7,53,'7:53','کیا اب اسی کے منتظر ہیں کہ اس کا مضمون ظاہر ہو جائے جس دن ظاہر ہو جائے گا اس کا مضمون کہنے لگیں گے وہ لوگ جو اس کو بھول رہے تھے پہلےسے بیشک لائے تھے ہمارے رب کے رسول سچی بات سو اب کوئی ہماری سفارش والے ہیں تو ہماری سفارش کریں یا ہم لوٹا دیے جائیں تو ہم عمل کریں خلاف اس کےجو ہم کر رہے تھے بیشک تباہ کیا انہوں نے اپنے آپ کو اور گم ہو جائے گا ان سے جو وہ افترا کیا کرتے تھے ');
INSERT INTO "al_hind" VALUES(7,54,'7:54','بیشک تمہارا رب اللہ ہے جس نے پیدا کئے آسمان اور زمین چھ دن میں پھر قرار پکڑا عرش پر اوڑھاتا ہے رات پر دن کہ وہ اسکے پیچھے لگا آتا ہے دوڑتا ہوا اور پیدا کئے سورج اور چاند اور تارے تابعدار اپنے حکم کے سن لو اسی کا کام ہے پیدا کرنا اور حکم فرمانا بڑی برکت والا ہے اللہ جو رب ہے سارے جہان کا ');
INSERT INTO "al_hind" VALUES(7,55,'7:55','پکارو اپنے رب کو گڑگڑا کر اور چپکے چپکے اس کو خوش نہیں آتے حد سے بڑھنے والے ');
INSERT INTO "al_hind" VALUES(7,56,'7:56','اور مت خرابی ڈالو زمین میں اس کی اصلاح کے بعد اور پکارو اس کو ڈر اور توقع سے بیشک اللہ کی رحمت نزدیک ہے نیک کام کرنے والوں سے');
INSERT INTO "al_hind" VALUES(7,57,'7:57','اور وہی ہے کہ چلاتا ہے ہوائیں خوشخبری لانے والی مینہ سے پہلے یہاں تک کہ جب وہ ہوائیں اٹھا لاتی ہیں بھاری بادلوں کو تو ہانک دیتے ہیں ہم اس بادل کو ایک شہر مردہ کی طرف پھر ہم اتارتے ہیں اس بادل سے پانی پھر اس سے نکالتے ہیں سب طرح کے پھل اسی طرح ہم نکالیں گے مردوں کو تاکہ تم غور کرو');
INSERT INTO "al_hind" VALUES(7,58,'7:58','اور جو شہر پاکیزہ ہے اس کا سبزہ نکلتا ہے اس کے رب کے حکم سے اور جو خراب ہے اس میں نہیں نکلتا مگر ناقص یوں پھیر پھیر کر بتلاتے ہیں ہم آیتیں حق ماننے والے لوگوں کو ');
INSERT INTO "al_hind" VALUES(7,59,'7:59','بیشک بھیجا ہم نے نوح کو اس کی قوم کی طرف پس اس نے کہا اے میری قوم بندگی کرو اللہ کی کوئی نہیں تمہارا معبود اس کے سوا میں خوف کرتا ہوں تم پر ایک بڑے دن کے عذاب سے');
INSERT INTO "al_hind" VALUES(7,60,'7:60','بولے سردار اس کی قوم کے ہم دیکھتے ہیں تجھ کو صریح بہکا ہوا ');
INSERT INTO "al_hind" VALUES(7,61,'7:61','بولا اے میری قوم میں ہرگز بہکا نہیں و لیکن میں بھیجا ہوا ہوں جہان کے پروردگار کا');
INSERT INTO "al_hind" VALUES(7,62,'7:62','پہنچاتا ہوں تم کو پیغام اپنے رب کے اور نصیحت کرتا ہوں تمکو اور جانتا ہوں اللہ کی طرف سے وہ باتیں جو تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(7,63,'7:63','کیا تم کو تعجب ہوا کہ آئی تمہارے پاس نصیحت تمہارے رب کی طرف سے ایک مرد کی زبانی جو تم ہی میں سے ہے تاکہ وہ تم کو ڈرائے اور تاکہ تم بچو اور تاکہ تم پر رحم ہو ');
INSERT INTO "al_hind" VALUES(7,64,'7:64','پھر انہوں نے اس کو جھٹلایا پھر ہم نے بچا لیا اسکو اور انکو کہ جو اسکے ساتھ تھے کشتی میں اور غرق کر دیا انکو جو جھٹلاتے تھے ہماری آیتوں کو بیشک وہ لوگ تھے اندھے ');
INSERT INTO "al_hind" VALUES(7,65,'7:65','اور قوم عاد کی طرف بھیجا انکے بھائی ہود کو بولا اے میری قوم بندگی کرو اللہ کی کوئی نہیں تمہارا معبود اسکے سوا سو کیا تم ڈرتے نہیں ');
INSERT INTO "al_hind" VALUES(7,66,'7:66','بولے سردار جو کافر تھے اس کی قوم میں ہم تو دیکھتے ہیں تجھ کو عقل نہیں اور ہم تو تجھ کو جھوٹا گمان کرتے ہیں ');
INSERT INTO "al_hind" VALUES(7,67,'7:67','بوالا اے میری قوم میں کچھ بےعقل نہیں لیکن میں بھیجا ہوا ہوں پروردگار عالم کا');
INSERT INTO "al_hind" VALUES(7,68,'7:68','پہنچاتا ہوں تم کو پیغام اپنے رب کے اور میں تمہارا خیرخواہ ہوں اور اطمینان کے لائق ');
INSERT INTO "al_hind" VALUES(7,69,'7:69','کیا تم کو تعجب ہوا کہ آئی تمہارے پاس نصیحت تمہارے رب کی طرف سے ایک مرد کی زبانی جو تم ہی میں سے ہے تاکہ تمکو ڈرائے اور یاد کرو جبکہ تم کو سردار کر دیا پیچھے قوم نوح کے اور زیادہ کر دیا تمہارے بدن کا پھیلاؤ سو یاد کرو اللہ کےاحسان تاکہ تمہارا بھلا ہو ');
INSERT INTO "al_hind" VALUES(7,70,'7:70','بولےکیا تو اس واسطے ہمارے پاس آیا کہ ہم بندگی کریں اللہ اکیلے کی اور چھوڑ دیں جن کو پوجتے رہے ہمارے باپ دادےپس تو لے آ ہمارے پاس جس چیز سے تو ہم کو ڈراتا ہے اگر تو سچا ہے ');
INSERT INTO "al_hind" VALUES(7,71,'7:71','کہا تم پر واقع ہو چکا ہے تمہارے رب کی طرف سے عذاب اور غصہ کیوں جھگڑتے ہو مجھ سے ان ناموں پر کہ رکھ لئے ہیں تم نے اور تمہارے باپ دادوں نے نہیں اتاری اللہ نے ان کی کوئی سند سو منتظر رہو میں بھی تمہارے ساتھ منتظر ہوں ');
INSERT INTO "al_hind" VALUES(7,72,'7:72','پھر ہم نے بچا لیا اس کو اور جو اس کےساتھ تھے اپنی رحمت سے اور جڑ کاٹی ان کی جو جھٹلاتے تھے ہماری آیتوں کو او رنہیں مانتے تھے ');
INSERT INTO "al_hind" VALUES(7,73,'7:73','اور ثمود کی طرف بھیجا انکے بھائی صالح کو بولا اے میری قوم بندگی کرو اللہ کی کوئی نہیں تمہارا معبود اسکے سوا تم کو پہنچ چکی ہے دلیل تمہارے رب کی طرف سے یہ اونٹنی اللہ کی ہے تمہارے لئے نشانی سو اس کو چھوڑ دو کہ کھائے اللہ کی زمین میں اور اس کو ہاتھ نہ لگاؤ بری طرح پھر تم کو پکڑے گا عذاب دردناک ');
INSERT INTO "al_hind" VALUES(7,74,'7:74','اور یاد کرو جبکہ تم کو سردار کر دیا عاد کے پیچھے اور ٹھکانہ دیا تم کو زمین میں کہ بناتے ہو نرم زمین میں محل اور تراشتے ہو پہاڑوں کے گھر سو یاد کرو احسان اللہ کے اور مت مچاتے پھرو زمین میں فساد ');
INSERT INTO "al_hind" VALUES(7,75,'7:75','کہنے لگے سردار جو متکبر تھے اس کی قوم میں غریب لوگوں کو کہ جو ان میں ایمان لا چکے تھے کیا تم کو یقین ہے کہ صالح کو بھیجا ہے اس کے رب نے بولے ہم کو تو جو وہ لے کر آیا اس پر یقین ہے');
INSERT INTO "al_hind" VALUES(7,76,'7:76','کہنے لگے وہ لوگ جو متکبر تھے جس پر تم کو یقین ہے ہم اسکو نہیں مانتے ');
INSERT INTO "al_hind" VALUES(7,77,'7:77','پھر انہوں نے کاٹ ڈالا اونٹنی کو اور پھر گئے اپنے رب کے حکم سے اور بولے اے صالح لے آ ہم پر جس سے تو ہم کو ڈراتا تھا اگر تو رسول ہے ');
INSERT INTO "al_hind" VALUES(7,78,'7:78','پس آ پکڑا انکو زلزلہ نے پھر صبح کو رہ گئے اپنے گھر میں اندھے پڑے ');
INSERT INTO "al_hind" VALUES(7,79,'7:79','پھر صالح الٹا پھرا ان سے اور بولا اے میری قوم میں پہنچا چکا تم کو پیغام اپنے رب کا اور خیرخواہی کی تمہاری لیکن تمکو محبت نہیں خیرخواہوں سے ');
INSERT INTO "al_hind" VALUES(7,80,'7:80','اور بھیجا لوط کو جب کہا اس نے اپنی قوم کو کیا تم کرتے ہو ایسی بے حیائی کہ تم سے پہلے نہیں کیا اس کو کسی نے جہان میں ');
INSERT INTO "al_hind" VALUES(7,81,'7:81','تم تو دوڑتے ہو مردوں پر شہوت کے مارے عورتوں کو چھوڑ کر بلکہ تم لوگ ہو حد سے گذرنے والے ');
INSERT INTO "al_hind" VALUES(7,82,'7:82','اور کچھ جواب نہ دیا اس کی قوم نے مگر یہی کہا کہ نکالو ان کو اپنے شہر سے یہ لوگ بہت ہی پاک رہنا چاہتے ہیں');
INSERT INTO "al_hind" VALUES(7,83,'7:83','پھر بچا دیا ہم نے اس کو اور اسکے گھر والوں کو مگر اس کی عورت کہ رہ گئ وہاں کے رہنے والوں میں ');
INSERT INTO "al_hind" VALUES(7,84,'7:84','اور برسایا ہم نے انکے اوپر مینہ یعنی پتھروں کا پھر دیکھ کیا ہوا انجام گنہگاروں کا ');
INSERT INTO "al_hind" VALUES(7,85,'7:85','اور مدین کی طرف بھیجا ان کےبھائی شعیب کو بولا اے میری قوم بندگی کرو اللہ کی کوئی نہیں تمہارا معبود اسکے سوا تمہارے پاس پہنچ چکی ہے دلیل تمہارے رب کی طرف سے سو پوری کرو ماپ اور تول اور مت گھٹا کر دو لوگوں کو ان کی چیزیں اور مت خرابی ڈالو زمین میں اس کی اصلاح کے بعد یہ بہتر ہےتمہارےلئے اگر تم ایمان والے ہو ');
INSERT INTO "al_hind" VALUES(7,86,'7:86','اور مت بیٹھو راستوں پر کہ ڈراؤ اور روکو اللہ کے راستہ سے اس کو جو کہ ایمان لائے اس پر اور ڈھونڈو اس میں عیب اور یاد کرو جبکہ تھے تم بہت تھوڑے پھر تم کو بڑھا دیا اور دیکھو کیا ہوا انجام فساد کرنے والوں کا ');
INSERT INTO "al_hind" VALUES(7,87,'7:87','اور اگر تم میں سے ایک فرقہ ایمان لایا اس پر جو میرے ہاتھ بھیجا گیا اور ایک فرقہ ایمان نہیں لایا تو صبر کرو جب تک اللہ فیصلہ کرے درمیان ہمارے اور وہ سب سے بہتر فیصلہ کرنے والا ہے ');
INSERT INTO "al_hind" VALUES(7,88,'7:88','بولے سردار جو متکبر تھے اس کی قوم میں ہم ضرور نکال دیں گے اے شعیب تجھ کو اور ان کو جو کہ ایمان لائے تیرےساتھ اپنے شہر سے یا یہ کہ تم لوٹ آؤ ہمارے دین میں بولا کیا ہم بیزار ہوں تو بھی ');
INSERT INTO "al_hind" VALUES(7,89,'7:89','بیشک ہم نے بہتان باندھا اللہ پر جھوٹا اگر لوٹ آئیں تمہارے دین میں بعد اس کی کہ نجات دے چکا ہم کو اللہ اس سے اور ہمارا کام نہیں کہ لوٹ آئیں اس میں مگر یہ کہ چاہے اللہ رب ہمارا گھیرے ہوئے ہے ہمارا پروردگار سب چیزوں کو اپنے علم میں اللہ ہی پر ہم نے بھروسا کیا اے ہمارے رب فیصلہ کر ہم میں اور ہماری قوم میں انصاف کے ساتھ اور تو سب سےبہتر فیصلہ کرنے والا ہے ');
INSERT INTO "al_hind" VALUES(7,90,'7:90','اور بولے سردار جو کافر تھے اس کی قوم میں اگر پیروی کرو گے تم شعیب کی تو تم بیشک خراب ہو گے ');
INSERT INTO "al_hind" VALUES(7,91,'7:91','پھر آ پکڑا ان کو زلزلہ نے پس صبح کو رہ گئے اپنے گھروں کے اندر اوندھے پڑے ');
INSERT INTO "al_hind" VALUES(7,92,'7:92','جنہوں نے جھٹلایا شعیب کو گویا کبھی بسے ہی نہ تھے وہاں جنہوں نے جھٹلایا شعیب کو وہی ہوئے خراب ');
INSERT INTO "al_hind" VALUES(7,93,'7:93','پھر الٹا پھر ان لوگوں سے اور بولا اے میری قوم میں پہنچا چکا تم کو پیغام اپنے رب کے اور خیر خواہی کر چکا تمہاری اب کیا افسوس کروں کافروں پر ');
INSERT INTO "al_hind" VALUES(7,94,'7:94','اور نہیں بھیجا ہم نے کسی بستی میں کوئی نبی کہ نہ پکڑا ہو ہم نے وہاں کےلوگوں کو سختی اور تکلیف میں تاکہ وہ گڑگڑائیں');
INSERT INTO "al_hind" VALUES(7,95,'7:95','پھر بدل دی ہم نے برائی کی جگہ بھلائی یہاں تک کہ وہ بڑھ گئے اور کہنے لگے کہ پہنچتی رہی ہے ہمارےباپ دادوں کو بھی تکلیف اورخوشی پھر پکڑا ہم نے ان کو ناگہاں اور ان کو خبر نہ تھی ');
INSERT INTO "al_hind" VALUES(7,96,'7:96','اور اگر بستیوں والے ایمان لاتے اور پرہیزگاری کرتے تو ہم کھول دیتے ان پر نعمتیں آسمان اور زمین سے لیکن جھٹلایا انہوں نے پس پکڑا ہم نے ان کو ان کے اعمال کےبدلے ');
INSERT INTO "al_hind" VALUES(7,97,'7:97','اب کیا بے ڈر ہیں بستیوں والے اس سے کہ آ پہنچے ان پر آفت ہماری راتوں رات جب سوتے ہوں');
INSERT INTO "al_hind" VALUES(7,98,'7:98','یا بے ڈر ہیں بستیوں والے اس بات سے کہ آ پہنچے ان پر عذاب ہمارا دن چڑھے جب کھیلتے ہوں ');
INSERT INTO "al_hind" VALUES(7,99,'7:99','یا بے ڈر ہو گئے اللہ کے داؤ سے سو بے ڈر نہیں ہوتے اللہ کے داؤ سےمگر خرابی میں پڑنے والے ');
INSERT INTO "al_hind" VALUES(7,100,'7:100','کیا نہیں ظاہر ہوا ان لوگوں پر جو وارث ہوئے زمین کے وہاں کے لوگوں کے ہلاک ہونے کے بعد کہ اگر ہم چاہیں تو ان کو پکڑ لیں ان کے گناہوں پر اور ہم نے مہر کر دی ہے انکے دلوں پر سو وہ نہیں سنتے');
INSERT INTO "al_hind" VALUES(7,101,'7:101','یہ بستیاں ہیں کہ سناتے ہیں ہم تجھ کو ان کے کچھ حالات اور بیشک ان کے پاس پہنچ چکے ان کے رسول نشانیاں لے کر پھر ہرگز نہ ہوا کہ ایمان لائیں اس بات پر جس کو پہلے جھٹلا چکے تھے یوں مہر کر دیتا ہے اللہ کافروں کے دلوں پر ');
INSERT INTO "al_hind" VALUES(7,102,'7:102','اور نہ پایا ان کے اکثر لوگوں میں ہم نے عہد کا نباہ اور اکثر ان میں پائےنافرمان ');
INSERT INTO "al_hind" VALUES(7,103,'7:103','پھر بھیجا ہم نے ان کے پیچھے موسٰی کو اپنی نشانیاں دے کر فرعون اور اسکےسرداروں کے پاس پس کفر کیا انہوں نےانکے مقابلہ میں سو دیکھ کیا انجام ہوا مفسدوں کا ');
INSERT INTO "al_hind" VALUES(7,104,'7:104','اور کہا موسٰی نے اے فرعون میں رسول ہوں پروردگار عالم کا');
INSERT INTO "al_hind" VALUES(7,105,'7:105','قائم ہوں اس بات پر کہ نہ کہوں اللہ کی طرف سے مگر جو سچ ہے لایا ہوں تمہارے پاس نشانی تمہارے رب کی سو بھیجدے میرےساتھ بنی اسرائیل کو ');
INSERT INTO "al_hind" VALUES(7,106,'7:106','بولا اگر تو آیا ہے کوئی نشانی لے کر تو لا اس کو اگر تو سچا ہے');
INSERT INTO "al_hind" VALUES(7,107,'7:107','تب ڈال دیا اس نے اپنا عصا تو اسی وقت ہو گیا اژدھا صریح ');
INSERT INTO "al_hind" VALUES(7,108,'7:108','اور نکالا اپنا ہاتھ تو اسی وقت وہ سفید نظر آنے لگا دیکھنے والوں کو ');
INSERT INTO "al_hind" VALUES(7,109,'7:109','بولے سردار فرعون کی قوم کے یہ تو کوئی بڑا واقف جادوگر ہے ');
INSERT INTO "al_hind" VALUES(7,110,'7:110','نکالنا چاہتا ہےتم کو تمہارےملک سے اب تمہاری کیا صلاح ہے ');
INSERT INTO "al_hind" VALUES(7,111,'7:111','بولےڈھیل دے اس کو اور اسکے بھائی کو اور بھیج پرگنوں میں جمع کرنے والوں کو');
INSERT INTO "al_hind" VALUES(7,112,'7:112','کہ جمع کر لائیں تیرے پاس جو ہو کامل جادوگر ');
INSERT INTO "al_hind" VALUES(7,113,'7:113','اور آئےجادوگر فرعون کے پاس بولے ہمارے لئے کچھ مزدوری ہے اگر ہم غالب ہوئے ');
INSERT INTO "al_hind" VALUES(7,114,'7:114','بولا ہاں اور بیشک تم مقرب ہو جاؤ گے ');
INSERT INTO "al_hind" VALUES(7,115,'7:115','بولے اے موسٰی یا تو تو ڈال اور یا ہم ڈالتے ہیں ');
INSERT INTO "al_hind" VALUES(7,116,'7:116','کہا ڈالو پھر اجب انہوں نےڈالا باندھ دیا لوگوں کی آنکھوں کو اور ان کو ڈرا دیا اور لائے بڑا جادو ');
INSERT INTO "al_hind" VALUES(7,117,'7:117','اور ہم نے حکم بھیجا موسٰی کو کہ ڈال دے اپنا عصا سو وہ جبہی لگا نگلنے جو سانگ انہوں نے بنایا تھا');
INSERT INTO "al_hind" VALUES(7,118,'7:118','پس ظاہر ہو گیا حق اور غلط ہو گیا جو کچھ انہوں نے کیا تھا');
INSERT INTO "al_hind" VALUES(7,119,'7:119','پس ہار گئے اس جگہ اور لوٹ گئے ذلیل ہو کر');
INSERT INTO "al_hind" VALUES(7,120,'7:120','اور گر پڑے جادوگر سجدہ میں ');
INSERT INTO "al_hind" VALUES(7,121,'7:121','بولے ہم ایمان لائے پروردگار عالم پر');
INSERT INTO "al_hind" VALUES(7,122,'7:122','جو رب ہے موسٰی اور ہارون کا ');
INSERT INTO "al_hind" VALUES(7,123,'7:123','بولا فرعون کیا تم ایمان لے آئے اس پر میری اجازت سے پہلے یہ تو مکر ہے جو بنایا تم سب نے اس شہر میں تاکہ نکال دو اس شہر سے اسکے رہنے والوں کو سو اب تم کو معلوم ہو جائے گا ');
INSERT INTO "al_hind" VALUES(7,124,'7:124','میں ضرور کاٹوں گا تمہارے ہاتھ اور دوسری طرف کے پاؤں پھر سولی پر چڑھاؤں گا تم سب کو');
INSERT INTO "al_hind" VALUES(7,125,'7:125','وہ بولے ہم کو تو اپنے رب کی طرف لوٹ کر جانا ہی ہے ');
INSERT INTO "al_hind" VALUES(7,126,'7:126','اور تجھ کو ہم سے یہی دشمنی ہے کہ مان لیا ہم نے اپنے رب کی نشانیوں کو جب وہ ہم تک پہنچیں اے ہمارے رب دہانے کھول دے ہم پرصبر کے اور ہم کو مار مسلمان ');
INSERT INTO "al_hind" VALUES(7,127,'7:127','اور بولے سردار قوم فرعون کےکیوں چھوڑتا ہے تو موسٰی کو اور اسکی قوم کو کہ اودھم مچائیں ملک میں اور موقوف کر دے تجھ کو اور تیرے بتوں کو بولا اب ہم مار ڈالیں گے انکے بیٹوں کو اور زندہ رکھیں گے انکی عورتوں کو اور ہم ان پر زور آور ہیں ');
INSERT INTO "al_hind" VALUES(7,128,'7:128','موسٰی نے کہا اپنی قوم سے مدد مانگو اللہ سے اور صبر کرو بیشک زمین ہے اللہ کی اس کا وارث کر دے جسکو وہ چاہے اپنے بندوں میں اور آخر میں بھلائی ہے ڈرنے والوں کے لئے ');
INSERT INTO "al_hind" VALUES(7,129,'7:129','وہ بولے ہم پر تکلیفیں رہیں تیرے آنے سے پہلے اور تیرے آنے کے بعد کہا نزدیک ہے کہ رب تمہارا ہلاک کر دے تمہارے دشمن کو اور خلیفہ کر دے تم کو ملک میں پھر دیکھے تم کیسے کام کرتے ہو ');
INSERT INTO "al_hind" VALUES(7,130,'7:130','اور ہم نے پکڑ لیا فرعون والوں کو قحطوں میں اور میوؤں کے نقصان میں تاکہ وہ نصیحت مانیں');
INSERT INTO "al_hind" VALUES(7,131,'7:131','پھر جب پہنچی انکو بھلائی کہنے لگے یہ ہے ہمارے لائق اور اگر پہنچی برائی تو نحوست بتلاتے موسٰی کی اور اسکے ساتھ والوں کی سن لو ان کی شومی تو اللہ کے پاس ہے پر اکثر لوگ نہیں جانتے ');
INSERT INTO "al_hind" VALUES(7,132,'7:132','اور کہنے لگے جو کچھ تو لائے گا ہمارے پاس نشانی کہ ہم پر اسکی وجہ سے جادو کرے سو ہم ہرگز تجھ پر ایمان نہ لائیں گے ');
INSERT INTO "al_hind" VALUES(7,133,'7:133','پھر ہم نے بھیجا ان پر طوفان اور ٹڈی اور چچڑی اور مینڈک اور خون بہت سی نشانیاں جدی جدی پھر بھی تکبر کرتے رہے اور تھے وہ لوگ گنہگار ');
INSERT INTO "al_hind" VALUES(7,134,'7:134','اور جب پڑتا ان پر کوئی عذاب تو کہتے اے موسٰی دعا کر ہمارے واسطے اپنے رب سے جیسا کہ اس نے بتلا رکھا ہے تجھ کو اگر تو نے دور کر دیا ہم سے یہ عذاب تو بیشک ہم ایمان لے آئیں گے تجھ پر اور جانے دیں گے تیرے ساتھ بنی اسرائیل کو');
INSERT INTO "al_hind" VALUES(7,135,'7:135','پھر جب ہم نے اٹھا لیا ان سےعذاب ایک مدت تک کہ ان کو اس مدت تک پہنچنا تھا اسی وقت عہد توڑ ڈالتے ');
INSERT INTO "al_hind" VALUES(7,136,'7:136','پھر ہم نے بدلہ لیا ان سے سو ڈبو دیا ہم نے انکو دریا میں اس وجہ سے کہ انہوں نے جھٹلایا ہماری آیتوں کو اور ان سے تغافل کرتے تھے ');
INSERT INTO "al_hind" VALUES(7,137,'7:137','اور وارث کر دیا ہم نے ان لوگوں کو جو کمزور سمجھے جاتے تھے اس زمین کے مشرق او رمغرب کا کہ جس میں برکت رکھی ہے ہم نے اور پورا ہو گیا نیکی کا وعدہ تیرے رب کا بنی اسرائیل پر بسبب انکے صبر کرنے کے اور خراب کر دیا ہم نے جو کچھ بنایا تھا فرعون اور اسکی قوم نے اور جو اونچا کر کے چھایا تھا ');
INSERT INTO "al_hind" VALUES(7,138,'7:138','اور پار اتار دیا ہم نے بنی اسرائیل کو دریا سےتو پہنچے ایک قوم پر جو پوجنے میں لگے رہتے تھے اپنے بتوں کے کہنے لگے اے موسٰی بنا دے ہماری عبادت کے لئے بھی ایک بت جیسے ان کے بت ہیں کہا تم لوگ تو جہل کرتے ہو ');
INSERT INTO "al_hind" VALUES(7,139,'7:139','یہ لوگ تباہ ہونے والی ہے وہ چیز جس میں وہ لگے ہوئے ہیں اور غلط ہے جو وہ کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(7,140,'7:140','کہا کیا اللہ کےسوا ڈھونڈوں تمہارے واسطے کوئی اور معبود حالانکہ اس نے تمکو بڑائی دی تمام جہان پر ');
INSERT INTO "al_hind" VALUES(7,141,'7:141','اور وہ وقت یاد کرو جب نجات دی ہم نے تم کو فرعون والوں سے کہ دیتے تھے تم کو برا عذاب کہ مار ڈالتے تھے تمہارے بیٹوں کو اور جیتا رکھتے تھے تمہاری عورتوں کو اور اس میں احسان ہے تمہارے رب کا بڑا ');
INSERT INTO "al_hind" VALUES(7,142,'7:142','اور وعدہ کیا ہم نےموسٰی سے تیس رات کا اور پورا کیا ان کو اور دس سے پس پوری ہو گئ مدت تیرے رب کی چالیس راتیں اور کہا موسٰی نے اپنے بھائی ہارون سےکہ میرا خلیفہ رہ میری قوم میں اور اصلاح کرتے رہنا اور مت چلنا مفسدوں کی راہ ');
INSERT INTO "al_hind" VALUES(7,143,'7:143','اور جب پہنچا موسٰی ہمارے وعدہ پر اور کلام کیا اس سے اسکے رب نے بولا اے میرے رب تو مجھ کو دکھا کہ میں تجھ کو دیکھوں فرمایا تو مجھ کو ہر گز نہ دیکھے گا لیکن تو دیکھتا رہ پہاڑ کی طرف اگر وہ اپنی جگہ ٹھہرا رہا تو تو مجھ کو دیکھ لے گا پھر جب تجلی کی اسکے رب نے پہاڑ کی طرف کر دیا اس کو ڈھا کر برابر اور گر پڑ اموسٰی بےہوش ہو کر پھر جب ہوش میں آیا بولا تیری ذات پاک ہے میں نے توبہ کی تیری طرف اور میں سب سے پہلے یقین لایا ');
INSERT INTO "al_hind" VALUES(7,144,'7:144','فرمایا اے موسٰی میں نے تجھ کو امتیاز دیا لوگوں سے اپنے پیغام بھیجنے کا اور اپنے کلام کرنے کا سو لے جو میں نے تجھ کو دیا اور شاکر رہ ');
INSERT INTO "al_hind" VALUES(7,145,'7:145','اور لکھ دی ہم نے اس کو تختیوں پر ہر قسم کی نصیحت اور تفصیل ہر چیز کی سو پکڑ لے انکو زور سے اور حکم کر اپنی قوم کو کہ پکڑے رہیں اس کی بہتر باتیں عنقریب میں تم کو دکھلاؤں گا گھر نافرمانوں کا ');
INSERT INTO "al_hind" VALUES(7,146,'7:146','میں پھیر دوں گا اپنی آیتوں سے انکو جو تکبر کرتے ہیں زمین میں ناحق اور اگر دیکھ لیں ساری نشانیاں ایمان نہ لائیں ان پر اور اگر دیکھیں رستہ ہدایت کا تو نہ ٹھہرائیں اس کو راہ اور اگر دیکھیں رستہ گمراہی کا تو اس کو ٹھہرا لیں راہ یہ اس لئے کہ انہوں نے جھوٹ جانا ہماری آیتوں کو اور رہے ان سے بے خبر ');
INSERT INTO "al_hind" VALUES(7,147,'7:147','اور جنہوں نے جھوٹ جانا ہماری آیتوں کو اور آخرت کی ملاقات کو برباد ہوئیں ان کی محنتیں وہی بدلا پائیں گے جو کچھ عمل کرتے تھے ');
INSERT INTO "al_hind" VALUES(7,148,'7:148','اور بنا لیا موسٰی کی قوم نے اس کے پیچھے اپنے زیور سے بچھڑا ایک بدن کہ اسمیں گائے کی آواز تھی کیا انہوں نے یہ نہ دیکھا کہ وہ ان سے بات بھی نہیں کرتاا ور نہیں بتلاتا رستہ معبود بنا لیا اس کو اور وہ تھے ظالم ');
INSERT INTO "al_hind" VALUES(7,149,'7:149','اور جب پچتائے اور سمجھے کہ ہم بیشک گمراہ ہو گئے تو کہنے لگے اگر نہ رحم کرے ہم پر ہمارا رب اور نہ بخشے ہم کو تو بیشک ہم تباہ ہوں گے ');
INSERT INTO "al_hind" VALUES(7,150,'7:150','اور جب لوٹ آیا موسٰی اپنی قوم میں غصہ میں بھرا ہوا افسوسناک بولا کیا بری نیابت کی تم نے میری میرےبعد کیوں جلدی کی تم نےاپنے رب کےحکم سے اور ڈالدیں وہ تختیاں اور پکڑا سر اپنے بھائی کا لگا کھینچنے اس کو اپنی طرف وہ بولا کہ اے میری ماں کے جنے لوگوں نے مجھ کو کمزور سمجھا اور قریب تھے کہ مجھ کو مار ڈالیں سو مت ہنسا مجھ پر دشمنوں کو اور نہ ملا مجھ کو گنہگار لوگوں میں ');
INSERT INTO "al_hind" VALUES(7,151,'7:151','بولا اے میرے رب معاف کر مجھ کو اور میرے بھائی کو اور داخل کر ہم کو اپنی رحمت میں اور تو سب سے زیادہ رحم کرنے والا ہے ');
INSERT INTO "al_hind" VALUES(7,152,'7:152','البتہ جنہوں نے بچھڑے کو معبود بنا لیا ان کو پہنچے گا غصہ ان کے رب کا اور ذلت دنیا کی زندگی میں اور یہی سزا دیتے ہیں ہم بہتان باندھنے والوں کو ');
INSERT INTO "al_hind" VALUES(7,153,'7:153','اور جنہوں نے کئے برے کام پھر توبہ کی اس کے بعد اور ایمان لائے تو بیشک تیرا رب توبہ کے پیچھے البتہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(7,154,'7:154','اور جب تھم گیا موسٰی کا غصہ تو اس نے اٹھا لیا تختیوں کو اور جو ان میں لکھا ہوا تھا اس میں ہدایت اور رحمت تھی ان کے واسطے جو اپنے رب سے ڈرتے ہیں');
INSERT INTO "al_hind" VALUES(7,155,'7:155','اور چن لئے موسٰی نے اپنی قوم میں سے ستر مرد ہمارے وعدہ کے وقت پر لانے کو پھر جب ان کو زلزلہ نے پکڑا تو بولا اے رب میرے اگر تو چاہتا تو پہلے ہی ہلاک کر دیتا انکو اور مجھ کو کیا ہم کو ہلاک کرتا ہے اس کام پر جو کیا ہماری قوم کے احمقوں نے یہ سب تیری آزمائش ہے بچلا دے اس میں جسکو تو چاہے اور سیدھا رکھے جس کو چاہے تو ہی ہے ہمارا تھامنے والا سو بخش دے ہم کو اور رحمت کر ہم پر اور تو سب سے بہتر بخشنے والا ہے ');
INSERT INTO "al_hind" VALUES(7,156,'7:156','اور لکھ دے ہمارےلئے اس دنیا میں بھلائی اور آخرت میں ہم نے رجوع کیا تیری طرف فرمایا میرا عذاب ڈالتا ہوں میں اسکو جس پر چاہوں اور میری رحمت شامل ہے ہر چیز کو سو اس کو لکھ دوں گا ان کے لئے جو ڈر رکھتے ہیں اور دیتے ہیں زکوٰۃ اور جو ہماری باتوں پر یقین رکھتے ہیں ');
INSERT INTO "al_hind" VALUES(7,157,'7:157','وہ لوگ جو پیروی کرتے ہیں اس رسول کی جو نبی امی ہے کہ جس کو پاتے ہیں لکھا ہوا اپنے پاس توریت اور انجیل میں وہ حکم کرتا ہے ان کو نیک کام کا اور منع کرتا ہے برے کام سے اور حلال کرتا ہے انکے لئے سب پاک چیزیں اور حرام کرتا ہے ان پر ناپاک چیزیں اور اتارتا ہے ان پر سے ان کے بوجھ اور وہ قیدیں جو ان پر تھیں سو جو لوگ اس پر ایمان لائے اور اسکی رفاقت کی اور اس کی مدد کی اور تابع ہوئے اس نور کے جو اسکے ساتھ اترا ہے وہی لوگ پہنچے اپنی مراد کو');
INSERT INTO "al_hind" VALUES(7,158,'7:158','تو کہہ اے لوگو میں رسول ہوں اللہ کا تم سب کی طرف جس کی حکومت ہے آسمانوں اور زمین میں کسی کی بندگی نہیں اسکےسوا وہی جلاتا ہے اور مارتا ہے سو ایمان لاؤ اللہ پر اور اس کے بھیجے ہوئے نبی امی پر جو کہ یقین رکھتا ہے اللہ پر اور اسکے سب کلاموں پر اور اسکی پیروی کرو تاکہ تم راہ پاؤ ');
INSERT INTO "al_hind" VALUES(7,159,'7:159','اور موسٰی کی قوم میں ایک گروہ ہے جو راہ بتلاتے ہیں حق کی اور اسی کے موافق انصاف کرتے ہیں ');
INSERT INTO "al_hind" VALUES(7,160,'7:160','اور جدا جدا کر دیئے ہم نے ان کو بارہ دادوں کی اولاد بڑی بڑی جماعتیں اور حکم بھیجا ہم نے موسٰی کو جب پانی مانگا اس سے اس کی قوم نے کہ مار اپنی لاٹھی اس پتھر پر تو پھوٹ نکلے اس سے بارہ چشمے پہچان لیا ہر قبیلہ نے اپنا گھاٹ اور سایہ کیا ہم نے ان پر ابر کا اور اتارا ہم نے ان پر من اور سلویٰ کھاؤ ستھری چیزیں جو ہم نے روزی دی تم کو اور انہوں نے ہمارا کچھ نہ بگاڑا لیکن اپنا ہی نقصان کرتے رہے');
INSERT INTO "al_hind" VALUES(7,161,'7:161','اور جب حکم ہوا ان کو کہ بسو اس شہر میں اور کھاؤ اس میں جہاں سے چاہو اور کہو ہم کو بخش دے اور داخل ہو دروازہ میں سجدہ کرتے ہوئے تو بخشدیں گے ہم تمہاری خطائیں البتہ زیادہ دیں گے ہم نیکی کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(7,162,'7:162','سو بدل ڈالا ظالموں نے ان میں سے دوسرا لفظ اسکے سوا جو ان سے کہہ دیا گیا تھا پھر بھیجا ہم نے ان پر عذاب آسمان سے بسبب ان کی شرارت کے ');
INSERT INTO "al_hind" VALUES(7,163,'7:163','اور پوچھ ان سےحال اس بستی کا جو تھی دریا کے کنارے جب حد سے بڑھنے لگے ہفتہ کے حکم میں جب آنے لگیں انکے پاس مچھلیاں ہفتہ کے دن پانی کے اوپر اور جس دن ہفتہ نہ ہو تو نہ آتی تھیں اس طرح ہم نے ان کو آزمایا اس لئے کہ وہ نافرمان تھے ');
INSERT INTO "al_hind" VALUES(7,164,'7:164','اور جب بولا ان میں سے ایک فرقہ کیوں نصیحت کرتے ہو ان لوگوں کو جنکو اللہ چاہتا ہےکہ ہلاک کرے یا ان کو عذاب دے سخت وہ بولے الزام اتارنے کی غرض سے تمہارے رب کے آگے اور اسلئے کہ شاید وہ ڈریں ');
INSERT INTO "al_hind" VALUES(7,165,'7:165','پھر جب وہ بھول گئے اس کو جو انکو سمجھایا تھا تو نجات دی ہم نے ان کو جو منع کرتے تھے برے کام سے اور پکڑا گنہگاروں کو برے عذاب میں بسبب ان کی نافرمانی کے ');
INSERT INTO "al_hind" VALUES(7,166,'7:166','پھر جب بڑھنےلگےاس کام میں جس سے وہ روکے گئے تھے تو ہم نے حکم کیا کہ ہو جاؤ بندر ذلیل ');
INSERT INTO "al_hind" VALUES(7,167,'7:167','اور اس وقت کو یاد کرو جب خبر کر دی تھی تیرے رب نے کہ ضرور بھیجتا رہے گا یہود پر قیامت کے دن تک ایسے شخص کو کہ دیا کرے ان کو برا عذاب بیشک تیرا رب جلد عذاب کرنے والا ہے اور وہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(7,168,'7:168','اور متفرق کر دیا ہم نے ان کو ملک میں فرقے فرقے بعضے ان میں نیک اور بعضے اور طرح کے اور ہم نے انکی آزمائش کی خوبیوں میں او ربرائیوں میں تاکہ وہ پھر آئیں ');
INSERT INTO "al_hind" VALUES(7,169,'7:169','پھر ان کے پیچھے آئے ناخلف جو وارث بنے کتاب کے لے لیتے ہیں اسباب اس ادنیٰ زندگانی کا اور کہتے ہیں کہ ہم کو معاف ہو جائے گا اور اگر ایسا ہی اسباب انکے سامنے پھر آئے تو اسکو لے لیویں کیا ان سے کتاب میں عہد نہیں لیا گیا کہ نہ بولیں اللہ پر سوا سچ کے اور انہوں نے پڑھا ہے جو کچھ اسمیں لکھا ہے اور آخرت کا گھر بہتر ہے ڈرنے والوں کے لئے کیا تم سمجھتے نہیں ');
INSERT INTO "al_hind" VALUES(7,170,'7:170','اور جو لوگ خوب پکڑ رہے ہیں کتاب کو اور قائم رکھتے ہیں نماز کو بیشک ہم ضائع نہ کریں گے ثواب نیکی والوں کا ');
INSERT INTO "al_hind" VALUES(7,171,'7:171','اور جس وقت اٹھایا ہم نے پہاڑ ان کے اوپر مثل سائبان کے اور ڈرے کہ وہ ان پر گرے گا ہم نے کہا پکڑو جو ہم نے تم کو دیا ہے زور سے اور یاد رکھو جو اس میں ہے تاکہ تم بچتے رہو ');
INSERT INTO "al_hind" VALUES(7,172,'7:172','اور جب نکالا تیرے رب نے بنی آدم کی پیٹھوں سے ان کی اولاد کو اور اقرار کرایا ان سے ان کی جانوں پر کیا میں نہیں ہوں تمہارا رب بولے ہاں ہے ہم اقرار کرتے ہیں کبھی کہنے لگو قیامت کے دن ہم کو تو اس کی خبر نہ تھی');
INSERT INTO "al_hind" VALUES(7,173,'7:173','یا کہنے لگو کہ شرک تو نکالا تھا ہمارے باپ دادوں نے ہم سے پہلے اور ہم ہوئے انکی اولاد ان کے پیچھے تو کیا تو ہم کو ہلاک کرتا ہے اس کام پر جو کیا گمراہوں نے ');
INSERT INTO "al_hind" VALUES(7,174,'7:174','اور یوں ہم کھول کر بیان کرتے ہیں باتیں تاکہ وہ پھر آئیں ');
INSERT INTO "al_hind" VALUES(7,175,'7:175','اور سنا دے ان کو حال اس شخص کا جسکو ہم نے دی تھیں اپنی آیتیں پھر وہ انکو چھوڑ نکلا پھر اس کے پیچھےلگا شیطان تو وہ ہو گیا گمراہوں میں');
INSERT INTO "al_hind" VALUES(7,176,'7:176','اور ہم چاہتے تو بلند کرتے اس کا رتبہ ان آیتوں کی بدولت لیکن وہ تو ہو رہا زمین کا اور پیچھے ہو لیا اپنی خواہش کے تو اس کا حال ایسا جیسے کتا اس پر تو بوجھ لادے تو ہانپے اور چھوڑ دے تو ہانپے یہ مثال ہے ان لوگوں کی جنہوں نے جھٹلایا ہماری آیتوں کو سو بیان کر یہ احوال تاکہ وہ دھیان کریں ');
INSERT INTO "al_hind" VALUES(7,177,'7:177','بری مثال ہے ان لوگوں کی کہ جھٹلایا انہوں نے ہماری آیتوں کو اور وہ اپنا ہی نقصان کرتے رہے ');
INSERT INTO "al_hind" VALUES(7,178,'7:178','جس کو اللہ رستہ دے وہی رستہ پاوے اور جس کو وہ بچلا دے سو وہی ہیں ٹوٹے میں ');
INSERT INTO "al_hind" VALUES(7,179,'7:179','اور ہم نے پیدا کئے دوزخ کے واسطے بہت سے جن اور آدمی انکے دل ہیں کہ ان سے سمجھتے نہیں اور آنکھیں ہیں کہ ان سے دیکھتے نہیں اور کان ہیں کہ ان سےسنتے نہیں وہ ایسے ہیں جیسے چوپائے بلکہ ان سے بھی زیادہ بیراہ وہی لوگ ہیں غافل ');
INSERT INTO "al_hind" VALUES(7,180,'7:180','اور اللہ کےلئے ہیں سب نام اچھے سو اسکو پکارو وہی نام کہہ کر اور چھوڑ دو انکو جو کج راہ چلتے ہیں اس کے ناموں میں وہ بدلہ پا رہیں گے اپنے کئے کا ');
INSERT INTO "al_hind" VALUES(7,181,'7:181','اور ان لوگوں میں کہ جنکو ہم نے پیدا کیا ہے ایک جماعت ہے کہ راہ بتلاتے ہیں سچی اور اسی کے موافق انصاف کرتےہیں ');
INSERT INTO "al_hind" VALUES(7,182,'7:182','اور جنہوں نے جھٹلایا ہماری آیتوں کو ہم ان کو آہستہ آہستہ پکڑیں گے ایسی جگہ سےجہاں سے انکو خبر بھی نہ ہو گی');
INSERT INTO "al_hind" VALUES(7,183,'7:183','اور میں انکو ڈھیل دوں گا بیشک میرا داؤ پکا ہے ');
INSERT INTO "al_hind" VALUES(7,184,'7:184','کیا انہوں نے دھیان نہیں کیا کہ ان کے رفیق کو کچھ بھی جنون نہیں وہ تو ڈرانے والا ہے صاف');
INSERT INTO "al_hind" VALUES(7,185,'7:185','کیا انہوں نے نظر نہیں کی سلطنت میں آسمان اور زمین کی اور جو کچھ پیدا کیا ہے اللہ نے ہر چیز سے اور اس میں کہ شاید قریب آ گیا ہو ان کا وعدہ سو اس کے پیچھے کس بات پر ایمان لائیں گے ');
INSERT INTO "al_hind" VALUES(7,186,'7:186','جس کو اللہ بچلائے اس کو کوئی نہیں راہ دکھلانے والا اور اللہ چھوڑے رکھتا ہے انکو ان کی شرارت میں سرگرداں ');
INSERT INTO "al_hind" VALUES(7,187,'7:187','تجھ سے پوچھتے ہیں قیامت کو کہ کب ہے اسکے قائم ہونے کا وقت تو کہہ اسکی خبر تو میرے رب ہی کے پاس ہے وہی کھول دکھائے گا اس کو اسکے وقت پر وہ بھاری بات ہے آسمانوں اور زمین میں جب تم پر آئے گی تو بیخبر آئے گی تجھ سے پوچھنے لگتے ہیں کہ گویا تو اسکی تلاش میں لگا ہوا ہے تو کہہ دے اس کی خبر ہے خاص اللہ کے پاس لیکن اکثر لوگ نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(7,188,'7:188','تو کہہ دے کہ میں مالک نہیں اپنی جان کے بھلے کا اور نہ برے کا مگر جو اللہ چاہے اور اگر میں جان لیا کرتا غیب کی بات تو بہت کچھ بھلائیاں حاصل کر لیتا اور مجھ کو برائی کبھی نہ پہنچتی میں تو بس ڈر اور خوشخبری سنانے والا ہوں ایماندار لوگوں کو');
INSERT INTO "al_hind" VALUES(7,189,'7:189','وہی ہے جس نے تم کو پیدا کیا ایک جان سے اور اسی سے بنایا اس کا جوڑا تاکہ اسکے پاس آرام پکڑے پھر جب مرد نے عورت کو ڈھانکا حمل رہا ہلکا سا حمل تو چلتی پھرتی رہی اسکے ساتھ پھر جب بوجھل ہو گئ تو دونوں نے پکارا اللہ اپنے رب کو کہ اگر تو ہم کو بخشے چنگا بھلا تو ہم تیرا شکر کریں');
INSERT INTO "al_hind" VALUES(7,190,'7:190','پھر جب ان کو دیا چنگا بھلا تو بنانے لگے اسکے لئے شریک اسکی بخشی ہوئی چیز میں سو اللہ برتر ہے انکے شریک بنانے سے ');
INSERT INTO "al_hind" VALUES(7,191,'7:191','کیا شریک بناتے ہیں ایسوں کو جو پیدا نہ کریں ایک چیز بھی اور وہ پیدا ہوئے ہیں ');
INSERT INTO "al_hind" VALUES(7,192,'7:192','اور نہیں کر سکتے ہیں ان کی مدد اور نہ اپنی مدد کریں');
INSERT INTO "al_hind" VALUES(7,193,'7:193','اور اگر تم ان کو پکارو رستہ کی طرف تو نہ چلیں تمہاری پکار پر برابر ہے تم پر کہ ان کو پکارو یا چپکے رہو');
INSERT INTO "al_hind" VALUES(7,194,'7:194','جن کو تم پکارتے ہو اللہ کے سوا وہ بندے ہیں تم جیسے بھلا پکارو تو ان کو پس چاہیئے کہ وہ قبول کریں تمہارے پکارنے کو اگر تم سچے ہو');
INSERT INTO "al_hind" VALUES(7,195,'7:195','کیا انکے پاؤں ہیں جن سے چلتے ہیں یا ان کے ہاتھ ہیں جن سے پکڑتے ہیں یا ان کے آنکھیں ہیں جن سے دیکھتے ہیں یا ان کے کان ہیں جن سے سنتے ہیں تو کہہ دے کہ پکارو اپنے شریکوں کو پھر برائی کرو میرے حق میں اور مجھ کو ڈھیل نہ دو ');
INSERT INTO "al_hind" VALUES(7,196,'7:196','میرا حمایتی تو اللہ ہے جس نے اتاری کتاب اور وہ حمایت کرتا ہے نیک بندوں کی ');
INSERT INTO "al_hind" VALUES(7,197,'7:197','اور جن کو تم پکارتے ہو اس کے سوا وہ نہیں کر سکتے تمہاری مدد اور نہ اپنی جان بچا سکیں');
INSERT INTO "al_hind" VALUES(7,198,'7:198','اور اگر تم ان کو پکارو رستہ کی طرف تو کچھ نہ سنیں اور تو دیکھتا ہے انکو کہ تک رہے ہیں تیری طرف اور وہ کچھ نہیں دیکھتے ');
INSERT INTO "al_hind" VALUES(7,199,'7:199','عادت کر درگذر کی اور حکم کر نیک کام کرنے کا اور کنارہ کر جاہلوں سے');
INSERT INTO "al_hind" VALUES(7,200,'7:200','اور اگر ابھارے تجھ کو شیطان کی چھیڑ تو پناہ مانگ اللہ سے وہی ہےسننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(7,201,'7:201','جن کے دل میں ڈر ہے جہاں پڑ گیا ان پر شیطان کا گذر چونک گئے پھر اسی وقت ان کو سوجھ آ جاتی ہے');
INSERT INTO "al_hind" VALUES(7,202,'7:202','اور جو شیطانوں کے بھائی ہیں وہ انکو کھینچتے چلے جاتے ہیں گمراہی میں پھر وہ کمی نہیں کرتے ');
INSERT INTO "al_hind" VALUES(7,203,'7:203','اور جب تو لیکر نہ جائے انکے پاس کوئی نشانی تو کہتے ہیں کیوں نہ چھانٹ لایا تو کچھ اپنی طرف سے تو کہہ دے میں تو چلتا ہوں اس پر جو حکم آئے میری طرف میرے رب سے یہ سوجھ کی باتیں ہیں تمہارے رب کی طرف سے اور ہدایت اور رحمت ہے ان لوگوں کو جو مومن ہیں ');
INSERT INTO "al_hind" VALUES(7,204,'7:204','اور جب قرآن پڑھا جائے تو اس کی طرف کان لگائے رہو اور چپ رہو تاکہ تم پر رحم ہو ');
INSERT INTO "al_hind" VALUES(7,205,'7:205','اور یاد کرتا رہ اپنے رب کو اپنے دل میں گڑگڑاتا ہوا اور ڈرتا ہوا اور ایسی آواز سے جو کہ پکار کر بولنے سے کم ہو صبح کے وقت اور شام کے وقت اور مت رہ بے خبر');
INSERT INTO "al_hind" VALUES(7,206,'7:206','بیشک جو تیرے رب کے نزدیک ہیں وہ تکبر نہیں کرتے اس کی بندگی سے اور یاد کرتے ہیں اسکی پاک ذات کو اور اسی کو سجدہ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(8,1,'8:1',' تجھ سے پوچھتے ہیں حکم غنیمت کا تو کہہ دے کے مال غنیمت اللہ کا ہے اور رسول کا سو ڈرو اللہ سے اور صلح کرو آپس میں اور حکم مانو اللہ کا اور اسکے رسول کا اگر تم ایمان رکھتے ہو');
INSERT INTO "al_hind" VALUES(8,2,'8:2','ایمان والے وہی ہیں کہ جب نام آئے اللہ کا تو ڈر جائیں ان کے دل اور جب پڑھا جائے ان پر اس کا کلام تو زیادہ ہو جاتا ہےان کا ایمان اور وہ اپنے رب پر بھروسا رکھتے ہیں');
INSERT INTO "al_hind" VALUES(8,3,'8:3','وہ لوگ جو کہ قائم رکھتے ہیں نماز کو اور ہم نے جو انکو روزی دی ہے اس میں سے خرچ کرتے ہیں');
INSERT INTO "al_hind" VALUES(8,4,'8:4','وہی ہیں سچے ایمان والے انکے لئے درجے ہیں اپنے رب کے پاس اور معافی اور روزی عزت کی ');
INSERT INTO "al_hind" VALUES(8,5,'8:5','جیسے نکالا تجھ کو تیرے رب نے تیرے گھر سے حق کام کے واسطے اور ایک جماعت اہل ایمان کی راضی نہ تھی');
INSERT INTO "al_hind" VALUES(8,6,'8:6','وہ تجھ سے جھگڑتے تھے حق بات میں اسکے ظاہر ہو چکنے کے بعد گویا وہ ہانکے جاتے ہیں موت کی طرف آنکھوں دیکھتے ');
INSERT INTO "al_hind" VALUES(8,7,'8:7','اور جس وقت تم سے وعدہ کرتا تھا اللہ دو جماعتوں میں سے ایک کا کہ وہ تمہارے ہاتھ لگے گی اور تم چاہتے تھے کہ جس میں کانٹا نہ لگے وہ تم کو ملے اور اللہ چاہتا تھا کہ سچا کر دے سچ کو اپنے کلاموں سے اور کاٹ ڈالے جڑ کافروں کی');
INSERT INTO "al_hind" VALUES(8,8,'8:8','تاکہ سچا کرے سچ کو اور جھوٹا کر دے جھوٹ کو اور اگرچہ ناراض ہوں گنہگار ');
INSERT INTO "al_hind" VALUES(8,9,'8:9','جب تم لگے فریاد کرنے اپنے رب سے تو وہ پہنچا تمہاری فریاد کو کہ میں مدد بھیجوں گا تمہاری ہزار فرشتے لگاتار آنے والے');
INSERT INTO "al_hind" VALUES(8,10,'8:10','اور یہ تو دی اللہ نے فقط خوشخبری اور تاکہ مطمئن ہو جائیں اس سے تمہارےدل اور مدد نہیں مگر اللہ کی طرف سے بیشک اللہ زورآور ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(8,11,'8:11','جس وقت کہ ڈالدی اس نے تم پر اونگھ اپنی طرف سے تسکین کے واسطے اور اتارا تم پر آسمان سے پانی کہ اس سے تم کو پاک کر دے اور دور کر دے تم سے شیطان کی نجاست اور مضبوط کر دے تمہارے دلوں کو اور جما دے اس سے تمہارے قدم ');
INSERT INTO "al_hind" VALUES(8,12,'8:12','جب حکم بھیجا تیرے رب نے فرشتوں کو کہ میں ساتھ ہوں تمہارے سو تم دل ثابت رکھو مسلمانوں کے میں ڈال دوں گا دل میں کافروں کے دہشت سو مارو گردنوں پر اور کاٹو ان کی پور پور');
INSERT INTO "al_hind" VALUES(8,13,'8:13','یہ اس واسطے ہے کہ وہ مخالف ہوئے اللہ کے اور اسکے رسول کے اور جو کوئی مخالف ہوا اللہ کا اور اس کے رسول کا تو بیشک اللہ کا عذاب سخت ہے');
INSERT INTO "al_hind" VALUES(8,14,'8:14','یہ تو تم چکھ لو اور جان رکھو کہ کافروں کے لئے ہے عذاب دوزخ کا ');
INSERT INTO "al_hind" VALUES(8,15,'8:15','اے ایمان والو جب بھڑو تم کافروں سے میدان جنگ میں تو مت پھیرو ان سے پیٹھ ');
INSERT INTO "al_hind" VALUES(8,16,'8:16','اور جو کوئی ان سے پھیرے پیٹھ اس دن مگر یہ کہ ہنر کرتا ہو لڑائی کا یا جا ملتا ہو فوج میں سو وہ پھرا اللہ کا غضب لے کر اور اس کا ٹھکانہ دوزخ ہے اور وہ کیا برا ٹھکانہ ہے ');
INSERT INTO "al_hind" VALUES(8,17,'8:17','سو تم نے ان کو نہیں مارا لیکن اللہ نے ان کو مارا اور تو نے نہینں پھینکی مٹھی خا ک کی جس وقت کہ پھینکی تھی لیکن اللہ نے پھینکی اور تاکہ کرے ایمان والوں پر اپنی طرف سے خوب احسان بیشک اللہ ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(8,18,'8:18','یہ تو ہو چکا اور جان رکھو کہ اللہ سست کر دے گا تدبیر کافروں کی ');
INSERT INTO "al_hind" VALUES(8,19,'8:19','اگر تم چاہتے ہو فیصلہ تو پہنچ چکا تمہارے پاس فیصلہ اور اگر باز آؤ تو تمہارے لئے بہتر ہے اور اگر پھر یہی کرو گے تو ہم بھی پھر یہی کریں گے اور کچھ کام نہ آئے گا تمہارے تمہارا جتھا اگرچہ بہت ہوں اور جان لو کہ اللہ ایمان والوں کے ساتھ ہے ');
INSERT INTO "al_hind" VALUES(8,20,'8:20','اے ایمان والو حکم مانو اللہ کا اور اسکے رسول کا اور اس سے مت پھرو سن کر ');
INSERT INTO "al_hind" VALUES(8,21,'8:21','اور ان جیسے مت ہو جنہوں نے کہا ہم نے سن لیا اور وہ سنتے نہیں ');
INSERT INTO "al_hind" VALUES(8,22,'8:22','بیشک سب جانداروں میں بدتر اللہ کے نزدیک وہی بہرےگونگے ہیں جو نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(8,23,'8:23','اور اگر اللہ جانتا ان میں کچھ بھلائی تو ان کو سنا دیتا اور اگر ان کو اب سنا دے تو ضرور بھاگیں گے منہ پھیر کر ');
INSERT INTO "al_hind" VALUES(8,24,'8:24','اے ایمان والو حکم مانو اللہ کا اور رسول کا جس وقت بلائے تم کو اس کام کی طرف جس میں تمہاری زندگی ہے اور جان لو کہ اللہ روک لیتا ہے آدمی سے اس کے دل کو اور یہ کہ اسی کے پاس تم جمع ہو گے ');
INSERT INTO "al_hind" VALUES(8,25,'8:25','اور بچتے رہو اس فساد سے کہ نہیں پڑے گا تم میں سے خاص ظالموں ہی پر اور جان لو کہ اللہ کا عذاب سخت ہے ');
INSERT INTO "al_hind" VALUES(8,26,'8:26','اور یاد کرو جس وقت تم تھوڑے تھے مغلوب پڑے ہوئے ملک میں ڈرتے تھے کہ اچک لیں تم کو لوگ پھر اس نے تم کو ٹھکانہ دیا اور قوت دی تم کو اپنی مدد سے اور روزی دی تم کو ستھری چیزیں تاکہ تم شکر کرو ');
INSERT INTO "al_hind" VALUES(8,27,'8:27','اے ایمان والو خیانت نہ کرو اللہ سے اور رسول سے اور خیانت نہ کرو آپس کی امانتوں میں جان کر ');
INSERT INTO "al_hind" VALUES(8,28,'8:28','اور جان لو کہ بیشک تمہارےمال اور اولاد خرابی میں ڈالنے والے ہیں اور یہ کہ اللہ کے پاس بڑا ثواب ہے ');
INSERT INTO "al_hind" VALUES(8,29,'8:29','اے ایمان والو اگر تم ڈرتے رہو گے اللہ سے تو کر دے گا تم میں فیصلہ اور دور کر دے گا تم سے تمہارے گناہ اور تم کو بخش دے گا اور اللہ کا فضل بڑا ہے');
INSERT INTO "al_hind" VALUES(8,30,'8:30','اور جب فریب کرتے تھے کافر کہ تجھ کو قید میں کر دیں یا مار ڈالیں یا نکال دیں اور وہ بھی داؤ کرتے تھے اور اللہ بھی داؤ کرتا تھا اور اللہ کا داؤ سب سے بہتر ہے ');
INSERT INTO "al_hind" VALUES(8,31,'8:31','اور جب کوئی پڑھے ان پر ہماری آیتیں تو کہیں ہم سن چکے اگر ہم چاہیں تو ہم بھی کہہ لیں ایسا یہ تو کچھ بھی نہیں مگر احوال ہیں اگلوں کے ');
INSERT INTO "al_hind" VALUES(8,32,'8:32','اور جب وہ کہنے لگے کہ یا اللہ اگر یہی دین حق ہے تیری طرف سے تو ہم پر برسا دے پتھر آسمان سے یا لا ہم پر کوئی عذاب دردناک ');
INSERT INTO "al_hind" VALUES(8,33,'8:33','اور اللہ ہر گز نہ عذاب کرتا ان پر جب تک تو رہتا ان میں اور اللہ ہرگز نہ عذاب کرے گا ان پر جب تک وہ معافی مانگتے رہیں گے ');
INSERT INTO "al_hind" VALUES(8,34,'8:34','اور ان میں کیا بات ہے کہ عذاب نہ کرے ان پر اللہ اور وہ تو روکتے ہیں مسجد حرام سے اور وہ اس کے اختیار والے نہیں اس کے اختیار والے تو وہی ہیں جو پرہیزگار ہیں لیکن ان میں اکثروں کو اس کی خبر نہیں ');
INSERT INTO "al_hind" VALUES(8,35,'8:35','اور ان کی نماز نہیں تھی کعبہ کے پاس مگر سیٹیاں بجانی اور تالیاں سو چکھو عذاب بدلا اپنے کفر کا ');
INSERT INTO "al_hind" VALUES(8,36,'8:36','بیشک جو لوگ کافر ہیں وہ خرچ کرتے ہیں اپنے مال تاکہ روکیں اللہ کی راہ سے سو ابھی اور خرچ کریں گے پھر آخر ہو گا وہ ان پر افسوس اور آخر مغلوب ہوں گے اور جو کافر ہیں وہ دوزخ کی طرف ہانکے جائیں گے ');
INSERT INTO "al_hind" VALUES(8,37,'8:37','تاکہ جدا کر دے اللہ ناپاک کو پاک سے اور رکھے ناپاک کو ایک کو ایک پر پھر اس کو ڈھیر کر دے اکٹھا پھر ڈالدے اسکو دوزخ میں وہی لوگ ہیں نقصان میں ');
INSERT INTO "al_hind" VALUES(8,38,'8:38','تو کہہ دے کافروں کو کہ اگر وہ باز آ جائیں تو معاف ہو ان کو جو کچھ ہو چکا اور اگر پھر بھی وہی کریں گے تو پڑ چکی ہے راہ اگلوں کی ');
INSERT INTO "al_hind" VALUES(8,39,'8:39','اور لڑتے رہو ان سے یہاں تک کہ نہ رہے فساد اور ہو جائے حکم سب اللہ کا پھر اگر وہ باز آ جائیں تو اللہ ان کے کام کو دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(8,40,'8:40','اور اگر وہ نہ مانیں تو جان لو کہ اللہ تمہارا حمایتی ہے کیا خوب حمایتی ہے اور کیا خوب مددگار ');
INSERT INTO "al_hind" VALUES(8,41,'8:41','اور جان رکھو کہ جو کچھ تم کو غنیمت ملے کسی چیز سے سو اللہ کے واسطے ہے اس میں سے پانچواں حصہ اور رسول کے واسطے اور اسکے قرابت والوں کے واسطے اور یتیموں اور محتاجوں اور مسافروں کے واسطے اگر تم کو یقین ہے اللہ پر اور اس چیز پر جو ہم نے اتاری اپنے بندے پر فیصلہ کے دن جس دن بھڑ گئیں دونوں فوجیں اور اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(8,42,'8:42','جس وقت تم تھے ورلے کنارہ پر اور وہ پرلے کنارہ پر اور قافلہ نیچے اتر گیا تھا تم سے اور اگر تم آپس میں وعدہ کرتےتو نہ پہنچتے وعدہ پر ایک ساتھ لیکن اللہ کو کر ڈالنا تھا ایک کام کو جو مقرر ہو چکا تھا تاکہ مرے جس کو مرنا ہے قیام حجت کےبعد اور جیوے جس کو جینا ہے قیام حجت کے بعد اور بیشک اللہ سننے والا جاننے والا ہے ');
INSERT INTO "al_hind" VALUES(8,43,'8:43','جب اللہ نے وہ کافر دکھلائے تجھ کو تیری خواب میں تھوڑے اور اگر تجھ کو بہت دکھلا دیتا تو تم لوگ نامردی کرتے اور جھگرا ڈالتے کام میں لیکن اللہ نے بچا لیا اس کو خوب معلوم ہے جو بات ہے دلوں میں ');
INSERT INTO "al_hind" VALUES(8,44,'8:44','اور جب تم کو دکھلائی وہ فوج مقابلہ کے وقت تمہاری آنکھوں میں تھوڑی اور تم کو تھوڑا دکھلایا ان کی آنکھوں میں تاکہ کر ڈالے اللہ ایک کام جو مقرر ہو چکا تھا اور اللہ تک پہنچتا ہے ہر کام ');
INSERT INTO "al_hind" VALUES(8,45,'8:45','اے ایمان والو جب بھڑو کسی فوج سے تو ثابت قدم رہو اور اللہ کو بہت یاد کرو تاکہ تم مراد پاؤ');
INSERT INTO "al_hind" VALUES(8,46,'8:46','اور حکم مانو اللہ کا اور اسکے رسول کا اور آپس میں نہ جھگڑو پس نامرد ہو جاؤ گے اور جاتی رہے گی تمہاری ہوا اور صبر کرو بیشک اللہ ساتھ ہے صبر والوں کے ');
INSERT INTO "al_hind" VALUES(8,47,'8:47','اور نہ ہو جاؤ ان جیسے جو کہ نکلے اپنے گھروں سے اتراتے ہوئے اور لوگوں کے دکھانے کو اور روکتے تھے اللہ کی راہ سے اور اللہ کے قابو میں ہے جو کچھ وہ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(8,48,'8:48','اور جس وقت خوشنما کر دیا شیطان نے انکی نظروں میں ان کے عملوں کو اور بولا کہ کوئی بھی غالب نہ ہوگا تم پر آج کے دن لوگوں میں سے اور میں تمہارا حمایتی ہوں پھر جب سامنے ہوئیں دونوں فوجیں تو وہ الٹا پھرا اپنی ایڑیوں پر اور بولا میں تمہارے ساتھ نہیں ہو ں میں دیکھتا ہوں جو تم نہیں دیکھتے میں ڈرتا ہوں اللہ سے اور اللہ کا عذاب سخت ہے ');
INSERT INTO "al_hind" VALUES(8,49,'8:49','جب کہنے لگے منافق اور جن کےدلوں میں بیماری ہے یہ لوگ مغرور ہیں اپنے دین پر اور جو کوئی بھروسہ کرے اللہ پر تو اللہ زبردست ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(8,50,'8:50','اور اگر تو دیکھے جس وقت جان قبض کرتے ہیں کافروں کی فرشتے مارتے ہیں ان کے منہ پر اور ان کے پیچھے اور کہتے ہیں چکھو عذاب جلنے کا ');
INSERT INTO "al_hind" VALUES(8,51,'8:51','یہ بدلا ہے اسی کا جو تم نے آگے بھیجا اپنے ہاتھوں اور اس واسطے کہ اللہ ظلم نہیں کرتا بندوں پر ');
INSERT INTO "al_hind" VALUES(8,52,'8:52','جیسے دستور فرعون والوں کا اور جو ان سے پہلے تھے کہ منکر ہوئے اللہ کی باتوں سے سو پکڑا ان کو اللہ نے ان کے گناہوں پر بیشک اللہ زور آور ہے سخت عذاب کرنے والا ');
INSERT INTO "al_hind" VALUES(8,53,'8:53','اس کا سبب یہ ہے کہ اللہ ہر گز بدلنے والا نہیں اس نعمت کو جو دی تھی اس نے کسی قوم کو جب تک وہی نہ بدل ڈالیں اپنے جیوں کی بات اور یہ کہ اللہ سننے والا جاننے والا ہے ');
INSERT INTO "al_hind" VALUES(8,54,'8:54','جیسے دستور فرعون والوں کا اور جو ان سے پہلے تھے کہ انہوں نے جھٹلائیں باتیں اپنے رب کی پھر ہلاک کر دیا ہم نے انکو انکے گناہوں پر اور ڈبو دیا ہم نے فرعون والوں کو اور سارے ظالم تھے ');
INSERT INTO "al_hind" VALUES(8,55,'8:55','بدتر سب جانداروں میں اللہ کے ہاں وہ ہیں جو منکر ہوئے پھر وہ نہیں ایمان لاتے');
INSERT INTO "al_hind" VALUES(8,56,'8:56','جن سے تو نے معاہدہ کیا ہے ان میں سے پھر وہ توڑتے ہیں اپنا عہد ہر بار اور وہ ڈر نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(8,57,'8:57','سو اگر کبھی تو پائے ان کو لڑائی میں تو انکو ایسی سزا دے کہ دیکھ کر بھاگ جائیں ان کے پچھلے تاکہ ان کو عبرت ہو');
INSERT INTO "al_hind" VALUES(8,58,'8:58','اور اگر تجھ کو ڈر ہو کسی قوم سےدغا کا تو پھینک دے انکا عہد انکی طرف ایسی طرح پر کہ ہو جاؤ تم اور وہ برابر بیشک اللہ کو خوش نہیں آتے دغاباز ');
INSERT INTO "al_hind" VALUES(8,59,'8:59','اور یہ نہ سمجھیں کافر لوگ کہ وہ بھاگ نکلے وہ ہرگز تھکا نہ سکیں گے ہم کو ');
INSERT INTO "al_hind" VALUES(8,60,'8:60','اور تیار کرو انکی لڑائی کے واسطے جو کچھ جمع کر سکو قوت سے اور پلے ہوئے گھوڑوں سے کہ اس سے دھاک پڑے اللہ کے دشمنوں پر اور تمہارے دشمنوں پر اور دوسروں پر ان کےسوا جن کو تم نہیں جانتے اللہ ان کو جانتا ہے اور جو کچھ تم خرچ کرو گے اللہ کی راہ میں وہ پورا ملے گا تم کو اور تمہار حق نہ رہ جائے گا ');
INSERT INTO "al_hind" VALUES(8,61,'8:61','اور اگر وہ جھکیں صلح کی طرف تو تو بھی جھک اسی طرف اور بھروسہ کر اللہ پر بیشک وہی ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(8,62,'8:62','اور اگر وہ چاہیں کہ تجھ کو دغا دیں تو تجھ کو کافی ہے اللہ اسی نے تجھ کو زور دیا اپنی مدد کا اور مسلمانوں کا ');
INSERT INTO "al_hind" VALUES(8,63,'8:63','اور الفت ڈالی انکے دلوں میں اگر تو خرچ کر دیتا جو کچھ زمین میں ہے سارا نہ الفت ڈال سکتا ان کے دلوں میں لیکن اللہ نے الفت ڈالی ان میں بیشک وہ زورآور ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(8,64,'8:64','اے نبی کافی ہے تجھ کو اللہ اور جتنے تیرے ساتھ ہیں مسلمان ');
INSERT INTO "al_hind" VALUES(8,65,'8:65','اے نبی شوق دلا مسلمانوں کو لڑائی کا اگر ہوں تم میں بیس شخص ثابت قدم رہنے والے تو غالب ہوں دو سو پر اور اگر ہوں تم میں سو شخص تو غالب ہوں ہزار کافروں پر اس واسطے کہ وہ لوگ سمجھ نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(8,66,'8:66','اب بوجھ ہلکا کر دیا اللہ نے تم پر سے اور جانا کہ تم میں سستی ہےسو اگر ہوں تم میں سو شخص ثابت قدم رہنے والے تو غالب ہوں دو سو پر اور اگر ہوں تم میں ہزار تو غالب ہوں دو ہزار پر اللہ کے حکم سے اور اللہ ساتھ ہے ثابت قدم رہنے والوں کے ');
INSERT INTO "al_hind" VALUES(8,67,'8:67','نبی کو نہیں چاہئے کہ اپنے ہاں رکھے قیدیوں کو جب تک خوب خونریزی نہ کر لے ملک میں تم چاہتے ہو اسباب دنیا اور اور اللہ کے ہاں چاہئے آخرت اور اللہ زور آور ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(8,68,'8:68','اگر نہ ہوتی ایک بات جس کو لکھ چکا اللہ پہلے سے تو تم کو پہنچتا اس لینے میں بڑا عذاب ');
INSERT INTO "al_hind" VALUES(8,69,'8:69','سو کھاؤ جو تم کو غنیمت میں ملا حلال ستھرا اور ڈرتے رہو اللہ سے بیشک اللہ ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(8,70,'8:70','اے نبی کہہ دے ان سے جو تمہارے ہاتھ میں ہیں قیدی اگر جانے گا اللہ تمہارے دلوں میں کچھ نیکی تو دے گا تم کو بہتر اس سے جو تم سے چھن گیا اور تم کو بخشے گا اور اللہ ہے بخشنے والا مہربان');
INSERT INTO "al_hind" VALUES(8,71,'8:71','اور اگر چاہیں گے تجھ سے دغا کرنی سو وہ دغا کر چکے ہیں اللہ سے اس سے پہلے پھر اس نے انکو پکڑوا دیا اور اللہ سب کچھ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(8,72,'8:72','جو لوگ ایمان لائے اور گھر چھوڑا اور لڑے اپنے مال اور جان سے اللہ کی راہ میں اور جن لوگوں نے جگہ دی اور مدد کی وہ ایک دوسرے کے رفیق ہیں اور جو ایمان لائے اور گھر نہیں چھوڑا تم کو ان کی رفاقت سے کچھ کام نہیں جب تک وہ گھر نہ چھوڑ آئیں اور اگر وہ تم سے مدد چاہیں دین میں تو تم کو لازم ہے انکی مدد کرنی مگر مقابلہ میں ان لوگوں کے کہ ان میں اور تم میں عہد ہو اور اللہ جو تم کرتے ہو اس کو دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(8,73,'8:73','اور جو لوگ کافر ہیں وہ ایک دوسرے کے رفیق ہیں اگر تم یوں نہ کرو گے تو فتنہ پھیلے گا ملک میں اور بڑی خرابی ہو گی ');
INSERT INTO "al_hind" VALUES(8,74,'8:74','اور جو لوگ ایمان لائے اور اپنے گھر چھوڑے اور لڑے اللہ کی راہ میں اور جن لوگوں نے ان کو جگہ دی اور انکی مدد کی وہی ہیں سچے مسلمان انکے لئے بخشش ہے اور روزی عزت کی ');
INSERT INTO "al_hind" VALUES(8,75,'8:75','اور جو ایمان لائے اسکے بعد اور گھر چھوڑ آئے اور لڑے تمہارے ساتھ ہو کر سو وہ لوگ بھی تمہی میں ہیں اور رشتہ دار آپس میں حقدار زیادہ ہیں ایک دوسرے کے اللہ کے حکم میں تحقیق اللہ ہر چیز سے خبردار ہے ');
INSERT INTO "al_hind" VALUES(9,1,'9:1','(اس سورت کے شروع میں بسم اللہ نہیں لکھی گئ اس کے مختلف اسباب بیان کئے گئے ہیں) صاف جواب ہے اللہ کی طرف سے اور اسکے رسول کی ان مشرکوں کو جن سے تمہارا عہد ہوا تھا');
INSERT INTO "al_hind" VALUES(9,2,'9:2','سو پھر لو اس ملک میں چار مہینے اور جان لو کہ تم نہ تھکا سکو گے اللہ کو اور یہ کہ اللہ رسوا کرنے والا ہے کافروں کو ');
INSERT INTO "al_hind" VALUES(9,3,'9:3','اور سنا دینا ہے اللہ کی طرف سے اور اس کے رسول کی لوگوں کو دن بڑے حج کے کہ اللہ الگ ہے مشرکوں سے اور اس کا رسول سو اگر تم توبہ کرو تو تمہارے لئے بہتر ہے اور اگر نہ مانو تو جان لو کہ تم ہرگز نہ تھکا سکو گے اللہ کو اور خوشخبری سنادے کافروں کو عذاب دردناک کی ');
INSERT INTO "al_hind" VALUES(9,4,'9:4','مگر جن مشرکوں سے تم نے عہد کیا تھا پھر انہوں نے کچھ قصور نہ کیا تمہارے ساتھ اور مدد نہ کی تمہارے مقابلہ میں کسی کی سو ان سے پورا کر دو ان کا عہد ان کے وعدہ تک بیشک اللہ کو پسند ہیں احتیاط والے ');
INSERT INTO "al_hind" VALUES(9,5,'9:5','پھر جب گذر جائیں مہینے پناہ کے تو مارو مشرکوں کو جہاں پاؤ اور پکڑو اور گھیرو اور بیٹھو ہر جگہ ان کی تاک میں پھر اگر وہ توبہ کریں اور قائم رکھیں نماز اور دیا کریں زکوٰۃ تو چھوڑ دو ان کا رستہ بیشک اللہ ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(9,6,'9:6','اور اگر کوئی مشرک تجھ سے پناہ مانگے تو اس کو پنادہ دیدے یاں تک کہ وہ سن لے کلام اللہ کا پھر پہنچا دے اسکو اسکی امن کی جگہ یہ اس واسطے کہ وہ لوگ علم نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(9,7,'9:7','کیونکر ہووے مشرکوں کے لئے عہد اللہ کے نزدیک اور اسکے رسول کے نزدیک مگر جن لوگوں سے تم نے عہد کیا تھا مسجد حرام کے پاس سو جب تک وہ تم سے سیدھے رہیں تم ان سے سیدھے رہو بیشک اللہ کو پسند ہیں احتیاط والے');
INSERT INTO "al_hind" VALUES(9,8,'9:8','کیونکر رہے صلح اور اگر وہ تم پر قابو پائیں تو نہ لحاظ کریں تمہاری قرابت کا اور نہ عہد کا تم کو راضی کر دیتے ہیں اپنے منہ کی بات سے اور انکے دل نہیں مانتے اور اکثر ان میں بد عہد ہیں ');
INSERT INTO "al_hind" VALUES(9,9,'9:9','بیچ ڈالے انہوں نے اللہ کے حکم تھوڑی قیمت پر پھر روکا اس کے رستہ سے برے کام ہیں جو وہ لوگ کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(9,10,'9:10','نہیں لحاظ کرتے کسی مسلمان کے حق میں قرابت کا اور نہ عہد کا اور وہی ہیں زیادتی پر ');
INSERT INTO "al_hind" VALUES(9,11,'9:11','سو اگر توبہ کریں اور قائم رکھیں نماز اور دیتے رہیں زکوٰۃ تو تمہارے بھائی ہیں حکم شریعت میں اور ہم کھول کر بیان کرتے ہیں حکموں کو جاننے والے لوگوں کے واسطے');
INSERT INTO "al_hind" VALUES(9,12,'9:12','اور اگر وہ توڑ دیں اپنی قسمیں عہد کرنے کے بعد اور عیب لگائیں تمہارے دین میں تو لڑو کفر کے سرداروں سے بیشک انکی قسمیں کچھ نہیں تاکہ وہ باز آئیں ');
INSERT INTO "al_hind" VALUES(9,13,'9:13','کیا نہیں لڑتے ایسے لوگوں سے جو توڑیں اپنی قسمیں اور فکر میں رہیں کہ رسول کو نکال دیں اور انہوں نے پہلے چھیڑ کی تم سے کیا تم ان سے ڈرتے ہو سو اللہ کا ڈر چاہئے تم کو زیادہ اگر تم ایمان رکھتے ہو ');
INSERT INTO "al_hind" VALUES(9,14,'9:14','لڑو ان سے تا عذاب دے اللہ انکو تمہارے ہاتھوں اور رسوا کرے اور تم کو ان پر غالب کرے اور ٹھنڈے کرے دل مسلمانوں کے');
INSERT INTO "al_hind" VALUES(9,15,'9:15','اور نکالے ان کے دل کی جلن اور اللہ توبہ نصیب کرے گا جس کو چاہے گا اور اللہ سب کچھ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(9,16,'9:16','کیا تم یہ گمان کرتے ہو کہ چھوٹ جاؤ گے اور حالانکہ ابھی معلوم نہیں کیا اللہ نے تم میں سے ان لوگوں کو جنہوں نے جہاد کیا ہے اور نہیں پکڑا انہوں نے سوا اللہ کے اور اسکے رسول کے اور مسلمانوں کے کسی کو بھیدی اور اللہ کو خبر ہے جو تم کر رہے ہو ');
INSERT INTO "al_hind" VALUES(9,17,'9:17','مشرکوں کا کام نہیں کہ آباد کریں اللہ کی مسجدیں اور تسلیم کر رہے ہوں اپنے اوپر کفر کو وہ لوگ خراب گئے ان کےعمل اور آگ میں رہیں گے وہ ہمیشہ');
INSERT INTO "al_hind" VALUES(9,18,'9:18','وہی آباد کرتا ہے مسجدیں اللہ کی جو یقین لایا اللہ پر اور آخرت کے دن پر اور قائم کیا نماز کو اور دیتا رہا زکوٰۃ اور نہ ڈرا سوائے اللہ کے کسی سے سو امیدوار ہیں وہ لوگ کہ ہوویں ہدایت والوں میں ');
INSERT INTO "al_hind" VALUES(9,19,'9:19','کیا تم نے کر دیا حاجیوں کا پانی پلانا اور مسجد الحرام کا بسانا برابر اس کے جو یقین لایا اللہ پر اور آخرت کےدن پر اور لڑا اللہ کی راہ میں یہ برابر نہیں ہیں اللہ کے نزدیک اور اللہ رستہ نہیں دیتا ظالم لوگوں کو ');
INSERT INTO "al_hind" VALUES(9,20,'9:20','جو ایمان لائے اور گھر چھوڑ آئے اور لڑے اللہ کی راہ میں اپنے مال اور جان سے ان کے لئے بڑا درجہ ہے اللہ کے ہاں اور وہی مراد کو پہنچنے والے ہیں');
INSERT INTO "al_hind" VALUES(9,21,'9:21','خوشخبری دیتا ہے انکو پروردگار انکا اپنی طرف سے مہربانی کی اور ضامندی کی اور باغوں کی کہ جن میں انکو آرام ہے ہمیشہ کا');
INSERT INTO "al_hind" VALUES(9,22,'9:22','رہا کریں ان میں مدام بیشک اللہ پاس بڑا ثواب ہے ');
INSERT INTO "al_hind" VALUES(9,23,'9:23','اےایمان والو مت پکڑو اپنے باپوں کو اور بھائیوں کو رفیق اگر وہ عزیز رکھیں کفر کو ایمان سے اور جو تم میں انکی رفاقت کرے سو وہی لوگ ہیں گنہگار ');
INSERT INTO "al_hind" VALUES(9,24,'9:24','تو کہہ دے اگر تمہارے باپ اور بیٹے اور بھائی اور عورتیں اور برادری اور مال جو تم نے کمائے ہیں اور سوداگری جسکے بند ہونے سے تم ڈرتے ہو اور حویلیاں جنکو پسند کرتے ہو تم کو زیادہ پیاری ہیں اللہ سے اور اسکے رسول سے اور لڑنے سے اسکی راہ میں تو انتظار کرو یہاں تک کہ بھیجے اللہ اپنا حکم اور اللہ رستہ نہیں دیتا نافرمان لوگوں کو ');
INSERT INTO "al_hind" VALUES(9,25,'9:25','مدد کر چکا ہے اللہ تمہاری بہت میدانوں میں اور حنین کے دن جب خوش ہوئے تم اپنی کثرت پر پھر وہ کچھ کام نہ آئی تمہارے اور تنگ ہو گئی تم پر زمین باوجود اپنی فراخی کے پھر ہٹ گئے تم پیٹھ دے کر');
INSERT INTO "al_hind" VALUES(9,26,'9:26','پھر اتاری اللہ نے اپنی طرف سے تسکین اپنے رسول پر اور ایمان والوں پر اور اتاریں فوجیں کہ جنکو تم نے نہیں دیکھا اور عذاب دیا کافروں کو اور یہ سزا ہے منکروں کی ');
INSERT INTO "al_hind" VALUES(9,27,'9:27','پھر توبہ نصیب کرے گا اللہ اس کے بعد جس کو چاہے اور اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(9,28,'9:28','اے ایمان والو مشرک جو ہیں سو پلید ہیں سو نزدیک نہ آنے پائیں مسجدالحرام کے اس برس کے بعد اور اگر تم ڈرتے ہو فقر سے تو آئندہ غنی کر دے گا تم کو اللہ اپنے فضل سے اگر چاہے بیشک اللہ سب کچھ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(9,29,'9:29','لڑو ان لوگوں سے جو ایمان نہیں لاتے اللہ پر اور نہ آخرت کے دن پر اور نہ حرام جانتے ہیں اسکو جس کو حرام کیا اللہ نے اور اسکے رسول نے اور نہ قبول کرتے ہیں دین سچا ان لوگوں میں سے جو کہ اہل کتاب ہیں یہاں تک کہ وہ جزیہ دیں اپنے ہاتھ سے ذلیل ہو کر ');
INSERT INTO "al_hind" VALUES(9,30,'9:30','اور یہود نے کہا کہ عزیر اللہ کا بیٹا ہے اور نصاریٰ نے کہا کہ مسیح اللہ کا بیٹا ہے یہ باتیں کہتے ہیں اپنے منہ سے ریس کرنے لگے اگلے کافروں کی بات کی ہلاک کرے ان کو اللہ کہاں سے پھرے جاتے ہیں ');
INSERT INTO "al_hind" VALUES(9,31,'9:31','ٹھہرا لیا اپنے عالموں اور درویشوں کو خدا اللہ کو چھوڑ کر اور مسیح مریم کے بیٹے کو بھی اور انکو حکم یہی ہوا تھا کہ بندگی کریں ایک معبود کی کسی کی بندگی نہیں اسکے سوا وہ پاک ہے ان کے شریک بتلانے سے');
INSERT INTO "al_hind" VALUES(9,32,'9:32','چاہتے ہیں کہ بھجا دیں روشنی اللہ کی اپنے منہ سے اور اللہ نہ رہے گا بدون پورا کئے اپنی روشنی کے اور پڑے برا مانیں کافر ');
INSERT INTO "al_hind" VALUES(9,33,'9:33','اسی نے بھیجا اپنے رسول کو ہدایت اور سچا دین دے کر تاکہ اس کو غلبہ دے ہر دین پر اور پڑے برا مانیں مشرک ');
INSERT INTO "al_hind" VALUES(9,34,'9:34','اے ایمان والو بہت سے عالم اور درویش اہل کتاب کے کھاتے ہیں مال لوگوں کے ناحق اور روکتے ہیں اللہ کی راہ سے اور جو لوگ گاڑھ کر رکھتے ہیں سونا اور چاندی اور اسکو خرچ نہیں کرتے اللہ کی راہ میں سو انکو خوشخبری سنا دے عذاب دردناک کی ');
INSERT INTO "al_hind" VALUES(9,35,'9:35','جس دن کہ آگ دہکائیں گے اس مال پر دوزخ کی پھر داغیں گے اس سے ان کے ماتھے اور کروٹیں اور پیٹھیں (کہا جائے گا) یہ ہے جو تم نے گاڑھ کر رکھا تھا اپنے واسطے اب چکھو مزا اپنے گاڑھنے کا ');
INSERT INTO "al_hind" VALUES(9,36,'9:36','مہینوں کی گنتی اللہ کے نزدیک بارہ مہینے ہیں اللہ کےحکم میں جس دن اس نے پیدا کئے تھے آسمان اور زمین ان میں چار مہینے ہیں ادب کے یہی ہے سیدھا دین سو ان میں ظلم مت کرو اپنے اوپر اور لڑو سب مشرکوں سے ہر حال میں جیسے وہ لڑتے ہیں تم سب سے ہر حال میں اور جان لو کہ اللہ ساتھ ہے ڈرنے والوں کے ');
INSERT INTO "al_hind" VALUES(9,37,'9:37','یہ جو مہینہ ہٹا دینا ہے سو بڑھائی ہوئی بات ہے کفر کےعہد میں گمراہی میں پڑتے ہیں اس سے کافر حلال کر لیتے ہیں اُس مہینہ کو ایک برس اور حرام رکھتے ہیں دوسرے برس تاکہ پوری کر لیں گنتی ان مہینوں کی جو اللہ نے ادب کے لئے رکھے ہیں پھر حلال کر لیتے ہیں جو مہینہ کہ اللہ نے حرام کیا بھلے کر دیئے گئے انکی نظر میں ان کے برے کام اور اللہ راستہ نہیں دیتا کافر لوگوں کو ');
INSERT INTO "al_hind" VALUES(9,38,'9:38','اے ایمان والو تم کو کیا ہوا جب تم سے کہا جاتا ہے کہ کوچ کرو اللہ کی راہ میں تو گرے جاتے ہو زمین پر کیا خوش ہو گئے دنیا کی زندگی پر آخرت کو چھوڑ کر سو کچھ نہیں نفع اٹھانا دنیا کی زندگی کا آخرت کے مقابلہ میں مگر بہت تھوڑا ');
INSERT INTO "al_hind" VALUES(9,39,'9:39','اگر تم نہ نکلو گے تو دے گا تم کو عذاب دردناک اور بدلے میں لائے گا اور لوگ تمہارے سوا اور کچھ نہ بگاڑ سکو گے تم اس کا اور اللہ سب چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(9,40,'9:40','اگر تم نہ مدد کرو گے رسول کی تو اس کی مدد کی ہے اللہ نے جس وقت اس کو نکالا تھا کافروں نے کہ وہ دوسرا تھا دو میں کا جب وہ دونوں تھے غار میں جب وہ کہہ رہا تھا اپنے رفیق سے تو غم نہ کھا بیشک اللہ ہمارے ساتھ ہے پھر اللہ نے اتاری اپنی طرف سے اس پر تسکین اور اسکی مدد کو وہ فوجیں بھیجیں کہ تم نے نہیں دیکھیں اور نیچے ڈالی کافروں کی اور اللہ کی بات ہمیشہ اوپر ہے اور اللہ زبردست ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(9,41,'9:41','نکلو ہلکے اور بوجھل اور لڑو اپنے مال سے اور جان سے اللہ کی راہ میں یہ بہتر ہے تمہارے حق میں اگر تم کو سمجھ ہے ');
INSERT INTO "al_hind" VALUES(9,42,'9:42','اگر مال ہو تا نزدیک اور سفر ہلکا تو وہ لوگ ضرور تیرے ساتھ ہو لیتے لیکن لمبی نظر آئی ان کو مسافت اور اب قسمیں کھائیں گے اللہ کی کہ اگر ہم سے ہو سکتا تو ہم ضرور چلتے تمہارے ساتھ وبال میں ڈالتے ہیں اپنی جانوں کو اور اللہ جانتا ہےکہ وہ جھوٹے ہیں ');
INSERT INTO "al_hind" VALUES(9,43,'9:43','اللہ بخشے تجھ کو کیوں رخصت دے دی تو نے انکو یہاں تک کہ ظاہر ہو جاتے تجھ پر سچ کہنے والے اور جان لیتا تو جھوٹوں کو ');
INSERT INTO "al_hind" VALUES(9,44,'9:44','نہیں رخصت مانگتے تجھ سے وہ لوگ جو ایمان لائے اللہ پر اور آخرت کے دن پر اس سے کہ لڑیں اپنے مال اور جان سے اور اللہ خوب جانتا ہے ڈر والوں کو');
INSERT INTO "al_hind" VALUES(9,45,'9:45','رخصت وہی مانگتے ہیں تجھ سے جو نہیں ایمان لائے اللہ پر اور آخرت کے دن پر اور شک میں پڑے ہیں دل ان کے سو وہ اپنے شک ہی میں بھٹک رہے ہیں ');
INSERT INTO "al_hind" VALUES(9,46,'9:46','اور اگر وہ چاہتے نکلنا تو ضرور تیار کرتے کچھ سامان اس کا لیکن پسند نہ کیا اللہ نے ان کا اٹھنا سو روک دیا انکو اور حکم ہوا کہ بیٹھے رہو ساتھ بیٹھنے والوں کے ');
INSERT INTO "al_hind" VALUES(9,47,'9:47','اگر نکلتےتم میں تو کچھ نہ بڑھاتے تمہارے لئے مگر خرابی اور گھوڑے دوڑاتے تمہارے اندر بگاڑ کروانے کی تلاش میں اور تم میں بعضے جاسوس ہیں انکے اور اللہ خوب جانتا ہے ظالموں کو ');
INSERT INTO "al_hind" VALUES(9,48,'9:48','وہ تلاش کرتے رہے ہیں بگاڑ کی پہلے سے اور الٹتے رہے ہیں تیرے کام یہاں تک کہ آ پہنچا سچا وعدہ اور غالب رہا حکم اللہ کا اور وہ ناخوش ہی رہے ');
INSERT INTO "al_hind" VALUES(9,49,'9:49','اور بعضے ان میں کہتے ہیں مجھ کو رخصت دے اور گمراہی میں نہ ڈال سنتا ہے وہ تو گمراہی میں پڑ چکے ہیں اور بیشک دوزخ گھیر رہی ہے کافروں کو ');
INSERT INTO "al_hind" VALUES(9,50,'9:50','اگر تجھ کو پہنچے کوئی خوبی تو وہ بری لگتی ہے انکو اور اگر پہنچے کوئی سختی تو کہتےہیں ہم نے تو سنبھال لیا تھا اپنا کام پہلے ہی اور پھر کر جائیں خوشیاں کرتے ');
INSERT INTO "al_hind" VALUES(9,51,'9:51','تو کہہ دے ہم کو ہر گز نہ پہنچے گا مگر وہی جو لکھ دیا اللہ نے ہمارے لئے وہی ہے کارساز ہمارا اور اللہ ہی پر چاہئے کہ بھروسہ کریں مسلمان');
INSERT INTO "al_hind" VALUES(9,52,'9:52','تو کہہ دے تم کیا امید کرو گے ہمارے حق میں مگر دو خوبیوں میں سے ایک کی اور ہم امیدوار ہیں تمہارے حق میں کہ ڈالے تم پر اللہ کوئی عذاب اپنے پاس سے یا ہمارے ہاتھوں سو منتظر رہو ہم بھی تمہارے ساتھ منتظر ہیں ');
INSERT INTO "al_hind" VALUES(9,53,'9:53','کہہ دے کہ مال خرچ کرو خوشی سے یا ناخوشی سے ہر گز قبول نہ ہوگا تم سے بیشک تم نافرمان لوگ ہو ');
INSERT INTO "al_hind" VALUES(9,54,'9:54','اور موقوف نہیں ہوا قبول ہونا ان کے خرچ کا مگر اسی بات پر کہ وہ منکر ہوئے اللہ سے اور اسکے رسول سے اور نہیں آتے نماز کو مگر ہارے جی سے اور خرچ نہیں کرتے مگر برے دل سے ');
INSERT INTO "al_hind" VALUES(9,55,'9:55','سو تو تعجب نہ کر ان کے مال اور اولاد سے یہی چاہتا ہے اللہ کہ انکو عذاب میں رکھے ان چیزوں کی وجہ دنیا کی زندگی میں اور نکلے ان کی جان اور وہ اس وقت تک کافر ہی رہیں ');
INSERT INTO "al_hind" VALUES(9,56,'9:56','اور قسمیں کھاتے ہیں اللہ کی کہ وہ بیشک تم میں ہیں اور وہ تم میں نہیں و لیکن وہ لوگ ڈرتے ہیں تم سے');
INSERT INTO "al_hind" VALUES(9,57,'9:57','اگر وہ پائیں کوئی پناہ کی جگہ یا غار یا سر گھسانے کو جگہ تو الٹے بھاگیں اسی طرف رسیاں تڑاتے ');
INSERT INTO "al_hind" VALUES(9,58,'9:58','اور بعضے ان میں وہ ہیں کہ تجھ کو طعن دیتےہیں خیرات بانٹنے میں سو اگر ان کو ملے اس میں سے تو راضی ہوں اور اگر نہ ملے تو جبہی وہ ناخوش ہو جائیں ');
INSERT INTO "al_hind" VALUES(9,59,'9:59','اور کیا اچھا ہوتا اگر وہ راضی ہو جاتے اسی پر جو دیا انکو اللہ نے اور اسکے رسول نے اور کہتے کافی ہے ہم کو اللہ وہ دے گا ہم کو اپنے فضل سے اور اس کا رسول ہم کو تو اللہ ہی چاہیئے ');
INSERT INTO "al_hind" VALUES(9,60,'9:60','زکوٰۃ جو ہے سو وہ حق ہے مفلسوں کا اور محتاجوں کا اور زکوٰۃ کے کام پر جانے والوں کا اور جن کا دل پرچانا منظور ہے اور گردنوں کے چھڑانے میں اور جو تاوان بھریں اور اللہ کے رستہ میں اور راہ کے مسافر کو ٹھہرایا ہوا ہے اللہ کا اور اللہ سب کچھ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(9,61,'9:61','اور بعضے ان میں بدگوئی کرتے ہیں نبی کی اور کہتے ہیں کہ یہ شخص تو کان ہے تو کہہ کان ہے تمہارے بھلے کے واسطے یقین رکھتا ہے اللہ پر اور یقین کرتا ہے مسلمانوں کی بات کا اور رحمت ہے ایمان والوں کے حق میں تم میں سے اور جو لوگ بدگوئی کرتے ہیں اللہ کے رسول کی ان کے لئے عذاب ہے دردناک ');
INSERT INTO "al_hind" VALUES(9,62,'9:62','قسمیں کھاتے ہیں اللہ کی تمہارےآگے تاکہ تم کر راضی کریں اور اللہ کو اور اسکے رسول کو بہت ضرور ہے راضی کرنا اگر وہ ایمان رکھتے ہیں ');
INSERT INTO "al_hind" VALUES(9,63,'9:63','کیا وہ جان نہیں چکے کہ جو کوئی مقابلہ کرے اللہ سے اور اسکے رسول سے تو اسکے واسطے ہے دوزخ کی آگ سدا رہے اس میں یہی ہے بڑی رسوائی ');
INSERT INTO "al_hind" VALUES(9,64,'9:64','ڈرا کرتے ہیں منافق اس بات سے کہ نازل ہو مسلمانوں پر ایسی سورت کہ جتا دے انکو جو انکے دل میں ہے تو کہہ دے ٹھٹھے کرتے رہو اللہ کھول کر رہے گا اس چیز کو جس کا تم کو ڈر ہے ');
INSERT INTO "al_hind" VALUES(9,65,'9:65','اور اگر تو ان سے پوچھے تو وہ کہیں گے ہم تو بات چیت کرتےتھے اور دل لگی تو کہہ کیا اللہ سے اور اسکے حکموں سے اور اسکے رسول سے تم ٹھٹھے کرتے تھے ');
INSERT INTO "al_hind" VALUES(9,66,'9:66','بہانے مت بناؤ تم تو کافر ہو گئے اظہار ایمان کے پیچھے اگر ہم معاف کر دیں گے تم میں سے بعضوں کو تو البتہ عذاب بھی دیں گے بعضوں کو اس سبب سےکہ وہ گنہگار تھے ');
INSERT INTO "al_hind" VALUES(9,67,'9:67','منافق مرد اور منافق عورتیں سب کی ایک چال ہے سکھائیں بات بری اور چھڑائیں بات بھلی اوربند رکھیں اپنی مٹھی بھول گئے اللہ کو سو وہ بھول گیا انکو تحقیق منافق وہی ہیں نافرمان ');
INSERT INTO "al_hind" VALUES(9,68,'9:68','وعدہ دیا ہے اللہ نے منافق مرد اور منافق عورتوں کو اور کافروں کو دوزخ کی آگ کا پڑے رہیں گے اس میں وہی بس ہے انکو اور اللہ نے انکو پھٹکار دیا اور انکے لئے عذاب ہے برقرار رہنے والا ');
INSERT INTO "al_hind" VALUES(9,69,'9:69','جس طرح تم سے اگلے لوگ زیادہ تھے تم سے زور میں اور زیادہ رکھتے تھے مال اور اولاد پھر فائدہ اٹھا گئے اپنے حصہ سے پھر فائدہ اٹھایا تم نے اپنے حصہ سے جیسے فائدہ اٹھا گئے تم سے اگلے اپنے حصہ سے اور تم بھی چلتے ہو انہی کی سی چال وہ لوگ مٹ گئے ان کے عمل دنیا میں اور آخرت میں اور وہی لوگ پڑے نقصان میں ');
INSERT INTO "al_hind" VALUES(9,70,'9:70','کیا پہنچی نہیں انکو خبر ان لوگوں کی جو ان سے پہلے تھے قوم نوح کی اور عاد کی اور ثمود کی اور قوم ابراہیم کی اور مدین والوں کی اور ان بستیوں کی خبر جو الٹ دی گئ تھیں پہنچے انکے پاس انکے رسول صاف حکم لے کر سو اللہ تو ایسا نہ تھا کہ ان پر ظلم کرتا لیکن وہ اپنے اوپر آپ ظلم کرتے تھے ');
INSERT INTO "al_hind" VALUES(9,71,'9:71','اور ایمان والے مرد اور ایمان والی عورتیں ایک دوسرے کی مددگار ہیں سکھلاتے ہیں نیک بات اور منع کرتے ہیں بری بات سے اور قائم رکھتے ہیں نماز اور دیتے ہیں زکوٰۃ اور حکم پر چلتے ہیں اللہ کے اور اسکے رسول کے وہی لوگ ہیں جن پر رحم کرے گا اللہ بیشک اللہ زبردست ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(9,72,'9:72','وعدہ دیا ہے اللہ نے ایمان والے مردوں اور ایمان والی عورتوں کو باغوں کا کہ بہتی ہیں نیچے ان کے نہریں رہا کریں انہی میں اور ستھرے مکانوں کا رہنے کے باغوں میں اور رضامندی اللہ کی ان سب سے بڑی ہے یہی ہے بڑی کامیابی ');
INSERT INTO "al_hind" VALUES(9,73,'9:73','اے نبی لڑائی کر کافروں سے اور منافقوں سے اور تندخوئی کر ان پر اور ان کا ٹھکانہ دوزخ ہے اور وہ برا ٹھکانہ ہے ');
INSERT INTO "al_hind" VALUES(9,74,'9:74','قسمیں کھاتے ہیں اللہ کی کہ ہم نے نہیں کہا اور بیشک کہا ہے انہوں نے لفظ کفر کا اور منکر ہو گئے مسلمان ہو کر اور قصد کیا تھا اس چیز کا جو ان کو نہ ملی اور یہ سب کچھ اسی کا بدلہ تھا کہ دولتمند کر دیا ان کو اللہ نے اور اسکے رسول نے اپنے فضل سےسو اگر توبہ کر لیں تو بھلا ہے ان کے حق میں اور اگر نہ مانیں گے تو عذاب دے گا انکو اللہ عذاب دردناک دنیا اور آخرت میں اور نہیں ان کا روئے زمین پر کوئی حمایتی اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(9,75,'9:75','اور بعضے ان میں وہ ہیں کہ عہد کیا تھا اللہ سے اگر دیوے ہم کو اپنے فضل سے تو ہم ضرور خیرات کریں اور ہو رہیں ہم نیکی والوں میں');
INSERT INTO "al_hind" VALUES(9,76,'9:76','پھر جب دیا انکو اپنے فضل سے تو اس میں بخل کیا اور پھر گئے ٹلا کر ');
INSERT INTO "al_hind" VALUES(9,77,'9:77','پھر اس کا اثر رکھ دیا نفاق انکے دلوں میں جس دن تک کہ وہ اس سے ملیں گے اس وجہ سے کہ انہوں نے خلاف کیا اللہ سے جو وعدہ اس سے کیا تھا اور اس وجہ سے کہ بولتے تھے جھوٹ ');
INSERT INTO "al_hind" VALUES(9,78,'9:78','کیا وہ جان نہیں چکے کہ اللہ جانتا ہے ان کا بھید اور ان کا مشورہ اور یہ کہ اللہ خوب جانتا ہے سب چھپی باتوں کو ');
INSERT INTO "al_hind" VALUES(9,79,'9:79','وہ لوگ جو طعن کرتے ہیں ان مسلمانوں پر جو دل کھول کر خیرات کرتے ہیں اور ان پر جو نہیں رکھتے مگر اپنی محنت کا پھر ان پر ٹھٹھے کرتے ہیں اللہ نے ان سے ٹھٹھا کیا ہے اور ان کے لئے عذاب دردناک ہے ');
INSERT INTO "al_hind" VALUES(9,80,'9:80','تو انکے لئے بخشش مانگ یا نہ مانگ اگر ان کے لئےستر بار بخشش مانگے تو بھی ہرگز نہ بخشے گا انکو اللہ یہ اس واسطے کہ وہ منکر ہوئے اللہ سے اور اس کے رسول سے اور اللہ رستہ نہیں دیتا نافرمان لوگوں کو ');
INSERT INTO "al_hind" VALUES(9,81,'9:81','خوش ہو گئے پیچھے رہنے والے اپنے بیٹھ رہنے سے جدا ہو کر رسول اللہ سے اور گھبرائے اس سے کہ لڑیں اپنے مال سےاور جان سے اللہ کی راہ میں اور بولے کہ مت کوچ کرو گرمی میں تو کہہ دوزخ کی آگ سخت گرم ہے اگر ان کو سمجھ ہوتی ');
INSERT INTO "al_hind" VALUES(9,82,'9:82','سو وہ ہنس لیویں تھوڑ ا اور روویں بہت سا بدلا اس کا جو وہ کماتے تھے ');
INSERT INTO "al_hind" VALUES(9,83,'9:83','سو اگر پھر لے جائے تجھ کو اللہ کسی فرقہ کی طرف ان میں سے پھر اجازت چاہیں تجھ سے نکلنے کی تو تو کہہ دینا کہ تم ہرگز نہ نکلو گے میرے ساتھ کبھی اور نہ لڑو گے میرے ساتھ ہو کر کسی دشمن سے تم کو پسند آیا بیٹھ رہنا پہلی بار سو بیٹھے رہو پیچھے رہنے والوں کے ساتھ ');
INSERT INTO "al_hind" VALUES(9,84,'9:84','اور نماز نہ پڑھ ان میں سے کسی پر جو مر جائے کبھی اور نہ کھڑا ہو اس کی قبر پر وہ منکر ہوئے اللہ سے اور اسکے رسول سے اور وہ مر گئے نافرمان ');
INSERT INTO "al_hind" VALUES(9,85,'9:85','اور تعجب نہ کر ان کے مال اور اولاد سے اللہ تو یہی چاہتا ہے کہ عذاب میں رکھے انکو ان چیزوں کے باعث دنیا میں اور نکلے ان کی جان اور وہ اس وقت تک کافر ہی رہیں ');
INSERT INTO "al_hind" VALUES(9,86,'9:86','اور جب نازل ہوتی ہے کوئی سورت کہ ایمان لاؤ اللہ پر اور لڑائی کرو اسکے رسول کے ساتھ ہو کر جو تجھ سے رخصت مانگتے ہیں مقدور والے انکے اور کہتے ہیں ہم کو چھوڑ دے کہ رہ جائیں ساتھ بیٹھنے والوں کے');
INSERT INTO "al_hind" VALUES(9,87,'9:87','خوش ہوئے کہ رہ جائیں پیچھے رہنے والی عورتوں کے ساتھ اور مہر کر دی گئی ان کے دل پر سو وہ نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(9,88,'9:88','لیکن رسول اور جو لوگ ایمان لائے ہیں ساتھ اسکے وہ لڑے ہیں اپنے مال اور جان سے اور انہی کے لئے ہیں خوبیاں اور وہی ہیں مراد کو پہنچنے والے');
INSERT INTO "al_hind" VALUES(9,89,'9:89','تیار کر رکھے ہیں اللہ نےانکے واسطے باغ کہ بہتی ہیں نیچے انکے نہریں رہا کریں ان میں یہی ہے بڑی کامیابی ');
INSERT INTO "al_hind" VALUES(9,90,'9:90','اور آئے بہانہ کرنے والے گنوار تاکہ انکو رخصت مل جائے اور بیٹھ رہے جنہوں نے جھوٹ بولا تھا اللہ سے اور اسکے رسول سے اب پہنچے گا انکو جو کافر ہیں ان میں عذاب دردناک ');
INSERT INTO "al_hind" VALUES(9,91,'9:91','نہیں ہے ضعیفوں پر اور نہ مریضوں پر اور نہ ان لوگوں پر جنکے پاس نہیں ہے خرچ کرنے کو کچھ گناہ جبکہ دل سےصاف ہوں اللہ اور اسکے رسول کے ساتھ نہیں ہے نیکی والوں پر الزام کی کوئی راہ اور اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(9,92,'9:92','اور نہ ان لوگوں پر کہ جب تیرے پاس آئے تاکہ تو ان کو سواری دے تو نے کہا میرے پاس کوئ چیز نہیں کہ تم کو اس پر سوار کر دوں تو الٹے پھرے اور انکی آنکھوں سے بہتے تھے آنسو اس غم میں کہ نہیں پاتے وہ چیز جو خرچ کریں ');
INSERT INTO "al_hind" VALUES(9,93,'9:93','راہ الزام کی تو ان پر ہے جو رخصت مانگتے ہیں تجھ سے اور وہ مالدار ہیں خوش ہوئے اس بات سے کہ رہ جائیں ساتھ پیچھے رہنے والیوں کے اور مہر کر دی اللہ نے انکے دلوں پر سو وہ نہیں جانتے ');
INSERT INTO "al_hind" VALUES(9,94,'9:94','بہانے لائیں گے تمہارے پاس جب تم پھر کر جاؤ گے ان کی طرف تو کہہ بہانے مت بناؤ ہم ہر گز نہ مانیں گے تمہاری بات ہم کو بتا چکا ہے اللہ تمہارے احوال اور ابھی دیکھے گا اللہ تمہارےکام اور اس کا رسول پھر تم لوٹائے جاؤ گے طرف اس جاننے والے چھپے اور کھلے کی سو وہ بتائے گا تم کو جو تم کر رہے تھے ');
INSERT INTO "al_hind" VALUES(9,95,'9:95','اب قسمیں کھائیں گے اللہ کی تمہارے سامنے جب تم پھر کر جاؤ گے ان کی طرف تاکہ ان سے درگذر کرو سو تم درگذر کرو ان سے بیشک وہ لوگ پلید ہیں اور ان کا ٹھکانہ دوزخ ہے بدلا ان کے کاموں کا ');
INSERT INTO "al_hind" VALUES(9,96,'9:96','وہ لوگ قسمیں کھائیں گے تمہارے سامنے تاکہ تم ان سے راضی ہو جاؤ سو اگر تم راضی ہو گئے ان سے تو اللہ راضی نہیں ہوتا نافرمان لوگوں سے ');
INSERT INTO "al_hind" VALUES(9,97,'9:97','گنوار بہت سخت ہیں کفر میں اور نفاق میں اور اسی لائق ہیں کہ نہ سیکھیں وہ قاعدے جو نازل کئے اللہ نے اپنے رسول پر اور اللہ سب کچھ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(9,98,'9:98','اور بعضے گنوار ایسے ہیں کہ شمار کرتے ہیں اپنے خرچ کرنے کو تاوان اور انتظار کرتے ہیں تم پر زمانہ کی گردشوں کا ان ہی پر آئے گردش بری اور اللہ سننے والا جاننے والا ہے ');
INSERT INTO "al_hind" VALUES(9,99,'9:99','اور بعضے گنوار وہ ہیں کہ ایمان لاتے ہیں اللہ پر اور قیامت کے دن پر اور شمار کرتے ہیں اپنے خرچ کرنے کو نزدیک ہونا اللہ سے اور دعا لینی رسول کی سنتا ہے وہ ان کے حق میں نزدیکی ہے داخل کرے گا انکو اللہ اپنی رحمت میں بیشک اللہ بخشننے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(9,100,'9:100','اور جو لوگ قدیم ہیں سب سے پہلے ہجرت کرنے والے اور مدد کرنے والے اور جو انکے پیرو ہوئے نیکی کے ساتھ اللہ راضی ہوا ان سے اور وہ راضی ہوئے اس سے اور تیار کر رکھے ہیں واسطے انکے باغ کہ بہتی ہیں نیچے ان کےنہریں رہا کریں انہی میں ہمیشہ یہی ہے بڑی کامیابی ');
INSERT INTO "al_hind" VALUES(9,101,'9:101','اور بعضے تمہارے گرد کے گنوار منافق ہیں اور بعضے لوگ مدینہ والے اڑ رہے ہیں نفاق پر تو انکو نہیں جانتا ہم کو وہ معلوم ہیں انکو ہم عذاب دیں گے دو بار پھر وہ لوٹائے جائیں گے بڑے عذاب کی طرف ');
INSERT INTO "al_hind" VALUES(9,102,'9:102','اور بعضے لوگ ہیں کہ اقرار کیا انہوں نے اپنے گناہوں کا ملایا انہوں نے ایک کام نیک اور دوسرا بد قریب ہے کہ اللہ معاف کرے انکو بیشک اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(9,103,'9:103','لے انکے مال میں سے زکوٰۃ کہ پاک کرے تو انکو اور بابرکت کرے تو انکو اسکی وجہ سے اور دعا دے انکو بیشک تیری دعا ان کے لئے تسکین ہے اور اللہ سب کچھ سنتا جانتا ہے ');
INSERT INTO "al_hind" VALUES(9,104,'9:104','کیا وہ جان نہیں چکے کہ اللہ آپ قبول کرتا ہے توبہ اپنے بندوں سے اور لیتا ہے زکوٰتیں اور یہ کہ اللہ ہی توبہ قبول کرنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(9,105,'9:105','اور کہہ کہ عمل کئے جاؤ پھر آگے دیکھ لے گا اللہ تمہارے کام کو اور اس کا رسول اور مسلمان اور تم جلد لوٹائے جاؤ گے اسکے پاس جو تمام چھپی اور کھلی چیزوں سے واقف ہے پھر وہ جتا دے گا تم کو جو کچھ تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(9,106,'9:106','اور بعضے اور لوگ ہیں کہ انکا کام ڈھیل میں ہے حکم پر اللہ کے یا وہ انکو عذاب دے اور یا انکو معاف کرے اور اللہ سب کچھ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(9,107,'9:107','اور جنہوں نے بنائی ہے ایک مسجد ضد پر اور کفر پر اور پھوٹ ڈالنے کو مسلمانوں میں اور گھات لگانے کو اس شخص کی جو لڑ رہا ہے اللہ سے اور اسکے رسول سے پہلے سے اور وہ قسمیں کھائیں گے کہ ہم نے تو بھلائی ہی چاہی تھی اور اللہ گواہ ہے کہ وہ جھوٹے ہیں ');
INSERT INTO "al_hind" VALUES(9,108,'9:108','تو نہ کھڑا ہو اس میں کبھی البتہ وہ مسجد جسکی بنیاد دھری گئ پرہیزگاری پر اول دن سے وہ لائق ہے کہ تو کھڑا ہو اسمیں اس میں ایسےلوگ ہیں جو دوست رکھتے ہیں پاک رہنے کو اور اللہ دوست رکھتا ہے پاک رہنے والوں کو ');
INSERT INTO "al_hind" VALUES(9,109,'9:109','بھلا جس نے بنیاد رکھی اپنی عمارت کی اللہ سے ڈرنے پر اور اسکی رضامندی پر وہ بہتر یا جس نے بنیاد رکھی اپنی عمارت کی کنارہ پر ایک کھائی کے جو گرنے کو ہے پھر اسکو لیکر ڈھے پڑا دوزخ کی آگ میں اور اللہ راہ نہیں دیتا ظالم لوگوں کو ');
INSERT INTO "al_hind" VALUES(9,110,'9:110','ہمیشہ رہے گا اس عمارت سے جو انہوں نے بنائی تھی شبہ انکے دلوں میں مگر جب ٹکڑے ہو جائیں انکے دل کے اور اللہ سب کچھ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(9,111,'9:111','اللہ نے خرید لی مسلمانوں سے ان کی جان اور ان کا مال اس قیمت پر کہ انکے لئے جنت ہے لڑتے ہیں اللہ کی راہ میں پھر مارتے ہیں اور مرتے ہیں وعدہ ہو چکا اسکے ذمہ پر سچا توریت اور انجیل اور قرآن میں اور کون ہے قول کا پورا اللہ سے زیادہ سو خوشیاں کرو اس معاملہ پر جو تم نے کیا ہے اس سے اور یہ یہی ہے بڑی کامیابی ');
INSERT INTO "al_hind" VALUES(9,112,'9:112','وہ توبہ کرنے والے ہیں بندگی کرنے والے شکر کرنے والے بے تعلق رہنے والے رکوع کرنے والے سجدہ کرنے والے حکم کرنے والے نیک بات کا اور منع کرنے والے بری بات سے اور حفاظت کرنے والے ان حدود کی جو باندھی اللہ نے اور خوشخبری سنا دے ایمان والوں کو ');
INSERT INTO "al_hind" VALUES(9,113,'9:113','لائق نہیں نبی کو اور مسلمانوں کو کہ بخشش چاہیں مشرکوں کی اور اگرچہ وہ ہوں قرابت والے جب کہ کھل چکا ان پر کہ وہ ہیں دوزخ والے ');
INSERT INTO "al_hind" VALUES(9,114,'9:114','اور بخشش مانگنا ابراہیم کا اپنے باپ کے واسطے سو نہ تھا مگر وعدہ کے سبب کہ وعدہ کر چکا تھا اس سے پھر جب کھل گیا ابراہیم پر کہ وہ دشمن ہے اللہ کا تو اس سے بیزار ہو گیا بیشک ابراہیم بڑا نرم دل تھا تحمل کرنے والا ');
INSERT INTO "al_hind" VALUES(9,115,'9:115','اور اللہ ایسا نہیں کہ گمراہ کرے کسی قوم کو جبکہ ان کو راہ پر لا چکا جب تک کھول نہ دے ان پر جس سے انکو بچنا چاہئے بیشک اللہ ہر چیز سے واقف ہے ');
INSERT INTO "al_hind" VALUES(9,116,'9:116','اللہ ہی کی ہے سلطنت ہے آسمانوں اور زمین میں جلاتا ہے اور مارتا ہے اور تمہارا کوئی نہیں اللہ کے سوا حمایتی اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(9,117,'9:117','اللہ مہربان ہوا نبی پر اور مہاجرین اور انصار پر جو ساتھ رہے نبی کے مشکل کی گھڑی میں بعد اسکے کہ قریب تھا کہ دل پھر جائیں بعضوں کے ان میں سے پھر مہربان ہوا ان پر بیشک وہ ان پر مہربان ہے رحم کرنے والا ');
INSERT INTO "al_hind" VALUES(9,118,'9:118','اور ان تین شخصوں پر جن کو پیچھے رکھا تھا یہاں تک کہ جب تنگ ہو گئی ان پر زمین باوجود کشادہ ہونے کے اور تنگ ہو گئیں ان پر ان کی جانیں اور سمجھ گئے کہ کہیں پناہ نہیں اللہ سے مگر اس کی طر ف پھر مہربان ہوا ان پر تاکہ وہ پھر آئیں بیشک اللہ ہی ہے مہربان رحم والا ');
INSERT INTO "al_hind" VALUES(9,119,'9:119','اے ایمان والو ڈرتے رہو اللہ سے اور رہو ساتھ سچوں کے ');
INSERT INTO "al_hind" VALUES(9,120,'9:120','نہ چاہئے مدینہ والوں کو اور انکے گرد کے گنواروں کو کہ پیچھے رہ جائیں رسول اللہ کے ساتھ سے اور نہ یہ کہ اپنی جان کو چاہیں زیادہ رسول کی جان سے یہ اس واسطے کہ جہاد کرنے والے نہیں پہنچتی انکو پیاس اور نہ محنت اور نہ بھوک اللہ کی راہ میں اور نہیں قدم رکھتے کہیں جس سے کہ خفا ہوں کافر اور نہ چھینتے ہیں دشمن سے کوئی چیز مگر لکھا جاتا ہے ان کے واسطے اسکے بدلے نیک عمل بیشک اللہ نہیں ضائع کرتا حق نیکی کرنے والوں کا ');
INSERT INTO "al_hind" VALUES(9,121,'9:121','اور نہ خرچ کرتے ہیں کوئی خرچ چھوٹا اور نہ بڑا اور نہ طے کرتے ہیں کوئی میدان مگر لکھ لیا جاتا ہے انکے واسطے تاکہ بدلا دے ان کو اللہ بہتر اس کام کا جو کرتے تھے ');
INSERT INTO "al_hind" VALUES(9,122,'9:122','اور ایسے تو نہیں مسلمان کہ کوچ کریں سارے سو کیوں نہ نکلا ہر فرقہ میں سے ان کا ایک حصہ تاکہ سمجھ پیدا کریں دین میں اور تاکہ خبر پہنچائیں اپنی قوم کو جب کہ لوٹ کر آئیں ان کی طرف تاکہ وہ بچتے رہیں ');
INSERT INTO "al_hind" VALUES(9,123,'9:123','اے ایمان والو لڑتے جاؤ اپنے نزدیک کے کافروں سے اور چاہئے کہ ان پر معلوم ہو تمہارے اندر سختی اور جانو کہ اللہ ساتھ ہے ڈر والوں کے ');
INSERT INTO "al_hind" VALUES(9,124,'9:124','اور جب نازل ہوتی ہے کوئی سورت تو بعضے ان میں کہتے ہیں کس کا تم میں سے زیادہ کر دیا اس سورت نے ایمان سو جو لوگ ایمان رکھتے ہیں ان کا زیادہ کر دیا اس سورت نے ایمان اور وہ خوش وقت ہوتے ہیں');
INSERT INTO "al_hind" VALUES(9,125,'9:125','اور جنکے دل میں مرض ہے سو انکے لئے بڑھا دی گندگی پر گندگی اور وہ مرنے تک کافر ہی رہے ');
INSERT INTO "al_hind" VALUES(9,126,'9:126','کیا نہیں دیکھتے کہ وہ آزمائے جاتے ہیں ہر برس میں ایک بار یا دو بار پھر بھی توبہ نہیں کرتے اور نہ وہ نصیحت پکڑتے ہیں ');
INSERT INTO "al_hind" VALUES(9,127,'9:127','اور جب نازل ہوتی ہے کوئی سورت تو دیکھنے لگتا ہے ان میں ایک دوسرے کی طرف کہ کیا دیکھتا ہے تم کو کوئی مسلمان پھر چل دیتےہیں پھیر دیے ہیں اللہ نے دل ان کے اس واسطے کہ وہ لوگ ہیں کہ سمجھ نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(9,128,'9:128','آیا ہے تمہارے پاس رسول تم میں کا بھاری ہے اس پر جو تم کو تکلیف پہنچے حریص ہے تمہارے بھلائی پر ایمان والوں پر نہایت شفیق مہربان ہے ');
INSERT INTO "al_hind" VALUES(9,129,'9:129','پھر بھی اگر منہ پھیریں تو کہہ دے کہ کافی ہے مجھ کو اللہ کسی کی بندگی نہیں اسکے سوا اسی پر میں نے بھروسہ کیا اور وہی مالک ہے عرش عظیم کا ');
INSERT INTO "al_hind" VALUES(10,1,'10:1','یہ آیتیں ہیں پکی کتاب کی ');
INSERT INTO "al_hind" VALUES(10,2,'10:2','کیا لوگوں کو تعجب ہوا کہ وحی بھیجی ہم نے ایک مرد پر ان میں سے یہ کہ ڈر سنا دے لوگوں کو اور خوشخبری سنا دے ایمان لانے والوں کو کہ ان کے لئے پایہ سچا ہے اپنے رب کے یہاں کہنے لگے منکر بیشک یہ تو جادوگر ہے صریح ');
INSERT INTO "al_hind" VALUES(10,3,'10:3','تحقیق تمہارا رب اللہ ہے جس نے بنائے آسمان اور زمین چھ دن میں پھر قائم ہوا عرش پر تدبیر کرتا ہے کام کی کوئی سفارش نہیں کر سکتا مگر اس کی اجازت کے بعد وہ اللہ ہے رب تمہارا سو اسکی بندگی کرو کیا تم دھیان نہیں کرتے ');
INSERT INTO "al_hind" VALUES(10,4,'10:4','اسی کی طرف لوٹ کر جانا ہے تم سب کو وعدہ ہے اللہ کا سچا وہی پیدا کرتا ہے اول بار پھر دوبارہ کرے گا اس کو تاکہ بدلا دے انکو جو ایمان لائے تھے اور کئے تھے کام نیک انصاف کے ساتھ اور جو کافر ہوئے ان کو پینا ہے کھولتا پانی اور عذاب ہے دردناک اس لئے کہ کفر کرتے تھے');
INSERT INTO "al_hind" VALUES(10,5,'10:5','وہی ہے جس نے بنایا سورج کو چمک (چمکتا) اور چاند کو چاندنا اور مقرر کیں اسکے لئے منزلیں تاکہ پہچانو گنتی برسوں کی اور حساب یوں ہی نہیں بنایا اللہ نے یہ سب کچھ مگر تدبیر سے ظاہر کرتا ہے نشانیاں ان لوگوں کے لئے جن کو سمجھ ہے ');
INSERT INTO "al_hind" VALUES(10,6,'10:6','البتہ بدلنے میں رات اور دن کے اور جو کچھ پیدا کیا ہے اللہ نے آسمانوں اور زمین میں نشانیاں ہیں ان لوگوں کو جو ڈرتے ہیں ');
INSERT INTO "al_hind" VALUES(10,7,'10:7','البتہ جو لوگ امید نہیں رکھتے ہمارے ملنے کی اور خوش ہوئے دنیا کی زندگی پر اور اسی پر مطمئن ہو گئے اور جو لوگ ہماری نشانیوں (قدرتوں) سے بے خبر ہیں ');
INSERT INTO "al_hind" VALUES(10,8,'10:8','ایسوں کا ٹھکانا ہے آگ بدلا اس کا جو کماتے تھے ');
INSERT INTO "al_hind" VALUES(10,9,'10:9','البتہ جو لوگ ایمان لائے اور کام کئے اچھے ہدایت کرے گا انکو رب ان کا ان کے ایمان سے بہتی ہیں ان کے نیچے نہریں باغوں میں آرام کے');
INSERT INTO "al_hind" VALUES(10,10,'10:10','ان کی دعا اس جگہ یہ کہ پاک ذات ہے تیری یا اللہ اور ملاقات ان کی سلام اور خاتمہ انکی دعا کا اس پر کہ سب خوبی اللہ کو جو پروردگار سارے جہان کا ');
INSERT INTO "al_hind" VALUES(10,11,'10:11','اور اگر جلدی پہنچا دے اللہ لوگوں کو برائی جیسے کہ جلدی مانگتے ہیں وہ بھلائی تو ختم کر دی جائے انکی عمر سو ہم چھوڑے رکھتے ہیں انکو جن کو امید نہیں ہماری ملاقات کی انکی شرارت میں سرگرداں ');
INSERT INTO "al_hind" VALUES(10,12,'10:12','اور جب پہنچے انسان کو تکلیف پکارے ہم کو پڑا ہوا یا بیٹھا یا کھڑا پھر جب ہم کھول دیں اس سے وہ تکلیف چلا جائے گویا کبھی نہ پکارا تھا ہم کو کسی تکلیف پہنچنے پر اسی طرح پسند آیا ہے بیباک لوگوں کو جو کچھ کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(10,13,'10:13','اور البتہ ہم ہلاک کر چکے ہیں جماعتوں کو تم سے پہلے جب ظالم ہو گئے حالانکہ لائے تھے انکے پاس رسول انکے کھلی نشانیاں اور ہرگز نہ تھے ایمان لانے والے یوں ہی سزا دیتے ہیں ہم قوم گنہگاروں کو ');
INSERT INTO "al_hind" VALUES(10,14,'10:14','پھر تم کو ہم نے ناٹب کیا زمین میں ان کے بعد تاکہ دیکھیں تم کیا کرتے ہو ');
INSERT INTO "al_hind" VALUES(10,15,'10:15','اور جب پڑھی جاتی ہیں ان کے سامنے آیتیں ہماری واضح کہتے ہیں وہ لوگ جن کو امید نہیں ہم سے ملاقات کی لے آ کوئی قرآن اس کے سوا یا اس کو بدل ڈال تو کہہ دے میرا کام نہیں کہ اس کو بدل ڈالوں اپنی طرف سے میں تابعداری کرتا ہوں اسی کی جو حکم آئے میری طرف میں ڈرتا ہوں اگر نافرمانی کروں اپنے رب کی بڑے دن کے عذاب سے ');
INSERT INTO "al_hind" VALUES(10,16,'10:16','کہہ دے کہ اگر اللہ چاہتا تو میں نہ پڑھتا اس کو تمہارے سامنے اور نہ وہ تم کو خبر کرتا اسکی کیونکہ میں رہ چکا ہوں تم میں ایک عمر اس سے پہلے کیا پھر تم نہیں سوچتے ');
INSERT INTO "al_hind" VALUES(10,17,'10:17','پھر اس سے بڑا ظالم کون جو باندھے اللہ پر بہتان یا جھٹلائے اس کی آیتوں کو بیشک بھلا نہیں ہوتا گنہگاروں کا ');
INSERT INTO "al_hind" VALUES(10,18,'10:18','اور پرستش کرتے ہیں اللہ کےسوا اس چیز کی جو نہ نقصان پہنچا سکے ان کو نہ نفع اور کہتے ہیں یہ تو ہمارے سفارشی ہیں اللہ کے پاس تو کہہ کیا تم اللہ کو بتلاتے ہو جو اسکو معلوم نہیں آسمانوں میں اور نہ زمین میں وہ پاک ہے اور برتر ہے اس سے جسکو شریک کرتے ہیں ');
INSERT INTO "al_hind" VALUES(10,19,'10:19','اور لوگ جو ہیں سو ایک ہی امت ہیں پیچھے جدا جدا ہو گئے اور اگر نہ ایک بات پہلے ہو چکتی تیرے رب کی تو فیصلہ ہو جاتا ان میں جس بات میں کہ اختلاف کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(10,20,'10:20','اور کہتے ہیں کیوں نہ اتری اس پر ایک نشانی اس کے رب سے سو تو کہہ دے کہ غیب کی بات اللہ ہی جانے سو منتظر رہو میں بھی تمہارے ساتھ انتظار کرتا ہوں ');
INSERT INTO "al_hind" VALUES(10,21,'10:21','اور جب چکھائیں ہم لوگوں کو مزا اپنی رحمت کا بعد ایک تکلیف کے جو ان کو پہنچی تھی اسی وقت بنانے لگیں حیلے ہماری قدرتوں میں کہہ دے کہ اللہ سب سے جلد بنا سکتا ہے حیلے تحقیق ہمارے فرشتے لکھتے ہیں حیلہ بازی تمہاری ');
INSERT INTO "al_hind" VALUES(10,22,'10:22','وہی تم کو پھراتا ہے جنگل اور دریا میں یہاں تک کہ جب تم بیٹھے کشتیوں میں اور لے کر چلیں وہ لوگوں کو اچھی ہوا سے اور خوش ہوئے اس سے آئی کشتیوں پر ہوا تند اور آئی ان پر موج ہر جگہ سے اور جان لیا انہوں نےکہ وہ گھر گئے پکارنے لگے اللہ کو خالص ہو کر اس کی بندگی میں اگر تو نے بچا لیا ہم کو اس سے تو بیشک ہم رہیں گے شکر گذار');
INSERT INTO "al_hind" VALUES(10,23,'10:23','پھر جب بچا دیا ان کو اللہ نے لگے شرارت کرنے اسی وقت زمین میں ناحق کی سنو لوگو تمہاری شرارت ہے تمہی پر نفع اٹھا لو دنیا کی زندگانی کا پھر ہمارے پاس ہے تم کو لوٹ کر آنا پھر ہم بتلا دیں گے جو کچھ کہ تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(10,24,'10:24','دنیا کی زندگانی کی وہی مثل ہے جیسے ہم نے پانی اتارا آسمان سے پھر رلا ملا نکلا اس سے سبزہ زمین کا جو کہ کھائیں آدمی اور جانور یہاں تک کہ جب پکڑی زمین نے رونق اور مزین ہو گئی اور خیال کیا زمین والوں نے کہ یہ ہمارے ہاتھ لگے گی ناگاہ پہنچا اس پر ہمارا حکم رات کو یا دن کو پھر کر ڈالا اس کو کاٹ کر ڈھیر گویا کل یہاں نہ تھی آبادی اسی طرح ہم کھول کر بیان کرتے ہیں نشانیوں کو ان لوگوں کے سامنے جو غور کرتے ہیں ');
INSERT INTO "al_hind" VALUES(10,25,'10:25','اور اللہ بلاتا ہے سلامتی کے گھر کی طرف اور دکھلاتا ہے جس کو چاہے راستہ سیدھا ');
INSERT INTO "al_hind" VALUES(10,26,'10:26','جنہوں نے کی بھلائی انکے لئے ہے بھلائی اور زیادتی اور نہ چڑھے گی ان کے منہ پر سیاہی اور نہ رسوائی وہ ہیں جنت والے وہ اسی میں رہا کریں گے ');
INSERT INTO "al_hind" VALUES(10,27,'10:27','اور جنہوں نے کمائیں برائیاں بدلا ملے برائی کا اس کے برابر اور ڈھانک لے گی انکو رسوائی کوئی نہیں انکو اللہ سے بچانے والا گویا کہ ڈھانک دیے گئے انکے چہرے اندھیری رات کے ٹکڑوں سے وہ ہیں دوزخ والے وہ اسی میں رہا کریں گے');
INSERT INTO "al_hind" VALUES(10,28,'10:28','اور جس دن جمع کریں گے ہم ان سب کو پھر کہیں گے شرک کرنے والوں کو کھڑے ہو اپنی اپنی جگہ تم اور تمہارے شریک پھر تڑا دیں گے ہم آپس میں انکو اور کہیں گے ان کے شریک تم ہماری تو بندگی نہ کرتے تھے');
INSERT INTO "al_hind" VALUES(10,29,'10:29','سو اللہ کافی ہے شاہد ہمارے اور تمہارے بیچ میں ہم کو تمہاری بندگی کی خبر نہ تھی ');
INSERT INTO "al_hind" VALUES(10,30,'10:30','وہاں جانچ لے گا ہر کوئی جو اس نے پہلے کیا تھا اور رجوع کریں گے اللہ کی طرف جو سچا مالک ہے ان کا اور جاتا رہے گا انکے پاس سے جو جھوٹ باندھا کرتےتھے ');
INSERT INTO "al_hind" VALUES(10,31,'10:31','تو پوچھ کون روزی دیتا ہے تم کو آسمان سے اور زمین سے یا کون مالک ہے کان اور آنکھوں کا اور کون نکالتا ہے زندہ کو مردہ سے اور نکالتا ہے مردہ کو زندہ سے اور کون تدبیر کرتا ہے کاموں کی سو بو ل اٹھیں گے کہ اللہ تو تو کہہ پھر ڈرتے نہیں ہو');
INSERT INTO "al_hind" VALUES(10,32,'10:32','سو یہ اللہ ہے رب تمہارا سچا پھر کیا رہ گیا سچ کے پیچھے مگر بھٹکنا سو کہاں سے لوٹے جاتے ہو ');
INSERT INTO "al_hind" VALUES(10,33,'10:33','اسی طرح ٹھیک آئی بات تیرے رب کی ان نافرمانوں پر کہ یہ ایمان نہ لائیں گے ');
INSERT INTO "al_hind" VALUES(10,34,'10:34','پوچھ کوئی ہے تمہارے شریکوں میں جو پیدا کرے خلق کو پھر دوبارہ زندہ کرے تو کہہ اللہ پیدا کرتا ہے پھر اس کو دہرائے گا سو کہاں سے پلٹے جاتے ہو ');
INSERT INTO "al_hind" VALUES(10,35,'10:35','پوچھ کوئی ہے تمہارے شریکوں میں جو راہ بتلائے صحیح تو کہہ اللہ راہ بتلاتا ہے صحیح تو اب جو کوئی راہ بتائے صحیح اسکی بات ماننی چاہئے یا اسکی جو آپ نہ پائے راہ مگر جب کوئی اور اسکو راہ بتلائے سو کیا ہو گیا تم کو کیسا انصاف کرتے ہو ');
INSERT INTO "al_hind" VALUES(10,36,'10:36','اور وہ اکثر چلتے ہیں محض اٹکل پر سو اٹکل کام نہیں دیتی حق بات میں کچھ بھی اللہ کو خوب معلوم ہے جو کچھ وہ کرتے ہیں');
INSERT INTO "al_hind" VALUES(10,37,'10:37','اور وہ نہیں یہ قرآن کہ کوئی بنا لے اللہ کےسوا اور لیکن تصدیق کرتا ہے اگلے کلام کی اور بیان کرتا ہے ان چیزوں کو جو تم پر لکھی گئیں جس میں کوئی شبہ نہیں پروردگار عالم کی طرف سے ');
INSERT INTO "al_hind" VALUES(10,38,'10:38','کیا لوگ کہتےہیں کہ یہ بنا لایا ہے تو کہہ دے تم لے آؤ ایک ہی سورت ایسی اور بلا لو جس کو بلا سکو اللہ کے سوا اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(10,39,'10:39','بات یہ ہے کہ جھٹلانے لگے جس کے سمجھنے پر انہوں نےقابو نہ پایا اور ابھی آئی نہیں اسکی حقیقت اسی طرح جھٹلاتے رہے ان سے اگلے سو دیکھ لے کیسا ہوا انجام گنہگاروں کا');
INSERT INTO "al_hind" VALUES(10,40,'10:40','اور بعضے ان میں یقین کریں گے قرآن کا اور بعضے یقین نہ کریں گے اور تیرا رب خوب جانتا ہے شرارت والوں کو ');
INSERT INTO "al_hind" VALUES(10,41,'10:41','اور اگر تجھ کو جھٹلائیں تو کہہ میرے لئے میرا کام اور تمہارے لئے تمہارا کام تم پر ذمہ نہیں میرے کام کا اور مجھ پر ذمہ نہیں جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(10,42,'10:42','اور بعضے ان میں کان رکھتے ہیں تیری طرف کیا تو سنائے گا بہروں کو اگرچہ ان کو سمجھ نہ ہو');
INSERT INTO "al_hind" VALUES(10,43,'10:43','اور بعضے ان میں نگاہ کرتے ہیں تیری طرف کیا تو راہ دکھائے گا اندھوں کو اگرچہ وہ سوجھ نہ رکھتے ہوں ');
INSERT INTO "al_hind" VALUES(10,44,'10:44','اللہ ظلم نہیں کرتا لوگوں پر کچھ بھی لیکن لوگ اپنے اوپر آپ ظلم کرتے ہیں ');
INSERT INTO "al_hind" VALUES(10,45,'10:45','اور جس دن انکو جمع کرے گا گویا وہ نہ رہے تھے مگر ایک گھڑی دن ایک دوسرے کو پہچانیں گے بیشک خسارے میں پڑے جنہوں نے جھٹلایا اللہ سے ملنے کو اور نہ آئے وہ راہ پر ');
INSERT INTO "al_hind" VALUES(10,46,'10:46','اور اگر ہم دکھائیں گے تجھ کو کوئی چیز ان وعدوں میں سے جو کئے ہیں ہم نے ان سے یا وفات دیں تجھ کو سو ہماری ہی طرف ہے ان کو لوٹنا پھر اللہ شاہد ہے ان کاموں پر جو کرتے ہیں ');
INSERT INTO "al_hind" VALUES(10,47,'10:47','اور ہر فرقہ کا ایک رسول ہے پھر جب پہنچا انکے پاس رسول ان کا فیصلہ ہوا ان میں انصاف سے اور ان پر ظلم نہیں ہوتا ');
INSERT INTO "al_hind" VALUES(10,48,'10:48','اور کہتے ہیں کب ہے یہ وعدہ اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(10,49,'10:49','تو کہہ میں مالک نہیں اپنے واسطے برے کا نہ بھلے کا مگر جو چاہے اللہ ہر فرقہ کا ایک وعدہ ہے جب آ پہنچے گا ان کا وعدہ پھر نہ پیچھے سرک سکیں گے ایک گھڑی اور نہ آگے سرک سکیں گے ');
INSERT INTO "al_hind" VALUES(10,50,'10:50','تو کہہ بھلا دیکھو تو اگر آ پہنچے تم پر عذاب اس کا راتوں رات یا دن کو تو کیا کر لیں گے اس سے پہلے گنہگار ');
INSERT INTO "al_hind" VALUES(10,51,'10:51','کیاپھر جب عذاب واقع ہو چکے گا تب اس پر یقین کرو گے اب قائل ہوئے اور تم اسی کا تقاضا کرتے تھے ');
INSERT INTO "al_hind" VALUES(10,52,'10:52','پھر کہیں گے گنہگاروں کو چکھتے رہو عذاب ہمیشگی کا وہی بدلا ملتا ہے جو کچھ کماتے تھے ');
INSERT INTO "al_hind" VALUES(10,53,'10:53','اور تجھ سے خبر پوچھتے ہیں کیا سچ ہے یہ بات تو کہہ البتہ قسم میرے رب کی یہ سچ ہے اور تم تھکا نہ سکو گے ');
INSERT INTO "al_hind" VALUES(10,54,'10:54','اور اگر ہو ہر شخص گنہگار کے پاس جتنا کچھ ہے زمین میں البتہ دے ڈالے اپنے بدلے میں اور چھپے چھپے پچھتائیں گے جب دیکھیں گےعذاب اور ان میں فیصلہ ہو گا انصاف سے اور ان پر ظلم نہ ہو گا ');
INSERT INTO "al_hind" VALUES(10,55,'10:55','سن رکھو اللہ کا ہے جو کچھ ہے آسمان اور زمین میں سن رکھو وعدہ اللہ کا سچ ہے پر بہت لوگ نہیں جانتے ');
INSERT INTO "al_hind" VALUES(10,56,'10:56','وہی جلاتا ہے اور مارتا ہے اور اسی کی طرف پھر جاؤ گے ');
INSERT INTO "al_hind" VALUES(10,57,'10:57','اے لوگو تمہارے پاس آئی ہے نصیحت تمہارے رب سے اور شفا دلوں کے روگ کی اور ہدایت اور رحمت مسلمانوں کے واسطے ');
INSERT INTO "al_hind" VALUES(10,58,'10:58','کہہ اللہ کے فضل سے اور اسکی مہربانی سے سو اسی پر انکو خوش ہونا چاہئے یہ بہت ہے ان چیزوں سے جو جمع کرتے ہیں ');
INSERT INTO "al_hind" VALUES(10,59,'10:59','تو کہہ بھلا دیکھو تو اللہ نے جو اتاری تمہارے واسطے روزی پھر تم نے ٹھہرائی اس میں سے کوئی حرام اور کوئی حلال کہہ کیا اللہ نے حکم دیا تم کو یا اللہ پر افترا کرتے ہو ');
INSERT INTO "al_hind" VALUES(10,60,'10:60','اور کیا خیال ہے جھوٹ باندھنے والوں کا اللہ پر قیامت کے دن اللہ تو فضل کرتا ہے لوگوں پر اور لیکن بہت لوگ حق نہیں مانتے ');
INSERT INTO "al_hind" VALUES(10,61,'10:61','اور نہیں ہوتا تو کسی حال میں اور نہ پڑھتا ہے اس میں سے کچھ قرآن اور نہیں کرتے ہو تم لوگ کچھ کام کہ ہم نہیں ہوتے حاضر تمہارے پاس جب تم مصروف ہوتے ہو اس میں اور غائب نہیں رہتا تیرے رب سے ایک ذرہ بھر زمین میں اور نہ آسمان میں اور نہ چھوٹا اس سے اور نہ بڑا جو نہیں ہے کھلی ہوئی کتاب میں ');
INSERT INTO "al_hind" VALUES(10,62,'10:62','یاد رکھو جو لوگ اللہ کے دوست ہیں نہ ڈر ہے ان پر اور نہ وہ غمگین ہوں گے ');
INSERT INTO "al_hind" VALUES(10,63,'10:63','جو لوگ کہ ایمان لائے اور ڈرتے رہے ');
INSERT INTO "al_hind" VALUES(10,64,'10:64','ان کے لئے ہے خوشخبری دنیا کی زندگانی میں اور آخرت میں بدلتی نہیں اللہ کی باتیں یہی ہے بڑی کامیابی');
INSERT INTO "al_hind" VALUES(10,65,'10:65','اور رنج مت کر ان کی بات سے اصل میں سب زور اللہ کے لئے ہے وہی ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(10,66,'10:66','سنتا ہے بیشک اللہ کا ہے جو کوئی ہے آسمانوں میں اور جو کوئی ہے زمین میں اور یہ جو پیچھے پڑے ہیں اللہ کے سوا شریکوں کو پکارنے والے سو یہ کچھ نہیں مگر پیچھے پڑے ہیں اپنے خیال کے اور کچھ نہیں مگر اٹکلیں دوڑاتے ہیں ');
INSERT INTO "al_hind" VALUES(10,67,'10:67','وہی ہے جس نے بنایا تمہارے واسطے رات کو کہ چین حاصل کرو اس میں اور دن دیا دکھلانے والا بیشک اس میں نشانیاں ہیں ان لوگوں کے لئے جو سنتے ہیں ');
INSERT INTO "al_hind" VALUES(10,68,'10:68','کہتےہیں ٹھہرا لیا اللہ نے بیٹا وہ پاک ہے وہ بے نیاز ہے اسی کا ہے جو کچھ ہے آسمانوں میں اور جو کچھ ہے زمین میں نہیں تمہارےپاس کوئی سند اس کی کیوں جھوٹ کہتے ہو اللہ پر جس بات کی تم کو خبر نہیں ');
INSERT INTO "al_hind" VALUES(10,69,'10:69','کہہ جو لوگ باندھتے ہیں اللہ پر جھوٹ بھلائی نہیں پاتے');
INSERT INTO "al_hind" VALUES(10,70,'10:70','تھوڑا سا نفع اٹھا لینا دنیا میں پھر ہماری طرف ہے ان کو لوٹنا پھر چکھائیں گے ہم انکو سخت عذاب بدلا ان کے کفر کا ');
INSERT INTO "al_hind" VALUES(10,71,'10:71','اور سنا ان کو حال نوح کا جب کہا اپنی قوم کو اے قوم اگر بھاری ہوا ہے تم پر میرا کھڑا ہونا اور نصیحت کرنا اللہ کی آیتوں سےتو میں نے اللہ پر بھروسہ کیا اب تم سب مل کر مقرر کرو اپنا کام اور جمع کرو اپنے شریکوں کو پھر نہ رہے تم کو اپنے کام میں شبہ پھر کر گذرو میرے ساتھ اور مجھ کو مہلت نہ دو ');
INSERT INTO "al_hind" VALUES(10,72,'10:72','پرھ اگر منہ پھیرو گے تو میں نے نہیں چاہی تم سے مزدروی میری مزدوری ہے اللہ پر اور مجھ کو حکم ہے کہ رہوں فرمانبردار ');
INSERT INTO "al_hind" VALUES(10,73,'10:73','پھر اس کو جھٹلایا سو ہم نے بچا لیا اسکو اور جو اس کے ساتھ تھے کشتی میں اور ان کو قائم کر دیا جگہ پر اور ڈبا دیا ان کو جو جھٹلاتے تھے ہماری باتوں کو سو دیکھ لے کیسا ہوا انجام ان کا جن کو ڈرایا تھا ');
INSERT INTO "al_hind" VALUES(10,74,'10:74','پھر بھیجے ہم نے نوح کے بعد کتنے پیغمبر ان کی قوم کی طرف پھر لائے انکے پاس کھلی دلیلیں سو ان سے یہ نہ ہوا کہ ایمان لے آئیں اس بات پر جسکو جھٹلا چکے تھے پہلے سے اسی طرح ہم مہر لگا دیتے ہیں دلوں پر حد سے نکل جانے والوں کے ');
INSERT INTO "al_hind" VALUES(10,75,'10:75','پھر بھیجا ہم نے ان کے پیچھے موسٰی اور ہارون کو فرعون اور اس کے سرداروں کے پاس اپنی نشانیاں دے کر پھر تکبر کرنے لگے اور وہ تھے لوگ گنہگار ');
INSERT INTO "al_hind" VALUES(10,76,'10:76','پھر جب پہنچی ان کو سچی بات ہمارے پاس سے کہنے لگے یہ تو جادو ہے کھلا ');
INSERT INTO "al_hind" VALUES(10,77,'10:77','کہا موسٰی نے کیا تم یہ کہتے ہو حق بات کو جب وہ پہنچے تمہارے پاس کیا یہ جادو ہے اور نجات نہیں پاتے جادو کرنے والے ');
INSERT INTO "al_hind" VALUES(10,78,'10:78','بولے کیا تو آیا ہے کہ ہم کو پھیر دے اس رستہ سے جس پر پایا ہم نے اپنے باپ دادوں کو اور تم دونوں کو سرداری مل جائے اس ملک میں اور ہم نہیں ہیں تم کو ماننے والے ');
INSERT INTO "al_hind" VALUES(10,79,'10:79','اور بولا فرعون لاؤ میرے پاس جو جادوگر ہو پڑھا ہوا ');
INSERT INTO "al_hind" VALUES(10,80,'10:80','پھر جب آئے جادوگر کہا ان کو موسٰی نے ڈالو جو تم ڈالتے ہو ');
INSERT INTO "al_hind" VALUES(10,81,'10:81','پھر جب انہوں نے ڈالا موسٰی بولا کہ جو تم لائے ہو سو جادو ہے اب اللہ اسکو بگاڑتا ہے بیشک اللہ نہیں سنوارتا شریروں کے کام ');
INSERT INTO "al_hind" VALUES(10,82,'10:82','اور اللہ سچا کرتا ہے حق بات کو اپنے حکم سے اور پڑے برا مانیں گنہگار');
INSERT INTO "al_hind" VALUES(10,83,'10:83','پھر کوئی ایمان نہ لایا موسٰی پر مگر کچھ لڑکے اس کی قوم کے ڈرتے ہوئے فرعون سے اور انکے سرداروں سے کہ کہیں انکو بچلا نہ دے اور فرعون چڑھ رہا ہے ملک میں اور اس نے ہاتھ چھوڑ رکھا ہے ');
INSERT INTO "al_hind" VALUES(10,84,'10:84','اور کہا موسٰی نے اے میری قوم اگر تم ایمان لائے ہو اللہ پر تو اسی پر بھروسہ کرو اگر ہو تم فرمانبردار ');
INSERT INTO "al_hind" VALUES(10,85,'10:85','تب وہ بولے ہم نے اللہ پر بھرسہ کیا اے رب ہمارے نہ آزما ہم پر زور اس ظالم قوم کا ');
INSERT INTO "al_hind" VALUES(10,86,'10:86','اور چھڑا دے ہم کو مہربانی فرما کر ان کافر لوگوں سے ');
INSERT INTO "al_hind" VALUES(10,87,'10:87','اور حکم بھیجا ہم نے موسٰی کو اور اسکے بھائی کو کہ مقرر کرو اپنی قوم کے واسطے مصر میں سے گھر اور بناؤ اپنے گھر قبلہ رو اور قائم کرو نماز اور خوشخبری دے ایمان والوں کو ');
INSERT INTO "al_hind" VALUES(10,88,'10:88','اور کہا موسٰی نے اے رب ہمارے تو نے دی ہے فرعون کو اور اس کے سرداروں کو رونق اور مال دنیا کی زندگی میں اے رب اس واسطے کہ بہکائیں تیری راہ سے اے رب مٹا دے ان کے مال اور سخت کر دے ان کے دل کہ نہ ایمان لائیں جب تک دیکھ لیں عذاب دردناک ');
INSERT INTO "al_hind" VALUES(10,89,'10:89','فرمایا قبول ہو چکی دعا تمہاری سو تم دونوں ثابت رہو اور مت چلو راہ انکی جو ناواقف ہیں ');
INSERT INTO "al_hind" VALUES(10,90,'10:90','اور پار کر دیا ہم نے بنی اسرائیل کو دریا سے پھرپیچھا کیا ان کا فرعون نے اور اس کے لشکر نے شرارت سے اور تعدی سے یہاں تک کہ جب ڈوبنے لگا بولا یقین کر لیا میں نے کہ کوئی معبود نہیں مگر جس پر کہ ایمان لائے بنی اسرائیل اور میں ہوں فرمانبرداروں میں');
INSERT INTO "al_hind" VALUES(10,91,'10:91','اب یہ کہتا ہے اور تو نافرمانی کرتا رہا اس سے پہلے اور رہا گمراہوں میں ');
INSERT INTO "al_hind" VALUES(10,92,'10:92','سو آج بچائے دیتے ہیں ہم تیرے بدن کو تاکہ ہووے تو اپنے پچھلوں کے واسطے نشانی اور بیشک بہت لوگ ہماری قدرتوں پر توجہ نہیں کرتے ');
INSERT INTO "al_hind" VALUES(10,93,'10:93','اور جگہ دی ہم نے بنی اسرائیل کو پسندیدہ جگہ اور کھانے کو دیں ستھری چیزیں سو ان میں پھوٹ نہیں پڑی یہاں تک کہ پہنچی ان کو خبر بیشک تیرا رب ان میں فیصلہ کر ے گا قیامت کے دن جس بات میں کہ ان میں پھوٹ پڑی ');
INSERT INTO "al_hind" VALUES(10,94,'10:94','سو اگر تو ہے شک میں اس چیز سے کہ اتاری ہم نے تیری طرف تو پوچھ ان سے جو پڑھتے ہیں کتاب تجھ سے پہلے بیشک آئی ہے تیرے پاس حق بات تیرے رب سے سو تو ہرگز مت ہو شک کرنے والا');
INSERT INTO "al_hind" VALUES(10,95,'10:95','اور مت ہو ان میں جنہوں نے جھٹلایا اللہ کی باتوں کو پھر تو بھی ہو جائے خرابی میں پڑنے والا');
INSERT INTO "al_hind" VALUES(10,96,'10:96','جن پر ثابت ہو چکی بات تیرے رب کی وہ ایمان نہ لائیں گے');
INSERT INTO "al_hind" VALUES(10,97,'10:97','اگرچہ پہنچیں انکو ساری نشانیاں جب تک نہ دیکھ لیں عذاب دردناک ');
INSERT INTO "al_hind" VALUES(10,98,'10:98','سو کیوں نہ ہوئی کوئی بستی کہ ایمان لاتی پھر کام آتا انکو ایمان لانا مگر یونس کی قوم جب وہ ایمان لائی اٹھا لیا ہم نے ان پر سے ذلت کا عذاب دنیا کی زندگانی میں اور فائدہ پہنچایا ہم نے انکو ایک وقت تک ');
INSERT INTO "al_hind" VALUES(10,99,'10:99','اور اگر تیرا رب چاہتا بیشک ایمان لے آتے جتنے لوگ کہ زمین میں ہیں سارےتمام اب کیا تو زبردستی کرے گا لوگوں پر کہ ہو جائیں با ایمان ');
INSERT INTO "al_hind" VALUES(10,100,'10:100','اور کسی سے نہیں ہو سکتا کہ ایمان لائے مگر اللہ کے حکم سے اور وہ ڈالتا ہے گندگی ان پر جو نہیں سوچتے ');
INSERT INTO "al_hind" VALUES(10,101,'10:101','تو کہہ دیکھو تو کیا کچھ ہے آسمانوں میں اور زمین میں اور کچھ کام نہیں آتیں نشانیاں اور ڈرانے والے ان لوگوں کو جو نہیں مانتے ');
INSERT INTO "al_hind" VALUES(10,102,'10:102','سو اب کچھ نہیں جس کا انتظار کریں مگر انہی کے سے دن جو گذر چکے ہیں ان سے پہلے تو کہہ اب راہ دیکھو میں بھی تمہارے ساتھ راہ دیکھتا ہوں ');
INSERT INTO "al_hind" VALUES(10,103,'10:103','پھر ہم بچا لیتے ہیں اپنے رسولوں کو اور ان کو جو ایمان لائے اسی طرح ذمہ ہے ہمارا بچا دیں گے ایمان والوں کو ');
INSERT INTO "al_hind" VALUES(10,104,'10:104','کہہ دے اے لوگو اگر تم شک میں ہو میرے دین سے تو میں عبادت نہیں کرتا جن کی تم عبادت کرتے ہو اللہ کےسوا اور لیکن میں عبادت کرتا ہوں اللہ کی جو کھینچ لیتا ہے تم کو اور مجھ کو حکم ہے کہ رہوں ایمان والوں میں');
INSERT INTO "al_hind" VALUES(10,105,'10:105','اوریہ کہ سیدھا کر منہ اپنا دین پر حنیف ہو کر اور مت ہو شرک والوں میں');
INSERT INTO "al_hind" VALUES(10,106,'10:106','اور مت پکار اللہ کے سوا ایسے کو کہ نہ بھلا کرے تیرا اور نہ برا پھر اگر تو ایسا کرے تو تو بھی اس وقت ہو ظالموں میں ');
INSERT INTO "al_hind" VALUES(10,107,'10:107','اور اگر پہنچا دیوے تجھ کو اللہ کچھ تکلیف تو کوئی نہیں اس کو ہٹانے والا اس کے سوا اور اگر پہنچانا چاہے تجھ کو کچھ بھلائی تو کوئی پھرنے والا نہیں اس کے فضل کو پہنچائے اپنا فضل جس پر چاہے اپنے بندوں میں اور وہی ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(10,108,'10:108','کہہ دے اے لوگو پہنچ چکا حق تم کو تمہارے رب سے اب جو کوئی راہ پر آئے سو وہ راہ پاتا ہے اپنے بھلے کو اور جو کوئی بہکا پھرے سو بہکا پھرے گا اپنے برے کو اور میں تم پر نہیں ہوں مختار ');
INSERT INTO "al_hind" VALUES(10,109,'10:109','اور تو چل اسی پر جو حکم پہنچے تیری طرف اور صبر کر جب تک فیصلہ کرے اللہ اور وہ ہے سب سے بہتر فیصلہ کرنے والا ');
INSERT INTO "al_hind" VALUES(11,1,'11:1','یہ کتاب ہے کہ جانچ لیا ہے اسکی باتوں کو پھر کھولی گئی ہیں ایک حکمت والے خبردار کے پاس سے ');
INSERT INTO "al_hind" VALUES(11,2,'11:2','کہ عبادت نہ کرو مگر اللہ کی میں تم کو اسی کی طرف سے ڈر اور خوشخبری سناتا ہوں ');
INSERT INTO "al_hind" VALUES(11,3,'11:3','اور یہ کہ گناہ بخشواؤ اپنے رب سے پھر رجوع کرو اسکی طرف کہ فائدہ پہنچائے تم کو اچھا فائدہ ایک وقت مقرر تک اور دیوے ہر زیادتی والے کو زیادتی اپنی اور اگر تم پھر جاؤ گے تو میں ڈرتا ہوں تم پر ایک بڑے دن کےعذاب سے ');
INSERT INTO "al_hind" VALUES(11,4,'11:4','اللہ کی طرف ہے تم کو لوٹ کر جانا اور وہ ہر چیز پر قادر ہے');
INSERT INTO "al_hind" VALUES(11,5,'11:5','سنتا ہے وہ دوہرے کرتے ہیں اپنے سینے تاکہ چھپائیں اس سے سنتا ہے جس وقت اوڑھتے ہیں اپنے کپڑے جانتا ہے جو کچھ چھپاتے ہیں اور جو ظاہر کرتے ہیں وہ تو جاننے والا ہے دلوں کی بات ');
INSERT INTO "al_hind" VALUES(11,6,'11:6','اور کوئی نہیں ہے چلنے والا زمین پر مگر اللہ پر ہے اس کی روزی اور جانتا ہے جہاں وہ ٹھہرتا ہے اور جہاں سونپا جاتا ہے سب کچھ موجود ہے کھلی کتاب میں ');
INSERT INTO "al_hind" VALUES(11,7,'11:7','اور وہی ہے جس نے بنائے آسمان اور زمین چھ دن میں اور تھا اس کا تخت پانی پر تاکہ آزمائے تم کو کہ کون تم میں اچھا کرتا ہے کام اور اگر تو کہے کہ تم اٹھو گے مرنے کے بعد تو البتہ کافر کہنے لگیں یہ کچھ نہیں مگر جادو ہے کھلا ہوا ');
INSERT INTO "al_hind" VALUES(11,8,'11:8','اور اگر ہم روکے رکھیں ان سے عذاب کو ایک مدت معلوم تک تو کہنے لگیں کس چیز نے روک دیا عذاب کو سنتا ہے جس دن آئے گا ان پر نہ پھیرا جائے گا ان سے اور گھیر لے گی ان کو وہ چیز جس پر ٹھٹھے کیا کرتے تھے ');
INSERT INTO "al_hind" VALUES(11,9,'11:9','اور اگر ہم چکھا دیں آدمی کو اپنی طرف سے رحمت پھر وہ چھین لیں اس سے تو وہ ناامید ناشکر ہوتا ہے ');
INSERT INTO "al_hind" VALUES(11,10,'11:10','اور اگر ہم چکھاویں اسکو آرام بعد تکلیف کے جو پہنچی تھی اسکو تو بول اٹھے دور ہوئیں برائیاں مجھ سے وہ تو اترانے والا شیخی خورا ہے ');
INSERT INTO "al_hind" VALUES(11,11,'11:11','مگر جو لوگ صابر ہیں اور کرتے ہیں نیکیاں انکے واسطے بخشش ہے اور ثواب بڑا ');
INSERT INTO "al_hind" VALUES(11,12,'11:12','سو کہیں تو چھوڑ بیٹھے گا کچھ چیز ان میں سے جو وحی آئی تیری طرف اور تنگ ہو گا اس سے تیرا جی اس بات پر کہ وہ کہتے ہیں کیوں نہ اترا اس پر خزانہ یا کیوں نہ آیا اسکے ساتھ فرشتہ تو تو ڈرانے والا ہے اور اللہ ہے ہر چیز کا ذمہ دار ');
INSERT INTO "al_hind" VALUES(11,13,'11:13','کیا کہتے ہیں کہ بنا لیا ہے تو قرآن کو کہہ دے تم بھی لے آؤ ایک دس سورتیں ایسی بنا کر اور بلا لو جس کو بلا سکو اللہ کے سوا اگر ہو تم سچے');
INSERT INTO "al_hind" VALUES(11,14,'11:14','پھر اگر نہ پورا کریں تمہارا کہنا تو جان لو کہ قرآن تو اترا ہے اللہ کی وحی سے اور یہ کہ کوئی حاکم نہیں اسکے سوا پھر اب تم حکم مانتے ہو ');
INSERT INTO "al_hind" VALUES(11,15,'11:15','جو کوئی چاہے دنیا کی زندگانی اور اسکی زینت بھگتا دیں گے ہم انکو ان کے عمل دنیا میں اور انکو اس میں کچھ نقصان نہیں ');
INSERT INTO "al_hind" VALUES(11,16,'11:16','یہی ہیں جن کے واسطے کچھ نہیں آخرت میں آگ کے سوا اور برباد ہوا جو کچھ کیا تھا یہاں اور خراب گیا جو کمایا تھا ');
INSERT INTO "al_hind" VALUES(11,17,'11:17','بھلا ایک شخص جو ہے صاف رستہ پر اپنے رب کے اور اس کے ساتھ ساتھ ہے ایک گواہ اللہ کی طرف سے اور اس سے پہلے گواہ تھی موسٰی کی کتاب رستہ بتلاتی اور بخشواتی (اورونکی برابر ہے) یہی لوگ مانتے ہیں قرآن کو اور جو کوئی منکر ہو اس سے سب فرقوں میں سےسو دوزخ ہے ٹھکانہ اس کا سو تو مت رہ شبہ میں اس سے بیشک وہ حق ہے تیرے رب کی طرف سے اور پر بہت سے لوگ یقین نہیں کرتے ');
INSERT INTO "al_hind" VALUES(11,18,'11:18','اور اس سے بڑھ کر ظالم کون جو باندھے اللہ پر جھوٹ وہ لوگ روبرو آئیں گے اپنے رب کے اور کہیں گے گواہی دینے والے یہی ہیں جنہوں نے جھوٹ کہا تھا اپنے رب پر سن لو پھٹکار ہے اللہ کی ناانصاف لوگوں پر');
INSERT INTO "al_hind" VALUES(11,19,'11:19','جو کہ روکتے ہیں اللہ کی راہ سے اور ڈھونڈھتے ہیں اس میں کجی اور وہی ہیں آخر ت سے منکر ');
INSERT INTO "al_hind" VALUES(11,20,'11:20','وہ لوگ نہیں تھکانے والے زمین میں بھاگ کر اور نہیں انکے واسطے اللہ کے سوا کوئی حمایتی دونا ہے ان کے لئے عذاب نہ طاقت رکھتے تھے سننے کی اور نہ دیکھتے تھے ');
INSERT INTO "al_hind" VALUES(11,21,'11:21','وہی ہیں جو کھو بیٹھے اپنی جان اور گم ہو گیا ان سےجو جھوٹ باندھا تھا ');
INSERT INTO "al_hind" VALUES(11,22,'11:22','اس میں شک نہیں کہ یہ لوگ آخرت میں یہی ہیں سب سے زیادہ نقصان میں');
INSERT INTO "al_hind" VALUES(11,23,'11:23','البتہ جو لوگ ایمان لائے اور کام کئے نیک اور عاجزی کی اپنے رب کے سامنے وہ ہیں جنت کے رہنے والے وہ اسی میں رہا کریں گے ');
INSERT INTO "al_hind" VALUES(11,24,'11:24','مثال ان دونوں فرقوں کی جیسے ایک تو اندھا اور بہرا اور دوسرا دیکھتا اور سنتا کیا برابر ہے دونوں کا حال پھر کیا تم غور نہیں کرتے ');
INSERT INTO "al_hind" VALUES(11,25,'11:25','اور ہم نے بھیجا نوح کو اسکی قوم کی طرف کہ میں تم کو ڈر کی بات سناتا ہوں کھول کر ');
INSERT INTO "al_hind" VALUES(11,26,'11:26','کہ نہ پرستش کرو اللہ کے سوا میں ڈرتا ہوں تم پر دردناک دن کے عذاب سے ');
INSERT INTO "al_hind" VALUES(11,27,'11:27','پھر بولے سردار جو کافر تھے اس کی قوم کے ہم کو تو تو نظر نہیں آتا مگر ایک آدمی ہم جیسا اور دیکھتے نہیں کوئی تابع ہوا ہو تیرا مگر جو ہم میں نیچ قوم ہیں بلا تامل اور ہم نہیں دیکھتے تم کو اوپر اپنے کچھ بڑائی بلکہ ہم کو تو خیال ہے کہ تم سب جھوٹے ہو ');
INSERT INTO "al_hind" VALUES(11,28,'11:28','بولا اے قوم دیکھو تو اگر میں ہوں صاف راستہ پر اپنے رب کے اور اس نے بھیجی مجھ پر رحمت اپنے پاس سے پھر اس کو تمہاری آنکھ سے مخفی رکھا تو کیا ہم تم کو مجبور کر سکتے ہیں اس پر اور تم اس سے بیزا ہو ');
INSERT INTO "al_hind" VALUES(11,29,'11:29','اور اے میری قوم نہیں مانگتا میں تم سے اس پر کچھ مال میری مزدوری نہیں مگر اللہ پر اور میں نہیں ہانکنے والا ایمان والوں کو ان کو ملنا ہے اپنے رب سے لیکن میں دیکھتا ہوں تم لوگ جاہل ہو ');
INSERT INTO "al_hind" VALUES(11,30,'11:30','اور اےقوم کون چھڑائے مجھ کو اللہ سے اگر ان کو ہانک دوں کیا تم دھیان نہیں کرتے ');
INSERT INTO "al_hind" VALUES(11,31,'11:31','اور میں نہیں کہتا تم کو کہ میر ے پاس ہیں خزانے اللہ کے اور نہ میں خبر رکھوں غیب کی اور نہ کہوں کہ میں فرشتہ ہوں اور نہ کہوں گا کہ جو لوگ تمہاری آنکھ میں حقیر ہیں نہ دے گا ان کو اللہ بھلائی اللہ خوب جانتا ہے جو کچھ انکے جی میں ہے یہ کہوں تو میں بے انصاف ہوں ');
INSERT INTO "al_hind" VALUES(11,32,'11:32','بولے اے نوح تو نے ہم سے جگھڑا کیا اور بہت جھگڑ چکا اب لے آ جو تو وعدہ کرتا ہے ہم سے اگر تو سچا ہے ');
INSERT INTO "al_hind" VALUES(11,33,'11:33','کہا کہ لائے گا تو اس کو اللہ ہی اگر چاہے گا اور تم نہ تھکا سکو گے بھاگ کر ');
INSERT INTO "al_hind" VALUES(11,34,'11:34','اور نہ کارگر ہو گی تم کو میری نصیحت جو چاہوں کہ تم کو نصیحت کروں اگر اللہ چاہتا ہو گا کہ تم کو گمراہ کرے وہی ہے رب تمہارا اور اسی کی طرف لوٹ جاؤ گے ');
INSERT INTO "al_hind" VALUES(11,35,'11:35','کیا کہتے ہیں کہ بنا لایا قرآن کو کہہ دے اگر میں بنا لایا ہوں تو مجھ پر ہے میرا گناہ اور میرا ذمہ نہیں جو تم گناہ کرتے ہو ');
INSERT INTO "al_hind" VALUES(11,36,'11:36','اور حکم ہوا طرف نوح کی کہ اب ایمان نہ لائے گا تیری قوم میں مگر جو ایمان لاچکا سو غمگین نہ رہ ان کاموں پر جو کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(11,37,'11:37','اور بنا کشتی روبرو ہمارے اور ہمارے حکم سے اور نہ بات کر مجھ سے ظالموں کے حق میں یہ بیشک غرق ہوں گے ');
INSERT INTO "al_hind" VALUES(11,38,'11:38','اور وہ کشتی بناتا تھا اور جب گذرتے اس پر سردار اس کی قوم کے ہنسی کرتے اس سے بولا اگر تم ہنستے ہو ہم سے تو ہم ہنستے ہیں تم سے جیسے تم ہنستے ہو ');
INSERT INTO "al_hind" VALUES(11,39,'11:39','اب جلد جان لو گے کہ کس پر آتا ہے عذاب کہ رسوا کرے اسکو اور اترتا ہے اس پر عذاب دائمی ');
INSERT INTO "al_hind" VALUES(11,40,'11:40','یہاں تک کہ جب پہنچا حکم ہمارا اور جوش مارا تنور نے کہا ہم نے چڑھا لے کشتی میں ہر قسم سے جوڑا دو عدد اور اپنے گھر کے لوگ مگر جس پر پہلے ہو چکا ہے حکم اور سب ایمان والوں کو اور ایمان نہ لائے تھے اس کے ساتھ مگر تھوڑے ');
INSERT INTO "al_hind" VALUES(11,41,'11:41','اور بولا سوار ہو جاؤ اس میں اللہ کے نام سے ہے اس کا چلنا اور ٹھہرنا تحقیق میرا رب ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(11,42,'11:42','اور وہ لئے جارہی تھی انکو لہروں میں جیسے پہاڑ اور پکارا نوح نے اپنے بیٹے کو اور وہ ہو رہا تھا کنارے اے بیٹے سوار ہو جا ساتھ ہمارے اور مت رہ ساتھ کافروں کے ');
INSERT INTO "al_hind" VALUES(11,43,'11:43','بولا جا لگوں گا کسی پہاڑ کو جو بچا لے گا مجھ کو پانی سے کہا کوئی بچانے والا نہیں آ ج اللہ کے حکم سے مگر جس پر وہی رحم کرے اور حائل ہو گئ دونوں میں موج پھر ہو گیا ڈوبنے والوں میں ');
INSERT INTO "al_hind" VALUES(11,44,'11:44','اور حکم آیا اے زمین نگل جا اپنا پانی اور اے آسمان تھم جا اور سکھا دیا گیا پانی اور ہو چکا کام اور کشتی ٹھہری جودی پہاڑ پر اور حکم ہوا کہ دور ہو قوم ظالم ');
INSERT INTO "al_hind" VALUES(11,45,'11:45','اور پکارا نوح نے اپنے رب کو کہا اے رب میرا بیٹا ہے میرے گھر والوں میں اور بیشک تیرا وعدہ سچا ہے اور تو سب سے بڑا حاکم ہے');
INSERT INTO "al_hind" VALUES(11,46,'11:46','فرمایا اے نوح وہ نہیں تیرے گھر والوں میں اس کے کام ہیں خراب سو مت پوچھ مجھ سے جو تجھ کو معلوم نہیں میں نصیحت کرتا ہو ں تجھ کو کہ نہ ہو جائے تو جاہلوں میں ');
INSERT INTO "al_hind" VALUES(11,47,'11:47','بولا اے رب میں پناہ لیتا ہوں تیری اس سے کہ پوچھوں تجھ سےجو معلوم نہ ہو مجھ کو [۶۶[ اور اگر تو نہ بخشے مجھ کو اور رحم نہ کرے تو میں ہوں نقصان والوں میں ');
INSERT INTO "al_hind" VALUES(11,48,'11:48','حکم ہوا اے نوح اتر سلامتی کے ساتھ ہماری طرف سے اور برکتوں کےساتھ تجھ پر اور ان فرقوں پر جو تیرے ساتھ ہیں اور دوسرے فرقے ہیں کہ ہم فائدہ دیں گے انکو پھر پہنچے گا ان کو ہماری طرف سے عذاب دردناک ');
INSERT INTO "al_hind" VALUES(11,49,'11:49','یہ باتیں منجملہ غیب کی خبروں کی ہیں کہ ہم بھیجتے ہیں تیری طرف نہ تجھ کو ان کی خبر تھی اور نہ تیری قوم کو اس سے پہلے سو تو صبر کر البتہ انجام بھلا ہے ڈرنے والوں کا ');
INSERT INTO "al_hind" VALUES(11,50,'11:50','اور عاد کی طرف ہم نے بھیجا انکے بھائی ہود کو بولا اے قوم بندگی کرو اللہ کی کوئی تمہارا حاکم نہیں سوائے اس کے تم سب جھوٹ کہتے ہو ');
INSERT INTO "al_hind" VALUES(11,51,'11:51','اےقوم میں تم سے نہیں مانگتا اس پر مزدوری میری مزدوری اسی پر ہے جس نے مجھ کو پیدا کیا پھر کیا تم نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(11,52,'11:52','اور اے قوم گناہ بخشواؤ اپنے رب سے پھر رجوع کرو اسکی طرف چھوڑے گا تم پر آسمان سے دھاریں اور زیادہ دے گا تم کو زور پر زور اور روگردانی نہ کرو گنہگار ہو کر ');
INSERT INTO "al_hind" VALUES(11,53,'11:53','بولے اے ہود تو ہمارے پاس کوئی سند لے کر نہیں آیا اور ہم نہیں چھوڑنے والے اپنے ٹھاکروں (معبودوں) کو تیرے کہنے سے اور ہم نہیں تجھ کو ماننے والے ');
INSERT INTO "al_hind" VALUES(11,54,'11:54','ہم تو یہی کہتے ہیں کہ تجھ کو آسیب پہنچایا ہے کسی ہمارے ٹھاکروں (معبودوں) نے بری طرح بولا میں گواہ کرتا ہوں اللہ کو اور تم گواہ رہو کہ میں بیزار ہوں ان سے جن کو تم شریک کرتے ہو');
INSERT INTO "al_hind" VALUES(11,55,'11:55','اس کے سوا سو برائی کرو میرے حق میں تم سب مل کر پھر مجھ کو مہلت نہ دو');
INSERT INTO "al_hind" VALUES(11,56,'11:56','میں نے بھروسہ کیا اللہ پر جو رب ہے میرا اور تمہارا کوئی نہیں زمین پر پاؤں دھرنے والا مگر اللہ کے ہاتھ میں ہے چوٹی اسکی بیشک میرا رب ہے سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(11,57,'11:57','پھر اگر تم منہ پھیرو گے تو میں پہنچا چکا تم کو جو میرے ہاتھ بھیجا تھا تمہاری طرف اور قائم مقام کرے گا میرا رب کوئی اور لوگ اور نہ بگاڑ سکو گے اللہ کا کچھ تحقیق میرا رب ہے ہر چیز پر نگہبان ');
INSERT INTO "al_hind" VALUES(11,58,'11:58','اور جب پہنچا ہمارا حکم بچا دیا ہم نے ہود کو اور جو لوگ ایمان لائے تھے اس کےساتھ اپنی رحمت سے اور بچا دیا انکو ایک بھاری عذاب سے ');
INSERT INTO "al_hind" VALUES(11,59,'11:59','اور یہ تھے عاد کہ منکر ہوئے اپنے رب کی باتوں سے اور نہ مانا اسکے رسولوں کو اور مانا حکم انکا جو سرکش تھے مخالف ');
INSERT INTO "al_hind" VALUES(11,60,'11:60','اور پیچھے سے آئی انکو اس دنیا میں پھٹکار اور قیامت کے دن بھی سن لو عاد منکر ہوئے اپنے رب سے سن لو پھٹکار ہے عاد کو جو قوم تھی ہود کی ');
INSERT INTO "al_hind" VALUES(11,61,'11:61','اور ثمود کی طرف بھیجا ان کا بھائی صالح بولا اے قوم بندگی کرو اللہ کی کوئی حاکم نہیں تمہارا اس کے سوا اسی نے بنایا تم کو زمین سے اور بسایا تم کو اس میں سو گناہ بخشواؤ اس سے اور رجوع کرو اسکی طرف تحقیق میرا رب نزدیک ہے قبول کرنے والا ');
INSERT INTO "al_hind" VALUES(11,62,'11:62','بولے اے صالح تجھ سے تو ہم کو امید تھی اس سے پہلے کیا تو ہم کو منع کرتا ہے کہ پرستش کریں جنکی پرستش کرتے رہے ہمارے باپ دادے اور ہم کو تو شبہ ہے اس میں جس کی طرف تو بلاتا ہے ایسا کہ دل نہیں مانتا ');
INSERT INTO "al_hind" VALUES(11,63,'11:63','بولا اے قوم بھلا دیکھو تو اگر مجھ کو سمجھ مل گئ اپنے رب کی طرف سے اور اس نے مجھ کو دی رحمت اپنی طرف سے پھر کون بچائے مجھ کو اس سے اگر اسکی نافرمانی کروں سو تم کچھ نہیں بڑھاتے میرا سوائے نقصان کے ');
INSERT INTO "al_hind" VALUES(11,64,'11:64','اور اے قوم یہ اونٹنی ہے اللہ کی تمہارے لئے نشانی سو چھوڑ دو اسکو کھاتی پھرے اللہ کی زمین میں اور مت ہاتھ لگاؤ اس کو بری طرح پھر تو آ پکڑے گا تم کو عذاب بہت جلد');
INSERT INTO "al_hind" VALUES(11,65,'11:65','پھر اسکے پاؤں کاٹے تب کہا فائدہ اٹھالو اپنے گھروں میں تین دن یہ وعدہ ہے جو جھوٹا نہ ہو گا ');
INSERT INTO "al_hind" VALUES(11,66,'11:66','پھر جب پہنچا حکم ہمارا بچا دیا ہم نے صالح کو اور جو ایمان لائے اسکے ساتھ اپنی رحمت سے اور اس دن کی رسوائی سے بیشک تیرا رب وہی ہے زور والا زبردست ');
INSERT INTO "al_hind" VALUES(11,67,'11:67','اور پکڑ لیا ان ظالموں کو ہولناک آواز نے پھر صبح کو رہ گئے اپنے گھروں میں اوندھے پڑے ہوئے');
INSERT INTO "al_hind" VALUES(11,68,'11:68','جیسے کبھی رہے ہی نہ تھے وہاں سن لو ثمود منکر ہوئے اپنے رب سےسن لو پھٹکار ہے ثمود کو ');
INSERT INTO "al_hind" VALUES(11,69,'11:69','اور البتہ آ چکے ہیں ہمارے بھیجے ہوئے ابراہیم کے پاس خوشخبری لے کر بولے سلام وہ بولا سلام ہے پھر دیر نہ کی کہ لے آیا ایک بچھڑا تلا ہوا ');
INSERT INTO "al_hind" VALUES(11,70,'11:70','پھر جب دیکھا ان کے ہاتھ نہیں آتے کھانے پر تو کھٹکا اور دل میں ان سے ڈرا وہ بولے مت ڈر ہم بھیجے ہوئے آئے ہیں طرف قوم لوط کی ');
INSERT INTO "al_hind" VALUES(11,71,'11:71','اور اس کی عورت کھڑی تھی تب وہ ہنس پڑی پھر ہم نے خوشخبری دی اسکو اسحٰق کے پیدا ہونے کی اور اسحٰق کے پیچھے یعقوب کی ');
INSERT INTO "al_hind" VALUES(11,72,'11:72','بولی اے خرابی کیا میں بچہ جنوں گی اور میں بڑھیا ہوں اور یہ خاوند میر اہے بوڑھا یہ تو ایک عجیب بات ہے ');
INSERT INTO "al_hind" VALUES(11,73,'11:73','وہ بولے کیا تو تعجب کرتی ہے اللہ کے حکم سے اللہ کی رحمت ہے اور برکتیں تم پر اے گھر والو تحقیق اللہ ہے تعریف کیا گیا بڑائیوں والا ');
INSERT INTO "al_hind" VALUES(11,74,'11:74','پھر جب جاتا رہا ابراہیم سے ڈر اور آئی اس کو خوشخبری جھگڑنے لگا ہم سے قوم لوط کے حق میں');
INSERT INTO "al_hind" VALUES(11,75,'11:75','البتہ ابراہیم تحمل والا نرم دل ہے رجوع رہنے والا');
INSERT INTO "al_hind" VALUES(11,76,'11:76','اے ابراہیم چھوڑ یہ خیال وہ تو آ چکا حکم تیرے رب کا اور ان پر آتا ہے عذاب جو لوٹایا نہیں جاتا ');
INSERT INTO "al_hind" VALUES(11,77,'11:77','اور جب پہنچے ہمارے بھیجے ہوئے لوط کے پاس غمگین ہوا انکے آنے سے اور تنگ دل ہوا دل میں اور بولا آج دن بڑ اسخت ہے ');
INSERT INTO "al_hind" VALUES(11,78,'11:78','اور آئی اس کے پاس قوم اسکی دوڑتی بے اختیار اور آگے سے کر رہے تھے برے کام بولا اے قوم یہ میری بیٹیاں حاضر ہیں یہ پاک ہیں تم کو اُن سے سو ڈرو تم اللہ سے اور مت رسوا کرو مجھ کو میرے مہمانوں میں کیا تم میں ایک مرد بھی نہیں نیک چلن ');
INSERT INTO "al_hind" VALUES(11,79,'11:79','بولے تو تو جانتا ہے ہم کو تیری بیٹیوں سے کچھ غرض نہیں اور تجھ کو تو معلوم ہے جو ہم چاہتے ہیں ');
INSERT INTO "al_hind" VALUES(11,80,'11:80','کہنے لگا کاش مجھ کو تمہارے مقابلہ میں زور ہوتا یا جا بیٹھتا کسی مستحکم پناہ میں ');
INSERT INTO "al_hind" VALUES(11,81,'11:81','مہمان بولے اے لوط ہم بھیجے ہوئے ہیں تیرے رب کے ہرگز نہ پہنچ سکیں گے تجھ تک سو لے نکل اپنے لوگوں کو کچھ رات سے اور مڑ کر نہ دیکھے تم میں کوئی مگر عورت تیری کہ اس کو پہنچ کر رہے گا جو ان کو پہنچے گا ان کے وعدہ کا وقت ہے صبح کیا صبح نہیں ہے نزدیک ');
INSERT INTO "al_hind" VALUES(11,82,'11:82','پھر جب پہنچا حکم ہمارا کر ڈالی ہم نے وہ بستی اوپر نیچے اور برسائے ہم نے اس پر پتھر کنکر کے تہہ بہ تہہ ');
INSERT INTO "al_hind" VALUES(11,83,'11:83','نشان کئے ہوئے تیرے رب کے پاس اور نہیں ہے وہ بستی ان ظالموں سے کچھ دور ');
INSERT INTO "al_hind" VALUES(11,84,'11:84','اور مدین کی طرف بھیجا ان کے بھائی شعیب کو بولا اے میری قوم بندگی کرو اللہ کی کوئی نہیں تمہارا معبود اس کے سوائے اور نہ گھٹاؤ ماپ اور تول کو میں دیکھتا ہوں تم کو آسودہ حال اور ڈرتا ہوں تم پر عذاب سے ایک گھیر لینے والے دن کے ');
INSERT INTO "al_hind" VALUES(11,85,'11:85','اور اے قوم پورا کرو ماپ اور تول کو انصاف سے اور نہ گھٹا دو لوگوں کو ان کی چیزیں اور مت مچاؤ زمین میں فساد ');
INSERT INTO "al_hind" VALUES(11,86,'11:86','جو بچ رہے اللہ کا دیا وہ بہتر ہے تم کو اگر ہو تم ایمان والے اور میں نہیں ہوں تم پر نگہبان ');
INSERT INTO "al_hind" VALUES(11,87,'11:87','بولے اے شعیب کیا تیرے نماز پڑھنے نے تجھ کو یہ سکھایا کہ ہم چھوڑ دیں جن کو پوجتے رہے ہمارے باپ دادے یا چھوڑ دیں کرنا جو کچھ کہ کرتے ہیں اپنے مالوں میں تو ہی بڑ ابا وقار ہے نیک چلن ');
INSERT INTO "al_hind" VALUES(11,88,'11:88','بولا اے قوم دیکھو تو اگر مجھ کو سمجھ آ گئ اپنے رب کی طرف سے اور اس نے روزی دی مجھ کو نیک روزی اور میں یہ نہیں چاہتا کہ بعد کو خود کروں وہ کام جو تم سے چھڑاؤں میں تو چاہتا ہوں سنوارنا جہاں تک ہو سکے اور بن آنا ہے اللہ کی مدد سے اسی پر میں نے بھروسہ کیا ہے اور اسی کی طرف میرا رجوع ہے ');
INSERT INTO "al_hind" VALUES(11,89,'11:89','اور اے قوم نہ کمائیو میری ضد کر کے یہ کہ پڑے تم پر جیسا کچھ کہ پڑ چکا قوم نوح پر یا قوم ہود پر یا قوم صالح پر اور قوم لوط تو تم سے کچھ دور ہی نہیں ');
INSERT INTO "al_hind" VALUES(11,90,'11:90','اور گناہ بخشواؤ اپنے رب سے اور رجوع کرو اسکی طرف البتہ میرا رب ہے مہربان محبت والا ');
INSERT INTO "al_hind" VALUES(11,91,'11:91','بولے اے شعیب ہم نہیں سمجھتے بہت باتیں جو تو کہتا ہے اور ہم تو دیکھتے ہیں کہ تو ہم میں کمزور ہے اور اگر نہ ہوتے تیرے بھائی بند تو تجھ کو تو ہم سنگسار کر ڈالتے اور ہماری نگاہ میں تیری کچھ عزت نہیں ');
INSERT INTO "al_hind" VALUES(11,92,'11:92','بولا اے قوم کیا میرے بھائی بندوں کا دباؤ تم پر زیادہ ہے اللہ سے اور اس کو ڈال رکھا تم نے پیٹھ پیچھے بھلا کر تحقیق میرے رب کے قابو میں ہے جو کچھ کرتے ہو ');
INSERT INTO "al_hind" VALUES(11,93,'11:93','اور اے میری قوم کام کئےجاؤ اپنی جگہ میں بھی کام کرتا ہوں آگے معلوم کر لو گے کس پر آتا ہے عذاب رسوا کرنے والا اور کون ہے جھوٹا اور تاکتے رہو میں بھی تمہارے ساتھ تاک رہا ہوں ');
INSERT INTO "al_hind" VALUES(11,94,'11:94','اور جب پہنچا ہمارا حکم بچا دیا ہم نے شعیب کو اور جو ایمان لائے تھے اسکے ساتھ اپنی مہربانی سے اور آ پکڑا ان ظالموں کو کڑک نے پھر صبح کو رہ گئے اپنے گھروں میں اوندھے پڑے ہوئے');
INSERT INTO "al_hind" VALUES(11,95,'11:95','گویا کبھی وہاں بسے ہی نہ تھے سن لو پھٹکار ہے مدین کو جیسے پھٹکار ہوئی تھی ثمود کو ');
INSERT INTO "al_hind" VALUES(11,96,'11:96','اور البتہ بھیج چکے ہیں ہم موسٰی کو اپنی نشانیاں اور واضح سند دے کر ');
INSERT INTO "al_hind" VALUES(11,97,'11:97','فرعون اور اس کے سرداروں کے پاس پھر وہ چلے حکم پر فرعون کے اور نہیں بات فرعون کی کچھ کام کی ');
INSERT INTO "al_hind" VALUES(11,98,'11:98','آگے ہو گا اپنی قوم کے قیامت کے دن پھر پہنچائے انکو آگ پر اور برا گھاٹ ہے جس پر پہنچے ');
INSERT INTO "al_hind" VALUES(11,99,'11:99','اور پیچھے سے ملتی رہی اس جہان میں لعنت اور دن قیامت کے بھی برا انعام ہے جو ان کو ملا ');
INSERT INTO "al_hind" VALUES(11,100,'11:100','یہ تھوڑے سے حالات ہیں بستیوں کے کہ ہم سناتے ہیں تجھ کو بعض ان میں سےابتک قائم ہیں اور بعض کی جڑ کٹ گئ ');
INSERT INTO "al_hind" VALUES(11,101,'11:101','اور ہم نے ان پر ظلم نہیں کیا لیکن ظلم کر گئے وہی اپنی جان پر پھر کچھ کام نہ آئے ان کے ٹھاکر (معبود) جن کو پکارتے تھے سوائے اللہ کے کسی چیز میں جس وقت پہنچا حکم تیرے رب کا اور نہیں بڑھایا انکے حق میں سوائے ہلاک کرنے کے ');
INSERT INTO "al_hind" VALUES(11,102,'11:102','اور ایسی ہی ہے پکڑ تیرے رب کی جب پکڑتا ہے بستیوں کو اور وہ ظلم کرتے ہوتے ہیں بیشک اس کی پکڑ دردناک ہے شدت کی ');
INSERT INTO "al_hind" VALUES(11,103,'11:103','اس بات میں نشانی ہے اس کو جو ڈرتا ہے آخرت کے عذاب سے وہ ایک دن ہے جس میں جمع ہوں گے سب لوگ اور وہ دن ہے سب کے پیش ہونے کا ');
INSERT INTO "al_hind" VALUES(11,104,'11:104','اور اس کو ہم دیر جو کرتے ہیں سو ایک وعدہ کے لئے جو مقرر ہے ');
INSERT INTO "al_hind" VALUES(11,105,'11:105','جس دن وہ آئے گا بات نہ کر سکے گا کوئی جاندار مگر اسکے حکم سے سو ان میں بعضے بدبخت ہیں اور بعضے نیک بخت ');
INSERT INTO "al_hind" VALUES(11,106,'11:106','سو جو لوگ بد بخت ہیں وہ تو آگ میں ہیں انکو وہاں چیخنا ہے اور دہاڑنا');
INSERT INTO "al_hind" VALUES(11,107,'11:107','ہمیشہ رہیں اس میں جب تک رہے آسمان اور زمین مگر جو چاہے تیرا رب بیشک تیرا رب کر ڈالتا ہے جو چاہے');
INSERT INTO "al_hind" VALUES(11,108,'11:108','اور جو لوگ نیک بخت ہیں سو جنت میں ہیں ہمیشہ رہیں اس میں جب تک رہے آسمان اور زمین مگر جو چاہے تیرا رب بخشش ہے بے انتہا ');
INSERT INTO "al_hind" VALUES(11,109,'11:109','سو تو نہ رہ دھوکے میں ان چیزوں سے جن کو پوجتے ہیں یہ لوگ کچھ نہیں پوجتے مگر ویسا ہی جیسا کہ پوجتے تھے انکے باپ دادے اس سے پہلے اور ہم دینے والے ہیں انکو انکا حصہ یعنی عذاب سے بلا نقصان ');
INSERT INTO "al_hind" VALUES(11,110,'11:110','اور البتہ ہم نے دی تھی موسٰی کو کتاب پھر اس میں پھوٹ پڑ گئ اور اگر نہ ہوتا ایک لفظ کہ پہلے فرما چکا تھا تیرا رب تو فیصلہ ہو جاتا ان میں اور ان کو اس میں شبہ ہے کہ مطمئن نہیں ہونے دیتا ');
INSERT INTO "al_hind" VALUES(11,111,'11:111','اور جتنے لوگ ہیں جب وقت آیا پورا (بھگتا دے گا) دے گا رب تیرا ان کو ان کے اعمال اس کو سب خبر ہے جو کچھ وہ کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(11,112,'11:112','سو تو سیدھا چلا جا جیسا تجھ کو حکم ہوا اور جس نے توبہ کی تیرے ساتھ اور حد سے نہ بڑھو بیشک وہ دیکھتا ہے جو کچھ تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(11,113,'11:113','اور مت جھکو ان کی طرف جو ظالم ہیں پھر تم کو لگے گی آگ اور کوئی نہیں تمہارا اللہ کے سوا مددگار پھر کہیں مدد نہ پاؤ گے ');
INSERT INTO "al_hind" VALUES(11,114,'11:114','اور قائم کر نماز کو دونوں طرف دن کے اورکچھ ٹکڑوں میں رات کے البتہ نیکیاں دور کرتی ہیں برائیوں کو یہ یادگاری ہے یاد رکھنے والوں کو ');
INSERT INTO "al_hind" VALUES(11,115,'11:115','اور صبر کر البتہ اللہ ضائع نہیں کرتا ثواب نیکی کرنے والوں کا ');
INSERT INTO "al_hind" VALUES(11,116,'11:116','سو کیویں نہ ہوئے ان جماعتوں میں جو تم سے پہلے تھیں ایسے لوگ جن میں اثر خیر رہا ہو کہ منع کرتے رہتے بگاڑ کرنے سے ملک میں مگر تھوڑے کہ جن کو ہم نے بچا لیا ان میں سے اور چلے وہ لوگ (اور پیچھے پڑے رہے ظالم اسی چیز کے جس میں ان کو عیش ملا) جو ظالم تھے وہی راہ جس میں عیش سے رہتے تھے اور تھے گنہگار ');
INSERT INTO "al_hind" VALUES(11,117,'11:117','اور تیرا رب ہرگز ایسا نہیں کہ ہلاک کرے بستیوں کو زبردستی سے اور لوگ وہاں کے نیک ہوں ');
INSERT INTO "al_hind" VALUES(11,118,'11:118','اور اگر چاہتا تیرا رب کر ڈالتا لوگوں کو ایک راستہ پر اور ہمیشہ رہتے ہیں اختلاف میں');
INSERT INTO "al_hind" VALUES(11,119,'11:119','مگر جن پر حم کیا تیرے رب نے اور اسی واسطے انکو پیدا کیا ہے اور پوری ہوئی بات تیرے رب کی کہ البتہ بھر دوں گا دوزخ جنوں سے اور آدمیوں سے اکٹھے ');
INSERT INTO "al_hind" VALUES(11,120,'11:120','اور سب چیز بیان کرتے ہیں ہم تیرے پاس رسولوں کےاحوال سے جس سے تسلی دیں تیرے دل کو اور آئی تیرے پاس اس سورت میں تحقیق بات اور نصیحت اور یاداشت ایمان والوں کو ');
INSERT INTO "al_hind" VALUES(11,121,'11:121','اور کہہ دے ان کو جو ایمان نہیں لاتے کام کئے جاؤ اپنی جگہ پر ہم بھی کام کرتے ہیں');
INSERT INTO "al_hind" VALUES(11,122,'11:122','اور انتظار کرو ہم بھی منتظر ہیں ');
INSERT INTO "al_hind" VALUES(11,123,'11:123','اور اللہ کے پا س ہے چھپی بات آسمانوں کی اور زمین کی اور اسی کی طرف رجوع ہے سب کام کا سو اسی کی بندگی کر اور اسی پر بھروسہ رکھ اور تیرا رب بیخبر نہیں جو کام تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(12,1,'12:1','یہ آیتیں ہیں واضح کتاب کی ');
INSERT INTO "al_hind" VALUES(12,2,'12:2','ہم نے اس کو اتارا ہے قرآن عربی زبان کا تاکہ تم سمجھ لو ');
INSERT INTO "al_hind" VALUES(12,3,'12:3','ہم بیان کرتے ہیں تیرے پاس بہت اچھا بیان اس واسطے کہ بھیجا ہم نے تیری طرف یہ قرآن اور تو تھا اس سے پہلے البتہ بیخبروں میں ');
INSERT INTO "al_hind" VALUES(12,4,'12:4','جس وقت کہا یوسف نے اپنے باپ سے اے باپ میں نے دیکھا خواب میں گیارہ ستاروں کو اور سورج کو اور چاند کو دیکھا میں نے انکو اپنے واسطے سجدہ کرتے ہوئے ');
INSERT INTO "al_hind" VALUES(12,5,'12:5','کہا اے بیٹے مت بیان کرنا خواب اپنا اپنے بھائیوں کے آگے پھر وہ بنائیں گے تیرے واسطے کچھ فریب البتہ شیطان ہے انسان کا صریح دشمن ');
INSERT INTO "al_hind" VALUES(12,6,'12:6','اور اسی طرح برگزیدہ کرے گا تجھ کو تیرا رب اور سکھلائے گا تجھ کو ٹھکانے پر لگانا باتوں کا اور پورا کرے گا اپنا انعام تجھ پر اور یعقوب کے گھر پر جیسا پورا کیا ہے تیرے دو باپ دادوں پر اس سے پہلے ابراہیم اور اسحٰق پر البتہ تیرا رب خبردار ہے حکمت والا ');
INSERT INTO "al_hind" VALUES(12,7,'12:7','البتہ ہیں یوسف کے قصہ میں اور اس کے بھائیوں کے قصہ میں نشانیاں پوچھنے والوں کے لئے ');
INSERT INTO "al_hind" VALUES(12,8,'12:8','جب کہنے لگے البتہ یوسف اور اس کا بھائی زیادہ پیارا ہے ہمارے باپ کو ہم سے اور ہم ان سے قوت والے لوگ ہیں البتہ ہمارا باپ صریح خطا پر ہے ');
INSERT INTO "al_hind" VALUES(12,9,'12:9','مار ڈالو یوسف کو یا پھینک دو کسی ملک میں کہ خالص رہے تم پر توجہ تمہارے باپ کی اور ہو رہنا اس کے بعد نیک لوگ ');
INSERT INTO "al_hind" VALUES(12,10,'12:10','بولا ایک بولنے والا ان میں مت مار ڈالو یوسف کو اور ڈال دو اس کو گمنام کنوئیں میں کہ اٹھالے جائے اس کو کوئی مسافر اگر تم کو کرنا ہے ');
INSERT INTO "al_hind" VALUES(12,11,'12:11','بولے اے باپ کیا بات ہے کہ تو اعتبار نہیں کرتا ہمارا یوسف پر اور ہم تو اسکے خیرا خواہ ہیں ');
INSERT INTO "al_hind" VALUES(12,12,'12:12','بھیج اسکو ہمارےساتھ کل کو خوب کھائے اور کھیلے اور ہم تو اسکے نگہبان ہیں ');
INSERT INTO "al_hind" VALUES(12,13,'12:13','بولا مجھ کو غم ہوتا ہے اس سے کہ تم اس کو لے جاؤ اور ڈرتا ہوں اس سے کہ کھا جائے اسکو بھیڑیا اور تم اس سے بیخبر رہو ');
INSERT INTO "al_hind" VALUES(12,14,'12:14','بولے اگر کھا گیا اس کو بھیڑیا اور ہم ایک جماعت ہیں وقت ور تو تو ہم نے سب کچھ گنوا دیا ');
INSERT INTO "al_hind" VALUES(12,15,'12:15','پھر جب لیکر چلے اس کو اور متفق ہوئے کہ ڈالیں اسکو گمنام کنویں میں اور ہم نے اشارہ کر دیا اسکو کہ تو جتائے گا انکو ان کا یہ کام اور وہ تجھ کو نہ جانیں گے ');
INSERT INTO "al_hind" VALUES(12,16,'12:16','اور آئے اپنے باپ کے پاس اندھیرا پڑے روتے ہوئے ');
INSERT INTO "al_hind" VALUES(12,17,'12:17','کہنے لگے اے باپ ہم لگے دوڑنے آگے نکلنے کو اور چھوڑا یوسف کو اپنے اسباب کے پاس پھر اس کو کھا گیا بھیڑیا اور تو باور نہ کرے گا ہمارا کہنا اور اگرچہ ہم سچے ہوں ');
INSERT INTO "al_hind" VALUES(12,18,'12:18','اور لائے اس کے کرتے پر لہو لگا کر جھوٹ بولا یہ ہر گز نہیں بلکہ بنا دی ہے تم کو تمہارے جیوں نےایک بات اب صبر ہی بہتر ہے اور اللہ ہی سے مدد مانگتا ہوں اس بات پر جو تم ظاہر کرتے ہو ');
INSERT INTO "al_hind" VALUES(12,19,'12:19','اور آیا ایک قافلہ پھر بھیجا اپنا پانی بھرنے والا اس نے لٹکایا اپنا ڈول کہنے لگا کیا خوشی کی بات ہے یہ ہے ایک لڑکا اور چھپا لیا اس کو تجارت کا مال سمجھ کر اور اللہ خوب جانتا ہی جو کچھ وہ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(12,20,'12:20','اور بیچ آئے اسکو بھائی ناقص قیمت کو گنتی کی چونیاں اور ہو رہے تھے اس سے بیزار ');
INSERT INTO "al_hind" VALUES(12,21,'12:21','اور کہا جس شخص نےخرید کیا اسکو مصر سے اپنی عورت کو آبرو سے رکھ اس کو شاید ہمارےکام آئے یا ہم کر لیں اسکو بیٹا اور اسی طرح جگہ دی ہم نے یوسف کو اس ملک میں اور اس واسطے کہ اسکو سکھائیں کچھ ٹھکانے پر بٹھانا باتوں کو اور اللہ در رہتا ہے اپنے کام میں و لیکن اکثر لوگ نہیں جانتے ');
INSERT INTO "al_hind" VALUES(12,22,'12:22','اور جب پہنچ گیا اپنی قوت کو دیا ہم نے اسکو حکم اور علم اور ایسا ہی بدلہ دیتےہیں ہم نیکی والوں کو ');
INSERT INTO "al_hind" VALUES(12,23,'12:23','اور پھسلایا اسکو اس عورت نے جسکے گھر میں تھا اپنا جی تھامنے سے اور بند کردیئے دروازے اور بولی شتابی کر کہا خدا کی پناہ وہ عزیز مالک ہے میرا اچھی طرح رکھا ہے مجھ کو بیشک بھلائی نہیں پاتے جو لوگ کہ بے انصاف ہوں ');
INSERT INTO "al_hind" VALUES(12,24,'12:24','اور البتہ عوت نے فکر کیا اس کا اور اس نے فکر کیا عورت کا اگر نہ ہوتا یہ کہ دیکھے قدرت اپنے رب کی یوں ہی ہوا تاکہ ہٹائیں ہم اس سے برائی اور بے حیائی البتہ وہ ہے ہمارے برگزیدہ بندوں میں ');
INSERT INTO "al_hind" VALUES(12,25,'12:25','اور دونوں دوڑے دروازہ کو اور عورت نے چیر ڈالا اس کا کرتہ پیچھے سے اور دونوں مل گئے عورت کے خاوند سے دروازہ کے پاس بولی اور کچھ سزا نہیں ایسے شخص کی جو چاہے تیرے گھر میں برائی مگر یہی کہ قید میں ڈالا جائے یا عذاب دردناک ');
INSERT INTO "al_hind" VALUES(12,26,'12:26','یوسف بولا اسی نے خواہش کی مجھ سے کہ نہ تھاموں اپنے جی کو اور گواہی دی ایک گواہ نے عورت کے لوگوں میں سے اگر ہے کرتا اس کا پھٹا آگے سے تو عورت سچی ہے اور وہ ہے جھوٹا');
INSERT INTO "al_hind" VALUES(12,27,'12:27','اور اگر ہے کرتہ اس کا پھٹا پیچھے سے تو یہ جھوٹی ہے اور وہ سچا ہے');
INSERT INTO "al_hind" VALUES(12,28,'12:28','پھر جب دیکھا عزیز نے کرتہ اس کا پھٹا ہوا پیچھے سے کہا بیشک یہ ایک فریب ہے تم عورتوں کا البتہ تمہارا فریب بڑا ہے');
INSERT INTO "al_hind" VALUES(12,29,'12:29','یوسف جانے دے اس ذکر کو اور عورت تو بخشوا اپنا گناہ بیشک تو ہی گنہگار تھی ');
INSERT INTO "al_hind" VALUES(12,30,'12:30','اور کہنے لگیں عورتیں اس شہر میں عزیز کی عورت خواہش کرتی ہے اپنے غلام سے اسکے جی کو فریفتہ ہو گیا اس کا دل اسکی محبت میں ہم تو دیکھتے ہیں اسکو صریح خطا پر ');
INSERT INTO "al_hind" VALUES(12,31,'12:31','پھر جب سنا اس نے ان کا فریب بلوا بھیجا انکو اور تیار کی انکے واسطے ایک مجلس اور دی انکو ہر ایک کے ہاتھ میں ایک چھری اور بولی یوسف نکل آ انکے سامنے پھر جب دیکھا اس کو ششدر رہ گئیں اور کاٹ ڈالے اپنے ہاتھ اور کہنے لگیں حاشا للہ نہیں یہ شخص آدمی یہ تو کوئی فرشتہ ہے بزرگ ');
INSERT INTO "al_hind" VALUES(12,32,'12:32','بولی یہ وہی ہے کہ طعنہ دیا تھا تم نے مجھ کو اسکے واسطے اور میں نے لینا چاہا تھا اس سے اس کا جی پھر اس نے تھام رکھا اور بیشک اگر نہ کرے گا جو میں اسکو کہتی ہوں تو قید میں پڑے گا اور ہو گا بےعزت ');
INSERT INTO "al_hind" VALUES(12,33,'12:33','یوسف بولا اے رب مجھ کو قید پسند ہے اس بات سے جسکی طرف مجھ کو بلاتی ہیں اور اگر تو نہ دفع کرے گا مجھ سے ان کا فریب تو مائل ہو جاؤں گا انکی طرف اور ہو جاؤں گا بے عقل ');
INSERT INTO "al_hind" VALUES(12,34,'12:34','سو قبول کر لی اس کی دعا اس کے رب نے پھر دفع کیا اس سے ان کا فریب البتہ وہی ہے سننے والا خبردار ');
INSERT INTO "al_hind" VALUES(12,35,'12:35','پھر یوں سمجھ آیا لوگوں کی ان نشانیوں کے دیکھنے پر کہ قید رکھیں اسکو ایک مدت ');
INSERT INTO "al_hind" VALUES(12,36,'12:36','اور داخل ہوئے قید خانہ میں اسکے ساتھ دو جوان کہنے لگا ان میں سے ایک میں دیکھتا ہوں کہ میں نچوڑتا ہوں شراب اور دوسرے نے کہا میں دیکھتا ہوں کہ اٹھار رہا ہوں اپنے سر پر روٹی کہ جانور کھاتے ہیں اس میں سے بتلا ہم کو اس کی تعبیر ہم دیکھتے ہیں تجھ کو نیکی والا ');
INSERT INTO "al_hind" VALUES(12,37,'12:37','بولا نہ آنے پائے گا تم کو کھانا جو ہر روز تم کو ملتا ہے مگر بتا چکوں گا تم کو اس کی تعبیر اس کے آنے سے پہلے یہ علم ہے کہ مجھ کو سکھایا میرے رب نے میں نے چھوڑا دین اس قوم کا کہ ایمان نہیں لاتے اللہ پر اور آخرت سے وہ لوگ منکر ہیں ');
INSERT INTO "al_hind" VALUES(12,38,'12:38','اور پکڑا میں نے دین اپنے باپ دادوں کا ابراہیم اور اسحاق اور یعقوب کا ہمارا کام نہیں کہ شریک کریں اللہ کا کسی چیز کو یہ فضل ہے اللہ کا ہم پر اور سب لوگوں پر لیکن بہت لوگ احسان نہیں مانتے ');
INSERT INTO "al_hind" VALUES(12,39,'12:39','اے رفیقو قید خانہ کے بھلا کئی معبود جدا جدا بہتر یا اللہ اکیلا زبردست ');
INSERT INTO "al_hind" VALUES(12,40,'12:40','کچھ نہیں پوجتے ہو سوائے اس کے مگر نام ہیں جو رکھ لئے ہیں تم نے اور تمہارے باپ دادوں نے نہیں اتاری اللہ نے ان کی کوئی سند حکومت نہیں ہے کسی کی سوائے اللہ کے اس نے فرما دیا کہ نہ پوجو مگر اسی کو یہی ہے راستہ سیدھا پر بہت لوگ نہیں جانتے ');
INSERT INTO "al_hind" VALUES(12,41,'12:41','اے رفیقو قید خانہ کے ایک جو ہے تم دونوں میں سو پلائے گا اپنے خاوند (مالک) کو شراب اور دوسرا جو ہے سو سولی دیا جائے گا (چڑھے گا) پھر کھائیں گے جانور اس کے سر میں سے فیصل ہوا وہ کام جس کی تحقیق تم چاہتے تھے ');
INSERT INTO "al_hind" VALUES(12,42,'12:42','اور کہہ دیا یوسف نے اسکو جسکو گمان کیا تھا کہ بچے گا ان دونوں میں میرا ذکر کرنا اپنے خاوند (مالک) کے پاس سو بھلا دیا اسکو شیطان نے ذکر کرنا اپنے خاوند (مالک) سے پھر رہا قید میں کئ برس ');
INSERT INTO "al_hind" VALUES(12,43,'12:43','اور کہا بادشاہ نے میں خواب میں دیکھتا ہوں سات گائیں موٹی انکو کھاتی ہیں سات گائیں دبلی اور سات بالیں ہری اور دوسری سوکھی اے دربار والو تعبیر کہو مجھ سے میرے خواب کی اگر ہو تم خواب کی تعبیر دینے والے ');
INSERT INTO "al_hind" VALUES(12,44,'12:44','بولے یہ خیالی خواب ہیں اور ہم کو ایسے خوابوں کی تعبیر معلوم نہیں ');
INSERT INTO "al_hind" VALUES(12,45,'12:45','اور بولا وہ جو بچا تھا ان دونوں میں سے اور یاد آ گیا اس کو مدت کے بعد میں بتاؤں تم کو اس کی تعبیر سو تم مجھ کو بھیجو ');
INSERT INTO "al_hind" VALUES(12,46,'12:46','جا کر کہا اے یوسف اے سچے حکم دےہم کو اس خواب میں سات گائیں موٹی انکو کھائیں سات دبلی اور سات بالیں ہری اور دوسری سوکھی تاکہ لے جاؤں میں لوگوں کے پاس شاید انکو معلوم ہو ');
INSERT INTO "al_hind" VALUES(12,47,'12:47','کہا تم کھیتی کرو گے سات برس جم کر سو جو کاٹو اس کو چھوڑ دو اس کی بال میں مگر تھوڑا سا جو تم کھاؤ');
INSERT INTO "al_hind" VALUES(12,48,'12:48','پھر آئیں گے اس کے بعد سات برس سختی کے کھا جائیں گے جو رکھا تم نے انکے واسطے مگر تھوڑا سا جو روک رکھو گے بیج کے واسطے');
INSERT INTO "al_hind" VALUES(12,49,'12:49','پھر آئے گا اس کے پیچھے ایک برس اس میں مینہ برسے گا لوگوں پر اور اس میں رس نچوڑیں گے ');
INSERT INTO "al_hind" VALUES(12,50,'12:50','اور کہا بادشاہ نے لے آؤ اسکو میرے پاس پھر جب پہنچا اس کے پاس بھیجا ہوا آدمی کہا لو ٹ جا اپنے خاوند (مالک) کے پاس اور پوچھ اس سے کیا حقیقت ہے ان عورتوں کی جنہوں نے کاٹے تھے ہاتھ اپنے میرا رب تو ان کا فریب سب جانتا ہے ');
INSERT INTO "al_hind" VALUES(12,51,'12:51','کہا بادشاہ نے عورتوں کو کیا حقیقت ہے تمہاری جب تم نے پھسلایا یوسف کو اسکے نفس کی حفاظت سے بولیں حاشا للہ ہم کو معلوم نہیں اس پر کچھ برائی بولی عورت عزیز کی اب کھل گئ سچی بات میں نے پھسلایا تھا اسکو اس کے جی سے اور وہ سچا ہے ');
INSERT INTO "al_hind" VALUES(12,52,'12:52','یوسف نے کہا یہ اس واسطے کہ عزیز معلوم کر لیوے کہ میں نے اسکی چوری نہیں کی چھپ کر اور یہ کہ اللہ نہیں چلاتا (چلنے نہ دیتا) فریب دغا بازوں کا ');
INSERT INTO "al_hind" VALUES(12,53,'12:53','اور میں پاک نہیں کہتا اپنے جی کو بیشک جی تو سکھلاتا ہے برائی مگر جو رحم کر دیا میرے رب نے بیشک میرا رب بخشنے والا ہے مہربان ');
INSERT INTO "al_hind" VALUES(12,54,'12:54','اور کہا بادشاہ نے لے آؤ اسکو میرے پاس میں خالص کر رکھوں اسکو اپنے کام میں پھر جب بات چیت کی اس سے کہا واقعی تو نے آج سے ہمارے پاس جگہ پائی معتبر ہو کر ');
INSERT INTO "al_hind" VALUES(12,55,'12:55','یوسف نے کہا مجھ کو مقرر کر ملک کے خزانوں پر میں نگہبان ہوں خوب جاننے والا ');
INSERT INTO "al_hind" VALUES(12,56,'12:56','اور یوں قدرت دی ہم نے یوسف کو اس زمین میں جگہ پکڑتا تھا اس میں جہاں چاہتا پہنچا دیتے ہیں ہم رحمت اپنی جس کو چاہیں اور ضائع نہیں کرتے ہم بدلا بھلائی والوں کا');
INSERT INTO "al_hind" VALUES(12,57,'12:57','اور ثواب آخرت کا بہتر ہے انکو جو ایمان لائے اور رہے پرہیزگاری میں ');
INSERT INTO "al_hind" VALUES(12,58,'12:58','اور آئے بھائی یوسف کے پھر داخل ہوئے اسکے پاس تو اس نے پہچان لیا انکو اور وہ نہیں پہچانتے ');
INSERT INTO "al_hind" VALUES(12,59,'12:59','اور جب تیار کر دیا انکو ان کا اسباب کہا لے آیئو میرے پاس ایک بھائی جو تمہارا ہے باپ کی طرف سے تم نہیں دیکھتے ہو کہ میں پورا دیتا ہوں ماپ اور خوب طرح اتارتا ہوں مہمانوں کو ');
INSERT INTO "al_hind" VALUES(12,60,'12:60','پھر اگر اس کو نہ لائے میرے پاس تو تمہارے لئے بھرتی نہیں میرے نزدیک اور میرےپاس نہ آئیو ');
INSERT INTO "al_hind" VALUES(12,61,'12:61','بولے ہم خواہش کریں گے اس کے باپ سے اور ہم کو یہ کام کرنا ہے ');
INSERT INTO "al_hind" VALUES(12,62,'12:62','اور کہدیا اپنے خدمت گاروں کو رکھ دو انکی پونجی انکے اسباب میں شاید اس کو پہچانیں جب پھر کر پہنچیں اپنے گھر شاید وہ پھر آ جائیں ');
INSERT INTO "al_hind" VALUES(12,63,'12:63','پھر جب پہنچے اپنے باپ کے پاس بولے اے باپ روک دی گئ ہم سے بھرتی سو بھیج ہمارے ساتھ ہمارے بھائی کو کہ بھرتی لے آئیں اور ہم اسکے نگہبان ہیں ');
INSERT INTO "al_hind" VALUES(12,64,'12:64','کہا میں کیا اعتبار کروں تمہارا اس پر مگر وہی جیسا اعتبار کیا تھا اس کے بھائی پر اس سے پہلے سو اللہ بہتر ہے نگہبان اور وہی ہے سب مہربانوں سے مہربان ');
INSERT INTO "al_hind" VALUES(12,65,'12:65','اور جب کھولی اپنی چیز بست پائی اپنی پونچی کہ پھیر دی گئ انکی طرف بولے اے باپ ہم کو اور کیا چاہیئے یہ پونچی ہماری پھیر دی ہے ہم کو اب جائیں تو رسد لائیں ہم اپنے گھر کو اور خبرداری کر یں گے اپنے بھائی کی اور زیادہ لیویں بھرتی ایک اونٹ کی وہ بھرتی آسان ہے ');
INSERT INTO "al_hind" VALUES(12,66,'12:66','کہا ہر گز نہ بھیجوں گا اسکو تمہارے ساتھ یہاں تک کہ دو مجھ کو عہد خدا کا کہ البتہ پہنچا دو گے اسکو میرے پاس مگر یہ کہ گھیرے جاؤ تم سب پھر جب دیا اسکو سب نے عہد بولا اللہ ہماری باتوں پر نگہبان ہے ');
INSERT INTO "al_hind" VALUES(12,67,'12:67','اور کہا اے بیٹو نہ داخل ہونا ایک دروازہ سے اور داخل ہونا کئ دروازوں سے جدا جدا اور میں نہیں بچا سکتا تم کو اللہ کی کسی بات سے حکم کسی کا نہیں سوائے اللہ کے اسی پر مجھ کو بھروسہ ہے اور اسی پر بھروسہ چاہئے بھروسہ کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(12,68,'12:68','اور جب داخل ہوئے جہاں سے کہا تھا انکے باپ نے کچھ نہ بچا سکتا تھا انکو اللہ کی کسی بات سے مگر ایک خواہش تھی یعقوب کے جی میں سو پوری کر چکا اور وہ تو خبردار تھا جو کچھ ہم نے اس کو سکھایا لیکن بہت لوگوں کو خبر نہیں ');
INSERT INTO "al_hind" VALUES(12,69,'12:69','اور جب داخل ہوئے یوسف کے پاس اپنے پاس رکھا اپنے بھائی کو کہا تحقیق میں ہوں بھائی تیرا سو غمگین مت ہو ان کاموں سے جو انہوں نے کئے ہیں ');
INSERT INTO "al_hind" VALUES(12,70,'12:70','پھر جب تیار کر دیا انکے واسطے اسباب انکا رکھ دیا پینے کا پیالہ اسباب میں اپنے بھائی کے پھر پکارا پکارنے والے نے اے قافلہ والو تم تو البتہ چور ہو ');
INSERT INTO "al_hind" VALUES(12,71,'12:71','کہنے لگے منہ کر کے انکی طرف تمہاری کیا چیز گم ہو گئ ');
INSERT INTO "al_hind" VALUES(12,72,'12:72','بولے ہم نہیں پاتے بادشاہ کا پیمانہ اور جو کوئی اس کو لائے اسکو ملے ایک بوجھ اونٹ کا اور میں ہوں اس کا ضامن ');
INSERT INTO "al_hind" VALUES(12,73,'12:73','بولے قسم اللہ کی تم کو معلوم ہے ہم شرارت کرنے کو نہیں آئے ملک میں اور نہ ہم کبھی چور تھے ');
INSERT INTO "al_hind" VALUES(12,74,'12:74','بولے پھر کیا سزا ہے اسکی اگر تم نکلے جھوٹے ');
INSERT INTO "al_hind" VALUES(12,75,'12:75','کہنے لگے اس کی سزا یہ کہ جس کے اسباب میں سے ہاتھ آئے وہی اسکے بدلے میں جائے ہم یہی سزا دیتے ہیں ظالموں کو ');
INSERT INTO "al_hind" VALUES(12,76,'12:76','پھر شروع کیں یوسف نے انکی خرجیاں دیکھنی اپنے بھائی کی خرجی سے پہلے آخر کو وہ برتن نکالا اپنے بھائی کی خرجی سے یوں داؤ بتا دیا ہم نے یوسف کو وہ ہر گز نہ لے سکتا تھا اپنے بھائی کو دین (قانون) میں اس بادشاہ کے مگر جو چاہے اللہ ہم درجے بلند کرتے ہیں جس کے چاہیں اور ہر جاننے والے سے اوپر ہے ایک جاننے والا ');
INSERT INTO "al_hind" VALUES(12,77,'12:77','کہنے لگےاگر اس نے چرایا تو چوری کی تھی اسکے ایک بھائی نے بھی اس سے پہلے تب آہستہ سے کہا یوسف نے اپنے جی میں اور انکو نہ جتایا کہا جی میں کہ تم بدتر ہو درجہ میں اور اللہ خوب جانتا ہے جو تم بیان کرتے ہو ');
INSERT INTO "al_hind" VALUES(12,78,'12:78','کہنے لگے اے عزیز اس کا ایک باپ ہے بوڑھا بڑی عمر کا سو رکھ لے ایک کو ہم میں سے اس کی جگہ ہم دیکھتے ہیں تو ہے احسان کرنے والا ');
INSERT INTO "al_hind" VALUES(12,79,'12:79','بولا اللہ پناہ دے کہ ہم کسی کو پکڑیں مگر جس کے پاس پائی ہم نے اپنی چیز تو تو ہم ضرور بے انصاف ہوئے ');
INSERT INTO "al_hind" VALUES(12,80,'12:80','پھر جب ناامید ہوئے اس سے اکیلے ہو بیٹھے مشورہ کرنے کو بولا ان میں کا بڑا کیا تم کو معلوم نہیں کہ تمہارے باپ نے لیا ہے تم سے عہد اللہ کا اور پہلے جو قصور کر چکے ہو یوسف کے حق (قصہ) میں سو میں تو ہرگز نہ سرکوں گا اس ملک سے جب تک کہ حکم دے مجھ کو باپ میرا یا قضیہ چکا دے اللہ میری طرف اور وہ ہے سب سے بہتر چکانے والا ');
INSERT INTO "al_hind" VALUES(12,81,'12:81','پھر جاؤ اپنے باپ کے پاس اور کہو اے باپ تیرے بیٹے نے تو چوری کی اور ہم نے تو وہی کہا تھا جو ہم کو خبر تھی اور ہم کو غیب کی بات کا دھیان نہ تھا ');
INSERT INTO "al_hind" VALUES(12,82,'12:82','اور پوچھ لے اس بستی سے جس میں ہم تھے اور اس قافلہ سے جس میں ہم آئے ہیں اور ہم بیشک سچ کہتے ہیں ');
INSERT INTO "al_hind" VALUES(12,83,'12:83','بولا کوئی نہیں بنالی ہے تمہارے جی نے ایک بات اب صبر ہی بہتر (کام آئے ، بن پڑے) ہے شاید اللہ لے آئے میرے پاس ان سب کو وہی ہے خبردار حکمتوں والا ');
INSERT INTO "al_hind" VALUES(12,84,'12:84','اور الٹا پھرا انکے پاس سے اور بولا اے افسوس یوسف پر اور سفید ہو گئیں آنکھیں اسکی غم سے سو وہ آپ کو گھونٹ رہا تھا ');
INSERT INTO "al_hind" VALUES(12,85,'12:85','کہنے لگے قسم اللہ کی تو نہ چھوڑے گا یوسف کی یاد کو جب تک کہ گھل جائے یا ہو جائے مردہ');
INSERT INTO "al_hind" VALUES(12,86,'12:86','بولا میں تو کھولتا ہوں اپنا اضطراب اور غم اللہ کے سامنے اور جانتا ہوں اللہ کی طرف سے جو تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(12,87,'12:87','اے بیٹو جاؤ اور تلاش کرو یوسف کی اور اسکے بھائی کی اور ناامید مت ہو اللہ کے فیض سے بیشک نا امید نہیں ہوتے اللہ کے فیض سے مگر وہی لوگ جو کافر ہیں ');
INSERT INTO "al_hind" VALUES(12,88,'12:88','پھر جب داخل ہوئے اسکے پاس بولے اے عزیز پڑی ہم پر اور ہمارے گھر پر سختی اور لائے ہیں ہم پونجی ناقص سو پوری دے ہم کو بھرتی اور خیرات کر ہم پر اللہ بدلہ دیتا ہے خیرات کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(12,89,'12:89','کہا کچھ تم کو خبر ہے کہ کیا کیا تم نے یوسف سے اور اسکے بھائی سے جب تم کو سمجھ نہ تھی ');
INSERT INTO "al_hind" VALUES(12,90,'12:90','بولے کیا سچ تو ہی ہے یوسف کہا میں یوسف ہوں اور یہ ہے میرا بھائی اللہ نے احسان کیا ہم پر البتہ جو کوئی ڈرتا ہے اور صبر کرتا ہے تو اللہ ضائع نہیں کرتا حق نیکی والوں کا ');
INSERT INTO "al_hind" VALUES(12,91,'12:91','بولے قسم اللہ کی البتہ پسند کر لیا تجھ کو اللہ نے ہم سے اور ہم تھے چوکنے والے ');
INSERT INTO "al_hind" VALUES(12,92,'12:92','کہا کچھ الزام نہیں تم پر آج بخشے اللہ تم کو اور وہ ہے سب مہربانوں سے مہربان ');
INSERT INTO "al_hind" VALUES(12,93,'12:93','لیجاؤ یہ کرتا میرا اور ڈالو اسکو منہ پر میرے باپ کے کہ چلا آئے آنکھوں سے دیکھتا ہوا اور لے آؤ میرے پاس گھر اپنا سارا ');
INSERT INTO "al_hind" VALUES(12,94,'12:94','اور جب جدا ہوا قافلہ کہا انکے باپ نے میں پاتا ہوں بو یوسف کی اگر نہ کہو مجھ کو کہ بوڑھا بہک گیا ');
INSERT INTO "al_hind" VALUES(12,95,'12:95','لوگ بولے قسم اللہ کی تو تو اپنی اسی قدیم غلطی میں ہے ');
INSERT INTO "al_hind" VALUES(12,96,'12:96','پھر جب پہنچا خوشخبری والا ڈالا اس نے وہ کرتا اس کے منہ پر پھر لوٹ کر ہو گیا دیکھنے والا بولا میں نے نہ کہا تھا تم کو کہ میں جانتا ہوں اللہ کی طرف سے جو تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(12,97,'12:97','بولے اے باپ بخشوا ہمارے گناہوں کو بیشک ہم تھے چوکنے والے ');
INSERT INTO "al_hind" VALUES(12,98,'12:98','کہا دم لو بخشواؤں گا تم کو اپنے رب سے وہی ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(12,99,'12:99','پھر جب داخل ہوئے یوسف کے پاس جگہ دی اپنے پاس ماں باپ کو اور کہا داخل ہو مصر میں اللہ نے چاہا تو دل جمعی سے ');
INSERT INTO "al_hind" VALUES(12,100,'12:100','اور اونچا بٹھایا اپنے ماں باپ کو تخت پر اور سب گرے اس کے آگے سجدہ میں اور کہا اے باپ یہ بیان ہے میرے اس پہلے خواب کا اس کو میرے رب نے سچ کر دیا اور اس نے انعام کیا مجھ پر جب مجھ کو نکالا قید خانہ سے اور تم کو لے آیا گاؤں سے بعد اس کے کہ جھگڑا ڈال چکا تھا شیطان مجھ میں اور میرے بھائیوں میں میرا رب تدبیر سے کرتا ہے جو چاہتا ہے بیشک وہی ہے خبردار حکمت والا ');
INSERT INTO "al_hind" VALUES(12,101,'12:101','اے رب تو نے دی مجھ کو کچھ حکومت اور سکھایا مجھ کو کچھ پھیرنا باتوں کا اے پیدا کرنے والے آسمان اور زمین کے تو ہی میرا کارساز ہے دنیا میں اور آخرت میں موت دے مجھ کو اسلام پر اور ملا مجھ کو نیک بختوں میں ');
INSERT INTO "al_hind" VALUES(12,102,'12:102','یہ خبریں ہیں غیب کی ہم بھیجتے ہیں تیرے پاس اور تو نہیں تھا انکے پاس جب وہ ٹھہرانے لگے اپنا کام اور فریب کرنے لگے ');
INSERT INTO "al_hind" VALUES(12,103,'12:103','اور اکثر لوگ نہیں ہیں یقین کرنے والے اگرچہ تو کتنا ہی چاہے ');
INSERT INTO "al_hind" VALUES(12,104,'12:104','اور تو مانگتا نہیں ان سے اس پر کچھ بدلا یہ تو اور کچھ نہیں مگر نصیحت سارے عالم کو ');
INSERT INTO "al_hind" VALUES(12,105,'12:105','اور بہتیری نشانیاں ہیں آسمان اور زمین میں جن پر گذر ہوتا رہتا ہے ان کا اور وہ ان پر دھیان نہیں کرتے ');
INSERT INTO "al_hind" VALUES(12,106,'12:106','اور نہیں ایمان لاتے بہت لوگ اللہ پر مگر ساتھ ہی شریک بھی کرتے ہیں ');
INSERT INTO "al_hind" VALUES(12,107,'12:107','کیا نڈر ہو گئے اس سے کہ آ ڈھانکے انکو ایک آفت اللہ کے عذاب کی یا آ پہنچے قیامت اچانک اور انکو خبر نہ ہو ');
INSERT INTO "al_hind" VALUES(12,108,'12:108','کہہ دے یہ میری راہ ہے بلاتا ہوں اللہ کی طرف سمجھ بوجھ کر میں اور جو میرے ساتھ ہے اور اللہ پاک ہے اور میں نہیں شریک بتانے والوں میں ');
INSERT INTO "al_hind" VALUES(12,109,'12:109','اور جتنے بھیجے ہم نے تجھ سے پہلے وہ سب مرد ہی تھے کہ وحی بھیجتے تھے ہم انکو بستیوں کے رہنے والے سو کیا ان لوگوں نے نہیں سیر کی ملک کی کہ دیکھ لیتے کیسا ہوا انجام ان لوگوں کا جو ان سے پہلے تھے اور آخرت کا گھر تو بہتر ہے پرہیز کرنے والوں کو کیا اب بھی نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(12,110,'12:110','یہاں تک کہ جب ناامید ہونے لگے رسول اور خیال کرنے لگے کہ ان سے جھوٹ کہا گیا تھا پہنچی انکو ہماری مدد پھر بچا دیا جنکو ہم نے چاہا اور پھرتا نہیں عذاب ہمارا قوم گنہگار سے ');
INSERT INTO "al_hind" VALUES(12,111,'12:111','البتہ انکے احوال سے اپنا حال قیاس کرنا ہے عقل والوں کو کچھ بنائی ہوئی بات نہیں لیکن موافق ہے اس کلام کے جو اس سے پہلے ہے اور بیان ہر چیز کا اور ہدایت اور رحمت ان لوگوں کو جو ایمان لاتے ہیں ');
INSERT INTO "al_hind" VALUES(13,1,'13:1','یہ آیتیں ہیں کتاب کی اور جو کچھ اترا تجھ پر تیرے رب سے سو حق ہے لیکن بہت لوگ نہیں مانتے ');
INSERT INTO "al_hind" VALUES(13,2,'13:2','اللہ وہ ہے جس نے اونچے بنائے آسمان بغیر ستون دیکھتے ہو پھر قائم ہوا عرش پر اور کام میں لگا دیا سورج اور چاند کو ہر ایک چلتا ہے وقت مقرر پر (تک) تدبیر کرتا ہے کام کی ظاہر کرتا ہے نشانیاں کہ (تاکہ) شاید تم اپنے رب سے ملنے کا یقین کرو ');
INSERT INTO "al_hind" VALUES(13,3,'13:3','اور وہی ہے جس نے پھیلائی زمین اور رکھے اس میں بوجھ (پہاڑ) اور ندیاں اور ہر میوے کے رکھے اس میں جوڑے دو دو قسم ڈھانکتا ہے دن پر رات کو اس میں نشانیاں ہیں انکے واسطے جو کہ دھیان کرتے ہیں');
INSERT INTO "al_hind" VALUES(13,4,'13:4','اور زمین میں کھیت ہیں مختلف ایک دوسرے (پاس پاس) سے متصل اور باغ ہیں انگور کے اور کھیتیاں ہیں اور کھجوریں ہیں ایک کی جڑ دوسری سے ملی ہوئی اور بعضی بن ملی انکو پانی بھی ایک ہی دیا جاتا ہے اور ہم ہیں کہ بڑھا دیتے ہیں ان میں ایک کو ایک سے میووں میں ان چیزوں میں نشانیاں ہیں انکو جو غور کرتے ہیں ');
INSERT INTO "al_hind" VALUES(13,5,'13:5','اور اگر تو عجیب بات چاہے تو عجیب ہے ان کا کہنا کہ کیا جب ہو گئے ہم مٹی کیا نئے سرے سے بنائے جائیں گے وہی ہیں جو منکر ہو گئے اپنے رب سے اور وہی ہیں کہ طوق ہیں انکی گردنوں میں اور وہ ہیں دوزخ والے وہ اسی میں رہیں گے برابر ');
INSERT INTO "al_hind" VALUES(13,6,'13:6','اور جلد مانگتے ہیں تجھ سے برائی کو پہلے بھلائی سے اور گذر چکے ہیں ان سے پہلے بہت سے عذاب (مثالیں) اور تیرا رب معاف بھی کرتا ہے لوگوں کو باوجود انکے ظلم کے اور تیرے رب کا عذاب بھی سخت ہے ');
INSERT INTO "al_hind" VALUES(13,7,'13:7','اور کہتے ہیں کافر کیوں نہ اتری اس پر کوئی نشانی اسکے رب سے تیرا کام تو ڈر سنا دینا ہے اور ہر قوم کے لئے ہوا ہے راہ بتانے والا ');
INSERT INTO "al_hind" VALUES(13,8,'13:8','اللہ جانتا ہے جو پیٹ میں رکھتی ہے ہر مادہ اور جو سکڑتے ہیں پیٹ اور بڑھتے ہیں اور ہر چیز کا اس کے یہاں اندازہ ہے ');
INSERT INTO "al_hind" VALUES(13,9,'13:9','جاننے والا پوشیدہ اور ظاہر کا سب سے بڑا برتر ');
INSERT INTO "al_hind" VALUES(13,10,'13:10','برابر ہے تم میں جو آہستہ بات کہے اور جو کہے پکار کر اور جو چھپ رہا ہے رات میں اور جو گلیوں میں پھرتا ہے دن کو ');
INSERT INTO "al_hind" VALUES(13,11,'13:11','اسکے پہرے والے ہیں بندہ کے آگے سے پیچھے سے اسکی نگہبانی کرتے ہیں اللہ کے حکم سے اللہ نہیں بدلتا کسی قوم کی حالت کو جب تک وہ نہ بدلیں جو ان کے جیوں میں ہے اور جب چاہتا ہے اللہ کسی قوم پر آفت پھر وہ نہیں پھرتی اور کوئی نہیں ان کا اس کےسوا مدگار ');
INSERT INTO "al_hind" VALUES(13,12,'13:12','وہی ہےکہ تم کو دکھلاتا ہے بجلی ڈر کو (ڈرانے کو) اور امید کو اور اٹھاتا ہے بادل بھاری ');
INSERT INTO "al_hind" VALUES(13,13,'13:13','اور پڑھتا ہے گرج والا خوبیاں اسکی اور سب فرشتے اس کے ڈر سے اور بھیجتا ہے کڑک بجلیاں پھر ڈالتا ہے جس پر چاہے اور یہ لوگ جھگڑتے ہیں اللہ کی بات میں اور اس کی آن (پکڑ) سخت ہے ');
INSERT INTO "al_hind" VALUES(13,14,'13:14','اسی کا پکارنا سچ ہے اور جن لوگوں کو کہ پکارتے ہیں اس کے سوا وہ نہیں کام آتے ان کے کچھ بھی مگر جیسے کسی نے پھیلائے دونوں ہاتھ پانی کی طرف کہ آ پہنچے اسکے منہ تک اور وہ کبھی نہ پہنچے گا اس تک اور جتنی پکار ہے کافروں کی سب گمراہی ہے ');
INSERT INTO "al_hind" VALUES(13,15,'13:15','اور اللہ کو سجدہ کرتا ہے جو کوئی ہے آسمان اور زمین میں خوشی سے اور زور سے اور انکی پرچھائیاں صبح اور شام ');
INSERT INTO "al_hind" VALUES(13,16,'13:16','پوچھ کون ہے رب آسمان اور زمین کا کہدے اللہ ہے کہہ پھر کیا تم نے پکڑے ہیں اس کے سوا ایسے حمایتی جو مالک نہیں اپنے بھلے اور برے کے کہہ کیا برابر ہوتا ہے اندھا اور دیکھنے والا یا کہیں برابر ہے اندھیرا اور اجالا کیا ٹھہرائے ہیں انہوں نے اللہ کے لئے شریک کہ انہوں نے کچھ پیدا کیا ہے جیسے پیدا کیا اللہ نے پھر مشتبہ ہو گئ پیدائش انکی نظر میں کہہ اللہ ہے پیدا کرنے والا ہر چیز کا اور وہی ہے اکیلا زبردست ');
INSERT INTO "al_hind" VALUES(13,17,'13:17','اتارا اس نے آسمان سے پانی پھر بہنے لگے نالےاپنی اپنی موافق پھر اوپر لے آیا وہ نالا جھاگ پھولا ہوا اور جس چیز کو دھونکتے ہیں آگ میں واسطے زیور کے یا اسباب کے اسمیں بھی جھاگ ہے ویسا ہی یوں بیان (ٹھہراتا ہے) کرتا ہے اللہ حق اور باطل کو سو وہ جھاگ تو جاتا رہتا ہے سوکھ کر اور وہ جو کام آتا ہے لوگوں کے سو باقی رہتا ہے زمین میں اس طرح بیان کرتا ہے اللہ مثالیں ');
INSERT INTO "al_hind" VALUES(13,18,'13:18','جنہوں نے مانا اپنے رب کا حکم ان کے واسطے بھلائی ہے اور جنہوں نے اس کا حکم نہ مانا اگر انکے پاس ہو جو کچھ کہ زمین میں ہے سارا اور اتنا ہی اس کےساتھ اور تو سب دیویں (دے ڈالیں) اپنے بدلہ میں ان لوگوں کے لئے ہے برا حساب اور ٹھکانا ان کا دوزخ ہے اور وہ بری (برا بچھونا ہے) آرام کی جگہ ہے');
INSERT INTO "al_hind" VALUES(13,19,'13:19','بھلا جو شخص جانتا ہے کہ جو کچھ اترا تجھ پر تیرے رب سے حق ہے برابر ہو سکتا ہے اسکے جو کہ اندھا ہے سمجھتے وہی ہیں جنکو عقل ہے ');
INSERT INTO "al_hind" VALUES(13,20,'13:20','وہ لوگ جو پورا کرتے ہیں اللہ کے عہد کو اور نہیں توڑتے اس عہد کو ');
INSERT INTO "al_hind" VALUES(13,21,'13:21','اور وہ لوگ جو ملاتے ہیں جس کو اللہ نے فرمایا ملانا اور ڈرتے ہیں اپنے رب سے اور اندیشہ رکھتے ہیں برے حساب کا ');
INSERT INTO "al_hind" VALUES(13,22,'13:22','اور وہ لوگ جنہوں نے صبر کیا خوشی کو اپنے رب کی اور قائم رکھی نماز اور خرچ کیا ہمارے دیے میں سے پوشیدہ (چھپے) اور ظاہر [کھلے) اور کرتے ہیں برائی کے مقابلہ میں بھلائی ان لوگوں کے لئے ہے آخرت کا گھر');
INSERT INTO "al_hind" VALUES(13,23,'13:23','باغ ہیں رہنے کے داخل ہوں گے ان میں اور جو نیک ہوئے انکے باپ دادوں میں اور جو روؤں میں اور اولاد میں اور فرشتے آئیں ان کے پاس ہر دروازے سے');
INSERT INTO "al_hind" VALUES(13,24,'13:24','کہیں گے سلامتی تم پر بدلے اسکے کہ تم نے صبر کیا سو خوب ملا عاقبت کا گھر ');
INSERT INTO "al_hind" VALUES(13,25,'13:25','اور جو لوگ توڑتے ہیں عہد اللہ کا مضبوط کرنے کے بعد اور قطع کرتے ہیں اس چیز کو جس کو فرمایا اللہ نے جوڑنا اور فساد اٹھاتے ہیں ملک میں ایسےلوگ انکے واسطے ہے لعنت اور انکے لئے ہے برا گھر ');
INSERT INTO "al_hind" VALUES(13,26,'13:26','اللہ کشادہ کرتا ہے روزی جس کو چاہے اور تنگ کرتا ہے اور فریفتہ ہیں دنیا کی زندگی پر اور دنیا کی زندگی کچھ نہیں آخرت کے آگے مگر متاع (مال) حقیر ');
INSERT INTO "al_hind" VALUES(13,27,'13:27','اور کہتے ہیں کافر کیوں نہ اتری اس پر کوئی نشانی اس کے رب سے کہہ دے اللہ گمراہ (بچلاتا ہے) کرتا ہے جس کو چاہے اور راہ دکھلاتا ہے اپنی طرف اسکو جو رجوع ہوا ');
INSERT INTO "al_hind" VALUES(13,28,'13:28','وہ لوگ جو ایمان لائے اور چین پاتے ہیں انکے دل اللہ کی یاد سے سنتا ہے اللہ کی یاد ہی سے چین پاتے ہیں دل ');
INSERT INTO "al_hind" VALUES(13,29,'13:29','جو لوگ ایمان لائے اور کام کئے اچھے خوشحالی (خوبی) ہے انکے واسطے اور اچھا ٹھکانا ');
INSERT INTO "al_hind" VALUES(13,30,'13:30','اسی طرح تجھ کو بھیجا ہم نے ایک امت میں کہ گذر چکی ہیں اس سے پہلے بہت امتیں تاکہ سنا دے تو انکو جو حکم بھیجا ہم نے تیری طرف اور وہ منکر ہوتے ہیں رحمٰن سے تو کہہ وہی رب میرا ہے کسی کی بندگی نہیں اسکے سوا اسی پر میں نے بھروسہ کیا ہے اور اسی کی طرف آتا ہوں رجوع کر کے ');
INSERT INTO "al_hind" VALUES(13,31,'13:31','اور اگر کوئی قرآن ہوا ہوتا کہ چلیں اس سے پہاڑ یا ٹکڑے ہووے اس سے زمین یا بولیں ( بولنے لگیں) اس سے مردے تو کیا ہوتا بلکہ سب کام تو اللہ کے ہاتھ میں ہیں سو کیا خاطر جمع نہیں ایمان والوں کو اس پر کہ اگر چاہے اللہ تو راہ پر لائے سب لوگوں کو اور برابر پہنچتا رہے گا منکروں کو ان کے کرتوت پر صدمہ (دھڑکا) یا اترے گا انکے گھر سے نزدیک جب تک کہ پہنچے وعدہ اللہ کا بیشک اللہ خلاف نہیں کرتا اپنا وعدہ ');
INSERT INTO "al_hind" VALUES(13,32,'13:32','اور ٹھٹھا کر چکے ہیں کتنے رسولوں سے تجھ سے پہلے سو ڈھیل دی میں نے منکروں کو پھر ان کو پکڑ لیا سو کیسا تھا میرا بدلہ ');
INSERT INTO "al_hind" VALUES(13,33,'13:33','بھلا جو لئے کھڑا ہے ہر کسی کے سر پر جو کچھ اس نے کیا ہے (اوروں کی برابر ہو سکتا ہے) اور مقرر کرتے ہیں اللہ کے لئے شریک کہہ ان کا نام لو یا اللہ کو بتلاتے ہو جو وہ نہیں جانتا زمین میں یا کرتے ہو اوپر ہی اوپر باتیں یہ نہیں بلکہ بھلے سمجھا دیے ہیں منکروں کو ان کے فریب اور وہ روک دیئے گئے ہیں راہ سے اور جس کو گمراہ کرے اللہ سو کوئی نہیں اسکو راہ بتانے والا ');
INSERT INTO "al_hind" VALUES(13,34,'13:34','ان کو مار پڑتی ہے دنیا کی زندگی میں اور آخرت کی مار تو بہت ہی سخت ہے اور کوئی نہیں انکو اللہ سے بچانے والا ');
INSERT INTO "al_hind" VALUES(13,35,'13:35','حال جنت کا جس کا وعدہ ہے پرہیزگاروں سے بہتی ہیں اس کے نیچے نہریں میوہ اس کا ہمیشہ ہے اور سایہ بھی یہ بدلہ ہے ان کا جو ڈرتے رہے اور بدلہ منکروں کا آگ ہے ');
INSERT INTO "al_hind" VALUES(13,36,'13:36','اور وہ لوگ جن کو ہم نے دی ہے کتاب خوش ہوتے ہیں اس سے جو نازل ہوا تجھ پر اور بعضےفرقے نہیں مانتے اسکی بعضی بات کہہ مجھ کو یہی حکم ہوا کہ بندگی کروں اللہ کی اور شریک نہ کروں اس کا اسی کی طرف بلاتا ہوں اور اسی کی طرف ہے میرا ٹھکانہ ');
INSERT INTO "al_hind" VALUES(13,37,'13:37','اور اسی طرح اتارا ہم نے یہ کلام حکم عربی زبان میں اور اگر تو چلے انکی خواہش کے موافق بعد اس علم کےجو تجھ کو پہنچ چکا کوئی نہیں تیرا اللہ سے حمایتی اور نہ بچانے والا ');
INSERT INTO "al_hind" VALUES(13,38,'13:38','اور بھیج چکے ہیں ہم کتنے رسول تجھ سے پہلے اور ہم نے دی تھیں انکو جو روئیں اور اولاد اور نہیں ہوا کسی رسول سے کہ وہ لے آئے کوئی نشانی مگر اللہ کے اذن سے ہر ایک وعدہ ہے لکھا ہوا ');
INSERT INTO "al_hind" VALUES(13,39,'13:39','مٹاتا ہے اللہ جو چاہے اور باقی رکھتا ہے اور اسی کے پاس ہے اصل کتاب ');
INSERT INTO "al_hind" VALUES(13,40,'13:40','اور اگر دکھلا دیں ہم تجھ کو کوئی وعدہ جو ہم نے کیا ہے ان سے یا تجھ کو اٹھالیویں سو تیرا ذمہ تو پہنچا دینا ہے اور ہمارا ذمہ ہے حساب لینا ');
INSERT INTO "al_hind" VALUES(13,41,'13:41','کیا وہ نہیں دیکھتے کہ ہم چلے آتے ہیں زمین کو گھٹاتے اس کے کناروں سے اور اللہ حکم کرتا ہے کوئی نہیں کہ پیچھے ڈالے اس کا حکم اور وہ جلد لیتا ہے حساب ');
INSERT INTO "al_hind" VALUES(13,42,'13:42','اور فریب کر چکے ہیں جو ان سے پہلے تھے سو اللہ کے ہاتھ میں ہے سب فریب جانتا ہے جو کچھ کماتا ہے ہر ایک جی اور اب معلوم کئے لیتے ہیں کافر کہ کس کا ہوتا ہے پچھلا گھر ');
INSERT INTO "al_hind" VALUES(13,43,'13:43','اور کہتےہیں کافر تو بھیجا ہوا نہیں آیا کہدے اللہ کافی ہے گواہ میرے اور تمہارے بیچ میں اور جس کو خبر ہے کتاب کی ');
INSERT INTO "al_hind" VALUES(14,1,'14:1','الرٓ یہ ایک کتاب ہے کہ ہم نے اتاری تیری طرف کہ تو نکالے لوگوں کو اندھیروں سے اجالے کی طرف انکے رب کے حکم سے رستہ (راہ) پر (کی طرف) اس زبردست خوبیوں والے');
INSERT INTO "al_hind" VALUES(14,2,'14:2','اللہ کہ جس کا ہے جو کچھ کہ موجود ہے آسمانوں میں اور جو کچھ ہے زمین میں اور مصیبت ہے کافروں کو ایک سخت عذاب سے ');
INSERT INTO "al_hind" VALUES(14,3,'14:3','جو کہ پسند رکھتے ہیں زندگی دنیا کی آخرت سے اور روکتے ہیں اللہ کی راہ سے اور تلاش کرتے ہیں (نکالنا چاہے ہیں) اس میں کجی وہ راستہ بھول کر جاپڑے ہیں دور ');
INSERT INTO "al_hind" VALUES(14,4,'14:4','اور کوئی رسول نہیں بھیجا ہم نے مگر بولی بولنے والا اپنی قوم کی تاکہ ان کو سمجھائے پھر راستہ بھلاتا ہے (بھٹکاتا ہے) اللہ جس کو چاہے اور راستہ دکھلا دیتا ہے (دیتا ہے ) جسکو چاہے اور وہ ہے زبردست حکمتوں والا ');
INSERT INTO "al_hind" VALUES(14,5,'14:5','اور بھیجا تھا ہم نے موسٰی کو اپنی نشانیاں دے کر کہ نکال اپنی قوم کو اندھیروں سے اجالے کی طرف اور یاد دلا انکو دن اللہ کے البتہ اس میں نشانیاں ہیں اس کو جو صبر کرنے والا ہے شکر گذار (حق ماننے والا) ');
INSERT INTO "al_hind" VALUES(14,6,'14:6','اور جب کہا موسٰی نے اپنی قوم کو یاد کرو اللہ کا احسان اپنے اوپر جب چھڑا دیا تم کو فرعون کی قوم سے وہ پہنچاتے تھے تم کو برا عذاب اور ذبح کرتے تمہارے بیٹوں کو اور زندہ رکھتے تمہاری عورتوں کو اور اس میں مدد ہوئی تمہارے رب کی طرف سے بڑی ');
INSERT INTO "al_hind" VALUES(14,7,'14:7','اور جب سنا دیا تمہارے رب نے اگر احسان مانو گے تو اور بھی دوں گا تم کو اور اگر ناشکری کرو گے تو میرا عذاب البتہ سخت ہے ');
INSERT INTO "al_hind" VALUES(14,8,'14:8','اور کہا موسٰی نے اگر کفر (منکر ہو گے) کرو گے تم اور جو لوگ زمین میں ہیں سارے تو اللہ بے پروا ہے سب خوبیوں والا ');
INSERT INTO "al_hind" VALUES(14,9,'14:9','کیا نہیں پہنچی تم کو خبر ان لوگوں کی جو پہلے تھے تم سے قوم نوح کی اور عاد اور ثمود اور جو ان سے پیچھے ہوئے کسی کو انکی خبر نہیں مگر اللہ کو آئے انکے پاس انکے رسول نشانیاں لے کر پھر لوٹائے (الٹے دے لئے) انہوں نے اپنے ہاتھ اپنے منہ میں اور بولے ہم نہیں مانتے جو تم کو دے کر بھیجا اور ہم کو تو شبہ ہے اس راہ میں جسکی طرف تم ہم کو بلاتے ہو خلجان میں ڈالنے والا');
INSERT INTO "al_hind" VALUES(14,10,'14:10','بولے انکے رسول کیا اللہ میں شبہ ہے جس نے بنائے آسمان اور زمین وہ تم کو بلاتا ہے تاکہ بخشے تم کو کچھ گناہ تمہارے اور ڈھیل دے تم کو ایک وعدہ تک جو ٹھہر چکا ہے کہنے لگے تم تو یہی آدمی ہو ہم جیسے تم چاہتے ہو کہ روک دو ہم کو ان چیزوں سے جن کو پوجتے رہے ہمارے باپ دادے سو لاؤ کوئی سند کھلی ہوئی ');
INSERT INTO "al_hind" VALUES(14,11,'14:11','ان کو کہا ان کے رسولوں نے ہم تو یہی آدمی ہیں جیسےتم لیکن اللہ احسان کرتا ہے اپنے بندوں میں جس پر چاہے اور ہمارا کام نہیں کہ لے آئیں تمہارے پاس سند مگر اللہ کے حکم سے اور اللہ پر بھروسہ چاہئے ایمان والوں کو ');
INSERT INTO "al_hind" VALUES(14,12,'14:12','اور ہم کو کیا ہوا کہ بھروسہ نہ کریں اللہ پر اور وہ سمجھا چکا ہم کو ہماری راہیں اور ہم صبر کریں گے ایذا پر جو تم ہم کو دیتے ہو اور اللہ پر بھروسہ چاہئے بھروسے والوں کو ');
INSERT INTO "al_hind" VALUES(14,13,'14:13','اور کہا کافروں نے اپنے رسولوں کو ہم نکال دیں گے تم کو اپنی زمین سے یا لوٹ آؤ ہمارے دین میں تب حکم بھیجا انکو ان کے رب نے ہم غارت کریں گے ان ظالموں کو');
INSERT INTO "al_hind" VALUES(14,14,'14:14','اور آباد کریں گے تم کو اس زمین میں ان کے پیچھے یہ ملتا ہے اس کو جو ڈرتا ہے کھڑے ہونے سے میرے سامنے اور ڈرتا ہے میرے عذاب کے وعدہ سے ');
INSERT INTO "al_hind" VALUES(14,15,'14:15','اور فیصلہ (فتح) لگے ماننے پیغمبر اور نامراد ہو ا ہر ایک سرکش ضدی (ضد کرنے والا) ');
INSERT INTO "al_hind" VALUES(14,16,'14:16','پیچھے اس کے دوزخ ہے اور پلائیں گے اس کو پانی پیپ کا ');
INSERT INTO "al_hind" VALUES(14,17,'14:17','گھونٹ گھونٹ پیتا ہے اسکو اور گلے سے نہیں اتار سکتا اور چلی آتی ہے اس پر موت ہر طرف (جگہ) سے اور وہ نہیں مرتا اور اس کے پیچھے عذاب ہے سخت ');
INSERT INTO "al_hind" VALUES(14,18,'14:18','حال ان لوگوں کا جو منکر ہوئے اپنے رب سے انکے عمل ہیں جیسے وہ راکھ کہ زور کی چلے اس پر ہوا آندھی کے دن کچھ ان کے ہاتھ میں نہ ہو گا اپنی کمائی میں سے یہی ہے بہک کر دور جا پڑنا ');
INSERT INTO "al_hind" VALUES(14,19,'14:19','تو نے کیا نہیں دیکھا کہ اللہ نے بنائے آسمان اور زمین جیسی چاہئے اگر چاہے تم کو لیجائے اور لائے کوئی پیدائش (مخلوق) نئی');
INSERT INTO "al_hind" VALUES(14,20,'14:20','اور یہ اللہ کو کچھ مشکل نہیں ');
INSERT INTO "al_hind" VALUES(14,21,'14:21','سامنے کھڑے ہوں گے اللہ کے سارے پھر کہیں گے کمزور بڑائی والوں کو ہم تو تمہارے تابع تھے سو کیا بچاؤ گے ہم کو اللہ کے کسی عذاب سے کچھ وہ کہیں گے اگر ہدایت (راہ پر لانا) کرتا ہم کو اللہ تو البتہ ہم تم کو ہدایت (راہ پر لاتے) کرتے اب برابر ہے ہمارےحق میں ہم بے قراری کریں یا صبر کریں ہم کو نہیں خلاصی ');
INSERT INTO "al_hind" VALUES(14,22,'14:22','اور بولا شیطان جب فیصل ہو چکا سب کام بیشک اللہ نے تم کو دیا تھا سچا وعدہ اور میں نے تم سے وعدہ کیا پھر (سو) جھوٹا کیا اور میری تم پر کچھ حکومت نہ تھی مگر یہ کہ میں نے بلایا تم کو پھر تم نے مان لیا میری بات کو سو الزام نہ دو مجھ کو اور الزام دو اپنے آپ کو نہ میں تمہاری فریاد کو پہنچوں نہ تم میری فریاد کو پہنچو میں منکر ہوں (مجھ کو قبول نہیں) جو تم نے مجھ کو شریک بنایا تھا اس سے پہلے البتہ جو ظالم ہیں انکے لئے ہے عذاب دردناک ');
INSERT INTO "al_hind" VALUES(14,23,'14:23','اور داخل کئے گئے جو لوگ ایمان لائے تھے اور کام کئے تھے نیک باغوں میں جن کے نیچے بہتی ہیں نہریں ہمیشہ رہیں (رہا کریں انہی میں) ان میں اپنے رب کے حکم سے انکی ملاقات ہے وہاں سلام ');
INSERT INTO "al_hind" VALUES(14,24,'14:24','تو نے نہ دیکھا کیسی بیان کی اللہ نے ایک مثال بات ستھری جیسے ایک درخت ستھرا اسکی جڑ مضبوط ہے اور ٹہنے (شاخیں) ہیں آسمان میں ');
INSERT INTO "al_hind" VALUES(14,25,'14:25','لاتا ہے پھل اپنا ہر وقت پر اپنے رب کے حکم سے اور بیان کرتا ہے اللہ مثالیں لوگوں کے واسطے تاکہ وہ فکر کریں (سوچیں)');
INSERT INTO "al_hind" VALUES(14,26,'14:26','اور مثال گندی بات کی جیسے درخت گندا اکھاڑ لیا (پھینکا) اسکو زمین کے اوپر سے کچھ نہیں اسکو ٹھہراؤ (جماؤ) ');
INSERT INTO "al_hind" VALUES(14,27,'14:27','مضبوط کرتا ہے اللہ ایمان والوں کو مضبوط بات سے دنیا کی زندگی میں اور آخرت میں اور بچلا (راہ بھلا دیتا ہے) دیتا ہے اللہ بے انصافوں کو اور کرتا ہے اللہ جو چاہے ');
INSERT INTO "al_hind" VALUES(14,28,'14:28','تو نے نہ دیکھا انکو جنہوں نے بدلہ کیا اللہ کے احسان کا ناشکری اور اتارا اپنی قوم کو تباہی کے گھر میں ');
INSERT INTO "al_hind" VALUES(14,29,'14:29','جو دوزخ ہے داخل ہوں گے اس میں اور وہ برا ٹھکانہ ہے');
INSERT INTO "al_hind" VALUES(14,30,'14:30','اور ٹھہرائے اللہ کے لئے مقابل کہ بہکائیں لوگوں کو اس کی راہ سے تو کہہ مزا اڑا لو پھر تو کو لوٹنا ہے طرف آگ کی ');
INSERT INTO "al_hind" VALUES(14,31,'14:31','کہدے میرے بندوں کو جو ایمان لائے ہیں قائم رکھیں نماز اور خرچ کریں ہماری دی ہوئی روزی میں سے پوشیدہ اور ظاہر (چھپے اور کھلے) پہلے اس سے کہ آئے وہ دن جس میں نہ سودا (خرید و فروخت) ہے نہ دوستی ');
INSERT INTO "al_hind" VALUES(14,32,'14:32','اللہ وہ ہے جس نے بنائے آسمان اور زمین اور اتارا آسمان سے پانی پھر اس سے نکالی روزی تمہاری میوے اور کہنے میں کیا تمہارے کشتی کو (کام میں دیں تمہارے کشتیاں کہ چلیں) کہ چلے دریا میں اسکے حکم سے اور کام میں لگایا (دیں) تمہارے ندیوں (ندیاں) کو');
INSERT INTO "al_hind" VALUES(14,33,'14:33','اور کام میں لگا دیا تمہارے سورج اور چاند کو ایک دستور پر برابر اور کام میں لگا دیا تمہارے رات اور دن کو ');
INSERT INTO "al_hind" VALUES(14,34,'14:34','اور دیا تم کو ہر چیز میں سےجو تم نے مانگی اور اگر گنو احسان اللہ کے نہ پورے کر سکو بیشک آدمی بڑا بے انصاف ہے ناشکر ');
INSERT INTO "al_hind" VALUES(14,35,'14:35','اور جس وقت کہا ابراہیم نے اے رب کر دے اس شہر کو امن والا اور دور کر مجھ کو اور میری اولاد کو اس بات سے کہ ہم پوجیں مورتوں کو ');
INSERT INTO "al_hind" VALUES(14,36,'14:36','اے رب انہوں نے گمراہ کیا (گمراہی میں ڈالا) بہت لوگوں کو سو جس نے پیروی کی میری (جو کوئی میرے رستہ پر چلا) وہ تو میرا ہے اور جس نے میرا کہنا نہ مانا سو تو بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(14,37,'14:37','اے رب میں نے بسایا ہے اپنی ایک اولاد کو میدان میں کہ جہاں کھیتی نہیں تیرے محترم (حرمت والے) گھر کے پاس اے رب ہمارے تاکہ قائم رکھیں نماز کو سو رکھ بعض لوگوں کے دل کہ مائل (جھکتے رہیں ) ہوں ان کی طرف اور روزی دے انکو میووں سے شاید وہ شکر کریں ');
INSERT INTO "al_hind" VALUES(14,38,'14:38','اے رب ہمارے تو تو جانتا ہے جو کچھ ہم کرتے ہیں چھپا کر اور جو کچھ کرتے ہیں دکھا کر (کھول کر) اور مخفی نہیں اللہ پر کوئی چیز زمین میں نہ آسمان میں ');
INSERT INTO "al_hind" VALUES(14,39,'14:39','شکر ہے اللہ کا جس نے بخشا مجھ کو اتنی بڑی عمر میں اسمٰعیل اور اسحٰق بیشک میرا رب سنتا ہے دعا کو ');
INSERT INTO "al_hind" VALUES(14,40,'14:40','اے رب میرے کر مجھ کو کہ قائم رکھوں نماز اور میری اولاد میں سے بھی اے رب میرے اور قبول کر میری دعا ');
INSERT INTO "al_hind" VALUES(14,41,'14:41','اے ہمارے رب بخش مجھ کو اور میرے ماں باپ کو اور سب ایمان والوں کو جس دن قائم ہو حساب ');
INSERT INTO "al_hind" VALUES(14,42,'14:42','اور ہرگز مت خیال کر کہ اللہ بیخبر ہے ان کاموں سے جو کرتے ہیں بے انصاف ان کو تو ڈھیل دے رکھی ہے (چھوڑ رکھا ہے) اس دن کے لئے کہ پتھرا جائیں گی (کھلی رہ جائیں گی) آنکھیں ');
INSERT INTO "al_hind" VALUES(14,43,'14:43','دوڑتے ہوں گے اوپر اٹھائے اپنے سر پھر کر نہیں آئیں گی انکی طرف انکی آنکھیں اور دل انکے اڑ گئے ہوں گے ');
INSERT INTO "al_hind" VALUES(14,44,'14:44','اور ڈرا دے لوگوں کو اس دن سے کہ آئے گا ان پر عذاب تب کہیں گے ظالم اے رب ہمارے مہلت دے ہم کو تھوڑی مدت تک کہ ہم قبول کر لیں تیرے بلانے کو اور پیروی کر لیں رسولوں کی کیا تم پہلے قسم نہ کھاتے تھے کہ تم کو نہیں دنیا سے ٹلنا (کچھ زوال) ');
INSERT INTO "al_hind" VALUES(14,45,'14:45','اور آباد تھے تم بستیوں میں انہی لوگوں کی جنہوں نے ظلم کیا اپنی جان پر اور کھل چکا تھا تم کو کہ کیسا کیا ہم نے ان سے اور بتلائے ہم نے تم کو سب قصے ');
INSERT INTO "al_hind" VALUES(14,46,'14:46','اور یہ بنا چکے ہیں اپنا داؤ اور اللہ کے آگے ہے ان کا داؤ اور نہ ہو گا ان کا داؤ کہ ٹل جائیں اس سے پہاڑ ');
INSERT INTO "al_hind" VALUES(14,47,'14:47','سو خیال مت کر کہ اللہ خلاف کرے گا اپنا وعدہ اپنے رسولوں سے بیشک اللہ زبردست ہے بدلہ لینے والا ');
INSERT INTO "al_hind" VALUES(14,48,'14:48','جس دن بدلی جائے اس زمین سے اور زمین اور بدلے جائیں آسمان اور لوگ نکل کھڑے ہوں سامنے اللہ اکیلے زبردست کے ');
INSERT INTO "al_hind" VALUES(14,49,'14:49','اور دیکھے تو گنہگاروں کو اس دن باہم جکڑے ہوئے زنجیروں میں ');
INSERT INTO "al_hind" VALUES(14,50,'14:50','کرتے ان کے ہیں گندھک کے اور ڈھانکے لیتی ہے انکے منہ کو آگ ');
INSERT INTO "al_hind" VALUES(14,51,'14:51','تاکہ بدلہ دے اللہ ہر ایک جی کو اس کی کمائی کا بیشک اللہ جلد کرنے والا ہے حساب ');
INSERT INTO "al_hind" VALUES(14,52,'14:52','یہ خبر پہنچا دینی ہے لوگوں کو اور تاکہ چونک جائیں اس سے اور تاکہ جان لیں کہ معبود وہی ہے ایک ہے اور تاکہ سوچ لیں عقل والے ');
INSERT INTO "al_hind" VALUES(15,1,'15:1','یہ آیتیں ہیں کتاب کی اور واضح قراآن کی ');
INSERT INTO "al_hind" VALUES(15,2,'15:2','کسی وقت آرزو کریں گے یہ لوگ جو منکر ہیں کیا اچھا ہوتا جو ہوتے مسلمان ');
INSERT INTO "al_hind" VALUES(15,3,'15:3','چھوڑ دے انکو کھالیں اور برت لیں (فائدہ اٹھا لیں) اور امید میں (پر بھولے) لگے رہیں سو آئندہ معلوم کر لیں گے ');
INSERT INTO "al_hind" VALUES(15,4,'15:4','اور کوئی بستی ہم نے غارت نہیں کی مگر اس کا وقت لکھا ہوا تھا مقرر ');
INSERT INTO "al_hind" VALUES(15,5,'15:5','نہ سبقت کرتا ہے کوئی فرقہ اپنے وقت مقرر سے اور نہ پیچھے رہتا ہے ');
INSERT INTO "al_hind" VALUES(15,6,'15:6','اور لوگ کہتے ہیں اے وہ شخص کہ تجھ پر اترا ہے قرآن (نصیحت) تو بیشک دیوانہ ہے ');
INSERT INTO "al_hind" VALUES(15,7,'15:7','کیوں نہیں لے آتا ہمارے پاس فرشتوں کو اگر تو سچا ہے ');
INSERT INTO "al_hind" VALUES(15,8,'15:8','ہم نہیں اتارتے فرشتوں کو مگر کام پورا (ٹھیک) کر کے اور اس وقت نہ ملے گی ان کو مہلت (ڈھیل) ');
INSERT INTO "al_hind" VALUES(15,9,'15:9','ہم نے آپ اتاری ہے یہ نصیحت اور ہم آپ اس کے نگہبان ہیں ');
INSERT INTO "al_hind" VALUES(15,10,'15:10','اور ہم بھیج چکے ہیں رسول تجھ سے پہلے اگلے فرقوں میں');
INSERT INTO "al_hind" VALUES(15,11,'15:11','اور نہیں آتا ان کے پاس کوئی رسول مگر کرتے رہے ہیں اس سے ہنسی ');
INSERT INTO "al_hind" VALUES(15,12,'15:12','اسی طرح بٹھا دیتے ہیں ہم اسکو دل میں گنہگاروں کے ');
INSERT INTO "al_hind" VALUES(15,13,'15:13','یقین نہ لائیں گے اس پر اور ہوتی آئی ہے رسم پہلوں کی ');
INSERT INTO "al_hind" VALUES(15,14,'15:14','اور اگر ہم کھول دیں ان پر دروازہ آسمان سے اور سارے دن اس میں چڑھتے رہیں');
INSERT INTO "al_hind" VALUES(15,15,'15:15','توبھی یہی کہیں گے کہ باندھ دیا ہے ہماری نگاہ کو نہیں بلکہ ہم لوگوں پر جادو ہوا ہے ');
INSERT INTO "al_hind" VALUES(15,16,'15:16','اور ہم نے بنائے ہیں آسمان میں برج اور رونق دی اسکو دیکھنے والوں کی نظر میں ');
INSERT INTO "al_hind" VALUES(15,17,'15:17','اور محفوظ رکھا ہم نے اسکو ہر شیطان مردود سے');
INSERT INTO "al_hind" VALUES(15,18,'15:18','مگر جو چوری سے سن بھاگا سو اسکے پیچھے پڑا انگارہ چمکتا ہوا ');
INSERT INTO "al_hind" VALUES(15,19,'15:19','اور زمین کو ہم نے پھیلایا اور رکھ دیے اس پر بوجھ (پہاڑ) اور اگائی اس میں ہر چیز اندازے سے');
INSERT INTO "al_hind" VALUES(15,20,'15:20','اور بنا دیے تمہارے واسطے اس میں معیشت کے اسباب اور وہ چیزیں جنکو تم روزی نہیں دیتے ');
INSERT INTO "al_hind" VALUES(15,21,'15:21','اور ہر چیز کے ہمارے پاس خزانے ہیں اور اتارتے ہیں ہم اندازہ معین پر (ٹھہرے ہوئے اندازہ پر) ');
INSERT INTO "al_hind" VALUES(15,22,'15:22','اور چلائیں ہم نے ہوائیں رس بھری (بوجھل کرنے والی ابر کی) پھر اتارا ہم نے آسمان سے پانی پھر تم کو وہ پلایا اور تمہارے پاس نہیں اس کا خزانہ ');
INSERT INTO "al_hind" VALUES(15,23,'15:23','اور ہم ہی ہیں جلانے والے اور مارنے والے اور ہم ہی ہیں پیچھے رہنے والے ');
INSERT INTO "al_hind" VALUES(15,24,'15:24','اور ہم نے جان رکھا ہے آگے بڑھنے والوں کو تم میں سے اور جان رکھا ہے پیچھے رہنے والوں کو ');
INSERT INTO "al_hind" VALUES(15,25,'15:25','اور تیرا رب وہی اکٹھا کر لائے گا انکو بیشک وہی ہے حکمتوں والا خبردار ');
INSERT INTO "al_hind" VALUES(15,26,'15:26','اور بنایا ہم نے آدمی کو کھنکھناتے (بجنے والی مٹی سے) سنے (جو بنی ہے سڑے ہوئے گارے سے) ہوئے گارے سے ');
INSERT INTO "al_hind" VALUES(15,27,'15:27','اور جان (جنوں کو) بنایا ہم نے اس سے پہلے لو کی آگ سے ');
INSERT INTO "al_hind" VALUES(15,28,'15:28','اور جب کہا تیرے رب نے فرشتوں کو میں بناؤں گا ایک بشر کھنکھناتے سنے ہوئے گارے سے');
INSERT INTO "al_hind" VALUES(15,29,'15:29','پھر جب ٹھیک کروں اسکو اور پھونک دوں اس میں اپنی جان سے تو گر پڑیو اسکے آگے سجدہ کرتے ہوئے ');
INSERT INTO "al_hind" VALUES(15,30,'15:30','تب سجدہ کیا ان فرشتوں نے سب نے مل کر');
INSERT INTO "al_hind" VALUES(15,31,'15:31','مگر ابلیس نے نہ مانا کہ ساتھ ہو سجدہ کرنے والوں کے');
INSERT INTO "al_hind" VALUES(15,32,'15:32','فرمایا اے ابلیس کیا ہوا تجھ کو کہ ساتھ نہ ہوا سجدہ کرنے والوں کے');
INSERT INTO "al_hind" VALUES(15,33,'15:33','بولا میں وہ نہیں کہ سجدہ کروں ایک بشر کو جسکو تو نے بنایا کھنکھناتے سنے ہوئے گارے سے');
INSERT INTO "al_hind" VALUES(15,34,'15:34','فرمایا تو تو نکل یہاں سے تجھ پر مار ہے ');
INSERT INTO "al_hind" VALUES(15,35,'15:35','اور تجھ پر پھٹکار ہے اس دن تک کہ انصاف ہو ');
INSERT INTO "al_hind" VALUES(15,36,'15:36','بولا اے رب تو مجھ کو ڈھیل دے اس دن تک کہ مردے زندہ ہوں');
INSERT INTO "al_hind" VALUES(15,37,'15:37','فرمایا تو تجھ کو ڈھیل دی');
INSERT INTO "al_hind" VALUES(15,38,'15:38','اسی مقرر وقت کے دن تک ');
INSERT INTO "al_hind" VALUES(15,39,'15:39','بولا اے رب جیسا تو نے مجھ کو راہ سے کھو دیا میں بھی ان سب کو بہاریں دکھلاؤں گا زمین میں اور راہ سےکھو دوں گا ان سب کو');
INSERT INTO "al_hind" VALUES(15,40,'15:40','مگر جو تیرے چنے ہوئے بندے ہیں ');
INSERT INTO "al_hind" VALUES(15,41,'15:41','فرمایا یہ راہ ہے مجھ تک سیدھی ');
INSERT INTO "al_hind" VALUES(15,42,'15:42','جو میرے بندے ہیں تیرا ان پر کچھ زور نہیں مگر جو تیری راہ چلا بہکے ہوؤں میں ');
INSERT INTO "al_hind" VALUES(15,43,'15:43','اور دوزخ پر وعدہ ہے ان سب کا ');
INSERT INTO "al_hind" VALUES(15,44,'15:44','اسکے سات دروازے ہیں ہر دروازہ کے واسطے ان میں سے ایک فرقہ ہے بانٹا ہوا ');
INSERT INTO "al_hind" VALUES(15,45,'15:45','پرہیزگار ہیں باغوں میں اور چشموں میں ');
INSERT INTO "al_hind" VALUES(15,46,'15:46','کہیں گے انکو جاؤ ان میں سلامتی سے خاطر جمع سے (بے کھٹکے) ');
INSERT INTO "al_hind" VALUES(15,47,'15:47','اور نکال ڈالی ہم نے جو انکے جیوں میں تھی خفگی بھائی ہو گئے تختوں پر بیٹھے آمنے سامنے ');
INSERT INTO "al_hind" VALUES(15,48,'15:48','نہ پہنچے گی انکو وہاں کچھ تکلیف اور نہ انکو وہاں سے کوئی نکالے ');
INSERT INTO "al_hind" VALUES(15,49,'15:49','خبر سنا دے میرے بندوں کو کہ میں ہوں اصل بخشنے والا مہربان');
INSERT INTO "al_hind" VALUES(15,50,'15:50','اور یہ بھی کہ میرا عذاب وہی عذاب دردناک ہے ');
INSERT INTO "al_hind" VALUES(15,51,'15:51','اور حال سنا دے انکو ابراہیم کے مہمانوں کا ');
INSERT INTO "al_hind" VALUES(15,52,'15:52','جب چلے آئے اسکے گھر میں اور بولے (کیا انہوں نے) سلام وہ بولا ہم کو تم سے ڈر معلوم ہوتا ہے ');
INSERT INTO "al_hind" VALUES(15,53,'15:53','بولے ڈر مت ہم تجھ کو خوشخبری سناتے ہیں ایک ہوشیار لڑکے کی ');
INSERT INTO "al_hind" VALUES(15,54,'15:54','بولا کیا خوشخبری سناتے ہو مجھ کو جب پہنچ چکا مجھ کو بڑھاپا اب کاہے پر خوشخبری سناتے ہو ');
INSERT INTO "al_hind" VALUES(15,55,'15:55','بولے ہم نے تجھ کو خوشخبری سنائی سچی (پکی) سو مت ہو تو نا امیدوں میں');
INSERT INTO "al_hind" VALUES(15,56,'15:56','بولا اور کون آس توڑے اپنے رب کی رحمت سے مگر (وہی) جو گمراہ ہیں ');
INSERT INTO "al_hind" VALUES(15,57,'15:57','بولا پھر کیا مہم ہے تمہاری اے اللہ کے بھیجے ہوؤ ');
INSERT INTO "al_hind" VALUES(15,58,'15:58','بولے ہم بھیجے ہوئے آئے ہیں ایک قوم گنہگار پر');
INSERT INTO "al_hind" VALUES(15,59,'15:59','مگر لوط کے گھر والے ہم انکو بچا لیں گے سب کو');
INSERT INTO "al_hind" VALUES(15,60,'15:60','مگر ایک اسکی عورت ہم نے ٹھہرا لیا وہ ہے رہ جانے والوں میں ');
INSERT INTO "al_hind" VALUES(15,61,'15:61','پھر جب پہنچے لوط کے گھر وہ بھیجے ہوئے');
INSERT INTO "al_hind" VALUES(15,62,'15:62','بولا تم لوگ ہو اوپرے (جن سے کھٹکا ہوتا ہے) ');
INSERT INTO "al_hind" VALUES(15,63,'15:63','بولے نہیں پر ہم لیکر آئے ہیں تیرے پاس وہ چیز جس میں وہ جھگڑتے تھے ');
INSERT INTO "al_hind" VALUES(15,64,'15:64','اور ہم لائے ہیں تیرے پاس پکی بات اور ہم سچ کہتے ہیں ');
INSERT INTO "al_hind" VALUES(15,65,'15:65','سو لے نکل اپنے گھر کو کچھ رات رہے سے اور تو چل ان کے پیچھے اور مڑ کر نہ دیکھے تم میں سے کوئی اور چلے جاؤ جہاں تم کو حکم ہے ');
INSERT INTO "al_hind" VALUES(15,66,'15:66','اور مقرر کر دی ہم نے اس کو یہ بات کہ ان کی جڑ کٹے گی صبح ہوتے ');
INSERT INTO "al_hind" VALUES(15,67,'15:67','اور آئے شہر کے لوگ خوشیاں کرتے ');
INSERT INTO "al_hind" VALUES(15,68,'15:68','لوط نے کہا یہ لوگ میرے مہمان ہیں سو مجھ کو رسوا مت کرو ');
INSERT INTO "al_hind" VALUES(15,69,'15:69','اور ڈرو اللہ سے اور میری آبرو مت کھوؤ ');
INSERT INTO "al_hind" VALUES(15,70,'15:70','بولے کیا ہم نے تجھ کو منع نہیں کیا جہان کی حمایت سے ');
INSERT INTO "al_hind" VALUES(15,71,'15:71','بولا یہ حاضر ہیں میری بیٹیاں اگر تم کو کرنا ہے ');
INSERT INTO "al_hind" VALUES(15,72,'15:72','قسم ہے تیری جان کی وہ اپنی مستی میں مدہوش (نشے) ہیں ');
INSERT INTO "al_hind" VALUES(15,73,'15:73','پھر آ پکڑا انکو چنگھاڑ نے سورج نکلتے وقت (ہی) ');
INSERT INTO "al_hind" VALUES(15,74,'15:74','پھر کر ڈالی ہم نے وہ بستی اوپر تلے اور برسائے ان پر پتھر کھنگر (کنکر) کے ');
INSERT INTO "al_hind" VALUES(15,75,'15:75','بیشک اس میں نشانیاں ہیں دھیان کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(15,76,'15:76','اور وہ بستی واقع ہےسیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(15,77,'15:77','البتہ اس میں نشانی ہے ایمان والوں کو (یقین کرنے والوں کو) ');
INSERT INTO "al_hind" VALUES(15,78,'15:78','اور تحقیق تھے بن کے رہنے والے گنہگار ');
INSERT INTO "al_hind" VALUES(15,79,'15:79','سو ہم نے بدلہ لیا ان سے اور یہ دونوں بستیاں واقع ہیں کھلے راستہ پر ');
INSERT INTO "al_hind" VALUES(15,80,'15:80','اور بیشک جھٹلایا حجر والوں نے (حجر کے رہنے والوں نے) رسولوں کو ');
INSERT INTO "al_hind" VALUES(15,81,'15:81','اور دیں ہم نے انکو اپنی نشانیاں سو رہے ان سے منہ پھیرتے (ان کو ٹالنے) ');
INSERT INTO "al_hind" VALUES(15,82,'15:82','اور تھے کہ تراشتے تھے پہاڑوں کے گھر اطمینان کے ساتھ ');
INSERT INTO "al_hind" VALUES(15,83,'15:83','پھر پکڑا انکو چنگھاڑ نے صبح ہونے (ہوتے) کے وقت');
INSERT INTO "al_hind" VALUES(15,84,'15:84','پھر کام نہ آیا انکے جو کچھ کمایا تھا ');
INSERT INTO "al_hind" VALUES(15,85,'15:85','اور ہم نے بنائے نہیں آسمان اور زمین اور جو ان کے بیچ میں ہے بغیر حکمت (تدبیر) اور قیامت بیشک آنے والی ہے سو کنارہ کر اچھی طرح کنارہ ');
INSERT INTO "al_hind" VALUES(15,86,'15:86','تیرا رب جو ہے وہی ہے پیدا کرنے والا خبردار ');
INSERT INTO "al_hind" VALUES(15,87,'15:87','اور ہم نے دی ہیں تجھ کو سات آیتیں وظیفہ اور قرآن بڑے درجہ کا ');
INSERT INTO "al_hind" VALUES(15,88,'15:88','مت ڈال اپنی آنکھیں ان چیزوں پر جو برتنے کو دیں ہم نے ان میں سے کئ طرح کے لوگوں کو اور نہ غم کھا ان پر اور جھکا اپنے بازو ایمان والوں کے واسطے ');
INSERT INTO "al_hind" VALUES(15,89,'15:89','اور کہہ کہ میں وہی ہوں ڈرانے والا کھول کر ');
INSERT INTO "al_hind" VALUES(15,90,'15:90','جیسا ہم نے بھیجا ہے ان بانٹنے والوں پر');
INSERT INTO "al_hind" VALUES(15,91,'15:91','جنہوں نے کیا ہے قرآن کو بوٹیاں ');
INSERT INTO "al_hind" VALUES(15,92,'15:92','سو قسم ہے تیرے رب کی ہم کو پوچھنا ہے ان سب سے');
INSERT INTO "al_hind" VALUES(15,93,'15:93','جو کچھ وہ کرتے تھے ');
INSERT INTO "al_hind" VALUES(15,94,'15:94','سو سنا دے کھول کر جو تجھ کو حکم ہوا اور پروا نہ کر مشرکوں کی ');
INSERT INTO "al_hind" VALUES(15,95,'15:95','ہم بس (کافی) ہیں تیری طرف سے ٹھٹھے کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(15,96,'15:96','جو کہ ٹھہراتے ہیں اللہ کے ساتھ دوسرے کی بندگی سو عنقریب معلوم کر لیں گے ');
INSERT INTO "al_hind" VALUES(15,97,'15:97','اور ہم جانتے ہیں کہ تیرا جی رکتا ہے انکی باتوں سے');
INSERT INTO "al_hind" VALUES(15,98,'15:98','سو تو یاد کر خوبیاں اپنے رب کی اور ہو سجدہ کرنے والوں سے ');
INSERT INTO "al_hind" VALUES(15,99,'15:99','اور بندگی کئے جا اپنے رب کی جب تک آئے تیرے پاس یقینی بات ');
INSERT INTO "al_hind" VALUES(16,1,'16:1','آ پہنچا حکم اللہ کا سو اس کی جلدی مت کرو وہ پاک ہے اور برتر ہے انکے شریک بتلانے سے ');
INSERT INTO "al_hind" VALUES(16,2,'16:2','اتارتا ہے فرشتوں کو بھید دے کر اپنے حکم سے جس پر چاہے اپنے بندوں میں کہ خبردار کر دو کہ کسی کی بندگی نہیں سوا میرے سو مجھ سے ڈرو ');
INSERT INTO "al_hind" VALUES(16,3,'16:3','بنائے آسمان اور زمین ٹھیک ٹھیک وہ برتر ہے انکے شریک بتلانے سے ');
INSERT INTO "al_hind" VALUES(16,4,'16:4','بنایا آدمی کو ایک بوند سے پھر جبھی ہو گیا جھگڑا کرنے والا بولنے والا ');
INSERT INTO "al_hind" VALUES(16,5,'16:5','اور چوپائے بنا دیے تمہارے واسطے ان میں جڑا دل ہے اور کتنے فائدے اور بعضوں کو کھاتے ہو ');
INSERT INTO "al_hind" VALUES(16,6,'16:6','اور تم کو ان سے عزت ہے جب شام کو چرا کر لاتے ہو اور جب چرانے لے جاتے ہو ');
INSERT INTO "al_hind" VALUES(16,7,'16:7','اور اٹھا لے چلتے ہیں بوجھ تمہارے ان شہروں تک کہ تم نہ پہنچتے وہاں مگر جان مار کر بیشک تمہارا رب بڑا شفقت کرنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(16,8,'16:8','اور گھوڑے پیدا کئے اور خچریں اور گدھے کہ ان پر سوار ہو اور زینت کے لئے اور پیدا کرتا ہے جو تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(16,9,'16:9','اور اللہ تک پہنچتی ہے سیدھی راہ اور بعضی راہ کج بھی ہے اور اگر وہ چاہے تو سیدھی راہ دے تم سب کو ');
INSERT INTO "al_hind" VALUES(16,10,'16:10','وہی ہے جس نے اتارا آسمان سے تمہارے لئے پانی اس سے پیتے ہو اور اسی سے درخت ہوتے ہیں جس میں چراتے ہو ');
INSERT INTO "al_hind" VALUES(16,11,'16:11','اگاتا ہے تمہارے واسطے اس سے کھیتی اور زیتون اور کھجوریں اور انگور اور ہر قسم کے میوے اس میں البتہ نشانی ہے ان لوگوں کو جو غور کرتے ہیں ');
INSERT INTO "al_hind" VALUES(16,12,'16:12','اور تمہارے کام میں لگا دیا رات اور دن اور سورج اور چاند کو اور ستارے کام میں لگے ہیں اسکے حکم سے اس میں نشانیاں ہیں ان لوگوں کو جو سمجھ رکھتے ہیں');
INSERT INTO "al_hind" VALUES(16,13,'16:13','اور جو چیزیں پھیلائیں تمہارے واسطے زمین میں رنگ برنگ کی اس میں نشانی ہے ان لوگوں کو جو سوچتے ہیں');
INSERT INTO "al_hind" VALUES(16,14,'16:14','اور وہی ہے جس نے کام میں لگا دیا دریا کو کہ کھاؤ اس میں سے گوشت تازہ اور نکالو اس میں سے گہنا جو پہنتے ہو اور دیکھتا ہے تو کشتیوں کو چلتی ہیں پانی پھاڑ کر اسمیں (دریا میں) اور اس واسطے کہ تلاش کرو اسکے فضل سے اور تاکہ احسان مانو ');
INSERT INTO "al_hind" VALUES(16,15,'16:15','اور رکھ دیے زمین پر بوجھ (پہاڑ) کہ کبھی جھک پڑے تم کو لے کر اور بنائیں ندیاں اور راستے تا کہ تم راہ پاؤ ');
INSERT INTO "al_hind" VALUES(16,16,'16:16','اور بنائیں (رکھیں) علامتیں اور ستاروں سے لوگ راہ پاتے ہیں ');
INSERT INTO "al_hind" VALUES(16,17,'16:17','بھلا جو پیدا کرے برابر ہے اس کے جو کچھ نہ پیدا کرے کیا تم سوچتے نہیں ');
INSERT INTO "al_hind" VALUES(16,18,'16:18','اور اگر شمار (گنو) کرو اللہ کی نعمتوں کو نہ پورا کر سکو گے ان کو بیشک اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(16,19,'16:19','اور اللہ جانتا ہے جو تم چھپاتے ہو اور جو ظاہر کرتے ہو ');
INSERT INTO "al_hind" VALUES(16,20,'16:20','اور جن کو پکارتے ہیں اللہ کے سوائے کچھ پیدا نہیں کرتے اور وہ خود پیدا کئے ہوئے ہیں ');
INSERT INTO "al_hind" VALUES(16,21,'16:21','مردے ہیں جن میں جان نہیں اور نہیں جانتے کب اٹھائے جائیں گے ');
INSERT INTO "al_hind" VALUES(16,22,'16:22','معبود تمہارا معبود ہے اکیلا سو جن کو یقین نہیں آخرت کی زندگی کا ان کے دل نہیں مانتے اور وہ مغرور ہیں ');
INSERT INTO "al_hind" VALUES(16,23,'16:23','ٹھیک بات ہے کہ اللہ جانتا ہے جو کچھ چھپاتے ہیں اور جو کچھ ظاہر (جتلاتے ہیں) کرتے ہیں بیشک وہ نہیں پسند کرتا غرور کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(16,24,'16:24','اور جب کہے ان سےکہ کیا اتارا ہے تمہارے رب نے تو کہیں کہانیاں ہیں پہلوں کی ');
INSERT INTO "al_hind" VALUES(16,25,'16:25','تاکہ اٹھائیں بوجھ اپنے پورے دن قیامت کے اور کچھ بوجھ ان کے جن کو بہکاتے ہیں بلا تحقیق سنتا ہے برا بوجھ ہے جو اٹھاتے ہیں ');
INSERT INTO "al_hind" VALUES(16,26,'16:26','البتہ دغابازی کر چکے ہیں جو تھے ان سے پہلے (ان سے اگلے) پھر پہنچا حکم اللہ کا ان کی عمارت پر بنیادوں سے پھر گر پڑی ان پر چھت اوپر سے اور آیا ان پرعذاب جہاں سے ان کو خبر نہ تھی ');
INSERT INTO "al_hind" VALUES(16,27,'16:27','پھر قیامت کے دن رسوا کرے گا ان کو اور کہے گا کہاں ہیں میرے شریک جن پر تم کو بڑی ضد تھی بولیں گے جن کو دی گئ تھی خبر بیشک رسوائی آج کے دن اور برائی منکروں پر ہے ');
INSERT INTO "al_hind" VALUES(16,28,'16:28','جن کی جان نکالتے ہیں فرشتے اور وہ برا کر رہے ہیں اپنے حق میں تب ظاہر کریں گے اطاعت کہ ہم تو کرتے نہ تھے کچھ برائی کیوں نہیں اللہ خوب جانتا ہے جو تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(16,29,'16:29','سو داخل ہو دروازوں میں دوزخ کے رہا کرو سدا اسی میں سو کیا برا ٹھکانا ہے غرور کرنے والوں کا');
INSERT INTO "al_hind" VALUES(16,30,'16:30','اور کہا پرہیزگاروں کو کیا اتارا تمہارے رب نے بولے نیک بات جنہوں نے بھلائی کی اس دنیا میں انکو بھلائی ہے اور آخرت کا گھر بہتر ہے اور کیا خوب گھر ہے پرہیزگاروں کا ');
INSERT INTO "al_hind" VALUES(16,31,'16:31','باغ ہیں ہمیشہ رہنے کے جن میں وہ جائیں گے بہتی ہیں ان کے نیچے نہریں ان کے واسطے وہاں ہے جو چاہیں ایسا بدلہ دے گا اللہ پرہیزگاروں کو ');
INSERT INTO "al_hind" VALUES(16,32,'16:32','جن کی جان قبض کرتے ہیں فرشتے اور وہ ستھری ہیں کہتےہیں فرشتے سلامتی تم پر جاؤ بہشت میں بدلہ ہے اس کا جو تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(16,33,'16:33','کیا کافر اب اس کے منتظر ہیں کہ آئیں ان پر فرشتے یا پہنچے حکم تیرے رب کا اسی طرح کیا تھا ان سے اگلوں نے اور اللہ نے ظلم نہ کیا ان پر لیکن وہ خود اپنا برا کرتے رہے');
INSERT INTO "al_hind" VALUES(16,34,'16:34','پھر پڑے ان کے سر ان کے برے کام اور الٹ پڑا ان پر جو ٹھٹھا کرتے تھے ');
INSERT INTO "al_hind" VALUES(16,35,'16:35','اور بولے شرک کرنے والے اگر چاہتا اللہ نہ پوجتے ہم اس کےسوا کسی چیز کو اور نہ ہمارے باپ اور نہ حرام ٹھہرا لیتے ہم بدون اسکے حکم کے کسی چیز کو اسی طرح کیا ان سے اگلوں نے سو رسولوں کے ذمہ نہیں مگر پہنچا دینا صاف صاف ');
INSERT INTO "al_hind" VALUES(16,36,'16:36','اور ہم نے اٹھائے ہیں (بھیجے ہیں) ہر امت میں رسول کہ بندگی کرو اللہ کی اور بچو ہڑونگے سے (جھوٹے معبودوں سے) پھر کسی کو ان میں سے ہدایت کی (راہ سمجھائی) اللہ نے اور کسی پر ثابت ہوئی گمراہی سو سفر کرو (چلو پھرو زمین میں) ملکوں میں پھر دیکھو کیا ہوا انجام جھٹلانے والوں کا');
INSERT INTO "al_hind" VALUES(16,37,'16:37','اگر تو طمع کرے انکو راہ پر لانے کی تو اللہ راہ نہیں دیتا جن کو بچلاتا ہے اور کوئی نہیں ان کا مددگار ');
INSERT INTO "al_hind" VALUES(16,38,'16:38','اور قسمیں کھاتے ہیں اللہ کی سخت قسمیں کہ نہ اٹھائے گا اللہ جو کوئی مر جائے کیوں نہیں (بیشک اٹھائے گا) وعدہ ہو چکا ہے اس پر پکا لیکن اکثر لوگ نہیں جانتے ');
INSERT INTO "al_hind" VALUES(16,39,'16:39','اٹھائے گا تاکہ ظاہر کر دے ان پر جس بات میں کہ جھگڑتے ہیں اور تاکہ معلوم کر لیں کافر کہ وہ جھوٹے تھے ');
INSERT INTO "al_hind" VALUES(16,40,'16:40','ہمارا کہنا کسی چیز کو جب ہم اس کو کرنا چاہیں یہی ہے کہ کہیں اس کو ہو جا تو وہ ہو جائے ');
INSERT INTO "al_hind" VALUES(16,41,'16:41','اور جنہوں نے گھر چھوڑا اللہ کے واسطے بعد اس کے کہ ظلم اٹھایا البتہ انکو ہم ٹھکانا دیں گے دنیا میں اچھا اور ثواب آخرت کا تو بہت بڑا ہے اگر ان کو معلوم ہوتا ');
INSERT INTO "al_hind" VALUES(16,42,'16:42','جو ثابت قدم رہے اور اپنے رب پر بھروسہ کیا ');
INSERT INTO "al_hind" VALUES(16,43,'16:43','اور تجھ سے پہلے بھی ہم نے یہی مرد بھیجے تھے کہ حکم بھیجتے تھے ہم انکی طرف سو پوچھو یاد رکھنے والوں سے اگر تم کو معلوم نہیں ');
INSERT INTO "al_hind" VALUES(16,44,'16:44','بھیجا تھا انکو نشانیاں دے کر اور ورقے (اوراق) اور اتاری ہم نے تجھ پر یہ یاداشت کہ تو کھول دے لوگوں کے سامنے وہ چیز جو اتری انکے واسطے تاکہ وہ غور (دھیان) کریں ');
INSERT INTO "al_hind" VALUES(16,45,'16:45','سو کیا نڈر ہو گئے وہ لوگ جو برے فریب (داؤ) کرتے ہیں اس سے کہ دھنسا دیوے اللہ ان کو زمین میں یا آ پہنچے ان پر عذاب جہاں سے خبر نہ رکھتے ہوں ');
INSERT INTO "al_hind" VALUES(16,46,'16:46','یا پکڑ لے ان کو چلتے پھرتے سو وہ نہیں ہیں عاجز کرنے والے ');
INSERT INTO "al_hind" VALUES(16,47,'16:47','یا پکڑ لے ان کو ڈرانے کے بعد (ڈرا کر) سو تمہارا رب بڑا نرم ہے مہربان ');
INSERT INTO "al_hind" VALUES(16,48,'16:48','کیا نہیں دیکھتے وہ جو کہ اللہ نے پیدا کی ہے کوئی چیز کہ ڈھلتے ہیں سایے ان کے داہنی طرف سے اور بائیں طرف سے سجدہ کرتے ہوئے اللہ کو اور وہ عاجزی میں ہیں ');
INSERT INTO "al_hind" VALUES(16,49,'16:49','اور اللہ کو سجدہ کرتا ہے جو آسمان میں ہے اور جو زمین میں ہے جانداروں سے اور فرشتے اور وہ تکبر نہیں کرتے ');
INSERT INTO "al_hind" VALUES(16,50,'16:50','ڈر رکھتے ہیں اپنے رب کا اپنے اوپر سے اور کرتے ہیں جو حکم پاتے ہیں ');
INSERT INTO "al_hind" VALUES(16,51,'16:51','اور کہا ہے اللہ نے مت پکڑو معبود دو وہ معبود ایک ہی ہے سو مجھ سے ڈرو ');
INSERT INTO "al_hind" VALUES(16,52,'16:52','اور اسی کا ہے جو کچھ ہے آسمانوں میں اور زمین میں اور اسی کی عبادت ہے (اسی کا انصاف) ہمیشہ سو کیا سوائے اللہ کے کسی سے ڈرتے ہو (خطرہ رکھتے ہو)');
INSERT INTO "al_hind" VALUES(16,53,'16:53','اور جو کچھ تمہارے پاس ہے نعمت سو اللہ کی طرف سے پھر جب پہنچتی ہے تم کو سختی تو اسی کی طرف چلاتے ہو (اسی سے فریاد کرتے ہو) ');
INSERT INTO "al_hind" VALUES(16,54,'16:54','پھر جب کھول دیتا ہے سختی تم سے اسی وقت ایک فرقہ تم میں سے اپنے رب کے ساتھ لگتا ہے شریک بتانے');
INSERT INTO "al_hind" VALUES(16,55,'16:55','تاکہ منکر ہو جائیں اس چیز سے جو کہ ہم نے انکو دی ہے سو مزے اڑا لو آخر معلوم کر لو گے ');
INSERT INTO "al_hind" VALUES(16,56,'16:56','اور ٹھہراتے ہیں ان کے لئے جن کی خبر نہیں رکھتے (جن کو خبر نہیں) ایک حصہ ہماری دی ہوئی روزی میں سے قسم اللہ کی تم سے پوچھنا ہے جو تم بہتان باندھتے ہو ');
INSERT INTO "al_hind" VALUES(16,57,'16:57','اور ٹھہراتے ہیں اللہ کے لئے بیٹیاں وہ اس سے پاک ہے (لائق نہیں) اور اپنے لئے جو دل چاہتا ہے ');
INSERT INTO "al_hind" VALUES(16,58,'16:58','اور جب خوشخبری ملے ان میں کسی کو بیٹی کی سارے دن رہے منہ اس کا سیاہ اور جی میں گھٹتا رہے ');
INSERT INTO "al_hind" VALUES(16,59,'16:59','چھپتا پھرے لوگوں سے مارےبرائی اس خوشخبری کے جو سنی اس کو رہنے دے ذلت قبول کر کے یا اس کو داب دے مٹی میں سنتا ہے برا فیصلہ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(16,60,'16:60','جو نہیں مانتے آخرت کو ان کی بری مثال ہے اور اللہ کی مثال (شان) سب سے اوپر اور وہی ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(16,61,'16:61','اور اگر پکڑے اللہ لوگوں کو ان کی بے انصافی پر نہ چھوڑے زمین پر ایک چلنے والا لیکن ڈھیل دیتا ہے ان کو ایک وقت موعود تک پھر جب آ پہنچے گا ان کا وعدہ نہ پیچھے سرک سکیں گے ایک گھڑی اور نہ آگے سرک سکیں گے ');
INSERT INTO "al_hind" VALUES(16,62,'16:62','اور کرتے ہیں (ٹھہراتے ہیں) اللہ کے واسطے جس کو اپنا جی نہ چاہے اور بیان کرتی ہیں زبانیں انکی جھوٹ کہ انکے واسطے خوبی ہے آپ ثابت ہے (محقق ہو گیا)کہ انکے واسطے آگ ہے اور وہ بڑھائے جارہے ہیں ');
INSERT INTO "al_hind" VALUES(16,63,'16:63','قسم اللہ کی ہم نے رسول بھیجے مختلف فرقوں میں تجھ سے پہلے پھر اچھے کر کے دکھلائے انکو شیطان نے ان کے کام سو وہی رفیق ان کا ہے آج اور ان کے واسطے عذاب دردناک ہے ');
INSERT INTO "al_hind" VALUES(16,64,'16:64','اور ہم نے اتاری تجھ پر کتاب اسی واسطے کہ کھول کر سنا دے تو ان کو وہ چیز کہ جس میں جھگڑ رہے ہیں اور سیدھی راہ سجھانے کو اور واسطے بخشش ایمان لانے والوں کے (رحمت ان لوگوں کے لئے جو ایمان لائے) ');
INSERT INTO "al_hind" VALUES(16,65,'16:65','اور اللہ نے اتارا آسمان سے پانی پھر اس سے زندہ کیا زمین کو اس کے مرنے کے پیچھے اس میں نشانی ہے ان لوگوں کو جو سنتے ہیں ');
INSERT INTO "al_hind" VALUES(16,66,'16:66','اور تمہارے واسطے چوپاؤں میں سوچنے کی جگہ ہے پلاتے ہیں تم کو اس کے پیٹ کی چیزوں میں سےگوبر اور لہو کے بیچ میں سے (درمیان سے) دودھ ستھرا خوشگوار پینے والوں کے لئے ');
INSERT INTO "al_hind" VALUES(16,67,'16:67','اور میووں سے کھجور کے اور انگور کے بناتے ہو اس سے نشہ اور روزی خاصی اس میں نشانی ہے ان لوگوں کے واسطے جو سمجھتے ہیں (سوچتے ہیں) ');
INSERT INTO "al_hind" VALUES(16,68,'16:68','اور حکم دیا تیرے رب نے شہد کی مکھی کو کہ بنا لے پہاڑوں میں گھر اور درختوں میں اور جہاں ٹٹیاں باندھتے ہیں ');
INSERT INTO "al_hind" VALUES(16,69,'16:69','پھر کھا ہر طرح کے میووں سے پھر چل راہوں میں (راستوں میں) اپنے رب کی صاف پڑے ہیں نکلتی ہے ان کے پیٹ میں سے پینے کی چیز جسکے مختلف رنگ ہیں اس میں مرض اچھے ہوتے ہیں لوگوں کے اس میں نشانی ہے ان لوگوں کے لئے جو دھیان کرتے ہیں ');
INSERT INTO "al_hind" VALUES(16,70,'16:70','اور اللہ نے تم کو پیدا کیا پھر تم کو موت دیتا ہے اور کوئی تم میں سے پہنچ جاتا ہے نکمی عمر کو کہ سمجھنے کے پیچھے اب کچھ نہ سمجھے اللہ خبردار ہے قدرت والا ');
INSERT INTO "al_hind" VALUES(16,71,'16:71','اور اللہ نے بڑائی دی تم میں ایک کو ایک پر روزی میں سو جن کو بڑائی دی وہ نہیں پہنچا دیتے اپنی روزی ان کو جن کے مالک ان کے ہاتھ ہیں کہ وہ سب اس میں برابر ہو جائیں کیا اللہ کی نعمت کے منکر ہیں ');
INSERT INTO "al_hind" VALUES(16,72,'16:72','اور اللہ نے پیدا کیں تمہارے واسطے تمہای ہی قسم سے عورتیں اور دیے تم کو تمہاری عورتوں سے بیٹے اور پوتے اور کھانے کو دیں تمکو ستھری چیزیں سو کیا جھوٹی باتیں مانتے ہیں اور اللہ کے فضل کو نہیں مانتے ');
INSERT INTO "al_hind" VALUES(16,73,'16:73','اور پوجتے ہیں اللہ کے سوائے ایسوں کو جو مختار نہیں انکی روزی کے آسمان اور زمین میں سے کچھ بھی اور نہ قدرت رکھتے ہیں ');
INSERT INTO "al_hind" VALUES(16,74,'16:74','سو مت چسپاں کرو (بٹھلاؤ) اللہ پر مثالیں بیشک اللہ جانتا ہے اور تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(16,75,'16:75','اللہ نے بتلائی ایک مثال ایک بندہ (غلام) پرایا مال نہیں قدرت رکھتا کسی چیز پر اور ایک جسکو ہم نے روزی دی اپنی طرف سے خاصی روزی سو وہ خرچ کرتا ہے اس میں سے چھپا کر اور سب کے روبرو کہیں برابر ہوتے ہیں سب تعریف اللہ کو ہے پر بہت لوگ نہیں جانتے ');
INSERT INTO "al_hind" VALUES(16,76,'16:76','اور بتائی اللہ نے ایک دوسری مثال دو مرد ہیں ایک گونگا کچھ کام نہیں کر سکتا اور وہ بھاری ہے اپنے صاحب (مالک) پر جس طرف اسکو بھیجے نہ کر کے لائے کچھ بھلائی کہیں برابر ہے وہ اور ایک وہ شخص جو حکم کرتا ہے انصاف سے اور ہے سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(16,77,'16:77','اور اللہ ہے کہ پاس ہیں بھید آسمانوں اور زمین کے اور قیامت کا کام تو ایسا ہے جسیے لپک نگاہ کی یا اس سے بھی قریب اور اللہ ہر چیز پر قادر ہے ');
INSERT INTO "al_hind" VALUES(16,78,'16:78','اور اللہ نے تم کو نکالا تمہاری ماں کے پیٹ سے نہ جانتے تھے تم کسی چیز کو اور دیئے تم کو کان اور آنکھیں اور دل تاکہ تم احسان مانو ');
INSERT INTO "al_hind" VALUES(16,79,'16:79','کیا نہیں دیکھے اڑتے جانور حکم کے باندھے ہوئے آسمان کی ہوا میں کوئی نہیں تھام رہا ان کو سوائے اللہ کے اس میں نشانیاں ہیں ان لوگوں کو جو یقین لاتے ہیں ');
INSERT INTO "al_hind" VALUES(16,80,'16:80','اور اللہ نے بنا دیئے تم کو تمہارے گھر بسنے کی جگہ اور بنا دیئے تم کو چوپاؤں کی کھال سے ڈیرے جو ہلکے رہتے ہیں تم پر جس دن سفر میں ہو اور جس دن گھر میں اور بھیڑوں کی اون سے اور اونٹوں کی ببریوں سے اور بکریوں کے بالوں سے کتنے اسباب اور استعمال کی چیزیں وقت مقرر تک ');
INSERT INTO "al_hind" VALUES(16,81,'16:81','اور اللہ نے بنا دیئے تمہارے واسطے اپنی بنائی ہوئی چیزوں کے سائے اور بنا دیں تمہارےواسطے پہاڑوں میں چھپنے کی جگہیں اور بنا دیے تم کو کرتے جو بچاؤ ہیں گرمی میں اور کرتے جو بچاؤ ہیں لڑائی میں اسی طرح پورا کرتا ہے اپنا احسان تم پر تاکہ تم حکم مانو ');
INSERT INTO "al_hind" VALUES(16,82,'16:82','پھر اگر پھر جائیں تو تیرا کام تو یہی ہے کھول کر سنا دینا ');
INSERT INTO "al_hind" VALUES(16,83,'16:83','پہچانتے ہیں اللہ کا احسان پھر منکر ہو جاتے ہیں اور بہت ان میں ناشکر ہیں ');
INSERT INTO "al_hind" VALUES(16,84,'16:84','اور جس دن کھڑا کریں ہم ہر فرقہ میں ایک بتلانے والا پھر حکم (اجازت) نہ ملے منکروں کو اور نہ ان سے توبہ لی جائے ');
INSERT INTO "al_hind" VALUES(16,85,'16:85','اور جب دیکھیں گے ظالم عذاب کو پھر ہلکا نہ ہو گا ان سے اور نہ ان کو ڈھیل ملے ');
INSERT INTO "al_hind" VALUES(16,86,'16:86','اور جب دیکھیں مشرک اپنے شریکوں کو بولیں اے رب یہ ہمارے شریک ہیں جن کو ہم پکارتے تھے تیرے سوائے تب وہ ان پر ڈالیں گے بات کہ تم جھوٹے ہو ');
INSERT INTO "al_hind" VALUES(16,87,'16:87','اور آ پڑیں اللہ کے آگے اس دن عاجز ہو کر اور بھول جائیں (جائے گی ان سے) جو جھوٹ باندھتے تھے ');
INSERT INTO "al_hind" VALUES(16,88,'16:88','جو لوگ منکر ہوئے ہیں اور روکتے رہے ہیں اللہ کی راہ سے انکو ہم بڑھا دیں گے عذاب پر عذاب بدلہ اس کا جو شرارت کرتے تھے ');
INSERT INTO "al_hind" VALUES(16,89,'16:89','اور جس دن کھڑا کریں گے ہم ہر فرقہ میں ایک بتلانے والا ان پر انہی میں کا اور تجھ کو لائیں بتلانے کو ان لوگوں پر اور اتاری ہم نے تجھ پر کتاب کھلا بیان ہر چیز کا اور ہدایت اور رحمت اور خوشخبری حکم ماننے والوں کے لئے ');
INSERT INTO "al_hind" VALUES(16,90,'16:90','اللہ حکم کرتا ہے انصاف کرنے کا اور بھلائی کرنے کا اور قرابت والوں کے (کو) دینے کا اور منع کرتا ہے بے حیائی سے اور نامعقول کام سے اور سرکشی سے تم کو سمجھاتا ہے تاکہ تم یاد رکھو ');
INSERT INTO "al_hind" VALUES(16,91,'16:91','اور پورا کرو عہد اللہ کا جب آپس میں عہد کرو اور نہ توڑو قسموں کو پکا کرنے کے بعد اور تم نے کیا ہے اللہ کو اپنا ضامن اللہ جانتا ہے جو تم کرتےہو ');
INSERT INTO "al_hind" VALUES(16,92,'16:92','اور مت رہو جیسے وہ عورت کہ توڑا اس نے اپنا سوت کاتا ہوا محنت (مضبوط کرنے ) کے بعد ٹکڑے ٹکڑے کہ ٹھہراؤ اپنی قسموں کو دخل دینے کا بہانہ ایک دوسرے میں (آپس میں) اس واسطے کہ ایک فرقہ ہو چڑھا ہوا دوسرے سے یہ تو اللہ پر کھتا ہے تمکو اس سے اور آئندہ کھول دے گا اللہ تم کو قیامت کے دن جس بات میں تم جھگڑ رہے تھے ');
INSERT INTO "al_hind" VALUES(16,93,'16:93','اور اللہ چاہتا تو تم سب کو ایک ہی فرقہ کر دیتا ہے لیکن راہ بھلاتا ہے جس کو چاہے اور سمجھاتا ہے جس کو چاہے اور تم سے پوچھ ہو گی جو کام تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(16,94,'16:94','اور نہ ٹھہراؤ اپنی قسموں کو دھوکا [فریب] آپس میں کہ ڈگ نہ جائے کسی کا پاؤں جمنے کے پیچھے اور تم چکھو سزا اس بات پر کہ تم نے روکا اللہ کی راہ سے اور تم کو بڑا عذاب ہو ');
INSERT INTO "al_hind" VALUES(16,95,'16:95','اور نہ لو اللہ کے عہد پر مول (مال) تھوڑا سا بیشک جو اللہ کے یہاں ہے وہی بہتر ہے تمہارے حق میں اگر تم جانتے ہو ');
INSERT INTO "al_hind" VALUES(16,96,'16:96','جو تمہارے پاس ہے ختم ہو جائے گا اور جو اللہ کے پاس ہے کبھی ختم نہ ہو گا (سو رہنے والا ہے) اور ہم بدلے میں دیں گے صبر کرنے والوں کو ان کا حق اچھے کاموں پر جو کرتے تھے ');
INSERT INTO "al_hind" VALUES(16,97,'16:97','جس نے کیا نیک کام مرد ہو یا عورت ہو اور وہ ایمان پر ہے تو اس کو ہم زندگی دیں گے ایک اچھی زندگی اور بدلے میں دیں گے ان کو حق ان کا بہتر کاموں پر جو کرتے تھے');
INSERT INTO "al_hind" VALUES(16,98,'16:98','سو جب تو پڑھنے لگے قرآن تو پناہ لے اللہ کی شیطان مردود سے ');
INSERT INTO "al_hind" VALUES(16,99,'16:99','اس کا زور نہیں چلتا ان پر جو ایمان رکھتے ہیں اور اپنے رب پر بھروسہ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(16,100,'16:100','اس کا زور تو انہی پر ہے جو اس کو رفیق سمجھتے ہیں اور جو اسکو شریک مانتے ہیں ');
INSERT INTO "al_hind" VALUES(16,101,'16:101','اور جب ہم بدلتے ہیں ایک آیت کی جگہ دوسری آیت اور اللہ خوب جانتا ہے جو اتارتا ہے تو کہتے ہیں تو تو بنا لاتا ہے یہ بات نہیں پر اکثروں کو ان میں خبر نہیں ');
INSERT INTO "al_hind" VALUES(16,102,'16:102','تو کہہ اس کو اتارا ہے پاک فرشتے نے تیرے رب کی طرف سے بلاشبہ تاکہ ثابت کرے ایمان والوں کو اور ہدایت اور خوشخبری مسلمانوں کے واسطے ');
INSERT INTO "al_hind" VALUES(16,103,'16:103','اور ہم کو خوب معلوم ہے کہ وہ کہتے ہیں اس کو تو سکھلاتا ہے ایک آدمی جس کی طرف تعریض کرتے ہیں اس کی زبان ہے عجمی اور یہ قرآن زبان عربی ہے صاف ');
INSERT INTO "al_hind" VALUES(16,104,'16:104','وہ لوگ جن کو اللہ کی باتوں پر یقین نہیں ان کو اللہ راہ نہیں دیتا اور ان کےلئے عذاب دردناک ہے ');
INSERT INTO "al_hind" VALUES(16,105,'16:105','جھوٹ تو وہ لوگ بناتے ہیں جن کو یقین نہیں اللہ کی باتوں پر اور وہی لوگ جھوٹے ہیں ');
INSERT INTO "al_hind" VALUES(16,106,'16:106','جو کوئی منکر ہوا اللہ سے یقین لانے کے پیچھے مگر وہ نہیں جس پر زبردستی کی گئ اور اس کا دل برقرار ہے ایمان پر و لیکن جو کوئی دل کھول کر منکر ہوا سو ان پر غضب ہے اللہ کا اور ان کو بڑا عذاب ہے');
INSERT INTO "al_hind" VALUES(16,107,'16:107','یہ اس واسطے کہ انہوں نے عزیز رکھا دنیا کی زندگی کو آخرت سے اور اللہ راستہ نہیں دیتا منکر لوگوں کو ');
INSERT INTO "al_hind" VALUES(16,108,'16:108','یہ وہی ہیں کہ مہر کر دی اللہ نے ان کے دل پر اور کانوں پر اور آنکھوں پر اور یہی ہیں بے ہوش ');
INSERT INTO "al_hind" VALUES(16,109,'16:109','خود ظاہر ہے کہ آخرت میں یہی لوگ خراب ہیں ');
INSERT INTO "al_hind" VALUES(16,110,'16:110','پھر بات یہ ہے کہ تیرا رب ان لوگوں پر کہ انہوں نے وطن چھوڑا ہے بعد اس کے مصیبت (بچلائے گئے) اٹھائی پھر جہاد کرتے رہے اور قائم رہے بیشک تیرا رب ان باتوں کے بعد بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(16,111,'16:111','جس دن آئے گا ہر جی جواب سوال کرتا اپنی طرف سے اور پورا ملے گا ہر کسی کو جو اس نے کمایا اور ان پر ظلم نہ ہو گا ');
INSERT INTO "al_hind" VALUES(16,112,'16:112','اور بتلائی اللہ نے ایک مثال (مثل) ایک بستی تھی چین امن سے چلی آتی تھی اسکو روزی فراغت کی ہر جگہ سے پھر ناشکری کی اللہ کے احسانوں کی پھر چکھایا اس کو اللہ نے مزہ کہ ان کے تن کے کپڑے ہو گئے بھوک اور ڈر بدلہ اس کا جو وہ کرتے تھے ');
INSERT INTO "al_hind" VALUES(16,113,'16:113','اور ان کے پاس پہنچ چکا رسول انہی میں کا پھر اس کو جھٹلایا پھر آ پکڑا انکو عذاب نے اور وہ گنہگار تھے ');
INSERT INTO "al_hind" VALUES(16,114,'16:114','سو کھاؤ جو روزی دی تم کو اللہ نے حلال اور پاک اور شکر کرو اللہ کے احسان کا اگر تم اسی کو پوجتے ہو ');
INSERT INTO "al_hind" VALUES(16,115,'16:115','اللہ نے تو یہی حرام کیا ہےتم پر مردار اور لہو اور سور کا گوشت اور جس پر نام پکارا اللہ کے سوا کسی اور کا پھر جو کوئی ناچار ہو جائے نہ زور کرتا ہو نہ زیادتی تو اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(16,116,'16:116','اور مت کہو اپنی زبانوں کے جھوٹ بنا لینے سے کہ یہ حلال ہے اور یہ حرام ہے کہ اللہ پر بہتان باندھو بیشک جو بہتان باندھتے ہیں اللہ پر ان کا بھلا نہ ہو گا');
INSERT INTO "al_hind" VALUES(16,117,'16:117','تھوڑا سا فائدہ اٹھا لیں اور ان کےواسطے عذاب دردناک ہے ');
INSERT INTO "al_hind" VALUES(16,118,'16:118','اور جو لوگ یہودی ہیں ان پر ہم نے حرام کیا تھا جو تجھ کو پہلے سنا چکے اور ہم نے ان پر ظلم نہیں کیا پر وہ اپنے اوپر آپ ظلم کرتے تھے ');
INSERT INTO "al_hind" VALUES(16,119,'16:119','پھر بات یہ ہے کہ تیرا رب ان لوگوں پر جنہوں نے برائی کی نادانی سے پھر توبہ کی اس کے پیچھے اور سنوارا اپنے کام کو سو تیرا رب ان باتوں کے پیچھے بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(16,120,'16:120','اصل میں تو ابراہیم تھا راہ ڈالنے والا فرمانبردار اللہ کا سب سے ایک طرف ہو کر اور نہ تھا شرک والوں میں ');
INSERT INTO "al_hind" VALUES(16,121,'16:121','حق ماننے والا اس کے احسانوں کا اس کو اللہ نے چن لیا اور چلایا سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(16,122,'16:122','اور دی ہم نے دنیا میں اس کو خوبی اور وہ آخرت میں اچھے لوگوں میں ہے ');
INSERT INTO "al_hind" VALUES(16,123,'16:123','پھر حکم بھیجا ہم نے تجھ کو کہ چل دین ابراہیم پر جو ایک طرف کا تھا اور نہ تھا وہ شرک والوں میں ');
INSERT INTO "al_hind" VALUES(16,124,'16:124','ہفتہ کا دن جو مقرر کیا سو انہی پر جو اس میں اختلاف کرتے تھے اور تیرا رب حکم کرے گا ان میں قیامت کے دن جس بات میں اختلاف کرتے تھے ');
INSERT INTO "al_hind" VALUES(16,125,'16:125','بلا اپنے رب کی راہ پر پکی باتیں سمجھا کر اور نصیحت سنا کر بھلی طرح اور الزام دے انکو جس طرح بہتر ہو تیرا رب ہی بہتر جانتا ہے ان کو جو بھول گیا اس کی راہ اور وہی بہتر جانتا ہے انکو جو راہ پر ہیں ');
INSERT INTO "al_hind" VALUES(16,126,'16:126','اور اگر بدلہ لو تو بدلہ لو اسی قدر جس قدر کو تم کو تکلیف پہنچائی جائے (پہنچے) اور اگر صبر کرو تو یہ بہتر ہے صبر کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(16,127,'16:127','اور تو صبر کر اور تجھ سے صبر ہو سکے اللہ ہی کی مدد سے اور ان پر غم نہ کھا اور تنگ مت ہو ان کے فریب سے ');
INSERT INTO "al_hind" VALUES(16,128,'16:128','اللہ ساتھ ہے ان کے جو پرہیزگار ہیں اور جو نیکی کرتے ہیں ');
INSERT INTO "al_hind" VALUES(17,1,'17:1','پاک ذات ہے جو لے گیا اپنے بندہ کو راتوں رات مسجد حرام سے مسجد اقصٰی تک جس کو گھیر رکھا ہے ہماری برکت نے تاکہ دکھلائیں اُسکو کچھ اپنی قدرت کے نمونے وہی ہے سننے والا دیکھنے والا ');
INSERT INTO "al_hind" VALUES(17,2,'17:2','اور دی ہم نے موسٰی کو کتاب اور کیا اس کو ہدایت بنی اسرائیل کے واسطے کہ نہ ٹھہراؤ میرےسوا کسی کو کارساز ');
INSERT INTO "al_hind" VALUES(17,3,'17:3','تم جو اولاد ہو ان لوگوں کی جن کو چڑھایا ہم نے نوح کےساتھ بیشک وہ تھا بندہ حق ماننے والا ');
INSERT INTO "al_hind" VALUES(17,4,'17:4','اور صاف کہہ سنایا ہم نے بنی اسرائیل کو کتاب میں کہ تم خرابی کرو گے ملک میں دو بار اور سرکشی کرو گے بڑی سرکشی ');
INSERT INTO "al_hind" VALUES(17,5,'17:5','پھر جب آیا پہلا وعدہ بھیجے ہم نے تم پر اپنے بندے سخت لڑائی والے پھر پھیل پڑے شہروں کے بیچ اور وہ وعدہ ہونا ہی تھا ');
INSERT INTO "al_hind" VALUES(17,6,'17:6','پھر ہم نے پھیر دی تمہاری باری ان پر اور قوت دی تم کو مال سے اور بیٹوں سے اور اس سے زیادہ کر دیا تمہارا لشکر');
INSERT INTO "al_hind" VALUES(17,7,'17:7','اگر بھلائی کی تم نے تو بھلا کیا اپنا اور اگر برائی کی تو اپنے لئے پھر جب پہنچا وعدہ دوسرا بھیجے اور بندے کہ اداس کر دیں تمہارے منہ اور گھس جائیں مسجد میں جیسےگھس گئے تھے پہلی بار اور خراب کر دیں جس جگہ غالب ہوں پوری خرابی ');
INSERT INTO "al_hind" VALUES(17,8,'17:8','بعید نہیں تمہارے رب سے کہ رحم کرے تم پر اور اگر پھر وہی کرو گے تو ہم پھر وہی کریں گے اور کیا ہے ہم نے دوزخ کو کافروں کا قید خانہ ');
INSERT INTO "al_hind" VALUES(17,9,'17:9','یہ قرآن بتلاتا ہے وہ راہ جو سب سے سیدھی ہے اور خوشخبری سناتا ہے ایمان والوں کو جو عمل کرتے ہیں اچھے کہ ان کے لئے ہے ثواب بڑا ');
INSERT INTO "al_hind" VALUES(17,10,'17:10','اور یہ کہ جو نہیں مانتے آخرت کو ان کے لئے تیار کیا ہے ہم نے عذاب دردناک');
INSERT INTO "al_hind" VALUES(17,11,'17:11','اور مانگتا ہے آدمی برائی جیسے مانگتا ہے بھلائی اور ہے انسان جلد باز ');
INSERT INTO "al_hind" VALUES(17,12,'17:12','اور ہم نے بنائے رات اور دن دو نمونے پھر مٹا دیا رات کا نمونہ اور بنا دیا دن کا نمونہ دیکھنے کو تاکہ تلاش کرو فضل اپنے رب کا اور تاکہ معلوم کرو گنتی برسوں کی اور حساب اور سب چیز سنائی ہم نے کھول کر ');
INSERT INTO "al_hind" VALUES(17,13,'17:13','اور جو آدمی ہے لگا دی ہے ہم نے اس کی بری قسمت اس کی گردن سے اور نکال دکھائیں گے اس کو قیامت کے دن ایک کتاب کہ دیکھے گا اسکو کھلی ہوئی ');
INSERT INTO "al_hind" VALUES(17,14,'17:14','پڑھ لے کتاب اپنی (لکھا اپنا) تو ہی بس (کافی) ہے آج کے دن اپنا حساب لینے والا ');
INSERT INTO "al_hind" VALUES(17,15,'17:15','جو کوئی راہ پر آیا تو آیا اپنے ہی بھلے کو اور جو کوئی بہکا رہا تو بہکا رہا اپنے ہی برے کو اور کسی پر نہیں پڑتا بوجھ دوسرے کا اور ہم نہیں ڈالتے بلا جب تک نہ بھیجیں کوئی رسول ');
INSERT INTO "al_hind" VALUES(17,16,'17:16','اور جب ہم نے چاہا کہ غارت کریں کسی بستی کو حکم بھیج دیا اس کےعیش کرنے والوں کو پھر انہوں نے نافرمانی کی اس میں تب ثابت ہو گئ ان پر بات پھر اکھاڑ مارا ہم نے انکو اٹھا کر ');
INSERT INTO "al_hind" VALUES(17,17,'17:17','اور بہت غارت کر دیئے ہم نے قرن نوح کے پیچھے اور کافی ہےتیرا رب اپنے بندوں کے گناہ جاننے والا دیکھنے والا ');
INSERT INTO "al_hind" VALUES(17,18,'17:18','جو کوئی چاہتا ہو پہلا گھر جلد دے دیں ہم اس کو اسی میں جتنا چاہیں جس کو چاہیں پھر ٹھہرایا ہم نے اس کے واسطے دوزخ داخل ہو گا ا سمیں اپنی برائی سنکر دھکیلا جا کر ');
INSERT INTO "al_hind" VALUES(17,19,'17:19','اور جس نے چاہا پچھلا گھر اور دوڑ کی اس کے واسطے جو اس کی دوڑ ہے اور وہ یقین پر ہے سو ایسوں کی دوڑ ٹھکانے لگی ہے ');
INSERT INTO "al_hind" VALUES(17,20,'17:20','ہر ایک کو ہم پہنچائے جاتے ہیں۔ اِنکو اور اُن کو تیرے رب کی بخشش میں سے اور تیرے رب کی بخشش کسی نے نہیں روک لی ');
INSERT INTO "al_hind" VALUES(17,21,'17:21','دیکھ کیا بڑھا دیا ہم نے ایک کو ایک سے اور پچھلے گھر میں تو اور بڑے درجے ہیں اور بڑی فضیلت ');
INSERT INTO "al_hind" VALUES(17,22,'17:22','مت ٹھہرا للہ کے ساتھ دوسرا حاکم پھر بیٹھ رہے گا تو الزام کھا کر بےکس ہو کر ');
INSERT INTO "al_hind" VALUES(17,23,'17:23','اور حکم کر چکا تیرا رب کہ نہ پوجو اس کے سوائے اور ماں باپ کے ساتھ بھلائی کرو اگر پہنچ جائے تیرے سامنے بڑھاپے کو ایک ان میں سے یا دونوں تو نہ کہہ ان کو ہوں اور نہ جھڑک ان کو اور کہہ ان سے بات ادب کی ');
INSERT INTO "al_hind" VALUES(17,24,'17:24','اور جھکا دے ان کے آگے کندھے عاجزی کر کر نیازمندی سے اور کہہ اے رب ان پر رحم کر جیسا پالا انہوں نے مجھ کو چھوٹا سا ');
INSERT INTO "al_hind" VALUES(17,25,'17:25','تمہارا رب خوب جانتا ہے جو تمہارے جی میں ہے اگر تم نیک ہو گے تو وہ رجوع کرنے والوں کو بخشتا ہے ');
INSERT INTO "al_hind" VALUES(17,26,'17:26','اور دے قرابت والے کو اس کا حق اور محتاج کو اور مسافر کو اور مت اڑا بیجا (فضول) ');
INSERT INTO "al_hind" VALUES(17,27,'17:27','بےشک اڑانے والے بھائی ہیں شیطانوں کے اور شیطان ہے اپنے رب کا ناشکر ');
INSERT INTO "al_hind" VALUES(17,28,'17:28','اور اگر کبھی تغافل کرے تو ان کی طرف سے انتظار میں اپنے رب کی مہربانی کے جسکی تجھ کو توقع ہے تو کہہ دے انکو بات نرمی کی ');
INSERT INTO "al_hind" VALUES(17,29,'17:29','اور نہ رکھ اپنا ہاتھ بندھا ہوا اپنی گردن کے ساتھ اور نہ کھول دے اس کو بالکل کھول دینا پھر تو بیٹھ رہے الزام کھایا ہارا ہوا ');
INSERT INTO "al_hind" VALUES(17,30,'17:30','تیرا رب کھول دیتا ہے روزی جسکے واسطے چاہے اور تنگ بھی وہی کرتا ہے وہی ہے اپنے بندوں کو جاننے والا دیکھنے والا ');
INSERT INTO "al_hind" VALUES(17,31,'17:31','اور نہ مار ڈالو اپنی اولاد کو مفلسی کے خوف سے ہم روزی دیتےہیں انکو اور تم کو بیشک اُن کا مارنا بڑی خطاء ہے ');
INSERT INTO "al_hind" VALUES(17,32,'17:32','اور پاس نہ جاؤ زنا کے وہ ہے بے حیائی اور بری راہ ہے ');
INSERT INTO "al_hind" VALUES(17,33,'17:33','اور نہ مارو اس جان کو جس کو منع کر دیا اللہ نے مگر حق پر اور جو مارا گیا ظلم سے تو دیا ہم نے اسکے وارث کو زور سو حد سے نہ نکل جائے قتل کرنے میں اسکو مدد ملتی ہے ');
INSERT INTO "al_hind" VALUES(17,34,'17:34','اور پاس نہ جاؤ یتیم کے مال کے مگر جس طرح کہ بہتر ہو جب تک کہ وہ پہنچے اپنی جوانی کو اور پورا کرو عہد کو بیشک عہد کی پوچھ ہو گی ');
INSERT INTO "al_hind" VALUES(17,35,'17:35','اور پورا بھر دو ماپ جب ماپ کر دینے لگو اور تولو سیدھی ترازو سے یہ بہتر ہے اور اچھا ہے اس کا انجام ');
INSERT INTO "al_hind" VALUES(17,36,'17:36','اور نہ پیچھے پڑ جس بات کی خبر نہیں تجھ کو بیشک کان اور آنکھ اور دل ان سب کی اس سے پوچھ ہو گی ');
INSERT INTO "al_hind" VALUES(17,37,'17:37','اور مت چل زمین پر اتراتا ہوا تو پھاڑ نہ ڈالے گا زمین کو اور نہ ہنچے گا پہاڑوں تک لمبا ہو کر ');
INSERT INTO "al_hind" VALUES(17,38,'17:38','یہ جتنی باتیں ہیں ان سب میں بری چیز ہے تیرے رب کی بیزاری ');
INSERT INTO "al_hind" VALUES(17,39,'17:39','یہ ہے ان باتوں میں سے جو وحی بھیجی تیرے رب نے تیری طرف عقل کے کاموں سے اور نہ ٹھہرہا اللہ کے سوائے کسی اور کی بندگی پھر پڑے تو دوزخ میں الزام کھا کر دھکیلا جا کر ');
INSERT INTO "al_hind" VALUES(17,40,'17:40','کیا تم کو چن کر دیدئے تمہارے رب نے بیٹے اور اپنے لئے کر لیا فرشتوں کو بیٹیاں تم کہتے ہو بھاری بات ');
INSERT INTO "al_hind" VALUES(17,41,'17:41','اور پھیر پھیر کر سمجھایا ہم نے اس قرآن میں تاکہ وہ سوچیں اور ان کو زیاد ہوتا ہے وہی بدکنا ');
INSERT INTO "al_hind" VALUES(17,42,'17:42','کہہ اگر ہوتے اس کے ساتھ اور حاکم جیسا یہ بتلاتے ہیں تو نکالتے صاحب عرش کی طرف راہ ');
INSERT INTO "al_hind" VALUES(17,43,'17:43','وہ پاک ہے اور برتر ہے ان کی باتوں سے بے نہایت');
INSERT INTO "al_hind" VALUES(17,44,'17:44','اس کی پاکی بیان کرتے ہیں ساتوں آسمان اور زمین اور جو کوئی ان میں ہے اور کوئی چیز نہیں جو نہیں پڑھتی خوبیاں اس کی لیکن تم نہیں سمجھتے ان کا پڑھنا بیشک وہ ہے تحمل والا بخشنے والا ');
INSERT INTO "al_hind" VALUES(17,45,'17:45','اور جب تو پڑھتا ہے قرآن کر دیتے ہیں ہم بیچ میں تیرے اور ان لوگوں کے جو نہیں مانتے آخرت کو ایک پردہ چھپا ہوا ');
INSERT INTO "al_hind" VALUES(17,46,'17:46','اور ہم رکھتے ہیں ان کے دلوں پر پردہ کہ اس کو نہ سمجھیں اور ان کے کانوں میں بوجھ اور جب ذکر کرتا ہے تو قرآن میں اپنے رب کا اکیلا کر کر بھاگتے ہیں اپنی پیٹھ پر بدک کر ');
INSERT INTO "al_hind" VALUES(17,47,'17:47','ہم خوب جانتے ہیں جس واسطے وہ سنتے ہیں جس وقت کان رکھتے ہیں تیری طرف اور جب وہ مشورت کرتے ہیں جب کہ کہتے ہیں یہ بے انصاف جس کے کہنے پر تم چلتے ہو وہ نہیں ہے مگر ایک مرد جادو کا مارا ');
INSERT INTO "al_hind" VALUES(17,48,'17:48','دیکھ لے کیسے جماتے ہیں تجھ پر مثلیں اور بہکتے پھرتے ہیں سو راہ نہیں پا سکتے ');
INSERT INTO "al_hind" VALUES(17,49,'17:49','اور کہتے ہیں کیا جب ہم ہو جائیں ہڈیاں اور چورا چورا پھر اٹھیں گے نئے بن کر ');
INSERT INTO "al_hind" VALUES(17,50,'17:50','تو کہہ تم ہو جاؤ پتھر یا لوہا');
INSERT INTO "al_hind" VALUES(17,51,'17:51','یا کوئی خلقت جس کو مشکل سمجھو اپنے جی میں پھر اب کہیں گے کون لوٹا کر لائے گا ہم کو کہہ جس نے پیدا کیا تم کو پہلی بار پھر اب مٹکائیں گے تیری طرف اپنے سر اور کہیں گے کب ہو گا یہ تو کہہ شاید نزدیک ہی ہو گا ');
INSERT INTO "al_hind" VALUES(17,52,'17:52','جس دن تم کو پکارے گا پھر چلے آؤ گے اسکی تعریف کرتے ہوئے اور اٹکل کرو گے کہ دیر نہیں لگی تم کو مگر تھوڑی ');
INSERT INTO "al_hind" VALUES(17,53,'17:53','اور کہہ دے میرے بندوں کہ بات وہی کہیں جو بہتر ہو شیطان جھڑپ کرواتا ہے ان میں شیطان ہے انسان کا دشمن صریح ');
INSERT INTO "al_hind" VALUES(17,54,'17:54','تمہارا رب خوب جانتا ہے تم کو اگر چاہے تم پر رحم کرے اور اگر چاہے تم کو عذاب دے اور تجھ کو نہیں بھیجا ہم نے ان پر ذمہ لینے والا ');
INSERT INTO "al_hind" VALUES(17,55,'17:55','اور تیرا رب خوب جانتا ہے انکو جو آسمانوں میں ہیں اور زمین میں اور ہم نے افضل کیا ہے بعض پیغمبروں کو بعضوں سے اور دی ہم نے داؤد کو زبور ');
INSERT INTO "al_hind" VALUES(17,56,'17:56','کہہ پکارو جن کو تم سمجھتے ہو سوائے اس کے سو وہ اختیار نہیں رکھتے کہ کھول دیں تکلیف کو تم سے اور نہ بدل دیں ');
INSERT INTO "al_hind" VALUES(17,57,'17:57','وہ لوگ جن کو یہ پکارتے ہیں وہ خود ڈھونڈتے ہیں اپنے رب تک وسیلہ کہ کونسا بندہ بہت نزدیک ہے اور امید رکھتے ہیں اس کی مہربانی کی اور ڈرتے ہیں اس کے عذاب سے بیشک تیرے رب کا عذاب ڈرنے کی چیز ہے ');
INSERT INTO "al_hind" VALUES(17,58,'17:58','اور کوئی بستی نہیں جس کو ہم خراب نہ کر دیں گے قیامت سے پہلے یا آفت ڈالیں گے اس پر سخت آفت یہ ہے کتاب میں لکھا گیا ');
INSERT INTO "al_hind" VALUES(17,59,'17:59','اور ہم نے اس لئے موقوف کیں نشانیاں بھیجنی کہ اگلوں نے ان کو جھٹلایا اور ہم نے دی ثمود کو اونٹنی ان کے سجھانے کو پھر ظلم کیا اس پر اور نشانیاں جو ہم بھیجتے ہیں سو ڈرانے کو ');
INSERT INTO "al_hind" VALUES(17,60,'17:60','اور جب کہہ دیا ہم نے تجھ سے کہ تیرے رب نے گھیر لیا ہے لوگوں کو اور وہ دکھلاوا جو تجھ کو دکھلایا ہم نے سو جانچنے کو لوگوں کے اور ایسے ہی وہ درخت جس پر پھٹکار ہے قرآن میں اور ہم انکو ڈراتے ہیں تو انکو زیادہ ہوتی ہے بری شرارت ');
INSERT INTO "al_hind" VALUES(17,61,'17:61','اور جب ہم نے کہا فرشتوں کو سجدہ کرو آدم کو تو سجدہ میں گر پڑے مگر ابلیس بولا کیا میں سجدہ کروں ایک شخص کو جس کو تو نے بنایا مٹی کا ');
INSERT INTO "al_hind" VALUES(17,62,'17:62','کہنے لگا بھلا دیکھ تو یہ شخص جس کو تو نے مجھ سے بڑھا دیا اگر تو مجھ کو ڈھیل دیوے قیامت کے دن تک تو میں اس کی اولاد کو ڈانٹی دے لوں گا مگر تھوڑے سے ');
INSERT INTO "al_hind" VALUES(17,63,'17:63','فرمایا جا پھر جو کوئی تیرے ساتھ ہوا ان میں سے سو دوزخ ہے تم سب کی سزا بدلہ پورا ');
INSERT INTO "al_hind" VALUES(17,64,'17:64','اور گھبرا لے ان میں جس کو تو گھبرا سکے اپنی آواز سے اور لے آ ان پر اپنے سوار اور پیادے اور ساجھا کر ان سے مال اور اولاد میں اور وعدے دے انکو اور کچھ نہیں وعدہ دیتا انکو شیطان مگر دغابازی ');
INSERT INTO "al_hind" VALUES(17,65,'17:65','وہ جو میرے بندے ہیں ان پر نہیں تیری حکومت اور تیرا رب کافی ہے کام بنانے والا ');
INSERT INTO "al_hind" VALUES(17,66,'17:66','تمہارا رب وہ ہے جو چلاتا ہے تمہارے واسطے کشتی دریا میں تاکہ تلاش کرو اس کا فضل وہی ہے تم پر مہربان');
INSERT INTO "al_hind" VALUES(17,67,'17:67','اور جب آتی ہے تم پر آفت دریا میں بھول جاتے ہو جن کو پکارا کرتے تھے اللہ کے سوائے پھر جب بچا لایا تم کو خشکی میں پھر جاتے ہو اور ہے انسان بڑا نا شکر ');
INSERT INTO "al_hind" VALUES(17,68,'17:68','سو کیا تم بے ڈر ہو گئے اس سے کہ دھنسا دے تم کو جنگل کے کنارے یا بھیج دے تم پر آندھی پتھر برسانے والی پھر نہ پاؤ اپنا کوئی نگہبان');
INSERT INTO "al_hind" VALUES(17,69,'17:69','یا بے ڈر ہو گئے ہو اس سے کہ پھر لیجائے تمکو دریا میں دوسری بار پھر بھیجے تم پر ایک سخت جھوکا ہوا کا پھر ڈبا دے تم کو بدلے میں اس ناشکری کے پھر نہ پاؤ اپنی طرف سے ہم پر اس کا کوئی باز پرس کرنے والا ');
INSERT INTO "al_hind" VALUES(17,70,'17:70','اور ہم نےعزت دی ہے آدم کی اولاد کو اور سواری دی ان کو جنگل اور دریا میں اور روزی دی ہم نے ان کو ستھری چیزوں سے اور بڑھا دیا ان کو بہتوں سے جن کو پیدا کیا ہم نے بڑائی دے کر ');
INSERT INTO "al_hind" VALUES(17,71,'17:71','جس دن ہم بلائیں گے ہر فرقہ کو ان کے سرداروں کے ساتھ سو جس کو ملا اس کا اعمال نامہ اس کے داہنی ہاتھ میں سو وہ لوگ پڑھیں گے اپنا لکھا اور ظلم نہ ہو گا ان پر ایک تاگے کا ');
INSERT INTO "al_hind" VALUES(17,72,'17:72','اور جو کوئی رہا اس جہان میں اندھا سو وہ پچھلے جہان میں بھی اندھا ہے اور بہت دور پڑا ہوا راہ سے ');
INSERT INTO "al_hind" VALUES(17,73,'17:73','اور وہ لوگ تو چاہتے تھے کہ تجھ کو بچلا دیں اس چیز سے کہ جو وحی بھیجی ہم نے تیری طرف تاکہ جھوٹ بنا لائے تو ہم پر وحی کے سوا اور تب تو بنا لیتے تجھ کو دوست ');
INSERT INTO "al_hind" VALUES(17,74,'17:74','اور اگر یہ نہ ہوتا کہ ہم نے تجھ کو سنبھالے رکھا تو لگ جاتا جھکنے ان کی طرف تھوڑا سا ');
INSERT INTO "al_hind" VALUES(17,75,'17:75','تب تو ضرور چکھاتے ہم تجھ کو دونا مزہ زندگی میں اور دونا مرنے میں پھر نہ پاتا تو اپنے واسطے ہم پر مدد کرنے والا ');
INSERT INTO "al_hind" VALUES(17,76,'17:76','اور وہ تو چاہتے تھے کہ گھبرا دیں تجھ کو اس زمین سے تاکہ نکال دیں تجھ کو یہاں سے اور اس وقت نہ ٹھہریں گے وہ بھی تیرے پیچھے مگر تھوڑا ');
INSERT INTO "al_hind" VALUES(17,77,'17:77','دستور چلا آتا ہے ان رسولوں کا جو تجھ سے پہلے بھیجے ہم نے اپنے پیغمبر اور نہ پائے گا تو ہمارے دستور میں تفاوت ');
INSERT INTO "al_hind" VALUES(17,78,'17:78','قائم رکھ نماز کو سورج ڈھلنے سے رات کے اندھیرے تک اورقرآن پڑھنا فجر کا بیشک قرآن پڑھنا فجر کا ہوتا ہے روبرو ');
INSERT INTO "al_hind" VALUES(17,79,'17:79','اور کچھ رات جاگتا رہ قرآن کے ساتھ یہ زیادتی ہے تیرے لئے قریب ہے کہ کھڑا کر دے تجھ کو تیرا رب مقام محمود میں ');
INSERT INTO "al_hind" VALUES(17,80,'17:80','اور کہہ اے رب داخل کر مجھ کو سچا داخل کرنا اور نکال مجھ کو سچا نکالنا اور عطا کر دے مجھ کو اپنے پاس سے حکومت کی مدد ');
INSERT INTO "al_hind" VALUES(17,81,'17:81','اور کہہ آیا سچ اور نکل بھاگا جھوٹ بیشک جھوٹ ہے نکل بھاگنے والا ');
INSERT INTO "al_hind" VALUES(17,82,'17:82','اور ہم اتارتے ہیں قرآن میں سے جس سے روگ دفع ہوں اور رحمت ایمان والوں کے واسطے اور گنہگاروں کو تو اس سے نقصان ہی بڑھتا ہے ');
INSERT INTO "al_hind" VALUES(17,83,'17:83','اور جب ہم آرام بھیجیں انسان پر تو ٹال جائے اور بچائے اپنا پہلو اور جب پہنچے اس کو برائی تو رہ جائے مایوس ہو کر ');
INSERT INTO "al_hind" VALUES(17,84,'17:84','تو کہہ ہر ایک کام کرتا ہے اپنے ڈھنگ پر سو تیرا رب خوب جانتا ہے کس نے خوب پالیا راستہ ');
INSERT INTO "al_hind" VALUES(17,85,'17:85','اور تجھ سے پوچھتے ہیں روح کو کہدے روح ہے میرے رب کے حکم سے اور تم کو علم دیا ہے تھوڑا سا ');
INSERT INTO "al_hind" VALUES(17,86,'17:86','اور اگر ہم چاہیں تو لیجائیں اس چیز کو جو ہم نے تجھ کو وحی بھیجی پھر تو نہ پائے اپنے واسطے اس کے لاد ینے کو ہم پر کوئی ذمہ دار');
INSERT INTO "al_hind" VALUES(17,87,'17:87','مگر مہربانی سے تیرے رب کی اس کی بخشش تجھ پر بڑی ہے ');
INSERT INTO "al_hind" VALUES(17,88,'17:88','کہہ اگر جمع ہوں آدمی اور جن اس پر کہ لائیں ایسا قرآن ہر گز نہ لائیں گے ایسا قرآن اور پڑے مدد کیا کریں ایک دوسرے کی ');
INSERT INTO "al_hind" VALUES(17,89,'17:89','اور ہم نے پھیر پھیر کو سمجھائی لوگوں کو اس قرآن میں ہر مثل سو نہیں رہتے بہت لوگ بن ناشکری کئے ');
INSERT INTO "al_hind" VALUES(17,90,'17:90','اور بولے ہم نہ مانیں گے تیرا کہا جب تک تو نہ جاری کر دے ہمارے واسطے زمین سے ایک چشمہ ');
INSERT INTO "al_hind" VALUES(17,91,'17:91','یا ہو جائے تیرے واسطے ایک باغ کھجور اور انگور کا پھر بہائے تو اس کے بیچ نہریں چلا کر');
INSERT INTO "al_hind" VALUES(17,92,'17:92','یا گرا دے آسمان ہم پر جیسا کہ تو کہا کرتا ہے ٹکڑے ٹکڑے یا لے آ اللہ کو اور فرشتوں کو سامنے ');
INSERT INTO "al_hind" VALUES(17,93,'17:93','یا ہو جائے تیرے لئے ایک گھر سنہرا یا چڑھ جائے تو آسمان میں اور ہم نہ مانیں گے تیرے چڑھ جانے کو جب تک نہ اتار لائے ہم پر ایک کتاب جس کو ہم پڑھیں تو کہہ سبحان اللہ میں کون ہوں مگر ایک آدمی ہوں بھیجا ہوا ');
INSERT INTO "al_hind" VALUES(17,94,'17:94','اور لوگوں کو روکا نہیں ایمان لانے سے جب پہنچی ان کو ہدایت مگر اسی بات نے کہ کہنے لگے کیا اللہ نے بھیجا آدمی کو پیغام دے کر ');
INSERT INTO "al_hind" VALUES(17,95,'17:95','کہہ اگر ہوتے زمین میں فرشتے پھرتے بستے تو ہم اتارتے ان پر آسمان سے کوئی فرشتہ پیغام دے کر ');
INSERT INTO "al_hind" VALUES(17,96,'17:96','کہہ اللہ کافی ہے حق ثابت کرنے والا میرے اور تمہارے بیچ میں وہ ہے اپنے بندوں سے خبردار دیکھنے والا ');
INSERT INTO "al_hind" VALUES(17,97,'17:97','اور جس کو راہ دکھلائے اللہ وہی ہے راہ پانے والا اور جس کو بھٹکائے پھر تو نہ پائے انکے واسطے کوئی رفیق اللہ کے سوائے اور اٹھائیں گے ہم انکو دن قیامت کے چلیں گے منہ کے بل اندھے اور گونگے اور بہرے ٹھکانا ان کو دوزخ ہے جب لگے گی بجھنے اور بھڑکا دیں گے ان پر ');
INSERT INTO "al_hind" VALUES(17,98,'17:98','یہ ان کی سزا ہے اس واسطے کہ منکر ہوئے ہماری آیتوں سے اور بولے کیا جب ہم ہو گئے ہڈیاں چورا چورا کیا ہم کو اٹھائیں گے نئے بنا کر ');
INSERT INTO "al_hind" VALUES(17,99,'17:99','کیا نہیں دیکھ چکےکہ جس اللہ نے بنائے آسمان اور زمین وہ بنا سکتا ہے ایسوں کو اور مقرر کیا ہے انکے واسطے ایک وقت بے شبہ سو نہیں رہا جاتا بے انصافوں کو بن ناشکری کیے ');
INSERT INTO "al_hind" VALUES(17,100,'17:100','کہہ اگر تمہارے ہاتھ میں ہوتے میرے رب کی رحمت کے خزانے تو ضرور بند کر رکھتے اس ڈر سے کہ خرچ نہ ہو جائیں اور ہے انسان دل کا تنگ ');
INSERT INTO "al_hind" VALUES(17,101,'17:101','اور ہم نے دیں موسٰی کو نو نشانیاں صاف پھر پوچھ بنی اسرائیل سے جب آیا وہ انکے پاس تو کہا اس کو فرعون نے میری اٹکل میں تو موسٰی تجھ پر جادو ہوا ');
INSERT INTO "al_hind" VALUES(17,102,'17:102','بولا تو جان چکا ہے کہ یہ چیزیں کسی نے نہیں اتاریں مگر آسمان اور زمین کے مالک نے سجھانے کو اور میری اٹکل میں فرعون تو غارت ہوا چاہتا ہے ');
INSERT INTO "al_hind" VALUES(17,103,'17:103','پھر چاہا کہ بنی اسرائیل کو چین نہ دے اس زمین میں پھر ڈبا دیا ہم نے اسکو اور اس کے ساتھ والوں کو سب کو ');
INSERT INTO "al_hind" VALUES(17,104,'17:104','اور کہا ہم نے اس کے پیچھے بنی اسرائیل کو آباد رہو تم زمین میں پھر جب آئے گا وعدہ آخرت کالے آئیں گے ہم تم کو سمیٹ کر ');
INSERT INTO "al_hind" VALUES(17,105,'17:105','اور سچ کے ساتھ اتارا ہم نے یہ قرآن اور سچ کے ساتھ اترا اور تجھ کو جو بھیجا ہم نے سو خوشی اور ڈر سنانے کو ');
INSERT INTO "al_hind" VALUES(17,106,'17:106','اور پڑھنے کا وظیفہ کیا ہم نےقرآن کو جدا جدا کر کے کہ پڑھے تو اسکو لوگوں پر ٹھہر ٹھہر کر اور اس کو ہم نے اتارتے اتارتے اتارا ');
INSERT INTO "al_hind" VALUES(17,107,'17:107','کہہ کہ تم اس کو مانو یا نہ مانو جن کو علم ملا ہے اس کے پہلے سے جب ان کے پاس اس کو پڑھیں گرتے ہیں تھوڑیوں پر سجدہ میں');
INSERT INTO "al_hind" VALUES(17,108,'17:108','اور کہتے ہیں پاک ہے ہمارا رب بیشک ہمارے رب کا وعدہ ہو کر رہے گا ');
INSERT INTO "al_hind" VALUES(17,109,'17:109','اور گرتے ہیں تھوڑیوں پر روتے ہوئے اور زیادہ ہوتی ہے ان کو عاجزی ');
INSERT INTO "al_hind" VALUES(17,110,'17:110','کہہ اللہ کہہ کر پکارو یا رحمٰن کہہ کر جو کر پکارو گے سو اسی کے ہیں سب نام خاصے اور پکار کر مت پڑھ اپنی نماز اور نہ چپکے پڑھ اور ڈھونڈ لے اس کے بیچ میں راہ ');
INSERT INTO "al_hind" VALUES(17,111,'17:111','اور کہہ سب تعریفیں اللہ کو جونہیں رکھتا اولاد اور نہ کوئی اس کا ساجھی سلطنت میں اور نہ کوئی اس کا مددگار ذلت کے وقت پر اور اس کی برائی کر بڑا جان کر ');
INSERT INTO "al_hind" VALUES(18,1,'18:1','سب تعریف اللہ کو جس نے اتاری اپنے بندہ پر کتاب اور نہ رکھی اس میں کچھ کجی ');
INSERT INTO "al_hind" VALUES(18,2,'18:2','ٹھیک اتاری تاکہ ڈر سنا دےایک سخت آفت کا اللہ کی طرف سے اور خوشخبری دے ایمان لانے والوں کو جو کرتے ہیں نیکیاں کہ ان کے لئے اچھا بدلہ ہے');
INSERT INTO "al_hind" VALUES(18,3,'18:3','جس میں رہا کریں ہمیشہ ');
INSERT INTO "al_hind" VALUES(18,4,'18:4','اور ڈر سنا دے انکو جو کہتے ہیں اللہ رکھتا ہے اولاد ');
INSERT INTO "al_hind" VALUES(18,5,'18:5','کچھ خبر نہیں ان کو اس بات کی اور نہ ان کے باپ دادوں کو کیا بڑی بات نکلتی ہے ان کے منہ سے سب جھوٹ ہے جو کہتے ہیں ');
INSERT INTO "al_hind" VALUES(18,6,'18:6','سو کہیں تو گھونٹ ڈالے گا اپنی جان کو ان کے پیچھے اگر وہ نہ مانیں گے اس بات کو پچتا پچتا کر ');
INSERT INTO "al_hind" VALUES(18,7,'18:7','ہم نے بنایا ہے جو کچھ زمین پر ہے اس کی رونق تاکہ جانچیں لوگوں کو کون ان میں اچھا کرتا ہے کام ');
INSERT INTO "al_hind" VALUES(18,8,'18:8','اور ہم کو کرنا ہے جو کچھ اس پر ہے میدان چھانٹ کر ');
INSERT INTO "al_hind" VALUES(18,9,'18:9','کیا تو خیال کرتا ہے کہ غار اور کھوہ کے رہنے والے ہماری قدرتوں میں عجب اچنبھا تھے ');
INSERT INTO "al_hind" VALUES(18,10,'18:10','جب جا بیٹھے وہ جوان پہاڑ کی کھو میں پھر بولے اے رب دے ہم کو اپنے پاس سے بخشش اور پوری کر دے ہمارے کام کی درستی');
INSERT INTO "al_hind" VALUES(18,11,'18:11','پھر تھپک دئے ہم نے ان کے کان اس کھوہ میں چند برس گنتی کے ');
INSERT INTO "al_hind" VALUES(18,12,'18:12','پھر ہم نے ان کو اٹھایا کہ معلوم کریں دو فرقوں میں کس نے یاد رکھی ہے جتنی مدت وہ رہے ');
INSERT INTO "al_hind" VALUES(18,13,'18:13','ہم سنا دیں تجھ کو ان کا حال تحقیقی وہ کئ جوان ہیں کہ یقین لائے اپنے رب پر اور زیادہ دی ہم نے انکو سوجھ ');
INSERT INTO "al_hind" VALUES(18,14,'18:14','اور گرہ دی ان کے دل پر جب کھڑے ہوئے پھر بولے ہمارا رب ہے رب آسمان اور زمین کا نہ پکاریں گے ہم اس کے سوائے کسی کو معبود نہیں تو کہی ہم نے بات عقل سے دور ');
INSERT INTO "al_hind" VALUES(18,15,'18:15','یہ ہماری قوم ہے ٹھہرا لئے انہوں نے اللہ کے سوائے اور معبود کیوں نہیں لاتے ان پر کوئی سند کھلی پھر اس سے بڑا گنہگار کون جس نے باندھا اللہ پر جھوٹ ');
INSERT INTO "al_hind" VALUES(18,16,'18:16','اور جب تم نے کنارہ کر لیا ان سے اور جن کو وہ پوجتے ہیں اللہ کےسوائے تو اب جا بیٹھو اس کھوہ میں پھیلا دے تم پر رب تمہار ا کچھ اپنی رحمت سے اور بنا دیوے تمہارے واسطے تمہارے کام میں آرام ');
INSERT INTO "al_hind" VALUES(18,17,'18:17','اور تو دیکھے دھوپ جب نکلتی ہے بچ کر جاتی ہے ان کی کھوہ سےداہنے کو اور جب ڈوبتی ہے کترا جاتی ہے ان سے بائیں کو اور وہ میدان میں ہیں اس کے یہ ہے اللہ کی قدرتوں سے جسکو راہ دیوے اللہ وہی آئے راہ پر اور جس کو وہ بچلائے پھر تو نہ پائے اس کا کوئی رفیق راہ پر لانے والا ');
INSERT INTO "al_hind" VALUES(18,18,'18:18','اور تو سمجھے وہ جاگتے ہیں اور وہ سو رہے ہیں اور کروٹیں دلاتے ہیں ہم انکو داہنے اور بائیں اور کتا ان کا پسار رہا ہے اپنی باہیں چوکھٹ پر اگر تو جھانک کر دیکھے ان کو تو پیٹھ دے کر بھاگے ان سے اور بھر جائے تجھ میں انکی دہشت ');
INSERT INTO "al_hind" VALUES(18,19,'18:19','اور اسی طرح ان کو جگا دیا ہم نے کہ آپس میں پوچھنے لگے ایک بولا ان میں کتنی دیر ٹھہرےتم بولے ہم ٹھہرے ایک دن یا دن سے کم بولے تمہارا رب ہی خوب جانے جتنی دیر تم رہے ہو اب بھیجو اپنے میں سے ایک کو یہ روپیہ دے کر اپنا اس شہر میں پھر دیکھے کونسا کھانا ستھرا ہے سو لائے تمہارے پاس اس میں سے کھانا اور نرمی سے جائے اور جتا نہ دے تمہاری خبر کسی کو');
INSERT INTO "al_hind" VALUES(18,20,'18:20','وہ لوگ اگر خبر پالیں تمہاری پتھروں سے مار ڈالیں تم کو یا لوٹا لیں تم کو اپنے دین میں اور تب تو بھلا نہ ہو گا تمہارا کبھی ');
INSERT INTO "al_hind" VALUES(18,21,'18:21','اور اسی طرح خبر ظاہر کر دی ہم نے انکی تاکہ لوگ جان لیں کہ اللہ کا وعدہ ٹھیک ہے اور قیامت کے آنے میں دھوکہ نہیں جب جھگڑ رہے تھے آپس میں اپنی بات پر پھر کہنے لگے بناؤ ان پر ایک عمارت انکا رب خوب جانتا ہے ان کا حال بولے وہ لوگ جن کا کام غالب تھا ہم بنائیں گے ان کی جگہ پر عبادت خانہ ');
INSERT INTO "al_hind" VALUES(18,22,'18:22','اب یہی کہیں گے وہ تین ہیں چوتھا ان کا کتا اور یہ بھی کہیں گے وہ پانچ ہیں چھٹا ان کا کتا بدون نشانی دیکھے پتھر چلانا اور یہ بھی کہیں گے وہ سات ہیں اور آٹھوں انکا کتا تو کہہ میرا رب خوب جانتا ہے ان کی گنتی ان کی خبر نہیں رکھتے مگر تھوڑے لوگ سو مت جھگڑ ان کی بات میں مگر سر سری جھگڑا اور مت تحقیق کر ان کا حال ان میں کسی سے ');
INSERT INTO "al_hind" VALUES(18,23,'18:23','اور نہ کہنا کسی کام کو کہ میں یہ کروں گا کل کو');
INSERT INTO "al_hind" VALUES(18,24,'18:24','مگر یہ کہ اللہ چاہے اور یاد کر لے اپنے رب کو جب بھول جائے اور کہہ امید ہے کہ میرا رب مجھ کو دکھلائے اس سے زیادہ نزدیک راہ نیکی کی ');
INSERT INTO "al_hind" VALUES(18,25,'18:25','اور مدت گذری ان پر اپنی کھوہ میں تین سو برس اور ان کے اوپر نو ');
INSERT INTO "al_hind" VALUES(18,26,'18:26','تو کہہ اللہ خوب جانتا ہے جتنی مدت ان پر گذری اسی کی پاس ہیں چھپے بھید آسمان اور زمین کے کیا عجیب دیکھتا اور سنتا ہے کوئی نہیں بندوں پر اس کے سوائے مختار اور نہیں شریک کرتا اپنے حکم میں کسی کو ');
INSERT INTO "al_hind" VALUES(18,27,'18:27','اور پڑھ جو وحی ہوئی تجھ کو تیرے رب کی کتاب سے کوئی بدلنے والا نہیں اسکی باتیں اور کہیں نہ پائے گا تو اس کے سوائے چھپنے کو جگہ ');
INSERT INTO "al_hind" VALUES(18,28,'18:28','اور روکے رکھ اپنے آپ کو ان کے ساتھ جو پکارتے ہیں اپنے رب کو صبح اور شام طالب ہیں اسکے منہ کے اور نہ دوڑیں تیری آنکھیں انکو چھوڑ کر تلاش میں رونق زندگانی دنیا کی اور نہ کہا مان اس کا جس کا دل غافل کیا ہم نے اپنی یاد سے اور پیچھے پڑا ہوا ہے اپنی خوشی کے اور اس کا کام ہے حد پر نہ رہنا ');
INSERT INTO "al_hind" VALUES(18,29,'18:29','اور کہہ سچی بات ہے تمہارے رب کی طرف سے پھر جو کوئی چاہے مانے اور جو کوئی چاہے نہ مانے ہم نے تیار کر رکھی ہے گنہگاروں کے واسطے آگ کہ گھیر رہی ہیں انکو اسکی قناتیں اور اگر فریاد کریں گے تو ملے گا پانی جیسے پیپ بھون ڈالے منہ کو کیا برا پینا ہے اور کیا برا آرام ');
INSERT INTO "al_hind" VALUES(18,30,'18:30','بیشک جو لوگ یقین لائے اور کیں نیکیاں ہم نہیں کھوتے بدلہ اس کا جس نے بھلا کیا کام ');
INSERT INTO "al_hind" VALUES(18,31,'18:31','ایسوں کے واسطے باغ ہیں بسنے کے بہتی ہیں ان کے نیچے نہریں پہنائے جائیں گے ان کو وہاں کنگن سونے کے اور پہنیں گے کپڑے سبز باریک اور گاڑھے ریشم کے تکیہ لگائے ہوئے ان میں تختوں پر کیا خوب بدلہ ہے اور کیا خوب آرام ');
INSERT INTO "al_hind" VALUES(18,32,'18:32','اور بتلا انکو مثل دو مردوں کی کر دیے ہم نے ان میں سے ایک کے لئے دو باغ انگور کے اور گرد ان کے کھجویرں اور رکھی دونوں کے بیچ میں کھیتی ');
INSERT INTO "al_hind" VALUES(18,33,'18:33','دونوں باغ لاتے ہیں اپنا میوہ اور نہیں گھٹاتے اس میں سے کچھ اور بہا دی ہم نے ان دنوں کے بیچ نہر ');
INSERT INTO "al_hind" VALUES(18,34,'18:34','اور ملا اسکو پھل پھر بولا اپنے ساتھی سے جب باتیں کرنے لگا اس سے میرے پاس زیادہ ہے تجھ سے مال اور آبرو کے لوگ ');
INSERT INTO "al_hind" VALUES(18,35,'18:35','اور گیا اپنے باغ میں اور وہ برا کر رہا تھا اپنی جان پر بولا نہیں آتا مجھ کو خیال کہ خراب ہووے یہ باغ کبھی');
INSERT INTO "al_hind" VALUES(18,36,'18:36','اور نہیں خیال کرتا ہوں کہ قیامت ہونے والی ہے اور اگر کبھی پہنا دیا گیا میں اپنے رب کے پاس پاؤں گا بہتر اس سے وہاں پہنچ کر ');
INSERT INTO "al_hind" VALUES(18,37,'18:37','کہا اس کو دوسرے نے جب بات کرنے لگا کیا تو منکر ہو گیا اس سے جس نے پیدا کیا تجھ کو مٹی سے پھر قطرہ سے پھر پورا کر دیا تجھ کو مرد');
INSERT INTO "al_hind" VALUES(18,38,'18:38','پھر میں تو یہی کہتا ہوں وہی اللہ ہے میرا رب اور نہیں مانتا شریک اپنے رب کا کسی کو ');
INSERT INTO "al_hind" VALUES(18,39,'18:39','اور جب تو آتا تھا اپنے باغ میں کیوں نہ کہا تو نے جو چاہے اللہ سو ہو طاقت نہیں مگر جو دے اللہ اگر تو دیکھتا ہے مجھ کو کہ میں کم ہوں تجھ سے مال اور اولاد میں');
INSERT INTO "al_hind" VALUES(18,40,'18:40','تو امید ہے کہ میرا رب دیوے مجھ کو تیرے باغ سے بہتر اور بھیج دے اس پر لو کا ایک جھونکا آسمان سے پھر صبح کو رہ جاٹے میدان صاف');
INSERT INTO "al_hind" VALUES(18,41,'18:41','یا صبح کو ہو رہے اس کا پانی خشک پھر نہ لا سکے تو اس کو ڈھونڈھ کر ');
INSERT INTO "al_hind" VALUES(18,42,'18:42','اور سمیٹ لیا گیا اس کا سارا پھل پھر صبح کو رہ گیا ہاتھ نچاتا اس مال پر جو اس میں لگایا تھا اور وہ گرا پڑا تھا اپنی چھتریوں پر اور کہنے لگا کیا خوب ہوتا اگر میں شریک نہ بناتا اپنے رب کا کسی کو ');
INSERT INTO "al_hind" VALUES(18,43,'18:43','اور نہ ہوئی اس کی جماعت کہ مدد کریں اس کی اللہ کے سوائے اور نہ ہوا وہ کہ خود بدلہ لے سکے ');
INSERT INTO "al_hind" VALUES(18,44,'18:44','یہاں سب اختیار ہے اللہ سچے کا اسی کا انعام بہتر ہے اور اچھا ہے اسی کا دیا ہوا بدلہ ');
INSERT INTO "al_hind" VALUES(18,45,'18:45','اور بتلا دے ان کو مثل دنیا کی زندگی کی جیسے پانی اتارا ہم نے آسمان سے پھر رلا ملا نکلا اس کی وجہ سے زمین کا سبزہ پھر کل کو ہو گیا چورا چورا ہوا میں اڑتا ہوا اور اللہ کو ہے ہر چیز پر قدرت ');
INSERT INTO "al_hind" VALUES(18,46,'18:46','مال اور بیٹے رونق ہیں دنیا کی زندگی میں اور باقی رہنے والی نیکیوں کا بہتر ہے تیرے رب کے یہاں بدلا اور بہتر ہے توقع ');
INSERT INTO "al_hind" VALUES(18,47,'18:47','اور جس دن ہم چلائیں پہاڑ اور تو دیکھے زمین کو کھلی ہوئی اور گھیر ملائیں ہم انکو پھر نہ چھوڑیں ان میں سے ایک کو ');
INSERT INTO "al_hind" VALUES(18,48,'18:48','اور سامنے آئیں تیرے رب کے صف باندھ کر آ پہنچے تم ہمارے پاس جیسے ہم نے بنایا تھا تم کو پہلی بار نہیں تم تو کہتے تھے کہ نہ مقرر کریں گے ہم تمہارے لئے کوئی وعدہ ');
INSERT INTO "al_hind" VALUES(18,49,'18:49','اور رکھا جائے گا حساب کا کاغذ پھر تو دیکھے گنہگاروں کو ڈرتے ہیں اس سے جو اس میں لکھا ہے اور کہتے ہیں ہائے خرابی کیا ہے یہ کاغذ نہیں چھوٹی اس سے چھوٹی بات اور نہ بڑی بات جو اس میں نہیں آ گئ اور پائیں گے جو کچھ کیا ہے سامنے اور تیرا رب ظلم نہ کرے گا کسی پر ');
INSERT INTO "al_hind" VALUES(18,50,'18:50','اور جب کہا ہم نے فرشتوں کو سجدہ کرو آدم کو تو سجدہ میں گر پڑے مگر ابلیس تھا جن کی قسم سے سو نکل بھاگا اپنے رب کے حکم سے سو کیا اب تم ٹھہراتے ہو اس کو اور اس کی اولاد کو رفیق میرے سوائے اور وہ تمہارے دشمن ہیں برا ہاتھ لگا بے انصافوں کے بدلہ ');
INSERT INTO "al_hind" VALUES(18,51,'18:51','دکھلا نہیں لیا تھا میں نے انکو بنانا آسمان اور زمین کا اور نہ بنانا خود ان کا اور میں وہ نہیں کہ بناؤں بہکانے والوں کو اپنا مددگار ');
INSERT INTO "al_hind" VALUES(18,52,'18:52','اور جس دن فرمائے گا پکارو میرے شریکوں کو جن کو تم مانتے تھے پھر پکاریں گے سو وہ جواب نہ دیں گے ان کو اور کر دیں گے ہم انکے بیچ مرنے کی جگہ ');
INSERT INTO "al_hind" VALUES(18,53,'18:53','اور دیکھیں گےگنہگار آگ کو پھر سمجھ لیں گےکہ ان کو پڑنا ہے اس میں اور نہ بدل سکیں گے اس سے رستہ ');
INSERT INTO "al_hind" VALUES(18,54,'18:54','اور بیشک پھیر پھیر کو سمجھائی ہم نے اس قرآن میں لوگوں کو ہر ایک مثل اور ہے انسان سب چیز سے زیادہ جھگڑالو ');
INSERT INTO "al_hind" VALUES(18,55,'18:55','اور لوگوں کو جو روکا اس بات سے کہ یقین لے آئیں جب پہنچی انکو ہدایت اور گناہ بخشوائیں اپنے رب سے سو اسی انتظار نے کہ پہنچے ان پر رسم پہلوں کی یا آ کھڑا ہو ان پر عذاب سامنے کا ');
INSERT INTO "al_hind" VALUES(18,56,'18:56','اور ہم جو رسول بھیجتے ہیں سو خوشخبری اور ڈر سنانے کو اور جھگڑا کرتے ہیں کافر جھوٹا جھگڑا کہ ٹلا دیں اس سے سچی بات کو اور ٹھہرا لیا انہوں نے میرے کلام کو اور جو ڈر سنائے گئے ٹھٹھا ');
INSERT INTO "al_hind" VALUES(18,57,'18:57','اور اس سے زیادہ ظالم کون جس کو سمجھایا اسکے رب کے کلام سے پھر منہ پھیر لیا اسکی طرف سے اور بھول گیا جو کچھ آگے بھیج چکے ہیں اس کے ہاتھ ہم نے ڈال دیے ہیں انکے دلوں پر پردے کہ اس کو نہ سمجھیں اور انکے کانوں میں ہے بوجھ اور اگر تو ان کو بلائے راہ پر تو ہر گز نہ آئیں راہ پر اس وقت کبھی ');
INSERT INTO "al_hind" VALUES(18,58,'18:58','اور تیرا رب بڑا بخشنے والا ہے رحمت والا اگر ان کو پکڑے انکے کئے پر تو جلد ڈالے ان پر عذاب پر ان کے لئے ایک وعدہ ہے کہیں نہ پائیں گے اس سے ورے سرک جانے کو جگہ ');
INSERT INTO "al_hind" VALUES(18,59,'18:59','اور یہ سب بستیاں ہیں جن کو ہم نے غارت کیا جب وہ ظالم ہو گئے اور مقرر کیا تھا ہم نے انکی ہلاکت کا ایک وعدہ ');
INSERT INTO "al_hind" VALUES(18,60,'18:60','اور جب کہا موسٰی نے اپنے جوان کو میں نہ ہٹوں گا جب تک نہ پہنچ جاؤں جہاں ملتے ہیں دو دریا یا چلا جاؤں قرنوں میں ');
INSERT INTO "al_hind" VALUES(18,61,'18:61','پھر جب پہنچے دونوں دریا کے ملاپ تک بھول گئے اپنی مچھلی پھر اس نے اپنی راہ کر لی دریا میں سرنگ بنا کر ');
INSERT INTO "al_hind" VALUES(18,62,'18:62','پھر جب آگے چلے کہا موسٰی نے اپنے جوان کو لا ہمارے پاس ہمارا کھانا ہم نے پائی اپنے اس سفر میں تکلیف ');
INSERT INTO "al_hind" VALUES(18,63,'18:63','بولا وہ دیکھا تو نے جب ہم نے جگہ پکڑی اس پتھر کے پاس سو میں بھول گیا مچھلی اور یہ مجھ کو بھلا دیا شیطان ہی نے کہ اس کا ذکر کروں اور اس نے کر لیا اپنا رستہ دریا میں عجیب طرح');
INSERT INTO "al_hind" VALUES(18,64,'18:64','کہا یہی ہے جو ہم چاہتے تھے پھر الٹے پھرے اپنے پیر پہچانتے ');
INSERT INTO "al_hind" VALUES(18,65,'18:65','پھر پایا ایک بندہ ہمارے بندوں میں کا جس کو دی تھی ہم نے رحمت اپنے پاس سے اور سکھلایا تھا اپنے پاس سے ایک علم ');
INSERT INTO "al_hind" VALUES(18,66,'18:66','کہا اس کو موسٰی نے کہے تو تیرے ساتھ رہوں اس بات پر کہ مجھ کو سکھلا دے کچھ جو تجھ کو سکھلائی ہے بھلی راہ ');
INSERT INTO "al_hind" VALUES(18,67,'18:67','بولا تو نہ ٹھہر سکے گا میرے ساتھ');
INSERT INTO "al_hind" VALUES(18,68,'18:68','اور کیونکر ٹھہرے گا دیکھ کر ایسی چیز کو کہ تیرے قابو میں نہیں اس کا سمجھنا ');
INSERT INTO "al_hind" VALUES(18,69,'18:69','کہا تو پائے گا اگر اللہ نے چاہا مجھ کو ٹھہرنے والا اور نہ ٹالوں گا تیرا کوئی حکم ');
INSERT INTO "al_hind" VALUES(18,70,'18:70','بولا پھر اگر میرے ساتھ رہنا ہےتو مت پوچھیو مجھ سے کوئی چیز جب تک میں شروع نہ کروں تیرے آگے اس کا ذکر ');
INSERT INTO "al_hind" VALUES(18,71,'18:71','پھر دونوں چلے یہاں تک کہ جب چڑھے کشتی میں اس کو پھاڑ ڈالا موسٰی بولا کیا تو نے اس کو پھاڑ ڈالا کہ ڈبا دے اسکے لوگوں کو البتہ تو نے کی ایک چیز بھاری ');
INSERT INTO "al_hind" VALUES(18,72,'18:72','بولا میں نے نہ کہا تھا تو نہ ٹھہر سکے گا میرے ساتھ');
INSERT INTO "al_hind" VALUES(18,73,'18:73','کہا مجھ کو نہ پکڑ میری بھول پر اور مت ڈال مجھ پر میرا کام مشکل ');
INSERT INTO "al_hind" VALUES(18,74,'18:74','پھر دونوں چلے یہاں تک کہ جب ملے ایک لڑکے سے تو اسکو مار ڈالا موسٰی بولا کیا تو نے مار ڈالی ایک جان ستھری بغیر عوض کسی جان کے بیشک تو نے کی ایک چیز نا معقول ');
INSERT INTO "al_hind" VALUES(18,75,'18:75','بولا میں نے تجھ کو نہ کہا تھا کہ تو نہ ٹھہر سکے گا میرے ساتھ ');
INSERT INTO "al_hind" VALUES(18,76,'18:76','کہا اگر تجھ سے پوچھوں کوئی چیز اس کے بعد تو مجھ کو ساتھ نہ رکھیو تو اتار چکا میری طرف سے الزام ');
INSERT INTO "al_hind" VALUES(18,77,'18:77','پھر دونوں چلے یہاں تک کہ جب پہنچے ایک گاؤں کے لوگوں تک کھانا چاہا وہاں کے لوگوں سے انہوں نے نہ مانا انکو مہمان رکھیں پھر پائی وہاں ایک دیوار جو گرا چاہتی تھی اسکو سیدھا کر دیا بولا موسٰی اگر تو چاہتا تو لے لیتا اس پر مزدوری ');
INSERT INTO "al_hind" VALUES(18,78,'18:78','کہا اب جدائی ہے میرے اور تیرے بیچ اب جتلائے دیتا ہوں تجھ کو پھیر ان باتوں کا جس پر تو صبر نہ کر سکا ');
INSERT INTO "al_hind" VALUES(18,79,'18:79','وہ جو کشتی تھی سو چند محتاجوں کی جو محنت کرتے تھے دریا میں سو میں نے چاہا کہ اس میں عیب ڈال دوں اور ان کے پرے تھا ایک بادشاہ جو لے لیتا تھا ہر کشتی کو چھین کر ');
INSERT INTO "al_hind" VALUES(18,80,'18:80','اور وہ جو لڑکا تھا سو اس کے ماں باپ تھے ایمان والے پھر ہم کو اندیشہ ہوا کہ ان کو عاجز کر دے زبردستی اور کفر کر کر ');
INSERT INTO "al_hind" VALUES(18,81,'18:81','پھر ہم نے چاہا کہ بدلہ دے ان کو ان کا رب بہتر اس سے پاکیزگی میں اور نزدیک تر شفقت میں ');
INSERT INTO "al_hind" VALUES(18,82,'18:82','اور وہ جو دیوار تھی سو دو یتیم لڑکوں کی تھی اس شہر میں اور اسکے نیچے مال گڑا تھا ان کا اور ان کا باپ تھا نیک پھر چاہا تیرے رب نے کہ وہ پہنچ جائیں اپنی جوانی کو اور نکالیں اپنامال گڑا ہوا مہربانی سے تیرے رب کی اور میں نے یہ نہیں کیا اپنے حکم سے یہ ہے پھیر ان چیزوں کا جن پر تو صبر نہ کر سکا');
INSERT INTO "al_hind" VALUES(18,83,'18:83','اور تجھ سے پوچھتے ہیں ذوالقرنین کو کہہ اب پڑھتا ہوں تمہارے آگے اس کا کچھ احوال');
INSERT INTO "al_hind" VALUES(18,84,'18:84','ہم نے اس کو جمایا تھا ملک میں اور دیا تھا ہم نے اسکو ہر چیز کا سامان ');
INSERT INTO "al_hind" VALUES(18,85,'18:85','پھر پیچھے پڑا ایک سامان کے ');
INSERT INTO "al_hind" VALUES(18,86,'18:86','یہاں تک کہ جب پہنچا سورج ڈونبے کی جگہ پایا کہ وہ ڈوبتا ہے ایک دلدل کی ندی میں اور پایا اس کے پاس لوگوں کو ہم نے کہا اے ذوالقرنین یا تو تو لوگوں کو تکلیف دے اور یا رکھ ان میں خوبی ');
INSERT INTO "al_hind" VALUES(18,87,'18:87','بولا جو کوئی ہو گا بے انصاف سو ہم اسکو سزا دیں گے پھرلوٹ جائے گا اپنے رب کے پاس وہ عذاب دے گا اس کو برا عذاب');
INSERT INTO "al_hind" VALUES(18,88,'18:88','اور جو کوئی یقین لایا اور کیا اس نے بھلا کام سو اس کا بدلہ بھلائی ہے اور ہم حکم دیں گے اس کو اپنے کام میں آسانی ');
INSERT INTO "al_hind" VALUES(18,89,'18:89','پھر لگا ایک سامان کے پیچھے ');
INSERT INTO "al_hind" VALUES(18,90,'18:90','یہاں تک کہ جب پہنچا سورج نکلنے کی جگہ پایا اس کو کہ نکلتا ہے ایک قوم پر کہ نہیں بنا دیا ہم نے انکے لئے آفتاب سے ورے کوئی حجاب ');
INSERT INTO "al_hind" VALUES(18,91,'18:91','یونہی ہے اور ہمارے قابو میں آ چکی ہے اس کے پاس کی خبر ');
INSERT INTO "al_hind" VALUES(18,92,'18:92','پھر لگا ایک سامان کے پیچھے ');
INSERT INTO "al_hind" VALUES(18,93,'18:93','یہاں تک کہ جب پہنچا دو پہاڑوں کے بیچ پائے ان سے ورے ایسے لوگ جو لگتے نہیں کہ سمجھیں ایک بات ');
INSERT INTO "al_hind" VALUES(18,94,'18:94','بولے اے ذوالقرنین یہ یاجوج و ماجوج دھوم اٹھاتے ہیں ملک میں سو تو کہے تو ہم مقرر کر دیں تیرے واسطے کچھ محصول اس شرط پر کہ بنا دےتو ہم میں ان میں ایک آڑ ');
INSERT INTO "al_hind" VALUES(18,95,'18:95','بولا جو مقدور دیا مجھ کو میرے رب نے وہ بہتر ہے سو مدد کرو میری محنت میں بنا دوں تمہارے انکے بیچ ایک دیوار موٹی ');
INSERT INTO "al_hind" VALUES(18,96,'18:96','لا دو مجھ کو تختے لوہے کے یہاں تک کہ جب برابر کر دیا دونوں پھانکوں تک پہاڑ کی کہا دھونکو یہاں تک کہ جب کر دیا اس کو آگ کہا لاؤ میرے پاس کہ ڈالوں اس پر پگھلا ہوا تانبا ');
INSERT INTO "al_hind" VALUES(18,97,'18:97','پھر نہ چڑھ سکیں اس پر اور نہ کر سکیں اس میں سوراخ ');
INSERT INTO "al_hind" VALUES(18,98,'18:98','بولا یہ ایک مہربانی ہے میرے رب کی پھر جب آئے وعدہ میرے رب کا گرا دے اس کو ڈھا کر اور ہے وعدہ میرے رب کا سچا ');
INSERT INTO "al_hind" VALUES(18,99,'18:99','اور چھوڑ دیں گے ہم خلق کو اس دن ایک دوسرے میں گھستے اور پھونک ماریں گے صور میں پھر جمع کر لائیں گے ہم ان سب کو');
INSERT INTO "al_hind" VALUES(18,100,'18:100','اور دکھلا دیں ہم دوزخ اس دن کافروں کو سامنے ');
INSERT INTO "al_hind" VALUES(18,101,'18:101','جن کی آنکھوں پر پردہ پڑا تھا میری یاد سے اور نہ سن سکتے تھے ');
INSERT INTO "al_hind" VALUES(18,102,'18:102','اب کیا سمجھتے ہیں منکر کہ ٹھہرائیں میرے بندوں کو میرے سوا حمایتی ہم نے تیار کیا ہے دوزخ کو کافروں کی مہمانی ');
INSERT INTO "al_hind" VALUES(18,103,'18:103','تو کہہ ہم بتائیں تم کو کن کا کیا ہوا گیا بہت اکارت');
INSERT INTO "al_hind" VALUES(18,104,'18:104','وہ لوگ جن کی کوشش بھٹکتی رہی دنیا کی زندگی میں اور وہ سمجھتے رہے کہ خوب بناتے ہیں کام ');
INSERT INTO "al_hind" VALUES(18,105,'18:105','وہی ہیں جو منکر ہوئے اپنے رب کی نشانیوں سے اور اسکے ملنے سے سو برباد گیا ان کا کیا ہوا پھر نہ کھڑی کریں گے ہم انکے واسطے قیامت کے دن تول ');
INSERT INTO "al_hind" VALUES(18,106,'18:106','یہ بدلہ ان کا ہے دوزخ اس پر کہ منکر ہوئے اور ٹھہرایا میری باتوں اور میرے رسولوں کو ٹھٹھا ');
INSERT INTO "al_hind" VALUES(18,107,'18:107','جو لوگ ایمان لائے ہیں اور کئے ہیں بھلے کام انکے واسطے ہے ٹھنڈی چھاؤں کے باغ مہمانی');
INSERT INTO "al_hind" VALUES(18,108,'18:108','رہا کریں ان میں نہ چاہیں وہاں سے جگہ بدلنی ');
INSERT INTO "al_hind" VALUES(18,109,'18:109','تو کہہ اگر دریا سیاہی ہو کہ لکھے میرے رب کی باتیں بیشک دریا خرچ ہو چکے ابھی نہ پوری ہوں میرے رب کی باتیں اور اگرچہ دوسرا بھی لائیں ہم ویسا ہی اسکی مدد کو ');
INSERT INTO "al_hind" VALUES(18,110,'18:110','تو کہہ میں بھی ایک آدمی ہوں جیسے تم حکم آتا ہے مجھ کو کہ معبود تمہارا ایک معبود ہے سو پھر جس کو امید ہو ملنے کی اپنے رب سے سوہ وہ کرے کچھ کام نیک اور شریک نہ کرے اپنے رب کی بندگی میں کسی کو ');
INSERT INTO "al_hind" VALUES(19,1,'19:1','کہٰیٰعص');
INSERT INTO "al_hind" VALUES(19,2,'19:2','یہ مذکور ہے تیرے رب کی رحمت کا اپنے بندہ زکریا پر ');
INSERT INTO "al_hind" VALUES(19,3,'19:3','جب پکارا اُس نے اپنے رب کو چھپی آواز سے ');
INSERT INTO "al_hind" VALUES(19,4,'19:4','بولا اے میرے رب بوڑھی ہو گئیں میری ہڈیاں اور شعلہ نکلا سر سے بڑھاپے کا اور تجھ سے مانگ کر اے رب میں کبھی محروم نہیں رہا ');
INSERT INTO "al_hind" VALUES(19,5,'19:5','اور میں ڈرتا ہوں بھائی بندوں سے اپنے پیچھے اور عورت میری بانجھ ہے سو بخش تو مجھ کو اپنے پاس سے ایک کام اٹھانے والا');
INSERT INTO "al_hind" VALUES(19,6,'19:6','جو میری جگہ بیٹھے اور یعقوب کی اولاد کی اور کر اُسکو اے رب من مانتا ');
INSERT INTO "al_hind" VALUES(19,7,'19:7','اے زکریا ہم تجھ کو خوشخبری سناتے ہیں ایک لڑکے کی جس کا نام ہے یحیٰی نہیں کیا ہم نے پہلے اس نام کا کوئی ');
INSERT INTO "al_hind" VALUES(19,8,'19:8','بولا اے رب کہاں سے ہو گا مجھ کو لڑکا اور میری عورت بانجھ ہے اور میں بوڑھا ہو گیا یہاں تک کہ اکڑ گیا ');
INSERT INTO "al_hind" VALUES(19,9,'19:9','کہا یونہی ہو گا فرما دیا تیرے رب نے وہ مجھ پر آسان ہے اور تجھ کو پیدا کیا میں نے پہلے سے اور نہ تھا تو کوئی چیز ');
INSERT INTO "al_hind" VALUES(19,10,'19:10','بولا اے رب ٹھہرا دے میرے لئے کوئی نشانی فرمایا تیری نشانی یہ کہ بات نہ کرے تو لوگوں سے تین رات تک صحیح تندرست ');
INSERT INTO "al_hind" VALUES(19,11,'19:11','پھر نکلا اپنے لوگوں کے پاس حجرہ سے تو اشارہ سے کہا اُنکو کہ یاد کرو صبح اور شام ');
INSERT INTO "al_hind" VALUES(19,12,'19:12','اے یحیٰی اٹھا لے کتاب زور سے اور دیا ہم نے اُسکو حکم کرنا لڑکا پن میں ');
INSERT INTO "al_hind" VALUES(19,13,'19:13','اور شوق دیا اپنی طرف سے اورستھرائی اور تھا پرہیز گار ');
INSERT INTO "al_hind" VALUES(19,14,'19:14','اور نیکی کرنے والا اپنے ماں باپ سے اور نہ تھا زبردست خود سر ');
INSERT INTO "al_hind" VALUES(19,15,'19:15','اور سلام ہے اُس پر جس دن پیدا ہو ا اور جس دن مرے اورجس دن اٹھ کھڑا ہو زندہ ہو کر ');
INSERT INTO "al_hind" VALUES(19,16,'19:16','اور مذکور کر کتاب میں مریم کا جب جدا ہوئی اپنے لوگوں سے ایک شرقی مکان میں ');
INSERT INTO "al_hind" VALUES(19,17,'19:17','پھر پکڑ لیا اُن سے ورے ایک پردہ پھر بھیجا ہم نے اُسکے پاس اپنا فرشتہ پھر بن کر آیا اُسکے آگے آدمی پورا ');
INSERT INTO "al_hind" VALUES(19,18,'19:18','بولی مجھ کو رحمٰن کی پناہ تجھ سے اگر ہے تو ڈر رکھنے والا ');
INSERT INTO "al_hind" VALUES(19,19,'19:19','بولا میں تو بھیجا ہوا ہوں تیرے رب کا کہ دے جاؤں تجھ کو ایک لڑکا ستھرا ');
INSERT INTO "al_hind" VALUES(19,20,'19:20','بولی کہاں سے ہو گا میرے لڑکا اور چھوا نہیں مجھ کو آدمی نے اور میں بدکار کبھی نہیں تھی ');
INSERT INTO "al_hind" VALUES(19,21,'19:21','بولا یونہی فرما دیا تیرے رب نے وہ مجھ پر آسان ہے اور اُسکو ہم کیا چاہتے ہیں لوگوں کیلئے نشانی اور مہربانی اپنی طرف سے اور ہے یہ کام مقرر ہو چکا ');
INSERT INTO "al_hind" VALUES(19,22,'19:22','پھر پیٹ میں لیا اُسکو پھر یکسو ہوئی اُسکو لیکر ایک بعید مکان میں ');
INSERT INTO "al_hind" VALUES(19,23,'19:23','پھر لے آیا اُسکو دردزہ ایک کھجور کی جڑ میں بولی کسی طرح میں مر چکتی اس سے پہلے اور ہو جاتی بھولی بسری ');
INSERT INTO "al_hind" VALUES(19,24,'19:24','پس آواز دی اسکو اُسکے نیچے سے کہ غمگین مت ہو کر دیا تیرے رب نے تیرے نیچے ایک چشمہ');
INSERT INTO "al_hind" VALUES(19,25,'19:25','اور ہلا اپنی طرف کھجور کی جڑ اُس سے گریں گی تجھ پر پکی کھجوریں ');
INSERT INTO "al_hind" VALUES(19,26,'19:26','اب کھا اور پی اور آنکھ ٹھنڈی رکھ پھر اگر تو دیکھے کوئی آدمی تو کہیو میں نے مانا ہے رحمٰن کا روزہ سو بات نہ کروں گی آج کسی آدمی سے ');
INSERT INTO "al_hind" VALUES(19,27,'19:27','پھر لائی اسکو اپنے لوگوں کے پاس گود میں وہ اُسکو کہنے لگے اے مریم تو نے کی یہ چیز طوفان کی ');
INSERT INTO "al_hind" VALUES(19,28,'19:28','اے بہن ہارون کی نہ تھا تیرا باپ برا آدمی اور نہ تھی تیری ماں بدکار ');
INSERT INTO "al_hind" VALUES(19,29,'19:29','پھر ہاتھ سے بتلایا اُس لڑکے کو بولے ہم کیونکر بات کریں اُس شخص سے کہ وہ ہے گود میں لڑکا ');
INSERT INTO "al_hind" VALUES(19,30,'19:30','وہ بولا میں بندہ ہوں اللہ کا مجھ کو اُس نے کتاب دی ہے اور مجھ کو اُس نے نبی کیا ');
INSERT INTO "al_hind" VALUES(19,31,'19:31','اور بنایا مجھ کو برکت والا جس جگہ میں ہوں اور تاکید کی مجھ کو نماز کی اور زکوٰۃ کی جب تک میں رہوں زندہ ');
INSERT INTO "al_hind" VALUES(19,32,'19:32','اورسلوک کرنے والا اپنی ماں سے اور نہیں بنایا مجھ کو زبردست بدبخت ');
INSERT INTO "al_hind" VALUES(19,33,'19:33','اور سلام ہے مجھ پر جس دن میں پیدا ہوا اور جس دن مروں اور جس دن اٹھ کھڑا ہوں زندہ ہو کر ');
INSERT INTO "al_hind" VALUES(19,34,'19:34','یہ ہے عیسٰی مریم کا بیٹا سچی بات جس میں لوگ جھگڑتے ہیں ');
INSERT INTO "al_hind" VALUES(19,35,'19:35','اللہ ایسا نہیں کہ رکھے اولاد وہ پاک ذات ہے جب ٹھہرا لیتا ہے کسی کام کا کرنا سو یہی کہتا ہے اسکو کہ ہو وہ ہو جاتا ہے ');
INSERT INTO "al_hind" VALUES(19,36,'19:36','اور کہا بیشک اللہ ہے رب میرا اور رب تمہارا سو اُسکی بندگی کرو یہ ہے راہ سیدھی');
INSERT INTO "al_hind" VALUES(19,37,'19:37','پھر جُدی جُدی راہ اختیار کی فرقوں نے اُن میں سے سو خرابی ہے منکروں کو جس وقت دیکھیں گے ایک دن بڑا ');
INSERT INTO "al_hind" VALUES(19,38,'19:38','کیا خوب سنتے اور دیکھتے ہونگے جس دن آئیں گے ہمارے پاس پر بے انصاف آج کے دن صریح بہک رہے ہیں ');
INSERT INTO "al_hind" VALUES(19,39,'19:39','اور ڈر سنا دے اُنکو اُس پچتاوے کے دن کا جب فیصل ہو چکے گا کام اور وہ بھول رہے ہیں اور وہ یقین نہیں لاتے ');
INSERT INTO "al_hind" VALUES(19,40,'19:40','ہم وارث ہوں گے زمین کے اور جو کوئی ہے زمین پر اور وہ ہماری طرف پھر آئیں گے ');
INSERT INTO "al_hind" VALUES(19,41,'19:41','اور مذکور کر کتاب میں ابراہیم کا بیشک تھا وہ سچا نبی ');
INSERT INTO "al_hind" VALUES(19,42,'19:42','جب کہا اپنے باپ کو اے باپ میرے کیوں پوجتا ہے اسکو جو نہ سنے اور نہ دیکھے اور نہ کام آئے تیرے کچھ ');
INSERT INTO "al_hind" VALUES(19,43,'19:43','اے باپ میرے مجھ کو آئی ہے خبر ایک چیز کی جو تجھ کو نہیں آئی سو میری راہ چل دکھلا دوں تجھ کو راہ سیدھی ');
INSERT INTO "al_hind" VALUES(19,44,'19:44','اے باپ میرے مت پوج شیطان کو بیشک شیطان ہے رحمٰن کا نافرمان ');
INSERT INTO "al_hind" VALUES(19,45,'19:45','اے باپ میرے میں ڈرتا ہوں کہیں آ لگے تجھ کو ایک آفت رحمٰن سے پھر تو ہو جائے شیطان کا ساتھی ');
INSERT INTO "al_hind" VALUES(19,46,'19:46','وہ بولا کیا تو پھرا ہوا ہے میرے ٹھاکروں سے اے ابراہیم اگر تو باز نہ آئے گا تو تجھ کو سنگسار کروں گا اور دور ہو جا میرے پاس سے ایک مدت ');
INSERT INTO "al_hind" VALUES(19,47,'19:47','کہا تیری سلامتی رہے میں گناہ بخشواؤں گا تیرا اپنے رب سے بیشک وہ ہے مجھ پر مہربان ');
INSERT INTO "al_hind" VALUES(19,48,'19:48','اور چھوڑتا ہوں تم کو اور جنکو تم پوجتے ہو اللہ کے سوا اور میں بندگی کروں گا اپنے ر ب کی امید ہے کہ نہ رہوں گا اپنے رب کی بندگی کر کر محروم ');
INSERT INTO "al_hind" VALUES(19,49,'19:49','پھر جب جُدا ہوا اُن سے اور جنکو وہ پوجتے تھے اللہ کے سوا بخشا ہم نے اُسکو اسحٰق او ریعقوب اور دونوں کو نبی کیا ');
INSERT INTO "al_hind" VALUES(19,50,'19:50','اور دیا ہم نے انکو اپنی رحمت سے اور کہا اُنکے واسطے سچا بول اونچا ');
INSERT INTO "al_hind" VALUES(19,51,'19:51','اور مذکور کر کتاب میں موسٰی کا بیشک وہ تھا چنا ہوا اور تھا رسول نبی ');
INSERT INTO "al_hind" VALUES(19,52,'19:52','اور پکارا ہم نے اُسکو داہنی طرف سے طور پہاڑ کی اور نزدیک بلایا اُسکو بھید کہنے کو ');
INSERT INTO "al_hind" VALUES(19,53,'19:53','اور بخشا ہم نے اُسکو اپنی مہربانی سے بھائی اُس کا ہارون نبی ');
INSERT INTO "al_hind" VALUES(19,54,'19:54','اور مذکور کر کتاب میں اسمٰعیل کا وہ تھا وعدہ کا سچا اور تھا رسول نبی ');
INSERT INTO "al_hind" VALUES(19,55,'19:55','اور حکم کرتا تھا اپنے گھر والوں کو نماز کا اور زکوٰۃ کا اور تھا اپنے رب کے یہاں پسندیدہ ');
INSERT INTO "al_hind" VALUES(19,56,'19:56','اور مذکور کر کتاب میں ادریس کا وہ تھا سچا نبی ');
INSERT INTO "al_hind" VALUES(19,57,'19:57','اور اٹھا لیا ہم نے اسکو ایک اونچے مکان پر ');
INSERT INTO "al_hind" VALUES(19,58,'19:58','یہ وہ لوگ ہیں جن پر انعام کیااللہ نے پیغمبروں میں آدم کی اولاد میں اور اُن میں جنکو سوار کر لیا ہم نے نوح کے ساتھ اور ابراہیم کی اولاد میں اور اسرائیل کی اور اُن میں جنکو ہم نے ہدایت کی اور پسند کیا جب اُنکو سنائے آیتیں رحمٰن کی گرتے ہیں سجدہ میں اور روتے ہوئے ');
INSERT INTO "al_hind" VALUES(19,59,'19:59','پھر اُنکی جلگہ آئے ناخلف کھو بیٹھے نماز اور پیچھے پڑ گئے مزوں کے سو آگے دیکھ لیں گے گمراہی کو ');
INSERT INTO "al_hind" VALUES(19,60,'19:60','مگر جس نے توبہ کی اور یقین لایا اور کی نیکی سو وہ لوگ جائیں گے بہشت میں اور اُن کا حق ضائع نہ ہو گا کچھ ');
INSERT INTO "al_hind" VALUES(19,61,'19:61','باغوں میں بسنے کے جن کا وعدہ کیا ہے رحمٰن نے اپنے بندوں سے اُنکے بن دیکھے بیشک ہے اُس کے وعدہ پر پہنچنا ');
INSERT INTO "al_hind" VALUES(19,62,'19:62','نہ سنیں گے وہاں بک بک سوائے سلام اور اُنکے لئے ہے اُنکی روزی وہاں صبح اور شام ');
INSERT INTO "al_hind" VALUES(19,63,'19:63','یہ وہ بہشت ہے جو میراث دینگے ہم اپنے بندوں میں جو کوئی ہو گا پرہیزگار ');
INSERT INTO "al_hind" VALUES(19,64,'19:64','اور ہم نہیں اترتے مگر حکم سے تیرے رب کے اُسی کا ہے جو ہمارے آگے ہے اور جو ہمارے پیچھے اور جو اسکے بیچ میں ہے اور تیرا رب نہیں ہے بھولنے والا ');
INSERT INTO "al_hind" VALUES(19,65,'19:65','رب آسمانوں کا اور زمین کا اور جو اُنکے بیچ میں ہے سو اُسی کی بندگی کر اور قائم رہ اسکی بندگی پر کسی کو پہچانتا ہے تو اسکے نام کا ');
INSERT INTO "al_hind" VALUES(19,66,'19:66','اور کہتا ہے آدمی کیا جب میں مر جاؤں تو پھر نکلوں گا زندہ ہو کر ');
INSERT INTO "al_hind" VALUES(19,67,'19:67','کیا یاد نہیں رکھتا آدمی کہ ہم نے اسکو بنایا پہلے سے اور وہ کچھ چیز نہ تھا ');
INSERT INTO "al_hind" VALUES(19,68,'19:68','سو قسم ہے تیرے رب کی ہم گھیر بلائیں گے انکو اور شیطانوں کو پھر سامنے لائیں گے گرد دوزخ کے گھٹنوں پر گرے ہوئے ');
INSERT INTO "al_hind" VALUES(19,69,'19:69','پھر جدا کر لیں گے ہم ہر ایک فرقہ میں سے جو نسا اُن میں سے سخت رکھتا تھا رحمٰن سے اکڑ');
INSERT INTO "al_hind" VALUES(19,70,'19:70','پھر ہم کو خوب معلوم ہیں جو بہت قابل ہیں اُس میں داخل ہونے کے ');
INSERT INTO "al_hind" VALUES(19,71,'19:71','اور کوئی نہیں تم میں جو نہ پہنچے گا اس پر ہو چکا یہ وعدہ تیرے رب پر لازم مقرر');
INSERT INTO "al_hind" VALUES(19,72,'19:72','پھر بچائیں گے ہم انکو جو ڈرتے رہے اور چھوڑ دیں گے گنہگاروں کو اس میں اوندھے گرے ہوئے ');
INSERT INTO "al_hind" VALUES(19,73,'19:73','اور جب سنائے انکو ہماری آیتیں کھلی ہوئی کہتے ہیں جو لوگ کہ منکر ہیں ایمان والوں کو دونوں فرقوں میں کس کا مکان بہتر ہے اور کس کی اچھی لگی ہے مجلس ');
INSERT INTO "al_hind" VALUES(19,74,'19:74','اورکتنی ہلاک کر چکے ہیں ہم پہلے اُن سے جماعتیں وہ اُن سے بہتر تھے سامان میں اور نمود میں ');
INSERT INTO "al_hind" VALUES(19,75,'19:75','تو کہہ جو رہا بھٹکتا سو چاہئے اُسکو کھینچ لیجائے رحمٰن لنبا یہاں تک کہ جب دیکھیں گے جو وعدہ ہوا تھا اُن سے یا آفت اور یا قیامت سو تب معلوم کر لیں گے کس کا برا ہے مکان اور کس کی فوج کمزور ہے ');
INSERT INTO "al_hind" VALUES(19,76,'19:76','اور بڑھاتا جاتا ہے اللہ سوجھنے والوں کو سوجھ اور باقی رہنے والی نیکیاں بہتر رکھتی ہیں تیرے رب کے یہاں بدلہ اور بہتر پھر جانے کو جگہ ');
INSERT INTO "al_hind" VALUES(19,77,'19:77','بھلا تو نے دیکھا اُسکو جو منکر ہوا ہماری آیتوں سے اور کہا مجھ کو مل کر رہے گا مال اور اولاد ');
INSERT INTO "al_hind" VALUES(19,78,'19:78','کیا جھانک آیا ہے غیب کو یا لے رکھا ہے رحمٰن سے عہد ');
INSERT INTO "al_hind" VALUES(19,79,'19:79','یہ نہیں ہم لکھ رکھیں گے جو وہ کہتا ہے اور بڑھاتے جائیں گے اسکو عذاب میں لنبا ');
INSERT INTO "al_hind" VALUES(19,80,'19:80','اور ہم لے لیں گے اُسکے مرنے پر جو کچھ وہ بتلا رہا ہے اور آئے گا ہمارے پاس اکیلا ');
INSERT INTO "al_hind" VALUES(19,81,'19:81','اور پکڑ رکھا ہے لوگوں نے اللہ کے سوا اوروں کو معبود تاکہ وہ ہوں اُنکے لئے مدد ہرگز نہیں ');
INSERT INTO "al_hind" VALUES(19,82,'19:82','وہ منکر ہونگے اُنکی بندگی سے اورہو جائیں گے اُنکے مخالف ');
INSERT INTO "al_hind" VALUES(19,83,'19:83','تو نے نہیں دیکھا کہ ہم نے چھوڑ رکھے ہیں شیطان منکروں پر اچھالتے ہیں انکو ابھار کر');
INSERT INTO "al_hind" VALUES(19,84,'19:84','سو تو جلدی نہ کر ان پرہم تو پوری کرتے ہیں انکی گنتی ');
INSERT INTO "al_hind" VALUES(19,85,'19:85','جس دن ہم اکٹھا کرلائیں گے پرہیزگاروں کو رحمٰن کے پاس مہمان بلائے ہوئے');
INSERT INTO "al_hind" VALUES(19,86,'19:86','اور ہانک لیجائیں گے گنہگاروں کو دوزذخ کی طرف پیاس سے ');
INSERT INTO "al_hind" VALUES(19,87,'19:87','نہیں اختیار رکھتے لوگ سفارش کا مگر جس نے لے لیا ہے رحمٰن سے وعدہ ');
INSERT INTO "al_hind" VALUES(19,88,'19:88','اور لوگ کہتے ہیں رحمٰن رکھتا ہے اولاد ');
INSERT INTO "al_hind" VALUES(19,89,'19:89','بیشک تم آ پھنسے ہو بھاری چیز میں');
INSERT INTO "al_hind" VALUES(19,90,'19:90','ابھی آسمان پھٹ پڑیں اس بات سے اور ٹکڑے ہو زمیں اور گر پڑیں پہاڑ ڈھے کر');
INSERT INTO "al_hind" VALUES(19,91,'19:91','اس پر کہ پکارتے ہیں رحمٰن کے نام پر اولاد ');
INSERT INTO "al_hind" VALUES(19,92,'19:92','اور نہیں پھبتا رحمٰن کو کہ رکھے اولاد ');
INSERT INTO "al_hind" VALUES(19,93,'19:93','کوئی نہیں آسمان اور زمین میں جو نہ آئے رحمٰن کا بندہ ہو کر ');
INSERT INTO "al_hind" VALUES(19,94,'19:94','اُسکے پاس اُنکی شمار ہے اور گن رکھی ہے انکی گنتی');
