INSERT INTO "al_hind" VALUES(19,97,'19:97','سو ہم نے آسان کر دیا یہ قرآن تیری زبان میں اسی واسطے کہ خوشخبری سنا دے تو ڈرنے والوں کو اور ڈرا دے جھگڑالو لوگوں کو ');
