INSERT INTO "al_hind" VALUES(20,10,'20:10','جب اُس نے دیکھی ایک آگ تو کہا اپنے گھر والوں کو ٹھہرو میں نے دیکھی ہے ایک آگ شاید لے آؤں تمہارے پاس اُس میں سے سلگا کر یا پاؤں آگ پر پہنچ کر رستہ کا پتہ');
