INSERT INTO "al_hind" VALUES(20,12,'20:12','میں ہوں تیرا رب سو اتار ڈال اپنی جوتیاں تو ہے پاک میدان طویٰ میں ');
INSERT INTO "al_hind" VALUES(20,13,'20:13','اور میں نے تجھ کو پسند کیا ہے سو تو سنتا رہ جو حکم ہو ');
INSERT INTO "al_hind" VALUES(20,14,'20:14','میں جو ہوں اللہ ہوں کسی کی بندگی نہیں سوا میرے سو میری بندگی کر اور نماز قائم رکھ میری یادگاری کو ');
INSERT INTO "al_hind" VALUES(20,15,'20:15','قیامت بیشک آنے والی ہے میں مخفی رکھنا چاہتا ہوں اُسکو تاکہ بدلہ ملے ہر شخص کو جو اُس نے کمایا ہے ');
INSERT INTO "al_hind" VALUES(20,16,'20:16','سو کہیں تجھ کو نہ روک دے اُس سے وہ شخص جو یقین نہیں رکھتا اُس کا اور پیچھے پڑ رہا ہے اپنے مزوں کے پھر تو بھی پٹکا جائے ');
