INSERT INTO "al_hind" VALUES(20,20,'20:20','تو اُسکو ڈال دیا پھر اسی وقت وہ تو سانپ ہو گیا دوڑتا ہوا ');
INSERT INTO "al_hind" VALUES(20,21,'20:21','فرمایا پکڑ لے اُسکو اور مت ڈر ہم ابھی پھیر دیں گے اُسکو پہلی حالت پر ');
INSERT INTO "al_hind" VALUES(20,22,'20:22','اور ملا لے اپنا ہاتھ اپنی بغل سےکہ نکلے سفید ہو کر بلا عیب یہ نشانی دوسری');
