INSERT INTO "al_hind" VALUES(20,36,'20:36','فرمایا ملا تجھ کو تیرا سوال اے موسٰی ');
INSERT INTO "al_hind" VALUES(20,37,'20:37','اور احسان کیا تھا ہم نے تجھ پر ایک بار اور بھی ');
INSERT INTO "al_hind" VALUES(20,38,'20:38','جب حکم بھیجا ہم نے تیری ماں کو جو آگے سناتے ہیں ');
INSERT INTO "al_hind" VALUES(20,39,'20:39','کہ ڈال اُسکو صندوق میں پھر اُسکو ڈال دے دریا میں پھر دریا اُسکو لے ڈالے کنارے پر اٹھا لے اُسکو ایک دشمن میرا اور اُس کا اور ڈال دی میں نے تجھ پر محبت اپنی طرف سے اور تاکہ پرورش پائے تو میری آنکھ کے سامنے ');
INSERT INTO "al_hind" VALUES(20,40,'20:40','جب چلنے لگی تیری بہن اور کہنے لگی میں بتاؤں تمکو ایسا شخص جو اُسکو پالے پھر پہنچا دیا ہم نے تجھ کو تیری ماں کے پاس کہ ٹھنڈی رہے اُسکی آنکھ اور غم نہ کھائے اور تو نے مار ڈالا ایک شخص کو پھر بچا دیا ہم نے تجھ کو اُس غم سے اور جانچا ہم نے تجھ کو ایک ذرا جانچنا پھر ٹھہرا رہا تو کئ برس مدین والوں میں پھر آیا تو تقدیر سے اے موسٰی');
