INSERT INTO "al_hind" VALUES(20,46,'20:46','فرمایا نہ ڈرو میں ساتھ ہوں تمہارے سنتا ہوں اور دیکھتا ہوں ');
INSERT INTO "al_hind" VALUES(20,47,'20:47','سو جاؤ اُسکے پاس اور کہو ہم دونوں بھیجے ہوئے ہیں تیرے رب کے سو بھیجدے ہمارے ساتھ بنی اسرائیل کو اور مت ستا اُنکو ہم آئے ہیں تیرے پاس نشانی لیکر تیرے رب کی اور سلامتی ہو اُسکی جو مان لے راہ کی بات');
INSERT INTO "al_hind" VALUES(20,48,'20:48','ہم کو حکم ملا ہے کہ عذاب اُس پر ہے جو جھٹلائے اور منہ پھیر لے ');
INSERT INTO "al_hind" VALUES(20,49,'20:49','بولا پھر کون ہے رب تم دونوں کا اے موسٰی ');
INSERT INTO "al_hind" VALUES(20,50,'20:50','کہا رب ہمارا وہ ہے جس نے دی ہر چیز کو اُسکی صورت پھر راہ سجھائی ');
INSERT INTO "al_hind" VALUES(20,51,'20:51','بولا پھر کیا حقیقت ہے اُن پہلی جماعتوں کی');
INSERT INTO "al_hind" VALUES(20,52,'20:52','کہا اُنکی خبر میرے رب کے پاس لکھی ہوئی ہے نہ بہکتا ہے میرا رب اور نہ بھولتا ہے ');
INSERT INTO "al_hind" VALUES(20,53,'20:53','وہ ہے جس نے بنا دیا تمہارے واسطے زمین کو بچھونا اور چلائیں تمہارے لئے اُس میں راہیں اور اتارا آسمان سے پانی پھر نکالی ہم نے اُس سے طرح طرح کی سبزی ');
INSERT INTO "al_hind" VALUES(20,54,'20:54','کھاؤ اور چراؤ اپنے چوپایوں کو البتہ اُس میں نشانیاں ہیں عقل رکھنے والوں کو ');
INSERT INTO "al_hind" VALUES(20,55,'20:55','اسی زمین میں ہم نے تمکو بنایا اور اسی میں تمکو پھر پہنچا دیتے ہیں اور اسی سے نکالیں گے تمکو دوسری بار ');
INSERT INTO "al_hind" VALUES(20,56,'20:56','اور ہم نے فرعون کو دکھلا دیں اپنی سب نشانیاں پھر اُس نے جھٹلایا اور نہ مانا ');
INSERT INTO "al_hind" VALUES(20,57,'20:57','بولا کیا تو آیا ہے ہم کو نکالنے ہمارے ملک سے اپنے جادو کے زور سے اے موسٰی ');
INSERT INTO "al_hind" VALUES(20,58,'20:58','سو ہم بھی لائیں گے تیرے مقابلہ میں ایک ایسا ہی جادو سو ٹھہرا لے ہمارے اور اپنے بیچ میں ایک وعدہ نہ ہم خلاف کریں اُس کا اور نہ تو ایک میدان صاف میں ');
INSERT INTO "al_hind" VALUES(20,59,'20:59','کہا وعدہ تمہارا ہے جشن کا دن اور یہ کہ جمع ہوں لوگ دن چڑھے ');
INSERT INTO "al_hind" VALUES(20,60,'20:60','پھر اُلٹا پھرا فرعون پھر جمع کئے اپنے سارے داؤ پھر آیا ');
INSERT INTO "al_hind" VALUES(20,61,'20:61','کہا اُنکو موسٰی نے کم بختی تمہاری جھوٹ نہ بولو اللہ پر پھر غارت کر دے تمکو کسی سخت آفت سے اور مراد کو نہیں پہنچا جس نے جھوٹ باندھا ');
INSERT INTO "al_hind" VALUES(20,62,'20:62','پھر جھگڑے اپنے کا م پر آپس میں اور چھپ کر کیا مشورہ ');
INSERT INTO "al_hind" VALUES(20,63,'20:63','بولے مقرر یہ دونوں جادوگر ہیں چاہتے ہیں کہ نکال دیں تمکو تمہارے ملک سے اپنے جادو کے زور سے اور موقوف کرا دیں تمہارے اچھے خاصے چلن کو ');
INSERT INTO "al_hind" VALUES(20,64,'20:64','سو مقرر کر لو اپنی تدبیر پھر آؤ قطار باندھ کر اور جیت گیا آج جو غالب رہا ');
INSERT INTO "al_hind" VALUES(20,65,'20:65','بولے اے موسٰی یا تو تو ڈال اور یا ہم ہوں پہلے ڈالنے والے');
INSERT INTO "al_hind" VALUES(20,66,'20:66','کہا نہیں تم ڈالو پھر تبھی اُنکی رسیاں اور لاٹھیاں اُسکے خیال میں آئیں اُنکے جادو سے کہ دوڑ رہی ہیں ');
INSERT INTO "al_hind" VALUES(20,67,'20:67','پھر پانے لگا اپنے جی میں ڈر موسٰی ]');
INSERT INTO "al_hind" VALUES(20,68,'20:68','ہم نے کہا تو مت ڈر مقرر تو ہی رہے گا غالب ');
INSERT INTO "al_hind" VALUES(20,69,'20:69','اور ڈال جو تیرے داہنے ہاتھ میں ہے کہ نگل جائے جو کچھ اُنہوں نے بنایا اُن کا بنایا ہوا فریب ہے جادوگر کا اور بھلا نہیں ہوتا جادوگر کا جہاں ہو ');
INSERT INTO "al_hind" VALUES(20,70,'20:70','پھر گر پڑے جادوگر سجدہ میں بولے ہم یقین لائے رب پر ہارون اور موسٰی کے ');
INSERT INTO "al_hind" VALUES(20,71,'20:71','بولا فرعون تم نے اُسکو مان لیا میں نے ابھی حکم نہ دیا تھا وہی تمہارا بڑا ہے جس نے سکھلایا تمکو جادو سو اب میں کٹواؤں گا تمہارے ہاتھ اور دوسری طرف کے پاؤں اور سولی دوں گا تمکو کھجور کے تنا پر اور جان لو گے ہم میں کس کا عذاب سخت ہے اور دیر تک رہنے والا ');
INSERT INTO "al_hind" VALUES(20,72,'20:72','وہ بولے ہم تجھ کو زیادہ نہ سمجھیں گے اُس چیز سے جو پہنچی ہم کو صاف دلیل اور اُس سے جس نے ہم کو پیدا کیا سو تو کر گذر جو تجھ کو کرنا ہے تو یہی کرے گا اس دنیا کی زندگی میں');
INSERT INTO "al_hind" VALUES(20,73,'20:73','ہم یقین لائے ہیں اپنے رب پر تاکہ بخشے ہم کو ہمارے گناہ اور جو تو نے زبردستی کروایا ہم سے یہ جادو اور اللہ بہتر ہے اور سدا باقی رہنے والا ');
INSERT INTO "al_hind" VALUES(20,74,'20:74','بات یہی ہے کہ جو کوئی آیا رب کے پاس گناہ لیکر سو اُس کے واسطے دوزخ ہے نہ مرے اُس میں نہ جئے ');
INSERT INTO "al_hind" VALUES(20,75,'20:75','اور جو آیا اُسکے پاس ایمان لیکر نیکیاں کر کر سو اُن لوگوں کیلئے ہیں درجے بلند');
INSERT INTO "al_hind" VALUES(20,76,'20:76','باغ ہیں بسنے کے بہتی ہیں اُنکے نیچے سے نہریں ہمیشہ رہا کریں گے اُن میں اور یہ بدلہ ہے اُس کا جو پاک ہوا ');
INSERT INTO "al_hind" VALUES(20,77,'20:77','اور ہم نے حکم بھیجا موسٰی کو کہ لے نکل میرےبندوں کو رات سے پھر ڈال دے اُنکے لئے سمندر میں رستہ سوکھا نہ خطرہ کر آ پکڑنے کا اور نہ ڈر ڈوبنے سے');
INSERT INTO "al_hind" VALUES(20,78,'20:78','پھر پیھچا کیا اُن کا فرعون نے اپنے لشکروں کو لیکر پھر ڈھانپ لیا اُنکو پانی نے جیسا کہ ڈھانپ لیا ');
INSERT INTO "al_hind" VALUES(20,79,'20:79','اور بہکایا فرعون نے اپنی قوم کو اور نہ سمجھایا ');
INSERT INTO "al_hind" VALUES(20,80,'20:80','اے اولاد اسرائیل چھڑا لیا ہم نے تمکو تمہارے دشمن سے اور وعدہ ٹھہرا یا تم سے داہنی طرف پہاڑ کی اور اتارا تم پر من اور سلویٰ');
INSERT INTO "al_hind" VALUES(20,81,'20:81','کھاؤ ستھری چیزیں جو روزی دی ہم نے تمکو اور نہ کرو اُس میں زیادتی پھر تو اترے گا تم پر میرا غصہ اور جس پر اترا میرا غصہ سو وہ پٹکا گیا ');
INSERT INTO "al_hind" VALUES(20,82,'20:82','اور میری بڑی بخشش ہے اس پر جو توبہ کرے اور یقین لائے اور کرے بھلا کام پھر راہ پر رہے ');
INSERT INTO "al_hind" VALUES(20,83,'20:83','اور کیوں جلدی کی تونے اپنی قوم سے اے موسٰی');
INSERT INTO "al_hind" VALUES(20,84,'20:84','بولا وہ یہ آ رہے ہیں میرے پیچھے اور میں جلدی آیا تیری طرف اے میرے رب تاکہ تو راضی ہو ');
INSERT INTO "al_hind" VALUES(20,85,'20:85','فرمایا ہم نے بچلا دیا تیری قوم کو تیرے پیچھے اور بہکایا اُنکو سامری نے ');
INSERT INTO "al_hind" VALUES(20,86,'20:86','پھر الٹا پھرا موسٰی اپنی قوم کے پاس غصہ میں بھرا پچتاتا ہوا کہا اے قوم کیا تم سے وعدہ نہ کیا تھا تمہارے رب نے اچھا وعدہ کیا طویل ہو گئ تم پر مدت یا چاہا تم نے کہ اترے تم پر غضب تمہارے رب کا اس لئے خلاف کیا تم نے میرا وعدہ ');
INSERT INTO "al_hind" VALUES(20,87,'20:87','بولے ہم نے خلاف نہیں کیا تیرا وعدہ اپنے اختیار سے و لیکن اٹھوایا ہم سے بھاری بوجھ قوم فرعون کے زیور کا سو ہم نے اُسکو پھینک دیا پھر اس طرح ڈھالا سامری نے ');
INSERT INTO "al_hind" VALUES(20,88,'20:88','پھر بنا نکالا اُنکے واسطے ایک بچھڑا ایک دھڑ جسمیں آواز گائے کی پھر کہنے لگے یہ معبود ہے تمہارا اور معبود ہے موسٰی کا سو وہ بھول گیا ');
INSERT INTO "al_hind" VALUES(20,89,'20:89','بھلا یہ لوگ نہیں دیکھتے کہ وہ جواب تک نہیں دیتا اُنکو کسی بات کا اور اختیار نہیں رکھتا اُنکے برے کا اور نہ بھلے کا ');
INSERT INTO "al_hind" VALUES(20,90,'20:90','اور کہا تھا اُنکو ہارون نے پہلے سے اے قوم بات یہی ہے کہ تم بہک گئے اس بچھڑے سے اور تمہارا رب تو رحمٰن ہے سو میری راہ چلو اور مانو بات میری ');
INSERT INTO "al_hind" VALUES(20,91,'20:91','بولے ہم برابر اسی پر لگے بیٹھے رہیں گے جب تک لوٹ کر نہ آئے ہمارے پاس موسٰی ');
INSERT INTO "al_hind" VALUES(20,92,'20:92','کہا موسٰی نے اے ہارون کس چیز نے روکا تجھ کو جب دیکھا تھا تو نے کہ وہ بہک گئے');
INSERT INTO "al_hind" VALUES(20,93,'20:93','کہ تو میرےپیچھے نہ آیا کیا تو نے رد کیا میرا حکم ');
INSERT INTO "al_hind" VALUES(20,94,'20:94','وہ بولا اے میری ماں کے جنے نہ پکڑ میری داڑھی اور نہ سر میں ڈرا کہ تو کہے گا پھوٹ ڈالدی تو نے بنی اسرائیل میں اور یاد نہ رکھی میری بات ');
INSERT INTO "al_hind" VALUES(20,95,'20:95','کہا موسٰی نے اب تیری کیا حقیقت ہے اے سامری ');
INSERT INTO "al_hind" VALUES(20,96,'20:96','بولا میں نے دیکھ لیا جو اوروں نے نہ دیکھا پھر بھر لی میں نے ایک مٹھی پاؤں کے نیچے سے اُس بھیجے ہوئے کے پھر میں نے وہی ڈالدی اور یہی صلاح دی مجھ کو میرے جی نے ');
INSERT INTO "al_hind" VALUES(20,97,'20:97','کہا موسٰی نے دور ہو تیرے لئے زندگی بھر تو اتنی سزا ہے کہ کہا کرے مت چھیڑو اور تیرے واسطے ایک وعدہ ہے وہ ہرگز تجھ سے خلاف نہ ہو گا اور دیکھ اپنے معبود کو جس پر تمام دن تو معتکف رہتا تھا ہم اسکو جلا دیں گے پھر بکھیر دیں گے دریا میں اڑا کر ');
INSERT INTO "al_hind" VALUES(20,98,'20:98','تمہارا معبود تو وہی اللہ ہے جس کے سوا کسی کی بندگی نہیں سب چیز سما گئ ہے اُسکے علم میں ');
INSERT INTO "al_hind" VALUES(20,99,'20:99','یوں سناتےہیں ہم تجھ کو اُنکے احوال جو پہلے گذر چکے اور ہم نے دی تجھ کو اپنے پاس سے پڑھنے کی کتاب ');
INSERT INTO "al_hind" VALUES(20,100,'20:100','جو کوئی منہ پھیر لے اُس سے وہ اٹھائے گا دن قیامت کے ایک بوجھ');
INSERT INTO "al_hind" VALUES(20,101,'20:101','سدا رہیں گے اس میں اور برا ہے اُن پر قیامت میں وہ بوجھ اٹھانے کا ');
INSERT INTO "al_hind" VALUES(20,102,'20:102','جس دن پھونکیں گے صور میں اور گھیر لائیں گے ہم گناہگاروں کو اُس دن نیلی آنکھیں ');
INSERT INTO "al_hind" VALUES(20,103,'20:103','چپکے چپکے کہتے ہونگے آپس میں تم نہیں رہے مگر دس دن ');
INSERT INTO "al_hind" VALUES(20,104,'20:104','ہم کو خوب معلوم ہے جو کچھ کہتے ہیں جب بولے گا اُن میں اچھی راہ روشن والا تم نہیں رہے مگر ایک دن ');
INSERT INTO "al_hind" VALUES(20,105,'20:105','اور تجھ سے پوچھتے ہیں پہاڑوں کا حال سو تو کہہ اُنکو بکھیر دے گا میرا رب اڑا کر');
INSERT INTO "al_hind" VALUES(20,106,'20:106','پھر کر چھوڑے گا زمین کو صاف میدان');
INSERT INTO "al_hind" VALUES(20,107,'20:107','نہ دیکھے تو اُس میں موڑ اور نہ ٹیلا ');
INSERT INTO "al_hind" VALUES(20,108,'20:108','اُس دن پیچھے دوڑیں گے پکارنے والے کے ٹیڑھی نہیں جسکی بات اور دب جائیں گی آوازیں رحمٰن کے ڈر سے پھر تو نہ سنے گا مگر گھس گھسی آواز ');
INSERT INTO "al_hind" VALUES(20,109,'20:109','اُس دن کام نہ آئے گی سفارش مگر جسکو اجازت رحمٰن نے دی اور پسند کی اسکی بات ');
INSERT INTO "al_hind" VALUES(20,110,'20:110','وہ جانتا ہے جو کچھ ہے اُنکے آگے اور پیچھے اور یہ قابو میں نہیں لا سکتے اُسکو دریافت کر کر ');
INSERT INTO "al_hind" VALUES(20,111,'20:111','اور رگڑتے ہیں منہ آگے اس جیتے ہمیشہ رہنے والے کے اور خراب ہوا جس نے بوجھ اٹھایا ظلم کا ');
INSERT INTO "al_hind" VALUES(20,112,'20:112','اور جو کوئی کرے کچھ بھلائیاں اور وہ ایمان بھی رکھتا ہو سو اُسکو ڈر نہیں بے انصافی کا اور نہ نقصان پہنچنے کا ');
INSERT INTO "al_hind" VALUES(20,113,'20:113','اور اسی طرح اتارا ہم نے قرآن عربی زبان کا اور پھیر پھیر کر سنائی ہم نے اُس میں ڈرانے کی باتیں تاکہ وہ پرہیز کریں یا ڈالے اُنکے دل میں سوچ ');
INSERT INTO "al_hind" VALUES(20,114,'20:114','سو بلند درجہ اللہ کا اُس سچے بادشاہ کا اور تو جلدی نہ کر قرآن کے لینے میں جب تک کہ پورا نہ ہو چکے اُس کا اترنا اور کہہ اے رب زیادہ کر میری سمجھ ');
INSERT INTO "al_hind" VALUES(20,115,'20:115','اور ہم نے تاکید کر دی تھی آدم کو اُس سے پہلے پھر بھول گیا اور نہ پائی ہم نے اسمیں کچھ ہمت ');
INSERT INTO "al_hind" VALUES(20,116,'20:116','اور جب کہا ہم نے فرشتوں کو سجدہ کرو آدم کو تو سجدہ میں گر پڑے مگر نہ مانا ابلیس نے');
INSERT INTO "al_hind" VALUES(20,117,'20:117','پھر کہہ دیا ہم نے اے آدم یہ دشمن تیرا ہے اور تیرے جوڑے کا سو نکلوا نہ دے تمکو بہشت سے پھر تو پڑ جائے تکلیف میں ');
INSERT INTO "al_hind" VALUES(20,118,'20:118','تجھ کو یہ ملا ہے کہ نہ بھوکا ہو تو اُس میں اور نہ ننگا');
INSERT INTO "al_hind" VALUES(20,119,'20:119','اور یہ کہ نہ پیاس کھینچے تو اس میں اور نہ دھوپ ');
INSERT INTO "al_hind" VALUES(20,120,'20:120','پھر جی میں ڈالا اُسکے شیطان نے کہا اے آدم میں بتاؤں تجھ کو درخت سدا زندہ رہنے کا اور بادشاہی جو پرانی نہ ہو ');
INSERT INTO "al_hind" VALUES(20,121,'20:121','پھر دونوں نے کھا لیا اُسمیں سے پھر کھل گئیں اُن پر اُنکی بری چیزیں اور لگے گانٹھنے اپنے اوپر پتے بہشت کے اور حکم ٹالا آدم نے اپنے رب کا پھر راہ سے بہکا');
INSERT INTO "al_hind" VALUES(20,122,'20:122','پھر نواز دیا اُسکو اُسکے رب نے پھر متوجہ ہوا اُس پر اور راہ پر لایا ');
INSERT INTO "al_hind" VALUES(20,123,'20:123','فرمایا اترو یہاں سے دونوں اکٹھے رہو ایک دوسرے کے دشمن پھر اگر پہنچے تمکو میری طرف سے ہدایت پھر جو چلا میری بتلائی راہ پر سو نہ وہ بہکے گا اور نہ وہ تکلیف میں پڑے گا ');
INSERT INTO "al_hind" VALUES(20,124,'20:124','اور جس نے منہ پھیرا میری یاد سے تو اُسکو ملنی ہے گذران تنگی کی اور لائیں گے ہم اُسکو دن قیامت کے اندھا ');
INSERT INTO "al_hind" VALUES(20,125,'20:125','وہ کہے گا اے رب کیوں اٹھا لایا تو مجھ کو اندھا اور میں تو تھا دیکھنے والا ');
INSERT INTO "al_hind" VALUES(20,126,'20:126','فرمایا یونہی پہنچی تھیں تجھ کو میری آیتیں پھر تو نے انکو بھلا دیا اور اسی طرح آج تجھ کو بھلا دیں گے ');
INSERT INTO "al_hind" VALUES(20,127,'20:127','اور اسی طرح بدلہ دیں گے ہم اُسکو جو حد سے نکلا اور یقین نہ لایا اپنے رب کی باتوں پر اور آخرت کا عذاب سخت ہے اور بہت باقی رہنے والا ');
INSERT INTO "al_hind" VALUES(20,128,'20:128','سو کیا اُنکو سمجھ نہ آئی اس بات سے کہ کتنی غارت کر دیں ہم نے اُن سے پہلے جماعتیں یہ لوگ پھرتے ہیں اُنکی جگہوں میں اس میں خوب نشانیاں ہیں عقل رکھنے والوں کو');
INSERT INTO "al_hind" VALUES(20,129,'20:129','اور اگر نہ ہوتی ایک بات کہ نکل چکی تیرے ب کی طرف سے تو ضرور ہو جاتی مٹھ بھیڑ اور اگر نہ ہوتا وعدہ مقرر کیا گیا ');
INSERT INTO "al_hind" VALUES(20,130,'20:130','سو تو سہتا رہ جو وہ کہیں اور پڑھتا رہ خوبیاں اپنے رب کی سورج نکلنے سے پہلے اور غروب ہونے سے پہلے اور کچھ گھڑیوں میں رات کی پڑھا کر اور دن کی حدوں پر شاید تو راضی ہو ');
INSERT INTO "al_hind" VALUES(20,131,'20:131','اور مت پسار اپنی آنکھیں اُس چیز پر جو فائدہ اٹھانے کو دی ہم نے ان طرح طرح کے لوگوں کو رونق دنیا کی زندگی کی اُنکے جانچے کو اور تیرے رب کی دی ہوئی روزی بہتر ہے اور بہت باقی رہنے والی ');
INSERT INTO "al_hind" VALUES(20,132,'20:132','اور حکم کر اپنے گھر والوں کو نماز کا اور خود بھی قائم رہ اُس پر ہم نہیں مانگتے تجھ سے روزی ہم روزی دیتے ہیں تجھ کو اور انجام بھلا ہے پرہیزگاری کا ');
INSERT INTO "al_hind" VALUES(20,133,'20:133','اور لوگ کہتے ہیں کہ کیوں نہیں لے آتا ہمارے پاس کوئی نشانی اپنے رب سے کیا پہنچ نہیں چکی اُنکو نشانی اگلی کتابوں میں کی ');
INSERT INTO "al_hind" VALUES(20,134,'20:134','اور اگر ہم ہلاک کر دیتے اُنکو کسی آفت میں اس سے پہلے تو کہتے اے رب کیوں نہ بھیجا ہم تک کسی کو پیغام دیکر کہ ہم چلتے تیری کتاب پر ذلیل اور رسوا ہونے سے پہلے');
INSERT INTO "al_hind" VALUES(20,135,'20:135','تو کہہ ہر کوئی راہ دیکھتا ہے سو تم بھی راہ دیکھو اور آئندہ جان لو گے کون ہیں سیدھی راہ والے اور کس نے راہ پائی ');
INSERT INTO "al_hind" VALUES(21,1,'21:1','نزدیک آ گیا لوگوں کے اُنکے حساب کا وقت اور وہ بیخبر ٹلا رہے ہیں ');
INSERT INTO "al_hind" VALUES(21,2,'21:2','کوئی نصیحت نہیں پہنچتی اُنکو اُنکے رب سے نئی مگر اُسکو سنتے ہیں کھیل میں لگےہوئے');
INSERT INTO "al_hind" VALUES(21,3,'21:3','کھیل میں پڑے ہیں دل اُنکے اور چھپا کر مصلحت کی بے انصافوں نے یہ شخص کون ہے ایک آدمی ہےتم ہی جیسا پھر کیوں پھنستے ہو اُسکے جاد ومیں آنکھوں دیکھتے ');
INSERT INTO "al_hind" VALUES(21,4,'21:4','اُس نے کہا میرے رب کو خبر ہے بات کی آسمان میں ہو یا زمین میں اور وہ ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(21,5,'21:5','اُسکو چھوڑ کر کہتے ہیں بیہودہ خواب ہیں نہیں جھوٹ باندھ لیا ہے نہیں شعر کہتا ہے پھر چاہیئے لے آئے ہمارے پاس کوئی نشانی جیسے پیغام لیکر آئے ہیں پہلے ');
INSERT INTO "al_hind" VALUES(21,6,'21:6','نہیں مانا اُن سے پہلے کسی بستی نے جنکو غارت کر دیا ہم نے کیا اب یہ مان لیں گے ');
INSERT INTO "al_hind" VALUES(21,7,'21:7','اور پیغام نہیں بھیجا ہم نے تجھ سے پہلے مگر یہی مردوں کے ہاتھ وحی بھیجتے تھے ہم اُنکو سو پوچھ لو یاد رکھنے والوں سے اگر تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(21,8,'21:8','اور نہیں بنائے تھے ہم نے اُنکے ایسے بدن کہ وہ کھانا نہ کھائیں اور نہ تھے وہ ہمیشہ رہ جانے والے ');
INSERT INTO "al_hind" VALUES(21,9,'21:9','پھر سچا کر دیا ہم نے اُن سے وعدہ سو بچا دیا اُنکو اور جسکو ہم نے چاہا اور غارت کر دیا حد سے نکلے والوں کو ');
INSERT INTO "al_hind" VALUES(21,10,'21:10','ہم نے اتاری ہے تمہاری طرف کتاب کہ اُس میں تمہارا ذکر ہے کیا تم سمجھتے نہیں ');
INSERT INTO "al_hind" VALUES(21,11,'21:11','اور کتنی پیس ڈالیں ہم نے بسیتاں جو تھیں گنہگار اور اٹھا کھڑے کئے اُنکے پیچھے اور لوگ ');
INSERT INTO "al_hind" VALUES(21,12,'21:12','پھر جب آہٹ پائی انہوں نے ہماری آفت کی تب لگے وہاں سے ایڑ کرنے');
INSERT INTO "al_hind" VALUES(21,13,'21:13','ایڑ مت کرو اور لوٹ جاؤ جہاں تم نے عیش کیا تھا اور اپنے گھروں میں شاید کوئی تمکو پوچھے ');
INSERT INTO "al_hind" VALUES(21,14,'21:14','کہنے لگے ہائے خرابی ہم تھے بیشک گنہگار');
INSERT INTO "al_hind" VALUES(21,15,'21:15','پھر برابر یہی رہی اُنکی فریاد یہاں تک کہ ڈھیر کر دیے گئے کاٹ کر بجھے پڑے ہوئے ');
INSERT INTO "al_hind" VALUES(21,16,'21:16','اور ہم نے نہیں بنایا آسمان اور زمین کو اور جو کچھ انکے بیچ میں ہےکھیلتے ہوئے ');
INSERT INTO "al_hind" VALUES(21,17,'21:17','اگر ہم چاہتے کہ بنا لیں کچھ کھلونا تو بنا لیتے ہم اپنے پاس سے اگر ہم کو کرنا ہوتا');
INSERT INTO "al_hind" VALUES(21,18,'21:18','یوں نہیں پر ہم پھینک مارتے ہیں سچ کو جھوٹ پر پھر وہ اُس کا سر پھوڑ ڈالتا ہے پھر وہ جاتا رہتا ہے اور تمہارے لئے خرابی ہے اُن باتوں سے جو تم بتلاتے ہو ');
INSERT INTO "al_hind" VALUES(21,19,'21:19','اور اُسی کا ہے جو کوئی ہے آسمان اور زمین میں اور جو اُسکے نزدیک رہتے ہیں سرکشی نہیں کرتے اُسکی عبادت سے اور نہیں کرتے کاہلی');
INSERT INTO "al_hind" VALUES(21,20,'21:20','یاد کرتےہیں رات اور دن نہیں تھکتے ');
INSERT INTO "al_hind" VALUES(21,21,'21:21','کیا ٹھہرائے ہیں انہوں نے اور معبود زمین میں کہ وہ جلا اٹھائیں گے اُنکو ');
INSERT INTO "al_hind" VALUES(21,22,'21:22','اگر ہوتے ان دونوں میں اور معبود سوائے اللہ کے تو دونوں خراب ہو جاتے سو پاک ہے اللہ عرش کا مالک اُن باتوں سے جو یہ بتلاتے ہیں ');
INSERT INTO "al_hind" VALUES(21,23,'21:23','اُس سے پوچھا نہ جائے جو وہ کرے اور اِن سے پوچھا جائے ');
INSERT INTO "al_hind" VALUES(21,24,'21:24','کیا ٹھہرائے ہیں انہوں نے اُس سے ورے اور معبود تو کہہ لاؤ اپنی سند یہی بات ہے میرے ساتھ والوں کی اور یہی بات ہے مجھ سے پہلوں کی کوئی نہیں پر وہ بہت لوگ نہیں سمجھتے سچی بات سو ٹلا رہے ہیں ');
INSERT INTO "al_hind" VALUES(21,25,'21:25','اور نہیں بھیجا ہم نے تجھ سے پہلے کوئی رسول مگر اُسکو یہی حکم بھیجا کہ بات یوں ہے کہ کسی کی بندگی نہیں سوائے میرے سو میری بندگی کرو ');
INSERT INTO "al_hind" VALUES(21,26,'21:26','اور کہتے ہں رحمٰن نے کر لیا کسی کو بیٹا وہ ہر گز اس لائق نہیں لیکن وہ بندے ہیں جنکو عزت دی ہے');
INSERT INTO "al_hind" VALUES(21,27,'21:27','اس سے بڑھ کر نہیں بول سکتے اور وہ اسی کے حکم پر کام کرتے ہیں ');
INSERT INTO "al_hind" VALUES(21,28,'21:28','اُسکو معلوم ہے جو اُنکے آگے ہیں اور پیچھے اور وہ سفارش نہیں کرتے مگر اُسکی جس سے اللہ راضی ہو اور وہ اُسکی ہیبت سے ڈرتے ہیں ');
INSERT INTO "al_hind" VALUES(21,29,'21:29','اور جو کوئی اُن میں کہے کہ میری بندگی ہے اس سے ورے سو اسکو ہم بدلہ دیں گے دوزخ یونہی ہم بدلہ دیتے ہیں بے انصافوں کو ');
INSERT INTO "al_hind" VALUES(21,30,'21:30','اور کیا نہیں دیکھا ان منکروں نے کہ آسمان اور زمین منہ بند تھے پھر ہم نے انکو کھول دیا اور بنائی ہم نے پانی سے ہر ایک چیز جس میں جان ہے پھر کیا یقین نہیں کرتے ');
INSERT INTO "al_hind" VALUES(21,31,'21:31','اور رکھ دیے ہم نے زمین میں بھاری بوجھ کبھی اُنکو لے کر جھک پڑے اور رکھیں اُس میں کشادہ راہیں تاکہ وہ راہ پائیں ');
INSERT INTO "al_hind" VALUES(21,32,'21:32','اور بنایا ہم نے آسمان کو چھت محفوظ اور وہ آسمان کی نشانیوں کو دھیان میں نہیں لاتے ');
INSERT INTO "al_hind" VALUES(21,33,'21:33','اور وہی ہے جس نے بنائے رات اور دن اور سورج اور چاند سب اپنے اپنے گھر میں پھرتے ہیں ');
INSERT INTO "al_hind" VALUES(21,34,'21:34','اور نہیں دیا ہم نے تجھ سے پہلے کسی آدمی کو ہمیشہ کیلئے زندہ رہنا پھر کیا اگر تو مر گیا تو وہ رہ جائیں گے');
INSERT INTO "al_hind" VALUES(21,35,'21:35','ہر جی کو چکھنی ہے موت اور ہم تمکو جانچتے ہیں برائی سے اور بھلائی سے آزمانے کو اور ہماری طرف پھر کر آ جاؤ گے ');
INSERT INTO "al_hind" VALUES(21,36,'21:36','اور جہاں تجھ کو دیکھا منکروں نے تو کوئی کام نہیں اُنکو تجھ سے مگر ٹھٹھا کرنا کیا یہی شخص ہے جو نام لیتا ہے تمہارے معبودوں کا اور وہ رحمٰن کے نام سے منکر ہیں ');
INSERT INTO "al_hind" VALUES(21,37,'21:37','بنا ہے آدمی جلدی کا اب دکھلاتا ہوں تمکو اپنی نشانیاں سو مجھ سے جلدی مت کرو ');
INSERT INTO "al_hind" VALUES(21,38,'21:38','اور کہتے ہیں کب ہو گا یہ وعدہ اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(21,39,'21:39','اگر جان لیں یہ منکر اُس وقت کو کہ نہ روک سکیں گے اپنے منہ سے آگ اور نہ اپنی پیٹھ سے اور نہ اُنکو مدد پہنچے گی');
INSERT INTO "al_hind" VALUES(21,40,'21:40','کچھ نہیں وہ آئے گی اُن پر ناگہاں پھر انکے ہوش کھو دیگی پھر نہ پھیر سکیں گے اُسکو اور نہ اُنکو فرصت ملے گی ');
INSERT INTO "al_hind" VALUES(21,41,'21:41','اور ٹھٹھے ہو چکے ہیں رسولوں سے تجھ سے پہلے پھر الٹ پڑی ٹھٹھا کرنے والوں پر اُن میں سے وہ چیز جس کا ٹھٹھا کرتے تھے ');
INSERT INTO "al_hind" VALUES(21,42,'21:42','تو کہہ کون نگہبانی کرتا ہے تمہاری رات میں اور دن میں رحمٰن سے کوئی نہیں وہ اپنے رب کے ذکر سے منہ پھیرتے ہیں ');
INSERT INTO "al_hind" VALUES(21,43,'21:43','یا اُنکے واسطے کوئی معبود ہیں کہ اُنکو بچاتے ہیں ہمارے سوا وہ اپنی بھی مدد نہیں کر سکتے اور نہ اُنکی ہماری طرف سے رفاقت ہو ');
INSERT INTO "al_hind" VALUES(21,44,'21:44','کوئی نہیں پر ہم نے عیش دیا اُنکو اور اُنکے باپ دادوں کو یہاں تک کہ بڑھ گئ اُن پر زندگی پھر کیا نہیں دیکھتے کہ ہم چلے آتے ہیں زمین کو گھٹاتے اُسکے کناروں سے اب کیا وہ جیتنے والےہیں ');
INSERT INTO "al_hind" VALUES(21,45,'21:45','تو کہہ میں جو تمکو ڈراتا ہوں سو حکم کے موافق اور سنتے نہیں بہرے پکارنے کو جب کوئی اُنکو ڈر کی بات سنائے ');
INSERT INTO "al_hind" VALUES(21,46,'21:46','اور کہیں پہنچ جائے اُن تک ایک بھاپ تیرے رب کے عذاب کی تو ضرور کہنے لگیں ہائے کم بختی ہماری بیشک ہم تھے گنہگار ');
INSERT INTO "al_hind" VALUES(21,47,'21:47','اور رکھیں گے ہم ترازوئیں انصاف کی قیامت کے دن پھر ظلم نہ ہو گا کسی جی پر ایک ذرہ اور اگر ہو گا برابر رائی کے دانہ کی تو ہم لے آئیں گے اُسکو اور ہم کافی ہیں حساب کرنے کو ');
INSERT INTO "al_hind" VALUES(21,48,'21:48','اور ہم نے دی تھی موسٰی اور ہارون کو قضیے چکانے والی کتاب اور روشنی اور نصیحت ڈرنے والوں کو ');
INSERT INTO "al_hind" VALUES(21,49,'21:49','جو ڈرتے ہیں اپنے رب سے بن دیکھے اور وہ قیامت کا خطرہ رکھتے ہیں ');
INSERT INTO "al_hind" VALUES(21,50,'21:50','اور یہ ایک نصیحت ہے برکت کی جو ہم نے اتاری سو کیا تم اُسکو نہیں مانتے ');
INSERT INTO "al_hind" VALUES(21,51,'21:51','اور آگے دی تھی ہم نے ابراہیم کو اُسکی نیک راہ اور ہم رکھتے ہیں اُسکی خبر ');
INSERT INTO "al_hind" VALUES(21,52,'21:52','جب کہا اُس نے اپنے باپ کو اور اپنی قوم کو یہ کیسی مورتیں ہیں جن پر تم مجاور بنے بیٹھے ہو ');
INSERT INTO "al_hind" VALUES(21,53,'21:53','بولے ہم نے پایا اپنے باپ دادوں کو انہی کی پوجا کرتے ');
INSERT INTO "al_hind" VALUES(21,54,'21:54','بولا مقرر ہے تم اور تمہارے باپ دادے صریح گمراہی میں ');
INSERT INTO "al_hind" VALUES(21,55,'21:55','بولے تو ہمارے پاس لایا ہے سچی بات یا تو کھلاڑیاں کرتا ہے ');
INSERT INTO "al_hind" VALUES(21,56,'21:56','بولا نہیں رب تمہارا وہی ہے رب آسمان اور زمین کا جس نے اُنکو بنایا اور میں اسی بات کا قائل ہوں ');
INSERT INTO "al_hind" VALUES(21,57,'21:57','اور قسم اللہ کی میں علاج کروں گا تمہارے بتوں کا جب تم جا چکو گے پیٹھ پھیر کر ');
INSERT INTO "al_hind" VALUES(21,58,'21:58','پھر کر ڈلا اُنکو ٹکڑے ٹکڑے مگر ایک بڑا اُن کا کہ شاید اُسکی طرف رجوع کریں ');
INSERT INTO "al_hind" VALUES(21,59,'21:59','کہنے لگے کس نے کیا یہ کام ہمارے معبودوں کے ساتھ وہ تو کوئی بے انصاف ہے ');
INSERT INTO "al_hind" VALUES(21,60,'21:60','وہ بولے ہم نے سنا ہے ایک جوان جو بتوں کو کچھ کہا کرتا ہے اُسکو کہتے ہیں ابراہیم ');
INSERT INTO "al_hind" VALUES(21,61,'21:61','وہ بولے اُسکو لے آؤ لوگوں کے سامنے شاید وہ دیکھیں ');
INSERT INTO "al_hind" VALUES(21,62,'21:62','بولے کیا تو نےکیا ہے یہ ہمارے معبودوں کیساتھ اے ابراہیم');
INSERT INTO "al_hind" VALUES(21,63,'21:63','بولا نہیں پر یہ کیا ہے اُنکے اس بڑے نے سو اُن سے پوچھ لو اگر وہ بولتے ہیں ');
INSERT INTO "al_hind" VALUES(21,64,'21:64','پھر سوچے اپنے جی میں پھر بولے لوگو تم ہی بے انصاف ہو ');
INSERT INTO "al_hind" VALUES(21,65,'21:65','پھر اوندھے ہو گئے سر جھکا کر تو تو جانتا ہے جیسا یہ بولتے ہیں ');
INSERT INTO "al_hind" VALUES(21,66,'21:66','بولا کیا پھر تم پوجتے ہو اللہ سے ورے ایسے کو جو تمہارا کچھ بھلا کرنے نہ برا');
INSERT INTO "al_hind" VALUES(21,67,'21:67','بیزار ہوں میں تم سے اور جنکو تم پوجتے ہو اللہ کے سوائے کیا تم کو سمجھ نہیں ');
INSERT INTO "al_hind" VALUES(21,68,'21:68','بولے اسکو جلاؤ اور مدد کرو اپنے معبودوں کی اگر کچھ کرتے ہو ');
INSERT INTO "al_hind" VALUES(21,69,'21:69','ہم نے کہا اے آگ ٹھنڈی ہو جا اور آرام کر ابراہیم پر ');
INSERT INTO "al_hind" VALUES(21,70,'21:70','اور چاہنے لگے اُس کا برا پھر اُنہی کو ہم نے ڈالا نقصان میں ');
INSERT INTO "al_hind" VALUES(21,71,'21:71','اور بچا نکالا ہم نے اُسکو اور لوط کو اُس زمین کی طرف جس میں برکت رکھی ہم نے جہان کے واسطے ');
INSERT INTO "al_hind" VALUES(21,72,'21:72','اور بخشا ہم نے اُسکو اسحٰق اور یعقوب دیا انعام میں اور سب کو نیک بخت کیا ');
INSERT INTO "al_hind" VALUES(21,73,'21:73','اور اُنکو کیا ہم نے پیشوا راہ بتلاتے تھے ہمارے حکم سے اور کہلا بھیجا ہم نے اُنکو کرنا نیکیوں کا اور قائم رکھنی نماز اور دینی زکوٰۃ اور وہ تھے ہماری بندگی میں لگے ہوئے ');
INSERT INTO "al_hind" VALUES(21,74,'21:74','اور لوط کو دیا ہم نے حکم اور سمجھ اور بچا نکالا اُسکو اس بستی سے جو کرتے تھے گندے کام وہ تھے لوگ بڑے نافرمان ');
INSERT INTO "al_hind" VALUES(21,75,'21:75','اور اُسکو لے لیا ہم نے اپنی رحمت میں وہ ہے نیک بختوں میں ');
INSERT INTO "al_hind" VALUES(21,76,'21:76','اور نوح کو جب اُس نے پکارا اُس سے پہلے پھر قبول کر لی ہم نے اُس کی دعا سو بچا دیا اُسکو اور اسکے گھر والوں کو بڑی گھبراہٹ سے');
INSERT INTO "al_hind" VALUES(21,77,'21:77','اور مدد کی اُسکی اُن لوگوں پر جو جھٹلاتے تھے ہماری آیتیں وہ تھے برے لوگ پھر ڈبا دیا ہم نے اُن سب کو ');
INSERT INTO "al_hind" VALUES(21,78,'21:78','اور داؤد اور سلیمان کو جب لگے فیصل کرنے کھیتی کا جھگڑا جب روند گئیں اُسکو رات میں ایک قوم کی بکریاں اور سامنے تھا ہمارے اُن کا فیصلہ');
INSERT INTO "al_hind" VALUES(21,79,'21:79','پھر سمجھا دیا ہم نے وہ فیصلہ سلیمان کو اور دونوں کو دیا تھا ہم نے حکم اور سمجھ اور تابع کیے ہم نے داؤد کے ساتھ پہاڑ تسبیح پڑھا کرتے اور اڑتے جانور اور یہ سب کچھ ہم نے کیا ');
INSERT INTO "al_hind" VALUES(21,80,'21:80','اور اُسکو سکھلایا ہم نے بنانا ایک تمہارا لباس کہ بچاؤ ہو تمکو لڑائی میں سو کچھ تم شکر کرتے ہو ');
INSERT INTO "al_hind" VALUES(21,81,'21:81','اور سلیمان کے تابع کی ہوا زور سے چلنے والی کہ چلتی اسکے حکم اسے اُس زمین کی طرف جہاں برکت دی ہے ہم نے اور ہم کو سب چیز کی خبر ہے ');
INSERT INTO "al_hind" VALUES(21,82,'21:82','اور تابع کئے کتنے شیطان جو غوطہ لگاتے اُسکے واسطے اور بہت سے کام بناتے اسکے سوائے اور ہم نے اُنکو تھام رکھا تھا ');
INSERT INTO "al_hind" VALUES(21,83,'21:83','اور ایوب کو جس وقت پکارا اُس نے اپنے رب کو کہ مجھ پر پڑی ہے تکلیف اور تو ہے سب رحم والوں سے رحم کرنے والا');
INSERT INTO "al_hind" VALUES(21,84,'21:84','پھر ہم نے سن لی اُسکی فریاد سو دور کر دی جو اُس پر تھی تکلیف اور عطا کئے اُسکو اسکے گھر والے اور اتنے ہی اور اُنکے ساتھ رحمت اپنی طرف سے اور نصیحت بندگی کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(21,85,'21:85','اور اسمٰعیل اور ادریس اور ذوالکفل کو یہ سب ہیں صبر والے ');
INSERT INTO "al_hind" VALUES(21,86,'21:86','اور لے لیا ہم نے اُنکو اپنی رحمت میں وہ ہیں نیک بختوں میں');
INSERT INTO "al_hind" VALUES(21,87,'21:87','اور مچھلی والے کو جب چلا گیا غصہ ہو کر پھر سمجھا کہ ہم نہ پکڑ سکیں گے اُسکو پھر پکارا اُن اندھیروں میں کہ کوئی حاکم نہیں سوائے تیرے تو بے عیب ہے میں تھا گنہگاروں سے ');
INSERT INTO "al_hind" VALUES(21,88,'21:88','پھر سن لی ہم نے اُسکی فریاد اور بچا دیا اُسکو اُس گھٹنے سے اور یوں ہم بچا دیتےہیں ایمان والوں کو ');
INSERT INTO "al_hind" VALUES(21,89,'21:89','اور زکریا کو جب پکارا اُس نے اپنے رب کو اے رب نہ چھوڑ مجھ کو اکیلا اور تو ہے سب سے بہتر وارث ');
INSERT INTO "al_hind" VALUES(21,90,'21:90','پھر ہم نے سن لی اُسکی دعا اور بخشا اُسکو یحیٰی اور اچھا کر دیا اُسکی عورت کو وہ لوگ دوڑتے تھے بھلائیوں پر اور پکارتے تھے ہم کو توقع سے اور ڈر سے اور تھے ہمارے آگے عاجز ');
INSERT INTO "al_hind" VALUES(21,91,'21:91','اور وہ عورت جس نے قابو میں رکھی اپنی شہوت پھر پھونک دی ہم نے اُس عورت میں اپنی روح اور کیا اُسکو اور اُسکے بیٹے کو نشانی جہان والوں کے واسطے ');
INSERT INTO "al_hind" VALUES(21,92,'21:92','یہ لوگ ہیں تمہارے دین کے سب ایک دین پر اور میں ہوں رب تمہارا سو میری بندگی کرو ');
INSERT INTO "al_hind" VALUES(21,93,'21:93','اور ٹکڑے ٹکڑے بانٹ لیا لوگوں نے آپس میں اپنا کام سب ہمارےپاس پھر آئیں گے ');
INSERT INTO "al_hind" VALUES(21,94,'21:94','سو جو کوئی کرےکچھ نیک کام اور وہ رکھتا ہو ایمان سو اکارت نہ کریں گے اُسکی سعی کو اور ہم اُسکو لکھ لیتے ہیں ');
INSERT INTO "al_hind" VALUES(21,95,'21:95','اور مقرر ہو چکا ہر بستی پر جس کو غارت کر دیا ہم نے کہ وہ پھر کر نہیں آئیں گے ');
INSERT INTO "al_hind" VALUES(21,96,'21:96','یہاں تک کہ جب کھول دئے جائیں یاجوج اور ماجوج اور وہ ہر اوچان سے پھسلتے چلے آئیں ');
INSERT INTO "al_hind" VALUES(21,97,'21:97','اور نزدیک آ لگے سچا وعدہ پھر اس دم اوپر لگی رہ جائیں منکروں کی آنکھیں ہائے کمبختی ہماری ہم بے خبر رہے اس سے نہیں پر ہم تھے گنہگار ');
INSERT INTO "al_hind" VALUES(21,98,'21:98','تم اور جو کچھ تم پوجتے ہو اللہ کے سوائے ایندھن ہے دوزخ کا تمکو اُس پر پہنچنا ہے ');
INSERT INTO "al_hind" VALUES(21,99,'21:99','اگر ہوتے یہ بت معبود تو نہ پہنچتے اُس پر اور سارے اُس میں سدا پڑے رہیں گے ');
INSERT INTO "al_hind" VALUES(21,100,'21:100','اُنکو وہاں چلانا ہے اور اُس میں کچھ نہ سنیں گے ');
INSERT INTO "al_hind" VALUES(21,101,'21:101','جنکے لئے پہلے سے ٹھہر چکی ہماری طرف سے نیکی وہ اُس سے دور رہیں گے ');
INSERT INTO "al_hind" VALUES(21,102,'21:102','نہیں سنیں گے اُسکی آہٹ اور وہ اپنے جی کے مزوں میں سدا رہیں گے ');
INSERT INTO "al_hind" VALUES(21,103,'21:103','نہ غم ہو گا اُنکو اس بڑی گھبراہٹ میں اور لینے آئیں گے اُنکو فرشتے آج دن تمہارا ہے جس کا تم سے وعدہ کیا گیا تھا ');
INSERT INTO "al_hind" VALUES(21,104,'21:104','جس دن ہم لپیٹ دیں گے آسمان کو جیسے لپیٹتے ہیں طومار میں کاغذ جیسا سرے سے بنایا تھا ہم نے پہلی بار پھر اُسکو دہرائیں گے وعدہ ضرور ہو چکا ہے ہم پر ہم کو پورا کرنا ہے ');
INSERT INTO "al_hind" VALUES(21,105,'21:105','اور ہم نے لکھ دیا ہے زبور میں نصیحت کے پیچھے کہ آخر زمین پر مالک ہوں گے میرے نیک بندے ');
INSERT INTO "al_hind" VALUES(21,106,'21:106','اس میں مطلب کو پہنچتے ہیں لوگ بندگی والے ');
INSERT INTO "al_hind" VALUES(21,107,'21:107','اور تجھ کو جو ہم نے بھیجا سو مہربانی کر کر جہاں کے لوگوں پر ');
INSERT INTO "al_hind" VALUES(21,108,'21:108','تو کہہ مجھ کو تو حکم یہی آیا ہے کہ معبود تمہارا ایک معبود ہے پھر کیا ہو تم حکمبرداری کرنے والے ');
INSERT INTO "al_hind" VALUES(21,109,'21:109','پھر اگر وہ منہ موڑیں تو تو کہدے میں نے خبر کر دی تمکو دونوں طرف باربار اور میں نہیں جانتا نزدیک ہے یا دور ہے جو تم سے وعدہ ہوا ');
INSERT INTO "al_hind" VALUES(21,110,'21:110','وہ رب جانتا ہے جو بات پکار کر کرو اور جانتا ہے جو تم چھپاتے ہو ');
INSERT INTO "al_hind" VALUES(21,111,'21:111','اور میں نہیں جانتا شاید تاخیر میں تمکو جانچنا ہے اور فائدہ دینا ہے ایک وقت تک ');
INSERT INTO "al_hind" VALUES(21,112,'21:112','رسول نے کہا اے رب فیصلہ کر انصاف کا اور رب ہمارا رحمٰن ہے اُسی سے مدد مانگتے ہیں اُن باتوں پر جو تم بتلاتے ہو ');
INSERT INTO "al_hind" VALUES(22,1,'22:1','لوگو ڈرو اپنے رب سے بیشک بھونچال قیامت کا ایک بڑی چیز ہے');
INSERT INTO "al_hind" VALUES(22,2,'22:2','جس دن اُسکو دیکھو گے بھول جائیگی ہر دودھ پلانے والی اپنے دودھ پلائے کو اور ڈال دیگی ہر پیٹ والی اپنا پیٹ اور تو دیکھے لوگوں پر نشہ اور اُن پر نشہ نہیں پر آفت اللہ کی سخت ہے ');
INSERT INTO "al_hind" VALUES(22,3,'22:3','اور بعضے لوگ وہ ہیں جو جھگڑتے ہیں اللہ کی بات میں بیخبری سے اور پیروی کرتا ہے ہر شیطان سرکش کی ');
INSERT INTO "al_hind" VALUES(22,4,'22:4','جس کے حق میں لکھ دیا گیا ہے کہ جو کوئی اُسکا رفیق ہو سو وہ اُسکو بہکائے اور لیجائے عذاب میں دوزخ کے ');
INSERT INTO "al_hind" VALUES(22,5,'22:5','اے لوگو اگر تمکو دھوکا ہے جی اٹھنے میں تو ہم نے تمکو بنایا مٹی سے پھر قطرہ سے پھر جمے ہوئے خون سے پھر گوشت کی بوٹی نقشہ بنی ہوئی سے اور بدون نقشہ بنی ہوئی سے اس واسطے کہ تم کو کھول کر سنا دیں اور ٹھہرا رکھتے ہیں ہم پیٹ میں جو کچھ چاہیں ایک وقت معین تک پھر تم کو نکالتے ہیں لڑکا پھر جب تک کہ پہنچو اپنی جوانی کے زور کو اورکوئی تم میں سے قبضہ کر لیا جاتا ہے اور کوئی تم میں سے پھر چلایا جاتا ہے نکمی عمر تک تاکہ سمجھنے کے پیچھے کچھ نہ سمجھنے لگے اور تو دیکھتا ہے زمین خراب پڑی ہوئی پھر جہاں ہم نے اتارا اُس پر پانی تازی ہو گئ اور ابھری اور اگائیں ہر قسم قسم رونق کی چیزیں ');
INSERT INTO "al_hind" VALUES(22,6,'22:6','یہ سب کچھ اس واسطے کہ اللہ وہی ہے محقق اور وہ جِلاتا ہے مُردوں کو اور وہ ہر چیز کر سکتا ہے');
INSERT INTO "al_hind" VALUES(22,7,'22:7','اور یہ کہ قیامت آنی ہے اُس میں دھوکا نہیں اور یہ کہ اللہ اٹھائے گا قبروں میں پڑے ہوؤں کو ');
INSERT INTO "al_hind" VALUES(22,8,'22:8','اور بعضا شخص وہ ہے جو جھگڑتا ہے اللہ کی بات میں بغیر جانے اور بغیر دلیل اور بدون روشن کتاب کے ');
INSERT INTO "al_hind" VALUES(22,9,'22:9','اپنی کروٹ موڑ کر تاکہ بہکائے اللہ کی راہ سے اُسکے لئے دنیا میں رسوائی ہے اور چکھائیں گے ہم اُسکو قیامت کے دن جلن کی مار ');
INSERT INTO "al_hind" VALUES(22,10,'22:10','یہ اُسکی وجہ سے جو آگے بھیج چکے تیرے دو ہاتھ اور اُس وجہ سےکہ اللہ نہیں ظلم کرتا بندوں پر ');
INSERT INTO "al_hind" VALUES(22,11,'22:11','اور بعضا شخص وہ ہے کہ بندگی کرتا ہے اللہ کی کنارے پر پھر اگر پہنچی اُسکو بھلائی تو قائم ہو گیا اُس عبادت پر اور اگر پہنچ گئ اُسکو جانچ پھر گیا الٹا اپنے منہ پر گنوائی دنیا اور آخرت یہی ہے ٹوٹا صریح ');
INSERT INTO "al_hind" VALUES(22,12,'22:12','پکارتا ہے اللہ کے سوائے ایسی چیز کو کہ نہ اُسکا نقصان کرے اور نہ اُسکا فائدہ کرے یہی ہے دور جا پڑنا گمراہ ہو کر ');
INSERT INTO "al_hind" VALUES(22,13,'22:13','پکارے جاتا ہے اُسکو جس کا ضرر پہلے پہنچے نفع سے بیشک برا دوست ہے اور برا رفیق ');
INSERT INTO "al_hind" VALUES(22,14,'22:14','اللہ داخل کرے گا اُنکو جو ایمان لائے اور کیں بھلائیاں باغوں میں بہتی ہیں نیچے اُنکے نہریں اللہ کرتا ہے جو چاہے ');
INSERT INTO "al_hind" VALUES(22,15,'22:15','جسکو یہ خیال ہو کہ ہرگز نہ مدد کرے گا اُسکی اللہ دنیا میں اور آخرت میں تو تان لے ایک رسی آسمان کو پھر کاٹ ڈالے اب دیکھے کچھ جاتا رہا اُسکی اس تدبیر سے اُس کا غصہ ');
INSERT INTO "al_hind" VALUES(22,16,'22:16','اور یوں اتارا ہم نے یہ قرآن کھلی باتیں اور یہ ہے کہ اللہ سمجھا دیتا ہے جسکو چاہے ');
INSERT INTO "al_hind" VALUES(22,17,'22:17','جو لوگ مسلمان ہیں اور جو یہود ہیں اور صابئیں اور نصاریٰ اور مجوس اور جو شرک کرتے ہیں مقرر اللہ فیصلہ کرے گا اُن میں قیامت کے دن اللہ کے سامنے ہے ہر چیز ');
INSERT INTO "al_hind" VALUES(22,18,'22:18','تو نے نہیں دیکھا کہ اللہ کو سجدہ کرتا ہے جو کوئی آسمان میں ہے اور جو کوئی زمین میں ہے اور سورج اور چاند اورتارے اور پہاڑ اور درخت اور جانور اور بہت آدمی اور بہت ہیں کہ اُن پر ٹھہر چکا عذاب اور جسکو اللہ ذلیل کرے اُسے کوئی نہیں عزت دینے والا اللہ کرتا ہے جو چاہے ');
INSERT INTO "al_hind" VALUES(22,19,'22:19','یہ دو مدعی ہیں جھگڑے ہیں اپنے رب پر سو جو منکر ہوئے اُنکے واسطے بیونتے ہیں کپڑے آگ کے ڈالتےہیں اُنکے سر پر جلتا پانی');
INSERT INTO "al_hind" VALUES(22,20,'22:20','گل کر نکل جاتا ہے اُس سے جو کچھ اُنکے پیٹ میں ہے اور کھال بھی');
INSERT INTO "al_hind" VALUES(22,21,'22:21','اور اُنکے واسطے ہتوڑے ہیں لوہے کے ');
INSERT INTO "al_hind" VALUES(22,22,'22:22','جب چاہیں کہ نکل پڑیں دوزخ سے گھٹنے کے مارے پھر ڈال دیے جائیں اُسکے اندر اور چکھتے رہو جلنے کا عذاب ');
INSERT INTO "al_hind" VALUES(22,23,'22:23','بیشک اللہ داخل کرے گا اُنکو جو یقین لائے اور کیں بھلائیاں باغوں میں بہتی ہیں انکے نیچےنہریں گہنا پہنائیں گے اُنکو وہاں کنگن سونے کے اور موتی اور اُنکی پوشاک ہے وہاں ریشم کی ');
INSERT INTO "al_hind" VALUES(22,24,'22:24','اور راہ پائی انہوں نے ستھری بات کی اور پائی اُس تعریفوں والے کی راہ ');
INSERT INTO "al_hind" VALUES(22,25,'22:25','جو لوگ منکر ہوئے اور روکتے ہیں اللہ کی راہ سے اور مسجد حرام سے جو ہم نے بنائی سب لوگوں کے واسطے برابر ہے اس میں رہنے والا اور باہر سے آنے والا اور جو اُس میں چاہے ٹیڑھی راہ شرارت سے اسے ہم چکھائیں گے ایک عذاب دردناک ');
INSERT INTO "al_hind" VALUES(22,26,'22:26','اور جب ٹھیک کر دی ہم نے ابراہیم کو جگہ اُس گھر کی کہ شریک نہ کرنا میرے ساتھ کسی کو اور پاک رکھ میرا گھر طواف کرنے والوں کے واسطے اور کھڑے رہنے والوں کے اور رکوع و سجود والوں کے ');
INSERT INTO "al_hind" VALUES(22,27,'22:27','اور پکار دے لوگوں میں حج کے واسطے کہ آئیں تیری طرف پیروں چل کر اور سوار ہو کر دبلے دبلے اونٹوں پر چلے آئیں راہوں دور سے ');
INSERT INTO "al_hind" VALUES(22,28,'22:28','تاکہ پہنچیں اپنے فائدہ کی جگہوں پر اور پڑھیں اللہ کا نام کئ دن جو معلوم ہیں ذبح پر چوپایوں مواشی کے جو اللہ نے دیے ہیں اُنکو سو کھاؤ اُس میں سے اور کِھلاؤ برے حال کے محتاج کو ');
INSERT INTO "al_hind" VALUES(22,29,'22:29','پھر چاہیئے کہ ختم کر دیں اپنا میل کچیل اور پوری کریں اپنی منتیں اور طواف کریں اس قدیم گھر کا ');
INSERT INTO "al_hind" VALUES(22,30,'22:30','یہ سن چکے اور جو کوئی بڑائی رکھے اللہ کی حرمتوں کی سو وہ بہتر ہے آُسکے لئے اپنے رب کے پاس اور حلال ہیں تمکو چوپائے مگر جو تمکو سناتےہیں سو بچتے رہو بتوں کی گندگی سے اور بچتے رہو جھوٹی بات سے ');
INSERT INTO "al_hind" VALUES(22,31,'22:31','ایک اللہ کی طرف ہو کر نہ کہ اُسکے ساتھ شریک بنا کر اور جس نے شریک بنایا اللہ کا سو جیسے گر پڑا آسمان سے پھر اُچکتے ہیں اُسکو اڑنے والے مردار خوار یا جا ڈالا اُسکو ہوا نے نے کسی دور مکان میں ');
INSERT INTO "al_hind" VALUES(22,32,'22:32','یہ سن چکے اور جو کوئی ادب رکھے اللہ کے نام لگی چیزوں کا سو وہ دل کی پرہیزگاری کی بات ہے ');
INSERT INTO "al_hind" VALUES(22,33,'22:33','تمہارے واسطے چوپایوں میں فائدے ہیں ایک مقرر وعدہ تک پھر اُنکو پہنچنا اُس قدیم گھر تک ');
INSERT INTO "al_hind" VALUES(22,34,'22:34','اور ہر امت کے واسطے ہم نے مقرر کر دی ہے قربانی کہ یاد کریں اللہ کے نام ذبح پر چوپایوں کے جو اُنکو (اللہ نے دیے) سو اللہ تمہارا ایک اللہ ہے سو اسی کے حکم میں رہو اور بشارت سنا دے عاجزی کرنے والوں کو ');
INSERT INTO "al_hind" VALUES(22,35,'22:35','وہ کہ جب نام لیجئے اللہ کا ڈر جائیں انکے دل اور سہنے والے اُسکو جو اُن پر پڑے اور قائم رکھنے والے نماز کے اورہمارا دیا ہوا کچھ خرچ کرتے رہتے ہیں ');
INSERT INTO "al_hind" VALUES(22,36,'22:36','اور کعبہ کے چڑھانے کے اونٹ ٹھہرائے ہیں ہم نے تمہارے واسطے نشانی اللہ کے نام کی تمہارے واسطے اس میں بھلائی ہے سو پڑھو اُن پر نام اللہ کا قطار باندھ کر پھر جب گر پڑے اُنکی کروٹ تو کھاؤ اُس میں سے اور کھلاؤ صبر سے بیٹھے کو اوربیقراری کرتے کو اسی طرح تمہارے بس میں کر دیا ہم نے اُن جانوروں کو تاکہ تم احسان مانو ');
INSERT INTO "al_hind" VALUES(22,37,'22:37','اللہ کو نہیں پہنچتا اُن کا گوشت اور نہ اُن کا لہو لیکن اُسکو پہنچتا ہے تمہارے دل کا ادب اسی طرح اُنکو بس میں کر دیا تمہارے کہ اللہ کی بڑائی پڑھو اس بات پر کہ تم کو راہ سجھائی اور بشارت سنا دے نیکی والوں کو ');
INSERT INTO "al_hind" VALUES(22,38,'22:38','اللہ دشمنوں کو ہٹا دے گا ایمان والوں سے اللہ کو خوش نہیں آتا کوئی دغاباز نا شکر ');
INSERT INTO "al_hind" VALUES(22,39,'22:39','حکم ہوا اُن لوگوں کو جن سے کافر لڑتے ہیں اس واسطے کہ اُن پر ظلم ہوا اور اللہ اُنکی مدد کرنے پر قادر ہے ');
INSERT INTO "al_hind" VALUES(22,40,'22:40','وہ لوگ جنکو نکالا اُنکے گھروں سے اور دعویٰ کچھ نہیں سوائے اسکے کہ وہ کہتے ہیں ہمارا رب اللہ ہے اور اگر نہ ہٹایا کرتا اللہ لوگوں کو ایک کو دوسرے سے تو ڈھائے جاتے تکئے اور مدرسے اور عبادت خانے اور مسجدیں جن میں نام پڑھا جاتا ہے اللہ کا بہت اور اللہ مقرر مدد کرے گا اُسکی جو مدد کرے گا اُسکی بیشک اللہ زبردست ہے زور والا ');
INSERT INTO "al_hind" VALUES(22,41,'22:41','وہ لوگ کہ اگر ہم اُنکو قدرت دیں ملک میں تو وہ قائم رکھیں نماز اور دیں زکوٰۃ اور حکم کر دیں بھلے کام کا اور منع کریں برائی سے اور اللہ کے اختیار میں ہے آخر ہر کام کا ');
INSERT INTO "al_hind" VALUES(22,42,'22:42','اور اگر تجھ کو جھٹلائیں تو اُن سے پہلے جھٹلا چکی ہے نوح کی قوم اور عاد اور ثمود');
INSERT INTO "al_hind" VALUES(22,43,'22:43','اور ابراہیم کی قوم اور لوط کی قوم');
INSERT INTO "al_hind" VALUES(22,44,'22:44','اور مدین کے لوگ اور موسٰی کو جھٹلایا پھر میں نے ڈھیل دی منکروں کر پھر پکڑ لیا اُنکو تو کیسا ہوا میرا انکار ');
INSERT INTO "al_hind" VALUES(22,45,'22:45','سو کتنی بستیاں ہم نے غارت کر ڈالیں اور وہ گنہگار تھیں اب وہ گری پڑی ہیں اپنی چھتوں پر اور کتنے کونئیں نکمے پڑے اور کتنے محل گچ کاری کے ');
INSERT INTO "al_hind" VALUES(22,46,'22:46','کیا سیر نہیں کی ملک کی جو اُنکے دل ہوتے جن سے سمجھتے یا کان ہوتے جن سے سنتے سو کچھ آنکھیں اندھی نہیں ہوتیں پر اندھے ہو جاتے ہیں دل جو سینوں میں ہیں ');
INSERT INTO "al_hind" VALUES(22,47,'22:47','اور تجھ سے جلدی مانگتے ہیں عذاب اور اللہ ہرگز نہ ٹالے گا اپنا وعدہ اور ایک دن تیرے رب کے یہاں ہزار برس کے برابر ہوتا ہے جو تم گنتے ہو ');
INSERT INTO "al_hind" VALUES(22,48,'22:48','اور کتنی بستیاں ہیں کہ میں نے اُنکو ڈھیل دی اور وہ گنہگار تھیں پھر میں نے اُنکو پکڑا اور میری طرف پھر کر آنا ہے ');
INSERT INTO "al_hind" VALUES(22,49,'22:49','تو کہہ اے لوگو میں تو ڈر سنا دینے والا ہوں تمکو کھول کر ');
INSERT INTO "al_hind" VALUES(22,50,'22:50','سو جو لوگ یقین لائے اور کیں بھلائیاں اُنکے گناہ بخش دیتے ہیں اور اُنکو روزی ہے عزت کی ');
INSERT INTO "al_hind" VALUES(22,51,'22:51','اور جو دوڑے ہماری آیتوں کے ہرانے کو وہی ہیں دوزخ کے رہنے والے');
INSERT INTO "al_hind" VALUES(22,52,'22:52','اور جو رسول بھیجا ہم نے تجھ سے پہلے یا نبی سو جب لگا خیال باندھنے شیطان نے ملا دیا اُسکے خیال میں پھر اللہ مٹا دیتا ہے شیطان کا ملایا ہوا پھر پکی کر دیتا ہے اپنی باتیں اور اللہ سب خبر رکھتا ہے حکمتوں والا ');
INSERT INTO "al_hind" VALUES(22,53,'22:53','اس واسطے کہ جو کچھ شیطان نے ملایا اس سے جانچے اُنکو کہ جنکے دل میں روگ ہیں اور جنکے دل سخت ہیں اور گنہگار تو ہیں مخالفت میں دور جا پڑے');
INSERT INTO "al_hind" VALUES(22,54,'22:54','اور اس واسطے کہ معلوم کر لیں وہ لوگ جنکو سمجھ ملی ہے کہ یہ تحقیق ہے تیرے رب کی طرف سے پھر اس پر یقین لائیں اور نرم ہو جائیں اُسکے آگے اُنکے دل اور اللہ سمجھانے والا ہے یقین لانے والوں کو راہ سیدھی ');
INSERT INTO "al_hind" VALUES(22,55,'22:55','اور منکروں کوہمیشہ رہے گا اُس میں دھوکا جب تک کہ آ پہنچے اُن پر قیامت بیخبری میں یا آ پہنچے اُن پر آفت ایسے دن کی جس میں راہ نہیں خلاصی کی ');
INSERT INTO "al_hind" VALUES(22,56,'22:56','راج اُس دن اللہ کا ہے اُن میں فیصلہ کرے گا سو جو یقین لائے اور کیں بھلائیاں نعمت کے باغوں میں میں ہیں');
INSERT INTO "al_hind" VALUES(22,57,'22:57','اور جو منکر ہوئے اور جھٹلائیں ہماری باتیں سو انکے لئے ہے ذلت کا عذاب');
INSERT INTO "al_hind" VALUES(22,58,'22:58','اور جو لوگ گھر چھوڑ آئے اللہ کی راہ میں پھر مارے گئے یا مر گئے البتہ اُنکو دے گا اللہ روزی خاصی اور االلہ ہے سب سے بہتر روزی دینے والا');
INSERT INTO "al_hind" VALUES(22,59,'22:59','البتہ پہنچائے گا اُنکو ایک جگہ جسکو پسند کریں گے اور اللہ سب کچھ جانتا ہے تحمل والا ');
INSERT INTO "al_hind" VALUES(22,60,'22:60','یہ سن چکے اور جس نے بدلہ لیا جیسا کہ اُسکو دکھ دیا تھا پھر اُس پر کوئی زیادتی کرے تو البتہ اُسکی مدد کرے گا اللہ بیشک اللہ درگذر کرنے والا بخشنے والا ہے ');
INSERT INTO "al_hind" VALUES(22,61,'22:61','یہ اس واسطے کہ اللہ لے لیتا ہے رات کو دن میں اور دن کو رات میں اور اللہ سنتا دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(22,62,'22:62','یہ اس واسطے کہ اللہ وہی ہے صحیح اور جسکو پکارتے ہیں اُسکے سوائے وہی ہے غلط اور اللہ وہی ہے سب سے اوپر بڑا ');
INSERT INTO "al_hind" VALUES(22,63,'22:63','تو نے نہیں دیکھا کہ اللہ نے اتارا آسمان سے پانی پھر زمین ہو جاتی ہے سر سبز بیشک اللہ جانتا ہے چھپی تدبیریں خبردار ہے ');
INSERT INTO "al_hind" VALUES(22,64,'22:64','اُسی کا ہے جو کچھ ہے آسمان اور زمین میں اور اللہ وہی ہے بے پروا تعریفوں والا ');
INSERT INTO "al_hind" VALUES(22,65,'22:65','تو نے نہ دیکھا کہ اللہ نے بس میں کردیا تمہارے جو کچھ ہے زمین میں اور کشتی کو جو چلتی ہے دریا میں اُسکے حکم سے اور تھام رکھتا ہے آسمان کو اس سے کہ گر پڑے زمین پر مگر اُسکے حکم سے بیشک اللہ لوگوں پر نرمی کرنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(22,66,'22:66','اور اُسی نے تمکو جلایا پھر مارتا ہے پھر زندہ کرے گا بیشک انسان ناشکر ہے ');
INSERT INTO "al_hind" VALUES(22,67,'22:67','ہر امت کے لئے ہم نے مقرر کر دی ایک راہ بندگی کی کہ وہ اُسی طرح کرتے ہیں بندگی سو چاہئے تجھ سے جھگڑا نہ کریں اس کام میں اور تو بلائے جا اپنے رب کی طرف بیشک تو ہے سیدھی راہ پر سوجھ والا');
INSERT INTO "al_hind" VALUES(22,68,'22:68','اور اگر تجھ سے جھگڑنے لگیں تو تو کہہ اللہ بہتر جانتا ہے جو تم کرتے ہو');
INSERT INTO "al_hind" VALUES(22,69,'22:69','اللہ فیصلہ کرے گا تم میں قیامت کے دن جس چیز میں تمہاری راہ جدا جدا تھی ');
INSERT INTO "al_hind" VALUES(22,70,'22:70','کیا تجھ کو معلوم نہیں کہ اللہ جانتا ہے جو کچھ ہے آسمان اور زمیں میں یہ سب لکھا ہوا ہے کتاب میں یہ اللہ پر آسان ہے ');
INSERT INTO "al_hind" VALUES(22,71,'22:71','اور پوجتے ہیں اللہ کے سوائے اس چیز کو جسکی سند نہیں اتاری اُس نے اور جسکی خبر نہیں اُنکو اور بے انصافوں کا کوئی نہیں مددگار ');
INSERT INTO "al_hind" VALUES(22,72,'22:72','اور جب سنائے اُنکو ہماری آیتیں صاف تو پہچانے تو منکروں کے منہ کی بری شکل نزدیک ہوتے ہیں کہ حملہ کر پڑیں اُن پر جو پڑھتے ہیں اُنکے پاس ہماری آیتیں تو کہہ میں تمکو بتلاؤں ایک چیز اس سے بدتر وہ آگ ہے اس کا وعدہ کر دیا ہے اللہ نے منکروں کو اور وہ بہت بری ہے پھر جانے کی جگہ ');
INSERT INTO "al_hind" VALUES(22,73,'22:73','اے لوگو ایک مثل کہی ہے سو اس پر کان رکھو جنکو تم پوجتے ہو اللہ کے سوائے ہرگز نہ بنا سکیں گے ایک مکھی اگرچہ سارے جمع ہو جائیں اور اگر کچھ چھین لے اُن سے مکھی چھڑا نہ سکیں وہ اس سے بودا ہے چاہنے والا اور جنکو چاہتا ہے ');
INSERT INTO "al_hind" VALUES(22,74,'22:74','اللہ کی قدر نہیں سمجھے جیسی اُسکی قدر ہے بیشک اللہ زور آور ہے زبردست ');
INSERT INTO "al_hind" VALUES(22,75,'22:75','اللہ چھانٹ لیتا ہے فرشتوں میں پیغام پہنچانے والے اور آدمیوں میں اللہ سنتا دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(22,76,'22:76','جانتا ہے جو کچھ اُنکے آگے ہے اور جو کچھ اُنکے پیچھے اور اللہ تک پہنچ ہے ہر کام کی ');
INSERT INTO "al_hind" VALUES(22,77,'22:77','اے ایمان والو رکوع کرو اور سجدہ کرو اور بندگی کرو اپنے رب کی اور بھلائی کرو تاکہ تمہارا بھلا ہو ');
INSERT INTO "al_hind" VALUES(22,78,'22:78','اور محنت کرو اللہ کے واسطے جیسی کہ چاہئے اُسکے واسطے محنت اس نے تم کو پسند کیا اور نہیں رکھی تم پر دین میں کچھ مشکل دین تمہارے باپ ابراہیم کا اُسی نے نام رکھا تمہارا مسلمان پہلے سے اور اس قرآن میں تاکہ رسول ہو بتانے والا تم پر اور تم ہو بتانے والے لوگوں پر سو قائم رکھو نماز اور دیتے رہو زکوٰۃ اور مضبوط پکڑو اللہ کو وہ تمہارا مالک ہے سو خوب مالک ہے اور خوب مددگار ');
INSERT INTO "al_hind" VALUES(23,1,'23:1','کام نکال لے گئے ایمان والے');
INSERT INTO "al_hind" VALUES(23,2,'23:2','جو اپنی نماز میں جھکنے والے ہیں ');
INSERT INTO "al_hind" VALUES(23,3,'23:3','اور جو نکمی بات پر دھیان نہیں کرتے ');
