INSERT INTO "al_hind" VALUES(23,7,'23:7','پھر جو کوئی ڈھونڈے اسکےسوا سو وہی ہیں حد سے بڑھنے والے ');
INSERT INTO "al_hind" VALUES(23,8,'23:8','اور جو اپنی امانتوں سے اور اپنے قرار سے خبردار ہیں ');
INSERT INTO "al_hind" VALUES(23,9,'23:9','اور جو اپنی نمازوں کی خبر رکھتے ہیں ');
INSERT INTO "al_hind" VALUES(23,10,'23:10','وہی ہیں میراث لینے والے');
INSERT INTO "al_hind" VALUES(23,11,'23:11','جو میراث پائیں گے باغ ٹھنڈی چھاؤں کے وہ اسی میں ہمیشہ رہیں گے');
INSERT INTO "al_hind" VALUES(23,12,'23:12','اور ہم نے بنایا آدمی کو چنی ہوئی مٹی سے ');
INSERT INTO "al_hind" VALUES(23,13,'23:13','پھر ہم نے رکھا اُسکو پانی کی بوند کر کے ایک جمے ہوئے ٹھکانہ میں ');
INSERT INTO "al_hind" VALUES(23,14,'23:14','پھر بنایا اس بوند سے لہو جما ہوا پھر بنائی اس لہو جمے ہوئے سے گوشت کی بوٹی پھر بنائیں اُس بوٹی سےہڈیاں پھر پہنایا ہڈیوں پر گوشت پھر اٹھا کھڑا کیا اسکو ایک نئی صورت میں سو بڑی برکت اللہ کی جو سب سے بہتر بنانے والا ہے ');
INSERT INTO "al_hind" VALUES(23,15,'23:15','پھر تم اسکے بعد مرو گے ');
INSERT INTO "al_hind" VALUES(23,16,'23:16','پھر تم قیامت کے دن کھڑے کئےجاؤ گے ');
INSERT INTO "al_hind" VALUES(23,17,'23:17','اور ہم نے بنائے ہیں تمہارے اوپر سات رستے اور ہم نہیں ہیں خلق سے بے خبر ');
INSERT INTO "al_hind" VALUES(23,18,'23:18','اور اتارا ہم نے آسمان سے پانی ماپ کر پھر اُسکو ٹھہرا دیا زمین میں اور ہم اُسکو لیجائیں تو لیجا سکتے ہیں ');
INSERT INTO "al_hind" VALUES(23,19,'23:19','پھر اگا دیے تمہارے واسطے اُس سے باغ کھجور اور انگور کے تمہارے واسطے ان میں میوے ہیں بہت اور اُنہی میں سے کھاتے ہو ');
INSERT INTO "al_hind" VALUES(23,20,'23:20','اور وہ درخت جو نکلتا ہے سینا پہاڑ سے لے اگتا ہے تیل اور روٹی ڈبونا کھانے والوں کے واسطے ');
INSERT INTO "al_hind" VALUES(23,21,'23:21','اور تمہارے چوپایوں میں دھیان کرنے کی بات ہے پلاتے ہیں ہم تمکو اُنکے پیٹ کی چیز سے اور تمہارے لئے اُن میں بہت فائدے ہیں اور بعضوں کو کھاتے ہو ');
INSERT INTO "al_hind" VALUES(23,22,'23:22','اور اُن پر اور کشتیوں پر لدے پھرتے ہو ');
INSERT INTO "al_hind" VALUES(23,23,'23:23','اور ہم نے بھیجا نوح کو اُسکی قوم کے پاس تو اُس نے کہا اے قوم بندگی کرو اللہ کی تمہارا کوئی حاکم نہیں اُسکے سوائے کیا تم ڈرتے نہیں');
INSERT INTO "al_hind" VALUES(23,24,'23:24','تب بولے سردار جو کافر تھے اُسکی قوم میں یہ کیا ہے آدمی ہے جیسے تم چاہتا ہے کہ بڑائی کرے تم پر اور اگر اللہ چاہتا تو اتارتا فرشتے ہم نے یہ نہیں سنا اپنے اگلے باپ دادوں میں ');
INSERT INTO "al_hind" VALUES(23,25,'23:25','اور کچھ نہیں یہ ایک مرد ہے کہ اسکو سودا ہے سو راہ دیکھو اسکی ایک وقت تک ');
INSERT INTO "al_hind" VALUES(23,26,'23:26','بولا اے رب تو مدد کر میری کہ انہوں نے مجھ کو جھٹلایا ');
INSERT INTO "al_hind" VALUES(23,27,'23:27','پھر ہم نے حکم بھیجا اُسکو کہ بنا کشتی ہماری آنکھوں کے سامنے اور ہمارے حکم سے پھر جب پہنچے ہمارا حکم اور ابلے تنور تو تو ڈال لے کشتی میں ہر چیز کا جوڑا دو دو اور اپنے گھر کے لوگ مگر جسکی قسمت میں پہلے سے ٹھہر چکی ہے بات اور مجھ سے بات نہ کر ان ظالموں کے واسطے بیشک اُنکو ڈوبنا ہے ');
INSERT INTO "al_hind" VALUES(23,28,'23:28','پھر جب چڑھ چکے تو اور جو تیرے ساتھ ہے کشی پر تو کہہ شکر اللہ کا جس نے چھڑایا ہمکو گنہگار لوگوں سے ');
INSERT INTO "al_hind" VALUES(23,29,'23:29','اور کہہ اے رب اتار مجھ کو برکت کا اتارنا اور تو ہے بہتر اتارنے والا ');
INSERT INTO "al_hind" VALUES(23,30,'23:30','اس میں نشانیاں ہیں اور ہم ہیں جانچنے والے ');
INSERT INTO "al_hind" VALUES(23,31,'23:31','پھر پیدا کی ہم نے اُن سے پیچھے ایک جماعت اور ');
INSERT INTO "al_hind" VALUES(23,32,'23:32','پھر بھیجا ہم نے اُن میں ایک رسول اُن میں کا کہ بندگی کرو اللہ کی کوئی نہیں تمہارا حاکم اُسکے سوائے پھر کیا تم ڈرتے نہیں');
INSERT INTO "al_hind" VALUES(23,33,'23:33','اور بولے سردار اُسکی قوم کے جو کافر تھے اور جھٹلاتے تھے آخرت کی ملاقات کو اور آرام دیا تھا اُنکو ہم نے دنیا کی زندگی میں اور کچھ نہیں یہ ایک آدمی ہے جیسے تم، کھاتا ہے جس قسم سے تم کھاتے ہو اور پیتا ہے جس قسم سے تم پیتے ہو ');
INSERT INTO "al_hind" VALUES(23,34,'23:34','اور کہیں تم چلنے لگے کہنے پر ایک آدمی کے اپنے برابر کے تو تم بیشک خراب ہوئے ');
INSERT INTO "al_hind" VALUES(23,35,'23:35','کیا تم کو وعدہ دیتا ہے کہ جب تم مر جاؤ اور ہو جاؤ مٹی اور ہڈیاں تو تمکو نکلنا ہے');
INSERT INTO "al_hind" VALUES(23,36,'23:36','کہاں ہو سکتا ہے کہاں ہوسکتا ہے جو تم سے وعدہ ہوتا ہے ');
INSERT INTO "al_hind" VALUES(23,37,'23:37','اور کچھ نہیں یہی جینا ہے ہمارا دنیا کا مرتے ہیں اور جیتے ہیں اور ہمکو پھر اٹھنا نہیں ');
INSERT INTO "al_hind" VALUES(23,38,'23:38','اور کچھ نہیں یہ ایک مرد ہے باندھ لایا ہے اللہ پر جھوٹ اور اُسکو ہم نہیں ماننے والے');
INSERT INTO "al_hind" VALUES(23,39,'23:39','بولا اے رب میری مدد کر کہ انہوں نے مجھ کو جھٹلایا ');
INSERT INTO "al_hind" VALUES(23,40,'23:40','فرمایا اب تھوڑے دنوں میں صبح کو رہ جائیں گے پچتاتے ');
INSERT INTO "al_hind" VALUES(23,41,'23:41','پھر پکڑا اُنکو چنگھاڑ نے تحقیق پھر کر دیا ہم نے اُنکو کوڑا سو دور ہو جائیں گنہگار لوگ ');
INSERT INTO "al_hind" VALUES(23,42,'23:42','پھر پیدا کیں ہم نے اُن سے پیچھے جماعتیں اور');
INSERT INTO "al_hind" VALUES(23,43,'23:43','نہ آگے جائے کوئی قوم اپنے وعدہ سے اور نہ پیچھے رہے ');
INSERT INTO "al_hind" VALUES(23,44,'23:44','پھر بھیجتے رہے ہم اپنے رسول لگاتار جہاں پہنچا کسی امت کے پاس اُن کا رسول اُسکو جھٹلا دیا پھر چلاتے گئے ہیں ہم ایک کے پیچھے دوسرے اور کر ڈالا اُنکو کہانیاں سو دو ہو جائیں جو لوگ نہیں مانتے ');
INSERT INTO "al_hind" VALUES(23,45,'23:45','پھر بھیجا ہم نے موسٰی اور اُسکے بھائی ہارون کو اپنی نشانیاں دیکر اور کھلی سند');
INSERT INTO "al_hind" VALUES(23,46,'23:46','فرعون اور اُسکے سرداروں کے پاس پھر لگے بڑائی کرنے اور وہ لوگ زور پر چڑھ رہے تھے ');
INSERT INTO "al_hind" VALUES(23,47,'23:47','سو بولے کیا ہم مانیں گے اپنی برابر کے دو آدمیوں کو اور اُنکی قوم ہمارے تابعدار ہیں ');
INSERT INTO "al_hind" VALUES(23,48,'23:48','پھر جھٹلایا اُن دونوں کو پھر ہو گئے غارت ہونے والوں میں');
INSERT INTO "al_hind" VALUES(23,49,'23:49','اور ہم نے دی موسٰی کو کتاب تاکہ وہ راہ پائیں ');
INSERT INTO "al_hind" VALUES(23,50,'23:50','اور بنایا ہم نے مریم کے بیٹے اور اُسکی ماں کو ایک نشانی اور اُنکو ٹھکانا دیا ایک ٹیلہ پر جہاں ٹھہرنے کا موقع تھا اور پانی نتھرا ');
INSERT INTO "al_hind" VALUES(23,51,'23:51','اے رسولو کھاؤ ستھری چیزیں اور کام کرو بھلا جو تم کرتے ہو میں جانتا ہوں ');
INSERT INTO "al_hind" VALUES(23,52,'23:52','اور یہ لوگ ہیں تمہارے دین کے سب ایک دین پر اور میں ہوں تمہارا رب سو مجھ سے ڈرتے رہو');
INSERT INTO "al_hind" VALUES(23,53,'23:53','پھر پھوٹ ڈال کر کر لیا اپنا کام آپس میں ٹکڑے ٹکڑے ہرفرقہ جو اُنکے پاس ہے اُس پر ریجھ رہے ہیں ');
INSERT INTO "al_hind" VALUES(23,54,'23:54','سو چھوڑ دے تو اُنکو اُنکی بیہوشی میں ڈوبے ایک وقت تک ');
INSERT INTO "al_hind" VALUES(23,55,'23:55','کیا وہ خیال کرتےہیں کہ یہ جو ہم اُنکو دیے جاتے ہیں مال اور اولاد');
INSERT INTO "al_hind" VALUES(23,56,'23:56','سو دوڑ دوڑ کر پہنچا رہے ہیں ہم اُنکو بھلائیاں یہ بات نہیں وہ سمجھتے نہیں ');
INSERT INTO "al_hind" VALUES(23,57,'23:57','البتہ جو لوگ اپنے رب کے خوف سے اندیشہ رکھتے ہیں ');
INSERT INTO "al_hind" VALUES(23,58,'23:58','اور جو لوگ اپنے رب کی باتوں پر یقین کرتے ہیں ');
INSERT INTO "al_hind" VALUES(23,59,'23:59','اور جو لوگ اپنے رب کے ساتھ کسی کو شریک نہیں مانتے ');
INSERT INTO "al_hind" VALUES(23,60,'23:60','اور جو لوگ کہ دیتے ہیں جو کچھ دیتے ہیں وہ اور اُنکے دل ڈر رہے ہیں اس لئے کہ انکو اپنے رب کی طرف لوٹ کر جانا ہے ');
INSERT INTO "al_hind" VALUES(23,61,'23:61','وہ لوگ دوڑ دوڑ کر لیتے ہیں بھلائیاں اور وہ ان پر پہنچے سب سے آگے ');
INSERT INTO "al_hind" VALUES(23,62,'23:62','اور ہم کسی پر بوجھ نہیں ڈالتے مگر اسکی گنجائش کے موافق اور ہمارے پاس لکھا ہوا ہے جو بولتا ہے سچ اور ان پر ظلم نہ ہو گا ');
INSERT INTO "al_hind" VALUES(23,63,'23:63','کوئی نہیں انکے دل بیہوش ہیں اس طرف اور انکو اور کام لگ رہے ہیں اسکے سوائے کہ وہ انکو کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(23,64,'23:64','یہاں تک کہ جب پکڑیں گے ہم اُنکے آسودہ لوگوں کو آفت میں تبھی وہ لگیں گے چلانے');
INSERT INTO "al_hind" VALUES(23,65,'23:65','مت چلاؤ آج کے دن تم ہم سے چھوٹ نہ سکو گے ');
INSERT INTO "al_hind" VALUES(23,66,'23:66','تم کو سنائی جاتی تھیں میری آیتیں تو تم ایڑیوں پر الٹے بھاگتے تھے۔');
INSERT INTO "al_hind" VALUES(23,67,'23:67','اس سے تکبر کر کے ایک قصہ گو کو چھوڑ کر چلے گئے ');
INSERT INTO "al_hind" VALUES(23,68,'23:68','سو کیا انہوں نے دھیان نہیں کیا اس کلام میں یا آئی ہے ان کے پاس ایسی چیز جو نہ تھی ان کے پہلے باپ دادوں کے پاس ');
INSERT INTO "al_hind" VALUES(23,69,'23:69','یا پہچانا نہیں انہوں نے اپنے پیغام لانے والوں کو سو وہ اس کو اوپرا سمجھتے ہیں ');
INSERT INTO "al_hind" VALUES(23,70,'23:70','یا کہتے ہیں ان کو سودا ہے کوئی نہیں وہ تو لایا ہے ان کے پاس سچی باتے اور ان بہتوں کو سچی بات بری لگتی ہے ');
INSERT INTO "al_hind" VALUES(23,71,'23:71','اور اگر سچا رب چلے ان کی خوشی پر تو خراب ہو جائیں آسمان اور زمین اور جو کوئی ان میں ہے کوئی نہیں ہم نے پہنچائی ہے ان کو ان کی نصیحت سو وہ اپنی نصیحت کو دھیان نہیں کرتے ');
INSERT INTO "al_hind" VALUES(23,72,'23:72','یا تو ان سے مانگتا ہے کچھ محصول سو محصول تیرے رب کا بہتر ہے اور وہ ہے بہتر روزی دینے والا ');
INSERT INTO "al_hind" VALUES(23,73,'23:73','اور تو تو بلاتا ہے ان کو سیدھی راہ پر');
INSERT INTO "al_hind" VALUES(23,74,'23:74','اور جو لوگ نہیں مانتے آخرت کو راہ سے ٹیڑھے ہو گئے ہیں ');
INSERT INTO "al_hind" VALUES(23,75,'23:75','اور اگر ہم ان پر رحم کریں اور کھول دیں جو تکلیف پہنچی ان کو تو بھی برابر لگے رہیں گے اپنی شرارت میں بہکےہوئے ');
INSERT INTO "al_hind" VALUES(23,76,'23:76','اور ہم نے پکڑا تھا ان کو آفت میں پھر نہ عاجزی کی اپنے رب کے آگے اور نہ گڑگڑائے ');
INSERT INTO "al_hind" VALUES(23,77,'23:77','یہاں تک کہ جب کھول دیں ہم ان پر دروازہ ایک سخت آفت کا تب اس میں ان کی آس ٹوٹے گی ');
INSERT INTO "al_hind" VALUES(23,78,'23:78','اور اسی نے بنا دئے تمہارےکان اور آنکھیں اور دل تم بہت تھوڑا حق مانتے ہو ');
INSERT INTO "al_hind" VALUES(23,79,'23:79','اور اسی نے تم کو پھیلا رکھا ہے زمین میں اور اسی کی طرف جمع ہو کر جاؤ گے ');
INSERT INTO "al_hind" VALUES(23,80,'23:80','اور وہی ہے جلاتا اور مارتا اور اسی کا کام ہے بدلنا رات اور دن کا سو کیا تم کو سمجھ نہیں ');
INSERT INTO "al_hind" VALUES(23,81,'23:81','کوئی بات نہیں یہ تو وہی کہہ رہے ہیں جیسا کہا کرتے تھے پہلے لوگ');
INSERT INTO "al_hind" VALUES(23,82,'23:82','کہتے ہیں کیا جب ہم مر گئے اور ہو گئے مٹی اور ہڈیاں کیا ہم کو زندہ ہو کر اٹھنا ہے');
INSERT INTO "al_hind" VALUES(23,83,'23:83','وعدہ دیا جاتا ہے ہم کو اور ہمارے باپ دادوں کو یہی پہلے سے اور کچھ بھی نہیں یہ نقلیں ہیں پہلوں کی ');
INSERT INTO "al_hind" VALUES(23,84,'23:84','تو کہہ کس کی ہے زمین اور جو کوئی اس میں ہے بتاؤ اگر تم جانتے ہو');
INSERT INTO "al_hind" VALUES(23,85,'23:85','اب کہیں گے سب کچھ اللہ کا ہے تو کہہ پھر تم سوچتے نہیں ');
INSERT INTO "al_hind" VALUES(23,86,'23:86','تو کہہ کون ہے مالک ساتوں آسمان کا اور مالک اس بڑے تخت کا');
INSERT INTO "al_hind" VALUES(23,87,'23:87','اب بتائیں گے اللہ کو تو کہہ پھر تم ڈرتے نہیں ');
INSERT INTO "al_hind" VALUES(23,88,'23:88','تو کہہ کس کے ہاتھ میں ہے حکومت ہر چیز کی اور وہ بچا لیتا ہے اور اس سے کوئی بچا نہیں سکتا بتاؤ اگر تم جانتے ہو');
INSERT INTO "al_hind" VALUES(23,89,'23:89','اب بتائیں گے اللہ کو تو کہہ پھر کہاں سے تم پر جادو آ پڑتا ہے ');
INSERT INTO "al_hind" VALUES(23,90,'23:90','کوئی نہیں ہم نے ان کو پہنچایا سچ اور وہ البتہ جھوٹے ہیں ');
INSERT INTO "al_hind" VALUES(23,91,'23:91','اللہ نے کوئی بیٹا نہیں کیا اور نہ اس کے ساتھ کسی کا حکم چلے یوں ہوتا تو لیجاتا ہر حکم والا اپنی بنائی چیز کو اور چڑھائی کرتا ایک پر ایک اللہ نرالا (پاک) ہے ان کی بتلائی باتوں سے ');
INSERT INTO "al_hind" VALUES(23,92,'23:92','جاننے والا چھپے اور کھلے کا وہ بہت اوپر ہے اس سے جسکو یہ شریک بتلاتے ہیں ');
INSERT INTO "al_hind" VALUES(23,93,'23:93','تو کہہ اے رب اگر تو دکھانے لگے مجھ کو جو ان سے وعدہ ہوا ہے');
INSERT INTO "al_hind" VALUES(23,94,'23:94','تو اے رب مجھ کو نہ کریو ان گنہگار لوگوں میں ');
INSERT INTO "al_hind" VALUES(23,95,'23:95','اور ہم کو قدرت ہے کہ تجھ کو دکھلا دیں جو ان سےوعدہ کر دیا ہے');
INSERT INTO "al_hind" VALUES(23,96,'23:96','بری بات کے جواب میں وہ کہہ جو بہتر ہے ہم خوب جانتے ہیں جو یہ بتاتے ہیں ');
INSERT INTO "al_hind" VALUES(23,97,'23:97','اور کہہ اے رب میں تیری پناہ چاہتا ہوں شیطان کی چھیڑ سے ');
INSERT INTO "al_hind" VALUES(23,98,'23:98','اور پناہ تیری چاہتا ہوں اے رب اس سے کہ میرے پاس آئیں ');
INSERT INTO "al_hind" VALUES(23,99,'23:99','یہاں تک کہ جب پہنچے ان میں کسی کو موت کہے گا اے رب مجھ کو پھر بھیجدو');
INSERT INTO "al_hind" VALUES(23,100,'23:100','شاید کچھ میں بھلا کام کر لوں اس میں جو پیچھے چھوڑ آیا ہر گز نہیں یہ ایک بات ہے کہ وہی کہتا ہے اور ان کے پیچھے پردہ ہے اس دن تک کہ اٹھائے جائیں ');
INSERT INTO "al_hind" VALUES(23,101,'23:101','پھر جب پھونک ماریں صور میں تو نہ قرابتیں ہیں ان میں اس دن اور نہ ایک دوسرے کو پوچھے ');
INSERT INTO "al_hind" VALUES(23,102,'23:102','سو جسکی بھاری ہوئی تول تو وہی لوگ کام لے نکلے');
INSERT INTO "al_hind" VALUES(23,103,'23:103','اور جس کی ہلکی نکلی تول سو وہی لوگ ہیں جو ہار بیٹھے اپنی جان دوزخ ہی میں رہا کریں گے');
INSERT INTO "al_hind" VALUES(23,104,'23:104','جھلس دے گی انکے منہ کو آگ اور وہ اس میں بد شکل ہو رہے ہوں گے ');
INSERT INTO "al_hind" VALUES(23,105,'23:105','کیا تم کو سنائی نہ تھیں ہماری آیتیں پھر تم ان کو جھٹلاتے تھے ');
INSERT INTO "al_hind" VALUES(23,106,'23:106','بولے اے رب زور کیا ہم پر ہماری کم بختی نے اور رہے ہم لوگ بہکے ہوئے');
INSERT INTO "al_hind" VALUES(23,107,'23:107','اے ہمارے رب نکال لے ہم کو اس میں سے اگر ہم پھر کریں تو ہم گنہگار ');
INSERT INTO "al_hind" VALUES(23,108,'23:108','فرمایا پڑے رہو پھٹکار ے ہوئے اس میں اور مجھ سے نہ بولو');
INSERT INTO "al_hind" VALUES(23,109,'23:109','ایک فرقہ تھا میرے بندوں میں جو کہتے تھے اے رب ہمارے ہم یقین لائے سو معاف کر ہم کو اور رحم کر ہم پر اور تو سب رحم والوں سے بہتر ہے ');
INSERT INTO "al_hind" VALUES(23,110,'23:110','پھر تم نے ان کو ٹھٹھوں میں پکڑا یہاں تک کہ بھول گئے انکے پیچھے میری یاد اور تم ان سے ہنستے رہے ');
INSERT INTO "al_hind" VALUES(23,111,'23:111','میں نے آج دیا ان کو بدلہ انکے صبر کرنے کا کہ وہی ہیں مراد کو پہنچنے والے ');
INSERT INTO "al_hind" VALUES(23,112,'23:112','فرمایا تم کتنی دیر رہے زمین میں برسوں کی گنتی سے');
INSERT INTO "al_hind" VALUES(23,113,'23:113','بولے ہم رہے ایک یا کچھ دن سے کم تو پوچھ لے گنتی والوں سے ');
INSERT INTO "al_hind" VALUES(23,114,'23:114','فرمایا تم اس میں بہت نہیں تھوڑا ہی رہے ہو اگر تم جانتے ہوتے ');
INSERT INTO "al_hind" VALUES(23,115,'23:115','سو کیا تم خیال رکھتے ہو کہ ہم نے تم کو بنایا کھیلنے کو اور تم ہمارے پاس پھر کر نہ آؤ گے ');
INSERT INTO "al_hind" VALUES(23,116,'23:116','سو بہت اوپر ہے اللہ وہ بادشاہ سچا کوئی حاکم نہیں اس کے سوائے مالک اس عزت کے تخت کا ');
INSERT INTO "al_hind" VALUES(23,117,'23:117','اور جو کوئی پکارے اللہ کے ساتھ دوسرا حاکم جس کی سند نہیں اس کے پاس سو اس کا حساب ہے اس کے رب کے نزدیک بیشک بھلا نہ ہو گا منکروں کا');
INSERT INTO "al_hind" VALUES(23,118,'23:118','اور تو کہہ اے رب معاف کر اور رحم کر اور تو ہے بہتر سب رحم والوں سے ');
INSERT INTO "al_hind" VALUES(24,1,'24:1','یہ ایک سورت ہے کہ ہم نے اتاری اور ذمہ پر لازم کی اور اتاریں اس میں باتیں صاف تاکہ تم یاد رکھو ');
INSERT INTO "al_hind" VALUES(24,2,'24:2','بدکاری کرنے والی عورت اور مرد سو مارو ہر ایک کو دونوں میں سے سوسو درّے اور نہ آوے تم کو ان پر ترس اللہ کے حکم چلانے میں اگر تم یقین رکھتے ہو اللہ پر اور پچھلے دن پر اور دیکھیں ان کا مارنا کچھ لوگ مسلمان ');
INSERT INTO "al_hind" VALUES(24,3,'24:3','بدکارمرد نہیں نکاح کرتا مگر عورت بدکار سے یا شرک والی سے اور بدکار عورت سے نکاح نہیں کرتا مگر بدکار مرد یا مشرک اور یہ حرام ہوا ہے ایمان والوں پر ');
INSERT INTO "al_hind" VALUES(24,4,'24:4','اور جو لوگ عیب لگاتے ہیں حفاظت والیوں کو (پاکدامنوں کو) پھر نہ لائے چار مرد شاہد تو مارو ان کو اسی درّے اور نہ مانو ان کی گواہی کبھی اور وہی لوگ ہیں نافرمان ');
INSERT INTO "al_hind" VALUES(24,5,'24:5','مگر جنہوں نے توبہ کر لی اس کے پیچھے اور سنور گئے تو اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(24,6,'24:6','اور جو عیب لگائیں اپنی جوروؤں کو اور شاہد نہ ہوں انکے پاس سوائے انکی جان کے تو ایسے شخص کی گواہی کی یہ صورت ہے کہ چار بار گواہی دے اللہ کی قسم کھا کر مقرر وہ شخص سچا ہے');
INSERT INTO "al_hind" VALUES(24,7,'24:7','اور پانچویں بار یہ کہ اللہ کی پھٹکار ہو اس شخص پر اگر ہو وہ جھوٹا');
INSERT INTO "al_hind" VALUES(24,8,'24:8','اور عورت سے ٹل جائے مار یوں کہ وہ گواہی دے چار گواہی اللہ کی قسم کھا کر مقرر وہ شخص جھوٹا ہے');
INSERT INTO "al_hind" VALUES(24,9,'24:9','اور پانچویں یہ کہ اللہ کا غضب آئے اس عورت پر اگر وہ شخص سچا ہے ');
INSERT INTO "al_hind" VALUES(24,10,'24:10','اور اگر نہ ہوتا اللہ کا فضل تمہارے اوپر اور اسکی رحمت اور یہ کہ اللہ معاف کرنے والا ہے حکمتیں جاننے والا تو کیا کچھ نہ ہوتا ');
INSERT INTO "al_hind" VALUES(24,11,'24:11','جو لوگ لائے ہیں یہ طوفان تمہیں میں ایک جماعت ہیں تم اس کو نہ سمجھو برا اپنے حق میں بلکہ یہ بہتر ہے تمہارے حق میں ہر آدمی کے لئے ان میں سے وہ ہے جتنا اس نے گناہ کمایا اور جس نے اٹھایا ہے اس کا بڑا بوجھ اس کے واسطے بڑا عذاب ہے ');
INSERT INTO "al_hind" VALUES(24,12,'24:12','کیوں نہ جب تم نے اس کو سنا تھا خیال کیا ہوتا ایمان والے مردوں اور ایمان والی عورتوں نے اپنے لوگوں پر بھلا خیال اور کہا ہوتا یہ صریح طوفان ہے ');
INSERT INTO "al_hind" VALUES(24,13,'24:13','کیوں نہ لائے وہ اس بات پر چار شاہد پھر جب نہ لائے شاہد تو وہ لوگ اللہ کے یہاں وہی ہیں جھوٹے ');
INSERT INTO "al_hind" VALUES(24,14,'24:14','اور اگر نہ ہوتا اللہ کا فضل تم پر اور اسکی رحمت دنیا اور آخرت میں تو تم پر پڑ جاتی اس چرچا کرنے میں کوئی آفت بڑی ');
INSERT INTO "al_hind" VALUES(24,15,'24:15','جب لینے لگے تم اس کو اپنی زبانوں پر اور بولنے لگے اپنے منہ سے جس چیز کی تم کو خبر نہیں اور تم سمجھتے ہو اسکو ہلکی بات اور یہ اللہ کے یہاں بہت بڑی بات ہے ');
INSERT INTO "al_hind" VALUES(24,16,'24:16','اور کیوں نہ جب تم نے اس کو سنا تھا کہا ہوتا ہم کو نہیں لائق کہ منہ پر لائیں یہ بات اللہ تو پاک ہے یہ تو بڑا بہتان ہے ');
INSERT INTO "al_hind" VALUES(24,17,'24:17','اللہ تم کو سمجھاتا ہے کہ پھر نہ کرو ایسا کام کبھی اگر تم ایمان رکھتے ہو ');
INSERT INTO "al_hind" VALUES(24,18,'24:18','اور کھولتا ہے اللہ تمہارے واسطے پتے کی باتیں اور اللہ سب جانتا ہے حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(24,19,'24:19','جو لوگ چاہتے ہیں کہ چرچا ہو بدکاری کا ایمان والوں میں انکے لئے عذاب ہے دردناک دنیا اور آخرت میں اور اللہ جانتا ہے اور تم نہیں جانتے ');
INSERT INTO "al_hind" VALUES(24,20,'24:20','اور اگر نہ ہوتا اللہ کا فضل تم پر اور اسکی رحمت اور یہ کہ اللہ نرمی کرنے والا ہے مہربان تو کیا کچھ نہ ہوتا ');
INSERT INTO "al_hind" VALUES(24,21,'24:21','اے ایمان والو نہ چلو قدموں پر شیطان کے اور جو کوئی چلے گا قدموں پر شیطان کےسو وہ تو یہی بتلائے گا بیحائی اور بری بات اور اگر نہ ہوتا اللہ کا فضل تم پر اور اس کی رحمت تو نہ سنورتا تم میں ایک شخص بھی کبھی و لیکن اللہ سنوارتا ہے جس کو چاہے اور اللہ سب کچھ سنتا جانتا ہے ');
INSERT INTO "al_hind" VALUES(24,22,'24:22','اور قسم نہ کھائیں بڑے درجہ والے تم میں سے اور کشائش والے اس پر کہ دیں قرابتیوں کو اور محتاجوں کو اور وطن چھوڑنے والوں کو اللہ کی راہ میں اور چاہئے کہ معاف کریں اور درگزر کریں کیا تم نہیں چاہتے کہ اللہ تم کو معاف کرے اور اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(24,23,'24:23','جو لوگ عیب لگاتے ہیں حفاظت والیوں بیخبر ایمان والیوں کو ان کو پھٹکار ہے دنیا میں اور آخرت میں اور انکے لئے ہے بڑا عذاب ');
INSERT INTO "al_hind" VALUES(24,24,'24:24','جس دن کہ ظاہر کر دیں گی ان کی زبانیں اور ہاتھ اور پاؤں جو کچھ وہ کرتے تھے ');
INSERT INTO "al_hind" VALUES(24,25,'24:25','اس دن پوری دے گا انکو اللہ ان کی سزا جو چاہئے اور جان لیں گے کہ اللہ وہی ہے سچا کھولنے والا ');
INSERT INTO "al_hind" VALUES(24,26,'24:26','گندیاں ہیں گندوں کے واسطے اور گندے واسطے گندیوں کے اور ستھریاں ہیں ستھروں کے واسطے اور ستھرے واسطے ستھریوں کے وہ لوگ بے تعلق ہیں ان باتوں سے جو یہ کہتے ہیں انکے واسطے بخشش ہے اور روزی ہے عزت کی ');
INSERT INTO "al_hind" VALUES(24,27,'24:27','اے ایمان والو مت جایا کرو کسی گھر میں اپنے گھروں کے سوائے جب تک بول چال نہ کر لو اور سلام کر لو ان گھر والوں پر یہ بہتر ہے تمہارے حق میں تاکہ تم یاد رکھو ');
INSERT INTO "al_hind" VALUES(24,28,'24:28','پھر اگر نہ پاؤ اس میں کسی کو تو اس میں نہ جاؤ جب تک کہ اجازت نہ ملے تم کو اور اگر تم کو جواب ملے کہ پھر جاؤ تو پھر جاؤ اس میں خوب ستھرائی ہے تمہارے لئے اور اللہ جو تم کرتے ہو اس کو جانتا ہے ');
INSERT INTO "al_hind" VALUES(24,29,'24:29','نہیں گناہ تم پر اس میں کہ جاؤ ان گھروں میں جہاں کوئی نہیں بستا اس میں کچھ چیز ہو تمہاری اور اللہ کو معلوم ہے جو تم ظاہر کرتے ہو اور جو چھپاتے ہو ');
INSERT INTO "al_hind" VALUES(24,30,'24:30','کہہ دے ایمان والوں کو نیچی رکھیں ذری اپنی آنکھیں اور تھامتے رہیں اپنے ستر کو اس میں خوب ستھرائی ہے انکے لئے بیشک اللہ کو خبر ہے جو کچھ کرتےہیں ');
INSERT INTO "al_hind" VALUES(24,31,'24:31','اور کہہ دے ایمان والیوں کو نیچی رکھیں ذرا اپنی آنکھیں اور تھامتی رہیں اپنے ستر کو اور نہ دکھائیں اپنا سنگار مگر جو کھلی چیز ہے اس میں سے اور ڈال لیں اپنی اوڑھنی اپنے گریبان پر اور نہ کھولیں اپنا سنگار مگر اپنے خاوند کے آگے یا اپنے باپ کے یا پنے خاوند کے باپ کے یا اپنے بیٹے کے یا اپنے خاوند کے بیٹے کے یا اپنے بھائی کے یا اپنے بھتیجوں کے یا اپنے بھانجوں کے یا اپنی عورتوں کے یا اپنے ہاتھ کے مال کے یا کاربار کرنے والوں کے (خدمت میں مشغول رہنے والوں کے) جو مرد کہ کچھ غرض نہیں رکھتے یا لڑکوں کے جنہوں نے ابھی نہیں پہچانا عورتوں کے بھید کو اور نہ ماریں زمین پر اپنے پاؤں کو کہ جانا جائے جو چھپاتی ہیں اپنا سنگار اور توبہ کرو اللہ کے آگے سب مل کر اے ایمان والو تاکہ تم بھلائی پاؤ ');
INSERT INTO "al_hind" VALUES(24,32,'24:32','اور نکاح کر دو رانڈوں کا اپنے اندر اور جو نیک ہوں تمہارے غلام اور لونڈیاں اگر وہ ہوں گے مفلس اللہ ان کو غنی کر دے گا اپنے فضل سے اور اللہ کشائش والا ہے سب کچھ جانتا ہے ');
INSERT INTO "al_hind" VALUES(24,33,'24:33','اور اپنے آپ کو تھامتے رہیں جن کو نہیں ملتا سامان نکاح کا جب تک کہ مقدور دے ان کو اللہ اپنے فضل سے اور جو لوگ چاہیں لکھت آزادی کی مال دے کر ان میں سے کہ جو تمہارے ہاتھ کے مال ہیں (جن کے مالک ہیں تمہارےہاتھ) تو انکو لکھ کر دیدو اگر سمجھو ان میں کچھ نیکی اور دو ان کو اللہ کے مال سے جو اس نے تم کو دیا ہے اور نہ زبردستی کرو اپنی چھوکریوں پر بدکاری کے واسطے اگر وہ چاہیں قید سے رہنا کہ تم کمانا چاہو اسباب دنیا کی زندگانی کا اور جو کوئی ان پر زبردستی کرے گا تو اللہ ان کی بے بسی کے پیچھے بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(24,34,'24:34','اور ہم نے اتاریں تمہاری طرف آیتیں کھلی ہوئی اور کچھ حال انکا جو ہو چکے تم سے پہلے اور نصیحت ڈرنے والوں کو ');
INSERT INTO "al_hind" VALUES(24,35,'24:35','اللہ روشنی ہے آسمانوں اور زمین کی مثال اس کی روشنی کی جیسے ایک طاق اس میں ہو ایک چراغ وہ چراغ دھرا ہو ایک شیشہ میں وہ شیشہ ہے جیسے ایک تارہ چمکتا ہوا تیل جلتا ہے اس میں ایک برکت کے درخت کا وہ زیتون ہے نہ مشرق کی طرف ہے اور نہ مغرب کی طرف قریب ہے اس کا تیل کہ روشن ہو جائے اگرچہ نہ لگی ہو اس میں آگ روشنی پر روشنی اللہ راہ دکھلا دیتا ہے اپنی روشنی کی جس کو چاہے اور بیان کرتا ہے اللہ مثالیں لوگوں کے واسطے اور اللہ سب چیز کو جانتا ہے ');
INSERT INTO "al_hind" VALUES(24,36,'24:36','ان گھروں میں کہ اللہ نے حکم دیا انکو بلند کرنے کا اور و ہاں اس کا نام پڑھنے کا یاد کرتے ہیں اسکی وہاں صبح و شام ');
INSERT INTO "al_hind" VALUES(24,37,'24:37','وہ مرد کہ نہیں غافل ہوتے سودا کرنے میں اور نہ بیچنے میں اللہ کی یاد سے اور نماز قائم رکھنے سے اور زکوٰۃ دینے سے ڈرتے رہتے ہیں اس دن سے جس میں الٹ جائیں گے دل اور آنکھیں ');
INSERT INTO "al_hind" VALUES(24,38,'24:38','تاکہ بدلہ دے انکو اللہ انکے بہتر سے بہتر کاموں کا اور زیادتی دے انکو اپنے فضل سے اور اللہ روزی دیتا ہے جس کو چاہے بے شمار ');
INSERT INTO "al_hind" VALUES(24,39,'24:39','اور جو لوگ منکر ہیں ان کے کام جیسے ریت جنگل میں پیاسا جانے اس کو پانی یہاں تک کہ جب پہنچا اس پر اس کو کچھ نہ پایا اور اللہ کو پایا اپنے پاس پھر اسکو پورا پہنچا دیا اس کا لکھا اور اللہ جلد لینے والا ہے حساب ');
INSERT INTO "al_hind" VALUES(24,40,'24:40','یا جیسے اندھیرے گہرے دریا میں چڑھی آتی ہے اس پر ایک لہر اس پر ایک اور لہر اس کے اوپر بادل اندھیرے ہیں ایک پر ایک جب نکالے اپنا ہاتھ لگتا نہیں کہ اس کو وہ سوجھے اور جسکو اللہ نے نہ دی روشنی اسکے واسطے کہیں نہیں روشنی ');
INSERT INTO "al_hind" VALUES(24,41,'24:41','کیا تو نہ نے دیکھا کہ اللہ کی یاد کرتے ہیں جو کوئی ہیں آسمان و زمین میں اور اڑتے جانور پر کھولے ہوئے ہر ایک نے جان رکھی ہے اپنی طرح کی بندگی اور یاد اور اللہ کو معلوم ہے جو کچھ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(24,42,'24:42','اور اللہ کی حکومت ہے آسمان اور زمین میں اور اللہ ہی تک پھر جانا ہے ');
INSERT INTO "al_hind" VALUES(24,43,'24:43','تو نے نہ دیکھا اللہ ہانک لاتا ہے بادل کو پھر انکو ملا دیتا ہے پھر انکو رکھتا ہے تہ بر تہ پھر تو دیکھے مینہ نکلتا ہے اس کے بیچ سے اور اتارتا ہے آسمان سے اس میں جو پہاڑ ہیں اولوں کے پھر وہ ڈالتا ہے جس پر چاہے اور بچا دیتا ہے جس سے چاہے ابھی اس کی بجلی کی کوند لیجائے آنکھوں کو ');
INSERT INTO "al_hind" VALUES(24,44,'24:44','اللہ بدلتا ہے رات اور دن کو اس میں دھیان کرنے کی جگہ ہے آنکھ والوں کو ');
INSERT INTO "al_hind" VALUES(24,45,'24:45','اور اللہ نے بنایا ہر پھرنے والے کو ایک پانی سے پھر کوئی ہے کہ چلتا ہے اپنے پیٹ پر اور کوئی ہے کہ چلتا ہے دو پاؤں پر اور کوئی ہے کہ چلتا ہے چار پر بناتا ہے اللہ جو چاہتا ہے بیشک اللہ ہر چیز کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(24,46,'24:46','ہم نے اتاریں آیتیں کھول کھول کر بتلانے والی اور اللہ چلائے جس کو چاہے سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(24,47,'24:47','اور لوگ کہتے ہیں ہم نے مانا اللہ کو اور رسول کو اور حکم میں آ گئے پھر پھر جاتا ہے ایک فرقہ ان میں سے اس کے پیچھے اور وہ لوگ نہیں ماننے والے ');
INSERT INTO "al_hind" VALUES(24,48,'24:48','اور جب ان کو بلائیے اللہ اور رسول کی طرف کہ ان میں قضیہ چکائے تبھی ایک فرقہ کے لوگ ان میں منہ موڑتے ہیں');
INSERT INTO "al_hind" VALUES(24,49,'24:49','اور اگر ان کو کچھ پہنچتا ہو تو چلے آئیں اس کی طرف قبول کر کر ');
INSERT INTO "al_hind" VALUES(24,50,'24:50','کیا انکے دلوں میں روگ ہے یا دھوکے میں پڑے ہوئے ہیں یا ڈرتے ہیں کہ بے انصافی کرے گا ان پر اللہ اور اس کا رسول کچھ نہیں وہی لوگ بے انصاف ہیں ');
INSERT INTO "al_hind" VALUES(24,51,'24:51','ایمان والوں کی بات یہی تھی کہ جب بلائیے انکو اللہ اور رسول کی طرف فیصلہ کرنے کو ان میں تو کہیں ہم نے سن لیا اور حکم مان لیا اور وہ لوگ کہ انہی کا بھلا ہے ');
INSERT INTO "al_hind" VALUES(24,52,'24:52','اور جو کوئی حکم پر چلے اللہ کے اور اسکے رسول کے اور ڈرتا رہے اللہ سے اور بچکر چلے اس سے سو وہی لوگ ہیں مراد کو پہنچنے والے ');
INSERT INTO "al_hind" VALUES(24,53,'24:53','اور قسمیں کھاتے ہیں اللہ کی اپنی تاکید کی قسمیں کہ اگر تو حکم کرےتو سب کچھ چھوڑ کر نکل جائیں تو کہہ قسمیں نہ کھاؤ حکمبرداری چاہئے جو دستور ہے البتہ اللہ کو خبر ہے جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(24,54,'24:54','تو کہہ حکم مانو اللہ کا اور حکم مانو رسول کا پھر اگر تم منہ پھیرو گے تو اس کا ذمہ ہے جو بوجھ اس پر رکھا اور تمہارا ذمہ ہے جو بوجھ تم پر رکھا اور اگر اس کا کہا مانو تو راہ پاؤ اور پیغام لانے والے کا ذمہ نہیں مگر پہنچا دینا کھول کر ');
INSERT INTO "al_hind" VALUES(24,55,'24:55','وعدہ کر لیا اللہ نے ان لوگوں سے جو تم میں ایمان لائے ہیں اور کئے ہیں انہوں نے نیک کام البتہ پیچھے حاکم کر دے گا انکو ملک میں جیسا حاکم کیا تھا ان سے اگلوں کو اور جما دے گا ان کے لئے دین ان کا جو پسند کر دیا انکے واسطے اور دے گا ان کو ان کے ڈر کے بدلے میں امن میری بندگی کریں گے شریک نہ کریں گے میرا کسی کو اور جو کوئی ناشکری کرے گا اسکے پیچھے سو وہی لوگ ہیں نافرمان ');
INSERT INTO "al_hind" VALUES(24,56,'24:56','اور قائم رکھو نماز اور دیتے رہو زکوٰۃ اور حکم پر چلو رسول کے تاکہ تم پر رحم ہو ');
INSERT INTO "al_hind" VALUES(24,57,'24:57','نہ خیال کر کہ یہ جو کافر ہیں تھکا دیں گے بھاگ کر ملک میں اور ان کا ٹھکانہ آگ ہے اور وہ بری جگہ ہے پھر جانے کی ');
INSERT INTO "al_hind" VALUES(24,58,'24:58','اے ایمان والو اجازت لیکر آئیں تم سے جو تمہارےہاتھ کے مال ہیں اور جو کہ نہیں پہنچے تم میں عقل کی حد کو تین بار فجر کی نماز سے پہلے اور جب اتار رکھتے ہو اپنے کپڑے دوپہر میں اور عشاء کی نماز سے پیچھے یہ تین وقت بدن کھلنے کے ہیں تمہارے کچھ تنگی نہیں تم پر اور ان پر ان وقتوں کے پیچھے پھرا ہی کرتے ہو ایک دوسرے کے پاس یوں کھولتا ہے اللہ تمہارے آگے باتیں اور اللہ سب کچھ جاننے والا حکمت والا ہے');
INSERT INTO "al_hind" VALUES(24,59,'24:59','اور جب پہنچیں لڑکے تم میں کے عقل کی حد کو تو انکو ویسی ہی اجازت لینی چاہئے جیسے لیتے رہے ہیں ان سے اگلے یوں کھول کر سناتا ہے اللہ تم کو اپنی باتیں اور اللہ سب کچھ جاننے والا حکمت والا ہے');
INSERT INTO "al_hind" VALUES(24,60,'24:60','اور جو بیٹھ رہی ہیں گھروں میں تمہاری عورتوں میں سے جنکو توقع نہیں رہی نکاح کی ان پرگناہ نہیں کہ اتار رکھیں اپنے کپڑے یہ نہیں کہ دکھاتی پھریں اپنا سنگار اور اس سے بھی بچیں تو بہتر ہے انکے لئے اور اللہ سب باتیں سنتا جانتا ہے ');
INSERT INTO "al_hind" VALUES(24,61,'24:61','نہیں ہے اندھے پر کچھ تکلیف اور نہ لنگڑے پر تکلیف اور نہ بیمار پر تکلیف اور نہیں تکلیف تم لوگوں پر کہ کھاؤ اپنے گھروں سے یا اپنے باپ کے گھر سے یا اپنی ماں کے گھر سے یا اپنے بھائی کے گھر سے یا اپنی بہن کے گھر سے یا اپنے چچا کے گھر سے یا اپنی پھوپھی کے گھر سے یا اپنے ماموں کے گھر سے یا اپنی خالہ کے گھر سے یا جس گھر کی کنجیوں کے تم مالک ہو یا اپنے دوست کے گھر سے نہیں گناہ تم پر کہ کھاؤ آپس میں مل کر یا جدا ہو کر پھر جب کبھی جانے لگو گھروں میں تو سلام کہو اپنے لوگوں پر نیک دعا ہے اللہ کے یہاں سے برکت والی ستھری یوں کھولتا ہے اللہ تمہارے آگے اپنی باتیں تاکہ تم سمجھ لو ');
INSERT INTO "al_hind" VALUES(24,62,'24:62','ایمان والے وہ ہیں جو یقین لائے اللہ پر اور اسکے رسول پر اور جب ہوتے ہیں اس کے ساتھ کسی جمع ہونے کے کام میں تو چلے نہیں جاتے جب تک اس سے اجازت نہ لے لیں جو لوگ تجھ سے اجازت لیتے ہیں وہی ہیں جو مانتے ہیں اللہ کو اور اس کے رسول کو پھر جب اجازت مانگیں تجھ سے اپنے کسی کام کے لئے تو اجازت دے جس کو ان میں سے تو چاہے اور معافی مانگ انکے واسطے اللہ سے اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(24,63,'24:63','مت کو لو بلانا رسول کا اپنے اندر برابر اسکے جو بلاتا ہے تم میں ایک دوسرے کو اللہ جانتا ہے ان لوگوں کو تم میں سے جو سٹک جاتے ہیں آنکھ بچا کر سو ڈرتے رہیں وہ لوگ جو خلاف کرتے ہیں اس کے حکم کا اس سے کہ آپڑے ان پر کچھ خرابی یا پہنچے ان کو عذاب دردناک ');
INSERT INTO "al_hind" VALUES(24,64,'24:64','سنتے ہو اللہ ہی کا ہے جو کچھ ہے آسمانوں اور زمین میں اس کو معلوم ہے جس حال پر تم ہو اور جس دن پھیرےجائیں گے اس کی طرف تو بتائے گا ان کو جو کچھ انہوں نے کیا اور اللہ ہر ایک چیز کو جانتا ہے ');
INSERT INTO "al_hind" VALUES(25,1,'25:1','بڑی برکت ہے اسکی جس نے اتاری فیصلہ کی کتاب اپنے بندہ پر تاکہ رہے جہان والوں کے لئے ڈرانے والا ');
INSERT INTO "al_hind" VALUES(25,2,'25:2','وہ کہ جس کی ہے سلطنت آسمان اور زمین میں اور نہیں پکڑا اس نے بیٹا اور نہیں کوئی اس کا ساجھی سلطنت میں اور بنائی ہر چیز پھر ٹھیک کیا اسکو ماپ کر ');
INSERT INTO "al_hind" VALUES(25,3,'25:3','اور لوگوں نے پکڑ رکھے ہیں اس سے ورے کتنے حاکم جو نہیں بناتے کچھ چیز اور وہ خود بنائے گئے ہیں اور نہیں مالک اپنے حق میں برے کے اور نہ بھلے کے اور نہیں مالک مرنے کے اور نہ جینے کے اور نہ جی اٹھنے کے ');
INSERT INTO "al_hind" VALUES(25,4,'25:4','اور کہنے لگے جو منکر ہیں اور کچھ نہیں ہے یہ مگر طوفان باندھ لایا ہے اور ساتھ دیا ہے اس کا اس میں اور لوگوں نے سو آگئے بے انصافی اور جھوٹ پر ');
INSERT INTO "al_hind" VALUES(25,5,'25:5','اور کہنے لگے یہ نقلیں ہیں پہلوں کی جنکو اس نے لکھ رکھا ہے سو وہی لکھوائی جاتی ہیں اس کے پاس صبح اور شام ');
INSERT INTO "al_hind" VALUES(25,6,'25:6','تو کہہ اسکو اتارا ہے اس نے جو جانتا ہے چھپے ہوئے بھید آسمانوں اور زمین میں بیشک وہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(25,7,'25:7','اور کہنے لگے یہ کیسا رسول ہے کھاتا ہے کھانا اور پھرتا ہے بازاروں میں کیوں نہ اترا اسکی طرف کوئی فرشتہ کہ رہتا اس کے ساتھ ڈرانے کو');
INSERT INTO "al_hind" VALUES(25,8,'25:8','یا آ پڑتا اسکے پاس خزانہ یا ہو جاتا اس کے لئے ایک باغ کہ کھایا کرتا اس میں سے اور کہنے لگے بے انصاف تم پیروی کرتے ہو اس ایک مرد جادو مارے کی ');
INSERT INTO "al_hind" VALUES(25,9,'25:9','دیکھ کیسی بٹھلاتےہیں تجھ پر مثلیں سو بہک گئے اب پا نہیں سکتے راستہ ');
INSERT INTO "al_hind" VALUES(25,10,'25:10','بڑی برکت ہے اسکی جو چاہے تو کر دے تیرے واسطے اس سے بہتر باغ کہ نیچے بہتی ہیں ان کے نہریں اور کر دے تیرے واسطے محل ');
INSERT INTO "al_hind" VALUES(25,11,'25:11','کچھ نہیں وہ جھٹلاتے ہیں قیامت کو اور ہم نے تیار کی ہے اسکے واسطے کہ جھٹلاتا ہے قیامت کو آگ ');
INSERT INTO "al_hind" VALUES(25,12,'25:12','جب وہ دیکھے گی ان کو دور کی جگہ سے سنیں گے اس کا جھنجلانا اور چلانا ');
INSERT INTO "al_hind" VALUES(25,13,'25:13','اور جب ڈالے جائیں گے اس کے اندر ایک جگہ تنگ میں ایک زنجیر میں کئ کئی بندھے ہوئے پکاریں گے اس جگہ موت کو ');
INSERT INTO "al_hind" VALUES(25,14,'25:14','مت پکارو آج ایک مرنے کو اور پکارو بہت سے مرنے کو ');
INSERT INTO "al_hind" VALUES(25,15,'25:15','تو کہہ بھلا یہ چیز بہتر ہے یا باغ ہمیشہ رہنے کا جس کا وعدہ ہو چکا پرہیزگاروں سے وہ ہو گا ان کا بدلہ اور پھر جانے کی جگہ');
INSERT INTO "al_hind" VALUES(25,16,'25:16','انکے واسطے وہاں ہے جو وہ چاہیں رہا کریں ہمیشہ ہو چکا تیرے رب کے ذمہ وعدہ مانگا ملتا ');
INSERT INTO "al_hind" VALUES(25,17,'25:17','اور جس دن جمع کر بلائے گا انکو اور جنکو وہ پوجتے ہیں اللہ کے سوائے پھر ان سے کہے گا کیا تم نے بہکایا میرے ان بندوں کو یا وہ آپ بہکے راہ سے ');
INSERT INTO "al_hind" VALUES(25,18,'25:18','بولیں گے تو پاک ہے ہم سے بن نہ آتا تھا کہ پکڑ لیں کسی کو تیرے بغیر رفیق لیکن تو ان کو فائدہ پہنچاتا رہا اور انکے باپ دادوں کو یہاں تک کہ بھلا بیٹھے تیری یاد اور یہ تھے لوگ تباہ ہونے والے ');
INSERT INTO "al_hind" VALUES(25,19,'25:19','سو وہ تو جھٹلا چکے تم کو تمہاری بات میں اب نہ تم لوٹا سکتے ہو اور نہ مدد کر سکتے ہو اور جو کوئی تم میں گنہگار ہے اس کو ہم چکھائیں گے بڑ اعذاب ');
INSERT INTO "al_hind" VALUES(25,20,'25:20','اور جتنے بھیجے ہم نے تجھ سے پہلے رسول سب کھاتے تھے کھانا اور پھرتے تھے بازاروں میں اور ہم نے رکھا ہے تم میں ایک دوسرے کے جانچنے کو دیکھیں ثابت بھی رہتے ہو اور تیرا رب سب کچھ دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(25,21,'25:21','اور بولے وہ لوگ جو امید نہیں رکھتے کہ ہم سے ملیں گے کیوں نہ اترے ہم پر فرشتے یا ہم دیکھ لیتے اپنے رب کو بہت بڑائی رکھتے ہیں اپنے جی میں اور سر چڑھ رہے ہیں بڑی شرارت میں ');
INSERT INTO "al_hind" VALUES(25,22,'25:22','جس دن دیکھیں گے فرشتوں کو کچھ خوشخبری نہیں اس دن گنہگاروں کو اور کہیں گے کہیں روک دی جائے کوئی آڑ ');
INSERT INTO "al_hind" VALUES(25,23,'25:23','اور ہم پہنچے انکے کاموں پر جو انہوں نے کئے تھے پھر ہم نے کر ڈالا اسکو خاک اڑتی ہوئی ');
INSERT INTO "al_hind" VALUES(25,24,'25:24','بہشت کے لوگوں کا اس دن خوب ہے ٹھکانا اور خوب ہے جگہ دوپہر کے آرام کی ');
INSERT INTO "al_hind" VALUES(25,25,'25:25','اور جس دن پھٹ جائے گا آسمان بادل سے اور اتارے جائیں فرشتے تار لگا کر ');
INSERT INTO "al_hind" VALUES(25,26,'25:26','بادشاہی اس دن سچی ہے رحمٰن کی اور ہے وہ دن منکروں پر مشکل ');
INSERT INTO "al_hind" VALUES(25,27,'25:27','اور جس دن کاٹ کاٹ کھائے گا اپنے ہاتھوں کو کہے گا اے کاش کے میں نے پکڑا ہوتا رسول کے ساتھ رستہ ');
INSERT INTO "al_hind" VALUES(25,28,'25:28','اے خرابی میری کاش کے نہ پکڑا ہوتا میں نے فلانے کو دوست ');
INSERT INTO "al_hind" VALUES(25,29,'25:29','اس نے تو بہکا دیا مجھ کو نصیحت سے مجھ تک پہنچ چکنے کے پیچھے (بعد) اور ہے شیطان آدمی کو وقت پر دغا دینے والا ');
INSERT INTO "al_hind" VALUES(25,30,'25:30','اور کہا رسول نے اے میرے رب میری قوم نے ٹھہرایا ہے اس قرآن کو جھک جھک ');
INSERT INTO "al_hind" VALUES(25,31,'25:31','اور اسی طرح رکھے ہیں ہم نے ہر نبی کے لئے دشمن گنہگاروں میں سے اور کافی ہے تیرا رب راہ دکھانے اور مدد کرنے کو ');
INSERT INTO "al_hind" VALUES(25,32,'25:32','اور کہنے لگے وہ لوگ جو منکر ہیں کیوں نہ اترا اس پر قرآن سارا ایک جگہ ہو کر اسی طرح اتارا تاکہ ثابت رکھیں ہم اس سےتیرا دل اور پڑھ سنایا ہم نے اسکو ٹھہر ٹھہر کر ');
INSERT INTO "al_hind" VALUES(25,33,'25:33','اور نہیں لاتے تیرے پاس کوئی مثل کہ ہم نہیں پہنچا دیتے تجھ کو ٹھیک بات اور اس سے بہتر کھول کر ');
INSERT INTO "al_hind" VALUES(25,34,'25:34','جو لوگ کہ گھیر کر لائے جائیں گے اوندھے پڑے ہوئے اپنے منہ پر دوزخ کی طرف انہی کا برا درجہ ہے اور بہت بہکے ہوئے ہیں راہ سے ');
INSERT INTO "al_hind" VALUES(25,35,'25:35','اور ہم نے دی موسٰی کو کتاب اورکر دیا ہم نے اس کے ساتھ اس کا بھائی ہارون کام بٹانے والا');
INSERT INTO "al_hind" VALUES(25,36,'25:36','پھر کہا ہم نے دونوں جاؤ ان لوگوں کے پاس جنہوں نے جھٹلایا ہماری باتوں کو پھر دے مارا ہم نے ان کو اکھاڑ کر');
INSERT INTO "al_hind" VALUES(25,37,'25:37','اور نوح کی قوم کو جب انہوں نے جھٹلایا پیغام لانے والوں کو ہم نے انکو ڈبا دیا اور کیا ان کو لوگوں کے حق میں نشانی اور تیار کر رکھا ہے ہم نے گنہگاروں کے واسطے عذاب دردناک');
INSERT INTO "al_hind" VALUES(25,38,'25:38','اور عاد کو اور ثمود کو اور کنوئیں والوں کو اور اس کے بیچ میں بہت سی جماعتوں کو');
INSERT INTO "al_hind" VALUES(25,39,'25:39','اور سب کو کہہ سنائیں ہم نے مثالیں اور سب کو کھو دیا ہم نے غارت کر کر ');
INSERT INTO "al_hind" VALUES(25,40,'25:40','اور یہ لوگ ہو آئے ہیں اس بستی کے پاس جن پر برسا برُا برساؤ کیا دیکھتے نہ تھے اس کو نہیں پر امید نہیں رکھتے جی اٹھنے کی ');
INSERT INTO "al_hind" VALUES(25,41,'25:41','اور جہاں تجھ کو دیکھیں کچھ کام نہیں انکو تجھ سے مگر ٹھٹھے کرنے کیا یہ ہے جس کو بھیجا اللہ نے پیغام دے کر');
INSERT INTO "al_hind" VALUES(25,42,'25:42','یہ تو ہم کو بچلا ہی دیتا ہمارے معبودوں سے اگر ہم نہ جمے رہتے ان پر اور آگے جان لیں گے جس وقت دیکھیں گے عذاب کہ کون بہت بچلا ہوا ہے راہ سے ');
INSERT INTO "al_hind" VALUES(25,43,'25:43','بھلا دیکھ تو اس شخص کو جس نے پوجنا اختیار کیا اپنی خواہش کا کہیں تو لے سکتا ہے اس کا ذمہ ');
INSERT INTO "al_hind" VALUES(25,44,'25:44','یا تو خیال رکھتا ہے کہ بہت سے ان میں سنتے یا سمجھتے ہیں اور کچھ نہیں وہ برابر ہیں چوپایوں کے بلکہ وہ زیادہ بہکے ہوئے ہیں راہ سے ');
INSERT INTO "al_hind" VALUES(25,45,'25:45','تو نے نہیں دیکھا اپنے رب کی طرف کیسے دراز کیا سایہ کو اگر چاہتا تو اس کو ٹھہرا رکھتا پھر ہم نے مقرر کیا سورج کو اس کا راہ بتانے والا');
INSERT INTO "al_hind" VALUES(25,46,'25:46','پھر کھینچ لیا ہم نے اسکو اپنی طرف سہج سہج سمیٹ کر ');
INSERT INTO "al_hind" VALUES(25,47,'25:47','اور وہی ہے جس نے بنا دیا تمہارے واسطے رات کو اوڑھنا اور نیند کو آرام اور دن کو بنا دیا اٹھ نکلنے کے لئے ');
INSERT INTO "al_hind" VALUES(25,48,'25:48','اور وہی ہے جس نے چلائیں ہوائیں خوشخبری لانے والیاں اس کی رحمت سے آگے اور اتارا ہم نے آسمان سے پانی پاکی حاصل کرنے کا');
INSERT INTO "al_hind" VALUES(25,49,'25:49','کہ زندہ کر دیں اس سے مرے ہوئے دیس کو اور پلائیں اس کو اپنے پیدا کئے ہوئے بہت سے چوپایوں اور آدمیوں کو ');
INSERT INTO "al_hind" VALUES(25,50,'25:50','اور طرح طرح سے تقسیم کیا ہم نے اس کو انکے بیچ میں تا دھیان رکھیں پھر بھی نہیں رہتے بہت لوگ بدون ناشکری کیے ');
INSERT INTO "al_hind" VALUES(25,51,'25:51','اور اگر ہم چاہتے تو اٹھاتے ہر بستی میں کوئی ڈرانے والا');
INSERT INTO "al_hind" VALUES(25,52,'25:52','سو تو کہنا مت مان منکروں کا اور مقابلہ کر ان کا اسکے ساتھ بڑے زور سے ');
INSERT INTO "al_hind" VALUES(25,53,'25:53','اور وہی ہے جس نے ملے ہوئے چلائے دو دریا یہ میٹھا ہے پیاس بجھانے والا اور یہ کھاری ہے کڑوا اور رکھا ان دونوں کے بیچ پردا اور آڑ روکی ہوئی ');
INSERT INTO "al_hind" VALUES(25,54,'25:54','اور ہی ہے جس نے بنایا پانی سے آدمی پھر ٹھہرایا اس کے لئے جد اور سسرال اور تیرا رب سب کچھ کر سکتا ہے');
INSERT INTO "al_hind" VALUES(25,55,'25:55','اور پوجتے ہیں اللہ کو چھوڑ کر وہ چیز جو نہ بھلا کرے ان کا نہ برا اور ہے کافر اپنے رب کی طرف سے پیٹھ پھیر رہا ');
INSERT INTO "al_hind" VALUES(25,56,'25:56','اور تجھ کو ہم نے بھیجا یہی خوشی اور ڈر سنانے کے لئے');
INSERT INTO "al_hind" VALUES(25,57,'25:57','تو کہہ میں نہیں مانگتا تم سے اس پر کچھ مزدوری مگر جو کوئی چاہے کہ پکڑ لے اپنے رب کی طرف راہ ');
INSERT INTO "al_hind" VALUES(25,58,'25:58','اور بھروسہ کر اوپر اس زندہ کے جو نہیں مرتا اور یاد کر اسکی خوبیاں اور وہ کافی ہے اپنے بندوں کے گناہوں سے خبردار ');
INSERT INTO "al_hind" VALUES(25,59,'25:59','جس نے بنائے آسمان اور زمین اور جو کچھ انکے بیچ میں ہے چھ دن میں پھر قائم ہوا عرش پر وہ بڑی رحمت والا سو پوچھ اس سے جو اسکی خبر رکھتا ہو ');
INSERT INTO "al_hind" VALUES(25,60,'25:60','اور جب کہیے ان سے سجدہ کرو رحمٰن کو کہیں رحمٰن کیا ہے کیا سجدہ کرنے لگیں ہم جسکو تو فرمائے اور بڑھ جاتا ہے ان کا بدکنا ');
INSERT INTO "al_hind" VALUES(25,61,'25:61','بڑی برکت ہے اسکی جس نے بنائے آسمان میں برج اور رکھا اس میں چراغ اور چاند اجالا کرنے والا');
INSERT INTO "al_hind" VALUES(25,62,'25:62','اور وہی ہے جس نے بنائے رات اور دن بدلتے سدلتے اس شخص کے واسطے کہ چاہو دھیان رکھنا یا چاہے شکر کرنا ');
INSERT INTO "al_hind" VALUES(25,63,'25:63','اور بندے رحمٰن کے وہ ہیں جو چلتے ہیں زمین پر دبے پاؤں اور جب بات کرنے لگیں ان سے بے سمجھ لوگ تو کہیں صاحب سلامت ');
INSERT INTO "al_hind" VALUES(25,64,'25:64','اور وہ لوگ جو رات کاٹتے ہیں اپنے رب کے آگے سجدہ میں اور کھڑے ');
INSERT INTO "al_hind" VALUES(25,65,'25:65','اور وہ لوگ کہ کہتے ہیں اے رب ہٹا ہم سے دوزخ کا عذاب بیشک اس کا عذاب چمٹنے والا ہے');
INSERT INTO "al_hind" VALUES(25,66,'25:66','وہ بری جگہ ہے ٹھہرنے کی اور بری جگہ رہنے کی ');
INSERT INTO "al_hind" VALUES(25,67,'25:67','اور وہ لوگ کہ جب خرچ کرنے لگیں نہ بیجا اڑائیں اور نہ تنگی کریں اور ہے اس کے بیچ ایک سیدھی گذران ');
INSERT INTO "al_hind" VALUES(25,68,'25:68','اور وہ لوگ کہ نہیں پکارتے اللہ کے ساتھ دوسرے حاکم کو اور نہیں خون کرتے جان کا جو منع کر دی اللہ نے مگر جہاں چاہئے اور بدکاری نہیں کرتے اور جو کوئی کرے یہ کام وہ جا پڑ اگناہ میں ');
INSERT INTO "al_hind" VALUES(25,69,'25:69','دونا ہو گا اس کو عذب قیامت کےدن اور پڑا رہے گا اس میں خوار ہو کر ');
INSERT INTO "al_hind" VALUES(25,70,'25:70','مگر جس نے توبہ کی اور یقین لایا اور کیا کچھ کام نیک سو ان کو بدل دے گا، اللہ برائیوں کی جگہ بھلائیاں اور ہے اللہ بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(25,71,'25:71','اور جو کوئی توبہ کرے اور کرے کام نیک سو وہ پھر آتا ہے اللہ کی طرف پھر آنے کی جگہ ');
INSERT INTO "al_hind" VALUES(25,72,'25:72','اور جو لوگ شامل نہیں ہوتے جھوٹے کام میں اور جب گزرتے ہیں کھیل کی باتوں پر نکل جائیں بزرگانہ ');
INSERT INTO "al_hind" VALUES(25,73,'25:73','اور وہ لوگ کہ جب انکو سمجھائیں انکے رب کی باتیں نہ پڑیں ان پر بہرے اندھے ہو کر ');
INSERT INTO "al_hind" VALUES(25,74,'25:74','اور وہ لوگ جو کہتے ہیں اے رب دے ہم کو ہماری عورتوں کی طرف سے اور اولاد کی طرف سے آنکھ کی ٹھنڈک اور کر ہم کو پرہیزگاروں کا پیشوا ');
INSERT INTO "al_hind" VALUES(25,75,'25:75','ان کو بدلہ ملے گا کوٹھوں کی جھروکے اس لئے کہ وہ ثابت قدم رہے اور لینے آئیں گے ان کو وہاں دعا و سلام کہتے ہوئے ');
INSERT INTO "al_hind" VALUES(25,76,'25:76','سدا رہا کریں ان میں خوب جگہ ہے ٹھہرنے کی اور خوب جگہ رہنے کی ');
INSERT INTO "al_hind" VALUES(25,77,'25:77','تو کہہ پروا نہیں رکھتا میرا رب تمہاری اگر تم اس کو نہ پکارا کرو سو تم تو جھٹلا چکے اب آگے کو ہونی ہے مٹھ بھیڑ ');
INSERT INTO "al_hind" VALUES(26,1,'26:1','طٰسم۔');
INSERT INTO "al_hind" VALUES(26,2,'26:2','یہ آیتیں ہیں کھلی کتاب کی ');
INSERT INTO "al_hind" VALUES(26,3,'26:3','شاید تو گھونٹ مارے اپنی جان اس بات پر کہ وہ یقین نہیں کرتے ');
INSERT INTO "al_hind" VALUES(26,4,'26:4','اگر ہم چاہیں اتاریں ان پر آسمان سے ایک نشانی پھر رہ جائیں ان کی گردنیں اس کے آگے نیچی ');
INSERT INTO "al_hind" VALUES(26,5,'26:5','اور نہیں پہنچتی ان کے پاس کوئی نصیحت رحمٰن سے نئی جس سے منہ نہیں موڑتے ');
INSERT INTO "al_hind" VALUES(26,6,'26:6','سو یہ تو جھٹلا چکے اب پہنچے گی ان پر حقیقت اس بات کی جس پر ٹھٹھے کرتے تھے ');
INSERT INTO "al_hind" VALUES(26,7,'26:7','کیا نہیں دیکھتے وہ زمین کو کتنی اگائیں ہم نے اس میں ہر ایک قسم کی خاصی چیزیں');
INSERT INTO "al_hind" VALUES(26,8,'26:8','اس میں البتہ نشانی ہے اور ان میں بہت لوگ نہیں ماننے والے ');
INSERT INTO "al_hind" VALUES(26,9,'26:9','اور تیرا رب وہی ہے زبردست رحم والا ');
INSERT INTO "al_hind" VALUES(26,10,'26:10','اور جب پکارا تیرے رب نے موسٰی کو کہ جا اس قوم گنہگار کے پاس');
INSERT INTO "al_hind" VALUES(26,11,'26:11','قوم فرعون کے پاس کیا وہ ڈرتے نہیں ');
INSERT INTO "al_hind" VALUES(26,12,'26:12','بولا اے رب میں ڈرتا ہوں کہ مجھ کو جھٹلائیں');
INSERT INTO "al_hind" VALUES(26,13,'26:13','اور رک جاتا ہے میرا جی اور نہیں چلتی میری زبان سو پیغام دے ہارون کو ');
INSERT INTO "al_hind" VALUES(26,14,'26:14','اور انکو مجھ پر ہے ایک گناہ کا دعویٰ سو ڈرتا ہوں کہ مجھ کو مار ڈالیں ');
INSERT INTO "al_hind" VALUES(26,15,'26:15','فرمایا کبھی نہیں تم دونوں جاؤ لیکر ہماری نشانیاں ہم ساتھ تمہارے سنتے ہیں ');
INSERT INTO "al_hind" VALUES(26,16,'26:16','سو جاؤ فرعون کے پاس اور کہو ہم پیغام لے کر آئے ہیں پروردگار عالم کا');
INSERT INTO "al_hind" VALUES(26,17,'26:17','یہ کہ بھیجدے ہمارے ساتھ بنی اسرائیل کو ');
INSERT INTO "al_hind" VALUES(26,18,'26:18','بولا کیا نہیں پالا ہم نے تجھ کو اپنے اندر لڑکا سا اور رہا تو ہم میں اپنی عمر میں سے کئ برس ');
INSERT INTO "al_hind" VALUES(26,19,'26:19','اور کر گیا تو اپنی وہ کرتوت جو کر گیا اور تو ہے ناشکر ');
INSERT INTO "al_hind" VALUES(26,20,'26:20','کہا کیا تو تھا میں نے وہ کام اور میں تھا چوکنے والا ');
INSERT INTO "al_hind" VALUES(26,21,'26:21','پھر بھاگا میں تم سے جب تمہارا ڈر دیکھا پھر بخشا مجھ کو میرے رب نے حکم اور ٹھہرایا مجھ کو پیغام پہنچانے والا ');
INSERT INTO "al_hind" VALUES(26,22,'26:22','اور کیا وہ احسان ہے جو تو مجھ پر رکھتا ہے کہ غلام بنایا تو نے بنی اسرائیل کو ');
INSERT INTO "al_hind" VALUES(26,23,'26:23','بولا فرعون کیا معنی پروردگار عالم کا ');
INSERT INTO "al_hind" VALUES(26,24,'26:24','کہا پروردگار آسمان اور زمین کا اور جو کچھ انکے بیچ میں ہے اگر تم یقین کرو ');
INSERT INTO "al_hind" VALUES(26,25,'26:25','بولا اپنے گرد والوں سے کیا تم نہیں سنتے ہو ');
INSERT INTO "al_hind" VALUES(26,26,'26:26','کہا پروردگار تمہارا اور پروردگار تمہارے اگلے باپ دادوں کا ');
INSERT INTO "al_hind" VALUES(26,27,'26:27','بولا تمہارا پیغام لانے والا جو تمہاری طرف بھیجا گیا ضرور باؤلا ہے ');
INSERT INTO "al_hind" VALUES(26,28,'26:28','کہا پروردگار مشرق کا اور مغرب کا اور جو کچھ ان کے بیچ میں ہے اگر تم سمجھ رکھتے ہو ');
INSERT INTO "al_hind" VALUES(26,29,'26:29','بولا اگر تو نے ٹھہرایا کوئی اور حاکم میرے سوائے تو مقرر (ضرور) ڈالوں گا تجھ کو قید میں ');
INSERT INTO "al_hind" VALUES(26,30,'26:30','کہا اور اگر لیکر آیا ہوں تیرے پاس ایک چیز کھول دینے والی ');
INSERT INTO "al_hind" VALUES(26,31,'26:31','بولا تو وہ چیز لا اگر تو سچ کہتا ہے');
INSERT INTO "al_hind" VALUES(26,32,'26:32','پھر ڈال دیا اپنا عصا سو اسی وقت وہ اژدھا ہو گیا صریح');
INSERT INTO "al_hind" VALUES(26,33,'26:33','اور اندر (بغل) سے نکالا اپنا ہاتھ سو اسی وقت وہ سفید تھا دیکھنے والوں کے سامنے');
INSERT INTO "al_hind" VALUES(26,34,'26:34','بولا اپنے گرد کے سرداروں سے یہ تو کوئی جادوگر ہے پڑھا ہوا');
INSERT INTO "al_hind" VALUES(26,35,'26:35','چاہتا ہے کہ نکال دے تم کو تمہارے دیس سے اپنے جادو کے زور سے سو اب کیا حکم دیتے ہو ');
INSERT INTO "al_hind" VALUES(26,36,'26:36','بولے ڈھیل دے اسکو اور اسکے بھائی کو اور بھیجدے شہروں میں نقیب');
INSERT INTO "al_hind" VALUES(26,37,'26:37','لے آئیں تیرے پاس جو بڑا جادوگر ہو پڑھا ہوا');
INSERT INTO "al_hind" VALUES(26,38,'26:38','پھر اکٹھے کئے جادوگر وعدہ پر ایک مقرر دن کے ');
INSERT INTO "al_hind" VALUES(26,39,'26:39','اور کہہ دیا لوگوں کو کیا تم بھی اکٹھے ہو گئے');
INSERT INTO "al_hind" VALUES(26,40,'26:40','شاید ہم راہ قبول کر لیں جادوگروں کی اگر ہو ان کو غلبہ ');
INSERT INTO "al_hind" VALUES(26,41,'26:41','پھر جب آئے جادوگر کہنے لگےفرعون سے بھلا کچھ ہمارا حق بھی ہے اگر ہو ہم کو غلبہ');
INSERT INTO "al_hind" VALUES(26,42,'26:42','بولا البتہ (ہاں) اور تم اس وقت مقربوں (مصاحبوں) میں ہو گے ');
INSERT INTO "al_hind" VALUES(26,43,'26:43','کہا ان کو موسٰی نے ڈالو جو تم ڈالتے ہو ');
INSERT INTO "al_hind" VALUES(26,44,'26:44','پھر ڈالیں انہوں نے اپنی رسیاں اور لاٹھیاں اور بولے فرعون کے اقبال سے ہماری ہی فتح ہے ');
INSERT INTO "al_hind" VALUES(26,45,'26:45','پھر ڈالا موسٰی نے اپنا عصا پھر تبھی وہ نگلنے لگا جو سانگ انہوں نے بنایا تھا ');
INSERT INTO "al_hind" VALUES(26,46,'26:46','پھر اوندھے گرے جادوگر سجدہ میں');
INSERT INTO "al_hind" VALUES(26,47,'26:47','بولے ہم نے مان لیا جہان کے رب کو');
INSERT INTO "al_hind" VALUES(26,48,'26:48','جو رب ہے موسٰی اور ہارون کا');
INSERT INTO "al_hind" VALUES(26,49,'26:49','بولا تم نے اسکو مان لیا ابھی میں نے حکم نہیں دیا تم کو مقرر (بیشک) وہ تمہارا بڑا ہے جس نے تم کو سکھلایا جادو سو اب معلوم کر لو گے البتہ کاٹوں گا تمہارے ہاتھ اور دوسری طرف کے پاؤں اور سولی چڑھاؤں گا تم سب کو');
INSERT INTO "al_hind" VALUES(26,50,'26:50','بولے کچھ ڈر نہیں ہم کو اپنے رب کی طرف پھر جانا ہے ');
INSERT INTO "al_hind" VALUES(26,51,'26:51','ہم غرض رکھتے ہیں کہ بخش دے ہم کو رب ہمارا تقصیریں ہماری اس واسطے کہ ہم ہوئے پہلے قبول کرنے والے ');
INSERT INTO "al_hind" VALUES(26,52,'26:52','اور حکم بھیجا ہم نے موسٰی کو کہ رات کو لے نکل میرے بندوں کو البتہ تمہارا پیچھا کریں گے ');
INSERT INTO "al_hind" VALUES(26,53,'26:53','پھر بھیجے فرعون نے شہروں میں نقیب ');
INSERT INTO "al_hind" VALUES(26,54,'26:54','یہ لوگ جو ہیں سو ایک جماعت ہے تھوڑی سی ');
INSERT INTO "al_hind" VALUES(26,55,'26:55','اور وہ مقرر ہم سے دل جلے ہوئے ہیں ');
INSERT INTO "al_hind" VALUES(26,56,'26:56','اور ہم سارے ان سے خطرہ رکھتے ہیں ');
INSERT INTO "al_hind" VALUES(26,57,'26:57','پھر نکال باہر کیا ہم نے ان کو باغوں اور چشموں سے');
INSERT INTO "al_hind" VALUES(26,58,'26:58','اور خزانوں اور عمدہ مکانوں سے اسی طرح ');
INSERT INTO "al_hind" VALUES(26,59,'26:59','اور ہاتھ لگا دیں ہم نے یہ چیزیں بنی اسرائیل کے ');
INSERT INTO "al_hind" VALUES(26,60,'26:60','پھر پیچھے پڑے ان کے سورج کے نکلنے کے وقت');
INSERT INTO "al_hind" VALUES(26,61,'26:61','پھر جب مقابل ہوئیں دونوں فوجیں کہنے لگے موسٰی کے لوگ ہم تو پکڑے گئے ');
INSERT INTO "al_hind" VALUES(26,62,'26:62','کہا ہرگز نہیں میرے ساتھ ہے میرا رب وہ مجھ کو راہ بتلائے گا ');
INSERT INTO "al_hind" VALUES(26,63,'26:63','پھر حکم بھیجا ہم نے موسٰی کو کہ مار اپنے عصا سے دریا کو پھر دریا پھٹ گیا تو ہو گئ ہر پھانگ جیسے بڑا پہاڑ ');
INSERT INTO "al_hind" VALUES(26,64,'26:64','اور پاس پہنچا دیا ہم نے اسی جگہ دوسروں کو');
INSERT INTO "al_hind" VALUES(26,65,'26:65','اور بچا دیا ہم نے موسٰی کو اور جو لوگ تھے اس کے ساتھ سب کو');
INSERT INTO "al_hind" VALUES(26,66,'26:66','پھر ڈبا دیا ہم نے ان دوسروں کو ');
INSERT INTO "al_hind" VALUES(26,67,'26:67','اس چیز میں ایک نشانی ہے اور نہیں تھے بہت لوگ ان میں ماننے والے ');
INSERT INTO "al_hind" VALUES(26,68,'26:68','اور تیرا رب وہی ہے زبردست رحم والا ');
INSERT INTO "al_hind" VALUES(26,69,'26:69','اور سنا دے ان کو خبر ابراہیم کی');
INSERT INTO "al_hind" VALUES(26,70,'26:70','جب کہا اپنے باپ کو اور اس کی قوم کو تم کس کو پوجتے ہو ');
INSERT INTO "al_hind" VALUES(26,71,'26:71','وہ بولے ہم پوجتے ہیں مورتوں کو پھر سارے دن انہی کے پاس لگے بیٹھے رہتے ہیں ');
INSERT INTO "al_hind" VALUES(26,72,'26:72','کہا کچھ سنتے ہیں تمہارا کہا جب تم پکارتے ہو ');
INSERT INTO "al_hind" VALUES(26,73,'26:73','یا کچھ بھلا کرتے ہیں تمہارا یا برا ');
INSERT INTO "al_hind" VALUES(26,74,'26:74','بولے نہیں پر ہم نے پایا اپنے باپ دادوں کو یہی کام کرتے ');
INSERT INTO "al_hind" VALUES(26,75,'26:75','کہا بھلا دیکھتے ہو جن کو پوجتے رہے ہو');
INSERT INTO "al_hind" VALUES(26,76,'26:76','تم اور تمہارے باپ دادے اگلے ');
INSERT INTO "al_hind" VALUES(26,77,'26:77','سو وہ میرے غنیم ہیں مگر جہان کا رب ');
INSERT INTO "al_hind" VALUES(26,78,'26:78','جس نے مجھ کو بنایا سو وہی مجھ کو راہ دکھلاتا ہے ');
INSERT INTO "al_hind" VALUES(26,79,'26:79','اور وہ جو مجھ کو کھلاتا ہے اور پلاتا ہے');
INSERT INTO "al_hind" VALUES(26,80,'26:80','اور جب میں بیمار ہوں تو وہی شفا دیتا ہے');
INSERT INTO "al_hind" VALUES(26,81,'26:81','اور ہ جو مجھ کو مارے گا پھر جلائے گا ');
INSERT INTO "al_hind" VALUES(26,82,'26:82','اور وہ جو مجھ کو توقع ہے کہ بخشے میری تقصیر انصاف کے دن ');
INSERT INTO "al_hind" VALUES(26,83,'26:83','اے میرے رب دے مجھ کو حکم اور ملا مجھ کو نیکوں میں ');
INSERT INTO "al_hind" VALUES(26,84,'26:84','اور رکھ میرا بول سچا پچھلوں میں ');
INSERT INTO "al_hind" VALUES(26,85,'26:85','اور کر مجھ کو وارثوں میں نعمت کے باغ کے ');
INSERT INTO "al_hind" VALUES(26,86,'26:86','اور معاف کر میرے باپ کو وہ تھا راہ بھولے ہوؤں میں ');
