INSERT INTO "al_hind" VALUES(26,214,'26:214','اور ڈر سنا دے اپنے قریب کے رشتہ داروں کو ');
INSERT INTO "al_hind" VALUES(26,215,'26:215','اور اپنے بازو نیچے رکھ انکے واسطے جو تیرے ساتھ ہیں ایمان والے ');
INSERT INTO "al_hind" VALUES(26,216,'26:216','پھر اگر تیری نافرمانی کریں تو کہہ دے میں بیزار ہوں تمہارے کام سے ');
INSERT INTO "al_hind" VALUES(26,217,'26:217','اور بھروسہ کر اس زبردست رحم والے پر ');
INSERT INTO "al_hind" VALUES(26,218,'26:218','جو دیکھتا ہے تجھ کو جب تو اٹھتا ہے');
INSERT INTO "al_hind" VALUES(26,219,'26:219','اور تیرا پھرنا نمازیوں میں ');
INSERT INTO "al_hind" VALUES(26,220,'26:220','بیشک وہی ہے سننے والا جاننے والا');
INSERT INTO "al_hind" VALUES(26,221,'26:221','میں بتلاؤں تم کو کس پر اترتے ہیں شیطان');
INSERT INTO "al_hind" VALUES(26,222,'26:222','اترتے ہیں ہر جھوٹے گنہگار پر ');
INSERT INTO "al_hind" VALUES(26,223,'26:223','لا ڈالتے ہیں سنی ہوئی بات اور بہت ان میں جھوٹے ہیں ');
INSERT INTO "al_hind" VALUES(26,224,'26:224','اور شاعروں کی بات پر چلیں وہی جو بے راہ ہیں ');
INSERT INTO "al_hind" VALUES(26,225,'26:225','تو نے نہیں دیکھا کہ وہ ہر میدان میں سر مارے پھرتے ہیں ');
INSERT INTO "al_hind" VALUES(26,226,'26:226','اور یہ کہ وہ کہتے ہیں جو نہیں کرتے ');
INSERT INTO "al_hind" VALUES(26,227,'26:227','مگر وہ لوگ جو یقین لائے اور کام کئے اچھے اور یاد کی اللہ کی بہت اور بدلہ لیا اس کے پیچھے کہ ان پر ظلم ہوا اور اب معلوم کر لیں گے ظلم کرنے والے کہ کس کروٹ الٹتے ہیں ');
INSERT INTO "al_hind" VALUES(27,1,'27:1','طٰس۔ یہ آیتیں ہیں قرآن اور کھلی کتاب کی');
INSERT INTO "al_hind" VALUES(27,2,'27:2','ہدایت اور خوشخبری ایمان والوں کے واسطے');
INSERT INTO "al_hind" VALUES(27,3,'27:3','جو قائم رکھتے ہیں نماز کو اور دیتے ہیں زکوٰۃ اور ان کو آخرت پر یقین ہے');
INSERT INTO "al_hind" VALUES(27,4,'27:4','جو لوگ نہیں مانتے آخرت کو اچھے دکھلائے ہم نے ان کی نظروں میں انکے کام سو وہ بہکے پھرتے ہیں ');
INSERT INTO "al_hind" VALUES(27,5,'27:5','وہی ہیں جن کے واسطے بری طرح کا عذاب ہے اور آخرت میں وہی ہیں خراب ');
INSERT INTO "al_hind" VALUES(27,6,'27:6','اور تجھ کو قرآن پہنچتا ہے ایک حکمت والے خبردار کے پاس سے ');
INSERT INTO "al_hind" VALUES(27,7,'27:7','جب کہا موسٰی نے اپنے گھر والوں کو میں نے دیکھی ہے آگ اب لاتا ہوں تمہارے پاس وہاں سے کچھ خبر یا لاتا ہوں انگارا سلگا کر شاید تم سینکو ');
INSERT INTO "al_hind" VALUES(27,8,'27:8','پھر جب پہنچا اسکے پاس آواز ہوئی کہ برکت ہے اس پر جو کوئی کہ آگ میں ہے اور جو اسکے آس پاس ہے اور پاک ہے وہ ذات اللہ کی جو رب سارے جہان کا ');
INSERT INTO "al_hind" VALUES(27,9,'27:9','اے موسٰی وہ میں اللہ ہوں زبردست حکمتوں والا ');
INSERT INTO "al_hind" VALUES(27,10,'27:10','اور ڈال دے لاٹھی اپنی پھر جب دیکھا اسکو پھنپھناتے جیسے سانپ کی سٹک لوٹا پیٹھ پھیر کر او رمڑ کر نہ دیکھا اے موسٰی مت ڈر میں جو ہوں میرےپاس نہیں ڈرتے رسول ');
INSERT INTO "al_hind" VALUES(27,11,'27:11','مگر جس نے زیادتی کی پھر بدلے میں نیکی کی برائی کے پیچھے تو میں بخشنے والا مہربان ہوں ');
INSERT INTO "al_hind" VALUES(27,12,'27:12','اور ڈال دے ہاتھ اپنا اپنے گریبان میں کہ نکلے سفید ہو کر نہ کسی برائی سے یہ دونوں ملکر نو نشانیاں لیکر جا فرعون اور اس کی قوم کی طرف بیشک وہ تھے لوگ نافرمان ');
INSERT INTO "al_hind" VALUES(27,13,'27:13','پھر جب پہنچیں انکے پاس ہماری نشانیاں سمجھانے کو بولے یہ جادو ہے صریح');
INSERT INTO "al_hind" VALUES(27,14,'27:14','اور ان کا انکار کیا اور ان کا یقین کر چکے تھے اپنے جی میں بے انصافی اور غرور سے سو دیکھ لے کیا ہوا انجام خرابی کرنے والوں کا ');
INSERT INTO "al_hind" VALUES(27,15,'27:15','اور ہم نے دیا داؤد اور سلیمان کو ایک علم اور بولے شکر اللہ کا جس نے ہم کو بزرگی دی اپنے بہت سے بندوں ایمان والوں پر ');
INSERT INTO "al_hind" VALUES(27,16,'27:16','اور قائم مقام ہوا سلیمان داؤد کا اور بولے اے لوگو ہم کو سکھائی ہے بولی اڑتے جانوروں کی اور دیا ہم کو ہر چیز میں سے بیشک یہی ہے فضیلت صریح');
INSERT INTO "al_hind" VALUES(27,17,'27:17','اور جمع کئے گئے سلیمان کے پاس اسکے لشکر جن اور انسان اور اڑتے جانور پھر انکی جماعتیں بنائی ');
INSERT INTO "al_hind" VALUES(27,18,'27:18','یہاں تک کہ جب پہنچے چیونٹیوں کے میدان پر کہا ایک چیونٹی نے اے چیونٹیو گھس جاؤ اپنے گھروں میں نہ پیس ڈالے تم کو سلیمان اور اسکی فوجیں اور انکو خبر بھی نہ ہو ');
INSERT INTO "al_hind" VALUES(27,19,'27:19','پھر مسکرا کر ہنس پڑا اسکی بات سے اور بولا اے میرے رب میری قسمت میں دےکہ شکر کروں تیرے احسان کا جو تو نے کیا مجھ پر اور میرے ماں باپ پر اور یہ کہ کروں کام نیک جو تو پسند کرے اور ملا لے مجھ کو اپنی رحمت سے اپنے نیک بندوں میں ');
INSERT INTO "al_hind" VALUES(27,20,'27:20','اور خبر لی اڑتے جانوروں کی تو کہا کیا ہے جو میں نہیں دیکھتا ہد ہد کو یا ہے وہ غائب ');
INSERT INTO "al_hind" VALUES(27,21,'27:21','اس کو سزا دوں گا سخت سزا یا ذبح کر ڈالوں گا یا لائے میرے پاس کوئی سند صریح ');
INSERT INTO "al_hind" VALUES(27,22,'27:22','پھر بہت دیر نہ کی کہ آ کر کہا میں لے آیا خبر ایک چیز کی کہ تجھ کو اس کی خبر نہ تھی اور آیا ہوں تیرے پاس سبا سے ایک خبر لیکر تحقیقی ');
INSERT INTO "al_hind" VALUES(27,23,'27:23','میں نے پایا ایک عورت کو جو ان پر بادشاہی کرتی ہے اور اسکو ہر ایک چیز ملی ہے اور اس کا ایک تخت ہے بڑا ');
INSERT INTO "al_hind" VALUES(27,24,'27:24','میں نے پایا کہ وہ اور اسکی قوم سجدہ کرتے ہیں سورج کو اللہ کے سوائے اور بھلے دکھلا رکھے ہیں ان کو شیطان نے انکے کام پھر روک دیا ہے انکو رستہ سے سو وہ راہ نہیں پاتے ');
INSERT INTO "al_hind" VALUES(27,25,'27:25','کیوں نہ سجدہ کریں اللہ کو جو نکالتا ہے چھپی ہوئی چیز آسمانوں میں اور زمین میں اور جانتا ہے جو چھپاتے ہو اور جو ظاہر کرتے ہو ');
INSERT INTO "al_hind" VALUES(27,26,'27:26','اللہ ہے کسی کی بندگی نہیں اس کے سوائے پروردگار تخت بڑے کا ');
INSERT INTO "al_hind" VALUES(27,27,'27:27','سلیمان نے کہا ہم اب دیکھتے ہیں تو نے سچ کہا یا تو جھوٹا ہے ');
INSERT INTO "al_hind" VALUES(27,28,'27:28','لیجا میرا یہ خط اور ڈال دے ان کی طرف پھر ان کے پاس سے ہٹ آ پھر دیکھ وہ کیا جواب دیتے ہیں ');
INSERT INTO "al_hind" VALUES(27,29,'27:29','کہنے لگی اے دربار والو میرے پاس ڈالا گیا ایک خط عزت کا');
INSERT INTO "al_hind" VALUES(27,30,'27:30','وہ خط ہے سلیمان کی طرف سے اور وہ یہ ہے شروع اللہ کے نام سے جو بیحد مہربان نہایت رحم والا ہے');
INSERT INTO "al_hind" VALUES(27,31,'27:31','کہ زور نہ کرو میرے مقابلہ میں اور چلے آؤ میرے سامنے حکمبردار ہو کر ');
INSERT INTO "al_hind" VALUES(27,32,'27:32','کہنے لگی اے دربار والو مشورہ دو مجھ کو میرے کام میں میں طے نہیں کرتی کوئی کام تمہارے حاضر ہونے تک ');
INSERT INTO "al_hind" VALUES(27,33,'27:33','وہ بولے ہم لوگ زور آور ہیں اور سخت لڑائی والے اور کام تیرے اختیار میں ہے سو تو دیکھ لے جو حکم کرے ');
INSERT INTO "al_hind" VALUES(27,34,'27:34','کہنے لگی بادشاہ جب گھستے ہیں کسی بستی میں اسکو خراب کر دیتے ہیں اور کر ڈالتے ہیں وہاں کے سرداروں کو بے عزت اور ایسا ہی کچھ کریں گے');
INSERT INTO "al_hind" VALUES(27,35,'27:35','اور میں بھیجتی ہوں ان کی طرف کچھ تحفہ پھر دیکھتی ہوں کیا جواب لیکر پھرتے ہیں بھیجے ہوئے ');
INSERT INTO "al_hind" VALUES(27,36,'27:36','پھر جب پہنچا سلیمان کے پاس بولا کیا تم میری اعانت کرتے ہو مال سے جو اللہ نے مجھ کو دیا ہے بہتر ہے اس سے جو تم کو دیا ہے بلکہ تم ہی اپنے تحفہ سے خوش رہو ');
INSERT INTO "al_hind" VALUES(27,37,'27:37','پھر جا ان کے پاس اب ہم پہنچتے ہیں ان پر ساتھ لشکروں کے جنکا مقابلہ نہ ہو سکے ان سے اور نکال دیں گے ان کو وہاں سے بےعزت کر کر اور وہ خوار ہوں گے ');
INSERT INTO "al_hind" VALUES(27,38,'27:38','بولا اے دربار والو تم میں کوئی ہے کہ لے آوے میرے پاس اس کا تخت پہلے اس سے کہ وہ آئیں میرے پاس حکم بردار ہو کر ');
INSERT INTO "al_hind" VALUES(27,39,'27:39','بولا ایک دیو جنوں میں سے میں لائے دیتا ہوں وہ تجھکو پہلے اس سے کہ تو اٹھے اپنی جگہ سے اور میں اس پر زورآور ہوں معتبر ');
INSERT INTO "al_hind" VALUES(27,40,'27:40','بولا وہ شخص جس کے پاس تھا ایک علم کتاب کا میں لائے دیتا ہوں تیرے پاس اسکو پہلے اس سے کہ پھر آئے تیری طرف تیری آنکھ پھر جب دیکھا اسکو دھرا ہوا اپنے پاس کہا یہ میرے رب کا فضل ہے میرے جانچنے کو کہ میں شکر کرتا ہوں یا ناشکری اور جو کوئی شکر کرے سو شکر کرے اپنے واسطے اور جو کوئی ناشکری کرے سو میرا رب بے پروا ہے کرم والا ');
INSERT INTO "al_hind" VALUES(27,41,'27:41','کہا روپ بدل دکھلاؤ اس عورت کے آگے اسکے تخت کا سمجھ پاتی ہے یا ان لوگوں میں ہوتی ہے جن کو سمجھ نہیں ');
INSERT INTO "al_hind" VALUES(27,42,'27:42','پھر جب وہ آ پہنچی کسی نے کہا کیا ایسا ہے تیرا تخت بولی گویا یہ وہی ہے اور ہم کو معلوم ہو چکا پہلے سے اور ہم ہو چکے حکم بردار ');
INSERT INTO "al_hind" VALUES(27,43,'27:43','اور روک دیا اس کو ان چیزوں سے جو پوجتی تھی اللہ کے سوائے البتہ وہ تھی منکر لوگوں میں ');
INSERT INTO "al_hind" VALUES(27,44,'27:44','کسی نے کہا اس عورت کو اندر چل محل میں پھر جب دیکھا اسکو خیال کیا کہ وہ پانی ہے گہرا اور کھولیں اپنی پنڈلیاں کہا یہ تو ایک محل ہے جڑے ہوئے ہیں اس میں شیشے بولی اے رب میں نے برا کیا ہے اپنی جان کا اور میں حکم بردار ہوئی ساتھ سلیمان کے اللہ کے آگے جو رب ہے سارے جہان کا ');
INSERT INTO "al_hind" VALUES(27,45,'27:45','اور ہم نے بھیجا تھا ثمود کی طرف ان کے بھائی صالح کو کہ بندگی کرو اللہ کی پھر وہ تو دو فرقے ہو کر لگے جھگڑنے ');
INSERT INTO "al_hind" VALUES(27,46,'27:46','کہا اے میری قوم کیوں جلدی مانگتے ہو برائی کو پہلے بھلائی سے کیوں نہیں گناہ بخشواتے اللہ سے شاید تم پر رحم ہو جائے ');
INSERT INTO "al_hind" VALUES(27,47,'27:47','بولے ہم نے منحوس قدم دیکھا تجھ کو اور تیرے ساتھ والوں کو کہا تمہاری بری قسمت اللہ کے پاس ہے کچھ نہیں تم لوگ جانچے جاتے ہو ');
INSERT INTO "al_hind" VALUES(27,48,'27:48','اور تھے اس شہر میں نو شخص کہ خرابی کرتے ملک میں اور اصلاح نہ کرتے ');
INSERT INTO "al_hind" VALUES(27,49,'27:49','بولے کہ آپس میں قسم کھاؤ اللہ کی کہ البتہ رات کو جا پڑیں ہم اس پر اسکے گھر پر پھر کہدیں گے اسکے دعویٰ کرنے والے کو ہم نے نہیں دیکھا جب تباہ ہوا اس کا گھر اور ہم بیشک سچ کہتے ہیں ');
INSERT INTO "al_hind" VALUES(27,50,'27:50','اور انہوں نے بنایا ایک فریب اور ہم نے بنایا ایک فریب اور انکو خبر نہ ہوئی ');
INSERT INTO "al_hind" VALUES(27,51,'27:51','پھر دیکھ لے کیسا ہوا انجام انکے فریب کا کہ ہلاک کر ڈالا ہم نے انکو اور انکی قوم کو سب کو ');
INSERT INTO "al_hind" VALUES(27,52,'27:52','سو یہ پڑے ہیں ان کے گھر ڈھیر ہوئے بسبب انکے انکار کے البتہ اس میں نشانی ہے ان لوگوں کے لئے جو جانتے ہیں ');
INSERT INTO "al_hind" VALUES(27,53,'27:53','اور بچا دیا ہم نے ان کو جو یقین لائے تھے اور بچتے رہے تھے ');
INSERT INTO "al_hind" VALUES(27,54,'27:54','اور لوط کو جب کہا اس نے اپنی قوم کو کیا تم کرتے ہو بے حیائی اور تم دیکھتے ہو ');
INSERT INTO "al_hind" VALUES(27,55,'27:55','کیا تم دوڑتے ہو مردوں پر للچا کر عورتوں کو چھوڑ کر کوئی نہیں تم لوگ بے سمجھ ہو ');
INSERT INTO "al_hind" VALUES(27,56,'27:56','پھر اور کچھ جواب نہ تھا اس کی قوم کا مگر یہی کہ کہتے تھے نکال دو لوط کے گھر کو اپنے شہر سے یہ لوگ ہیں ستھرے رہا چاہتے ');
INSERT INTO "al_hind" VALUES(27,57,'27:57','پھر بچا دیا ہم نے اسکو اور اسکے گھر والوں کو مگر اسکی عورت مقرر کر دیا تھا ہم نے اسکو رہ جانے والوں میں ');
INSERT INTO "al_hind" VALUES(27,58,'27:58','اور برسا دیا ہم نے ان پر برساؤ پھر کیا برا برساؤ تھا ان ڈرائے ہوؤں کا ');
INSERT INTO "al_hind" VALUES(27,59,'27:59','تو کہہ تعریف ہے اللہ کو اور سلام ہے اس کے بندوں پر جن کو اس نے پسند کیا بھلا اللہ بہتر ہے یا جنکو وہ شریک کرتے ہیں ');
INSERT INTO "al_hind" VALUES(27,60,'27:60','بھلا کس نے بنائے آسمان اور زمین اور اتار دیا تمہارے لئے آسمان سے پانی پھر اگائے ہم نے اس سے باغ رونق والے تمہارا کام نہ تھا کہ اگاتے ان کے درخت اب کوئی اور حاکم ہے اللہ کے ساتھ کوئی نہیں وہ لوگ راہ سے مڑتے ہیں ');
INSERT INTO "al_hind" VALUES(27,61,'27:61','بھلا کس نے بنایا زمین کو ٹھہرنے کی جگہ اور بنائیں اس کے بیچ میں ندیاں اور رکھے اسکے ٹھہرانے کو بوجھ اور رکھا دو دریا میں پردہ اب کوئی اور حاکم ہے اللہ کے ساتھ کوئی نہیں بہتوں کو ان میں سمجھ نہیں ');
INSERT INTO "al_hind" VALUES(27,62,'27:62','بھلا کون پہنچتا ہے بےکس کی پکار کو جب اسکو پکارتا ہے اور دور کر دیتا ہے سختی اور کرتا ہے تم کو نائب اگلوں کا زمین پر اب کوئی حاکم ہے اللہ کے ساتھ تم بہت کم دھیان کرتے ہو ');
INSERT INTO "al_hind" VALUES(27,63,'27:63','بھلا کون راہ بتاتا ہے تم کو اندھیروں میں جنگل کے اور دریا کے اور کون چلاتا ہے ہوائیں خوشخبری لانے والیاں اس کی رحمت سے پہلے اب کوئی حاکم ہے اللہ کے ساتھ اللہ بہت اوپر ہے اس سے جسکو شریک بتلاتے ہیں ');
INSERT INTO "al_hind" VALUES(27,64,'27:64','بھلا کون سرے سے بناتا ہے پھر اسکو دہرائے گا اور کون روزی دیتا ہے تمکو آسمان سے اور زمین سے اب کوئی حاکم ہے اللہ کے ساتھ تو کہہ لاؤ اپنی سند اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(27,65,'27:65','تو کہہ خبر نہیں رکھتا جو کوئی ہے آسمان اور زمین میں چھپی ہوئی چیز کی مگر اللہ اور ان کو خبر نہیں کب جی اٹھیں گے ');
INSERT INTO "al_hind" VALUES(27,66,'27:66','بلکہ تھک کر گر گیا ان کا فکر آخرت کے بارہ میں بلکہ ان کو شبہ ہے اس میں بلکہ وہ اس سے اندھے ہیں ');
INSERT INTO "al_hind" VALUES(27,67,'27:67','اور بولے وہ لوگ جو منکر ہیں کیا جب ہم ہو جائیں مٹی اور ہمارے باپ دادے کیا ہم کو زمین سے نکال لیں گے');
INSERT INTO "al_hind" VALUES(27,68,'27:68','وعدہ پہنچ چکا ہے اس کا ہم کو اور ہمارے باپ دادوں کو پہلے سے کچھ بھی نہیں یہ نقلیں ہیں اگلوں کی ');
INSERT INTO "al_hind" VALUES(27,69,'27:69','تو کہہ دے پھر و ملک میں تو دیکھو کیسا ہوا انجام کار گنہاگاروں کا ');
INSERT INTO "al_hind" VALUES(27,70,'27:70','اور غم نہ کر ان پر اور نہ خفا ہو ان کے فریب بنانے سے ');
INSERT INTO "al_hind" VALUES(27,71,'27:71','اور کہتے ہیں کب ہوگا یہ وعدہ اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(27,72,'27:72','تو کہہ کیا بعید ہے جو تمہاری پیٹھ پر پہنچ چکی ہو بعضی وہ چیز جس کی جلدی کر رہے ہو ');
INSERT INTO "al_hind" VALUES(27,73,'27:73','اور تیرا رب تو فضل رکھتا ہے لوگوں پر پر ان میں بہت لوگ شکر نہیں کرتے ');
INSERT INTO "al_hind" VALUES(27,74,'27:74','اور تیرا رب جانتا ہے جو چھپ رہا ہے انکے سینوں میں اور جو کچھ کہ ظاہر کرتے ہیں');
INSERT INTO "al_hind" VALUES(27,75,'27:75','اور کوئی چیز نہیں جو غائب ہو آسمان اور زمین میں مگر موجود ہے کھلی کتاب میں ');
INSERT INTO "al_hind" VALUES(27,76,'27:76','یہ قرآن سناتا ہے بنی اسرائیل کو بہت چیزیں جس میں وہ جھگڑ رہے ہیں');
INSERT INTO "al_hind" VALUES(27,77,'27:77','اور بیشک وہ ہدایت ہے اور رحمت ہے ایمان والوں کے واسطے ');
INSERT INTO "al_hind" VALUES(27,78,'27:78','تیرا رب ان میں فیصلہ کرے گا اپنی حکومت سے اور وہی ہے زبردست سب کچھ جاننے والا ');
INSERT INTO "al_hind" VALUES(27,79,'27:79','سو تو بھروسہ کر اللہ پر بیشک تو ہے صحیح کھلے رستہ پر ');
INSERT INTO "al_hind" VALUES(27,80,'27:80','البتہ تو نہیں سنا سکتا مردوں کو اور نہیں سنا سکتا بہروں کو اپنی پکار جب لوٹیں وہ پیٹھ پھیر کر');
INSERT INTO "al_hind" VALUES(27,81,'27:81','اور نہ تو دکھلا سکے اندھوں کو جب وہ راہ سے بچلیں تو تو سناتا ہے اسکو جو یقین رکھتا ہو ہماری باتوں پر سو وہ حکمبردار ہیں ');
INSERT INTO "al_hind" VALUES(27,82,'27:82','اور جب پڑ چکے گی ان پر بات نکالیں گے ہم ان کے آگے ایک جانور زمین سے ان سے باتیں کرے گا اس واسطے کہ لوگ ہماری نشانیوں کا یقین نہیں کرتے تھے ');
INSERT INTO "al_hind" VALUES(27,83,'27:83','اور جس دن گھیر بلائیں گے ہم اہر ایک فرقہ میں سے ایک جماعت جو جھٹلاتے تھے ہماری باتوں کو پھر انکی جماعت بندی ہو گی ');
INSERT INTO "al_hind" VALUES(27,84,'27:84','یہاں تک کہ جب حاضر ہو جائیں فرمائے گا کیوں جھٹلایا تم نے میری باتوں کو اور نہ آ چکی تھیں تمہاری سمجھ میں یا بولو کہ کیا کرتے تھے ');
INSERT INTO "al_hind" VALUES(27,85,'27:85','اور پڑ چکی ان پر بات اس واسطے کہ انہوں نے شرارت کی تھی اب وہ کچھ نہیں بول سکتے ');
INSERT INTO "al_hind" VALUES(27,86,'27:86','کیا نہیں دیکھتے کہ ہم نے بنائی رات کہ اس میں چین حاصل کریں اور دن بنایا دیکھنے کا البتہ اس میں نشانیاں ہیں ان لوگوں کے لئے جو یقین کرتے ہیں ');
INSERT INTO "al_hind" VALUES(27,87,'27:87','اور جس دن پھونکی جائیگی صور تو گھبرا جائے جو کوئی ہے آسمان میں اور جو کوئی ہے زمین میں مگر جس کو اللہ چاہے اور سب چلے آئیں اسکے آگے عاجزی سے ');
INSERT INTO "al_hind" VALUES(27,88,'27:88','اور تو دیکھے پہاڑوں کو سمجھے کہ وہ جم رہے ہیں اور وہ چلیں گے جیسے چلے بادل کاریگری اللہ کی جس نے سادھا ہے ہر چیز کو اس کو خبر ہے جو کچھ تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(27,89,'27:89','جو کوئی لیکر آیا بھلائی تو اس کو ملے اس سے بہتر اور ان کو گھبراہٹ سے اس دن امن ہے ');
INSERT INTO "al_hind" VALUES(27,90,'27:90','اور جو کوئی لیکر آیا برائی سو اوندھے ڈالیں ان کے منہ آگ میں وہی بدلہ پاؤ گے جو کچھ تم کیا کرتے تھے ');
INSERT INTO "al_hind" VALUES(27,91,'27:91','مجھ کو یہی حکم ہے کہ بندگی کروں اس شہر کے مالک کی جس نے اس کو حرمت دی اور اسی کی ہے ہر ایک چیز اور مجھ کو حکم ہے کہ رہوں حکم برداروں میں ');
INSERT INTO "al_hind" VALUES(27,92,'27:92','اور یہ کہ سنا دون قرآن پھر جو کوئی راہ پر آیا سو راہ پر آئے گا اپنے ہی بھلے کو اور جو کوئی بہکا رہا تو کہدے میں تو یہی ہوں ڈر سنا دینے والا ');
INSERT INTO "al_hind" VALUES(27,93,'27:93','اور کہہ تعریف ہے سب اللہ کو آگے دکھائے گا تم کو اپنے نمونے تو انکو پہچان لو گے اور تیرا رب بے خبر نہیں ان کاموں سے جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(28,1,'28:1','طٰسم۔');
INSERT INTO "al_hind" VALUES(28,2,'28:2','یہ آیتیں ہیں کھلی کتاب کی');
INSERT INTO "al_hind" VALUES(28,3,'28:3','ہم سناتے ہیں تجھ کو کچھ احوال موسٰی اور فرعون کا تحقیقی ان لوگوں کے واسطے جو یقین کرتے ہیں ');
INSERT INTO "al_hind" VALUES(28,4,'28:4','فرعون چڑھ رہا تھا ملک میں اور کر رکھا تھا وہاں کے لوگوں کو کئ فرقے کمزور کر رکھا تھا ایک فرقہ کو ان میں ذبح کرتا تھا انکے بیٹوں کو اور زندہ رکھتا تھا انکی عورتوں کو بیشک وہ تھا خرابی ڈالنے والا ');
INSERT INTO "al_hind" VALUES(28,5,'28:5','اور ہم چاہتے ہیں کو احسان کریں ان لوگوں پر جو کمزور ہوئے پڑے تھے ملک میں اور کر دیں ان کو سردار اور کر دیں ان کو قائم مقام');
INSERT INTO "al_hind" VALUES(28,6,'28:6','اور جما دیں ان کو ملک میں اور دکھا دیں فرعون اور ہامان کو اور انکے لشکروں کو انکے ہاتھ سے جس چیز کا ان کو خطرہ تھا ');
INSERT INTO "al_hind" VALUES(28,7,'28:7','اور ہم نے حکم بھیجا موسٰی کی ماں کو کہ اس کو دودھ پلاتی رہ پھر جب تجھ کو ڈر ہو اس کا تو ڈال دے اسکو دریا میں اور نہ خطرہ کر اور نہ غمگین ہو ہم پھر پہنچا دیں گے اسکو تیری طرف اور کریں گے اس کو رسولوں سے ');
INSERT INTO "al_hind" VALUES(28,8,'28:8','پھر اٹھا لیا اس کو فرعون کے گھر والوں نے کہ ہو ان کا دشمن اور غم میں ڈالنے والا بیشک فرعون اور ہامان اور ان کے لشکر تھے چوکنے والے ');
INSERT INTO "al_hind" VALUES(28,9,'28:9','اور بولی فرعون کی عورت یہ تو آنکھوں کی ٹھنڈک ہے میرے لئے اور تیرے لئے اسکو مت مارو کچھ بعید نہیں جو ہمارے کام آئے یا ہم اسکو کر لیں بیٹا اور ان کو کچھ خبر نہ تھی ');
INSERT INTO "al_hind" VALUES(28,10,'28:10','اور صبح کو موسٰی کی ماں کے دل میں قرار نہ رہا قریب تھی کہ ظاہر کر دے بیقراری کو اگر نہ ہم نے گرہ دی ہوتی اس کے دل پر اس واسطے کہ رہے یقین کرنے والوں میں ');
INSERT INTO "al_hind" VALUES(28,11,'28:11','اور کہہ دیا اس کی بہن کو پیچھے چلی جا پھر دیکھتی رہی اس کو اجنبی ہو کر اور ان کو خبر نہ ہوئی ');
INSERT INTO "al_hind" VALUES(28,12,'28:12','اور روک رکھا تھا ہم نے موسٰی سے دائیوں کو پہلے سے پھر بولی میں بتلاؤں تم کو ایک گھر والے کہ اس کو پال دیں تمہارے لئے اور وہ اس کا بھلا چاہنے والے ہیں ');
INSERT INTO "al_hind" VALUES(28,13,'28:13','پھر ہم نے پہنچا دیا اس کو اسکی ماں کی طرف کہ ٹھنڈی رہے اس کی آنکھ اور غمگین نہ ہو اور جانے کہ اللہ کا وعدہ ٹھیک ہے پر بہت لوگ نہیں جانتے ');
INSERT INTO "al_hind" VALUES(28,14,'28:14','اور جب پہنچ گیا اپنے زور پر اور سنھبل گیا دی ہم نے اس کو حکمت اور سمجھ اور اسی طرح ہم بدلہ دیتے ہیں نیکی والوں کو ');
INSERT INTO "al_hind" VALUES(28,15,'28:15','اور آیا شہر کے اندر جس وقت بیخبر ہوئے تھے وہاں کے لوگ پھر پائے اس نے دو مرد لڑتے ہوئے یہ ایک اسکے رفیقوں میں اور یہ دوسرا اسکے دشمنوں میں پھر فریاد کی اس سے اس نے جو تھا اس کے رفیقوں میں اس کی جو تھا اس کے دشمنوں میں پھر مکا مارا اسکو موسٰی نے پھر اسکو تمام کر دیا بولا یہ ہوا شیطان کے کام سے بیشک وہ دشمن ہے بہکانے والا صریح');
INSERT INTO "al_hind" VALUES(28,16,'28:16','بولا اے میرے رب میں نے برا کیا اپنی جان کا سو بخش مجھ کو پھر اس کو بخش دیا بیشک وہی ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(28,17,'28:17','بولا اے رب جیسا تو نے فضل کر دیا مجھ پر پھر میں کبھی نہ ہوں گا مددگار گنہگاروں کا ');
INSERT INTO "al_hind" VALUES(28,18,'28:18','پھر صبح کو اٹھا اس شہر میں ڈرتا ہوا انتظار کرتا ہوا پھر ناگہاں جس نے کل مدد مانگی تھی اس سے آج پھر فریاد کرتا ہے اس سے کہا موسٰی نے بیشک تو بے راہ ہے صریح ');
INSERT INTO "al_hind" VALUES(28,19,'28:19','پھر جب چاہا کہ ہاتھ ڈالے اس پر جو دشمن تھا ان دونوں کا بول اٹھا اے موسٰی کیا تو چاہتا ہے کہ خون کرے میرا جیسے خون کر چکا ہے کل ایک جان کا تیرا یہی جی چاہتا ہے کہ زبردستی کرتا پھرے ملک میں اور نہیں چاہتا کہ ہو صلح کرا دینے والا ');
INSERT INTO "al_hind" VALUES(28,20,'28:20','اور آیا شہر کے پرلے سرے سے ایک مرد دوڑتا ہوا کہا اے موسٰی دربار والے مشورہ کرتے ہیں تجھ پر کہ تجھ کو مار ڈالیں سو نکل جا میں تیرا بھلا چاہنے والا ہوں ');
INSERT INTO "al_hind" VALUES(28,21,'28:21','پھر نکلا وہاں سے ڈرتا ہوا راہ دیکھتا بولا اے رب بچا لے مجھ کو اس قوم بے انصاف سے');
INSERT INTO "al_hind" VALUES(28,22,'28:22','اور جب منہ کیا مدین کی سیدھ پر بولا امید ہے کہ میرا رب لے جائے مجھ کو سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(28,23,'28:23','اور جب پہنچا مدین کے پانی پر پایا وہاں ایک جماعت کو لوگوں کی پانی پلاتے ہوئے اور پایا ان سے ورے دو عورتوں کو کہ روکے ہوئے کھڑی تھیں اپنی بکریاں بولا تمہارا کیا حال ہے وہ بولیں ہم نہیں پلاتیں پانی چرواہوں کے پھیر لیجانے تک اور ہمارا باپ بوڑھا ہے بڑی عمر کا ');
INSERT INTO "al_hind" VALUES(28,24,'28:24','پھر اس نے پانی پلا دیا انکے جانوروں کو پھر ہٹ کر آیا چھاؤں کی طرف بولا اے رب تو جو چیز اتارے میری طرف اچھی میں اس کا محتاج ہوں ');
INSERT INTO "al_hind" VALUES(28,25,'28:25','پھر آئی اسکے پاس ان دونوں میں سے ایک چلتی تھی شرم سے بولی میرا باپ تجھ کو بلاتا ہے کہ بدلے میں دے حق اس کا کہ تو نے پانی پلا دیا ہمارے جانوروں کو پھر جب پہنچا اسکے پاس اور بیان کیا اس سے احوال کہا مت ڈر بچ آیا تو اس قوم بے انصاف سے ');
INSERT INTO "al_hind" VALUES(28,26,'28:26','بولی ان دونوں میں سے ایک اے باپ اس کو نوکر رکھ لے البتہ بہتر نوکر جو کہ تو رکھنا چاہے وہ ہے جو زور آور ہو امانت دار ');
INSERT INTO "al_hind" VALUES(28,27,'28:27','کہا میں چاہتا ہوں کہ بیاہ دوں تجھ کو ایک بیٹی اپنی ان دونوں میں سے اس شرط پر کہ تو میری نوکری کرے آٹھ برس پھر اگر تو پورے کر دے دس برس تو وہ تیری طرف سے ہے اور میں نہیں چاہتا کہ تجھ پر تکلیف ڈالوں تو پائے گا مجھ کو اگر اللہ نے چاہا نیک بختوں سے ');
INSERT INTO "al_hind" VALUES(28,28,'28:28','بولا یہ وعدہ ہو چکا میرے اور تیرے بیچ جونسی مدت ان دنوں میں پوری کر دوں سو زیادتی نہ ہو مجھ پر اور اللہ پر بھروسہ اس چیز کا جو ہم کہتے ہیں ');
INSERT INTO "al_hind" VALUES(28,29,'28:29','پھر جب پوری کر چکا موسٰیؑ وہ مدت اور لیکر چلا اپنے گھر والوں کو دیکھی کوہ طور کی طرف سے ایک آگ کہا اپنے گھر والوں کو ٹھہرو میں نے دیکھی ہے ایک آگ شاید لے آؤں تمہارے پاس وہاں کی کچھ خبر یا انگارا آگ کا تاکہ تم تاپو');
INSERT INTO "al_hind" VALUES(28,30,'28:30','پھر جب پہنچا اسکے پاس آواز ہوئی میدان کے داہنے کنارے سے برکت والے تختہ میں ایک درخت سے کہ اے موسٰی میں ہوں میں اللہ جہان کا رب');
INSERT INTO "al_hind" VALUES(28,31,'28:31','اور یہ کہ ڈالدے اپنی لاٹھی پھر جب دیکھا اسکو پھنپناتے جیسے سانپ کی سٹک الٹا پھرا منہ موڑ کر اور نہ دیکھا پیچھے پھر کر اے موسٰی آگے آ اور مت ڈر تجھ کو کچھ خطرہ نہیں');
INSERT INTO "al_hind" VALUES(28,32,'28:32','ڈال اپنا ہاتھ اپنے گریبان میں نکل آئے سفید ہو کر نہ کہ کسی برائی سے اور ملا لے اپنی طرف اپنا بازو ڈر سے سو یہ دو سندیں ہیں تیرے رب کی طرف سے فرعون اور اسکے سرداروں پر بیشک وہ تھے لوگ نافرمان');
INSERT INTO "al_hind" VALUES(28,33,'28:33','بولا اے رب میں نے خون کیا ہے ان میں ایک جان کا سو ڈرتا ہوں کہ مجھ کو مار ڈالیں گے ');
INSERT INTO "al_hind" VALUES(28,34,'28:34','اور میرا بھائی ہارون اس کی زبان چلتی ہے مجھ سے زیادہ سو اس کو بھیج میرے ساتھ مدد کو کہ میری تصدیق کرے میں ڈرتا ہوں کہ مجھ کو جھوٹا کریں ');
INSERT INTO "al_hind" VALUES(28,35,'28:35','فرمایا ہم مضبوط کر دیں گے تیرے بازو کو تیرے بھائی سے اور دیں گے تم کو غلبہ پھر وہ نہ پہنچ سکیں گے تم تک ہماری نشانیوں سے تم اور جو تمہارے ساتھ ہو غالب رہو گے ');
INSERT INTO "al_hind" VALUES(28,36,'28:36','پھر جب پہنچا انکے پاس موسٰی لیکر ہماری نشانیاں کھلی ہوئی بولے اور کچھ نہیں یہ جادو ہے باندھا ہوا اور ہم نے سنا نہیں یہ اپنے اگلے باپ دادوں میں ');
INSERT INTO "al_hind" VALUES(28,37,'28:37','اور کہا موسٰی نے میرا رب تو خوب جانتا ہے جو کوئی لایا ہے ہدایت کی بات اس کے پاس سے اور جس کو ملے گا آخرت کا گھر بیشک بھلا نہ ہوگا بے انصافوں کا ');
INSERT INTO "al_hind" VALUES(28,38,'28:38','اور بولا فرعون اے دربار والو مجھ کو تو معلوم نہیں تمہارا کوئی حاکم ہو میرے سوا سو آگ دے اے ہامان میرے واسطے گارے کو پھر بنا میرے واسطے ایک محل تا کہ میں جھانک کر دیکھ لوں موسٰی کے رب کو اور میری اٹکل میں تو وہ جھوٹا ہے ');
INSERT INTO "al_hind" VALUES(28,39,'28:39','اور بڑائی کرنے لگے وہ اور اس کے لشکر ملک میں ناحق اور سمجھے کہ وہ ہماری طرف پھر کر نہ آئیں گے');
INSERT INTO "al_hind" VALUES(28,40,'28:40','پھر پکڑ اہم نے اسکو اور اسکے لشکروں کو پھر پھینک دیا ہم نے انکو دریا میں سو دیکھ لے کیسا ہوا انجام گنہگاروں کا ');
INSERT INTO "al_hind" VALUES(28,41,'28:41','اور کیا ہم نے ان کو پیشوا کہ بلاتے ہیں دوزخ کی طرف اور قیامت کے دن ان کو مدد نہ ملے گی ');
INSERT INTO "al_hind" VALUES(28,42,'28:42','اور پیچھے رکھ دی ہم نے ان پر اس دنیا میں پھٹکار اور قیامت کے دن ان پر برائی ہے ');
INSERT INTO "al_hind" VALUES(28,43,'28:43','اور دی ہم نے موسٰیؑ کو کتاب بعد ا سکے کہ ہم غارت کر چکے پہلی جماعتوں کو سمجھانے والی لوگوں کو اور راہ بتانے والی اور رحمت تاکہ وہ یاد رکھیں ');
INSERT INTO "al_hind" VALUES(28,44,'28:44','اور تو نہ تھا غرب کی طرف جب ہم نے بھیجا موسٰیؑ کو حکم اور نہ تھا تو دیکھنے والا');
INSERT INTO "al_hind" VALUES(28,45,'28:45','لیکن ہم نے پیدا کیں کئ جماعتیں پھر دراز ہوئی ان پر مدت اور تو نہ رہتا تھا مدین والوں میں کہ ان کو سناتا ہماری آیتیں پر ہم رہے ہیں رسول بھیجتے ');
INSERT INTO "al_hind" VALUES(28,46,'28:46','اور تو نہ تھاطور کے کنارے جب ہم نے آواز دی لیکن یہ انعام ہے تیرے رب کا تاکہ تو ڈر سنا دے ان لوگوں کو جنکے پاس نہیں آیا کوئی ڈر سنانے والا تجھ سے پہلے تاکہ وہ یاد رکھیں ');
INSERT INTO "al_hind" VALUES(28,47,'28:47','اور اتنی بات کےلئے کہ کبھی آن پڑے ان پر آفت ان کاموں کی وجہ سے جنکو بھیج چکے ہیں انکے ہاتھ تو کہنے لگیں اے رب ہمارے کیوں نہ بھیج دیا ہمارے پاس کسی کو پیغام دے کر تو ہم چلتے تیری باتوں پر اور ہوتے ایمان والوں میں ');
INSERT INTO "al_hind" VALUES(28,48,'28:48','پھر جب پہنچی ان کو ٹھیک بات ہمارے پاس سے کہنے لگے کیوں نہ ملا اس رسول کو جیسا ملا تھا موسٰی کو کیا ابھی منکر نہیں ہو چکے اس سے جو موسیٰ کو ملا تھا اس سے پہلے کہنے لگے دونوں جادو ہیں آپس میں موافق اور کہنے لگے ہم دونوں کو نہیں مانتے ');
INSERT INTO "al_hind" VALUES(28,49,'28:49','تو کہہ اب تم لاؤ کوئی کتاب اللہ کے پاس کی جو ان دونوں سے بہتر ہو کہ میں اس پر چلوں اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(28,50,'28:50','پھر اگر نہ کر لائیں تیرا کہا تو جان لے کہ وہ چلتے ہیں نری اپنی خواہشوں پر اور اس سے گمراہ زیادہ کون جو چلے اپنی خواہش پر بدون راہ بتلائے اللہ کے بیشک اللہ راہ نہیں دیتا بے انصاف لوگوں کو ');
INSERT INTO "al_hind" VALUES(28,51,'28:51','اور ہم پے درپے بھیجتے رہے ہیں انکو اپنے کلام تاکہ وہ دھیان میں لائیں ');
INSERT INTO "al_hind" VALUES(28,52,'28:52','جن کو ہم نے دی ہے کتاب اس سے پہلے وہ اس پر یقین کرتے ہیں');
INSERT INTO "al_hind" VALUES(28,53,'28:53','اور جب ان کو سنائے تو کہیں ہم یقین لائے اس پر یہی ہے ٹھیک ہمارے رب کا بھیجا ہوا ہم ہیں اس سے پہلے کے حکم بردار ');
INSERT INTO "al_hind" VALUES(28,54,'28:54','وہ لوگ پائیں گے اپنا ثواب دوہرا اس بات پر کہ قائم رہے اور بھلائی کرتے ہیں برائی کے جواب میں اور ہمارا دیا ہو کچھ خرچ کرتے رہتے ہیں ');
INSERT INTO "al_hind" VALUES(28,55,'28:55','اور جب سنیں نکمی باتیں اس سے کنارہ کریں اورکہیں ہم کو ہمارے کام اور تم کو تمہارے کام سلامت رہو ہم کو نہیں چاہئیں بے سمجھ لوگ ');
INSERT INTO "al_hind" VALUES(28,56,'28:56','تو راہ پر نہیں لاتا جس کو چاہے پر اللہ راہ پر لائے جس کو چاہے اور وہی خوب جانتا ہے جو راہ پر آئیں گے ');
INSERT INTO "al_hind" VALUES(28,57,'28:57','اور کہنے لگے اگر ہم راہ پر آئیں تیرے ساتھ اچک لئے جائیں اپنے ملک سے کیا ہم نے جگہ نہیں دی ان کو حرمت والے پناہ کے مکان میں کھینچے چلے آتے ہیں اسکی طرف میوے ہر چیز کے روزی ہماری طرف سے پر بہت ان میں سمجھ نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(28,58,'28:58','اور کتنی غارت کر دیں ہم نے بستیاں جو اترا چلی تھیں اپنی گذران میں اب یہ ہیں ان کے گھر آباد نہیں ہوئے ان کے پیچھے مگر تھوڑے اور ہم ہیں آخر کو سب کچھ لینے والے ');
INSERT INTO "al_hind" VALUES(28,59,'28:59','اور تیرا رب نہیں غارت کرنے والا بستیوں کو جب تک نہ بھیج لے ان کی بڑی بستی میں کسی کو پیغام دے کر جو سنائے انکو ہماری باتیں اور ہم ہر گز نہیں غارت کرنے والے بستیوں کو مگر جب کہ وہاں کے لوگ گنہگار ہوں ');
INSERT INTO "al_hind" VALUES(28,60,'28:60','اور جو تم کو ملی ہے کوئی چیز سو فائدہ اٹھا لینا ہے دنیا کی زندگی میں اور یہاں کی رونق ہے اور جو اللہ کے پاس ہے سو بہتر ے اور باقی رہنے والا کیا تم کو سمجھ نہیں ');
INSERT INTO "al_hind" VALUES(28,61,'28:61','بھلاا یک شخص جس سے ہم نے وعدہ کیا ہے اچھا وعدہ سو ہ اس کو پانے والا ہے برابر ہے، اسکے جسکو ہم نے فائدہ دیا دنیا کی زندگانی کا پھر وہ قیامت کے دن پکڑ ا ہوا آیا ');
INSERT INTO "al_hind" VALUES(28,62,'28:62','اور جس دن انکو پکارے گا تو کہے گا کہاں ہیں میرے شریک جن کا تم دعویٰ کرتے تھے ');
INSERT INTO "al_hind" VALUES(28,63,'28:63','بولے جن پر ثابت ہو چکی بات اے رب یہ لوگ ہیں جن کو ہم نے بہکایا ان کو بہکایا جیسے ہم آپ بہکے ہم منکر ہوئے تیرے آگے وہ ہم کو نہ پوجتے تھے ');
INSERT INTO "al_hind" VALUES(28,64,'28:64','اور کہیں گے پکارو اپنے شریکوں کو پھر پکاریں گے انکو تو وہ جواب نہ دیں گے ان کو اور دیکھیں گے عذاب کاش کہ کسی طرح وہ راہ پائے ہوئے ہوتے ');
INSERT INTO "al_hind" VALUES(28,65,'28:65','اور جس دن ان کو پکارے گا تو فرمائے گا کیا جواب دیا تھا تم نے پیغام پہنچانے والوں کو');
INSERT INTO "al_hind" VALUES(28,66,'28:66','پھر بند ہو جائیں گی ان پر باتیں اس دن سو وہ آپس میں بھی نہ پوچھیں گے ');
INSERT INTO "al_hind" VALUES(28,67,'28:67','سو جس نے کہ توبہ کی اور یقین لایا اعمال کئے اچھے سو امید ہے کہ ہو چھوٹنے والوں میں ');
INSERT INTO "al_hind" VALUES(28,68,'28:68','اور تیرا رب پیدا کرتا ہے جو چاہے اور پسند کرے جس کو چاہے انکے ہاتھ میں نہیں پسند کرنا اللہ نرالا ہے اور بہت اوپر ہے اس چیز سے کہ شریک بتلاتے ہیں ');
INSERT INTO "al_hind" VALUES(28,69,'28:69','اور تیرا رب جانتا ہے جو چھپ رہا ہے انکے سینوں میں اور جو کچھ کہ ظاہر میں کرتے ہیں ');
INSERT INTO "al_hind" VALUES(28,70,'28:70','اور وہی اللہ ہے کسی کی بندگی نہیں اسکے سوا اسی کی تعریف ہے دنیا اور آخرت میں اور اسی کے ہاتھ حکم ہے اور اسی کے پاس پھیرے جاؤ گے ');
INSERT INTO "al_hind" VALUES(28,71,'28:71','تو کہہ دیکھو تو اگر اللہ رکھ دے تم پر رات ہمیشہ کو قیامت کے دن تک کون حاکم ہے اللہ کے سوائے کہ لائے تم کو کہیں سے روشنی پھر کیا تم سنتے نہیں ');
INSERT INTO "al_hind" VALUES(28,72,'28:72','تو کہہ دیکھو تو اگر رکھ دے اللہ تم پر دن ہمیشہ کو قیامت کے دن تک کون حاکم ہے اللہ کے سوائے کہ لائے تم کو رات جس میں آرام کرو پھر کیا تم نہیں دیکھتے ');
INSERT INTO "al_hind" VALUES(28,73,'28:73','اور اپنی مہربانی سے بنا دئے تمہارے واسطے رات اور دن کہ اس میں چین بھی کرو اور تلاش بھی کرو کچھ اس کا فضل اور تاکہ تم شکر کرو ');
INSERT INTO "al_hind" VALUES(28,74,'28:74','اور جس دن ان کو پکارے گا تو فرمائے گا کہاں ہیں میرے شریک جن کا تم دعویٰ کرتے تھے');
INSERT INTO "al_hind" VALUES(28,75,'28:75','اور جدا کریں گے ہم ہر فرقہ میں سے ایک احوال بتلانے والا پھر کہیں گے لاؤ اپنی سند تب جان لیں گے کہ سچ بات ہے اللہ کی اور کھوئی جائیں گی ان سے جو باتیں وہ جوڑتے تھے ');
INSERT INTO "al_hind" VALUES(28,76,'28:76','قارون جو تھا سو موسٰی کی قوم سے پھر شرارت کرنے لگا ان پر اور ہم نے دیے تھے اسکو خزانے اتنے کہ اسکی کنجیاں اٹھانے سے تھک جاتے کئ مرد زور آور جب کہا اس کو اسکی قوم نے اترا مت اللہ کو نہیں بھاتے اترانے والے ');
INSERT INTO "al_hind" VALUES(28,77,'28:77','اور جو تجھ کو اللہ نے دیا ہے اس سے کما لے پچھلا گھر اور نہ بھول اپنا حصہ دنیا سے اور بھلائی کر جیسے اللہ نے بھلائی کی تجھ سے اور مت چاہ خرابی ڈالنی ملک میں اللہ کو بھاتے نہیں خرابی ڈالنے والے ');
INSERT INTO "al_hind" VALUES(28,78,'28:78','بولا یہ مال تو مجھ کو ملا ہے ایک ہنر سے جو میرے پاس ہے کیا اس نے یہ نہ جانا کہ اللہ غارت کر چکا ہے اس سے پہلے کتنی جماعتیں جو اس سے زیادہ رکھتی تھیں زور اور زیادہ رکھتی تھیں مال کی جمع اور پوچھی نہ جائیں گنہگاروں سے ان کے گناہ ');
INSERT INTO "al_hind" VALUES(28,79,'28:79','پھر نکلا اپنی قوم کے سامنے اپنے ٹھاٹھ سےکہنے لگے جو لوگ طالب تھے دنیا کی زندگانی کے اے کاش ہم کو ملے جیسا کچھ ملا ہے قارون کو بیشک اسکی بڑی قسمت ہے ');
INSERT INTO "al_hind" VALUES(28,80,'28:80','اور بولے جن کو ملی تھی سمجھ اے خرابی تمہاری اللہ کا دیا ثواب بہتر ہے انکے واسطے جو یقین لائے اور کام کیا بھلا اور یہ بات انہی کے دل میں پڑتی ہے جو سہنے والے ہیں ');
INSERT INTO "al_hind" VALUES(28,81,'28:81','پھر دھنسا دیا ہم نے اس کو اور اسکے گھر کو زمین میں پھر نہ ہوئی س کی کوئی جماعت جو مدد کرتی اسکی اللہ کے سوائے اور نہ وہ خود مدد لا سکا ');
INSERT INTO "al_hind" VALUES(28,82,'28:82','اور فجر کو لگے کہنے جو کل شام آرزو کرتے تھے اس کا سا درجہ ارے خرابی یہ تو اللہ کھول دیتا ہے روزی جس کو چاہے اپنے بندوں میں اور تنگ کر دیتا ہے اگر نہ احسان کرتا ہم پر اللہ تو ہم کو بھی دھنسا دیتا اے خرابی یہ تو چھٹکارا نہیں پاتے منکر ');
INSERT INTO "al_hind" VALUES(28,83,'28:83','وہ گھر پچھلا ہے ہم دیں گے وہ ان لوگوں کو جو نہیں چاہتے اپنی بڑائی ملک میں اور نہ بگاڑ ڈالنا اور عاقبت بھلی ہے ڈرنے والوں کی ');
INSERT INTO "al_hind" VALUES(28,84,'28:84','جو لیکر آیا بھلائی اس کو ملنا ہے اس سے بہتر اور جو کوئی لے کر آیا برائی سو برائیاں کرنے والے ان کو وہی سزا ملے گی جو کچھ کرتے تھے ');
INSERT INTO "al_hind" VALUES(28,85,'28:85','جس نے حکم بھیجا تجھ پر قرآن کا وہ پھر لانے والا ہے تجھ کو پہلی جگہ تو کہہ میرا رب خوب جانتا ہے کون لایا ہے راہ کی سوجھ اور کون پڑا ہے صریح گمراہی میں ');
INSERT INTO "al_hind" VALUES(28,86,'28:86','اور تو توقع نہ رکھتا تھا کہ اتاری جائے تجھ پر کتاب مگر مہربانی سے تیرے رب کی سو تو مت ہو مددگار کافروں کا ');
INSERT INTO "al_hind" VALUES(28,87,'28:87','اور نہ ہو کہ وہ تجھ کو روک دیں اللہ کے حکموں سے بعد اس کے کہ اتر چکے تیری طرف اور بلا اپنے رب کی طرف اور مت ہو شریک والوں میں ');
INSERT INTO "al_hind" VALUES(28,88,'28:88','اور مت پکار اللہ کے سوائے دوسرا حاکم کسی کی بندگی نہیں اسکے سوائے ہر چیز فنا ہے مگر اس کا منہ اسی کا حکم ہے اور اسی کی طرف پھر جاؤ گے ');
INSERT INTO "al_hind" VALUES(29,1,'29:1','الٓم۔');
INSERT INTO "al_hind" VALUES(29,2,'29:2','کیا یہ سمجھتے ہیں لوگ کہ چھوٹ جائیں گے اتنا کہہ کر کہ ہم یقین لائے اور ان کو جانچ نہ لیں گے ');
INSERT INTO "al_hind" VALUES(29,3,'29:3','اور ہم نے جانچا ہے ان کو جو ان سے پہلے تھے سو البتہ معلوم کرے گا اللہ جو لوگ سچے ہیں اور البتہ معلوم کرے گا جھوٹوں کو ');
INSERT INTO "al_hind" VALUES(29,4,'29:4','کیا یہ سمجھتے ہیں جو لوگ کہ کرتے ہیں برائیاں کہ ہم سے بچ جائیں بری بات طے کرتے ہیں ');
INSERT INTO "al_hind" VALUES(29,5,'29:5','جو کوئی توقع رکھتا ہے اللہ کی ملاقات کی سو اللہ کا وعدہ آ رہا ہے اور وہ ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(29,6,'29:6','اور جو کوئی محنت اٹھائے سو اٹھاتا ہے اپنے ہی واسطے اللہ کو پروا نہیں جہان والوں کی ');
INSERT INTO "al_hind" VALUES(29,7,'29:7','اور جو لوگ یقین لائے اور کیے بھلے کام ہم اتار دیں گے ان پر سے برائیاں ان کی اور بدلا دیں گے ان کو بہتر سے بہتر کاموں کا ');
INSERT INTO "al_hind" VALUES(29,8,'29:8','اور ہم نے تاکید کر دی انسان کو اپنے ماں باپ سے بھلائی سے رہنے کی اور اگر وہ تجھ سے زور کریں کہ تو شریک کرے میرا جس کی تجھ کو خبر نہیں تو انکا کہنا مت مان مجھی تک پھر آنا ہے تم کو سو میں بتلا دوں گا تمکو جو کچھ تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(29,9,'29:9','اور جو لوگ یقین لائے اور بھلے کام کیے ہم ان کو داخل کریں گے نیک لوگوں میں ');
INSERT INTO "al_hind" VALUES(29,10,'29:10','اور ایک وہ لوگ ہیں کہ کہتے ہیں یقین لائے ہم اللہ پر پھر جب اس کو ایذا پہنچے اللہ کی راہ میں کرنے لگے لوگوں کے ستانے کو برابر اللہ کے عذاب کے اور اگر آ پہنچے مدد تیرے رب کی طرف سے تو کہنے لگیں ہم تو تمہارے ساتھ ہیں کیا یہ نہیں کہ اللہ خوب خبردار ہے جو کچھ سینوں میں ہے جہان والوں کے ');
INSERT INTO "al_hind" VALUES(29,11,'29:11','اور البتہ معلوم کرے گا اللہ ان لوگوں کو جو یقین لائے ہیں اور البتہ معلوم کرے گا جو لوگ دغاباز ہیں ');
INSERT INTO "al_hind" VALUES(29,12,'29:12','اور کہنے لگے منکر ایمان والوں کو تم چلو ہماری راہ اور ہم اٹھا لیں تمہارے گناہ اور وہ کچھ نہ اٹھائیں گے ان کے گناہ بیشک وہ جھوٹے ہیں');
INSERT INTO "al_hind" VALUES(29,13,'29:13','اور البتہ اٹھائیں گے اپنے بوجھ اور کتنے بوجھ ساتھ اپنے بوجھ کے اور البتہ ان سے پوچھ ہو گی قیامت کے دن جو باتیں کہ جھوٹ بناتے تھے ');
INSERT INTO "al_hind" VALUES(29,14,'29:14','اور ہم نے بھیجا نوح کو اس کی قوم کے پاس پھر رہا ان میں ہزار برس پچاس برس کم پھر پکڑا ان کو طوفان نے اور وہ گنہگار تھے ');
INSERT INTO "al_hind" VALUES(29,15,'29:15','پھر بچا دیا ہم نے اسکو اور جہاز والوں کو اور رکھا ہم نے جہاز کو نشانی جہاں والوں کے واسطے ');
INSERT INTO "al_hind" VALUES(29,16,'29:16','اور ابراہیم کو جب کہا اس نے اپنی قوم کو بندگی کرو اللہ کی اور ڈرتے رہو اس سے یہ بہتر ہے تمہارے حق میں اگر تم سمجھ رکھتے ہو');
INSERT INTO "al_hind" VALUES(29,17,'29:17','تم تو پوجتے ہو اللہ کے سوائے یہ بتوں کے تھان اور بناتے ہو جھوٹی باتیں بیشک جن کو تم پوجتے ہو اللہ کے سوائے وہ مالک نہیں تمہاری روزی کے سو تم ڈھونڈو اللہ کے یہاں روزی اور اس کی بندگی کرو اور اس کا حق مانو اسی کی طرف پھر جاؤ گے ');
INSERT INTO "al_hind" VALUES(29,18,'29:18','اور اگر تم جھٹلاؤ گے تو جھٹلا چکے ہیں بہت فرقے تم سے پہلے اور رسول کا ذمہ تو بس یہی ہے پیغام پہنچا دینا کھول کر ');
INSERT INTO "al_hind" VALUES(29,19,'29:19','کیا دیکھتے نہیں کیونکر شروع کرتا ہے اللہ پیدائش کو پھر اس کو دہرائے گا یہ اللہ پر آسان ہے ');
INSERT INTO "al_hind" VALUES(29,20,'29:20','تو کہہ ملک میں پھرو پھر دیکھو کیونکر شروع کیا ہے پیدائش کو پھر اللہ اٹھائے گا پچھلا اٹھان بیشک اللہ ہر چیز کر سکتا ہے');
INSERT INTO "al_hind" VALUES(29,21,'29:21','دکھ دے گا جس کو چاہے اور رحم کرے گا جس پر چاہے اور اسی کی طرف پھر جاؤ گے');
INSERT INTO "al_hind" VALUES(29,22,'29:22','اور تم عاجز کرنے والے نہیں زمین میں اور نہ آسمان میں اور کوئی نہیں تمہارا اللہ سے ورے حمایتی اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(29,23,'29:23','اور جو لوگ منکر ہوئے اللہ کی باتوں سے اور اس کے ملنے سے وہ ناامید ہوئے میری رحمت سے اور ان کے لیے دردناک عذاب ہے');
INSERT INTO "al_hind" VALUES(29,24,'29:24','پھر کچھ جواب نہ تھا اس کی قوم کا مگر یہی کہ بولے اس کو مار ڈالو یا جلا دو پھر اس کو بچا دیا اللہ نے آگ سے اس میں بڑی نشانیاں ہیں ان لوگوں کے لیے جو یقین لاتے ہیں ');
INSERT INTO "al_hind" VALUES(29,25,'29:25','اور ابراہیم بولا جو ٹھہرائے ہیں تم نے اللہ کے سوائے بتوں کے تھان سو دوستی کر کر آپس میں دنیا کی زندگانی میں پھر دن قیامت کے منکر ہو جاؤ گے ایک سے ایک اور لعنت کرو گے ایک کو ایک اور ٹھکانا تمہارا آگ ہے اور کوئی نہیں تمہارا مددگار ');
INSERT INTO "al_hind" VALUES(29,26,'29:26','پھر مان لیا اس کو لوط نے اور وہ بولا میں تو وطن چھوڑتا ہوں اپنے رب کی طرف بیشک وہی ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(29,27,'29:27','اور دیا ہم نے اس کو اسحٰق اور یعقوب اور رکھ دی اس کی اولاد میں پیغمبری اور کتاب اور دیا ہم نے اس کو اس کا ثواب دنیا میں اور وہ آخرت میں البتہ نیکوں سے ہے ');
INSERT INTO "al_hind" VALUES(29,28,'29:28','اور بھیجا لوط کو جب کہا اپنی قوم کو تم آتے ہو بیحیائی کے کام پر تم سے پہلے نہیں کیا وہ کسی نے جہان میں ');
INSERT INTO "al_hind" VALUES(29,29,'29:29','کیا تم دوڑتے ہو مردوں پر اور راہ مارتے ہو اور کرتے ہو اپنی مجلس میں برا کام پھر کچھ جواب نہ تھا اس کی قوم کا مگر یہی کہ بولے لے آ ہم پر عذاب ا للہ کا اگر تو ہے سچا ');
INSERT INTO "al_hind" VALUES(29,30,'29:30','بولا اے رب میری مدد کر ان شریر لوگوں پر ');
INSERT INTO "al_hind" VALUES(29,31,'29:31','اور جب پہنچے ہمارے بھیجے ہوئے ابراہیم کےپاس خوشخبری لے کر بولے ہم کو غارت کرنا ہے اس بستی والوں کو بیشک اس کے لوگ ہور رہے ہیں گنہگار ');
INSERT INTO "al_hind" VALUES(29,32,'29:32','بولا اس میں تو لوط بھی ہے وہ بولے ہم کو خوب معلوم ہے جو کوئی اس میں ہے ہم بچا لیں گے اسکو اور اس کے گھر والوں کو مگر اس کی عورت کہ رہے گی رہ جانے والوں میں ');
INSERT INTO "al_hind" VALUES(29,33,'29:33','اور جب پہنچے ہمارے بھیجے ہوئے لوط کے پاس ناخوش ہوا ان کو دیکھ کر اور تنگ ہوا دل میں اور وہ بولے مت ڈر اور غم نہ کھا ہم بچائیں گے تجھ کو اور تیرے گھر کو مگر عورت تیری رہ گئ رہ جانے والوں میں');
INSERT INTO "al_hind" VALUES(29,34,'29:34','ہم کو اتارنی ہے اس بستی والوں پر ایک آفت آسمان سے اس بات پر کہ وہ نافرمان ہو رہے تھے ');
INSERT INTO "al_hind" VALUES(29,35,'29:35','اور چھوڑ رکھا ہم نے اس کا نشان نظر آتا ہوا سمجھ دار لوگوں کے واسطے ');
INSERT INTO "al_hind" VALUES(29,36,'29:36','اور بھیجا مدین کے پاس ان کے بھائی شعیب کو پھر بولا اے قوم بندگی کرو اللہ کی اور توقع رکھو پچھلے دن کی اور مت پھرو زمین میں خرابی مچاتے ');
INSERT INTO "al_hind" VALUES(29,37,'29:37','پھر اس کو جھٹلایا تو پکڑ لیا انکو زلزلے نے پھر صبح کو رہ گئے اپنے گھروں میں اوندھے پڑے');
INSERT INTO "al_hind" VALUES(29,38,'29:38','اور ہلاک کیا عاد کو اور ثمود کو اور تم پر حال کھل چکا ہے ان کے گھروں سے اور فریفتہ کیا انکو شیطان نے ان کے کاموں پر پھر روک دیا ان کو راہ سے اور تھے ہوشیار ');
INSERT INTO "al_hind" VALUES(29,39,'29:39','اور ہلاک کیا قارون اور فرعون اور ہامان کو اور ان کے پاس پہنچا موسٰی کھلی نشانیاں لے کر پھر بڑائی کرنے لگے ملک میں اور نہیں تھے ہم سے جیت جانے والے ');
INSERT INTO "al_hind" VALUES(29,40,'29:40','پھر سب کو پکڑ ا ہم نے اپنے اپنے گناہ پر پھر کوئی تھا کہ اس پر ہم نے بھیجا پتھراؤ ہوا سے اور کوئی تھا کہ اس کو پکڑا چنگھاڑ نے اور کوئی تھا کہ اس کو دھنسا دیا ہم نے زمین میں اور کوئی تھا کہ اس کو ڈبا دیا ہم نے اور اللہ ایسا نہ تھا کہ ان پر ظلم کرے پر تھے وہ اپنا آپ ہی برا کرتے ');
INSERT INTO "al_hind" VALUES(29,41,'29:41','مثال ان لوگوں کی جنہوں نے پکڑے اللہ کو چھوڑ کر اور حمایتی جیسے مکڑی کی مثال بنا لیا اس نے ایک گھر اور سب گھروں میں بودا سو مکڑی کا گھر اگر ان کو سمجھ ہوتی ');
INSERT INTO "al_hind" VALUES(29,42,'29:42','اللہ جانتا ہے جس جس کو وہ پکارتے ہیں اس کے سوائے کوئی چیز ہو اور وہ زبردست ہے حکمتوں والا ');
INSERT INTO "al_hind" VALUES(29,43,'29:43','اور یہ مثالیں بٹھلاتے ہیں ہم لوگوں کے واسطے اور ان کو سمجھتے وہی ہیں جن کو سمجھ ہے ');
INSERT INTO "al_hind" VALUES(29,44,'29:44','اللہ نے بنائے آسمان اور زمین جیسے چاہئیں اس میں نشانی ہے یقین لانے والوں کے لئے ');
INSERT INTO "al_hind" VALUES(29,45,'29:45','تو پڑھ جو اتری تیری طرف کتاب اور قائم رکھ نماز بیشک نماز روکتی ہے بیحیائی اور بری بات سے اور اللہ کی یاد ہے سب سے بڑی اور اللہ کو خبر ہے جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(29,46,'29:46','اور جھگڑا نہ کرو اہل کتاب سے مگر اس طرح پر جو بہتر ہو مگر جو ان میں بے انصاف ہیں اور یوں کہو کہ ہم مانتے ہیں جو اترا ہم کو اور اترا تم کو اور بندگی ہماری اور تمہاری ایک ہی کو ہے اور ہم اسی کے حکم پر چلتے ہیں ');
INSERT INTO "al_hind" VALUES(29,47,'29:47','اور ویسی ہی ہم نے اتاری تجھ پر کتاب سو جن کو ہم نے کتاب دی ہے وہ اس کو مانتے ہیں اور ان مکہ والوں میں بھی بعضے ہیں کہ اس کو مانتے ہیں اور منکر وہی ہیں ہماری باتوں سے جو نافرمان ہیں ');
INSERT INTO "al_hind" VALUES(29,48,'29:48','اور تو پڑھتا نہ تھا اس سے پہلے کوئی کتاب اور نہ لکھتا تھا اپنے داہنے ہاتھ سے تب تو البتہ شبہ میں پڑتے یہ جھوٹے ');
INSERT INTO "al_hind" VALUES(29,49,'29:49','بلکہ یہ قرآن تو آیتیں ہیں صاف ان لوگوں کے سینوں میں جن کو ملی ہے سمجھ اور منکر نہیں ہماری باتوں سے مگر وہی جو بے انصاف ہیں ');
INSERT INTO "al_hind" VALUES(29,50,'29:50','اور کہتے ہیں کیوں نہ اتریں اس پر کچھ نشانیاں اس کے رب سے تو کہہ نشانیاں تو ہیں اختیار میں اللہ کے اور میں تو بس سنا دینے والا ہوں کھول کر ');
INSERT INTO "al_hind" VALUES(29,51,'29:51','کیا ان کو یہ کافی نہیں کہ ہم نے تجھ پر اتاری کتاب کہ ان پر پڑھی جاتی ہے بیشک اس میں رحمت ہے اور سمجھانا ان لوگوں کو جو مانتے ہیں ');
INSERT INTO "al_hind" VALUES(29,52,'29:52','تو کہہ کافی ہے اللہ میرے اور تمہارے بیچ گواہ جانتا ہے جو کچھ ہے آسمان اور زمین میں اور جو لوگ یقین لاتے ہیں جھوٹ پر اور منکر ہوئے ہیں اللہ سے وہی ہیں نقصان پانے والے ');
INSERT INTO "al_hind" VALUES(29,53,'29:53','اور جلد مانگتے ہیں تجھ سے آفت اور اگر نہ ہوتا ایک وعدہ مقررہ تو آ پہنچتی ان پر آفت اور البتہ آئے گی ان پر اچانک اور ان کو خبر نہ ہو گی ');
INSERT INTO "al_hind" VALUES(29,54,'29:54','جلدی مانگتے ہیں تجھ سے عذاب اور دوزخ گھیر رہی ہے مجرموں کو ');
INSERT INTO "al_hind" VALUES(29,55,'29:55','جس دن گھیر لے گا ان کو عذاب ان کے اوپر سے اور پاؤں کے نیچے سے اور کہے گا چکھو جیسا کچھ تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(29,56,'29:56','اے بندو میرے جو یقین لائے ہو میری زمین کشادہ ہے سو مجھی کو بندگی کر و ');
INSERT INTO "al_hind" VALUES(29,57,'29:57','جو جی ہے سو چکھے گا موت پھر ہماری طرف پھر آؤ گے ');
INSERT INTO "al_hind" VALUES(29,58,'29:58','اور جو لوگ یقین لائے اور کیے بھلے کام ان کو ہم جگہ دیں گے بہشت میں جھروکے نیچے بہتی ہیں ان کےنہریں سدا رہیں ان میں خوب ثواب ملا کام والوں کو');
INSERT INTO "al_hind" VALUES(29,59,'29:59','جنہوں نے صبر کیا اور اپنے رب پر بھروسہ رکھا ');
INSERT INTO "al_hind" VALUES(29,60,'29:60','اور کتنے جانور ہیں جو اٹھا نہیں رکھتے اپنی روزی اللہ روزی دیتا ہے انکو اور تم کو بھی اور وہی ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(29,61,'29:61','اور اگر تو لوگوں سے پوچھے کہ کس نے بنایا ہے آسمان اور زمین کو اور کام میں لگایا سورج اور چاند کو تو کہیں اللہ نے پھر کہاں سے الٹ جاتے ہیں ');
INSERT INTO "al_hind" VALUES(29,62,'29:62','اللہ پھیلاتا ہے روزی جس کے واسطے چاہے اپنے بندوں میں اور ماپ کر دیتا ہے جسکو چاہے بیشک اللہ ہر چیز سے خبردار ہے ');
INSERT INTO "al_hind" VALUES(29,63,'29:63','اور جو تو پوچھے ان سے کس نے اتارا آسمان سے پانی پھر زندہ کر دیا اس سے زمین کو اس کے مرجانے کے بعد تو کہیں اللہ نے تو کہہ سب خوبی اللہ کو ہے پر بہت لوگ نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(29,64,'29:64','اور یہ دنیا کا جینا تو بس جی بہلانا اور کھیلنا ہے اور پچھلا گھر جو ہے سو وہی ہے زندہ رہنا اگر ان کو سمجھ ہوتی ');
INSERT INTO "al_hind" VALUES(29,65,'29:65','پھر جب سوار ہوئے کشتی میں پکارنے لگے اللہ کو خالص اسی پر رکھ کر اعتقاد پھر جب بچا لایا انکو زمین کی طرف اسی وقت لگے شریک بنانے');
INSERT INTO "al_hind" VALUES(29,66,'29:66','تاکہ مکرتے رہیں ہمارے دیے ہوئے سے اور مزے اڑاتے رہیں سو عنقریب جان لیں گے ');
INSERT INTO "al_hind" VALUES(29,67,'29:67','کیا نہیں دیکھتے کہ ہم نے رکھ دی ہے پناہ کی جگہ امن کی اور لوگ اچکے جاتے ہیں ان کے آس پاس سے کیاجھوٹ پر یقین رکھتے ہیں اور اللہ کا احسان نہیں مانتے ');
INSERT INTO "al_hind" VALUES(29,68,'29:68','اور اس سے زیادہ بے انصاف کون جو باندھے اللہ پر جھوٹ یا جھٹلائے سچی بات کو جب اس تک پہنچے کیا دوزخ میں بسنے کی جگہ نہیں منکروں کے لئے ');
INSERT INTO "al_hind" VALUES(29,69,'29:69','اور جنہوں نے محنت کی ہمارے واسطے ہم سجھا دیں گے ان کو اپنی راہیں اور بیشک اللہ ساتھ ہے نیکی والوں کے ');
INSERT INTO "al_hind" VALUES(30,1,'30:1','الٓم۔');
INSERT INTO "al_hind" VALUES(30,2,'30:2','مغلوب ہو گئے ہیں رومی');
INSERT INTO "al_hind" VALUES(30,3,'30:3','ملتے ہوئے ملک میں اور وہ اس مغلوب ہونے کے بعد عنقریب غالب ہوں گے');
INSERT INTO "al_hind" VALUES(30,4,'30:4','چند برسوں میں اللہ کے ہاتھ ہیں سب کام پہلے اور پچھلے اور اس دن خوش ہوں گے مسلمان');
INSERT INTO "al_hind" VALUES(30,5,'30:5','اللہ کی مدد سے مدد کرتا ہے جس کی چاہتا ہے اور وہی ہے زبردست رحم والا ');
INSERT INTO "al_hind" VALUES(30,6,'30:6','اللہ کا وعدہ ہو چکا خلاف نہ کرے گا اللہ اپنا وعدہ لیکن بہت لوگ نہیں جانتے ');
INSERT INTO "al_hind" VALUES(30,7,'30:7','جانتے ہیں اوپر اوپر دنیا کے جینے کو اور وہ لوگ آخرت کی خبر نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(30,8,'30:8','کیا دھیان نہیں کرتے اپنے جی میں کہ اللہ نے جو بنائے آسمان اور زمین اور جو کچھ ان کے بیچ میں ہے سو ٹھیک سادہ کر اور وعدہ مقرر پر اور بہت لوگ اپنے رب کا ملنا نہیں مانتے ');
INSERT INTO "al_hind" VALUES(30,9,'30:9','کیا انہوں نے سیر نہیں کی ملک کی جو دیکھیں انجام کیسا ہوا ان سے پہلوں کا ان سے زیادہ تھے زور میں اور جوتا انہوں نے زمین کو اور بسایا اس کو ان کے بسانے سے زیادہ اور پہنچے ان کے پاس رسول انکے لے کر کھلے حکم سو اللہ نہ تھا ان پر ظلم کرنے والا لیکن وہ اپنا آپ برا کرتے تھے ');
INSERT INTO "al_hind" VALUES(30,10,'30:10','پھر ہوا انجام برا کرنے والوں کا برا اس واسطے کہ جھٹلاتے تھے اللہ کی باتیں اور ان پر ٹھٹھے کرتے تھے ');
INSERT INTO "al_hind" VALUES(30,11,'30:11','اللہ بناتا ہے پہلی بار پھر اس کو دہرائے گا پھر اسی کی طرف جاؤ گے');
INSERT INTO "al_hind" VALUES(30,12,'30:12','اور جس دن برپا ہو گی قیامت آس توڑ کر رہ جائیں گے گنہگار');
INSERT INTO "al_hind" VALUES(30,13,'30:13','اور نہ ہوں گے ان کے شریکوں میں کوئی انکے سفارش کرنے والے اور وہ ہو جائیں گے اپنے شریکوں سے منکر ');
INSERT INTO "al_hind" VALUES(30,14,'30:14','اور جس دن قائم ہو گی قیامت اس دن لوگ ہوں گے قسم قسم ');
INSERT INTO "al_hind" VALUES(30,15,'30:15','سو جو لوگ یقین لائے اور کیے بھلے کام سو باغ میں ہوں گے انکی آؤ بھگت ہو گی ');
INSERT INTO "al_hind" VALUES(30,16,'30:16','اور جو منکر ہوئے اور جھٹلائیں ہماری باتیں اور ملنا پچھلے گھر کا سو وہ عذاب میں پکڑے آئیں گے');
INSERT INTO "al_hind" VALUES(30,17,'30:17','سو پاک اللہ کی یاد کرو جب شام کرو اور جب صبح کرو');
INSERT INTO "al_hind" VALUES(30,18,'30:18','اور اسی کی خوبی ہے آسمان میں اور زمین میں اور پچھلے وقت اور جب دوپہر ہو ');
INSERT INTO "al_hind" VALUES(30,19,'30:19','نکالتا ہے زندہ کو مردہ سے اور نکالتا ہے مردہ کو زندہ سے اور زندہ کرتا ہے زمین کو اس کے مرنے کے پیچھے اور اسی طرح تم نکالے جاؤ گے ');
INSERT INTO "al_hind" VALUES(30,20,'30:20','اور اسکی نشانیوں میں سے ہے یہ کہ تم کو بنایا پھر اب تم انسان ہو زمین میں پھیلے پڑے ');
INSERT INTO "al_hind" VALUES(30,21,'30:21','اور اسکی نشانیوں سے ہے یہ کہ بنا دیے تمہارے واسطے تمہاری قسم سے جوڑے کہ چین سے رہو انکے پاس اور رکھا تمہارے بیچ میں پیار اور مہربانی البتہ اس میں بہت پتے کی باتیں ہیں انکے لئے جو دھیان کرتے ہیں ');
INSERT INTO "al_hind" VALUES(30,22,'30:22','اور اسکی نشانیوں سے ہے آسمان اور زمین کا بنانا اور طرح طرح کی بولیاں تمہاری اور رنگ اس میں بہت نشانیاں ہیں سمجھنے والوں کو ');
INSERT INTO "al_hind" VALUES(30,23,'30:23','اور اسکی نشانیوں سے ہے تمہارا سونا رات اور دن میں اور تلاش کرنا اس کے فضل سے اس میں بہت پتے ہیں ان کو جو سنتے ہیں ');
INSERT INTO "al_hind" VALUES(30,24,'30:24','اور اسکی نشانیوں سے ہے یہ کہ دکھلاتا ہے تمکو بجلی ڈر اور امید کے لئے اور اتارتا ہے آسمان سے پانی پھر زندہ کرتا ہے اس سے زمین کر مر گئے پیچھے اس میں بہت پتے ہیں ان کے لئے جو سوچتے ہیں ');
INSERT INTO "al_hind" VALUES(30,25,'30:25','اور اس کی نشانیوں سے یہ ہے کہ کھڑ ا ہے آسمان اور زمین اس کے حکم سے پھر جب پکارے گا تم کو ایک بار زمین سے اسی وقت تم نکل پڑو گے ');
INSERT INTO "al_hind" VALUES(30,26,'30:26','اور اسی کا ہے جو کوئی ہے آسمان اور زمین میں سب اس کے حکم کے تابع ہیں ');
INSERT INTO "al_hind" VALUES(30,27,'30:27','اور وہی ہے جو پہلی بار بناتا ہے پھر اس کو دھرائے گا اور وہ آسان ہے اس پر اور اسکی شان سب سے اوپر ہے آسمان اور زمین میں اور وہی ہے زبردست حکمتوں والا ');
INSERT INTO "al_hind" VALUES(30,28,'30:28','بتلائی تم کو ایک مثل تمہارے اندر سے دیکھو جو تمہارے ہاتھ کے مال ہیں ان میں ہیں کوئی ساجھی تمہارے ہماری دی ہوئی روزی میں کہ تم سب اس میں برابر رہو خطر رکھو ان کا جیسے خطرہ رکھو اپنوں کا یوں کھول کر بیان کرتے ہیں ہم نشانیاں ان لوگوں کے لئے جو سمجھتے ہیں ');
INSERT INTO "al_hind" VALUES(30,29,'30:29','بلکہ چلتے ہیں یہ بے انصاف اپنی خواہشوں پر بن سمجھے سو کون سمجھائے جس کو اللہ نے بھٹکایا اور کوئی نہیں ان کا مددگار ');
INSERT INTO "al_hind" VALUES(30,30,'30:30','سو تو سیدھا رکھ اپنا منہ دین پر ایک طرف کا ہو کر وہی تراش اللہ کی جس پر تراشا لوگوں کو بدلنا نہیں اللہ کے بنائے کو یہی ہے دین سیدھا و لیکن اکثر لوگ نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(30,31,'30:31','سب رجوع ہو کر اسکی طرف اور اس سے ڈرتے رہو اور قائم رکھو نماز اور مت ہو شرک کرنے والوں میں');
INSERT INTO "al_hind" VALUES(30,32,'30:32','جنہوں نے کہ پھوٹ ڈالی اپنے دین میں اور ہو گئے ان میں بہت فرقے ہر فرقہ جو اس کے پاس ہے اس پر فریفتہ ہے ');
INSERT INTO "al_hind" VALUES(30,33,'30:33','اور جب پہنچے لوگوں کو کچھ سختی تو پکاریں اپنے رب کو اس کی طرف رجوع ہو کر پھر جہاں چکھائی ان کو اپنی طرف سے کچھ مہربانی اسی وقت ایک جماعت ان میں اپنے رب کا شریک لگی بتانے');
INSERT INTO "al_hind" VALUES(30,34,'30:34','کہ منکر ہوجائیں ہمارے دیے ہوئے سے سو مزے اڑا لو اب آگے جان لو گے ');
INSERT INTO "al_hind" VALUES(30,35,'30:35','کیا ہم نے ان پر اتاری ہے کوئی سند سو وہ بول رہی ہے جو یہ شریک بتاتے ہیں ');
INSERT INTO "al_hind" VALUES(30,36,'30:36','اور جب چکھائیں ہم لوگوں کو کچھ مہربانی اس پر پھولے نہیں سماتے اور اگر آ پڑے ان پر کچھ برائی اپنے ہاتھوں کے بھیجے ہوئے پر تو آس توڑ بیٹھیں ');
INSERT INTO "al_hind" VALUES(30,37,'30:37','کیا نہیں دیکھ چکے کہ اللہ پھیلا دیتا ہے روزی کو جس پر چاہے اور ماپ کر دیتا ہے جسکو چاہے اس میں نشانیاں ہیں ان لوگوں کو جو یقین رکھتے ہیں ');
INSERT INTO "al_hind" VALUES(30,38,'30:38','سو تو دے قرابت والے کو اس کا حق اور محتاج کو اور مسافر کو یہ بہتر ہے ان کے لئے جو چاہتے ہیں اللہ کا منہ اور وہی ہیں جن کا بھلا ہے ');
INSERT INTO "al_hind" VALUES(30,39,'30:39','اور جو دیتے ہو بیاج پر کہ بڑھتا رہے لوگوں کے مال میں سو وہ نہیں بڑھتا اللہ کے یہاں اور جو دیتے ہو پاک دل سے چاہ کر رضامندی اللہ کی سو یہ وہی ہیں جن کے دونے ہوئے ');
INSERT INTO "al_hind" VALUES(30,40,'30:40','اللہ وہ ہی ہے جس نے تم کو بنایا پھر تم کو روزی دی پھر تم کو مارتا ہے پھر تم کو جلائے گا کوئی ہے تمہارے شریکوں میں جو کر سکے ان کاموں میں سے ایک کام وہ نرالا ہے اور بہت اوپر ہے اس سے کہ شریک بتلاتے ہیں ');
INSERT INTO "al_hind" VALUES(30,41,'30:41','پھیل پڑی ہے خرابی جنگل میں اور دریا میں لوگوں کے ہاتھ کی کمائی سے چکھانا چاہیے ان کو کچھ مزہ ان کے کام کا تاکہ وہ پھر آئیں ');
INSERT INTO "al_hind" VALUES(30,42,'30:42','تو کہہ پھرو ملک میں تو دیکھو کیسا ہوا انجام پہلوں کا بہت ان میں تھے شرک کرنے والے ');
INSERT INTO "al_hind" VALUES(30,43,'30:43','سو تو سیدھا رکھ اپنا منہ سیدھی ر اہ پر اس سے پہلے کہ آ پہنچے وہ دن جس کو پھرنا نہیں اللہ کی طرف سے اس دن لوگ جدا جدا ہوں گے ');
INSERT INTO "al_hind" VALUES(30,44,'30:44','جو منکر ہوا سو اس پر پڑے اسکا منکر ہونا او رجو کوئی کرے بھلے کام سو وہ اپنی راہ سنوارتے ہیں ');
INSERT INTO "al_hind" VALUES(30,45,'30:45','تاکہ وہ بدلہ دے ان کو جو یقین لائے اور کام کئے بھلے اپنے فضل سے بیشک اس کو نہیں بھاتے انکار والے ');
INSERT INTO "al_hind" VALUES(30,46,'30:46','اور اس کی نشانیوں میں ایک یہ ہے کہ چلاتا ہے ہوائیں خوشخبری لانے والیاں اور تاکہ چکھائے تمکو کچھ مزہ اپنی مہربانی کا اور تاکہ چلیں جہاز اسکے حکم سے اور تاکہ تلاش کرو اس کے فضل سے اور تاکہ تم حق مانو ');
INSERT INTO "al_hind" VALUES(30,47,'30:47','اور ہم بھیج چکے ہیں تجھ سے پہلے کتنے رسول اپنی اپنی قوم کے پاس سو پہنچے انکے پاس نشانیاں لے کر پھر بدلہ لیا ہم نے ان سے جو گنہگار تھے اور حق ہے ہم پر مدد ایمان والوں کی ');
INSERT INTO "al_hind" VALUES(30,48,'30:48','اللہ ہے جو چلاتا ہے ہوائیں پھر وہ اٹھاتی ہیں بادل کو پھر پھیلا دیتا ہے اس کو آسمان میں جس طرح چاہے اور رکھتا ہے اس کو تہہ بہ تہہ پھر تو دیکھے مینہ کو کہ نکلتا ہے اس کے بیچ میں سے پھر جب اس کو پہنچاتا ہے جس کو کہ چاہتا ہے اپنے بندوں میں تبھی وہ لگتے ہیں خوشیاں کرنے ');
INSERT INTO "al_hind" VALUES(30,49,'30:49','اور پہلے سے ہو رہے تھے اس کے اترنے سے پہلے ہی نا امید ');
INSERT INTO "al_hind" VALUES(30,50,'30:50','سو دیکھ لے اللہ کی مہربانی کی نشانیاں کیونکر زندہ کرتا ہے زمین کو ا سکے مرگئے پیچھے بیشک وہی ہے مردوں کو زندہ کرنے والا اور وہ ہرچیز کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(30,51,'30:51','اور اگر ہم بھیجیں ایک ہوا پھر دیکھیں وہ کھیتی کو کہ زرد پڑ گئ تو لگیں اس کے پیچھے ناشکری کرنے ');
INSERT INTO "al_hind" VALUES(30,52,'30:52','سو تو سنا نہیں سکتا مردوں کو اور نہیں سنا سکتا بہروں کو پکارنا جب کہ پھریں پیٹھ دے کر');
INSERT INTO "al_hind" VALUES(30,53,'30:53','اور نہ تو راہ سمجھائے ا ندھوں کو انکے بھٹکنے سے تو تو سنائے اسی کو جو یقین لائے ہماری باتوں پر سو وہ مسلمان ہوتے ہیں ');
INSERT INTO "al_hind" VALUES(30,54,'30:54','اللہ ہے جس نے بنایا تم کمزوری سے پھر دیا کمزوری کے پیچھے زور پھر دے گا زور کے پیچھے کمزوری اور سفید بال بناتا ہے جو کچھ چاہے اور وہ ہے سب کچھ جانتا کر سکتا ');
INSERT INTO "al_hind" VALUES(30,55,'30:55','اور جس دن قائم ہو گی قیامت قسمیں کھائیں گے گنہگار کہ ہم نہیں رہے تھے ایک گھڑی سے زیادہ اسی طرح تھے الٹے جاتے ');
INSERT INTO "al_hind" VALUES(30,56,'30:56','اور کہیں گے جنکو ملی ہے سمجھ اور یقین تمہارا ٹھہرنا تھا اللہ کی کتاب میں جی اٹھنے کے دن تک سو یہ ہے جی اُٹھنے کا دن پر تم نہیں تھے جانتے ');
INSERT INTO "al_hind" VALUES(30,57,'30:57','سو اُس دن کام نہ آئے گا ان گنہگاروں کو قصور بخشوانا اور نہ ان سے کوئی منانا چاہے ');
INSERT INTO "al_hind" VALUES(30,58,'30:58','اور ہم نے بٹھلائی ہے آدمیوں کے واسطے اس قرآن میں ہر ایک طرح کی مثل اور جو تو لائے اُنکے پاس کوئی آیت تو ضرور کہیں وہ منکر تم سب جھوٹ بناتے ہو ');
INSERT INTO "al_hind" VALUES(30,59,'30:59','یوں مہر لگا دیتا ہے اللہ اُن کے دلوں پر جو سمجھ نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(30,60,'30:60','سو تو قائم رہ بیشک اللہ کا وعدہ ٹھیک ہے اور اکھاڑ نہ دیں تجھ کو وہ لوگ جو یقین نہیں لاتے ');
INSERT INTO "al_hind" VALUES(31,1,'31:1','الٓم۔');
INSERT INTO "al_hind" VALUES(31,2,'31:2','یہ آیتیں ہیں پکی کتاب کی');
INSERT INTO "al_hind" VALUES(31,3,'31:3','ہدایت ہے اور مہربانی نیکی کرنے والوں کے لئے ');
INSERT INTO "al_hind" VALUES(31,4,'31:4','جو کہ قائم رکھتے ہیں نماز اور دیتے ہیں زکوٰۃ اور وہ ہیں جو آخرت پر ان کو یقین ہے');
INSERT INTO "al_hind" VALUES(31,5,'31:5','انہوں نے پائی ہے راہ اپنے رب کی طرف سےاور وہی مردا کو پہنچے ');
INSERT INTO "al_hind" VALUES(31,6,'31:6','اور ایک وہ لوگ ہیں کہ خریدارا ہیں کھیل کی باتوں کے تاکہ بچلائیں اللہ کی راہ سے بن سمجھے اور ٹھہرائیں اسی کو ہنسی وہ جو ہیں ان کو ذلت کا عذاب ہے ');
INSERT INTO "al_hind" VALUES(31,7,'31:7','اور جب سنائے اس کو ہمای آیتیں پیٹھ دیجائے غرور سے گویا ان کو سنا ہی نہیں گویا اس کےدونوں کان بہرے ہیں سو خوشخبری دے اس کو دردناک عذاب کی ');
INSERT INTO "al_hind" VALUES(31,8,'31:8','جو لوگ یقین لائے اور کیے بھلے کام ان کے واسطے ہیں نعمت کے باغ');
INSERT INTO "al_hind" VALUES(31,9,'31:9','ہمیشہ رہا کریں ان میں وعدہ ہو چکا اللہ کا سچا اور وہ زبردست ہے حکمتوں والا ');
INSERT INTO "al_hind" VALUES(31,10,'31:10','بنائے آسمان بغیر ستونوں کے تم اس کو دیکھتے ہو اور رکھ دیے زمین پر پہاڑ کہ تم کو لیکر جھک نہ پڑے اور بکھیر دیے اس میں سب طرح کے جانور اور اتارا ہم نے آسمان سے پانی پھر اگائے زمین میں ہر قسم کے جوڑے خاصے ');
INSERT INTO "al_hind" VALUES(31,11,'31:11','یہ سب کچھ بنایا ہوا ہے اللہ کا اب دکھلاؤ مجھ کو کیا بنایا ہے اوروں نے جو اس کے سوا ہیں کچھ نہیں پر بے انصاف صریح بھٹک رہے ہیں ');
INSERT INTO "al_hind" VALUES(31,12,'31:12','اور ہم نے دی لقمان کو عقل مندی کہ حق مان اللہ کا اور جو کوئی حق مانے اللہ کا تو مانے گا اپنے بھلے کو اور جو کوئی منکر ہو گا تو اللہ بے پروا ہے سب تعریفوں والا ');
INSERT INTO "al_hind" VALUES(31,13,'31:13','اور جب کہا لقمان نے اپنے بیٹے کو جب اس کو سمجھانے لگا اے بیٹے شریک نہ ٹھہرائیو اللہ کا بیشک شریک بنانا بھاری بے انصافی ہے ');
INSERT INTO "al_hind" VALUES(31,14,'31:14','اور ہم نے تاکید کر دی انسان کو اسکے ماں باپ کے واسطے پیٹ میں رکھا اس کو اسکی ماں نے تھک تھک کر اور دودھ چھڑانا ہے اس کا دو برس میں کہ حق مان میرا اور اپنے ماں باپ کا آخر مجھی تک آنا ہے ');
INSERT INTO "al_hind" VALUES(31,15,'31:15','اور اگر وہ دونوں تجھ سے اڑیں اس بات پر کہ شریک مان میرا اس چیز کو جو تجھ کو معلوم نہیں تو انکا کہنا مت مان اور ساتھ دے ان کا دنیا میں دستور کے موافق اور راہ چل اس کی جو رجوع ہوا میری طرف پھر میری طرف ہے تم کو پھر آنا پھر میں جتلا دوں گا تم کو جو کچھ تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(31,16,'31:16','اے بیٹے اگر کوئی چیز ہو برابر رائی کے دانہ کی پھر وہ ہو کسی پتھر میں یا آسمانوں میں یا زمین میں لا حاضر کرے اس کو اللہ بیشک اللہ جانتا ہے چھپی ہوئی چیزوں کو خبردار ہے ');
INSERT INTO "al_hind" VALUES(31,17,'31:17','اے بیٹے قائم رکھ نماز اور سکھلا بھلی بات اور منع کر برائی سے اور تحمل کر جو تجھ پر پڑے بیشک یہ ہیں ہمت کے کام ');
INSERT INTO "al_hind" VALUES(31,18,'31:18','اور اپنے گال مت پھلا لوگوں کی طرف اور مت چل زمین پر اتراتا بیشک اللہ کو نہیں بھاتا کوئی اتراتا بڑائیاں کرنے والا ');
INSERT INTO "al_hind" VALUES(31,19,'31:19','اور چل بیچ کی چال اور نیچی کر آواز اپنی بیشک بری سے بری آواز گدھے کی آواز ہے ');
INSERT INTO "al_hind" VALUES(31,20,'31:20','کیا تم نے نہیں دیکھا کہ اللہ نے کام میں لگائے تمہارے جو کچھ ہے آسمان اور زمین میں اور پوری کر دیں تم پر اپنی نعمتیں کھلی اور چھپی اور لوگوں میں ایسے بھی ہیں جو جھگڑتے ہیں اللہ کی بات میں نہ سمجھ رکھیں نہ سوجھ اور نہ روشن کتاب ');
INSERT INTO "al_hind" VALUES(31,21,'31:21','اور جب ان کو کہیے چلو اس حکم پر جو اتارا اللہ نے کہیں نہیں ہم تو چلیں گے اس پر جس پر پایا ہم نے اپنے باپ دادوں کو بھلا اور جو شیطان بلاتا ہو ان کو دوزخ کے عذاب کی طرف تو بھی ');
INSERT INTO "al_hind" VALUES(31,22,'31:22','اور جو کوئی تابع کرے اپنا منہ اللہ کی طرف اور وہ ہو نیکی پر سو اس نے پکڑ لیا مضبوط کڑا اور اللہ کی طرف ہے آخر ہر کام کا ');
INSERT INTO "al_hind" VALUES(31,23,'31:23','اور جو کوئی منکر ہوا تو تو غم نہ کھا اسکے انکار سے ہماری طرف پھر آنا ہے انکو پھر ہم جتلا دیں گے ان کو جو انہوں نے کیا ہے البتہ اللہ جانتا ہے جو بات ہے دلوں میں ');
INSERT INTO "al_hind" VALUES(31,24,'31:24','کام چلا دیں گے ہم انکا تھوڑے دنوں میں پھر پکڑ بلائیں گے ان کو گاڑھے عذاب میں ');
INSERT INTO "al_hind" VALUES(31,25,'31:25','اور اگر تو پوچھے ان سے کس نے بنائے آسمان اور زمین تو کہیں اللہ نے تو کہہ سب خوبی اللہ کو ہے پر وہ بہت لوگ سمجھ نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(31,26,'31:26','اللہ کا ہے جو کچھ ہے آسمان اور زمین میں بیشک اللہ وہی ہے بے پروا سب خوبیوں والا ');
INSERT INTO "al_hind" VALUES(31,27,'31:27','اور اگر جتنے درخت ہیں زمین میں قلم ہوں اور سمندر ہو اس کی سیاہی اسکے پیچھے ہوں سات سمندر نہ تمام ہوں باتیں اللہ کی بیشک اللہ زبردست ہے حکمتوں والا ');
INSERT INTO "al_hind" VALUES(31,28,'31:28','تم سب کا بنانا اور مرے پیچھے جلانا ایسا ہی ہے جیسا ایک جی کا بیشک اللہ سب کچھ سنتا دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(31,29,'31:29','تو نے نہیں دیکھا کہ اللہ داخل کرتا ہے رات کو دن میں اور داخل کرتا ہے دن کو رات میں اور کام میں لگا دیا ہے سورج اور چاند کو ہر ایک چلتا ہے ایک مقرر وقت تک اور یہ کہ اللہ خبر رکھتا ہے اسکی جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(31,30,'31:30','یہ اس لیے کہا کہ اللہ وہی ہے ٹھیک اور جس کسی کو پکارتے ہیں اس کے سوائے سو وہی جھوٹ ہے اور اللہ وہی ہے سب سے اوپر بڑا ');
INSERT INTO "al_hind" VALUES(31,31,'31:31','تو نے نہ دیکھا کہ جہاز چلتے ہیں سمندر میں اللہ کی نعمت لے کر تاکہ دکھلائے تمکو کچھ اپنی قدرتیں البتہ اس میں نشانیاں ہیں ہر ایک تحمل کرنے والے احسان ماننے والے کے واسطے ');
INSERT INTO "al_hind" VALUES(31,32,'31:32','اور جب سر پر آئے انکے موج جیسے بادل پکارنے لگیں اللہ کو خالص کر کر اسی کے لیے بندگی پھر جب بچا دیا انکو جنگل کی طرف تو کوئی ہوتا ہے ان میں بیچ کی چال پر اور منکر وہی ہوتے ہیں ہماری قدرتوں سے جو قول کے جھوٹے ہیں حق نہ ماننے والے ');
INSERT INTO "al_hind" VALUES(31,33,'31:33','اے لوگوں بچتے ہو اپنے رب سے اور ڈرو اس دن سے کہ کام نہ آئے کوئی باپ اپن بیٹے کے بدلے اور نہ کوئی بیٹا ہو جو کام آئے اپنے باپ کی جگہ کچھ بھی بیشک اللہ کا وعدہ ٹھیک ہے سو تم کو نہ بہکائے دنیا کی زندگانی اور نہ دھوکا دے تم کو اللہ کے نام سے وہ دغاباز ');
INSERT INTO "al_hind" VALUES(31,34,'31:34','بیشک اللہ کے پاس ہے قیامت کی خبر اور اتارتا ہے مینہ اور جانتا ہے جو کچھ ہے ماں کے پیٹ میں اور کسی جی کو معلوم نہیں کہ کل کو کیا کرے گا اور کسی جی کو خبر نہیں کہ کس زمین میں مرے گا تحقیق اللہ سب کچھ جاننے والا خبردار ہے ');
INSERT INTO "al_hind" VALUES(32,1,'32:1','الٓم۔');
INSERT INTO "al_hind" VALUES(32,2,'32:2','اتارنا کتاب کا اس میں کچھ دھوکا نہیں پروردگار عالم کی طرف سے ہے ');
INSERT INTO "al_hind" VALUES(32,3,'32:3','کیا کہتے ہیں کہ یہ جھوٹ باندھ لایا ہے کوئی نہیں وہ ٹھیک ہے تیرے رب کی طرف سے تاکہ تو ڈر سنا دے ان لوگوں کو جن کے پاس نہیں آیا کوئی ڈرانے والا تجھ سے پہلے تاکہ وہ راہ پر آئیں ');
INSERT INTO "al_hind" VALUES(32,4,'32:4','اللہ ہے جس نے بنائے آسمان اور زمین اور جو کچھ انکے بیچ میں ہے چھ دن کے اندر پھر قائم ہوا عرش پر کوئی نہیں تمہارا اس کے سوائے حمایتی اور نہ سفارشی پھر تم کیا دھیان نہیں کرتے ');
INSERT INTO "al_hind" VALUES(32,5,'32:5','تدبیر سے اتارتا ہے ہر کام آسمان سے زمین تک پھر چڑھتا ہے وہ کام اس کی طرف ایک دن میں جس کا پیمانہ ہزار برس کا ہے تمہاری گنتی میں ');
INSERT INTO "al_hind" VALUES(32,6,'32:6','یہ ہی جاننے والا چھپے اور کھلے کا زبردست رحم والا ');
INSERT INTO "al_hind" VALUES(32,7,'32:7','جس نے خوب بنائی جو چیز بنائی اور شروع کی انسان کی پیدائش ایک گارے سے');
INSERT INTO "al_hind" VALUES(32,8,'32:8','پھر بنائی اسکی اولاد نچڑے ہوئے بےقدر پانی سے ');
INSERT INTO "al_hind" VALUES(32,9,'32:9','پھر اس کو برابر کیا اور پھونکی اس میں اپنی ایک جان اور بنا دیے تمہارے لیے کان اور آنکھیں اور دل تم بہت تھوڑا شکر کرتے ہو ');
INSERT INTO "al_hind" VALUES(32,10,'32:10','اور کہتے ہیں کیا جب ہم رل گئے زمین میں کیا ہم کو نیا بننا ہے کچھ نہیں وہ اپنے رب کی ملاقات سے منکر ہیں ');
INSERT INTO "al_hind" VALUES(32,11,'32:11','تو کہہ قبض کر لیتا ہے تم کو فرشتہ موت کا جو تم پر مقرر ہے پھر اپنے رب کی طرف پھر جاؤ گے ');
INSERT INTO "al_hind" VALUES(32,12,'32:12','اور کبھی تو دیکھے جس وقت کہ منکر سر ڈالے ہوئے ہوں گے اپنے رب کے سامنے اے رب ہم نے دیکھ لیا اور سن لیا اب ہم کو بھیج دے کہ ہم کریں بھلے کام ہم کو یقین آ گیا ');
INSERT INTO "al_hind" VALUES(32,13,'32:13','اور اگر ہم چاہتے تو سجھا دیتے ہر جی کو اس کی راہ لیکن ٹھیک پڑ چکی میری کہی بات کہ مجھ کو بھرنی ہے دوزخ جنوں سے اور آدمیوں سے اکھٹے ');
INSERT INTO "al_hind" VALUES(32,14,'32:14','سو اب چکھو مزہ جیسے تم نے بھلا دیا تھا اس اپنے دن کے ملنے کو ہم نے بھی بھلا دیا تم کو اور چکھو عذاب سدا کا عوض اپنے کیے کا');
INSERT INTO "al_hind" VALUES(32,15,'32:15','ہماری باتوں کو وہی مانتے ہیں کہ جب ان کو سمجھائے ان سے گر پڑیں سجدہ کر کر اور پاک ذات کو یاد کریں اپنے رب کی خوبیوں کے ساتھ اور وہ بڑائی نہیں کرتے ');
INSERT INTO "al_hind" VALUES(32,16,'32:16','جدا رہتی ہیں ان کی کروٹیں اپنے سونے کی جگہ سے پکارتے ہیں اپنے رب کو ڈر سے اور لالچ سے اور ہمارا دیا ہوا کچھ خرچ کرتے ہیں');
INSERT INTO "al_hind" VALUES(32,17,'32:17','سو کسی جی کو معلوم نہیں جو چھپا دھری ہے انکے واسطے آنکھوں کی ٹھنڈک بدلا اس کو جو کرتے تھے ');
INSERT INTO "al_hind" VALUES(32,18,'32:18','بھلا ایک جو ہے ایمان پر برابر ہے اسکے جو نافرمان ہے نہیں برابر ہوتے ');
INSERT INTO "al_hind" VALUES(32,19,'32:19','سو وہ لوگ جو یقین لائے اور کیے کام بھلے تو ان کے لیے باغ ہیں رہنے کے مہمانی ان کاموں کی وجہ سے جو کرتے تھے ');
INSERT INTO "al_hind" VALUES(32,20,'32:20','اور وہ لوگ جو نافرمان ہوئے سو انکا گھر ہے آگ جب چاہیں کہ نکل پڑیں اس میں سے الٹا دیے جائیں پھر اسی میں اور کہیں ان کو چکھو آگ کا عذاب جس کو تم جھٹلایا کرتے تھے ');
INSERT INTO "al_hind" VALUES(32,21,'32:21','اور البتہ چکھائیں گے ہم ان کو تھوڑا عذاب ورے اس بڑے عذاب سے تاکہ وہ پھر آئیں ');
INSERT INTO "al_hind" VALUES(32,22,'32:22','اور کون بے انصاف زیادہ اس سے جس کو سمجھایا گیا اسکے رب کی باتوں سے پھر ان سے منہ موڑ گیا مقرر ہم کو ان گنہگاروں سے بدلا لینا ہے ');
INSERT INTO "al_hind" VALUES(32,23,'32:23','اور ہم نے دی ہے موسٰی کو کتاب سو تو مت رہ دھوکے میں اس کے ملنے سے اور کیا ہم نے اس کو ہدایت بنی اسرائیل کے واسطے');
INSERT INTO "al_hind" VALUES(32,24,'32:24','اور کیے ہم نے ان میں پیشوا جو راہ چلاتے تھے ہمارے حکم سے جب وہ صبر کرتے رہے اور رہے ہماری باتوں پر یقین کرتے ');
INSERT INTO "al_hind" VALUES(32,25,'32:25','تیرا رب جو ہے وہی فیصلہ کرے گا ان میں دن قیامت کے جس بات میں کہ وہ اختلاف کرتے ہیں ');
INSERT INTO "al_hind" VALUES(32,26,'32:26','کیا ان کو راہ نہ سوجھی اس بات سے کہ کتنی غارت کر ڈالیں ہم نے ان سے پہلے جماعتیں کہ پھرتے ہیں یہ ان کے گھروں میں اس میں بہت نشانیاں ہیں کیا وہ سنتے نہیں ');
INSERT INTO "al_hind" VALUES(32,27,'32:27','یا دیکھا نہیں انہوں نے کہ ہم ہانک دیتے ہیں پانی کو ایک زمین چٹیل کی طرف پھر ہم نکالتے ہیں اس سے کھیتی کہ کھاتے ہیں اس میں انکے چوپائے اور خود وہ بھی پھر کیا دیکھتے نہیں ');
INSERT INTO "al_hind" VALUES(32,28,'32:28','اور کہتے ہیں کب ہو گا یہ فیصلہ اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(32,29,'32:29','تو کہہ کہ فیصلہ کے دن کام نہ آئے گا منکروں کو انکا ایمان لانا اور نہ ان کو ڈھیل ملے گی ');
INSERT INTO "al_hind" VALUES(32,30,'32:30','سو تو خیال چھوڑ ان کا اور منتظر رہ وہ بھی منتظر ہیں ');
INSERT INTO "al_hind" VALUES(33,1,'33:1','اے نبی ڈر اللہ سے اور کہا نہ مان منکروں کا اور دغا بازوں کا مقرر اللہ ہے سب کچھ جاننے والا حکمتوں والا');
INSERT INTO "al_hind" VALUES(33,2,'33:2','اور چل اسی پر جو حکم آئے تجھ کو تیرے رب کی طرف سے بیشک اللہ تمہارے کام کی خبر رکھتا ہے');
INSERT INTO "al_hind" VALUES(33,3,'33:3','اور بھروسہ رکھ اللہ پر اور اللہ کافی ہے کام بنانے والا ');
INSERT INTO "al_hind" VALUES(33,4,'33:4','اللہ نے رکھے نہیں کسی مرد کے دو دل اس کے اندر اور نہیں کیا تمہاری جوروؤں کو جن کو ماں کہہ بیٹھے ہو سچی مائیں تمہاری اور نہیں کیا تمہارے لے پالکوں کو تمہارے بیٹے یہ تمہاری بات ہے اپنے منہ کی اور اللہ کہتا ہے ٹھیک بات اور وہی سجھاتا ہے راہ ');
INSERT INTO "al_hind" VALUES(33,5,'33:5','پکارو لے پالکوں کو انکے باپ کی طرف نسبت کر کے یہی پورا انصاف ہے اللہ کے یہاں پھر اگر نہ جانتے ہو ان کے باپ کو تو تمہارے بھائی ہیں دین میں اور رفیق ہیں اور گناہ نہیں تم پر جس چیز میں چوک جاؤ پر وہ جو دل سے ارادہ کرو اور اللہ ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(33,6,'33:6','نبی سے لگاؤ ہے ایمان والوں کو زیادہ اپنی جان سے اور اسکی عورتیں انکی مائیں ہیں اور قرابت والے ایک دوسرے سے لگاؤ رکھتے ہیں اللہ کے حکم میں زیادہ سب ایمان والوں اور ہجرت کرنے والوں سے مگر یہ کہ کرنا چاہو اپنے رفیقوں سے احسان یہ ہے کتاب میں لکھا ہوا ');
INSERT INTO "al_hind" VALUES(33,7,'33:7','اور جب لیا ہم نے نبیوں سے ان کا قرار اور تجھ سے اور نوح سے اور ابراہیم سے اور موسٰی سے اور عیسٰی سے جو بیٹا مریم کا اور لیا ہم نے ان سے گاڑھا قرار ');
INSERT INTO "al_hind" VALUES(33,8,'33:8','تاکہ پوچھے اللہ سچوں سے ان کا سچ اور تیار رکھا ہے منکروں کے لیے دردناک عذاب ');
INSERT INTO "al_hind" VALUES(33,9,'33:9','اے ایمان والو یاد کرو احسان اللہ کا اپنے اوپر جب چڑھ آئیں تم پر فوجیں پھر ہم نے بھیجدی ان پر ہوا اور وہ فوجیں جو تم نے نہیں دیکھیں اور ہے اللہ جو کچھ کرتے ہو دیکھنے والا ');
INSERT INTO "al_hind" VALUES(33,10,'33:10','جب چڑھ آئے تم پر اوپر کی طرف سے اور نیچے سے اور جب بدلنے لگیں آنکھیں اور پہنچے دل گلوں تک اور اٹکلنے لگے تم اللہ پر طرح طرح کی اٹکلیں ');
INSERT INTO "al_hind" VALUES(33,11,'33:11','وہاں جانچے گئے ایمان والے اور جھڑ جھڑائے گئے زور کا جھڑ جھڑانا ');
INSERT INTO "al_hind" VALUES(33,12,'33:12','اور جب کہنے لگے منافق اور جن کے دلوں میں روگ ہے جو وعدہ کیا تھا ہم سے اللہ نے اور اس کے رسول نے سب فریب تھا ');
INSERT INTO "al_hind" VALUES(33,13,'33:13','اور جب کہنے لگی ایک جماعت ان میں اے یثرب والو تمہارے لئے ٹھکانہ نہیں سو پھر چلو اور رخصت مانگنے لگا ایک فرقہ ان میں نبی سے کہنے لگے ہمارے گھر کھلے پڑے ہیں اور وہ کھلے نہیں پڑے ان کی کوئی غرض نہیں مگر بھاگ جانا ');
INSERT INTO "al_hind" VALUES(33,14,'33:14','اور اگر شہر میں کوئی گھس آئے ان پر اس کے کناروں سے پھر ان سے چاہے دین سے بچلنا تو مان لیں اور دیر نہ کریں اس میں مگر تھوڑی ');
INSERT INTO "al_hind" VALUES(33,15,'33:15','اور اقرار کر چکے تھے اللہ سے پہلے کہ نہ پھیریں گے پیٹھ اور اللہ کے قرار کی پوچھ ہوتی ہے ');
INSERT INTO "al_hind" VALUES(33,16,'33:16','تو کہہ کچھ کام نہ آئے گا تمہارے یہ بھاگنا اگر بھاگو گے مرنے سے یا مارے جانے سے اور پھر بھی پھیل نہ پاؤ گے مگر تھوڑے دنوں ');
INSERT INTO "al_hind" VALUES(33,17,'33:17','تو کہہ کون ہے کہ تم کو بچائے اللہ سے اگر چاہے تم پر برائی یا چاہے تم پر مہربانی اور نہ پائیں گے اپنے واسطے اللہ کے سوائے کوئی حمایتی اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(33,18,'33:18','اللہ کو خوب معلوم ہیں جو اٹکانے والے ہیں تم میں اور کہتے ہیں اپنے بھائیوں کو چلے آؤ ہمارے پاس اور لڑائی میں نہیں آتے مگر کبھی ');
INSERT INTO "al_hind" VALUES(33,19,'33:19','دریغ (بخل کرتے) رکھتے ہیں تم سے پھر جب آئے ڈر کا وقت تو تو دیکھے ان کو کہ تکتے ہیں تیری طرف پھرتی ہیں آنکھیں ان کی جیسے کسی پر آئے بے ہوشی موت کی پھر جب جاتا رہے ڈر کا وقت چڑھ چڑھ کر بولیں تم پر تیز تیز زبانوں سے ڈھکے (ٹوٹے) پڑتے ہیں مال پر وہ لوگ یقین نہیں لائے پھر اکارت کر ڈالے اللہ نے انکے کئے کام اور یہ ہے اللہ پر آسان ');
INSERT INTO "al_hind" VALUES(33,20,'33:20','سمجھتے ہیں کہ فوجیں کفار کی نہیں پھر گئیں اور اگر آجائیں وہ فوجیں تو آرزو کریں کسی طرح ہم باہر نکلے ہوئے ہوں گاؤں میں پوچھ لیا کریں تمہاری خبریں اور اگر ہوں تم میں لڑائی نہ کریں مگر بہت تھوڑی ');
INSERT INTO "al_hind" VALUES(33,21,'33:21','تمہارے لئے بھلی تھی سیکھنی رسول اللہ کی چال اسکے لئے جو کوئی امید رکھتا ہے اللہ کی اور پچھلے دن کی اور یاد کرتا ہے اللہ کو بہت سا ');
INSERT INTO "al_hind" VALUES(33,22,'33:22','اور جب دیکھی مسلمانوں نے فوجیں بولے یہ وہی ہے جو وعدہ دیا تھا ہم کو اللہ نے اور اسکے رسول نے اور سچ کہا اللہ نے اور اسکے رسول نے اور ان کو اور بڑھ گیا یقین اور اطاعت کرنا ');
INSERT INTO "al_hind" VALUES(33,23,'33:23','ایمان والوں میں کتنے مرد ہیں کہ سچ کر دکھلایا جس بات کا عہد کیا تھا اللہ سے پھر کوئی تو ان میں پورا کر چکا اپنا ذمہ اور کوئی ہے ان میں راہ دیکھ رہا اور بدلا نہیں ایک ذرہ ');
INSERT INTO "al_hind" VALUES(33,24,'33:24','تاکہ بدلا دے اللہ سچوں کو انکے سچ کا اور عذاب کرے منافقوں پر اگر چاہے یا تو بہ ڈالے انکے دل پر بیشک اللہ ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(33,25,'33:25','اور پھیر دیا اللہ نے منکروں کو اپنے غصہ میں بھرے ہوئے ہاتھ نہ لگی کچھ بھلائی اور اپنے اوپر لے لی اللہ نے مسلمانوں کی لڑائی اور ہے اللہ زور آور زبردست ');
INSERT INTO "al_hind" VALUES(33,26,'33:26','اور اتار دیا ان کو جو ان کے پشت پناہ ہوئے تھے اہل کتاب سے ان کے قلعوں سے اور ڈال دی ان کے دلوں میں دھاک کتنوں کو تم جان سے مارنے لگے اور کتنوں کو قید کر لیا ');
INSERT INTO "al_hind" VALUES(33,27,'33:27','اور تم کو دلائی ان کی زمین اور انکے گھر اور ان کے مال اور ایک زمین کہ جس پر نہیں پھیرے تم نے اپنے قدم اور ہے اللہ سب کچھ کر سکتا ');
INSERT INTO "al_hind" VALUES(33,28,'33:28','اے نبی کہہ دے اپنی عورتوں کو اگر تم چاہتی ہو دنیا کی زندگانی اور یہاں کی رونق تو آؤ کچھ فائدہ پہنچا دوں تم کو اور رخصت کر دوں بھلی طرح سے رخصت کرنا');
INSERT INTO "al_hind" VALUES(33,29,'33:29','اور اگر تم چاہتی ہو اللہ کو اور اسکے رسول کو اور پچھلے گھر کو تو اللہ نے رکھ چھوڑا ہے انکے لئے جو تم میں نیکی پر ہیں بڑا ثواب ');
INSERT INTO "al_hind" VALUES(33,30,'33:30','اے نبی کی عورتو جو کوئی کر لائے تم میں کام بیحیائی کا صریح دونا ہو اس کو عذاب دہرا اور ہے یہ اللہ پر آسان ');
INSERT INTO "al_hind" VALUES(33,31,'33:31','اور جو کوئی تم میں اطاعت کرے اللہ کی اور اسکے رسول کی اور عمل کرے اچھے دیویں ہم اسکو اس کا ثواب دو بار اور رکھی ہے ہم نے اس کے واسطے روزی عزت کی ');
INSERT INTO "al_hind" VALUES(33,32,'33:32','اے نبی کی عورتو تم نہیں ہو جیسے ہر کوئی عورتیں اگر تم ڈر رکھو سو تم دب کر بات نہ کرو پھر لالچ کرے کوئی جس کے دل میں روگ ہے اور کہو بات معقول ');
INSERT INTO "al_hind" VALUES(33,33,'33:33','اور قرار پکڑو اپنے گھروں میں اور دکھلاتی نہ پھرو جیسا کہ دکھانا دستور تھا پہلے جہالت کے وقت میں اور قائم رکھو نماز اور دیتی رہو زکوٰۃ اور اطاعت میں رہو اللہ کی اور اس کے رسول کی اللہ یہی چاہتا ہے کہ دور کرے تم سے گندی باتیں اے نبی کے گھر والو اور ستھرا کر دے تم کو ایک ستھرائی سے ');
INSERT INTO "al_hind" VALUES(33,34,'33:34','اور یاد کرو جو پڑھی جاتی ہیں تمہارے گھروں میں اللہ کی باتیں اور عقلمندی کی مقرر اللہ ہے بھید جاننے والا خبردار ');
INSERT INTO "al_hind" VALUES(33,35,'33:35','تحقیق مسلمان مرد اور مسلمان عورتیں اور ایمان دار مرد اور ایمان دار عورتیں اور بندگی کرنے والے مرد اور بندگی کرنے والی عورتیں اور سچے مرد اور سچی عورتیں اور محنت جھیلنے والے مرد اور محنت جھیلنے والی عورتیں اور دبے رہنے والے مرد اور دبی رہنے والی عورتیں اور خیرات کرنے والے مرد اور خیرات کرنے والی عورتیں اور روزہ دار مرد اور روزہ دار عورتیں اور حفاظت کرنے والے مرد اپنی شہوت کی جگہ کو اور حفاظت کرنے والی عورتیں اور یاد کرنے والے مرد اللہ کو بہت سا اور یاد کرنے والی عورتیں رکھی ہے اللہ نے ان کے واسطے معافی اور ثواب بڑا ');
INSERT INTO "al_hind" VALUES(33,36,'33:36','اور کام نہیں کسی ایماندار مرد کا اور نہ ایماندار عورت کا جب کہ مقرر کر دے اللہ اور اس کا رسول کوئی کام ان کو رہے اختیار اپنے کام کا اور جس نے نافرمانی کی اللہ کی اور اس کے رسول کی سو وہ راہ بھولا صریح چوک کر ');
INSERT INTO "al_hind" VALUES(33,37,'33:37','اور جب تو کہنے لگا اس شخص کو جس پر اللہ نے احسان کیا اور تو نے احسان کیا رہنے دے اپنے پاس اپنی جورو کو اور ڈر اللہ سے اور تو چھپاتا تھا اپنے دل میں ایک چیز جس کو اللہ کھولنا چاہتا ہے اور ڈرتا تھا لوگوں سے اور اللہ سے زیادہ چاہئیے ڈرنا تجھ کو پھر جب زید تمام کر چکا اس عورت سے اپنی غرض ہم نے ا سکو تیرے نکاح میں دیدیا تا نہ رہے مسلمانوں پر گناہ نکاح کر لینا جوروئیں اپنے لے پالکوں کی جب وہ تمام کر لیں ان سے اپنی غرض اور ہے اللہ کا حکم بجا لانا ');
INSERT INTO "al_hind" VALUES(33,38,'33:38','نبی پر کچھ مضائقہ نہیں ا س بات میں جو مقرر کر دی اللہ نے اسکے واسطے جیسے دستور رہا ہے اللہ کا ان لوگوں میں جو گذرے پہلے اور ہے حکم اللہ کا مقرر ٹھہر چکا');
INSERT INTO "al_hind" VALUES(33,39,'33:39','وہ لوگ جو پہنچاتے ہیں پیغام اللہ کے اور ڈرتے ہیں اس سے اور نہیں ڈرتے کسی سے سوائے اللہ کے اور بس ہے اللہ کفایت کرنے والا ');
INSERT INTO "al_hind" VALUES(33,40,'33:40','محمد باپ نہیں کسی کا تمہارے مردوں میں سے لیکن رسول ہے اللہ کا اور مہر سب نبیوں پر اور ہے اللہ سب چیزوں کو جاننے والا ');
INSERT INTO "al_hind" VALUES(33,41,'33:41','اے ایمان والو یاد کرو اللہ کی بہت سی یاد');
INSERT INTO "al_hind" VALUES(33,42,'33:42','اور پاکی بولتے رہو اسکی صبح اور شام ');
INSERT INTO "al_hind" VALUES(33,43,'33:43','وہی ہے جو رحمت بھیجتا ہے تم پر اور اس کے فرشتے تاکہ نکالے تم کو اندھیروں سے اجالے میں اور ہے ایمان والوں پر مہربان ');
INSERT INTO "al_hind" VALUES(33,44,'33:44','دعا ان کی جس دن اس سے ملیں گے سلام ہے اور تیار رکھا ہے انکے واسطے ثواب عزت کا ');
INSERT INTO "al_hind" VALUES(33,45,'33:45','اے نبی ہم نے تجھ کو بھیجا بتانے والا اور خوشخبری سنانے والا اور ڈرانے والا ');
INSERT INTO "al_hind" VALUES(33,46,'33:46','اور بلانے والا للہ کی طرف اسکے حکم سے اور چمکتا ہوا چراغ ');
INSERT INTO "al_hind" VALUES(33,47,'33:47','اور خوشخبری سنا دے ایمان والوں کو کہ ان کے لیے ہے خدا کی طرف سے بڑی بزرگی ');
INSERT INTO "al_hind" VALUES(33,48,'33:48','اور کہا مت مان منکروں کا اور دغابازوں کا اور چھوڑ دے انکا ستانا اور بھروسہ کر اللہ پر اور اللہ بس ہے کام بنانے والا ');
INSERT INTO "al_hind" VALUES(33,49,'33:49','اے ایمان والوں جب تم نکاح میں لاؤ مسلمان عورتوں کو پھر ان کو چھوڑ دو پہلے اس سے کہ ان کو ہاتھ لگاؤ سو ان پر تم کو حق نہیں عدت میں بٹھلانا کہ گنتی پوری کراؤ سو ان کو دو کچھ فائدہ اور رخصت کرو بھلی طرح سے ');
INSERT INTO "al_hind" VALUES(33,50,'33:50','اے نبی ہم نے حلال رکھیں تجھ کو تیری عورتیں جن کے مہر تو دے چکا ہے اور جو مال ہو تیرے ہاتھ کا جو ہاتھ لگا دے تیرے اللہ اور تیرے چچا کی بیٹیاں اور پھوپھیوں کی بیٹیاں اور تیرے ماموں کی بیٹیاں اور تیری خالاؤں کی بیٹیاں جنہوں نے وطن چھوڑا تیرے ساتھ اور جو عورت ہو مسلمان اگر بخشدے اپنی جان نبی کو اگر نبی چاہے کہ اس کو نکاح میں لائے یہ خاص ہے تیرے لیے سوائے سب مسلمانوں کے ہم کو معلوم ہے جو مقرر کر دیا ہے ہم نے ان پر انکی عوتوں کے حق میں اور انکے ہاتھ کے مال میں تا نہ رہے تجھ پر تنگی اور ہے اللہ بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(33,51,'33:51','پیچھے رکھ دے تو جس کو چاہو ان میں اور جگہ دے اپنے پاس جس کو چاہے اور جس کو جی چاہے تیرا ان میں سے جن کو کنارے کر دیا تھا تو کچھ گناہ نہیں تجھ پر اس میں قریب ہے کہ ٹھنڈی رہیں آنکھیں انکی اور غم نہ کھائیں اور راضی رہیں اس پر جو تو نے دیا ان سب کی سب کو اور اللہ جانتا ہے جو کچھ تمہارے دلوں میں ہے اور ہے اللہ سب کچھ جاننے والا تحمل والا ');
INSERT INTO "al_hind" VALUES(33,52,'33:52','حلال نہیں تجھ کو عورتیں اسکے بعد اور نہ یہ کہ اُنکے بدلے کر لے اور عورتیں اگرچہ خوش لگے تجھ کو اُنکی صورت مگر جو مال ہو تیرے ہاتھ کا اور ہے اللہ ہر چیز پر نگہبان ');
INSERT INTO "al_hind" VALUES(33,53,'33:53','اے ایمان والو مت جاؤ نبی کے گھروں میں مگر جو تم کو حکم ہو کھانے کے واسطے نہ راہ دیکھنے والے اس کے پکنے کی لیکن جب تم کو بلائے تب جا ؤ پھر جب کھا چکو تو آپ آپ کو چلے جاؤ اور نہ آپس میں جی لگا کر بیٹھو باتوں میں اس بات سے تمہاری تکلیف تھی نبی کو پھر تم سے شرم کرتا ہے اور اللہ شرم نہیں کرتا ٹھیک بات بتلانے میں اور جب مانگنے جاؤ بیبیوں سے کچھ چیز کام کی تو مانگ لو پردہ کے باہر سے اس میں خوب ستھرائی ہے تمہارے دل کو اور ان کے دل کو اور تم کو نہیں پہنچتا کہ تکلیف دو اللہ کے رسول کو اور نہ یہ کہ نکاح کرو اسکی عورتوں سے اس کے پیچھے کبھی البتہ یہ تمہاری بات اللہ کے یہاں بڑا گناہ ہے ');
INSERT INTO "al_hind" VALUES(33,54,'33:54','اگر کھول کر کہو تم کسی چیز کو یا اسکو چھپاؤ سو اللہ ہے ہر چیز کو جاننے والا ');
INSERT INTO "al_hind" VALUES(33,55,'33:55','گناہ نہیں ان عورتوں کو سامنے ہونے کا اپنے باپوں سے اور نہ اپنے بیٹوں سے اور نہ اپنے بھائیوں سے اور نہ اپنے بھائی کے بیٹوں سے اور نہ اپنی بہن کے بیٹوں سے اور نہ اپنی عورتوں سے اور نہ اپنے ہاتھ کے مال سے اور ڈرتی رہو اے عورتو اللہ سے بیشک اللہ کے سامنے ہے ہر چیز ');
INSERT INTO "al_hind" VALUES(33,56,'33:56','اللہ اور اس کے فرشتے رحمت بیجھتے ہیں رسول پر اے ایمان والو رحمت بھیجو اس پر اور سلام بھیجو سلام کہہ کر ');
INSERT INTO "al_hind" VALUES(33,57,'33:57','جو لوگ ستاتے ہیں اللہ کو اور اس کے رسول کو ان کو پھٹکارا اللہ نے دنیا میں اور آخرت میں اور تیار رکھا ہے ان کے واسطے ذلت کا عذاب ');
INSERT INTO "al_hind" VALUES(33,58,'33:58','اور جو لوگ تمہت لگاتے ہیں مسلمان مردوں کو اور مسلمان عورتوں کو بدون گناہ کیے تو اٹھایا انہوں نے بوجھ جھوٹ کا اور صریح گناہ کا ');
INSERT INTO "al_hind" VALUES(33,59,'33:59','اے نبی کہہ دے اپنی عورتوں کو اور اپنی بیٹیوں کو اور مسلمانوں کی عورتوں کو نیچے لٹکا لیں اپنے اوپر تھوڑی سی اپنی چادریں اس میں بہت قریب ہے کہ پہچانی پڑیں تو کوئی انکو نہ ستائے اور ہے اللہ بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(33,60,'33:60','البتہ اگر باز نہ آئے منافق اور جن کے دل میں روگ ہے اور جھوٹی خبریں اڑانے والے مدینہ میں تو ہم لگا دیں گے تجھ کو ان کے پیچھے پھر نہ رہنے پائیں گے تیرے ساتھ اس شہر میں مگر تھوڑے دنوں');
INSERT INTO "al_hind" VALUES(33,61,'33:61','پھٹکارے ہوئے جہاں پائے گئے پکڑے گئے اور مارے گئے جان سے ');
INSERT INTO "al_hind" VALUES(33,62,'33:62','دستور پڑا ہوا ہے اللہ کا ان لوگوں میں جو پہلے ہو چکے ہیں اور تو نہ دیکھے گا اللہ کی چال بدلتی ');
INSERT INTO "al_hind" VALUES(33,63,'33:63','لوگ تجھ سے پوچھتے ہیں قیامت کو تو کہہ اس کی خبر ہے اللہ ہی کے پاس اور تو کیا جانے شاید وہ گھڑی پاس ہی ہو ');
INSERT INTO "al_hind" VALUES(33,64,'33:64','بیشک اللہ نے پھٹکار دیا ہے منکروں کو اور رکھی ہے انکے واسطے دہکتی ہوئی آگ ');
INSERT INTO "al_hind" VALUES(33,65,'33:65','رہا کریں اسی میں ہمیشہ نہ پائیں کوئی حمایتی اور نہ مددگار');
INSERT INTO "al_hind" VALUES(33,66,'33:66','جس دن اوندھے ڈالے جائیں گے ان کے منہ آگ میں کہیں گے کیا اچھا ہوتا جو ہم نے کہا مانا ہوتا اللہ کا اور کہا مانا ہوتا رسول کا ');
INSERT INTO "al_hind" VALUES(33,67,'33:67','اور کہیں گے اے رب ہم نے کہا مانا اپنے سرداروں کا اور اپنے بڑوں کا پھر انہوں نے چکا دیا ہم کو راہ سے');
INSERT INTO "al_hind" VALUES(33,68,'33:68','اے رب ان کو دے دونا عذاب اور پھٹکار انکو بڑی پھٹکار ');
INSERT INTO "al_hind" VALUES(33,69,'33:69','اے ایمان والو تم مت ہو ان جیسے جنہوں نے ستایا موسٰی کو پھر بے عیب دکھلا دیا اسکو اللہ نے انکے کہنے سے اور تھا اللہ کے یہاں آبرو والا ');
INSERT INTO "al_hind" VALUES(33,70,'33:70','اے ایمان والو ڈرتے رہو اللہ سے اور کہو بات سیدھی');
INSERT INTO "al_hind" VALUES(33,71,'33:71','کہ سنوار دے تمہارے واسطے تمہارے کام اور بخشدے تم کو تمہارے گناہ اور جو کوئی کہنے پر چلا اللہ کے اور اس کے رسول کے اس نے پائی بڑی مراد ');
INSERT INTO "al_hind" VALUES(33,72,'33:72','ہم نے دکھلائی امانت آسمانوں کو اور زمین کو اور پہاڑوں کو پھر کسی نے قبول نہ کیا کہ اس کو اٹھائیں اورا س سے ڈر گئے اور اٹھا لیا اس کو انسان نے یہ ہے بڑا بے ترس نادان ');
INSERT INTO "al_hind" VALUES(33,73,'33:73','تاکہ عذاب کرے اللہ منافق مردوں کو اور عورتوں کو اور شرک والے مردوں کو اور عورتوں کو اور معاف کرے اللہ ایمان دار مردوں کو اور عورتوں کو اور ہے اللہ بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(34,1,'34:1','سب خوبی اللہ کی ہے جس کا ہے جو کچھ کہ ہے آسمان اور زمین میں اور اسی کی تعریف ہے آخرت میں اور وہی ہے حکمتوں والا سب کچھ جاننے والا ');
INSERT INTO "al_hind" VALUES(34,2,'34:2','جانتا ہے جو کچھ کہ اندر گھستا ہے زمین کے اور جو کچھ کہ نکتا ہے اس سے اور جو اترتا ہے آسمان سے اور جو چڑھتا ہے اس میں اور وہی ہے رحم والا بخشنے والا ');
INSERT INTO "al_hind" VALUES(34,3,'34:3','اور کہنے لگے منکر نہ آئے گی ہم پر قیامت تو کہہ کیوں نہیں قسم ہے میرے رب کی البتہ آئے گی تم پر اس عالم الغیب کی غائب نہیں ہو سکتا اس سے کچھ ذرہ بھر آسمانوں میں اور نہ زمین میں اور کوئی چیز نہیں اس سےچھوٹی اور نہ اس سے بڑی جو نہیں ہے کھلی کتاب میں ');
INSERT INTO "al_hind" VALUES(34,4,'34:4','تاکہ بدلا دے انکو جو یقین لائے اور کئے بھلے کام وہ لوگ جو ہیں ان کے لئے ہے معافی اور عزت کی روزی');
INSERT INTO "al_hind" VALUES(34,5,'34:5','اور جو لوگ دوڑے ہماری آیتوں کے ہرانے کو ان کو بلا کا عذاب ہے دردناک ');
INSERT INTO "al_hind" VALUES(34,6,'34:6','اور دیکھ لیں جن کو ملی ہے سمجھ کہ جو تجھ پر اترا تیرے رب سے وہی ٹھیک ہے اور سجھاتا ہے راہ اس زبردست خوبیوں والے کی ');
INSERT INTO "al_hind" VALUES(34,7,'34:7','اور کہنے لگے منکر ہم بتلائیں تمکو ایک مرد کہ تم کو خبر دیتا ہے جب تم پھٹ کر ہو جاؤ ٹکڑے ٹکڑے تم کو پھر نئے سرے سے بننا ہے');
INSERT INTO "al_hind" VALUES(34,8,'34:8','کیا بنا لایا ہے اللہ پر جھوٹ یا ا سکو سودا ہے کچھ بھی نہیں پر جو یقین نہیں رکھتے آخرت کا آفت میں ہیں اور دور جا پڑے غلطی میں ');
INSERT INTO "al_hind" VALUES(34,9,'34:9','کیا دیکھتے نہیں جو کچھ ان کے آگے ہے اور پیچھے ہے آسمان اور زمین سے اگر ہم چاہیں دھنسا دیں ان کو زمین میں یا گرا دیں ان پر ٹکڑا آسمان سے تحقیق اس میں نشانی ہے ہر بندے رجوع کرنے والے کے واسطے ');
INSERT INTO "al_hind" VALUES(34,10,'34:10','اور ہم نے دی ہے داؤد کو اپنی طرف سے بڑائی اے پہاڑو خوش آوازی سے پڑھو اسکے ساتھ اور اڑتے جانوروں کو اور نرم کر دیا ہم نے اس کے آگے لوہا');
INSERT INTO "al_hind" VALUES(34,11,'34:11','کہ بنا زرہیں کشادہ اور اندازے سے جوڑ کڑیاں اور کرو تم سب کام بھلا میں جو کچھ تم کرتے ہو دیکھتا ہوں ');
INSERT INTO "al_hind" VALUES(34,12,'34:12','اور مسخر کردیا سلیمان کے آگے ہوا کو صبح کی منزل اسکی ایک مہینہ اور شام کی منزل ایک مہینہ کی اور بہا د یا ہم نے اسکے واسطے چشمہ پگھلے ہوئے تانبے کا اور جنوں میں کتنے لوگ تھے جو محنت کرتے اس کے سامنے اسکے رب کے حکم سے اور جوکوئی پھرے ان میں سے ہمارے حکم سے چکھائیں ہم اسکو آگ کا عذاب ');
INSERT INTO "al_hind" VALUES(34,13,'34:13','بناتے اس کے واسطے جو کچھ چاہتا قلعے اور تصویریں اور لگن جیسے تالاب اور دیگیں چولہوں پر جمی ہوئی کام کرو ا ے داؤد کے گھر والو احسان مان کر اور تھوڑے ہیں میرے بندوں میں احسان ماننے والے ');
INSERT INTO "al_hind" VALUES(34,14,'34:14','پھر جب مقرر کیا ہم نےاس پر موت کو نہ جتلایا ان کو اس کا مرنا مگر کیڑے نے گھن کے کھاتا رہا اس کا عصا پھر جب وہ گر پڑا معلوم کیا جنوں نے کہ اگر خبر رکھتے ہوتے غیب کی نہ رہتے ذلت کی تکلیف میں ');
INSERT INTO "al_hind" VALUES(34,15,'34:15','تحقیق قوم سبا کو تھی ان کی بستی میں نشانی دو باغ داہنے اور بائیں کھاؤ روزی اپنے رب کی اور اس کا شکر کرو شہر ہے پاکیزہ اور رب ہے گناہ بخشنے والا ');
INSERT INTO "al_hind" VALUES(34,16,'34:16','سو دھیان میں نہ لائے پھر چھوڑ دیا ہم نے ان پر ایک نالا زور کا اور دیے ہم نے انکو بدلے میں ان دو باغوں کے دو اور باغ جن میں کچھ میوہ کسیلا تھا اور جھاؤ اور کچھ بیر تھوڑے سے ');
INSERT INTO "al_hind" VALUES(34,17,'34:17','یہ بدلا دیا ہم نے ان کو اس پر کہ ناشکری کی اور ہم یہ بدلا اسی کو دیتے ہیں جو ناشکر ہو ');
INSERT INTO "al_hind" VALUES(34,18,'34:18','اور رکھی تھیں ہم نے ان میں اور ان بستیوں میں جہاں ہم نے برکت رکھی ہے ایسی بستیاں جو راہ پر نظر آتی تھیں اور منزلیں مقرر کر دیں ہم نے ان میں آنے جانے کی پھرو ان میں راتوں کو اور دنوں کو امن سے ');
INSERT INTO "al_hind" VALUES(34,19,'34:19','پھر کہنے لگے اے رب دراز کر دے ہمارے سفروں کو اور آپ اپنا برا کیا پھر کر ڈالا ہم نے انکو کہانیاں اور کر ڈالا چیر کر ٹکڑے ٹکڑے اس میں پتے کی باتیں ہیں ہر صبر کرنے والے شکر گذار کو ');
INSERT INTO "al_hind" VALUES(34,20,'34:20','اور سچ کر دکھلائی ان پر ابلیس نے اپنی اٹکل پھر اسی کی راہ چلے مگر تھوڑے سے ایمان دار ');
INSERT INTO "al_hind" VALUES(34,21,'34:21','اور اس کا ان پر کچھ زور نہ تھا مگر اتنے واسطے کہ معلوم کر لیں ہم اسکو جو یقین لاتا ہے آخرت پر جدا کر کے اس سے جو رہتا ہے آخرت کی طرف سے دھوکہ میں اور تیرا رب ہر چیز پر نگہبان ہے ');
INSERT INTO "al_hind" VALUES(34,22,'34:22','تو کہہ پکارو ان کو جن کو گمان کرتے ہو سوائے اللہ کے وہ مالک نہیں ایک ذرہ بھر کے آسمانوں میں اور نہ زمین میں اور نہ ان کا ان دونوں میں کچھ ساجھا اور نہ ان میں کوئی اس کا مددگار');
INSERT INTO "al_hind" VALUES(34,23,'34:23','اور کام نہیں آتی سفارش اس کے پاس مگر اس کو کہ جس کے واسطے حکم کر دے یہاں تک کہ جب گھبراہٹ دور ہو جائے ان کے دل سےکہیں کیا فرمایا تمہارے رب نے وہ کہیں فرمایا جو واجبی ہے اور وہی ہے سب سے اوپر بڑا ');
INSERT INTO "al_hind" VALUES(34,24,'34:24','تو کہہ کون روزی دیتا ہے تم کو آسمان سے اور زمیں سے بتلا دے کہ اللہ اور یا ہم یا تم بیشک ہدایت پر ہیں یا پڑے ہیں گمراہی میں صریح ');
INSERT INTO "al_hind" VALUES(34,25,'34:25','تو کہ تم سے پوچھ نہ ہوگی اسکی جو ہم نے گناہ کیا اور ہم سے پوچھ نہ ہوگی اسکی جو تم کرتے ہو');
INSERT INTO "al_hind" VALUES(34,26,'34:26','تو کہہ جمع کرے گا ہم سب کو رب ہمارا پھر فیصلہ کرے گا ہم میں انصاف کا اور وہی ہے قصہ چکانے والا سب کچھ جاننے والا ');
INSERT INTO "al_hind" VALUES(34,27,'34:27','تو کہہ مجھ کو دکھلاؤ تو سہی جن کو اس سے ملاتے ہو ساجھی قرار دے کر کوئی نہیں وہی اللہ ہے زبردست حکمتوں والا ');
INSERT INTO "al_hind" VALUES(34,28,'34:28','اور تجھ کو جو ہم نے بھیجا سو سارے لوگوں کے واسطے خوشی اور ڈر سنانے کو لیکن بہت لوگ نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(34,29,'34:29','اور کہتے ہیں کب ہے یہ وعدہ اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(34,30,'34:30','تو کہہ تمہارے لیے وعدہ ہے ایک دن کا نہ دیر کرو گے اس سے ایک گھڑی نہ جلدی ');
INSERT INTO "al_hind" VALUES(34,31,'34:31','اور کہنے لگے منکر ہم ہر گز نہ مانیں گے اس قرآن کو اور نہ اس سے اگلے کو اور کبھی تو دیکھے جبکہ گنہگار کھڑے کئے جائیں اپنے رب کے پاس ایک دوسرے پر ڈالتا ہے بات کو کہتے ہیں وہ لوگ جو کمزور سمجھے جاتے تھے بڑائی کرنے والوں کو اگر تم نہ ہوتے تو ہم ایمان دار ہوتے ');
INSERT INTO "al_hind" VALUES(34,32,'34:32','کہنے لگے بڑائی کرنے والے ان سے جو کہ کمزر گنے گئے تھے کیا ہم نے روکا تم کو حق بات سے تمہار ے پاس پہنچ چکنے کے بعد کوئی نہیں تم ہی تھے گنہگار ');
INSERT INTO "al_hind" VALUES(34,33,'34:33','اور کہنے لگے وہ لوگ جو کمزور گنے گئے تھے بڑائی کرنے والوں کو کوئی نہیں پر فریب سے رات دن کے جب تم ہم کو حکم کیا کرتے کہ ہم نہ مانیں اللہ کو اور ٹھہرائیں اسکے ساتھ برابر کے ساجھی اور چھپے چھپے پچتانے لگے جب دیکھ لیا عذاب اور ہم نے ڈالے ہیں طوق گردنوں میں منکروں کے وہی بدلہ پاتے ہیں جو عمل کرتے تھے ');
INSERT INTO "al_hind" VALUES(34,34,'34:34','اور نہیں بھیجا ہم نے کسی بستی میں کوئی ڈرانے والا مگر کہنے لگے ہیں وہاں کے آسودہ لوگ جو تمہارے ہاتھ بھیجا گیا ہم اس کو نہیں مانتے ');
INSERT INTO "al_hind" VALUES(34,35,'34:35','اور کہنے لگے ہم زیادہ ہیں مال اور اولاد میں اور ہم پر آفت نہیں آنے والی ');
INSERT INTO "al_hind" VALUES(34,36,'34:36','تو کہہ میرا رب ہے جو کشادہ کر دیتا ہے روزی جس کو چاہے اور ماپ کر دیتا ہے لیکن بہت لوگ سمجھ نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(34,37,'34:37','اور تمہارے مال اور تمہاری اولاد وہ نہیں کہ نزدیک کر دیں ہمارے پاس تمہارا درجہ پر جو کوئی یقین لایا اور بھلا کام کیا سو ان کے لیے ہے بدلا دونا انکے کیے کام کا اور وہ جھروکوں میں بیٹھے ہیں دلجمعی سے');
INSERT INTO "al_hind" VALUES(34,38,'34:38','اور جو لوگ دوڑتے ہیں ہماری آیتوں کے ہرانے کو وہ عذاب میں پکڑے ہوئے آتے ہیں ');
INSERT INTO "al_hind" VALUES(34,39,'34:39','تو کہہ میرا رب ہے جو کشادہ کر دیتا ہے روزی جس کو چاہے اپنے بندوں میں اور ماپ کر دیتا ہے اور جو خرچ کرتے ہو کچھ چیز وہ اس کا عوض دیتا ہے اور وہ بہتر ہے روزی دینے والا ');
INSERT INTO "al_hind" VALUES(34,40,'34:40','اور جس دن جمع کرے گا ان سب کو پھر کہے گا فرشتوں کو کیا یہ لوگ تم کوپوجا کرتے تھے ');
INSERT INTO "al_hind" VALUES(34,41,'34:41','وہ کہیں گے پاک ذات ہے تیری ہم تیری طرف میں ہیں نہ انکی طرف میں نہیں پر پوجتے تھے جنوں کو یہ اکثر انہی پر اعتقاد رکھتے تھے ');
INSERT INTO "al_hind" VALUES(34,42,'34:42','سو آج تم مالک نہیں ایک دوسرے کے بھلے کے نہ برے کے اور کہیں گے ہم ان گنہگاروں کو چکھو تکلیف اس آگ کی جس کو تم جھوٹ بتلاتے تھے');
INSERT INTO "al_hind" VALUES(34,43,'34:43','اور جب پڑھی جائیں انکے پاس ہماری آیتیں کھلی کھلی کہیں اور کچھ نہیں مگر یہ ایک مرد ہے چاہتا ہے کہ روک دے تم کو ان سے جن کو پوجتے رہے تمہارے باپ دادے اور کہیں اور کچھ نہیں یہ جھوٹ ہے باندھا ہوا اور کہتے ہیں منکر حق بات کو جب پہنچے ان تک اور کچھ نہیں یہ ایک جادو ہے صریح ');
INSERT INTO "al_hind" VALUES(34,44,'34:44','اور ہم نے دی نہیں انکو کچھ کتابیں کہ جن کو وہ پڑھتے ہوں اور بھیجا نہیں انکے پاس تجھ سے پہلے کوئی ڈرانے والا ');
INSERT INTO "al_hind" VALUES(34,45,'34:45','اور جھٹلایا ہے ان سے اگلوں نے اور یہ نہیں پہنچے دسویں حصہ کو اس کے جو ہم نے انکو دیا تھا پھر جھٹلایا انہوں نے میرے بھیجے ہوؤں کو تو کیسا ہوا انکار میرا ');
INSERT INTO "al_hind" VALUES(34,46,'34:46','تو کہ میں تو ایک ہی نصیحت کرتا ہوں تم کو کہ اٹھ کھڑے ہو اللہ کے نام پر دو دو اور ایک ایک پھر دھیان کرو کہ اس تمہارے رفیق کو کچھ سودا نہیں یہ تو ایک ڈرانے والا ہے تم کو ایک بڑی آفت کے آنے سے ');
INSERT INTO "al_hind" VALUES(34,47,'34:47','تو کہہ جو میں نے تم سے مانگا ہوکچھ بدلا سو وہ تمہی رکھو میرا بدلہ ہے اسی اللہ پر اور اس کے سامنے ہے ہر چیز ');
INSERT INTO "al_hind" VALUES(34,48,'34:48','تو کہہ میرا رب پھینک رہا ہے سچا دین اور وہ جانتا ہے چھپی چیزیں ');
INSERT INTO "al_hind" VALUES(34,49,'34:49','تو کہہ آیا دین سچا اور جھوٹ تو کسی چیز کو نہ پیدا کرے اور نہ پھیر کر لائے ');
INSERT INTO "al_hind" VALUES(34,50,'34:50','تو کہہ اگر میں بہکا ہوا ہوں تو بہکوں گا اپنے ہی نقصان کو اور اگر ہوں سیدھے راستہ پر تو اس سبب سے کہ وحی بھیجتا ہے مجھ کو میر ارب بیشک وہ سب کچھ سنتا ہے نزدیک ');
INSERT INTO "al_hind" VALUES(34,51,'34:51','اور کبھی تو دیکھے جب یہ گھبرائیں پھر نہ بچیں بھاگ کر اور پکڑے ہوئے آئیں نزدیک جگہ سے ');
INSERT INTO "al_hind" VALUES(34,52,'34:52','اور کہنے لگیں ہم نے اسکو یقین مان لیا اور اب کہاں انکا ہاتھ پہنچ سکتا ہے بعید جگہ سے ');
INSERT INTO "al_hind" VALUES(34,53,'34:53','اور اس سے منکر رہے پہلے سے اور پھینکتے رہے بن دیکھے نشانہ پر دور کی جگہ سے ');
INSERT INTO "al_hind" VALUES(34,54,'34:54','اور رکاوٹ پڑ گئ ان میں اور ان کی آرزو میں جیسا کہ کیا گیا ہے انکے طریقہ والوں کے ساتھ اس سے پہلے وہ لوگ تھے ایسے تردد میں جو چین نہ لینے دے ');
INSERT INTO "al_hind" VALUES(35,1,'35:1','سب خوبی اللہ کو ہے جس نے بنا نکالے آسمان اور زمین جس نے ٹھہرایا فرشتوں کو پیغام لانے والے جن کے پر ہیں دو دو اور تین تین اور چار چار بڑھا دیتا ہے پیدائش میں جو چاہے بیشک اللہ ہر چیز کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(35,2,'35:2','جو کچھ کہ کھول دے اللہ لوگوں پر رحمت میں سے تو کوئی نہیں اس کو روکنے والا اور جو کچھ روک رکھے تو کوئی نہیں اس کو بھیجنے والا اسکے سوائے اور وہی ہے زبردست حکمتوں والا ');
INSERT INTO "al_hind" VALUES(35,3,'35:3','اے لوگو یاد کرو احسان اللہ کا اپنے اوپر کیا کوئی ہے بنانے والا اللہ کے سوائے روزی دیتا ہے تم کو آسمان سے اور زمین سے کوئی حاکم نہیں مگر وہ پھر کہاں الٹے جاتے ہو ');
INSERT INTO "al_hind" VALUES(35,4,'35:4','اور اگر تجھ کو جھٹلائیں تو جھٹلائے گئے کتنے رسول تجھ سے پہلے اور اللہ تک پہنچتے ہیں سب کام ');
INSERT INTO "al_hind" VALUES(35,5,'35:5','اے لوگو بیشک اللہ کا وعدہ ٹھیک ہے سو نہ بہکائے تم کو دنیا کی زندگانی اور نہ دغا دے تم کو اللہ کے نام سے وہ دغا باز');
INSERT INTO "al_hind" VALUES(35,6,'35:6','تحقیق شیطان تمہارا دشمن ہے سو تم بھی سمجھ رکھو اس کو دشمن وہ تو بلاتا ہے اپنے گروہ کو اسی واسطے کے ہوں دوزخ والوں میں ');
INSERT INTO "al_hind" VALUES(35,7,'35:7','جو منکر ہوئے ان کو سخت عذاب ہے اور جو یقین لائے اور کئے بھلے کام انکے لئے ہے معافی اور بڑا ثواب');
INSERT INTO "al_hind" VALUES(35,8,'35:8','بھلا ایک شخص کو کہ بھلی سمجھائی گئ اسکو اسکے کام کی برائی پھر دیکھا اس نے اسکو بھلا کیونکہ اللہ بھٹکاتا ہے جس کو چاہے اور سمجھاتا ہے جس کو چاہے سو تیرا جی نہ جاتا رہے ان پر پچتا پچتا کر اللہ کو معلوم ہے جو کچھ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(35,9,'35:9','اور اللہ ہے جس نے چلائی ہیں ہوائیں پھر وہ اٹھاتی ہیں بادل کو پھر ہانک لے گئے ہم اسکو ایک مردہ دیس کی طرف پھر زندہ کر دیا ہم نے اس سے زمین کو اسکے مرجانے کے بعد اسی طرح ہو گا جی اٹھنا ');
INSERT INTO "al_hind" VALUES(35,10,'35:10','جس کو چاہئے عزت تو اللہ کے لیے ہے ساری عزت اس کی طرف چڑھتا ہے کلام ستھرا اور کام نیک اس کو اٹھا لیتا ہے اور جو لوگ داؤ میں ہیں برائیوں کے انکے لیے سخت عذاب ہے اور ان کا داؤ ہے ٹوٹے کا ');
INSERT INTO "al_hind" VALUES(35,11,'35:11','اور اللہ نے تم کو بنایا مٹی سے پھر بوند پانی سے پھر بنایا تم کو جوڑے جوڑے اور نہ پیٹ رہتا ہے کسی مادہ کو اور نہ وہ جنتی ہے بن خبر اس کے اور نہ عمر پاتا ہے کوئی بڑی عمر والا اور نہ گھٹتی ہے کسی کی عمر مگر لکھا ہے کتاب میں بیشک یہ اللہ پر آسان ہے ');
INSERT INTO "al_hind" VALUES(35,12,'35:12','اور برابر نہیں دو دریا یہ میٹھا ہے پیاس بجھاتا ہے خوشگوار ہے پینا اس کا اور یہ کھارا کڑوا اور دونوں میں سے کھاتے ہو گوشت تازہ اور نکالتے ہو گہنا جسکو پہنتے ہو اور تو دیکھے جہازوں کو اس میں کہ چلتے ہیں پانی کو پھاڑتے تاکہ تلاش کرو اسکے فضل سے اور تاکہ تم حق مانو ');
INSERT INTO "al_hind" VALUES(35,13,'35:13','رات گھساتا ہے دن میں اور دن گھساتا ہے رات میں اور کام میں لگا دیا سورج اورچاند کو ہر ایک چلتا ہے ایک مقرر وعدہ تک یہ اللہ ہے تمہارا رب اسی کے لئے بادشاہی ہے اور جن کو تم پکارتے ہو اس کے سوائے وہ مالک نہیں کھجور کی گٹھلی کے ایک چھلکے کے ');
INSERT INTO "al_hind" VALUES(35,14,'35:14','اگر تم ان کو پکارو سنیں نہیں تمہاری پکار اور اگر سنیں پہنچیں نہیں تمہارے کام پر اور قیامت کے دن منکر ہوں گے تمہارے شریک ٹھہرانے سے اور کوئی نہ بتلائے گا تجھ کو جیسا بتلائے خبر رکھنے والا ');
INSERT INTO "al_hind" VALUES(35,15,'35:15','اے لوگو تم ہو محتاج اللہ کی طرف اور اللہ وہی ہے بے پروا سب تعریفوں والا ');
INSERT INTO "al_hind" VALUES(35,16,'35:16','اگر چاہے تم کو لیجائے اور لے آئے ایک نئی خلقت');
INSERT INTO "al_hind" VALUES(35,17,'35:17','اور یہ بات اللہ پر مشکل نہیں ');
INSERT INTO "al_hind" VALUES(35,18,'35:18','اور نہ اٹھائے گا کوئی اٹھانے والا بوجھ دوسرے کا اور اگر پکارے کوئی بوجھل اپنا بوجھ بٹانے کو کوئی نہ اٹھائے اس میں سے ذرا بھی اگر چہ ہو قرابتی تو تو ڈر سنا دیتا ہے انکو جو ڈرتے ہیں اپنے رب سے بن دیکھے اور قائم رکھتے ہیں نماز او جو کوئی سنورے گا تو یہی ہے کہ سنورے گا اپنے فائدہ کو اور اللہ کی طرف ہے سب کو پھر جانا ');
INSERT INTO "al_hind" VALUES(35,19,'35:19','اور برابر نہیں اندھا اور دیکھتا');
INSERT INTO "al_hind" VALUES(35,20,'35:20','اور نہ اندھیرا اور نہ اجالا');
INSERT INTO "al_hind" VALUES(35,21,'35:21','اور نہ سایہ اور نہ لُو');
INSERT INTO "al_hind" VALUES(35,22,'35:22','اور برابر نہیں جیتے اور نہ مردے اللہ سناتا ہے جس کو چاہے اور تو نہیں سنانے والا قبر میں پڑے ہوؤں کو');
INSERT INTO "al_hind" VALUES(35,23,'35:23','تو تو بس ڈر کی خبر پہنچانے والا ہے ');
INSERT INTO "al_hind" VALUES(35,24,'35:24','ہم نے بھیجا ہے تجھ کو سچا دین دیکر خوشی اور ڈر سنانے والا اور کوئی فرقہ نہیں جس میں نہیں ہو چکا کوئی ڈر سنانے والا ');
INSERT INTO "al_hind" VALUES(35,25,'35:25','اور اگر وہ تجھ کو جھٹلائیں تو آگے جھٹلا چکے ہیں جو لوگ کہ ان سے پہلے تھے پہنچے ان کے پاس رسول انکے لیکر کھلی باتیں اور صحیفے اور روشن کتاب ');
INSERT INTO "al_hind" VALUES(35,26,'35:26','پھر پکڑا میں نے منکروں کو سو کیسا ہوا انکار میرا ');
INSERT INTO "al_hind" VALUES(35,27,'35:27','کیا تو نے نہ دیکھا کہ اللہ نے اتارا آسمان سے پانی پھر ہم نے نکالے اس سے میوے طرح طرح کے انکے رنگ اور پہاڑوں میں گھاٹیاں ہیں سفید اور سرخ طرح طرح کے انکے رنگ اور بھجنگے کالے ');
INSERT INTO "al_hind" VALUES(35,28,'35:28','اور آدمیوں میں اور کیڑوں میں اور چوپاؤں میں کتنے رنگ ہیں اسی طرح اللہ سے ڈرتے وہی ہیں اس کے بدنوں میں جن کو سمجھ ہے تحقیق اللہ زبردست ہے بخشنے والا ');
INSERT INTO "al_hind" VALUES(35,29,'35:29','جو لوگ پڑھتے ہیں کتاب اللہ کی اور سیدھی کرتے ہیں نماز اور خرچ کرتے ہیں کچھ ہمارا دیا ہوا چھپے اور کھلے امیدوار ہیں ایک بیوپار کے جس میں ٹوٹا نہ ہو ');
INSERT INTO "al_hind" VALUES(35,30,'35:30','تاکہ پورا دے انکو ثواب ان کا اور زیادہ دے اپنے فضل سے تحقیق وہ ہے بخشنے والا قدر دان ');
INSERT INTO "al_hind" VALUES(35,31,'35:31','اور جو ہم نے تجھ پر اتاری کتاب وہی ٹھیک ہے تصدیق کرنے والی اپنے سےاگلی کتابوں کی بیشک اللہ اپنے بندوں سے خبردار ہے دیکھنے والا ');
INSERT INTO "al_hind" VALUES(35,32,'35:32','پھر ہم نے وارث کئے کتاب کے وہ لوگ جن کو چن لیا ہم نے اپنے بندوں میں سے پھر کوئی ان میں برا کرتا ہے اپنی جان کا اور کوئی ان میں ہے بیچ کی چال پر اور کوئی ان میں آگے بڑھ گیا ہے لیکر خوبیاں اللہ کے حکم سے یہی ہے بڑی بزرگی ');
INSERT INTO "al_hind" VALUES(35,33,'35:33','باغ ہیں بسنے کے جن میں وہ جائیں گے وہاں انکو پہنایا جائے گا کنگن سونے کے او رموتی کے اور انکی پوشاک وہاں ریشمی ہے ');
INSERT INTO "al_hind" VALUES(35,34,'35:34','اور کہیں گے شکر اللہ کا جس نے دور کیا ہم سےغم بیشک ہمارا رب بخشنے والا قدردان ہے ');
INSERT INTO "al_hind" VALUES(35,35,'35:35','جس نے اتارا ہم کو آباد رہنے کے گھر میں اپنے فضل سے نہ پہنچے ہم کو اس میں مشقت اور نہ پہنچے ہمکو اس میں تھکنا ');
INSERT INTO "al_hind" VALUES(35,36,'35:36','اور جو لوگ منکر ہیں انکے لئے ہے آگ دوزخ کی نہ ان پر حکم پہنچے کہ مر جائیں اور نہ ان پر ہلکی ہو وہاں کی کچھ کلفت یہ سزا دیتےہیں ہم ہر ناشکر کو ');
INSERT INTO "al_hind" VALUES(35,37,'35:37','اور وہ چلائیں اس میں اے رب ہم کو نکال کہ ہم کچھ بھلا کر لیں وہ نہیں جو کرتے رہے کیا ہم نے عمر نہ دی تھی تمکو اتنی کہ جس میں سوچ لے جس کو سوچنا ہو او رپہنچا تمہارے پاس ڈرانے والا اب چکھو کہ کوئی نہیں گنہگاروں کا مددگار ');
INSERT INTO "al_hind" VALUES(35,38,'35:38','اللہ بھید جاننے والا ہے آسمانوں کا اور زمین کا اس کو خوب معلوم ہے جو بات ہے دلوں میں ');
INSERT INTO "al_hind" VALUES(35,39,'35:39','وہی ہے جس نے کیا تمکو قائم مقام زمین میں پھر جو کوئی ناشکری کرے تو اس پر پڑے اسکی ناشکری اور منکروں کو نہ بڑھے گی انکے انکار سے ان کے رب کے سامنے مگر بیزاری اور منکروں کو نہ بڑھے گا انکے انکار سے مگر نقصان ');
INSERT INTO "al_hind" VALUES(35,40,'35:40','تو کہہ بھلا دیکھو تو اپنے شریکوں کو جن کو پکارتے ہو اللہ کے سوائے دکھلاؤ تو مجھ کو کیا بنایا انہوں نے زمین میں یا کچھ ان کا ساجھا ہے آسمانوں میں یا ہم نے دی ہے انکو کوئی کتاب سو یہ سند رکھتے ہیں اس کی کوئی نہیں پر جو وعدہ بتلاتے ہیں گنہگار ایک دوسرے کو سب فریب ہے ');
INSERT INTO "al_hind" VALUES(35,41,'35:41','تحقیق اللہ تھام رہا ہے آسمانوں کو اور زمین کو کہ ٹل نہ جائیں اور اگر ٹل جائیں تو کوئی نہ تھام سکے انکو اس کے سوائے وہ ہے تحمل والا بخشنے والا ');
INSERT INTO "al_hind" VALUES(35,42,'35:42','اور قسمیں کھاتے تھے اللہ کی تاکید کی قسمیں اپنی کہ اگر آئے گا انکے پاس کوئی ڈر سنانے والا البتہ بہتر راہ چلیں گے ہر ایک امت سے پھر جب آیا ان کے پاس ڈر سنانے والا اور زیادہ ہو گیا ان کا بدکنا');
INSERT INTO "al_hind" VALUES(35,43,'35:43','غرور کرنا ملک میں اور داؤ کرنا برے کام کا اور برائی کا داؤ الٹے گا انہی داؤں والوں پر پھر اب وہی راہ دیکھتے ہیں پہلوں کے دستور کی سو تو نہ پائے گا اللہ کا دستور بدلتا اور نہ پائے گا اللہ کا دستور ٹلتا ');
INSERT INTO "al_hind" VALUES(35,44,'35:44','کیا پھرے نہیں ملک میں کہ دیکھ لیں کیسا ہوا انجام ان لوگوں کا جو ان سے پہلے تھے اور تھے ان سے بہت سخت زور میں اور اللہ وہ نہیں جس کو تھکائے کوئی چیز آسمانوں میں اور نہ زمین میں وہی ہے سب کچھ جانتا کر سکتا ');
INSERT INTO "al_hind" VALUES(35,45,'35:45','اور اگر پکڑ کرے اللہ لوگوں کی ان کی کمائی پرنہ چھوڑے زمین کی پیٹھ پر ایک بھی ہلنے چلنے والا پر ان کو ڈھیل دیتا ہے ایک مقرر وعدہ تک پھر جب آئے ان کا وعدہ تو اللہ کی نگاہ میں ہیں اس کے سب بندے ');
INSERT INTO "al_hind" VALUES(36,1,'36:1','یٰس ۔');
INSERT INTO "al_hind" VALUES(36,2,'36:2','قسم ہے اس پکے قرآن کی');
INSERT INTO "al_hind" VALUES(36,3,'36:3','تو تحقیق ہے بھیجے ہوؤں میں سے');
INSERT INTO "al_hind" VALUES(36,4,'36:4','اوپر سیدھی راہ کے ');
INSERT INTO "al_hind" VALUES(36,5,'36:5','اتارا زبردست رحم والے نے ');
INSERT INTO "al_hind" VALUES(36,6,'36:6','تاکہ تو ڈرائے ایک قوم کو کہ ڈر نہیں سنا انکے باپ دادوں نے سو ان کو خبر نہیں');
INSERT INTO "al_hind" VALUES(36,7,'36:7','ثابت ہو چکی ہے بات ان میں بہتوں پر سو وہ نہ مانیں گے ');
INSERT INTO "al_hind" VALUES(36,8,'36:8','ہم نے ڈالے ہیں انکی گردنوں میں طوق سو وہ ہیں ٹھوڑیوں تک پھر انکے سر اُلل (ابھر) رہے ہیں ');
INSERT INTO "al_hind" VALUES(36,9,'36:9','اور بنائی ہم نے ان کے آگے دیوار اور پیچھے دیوار پھر اوپر سے ڈھانک دیا سو ان کو کچھ نہیں سوجھتا ');
INSERT INTO "al_hind" VALUES(36,10,'36:10','اور برابر ہے ان کو تو ڈرائے یا نہ ڈرائے یقین نہیں کریں گے ');
INSERT INTO "al_hind" VALUES(36,11,'36:11','تو ڈر سناتے اسکو جو چلے سمجھائے پر اور ڈرے رحمٰن سے بن دیکھے سو اسکو خوشخبری دے معافی کی اور عزت کے ثواب کی ');
INSERT INTO "al_hind" VALUES(36,12,'36:12','ہم تو ہیں جو زندہ کرتے ہیں مردوں کو اور لکھتے ہیں جو آگے بھیج چکے اور جو نشان انکے پیچھے رہے اور ہر چیز گن لی ہے ہم نے ایک کھلی اصل میں ');
INSERT INTO "al_hind" VALUES(36,13,'36:13','اور بیان کر انکے واسطے ایک مثل اس گاؤں کے لوگوں کی جبکہ آئے اس میں بھیجے ہوئے ');
INSERT INTO "al_hind" VALUES(36,14,'36:14','جب بھیجے ہم نے انکی طرف دو تو ان کو کو جھٹلایا پھر ہم نے قوت دی تیسرے سے تب کہا انہوں نے ہم تمہاری طرف آئے ہیں بھیجے ہوئے ');
INSERT INTO "al_hind" VALUES(36,15,'36:15','وہ بولے تم تو یہی انسان ہو جیسے ہم اور رحمٰن نے کچھ نہیں اتارا تم سارے جھوٹ کہتے ہو ');
INSERT INTO "al_hind" VALUES(36,16,'36:16','کہا ہمارا رب جانتا ہے ہم بیشک تمہاری طرف بھیجے ہوئے آئے ہیں ');
INSERT INTO "al_hind" VALUES(36,17,'36:17','اور ہمارا ذمہ یہی ہے پیغام پہنچا دینا کھول کر ');
INSERT INTO "al_hind" VALUES(36,18,'36:18','بولے ہم نے نامبارک دیکھا تم کو اگر تم باز نہ رہو گے تو ہم تم کو سنگسار کریں گے اور تم کو پہچے گا ہمارے ہاتھ سے عذاب دردناک ');
INSERT INTO "al_hind" VALUES(36,19,'36:19','کہنے لگے تمہاری نا مبارکی تمہارے ساتھ ہے کیا اتنی بات پر کہ تم کو سمجھایا کوئی نہیں پرتم لوگ کہ حد پر نہیں رہتے ');
INSERT INTO "al_hind" VALUES(36,20,'36:20','اور آیا شہر کے پرلے سرے سے ایک مرد دوڑتا ہوا بولا اے قوم چلو راہ پر بھیجے ہوؤں کی');
INSERT INTO "al_hind" VALUES(36,21,'36:21','چلو راہ پر ایسے شخص کی جو تم سے بدلا نہیں چاہتے اور وہ ٹھیک رستہ پر ہیں ');
INSERT INTO "al_hind" VALUES(36,22,'36:22','اور مجھ کو کیا ہوا کہ میں بندگی نہ کروں اسکی جس نے مجھ کو بنایا اور اسی کی طرف سب پھر جاؤ گے ');
INSERT INTO "al_hind" VALUES(36,23,'36:23','بھلا میں پکڑوں اس کے سوائے اوروں کو پوجنا کہ اگر مجھ پر چاہے رحمٰن تکلیف تو کچھ کام نہ آئے مجھ کو ان کی سفارش اور نہ وہ مجھ کو چھڑائیں');
INSERT INTO "al_hind" VALUES(36,24,'36:24','تو تو میں بھٹکتا رہوں صریح ');
INSERT INTO "al_hind" VALUES(36,25,'36:25','میں یقین لایا تمہارے رب پر مجھ سے سن لو ');
INSERT INTO "al_hind" VALUES(36,26,'36:26','حکم ہوا چلا جا بہشت میں بولا کسی طرح میری قوم معلوم کر لیں');
INSERT INTO "al_hind" VALUES(36,27,'36:27','کہ کس بنا پر بخشا مجھ کو میرے رب نے اور کیا مجھ کو عزت والوں میں ');
INSERT INTO "al_hind" VALUES(36,28,'36:28','اور اتاری نہیں ہم نے اس کی قوم پر اس کے پیچھے کوئ فوج آسمان سے اور ہم فوج نہیں اتارا کرتے');
INSERT INTO "al_hind" VALUES(36,29,'36:29','بس یہی تھی ایک چنگھاڑ پھر اسی دم سب بجھ گئے ');
INSERT INTO "al_hind" VALUES(36,30,'36:30','کیا افسوس ہے بندوں پر کوئی رسول نہیں آیا ان کے پاس جس سے ٹھٹھا نہیں کرتے');
INSERT INTO "al_hind" VALUES(36,31,'36:31','کیا نہیں دیکھتے کتنی غارت کر چکے ہم ان سے پہلے جماعتیں کہ وہ انکے پاس پھر کر نہیں آئیں گی ');
INSERT INTO "al_hind" VALUES(36,32,'36:32','اور ان سب میں کوئی نہیں جو اکھٹے ہو کر نہ آئیں ہمارے پس پکڑے ہوئے ');
INSERT INTO "al_hind" VALUES(36,33,'36:33','اور ایک نشانی ہے انکے واسطے زمین مردہ اسکو ہم نے زندہ کر دیا اور نکالا اس میں سے اناج سو اسی میں سے کھاتے ہیں');
INSERT INTO "al_hind" VALUES(36,34,'36:34','اور بنائے ہم نے اس میں باغ کھجور کے اور انگور کے اور بہا دیے اس میں بعضے چشمے');
INSERT INTO "al_hind" VALUES(36,35,'36:35','کہ کھائیں اسکے میووں سے اور اس کو بنایا نہیں انکے ہاتھوں نے پھر کیوں شکر نہیں کرتے ');
INSERT INTO "al_hind" VALUES(36,36,'36:36','پاک ذات ہے جس نے بنائے جوڑے سب چیز کے اس قسم سے جو اگتا ہے زمین میں اور خود ان میں سے اور ان چیزوں میں کہ جن کی انکو خبر نہیں ');
INSERT INTO "al_hind" VALUES(36,37,'36:37','اور ایک نشانی ہے انکے واسطے رات کھینچ لیتے ہیں ہم اس پر سے دن کو پھر تبہی یہ رہ جاتے ہیں اندھیرے میں');
INSERT INTO "al_hind" VALUES(36,38,'36:38','اور سورج چلا جاتا ہے اپنے ٹھہرے ہوئے رستہ پر یہ سادھا ہے اس زبردست باخبر نے ');
INSERT INTO "al_hind" VALUES(36,39,'36:39','اور چاند کو ہم نے بانٹ دی ہیں منزلیں یہاں تک کہ پھر آ رہا ہے جیسے ٹہنی پرانی ');
INSERT INTO "al_hind" VALUES(36,40,'36:40','نہ سورج سے ہو کہ پکڑ لے چاند کو اور نہ رات آگے بڑھے دن سے اور ہر کوئی ایک چکر میں پیرتے ہیں ');
INSERT INTO "al_hind" VALUES(36,41,'36:41','اور ایک نشانی ہے انکے واسطے کہ ہم نے اٹھا لیا ان کی نسل کو اس بھری ہوئی کشتی میں');
INSERT INTO "al_hind" VALUES(36,42,'36:42','اور بنا دیا ہم نے انکے واسطے کشتی جیسی چیزوں کو جس پر سوار ہوتے ہیں ');
INSERT INTO "al_hind" VALUES(36,43,'36:43','اور اگر ہم چاہیں تو انکو ڈبا دیں پھر کوئی نہ پہنچےانکی فریاد کو اور نہ وہ چھڑائے جائیں');
INSERT INTO "al_hind" VALUES(36,44,'36:44','مگر ہم اپنی مہربانی سے اور انکا کام چلانے کو ایک وقت تک ');
INSERT INTO "al_hind" VALUES(36,45,'36:45','اور جب کہیے ان کو بچو اس سے جو تمہارے سامنے آتا ہے اور جو پیچھے چھوڑتے ہو شاید تم پر رحم ہو');
INSERT INTO "al_hind" VALUES(36,46,'36:46','اور کوئی حکم نہیں پہنچتا انکو اپنے رب کے حکموں سے جس کو وہ ٹلاتے نہ ہوں ');
INSERT INTO "al_hind" VALUES(36,47,'36:47','اور جب کہیے ان کو خرچ کرو کچھ اللہ کا دیا کہتے ہیں منکر ایمان والوں کو ہم کیوں کھلائیں ایسے کو کہ اللہ چاہتا تو اس کو کھلا دیتا تم لوگ تو بالکل بہک رہے ہو صریح ');
INSERT INTO "al_hind" VALUES(36,48,'36:48','اور کہتے ہیں کب ہو گا یہ وعدہ اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(36,49,'36:49','یہ تو راہ دیکھتے ہیں ایک چنگھاڑ کی جو ان کو آ پکڑے گی جب آپس میں جھگڑ رہے ہوں گے');
INSERT INTO "al_hind" VALUES(36,50,'36:50','پھر نہ کر سکیں گے کہ کچھ کہہ ہی مریں اور نہ اپنے گھر کو پھر کر جا سکیں گے ');
INSERT INTO "al_hind" VALUES(36,51,'36:51','اور پھونکی جائے صور پھر تبہی وہ قبروں سے اپنے رب کی پھیل پڑیں گے ');
INSERT INTO "al_hind" VALUES(36,52,'36:52','کہیں گے اے خرابی ہماری کس نے اٹھا دیا ہم کو ہماری نیند کی جگہ سے یہ وہ ہے جو وعدہ کیا تھا رحمٰن نے اور سچ کہا تھا پیغمبروں نے ');
INSERT INTO "al_hind" VALUES(36,53,'36:53','بس ایک چنگھاڑ ہو گی پھر اسی دم وہ سارے ہمارے پاس پکڑے چلے آئیں گے ');
INSERT INTO "al_hind" VALUES(36,54,'36:54','پھر آج کے دن ظلم نہ ہوگا کسی جی پر ذرا اور وہی بدلہ پاؤ گے جو کرتے تھے ');
INSERT INTO "al_hind" VALUES(36,55,'36:55','تحقیق بہشت کے لوگ آج ایک مشغلہ میں ہیں باتیں کرتے');
INSERT INTO "al_hind" VALUES(36,56,'36:56','وہ اور ان کی عورتیں سایوں میں تختوں پر بیٹھے ہیں تکیہ لگائے');
INSERT INTO "al_hind" VALUES(36,57,'36:57','انکے لئے وہاں ہے میوہ اور انکے لئے ہے جو کچھ مانگیں ');
INSERT INTO "al_hind" VALUES(36,58,'36:58','سلام بولنا ہے رب مہربان سے ');
INSERT INTO "al_hind" VALUES(36,59,'36:59','اور تم الگ ہو جاؤ آج اے گناہگارو ');
INSERT INTO "al_hind" VALUES(36,60,'36:60','میں نے نہ کہہ رکھا تھا تم کو اے آدم کی اولاد کہ نہ پوجیو شیطان کو وہ کھلا دشمن ہے تمہارا');
INSERT INTO "al_hind" VALUES(36,61,'36:61','اور یہ کہ پوجو مجھ کو یہ راہ ہے سیدھی ');
INSERT INTO "al_hind" VALUES(36,62,'36:62','اور وہ بہکا لے گیا تم میں سے بہت خلقت کو پھر کیا تم کو سمجھ نہ تھی');
INSERT INTO "al_hind" VALUES(36,63,'36:63','یہ دوزخ ہے جس کا تم کو وعدہ تھا');
INSERT INTO "al_hind" VALUES(36,64,'36:64','جا پڑو اس میں آج کے دن بدلا اپنے کفر کا ');
INSERT INTO "al_hind" VALUES(36,65,'36:65','آج ہم مہر لگا دیں گے ان کے منہ پر اور بولیں گے ہم سے ان کے ہاتھ اور بتلائیں گے ان کے پاؤں جو کچھ وہ کماتے تھے ');
INSERT INTO "al_hind" VALUES(36,66,'36:66','اور اگر ہم چاہیں مٹا دیں ان کی آنکھیں پھر دوڑیں رستہ پانے کو پھر کہاں سے سوجھے');
INSERT INTO "al_hind" VALUES(36,67,'36:67','اور اگر ہم چاہیں صورت مسخ کر دیں انکی جہاں کی تہاں پھر نہ آگے چل سکیں اور نہ وہ الٹے پھر سکیں ');
INSERT INTO "al_hind" VALUES(36,68,'36:68','اور جس کو ہم بوڑھا کریں اوندھا کریں اس کی پیدائش میں پھر کیا ان کو سمجھ نہیں ');
INSERT INTO "al_hind" VALUES(36,69,'36:69','اور ہم نے نہیں سکھایا اسکو شعر کہنا اور یہ اس کے لائق نہیں یہ تو خالص نصیحت ہے اورقرآن ہے صاف ');
INSERT INTO "al_hind" VALUES(36,70,'36:70','تاکہ ڈر سنائے اس کو جس میں جان ہو اور ثابت ہو الزام منکروں پر ');
INSERT INTO "al_hind" VALUES(36,71,'36:71','کیا اور نہیں دیکھتے وہ کہ ہم نے بنا دیے انکے واسطے اپنے ہاتھوں کی بنائی چیزوں سے چوپائے پھر وہ انکے مالک ہیں ');
