INSERT INTO "al_hind" VALUES(38,32,'38:32','تو بولا میں نے دوست رکھا مال کی محبت کو اپنے رب کی یاد سے یہاں تک کہ سورج چھپ گیا اوٹ میں');
INSERT INTO "al_hind" VALUES(38,33,'38:33','پھیر لاؤ ان کو میرے پاس پھر لگا جھاڑنے انکی پنڈلیاں اور گردنیں ');
INSERT INTO "al_hind" VALUES(38,34,'38:34','اور ہم نے جانچا سلیمان کو اور ڈال دیا اس کے تخت پر ایک دھڑ پھر وہ رجوع ہوا ');
INSERT INTO "al_hind" VALUES(38,35,'38:35','بولا اے رب میرے معاف کر مجھ کو اور بخش مجھ کو وہ بادشاہی کہ مناسب نہ ہو کسی کو میرے پیچھے بیشک تو ہے سب کچھ بخشنے والا ');
INSERT INTO "al_hind" VALUES(38,36,'38:36','پھر ہم نے تابع کر دیا اسکے ہوا کو چلتی تھی اس کے حکم سے نرم نرم جہاں پہنچنا چاہتا');
INSERT INTO "al_hind" VALUES(38,37,'38:37','اور تابع کر دیے شیطان سارے عمارت بنانے والے اور غوطہ لگانے والے ');
INSERT INTO "al_hind" VALUES(38,38,'38:38','بہت سے اور جو باہم جکڑے ہوئے ہیں بیڑیوں میں ');
INSERT INTO "al_hind" VALUES(38,39,'38:39','یہ ہے بخشش ہماری اب تو احسان کریا رکھ چھوڑ کچھ حساب نہ ہوگا ');
INSERT INTO "al_hind" VALUES(38,40,'38:40','اور س کا ہمارے یہاں مرتبہ ہے اور اچھا ٹھکانہ ');
INSERT INTO "al_hind" VALUES(38,41,'38:41','اور یاد کر ہمارے بندے ایوب کو جب اس نے پکارا اپنے رب کہ مجھ کو لگا دی شیطان نے ایذ اور تکلیف ');
INSERT INTO "al_hind" VALUES(38,42,'38:42','لات مار اپنے پاؤں سے یہ چشمہ نکلا نہانے کو اور ٹھنڈا اور پینے کو');
INSERT INTO "al_hind" VALUES(38,43,'38:43','اور بخشے ہم نے اسکو اسکے گھر والے اور ان کے برابر ان کے ساتھ اپنی طرف کی مہربانی سے اور یاد رکھنے کو عقل والوں کے ');
INSERT INTO "al_hind" VALUES(38,44,'38:44','اور پکڑ اپنے ہاتھ میں سیلنکوں کا مٹھا پھر اس سے مار لے اور قسم میں جھوٹا نہ ہو ہم نے اس کو پایا جھیلنے والا بہت خوب بندہ تحقیق وہ ہے رجوع رہنے والا');
INSERT INTO "al_hind" VALUES(38,45,'38:45','اور یاد کر ہمارے بندوں کو ابراہیم اور اسحٰق اور یعقوب ہاتھوں والے اور آنکھوں والے ');
INSERT INTO "al_hind" VALUES(38,46,'38:46','ہم نے امتیاز دیا انکو ایک چنی ہوئی بات کا وہ یاد اس گھر کی ');
INSERT INTO "al_hind" VALUES(38,47,'38:47','اور وہ سب ہمارے نزدیک ہیں چنے ہوئے نیک لوگوں میں');
INSERT INTO "al_hind" VALUES(38,48,'38:48','اور یاد کر اسمٰعیل کو اور الیسع کو اور ذوالکفل کو اور ہر ایک تھا خوبی والا ');
INSERT INTO "al_hind" VALUES(38,49,'38:49','یہ ایک مذکور ہو چکا اور تحقیق ڈر والوں کے لیے ہے اچھا ٹھکانا');
INSERT INTO "al_hind" VALUES(38,50,'38:50','باغ ہیں سدا بسنے کے کھول رکھے ہیں انکے واسطے دروازے ');
INSERT INTO "al_hind" VALUES(38,51,'38:51','تکیہ لگائے ہوئے بیٹھے ہیں ان میں میوے بہت اور شراب ');
INSERT INTO "al_hind" VALUES(38,52,'38:52','اور ان کے پاس عورتیں ہیں نیچی نگاہ والیاں ایک عمر کی ');
INSERT INTO "al_hind" VALUES(38,53,'38:53','یہ وہ ہے جو تم سے وعدہ کیا گیا حساب کے دن پر');
INSERT INTO "al_hind" VALUES(38,54,'38:54','یہ ہی روزی ہماری دی ہوئی اسکو نہیں نبڑنا ');
INSERT INTO "al_hind" VALUES(38,55,'38:55','یہ سن چکے اور تحقیق شریروں کے واسطے ہے برا ٹھکانہ');
INSERT INTO "al_hind" VALUES(38,56,'38:56','دوزخ ہے جس میں انکو ڈالیں گے سو کیا بری آرام کرنے کی جگہ ہے');
INSERT INTO "al_hind" VALUES(38,57,'38:57','یہ ہے اب اسکو چکھیں گرم پانی اور پیپ ');
INSERT INTO "al_hind" VALUES(38,58,'38:58','اور کچھ اور اسی شکل کی طرح طرح کی چیزیں');
INSERT INTO "al_hind" VALUES(38,59,'38:59','یہ ایک فوج ہے دھستی آ رہی ہے تمہارے ساتھ جگہ نہ ملیو ان کو یہ ہیں گھسنے والے آگ میں');
INSERT INTO "al_hind" VALUES(38,60,'38:60','وہ بولے بلکہ تم ہی ہو کہ جگہ نہ ملیو تم کو تم ہی پیش لائے ہمارے یہ بلا سو کیا بری ٹھہرنے کی جگہ ہے ');
INSERT INTO "al_hind" VALUES(38,61,'38:61','وہ بولے اے رب ہمارے جو کوئی لایا ہمارے پیش یہ سو بڑھا دے اسکو دونا عذاب آگ میں ');
INSERT INTO "al_hind" VALUES(38,62,'38:62','اور کہیں گے کیا ہوا کہ ہم نہیں دیکھتے ان مردوں کو کہ ہم ان کو شمار کرتے تھے برے لوگوں میں');
INSERT INTO "al_hind" VALUES(38,63,'38:63','کیا ہم نے انکو ٹھٹھے میں پکڑا تھا یا چوک گئیں ان سے ہماری آنکھیں ');
INSERT INTO "al_hind" VALUES(38,64,'38:64','یہ بات ٹھیک ہونی ہے جھگڑا کرنا آپس میں دوزخیوں کا ');
INSERT INTO "al_hind" VALUES(38,65,'38:65','تو کہہ میں تو یہی ہوں ڈر سنا دینے والا حاکم کوئی نہیں مگر اللہ اکیلا دباؤ والا');
INSERT INTO "al_hind" VALUES(38,66,'38:66','رب آسمانون کا اور زمین کا اور جو انکے بیچ میں زبردست بخشنے والا ');
INSERT INTO "al_hind" VALUES(38,67,'38:67','تو کہہ یہ ایک بڑی خبر ہے');
INSERT INTO "al_hind" VALUES(38,68,'38:68','کہ تم اس کو دھیان میں نہیں لاتے ');
INSERT INTO "al_hind" VALUES(38,69,'38:69','مجھ کو کچھ خبر نہ تھی او پر کی مجلس کی جب وہ آپس میں تکرار کرتے ہیں');
INSERT INTO "al_hind" VALUES(38,70,'38:70','مجھ کو تو یہی حکم آتا ہے کہ اور کچھ نہیں میں تو ڈر سنا دینے والا ہوں کھول کر ');
INSERT INTO "al_hind" VALUES(38,71,'38:71','جب کہا تیرے رب نے فرشتوں کو میں بناتا ہوں ایک انسان مٹی کا ');
INSERT INTO "al_hind" VALUES(38,72,'38:72','پھر جب ٹھیک بنا چکوں اور پھونکوں اس میں ایک اپنی جان تو تم گرِ پڑو اسکے آگے سجدہ میں');
INSERT INTO "al_hind" VALUES(38,73,'38:73','پھر سجدہ کیا فرشتوں نے سب نے اکھٹے ہو کر');
INSERT INTO "al_hind" VALUES(38,74,'38:74','مگر ابلیس نے غرور کیا اور تھا وہ منکروں میں ');
INSERT INTO "al_hind" VALUES(38,75,'38:75','فرمایا اے ابلیس کس چیز نے روک دیا تجھ کو کہ سجدہ کرے اس کو جس کو میں نے بنایا اپنے دونوں ہاتھوں سے یہ تو نے غرور کیا یا تو بڑا تھا درجہ میں ');
INSERT INTO "al_hind" VALUES(38,76,'38:76','بولا میں بہتر ہوں اس سے مجھ کو بنایا تو نے آگ سے اور اس کو بنایا مٹی سے ');
INSERT INTO "al_hind" VALUES(38,77,'38:77','فرمایا تو تو نکل یہاں سے کہ تو مردود ہوا ');
INSERT INTO "al_hind" VALUES(38,78,'38:78','اور تجھ پر میری پھٹکار ہے اس جزا کے دن تک ');
INSERT INTO "al_hind" VALUES(38,79,'38:79','بولا اے رب مجھ کو ڈھیل دے جس دن تک کہ مردے جی اٹھیں ');
INSERT INTO "al_hind" VALUES(38,80,'38:80','فرمایا تو تجھ کو ڈھیل ہے');
INSERT INTO "al_hind" VALUES(38,81,'38:81','اسی وقت کے دن تک جو معلوم ہے ');
INSERT INTO "al_hind" VALUES(38,82,'38:82','بولا تو قسم ہے تیری عزت کی میں گمراہ کروں گا ان سب کو');
INSERT INTO "al_hind" VALUES(38,83,'38:83','مگر جو بندے ہیں تیرے ان میں چنے ہوئے');
INSERT INTO "al_hind" VALUES(38,84,'38:84','فرمایا تو ٹھیک بات یہ ہے اور میں ٹھیک ہی کہتا ہوں ');
INSERT INTO "al_hind" VALUES(38,85,'38:85','مجھ کو بھرنا ہے دوزخ تجھ سے اور جو ان میں تیری راہ چلے ان سب سے');
INSERT INTO "al_hind" VALUES(38,86,'38:86','تو کہہ میں مانگتا نہیں تم سے اس پر کچھ بدلا اور میں نہیں اپنے آپ کو بنانے والا');
INSERT INTO "al_hind" VALUES(38,87,'38:87','یہ تو ایک فہمائش ہے سارے جہان والوں کو');
INSERT INTO "al_hind" VALUES(38,88,'38:88','اور معلوم کر لو گے اس کا احوال تھوڑی دیر کے پیچھے ');
INSERT INTO "al_hind" VALUES(39,1,'39:1','اتارنا ہے کتاب کا اللہ سے جو زبردست ہے حکمتوں والا ');
INSERT INTO "al_hind" VALUES(39,2,'39:2','میں نے اتاری ہے تیری طرف کتاب ٹھیک ٹھیک سو بندگی کر اللہ کی خالص کر کر اس کے واسطے بندگی');
INSERT INTO "al_hind" VALUES(39,3,'39:3','سنتا ہے اللہ ہی کیلئے ہے بندگی خالص اور جنہوں نے پکڑ رکھے ہیں اس سے ورے حمایتی کہ ہم تو ان کو پوجتے ہیں اس واسطے کہ ہم کو پہنچا دیں اللہ کی طرف قریب کے درجہ میں بیشک اللہ فیصلہ کر دے گا ان میں جس چیز میں وہ جھگڑ رہے ہیں البتہ اللہ راہ نہیں دیتا اسکو جو ہو جھوٹا حق نہ ماننے والا ');
INSERT INTO "al_hind" VALUES(39,4,'39:4','اگر اللہ چاہتا کہ اولاد کر لے تو چن لیتا اپنی خلق میں جو کچھ چاہتا وہ پاک ہے وہی ہے اللہ اکیلا دباؤ والا ');
INSERT INTO "al_hind" VALUES(39,5,'39:5','بنائے آسمان اور زمین ٹھیک لپیٹتا ہے رات کو دن پر اور لپیٹتا ہے دن کو رات پر اور کام میں لگا دیا سورج اور چاند کو ہر ایک چلتا ہے ایک ٹھہری ہوئی مدت پر سنتا ہے وہی ہے زبردست گناہ بخشنے والا ');
INSERT INTO "al_hind" VALUES(39,6,'39:6','بنایا تم کو ایک جی سے پھر بنایا اس سے اس کا جوڑ ا اور اتارے تمہارے واسطے چوپاؤں سے آٹھ نر مادہ بناتا ہے تم کو ماں کے پیٹ میں ایک طرح پر دوسری طرح کے پیچھے تین اندھیروں کے بیچ وہ اللہ ہے رب تمہارا اسی کا راج ہے کسی کی بندگی نہیں اسکےسوائے پھر کہاں سے پھرے جاتے ہو ');
INSERT INTO "al_hind" VALUES(39,7,'39:7','اگر تم منکر ہو گے تو اللہ پروا نہیں رکھتا تمہاری اور پسند نہیں کرتا اپنے بندوں کا منکر ہونا اور اگر اس کا حق مانو گے تو اسکو تمہارے لئے پسند کرے گا اور نہ اٹھائے گا کوئی اٹھانے والا بوجھ دوسرے کا پھر اپنے رب کی طرف تم کو جانا ہے تو وہ جتلائے گا تم کو جو تم کرتے تھے مقرر اسکو خبر ہے دلوں کی بات کی ');
INSERT INTO "al_hind" VALUES(39,8,'39:8','اور جب آ لگے انسان کو سختی پکارے اپنے رب کو رجوع ہو کر اسکی طرف پھر جب بخشے اسکو نعمت اپنی طرف سے بھول جائے اس کو کہ جس کے لئے پکار رہا تھا پہلے سے اور ٹھہرائے اللہ کی برابر اوروں کو تاکہ بہکائے اسکی راہ سے تو کہہ برت لے ساتھ اپنے کفر کے تھوڑے دنوں تو ہے دوزخ والوں میں ');
INSERT INTO "al_hind" VALUES(39,9,'39:9','بھلا ایک جو بندگی میں لگا ہوا ہے رات کی گھڑیوں میں سجدے کرتا ہوا اور کھڑا ہو اخطرہ رکھتا ہے آخرت کا اور امید رکھتا ہے اپنے رب کی مہربانی کی تو کہہ کوئی برابر ہوتے ہیں سمجھ والے اور بے سمجھ سوچتے وہی ہیں جن کو عقل ہے ');
INSERT INTO "al_hind" VALUES(39,10,'39:10','تو کہہ اے بندو میرے جو یقین لائے ہو ڈرو اپنے رب سے جنہوں نے نیکی کی اس دنیا میں انکے لئے ہی ہے بھلائی اور زمین اللہ کی کشادہ ہے صبر کرنے والوں ہی کو ملتا ہے ان کا ثواب بے شمار ');
INSERT INTO "al_hind" VALUES(39,11,'39:11','تو کہہ مجھ کو حکم ہے کہ بندگی کروں اللہ کی خالص کر کر اسکے لئے بندگی');
INSERT INTO "al_hind" VALUES(39,12,'39:12','اور حکم ہے کہ میں ہوں سب سے پہلے حکم بردار ');
INSERT INTO "al_hind" VALUES(39,13,'39:13','تو کہہ میں ڈرتا ہوں اگر حکم نہ مانوں اپنے رب کا ایک بڑے دن کے عذاب سے ');
INSERT INTO "al_hind" VALUES(39,14,'39:14','تو کہہ میں تو اللہ کو پوجتا ہوں خالص کر کر اپنی بندگی اس کے واسطے');
INSERT INTO "al_hind" VALUES(39,15,'39:15','اب تم پوجو جس کو چاہو اسکے سوائے تو کہہ بڑے ہارنے والے وہ جو ہار بیٹھے اپنی جان کو اور اپنے گھر والوں کو قیامت کے دن سنتا ہے یہی ہے صریح ٹوٹا ');
INSERT INTO "al_hind" VALUES(39,16,'39:16','ان کے واسطے اوپر سے بادل ہیں آگ کے اور نیچے سے بادل اس چیز سے ڈراتا ہے اللہ اپنے بندوں کو اے بندو میرے تو مجھ سے ڈرو ');
INSERT INTO "al_hind" VALUES(39,17,'39:17','اور جو لوگ بچے شیطانوں سے کہ انکو پوجیں اور رجوع ہوئے اللہ کی طرف انکے لئے ہے خوشخبری سو تو خوشی سنا دے میرے بندوں کو');
INSERT INTO "al_hind" VALUES(39,18,'39:18','جو سنتے ہیں بات پھر چلتے ہیں اس پر جو اس میں نیک ہے وہی ہیں جن کو رستہ دیا اللہ نے اور وہی ہیں عقل والے ');
INSERT INTO "al_hind" VALUES(39,19,'39:19','بھلا جس پر ٹھیک ہو چکا عذاب کا حکم بھلا تو خلاص کر سکے گا اسکو جو آگ میں پڑ چکا ');
INSERT INTO "al_hind" VALUES(39,20,'39:20','لیکن جو ڈرتے ہیں اپنے رب سے ان کے واسطے ہیں جھروکے ان کے اوپر اور جھروکے چنے ہوئے اور انکے نیچے بہتی ہیں ندیاں وعدہ ہو چکا اللہ کا اللہ نہیں خلاف کرتا اپنا وعدہ');
INSERT INTO "al_hind" VALUES(39,21,'39:21','تو نے نہیں دیکھا کہ اللہ نے اتارا آسمان سے پانی پھر چلا دیا وہ پانی چشموں میں زمین کے پھر نکالتا ہے اس سے کھیتی کئ کئ رنگ بدلتی اس پر پھر آئے تیاری پر تو تو دیکھے اس کا رنگ زرد پھر کر ڈالتا ہے اسکو چورا چورا بیشک اس میں نصیحت ہے عقلمندوں کے واسطے ');
INSERT INTO "al_hind" VALUES(39,22,'39:22','بھلا جس کا سینہ کھول دیا اللہ نے دین اسلام کے واسطے سو وہ روشنی میں ہے اپنے رب کی طرف سے سو خرابی ہے انکو جن کے دل سخت ہیں اللہ کی یاد سے وہ پڑے پھرتے ہیں بھٹکتے صریح ');
INSERT INTO "al_hind" VALUES(39,23,'39:23','اللہ نے اتاری بہتر بات کتاب کی آپس میں ملتی دہرائی ہوئی بال کھڑے ہوئے ہیں اس سے کھال پر ان لوگوں کے جو ڈرتے ہیں اپنے رب سے پھر نرم ہوتی ہیں ان کی کھالیں اور ان کے دل اللہ کی یاد پر یہ ہے راہ دینا اللہ کا اس طرح راہ دیتا ہے جس کو چاہے اور جس کو راہ بھلائے اللہ اسکو کوئی نہیں سجھانے والا ');
INSERT INTO "al_hind" VALUES(39,24,'39:24','بھلا ایک وہ جو روکتا ہے اپنے منہ پر عذاب دن قیامت کے اور کہے گا بے انصافوں کو چکھو جو تم کماتے تھے ');
INSERT INTO "al_hind" VALUES(39,25,'39:25','جھٹلا چکے ہیں ان سے اگلے پھر پہنچا ان پر عذاب ایسی جگہ سے کہ انکو خیال بھی نہ تھا۔');
INSERT INTO "al_hind" VALUES(39,26,'39:26','پھر چکھائی انکو اللہ نے رسوائی دنیا کی زندگی میں اور عذاب آخرت کا تو بہت ہی بڑا ہے اگر ان کو سمجھ ہوتی ');
INSERT INTO "al_hind" VALUES(39,27,'39:27','اور ہم نے بیان کی لوگوں کے واسطے اس قرآن میں سب چیز کی مثل تاکہ وہ دھیان کریں');
INSERT INTO "al_hind" VALUES(39,28,'39:28','قرآن ہے عربی زبان کا جس میں کجی نہیں تاکہ وہ بچ کر چلیں ');
INSERT INTO "al_hind" VALUES(39,29,'39:29','اللہ نے بتلائی ایک مثل ایک مرد ہے کہ اس میں شریک ہیں کئ ضدی اور ایک مرد ہے پورا ایک شخص کا کیا برابر ہوتی ہیں دونوں مثل سب خوبی اللہ کے لئے ہے پر وہ بہت لوگ سمجھ نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(39,30,'39:30','بیشک تو بھی مرتا ہے اور وہ بھی مرتے ہیں');
INSERT INTO "al_hind" VALUES(39,31,'39:31','پھر مقرر تم قیامت کے دن اپنے رب کے آگے جھگڑو گے ');
INSERT INTO "al_hind" VALUES(39,32,'39:32','پھر اس سے زیادہ ظالم کون جس نے جھوٹ بولا اللہ پر اور جھٹلایا سچی بات کو جب پہنچی اس کے پاس کیا نہیں دوزخ میں ٹھکانہ منکروں کا ');
INSERT INTO "al_hind" VALUES(39,33,'39:33','اور جو لے کر آیا سچی بات اور سچ مانا جس نے اسکو وہی لوگ ہیں ڈر والے ');
INSERT INTO "al_hind" VALUES(39,34,'39:34','ان کے لئے ہے جو وہ چاہیں اپنے رب کے پاس یہ ہے بدلا نیکی والوں کا');
INSERT INTO "al_hind" VALUES(39,35,'39:35','تاکہ اتار دے اللہ ان پر سے برے کام جو انہوں نے کئے تھے اور بدلہ میں دے ان کو ثواب بہتر کاموں کا جو وہ کرتے تھے ');
INSERT INTO "al_hind" VALUES(39,36,'39:36','کیا اللہ بس نہں اپنے بندہ کو اور تجھ کو ڈراتے ہیں ان سے جو اس کے سوائے ہیں اور جسکو راہ بھلائے اللہ تو کوئی نہیں اسکو راہ دینے والا');
INSERT INTO "al_hind" VALUES(39,37,'39:37','اور جسکو راہ سجھائے اللہ تو کوئی نہیں اسکو بھلانے والا کیا نہیں ہے اللہ زبردست بدلہ لینے والا ');
INSERT INTO "al_hind" VALUES(39,38,'39:38','اور جو تو ان سے پوچھے کس نے بنائے آسمان اور زمین تو کہیں اللہ نے تو کہہ بھلا دیکھو تو جن کو پوجتے ہو اللہ کے سوائے اگر چاہے اللہ مجھ پر کچھ تکلیف تو وہ ایسے ہیں کہ کھول دیں تکلیف اسکی ڈالی ہوئی یا وہ چاہے مجھ پر مہربانی تو وہ ایسے ہیں کہ روک دیں اسکی مہربانی کو تو کہہ مجھ کو بس ہے اللہ اسی پر بھروسہ رکھتے ہیں بھروسہ رکھنے والے ');
INSERT INTO "al_hind" VALUES(39,39,'39:39','تو کہہ اے قوم کام کئے جاؤ اپنی جگہ پر میں بھی کام کرتا ہوں اب آگے جان لو گے');
INSERT INTO "al_hind" VALUES(39,40,'39:40','کس پر آتی ہے آفت کہ اس کو رسوا کرے اور اترتا ہے اس پر عذاب سدا رہنے والا ');
INSERT INTO "al_hind" VALUES(39,41,'39:41','ہم نے اتاری ہے تجھ پر کتاب لوگوں کے واسطے سچے دین کے ساتھ پھر جو کوئی راہ پر آیا سو اپنے بھلے کو اور جو کوئی بہکا سو یہی بات ہے کہ بہکا اپنے برے کو اور تو ان کا ذمہ دار نہیں ');
INSERT INTO "al_hind" VALUES(39,42,'39:42','اللہ کھینچ لیتا ہے جانیں جب وقت ہو انکے مرنے کا اور جو نہیں مریں انکو کھینچ لیتا ہے انکی نیند میں پھر رکھ چھوڑتا ہے جن پر مرنا ٹھہرا دیا ہے بھیج دیتا ہے اوروں کو ایک وعدہ مقرر تک اس بات میں پتے ہیں ان لوگوں کو جو دھیان کریں ');
INSERT INTO "al_hind" VALUES(39,43,'39:43','کیا انہوں نے پکڑے ہیں اللہ کے سوائے کوئی سفارش والے تو کہہ اگرچہ ان کو اختیار نہ ہو کسی چیز کا اور نہ سمجھ ');
INSERT INTO "al_hind" VALUES(39,44,'39:44','تو کہہ اللہ کے اختیار میں ہے ساری سفارش اسی کا راج ہے آسمان اور زمین میں پھر اسی کی طرف پھیرے جاؤ گے ');
INSERT INTO "al_hind" VALUES(39,45,'39:45','اور جب نام لیجئے خالص اللہ کا رک جاتے ہیں دل انکے جو یقین نہیں رکھتے پچھلے گھر کا اور جب نام لیجئے اس کے سوا اوروں کا تب وہ لگیں خوشیاں کرنے ');
INSERT INTO "al_hind" VALUES(39,46,'39:46','تو کہہ اے اللہ پیدا کرنے والے آسمانوں کے اور زمین کے جاننے والے چھپے اور کھلے کے تو ہی فیصلہ کرے اپنے بندوں میں جس چیز میں وہ جھگڑ رہے تھے ');
INSERT INTO "al_hind" VALUES(39,47,'39:47','اور اگر گنہگاروں کے پاس ہو جتنا کچھ کہ زمین میں ہے سارا اور اتنا ہی اور اسکے ساتھ تو سب دے ڈالیں اپنے چھڑوانے میں بری طرح کے عذاب سے دن قیامت کے اور نظر آئے انکو اللہ کی طرف سے جو خیال بھی نہ رکھتے تھے');
INSERT INTO "al_hind" VALUES(39,48,'39:48','اور نظر آئیں ان کو برے کام اپنے جو کماتے تھے اور الٹ پڑے ان پر وہ چیز جس پر ٹھٹھا کرتے تھے ');
INSERT INTO "al_hind" VALUES(39,49,'39:49','سو جب آ لگتی ہے آدمی کو کچھ تکلیف ہم کو پکارنے لگتا ہے پھر جب ہم بخشیں اسکو اپنی طرف سے کوئی نعمت کہتا ہے یہ تو مجھ کو ملی کہ پہلے سے معلوم تھی کوئی نہیں یہ جانچ ہے پر وہ بہت سے لوگ نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(39,50,'39:50','کہہ چکے ہیں یہ بات ان سے اگلے پھر کچھ کام نہ آیا انکو جو کماتے تھے');
INSERT INTO "al_hind" VALUES(39,51,'39:51','پھر پڑ گئیں ان پربرائیاں جو کمائی تھیں اور جو گنہگار ہیں ان میں سے ان پر بھی اب پڑتی ہیں برائیاں جو کمائی ہیں اور وہ نہیں تھکانے والے ');
INSERT INTO "al_hind" VALUES(39,52,'39:52','اور کیا نہیں جان چکے کہ اللہ پھیلاتا ہے روزی جس کے واسطے چاہے اور ماپ کر دیتا ہے البتہ اس میں پتے ہیں ان لوگوں کے واسطے جو مانتے ہیں ');
INSERT INTO "al_hind" VALUES(39,53,'39:53','کہہ دے اے بندو میرے جنہوں نے کہ زیادتی کہ ہے اپنی جان پر آس مت توڑو اللہ کی مہربانی سے بیشک اللہ بخشتا ہے سب گناہ وہ جو ہے وہی ہے گناہ معاف کرنے والا مہربان ');
INSERT INTO "al_hind" VALUES(39,54,'39:54','اور رجوع ہو جاؤ اپنے رب کی طرف اور اسکی حکمبرداری کرو پہلے اس سے کہ آئے تم پر عذاب پھر کوئی تمہاری مدد کو نہ آئے گا ');
INSERT INTO "al_hind" VALUES(39,55,'39:55','اور چلو بہتر بات پر جو اتری تمہاری طرف تمہارے رب سے پہلے اس سے کہ پہنچے تم پر عذاب اچانک اور تمکو خبر نہ ہو ');
INSERT INTO "al_hind" VALUES(39,56,'39:56','کہیں کہنے لگے کوئی جی اے افسوس اس بات پر کہ میں کوتاہی کرتا رہا اللہ کی طرف سے اور میں تو ہنستا ہی رہا ');
INSERT INTO "al_hind" VALUES(39,57,'39:57','یا کہنے لگے اگر اللہ مجھ کو راہ دکھاتا تو میں ہوتا ڈرنے والوں میں ');
INSERT INTO "al_hind" VALUES(39,58,'39:58','یا کہنے لگے جب دیکھے عذاب کو کسی طرح مجھ کو پھر جانا ملے تو میں ہو جاؤں نیکی والوں میں ');
INSERT INTO "al_hind" VALUES(39,59,'39:59','کیوں نہیں پہنچ چکے تھے تیرے پاس میرے حکم پھر تو نے انکو جھٹلایا اور غرور کیا اور تو تھا منکروں میں ');
INSERT INTO "al_hind" VALUES(39,60,'39:60','اور قیامت کے دن تو دیکھے انکو جو جھوٹ بولتے ہیں اللہ پر کہ ان کے منہ ہوں سیاہ کیا نہیں دوزخ میں ٹھکانہ غرور والوں کا ');
INSERT INTO "al_hind" VALUES(39,61,'39:61','اور بچائے گا اللہ انکو جو ڈرتے رہے انکے بچاؤ کی جگہ نہ لگے انکو برائی اور نہ وہ غمگین ہوں ');
INSERT INTO "al_hind" VALUES(39,62,'39:62','اللہ بنانے والا ہے ہر چیز کا اور وہ ہرچیز کا ذمہ لیتا ہے');
INSERT INTO "al_hind" VALUES(39,63,'39:63','اسی کے پاس ہیں کنجیاں آسمانوں کی اور زمین کی اور جو منکر ہوئے ہیں اللہ کی باتوں سے وہ لوگ جو ہیں وہی ہیں ٹوٹے میں پڑے ');
INSERT INTO "al_hind" VALUES(39,64,'39:64','تو کہہ اب اللہ کے سوائے کسی کو بتلاتے ہو کہ پوجوں اے نادانو ');
INSERT INTO "al_hind" VALUES(39,65,'39:65','اور حکم ہو چکا ہے تجھ کو اور تجھ سے اگلوں کو کہ اگر تو نے شریک مان لیا تو اکارت جائیں گے تیرے عمل اور تو ہو گا ٹوٹے میں پڑا');
INSERT INTO "al_hind" VALUES(39,66,'39:66','نہیں بلکہ اللہ ہی کو پوج اور رہ حق ماننے والوں میں ');
INSERT INTO "al_hind" VALUES(39,67,'39:67','اور نہیں سمجھے اللہ کو جتنا کچھ وہ ہے اور زمین ساری ایک مٹھی ہے اسکی دن قیامت کے اور آسمان لپٹے ہوئے ہوں اسکے داہنے ہاتھ میں وہ پاک ہے اور بہت اوپر ہے اس سے کہ شریک بتلاتے ہیں ');
INSERT INTO "al_hind" VALUES(39,68,'39:68','اور پھونکا جائے صور میں پھر بیہوش ہو جائے جو کوئی ہے آسمانوں میں اور زمین میں مگر جس کو اللہ چاہے پھر پھونکی جائے دوسری بار تو فورًا وہ کھڑے ہو جائیں ہر طرف دیکھتے ');
INSERT INTO "al_hind" VALUES(39,69,'39:69','اور چمکے زمین اپنے رب کے نور سے اور لا دھریں دفتر اور حاضر آئیں پیغمبر اور گواہ اور فیصلہ ہو ان میں انصاف سے اور ان پر ظلم نہ ہو گا ');
INSERT INTO "al_hind" VALUES(39,70,'39:70','اور پورا ملے ہر جی کو جو اس نے کیا اور اسکو خوب خبر ہے جو کچھ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(39,71,'39:71','اور ہانکے جائیں جو منکر تھے دوزخ کی طرف گروہ گروہ یہاں تک کہ جب پہنچ جائیں اس پر کھولے جائیں اسکے دروازے اور کہنے لگیں انکو اسکے داروغہ کیا نہ پہنچے تھے تمہارے پاس رسول تم میں کے پڑھتے تھے تم پرباتیں تمہارے رب کی اور ڈراتے تھے تم کو اس تمہارے دن کی ملاقات سے بولیں کیوں نہیں پر ثابت ہوا حکم عذاب کا منکروں پر ');
INSERT INTO "al_hind" VALUES(39,72,'39:72','حکم ہووے گا کہ داخل ہو جاؤ دروازوں میں دوزخ کے سدا رہنے کو اس میں سو کیا بری جگہ ہے رہنے کی غرور والوں کو ');
INSERT INTO "al_hind" VALUES(39,73,'39:73','اور ہانکے جائیں وہ لوگ جو ڈرتے رہے تھے اپنے رب سے جنت کو گروہ گروہ یہاں تک کہ جب پہنچ جائیں اس پر اور کھولے جائیں اس کے دروازے اور کہنے لگیں ان کو داروغہ اس کے سلام پہنچے تم پر تم لوگ پاکیزہ ہو سو داخل ہو جاؤ اس میں سدا رہنے کو ');
INSERT INTO "al_hind" VALUES(39,74,'39:74','اور وہ بولیں شکر اللہ کا جس نے سچ کیا ہم سے اپنا وعدہ اور وارث کیا ہمکو اس زمین کا گھر لے لیویں بہشت میں سے جہاں چاہیں سو کیا خوب بدلا ہے محنت کرنے والوں کا');
INSERT INTO "al_hind" VALUES(39,75,'39:75','اور تو دیکھے فرشتوں کو گھر رہے ہیں عرش کے گرد پاکی بولتے ہیں اپنے رب کی خوبیاں اور فیصلہ ہوتا ہے ان میں انصاف کا اور یہی بات کہتے ہیں کہ سب خوبی ہے اللہ کو جو رب ہے سارے جہان کا ');
INSERT INTO "al_hind" VALUES(40,1,'40:1','حٰم۔');
INSERT INTO "al_hind" VALUES(40,2,'40:2','اتارنا کتاب کا اللہ سے ہے جو زبردست ہے خبردار');
INSERT INTO "al_hind" VALUES(40,3,'40:3','گناہ بخشنے والا اور توبہ قبول کرنے والا سخت عذاب دینے والا مقدور والا کسی کی بندگی نہیں سوائے اسکے اسی کی طرف پھر جانا ہے ');
INSERT INTO "al_hind" VALUES(40,4,'40:4','وہی جھگڑتے ہیں اللہ کی باتوں میں جو منکر ہیں سو تجھ کو دھوکا نہ دے یہ بات کہ وہ چلتے پھرتے ہیں شہروں میں ');
INSERT INTO "al_hind" VALUES(40,5,'40:5','جھٹلا چکے ہیں ان سے پہلے قوم نوح کی اور کتنے فرقے ان سے پیچھے اور ارادہ کیا ہر امت نے اپنے رسول پر کہ اس کو پکڑ لیں اور لانے لگے جھوٹے جھگڑے کہ اس سے ڈگا دیں سچے دین کو پھر میں نے انکو پکڑ لیا کہو پھر کیسا ہوا میرا سزا دینا ');
INSERT INTO "al_hind" VALUES(40,6,'40:6','اور اسی طرح ٹھیک ہو چکی بات تیرے رب کی منکروں پر کہ یہ ہیں دوزخ والے ');
INSERT INTO "al_hind" VALUES(40,7,'40:7','جو لوگ اٹھا رہے ہیں عرش کو اور جو اس کے گرد ہیں پاکی بولتے ہیں اپنے رب کی خوبیاں اور اس پر یقین رکھتے ہیں اور گناہ بخشواتے ہیں ایمان والوں کے اے پروردگار ہمارے ہر چیز سمائی ہوئی ہے تیری بخشش اور خبر میں سو معاف کر انکو جو توبہ کریں اور چلیں تیری راہ پر اور بچا اُنکو آگ کے عذاب سے ');
INSERT INTO "al_hind" VALUES(40,8,'40:8','اے رب ہمارے اور داخل کر انکو سدا بسنے کے باغوں میں جنکا وعدہ کیا تو نے ان سے اور جو کوئی نیک ہو انکے باپوں میں اور عورتوں میں اور اولاد میں بیشک تو ہی ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(40,9,'40:9','اور بچا انکو برائیوں سے اور جس کو تو بچائے برائیوں سے اس دن اس پر مہربانی کی تو نے اور یہ جو ہے یہی ہے بڑی مراد پانی ');
INSERT INTO "al_hind" VALUES(40,10,'40:10','جو لوگ منکر ہیں انکو پکار کر کہیں گے اللہ بیزار ہوتا تھا زیادہ اس سے جو تم بیزار ہوئے ہو اپنے جی سے جس وقت تمکو بلاتے تھے یقین لانے کو پھر تم منکر ہوتے تھے ');
INSERT INTO "al_hind" VALUES(40,11,'40:11','بولیں گے اے رب ہمارے تو موت دے چکا ہم کو دوبار اور زندگی دے چکا دو بار اب ہم قائل ہوئے اپنے گناہوں کے پھر اب بھی ہے نکلنے کی کوئی راہ ');
INSERT INTO "al_hind" VALUES(40,12,'40:12','یہ تم پر اس واسطے ہے کہ جب کسی نے پکارا اللہ کو اکیلا تو تم منکر ہوتے اور جب اسکے ساتھ پکارتے شریک کو تو تم یقین لانے لگتے اب حکم وہی ہے جو کرے اللہ سب سے اوپر بڑا ');
INSERT INTO "al_hind" VALUES(40,13,'40:13','وہی ہے تم کو دکھلاتا اپنی نشانیاں اور اتارتا ہے تمہارے واسطے آسمان سے روزی اور سوچ وہی کرے جو رجوع رہتا ہو ');
INSERT INTO "al_hind" VALUES(40,14,'40:14','سو پکارو اللہ کو خالص کر کر اسکے واسطے بندگی اور پڑے برا مانیں منکر ');
INSERT INTO "al_hind" VALUES(40,15,'40:15','وہی ہے اونچے درجوں والا مالک عرش کا اتارتا ہے بھید کی بات اپنے حکم سے جس پر چاہے اپنے بندوں میں تاکہ وہ ڈرائے ملاقات کے دن سے ');
INSERT INTO "al_hind" VALUES(40,16,'40:16','جس دن وہ لوگ نکل کھڑے ہوں گے چھپی نہ رہے گی اللہ پر اُنکی کوئی چیز کس کا راج ہے اُس دن اللہ کا ہے جو اکیلا ہے دباؤ والا ');
INSERT INTO "al_hind" VALUES(40,17,'40:17','آج بدلا ملے گا ہر جی کو جیسا اس نے کمایا بالکل ظلم نہیں آج بیشک اللہ جلد لینے والا ہے حساب');
INSERT INTO "al_hind" VALUES(40,18,'40:18','اور خبر سنا دے اُنکو اس نزدیک آنے والے دِن کی جس وقت دل پہنچیں گے گلوں کو تو وہ دبا رہے ہوں گے کوئی نہیں گنہگاروں کا دوست اور نہ سفارشی کہ جسکی بات مانی جائے ');
INSERT INTO "al_hind" VALUES(40,19,'40:19','وہ جانتا ہے چوری کی نگاہ اور جو کچھ چھپا ہوا ہے سینوں میں');
INSERT INTO "al_hind" VALUES(40,20,'40:20','اور اللہ فیصلہ کرتا ہے انصاف سے اور جنکو پکارتے ہیں اُس کے سوائے نہیں فیصلہ کرتے کچھ بھی بیشک اللہ ہی وہی ہے سننے والا دیکھنے والا ');
INSERT INTO "al_hind" VALUES(40,21,'40:21','کیا وہ پھرے نہیں ملک میں کہ دیکھتے انجام کیسا ہوا اُنکا جو تھے اُن سے پہلے وہ تھے اُن سے سخت زور میں اور نشانیوں میں جو چھوڑ گئے زمین میں پھر اُنکو پکڑا اللہ نے اُنکے گناہوں پر اور نہ ہوا اُنکو اللہ سے کوئی بچانے والا ');
INSERT INTO "al_hind" VALUES(40,22,'40:22','یہ اس لئے کہ اُن کے پاس آتے تھے اُن کے رسول کھلی نشانیاں لیکر پھر منکر ہو گئے تو اُن کو پکڑا اللہ نے بیشک وہ زورآور ہے سخت عذاب دینے والا ');
INSERT INTO "al_hind" VALUES(40,23,'40:23','اور ہم نے بھیجا موسٰی کو اپنی نشانیاں دیکر اور کھلی سند ');
INSERT INTO "al_hind" VALUES(40,24,'40:24','فرعون اور ہامان اور قارون کے پاس پھر کہنے لگے یہ جادوگر ہے جھوٹا ');
INSERT INTO "al_hind" VALUES(40,25,'40:25','پھر جب پہنچا انکے پاس لیکر سچی بات ہمارے پاس سے بولے مار ڈالو بیٹے انکے جو یقین لائے ہیں اسکے ساتھ اور جیتی رکھو انکی عورتیں اور جو داؤ ہے منکروں کا سو غلطی میں ');
INSERT INTO "al_hind" VALUES(40,26,'40:26','اور بولا فرعون مجھکو چھوڑو کہ مار ڈالوں موسٰی کو اور پڑا پکارے اپنے رب کو میں ڈرتا ہوں کہ بگاڑ دے تمہارا دین یا پھیلائے ملک میں خرابی ');
INSERT INTO "al_hind" VALUES(40,27,'40:27','اور کہا موسٰی نے میں پناہ لے چکا ہوں اپنے اور تمہارے رب کی ہر غرور والے سے جو یقین نہ کرے حساب کے دن کا ');
INSERT INTO "al_hind" VALUES(40,28,'40:28','اور بولا ایک مرد ایماندار فرعون کے لوگوں میں جو چھپاتا تھا اپنا ایمان کیا مارے ڈالتے ہو ایک مرد کو اس بات پر کہ کہتا ہے میرا رب اللہ ہے اور لایا تمہارے پاس کھلی نشانیاں تمہارے رب کی اور اگر وہ جھوٹا ہو گا تو اس پر پڑے گا اس کا جھوٹ اور اگر وہ سچا ہو گا تو تم پر پڑے گا کوئی نہ کوئی وعدہ جو تم سے کرتا ہے بیشک اللہ راہ نہیں دیتا اسکو جو ہے بے لحاظ جھوٹا ');
INSERT INTO "al_hind" VALUES(40,29,'40:29','اے میری قوم تمہارا راج ہے چڑھ رہے ہو ملک میں پھرکون مدد کرے گا ہماری اللہ کی آفت سے اگر آ گئ ہم پر بولا فرعون میں تو وہی بات سمجھاتا ہوں تمکو جو سوجھی مجھکو اور وہی راہ بتلاتا ہوں جس میں بھلائی ہے ');
INSERT INTO "al_hind" VALUES(40,30,'40:30','اور کہا اُسی ایمان دار نے اے قوم میری میں ڈرتا ہوں کہ آئے تم پر دن اگلے فرقوں کا سا');
INSERT INTO "al_hind" VALUES(40,31,'40:31','جیسے حال ہوا قوم نوح کا اور عاد اور ثمود کا اور جو لوگ انکے پیچھے ہوئے اور اللہ بے انصافی نہیں چاہتا بندوں پر ');
INSERT INTO "al_hind" VALUES(40,32,'40:32','اور اے قوم میری میں ڈرتا ہوں کہ تم پر آئے دن ہانک پکار کا ');
INSERT INTO "al_hind" VALUES(40,33,'40:33','جس دن بھاگو گے پیٹھ پھیر کر کوئی نہیں تم کو اللہ سے بچانے والا اور جس کو غلطی میں ڈالے اللہ تو کوئی نہیں اسکو سجانے والا ');
INSERT INTO "al_hind" VALUES(40,34,'40:34','اور تمہارے پاس آ چکا ہے یوسف اس سے پہلے کھلی باتیں لے کر پھر تم رہے دھوکے ہی میں ان چیزوں سے جو وہ تمہارے پاس لے کر آیا یہاں تک کہ جب مر گیا لگے کہنے ہرگز نہ بھیجے گا اللہ اسکے بعد کوئی رسول اِسی طرح بھٹکاتا ہے اللہ اُس کو جو ہو بیباک شک کرنے والا');
INSERT INTO "al_hind" VALUES(40,35,'40:35','وہ جو کہ جھگڑتے ہیں اللہ کی باتوں میں بغیر کسی سند کے جو پہنچی ہو اُنکو بڑی بیزاری ہے اللہ کے یہاں اور ایمانداروں کے یہاں اِسی طرح مہر کر دیتا ہے اللہ ہر دل پر غرور کرنے والے سرکش کے ');
INSERT INTO "al_hind" VALUES(40,36,'40:36','اور بولا فرعون کے اے ہامان بنا میرے واسطے ایک اونچا محل شاید میں جا پہنچوں رستوں میں');
INSERT INTO "al_hind" VALUES(40,37,'40:37','رستوں میں آسمانوں کے پھر جھانک کر دیکھوں موسٰی کے معبود کو اور میری اٹکل میں تو وہ جھوٹا ہے اور اسی طرح بھلے دکھلا دیے فرعون کو اسکے برے کام اور روک دیا گیا سیدھی راہ سے اور جو داؤ تھا فرعون کا سو تباہ ہونے کے واسطے ');
INSERT INTO "al_hind" VALUES(40,38,'40:38','اور کہا اسی ایماندار نے اے قوم راہ چلو میری پہنچا دوں تمکو نیکی کی راہ پر ');
INSERT INTO "al_hind" VALUES(40,39,'40:39','اے میری قوم یہ جو زندگی ہے دنیا کی سو کچھ برت لینا ہے اور وہ گھر جو پچھلا ہے وہی ہے جم کر رہنے کا گھر ');
INSERT INTO "al_hind" VALUES(40,40,'40:40','جس نے کی ہے برائی تو وہی بدلا پائے گا اسکی برابر اور جس نے کی ہے بھلائی مرد ہو یا عورت اور وہ یقین رکھتا ہو سو وہ لوگ جائیں گے بہشت میں روزی پائیں گے وہاں بے شمار ');
INSERT INTO "al_hind" VALUES(40,41,'40:41','اور اے قوم مجھکو کیا ہوا ہے بلاتا ہوں تمکو نجات کی طرف اور تم بلاتے ہو مجھکو آگ کی طرف ');
INSERT INTO "al_hind" VALUES(40,42,'40:42','تم بلاتے ہو مجھکو کہ منکر ہو جاؤں اللہ سے اور شریک ٹھہراؤں اُسکا اُسکو جسکی مجھکو خبر نہیں اور میں بلاتا ہوں تمکو اس زبردست گناہ بخشنے والے کی طرف ');
INSERT INTO "al_hind" VALUES(40,43,'40:43','آپ ہی ظاہر ہے کہ جس کی طرف تم مجھکو بلاتے ہو اس کا بلاوا کہیں نہیں دنیا میں اور نہ آخرت میں اور یہ کہ ہم کو پھر جانا ہے اللہ کے پاس اور یہ کہ زیادتی والے وہی ہیں دوزخ کے لوگ ');
INSERT INTO "al_hind" VALUES(40,44,'40:44','سو آگے یاد کرو گے جو میں کہتا ہوں تمکو اور میں سونپتا ہوں اپنا کام اللہ کو بیشک اللہ کی نگاہ میں ہیں سب بندے ');
INSERT INTO "al_hind" VALUES(40,45,'40:45','پھر بچا لیا موسٰی کوا للہ نے برے داؤں سے جو وہ کرتے تھے اور الٹ پڑا فرعون والوں پر بری طرح کا عذاب ');
INSERT INTO "al_hind" VALUES(40,46,'40:46','وہ آگ ہے کہ دکھلا دیتے ہیں اُنکو صبح و شام اور جس دن قائم ہو گی قیامت حکم ہو گا داخل کرو فرعون والوں کو سخت سے سخت عذاب میں');
INSERT INTO "al_hind" VALUES(40,47,'40:47','اور جب آپس میں جھگڑیں گے آگ کے اندر پھر کہیں گے کمزور غرور کرنے والوں کو ہم تھے تمہارے تابع پھر کچھ تم ہم پر سے اٹھا لو گے حصہ آگ کا ');
INSERT INTO "al_hind" VALUES(40,48,'40:48','کہیں گے جو غرور کرتے تھے ہم سبھی پڑے ہوئے ہیں اسمیں بیشک اللہ فیصلہ کر چکا بندوں میں ');
INSERT INTO "al_hind" VALUES(40,49,'40:49','اور کہیں گے جو لوگ پڑے ہیں آگ میں دوزخ کے داروغوں کو مانگو اپنے رب سے کہ ہم پر ہلکا کر دے ایک دن تھوڑا عذاب ');
INSERT INTO "al_hind" VALUES(40,50,'40:50','وہ بولے کیا نہ آتے تھے تمہارے پاس تمہارے رسول کھلی نشانیاں لے کر کہیں گے کیوں نہیں بولے پھر پکارو اور کچھ نہیں کافروں کا پکارنا مگر بھٹکنا ');
INSERT INTO "al_hind" VALUES(40,51,'40:51','ہم مدد کرتے ہیں اپنے رسولوں کی اور ایمان والوں کی دنیا کی زندگانی میں اور جب کھڑے ہوں گے گواہ ');
INSERT INTO "al_hind" VALUES(40,52,'40:52','جس دن کام نہ آئیں گے منکروں کو انکے بہانے اور انکو پھٹکار ہے اور ان کے واسطے برا گھر ');
INSERT INTO "al_hind" VALUES(40,53,'40:53','اور ہم نے دی موسٰی کو راہ کی سوجھ اور وارث کیا بنی اسرائیل کو کتاب کا');
INSERT INTO "al_hind" VALUES(40,54,'40:54','سجھانے اور سمجھانے والے عقل مندوں کو ');
INSERT INTO "al_hind" VALUES(40,55,'40:55','سو تو ٹھہرا رہ بیشک وعدہ اللہ کا ٹھیک ہے اور بخشوا اپنا گناہ اور پاکی بول اپنے رب کی خوبیاں شام کو اور صبح کو ');
INSERT INTO "al_hind" VALUES(40,56,'40:56','جو لوگ جھگڑتے ہیں اللہ کی باتوں میں بغیر کسی سند کے جو پہنچی ہو انکو اور کوئی بات نہیں انکے دلوں میں غرور ہے کہ کبھی نہ پہنچیں گے اُس تک سو تو پناہ مانگ اللہ کی بیشک وہ سنتا دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(40,57,'40:57','البتہ پیدا کرنا آسمانوں اور زمین کا بڑا ہے لوگوں کے بنانے سے لیکن بہت لوگ نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(40,58,'40:58','اور برابر نہیں اندھا اور آنکھوں والا اور نہ ایماندار جو بھلے کام کرتے ہیں اور نہ بدکار تم بہت کم سوچ کرتے ہو ');
INSERT INTO "al_hind" VALUES(40,59,'40:59','تحقیق قیامت آنی ہے اس میں دھوکا نہیں ولیکن بہت لوگ نہیں مانتے');
INSERT INTO "al_hind" VALUES(40,60,'40:60','اور کہتا ہے تمہارا رب مجھ کو پکارو کہ پہنچوں تمہاری پکار کو بیشک جو لوگ تکبر کرتے ہیں میری بندگی سے اب داخل ہوں گے دوزخ میں ذلیل ہو کر ');
INSERT INTO "al_hind" VALUES(40,61,'40:61','اللہ ہے جس نے بنایا تمہارے واسطے رات کو کہ اس میں چین پکڑو اور دن بنایا دیکھنے کا اللہ تو فضل والا ہے لوگوں پر اور لیکن بہت لوگ حق نہیں مانتے ');
INSERT INTO "al_hind" VALUES(40,62,'40:62','اور اللہ ہے تمہارا رب ہر چیز بنانے والا کسی کی بندگی نہیں اسکے سوائے پھر کہاں سے پھرے جاتے ہو ');
INSERT INTO "al_hind" VALUES(40,63,'40:63','اسی طرح پھرے جاتے ہیں جو لوگ کہ اللہ کی باتوں سے منکر ہوتے رہتے ہیں');
INSERT INTO "al_hind" VALUES(40,64,'40:64','اللہ ہے جس نے بنایا تمہارے لئے زمین کو ٹھہرنے کی جگہ اور آسمان کو عمارت اور صورت بنائی تمہاری تو اچھی بنائیں صورتیں تمہاری اور روزی دی تمکو ستھری چیزوں سے وہ اللہ ہے رب تمہارا سو بڑی برکت ہے اللہ کی جو رب ہے سارے جہان کا ');
INSERT INTO "al_hind" VALUES(40,65,'40:65','وہ ہے زندہ رہنے والا کسی کی بندگی نہیں اسکے سوائے سو اس کو پکارو خالص کر کر اس کی بندگی سب خوبی اللہ کو جو رب ہے سارے جہان کا ');
INSERT INTO "al_hind" VALUES(40,66,'40:66','تو کہ مجھکو منع کر دیا کہ پوجوں انکو جنکو تم پکارتے ہو سوائے اللہ کے جب پہنچ چکیں میرے پاس کھلی نشانیاں میرے رب سے اور مجھکو حکم ہوا کہ تابع رہوں جہان کے پروردگار کا ');
INSERT INTO "al_hind" VALUES(40,67,'40:67','وہی ہے جس نے بنایا تم کو خاک سے پھر پانی کی بوند سے پھر خون جمے ہوئے سے پھر تم کو نکالتا ہے بچہ پھر جب تک کہ پہنچو اپنے پورے زور کو پھر جب تک کہ ہو جاؤ بوڑھے اور کوئی تم میں ایسا ہے کہ مر جاتا ہے پہلے اس سے اور جب تک کہ پہنچو لکھے وعدے کو اور تاکہ تم سوچو ');
INSERT INTO "al_hind" VALUES(40,68,'40:68','وہی ہے جو جلاتا ہے اور مارتا ہے پھر جب حکم کرے کسی کام کو تو یہ کہے اسکو کہ ہو جا وہ ہو جاتا ہے ');
INSERT INTO "al_hind" VALUES(40,69,'40:69','تو نے نہ دیکھا ان کو جو جھگڑتے ہیں اللہ کی باتوں میں کہاں سے پھیر جاتے ہیں');
INSERT INTO "al_hind" VALUES(40,70,'40:70','وہ لوگ کہ جنہوں نے جھٹلایا اس کتاب کو اور اس کو کہ بھیجا ہم نے اپنے رسولوں کے ساتھ سو آخر جان لیں گے ');
INSERT INTO "al_hind" VALUES(40,71,'40:71','جب طوق پڑیں انکی گردنوں میں اور زنجیریں بھی گھسیٹے جائیں');
INSERT INTO "al_hind" VALUES(40,72,'40:72','جلتے پانی میں پھر آگ میں انکو جھونک دیں ');
INSERT INTO "al_hind" VALUES(40,73,'40:73','پھر انکو کہیں کہاں گئے جنکو تم شریک بتلایا کرتے تھے');
INSERT INTO "al_hind" VALUES(40,74,'40:74','اللہ کے سوائے بولیں وہ ہم سےچوک گئے کوئی نہیں ہم تو پکارتے نہ تھے پہلے کسی چیز کو اسی طرح بچلاتا ہے اللہ منکروں کو ');
INSERT INTO "al_hind" VALUES(40,75,'40:75','یہ بدلا اس کا جو تم اتراتے پھرتے تھے زمین میں ناحق اور اس کا جو تم اکڑتے تھے ');
INSERT INTO "al_hind" VALUES(40,76,'40:76','داخل ہو جاؤ دروازوں میں دوزخ کے سدا رہنے کو اس میں سو کیا برا ٹھکانہ ہے غرور والوں کا');
INSERT INTO "al_hind" VALUES(40,77,'40:77','سو تو ٹھہرا رہ بیشک وعدہ اللہ کا ٹھیک ہے پھر اگر ہم دکھلا دیں تجھ کو کوئی وعدہ جو ہم ان سے کرتے ہیں یا قبض کر لیں تجھ کو ہر حالت میں ہماری ہی طرف پھر آئیں گے ');
INSERT INTO "al_hind" VALUES(40,78,'40:78','اور ہم نے بھیجے ہیں رسول تجھ سے پہلے بعضے ان میں وہ ہیں کہ سنایا ہم نے تجھکو ان کا احوال اور بعضے ہیں کہ نہیں سنایا اور کسی رسول کو مقدور نہ تھا کہ لے آتا کوئی نشانی مگر اللہ کے حکم سے پھر جب آیا حکم اللہ کا فیصلہ ہو گیا انصاف سے اور ٹوٹے میں پڑے اس جگہ جھوٹے ');
INSERT INTO "al_hind" VALUES(40,79,'40:79','اللہ ہے جس نے بنا دیے تمہارے واسطے چوپائے تاکہ سواری کرو بعضوں پر اور بعضوں کو کھاتے ہو');
INSERT INTO "al_hind" VALUES(40,80,'40:80','اور ان میں تمکو بہت فائدے ہیں اور تاکہ پہنچو ان پر چڑھ کر کسی کام تک جو تمہارے جی میں ہو اور ان پر اور کشتیوں پر لدے پھرتے ہو ');
INSERT INTO "al_hind" VALUES(40,81,'40:81','اور دکھلاتا ہے تم کو اپنی نشانیاں پھر کون کون سی نشانیوں کو اپنے رب کی نہ مانو گے ');
INSERT INTO "al_hind" VALUES(40,82,'40:82','کیا پھرے نہیں وہ ملک میں کہ دیکھ لیتے کیسا انجام ہوا ان سے پہلوں کا وہ تھے ان سے زیادہ اور زور میں سخت اور نشانیوں میں جو چھوڑ گئے ہیں زمین پر پھر کام نہ آیا اُن کے جو وہ کماتے تھے ');
INSERT INTO "al_hind" VALUES(40,83,'40:83','پھر جب پہنچے انکے پاس رسول انکے کھلی نشانیاں لے کر اترانے لگے اس پر جو انکے پاس تھی خبر اور الٹ پڑی ان پر وہ چیز جس پر ٹھٹھا کرتے تھے ');
INSERT INTO "al_hind" VALUES(40,84,'40:84','پھر جب انہوں نے دیکھ لیا ہماری آفت کو بولے ہم یقین لائے اللہ اکیلے پر اور ہم نے چھوڑ دیں وہ چیزیں جنکو شریک بتلاتے تھے ');
INSERT INTO "al_hind" VALUES(40,85,'40:85','پھر نہ ہوا کہ کام آئے انکو یقین لانا ان کا جس وقت دیکھ چکے ہمارا عذاب رسم پڑی ہوئی اللہ کی جو چلی آئی اس کے بندوں میں اور خراب ہوئے اس جگہ منکر ');
INSERT INTO "al_hind" VALUES(41,1,'41:1','حٰم۔');
INSERT INTO "al_hind" VALUES(41,2,'41:2','اتارا ہوا ہے بڑے مہربان رحم والے کی طرف سے ');
INSERT INTO "al_hind" VALUES(41,3,'41:3','ایک کتاب ہے جدی جدی کی ہیں اس کی آیتیں قرآن عربی زبان کا ایک سمجھ والے لوگوں کو ');
INSERT INTO "al_hind" VALUES(41,4,'41:4','سنانے والا خوشخبری اور ڈر پر دھیان میں نہ لائے وہ بہت لوگ سو وہ نہیں سنتے ');
INSERT INTO "al_hind" VALUES(41,5,'41:5','اور کہتے ہیں ہمارے دل غلاف میں ہیں اس بات سے جسکی طرف تو ہم کو بلاتا ہے اور ہمارے کانوں میں بوجھ ہے اور ہمارے اور تیرے بیچ میں پردہ ہے سو تو اپنا کام کر ہم اپنا کام کرتے ہیں ');
INSERT INTO "al_hind" VALUES(41,6,'41:6','تو کہہ میں بھی آدمی ہوں جیسے تم حکم آتا ہے مجھکو کہ تم پر بندگی ایک حاکم کی ہے سو سیدھے رہو اسکی طرف اور اس سے گناہ بخشواؤ اور خرابی ہے شریک کرنے والوں کو');
INSERT INTO "al_hind" VALUES(41,7,'41:7','جو نہیں دیتے زکوٰۃ اور وہ آخرت سے منکر ہیں ');
INSERT INTO "al_hind" VALUES(41,8,'41:8','البتہ جو لوگ یقین لائے اور کئے بھلے کام ان کو ثواب ملنا ہے جو موقوف نہ ہو ');
INSERT INTO "al_hind" VALUES(41,9,'41:9','تو کہہ کیا تم منکر ہو اس سے جس نے بنائی زمین دو دن میں اور برابر کرتے ہو اسکے ساتھ اوروں کو وہ ہے رب جہان کا ');
INSERT INTO "al_hind" VALUES(41,10,'41:10','اور رکھے اس میں بھاری پہاڑ اوپر سے اور برکت رکھی اسکے اندر اور ٹھہرائیں اُسمیں خوراکیں اُسکی چار دن میں پورا ہوا پوچھنے والوں کو ');
INSERT INTO "al_hind" VALUES(41,11,'41:11','پھر چڑھا آسمان کو اور وہ دھواں ہو رہا تھا پھر کہا اسکو اور زمین کو آؤ تم دونوں خوشی سے یا زور سے وہ بولے ہم آئے خوشی سے ');
INSERT INTO "al_hind" VALUES(41,12,'41:12','پھر کر دیے وہ سات آسمان دو دن میں اور اتارا ہر آسمان میں حکم اُس کا اور رونق دی ہم نے سب سے ورلے آسمان کو چراغوں سے اور محفوظ کر دیا یہ سادھا ہوا ہے زبردست خبردار کا ');
INSERT INTO "al_hind" VALUES(41,13,'41:13','پھر اگر وہ ٹلائیں تو تو کہہ میں نے خبر سنادی تمکو ایک سخت عذاب کی جیسے عذاب آیا عاد اور ثمود پر ');
INSERT INTO "al_hind" VALUES(41,14,'41:14','جب آئے انکے پاس رسول آگے سے اور پیچھے سے کہ نہ پوجو کسی کو سوائے اللہ کے کہنے لگے اگر ہمارا رب چاہتا تو بھیجتا فرشتے سو ہم تمہارا لایا ہوا نہیں مانتے ');
INSERT INTO "al_hind" VALUES(41,15,'41:15','سو وہ جو عاد تھے وہ تو غرور کرنے لگے ملک میں ناحق اور کہنے لگے کون ہے ہم سے زیادہ زور میں کیا دیکھتے نہیں کہ اللہ جس نے انکو بنایا وہ زیادہ ہے ان سے زور میں اور تھے ہماری نشانیوں سے منکر ');
INSERT INTO "al_hind" VALUES(41,16,'41:16','پھر بھیجی ہم نے ان پر ہوا بڑے زور کی کئ دن جو مصیبت کے تھے تاکہ چکھائیں انکو رسوائی کا عذاب دنیا کی زندگانی میں اور آخرت کے عذاب میں تو پوری رسوائی ہے اور انکو کہیں مدد نہیں ');
INSERT INTO "al_hind" VALUES(41,17,'41:17','اور وہ جو ثمود تھے سو ہم نے انکو راہ بتلائی پھر انکو خوش لگا اندھا رہنا راہ سوجھنے سے پھر پکڑ انکو کڑک نے ذلت کے عذاب کی بدلا اس کا جو کماتے تھے ');
INSERT INTO "al_hind" VALUES(41,18,'41:18','اور بچا دیا ہم نے ان لوگوں کو جو یقین لائے تھے اور بچ کر چلتے تھے ');
INSERT INTO "al_hind" VALUES(41,19,'41:19','اور جس دن جمع ہوں گے دشمن اللہ کے دوزخ پر تو انکی جماعتیں بنائی جائیں گی ');
INSERT INTO "al_hind" VALUES(41,20,'41:20','یہاں تک کہ جب پہنچیں اس پر بتائیں گے انکو انکے کان اور انکی آنکھیں اور انکے چمڑے جو کچھ وہ کرتے تھے ');
INSERT INTO "al_hind" VALUES(41,21,'41:21','اور وہ کہیں گے اپنے چمڑوں کو تم نے کیوں بتلایا ہمکو وہ بولیں گے ہمکو بلوایا اللہ نے جس نے بلوایا ہے ہر چیز کو اور اسی نے بنایا تمکو پہلی بار اور اسی کی طرف پھیرے جاتے ہو ');
INSERT INTO "al_hind" VALUES(41,22,'41:22','اور تم پردہ نہ کرتے تھے اس بات سے کہ تم کو بتلائیں گے تمہارے کان اور نہ تمہاری آنکھیں اور نہ تمہارے چمڑے پر تم کو یہ خیال تھا کہ اللہ نہیں جانتا بہت چیزیں جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(41,23,'41:23','اور یہ وہی تمہارا خیال ہے جو تم رکھتے تھے اپنے رب کے حق میں اسی نے تمکو غارت کیا پھر آج رہ گئے ٹوٹے میں');
INSERT INTO "al_hind" VALUES(41,24,'41:24','پھر اگر وہ صبر کریں تو آگ ان کا گھر ہے اور اگر وہ منایا چاہیں تو انکو کوئی نہیں مانتا ');
INSERT INTO "al_hind" VALUES(41,25,'41:25','اور لگا دیے ہم نے انکے پیچھے ساتھ رہنے والے پھر انہوں نے خوبصورت بنادیا انکی آنکھوں میں اسکو جو انکے آگے ہو اور جو انکے پیچھے ہو اور ٹھیک پڑ چکی ان پر عذاب کی بات ان فرقوں کے ساتھ جو گذر چکے ان سے پہلے جنوں کے اور آدمیوں کے بیشک وہ تھے ٹوٹے والے ');
INSERT INTO "al_hind" VALUES(41,26,'41:26','اور کہنے لگے منکر مت کان دھرو اس قرآن کے سننے کو اور بک بک کرو اسکے پڑھنے میں شاید تم غالب ہو ');
INSERT INTO "al_hind" VALUES(41,27,'41:27','سو ہم کو ضرور چکھانا ہے منکروں کو سخت عذاب اور ان کو بدلا دینا ہے برے سے کاموں کا جو وہ کرتے تھے ');
INSERT INTO "al_hind" VALUES(41,28,'41:28','یہ سزا ہے اللہ کے دشمنوں کی آگ اُن کا اسی میں گھر ہے سدا کو بدلا اس کا جو ہماری باتوں سے انکار کرتے تھے ');
INSERT INTO "al_hind" VALUES(41,29,'41:29','اور کہیں گے وہ لوگ جو منکر ہیں اے ہمارے رب ہمکو دکھلا دے وہ دونوں جنہوں نے ہم کو بہکایا جو جن ہے اور جو آدمی کہ ہم ڈالیں انکو اپنے پاؤں کے نیچے کہ وہ رہیں سب سے نیچے ');
INSERT INTO "al_hind" VALUES(41,30,'41:30','تحقیق جنہوں نے کہا رب ہمارا اللہ ہے پھر اس پر قائم رہے ان پر اترتے ہیں فرشتے کہ تم مت ڈرو اور نہ غم کھاؤ اور خوشخبری سنو اس بہشت کی جس کا تم سے وعدہ تھا ');
INSERT INTO "al_hind" VALUES(41,31,'41:31','ہم ہیں تمہارے رفیق دنیا میں اور آخرت میں اور تمہارے لئے وہاں ہے جو چاہے جی تمہارا اور تمہارے لئے وہاں ہے جو کچھ مانگو ');
INSERT INTO "al_hind" VALUES(41,32,'41:32','مہمانی ہے اس بخشنے والے مہربان کی طرف سے ');
INSERT INTO "al_hind" VALUES(41,33,'41:33','اور اس سے بہتر کس کی بات جس نے بلایا اللہ کی طرف اور کیا نیک کام اور کہا میں حکمبردار ہوں ');
INSERT INTO "al_hind" VALUES(41,34,'41:34','اور برابر نہیں نیکی اور نہ بدی جواب میں وہ کہہ جو اس سے بہتر ہو پھر تو دیکھ لے کہ تجھ میں اور جس میں دشمنی تھی گویا دوستدار ہے قرابت والا ');
INSERT INTO "al_hind" VALUES(41,35,'41:35','اور یہ بات ملتی ہے انہی کو جو سہارے رکھتے ہیں اور یہ بات ملتی ہے اسی کو جس کی بڑی قسمت ہے ');
INSERT INTO "al_hind" VALUES(41,36,'41:36','اور جو کبھی چوک لگے تجھ کو شیطان کے چوک لگانے سے تو پناہ پکڑ اللہ کی بیشک وہی ہے سننے والا جاننے والا ');
INSERT INTO "al_hind" VALUES(41,37,'41:37','اور اسکی قدرت کے نمونے ہیں رات اور دن اور سورج اور چاند سجدہ نہ کرو سورج کو اور نہ چاند کو اور سجدہ کرو اللہ کو جس نے ان کو بنایا اگر تم اسی کو پوجتے ہو ');
INSERT INTO "al_hind" VALUES(41,38,'41:38','پھر اگر غرور کریں تو جو لوگ تیرے رب کے پاس ہیں پاکی بولتے رہتے ہیں اسکی رات اور دن اور وہ نہیں تھکتے ');
INSERT INTO "al_hind" VALUES(41,39,'41:39','اور ایک اس کی نشانی یہ کہ تو دیکھتا ہے زمین کو دبی پڑی پھر جب اتارا ہم نے اس پر پانی تازی ہوئی اور ابھری بیشک جس نے اس کو زندہ کیا وہ زندہ کرے گا مردوں کو وہ سب کچھ کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(41,40,'41:40','جو لوگ ٹیڑھے چلتے ہیں ہماری باتوں میں وہ ہم سے چھپے ہوئے نہیں بھلا ایک جو پڑتا ہے آگ میں وہ بہتر یا ایک جو آئے گا امن سے دن قیامت کے کئے جاؤ جو چاہو بیشک جو تم کرتے ہو وہ دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(41,41,'41:41','جو لوگ منکر ہوئے نصیحت سے جب آئی ان کے پاس اور وہ کتاب ہے نادر');
INSERT INTO "al_hind" VALUES(41,42,'41:42','اُس پر جھوٹ کا دخل نہیں آگے سے اور نہ پیچھے سے اتاری ہوئی ہے حکمتوں والے سب تعریفوں والے کی ');
INSERT INTO "al_hind" VALUES(41,43,'41:43','تجھے وہی کہتے ہیں جو کہہ چکےہیں سب رسولوں سے تجھ سے پہلے تیرے رب کے یہاں معافی بھی ہے اور سزا بھی ہے دردناک ');
INSERT INTO "al_hind" VALUES(41,44,'41:44','اور اگر ہم اُسکو کرتے قرآن اوپری زبان کا تو کہتے اس کی باتیں کیوں نہ کھولی گئیں کیا اوپری زبان کی کتاب اور عربی لوگ تو کہہ یہ ایمان والوں کے لئے سوجھ ہے اور روگ کا دور کرنے والا اور جو یقین نہیں لاتے ان کے کانوں میں بوجھ ہے اور یہ قرآن انکے حق میں اندھاپا ہے انکو پکارتے ہیں دور کی جگہ سے ');
INSERT INTO "al_hind" VALUES(41,45,'41:45','اور ہم نے دی تھی موسٰی کو کتاب پھر اس میں اختلاف پڑا اور اگر نہ ہوتی ایک بات جو پہلے نکل چکی تیرے رب کی طرف سے تو ان میں فیصلہ ہو جاتا اور وہ ایسے دھوکے میں ہیں اس قرآن سے جو چین نہیں لینے دیتا ');
INSERT INTO "al_hind" VALUES(41,46,'41:46','جس نے کی بھلائی سو اپنے واسطے اور جس نے کی برائی سو وہ بھی اسی پر اور تیرا رب ایسا نہیں کہ ظلم کرے بندوں پر ');
INSERT INTO "al_hind" VALUES(41,47,'41:47','اُسی کی طرف حوالہ ہے قیامت کی خبر کا اور نہیں نکلتے کوئی میوے اپنے غلاف سے اور نہیں رہتا حمل کسی مادہ کو اور نہ وہ جنے کہ جسکی اسکو خبر نہیں اور جس دن انکو پکارے گا کہاں ہیں میرےشریک بولیں گے ہم نے تجھ کو کہہ سنایا ہم میں کوئی اس کا اقرار نہیں کرتا ');
INSERT INTO "al_hind" VALUES(41,48,'41:48','اور چوک گیا ان سے جو پکارتے تھے پہلے اور سمجھ گئے کہ انکو کہیں نہیں خلاصی ');
INSERT INTO "al_hind" VALUES(41,49,'41:49','نہیں تھکتا آدمی مانگنے سے بھلائی اور اگر لگ جائے اس کو برائی تو آس توڑ بیٹھے ناامید ہو کر');
INSERT INTO "al_hind" VALUES(41,50,'41:50','اور اگر ہم چکھائیں اسکو کچھ اپنی مہربانی پیچھے ایک تکلیف کے جو اسکو پہنچی تھی تو کہنے لگے یہ ہے میرے لائق اور میں نہیں سمجھتا کہ قیامت آنے والی ہے اور اگر میں پھر بھی گیا اپنے رب کی طرف بیشک میرے لئے ہے اسکے پاس خوبی سو ہم جتلا دیں گے منکروں کو جو انہوں نے کیا ہے اور چکھائیں گے انکو ایک گاڑھا عذاب ');
INSERT INTO "al_hind" VALUES(41,51,'41:51','اور جب ہم نعمتیں بھیجیں انسان پر تو ٹلا جائے اور موڑ لے اپنی کروٹ اور جب لگے اسکو برائی تو دعائیں کرے چوڑی ');
INSERT INTO "al_hind" VALUES(41,52,'41:52','تو کہہ بھلا دیکھو تو اگر یہ ہو اللہ کے پاس سے پھر تم نے اس کو نہ مانا پھر اس سے گمراہ زیادہ کون جو دور چلا جائے مخالف ہو کر ');
INSERT INTO "al_hind" VALUES(41,53,'41:53','اب ہم دکھلائیں گے انکو اپنے نمونے دنیا میں اور خود انکی جانوں میں یہاں تک کہ کھل جائے ان پر کہ یہ ٹھیک ہے کیا تیرا رب تھوڑا ہے ہر چیز پر گواہ ہونے کے لئے ');
INSERT INTO "al_hind" VALUES(41,54,'41:54','سنتا ہے وہ دھوکے میں ہیں اپنے رب کی ملاقات سے سنتا ہے وہ گھیر رہا ہے ہر چیز کو ');
INSERT INTO "al_hind" VALUES(42,1,'42:1','حٰم۔');
INSERT INTO "al_hind" VALUES(42,2,'42:2','عٓسٓقٓ۔');
INSERT INTO "al_hind" VALUES(42,3,'42:3','اسی طرح وحی بھیجتا ہے تیری طرف اور تجھ سے پہلوں کی طرف اللہ زبردست حکمتوں والا');
INSERT INTO "al_hind" VALUES(42,4,'42:4','اسی کا ہے جو کچھ ہے آسمانوں میں اور زمین میں اور وہی ہے سب سے اوپر بڑا ');
INSERT INTO "al_hind" VALUES(42,5,'42:5','قریب ہے کہ پھٹ پڑیں آسمان اوپر سے اور فرشتے پاکی بولتے ہیں خوبیاں اپنے رب کی اور گناہ بخشواتے ہیں زمین والوں کے سنتا ہے وہی ہے معاف کرنے والا مہربان ');
INSERT INTO "al_hind" VALUES(42,6,'42:6','اور جنہوں نے پکڑے ہیں اس کے سوائے رفیق اللہ کو وہ سب یاد ہیں اور تجھ پر نہیں ان کا ذمہ ');
INSERT INTO "al_hind" VALUES(42,7,'42:7','اور اسی طرح اتارا ہم نے تجھ پر قرآن عربی زبان کا کہ تو ڈر سنا دے بڑے گاؤں کو اور آس پاس والوں کو اور خبر سنا دے جمع ہونے کے دن کی اس میں دھوکا نہیں ایک فرقہ بہشت میں اور ایک فرقہ آگ میں ');
INSERT INTO "al_hind" VALUES(42,8,'42:8','اور اگر چاہتا اللہ تو سب لوگوں کو کرتا ایک ہی فرقہ و لیکن وہ داخل کرتا ہے جس کو چاہے اپنی رحمت میں اور گنہگار جو ہیں ان کا کوئی نہیں رفیق اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(42,9,'42:9','کیا انہوں نے پکڑے ہیں اُس سے ورے کام بنانے والے سو اللہ جو ہے وہ ہی ہے کام بنانے والا اور وہی جِلاتا ہے مُردوں کو اور وہ ہر چیز کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(42,10,'42:10','اور جس بات میں جھگڑا کرتے ہو تم لوگ کوئی چیز ہو اس کا فیصلہ ہے اللہ کے حوالے وہ اللہ ہے رب میرا اسی پر ہے مجھکو بھروسہ اور اسی کی طرف میری رجوع ہے ');
INSERT INTO "al_hind" VALUES(42,11,'42:11','بنا نکالنے والا آسمانوں کا اور زمین کا بنا دیے تمہارے واسطے تمہی میں سے جوڑے اور چوپایوں میں سے جوڑے بکھیرتا ہے تمکو اسی طرح نہیں ہے اس کی طرح کا سا کوئی اور وہی ہے سننے والا دیکھنے والا ');
INSERT INTO "al_hind" VALUES(42,12,'42:12','اسی کے پاس ہیں کنجیاں آسمانوں کی اور زمین کی پھیلا دیتا ہے روزی جسکے واسطے چاہے اور ماپ کر دیتا ہے وہ ہر چیز کی خبر رکھتا ہے ');
INSERT INTO "al_hind" VALUES(42,13,'42:13','راہ ڈالدی تمہارے لئے دین میں وہی جس کا حکم کیا تھا نوح کو اور جس کا حکم بھیجا ہم نے تیری طرف اور جس کا حکم کیا ہم نے ابراہیم کو اورموسٰی کو اور عیسٰی کو یہ کہ قائم رکھو دین کو اور اختلاف نہ ڈالو اس میں بھاری ہے شرک کرنے والوں کو وہ چیز جسکی طرف تو انکو بلاتا ہے اللہ چن لیتا ہے اپنی طرف سے جسکو چاہے اور راہ دیتا ہے اپنی طرف اس کو جو رجوع لائے ');
INSERT INTO "al_hind" VALUES(42,14,'42:14','اور جنہوں نے اختلاف ڈالا سو سمجھ آ چکنے کے بعد آپس کی ضد سے اور اگر نہ ہوتی ایک بات جو نکلی ہے تیرے رب سے ایک مقررہ وعدہ تک تو فیصلہ ہو جاتا ان میں اور جنکو ملی ہے کتاب ان کے پیچھے وہ البتہ اسکے دھوکے میں ہیں جو چین نہیں آنے دیتا ');
INSERT INTO "al_hind" VALUES(42,15,'42:15','سو تو اُسی طرف بلا اور قائم رہ جیسا کہ فرما دیا ہے تجھکو اور مت چل انکی خواہشوں پر اور کہہ میں یقین لایا ہر کتاب پر جو اتاری اللہ نے اور مجھ کو حکم ہے کہ انصاف کروں تمہارے بیچ میں اللہ رب ہے ہمارا اور تمہارا ہم کو ملیں گے ہمارے کام اور تمکو تمہارے کام کچھ جھگڑا نہیں ہم میں اور تم میں اللہ اکٹھا کرے گا ہم سب کو اور اسی کی طرف پھر جانا ہے ');
INSERT INTO "al_hind" VALUES(42,16,'42:16','اور جو لوگ جھگڑا ڈالتے ہیں اللہ کی بات میں جب لوگ اس کو مان چکے ان کا جھگڑا باطل ہے ان کے رب کے یہاں پر اور ان پر غصہ ہے اور انکو سخت عذاب ہے ');
INSERT INTO "al_hind" VALUES(42,17,'42:17','اللہ وہی ہے جس نے اتاری کتاب سچے دین پر اور ترازو بھی اور تجھ کو کیا خبر ہے شاید وہ گھڑی پاس ہو ');
INSERT INTO "al_hind" VALUES(42,18,'42:18','جلدی کرتےہیں اس گھڑی کی وہ لوگ کہ یقین نہیں رکھتے اس پر اور جو یقین رکھتے ہیں انکو اس کا ڈر ہے اور جانتے ہیں کہ وہ ٹھیک ہے سنتا ہے جو لوگ جھگڑتے ہیں اس گھڑی کے آنے میں وہ بہک کر دور جا پڑے ');
INSERT INTO "al_hind" VALUES(42,19,'42:19','اللہ نرمی رکھتا ہے اپنے بندوں پر روزی دیتا ہے جس کو چاہے اور وہی ہے زورآور زبردست ');
INSERT INTO "al_hind" VALUES(42,20,'42:20','جو کوئی چاہتا ہو آخرت کی کھیتی زیادہ کریں ہم اسکے واسطے اسکی کھیتی اور جو کوئی چاہتا ہو دنیا کی کھیتی اس کو دیویں ہم کچھ اس میں سے اور اسکے لئے نہیں آخرت میں کچھ حصہ ');
INSERT INTO "al_hind" VALUES(42,21,'42:21','کیا ان کے لئے اور شریک ہیں کہ راہ ڈالی ہے انہوں نے انکے واسطے دین کی کہ جس کا حکم نہیں دیا اللہ نے اور اگر نہ مقرر ہو چکی ہوتی ایک بات فیصلہ کی تو فیصلہ ہو جاتا ان میں اور بیشک جو گنہگار ہیں انکو عذاب ہے دردناک ');
INSERT INTO "al_hind" VALUES(42,22,'42:22','تو دیکھے گا گنہگاروں کو کہ ڈرتے ہوں گے اپنی کمائی سے اور وہ پڑ کر رہے گا ان پر اور جو لوگ یقین لائے اور بھلے کام کئے باغوں میں ہیں جنت کے اور ان کے لئے ہے جو وہ چاہیں اپنے رب کے پاس یہی ہے بڑی بزرگی ');
INSERT INTO "al_hind" VALUES(42,23,'42:23','یہ ہے جو خوشخبری دیتا ہے اللہ اپنے ایماندار بندوں کو جو کرتے ہیں بھلے کام تو کہہ میں مانگتا نہیں تم سے اس پر کچھ بدلا مگر دوستی چاہئے قرابت میں اور جو کوئی کمائے گا نیکی ہم اسکو بڑھا دیں گے اس کی خوبی بیشک اللہ معاف کرنے والا حق ماننے والا ہے ');
INSERT INTO "al_hind" VALUES(42,24,'42:24','کیا وہ کہتے ہیں کہ اس نے باندھا اللہ پر جھوٹ سو اگراللہ چاہے مہر کر دے تیرے دل پر اور مٹاتا ہے اللہ جھوٹ کو اور ثابت کرتا ہے سچ کو اپنی باتوں سے اُس کو معلوم ہے جو دلوں میں ہے ');
INSERT INTO "al_hind" VALUES(42,25,'42:25','اور وہی ہے جو قبول کرتا ہے توبہ اپنے بندوں کی اور معاف کرتا ہے برائیاں اور جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "al_hind" VALUES(42,26,'42:26','اور دعا سنتا ہے ایمان والوں کی جو بھلے کام کرتے ہیں اور زیادہ دیتا ہے اُنکو اپنے فضل سے اور جو منکر ہیں ان کے لئے سخت عذاب ہے');
INSERT INTO "al_hind" VALUES(42,27,'42:27','اور اگر پھیلا دے اللہ روزی اپنے بندوں کو تو دھوم اٹھا دیں ملک میں ولیکن اتارتا ہے ماپ کر جتنی چاہتا ہے بیشک وہ اپنے بندوں کی خبر رکھتا ہے دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(42,28,'42:28','اور وہی ہے جو اتارتا ے مینہ بعد اسکے کہ آس توڑ چکے اور پھیلاتا ہے اپنی رحمت اور وہی ہے کام بنانے والا سب تعریفوں کے لائق ');
INSERT INTO "al_hind" VALUES(42,29,'42:29','اور ایک اسکی نشانی ہے بنانا آسمانوں کا اور زمین کا اور جس قدر بکھیرے ہیں ان میں جانور اور وہ جب چاہے اُن سب کو اکٹھا کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(42,30,'42:30','اور جو پڑے تم پر کوئی سختی سو وہ بدلا ہے اس کا جو کمایا تمہارے ہاتھوں نے اور معاف کرتا ہے بہت سے گناہ ');
INSERT INTO "al_hind" VALUES(42,31,'42:31','اور تم تھکا دینے والے نہیں بھاگ کر زمین میں اور کوئی نہیں تمہارا اللہ کے سوائے کام بنانے والا اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(42,32,'42:32','اور ایک اُسکی نشانی ہے کہ جہاز چلتے ہیں دریا میں جیسے پہاڑ ');
INSERT INTO "al_hind" VALUES(42,33,'42:33','اگر چاہے تو تھام دے ہوا کو پھر رہیں سارے دن ٹھہرے ہوئے اس کی پیٹھ پر مقرر اس بات میں پتے ہیں ہر قائم رہنے والے کو جو احسان مانے ');
INSERT INTO "al_hind" VALUES(42,34,'42:34','یا تباہ کر دے انکو بسبب اُنکی کمائی کے اور معاف بھی کرے بہتوں کو ');
INSERT INTO "al_hind" VALUES(42,35,'42:35','اور تاکہ جان لیں وہ جو جھگڑتے ہیں ہماری قدرتوں میں کہ نہیں اُنکے لئے بھاگنے کی جگہ ');
INSERT INTO "al_hind" VALUES(42,36,'42:36','سو جو چکھ ملا ہے تمکو کوئی چیز ہو سو وہ برت لینا ہے دنیا کی زندگانی میں اورجو کچھ اللہ کے یہاں ہے بہتر ہے اور باقی رہنے والا واسطے ایمان والوں کے جو اپنے رب پر بھروسہ رکھتے ہیں ');
INSERT INTO "al_hind" VALUES(42,37,'42:37','اور جو لوگ کہ بچتے ہیں بڑے گناہوں سے اور بیحیائی سے اور جب غصہ آوے تو وہ معاف کر دیتے ہیں ');
INSERT INTO "al_hind" VALUES(42,38,'42:38','اور جنہوں نے کہ حکم مانا اپنے رب کا اور قائم کیا نماز کو اور کام کرتے ہیں مشورہ سے آپس کے اور ہمارا دیا کچھ خرچ کرتے ہیں');
INSERT INTO "al_hind" VALUES(42,39,'42:39','اور وہ لوگ کہ جب ان پر ہووے چڑھائی تو وہ بدلا لیتے ہیں ');
INSERT INTO "al_hind" VALUES(42,40,'42:40','اور برائی کا بدلا ہے برائی ویسی ہی پھر جو کوئی معاف کرے اور صلح کرے سو اس کا ثواب ہے اللہ کے ذمہ بیشک اللہ کو پسند نہیں آتے گنہگار ');
INSERT INTO "al_hind" VALUES(42,41,'42:41','اور جو کوئی بدلہ لے اپنے مظلوم ہونے کے بعد سو ان پر بھی نہیں کچھ الزام ');
INSERT INTO "al_hind" VALUES(42,42,'42:42','الزام تو ان پر ہے جو ظلم کرتے ہیں لوگوں پر اور دھوم اٹھاتے ہیں ملک میں ناحق ان لوگوں کے لئے ہے عذاب دردناک');
INSERT INTO "al_hind" VALUES(42,43,'42:43','اور البتہ جس نے سہا اور معاف کیا بیشک یہ کام ہمت کے ہیں ');
INSERT INTO "al_hind" VALUES(42,44,'42:44','اور جسکو راہ نہ سجھائے اللہ تو کوئی نہیں اسکا کام بنانے والا اسکے سوا اور تو دیکھے گنہگاروں کو جس وقت دیکھیں گے عذاب کہیں گے کسی طرح پھر جانے کی بھی ہو گی کوئی راہ ');
INSERT INTO "al_hind" VALUES(42,45,'42:45','اور تو دیکھے انکو کہ سامنے لائے جائیں آگ کے آنکھیں جھکائے ہوئے ذلت سے دیکھتے ہوں گے چھپی نگاہ سے اور کہیں وہ لوگ جو ایماندار تھے مقرر ٹوٹے والے وہی ہیں جنہوں نے گنوایا اپنی جان کو اور اپنے گھر والوں کو قیامت کے دن سنتا ہے گنہگار پڑے ہیں سدا کے عذاب میں');
INSERT INTO "al_hind" VALUES(42,46,'42:46','اور کوئی نہ ہوئے انکے حمایتی جو مدد کرتے انکی اللہ کے سوائے اور جسکو بھٹکائے اللہ اسکے لئے کہیں نہیں راہ ');
INSERT INTO "al_hind" VALUES(42,47,'42:47','مانو اپنے رب کا حکم اُس سے پہلے کہ آئے وہ دن جسکو پھرنا نہیں اللہ کے یہاں سے نہیں ملے گا تمکو بچاؤ اُس دن اور نہیں تمہاری طرف سے کوئی انکار کرنے والا ');
INSERT INTO "al_hind" VALUES(42,48,'42:48','پھر اگر وہ منہ پھیریں تو تجھ کو نہیں بھیجا ہم نے ان پر نگہبان تیرا ذمہ تو بس یہی ہے پہنچا دینا اور ہم جب چکھاتے ہیں آدمی کو اپنی طرف سے رحمت اس پر پھولا نہیں سماتا اور اگر پہنچتی ہے انکو کچھ برائی بدلے میں اپنی کمائی کے تو انسان بڑا ناشکرا ہے ');
INSERT INTO "al_hind" VALUES(42,49,'42:49','اللہ کا راج ہے آسمانوں میں اور زمین میں پیدا کرتا ہے جو چاہے بخشتا ہے جسکو چاہے بیٹیاں اور بخشتا ہے جسکو چاہے بیٹے۔');
INSERT INTO "al_hind" VALUES(42,50,'42:50','یا اُنکو دیتا ہے جوڑے بیٹے اور بیٹیاں اور کر دیتا ہے جسکو چاہے بانجھ وہ ہی سب کچھ جانتا کر سکتا ');
INSERT INTO "al_hind" VALUES(42,51,'42:51','اور کسی آدمی کی طاقت نہیں کہ اس سے باتیں کرے اللہ مگر اشارہ سے یا پردہ کے پیچھے سے یا بھیجے کوئی پیغما لانے والا پھر پہنچا دے اسکے حکم سے جو ہو چاہے تحقیقی وہ سب سے اوپر ہے حکمتوں والا ');
INSERT INTO "al_hind" VALUES(42,52,'42:52','اور اسی طح بھیجا ہم نے تیری طف ایک فرشتہ اپجے حکم سے تو نہ جانتا تھا کہ کیا ہے کتاب اور نہ ایمان ولیکن ہم نے رکھی ہے یہ روشنی اس سے راہ سجھا دیتے ہیں جسکو چاہیں اپنے بندوں میں اور بیشک تو سجھاتا ہے سیدھی راہ ');
INSERT INTO "al_hind" VALUES(42,53,'42:53','راہ اللہ کی اسی کا ہے جو کچھ ہے آسمانوں میں اور زمین میں سنتا ہے اللہ ہی تک پہنچتے ہیں سب کام ');
INSERT INTO "al_hind" VALUES(43,1,'43:1','حٰمٓ۔');
INSERT INTO "al_hind" VALUES(43,2,'43:2','قسم ہے اس کتاب واضح کی');
INSERT INTO "al_hind" VALUES(43,3,'43:3','ہم نے رکھا اسکو قرآن عربی زبان کا تاکہ تم سمجھو ');
INSERT INTO "al_hind" VALUES(43,4,'43:4','اور تحقیق یہ قرآن لوح محفوظ ہیں ہمارے پاس ہے برتر مستحکم ');
INSERT INTO "al_hind" VALUES(43,5,'43:5','کیا پھیر دیں گے ہم تمہاری طرف سے یہ کتاب موڑ کر اس سبب سے کہ تم ہو ایسے لوگ کہ حد پر نہیں رہتے ');
INSERT INTO "al_hind" VALUES(43,6,'43:6','اور بہت بھیجے ہیں ہم نے نبی پہلوں میں');
INSERT INTO "al_hind" VALUES(43,7,'43:7','اور نہیں آتا لوگوں کے پاس کوئی پیغام لانے والا جس سے ٹھٹھا نہیں کرتے ');
INSERT INTO "al_hind" VALUES(43,8,'43:8','پھر برباد کر ڈالے ہم نے اُن سے سخت زور والے اور چلی آئی ہے مثال پہلوں کی ');
INSERT INTO "al_hind" VALUES(43,9,'43:9','اور اگر تو ان سے پوچھے کس نے بنائے آسمان اور زمین تو کہیں بنائے اس زبردست خبردار نے');
INSERT INTO "al_hind" VALUES(43,10,'43:10','وہی ہے جس نے بنا دیا تمہارے لئے زمین کو بچھونا اور رکھ دیں تمہارے واسطے اُس میں راہیں تاکہ تم راہ پاؤ ');
INSERT INTO "al_hind" VALUES(43,11,'43:11','اور جس نے اتارا آسمان سے پانی ماپ کر پھر ابھار کھڑا کیا ہم نے اس سے ایک دیس مردہ کو اسی طرح تمکو بھی نکالیں گے ');
INSERT INTO "al_hind" VALUES(43,12,'43:12','اور جس نے بنائے سب چیز کے جوڑے اور بنا دیا تمہارے واسطے کشتیوں اور چوپایوں کو جس پر تم سوار ہوتے ہو');
INSERT INTO "al_hind" VALUES(43,13,'43:13','تاکہ چڑھ بیٹھو تم اسکی پیٹھ پر پھر یاد کرو اپنے رب کا احسان جب بیٹھ چکو اُس پر اور کہو پاک ذات ہے وہ جس نے بس میں کر دیا ہمارے اسکو اور ہم نہ تھے اسکو قابو میں لا سکتے ');
INSERT INTO "al_hind" VALUES(43,14,'43:14','اور ہمکو اپنے رب کی طرف پھر جانا ہے ');
INSERT INTO "al_hind" VALUES(43,15,'43:15','اور ٹھہرائی ہے اُنہوں نے حق تعالیٰ کے واسطے اولاد اسکے بندوں میں سے تحقیق انسان بڑا ناشکر ہے صریح');
INSERT INTO "al_hind" VALUES(43,16,'43:16','کیا اس نے رکھ لیں اپنی مخلوقات میں سے بیٹیاں اور تمکو دیدئے چن کر بیٹے ');
INSERT INTO "al_hind" VALUES(43,17,'43:17','اور جب اُن میں کسی کو خوشخبری ملے اس چیز کی جس کو رحمٰن کے نام لگایا تو سارے دن رہے منہ اس کا سیاہ اور وہ دل میں گھٹ رہا ہے ');
INSERT INTO "al_hind" VALUES(43,18,'43:18','کیا ایسا شخص کہ پرورش پاتا ہے زیور میں اور وہ جھگڑے میں بات نہ کہہ سکے ');
INSERT INTO "al_hind" VALUES(43,19,'43:19','اور ٹھہرایا انہوں نے فرشتوں کو جو بندے ہیں رحمٰن کے عورتیں کیا دیکھتے تھے ان کا بننا اب لکھ رکھیں گے اُنکی گواہی اور اُن سے پوچھ ہو گی ');
INSERT INTO "al_hind" VALUES(43,20,'43:20','اور کہتے ہیں اگر چاہتا رحمٰن تو ہم نہ پوجتے انکو کچھ خبر نہیں انکو اس کی یہ سب اٹکلیں دوڑاتے ہیں ');
INSERT INTO "al_hind" VALUES(43,21,'43:21','کیا ہم نے کوئی کتاب دی ہے انکو اس سے پہلے سو انہوں نے اسکو مضبوط پکڑ رکھا ہے');
INSERT INTO "al_hind" VALUES(43,22,'43:22','بلکہ کہتے ہیں ہم نے پایا اپنے باپ دادوں کو ایک راہ پر اور ہم اُنہی کے قدموں پر ہیں راہ پائے ہوئے ');
INSERT INTO "al_hind" VALUES(43,23,'43:23','اور اسی طرح جس کسی کو بھیجا ہم نے تجھ سے پہلے ڈر سنانے والا کسی گاؤں میں سو کہنے لگے وہاں کے خوشحال لوگ ہم نے تو پایا اپنے باپ دادوں کو ایک راہ پراور ہم اُنہی کے قدموں پر چلتے ہیں');
INSERT INTO "al_hind" VALUES(43,24,'43:24','وہ بولا اور جو میں لادوں تمکو اس سے زیادہ سوجھ کی راہ جس پر تم نے پایا اپنے باپ دادوں کو تو یہی کہنے گے ہم تمہارا لایا ہوا نہیں مانیں گے ');
INSERT INTO "al_hind" VALUES(43,25,'43:25','پھر ہم نے اُن سے بدلا لیا سو دیکھ لے کیسا ہوا انجام جھٹلانے والوں کا');
INSERT INTO "al_hind" VALUES(43,26,'43:26','اور جب کہا ابراہیم نے اپنے باپ کو اور اُس کی قوم کو میں الگ ہوں اُن چیزوں سے جنکو تم پوجتے ہو');
INSERT INTO "al_hind" VALUES(43,27,'43:27','مگر جس نے مجھ کو بنایا سو وہ مجھ کو راہ سجھائے گا ');
INSERT INTO "al_hind" VALUES(43,28,'43:28','اور یہی بات پیچھے چھوڑ گیا اپنی اولاد میں تاکہ وہ رجوع رہیں ');
INSERT INTO "al_hind" VALUES(43,29,'43:29','کوئی نہیں پر میں نے برتنے دیا انکو اور اُنکے باپ دادوں کو یہاں تک کہ پہنچا اُنکے پاس دین سچا اور رسول کھول کر سنا دینے والا ');
INSERT INTO "al_hind" VALUES(43,30,'43:30','اور جب پہنچا اُنکے پاس سچا دین کہنے لگے یہ جادو ہے اور ہم اُسکو نہ مانیں گے ');
INSERT INTO "al_hind" VALUES(43,31,'43:31','اور کہتے ہیں کیوں نہ اترا یہ قرآن کسی بڑے مرد پر ان دونوں بستیوں میں کے ');
INSERT INTO "al_hind" VALUES(43,32,'43:32','کیا وہ بانٹتے ہیں تیرے رب کی رحمت کو ہم نے بانٹ دی ہے ان میں روزی اُن کی دنیا کی زندگانی میں اور بلند کر دیے درجے بعض کے بعض پر کہ ٹھہراتا ہے ایک دوسرے کو خدمتگار اور تیرے رب کی رحمت بہتر ہے ان چیزوں سے جو سمیٹتے ہیں ');
INSERT INTO "al_hind" VALUES(43,33,'43:33','اور اگر یہ بات نہ ہوتی کہ سب لوگ ہو جائیں ایک دین پر تو ہم دیتے ان لوگوں کو جو منکر ہیں رحمٰن سے اُنکے گھروں کے واسطے چھت چاندی کی اور سیڑھیاں جن پر چڑھیں');
INSERT INTO "al_hind" VALUES(43,34,'43:34','اور اُن کے گھروں کے واسطے دروازے اور تخت جن پر تکیہ لگا کر بیٹھیں');
INSERT INTO "al_hind" VALUES(43,35,'43:35','اور سونے کے اور یہ سب کچھ نہیں ہے مگر برتنا دنیا کی زندگانی کا اور آخرت تیرے رب کے یہاں اُنہی کیلئے ہیں جو ڈرتے ہیں ');
INSERT INTO "al_hind" VALUES(43,36,'43:36','اور جو کوئی آنکھیں چرائے رحمٰن کی یاد سے ہم اُس پر مقرر کر دیں ایک شیطان پھر وہ رہے اسکا ساتھی ');
INSERT INTO "al_hind" VALUES(43,37,'43:37','اور وہ اُنکو روکتے رہتے ہیں راہ سے اور یہ سمجھتےہیں کہ ہم راہ پر ہیں ');
INSERT INTO "al_hind" VALUES(43,38,'43:38','یہاں تک کہ جب آئے ہمارے پاس کہے کسی طرح مجھ میں اور تجھ میں فرق ہو مشرق مغرب کا سا کہ کیا برا ساتھی ہے ');
INSERT INTO "al_hind" VALUES(43,39,'43:39','اور کچھ فائدہ نہیں تمکو آج کے دن جبکہ تم ظالم ٹھہر چکے اس بات سے کہ تم عذاب میں شامل ہو ');
INSERT INTO "al_hind" VALUES(43,40,'43:40','سو کیا تو سنائے گا بہروں کو یا سمجھائے گا اندھوں کو اور صریح غلطی میں بھٹکتوں کو');
INSERT INTO "al_hind" VALUES(43,41,'43:41','پھر اگر کبھی ہم تجھ کو یہاں سے لیجائیں تو ہمکو ان سے بدلا لینا ہے');
INSERT INTO "al_hind" VALUES(43,42,'43:42','یا تجھ کو دکھا دیں جو اُن سے وعدہ ٹھہرایا ہے تو یہ ہمارے بس میں ہیں');
INSERT INTO "al_hind" VALUES(43,43,'43:43','سو تو مضبوط پکڑ ے رہ اسی کو جو تجھ کو حکم پہنچا تو ہے بیشک سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(43,44,'43:44','اور یہ مذکور رہے گا تیرا اور تیری قوم کا اور آگے تم سے پوچھ ہو گی ');
INSERT INTO "al_hind" VALUES(43,45,'43:45','اور پوچھ دیکھ جو رسول بھیجے ہم نے تجھ سے پہلے کبھی ہم نے رکھے ہیں رحمٰن کے سوائے اور حاکم کہ پوجے جائیں ');
INSERT INTO "al_hind" VALUES(43,46,'43:46','اور ہم نے بھیجا موسٰی کو اپنی نشانیاں دے کر فرعون اور اسکے سرداروں کے پاس تو کہا میں بھیجا ہوا ہوں جہان کے رب کا');
INSERT INTO "al_hind" VALUES(43,47,'43:47','پھر جب لایا انکے پاس ہماری نشانیاں وہ تو لگے ان پر ہنسنے ');
INSERT INTO "al_hind" VALUES(43,48,'43:48','اور جو دکھاتے گئے ہم انکو نشانی سو پہلی سے بڑی اور پکڑا ہم نے اُنکو تکلیف میں تاکہ وہ باز آئیں ');
INSERT INTO "al_hind" VALUES(43,49,'43:49','اور کہنے لگے اے جادوگر پکار ہمارے واسطے اپنے رب کو جیسا سکھلا رکھا ہے تجھ کو ہم ضرور راہ پر آجائیں گے ');
INSERT INTO "al_hind" VALUES(43,50,'43:50','پھر جب اٹھا لی ہم نے ان پر سے تکلیف تبھی وہ وعدہ توڑ ڈالتے ');
INSERT INTO "al_hind" VALUES(43,51,'43:51','اور پکارا فرعون نے اپنی قوم میں بولا اے میری قوم بھلا میرے ہاتھ میں نہیں حکومت مصر کی اور یہ نہریں چل رہی ہیں میرے محل کے نیچے کیا تم نہیں دیکھتے ');
INSERT INTO "al_hind" VALUES(43,52,'43:52','بھلا میں ہوں بھی بہتر اس شخص سے جسکو کچھ عزت نہیں اور صاف نہیں بول سکتا ');
INSERT INTO "al_hind" VALUES(43,53,'43:53','پھر کیوں نہ آ پڑے اس پر کنگن سونے کے یا آتے اسکے ساتھ فرشتے پرا باندھ کر ');
INSERT INTO "al_hind" VALUES(43,54,'43:54','پھر عقل کھو دی اپنی قوم کی پھر اسی کا کہنا مانا مقرر وہ تھے لوگ نافرمان ');
INSERT INTO "al_hind" VALUES(43,55,'43:55','پھر جب ہم کو غصہ دلایا تو ہم نے اُن سے بدلا لیا پھر ڈبو دیا اُن سب کو');
INSERT INTO "al_hind" VALUES(43,56,'43:56','پھر کر ڈالا اُنکو گئے گذرے اور ایک نظیر پچھلوں کے واسطے ');
INSERT INTO "al_hind" VALUES(43,57,'43:57','اور جب مثال لائے مریم کے بیٹے کی تبھی قوم تیری اس سے چلانے لگتے ہیں');
INSERT INTO "al_hind" VALUES(43,58,'43:58','اور کہتے ہیں ہمارے معبود بہتر ہیں یا وہ یہ مثال جو ڈالتے ہیں تجھ پرسو جھگڑنے کو بلکہ یہ لوگ ہیں جھگڑالو');
INSERT INTO "al_hind" VALUES(43,59,'43:59','وہ کیا ہے ایک بندہ ہے کہ ہم نے اس پر فضل کیا اور کھڑا کر دیا اسکو بنی اسرائیل کے واسطے ');
INSERT INTO "al_hind" VALUES(43,60,'43:60','اور اگر ہم چاہیں نکالیں تم میں سے فرشتے رہیں زمین میں تمہاری جگہ ');
INSERT INTO "al_hind" VALUES(43,61,'43:61','اور وہ نشان ہے قیامت کا سو اس میں شک مت کرو اور میرا کہا مانو یہ ایک سیدھی راہ ہے');
INSERT INTO "al_hind" VALUES(43,62,'43:62','اور نہ روک دے تم کو شیطان وہ تو تمہارا دشمن ہے صریح ');
INSERT INTO "al_hind" VALUES(43,63,'43:63','اور جب آیا عیسٰی نشانیاں لے کر بولا میں لایا ہوں تمہارے پاس پکی باتیں اور بتلانے کو بعضی وہ چیز جس میں تم جھگڑتے تھے سو ڈرو اللہ سے اور میرا کہا مانو');
INSERT INTO "al_hind" VALUES(43,64,'43:64','بیشک اللہ جو ہے وہی ہے رب میرا اور رب تمہارا سو اسی کی بندگی کرو یہ ایک سیدھی راہ ہے ');
INSERT INTO "al_hind" VALUES(43,65,'43:65','پھر پھٹ گئے کتنے فرقے انکے بیچ سے سو خرابی ہے گنہگاروں کو آفت سے دکھ والے دن کی');
INSERT INTO "al_hind" VALUES(43,66,'43:66','اب یہی ہے کہ راہ دیکھتے ہیں قیامت کی کہ آ کھڑی ہو ان پر اچانک اور انکو خبر بھی نہ ہو ');
INSERT INTO "al_hind" VALUES(43,67,'43:67','جتنے دوست ہیں اس دن ایک دوسرے کے دشمن ہوں گے مگر جو لوگ ہیں ڈر والے ');
INSERT INTO "al_hind" VALUES(43,68,'43:68','اے بندو میرے نہ ڈر ہے تم پر آج کے دن اور نہ تم غمگین ہو گے ');
INSERT INTO "al_hind" VALUES(43,69,'43:69','جو یقین لائے ہماری باتوں پر اور رہے حکم بردار ');
INSERT INTO "al_hind" VALUES(43,70,'43:70','چلے جاؤ بہشت میں تم اور تمہاری عورتیں کہ تمہاری عزت کریں');
INSERT INTO "al_hind" VALUES(43,71,'43:71','لئے پھریں گے انکے پاس رکابیاں سونے کی اور آبخورے اور وہاں ہے جو دل چاہے اور جس سے آنکھیں آرام پائیں اور تم ان میں ہمیشہ رہو گے');
INSERT INTO "al_hind" VALUES(43,72,'43:72','اور وہی بہشت ہے جو میراث پائی تم نے بدلے میں ان کاموں کے جو کرتے تھے ');
INSERT INTO "al_hind" VALUES(43,73,'43:73','تمہارے واسطے ان میں بہت میوے ہیں ان میں سے کھاتے رہو ');
INSERT INTO "al_hind" VALUES(43,74,'43:74','البتہ جو لوگ گنہگار ہیں وہ دوزخ کے عذاب میں ہمیشہ رہنے والے ہیں');
INSERT INTO "al_hind" VALUES(43,75,'43:75','نہ ہلکا ہوتا ہے ان پر سے اور وہ اسی میں پڑے ہیں آس ٹوٹے ');
INSERT INTO "al_hind" VALUES(43,76,'43:76','اور ہم نے اُن پر ظلم نہیں کیا لیکن تھے وہی بے انصاف ');
INSERT INTO "al_hind" VALUES(43,77,'43:77','اور پکاریں گے اے مالک کہیں ہم پر فیصل کر چکے تیرا رب وہ کہے گا تمکو ہمیشہ رہنا ہے ');
INSERT INTO "al_hind" VALUES(43,78,'43:78','ہم لائے ہیں تمہارے پاس سچا دین پر تم بہت لوگ سچی بات سے برا مانتے ہو ');
INSERT INTO "al_hind" VALUES(43,79,'43:79','کیا انہوں نے ٹھہرائی ہے ایک بات تو ہم بھی کچھ ٹھہرائیں گے ');
INSERT INTO "al_hind" VALUES(43,80,'43:80','کیا خیال رکھتے ہیں کہ ہم نہیں جانتے ان کا بھید اور ان کا مشورہ کیوں نہیں اور ہمارے بھیجے ہوئے انکے پاس لکھتے رہتے ہیں ');
INSERT INTO "al_hind" VALUES(43,81,'43:81','تو کہہ اگر ہو رحمٰن کے واسطے اولاد تو میں سب سے پہلے پوجوں ');
INSERT INTO "al_hind" VALUES(43,82,'43:82','پاک ذات ہے وہ رب آسمانوں کا اور زمین کا صاحب عرش کا ان باتوں سے جو یہ بیان کرتے ہیں ');
INSERT INTO "al_hind" VALUES(43,83,'43:83','اب چھوڑ دے انکو بک بک کریں اور کھیلیں یہاں تک کہ ملیں اپنے اس دن سے جس کا انکو وعدہ دیا ہے ');
INSERT INTO "al_hind" VALUES(43,84,'43:84','اور وہی ہے جس کی بندگی ہے آسمان میں اور اسکی بندگی ہے زمین میں اور وہی ہے حکمت والا سب سے خبردار ');
INSERT INTO "al_hind" VALUES(43,85,'43:85','اور بڑی برکت ہے اسکی جس کا راج ہے آسمانوں میں اور زمین میں اور جو کچھ انکے بیچ میں ہے اور اسی کے پاس ہے خبر قیامت کی اور اسی تک پھر کر پہنچ جاؤ گے ');
INSERT INTO "al_hind" VALUES(43,86,'43:86','اور اختیار نہیں رکھتے وہ لوگ جنکو یہ پکارتے ہیں سفارش کا مگر جس نے گواہی دی سچی اور انکو خبر تھی ');
INSERT INTO "al_hind" VALUES(43,87,'43:87','اور اگر تو ان سے پوچھے کہ انکو کس نے بنایا تو کہیں گے اللہ نے پھر کہاں سے الٹ جاتے ہیں ');
INSERT INTO "al_hind" VALUES(43,88,'43:88','قسم ہے رسول کے اس کہنے کی کہ اے رب یہ لوگ ہیں کہ یقین نہیں لاتے ');
INSERT INTO "al_hind" VALUES(43,89,'43:89','سو تو منہ پھیر لے انکی طرف سے اور کہہ سلام ہے اب آخر کو معلوم کر لیں گے ');
