INSERT INTO "al_hind" VALUES(44,7,'44:7','رب آسمانوں کا اور زمین کا اور جو کچھ انکے بیچ میں ہے اگر تمکو یقین ہے ');
INSERT INTO "al_hind" VALUES(44,8,'44:8','کسی کی بندگی نہیں سوائے اسکے جلاتا ہے اور مارتا ہے رب تمہارا اور رب تمہارے اگلے باپ دادوں کا ');
INSERT INTO "al_hind" VALUES(44,9,'44:9','کوئی نہیں وہ دھوکے میں ہیں کھیلتے ');
