INSERT INTO "al_hind" VALUES(44,17,'44:17','اور جانچ چکے ہیں ہم ان سے پہلے فرعون کی قوم کو اور آیا ان کے پاس رسول عزت والا ');
INSERT INTO "al_hind" VALUES(44,18,'44:18','کہ حوالے کرو میرے بندے خدا کے میں تمہارے پاس آیا ہوں بھیجا ہوا معتبر');
