INSERT INTO "al_hind" VALUES(44,47,'44:47','پکڑو اسکو اور دھکیل کر لیجاؤ بیچوں بیچ دوزخ کے ');
INSERT INTO "al_hind" VALUES(44,48,'44:48','پھر ڈالو اُسکے سر پر جلتے پانی کا عذاب ');
INSERT INTO "al_hind" VALUES(44,49,'44:49','یہ چکھ تو ہی ہے بڑا عزت والا سردار ');
INSERT INTO "al_hind" VALUES(44,50,'44:50','یہ وہی ہے جس میں تم دھوکے میں پڑے تھے ');
INSERT INTO "al_hind" VALUES(44,51,'44:51','بیشک ڈرنے والے گھر میں ہیں چین کے ');
INSERT INTO "al_hind" VALUES(44,52,'44:52','باغوں میں اور چشموں میں');
INSERT INTO "al_hind" VALUES(44,53,'44:53','پہنتے ہیں پوشاک ریشمی پتلی اور گاڑھی ایک دوسرے کے سامنے ');
INSERT INTO "al_hind" VALUES(44,54,'44:54','اسی طرح ہو گا اور بیاہ دیں گے ہم اُنکو حوریں بڑی آنکھوں والیاں ');
INSERT INTO "al_hind" VALUES(44,55,'44:55','منگوائیں گے وہاں ہر میوہ دل جمعی سے ');
INSERT INTO "al_hind" VALUES(44,56,'44:56','نہ چکھیں گے وہاں موت مگر جو پہلے آچکی اور بچایا انکو دوزخ کے عذاب سے');
INSERT INTO "al_hind" VALUES(44,57,'44:57','فضل سے تیرے رب کے یہی ہے بڑی مراد ملنی ');
INSERT INTO "al_hind" VALUES(44,58,'44:58','سو یہ قرآن آسان کیا ہم نے اسکو تیری بولی میں تاکہ وہ یاد رکھیں ');
INSERT INTO "al_hind" VALUES(44,59,'44:59','اب تو راہ دیکھ وہ بھی راہ تکتے ہیں ');
INSERT INTO "al_hind" VALUES(45,1,'45:1','حٰمٓ۔');
INSERT INTO "al_hind" VALUES(45,2,'45:2','اتارنا کتاب کا ہے اللہ کی طرف سے جو زبردست ہے حکمتوں والا');
INSERT INTO "al_hind" VALUES(45,3,'45:3','بیشک آسمانوں میں اور زمین میں بہت نشانیاں ہیں ماننے والوں کے واسطے ');
INSERT INTO "al_hind" VALUES(45,4,'45:4','اور تمہارے بنانے میں اور جس قدر پھیلا رکھے ہیں جانور نشانیاں ہیں ان لوگوں کے واسطے جو یقین رکھتےہیں ');
INSERT INTO "al_hind" VALUES(45,5,'45:5','اور بدلنے میں رات دن کے اور وہ جو اتاری اللہ نے آسمان سے روزی پھر زندہ کر دیا اس سے زمین کو اس کے مرجانے کے بعد اور بدلنے میں ہواؤں کے نشانیان ہیں ان لوگوں کے واسطے جو سمجھ سےکام لیتے ہیں ');
INSERT INTO "al_hind" VALUES(45,6,'45:6','یہ باتیں ہیں اللہ کی ہم سناتے ہیں تجھ کو ٹھیک ٹھیک پھر کونسی بات کو اللہ اور اسکی باتوں کو چھوڑ کر مانیں گے ');
INSERT INTO "al_hind" VALUES(45,7,'45:7','خرابی ہے ہر جھوٹے گنہگار کے لئے');
INSERT INTO "al_hind" VALUES(45,8,'45:8','کہ سنتا ہے باتیں اللہ کی اسکے پاس پڑھی جاتی ہیں پھر ضد کرتا ہے غرور سے گویا سنا ہی نہیں سو خوشخبری سنا دے اسکو ایک عذاب دردناک کی');
INSERT INTO "al_hind" VALUES(45,9,'45:9','اور جب خبر پائے ہماری باتوں میں سے کسی کی اسکو ٹھہرائے ٹھٹھا ایسوں کو ذلت کا عذاب ہے ');
INSERT INTO "al_hind" VALUES(45,10,'45:10','پرے انکے دزوخ ہے اور کام نہ آئے گا انکے جو کمایا تھا ذرا بھی اور نہ وہ کہ جنکو پکڑا تھا اللہ کے سوائے رفیق اور اُنکے واسطے بڑا عذاب ہے');
INSERT INTO "al_hind" VALUES(45,11,'45:11','یہ سجھا دیا اور جو منکر ہیں اپنے رب کی باتوں سے انکے لئے عذاب ہے ایک بلا کا دردناک ');
INSERT INTO "al_hind" VALUES(45,12,'45:12','اللہ وہ ہے جس نے بس میں کر دیا تمہارے دریا کو کہ چلیں اس میں جہاز اس کے حکم سے اور تاکہ تلاش کرو اسکے فضل سے اور تاکہ تم حق مانو ');
INSERT INTO "al_hind" VALUES(45,13,'45:13','اور کام میں لگا دیا تمہارے جو کچھ ہے آسمانوں میں اور زمین میں سب کو اپنی طرف سے اس میں نشانیاں ہیں ان لوگوں کے واسطے جو دھیان کرتے ہیں ');
INSERT INTO "al_hind" VALUES(45,14,'45:14','کہہ دے ایمان والوں کو درگذر کریں ان سے جو امید نہیں رکھتے اللہ کے دنوں کی تاکہ وہ سزا دے ایک قوم کو بدلا اس کا جو کماتے تھے ');
INSERT INTO "al_hind" VALUES(45,15,'45:15','جس نے بھلا کام کیا تو اپنے واسطے اور جس نے برا کیا سو اپنے حق میں پھر اپنے رب کی طرف پھیرے جاؤ گے ');
INSERT INTO "al_hind" VALUES(45,16,'45:16','اور ہم نے دی بنی اسرائیل کو کتاب اور حکومت اور پیغمبری اور کھانے کو دیں ستھری چیزیں اور بزرگی دی انکو جہان پر ');
INSERT INTO "al_hind" VALUES(45,17,'45:17','اور دیں اُنکو کھلی باتیں دین کی پھر انہوں نے پھوٹ جو ڈالی تو سمجھ آ چکنے کے بعد آپس کی ضد سے بیشک تیرا رب فیصلہ کرے گا ان میں قیامت کے دن جس بات میں وہ جھگڑتے تھے ');
INSERT INTO "al_hind" VALUES(45,18,'45:18','پھر تجھ کو رکھا ہم نے ایک رستہ پر دین کے کام کے سو تو اسی پر چل اور مت چل خواہشوں پر نادانوں کی ');
INSERT INTO "al_hind" VALUES(45,19,'45:19','وہ ہر گز کام نہ آئیں گے تیرے اللہ کے سامنے ذرا بھی اور بے انصاف ایک دوسرے کے رفیق ہیں اور اللہ رفیق ہے ڈرنے والوں کا ');
INSERT INTO "al_hind" VALUES(45,20,'45:20','یہ سوجھ کی باتیں ہیں لوگوں کے واسطے اور راہ کی اور رحمت ہے ان لوگوں کے لئے جو یقین لاتے ہیں ');
INSERT INTO "al_hind" VALUES(45,21,'45:21','کیا خیال رکھتے ہیں جنہوں نے کمائی ہیں برائیاں کہ ہم کر دیں گے انکو برابر ان لوگوں کی جو کہ یقین لائے اور کیے بھلے کام ایک سا ہے ان کا جینا اور مرنا برے دعوے ہیں جو کرتے ہیں ');
INSERT INTO "al_hind" VALUES(45,22,'45:22','اور بنائے اللہ نے آسمان اور زمین جیسے چاہئیں اور تاکہ بدلا پائے ہر کوئی اپنی کمائی کا اور ان پر ظلم نہ ہو گا۔ ');
INSERT INTO "al_hind" VALUES(45,23,'45:23','بھلا دیکھ تو جس نے ٹھہرا لیا اپنا حاکم اپنی خواہش کو اور راہ سے بچلا دیا اسکو اللہ نے جانتا بوجھتا اور مہر لگا دی اسکے کان پر اوردل پر اور ڈالدی اسکی آنکھ پر اندھیری پھر کون راہ پر لائے اسکو اللہ کے سوائے سو کیا تم غور نہیں کرتے ');
INSERT INTO "al_hind" VALUES(45,24,'45:24','اور کہتے ہیں اورکچھ نہیں بس یہی ہے ہمارا جینا دنیا کا ہم مرتے ہیں اور جیتے ہیں اور ہم جو مرتے ہیں سو زمانہ سے اور انکو کچھ خبر نہیں اسکی محض اٹکلیں دوڑاتے ہیں ');
INSERT INTO "al_hind" VALUES(45,25,'45:25','اور جب سنائی جاتیں انکو ہماری آیتیں کھلی کھلی اور کچھ دلیل نہیں انکی مگر یہی کہتے ہیں لے آؤ ہمارے باپ دادوں کو اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(45,26,'45:26','تو کہہ کہ اللہ ہی جِلاتا ہے تمکو پھر مارے گا تمکو پھر اکھٹا کرے گا تمکو قیامت کے دن تک اس میں کچھ شک نہیں پر بہت لوگ نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(45,27,'45:27','اور اللہ ہی کا راج ہے آسمانوں میں اور زمین میں اور جس دن قائم ہو گی قیامت اس دن خراب ہوں گے جھوٹے ');
INSERT INTO "al_hind" VALUES(45,28,'45:28','اور تو دیکھے ہر فرقہ کو کہ بیٹھے ہیں گھٹنوں کے بل ہر فرقہ بلایا جائے اپنے اپنے دفتر کے پاس آج بدلا پاؤ گے جیسا تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(45,29,'45:29','یہ ہمارا دفتر ہے بولتا ہے تمہارے کام ٹھیک ہم لکھواتے جاتے تھے جو کچھ تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(45,30,'45:30','سو جو لوگ یقین لائے ہیں اور بھلے کام کیے سو انکو داخل کرے گا ان کا رب اپنی رحمت میں یہ جو ہے یہی ہے صریح مراد ملنی ');
INSERT INTO "al_hind" VALUES(45,31,'45:31','اور جو منکر ہوئے کیا تمکو سنائی نہ جاتی تھی باتیں میری پھر تم نے غرور کیا اور ہو گئے تم لوگ گنہگار ');
INSERT INTO "al_hind" VALUES(45,32,'45:32','اور جب کہیے کہ وعدہ اللہ کا ٹھیک ہے اور قیامت میں کچھ شبہ نہیں تم کہتے تھے ہم نہیں سمجھتے کیا ہے قیامت ہم کو آتا تو ہے ایک خیال سا اور ہم کو یقین نہیں ہوتا ');
INSERT INTO "al_hind" VALUES(45,33,'45:33','اور کھل جائیں ان پر برائیاں ان کاموں کی جو کئے تھے اور الٹ پڑے ان پر وہ چیز جس پر ٹھٹھا کرتے تھے ');
INSERT INTO "al_hind" VALUES(45,34,'45:34','اور حکم ہو گا کہ آج ہم تم کو بھلا دیں گے جیسے تم نے بھلا دیا تھا اپنے اس دن کی ملاقات کو اور گھر تمہارا دوزخ ہے اور کوئی نہیں تمہارا مددگار');
INSERT INTO "al_hind" VALUES(45,35,'45:35','یہ تم پر اس واسطے کہ تم نے پکڑا اللہ کی باتوں کو ٹھٹھا اور بہکے رہے دنیا کی زندگانی پر سو آج نہ انکو نکالنا منظور ہے وہاں سے اور نہ ان سے مطلوب ہے توبہ ');
INSERT INTO "al_hind" VALUES(45,36,'45:36','سو اللہ ہی کے واسطے ہے سب خوبی جو رب ہے آسمانوں کا اور رب ہے زمین کا رب سارے جہان کا');
INSERT INTO "al_hind" VALUES(45,37,'45:37','اور اُسی کیلئے بڑائی ہے آسمانوں میں اور زمین میں اور وہی ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(46,1,'46:1','حٰمٓ۔');
INSERT INTO "al_hind" VALUES(46,2,'46:2','اتارنا کتاب کا ہے اللہ زبردست حکمت والے کی طرف سے');
INSERT INTO "al_hind" VALUES(46,3,'46:3','ہم نے جو بنائے آسمان اور زمین اور جو انکے بیچ میں ہے سو ٹھیک کام پر اور ایک ٹھہرے وعدہ پر اور جو لوگ منکر ہیں وہ ڈر کو سن کر منہ پھیر لیتے ہیں ');
INSERT INTO "al_hind" VALUES(46,4,'46:4','تو کہہ بھلا دیکھو تو جنکو تم پکارتے ہو اللہ کے سوائے دکھلاؤ تو مجھ کو انہوں نے کیا بنایا زمین میں یا ان کا کچھ ساجھا ہے آسمانوں میں لاؤ میرے پاس کوئی کتاب اس سے پہلے کی یا کوئی علم جو چلا آتا ہو اگر ہو تم سچے ');
INSERT INTO "al_hind" VALUES(46,5,'46:5','اور اس سے زیادہ گمراہ کون جو پکارے اللہ کے سو ائے ایسے کو کہ نہ پہنچے اس کی پکار کو دن قیامت تک اور انکو خبر نہیں انکے پکارنے کی ');
INSERT INTO "al_hind" VALUES(46,6,'46:6','اور جب لوگ جمع ہوں گے وہ ہوں گے انکے دشمن اور ہوں گے انکے پوجنے سے منکر ');
INSERT INTO "al_hind" VALUES(46,7,'46:7','اور جب سنائی جائیں انکو ہماری باتیں کھلی کھلی کہتے ہیں منکر سچی بات کو جب ان تک پہنچی یہ جادو ہے صریح ');
INSERT INTO "al_hind" VALUES(46,8,'46:8','کیا کہتے ہیں یہ بنا لایا ہے تو کہہ اگر میں یہ بنا لایا ہوں تو تم میرا بھلا نہیں کر سکتے اللہ کے سامنے ذرا بھی اس کو خوب خبر ہے جن باتوں میں تم لگ رہے ہو وہ کافی ہے حق بتانے والا میرے اور تمہارے بیچ اور وہی ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(46,9,'46:9','تو کہہ میں کچھ نیا رسول نہیں آیا اور مجھ کو معلوم نہیں کیا ہونا ہے مجھ سے اور تم سے میں اسی پر چلتا ہوں جو حکم آتا ہے مجھ کو اور میرا کام تو یہ ہے ڈر سنا دینا کھول کر ');
INSERT INTO "al_hind" VALUES(46,10,'46:10','تو کہہ بھلا دیکھو تو اگر یہ آیا ہو اللہ کے یہاں سے اور تم نے اس کو نہیں مانا اور گواہی دے چکا ایک گواہ بنی اسرائیل کا ایک اسی کتاب کی پھر وہ یقین لایا اور تم نے غرور کیا بیشک اللہ راہ نہیں دیتا گنہگاروں کو ');
INSERT INTO "al_hind" VALUES(46,11,'46:11','اور کہنے لگے منکر ایمان والوں کو اگر یہ دین بہتر ہوتا تو یہ نہ دوڑتے اس پر ہم سے پہلے اور جب راہ پر نہیں آئے اسکے بتلانے سے تو یہ اب کہیں گے یہ جھوٹ ہے بہت پرانا ');
INSERT INTO "al_hind" VALUES(46,12,'46:12','اور اس سے پہلے کتاب موسٰی کی تھی راہ ڈالنے والی اور حمت اور یہ کتاب ہے اسکی تصدیق کرتی عربی زبان میں تاکہ ڈر سنائے گنہگاروں کو اور خوشخبری نیکی والوں کو');
INSERT INTO "al_hind" VALUES(46,13,'46:13','مقرر جنہوں نے کہا رب ہمارا اللہ ہے پھر ثابت قدم رہے تو نہ ڈر ہے ان پر اور نہ وہ غمگین ہوں گے ');
INSERT INTO "al_hind" VALUES(46,14,'46:14','وہ لوگ ہیں بہشت والے سدا رہیں گے اس میں بدلا ہے ان کاموں کا جو کرتے تھے ');
INSERT INTO "al_hind" VALUES(46,15,'46:15','اور ہم نے حکم کر دیا انسان کو اپنے ماں باپ سے بھلائی کا پیٹ میں رکھا اسکو اسکی ماں نے تکلیف سے اور جنا اسکو تکلیف سے اور حمل میں رہنا اس کا اور دودھ چھوڑنا تیس مہینے میں ہے یہاں تک کہ جب پہنچا اپنی قوت کو اور پہنچ گیا چالیس برس کو کہنے لگا اے رب میرے میری قسمت میں کر کہ شکر کروں تیرے احسان کا جو تو نے مجھ پر کیا اور میرے ماں باپ پر اور یہ کہ کروں نیک کام جس سے تو راضی ہو اور مجھ کو دے نیک اولاد میری میں نے توبہ کی تیری طرف اور میں ہوں حکم بردار ');
INSERT INTO "al_hind" VALUES(46,16,'46:16','یہ وہ لوگ ہیں جن سے ہم قبول کرتے ہیں بہتر سے بہتر کام جو کئے ہیں اور معاف کرتے ہیں ہم برائیاں ان کی رہنے والے جنت کے لوگوں میں سچا وعدہ جو ان سے کیا جاتا تھا ');
INSERT INTO "al_hind" VALUES(46,17,'46:17','اور جس شخص نے کہا اپنے مان باپ کو میں بیزار ہوں تم سے کیا مجھ کو وعدہ دیتے ہو کہ میں نکالا جاؤں گا قبر سے اور گزر چکی ہیں بہت جماعتیں مجھ سے پہلے اور وہ دونوں فریاد کرتےہیں اللہ سے کہ اے خرابی تیری تو ایمان لے آ بیشک وعدہ اللہ کا ٹھیک ہے پھر کہتا ہے یہ سب نقلیں ہیں پہلوں کی ');
INSERT INTO "al_hind" VALUES(46,18,'46:18','یہ وہ لوگ ہیں کہ جن پر ثابت ہوئی بات عذاب کی شامل اور فرقوں میں جو گذر چکے ہیں ان سے پہلے جنوں کے اور آدمیوں کے بیشک وہ تھے ٹوٹے میں پڑے ');
INSERT INTO "al_hind" VALUES(46,19,'46:19','اور ہر فرقہ کے کئ درجے ہیں اپنے کئے کاموں کے موافق اور تاکہ پورے دے انکو کام انکے اور ان پر ظلم نہ ہو گا ');
INSERT INTO "al_hind" VALUES(46,20,'46:20','اور جس دن لائے جائیں گے منکر آگ کے کنارہ پر ضائع کئے تم نے اپنے مزے دنیا کی زندگانی میں اور انکو برت چکے اب آج سزا پاؤ گے ذلت کا عذاب بدلا اس کا جو تم غرور کرتے تھے ملک میں ناحق اور اس کا جو تم نافرمانی کرتے تھے ');
INSERT INTO "al_hind" VALUES(46,21,'46:21','اور یاد کر عاد کے بھائی کو جب ڈرایا اپنی قوم کو احقاف میں اور گذر چکے تھے دڑانے والے اسکے آگے سے اور پیچھے سے کہ بندگی نہ کرو کسی کی اللہ کے سوائے میں ڈرتا ہوں تم پر آفت سے ایک بڑے دن کی ');
INSERT INTO "al_hind" VALUES(46,22,'46:22','بولے کیا تو آیا ہے ہمارے پاس کہ پھیر دے ہمکو ہمارے معبودوں سے سو لے آ ہم پر جو وعدہ کرتا ہے اگر ہے تو سچا ');
INSERT INTO "al_hind" VALUES(46,23,'46:23','کہا یہ خبر تو اللہ ہی کو ہے اور میں تو پہنچا دیتا ہوں جو کچھ بھیجدیا میرے ہاتھ لیکن میں دیکھتا ہوں تم لوگ نادانی کرتے ہو ');
INSERT INTO "al_hind" VALUES(46,24,'46:24','پھر جب دیکھا اس کو ابر سامنے آیا انکے نالوں کے بولے یہ ابر ہے ہم پر برسے گا کوئی نہیں یہ تو وہ چیز ہے جسکی تم جلدی کرتے تھے ہوا ہے جس میں عذاب ہے دردناک ');
INSERT INTO "al_hind" VALUES(46,25,'46:25','اکھاڑ پھینکے ہر چیز کو اپنے رب کے حکم سے پھر کل کو رہ گئ کہ کوئی نظر نہیں آتا تھا سوائے انکے گھروں کے یوں ہم سزا دیتے ہیں گنہگار لوگوں کو ');
INSERT INTO "al_hind" VALUES(46,26,'46:26','اور ہم نے مقدور دیا تھا انکو ان چیزوں کا جن کا تم کو مقدور نہیں دیا اور ہم نے انکو دیے تھے کان اور آنکھیں اور دل پھر کام نہ آئے انکےکان انکے اور نہ آنکھیں انکی اور نہ دل انکے کسی چیز میں اس لئے کہ منکر ہوتے تھے اللہ کی باتوں سے اور الٹ پڑی ان پر جس بات سے کہ وہ ٹھٹھا کرتے تھے ');
INSERT INTO "al_hind" VALUES(46,27,'46:27','اور ہم غارت کر چکے ہیں جتنی تمہارے آس پاس ہیں بستیاں اور طرح طرح سے پھیر کر سنائیں انکو باتیں تاکہ وہ لوٹ آئیں ');
INSERT INTO "al_hind" VALUES(46,28,'46:28','پھر کیوں نہ مدد پہنچی انکو ان لوگوں کی طرف سے جنکو پکڑ ا تھا اللہ سے ورے معبود بڑے درجے پانے کو کوئی نہیں گم ہو گئے ان سے اور یہ جھوٹ تھا ان کا اور جو اپنے جی سے باندھتے تھے ');
INSERT INTO "al_hind" VALUES(46,29,'46:29','اور جس وقت متوجہ کر دیے ہم نے تیری طرف کتنے اک لوگ جنوں میں سے سننے لگے قرآن پھر جب وہاں پہنچ گئے بولے چپ رہو پھر جب ختم ہوا الٹے پھرے اپنی قوم کو ڈر سناتے ہوئے ');
INSERT INTO "al_hind" VALUES(46,30,'46:30','بولے اے قوم ہماری ہم نے سنی ایک کتاب جو اتری ہے موسٰی کے بعد سچا کرنے والی سب اگلی کتابوں کو سمجھاتی ہے سچا دین اور ایک راہ سیدھی ');
INSERT INTO "al_hind" VALUES(46,31,'46:31','اے قوم ہماری مانو اللہ کے بلانے والے کو اور اس پر یقین لاؤ کہ بخشے تمکو کچھ تمہارے گناہ اور بچا دے تمکو ایک عذاب دردناک سے');
INSERT INTO "al_hind" VALUES(46,32,'46:32','اور جو کوئی نہ مانے گا اللہ کے بلانے والے کو تو وہ نہ تھکا سکے گا بھاگ کر زمین میں اور کوئی نہیں اس کا اسکے سوائے مددگار وہ لوگ بھٹکتے ہیں صریح');
INSERT INTO "al_hind" VALUES(46,33,'46:33','کیا نہیں دیکھتے کہ وہ اللہ جس نے بنائے آسمان اور زمین اور نہ تھکا انکے بنانے میں وہ قدرت رکھتا ہے کہ زندہ کرے مردوں کو کیوں نہیں وہ ہر چیز کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(46,34,'46:34','اور جس دن سامنے لائیں منکروں کو آگ کے کیا یہ ٹھیک نہیں کہیں گے کیوں نہیں قسم ہے ہمارے رب کی کہا تو چکھو عذاب بدلا اس کا جو تم منکر ہوتے تھے ');
INSERT INTO "al_hind" VALUES(46,35,'46:35','سو تو ٹھہرا رہ جیسے ٹھہرے رہے ہیں ہمت والے رسول اور جلدی نہ کر انکے معاملہ میں یہ لوگ جس دن دیکھ لیں گے اس چیز کو جس کا ان سےوعدہ ہے جیسے ڈھیل نہ پائی تھی مگر ایک گھڑی دن کی یہ پہنچا دینا ہے اب وہی غارت ہوں گے جو لوگ نافرمان ہیں ');
INSERT INTO "al_hind" VALUES(47,1,'47:1','جو لوگ کہ منکر ہوئے اور روکا اوروں کو اللہ کی راہ سے کھو دیے اللہ نے انکے کئے کام ');
INSERT INTO "al_hind" VALUES(47,2,'47:2','اور جو یقین لائے اور کئے بھلے کام اور مانا اسکو جو اترا محمد پر اور وہی ہے سچا دین انکے رب کی طرف سے ان پر سے اتاریں انکی برائیاں اور سنورا انکا حال ');
INSERT INTO "al_hind" VALUES(47,3,'47:3','یہ اس لئے کہ جو منکر ہیں وہ چلے جھوٹی بات پر اور جو یقین لائے انہوں نے مانی سچی بات اپنے رب کی طرف سے یوں بتلاتا ہے اللہ لوگوں کو انکے احوال ');
INSERT INTO "al_hind" VALUES(47,4,'47:4','سو جب تم مقابل ہو منکروں کے تو مارو گردنیں یہاں تک کہ جب خوب قتل کر چکو انکو تو مضبوط باندھ لو قید پھر یا احسان کیجیو اور یا معاوضہ لیجیو جب تک کہ رکھ دے لڑائی اپنے ہتھیار یہ سن چکے اور اگر چاہے اللہ تو بدلا لے ان سے پر جانچنا چاہتا ہے تمہارے ایک سے دوسرے کو اور جو لوگ مارے گئے اللہ کی راہ میں تو نہ ضائع کرے گا وہ انکے کئے کام');
INSERT INTO "al_hind" VALUES(47,5,'47:5','انکو راہ دے گا اور سنوارے گا ان کا حال ');
INSERT INTO "al_hind" VALUES(47,6,'47:6','اور داخل کرے گا انکو بہشت میں جو معلوم کرا دی ہے انکو ');
INSERT INTO "al_hind" VALUES(47,7,'47:7','اے ایمان والو اگر تم مدد کر و گے اللہ کی تو وہ تمہاری مدد کرے گا اور جما دے گا تمہارے پاؤں ');
INSERT INTO "al_hind" VALUES(47,8,'47:8','اور جو لوگ کہ منکر ہوئے وہ گرے منہ کے بل اور کھو دیے انکے کئے کام ');
INSERT INTO "al_hind" VALUES(47,9,'47:9','یہ اس لئے کہ انکو پسند نہ ہوا جو اتار اللہ نے پھر اکارت کر دیے انکے کئے کام ');
INSERT INTO "al_hind" VALUES(47,10,'47:10','کیا وہ پھرے نہیں ملک میں کہ دیکھیں کیسا ہوا انجام ان کا جو ان سے پہلے تھے ہلاکی ڈالی اللہ نے ان پر اور منکروں کو ملتی رہی ہیں ایسی چیزیں ');
INSERT INTO "al_hind" VALUES(47,11,'47:11','یہ اس لئے کہ اللہ رفیق ہے ان کا جو یقین لائے اور یہ کہ جو منکر ہیں ان کا رفیق نہیں کوئی ');
INSERT INTO "al_hind" VALUES(47,12,'47:12','مقرر اللہ داخل کرے گا انکو جو یقین لائے اور کئے بھلے کام باغوں میں جنکے نیچے بہتی ہیں نہریں اور جو لو گ منکر ہیں برت رہے ہیں اور کھاتے ہیں جیسے کہ کھائیں چوپائے اور آگ ہے گھر ان کا ');
INSERT INTO "al_hind" VALUES(47,13,'47:13','اور کتنی تھیں بستیاں جو زیادہ تھیں زور میں اس تیری بستی سے جس نے تجھ کو نکالا ہم نے انکو غارت کر دیا پھر کوئی نہیں ان کا مددگار ');
INSERT INTO "al_hind" VALUES(47,14,'47:14','بھلا ایک جو چلتا ہے واضح رستہ پر اپنے رب کے برابر ہے اسکے جسکو بھلا دکھلایا اس کا برا کام اور چلتے ہیں اپنی خواہشوں پر ');
INSERT INTO "al_hind" VALUES(47,15,'47:15','احوال اس بہشت کا جس کا وعدہ ہوا ہے ڈرنے والوں سے اس میں نہریں ہیں پانی کی جو بو نہیں کر گیا اور نہریں ہیں دودھ کی جس کا مزہ نہیں پھرا اور نہریں ہیں شراب کی جس میں مزہ ہے پینے والوں کے واسطے اور نہریں ہیں شہد کی جھاگ اترا ہوا اور انکے لئے وہاں سب طرح کے میوےہیں اور معافی ہے انکے رب سے یہ برابر ہے اسکے جو سدا رہے آگ میں اور پلایا جائے انکو کھولتا پانی تو کاٹ نکالے انکی آنتیں ');
INSERT INTO "al_hind" VALUES(47,16,'47:16','اور بعضے ان میں ہیں کہ کان رکھتے ہیں تیری طرف یہاں تک کہ جب نکلیں تیرے پاس سے کہتے ہیں انکو جنکو علم ملا ہے کیا کہا تھا اس شخص نے ابھی یہ وہی ہیں جنکے دلوں پر مہر لگا دی ہے اللہ نے اور چلے ہیں اپنی خواہشوں پر ');
INSERT INTO "al_hind" VALUES(47,17,'47:17','اور جو لوگ راہ پر آئے ہیں انکو اور بڑھ گئ اس سے سوجھ اور انکو اس سے ملا بچ کر چلنا ');
INSERT INTO "al_hind" VALUES(47,18,'47:18','اب یہی انتظار کرتے ہیں قیامت کا کہ آ کھڑی ہو ان پر اچانک سو آچکی ہیں اسکی نشانیاں پھر کہاں نصیب ہو گا انکو جب وہ آ پہنچے ان پر سمجھ پکڑنا ');
INSERT INTO "al_hind" VALUES(47,19,'47:19','سو تو جان لے کہ کسی کی بندگی نہیں سوائے اللہ کے اور معافی مانگ اپنے گناہ کے واسطے اور ایماندار مردوں اور عورتوں کے لئے اور اللہ کو معلوم ہے بازگشت تمہاری اور گھر تمہارا ');
INSERT INTO "al_hind" VALUES(47,20,'47:20','اور کہتے ہیں ایمان والے کیوں نہ اتری ایک سورت پھر جب اتری ایک سورت جانچی ہوئی اور ذکر ہوا اس میں لڑائی کا تو تو دیکھتا ہے انکو جنکے دل میں روگ ہے تکتے ہیں تیری طرف جیسے تکتا ہے کوئی بیہوش پڑا ہوا مرنے کے وقت سو خرابی ہے انکی ');
INSERT INTO "al_hind" VALUES(47,21,'47:21','حکم ماننا ہے اور بھلی بات کہنی پھر جب تاکید ہو کام کی تو اگر سچے رہیں اللہ سے تو ان کا بھلا ہے ');
INSERT INTO "al_hind" VALUES(47,22,'47:22','پھر تم سے یہ بھی توقع ہے کہ اگر تمکو حکومت مل جائے تو خرابی ڈالو ملک میں اور قطع کرو اپنی قرابتیں ');
INSERT INTO "al_hind" VALUES(47,23,'47:23','ایسے لوگ ہیں جن پر لعنت کی اللہ نے پھر کردیا انکو بہرا اور اندھی کردیں اُنکی آنکھیں ');
INSERT INTO "al_hind" VALUES(47,24,'47:24','کیا دھیان نہیں کرتے قرآن میں یا دلوں پر لگ رہے ہیں انکے قفل ');
INSERT INTO "al_hind" VALUES(47,25,'47:25','بیشک جو لوگ الٹے پھر گئے اپنی پیٹھ پر بعد اسکے کہ ظاہر ہو چکی ان پر سیدھی راہ شیطان نے بات بنائی انکے دل میں اور دیر کے وعدے کئے ');
INSERT INTO "al_hind" VALUES(47,26,'47:26','یہ اس واسطے کہ انہوں نے کہا ان لوگوں سے جو بیزار ہیں اللہ کی اتاری کتاب سے ہم تمہاری بات بھی مانیں گے بعضے کاموں میں اور اللہ جانتا ہے ان کا مشورہ کرنا ');
INSERT INTO "al_hind" VALUES(47,27,'47:27','پھر کیسا ہوگا حال جبکہ فرشتے جان نکالیں گے اُنکی مارتے جاتے ہوں انکے منہ پر اور پیٹھ پر ');
INSERT INTO "al_hind" VALUES(47,28,'47:28','یہ اس لئے کہ وہ چلے اس راہ جس سے اللہ بیزار ہے اور ناپسند کی اسکی خوشی پھر اس نے اکارت کردیے انکے کیے کام ');
INSERT INTO "al_hind" VALUES(47,29,'47:29','کیا خیال رکھتے ہیں وہ لوگ جنکے دلوں میں روگ ہے کہ اللہ ظاہر نہ کر دے گا اُنکے کینے ');
INSERT INTO "al_hind" VALUES(47,30,'47:30','اگر ہم چاہیں تجھ کو دکھلا دیں وہ لوگ سو تو پہچان تو چکا ہے اُنکو اُنکے چہرہ سے اور آگے پہچان لے گا بات کے ڈھب سے اور اللہ کو معلوم ہیں تمہارے سب کام ');
INSERT INTO "al_hind" VALUES(47,31,'47:31','اور البتہ ہم تم کو جانچیں گے تا معلوم کر لیں جو تم میں لڑائی کرنے والے ہیں اور قائم رہنے والے اور تحقیق کر لیں تمہاری خبریں ');
INSERT INTO "al_hind" VALUES(47,32,'47:32','جو لوگ منکر ہوئے اور روکا انہوں نے اللہ کی راہ سے اور مخالف ہوگئے رسول سے بعد اس کے کہ ظاہر ہو چکی ان پر سیدھی راہ نہ بگاڑ سکیں گے اللہ کا کچھ اور وہ اکارت کر دے گا انکے سب کام ');
INSERT INTO "al_hind" VALUES(47,33,'47:33','اے ایمان والو حکم پر چلو اللہ کے اور حکم پر چلو رسول کے اور ضائع مت کرو اپنے کئے ہوئے کام ');
INSERT INTO "al_hind" VALUES(47,34,'47:34','جو لوگ منکر ہوئے اور روکا لوگوں کو اللہ کی راہ سے پھر مر گئے اور وہ منکر ہی رہے تو ہر گز نہ بخشے گا انکو اللہ ');
INSERT INTO "al_hind" VALUES(47,35,'47:35','سو تم بودے نہ ہوئے جاؤ اور (کہ) لگو پکارنے صلح اور تم ہی رہو گے غالب اور اللہ تمہارے ساتھ ہے اور نقصان نہ دے گا تمکو تمہارے کاموں میں ');
INSERT INTO "al_hind" VALUES(47,36,'47:36','یہ دنیا کا جینا تو کھیل ہے اور تماشا اور اگر تم یقین لاؤ گے اور بچ کر چلو گے دے گا تمکو تمہارا بدلا اور نہ مانگے گا تم سے مال تمہارے ');
INSERT INTO "al_hind" VALUES(47,37,'47:37','اگر مانگے تم سے وہ مال پھر تم کو تنگ کرے تو بخل کرنے لگو اور ظاہر کر دے تمہارے دل کی خفگیاں ');
INSERT INTO "al_hind" VALUES(47,38,'47:38','سنتے ہو تم لوگ تمکو بلاتے ہیں کہ خرچ کرو اللہ کی راہ میں پھر تم میں کوئی ایسا ہے کہ نہیں دیتا اور جو کوئی نہ دے گا سو نہ دے گا آپکو اور اللہ بے نیاز ہے اور تم محتاج ہو اور اگر تم پھر جاؤگے تو بدل لے گا اور لوگ تمہارے سوائے پھر وہ نہ ہوں گے تمہاری طرح کے ');
INSERT INTO "al_hind" VALUES(48,1,'48:1',' ہم نے فیصلہ کر دیا تیرے واسطے صریح فیصلہ');
INSERT INTO "al_hind" VALUES(48,2,'48:2','تا معاف کرے تجھ کو اللہ جو آگے ہو چکے تیرے گناہ اور جو پیچھے رہے اور پورا کر دے تجھ پر اپنا احسان اور چلائے تجھ کو سیدھی راہ ');
INSERT INTO "al_hind" VALUES(48,3,'48:3','اور مدد کرے تیری اللہ زبردست مدد ');
INSERT INTO "al_hind" VALUES(48,4,'48:4','وہی ہے جس نے اتارا اطمینان دل میں ایمان والوں کے تاکہ اور بڑھ جائے انکو ایمان اپنے ایمان کے ساتھ اور اللہ کے ہیں سب لشکر آسمانوں کے اور زمین کے اور اللہ ہے خبردار حکمت والا ');
INSERT INTO "al_hind" VALUES(48,5,'48:5','تاکہ پہنچا دے ایمان والے مردوں کو اور ایمان والی عورتوں کو باغوں میں نیچے بہتی ہیں انکے نہریں ہمیشہ رہیں ان میں اور اتار دی اُن پر سے اُنکی برائیاں اور یہ ہے اللہ کے یہاں بڑی مراد ملتی ');
INSERT INTO "al_hind" VALUES(48,6,'48:6','اور تاکہ عذاب کرے دغاباز مردوں کو اور دغاباز عورتوں کو اور شرک والے مردوں کو اور اور شرک والی عورتوں کو جو اٹکلیں کرتے ہیں اللہ پر بری اٹکلیں انہی پر پڑے پھیر مصیبت کا اور غصہ ہوا اللہ اُن پر اور لعنت کی انکو اور تیار کی اُنکے واسطے دوزخ اور بری جگہ پہنچے');
INSERT INTO "al_hind" VALUES(48,7,'48:7','اور اللہ کے ہیں سب لشکر آسمانوں کے اور زمین کے اور ہے اللہ زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(48,8,'48:8','ہم نے تجھ کو بھیجا احوال بتانے والا اور خوشی اور ڈر سنانے والا ');
INSERT INTO "al_hind" VALUES(48,9,'48:9','تاکہ تم لوگ یقین لاؤ اللہ پر اور اسکے رسول پر اور اسکی مدد کرو اور اسکی عظمت رکھو اور اُسکی پاکی بولتے رہو صبح اور شام ');
INSERT INTO "al_hind" VALUES(48,10,'48:10','تحقیق جو لوگ بیعت کرتے ہیں تجھ سے وہ بیعت کرتے ہیں اللہ سے اللہ کا ہاتھ ہے اوپر اُنکے ہاتھ کے پھر جو کوئی قول توڑےسو توڑتا ہے اپنے نقصان کو اور جو کوئی پورا کرے اس چیز کو جس پر اقرار کیا اللہ سے تو وہ اس کو دے گا بدلہ بہت بڑا ');
INSERT INTO "al_hind" VALUES(48,11,'48:11','اب کہیں گے تجھ سے پیچھے رہ جانے والے گنوار ہم کام میں لگے رہ گئے اپنے مالوں کے اور گھر والوں کے سو ہمارا گناہ بخشوا وہ کہتے ہیں اپنی زبان سے جو انکے دل میں نہیں تو کہہ کس کا کچھ بس چلتا ہے اللہ سے تمہارے واسطے اگر وہ چاہے تمہارا نقصان یا چاہے تمہارافائدہ بلکہ اللہ ہے تمہارے سب کاموں سے خبردار ');
INSERT INTO "al_hind" VALUES(48,12,'48:12','کوئی نہیں تم نے تو خیال کیا تھا کہ پھر کر نہ آئے گا رسول اور مسلمان اپنے گھر کبھی اور کھب گیا تمہارے دل میں یہ خیال اور اٹکل کی تم نے بری اٹکلیں اور تم لوگ تھے تباہ ہونے والے ');
INSERT INTO "al_hind" VALUES(48,13,'48:13','اور جو کوئی یقین نہ لائے اللہ پر اور اسکے رسول پر تو ہم نے تیار کر رکھی ہے منکروں کے واسطے دہکتی آگ');
INSERT INTO "al_hind" VALUES(48,14,'48:14','اور اللہ کے لئے ہے راج آسمانوں کا اور زمین کا بخشے جسکو چاہے اور عذاب میں ڈالے جسکو چاہے اور ہے اللہ بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(48,15,'48:15','اب کہیں گے پیچھے رہ گئے ہوئے جب تم چلو گے غنیمتیں لینے کو چھوڑ و ہم بھی چلیں تمہارے ساتھ چاہتے ہیں کہ بدل دیں اللہ کا کہا تو کہہ دے تم ہمارے ساتھ ہر گز نہ چلو گے یونہی کہہ دیا اللہ نے پہلے سے پھر اب کہیں گے نہیں تم تو جلتے ہو ہمارے فائدہ سے کوئی نہیں پر وہ نہیں سمجھتے ہیں مگر تھوڑا سا ');
INSERT INTO "al_hind" VALUES(48,16,'48:16','کہہ دے پیچھے رہ جانے والے گنواروں سے آئندہ تم کو بلائیں گے ایک قوم پر بڑے سخت لڑنے والے تم ان سے لڑو گے یا وہ مسلمان ہوں گے پھر اگر حکم مانو گے دے گا تم کو اللہ بدلہ اچھا اور اگر پلٹ جاؤ گے جیسے پلٹ گئے تھے پہلی بار دے گا تم کو ایک عذاب دردناک ');
INSERT INTO "al_hind" VALUES(48,17,'48:17','اندھے پر تکلیف نہیں اور نہ لنگڑے پر تکلیف اور نہ بیمار پر تکلیف اور جو کوئی حکم مانے اللہ کا اور اس کے رسول کا اس کو داخل کرے گا باغوں میں جنکے نیچے بہتی ہیں نہریں اور جو کوئی پلٹ جائے اسکو عذاب دے گا دردناک ');
INSERT INTO "al_hind" VALUES(48,18,'48:18','تحقیق اللہ خوش ہوا ایمان والوں سے جب بیعت کرنے لگے تجھ سے اس درخت کے نیچے پھر معلوم کیا جو اُنکے جی میں تھا پھر اتارا اُن پر اطمینان اور انعام دیا اُنکو ایک فتح نزدیک');
INSERT INTO "al_hind" VALUES(48,19,'48:19','اور بہت غنیمتیں جنکو وہ لیں گے اور ہے اللہ زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(48,20,'48:20','وعدہ کیا ہے تم سے اللہ نے بہت غنیمتوں کا کہ تم اُن کو لو گے سو جلدی پہنچا دی تمکو یہ غنیمت اور روک دیا لوگوں کے ہاتھوں کو تم سے اور تاکہ ایک نمونہ ہو قدرت کا مسلمانوں کے واسطے اور چلائے تم کو سیدھی راہ ');
INSERT INTO "al_hind" VALUES(48,21,'48:21','اور ایک فتح اور جو تمہارے بس میں نہ آئی وہ اللہ کےقابو میں ہے اور اللہ ہرچیز کر سکتاہے ');
INSERT INTO "al_hind" VALUES(48,22,'48:22','اور اگرلڑتے تم سے کافر تو پھیرتے پیٹھ پھر نہ پاتے کوئی حمایتی اور نہ مددگار ');
INSERT INTO "al_hind" VALUES(48,23,'48:23','رسم پڑی ہوئی اللہ کی جو چلی آتی ہے پہلے سے اور تو ہر گز نہ دیکھے گا اللہ کی رسم کو بدلتے ');
INSERT INTO "al_hind" VALUES(48,24,'48:24','اور ہی ہے جس نے روک رکھا اُنکے ہاتھوں کو تم سے اور تمہاے ہاتھوں کو ان سے بیچ شہر مکہ کے بعد اسکے کہ تمہارےہاتھ لگادیا اُنکو اور ہے اللہ جو کچھ تم کرتے ہو دیکھتا ');
INSERT INTO "al_hind" VALUES(48,25,'48:25','یہ وہی لوگ ہیں جو منکر ہوئے اور روکا تم کو مسجد حرام سے اور نیاز کی قربانی کو بھی بند پڑی ہوئی اس بات سے کہ پہنچے اپنی جگہ تک اور اگر نہ ہوتے کتنے ایک مرد ایمان والے اور کتنی عورتیں ایمان والیاں جو تم کو معلوم نہیں یہ خطرہ کہ تم انکو پیس ڈالتے پھر تم پر انکی وجہ سے خرابی پڑ جاتی بیخبری سےکہ اللہ کو داخل کرنا ہے اپنی رحمت میں جسکو چاہے اگر وہ لوگ ایک طرف ہو جاتے تو آفت ڈالتے ہم منکروں پر عذاب دردناک کی ');
INSERT INTO "al_hind" VALUES(48,26,'48:26','جب رکھی منکروں نے اپنے دلوں میں کد نادانی کی ضد پھر اتارا اللہ نے اپنی طرف کا اطمینان اپنے رسول پر اور مسلمانوں پر اور قائم رکھا انکو ادب کی بات پر اور وہی تھے اسکے لائق اور اس کام کے اور ہے اللہ ہر چیز سے خبردار ');
INSERT INTO "al_hind" VALUES(48,27,'48:27','اللہ نے سچ دکھلایا اپنے رسول کو خواب تحقیقی کہ تم داخل ہو رہو گے مسجد حرام میں اگر اللہ نے چاہا آرام سے بال مونڈتے ہوئے اپنے سروں کے اور کترتے ہوئے بے کھٹکے پھر جانا وہ جو تم نہیں جانتے پھر مقرر کر دی اس سے ورے ایک فتح نزدیک ');
INSERT INTO "al_hind" VALUES(48,28,'48:28','وہی ہے جس نے بھیجا اپنا رسول سیدھی راہ پر اور سچے دین پر تاکہ اوپر رکھے اسکو ہر دین سے اور کافی ہے اللہ حق ثابت کرنے والا ');
INSERT INTO "al_hind" VALUES(48,29,'48:29','محمد رسول اللہ کا اور جو لوگ اسکے ساتھ ہیں زورآور ہیں کافروں پر نرم دل ہیں آپس میں تو دیکھے ان کو رکوع میں اور سجدہ میں ڈھونڈتے ہیں اللہ کا فضل اور اسکی خوشی نشانی اُنکی اُنکے منہ پر ہے سجدہ کے اثر سے یہ شان ہے انکی تورات میں اور مثال انکی انجیل میں جیسے کھیتی نے نکالا اپنا پٹھا پھر اُسکی کمر مضبوط کی پھر موٹا ہوا پھر کھڑا ہو گیا اپنی نال پر خوش لگتا ہے کھیتی والوں کو تاکہ جلائے ان سے جی کافروں کا وعدہ کیا ہے اللہ نے ان سے جو یقین لائے ہیں اور کئے ہیں بھلے کام معافی کا اور بڑے ثواب کا ');
INSERT INTO "al_hind" VALUES(49,1,'49:1','اے ایمان والو آگے نہ بڑھو اللہ سے اور اسکے رسول سے اور ڈرتے رہو اللہ سے اللہ سنتا ہے جانتا ہے ');
INSERT INTO "al_hind" VALUES(49,2,'49:2','اے ایمان والو بلند نہ کرو اپنی آوازیں نبی کی آواز سے اوپر اور اس سے نہ بولو تڑخ کر جیسے تڑختے ہو ایک دوسرے پر کہیں اکارت نہ ہو جائیں تمہارے کام اور تم کو خبر بھی نہ ہو ');
INSERT INTO "al_hind" VALUES(49,3,'49:3','جو لوگ دبی آواز سے بولتے ہیں رسول اللہ کے پاس وہی ہیں جنکے دلوں کو جانچ لیا ہے اللہ نے ادب کے واسطے انکے لئے معافی ہے اور ثواب بڑا ');
INSERT INTO "al_hind" VALUES(49,4,'49:4','جو لوگ پکارتے ہیں تجھ کو دیوار کے پیچھے سے وہ اکثر عقل نہیں رکھتے');
INSERT INTO "al_hind" VALUES(49,5,'49:5','اور اگر وہ صبر کرتے جب تک تو نکلتا اُنکی طرف تو اُنکے حق میں بہتر ہوتا اور اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(49,6,'49:6','اے ایمان والو اگر آئے تمہارے پاس کوئی گنہگار خبر لے کر تو تحقیق کر لو کہیں جا نہ پڑو کسی قوم پر نادانی سے پھر کل کو اپنے کئے پر لگو پچتانے ');
INSERT INTO "al_hind" VALUES(49,7,'49:7','اور جان لو کہ تم میں رسول ہے اللہ کا اگر وہ تمہاری بات مان لیا کرے بہت کاموں میں تو تم پر مشکل پڑے پر اللہ نے محبت ڈال دی تمہارے دل میں ایمان کی اور کھبا دیا اسکو تمہارے دلوں میں اور نفرت ڈال دی تمہارے دل میں کفر اور گناہ اور نافرمانی کی وہ لوگ وہی ہیں نیک راہ پر');
INSERT INTO "al_hind" VALUES(49,8,'49:8','اللہ کے فضل سے اور احسان سے اور اللہ سب کچھ جانتا ہے حکمتوں والا ہے ');
INSERT INTO "al_hind" VALUES(49,9,'49:9','اور اگر دو فریق مسلمانوں کے آپس میں لڑ پڑیں تو ان میں ملاپ کرا دو پھر اگر چڑھا چلا جائے ایک ان میں سے دوسرے پر تو تم سب لڑو اس چڑھائی والے سے یہاں تک کہ پھر آئے اللہ کے حکم پر پھر اگر پھر آیا تو ملاپ کرا دو ان میں برابر اور انصاف کرو بیشک اللہ کو خوش آتے ہیں انصاف والے ');
INSERT INTO "al_hind" VALUES(49,10,'49:10','مسلمان ہو ہیں سو بھائی ہیں سو ملاپ کرا دو اپنے دو بھائیوں میں اور ڈرتے رہو اللہ سے تاکہ تم پر رحم ہو ');
INSERT INTO "al_hind" VALUES(49,11,'49:11','اے ایمان والون ٹھٹھا نہ کریں ایک لوگ دوسروں سے شاید وہ بہتر ہوں ان سے اور نہ عورتیں دوسری عورتوں سے شیاد وہ بہتر ہوں ان سے اور عیب نہ لگاؤ ایک دوسرےکو اور نام نہ ڈالو چڑانے کو ایک دوسرے کے برا نام ہے گنہگاری پیچھے ایمان کے اور جو کوئی توبہ نہ کرے تو وہی ہیں بے انصاف ');
INSERT INTO "al_hind" VALUES(49,12,'49:12','اے ایمان والو بچتے رہو بہت تہمتیں کرنے سے مقرر بعضی تہمت گناہ ہے اور بھید نہ ٹٹولو کسی کا اور برا نہ کہیو پیٹھ پیچھے ایک دوسرے کو بھلا خوش لگتا ہے تم میں کسی کو کہ کھائے گوشت اپنے بھائی کا جو مردہ ہو سو گھن آتا ہے تم کو اس سے اور ڈرتے رہو اللہ سے بیشک اللہ معاف کرنے والا ہے مہربان ');
INSERT INTO "al_hind" VALUES(49,13,'49:13','اے آدمیو ہم نے تم کو بنایا ایک مرد اور ایک عورت سےاور رکھیں تمہاری ذاتیں اور قبیلے تاکہ آپس کی پہچان ہو تحقیق عزت اللہ کے یہاں اسکی کو بڑی جسکو ادب بڑا اللہ سب کچھ جانتا ہے خبردار ');
INSERT INTO "al_hind" VALUES(49,14,'49:14','کہتے ہیں گنوار کہ ہم ایمان لائے تو کہہ تم ایما ن نہیں لائے پر تم کہو ہم مسلمان ہوئے اور ابھی نہیں گھُسا ایمان تمہارے دلوں میں اور اگر ھم پر چلو گے اللہ کے اور اسکے رسول کے کاٹ نہ لے گا تمہارے کاموں میں سے کچھ اللہ بخشتا ہے مہربان ہے۔ ');
INSERT INTO "al_hind" VALUES(49,15,'49:15','ایمان والے وہ لوگ ہیں جو ایمان لائے اللہ پر اور اسکے رسول پر پھر شبہ نہ لائے اور لڑے اللہ کی راہ میں اپنے مال اور اپنی جان سے وہ لوگ جو ہیں وہی ہیں سچے ');
INSERT INTO "al_hind" VALUES(49,16,'49:16','تو کہہ کیا تم جتلاے ہو اللہ کو اپنی دینداری اور اللہ کو تو خبر ہے جو کچھ ہے آسمانوں میں اور زمین میں اور اللہ ہر چیز کو جانتا ہے ');
INSERT INTO "al_hind" VALUES(49,17,'49:17','تجھ پر احسان رکھتے ہیں کہ مسلمان ہوئے تو کہہ مجھ پر احسان نہ رکھو اپنے اسلام لانے کا بلکہ اللہ تم پر احسان رکھتا ہے کہ اس نے تم کو راہ دی ایمان کی اگر سچ کہو ');
INSERT INTO "al_hind" VALUES(49,18,'49:18','اللہ جانتا ہے چھپے بھید آسمانوں کے اور زمین کے اور اللہ دیکھتا ہے جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(50,1,'50:1','ق۔قسم ہے اس قرآن بڑی شان والے کی');
INSERT INTO "al_hind" VALUES(50,2,'50:2','بلکہ انکو تعجب ہوا کہ آیا اُنکے پاس ڈر سنانے والا اُنہی میں کا تو کہنے لگے منکر یہ تعجب کی چیز ہے');
INSERT INTO "al_hind" VALUES(50,3,'50:3','کیا جب ہم مر چکیں اور ہو جائیں مٹی یہ پھر آنا بہت دور ہے ');
INSERT INTO "al_hind" VALUES(50,4,'50:4','ہم کو معلوم ہے جتنا گھٹاتی ہے زمین اُن میں سے اور ہمارے پاس کتاب ہے جس میں سب کچھ محفوظ ہے ');
INSERT INTO "al_hind" VALUES(50,5,'50:5','کوئی نہیں پر جھٹلاتے ہیں سچے دین کو جب اُن تک پہنچا سو وہ پڑ رہے ہیں الجھی ہوئی بات میں ');
INSERT INTO "al_hind" VALUES(50,6,'50:6','کیا نہیں دیکھتے آسمان کو اپنے اوپر کیساہم نے اسکو بنایا اور رونق دی اور اس میں نہیں کوئی سوراخ ');
INSERT INTO "al_hind" VALUES(50,7,'50:7','اور زمین کوپھیلایا اور ڈالے اس میں بوجھ اور اگائی اس میں ہر ہر قسم کی رونق کی چیز');
INSERT INTO "al_hind" VALUES(50,8,'50:8','سُجھانے کو اور یاد دلانے کو اُس بندہ کے لئے جو رجوع کرے ');
INSERT INTO "al_hind" VALUES(50,9,'50:9','اور اتارا ہم نے آسمان سے پانی برکت کا پھر اگائے ہم نے اُس سے باغ اور اناج جس کا کھیت کاٹا جاتا ہے ');
INSERT INTO "al_hind" VALUES(50,10,'50:10','اور کھجوریں لنبی اُن کا خوشہ ہے تہ بہ تہ ');
INSERT INTO "al_hind" VALUES(50,11,'50:11','روزی دینے کو بندوں کے اور زندہ کیا ہم نے اُس سے ایک مردہ دیس کو یونہی ہو گا نکل کھڑے ہونا ');
INSERT INTO "al_hind" VALUES(50,12,'50:12','جھٹلا چکے ہیں اُن سے پہلے نوح کی قوم اور کنوے والے اور ثمود');
INSERT INTO "al_hind" VALUES(50,13,'50:13','اور عاد اور فرعون اور لوط کے بھائی');
INSERT INTO "al_hind" VALUES(50,14,'50:14','اور بن کے رہنے والے اور تبع کی قوم ان سب نے جھٹلایا رسولوں کو پر ٹھیک پڑا میرا ڈرانا ');
INSERT INTO "al_hind" VALUES(50,15,'50:15','اب کیا ہم تھک گئے پہلی بار بنا کر کوئی نہیں اُنکو دھوکا ہے ایک نئے بنانے میں ');
INSERT INTO "al_hind" VALUES(50,16,'50:16','اور البتہ ہم نے بنایا انسان کو اور ہم جانتےہیں جو باتیں آتی رہتی ہیں اُسکے جی میں اور ہم اُس سے نزدیک ہیں دھڑکتی رگ سے زیادہ ');
INSERT INTO "al_hind" VALUES(50,17,'50:17','جب لیتے لجاتے ہیں دو لینے والے داہنے بیٹھا اور بائیں بیٹھا ');
INSERT INTO "al_hind" VALUES(50,18,'50:18','نہیں بولتا کچھ بات جو نہیں ہوتا اُس کے پاس ایک راہ دیکھنے والا تیار ');
INSERT INTO "al_hind" VALUES(50,19,'50:19','اور وہ آئی بیہوشی موت کی تحقیق یہ وہ ہے جس سے تو ٹلتا رہتا تھا ');
INSERT INTO "al_hind" VALUES(50,20,'50:20','اور پھونکا گیا صور یہ ہے دن ڈرانے کا ');
INSERT INTO "al_hind" VALUES(50,21,'50:21','اور آیا ہر ایک جی اس کے ساتھ ہے ایک ہانکنے والا ایک احوال بتانے والا ');
INSERT INTO "al_hind" VALUES(50,22,'50:22','تو بیخبر رہا اس دن سے اب کھول دی ہے ہم نے تجھ پر سے تیری اندھیری سو تیری نگاہ آج تیز ہے ');
INSERT INTO "al_hind" VALUES(50,23,'50:23','اور بولا فرشتہ اسکے ساتھ والا یہ ہے جو میرے پاس تھا حاضر ');
INSERT INTO "al_hind" VALUES(50,24,'50:24','ڈال دو تم دونوں دوزخ میں ہر ناشکر مخالف کو');
INSERT INTO "al_hind" VALUES(50,25,'50:25','نیکی سے روکنے والا حد سے بڑھنے والا شبہ ڈالنے والا ');
INSERT INTO "al_hind" VALUES(50,26,'50:26','جس نے ٹھہرایا اللہ کے ساتھ اور کو پوجنا سو ڈال دو اُسکو سخت عذاب میں ');
INSERT INTO "al_hind" VALUES(50,27,'50:27','بولا شیطان اُس کا ساتھی اے رب ہمارےمیں نے اُسکو شرارت میں نہیں ڈالا پر یہ تھا راہ کو بھولا دور پڑا ہوا ');
INSERT INTO "al_hind" VALUES(50,28,'50:28','فرمایا جھگڑا نہ کرو میرے پاس اور میں پہلےہی ڈرا چکا تھا تم کو عذاب سے ');
INSERT INTO "al_hind" VALUES(50,29,'50:29','بدلتی نہیں بات میرے پاس اور میں ظلم نہیں کرتا بندوں پر ');
INSERT INTO "al_hind" VALUES(50,30,'50:30','جس دن ہم کہیں دوزخ کو تو بھر بھی چکی اور وہ بولے کچھ اور بھی ہے ');
INSERT INTO "al_hind" VALUES(50,31,'50:31','اور نزدیک لائی جائے بہشت ڈرنے والوں کے واسطے دور نہیں ');
INSERT INTO "al_hind" VALUES(50,32,'50:32','یہ ہے جس کا وعدہ ہوا تھا تم سے ہر ایک رجوع رہنے والے یاد رکھنے والے کے واسطے');
INSERT INTO "al_hind" VALUES(50,33,'50:33','جو ڈرا رحمٰن سے بن دیکھے اور لایا دل رجوع ہونے والا');
INSERT INTO "al_hind" VALUES(50,34,'50:34','چلے جاؤ اس میں سلامت یہ دن ہے ہمیشہ رہنے کا ');
INSERT INTO "al_hind" VALUES(50,35,'50:35','اُنکے واسطے ہے وہاں جو چاہیں اور ہمارے پاس ہے کچھ دیا وہ بھی ');
INSERT INTO "al_hind" VALUES(50,36,'50:36','اور کتنی تباہ کر چکے ہم ان سے پہلے جماعتیں کہ انکی قوت زبردست تھی اُن سے پھر لگے کریدنے شہروں میں کہیں ہے بھاگ جانے کو ٹھکانہ ');
INSERT INTO "al_hind" VALUES(50,37,'50:37','اس میں سوچنے کی جگہ ہے اُسکو جسکے اندر دل ہے یا لگائے کان دل لگا کر ');
INSERT INTO "al_hind" VALUES(50,38,'50:38','اور ہم نے بنائے آسمان اور زمین اور جو کچھ اُن کے بیچ میں ہے چھ دن میں اور ہم کو نہ ہوا کچھ تکان ');
INSERT INTO "al_hind" VALUES(50,39,'50:39','سو تو سہتا رہ جو کچھ وہ کہتے ہیں اور پاکی بولتا رہ خوبیاں اپنے رب کی پہلے سورج نکلنے سے اور پہلے ڈوبنے سے');
INSERT INTO "al_hind" VALUES(50,40,'50:40','اور کچھ رات میں بول اسکی پاکی اور پیچھے سجدہ کے ');
INSERT INTO "al_hind" VALUES(50,41,'50:41','اور کان رکھ جس دن پکارے پکارنے والا نزدیک کی جگہ سے ');
INSERT INTO "al_hind" VALUES(50,42,'50:42','جس دن سُنیں گے چنگھاڑ محقق وہ ہے دن نکل پڑنے کا ');
INSERT INTO "al_hind" VALUES(50,43,'50:43','ہم ہیں جِلاتے اور مارتے اور ہم تک ہے سب کو پہنچنا ');
INSERT INTO "al_hind" VALUES(50,44,'50:44','جس دن زمین پھٹ کر نکل پڑیں وہ سب دوڑتے ہوئے یہ اکٹھا کرنا ہم کو آسان ہے ');
INSERT INTO "al_hind" VALUES(50,45,'50:45','ہم خوب جانتے ہیں جو کچھ وہ کہتے ہیں اور تو نہیں ہے اُن پر زور کرنے والا سو توسجھا قرآن سے اُس کو جو ڈرے میرے ڈرانے سے ');
INSERT INTO "al_hind" VALUES(51,1,'51:1','قسم ہے اُن ہواؤں کی جو بکھیرتی ہیں اڑا کر');
