INSERT INTO "al_hind" VALUES(51,6,'51:6','اور بیشک انصاف ہونا ضرور ہے ');
INSERT INTO "al_hind" VALUES(51,7,'51:7','قسم ہے آسمان جالدار (جالیدار) کی ');
