INSERT INTO "al_hind" VALUES(51,9,'51:9','آُس سے باز رہے وہی جو پھیرا گیا ');
INSERT INTO "al_hind" VALUES(51,10,'51:10','مارے پڑے اٹکل دوڑانے والے ');
INSERT INTO "al_hind" VALUES(51,11,'51:11','وہ جو غفلت میں ہیں بھول رہے ');
INSERT INTO "al_hind" VALUES(51,12,'51:12','پوچھتے ہیں کب ہے دن انصاف کا ');
INSERT INTO "al_hind" VALUES(51,13,'51:13','جس دن وہ آگ پر الٹے سیدھے پڑیں گے');
INSERT INTO "al_hind" VALUES(51,14,'51:14','چکھو مزا اپنی شرارت کا یہ ہے جس کی تم جلدی کرتے تھے ');
INSERT INTO "al_hind" VALUES(51,15,'51:15','البتہ ڈرنے والے باغوں میں ہیں اور چشموں میں');
INSERT INTO "al_hind" VALUES(51,16,'51:16','لیتے ہیں جو دیا اُنکو اُنکے رب نے وہ تھے اس سے پہلے نیکی والے ');
INSERT INTO "al_hind" VALUES(51,17,'51:17','وہ تھے رات کو تھوڑا سوتے');
INSERT INTO "al_hind" VALUES(51,18,'51:18','اور صبح کے وقتوں میں معافی مانگنے والے ');
INSERT INTO "al_hind" VALUES(51,19,'51:19','اور اُنکے مال میں حصہ تھا مانگنے والوں کا اور ہارے ہوئے کا ');
INSERT INTO "al_hind" VALUES(51,20,'51:20','اور زمین میں نشانیاں ہیں یقین لانے والوں کے واسطے');
INSERT INTO "al_hind" VALUES(51,21,'51:21','اور خود تمہارے اندر سو کیا تم کو سوجھتا نہیں ');
INSERT INTO "al_hind" VALUES(51,22,'51:22','اور آسمان میں ہے روزی تمہاری اور جو تم سے وعدہ کیا گیا ');
INSERT INTO "al_hind" VALUES(51,23,'51:23','سو قسم ہے رب آسمان اور زمین کی کہ یہ بات تحقیق ہے جیسے کہ تم بولتے ہو ');
INSERT INTO "al_hind" VALUES(51,24,'51:24','کیا پہنچی ہے تجھ کو بات ابراہیم کے مہمانوں کی جو عزت والے تھے ');
INSERT INTO "al_hind" VALUES(51,25,'51:25','جب اندر پہنچے اُسکے پاس تو بولے سلام وہ بولا سلام ہے یہ لوگ ہیں اوپرے ');
INSERT INTO "al_hind" VALUES(51,26,'51:26','پھر دوڑا اپنے گھر کو تو لے آیا ایک بچھڑا گھی میں تلا ہوا');
INSERT INTO "al_hind" VALUES(51,27,'51:27','پھر اُنکے سامنے رکھا کہا کیوں تم کھاتے نہیں ');
INSERT INTO "al_hind" VALUES(51,28,'51:28','پھر جی میں گھبرایا اُن کے ڈر سے بولے تو مت ڈر اور خوشخبری دی اُسکو ایک لڑکے ہوشیار کی ');
INSERT INTO "al_hind" VALUES(51,29,'51:29','پھر سامنے سے آئی اسکی عورت بولتی ہوئی پھر پیٹا اپنا ماتھا اور کہنے لگی کہیں بڑھیا بانجھ ');
INSERT INTO "al_hind" VALUES(51,30,'51:30','وہ بولے یوں ہی کہا تیرے رب نے وہ جو ہے وہی ہے حکمت والا خبردار ');
INSERT INTO "al_hind" VALUES(51,31,'51:31','بولا پھر کیا مطلب ہے تمہارا اے بھیجے ہوؤ ');
