INSERT INTO "al_hind" VALUES(51,38,'51:38','اور نشانی ہے موسٰی کے حال میں جب بیجھا ہم نے اُسکو فرعون کے پاس دے کر کھلی سند ');
INSERT INTO "al_hind" VALUES(51,39,'51:39','پھر اُس نے منہ موڑ لیا اپنے زور پر اور بولا یہ جادوگر ہے یا دیوانہ ');
INSERT INTO "al_hind" VALUES(51,40,'51:40','پھر پکڑا ہم نے اُسکو اور اُسکے لشکروں کو پھر پھینک دیا اُنکو دریا میں اور اُس پر لگا الزام ');
INSERT INTO "al_hind" VALUES(51,41,'51:41','اور نشانی ہے عاد میں جب بھیجی ہم نے اُن پر ہوا خیر سے خالی');
INSERT INTO "al_hind" VALUES(51,42,'51:42','نہیں چھوڑتی کسی چیز کو جس پر گذرے کہ نہ کر ڈالے اسکو جیسے چورا ');
INSERT INTO "al_hind" VALUES(51,43,'51:43','اور نشانی ہے ثمود میں جب کہا اُنکو برت لو ایک وقت تک ');
INSERT INTO "al_hind" VALUES(51,44,'51:44','پھر شرارت کرنے لگے اپنے رب کے حکم سے پھر پکڑا اُنکو کڑک نے اور وہ دیکھتے تھے');
INSERT INTO "al_hind" VALUES(51,45,'51:45','پھر نہ ہو سکا اُن سے کہ اٹھیں اور نہ ہوئے کہ بدلا لیں ');
INSERT INTO "al_hind" VALUES(51,46,'51:46','اور ہلاک کیا نوح کی قوم کو اس سے پہلے تحقیق وہ تھے لوگ نافرمان ');
INSERT INTO "al_hind" VALUES(51,47,'51:47','اور بنایا ہم نے آسمان ہاتھ کے بل سے اور ہم کو سب مقدور ہے ');
INSERT INTO "al_hind" VALUES(51,48,'51:48','اور زمین کو بچھایا ہم نے سو کیا خوب بچھانا جانتے ہیں ہم ');
INSERT INTO "al_hind" VALUES(51,49,'51:49','اور ہر چیز کے بنائے ہم نے جوڑے تاکہ تم دھیان کرو ');
INSERT INTO "al_hind" VALUES(51,50,'51:50','سو بھاگو اللہ کی طرف میں تم کو اُسکی طرف سے ڈر سناتا ہوں کھول کر');
INSERT INTO "al_hind" VALUES(51,51,'51:51','اور مٹ ٹھہراؤ اللہ کے ساتھ اور کسی کو معبود میں تم کو اُسکی طرف سے ڈر سناتا ہوں کھول کر ');
INSERT INTO "al_hind" VALUES(51,52,'51:52','اسی طرح ان سے پہلے لوگوں کے پاس جو رسول آیا اُس کو یہی کہا کہ جادوگر ہے یا دیوانہ ');
INSERT INTO "al_hind" VALUES(51,53,'51:53','کیا یہی وصیت کر مرےہیں ایک دوسرے کو کوئی نہیں پر یہ لوگ شریر ہیں ');
