INSERT INTO "al_hind" VALUES(51,59,'51:59','سو ان گنہگاروں کا بھی ڈول بھر چکا ہے جیسے ڈول بھرا اُنکے ساتھیوں کا اب مجھ سے جلدی نہ کریں ');
INSERT INTO "al_hind" VALUES(51,60,'51:60','سو خرابی ہے منکروں کو اُنکے اس دن سے جس کا اُن سے وعدہ ہو چکا ہے ');
