INSERT INTO "al_hind" VALUES(52,18,'52:18','میوے کھاتے ہوئے جو اُنکو دیے اُنکے رب نے اور بچایا اُنکے رب نے دوزخ کے عذاب سے ');
INSERT INTO "al_hind" VALUES(52,19,'52:19','کھاؤ اور پیو رچتا ہوا بدلا اُن کاموں کا جو تم کرتے تھے');
INSERT INTO "al_hind" VALUES(52,20,'52:20','تکیہ لگائے بیٹھے تختوں پر برابر بچھے ہوئے قطار باندھ کر اور بیاہ دیں ہم اُنکو حوریں بڑی آنکھوں والیاں');
INSERT INTO "al_hind" VALUES(52,21,'52:21','اور جو لوگ یقین لائے اور اُنکی راہ پر چلی اُنکی اولاد ایمان سے پہنچا دیا ہم نے اُن تک اُنکی اولاد کو اور گھٹایا نہیں ہم نے اُن سے ان کا کیا ذرا بھی ہر آدمی اپنی کمائی میں پھنسا ہے ');
INSERT INTO "al_hind" VALUES(52,22,'52:22','اور تار لگا دیا ہم نے اُن پر میووں کا اور گوشت کا جس چیز کو جی چاہے ');
