INSERT INTO "al_hind" VALUES(52,48,'52:48','اور تو ٹھہرا رہ منتظر اپنے رب کے حکم کا تو تو ہماری آنکھوں کے سامنے ہے اور پاکی بیان کر اپنے رب کی خوبیاں جس وقت تو اٹھتا ہے ');
INSERT INTO "al_hind" VALUES(52,49,'52:49','اور کچھ رات میں بول اُسکی پاکی اور پیٹھ پھیرتے وقت تاروں کے ');
