INSERT INTO "al_hind" VALUES(56,79,'56:79','اُس کو وہی چھوتے ہیں جو پاک بنائے گئے ہیں ');
INSERT INTO "al_hind" VALUES(56,80,'56:80','اتارا ہوا ہے اپنے پروردگار عالم کی طرف سے ');
INSERT INTO "al_hind" VALUES(56,81,'56:81','اب کیا اس بات میں تم سستی کرتے ہو');
INSERT INTO "al_hind" VALUES(56,82,'56:82','اور اپنا حصہ تم یہی لیتے ہو کہ اُسکو جھٹلاتے ہو ');
INSERT INTO "al_hind" VALUES(56,83,'56:83','پھر کیوں نہیں جس وقت جان پہنچے حلق کو');
INSERT INTO "al_hind" VALUES(56,84,'56:84','اور تم اُس وقت دیکھ رہے ہو');
INSERT INTO "al_hind" VALUES(56,85,'56:85','اور ہم اُسکے پاس ہیں تم سے زیادہ پر تم نہیں دیکھتے');
INSERT INTO "al_hind" VALUES(56,86,'56:86','پھر کیوں نہیں اگر تم نہیں ہو کسی کے حکم میں');
INSERT INTO "al_hind" VALUES(56,87,'56:87','تو کیوں نہیں پھیر لیتے اُس روح کو اگر ہو تم سچے ');
INSERT INTO "al_hind" VALUES(56,88,'56:88','سو جو اگر وہ مردہ ہوا مقرب لوگوں میں');
INSERT INTO "al_hind" VALUES(56,89,'56:89','تو راحت ہے اور روزی ہے اور باغ نعمت کا');
INSERT INTO "al_hind" VALUES(56,90,'56:90','اور جو اگر وہ ہوا داہنے والوں میں');
INSERT INTO "al_hind" VALUES(56,91,'56:91','تو سلامتی پہنچے تجھ کو داہنے والوں سے ');
INSERT INTO "al_hind" VALUES(56,92,'56:92','اور جو اگر وہ ہوا جھٹلانے والوں بہکنے والوں میں سے');
INSERT INTO "al_hind" VALUES(56,93,'56:93','تو مہمانی ہے جلتا پانی');
INSERT INTO "al_hind" VALUES(56,94,'56:94','اور ڈالنا آگ میں ');
INSERT INTO "al_hind" VALUES(56,95,'56:95','بیشک یہ بات یہی ہے لائق یقین کے ');
INSERT INTO "al_hind" VALUES(56,96,'56:96','سو بول پاکی اپنے رب کے نام سے جو سب سے بڑا ہے ');
INSERT INTO "al_hind" VALUES(57,1,'57:1','اللہ کی پاکی بولتا ہے جو کچھ ہے آسمانوں میں اور زمین میں اور وہی ہے زبردست حکمتوں والا');
INSERT INTO "al_hind" VALUES(57,2,'57:2','اُسی کے لئے ہے راج آسمانوں کا اور زمین کا جِلاتا ہے اور مارتا ہے اور وہ سب کچھ کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(57,3,'57:3','وہی ہے سب سے پہلا اور سب سے پچھلا اور باہر اور اندر اور وہ سب کچھ جانتا ہے ');
INSERT INTO "al_hind" VALUES(57,4,'57:4','وہی ہے جس نے بنائے آسمان اور زمین چھ دن میں پھر قائم ہوا تخت پر جانتا ہے جو اندر جاتا ہے زمین کے اور جو اُس سے نکلتا ہے اور جو کچھ اترتا ہے آسمان سے اور جو کچھ اُس میں چڑھتا ہے اور وہ تمہارے ساتھ ہے جہاں کہیں تم ہو اور اللہ جو تم کرتے ہو اُسکو دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(57,5,'57:5','اُسی کے لئے ہے راج آسمانوں کا اور زمین کا اور اللہ ہی تک پہنچتے ہیں سب کام ');
INSERT INTO "al_hind" VALUES(57,6,'57:6','داخل کرتا ہے رات کو دن مین اور داخل کرتا ہے دن کو رات میں اور اُسکو خبر ہے جیوں کی بات کی ');
INSERT INTO "al_hind" VALUES(57,7,'57:7','یقین لاؤ اللہ پر اور اُسکے رسول پر اور خرچ کرو اُس میں سے جو تمہارے ہاتھ میں دیا ہے اپنا نائب کر کر سو جو لوگ تم میں یقین لائے ہیں اور خرچ کرتے ہیں اُنکو بڑا ثواب ہے ');
INSERT INTO "al_hind" VALUES(57,8,'57:8','اور تم کو کیا ہوا کہ یقین نہیں لاتے اللہ پر اور رسول بلاتا ہے تم کو کہ یقین لاؤ اپنے رب پر اور لے چکا ہے تم سے عہد پکا اگر ہو تم ماننے والے ');
INSERT INTO "al_hind" VALUES(57,9,'57:9','وہی ہے جو اتارتا ہے اپنے بندے پر آیتیں صاف کہ نکال لائے تم کو اندھیروں سے اجالے میں اور اللہ تم پر نرمی کرنے والا ہے مہربان ');
INSERT INTO "al_hind" VALUES(57,10,'57:10','اور تم کو کیا ہوا ہے کہ خرچ نہیں کرتے اللہ کی راہ میں اور اللہ ہی کو بچ رہتی ہر شے آسمانوں میں اور زمین میں برابر نہیں تم میں جس نے کہ خرچ کیا فتح مکہ سے پہلے اور لڑائی کی اُن لوگوں کا درجہ بڑا ہے اُن سے جو کہ خرچ کریں اُسکے بعد اور لڑائی کریں اور سب سے وعدہ کیا ہے اللہ نے خوبی کا اور اللہ کو خبر ہے جو کچھ تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(57,11,'57:11','کون ہے ایسا کہ قرض دے اللہ کو اچھی طرح پھر وہ اُسکو دونا کر دے اُسکے واسطے اور اُسکو ملے ثواب عزت کا ');
INSERT INTO "al_hind" VALUES(57,12,'57:12','جس دن تو دیکھے ایمان والے مردوں کو اور ایمان والی عورتوں کو دوڑتی ہوئی چلتی ہے اُنکی روشنی اُنکے آگے اور ان کے داہنے خوشخبری ہے تم کو آج کے دن باغ ہیں کہ نیچے بہتی ہیں جنکے نہریں سدا رہو اُن میں یہ جو ہے یہی ہے بڑی مراد ملنی ');
INSERT INTO "al_hind" VALUES(57,13,'57:13','جس دن کہیں گے دغاباز مرد اور عورتیں ایمان والوں کو راہ دیکھو ہماری ہم بھی روشنی لے لیں تمہارے نور سے کہ کوئی کہے گا لوٹ جاؤ پیچھے پھر ڈھونڈ لو روشنی پھر کھڑی کر دی جائے اُنکے بیچ میں ایک دیوار جس میں ہوگا دروازہ اُس کے اندر رحمت ہو گی اور باہر کی طرف عذاب ');
INSERT INTO "al_hind" VALUES(57,14,'57:14','یہ اُن کو پکاریں گے کیا ہم نہ تھے تمہارے ساتھ کہیں گے کیوں نہیں لیکن تم نے بچلا دیا اپنے آپ کو اور راہ دیکھتے رہے اور دھوکے میں پڑے اور بہک گئے اپنے خیالوں پر یہاں تک کہ آ پہنچا حکم اللہ کا اور تم کو بہکا دیا اللہ کے نام سے اُس دغاباز نے ');
INSERT INTO "al_hind" VALUES(57,15,'57:15','سو آج تم سے قبول نہ ہو گا فدیہ دینا اور نہ منکروں سے تم سب کا گھر دوزخ ہے وہی ہے رفیق تمہاری اور بری جگہ جا پہنچے ');
INSERT INTO "al_hind" VALUES(57,16,'57:16','کیا وقت نہیں آیا ایمان والوں کو کہ گڑگڑائیں اُنکے دل اللہ کی یاد سے اور جو اترا ہے سچا دین اور نہ ہوں اُن جیسے جنکو کتاب ملی تھی اس سے پہلے پھر دراز گذری ان پر مدت پھر سخت ہو گئے اُنکے دل اور بہت اُن میں نافرمان ہیں ');
INSERT INTO "al_hind" VALUES(57,17,'57:17','جان رکھو کہ اللہ زندہ کرتا ہے زمین کو اُسکے مرجانے کے بعد ہم نے کھول کر سنا دیے تم کو پتے اگر تم کو سمجھ ہے ');
INSERT INTO "al_hind" VALUES(57,18,'57:18','تحقیق جو لوگ خیرات کرنے والے ہیں مرد اور عورتیں اور قرض دیتے ہیں اللہ کو اچھی طرح اُنکو ملتا ہے دونا اور اُنکو ثواب ہے عزت کا ');
INSERT INTO "al_hind" VALUES(57,19,'57:19','اور جو لوگ یقین لائے اللہ پر اور اُسکے سب رسولوں پر وہی ہیں سچے ایمان والے اور لوگوں کا احوال بتلانے والے اپنے رب کے پاس اُنکے واسطے ہے اُں کا ثواب اور اُنکی روشنی اور جو لوگ منکر ہوئے اور جھٹلایا ہماری باتوں کو وہ ہیں دوزخ کے لوگ ');
INSERT INTO "al_hind" VALUES(57,20,'57:20','جان رکھو کہ دنیا کی زندگانی یہی ہے کھیل اور تماشا اور بناؤ اور بڑائیاں کرنی آپس میں اور بہتایت ڈھونڈنی مال کی اور اولاد کی جیسے حالت ایک مینہ کی جو خوش لگا کسانوں کو اُس کا سبزہ پھر زور پر آتا ہے پھر تو دیکھے زرد ہو گیا پھر ہو جاتا ہے روندا ہوا گھاس اور آخرت میں سخب عذاب ہے اور معافی بھی ہے اللہ سے اور رضامندی اور دنیا کی زندگانی تو یہی ہے مال دغا کا ');
INSERT INTO "al_hind" VALUES(57,21,'57:21','دوڑو اپنے رب کی معافی کی طرف کو اور بہشت کو جس کا پھیلاؤ ہے جیسے پھیلاؤ آسمان اور زمین کا تیار رکھی ہے واسطے اُنکے جو یقین لائے اللہ پر اور اسکے رسولوں پر یہ فضل اللہ کا ہے دے اُسکو جسکو چاہے اور اللہ کا فضل بڑا ہے ');
INSERT INTO "al_hind" VALUES(57,22,'57:22','کوئی آفت نہیں پڑتی ملک میں اور نہ تمہاری جانوں میں جو لکھی نہ ہو ایک کتاب میں پہلے اس سےکہ پیدا کریں ہم اُسکو دنیا میں بیشک یہ اللہ پر آسان ہے ');
INSERT INTO "al_hind" VALUES(57,23,'57:23','تاکہ تم غم نہ کھایا کرو اُس پر جو ہاتھ نہ آیا اور نہ شیخی کیا کرو اُس پر جو تم کو اُس نے دیا اور اللہ کو خوش نہیں آتا کوئی اترانے والا بڑائی مارنے والا');
INSERT INTO "al_hind" VALUES(57,24,'57:24','وہ جو کہ آپ نہ دیں اور سکھلائیں لوگوں کو بھی نہ دینا اور جو کوئی منہ موڑے تو اللہ آپ ہے بے پروا سب خوبیوں کے ساتھ موصوف ');
INSERT INTO "al_hind" VALUES(57,25,'57:25','ہم نے بھیجے ہیں اپنے رسول نشانیاں دیکر اور اتاری اُنکے ساتھ کتاب اور ترازو تاکہ لوگ سیدھے رہیں انصاف پر اور ہم نے اتارا لو ہا اُس میں سخت لڑائی ہے اور لوگوں کے کام چلتے ہیں اور تاکہ معلوم کرے اللہ کون مدد کرتا ہے اُسکے رسولوں کی بن دیکھے بیشک اللہ زوآور ہے زبردست ');
INSERT INTO "al_hind" VALUES(57,26,'57:26','اور ہم نے بھیجا نوح کو اور ابراہیم کو اور ٹھہرا دی دونوں کی اولاد میں پیغمبری اور کتاب پھر کوئی اُن میں راہ پر ہے اور بہت اُن میں نافرمان ہیں ');
INSERT INTO "al_hind" VALUES(57,27,'57:27','پھر پیچھے بھیجے اُنکے قدموں پر اپنے رسول اور پیچھے بھیجا ہم نے عیسٰی مریم کے بیٹے کو اور اُسکو ہم نے دی انجیل اور رکھ دی اُس کے ساتھ چلنے والوں کے دل میں نرمی اور مہربانی اور ایک ترک کرنا دنیا کا جو انہوں نے نئی بات نکالی تھی ہم نے نہیں لکھا تھا یہ اُن پر مگر کیاچاہنے کو اللہ کی رضامندی پھر نہ نباہا اُسکو جیسا چاہئے تھا نباہنا پھر دیا ہم نے اُن لوگوں کو جو اُن میں ایماندار تھے اُنکا بدلا اور بہت اُن میں نافرمان ہں ');
INSERT INTO "al_hind" VALUES(57,28,'57:28','اے یمان والو ڈرتے رہو اللہ سے اور یقین لاؤ اُسکے رسول پر دے گا تم کو دو حصے اپنی رحمت سے اور رکھ دے گا تم میں روشنی جس کو لئے پھرو اور تم کو معاف کرے گا اور اللہ معاف کرنے والا ہے مہربان ');
INSERT INTO "al_hind" VALUES(57,29,'57:29','تا کہ نہ جانیں کتاب والے کہ پا نہیں سکتے کوئی چیز اللہ کے فضل میں سے اور یہ کہ بزرگی اللہ کے ہاتھ ہے دیتا ہے جسکو چاہے اور اللہ کا فضل بڑا ہے ');
INSERT INTO "al_hind" VALUES(58,1,'58:1','سن لی اللہ نے بات اُس عورت کی جو جھگڑتی تھی تجھ سے اپنے خاوند کے حق میں اور جھینکتی تھی اللہ کے آگے اور اللہ سنتا تھا سوال و جواب تم دونوں کا بیشک اللہ سنتا ہے دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(58,2,'58:2','جو لوگ ماں کہہ بیٹھیں تم میں سے اپنی عورتوں کو وہ نہیں ہو جاتیں اُنکی مائیں اُنکی مائیں تو وہی ہیں جنہوں نے اُنکو جنا اور وہ بولتے ہیں ایک ناپسند بات اور جھوٹی اور اللہ معاف کرنے والا بخشنے والا ہے ');
INSERT INTO "al_hind" VALUES(58,3,'58:3','اور جو لوگ ماں کہہ بیٹھیں اپنی عورتوں کو پھر کرنا چاہیں وہی کام جسکو کہا ہے تو آزاد کرنا چاہئے ایک بردہ پہلے اس سے کہ آپس میں ہاتھ لگائیں اس سے تم کو نصیحت ہو گی اور اللہ خبر رکھتا ہے جو کچھ تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(58,4,'58:4','پھو جو کوئی نہ پائے تو روزے ہیں دو مہینے کے لگاتار پہلے اس سے کہ آپس میں چھوئیں پھر جو کوئی یہ نہ کر سکے تو کھانا دینا ہے ساٹھ محتاجوں کا یہ حکم اس واسطے کہ تابعدار ہو جاؤ اللہ کے اور اُسکے رسول کے اور یہ حدیں باندھی ہیں اللہ کی اور منکروں کے واسطے عذاب ہے دردناک');
INSERT INTO "al_hind" VALUES(58,5,'58:5','جو لوگ کہ مخالفت کرتے ہیں اللہ کی اور اُسکے رسول کی وہ خوار ہوئے جیسے کہ خوار ہوئے ہیں وہ لوگ جو اُن سے پہلے تھے اور ہم نے اتاری ہیں آیتیں بہت صاف اور منکروں کے واسطے عذاب ہے ذلت کا ');
INSERT INTO "al_hind" VALUES(58,6,'58:6','جس دن کہ اٹھائے گا اللہ ان سب کو پھر جتلائے گا اُنکو اُنکے کئے کام اللہ نے وہ سب گن رکھی ہیں اور وہ بھول گئے اور اللہ کے سامنے ہے ہر چیز ');
INSERT INTO "al_hind" VALUES(58,7,'58:7','تو نے نہیں دیکھا کہ اللہ کو معلوم ہے جو کچھ ہے آسمانوں میں اور جو کچھ ہے زمین میں کہیں نہیں ہوتا مشورہ تین کا جہاں وہ نہیں ہوتا ان میں چوتھا اور نہ پانچ کا جہاں وہ نہیں ہوتا اُن میں چھٹا اور نہ اُس سے کم اور نہ زیادہ جہاں وہ نہیں ہوتا اُنکے ساتھ جہاں کہیں ہوں پھرجتلا دے گا اُنکو جو کچھ انہوں نے کیا قیامت کے دن بیشک اللہ کو معلوم ہے ہر چیز');
INSERT INTO "al_hind" VALUES(58,8,'58:8','تو نے نہ دیکھا اُن لوگوں کو جنکو منع ہوئی کانا پھوسی پھر بھی وہی کرتے ہیں جو منع ہو چکا ہے اور کان میں باتیں کرتے ہیں گناہ کی اور زیادتی کی اور رسول کی نافرمانی کی اور جب آئیں تیرے پاس تجھ کو وہ دعا دیں جو دعا نہیں دی تجھ کو اللہ نے اور کہتے ہیں اپنے دل میں کیوں نہیں عذاب کرتا ہم کو اللہ اُس پر جو ہم کہتے ہیں کافی ہے اُنکو دوزخ داخل ہوں گے اس میں سو بری جگہ پہنچے ');
INSERT INTO "al_hind" VALUES(58,9,'58:9','اے ایمان والو جب تم کان میں بات کرو تو مت کرو بات گناہ کی اور زیادتی کی اور رسول کی نافرمانی کی اور بات کرو احسان کی اور پرہیزگاری کی اور ڈرتے رہو اللہ سے جسکے پاس تم کو جمع ہو نا ہے ');
INSERT INTO "al_hind" VALUES(58,10,'58:10','یہ جو ہے کانا پھوسی سو شیطان کا کام ہے تاکہ دلگیر کرے ایمان والوں کو اور وہ اُن کا کچھ نہ بگاڑے گا بدون اللہ کے حکم کے اور اللہ پر چاہئے کہ بھروسہ کریں ایمان والے ');
INSERT INTO "al_hind" VALUES(58,11,'58:11','اے ایمان والو جب کوئی تم کو کہے کہ کھل کر بیٹھو مجلسوں میں تو کھل جاؤ اللہ کشادگی دے تم کو اور جب کوئی کہے کہ اٹھ کھڑے ہو تو اٹھ کھڑے ہو اللہ بلند کرے گا اُنکے لئے جو کہ ایمان رکھتے ہیں تم میں سے اور علم انکے درجے اور اللہ کو خبر ہے جو کچھ تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(58,12,'58:12','اے ایمان والو جب تم کان میں بات کہنا چاہو رسول سے تو آگے بھیجو اپنی بات کہنے سے پہلے خیرات یہ بہتر ہے تمہارے حق میں اور بہت ستھرا پھر اگر نہ پاؤ تو اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(58,13,'58:13','کیا تم ڈر گئے کہ آگے بھیجا کرو کان کی بات سے پہلے خیراتیں سو جب تم نے نہ کیا اور اللہ نے معاف کردیا تم کو تو اب قائم رکھو نماز اور دیتے رہو زکوٰۃ اور حکم پر چلو اللہ کے اور اُسکے رسول کے اور اللہ کو خبر ہے جو کچھ تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(58,14,'58:14','کیا تو نے دیکھا اُن لوگوں کو جو دوست ہوئے ہیں اُس قوم کے جن پر غصہ ہوا ہے اللہ کا نہ وہ تم میں ہیں اور نہ اُن میں ہیں اور قسمیں کھاتے ہیں جھوٹ بات پر اور اُنکو خبر ہے ');
INSERT INTO "al_hind" VALUES(58,15,'58:15','تیار رکھا ہے اللہ نے اُنکے لئے سخت عذاب بیشک وہ برے کام ہیں جو وہ کرتے ہیں ');
INSERT INTO "al_hind" VALUES(58,16,'58:16','بنا رکھا ہے اپنی قسموں کو ڈھال پھر روکتےہیں اللہ کی راہ سے تو اُنکو ذلت کا عذاب ہے');
INSERT INTO "al_hind" VALUES(58,17,'58:17','کام نہ آئیں گے اُنکو اُنکے مال اور نہ اُنکی اولاد اللہ کے ہاتھ سے کچھ بھی وہ لوگ ہیں دوزخ کے وہ اُسی میں پڑے رہیں گے ');
INSERT INTO "al_hind" VALUES(58,18,'58:18','جس دن جمع کرے گا اللہ اُن سب کو پھر قسمیں کھائیں گے اُسکے آگے جیسے کھاتے ہیں تمہارے آگے اور خیال کرتے ہیں کہ وہ کچھ بھلی راہ پر ہیں سنتا ہے وہی ہیں اصل جھوٹے ');
INSERT INTO "al_hind" VALUES(58,19,'58:19','قابو کر لیا ہے اُن پر شیطان نے پھر بھلا دی اُنکو اللہ کی یاد وہ لوگ ہیں گروہ شیطان کا سنتا ہے جو گروہ ہے شیطان کا وہی خراب ہوتے ہیں ');
INSERT INTO "al_hind" VALUES(58,20,'58:20','جو لوگ خلاف کرتے ہیں اللہ کا اور اُسکے رسول کا وہ لوگ ہیں سب سے بےقدر لوگوں میں');
INSERT INTO "al_hind" VALUES(58,21,'58:21','اللہ لکھ چکا کہ میں غالب ہوں گا اور میرے رسول بیشک اللہ زورآور ہے زبردست ');
INSERT INTO "al_hind" VALUES(58,22,'58:22','تو نہ پائے گا کسی قوم کو جو یقین رکھتے ہوں اللہ پر اور پچھلے دن پر کہ دوستی کریں ایسوں سے جو مخالف ہوئے اللہ کے اور اُسکے رسول کے خواہ وہ اپنے باپ ہوں یا اپنے بیٹے یا اپنے بھائی یا اپنے گھرانے کے اُنکے دلوں میں اللہ نے لکھ دیا ہے ایمان اور اُنکی مدد کی ہے اپنے غیب کے فیض سے اور داخل کرے گا اُنکو باغوں میں جنکے نیچے بہتی ہیں نہریں ہمیشہ رہیں گے اُن میں اللہ اُن سے راضی اور وہ اُس سے راضی وہ لوگ ہیں گروہ اللہ کا سنتا ہے جو گروہ ہے اللہ کا وہی مراد کو پہنچے ');
INSERT INTO "al_hind" VALUES(59,1,'59:1','اللہ کی پاکی بیان کرتا ہے جو کچھ ہے آسمانوں میں اور زمین میں اور وہی ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(59,2,'59:2','وہی ہے جس نے نکال دیا اُنکو جو منکر ہیں کتاب والوں میں اُنکے گھروں سے پہلے ہی اجتماع پر لشکر کے تم نہ اٹکل کرتے تھے کہ نکلیں گے اور وہ خیال کرتے تھے کہ اُنکو بچا لیں گے اُنکے قلعے اللہ کے ہاتھ سے پھر پہنچا اُن پر اللہ جہاں سے اُنکو خیال نہ تھا اور ڈال دی اُنکے دلوں میں دھاک اجاڑنے لگے اپنے گھر اپنے ہاتھوں اور مسلمانوں کے ہاتھوں سو عبرت پکڑو اے آنکھ والو ');
INSERT INTO "al_hind" VALUES(59,3,'59:3','اور اگر نہ ہوتی یہ بات کہ لکھ دیا تھا اللہ نے اُن پر جلاوطن ہونا تو اُنکو عذاب دیتا دنیا میں اور آخرت میں ہے اُنکے لئے آگ کا عذاب ');
INSERT INTO "al_hind" VALUES(59,4,'59:4','یہ اس لئے کہ وہ مخالف ہوئے اللہ سے اور اُسکے رسول سے اور جو کوئی مخالف ہو اللہ سے تو اللہ کا عذاب سخت ہے ');
INSERT INTO "al_hind" VALUES(59,5,'59:5','جو کاٹ ڈالا تم نے کھجور کا درخت یا رہنے دیا کھڑا اپنی جڑ پر سو اللہ کے حکم سے اور تاکہ رسوا کرے نافرمانوں کو ');
INSERT INTO "al_hind" VALUES(59,6,'59:6','اور جو مال کہ لوٹا دیا اللہ نے اپنے رسول پر اُن سے سو تم نے نہیں دوڑائے اُس پر گھوڑے اور نہ اونٹ ولیکن اللہ غلبہ دیتا ہے اپنے رسولوں کو جس پر چاہے اور اللہ سب کچھ کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(59,7,'59:7','جو مال لوٹایا اللہ نے اپنے رسول پر بستیوں والوں سے سو اللہ کے واسطے اور رسول کے اور قرابت والے کے اور یتیموں کے اور محتاجوں کے اور مسافر کے تاکہ نہ آئے لینے دینے میں دولتمندوں کے تم میں سے اور جو دے تم کو رسول سو لے لو اور جس سے منع کرے سو چھوڑ دو اور ڈرتے رہو اللہ سے بیشک اللہ کا عذاب سخت ہے ');
INSERT INTO "al_hind" VALUES(59,8,'59:8','واسطے ان مفلسوں وطن چھوڑنے والوں کے جو نکالے ہوئے آتے ہیں اپنے گھروں سے اور اپنے مالوں سے ڈھونڈتے آئے ہیں اللہ کا فضل اور اُسکی رضامندی اور مدد کرنے کو اللہ کی اور اسکے رسول کی وہ لوگ وہی ہیں سچے ');
INSERT INTO "al_hind" VALUES(59,9,'59:9','اور جو لوگ جگہ پکڑ رہے ہیں اس گھر میں اور ایمان میں ان سے پہلے سے وہ محبت کرتے ہیں اس سے جو وطن چھوڑ کر آئے انکے پاس اور نہیں پاتے اپنے دل میں تنگی اس چیز سے جو مہاجرین کو دی جائے اور مقدم رکھتے ہیں اُنکو اپنی جان سے اور اگر چہ ہو اپنے اوپر فاقہ اور جو بچایا گیا اپنے جی کے لالچ سے تو وہ لوگ ہیں مراد پانے والے ');
INSERT INTO "al_hind" VALUES(59,10,'59:10','اور واسطے ان لوگوں کے جو آئے اُنکے بعد کہتے ہوئے اے رب بخش ہم کو اور ہمارے بھائیوں کو جو ہم سے پہلے داخل ہوئے ایمان میں اور نہ رکھ ہمارے دلوں میں بیر ایمان والوں کا اے رب تو ہی ہے نرمی والا مہربان ');
INSERT INTO "al_hind" VALUES(59,11,'59:11','کیا تو نے نہیں دیکھا ان لوگوں کو جو دغاباز ہیں کہتے ہیں اپنے بھائیوں کو کافر ہیں اہل کتاب میں سے اگر تم کو کوئی نکال دے گا تو ہم بھی نکلیں گے تمہارےساتھ اور کہا نہ مانیں گے کسی کا تمہارے معاملہ میں کبھی اور اگر تم سے لڑائی ہوئی تو ہم تمہاری مدد کریں گے اور اللہ گواہی دیتا ہے کہ وہ جھوٹے ہیں ');
INSERT INTO "al_hind" VALUES(59,12,'59:12','اگر وہ نکالے جائیں یہ نہ نکلیں گے اُنکے ساتھ اور اگر اُن سےلڑائی ہوئی یہ نہ مدد کریں گے اُنکی اور اگر مدد کریں گے تو بھاگیں گے پیٹھ پھیر کر پھر کہیں نہ مدد پائیں گے ');
INSERT INTO "al_hind" VALUES(59,13,'59:13','البتہ تمہارا ڈر زیادہ ہے اُنکے دلوں میں اللہ کے ڈر سے یہ ا سلئے کہ وہ لوگ سمجھ نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(59,14,'59:14','لڑ نہ سکیں گے تم سے سب مل کر مگر بستیوں کےکوٹ میں یا دیواروں کی اوٹ میں اُنکی لڑائی آپس میں سخت ہے تو سمجھے وہ اکھٹے ہیں اور اُنکے دل جدا جدا ہو رہے ہیں یہ اس لئے کہ وہ لوگ عقل نہیں رکھتے ');
INSERT INTO "al_hind" VALUES(59,15,'59:15','جیسے قصہ اُن لوگوں کا جو ہو چکے ہیں اُن سے پہلے قریب ہی چکھی اُنہوں نے سزا اپنے کام کی اور اُنکے لئے عذاب دردناک ہے ');
INSERT INTO "al_hind" VALUES(59,16,'59:16','جیسے قصہ شیطان کا جب کہے انسان کو تو منکر ہو پھر جب وہ منکر ہو گیا کہے میں الگ ہوں تجھ سے میں ڈرتا ہوں اللہ سے جو رب ہے سارے جہان کا');
INSERT INTO "al_hind" VALUES(59,17,'59:17','پھر انجام دونوں کا یہی کہ وہ دونوں ہیں آگ میں ہمیشہ رہیں اُسی میں اور یہی ہے سزا گنہگاروں کی ');
INSERT INTO "al_hind" VALUES(59,18,'59:18','اے ایمان والو ڈرتے رہو اللہ سے اور چاہیے کہ دیکھ لے ہر ایک جی کیا بھیجتا ہے کل کے واسطے اور ڈرتے رہو اللہ سے بیشک اللہ کو خبر ہے جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(59,19,'59:19','اور مت ہو اُن جیسے جنہوں نے بھلا دیا اللہ کو پر اللہ نے بھلا دیے اُنکو اُن کے جی وہ لوگ وہی ہیں نافرمان ');
INSERT INTO "al_hind" VALUES(59,20,'59:20','برابر نہیں دوزخ والے اور بہشت والے بہشت والے جو ہیں وہی ہیں مراد پانے والے ');
INSERT INTO "al_hind" VALUES(59,21,'59:21','اگر ہم اتارتے یہ قرآن ایک پہاڑ پر تو تُو دیکھ لیتا کہ وہ دب جاتا پھٹ جاتا اللہ کے ڈر سے اور یہ مثالیں ہم سناتے ہیں لوگوں کو تاکہ وہ غور کریں ');
INSERT INTO "al_hind" VALUES(59,22,'59:22','وہ اللہ ہے جس کے سوائے بندگی نہیں کسی کی جانتا ہے جو پوشیدہ ہے اور جو ظاہر ہے وہ ہے بڑا مہربان رحم والا');
INSERT INTO "al_hind" VALUES(59,23,'59:23','وہ اللہ ہے جس کے سوائے بندگی نہیں کسی کی وہ بادشاہ ہے پاک ذات سب عیبوں سے سالم امان دینے والا پناہ میں لینے والا زبردست دباؤ والا صاحب عظمت پاک ہے اللہ اُنکے شریک بتلانے سے ');
INSERT INTO "al_hind" VALUES(59,24,'59:24','وہ اللہ ہے بنانے والا نکال کھڑا کرنے والا صورت کھینچنے والا اُسی کے ہیں سب نام خاصے (عمدہ) پاکی بول رہا ہے اُسکی جو کچھ ہے آسمانوں میں اور زمین میں اور وہی ہے زبردست حکمتوں والا ');
INSERT INTO "al_hind" VALUES(60,1,'60:1','[ا] اے ایمان والو نہ پکڑو میرےاور اپنے دشمنوں کو دوست تم اُنکو پیغام بھیجتےہو دوستی سے اور وہ منکر ہوئے ہیں اُس سے جو تمہارے پاس آیا سچا دین نکالتے ہیں رسول کو اور تم کو اس بات پر کہ تم مانتے ہو اللہ کو جو رب ہے تمہارا اگر تم نکلے ہو لڑنے کو میری راہ میں اور طلب کرنے کو میری رضامندی تم اُنکو چھپا کر بھیجتے ہو دوستی کے پیغام اور مجھ کو خوب معلوم ہے جو چھپایا تم نے اور جو ظاہر کیا تم نے اور جو کوئی تم میں یہ کام کرے تو وہ بھول گیا سیدھی راہ ');
INSERT INTO "al_hind" VALUES(60,2,'60:2','اگر تم اُنکے ہاتھ آ جاؤ ہو جائیں تمہارے دشمن اور چلائیں تم پر اپنے ہاتھ اور اپنی زبانیں برائی کے ساتھ اور چاہیں کہ کسی طرح تم بھی منکر ہو جاؤ ');
INSERT INTO "al_hind" VALUES(60,3,'60:3','ہرگز کام نہ آئیں گے تمہارے کنبے والے اور نہ تمہاری اولاد قیامت کے دن وہ فیصلہ کرے گا تم میں اور اللہ جو تم کرتے ہو دیکھتا ہے ');
INSERT INTO "al_hind" VALUES(60,4,'60:4','تم کو چال چلنی چاہئیے اچھی ابراہیم کی اور جو اُسکے ساتھ تھے جب انہوں نے کہا اپنی قوم کو ہم الگ ہیں تم سے اور اُن سے کہ جنکو تم پوجتے ہو اللہ کے سوائے ہم منکر ہوئے تم سے اور کھل پڑی ہم میں اور تم میں دشمنی اور بیر ہمیشہ کو یہاں تک کہ تم یقین لاؤ اللہ اکیلے پر مگر ایک کہنا ابراہیم کا اپنے باپ کو کہ میں مانگوں گا معافی تیرے لئے اور مالک نہیں میں تیرے نفع کا اللہ کے ہاتھ سے کسی چیز کا اے رب ہمارے ہم نے تجھ پر بھروسہ کیا اور تیری طرف رجوع ہوئے اور تیری طرف ہے سب کو پھر آنا ');
INSERT INTO "al_hind" VALUES(60,5,'60:5','اے رب ہمارے مت جانچ ہم پر کافروں کو اور ہم کو معاف کر اے رب ہمارے تو ہی ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(60,6,'60:6','البتہ تم کو بھلی چال چلنی چاہئے اُنکی جو کوئی امید رکھتا ہو اللہ کی اور پچھلے دن کی اور جو کوئی منہ پھیرے تو اللہ وہی ہے بے پروا سب تعریفوں والا ');
INSERT INTO "al_hind" VALUES(60,7,'60:7','امید ہے کہ کر دے اللہ تم میں اور جو دشمن ہیں تمہارے اُن میں دوستی اور اللہ سب کچھ کر سکتا ہے اور اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(60,8,'60:8','اللہ تم کو منع نہیں کرتا اُن لوگوں سے جو لڑے نہیں تم سے دین پر اور نکالا نہیں تم کو تمہارےگھروں سے کہ اُن سے کرو بھلائی اور انصاف کا سلوک بیشک اللہ چاہتا ہے انصاف والوں کو ');
INSERT INTO "al_hind" VALUES(60,9,'60:9','اللہ تو منع کرتا ہے تم کو اُن سے جو لڑے تم سے دین پر اور نکالا تم کو تمہارے گھروں سے اور شریک ہوئے تمہارے نکالنے میں کہ اُن سے کرو دوستی اور جو کوئی ان سے دوستی کرے سو وہ لوگ وہی ہیں گنہگار ');
INSERT INTO "al_hind" VALUES(60,10,'60:10','اے ایمان والو جب آئیں تمہارے پاس ایمان والی عورتیں وطن چھوڑ کر تو ان کو جانچ لو اللہ خوب جانتا ہے انکے ایمان کو پھر اگر جانو کہ وہ ایمان پر ہیں تو مت پھیرو اُنکو کافروں کی طرف نہ یہ عورتیں حلال ہیں اُن کافروں کو اور نہ وہ کافر حلال ہیں ان عورتوں کو اور دیدو اُن کافروں کو جو ان کا خرچ ہوا ہو اور گناہ نہیں تم کو کہ نکاح کر لو اُن عورتوں سے جب اُنکو دو انُکے مہر اور نہ رکھو اپنے قبضہ میں ناموس کافر عورتوں کے اور تم مانگ لو جو تم نے خرچ کیا اور وہ کافر مانگ لیں جو انہوں نے خرچ کیا یہ اللہ کا فیصلہ ہے تم میں فیصلہ کرتا ہے اور اللہ سب کچھ جاننے والا حکمت والا ہے ');
INSERT INTO "al_hind" VALUES(60,11,'60:11','اور اگر جاتی رہیں تمہارے ہاتھ سے کچھ عورتیں کافروں کی طرف پھر تم ہاتھ مارو تو دیدو اُنکو جن کی عورتیں جاتی رہی ہیں جتنا انہوں نے خرچ کیا تھا اور ڈرتے رہو اللہ سے جس پر تم کو یقین ہے ');
INSERT INTO "al_hind" VALUES(60,12,'60:12','اے نبی جب آئیں تیرے پاس مسلمان عورتیں بیعت کرنے کو اس بات پر کہ شریک نہ ٹھہرائیں اللہ کا کسی کو اور چوری نہ کریں اور بدکاری نہ کریں اور اپنی اولاد کو نہ مار ڈالیں اور طوفان نہ لائیں باندھ کر اپنے ہاتھوں اور پاؤں میں اور تیری نافرمانی نہ کریں کسی بھلے کام میں تو اُنکو بیعت کر لے اور معافی مانگ اُنکے واسطے اللہ سے بیشک اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(60,13,'60:13','اے ایمان والو مت دوستی کرو اُن لوگوں سے کہ غصہ ہوا ہے اللہ اُن پر وہ آس توڑ چکے ہیں پچھلے گھر سے جیسے آس توڑی منکروں نے قبر والوں سے ');
INSERT INTO "al_hind" VALUES(61,1,'61:1','اللہ کی پاکی بولتا ہے جوکچھ ہے آسمانوں میں اور جو کچھ ہے زمین میں اور وہی ہے زبردست حکمت والا');
INSERT INTO "al_hind" VALUES(61,2,'61:2','اے ایمان والو کیوں کہتے ہو منہ سے جو نہیں کرتے');
INSERT INTO "al_hind" VALUES(61,3,'61:3','بڑی بیزاری کی بات ہے اللہ کے یہاں کہ کہو وہ چیز جو نہ کرو');
INSERT INTO "al_hind" VALUES(61,4,'61:4','اللہ چاہتا ہے اُن لوگوں کو جو لڑتے ہیں اُسکی راہ میں قطار باندھ کر گویا وہ دیوار ہیں سیسہ پلائی ہوئی ');
INSERT INTO "al_hind" VALUES(61,5,'61:5','اور جب کہا موسٰی نے اپنی قوم کو اے قوم میری کیوں ستاتے ہو مجھ کو اور تم کو معلوم ہے کہ میں اللہ کا بھیجا آیا ہوں تمہارے پاس پھر جب وہ پھر گئے تو پھیر دیے اللہ نے انکے دل اور اللہ راہ نہیں دیتا نافرمان لوگوں کو ');
INSERT INTO "al_hind" VALUES(61,6,'61:6','اور جب کہا عیسٰی مریم کے بیٹے نے اے بنی اسرائیل میں بھیجا ہوا آیا ہوں اللہ کا تمہارے پاس یقین کرنے والا اُس پر جو مجھ سے آگے ہے توریت اور خوشخبری سنانے والا ایک رسول کی جو آئے گا میرے بعد اُس کا نام ہے احمد پھر جب آیا انکے پاس کھلی نشانیاں لیکر کہنے لگے یہ جادو ہے صریح ');
INSERT INTO "al_hind" VALUES(61,7,'61:7','اور اس سے زیادہ بے انصاف کون جو باندھے اللہ پر جھوٹ اور اُسکو بلاتے ہیں مسلمان ہونے کو اور اللہ راہ نہیں دیتا بے انصاف لوگوں کو ');
INSERT INTO "al_hind" VALUES(61,8,'61:8','چاہتے ہیں کہ بجھا دیں اللہ کی روشنی اپنے منہ سے اور اللہ کو پوری کرنی ہے اپنی روشنی اور پڑے برا مانیں منکر ');
INSERT INTO "al_hind" VALUES(61,9,'61:9','وہی ہے جس نے بھیجا اپنا رسول راہ کی سوجھ دے کر اور سچا دین کہ اس کو اوپر کرے سب دینوں سے اور پڑے برا مانیں شرک کرنے والے ');
INSERT INTO "al_hind" VALUES(61,10,'61:10','اے ایمان والو میں بتلاؤں تم کو ایسی سوداگری جو بچائے تم کو ایک عذاب دردناک سے');
INSERT INTO "al_hind" VALUES(61,11,'61:11','ایمان لاؤ اللہ پر اور اُسکے رسول پر اور لڑو اللہ کی راہ میں اپنے مال سے اور اپنی جان سے یہ بہتر ہے تمہارے حق میں اگر تم سمجھ رکھتے ہو');
INSERT INTO "al_hind" VALUES(61,12,'61:12','بخشے گاہ وہ تمہارے گناہ اور داخل کرے گا تم کو باغوں میں جنکے نیچے بہتی ہیں نہریں اور ستھرے گھروں میں بسنے کے باغوں کے اندر یہ ہے بڑی مراد ملنی');
INSERT INTO "al_hind" VALUES(61,13,'61:13','اور ایک اور چیز دے جس کو تم چاہتے ہو مدد اللہ کی طرف سے اور فتح جلدی اور خوشی سنا دے ایمان والوں کو ');
INSERT INTO "al_hind" VALUES(61,14,'61:14','اے ایمان والو تم ہو جاؤ مددگار اللہ کے جیسے کہا عیسٰی مریم کے بیٹے نے اپنے یاروں کو کون ہے کہ مدد کرے میری اللہ کی راہ میں بولے یار ہم ہیں مددگار اللہ کے پھر ایمان لایا ایک فرقہ بنی اسرائیل سے اور منکر ہوا ایک فرقہ پھر قوت دی ہم نے اُنکو جو ایمان لائے تھے اُنکے دشمنوں پر پھر ہو رہے غالب ');
INSERT INTO "al_hind" VALUES(62,1,'62:1','اللہ کی پاکی بولتا ہے جو کچھ کہ ہے آسمانوں میں اورجو کچھ کہ ہے زمین میں بادشاہ پاک ذات زبردست حکمتوں والا');
INSERT INTO "al_hind" VALUES(62,2,'62:2','وہی ہے جس نے اٹھایا ان پڑھوں میں ایک رسول انہی میں کا پڑھ کر سناتا ہے اُنکو اُسکی آیتیں اور انکو سنوارتا ہے اور سکھلاتا ہے اُنکو کتاب اور عقلمندی اور اس سے پہلے وہ پڑے ہوئے تھے صریح بھول میں ');
INSERT INTO "al_hind" VALUES(62,3,'62:3','اور اٹھایا اس رسول کو ایک دوسرے لوگوں کے واسطے بھی انہی میں سےجو ابھی نہیں ملے ان میں اور وہی ہے زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(62,4,'62:4','یہ بڑائی اللہ کی ہے دیتا ہے جسکو چاہے اور اللہ کا فضل بڑا ہے ');
INSERT INTO "al_hind" VALUES(62,5,'62:5','مثال ان لوگوں کی جن پر لادی توریت پھر نہ اٹھائی انہوں نے جیسے مثال گدھے کی کہ پیٹھ پر لے چلتا ہے کتابیں بری مثال ہے اُن لوگوں کی جنہوں نے جھٹلایا اللہ کی باتوں کو اور اللہ راہ نہیں دیتا بے انصاف لوگوں کو ');
INSERT INTO "al_hind" VALUES(62,6,'62:6','تو کہہ اے یہودی ہونے والو اگر تم کو دعوٰی ہے کہ تم دوست ہو اللہ کے سب لوگوں کے سوائے تو مناؤ اپنے مرنے کو اگر تم سچے ہو');
INSERT INTO "al_hind" VALUES(62,7,'62:7','اور وہ کبھی نہ منائیں گے اپنا مرنا اُن کاموں کی وجہ سے جنکو آگے بھیج چکے ہیں اُنکے ہاتھ اور اللہ کو خوب معلوم ہیں سب گنہگار ');
INSERT INTO "al_hind" VALUES(62,8,'62:8','تو کہہ موت وہ جس سے تم بھاگتے ہو وہ تم سے ضرور ملنے والی ہے پھر تم پھیرے جاؤ گے اس سے چھپے اور کھلے جاننے والے کے پاس پھر جتلا دے گا تم کو جو تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(62,9,'62:9','اے ایمان والو جب اذان ہو نماز کی جمعہ کے دن تو دوڑو اللہ کی یاد کو اور چھوڑ دو خرید و فروخت یہ بہتر ہے تمہارے حق میں اگر تم کو سمجھ ہے ');
INSERT INTO "al_hind" VALUES(62,10,'62:10','پھر جب تمام ہو چکے نماز تو پھیل پڑو زمین میں اور ڈھونڈو فضل اللہ کا اور یاد کرو اللہ کو بہت سا تاکہ تمہارا بھلا ہو ');
INSERT INTO "al_hind" VALUES(62,11,'62:11','اور جب دیکھیں سودا بکتا یا کچھ تماشا متفرق ہو جائیں اُسکی طرف اور تجھ کو چھوڑ جائیں کھڑا تو کہہ جو اللہ کے پاس ہے سو بہتر ہے تماشے سے اور سوداگری سے اور اللہ بہتر ہے روزی دینے والا ');
INSERT INTO "al_hind" VALUES(63,1,'63:1','جب آئیں تیرے پاس منافق کہیں ہم قائل ہیں تو رسول ہے اللہ کا اور اللہ جانتا ہے کہ تو اُس کا رسول ہے اور اللہ گواہی دیتا ہے کہ یہ منافق جھوٹے ہیں ');
INSERT INTO "al_hind" VALUES(63,2,'63:2','انہوں نے رکھا ہے اپنی قسموں کو ڈھال بنا کر پھر روکتے ہیں اللہ کی راہ سے یہ لوگ برے کام ہیں جو کر رہے ہیں ');
INSERT INTO "al_hind" VALUES(63,3,'63:3','یہ اس لئے کہ وہ ایمان لائے پھر منکر ہو گئے پھر مہر لگ گئ اُنکے دل پر سو وہ اب کچھ نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(63,4,'63:4','اور جب تو دیکھے اُنکو تو اچھے لگیں تجھ کو انکے ڈیل اور اگر بات کہیں سنے تو اُنکی بات کیسے ہیں جیسے کہ لکڑی لگا دی دیوار سے جو کوئی چیخے جانیں ہم ہی پر بلا آئی وہی ہیں دشمن اُن سے بچتا رہ گردن مارے انکی اللہ کہاں سے پھرے جاتے ہیں ');
INSERT INTO "al_hind" VALUES(63,5,'63:5','اور جب کہیے اُنکو آؤ معاف کرا دے تم کو رسول اللہ کا مٹکاتے ہیں اپنے سر اور تو دیکھےکہ وہ رکتے ہیں اور وہ غرور کرتے ہیں ');
INSERT INTO "al_hind" VALUES(63,6,'63:6','برابر ہے اُن پر تو معافی چاہے اُنکی یا نہ معافی چاہے ہرگز نہ معاف کرے گا اُنکو اللہ بیشک اللہ راہ نہیں دیتا نافرمان لوگوں کو ');
INSERT INTO "al_hind" VALUES(63,7,'63:7','وہی ہیں جو کہتے ہیں مت خرچ کرو ان پر جو پاس رہتے ہیں رسول اللہ کے یہاں تک کہ متفرق ہو جائیں اور اللہ کے ہیں خزانے آسمانوں کے اور زمین کے ولیکن منافق نہیں سمجھتے ');
INSERT INTO "al_hind" VALUES(63,8,'63:8','کہتے ہیں البتہ اگر ہم پھر گئے مدینہ کو تو نکال دے گا جس کا زور ہے وہاں سے کمزور لوگوں کو اور زور تو اللہ کا ہے اور اُسکے رسول کا اور ایمان والوں کا لیکن منافق نہیں جانتے ');
INSERT INTO "al_hind" VALUES(63,9,'63:9','اے ایمان والو غافل نہ کردیں تم کو تمہاے مال اور تمہاری اولاد اللہ کی یاد سے اور جو کوئی یہ کام کرے تو وہی لوگ ہیں ٹوٹے میں ');
INSERT INTO "al_hind" VALUES(63,10,'63:10','اور خرچ کرو کچھ ہمارا دیا ہوا اُس سے پہلے کہ آ پہنچے تم میں کسی کو موت تب کہے اے رب کیوں نہ ڈھیل دی تو نے مجھ کو ایک تھوڑی سے مدت کہ میں خیرات کرتا اور ہو جاتا نیک لوگوں میں');
INSERT INTO "al_hind" VALUES(63,11,'63:11','اور ہرگز نہ ڈھیل دے گا اللہ کسی جی کو جب آ پہنچا اُس کا وعدہ اور اللہ کو خبر ہے جو تم کرتے ہو ');
INSERT INTO "al_hind" VALUES(64,1,'64:1','پاکی بول رہا ہے اللہ کی جو کچھ ہے آسمانوں میں اور جو کچھ ہے زمین میں اُسی کا راج ہے اور اُسی کی تعریف ہے اور وہی ہر چیز کر سکتا ہے');
INSERT INTO "al_hind" VALUES(64,2,'64:2','وہی ہے جس نے تم کو بنایا پھر کوئی تم میں منکر ہے اورکوئی تم میں ایماندار اور اللہ جو تم کرتے ہو دیکھتا ہے');
INSERT INTO "al_hind" VALUES(64,3,'64:3','بنایا آسمانوں کو اور زمین کو تدبیر سے اور صورت کھینچی تمہاری پھر اچھی بنائی تمہاری صورت اور اُسکی طرف سب کو پھر جانا ہے');
INSERT INTO "al_hind" VALUES(64,4,'64:4','جانتا ہے جو کچھ ہے آسمانوں میں اور زمین میں اور جانتا ہے جو تم چھپاتے ہو اور جو کھول کر کرتے ہو اور اللہ کو معلوم ہے جیوں کی بات');
INSERT INTO "al_hind" VALUES(64,5,'64:5','کیا پہنچی نہیں تم کو خبر اُن لوگوں کی جو منکر ہو چکے ہیں پہلے پھر اُنہوں نے چکھی سزا اپنے کام کی اور اُن کو عذاب دردناک ہے ');
INSERT INTO "al_hind" VALUES(64,6,'64:6','یہ اس لئےکہ لاتےتھے اُنکے پاس اُن کے سول نسانیاں پھر کتے کیا آدمی ہم کر راہ سمجھائیں گے پھر منکر ہوئے اور منہ موڑ لیا اور اللہ نے بے پروائی کی اور اللہ بے پروا ہے سب تعریفوں والا ');
INSERT INTO "al_hind" VALUES(64,7,'64:7','دعوٰی کرتے ہیں منکر کہ ہر گز اُنکو کوئی نہ اٹھائے گا تو کہہ کیوں نہیں قسم ہے میرے رب کی تم کو بیشک اٹھانا ہے پھر تم کو جتلانا ہے جو کچھ تم نے کیا اور یہ اللہ پر آسان ہے ');
INSERT INTO "al_hind" VALUES(64,8,'64:8','سو ایمان لاؤ اللہ پر اور اسکے رسول پر اور اُس نور پر جو ہم نے اتارا اور اللہ کو تمہاے سب کام کی خبر ہے ');
INSERT INTO "al_hind" VALUES(64,9,'64:9','جس دن تم کو اکٹھا کرے گا جمع ہونے کے دن وہ دن ہے ہار جیت کا اور جو کوئی یقین لائے اللہ پر اور کرے کام بھلا اتار دے گا اس پر سے اُسکی برائیاں اور داخل کرے گا اُسکو باغوں میں جنکے نیچے بہتی ہیں ندیاں رہا کریں اُن میں ہمیشہ یہی ہے بڑی مراد ملنی ');
INSERT INTO "al_hind" VALUES(64,10,'64:10','اور جو لوگ منکر ہوئے اور جھٹلائیں انہوں نے ہماری آیتیں وہ لوگ ہیں دوزخ والے رہا کریں اُسی میں اور بری جگہ جا پہنچے');
INSERT INTO "al_hind" VALUES(64,11,'64:11','نہیں پہنچتی کوئی تکلیف بدون حکم اللہ کے اور جو کوئی یقین لائے اللہ پر وہ راہ بتلائے اسکے دل کو اور اللہ کو ہر چیز معلوم ہے ');
INSERT INTO "al_hind" VALUES(64,12,'64:12','اور حکم مانو اللہ کا اور حکم مانو رسول کا پھر اگر تم منہ موڑو تو ہمارے رسول کا تو یہی کام ہے پہنچا دینا کھول کر ');
INSERT INTO "al_hind" VALUES(64,13,'64:13','اللہ اُسکے سوائے کسی کی بندگی نہیں اور اللہ پر چاہئے بھروسہ کریں ایمان والے ');
INSERT INTO "al_hind" VALUES(64,14,'64:14','اے ایمان والو تمہاری بعض جوروئیں اور اولاد دشمن ہیں تمہارے سو اُن سے بچتے رہو اور اگر معاف کرو اور درگزرو اور بخشو تو اللہ ہے بخشنے والا مہربان ');
INSERT INTO "al_hind" VALUES(64,15,'64:15','تمہارے مال اور تمہاری اولاد یہی ہیں جانچنے کو اور اللہ جو ہے اُسکے پاس ہے ثواب بڑا ');
INSERT INTO "al_hind" VALUES(64,16,'64:16','سو ڈرو اللہ سے جہاں تک ہو سکے اور سنو اور مانو اور خرچ کرو اپنے بھلے کو اور جسکو بچا دیا اپنے جی کے لالچ سے سو وہ لوگ وہی مراد کو پہنچے ');
INSERT INTO "al_hind" VALUES(64,17,'64:17','اگر قرض دو اللہ کو اچھی طرح پر قرض دینا وہ دونا کر دے تم کو اور تم کو بخشے اور اللہ قدردان ہے تحمل والا ');
INSERT INTO "al_hind" VALUES(64,18,'64:18','جاننے والا پوشیدہ اور ظاہر کا زبردست حکمت والا ');
INSERT INTO "al_hind" VALUES(65,1,'65:1','اے نبی جب تم طلاق دو عورتوں کو تو انکو طلاق دو اُنکی عدت پر اور گنتے رہو عدت کو اور ڈرو اللہ سے جو رب ہے تمہارا مت نکالو اُنکو اُنکے گھروں سے اور وہ بھی نہ نکلیں مگر جو کریں صریح بے حیائی اور یہ حدیں ہیں باندھی ہوئی اللہ کی اور جو کوئی بڑھے اللہ کی حدوں سے تو اُس نے برا کیا اپنا اُسکو خبر نہیں شاید اللہ پیدا کر دے اُس طلاق کے بعد نئی صورت ');
INSERT INTO "al_hind" VALUES(65,2,'65:2','پھر جب پہنچیں اپنے وعدہ کو تو رکھ لو اُنکو دستور کے موافق یا چھوڑ دو اُنکو دستور کے موافق اور گواہ کر لو دو معتبر اپنے میں کے اور سیدھی ادا کرو گواہی اللہ کے واسطے یہ بات جو ہے اس سے سمجھ جائے گا جو کوئی یقین رکھتا ہو گا اللہ پر اور پچھلے دن پر اور جو کوئی ڈرتا ہے اللہ سے وہ کر دے اُس کا گذارہ ');
INSERT INTO "al_hind" VALUES(65,3,'65:3','اور روزی دے اُسکو جہاں سے اُسکو خیال بھی نہ ہو اور جو کوئی بھروسہ رکھے اللہ پر تو وہ اُسکو کافی ہے تحقیق اللہ پورا کر لیتا ہے اپنا کام اللہ نےرکھا ہے ہر چیز کا اندازہ ');
INSERT INTO "al_hind" VALUES(65,4,'65:4','اور جو عورتیں ناامید ہو گئیں حیض سے تمہاری عورتوں میں اگر تم کو شبہ رہ گیا تو اُنکی عدت ہے تین مہینے اور ایسے ہی جن کو حیض نہیں آیا اور جن کے پیٹ میں بچہ ہے اُنکی عدت یہ کہ جن لیں پیٹ کا بچہ اور جو کوئی ڈرتا رہے اللہ سے کر دے وہ اُس کے کام میں آسانی');
INSERT INTO "al_hind" VALUES(65,5,'65:5','یہ حکم ہے اللہ کا جو اتارا تمہاری طرف اور جو کوئی ڈرتا رہے اللہ سے اتار دے اس پر سے اسکی برائیاں اور بڑا دے اُسکو ثواب ');
INSERT INTO "al_hind" VALUES(65,6,'65:6','اُنکو گھر دو رہنے کے واسطے جہاں تم آپ رہو اپنے مقدور کے موافق اور ایذا دینا نہ چاہو اُنکو تاکہ تنگ پکڑو اُنکو اور اگر رکھتی ہوں پیٹ میں بچہ تو اُن پر خرچ کرو جب تک جنیں پیٹ کا بچہ پھر اگر وہ دودھ پلائیں تمہاری خاطر تو دو اُنکو اُن کا بدلا اور سکھاؤ آپس میں نیکی اور اگر ضد کرو آپس میں تو دودھ پلائے گی اُسکی خاطر اور کوئی عورت ');
INSERT INTO "al_hind" VALUES(65,7,'65:7','چاہیے خرچ کرے وسعت والا اپنی وسعت کے موافق اور جسکو نپی تلی ملتی ہے اُسکی روزی تو خرچ کرے جیسا کہ دیا ہے اُسکو اللہ نے اللہ کسی پر تکلیف نہیں رکھتا مگر اُسی قدر جو اُسکو دیا اب کر دے گا اللہ سختی کے پیچھے کچھ آسانی ');
INSERT INTO "al_hind" VALUES(65,8,'65:8','اور کتنی بستیاں کہ نکل چلیں حکم سے اپنے رب کے اور اُسکے رسولوں کے پھر ہم نے حساب میں پکڑا اُنکو سخت حساب میں اور آفت ڈالی اُن پر بن دیکھی آفت ');
INSERT INTO "al_hind" VALUES(65,9,'65:9','پھر چکھی انہوں نے سزا اپنے کام کی اور آخر کو اُنکے کام میں ٹوٹا آ گیا ');
INSERT INTO "al_hind" VALUES(65,10,'65:10','تیار رکھا ہے اللہ نے واسطے اُنکے سخت عذاب سو ڈرتے رہو اللہ سے اے عقل والو جنکو یقین ہے بیشک اللہ نے اتاری ہے تم پر نصیحت ');
INSERT INTO "al_hind" VALUES(65,11,'65:11','رسول ہے جو پڑھ کر سناتا ہے تم کو اللہ کی آیتیں کھول کر سنانے والی تاکہ نکالے اُن لوگوں کو جو کہ یقین لائے اور کئے بھلے کام اندھیروں سے اجالے میں اور جو کوئی یقین لائے اللہ پر اورکرے کچھ بھلائی اُسکو داخل کرے باغوں میں نیچے بہتی ہیں جنکے نہریں سدا رہیں اُن میں ہمیشہ البتہ خوب دی اللہ نے اُسکو روزی ');
INSERT INTO "al_hind" VALUES(65,12,'65:12','اللہ وہی ہے جس نے بنائے سات آسمان اور زمین بھی اتنی ہی اترتا ہے اُس کا کا حکم اُنکے اندر تاکہ تم جانو کہ اللہ ہر چیز کر سکتا ہے اور اللہ کے علم میں سمائی ہے ہر چیز کی ');
INSERT INTO "al_hind" VALUES(66,1,'66:1',' اے نبی تو کیوں حرام کرتا ہے جو حلال کیا اللہ نے تجھ پر چاہتا ہے تو رضامندی اپنی عورتوں کی اور اللہ بخشنے والا ہے مہربان ');
INSERT INTO "al_hind" VALUES(66,2,'66:2','مقرر کر دیا ہے اللہ نے تمہارے لئے کھول ڈالنا تمہاری قسموں کا اور اللہ مالک ہے تمہارا اور وہی ہے سب کچھ جانتا حکمت والا ');
INSERT INTO "al_hind" VALUES(66,3,'66:3','اور جب چھپا کر کہی نبی نے اپنی کسی عورت سے ایک بات پھر جب اُس نے خبر کر دی اُسکی اور اللہ نے جتلا دی نبی کو وہ بات تو جتلائی نبی نے اُس میں سےکچھ اور ٹلا دی کچھ پھر جب وہ جتلائی عورت کو بولی تجھ کو کس نے بتلا دی یہ کہا مجھ کو بتایا اُس خبر والے واقف نے ');
INSERT INTO "al_hind" VALUES(66,4,'66:4','اگر تم دونوں توبہ کرتی ہو تو جھک پڑے ہیں دل تمہارے اور اگر تم دونوں چڑھائی کرو گی اُس پر تو اللہ ہے اُس کا رفیق اور جبرئیل اور نیک بخت ایمان والے اور فرشتے اسکے پیچھے مددگار ہیں ');
INSERT INTO "al_hind" VALUES(66,5,'66:5','اگر نبی چھوڑ دے تم سب کو ابھی اُس کا رب بدلے میں دیدے اُسکو عورتیں تم سے بہتر حکم بردار یقین رکھنے والیاں نماز میں کھڑی ہونے والیاں توبہ کرنے والیاں بندگی بجا لانے والیاں روزہ رکھنے والیاں بیاہیاں اور کنواریاں ');
INSERT INTO "al_hind" VALUES(66,6,'66:6','اے ایمان والو بچاؤ اپنی جان کو اور اپنے گھر والوں کو اُس آگ سے جسکی چھپٹیاں ہیں آدمی اور پتھر اُس پر مقرر ہیں فرشتے تندخو زبردست نافرمانی نہیں کرتے اللہ کی جو بات فرمائے اُنکو اور وہی کام کرتے ہیں جو اُنکو حکم ہو ');
INSERT INTO "al_hind" VALUES(66,7,'66:7','اے منکر ہونے والو مت بہانے بتلاؤ آج کے دن وہی بدلا پاؤ گے جو تم کرتے تھے ');
INSERT INTO "al_hind" VALUES(66,8,'66:8','اے ایمان والو توبہ کرو اللہ کی طرف صاف دل کی توبہ امید ہے تمہارا رب اتار دے تم پر سے تمہاری برائیاں اور داخل کرے تم کو باغوں میں جنکے نیچے بہتی ہیں نہریں جس دن کہ اللہ ذلیل نہ کرے گا نبی کو اور اُن لوگوں کو جو یقین لاتے ہیں اُسکے ساتھ اُنکی روشنی دوڑتی ہے اُنکے آگے اور اُنکے داہنے کہتے ہیں اے رب ہمارے پوری کر دے ہماری روشنی اور معاف کر ہم کو بیشک تو سب کچھ کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(66,9,'66:9','اے نبی لڑائی کر منکروں سے اور دغابازوں سے اور سختی کر اُن پر اور اُن کا گھر دوزخ ہے اور بری جگہ جا پہنچے ');
INSERT INTO "al_hind" VALUES(66,10,'66:10','اللہ نے بتلائی ایک مثل منکروں کے واسطے عورت نوح کی اور عورت لوط کی گھر میں تھیں دونوں دو نیک بندوں کے ہمارے نیک بندوں میں سے پھر اُنہوں نے اُن سے چوری کی پھر وہ کام نہ آئے اُنکے اللہ کے ہاتھ سے کچھ بھی اور حکم ہوا کہ چلی جاؤ دوزخ میں جانے والوں کے ساتھ');
INSERT INTO "al_hind" VALUES(66,11,'66:11','اور اللہ نے بتلائی ایک مثل ایمان والوں کے لئے عورت فرعون کی جب بولی اے رب بنا میرے واسطے اپنے پاس ایک گھر بہشت میں اور بچا نکال مجھ کو فرعون سے اور اُسکے کام سے اور بچا نکال مجھ کو ظالم لوگوں سے ');
INSERT INTO "al_hind" VALUES(66,12,'66:12','اور مریم بیٹی عمران کی جس نے روکے رکھا اپنی شہوت کی جگہ کو پھر ہم نے پھونک دی اُس میں ایک اپنی طرف سے جان اور سچا جانا اپنے رب کی باتوں کو اور اُسکی کتابوں کو اور وہ تھی بندگی کرنے والوں میں ');
INSERT INTO "al_hind" VALUES(67,1,'67:1','بڑی برکت ہے اُسکی جسکے ہاتھ میں ہے راج اور وہ سب کچھ کر سکتا ہے ');
INSERT INTO "al_hind" VALUES(67,2,'67:2','جس نے بنایا مرنا اور جینا تاکہ تم کو جانچے کون تم میں اچھا کرتا ہے کام اور وہ زبردست ہے بخشنے والا ');
INSERT INTO "al_hind" VALUES(67,3,'67:3','جس نے بنائے سات آسمان تہہ پر تہہ کیا دیکھتا ہے تو رحمٰن کے بنانے میں کچھ فرق پھر دوبارہ نگاہ کر کہیں نظر آتی ہے تجھ کو دڑاڑ ');
INSERT INTO "al_hind" VALUES(67,4,'67:4','پھر لوٹا کر نگاہ کر دو دو بار لوٹ آئے گی تیرے پاس تیری نگاہ رد ہو کر تھک کر ');
INSERT INTO "al_hind" VALUES(67,5,'67:5','اور ہم نے رونق دی سب سے ورلے آسمان کو چراغوں سے اور اُن سے کر رکھی ہے ہم نے پھینک مار شیطانوں کے واسطے اور رکھا ہے اُنکے واسطے عذاب دہکتی آگ کا ');
INSERT INTO "al_hind" VALUES(67,6,'67:6','اور جو لوگ منکر ہوئے اپنے رب سے اُنکے واسطے ہے عذاب دردناک اور بری جگہ جا پہنچے ');
INSERT INTO "al_hind" VALUES(67,7,'67:7','جب اُس میں ڈالے جائیں گے سنیں گے اُس کا دہاڑنا اور وہ اچھل رہی ہو گی');
INSERT INTO "al_hind" VALUES(67,8,'67:8','ایسا لگتا ہے کہ پھٹ پڑے گی جوش سے جس وقت پڑے اُس میں ایک گروہ پوچھیں اُن سے دوزخ کے داروغہ کیا نہ پہنچا تھا تمہارے پاس کوئی ڈر سنانے والا ');
INSERT INTO "al_hind" VALUES(67,9,'67:9','وہ بولیں کیوں نہیں ہمارے پاس پہنچا تھا ڈر سنانے والا پھر ہم نے جھٹلایا اور کہا نہیں اتاری اللہ نے کوئی چیز تم تو پڑے ہوئے ہو بڑے بہکاوے میں ');
INSERT INTO "al_hind" VALUES(67,10,'67:10','اور کہیں گے اگر ہم ہوتے سنتے یا سمجھتے تو نہ ہوتے دوزخ والوں میں ');
INSERT INTO "al_hind" VALUES(67,11,'67:11','سو قائل ہو گئے اپنے گناہ کے اب دفع ہو جائیں دوزخ والے ');
INSERT INTO "al_hind" VALUES(67,12,'67:12','جو لوگ ڈرتے ہیں اپنے رب سے بن دیکھے اُنکے لئے معافی ہے اور ثواب بڑا');
INSERT INTO "al_hind" VALUES(67,13,'67:13','اور تم چھپا کر کہو اپنی بات یا کھول کر وہ خوب جانتا ہے جیوں کے بھید ');
INSERT INTO "al_hind" VALUES(67,14,'67:14','بھلا وہ نہ جانے جس نے بنایا اور وہی ہے بھید جاننے والا خبردار ');
INSERT INTO "al_hind" VALUES(67,15,'67:15','وہی ہے جس نے کیا تمہارے آگے زمین کو پست اب چلو پھرو اُسکے کندھوں پر اور کھاؤ کچھ اُسکی دی ہوئی روزی اور اُسی کی طرف جی اٹھنا ہے ');
INSERT INTO "al_hind" VALUES(67,16,'67:16','کیا تم نڈر ہو گئے اُس سے جو آسمان میں ہے اس سے کہ دھنسا دے تم کو زمین میں پھر تبھی وہ لرزنے لگے ');
INSERT INTO "al_hind" VALUES(67,17,'67:17','یا نڈر ہو گئے ہو اُس سے جو آسمان میں ہے اس بات سے کہ برسا دے تم پر مینہ پتھروں کا سو جان لو گے کیسا ہے میرا ڈرانا ');
INSERT INTO "al_hind" VALUES(67,18,'67:18','اور جھٹلا چکے ہیں جو اُن سے پہلے تھے پھر کیسا ہوا میرا انکار ');
INSERT INTO "al_hind" VALUES(67,19,'67:19','اور کیا نہیں دیکھتے ہو اڑتے جانوروں کو اپنے اوپر پر کھولے ہوئے اور پر جھپکتے ہوئے اُن کو کوئی نہیں تھام رہا رحمان کے سوائے اُسکی نگاہ میں ہے ہر چیز ');
INSERT INTO "al_hind" VALUES(67,20,'67:20','بھلا وہ کون ہے جو فوج ہے تمہاری مدد کرے تمہاری رحمٰن کے سوائے منکر پڑے ہیں برے بہکائے میں ');
INSERT INTO "al_hind" VALUES(67,21,'67:21','بھلا وہ کون ہے جو روزی دے تم کو اگر وہ رکھ چھوڑے اپنی روزی کوئی نہیں پر اڑ رہے ہیں شرارت اور بدکنے پر ');
INSERT INTO "al_hind" VALUES(67,22,'67:22','بھلا ایک جو چلے اوندھا اپنے منہ کے بل وہ سیدھی راہ پائے یا وہ شخص جو چلے سیدھا ایک سیدھی راہ پر ');
INSERT INTO "al_hind" VALUES(67,23,'67:23','تو کہہ وہی ہے جس نے تم کو بنا کھڑا کیا اور بنا دیے تمہارے واسطے کان اور آنکھیں اور دل تم بہت تھوڑا حق مانتے ہو ');
INSERT INTO "al_hind" VALUES(67,24,'67:24','تو کہہ وہی ہے جس نے کھنڈا دیا تم کو زمین میں اور اُسی کی طرف اکٹھے کئے جاؤ گے ');
INSERT INTO "al_hind" VALUES(67,25,'67:25','اور کہتے ہیں کب ہو گا یہ وعدہ اگر تم سچے ہو ');
INSERT INTO "al_hind" VALUES(67,26,'67:26','تو کہہ خبر تو ہے اللہ ہی کے پاس اور میرا کام تو یہی ڈر سنا دینا ہے کھول کر ');
INSERT INTO "al_hind" VALUES(67,27,'67:27','پھر جب دیکھیں گے کہ وہ پاس آ لگا تو بگڑ جائیں گے منہ منکروں کے اور کہے گا یہی ہے جس کو تم مانگتے تھے ');
INSERT INTO "al_hind" VALUES(67,28,'67:28','تو کہہ بھلا دیکھو تو اگر ہلاک کر دے مجھ کو اللہ اور میرے ساتھ والو کو یا ہم پر رحم کرے پھر وہ کون ہے جو بچائے منکروں کو عذاب دردناک سے ');
INSERT INTO "al_hind" VALUES(67,29,'67:29','تو کہہ وہی رحمٰن ہے ہم نے اُسکو مانا اور اُسی پر بھروسہ کیا سو اب تم جان لو گے کون پڑ اہے صریح بہکائے میں ');
INSERT INTO "al_hind" VALUES(67,30,'67:30','تو کہہ بھلا دیکھو تو اگر ہو جائے صبح کو پانی تمہارا خشک پھر کون ہے جو لائے تمہارے پاس پانی نتھرا ');
INSERT INTO "al_hind" VALUES(68,1,'68:1','نٓ۔ قسم ہے قلم کی اور جو کچھ لکھتے ہیں');
INSERT INTO "al_hind" VALUES(68,2,'68:2','تو نہیں اپنے رب کے فضل سے دیوانہ ');
INSERT INTO "al_hind" VALUES(68,3,'68:3','اور تیرے واسطے بدلا ہے بے انتہا ');
INSERT INTO "al_hind" VALUES(68,4,'68:4','اور تو پیدا ہوا ہے بڑے خلق پر ');
INSERT INTO "al_hind" VALUES(68,5,'68:5','سو اب تو بھی دیکھ لے گا اور وہ بھی دیکھ لیں گے');
INSERT INTO "al_hind" VALUES(68,6,'68:6','کہ کون ہے تم میں جو بچل رہا ہے ');
INSERT INTO "al_hind" VALUES(68,7,'68:7','بیشک تیرا رب وہی خوب جانے اُسکو جو بہکا اُسکی راہ سے اور وہی جانتا ہے راہ پانے والوں کو ');
INSERT INTO "al_hind" VALUES(68,8,'68:8','سو تو کہنا مت مان جھٹلانے والوں کا');
INSERT INTO "al_hind" VALUES(68,9,'68:9','وہ چاہتے ہیں کسی طرح تو ڈھیلا ہو تو وہ بھی ڈھیلے ہوں ہوں');
INSERT INTO "al_hind" VALUES(68,10,'68:10','اور تو کہا مت مان کسی قسمیں کھانے والے بے قدر کا ');
INSERT INTO "al_hind" VALUES(68,11,'68:11','طعنے دے چغلی کھاتا پھرے');
INSERT INTO "al_hind" VALUES(68,12,'68:12','بھلے کام سے روکے حد سے بڑھے بڑا گنہگار');
INSERT INTO "al_hind" VALUES(68,13,'68:13','اُجڈ ان سب کے پیچھے بدنام ');
INSERT INTO "al_hind" VALUES(68,14,'68:14','اس واسطے کہ رکھتا ہے مال اور بیٹے ');
INSERT INTO "al_hind" VALUES(68,15,'68:15','جب سنائے اُس کو ہماری باتیں کہے یہ نقلیں ہیں پہلوں کی ');
INSERT INTO "al_hind" VALUES(68,16,'68:16','اب داغ دیں گے ہم اُسکو سونڈ پر ');
INSERT INTO "al_hind" VALUES(68,17,'68:17','ہم نے اُن کو جانچا ہے جیسے جانچا تھا باغ والوں کو جب اُن سب نے قسم کھائی کہ اُس کا میوہ توڑیں گے صبح ہوتے');
INSERT INTO "al_hind" VALUES(68,18,'68:18','اور ان شاء اللہ نہ کہا ');
INSERT INTO "al_hind" VALUES(68,19,'68:19','پھر پھیرا کر گیا اُس پر کوئی پھیرے والا تیرے رب کی طرف سے اور وہ سوتے ہی رہے');
INSERT INTO "al_hind" VALUES(68,20,'68:20','پھر صبح تک ہو رہاجیسے ٹوٹ چکا ');
INSERT INTO "al_hind" VALUES(68,21,'68:21','پھر آپس میں بولے صبح ہوتے');
INSERT INTO "al_hind" VALUES(68,22,'68:22','کہ سویرے چلو اپنے کھیت پر اگر تمکو توڑنا ہے');
INSERT INTO "al_hind" VALUES(68,23,'68:23','پھر چلے اور آپس میں کہتے تھے چپکے چپکے');
INSERT INTO "al_hind" VALUES(68,24,'68:24','کہ اندر نہ آنے پائے اُس میں آج تمہارے پاس کوئی محتاج');
INSERT INTO "al_hind" VALUES(68,25,'68:25','اور سویرے چلے لپکتے ہوئے زور کے ساتھ ');
INSERT INTO "al_hind" VALUES(68,26,'68:26','پھر جب اُسکو دیکھا بولے ہم تو راہ بھول آئے');
INSERT INTO "al_hind" VALUES(68,27,'68:27','نہیں ہماری تو قسمت پھوٹ گئ ');
INSERT INTO "al_hind" VALUES(68,28,'68:28','بولا بچلا اُن کا میں نے تم کو نہ کہا تھا کہ کیوں نہیں پاکی بولتے اللہ کی ');
INSERT INTO "al_hind" VALUES(68,29,'68:29','بولے پاک ذات ہے ہمارے رب کی ہم ہی تقصیر وار تھے');
INSERT INTO "al_hind" VALUES(68,30,'68:30','پھر منہ کر کر ایک دوسرے کی طرف لگے اُلاہنا دینے ');
INSERT INTO "al_hind" VALUES(68,31,'68:31','یبولے ہائے خرابی ہماری ہم ہی تھے حد سے بڑھنے والے');
INSERT INTO "al_hind" VALUES(68,32,'68:32','شاید ہمارا رب بدل دے ہمکو اس سے بہتر ہم اپنے رب سے آرزو رکھتے ہیں ');
INSERT INTO "al_hind" VALUES(68,33,'68:33','یوں آتی ہے آفت اور آخرت کی آفت تو سب سے بڑی ہے اگر اُنکو سمجھ ہوتی ');
INSERT INTO "al_hind" VALUES(68,34,'68:34','البتہ ڈرنے والوں کو اُنکے رب کے پاس باغ ہیں نعمت کے ');
INSERT INTO "al_hind" VALUES(68,35,'68:35','کیا ہم کر دیں گے حکم برداروں کو برابر گنہگاروں کے');
INSERT INTO "al_hind" VALUES(68,36,'68:36','کیا ہو گیا تمکو کیسے ٹھہرا رہے ہو بات ');
INSERT INTO "al_hind" VALUES(68,37,'68:37','کیا تمہارے پا س کوئی کتاب ہے جس میں پڑھ لیتے ہو');
INSERT INTO "al_hind" VALUES(68,38,'68:38','اُس میں ملتا ہے تمکو جو تم پسند کر لو');
INSERT INTO "al_hind" VALUES(68,39,'68:39','کیا تم نے ہم سے قسمیں لے لی ہیں ٹھیک پہنچنے والی قیامت کے دن تک کہ تم کو ملے گاجو کچھ تم ٹھہراؤ گے');
INSERT INTO "al_hind" VALUES(68,40,'68:40','پوچھ اُن سے کونسا اُن میں اس کا ذمہ لیتا ہے ');
INSERT INTO "al_hind" VALUES(68,41,'68:41','کیا انکے واسطے کوئی شریک ہیں پھر توچاہئے لے آئیں اپنے اپنے شریکوں کو اگر وہ سچے ہیں ');
INSERT INTO "al_hind" VALUES(68,42,'68:42','جس دن کہ کھولی جائے پنڈلی اور وہ بلائے جائیں سجدہ کرنےکو پھر نہ کر سکیں ');
INSERT INTO "al_hind" VALUES(68,43,'68:43','جھکی پڑتی ہوں گی اُنکی آنکھیں چڑھی آتی ہو گی اُن پر ذلت اور پہلے اُنکو بلاتے رہے سجدہ کرنے کو اور وہ تھے اچھے خاصے ');
INSERT INTO "al_hind" VALUES(68,44,'68:44','اب چھوڑ دے مجھ کو اور اُنکو جو کہ جھٹلائیں اس بات کو اب ہم سیڑھی سیڑھی اتاریں گے اُنکو جہاں سے اُنکو پتہ بھی نہیں ');
INSERT INTO "al_hind" VALUES(68,45,'68:45','اور انکو ڈھیل دیے جاتا ہوں بیشک میرا داؤ پکا ہے ');
INSERT INTO "al_hind" VALUES(68,46,'68:46','کیا تو مانگتا ہے اُن سے کچھ حق سو اُن پر تاوان کا بوجھ پڑ رہا ہے');
INSERT INTO "al_hind" VALUES(68,47,'68:47','کیا اُنکے پاس خبر ہے غیب کی سو وہ لکھ لاتے ہیں ');
INSERT INTO "al_hind" VALUES(68,48,'68:48','اب تو استقلال سے راہ دیکھتا رہ اپنے رب کے حکم کی اور مت ہو جیسا وہ مچھلی والا جب پکارا اُس نے اور وہ غصہ میں بھرا تھا ');
INSERT INTO "al_hind" VALUES(68,49,'68:49','اگر نہ سنبھالتا اُسکو احسان تیرے رب کا تو پھینکا گیا ہی تھا چٹیل میدان میں الزام کھا کر ');
