INSERT INTO "al_hind" VALUES(71,19,'71:19','اور اللہ نے بنا دیا تمہارے لئے زمین کو بچھونا');
INSERT INTO "al_hind" VALUES(71,20,'71:20','تاکہ چلو اُس میں کشادہ رستے ');
INSERT INTO "al_hind" VALUES(71,21,'71:21','کہا نوح نے اے رب میرے انہوں نے میرا کہا نہ مانا اور مانا ایسے کا جسکو اُسکے مال اور اولاد سے اور زیادہ ہو ٹوٹا ');
INSERT INTO "al_hind" VALUES(71,22,'71:22','اور داؤ کیا ہے بڑ اداؤ ');
INSERT INTO "al_hind" VALUES(71,23,'71:23','اور بولے ہرگز نہ چھوڑیو اپنے معبودوں کو اور نہ چھوڑیو ودّ کو اور نہ سواع کو اور نہ یغوث کو اور نہ یعوق اور نسر کو ');
INSERT INTO "al_hind" VALUES(71,24,'71:24','اور بہکا دیا بہتوں کو اور تو نہ زیادہ کرنا (کریو) بے انصافوں کو مگر بھٹکنا ');
INSERT INTO "al_hind" VALUES(71,25,'71:25','کچھ وہ اپنے گناہوں سے دبائے گئے پھر ڈالے گئے آگ میں پھر نہ پائے اپنے واسطے انہوں نے اللہ کے سوائے کوئی مددگار ');
INSERT INTO "al_hind" VALUES(71,26,'71:26','اور کہا نوح نے اے رب نہ چھوڑیو زمین پر منکروں کا ایک گھر بسنے والا');
INSERT INTO "al_hind" VALUES(71,27,'71:27','مقرر اگر تو چھوڑ دے گا اُنکو بہکائیں گے تیرے بندوں کو اور جو جنیں گے سو ڈھیٹھ حق کا منکر ');
INSERT INTO "al_hind" VALUES(71,28,'71:28','اے رب معاف کر مجھ کو اور میرے ماں باپ کو اور جو آئے میرے گھر میں ایماندار اور سب ایمان والے مردوں کو اور عورتوں کو اور گنہگاروں پر بڑھتا رکھ یہی برباد ہونا');
INSERT INTO "al_hind" VALUES(72,1,'72:1','تو کہہ مجھ کو حکم آیا کہ سن گئے کتنے لوگ جنوں کے پھر کہنے لگےہم نے سنا ہے ایک قرآن عجیب');
INSERT INTO "al_hind" VALUES(72,2,'72:2','کہ سمجھاتا ہے نیک راہ سو ہم بھی اُس پر یقین لا۴ئے اور ہر گز نہ شریک بتلائیں گے ہم اپنے رب کا کسی کو ');
INSERT INTO "al_hind" VALUES(72,3,'72:3','اور یہ کو اونچی ہے شان ہمارے رب کی نہیں رکھی اُس نے جورو نہ بیٹا ');
INSERT INTO "al_hind" VALUES(72,4,'72:4','اور یہ کہ ہم میں سے بیوقوف [یعنی ابلیس] اللہ پر بڑھا کر باتیں کہا کرتا تھا ');
INSERT INTO "al_hind" VALUES(72,5,'72:5','اور یہ کہ ہم کو خیال تھا کہ ہرگز نہ بولیں گے آدمی اور جن اللہ پر جھوٹ ');
INSERT INTO "al_hind" VALUES(72,6,'72:6','اور یہ کہ تھے کتنے مرد آدمیوں میں کے پناہ پکڑتے تھے کتنے مردوں کی جنوں میں کے پھر تو وہ اور زیادہ سر چڑھنے لگے ');
INSERT INTO "al_hind" VALUES(72,7,'72:7','اور یہ کہ اُنکو بھی خیال تھا جیسا تم کو خیال تھا کہ ہرگز نہ اٹھائے گا اللہ کسی کو ');
INSERT INTO "al_hind" VALUES(72,8,'72:8','اور یہ کہ ہم نے ٹٹول کر دیکھا آسمان کو پھر پایا اُسکو بھر رہے ہیں اُس میں چوکیدار سخت اور انگارے');
INSERT INTO "al_hind" VALUES(72,9,'72:9','اور یہ کہ ہم بیٹھا کرتے تھے ٹھکانوں میں سننے کے واسطے پھر جو کوئی اب سننا چاہے وہ پائےاپنے واسطے ایک انگارا گھات میں ');
INSERT INTO "al_hind" VALUES(72,10,'72:10','اور یہ کہ ہم نہیں جانتے کہ برا اراداہ ٹھہرا ہے زمین کے رہنے والوں پر یا چاہا ہے اُنکے حق میں اُنکے رب نے راہ پر لانا ');
INSERT INTO "al_hind" VALUES(72,11,'72:11','اور یہ کہ کوئی ہم میں نیک ہیں اور کوئی اسکےسوائے ہم تھے کئ راہ پر پھٹے ہوئے ');
INSERT INTO "al_hind" VALUES(72,12,'72:12','اور یہ کہ ہمارے خیال میں آ گیا کہ ہم چھپ نہ جائیں گے اللہ سے زمین میں اور نہ تھکا دیں گے اُسکو بھاگ کر ');
INSERT INTO "al_hind" VALUES(72,13,'72:13','اور یہ کہ جب ہم نے سن لی راہ کی بات تو ہم نے اُسکو مان لیا پھر جو کوئی یقین لائے گا اپنے رب پر سو وہ نہ ڈرے گا نقصان سے اور نہ زبردستی سے ');
INSERT INTO "al_hind" VALUES(72,14,'72:14','اور یہ کہ کچھ ہم میں حکم بردار ہیں اور کچھ ہیں بے انصاف سو جو لوگ حکم میں آ گئے سو انہوں نے اٹکل کر لیا نیک راہ کو');
INSERT INTO "al_hind" VALUES(72,15,'72:15','اور جو بے انصاف ہیں وہ ہوئے دوزخ کے ایندھن ');
INSERT INTO "al_hind" VALUES(72,16,'72:16','اور یہ حکم آیا کہ اگر لوگ سیدھے رہتے راہ پر تو ہم پلاتے اُنکو پانی بھر کر');
INSERT INTO "al_hind" VALUES(72,17,'72:17','تاکہ اُنکو جانچیں اس میں اور جو کوئی منہ موڑے اپنے رب کی یاد سے وہ ڈال دے گا اُسکو چڑھتے عذاب میں ');
INSERT INTO "al_hind" VALUES(72,18,'72:18','اور یہ کہ مسجدیں اللہ کی یاد کے واسطے ہیں سو مت پکارو اللہ کے ساتھ کسی کو ');
INSERT INTO "al_hind" VALUES(72,19,'72:19','اور یہ کہ جب کھڑا ہوا اللہ کا بندہ کہ اُسکو پکارے لوگوں کا بندھنے لگتا ہے اُس پر ٹھٹھ ');
INSERT INTO "al_hind" VALUES(72,20,'72:20','تو کہہ میں تو پکارتا ہوں بس اپنے رب کو اور شریک نہیں کرتا اُس کا کسی کو ');
INSERT INTO "al_hind" VALUES(72,21,'72:21','تو کہہ میرے اختیار میں نہیں تمہارا برا اور نہ راہ پر لانا ');
INSERT INTO "al_hind" VALUES(72,22,'72:22','تو کہہ مجھ کو نہ بچائے گا اللہ کے ہاتھ سے کوئی اور نہ پاؤں گا اُسکے سوائے کہیں سرک رہنے کو جگہ ');
INSERT INTO "al_hind" VALUES(72,23,'72:23','مگر پہنچانا ہے اللہ کی طرف سے اور اُسکے پیغام لانے اور جو کوئی حکم نہ مانے اللہ کا اور اُسکے رسول کا سو اُسکے لئے آگ ہے دوزخ کی رہا کریں اُس میں ہمیشہ ');
INSERT INTO "al_hind" VALUES(72,24,'72:24','یہاں تک کہ جب دیکھیں گے جو کچھ اُن سے وعدہ ہوا تب جان لیں گے کس کے مددگار کمزور ہیں اور گنتی میں تھوڑے ');
INSERT INTO "al_hind" VALUES(72,25,'72:25','تو کہہ میں نہیں جانتا کہ نزدیک ہے جس چیز کا تم سے وعدہ ہوا ہے یا کر دے اسکو میرا رب ایک مدت کے بعد ');
INSERT INTO "al_hind" VALUES(72,26,'72:26','جاننے والا بھید کا سو نہیں خبر دیتا اپنے بھید کی کسی کو');
INSERT INTO "al_hind" VALUES(72,27,'72:27','مگر جو پسند کر لیا کسی رسول کو تو وہ چلاتا ہے اُسکے آگے اور پیچھے چوکیدار ');
INSERT INTO "al_hind" VALUES(72,28,'72:28','تاکہ جانے کہ انہوں نے پہنچائے پیغام اپنے رب کے اور قابو میں رکھا ہے جو اُنکے پاس ہے اور گن لی ہے ہر چیز کی گنتی ');
INSERT INTO "al_hind" VALUES(73,1,'73:1','اے کپڑے میں لپٹنے والے ');
INSERT INTO "al_hind" VALUES(73,2,'73:2','کھڑا رہ رات کو مگر کسی رات ');
INSERT INTO "al_hind" VALUES(73,3,'73:3','آدھی رات یا اُس میں سے کم کر دے تھوڑا سا');
INSERT INTO "al_hind" VALUES(73,4,'73:4','یا زیادہ کر اُس پر اور کھول کھول کر پڑھ قرآن کو صاف ');
INSERT INTO "al_hind" VALUES(73,5,'73:5','ہم ڈالنے والے ہیں تجھ پر ایک بات وزن دار ');
INSERT INTO "al_hind" VALUES(73,6,'73:6','البتہ اٹھنا رات کو سخت روندتا ہے اور سیدھی نکلتی ہے بات ');
INSERT INTO "al_hind" VALUES(73,7,'73:7','البتہ تجھ کو دن میں شغل رہتا ہے لمبا ');
INSERT INTO "al_hind" VALUES(73,8,'73:8','اور پڑھے جا نام اپنے رب کا اور چھوٹ کر آ اسکی طرف سب سے الگ ہو کر ');
INSERT INTO "al_hind" VALUES(73,9,'73:9','مالک مشرق اور مغرب کا اُسکے سوا کسی کی بندگی نہیں سو پکڑ لے اُسکو کام بنانے والا ');
INSERT INTO "al_hind" VALUES(73,10,'73:10','اور سہتا رہ جو کچھ کہتے رہیں اور چھوڑ دے اُنکو بھلی طرح کا چھوڑنا ');
INSERT INTO "al_hind" VALUES(73,11,'73:11','اور چھوڑ دے مجھ کو اور جھٹلانے والوں کو جو آرام میں رہے ہیں اور ڈھیل دےاُنکو تھوڑی سے ');
INSERT INTO "al_hind" VALUES(73,12,'73:12','البتہ ہمارے پاس بیڑیاں ہیں اور آگ کا ڈھیر');
INSERT INTO "al_hind" VALUES(73,13,'73:13','اور کھانا گلے میں اٹکنے والا اور عذاب دردناک ');
INSERT INTO "al_hind" VALUES(73,14,'73:14','جس دن کہ کانپے گی زمین اور پہاڑ اور ہو جائیں گے پہاڑ ریت کے تودے پھسلتے ');
INSERT INTO "al_hind" VALUES(73,15,'73:15','ہم نے بھیجا تمہاری طرف رسول بتلانے والا تمہاری باتوں کا جیسے بھیجا فرعون کے پاس رسول ');
INSERT INTO "al_hind" VALUES(73,16,'73:16','پھر کہا نہ مانا فرعون نے رسول کا پھر پکڑی ہم نے اُسکو وبال کی پکڑ ');
INSERT INTO "al_hind" VALUES(73,17,'73:17','پھر کیونکر بچو گے اگر منکر ہو گئے اُس دن سے جو کر ڈالے لڑکوں کو بوڑھا ');
INSERT INTO "al_hind" VALUES(73,18,'73:18','آسمان پھٹ جائے گا اُس دن میں اُس کا وعدہ ہونے والا ہے ');
INSERT INTO "al_hind" VALUES(73,19,'73:19','یہ تو نصیحت ہے پھر جو کوئی چاہے بنالے اپنے رب کی طرف راہ ');
INSERT INTO "al_hind" VALUES(73,20,'73:20','بیشک تیرا رب جانتا ہے کہ تو اٹھتا ہے نزدیک دو تہائی رات کے اور آدھی رات کے اور تہائی رات کے اور کتنے لوگ تیرے ساتھ کے اور اللہ ماپتا ہے رات کو اور دن کو اُس نے جانا کہ تم اُس کو پورا نہ کر سکو گے سو تم پرمعافی بھیجدی اب پڑھو جتنا تم کو آسان ہو قرآن سے جانا کہ کتنے ہوں گے تم میں بیمار اور کتنے اور لوگ پھریں گے ملک میں ڈھونڈھتے اللہ کے فضل کو اور کتنے لوگ لڑتے ہوں گے اللہ کی راہ میں سو پڑھ لیا کرو جتنا آسان ہو اُس میں سے اورقائم رکھو نماز اور دیتے رہو زکوٰۃ اور قرض دو اللہ کو اچھی طرح پر قرض دینا اور جو کچھ آگے بھیجو گے اپنے واسطے کوئی نیکی اُسکو پاؤ گے اللہ کے پاس بہتر اور ثواب میں زیادہ اور معافی مانگو اللہ سے بیشک اللہ بخشنے والا مہربان ہے ');
INSERT INTO "al_hind" VALUES(74,1,'74:1','اے لحاف میں لپٹنے والے ');
INSERT INTO "al_hind" VALUES(74,2,'74:2','کھڑا ہو پھر ڈر سنا دے ');
INSERT INTO "al_hind" VALUES(74,3,'74:3','اور اپنے رب کی بڑائی بول ');
INSERT INTO "al_hind" VALUES(74,4,'74:4','اور اپنے کپڑے پاک رکھ');
INSERT INTO "al_hind" VALUES(74,5,'74:5','اور گندگی سے دور رہ ');
INSERT INTO "al_hind" VALUES(74,6,'74:6','اور ایسا نہ کر کہ احسان کرے اور بدلا بہت چاہے');
INSERT INTO "al_hind" VALUES(74,7,'74:7','اور اپنے رب سے امید رکھ ');
INSERT INTO "al_hind" VALUES(74,8,'74:8','پھر جب بجنے لگے وہ کھوکھری چیز ');
INSERT INTO "al_hind" VALUES(74,9,'74:9','پھر وہ اس دن مشکل دن ہے ');
INSERT INTO "al_hind" VALUES(74,10,'74:10','منکروں پر نہیں آسان ');
INSERT INTO "al_hind" VALUES(74,11,'74:11','چھوڑ دے مجھ کو اور اُس کو جسکو میں نے بنایا انکا ');
INSERT INTO "al_hind" VALUES(74,12,'74:12','اور دیا میں نے اُسکو مال پھیلا کر');
INSERT INTO "al_hind" VALUES(74,13,'74:13','اور بیٹے مجلس میں بیٹھنے والے ');
INSERT INTO "al_hind" VALUES(74,14,'74:14','اور تیاری کر دی اُسکے لئے خوب تیاری ');
INSERT INTO "al_hind" VALUES(74,15,'74:15','پھر لالچ رکھتا ہے کہ اور بھی دوں ');
INSERT INTO "al_hind" VALUES(74,16,'74:16','ہرگز نہیں وہ ہے ہماری آیتوں کا مخالف ');
INSERT INTO "al_hind" VALUES(74,17,'74:17','اب اُسی سے چڑھواؤں گا بڑی چڑھائی ');
INSERT INTO "al_hind" VALUES(74,18,'74:18','اُس نے فکر کیا اور دل میں ٹھہرا لیا');
INSERT INTO "al_hind" VALUES(74,19,'74:19','سو مارا جائیو کیسا ٹھہرایا');
INSERT INTO "al_hind" VALUES(74,20,'74:20','پھر مارا جائیو کیسا ٹھہرایا ');
INSERT INTO "al_hind" VALUES(74,21,'74:21','پھر نگاہ کی');
INSERT INTO "al_hind" VALUES(74,22,'74:22','پھر تیوری چڑھائی اور منہ تھتھایا');
INSERT INTO "al_hind" VALUES(74,23,'74:23','پھر پیٹھ پھیری اور غرور کیا');
INSERT INTO "al_hind" VALUES(74,24,'74:24','پھر بولا اور کچھ نہیں یہ جادو ہے چلا آتا');
INSERT INTO "al_hind" VALUES(74,25,'74:25','اور کچھ نہیں یہ کہا ہوا ہے آدمی کا ');
INSERT INTO "al_hind" VALUES(74,26,'74:26','اب اُسکو ڈالوں گا آگ میں ');
INSERT INTO "al_hind" VALUES(74,27,'74:27','اور تو کیا سمجھا کیسی ہے وہ آگ');
INSERT INTO "al_hind" VALUES(74,28,'74:28','نہ باقی رکھے اور نہ چھوڑے ');
INSERT INTO "al_hind" VALUES(74,29,'74:29','جلا دینے والی ہے آدمیوں کو ');
INSERT INTO "al_hind" VALUES(74,30,'74:30','اُس پر مقرر ہیں انیس فرشتے ');
INSERT INTO "al_hind" VALUES(74,31,'74:31','اور ہم نے جو رکھے ہیں دوزخ پر داروغہ وہ فرشتے ہی ہیں اور اُنکی جو گنتی رکھی ہے سو جانچنے کو منکروں کے تاکہ یقین کر لیں وہ لوگ جنکو ملی ہے کتاب اور بڑھے ایمانداروں کا ایمان اور دھوکا نہ کھائیں جنکو ملی ہے کتاب اور مسلمان اور تاکہ کہیں وہ لوگ کہ جنکے دل میں روگ ہے اور منکر کیا غرض تھی اللہ کو اس مثل سے یوں بچلاتا ہے اللہ جسکو چاہے اور راہ دیتا ہے جسکو چاہے اور کوئی نہیں جانتا تیرے رب کے لشکر مگر خود ہی اور وہ تو سمجھانا ہے لوگوں کے واسطے ');
