INSERT INTO "al_hind" VALUES(110,2,'110:2','اور تو دیکھے لوگوں کو داخل ہوتے دین میں غول کے غول');
INSERT INTO "al_hind" VALUES(110,3,'110:3','تو پاکی بول اپنے رب کی خوبیاں اور گناہ بخشوا اس سے بیشک وہ معاف کرنے والا ہے ');
INSERT INTO "al_hind" VALUES(111,1,'111:1','ٹوٹ گئے ہاتھ ابی لہب کے اور ٹوٹ گیا وہ آپ ');
INSERT INTO "al_hind" VALUES(111,2,'111:2','کام نہ آیا اُسکو مال اُسکا اور نہ جو اُس نے کمایا ');
INSERT INTO "al_hind" VALUES(111,3,'111:3','ڈیگ مارتی آگ میں ');
INSERT INTO "al_hind" VALUES(111,4,'111:4','اور اُسکی جورو جو سر پر لئے پھرتی ہے ایندھن ');
INSERT INTO "al_hind" VALUES(111,5,'111:5','اُسکی گردن میں رسی ہے مونجھ کی ');
INSERT INTO "al_hind" VALUES(112,1,'112:1','تو کہہ وہ اللہ ایک ہے ');
INSERT INTO "al_hind" VALUES(112,2,'112:2','اللہ بے نیاز ہے ');
INSERT INTO "al_hind" VALUES(112,3,'112:3','نہ کسی کو جنا نہ کسی سے جنا ');
INSERT INTO "al_hind" VALUES(112,4,'112:4','اور نہیں اُس کے جوڑ کا کوئی ');
INSERT INTO "al_hind" VALUES(113,1,'113:1','تو کہہ میں پناہ میں آیا صبح کے رب کی ');
INSERT INTO "al_hind" VALUES(113,2,'113:2','ہر چیز کی بدی سے جو اُس نے بنائی ');
INSERT INTO "al_hind" VALUES(113,3,'113:3','اور بدی سے اندھیرے کی جب سمٹ آئے ');
INSERT INTO "al_hind" VALUES(113,4,'113:4','اور بدی سے عورتوں کی جو گرہوں میں پھونک ماریں ');
INSERT INTO "al_hind" VALUES(113,5,'113:5','اور بدی سے برا چاہنے والے کی جب لگے ٹوک لگانے ');
INSERT INTO "al_hind" VALUES(114,1,'114:1','تو کہہ میں پناہ میں آیا لوگوں کے رب کی');
INSERT INTO "al_hind" VALUES(114,2,'114:2','لوگوں کے بادشاہ کی');
INSERT INTO "al_hind" VALUES(114,3,'114:3','لوگوں کے معبود کی ');
INSERT INTO "al_hind" VALUES(114,4,'114:4','بدی سے اُسکی جو پھسلائے اور چھپ جائے ');
INSERT INTO "al_hind" VALUES(114,5,'114:5','وہ جو خیال ڈالتا ہے لوگوں کے دل میں');
INSERT INTO "al_hind" VALUES(114,6,'114:6','جنوں میں اور آدمیوں میں ');
