INSERT INTO "al_umari" VALUES(1,1,'1:1','अल्लाह के नाम से, जो अत्यन्त कृपाशील तथा दयावान् है।');
INSERT INTO "al_umari" VALUES(1,2,'1:2','सब प्रशंसायें अल्लाह[ के लिए हैं, जो सारे संसारों का पालनहार है।');
INSERT INTO "al_umari" VALUES(1,3,'1:3','जो अत्यंत कृपाशील और दयावान्[ है। ');
INSERT INTO "al_umari" VALUES(1,4,'1:4','जो प्रतिकार[ (बदले) के दिन का मालिक है। ');
INSERT INTO "al_umari" VALUES(1,5,'1:5','(हे अल्लाह!) हम केवल तुझी को पूजते हैं और केवल तुझी से सहायता मांगते[ हैं। ');
INSERT INTO "al_umari" VALUES(1,6,'1:6','हमें सुपथ (सीधा मार्ग) दिखा।');
INSERT INTO "al_umari" VALUES(1,7,'1:7','उनका मार्ग, जिनपर तूने पुरस्कार किया।[ उनका नहीं, जिनपर तेरा प्रकोप हुआ और न ही उनका, जो कुपथ (गुमराह) हो गये।');
INSERT INTO "al_umari" VALUES(2,1,'2:1','अलिफ़, लाम, मीम।');
INSERT INTO "al_umari" VALUES(2,2,'2:2','ये पुस्तक है, जिसमें कोई संशय (संदेह) नहीं, उन्हें सीधी डगर दिखाने के लिए है, जो (अल्लाह से) डरते हैं।');
INSERT INTO "al_umari" VALUES(2,3,'2:3','जो ग़ैब (परोक्ष)[ पर ईमान (विश्वास) रखते हैं तथा नमाज़ की स्थापना करते हैं और जो कुछ हमने उन्हें दिया है, उसमें से दान करते हैं। ');
INSERT INTO "al_umari" VALUES(2,4,'2:4','तथा जो आप (हे नबी!) पर उतारी गयी (पुस्तक क़ुर्आन) तथा आपसे पूर्व उतारी गयी (पुस्तकों)[ पर ईमान रखते हैं तथा आख़िरत (परलोक) पर भी विश्वास रखते हैं।');
INSERT INTO "al_umari" VALUES(2,5,'2:5','वही अपने पालनहार की बताई सीधी डगर पर हैं तथा वही सफल होंगे।');
INSERT INTO "al_umari" VALUES(2,6,'2:6','वास्तव[ में, जो काफ़िर (विश्वासहीन) हो गये, (हे नबी!) उन्हें आप सावधान करें या न करें, वे ईमान नहीं लायेंगे। ');
INSERT INTO "al_umari" VALUES(2,7,'2:7','अल्लाह ने उनके दिलों तथा कानों पर मुहर लगा दी है और उनकी आंखों पर पर्दे पड़े हैं तथा उन्हीं के लिए घोर यातना है।');
INSERT INTO "al_umari" VALUES(2,8,'2:8','और[ कुछ लोग कहते हैं कि हम अल्लाह तथा आख़िरत (परलोक) पर ईमान ले आये, जबकि वे ईमान नहीं रखते। ');
INSERT INTO "al_umari" VALUES(2,9,'2:9','वे अल्लाह तथा जो ईमान लाये, उन्हें धोखा देते हैं। जबकि वे स्वयं अपने-आप को धोखा देते हैं, परन्तु वे इसे समझते नहीं।');
INSERT INTO "al_umari" VALUES(2,10,'2:10','उनके दिलों में रोग (दुविधा) है, जिसे अल्लाह ने और अधिक कर दिया और उनके लिए झूठ बोलने के कारण दुखदायी यातना है।');
INSERT INTO "al_umari" VALUES(2,11,'2:11','और जब उनसे कहा जाता है कि धरती में उपद्रव न करो, तो कहते हैं कि हम तो केवल सुधार करने वाले हैं।');
INSERT INTO "al_umari" VALUES(2,12,'2:12','सावधान! वही लोग उपद्रवी हैं, परन्तु उन्हें इसका बोध नहीं।');
INSERT INTO "al_umari" VALUES(2,13,'2:13','और[ जब उनसे कहा जाता है कि जैसे और लोग ईमान लाये, तुमभी ईमान लाओ, तो कहते हैं कि क्या मूर्खों के समान हमभी विश्वास कर लें? सावधान! वही मूर्ख हैं, परन्तु वे जानते नहीं। ');
INSERT INTO "al_umari" VALUES(2,14,'2:14','तथा जब वे ईमान वालों से मिलते हैं, तो कहते हैं कि हम ईमान लाये और जब अकेले में अपने शैतानों (प्रमुखों) के साथ होते हैं, तो कहते हैं कि हम तुम्हारे साथ हैं। हम तो मात्र परिहास कर रहे हैं।');
INSERT INTO "al_umari" VALUES(2,15,'2:15','अल्लाह उनसे परिहास कर रहा है तथा उन्हें, उनके कुकर्मों में बहकने का अवसर दे रहा है।');
INSERT INTO "al_umari" VALUES(2,16,'2:16','ये वे लोग हैं, जिन्होंने सीधी डगर (सुपथ) के बदले गुमराही (कुपथ) खरीद ली। परन्तु उनके व्यापार में लाभ नहीं हुआ और न उन्होंने सीधी डगर पायी।');
INSERT INTO "al_umari" VALUES(2,17,'2:17','उनकी[ दशा उनके जैसी है, जिन्होंने अग्नि सुलगायी और जब उनके आस-पास उजाला हो गया, तो अल्लाह ने उनका उजाला छीन लिया तथा उन्हें ऐसे अन्धेरों में छोड़ दिया, जिनमें उन्हें कुछ दिखाई नहीं देता। ');
INSERT INTO "al_umari" VALUES(2,18,'2:18','वे गूँगे, बहरे, अंधे हैं। अतः अब वे लौटने वाले नहीं।');
INSERT INTO "al_umari" VALUES(2,19,'2:19','अथवा[ (उनकी दशा) आकाश की वर्षा के समान है, जिसमें अंधेरे और कड़क तथा विद्धुत हो, वे कड़क के कारण, मृत्यु के भय से, अपने कानों में उंगलियाँ डाल लेते हैं और अल्लाह, काफ़िरों को अपने नियंत्रण में लिए हुए है। ');
INSERT INTO "al_umari" VALUES(2,20,'2:20','विद्धुत उनकी आँखों को उचक लेने के समीप हो जाती है। जब उनके लिए चमकती है, तो उसके उजाले में चलने लगते हैं और जब अंधेरा हो जाता है, तो खड़े हो जाते हैं और यदि अल्लाह चाहे, तो उनके कानों को बहरा और उनकी आँखों का अंधा कर दे। निश्चय अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(2,21,'2:21','हे लोगो! केवल अपने उस पालनहार की इबादत (वंदना) करो, जिसने तुम्हें तथा तुमसे पहले वाले लोगों को पैदा किया, इसी में तुम्हारा बचाव[ है। ');
INSERT INTO "al_umari" VALUES(2,22,'2:22','जिसने धरती को तुम्हारे लिए बिछौना तथा गगन को छत बनाया और आकाश से जल बरसाया, फिर उससे तुम्हारे लिए प्रत्येक प्रकार के खाद्य पदार्थ उपजाये, अतः, जानते हुए[ भी उसके साझी न बनाओ। ');
INSERT INTO "al_umari" VALUES(2,23,'2:23','और यदि तुम्हें उसमें कुछ संदेह हो, जो (अथवा क़ुर्आन) हमने अपने भक्त पर उतारा है, तो उसके समान कोई सूरह ले आओ? और अपने समर्थकों को भी, जो अल्लाह के सिवा हों, बुला लो, यदि तुम सच्चे[ हो। ');
INSERT INTO "al_umari" VALUES(2,24,'2:24','और यदि ये न कर सको तथा कर भी नहीं सकोगे, तो उस अग्नि (नरक) से बचो, जिसका ईंधन मानव तथा पत्थर होंगे।');
INSERT INTO "al_umari" VALUES(2,25,'2:25','(हे नबी!) उन लोगों को शुभ सूचना दो, जो ईमान लाये तथा सदाचार किये कि उनके लिए ऐसे स्वर्ग हैं, जिनमें नहरें बह रही होंगी। जब उनका कोई भी फल उन्हें दिया जायेगा, तो कहेंगेः ये तो वही है, जो इससे पहले हमें दिया गया और उन्हें समरूप फल दिये जायेंगे तथा उनके लिए उनमें निर्मल पत्नियाँ होंगी और वे उनमें सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(2,26,'2:26','अल्लाह,[ मच्छर अथवा उससे तुच्छ चीज़ से उपमा (मिसाल) देने से नहीं लज्जाता। जो ईमान लाये, वे जानते हैं कि ये उनके पालनहार की ओर से उचित है और जो काफ़िर (विश्वासहीन) हो गये, वे कहते हैं कि अल्लाह ने इससे उपमा देकर क्या निश्चय किया है? अल्लाह इससे बहुतों को गुमराह (कुपथ) करता है और बहुतों को मार्गदर्शन देता है तथा जो अवज्ञाकारी हैं, उन्हीं को कुपथ करता है। ');
INSERT INTO "al_umari" VALUES(2,27,'2:27','जो अल्लाह से पक्का वचन करने के बाद उसे भंग कर देते हैं तथा जिसे अल्लाह ने जोड़ने का आदेश दिया, उसे तोड़ते हैं और धरती में उपद्रव करते हैं, वही लोग क्षति में पड़ेंगे।');
INSERT INTO "al_umari" VALUES(2,28,'2:28','तुम अल्लाह का इन्कार कैसे करते हो? जबकि पहले तुम निर्जीव थे, फिर उसने तुम्हें जीवन दिया, फिर तुम्हें मौत देगा, फिर तुम्हें (परलोक में) जीवन प्रदान करेगा, फिर तुम उसी की ओर लौटाये[ जाओगे! ');
INSERT INTO "al_umari" VALUES(2,29,'2:29','वही है, जिसने धरती में जो भी है, सबको तुम्हारे लिए उत्पन्न किया, फिर आकाश की ओर आकृष्ट हुआ, तो बराबर सात आकाश बना दिये और वह प्रत्येक चीज़ का जानकार है।');
INSERT INTO "al_umari" VALUES(2,30,'2:30','और (हे नबी! याद करो) जब आपके पालनहार ने फ़रिश्तों से कहा कि मैं धरती में एक ख़लीफ़ा[ बनाने जा रहा हूँ। वे बोलेः क्या तू उसमें उसे बनायेग, जो उसमें उपद्रव करेगा तथा रक्त बहायेगा? जबकि हम तेरी प्रशंसा के साथ तेरे गुण और पवित्रता का गान करते हैं! (अल्लाह) ने कहाः जो मैं जानता हूँ, वह तुम नहीं जानते। ');
INSERT INTO "al_umari" VALUES(2,31,'2:31','और उसने आदम[ को सभी नाम सिखा दिये, फिर उन्हें फ़रिश्तों के समक्ष प्रस्तुत किया और कहाः मुझे इनके नाम बताओ, यदि तुम सच्चे हो! ');
INSERT INTO "al_umari" VALUES(2,32,'2:32','सबने कहाः तू पवित्र है। हम तो उतना ही जानते हैं, जितना तूने हमें सिखाया है। वास्तव में, तू अति ज्ञानी तत्वज्ञ[ है। ');
INSERT INTO "al_umari" VALUES(2,33,'2:33','(अल्लाह ने) कहाः हे आदम! इन्हें इनके नाम बताओ और आदम ने जब उनके नाम बता दिये, तो (अल्लाह ने) कहाःक्या मैंने तुमसे नहीं कहा था कि मैं आकाशों तथा धरती की क्षिप्त बातों को जानता हूँ तथा तुम जो बोलते और मन में रखते हो, सब जानता हूँ?');
INSERT INTO "al_umari" VALUES(2,34,'2:34','और जब हमने फ़रिश्तों से कहाः आदम को सज्दा करो, तो इब्लीस के सिवा सबने सज्दा किया, उसने इन्कार तथा अभिमान किया और काफ़िरों में से हो गया।');
INSERT INTO "al_umari" VALUES(2,35,'2:35','और हमने कहाः हे आदम! तुम और तुम्हारी पत्नी स्वर्ग में रहो तथा इसमें से जिस स्थान से चाहो, मनमानी खाओ और इस वृक्ष के समीप न जाना, अन्यथा अत्याचारियों में से हो जाओगे।');
INSERT INTO "al_umari" VALUES(2,36,'2:36','तो शैतान ने दोनों को उससे भटका दिया और जिस (सुख) में थे, उससे उन्हें निकाल दिया और हमने कहाः तुम सब उससे उतरो, तुम एक-दूसरे के शत्रु हो और तुम्हारे लिए धरती में रहना तथा एक निश्चित अवधि[ तक उपभोग्य है। ');
INSERT INTO "al_umari" VALUES(2,37,'2:37','फिर आदम ने अपने पालनहार से कुछ शब्द सीखे, तो उसने उसे क्षमा कर दिया। वह बड़ा क्षमी दयावान्[ है। ');
INSERT INTO "al_umari" VALUES(2,38,'2:38','हमने कहाः इससे सब उतरो, फिर यदि तुम्हारे पास मेरा मार्गदर्शन आये, तो जो मेरे मार्गदर्शन का अनुसरण करेंगे, उनके लिए कोई डर नहीं होगा और न वे उदासीन होंगे।');
INSERT INTO "al_umari" VALUES(2,39,'2:39','तथा जो अस्वीकार करेंगे और हमारी आयतों को मिथ्या कहेंगे, तो वही नारकी हैं और वही उसमें सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(2,40,'2:40','हे बनी इस्राईल![ मेरे उस पुरस्कार को याद करो, जो मैंने तुमपर किया तथा मुझसे किया गया वचन पूरा करो, मैं तुम्हें अपना दिया वचन पूरा करूँगा तथा मुझी से डरो। ');
INSERT INTO "al_umari" VALUES(2,41,'2:41','तथा उस (क़ुर्आन) पर ईमान लाओ, जो मैंने उतारा है, वह उसका प्रमाणकारी है, जो तुम्हारे पास[ है और तुम, सबसे पहले इसके निवर्ती न बन जाओ तथा मेरी आयतों को तनिक मूल्य पर न बेचो और केवल मुझी से डरो। ');
INSERT INTO "al_umari" VALUES(2,42,'2:42','तथा सत्य को असत्य से न मिलाओ और न सत्य को जानते हुए छुपाओ।[ ');
INSERT INTO "al_umari" VALUES(2,43,'2:43','तथा नमाज़ की स्थापना करो और ज़कात दो तथा झुकने वालों के साथ झुको (रुकू करो)');
INSERT INTO "al_umari" VALUES(2,44,'2:44','क्या तुम, लोगों को सदाचार का आदेश देते हो और अपने-आपको भूल जाते हो? जबकि तुम पुस्तक (तौरात) का अध्ययन करते हो! क्या तुम समझ नहीं रखते?[ ');
INSERT INTO "al_umari" VALUES(2,45,'2:45','तथा धैर्य और नमाज़ का सहारा लो, निश्चय नमाज़ भारी है, परन्तु विनीतों पर (भारी नहीं)।[ ');
INSERT INTO "al_umari" VALUES(2,46,'2:46','जो समझते हैं कि उन्हें अपने पालनहार से मिलना है और उन्हें फिर उसी की ओर (अपने कर्मों का फल भोगने के लिए) जाना है।');
INSERT INTO "al_umari" VALUES(2,47,'2:47','हे बनी इस्राईल! मेरे उस पुरस्कार को याद करो, जो मैंने तुमपर किया और ये कि तुम्हें संसार वासियों पर प्रधानता दी थी।');
INSERT INTO "al_umari" VALUES(2,48,'2:48','तथा उस दिन से डरो, जिस दिन कोई किसी के कुछ काम नहीं आयेगा और न उसकी कोई अनुशंसा (सिफ़ारिश) मानी जायेगी और न उससे कोई अर्थदण्ड लिया जायेगा और न उन्हें कोई सहायता मिल सकेगी।');
INSERT INTO "al_umari" VALUES(2,49,'2:49','तथा (वह समय याद करो) जब हमने तुम्हें फ़िरऔनियों[ से मुक्ति दिलाई। वे तुम्हें कड़ी यातना दे रहे थे; वे तुम्हारे पुत्रों को वध कर रहे थे तथा तुम्हारी नारियों को जीवित रहने देते थे। इसमें तुम्हारे पालनहार की ओर से कड़ी परीक्षा थी। ');
INSERT INTO "al_umari" VALUES(2,50,'2:50','तथा (याद करो) जब हमने तुम्हारे लिए सागर को फाड़ दिया, फिर तुम्हें बचा लिया और तुम्हारे देखते-देखते फ़िरऔनियों को डुबो दिया।');
INSERT INTO "al_umari" VALUES(2,51,'2:51','तथा (याद करो) जब हमने मूसा को (तौरात प्रदान करने के लिए) चालीस रात्रि का वचन दिया, फिर उनके पीछे तुमने बछड़े को (पूज्य) बना लिया और तुम अत्याचारी थे।');
INSERT INTO "al_umari" VALUES(2,52,'2:52','फिर हमने इसके पश्चात् तुम्हें क्षमा कर दिया, ताकि तुम कृतज्ञ बनो।');
INSERT INTO "al_umari" VALUES(2,53,'2:53','तथा (याद करो) जब हमने मूसा को पुस्तक (तौरात) तथा फ़ुर्क़ान[ प्रदान किया, ताकि तुम सीधी डगर पा सको। ');
INSERT INTO "al_umari" VALUES(2,54,'2:54','तथा (याद करो) जब मूसा ने अपनी जाति से कहाः तुमने बछड़े को पूज्य बनाकर अपने ऊपर अत्याचार किया है, अतः तुम अपने उत्पत्तिकार के आगे क्षमा याचना करो, वो ये कि आपस में एक-दूसरे[ को वध करो। इसी में तुम्हारे उत्पत्तिकार के समीप तुम्हारी भलाई है। फिर उसने तुम्हारी तौबा स्वीकार कर ली। वास्तव में, वह बड़ा क्षमाशील, दयावान् है। ');
INSERT INTO "al_umari" VALUES(2,55,'2:55','तथा (याद करो) जब तुमने मूसा से कहाः हम तुम्हारा विश्वास नहीं करेंगे, जब तक हम अल्लाह को आँखों से देख नहीं लेंगे, फिर तुम्हारे देखते-देखते तुम्हें कड़क ने धर लिया (जिससे सब निर्जीव हो कर गिर गये)।');
INSERT INTO "al_umari" VALUES(2,56,'2:56','फिर (निर्जीव होने के पश्चात्) हमने तुम्हें जीवित कर दिया, ताकि तुम हमारा उपकार मानो।');
INSERT INTO "al_umari" VALUES(2,57,'2:57','और हमने तुमपर बादलों की छाँव[ की तथा तुमपर ''मन्न'' और ''सलवा'' उतारा, तो उन स्वच्छ चीज़ों में से, जो हमने तुम्हें प्रदान की हैं, खाओ और उन्होंने हमपर अत्याचार नहीं किया, परन्तु वे स्वयं अपने ऊपर ही अत्याचार कर रहे थे।');
INSERT INTO "al_umari" VALUES(2,58,'2:58','और (याद करो) जब हमने कहा कि इस बस्ती[ में प्रवेश करो, फिर इसमें से जहाँ से चाहो, मनमानी खाओ और उसके द्वार में सज्दा करते (सिर झुकाये) हुए प्रवेश करो और क्षमा-क्षमा कहते जाओ, हम तुम्हारे पापों को क्षमा कर देंगे तथा सुकर्मियों को अधिक प्रदान करेंगे। ');
INSERT INTO "al_umari" VALUES(2,59,'2:59','फिर इन अत्याचारियों ने जो बात इनसे कही गयी थी, उसे दूसरी बात से बदल दिया। तो हमने इन अत्याचारियों पर आकाश से इनकी अवज्ञा के कारण प्रकोप उतार दिया।');
INSERT INTO "al_umari" VALUES(2,60,'2:60','तथा (याद करो) जब मूसा ने अपनी जाति के लिए जल की प्रार्थना की, तो हमने कहाः अपनी लाठी पत्थर पर मारो। तो उससे बारह[ सोते फूट पड़े और प्रत्येक परिवार ने अपने पीने के स्थान को पहचान लिया। अल्लाह का दिया खाओ और पिओ और धरती में उपद्रव करते न फिरो। ');
INSERT INTO "al_umari" VALUES(2,61,'2:61','तथा (याद करो) जब तुमने कहाः हे मूसा! हम एक प्रकार का खाना सहन नहीं करेंगे। तुम अपने पालनहार से प्रार्थना करो कि हमारे लिए धरती की उपज; साग, ककड़ी, लहसुन, प्याज़, दाल आदि निकाले। (मूसा ने) कहाः क्या तुम उत्तम के बदले तुच्छ मांगते हो? तो किसी नगर में उतर पड़ो, जो तुमने मांगा है, वहाँ वह मिलेगा! और उनपर अपमान तथा दरिद्रता थोप दी गयी और वे अल्लाह के प्रकोप के साथ फिरे। ये इसलिए कि वे अल्लाह की आयतों के साथ कुफ़्र कर रहे थे और नबियों की अकारण हत्या कर रहे थे। ये इसलिए कि उन्होंने अवज्ञा की तथा (धर्म की) सीमा का उल्लंघन किया।');
INSERT INTO "al_umari" VALUES(2,62,'2:62','वस्तुतः, जो ईमान लाये तथा जो यहूदी हुए और नसारा (ईसाई) तथा साबी, जो भी अल्लाह तथा अन्तिम दिन (प्रलय) पर ईमान लायेगा और सत्कर्म करेगा, उनका प्रतिफल उनके पालनहार के पास है और उन्हें कोई डर नहीं होगा और न ही वे उदासीन होंगे।[ ');
INSERT INTO "al_umari" VALUES(2,63,'2:63','और (याद करो) जब हमने तूर (पर्वत) को तुम्हारे ऊपर करके तुमसे वचन लिया कि जो हमने तुम्हें दिया है, उसे दृढ़ता से पकड़ लो और उसमें जो (आदेश-निर्देश) हैं, उन्हें याद रखो; ताकि तुम यातना से बच सको।');
INSERT INTO "al_umari" VALUES(2,64,'2:64','फिर उसके बाद तुम मुकर गये, तो यदि तुमपर अल्लाह की अनुग्रह और दया न होती, तो तुम क्षतिग्रस्तों में हो जाते।');
INSERT INTO "al_umari" VALUES(2,65,'2:65','और तुम उन्हें जानते ही हो, जिन्होंने शनिवार के बारे में (धर्म की) सीमा का उल्लंघन किया, तो हमने कहा कि तुम तिरस्कृत बंदर[ हो जाओ। ');
INSERT INTO "al_umari" VALUES(2,66,'2:66','फिर हमने उसे, उस समय के तथा बाद के लोगों के लिए चेतावनी और अल्लाह से डरने वालों के लिए शिक्षा बना दिया।');
INSERT INTO "al_umari" VALUES(2,67,'2:67','तथा (याद करो) जब मूसा ने अपनी जाति से कहाः अल्लाह तुम्हें एक गाय वध करने का आदेश देता है। उन्होंने कहाः क्या तुम हमसे उपहास कर रहे हो? (मूसा ने) कहाः मैं अल्लाह की शरण माँगता हूँ कि मूर्खों में से हो जाऊँ।');
INSERT INTO "al_umari" VALUES(2,68,'2:68','वह बोले कि अपने पालनहार से हमारे लिए निवेदन करो कि हमें बता दे कि वह गाय कैसी हो? (मूसा ने) कहाः वह (अर्थातःअल्लाह) कहता है कि वह न बूढ़ी हो और न बछिया हो, इसके बीच आयु की हो। अतः जो आदेश तुम्हें दिया जा रहा है, उसे पूरा करो।');
INSERT INTO "al_umari" VALUES(2,69,'2:69','वे बोले कि अपने पालनहार से हमारे लिए निवेदन करो कि हमें उसका रंग बता दे। (मूसा ने) कहाः वह कहता है कि पीले गहरे रंग की गाय हो, जो देखने वालों को प्रसन्न कर दे।');
INSERT INTO "al_umari" VALUES(2,70,'2:70','वे बोले कि अपने पालनहार से हमारे लिए निवेदन करो कि हमें बताये कि वह किस प्रकार की हो? वास्तव में, हम गाय के बारे में दुविधा में पड़ गये हैं और यदि अल्लाह ने चाहा तो हम (उस गाय का) पता लगा लेंगे।');
INSERT INTO "al_umari" VALUES(2,71,'2:71','मूसा बोलेः वह कहता है कि वह ऐसी गाय हो, जो सेवा कार्य न करती हो, न खेत (भूमि) जोतती हो और न खेत सींचती हो, वह स्वस्थ हो और उसमें कोई धब्बा न हो। वे बोलेः अब तुमने उचित बात बताई है। फिर उन्होंने उसे वध कर दिया। जबकि वे समीप थे कि ये काम न करें।');
INSERT INTO "al_umari" VALUES(2,72,'2:72','और (याद करो) जब तुमने एक व्यक्ति की हत्या कर दी तथा एक-दूसरे पर (दोष) थोपने लगे और अल्लाह को उसे व्यक्त करना था, जिसे तुम छुपा रहे थे।');
INSERT INTO "al_umari" VALUES(2,73,'2:73','अतः हमने कहा कि उसे (निहत व्यक्ति के शव को) उस (गाय) के किसी भाग से मारो।[ इसी प्रकार अल्लाह मुर्दों को जीवित करेगा और वह तुम्हें अपनी निशानियाँ दिखाता है; ताकि तुम समझो। ');
INSERT INTO "al_umari" VALUES(2,74,'2:74','फिर ये (निशानियाँ देखने) के बाद तुम्हारे दिल पत्थरों के समान या उनसे भी अधिक कठोर हो गये; क्योंकि पत्थरों में कुछ ऐसे होते हैं, जिनसे नहरें फूट पड़ती हैं और कुछ फट जाते हैं और उनसे पानी निकल आता है और कुछ अल्लाह के डर से गिर पड़ते हैं और अल्लाह तुम्हारे करतूतों से निश्चेत नहीं है।');
INSERT INTO "al_umari" VALUES(2,75,'2:75','क्या तुम आशा रखते हो कि (यहूदी) तुम्हारी बात मान लेंगे, जबकि उनमें एक गिरोह ऐसा था, जो अल्लाह की वाणी (तौरात) को सुनता था और समझ जाने के बाद जान-बूझ कर उसमें परिवर्तण कर देता था?');
INSERT INTO "al_umari" VALUES(2,76,'2:76','तथा जब वे ईमान वालों से मिलते हैं, तो कहते हैं कि हम भी ईमान लाये और जब एकान्त में आपस में एक-दूसरे से मिलते हैं, तो कहते हैं कि तुम उन्हें वो बातें क्यों बताते हो, जो अल्लाह ने तुमपर खोली[ हैं? इसलिए कि प्रलय के दिन तुम्हारे पालनहार के पास इसे तुम्हारे विरुध्द प्रमाण बनायें? क्या तुम समझते नहीं हो? ');
INSERT INTO "al_umari" VALUES(2,77,'2:77','क्या वे नहीं जानते कि वे जो कुछ छुपाते तथा व्यक्त करते हैं, वो सब अल्लाह जानता है?');
INSERT INTO "al_umari" VALUES(2,78,'2:78','तथा उनमें कुछ अनपढ़ हैं, वे पुस्तक (तौरात) का ज्ञान नहीं रखते, परन्तु निराधार कामनायें करते तथा केवल अनुमान लगाते हैं।');
INSERT INTO "al_umari" VALUES(2,79,'2:79','तो विनाश है उनके लिए[ जो अपने हाथों से पुस्तक लिखते हैं, फिर कहते हैं कि ये अल्लाह की ओर से है, ताकि उसके द्वारा तनिक मूल्य खरीदें! तो विनाश है उनके अपने हाथों के लेख के कारण! और विनाश है उनकी कमाई के कारण! ');
INSERT INTO "al_umari" VALUES(2,80,'2:80','तथा उन्होंने कहा कि हमें नरक की अग्नि गिनती के कुछ दिनों के सिवा स्पर्श नहीं करेगी। (हे नबी!) उनसे कहो कि क्या तुमने अल्लाह से कोई वचन ले लिया है कि अल्लाह अपना वचन भंग नहीं करेगा? बल्कि तुम अल्लाह के बारे में ऐसी बातें करते हो, जिनका तुम्हें ज्ञान नहीं।');
INSERT INTO "al_umari" VALUES(2,81,'2:81','क्यों[ नहीं, जो भी बुराई कमायेगा तथा उसका पाप उसे घेर लेगा, तो वही नारकीय हैं और वही उसमें सदावासी होंगे। ');
INSERT INTO "al_umari" VALUES(2,82,'2:82','तथा जो ईमान लायें और सत्कर्म करें, वही स्वर्गीय हैं और वे उसमें सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(2,83,'2:83','और (याद करो) जब हमने बनी इस्राईल से दृढ़ वचन लिया कि अल्लाह के सिवा किसी की इबादत (वंदना) नहीं करोगे तथा माता-पिता के साथ उपकार करोगे और समीपवर्ती संबंधियों, अनाथों, दीन-दुखियों के साथ और लोगों से भली बात बोलोगे तथा नमाज़ की स्थापना करोगे और ज़कात दोगे, फिर तुममें से थोड़े के सिवा सबने मुँह फेर लिया और तुम (अभी भी) मुँह फेरे हुए हो।');
INSERT INTO "al_umari" VALUES(2,84,'2:84','तथा (याद करो) जब हमने तुमसे दृढ़ वचन लिया कि आपस में रक्तपात नहीं करोगे और न अपनों को अपने घरों से निकालोगे। फिर तुमने स्वीकार किया और तुम उसके साक्षी हो।');
INSERT INTO "al_umari" VALUES(2,85,'2:85','फिर[ तुम वही हो, जो अपनों की हत्या कर रहे हो तथा अपनों में से एक गिरोह को उनके घरों से निकाल रहे हो और पाप तथा अत्याचार के साथ उनके विरुध्द सहायता करते हो और यदि वे बंदी होकर तुम्हारे पास आयें, तो उनका अर्थदण्ड चुकाते हो, जबकि उन्हें निकालना ही तुमपर ह़राम (अवैध) था, तो क्या तुम पुस्तक के कुछ भाग पर ईमान रखते हो और कुछ का इन्कार करते हो? फिर तुममें से जो ऐसा करते हों, तो उनका दण्ड क्या है, इसके सिवा कि सांसारिक जीवन में अपमान तथा प्रलय के दिन अति कड़ी यातना की ओर फेरे जायें? और अल्लाह तुम्हारे करतूतों से निश्चेत नहीं है! ');
INSERT INTO "al_umari" VALUES(2,86,'2:86','उन्होंने ही आख़िरत (परलोक) के बदले सांसारिक जीवन ख़रीद लिया। अतः उनसे यातना मंद नहीं की जायेगी और न उनकी सहायता की जायेगी।');
INSERT INTO "al_umari" VALUES(2,87,'2:87','तथा हमने मूसा को पुस्तक (तौरात) प्रदान की और उसके पश्चात् निरन्तर रसूल भेजे और हमने मर्यम के पुत्र ईसा को खुली निशानियाँ दीं और रूह़ुल क़ुदुस[ द्वारा उसे समर्थन दिया, तो क्या जब भी कोई रसूल तुम्हारी अपनी मनमानी के विरुध्द कोई बात तुम्हारे पास लेकर आया, तो तुम अकड़ गये, अतः कुछ नबियों को झुठला दिया और कुछ की हत्या करने लगे? ');
INSERT INTO "al_umari" VALUES(2,88,'2:88','तथा उन्होंने कहा कि हमारे दिल तो बंद[ हैं। बल्कि उनके कुफ़्र (इन्कार) के कारण अल्लाह ने उन्हें धिक्कार दिया है। इसीलिए उनमें से बहुत थोड़े ही ईमान लाते हैं। ');
INSERT INTO "al_umari" VALUES(2,89,'2:89','और जब उनके पास अल्लाह की ओर से एक पुस्तक (क़ुर्आन) आ गयी, जो उनके साथ की पुस्तक का प्रमाणकारी है, जबकि इससे पूर्व वे स्वयं काफ़िरों पर विजय की प्रार्थना कर रहे थे, तो जब उनके पास वह चीज़ आ गयी, जिसे वे पहचान भी गये, फिर भी उसका इन्कार कर[ दिया, तो काफ़िरों पर अल्लाह की धिक्कार है। ');
INSERT INTO "al_umari" VALUES(2,90,'2:90','अल्लाह की उतारी हुई (पुस्तक)[ का इन्कार करके बुरे बदले पर इन्होंने अपने प्राणों को बेच दिया, इस द्वेष के कारण कि अल्लाह ने अपना प्रदान (प्रकाशना), अपने जिस भक्त पर चाहा, उतार दिया। अतः वे प्रकोप पर प्रकोप के अधिकारी बन गये और ऐसे काफ़िरों के लिए अपमानकारी यातना है।');
INSERT INTO "al_umari" VALUES(2,91,'2:91','और जब उनसे कहा जाता है कि अल्लाह ने जो उतारा[ है, उसपर ईमन लाओ, तो कहते हैं: हम तो उसीपर ईमान रखते हैं, जो हमपर उतरा है और इसके सिवा जो कुछ है, उसका इन्कार करते हैं। जबकि वह सत्य है और उसका प्रमाण्कारी है, जो उनके पास है। कहो कि फिर इससे पूर्व अल्लाह के नबियों की हत्या क्यों करते थे, यदि तुम ईमान वाले थे तो? ');
INSERT INTO "al_umari" VALUES(2,92,'2:92','तथा मूसा तुम्हारे पास खुली निशानियाँ लेकर आये। फिर तुमने अत्याचार करते हुए बछड़े को पूज्य बना लिया।');
INSERT INTO "al_umari" VALUES(2,93,'2:93','फिर उस दृढ़ वचन को याद करो, जो हमने तुम्हारे ऊपर तूर (पर्वत) को उठाकर लिया। (हमने कहाः) पकड़ लो, जो हमने दिया है तुम्हें दृढ़ता से और सुनो। तो उन्होंने कहाः हमने सुना और अवज्ञा की। और उनके दिलों में बछड़े का प्रेम पिला दिया गया, उनकी अवज्ञा के कारण। (हे नबी!) आप कह दीजियेः बुरा है वह जिसका आदेश दे रहा है तुम्हें तुम्हारा ईमान, यदि तुम ईमान वाले हो।');
INSERT INTO "al_umari" VALUES(2,94,'2:94','(हे नबी!) आप कह दीजियेः यदि तुम्हारे लिये विशेष है परलोक का घर सारे लोगों को छोड़कर, तो तुम कामना करो मौत की, यदि तुम सत्यवादी हो।');
INSERT INTO "al_umari" VALUES(2,95,'2:95','और वे कदापि उसकी कामना नहीं करेंगे, उनके कर्तूतों के कारण और अल्लाह अत्याचारियों से अधिक सूचित है।');
INSERT INTO "al_umari" VALUES(2,96,'2:96','तुम इन्हें, सबसे बढ़कर जीने का लोभी पाओगे और मिश्रणवादियों से (भी)। इनमें से प्रत्येक व्यक्ति चाहता है कि उसे एक-एक हज़ार वर्ष की आयु मिल जाये। ह़ालाँकि ये (लम्बी) आयु भी उसे यातना से बचा नहीं सकती और अल्लाह देखने वाला है जो वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(2,97,'2:97','(हे नबी!)[ कह दो कि जो व्यक्ति जिब्रील का शत्रु है, (तो रहे)। उसने तो अल्लाह की अनुमति से इस संदेश (क़ुर्आन) को आपके दिल पर उतारा है, जो इससे पूर्व की सभी पुस्तकों का प्रमाणकारी तथा ईमान वालों के लिए मार्गदर्शन एवं (सफलता) का शुभ समाचार है। ');
INSERT INTO "al_umari" VALUES(2,98,'2:98','जो अल्लाह तथा उसके फ़रिश्तों और उसके रसूलों और जिब्रील तथा मीकाईल का शत्रु हो, तो अल्लाह (उन) काफ़िरों का शत्रु है।[ ');
INSERT INTO "al_umari" VALUES(2,99,'2:99','और (हे नबी!) हमने आप पर खुली आयतें उतारी हैं और इनका इन्कार केवल वही लोग[ करेंगे, जो कुकर्मी हैं। ');
INSERT INTO "al_umari" VALUES(2,100,'2:100','क्या ऐसा नहीं हुआ है कि जब कभी उन्होंने कोई वचन दिया, तो उनके एक गिरोह ने उसे भंग कर दिया? बल्कि इनमें बहुतेरे ऐसे हैं, जो ईमान नहीं रखते।');
INSERT INTO "al_umari" VALUES(2,101,'2:101','तथा जब उनके पास अल्लाह की ओर से एक रसूल, उस पुस्तक का समर्थन करते हुए, जो उनके पास है, आ गया,[ तो उनके एक समुदाय ने जिनहें पुस्तक दी गयी, अल्लाह की पुस्तक को ऐसे पीछे डाल दिया, जैसे वे कुछ जानते ही न हों। ');
INSERT INTO "al_umari" VALUES(2,102,'2:102','तथा सुलैमान के राज्य में शैतान जो मिथ्या बातें बना रहे थे, उनका अनुसरण करने लगे। जबकि सुलैमान ने कभी कुफ़्र (जादू) नहीं किया, परन्तु कुफ़्र तो शैतानों ने किया, जो लोगों को जादू सिखा रहे थे तथा वे उन बातों का (अनुसरण करने लगे) जो बाबिल (नगर) के दो फ़रिश्तों; हारूत और मारूत पर उतारी गयीं, जबकि वे दोनों किसी को जादू नहीं सिखाते, जब तक ये न कह देते कि हम केवल एक परीक्षा हैं, अतः, तू कुफ़्र में न पड़। फिर भी वे उन दोनों से वो चीज़ सीखते, जिसके द्वारा वे पति और पत्नी के बीच जुदाई डाल दें और वे अल्लाह की अनुमति बिना इसके द्वारा किसी को कोई हानि नहीं पहुँचा सकते थे, परन्तु फिर भी ऐसी बातें सीखते थे, जो उनके लिए हानिकारक हों और लाभकारी न हों और वे भली-भाँति जानते थे कि जो इसका ख़रीदार बना, परलोक में उसका कोई भाग नहीं तथा कितना बुरा उपभोग्य है, जिसके बदले वे अपने प्राणों का सौदा कर रहे हैं[, यदि वे जानते होते! ');
INSERT INTO "al_umari" VALUES(2,103,'2:103','और यदि वे ईमान लाते और अल्लाह से डरते, तो अल्लाह के पास इसका जो प्रतिकार (बदला) मिलता, वह उनके लिए उत्तम होता, यदि वे जानते होते।');
INSERT INTO "al_umari" VALUES(2,104,'2:104','हे ईमान वालो! तुम ''राइना''[ न कहो, ''उन्ज़ुरना'' कहो और ध्यान से बात सुनो तथा काफ़िरों के लिए दुखदायी यातना है। ');
INSERT INTO "al_umari" VALUES(2,105,'2:105','अह्ले किताब में से जो काफ़िर हो गये तथा जो मिश्रणवादी हो गये, वे नहीं चाहते कि तुम्हारे पालनहार की ओर से तुमपर कोई भलाई उतारी जाये और अल्लाह जिसपर चाहे, अपनी विशेष दया करता है और अल्लाह बड़ा दानशील है।');
INSERT INTO "al_umari" VALUES(2,106,'2:106','हम अपनी कोई आयत निरस्त कर देते अथवा भुला देते हैं, तो उससे उत्तम अथवा उसके समान लाते हैं। क्या तुम नहीं जानते कि अल्लाह जो चाहे[, कर सकता है? ');
INSERT INTO "al_umari" VALUES(2,107,'2:107','क्या तुम ये नहीं जानते कि आकाशों तथा धरती का राज्य अल्लाह ही के लिए है और उसके सिवा तुम्हारा कोई रक्षक और सहायक नहीं है?');
INSERT INTO "al_umari" VALUES(2,108,'2:108','क्या तुम चाहते हो कि अपने रसुल से उसी प्रकार प्रश्न करो, जैसे मूसा से प्रश्न किये जाते रहे? और जो व्यक्ति ईमान की नीति को कुफ़्र से बदल लेगा, तो वह सीधी डगर से विचलित हो गया।');
INSERT INTO "al_umari" VALUES(2,109,'2:109','अहले किताब में से बहुत-से चाहते हैं कि तुम्हारे ईमान लाने के पश्चात् अपने द्वेष के कारण तुम्हें कुफ़्र की ओर फेंक दें। जबकि सत्य उनके लिए उजागर हो गया। फिर भी तुम क्षमा से काम लो और जाने दो। यहाँ तक कि अल्लाह अपना निर्णय कर दे। निश्चय अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(2,110,'2:110','तथा तुम नमाज़ की स्थापना करो और ज़कात दो और जो भी भलाई अपने लिए किये रहोगे, उसे अल्लाह के यहाँ पाओगे। तुम जो कुछ कर रहे हो, अल्लाह उसे देख रहा है।');
INSERT INTO "al_umari" VALUES(2,111,'2:111','तथा उन्होंने कहा कि कोई स्वर्ग में कदापि नहीं जायेगा, जब तक यहूदी अथवा नसारा[ (ईसाई) न हो। ये उनकी कामनायें हैं। उनसे कहो कि यदि तुम सत्यवादी हो, तो कोई प्रमाण प्रस्तुत करो। ');
INSERT INTO "al_umari" VALUES(2,112,'2:112','क्यों नहीं?[ जो भी स्वयं को अल्लाह की आज्ञा पालन के लिए समर्पित कर देगा तथा सदाचारी होगा, तो उसके पालनहार के पास उसका प्रतिफल है और उनपर कोई भय नहीं होगा और न वे उदासीन होंगे। ');
INSERT INTO "al_umari" VALUES(2,113,'2:113','तथा यहूदियों ने कहा कि ईसाईयों के पास कुछ नहीं और ईसाईयों ने कहा कि यहूदियों के पास कुछ नहीं है। जबकि वे धर्म पुस्तक[ पढ़ते हैं। इसी जैसी बात उन्होंने भी कही, जिनके पास धर्म पुस्तक का कोई ज्ञान नहीं। ये जिस विषय में विभेद कर रहे हैं, उसका निर्णय अल्लाह प्रलय के दिन उनके बीच कर देगा।');
INSERT INTO "al_umari" VALUES(2,114,'2:114','और उससे बड़ा अत्याचारी कौन होगा, जो अल्लाह की मस्जिदों में उसके नाम का वर्णन करने से रोके और उन्हें उजाड़ने का प्रयत्न करे?[ उन्हीं के लिए योग्य है कि उसमें डरते हुए प्रवेश करें, उन्हीं के लिए संसार में अपमान है और उन्हीं के लिए आख़िरत (परलोक) में घोर यातना है। ');
INSERT INTO "al_umari" VALUES(2,115,'2:115','तथा पूर्व और पश्चिम अल्लाह ही के हैं; तुम जिधर भी मुख करो[, उधर ही अल्लाह का मुख है और अल्लाह विशाल अति ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(2,116,'2:116','तथा उन्होंने कहा[ कि अल्लाह ने कोई संतान बना ली। वह इससे पवित्र है। आकाशों तथा धरती में जो भी है, वह उसी का है और सब उसी के आज्ञाकारी हैं। ');
INSERT INTO "al_umari" VALUES(2,117,'2:117','वह आकाशों तथा धरती का अविष्कारक है। जब वह किसी बात का निर्णय कर लेता है, तो उसके लिए बस ये आदेश देता है कि "हो जा।" और वह हो जाती है।');
INSERT INTO "al_umari" VALUES(2,118,'2:118','तथा उन्होंने कहा जो ज्ञान[ नहीं रखते कि अल्लाह हमसे बात क्यों नहीं करता या हमारे पास कोई आयत क्यों नहीं आती? इसी प्रकार की बात इनसे पूर्व के लोगों ने कही थी। इनके दिल एक समान हो गये। हमने उनके लिए निशानियाँ उजागर कर दी हैं, जो विश्वास रखते हैं। ');
INSERT INTO "al_umari" VALUES(2,119,'2:119','(हे नबी!) हमने आपको सत्य के साथ शुभ सूचना देने तथा सावधान[ करने वाला बनाकर भेजा है और आपसे नारकियों के विषय में प्रश्न नहीं किया जायेगा। ');
INSERT INTO "al_umari" VALUES(2,120,'2:120','(हे नबी!) आपसे यहूदी तथा ईसाई सहमत (प्रसन्न) नहीं होंगे, जब तक आप उनकी रीति पर न चलें। कह दो कि सीधी डगर वही है, जो अल्लाह ने बताई है और यदि आपने उनकी आकांक्षाओं का अनुसरण किया, इसके पश्चात कि आपके पास ज्ञान आ गया, तो अल्लाह (की पकड़) से आपका कोई रक्षक और सहायक नहीं होगा।');
INSERT INTO "al_umari" VALUES(2,121,'2:121','और हमने जिन्हें पुस्तक प्रदान की है और उसे वैसे पढ़ते हैं, जैसे पढ़ना चाहिये, वही उसपर ईमान रखते हैं और जो उसे नकारते हैं, वही क्षतिग्रस्तों में से हैं।');
INSERT INTO "al_umari" VALUES(2,122,'2:122','हे बनी इस्राईल! मेरे उस पुरस्कार को याद करो, जो मैंने तुमपर किया है और ये कि तुम्हें (अपने युग के) संसार-वसियों पर प्रधानता दी थी।');
INSERT INTO "al_umari" VALUES(2,123,'2:123','तथा उस दिन से डरो, जब कोई व्यक्ति किसी व्यक्ति के कुछ काम नहीं आयेगा और न उससे कोई अर्थदण्ड स्वीकार किया जायेगा और न उसे कोई अनुशंसा (सिफ़ारिश) लाभ पहुँचायेगी और न उनकी कोई सहायता की जायेगी।');
INSERT INTO "al_umari" VALUES(2,124,'2:124','और (याद करो) जब इब्राहीम की उसके पालनहार ने कुछ बातों से परीक्षा ली और वह उसमें पूरा उतरा, तो उसने कहा कि मैं तुम्हें सब इन्सानों का इमाम (धर्मगुरु) बनाने वाला हूँ। (इब्राहीम ने) कहाः तथा मेरी संतान से भी। (अल्लाह ने कहाः) मेरा वचन उनके लिए नहीं, जो अत्याचारी[ हैं। ');
INSERT INTO "al_umari" VALUES(2,125,'2:125','और (याद करो) जब हमने इस घर (अर्थातःकाबा) को लोगों के लिए बार-बार आने का केंद्र तथा शांति स्थल निर्धारित कर दिया तथा ये आदेश दे दिया कि ''मक़ामे इब्राहीम'' को नमाज़ का स्थान[ बना लो तथा इब्राहीम और इस्माईल को आदेश दिया कि मेरे घर को तवाफ़ (परिक्रमा) तथा एतिकाफ़ करने वालों और सज्दा तथा रुकू करने वालों के लिए पवित्र रखो।');
INSERT INTO "al_umari" VALUES(2,126,'2:126','और (याद करो) जब इब्राहीम ने अपने पालनहार से प्रार्थना कीः हे मेरे पालनहार! इस छेत्र को शांति का नगर बना दे तथा इसके वासियों को, जो उनमें से अल्लाह और अंतिम दिन (प्रलय) पर ईमान रखे, विभिन्न प्रकार की उपज (फलों) से आजीविका प्रदान कर। (अल्लाह ने) कहाः तथा जो काफ़िर है, उसे भी मैं थोड़ा लाभ दूंगा, फिर उसे नरक की यातना की ओर बाध्य कर दूँगा और वह बहुत बुरा स्थान है।');
INSERT INTO "al_umari" VALUES(2,127,'2:127','और (याद करो) जब इब्राहीम और इस्माईल इस घर की नींव ऊँची कर रहे थे तथा प्रार्थना कर रहे थेः हे हमारे पालनहार! हमसे ये सेवा स्वीकार कर ले। तू ही सब कुछ सुनता और जानता है।');
INSERT INTO "al_umari" VALUES(2,128,'2:128','हे हमारे पालनहार! हम दोनों को अपना आज्ञाकारी बना तथा हमारी संतान से एक ऐसा समुदाय बना दे, जो तेरा आज्ञाकारी हो और हमें हमारे (हज्ज की) विधियाँ बता दे तथा हमें क्षमा कर। वास्तव में, तू अति क्षमी, दयावान् है।');
INSERT INTO "al_umari" VALUES(2,129,'2:129','हे हमारे पालनहार! उनके बीच उन्हीं में से एक रसूल भेज, जो उन्हें तेरी आयतें सुनाये और उन्हें पुस्तक (क़ुर्आन) तथा ह़िक्मत (सुन्नत) की शिक्षा दे और उन्हें शुध्द तथा आज्ञाकारी बना दे। वास्तव में, तू ही प्रभुत्वशाली तत्वज्ञ[ है। ');
INSERT INTO "al_umari" VALUES(2,130,'2:130','तथा कौन होगा, जो ईब्राहीम के धर्म से विमुख हो जाये, परन्तु वही जो स्वयं को मूर्ख बना ले? जबकि हमने उसे संसार में चुन[ लिया तथा आख़िरत (परलोक) में उसकी गणना सदाचारियों में होगी। ');
INSERT INTO "al_umari" VALUES(2,131,'2:131','तथा (याद करो) जब उसके पालनहार ने उससे कहाः मेरा आज्ञाकारी हो जा। तो उसने तुरन्त कहाः मैं विश्व के पालनहार का आज्ञाकारी हो गया।');
INSERT INTO "al_umari" VALUES(2,132,'2:132','तथा इब्राहीम ने अपने पुत्रों को तथा याक़ूब ने, इसी बात पर बल दिया कि हे मेरे पुत्रो! अल्लाह ने तुम्हारे लिए ये धर्म (इस्लाम) निर्वाचित कर दिया है। अतः मरते समय तक तुम इसी पर स्थिर रहना।');
INSERT INTO "al_umari" VALUES(2,133,'2:133','क्या तुम याक़ूब के मरने के समय उपस्थित थे; जब याक़ूब ने अपने पुत्रों से कहाः मेरी मृत्यु के पश्चात् तुम किसकी इबादत (वंदना) करोगे? उन्होंने कहाः हम तेरे तथा तेरे पिता इब्राहीम और इस्माईल तथा इस्ह़ाक़ के एकमात्र पूज्य की इबादत (वंदना) करेंगे और उसी के आज्ञाकारी रहेंगे।');
INSERT INTO "al_umari" VALUES(2,134,'2:134','ये एक समुदाय था, जो जा चुका। उन्होंने जो कर्म किये, वे उनके लिए हैं तथा जो तुमने किये, वे तुम्हारे लिए और उनके किये का प्रश्न तुमसे नहीं किया जायेगा।');
INSERT INTO "al_umari" VALUES(2,135,'2:135','और वे कहते हैं कि यहूदी हो जाओ अथवा ईसाई हो जाओ, तुम्हें मार्गदर्शन मिल जायेगा। आप कह दें, नहीं! हम तो एकेश्वरवादी इब्राहीम के धर्म पर हैं और वह मिश्रणवादियों में से नहीं था।');
INSERT INTO "al_umari" VALUES(2,136,'2:136','(हे मुसलमानो!) तुम सब कहो कि हम अल्लाह पर ईमान लाये तथा उसपर जो (क़ुर्आन) हमारी ओर उतारा गया और उसपर जो इब्राहीम, इस्माईल, इस्ह़ाक़, याक़ूब तथा उनकी संतान की ओर उतारा गया और जो मूसा तथा ईसा को दिया गया तथा जो दूसरे नबियों को, उनके पालनहार की ओर से दिया गया। हम इनमें से किसी के बीच अन्तर नहीं करते और हम उसी के आज्ञाकारी हैं।');
INSERT INTO "al_umari" VALUES(2,137,'2:137','तो यदि वे तुम्हारे ही समान ईमान ले आयें, तो वे मार्गदर्शन पा लेंगे और यदि विमुख हों, तो वे विरोध में लीन हैं। उनके विरुध्द तुम्हारे लिए अल्लाह बस है और वह सब सुनने वाला और जानने वाला है।');
INSERT INTO "al_umari" VALUES(2,138,'2:138','तुम सब अल्लाह के रंग[ (स्वभाविक धर्म) को ग्रहण कर लो और अल्लाह के रंग से अच्छा किसका रंग होगा? हम तो उसी की इबादत (वंदना) करते हैं। ');
INSERT INTO "al_umari" VALUES(2,139,'2:139','(हे नबी!) कह दो कि क्या तुम हमसे अल्लाह के (एक होने के) विषय में झगड़ते हो, जबकि वही हमारा तथा तुम्हारा पालनहार है?[ फिर हमारे लिए हमारा कर्म है और तुम्हारे लिए तुम्हारा कर्म है और हम तो बस उसी की इबादत (वंदना) करने वाले हैं। ');
INSERT INTO "al_umari" VALUES(2,140,'2:140','(हे अह्ले किताब!) क्या तुम कहते हो कि इब्राहीम, इस्माईल, इस्ह़ाक़, याक़ूब तथा उनकी संतान यहूदी या ईसाई थी? उनसे कह दो कि तुम अधिक जानते हो अथवा अल्लाह? और उससे बड़ा अत्याचारी कौन होगा, जिसके पास अल्लाह का साक्ष्य हो और उसे छुपा दे? और अल्लाह तुम्हारे करतूतों से अचेत तो नहीं है[। ');
INSERT INTO "al_umari" VALUES(2,141,'2:141','ये एक समुदाय था, जो जा चुका। उनके लिए उनका कर्म है तथा तुम्हारे लिए तुम्हारा कर्म है। तुमसे उनके कर्मों के बारे में प्रश्न नहीं किया जायेगा[। ');
INSERT INTO "al_umari" VALUES(2,142,'2:142','शीघ्र ही मूर्ख लोग कहेंगे कि उन्हें जिस क़िबले[ पर वे थे, उससे किस बात ने फेर दिया? (हे नबी!) उन्हें बता दो कि पूर्व और पश्चिम सब अल्लाह के हैं। वह जिसे चाहे, सीधी राह पर लगा देता है। ');
INSERT INTO "al_umari" VALUES(2,143,'2:143','और इसी प्रकार हमने तुम्हें मध्यवर्ती उम्मत (समुदाय) बना दिया; ताकि तुम, सबपर साक्षी[ बनो और रसूल तुमपर साक्षी हों और हमने वह क़िबला जिसपर तुम थे, इसीलिए बनाया था, ताकि ये बात खोल दें कि कौन (अपने धर्म से) फिर जाता है और ये बात बड़ी भारी थी, परन्तु उनके लिए नहीं, जिन्हें अल्लाह ने मार्गदर्शन दे दिया है और अल्लाह ऐसा नहीं कि तुम्हारे ईमान (अर्थात बैतुल मक़्दिस की दिशा में नमाज़ पढ़ने) को व्यर्थ कर दे , वास्तव में अल्लाह लोगों के लिए अत्यंत करुणामय तथा दयावान् है।');
INSERT INTO "al_umari" VALUES(2,144,'2:144','(हे नबी!) हम आपके मुख को बार-बार आकाश की ओर फिरते देख रहे हैं। तो हम अवश्य आपको उस क़िबले (काबा) की ओर फेर देंगे, जिससे आप प्रसन्न हो जायें। तो (अब) अपने मुख मस्जिदे ह़राम की ओर फेर लो[ तथा (हे मुसलमानों!) तुम भी जहाँ रहो, उसी की ओर मुख किया करो और निश्चय अह्ले किताब जानते हैं कि ये उनके पालनहार की ओर से सत्य है और अल्लाह उनके कर्मों से असूचित नहीं है।');
INSERT INTO "al_umari" VALUES(2,145,'2:145','और यदि आप अह्ले किताब के पास प्रत्येक प्रकार की निशानी ला दें, तब भी वे आपके क़िबले का अनुसरण नहीं करेंगे और न आप उनके क़िबले का अनुसरण करेंगे और न उनमें से कोई दूसरे के क़िबले का अनुसरण करेगा और यदि ज्ञान आने के पश्चात् आपने उनकी आकांक्षाओं का अनुसरण किया, तो आप अत्याचारियों में से हो जायेंगे।');
INSERT INTO "al_umari" VALUES(2,146,'2:146','जिन्हें हमने पुस्तक दी है, वे आपको ऐसे ही[ पहचानते हैं, जैसे अपने पुत्रों को पहचानते हैं और उनका एक समुदाय जानते हुए भी सत्य को छुपा रहा है। ');
INSERT INTO "al_umari" VALUES(2,147,'2:147','सत्य वही है, जो आपके पालनहार की ओर से उतारा गया। अतः, आप कदापि संदेह करने वालों में न हों।');
INSERT INTO "al_umari" VALUES(2,148,'2:148','प्रत्येक के लिए एक दिशा है, जिसकी ओर वह मुख कर हा है। अतः तुम भलाईयों में अग्रसर बनो। तुम जहाँ भी रहोगे, अल्लाह तुम सभी को (प्रलय के दिन) ले आयेगा। निश्चय अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(2,149,'2:149','और आप जहाँ भी निकलें, अपना मुख मस्जिदे ह़राम की ओर फेरें। निःसंदेह ये आपके पालनहार की ओर से सत्य (आदेश) है और अल्लाह तुम्हारे कर्मों से असूचित नहीं है।');
INSERT INTO "al_umari" VALUES(2,150,'2:150','और आप जहाँ से भी निकलें, अपना मुख मस्जिदे ह़राम की ओर फेरें और (हे मुसलमानों!) तुम जहाँ भी रहो, अपने मुखों को उसी की ओर फेरो; ताकि उन्हें तुम्हारे विरुध्द किसी विवाद का अवसर न मिले, मगर उन लोगों के अतिरिक्त, जो अत्याचार करें। अतः उनसे न डरो। मुझी से डरो और ताकि मैं तुमपर अपना पुरस्कार (धर्म विधान) पूरा कर दूँ और ताकि तुम सीधी डगर पाओ।');
INSERT INTO "al_umari" VALUES(2,151,'2:151','जिस प्रकार हमने तुम्हारे लिए तुम्हीं में से एक रसूल भेजा, जो तुम्हें हमारी आयतें सुनाता तथा तुम्हें शुध्द आज्ञाकारी बनाता है और तुम्हें पुस्तक (कुर्आन) तथा ह़िक्मत (सुन्नत) सिखाता है तथा तुम्हें वो बातें सिखाता है, जो तुम नहीं जानते थे।');
INSERT INTO "al_umari" VALUES(2,152,'2:152','अतः, मुझे याद करो[, मैं तुम्हें याद करूँगा और मेरे आभारी रहो और मेरे कृतघ्न न बनो।');
INSERT INTO "al_umari" VALUES(2,153,'2:153','हे ईमान वालो! धैर्य तथा नमाज़ का सहारा लो, निश्चय अल्लाह धैर्यवानों के साथ है।');
INSERT INTO "al_umari" VALUES(2,154,'2:154','तथा जो अल्लाह की राह में मारे जायें, उन्हें मुर्दा न कहो, बल्कि वे जीवित हैं, परन्तु तुम (उनके जीवन की दशा) नहीं समझते।');
INSERT INTO "al_umari" VALUES(2,155,'2:155','तथा हम अवश्य कुछ भय, भूक तथा धनों और प्राणों तथा खाद्य पदार्थों की कमी से तुम्हारी परीक्षा करेंगे और धैर्यवानों को शुभ समाचार सुना दो।');
INSERT INTO "al_umari" VALUES(2,156,'2:156','जिनपर कोई आपदा आ पड़े, तो कहते हैं कि हम अल्लाह के हैं और हमें उसी के पास फिर कर जाना है।');
INSERT INTO "al_umari" VALUES(2,157,'2:157','इन्हीं पर इनके पालनहार की कृपायें तथा दया हैं और यही सीधी राह पाने वाले हैं।');
INSERT INTO "al_umari" VALUES(2,158,'2:158','बेशक सफ़ा तथा मरवा पहाड़ी[ अल्लाह (के धर्म) की निशानियों में से हैं। अतः जो अल्लाह के घर का ह़ज या उमरह करे, तो उसपर कोई दोष नहीं कि उन दोनों का फेरा लगाये और जो स्वेच्छा से भलाई करे, तो निःसंदेह अल्लाह उसका गुणग्राही अति ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(2,159,'2:159','तथा जो हमारी उतारी हुई आयतों (अन्तिम नबी के गुणों) तथा मार्गदर्शन को, इसके पश्चात् कि हमने पुस्तक[ में उसे लोगों के लिए उजागर कर दिया है, छुपाते हैं, उन्हीं को अल्लाह धिक्कारता है तथा सब धिक्कारने वाले धिक्कारते हैं।');
INSERT INTO "al_umari" VALUES(2,160,'2:160','जिन लोगों ने तौबा (क्षमा याचना) कर ली और सुधार कर लिया और उजागर कर दिया, तो मैं उनकी तौबा स्वीकार कर लूँगा तथा मैं अत्यंत क्षमाशील, दयावान् हूँ।');
INSERT INTO "al_umari" VALUES(2,161,'2:161','वास्तव में, जो काफ़िर (अविश्वासी) हो गये और इसी दशा में मरे, तो वही हैं, जिनपर अल्लाह तथा फ़रिश्तों और सब लोगों की धिक्कार है।');
INSERT INTO "al_umari" VALUES(2,162,'2:162','वह इस (धिक्कार) में सदावासी होंगे, उनसे यातना मंद नहीं की जायेगी और न उनको अवकाश दिया जायेगा।');
INSERT INTO "al_umari" VALUES(2,163,'2:163','और तुम्हारा पूज्य एक ही[ पूज्य है, उस अत्यंत दयालु, दयावान के सिवा कोई पूज्य नहीं। ');
INSERT INTO "al_umari" VALUES(2,164,'2:164','बेशक आकाशों तथा धरती की रचना में, रात तथा दिन के एक-दूसरे के पीछे निरन्तर आने-जाने में, उन नावों में, जो मानव के लाभ के साधनों को लिए, सागरों में चलती-फिरती हैं और वर्षा के उस पानी में, जिसे अल्लाह आकाश से बरसाता है, फिर धरती को उसके द्वारा, उसके मरण् (सूखने) के पश्चात् जीवित करता है और उसमें प्रत्येक जीवों को फैलाता है तथा वायुओं को फेरने में और उन बादलों में, जो आकाश और धरती के बीच उसकी आज्ञा[ के अधीन रहते हैं, (इन सब चीज़ों में) अगणित निशानियाँ (लक्ष्ण) हैं, उन लोगों के लिए, जो समझ-बूझ रखते हैं। ');
INSERT INTO "al_umari" VALUES(2,165,'2:165','कुछ ऐसे लोग भी हैं, जो अल्लाह के सिवा दूसरों को उसका साझी बनाते हैं और उनसे, अल्लाह से प्रेम करने जैसा प्रेम करते हैं तथा जो ईमान लाये, वे अल्लाह से सर्वाधिक प्रेम करते हैं और क्या ही अच्छा होता, यदि ये अत्याचारी यातना देखने के समय[ जो बात जानेंगे, इसी समय जानते कि सब शक्ति तथा अधिकार अल्लाह ही को है और अल्लाह का दण्ड भी बहुत कड़ा है, (तो अल्लाह के सिवा दूसरे की पूजा अराधना नहीं करते।)');
INSERT INTO "al_umari" VALUES(2,166,'2:166','जब ये दशा[ होगी कि जिनका अनुसरण किया गया , वे अपने अनुयायियों से विरक्त हो जायेंगे और उनके आपस के सभी सम्बंध टूट जायेंगे।');
INSERT INTO "al_umari" VALUES(2,167,'2:167','तथा जो अनुयायी होंगे, वे ये कामना करेंगे कि एक बार और हम संसार में जाते, तो इनसे ऐसे ही विरक्त हो जाते, जैसे ये हमसे विरक्त हो गये हैं! ऐसे ही अल्लाह उनके कर्मों को उनके लिए संताप बनाकर दिखाएगा और वे अग्नि से निकल नहीं सकेंगे।');
INSERT INTO "al_umari" VALUES(2,168,'2:168','हे लोगो! धरती में जो ह़लाल (वैध) स्वच्छ चीज़ें हैं, उन्हें खाओ और शैतान की बताई राहों पर न चलो[, वह तुम्हारा खुला शत्रु है। ');
INSERT INTO "al_umari" VALUES(2,169,'2:169','वह तुम्हें बुराई तथा निर्लज्जा का आदेश देता है और ये कि अल्लाह पर उस चीज़ का आरोप[ धरो, जिसे तुम नहीं जानते हो। ');
INSERT INTO "al_umari" VALUES(2,170,'2:170','और जब उनसे[ कहा जाता है कि जो (क़ुर्आन) अल्लाह ने उतारा है, उसपर चलो, तो कहते हैं कि हम तो उसी रीति पर चलेंगे, जिसपर अपने पूर्वजों को पाया है। क्या यदि उनके पूर्वज कुछ न समझते रहे तथा कुपथ पर रहे हों, (तब भी वे उन्हीं का अनुसरण करते रहेंगे?) ');
INSERT INTO "al_umari" VALUES(2,171,'2:171','उनकी दशा जो काफ़िर हो गये, उसके समान है, जो उसे (अर्थात पशु को) पुकारता है, जो हाँक-पुकार के सिवा कुछ[ नहीं सुनता, ये (काफ़िर) बहरे, गोंगे तथा अंधे हैं। इसलिए कुछ नहीं समझते। ');
INSERT INTO "al_umari" VALUES(2,172,'2:172','हे ईमान वालो! उन स्वच्छ चीज़ों में से खाओ, जो हमने तुम्हें दी हैं तथा अल्लाह की कृतज्ञता का वर्णन करो, यदि तुम केवल उसी की इबादत (वंदना) करते हो।');
INSERT INTO "al_umari" VALUES(2,173,'2:173','(अल्लाह) ने तुमपर मुर्दार[ तथा (बहता) रक्त और सुअर का माँस तथा जिसपर अल्लाह के सिवा किसी और का नाम पुकारा गया हो, उन्हें ह़राम (निषेध) कर दिया है। फिर भी जो विवश हो जाये, जबकि वह नियम न तोड़ रहा हो और आवश्यक्ता की सीमा का उल्लंघन न कर रहा हो, तो उसपर कोई दोष नहीं। अल्लाह अति क्षमाशील, दयावान् है। ');
INSERT INTO "al_umari" VALUES(2,174,'2:174','वास्तव में, जो लोग अल्लाह की उतारी पुस्तक (की बातों) को छुपा रहे हैं और उनके बदले तनिक मूल्य प्राप्त कर लेते हैं, वे अपने उदर में केवल अग्नि भर रहे हैं तथा अल्लाह उनसे बात नहीं करेगा और न उन्हें विशुध्द करेगा और उन्हीं के लिए दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(2,175,'2:175','यही वे लोग हैं, जिन्होंने सुपथ (मार्गदर्शन) के बदले कुपथ खरीद लिया है तथा क्षमा के बदले यातना। तो नरक की अग्नि पर वे कितने सहनशील हैं?');
INSERT INTO "al_umari" VALUES(2,176,'2:176','इस यातना के अधिकारी वे इसलिए हुए कि अल्लाह ने पुस्तक सत्य के साथ उतारी और जो पुस्तक में विभेद कर बैठे, वे वास्तव में, विरोध में बहुत दुर निकल गये।');
INSERT INTO "al_umari" VALUES(2,177,'2:177','भलाई ये नहीं है कि तुम अपना मुख पूर्व अथवा पश्चिम की ओर फेर लो! भला कर्म तो उसका है, जो अल्लाह और अन्तिम दिन (प्रलय) पर ईमान लाया तथा फ़रिश्तों, सब पुस्तकों, नबियों पर (भी ईमान लाया), धन का मोह रखते हुए, समीपवर्तियों, अनाथों, निर्धनों, यात्रियों तथा याचकों (काफ़िरों) को और दास मुक्ति के लिए दिया, नमाज़ की स्थापना की, ज़कात दी, अपने वचन को, जब भी वचन दिया, पूरा करते रहे एवं निर्धनता और रोग तथा युध्द की स्थिति में धैर्यवान रहे। यही लोग सच्चे हैं तथा यही (अल्लाह से) डरते[ हैं। ');
INSERT INTO "al_umari" VALUES(2,178,'2:178','हे ईमान वालो! तुमपर निहत व्यक्तियों के बारे में क़िसास (बराबरी का बदला) अनिवार्य[ कर दिया गया है। स्वतंत्र का बदला स्वतंत्र से लिया जायेगा, दास का दास से और नारी का नारी से। जिस अपराधी के लिए उसके भाई की ओर से कुछ क्षमा कर दिया जाये, तो उसे सामान्य नियम का अनुसरण (अनुपालन) करना चाहिये। निहत व्यक्ति के वारिस को भलाई के साथ दियत (अर्थदण्ड) चुका देना चाहिये। ये तुम्हारे पालनहार की ओर से सुविधा तथा दया है। इसपर भी जो अत्याचार करे, तो उसके लिए दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(2,179,'2:179','और हे समझ वालो! तुम्हारे लिए क़िसास (के नियम में) जीवन है, ताकि तुम रक्तपात से बचो।[ ');
INSERT INTO "al_umari" VALUES(2,180,'2:180','और जब तुममें से किसी के निधन का समय हो और वह धन छोड़ रहा हो, तो उसपर माता-पिता और समीपवर्तियों के लिए साधारण नियमानुसार वसिय्यत (उत्तरदान) करना अनिवार्य कर दिया गया है। ये आज्ञाकारियों के लिए सुनिश्चित[ है। ');
INSERT INTO "al_umari" VALUES(2,181,'2:181','फिर जिसने वसिय्यत सुनने के पश्चात उसे बदल दिया, तो उसका पाप उनपर है, जो उसे बदलेंगे और अल्लाह सब कुछ सुनता-जानता है।');
INSERT INTO "al_umari" VALUES(2,182,'2:182','फिर जिसे डर हो कि वसिय्यत करने वाले ने पक्षपात या अत्याचार किया है, फिर उसने उनके बीच सुधार करा दिया, तो उसपर कोई पाप नहीं। निश्चय अल्लाह अति क्षमाशील तथा दयावान् है।');
INSERT INTO "al_umari" VALUES(2,183,'2:183','हे ईमान वालो! तुमपर रोज़े[ उसी प्रकार अनिवार्य कर दिये गये हैं, जैसे तुमसे पूर्व के लोगों पर अनिवार्य किये गये, ताकि तुम अल्लाह से डरो। ');
INSERT INTO "al_umari" VALUES(2,184,'2:184','वह गिनती के कुछ दिन हैं। फिर यदि तुममें से कोई रोगी अथवा यात्रा पर हो, तो ये गिनती, दूसरे दिनों से पूरी करे और जो उस रोज़े को सहन न कर सके[, वह फ़िद्या (प्रायश्चित्त) दे, जो एक निर्धन को खाना खिलाना है और जो स्वेच्छा भलाई करे, वह उसके लिए अच्छी बात है। यदी तुम समझो, तो तुम्हारे लिए रोज़ा रखना ही अच्छा है। ');
INSERT INTO "al_umari" VALUES(2,185,'2:185','रमज़ान का महीना वह है, जिसमें क़ुर्आन उतारा गया, जो सब मानव के लिए मार्गदर्शन है तथा मार्गदर्शन और सत्योसत्य के बीच अन्तर करने के खुले प्रमाण रखता है। अतः जो व्यक्ति इस महीने में उपस्थित[ हो, वह उसका रोज़ा रखे, फिर यदि तुममें से कोई रोगी अथवा यात्रा पर हो, तो उसे दूसरे दिनों में गिनती पुरी करनी चाहिए। अल्लाह तुम्हारे लिए सुविधा चाहता है, तंगी (असुविधा) नहीं चाहता और चाहता है कि तुम गिनती पूरी करो तथा इस बातपर अल्लाह की महिमा का वर्णन करो कि उसने तुम्हें मार्गदर्शन दिया और (इस प्रकार) तुम उसके कृतज्ञ बन सको।');
INSERT INTO "al_umari" VALUES(2,186,'2:186','(हे नबी!) जब मेरे भक्त मेरे विषय में आपसे प्रश्न करें, तो उन्हें बता दें कि निश्चय मैं समीप हूँ। मैं प्रार्थी की प्रार्थना का उत्तर देता हूँ। अतः, उन्हें भी चाहिये कि मेरे आज्ञाकारी बनें तथा मुझपर ईमान (विश्वास) रखें, ताकि वे सीधी राह पायें।');
INSERT INTO "al_umari" VALUES(2,187,'2:187','तुम्हारे लिए रोज़े की रात में अपनी स्त्रियों से सहवास ह़लाल (उचित) कर दिया गया है। वे तुम्हारा वस्त्र[ हैं तथा तुम उनका वस्त्र हो, अल्लाह को ज्ञान हो गया है कि तुम अपना उपभोग कर रहे थे। उसने तुम्हारी तौबा (क्षमा याचना) स्वीकार कर ली तथा तुम्हें क्षमा कर दिया। अब उनसे (रात्रि में) सहवास करो और अल्लाह के (अपने भाग्य में) लिखे की खोज करो और रात्रि में खाओ तथा पिओ, यहाँ तक की भोर की सफेद धारी रात की काली धारी से उजागर हो जाये, फिर रोज़े को रात्रि (सूर्यास्त) तक पूरा करो और उनसे सहवास न करो, जब मस्जिदों में ऐतिकाफ़ (एकान्तवास) में रहो। ये अल्लाह की सीमायें हैं, इनके समीप भी न जाओ। इसी प्रकार अल्लाह लोगों के लिए अपनी आयतों को उजागर करता है, ताकि वे (उनके उल्लंघन से) बचें।');
INSERT INTO "al_umari" VALUES(2,188,'2:188','तथा आपस में एक-दूसरे का धन अवैध रूप से न खाओ और न अधिकारियों के पास उसे इस धेय से ले जाओ कि लोगों के धन का कोई भाग, जान-बूझ कर पाप[ द्वारा खा जाओ। ');
INSERT INTO "al_umari" VALUES(2,189,'2:189','(हे नबी!) लोग आपसे चंद्रमा के (घटने-बढ़ने) के विषय में प्रश्न करते हैं? तो आप कह दें, इससे लोगों को तिथियों के निर्धारण तथा ह़ज के समय का ज्ञान होता है और ये कोई भलाई नहीं है कि घरों में उनके पीछे से प्रवेश करो, परन्तु भलाई तो अल्लाह की अवज्ञा से बचने में है। घरों में उनके द्वारों से आओ तथा अल्लाह से डरते रहो, ताकि तुम[ सफल हो जाओ। ');
INSERT INTO "al_umari" VALUES(2,190,'2:190','तथा अल्लाह की राह में, उनसे युध्द करो, जो तुमसे युध्द करते हों और अत्याचार न करो, अल्लाह अत्याचारियों से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(2,191,'2:191','और उन्हें हत करो, जहाँ पाओ और उन्हें निकालो, जहाँ से उन्होंने तुम्हें निकाला है, इसलिए कि फ़ितना[ (उपद्रव), हत करने से भी बुरा है और उनसे मस्जिदे ह़राम के पास युध्द न करो, जब तक वे तुमसे वहाँ युध्द न करें। परन्तु, यदि वे तुमसे युध्द करें, तो उनकी हत्या करो, यही काफ़िरों का बदला है।');
INSERT INTO "al_umari" VALUES(2,192,'2:192','फिर यदि वे (आक्रमण करने से) रुक जायें, तो अल्लाह अति क्षमी, दयावान् है।');
INSERT INTO "al_umari" VALUES(2,193,'2:193','तथा उनसे युध्द करो, यहाँ तक कि फ़ितना न रह जाये और धर्म केवल अल्लाह के लिए रह जाये, फिर यदि वे रुक जायें, तो अत्याचारियों के अतिरिक्त किसी और पर अत्याचार नहीं करना चाहिए।');
INSERT INTO "al_umari" VALUES(2,194,'2:194','सम्मानित[ मास, सम्मानित मास के बदले है और सम्मानित विषयों में बराबरी है। अतः, जो तुमपर अतिक्रमण (अत्याचार) करे, तो तुम भी उसपर उसी के समान (अतिक्रमण) करो तथा अल्लाह के आज्ञाकारी रहो और जान लो कि अल्लाह आज्ञाकारियों के साथ है। ');
INSERT INTO "al_umari" VALUES(2,195,'2:195','तथा अल्लाह की राह (जिहाद) में धन ख़र्च करो और अपने-आपको विनाश में न डालो तथा उपकार करो, निश्चय अल्लाह उपकारियों से प्रेम करता है।');
INSERT INTO "al_umari" VALUES(2,196,'2:196','तथा ह़ज और उमरह अल्लाह के लिए पूरा करो और यदि रोक दिये जाओ[, तो जो क़ुर्बानी सुलभ हो (कर दो) और अपने सिर न मुँडाओ, जब तक कि क़ुर्बानी अपने स्थान तक न पहुँच जाये, यदि तुममें से कोई व्यक्ति रोगी हो या उसके सिर में कोई पीड़ा हो (और सिर मुँडा ले), तो उसके बदले में रोज़ा रखना या दान देना या क़ुर्बानी देना है और जब तुम निर्भय (शान्त) रहो, तो जो उमरे से ह़ज तक लाभान्वित हो, वह जो क़ुर्बानी सुलभ हो, उसे करे और जिसे उपलब्ध न हो, वह तीन रोज़े ह़ज के दिनों में रखे और सात, जब रखे, जब तुम (घर) वापस आओ। ये पूरे दस हुए। ये उसके लिए है, जो मस्जिदे ह़राम का निवासी न हो और अल्लाह से डरो तथा जान लो कि अल्लाह की यातना बहुत कड़ी है।');
INSERT INTO "al_umari" VALUES(2,197,'2:197','ह़ज के महीने प्रसिध्द हैं, जो व्यक्ति इनमें ह़ज का निश्चय कर ले, तो (ह़ज के बीच) काम वासना तथा अवज्ञा और झगड़े की बातें न करे तथा तुम जो भी अच्छे कर्म करोगे, उसका ज्ञान अल्लाह को हो जायेगा और अपने लिए पाथेय बना लो, उत्तम पाथेय अल्लाह की आज्ञाकारिता है तथा हे समझ वालो! मुझी से डरो।');
INSERT INTO "al_umari" VALUES(2,198,'2:198','तथा तुमपर कोई दोष[ नहीं कि अपने पालनहार के अनुग्रह की खोज करो, फिर जब तुम अरफ़ात से चलो, तो मश्अरे ह़राम (मुज़दलिफ़ह) के पास अल्लाह का स्मरण करो, जिस प्रकार अल्लाह ने तुम्हें बताया है। यद्यपि इससे पहले तुम कुपथों में से थे।');
INSERT INTO "al_umari" VALUES(2,199,'2:199','फिर तुम[ भी वहीं से फिरो, जहाँ से लोग फिरते हैं तथा अल्लाह से क्षमा माँगो। निश्चय अल्लाह अति क्षमाशील, दयावान् है। ');
INSERT INTO "al_umari" VALUES(2,200,'2:200','और जब तुम अपने ह़ज के मनासिक (कर्म) पूरे कर लो, तो जिस प्रकार पहले अपने पूर्वजों की चर्चा करते रहे, उसी प्रकार बल्कि उससे भी अधिक अल्लाह का स्मरण[ करो। उनमें से कुछ ऐसे हैं, जो ये कहते हैं कि हे हमारे पालनहार! (हमें जो देना है,) संसार ही में दे दे। अतः ऐसे व्यक्ति के लिए परलोक में कोई भाग नहीं है। ');
INSERT INTO "al_umari" VALUES(2,201,'2:201','तथा उनमें से कुछ ऐसे हैं, जो ये कहते हैं कि हमारे पालनहार! हमें संसार में भलाई दे तथा परलोक में भी भलाई दे और हमें नरक की यातना से सुरक्षित रख।');
INSERT INTO "al_umari" VALUES(2,202,'2:202','इन्हीं को इनकी कमाई के कारण भाग मिलेगा और अल्लाह शीघ्र ह़िसाब चुकाने बाला है।');
INSERT INTO "al_umari" VALUES(2,203,'2:203','तथा इन गिनती[ के कुछ दिनों में अल्लाह को स्मरण (याद) करो, फिर जो व्यक्ति शीघ्रता से दो ही दिन में (मिना से) चल दे, उसपर कोई दोष नहीं और जो विलम्ब करे, उसपर भी कोई दोष नहीं, उस व्यक्ति के लिए जो अल्लाह से डरा तथा तुम अल्लाह से डरते रहो और ये समझ लो कि तुम उसी के पास प्रलय के दिन एकत्र किए जाओगो।');
INSERT INTO "al_umari" VALUES(2,204,'2:204','(हे नबी!) लोगों[ में ऐसा व्यक्ति भी है, जिसकी बात आपको सांसारिक विषय में भाती है तथा जो कुछ उसके दिल में है, वह उसपर अल्लाह को साक्षी बनाता है, जबकि बह बड़ा झगड़ालू है। ');
INSERT INTO "al_umari" VALUES(2,205,'2:205','तथा जब वह आपके पास से जाता है, तो धरती में उपद्रव मचाने का प्रयास करता है और खेती तथा पशुओं का विनाश करता है और अल्लाह उपद्रव से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(2,206,'2:206','तथा जब उससे कहा जाता है कि अल्लाह से डर, तो अभिमान उसे पाप पर उभार देता है। अतः उसके (दण्ड) के लिए नरक काफ़ी है और वह बहुत बुरा बिछौना है।');
INSERT INTO "al_umari" VALUES(2,207,'2:207','तथा लोगों में ऐसा व्यक्ति भी है, जो अल्लाह की प्रसन्नता की खोज में अपना प्राण बेच[ देता है और अल्लाह अपने भक्तों के लिए अति करुणामय है। ');
INSERT INTO "al_umari" VALUES(2,208,'2:208','हे ईमान वालो! तुम सर्वथा इस्लाम में प्रवेश[ कर जाओ और शैतान की राहों पर मत चलो, निश्चय वह तुम्हारा खुला शत्रु है। ');
INSERT INTO "al_umari" VALUES(2,209,'2:209','फिर यदि तुम खुले तर्कों (दलीलों)[ के आने के पश्चात विचलित हो गये, तो जान लो कि अल्लाह प्रभुत्वशाली तथा तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(2,210,'2:210','क्या (इन खुले तर्कों के आ जाने के पश्चात्) वे इसकी प्रतीक्षा कर रहे हैं कि उनके समक्ष अल्लाह तथा फ़रिश्ते बादलों के छत्र में आ जायें और निर्णय ही कर दिया जाये? और सभी विषय अल्लाह ही की ओर फेरे[ जायेंगे। ');
INSERT INTO "al_umari" VALUES(2,211,'2:211','बनी इस्राईल से पूछो कि हमने उन्हें कितनी खुली निशानियाँ दीं? इसपर भी जिसने अल्लाह की अनुकम्पा को, उसके अपने पास आ जाने के पश्चात् बदल दिया, तो अल्लाह की यातना भी बहुत कड़ी है।');
INSERT INTO "al_umari" VALUES(2,212,'2:212','काफ़िरों के लिए सांसारिक जीवन शोभनीय (मनोहर) बना दिया गया है तथा जो ईमान लाये ये उनका उपहास[ करते हैं और प्रलय के दिन अल्लाह के आज्ञाकारी उनसे उच्च स्थान पर रहेंगे तथा अल्लाह जिसे चाहे, अगणित आजीविका प्रदान करता है।');
INSERT INTO "al_umari" VALUES(2,213,'2:213','(आरंभ में) सभी मानव एक ही (स्वाभाविक) सत्धर्म पर थे। (फिर विभेद हुआ) तो अल्लाह ने नबियों को शुभ समाचार सुनाने[ और (अवज्ञा) से सचेत करने के लिए भेजा और उनपर सत्य के साथ पुस्तक उतारी, ताकि वे जिन बातों में विभेद कर रहे हैं, उनका निर्णय कर दे। और आपकी दुराग्रह के कारण उन्होंने ही विभेद किया, जिन्हें (विभेद निवारण के लिए) ये पुस्तक दी गयी। (अल्बत्ता) जो ईमान लाये, अल्लाह ने उस विभेद में उन्हें अपनी अनुमति से सत्पथ दर्शा दिया और अल्लाह जिसे चाहे, सत्पथ दर्शा देता है। ');
INSERT INTO "al_umari" VALUES(2,214,'2:214','क्या तुमने समझ रखा है कि यूँ ही स्वर्ग में प्रवेश कर जाओगे, ह़ालाँकि अभी तक तुम्हारी वह दशा नहीं हुई, जो तुमसे पूर्व के ईमान वालों की हुई? उन्हें तंगियों तथा आपदाओं ने घेर लिया और वे झंझोड़ दिये गये, यहाँ तक कि रसूल और जो उसपर ईमान लाये, गुहारने लगे कि अल्लाह की सहायता कब आयेगी? (उस समय कहा गयाः)सुन लो! अल्लाह की सहायता समीप[ है। ');
INSERT INTO "al_umari" VALUES(2,215,'2:215','(हे नबी!) वे आपसे प्रश्न करते हैं कि कैसे व्यय (ख़र्च) करें? उनसे कह दीजिये कि जो भी धन तुम ख़र्च करो, तो अपने माता-पिता, समीपवर्तियों, अनाथों, निर्धनों तथा यात्रियों (को दो)। तथा जो भी भलाई तुम करते हो, उसे अल्लाह भलि-भाँति जानता है।');
INSERT INTO "al_umari" VALUES(2,216,'2:216','(हे ईमान वालो!) तुमपर युध्द करना अनिवार्य कर दिया गया है, ह़लाँकि वह तुम्हें अप्रिय है। हो सकता है कि कोई चीज़ तुम्हें अप्रिय हो और वही तुम्हारे लिए अच्छी हो और इसी प्रकार सम्भव है कि कोई चीज़ तुम्हें प्रिय हो और वह तुम्हारे लिए बुरी हो। अल्लाह जानता है और तुम नहीं[ जानते। ');
INSERT INTO "al_umari" VALUES(2,217,'2:217','(हे नबी!) वे[ आपसे प्रश्न करते हैं कि सम्मानित मास में युध्द करना कैसा है? आप उनसे कह दें कि उसमें युध्द करना घोर पाप है, परन्तु अल्लाह की राह से रोकना, उसका इन्कार करना, मस्जिदे ह़राम से रोकना और उसके निवासियों को उससे निकालना, अल्लाह के समीप उससे भी घोर पाप है तथा फ़ितना (सत्धर्म से विचलाना) हत्या से भी भारी है। और वे तो तुमसे युध्द करते ही जायेंगे, यहाँ तक कि उनके बस में हो, तो तुम्हें तुम्हारे धर्म से फेर दें और तुममें से जो व्यक्ति अपने धर्म (इस्लाम) से फिर जायेगा, फिर कुफ़्र पर ही उसकी मौत होगी, ऐसों का किया-कराया, संसार तथा परलोक में व्यर्थ हो जायेगा तथा वही नारकी हैं और वे उसमें सदावासी होंगे। ');
INSERT INTO "al_umari" VALUES(2,218,'2:218','(इसके विपरीत) जो लोग ईमान लाये, हिजरत[ की तथा अल्लाह की राह में जिहाद किया, वास्तव में, वही अल्लाह की दया की आशा रखते हैं तथा अल्लाह अति क्षमाशील और बहुत दयालु है। ');
INSERT INTO "al_umari" VALUES(2,219,'2:219','(हे नबी!) वे आपसे मदिरा और जुआ के विषय में प्रश्न करते हैं। आप बता दें कि इन दोनों में बड़ा पाप है तथा लोगों का कुछ लाभ भी है; परन्तु उनका पाप उनके लाभ से अधिक[ बड़ा है। तथा वे आपसे प्रश्न करते हैं कि अल्लाह की राह में क्या ख़र्च करें? उनसे कह दीजिये कि जो अपनी आवश्यक्ता से अधिक हो। इसी प्रकार अल्लाह तुम्हारे लिए आयतों (धर्मादेशों) को उजागर करता है, ताकि तुम सोच-विचार करो। ');
INSERT INTO "al_umari" VALUES(2,220,'2:220','दुनिया और आख़रित दोनो में और वे आपसे अनाथों के विषय में प्रश्ण करते हैं। उनसे कह दीजिए कि जिस बात में उनका सुधार हो वही सबसे अच्छी है। यदि तुम उनसे मिलकर रहो, तो वे तुम्हारे भाई ही हैं और अल्लाह जानता है कि कौन सुधारने और कौन बिगाड़ने वाला है और यदि अल्लाह चाहता, तो तुमपर सख्ती[ कर देता। वास्तव में, अल्लाह प्रभुत्वशाली, तत्वज्ञ है। ');
INSERT INTO "al_umari" VALUES(2,221,'2:221','तथा मुश्रिक[ स्त्रियों से तुम विवाह न करो, जब तक वे ईमान न लायें और ईमान वाली दासी मुश्रिक स्त्री से उत्तम है, यद्यपि वह तुम्हारे मन को भा रही हो और अपनी स्त्रियों का निकाह़ मुश्रिकों से न करो, जब तक वे ईमान न लायें और ईमान वाला दास मुश्रिक से उत्तम है, यद्यपि वह तुम्हें भा रहा हो। वे तुम्हें अग्नि की ओर बुलाते हैं तथा अल्लाह स्वर्ग और क्षमा की ओर बुला रहा है और (अल्लाह) सभी मानव के लिए अपनी आयतें (आदेश) उजागर कर रहा है, ताकि वह शिक्षा ग्रहन करें। ');
INSERT INTO "al_umari" VALUES(2,222,'2:222','तथा वे आपसे मासिक धर्म के विषय में प्रश्न करते हैं। तो कह दें कि वह मलीनता है और उनके समीप भी न[ जाओ, जब तक पवित्र न हो जायें। फिर जब वे भली भाँति स्वच्छ हो जायें, तो उनके पास उसी प्रकार जाओ, जैसे अल्लाह ने तुम्हें आदेश दिया है। निश्चय अल्लाह तौबा करने वालों तथा पवित्र रहने वालों से प्रेम करता है।');
INSERT INTO "al_umari" VALUES(2,223,'2:223','तुम्हारी पत्नियाँ तुम्हारे लिए खेतियाँ[ हैं। तुम्हें अनुमति है कि जैसे चाहो, अपनी खेतियों में जाओ; परन्तु भविष्य के लिए भी सत्कर्म करो तथा अल्लाह से डरते रहो और विश्वास रखो कि तुम्हें उससे मिलना है और ईमान वालों को शुभ सूचना सुना दो। ');
INSERT INTO "al_umari" VALUES(2,224,'2:224','तथा अल्लाह के नाम पर, अपनी शपथों को उपकार तथा सदाचार और लोगों में मिलाप कराने के लिए रोक[ न बनाओ और अल्लाह सब कुछ सुनता-जानता है। ');
INSERT INTO "al_umari" VALUES(2,225,'2:225','अल्लाह तम्हारी निरर्थक शपथों पर तुम्हें नहीं पकड़ेगा, परन्तु जो शपथ अपने दिलों के संकल्प से लोगे, उनपर पकड़ेगा और अल्लाह अति क्षमाशील, सहनशील है।');
INSERT INTO "al_umari" VALUES(2,226,'2:226','तथा जो लोग अपनी पत्नियों से संभोग न करने की शपथ लेते हों, वे चार महीने प्रतीक्षा करें। फिर[ यदि (इस बीच) अपनी शपथ से फिर जायें, तो अल्लाह अति क्षमाशील, दयावान् है। ');
INSERT INTO "al_umari" VALUES(2,227,'2:227','और यदि उन्होंने तलाक़ का संकल्प ले लिया हो, तो निःसंदेह अल्लाह सब कुछ सुनता और जानता है।');
INSERT INTO "al_umari" VALUES(2,228,'2:228','तथा जिन स्त्रियों को तलाक़ दी गयी हो, वे तीन बार रजवती होने तक अपने आपको विवाह से रोके रखें। उनके लिए ह़लाल (वैध) नहीं है कि अल्लाह ने जो उनके गर्भाशयों में पैदा किया[ है, उसे छुपायें, यदि वे अल्लाह तथा आख़िरत (परलोक) पर ईमान रखती हों तथा उनके पति इस अवधि में अपनी पत्नियों को लौटा लेने के अधिकारी हैं, यदि वे मिलाप चाहते हों तथा सामान्य नियमानुसार स्त्रियों के लिए वैसे ही अधिकार हैं, जैसे पुरुषों के उनके ऊपर हैं। फिर भी पुरुषों को स्त्रियों पर एक प्रधानता प्राप्त है और अल्लाह अति प्रभुत्वशाली, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(2,229,'2:229','तलाक़ दो बार है; फिर नियमानुसार स्त्री को रोक लिया जाये या भली-भाँति विदा कर दिया जाये और तुम्हारे लिए ये ह़लाल (वैध) नहीं है कि उन्हें जो कुछ तुमने दिया है, उसमें से कुछ वापिस लो। फिर यदि तुम्हें ये भय[ हो कि पति पत्नि अल्लाह की निर्धारित सीमाओं को स्थापित न रख सकेंगे, तो उन दोनों पर कोई दोष नहीं कि पत्नि अपने पति को कुछ देकर मुक्ति करा ले। ये अल्लाह की सीमायें हैं, इनका उल्लंघन न करो और जो अल्लाह की सीमाओं का उल्लंघन करेंगे, वही अत्याचारी हैं।');
INSERT INTO "al_umari" VALUES(2,230,'2:230','फिर यदि उसे (तीसरी बार) तलाक़ दे दी, तो वह स्त्री उसके लिए ह़लाल (वैध) नहीं होगी, जब तक दूसरे पति से विवाह न कर ले। अब यदि दूसरा पति (संभोग के पश्चात्) उसे तलाक दे दे, तब प्रथम पति से (निर्धारित अवधि पूरी करके) फिर विवाह कर सकती है, यदि वे दोनों समझते हों कि अल्लाह की सीमाओं को स्थापित रख[ सकेंगे और ये अल्लाह की सीमायें हैं, जिन्हें उन लोगों के लिए उजागर कर रहा है, जो ज्ञान रखते हैं। ');
INSERT INTO "al_umari" VALUES(2,231,'2:231','और यदि स्त्रियों को (एक या दो) तलाक़ दे दो और उनकी निर्धारित अवधि (इद्दत) पूरी होने लगे, तो नियमानुसार उन्हें रोक लो अथवा नियमानुसार विदा कर दो। उन्हें हानि पहुँचाने के लिए न रोको, ताकि उनपर अत्याचार करो और जो कोई ऐसा करेगा, वह स्वयं अपने ऊपर अत्याचार करेगा तथा अल्लाह की आयतों (आदेशों) को उपहास न बनाओ और अपने ऊपर अल्लाह के उपकार तथा पुस्तक (क़ुर्आन) एवं ह़िक्मत (सुन्नत) को याद करो, जिन्हें उसने तुमपर उतारा है और उनके द्वारा तुम्हें शिक्षा दे रहा है तथा अल्लाह से डरो और विश्वास रखो कि अल्लाह सब कुछ जानता है।[ ');
INSERT INTO "al_umari" VALUES(2,232,'2:232','और जब तुम अपनी पत्नियों को (तीन से कम) तलाक़ दो और वे अपनी निश्चित अवधि (इद्दत) पूरी कर लें, तो (स्त्रियों के संरक्षको!) उन्हें अपने पतियों से विवाह करने से न रोको, जबकि सामान्य नियमानुसार वे आपस में विवाह करने पर सहमत हों, ये तुममें से उसे निर्देश दिया जा रहा है, जो अल्लाह तथा अंतिम दिन (प्रलय) पर ईमान (विश्वास) रखता है, यही तुम्हारे लिए अधिक स्वच्छ तथा पवित्र है और अल्लाह जानता है, तुम नहीं जानते।');
INSERT INTO "al_umari" VALUES(2,233,'2:233','और मातायें अपने बच्चों को पूरे दो वर्ष दूध पिलायें और पिता को नियमानुसार उन्हें खाना-कपड़ा देना है, किसी पर उसकी सकत से अधिक भार नहीं डाला जायेगा; न माता को उसके बच्चे के कारण हानि पहुँचाई जाये और न पिता को उसके बच्चे के कारण। और इसी प्रकार उस (पिता) के वारिस (उत्तराधिकारी) पर (खाना कपड़ा देने का) भार है। फिर यदि दोनों आपस की सहमति तथा प्रामर्श से (दो वर्ष से पहले) दूध छुड़ाना चाहें, तो दोनों पर कोई दोष नहीं और यदि (तुम्हारा विचार किसी अन्य स्त्री से) दूध पिलवाने का हो, तो इसमें भी तुमपर कोई दोष नहीं, जबकि जो कुछ नियमानुसार उसे देना है, उसे चुका दो तथा अल्लाह से डरते रहो और जान लो कि तुम जो कुछ करते हो, उसे अल्लाह देख रहा[ है। ');
INSERT INTO "al_umari" VALUES(2,234,'2:234','और तुममें से जो मर जायें और अपने पीछे पत्नियाँ छोड़ जायें, तो वे स्वयं को चार महीने दस दिन रोके रखें।[ फिर जब उनकी अवधि पूरी हो जाये, तो वे सामान्य नियमानुसार अपने विषय में जो भी करें, उसमें तुमपर कोई दोष नहीं तथा अल्लाह तुम्हारे कर्मों से सूचित है।');
INSERT INTO "al_umari" VALUES(2,235,'2:235','इस अवधि में यदि तुम (उन) स्त्रियों को विवाह का संकेत दो अथवा अपने मन में छुपाये रखो, तो तुमपर कोई दोष नहीं। अल्लाह जानता है कि उनका विचार तुम्हारे मन में आयेगा, परन्तु उन्हें गुप्त रूप से विवाह का वचन न दो। परन्तु ये कि नियमानुसार[ कोई बात कहो तथा विवाह के बंधन का निश्चय उस समय तक न करो, जब तक निर्धारित अवधि पूरी न हो जाये तथा जान लो कि जो कुछ तुम्हारे मन में है, उसे अल्लाह जानता है। अतः उससे डरते रहो और जान लो कि अल्लाह क्षमाशील, सहनशील है।');
INSERT INTO "al_umari" VALUES(2,236,'2:236','और तुमपर कोई दोष नहीं, यदि तुम स्त्रियों को संभोग करने तथा महर (विवाह उपहार) निर्धारित करने से पहले तलाक़ दे दो, (अल्बत्ता) उन्हें नियमानुसार कुछ दो; धनी पर अपनी शक्ति के अनुसार तथा निर्धन पर अपनी शक्ति के अनुसार देना है। ये उपकारियों पर आवश्यक है।');
INSERT INTO "al_umari" VALUES(2,237,'2:237','और यदि तुम उनहें, उनसे संभोग करने से पहले तलाक़ दो, इस स्थिति में कि तुमने उनके लिए महर (विवाह उपहार) निर्धारित किया है, तो निर्धारित महर का आधा देना अनिवार्य है। ये और बात है कि वे क्षमा कर दें अथवा वे क्षमा कर दें जिनके हाथ में विवाह का बंधन[ है और क्षमा कर देना संयम से अधिक समीप है और आपस में उपकार को न भूलो। तुम जो कुछ कर रहे हो, अल्लाह सब देख रहा है। ');
INSERT INTO "al_umari" VALUES(2,238,'2:238','नमाज़ों का, विशेष रूप से माध्यमिक नमाज़ (अस्र) का ध्यान रखो[ तथा अल्लाह के लिए विनय पूर्वक खड़े रहो। ');
INSERT INTO "al_umari" VALUES(2,239,'2:239','और यदि तुम्हें भय[ हो, तो पैदल या सवार (जैसे सम्भव हो) नमाज़ पढ़ो, फिर जब निश्चिंत हो जाओ, तो अल्लाह ने तुम्हें जैसे सिखाया है, जिसे पहले तुम नहीं जानते थे, वैसे अल्लाह को याद करो। ');
INSERT INTO "al_umari" VALUES(2,240,'2:240','और जो तुममें से मर जायें तथा पत्नियाँ छोड़ जायें, वे अपनी पत्नियों के लिए एक वर्ष तक, उनहें खर्च देने तथा (घर से) न निकालने की वसिय्यत कर जायें, यदि वे स्वयं निकल जायें[ तथा सामान्य नियमानुसार अपने विषय में कुछ भी करें, तो तुमपर कोई दोष नहीं। अल्लाह प्रभावशाली तत्वज्ञ है। ');
INSERT INTO "al_umari" VALUES(2,241,'2:241','तथा जिन स्त्रियों को तलाक़ दी गयी हो, तो उन्हें भी उचित रूप से सामग्री मिलनी चाहिए। ये आज्ञाकारियों पर आवश्यक है।');
INSERT INTO "al_umari" VALUES(2,242,'2:242','इसी प्रकार अल्लाह तुम्हारे लिए अपनी आयतों को उजागर कर देता है, ताकि तुम समझो।');
INSERT INTO "al_umari" VALUES(2,243,'2:243','क्या आपने उनकी दशा पर विचार नहीं किया, जो अपने घरों से मौत के भय से निकल गये[, जबकि उनकी संख्या हज़ारों में थी, तो अल्लाह ने उनसे कहा कि मर जाओ, फिर उन्हें जीवित कर दिया। वास्तव में, अल्लाह लोगों के लिए बड़ा उपकारी है, परन्तु अधिकांश लोग कृतज्ञता नहीं करते। ');
INSERT INTO "al_umari" VALUES(2,244,'2:244','और तुम अल्लाह (के धर्म के समर्थन) के लिए युध्द करो और जान लो कि अल्लाह सब कुछ सुनता-जानता है।');
INSERT INTO "al_umari" VALUES(2,245,'2:245','कौन है, जो अल्लाह को अच्छा उधार[ देता है, ताकि अल्लाह उसे उसके लिए कई गुना अधिक कर दे? और अल्लाह ही थोड़ा और अधिक करता है और उसी की ओर तुम सब फेरे जाओगे। ');
INSERT INTO "al_umari" VALUES(2,246,'2:246','(हे नबी!) क्या आपने बनी इस्राईल के प्रमुखों के विषय पर विचार नहीं किया, जो मूसा के बाद सामने आया? जब उसने अपने नबी से कहाः हमारे लिए एक राजा बना दो। हम अल्लाह की राह में युध्द करेंगे, (नबी ने) कहाः ऐसा तो नहीं होगा कि तुम्हें युध्द का आदेश दे दिया जाये तो अवज्ञा कर जाओ? उन्होंने कहाः ऐसा नहीं हो सकता कि हम अल्लाह की राह में युध्द न करें। जबकि हम अपने घरों और अपने पुत्रों से निकाल दिये गये हैं। परन्तु, जब उन्हें युध्द का आदेश दे दिया गया, तो उनमें से थोड़े के सिवा सब फिर गये। और अल्लाह अत्याचारियों को भली भाँति जानता है।');
INSERT INTO "al_umari" VALUES(2,247,'2:247','तथा उनके नबी ने कहाः अल्लाह ने ''तालूत'' को तुम्हारा राजा बना दिया है। वे कहने लगेः ''तालूत'' हमारा राजा कैसे हो सकता है? हम उससे अधिक राज्य का अधिकार रखते हैं, वह तो बड़ा धनी भी नहीं है। (नबी ने) कहाः अल्लाह ने उसे तुमपर निर्वाचित किया है और उसे अधिक ज्ञान तथा शारीरिक बल प्रदान किया है और अल्लाह जिसे चाहे, अपना राज्य प्रदान करे तथा अल्लाह ही विशाल, अति ज्ञानी[ है। ');
INSERT INTO "al_umari" VALUES(2,248,'2:248','तथा उनके नबी ने उनसे कहाः उसके राज्य का लक्षण ये है कि वह ताबूत तुम्हारे पास आयेगा, जिसमें तुम्हारे पालनहार की ओर से तुम्हारे लिए संतोष तथा मूसा और हारून के घराने के छोड़े हुए अवशेष हैं, उसे फ़रिश्ते उठाये हुए होंगे। निश्चय यदि तुम ईमान वाले हो, तो इसमें तुम्हारे लिए बड़ी निशानी[ (लक्षण) है। ');
INSERT INTO "al_umari" VALUES(2,249,'2:249','फिर जब तालूत सेना लेकर चला, तो उसने कहाः निश्चय अल्लाह एक नहर द्वारा तुम्हारी परीक्षा लेने वाला है। जो उसमें से पियेगा वह मेरा साथ नहीं देगा और जो उसे नहीं चखेगा, वह मेरा साथ देगा, परन्तु जो अपने हाथ से चुल्लू भर पी ले, (तो कोई दोष नहीं)। तो थोड़े के सिवा सबने उसमें से पी लिया। फिर जब उस (तालूत) ने और जो उसके साथ ईमान लाये, उसे (नहर को) पार किया, तो कहाः आज हममें (शत्रु) जालूत और उसकी सेना से युध्द करने की शक्ति नहीं। (परन्तु) जो समझ रहे थे कि उन्हें अल्लाह से मिलना है, उन्होंने कहाः बहुत-से छोटे दल, अल्लाह की अनुमति से, भारी दलों पर विजय प्राप्त कर चुके हैं और अल्लाह सहनशीलों के साथ है।');
INSERT INTO "al_umari" VALUES(2,250,'2:250','और जब वे, जालूत और उसकी सेना के सम्मुख हुए, तो प्रार्थना कीः हे हमारे पालनहार! हमें धैर्य प्रदान कर तथा हमारे चरणों को (रणक्षेत्र में) स्थिर कर दे और काफ़िरों पर हमारी सहायता कर।');
INSERT INTO "al_umari" VALUES(2,251,'2:251','तो उन्होंने अल्लाह की अनुमति से उन्हें पराजित कर दिया और दावूद ने जालूत को वध कर दिया तथा अल्लाह ने उसे (दावूद[ को) राज्य और ह़िक्मत (नुबूवत) प्रदान की तथा उसे जो ज्ञान चाहा, दिया और यदि अल्लाह कुछ लोगों की कुछ लोगों द्वारा रक्षा न करता, तो धरती की व्यवस्था बिगड़ जाती, परन्तु संसार वासियों पर अल्लाह बड़ा दयाशील है। ');
INSERT INTO "al_umari" VALUES(2,252,'2:252','(हे नबी!) ये अल्लाह की आयतें हैं, जो हम आपको सुना रहे हैं तथा वास्तव में, आप रसूलों में से हैं।');
INSERT INTO "al_umari" VALUES(2,253,'2:253','वो रसूल हैं। उनहें हमने एक-दूसरे पर प्रधानता दी है। उनमें से कुछ ने अल्लाह से बात की और कुछ को कई श्रेणियाँ ऊँचा किया तथा मर्यम के पुत्र ईसा को खुली निशानियाँ दीं और रूह़ुलक़ुदुस[ द्वारा उसे समर्थन दिया और यदि अल्लाह चाहता, तो इन रसूलों के पश्चात् खुली निशानियाँ आ जाने पर लोग आपस में न लड़ते, परन्तु उन्होंने विभेद किया, तो उनमें से कोई ईमान लाया और किसी ने कुफ़्र किया और यदि अल्लाह चाहता, तो वे नहीं लड़ते, परन्तु अल्लाह जो चाहता है, करता है। ');
INSERT INTO "al_umari" VALUES(2,254,'2:254','हे ईमान वालो! हमने तुम्हें जो कुछ दिया है, उसमें से दान करो, उस दिन (अर्थातः प्रलय) के आने से पहले, जिसमें न कोई सौदा होगा, न कोई मैत्री और न ही कोई अनुशंसा (सिफ़ारिश) काम आएगी तथा काफ़िर लोग[ ही अत्याचारी हैं।');
INSERT INTO "al_umari" VALUES(2,255,'2:255','अल्लाह के सिवा कोई पूज्य नहीं, वह जीवित[ तथा नित्य स्थायी है, उसे ऊँघ तथा निद्रा नहीं आती। आकाश और धरती में जो कुछ है, सब उसी का है। कौन है, जो उसके पास उसकी अनुमति के बिना अनुशंसा (सिफ़ारिश) कर सके? जो कुछ उनके समक्ष और जो कुछ उनसे ओझल है, सब जानता है। उसके ज्ञान में से वही जान सकते हैं, जिसे वह चाहे। उसकी कुर्सी आकाश तथा धरती को समोये हुए है। उन दोनों की रक्षा उसे नहीं थकाती। वही सर्वोच्च , महान है।');
INSERT INTO "al_umari" VALUES(2,256,'2:256','धर्म में बल प्रयोग नहीं। सुपथ, कुपथ से अलग हो चुका है। अतः, अब जो ताग़ूत (अर्थात अल्लाह के सिवा पूज्यों) को नकार दे तथा अल्लाह पर ईमान लाये, तो उसने दृढ़ कड़ा (सहारा) पकड़ लिया, जो कभी खण्डित नहीं हो सकता तथा अल्लाह सब कुछ सुनता-जानता[ है। ');
INSERT INTO "al_umari" VALUES(2,257,'2:257','अल्लाह उनका सहायक है, जो ईमान लाये। वह उनहें अंधेरों से निकालता है और प्रकाश में लाता है और जो काफ़िर (विश्वासहीन) हैं, उनके सहायक ताग़ूत (उनके मिथ्या पूज्य) हैं। जो उन्हें प्रकाश से अंधेरों की और ले जाते हैं। यही नारकी हैं, जो उसमें सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(2,258,'2:258','(हे नबी!) क्या आपने उस व्यक्ति की दशा पर विचार नहीं किया, जिसने इब्राहीम से उसके पालनहार के विषय में विवाद किया, इसलिए कि अल्लाह ने उसे राज्य दिया था? जब इब्राहीम ने कहाः मेरा पालनहार वो है, जो जीवित करता तथा मारता है, तो उसने कहाः मैं भी जीवित[ करता तथा मारता हूँ। इब्राहीम ने कहाः अल्लाह सूर्य को पूर्व से लाता है, तू उसे पश्चिम से ले आ! (ये सुनकर) काफ़िर चकित रह गया और अल्लाह अत्याचारियों को मार्गदर्शन नहीं देता। ');
INSERT INTO "al_umari" VALUES(2,259,'2:259','अथवा उस व्यक्ति के प्रकार, जो एक ऐसी नगरी से गुज़रा, जो अपनी छतों सहित ध्वस्त पड़ी थी? उसने कहाः अल्लाह इसके ध्वस्त हो जाने के पश्चात् इसे कैसे जीवित (आबाद) करेगा? फिर अल्लाह ने उसे सौ वर्ष तक मौत दे दी। फिर उसे जीवित किया और कहाः तुम कितनी अवधि तक मुर्दा पड़े रहे? उसने कहाः एक दिन अथवा दिन के कुछ क्षण। (अल्लाह ने) कहाः बल्कि तुम सौ वर्ष तक पड़े रहे। अपने खाने पीने को देखो कि तनिक परिवर्तन नहीं हुआ है तथा अपने गधे की ओर देखो, ताकी हम तुम्हें लोगों के लिए एक निशानी (चिन्ह) बना दें तथा (गधे की) अस्थियों को देखो कि हम उन्हें कैसे खड़ा करते हैं और उनपर कैसे माँस चढ़ाते हैं? इस प्रकार जब उसके समक्ष बातें उजागर हो गयीं, तो वह[ पुकार उठा कि मझे (प्रत्यक्ष) ज्ञान हो गया कि अल्लाह जो चाहे, कर सकता है। ');
INSERT INTO "al_umari" VALUES(2,260,'2:260','तथा (याद करो) जब इब्राहीम ने कहाः हे मेरे पालनहार! मुझे दिखा दे कि तू मुर्दों को कैसे जीवित कर देता है? (अल्लाह ने) कहाः क्या तुम ईमान नहीं लाये? उसने कहाः क्यों नहीं? परन्तु ताकि मेरे दिल को संतोष हो जाये। अल्लाह ने कहाः चार पक्षी ले आओ और उनहें अपने से परचा लो। (फिर उन्हें वध करके) उनका एक-एक अंश (भाग) पर्वत पर रख दो। फिर उन्हें पुकारो। वे तुम्हारे पास दौड़े चले आयेंगे और ये जान ले कि अल्लाह प्रभुत्वशाली, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(2,261,'2:261','जो अल्लाह की राह में अपना धन दान करते हैं, उस (दान) की दशा उस एक दाने जैसी है, जिसने सात बालियाँ उगायी हों। (उसकी) प्रत्येक बाली में सौ दाने हों और अल्लाह जिसे चाहे और भी अधिक देता है तथा अल्लाह विशाल[, ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(2,262,'2:262','जो अपना धन अल्लाह की राह में दान करते हैं, फिर दान करने के पश्चात् उपकार नहीं जताते और न (जिसे दिया हो उसे) दुःख देते हैं, उन्हीं के लिए उनके पालनहार के पास उनका प्रतिकार (बदला) है और उनपर कोई डर नहीं होगा और न ही वे उदासीन[ होंगे। ');
INSERT INTO "al_umari" VALUES(2,263,'2:263','भली बात बोलना तथा क्षमा, उस दान से उत्तम है, जिसके पश्चात् दुःख दिया जाये तथा अल्लाह निस्पृह, सहनशील है।');
INSERT INTO "al_umari" VALUES(2,264,'2:264','हे ईमान वालो! उस व्यक्ति के समान उपकार जताकर तथा दुःख देकर, अपने दानों को व्यर्थ न करो, जो लोगों को दिखाने के लिए दान करता है और अल्लाह तथा अन्तिम दिन (परलोक) पर ईमान नहीं रखता। उसका उदाहरण उस चटेल पत्थर जैसा है, जिसपर मिट्टी पड़ी हो और उसपर घोर वर्षा हो जाये और उस (पत्थर) को चटेल छोड़ दे। वे अपनी कमाई का कुछ भी न पा सकेंगे और अल्लाह काफ़िरों को सीधी डगर नहीं दिखाता।');
INSERT INTO "al_umari" VALUES(2,265,'2:265','तथा उनकी उपमा, जो अपना धन अल्लाह की प्रसन्नता की इच्छा में अपने मन की स्थिरता के साथ दान करते हैं, उस बाग़ (उद्यान) जैसी है, जो पृथ्वी तल के किसी ऊँचे भाग पर हो, उसपर घोर वर्षा हुई, तो दोगुना फल लाया और यदि घोर वर्षा नहीं हुई, तो (उसके लिए) फुहार ही बस[ हो तथा तुम जो कुछ कर रहे हो, उसे अल्लाह देख रहा है। ');
INSERT INTO "al_umari" VALUES(2,266,'2:266','क्या तुममें से कोई चाहेगा कि उसके खजूर तथा अँगूरों के बाग़ हों, जिनमें नहरें बह रही हों, उनमें उसके लिए प्रत्येक प्रकार के फल हों तथा वह बूढ़ा हो गया हो और उसके निर्बल बच्चे हों, फिर वह बगोल के आघात से जिसमें आग हो, झुलस जाये।[ इसी प्रकार अल्लाह तुम्हारे लिए आयतें उजागर करता है, ताकि तुम सोच विचार करो। ');
INSERT INTO "al_umari" VALUES(2,267,'2:267','हे ईमान वालो! उन स्वच्छ चीजों में से, जो तुमने कमाई हैं तथा उन चीज़ों में से, जो हमने तुम्हारे लिए धरती से उपजायी हैं, दान करो तथा उसमें से उस चीज़ को दान करने का निश्चय न करो, जिसे तुम स्वयं न ले सको, परन्तु ये कि अनदेखी कर जाओ तथा जान लो कि अल्लाह निःस्पृह, प्रशंसित है।');
INSERT INTO "al_umari" VALUES(2,268,'2:268','शैतान तुम्हें निर्धनता से डराता है तथा निर्लज्जा की प्रेरणा देता है तथा अल्लाह तुम्हें अपनी क्षमा और अधिक देने का वचन देता है तथा अल्लाह विशाल ज्ञानी है।');
INSERT INTO "al_umari" VALUES(2,269,'2:269','वह जिसे चाहे, प्रबोध (धर्म की समझ) प्रदान करता है और जिसे प्रबोध प्रदान कर दिया गया, उसे बड़ा कल्याण मिल गया और समझ वाले ही शिक्षा ग्रहण करते हैं।');
INSERT INTO "al_umari" VALUES(2,270,'2:270','तथा तुम जो भी दान करो अथवा मनौती[ मानो, अल्लाह उसे जानता है तथा अत्याचारियों का कोई सहायक न होगा। ');
INSERT INTO "al_umari" VALUES(2,271,'2:271','यदि तुम खुले दान करो, तो वह भी अच्छा है तथा यदी छुपाकर करो और कंगालों को दो, तो वह तुम्हारे लिए अधिक अच्छा[ है। ये तुमसे तुम्हारे पापों को दूर कर देगा तथा तुम जो कुछ कर रहे हो, उससे अल्लाह सूचित है। ');
INSERT INTO "al_umari" VALUES(2,272,'2:272','उन्हें सीधी डगर पर लगा देना, आपका दायित्व नहीं, परन्तु अल्लाह जिसे चाहे, सीधी डगर पर लगा देता है तथा तुम जो भी दान देते हो, तो अपने लाभ के लिए देते हो तथा तुम अल्लाह की प्रसन्नता प्राप्त करने के लिए ही देते हो तथा तुम जो भी दान दोगे, तुम्हें उसका भर पूर प्रतिफल (बदला) दिया जायेगा और तुमपर अत्याचार[ नहीं किया जायेगा। ');
INSERT INTO "al_umari" VALUES(2,273,'2:273','दान उन निर्धनों (कंगालों) के लिए है, जो अल्लाह की राह में ऐसे घिर गये हों कि धरती में दौड़-भाग न कर[ सकते हों, उन्हें अज्ञान लोग न माँगने के कारण धनी समझते हैं, वे लोगों के पीछे पड़ कर नहीं माँगते। तुम उन्हें उनके लक्षणों से पहचान लोगे तथा जो भी धन तुम दान करोगे, निःसंदेह अल्लाह उसे भलि-भाँति जानने वाला है। ');
INSERT INTO "al_umari" VALUES(2,274,'2:274','जो लोग अपना धन रात-दिन खुले-छुपे दान करते हैं, तो उन्हीं के लिए उनके पालनहार के पास, उनका प्रतिफल (बदला) है और उन्हें कोई डर नहीं होगा और न वे उदासीन होंगे।');
INSERT INTO "al_umari" VALUES(2,275,'2:275','जो लोग ब्याज खाते हैं, ऐसे उठेंगे जैसे वह उठता है, जिसे शैतान ने छूकर उनमत्त कर दिया हो। उनकी ये दशा इस कारण होगी कि उन्होंने कहा कि व्यापार भी तो ब्याज ही जैसा है, जबकि अल्लाह ने व्यापार को ह़लाल (वैध) , तथा ब्याज को ह़राम (अवैध) कर[ दिया है। अब जिसके पास उसके पालनहार की ओर से निर्देश आ गया और इस कारण उससे रुक गया, तो जो कुछ पहले लिया, वह उसी का हो गया तथा उसका मामला अल्लाह के ह़वाले है और जो (लोग) फिर वही करें, तो वही नारकी हैं, जो उसमें सदावासी होंगे। ');
INSERT INTO "al_umari" VALUES(2,276,'2:276','अल्लाह ब्याज को मिटाता है और दानों को बढ़ाता है और अल्लाह किसी कृतघ्न, घोर पापी से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(2,277,'2:277','वास्तव में, जो ईमान लाये, सदाचार किये, नमाज़ की स्थाप्ना करते रहे और ज़कात देते रहे, उन्हीं के लिए उनके पालनहार के पास उनका प्रतिफल है और उन्हें कोई डर नहीं होगा और न वे उदासीन होंगे।');
INSERT INTO "al_umari" VALUES(2,278,'2:278','हे ईमान वालो! अल्लाह से डरो और जो ब्याज शेष रह गया है, उसे छोड़ दो, यदि तुम ईमान रखने वाले हो तो।');
INSERT INTO "al_umari" VALUES(2,279,'2:279','और यदि तुमने ऐसा नहीं किया, तो अल्लाह तथा उसके रसूल से युध्द के लिए तैयार हो जाओ और यदि तुम तौबा (क्षमा याचना) कर लो, तो तुम्हारे लिए तुम्हारा मूलधन है। न तुम अत्याचार करो[, न तुमपर अत्याचार किया जाये। ');
INSERT INTO "al_umari" VALUES(2,280,'2:280','और यदि तुम्हारा ऋणि असुविधा में हो, तो उसे सुविधा तक अवसर दो और अगर क्षमा कर दो, (अर्थात दान कर दो) तो ये तुम्हारे लिए अधिक अच्छा है, यदि तुम समझो तो।');
INSERT INTO "al_umari" VALUES(2,281,'2:281','तथा उस दिन से डरो, जिसमें तुम अल्लाह की ओर फेरे जाओगे, फिर प्रत्येक प्राणी को उसकी कमाई का भरपूर प्रतिकार दिया जायेगा तथा किसी पर अत्याचार न होगा।');
INSERT INTO "al_umari" VALUES(2,282,'2:282','हे ईमान वालो! जब तुम आपस में किसी निश्चित अवधि तक के लिए उधार लेन-देन करो, तो उसे लिख लिया करो, तुम्हारे बीच न्याय के साथ कोई लेखक लिखे, जिसे अल्लाह ने लिखने की योग्यता दी है, वह लिखने से इन्कार न करे तथा वह लिखवाये, जिसपर उधार है और अपने पालनहार अल्लाह से डरे और उसमें से कुछ कम न करे। यदि जिसपर उधार है, वह निर्बोध अथवा निर्बल हो अथवा लिखवा न सकता हो, तो उसका संरक्षक न्याय के साथ लिखवाये तथा अपने में से दो पुरुषों को साक्षी (गवाह) बना लो। यदि दो पुरुष न हों, तो एक पुरुष तथा दो स्त्रियों को, उन साक्षियों में से, जिन्हें साक्षी बनाना पसन्द करो। ताकि दोनों (स्त्रियों) में से एक भूल जाये, तो दूसरी याद दिला दे तथा जब साक्षी बुलाये जायें, तो इन्कार न करें तथा विषय छोटा हो या बड़ा, उसकी अवधि सहित लिखवाने में आलस्य न करो, ये अल्लाह के समीप अधिक न्याय है तथा साक्ष्य के लिए अधिक सहायक और इससे अधिक समीप है कि संदेह न करो। परन्तु यदि तुम व्यापारिक लेन-देन हाथों-हाथ (नगद करते हो), तो तुमपर कोई दोष नहीं कि उसे न लिखो तथा जब आपस में लेन-देन करो, तो साक्षी बना लो और लेखक तथा साक्षी को हानि न पहुँचाई जाये और यदि ऐसा करोगो, तो तुम्हारी अवज्ञा ही होगी तथा अल्लाह से डरो और अल्लाह तुम्हें सिखा रहा है और निःसंदेह अल्लाह सब कुछ जानता है।');
INSERT INTO "al_umari" VALUES(2,283,'2:283','और यदि तुम यात्रा में रहो तथा लिखने के लिए किसी को न पाओ, तो धरोहर रख दो और यदि तुममें परस्पर एक-दूसरे पर भरोसा हो, (तो धरोहर की भी आवश्यक्ता नहीं,) जिसपर अमानत (उधार) है, वह उसे चुका दे तथा अल्लाह (अपने पालनहार) से डरे और साक्ष्य न छुपाओ और जो उसे छुपायेगा, उसका दिल पापी है तथा तुम जो करते हो, अल्लाह सब जानता है।');
INSERT INTO "al_umari" VALUES(2,284,'2:284','आकाशों तथा धरती में जो कुछ है, सब अल्लाह का है और जो तुम्हारे मन में है, उसे बोलो अथवा मन ही में रखो, अल्लाह तुमसे उसका ह़िसाब लेगा। फिर जिसे चाहे, क्षमा कर देगा और जिसे चाहे, दण्ड देगा और अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(2,285,'2:285','रसूल उस चीज़ पर ईमान लाया, जो उसके लिए अल्लाह की ओर से उतारी गई तथा सब ईमान वाले उसपर ईमान लाये। वे सब अल्लाह तथा उसके फ़रिश्तों और उसकी सब पुस्तकों एवं रसूलों पर ईमान लाये। (वे कहते हैः) हम उसके रसूलों में से किसी के बीच अन्तर नहीं करते। हमने सुना और हम आज्ञाकारी हो गये। हे हमारे पालनहार! हमें क्षमा कर दे और हमें तेरे ही पास[ आना है। ');
INSERT INTO "al_umari" VALUES(2,286,'2:286','अल्लाह किसी प्राणी पर उसकी सकत से अधिक (दायित्व का) भार नहीं रखता। जो सदाचार करेगा, उसका लाभ उसी को मिलेगा और जो दुराचार करेगा, उसकी हानि भी उसी को होगी। हे हमारे पालनहार! यदि हम भूल चूक जायें, तो हमें न पकड़। हे हमारे पालनहार! हमारे ऊपर इतना बोझ न डाल, जितना हमसे पहले के लोगों पर डाला गया। हे हमारे पालनहार! हमारे पापों की अनदेखी कर दे, हमें क्षमा कर दे तथा हमपर दया कर। तू ही हमारा स्वामी है तथा काफ़िरों के विरुध्द हमारी सहायता कर।');
INSERT INTO "al_umari" VALUES(3,1,'3:1','अलिफ़, लाम, मीम।');
INSERT INTO "al_umari" VALUES(3,2,'3:2','अल्लाह के सिवा कोई पूज्य नहीं, वह जीवित, नित्य स्थायी है।');
INSERT INTO "al_umari" VALUES(3,3,'3:3','उसीने आपपर सत्य के साथ पुस्तक (क़ुर्आन) उतारी है, जो इससे पहले की पुस्तकों के लिए प्रमाणकारी है और उसीने तौरात तथा इंजील उतारी है।');
INSERT INTO "al_umari" VALUES(3,4,'3:4','इससे पूर्व, लोगों के मार्गदर्शन के लिए और फ़ुर्क़ान उतारा है[ तथा जिन्होंने अल्लाह की आयतों को अस्वीकार किया, उन्हीं के लिए कड़ी यातना है और अल्लाह प्रभुत्वशाली, बदला लेने वाला है। ');
INSERT INTO "al_umari" VALUES(3,5,'3:5','निःसंदेह अल्लाह से आकाशों तथा धरती की कोई चीज़ छुपी नहीं है।');
INSERT INTO "al_umari" VALUES(3,6,'3:6','वही तुम्हारा रूप आकार गर्भाषयों में जैसे चाहता है, बनाता है। कोई पूज्य नहीं, परन्तु वही प्रभुत्वशाली, तत्वज्ञ।');
INSERT INTO "al_umari" VALUES(3,7,'3:7','उसीने आपपर[ ये पुस्तक (क़ुर्आन) उतारी है, जिसमें कुछ आयतें मुह़कम (सुदृढ़) हैं, जो पुस्तक का मूल आधार हैं तथा कुछ दूसरी मुतशाबिह (संदिग्ध) हैं। तो जिनके दिलों में कुटिलता है, वे उपद्रव की खोज तथा मनमाना अर्थ करने के लिए, संदिग्ध के पीछे पड़ जाते हैं। जबकि उनका वास्तविक अर्थ, अल्लाह के सिवा कोई नहीं जानता तथा जो ज्ञान में पक्के हैं, वे कहते हैं कि सब, हमारे पालनहार के पास से है और बुध्दिमान लोग ही शिक्षा ग्रहण करते हैं।');
INSERT INTO "al_umari" VALUES(3,8,'3:8','(तथा कहते हैः) हे हमारे पालनहार! हमारे दिलों को, हमें मार्गदर्शन देने के पश्चात् कुटिल न कर, वास्तव में, तू बहुत बड़ा दाता है।');
INSERT INTO "al_umari" VALUES(3,9,'3:9','हे मारे पालनहार! तू उस दिन सबको एकत्र करने वाला है, जिसमें कोई संदेह नहीं। निःसंदेह अल्लाह अपने निर्धारित समय का विरुध्द नहीं करता।');
INSERT INTO "al_umari" VALUES(3,10,'3:10','निश्चय जो क़ाफ़िर हो गये, उनके धन तथा उनकी संतान अल्लाह (की यातना) से (बचाने में) उनके कुछ काम नहीं आयेगी तथा वही अग्नि के ईंधन बनेंगे।');
INSERT INTO "al_umari" VALUES(3,11,'3:11','जैसे फ़िरऔनियों तथा उनके पहले लोगों की दशा हुई, उन्होंने हमारी निशानियों को मिथ्या कहा, तो अल्लाह ने उनके पापों के कारण उनको धर लिया तथा अल्लाह कड़ा दण्ड देने वाला है।');
INSERT INTO "al_umari" VALUES(3,12,'3:12','(हे नबी!) काफ़िरों से कह दो कि तुम शीघ्र ही प्रास्त कर दिये जाओगे तथा नरक की ओर एकत्र किये जाओगे और वह बहुत बुरा ठिकाना[ है। ');
INSERT INTO "al_umari" VALUES(3,13,'3:13','वास्तव में, तुम्हारे लिए उन दो दलों में, जो (बद्र में) सम्मुख हो गये, एक निशानी थी; एक अल्लाह की राह में युध्द कर रहा था तथा दूसरा काफ़िर था, वे (अर्थात काफ़िर गिरोह के लोग) अपनी आँखों से देख रहे थे कि ये (मुसलमान) तो दुगने लग रहे हैं तथा अल्लाह अपनी सहायता द्वारा जिसे चाहे, समर्थन देता है। निःसंदेह इसमें समझ-बूझ वालों के लिए बड़ी शिक्षा[ है। ');
INSERT INTO "al_umari" VALUES(3,14,'3:14','लोगों के लिए उनके मनको मोहने वाली चीज़ें, जैसे स्त्रियाँ, संतान, सोने चाँदी के ढेर, निशान लगे घोड़े, पशुओं तथा खेती शोभनीय बना दी गई हैं। ये सब सांसारिक जीवन के उपभोग्य हैं और उत्तम आवास अल्लाह के पास है।');
INSERT INTO "al_umari" VALUES(3,15,'3:15','(हे नबी!) कह दोः क्या मैं तुम्हें इससे उत्तम चीज़ बता दूँ? उनके लिए जो डरें, उनके पालनहार के पास ऐसे स्वर्ग हैं, जिनमें नहरें बह रही हैं। वे उनमें सदावासी होंगे और निर्मल पत्नियाँ होंगी तथा अल्लाह की प्रसन्नता प्राप्त होगी और अल्लाह अपने भक्तों को देख रहा है।');
INSERT INTO "al_umari" VALUES(3,16,'3:16','जो (ये) प्रार्थना करते हैं कि हमारे पालनहार! हम ईमान लाये, अतः हमारे पाप क्षमा कर दे और हमें नरक की यातना से बचा।');
INSERT INTO "al_umari" VALUES(3,17,'3:17','जो सहनशील हैं, सत्यवादी हैं, आज्ञाकारी हैं, दानशील तथा भोरों में अल्लाह से क्षमा याचना करने वाले हैं।');
INSERT INTO "al_umari" VALUES(3,18,'3:18','अल्लाह साक्षी है, जो न्याय के साथ क़ायम है कि उसके सिवा कोई पूज्य नहीं है, इसी प्रकार फ़रिश्ते और ज्ञानी लोग भी (साक्षी हैं) कि उसके सिवा कोई पूज्य नहीं। वह प्रभुत्वशाली, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(3,19,'3:19','निःसंदेह (वास्तविक) धर्म अल्लाह के पास इस्लाम ही है और अह्ले किताब ने जो विभेद किया, तो अपने पास ज्ञान आने के पश्चात् आपस में द्वेष के कारण किया तथा जो अल्लाह की आयतों के साथ कुफ़्र (अस्वीकार) करेगा, तो निश्चय अल्लाह शीघ्र ह़िसाब लेने वाला है।');
INSERT INTO "al_umari" VALUES(3,20,'3:20','फिर यदि वे आपसे विवाद करें, तो कह दें कि मैं स्वयं तथा जिसने मेरा अनुसरण किया अल्लाह के आज्ञाकारी हो गये तथा अह्ले किताब और उम्मियों (अर्थात जिनके पास कोई किताब नहीं आयी) से कहो कि क्या तुम भी आज्ञाकारी हो गये? यदि वे आज्ञाकारी हो गये, तो मार्गदर्शन पा गये और यदि विमुख हो गये, तो आपका दायित्व (संदेश) पहुँचा[ देना है तथा अल्लाह भक्तों को देख रहा है। ');
INSERT INTO "al_umari" VALUES(3,21,'3:21','जो लोग अल्लाह की आयतों के साथ कुफ़्र करते हों तथा नबियों को अवैध वध करते हों, तथा उन लोगों का वध करते हों, जो न्याय का आदेश देते हैं, तो उन्हें दुःखदायी यातना[ की शुभ सूचना सुना दो। ');
INSERT INTO "al_umari" VALUES(3,22,'3:22','यही हैं, जिनके कर्म संसार तथा परलोक में अकारथ गये और उनका कोई सहायक नहीं होगा।');
INSERT INTO "al_umari" VALUES(3,23,'3:23','(हे नबी!) क्या आपने उनकी[ दशा नहीं देखी, जिन्हें पुस्तक का कुछ भाग दिया गया? वे अल्लाह की पुस्तक की ओर बुलाये जा रहे हैं, ताकि उनके बीच निर्णय करे, तो उनका एक गिरोह मुँह फेर रहा है और वे हैं ही मुँह फेरने वाले।');
INSERT INTO "al_umari" VALUES(3,24,'3:24','उनकी ये दशा इसलिए है कि उन्होंने कहा कि नरक की अग्नि हमें गिनती के कुछ दिन ही छुएगी तथा उन्हें अपने धर्म में उनकी मिथ्या बनायी हुई बातों ने धोखे में डाल रखा है।');
INSERT INTO "al_umari" VALUES(3,25,'3:25','तो उनकी क्या दशा होगी, जब हम उन्हें उस दिन एकत्र करेंगे, जिस (के आने) में कोई संदेह नहीं तथा प्रत्येक प्राणी को उसके किये का भरपूर प्रतिफल दिया जायेगा और किसी के साथ कोई अत्याचार नहीं किया जायेगा?');
INSERT INTO "al_umari" VALUES(3,26,'3:26','(हे नबी!) कहोः हे अल्लाह! राज्य के[ अधिपति (स्वामी)! तू जिसे चाहे, राज्य दे और जिससे चाहे, राज्य छीन ले तथा जिसे चाहे, सम्मान दे और जिसे चाहे, अपमान दे। तेरे ही हाथ में भलाई है। निःसंदेह तू जो चाहे, कर सकता है। ');
INSERT INTO "al_umari" VALUES(3,27,'3:27','तू रात को दिन में प्रविष्ट कर देता है तथा दिन को रात में प्रविष्ट कर[ देता है और जीव को निर्जीव से निकालता है तथा निर्जीव को जीव से निकालता है और जिसे चाहे अगणित आजीविका प्रदान करता है। ');
INSERT INTO "al_umari" VALUES(3,28,'3:28','मोमिनों को चाहिए कि वो ईमान वालों के विरुध्द काफ़िरों को अपना सहायक मित्र न बनायें और जो ऐसा करेगा, उसका अल्लाह से कोई संबंध नहीं। परन्तु उनसे बचने के लिए[ और अल्लाह तुम्हें स्वयं अपने से डरा रहा है और अल्लाह ही की ओर जाना है। ');
INSERT INTO "al_umari" VALUES(3,29,'3:29','(हे नबी!) कह दो कि जो तुम्हारे मन में है, उसे मन ही में रखो या व्यक्त करो, अल्लाह उसे जानता है तथा जो कुछ आकाशों तथा धरती में है, वह सबको जानता है और अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(3,30,'3:30','जिस दिन प्रत्येक प्राणी ने जो सुकर्म किया है, उसे उपस्थित पायेगा तथा जिसने कुकर्म किया है, वह कामना करेगा कि उसके तथा उसके कुकर्मों के बीच बड़ी दूरी होती तथा अल्लाह तुम्हें स्वयं से डराता[ है और अल्लाह अपने भक्तों के लिए अति करुणामय है। ');
INSERT INTO "al_umari" VALUES(3,31,'3:31','(हे नबी!) कह दोः यदि तुम अल्लाह से प्रेम करते हो, तो मेरा अनुसरण करो, अल्लाह तुमसे प्रेम[ करेगा तथा तुम्हारे पाप क्षमा कर देगा और अल्लाह अति क्षमाशील, दयावान् है। ');
INSERT INTO "al_umari" VALUES(3,32,'3:32','(हे नबी!) कह दोः अल्लाह और रसूल की आज्ञा का अनुपालन करो। फिर भी यदि वे विमुख हों, तो निःसंदेह अल्लाह काफ़िरों से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(3,33,'3:33','वस्तुतः, अल्लाह ने आदम, नूह़, इब्राहीम की संतान तथा इमरान की संतान को संसार वासियों में चुन लिया था।');
INSERT INTO "al_umari" VALUES(3,34,'3:34','ये एक-दूसरे की संतान हैं और अल्लाह सब सुनता और जानता है।');
INSERT INTO "al_umari" VALUES(3,35,'3:35','जब इमरान की पत्नी[ ने कहाः हे मेरे पालनहार! जो मेरे गर्भ में है, मैंने तेरे लिए उसे मुक्त करने की मनौती मान ली है। तू इसे मुझसे स्वीकार कर ले। वास्तव में, तू ही सब कुछ सुनता और जानता है।');
INSERT INTO "al_umari" VALUES(3,36,'3:36','फिर जब उसने बालिका जनी, तो (संताप से) कहाः मेरे पालनहार! मुझे तो बालिका हो गयी, हालाँकि जो उसने जना, उसका अल्लाह को भली-भाँति ज्ञान था -और नर नारी के समान नहीं होता- और मैंने उसका नाम मर्यम रखा है और मैं उसे तथा उसकी संतान को धिक्कारे हुए शैतान से तेरी शरण में देती हूँ।[ ');
INSERT INTO "al_umari" VALUES(3,37,'3:37','तो तेरे पालनहार ने उसे भली-भाँति स्वीकार कर लिया तथा उसका अच्छा प्रतिपालन किया और ज़करिय्या को उसका संरक्षक बनाया। ज़करिय्या जबभी उसके मेह़राब (उपासना कोष्ट) में जाता, तो उसके पास कुछ खाद्य पदार्थ पाता, वह कहता कि हे मर्यम! ये कहाँ से (आया) है? वह कहतीः ये अल्लाह के पास से (आया) है। वास्तव में, अल्लाह जिसे चाहता है, अगणित जीविका प्रदा करता है।');
INSERT INTO "al_umari" VALUES(3,38,'3:38','तब ज़करिय्या ने अपने पालनहार से प्रार्थना कीः हे मेरे पालनहार! मुझे अपनी ओर से सदाचारी संतान प्रदान कर। निःसंदेह तू प्रार्थना सुनने वाला है।');
INSERT INTO "al_umari" VALUES(3,39,'3:39','तो फ़रिश्तों ने उसे पुकारा- जब वह मेह़राब में खड़ा नमाज़ पढ़ रहा था- कि अल्लाह तुझे ''यह़्या'' की शुभ सूचना दे रहा है, जो अल्लाह के शब्द (ईसा) का पुष्टि करने वाला, प्रमुख तथा संयमी और सदाचारियों में से एक नबी होगा।');
INSERT INTO "al_umari" VALUES(3,40,'3:40','उसने कहाः मेरे पालनहार! मेरे कोई पुत्र कहाँ से होगा, जबकि मैं बूढ़ा हो गया हूँ और मेरी पत्नी बाँझ[ है? उसने कहाः अल्लाह इसी प्रकार जो चाहता है, कर देता है। ');
INSERT INTO "al_umari" VALUES(3,41,'3:41','उसने कहाः मेरे पालनहार! मेरे लिए कोई लक्षण बना दे। उसने कहाः तेरा लक्षण ये होगा कि तीन दिन तक लोगों से बात नहीं कर सकेगा, परन्तु संकेत से तथा अपने पालनहार का बहुत स्मरण करता रह और संध्या-प्राता उसी की पवित्रता का वर्णन कर।');
INSERT INTO "al_umari" VALUES(3,42,'3:42','और (याद करो) जब फरिश्तों ने मर्यम से कहाः हे मर्यम! तुझे अल्लाह ने चुन लिया तथा पवित्रता प्रदान की और संसार की स्त्रियों पर तुझे चुन लिया।');
INSERT INTO "al_umari" VALUES(3,43,'3:43','हे मर्यम! अपने पालनहार की आज्ञाकारी रहो, सज्दा करो तथा रुकूअ करने वालों के साथ रुकूअ करती रहो।');
INSERT INTO "al_umari" VALUES(3,44,'3:44','ये ग़ैब (परोक्ष) की सूचनायें हैं, जिन्हें हम आपकी ओर प्रकाशना कर रहे हैं और आप उनके पास उपस्थित नहीं थे, जब वे अपनी लेखनियाँ[ फेंक रहे थे कि कौन मर्यम का अभिरक्षण करेगा और न उनके पास उपस्थित थे, जब वे झगड़ रहे थे। ');
INSERT INTO "al_umari" VALUES(3,45,'3:45','जब फ़रिश्तों ने कहाः हे मर्यम! अल्लाह तुझे अपने एक शब्द[ की शुभ सूचना दे रहा है, जिसका नाम मसीह़ ईसा पुत्र मर्यम होगा। वह लोक-प्रलोक में प्रमुख तथा (मेरे) समीपवर्तियों में होगा। ');
INSERT INTO "al_umari" VALUES(3,46,'3:46','वह लोगों से गोद में तथा अधेड़ आयु में बातें करेगा और सदाचारियों में होगा।');
INSERT INTO "al_umari" VALUES(3,47,'3:47','मर्यम ने (आश्चर्य से) कहाः मेरे पालनहार! मुझे पुत्र कहाँ से होगा, मुझे तो किसी पुरुष ने हाथ भी नहीं लगाया है? उसने[ कहाः इसी प्रकार अल्लाह जो चाहता है, उत्पन्न कर देता है। जब वह किसी काम के करने का निर्णय कर लेता है, तो उसके लिए कहता है किः "हो जा", तो वह हो जाता है। ');
INSERT INTO "al_umari" VALUES(3,48,'3:48','और अल्लाह उसे पुस्तक तथा प्रबोध और तौरात तथा इंजील की शिक्षा देगा।');
INSERT INTO "al_umari" VALUES(3,49,'3:49','और फिर वह बनी इस्राईल का एक रसूल होगा (और कहेगाः) कि मैं तुम्हारे पालनहार की ओर से निशानी लाया हूँ। मैं तुम्हारे लिए मिट्टी से पक्षी के आकार के समान बनाऊँगा, फिर उसमें फूँक दूँगा, तो वह अल्लाह की अनुमति से पक्षी बन जायेगा और अल्लाह की अनुमति से जन्म से अंधे तथा कोढ़ी को स्वस्थ कर दूँगा और मुर्दों को जीवित कर दूँगा तथा जो कुछ तुम खाते तथा अपने घरों में संचित करते हो, उसे तुम्हें बता दूँगा। निःसंदेह, इसमें तुम्हारे लिए बड़ी निशानियाँ हैं, यदि तुम ईमान वाले हो।');
INSERT INTO "al_umari" VALUES(3,50,'3:50','तथा मैं उसकी सिध्दि करने वाला हूँ, जो मुझसे पहले की है ''तौरात''। तुम्हारे लिए कुछ चीज़ों को ह़लाला (वैध) करने वाला हूँ, जो तुमपर ह़राम (अवैध) की गयी हैं तथा मैं तुम्हारे पास तुम्हारे पालनहार की निशानी लेकर आया हूँ। अतः तुम अल्लाह से डरो और मेरे आज्ञाकारी हो जाओ।');
INSERT INTO "al_umari" VALUES(3,51,'3:51','वास्तव में, अल्लाह मेरा और तुम सबका पालनहार है। अतः उसी की इबादत (वंदना) करो। यही सीधी डगर है।');
INSERT INTO "al_umari" VALUES(3,52,'3:52','तथा जब ईसा ने उनसे कुफ़्र का संवेदन किया, तो कहाः अल्लाह के धर्म की सहायता में कौन मेरा साथ देगा? तो ह़वारियों (सहचरों) ने कहाः हम अल्लाह के सहायक हैं। हम अल्लाह पर ईमान लाये, तुम इसके साक्षी रहो कि हम मुस्लिम (आज्ञाकारी) हैं।');
INSERT INTO "al_umari" VALUES(3,53,'3:53','हे हमारे पालनहार! जो कुछ तूने उतारा है, हम उसपर ईमान लाये तथा तेरे रसूल का अनुसरण किया, अतः हमें भी साक्षियों में अंकित कर ले।');
INSERT INTO "al_umari" VALUES(3,54,'3:54','तथा उन्होंने षड्यंत्र[ रचा और हमने भी षड्यंत्र रचा तथा अल्लाह षड्यंत्र रचने वालों में सबसे अच्छा है। ');
INSERT INTO "al_umari" VALUES(3,55,'3:55','जब अल्लाह ने कहाः हे ईसा! मैं तुझे पूर्णतः लेने वाला तथा अपनी ओर उठाने वाला हूँ तथा तुझे काफ़िरों से पवित्र (मुक्त) करने वाला हूँ तथा तेरे अनुयायियों को प्रलय के दिन तक काफ़िरों के ऊपर[ करने वाला हूँ। फिर तुम्हारा लौटना मेरी ही ओर है। तो मैं तुम्हारे बीच उस विषय में निर्णय कर दूँगा, जिसमें तुम विभेद कर रहे हो। ');
INSERT INTO "al_umari" VALUES(3,56,'3:56','फिर जो काफ़िर हो गये, उन्हें लोक-प्रलोक में कड़ी यातना दूँगा तथा उनका कोई सहायक न होगा।');
INSERT INTO "al_umari" VALUES(3,57,'3:57','तथा जो ईमान लाये और सदाचार किये, तो उन्हें उनका भरपूर प्रतिफल दूँगा तथा अल्लाह अत्याचारियों से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(3,58,'3:58','(हे नबी!) ये हमारी आयतें और तत्वज्ञता की शिक्षा है, जो हम तुम्हें सुना रहे हैं।');
INSERT INTO "al_umari" VALUES(3,59,'3:59','वस्तुतः अल्लाह के पास ईसा की मिसाल ऐसी ही है[, जैसे आदम की। उसे (अर्थात, आदम को) मिट्टी से उत्पन्न किया, फिर उससे कहाः "हो जा" तो वह हो गया। ');
INSERT INTO "al_umari" VALUES(3,60,'3:60','ये आपके पालनहार की ओर से सत्य[ है, अतः आप संदेह करने वालों में न हों। ');
INSERT INTO "al_umari" VALUES(3,61,'3:61','फिर आपके पास ज्ञान आ जाने के पश्चात् कोई आपसे ईसा के विषय में विवाद करे, तो कहो कि आओ, हम अपने पुत्रों तथा तुम्हारे पुत्रों और अपनी स्त्रियों तथा तुम्हारी स्त्रियों को बुलाते हैं और स्वयं को भी, फिर अल्लाह से सविनय प्रार्थना करें कि अल्लाह की धिक्कार मिथ्यावादियों पर[ हो। ');
INSERT INTO "al_umari" VALUES(3,62,'3:62','वास्तव में, यही सत्य वर्णन है तथा अल्लाह के सिवा कोई पूज्य नहीं। निश्चय अल्लाह ही प्रभुत्वशाली तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(3,63,'3:63','फिर भी यदि वे मुँह[ फेरें, तो निःसंदेह अल्लाह उपद्रवियों को भली-भाँति जानता है। ');
INSERT INTO "al_umari" VALUES(3,64,'3:64','(हे नबी!) कहो कि हे अह्ले किताब! एक ऐसी बात की ओर आ जाओ, जो हमारे तथा तुम्हारे बीच समान रूप से मान्य है कि अल्लाह के सिवा किसी की इबादत (वंदना) न करें और किसी को उसका साझी न बनायें तथा हममें से कोई एक-दूसरे को अल्लाह के सिवा पालनहार न बनाये। फिर यदि वे विमुख हों, तो आप कह दें कि तुम साक्षी रहो कि हम (अल्लाह के)[ आज्ञाकारी हैं। ');
INSERT INTO "al_umari" VALUES(3,65,'3:65','हे अह्ले किताब! तुम इब्राहीम के बारे में विवाद[ क्यों करते हो, जबकि तौरात तथा इंजील इब्राहीम के पश्चात् उतारी गई हैं? क्या तुम समझ नहीं रखते? ');
INSERT INTO "al_umari" VALUES(3,66,'3:66','और फिर तुम्हीं ने उस विषय में विवाद किया, जिसका तुम्हें कुछ ज्ञान[ था, तो उस विषय में क्यों विवाद कर रहे हो, जिसका तुम्हें कोई ज्ञान नहीं था? तथा अल्लाह जानता है और तुम नहीं जानते।');
INSERT INTO "al_umari" VALUES(3,67,'3:67','इब्राहीम न यहूदी था, न नस्रानी (ईसाई)। परन्तु वह एकेश्वरवादी, मुस्लिम ''आज्ञाकारी'' था तथा वह मिश्रणवादियों में से नहीं था।');
INSERT INTO "al_umari" VALUES(3,68,'3:68','वास्तव में, इब्राहीम से सबसे अधिक समीप तो वह लोग हैं, जिन्होंने उसका अनुसरण किया तथा ये नबी[ और जो ईमान लाये और अल्लाह ईमान वालों का संरक्षकभमित्र है। ');
INSERT INTO "al_umari" VALUES(3,69,'3:69','अह्ले किताब में से एक गिरोह की कामना है कि तुम्हें कुपथ कर दे। जबकि वे स्वयं को कुपथ कर रहे हैं, परन्तु वे समझते नहीं हैं।');
INSERT INTO "al_umari" VALUES(3,70,'3:70','हे अह्ले किताब! तुम अल्लाह की आयतों[ के साथ कुफ़्र क्यों कर रहे हो, जबकि तुम साक्षी हो?');
INSERT INTO "al_umari" VALUES(3,71,'3:71','हे अह्ले किताब! क्यों सत्य को असत्य के साथ मिलाकर संदिग्ध कर देते हो और सत्य को छुपाते हो, जबकि तुम जानते हो?');
INSERT INTO "al_umari" VALUES(3,72,'3:72','अह्ले किताब के एक समुदाय ने कहा कि दिन के आरंभ में उसपर ईमान ले आओ, जो ईमान वालों पर उतारा गया है और उसके अन्त (अर्थातः संध्या-समय) कुफ़्र कर दो, संभवतः वे फिर[ जायें। ');
INSERT INTO "al_umari" VALUES(3,73,'3:73','और केवल उसी की मानो, जो तुम्हारे (धर्म) का अनुसरण करे। (हे नबी!) कह दो कि मार्गदर्शन तो वही है, जो अल्लाह का मार्गदर्शन है। (और ये भी न मानो कि) जो (धर्म) तुम्हें दिया गया है, वैसा किसी और को दिया जायेगा अथवा वे तुमसे तुम्हारे पालनहार के पास विवाद कर सकेंगे। आप कह दें कि प्रदान अल्लाह के हाथ में है, वह जिसे चाहे, देता है और अल्लाह विशाल ज्ञानी है।');
INSERT INTO "al_umari" VALUES(3,74,'3:74','वह जिसे चाहे, अपनी दया के साथ विशेष कर देता है तथा अल्लाह बड़ा दानशील है।');
INSERT INTO "al_umari" VALUES(3,75,'3:75','तथा अह्ले किताब में से वो भी है, जिसके पास चाँदी-सोने का ढेर धरोहर रख दो, तो उसे तुम्हें चुका देगा तथा उनमें वो भी है, जिसके पास एक दीनार[ भी धरोहर रख दो, तो तुम्हें नहीं चुकायेगा, परन्तु जब सदा उसके सिर पर सवार रहो। ये (बात) इसलिए है कि उन्होंने कहा कि उम्मियों के बारे में हमपर कोई दोष नहीं तथा अल्लाह पर जानते हुए झूठ बोलते हैं।');
INSERT INTO "al_umari" VALUES(3,76,'3:76','क्यों नहीं, जिसने अपना वचन पूरा किया और (अल्लाह से) डरा, तो वास्तव में अल्लाह डरने वालों से प्रेम करता है।');
INSERT INTO "al_umari" VALUES(3,77,'3:77','निःसंदेह जो अल्लाह के[ वचन तथा अपनी शपथों के बदले तनिक मूल्य खरीदते हैं, उन्हीं का आख़िरत (परलोक) में कोई भाग नहीं, न प्रलय के दिन अल्लाह उनसे बात करेगा और न उनकी ओर देखेगा और न उन्हें पवित्र करेगा तथा उन्हीं के लिए दुःखदायी यातना है। ');
INSERT INTO "al_umari" VALUES(3,78,'3:78','और बेशक उनमें से एक गिरोह[ ऐसा है, जो अपनी ज़बानों को किताब पढ़ते समय मरोड़ते हैं, ताकि तुम उसे पुस्तक में से समझो, जबकि वह पुस्तक में से नहीं है और कहते हैं कि वह अल्लाह के पास से है, जबकि वह अल्लाह के पास से नहीं है और अल्लाह पर जानते हुए झूठ बोलते हैं। ');
INSERT INTO "al_umari" VALUES(3,79,'3:79','किसी पुरुष जिसे अल्लाह ने पुस्तक, निर्णय शक्ति और नुबुव्वत दी हो, उसके लिए योग्य नहीं कि लोगों से कहे कि अल्लाह को छोड़कर मेरे दास बन जाओ[, अपितु (वह तो यही कहेगा कि) तुम अल्लाह वाले बन जाओ। इस कारण कि तुम पुस्तक की शिक्षा देते हो तथा इस कारण कि उसका अध्ययन स्वयं भी करते रहते हो। ');
INSERT INTO "al_umari" VALUES(3,80,'3:80','तथा वह तुम्हें कभी आदेश नहीं देगा कि फ़रिश्तों तथा नबियों को अपना पालनहार[ (पूज्य) बना लो। क्या तुम्हें कुफ़्र करने का आदेश देगा, जबकि तुम अल्लाह के आज्ञाकारी हो? ');
INSERT INTO "al_umari" VALUES(3,81,'3:81','तथा (याद करो) जब अल्लाह ने नबियों से वचन लिया कि जबभी मैं तुम्हें कोई पुस्तक और प्रबोध (तत्वदर्शिता) दूँ, फिर तुम्हारे पास कोई रसूल उसे प्रमाणित करते हुए आये, जो तुम्हारे पास है, तो तुम अवश्य उसपर ईमान लाना और उसका समर्थन करना। (अल्लाह) ने कहाः क्या तुमने स्वीकार किया और इसपर मेरे वचन का भार उठाया? तो सबने कहाः हमने स्वीकार कर लिया। अल्लाह ने कहाः तुम साक्षी रहो और मैं भी तुम्हारे[ साथ साक्षियों में से हूँ। ');
INSERT INTO "al_umari" VALUES(3,82,'3:82','फिर जिसने इसके[ पश्चात् मुँह फेर लिया, तो वही अवज्ञाकारी है। ');
INSERT INTO "al_umari" VALUES(3,83,'3:83','तो क्या वे अल्लाह के धर्म (इस्लाम) के सिवा (कोई दूसरा धर्म) खोज रहे हैं? जबकि जो आकाशों तथा धरती में है, स्वेच्छा तथा अनिच्छा उसी के आज्ञाकारी[ हैं तथा सब उसी की ओर फेरे जायेंगे।');
INSERT INTO "al_umari" VALUES(3,84,'3:84','(हे नबी!) आप कहें कि हम अल्लाह पर तथा जो हमपर उतारा गया और जो इब्राहीम, इस्माईल, इस्ह़ाक़, याक़ूब एवं (उनकी) संतानों पर उतारा गया तथा जो मूसा, ईसा तथा अन्य नबियों को उनके पालनहार की ओर से प्रदान किया गया है, (उनपर) ईमान लाये। हम उन (नबियों) में किसी के बीच कोई अंतर नहीं[ करते और हम उसी (अल्लाह) के आज्ञाकारी हैं। ');
INSERT INTO "al_umari" VALUES(3,85,'3:85','और जो भी इस्लाम के सिवा (किसी और धर्म) को चाहेगा, तो उसे उससे कदापि स्वीकार नहीं किया जायेगा और वे प्रलोक में क्षतिग्रस्तों में होगा।');
INSERT INTO "al_umari" VALUES(3,86,'3:86','अल्लाह ऐसी जाति को कैसे मार्गदर्शन देगा, जो अपने ईमान के पश्चात् काफ़िर हो गये और साक्षी रहे कि ये रसूल सत्य हैं तथा उनके पास खुले तर्क आ गये? और अल्लाह अत्याचारियों को मार्गदर्शन नहीं देता।');
INSERT INTO "al_umari" VALUES(3,87,'3:87','इन्हीं का प्रतिकार (बदला) ये है कि उनपर अल्लाह तथा फ़रिश्तों और सब लोगों की धिक्कार होगी।');
INSERT INTO "al_umari" VALUES(3,88,'3:88','वे उसमें सदावासी होंगे, उनसे यातना कम नहीं की जायेगी और न उन्हें अवकाश दिया जायेगा।');
INSERT INTO "al_umari" VALUES(3,89,'3:89','परन्तु जिन्होंने इसके पश्चात् तौबा (क्षमा याचना) कर ली तथा सुधार कर लिया, तो निश्चय अल्लाह अति क्षमाशील दयावान् है।');
INSERT INTO "al_umari" VALUES(3,90,'3:90','वास्तव में, जो अपने ईमान लाने के पश्चात् काफ़िर हो गये, फिर कुफ़्र में बढ़ते गये, तो उनकी तौबा (क्षमा याचना) कदापि[ स्वीकार नहीं की जायेगी तथा वही कुपथ हैं। ');
INSERT INTO "al_umari" VALUES(3,91,'3:91','निश्चय जो काफ़िर हो गये तथा काफ़िर रहते हुए मर गये, तो उनसे धरती भर सोना भी स्वीकार नहीं किया जायेगा, यद्यपि उसके द्वारा अर्थदणड दे। उन्हीं के लिए दुःखदायी यातना है और उनका कोई सहायक न होगा।');
INSERT INTO "al_umari" VALUES(3,92,'3:92','तुम पुण्य[ नहीं पा सकोगे, जब तक उसमें से दान न करो, जिससे मोह रखते हो तथा तुम जो भी दान करोगे, वास्तव में, अल्लाह उसे भली-भाँति जानता है। ');
INSERT INTO "al_umari" VALUES(3,93,'3:93','प्रत्येक खाद्य पदार्थ बनी इस्राईल[ के लिए ह़लाल (वैध) थे, परन्तु जिसे इस्राईल ने अपने ऊपर ह़राम (अवैध) कर लिया, इससे पहले कि तौरात उतारी जाये। (हे नबी!) कहो कि तौरात लाओ तथा उसे पढ़ो, यदि तुम सत्यवादि हो। ');
INSERT INTO "al_umari" VALUES(3,94,'3:94','फिर इसके पश्चात् जो अल्लाह पर मिथ्या आरोप लगायें, तो वही वास्तव, में अत्याचारी हैं।');
INSERT INTO "al_umari" VALUES(3,95,'3:95','उनसे कह दो, अललाह सच्चा है, इसलिए तुम एकेश्वरवादी इब्राहीम के धर्म पर चलो तथा वह मिश्रणवादियों में से नहीं था।');
INSERT INTO "al_umari" VALUES(3,96,'3:96','निःसंदेह पहला घर, जो मानव के लिए (अल्लाह की वंदना का केंद्र) बनाया गया, वह वही है, जो मक्का में है, जो शुभ तथा संसार वासियों के लिए मार्गदर्शन है।');
INSERT INTO "al_umari" VALUES(3,97,'3:97','उसमें खुली निशानियाँ हैं, (जिनमें) मक़ामे[ इब्राहीम है तथा जो कोई उस (की सीमा) में प्रवेश कर गया, तो वह शांत (सुरक्षित) हो गया। तथा अल्लाह के लिए लोगों पर इस घर का ह़ज अनिवार्य है, जो उसतक राह पा सकता हो तथा जो कुफ़्र करेगा, तो अल्लाह संसार वासियों से निस्पृह है। ');
INSERT INTO "al_umari" VALUES(3,98,'3:98','(हे नबी!) आप कह दें कि हे अह्ले किताब! ये क्या है कि तुम अल्लाह की आयतों के साथ कुफ़्र कर रहे हो, जबकि अल्लाह तुम्हारे कर्मों का साक्षी है?');
INSERT INTO "al_umari" VALUES(3,99,'3:99','हे अह्ले किताब! किस लिए लोगों को, जो ईमान लाना चाहें, अल्लाह की राह से रोक रहे हो, उसे उलझाना चाहते हो, जबकि तुम साक्षी[ हो और अल्लाह तुम्हारे कर्मों से असूचित नहीं है? ');
INSERT INTO "al_umari" VALUES(3,100,'3:100','हे ईमान वालो! यदि तुम अह्ले किताब के किसी गिरोह की बात मानोगे, तो वह तुम्हारे ईमान के पश्चात् फिर तुम्हें काफ़िर बना देंगे।');
INSERT INTO "al_umari" VALUES(3,101,'3:101','तथा तुम कुफ़्र कैसे करोगे, जबकि तुम्हारे सामने अललाह की आयतें पढ़ी जा रही हैं और तुममें उसके रसूल[ मौजूद हैं? और जिसने अल्लाह को थाम लिया, तो उसे सुपथ दिखा दिया गया।');
INSERT INTO "al_umari" VALUES(3,102,'3:102','हे ईमान वालो! अल्लाह से ऐसे डरो, जो वास्तव में, उससे डरना हो तथा तुम्हारी मौत इस्लाम पर रहते हुए ही आनी चीहि।');
INSERT INTO "al_umari" VALUES(3,103,'3:103','तथा अल्लाह की रस्सी[ को सब मिलकर दृढ़ता से पकड़ लो और विभेद में न पड़ो तथा अपने ऊपर अललाह के पुरस्कार को याद करो, जब तुम एक-दूसरे के शत्रु थे, तो तुम्हारे दिलों को जोड़ दिया और तुम उसके पुरस्कार के कारण भाई-भाई हो गए तथा तुम अग्नि के गड़हे के किनारे पर थे, तो तुम्हें उससे निकाल दिया। इसी प्रकार अल्लाह तुम्हारे लिए अपनी आयतें उजागर करता है, ताकि तुम मार्गदर्शन पा जाओ। ');
INSERT INTO "al_umari" VALUES(3,104,'3:104','तथा तुममें एक समुदाय ऐसा अवश्य होना चाहिए, जो भली बातों[ की ओर बुलाये, भलाई का आदेश देता रहे, बुराई से रोकता रहे और वही सफल होंगे।');
INSERT INTO "al_umari" VALUES(3,105,'3:105','तथा उनके[ समान न हो जाओ, जो खुली निशानियाँ आने के पश्चात्, विभेद तथा आपसी विरोध में पड़ गये और उन्हीं के लिए घोर यातना है। ');
INSERT INTO "al_umari" VALUES(3,106,'3:106','जिस दिन बहुत-से मुख उजले तथा बहुत-से मुख काले होंगे। फिर जिनके मुख काले होंगे (उनसे कहा जायेगाः) क्या तुमने अपने ईमान के पश्चात् कुफ़्र कर लिया था? तो अपने कुफ़्र करने का दण्ड चखो।');
INSERT INTO "al_umari" VALUES(3,107,'3:107','तथा जिनके मुख उजले होंगे, वे अल्लाह की दया (स्वर्ग) में रहेंगे। व उसमें सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(3,108,'3:108','ये अल्लाह की आयतें हैं, जो हम आपको ह़क़ के साथ सुना रहे हैं तथा अल्लाह संसार वासियों पर अत्याचार नहीं करना चाहता।');
INSERT INTO "al_umari" VALUES(3,109,'3:109','तथा अल्लाह ही का है, जो आकाशों में और जो धरती में है तथा अल्लाह ही की ओर सब विषय फेरे जायेंगे।');
INSERT INTO "al_umari" VALUES(3,110,'3:110','तुम, सबसे अच्छी उम्मत हो, जिसे सब लोगों के लिए उत्पन्न किया गया है कि तुम भलाई का आदेश देते हो तथा बुराई से रोकते हो और अल्लाह पर ईमान (विश्वास) रखते[ हो। यदि अह्ले किताब ईमान लाते, तो उनके लिए अच्छा होता। उनमें कुछ ईमान वाले हैं और अधिक्तर अवज्ञाकारी हैं। ');
INSERT INTO "al_umari" VALUES(3,111,'3:111','वे तुम्हें सताने के सिवा कोई हानि पहुँचा नहीं सकेंगे और यदि तुमसे युध्द करेंगे, तो वे तुम्हें पीठ दिखा देंगे। फिर सहायता नहीं दिए जायेंगे।');
INSERT INTO "al_umari" VALUES(3,112,'3:112','इन (यहूदियों) पर जहाँ भी रहें, अपमान थोंप दिया गया है, (ये और बात है कि) अल्लाह की शरण[ अथवा लोगों की शरण में हों , ये अल्लाह के प्रकोप के अधिकारी हो गये तथा इनपर दरिद्रता थोंप दी गयी। ये इस कारण हुआ कि ये अल्लाह की आयतों के साथ कुफ़्र कर रहे थे और नबियों का अवैध वध कर रहे थे। ये इस कारण कि इन्होंने अवज्ञा की और (धर्म की) सीमा का उल्लंघन कर रहे थे।');
INSERT INTO "al_umari" VALUES(3,113,'3:113','वे सभी समान नहीं हैं; अह्ले किताब में एक( सत्य पर) स्थित उम्मत[ भी है, जो अल्लाह की आयतें रातों में पढ़ते हैं तथा सज्दा करते रहते हैं। ');
INSERT INTO "al_umari" VALUES(3,114,'3:114','अल्लाह तथा अंतिम दिन (प्रलय) पर ईमान रखते हैं, भलाई का आदेश देते हैं, बुराई से रोकते हैं, भलाईयों में अग्रसर रहते हैं और यही सदाचारियों में हैं।');
INSERT INTO "al_umari" VALUES(3,115,'3:115','वे, जो भी भलाई करेंगे, उसकी उपेक्षा (अनादर) नहीं की जायेगी और अल्लाह आज्ञाकारियों को भली-भाँति जानता है।');
INSERT INTO "al_umari" VALUES(3,116,'3:116','(परन्तु) जो काफ़िर[ हो गये, उनके धन और उनकी संतान अल्लाह (की यातना) से उन्हें तनिक भी बचा नहीं सकेगी तथा वही नारकी हैं, वही उसमें सदावासी होंगे। ');
INSERT INTO "al_umari" VALUES(3,117,'3:117','जो दान, वे इस सांसारिक जीवन में करते हैं, वो उस वायु के समान है, जिसमें पाला हो, जो किसी क़ौम की खेती को लग जाये, जिन्होंने अपने ऊपर अत्याचार[ किया हो और उसका नाश कर दे तथा अल्लाह ने उनपर अत्याचार नहीं किया, परन्तु वे स्वयं अपने ऊपर अत्याचार कर रहे थे। ');
INSERT INTO "al_umari" VALUES(3,118,'3:118','हे ईमान वालो! अपनों के सिवा किसी को अपना भेदी न बनाओ[, वे तुम्हारा बिगाड़ने में तनिक भी नहीं चूकेंगे, उनहें वही बात भाती है, जिससे तुम्हें दुःख हो। उनके मुखों से शत्रुता खुल चुकी है तथा जो उनके दिल छुपा रहे हैं, वो इससे बढ़कर है। हमने तुम्हारे लिए आयतों का वर्णन कर दिया है, यदि तुम समझो। ');
INSERT INTO "al_umari" VALUES(3,119,'3:119','सावधान! तुमही वो हो कि उनसे प्रेम करते हो, जबकि वे तुमसे प्रेम नहीं करते और तुम सभी पुस्तकों पर ईमान रखते हो, जबकि वे जब तुमसे मिलते हैं, तो कहते हैं कि हम ईमान लाये और जब अकेले होते हैं, तो क्रोध से तुमपर उँगलियों की पोरें चबाते हैं। कह दो कि अपने क्रोध से मर जाओ। निःसंदेह अल्लाह सीनों की बातों को जानता है।');
INSERT INTO "al_umari" VALUES(3,120,'3:120','यदि तुम्हारा कुछ भला हो, तो उन्हें बुरा लगता है और यदि तुम्हारा कुछ बुरा हो जाये, तो वे उससे प्रसन्न हो जाते हैं। अलबत्ता, यदि तुम सहन करते रहे और आज्ञाकारी रहे, तो उनका छल तुम्हें कोई हानि नहीं पहुँचायेगा। उनके सभी कर्म अल्लाह के घेरे में हैं।');
INSERT INTO "al_umari" VALUES(3,121,'3:121','तथा (हे नबी! वो समय याद करें) जब आप प्रातः अपने घर से निकले, ईमान वालों को युध्द[ के स्थानों पर नियुक्त कर रहे थे तथा अल्लाह सब कुछ सुनने-जानने वाला है। ');
INSERT INTO "al_umari" VALUES(3,122,'3:122','तथा (याद करें) जब आपमें से दो गिरोहों[ ने कायरता दिखाने का विचार किया और अल्लाह उनका रक्षक था तथा ईमान वालों को अल्लाह ही पर भरोसा करना चाहिए। ');
INSERT INTO "al_umari" VALUES(3,123,'3:123','अल्लाह बद्र में तुम्हारी सहायता कर चुका है, जब तुम निर्बल थे। अतः अल्लाह से डरते रहो, ताकि उसके कृतज्ञ रहो।');
INSERT INTO "al_umari" VALUES(3,124,'3:124','(हे नबी! वो समय भी याद करें) जब आप ईमान वालों से कह रहे थेः क्या तुम्हारे लिए ये बस नहीं है कि अल्लाह तुम्हें (आकाश से) उतारे हुए, तीन हज़ार फ़रिश्तों द्वारा समर्थन दे?');
INSERT INTO "al_umari" VALUES(3,125,'3:125','क्यों[ नहीं? यदि तुम सहन करोगे, आज्ञाकारी रहोगे और वे (शत्रु) तुम्हारे पास अपनी उत्तेजना के साथ आ गये, तो तुम्हारा पालनहार तुम्हें (तीन नहीं,) पाँच हज़ार चिन्ह लगे फ़रिश्तों द्वारा समर्थन देगा।');
INSERT INTO "al_umari" VALUES(3,126,'3:126','और अल्लाह ने इसे तुम्हारे लिए केवल शुभ सूचना बनाया है और ताकि तुम्हारे दिलों को संतोष हो जाये और समर्थन तो केवल अल्लाह ही के पास से है, जो प्रभुत्वशाली तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(3,127,'3:127','ताकि[ वह काफ़िरों का एक भाग काट दे अथवा उन्हें अपमानित कर दे। फिर वे असफल वापस हो जायेँ। ');
INSERT INTO "al_umari" VALUES(3,128,'3:128','हे नबी! इस[ विषय में आपको कोई अधिकार नहीं, अल्लाह चाहे तो उनकी क्षमा याचना स्वीकार करे या दण्ड दे, क्योंकि वे अत्याचारी हैं।');
INSERT INTO "al_umari" VALUES(3,129,'3:129','अल्लाह ही का है, जो कुछ आकाशों तथा धरती में है, वह जिसे चाहे, क्षमा करे और जिसे चाहे, दण्ड दे तथा अल्लाह अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(3,130,'3:130','हे ईमान वालो! कई-कई गुणा करके ब्याज[ न खाओ तथा अल्लाह से डरो, ताकि सफल हो जाओ। ');
INSERT INTO "al_umari" VALUES(3,131,'3:131','तथा उस अग्नि से बचो, जो काफ़िरों के लिए तैयार की गयी है।');
INSERT INTO "al_umari" VALUES(3,132,'3:132','तथा अल्लाह और रसूल के आज्ञाकारी रहो, ताकि तुमपर दया की जाये।');
INSERT INTO "al_umari" VALUES(3,133,'3:133','और अपने पालनहार की क्षमा और उस स्वर्ग की ओर अग्रसर हो जाओ, जिसकी चौड़ाई आकाशों तथा धरती के बराबर है, वह आज्ञाकारियों के लिए तैयार की गयी है।');
INSERT INTO "al_umari" VALUES(3,134,'3:134','जो सुविधा तथा असुविधा की दशा में दान करते रहते हैं, क्रोध पी जाते और लोगों के दोष क्षमा कर दिया करते हैं और अल्लाह सदाचारियों से प्रेम करता है।');
INSERT INTO "al_umari" VALUES(3,135,'3:135','और जब कभी वे कोई बड़ा पाप कर जायेँ अथवा अपने ऊपर अत्याचार कर लें, तो अल्लाह को याद करते हैं, फिर अपने पापों के लिए क्षमा माँगते हैं -तथा अल्लाह के सिवा कौन है, जो पापों को क्षमा करे?- और अपने किये पर जान-बूझ कर अड़े नहीं रहते।');
INSERT INTO "al_umari" VALUES(3,136,'3:136','उन्हीं का प्रतिफल (बदला) उनके पालनहार की क्षमा तथा ऐसे स्वर्ग हैं, जिनमें नहरें परवाहित हैं, जिनमें वे सदावासी होंगे। तो क्या ही अच्छा है सत्कर्मियों का ये प्रतिफल!');
INSERT INTO "al_umari" VALUES(3,137,'3:137','तुमसे पहले भी इसी प्रकार हो चुका[ है। तुम धरती में फिरो और देखो कि झुठलाने वालों का परिणाम कैसा रहा? ');
INSERT INTO "al_umari" VALUES(3,138,'3:138','ये (क़ुर्आन) लोगों के लिए एक वर्णन, मार्गदर्शन और एक शिक्षा है, (अल्लाह से) डरने वालों के लिए।');
INSERT INTO "al_umari" VALUES(3,139,'3:139','(इसपराजय से) तुम निर्बल तथा उदासीन न बनो और तुमही सर्वोच्च रहोगे, यदि तुम ईमान वाले हो।');
INSERT INTO "al_umari" VALUES(3,140,'3:140','यदि तुम्हें कोई घाव लगा है, तो क़ौम (शत्रु)[ को भी इसी के समान घाव लग चुका है तथा उन दिनों को हम लोगों के बीच फेरते रहते हैं। ताकि अल्लाह उन लोगों को जान ले, जो ईमान लाये और तुममें से साक्षी बनाये और अल्लाह अत्याचारियों से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(3,141,'3:141','तथा ताकि अल्लाह उन्हें शुध्द कर दे, जो ईमान लाये हैं और काफ़िरों को नाश कर दे।');
INSERT INTO "al_umari" VALUES(3,142,'3:142','क्या तुमने समझ रखा है कि स्वर्ग में प्रवेश कर जाओगे? जबकि अल्लाह ने (परीक्षा करके) उन्हें नहीं जाना है, जिन्होंने तुममें से जिहाद किया है और न सहनशीलों को जाना है?');
INSERT INTO "al_umari" VALUES(3,143,'3:143','तथा तुम मौत की कामना कर[ रहे थे, इससे पूर्व कि उसका सामना करो, तो अब तुमने उसे आँखों से देख लिया है और देख रहे हो। ');
INSERT INTO "al_umari" VALUES(3,144,'3:144','मुह़म्मद केवल एक रसूल हैं, इससे पहले बहुत-से रसूल हो चुके हैं, तो क्या यदि वो मर गये अथवा मार दिये गये, तो तुम अपनी एड़ियों के बल[ फिर जाओगे? तथा जो अपनी एड़ियों के बल फिर जायेगा, वो अल्लाह को कुछ हानि नहीं पहुँचा सकेगा और अल्लाह शीघ्र ही कृतज्ञों को प्रतिफल प्रदान करेगा। ');
INSERT INTO "al_umari" VALUES(3,145,'3:145','कोई प्राणी ऐसा नहीं कि अल्लाह की अनुमति के बिना मर जाये, उसका अंकित निर्धारित समय है। जो सांसारिक प्रतिफल चाहेगा, हम उसे उसमें से कुछ देंगे तथा जो परलोक का प्रतिफल चाहेगा, हम उसे उसमें से देंगे और हम कृतज्ञों को शीघ्र ही प्रतिफल देंगे।');
INSERT INTO "al_umari" VALUES(3,146,'3:146','कितने ही नबी थे, जिनके साथ होकर बहुत-से अल्लाह वालों ने युध्द किया, तो वे अल्लाह की राह में आयी आपदा पर न आलसी हुए, न निर्बल बने और न (शत्रु से) दबे तथा अल्लाह धैर्यवानों से प्रेम करता है।');
INSERT INTO "al_umari" VALUES(3,147,'3:147','तथा उनका कथन बस यही था कि उन्होंने कहाः हे हमारे पालनहार! हमारे लिए हमारे पापों को क्षमा कर दे तथा हमारे विषय में हमारी अति को। और हमारे पैरों को दृढ़ कर दे और काफ़िर जाति के विरुध्द हमारी सहायता कर।');
INSERT INTO "al_umari" VALUES(3,148,'3:148','तो अल्लाह ने उन्हें सांसारिक प्रतिफल तथा आख़िरत (परलोक) का अच्छा प्रतिफल प्रदान कर दिया तथा अल्लाह सुकर्मियों से प्रेम करता है।');
INSERT INTO "al_umari" VALUES(3,149,'3:149','हे ईमान वालो! यदि तुम काफ़िरों की बात मानोगे, तो वे तुम्हें तुम्हारी एड़ियों के बल फेर देंगे और तुम फिर से क्षति में पड़ जाओगे।');
INSERT INTO "al_umari" VALUES(3,150,'3:150','बल्कि अल्लाह तुम्हारा रक्षक है तथा वह सबसे अच्छा सहायक है।');
INSERT INTO "al_umari" VALUES(3,151,'3:151','शीघ्र ही हम काफ़िरों के दिलों में तुम्हारा भय डाल देंगे, इस कारण कि उन्होंने अल्लाह का साझी उसे बना लिया है, जिसका कोई तर्क (प्रमाण) अल्लाह ने नहीं उतारा है और इनका आवास नरक है और वह क्या ही बुरा आवास है?');
INSERT INTO "al_umari" VALUES(3,152,'3:152','तथा अल्लाह ने तुमसे अपना वचन सच कर दिखाया है, जब तुम उसकी अनुमति से, उनहें काट[ रहे थे, यहाँ तक कि जब तुमने कायरता दिखायी तथा (रसूल के) आदेश में विभेद कर लिया और अवज्ञा की, इसके पश्चात् कि तुम्हें वह (विजय) दिखा दी, जिसे तुम चाहते थे। तुममें से कुछ संसार चाहते हैं तथा कुछ परलोक चाहते हैं। फिर तुम्हें उनसे फेर दिया, ताकि तुम्हारी परीक्षा ले और तुम्हें क्षमा कर दिया तथा अल्लाह ईमान वालों के लिए दानशील है।');
INSERT INTO "al_umari" VALUES(3,153,'3:153','(और याद करो) जब तुम चढ़े (भागे) जा रहे थे और किसी की ओर मुड़कर नहीं देख रहे थे और रसूल तुम्हें तुम्हारे पीछे से पुकार[ रहे थे, तो (अल्लाह ने) तुम्हें शोक के बदले शोक दे दिया, ताकि जो तुमसे खो गया और जो दुख तुम्हें पहुँचा, उसपर उदासीन न हो तथा अल्लाह उससे सूचित है, जो तुम कर रहे हो। ');
INSERT INTO "al_umari" VALUES(3,154,'3:154','फिर तुमपर शोक के पश्चात् शान्ति (ऊँघ) उतार दी, जो तुम्हारे एक गिरोह[ को आने लगी और एक गिरोह को अपनी पड़ी हुई थी। वे अल्लाह के बारे में असत्य जाहिलिय्यत की सोच सोच रहे थे। वे कह रहे थे कि क्या हमारा भी कुछ अधिकार है? (हे नबी!) कह दें कि सब अधिकार अल्लाह को है। वे अपने मनों में जो छुपा रहे थे, आपको नहीं बता रहे थे। वे कह रहे थे कि यदि हमारा कुछ भी अधिकार होता, तो यहाँ मारे नहीं जाते। आप कह दें: यदि तुम अपने घरों में रहते, तबभी जिनके (भाग्य में) मारा जाना लिखा है, वे अपने निहत होने के स्थानों की ओर निकल आते और ताकि अल्लाह जो तुम्हारे दिलों में है, उसकी परीक्षा ले तथा जो तुम्हारे दिलों में है, उसे शुध्द कर दे और अल्लाह दिलों के भेदों से अवगत है।');
INSERT INTO "al_umari" VALUES(3,155,'3:155','वस्तुतः तुममें से जिन्होंने दो गिरोहों के सम्मुख होने के दिन मुँह फेर लिया, शैतान ने उनहें उनके कुछ कुकर्मों के कारण फिसला दिया तथा अल्लाह ने उन्हें क्षमा कर दिया है। वास्तव में, अल्लाह अति क्षमाशील सहनशील है।');
INSERT INTO "al_umari" VALUES(3,156,'3:156','हे ईमान वालो! उनके समान न हो जाओ, जो काफ़िर हो गये तथा अपने भाईयों से -जब यात्रा में हों अथवा युध्द में- कहा कि यदि वे हमारे पास होते, तो न मरते और न मारे जाते, ताकि अल्लाह उनके दिलों में इसे संताप बना दे और अल्लाह ही जीवित करता तथा मौत देता है और अल्लाह जो तुम कर रहे हो, उसे देख रहा है।');
INSERT INTO "al_umari" VALUES(3,157,'3:157','यदि तुम अल्लाह की राह में मार दिये जाओ अथवा मर जाओ, तो अल्लाह की क्षमा उससे उत्तम है, जो, लोग एकत्र कर रहे हैं।');
INSERT INTO "al_umari" VALUES(3,158,'3:158','तथा यदि तुम मर गये अथवा मार दिये गये, तो अल्लाह ही के पास एकत्र किये जाओगे।');
INSERT INTO "al_umari" VALUES(3,159,'3:159','अल्लाह की दया के कारण ही आप उनके लिए[ कोमल (सुशील) हो गये और यदि आप अक्खड़ तथा कड़े दिल के होते, तो वे आपके पास से बिखर जाते। अतः, उन्हें क्षमा कर दो और उनके लिए क्षमा की प्रार्थना करो तथा उनसे भी मामले में प्रामर्श करो, फिर जब कोई दृढ़ संकल्प ले लो, तो अल्लाह पर भरोसा करो। निःसंदेह, अल्लाह भरोसा रखने वालों से प्रेम करता है। ');
INSERT INTO "al_umari" VALUES(3,160,'3:160','यदि अल्लाह तुम्हारी सहायता करे, तो तुमपर कोई प्रभुत्व नहीं पा सकता तथा यदि तुम्हारी सहायता न करे, तो फिर कौन है, जो उसके पश्चात तुम्हारी सहायता कर सके? अतः ईमान वालों को अल्लाह ही पर भरोसा करना चाहिये।');
INSERT INTO "al_umari" VALUES(3,161,'3:161','किसी नबी के लिए योग्य नहीं कि अपभोग[ करे और जो अपभोग करेगा, प्रलय के दिन उसे लायेगा। फिर प्रत्येक प्राणी को, उसकी कमाई का भरपूर प्रतिकार (बदला) दिया जायेगा तथा उनपर अत्याचार नहीं किया जायेगा। ');
INSERT INTO "al_umari" VALUES(3,162,'3:162','तो क्या जिसने अल्लाह की प्रसन्नता का अनुसरण किया हो, उसके समान हो जायेगा, जो अल्लाह का क्रोध[ लेकर फिरा और उसका आवास नरक है? ');
INSERT INTO "al_umari" VALUES(3,163,'3:163','अल्लाह के पास उनकी श्रेणियाँ हैं तथा अल्लाह उसे देख[ रहा है, जो वे कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(3,164,'3:164','अल्लाह ने ईमान वालों पर उपकार किया है कि उनमें उन्हीं में से एक रसूल भेजा, जो उनके सामने (अल्लाह) की आयतें पढ़ता है, उन्हें शुध्द करता है तथा उन्हें पुस्तक (क़ुर्आन) और ह़िक्मत (सुन्नत) की शिक्षा देता है, यद्यपि वे इससे पहले खुले कुपथ में थे।');
INSERT INTO "al_umari" VALUES(3,165,'3:165','तथा जब तुम्हें एक दुःख पहुँचा,[ जबकि इसके दुगना (दुःख) तुमने (उन्हें) पहुँचाया है , तो तुमने कह दिया कि ये कहाँ से आ गया? (हे नबी!) कह दोः ये तुम्हारे पास से आया है। वास्तव में, अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(3,166,'3:166','तथा जो भी आपदा, दो गिरोहों के सम्मुख होने के दिन, तुमपर आई, तो वो अल्लाह की अनुमति से (आई)। ताकि वह ईमान वालों को जान ले।');
INSERT INTO "al_umari" VALUES(3,167,'3:167','और ताकि उन्हें जान ले, जो मुनाफ़िक़ हैं। (जब) उनसे कहा गया कि आओ, अल्लाह की राह में युध्द करो अथवा रक्षा करो, तो उन्होंने कहा कि यदि हम युध्द होना जानते, तो अवश्य तुम्हारा साथ देते। वे उस दिन ईमान से अधिक कुफ़्र के समीप थे, व अपने मुखों से ऐसी बात बोल रहे थे, जो उनके दिलों में नहीं थी तथा अल्लाह, जिसे वे छुपा रहे थे, अधिक जानता था।');
INSERT INTO "al_umari" VALUES(3,168,'3:168','इन्होंने ही अपने भाईयों से कहा और (स्वयं घरों में) आसीन रह गयेः यदि वे हमारी बात मानते, तो मारे नहीं जाते! (हे नबी!) कह दोः फिर तो मौत से[ अपनी रक्षा कर लो, यदि तुम सच्चे हो। ');
INSERT INTO "al_umari" VALUES(3,169,'3:169','जो अल्लाह की राह में मार दिये गये, तो तुम उन्हें मरा हुआ न समझो, बल्कि वे जीवित हैं[, अपने पालनहार के पास जीविका दिये जा रहे हैं। ');
INSERT INTO "al_umari" VALUES(3,170,'3:170','तथा उससे प्रसन्न हैं, जो अल्लाह ने उन्हें अपनी दया से प्रदान किया है और उनके लिए प्रसन्न (हर्षित) हो रहे हैं, जो उनसे मिले नहीं, उनके पीछे[ रह गये हैं कि उन्हें कोई डर नहीं होगा और न वे उदासीन होंगे। ');
INSERT INTO "al_umari" VALUES(3,171,'3:171','वे अल्लाह के पुरस्कार और प्रदान के कारण प्रसन्न हो रहे हैं तथा इसपर कि अल्लाह ईमान वालों का प्रतिफल व्यर्थ नहीं करता।');
INSERT INTO "al_umari" VALUES(3,172,'3:172','जिन्होंने अल्लाह और रसूल की पुकार स्वीकार[ की, इसके पश्चात् कि उन्हें आघात पहुँचा, उनमें से उनके लिए जिन्होंने सुकर्म किया तथा (अल्लाह से) डरे, महा प्रतिफल है। ');
INSERT INTO "al_umari" VALUES(3,173,'3:173','ये वे लोग हैं, जिनसे लोगों ने कहा कि तुम्हारे लिए लोगों (शत्रु) ने (वापिस आने का) संकल्प[ लिया है। अतः उनसे डरो, तो इस (सूचना) ने उनके ईमान को और अधिक कर दिया और उन्होंने कहाः हमें अल्लाह बस है और वह अच्छा काम बनाने वाला है। ');
INSERT INTO "al_umari" VALUES(3,174,'3:174','तथा अल्लाह के अनुग्रह एवं दया के साथ[ वापिस हुए। उन्हें कोई दुःख नहीं पहुँचा तथा अल्लाह की प्रसन्नता पर चले और अल्लाह बड़ा दयाशील है। ');
INSERT INTO "al_umari" VALUES(3,175,'3:175','वह शैतान है, जो तुम्हें अपने सहयोगियों से डरा रहा है, तो उनसे[ न डरो तथा मुझी से डरो यदि तुम ईमान वाले हो। ');
INSERT INTO "al_umari" VALUES(3,176,'3:176','(हे नबी!) आपको वे काफ़िर उदासीन न करें, जो कुफ़्र में अग्रसर हैं, वे अल्लाह को कोई हानि नहीं पहुँचा सकेंगे। अल्लाह चाहता है कि आख़िरत (परलोक) में उनका कोई भाग न बनाये तथा उन्हीं के लिए घोर यातना है।');
INSERT INTO "al_umari" VALUES(3,177,'3:177','वस्तुतः जिन्होंने ईमान के बदले कुफ़्र खरीद लिया, वे अल्लाह को कोई हानि नहीं पहुँचा सकेंगे तथा उन्हीं के दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(3,178,'3:178','जो काफ़िर हो गये, वे कदापि ये न समझें कि हमारा उन्हें अवसर[ देना, उनके लिए अच्छा है, वास्तव में, हम उन्हें इसलिए अवसर दे रहे हैं कि उनके पाप अधिक हो जायेँ तथा उन्हीं के लिए अपमानकारी यातना है।');
INSERT INTO "al_umari" VALUES(3,179,'3:179','अल्लाह ऐसा नहीं है कि ईमान वालों को उसी (दशा) पर छोड़ दे, जिसपर तुम हो, जब तक बुरे को अच्छे से अलग न कर दे और अल्लाह ऐसा (भी) नहीं है कि तुम्हें ग़ैब (परोक्ष) से[ सूचित कर दे, परन्तु अल्लाह अपने रसूलों में से (परोक्ष पर अवगत करने के लिए) जिसे चाहे, चुन लेता है तथा यदि तुम ईमान लाओ और अल्लाह से डरते रहो, तो तुम्हारे लिए बड़ा प्रतिफल है। ');
INSERT INTO "al_umari" VALUES(3,180,'3:180','वे लोग कदापि ये न समझें, जो उसमें कृपण (कंजसी) करते हैं, जो अल्लाह ने उन्हों अपनी दया से प्रदान किया[ है कि वह उनके लिए अच्छा है, बल्कि वह उनके लिए बुरा है, जिसमें उन्होंने कृपण किया है। प्रलय के दिन उसे उनके गले का हार बना दिया जायेगा और आकाशों तथा धरती की मीरास (उत्तराधिकार) अल्लाह के लिए है तथा अल्लाह जो कुछ तुम करते हो, उससे सूचित है।');
INSERT INTO "al_umari" VALUES(3,181,'3:181','अल्लाह ने उनकी बात सुन ली है, जिन्होंने कहा कि अल्लाह निर्धन और हम धनी[ हैं, उन्होंने जो कुछ कहा है, हम उसे लिख लेंगे और उनके नबियों की अवैध हत्या करने को भी, तथा कहेंगे कि दहन की यातना चखो। ');
INSERT INTO "al_umari" VALUES(3,182,'3:182','ये तुम्हारे करतूतों का दुष्परिणाम है तथा वास्तव में, अल्लाह बंदों के लिए तनिक भी अत्याचारी नहीं है।');
INSERT INTO "al_umari" VALUES(3,183,'3:183','जिन्होंने कहाः अल्लाह ने हमसे वचन लिया है कि किसी रसूल का विश्वास न करें, जब तक हमारे समक्ष ऐसी बली न दें, जिसे अग्नि खा[ जाये। (हे नबी!) आप कह दें कि मुझसे पूर्व बहुत-से रसूल खुली निशानियाँ और वो चीज़ लाये, जो तुमने कही, तो तुमने उनकी हत्या क्यों कर दी, यदि तुम सच्चे हो तो? ');
INSERT INTO "al_umari" VALUES(3,184,'3:184','फिर यदि इन्होंने[ आपको झुठला दिया, तो आपसे पहले भी बहुत-से रसूल झुठलाये गये हैं, जो खुली निशानियाँ तथा (आकाशीय) ग्रंथ और प्रकाशक पुस्तकें लाये ।');
INSERT INTO "al_umari" VALUES(3,185,'3:185','प्रत्येक प्राणी को मौत का स्वाद चखना है और तुम्हें, तुम्हारे कर्मों का प्रलय के दिन भरपूर प्रतिफल दिया जायेगा, तो (उस दिन) जो व्यक्ति नरक से बचा लिया गया तथा स्वर्ग में प्रवेश पा गया[, तो वह सफल हो गया तथा सांसारिक जीवन धोखे की पूंजी के सिवा कुछ नहीं है। ');
INSERT INTO "al_umari" VALUES(3,186,'3:186','(हे ईमान वालो!) तुम्हारे धनों तथा प्राणों में तुम्हारी परीक्षा अवश्य ली जायेगी और तुम उनसे अवश्य बहुत सी दुःखद बातें सुनोगे, जो तुमसे पूर्व पुस्तक दिये गये तथा उनसे जो मिश्रणवादी[ हैं। अब यदि तुमने सहन किया और (अल्लाह से) डरते रहे, तो ये बड़ी साहस की बात होगी। ');
INSERT INTO "al_umari" VALUES(3,187,'3:187','तथा (हे नबी! याद करो) जब अल्लाह ने उनसे दृढ़ वचन लिया था, जो पुस्तक[ दिये गये कि तुम अवश्य इसे लोगों के लिए उजागर करते रहोगे और इसे छुपाओगे नहीं। तो उन्होंने इस वचन को अपने पीछे डाल दिया (भंग कर दिया) और उसके बदले तनिक मूल्य खरीद लिया। तो वे कितनी बुरी चीज़ खरीद रहे हैं?');
INSERT INTO "al_umari" VALUES(3,188,'3:188','(हे नबी!) जो[ अपने करतूतों पर प्रसन्न हो रहे हैं और चाहते हैं कि उन कर्मों के लिए सराहे जायें, जो उन्होंने नहीं किये। आप उन्हें कदापि न समझें कि यातना से बचे रहेंगे तथा (वास्तविकता ये कि) उन्हीं के लिए दुःखदायी यातना है। ');
INSERT INTO "al_umari" VALUES(3,189,'3:189','तथा आकाशों और धरती का राज्य अल्लाह ही का है तथा अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(3,190,'3:190','वस्तुतः आकाशों तथा धरती की रचना और रात्रि तथा दिवस के एक के पश्चात् एक आते-जाते रहने में, मतिमानों के लिए बहुत सी निशानियाँ (लक्षण)[ हैं। ');
INSERT INTO "al_umari" VALUES(3,191,'3:191','जो खड़े, बैठे तथा सोए (प्रत्येक स्थिति में,) अल्लाह की याद करते तथ आकाशों और धरती की रचना में विचार करते रहते हैं। (कहते हैः) हे हमारे पालनहार! तूने ये सब[ व्यर्थ नहीं रचा है। हमें अग्नि के दण्ड से बचा ले। ');
INSERT INTO "al_umari" VALUES(3,192,'3:192','हे हमारे पालनहार! तूने जिसे नरक में झोंक दिया, उसे अपमानित कर दिया और अत्याचारियों का कोई सहायक न होगा।');
INSERT INTO "al_umari" VALUES(3,193,'3:193','हे हमारे पालनहार! हमने एक[ पुकारने वाले को ईमान के लिए पुकारते हुए सुना कि अपने पालनहार पर ईमान लाओ, तो हम ईमान ले आये। हे हमारे पालनहार! हमारे पाप क्षमा कर दे तथा हमारी बुराईयों को अनदेखी कर दे तथा हमारी मौत पुनीतों (सदाचारियों) के साथ हो। ');
INSERT INTO "al_umari" VALUES(3,194,'3:194','हे हमारे पालनहार! हमें, तूने रसूलों द्वारा जो वचन दिया है, हमें वो प्रदान कर तथा प्रलय के दिन हमें अपमानित न कर, वास्तव में तू वचन विरोधी नहीं है।');
INSERT INTO "al_umari" VALUES(3,195,'3:195','तो उनके पालनहार ने उनकी (प्रार्थना) सुन ली, (तथा कहा किः) निःसंदेह मैं किसी कार्यकर्ता के कार्य को व्यर्थ नहीं करता[, नर हो अथवा नारी। तो जिन्होंने हिजरत (प्रस्थान) की, अपने घरों से निकाले गये, मेरी राह में सताये गये और युध्द किया तथा मारे गये, तो हम अवश्य उनके दोषों को क्षमा कर देंगे तथा उन्हें ऐसे स्वर्गों में प्रवेश देंगे, जिनमें नहरें बह रही हैं। ये अल्लाह के पास से उनका प्रतिफल होगा और अल्लाह ही के पास अच्छा प्रतिफल है। ');
INSERT INTO "al_umari" VALUES(3,196,'3:196','(हे नबी!) नगरों में काफ़िरों का (सुविधा के साथ) फिरना आपको धोखे में न डाल दे।');
INSERT INTO "al_umari" VALUES(3,197,'3:197','ये तनिक लाभ[ है, फिर उनका स्थान नरक है और वह क्या ही बुरा आवास है! ');
INSERT INTO "al_umari" VALUES(3,198,'3:198','परन्तु जो अपने पालनहार से डरे, तो उनके लिए ऐसे स्वर्ग हैं, जिनमें नहरें प्रवाहित हैं। उनमें वे सदावासी होंगे। ये अल्लाह के पास से अतिथि सत्कार होगा तथा जो अल्लाह के पास है, पुनीतों के लिए उत्तम है।');
INSERT INTO "al_umari" VALUES(3,199,'3:199','और निःसंदेह अह्ले किताब (अर्थात यहूद और ईसाई) में से कुछ ऐसे भी हैं, जो अल्लाह पर ईमान रखते हैं और तुम्हारी ओर जो उतारा गया है, उसपर भी, अल्लाह से डरे रहते हैं और उसकी आयतों को थोड़ी थोड़ी क़ीमतों पर बेचते भी नहीं[। उनका बदला उनके रब के पास है। निःसंदेह अल्लाह जल्दी ही हिसाब लेने वाला है। ');
INSERT INTO "al_umari" VALUES(3,200,'3:200','हे ईमान वालो! तुम धैर्य रखो[, एक-दूसरे को थामे रखो, जिहाद के लिए तैयार रहो और अल्लाह से डरते रहो, ताकि तुम अपने उद्देश्य को पहुँचो। ');
INSERT INTO "al_umari" VALUES(4,1,'4:1','हे मनुष्यों! अपने[ उस पालनहार से डरो, जिसने तुम्हें एक जीव (आदम) से उत्पन्न किया तथा उसीसे उसकी पत्नी (हव्वा) को उत्पन्न किया और उन दोनों से बहुत-से नर-नारी फैला दिये। उस अल्लाह से डरो, जिसके द्वारा तुम एक-दूसरे से (अधिकार) मांगते हो तथा रक्त संबंधों को तोड़ने से डरो। निःसंदेह अल्लाह तुम्हारा निरीक्षक है। ');
INSERT INTO "al_umari" VALUES(4,2,'4:2','तथा (हे संरक्षको!) अनाथों को उनके धन चुका दो और (उनकी) अच्छी चीज़ से (अपनी) बुरी चीज़ न बदलो और उनके धन, अपने धनों में मिलाकर न खाओ। निःसंदेह ये बहुत बड़ा पाप है।');
INSERT INTO "al_umari" VALUES(4,3,'4:3','और यदि तुम डरो कि अनाथ (बालिकाओं) के विषय[ में न्याय नहीं कर सकोगे, तो नारियों में से जो भी तुम्हें भायें, दो से, तीन से, चार तक से विवाह कर लो और यदि डरो कि (उनके बीच) न्याय नहीं कर सकोगे, तो एक ही से करो अथवा जो तुम्हारे स्वामित्व में हो, उसीपर बस करो। ये अधिक समीप है कि अन्याय न करो।');
INSERT INTO "al_umari" VALUES(4,4,'4:4','तथा स्त्रियों को उनके महर (विवाह उपहार) प्रसन्नता से चुका दो। फिर यदि वे उसमें से कुछ तुम्हें अपनी इच्छा से दे दें, तो प्रसन्न होकर खाओ।');
INSERT INTO "al_umari" VALUES(4,5,'4:5','तथा अपने धन, जिसे अल्लाह ने तुम्हारे लिए जीवन स्थापन का साधन बनाया है, अज्ञानों को न[ दो। हाँ, उसमें से उन्हें खाना-कपड़ा दो और उनसे भली बात बोलो। ');
INSERT INTO "al_umari" VALUES(4,6,'4:6','तथा अनाथों की परीक्षा लेते रहो, यहाँ तक कि वे विवाह की आयु को पहुँच जायें, तो यदि तुम उनमें सुधार देखो, तो उनका धन उन्हें समर्पित कर दो और उसे अपव्यय तथा शीघ्रता से इसलिए न खाओ कि वे बड़े हो जायेंगे और जो धनी हो, तो वह बचे तथा जो निर्धन हो, वह नियमानुसार खा ले तथा जब तुम उनका धन उनके ह़वाले करो, तो उनपर साक्षी बना लो और अल्लाह ह़िसाब लेने के लिए काफ़ी है।');
INSERT INTO "al_umari" VALUES(4,7,'4:7','और पुरुषों के लिए उसमें से भाग है, जो माता-पिता तथा समीपवर्तियों ने छोड़ा है तथा स्त्रियों के लिए उसमें से भाग है, जो माता-पिता तथा समीपवर्तियों ने छोड़ा हो, वह थोड़ा हो अथवा अधिक, सबके भाग[ निर्धारित हैं। ');
INSERT INTO "al_umari" VALUES(4,8,'4:8','और जब मीरास विभाजन के समय समीपवर्ती[, अनाथ और निर्धन उपस्थित हों, तो उन्हें भी थोड़ा बहुत दे दो तथा उनसे भली बात बोलो। ');
INSERT INTO "al_umari" VALUES(4,9,'4:9','और उन लोगों को डरना चाहिए, जो अपने पीछे निर्बल संतान छोड़ जायें और उनके नाश होने का भय हो, अतः उन्हें चाहिए कि अल्लाह से डरें और सीधी बात बोलें।');
INSERT INTO "al_umari" VALUES(4,10,'4:10','जो लोग अनाथों का धन अत्याचार से खाते हैं, वे अपने पेटों में आग भरते हैं और शीघ्र ही नरक की अग्नि में प्रवेश करेंगे।');
INSERT INTO "al_umari" VALUES(4,11,'4:11','अल्लाह तुम्हारी संतान के संबंध में तुम्हें आदेश देता है कि पुत्र का भाग, दो पुत्रियों के बराबर है। यदि पुत्रियाँ दो[ से अधिक हों, तो उनके लिए छोड़े हुए धन का दो तिहाई (भाग) है। यदि एक ही हो, तो उसके लिए आधा है और उसके माता-पिता, दोनों में से प्रत्येक के लिए उसमें से छठा भाग है, जो छोड़ा हो, यदि उसके कोई संतान हों। और यदि उसके कोई संतान (पुत्र या पुत्री) न हों और उसका वारिस उसका पिता हो, तो उसकी माता का तिहाई (भाग) है, (और शेष पिता का)। फिर यदि (माता पिता के सिवा) उसके एक से अधिक भाई अथवा बहनें हों, तो उसकी माता के लिए छठा भाग है, जो वसिय्यत तथा क़र्ज़ चुकाने के पश्चात् होगा। तुम नहीं जानते कि तुम्हारे पिताओं और पुत्रों में से कौन तुम्हारे लिए अधिक लाभदायक हैं। वास्तव में, अल्लाह अति बड़ा तथा गुणी, ज्ञानी, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(4,12,'4:12','और तुम्हारे लिए उसका आधा है, जो तुम्हारी पत्नियाँ छोड़ जायें, यदि उनके कोई संतान (पुत्र या पुत्री) न हों। फिर यदि उनके कोई संतान हों, तो तुम्हारे लिए उसका चौथाई है, जो वे छोड़ गयी हों, वसिय्यत (उत्तरदान) या ऋण चुकाने के पश्चात्। जबकि (पत्नयों) के लिए उसका चौथाई है, जो (माल आदि) तुमने छोड़ा हो, यदि तुम्हारे कोई संतान (पुत्र या पुत्री) न हों। फिर यदि तुम्हारे कोई संतान हों, तो उनके लिए उसका आठवाँ[ (भाग) है, जो तुमने छोड़ा है, वसिय्यत (उत्तरदान) जो तुमने की हो, पूरी करने अथवा ऋण चुकाने के पश्चात्। फिर यदि किसी ऐसे पुरुष या स्त्री का वारिस होने की बात हो, जो ''कलाला'' हो तथा (दूसरी माता से) उसका भाई अथवा बहन हो, तो उनमें से प्रत्येक के लिए छठा (भाग) है। फिर यदि (माँ जाये) (भाई या बहनें) इससे अधिक हों, तो वे सब तिहाई (भाग) में (बराबर के) साझी होंगे। ये वसिय्यत (उत्तरदान) तथा ऋण चुकाने के पश्चात होगा। किसी को हानि नहीं पहुँचायी जायेगी। ये अल्लाह की ओर से वसिय्यत है और अल्लाह ज्ञानी तथा ह़िक्मत वाला है।');
INSERT INTO "al_umari" VALUES(4,13,'4:13','ये अल्लाह की (निर्धारित) सीमायें हैं और जो अल्लाह तथा उसके रसूल का आज्ञाकारी रहेगा, तो वह उसे ऐसे स्वर्गों में प्रवेश देगा, जिनमें नहरें प्रवाहित होंगी। उनमें वे सदावासी होंगे तथा यही बड़ी सफलता है।');
INSERT INTO "al_umari" VALUES(4,14,'4:14','और जो अल्लाह तथा उसके रसूल की अवज्ञा तथा उसकी सीमाओं का उल्लंघन करेगा, उसे नरक में प्रवेश देगा। जिसमें वह सदावासी होगा और उसी के लिए अपमानकारी यातना है।');
INSERT INTO "al_umari" VALUES(4,15,'4:15','तथा तुम्हारी स्त्रियों में से, जो व्यभिचार कर जायेँ, तो उनपर अपनों में से चार साक्षी लाओ। फिर यदि वे साक्ष्य (गवाही) दें, तो उन्हें घरों में बंद कर दो, यहाँ तक कि उन्हें मौत आ जाये अथवा अल्लाह उनके लिए कोई अन्य[ राह बना दे। ');
INSERT INTO "al_umari" VALUES(4,16,'4:16','और तुममें से जो दो व्यक्ति ऐसा करें, तो दोनों को दुःख पहुँचाओ, यहाँ तक कि (जब) वे तौबा (क्षमा याचना) कर लें और अपना सुधार कर लें, तो उन्हें छोड़ दो। निश्चय अल्लाह बड़ा क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(4,17,'4:17','अल्लाह के पास उन्हीं की तौबा (क्षमा याचना) स्वीकार्य है, जो अनजाने में बुराई कर जाते हैं, फिर शीघ्र ही क्षमा याचना कर लेते हैं, तो अल्लाह उनकी तौबा (क्षमा याचना) स्वीकार कर लेता है तथा अल्लाह बड़ा ज्ञानी गुणी है।');
INSERT INTO "al_umari" VALUES(4,18,'4:18','और उनकी तौबा (क्षमा याचना) स्वीकार्य नहीं, जो बुराईयाँ करते रहते हैं, यहाँ तक कि जब उनमें से किसी की मौत का समय आ जाता है, तो कहता हैः अब मैंने तौबा कर ली। न ही उनकी (तौबा स्वीकार की जायेगी) जो काफ़िर रहते हुए मर जाते हैं, इन्हीं के लिए हमने दुःखदायी यातना तैयार कर रखी है।');
INSERT INTO "al_umari" VALUES(4,19,'4:19','हे ईमान वालो! तुम्हारे लिए ह़लाल (वैध) नहीं कि बलपूर्वक स्त्रियों के वारिस बन जाओ[ तथा उन्हें इसलिए न रोको कि उन्हें जो दिया हो, उसमें से कुछ मार लो। परन्तु ये कि खुली बुराई कर जायेँ तथा उनके साथ उचित व्यवहार से रहो। फिर यदि वे तुम्हें अप्रिय लगें, तो संभव है कि तुम किसी चीज़ को अप्रिय समझो और अल्लाह ने उसमें बड़ी भलाई रख दी हो।');
INSERT INTO "al_umari" VALUES(4,20,'4:20','और यदि तुम किसी पत्नी के स्थान पर किसी दूसरी पत्नी से विवाह करना चाहो और तुमने उनमें से एक को (सोने चाँदी) का ढेर भी (महर में) दिया हो, तो उसमें से कुछ न लो। क्या तुम चाहते हो कि उसे आरोप लगाकर तथा खुले पाप द्वारा ले लो?');
INSERT INTO "al_umari" VALUES(4,21,'4:21','तथा तुम उसे ले भी कैसे सकते हो, जबकि तुम एक-दूसरे से मिलन कर चुके हो तथा उन्होंने तुमसे (विवाह के समय) दृढ़ वचन लिया है।');
INSERT INTO "al_umari" VALUES(4,22,'4:22','और उन स्त्रियों से विवाह[ न करो, जिनसे तुम्हारे पिताओं ने विवाह किया हो, परन्तु जो पहले हो चुका । वास्तव में, ये निर्लज्जा की तथा अप्रिय बात और बुरी रीति थी।');
INSERT INTO "al_umari" VALUES(4,23,'4:23','तुमपर[ ह़राम (अवैध) कर दी गयी हैं; तुम्हारी मातायें, तुम्हारी पुत्रियाँ, तुम्हारी बहनें, तुम्हारी फूफियाँ, तुम्हारी मोसियाँ और भतीजियाँ, भाँजियाँ, तुम्हारी वे मातायें जिन्होंने तुम्हें दूध पिलाया हो तथा दूध पीने से संबंधित बहनें, तुम्हारी पत्नियों की मातायें, तुम्हारी पत्नियों की पुत्रियाँ जिनका पालन पोषण तुम्हारी गोद में हुआ हो और उन पत्नियों से तुमने संभोग किया हो, यदि उनसे संभोग न किया हो, तो तुमपर कोई दोष नहीं, तुम्हारे सगे पुत्रों की पत्नियाँ और ये कि तुम दो बहनों को एकत्र करो, परन्तु जो हो चुका। वास्तव में, अल्लाह अति क्षमाशील दयावान् है।');
INSERT INTO "al_umari" VALUES(4,24,'4:24','तथा उन स्त्रियों से (विवाह वर्जित है), जो दूसरों के निकाह़ में हों। परन्तु तुम्हारी दासियाँ[ जो (युध्द में) तुम्हारे हाथ आयी हों। (ये) तुमपर अल्लाह ने लिख दिया है। और इनके सिवा (दूसरी स्त्रियाँ) तुम्हारे लिए ह़लाल (उचित) कर दी गयी हैं। (प्रतिबंध ये है कि) अपने धनों द्वारा व्यभिचार से सुरक्षित रहने के लिए विवाह करो। फिर उनमें से जिससे लाभ उठाओ, उन्हें उनका महर (विवाह उपहार) अवश्य चुका दो तथा महर (विवाह उपहार) निर्धारित करने के पश्चात् (यदि) आपस की सहमति से (कोई कमी या अधिक्ता कर लो), तो तुमपर कोई दोष नहीं। निःसंदेह, अल्लाह अति ज्ञानी, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(4,25,'4:25','और जो व्यक्ति तुममें से स्वतंत्र ईमान वालियों से विवाह करने की सकत न रखे, तो वह अपने हाथों में आई हुई अपनी ईमान वाली दासियों से (विवाह कर ले)। दरअसल, अल्लाह तुम्हारे ईमान को अधिक जानता है। तुम आपस में एक ही हो[। अतः तुम उनके स्वामियों की अनुमति से उन (दासियों) से विवाह कर लो और उन्हें नियमानुसार उनके महर (विवाह उपहार) चुका दो, वे सती हों, व्यभिचारिणी न हों, न गुप्त प्रेमी बना रखी हों। फिर जब वे विवाहित हो जायें, तो यदि व्यभिचार कर जायें, तो उनपर उसका आधा दण्ड है, जो स्वतंत्र स्त्रियों पर है। ये (दासी से विवाह) उसके लिए है, जो तुममें से व्यभिचार से डरता हो और सहन करो, तो ये तुम्हारे लिए अधिक अच्छा है और अल्लाह अति क्षमाशील दयावान् है।');
INSERT INTO "al_umari" VALUES(4,26,'4:26','अल्लाह चाहता है कि तुम्हारे लिए उजागर कर दे तथा तुम्हें भी उनकी नीतियों की राह दर्शा दे, जो तुमसे पहले थे और तुम्हारी क्षमा याचना स्वीकार करे। अल्लाह अति ज्ञानी तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(4,27,'4:27','और अल्लाह चाहता है कि तुमपर दया करे तथा जो लोग आकांक्षाओं के पीछे पड़े हुए हैं, वे चाहते हैं कि तुम बहुत अधिक झुक[ जाओ। ');
INSERT INTO "al_umari" VALUES(4,28,'4:28','अल्लाह तुम्हारा (बोझ) हल्का करना[ चाहता है तथा मानव निर्बल पैदा किया गया है। ');
INSERT INTO "al_umari" VALUES(4,29,'4:29','हे ईमान वालो! आपस में एक-दूसरे का धन अवैध रूप से न खाओ, परन्तु ये कि लेन-देन तुम्हारी आपस की स्वीकृति से (धर्म विधानुसार) हो और आत्म हत्या[ न करो, वास्तव में, अल्लाह तुम्हारे लिए अति दयावान् है। ');
INSERT INTO "al_umari" VALUES(4,30,'4:30','और जो अतिक्रमण तथा अत्याचार से ऐसा करेगा, समीप है कि हम उसे अग्नि में झोंक देंगे और ये अल्लाह के लिए सरल है।');
INSERT INTO "al_umari" VALUES(4,31,'4:31','तथा यदि तुम, उन महा पापों से बचते रहे, जिनसे तुम्हें रोका जा रहा है, तो हम तुम्हारे लिए तुम्हारे दोषों को क्षमा कर देंगे और तुम्हें सम्मानित स्थान में प्रवेश देंगे।');
INSERT INTO "al_umari" VALUES(4,32,'4:32','तथा उसकी कामना न करो, जिसके द्वारा अल्लाह ने तुम्हें एक-दूसरे पर श्रेष्ठता दी है। पुरुषों के लिए उसका भाग है, जो उन्होंने कमाया[ और स्त्रियों के लिए उसका भाग है, जो उन्होंने कमाया है। तथा अल्लह से उससे अधिक की प्रार्थना करते रहो। निःसंदेह अल्लाह सब कुछ जानता है। ');
INSERT INTO "al_umari" VALUES(4,33,'4:33','और हमने प्रत्येक के लिए वारिस (उत्तराधिकारी) बना दिये हैं, उसमें से जो माता-पिता तथा समीपवर्तियों ने छोड़ा हो तथा जिनसे तुमने समझौता[ किया हो, उन्हें उनका भाग दो। वास्तव में, अल्लाह प्रत्येक चीज़ से सूचित है। ');
INSERT INTO "al_umari" VALUES(4,34,'4:34','पुरुष स्त्रियों के व्यवस्थापक[ हैं, इस कारण कि अल्लाह ने उनमें से एक को दूसरे पर प्रधानता दी है तथा इस कारण कि उन्होंने अपने धनों में से (उनपर) ख़र्च किया है। अतः, सदाचारी स्त्रियाँ वो हैं, जो आज्ञाकारी तथा उनकी (अर्थात, पतियों की) अनुपस्थिति में अल्लाह की रक्षा में उनके अधिकारों की रक्षा करती हों। फिर तुम्हें जिनकी अवज्ञा का डर हो, तो उन्हें समझाओ और शयनागारों (सोने के स्थानों) में उनसे अलग हो जाओ तथा उन्हें मारो। फिर यदि वे तुम्हारी बात मानें, तो उनपर अत्याचार का बहाना न खोजो और अल्लाह सबसे ऊपर, सबसे बड़ा है। ');
INSERT INTO "al_umari" VALUES(4,35,'4:35','और यदि तुम्हें[ दोनों के बीच वियोग का डर हो, तो एक मध्यस्त उस (पति) के घराने से तथा एक मध्यस्त उस (पत्नी) के घराने से नियुक्त करो, यदि वे दोनों संधि कराना चाहेंगे, तो अल्लाह उन दोनों के बीच संधि करा देगा। वास्तव में, अल्लाह अति ज्ञानी सर्वसूचित है।');
INSERT INTO "al_umari" VALUES(4,36,'4:36','तथा अल्लाह की इबादत (वंदना) करो, किसी चीज़ को उसका साझी न बनाओ तथा माता-पिता, समीपवर्तियों, अनाथों, निर्धनों, समीप और दूर के पड़ोसी, यात्रा के साथी, यात्रि और अपने दास दासियों के साथ उपकार करो। निःसंदेह अल्लाह उससे प्रेम नहीं करता, जो अभिमानी अहंकारी[ हो। ');
INSERT INTO "al_umari" VALUES(4,37,'4:37','और जो स्वयं कृपण (कंजूसी) करते हैं तथा दूसरों को भी कृपण (कंजूसी) का आदेश देते हैं और उसे छुपाते हैं, जो अल्लाह ने उन्हें अपनी दया से प्रदान किया है। हमने कृतघ्नों के लिए अपमानकारी यातना तैयार कर रखी है।');
INSERT INTO "al_umari" VALUES(4,38,'4:38','तथा जो लोग अपना धन, लोगों को दिखाने के लिए दान करते हैं और अल्लह तथा अंतिम दिन (प्रलय) पर ईमान नहीं रखते तथा शैतान जिसका साथी हो, तो वह बहुत बुरा साथी[ है। ');
INSERT INTO "al_umari" VALUES(4,39,'4:39','और उनका क्या बिगड़ जाता, यदि वे अल्लाह तथा अन्तिम दिन (परलोक) पर ईमान (विश्वास) रखते और अल्लाह ने जो उन्हें दिया है, उसमें से दान करते? और अल्लाह उन्हें भली-भाँति जानता है।');
INSERT INTO "al_umari" VALUES(4,40,'4:40','अल्लाह कण भर भी किसी पर अत्याचार नहीं करता, यदि कुछ भलाई (किसी ने) की हो, तो (अल्लाह) उसे अधिक कर देता है तथा अपने पास से बड़ा प्रतिफल प्रदान करता है।');
INSERT INTO "al_umari" VALUES(4,41,'4:41','तो क्या दशा होगी, जब हम प्रत्येक उम्मत (समुदाय) से एक साक्षी लायेंगे और (हे नबी!) आपको उनपर (बनाकर) साक्षी लायेंगे[। ');
INSERT INTO "al_umari" VALUES(4,42,'4:42','उस दिन, जो काफ़िर तथा रसूल के अवज्ञाकारी हो गये, ये कामना करेंगे कि उनके सहित भूमि बराबर[ कर दी जाये और वे अल्लाह से कोई बात छुपा नहीं सकेंगे। ');
INSERT INTO "al_umari" VALUES(4,43,'4:43','हे ईमान वालो! तुम जब नशे[ में रहो, तो नमाज़ के समीप न जाओ, जब तक जो कुछ बोलो, उसे न समझो और न जनाबत की स्थिति में (मस्जिदों के समीप जाओ), परन्तु रास्ता पार करते हुए। यदि तुम रोगी हो अथवा यात्रा में रहो या स्त्रियों से सहवास कर लो, फिर जल न पाओ, तो पवित्र मिट्टी से तयम्मुम कर लो; उसे अपने मुखों तथा हाथों पर फेर लो। वास्तव में, अल्लाह अति क्षांत (सहिष्णु) क्षमाशील है।');
INSERT INTO "al_umari" VALUES(4,44,'4:44','क्या आपने उनकी दशा नहीं देखी, जिन्हें पुस्तक[ का कुछ भाग दिया गया? वे कुपथ ख़रीद रहे हैं तथा चाहते हैं कि तुमभी सुपथ से विचलित हो जाओ। ');
INSERT INTO "al_umari" VALUES(4,45,'4:45','तथा अल्लाह तुम्हारे शत्रुओं से भली-भाँति अवगत है और (तुम्हारे लिए) अल्लाह की रक्षा काफ़ी है तथा अल्लाह की सहायता काफ़ी है।');
INSERT INTO "al_umari" VALUES(4,46,'4:46','(हे नबी!) यहूदियों में से कुछ लोग ऐसे हैं, जो शब्दों को उनके (वास्तविक) स्थानों से फेरते हैं और (आपसे) कहते हैं कि हमने सुन लिया तथा (आपकी) अवज्ञा की और आप सुनिए, आप सुनाये न जायें! तथा अपनी ज़ुबानें मोड़कर "राइना" कहते और सत्धर्म में व्यंग करते हैं और यदि वे "हमने सुन लिया और आज्ञाकारी हो गये" और "हमें देखिए" कहते, तो उनके लिए अधिक अच्छी तथा सही बात होती। परन्तु अल्लाह ने उनके कुफ़्र के कारण उन्हें धिक्कार दिया है। अतः, उनमें से थोड़े ही ईमान लायेंगे।');
INSERT INTO "al_umari" VALUES(4,47,'4:47','हे अह्ले किताब! उस (क़ुर्आन) पर ईमान लाओ, जिसे हमने उन (पुस्तकों) का प्रमाणकारी बनाकर उतारा है, जो तुम्हारे साथ हैं, इससे पहले कि हम चेहरे बिगाड़ कर पीछे कर दें अथवा उन्हें ऐसे ही धिक्कार[ दें, जैसे शनिवार वालों को धिक्कार दिया। अल्लाह का आदेश पूरा हो कर रहा। ');
INSERT INTO "al_umari" VALUES(4,48,'4:48','निःसंदेह अल्लाह ये नहीं क्षमा करेगा कि उसका साझी बनाया जाये[ और उसके सिवा जिसे चाहे, क्षमा कर देगा। जो अल्लाह का साझी बनाता है, तो उसने महा पाप गढ़ लिया। ');
INSERT INTO "al_umari" VALUES(4,49,'4:49','क्या आपने उन्हें नहीं देखा, जो अपने आप पवित्र बन रहे हैं? बल्कि अल्लाह जिसे चाहे, पवित्र करता है और (लोगों पर) कण बराबर अत्याचार नहीं किया जायेगा।');
INSERT INTO "al_umari" VALUES(4,50,'4:50','देखो, ये लोग कैसे अल्लाह पर मिथ्या आरोप लगा रहे[ हैं! उनके खुले पाप के लिए यही बहुत है। ');
INSERT INTO "al_umari" VALUES(4,51,'4:51','(हे नबी!) क्या आपने उनकी दशा नहीं देखी, जिन्हें पुस्तक का कुछ भाग दिया गया? वे मूर्तियों तथा शैतानों पर ईमान (विश्वास) रखते हैं और काफ़िरों[ के बारे में कहते हैं कि ये ईमान वालों से अधिक सीधी डगर पर हैं। ');
INSERT INTO "al_umari" VALUES(4,52,'4:52','और जिसे अल्लाह धिक्कार दे, आप उसका कदापि कोई सहायक नहीं पाएंगे।');
INSERT INTO "al_umari" VALUES(4,53,'4:53','क्या उनके पास राज्य का कोई भाग है, इसलिए लोगों को (उसमें से) तनिक भी नहीं देंगे?');
INSERT INTO "al_umari" VALUES(4,54,'4:54','बल्कि वे लोगों[ से उस अनुग्रह पर विद्वेष कर रहे हैं, जो अल्लाह ने उन्हें प्रदान किया है। तो हमने (पहले भी) इब्राहीम के घराने को पुस्तक तथा ह़िक्मत (तत्वदर्शिता) दी है और उन्हें विशाल राज्य प्रदान किया है। ');
INSERT INTO "al_umari" VALUES(4,55,'4:55','फिर उनमें से कोई ईमान लाया और कोई उससे विमुख हो गया। (तो विमुख होने वाले के लिए) नरक की भड़कती अग्नि बहुत है।');
INSERT INTO "al_umari" VALUES(4,56,'4:56','वास्तव में, जिन लोगों ने हमारी आयतों के साथ कुफ़्र (अविश्वास) किया, हम उन्हें नरक में झोंक देंगे। जब-जब उनकी खालें पकेंगी, हम उनकी खालें बदल देंगे, ताकि वे यातना चखें, निःसंदेह अल्लाह प्रभुत्वशाली तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(4,57,'4:57','और जो लोग ईमान लाये तथा सदाचार किये, हम उन्हें ऐसे स्वर्गों में प्रवेश देंगे, जिनमें नहरें प्रवाहित हैं। जिनमें वे सदावासी होंगे, उनके लिए उनमें निर्मल पत्नियाँ होंगी और हम उन्हें घनी छाँवों में रखेंगे।');
INSERT INTO "al_umari" VALUES(4,58,'4:58','अल्लाह[ तुम्हें आदेश देता है कि धरोहर उनके स्वामियों को चुका दो और जब लोगों के बीच निर्णय करो, तो न्याय के साथ निर्णय करो। अल्लाह तुम्हें अच्छी बात का निर्देश दे रहा है। निःसंदेह अल्लाह सब कुछ सुनने-देखने वाला है। ');
INSERT INTO "al_umari" VALUES(4,59,'4:59','हे ईमान वालो! अल्लाह की आज्ञा का अनुपालन करो और रसूल की आज्ञा का अनुपालन करो तथा अपने शासकों की आज्ञापालन करो। फिर यदि किसी बात में तुम आपस में विवाद (विभेद) कर लो, तो उसे अल्लाह और रसूल की ओर फेर दो, यदि तुम अल्लाह तथा अन्तिम दिन (प्रलय) पर ईमान रखते हो। ये तुम्हारे लिए अच्छा[ और इसका परिणाम अच्छा है। ');
INSERT INTO "al_umari" VALUES(4,60,'4:60','(हे नबी!) क्या आपने उन द्विधावादियों (मुनाफ़िक़ों) को नहीं जाना, जिनका ये दावा है कि जो कुछ आपपर अवतरित हुआ है तथा जो कुछ आपसे पूर्व अवतरित हुआ है, उनपर ईमान रखते हैं, किन्तु चाहते हैं कि अपने विवाद का निर्णय विद्रोही के पास ले जायें, जबकि उन्हें आदेश दिया गया है कि उसे अस्वीकार कर दें? और शैतान चाहता है कि उन्हें सत्धर्म से बहुत दूर[ कर दे। ');
INSERT INTO "al_umari" VALUES(4,61,'4:61','तथा जब उनसे कहा जाता है कि उस (क़ुर्आन) की ओर आओ, जो अल्लाह ने उतारा है तथा रसूल की (सुन्नत की) ओर, तो आप मुनाफ़िक़ों (द्विधावादियों) को देखते हैं कि वे आपसे मुँह फेर रहे हैं।');
INSERT INTO "al_umari" VALUES(4,62,'4:62','फिर यदि उनके अपने ही करतूतों के कारण उनपर कोई आपदा आ पड़े, तो फिर आपके पास आ कर शपथ लेते हैं कि हमने[ तो केवल भलाई तथा (दोनों पक्षों में) मेल कराना चाहा था। ');
INSERT INTO "al_umari" VALUES(4,63,'4:63','यही वो लोग हैं, जिनके दिलों के भीतर की बातें अल्लाह जानता है। अतः, आप उन्हें क्षमा कर दें और उनसे ऐसी प्रभावी बात बोलें, जो उनके दिलों में उतर जाये।');
INSERT INTO "al_umari" VALUES(4,64,'4:64','और हमने जो भी रसूल भेजा, वो इसलिए, ताकि अल्लाह की अनुमति से, उसकी आज्ञा का पालन किया जाये और जब उन लोगों ने अपने ऊपर अत्याचार किया, तो यदि वे आपके पास आते, फिर अल्लाह से क्षमा याचना करते तथा उनके लिए रसूल क्षमा की प्रार्थना करते, तो अल्लाह को अति क्षमाशील दयावान् पाते।');
INSERT INTO "al_umari" VALUES(4,65,'4:65','तो आपके पालनहार की शपथ! वे कभी ईमान वाले नहीं हो सकते, जब तक अपने आपस के विवाद में आपको निर्णायक न बनाएँ[, फिर आप जो निर्णय कर दें, उससे अपने दिलों में तनिक भी संकीर्णता (तंगी) का अनुभव न करें और पूर्णता स्वीकार कर लें। ');
INSERT INTO "al_umari" VALUES(4,66,'4:66','और यदि हम उन्हें[ आदेश देते कि स्वयं को वध करो तथा अपने घरों से निकल जाओ, तो इनमें से थोड़े के सिवा कोई ऐसा नहीं करता। जबकि उन्हें जो निर्देश दिया जाता है, यदि वे उसका पालन करते, तो उनके लिए अच्छा और अधिक स्थिरता का कारण होता। ');
INSERT INTO "al_umari" VALUES(4,67,'4:67','और हम उन्हें अपने पास से बहुत बड़ा प्रतिफल देते।');
INSERT INTO "al_umari" VALUES(4,68,'4:68','तथा हम उन्हें सीधी डगर दर्शा देते।');
INSERT INTO "al_umari" VALUES(4,69,'4:69','तथा जो अल्लाह और रसूल की आज्ञा का अनुपालन करेंगे, वही (स्वर्ग में), उनके साथ होंगे, जिनपर अल्लाह ने पुरस्कार किया है, अर्थात नबियों, सत्यवादियों, शहीदों और सदाचारियों के साथ और वे क्या ही अच्छे साथी हैं?');
INSERT INTO "al_umari" VALUES(4,70,'4:70','ये प्रदान अल्लाह की ओर से है और अल्लाह का ज्ञान बहुत[ है। ');
INSERT INTO "al_umari" VALUES(4,71,'4:71','हे ईमान वालो! अपने (शत्रु से) बचाव के साधन तैयार रखो, फिर गिरोहों में अथवा एक साथ निकल पड़ो।');
INSERT INTO "al_umari" VALUES(4,72,'4:72','और तुममें कोई ऐसा व्यक्ति[ भी है, जो तुमसे अवश्य पीछे रह जोएगा और यदि तुमपर (युध्द में) कोई आपदा आ पड़े, तो कहेगाः अल्लाह ने मुझपर उपकार किया कि मैं उनके साथ उपस्थित न था। ');
INSERT INTO "al_umari" VALUES(4,73,'4:73','और यदि तुमपर अल्लाह की दया हो जाये, तो वे अवश्य ये कामना करेगा कि काश! मैं भी उनके साथ होता, तो बड़ी सफलता प्राप्त कर लेता। मानो उसके और तुम्हारे मध्य कोई मित्रता ही न थी।');
INSERT INTO "al_umari" VALUES(4,74,'4:74','तो चाहिए कि अल्लाह की राह[ में वे लोग युध्द करें, जो आख़िरत (परलोक) के बदले सांसारिक जीवन बेच चुके हैं और जो अल्लाह की राह में युध्द करेगा, वो मारा जाये अथवा विजयी हो जाये, हम उसे बड़ा प्रतिफल प्रदान करेंगे। ');
INSERT INTO "al_umari" VALUES(4,75,'4:75','और तुम्हें क्या हो गया है कि अल्लाह की राह में युध्द नहीं करते, जबकि कितने ही निर्बल पुरुष, स्त्रियाँ और बच्चे हैं, जो गुहार रहे हैं कि हे हमारे पालनहार! हमें इस नगर[ से निकाल दे, जिसके निवासी अत्याचारी हैं और हमारे लिए अपनी ओर से कोई रक्षक बना दे और हमारे लिए अपनी ओर से कोई सहायक बना दे। ');
INSERT INTO "al_umari" VALUES(4,76,'4:76','जो लोग ईमान लाये, वे अल्लाह की राह में युध्द करते हैं और जो काफ़िर हैं, वे उपद्रव के लिए युध्द करते हैं। तो शैतान के साथियों से युध्द करो। निःसंदेह शैतान की चाल निर्बल होती है।');
INSERT INTO "al_umari" VALUES(4,77,'4:77','(हे नबी!) क्या आपने उनकी दशा नहीं देखी, जिनसे कहा गया था कि अपने हाथों को (युध्द से) रोके रखो, नमाज़ की स्थापना करो और ज़कात दो? परन्तु जब उनपर युध्द करना लिख दिया गया, तो उनमें से एक गिरोह लोगों से ऐसे डर रहा है, जैसे अल्लाह से डर रहा हो या उससे भी अधिक तथा वे कहते हैं कि हे हमारे पालनहार! हमें युध्द करने का आदेश क्यों दे दिया, क्यों न हमें थोड़े दिनों का और अवसर दिया? आप कह दें कि सांसारिक सुख बहुत थोड़ा है तथा परलोक उसके लिए अधिक अच्छा है, जो अल्लाह[ से डरा और उनपर कण भर भी अत्याचार नहीं किया जायेगा। ');
INSERT INTO "al_umari" VALUES(4,78,'4:78','तुम जहाँ भी रहो, तुम्हें मौत आ पकड़ेगी, यद्यपि दृढ़ दुर्गों में क्यों न रहो। तथा उन्हें यदि कोई सुख पहुँचता है, तो कहते हैं कि ये अल्लाह की ओर से है और यदि कोई आपदा आ पड़े, तो कहते हैं कि ये आपके कारण है। (हे नबी!) उनसे कह दो कि सब अल्लाह की ओर से है। इन लोगों को क्या हो गया कि कोई बात समझने के समीप भी नहीं[ होते? ');
INSERT INTO "al_umari" VALUES(4,79,'4:79','(वास्तविक्ता तो ये है कि) तुम्हें जो सुख पहुँचता है, वह अल्लाह की ओर से होता है तथा जो हानि पहुँचती है, वह स्वयं (तुम्हारे कुकर्मों के) कारण होती है और हमने आपको सब मानव का रसूल (संदेशवाहक) बनाकर भेजा[ है और (आपके रसूल होने के लिए) अल्लाह का साक्ष्य बहुत है। ');
INSERT INTO "al_umari" VALUES(4,80,'4:80','जिसने रसूल की आज्ञा का अनुपालन किया, (वास्तव में) उसने अल्लाह की आज्ञा का पालन किया तथा जिसने मुँह फेर लिया, तो (हे नबी!) हमने आपको उनका प्रहरी (रक्षक) बनाकर नहीं भेजा[ है। ');
INSERT INTO "al_umari" VALUES(4,81,'4:81','तथा वे (आपके सामने) कहते हैं कि हम आज्ञाकारी हैं और जब आपके पास से जाते हैं, तो उनमें से कुछ लोग, रात में, आपकी बात के विरुध्द परामर्श करते हैं और वे जो परामर्श कर रहे हैं, उसे अल्लाह लिख रहा है। अतः आप उनपर ध्यान न दें और अल्लाह पर भरोसा करें तथा अल्लाह पर भरोसा काफ़ी है।');
INSERT INTO "al_umari" VALUES(4,82,'4:82','तो क्या वे क़ुर्आन के अर्थों पर सोच-विचार नहीं करते? यदि वे अल्लाह के सिवा दूसरे की ओर से होता, तो उसमें बहुत सी प्रतिकूल (बेमेल) बातें पाते[। ');
INSERT INTO "al_umari" VALUES(4,83,'4:83','और जब उनके पास शांति या भय की कोई सूचना आती है, तो उसे फैला देते हैं। जबकि यदि वे उसे अल्लाह के रसूल तथा अपने अधिकारियों की ओर फेर देते, तो जो बात की तह तक पहुँचने वाले हैं, वे उसकी वास्तविक्ता जान लेते। यदि तुमपर अल्लाह की अनुकम्पा तथा दया न होती, तो तुममें थोड़े के सिवा सब शैतान के पीछे भाग[ जाते। ');
INSERT INTO "al_umari" VALUES(4,84,'4:84','तो (हे नबी!) आप अल्लाह की राह में युध्द करें। केवल आपपर ये भार डाला जा रहा है तथा ईमान वालों को (इसकी) प्रेरणा दें। संभव है कि अल्लाह काफ़िरों का बल (तोड़ दे)। अल्लाह का बल और उसका दण्ड सबसे कड़ा है।');
INSERT INTO "al_umari" VALUES(4,85,'4:85','जो अच्छी अनुशंसा (सिफ़ारिश) करेगा, उसे उसका भाग (प्रतिफल) मिलेगा तथा जो बुरी अनुशंसा (सिफ़ारिश) करेगा, तो उसे भी उसका भाग (कुफल)[ मिलेगा और अल्लाह प्रत्येक चीज़ का निरीक्षक है। ');
INSERT INTO "al_umari" VALUES(4,86,'4:86','और जब तुमसे सलाम किया जाये, तो उससे अच्छा उत्तर दो अथवा उसी को दोहरा दो। निःसंदेह अल्लाह प्रत्येक विषय का ह़िसाब लेने वाला है।');
INSERT INTO "al_umari" VALUES(4,87,'4:87','अल्लाह के सिवा कोई वंदनीय (पूज्य) नहीं, वह अवश्य तुम्हें प्रलय के दिन एकत्र करेगा, इसमें कोई संदेह नहीं तथा बात कहने में अल्लाह से अधिक सच्चा कौन हो सकता है?');
INSERT INTO "al_umari" VALUES(4,88,'4:88','तुम्हें क्या हुआ है कि मुनाफ़िक़ों (द्विधावादियों) के बारे में दो पक्ष[ बन गये हो, जबकि अल्लाह ने उनके कुकर्मों के कारण उन्हें ओंधा कर दिया है। क्या तुम उसे सुपथ दर्शा देना चाहते हो, जिसे अल्लाह ने कुपथ कर दिया हो? और जिसे अल्लाह कुपथ कर दे, तो तुम उसके लिए कोई राह नहीं पा सकते। ');
INSERT INTO "al_umari" VALUES(4,89,'4:89','(हे ईमान वालो!) वे तो ये कामना करते हैं कि उन्हीं के समान तुमभी काफ़िर हो जाओ तथा उनके बराबर हो जाओ। अतः उनमें से किसी को मित्र न बनाओ, जब तक वे अल्लाह की राह में हिजरत न करें। यदि वे इससे विमुख हों, तो उन्हें जहाँ पाओ, वध करो और उनमें से किसी को मित्र न बनाओ और न सहायक।');
INSERT INTO "al_umari" VALUES(4,90,'4:90','परन्तु उनमें से जो किसी ऐसी क़ौम से जा मिलें, जिनके और तुम्हारे बीच संधि हो अथवा ऐसे लोग हों, जो तुम्हारे पास इस स्थिति में आयें कि उनके दिल इससे संकुचित हो रहे हों कि तुमसे युध्द करें अथवा (तुम्हारे साथ) अपनी जाति से युध्द करें। यदि अल्लाह चाहता, तो उनहें तुमपर सामर्थ्य दे देता, फिर वे तुमसे युध्द करते। यदि वे तुमसे विलग रह गये, तुमसे युध्द नहीं किया और तुमसे संधि कर ली, तो उनके विरुध्द अल्लाह ने तुम्हारे लिए कोई (युध्द करने की) राह नहीं बनाई[ है। ');
INSERT INTO "al_umari" VALUES(4,91,'4:91','तथा तुम्हें कुछ ऐसे दूसरे लोग भी मिलेंगे, जो तुम्हारी ओर से भी शान्त रहना चाहते हैं और अपनी जाति की ओर से शांत रहना (चाहते हैं)। फिर जब भी उपद्रव की ओर फेर दिये जायेँ, तो उसमें ओंधे होकर गिर जाते हैं। तो यदि वे तुमसे विलग न रहें और तुमसे संधि न करें तथा अपना हाथ न रोकें, तो उन्हें पकड़ो और जहाँ पाओ, वध करो। हमने उनके विरुध्द तुम्हारे लिए खुला तर्क बना दिया है।');
INSERT INTO "al_umari" VALUES(4,92,'4:92','किसी ईमान वाले के लिए वैध नहीं कि वो किसी ईमान वाले की हत्या कर दे, परन्तु चूक[ से। फिर जो किसी ईमान वाले की चूक से हत्या कर दे, तो उसे एक ईमान वाला दास मुक्त करना है और उसके घर वालों को दियत (अर्थदण्ड) देना है, परन्तु ये कि वे दान (क्षमा) कर दें। फिर यदि वह (निहत) उस जाति में से हो, जो तुम्हारी शत्रु है और वह (निहत) ईमान वाला है, तो एक ईमान वाला दास मुक्त करना है और यदि ऐसी क़ौम से हो, जिसके और तुम्हारे बीच संधि है, तो उसके घर वालों को अर्थदण्ड देना तथा एक ईमान वाला दास (भी) मुक्त करना है और जो दास न पाये, उसे निरन्तर दो महीने रोज़ा रखना है। अल्लाह की ओर से (उसके पाप की) यही क्षमा है और अल्लाह अति ज्ञानी तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(4,93,'4:93','और जो किसी ईमान वाले की हत्या जान-बूझ कर कर दे, उसका कुफल (बदला) नरक है, जिसमें वह सदावासी होगा और उसपर अल्लाह का प्रकोप तथा धिक्कार है और उसने उसके लिए घोर यातना तैयार कर रखी है।');
INSERT INTO "al_umari" VALUES(4,94,'4:94','हे ईमान वालो! जब तुम अल्लाह की राह में (जिहाद के लिए) निकलो, तो भली-भाँति परख[ लो और कोई तुम्हें सलाम करे, तो ये न कहो कि तुम ईमान वाले नहीं हो। क्या तुम सांसारिक जीवन का उपकरण चाहते हो? जबकि अल्लाह के पास बहुत-से परिहार (शत्रुधन) हैं। तुमभी पहले ऐसे ही थे, तो अल्लाह ने तुमपर उपकार किया। अतः, भली-भाँति परख लिया करो। निःसंदेह, अल्लाह उससे सूचित है, जो तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(4,95,'4:95','ईमान वालों में जो अकारण अपने घरों में रह जाते हैं और जो अल्लाह की राह में अपने धनों और प्राणों के द्वारा जिहाद करते हैं, दोनों बराबर नहीं हो सकते। अल्लाह ने उन्हें जो अपने धनों तथा प्राणों के द्वारा जिहाद करते हैं, उनपर जो घरों में रह जाते हैं, पद में प्रधानता दी है और प्रत्येक को अल्लाह ने भलाई का वचन दिया है और अल्लाह ने जिहाद करने वालों को उनपर, जो घरों में बैठे रह जाने वाले हैं, बड़े प्रतिफल में भी प्रधानता दी है।');
INSERT INTO "al_umari" VALUES(4,96,'4:96','अल्लाह की ओर से कई (उच्च) श्रेणियाँ तथा क्षमा और दया है और अल्लाह अति क्षमाशील दयावान् है।');
INSERT INTO "al_umari" VALUES(4,97,'4:97','निःसंदेह वे लोग, जिनके प्राण फ़रिश्ते निकालते हैं, इस दशा में कि वे अपने ऊपर (कुफ़्र के देश में रहकर) अत्याचार करने वाले हों, तो उनसे कहते हैं, तुम किस चीज़ में थे? वे कहते हैं कि हम धरती में विवश थे। तब फ़रिश्ते कहते हैं, क्या अल्लाह की धरती विस्तृत नहीं थी कि तुम उसमें हिजरत कर[ जाते? तो इन्हीं का आवास नरक है और वह क्या ही बुरा स्थान है! ');
INSERT INTO "al_umari" VALUES(4,98,'4:98','परन्तु जो पुरुष, स्त्रियाँ तथा बच्चे ऐसे विवश हूँ कि कोई उपाय न रखते हों और न (हिजरत की) कोई राह पाते हों।');
INSERT INTO "al_umari" VALUES(4,99,'4:99','तो आशा है कि अल्लाह उन्हें क्षमा कर देगा। निःसंदेह अल्लाह अति ज्ञान्त क्षमाशील है।');
INSERT INTO "al_umari" VALUES(4,100,'4:100','तथा जो कोई अल्लाह की राह में हिजरत करेगा, वह धरती में बहुत-से निवास स्थान तथा विस्तार पायेगा और जो अपने घर से अल्लाह और उसके रसूल की ओर निकल गया, फिर उसे (राह में ही) मौत ने पकड़ लिया, तो उसका प्रतिफल अल्लाह के पास निश्चित हो गया और अल्लाह अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(4,101,'4:101','और जब तुम धरती में यात्रा करो, तो नमाज़[ क़स्र (संक्षिप्त) करने में तुमपर कोई दोष नहीं, यदि तुम्हें डर हो कि काफ़िर तुम्हें सतायेंगे। वास्तव में, काफ़िर तुम्हारे खुले शत्रु हैं। ');
INSERT INTO "al_umari" VALUES(4,102,'4:102','तथा (हे नबी!) जब आप (रणक्षेत्र में) उपस्थित हों और उनके लिए नमाज़ की स्थापना करें, तो उनका एक गिरोह आपके साथ खड़ा हो जाये और अपने अस्त्र-शस्त्र लिए रहें और जब वे सज्दा कर लें, तो तुम्हारे पीछे हो जायें तथा दूसरा गिरोह आये, जिसने नमाज़ नहीं पढ़ी है और आपके साथ नमाज़ पढ़ें और अपने अस्त्र-शस्त्र लिए रहें। काफ़िर चाहते हैं कि तुम अपने शस्त्रों से निश्चेत हो जाओ, तो तुमपर यकायक धावा बोल दें। फिर तुमपर कोई दोष नहीं, यदि वर्षा के कारण तुम्हें दुःख हो अथवा तुम रोगी रहो कि अपने शस्त्र[ उतार दो तथा अपने बचाव का ध्यान रखो। निःसंदेह अल्लाह ने काफ़िरों के लिए अपमानकारी यातना तैयार कर रखी है। ');
INSERT INTO "al_umari" VALUES(4,103,'4:103','फिर जब तुम नमाज़ पूरी कर लो, तो खड़े, बैठे, लेटे प्रत्येक स्थिति में अल्लाह का स्मरण करो और जब तुम शान्त हो जाओ, तो पूरी नमाज़ पढ़ो। निःसंदेह, नमाज़ ईमान वालों पर निर्धारित समय पर अनिवार्य की गयी है।');
INSERT INTO "al_umari" VALUES(4,104,'4:104','तथा तुम (शत्रु) जाति का पीछा करने में सिथिल न बनो, यदि तुम्हें दुःख पहुँचा है, तो तुम्हारे समान उन्हें भी दुःख पहुँचा है तथा तुम अल्लाह से जो आशा[ रखते हो, वो आशा वे नहीं रखते तथा अल्लाह अति ज्ञानी तत्वज्ञ है। ');
INSERT INTO "al_umari" VALUES(4,105,'4:105','(हे नबी!) हमने आपकी ओर इस पुस्तक (क़ुर्आन) को सत्य के साथ उतारा है, ताकि आप, लोगों के बीच उसके अनुसार निर्णय करें, जो अल्लाह ने आपको बताया है, और आप विश्वासघातियों के पक्षधर न[ बनें। ');
INSERT INTO "al_umari" VALUES(4,106,'4:106','तथा अल्लाह से क्षमा याचना करते रहें, निःसंदेह अल्लाह अति क्षमाशील दयावान् है।');
INSERT INTO "al_umari" VALUES(4,107,'4:107','और उनका पक्ष न लें, जो स्वयं अपने साथ विश्वासघात करते हों, निःसंदेह अल्लाह विश्वासघाती, पापी से प्रेम नहीं करता[। ');
INSERT INTO "al_umari" VALUES(4,108,'4:108','वे (अपने करतूत) लोगों सो छुपा सकते हैं,परन्तु अल्लाह से नहीं छुपा सकते और वह उनके साथ होता है, जब वे रात में उस बात का परामर्श करते हैं, जिससे वह प्रसन्न नहीं[ होता तथा अल्लाह उसे घेरे हुए है, जो वे कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(4,109,'4:109','सुनो! तुम ही वो हो कि सांसारिक जीवन में उनकी ओर से झगड़ लिए। तो परलय के दिन उनकी ओर से कौन अल्लाह से झगड़ेगा और कौन उनका अभिभाषक (प्रतिनिधि) होगा?');
INSERT INTO "al_umari" VALUES(4,110,'4:110','जो व्यक्ति कोई कुकर्म करेगा अथवा अपने ऊपर अत्याचार करेगा, फिर अल्लाह से क्षमा याचना करेगा, तो वह उसे अति क्षमी दयावान् पायेगा।');
INSERT INTO "al_umari" VALUES(4,111,'4:111','और जो व्यक्ति कोई पाप करता है, तो अपने ऊपर करता[ है तथा अल्लाह अति ज्ञानी तत्वज्ञ है। ');
INSERT INTO "al_umari" VALUES(4,112,'4:112','और जो व्यक्ति कोई चूक अथवा पाप स्वयं करे और किसी निर्दोष पर उसका आरोप लगा दे, तो उसने मिथ्या दोषारोपन तथा खुले पाप का[ बोझ अपने ऊपर लाद लिया। ');
INSERT INTO "al_umari" VALUES(4,113,'4:113','और (हे नबी!) यदि आपपर अल्लाह की दया तथा कृपा न होती, तो उनके एक गिरोह ने संकल्प ले लिया था कि आपको कुपथ कर दें[ और वे स्वयं को ही कुपथ कर रहे थे। तथा वे आपको कोई हानि नहीं पहुँचा सकते। क्योंकि अल्लाह ने आपपर पुस्तक (क़ुर्आन) तथा हिक्मत (सुन्नत) उतारी है और आपको उसका ज्ञान दे दिया है, जिसे आप नहीं जानते थे तथा ये आपपर अल्लाह की बड़ी दया है। ');
INSERT INTO "al_umari" VALUES(4,114,'4:114','उनके अधिकांश सरगोशी में कोई भलाई नहीं होती, परन्तु जो दान, सदाचार या लोगों में सुधार कराने का आदेश दे और जो कोई ऐसे कर्म अल्लाह की प्रसन्नता के लिए करेगा, तो हम उसे बहुत भारी प्रतिफल प्रदान करेंगे।');
INSERT INTO "al_umari" VALUES(4,115,'4:115','तथा जो व्यक्ति अपने ऊपर मार्गदर्शन उजागर हो जाने के[ पश्चात् रसूल का विरोध करे और ईमान वालों की राह के सिवा (दूसरी राह) का अनुसरण करे, तो हम उसे वहीं फेर देंगे, जिधर फिरा है और उसे नरक में झोंक देंगे तथा वह बुरा निवास स्थान है।');
INSERT INTO "al_umari" VALUES(4,116,'4:116','निःसंदेह, अल्लाह इसे क्षमा[ नहीं करेगा कि उसका साझी बनाया जाये और इसके सिवा जिसे चाहेगा, क्षमा कर देगा तथा जो अल्लाह का साझी बनाता है, वह (दरअसल) कुपथ में बहुत दूर चला गया। ');
INSERT INTO "al_umari" VALUES(4,117,'4:117','वे (मिश्रणवादी), अल्लाह के सिवा देवियों ही को पुकारते हैं और धिक्कारे हुए शैतान को पुकारते हैं।');
INSERT INTO "al_umari" VALUES(4,118,'4:118','जिसे अल्लाह ने धिक्कार दिया है और जिसने कहा था कि मैं तेरे भक्तों से एक निश्चित भाग लेकर रहूँगा।');
INSERT INTO "al_umari" VALUES(4,119,'4:119','और उन्हें अवश्य बहकाऊँगा, कामनायें दिलाऊँगा और आदेश दूँगा कि वे पशुओं के कान चीर दें तथा उन्हें आदेश दूँगा, तो वे अवश्य अल्लाह की संरचना में परिवर्तन[ कर देंगे तथा जो शैतान को अल्लाह के सिवा सहायक बनायेगा, वह खुली क्षति में पड़ जायेगा। ');
INSERT INTO "al_umari" VALUES(4,120,'4:120','वह उन्हें वचन देता तथा कामनाओं में उलझाता है और उन्हें जो वचन देता है, वह धोखे के सिवा कुछ नहीं है।');
INSERT INTO "al_umari" VALUES(4,121,'4:121','उन्हीं का निवास स्थान नरक है और वे उससे भागने की कोई राह नहीं पायेंगे।');
INSERT INTO "al_umari" VALUES(4,122,'4:122','तथा जो लोग ईमान लाये और सत्कर्म किये, हम उन्हें ऐसे स्वर्गों में प्रवेश देंगे, जिनमें नहरें प्रवाहित होंगी। वे उनमें सदावासी होंगे। ये अल्लाह का सत्य वचन है और अल्लाह से अधिक सत्य कथन किसका हो सकता है?');
INSERT INTO "al_umari" VALUES(4,123,'4:123','(ये प्रतिफल) तुम्हारी कामनाओं तथा अह्ले किताब की कामनाओं पर निर्भर नहीं। जो कोई भी दुष्कर्म करेगा, वह उसका कुफल पायेगा तथा अल्लाह के सिवा अपना कोई रक्षक और सहायक नहीं पायेगा।');
INSERT INTO "al_umari" VALUES(4,124,'4:124','तथा जो सत्कर्म करेगा, वह नर हो अथवा नारी, फिर ईमान भी[ रखता होगा, तो वही लोग स्वर्ग में प्रवेश पायेंगे और तनिक भी अत्याचार नहीं किये जायेंगे। ');
INSERT INTO "al_umari" VALUES(4,125,'4:125','तथा उस व्यक्ति से अच्छा किसका धर्म हो सकता है, जिसने स्वयं को अल्लाह के लिए झुका दिया, वह एकेश्वरवादी भी हो और एकेश्वरवादी इब्राहीम के धर्म का अनुसरण कर रहा हो? और अल्लाह ने इब्राहीम को अपना विशुध्द मित्र बना लिया।');
INSERT INTO "al_umari" VALUES(4,126,'4:126','तथा अल्लाह ही का है, जो कुछ आकाशों तथा धरती में है और अल्लाह प्रत्येक चीज़ को अपने नियंत्रण में लिए हुए है।');
INSERT INTO "al_umari" VALUES(4,127,'4:127','(हे नबी!) वे स्त्रियों के बारे में आपसे धर्मादेश पूछ रहे हैं। आप कह दें कि अल्लाह उनके बारे में तुम्हें आदेश देता है और वे आदेश भी हैं, जो इससे पूर्व पुस्तक (क़ुर्आन) में तुम्हें, उन अनाथ स्त्रियों के बारे में सुनाये गये हैं, जिनके निर्धारित अधिकार तुम नहीं देते और उनसे विवाह करने की रुची रखते हो तथा उन बच्चों के बारे में भी, जो निर्बल हैं तथा (ये भी आदेश देता है कि) अनाथों के लिए न्याय पर स्थिर रहो[ तथा तुम जो भी भलाई करते हो, अल्लाह उसे भली-भाँति जानता है। ');
INSERT INTO "al_umari" VALUES(4,128,'4:128','और यदि किसी स्त्री को अपने पति से दुर्व्यवहार अथवा विमुख होने की शंका हो, तो उन दोनों पर कोई दोष नहीं कि आपस में कोई संधि कर लें और संधि कर लेना ही अच्छा[ है। लोभ तो सभी में होता है। यदि तुम एक-दूसरे के साथ उपकार करो और (अल्लाह से) डरते रहो, तो निःसंदेह तुम जो कुछ कर रहे हो, अल्लाह उससे सूचित है। ');
INSERT INTO "al_umari" VALUES(4,129,'4:129','और यदि तुम अपनी पत्नियों के बीच न्याय करना चाहो, तो भी ऐसा कदापि नहीं कर[ सकोगे। अतः एक ही की ओर पूर्णतः झुक न जाओ और (शेष को) बीच में लटकी हुई न छोड़ दो और यदि (अपने व्यवहार में) सुधार रखो और (अल्लाह से) डरते रहो, तो निःसंदेह अल्लाह अति क्षमाशील दयावन् है।');
INSERT INTO "al_umari" VALUES(4,130,'4:130','और यदि दोनों अलग हो जायें, तो अल्लाह प्रत्येक को अपनी दया से (दूसरे से) निश्चिंत[ कर देगा और अल्लाह बड़ा उदार तत्वज्ञ है। ');
INSERT INTO "al_umari" VALUES(4,131,'4:131','तथा अल्लाह ही का है, जो आकाशों और धरती में है और हमने तुमसे पूर्व अह्ले किताब को तथा (खुद) तुम्हें आदेश दिया है कि अल्लाह से डरते रहो। यदि तुम कुफ्र (अवज्ञा) करोगे, तो निःसंदेह जो कुछ आकाशों तथा धरती में है, वह अल्लाह ही का है तथा अल्लाह निस्पृह[ प्रशंसित है। ');
INSERT INTO "al_umari" VALUES(4,132,'4:132','तथा अल्लाह ही का है, जो कुछ आकाशों तथा धरती में है और अल्लाह काम बनाने के लिए बस है।');
INSERT INTO "al_umari" VALUES(4,133,'4:133','और वह चाहे तो, हे लोगो! तुम्हें ले जाये[ और तुम्हारे स्थान पर दूसरों को ला दे तथा अल्लाह ऐसा कर सकता है। ');
INSERT INTO "al_umari" VALUES(4,134,'4:134','जो सांसारिक प्रतिकार (बदला) चाहता हो, तो अल्लाह के पास संसार तथा परलोक दोनों का प्रतिकार (बदला) है तथा अल्लाह सबकी बात सुनता और सबके कर्म देख रहा है।');
INSERT INTO "al_umari" VALUES(4,135,'4:135','हे ईमान वालो! न्याय के साथ खड़े रहकर अल्लाह के लिए साक्षी (गवाह) बन जाओ। यद्यपि साक्ष्य (गवाही) तुम्हारे अपने अथवा माता पिता और समीपवर्तियों के विरुध्द हो, यदि कोई धनी अथवा निर्धन हो, तो अल्लाह तुमसे अधिक उन दोनों का हितैषी है। अतः अपनी मनोकांक्षा के लिए न्याय से न फिरो। यदि तुम बात घुमा फिरा कर करोगे अथवा साक्ष्य देने से कतराओगे, तो निःसंदेह अल्लाह उससे सूचित है, जो तुम करते हो।');
INSERT INTO "al_umari" VALUES(4,136,'4:136','हे ईमान वालो! अल्लाह, उसके रसूल और उस पुस्तक (क़ुर्आन) पर, जो उसने अपने रसूल पर उतारी है तथा उन पुस्तकों पर, जो इससे पहले उतारी हैं, ईमान लाओ। जो अल्लाह, उसके फ़रिश्तों, उसकी पुस्तकों और अन्त दिवस (प्रलय) को अस्वीकार करेगा, वह कुपथ में बहुत दूर जा पड़ेगा।');
INSERT INTO "al_umari" VALUES(4,137,'4:137','निःसंदेह जो ईमान लाये, फिर काफ़िर हो गये, फिर ईमान लाये, फिर काफ़िर हो गये, फिर कुफ़्र में बढ़ते ही चले गये, तो अल्लाह उन्हें कदापि क्षमा नहीं करेगा और न उन्हें सीधी डगर दिखायेगा।');
INSERT INTO "al_umari" VALUES(4,138,'4:138','(हे नबी!) आप मुनाफ़िक़ों (द्विधावादियों) को शुभ सूचना सुना दें कि उन्हीं के लिए दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(4,139,'4:139','जो ईमान वालों को छोड़कर, काफ़िरों को अपना सहायक मित्र बनाते हैं, क्या वे उनके पास मान सम्मान चाहते हैं? तो निःसंदेह सब मान सम्मान अल्लाह ही के लिए[ है। ');
INSERT INTO "al_umari" VALUES(4,140,'4:140','और उस (अल्लाह) ने तुम्हारे लिए अपनी पुस्तक (क़ुर्आन) में ये आदेश उतार[ दिया है कि जब तुम सुनो कि अल्लाह की आयतों को अस्वीकार किया जा रहा है तथा उनका उपहास किया जा रहा है, तो उनके साथ न बैठो, यहाँ तक कि वे दूसरी बात में लग जायें। निःसंदेह, तुम उस समय उन्हीं के समान हो जाओगे। निश्चय अल्लाह मुनाफ़िक़ों (द्विधावादियों) तथा काफ़िरों, सबको नरक में एकत्र करने वाला है। ');
INSERT INTO "al_umari" VALUES(4,141,'4:141','जो तुम्हारी प्रतीक्षा में रहा करते हैं; यदि तुम्हें अल्लाह की सहायता से विजय प्राप्त हो, तो कहते हैं, क्या हम तुम्हारे साथ न थे? और यदि उन (काफ़िरों) का पल्ला भारी रहे, तो कहते हैं कि क्या हम तुमपर छा नहीं गये थे और तुम्हें ईमान वालों से बचा (नहीं) रहे थे? तो अल्लाह ही प्रलय के दिन तुम्हारे बीच निर्णय करेगा और अल्लाह काफ़िरों के लिए ईमान वालों पर कदापि कोई राह नहीं बनायेगा[। ');
INSERT INTO "al_umari" VALUES(4,142,'4:142','वास्तव में, मुनाफ़िक (द्विधावादी) अल्लाह को धोखा दे रहे हैं, जबकी, वही उन्हें धोखे में डाल रहा[ है और जब वे नमाज़ के लिए खड़े होते हैं, तो आलसी होकर खड़े होते हैं, वे लोगों को दिखाते हैं और अल्लाह का स्मरण थोड़ा ही करते हैं। ');
INSERT INTO "al_umari" VALUES(4,143,'4:143','वह इसके बीच द्विधा में पड़े हुए हैं, न इधर न उधर। दरअसल, जिसे अल्लाह कुपथ कर दे, आप उसके लिए कोई राह नहीं पा सकेंगे।');
INSERT INTO "al_umari" VALUES(4,144,'4:144','हे ईमान वालो! ईमान वालों को छोड़कर काफ़िरों को सहायक मित्र न बनाओ। क्या तुम अपने विरुध्द अल्लाह के लिए खुला तर्क बनाना चाहते हो?');
INSERT INTO "al_umari" VALUES(4,145,'4:145','निश्चय मुनाफ़िक़ (द्विधावादी) नरक की सबसे नीची श्रेणी में होंगे और आप उनका कोई सहायक नहीं पायेंगे।');
INSERT INTO "al_umari" VALUES(4,146,'4:146','परन्तु जिन्होंने क्षमा याचना कर ली, अपना सुधार कर लिया, अल्लाह को सुदृढ़ पकड़ लिया और अपने धर्म को विशुध्द कर लिया, तो वे लोग ईमान वालों के साथ होंगे और अल्लाह ईमान वालों को बहुत बड़ा प्रतिफल प्रदान करेगा।');
INSERT INTO "al_umari" VALUES(4,147,'4:147','अल्लाह को क्या पड़ी है कि तुम्हें यातना दे, यदि तुम कृतज्ञ रहो तथा ईमान रखो और अल्लाह[ बड़ा गुणग्राही अति ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(4,148,'4:148','अल्लाह को अपशब्द (बुरी बात) की चर्चा नहीं भाती, परन्तु जिसपर अत्याचार किया गया[ हो और अल्लाह सब सुनता और जानता है। ');
INSERT INTO "al_umari" VALUES(4,149,'4:149','यदि तुमकोई भली बात खुल कर करो, उसे छुपाकर करो या किसी बुराई को क्षमा कर दो, (याद रखो कि) निःसंदेह अललाह अति क्षमी, सर्व शक्तिशाली है।');
INSERT INTO "al_umari" VALUES(4,150,'4:150','जो लोग अल्लाह और उसके रसूलों के साथ (अविश्वास) कुफ़्र करते हैं और चाहते हैं कि अल्लाह तथा उसके रसूलों के बीच अन्तर करें तथा कहते हैं कि हम कुछ पर ईमान रखते हैं और कुछ के साथ कुफ़्र करते हैं और इसके बीच राह[ अपनाना चाहते हैं। ');
INSERT INTO "al_umari" VALUES(4,151,'4:151','वही शुध्द काफ़िर हैं और हमने काफ़िरों के लिए अपमानकारी यातना तैयार कर रखी है।');
INSERT INTO "al_umari" VALUES(4,152,'4:152','तथा जो लोग अल्लाह और उसके रसूलों पर ईमान लाये और उनमें से किसी के बीच अन्तर नहीं किया, तो उन्हीं को, हम उनका प्रतिफल प्रदान करेंगे तथा अल्लाह अति क्षमाशील दयावान् है।');
INSERT INTO "al_umari" VALUES(4,153,'4:153','(हे नबी!) आपसे अह्ले किताब माँग करते हैं कि आप उनपर आकाश से कोई पुस्तक उतार दें, तो इन्होंने मूसा से इससे भी बड़ी माँग की थी; उन्होंने कहा था कि हमें अल्लाह को प्रत्यक्ष[ दिखा दो, तो इनके अत्याचारों के कारण, इन्हें बिजली ने धर लिया, फिर इन्होंने खुली निशानियाँ आने के पश्चात् बछड़े को पूज्य बना लिया, फिर हमने इसे भी क्षमा कर दिया और हमने मूसा को खुला प्रभुत्व प्रदान किया। ');
INSERT INTO "al_umari" VALUES(4,154,'4:154','और हमने (उनसे वचन लेने के लिए) उनके ऊपर तूर (पर्वत) उठा दिया तथा हमने उनसे कहाः द्वार में सज्दा करते हुए प्रवेश करो तथा हमने उनसे कहा कि शनिवार[ के विषय में अति न करो और हमने उनसे दृढ़ वचन लिया। ');
INSERT INTO "al_umari" VALUES(4,155,'4:155','तो उनके अपना वचन भंग करने, उनके अल्लाह की आयतों के साथ कुफ़्र करने, उनके नबियों को अवैध वध करने तथा उनके ये कहने के कारण कि हमारे दिल बंद हैं। ( ऐसी बात नहीं है) बल्कि अल्लाह ने उनके दिलों पर मुहर लगा दी है। अतः इनमें से थोड़े ही इमान लायेंगे।');
INSERT INTO "al_umari" VALUES(4,156,'4:156','तथा उनके कुफ़्र और मर्यम पर घोर आरोप लगाने के कारण।');
INSERT INTO "al_umari" VALUES(4,157,'4:157','तथा उनके (गर्व से) कहने के कारण कि हमने अल्लाह के रसूल, मर्यम के पुत्र, ईसा मसीह़ को वध कर दिया, जबकि (वास्तव में) उसे वध नहीं किया और न सलीब (फाँसी) दी, परन्तु उनके लिए (इसे) संदिग्ध कर दिया गया। निःसंदेह, जिन लोगों ने इसमें विभेद किया, वे भी शंका में पड़े हुए हैं और उन्हें इसका कोई ज्ञान नहीं, केवल अनुमान के पीछे पड़े हुए हैं और निश्चय उसे उन्होंने वध नहीं किया है।');
INSERT INTO "al_umari" VALUES(4,158,'4:158','बल्कि अल्लाह ने उसे अपनी ओर आकाश में उठा लिया है तथा अल्लाह प्रभुत्वशाली तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(4,159,'4:159','और सभी अह्ले किताब उस (ईसा) के मरण से पहले उसपर अवश्य ईमान[ लायेंगे और प्रलय के दिन वह उनके विरुध्द साक्षी होगा।');
INSERT INTO "al_umari" VALUES(4,160,'4:160','यहूदियों के (इसी) अत्याचार के कारण हमने उनपर स्वच्छ खाद्य पदार्थों को ह़राम (वर्जित) कर दिया, जो उनके लिए ह़लाल (वैध) थे तथा उनके बहुधा अल्लाह की राह से रोकने के कारण।');
INSERT INTO "al_umari" VALUES(4,161,'4:161','तथा उनके ब्याज लेने के कारण जबकि उन्हें उससे रोका गया था और उनके लोगों का धन अवैध रूप से खाने के कारण। हमने उनमें से काफ़िरों के लिए दुःखदायी यातना तैयार कर रखी है।');
INSERT INTO "al_umari" VALUES(4,162,'4:162','परन्तु जो उनमें से ज्ञान में पक्के हैं तथा वे ईमान वाले, जो आपकी ओर उतारी गयी (पुस्तक क़ुर्आन) तथा आपसे पूर्व उतारी गई (पुस्तक) पर ईमान रखते हैं और जो नमाज़ की स्थापना करने वाले, ज़कात देने वाले और अल्लाह तथा अन्तिम दिन पर ईमान रखने वाले हैं, उन्हीं को हम बहुत बड़ा प्रतिफल प्रदान करेंगे।');
INSERT INTO "al_umari" VALUES(4,163,'4:163','(हे नबी!) हमने आपकी ओर वैसे ही वह़्यी भेजी है, जैसे नूह़ और उसके पश्चात के नबियों के पास भेजी और इब्राहीम, इस्माईल, इस्ह़ाक़, याक़ूब तथा उसकी संतान, ईसा, अय्यूब, यूनुस, हारून और सुलैमान के पास वह़्यी भेजी और हमने दावूद को ज़बूर प्रदान[ की थी। ');
INSERT INTO "al_umari" VALUES(4,164,'4:164','कुछ रसूल तो ऐसे हैं, जिनकी चर्चा हम इससे पहले आपसे कर चुके हैं और कुछ की चर्चा आपसे नहीं की है और अल्लाह ने मूसा से वास्तव में बात की।');
INSERT INTO "al_umari" VALUES(4,165,'4:165','ये सभी रसूल शुभ सूचना सुनाने वाले और डराने वाले थे, ताकि इन रसूलों के (आगमन के) पश्चात् लोगों के लिए अल्लाह पर कोई तर्क न रह[ जाये और अल्लाह प्रभुत्वशाली तत्वज्ञ है। ');
INSERT INTO "al_umari" VALUES(4,166,'4:166','(हे नबी!) (आपको यहूदी आदि नबी न मानें) परन्तु अल्लाह उस (क़ुर्आन) के द्वारा, जिसे आपपर उतारा है, साक्ष्य (गवाही) देता है (कि आप नबी हैं)। उसने इसे अपने ज्ञान के साथ उतारा है तथा फ़रिश्ते साक्ष्य देते हैं और अल्लाह का साक्ष्य ही बहुत है।');
INSERT INTO "al_umari" VALUES(4,167,'4:167','वास्तव में, जिन्होंने कुफ़्र किया और अल्लाह की राह[ से रोका, वे सुपथ से बहुत दूर जा पड़े। ');
INSERT INTO "al_umari" VALUES(4,168,'4:168','निःसंदेह जो काफ़िर हो गये और अत्याचार करते रह गये, तो अल्लाह ऐसा नहीं है कि उन्हें क्षमा कर दे तथा न उन्हें कोई राह दिखायेगा।');
INSERT INTO "al_umari" VALUES(4,169,'4:169','परन्तु नरक की राह, जिसमें वे सदावासी होंगे और ये अल्लाह के लिए सरल है।');
INSERT INTO "al_umari" VALUES(4,170,'4:170','हे लोगो! तुम्हारे पास तुम्हारे पालनहार की ओर से रसूल सत्य लेकर[ आ गये हैं। अतः, उनपर ईमान लाओ, यही तुम्हारे लिए अच्छा है तथा यदि कुफ़्र करोगे, तो (याद रखो कि) अल्लाह ही का है, जो आकाशों तथा धरती में है और अल्लाह बड़ा ज्ञानी गुणी है। ');
INSERT INTO "al_umari" VALUES(4,171,'4:171','हे अह्ले किताब (ईसाईयो!) अपने धर्म में अधिकता न[ करो और अल्लाह पर केवल सत्य ही बोलो। मसीह़ मर्यम का पुत्र केवल अल्लाह का रसूल और उसका शब्द है, जिसे (अल्लाह ने) मर्यम की ओर डाल दिया तथा उसकी ओर से एक आत्मा है, अतः, अल्लाह और उसके रसूलों पर ईमान लाओ और ये न कहो कि (अल्लाह) तीन हैं, इससे रुक जाओ, यही तुम्हारे लिए अच्छा है, इसके सिवा कुछ नहीं कि अल्लाह ही अकेला पूज्य है, वह इससे पवित्र है कि उसका कोई पुत्र हो, आकाशों तथा धरती में जो कुछ है, उसी का है और अल्लाह काम बनाने के लिए बहुत है।');
INSERT INTO "al_umari" VALUES(4,172,'4:172','मसीह़ कदापि अल्लाह का दास होने को अपमान नहीं समझता और न (अल्लाह के) समीपवर्ती फ़रिश्ते। जो व्यक्ति उसकी (वंदना को) अपमान समझेगा तथा अभिमान करेगा, तो उन सभी को वह अपने पास एकत्र करेगा।');
INSERT INTO "al_umari" VALUES(4,173,'4:173','फिर जो लोग ईमान लाये तथा सत्कर्म किये, तो उन्हें उनका भरपूर प्रतिफल देगा और उन्हें अपनी दया से अधिक भी देगा।[ परन्तु जिन्होंने (वंदना को) अपमान समझा और अभिमान किया, तो उन्हें दुःखदायी यातना देगा तथा अल्लाह के सिवा वह कोई रक्षक और सहायक नहीं पायेगा। ');
INSERT INTO "al_umari" VALUES(4,174,'4:174','हे लोगो! तुम्हारे पास तुम्हारे पालनहार की ओर से खुला प्रमाण[ आ गया है और हमने तुम्हारी ओर खुली वह़्यी उतार दी है।');
INSERT INTO "al_umari" VALUES(4,175,'4:175','तो जो लोग अल्लाह पर ईमान लाये तथा इस (क़ुर्आन) को दृढ़ता से पकड़ लिया, वे उन्हीं को अपनी दया तथा अनुग्रह से (स्वर्ग) में प्रवेश देगा और उन्हें अपनी ओर सीधी राह दिखा देगा।');
INSERT INTO "al_umari" VALUES(4,176,'4:176','(हे नबी!) वे आपसे कलाला के विषय में आदेश चाहते हैं, तो आप कह दें कि वह कलाला के विषय में तुम्हें आदेश दे रहा है कि यदि कोई ऐसा पुरुष मर जाये, जिसके संतान न हों, (और न पिता-दादा हो।) और उसके एक बहन हों, तो उसके लिए उसके छोड़े हुए धन का आधा है और वह (पुरुष) उसके पूरे धन का वारिस होगा, यदि उस (बहन) के कोई संतान न हों, (और न पिता और दादा हो)। और यदि उसकी दो (अथवा अधिक) बहनें हों, तो उन्हें छोड़े हुए धन का दो तिहाई मिलेगा। यदि भाई-बहन दोनों हों, तो नर (भाई) को दो नारियों (बहनों) के बराबर[ (भाग) मिलेगा। अल्लाह तुम्हारे लिए आदेश उजागर कर रहा है, ताकि तुम कुपथ न हो जाओ तथा अल्लाह सब कुछ जानता है। ');
INSERT INTO "al_umari" VALUES(5,1,'5:1','हे वो लोगो जो ईमान लाये हो! प्रतिबंधों का पूर्ण रूप[ से पालन करो। तुम्हारे लिए सब पशु ह़लाल (वैध) कर दिये गये, परन्तु, जिनका आदेश तुम्हें सुनाया जायेगा, लेकिन एह़राम की स्थिति में शिकार न करो। बेशक अल्लाह जो आदेश चाहता है, देता है।');
INSERT INTO "al_umari" VALUES(5,2,'5:2','हे ईमान वालो! अल्लाह की निशानियों[ (चिन्हों) का अनादर न करो, न सम्मानित मासों का , न (ह़ज की) क़ुर्बानी का, न उन (ह़ज की) क़ुर्बानियों का, जिनके गले में पट्टे पड़े हों और न उनका, जो अपने पालनहार की अनुग्रह और उसकी प्रसन्नता की खोज में सम्मानित घर (काबा) की ओर जा रहे हों। जब एह़राम खोल दो, तो शिकार कर सकते हो। तुम्हें किसी गिरोह की शत्रुता इस बात पर न उभार दे कि अत्याचार करने लगो, क्योंकि उन्होंने मस्जिदे-ह़राम से तुम्हें रोक दिया था, सदाचार तथा संयम में एक-दूसरे की सहायता करो तथा पाप और अत्याचार में एक-दूसरे की सहायता न करो और अल्लाह से डरते रहो। निःसंदेह अल्लाह कड़ी यातना देने वाला है।');
INSERT INTO "al_umari" VALUES(5,3,'5:3','तुमपर मुर्दार[ ह़राम (अवैध) कर दिया गया है तथा (बहता हुआ) रक्त, सूअर का मांस, जिसपर अल्लाह से अन्य का नाम पुकारा गया हो, जो श्वास रोध और आघात के कारण, गिरकर और दूसरे के सींग मारने से मरा हो, जिसे हिंसक पशु ने खा लिया हो, -परन्तु इनमें से जिसे तुम वध (ज़िब्ह) कर लो- जिसे थान पर वध किया गया हो और ये कि पाँसे द्वारा अपना भाग निकालो। ये सब आदेश-उल्लंघन के कार्य हैं। आज काफ़िर तुम्हारे धर्म से निराश हो गये हैं। अतः, उनसे न डरो, मुझी से डरो। आज मैंने तुम्हारा धर्म तुम्हारे लिए परिपूर्ण कर दिय है तथा तुमपर अपना पुरस्कार पूरा कर दिया और तुम्हारे लिए इस्लाम को धर्म स्वरूप स्वीकार कर लिया है। फिर जो भूक से आतुर हो जाये, जबकि उसका झुकाव पाप के लिए न हो,(प्राण रक्षा के लिए खा ले) तो निश्चय अल्लाह अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(5,4,'5:4','वे आपसे प्रश्न करते हैं कि उनके लिए क्या ह़लाल (वैध) किया गया? आप कह दें कि सभी स्वच्छ पवित्र चीजें तुम्हारे लिए ह़लाल कर दी गयी हैं। और उन शिकारी जानवरों का शिकार जिन्हें तुमने उस ज्ञान द्वारा जो अल्लाह ने तुम्हें दिया है, उसमें से कुछ सिखाकर सधाया हो। तो जो (शिकार) वह तुमपर रोक दें उसमें से खाओ और उसपर अल्लाह का नाम[ लो तथा अल्लाह से डरते रहो। निःसंदेह, अल्लाह शीघ्र ह़िसाब लेने वाला है। ');
INSERT INTO "al_umari" VALUES(5,5,'5:5','आज सब स्वच्छ खाद्य तुम्हारे लिए ह़लाल (वैध) कर दिये गये हैं और ईमान वाली सतवंती स्त्रियाँ तथा उनमें से सतवंती स्त्रियाँ, जो तुमसे पहले पुस्तक दिये गये हैं, जबकि उन्हें उनका महर (विवाह उपहार) चुका दो, विवाह में लाने के लिए, व्यभिचार के लिए नहीं और न प्रेमिका बनाने के लिए। जो ईमान को नकार देगा, उसका सत्कर्म व्यर्थ हो जायेगा तथा परलोक में वह विनाशों में होगा।');
INSERT INTO "al_umari" VALUES(5,6,'5:6','हे ईमान वालो! जब नमाज़ के लिए खड़े हो, तो (पहले) अपने मुँह तथा हाथों को कुहनियों तक धो लो और अपने सिरों का मसह़[ कर लो तथा अपने पावों को टखनों तक (धो लो) और यदि जनाबत की स्थिति में हो, तो (स्नान करके) पवित्र हो जाओ तथा यदि रोगी अथवा यात्रा में हो अथवा तुममें से कोई शोच से आये अथवा तुमने स्त्रियों को स्पर्श किया हो और तुम जल न पाओ, तो शुध्द धूल से तयम्मुम कर लो और उससे अपने मुखों तथा हाथों का मसह़ कर लो। अल्लाह तुम्हारे लिए कोई संकीर्णता (तंगी) नहीं चाहता। परन्तु तुम्हें पवित्र करना चाहता है और ताकि तुमपर अपना पुरस्कार पूरा कर दे और ताकि तुम कृतज्ञ बनो।');
INSERT INTO "al_umari" VALUES(5,7,'5:7','तथा अपने ऊपर अल्लाह के पुरस्कार और उस दृढ़ वचन को याद करो, जो तुमसे लिया है। जब तुमने कहाः हमने सुन लिया और आज्ञाकारी हो गये तथा (सुनो!) अल्लाह से डरते रहो। निःसंदेह अल्लाह दिलों के भेदों को भली-भाँति जानने वाला है।');
INSERT INTO "al_umari" VALUES(5,8,'5:8','हे ईमान वालो! अल्लाह के लिए खड़े रहने वाले, न्याय के साथ साक्ष्य देने वाले रहो तथा किसी गिरोह की शत्रुता तुम्हें इसपर न उभार दे कि न्याय न करो। वह (अर्थातः सबके साथ न्याय) अल्लाह से डरने के अधिक समीप[ है। निःसंदेह तुम जो कुछ करते हो, अल्लाह उससे भली-भाँति सूचित है। ');
INSERT INTO "al_umari" VALUES(5,9,'5:9','जो लोग ईमान लाये तथा सत्कर्म किये, तो उनसे अल्लाह का वचन है कि उनके लिए क्षमा तथा बड़ा प्रतिफल है।');
INSERT INTO "al_umari" VALUES(5,10,'5:10','तथा जो काफ़िर रहे और हमारी आयतों को मिथ्या कहा, तो वही लोग नारकी हैं।');
INSERT INTO "al_umari" VALUES(5,11,'5:11','हे ईमान वालो! अल्लाह के उस उपकार को याद करो, जब एक गिरोह ने तुम्हारी ओर हाथ बढ़ाना[ चाहा, तो अल्लाह ने उनके हाथों को तुमसे रोक दिया तथा अल्लाह से डरते रहो और ईमान वालों को अल्लाह ही पर निरभर करना चाहिए। ');
INSERT INTO "al_umari" VALUES(5,12,'5:12','तथा अल्लाह ने बनी इस्राईल से (भी) दृढ़ वचन लिया था और उनमें बारह प्रमुख नियुक्त कर दिये थे तथा अल्लाह ने कहा था कि मैं तुम्हारे साथ हूँ, यदि तुम नमाज की स्थापना करते रहे, ज़कात देते रहे, मेरे रसूलों पर ईमान (विश्वास) रखे रहे, उन्हें समर्थन देते रहे तथा अल्लाह को उत्तम ऋण देते रहे। (अगर ऐसा हुआ) तो मैं अवश्य तुम्हें तुम्हारे पाप क्षमा कर दूँगा और तुम्हें ऐसे स्वर्गों में प्रवेश दूँगा, जिनमें नहरें प्रवाहित होंगी और तुममें से जो इसके पश्चात् भी कुफ़्र (अविश्वास) करेगा, (दरअसल) वह सुपथ[ से विचलित हो गया। ');
INSERT INTO "al_umari" VALUES(5,13,'5:13','तो उनके अपना वचन भंग करने के कारण, हमने उन्हें धिक्कार दिया और उनके दिलों को कड़ा कर दिया। वे अल्लाह की बातों को, उनके वास्तविक स्थानों से फेर देते[ हैं तथा जिस बात का उन्हें निर्देश दिया गया था, उसे भुला दिया और (अब) आप बराबर उनके किसी न किसी विश्वासघात से सूचित होते रहेंगे, परन्तु उनमें बहुत थोड़े के सिवा, जो ऐसा नहीं करते। अतः आप उन्हें क्षमा कर दें और उन्हें जाने दें। निःसंदेह अल्लाह उपकारियों से प्रेम करता है। ');
INSERT INTO "al_umari" VALUES(5,14,'5:14','तथा जिन्होंने कहा कि हम नसारा (ईसाई) हैं, हमने उनसे (भी) दृढ़ वचन लिया था, तो उन्हें जिस बात का निर्देश दिया गया था, उसे भुला बैठे, तो प्रलय के दिन तक के लिए हमने उनके बीच शत्रुता तथा पारस्परिक (आपसी) विद्वेष भड़का दिया और शीघ्र ही अल्लाह जो कुछ वे करते रहे हैं, उन्हें[ बता देगा। ');
INSERT INTO "al_umari" VALUES(5,15,'5:15','हे अह्ले किताब! तुम्हारे पास हमारे रसूल आ गये हैं[, जो तुम्हारे लिए उन बहुत सी बातों को उजागर कर रहे हैं, जिन्हें तुम छुपा रहे थे और बहुत सी बातों को छोड़ भी रहे हैं। अब तुम्हारे पास अल्लाह की ओर से प्रकाश तथा खुली पुस्तक (कुर्आन) आ गई है। ');
INSERT INTO "al_umari" VALUES(5,16,'5:16','जिसके द्वारा अल्लाह उन्हें शान्ति का मार्ग दिखा रहा है, जो उसकी प्रसन्नता पर चलते हों, उन्हें अपनी अनुमति से अंधेरों से निकालकर प्रकाश की ओर ले जाता है और उन्हें सुपथ दिखाता है।');
INSERT INTO "al_umari" VALUES(5,17,'5:17','निश्चय वे काफ़िर[ हो गये, जिन्होंने कहा कि मर्यम का पुत्र मसीह़ ही अल्लाह है। (हे नबी!) उनसे कह दो कि यदि अल्ललाह मर्यम के पुत्र और उसकी माता तथा जो भी धरती में है, सबका विनाश कर देना चाहे, तो किसमें शक्ति है कि वह उसे रोक दे? तथा आकाश और धरती और जो भी इनके बीच है, सब अल्लाह ही का राज्य है, वह जो चाहे, उतपन्न करता है तथा वह जो चाहे, कर सकता है। ');
INSERT INTO "al_umari" VALUES(5,18,'5:18','तथा यहूदी और ईसाईयों ने कहा कि हम अल्लाह के पुत्र तथा प्रियवर हैं। आप पूछें कि फिर वह तुम्हें तुम्हारे पापों का दण्ड क्यों देता है? बल्कि तुमभी वैसे ही मानव पूरुष हो, जैसे दूसरे हैं, जिनकी उत्पत्ति उसने की है। वह जिसे चाहे, क्षमा कर दे और जिसे चाहे, दण्ड दे तथा आकाश और धरती तथा जो उन दोनों के बीच है, अल्लाह ही का राज्य (अधिपत्य)[ है और उसी की ओर सबको जाना है। ');
INSERT INTO "al_umari" VALUES(5,19,'5:19','हे अह्ले किताब! तुम्हारे पास रसुलों के आने का क्रम बंद होने के पश्चात्, हमारे रसूल आ गये[ हैं, वह तुम्हारे लिए (सत्य को) उजागर कर रहे हैं, ताकि तुम ये न कहो कि हमारे पास कोई शुभ सूचना सुनाने वाला तथा सावधान करने वाला (नबी) नहीं आया, तो तुम्हारे पास शुभ सूचना सुनाने तथा सावधान करने वाला आ गया है तथा अल्लाह जो चाहे, कर सकता है। ');
INSERT INTO "al_umari" VALUES(5,20,'5:20','तथा याद करो, जब मूसा ने अपनी जाति से कहाः हे मेरी जाति! अपने ऊपर अल्लाह के पुरस्कार को याद करो कि उसने तुममें नबी और शासक बनाये तथा तुम्हें वह कुछ दिया, जो संसार वासियों में किसी को नहीं दिया।');
INSERT INTO "al_umari" VALUES(5,21,'5:21','हे मेरी जाति! उस पवित्र धरती (बैतुल मक़्दिस) में प्रवेश कर जाओ, जिसे अल्लाह ने तुम्हारे लिए लिख दिया है और पीछे न फिरो, अन्यथा असफल हो जाओगे।');
INSERT INTO "al_umari" VALUES(5,22,'5:22','उन्होंने कहाः हे मूसा! उसमें बड़े बलवान लोग हैं और हम उसमें कदापि प्रवेश नहीं करेंगे, जब तक वे उससे निकल न जायें, यदि वे निकल जाते हैं, तभी हम उसमें प्रवेश कर सकते हैं।');
INSERT INTO "al_umari" VALUES(5,23,'5:23','उनमें से दो व्यक्तियों ने, जो (अल्लाह से) डरते थे, जिनपर अल्लाह ने पुरस्कार किया था, कहा कि उनपर द्वार से प्रवेश कर जाओ, जबतुम उसमें प्रवेश कर जाओगे, तो निश्चय तुम प्रभुत्वशाली होगे तथा अल्लाह ही पर भरोसा करो, यदि तुम ईमान वाले हो।');
INSERT INTO "al_umari" VALUES(5,24,'5:24','वे बोलेः हे मूसा! हम उसमें कदापि प्रवेश नहीं करेंगे, जब तक वे उसमें (उपस्थित) रहेंगे, अतः तुम और तुम्हारा पालनहार जाओ, फिर तुम दोनों युध्द करो, हम यहीं बैठे रहेंगे।');
INSERT INTO "al_umari" VALUES(5,25,'5:25','(ये दशा देखकर) मूसा ने कहः हे मेरे पालनहार! मैं अपने और अपने भाई के सिवा किसी पर कोई अधिकार नहीं रखता। अतः तु हमारे तथा अवज्ञाकारी जाति के बीच निर्णय कर दे।');
INSERT INTO "al_umari" VALUES(5,26,'5:26','अल्लाह ने कहाः वह (धरती) उनपर चालीस वर्ष के लिए ह़राम (वर्जित) कर दी गई। वे धरती में फिरते रहेंगे, अतः तुम अवज्ञाकारी जाति पर तरस न खाओ[। ');
INSERT INTO "al_umari" VALUES(5,27,'5:27','तथा उनेहें आदम के दो पुत्रों का सही समाचार[ सुना दो, जब दोनों ने एक उपायन (क़ुर्बानी) प्रस्तुत की, तो एक से स्वीकार की गई तथा दूसरे से स्वीकार नहीं की गई। उस (दूसरे) ने कहाः मैं अवश्य तेरी हत्या कर दूँगा। उस (प्रथम) ने कहाः अल्लाह आज्ञाकारों ही से स्वीकार करता है। ');
INSERT INTO "al_umari" VALUES(5,28,'5:28','यदि तुम मेरी हत्या करने के लिए मेरी ओर हाथ बढ़ाओगे[, तो भी मैं तुम्हारी ओर तुम्हारी हत्या करने के लिए हाथ बढ़ाने वाला नहीं हूँ। मैं विश्व के पालनहार अल्लाह से डरता हूँ। ');
INSERT INTO "al_umari" VALUES(5,29,'5:29','मैं चाहता हूँ कि तुम मेरी (हत्या के) पाप और अपने पाप के साथ फिरो और नारकी हो जाओ और यही अत्याचारियों का प्रतिकार (बदला) है।');
INSERT INTO "al_umari" VALUES(5,30,'5:30','अंततः, उसने स्वयं को अपने भाई की हत्या पर तैयार कर लिया और विनाशों में हो गया।');
INSERT INTO "al_umari" VALUES(5,31,'5:31','फिर अल्लाह ने एक कौआ भेजा, जो भूमि कुरेद रहा था, ताकि उसे दिखाये कि अपने भाई के शव को कैसे छुपाये, उसने कहाः मुझपर खेद है! क्या मैं इस कौआ जैसा भी न हो सका कि अपने भाई का शव छुपा सकूँ, फिर बड़ा लज्जित हूआ।');
INSERT INTO "al_umari" VALUES(5,32,'5:32','इसी कारण हमने बनी इस्राईल पर लिख दिया[ कि जिसने भी किसी प्राणी की हत्या की, किसी प्राणी का ख़ून करने अथवा धरती में विद्रोह के बिना, तो समझो उसने पूरे मनुष्यों की हत्या कर दी और जिसने जीवित रखा एक प्राणी को, तो वास्तव में, उसने जीवित रखा सभी मनुष्यों को तथा उनके पास हमारे रसूल खुली निशानियाँ लाये, फिर भी उनमें से अधिकांश धरती में विद्रोह करने वाले हैं।');
INSERT INTO "al_umari" VALUES(5,33,'5:33','जो लोग[ अल्लाह और उसके रसूल से युध्द करते हों तथा धरती में उपद्रव करते फिर रहे हों, उनका दण्ड ये है कि उनकी हत्या की जाये तथा उन्हें फाँसी दी जाये अथवा उनके हाथ-पाँव विपरीत दिशाओं से काट दिये जायें अथवा उन्हें देश निकाला दे दिया जाये। ये उनके लिए संसार में अपमान है तथा परलोक में उनके लिए इससे बड़ा दण्ड है।');
INSERT INTO "al_umari" VALUES(5,34,'5:34','परन्तु जो तौबा (क्षमा याचना) कर लें, इससे पहले कि तुम उन्हें अपने नियंत्रण में लाओ, तो तुम जान लो कि अल्लाह अति क्षमाशील दयावान् है।');
INSERT INTO "al_umari" VALUES(5,35,'5:35','हे ईमान वालो! अल्लाह (की अवज्ञा) से डरते रहो और उसकी ओर वसीला[ खोजो तथा उसकी राह में जिहाद करो, ताकी तुम सफल हो जाओ। ');
INSERT INTO "al_umari" VALUES(5,36,'5:36','जो लोग काफ़िर हैं, यद्यपि धरती के सभी (धन-धान्य) उनके अधिकार (स्वामित्व) में आ जायें और उसी के समान और भी हो, ताकि वे, ये सब प्रलय के दिन की यातना से अर्थ दण्ड स्वरूप देकर मुक्त हो जायें, तो भी उनसे स्वीकार नहीं किया जायेगा और उन्हें दुखदायी यातना होगी।');
INSERT INTO "al_umari" VALUES(5,37,'5:37','वे चाहेंगे कि नरक से निकल जायें, जबकि वे उससे निकल नहीं सकेंगे और उन्हीं के लिए स्थायी यातना है।');
INSERT INTO "al_umari" VALUES(5,38,'5:38','चोर, पुरुष और स्त्री दोनों के हाथ काट दो, उनके करतूत के बदले, जो अल्लाह की ओर से शिक्षाप्रद दण्ड है[ और अल्लाह प्रभावशाली गुणी है। ');
INSERT INTO "al_umari" VALUES(5,39,'5:39','फिर जो अपने अत्याचार (चोरी) के पश्चात् तौबा (क्षमा याचना) कर ले और अपने को सुधार ले, तो अल्लाह उसकी तौबा स्वीकार कर लेगा[। निःसंदेह अल्लाह अति क्षमाशील दयावान् है। ');
INSERT INTO "al_umari" VALUES(5,40,'5:40','क्या तुम जानते नहीं कि अल्लाह ही के लिए है, आकाशों तथा धरती का राज्य। वह जिसे चाहे, क्षमा कर दे और जिसे चाहे, दण्ड दे तथा अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(5,41,'5:41','हे नबी! वे आपको उदासीन न करें, जो कुफ़्र में तीव्र गामी हैं; उनमें से जिन्होंने कहा कि हम ईमान लाये, जबकि उनके दिल ईमान नहीं लाये और उनमें से जो यहूदी हैं, जिनकी दशा ये है कि मिथ्या बातें सुनने के लिए कान लगाये रहते हैं तथा दूसरों के लिए, जो आपके पास नहीं आये, कान लगाये रहते हैं। वे शब्दों को उनके निश्चित स्थानों के पश्चात् वास्तविक अर्थों से फेर देते हैं। वे कहते हैं कि यदि तुम्हें यही आदेश दिया जाये (जो हमने बताया है) तो मान लो और यदि वह न दिये जाओ, तो उससे बचो। (हे नबी!) जिसे अल्लाह अपनी परीक्षा में डालना चाहे, आप उसे अल्लाह से बचाने के लिए कुछ नहीं कर सकते। यही वे हैं, जिनके दिलों को अल्लाह ने पवित्र करना नहीं चाहा। उन्हीं के लिए संसार में अपमान है और उन्हीं के लिए परलोक में घोर[ यातना है। ');
INSERT INTO "al_umari" VALUES(5,42,'5:42','वे मिथ्या बातें सुनने वाले, अवैध भक्षी हैं। अतः यदि वे आपके पास आयें, तो आप उनके बीच निर्णय कर दें अथवा उनसे मुँह फेर लें (आपको अधिकार है)। और यदि आप उनसे मुँह फेर लें, तो वे आपको कोई हाणि नहीं पहुँचा सकेंगे और यदि निर्णय करें, तो न्याय के साथ निर्णय करें। निःसंदेह अल्लाह न्यायकारियों से प्रेम करता है।');
INSERT INTO "al_umari" VALUES(5,43,'5:43','और वे आपको निर्णयकारी कैसे बना सकते हैं, जबकि उनके पास तौरात (पुस्तक) मौजूद है, जिसमें अल्लाह का आदेश है, फिर इसके पश्चात उससे मुँह फेर रहे हैं? वास्तव में, वे ईमान वाले हैं ही[ नहीं। ');
INSERT INTO "al_umari" VALUES(5,44,'5:44','निःसंदेह, हमने ही तौरात उतारी, जिसमें मार्गदर्शन तथा प्रकाश है, जिसके अनुसार वो नबी निर्णय करते रहे, जो आज्ञाकारी थे, उनके लिए जो यहूदी थे तथा धर्माचारी और विद्वान लोग, क्योंकि वे अल्लाह की पुस्तक के रक्षक बनाये गये थे और उसके (सत्य होने के) साक्षी थे। अतः, तुमभी लोगों से न डरो, मुझी से डरो और मेरी आयतों के बदले तनिक मूल्य न खरीदो और जो अल्लाह की उतारी (पुस्तक के) अनुसार निर्णय न करें, तो वही काफ़िर हैं।');
INSERT INTO "al_umari" VALUES(5,45,'5:45','और हमने उन (यहूदियों) पर उस (तौरात) में लिख दिया कि प्राण के बदले प्राण, आँख के बदले आँख, नाक के बदले नाक, कान के बदले कान और दाँत के बदले दाँत[ हैं तथा सभी आघातों में बराबरी का बदला है। फिर जो कोई बदला लेने को दान (क्षमा) कर दे, तो वह उसके लिए (उसके पापों का) प्रायश्चित हो जायेगा तथा जो अल्लाह की उतारी (पुस्तक के) अनुसार निर्णय न करें, वही अत्याचारी हैं। ');
INSERT INTO "al_umari" VALUES(5,46,'5:46','फिर हमने उन नबियों के पश्चात् मर्यम के पुत्र ईसा को भेजा, उसे सच बताने वाला, जो उसके सामने तौरात थी तथा उसे इंजील प्रदान की, जिसमें मार्गदर्शन तथा प्रकाश है, उसे सच बताने वाली, जो उसके आगे तौरात थी तथा अल्लाह से डरने वालों के लिए सर्वथा मार्गदर्शन तथा शिक्षा थी।');
INSERT INTO "al_umari" VALUES(5,47,'5:47','और इंजील के अनुयायी भी उसीसे निर्णय करें, जो अल्लाह ने उसमें उतारा है और जो उससे निर्णय न करें, जिसे अल्लाह ने उतारा है, वही अधर्मी हैं।');
INSERT INTO "al_umari" VALUES(5,48,'5:48','और (हे नबी!) हमने आपकी ओर सत्य पर आधारित पुस्तक (क़ुर्आन) उतार दी, जो अपने पूर्व की पुस्तकों को सच बताने वाली तथा संरक्षक[ है, अतः आप लोगों का निर्णय उसीसे करें, जो अल्लाह ने उतारा है तथा उनकी मनमानी पर उस सत्य से विमुख होकर न चलें, जो आपके पास आया है। हमने तुममें से प्रत्येक के लिए एक धर्म विधान तथा एक कार्य प्रणाली बना दिया था और यदि अल्लाह चाहता, तो तुम्हें एक ही समुदाय बना देता, परन्तु उसने जो कुछ दिया है, उसमें तुम्हारी परीक्षा लेना चाहता है। अतः, भलाईयों में एक-दूसरे से अग्रसर होने का प्रयास करो , अल्लाह ही की ओर तुम सबको लौटकर जाना है। फिर वह तुम्हें बता देगा, जिन बातों में तुम विभेद करते रहे।');
INSERT INTO "al_umari" VALUES(5,49,'5:49','तथा (हे नबी!) आप उनका निर्णय उसीसे करें, जो अल्लाह ने उतारा है और उनकी मनमानी पर न चलें तथा उनसे सावधान रहें कि आपको जो अल्लाह ने आपकी ओर उतारा है, उसमें से कुछ से फेर न दें। फिर यदि वे मुँह फेरें, तो जान लें कि अल्लाह चाहता है कि उनके कुछ पापों के कारण उन्हें दण्ड दे। वास्तव में, बहुत-से लोग उल्लंघनकारी हैं।');
INSERT INTO "al_umari" VALUES(5,50,'5:50','तो क्या वे जाहिलिय्यत (अंधकार युग) का निर्णय चाहते हैं? और अल्लाह से अच्छा निर्णय किसका हो सकता है, उनके लिए जो विश्वास रखते हैं?');
INSERT INTO "al_umari" VALUES(5,51,'5:51','हे ईमान वालो! तुम यहूदी तथा ईसाईयों को अपना मित्र न बनाओ, वे एक-दूसरे के मित्र हैं और जो कोई तुममें से उन्हें मित्र बनायेगा, वह उन्हीं में होगा तथा अल्लाह अत्याचारियों को सीधी राह नहीं दिखाता।');
INSERT INTO "al_umari" VALUES(5,52,'5:52','फिर (हे नबी!) आप देखेंगे कि जिनके दिलों में (द्विधा का) रोग है, वे उन्हीं में दौड़े जा रहे हैं, वे कहते हैं कि हम डरते हैं कि हम किसी आपदा के कुचक्र में न आ जायेँ, तो दूर नहीं कि अल्लाह उन्हें विजय प्रदान करेगा अथवा उसके पास से कोई बात हो जायेगी, तो वे लोग उस बात पर, जो उन्होंने अपने मन में छुपा रखी है, लज्जित होंगे।');
INSERT INTO "al_umari" VALUES(5,53,'5:53','तथा (उस समय) ईमान वाले कहेंगेः क्या यही वे हैं, जो अल्लाह की बड़ी गंभीर शपथें लेकर कहा करते थे कि वे तुम्हारे साथ हैं? इनके कर्म अकारथ गये और अंततः वे असफल हो गये।');
INSERT INTO "al_umari" VALUES(5,54,'5:54','हे ईमान वालो! तुममें से जो अपने धर्म से फिर जायेगा, तो अल्लाह (उसके स्थान पर) ऐसे लोगों को पैदा कर देगा, जिनसे वह प्रेम करेगा और वे उससे प्रेम करेंगे। वे ईमान वालों के लिए कोमल तथा काफ़िरों के लिए कड़े[ होंगे, अल्लाह की राह में जिहाद करेंगे, किसी निंदा करने वाले की निंदा से नहीं डरेंगे। ये अल्लाह की दया है, जिसे चाहे प्रदान करता है और अल्लाह (की दया) विशाल है और वह अति ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(5,55,'5:55','तुम्हारे सहायक केवल अल्लाह और उसके रसूल तथा वो हैं, जो ईमान लाये, नमाज़ की स्थापना करते हैं, ज़कात देते हैं और अल्लाह के आगे झुकने वाले हैं।');
INSERT INTO "al_umari" VALUES(5,56,'5:56','तथा जो अल्लाह और उसके रसूल तथा ईमान वालों को सहायक बनायेगा, तो निश्चय अल्लाह का दल ही छाकर रहेगा।');
INSERT INTO "al_umari" VALUES(5,57,'5:57','हे ईमान वालो! उन्हें जिन्होंने तुम्हारे धर्म को उपहास तथा खेल बना रखा है, उनमें से, जो तुमसे पहले पुस्तक दिये गये हैं तथा काफ़िरों को सहायक (मित्र) न बनाओ और अल्लाह से डरते रहो, यदि तुम वास्तव में ईमान वाले हो।');
INSERT INTO "al_umari" VALUES(5,58,'5:58','और जब तुम नमाज़ के लिए पुकारते हो, तो वे उसका उपहास करते तथा खेल बनाते हैं, इसलिए कि वे समझ नहीं रखते।');
INSERT INTO "al_umari" VALUES(5,59,'5:59','(हे नबी!) आप कह दें कि हे अह्ले किताब! इसके सिवा हमारा दोष किया है, जिसका तुम बदला लेना चाहते हो कि हम अल्लाह पर तथा जो हमारी ओर उतारा गया और जो हमसे पूर्व उतारा गया, उसपर ईमान लाये हैं और इसलिए कि तुममें अधिक्तर उल्लंघनकारी हैं?');
INSERT INTO "al_umari" VALUES(5,60,'5:60','आप उनसे कह दें कि क्या तुम्हें बता दूँ, जिनका प्रतिफल (बदला) अल्लाह के पास इससे भी बुरा है? वे हैं, जिन्हें अल्लाह ने धिक्कार दिया और उनपर उसका प्रकोप हुआ तथा उनमें से बंदर और सूअर बना दिये गये तथा ताग़ूत ( असुर, धर्म विरोधी शक्तियों) को पूजने लगे। इन्हीं का स्थान सबसे बुरा है तथा सर्वाधिक कुपथ हैं।');
INSERT INTO "al_umari" VALUES(5,61,'5:61','जब वे[ तुम्हारे पास आते हैं, तो कहते हैं कि हम ईमान लाये, जबकि वे कुफ़्र लिए हुए आये और उसी के साथ वापिस हुए तथा अल्लाह उसे भली-भाँति जानता है, जिसे वे छुपा रहे हैं। ');
INSERT INTO "al_umari" VALUES(5,62,'5:62','तथा आप उनमें से बहुतों को देखेंगे कि पाप तथा अत्याचार और अपने अवैध खाने में दौड़ रहे हैं। वे बड़ा कुकर्म कर रहे हैं।');
INSERT INTO "al_umari" VALUES(5,63,'5:63','उन्हें उनके धर्माचारी तथा विद्वान पाप की बात करने तथा अवैध खाने से क्यों नहीं रोकते? वे बहुत बुरी रीति बना रहे हैं।');
INSERT INTO "al_umari" VALUES(5,64,'5:64','तथा यहूदियों ने कहा कि अल्लाह के हाथ बंधे[ हुए हैं, उन्हीं के हाथ बंधे हुए हैं और वे अपने इस कथन के कारण धिक्कार दिये गये हैं; बल्कि उसके दोनों हाथ खुले हुए हैं, वह जैसे चाहे, व्यय (खर्च) करता है और इनमें से अधिक्तर को, जो (क़ुर्आन) आपके पालनहार की ओर से आपपर उतारा गया है, उल्लंघन तथा कुफ़्र (अविश्वास) में अधिक कर देगा और हमने उनके बीच प्रलय के दिन तक के लिए शत्रुता तथा बैर डाल दिया है। जब कभी वे युध्द की अग्नि सुलगाते हैं, तो अल्लाह उसे बुझा देता है। वे धरती में उपद्रव का प्रयास करते हैं और अल्लाह विद्रोहियों से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(5,65,'5:65','और यदि अह्ले किताब ईमान लाते तथा अल्लाह से डरते, तो हम अवश्य उनके दोषों को क्षमा कर देते और उन्हें सुख के स्वर्गों में प्रवेश देते।');
INSERT INTO "al_umari" VALUES(5,66,'5:66','तथा यदि वे स्थापित[ रखते तौरात और इंजील को और जो भी उनकी ओर उतारा गया है, उनके पालनहार की ओर से, तो अवश्य अपने ऊपर (आकाश) से तथा पैरों के नीचे (धरती) से , जीविका पाते। उनमें एक संतुलित समुदाय भी है और उनमें से बहुत-से कुकर्म कर रहे हैं।');
INSERT INTO "al_umari" VALUES(5,67,'5:67','हे रसूल![ जो कुछ आपपर आपके पालनहार की ओर से उतारा गया है, उसे (सबको) पहुँचा दें और यदि ऐसा नहीं किया, तो आपने उसका उपदेश नहीं पहुँचाया और अल्लाह (विरोधियों से) आपकी रक्षा करेगा , निश्चय अल्लाह काफ़िरों को मार्गदर्शन नहीं देता।');
INSERT INTO "al_umari" VALUES(5,68,'5:68','(हे नबी!) आप कह दें कि हे अह्ले किताब! तुम किसी धर्म पर नहीं हो, जब तक तौरात तथा इंजील और उस (क़ुर्आन) की स्थापना[ न करो, जो तुम्हारी ओर तुम्हारे पालनहार की ओर से उतारा गया है तथा उनमें से अधिक्तर को जो (क़ुर्आन) आपके पालनहार की ओर से उतारा गया है, अवश्य उल्लंघन तथा कुफ़्र (अविश्वास) में अधिक कर देगा। अतः, आप काफ़िरों (के अविश्वास) पर दुखी न हों। ');
INSERT INTO "al_umari" VALUES(5,69,'5:69','वास्तव में, जो ईमान लाये, जो यहूदी हुए, साबी तथा ईसाई, जो भी अल्लाह तथा अन्तिम दिन (प्रलय) पर ईमान लायेगा तथा सत्कर्म करेगा, तो उन्हीं के लिए कोई डर नहीं और न वे उदासीन[ होंगे। ');
INSERT INTO "al_umari" VALUES(5,70,'5:70','हमने बनी इस्राईल से दृढ़ वचन लिया तथा उनके पास बहुत-से रसूल भेजे, (परन्तु) जब कभी कोई रसूल उनकी अपनी आकांक्षाओं के विरुध्द कुछ लाया, तो एक गिरोह को उन्होंने झुठला दिया तथा एक गिरोह को वध करते रहे।');
INSERT INTO "al_umari" VALUES(5,71,'5:71','तथा वे समझे कि कोई परीक्षा न होगी, इसलिए अंधे-बहरे हो गये, फिर अल्लाह ने उन्हें क्षमा कर दिया, फिर भी उनमें से अधिक्तर अंधे और बहरे हो गये तथा वे जो कुछ कर रहे हैं, अल्लाह उसे देख रहा है।');
INSERT INTO "al_umari" VALUES(5,72,'5:72','निश्चय वे काफ़िर हो गये, जिन्होंने कहा कि अल्लाह[ मर्यम का पुत्र मसीह़ ही है। जबकि मसीह़ ने कहा थाः हे बनी इस्राईल! उस अल्लाह की इबादत (वंदना) करो, जो मेरा पालनहार तथा तुम्हारा पालनहार है, वास्तव में, जिसने अल्लाह का साझी बना लिया, उसपर अल्लाह ने स्वर्ग को ह़राम (वर्जित) कर दिया और उसका निवास स्थान नरक है तथा अत्याचारों का कोई सहायक न होगा। ');
INSERT INTO "al_umari" VALUES(5,73,'5:73','निश्चय वे भी काफ़िर हो गये, जिन्होंने कहा कि अल्लाह तीन का तीसरा है! जबकि कोई पूज्य नहीं है, परन्तु वही अकेला पूज्य है और यदि वे जो कुछ कहते हैं, उससे नहीं रुके, तो उनमें से काफ़िरों को दुखदायी यातना होगी।');
INSERT INTO "al_umari" VALUES(5,74,'5:74','वे अल्लाह से तौबा तथा क्षमा याचना क्यों नहीं करते, जबकि अल्लाह अति क्षमाशील दयावान् है?');
INSERT INTO "al_umari" VALUES(5,75,'5:75','मर्यम का पुत्र मसीह़ इसके सिवा कुछ नहीं कि वह एक रसूल है, उससे पहले भी बहुत-से रसूल हो चुके हैं, उसकी माँ सच्ची थी, दोनों भोजन करते थे, आप देखें कि हम कैसे उनके लिए निशानियाँ (एकेश्वरवाद के लक्षण) उजागर कर रहे हैं, फिर देखिए कि वे कहाँ बहके[ जा रहे हैं? ');
INSERT INTO "al_umari" VALUES(5,76,'5:76','आप उनसे कह दें कि क्या तुम अल्लाह के सिवा उसकी इबादत (वंदना) कर रहे हो, जो तुम्हें कोई हानि और लाभ नहीं पहुँचा सकता? तथा अल्लाह सब कुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(5,77,'5:77','(हे नबी!) कह दो कि हे अह्ले किताब! अपने धर्म में अवैध अति न करो[ तथा उनकी अभिलाषाओं पर न चलो, जो तुमसे पहले कुपथ हो चुके और बहुतों को कुपथ कर गये और संमार्ग से विचलित हो गये।');
INSERT INTO "al_umari" VALUES(5,78,'5:78','बनी इस्राईल में से जो काफ़िर हो गये, वे दावूद तथा मर्यम के पुत्र ईसा की ज़बान पर धिक्कार दिये[ गये, ये इस कारण कि उन्होंने अवज्ञा की तथी (धर्म की सीमा का) उल्लंघन कर रहे थे। ');
INSERT INTO "al_umari" VALUES(5,79,'5:79','वे एक-दूसरे को किसी बुराई से, जो वे करते थे, रोकते नहीं थे, निश्चय वे बड़ी बुराई कर रहे थे[। ');
INSERT INTO "al_umari" VALUES(5,80,'5:80','आप उनमें से अधिक्तर को देखेंगे कि काफ़िरों को अपना मित्र बना रहे हैं। जो कर्म उन्होंने अपने लिए आगे भेजा है, बहुत बुरा है कि अल्लाह उनपर क्रुध्द हो गया तथा यातना में वही सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(5,81,'5:81','और यदि वे अल्लाह पर तथा नबी पर और जो उनपर उतारा गया, उसपर ईमान लाते, तो उन्हें मित्र न बनाते[, परन्तु उनमें अधिक्तर उल्लंघनकारी हैं। ');
INSERT INTO "al_umari" VALUES(5,82,'5:82','(हे नबी!) आप उनका, जो ईमान लाये हैं, सबसे कड़ा शत्रु यहूदियों तथा मिश्रणवादियों को पायेंगे और जो ईमान लाये हैं, उनके सबसे अधिक समीप आप उन्हें पायेंगे, जो अपने को ईसाई कहते हैं। ये बात इसलिए है कि उनमें उपासक तथा सन्यासी हैं और वे अभिमान[ नहीं करते। ');
INSERT INTO "al_umari" VALUES(5,83,'5:83','तथा जब वे (ईसाई) उस (क़ुर्आन) को सुनते हैं, जो रसूल पर उतरा है, तो आप देखते हैं कि उनकी आँखें आँसू से उबल रही हैं, उस सत्य के कारण, जिसे उन्होंने पहचान लिया है। वे कहते हैं, हे हमारे पालनहार! हम ईमान ले आये, अतः हमें (सत्य) के साथियों में लिख[ ले। ');
INSERT INTO "al_umari" VALUES(5,84,'5:84','(तथा कहते हैं) क्या कारण है कि हम अल्लाह पर तथा इस सत्य (क़ुर्आन) पर ईमान (विश्वास) न करें? और हम आशा रखते हैं कि हमारा पालनहार हमें सदाचारियों में सम्मिलित कर देगा।');
INSERT INTO "al_umari" VALUES(5,85,'5:85','तो अल्लाह ने उनके ये कहने के कारण उन्हें ऐसे स्वर्ग प्रदान कर दिये, जिनमें नहरें प्रवाहित हैं, वे उनमें सदावासी होंगे तथा यही सत्कर्मियों का प्रतिफल (बदला) है।');
INSERT INTO "al_umari" VALUES(5,86,'5:86','तथा जो काफ़िर हो गये और हमारी आयतों को झुठला दिया, तो वही नारकी हैं।');
INSERT INTO "al_umari" VALUES(5,87,'5:87','हे ईमान वालो! उन स्वच्छ पवित्र चीजों को जो अल्लाह ने तुम्हारे लिए ह़लाल (वैध) की हैं, ह़राम (अवैध)[ न करो और सीमा का उल्लंघन न करो। निःसंदेह अल्लाह उल्लंघनकारियों से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(5,88,'5:88','तथा उसमें से खाओ, जो ह़लाल (वैध) स्वच्छ चीज़ अल्लाह ने तुम्हें प्रदान की हैं तथा अल्लाह (की अवज्ञा) से डरते रहो, यदि तुम उसीपर ईमान (विश्वास) रखते हो।');
INSERT INTO "al_umari" VALUES(5,89,'5:89','अल्लाह तुम्हें तुम्हारी व्यर्थ शपथों[ पर नहीं पकड़ता, परन्तु जो शपथ जान-बूझ कर ली हो, उसपर रकड़ता है, तो उसका प्रायश्चित दस निर्धनों को भोजन कराना है, उस माध्यमिक भोजन में से, जो तुम अपने परिवार को खिलाते हो अथवा उन्हें वस्त्र दो अथवा एक दास मुक्त करो और जिसे ये सब उपलब्ध न हो, तो तीन दिन रोज़ा रखना है। ये तुम्हारी शपथों का प्रायश्चित है, जब तुम शपथ लो तथा अपनी शपथों की रक्षा करो, इसी प्रकार अल्लाह तुम्हारे लिए अपनी आयतों (आदेशों) का वर्णन करता है, ताकि तुम उसका उपकार मानो।');
INSERT INTO "al_umari" VALUES(5,90,'5:90','हे ईमान वालो! निःसंदेह[ मदिरा, जुआ, देवस्थान और पाँसे शैतानी मलिन कर्म हैं, अतः इनसे दूर रहो, ताकि तुम सफल हो जाओ।');
INSERT INTO "al_umari" VALUES(5,91,'5:91','शैतान तो यही चाहता है कि शराब (मदिरा) तथा जूए द्वारा तुम्हारे बीच बैर तथा द्वेष डाल दे और तुम्हें अल्लाह की याद तथा नमाज़ से रोक दे, तो क्या तुम रुकोगे या नहीं?');
INSERT INTO "al_umari" VALUES(5,92,'5:92','तथा अल्लाह के आज्ञाकारी रहो, उसके रसूल के आज्ञाकारी रहो तथा (उनकी अवज्ञा से) सावधान रहो। यदि तुम विमुख हुए, तो जान लो कि हमारे रसूल पर केवल खुला उपदेश पहुँचा देना है।');
INSERT INTO "al_umari" VALUES(5,93,'5:93','उनपर जो ईमान लाये तथा सदाचार करते रहे, उसमें कोई दोष नहीं, जो (निषेधाज्ञा से पहले) खा लिया, जब वे अल्लाह से डरते रहे, ईमान पर स्थिर रहे, सत्कर्म करते रहे, फिर डरते और सत्कर्म करते रहे, फिर (रोके गये तो) अल्लाह से डरे और सदाचार करते रहे। अल्लाह सदाचारियों से प्रेम करता[ है। ');
INSERT INTO "al_umari" VALUES(5,94,'5:94','हे ईमान वालो! अल्लाह कुछ शिकार द्वारा जिन तक तुम्हारे हाथ तथा भाले पहुँचेंगे, अवश्य तुम्हारी परीक्षा लेगा, ताकि ये जान ले कि तुममें से कौन उससे बिन देखे डरता है? फिर इस (आदेश) के पश्चात् जिसने (इसका) उल्लंघन किया, तो उसी के लिए दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(5,95,'5:95','हे ईमान वलो! शिकार न करो[, जब तुम एह़राम की स्थिति में रहो तथा तुममें से जो कोई जान-बूझ कर ऐसा कर जाये, तो पालतू पशु से शिकार किये पशु जैसा बदला (प्रतिकार) है, जिसका निर्णय तुममें से दो न्यायकारी व्यक्ति करेंगे, जो काबा तक हद्य (उपहार स्वरूप) भेजा जाये। अथवा प्रायश्चित है, जो कुछ निर्धनों का खाना अथवा उसके बराबर रोज़े रखना है। ताकि अपने किये का दुष्परिणाम चखो। इस आदेश से पूर्व जो हुआ, अल्लाह ने उसे क्षमा कर दिया और जो फिर करेगा, अल्लाह उससे बदला लेगा और अल्लाह प्रभुत्वशाली बदला लेने वाला है।');
INSERT INTO "al_umari" VALUES(5,96,'5:96','तथा तुम्हारे लिए जल का शिकार और उसका खाद्य[ ह़लाल (वैध) कर दिया गया है, तुम्हारे तथा यात्रियों के लाभ के लिए तथा तुमपर थल का शिकार जब तक एह़राम की स्थिति में रहो, ह़राम (अवैध) कर दिया गया है और अल्लाह (की अवज्ञा) से डरते रहो, जिसकी ओर तुम सभी एकत्र किये जाओगे। ');
INSERT INTO "al_umari" VALUES(5,97,'5:97','अल्लाह ने आदरणीय घर काबा को लोगों के लिए (शांति तथा एकता की) स्थापना का साधन बना दिया है तथा आदरणीय मासों[ और (ह़ज) की क़ुर्बानी तथा क़ुर्बानी के पशुओं को, जिन्हें पट्टे पहनाये गये हों। ये इसलिए किया गया ताकि तुम्हें ज्ञान हो जाये कि अल्लाह, जो कुछ आकाशों और जो कुछ धरती में है, सबको जानता है। तथा निःसंदेह अल्लाह प्रत्येक विषय का ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(5,98,'5:98','तुम जान लो कि अल्लाह कड़ा दण्ड देने वाला है और ये कि अल्लाह अति क्षमाशील दयावान् (भी) है।');
INSERT INTO "al_umari" VALUES(5,99,'5:99','अल्लाह के रसूल का दायित्व इसके सिवा कुछ नहीं कि उपदेश पहुँचा दे और अल्लाह जो तुम बोलते और जो मन में रखते हो, सब जानता है।');
INSERT INTO "al_umari" VALUES(5,100,'5:100','(हे नबी!) कह दो कि मलिन तथा पवित्र समान नहीं हो सकते। यद्यपि मलिन की अधिक्ता तुम्हें भा रही हो। तो हे मतिमानो! अल्लाह (की अवज्ञा) से डरो, ताकि तुम सफल हो जाओ[। ');
INSERT INTO "al_umari" VALUES(5,101,'5:101','हे ईमान वालो! ऐसी बहुत सी चीज़ों के विषय में प्रश्न न करो, जो यदि तुम्हें बता दी जायें, तो तुम्हें बुरा लग जाये तथा यदि तुम, उनके विषय में, जबकि क़ुर्आन उतर रहा है, प्रश्न करोगे, तो वो तुम्हारे लिए खोल दी जायेंगी। अल्लाह ने तुम्हें क्षमा कर दिया और अल्लाह अति क्षमाशील सहनशील[ है। ');
INSERT INTO "al_umari" VALUES(5,102,'5:102','ऐसे ही प्रश्न एक समुदाय ने तुमसे पहले[ किये, फिर इसके कारण वे काफ़िर हो गये। ');
INSERT INTO "al_umari" VALUES(5,103,'5:103','अल्लाह ने बह़ीरा, साइबा, वसीला और ह़ाम, कुछ नहीं बनाया[ है, परन्तु जो काफ़िर हो गये वे अल्लाह पर झूठ घड़ रहे हैं और उनमें अधिक्तर निर्बोध हैं। ');
INSERT INTO "al_umari" VALUES(5,104,'5:104','और जब उनसे कहा जाता है कि उसकी ओर आओ, जो अल्लाह ने उतारा है तथा रसूल की ओर (आओ), तो कहते हैं, हमें वही बस है, जिसपर हमने अपने पूर्वजों को पाया है, क्या उनके पूर्वज कुछ न जानते रहे हों और न संमार्ग पर रहे हों (तब भी वे उन्हीं के रास्ते पर चलेंगे)?');
INSERT INTO "al_umari" VALUES(5,105,'5:105','हे ईमान वालो! तुम अपनी चिन्ता करो, तुम्हें वे हानि नहीं पहुँचा सकेंगे, जो कुपथ हो गये, जब तुम सुपथ पर रहो। अल्लाह की ओर तुम सबको (परलोक में) फिर कर जाना है, फिर वह तुम्हें तुम्हारे[ कर्मों से सूचित कर देगा। ');
INSERT INTO "al_umari" VALUES(5,106,'5:106','हे ईमान वालो! यदि किसी के मरण का समय हो, तो वसिय्यत[ के समय तुममें से दो न्यायकारियों को अथवा तुम्हारे सिवा दो अन्य व्यक्तियों को गवाह बनाये, यदि तुम धरती में यात्रा कर रहे हो और तुम्हें मरण की आपदा आ पहुँचे। उन दोनों को नमाज़ के बाद रोक लो, फिर वे दोनों अल्लाह की शपथ लें, यदि तुम्हें उनपर संदेह हो। वे ये कहें कि हम गवाही के द्वारा कोई मूल्य नहीं खरीदते, यद्यपि वे समीपवर्ती क्यों न हों और न हम अल्लाह की गवाही को छुपाते हैं, यदि हम ऐसा करें, तो हम पापियों में हैं। ');
INSERT INTO "al_umari" VALUES(5,107,'5:107','फिर यदि ज्ञान हो जाये कि वे दोनों (साक्षी) किसी पाप के अधिकारी हुए हैं, तो उन दोनों के स्थान पर दो अन्य गवाह खड़े हो जायेँ, उनमें से, जिनका अधिकार पहले दोनों ने दबाया है और वे दोनों शपथ लें कि हमारी गवाही उन दोनों की गवाही से अधिक सही है और हमने कोई अत्याचार नहीं किया है। यदि किया है, तो (निःसंदेह) हम अत्याचारी हैं।');
INSERT INTO "al_umari" VALUES(5,108,'5:108','इस प्रकार अधिक आशा है कि वे सही गवाही देंगे अथवा इस बात से डरेंगे कि उनकी शपथों को दूसरी शपथों के पश्चात् न माना जाये। अल्लाह से डरते रहो, (उसका आदेश) सुनो और अल्लाह उल्लंघनकारियों को सीधी राह नहीं[ दिखाता। ');
INSERT INTO "al_umari" VALUES(5,109,'5:109','जिस दिन अल्लाह सब रसूलों को एकत्र करेगा, फिर उनसे कहेगा कि तुम्हें (तुम्हारी जातियों की ओर से) क्या उत्तर दिया गया? वे कहेंगे कि हमें इसका कोई ज्ञान[ नहीं। निःसंदेह तू ही सब छुपे तथ्यों का ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(5,110,'5:110','तथा याद करो, जब अल्लाह ने कहाः हे मर्यम के पुत्र ईसा! अपने ऊपर तथा अपनी माता के ऊपर मेरा पुरस्कार याद कर, जब मैंने पवित्रात्मा (जिब्रील) द्वारा तुझे समर्थन दिया, तू गहवारे (गोद) में तथा बड़ी आयु में लोगों से बातें कर रहा था तथा तुझे पुस्तक, प्रबोध, तौरात और इंजील की शिक्षा दी, जब तू मेरी अनुमति से मिट्टी से पक्षी का रूप बनाता और उसमें फूँकता, तो वह मेरी अनुमति से वास्तव में पक्षी बन जाता था और तू जन्म से अंधे तथा कोढ़ी को मेरी अनुमति से स्वस्थ कर देता था और जब तू मुर्दों को मेरी अनुमति से जीवित कर देता था और मैंने बनी इस्राईल से तुझे बचाया था, जब तू उनके पास खुली निशानियाँ लाया, तो उनमें से काफ़िरों ने कहा कि ये तो खुले जादू के सिवा कुछ नहीं है।');
INSERT INTO "al_umari" VALUES(5,111,'5:111','तथा याद कर, जब मैंने तेरे ह़वारियों के दिलों में ये बात डाल दी कि मुझपर तथा मेरे रसूल (ईसा) पर ईमान लाओ, तो सबने कहा कि हम ईमान लाये और तू साक्षी रह कि हम मुस्लिम (आज्ञाकारी) हैं।');
INSERT INTO "al_umari" VALUES(5,112,'5:112','जब ह़वारियों ने कहाः हे मर्यम के पुत्र ईसा! क्या तेरा पालनहार ये कर सकता है कि हमपर आकाश से थाल (दस्तरख्वान) उतार दे? उस (ईसा) ने कहाः तुम अल्लाह से डरो, यदि तुम वास्तव में ईमान वाले हो।');
INSERT INTO "al_umari" VALUES(5,113,'5:113','उन्होंने कहाः हम चाहते हैं कि उसमें से खायें और हमारे दिलों को संतोष हो जाये तथा हमें विश्वास हो जाये कि तूने हमें जो कुछ बताया है, सच है और हम उसके साक्षियों में से हो जायेँ।');
INSERT INTO "al_umari" VALUES(5,114,'5:114','मर्यम के पुत्र ईसा ने प्रार्थना कीः हे अल्लाह, हमारे पालनहार! हमपर आकाश से एक थाल उतार दे, जो हमारे तथा हमारे पश्चात् के लोगों के लिए उत्सव (का दिन) बन जाये तथा तेरी ओर से एक चिन्ह (निशानी)। तथा हमें जीविका प्रदान कर, तू उत्तम जीविका प्रदाता है।');
INSERT INTO "al_umari" VALUES(5,115,'5:115','अल्लाह ने कहाः मैं तुमपर उसे उतारने वाला हूँ, फिर उसके पश्चात् भी जो कुफ़्र (अविश्वास) करेगा, तो मैं निश्चय उसे दण्ड दूँगा, ऐसा दण्ड[ कि संसार वासियों में से किसी को, वैसी दण्ड नहीं दूँगा। ');
INSERT INTO "al_umari" VALUES(5,116,'5:116','तथा जब अल्लाह (प्रलय के दिन) कहेगाः हे मर्यम के पुत्र ईसा! क्या तुमने लोगों से कहा था कि अल्लाह को छोड़कर मुझे तथा मेरी माता को पूज्य (आराध्य) बना लो? वह कहेगाः तू पवित्र है, मुझसे ये कैसे हो सकता है कि ऐसी बात कहूँ, जिसका मुझे कोई अधिकार नहीं? यदि मैंने कहा होगा, तो तुझे अवश्य उसका ज्ञान हुआ होगा। तू मेरे मन की बात जानता है और मैं तेरे मन की बात नहीं जानता। वास्तव में, तू ही परोक्ष (ग़ैब) का अति ज्ञानी है।');
INSERT INTO "al_umari" VALUES(5,117,'5:117','मैंने केवल उनसे वही कहा था, जिसका तूने आदेश दिया था कि अल्लाह की इबादत करो, जो मेरा पालनहार तथा तुम सभी का पालनहार है। मैं उनकी दशा जानता था, जब तक उनमें था और जब तूने मेरा समय पूरा कर दिया[, तो तू ही उन्हें जानता था और तू प्रत्येक वस्तु से सूचित है। ');
INSERT INTO "al_umari" VALUES(5,118,'5:118','यदि तू उन्हें दण्ड दे, तो वे तेरे दास (बन्दे) हैं और यदि तू उन्हें क्षमा कर दे, तो वास्तव में तू ही प्रभावशाली गुणी है।');
INSERT INTO "al_umari" VALUES(5,119,'5:119','अल्लाह कहेगाः ये वो दिन है, जिसमें सचों को उनका सच ही लाभ देगा। उन्हीं के लिए ऐसे स्वर्ग हैं, जिनमें नहरें प्रवाहित हैं। वे उनमें नित्य सदावासी होंगे, अल्लाह उनसे प्रसन्न हो गया तथा वे अल्लाह से प्रसन्न हो गये और यही सबसे बड़ी सफलता है।');
INSERT INTO "al_umari" VALUES(5,120,'5:120','आकाशों तथा धरती और उनमें जो कुछ है, सबका राज्य अल्लाह ही का[ है तथा वह जो चाहे, कर सकता है। ');
INSERT INTO "al_umari" VALUES(6,1,'6:1','सब प्रशंसा उस अल्लाह के लिए है, जिसने आकाशों तथा धरती को बनाया तथा अंधेरे और उजाला बनाया, फिर भी जो काफ़िर हो गये, वे दूसरों को अपने पालनहार के बराबर समझते[ हैं। ');
INSERT INTO "al_umari" VALUES(6,2,'6:2','वही है, जिसने तुम्हें मिट्टी से उत्पन्न[ किया, फिर (तुम्हारे जीवन की) अवधि निर्धारित कर दी और एक निर्धारित अवधि (प्रलय का समय) उसके पास है, फिर भी तुम संदेह करते हो।');
INSERT INTO "al_umari" VALUES(6,3,'6:3','वही अल्लाह पूज्य है आकाशों तथा धरती में। वह तुम्हारे भेदों तथा खुली बातों को जानता है तथा तुम जो भी करते हो, उसे जानता है।');
INSERT INTO "al_umari" VALUES(6,4,'6:4','और उनके पास उनके पालनहार की आयतों (निशानियों) में से कोई आयत (निशानी) नहीं आयी, जिससे उन्होंने मुँह फेर न[ लिए हो। ');
INSERT INTO "al_umari" VALUES(6,5,'6:5','उन्होंने सत्य को झुठला दिया है, जब भी उनके पास आया। तो शीघ्र ही उनके पास उसके समाचार आ जायेंगे[, जिसका उपहास कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(6,6,'6:6','क्या वह नहीं जानते कि उनसे पहले हमने कितनी जातियों का नाश कर दिया, जिन्हें हमने धरती में ऐसी शक्ति और अधिकार दिया था, जो अधिकार और शक्ति तुम्हें नहीं दिये हैं और हमने उनपर धारा प्रवाह वर्षा की और उनकी धरती में नहरें प्रवाहित कर दीं, फिर हमने उनके पापों के कारण उन्हें नाश[ कर दिया और उनके पश्चात् दूसरी जातियों को पैदा कर दिया। ');
INSERT INTO "al_umari" VALUES(6,7,'6:7','(हे नबी!) यदि हम आपपर काग़ज़ में लिखी हुई कोई पुस्तक उतार[ दें, फिर वे उसे अपने हाथों से छूयें, तबभी जो काफ़िर हैं, कह देंगे कि ये तो केवल खुला हुआ जादू है। ');
INSERT INTO "al_umari" VALUES(6,8,'6:8','तथा उन्होंने कहाः[ इस (नबी) पर कोई फ़रिश्ता क्यों नहीं उतारा गया? और यदि हम कोई फ़रिश्ता उतार देते, तो निर्णय ही कर दिया जाता, फिर उन्हें अवसर नहीं दिया जाता ।');
INSERT INTO "al_umari" VALUES(6,9,'6:9','और यदि हम किसी फ़रिश्ते को नबी बनाते, तो उसे किसी पुरुष ही के रूप में बनाते[ और उन्हें उसी संदेह में डाल देते, जो संदेह (अब) कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(6,10,'6:10','(हे नबी!) आपसे पहले भी रसूलों के साथ उपहास किया गया, तो जिन्होंने उनसे उपहास किया, उन्हें उनके उपहास (के दुष्परिणाम) ने घेर लिया।');
INSERT INTO "al_umari" VALUES(6,11,'6:11','(हे नबी!) उनसे कहो कि धरती में फिरो, फिर देखो कि झुठलाने वालों का दुष्परिणाम किया[ हुआ? ');
INSERT INTO "al_umari" VALUES(6,12,'6:12','(हे नबी!) उनसे पूछिये कि जो कुछ आकाशों तथा धरती में है, वह किसका है? कहोः अल्लाह का है! उसने अपने ऊपर दया को अनिवार्य कर[ लिया है, वह तुम्हें अवश्य प्रलय के दिन एकत्र करेगा, जिसमें कोई संदेह नहीं। जिन्होंने अपने-आपको क्षति में डाल लिया, वही ईमान नहीं ला रहे हैं।');
INSERT INTO "al_umari" VALUES(6,13,'6:13','तथा उसी का[ है, जो कुछ रात और दिन में बस रहा है और वह सब कुछ सुनता-जानता है। ');
INSERT INTO "al_umari" VALUES(6,14,'6:14','(हे नबी!) उनसे कहो कि क्या मैं उस अल्लाह के सिवा किसी को सहायक बना लूँ, जो आकाशों तथा धरती का बनाने वाला है, वह सबको खिलाता है और उसे कोई नहीं खिलाता? आप कहिये कि मुझे तो यही आदेश दिया गया है कि प्रथम आज्ञाकारी हो जाऊँ तथा कदापि मुश्रिकों में से न बनूँ।');
INSERT INTO "al_umari" VALUES(6,15,'6:15','आप कह दें कि मैं डरता हूँ, यदि अपने पालनहार की अवज्ञा करूँ, एक घोर दिन[ की यातना से। ');
INSERT INTO "al_umari" VALUES(6,16,'6:16','तथा जिससे उसे (यातना को) उस दिन फेर दिया गया, तो अल्लाह ने उसपर दया कर दी और यही खुली सफलता है।');
INSERT INTO "al_umari" VALUES(6,17,'6:17','यदि अललाह तुम्हें कोई हानि पहुँचाये, तो उसके सिवा कोई नहीं, जो उसे दूर कर दे और यदि तुम्हें कोई लाभ पहुँचाये, तो वही जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(6,18,'6:18','तथा वही है, जो अपने सेवकों पर पूरा अधिकार रखता है तथा वह बड़ा ज्ञानी सर्वसूचित है।');
INSERT INTO "al_umari" VALUES(6,19,'6:19','(हे नबी!) इन मुश्रिकों से पूछो कि किसकी गवाही सबसे बढ़ कर है? आप कह दें कि अल्लाह मेरे तथा तुम्हारे बीच गवाह[ है तथा मेरी ओर ये क़ुर्आन वह़्यी (प्रकाशना) द्वारा भेजा गया है, ताकि मैं तुम्हें सावधान करूँ तथा उसे, जिस तक ये पहुँचे। क्या वास्तव में, तुम ये साक्ष्य (गवाही) दे सकते हो कि अल्लाह के साथ दूसरे पूज्य भी हैं? आप कह दें कि मैं तो इसकी गवाही नहीं दे सकता। आप कह दें कि वह तो केवल एक ही पूज्य है तथा वास्तव में, मैं तुम्हारे शिर्क से विरक्त हूँ।');
INSERT INTO "al_umari" VALUES(6,20,'6:20','जिन लोगों को हमने पुस्तक[ प्रदान की है, वे आपको उसी प्रकार पहचानते हैं, जैसे अपने पुत्रों को पहचानते हैं, परन्तु जिन्होंने स्वयं को क्षति में डाल रखा है, वही ईमान नहीं ला रहे हैं।');
INSERT INTO "al_umari" VALUES(6,21,'6:21','तथा उससे बड़ा अत्याचारी कौन होगा, जो अल्लाह पर झूठा आरोप लगाये अथवा उसकी आयतों को झुठलाये? निःसंदेह अत्याचारी सफल नहीं होंगे।');
INSERT INTO "al_umari" VALUES(6,22,'6:22','जिस दिन हम, सबको एकत्र करेंगे, तो जिन्होंने शिर्क किया है, उनसे कहेंगे कि तुम्हारे वे साझी कहाँ गये, जिन्हें तुम (पूज्य) समझ रहे थे?');
INSERT INTO "al_umari" VALUES(6,23,'6:23','फिर नहीं होगा उनका उपद्रव इसके सिवा कि वे कहेंगेः अल्लाह की शपथ! हम मुश्रिक थे ही नहीं।');
INSERT INTO "al_umari" VALUES(6,24,'6:24','देखो कि कैसे अपने ऊपर ही झूठ बोल गये और उनसे वे (मिथ्या पूज्य) जो बना रहे थे, खो गये!');
INSERT INTO "al_umari" VALUES(6,25,'6:25','और उन मुश्रिकों में से कुछ आपकी बात ध्यान से सुनते हैं और (वास्तव में) हमने उनके दिलों पर पर्दे (आवरण) डाल रखे हैं कि बात न समझें[ और उनके कान भारी कर दिये हैं, यदि वे (सत्य के) प्रत्येक लक्षण देख लें, तब भी उसपर ईमान नहीं लायेंगे, यहाँ तक कि जब वे आपके पास आकर झगड़ते हैं, जो काफ़िर हैं, तो वे कहते हैं कि ये तो पूर्वजों की कथायें हैं। ');
INSERT INTO "al_umari" VALUES(6,26,'6:26','वे, उसे[ (सुनने से) दूसरों को रोकते हैं तथा स्वयं भी दूर रहते हैं और वे अपना ही विनाश कर रहे हैं, परन्तु समझते नहीं हैं। ');
INSERT INTO "al_umari" VALUES(6,27,'6:27','तथा (हे नबी!) यदि आप उन्हें उस समय देखेंगे, जब वे नरक के समीप खड़े किये जायेंगे, तो वे कामना कर रहे होंगे कि ऐसा होता कि हम संसार की ओर फेर दिये जाते और अपने पालनहार की आयतों को नहीं झुठलाते और हम ईमान वालों में हो जाते।');
INSERT INTO "al_umari" VALUES(6,28,'6:28','बल्कि उनके लिए वो बात खुल जायेगी, जिसे वे इससे पहले छुपा रहे थे[ और यदि संसार में फेर दिये जायेँ, तो फिर वही करेंगे, जिससे रोके गये थे, वास्तव में, वे हैं ही झूठे। ');
INSERT INTO "al_umari" VALUES(6,29,'6:29','तथा उन्होंने कहा कि जीवन बस हमारा सांसारिक जीवन है और हमें फिर जीवित होना[ नहीं है। ');
INSERT INTO "al_umari" VALUES(6,30,'6:30','तथा यदि आप उन्हें उस समय देखें, जब वे (परलय के दिन) अपने पालनहार के समक्ष खड़े किये जायेंगे, उस समय अल्लाह उनसे कहेगाः क्या ये (जीवन) सत्य नहीं? वे कहेंगेः क्यों नहीं? हमारे पालनहार की शपथ! इसपर अल्लाह कहेगाः तो अब अपने कुफ़्र करने की यातना चखो।');
INSERT INTO "al_umari" VALUES(6,31,'6:31','निश्चय वे क्षति में पड़ गये, जिन्होंने अल्लाह से मिलने को झुठला दिया, यहाँ तक कि जब प्रलय अचानक उनपर आ जायेगी तो कहेंगेः हाय! इस विषय में हमसे बड़ी चूक हुई और वे अपने पापों का बोझ अपनी पीठों पर उठाये होंगे। तो कैसा बुरा बोझ है, जिसे वे उठा रहे हैं।');
INSERT INTO "al_umari" VALUES(6,32,'6:32','तथा सांसारिक जीवन एक खेल और मनोरंजन[ है तथा परलोक का घर ही उत्तम है, उनके लिए जो अल्लाह से डरते हों, तो क्या तुम समझते नहीं हो?');
INSERT INTO "al_umari" VALUES(6,33,'6:33','(हे नबी!) हम जानते हैं कि उनकी बातें आपको उदासीन कर देती हैं, तो वास्तव में वे आपको नहीं झुठलाते, परन्तु ये अत्याचारी अल्लाह की आयतों को नकारते हैं।');
INSERT INTO "al_umari" VALUES(6,34,'6:34','और आपसे पहले भी बहुत-से रसूल झुठलाये गये। तो इसे उन्होंने सहन किया और उन्हें दुःख दिया गया, यहाँ तक कि हमारी सहायता आ गयी तथा अल्लाह की बातों को कोई बदल नहीं[ सकता और आपके पास रसूलों के समाचार आ चुके हैं। ');
INSERT INTO "al_umari" VALUES(6,35,'6:35','और यदि आपको उनकी विमुखता भारी लग रही है, तो यदि आपसे हो सके, तो धरती में कोई सुरंग खोज लें अथवा आकाश में कोई सीढ़ी लगा लें, फिर उनके पास कोई निशानी (चमत्कार) ला दें और यदि अल्लाह चाहे, तो इन्हें मार्गदर्शन पर एकत्र कर दे। अतः आप कदापि अज्ञानों में न हों।');
INSERT INTO "al_umari" VALUES(6,36,'6:36','आपकी बात वही स्वीकार करेंगे, जो सुनते हों, परन्तु जो मुर्दे हैं, उन्हें अल्लाह[ ही जीवित करेगा, फिर उसी की ओर फेरे जायेंगे। ');
INSERT INTO "al_umari" VALUES(6,37,'6:37','तथा उन्होंने कहा कि नबी पर उनके पालनहार की ओर से कोई चमत्कार क्यों नहीं उतारा गया? आप कह दें कि अल्लाह इसका सामर्थ्य रखता है, परन्तु अधिक्तर लोग अज्ञान हैं।');
INSERT INTO "al_umari" VALUES(6,38,'6:38','धरती में विचरते जीव तथा अपने दो पंखों से उड़ते पक्षी तुम्हारी जैसी जातियाँ हैं, हमने पुस्तक[ में कुछ कमी नहीं की है, फिर वे अपने पालनहार की ओर ही एकत्र किये जायेंगे।');
INSERT INTO "al_umari" VALUES(6,39,'6:39','तथा जिन्होंने हमारी निशानियों को झुठला दिया, वे गूँगे, बहरे, अंधेरों में हैं। जिसे अल्लाह चाहता है, कुपथ करता है और जिसे चाहता है, सीधी राह पर लगा देता है।');
INSERT INTO "al_umari" VALUES(6,40,'6:40','(हे नबी!) उनसे कहो कि यदि तुमपर अल्लाह का प्रकोप आ जाये अथवा तुमपर प्रलय आ जाये, तो क्या तुम अल्लाह के सिवा किसी और को पुकारोगे, यदि तुम सच्चे हो?');
INSERT INTO "al_umari" VALUES(6,41,'6:41','बल्कि तुम उसी को पुकारते हो, तो वह दूर करता है, उसे, जिसके लिए तुम पुकारते हो, यदि वह चाहे, और तुम उसे भूल जाते हो, जिसे साझी[ बनाते हो। ');
INSERT INTO "al_umari" VALUES(6,42,'6:42','और आपसे पहले भी समुदायों की ओर हमने रसूल भेजे, तो हमने उन्हें आपदाओं और दुखों में डाला[, ताकि वे विनय करें। ');
INSERT INTO "al_umari" VALUES(6,43,'6:43','तो जब उनपर हमारी यातना आई, तो वे हमारे समक्ष झुक क्यों नहीं गये? परन्तु उनके दिल और भी कड़े हो गये तथा शैतान ने उनके लिए उनके कुकर्मों को सुन्दर बना[ दिया। ');
INSERT INTO "al_umari" VALUES(6,44,'6:44','तो जब उन्होंने उसे भुला दिया, जो याद दिलाये गये थे, तो हमने उनपर प्रत्येक (सुख-सुविधा) के द्वार खोल दिये। यहाँ तक कि जब, जो कुछ वे दिये गये, उससे प्रफुल्ल हो गये, तो हमने उन्हें अचानक घेर लिया और वे निराश होकर रह गये।');
INSERT INTO "al_umari" VALUES(6,45,'6:45','तो उनकी जड़ काट दी गयी, जिन्होंने अत्याचार किया और सब प्रशंसा अल्लाह ही के लिए है, जो पूरे विश्व का पालनहार है।');
INSERT INTO "al_umari" VALUES(6,46,'6:46','(हे नबी!) आप कहें कि क्या तुमने इसपर भी विचार किया कि यदि अल्लाह तुम्हारे सुनने तथा देखने की शक्ति छीन ले और तुम्हारे दिलों पर मुहर लगा दे, तो अल्लाह के सिवा कौन है, जो तुम्हें इसे वापस दिला सके? देखो, हम कैसे बार-बार आयतें[ प्रस्तुत कर रहे हैं, फिर भी वे मुँह फेर रहे हैं।');
INSERT INTO "al_umari" VALUES(6,47,'6:47','आप कहें कि कभी तुमने इस बात पर विचार किया कि यदि तुमपर अल्लाह की यातना अचानक या खुल कर आ जाये, तो अत्याचारियों (मुश्रिकों) के सिवा किसका विनाश होगा?');
INSERT INTO "al_umari" VALUES(6,48,'6:48','और हम रसूलों को, इसीलिए भेजते हैं कि वे (आज्ञाकारियों को) शुभ सूचना दें तथा (अवज्ञाकारियों को) डरायें। तो जो ईमान लाये तथा अपने कर्म सुधार लिए, उनके लिए कोई भय नहीं और न वह उदासीन होंगे।');
INSERT INTO "al_umari" VALUES(6,49,'6:49','और जिन्होंने हमारी आयतों को झुठलाया, उन्हें अपनी अवज्ञा के कारण यातना अवश्य मिलेगी।');
INSERT INTO "al_umari" VALUES(6,50,'6:50','(हे नबी!) आप कह दें कि मेरे पास अल्लाह का कोष नहीं है, न मैं परोक्ष का ज्ञान रखता हूँ और न मैं ये कहता हूँ कि मैं कोई फ़रिश्ता हूँ। मैं तो केवल उसीपर चल रहा हूँ, जो मेरी ओर वह़्यी (प्रकाशना) की जा रही है। आप कहें कि क्या अंधा[ तथा आँख वाला बराबर हो जायेंगे? क्या तुम सोच विचार नहीं करते? ');
INSERT INTO "al_umari" VALUES(6,51,'6:51','और इस (वह़्यी) के द्वारा उन्हें सचेत करो, जो इस बात से डरते हों कि वे अपने पालनहार के पास (प्रलय के दिन) एकत्र किये जायेंगे, इस दशा में कि अल्लाह के सिवा कोई सहायक तथा अनुशंसक (सिफ़ारिशी) न होगा, संभवतः वे आज्ञाकारी हो जायेँ।');
INSERT INTO "al_umari" VALUES(6,52,'6:52','(हे नबी!) आप उन्हें अपने से दूर न करें, जो अपने पालनहार की वंदना प्रातः संध्या करते एवं उसकी प्रसन्नता की चाह में लगे रहते हैं। उनके ह़िसाब का कोई भार आपपर नहीं है और न आपके ह़िसाब का कोई भार उनपर[ है, अतः यदि आप उन्हें दूर करेंगे, तो अत्याचारियों में हो जायेंगे। ');
INSERT INTO "al_umari" VALUES(6,53,'6:53','और इसी प्रकार[ हमने कुछ लोगों की परीक्षा कुछ लोगों द्वारा की है, ताकि वे कहें कि क्या यही हैं, जिनपर हमारे बीच से अल्लाह ने उपकार किया है? तो क्या अल्लाह कृतज्ञयों को भली-भाँति जानता नहीं है?');
INSERT INTO "al_umari" VALUES(6,54,'6:54','तथा (हे नबी!) जब आपके पास वे लोग आयें, जो हमारी आयतों (क़ुर्आन) पर ईमान लाये हैं, तो आप कहें कि तुम[ पर सलाम (शान्ति) है। अल्लाह ने अपने ऊपर दया अनिवार्य कर ली है कि तुममें से जो भी अज्ञानता के कारण, कोई कुकर्म कर लेगा, फिर उसके पश्चात् तौबा (क्षमा याचना) कर लेगा और अपना सुधार कर लेगा, तो निःसंदेह अल्लाह अति क्षमाशील दयावान् है। ');
INSERT INTO "al_umari" VALUES(6,55,'6:55','और इसी प्रकार हम आयतों का वर्णन करते हैं और इस लिए ताकि अपराधियों का पथ उजागर हो जाये (और सत्यवादियों का पथ संदिग्ध न हो)।');
INSERT INTO "al_umari" VALUES(6,56,'6:56','(हे नबी!) आप (मुश्रिकों से) कह दें कि मुझे रोक दिया गया है कि मैं उनकी वंदना करूँ, जिन्हें तुम अल्लाह के सिवा पुकारते हो। उनसे कह दो कि मैं तुम्हारी आकांक्षाओं पर नहीं चल सकता। मैंने ऐसा किया तो मैं सत्य से कुपथ हो गया और मैं सुपथों में से नहीं रह जाऊँगा।');
INSERT INTO "al_umari" VALUES(6,57,'6:57','आप कह दें कि मैं अपने पालनहार के खुले तर्क पर स्थित[ हूँ। और तुमने उसे झुठला दिया है। जिस (निर्णय) के लिए तुम शीघ्रता करते हो, वह मेरे पास नहीं। निर्णय तो केवल अल्लाह के अधिकार में है। वह सत्य को वर्णित कर रहा है और सर्वोत्तम निर्णयकारी है। ');
INSERT INTO "al_umari" VALUES(6,58,'6:58','आप कह दें कि जिस (निर्णय) के लिए तुम शीघ्रता कर रहे हो, मेरे अधिकार में होता, तो हमारे और तुम्हारे बीच निर्णय हो गया होता तथा अल्लाह अत्याचारियों[ को भली-भाँति जानता है। ');
INSERT INTO "al_umari" VALUES(6,59,'6:59','और उसी (अल्लाह) के पास ग़ैब (परोक्ष) की कुंजियाँ[ हैं। उन्हें केवल वही जानता है तथा जो कुछ थल और जल में है, वह सबका ज्ञान रखता है और कोई पत्ता नहीं गिरता परन्तु उसे वह जानता है और न कोई अन्न, जो धरती के अंधेरों में हो और न कोई आर्द्र (भीगा) और न कोई शुष्क (सूखा) है, परन्तु वह एक खुली पुस्तक में है। ');
INSERT INTO "al_umari" VALUES(6,60,'6:60','वही है, जो रात्रि में तुम्हारी आत्माओं को ग्रहण कर लेता है तथा दिन में जो कुछ किया है, उसे जानता है। फिर तुम्हें उस (दिन) में जगा देता है, ताकि निर्धारित अवधि पूरी हो जाये[। फिर तुम्हें उसी की ओर प्रत्यागत (वापस) होना है। फिर वह तुम्हें तुम्हारे कर्मों से सूचित कर देगा। ');
INSERT INTO "al_umari" VALUES(6,61,'6:61','तथा वही है, जो अपने सेवकों पर पूरा अधिकार रखता है और तुमपर रक्षकों[ को भेजता है। यहाँ तक कि जब तुममें से किसी के मरण का समय आ जाता है, तो हमारे फ़रिश्ते उसका प्राण ग्रहण कर लेते हैं और वह तनिक भी आलस्य नहीं करते। ');
INSERT INTO "al_umari" VALUES(6,62,'6:62','फिर सब, अल्लाह, अपने वास्तविक स्वामी की ओर वापिस लाये जाते हैं। सावधान! उसी को निर्णय करने का अधिकार है और वह अति शीध्र ह़िसाब लेने वाला है।');
INSERT INTO "al_umari" VALUES(6,63,'6:63','(हे नबी!) उनसे पूछिए कि थल तथा जल के अंधेरों में तुम्हें कौन बचाता है, जिसे तुम विनय पूर्वक और धीरे-धीरे पुकारते हो कि यदि उसने हमें बचा लिया, तो हम अवश्य कृतज्ञों में हो जायेंगे?');
INSERT INTO "al_umari" VALUES(6,64,'6:64','आप कह दें कि अल्लाह ही उससे तथा प्रत्येक आपदा से तुम्हें बचाता है। फिर भी तुम उसका साझी बनाते हो!');
INSERT INTO "al_umari" VALUES(6,65,'6:65','आप उनसे कह दें कि वह इसका सामर्थ्य रखता है कि वह कोई यातना तुम्हारे ऊपर (आकाश) से भेज दे अथवा तुम्हारे पैरों के नीचे (धरती) से या तुम्हें सम्प्रदायों में करके एक को दूसरे के आक्रमण[ का स्वाद चखा दे। देखिये कि हम किस प्रकार आयतों का वर्णन कर रहे हैं कि संभवतः वे समझ जायेँ। ');
INSERT INTO "al_umari" VALUES(6,66,'6:66','और (हे नबी!) आपकी जाति ने इस (क़ुर्आन) को झुठला दिया, जबकि वह सत्य है और आप कह दें कि मैं तुमपर अधिकारी नहीं[ हूँ। ');
INSERT INTO "al_umari" VALUES(6,67,'6:67','प्रत्येक सूचना के पूरे होने का एक निश्चित समय है और शीघ्र ही तुम जान लोगे।');
INSERT INTO "al_umari" VALUES(6,68,'6:68','और जब आप, उन लोगों को देखें, जो हमारी आयतों में दोष निकालते हों, तो उनसे विमुख हो जायेँ, यहाँ तक कि वे किसी दूसरी बात में लग जायें और यदि आपको शैतान भुला दे, तो याद आ जाने के पश्चात् अत्याचारी लोगों के साथ न बैठें।');
INSERT INTO "al_umari" VALUES(6,69,'6:69','तथा उन[ के ह़िसाब में से कुछ का भार उनपर नहीं है, जो अल्लाह से डरते हों, परन्तु याद दिला देना उनका कर्तव्य है, ताकि वे भी डरने लगें।');
INSERT INTO "al_umari" VALUES(6,70,'6:70','तथा आप उन्हें छोड़ें जिन्होंने अपने धर्म को क्रीड़ा और खेल बना लिया है। दरअसल सांसारिक जीवन ने उन्हें धोखे में डाल रखा है। आप इस (क़ुर्आन) द्वारा उन्हें शिक्षा दें। ताकि कोई प्राणी अपने करतूतों के कारण बंधक न बन जाये, जिसका अल्लाह के सिवा कोई सहायक और अभिस्तावक (सिफ़ारिशी) न होगा।फिर यदि वे, सबकुछ बदले में दे दें, तो भी उनसे नहीं लिया जायेगा[। यही लोग अपने करतूतों के कारण बंधक होंगे। उनके लिए उनके कुफ़्र (अविश्वास) के कारण खौलता पेय तथा दुःखदायी यातना होगी। ');
INSERT INTO "al_umari" VALUES(6,71,'6:71','(हे नबी!) उनसे कहिए कि क्या हम अल्लाह के सिवा उनकी वंदना करें, जो हमें कोई लाभ और हानि नहीं पहुँचा सकते? और हम एड़ियों के बल फिर जायेँ, इसके पश्चात कि हमें अल्लाह ने मार्गदर्शन दे दिया है, उसके सामने, जिसे शैतानों ने धरती में बहका दिया हो, वह आश्चर्यचकित हो, उसके साथी उसे पुकार रहे हों कि सीधी राह की ओर हमारे पास आ जाओ[? आप कह दें कि मार्गदर्शन तो वास्तव में वही है, जो अल्लाह का मार्गदर्शन है। और हमें तो, यही आदेश दिया गया है कि हम विश्व के पालनहार के आज्ञाकारी हो जायेँ। ');
INSERT INTO "al_umari" VALUES(6,72,'6:72','और नमाज़ की स्थाप्ना करें और उससे डरते रहें तथा वही है, जिसके पास तुम एकत्र किये जाओगे।');
INSERT INTO "al_umari" VALUES(6,73,'6:73','और वही है, जिसने आकाशों तथा धरती की रचना सत्य के साथ की[ है और जिस दिन वह कहेगा कि "हो जा" तो वह (प्रलय) हो जायेगा। उसका कथन सत्य है और जिस दिन नरसिंघा में फूँक दिया जायेगा, उस दिन उसी का राज्य होगा। वह परोक्ष तथा प्रत्यक्ष का ज्ञानी है और वही गुणी सर्वसूचित है।');
INSERT INTO "al_umari" VALUES(6,74,'6:74','तथा जब इब्राहीम ने अपने पिता आज़र से कहाः क्या आप मुर्तियों को पूज्य बनाते हो? मैं आपको तथा आपकी जाति को खुले कुपथ में देख रहा हूँ।');
INSERT INTO "al_umari" VALUES(6,75,'6:75','और इब्राहीम को इसी प्रकार हम आकाशों तथा धरती के राज्य की व्यवस्था दिखाते रहे और ताकि वह विश्वासियों में हो जाये।');
INSERT INTO "al_umari" VALUES(6,76,'6:76','तो जब उसपर रात छा गयी, तो उसने एक तारा देखा। कहाः ये मेरा पालनहार है। फिर जब वह डूब गया, तो कहाः मैं डूबने वालों से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(6,77,'6:77','फिर जब उसने चाँद को चमकते देखा, तो कहाः ये मेरा पालनहार है। फिर जब वह डूब गया, तो कहाः यदि मुझे मेरे पालनहार ने मार्गदर्शन नहीं दिया, तो मैं अवश्य कुपथों में से हो जाऊँगा।');
INSERT INTO "al_umari" VALUES(6,78,'6:78','फिर जब (प्रातः) सूर्य को चमकते देखा, तो कहाः ये मेरा पालनहार है। ये सबसे बड़ा है। फिर जब वह भी डूब गया, तो उसने कहाः हे मेरी जाति के लोगो! निःसंदेह मैं उससे विरक्त हूँ, जिसे तुम (अल्लाह का) साझी बनाते हो।');
INSERT INTO "al_umari" VALUES(6,79,'6:79','मैंने तो अपना मुख एकाग्र होकर, उसकी ओर कर लिया है, जिसने आकाशों तथा धरती की रचना की है और मैं मुश्रिकों में से नहीं[ हूँ। ');
INSERT INTO "al_umari" VALUES(6,80,'6:80','और जब उसकी जाति ने उससे वाद-झगड़ा किया, तो उसने कहाः क्या तुम अल्लाह के विषय में मुझसे झगड़ रहे हो, जबकि उसने मुझे सुपथ दिखा दिया है तथा मैं उससे नहीं डरता हूँ, जिसे तुम साझी बनाते हो। परन्तु मेरा पालनहार कुछ चाहे (तभी वह मुझे हानि पहुँचा सकता है)। मेरा पालनहार प्रत्येक वस्तु को अपने ज्ञान में समोये हुए है। तो क्या तुम शिक्षा नहीं लेते?');
INSERT INTO "al_umari" VALUES(6,81,'6:81','और मैं उनसे कैसे डरूँ, जिन्हें तुमने उसका साझी बना लिया है, जब तुम उस चीज़ को उसका साझी बनाने से नहीं डरते, जिसका अल्लाह ने कोई तर्क (प्रमाण) नहीं उतारा है? तो दोनों पक्षों में कौन अधिक शान्त रहने का अधिकारी है, यदि तुम कुछ ज्ञान रखते हो?');
INSERT INTO "al_umari" VALUES(6,82,'6:82','जो लोग ईमान लाये और अपने ईमान को अत्याचार (शिर्क) से लिप्त नहीं[ किया, उन्हीं के लिए शान्ति है तथा वही मार्गदर्शन पर हैं। ');
INSERT INTO "al_umari" VALUES(6,83,'6:83','ये हमारा तर्क था, जो हमने इब्राहीम को उसकी जाति के विरुध्द प्रदान किया, हम जिसके पदों[ को चाहते हैं, ऊँचा कर देते हैं। वास्तव में, आपका पालनहार गुणी तथा ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(6,84,'6:84','और हमने, इब्राहीम को (पुत्र) इस्ह़ाक़ तथा (पौत्र) याक़ूब प्रदान किये। प्रत्येक को हमने मार्गदर्शन दिया और उससे पहले हमने नूह़ को मार्गदर्शन दिया और इब्राहीम की संतति में से दावूद, सुलैमान, अय्यूब, यूसुफ, मूसा तथा हारून को। इसी प्रकार हम सदाचारियों को प्रतिफल प्रदान करते हैं।');
INSERT INTO "al_umari" VALUES(6,85,'6:85','तथा ज़करिय्या, यह़्या, ईसा और इल्यास को। ये सभी सदाचारियों में थे।');
INSERT INTO "al_umari" VALUES(6,86,'6:86','तथा इस्माईल, यस्अ, यूनुस और लूत को। प्रत्येक को हमने संसार वासियों पर प्रधानता दी है।');
INSERT INTO "al_umari" VALUES(6,87,'6:87','तथा उनके पूर्वजों, उनकी संतति तथा उनके भाईयों को। हमने इनसब को निर्वाचित कर लिया और इन्हें सुपथ दिखा दिया था।');
INSERT INTO "al_umari" VALUES(6,88,'6:88','यही अल्लाह का मार्गदर्शन है, जिसके द्वारा अपने भक्तों में से जिसे चाहे, सुपथ दर्शा देता है और यदि वे शिर्क करते, तो उनका सब किया-धरा व्यर्थ हो जाता[। ');
INSERT INTO "al_umari" VALUES(6,89,'6:89','(हे नबी!) ये वे लोग हैं, जिन्हें हमने पुस्तक, निर्णय शक्ति एवं नुबूवत प्रदान की। फिर यदि ये (मुश्रिक) इन बातों को नहीं मानते, तो हमने इसे, कुछ ऐसे लोगों को सौंप दिया है, जो इसका इन्कार नहीं करते।');
INSERT INTO "al_umari" VALUES(6,90,'6:90','(हे नबी!) ये वे लोग हैं, जिन्हें अल्लाह ने सुपथ दर्शा दिया, तो आपभी उन्हीं के मार्गदर्शन पर चलें तथा कह दें कि मैं इस (कार्य)[ पर तुमसे कोई प्रतिदान नहीं माँगता। ये सब संसार वासियों के लिए एक शिक्षा के सिवा कुछ नहीं है। ');
INSERT INTO "al_umari" VALUES(6,91,'6:91','तथा उन्होंने अल्लाह का सम्मान जैसे करना चाहिए, नहीं किया। जब उन्होंने कहा कि अल्लाह ने किसी पुरुष पर कुछ नहीं उतारा। उनसे पूछिए कि वो पुस्तक, जिसे मूसा लाये, जो लोगों के लिए प्रकाश तथा मार्गदर्शन है, किसने उतारी है, जिसे तुम पन्नों में करके रखते हो? जिसमें से तुम कुछ को, लोगों के लिए बयान करते हो और बहुत-कुछ छुपा रहे हो तथा तुम्हें उसका ज्ञान दिया गया, जिसका तुम्हें और तुम्हारे पूर्वजों को ज्ञान न था? और कह दें कि अल्लाह ने। फिर उन्हें उनके विवादों में खेलते हुए छोड़ दें।');
INSERT INTO "al_umari" VALUES(6,92,'6:92','तथा ये (क़ुर्आन) एक पुस्तक है, जिसे हमने (तौरात के समान) उतारा है। जो शुभ तथा अपने से पूर्व (की पुस्तकों) को सच बताने वाली है, ताकि आप "उम्मुल क़ुरा" (मक्का नगर) तथा उसके चतुर्दिक के निवासियों को सचेत[ करें तथा जो परलोक के प्रति विश्वास रखते हैं, वही इसपर ईमान लाते हैं और वही अपनी नमाज़ों का पालन करते हैं।');
INSERT INTO "al_umari" VALUES(6,93,'6:93','और उससे बड़ा अत्याचारी कौन होगा, जो अल्लाह पर झूठ घड़े और कहे कि मेरी ओर प्रकाशना (वह़्यी) की गयी है, जबकि उसकी ओर वह़्यी (प्रकाशना) नहीं की गयी? तथा जो ये कहे कि अल्लाह ने जो उतारा है, उसके समान मैं भी उतार दूँगा? और (हे नबी!) आप यदि ऐसे अत्याचारी को मरण की घोर दशा में देखते, जबकि फ़रिश्ते उनकी ओर हाथ बढ़ाये (कहते हैं:) अपने प्राण निकालो! आज तुम्हें इस कारण अपमानकारी यातना दी जायेगी कि तुम अल्लाह पर झूठ बोलते और उसकी आयतों को (मानने से) अभिमान करते थे।');
INSERT INTO "al_umari" VALUES(6,94,'6:94','तथा (अल्लाह) कहेगाः तुम मेरे सामने उसी प्रकार अकेले आ गये, जैसे तुम्हें प्रथम बार हमने पैदा किया था तथा हमने जो कुछ दिया था, अपने पीछे (संसार ही में) छोड़ आये और आज हम तुम्हारे साथ, तुम्हारे अभिस्तावकों (सिफ़ारिशियों) को नहीं देख रहे हैं, जिनके बारे में तुम्हारा भ्रम था कि तुम्हारे कामों में वे (अल्लाह के) साझी हैं? निश्चय तुम्हारे बीच के संबंध भंग हो गये हैं और तुम्हारा सब भ्रम खो गया है।');
INSERT INTO "al_umari" VALUES(6,95,'6:95','वास्तव में, अल्लाह ही अन्न तथा गुठली को (धरती के भीतर) फाड़ने वाला है। वह निर्जीव से जीवित को निकालता है तथा जीवित से निर्जीव को निकालने वाला है। वही अल्लाह (सत्य पूज्य) है। फिर तुम कहाँ बहके जा रहे हो?');
INSERT INTO "al_umari" VALUES(6,96,'6:96','वह प्रभात का तड़काने वाला है और उसीने सुख के लिए रात्रि बनाई तथा सूर्य और चाँद ह़िसाब के लिए बनाये। ये प्रभावी गुणी का निर्धारित किया हुआ अंकन (माप)[ है। ');
INSERT INTO "al_umari" VALUES(6,97,'6:97','उसीने तुम्हारे लिए तारे बनाये हैं, ताकि उनकी सहायता से थल तथा जल के अंधकारों में रास्ता पाओ। हमने (अपनी दया के) लक्षणों का उनके लिए विवरण दे दिया है, जो लोग ज्ञान रखते हैं।');
INSERT INTO "al_umari" VALUES(6,98,'6:98','वही है, जिसने तुम्हें एक जीव से पैदा किया। फिर तुम्हारे लिए (संसार में) रहने का स्थान है और एक समर्पन (मरण) का स्थान है। हमने उन्हें अपनी आयतों (लक्षणों) का विवरण दे दिया, जो समझ-बूझ रखते हैं।');
INSERT INTO "al_umari" VALUES(6,99,'6:99','वही है, जिसने आकाश से जल की वर्षा की, फिर हमने उससे प्रत्येक प्रकार की उपज निकाल दी। फिर उससे हरियाली निकाल दी। फिर उससे तह पर तह दाने निकालते हैं तथा खजूर के गाभ से गुच्छे झुके हुए और अंगूरों तथा ज़ैतून और अनार के बाग़, समरूप तथा स्वाद में अलग-अलग। उसके फल को देखो, जब फल लाता है तथा उसके पकने को। निःसंदेह इनमें उन लोगों के लिए बड़ी निशानियाँ (लक्षण)[ हैं, जो ईमान लाते हैं। ');
INSERT INTO "al_umari" VALUES(6,100,'6:100','और उन्होंने जिन्नों को अल्लाह का साझी बना लिया। जबकि अल्लाह ही ने उनकी उत्पत्ति की है और बिना ज्ञान के उसके लिए पुत्र तथा पुत्रियाँ घड़ लीं। वह पवित्र तथा उच्च है, उन बातों से, जो वे लोग कह रहे हैं।');
INSERT INTO "al_umari" VALUES(6,101,'6:101','वह आकाशों तथा धरती का अविष्कारक है, उसके संतान कहाँ से हो सकते हैं, जबकि उसकी पत्नी नहीं है? तथा उसीने प्रत्येक वस्तु को पैदा किया है और वह प्रत्येक वस्तु को भली-भाँति जानता है।');
INSERT INTO "al_umari" VALUES(6,102,'6:102','वही अल्लाह तुम्हारा पालनहार है, उसके अतिरिक्त कोई सच्चा पूज्य नहीं। वह प्रत्येक वस्तु का उत्पत्तिकार है। अतः उसकी इबादत (वंदना) करो तथा वही प्रत्येक चीज़ का अभिरक्षक है।');
INSERT INTO "al_umari" VALUES(6,103,'6:103','उसका, आँखें इद्राक नहीं कर सकतीं[, जबकी वह सब कुछ देख रहा है। वह अत्यंत सूक्ष्मदर्शी और सब चीज़ों से अवगत है। ');
INSERT INTO "al_umari" VALUES(6,104,'6:104','तुम्हारे पास निशानियाँ आ चुकी हैं। तो जिसने समझ-बूझ से काम लिया, उसका लाभ उसी के लिए है और जो अन्धा हो गया, तो उसकी हानि उसीपर है और मैं तुमपर संरक्षक[ नहीं हूँ। ');
INSERT INTO "al_umari" VALUES(6,105,'6:105','और इसी प्रकार, हम अनेक शैलियों में आयतों का वर्णन कर रहे हैं। ताकि वे (काफ़िर) कहें कि आपने पढ़[ लिया है और ताकि हम उन लोगों के लिए (तर्कों को) उजागर कर दें, जो ज्ञान रखते हैं। ');
INSERT INTO "al_umari" VALUES(6,106,'6:106','आप उसपर चलें, जो आपपर आपके पालनहार की ओर से वह़्यी (प्रकाशना) की जा रही है। उसके सिवा कोई सत्य पूज्य नहीं है और मुश्रिकों की बातों पर ध्यान न दें।');
INSERT INTO "al_umari" VALUES(6,107,'6:107','और यदि अल्लाह चाहता, तो वो लोग साझी न बनाते और हमने आपको उनपर निरीक्षक नहीं बनाया है और न ही आप उनपर[ अधिकारी हैं। ');
INSERT INTO "al_umari" VALUES(6,108,'6:108','और हे ईमान वालो! उन्हें बुरा न कहो, जिन (मूर्तियों) को वे अल्लाह के सिवा पुकारते हैं। अन्यथा, वे लोग अज्ञानता के कारण अति करके अल्लाह को बुरा कहेंगे। इसी प्रकार, हमने प्रत्येक समुदाय के लिए उनके कर्म को सुशोभित बना दिया है। फिर उनके पालनहार की ओर ही उन्हें जाना है। तो उन्हें बता देगा, जो वे करते रहे।');
INSERT INTO "al_umari" VALUES(6,109,'6:109','और उन मुश्रिकों ने बलपूर्वक शपथें लीं कि यदि हमारे पास कोई आयत (निशानी) आ जाये, तो हम उसपर अवश्य ईमान लायेंगे। आप कह दें: आयतें (निशानियाँ) तो अल्लाह ही के पास हैं और (हे ईमान वालो!) तुम्हें क्या पता कि वह निशानियाँ जब आ जायेँगी, तो वे ईमान[ नहीं लायेंगे। ');
INSERT INTO "al_umari" VALUES(6,110,'6:110','और हम उनके दिलों और आँखों को ऐसे ही फेर[ देंगे, जैसे वे पहली बार इस (क़ुर्आन) पर ईमान नहीं लाये और हम उन्हें उनके कुकर्मों में बहकते छोड़ देंगे। ');
INSERT INTO "al_umari" VALUES(6,111,'6:111','और यदि हम इनकी ओर (आकाश से) फ़रिश्ते उतार देते और इनसे मुर्दे बात करते और इनके समक्ष प्रत्येक वस्तु एकत्र कर देते, तबभी ये ईमान नहीं लाते, परन्तु जिसे अल्लाह (मार्गदर्शन देना) चाहता। और इनमें से अधिक्तर (तथ्य से) अज्ञान हैं।');
INSERT INTO "al_umari" VALUES(6,112,'6:112','और (हे नबी!) इसी प्रकार, हमने मनुष्यों तथा जिन्नों में से प्रत्येक नबी का शत्रु बना दिया, जो धोखा देने के लिए एक-दूसरे को शोभनीय बात सुझाते रहते हैं और यदि आपका पालनहार चाहता, तो ऐसा नहीं करते। तो आप उन्हें छोड़ दें और उनकी घड़ी हई बातों को।');
INSERT INTO "al_umari" VALUES(6,113,'6:113','(वे ऐसा इस लिए करते हैं) ताकि उसकी ओर, उन लोगों के दिल झुक जायें, जो प्रलोक पर विश्वास नहीं रखते और ताकि वे उससे प्रसन्न हो जायेँ और ताकि वे भी वही कुकर्म करने लगें, जो कुकर्म वे लोग कर रहे हैं।');
INSERT INTO "al_umari" VALUES(6,114,'6:114','(हे नबी!) उनसे कहो कि क्या मैं अल्लाह के सिवा किसी दूसरे न्यायकारी की खोज करूँ, जबकि उसीने तुम्हारी ओर ये खुली पुस्तक (क़ुर्आन) उतारी[ है? तथा जिहें हमने पुस्तक प्रदान की है, वे जानते हैं कि ये क़ुर्आन आपके पालनहार की ओर से सत्य के साथ उतरा है। अतः आप संदेह करने वालों में से न हों।');
INSERT INTO "al_umari" VALUES(6,115,'6:115','आपके पालनहार की बात सत्य तथा न्याय की है, कोई उसकी बात (नियम) बदल नहीं सकता और वह सबकुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(6,116,'6:116','और (हे नबी!) यदि, आप संसार के अधिक्तर लोगों की बात मानेंगे, तो वे आपको अल्लाह के मार्ग से बहका देंगे। वे केवल अनुमान पर चलते[ और आँकलन करते हैं। ');
INSERT INTO "al_umari" VALUES(6,117,'6:117','वास्तव में, आपका पालनहार ही अधिक जानता है कि कौन उसकी राह से बहकता है तथा वही उन्हें भी जानता है, जो सुपथ पर हैं।');
INSERT INTO "al_umari" VALUES(6,118,'6:118','तो उन पशुओं में से, जिनपर वध करते समय अल्लाह का नाम लिया गया हो खाओ[, यदि तुम उसकी आयतों (आदेशों) पर ईमान (विश्वास) रखते हो। ');
INSERT INTO "al_umari" VALUES(6,119,'6:119','और तुम्हारे, उसमें से न खाने का क्या कारण है, जिसपर अल्लाह का नाम लिया गया[ हो, जबकि उसने तुम्हारे लिए स्पष्ट कर दिया है, जिसे तुमपर ह़राम (अवैध) किया है? परन्तु जिस (वर्जित) के (खाने के पर) विवश कर दिये जाओ और वास्तव में, बहुत-से लोग अपनी मनमानी के लिए, लोगों को अपनी अज्ञानता के कारण बहकाते हैं। निश्चय आपका पालनहार उल्लंघनकारियों को भली-भाँति जानता है।');
INSERT INTO "al_umari" VALUES(6,120,'6:120','(हे लोगो!) खुले तथा छुपे पाप छोड़ दो। जो लोग पाप कमाते हैं, वे अपने कुकर्मों का प्रतिकार (बदला) दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(6,121,'6:121','तथा उसमें से न खाओ, जिसपर अल्लाह का नाम न लिया गया हो। वास्तव में, उसे खाना (अल्लाह की) अवज्ञा है। निःसंदेंह, शैतान अपने सहायकों के मन में संशय डालते रहते हैं, ताकि वे तुमसे विवाद करें[ और यदि तुमने उनकी बात मान ली, तो निश्चय तुम मुश्रिक हो। ');
INSERT INTO "al_umari" VALUES(6,122,'6:122','तो क्या, जो निर्जीव रहा हो, फिर हमने उसे जीवन प्रदान किया हो तथा उसके लिए प्रकाश बना दिया हो, जिसके उजाले में वह लोगों के बीच चल रहा हो, उस जैसा हो सकता है, जो अंधेरों में हो, उससे निकल न रहा हो[? इसी प्रकार, काफ़िरों के लिए उनके कुकर्म सुंदर बना दिये गये हैं। ');
INSERT INTO "al_umari" VALUES(6,123,'6:123','और इसी प्रकार, हमने प्रत्येक बस्ती में उसके बड़े अपराधियों को लगा दिया, ताकि उससे षड्यंत्र रचें तथा वे अपने ही विरुध्द षड्यंत्र रचते[ हैं, परन्तु समझते नहीं हैं। ');
INSERT INTO "al_umari" VALUES(6,124,'6:124','और जब उनके पास कोई निशानी आती है, तो कहते हैं कि हम उसे कदापि नहीं मानेंगे, जब तक उसी के समान हमें भी प्रदान न किया जाये, जो अल्लाह के रसूलों को प्रदान किया गया है। अल्लाह ही अधिक जानता है कि अपना संदेश पहुँचाने का काम किससे ले। जो अपराधी हैं, शीध्र ही अल्लाह के पास उन्हें अपमान तथा कड़ी यातना, उस षड्यंत्र के बदले में मिलेगी, जो वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(6,125,'6:125','तो जिसे अल्लाह मार्ग दिखाना चाहता है, उसका सीना (वक्ष) इस्लाम के लिए खोल देता है और जिसे कुपथ करना चाहता है, उसका सीना संकीर्ण (तंग) कर देता है। जैसे वह बड़ी कठिनाई से आकाश पर चढ़ रहा[ हो। इसी प्रकार, अल्लाह उनपर यातना भेज देता है, जो ईमान नहीं लाते। ');
INSERT INTO "al_umari" VALUES(6,126,'6:126','और यही (इस्लाम) आपके पालनहार की सीधी राह है। हमने उन लोगों के लिए आयतें खोल दी हैं, जो शिक्षा ग्रहण करते हों।');
INSERT INTO "al_umari" VALUES(6,127,'6:127','उन्हीं के लिए आपके पालनहार के पास शान्ति का घर (स्वर्ग) है और वही उनके सुकर्मों के कारण उनका सहायक होगा।');
INSERT INTO "al_umari" VALUES(6,128,'6:128','तथा (हे नबी!) याद करो, जब वह सबको एकत्र करके (कहेगाः) हे जिन्नों के गिरोह! तुमने बहुत-से मनुष्यों को कुपथ कर दिया और मानव में से उनके मित्र कहेंगे कि हे हमारे पालनहार! हम एक-दूसरे से लाभान्वित होते रहे[ और वह समय आ पहुँचा, जो तूने हमारे लिए निर्धारित किया था। (अल्लाह) कहेगाः तुम सबका आवास नरक है, जिसमें सदावासी होगे। परन्तु, जिसे अल्लाह (बचाना) चाहे। वास्तव में, आपका पालनहार गुणी सर्व ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(6,129,'6:129','और इसी प्रकार, हम अत्याचारियों को उनके कुकर्मों के कारण एक-दूसरे का सहायक बना देते हैं।');
INSERT INTO "al_umari" VALUES(6,130,'6:130','(तथा कहेगाः) हे जिन्नों तथा मनुष्यों के (मुश्रिक) समुदाय! क्या तुम्हारे पास तुम्हीं में से रसूल नहीं आये,[ जो तुम्हें हमारी आयतें सुनाते और तुम्हें तुम्हारे इस दिन (के आने) से सावधान करते? वे कहेंगेः हम स्वयं अपने ही विरुध्द गवाह हैं। उन्हें सांसारिक जीवन ने धोखे में रखा था और अपने ही विरुध्द गवाह हो गये कि वास्तव में वही काफ़िर थे। ');
INSERT INTO "al_umari" VALUES(6,131,'6:131','(हे नबी!) ये (नबियों का भेजना) इसलिए हुआ कि आपका पालनहार ऐसा नहीं है कि अत्याचार से बस्तियों का विनाश कर दे[ , जबकि उसके निवासी (सत्य से) अचेत रहे हों। ');
INSERT INTO "al_umari" VALUES(6,132,'6:132','प्रत्येक के लिए उसके कर्मानुसार पद हैं और आपका पालनहार लोगों के कर्मों से अचेत नहीं है।');
INSERT INTO "al_umari" VALUES(6,133,'6:133','तथा आपका पालनहार निस्पृह दयाशील है। वह चाहे तो तुम्हें ले जाये और तुम्हारे स्थान पर दूसरों को ले आये। जैसे तुम लोगों को दूसरे लोगों की संतति से पैदा किया है।');
INSERT INTO "al_umari" VALUES(6,134,'6:134','तुम्हें जिस (प्रलय) का वचन दिया जा रहा है, उसे अवश्य आना है और तुम (अल्लाह को) विवश नहीं कर लकते।');
INSERT INTO "al_umari" VALUES(6,135,'6:135','आप कह दें: हे मेरी जाति के लोगो! (यदि तुम नहीं मानते) तो अपनी दशा पर कर्म करते रहो। मैं भी कर्म कर रहा हूँ। शीघ्र ही तुम्हें ये ज्ञान हो जायेगा कि किसका अन्त (परिणाम)[ अच्छा है। निःसंदेह अत्याचारी सफल नहीं होंगे। ');
INSERT INTO "al_umari" VALUES(6,136,'6:136','तथा उन लोगों ने, उस खेती और पशुओं में, जिन्हें अल्लाह ने पैदा किया है, उसका एक भाग निश्चित कर दिया, फिर अपने विचार से कहते हैं: ये अल्लाह का है और ये उन (देवताओं) का है, जिन्हें उन्होंने (अल्लाह का) साझी बनाया है। फिर जो उनके बनाये हुए साझियों का है, वह तो अल्लाह को नहीं पहुँचता, परन्तु जो अल्लाह का है, वह उनके साझियों[ को पहुँचता है। वे क्या ही बुरा निर्णय करते हैं! ');
INSERT INTO "al_umari" VALUES(6,137,'6:137','और इसी प्रकार, बहुत-से मुश्रिकों के लिए अपनी संतान के वध करने को उनके बनाये हुए साझियों ने सुशोभित कर दिया है, ताकि उनका विनाश कर दें और ताकि उनके धर्म को उनपर संदिग्ध कर दें और यदि अल्लाह चाहता, तो वे ये (कुकर्म) नहीं करते। अतः, आप उन्हें छोड़[ दें तथा उनकी बनायी हुई बातों को। ');
INSERT INTO "al_umari" VALUES(6,138,'6:138','तथा वे कहते हैं कि ये पशु और खेत वर्जित हैं, इन्हें वही खा सकता है, जिसे हम अपने विचार से खिलाना चाहें, फिर कुछ पशु हैं, जिनकी पीठ ह़राम[ (वर्जित) हैं और कुछ पशु हैं, जिनपर (वध करते समय) अल्लाह का नाम नहीं लेते, अल्लाह पर आरोप लगाने के कारण, अल्लाह उन्हें उनके आरोप लगाने का बदला अवश्य देगा। ');
INSERT INTO "al_umari" VALUES(6,139,'6:139','तथा उन्होंने कहा कि जो इन पशुओं के गर्भों में है, वो हमारे पुरुषों के लिए विशेष है और हमारी पत्नियों के लिए वर्जित है और यदि मुर्दा हो, तो सभी उसमें साझी हो सकते[ हैं। अल्लाह उनके विशेष करने का कुफल उन्हें अवश्य देगा। वास्तव में, वह तत्वज्ञ अति ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(6,140,'6:140','वास्तव में वे क्षति में पड़ गये, जिन्होंने मूर्खता से किसी ज्ञान के बिना अपनी संतान को वध किया[ और उस जीविका को, जो अल्लाह ने उन्हें प्रदान की, अल्लाह पर आरोप लगाकर, अवैध बना लिया, वे बहक गये और सीधी राह पर नहीं आ सके। ');
INSERT INTO "al_umari" VALUES(6,141,'6:141','अल्लाह वही है, जिसने बेलों वाले तथा बिना बेलों वाले बाग़ पैदा किये तथा खजूर और खेत, जिनसे विभिन्न प्रकार की पैदावार होती है और ज़ैतुन तथा अनार समरूप तथा स्वाद में विभिन्न, इसका फल खाओ, जब फले और फल तोड़ने के समय कुछ दान करो तथा अपव्यय[ (बेजा खर्च) न करो। निःसंदेह, अल्लाह बेजा ख़र्च करने वालों से प्रेम नहीं करता। ');
INSERT INTO "al_umari" VALUES(6,142,'6:142','तथा चौपायों में कुछ सवारी और बोझ लादने योग्य[ हैं और कुछ धरती से लगे हुए। तुम उनमें से खाओ, जो अल्लाह ने तुम्हें जीविका प्रदान की है और शैतान के पदचिन्हों पर न चलो, वास्तव में, वह तुम्हारा खुला शत्रु है।');
INSERT INTO "al_umari" VALUES(6,143,'6:143','आठ पशु आपस में जोड़े हैं: भेड़ में से दो तथा बकरी में से दो। आप उनसे पूछिये कि क्या अल्लाह ने दोनों के नर ह़राम (वर्जित) किये अथवा दोनों की मादा अथवा दोनों के गर्भ में जो बच्चे हों? मुझे ज्ञान के साथ बताओ, यदि तुम सच्चे हो।');
INSERT INTO "al_umari" VALUES(6,144,'6:144','और ऊँट में से दो तथा गाय में से दो। आप पूछिये कि क्या अल्लाह ने दोनों के नर ह़राम (वर्जित) किये हैं अथवा दोनों की मादा अथवा दोनों के गर्भ में जो बच्चे हों? क्या तुम उपस्थित थे, जब अल्लाह ने तुम्हें इसका आदेश दिया था, तो बताओ? उससे बड़ा अत्याचारी कौन होगा, जो बिना ज्ञान के अल्लाह पर झूठ घड़े? निश्चय अल्लाह अत्याचारियों को संमार्ग नहीं दिखाता।');
INSERT INTO "al_umari" VALUES(6,145,'6:145','(हे नबी!) आप कह दें कि उसमें, जो मेरी ओर वह़्यी (प्रकाशना) की गई है, इन[ में से खाने वालों पर कोई चीज़ वर्जित नहीं है, सिवाय उसके, जो मरा हुआ हो , बहा हुआ रक्त हो या सुअर का मांस हो; क्योंकि वह अशुध्द है, अथवा अवैध हो, जिसे अल्लाह के सिवा दूसरे के नाम पर वध किया गया हो। परन्तु जो विवश हो जाये (तो वह खा सकता है) यदि वह द्रोही तथा सीमा लांघने वाला न हो। तो वास्तव में आप का पालनहार अति क्षमी दयावान् है।');
INSERT INTO "al_umari" VALUES(6,146,'6:146','तथा हमने यहूदियों पर नखधारी[ जीव ह़राम कर दिये थे और गाय तथा बकरी में से उनपर दोनों की चर्बियाँ ह़राम (वर्जित) कर दी थीं। परन्तु जो दोनों की पीठों या आँतों से लगी हों अथवा जो किसी हड्डी से मिली हुई हो। ये हमने उनकी अवज्ञा के कारण उन्हें प्रतिकार (बदला) दिया था तथा निश्चय हम सच्चे हैं।');
INSERT INTO "al_umari" VALUES(6,147,'6:147','फर (हे नबी!) यदि ये लोग आपको झुठलायें, तो कह दें कि तुम्हारा पालनहार विशाल दयाकारी है तथा उसकी यातना को अपराधियों से फेरा नहीं जा सकेगा।');
INSERT INTO "al_umari" VALUES(6,148,'6:148','मिश्रणवादी अवश्य कहेंगेः यदि अल्लाह चाहता, तो हम तथा हमारे पूर्वज (अल्लाह का) साझी न बनाते और न कुछ ह़राम (वर्जित) करते। इसी प्रकार, इनसे पूर्व के लोगों ने (रसूलों को) झुठलाया था, यहाँ तक कि हमारी यातना का स्वाद चख लिया। (हे नबी!) उनसे पूछिये कि क्या तुम्हारे पास (इस विषय में) कोई ज्ञान है, जिसे तुम हमारे समक्ष प्रस्तुत कर सको? तुम तो केवल अनुमान पर चलते हो और केवल आँकलन कर रहे हो।');
INSERT INTO "al_umari" VALUES(6,149,'6:149','(हे नबी!) आप कह दें कि पूर्ण तर्क अल्लाह ही का है। तो यदि वह चाहता, तो तुम सबको सुपथ दिखा देता[। ');
INSERT INTO "al_umari" VALUES(6,150,'6:150','आप कहिए कि अपने साक्षियों (गवाहों) को लाओ[, जो साक्ष्य दें कि अल्लाह ने इसे ह़राम (अवैध) कर दिया है। फिर यदि वे साक्ष्य (गवाही) दें, तबभी आप उनके साथ होकर इसे न मानें तथा उनकी मनमानी पर न चलें, जिन्होंने हमारी आयतों को झुठला दिया और परलोक पर ईमान (विश्वास) नहीं रखते तथा दूसरों को अपने पालनहार के बराबर करते हैं। ');
INSERT INTO "al_umari" VALUES(6,151,'6:151','आप उनसे कहें कि आओ, मैं तुम्हें (आयतें) पढ़कर सुना दूँ कि तुमपर, तुम्हारे पालनहार ने क्या ह़राम (अवैध) किया है? वो ये है कि किसी चीज़ को उसका साझी न बनाओ, माता-पिता के साथ उपकार करो और अपनी संतानों को निर्धनता के भय से वध न करो। हम तुम्हें जीविका देते हैं और उन्हें भी देंगे और निर्लज्जा की बातों के समीप भी न जाओ, खुली हों अथवा छुपी और जिस प्राण को अल्लाह ने ह़राम (अवैध) कर दिया है, उसे वध न करो, परन्तु उचित कारण[ से। अल्लाह ने तुम्हें इसका आदेश दिया है, ताकि इसे समझो। ');
INSERT INTO "al_umari" VALUES(6,152,'6:152','और अनाथ के धन के समीप न जाओ, परन्तु ऐसे ढंग से, जो उचित हो। यहाँ तक कि वह अपनी युवा अवस्था को पहुँच जाये तथा नाप-तोल न्याय के साथ पूरा करो। हम किसी प्राण पर उसकी सकत से अधिक भार नहीं रखते और जब बोलो तो न्याय करो, यद्यपि समीपवर्ती ही क्यों न हो और अल्लाह का वचन पूरा करो, उसने तुम्हें इसका आदेश दिया है, संभवतः तुम शिक्षा ग्रहण करो।');
INSERT INTO "al_umari" VALUES(6,153,'6:153','तथा (उसने बताया कि) ये (इस्लाम ही) अल्लाह की सीधी राह[ है। अतः इसीपर चलो और दूसरी राहों पर न चलो, अन्यथा वह तुम्हें उसकी राह से दूर करके तित्तर-बित्तर कर देंगे। यही है, जिसका आदेश उसने तुम्हें दिया है, ताकि तुम उसके आज्ञाकारी रहो। ');
INSERT INTO "al_umari" VALUES(6,154,'6:154','फिर हमने मूसा को पुस्तक (तौरात) प्रदान की थी, उसपर पुरस्कार पूरा करने के लिए, जो सदाचारी हो तथा प्रत्येक वस्तु के विवरण के लिए तथा ये मार्गदर्शन और दया थी, ताकि वे अपने पालनहार से मिलने पर ईमान लायें।');
INSERT INTO "al_umari" VALUES(6,155,'6:155','तथा (उसी प्रकार) ये पुस्तक (क़ुर्आन) हमने अवतरित की है, ये बड़ा शुभकारी है, अतः इसपर चलो[ और अल्लाह से डरते रहो, ताकि तुमपर दया की जाये। ');
INSERT INTO "al_umari" VALUES(6,156,'6:156','ताकि (हे अरब वासियो!) तुम ये न कहो कि हमसे पूर्व दो समुदाय (यहूद तथा ईसाई) पर पुस्तक उतारी गयी और हम उनके पढ़ने-पढ़ाने से अनजान रह गये।');
INSERT INTO "al_umari" VALUES(6,157,'6:157','या ये न कहो कि यदि हमपर पुस्तक उतारी जाती, तो हम निश्चय उनसे अधिक सीधी राह पर होते, तो अब तुम्हारे पास तुम्हारे पालनहार की ओर से एक खुला तर्क आ गया, मार्गदर्शन तथा दया आ गयी। फिर उससे बड़ा अत्यचारी कौन होगा, जो अल्लाह की आयतों को मिथ्या कह दे और उनसे कतरा जाये? और जो लोग हमारी आयतों से कतराते हैं, हम उनके कतराने के बदले उन्हें कड़ी यातना देंगे।');
INSERT INTO "al_umari" VALUES(6,158,'6:158','क्या वे लोग इसी बात की प्रतीक्षा कर रहे हैं कि उनके पास फ़रिश्ते आ जायें, या स्वयं उनका पालनहार आ जाये या आपके पालनहार की कोई आयत (निशानी) आ जाये?[ जिस दिन आपके पालनहार की कोई निशानी आ जायेगी, तो किसी प्राणी को उसका ईमान लाभ नहीं देगा, जो पहले ईमान न लाया हो या अपने ईमान की स्थिति में कोई सत्कर्म न किया हो। आप कह दें कि तुम प्रतीक्षा करो, हम भी प्रतीक्षा कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(6,159,'6:159','जिन लोगों ने अपने धर्म में विभेद किया और कई समुदाय हो गये, (हे नबी!) आपका उनसे कोई संबंध नहीं, उनका निर्णय अल्लाह को करना है, फिर वह उन्हें बतायेगा कि वे क्या कर रहे थे।');
INSERT INTO "al_umari" VALUES(6,160,'6:160','जो (प्रलय के दिन) एक सत्कर्म लेकर (अल्लाह) से मिलेगा, उसे उसके दस गुना प्रतिफल मिलेगा और जो कुकर्म लायेगा, तो उसको उसी के बराबर कुफल दिया जायेगा तथा उनपर अत्याचार नहीं किया जायेगा।');
INSERT INTO "al_umari" VALUES(6,161,'6:161','(हे नबी!) आप कह दें कि मेरे पालनहार ने निश्चय मुझे सीधी राह (सुपथ) दिखा दी है। वही सीधा धर्म, जो एकेश्वरवादी इब्राहीम का धर्म था और वह मुश्रिकों में से न था।');
INSERT INTO "al_umari" VALUES(6,162,'6:162','आप कह दें कि निश्चय मेरी नमाज़, मेरी क़ुर्बानी तथा मेरा जीवन-मरण संसार के पालनहार अल्लाह के लिए है।');
INSERT INTO "al_umari" VALUES(6,163,'6:163','जिसका कोई साझी नहीं तथा मुझे इसी का आदेश दिया गया है और मैं प्रथम मुसलमानों में से हूँ।');
INSERT INTO "al_umari" VALUES(6,164,'6:164','आप उनसे कह दें कि क्या मैं अल्लाह के सिवा किसी और पालनहार की खोज करूँ? जबकि वह (अल्लाह) प्रत्येक चीज़ का पालनहार है तथा कोई प्राणी, कोई भी कुकर्म करेगा, तो उसका भार उसी के ऊपर होगा और कोई किसी दूसरे का बोझ नहीं उठायेगा। फिर (अंततः) तुम्हें अपने पालनहार के पास ही जाना है। तो जिन बातों में तुम विभेद कर रहे हो वो तुम्हें बता देगा।');
INSERT INTO "al_umari" VALUES(6,165,'6:165','वही है, जिसने तुम्हें धरती में अधिकार दिया है और तुममें से कुछ को (धन शक्ति में) दूसरे से कई श्रेणियाँ ऊँचा किया है। ताकि उसमें तुम्हारी परीक्षा[ ले, जो तुम्हें दिया है। वास्तव में, आपका पालनहार शीघ्र ही दण्ड देने वाला है और वास्तव में, वह अति क्षमी दयावान् है।');
INSERT INTO "al_umari" VALUES(7,1,'7:1','अलिफ़, लाम, मीम, साद।');
INSERT INTO "al_umari" VALUES(7,2,'7:2','ये पुस्तक है, जो आपकी ओर उतारी गयी है। अतः (हे नबी!) आपके मन में इससे कोई संकोच न हो, ताकि आप इसके द्वारा सावधान करें[ और ईमान वालों के लिए उपदेश है। ');
INSERT INTO "al_umari" VALUES(7,3,'7:3','(हे लोगो!) जो तुम्हारे पालनहार की ओर से तुमपर उतारा गया है, उसपर चलो और उसके सिवा दूसरे सहायकों के पीछे न चलो। तुम बहुत थोड़ी शिक्षा लेते हो।');
INSERT INTO "al_umari" VALUES(7,4,'7:4','तथा बहुत सी बस्तियाँ हैं, जिन्हें हमने ध्वस्त कर दिया है, उनपर हमारा प्रकोप अकस्मात रात्रि में आया या जब वे दोपहर के समय आराम कर रहे थे।');
INSERT INTO "al_umari" VALUES(7,5,'7:5','और जब उनपर हमारा प्रकोप आ पड़ा, तो उनकी पुकार यही थी कि वास्तव में हम ही अत्याचारी[ थे। ');
INSERT INTO "al_umari" VALUES(7,6,'7:6','तो हम उनसे अवश्य प्रश्न करेंगे, जिनके पास रसूलों को भेजा गया तथा रसूलों से भी अवश्य[ प्रश्न करेंगे। ');
INSERT INTO "al_umari" VALUES(7,7,'7:7','फिर हम अपने ज्ञान से उनके समक्ष वास्तविक्ता का वर्णन कर देंगे तथा हम अनुपस्थित नहीं थे।');
INSERT INTO "al_umari" VALUES(7,8,'7:8','तथा उस (प्रलय के) दिन (कर्मों की) तोल न्याय के साथ होगी। फिर जिसके पलड़े भारी होंगे, वही सफल होंगे।');
INSERT INTO "al_umari" VALUES(7,9,'7:9','और जिनके पलड़े हलके होंगे, वही स्वयं को क्षति में डाल लिए होंगे। क्योंकि वे हमारी आयतों के साथ अत्याचार करते[ रहे। ');
INSERT INTO "al_umari" VALUES(7,10,'7:10','तथ हमने तुम्हें धरती में अधिकार दिया और उसमें तुम्हारे लिए जीवन के संसाधन बनाये। तुम थोड़े ही कृतज्ञ होते हो।');
INSERT INTO "al_umari" VALUES(7,11,'7:11','और हमने तुम्हें पैदा किया[, फिर तुम्हारा रूप बनाया, फिर हमने फ़रिश्तों से कहा कि आदम को सज्दा करो, तो इब्लीस के सिवा सबने सज्दा किया। वह सज्दा करने वालों में से न हुआ। ');
INSERT INTO "al_umari" VALUES(7,12,'7:12','अल्लाह ने उससे कहाः किस बात ने तुझे सज्दा करने से रोक दिया, जबकि मैंने तुझे आदेश दिया था? उसने कहाः मैं उससे उत्तम हूँ। मेरी रचना तूने अग्नि से की है और उसकी मिट्टी से।');
INSERT INTO "al_umari" VALUES(7,13,'7:13','तो अल्लाह ने कहाः इस (स्वर्ग) से उतर जा। तेरे लिए ये योग्य नहीं कि इसमें घमण्ड करे। तू निकल जा। वास्तव में, तू अपमानितों में है।');
INSERT INTO "al_umari" VALUES(7,14,'7:14','उसने कहाः मुझे उस दिन तक के लिए अवसर दे दो, जब लोग फिर जीवित किये जायेंगे।');
INSERT INTO "al_umari" VALUES(7,15,'7:15','अल्लाह ने कहाः तुझे अवसर दिया जा रहा है।');
INSERT INTO "al_umari" VALUES(7,16,'7:16','उसने कहाः तो जिस प्रकार तूने मुझे कुपथ किया है, मैंभी तेरी सीधी राह पर इनकी घात में लगा रहूँगा।');
INSERT INTO "al_umari" VALUES(7,17,'7:17','फिर उनके पास उनके आगे और पीछे तथा दायें और बायें से आऊँगा[ और तू उनमें से अधिक्तर को (अपना) कृतज्ञ नहीं पायेगा ।');
INSERT INTO "al_umari" VALUES(7,18,'7:18','अल्लाह ने कहाः यहाँ से अपमानित धिक्कारा हुआ निकल जा। जो भी उनमें से तेरी राह चलेगा, तो मैं तुम सभी से नरक को अवश्य भर दूँगा।');
INSERT INTO "al_umari" VALUES(7,19,'7:19','और हे आदम! तुम और तुम्हारी पत्नी स्वर्ग में रहो और जहाँ से चाहो, खाओ और इस वृक्ष के समीप न जाना, अन्यथा अत्याचरियों में हो जाओगे।');
INSERT INTO "al_umari" VALUES(7,20,'7:20','तो शैतान ने दोनों को संशय में डाल दिया, ताकि दोनों के लिए उनके गुप्तांगों को खोल दे, जो उनसे छुपाये गये थे और कहाः तुम्हारे पालनहार ने तुम दोनों को इस वृक्ष से केवल इसलिए रोक दिया है कि तुम दोनों फ़रिश्ते अथवा सदावासी हो जाओगे।');
INSERT INTO "al_umari" VALUES(7,21,'7:21','तथा दोनों के लिए शपथ दी कि वास्तव में, मैं तुम दोनों का हितैषी हूँ।');
INSERT INTO "al_umari" VALUES(7,22,'7:22','तो उन दोनों को धोखे से रिझा लिया। फिर जब दोनों ने उस वृक्ष का स्वाद लिया, तो उनके लिए उनके गुप्तांग खुल गये और वे उनपर स्वर्ग के पत्ते चिपकाने लगे और उन्हें उनके पालनहार ने आवाज़ दीः क्या मैंने तुम्हें इस वृक्ष से नहीं रोका था और तुम दोनों से नहीं कहा था कि शैतान तुम्हारा खुला शत्रु है?');
INSERT INTO "al_umari" VALUES(7,23,'7:23','दोनों ने कहाः हे हमारे पालनहार! हमने अपने ऊपर अत्याचार कर लिया और यदि तू हमें क्षमा तथा हमपर दया नहीं करेगा, तो हम अवश्य ही नाश हो[ जायेंगे। ');
INSERT INTO "al_umari" VALUES(7,24,'7:24','उसने कहाः तुम सब उतरो, तुम एक-दूसरे के शत्रु हो और तुम्हारे लिए धरती में रहना और एक निर्धारित समय तक जीवन का साधन है।');
INSERT INTO "al_umari" VALUES(7,25,'7:25','तथा कहाः तुम उसीमें जीवित रहोगे, उसीमें मरोगे और उसीसे (फिर) निकाले जाओगे।');
INSERT INTO "al_umari" VALUES(7,26,'7:26','हे आदम के पुत्रो! हमने तुमपर ऐसा वस्त्र उतार दिया है, जो तुम्हारे गुप्तांगों को छुपाता तथा शोभा है और अल्लाह की आज्ञाकारिता का वस्त्र ही सर्वोत्तम है। ये अल्लाह की आयतों में से एक है, ताकि वे शिक्षा लें[। ');
INSERT INTO "al_umari" VALUES(7,27,'7:27','हे आदम के पुत्रो! ऐसा न हो कि शैतान तुम्हें बहका दे, जैसे तुम्हारे माता-पिता को स्वर्ग से निकाल दिया, उनके वस्त्र उतरवा दिये, ताकि उन्हें उनके गुप्तांग दिखा दे। वास्तव में, वह तथा उसकी जाति, तुम्हें ऐसे स्थान से देखती है, जहाँ से तुम उन्हें नहीं देख सकते। वास्तव में, हमने शैतानों को उनका सहायक बना दिया है, जो ईमान नहीं रखते।');
INSERT INTO "al_umari" VALUES(7,28,'7:28','तथा जब वे (मुश्रिक) कोई निर्लज्जा का काम करते हैं, तो कहते हैं कि इसी (रीति) पर हमने अपने पूर्वजों को पाया है तथा अल्लाह ने हमें इसका आदेश दिया है। (हे नबी!) आप उनसे कह दें कि अल्लाह कभी निर्लज्जा का आदेश नहीं देता। क्या तुम अल्लाह पर ऐसी बात का आरोप धरते हो, जिसे तुम नहीं जानते?');
INSERT INTO "al_umari" VALUES(7,29,'7:29','आप उनसे कह दें कि मेरे पालनहार ने न्याय का आदेश दिया है। (और वो ये है कि) प्रत्येक मस्जिद में नमाज़ के समय अपना ध्यान सीधे उसी की ओर करो[ और उसके लिए धर्म को विशुध्द करके उसी को पुकारो। जिस प्रकार, उसने तुम्हें पहले पैदा किया है, उसी प्रकार, (प्रलय में) फिर जीवित कर दिये जाओगे। ');
INSERT INTO "al_umari" VALUES(7,30,'7:30','एक समुदाय को उसने सुपथ दिखा दिया और दूसरा समुदाय कुपथ पर स्थित रह गया। वास्तव में, इन लोगों ने अल्लाह के सिवा शैतानों को सहायक बना लिया, फिर भी वे समझते हैं कि वास्तव में वही सुपथ पर हैं।');
INSERT INTO "al_umari" VALUES(7,31,'7:31','हे आदम के पुत्रो! प्रत्येक मस्जिद के पास (नमाज़ के समय) अपनी शोभा धारण करो,[ खाओ-पिओ और बेजा ख़र्च न करो। वस्तुतः, वह बेजा ख़र्च करने वालों से प्रेम नहीं करता। ');
INSERT INTO "al_umari" VALUES(7,32,'7:32','(हे नबी!) इन (मिश्रणवादियों) से कहिए कि किसने अल्लाह की उस शोभा को ह़राम (वर्जित) किया है,[ जिसे उसने अपने सेवकों के लिए निकाला है? तथा स्वच्छ जीविकाओं को? आप कह दें: ये सांसारिक जीवन में उनके लिए (उचित) है, जो ईमान लाये तथा प्रलय के दिन उन्हीं के लिए विशेष है। इसी प्रकार, हम अपनी आयतों का सविस्तार वर्णन उनके लिए करते हैं, जो ज्ञान रखते हों।');
INSERT INTO "al_umari" VALUES(7,33,'7:33','(हे नबी!) आप कह दें कि मेरे पालनहार ने तो केवल खुले तथा छुपे कुकर्मों, पाप तथा अवैध विद्रोह को ही ह़राम (वर्जित) किया है तथा इस बात को कि तुम उसे अल्लाह का साझी बनाओ, जिसका कोई तर्क उसने नहीं उतारा है तथा अल्लाह पर ऐसी बात बोलो, जिसे तुम नहीं जानते।');
INSERT INTO "al_umari" VALUES(7,34,'7:34','प्रत्येक समुदाय का[ एक निर्धारित समय है, फिर जब वह समय आ जायेगा, तो क्षण भर देर या सवेर नहीं होगी। ');
INSERT INTO "al_umari" VALUES(7,35,'7:35','हे आदम के पुत्रो! जब तुम्हारे पास तुम्हीं में से रसूल आ जायें जो तुम्हें मेरी आयतें सुना रहे हों, तो जो डरेगा और अपना सुधार कर लेगा, उसके लिए कोई डर नहीं होगा और न वे[ उदासीन होंगे। ');
INSERT INTO "al_umari" VALUES(7,36,'7:36','और जो हमारी आयतें झुठलायेंगे और उनसे घमण्ड करेंगे वही नारकी होंगे और वही उसमें सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(7,37,'7:37','फिर उससे बड़ा अत्याचारी कौन है, जो अल्लाह पर मिथ्या बातें बनाये अथवा उसकी आयतों को मिथ्या कहे? उन्हें उनके भाग्य में लिखा भाग मिल जायेगा। यहाँ तक कि जिस समय हमारे फ़रिश्ते उनका प्राण निकालने के लिए आयेंगे, तो उनसे कहेंगे कि वे कहाँ हैं, जिन्हें तुम अल्लाह के सिवा पुकारते थे? वे कहेंगे कि वे तो हमसे खो गये तथा अपने ही विरूध्द साक्षी (गवाह) बन जायेंगे कि वस्तुतः वे काफ़िर थे।');
INSERT INTO "al_umari" VALUES(7,38,'7:38','अल्लाह का आदेश होगा कि तुम भी प्रवेश कर जाओ, उन समुदायों में, जो तुमसे पहले के जिन्नों और मनुष्यों में से नरक में। जब भी कोई समुदाय नरक में प्रवेश करेगा, तो अपने समान दूसरे समुदाय को धिक्कार करेगा, यहाँ तक कि जब उसमें सब एकत्र हो जायेंगे, तो उनका पिछला अपने पहले के लिए कहेगाः हे हमारे पालनहार! इन्होंने ही हमें कुपथ किया है। अतः इन्हें दुगनी यातना दे। वह (अल्लाह) कहेगाः तुममें से प्रत्येक के लिए दुगनी यातना है, परन्तु तुम्हें ज्ञान नहीं।');
INSERT INTO "al_umari" VALUES(7,39,'7:39','तथा उनका पहला समुदाय अपने दूसरे समुदाय से कहेगाः (यदि हम दोषी थे) तो हमपर तुम्हारी कोई प्रधानता नहीं[ हुई, तो तुम अपने कुकर्मों की यातना का स्वाद लो। ');
INSERT INTO "al_umari" VALUES(7,40,'7:40','वास्तव में, जिन्होंने हमारी आयतों को झुठला दिया और उनसे अभिमान किया, उनके लिए आकाश के द्वार नहीं खोले जायेंगे और न वे स्वर्ग में प्रवेश करेंगे, जब तक[ ऊँट सूई के नाके से पार न हो जाये और हम इसी प्रकार अपराधियों को बदला देते हैं। ');
INSERT INTO "al_umari" VALUES(7,41,'7:41','उन्हीं के लिए नरक का बिछौना और उनके ऊपर से ओढ़ना होगा और इसी प्रकार हम अत्याचारियों को प्रतिकार (बदला[) देते हैं। ');
INSERT INTO "al_umari" VALUES(7,42,'7:42','और जो ईमान लाये और सत्कर्म किये और हम किसीपर उसकी सकत से (अधिक) भार नहीं रखते। वही स्वर्गी हैं, और वही उसमें सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(7,43,'7:43','तथा उनके दिलों में जो द्वेष होगा, उसे हम निकाल देंगे[। उन (स्वर्गों में) बहती नहरें होंगी तथा वे कहेंगे कि उस अल्लाह की प्रशंसा है, जिसने हमें इसकी राह दिखाई और यदि अल्लाह हमें मार्गदर्शन न देता, तो हमें मार्गदर्शन न मिलता। हमारे पालनहार के रसूल सत्य लेकर आये तथा उन्हें पुकारा जायेगा कि इस स्वर्ग के अधिकारी तुम अपने सत्कर्मों के कारण हुए हो। ');
INSERT INTO "al_umari" VALUES(7,44,'7:44','तथा स्वर्गवासी नरकवासियों को पुकारेंगे कि हमें, हमारे पालनहार ने जो वचन दिया था, उसे हमने सच पाया, तो क्या तुम्हारे पानलहार ने तुम्हें जो वचन दिया था, उसे तुमने सच पाया? वे कहेंगे कि हाँ! फिर उनके बीच एक पुकारने वाला पुकारेगा कि अल्लाह की धिक्कार है, उन अत्याचारियों पर।');
INSERT INTO "al_umari" VALUES(7,45,'7:45','जो लोगों को अल्लाह की राह (सत्धर्म) से रोकते तथा उसे टेढ़ा करना चाहते थे और वही प्रलोक के प्रति विश्वास नहीं रखते।');
INSERT INTO "al_umari" VALUES(7,46,'7:46','और दोनों (नरक तथा स्वर्ग) के बीच एक परदा होगा और कुछ लोग आराफ़[ (ऊँचाइयों) पर होंगे, जो प्रत्येक को उनके लक्षणों से पहचानेंगे और स्वर्ग वासियों को पुकारकर उन्हें सलाम करेंगे और उन्होंने उसमें प्रवेश नहीं किया होगा, परन्तु उसकी आशा रखते होंगे। ');
INSERT INTO "al_umari" VALUES(7,47,'7:47','और जब उनकी आँखें नरक वासियों की ओर फिरेंगी, तो कहेंगेः हे हमारे पालनहार! हमें अत्याचारियों में सम्मिलित न करना।');
INSERT INTO "al_umari" VALUES(7,48,'7:48','फिर आराफ़ (ऊँचाइयों) के लोग कुछ लोगों को उनके लक्षणों से पहचान जायेंगे[, उनसे कहेंगे कि (देखो!) तुम्हारे जत्थे और तुम्हारा घमंड तुम्हारे किसी काम नहीं आया! ');
INSERT INTO "al_umari" VALUES(7,49,'7:49','(और स्वर्गवासियों की ओर संकेत करेंगे कि) क्या यही वे लोग नहीं हैं, जिनके संबंध में तुम शपथ लेकर कह रहे थे कि अल्लाह इन्हें अपनी दया में से कुछ नहीं देगा? (आज उनसे कहा जा रहा है कि) स्वर्ग में प्रवेश कर जाओ, न तुमपर किसी प्रकार का भय है और न तुम उदासीन होगे।');
INSERT INTO "al_umari" VALUES(7,50,'7:50','तथा नरकवासी स्वर्गवासियों को पुकारेंगे कि हमपर तनिक पानी डाल दो अथवा जो अल्लाह ने तुम्हें प्रदान किया है, उसमें से कुछ दे दो। वे कहेंगे कि अल्लाह ने ये दोनों (आज) काफ़िरों के लिए ह़राम (वर्जित) कर दिया है।');
INSERT INTO "al_umari" VALUES(7,51,'7:51','(उसका निर्णय है कि) जिन्होंने अपने धर्म को तमाशा और खेल बना लिया था तथा जिन्हें सांसारिक जीवन ने धोखे में डाल रखा था, तो आज हम उन्हें ऐसे ही भुला देंगे, जिस प्रकार उन्होंने आज के दिन के आने को भुला दिया था[ और इसलिए भी कि वे हमारी आयतों का इन्कार करते रहे। ');
INSERT INTO "al_umari" VALUES(7,52,'7:52','जबकि हमने उनके लिए एक ऐसी पुस्तक दी, जिसे हमने ज्ञान के आधार पर सविस्तार वर्णित कर दिया है, जो मार्गदर्शन तथा दया है, उन लोगों के लिए, जो ईमान (विश्वास) रखते हैं।');
INSERT INTO "al_umari" VALUES(7,53,'7:53','(फिर) क्या वे इसकी प्रतीक्षा कर रहे हैं कि इसका परिणाम सामने आ जाये? जिस दिन इसका परिणाम आ जायेगा, तो वही, जो इससे पहले इसे भूले हुए थे, कहेंगे कि हमारे पालनहार के रसूल सच लेकर आए थे, (परन्तु हमने नहीं माना) तो क्या हमारे लिए कोई अनुशंसक (सिफ़ारिशी) है, जो हमारी अनुशंसा (सिफ़ारिश) करे? अथवा हम संसार में फेर दिए जायेँ, तो जो कर्म हम करते रहे, उनके विपरीत कर्म करेंगे! उनहोंने स्वयं को छति में डाल दिया तथा उनसे जो मिथ्या बातें बना रहे थे खो गईं।');
INSERT INTO "al_umari" VALUES(7,54,'7:54','तुम्हारा पालनहार वही अल्लाह है, जिसने आकाशों तथा धरती को छः दिनों में बनाया[, फिर अर्श (सिंहासन) पर स्थित हो गया। वह रात्रि से दिन को ढक देता है, दिन उसके पीछे दौड़ता हुआ आ जाता है, सूर्य तथा चाँद और तारे उसकी आज्ञा के अधीन हैं। सुन लो! वही उत्पत्तिकार है और वही शासक है। वही अल्लाह अति शुभ संसार का पालनहार है।');
INSERT INTO "al_umari" VALUES(7,55,'7:55','तुम अपने (उसी) पालनहर को रोते हुए तथा धीरे धीरे पुकारो। निःसंदेह वह सीमा पार करने वालों से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(7,56,'7:56','तथा धरती में उसके सुधार के पश्चात्[ उपद्रव न करो और उसीसे डरते हुए तथा आशा रखते हुए प्रार्थना करो। वास्तव में, अल्लाह की दया सदाचारियों के समीप है।');
INSERT INTO "al_umari" VALUES(7,57,'7:57','और वही है, जो अपनी दया (वर्षा) से पहले वायुओं को (वर्षा) की शुभ सूचना देने के लिए भेजता है और जब वे भारी बादलों को लिए उड़ती हैं, तो हम उसे किसी निर्जीव धरती को (जीवित) करने के लिए पहुँचा देते हैं, फिर उससे जल वर्षा कर, उसके द्वारा प्रत्येक प्रकार के फल उपजा देते हैं। इसी प्रकार, हम मुर्दों को जीवित करते हैं, ताकि तुम शिक्षा ग्रहण कर सको।');
INSERT INTO "al_umari" VALUES(7,58,'7:58','और स्वच्छ भूमि अपनी उपज अल्लाह की अनुमति से भरपूर देती है तथा खराब भूमि की उपज थोड़ी ही होती है। इसी प्रकार, हम अपनी[ आयतें (निशानियाँ) उनके लिए दुहराते हैं, जो शुक्र अदा करते हैं। ');
INSERT INTO "al_umari" VALUES(7,59,'7:59','हमने नूह़[ को उसकी जाति की ओर (अपना संदेश पहुँचाने के लिए) भेजा था, तो उसने कहाः हे मेरी जाति के लोगो! (केवल) अल्लाह की इबादत (वंदना) करो, उसके सिवा तुम्हारा कोई पूज्य नहीं। मैं तुमपर एक बड़े दिन की यातना से डरता हूँ। ');
INSERT INTO "al_umari" VALUES(7,60,'7:60','उसकी जाति के प्रमुखों ने कहाः हमें लगता है कि तुम खुले कुपथ में पड़ गये हो।');
INSERT INTO "al_umari" VALUES(7,61,'7:61','उसने कहाः हे मेरी जाति! मैं किसी कुपथ में नहीं हूँ, परन्तु मैं विश्व के पालनहार का रसूल हूँ।');
INSERT INTO "al_umari" VALUES(7,62,'7:62','तुम्हें अपने पालनहार का संदेश पहुँचा रहा हूँ, तुम्हारा भला चाहता हूँ और अल्लाह की ओर से उन चीज़ों का ज्ञान रखता हूँ, जिनका ज्ञान तुम्हें नहीं है।');
INSERT INTO "al_umari" VALUES(7,63,'7:63','क्या तुम्हें इसपर आश्चर्य हो रहा है कि तुम्हारे पालनहार की शिक्षा तुम्हीं में से एक पुरुष द्वारा तुम्हारे पास आ गयी है, ताकि वह तुम्हें सावधान करे और ताकि तुम आज्ञाकारी बनो और अल्लाह की दया के योग्य हो जाओ?');
INSERT INTO "al_umari" VALUES(7,64,'7:64','फिर भी उन्होंने उसे झुठला दिया। तो हमने उसे और जो नौका में उसके साथ थे, उन्हें बचा लिया और उन्हें डुबो दिया, जो हमारी आयतों को झुठला चुके थे। वास्तव में, वह (समझ-बूझ के) अंधे थे।');
INSERT INTO "al_umari" VALUES(7,65,'7:65','(और इसी प्रकार) आद[ की ओर उनके भाई हूद को (भेजा)। उसने कहाः हे मेरी जाति! अल्लाह की इबादत (वंदना) करो, उसके सिवा तुम्हारा कोई पूज्य नहीं। तो क्या तुम (उसकी अवज्ञा से) नहीं डरते? ');
INSERT INTO "al_umari" VALUES(7,66,'7:66','(इसपर) उनकी जाति में से उन प्रमुखों ने कहा, जो काफ़िर हो गये कि हमें ऐसा लग रहा है कि तुम नासमझ हो गये हो और वास्तव में हम तुम्हें झूठों में समझ रहे हैं।');
INSERT INTO "al_umari" VALUES(7,67,'7:67','उसने कहाः हे मेरी जाति! मुझमें कोई नासमझी की बात नहीं है, परन्तु मैं तो संसार के पालनहार का रसूल (संदेशवाहक) हूँ।');
INSERT INTO "al_umari" VALUES(7,68,'7:68','मैं तुम्हें अपने पालनहार का संदेश पहुँचा रहा हूँ और वास्तव में मैं तुम्हारा भरोसा करने योग्य शिक्षक हूँ।');
INSERT INTO "al_umari" VALUES(7,69,'7:69','क्या तुम्हें इसपर आश्चर्य हो रहा है कि तुम्हारे पालनहार की शिक्षा, तुम्हीं में से एक पुरुष द्वारा तुम्हारे पास आ गई है, ताकि वह तुम्हें सावधान करे? तथा याद करो कि अल्लाह ने नूह़ की जाति के पश्चात तुम्हें धरती में अधिकार दिया है और तुम्हें अधिक शारीरिक बल दिया है। अतः अल्लाह के पुरस्कारों को याद[ करो। संभवतः तुम सफल हो जाओगे। ');
INSERT INTO "al_umari" VALUES(7,70,'7:70','उन्होंने कहाः क्या तुम हमारे पास इसलिए आये हो कि हम केवल एक ही अल्लाह की इबादत (वंदना) करें और उन्हें छोड़ दें, जिनकी पूजा हमारे पूर्वज करते आ रहे हैं? तो वो बात हमारे पास ला दो, जिससे हमें डरा रहे हो, यदि तुम सच्चे हो?');
INSERT INTO "al_umari" VALUES(7,71,'7:71','उसने कहाः तुमपर तुम्हारे पालनहार का प्रकोप और क्रोध आ पड़ा है। क्या तुम मुझसे कुछ (मूर्तियों के) नामों के विषय में विवाद कर रहे हो, जिनका तुमने तथा तुम्हारे पूर्वजों ने (पूज्य) नाम रखा है, जिसका कोई तर्क (प्रमाण) अल्लाह ने नहीं उतारा है? तो तुम (प्रकोप की) प्रतीक्षा करो और तुम्हारे साथ मैं भी प्रतीक्षा कर रहा हूँ।');
INSERT INTO "al_umari" VALUES(7,72,'7:72','फिर हमने उसे और उसके साथियों को बचा लिया तथा उनकी जड़ काट दी, जिन्होंने हमारी आयतों (आदेशों) को झुठला दिया था और वे ईमान लाने वाले नहीं थे।');
INSERT INTO "al_umari" VALUES(7,73,'7:73','और (इसी प्रकार) समूद[ (जाति) के पास उनके भाई सालेह़ को भेजा। उसने कहाः हे मेरी जाति! अल्लाह की (वंदना) करो, उसके सिवा तुम्हारा कोई पूज्य नहीं। तुम्हारे पास तुम्हारे पालनहार की ओर से खुला प्रमाण (चमत्कार) आ गया है। ये अल्लाह की ऊँटनी तुम्हारे लिए एक चमत्कार है। अतः इसे अल्लाह की धरती में चरने के लिए छोड़ दो और इसे बुरे विचार से हाथ न लगाना, अन्यथा तुम्हें दुःखदायी यातना घेर लेगी।');
INSERT INTO "al_umari" VALUES(7,74,'7:74','तथा याद करो कि अल्लाह ने आद जाति के ध्वस्त किए जाने के पश्चात् तुम्हें धरती में अधिकार दिया है और तुम्हें धरती में बसाया है, तुम उसके मैदानों में भवन बनाते हो और पर्वतों को तराशकर घर बनाते हो। अतः अल्लाह के उपकारों को याद करो और धरती में उपद्रव करते न फिरो।');
INSERT INTO "al_umari" VALUES(7,75,'7:75','उसकी जाति के घमंडी प्रमुखों ने उन निर्बलों से कहा, जो उनमें से ईमान लाये थेः क्या तुम विश्वास रखते हो कि सालेह़ अपने पालनहार का भेजा हुआ है? उन्होंने कहाः निश्चय जिस (संदेश) के साथ वह भेजा गया है, हम उसपर ईमान (विश्वास) रखते हैं।');
INSERT INTO "al_umari" VALUES(7,76,'7:76','(तो इसपर) घमंडियों[ ने कहाः हमतो जिसका तुमने विश्वास किया है उसे नहीं मानते। ');
INSERT INTO "al_umari" VALUES(7,77,'7:77','फिर उन्होंने ऊँटनी को वध कर दिया और अपने पालनहार के आदेश का उल्लंघन किया और कहाः हे सालेह़! तू हमें जिस (यातना) की धमकी दे रहा था, उसे ले आ, यदि तू वास्तव में रसूलों में है।');
INSERT INTO "al_umari" VALUES(7,78,'7:78','तो उन्हें भूकंप ने पकड़ लिया। फिर जब भोर हुई, तो वे अपने घरों में औंधे पड़े हुए थे।');
INSERT INTO "al_umari" VALUES(7,79,'7:79','तो सालेह़ ने उनसे मुँह फेर लिया और कहाः हे मेरी जाति! मैंने तुम्हें अपने पालनहार के उपदेश पहुँचा दिये थे और मैंने तुम्हारा भला चाहा। परन्तु तुम उपकारियों से प्रेम नहीं करते।');
INSERT INTO "al_umari" VALUES(7,80,'7:80','और हमने लूत[ को भेजा। जब उसने अपनी जाति से कहाः क्या तुम ऐसी निर्लज्जा का काम कर रहे हो, जो तुमसे पहले संसारवासियों में से किसी ने नहीं किया है? ');
INSERT INTO "al_umari" VALUES(7,81,'7:81','तुम स्त्रियों को छोड़कर कामवासना की पूर्ति के लिए पुरुषों के पास जाते हो? बल्कि तुम सीमा लांघने वाली जाति[ हो। ');
INSERT INTO "al_umari" VALUES(7,82,'7:82','और उसकी जाति का उत्तर बस ये था कि इनको अपनी बस्ती से निकाल दो। ये लोग अपने में बड़े पवित्र बन रहे हैं।');
INSERT INTO "al_umari" VALUES(7,83,'7:83','हमने उसे और उसके परिवार को, उसकी पत्नी के सिवा, बचा लिया, वह पीछे रह जाने वाली थी।');
INSERT INTO "al_umari" VALUES(7,84,'7:84','और हमने उनपर (पत्थरों) की वर्षा कर दी। तो देखो कि अपराधियों का परिणाम कैसा रहा?');
INSERT INTO "al_umari" VALUES(7,85,'7:85','तथा मद्यन[ की ओर हमने उसके भाई शोऐब को रसूल बनाकर भेजा। उसने कहः हे मेरी जाति के लोगो! अल्लाह की इबादत (वंदना) करो, उसके सिवा तुम्हारा कोई पूज्य नहीं है। तुम्हारे पास तुम्हारे पालनहार का खुला तर्क (प्रमाण) आ गया है। अतः नाप और तोल पूरी करो और लोगों की चीज़ों में कमी न करो तथा धरती में उसके सुधार के पश्चात उपद्रव न करो। यही तुम्हारे लिए उत्त्म है, यदि तुम ईमान वाले हो। ');
INSERT INTO "al_umari" VALUES(7,86,'7:86','तथा प्रत्येक मार्ग पर लोगों को धमकाने के लिए न बैठो और उन्हें अल्लाह की राह से न रोको, जो उसपर ईमान लाये[ हैं और उसे टेढ़ा न बनाओ तथा उस समय को याद करो, जब तुम थोड़े थे, तो तुम्हें अल्लाह ने अधिक कर दिया तथा देखो कि उपद्रवियों का परिणाम क्या हुआ? ');
INSERT INTO "al_umari" VALUES(7,87,'7:87','और यदि तुम्हारा एक समुदाय उसपर ईमान लाया है, जिसके साथ मैं भेजा गया हूँ और दूसरा ईमान नहीं लाया है, तो तुम धैर्य रखो, यहाँ तक कि अल्लाह हमारे बीच निर्णय कर दे और वह उत्तम न्याय करने वाला है।');
INSERT INTO "al_umari" VALUES(7,88,'7:88','उसकी जाति के प्रमुखों ने, जिन्हें घमंड था, कहा कि हे शोऐब! हम तुम्हें तथा जो तुम्हारे साथ ईमान लाये हैं, अपने नगर से अवश्य निकाल देंगे अथवा तुम सबको हमारे धर्म में अवश्य वापस आना होगा। (शोऐब) ने कहाः क्या यदि हम उसे दिल से न मानें तो?');
INSERT INTO "al_umari" VALUES(7,89,'7:89','हमने अल्लाह पर मिथ्या आरोप लगाया है, यदि तुम्हारे धर्म में इसके पश्चात वापस आ गये, जबकि हमें अल्लाह ने उससे मुक्त कर दिया है और हमारे लिए संभव नहीं कि उसमें फिर आ जायें, परन्तु ये कि हमारा पालनहार चाहता हो। हमारा पालनहार प्रत्येक वस्तु को अपने ज्ञान में समोये हुए है, अल्लाह ही पर हमारा भरोसा है। हे हमारे पालनहार! हमारे और हमारी जाति के बीच न्याय के साथ निर्णय कर दे और तू ही उत्तम निर्णयकारी है।');
INSERT INTO "al_umari" VALUES(7,90,'7:90','तथा उसकी जाति के काफ़िर प्रमुखों ने कहा कि यदि तुम लोग शोऐब का अनुसरण करोगे, तो वस्तुतः तुम लोगों का उस समय नाश हो जायेगा।');
INSERT INTO "al_umari" VALUES(7,91,'7:91','तो उन्हें भुकम्प ने पकड़ लिया, फिर भोर हुई, तो वे अपने घरों में औंधे पड़े हुए थे।');
INSERT INTO "al_umari" VALUES(7,92,'7:92','जिन्होंने शोऐब को झुठलाया (उनकी ये दशा हुई कि) मानो कभी उस नगर में बसे ही नहीं थे।');
INSERT INTO "al_umari" VALUES(7,93,'7:93','तो शोऐब उनसे विमुख हो गया तथा कहाः हे मेरी जाति! मैंने तुम्हें अपने पालनहार के संदेश पहुँचा दिये, तथा तुम्हारा हितकारी रहा। तो काफ़िर जाति (के विनाश) पर कैसे शोक करूँ?');
INSERT INTO "al_umari" VALUES(7,94,'7:94','तथा हमने जब किसी नगरी में कोई नबी भेजा, तो उसके निवासियों को आपदा तथा दुःख में ग्रस्त कर दिया कि संभवतः वह विनती करें[। ');
INSERT INTO "al_umari" VALUES(7,95,'7:95','फिर हमने आपदा को सुःख सुविधा से बदल दिया, यहाँ तक कि जब वे सुःखी हो गये और उन्होंने कहा कि हमारे पूर्वजों को भी दुःख तथा सुख पहुँचता रहा है, तो अकस्मात् हमने उन्हें पकड़ लिया और वे समझ नहीं सके।');
INSERT INTO "al_umari" VALUES(7,96,'7:96','और यदि इन नगरों के वासी ईमान लाते और कुकर्मों से बचे रहते, तो हम उनपर आकाशों तथा धरती की सम्पन्नता के द्वार खोल देते। परन्तु उन्होंने झुठला दिया। अतः, हमने उनके करतूतों के कारण उन्हें (यातना में) घेर लिया।');
INSERT INTO "al_umari" VALUES(7,97,'7:97','तो क्या नगर वासी इस बात से निश्चिन्त हो गये हैं कि उनपर हमारी यातना रातों-रात आ जाये और वे पड़े सो रहे हों?');
INSERT INTO "al_umari" VALUES(7,98,'7:98','अथवा नगर वासी निश्चिन्त हो गये कि हमारी यातना उनपर दिन के समय आ पड़े और वे खेल रहे हों?');
INSERT INTO "al_umari" VALUES(7,99,'7:99','तो क्या वे अल्लाह के गुप्त उपाय से निश्चिन्त हो गये हैं? तो (याद रखो!) अल्लाह के गुप्त उपाय से नाश होने वाली जाति ही निश्चिन्त होती है।');
INSERT INTO "al_umari" VALUES(7,100,'7:100','तो क्या उनको शिक्षा नहीं मिली, जो धरती के वारिस होते हैं, उसके अगले वासियों के पश्चात् कि यदि हम चाहें, तो उनके पापों के बदले उन्हें आपदा में ग्रस्त कर दें और उनके दिलों पर मुहर लगा दें, फिर वे कोई बात ही न सुन सकें?');
INSERT INTO "al_umari" VALUES(7,101,'7:101','(हे नबी!) ये वे नगर हैं, जिनकी कथा हम आपको सुना रहे हैं। इन सबके पास उनके रसूल खुले तर्क (प्रमाण) लाये, तो वे ऐसे न थे कि उस सत्य पर विश्वास कर लें, जिसे वे इससे पूर्व झुठला[ चुके थे। इसी प्रकार, अल्लाह काफ़िरों के दिलों पर मुहर लगाता है। ');
INSERT INTO "al_umari" VALUES(7,102,'7:102','और हमने उनमें अधिक्तर को वचन पर स्थित नहीं पाया[ तथा हमने उनमें अधिक्तर को अवज्ञाकारी पाया। ');
INSERT INTO "al_umari" VALUES(7,103,'7:103','फिर हमने इन रसूलों के पश्चात् मूसा को अपनी आयतों (चमत्कारों) के साथ फ़िरऔन[ और उसके प्रमुखों के पास भेजा, तो उन्होंने भी हमारी आयतों के साथ अन्याय किया, तो देखो कि उपद्रवियों का क्या परिणाम हुआ? ');
INSERT INTO "al_umari" VALUES(7,104,'7:104','तथा मूसा ने कहाः हे फ़िरऔन! मैं वास्तव, में विश्व के पालनहार का रसूल (संदेश वाहक) हूँ।');
INSERT INTO "al_umari" VALUES(7,105,'7:105','मेरे लिए यही योग्य है कि अल्लाह के विषय में सत्य के अतिरिक्त कोई बात न करूँ। मैं तुम्हारे पास तुम्हारे पालनहार की ओर से खुला प्रमाण लाया हूँ। इसलिए मेरे साथ बनी इस्राईल[ को जाने दो। ');
INSERT INTO "al_umari" VALUES(7,106,'7:106','उसने कहाः यदि तुमकोई प्रमाण (चमत्कार) लाये हो, तो प्रस्तुत करो, यदि तुम सच्चे हो।');
INSERT INTO "al_umari" VALUES(7,107,'7:107','फिर मूसा ने अपनी लाठी फेंकी, तो अकस्मात् वह एक अजगर बन गई।');
INSERT INTO "al_umari" VALUES(7,108,'7:108','और अपना हाथ (जैब से) निकाला, तो वह देखने वालों के लिए चमक रहा था।');
INSERT INTO "al_umari" VALUES(7,109,'7:109','फ़िर्औन की जाति के प्रमुखों ने कहाः वास्तव में, ये बड़ा दक्ष जादूगर है।');
INSERT INTO "al_umari" VALUES(7,110,'7:110','वह तुम्हें, तुम्हारे देश से निकालना चाहता है। तो अब क्या आदेश देते हो?');
INSERT INTO "al_umari" VALUES(7,111,'7:111','सबने कहाः उसे और उसके भाई (हारून) को अभी छोड़ दो और नगरों में एकत्र करने के लिए हरकारे भेजो।');
INSERT INTO "al_umari" VALUES(7,112,'7:112','जो प्रत्येक दक्ष जादूगर को तुम्हारे पास लायें।');
INSERT INTO "al_umari" VALUES(7,113,'7:113','और जादूगर फ़िरऔन के पास आ गये। उन्होंने कहाः हमें निश्चय पुरस्कार मिलेगा, यदि हम ही विजय हो गये तो?');
INSERT INTO "al_umari" VALUES(7,114,'7:114','फ़िर्औन ने कहाः हाँ! और तुम मेरे समीपवर्तियों में से भी हो जाओगे।');
INSERT INTO "al_umari" VALUES(7,115,'7:115','जादूगरों ने कहाः हे मूसा! तुम (पहले) फेंकोगे या हमें फेंकना होगा?');
INSERT INTO "al_umari" VALUES(7,116,'7:116','मूसा ने कहाः तुम्हीं फेंको। तो उन्होंने जब (रस्सियाँ) फेंकीं, तो लोंगों की आँखों पर जादू कर दिया और उन्हें भयभीत कर दिया और बहुत बड़ा जादू कर दिखाया।');
INSERT INTO "al_umari" VALUES(7,117,'7:117','तो हमने मूसा को वह़्यी की कि अपनी लाठी फेंको और वह अकस्मात् झूठे इन्द्रजाल को निगलने लगी।');
INSERT INTO "al_umari" VALUES(7,118,'7:118','अतः सत्य सिध्द हो गया और उनका बनाया मंत्र-तंत्र व्यर्थ होकर[ रह गया। ');
INSERT INTO "al_umari" VALUES(7,119,'7:119','अंततः वे प्राजित कर दिये गये और तुच्छ तथा अपमानित होकर रह गये।');
INSERT INTO "al_umari" VALUES(7,120,'7:120','तथा सभी जादूगर (मूसा का सत्य) देखकर सज्दे में गिर गये।');
INSERT INTO "al_umari" VALUES(7,121,'7:121','उन्होंने कहाः हम विश्व के पालनहार पर ईमान लाये।');
INSERT INTO "al_umari" VALUES(7,122,'7:122','जो मूसा तथा हारून का पालनहार है।');
INSERT INTO "al_umari" VALUES(7,123,'7:123','फ़िरऔन ने कहाः इससे पहले कि मैं तुम्हें अनुमति दूँ, तुम उसपर ईमान ले आये? वास्तव में, ये षड्यंत्र है, जिसे तुमने नगर में रचा है, ताकि उसके निवासियों को उससे निकाल दो! तो शीघ्र ही तुम्हें इस (के परिणाम) का ज्ञान हो जायेगा।');
INSERT INTO "al_umari" VALUES(7,124,'7:124','मैं अवश्य तुम्हारे हाथ तथा पाँव विपरीत दिशाओं में काट दूँगा, फिर तुम सभी को फाँसी पर लटका दूँगा।');
INSERT INTO "al_umari" VALUES(7,125,'7:125','उन्होंने कहाः हमें अपने पालनहार ही की ओर प्रत्येक दशा में जाना है।');
INSERT INTO "al_umari" VALUES(7,126,'7:126','तू हमसे इसी बात का तो बदला ले रहा है कि हमारे पास हमारे पालनहार की आयतें (निशानियाँ) आ गयीं? तो हम उनपर ईमान ला चुके हैं। हे हमारे पालनहार! हमपर धैर्य (की धारा) उंडेल दे! और हमें इस दशा में (संसार से) उठा कि तेरे आज्ञाकारी रहें।');
INSERT INTO "al_umari" VALUES(7,127,'7:127','और फ़िरऔन की जाति के प्रमुखों ने (उससे) कहाः क्या तुम मूसा और उसकी जाति को छोड़ दोगे कि देश में विद्रोह करें तथा तुम्हें और तुम्हारे पूज्यों[ को छोड़ दें? उसने कहाः हम उनके पुत्रों को वध कर देंगे और उनकी स्त्रियों को जीवित रहने देंगे, हम उनपर दबाव रखते हैं। ');
INSERT INTO "al_umari" VALUES(7,128,'7:128','मूसा ने अपनी जाति से कहाः अल्लाह से सहायता माँगो और सहन करो, वास्तव में, धरती अल्लाह की है, वह अपने भक्तों में जिसे चाहे, उसका वारिस (उत्तराधिकारी) बना देता है और अन्त उन्हीं के लिए है, जो आज्ञाकारी हों।');
INSERT INTO "al_umari" VALUES(7,129,'7:129','उन्होंने कहाः हम तुम्हारे आने से पहले भी सताये गये और तुम्हारे आने के पश्चात् भी (सताये जा रहे हैं)! मूसा ने कहाः समीप है कि तुम्हारा पालनहार तुम्हारे शत्रु का विनाश कर दे और तुम्हें देश में अधिकारी बना दे। फिर देखे कि तुम्हारे कर्म कैसे होते हैं?');
INSERT INTO "al_umari" VALUES(7,130,'7:130','और हमने फ़िरऔन की जाति को अकालों तथा उपज की कमी में ग्रस्त कर दिया, ताकि वे सावधान हो जायेँ।');
INSERT INTO "al_umari" VALUES(7,131,'7:131','तो जब उनपर सम्पन्नता आती, तो कहते कि हम इसके योग्य हैं और जब अकाल पड़ता, तो मूसा और उसके साथियों से बुरा सगुन लेते। सुन लो! उनका बुरा सगुम तो अल्लाह के पास[ था, परन्तु अधिक्तर लोग इसका ज्ञान नहीं रखते। ');
INSERT INTO "al_umari" VALUES(7,132,'7:132','और उन्होंने कहाः तू हमपर जादू करने के लिए कोई भी आयत (चमत्कार) ले आये, हम तेरा विश्वास करने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(7,133,'7:133','अन्ततः, हमने उनपर तूफ़ान (उग्र वर्षा), टिड्डी दल, जुयें, मेढक और रक्त की वर्षा भेजी। अलग-अलग निशानियाँ; फिर भी उन्होंने अभिमान किया और वे थी ही अपराधी जाति।');
INSERT INTO "al_umari" VALUES(7,134,'7:134','और जब उनपर यातना आ पड़ी, तो उन्होंने कहाः हे मूसा! तू अपने पालनहार से उस वचन के कारण, जो उसने तुझे दिया है, हमारे लिए प्रार्थना कर। यदि तूने (अपनी प्रार्थना से) हमसे यातना दूर कर दी, तो हम अवश्य तेरा विश्वास कर लेंगे और बनी इस्राईल को तेरे साथ जाने की अनुमति देंगे।');
INSERT INTO "al_umari" VALUES(7,135,'7:135','फिर जब हमने एक विशेष समय तक के लिए उनसे यातना दूर कर दी, जिस तक उन्हें पहुँचना था, तो अकस्मात् वे वचन भंग करने लगे।');
INSERT INTO "al_umari" VALUES(7,136,'7:136','अन्ततः, हमने उनसे बदला लिया और उन्हें सागर में डुबो दिया, इस कारण कि उन्होंने हमारी आयतों (निशानियों) को झुठला दिया और उनसे निश्चेत हो गये। उनके धैर्य रखने के कारण तथा हमने उसे ध्वस्त कर दिया, जो फ़िरऔन और उसकी जाति कलाकारी कर रही थी और जो बेलें छप्परों पर चढ़ रही थीं।[ ');
INSERT INTO "al_umari" VALUES(7,137,'7:137','और हमने उस जाति (बनी इस्राईल) को, जो निर्बल समझे जा रहे थे, धरती (शाम देश) के पश्चिमों तथा पूर्वों का, जिसमें हमने बरकत दी थी, अधिकारी बना दिया और (इस प्रकार हे नबी!) आपके पालनहार का शुभ वचन बनी इस्राईल के लिए पूरा हो गया, उनके धैर्य रखने के कारण तथा हमने उसे धवस्त कर दिया, जो फ़िरऔन और उसकी जाति कलाकारी कर रही थी और जो बेलें छप्परों पर चढ़ा रहे थे[। ');
INSERT INTO "al_umari" VALUES(7,138,'7:138','और बनी इस्राईल को हमने सागर पार करा दिया, तो वे एक जाति के पास से होकर गये, जो अपनी मूर्तियों की पूजा कर रही थी, उन्होंने कहाः हे मूसा! हमारे लिए वैसा ही एक पूज्य बना दीजिये, जैसे इनके पूज्य हैं। मूसा ने कहाः वास्तव में, तुम अज्ञान जाति हो।');
INSERT INTO "al_umari" VALUES(7,139,'7:139','ये लोग जिस रीति में हैं, उसे नाश हो जाना है और वे जो कुछ कर रहे हैं, सर्वथा असत्य है।');
INSERT INTO "al_umari" VALUES(7,140,'7:140','मूसा ने कहाः क्या मैं अल्लाह के सिवा तुम्हारे लिए कोई दूसरा पूज्य निर्धारित करूँ, जबकि उसने तुम्हें सारे संसारों के वासियों पर प्रधानता दी है?');
INSERT INTO "al_umari" VALUES(7,141,'7:141','तथा उस समय को याद करो, जब हमने तुम्हें फ़िरऔन की जाति से बचाया। वह तुम्हें घोर यातना दे रहे थे; तुम्हारे पुत्रों को वध कर रहे थे और तुम्हारी स्त्रियों को जीवित रख रहे थे और इसमें तुम्हारे पालनहार की ओर से भारी परीक्षा थी।');
INSERT INTO "al_umari" VALUES(7,142,'7:142','और हमने मूसा को तीस रातों का वचन[ दिया और उसकी पूर्ती दस रातों से कर दी, तो तेरे पालनहार की निर्धारित अवधि चालीस रात पूरी हो गयी तथा मूसा ने अपने भाई हारून से कहाः तुम मेरी जाति में मेरा प्रतिनिधि रहना, सुधार करते रहना और उपद्रवकारियों की नीति न अपनाना। ');
INSERT INTO "al_umari" VALUES(7,143,'7:143','और जब मूसा हमारे निर्धारित समय पर आ गया और उसके पालनहार ने उससे बात की, तो उसने कहाः हे मेरे पालनहार! मेरे लिए अपने आपको दिखा दे, ताकि मैं तेरा दर्शन कर लूँ। अल्लाहने कहाः तू मेरा दर्शन नहीं कर सकेगा। परन्तु इस पर्वत की ओर देख! यदि ये अपने स्थान पर स्थिर रह गया, तो तू मेरा दर्शन कर सकेगा। फिर जब उसका पालनहार पर्वत की ओर प्रकाशित हुआ, तो उसे चूर-चूर कर दिया और मूसा निश्चेत होकर गिर गया। फिर जब चेतना आयी, तो उसने कहाः तू पवित्र है! मैं तुझसे क्षमा माँगता हूँ। तथा मैं सर्व प्रथम[ ईमान लाने वालों में से हूँ। ');
INSERT INTO "al_umari" VALUES(7,144,'7:144','अल्लाह ने कहाः हे मूसा! मैंने तुझे लोगों पर प्रधानता देकर अपने संदेशों तथा अपने वार्तालाप द्वारा निर्वाचित कर लिया है। अतः जो कुछ तुझे प्रदान किया है, उसे ग्रहण कर ले और कृतज्ञों में हो जा।');
INSERT INTO "al_umari" VALUES(7,145,'7:145','और हमने उसके लिए तख़्तियों पर (धर्म के) प्रत्येक विषय के लिए निर्देश और प्रत्येक बात का विवरण लिख दिया। (तथा कहा कि) इसे दृढ़ता से पकड़ लो और अपनी जाति को आदेश दो कि उसके उत्तम निर्देशों का पालन करे और मैं तुम्हें अवैज्ञाकरियों का घर दिखा दूँगा।');
INSERT INTO "al_umari" VALUES(7,146,'7:146','मैं उन्हें[ अपनी आयतों (निशानियों) से फेर दूँगा, जो धरती में अवैध अभिमान करते हैं, यदि वे प्रत्येक आयत (निशानी) देख लें, तब भी उसपर ईमान नहीं लायेंगे; यदि वे सुपथ देखेंगे, तो उसे नहीं अपनायेंगे और यदि कुपथ देख लें, तो उसे अपना लेंगे। ये इस कारण कि उन्होंने हमारी आयतों (निशानियों) को झुठला दिया और उनसे निश्चेत रहे।');
INSERT INTO "al_umari" VALUES(7,147,'7:147','और जिन लोगों ने हमारी आयतों तथा प्रलोक (में हमसे) मिलने को झुठला दिया, उन्हीं के कर्म व्यर्थ हो गये और उन्हें उसी का बदला मिलेगा, जो कुकर्म वे कर रहे थे।');
INSERT INTO "al_umari" VALUES(7,148,'7:148','और मूसा की जाति ने उसके (पर्वत पर जाने के) पश्चात् अपने आभूषणों से एक बछड़े की मूर्ति बना ली, जिससे गाय के डकारने के समान ध्वनि निकलती थी। क्या उन्होंने ये नहीं सोचा कि न तो वह उनसे बात[ करता है और न किसी प्रकार का मार्गदर्शन देता है? उन्होंने उसे बना लिया तथा वे अत्याचारी थे। ');
INSERT INTO "al_umari" VALUES(7,149,'7:149','और जब वे (अपने किये पर) लज्जित हुए और समझ गये कि वे कुपथ हो गये हैं, तो कहने लगेः यदि हमारे पालनहार ने हमपर दया नहीं की और हमें क्षमा नहीं किया, तो हम अवश्य विनाशों में हो जायेंगे।');
INSERT INTO "al_umari" VALUES(7,150,'7:150','और जब मूसा अपनी जाति की ओर क्रोध तथा दुःख से भरा हुआ वापस आया, तो उसने कहाः तुमने मेरे पश्चात् मेरा बहुत बुरा प्रतिनिधित्व किया। क्या तुम अपने पालनहार की आज्ञा से पहले ही जल्दी कर[ गये? तथा उसने लेख तख़्तियाँ डाल दीं तथा अपने भाई (हारून) का सिर पकड़ के अपनी ओर खींचने लगा। उसने कहाः हे मेरे माँ जाये भाई! लोगों ने मुझे निर्बल समझ लिया तथा समीप था कि वे मुझे मार डालें। अतः तू शत्रुओं को मुझपर हँसने का अवसर न दे। मुझे अत्याचारियों का साथी न बना। ');
INSERT INTO "al_umari" VALUES(7,151,'7:151','मूसा ने कहाः[ हे मेरे पालनहार! मुझे तथा मेरे भाई को क्षमा कर दे और हमें अपनी दया में प्रवेश दे और तू ही सब दयाकारियों से अधिक दयाशील है। ');
INSERT INTO "al_umari" VALUES(7,152,'7:152','जिन लोगों ने बछड़े को पूज्य बनाया, उनपर उनके पालनहार का प्रकोप आयेगा और वे सांसारिक जीवन में अपमानित होंगे और इसी प्रकार हम झूठ घड़ने वालों को दण्ड देते हैं।');
INSERT INTO "al_umari" VALUES(7,153,'7:153','और जिन लोगों ने दुष्कर्म किया, फिर उसके पश्चात् क्षमा माँग ली और ईमान लाये, तो वास्तव में, तेरा पालनहार अति क्षमाशील दयावान् है।');
INSERT INTO "al_umari" VALUES(7,154,'7:154','फिर जब मूसा का क्रोध शान्त हो गया, तो उसने लेख तख़्तियाँ उठा लीं, जिनके लिखे आदेशों में मार्गदर्शन तथा दया थी, उन लोगों के लिए, जो अपने पालनहार से ही डरते हों।');
INSERT INTO "al_umari" VALUES(7,155,'7:155','और मूसा ने हमारे निर्धारित[ समय के लिए अपनी जाति के सत्तर व्यक्तियों को चुन लिया और जब उन्हें भूकंप ने घेर लिया, तो मूसा ने कहाः हे मेरे पालनहार! यदि तू चाहता तो इन सबका इससे पहले ही विनाश कर देता और मेरा भी। क्या तू हमारा उस कुकर्म के कारण नाश कर देगा, जो हममें से कुछ निर्बोध कर गये? ये तेरी ओर से केवल एक परीक्षा थी। तू जिसे चाहे, उसके द्वारा कुपथ कर दे और जिसे चाहे, सुपथ दर्शा दे। तू ही हमारा संरक्षक है, अतः हमारे पापों को क्षमा कर दे और हमपर दया कर, तू सर्वोत्तम क्षमावान् है।');
INSERT INTO "al_umari" VALUES(7,156,'7:156','और हमारे लिए इस संसार में भलाई लिख दे तथा परलोक में भी, हम तेरी ओर लौट आये। उस (अल्लाह) ने कहाः मैं अपनी यातना जिसे चाहता हूँ, देता हूँ। और मेरी दया प्रत्येक चीज़ को समोये हुए है। मैं उसे उन लोगों के लिए लिख दूँगा, जो अवज्ञा से बचेंगे, ज़कात देंगे और जो हमारी आयतों पर ईमान लायेंगे।');
INSERT INTO "al_umari" VALUES(7,157,'7:157','जो उस रसूल का अनुसरण करेंगे, जो उम्मी नबी[ हैं, जिन (के आगमन) का उल्लेख वे अपने पास तौरात तथा इंजील में पाते हैं; जो सदाचार का आदेश देंगे और दुराचार से रोकेंगे, उनके लिए स्वच्छ चीज़ों को ह़लाल (वैध) तथा मलिन चीज़ों को ह़राम (अवैध) करेंगे, उनसे उनके बोझ उतार देंगे तथा उन बंधनों को खोल देंगे, जिनमें वे जकड़े हुए होंगे। अतः जो लोग आपपर ईमान लाये, आपका समर्थन किया, आपकी सहायता की तथा उस प्रकाश (क़ुर्आन) का अनुसरण किया, जो आपके साथ उतारा गया, तो वही सफल होंगे। ');
INSERT INTO "al_umari" VALUES(7,158,'7:158','(हे नबी!) आप लोगों से कह दें कि हे मानव जाति के लोगो! मैं तुम सभी की ओर उस अल्लाह का रसूल हूँ, जिसके लिए आकाश तथा धरती का राज्य है। कोई वंदनीय (पूज्य) नहीं है, परन्तु वही, जो जीवन देता तथा मारता है। अतः अल्लाह पर ईमान लाओ और उसके उस उम्मी नबी पर, जो अल्लाह पर और उसकी सभी (आदि) पुस्तकों पर ईमान रखते हैं और उनका अनुसरण करो, ताकि तुम मार्गदर्शन पा जाओ[। ');
INSERT INTO "al_umari" VALUES(7,159,'7:159','और मूसा की जाति में एक गिरोह ऐसा भी है, जो सत्य पर स्थित है और उसी के अनुसार निर्णय (न्याय) करता है।');
INSERT INTO "al_umari" VALUES(7,160,'7:160','और[ हमने मूसा की जाति के बारह घरानों को बारह जातियों में विभक्त कर दिया और हमने मूसा की ओर वह़्यी भेजी, जब उसकी जाति ने उससे जल माँगा कि अपनी लाठी इस पत्थर पर मारो, तो उससे बारह स्रोत उबल पड़े तथा प्रत्येक समुदाय ने अपने पीने का स्थान जान लिया और उनपर बादलों की छाँव की और उनपर मन्न तथा सल्वा उतारा। (हमने कहाः) इन स्वच्छ चीज़ों में से, जो हमने तुम्हें प्रदान की हैं, खाओ और हमने उनपर अत्याचार नहीं किया, परन्तु वे स्वयं (अवज्ञा करके) अपने प्राणों पर अत्याचार कर रहे थे। ');
INSERT INTO "al_umari" VALUES(7,161,'7:161','और जब उन (बनी इस्राईल) से कहा गया कि इस नगर (बैतुल मक़्दिस) में बस जाओ और उसमें से जहाँ इच्छा हो, खाओ और कहो कि हमें क्षमा कर दे तथा द्वार से सज्दा करते हुए प्रवेश करो, हम तुम्हारे लिए तुम्हारे दोष क्षमा कर देंगे और सत्कर्मियों को और अधिक देंगे।');
INSERT INTO "al_umari" VALUES(7,162,'7:162','तो उनमें से अत्याचारियों ने उस बात को, दूसरी बात से[ बदल दिया, जो उनसे कही गयी थी। तो हमने उनपर आकाश से प्रकोप उतार दिया। क्योंकि वे अत्याचार कर रहे थे। ');
INSERT INTO "al_umari" VALUES(7,163,'7:163','तथा (हे नबी!) इनसे उस नगरी के सम्बंध में प्रश्न करो, जो समुद्र (लाल सागर) के समीप थी, जब उसके निवासी सब्त (शनिवार) के दिन के विषय में आज्ञा का उल्लंघन[ कर रहे थे, जब उनके पास उनकी मछलियाँ सब्त के दिन पानी के ऊपर तैरकर आ जाती थीं और सब्त का दिन न हो, तो नहीं आती थीं। इसी प्रकार, उनकी अवज्ञा के कारण हम उनकी परीक्षा ले रहे थे। ');
INSERT INTO "al_umari" VALUES(7,164,'7:164','तथा जब उनमें से एक समुदाय ने कहा कि तुम उन्हें क्यों समझा रहे हो, जिन्हें अल्लाह (उनकी अवज्ञा के कारण) ध्वस्त करने अथवा कड़ा दण्ड देने वाला है? उन्होंने कहाः तुम्हारे पालनहार के समक्ष क्षम्य होने के लिए और इस आशा में कि वे आज्ञाकारी हो जायेँ[। ');
INSERT INTO "al_umari" VALUES(7,165,'7:165','फिर जब उन्होंने जो कुछ उन्हें स्मरण कराया गया, उसे भुला दिया, तो हमने उन लोगों को बचा लिया, जो उन्हें बुराई से रोक रहे थे और हमने अत्याचारियों को कड़ी यातना में उनकी अवज्ञा के कारण घेर लिया।');
INSERT INTO "al_umari" VALUES(7,166,'7:166','फिर जब उन्होंने उसका उल्लंघन किया, जिससे वे रोके गये थे, तो हमने उनसे कहा कि तुच्छ बंदर हो जाओ।');
INSERT INTO "al_umari" VALUES(7,167,'7:167','और याद करो, जब आपके पालनहार ने घोषणा कर दी कि वह प्रलय के दिन तक, उन (यहूदियों) पर उन्हें प्रभुत्व देता रहेगा, जो उन्हें घोर यातना देते रहेंगे[। निःसंदेह आपका पालनहार शीघ्र दण्ड देने वाला है और वह अति क्षमाशील, दयावान (भी) है। ');
INSERT INTO "al_umari" VALUES(7,168,'7:168','और हमने उन्हें धरती में कई सम्प्रदायों में विभक्त कर दिया, उनमें कुछ सदाचारी थे और कुछ इसके विपरीत थे। हमने अच्छाईयों तथा बुराईयों, दोनों के द्वारा उनकी परीक्षा ली, ताकि वे (कुकर्मों से) रुक जायेँ।');
INSERT INTO "al_umari" VALUES(7,169,'7:169','फिर उनके पीछे कुछ ऐसे लोगों ने उनकी जगह ली, जो पुस्तक के उत्तराधिकारी होकर भी तुच्छ संसार का लाभ समेटने लगे और कहने लगे कि हमें क्षमा कर दिया जायेगा और यदि उसी के समान उन्हें लाभ हाथ आ जाये, तो उसे भी ले लेंगे। क्या उनसे पुस्तक का दृढ़ वचन नहीं लिया गया है कि अल्लाह पर सच ही बोलेंगे, जबकि पुस्तक में जो कुछ है, उसका अध्ययन कर चुके हैं? और परलोक का घर (स्वर्ग) उत्तम है, उनके लिए जो अल्लाह से डरते हों। तो क्या वे इतना भी नहीं[ समझते? ');
INSERT INTO "al_umari" VALUES(7,170,'7:170','और जो लोग पुस्तक को दृढ़ता से पकड़ते और नमाज़ की स्थापना करते हैं, तो वास्तव में, हम सत्कर्मियों का प्रतिफल अकारथ नहीं करते।');
INSERT INTO "al_umari" VALUES(7,171,'7:171','और जब हमने उनके ऊपर पर्वत को इस प्रकार छा दिया, जैसे वह कोई छतरी हो और उन्हें विश्वास हो गया कि वह उनपर गिर पड़ेगा, (तथा ये आदेश दिया कि) जो (पुस्तक) हमने तुम्हें प्रदान की है, उसे दृढ़ता से थाम लो तथा उसमें जो कुछ है, उसे याद रखो, ताकि तुम आज्ञाकारी हो जाओ।');
INSERT INTO "al_umari" VALUES(7,172,'7:172','तथा (वह समय याद करो) जब आपके पालनहार ने आदम के पुत्रों की पीठों से उनकी संतति को निकाला और उन्हें स्वयं उनपर साक्षी (गवाह) बनाते हुए कहाः क्या मैं तुम्हारा पालनहार नहीं हूँ? सबने कहाः क्यों नहीं? हम (इसके) साक्षी[ हैं; ताकि प्रलय के दिन ये न कहो कि हम तो इससे असूचित थे। ');
INSERT INTO "al_umari" VALUES(7,173,'7:173','अथवा ये कहो कि हमसे पूर्व हमारे पूर्वजों ने शिर्क (मिश्रण) किया और हम उनके पश्चात् उनकी संतान थे। तो क्या तू गुमराहों के कर्म के कारण हमारा विनाश[ करेगा? ');
INSERT INTO "al_umari" VALUES(7,174,'7:174','और इसी प्रकार, हम आयतों को खोल-खोल कर बयान करते हैं, ताकि लोग (सत्य की ओर) लौट जायेँ।');
INSERT INTO "al_umari" VALUES(7,175,'7:175','और उन्हें उसकी दशा पढ़कर सुनायें, जिसे हमने अपनी आयतों (का ज्ञान) दिया, तो वह उस (के खोल से) निकल गया। फिर शैतान उसके पीछे लग गया और वह कुपथों में हो गया।');
INSERT INTO "al_umari" VALUES(7,176,'7:176','और यदि हम चाहते, तो उन (आयतों) के द्वारा उसका पद ऊँचा कर देते, परन्तु वह माया-मोह में पड़ गया और अपनी मनमानी करने लगा। तो उसकी दशा उस कुत्ते के समान हो गयी, जिसे हाँको, तब भी जीभ निकाले हाँपता रहे और छोड़ दो, तब भी जीभ निकाले हाँपता है। यही उपमा है, उन लोगों की, जो हमारी आयतों को झुठलाते हैं। तो आप ये कथायें उन्हें सुना दें, संभवतः वे सोच-विचार करें।');
INSERT INTO "al_umari" VALUES(7,177,'7:177','उनकी उपमा कितनी बुरी है, जिन लोगों ने हमारी आयतों को झुठला दिया! और वे अपने ही ऊपर अत्याचार[ कर रहे थे। ');
INSERT INTO "al_umari" VALUES(7,178,'7:178','जिसे अल्लाह सुपथ कर दे, वही सीधी राह पा सकता है और जिसे कुपथ कर दे,[ तो वही लोग असफल हैं। ');
INSERT INTO "al_umari" VALUES(7,179,'7:179','और बहुत-से जिन्न और मानव को हमने नरक के लिए पैदा किया है। उनके पास दिल हैं, जिनसे सोच-विचार नहीं करते, उनकी आँखें हैं, जिनसे[ नहीं देखते और कान हैं, जिनसे नहीं सुनते। वे पशुओं के समान हैं; बल्कि उनसे भी अधिक कुपथ हैं, यही लोग अचेतना में पड़े हुए हैं। ');
INSERT INTO "al_umari" VALUES(7,180,'7:180','और अल्लाह ही के शुभ नाम हैं, अतः उसे उन्हीं के द्वारा पुकारो और उन्हें छोड़ दो, जो उसके नामों में परिवर्तन[ करते हैं, उन्हें शीघ्र ही उनके कुकर्मों का कुफल दे दिया जायेगा। ');
INSERT INTO "al_umari" VALUES(7,181,'7:181','और उनमें से जिन्हें हमने पैदा किया है, एक समुदाय ऐसा (भी) है, जो सत्य का मार्ग दर्शाता तथा उसी के अनुसार (लोगों के बीच) न्याय करता है।');
INSERT INTO "al_umari" VALUES(7,182,'7:182','और जिन लोगों ने हमारी आयतों को झुठला दिया, हम उन्हें क्रमशः (विनाश तक) ऐसे पहुँचायेंगे कि उन्हें इसका ज्ञान नहीं होगा।');
INSERT INTO "al_umari" VALUES(7,183,'7:183','और उन्हें अवसर देंगे, निश्चय मेरा उपाय बड़ा सुदृढ़ है।');
INSERT INTO "al_umari" VALUES(7,184,'7:184','और क्या उन्होंने ये नहीं सोचा कि उनका साथी[ तनिक भी पागल नहीं है? वह तो केवल खुले रूप से सचेत करने वाला है। ');
INSERT INTO "al_umari" VALUES(7,185,'7:185','क्या उन्होंने आकाशों तथा धरती के राज्य को और जो कुछ अल्लाह ने पैदा किया है, उसे नहीं देखा[? और (येभी नहीं सोचा कि) हो सकता है कि उनका (निर्धारित) समय समीप आ गया हो? तो फिर इस (क़ुर्आन) के पश्चात् वह किस बात पर ईमान लायेंगे? ');
INSERT INTO "al_umari" VALUES(7,186,'7:186','जिसे अल्लाह कुपथ कर दे, उसका कोई पथपर्दर्शक नहीं और उन्हें उनके कुकर्मों में बहकते हुए छोड़ देता है।');
INSERT INTO "al_umari" VALUES(7,187,'7:187','(हे नबी!) वे आपसे प्रलय के विषय में प्रश्न करते हैं कि वह कब आयेगी? कह दो कि उसका ज्ञान तो मेरे पालनहार के पास है, उसे उसके समय पर वही प्रकाशित कर देगा। वह आकाशों तथा धरती में भारी होगी, तुमपर अकस्मात आ जायेगी। वह आपसे ऐसे प्रश्न कर रहे हैं, जैसे कि आप उसी की खोज में लगे हुए हों। आप कह दें कि उसका ज्ञान अल्लाह ही को है। परन्तु[ अधिकांश लोग इस (तथ्य) को नहीं जानते। ');
INSERT INTO "al_umari" VALUES(7,188,'7:188','आप कह दें कि मुझे तो अपने लाभ और हानि का अधिकार नहीं, परन्तु जो अल्लाह चाहे, (वही होता है)। यदि मैं ग़ैब (परोक्ष) का ज्ञान रखता, तो मैं बहुत सा लाभ प्राप्त कर लेता। मैं तो केवल उन लोगों को सावधान करने तथा शुभ सूचना देने वाला हूँ, जो ईमान (विश्वास) रखते हैं।');
INSERT INTO "al_umari" VALUES(7,189,'7:189','वही (अल्लाह) है, जिसने तुम्हारी उत्पत्ति एक जीव[ से की और उसीसे उसका जोड़ा बनाया, ताकि उससे उसे संतोष मिले। फिर जब किसी ने उस (अपनी स्त्री) से सहवास किया, तो उस (स्त्री) को हल्का सा गर्भ हो गया। जिसके साथ वह चलती फिरती रही, फिर जब बोझल हो गयी, तो दोनों (पति-पत्नी) ने अपने पालनहार से प्रार्थना कीः यदि तू हमें एक अच्छा बच्चा प्रदान करेगा, तो हम अवश्य तेरे कृतज्ञ (आभारी) होंगे।');
INSERT INTO "al_umari" VALUES(7,190,'7:190','और जब उन दोनों को (अल्लाह ने) एक स्वस्थ बच्चा प्रदान कर दिया, तो अल्लाह ने जो प्रदान किया, उसमें दूसरों को उसका साझी बनाने लगे। तो अल्लाह इनके शिर्क[ की बातों से बहुत ऊँचा है। ');
INSERT INTO "al_umari" VALUES(7,191,'7:191','क्या वह अल्लाह का साझी उन्हें बनाते हैं, जो कुछ पैदा नहीं कर सकते और वे स्वयं पैदा किये हुए हैं?');
INSERT INTO "al_umari" VALUES(7,192,'7:192','तथा न उनकी सहायता कर सकते हैं और न स्वयं अपनी सहायता कर सकते हैं?');
INSERT INTO "al_umari" VALUES(7,193,'7:193','और यदि तुम उन्हें सीधी राह की ओर बुलाओ, तो तुम्हारे पीछे नहीं चल सकते। तुम्हारे लिए बराबर है, चाहे उन्हें पुकारो अथवा तुम चुप रहो।');
INSERT INTO "al_umari" VALUES(7,194,'7:194','वास्तव में, अल्लाह के सिवा जिनको तुम पुकारते हो, वे तुम्हारे जैसे ही (अल्लाह के) दास हैं। अतः तुम उनसे प्रार्थना करो, फिर वे तुम्हारी प्रार्थना का उत्तर दें, यदि उनके बारे में तुम्हारे विचार सत्य हैं?');
INSERT INTO "al_umari" VALUES(7,195,'7:195','क्या इन (पत्थर की मूर्तियों) के पाँव हैं, जिनसे चलती हों? उनके हाथ हैं, जिनसे पकड़ती हों, उनकी आँखें हैं, जिनसे देखती हों? अथवा कान हैं, जिनसे सुनती हों? आप कह दें कि अपने साझियों को पुकार लो, फिर मेरे विरुध्द उपाय कर लो और मुझे कोई अवसर न दो!');
INSERT INTO "al_umari" VALUES(7,196,'7:196','वास्तव में, मेरा संरक्षक अल्लाह है, जिसने ये पुस्तक (क़ुर्आन) उतारी है और वही सदाचारियों की रक्षा करता है।');
INSERT INTO "al_umari" VALUES(7,197,'7:197','और जिन्हें अल्लाह के सिवा तुम पुकारते हो, वे न तुम्हारी सहायता कर सकते हैं और न स्वयं अपनी ही सहायता कर सकते हैं।');
INSERT INTO "al_umari" VALUES(7,198,'7:198','और यदि तुम उन्हें सीधी राह की ओर बुलाओ, तो वे सुन नहीं सकते और (हे नबी!) आप उन्हें देखेंगे कि वे आपकी ओर देख रहे हैं, जबकि वास्तव में वे कुछ नहीं देखते।');
INSERT INTO "al_umari" VALUES(7,199,'7:199','(हे नबी!) आप क्षमा से काम लें, सदाचार का आदेश दें तथा अज्ञानियों की ओर ध्यान[ न दें। ');
INSERT INTO "al_umari" VALUES(7,200,'7:200','और यदि शैतान आपको उकसाये, तो अल्लाह से शरण माँगिये। निःसंदेह वह सबकुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(7,201,'7:201','वास्तव में, जो आज्ञाकारी होते हैं, यदि शैतान की ओर से उन्हें कोई बुरा विचार आ भी जाये, तो तत्काल चौंक पड़ते हैं और फिर अकस्मात् उन्हें सूझ आ जाती है।');
INSERT INTO "al_umari" VALUES(7,202,'7:202','और जो शैतानों के भाई हैं, वे उन्हें कुपथ में खींचते जाते हैं, फिर (उन्हें कुपथ करने में) तनिक भी कमी (आलस्य) नहीं करते।');
INSERT INTO "al_umari" VALUES(7,203,'7:203','और जब आप, इन मिश्रणवादियों के पास कोई निशानी न लायेंगे, तो कहेंगे कि क्यों (अपनी ओर से) नहीं बना ली? आप कह दें कि मैं केवल उसी का अनुसरण करता हूँ, जो मेरे पालनहार के पास से मेरी ओर वह़्यी की जाती है। ये सूझ की बातें हैं, तुम्हारे पालनहार की ओर से (प्रमाण) हैं तथा मार्गदर्शन और दया हैं, उन लोगों के लिए जो ईमान (विश्वास) रखते हों।');
INSERT INTO "al_umari" VALUES(7,204,'7:204','और जब क़ुर्आन पढ़ा जाये, तो उसे ध्यान पूर्वक सुनो तथा मौन साध लो। शायद कि तुमपर दया[ की जाये। ');
INSERT INTO "al_umari" VALUES(7,205,'7:205','और (हे नबी!) अपने पालनहार का स्मरण विनय पूर्वक तथा डरते हुए और धीमे स्वर में प्रातः तथा संध्या करते रहो और उनमें न हो जाओ, जो अचेत रहते हैं।');
INSERT INTO "al_umari" VALUES(7,206,'7:206','वास्तव में, जो (फ़रिश्ते) आपके पालनहार के समीप हैं, वे उसकी इबादत (वंदना) से अभिमान नहीं करते, उसकी पवित्रता वर्णन करते रहते हैं और उसी को सज्दा[ करते हैं। ');
INSERT INTO "al_umari" VALUES(8,1,'8:1','(हे नबी!) आपसे (आपके साथी) युध्द में प्राप्त धन के विषय में प्रश्न कर रहे हैं। कह दें कि यूध्द में प्राप्त धन अल्लाह और रसूल के हैं। अतः अल्लाह से डरो और आपस में सुधार रखो तथा अल्लाह और उसके रसूल के आज्ञाकारी रहो[ यदि तुम ईमान वाले हो। ');
INSERT INTO "al_umari" VALUES(8,2,'8:2','वास्तव में, ईमान वाले वही हैं कि जब अल्लाह का वर्णन किया जाये, तो उनके दिल काँप उठते हैं और जब उनके समक्ष उसकी आयतें पढ़ी जायें, तो उनका ईमान अधिक हो जाता है और वे अपने पालनहार पर ही भरोसा रखते हैं।');
INSERT INTO "al_umari" VALUES(8,3,'8:3','जो नमाज़ की स्थापना करते हैं तथा हमने उन्हें जो कुछ प्रदान किया है, उसमें से दान करते हैं।');
INSERT INTO "al_umari" VALUES(8,4,'8:4','वही सच्चे ईमान वाले हैं, उन्हीं के लिए उनके पालनहार के पास श्रेणियाँ तथा क्षमा और उत्तम जीविका है।');
INSERT INTO "al_umari" VALUES(8,5,'8:5','जिस प्रकार,[ आपको, आपके पालनहार ने, आपके घर (मदीना) से (मिश्रणवादियों से युध्द के लिए सत्य के साथ) निकाला, जबकि ईमान वालों का एक समुदाय इससे अप्रसन्न था। ');
INSERT INTO "al_umari" VALUES(8,6,'8:6','वे आपसे सच (युध्द) के बारे में झगड़ रहे थे, जबकि वह उजागर हो गया था, (कि युध्द होना है, उनकी ये ह़ालत थी,) जैसे वे मौत की ओर हाँके जा रहे हों और वे उसे देख रहे हों।');
INSERT INTO "al_umari" VALUES(8,7,'8:7','तथा (वह समय याद करो) जब अल्लाह तुम्हें वचन दे रहा था कि दो गिरोहों में एक तुम्हारे हाथ आयेगा और तुम चाहते थे कि निर्बल गिरोह तुम्हारे हाथ लगे[। परन्तु अल्लाह चाहता था कि अपने वचन द्वारा सत्य को सिध्द कर दे और काफ़िरों की जड़ काट दे। ');
INSERT INTO "al_umari" VALUES(8,8,'8:8','इस प्रकार सत्य को सत्य और असत्य को असत्य कर दे। यद्यपि अपराधियों को बुरा लगे।');
INSERT INTO "al_umari" VALUES(8,9,'8:9','जब तुम अपने पालनहार को (बद्र के युध्द के समय) गुहार रहे थे। तो उसने तुम्हारी प्रार्थना सुन ली। (और कहाः) मैं तुम्हारी सहायता के लिए लगातार एक हज़ार फ़रिश्ते भेज रहा[ हूँ। ');
INSERT INTO "al_umari" VALUES(8,10,'8:10','और अल्लाह ने ये इसलिए बता दिया, ताकि (तुम्हारे लिए) शुभ सूचना हो और ताकि तुम्हारे दिलों को संतोष हो जाये। अन्यथा सहायता तो अल्लाह ही की ओर से होती है। वास्तव में, अल्लाह प्रभुत्वशाली तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(8,11,'8:11','और वह समय याद करो जब अल्लाह अपनी ओर से शान्ति के लिए तुमपर ऊँघ डाल रहा था और तुमपर आकाश से जल बरसा रहा था, ताकि तुम्हें स्वच्छ कर दे और तुमसे शैतान की मलीनता दूर कर दे और तुम्हारे दिलों को साहस दे और (तुम्हारे) पाँव जमा दे[। ');
INSERT INTO "al_umari" VALUES(8,12,'8:12','(हे नबी!) ये वो समय था, जब आपका पालनहार फ़रिश्तों को संकेत कर रहा था कि मैं तुम्हारे साथ हूँ। तुम ईमान वालों को स्थिर रखो, मैं कीफ़िरों के दिलों में भय डाल दूँगा। तो (हे मुसलमानों!) तुम उनकी गरदनों पर तथा पोर-पोर पर आघात पहुँचाओ।');
INSERT INTO "al_umari" VALUES(8,13,'8:13','ये इसलिए कि उन्होंने अल्लाह और उसके रसूल का विरोध किया तथा जो अल्लाह और उसके रसूल का विरोध करेगा, तो निश्चय अल्लाह उसे कड़ी यातना देने वाला है।');
INSERT INTO "al_umari" VALUES(8,14,'8:14','ये है (तुम्हारी यातना), तो इसका स्वाद चखो और (जान लो कि) काफ़िरों के लिए नरक की यातना (भी) है।');
INSERT INTO "al_umari" VALUES(8,15,'8:15','हे इमान वालो! जब काफ़िरों की सेना से भिड़ो, तो उन्हें पीठ न दिखाओ।');
INSERT INTO "al_umari" VALUES(8,16,'8:16','और जो कोई उस दिन अपनी पीठ दिखायेगा, परन्तु फिरकर आक्रमण करने अथवा (अपने) किसी गिरोह से मिलने के लिए, तो वह अल्लाह के प्रकोप में घिर जायेगा और उसका स्थान नरक है और वह बहुत ही बुरा स्थान है।');
INSERT INTO "al_umari" VALUES(8,17,'8:17','अतः (रणक्षेत्र में) उन्हें वध तुमने नहीं किया, परन्तु अल्लाह ने उन्हें वध किया और हे नबी! आपने नहीं फेंका, जब फेंका, परन्तु अल्लाह ने फेंका। और (ये इसलिए हुआ) ताकि अल्लाह इसके द्वारा ईमान वालों की एक उत्तम परीक्षा ले। वास्तव में, अल्लाह सबकुछ सुनने और जानने[ वाला है। ');
INSERT INTO "al_umari" VALUES(8,18,'8:18','ये सब तुम्हारे लिए हो गया और अल्लाह काफ़िरों की चालों को निर्बल करने वाला है।');
INSERT INTO "al_umari" VALUES(8,19,'8:19','यदि तुम[ निर्णय चाहते हो, तो तुम्हारे सामने निर्णय आ गया है और यदि तुम रुक जाओ, तो तुम्हारे लिए उत्तम है और यदि फिर पहले जैसा करोगे, तो हमभी वैसा ही करेंगे और तुम्हारा जत्था तुम्हारे कुछ काम नहीं आयेगा, यद्यपि अधिक हो और निश्चय अल्लाह ईमान वालों के साथ है। ');
INSERT INTO "al_umari" VALUES(8,20,'8:20','हे ईमान वालो! अल्लाह के आज्ञाकारी रहो तथा उसके रसूल के और उससे मुँह न फेरो, जबकि सुन रहे हो।');
INSERT INTO "al_umari" VALUES(8,21,'8:21','तथा उनके समान[ न हो जाओ, जिन्होंने कहा कि हमने सुन लिया, जबकि वास्तव में वे सुनते नहीं थे। ');
INSERT INTO "al_umari" VALUES(8,22,'8:22','वास्तव में, अल्लाह के हाँ सबसे बुरे पशु वे (मानव) हैं, जो बहरे-गूँगे हों, जो कुछ समझते न हों।');
INSERT INTO "al_umari" VALUES(8,23,'8:23','और यदि अल्लाह उनमें कुछ भी भलाई जानता, तो उन्हें सुना देता और यदि उन्हें सुना भी दे, तो भी वे मुँह फेर लेंगे और वे विमुख हैं ही।');
INSERT INTO "al_umari" VALUES(8,24,'8:24','हे ईमान वालो! अल्लाह और उसके रसूल की पुकार सुनो, जब तुम्हें उसकी ओर बुलाये, जो तुम्हारी[ (आत्मा) को जीवन प्रदान करे और जान लो कि अल्लाह मानव और उसके दिल के बीच आड़े आ जाता है और निःसंदेह तुम उसी के पास (अपने कर्म फल के लिए) एकत्र किये जाओगे।');
INSERT INTO "al_umari" VALUES(8,25,'8:25','तथा उस आपदा से डरो, जो तुममें से अत्याचारियों पर ही विशेष रूप से नहीं आयेगी और विश्वास रखो[ कि अल्लाह कड़ी यातना देने वाला है। ');
INSERT INTO "al_umari" VALUES(8,26,'8:26','तथा वो समय याद करो, जबतुम (मक्का में) बहुत थोड़े निर्बल समझे जाते थे। तुम डर रहे थे कि लोग तुम्हें उचक न लें। तो अल्लाह ने तुम्हें (मदीना में) शरण दी और अपनी सहायता द्वारा तुम्हें समर्थन दिया और तुम्हें स्वच्छ जीविका प्रदान की, ताकि तुम कृतज्ञ रहो।');
INSERT INTO "al_umari" VALUES(8,27,'8:27','हे ईमान वालो! अल्लाह तथा उसके रसूल के साथ विश्वासघात न करो और न अपनी अमानतों (कर्तव्य) के साथ विश्वासघात[ करो, जानते हुए। ');
INSERT INTO "al_umari" VALUES(8,28,'8:28','तथा जान लो कि तुम्हारा धन और तुम्हारी संतान एक परीक्षा हैं तथा ये कि अल्लाह के पास बड़ा प्रतिफल है।');
INSERT INTO "al_umari" VALUES(8,29,'8:29','हे ईमान वालो! यदि तुम अल्लाह से डरोगे, तो तुम्हें विवेक[ प्रदान करेगा तथा तुमसे तुम्हारी बुराईयाँ दूर कर देगा, तुम्हे क्षमा कर देगा और अल्लाह बड़ा दयाशील है। ');
INSERT INTO "al_umari" VALUES(8,30,'8:30','तथा (हे नबी! वो समय याद करो) जब (मक्का में) काफ़िर आपके विरुध्द षड्यंत्र रच रहे थे, ताकि आपको क़ैद कर लें, वध कर दें अथवा देश निकाला दे दें तथा वे षड्यंत्र रच रहे थे और अल्लह अपना उपाय कर रहा था और अल्लाह का उपाय[ सबसे उत्तम है। ');
INSERT INTO "al_umari" VALUES(8,31,'8:31','और जब उनको हमारी आयतें सुनाई जाती हैं, तो कहते हैः हमने (इसे) सुन लिया है। यदि हम चाहें, तो इसी (क़ुर्आन) जैसी बातें कह दें। ये तो वही प्राचीन लोगों की कथायें हैं।');
INSERT INTO "al_umari" VALUES(8,32,'8:32','तथा (याद करो) जब उन्होंने कहाः हे अल्लाह! यदि ये[ तेरी ओर से सत्य है, तो हमपर आकाश से पत्थरों की वर्षा कर दे अथवा हमपर दुःखदायी यातना ले आ। ');
INSERT INTO "al_umari" VALUES(8,33,'8:33','और अल्लाह उन्हें यातना नहीं दे सकता था, जब तक आप उनके बीच थे और न उन्हें यातना देने वाला है, जब तक वे क्षमा याचना कर रहे हों।');
INSERT INTO "al_umari" VALUES(8,34,'8:34','और अब उनपर क्यों न यातना उतारे, जबकि वे सम्मानित मस्जिद (काबा) से रोक रहे हैं, जबकि वे उसके संरक्षक नहीं हैं। उसके संरक्षक तो केवल अल्लाह के आज्ञाकारी हैं, परन्तु अधिकांश लोग (इसे) नहीं जानते।');
INSERT INTO "al_umari" VALUES(8,35,'8:35','और अल्लाह के घर (काबा) के पास इनकी नमाज़ इसके सिवा क्या थी कि सीटियाँ और तालियाँ बजायें? तो अब[ अपने कुफ़्र (अस्वीकार) के बदले यातना का स्वाद चखो। ');
INSERT INTO "al_umari" VALUES(8,36,'8:36','जो काफ़िर हो गये, वे अपना धन इसलिए ख़र्च करते हैं कि अल्लाह की राह से रोक दें। तो वे अपना धन ख़र्च करते रहेंगे, फिर (वो समय आयेगा कि) वह उनके लिए पछतावे का कारण हो जायेगा। फिर पराजित होंगे तथा जो काफ़िर हो गये, वे नरक की ओर हाँक दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(8,37,'8:37','ताकि अल्लाह मलीन को पवित्र से अलग कर दे तथा मलीनों को एक-दूसरे से मिला दे। फिर सबका ढेर बना दे और उन्हें नरक में फेंक दे, यही क्षतिग्रस्त हैं।');
INSERT INTO "al_umari" VALUES(8,38,'8:38','(हे नबी!) इन काफ़िरों से कह दोः यदि वे रुक[ गये, तो जो कुछ हो गया है, वह उनसे क्षमा कर दिया जायेगा और यदि पहले जैसा ही करेंगे, तो अगली जातियों की दुर्गत हो चुकी है। ');
INSERT INTO "al_umari" VALUES(8,39,'8:39','हे ईमान वालो! उनसे उस समय तक युध्द करो कि[ फ़ित्ना (अत्याचार तथा उपद्रव) समाप्त हो जाये और धर्म पूरा अल्लाह के लिए हो जाये। तो यदि वे (अत्याचार से) रुक जायें तो अल्लाह उनके कर्मों को देख रहा है। ');
INSERT INTO "al_umari" VALUES(8,40,'8:40','और यदि वे मुँह फेरें, तो जान लो कि अल्लाह रक्षक है और वह क्या ही अच्छा संरक्षक तथा क्या ही अच्छा सहायक है?');
INSERT INTO "al_umari" VALUES(8,41,'8:41','और जान[ लो कि तुम्हें जो कुछ ग़नीमत में मिला है, उसका पाँचवाँ भाग अल्लाह तथा रसूल और (आपके) समीपवर्तियों तथा अनाथों, निर्धनों और यात्रियों के लिए है, यदि तुम अल्लाह पर तथा उस (सहायता) पर ईमान रखते हो, जो हमने अपने भक्त पर निर्णय के दिन उतारी, जिस दिन, दो सेनायें भिड़ गईं और अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(8,42,'8:42','तथा उस समय को याद करो जब तुम (रणक्षेत्र में) इधर के किनारे तथा वे (शत्रु) उधर के किनारे पर थे और क़ाफ़िला तुमसे नीचे था। यदि तुम आपस में (युध्द का) निश्चय करते, तो निश्चित समय से अवश्य कतरा जाते। परन्तु अल्लाह ने (दोनों को भिड़ा दिया) ताकि जो होना था, उसका निर्णय कर दे, ताकि जो मरे, वह खुले प्रमाण के पश्चात मरे और जो जीवित रहे, वह खुले प्रमाण के साथ जीवित रहे और वस्तुतः अल्लाह सबकुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(8,43,'8:43','तथा (हे नबी! वो समय याद करें) जब आपको, (अल्लाह) आपके सपने[ में, उन्हें (शत्रु को) थोड़ा दिखा रहा था और यदि उन्हें आपको अधिक दिखा देता, तो तुम साहस खो देते और इस (युध्द के) विषय में आपस में झगड़ने लगते। परन्तु अल्लाह ने तुम्हें बचा लिया। वास्तव में, वह सीनों (अन्तरात्मा) की बातों से भली-भाँति अवगत है। ');
INSERT INTO "al_umari" VALUES(8,44,'8:44','तथा (याद करो उस समय को) जब अल्लाह उन (शत्रु) को लड़ाई के समय तुम्हारी आँखों में तुम्हारे लिए थोड़ा करके दिखा रहा था और इनकी आँखों में तुम्हें थोड़ा करके दिखा रहा था, ताकि जो होना था, अल्लाह उसका निर्णय कर दे और सभी कर्म अल्लाह ही की ओर फेरे[ जाते हैं। ');
INSERT INTO "al_umari" VALUES(8,45,'8:45','हे ईमान वालो! जब (आक्रमणकारियों) के किसी गिरोह से भिड़ो, तो जम जाओ तथा अल्लाह को बहुत याद करो, ताकि तुम सफल रहो।');
INSERT INTO "al_umari" VALUES(8,46,'8:46','तथा अल्लाह और उसके रसूल के आज्ञाकारी रहो और आपस में विवाद न करो, अन्यथा तुम कमज़ोर हो जाओगे और तुम्हारी हवा उखड़ जायेगी तथा धैर्य से काम लो, वास्तव में, अल्लाह धैर्यवानों के साथ है।');
INSERT INTO "al_umari" VALUES(8,47,'8:47','और उन[ के समान न हो जाओ, जो अपने घरों से इतराते हुए तथा लोगों को दिखाते हुए निकले और वे अल्लाह की राह (इस्लाम) से लोगों को रोकते हैं और अल्लाह उनके कर्मों को (अपने ज्ञान के) घेरे में लिए हुए है। ');
INSERT INTO "al_umari" VALUES(8,48,'8:48','जब शैतान[ ने उनके लिए उनके कुकर्मों को शोभनीय बना दिया था और उस (शैतान) ने कहाः आज तुमपर कोई प्रभुत्व नहीं पा सकता और मैं तुम्हारा सहायक हूँ। फिर जब दोनों सेनायें सम्मुख हो गईं, तो अपनी एड़ियों के बल फिर गया और कह दिया कि मैं तुमसे अलग हूँ। मैं जो देख रहा हूँ, तुम नहीं देखते। वास्तव में, मैं अल्लाह से डर रहा हूँ और अल्लाह कड़ी यातना देने वाला है। ');
INSERT INTO "al_umari" VALUES(8,49,'8:49','तथा (वो समय याद करो) जब मुनाफ़िक़ तथा जिनके दिलों में रोग है, वे कह रहे थे कि इन (मुसलमानों) को इनके धर्म ने धोखा दिया है तथा जो अल्लाह पर निर्भर करे, तो वास्तव में, अल्लाह प्रभुत्वशाली तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(8,50,'8:50','और क्या ही अच्छा होता, यदि आप उस दशा को देखते, जब फ़रिश्ते (बधिक) काफ़िरों के प्राण निकाल रहे थे, तो उनके मुखों और उनकी पीठों पर मार रहे थे तथा (कह रहे थे कि) दहन की यातना[ चखो। ');
INSERT INTO "al_umari" VALUES(8,51,'8:51','यही तुम्हारे करतूतों का प्रतिफल है और अल्लाह अपने भक्तों पर अत्याचार करने वाला नहीं है।');
INSERT INTO "al_umari" VALUES(8,52,'8:52','इनकी दशा भी फ़िरऔनियों तथा उनके जैसी हुई, जिन्होंने इससे पहले अल्लाह की आयतों को नकार दिया, तो अल्लाह ने उनके पापों के बदले उन्हें पकड़ लिया। वास्तव में, अल्लाह बड़ा शक्तिशाली, कड़ी यातना देने वाला है।');
INSERT INTO "al_umari" VALUES(8,53,'8:53','अल्लाह का ये नियम है कि वह उस पुरस्कार में परिवर्तन करने वाला नहीं है, जो किसी जाति पर किया हो, जब तक वह स्वयं अपनी दशा में परिवर्तन न कर ले और वास्तव में, अल्लाह सबकुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(8,54,'8:54','इनकी दशा फ़िरऔनियों तथा उन लोगों जैसी हुई, जो इनसे पहले थे, उन्होंने अल्लाह की आयतों को झुठला दिया, तो हमने उन्हें उनके पापों के कारण ध्वस्त कर दिया तथा फ़िरऔनियों को डुबो दिया और वह सभी अत्याचारी[ थे। ');
INSERT INTO "al_umari" VALUES(8,55,'8:55','वास्तव में, सबसे बुरे जीव अल्लाह के पास वो हैं, जो काफ़िर हो गये और ईमान नहीं लाये। ये वे[ लोग हैं, जिनसे आपने संधि की, फिर वे प्रत्येक अवसर पर अपना वचन भंग कर देते हैं और (अल्लाह से) नहीं डरते।');
INSERT INTO "al_umari" VALUES(8,56,'8:56','ये वे[ लोग हैं, जिनसे आपने संधि की। फिर वे प्रत्येक अवसर पर अपना वचन भंग कर देते हैं और (अल्लाह से) नहीं डरते। ');
INSERT INTO "al_umari" VALUES(8,57,'8:57','तो यदि ऐसे (वचनभंगी) आपको रणक्षेत्र में मिल जायें, तो उन्हें शिक्षाप्रद दण्ड दें, ताकि जो उनके पीछे हैं, वे शिक्षा ग्रहण करें।');
INSERT INTO "al_umari" VALUES(8,58,'8:58','और यदि आपको किसी जाति से विश्वासघात (संधि भंग करने) का भय हो, तो बराबरी के आधार पर संधि तोड़[ दें। क्योंकि अल्लाह विश्वासघातियों से प्रेम नहीं करता। ');
INSERT INTO "al_umari" VALUES(8,59,'8:59','जो काफ़िर हो गये, वे कदापि ये न समझें कि हमसे आगे हो जायेंगे। निश्चय वे (हमें) विवश नहीं कर सकेंगे।');
INSERT INTO "al_umari" VALUES(8,60,'8:60','तथा तुमसे जितनी हो सके, उनके लिए शक्ति तथा सीमा रक्षा के लिए घोड़े तैयार रखो, जिससे अल्लाह के शत्रुओं तथा अपने शत्रुओं को और इनके सिवा दूसरों को डराओ[, जिन्हें तुम नहीं जानते, उन्हें अल्लाह ही जानता है और अल्लाह की राह में तुम जो भी व्यय (खर्च) करोगे, तुम्हें पूरा मिलेगा और तुमपर अत्याचार नहीं किया जायेगा। ');
INSERT INTO "al_umari" VALUES(8,61,'8:61','और यदि वे (शत्रु) संधि की ओर झुकें, तो आपभी उसके लिए झुक जायें और अल्लाह पर भरोसा करें। निश्चय वह सब कुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(8,62,'8:62','और यदि वे (संधि करके) आपको धोखा देना चाहेंगे, तो अल्लाह आपके लिए काफ़ी है। वही है, जिसने अपनी सहायता तथा ईमान वालों के द्वारा आपको समर्थन दिया है।');
INSERT INTO "al_umari" VALUES(8,63,'8:63','और उनके दिलों को जाड़ दिया और यदि आप धरती में जोकुछ है, सब व्यय (खर्च) कर देते, तो भी उनके दिलों को नहीं जोड़ सकते थे। वास्तव में, अल्लाह प्रभुत्वशाली तत्वज्ञ (निपुन) है।');
INSERT INTO "al_umari" VALUES(8,64,'8:64','हे नबी! आपके लिए तथा आपके ईमान वाले साथियों के लिए अल्लाह काफ़ी है।');
INSERT INTO "al_umari" VALUES(8,65,'8:65','हे नबी! ईमान वालों को युध्द की प्रेरणा दो[। यदि तुममें से बीस धैर्यवान होंगे, तो दो सौ पर विजयी प्राप्त कर लेंगे और यदि तुमसे सौ होंगे, तो उनकाफ़िरों के एक हज़ार पर विजयी प्राप्त कर लेंगे। इसलिए कि वे समझ-बूझ नहीं रखते। ');
INSERT INTO "al_umari" VALUES(8,66,'8:66','अब अल्लाह ने तुम्हारा बोझ हल्का कर दिया और जान लिया कि तुममें कुछ निर्बलता है, तो यदि तुममें से सौ सहनशील हों, तो दो सौ पर विजय प्राप्त कर लेंगे और यदि तुममें से एक हज़ार हों, तो अल्लाह की अनुमति से दो हजार पर प्रभुत्व प्राप्त कर लेंगे और अल्लाह सहनशीलों के साथ है[। ');
INSERT INTO "al_umari" VALUES(8,67,'8:67','किसी नबी के लिए ये उचित न था कि उसके पास बंदी हों, जब तक कि धरती (रणक्षेत्र) में अच्छी तरह़ रक्तपात न कर दे। तुम सांसारिक लाभ चाहते हो और अल्लाह (तुम्हारे लिए) आख़िरत (परलोक) चाहता है और अल्लाह प्रभुत्वशाली तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(8,68,'8:68','यदि इसके बारे में, पहले से अल्लाह का लेख (निर्णय) न होता, तो जो (अर्थ दण्ड) तुमने लिया[ है, उसके लेने में तुम्हें बड़ी यातना दी जाती। ');
INSERT INTO "al_umari" VALUES(8,69,'8:69','तो उस ग़नीमत में से[ खाओ, वह ह़लाल (उचित) स्वच्छ है तथा अल्लाह के आज्ञाकारी रहो। वास्तव में, अल्लाह अति क्षमा करने वाला, दयावान है। ');
INSERT INTO "al_umari" VALUES(8,70,'8:70','हे नबी! जो तुम्हारे हाथों में बंदी हैं, उनसे कह दो कि यदि अल्लाह ने तुम्हारे दिलों में कोई भलाई देखी, तो तुम्हें उससे उत्तम चीज़ (ईमान) प्रदान करेगा, जो (अर्थ दण्ड) तुमसे लिया गया है और तुम्हें क्षमा कर देगा और अल्लाह अति क्षमाशील, दयावान है।');
INSERT INTO "al_umari" VALUES(8,71,'8:71','और यदि वे आपके साथ विश्वासघात करना चाहेंगे, तो इससे पूर्व वे अल्लाह के साथ विश्वासघात कर चुके हैं। इसलिए अल्लाह ने उन्हें (आपके) वश में किया है तथा अल्लाह अति ज्ञानी, उपायजानने वाला है।');
INSERT INTO "al_umari" VALUES(8,72,'8:72','निःसंदेह, जो ईमान लाये तथा हिजरत (परस्थान) कर गये और अल्लाह की राह में अपने धनों और प्राणों से जिहाद किये तथा जिन लोगों ने उन्हें शरण दिया तथा सहायता की, वही एक-दूसरे के सहायक हैं और जो ईमान नहीं लाये और न हिजरत (परस्थान) की, उनसे तुम्हारी सहायता का कोई संबंध नहीं, यहाँ तक कि हिजरत करके आ जायें और यदि वे धर्म के बारें में तुमसे सहायता मांगें, तो तुमपर उनकी सहायता करना आवश्यक है। परन्तु किसी ऐसी जाति के विरुध्द नहीं, जिनके और तुम्हारे बीच संधि हो तथा तुम जो कुछ कर रहे हो, उसे अल्लाह देख रहा है।');
INSERT INTO "al_umari" VALUES(8,73,'8:73','और कीफ़िर एक-दूसरे के समर्थक हैं और यदि तुम ऐसा न करोगे, तो धरती में उपद्रव तथा बड़ा बिगाड़ उत्पन्न हो जायेगा।');
INSERT INTO "al_umari" VALUES(8,74,'8:74','तथ जो ईमान लाये, हिजरत कर गये, अल्लाह की राह में संघर्ष किया और जिन लोगों ने (उन्हें) शरण दी और (उनकी) सहायता की, वही सच्चे ईमान वाले हैं। उन्हीं के लिए क्षमा तथा उन्हीं के लिए उत्तम जीविका है।');
INSERT INTO "al_umari" VALUES(8,75,'8:75','तथा जो लोग इनके पश्चात् ईमान लाये और हिजरत कर गये और तुम्हारे साथ मिलकर संघर्ष किया, वही तुम्हारे अपने हैं और वही परिवारिक समीपवर्ती अल्लाह के लेख (आदेश) में अधिक समीप[ हैं। वास्तव में, अललाह प्रत्येक चीज़ का अति ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(9,1,'9:1','अल्लाह तथा उसके रसूल की ओर से संधि मुक्त होने की घोषणा है, उन मिश्रणवादियों के लिए जिनसे तुमने संधि (समझौता) किया[ था। ');
INSERT INTO "al_umari" VALUES(9,2,'9:2','तो (हे कीफ़िरो!) तुम धरती में चार महीने (स्वतंत्र होकर) फिरो तथा जान लो कि तुम अल्लाह को विवश नहीं कर सकोगे और निश्चय अल्लाह, काफ़िरों को अपमानित करने वाला है।');
INSERT INTO "al_umari" VALUES(9,3,'9:3','तथा अल्लाह और उसके रसूल की ओर से सार्वजनिक सूचना है, महा ह़ज[ के दिन कि अल्लाह मिश्रणवादियों (मुश्रिकों) से अलग है तथा उसका रसूल भी। फिर यदि तुम तौबा (क्षमा याचना) कर लो, तो वह तुम्हारे लिए उत्तम है और यदि तुमने मुँह फेरा, तो जान लो कि तुम अल्लाह को विवश करने वाले नहीं हो और आप उन्हें जो काफ़िर हो गये, दुःखदायी यातना का शुभ समाचार सुना दें। ');
INSERT INTO "al_umari" VALUES(9,4,'9:4','सिवाय उन मुश्रिकों के, जिनसे तुमने संधि की, फिर उन्होंने तुम्हारे साथ कोई कमी नहीं की और न तुम्हारे विरुध्द किसी की सहायता की, तो उनसे उनकी संधि उनकी अवधि तक पूरी करो। निश्चय अल्लाह आज्ञाकारियों से प्रेम करता है।');
INSERT INTO "al_umari" VALUES(9,5,'9:5','अतः जब सम्मानित महीने बीत जायें, तो मिश्रणवादियों (मुश्रिकों) का वध करो, उन्हें जहाँ पाओ और उन्हें पकड़ो और घेरो[ और उनकी घात में रहो। फिर यदि वे तौबा कर लें और नमाज़ की स्थापना करें तथा ज़कात दें, तो उन्हें छोड़ दो। वास्तव में, अल्लाह अति क्षमाशील, दयावान् है। ');
INSERT INTO "al_umari" VALUES(9,6,'9:6','और यदि मुश्रिकों में से कोई तुमसे श्रण माँगे, तो उसे श्रण दो, यहाँ तक कि अल्लाह की बातें सुन ले। फिर उसे पहुँचा दो, उसके शान्ति के स्थान तक। ये इसलिए कि वे ज्ञान नहीं रखते।');
INSERT INTO "al_umari" VALUES(9,7,'9:7','इन मुश्रिकों (मिश्रणवादियों) की कोई संधि अल्लाह और उसके रसूल के पास कैसे हो सकती है? उनके सिवाय जिनसे तुमने सम्मानित मस्जिद (काबा) के पास संधि[ की थी। तो जब तक वे तुम्हारे लिए सीधे रहें, तो तुम भी उनके लिए सीधे रहो। वास्तव में, अल्लाह आज्ञाकारियों से प्रेम करता है। ');
INSERT INTO "al_umari" VALUES(9,8,'9:8','और उनकी संधि कैसे रह सकती है, जबकि वे यदि तुमपर अधिकार पा जायें, तो किसी संधि और किसी वचन का पालन नहीं करेंगे। वे तुम्हें अपने मुखों से प्रसन्न करते हैं, जबकि उनके दिल इन्कार करते हैं और उनमें अधिकांश वचनभंगी हैं।');
INSERT INTO "al_umari" VALUES(9,9,'9:9','उन्होंने अल्लाह की आयतों के बदले तनिक मूल्य ख़रीद लिया[, और (लोगों को) अल्लाह की राह (इस्लाम) से रोक दिया। वास्तव में, वे बड़ा कुकर्म कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(9,10,'9:10','वह किसी ईमान वाले के बारे में किसी संधि और वचन का पालन नहीं करते और वही उल्लंघनकारी हैं।');
INSERT INTO "al_umari" VALUES(9,11,'9:11','तो यदि वे (शिर्क से) तौबा कर लें, नमाज़ की स्थापना करें और ज़कात दें, तो तुम्हारे धर्म-बंधु हैं और हम उन लोगों के लिए आयतों का वर्णन कर हे हैं, जो ज्ञान रखते हों।');
INSERT INTO "al_umari" VALUES(9,12,'9:12','तो यदि वे अपनी शपथें अपना वचन देने के पश्चात तोड़ दें और तुम्हारे धर्म की निंदा करें, तो कुफ़्र के प्रमुखों से युध्द करो। क्योंकि उनकी शपथों का कोई विश्वास नहीं, ताकि वे (अत्याचार से) रुक जायेँ।');
INSERT INTO "al_umari" VALUES(9,13,'9:13','तुम उन लोगों से युध्द क्यों नहीं करते, जिन्होंने अपने वचन भंग कर दिये तथा रसूल को निकालने का निश्चय किया और उन्होंने ही युध्द का आरंभ किया है? क्या तुम उनसे डरते हो? तो अल्लाह अधिक योग्य है कि तुम उससे डरो, यदि तुम ईमान[ वाले हो। ');
INSERT INTO "al_umari" VALUES(9,14,'9:14','उनसे युध्द करो, उन्हें अल्लाह तुम्हारे हाथों दण्ड देगा, उन्हें अपमानित करेगा, उनके विरुध्द तुम्हारी सहायता करेगा और ईमान वालों के दिलों का सब दुःख दूर करेगा।');
INSERT INTO "al_umari" VALUES(9,15,'9:15','और उनके दिलों की जलन दूर कर देगा और जिसपर चाहेगा, दया करेगा और अल्लाह अति ज्ञानी, नीतिज्ञ है।');
INSERT INTO "al_umari" VALUES(9,16,'9:16','क्या तुमने समझा है कि यूँ ही छोड़ दिये जाओगे, जबकि (परीक्षा लेकर) अल्लाह ने उन्हें नहीं जाना है, जिसने तुममें से जिहाद किया तथा अल्लाह और उसके रसूल और ईमान वालों के सिवाय किसी को भेदी मित्र नहीं बनाया? और अल्लाह उससे सूचित है, जो तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(9,17,'9:17','मुश्रिकों (मिश्रणवादियों) के लिए योग्य नहीं है कि वे अल्लाह की मस्जिदों को आबाद करें, जबकि वे स्वयं अपने विरुध कुफ़्र (अधर्म) के साक्षी हैं। इन्हींके कर्म व्यर्थ हो गये और नरक में यही सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(9,18,'9:18','वास्तव में, अल्लाह की मस्जिदों को वही आबाद करता है, जो अल्लाह पर और अन्तिम दिन (प्रलय) पर ईमान लाया, नमाज़ की स्थापना की, ज़कात दी और अल्लाह के सिवा किसी से नहीं डरा। तो आशा है कि वही सीधी राह चलेंगे।');
INSERT INTO "al_umari" VALUES(9,19,'9:19','क्या तुम ह़ाजियों को पानी पिलाने और सम्मानित मस्जिद (काबा) की सेवा को, उसके (ईमान के) बराबर समझते हो, जो अल्लाह और अन्तिम दिन पर ईमान लाया तथा अल्लाह की राह में जिहाद किया? अल्लाह के समीप दोनों बराबर नहीं हैं तथा अल्लाह अत्याचारियों को सुपथ नहीं दिखाता!');
INSERT INTO "al_umari" VALUES(9,20,'9:20','जो लोग ईमान लाये तथा हिजरत कर गये और अल्लाह की राह में अपने धनों और प्राणों से जिहाद किया, अल्लाह के यहाँ उनका बहुत बड़ा पद है और वही सफल होने वाले हैं।');
INSERT INTO "al_umari" VALUES(9,21,'9:21','उन्हें उनका पालनहार शुभ सूचना देता है अपनी दया और प्रसन्नता की तथा ऐसे स्वर्गों की, जिनमें स्थायी सुख के साधन हैं।');
INSERT INTO "al_umari" VALUES(9,22,'9:22','जिनमें वे सदावासी होंगे। वास्तव में, अल्लाह के यहाँ (सत्कर्मियों के लिए) बड़ा प्रतिफल है।');
INSERT INTO "al_umari" VALUES(9,23,'9:23','हे ईमान वालो! अपने बापों और भाईयों को अपना सहायक न बनाओ, यदि वे ईमान की अपेक्षा कुफ़्र से प्रेम करें और तुममें से जो उन्हें सहायक बनाएँगे, तो वही अत्याचारी होंगे।');
INSERT INTO "al_umari" VALUES(9,24,'9:24','हे नबी! कह दो कि यदि तुम्हारे बाप, तुम्हारे पुत्र, तुम्हारे भाई, तुम्हारी पत्नियाँ, तुम्हारे परिवार, तुम्हारा धन जो तुमने कमाया है और जिस व्यपार के मंद हो जाने का तुम्हें भय है तथा वो घर जिनसे मोह रखते हो, तुम्हें अल्लाह तथा उसके रसूल और अल्लाह की राह में जिहाद करने से अधिक प्रिय हैं, तो प्रतीक्षा करो, यहाँ तक कि अल्लाह का निर्णय आ जाये और अल्लाह उल्लंघनकारियों को सुपथ नहीं दिखाता।');
INSERT INTO "al_umari" VALUES(9,25,'9:25','अल्लाह बहुत-से स्थानों पर तथा ह़ुनैन[ के दिन, तुम्हारी सहायता कर चुका है, जब तुम्हें तुम्हारी अधिक्ता पर गर्व था, तो वह तुम्हारे कुछ काम न आई तथा तुमपर धरती अपने विस्तार के होते हुए संकीर्ण (तंग) हो गई, फिर तुम पीठ दिखाकर भागे। ');
INSERT INTO "al_umari" VALUES(9,26,'9:26','फिर अल्लाह ने अपने रसूल और ईमान वालों पर शान्ति उतारी तथा ऐसी सेनायें उतारीं, जिन्हें तुमने नहीं देखा[ और काफ़िरों को यातना दी और यही काफ़िरों का प्रतिकार (बदला) है। ');
INSERT INTO "al_umari" VALUES(9,27,'9:27','फिर अल्लाह इसके पश्चात् जिसे चाहे, क्षमा कर दे[ और अल्लाह अति क्षमाशील, दयावान है। ');
INSERT INTO "al_umari" VALUES(9,28,'9:28','हे ईमान वालो! मुश्रिक (मिश्रणवादी) मलीन हैं। अतः इस वर्ष[ के पश्चात् वे सम्मानित मस्जिद (काबा) के समीप भी न आयें और यदि तुम्हें निर्धनता का भय हो, तो अल्लाह तुम्हें अपनी दया से धनी कर देगा, यदि वह चाहे। वास्तव में, अल्लाह सर्वज्ञ, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(9,29,'9:29','(हे ईमान वालो!) उनसे युध्द करो, जो न तो अल्लाह (सत्य) पर ईमान लाते हैं और न अन्तिम दिन (प्रलय) पर और न जिसे, अल्लाह और उसके रसूल ने ह़राम (वर्जित) किया है, उसे ह़राम (वर्जित) समझते हैं, न सत्धर्म को अपना धर्म बनाते हैं, उनमें से जो पुस्तक दिये गये हैं, यहाँ तक कि वे अपने हाथ से जिज़या[ दें और वे अपमानित होकर रहें। ');
INSERT INTO "al_umari" VALUES(9,30,'9:30','तथा यहूद ने कहा कि उज़ैर अल्लाह का पुत्र है और नसारा (ईसाईयों) ने कहा कि मसीह़ अल्लाह का पुत्र है। ये उनके अपने मुँह की बातें हैं। वे उनके जैसी बातें कर रहे हैं, जो इनसे पहले काफ़िर हो गये। उनपर अल्लाह की मार! वे कहाँ बहके जा रहे हैं?');
INSERT INTO "al_umari" VALUES(9,31,'9:31','उन्होंने अपने विद्वानों और धर्माचारियों (संतों) को अल्लाह के सिवा पूज्य[ बना लिया तथा मर्यम के पुत्र मसीह़ को, जबकि उन्हें जो आदेश दिया गया था, वो इसके सिवा कुछ न था कि एक अल्लाह की इबादत (वंदना) करें। कोई पूज्य नहीं है, परन्तु वही। वह उससे पवित्र है, जिसे उसका साझी बना रहे हैं। ');
INSERT INTO "al_umari" VALUES(9,32,'9:32','वे चाहते हैं कि अल्लाह के प्रकाश को अपनी फूकों से बुझा[ दें और अल्लाह अपने प्रकाश को पूरा किये बिना नहीं रहेगा, यद्यपि काफ़िरों को बुरा लगे। ');
INSERT INTO "al_umari" VALUES(9,33,'9:33','उसीने अपने रसूल[ को मार्गदर्शन तथा सत्धर्म (इस्लाम) के साथ भेजा है, ताकि उसे प्रत्येक धर्म पर प्रभुत्व प्रदान कर दे , यद्यपि मिश्रणवादियों को बुरा लगे।');
INSERT INTO "al_umari" VALUES(9,34,'9:34','हे ईमान वालो! बहुत-से (अह्ले किताब के) विद्वान तथा धर्माचारी (संत) लोगों का धन अवैध खाते हैं और (उन्हें) अल्लाह की राह से रोकते हैं तथा जो सोना-चाँदी एकत्र करके रखते हैं और उसे अल्लाह की राह में दान नहीं करते, उन्हें दुःखदायी यातना की शुभ सूचना सुना दें।');
INSERT INTO "al_umari" VALUES(9,35,'9:35','जिस (प्रलय के) दिन उसे नरक की अग्नि में तपाया जायेगा, फिर उससे उनके माथों, पार्शवों (पहलू) और पीठों को दागा जायेगा ( और कहा जायेगाः) यही है, जिसे तुम एकत्र कर रहे थे, तो (अब) अपने संचित किये धनों का स्वाद चखो।');
INSERT INTO "al_umari" VALUES(9,36,'9:36','वास्तव में, महीनों की संख्या बारह महीने हैं, अल्लाह के लेख में, जिस दिन से उसने आकाशों तथा धरती की रचना की है। उनमें से चार ह़राम (सम्मानित)[ महीने हैं। यही सीधा धर्म है। अतः अपने प्राणों पर अत्याचार न करो तथा मिश्रणवादियों से सब मिलकर युध्द करो।, जैसे वे तुमसे मिलकर युध्द करते हैं और विश्वास रखो कि अल्लाह आज्ञाकीरियों के साथ है।');
INSERT INTO "al_umari" VALUES(9,37,'9:37','नसी[ (महीनों को आगे-पीछे करना) कुफ़्र (अधर्म) में अधिक्ता है। इससे काफ़िर कुफथ किये जाते हैं। एक ही महीने को एक वर्ष ह़लाल (वैध) कर देते हैं तथा उसी को दूसरे वर्ष ह़राम (अवैध) कर देते हैं। ताकि अल्लाह ने सम्मानित महीनों की जो गिनती निश्चित कर दी है, उसे अपनी गिनती के अनुसार करके, अवैध महीनों को वैध कर लें। उनके लिए उनके कुकर्म सुन्दर बना दिये गये हैं और अल्लाह काफ़िरों को सुपथ नहीं दर्शाता। ');
INSERT INTO "al_umari" VALUES(9,38,'9:38','हे ईमान वालो! तुम्हें क्या हो गया है कि जब तुमसे कहा जाये कि अल्लाह की राह में निकलो, तो धरती के बोझ बन जाते हो, क्या तुम आख़िरत (परलोक) की अपेक्षा सांसारिक जीवन से प्रसन्न हो गये हो? जबकि परलोक की अपेक्षा सांसारिक जीवन के लाभ बहुत थोड़े हैं[। ');
INSERT INTO "al_umari" VALUES(9,39,'9:39','यदि तुम नहीं निकलोगे, तो तुम्हें अल्लाह दुःखदायी यातना देगा, तथा तुम्हारे सिवाय दूसरे लोगों को लायेगा। और तुम उसे कोई हानि नहीं पहुँचा सकोगे। और अल्लाह जो चाहे कर सकता है।');
INSERT INTO "al_umari" VALUES(9,40,'9:40','यदि तुम उस (नबी) की सहायता नहीं करोगे, तो अल्लाह ने उसकी सहायता उस समय[ की है, जब काफ़िरों ने उसे (मक्का से) निकाल दिया। वह दो में दूसरे थे। जब दोनों गुफा में थे, जब वह अपने साथी से कह रहे थेः उदासीन न हो, निश्चय अल्लाह हमारे साथ है । तो अल्लाह ने अपनी ओर से शान्ति उतार दी और आपको ऐसी सेना से समर्थन दिया, जिसे तुमने नहीं देखा और काफ़िरों की बात नीची कर दी और अल्लाह की बात ही ऊँची रही और अल्लाह प्रभुत्वशाली तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(9,41,'9:41','हल्के[ होकर और बोझल (जैसे हो) निकल पड़ो। और अपने धनों तथा प्राणों से अल्लाह की राह में जिहाद करो। यही तुम्हारे लिए उत्तम है, यदि तुम ज्ञान रखते हो। ');
INSERT INTO "al_umari" VALUES(9,42,'9:42','(हे नबी!) यदि लाभ समीप और यात्रा सरल होती, तो ये (मुनाफ़िक़) अवश्य आपके साथ हो जाते। परन्तु उन्हें मार्ग दूर लगा और (अब) अल्लाह की शपथ लेंगे कि यदि हम निकल सकते, तो अवश्य तुम्हारे साथ निकल पड़ते, वे अपना विनाश स्वयं कर रहे हैं और अल्लाह जानता है कि वे वास्तव में झूठे हैं।');
INSERT INTO "al_umari" VALUES(9,43,'9:43','(हे नबी!) अल्लाह आपको क्षमा करे! आपने उन्हें अनुमति क्यों दे दी? यहाँ तक कि आपके लिए जो सच्चे हैं, उजागर हो जाते और झूठों को जान लेते?');
INSERT INTO "al_umari" VALUES(9,44,'9:44','आपसे (पीछे रह जाने की) अनुमति वह नहीं माँग रहे हैं, जो अल्लाह तथा अन्तिम दिन (प्रलय) पर ईमान रखते हों कि अपने धनों तथा प्राणों से जिहाद करेंगे और अल्लाह आज्ञाकारियों को भलि-भाँति जानता है।');
INSERT INTO "al_umari" VALUES(9,45,'9:45','आपसे अनुमति वही माँग रहे हैं, जो अल्लाह तथा अन्तिम दिवस (परलोक) पर ईमान नहीं रखते और अपने संदेह में पड़े हुए हैं।');
INSERT INTO "al_umari" VALUES(9,46,'9:46','यदि वे निकलना चाहते, तो अवश्य उसके लिए कुछ तैयारी करते। परन्तु अल्लाह को उनका जाना अप्रिय था, अतः उन्हें आलसी बना दिया तथा कह दिया गया कि बैठने वालों के साथ बैठे रहो।');
INSERT INTO "al_umari" VALUES(9,47,'9:47','और यदि वे तुममें निकलते, तो तुममें बिगाड़ ही अधिक करते और तुम्हारे बीच उपद्रव के लिए दौड़ धूप करते और तुममें वह भी हैं, जो उनकी बातों पर ध्यान देते हैं और अल्लाह अत्याचारियों को भली-भाँति जानता है।');
INSERT INTO "al_umari" VALUES(9,48,'9:48','(हे नबी!) वे इससे पहले भी उपद्रव का प्रयास कर चुके हैं तथा आपके लिए बातों में हेर-फेर कर चुके हैं। यहाँ तक कि सत्य आ गया और अल्लाह का आदेश प्रभुत्वशाली हो गया और ये बात उन्हें अप्रिय है।');
INSERT INTO "al_umari" VALUES(9,49,'9:49','उनमें से कोई ऐसा भी है, जो कहता है कि आप मुझे अनुमति दे दें और परीक्षा में न डालें। सुन लो! परीक्षा में तो ये पहले ही से पड़े हुए हैं और वास्तव में, नरक काफ़िरों को घेरी हुई है।');
INSERT INTO "al_umari" VALUES(9,50,'9:50','(हे नबी!) यदि आपका कुछ भला होता है, तो उन (द्विधावादियों) को बुरा लगता है और यदि आपपर कोई आपदा आ पड़े, तो कहते हैं: हमने पहले ही अपनी सावधानी बरत ली थी और प्रसन्न होकर फिर जाते हैं।');
INSERT INTO "al_umari" VALUES(9,51,'9:51','आप कह दें: हमें कदापि कोई आपदा नहीं पहुँचेगी, परन्तु वही जो अल्लाह ने हमारे भाग्य में लिख दी है। वही हमारा सहायक है और अल्लाह ही पर ईमान वालों को निर्भर रहना चाहिए।');
INSERT INTO "al_umari" VALUES(9,52,'9:52','आप उनसे कह दें कि तुम हमारे बारे में जिसकी प्रतीक्षा कर रहे हो, वह यही है कि हमें दो[ भलाईयों में से एक मिल जाये और हम तुम्हारे बारे में इसकी प्रतीक्षा कर रहे हैं कि अल्लाह तुम्हें अपने पास से यातना देता है या हमारे हाथों से। तो तुम प्रतीक्षा करो। हम भी तुम्हारे साथ प्रतीक्षा कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(9,53,'9:53','आप (मुनाफ़िक़ों से) कह दें कि तुम स्वेच्छा दान करो अथवा अनिच्छा, तुमसे कदापि स्वीकार नहीं किया जायेगा। क्योंकि तुम अवज्ञाकारी हो।');
INSERT INTO "al_umari" VALUES(9,54,'9:54','और उनके दानों के स्वीकार न किये जाने का कारण, इसके सिवाय कुछ नहीं है कि उन्होंने अल्लाह और उसके रसूल के साथ कुफ़्र किया है और वे नमाज़ के लिए आलसी होकर आते हैं तथा दान भी करते हैं, तो अनिच्छा करते हैं।');
INSERT INTO "al_umari" VALUES(9,55,'9:55','अतः आपको उनके धन तथा उनकी संतान चकित न करे। अल्लाह तो ये चाहता है कि उन्हें इनके द्वारा सांसारिक जीवन में यातना दे और उनके प्राण इस दशा में निकलें कि वे काफ़िर हों।');
INSERT INTO "al_umari" VALUES(9,56,'9:56','वे (मुनाफ़िक़) अल्लाह की शपथ लेकर कहते हैं कि वे तुम में से हैं, जबकि वे तुममें से नहीं हैं, परन्तु भयभीत लोग हैं।');
INSERT INTO "al_umari" VALUES(9,57,'9:57','यदि वे कोई शरणगार, गुफा या प्रवेश स्थान पा जायेँ, तो उसकी ओर भागते हुए फिर जायेंगे।');
INSERT INTO "al_umari" VALUES(9,58,'9:58','(हे नबी!) उन (मुनाफ़िक़ों) में से कुछ ज़कात के वितरण में आपपर आक्षेप करते हैं। फिर यदि उन्हें उसमें से कुछ दे दिया जाये, तो प्रसन्न हो जाते हैं और यदि न दिया जाये, तो तुरन्त अप्रसन्न हो जाते हैं।');
INSERT INTO "al_umari" VALUES(9,59,'9:59','और क्या ही अच्छा होता, यदि वे उससे प्रसन्न हो जाते, जो उन्हें अल्लाह और उसके रसूल ने दिया है तथा कहते कि हमारे लिए अल्लाह काफ़ी है। हमें अपने अनुग्रह से (बहुत कुछ) प्रदान करेगा तथा उसके रसूल भी, हम तो उसी की ओर रूचि रखते हैं।');
INSERT INTO "al_umari" VALUES(9,60,'9:60','ज़कात (देय, दान) केवल फ़क़ीरों[, मिस्कीनों, कार्य-कर्ताओं तथा उनके लिए जिनके दिलों को जोड़ा जा रहा है और दास मुक्ति, ऋणियों (की सहायता), अल्लाह की राह में तथा यात्रियों के लिए है। अल्लाह की ओरसे अनिवार्य (देय) है और अल्लाह सर्वज्ञ, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(9,61,'9:61','तथा उन (मुनाफ़िक़ों) में से कुछ नबी को दुःख देते हैं और कहते हैं कि वह बड़े सुनवा[ हैं। आप कह दें कि वह तुम्हारी भलाई के लिए ऐसे हैं। वह अल्लाह पर ईमान रखते हैं और ईमान वालों की बात का विश्वास करते है और उनके लिए दया हैं, जो तुममें से ईमान लाये हैं और जो अल्लाह के रसूल को दुःख देते हैं, उनके लिए दुःखदायी यातना है। ');
INSERT INTO "al_umari" VALUES(9,62,'9:62','वे तुम्हारे समक्ष अल्लाह की शपथ लेते हैं, ताकि तुम्हें प्रसन्न कर लें। जबकि अल्लाह और उसके रसूल इसके अधिक योग्य हैं कि उन्हें प्रसन्न करें, यदि वे वास्तव में, ईमान वाले हैं।');
INSERT INTO "al_umari" VALUES(9,63,'9:63','क्या वे नहीं जानते कि जो अल्लाह और उसके रसूल का विरोध करता है, उसके लिए नरक की अग्नि है, जिसमें वे सदावासी होंगे और ये बहुत बड़ा अपमान है?');
INSERT INTO "al_umari" VALUES(9,64,'9:64','मुनाफ़िक़ (द्विधावादी) इससे डरते हैं कि उन[ पर कोई ऐसी सूरह न उतार दी जाये, जो उन्हें इनके दिलों की दशा बता दे। आप कह दें कि हँसी उड़ा लो। निश्चय अल्लाह उसे खोलकर रहेगा, जिससे तुम डर रहे हो। ');
INSERT INTO "al_umari" VALUES(9,65,'9:65','और यदि आप उनसे प्रश्न करें, तो वे अवश्य कह देंगे कि हमतो यूँ ही बातें तथा उपहास कर रहे थे। आप कह दें कि क्या अल्लाह, उसकी आयतों और उसके रसूल के ही साथ उपहास कर रहे थे? ');
INSERT INTO "al_umari" VALUES(9,66,'9:66','तुम बहाने न बनाओ, तुमने अपने ईमान के पश्चात् कुफ़्र किया है। यदि हम तुम्हारे एक गिरोह को क्षमा कर दें, तो भी एक गिरोह को अवश्य यातना देंगे। क्योंकि वही अपराधी हैं।');
INSERT INTO "al_umari" VALUES(9,67,'9:67','मुनाफ़िक़ पुरुष तथा स्त्रियाँ, सब एक-दूसरे जैसे हैं। वे बुराई का आदेश देते तथा भलाई से रोकते हैं और अपने हाथ बंद किये रहते[ हैं। वे अल्लाह को भूल गये, तो अल्लाह ने भी उन्हें भुला दिया। वास्तव में, मुनाफ़िक़ ही भ्रषटाचारी हैं।');
INSERT INTO "al_umari" VALUES(9,68,'9:68','अल्लाह ने मुनाफ़िक़ पुरुषों तथा स्त्रियों और काफ़िरों को नरक की अग्नि का वचन दिया है, जिसमें सदावासी होंगे। वही उन्हें प्रयाप्त है और अल्लाह ने उन्हें धिक्कार दिया है और उन्हीं के लिए स्थायी यातना है।');
INSERT INTO "al_umari" VALUES(9,69,'9:69','इनकी दशा वही हुई, जो इनसे पहले के लोगों की हुई। वे बल में इनसे कड़े और धन तथा संतान में इनसे अधिक थे। तो उन्होंने अपने (सांसारिक) भाग का आनंद लिया, अतः तुमभी अपने भाग का आनंद लो, जैसे तुमसे पूर्व के लोगों ने आनंद लिया और तुमभी उलझते हो, जैसे वे उलझते रहे, उन्हीं के कर्म लोक तथा परलोक में व्यर्थ गये और वही क्षति में हैं।');
INSERT INTO "al_umari" VALUES(9,70,'9:70','क्या इन्हें उनके समाचार नहीं पहुँचे, जो इनसे पहले थे; नूह़, आद, समूद तथा इब्राहीम की जाति के और मद्यन[ के वासियों और उन बस्तियों के, जो पलट दी गईं? उनके पास उनके रसूल खुली निशानियाँ लाये और ऐसा नहीं हो सकता था कि अल्लाह उनपर अत्याचार करता, परन्तु वे स्वयं अपने ऊपर अत्याचार कर रहे थे।');
INSERT INTO "al_umari" VALUES(9,71,'9:71','तथा ईमान वाले पुरुष और स्त्रियाँ एक-दूसरे के सहायक हैं। वे भलाई का आदेश देते तथा बुराई से रोकते हैं, नामज़ की स्थापना करते तथा ज़कात देते हैं और अल्लाह तथा उसके रसूल की आज्ञा का पालन करते हैं। इन्हीं पर अल्लाह दया करेगा, वास्तव में, अल्लाह प्रभुत्वशाली, त्तवज्ञ है।');
INSERT INTO "al_umari" VALUES(9,72,'9:72','अल्लाह ने ईमान वाले पुरुषों तथा ईमान वाली स्त्रियों को ऐसे स्वर्गों का वचन दिया है, जिनमें नहरें प्रवाहित होंगी, वे उनमें सदावासी होंगे और स्थायी स्वर्गों में, पवित्र आवासों का। और अल्लाह की प्रसन्नता इनसबसे बड़ा प्रदान होगी, यही बहुत बड़ी सफलता है।');
INSERT INTO "al_umari" VALUES(9,73,'9:73','हे नबी! काफ़िरों और मुनाफ़िक़ों से जिहाद करो और उनपर सख़्ती करो, उनका आवास नरक है और वह बहुत बुरा स्थान है।');
INSERT INTO "al_umari" VALUES(9,74,'9:74','वे अल्लाह की शपथ लेते हैं कि उन्होंने ये[ बात नहीं कही। जबकि वास्तव में, उन्होंने कुफ़्र की बात कही है और इस्लाम ले आने के पश्चात् काफ़िर हो गये हैं और उन्होंने ऐसी बात का निश्चय किया था, जो वे कर नहीं सके और उन्हें यही बात बुरी लगी कि अल्लाह और उसके रसूल ने उन्हें अपने अनुग्रह से धनी कर दिया। अब यदि वे क्षमा याचना कर लें, तो उनके लिए उत्तम है और यदि विमुःख हों, तो अल्लाह उन्हें दुःखदायी यातना लोक तथा प्रलोक में देगा और उनका धरती में कोई संरक्षक और सहायक न होगा।');
INSERT INTO "al_umari" VALUES(9,75,'9:75','उनमें से कुछ ने अल्लाह को वचन दिया था कि यदि वे अपनी दया से हमें (धन-धान्य) प्रदान करेगा, तो हम अवश्य दान करेंगे और सुकर्मियों में हो जायेंगे।');
INSERT INTO "al_umari" VALUES(9,76,'9:76','फिर जब अल्लाह ने अपनी दया से उन्हें प्रदान कर दिया, तो उससे कंजूसी कर गये और वचन से विमुख होकर फिर गये।');
INSERT INTO "al_umari" VALUES(9,77,'9:77','तो इसका परिणाम ये हुआ कि उनके दिलों में द्विधा का रोग, उस दिन तक के लिए हो गया, जब ये अल्लाह से मिलेंगे। क्योंकि उन्होंने उस वचन को भंग कर दिया, जो अल्लाह से किया था और इसलिए कि वे झूठ बोलते रहे।');
INSERT INTO "al_umari" VALUES(9,78,'9:78','क्या उन्हें इसका ज्ञान नहीं हुआ कि अल्लाह उनके भेद की बातें तथा सुनगुन को भी जानता है और वह सभी भेदों का अति ज्ञानी है?');
INSERT INTO "al_umari" VALUES(9,79,'9:79','जिनकी दशा ये है कि वे ईमान वालों में से स्वेच्छा दान करने वालों पर, दानों के विषय में आक्षेप करते हैं तथा उन्हें जो अपने परिश्रम ही से कुछ पाते (और दान करते हैं), ये (मुनाफ़िक़) उनसे उपहास करते हैं, अल्लाह उनसे उपहास करता[ है और उन्हीं के लिए दुःखदायी यातना है। ');
INSERT INTO "al_umari" VALUES(9,80,'9:80','(हे नबी!) आप उनके लिए क्षमा याचना करें अथवा न करें, यदि आप उनके लिए सत्तर बार भी क्षमा याचना करें, तो भी अल्लाह उन्हें क्षमा नहीं करेगा, इस कारण कि उन्होंने अल्लाह और उसके रसूल के साथ कुफ़्र किया और अल्लाह अवज्ञाकीरियों को मार्गदर्शन नहीं देता।');
INSERT INTO "al_umari" VALUES(9,81,'9:81','वे प्रसन्न[ हुए, जो पीछे कर दिये गये, अपने बैठे रहने के कारण अल्लाह के रसूल के पीछे और उन्हें बुरा लगा कि जिहाद करें अपने धनों तथा प्राणों से अल्लाह की राह में और उन्होंने कहा कि गर्मी में न निकलो। आप कह दें कि नरक की अग्नि गर्मी में इससे भीषण है, यदि वे समझते (तो ऐसी बात न करते)। ');
INSERT INTO "al_umari" VALUES(9,82,'9:82','तो उन्हें चाहिए कि हँसें कम और रोयें अधिक। जो कुछ वे कर रहे हैं, उसका बदला यही है।');
INSERT INTO "al_umari" VALUES(9,83,'9:83','तो (हे नबी!) यदि आपको अल्लाह इन (द्विधावादियों) के किसी गिरोह के पास (तबूक से) वापस लाये और वे आपसे (किसी दूसरे युध्द में) निकलने की अनुमति माँगें, तो आप कह दें कि तुम मेरे साथ कभी न निकलोगे और न मेरे साथ किसी शत्रु से युध्द कर सकोगे। तुम प्रथम बार बैठे रहने पर प्रसन्न थे, तो अबभी पीछे रहने वालों के साथ बैठे रहो।');
INSERT INTO "al_umari" VALUES(9,84,'9:84','(हे नबी!) आप उनमें से कोई मर जाये, तो उसके जनाज़े की नमाज़ कभी न पढ़ें और न उसकी समाधि (क़ब्र) पर खड़े हों। क्योंकि उन्होंने अल्लाह और उसके रसूल के साथ कुफ़्र किया है और अवज्ञाकारी रहते हुए मरे[ हैं। ');
INSERT INTO "al_umari" VALUES(9,85,'9:85','आपको उनके धन तथा उनकी संतान चकित न करे, अल्लाह तो चाहता है कि इनके द्वारा उन्हें संसार में यातना दे और उनके प्राण इस दशा में निकलें कि वे काफ़िर हों।');
INSERT INTO "al_umari" VALUES(9,86,'9:86','तथा जब कोई सूरह उतारी गयी कि अल्लाह पर ईमान लाओ तथा उसके रसूल के साथ जिहाद करो, तो आपसे उन (मुनाफ़िक़ों) में से समाई वालों ने अनुमति ली और कहा कि आप हमें छोड़ दें। हम बैठने वालों के साथ रहेंगे।');
INSERT INTO "al_umari" VALUES(9,87,'9:87','तथा प्रसन्न हो गये कि स्त्रियों के साथ रहें और उनके दिलों पर मुहर लगा दी गई। अतः वे नहीं समझते।');
INSERT INTO "al_umari" VALUES(9,88,'9:88','परन्तु रसूल ने और जो आपके साथ ईमान लाये, अपने धनों और प्राणों से जिहाद किया और उन्हीं के लिए भलाईयाँ हैं और वही सफल होने वाले हैं।');
INSERT INTO "al_umari" VALUES(9,89,'9:89','अल्लाह ने उनके लिए ऐसे स्वर्ग तैयार कर दिये हैं, जिनमें नहरें प्रवाहित हैं। वे उनमें सदावासी होंगे और यही बड़ी सफता है।');
INSERT INTO "al_umari" VALUES(9,90,'9:90','और देहातियों में से कुछ बहाना करने वाले आये, ताकि आप उन्हें अनुमति दें तथा वह बैठे रह गये, जिन्होंने अल्लाह और उसके रसूल से झूठ बोला। तो इनमें से काफ़िरों को दुःखदायी यातना पहुँचेगी।');
INSERT INTO "al_umari" VALUES(9,91,'9:91','निर्बलों तथा रोगियों और उनपर, जो इतना नहीं पाते कि (तैयारी के लिए) व्यय कर सकें, कोई दोष नहीं, जब अल्लाह और उसके रसूल के भक्त हों, तो उनपर (दोषारोपण) की कोई राह नहीं।');
INSERT INTO "al_umari" VALUES(9,92,'9:92','और उनपर, जो आपके पास जब आयें कि आप उनके लिए सवारी की व्यवस्था कर दें और आप कहें कि मेरे पास इतना नहीं कि तुम्हारे लिए सवारी की व्यवस्था करूँ, तो वे इस दशा में वापस हुए कि शोक के कारण, उनकी आँखें आँसू बहा रही[ थीं। ');
INSERT INTO "al_umari" VALUES(9,93,'9:93','दोष केवल उनपर है, जो आपसे अनुमति माँगते हैं, जबकि वे धनी हैं और वे इससे प्रसन्न हो गये कि स्त्रियों के साथ रह जायेंगे और अल्लाह ने उनके दिलों पर मुहर लगा दी, इसलिए, वे कुछ नहीं जानते।');
INSERT INTO "al_umari" VALUES(9,94,'9:94','वे तुमसे बहाने बनायेंगे, जब तुम उनके पास (तबूक से) वापस आओगे। आप कह दें कि बहाने न बनाओ, हम तुम्हारा विश्वास नहीं करेंगे। अल्लाह ने हमें तुम्हारी दशा बता दी है तथा भविष्य में भी अल्लाह और उसके रसूल तुम्हारा कर्म देखेंगे। फिर तुम परोक्ष और प्रत्यक्ष के ज्ञानी (अल्लाह) की ओर फेरे जाओगे। फिर वह तुम्हें बता देगा कि तुम क्या कर रहे थे।');
INSERT INTO "al_umari" VALUES(9,95,'9:95','वे तुमसे अल्लाह की शपथ लेंगे, जब तुम उनकी ओर वापस आओगे, ताकि तुम उनसे विमुख हो जाओ। तो तुम उनसे विमुख हो जाओ। वास्तव में, वे मलीन हैं और उनका आवास नरक है, उसके बदले, जो वे करते रहे।');
INSERT INTO "al_umari" VALUES(9,96,'9:96','वे तुम्हारे लिए शपथ लेंगे, ताकि तुम उनसे प्रसन्न हो जाओ, तो यदि तुम उनसे प्रसन्न हो गये, तबभी अल्लाह उल्लंघनकारी लोगों से प्रसन्न नहीं होगा।');
INSERT INTO "al_umari" VALUES(9,97,'9:97','देहाती[ अविश्वास तथा द्विधा में अधिक कड़े और अधिक योग्य हैं कि उस (धर्म) की सीमाओं को न जानें, जिसे अल्लाह ने उतारा है और अल्लाह सर्वज्ञ, तत्वज्ञ है। ');
INSERT INTO "al_umari" VALUES(9,98,'9:98','देहातियों में कुछ ऐसे भी हैं, जो अपने दिये हुए दान को अर्थदण्ड समझते हैं और तुमपर कालचक्र की प्रतीक्षा करते हैं। उन्हींपर कालकुचक्र आ पड़ा है और अल्लाह सबकुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(9,99,'9:99','और देहातियों में कुछ ऐसे भी हैं, जो अल्लाह तथा अन्तिम दिन (प्रलय) पर ईमान (विश्वास) रखते हैं और अपने दिये हुए दान को अल्लाह की समीप्ता तथा रसूल के आशीर्वादों का साधन समझते हैं। सुन लो! ये वास्तव में, उनके लिए समीप्य का साधन है। शीघ्र ही अल्लाह उन्हें अपनी दया में प्रवेश देगा, वास्तव में, अल्लाह अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(9,100,'9:100','तथा प्रथम अग्रसर मुहाजिरीन[ और अन्सार और जिन लोगों ने सुकर्म के साथ उनका अनुसरण किया, अल्लाह उनसे प्रसन्न हो गया और वे उससे प्रसन्न हो गये तथा उसने उनके लिए ऐसे स्वर्ग तैयार किये हैं, जिनमें नहरें प्रवाहित हैं। वे उनमें सदावासी होंगे, यही बड़ी सफलता है। ');
INSERT INTO "al_umari" VALUES(9,101,'9:101','और जो तुम्हारे आस-पास ग्रामीन हैं, उनमें से कुछ मुनाफ़िक़ (द्विधावादी) हैं और कुछ मदीने में हैं। जो (अपने) निफ़ाक़ में अभ्यस्त (निपुण) हैं। आप उन्हें नहीं जानते, उन्हें हम जानते हैं। हम उन्हें दो बार[ यातना देंगे। फिर घोर यातना की ओर फेर दिये जायेंगे। ');
INSERT INTO "al_umari" VALUES(9,102,'9:102','और कुछ दूसरे भी हैं, जिन्होंने अपने पापों को स्वीकार कर लिया है। उन्होंने कुछ सुकर्म और कुछ दूसरे कुकर्म को मिश्रित कर दिया है। आशा है कि अल्लाह उन्हें क्षमा कर देगा। वास्तव में, अल्लाह अति क्षमी, दयावान् है।');
INSERT INTO "al_umari" VALUES(9,103,'9:103','(हे नबी!) आप उनके धनों से दान लें और उसके द्वारा उन (के धनों) को पवित्र और उन (के मनों) को शुध्द करें और उन्हें आशीर्वाद दें। वास्तव में, आपका आशीर्वाद उनके लिए संतोष का कारण है और अल्लाह सब सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(9,104,'9:104','क्या वे नहीं जानते कि अल्लाह ही अपने भक्तों की क्षमा स्वीकार करता तथा (उनके) दानों को अंगीकार करता है और वास्तव में, अल्लाह अति क्षमी दयावान् है?');
INSERT INTO "al_umari" VALUES(9,105,'9:105','और (हे नबी!) उनसे कहो कि कर्म करते जाओ। अल्लाह, उसके रसूल और ईमान वाले तुम्हारा कर्म देखेंगे। (फिर) उस (अल्लाह) की ओर फेरे जाओगे, जो परोक्ष तथा प्रत्यक्ष (छुपे तथा खुले) का ज्ञानी है। तो वह तुम्हें बता देगा, जो तुम करते रहे।');
INSERT INTO "al_umari" VALUES(9,106,'9:106','और (इनके सिवाय) कुछ दूसरे भी हैं, जो अल्लाह के आदेश के लिए विलंबित[ हैं। वह उन्हें दण्ड दे अथवा उन्हें क्षमा कर दे, अल्लाह सर्वज्ञ, तत्वज्ञ है। ');
INSERT INTO "al_umari" VALUES(9,107,'9:107','तथा (द्विधावादियों में) वे भी हैं, जिन्होंने एक मस्जिद[ बनाई; इसलिए कि (इस्लाम को) हानि पहुँचायें, कुफ़्र करें, ईमान वालों में विभेद उतपन्न करें तथा उसका घात-स्थल बनाने के लिए, जो इससे पूर्व अल्लाह और उसके रसूल से युध्द कर चुका है और वे अवश्य शपथ लेंगे कि हमारा संकल्प भलाई के सिवा और कुछ न था तथा अल्लाह साक्ष्य देता है कि वे निश्चय मिथ्यावादी हैं।');
INSERT INTO "al_umari" VALUES(9,108,'9:108','(हे नबी!) आप उसमें कभी खड़े न हों। वास्तव में, वो मस्जिद[ जिसका शिलान्यास प्रथम दिन से अल्लाह के भय पर किया गया है, वो अधिक योग्य है कि आप उसमें (नमाज़ के लिए) खड़े हों। उसमें ऐसे लोग हैं, जो स्वच्छता से प्रेम करते हैं और अल्लाह स्वच्छ रहने वालों से प्रेम करता है।');
INSERT INTO "al_umari" VALUES(9,109,'9:109','तो क्या जिसने अपने निर्माण का शिलान्यास अल्लाह के भय और प्रसन्नता के आधार पर किया हो, वह उत्तम है अथवा जिसने उसका शिलान्यास एक खाई के गिरते हुए किनारे पर किया हो, जो उसके साथ नरक की अग्नि में गिर पड़ा? और अल्लाह अत्याचारियों को मार्गदर्शन नहीं देता।');
INSERT INTO "al_umari" VALUES(9,110,'9:110','ये निर्माण जो उन्होंने किया, बराबर उनके दिलों में एक संदेह बना रहेगा, परन्तु ये कि उनके दिलों को खण्ड-खण्ड कर दिया जाये और अल्लाह सर्वज्ञ, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(9,111,'9:111','निःसंदेह अल्लाह ने ईमान वालों के प्राणों तथा उनके धनों को इसके बदले ख़रीद लिया है कि उनके लिए स्वर्ग है। वे अल्लाह की राह में युध्द करते हैं, वे मारते तथा मरते हैं। ये अल्लाह पर सत्य वचन है, तौरात, इंजील और क़ुर्आन में और अल्लाह से बढ़कर अपना वचन पूरा करने वाला कौन हो सकता है? अतः, अपने इस सौदे पर प्रसन्न हो जाओ, जो तुमने किया और यही बड़ी सफलता है।');
INSERT INTO "al_umari" VALUES(9,112,'9:112','जो क्षमा याचना करने, वंदना करने तथा अल्लाह की स्तुति करने वाले, रोज़ा रखने तथा रुकूअ और सज्दा करने वाले, भलाई का आदेश देने और बुराई से रोकने वाले तथा अल्लाह की सीमाओं की रक्षा करने वाले हैं और (हे नबी!) आप ऐसे ईमान वालों को शुभ सूचना सुना दें।');
INSERT INTO "al_umari" VALUES(9,113,'9:113','किसी नबी तथा[ उनके लिए जो ईमान लाये हों, योग्य नहीं है कि मुश्रिकों (मिश्रणवादियों) के लिए क्षमा की प्रार्थना करें। यद्यपि वे उनके समीपवर्ती हों, जब ये उजागर हो गया कि वास्तव में, वह नारकी हैं।');
INSERT INTO "al_umari" VALUES(9,114,'9:114','और इब्राहीम का अपने बाप के लिए क्षमा की प्रार्थना करना, केवल इसलिए हुआ की उसने उसे, इसका वचन दिया[ था और जब उसके लिए उजागर हो गया कि वह अल्लाह का शत्रु है, तो उससे विरक्त हो गया। वास्तव में, इब्राहीम बड़ा कोमल हृदय, सहनशील था। ');
INSERT INTO "al_umari" VALUES(9,115,'9:115','अल्लाह ऐसा नहीं है कि किसी जाति को, मार्गदर्शन देने के पश्चात् कुपथ कर दे, जब तक उनके लिए जिससे बचना चाहिए, उसे उजागर न कर दे। वास्तव में, अल्लाह प्रत्येक वस्तु को भली-भाँति जानने वाला है।');
INSERT INTO "al_umari" VALUES(9,116,'9:116','वास्तव में, अल्लाह ही है, जिसके अधिकार में आकाशों तथा धरती का राज्य है। वही जीवन देता तथा मारता है और तुम्हारे लिए उसके सिवा कोई संरक्षक और सहायक नहीं है।');
INSERT INTO "al_umari" VALUES(9,117,'9:117','अल्लाह ने नबी तथा मुहाजिरीन और अन्सार पर दया की, जिन्होंने तंगी के समय आपका साथ दिया, इसके पश्चात् कि उनमें से कुछ लोगों के दिल कुटिल होने लगे थे। फिर उनपर दया की। निश्चय वह उनके लिए अति करुणामय, दयावान् है।');
INSERT INTO "al_umari" VALUES(9,118,'9:118','तथा उन तीनों[ पर जिनका मामला विलंबित कर दिया गाय था, जब उनपर धरती अपने विस्तार के होते सिकुड़ गयी और उनपर उनके प्राण संकीर्ण हो गये और उन्हें विश्वास था कि अल्लाह के सिवा उनके लिए कोई शरणागार नहीं, परन्तु उसी की ओर। फिर उनपर दया की, ताकि तौबा (क्षमा याचना) कर लें। वास्तव में, अल्लाह अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(9,119,'9:119','हे ईमान वालो! अल्लाह से डरो तथा सचों के साथ हो जाओ।');
INSERT INTO "al_umari" VALUES(9,120,'9:120','मदीना के वासियों तथा उनके आस-पास के देहातियों के लिए उचित नहीं था कि अल्लाह के रसूल से पीछे रह जायें और अपने प्राणों को आपके प्राण से प्रिय समझें। ये इसलिए कि उन्हें अल्लाह की राह में कोई प्यास और थकान तथा भूक नहीं पहुँचती है और न वे किसी ऐसे स्थान को रोंदते हैं, जो काफ़िरों को अप्रिय हो या किसी शत्रु से वह कोई सफलता प्राप्त नहीं करते हैं, परन्तु उनके लिए एक सत्कर्म लिख दिया जाता है। वास्तव में, अल्लाह सत्कर्मियों का फल व्यर्थ नहीं करता।');
INSERT INTO "al_umari" VALUES(9,121,'9:121','और वे (अल्लाह की राह में) थोड़ा या अधिक, जो भी व्यय करते हैं, और जो भी घाटी पार करते हैं, उसे उनके लिए लिख दिया जाता है, ताकि वह उन्हें उससे उत्तम प्रतिफल प्रदान करे, जो वे कर रहे थे।');
INSERT INTO "al_umari" VALUES(9,122,'9:122','ईमान वालों के लिए उचित नहीं कि सब एक साथ निकल पड़ें; तो क्यों नहीं प्रत्येक समुदाय से एक गिरोह निकलता, ताकि धर्म में बोध ग्रहण करें और ताकि अपनी जाति को सावधान करें, जब उनकी ओर वापस आयें, संभवतः वे (कुकर्मों से) बचें[। ');
INSERT INTO "al_umari" VALUES(9,123,'9:123','हे ईमान वलो! अपने आस-पास के काफ़िरों से युध्द करो[ और चाहिए कि वे तुममें कुटिलता पायें तथा विश्वास रखो कि अल्लाह आज्ञाकारियों के साथ है। ');
INSERT INTO "al_umari" VALUES(9,124,'9:124','और जब (क़ुर्आन की) कोई आयत उतारी जाती है, तो इन (द्विधावादियों में) से कुछ कहते हैं कि तुममें से किसका ईमान (विश्वास) इसने अधिक किया[? तो वास्तव में, जो ईमान रखते हैं, उनका विश्वास अवश्य अधिक कर दिया और वे इसपर प्रसन्न हो रहे हैं। ');
INSERT INTO "al_umari" VALUES(9,125,'9:125','परन्तु, जिनके दिलों में (द्विधा) का रोग है, तो उसने उनकी गंदगी और अधिक बढ़ा दी और वे काफ़िर रहते हुए ही मर गये।');
INSERT INTO "al_umari" VALUES(9,126,'9:126','क्या वे नहीं देखते कि उनकी परीक्षा प्रत्येक वर्ष एक बार अथवा दो बार ली जाती[ है? फिर भी वे तौबा (क्षमा याचना) नहीं करते और न शिक्षा ग्रहण करते हैं! ');
INSERT INTO "al_umari" VALUES(9,127,'9:127','और जब कोई सूरह उतारी जाये, तो वे एक-दूसरे की ओर देखते हैं कि तुम्हें कोई देख तो नहीं रहा है? फिर मुँह फेरकर चल देते हैं। अल्लाह ने उनके दिलों को (ईमान से)[ फेर दिया है। इस कारण कि वे समझ-बूझ नहीं रखते। ');
INSERT INTO "al_umari" VALUES(9,128,'9:128','(हे ईमान वालो!) तुम्हारे पास तुम्हीं में से अल्लाह का एक रसूल आ गया है। उसे वो बात भारी लगती है, जिससे तुम्हें दुःख हो, वह तुम्हारी सफलता की लालसा रखते हैं और ईमान वालों के लिए करुणामय दयावान् हैं।');
INSERT INTO "al_umari" VALUES(9,129,'9:129','(हे नबी!) फिर भी यदि वे आपसे मुँह फेरते हों, तो उनसे कह दो कि मेरे लिए अल्लाह (का सहारा) बस है। उसके अतिरिक्त कोई ह़क़ीक़ी पूज्य नहीं और वही महासिंहासन का मालिक (स्वामी) है।');
INSERT INTO "al_umari" VALUES(10,1,'10:1','अलिफ, लाम, रा। ये तत्वज्ञता से परिपूर्ण पुस्तक (क़ुर्आन) की आयतें हैं।');
INSERT INTO "al_umari" VALUES(10,2,'10:2','क्या मानव के लिए आश्चर्य की बात है कि हमने, उन्हीं में से एक पुरुष पर, प्रकाशना भेजी है कि आप, मानवगण को सावधान कर दें और जो ईमान लायें, उन्हें शुभ सूचना सुना दें कि उन्हीं के लिए उनके पालनहार के पास सत्य सम्मान[ है? तो काफ़िरों ने कह दिया कि ये खुला जादूगर है। ');
INSERT INTO "al_umari" VALUES(10,3,'10:3','वास्तव में, तुम्हारा पालनहार वही अल्लाह है, जिसने आकाशों तथा धरती को छः दिनों में उत्पन्न किया, फिर अर्श (राज सिंहासन) पर स्थिर हो गया। वही विश्व की व्यवस्था कर रहा है। कोई उसके पास अनुशंसा (सिफ़ारिश) नहीं कर सकता, परन्तु उसकी अनुमति के पश्चात्। वही अल्लाह तुम्हारा पालनहार है, अतः उसी की ईबादत (वंदना)[ करो। क्या तुम शिक्षा ग्रहण नहीं करते? ');
INSERT INTO "al_umari" VALUES(10,4,'10:4','उसी की ओर तुमसब को लौटना है। ये अल्लाह का सत्य वचन है। वही उत्पत्ति का आरंभ करता है। फिर वही पुनः उत्पन्न करेगा, ताकि उन्हें न्याय के साथ प्रतिफल प्रदान[ करे। जो ईमान लाये और सदाचार किये और जो काफ़िर हो गये, उनके लिए खोलता पेय तथा दुःखदायी यातना है, उस अविश्वास के बदले, जो कर रहे थे। ');
INSERT INTO "al_umari" VALUES(10,5,'10:5','उसीने सूर्य को ज्योति तथा चाँद को प्रकाश बनाया है और उस (चाँद) के गंतव्य स्थान निर्धारित कर दिये, ताकि तुम वर्षों की गिनती तथा ह़िसाब का ज्ञान कर लो। इनकी उत्पत्ति अल्लाह ने नहीं की है, परन्तु सत्य के साथ। वह उन लोगों के लिए निशानियों (लक्षणों) का वर्णन कर रहा है, जो ज्ञान रखते हों।');
INSERT INTO "al_umari" VALUES(10,6,'10:6','निःसंदेह रात्रि तथा दिवस के एक-दूसरे के पीछे आने में और जो कुछ अल्लह ने आकाशों तथा धरती में उत्पन्न किया है, उन लोगों के लिए निशानियाँ हैं, जो अल्लाह से डरते हों।');
INSERT INTO "al_umari" VALUES(10,7,'10:7','वास्तव में, जो लोग (प्रलय के दिन) हमसे मिलने की आशा नहीं रखते और सांसारिक जीवन से प्रसन्न हैं तथा उसीसे संतुष्ट हैं तथा जो हमारी निशानियों से असावधान हैं।');
INSERT INTO "al_umari" VALUES(10,8,'10:8','उन्हीं का आवास नरक है, उसके कारण, जो वे करते रहे।');
INSERT INTO "al_umari" VALUES(10,9,'10:9','वास्तव में, जो ईमान लाये और सुकर्म किये, उनका पालनहार, उनके ईमान के कारण, उन्हें (स्वर्ग की) राह दर्शा देगा, जिनमें नहरें प्रवाहित होंगी। वे सुख के स्वर्गों में होंगे।');
INSERT INTO "al_umari" VALUES(10,10,'10:10','उनकी पुकार उस (स्वर्ग) में ये होगीः "हे अल्लाह! तू पवित्र है।" और एक-दूसरे को उसमें उनका आशीर्वाद ये होगाः "तुमपर शान्ति हो।" और उनकी प्रार्थना का अंत ये होगाः "सब प्रशंसा, अल्लाह के लिए है, जो सम्पूर्ण विश्व का पालनहार है।"');
INSERT INTO "al_umari" VALUES(10,11,'10:11','और यदि अल्लाह, लोगों को तुरन्त बुराई का (बदला) दे देता, जैसे वे तुरन्त (सांसारिक) भलाई चाहते हैं, तो उनका समय कभी पूरा हो चुका होता! अतः जो (मरने के पश्चात्) हमसे मिलने की आशा नहीं रखते, हम उन्हें, उनके कुकर्मों में बहकते हुए[ छोड़ देंगे। ');
INSERT INTO "al_umari" VALUES(10,12,'10:12','और जब मानव को कोई दुःख पहुँचता है, तो हमें लेटे, बैठे या खड़े होकर पुकारता है। फिर जब हम उसका दुःख दूर कर देते हैं, तो ऐसे चल देता है, जैसे कभी हमें किसी दुःख के समय पुकारा ही न हो! इसी प्रकार, उल्लंघनकारियों के लिए उनके करतूत शोभित बना दिये गये हैं।');
INSERT INTO "al_umari" VALUES(10,13,'10:13','और तुमसे पहले, हम कई जातियों को ध्वस्त कर चुके हैं; जब उन्होंने अत्याचार किये और उनके पास उनके रसूल खुले तर्क (प्रमाण) लाये, परन्तु वे ऐसे नहीं थे कि ईमान लाते, इसी प्रकार, हम अपराधियों को बदला देते हैं।');
INSERT INTO "al_umari" VALUES(10,14,'10:14','फिर हमने धरती में उनके पश्चात्, तुम्हें उनका स्थान दिया, ताकि हम देखें कि तुम्हारे कर्म कैसे होते हैं?');
INSERT INTO "al_umari" VALUES(10,15,'10:15','और (हे नबी!) जब हमारी खुली आयतें उन्हें सुनाई जाती हैं, तो जो हमसे मिलने की आशा नहीं रखते, वे कहते हैं कि इसके सिवा कोई दूसरा क़ुर्आन लाओ या इसमें परिवर्तन कर दो। उनसे कह दो कि मेरे बस में ये नहीं है कि अपनी ओर से इसमें परिवर्तन कर दूँ। मैं तो बस उस प्रकाशना का अनुयायी हूँ, जो मेरी ओर की जाती है। मैं यदि अपने पालनहार की अवज्ञा करूँ, तो मैं एक घोर दिन की यातना से डरता हूँ।');
INSERT INTO "al_umari" VALUES(10,16,'10:16','आप कह दें: यदि अल्लाह चाहता, तो मैं क़ुर्आन तुम्हें सुनाता ही नहीं और न वह तुम्हें इससे सूचित करता। फिर मैं इससे पहले तुम्हारे बीच एक आयु व्यतीत कर चुका हूँ। तो क्या तुम समझ बूझ नहीं रखते हो[? ');
INSERT INTO "al_umari" VALUES(10,17,'10:17','फिर उससे अधिक अत्याचारी कौन होगा, जो अल्लाह पर मिथ्या आरोप लगाये अथवा उसकी आयतों को मिथ्या कहे? वास्तव में, ऐसे अपराधी सफल नहीं होते।');
INSERT INTO "al_umari" VALUES(10,18,'10:18','और वे अल्लाह के सिवा, उनकी इबादत (वंदना) करते हैं, जो न तो उन्हें कोई हानि पहुँचा सकते हैं और न कोई लाभ और कहते हैं ये अल्लाह के यहाँ, हमारे अभिस्तावक (सिफारिशी) हैं, आप कहियेः क्या तुम अल्लाह को ऐसी बात की सूचना दे रहे हो, जिसके होने को न वह आकाशों में जानता है और न धरती में? वह पवित्र और उच्च है, उस शिर्क (मिश्रणवाद) से, जो वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(10,19,'10:19','लोग एक ही धर्म (इस्लाम) पर थे, फिर उन्होंने विभेद[ किया और यदि आपके पालनहार की ओर से, पहले ही से एक बात निश्चित न होती, तो उनके बीच उसका (संसार ही में) निर्णय कर दिया जाता, जिसमें वे विभेद कर रहे हैं।');
INSERT INTO "al_umari" VALUES(10,20,'10:20','और वे ये भी कहते हैं कि आपपर कोई आयत (चमत्कार) क्यों नहीं उतारा गया[? आप कह दें कि परोक्ष की बातें तो अल्लाह के अधिकार में हैं। अतः, तुम प्रतीक्षा करो, मैंभी तुम्हारे साथ प्रतीक्षा कर रहा हूँ ।');
INSERT INTO "al_umari" VALUES(10,21,'10:21','और जब हम, लोगों को दुःख पहुँचने के पश्चात्, दया (का स्वाद) चखाते हैं, तो तुरन्त हमारी आयतों (निशानियों) के बारे में षड्यंत्र रचने लगते हैं। आप कह दें कि अल्लाह का उपाय अधिक तीव्र है। हमारे फ़रिश्ते तुम्हारी चालें लिख रहे हैं।');
INSERT INTO "al_umari" VALUES(10,22,'10:22','वही है, जो जल तथा थल में तुम्हें फिराता है। फिर जब तुम नौकाओं में होते हो और (नौकाएँ) उन (सवारों) को लेकर अनुकूल वायु के कारण चलती हैं और वे उससे प्रसन्न होते हैं, तो अकस्मात् प्रचन्ड वायु का झोंका आ जाता है और प्रत्येक स्थान से उन्हें लहरें मारने लगती हैं और समझते हैं कि उन्हें घेर लिया गया है, तो अल्लाह से उसके लिए धर्म को विशुध्द करके[ प्रार्थना करते हैं कि यदि तूने हमें बचा लिया, तो हम अवश्य तेरे कृतज्ञ बनकर रहेंगे! ');
INSERT INTO "al_umari" VALUES(10,23,'10:23','फिर जब उन्हें बचा लेता है, तो अकस्मात् धरती में अवैध विद्रोह करने लगते हैं। हे लोगो! तुम्हारा विद्रोह, तुम्हारे ही विरुध्द पड़ रहा है। ये सांसारिक जीवन के कुछ लाभ[ हैं। फिर तुम्हें हमारी ओर फिरकर आना है। तब हम, तुम्हें बता देंगे कि तुम क्या कर रहे थे? ');
INSERT INTO "al_umari" VALUES(10,24,'10:24','सांसारिक जीवन तो ऐसा ही है, जैसे हमने आकाश से जल बरसाया, जिससे धरती की उपज घनी हो गयी, जिसमें से लोग और पशु खाते हैं। फिर जब, वह समय आया कि धरती ने अपनी शोभा पूरी कर ली और सुसज्जित हो गयी और उसके स्वामी ने समझा कि वह, उससे लाभांवित होने पर सामर्थ्य रखते हैं, तो अकस्मात् रात या दिन में हमारा आदेश आ गया और हमने उसे, इस प्रकार काटकर रख दिया, जैसे कि कल वहाँ थी[ ही नहीं। इसी प्रकार, हम आयतों का वर्णन खोल-खोल कर करते हैं, ताकि लोग मनन-चिंतन करें। ');
INSERT INTO "al_umari" VALUES(10,25,'10:25','और अल्लाह तुम्हें शान्ति के घर (स्वर्ग) की ओर बुला रहा है और जिसे चाहता है, सीधी डगर दर्शा देता है।');
INSERT INTO "al_umari" VALUES(10,26,'10:26','जिन लोगों ने भलाई की, उनके लिए भलाई है और कुछ बढ़कर भी। और न उनके चेहरों पर कालिक लगी हुई होगी और न उन्हें ज़िल्लत का सामना होगा। यही लोग जन्नती हैं कि उसमें हमेशा रहेंगे[। ');
INSERT INTO "al_umari" VALUES(10,27,'10:27','और जिन लोगों ने बुराईयाँ कीं, तो बुराई का बदला उसी जैसा होगा; उनपर अपमान छाया होगा और उनके लिए अल्लाह से बचाने वाला कोई न होगा। उनके मुखों पर ऐसे कालिमा छायी होगी, जैसे अंधेरी रात के काले पर्दे, उनपर पड़े हुए हों। वही नारकी होंगे और वही उसमें सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(10,28,'10:28','जिस दिन, हम उनसब को एकत्र करेंगे, फिर उनसे कहेंगे, जिन्होंने साझी बनाया है कि अपने स्थान पर रुके रहो और तुम्हारे (बनाये हुए) साझी भी। फिर हम उनके बीच अलगाव कर देंगे और उनके साझी कहेंगेः तुमतो हमारी वंदना ही नहीं करते थे।');
INSERT INTO "al_umari" VALUES(10,29,'10:29','हमारे और तुम्हारे बीच, अल्लाह का साक्ष्य बस है कि तुम्हारी वंदना से, हम असूचित थे।');
INSERT INTO "al_umari" VALUES(10,30,'10:30','वहीं, प्रत्येक व्यक्ति उसे परख लेगा, जो पहले किया है और वे (निर्णय के लिए) अपने सत्य स्वामी की ओर फेर दिये जायेंगे और जो मिथ्या बातें बना रहे थे, उनसे खो जायेंगे।');
INSERT INTO "al_umari" VALUES(10,31,'10:31','(हे नबी!) उनसे पूछें कि तुम्हें कौन आकाश तथा धरती[ से जीविका प्रदान करता है? सुनने तथा देखने की शक्तियाँ किसके अधिकार में हैं? कौन निर्जीव से जीव को तथा जीव से निर्जीव को निकालता है? वह कौन है, जो विश्व की व्यवस्था कर रहा है? वे कह देंगे कि अल्लाह ! फिर कहो कि क्या तुम (सत्य के विरोध से) डरते नहीं हो?');
INSERT INTO "al_umari" VALUES(10,32,'10:32','तो वही अल्लाह तुम्हारा सत्य पालनहार है, फिर सत्य के पश्चात् कुपथ (असत्य) के सिवा क्या रह गया? फिर तुम किधर फिराये जा रहे हो?');
INSERT INTO "al_umari" VALUES(10,33,'10:33','इस प्रकार, आपके पालनहार की बातें अवज्ञाकारियों पर सत्य सिध्द हो गयीं कि वे ईमान नहीं लायेंगे।');
INSERT INTO "al_umari" VALUES(10,34,'10:34','आप उनसे कहियेः क्या तुम्हारे साझियों में कोई है, जो उत्पत्ति का आरंभ करता फिर उसे दुहराता है। फिर तुम कहाँ बहके जा रहे हो?');
INSERT INTO "al_umari" VALUES(10,35,'10:35','आप कहियेः क्या तुम्हारे साझियों में कोई संमार्ग दर्शाता है? (यदि नहीं,)तो क्या जो संमार्ग दर्शाता हो, वह अधिक योग्य है कि उसका अनुपालन किया जाये अथवा वह, जो स्वयं संमार्ग पर न हो, परन्तु ये कि उसे संमार्ग दर्शा दिया जाये? तो तुम्हें क्या हो गया है? तुम कैसा निर्णय कर रहे हो?');
INSERT INTO "al_umari" VALUES(10,36,'10:36','और उन (मिश्रणवादियों) में अधिकांश, अनुमान का अनुसरण करते हैं और सत्य को जानने में, अनुमान कुछ काम नहीं दे सकता। वास्तव में, अल्लाह जोकुछ वे कर रहे हैं, भली-भाँति जानता है।');
INSERT INTO "al_umari" VALUES(10,37,'10:37','और ये क़ुर्आन, ऐसा नहीं है कि अल्लाह के सिवा अपने मन से बना लिया जाये, परन्तु उन (पुस्तकों) की पुष्टि है, जो इससे पहले उतरी हैं और ये पुस्तक (क़ुर्आन) विवरण[ है। इसमें कोई संदेह नहीं कि ये सम्पूर्ण विश्व के पालनहार की ओर से है। ');
INSERT INTO "al_umari" VALUES(10,38,'10:38','क्या वे कहते हैं कि इस (क़ुर्आन) को उस (नबी) ने स्वयं बना लिया है? आप कह दें: इसीके समान एक सूरह ले आओ और अल्लाह के सिवा, जिसे (अपनी सहायता के लिए) बुला सकते हो बुला लो, यदि तुम सत्यवादि हो।');
INSERT INTO "al_umari" VALUES(10,39,'10:39','बल्कि उन्होंने उस (क़ुर्आन) को झुठला दिया, जो उनके ज्ञान के घेरे में नहीं[ आया और न उसका परिणाम उनके सामने आया। इसी प्रकार, उन्होंने भी झुठलाया था, जो इनसे पहले थे। तो देखो कि अत्याचारियों का क्या परिणाम हुआ? ');
INSERT INTO "al_umari" VALUES(10,40,'10:40','और उनमें से कुछ ऐसे हैं, जो इस (क़ुर्आन) पर ईमान लाते हैं और कुछ ईमान नहीं लाते और आपका पालनहार उपद्रवकारियों को अधिक जानता है।');
INSERT INTO "al_umari" VALUES(10,41,'10:41','और यदि वे आपको झुठलायें, तो आप कह दें: मेरे लिए मेरा कर्म है और तुम्हारे लिए तुम्हारा कर्म। तुम उससे निर्दोष हो, जो मैं करता हूँ तथा मैं उससे निर्दोष हूँ, जो तुम करते हो।');
INSERT INTO "al_umari" VALUES(10,42,'10:42','इनमें से कुछ लोग, आपकी ओर कान लगाते हैं। तो क्या आप बहरों[ को सुना सकते हैं, यद्यपि वे कुछभी न समझ सकते हों? ');
INSERT INTO "al_umari" VALUES(10,43,'10:43','और उनमें से कुछ ऐसे हैं, जो आपकी ओर तकते हैं, तो क्या आप अंधे को राह दिखा देंगे, यद्यपि उन्हें कुछ सूझता न हो?');
INSERT INTO "al_umari" VALUES(10,44,'10:44','वास्तव में, अल्लाह, लोगों पर अत्याचार नहीं करता, परन्तु लोग स्वयं अपने ऊपर अत्याचार करते हैं[। ');
INSERT INTO "al_umari" VALUES(10,45,'10:45','और जिस दिन अल्लाह उन्हें एकत्र करेगा, तो उन्हें लगेगा कि वे (संसार में) दिन के केवल कुछ क्षण रहे। वे आपस में परिचित होंगे। वास्तव में, वे क्षतिग्रस्त हो गये, जिन्होंने अल्लाह से मिलने को झुठला दिया और वे सीधी डगर पाने वाले न हूए।');
INSERT INTO "al_umari" VALUES(10,46,'10:46','और यदि, हम आपको उस (यातना) में से कुछ दिखा दें, जिसका वचन उन्हें दे रहे हैं अथवा (उससे पहले) आपका समय पूरा कर दें, तोभी उन्हें हमारे पास ही फिरकर आना है। फिर अल्लाह उसपर साक्षी है, जो वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(10,47,'10:47','और प्रत्येक समुदाय के लिए एक रसूल है। फिर जब उनका रसूल आ गया, तो (हमारा नियम ये है कि) उनके बीच न्याय के साथ निर्णय कर दिया जाता है और उनपर अत्याचार नहीं किया जाता।');
INSERT INTO "al_umari" VALUES(10,48,'10:48','और वे कहते हैं कि हमपर यातना का वचन कब पूरा होगा, यदि तुम सत्यवादि हो?');
INSERT INTO "al_umari" VALUES(10,49,'10:49','आप कह दें कि मैं स्वयं अपने लाभ तथा हानि का अधिकार नहीं रखता। वही होता है, जो अल्लाह चाहता है। प्रत्येक समुदाय का एक समय निर्धारित है तथा जब उनका समय आ जायेगा, तो न एक क्षण पीछे रह सकते हैं और न आगे बढ़ सकते हैं।');
INSERT INTO "al_umari" VALUES(10,50,'10:50','(हे नबी!) कह दो कि तुम बताओ यदि अल्लाह की यातना तुमपर रात अथवा दिन में आ जाये, ( तो तुम क्या कर सकते हो?) ऐसी क्या बात है कि अपराधि उसके लिए जल्दी मचा रहे हैं?');
INSERT INTO "al_umari" VALUES(10,51,'10:51','क्या जब वह आ जायेगी, उस समय तुम उसे मानोगे? अब, जबकि उसके शीघ्र आने की माँग कर रहे थे।');
INSERT INTO "al_umari" VALUES(10,52,'10:52','फिर अत्याचारियों से कहा जायेगा कि सदा की यातना चखो। तुम्हें उसी का प्रतिकार (बदला) दिया जा रहा है, जो तुम (संसार में) कमा रहे थे।');
INSERT INTO "al_umari" VALUES(10,53,'10:53','और वे आपसे पूछते हैं कि क्या ये बात वास्तव में सत्य है? आप कह दें कि मेरे पालनहार की शपथ! ये वास्तव में सत्य है और तुम अल्लाह को विवश नहीं कर सकते।');
INSERT INTO "al_umari" VALUES(10,54,'10:54','और यदि प्रत्येक व्यक्ति के पास, जिसने अत्याचार किया है, जो कुछ धरती में है, सब आ जाये, तो वे अवश्य उसे अर्थदण्ड के रूप में देने को तैयार हो जायेगा और जब वे उस यातना को देखेंगे, तो दिल ही दिल में पछतायेंगे और उनके बीच, न्याय के साथ निर्णय कर दिया जायेगा और उनपर अत्याचार नहीं किया जायेगा।');
INSERT INTO "al_umari" VALUES(10,55,'10:55','सुनो! अल्लाह ही का है, वो जो कुछ आकाशों तथा धरती में है। सुनो! उसका वचन सत्य है। परन्तु अधिक्तर लोग इसे नहीं जानते।');
INSERT INTO "al_umari" VALUES(10,56,'10:56','वही जीवन देता तथा वही मारता है और उसी की ओर तुमसब लौटाये जाओगे[। ');
INSERT INTO "al_umari" VALUES(10,57,'10:57','हे लोगो[! तम्हारे पास, तुम्हारे पालनहार की ओर से, शिक्षा (क़ुर्आन) आ गयी है, जो अन्तरात्मा के सब रोगों का उपचार (स्वास्थ्य कर), मार्गदर्शन और दया है, उनके लिए, जो विश्वास रखते हों। ');
INSERT INTO "al_umari" VALUES(10,58,'10:58','आप कह दें कि ये (क़ुर्आन) अल्लाह की अनुग्रह और उसकी दया है। अतः लोगों को इससे प्रसन्न हो जाना चाहिए और ये उस (धन-धान्य) से उत्तम है, जो लोग एकत्र रहे हैं।');
INSERT INTO "al_umari" VALUES(10,59,'10:59','(हे नबी!) उनसे कहोः क्या तुमने इसपर विचार किया है कि अल्लाह ने तुम्हारे लिए, जो जीविका उतारी है, तुमने उसमें से कुछ को ह़राम (अवैध) बना दिया है और कुछ को ह़लाल (वैध) , तो कहो कि क्या अल्लाह ने तुम्हें इसकी अनुमति दी है? अथवा तुम अल्लाह पर आरोप लगा रहे[ हो? ');
INSERT INTO "al_umari" VALUES(10,60,'10:60','और जो लोग, अल्लाह पर मिथ्या आरोप लगा रहे हैं, उन्होंने प्रलय के दिन को क्या समझ रखा है? वास्तव में, अल्लाह लोगों के लिए दयाशील[ है। परन्तु उनमें अधिक्तर कृतज्ञ नहीं होते। ');
INSERT INTO "al_umari" VALUES(10,61,'10:61','(हे नबी!) आप जिस दशा में हों और क़ुर्आन में से, जो कुछ भी सुनाते हों तथा (हे मनुष्यो!) तुम लोग भी कोई कर्म नहीं करते हो, परन्तु हम तुम्हें देखते रहते हैं, जब तुम उसे करते हो और (हे नबी!) आपके पालनहार से धरती में कण-भर भी कोई चीज़ छुपी नहीं रहती और न आकाश में, न इससे कोई छोटी न बड़ी, परन्तु वह खुली पुस्तक में अंकित है।');
INSERT INTO "al_umari" VALUES(10,62,'10:62','सुनो! जो अल्लाह के मित्र हैं, न उन्हें कोई भय होगा और न वे उदासीन होंगे।');
INSERT INTO "al_umari" VALUES(10,63,'10:63','जो ईमान लाये तथा अल्लाह से डरते रहे।');
INSERT INTO "al_umari" VALUES(10,64,'10:64','उन्हीं के लिए सांसारिक जीवन में, शुभ सूचना है तथा प्रलोक में भी। अल्लाह की बातों में कोई परिवर्तन नहीं, यही बड़ी सफलता है।');
INSERT INTO "al_umari" VALUES(10,65,'10:65','तथा (हे नबी!) आपको उन (काफ़िरों) की बात उदासीन न करे। वास्तव में, सभी प्रभुत्व अल्लाह ही के लिए है और वह सब कुछ सुनने जानने-वाला है।');
INSERT INTO "al_umari" VALUES(10,66,'10:66','सुनो! वास्तव में, अल्लाह ही के अधिकार में है, जो आकाशों तथा धरती में है और जो अल्लाह के सिवा दूसरे साझियों को पुकारते हैं, वे केवल अनुमान के पीछे लगे हुए हैं और वे केवल आँकलन कर रहे हैं।');
INSERT INTO "al_umari" VALUES(10,67,'10:67','वही है, जिसने तुम्हारे लिए रात बनाई है, ताकि उसमें सुख पाओ और दिन बनाया ताकि उसके प्रकाश में देखो। निःसंदेह इसमें (अल्लाह के व्यवस्थापक होने की) उनके लिए बड़ी निशानियाँ हैं, जो (सत्य को) सुनते हों।');
INSERT INTO "al_umari" VALUES(10,68,'10:68','और उन्होंने कह दिया कि अल्लाह ने कोई पुत्र बना लिया है। वह पवित्र है। वह निस्पृह है। वही स्वामी है उसका, जो अकाशों में तथा धरती में है। क्या तुम्हारे पास इसका कोई प्रमाण है? क्या तुम अल्लाह पर ऐसी बात कह रहे हो, जिसका तुम ज्ञान नहीं रखते?');
INSERT INTO "al_umari" VALUES(10,69,'10:69','(हे नबी!) आप कह दें: जो अल्लाह पर मिथ्या बातें बनाते हैं, वह सफल नहीं होंगे।');
INSERT INTO "al_umari" VALUES(10,70,'10:70','उनके लिए संसार ही का कुछ आनंद है, फिर हमारी ओर ही आना है। फिर हम उन्हें, उनके कुफ़्र (अविश्वास) करते रहने के कारण घोर यातना चखायेंगे।');
INSERT INTO "al_umari" VALUES(10,71,'10:71','आप उन्हें नूह़ की कथा सुनायें, जब उसने अपनी जाति से कहाः हे मेरी जाति! यदि मेरा तुम्हारे बीच रहना और तुम्हें अल्लाह की आयतों (निशानियों) द्वारा मेरा शिक्षा देना, तुमपर भारी हो, तो अल्लाह हीपर मैंने भरोसा किया है। तुम मेरे विरुध्द जो करना चाहो, उसे निश्चित कर लो और अपने साझियों (देवी-देवताओं) को भी बुला लो। फिर तुम्हारी योजना तुमपर तनिक भी छुपी न रह जाये, फिर जो करना हो, उसे कर जाओ और मुझे कोई अवसर न दो।');
INSERT INTO "al_umari" VALUES(10,72,'10:72','फिर यदि तुमने मुख फेरा, तो मैंने तुमसे किसी पारिश्रमिक की माँग नहीं की है। मेरा पारिश्रमिक तो अल्लाह के सिवा किसी के पास नहीं है और मुझे आदेश दिया गया है कि आज्ञाकारियों में रहूँ।');
INSERT INTO "al_umari" VALUES(10,73,'10:73','फिर भी उन्होंने उसे झुठला दिया, तो हमने उसे और जो नाव में उसके साथ (सवार) थे, बचा लिया और उन्हीं को उनका उत्तराधिकारी बना दिया और उन्हें जलमगन कर दिया, जिन्होंने हमारी निशानियों को झुठला दिया। अतः देखलो कि उनका परिणाम किया हुआ, जो सचेत किये गये थे।');
INSERT INTO "al_umari" VALUES(10,74,'10:74','फिर हमने उस (नूह़) के पश्चात बहुत-से रसूलों को, उनकी जाति के पास भेजा, वे उनके पास खुली निशानियाँ (तर्क) लाये, तो वे ऐसे न थे कि जिसे पहले झुठला दिया था, उसपर ईमान लाते, इसी प्रकार, हम उल्लंघनकारियों के दिलों पर मुहर[ लगा देते हैं। ');
INSERT INTO "al_umari" VALUES(10,75,'10:75','फिर हमने उनके पश्चात मूसा और हारून को फ़िरऔन और उसके प्रमुखों के पास भेजा। तो उन्होंने अभिमान किया और वे थे ही अपराधीगण।');
INSERT INTO "al_umari" VALUES(10,76,'10:76','फिर जब उनके पास हमारी ओर से सत्य आ गया, तो उन्होंने कह दिया कि वास्तव में ये तो खुला जादू है।');
INSERT INTO "al_umari" VALUES(10,77,'10:77','मूसा ने कहाः क्या तुम सत्य को, जब तुम्हारे पास आ गया, तो जादू कहने लगे? क्या ये जादू है? जबकि जादूगर (तांत्रिक) सफल नहीं होते!');
INSERT INTO "al_umari" VALUES(10,78,'10:78','उन्होंने कहाः क्या तुम इसलिए हमारे पास आये हो, ताकि हमें उस प्रथा से फेर दो, जिसपर हमने अपने पूर्वजों को पाया है और देश (मिस्र)में तुम दोनों की महिमा स्थापित हो जाये? हम, तुम दोनों का विश्वास करने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(10,79,'10:79','और फ़िरऔन ने कहाः (देश में) जितने दक्ष जादूगर हैं, उन्हें मेरे पास लाओ।');
INSERT INTO "al_umari" VALUES(10,80,'10:80','फिर जब जादूगर आ गये, तो मूसा ने कहाः जो कुछ तुम्हें फेंकना है, उसे फेंक दो।');
INSERT INTO "al_umari" VALUES(10,81,'10:81','और जब उन्होंने फेंक दिया, तो मूसा नो कहाः तुम जो कुछ लाये हो, वह जादू है। निश्चय अल्लाह उसे अभिव्यर्थ कर देगा। वास्तव में, अल्लाह उपद्रवकारियों के कर्म को नहीं सुधारता।');
INSERT INTO "al_umari" VALUES(10,82,'10:82','और अल्लाह सत्य को, अपने आदेशों के अनुसार, सत्य कर दिखायेगा। यद्यपि अपराधियों को बुरा लगे।');
INSERT INTO "al_umari" VALUES(10,83,'10:83','तो मूसा पर, उसकी जाति के कुछ नवयुवकों के सिवा, कोई ईमान नहीं लाया, फ़िरऔन और अपने प्रमुखों के भय से कि उन्हें किसी यातना में न डाल दें। वास्तव में, फ़िर्औन का धरती में बड़ा प्रभुत्व था और वह वस्तुतः उल्लंघनकारियों में था।');
INSERT INTO "al_umari" VALUES(10,84,'10:84','और मूसा ने (अपनी जाति, बनी इस्राईल से) कहाः हे मेरी जाति! जब तुम अल्लाह पर ईमान लाये हो, तो उसीपर निर्भर रहो, यदि तुम आज्ञाकारी हो।');
INSERT INTO "al_umari" VALUES(10,85,'10:85','तो उन्होंने कहाः हमने अल्लाह ही पर भरोसा किया है। हे हमारे पालनहार! हमें अत्याचारियों के लिए परीक्षा का साधन न बना।');
INSERT INTO "al_umari" VALUES(10,86,'10:86','और अपनी दया से हमें काफ़िरों से बचा ले।');
INSERT INTO "al_umari" VALUES(10,87,'10:87','और हमने मूसा तथा उसके भाई (हारून) की ओर प्रकाशना भेजी कि अपनी जाति के लिए मिस्र में कुछ घर बनाओ और अपने घरों को क़िब्ला[ बना लो तथा नमाज़ की स्थापना करो और ईमान वालों को शुभ सूचना दो। ');
INSERT INTO "al_umari" VALUES(10,88,'10:88','और मूसा ने प्रार्थना कीः हे मेरे पारलनहार! तूने फ़िरऔन और उसके प्रमुखों को सांसारिक जीवन में शोभा तथा धन-धान्य प्रदान किया है। तो मेरे पालनहार! क्या इसलिए कि वे तेरी राह से विचलित करते रहें? हे मेरे पालनहार! उनके धनों को निरस्त कर दे और उनके दिल कड़े कर दे कि वे ईमान न लायें, जब तक दुःखदायी यातना न देख लें।');
INSERT INTO "al_umari" VALUES(10,89,'10:89','अल्लाह ने कहाः तुम दोनों की प्रार्थना स्वीकार कर ली गयी। तो तुम दोनों अडिग रहो और उनकी राह का अनुसरण न करो, जो ज्ञान नहीं रखते।');
INSERT INTO "al_umari" VALUES(10,90,'10:90','और हमने बनी इस्राईल को सागर पार करा दिया, तो फ़िरऔन और उसकी सेना ने उनका पीछा किया, अत्याचार तथा शत्रुता के ध्येय से। यहाँ तक कि जब वह जलमगन होने लगा, तो बोलाः मैं ईमान ले आया और मान लिया कि उसके सिवा कोई पूज्य नहीं है, जिसपर बनी इस्राईल ईमान लाये हैं और मैं आज्ञाकारियों में हूँ।');
INSERT INTO "al_umari" VALUES(10,91,'10:91','(अल्लाह ने कहाः) अब? जबकि इससे पूर्व अवज्ञा करता रहा और उपद्रवियों में से था?');
INSERT INTO "al_umari" VALUES(10,92,'10:92','आज हम तेरे शव को बचा लेंगे, ताकि तू उनके लिए, जो तेरे पश्चात होंगे, एक (शिक्षाप्रद) निशानी[ बन जाये और वास्तव में, बहुत-से लोग, हमारी निशानियों से अचेत रहते हैं। ');
INSERT INTO "al_umari" VALUES(10,93,'10:93','और हमने बनी इस्राईल को अच्छा निवास स्थान[ दिया और स्वच्छ जीविका प्रदान की। फिर उन्होंने परस्पर विभेद उस समय किया, जब उनके पास ज्ञान आ गया। निश्चय अल्लाह उनके बीच प्रलय के दिन उसका निर्णय कर देगा, जिसमें वे विभेद कर रहे थे। ');
INSERT INTO "al_umari" VALUES(10,94,'10:94','फिर यदि आपको उसमें कुछ संदेह[ हो, जो हमने आपकी ओर उतारा है, तो उनसे पूछ लें, जो आपसे पहले से पुस्तक (तौरात) पढ़ते हैं। आपके पास, आपके पालनहार की ओर से सत्य आ गया है। अतः आप, कदापि संदेह करने वालों में न हों। ');
INSERT INTO "al_umari" VALUES(10,95,'10:95','और आप कदापि उनमें से न हों, जिन्होंने अल्लाह की आयतों को झुठला दिया, अन्यथा क्षतिग्रस्तों में हो जायेंगे।');
INSERT INTO "al_umari" VALUES(10,96,'10:96','(हे नबी!) जिनपर आपके पालनहार का आदेश सिध्द हो गया है, वह ईमान नहीं लाएँगे।');
INSERT INTO "al_umari" VALUES(10,97,'10:97','यद्यपि उनके पास, सभी निशानियाँ आ जायें, जब तक दुःखदायी यातना नहीं देख लेंगे।');
INSERT INTO "al_umari" VALUES(10,98,'10:98','फिर, ऐसा क्यों नहीं हुआ कि कोई बस्ती ईमान[ लाये, फिर उसका ईमान उसे लाभ पहुँचाये, यूनुस की जाति के सिवा, जब वे ईमान लाये, तो हमने उनके सांसारिक जीवन में अपमानकारी यातना दूर कर दी और उन्हें, एक निश्चित अवधि तक लाभान्वित होने का अवसर दे दिया।');
INSERT INTO "al_umari" VALUES(10,99,'10:99','और यदि आपका पालनहार चाहता, तो जोभी धरती में हैं, सब ईमान ले आते, तो क्या आप, लोगों को बाध्य करेंगे, यहाँ तक कि ईमान ले आयें[? ');
INSERT INTO "al_umari" VALUES(10,100,'10:100','किसी प्राणी के लिए ये संभव नहीं है कि अल्लाह की अनुमति[ के बिना ईमान लाये और वह (अल्लाह) मलीनता उनपर डाल देता है, जो बुध्दि का प्रयोग नहीं करते। ');
INSERT INTO "al_umari" VALUES(10,101,'10:101','(हे नबी!) उनसे कहो कि उसे देखो, जो आकाशो तथा धरती में है और निशानियाँ तथा चेतावनियाँ उन्हें क्या लाभ दे सकती हैं, जो ईमान (विश्वास) न रखते हों?');
INSERT INTO "al_umari" VALUES(10,102,'10:102','तो क्या, वे इस बात की प्रतीक्षा कर रहे हैं कि उनपर वैसे ही (बुरे) दिन आयें, जैसे उनसे पहले लोगों पर आ चुके हैं? आप कहिएः फिर तो तुम प्रतीक्षा करो। मैं (भी) तुम्हारे साथ प्रतीक्षा करने वालों में हूँ।');
INSERT INTO "al_umari" VALUES(10,103,'10:103','फिर हम अपने रसूलों को और जो ईमान लाये बचा लेते हैं। इसी प्रकार, हमने अपने ऊपर अनिवार्य कर लिया है कि ईमान वालों को बचा लेते हैं।');
INSERT INTO "al_umari" VALUES(10,104,'10:104','आप कह दें: हे लोगो! यदि तुम, मेरे धर्म के बारे में किसी संदेह में हो, तो (सुन लो) मैं उसकी इबादत (वंदना) कभी नहीं करूँगा, जिसकी इबादत (वंदना) अल्लाह के सिवा तुम करते हो। परन्तु, मैं उस अल्लाह की इबादत (वंदना) करता हूँ, जो तुम्हें मौत देता है और मुझे आदेश दिया गया है कि ईमान वालों में रहूँ।');
INSERT INTO "al_umari" VALUES(10,105,'10:105','और ये कि अपने मुख को धर्म के लिए सीधा रखो, एकेश्वरवादी होकर और कदापि मिश्रणवादियों में न रहो।');
INSERT INTO "al_umari" VALUES(10,106,'10:106','और अल्लाह के सिवा उसे न पुकारें, जो आपको न लाभ पहुँचा सकता है और न हानि पहुँचा सकता है। फिर यदि, आप ऐसा करेंगे, तो अत्याचारियों में हो जायेंगे।');
INSERT INTO "al_umari" VALUES(10,107,'10:107','और यदि अल्लाह आपको कोई दुःख पहुँचाना चाहे, तो उसके सिवा कोई उसे दूर करने वाला नहीं और यदि आपको कोई भलाई पहुँचाना चाहे, तो कोई उसकी भलाई को रोकने वाला नहीं। वह अपनी दया अपने भक्तों में से जिसपर चाहे, करता है तथा वह क्षमाशील दयावान् है।');
INSERT INTO "al_umari" VALUES(10,108,'10:108','(हे नबी!) कह दो कि हे लोगो! तुम्हारे पालनहार की ओर से तुम्हारे पास सत्य आ गया[ है। अब जो सीधी डगर अपनाता हो, तो उसी के लिए लाभदायक है और जो कुपथ हो जाये, तो उसका कुपथ उसी के लिए नाशकारी है और मैं तुमपर अधिकारी नहीं हूँ ।');
INSERT INTO "al_umari" VALUES(10,109,'10:109','आप उसी का अनुसरण करें, जो आपकी ओर प्रकाशना की जा रही है और धैर्य से काम लें, यहाँ तक कि अल्लाह निर्णय कर दे और वह सर्वोत्तम निर्णेता है।');
INSERT INTO "al_umari" VALUES(11,1,'11:1','अलिफ, लाम, रा। ये पुस्तक है, जिसकी आयतें सुदृढ़ की गयीं, फिर सविस्तार वर्णित की गयी हैं, उसकी ओर से, जो तत्वज्ञ, सर्वसूचित है।');
INSERT INTO "al_umari" VALUES(11,2,'11:2','कि अल्लाह के सिवा किसी की इबादत (वंदना) न करो। वास्तव में, मैं उसकी ओर से तुम्हें सचेत करने वाला तथा शुभ सूचना देने वाला हूँ।');
INSERT INTO "al_umari" VALUES(11,3,'11:3','और ये है कि अपने पालनहार से क्षमा याचना करो, फिर उसी की ओर ध्यानमग्न हो जाओ। वह तुम्हें एक निर्धारित अवधि तक अच्छा लाभ पहुँचाएगा और प्रत्येक श्रेष्ठ को उसकी श्रेष्ठता प्रदान करेगा और यदि तुम मुँह फेरोगे, तो मैं तुमपर एक बड़े दिन की यातना से डरता हूँ।');
INSERT INTO "al_umari" VALUES(11,4,'11:4','अल्लाह ही की ओर तुमसब को पलटना है और वह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(11,5,'11:5','सुनो! ये लोग अपने सीनों को मोड़ते हैं, ताकि उस[ से छुप जायेँ, सुनो! जिस समय वे अपने कपड़ों से स्वयं को ढाँपते हैं, तबभी वह (अल्लाह) उनके छुपे को जानता है तथा उनके खुले को भी। वास्तव में, वह उसे भी भली-भाँति जानने वाला है, जो सीनों में (भेद) है।');
INSERT INTO "al_umari" VALUES(11,6,'11:6','और धरती में कोई चलने वाला नहीं है, परन्तु उसकी जीविका अल्लाह के ऊपर है तथा वह उसके स्थायी स्थान तथा सौंपने के स्थान को जानता है। सबकुछ एक खुली पुस्तक में अंकित है[। ');
INSERT INTO "al_umari" VALUES(11,7,'11:7','और वही है, जिसने आकाशों तथा धरती की उत्पत्ति छः दिनों में की। उस समय उसका सिंहासन जल पर था, ताकि तुम्हारी परीक्षा ले कि तुममें किसका कर्म सबसे उत्तम है। और (हे नबी!) यदि आप, उनसे कहें कि वास्तव में तुम सभी मरण के पश्चात् पुनः जीवित किये जाओगे, तो जो काफ़िर हो गये, अवश्य कह देंगे कि ये तो केवल खुला जादू है।');
INSERT INTO "al_umari" VALUES(11,8,'11:8','और यदि, हम उनसे यातना में किसी विशेष अवधि तक देर कर दें, तो अवश्य कहेंगे कि उसे क्या चीज़ रोक रही है? सुन लो! वह जिस दिन उनपर आ जायेगी, तो उनसे फिरेगी नहीं और उन्हें वह (यातना) घेर लेगी, जिसकी वे हँसी उड़ा रहे थे।');
INSERT INTO "al_umari" VALUES(11,9,'11:9','और यदि, हम मनुष्य को अपनी कुछ दया चखा दें, फिर उसे उससे छीन लें, तो हताशा कृतघ्न हो जाता है।');
INSERT INTO "al_umari" VALUES(11,10,'11:10','और यदि, हम उसे सुख चखा दें, दुःख के पश्चात्, जो उसे पहुँचा हो, तो अवश्य कहेगा कि मेरा सब दुःख दूर हो गया। वास्तव में, वह प्रफुल्ल होकर अकड़ने लगता है[। ');
INSERT INTO "al_umari" VALUES(11,11,'11:11','परन्तु, जिन्होंने धैर्य धारण किया और सुकर्म किये, तो उनके लिए क्षमा और बड़ा प्रतिफल है।');
INSERT INTO "al_umari" VALUES(11,12,'11:12','तो (हे नबी!) संभवतः आप उसमें से कुछ को, जो आपकी ओर प्रकाशना की जा रही है, त्याग देने वाले हैं और इसके कारण आपका दिल सिकुड़ रहा है कि वे कहते हैं कि इसपर कोई कोष क्यों नहीं उतारा गया या इसके साथ कोई फरिश्ता क्यों (नहीं) आया? (तो सुनिए) आपकेवल सचेत करने वाले हैं और अल्लाह ही प्रत्येक चीज़ पर रक्षक है।');
INSERT INTO "al_umari" VALUES(11,13,'11:13','क्या वह कहते हैं कि उसने इस (क़ुर्आन) को स्वयं बना लिया है? आप कह दें कि इसीके समान दस सूरतें बना लाओ[ और अल्लाह के सिवा, जिसे हो सके, बुला हो, यदि तुम लोग सच्चे हो। ');
INSERT INTO "al_umari" VALUES(11,14,'11:14','फिर यदि वे उत्तर न दें, तो विश्वास कर लो कि उसे (क़ुर्आन को) अल्लाह के ज्ञान के साथ ही उतारा गया है और ये कि कोई वंदनीय (पूज्य) नहीं है, परन्तु वही। तो क्या तुम मुस्लिम होते हो?');
INSERT INTO "al_umari" VALUES(11,15,'11:15','जो व्यक्ति सांसारिक जीवन तथा उसकी शोभा चाहता हो, हम उनके कर्मों का (फल) उसीमें चुका देंगे और उनके लिए (संसार में) कोई कमी नहीं की जायेगी।');
INSERT INTO "al_umari" VALUES(11,16,'11:16','यही वो लोग हैं, जिनका परलोक में अग्नि के सिवा कोई भाग नहीं होगा और उन्होंने जो कुछ किया, वह व्यर्थ हो जायेगा और वे जो कुछ कर रहे हैं, असत्य सिध्द होने वाला है।');
INSERT INTO "al_umari" VALUES(11,17,'11:17','तो क्या जो अपने पालनहार की ओर से स्पष्ट प्रमाण[ रखता हो और उसके साथ ही एक गवाह (साक्षी) भी उसकी ओर से आ गया हो और इससे पहले मूसा की पुस्तक मार्गदर्शक तथा दया बनकर आ चुकी हो, ऐसे लोग तो इस (क़ुर्आन) पर ईमान रखते हैं और सम्प्रदायों मेंसे, जो इसे अस्वीकार करेगा, तो नरक ही उसका वचन-स्थान है। अतः आप, इसके बारे में किसी संदेह में न पड़ें। वास्तव में, ये आपके पालनहार की ओर से सत्य है। परन्तु अधिक्तर लोग ईमान (विश्वास) नहीं रखते।');
INSERT INTO "al_umari" VALUES(11,18,'11:18','और उससे बड़ा अत्याचारी कौन होगा, जो अल्लाह पर मिथ्यारोपण करे? वही लोग, अपने पालनहार के समक्ष लाये जायेंगे और साक्षी (फ़रिश्ते) कहेंगे कि इन्होंने अपने पालनहार पर झूठ बोले। सुनो! अत्याचारियों पर अल्लाह की धिक्कार है।');
INSERT INTO "al_umari" VALUES(11,19,'11:19','वही लोग, अल्लाह की राह से रोक रहे हैं और उसे टेढ़ा बनाना चाहते हैं। वही परलोक को न मानने वाले हैं।');
INSERT INTO "al_umari" VALUES(11,20,'11:20','वे लोग धरती में विवश करने वाले नहीं थे और न उनका अल्लाह के सिवा कोई सहायक था। उनके लिए दुगनी यातना होगी। वे न सुन सकते थे, न देख सकते थे।');
INSERT INTO "al_umari" VALUES(11,21,'11:21','उन्होंने ही स्वयं अपना विनाश कर लिया और उनसे वो बात खो गयी, जो वे बना रहे थे।');
INSERT INTO "al_umari" VALUES(11,22,'11:22','ये आवश्यक है कि परलोक में यही सर्वाधिक विनाश में होंगे।');
INSERT INTO "al_umari" VALUES(11,23,'11:23','वास्तव में, जो ईमान लाये और सदाचार किये तथा अपने पालनहार की ओर आकर्शित हुए, वही स्वर्गीय हैं और वे उसमें सदैव रहेंगे।');
INSERT INTO "al_umari" VALUES(11,24,'11:24','दोनों समुदाय की दशा ऐसी है, जैसे एक अन्धा और बहरा हो और दूसरा देखने और सुनने वाला हो। तो क्या दोनों की दशा समान हो सकती है? क्या तुम (इस अन्तर को) नहीं समझते[? ');
INSERT INTO "al_umari" VALUES(11,25,'11:25','और हमने नूह़ को उसकी जाति की ओर रसूल बनाकर भेजा। उन्होंने कहाः वास्तव में, मैं तुम्हारे लिए खुले रूप से सावधान करने वाला हूँ।');
INSERT INTO "al_umari" VALUES(11,26,'11:26','कि इबादत (वंदना) केवल अल्लाह ही की करो। मैं तुम्हारे ऊपर दुःख दायी दिन की यातना से डरता हूँ।');
INSERT INTO "al_umari" VALUES(11,27,'11:27','तो उन प्रमुखों ने, जो उनकी जाति में से काफ़िर हो गये, कहाः हमतो तुझे अपने ही जैसा मानव पुरुष देख रहे हैं और हम देख रहे हैं कि तुम्हारा अनुसरण केवन वही लोग कर रहे हैं, जो हममें नीच हैं। वो भी बिना सोचे-समझे और हम अपने ऊपर तुम्हारी कोई प्रधानता भी नहीं देखते, बल्कि हम तुम्हें झूठा समझते हैं।');
INSERT INTO "al_umari" VALUES(11,28,'11:28','उस (अर्थात् नूह़) ने कहाः हे मेरी जाति के लोगो! तुमने इस बात पर विचार किया कि यदि मैं अपने पालनहार की ओर से एक स्पष्ट प्रमाण पर हूँ और मुझे उसने अपने पास से एक दया[ प्रदान की हो, फिर वह तुम्हें सुझायी न दे, तो क्या हम उसे तुमसे चिपका दें, जबकि तुम उसे नहीं चाहते?');
INSERT INTO "al_umari" VALUES(11,29,'11:29','और हे मेरी जाति के लोगो! मैं इस (सत्य के प्रचार) पर तुमसे कोई धन नहीं माँगता। मेरा बदला तो अल्लाह के ऊपर है और मैं उन्हें (अपने यहाँ से) धुतकार नहीं सकता, जो ईमान लाये हैं, निश्चय वे अपने पालनहार से मिलने वाले हैं, परन्तु मैं देख रहा हूँ कि तुम जाहिलों जैसी बातें कर रहे हो।');
INSERT INTO "al_umari" VALUES(11,30,'11:30','और हे मेरी जाति के लोगो! कौन अल्लाह की पकड़ से[ मुझे बचायेगा, यदि मैं उन्हें अपने पास से धुतकार दूँ? क्या तुम सोचते नहीं हो? ');
INSERT INTO "al_umari" VALUES(11,31,'11:31','और मैं तुमसे ये नहीं कहता कि मेरे पास अल्लाह के कोषागार (ख़ज़ाने) हैं और न मैं गुप्त बातों का ज्ञान रखता हूँ और ये भी नहीं कहता कि मैं फ़रिश्ता हूँ और ये भी नहीं कहता कि जिन्हें तुम्हारी आँखें घृणा से देखती हैं, अल्लाह उन्हें कोई भलाई नहीं देगा। अल्लाह अधिक जानता है, जो कुछ उनके दिलों में है। यदि मैं ऐसा कहूँ, तो निश्चय अत्याचारियों में हो जाऊँगा।');
INSERT INTO "al_umari" VALUES(11,32,'11:32','उन्होंने कहाः हे नूह़! तूने हमसे झगड़ा किया और बहुत झगड़ लिया, अब वो (यातना) ला दो, जिसकी धमकी हमें देते हो, यदि तुम सच बोलने वालों में हो।');
INSERT INTO "al_umari" VALUES(11,33,'11:33','उसने कहाः उसे तो तुम्हारे पास अल्लाह ही लायेगा, यदि वह चाहेगा और तुम (उसे) विवश करने वाले नहीं हो।');
INSERT INTO "al_umari" VALUES(11,34,'11:34','और मेरी शुभ-चिन्ता तुम्हें कोई लाभ नहीं पहुँचा सकती, यदि मैं तुम्हारा हित चाहूँ, जबकि अल्लाह तुम्हें कुपथ करना चाहता हो और तुम उसी की ओर लौटाये जाओगे।');
INSERT INTO "al_umari" VALUES(11,35,'11:35','क्या वे कहते हैं कि उसने ये बात स्वयं बना ली है? तुम कहो कि यदि मैंने इसे स्वयं बना लिया है, तो मेरा अपराध मुझी पर है और मैं निर्दोष हूँ, उस अपराध से, जो तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(11,36,'11:36','और नूह़ की ओर वह़्यी (प्रकाशना) की गयी कि तुम्हारी जाति मेंसे ईमान नहीं लायेंगे, उनके सिवा, जो ईमान ला चुके हैं। अतः उससे दुःखी न बनो, जो वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(11,37,'11:37','और हमारी आँखों के सामने हमारी वह़्यी के अनुसार एक नाव बनाओ और मुझसे उनके बारे में कुछ[ न कहना, जिन्होंने अत्याचार किया है। वास्तव में, वे डूबने वाले हैं। ');
INSERT INTO "al_umari" VALUES(11,38,'11:38','और वह नाव बनाने लगा और जबभी उसकी जाति के प्रमुख उसके पास से गुज़रते, तो उसकी हँसी उड़ाते। नूह़ ने कहाः यदि तुम हमारी हँसी उड़ाते हो, तो हमभी ऐसे ही (एक दिन) तुम्हारी हँसी उड़येंगे।');
INSERT INTO "al_umari" VALUES(11,39,'11:39','फिर तुम्हें शीघ्र ही ज्ञान हो जायेगा कि किसपर अपमानकारी यातना आयेगी और स्थायी दुःख किसपर उतरेगा?');
INSERT INTO "al_umari" VALUES(11,40,'11:40','यहाँ तक कि जब हमारा आदेश आ गया और तन्नूर उबलने लगा, तो हमने (नूह़ से) कहाः उसमें प्रत्येक प्रकार के जीवों के दो जोड़े रख लो और अपने परिजनों को, उनके सिवा, जिनके बारे में पहले बता दिया गाय है और जो ईमान लाये हैं और उसके साथ थोड़े ही ईमान लाये थे।');
INSERT INTO "al_umari" VALUES(11,41,'11:41','और उस (नूह़) ने कहाः इसमें सवार हो जाओ, अल्लाह के नाम ही से इसका चलना तथा इसे रुकना है। वास्तव में, मेरा पालनहार बड़ा क्षमाशील दयावान् है।');
INSERT INTO "al_umari" VALUES(11,42,'11:42','और वह उन्हें लिए पर्वत जैसी ऊँची लहरों में चलती रही और नूह़ ने अपने पुत्र को पुकारा, जबकि वह उनसे अलग थाः हे मेरे पुत्र! मेरे साथ सवार हो जा और काफ़िरों के साथ न रह।');
INSERT INTO "al_umari" VALUES(11,43,'11:43','उसने कहाः मैं किसी पर्वत की ओर शरण ले लूँगा, जो मुझे जल से बचा लेगा। नूह़ ने कहाः आज अल्लाह के आदेश (यातना) से कोई बचाने वाला नहीं, परनु जिसपर वह (अल्लाह) दया करे और दोनों के बीच एक लहर आड़े आ गयी और वह डूबने वालों में हो गया।');
INSERT INTO "al_umari" VALUES(11,44,'11:44','और कहा गयाः हे धरती! अपना जल निगल जा और हे आकाश! तू थम जा औ जल उतर गया और आदेश पूरा कर दिया गया और नाव "जूदी[" पर ठहर गयी और कहा गया कि अत्याचारियों के लिए (अल्लाह की दया से) दूरी है। ');
INSERT INTO "al_umari" VALUES(11,45,'11:45','तथा नूह़ ने अपने पालनहार से प्रार्थना की और कहाः मेरे पालनहार! मेरा पुत्र मेरे परिजनों में से है। निश्चय तेरा वचन सत्य है तथा तू ही सबसे अच्छा निर्णय करने वाला है।');
INSERT INTO "al_umari" VALUES(11,46,'11:46','उस (अल्लाह) ने उत्तर दियाः वह तेरा परिजन नहीं। (क्योंकि) वह कुकर्मी है। अतः मुझसे उस चीज़ का प्रश्न न कर, जिसका तुझे कोई ज्ञान नहीं। मैं तुझे बताता हूँ कि अज्ञानों में न हो जा।');
INSERT INTO "al_umari" VALUES(11,47,'11:47','नूह़ ने कहाः मेरे पालनहार! मैं तेरी शरण चाहता हूँ कि मैं तुझसे ऐसी चीज़ की माँग करूँ, जिस (की वास्तविक्ता) का मुझे कोई ज्ञान नहीं है[ और यदि तूने मुझे क्षमा नहीं किया और मुझपर दया न की, तो मैं क्षतिग्रस्तों में हो जाऊँगा। ');
INSERT INTO "al_umari" VALUES(11,48,'11:48','कहा गया कि हे नूह़! उतर जा, हमारी ओर से रक्षा ओर सम्पन्नता के साथ, अपने ऊपर तथा तेरे साथ के समुदायों के ऊपर और कुछ समुदाय ऐसे हैं, जिन्हें हम सांसारिक जीवन सामग्री प्रदान करेंगें, फिर उन्हें हमारी दुःखदायी यातना पहुँचेगी।');
INSERT INTO "al_umari" VALUES(11,49,'11:49','यही ग़ैब की बातें हैं, जिन्हें (हे नबी!) हम आपकी ओर प्रकाशना (वह़्यी) कर रहे हैं। इससे पूर्व न तो आप इन्हें जानते थे और न आपकी जाति। अतः आप सहन करें। वास्तव में, अच्छा परिणाम आज्ञाकारियों के लिए है।');
INSERT INTO "al_umari" VALUES(11,50,'11:50','और "आद" (जाति) की ओर उनके भाई हूद को भेजा, उसने कहाः हे मेरी जाति को लोगो! अल्लाह की इबादत (वंदना) करो। उसके सिवा तुम्हारा कोई पूज्य नहीं है। तुम इसके सिवा कुछ नहीं हो कि झूठी बातें घड़ने वाले हो[। ');
INSERT INTO "al_umari" VALUES(11,51,'11:51','हे मेरी जाति के लोगो! मैं तुमसे इसपर कोई बदला नहीं चाहता; मेरा पारिश्रमिक (बदला) उसी (अल्लाह) पर है, जिसने मुझे पैदा किया है। तो क्या तुम (इतनी) बात भी नहीं समझते[? ');
INSERT INTO "al_umari" VALUES(11,52,'11:52','हे मेरी जाति के लोगो! अपने पालनहार से क्षमा माँगो। फिर उसकी ओर ध्यानमग्न हो जाओ। वह आकाश से तुमपर धारा प्रवाह वर्षा करेगा और तुम्हारी शक्ति में अधिक शक्ति प्रदान करेगा और अपराधी होकर मुँह न फेरो।');
INSERT INTO "al_umari" VALUES(11,53,'11:53','उन्होंने कहाः हे हूद! तुम हमारे पास कोई स्पष्ट (खुला) प्रमाण नहीं लाये तथा हम तुम्हारी बात के कारण अपने पूज्यों को त्यागने वाले नहीं हैं और न हम तुम्हारा विश्वास करने वाले हैं।');
INSERT INTO "al_umari" VALUES(11,54,'11:54','हम तो यही कहेंगे कि तुझे हमारे किसी देवता ने बुराई के साथ पकड़ लिया है। हूद ने कहाः मैं अल्लाह को (गवाह) बनाता हूँ और तुम भी साक्षी रहो कि मैं उस शिर्क (मिश्रणवाद) से विरक्त हूँ, जो तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(11,55,'11:55','उस (अल्लाह) के सिवा। तुम सब मिलकर मेरे विरुध्द षड्यंत्र रच लो, फिर मुझे कुछ भी अवसर न दो[। ');
INSERT INTO "al_umari" VALUES(11,56,'11:56','वास्तव में, मैंने अल्लाह पर, जो मेरा पालनहार और तुम्हारा पालनहार है, भरोसा किया है। कोई चलने वाला जीव ऐसा नहीं, जो उसके अधिकार में न हो, वास्तव में, मेरा पालनहार सीधी राह[ पर है। ');
INSERT INTO "al_umari" VALUES(11,57,'11:57','फिर यदि तुम विमुख रह गये, तो मैंने तुम्हें वह उपदेश पहुँचा दिया है, जिसके साथ मुझे भेजा गया है और मेरा पालनहार तुम्हारा स्थान तुम्हारे सिवा किसी[ और जाति को दे देगा और तुम उसे कुछ हानि नहीं पहुँचा सकोगे, वास्तव में, मेरा पालनहार प्रत्येक चीज़ का रक्षक है। ');
INSERT INTO "al_umari" VALUES(11,58,'11:58','और जब हमारा आदेश आ पहुँचा, तो हमने हूद को और उनको, जो उसके साथ ईमान लाये, अपनी दया से बचा लिया और हमने उन्हें घोर यातना से बचा लिया।');
INSERT INTO "al_umari" VALUES(11,59,'11:59','वही (जाति) "आद" है, जिसने अपने पालनहार की आयतों (निशानियों) का इन्कार किया और उसके रसूलों की बात नहीं मानी और प्रत्येक सच के विरोधी के पीछे चलते रहे।');
INSERT INTO "al_umari" VALUES(11,60,'11:60','और इस संसार में धिक्कार उनके साथ लगा दी गयी तथा प्रलय के दिन भी लगी रहेगी। सुनो! आद ने अपने पालनहार को अस्वीकार किया। सुनो! हूद की जाति आद के लिए दूरी हो[! ');
INSERT INTO "al_umari" VALUES(11,61,'11:61','और समूद[ की ओर उनके भाई सालेह़ को भेजा। उसने कहाः हे मेरी जाति के लोगो! अल्लाह की इबादत (वंदना) करो, उसके सिवा तुम्हारा कोई पूज्य नहीं है। उसीने तुम्हें धरती से उत्पन्न किया और तुम्हें उसमें बसा दिया, अतः उससे क्षमा माँगो और उसी की ओर ध्यानमग्न हो जाओ। वास्तव में, मेरा पालनहार समीप है ( और दुआयें) स्वीकार करने वाला है ।');
INSERT INTO "al_umari" VALUES(11,62,'11:62','उन्होंने कहाः हे सालेह! हमारे बीच इससे पहले तुझसे बड़ी आशा थी, क्या तू हमें इस बात से रोक रहा है कि हम उसकी पूजा करें, जिसकी पूजा हमारे बाप-दादा करते रहे? तू जिस चीज़ (एकेश्वरवाद) की ओर बुला रहा है, वास्तव में, उसके बारे में हमें संदेह है, जिसमें हमें द्विधा है।');
INSERT INTO "al_umari" VALUES(11,63,'11:63','उस (सालेह़) ने कहाः हे मेरी जाति के लोगो! तुमने विचार किया कि यदि मैं अपने पालनहार की ओर से एक स्पष्ट खुले प्रमाण पर हूँ और उसने मुझे अपनी दया प्रदान की हो, तो कौन है, जो अल्लाह के मुक़ाबले में मेरी सहायता करेगा, यदि मैं उसकी अवज्ञा करूँ? तुम मुझे घाटे में डालने के सिवा कुछ नहीं दे सकते।');
INSERT INTO "al_umari" VALUES(11,64,'11:64','और हे मेरी जाति के लोगो! ये अल्लाह[ की ऊँटनी तुम्हारे लिए एक निशानी है, इसे छोड़ दो, अल्लाह की धरती में चरती फिरे और इसे कोई दुःख न पहुँचाओ, अन्यथा तुम्हें तुरन्त यातना पकड़ लेगी। ');
INSERT INTO "al_umari" VALUES(11,65,'11:65','तो उन्होंने उसे मार डाला। तब सालेह़ ने कहाः तुम अपने नगर में तीन दिन और आनन्द ले लो! ये वचन झूठ नहीं है।');
INSERT INTO "al_umari" VALUES(11,66,'11:66','फिर जब हमारा आदेश आ गया, तो हमने सालेह़ को और जो लोग उसके साथ ईमान लाये, अपनी दया से, उस दिन के अपमान से बचा लिया। वास्तव में, आपका पालनहार ही शक्तिशाली प्रभुत्वशाली है।');
INSERT INTO "al_umari" VALUES(11,67,'11:67','और अत्याचारियों को कड़ी ध्वनि ने पकड़ लिया और वे अपने घरों में औंधे पड़े रह गये।');
INSERT INTO "al_umari" VALUES(11,68,'11:68','जैसै वे वहाँ कभी बसे ही नहीं थे। सावधान! समूद ने अपने पालनहार को अस्वीकार कर दिया। सुन लो! समूद के लिए दूरी हो।');
INSERT INTO "al_umari" VALUES(11,69,'11:69','और हमारे फ़रिश्ते इब्राहीम के पास शुभ सूचना लेकर आये। उन्होंने सलाम किया, तो उसने उत्तर में सलाम किया। फिर देर न हुई कि वह एक भुना हुआ वछड़ा[ ले आये। ');
INSERT INTO "al_umari" VALUES(11,70,'11:70','फिर जब देखा कि उनके हाथ उसकी ओर नहीं बढ़ते, तो उनकी ओर से संशय में पड़ गया और उनसे दिल में भय का अनुभव किया। उन्होंने कहाः भय न करो। हम लूत[ की जाति की ओर भेजे गये हैं। ');
INSERT INTO "al_umari" VALUES(11,71,'11:71','और उस (इब्राहीम) की पत्नी खड़ी होकर सुन रही थी। वह हँस पड़ी[, तो उसे हमने इस्ह़ाक़ (के जन्म) की शुभ सूचना दी और इस्ह़ाक़ के पश्चात् याक़ुब की।');
INSERT INTO "al_umari" VALUES(11,72,'11:72','वह बोलीः हाय मेरा दुर्भाग्य! क्या मेरी संतान होगी, जबकि मैं बुढ़िया हूँ और मेरा ये पति भी बूढ़ा है? वास्तव में, ये बड़े आश्चर्य की बात है।');
INSERT INTO "al_umari" VALUES(11,73,'11:73','फ़रिश्तों ने कहाः क्या तू अल्लाह के आदेश से आश्चर्य करती है? हे घर वालो! तुम सब पर अल्लाह की दया तथा सम्पन्नता है, निसंदेह वह अति प्रशंसित, श्रेष्ठ है।');
INSERT INTO "al_umari" VALUES(11,74,'11:74','फिर जब इब्राहीम से भय दूर हो गया और उसे शुभ सूचना मिल गयी, तो वह लूत की जाति के बारे में हमसे आग्रह करने लगा[। ');
INSERT INTO "al_umari" VALUES(11,75,'11:75','वास्तव में, इब्राहीम बड़ा सहनशील, कोमल हृदय तथा अल्लाह की ओर ध्यानमग्न रहने वाला था।');
INSERT INTO "al_umari" VALUES(11,76,'11:76','(फ़रिश्तों ने कहाः) हे इब्राहीम! इस बात को छोड़ो, वास्तव में, तेरे पालनहार का आदेश[ आ गया है तथा उनपर ऐसी यातना आने वाली, है जो टलने वाली नहीं है। ');
INSERT INTO "al_umari" VALUES(11,77,'11:77','और जब हमारे फ़रिश्ते लूत के पास आये, तो उनका आना उसे बुरा लगा और उनके कारण व्याकूल हो गया और कहाः ये तो बड़ी विपता का[ दिन है। ');
INSERT INTO "al_umari" VALUES(11,78,'11:78','और उसकी जाति के लोग दौड़ते हुए उसके पास आ गये और इससे पूर्व वह कुकर्म[ किया करते थे। लूत ने कहाः हे मेरी जाति के लोगो! ये मेरी पुत्रियाँ हैं, वे तुम्हारे लिए अधिक पवित्र हैं, अतः अल्लाह से डरो और मेरे अतिथियों के बारे में मुझे अपमानित न करो। क्या तुममें कोई भला मनुष्य नहीं है?');
INSERT INTO "al_umari" VALUES(11,79,'11:79','उन लोगों ने कहाः तुमतो जानते ही हो कि हमारा तुम्हारी पुत्रियों में कोई अधिकार नहीं[। तथा वास्तव में, तुम जानते ही हो कि हम क्या चाहते हैं। ');
INSERT INTO "al_umari" VALUES(11,80,'11:80','उस (लूत) ने कहाः काश मेरे पास बल होता! या कोई दृढ़ सहारा होता, जिसकी शरण लेता!');
INSERT INTO "al_umari" VALUES(11,81,'11:81','फ़रिश्तों ने कहाः हे लूत! हम तेरे पालनहार के भेजे हुए (फ़रिश्ते) हैं। वे कदापि तुझतक नहीं पहुँच सकेंगे। जब कुछ रात रह जाये, तो अपने परिवार के साथ निकल जा और तुममें से कोई फिरकर न देखे। परन्तु तेरी पत्नी (साथ नहीं जायेगी)। उसपर भी वही बीतने वाला है, जो उनपर बीतेगा। उनकी यातना का निर्धारित समय प्रातः काल है। क्या प्रातः काल समीप नहीं है?');
INSERT INTO "al_umari" VALUES(11,82,'11:82','फिर जब हमारा आदेश आ गया, तो हमने उस बस्ती को तहस-नहस कर दिया और उनपर पकी हुई कंकरियों की बारिश कर दी।');
INSERT INTO "al_umari" VALUES(11,83,'11:83','जो तेरे पालनहार के यहाँ चिन्ह लगायी हुयी थी और वह[ (बस्ती) अत्याचारियों से कोई दूर नहीं है।');
INSERT INTO "al_umari" VALUES(11,84,'11:84','और मद्यन की ओर उनके भाई शोऐब को भेजा। उसने कहाः हे मेरी जाति के लोगो! अल्लाह की इबादत (वंदना) करो। उसके सिवा कोई तुम्हारा पूज्य नहीं है और नाप-तोल में कमी न करो[। मैं तुम्हें सम्पन्न देख रहा हूँ। इसलिए मुझे डर है कि तुम्हें कहीं यातना न घेर ले। ');
INSERT INTO "al_umari" VALUES(11,85,'11:85','हे मेरी जाति के लोगो! नाप-तोल न्यायपुर्वक पूरा करो और लोगों को उनकी चीज़ कम न दो तथा धरती में उपद्रव फैलाते न फिरो।');
INSERT INTO "al_umari" VALUES(11,86,'11:86','अल्लाह की दी हुई बचत, तुम्हारे लिए अच्छी है, यदि तुम ईमान वाले हो और मैं तुमपर कोई रक्षक नहीं हूँ।');
INSERT INTO "al_umari" VALUES(11,87,'11:87','उन्होंने कहाः हे शोऐब! क्या तेरी नमाज़ (इबादत) तुझे आदेश दे रही है कि हम उसे त्याग दें, जिसकी पूजा हमारे बाप-दादा करते रहे? अथवा अपने धनों में जो चाहें करें? वास्तव में, तू बड़ा ही सहनशील तथा भला व्यक्ति है!');
INSERT INTO "al_umari" VALUES(11,88,'11:88','शोऐब ने कहाः हे मेरी जाति के लोगो! तुम बताओ, यदि मैं अपने पालनहार की ओर से प्रत्यक्ष प्रमाण पर हूँ और उसने मुझे अच्छी जीविका प्रदान की हो, (तो कैसे तुम्हारा साथ दूँ?) मैं नहीं चाहता कि उसके विरुध्द करूँ, जिससे तुम्हें रोक रहा हूँ। मैं जहाँ तक हो सके, सुधार ही चाहता हूँ और ये जो कुछ करना चाहता हूँ, अल्लाह के योगदान पर निर्भर करता है। मैंने उसीपर भरोसा किया है और उसी की ओर ध्यानमग्न रहता हूँ।');
INSERT INTO "al_umari" VALUES(11,89,'11:89','हे मेरी जाति के लोगो! तुम्हें मेरा विरोध इस बात पर न उभार दे कि तुमपर वही यातना आ पड़े, जो नूह़ की जाति, हूद की जाति अथवा सालेह़ की जाति पर आई और लूत की जाति तुमसे कुछ दूर नहीं है।');
INSERT INTO "al_umari" VALUES(11,90,'11:90','और अपने पालनहार से क्षमा माँगो, फिर उसी की ओर ध्यानमग्न हो जाओ। वास्तव में, मेरा पालनहार अति क्षमाशील तथा प्रेम करने वाला है।');
INSERT INTO "al_umari" VALUES(11,91,'11:91','उन्होंने कहाः हे शोऐब! तुम्हारी बहुत-सी बात हम नहीं समझते और हम, तुम्हें अपने बीच निर्बल देख रहे हैं और यदि, भाई बन्धु न होते, तो हम तुम्हें पथराव करके मार डालते और तुम, हमपर कोई भारी तो नहीं हो।');
INSERT INTO "al_umari" VALUES(11,92,'11:92','शोऐब ने कहाः हे मेरी जाति के लोगो! क्या मेरे भाई बन्धु तुमपर अल्लाह से अधिक भारी हैं कि तुमने उसे पीठ पीछे डाल दिया है[? निश्चय मेरा पालनहार उसे (अपने ज्ञान के) घेरे में लिए हुए है, जो तुम कर रहे हो। ');
INSERT INTO "al_umari" VALUES(11,93,'11:93','और हे मेरी जाति के लोगो! तुम अपने स्थान पर काम करो, मैं (अपने स्थान पर) काम कर रहा हूँ। तुम्हें शीघ्र ही ज्ञान हो जायेगा कि किसपर ऐसी यातना आयेगी, जो उसे अपमानित कर दे तथा कौन झूठा है? तुम प्रतीक्षा करो, मैं (भी) तुम्हारे साथ प्रतीक्षा करने वाला हूँ।');
INSERT INTO "al_umari" VALUES(11,94,'11:94','और जब हमारा आदेश आ गया, तो हमने शोऐब को और जो उसके साथ ईमान लाये थे, अपनी दया से बचा लिया और अत्याचारियों को बड़ी ध्वनि ने पकड़ लिया। फिर वे अपने घरों में औंधे मुँह पड़े रह गये।');
INSERT INTO "al_umari" VALUES(11,95,'11:95','जैसे वे कभी उनमें बसे ही न रहे हों। सुन लो! मद्यन वाले भी वैसे ही दूर फेंक दिये गये, जैसे समूद दूर फेंक दिये गये।');
INSERT INTO "al_umari" VALUES(11,96,'11:96','और हमने मूसा को अपनी निशानियों (चमत्कार) तथा खुले तर्क के साथ भेजा।');
INSERT INTO "al_umari" VALUES(11,97,'11:97','फ़िरऔन और उसके प्रमुखों की ओर। तो उन्होंने फ़िरऔन की आज्ञा का अनुसरण (पालन) किया। जबकि फ़िरऔन की आज्ञा सुधरी हुई न थी।');
INSERT INTO "al_umari" VALUES(11,98,'11:98','वह प्रलय के दिन अपनी जाति के आगे चलेगा और उनको नरक में उतारेगा और वह क्या ही बुरा उतरने का स्थान है?');
INSERT INTO "al_umari" VALUES(11,99,'11:99','और वे धिक्कार के पीछे लगा दिये गये, इस संसार में भी और प्रलय के दिन भी। कैसा बुरा पुरस्कार है, जो उन्हें दिया जायेगा?');
INSERT INTO "al_umari" VALUES(11,100,'11:100','हे नबी! ये उन बस्तियों के समाचार हैं, जिनका वर्णन हम आपसे कर रहे हैं। उनमें से कुछ निर्जन खड़ी और कुछ उजड़ चुकी हैं।');
INSERT INTO "al_umari" VALUES(11,101,'11:101','और हमने उनपर अत्याचार नहीं किया, परन्तु उन्होंने स्वयं अपने ऊपर अत्याचार किया। तो उनके वे पूज्य, जिन्हें व अल्लाह के सिवा पुकार रहे थे, उनके कुछ काम नहीं आये, जब आपके पालनहार का आदेश आ गया और उन्होंने उन्हें हानि पहुँचाने के सिवा और कुछ नहीं किया[। ');
INSERT INTO "al_umari" VALUES(11,102,'11:102','और इसी प्रकार, तेरे पालनहार की पकड़ होती है, जब वह किसी अत्याचार करने वालों की बस्ती को पकड़ता है। निश्चय उसकी पकड़ दुःखदायी और कड़ी होती[ है। ');
INSERT INTO "al_umari" VALUES(11,103,'11:103','निश्चय इसमें एक निशानी है, उसके लिए जो परलोक की यातना से डरे। वह ऐसा दिन होगा, जिसके लिए सभी लोग एकत्र होंगे तथा उस दिन सब उपस्थित होंगे।');
INSERT INTO "al_umari" VALUES(11,104,'11:104','और उसे हम केवल एक निर्धारित अवधि के लिए देर कर रहे हैं।');
INSERT INTO "al_umari" VALUES(11,105,'11:105','जब वह दिन आ जायेगा, तो अल्लाह की अनुमति बिना कोई प्राणी बात नहीं करेगा, फिर उनमें से कुछ अभागे होंगे और कुछ भाग्यवान होंगे।');
INSERT INTO "al_umari" VALUES(11,106,'11:106','फिर जो भाग्यहीन होंगे, वही नरक में होंगे, उन्हीं की उसमें चीख और पुकार होगी।');
INSERT INTO "al_umari" VALUES(11,107,'11:107','वे उसमें सदावासी होंगे, जब तक आकाश तथा धरती अवस्थित हैं। परन्तु ये कि आपका पालनहार कुछ और चाहे। वास्तव में, आपका पालनहार जो चाहे, करने वाला है।');
INSERT INTO "al_umari" VALUES(11,108,'11:108','और जो भाग्यवान हैं, वे स्वर्ग ही में सदैव रहेंगे, जब तक आकाश तथा धरती स्थित हैं। परन्तु आपका पालनहर कुछ और चाहे, ये प्रदान है अनवरत (निरन्तर)।');
INSERT INTO "al_umari" VALUES(11,109,'11:109','अतः (हे नबी!) आप उसके बारे में किसी संदेह में न हों, जिसे वे पूजते हैं। वे उसी प्रकार पूजते हैं, जैसे इससे पहले इनके बाप-दादा पूजते[ रहे हैं। वस्तुतः, हम उन्हें उनका बिना किसी कमी के पूरा भाग देने वाले हैं। ');
INSERT INTO "al_umari" VALUES(11,110,'11:110','और हमने मूसा को पुस्तक (तौरात) प्रदान की। तो उसमें विभेद किया गया और यदि आपके पालनहार ने पहले से एक बात[ निश्चित न की होती, तो उनके बीच निर्णय कर दिया गया होता और वास्तव में, वे उसके बारे में संदेह और शंका में हैं।');
INSERT INTO "al_umari" VALUES(11,111,'11:111','और प्रत्येक को आपका पालनहार अवश्य उनके कर्मों का पूरा बदला देगा। क्योंकि वह उनके कर्मों से सूचित है।');
INSERT INTO "al_umari" VALUES(11,112,'11:112','अतः (हे नबी!) जैसे आपको आदेश दिया गया है, उसपर सुदृढ़ रहिये और वे भी जो आपके साथ तौबा (क्षमा याचना) करके हो लिए हैं और सीमा का उल्लंघन न[ करो, क्योंकि वह (अल्लाह) तुम्हारे कर्मों को देख रहा है। ');
INSERT INTO "al_umari" VALUES(11,113,'11:113','और अत्याचारियों की ओर न झुक पड़ो। अन्यथा तुम्हें भी अग्नि स्पर्श कर लेगी और अल्लाह के सिवा तुम्हारा कोई सहायक नहीं, फिर तुम्हारी सहायता नहीं की जायेगी।');
INSERT INTO "al_umari" VALUES(11,114,'11:114','तथा आप नमाज़ की स्थापना करें, दिन के सिरों पर और कुछ रात बीतने[ पर। वास्तव में, सदाचार दुराचारों को दूर कर देते हैं। ये एक शिक्षा है, शिक्षा ग्रहण करने वालों के लिए।');
INSERT INTO "al_umari" VALUES(11,115,'11:115','तथा आप धैर्य से काम लें, क्योंकि अल्लाह सदाचारियों का प्रतिफल व्यर्थ नहीं करता।');
INSERT INTO "al_umari" VALUES(11,116,'11:116','तो तुमसे पहले युगों में ऐसे सदाचारी क्यों नहीं हुए, जो धरती में उपद्रव करने से रोकते? परन्तु ऐसा बहुत थोड़े युगों में हुआ, जिन्हें हमने बचा लिया और अत्याचारी उस स्वाद के पीछे पड़े रहे, जो धन-धान्य दिये गये थे और वे अपराधि बन कर रहे।');
INSERT INTO "al_umari" VALUES(11,117,'11:117','और आपका पालनहार ऐसा नहीं है कि बस्तियों को अत्याचार से ध्वस्त कर दे, जबकि उनके वासी सुधारक हूँ।');
INSERT INTO "al_umari" VALUES(11,118,'11:118','और यदि आपका पालनहार चाहता, तो सब लोगों को एक समुदाय बना देता और वे सदा विचार विरोधी रहेंगे।');
INSERT INTO "al_umari" VALUES(11,119,'11:119','परन्तु जिसपर आपका पालनहार दया करे और इसीके लिए उन्हें पैदा किया है[ और आपके पालनहार की बात पूरी हो गयी कि मैं नरक को सब जिन्नों तथा मानवों से अवश्य भर दूँगा ।');
INSERT INTO "al_umari" VALUES(11,120,'11:120','और (हे नबी!) ये नबियों की सब कथाएं हम आपको सुना रहे हैं, जिनके द्वारा आपके दिल को सुदृढ़ कर दें और इस विषय में आपके पास सत्य आ गया है और ईमान वालों के लिए एक शिक्षा और चेतावनी है।');
INSERT INTO "al_umari" VALUES(11,121,'11:121','और (हे नबी!) आप उनसे कह दें, जो ईमान नहीं लाते कि तुम अपने स्थान पर काम करते रहो। हम अपने स्थान पर काम करते हैं।');
INSERT INTO "al_umari" VALUES(11,122,'11:122','तथा तुम प्रतीक्षा[ करो, हमभी प्रतीक्षा करने वाले हैं। ');
INSERT INTO "al_umari" VALUES(11,123,'11:123','अल्लाह ही के अधिकार में आकाशों तथा धरती की छिपी हुई चीज़ों का ज्ञान है और प्रत्येक विषय उसी की ओर लोटाये जाते हैं। अतः आप उसी की इबादत (वंदना) करें और उसीपर निर्भर रहें। आपका पालनहार उससे अचेत नहीं है, जो तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(12,1,'12:1','अलिफ, लाम, रा। ये खुली पुस्तक की आयतें हैं।');
INSERT INTO "al_umari" VALUES(12,2,'12:2','हमने इस क़ुर्आन को अरबी में उतारा है, ताकि तुम समझो[। ');
INSERT INTO "al_umari" VALUES(12,3,'12:3','(हे नबी!) हम बहुत अच्छी शैली में आपकी ओर इस क़ुर्आन की वह़्यी द्वारा आपसे इस कथा का वर्णन कर रहे हैं। अन्यथा आप (भी) इससे पूर्व (इससे) असूचित थे।');
INSERT INTO "al_umari" VALUES(12,4,'12:4','जब यूसुफ़ ने अपने पिता से कहाः हे मेरे पिता! मैंने स्वप्न देखा है कि ग्यारह सितारे, सूर्य तथा चाँद मुझे सज्दा कर रहे हैं।');
INSERT INTO "al_umari" VALUES(12,5,'12:5','उसने कहाः हे मेरे पुत्र! अपना स्वप्न अपने भाईयों को न बताना[, अन्यथा वे तेरे विरुध्द षड्यंत्र रचेंगे। वास्तव में, शैतान मानव का खुला शत्रु है। ');
INSERT INTO "al_umari" VALUES(12,6,'12:6','और ऐसा ही होगा, तेरा पालनहार तुझे चुन लेगा तथा तुझे बातों का अर्थ सिखायेगा और तुझपर और याक़ूब के घराने पर अपना पुरस्कार पूरा करेगा[। जैसे इससे पहले तेरे पूर्वजों इब्राहीम और इस्ह़ाक़ पर पूरा किया। वास्तव में, तेरा पालनहार बड़ा ज्ञानी तथा गुणी है। ');
INSERT INTO "al_umari" VALUES(12,7,'12:7','वास्तव में, यूसुफ़ और उसके भाईयों (की कथा) में पूछने वालों के[ लिए कई निशानियाँ हैं। ');
INSERT INTO "al_umari" VALUES(12,8,'12:8','जब उन (भाईयों) ने कहाः यूसुफ़ और उसका भाई हमारे पिता को, हमसे अधिक प्रिय हैं। जबकि हम एक गिरोह हैं। वास्तव में, हमारे पिता खुली गुमराही में हैं।');
INSERT INTO "al_umari" VALUES(12,9,'12:9','यूसुफ़ को वध कर दो या उसे किसी धरती में फेंक दो। इससे तुम्हारे पिता का ध्यान केवल तुम्हारी तरफ हो जायेगा और इसके पश्चात् पवित्र बन जाओ।');
INSERT INTO "al_umari" VALUES(12,10,'12:10','उनमें से एक ने कहाः यूसुफ़ को वध न करो, उसे किसी अन्धे कुएँ में डाल दो, उसे कोई क़ाफ़िला निकाल ले जायेगा, यदि कुछ करने वाले हो।');
INSERT INTO "al_umari" VALUES(12,11,'12:11','उन्होंने कहाः हे हमारे पिता! क्या बात है कि यूसुफ़ के विषय में आप हमपर भरोसा नहीं करते? जबकि हम उसके शुभचिन्तक हैं।');
INSERT INTO "al_umari" VALUES(12,12,'12:12','उसे कल हमारे साथ वन में भेज दें। वह खाये-पिये और खेले-कूदे और हम उसके रक्षक (प्रहरी) हैं।');
INSERT INTO "al_umari" VALUES(12,13,'12:13','उस (पिता) ने कहाः मुझे बड़ी चिन्ता इस बात की है कि तुम उसे ले जाओ और मैं डरता हूँ कि उसे भेड़िया न खा जाये और तुम उससे असावधान रह जाओ।');
INSERT INTO "al_umari" VALUES(12,14,'12:14','सब (भाईयों) ने कहाः यदि उसे भेड़िया खा गया, जबकि हम एक गिरोह हैं, तो वास्तव में, हम बड़े विनाश में हैं।');
INSERT INTO "al_umari" VALUES(12,15,'12:15','फिर जब वे उसे ले गये और निश्चय किया कि उसे अंधे कुएं में डाल दें और हमने उस (यूसुफ़) की ओर वह़्यी की कि तुम अवश्य इन्हें इनका कर्म बताओगे और वे कुछ जानते न होंगे।');
INSERT INTO "al_umari" VALUES(12,16,'12:16','और वे संध्या को रोते हुए अपने पिता के पास आये।');
INSERT INTO "al_umari" VALUES(12,17,'12:17','सबने कहाः हे पिता! हम आपस में दौड़ करने लगे और यूसुफ को अपने सामान के पास छोड़ दिया और उसे भेड़िया खा गया और आप तो हमारा विश्वास करने वाले नहीं हैं, यद्यपि हम सच ही क्यों न बोल रहे हों।');
INSERT INTO "al_umari" VALUES(12,18,'12:18','और वे यूसुफ़ के कुर्ते पर झूठा रक्त[ लगाकर लाये। उसने कहाः बल्कि तुम्हारे मन ने तुम्हारे लिए एक सुन्दर बात बना ली है! तो अब धैर्य धारण करना ही उत्तम है और उसके संबन्ध में जो बात तुम बना रहे हो, अल्ला ही से सहायता माँगनी है। ');
INSERT INTO "al_umari" VALUES(12,19,'12:19','और एक क़ाफ़िला आया। उसने अपने पानी भरने वाले को भेजा, उसने अपना डोल डाला, तो पुकाराः शुभ हो! ये तो एक बालक है और उसे व्यपारिक सामग्री समझकर छुपा लिया और अल्लाह भली-भाँति जानने वाला था जो वे कर रहे थे।');
INSERT INTO "al_umari" VALUES(12,20,'12:20','और उसे तनिक मूल्य; कुछ गिनती के दिरहमों में बेच दिया और वे उसके बारे में कुछ अधिक की इच्छा नहीं रखते थे।');
INSERT INTO "al_umari" VALUES(12,21,'12:21','और मिस्र के जिस व्यक्ति ने उसे खरीदा, उसने अपनी पत्नी से कहाः इसे आदर-मान से रखो। संभव है ये हमें लाभ पहुँचाये, अथवा हम इसे अपना पुत्र बना लें। इस प्रकार उसे हमने स्थान दिया और ताकि उसे बातों का अर्थ सिखायें और अल्लाह अपना आदेश पूरा करके रहता है। परन्तु अधिक्तर लोग जानते नहीं हैं।');
INSERT INTO "al_umari" VALUES(12,22,'12:22','और जब वह जवानी को पहुँचा, तो हमने उसे निर्णय करने की शक्ति तथा ज्ञान प्रदान किया और इसी प्रकार हम सदाचारियों को प्रतिफल (बदला) देते हैं।');
INSERT INTO "al_umari" VALUES(12,23,'12:23','और वह जिस स्त्री[ के घर में था, उसने उसके मन को रिझाया और द्वार बन्द कर लिए और बोलीः "आ जाओ"। उसने कहाः अल्लाह की शरण! वह मेरा स्वामी है। उसने मुझे अच्छा स्थान दिया है। वास्तव में, अत्याचारी सफल नहीं होते। ');
INSERT INTO "al_umari" VALUES(12,24,'12:24','और उस स्त्री ने उसकी इच्छा की और वह (यूसुफ़) भी उसकी इच्छा करते, यदि अपने पालनहार का प्रमाण न देथ लेते[। इस प्रकार, हमने (उसे सावधान) किया ताकि उससे बुराई तथा निर्लज्जा को दूर कर दें। वास्तव में, वह हमारे शुध्द भक्तों में था। ');
INSERT INTO "al_umari" VALUES(12,25,'12:25','और दोनों द्वार की ओर दौड़े और उस स्त्री ने उसका कुर्ता पीछे से फाड़ दिया और दोनों ने उसके पति को द्वार के पास पाया। उस (स्त्री) ने कहाः जिसने तेरी पत्नी के साथ बुराई का निश्चय किया, उसका दण्ड इसके सिवा क्या है कि उसे बंदी बना दिया जाये अथवा उसे दुःखदायी यातना (दी जाये)?');
INSERT INTO "al_umari" VALUES(12,26,'12:26','उसने कहाः इसीने मुझे रिझाना चाहा था और उस स्त्री के घराने से एक साक्षी ने साक्ष्य दिया कि यदि उसका कुर्ता आगे से फाड़ा गया है, तो वह सच्ची है तथा वह झूठा है।');
INSERT INTO "al_umari" VALUES(12,27,'12:27','और यदि उसका कुर्ता पीछे से फाड़ा गया है, तो वह झूठी और वह (यूसुफ़) सच्चा है।');
INSERT INTO "al_umari" VALUES(12,28,'12:28','फिर जब उस (पति) ने देखा कि उसका कुर्ता पाछे से फाड़ा गया है, तो कहाः वास्तव में, ये तुम स्त्रियों की चालें हैं और तुम्हारी चालें बड़ी घोर होती हैं।');
INSERT INTO "al_umari" VALUES(12,29,'12:29','हे यूसुफ़! तुम इस बात को जाने दो और (हे स्त्री!) तू अपने पाप की क्षमा माँग, वास्तव में, तू पापियों में से है।');
INSERT INTO "al_umari" VALUES(12,30,'12:30','नगर की कुछ स्त्रियों ने कहाः अज़ीज़ (प्रमुख अधिकारी) की पत्नी, अपने दास को रिझा रही है! उसे प्रेम ने मुग्ध कर दिया है। हमारे विचार में वह खुली गुमराही में है।');
INSERT INTO "al_umari" VALUES(12,31,'12:31','फिर जब उसने उन स्त्रियों की मक्कारी की बात सुनी, तो उन्हें बुला भेजा और उनके (आतिथ्य) के लिए गाव-तकिये लगवाये और प्रत्येक स्त्री को एक छुरी दे दी[। उसने (यूसुफ़ से) कहाः इनके समक्ष "निकल आ"। फिर जब उन स्त्रियों ने उसे देखा, तो चकित (दंग) होकर अपने हाथ काट बैठीं तथा पुकार उठीं: अल्लाह पवित्र है! ये मनुष्य नहीं ये तो कोई सम्मानित फ़रिश्ता है। ');
INSERT INTO "al_umari" VALUES(12,32,'12:32','उसने कहाः यही वह है, जिसके बारे में तुमने मेरी निंदा की है। वास्तव में, मैंने ही उसे रिझाया था। मगर वह बच निकला और यदि, वह मेरी बात न मानेगा, तो अवश्य बंदी बना दिया जायेगा और अपमानितों में हो जायेगा।');
INSERT INTO "al_umari" VALUES(12,33,'12:33','यूसुफ़ ने प्रार्थना कीः हे मेरे पालनहार! मुझे क़ैद उससे अधिक प्रिय है, जिसकी ओर ये औरतें मुझे बुला रही हैं और यदि तूने मुझसे इनके छल को दूर नहीं किया, तो मैं इनकी ओर झुक पड़ूँगा और अज्ञानों में से हो जाऊँगा।');
INSERT INTO "al_umari" VALUES(12,34,'12:34','तो उसके पालनहार ने उसकी प्रार्थना स्वीकार कर ली और उससे उनके छल को दूर कर दिया। वास्तव में, वह बड़ा सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(12,35,'12:35','फिर उन लोगों[ ने उचित समझा, इसके पश्चात् कि निशानियाँ देख लीं कि उस (यूसुफ़) को एक अवधि तक के लिए बंदी बना दें।');
INSERT INTO "al_umari" VALUES(12,36,'12:36','और उसके साथ क़ैद में दो युवकों ने प्रवेश किया। उनमें से एक ने कहाः मैंने स्वप्न देखा है कि शराब निचोड़ रहा हूँ और दूसरे ने कहाः मैंने स्वप्न देखा है कि अपने सिर के ऊपर रोटी उठाये हुए हूँ, जिसमें से पक्षी खा रहे हैं। हमें इसका अर्थ (स्वप्नफल) बता दो। हम देख रहे हैं कि तुम सदाचारियों में से हो।');
INSERT INTO "al_umari" VALUES(12,37,'12:37','यूसुफ़ ने कहाः तुम्हारे पास तुम्हारा वह भोजन नहीं आयेगा, जो तुम दोनों को दिया जाता है, परन्तु मैं तुम दोनों को उसका अर्थ (फल) बता दूँगा; ये उन बातों में से है, जो मेरे पालनहार ने मुझे सिखायी हैं। मैंने उस जाति का धर्म तज दिया है, जो अल्लाह पर ईमान नहीं रखती और वही परलोक को नकारने वाले हैं।');
INSERT INTO "al_umari" VALUES(12,38,'12:38','और अपने पूर्वजों; इब्राहीम, इस्ह़ाक़ और याक़ूब के धर्म का अनुसरण किया है। हमारे लिए वैध नहीं कि किसी चीज़ को अल्लाह का साझी बनायें। ये अल्लाह की दया है, हमपर और लोगों पर। परन्तु अधिक्तर लोग कृतज्ञ नहीं होते[। ');
INSERT INTO "al_umari" VALUES(12,39,'12:39','हे मेरे क़ैद के दोनों साथियो! क्या विभिन्न पूज्य उत्तम हैं या एक प्रभुत्वशाली अल्लाह??');
INSERT INTO "al_umari" VALUES(12,40,'12:40','तुम अल्लाह के सिवा जिसकी इबादत (वंदना) करते हो, वे केवल नाम हैं, जो तुमने और तुम्हारे बाप-दादा ने रख लिए हैं। अल्लाह ने उनका कोई प्रमाण नहीं उतारा है। शासन तो केवल अल्लाह का है। उसने आदेश दिया है कि उसके सिवा किसी की इबादत (वंदना) न करो। यही सीधा धर्म है। परन्तु अधिक्तर लोग नहीं जानते हैं।');
INSERT INTO "al_umari" VALUES(12,41,'12:41','हे मेरे क़ैद के दोनों साथियो! रहा तुममें से एक, तो वह अपने स्वामि को शराब पिलायेगा तथा दूसरा, तो उसको फाँसी दी जायेगी और पक्षी उसके सिर में से खायेंगे। उसका निर्णय कर दिया गया है, जिसके संबन्ध में तुम दोनों प्रश्न कर रहे थे।');
INSERT INTO "al_umari" VALUES(12,42,'12:42','और उससे कहा, जिसे समझा कि वह उन दोनों में से मुक्त होने वाला हैः मेरी चर्चा अपने स्वामी के पास कर देना। तो शैतान ने उसे अपने स्वामी के पास, उसकी चर्चा करने को भुला दिया। अतः वह (यूसुफ़) कई वर्ष क़ैद में रह गया।');
INSERT INTO "al_umari" VALUES(12,43,'12:43','और (एक दिन) राजा ने कहाः मैं सात मोटी गायों को सपने में देखता हूँ, जिनको सात दुबली गायें खा रही हैं और सात हरी बालियाँ हैं और दूसरी सात सूखी हैं। हे प्रमुखो! मुझे मेरे स्वप्न के सम्बंध में बताओ, यदि तुम स्वप्न-फल बता सकते हो?');
INSERT INTO "al_umari" VALUES(12,44,'12:44','सबने कहाः ये तो उलझे स्वप्न की बातें हैं और हम ऐसे स्वप्नों का अर्थ (फल) नहीं जानते।');
INSERT INTO "al_umari" VALUES(12,45,'12:45','और उसने कहा, जो दोनों में से मुक्त हुआ था और उसे एक अवधि के पश्चात्, बात याद आयी थीः मैं तुम्हें इसका फल (अर्थ) बता दूँगा, तुम मुझे भेज[ दो। ');
INSERT INTO "al_umari" VALUES(12,46,'12:46','हे यूसुफ़! हे सत्यवादी! हमें सात मोटी गायों के बारे में बताओ, जिन्हें सात दुबली गायें खा रही हैं और सात हरी बालियाँ हैं और सात सूखी, ताकि लोगों के पास वापस जाऊँ और ताकि वे जान[ लें। ');
INSERT INTO "al_umari" VALUES(12,47,'12:47','यूसुफ़ ने कहाः तुम सात वर्ष निरन्तर खेती करते रहोगे। तो जो कुछ काटो, उसे उसकी बाली में छोड़ दो, परन्तु थोड़ा जिसे खाओगे। ( उसे बालियों से निकाल लो।)');
INSERT INTO "al_umari" VALUES(12,48,'12:48','फिर इसके पश्चात् सात कड़े अकाल के वर्ष होंगे, जो उसे खा जायेंगे, जो तुमने उनके लिए पहले से रखा है, परन्तु उसमें से थोड़ा, जिसे तुम सुरक्षित रखोगे।');
INSERT INTO "al_umari" VALUES(12,49,'12:49','फिर इसके पश्चात् एक ऐसा वर्ष आयेगा, जिसमें लोगों पर जल बरसाया जायेगा तथा उसीमें वे (रस) निचोड़ेंगे।');
INSERT INTO "al_umari" VALUES(12,50,'12:50','और राजा ने कहाः उसे मेरे पास लाओ और जब यूसुफ़ के पास भेजा हुआ आया, तो आपने उससे कहा कि अपने स्वामी के पास वापस जाओ[ और उससे पूछो कि उन स्त्रियों की क्या दशा है, जिन्होंने अपने हाथ काट लिए थे? वास्तव में, मेरा पालनहार उन स्त्रियों के छल से भली-भाँति अवगत है। ');
INSERT INTO "al_umari" VALUES(12,51,'12:51','(राजा) ने उन स्त्रियों से पूछाः तुम्हारा क्या अनुभव है, उस समय का, जब तुमने यूसुफ़ के मन को रिझाया था? सबने कहाः अल्लाह पवित्र है! उसपर हमने कोई बुराई का प्रभाव नहीं जाना। तब अज़ीज़ की पत्नी बोल उठीः अब सत्य उजागर हो गया, वास्तव में, मैंने ही उसके मन को रिझाया था और निःसंदेह वह सत्यवादियों में है[। ');
INSERT INTO "al_umari" VALUES(12,52,'12:52','ये (यूसुफ़ ने) इसलिए किया, ताकि उसे (अज़ीज़ को) विश्वास हो जाये, कि मैंने गुप्त रूप से उसके साथ विश्वास घात नहीं किया और वस्तुतः, अल्लाह विश्वासघातियों से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(12,53,'12:53','और मैं अपने मन को निर्दोश नहीं कहता, मन तो बुराई पर उभारता है; परन्तु जिसपर मेरा पालनहार दया कर दे। मेरा पालनहार अति क्षमाशील तथा दयावान् है।');
INSERT INTO "al_umari" VALUES(12,54,'12:54','राजा ने कहाः उसे मेरे पास लाओ, उसे मैं अपने लिए विशेष कर लूँ और जब (राजा ने) उस (यूसुफ़) से बात की, तो कहाः वस्तुतः तू आज हमारे पास आदर्णीय भरोसा, करने योग्य है।');
INSERT INTO "al_umari" VALUES(12,55,'12:55','उस (यूसुफ़) ने कहाः मुझे देश का कोषाधिकारी बना दीजिये। वास्तव में मैं, रखवाला, बड़ा ज्ञानी हूँ।');
INSERT INTO "al_umari" VALUES(12,56,'12:56','और इस प्रकार, हमने यूसुफ को उस धरती (देश) में अधिकार दिया, वह उसमें जहाँ चाहे, रहे। हम अपनी दया जिसे चाहें, प्रदान करते हैं और सदाचारियों का प्रतिफल व्यर्थ नहीं करते।');
INSERT INTO "al_umari" VALUES(12,57,'12:57','और निश्चय परलोक का प्रतिफल उन लोगों के लिए उत्तम है, जो ईमान लाये और अल्लाह से डरते रहे।');
INSERT INTO "al_umari" VALUES(12,58,'12:58','और यूसुफ़ के भाई आये[ तथा उसके पास उपस्थित हुए और उसने उन्हें पहचान लिया तथा वे उससे अपरिचित रह गये। ');
INSERT INTO "al_umari" VALUES(12,59,'12:59','और जब उनका सामान तैयार कर दिया, तो कहाः अपने सौतीले भाई[ को लाना। क्या तुम नहीं देखते कि मैं पूरा माप देता हूँ तथा उत्तम अतिथि-सत्कार करने वाला हूँ? ');
INSERT INTO "al_umari" VALUES(12,60,'12:60','फिर यदि, तुम उसे मेरे पास नहीं लाये, तो मेरे यहाँ तुम्हारे लिए कोई माप नहीं और न तुम मेरे समीप होगे।');
INSERT INTO "al_umari" VALUES(12,61,'12:61','वे बोलेः हम उसके पिता को इसकी प्रेरणा देंगे और हम अवश्य ऐसा करने वाले हैं।');
INSERT INTO "al_umari" VALUES(12,62,'12:62','और यूसुफ़ ने अपने सेवकों को आदेश दियाः उनका मूलधन[ उनकी बोरियों में रख दो, संभवतः वे उसे पहचान लें, जब अपने परिजनों में जायें और संभवतः वापस आयें। ');
INSERT INTO "al_umari" VALUES(12,63,'12:63','फिर जब अपने पिता के पास लौटकर गये, तो कहाः हमारे पिता! हम से भविष्य में (अन्न) रोक दिया गया है। अतः हमारे साथ हमारे भाई को भेजें कि हम सब अन्न (ग़ल्ला) लायें और (विशवास कीजीए कि) हम उसके रक्षक हैं।');
INSERT INTO "al_umari" VALUES(12,64,'12:64','उस (पिता) ने कहाः क्या मैं उसके लिए तुमपर वैसे ही विश्वास कर लूँ, जैसे इसके पहले उसके भाई (यूसुफ़) के बारे में विश्वास कर चुका हूँ? तो अल्लाह ही उत्तम रक्षक और वही सर्वाधिक दयावान् है।');
INSERT INTO "al_umari" VALUES(12,65,'12:65','और जब उन्होंने अपना सामान खोला, तो पाया कि उनका मूलधन उन्हें फेर दिया गया है, उन्होंने कहाः हे हमारे पिता! हमें और क्या चाहिए? ये हमारा धन, हमें फेर दिया गया है? हम अपने घराने के लिए ग़ल्ले (अन्न) लायेंगे और एक ऊँट का बोझ अधिक लायेंगे[, ये माप (अन्न) बहुत थोड़ा है। ');
INSERT INTO "al_umari" VALUES(12,66,'12:66','उस (पिता) ने कहाः मैं कदापि उसे तुम्हारे साथ नहीं भेजूँगा, यहाँ तक कि अल्लाह के नाम पर मुझे दृढ़ वचन दो कि उसे मेरे पास अवश्य लाओगे, परन्तु ये कि तुमको घेर लिया[ जाये और जब उन्होंने अपना दृढ़ वचन दिया, तो कहा, अल्लाह ही तुम्हारी बात (वचन) का निरीक्षक है। ');
INSERT INTO "al_umari" VALUES(12,67,'12:67','और ( जब वे जाने लगे) तो उस (पिता) ने कहाः हे मेरे पुत्रो! तुम एक द्वार से मिस्र में प्रवेश न करना, बल्कि विभिन्न द्वारों से प्रवेश करना और मैं तुम्हें किसी चीज़ से नहीं बचा सकता, जो अल्लाह की ओर से हो और आदेश तो अल्लाह का चलता है, मैंने उसीपर भरोसा किया तथा उसीपर भरोसा करने वालों को भरोसा करना चाहिए।');
INSERT INTO "al_umari" VALUES(12,68,'12:68','और जब उन्होंने (मिस्र में) प्रवेश किया, जैसे उनके पिता ने आदेश दिया था, तो ऐसा नहीं हुआ कि वह उन्हें अल्लाह से कुछ बचा सके; परन्तु ये याक़ूब के दिल में एक विचार उत्पन्न हुआ, जिसे उसने पूरा कर लिया[ और वास्तव में, वह उसका ज्ञानी था, जो ज्ञान हमने उसे दिया था। परन्तु अधिकांश लोग इस (की वास्तविक्ता) का ज्ञान नहीं रखते। ');
INSERT INTO "al_umari" VALUES(12,69,'12:69','और जब वे यूसुफ़ के पास पहुँचे, तो उसने अपने भाई को अपनी शरण में ले लिया (और उससे) कहाः मैं तेरा भाई (यूसुफ़) हूँ। अतः उससे उदासीन न हो, जो (दुरव्यवहार) वे करते आ रहे हैं।');
INSERT INTO "al_umari" VALUES(12,70,'12:70','फिर जब उस (यूसुफ़) ने उनका सामान तैयार करा दिया, तो प्याला अपने भाई के सामान में रख दिया। फिर एक पुकारने वाले ने पुकाराः हे क़ाफ़िले वालो! तुम लोग तो चोर हो!');
INSERT INTO "al_umari" VALUES(12,71,'12:71','उन्होंने फिरकर कहाः तुम क्या खो रहे हो?');
INSERT INTO "al_umari" VALUES(12,72,'12:72','उन (कर्मचारियों) ने कहाः हमें राजा का प्याला नहीं मिल रहा है और जो उसे ले आये, उसके लिए एक ऊँट का बोझ है और मैं उसका प्रतिभु[ हूँ। ');
INSERT INTO "al_umari" VALUES(12,73,'12:73','उन्होंने कहाः तुम जानते हो कि हम इस देश में उपद्रव करने नहीं आये हैं और न हम चोर ही हैं।');
INSERT INTO "al_umari" VALUES(12,74,'12:74','उन लोगों ने कहाः तो यदि तुम झूठे निकले, तो उसका दण्ड क्या होगा[? ');
INSERT INTO "al_umari" VALUES(12,75,'12:75','उन्होंने कहाः उसका दण्ड! जिसके सामान में (प्याला) पाया जाये, वही उसका दण्ड होगा। इसी प्रकार, हम अत्याचारियों को दण्ड देते हैं[। ');
INSERT INTO "al_umari" VALUES(12,76,'12:76','फिर उसने खोज का आरंभ उस (यूसुफ़) के भाई की बोरी से पहले, उनकी बोरियों से किया। फिर उसे उस (बिनयामीन) की बोरी से निकाल लिया। इस प्रकार, हमने यूसुफ़ के लिए उपाय किया। वह राजा के नियमानुसार अपने भाई को नहीं रख सकता था, परन्तु ये कि अल्लाह चाहता। हम जिसका चाहें मान सम्मान ऊँचा कर देते हैं और प्रत्येक ज्ञानी से ऊपर एक बड़ा ज्ञानी[ है। ');
INSERT INTO "al_umari" VALUES(12,77,'12:77','उन भाईयों ने कहाः यदि उसने चोरी की है, तो उसका एक भाई भी इससे पहले चोरी कर चुका है। तो यूसुफ़ ने ये बात अपने दिल में छुपा ली और उसे उनके लिए प्रकट नहीं किया। (यूसुफ़ ने) कहाः सबसे बुरा स्थान तुम्हारा है और अल्लाह उसे अधिक जानता है, जो तुम कह रहे हो।');
INSERT INTO "al_umari" VALUES(12,78,'12:78','उन्होंने कहाः हे अज़ीज़[! उसका पिता बहुत बूढ़ा है। अतः हम में से किसी एक को उसके स्थान पर ले लो। वास्तव में, हम आपको परोपकारी देख रहे हैं। ');
INSERT INTO "al_umari" VALUES(12,79,'12:79','उस (यूसुफ) ने कहाः अल्लाह की शरण कि हम (किसी अन्य को) पकड़ लें, उसके सिवा जिसके पास अपना सामान पाया है। (यदि ऐसा न करें) तो हम वास्तव में अत्याचारी होंगे।');
INSERT INTO "al_umari" VALUES(12,80,'12:80','फिर जब उससे निराश हो गये, तो एकान्त में होकर प्रामर्श करने लगे। उनके बड़े ने कहाः क्या तुम नहीं जानते कि तुम्हारे पिता ने तुमसे अल्लाह को साक्षी बनाकर दृढ़ वचन लिया था? और इससे पहले जो अपराध तुमने यूसुफ के बारे में किया है? तो मैं इस धरती (मिस्र) से नहीं जाऊँगा, जब तक मुझे मेरे पिता अनुमति न दे दें अथवा अल्लाह मेरे लिए निर्णय न कर दे और वही सबसे अच्छा निर्णय करने वाला है।');
INSERT INTO "al_umari" VALUES(12,81,'12:81','तुम अपने पिता की ओर लौट जाओ और कहो कि हमारे पिता! आपके पुत्र ने चोरी की है और हमने वही साक्ष्य दिया, जिसे हमने[ जाना और हम ग़ैब के रखवाले नहीं थे।');
INSERT INTO "al_umari" VALUES(12,82,'12:82','आप उस बस्ती वालों से पूछ लें, जिसमें हम थे और उस क़ाफ़िले से जिसमें हम आये हैं और वास्तव में हम सच्चे हैं।');
INSERT INTO "al_umari" VALUES(12,83,'12:83','उस (पिता) ने कहाः ऐसा नहीं है, बल्कि तुम्हारे दिलों ने एक बात बना ली है। इसलिए अब सहन करना ही उत्तम है, संभव है कि अल्लाह उनसब को मेरे पास वापस ले आए, वास्तव में, वही जानने वाला, तत्वदर्शी है।');
INSERT INTO "al_umari" VALUES(12,84,'12:84','और उनसे मुँह फेर लिया और कहाः हाय यूसुफ़! और उसकी दोनों आँखें शोक के कारण (रोते-रोते) सफेद हो गयीं और उसका दिल शोक से भर गया।');
INSERT INTO "al_umari" VALUES(12,85,'12:85','उन (पुत्रों) ने कहाः अल्लाह की शपथ! आप बराबर यूसुफ़ को याद करते रहेंगे, यहाँ तक कि (शोक से) घुल जायें या अपना विनाश कर लें।');
INSERT INTO "al_umari" VALUES(12,86,'12:86','उसने कहाः मैं अपनी आपदा तथा शोक की शिकायत अल्लाह के सिवा किसी से नहीं करता और अल्लाह की ओर से वह बात जानता हूँ, जो तुम नहीं जानते।');
INSERT INTO "al_umari" VALUES(12,87,'12:87','हे मेरे पुत्र! जाओ और यूसुफ़ और उसके भाई का पता लगाओ और अल्लाह की दया से निराश न हो। वास्तव में, अल्लाह की दया से वही निराश होते हैं, जो काफ़िर हैं।');
INSERT INTO "al_umari" VALUES(12,88,'12:88','फिर जब (यूसुफ़ के भाई) उसके पास (मिस्र) गये, तो कहाः हे अज़ीज़! हमपर और गमारे घराने पर आपदा (अकाल) आ पड़ी है और हम थोड़ा धन (मूल्य) लाये हैं, अतः हमें (अन्न का) पूरा माप दें और हम पर दान करें। वास्तव में, अल्लाह दानशीलों को प्रतिफल प्रदान करता है।');
INSERT INTO "al_umari" VALUES(12,89,'12:89','उस (यूसुफ़) ने कहाः क्या तुम जानते हो कि तुमने यूसुफ़ तथा उसके भाई के साथ क्या कुछ किया है, जब तुम अज्ञान थे?');
INSERT INTO "al_umari" VALUES(12,90,'12:90','उन्होंने कहाः क्या आप यूसुफ़ हैं? यूसुफ़ ने कहाः मैं यूसुफ़ हूँ और ये मेरा भाई है। अल्लाह ने हमपर उपकार किया है। वास्तव में, जो (अल्लाह से) डरता तथा सहन करता है, तो अल्लाह सदाचारियों का प्रतिफल व्यर्थ नहीं करता।');
INSERT INTO "al_umari" VALUES(12,91,'12:91','उन्होंने कहाः अल्लाह की शपथ! उसने आपको हमपर श्रेष्ठता प्रदान की है। वास्तव में, हम दोषी थे।');
INSERT INTO "al_umari" VALUES(12,92,'12:92','यूसुफ़ ने कहाः आज तुमपर कोई दोष नहीं, अल्लाह तुम्हें क्षमा कर दे! वही सर्वाधिक दयावान् है।');
INSERT INTO "al_umari" VALUES(12,93,'12:93','मेरा ये कुर्ता ले जाओ और मेरे पिता के मुँह पर डाल दो, वह देखने लगेंगे। और अपने पूरे घराने को (मिस्र) ले आओ।');
INSERT INTO "al_umari" VALUES(12,94,'12:94','और जब क़ाफ़िले ने परस्थान किया, तो उनके पिता ने कहाः मुझे यूसुफ़ की सुगन्ध आ रही है, यदि तुम मुझे बहका हुआ बूढ़ा न समझो।');
INSERT INTO "al_umari" VALUES(12,95,'12:95','उन लोगों[ ने कहाः अल्लाह की शपथ! आप तो अपनी पुरानी सनक में पड़े हुए हैं। ');
INSERT INTO "al_umari" VALUES(12,96,'12:96','फिर जब शुभ-सूचक आ गया, तो उसने वह कुर्ता उनके मुख पर डाल दिया और वे तुरन्त देखने लगे। याक़ूब ने कहाः क्या मैंने तुमसे नहीं कहा था कि वास्तव में अल्लाह की ओर से जो कुछ मैं जानता हूँ, तुम नहीं जानते?');
INSERT INTO "al_umari" VALUES(12,97,'12:97','सब (भाईयों) ने कहाः हे हमारे पिता! हमारे लिए हमारे पापों की क्षमा माँगिए, वास्तव में, हम ही दोषी थे।');
INSERT INTO "al_umari" VALUES(12,98,'12:98','याक़ूब ने कहाः मैं तुम्हारे लिए अपने पालनहार से क्षमा की प्रार्थना करूँगा, वास्तव में, वह अति क्षमी, दयावान् है।');
INSERT INTO "al_umari" VALUES(12,99,'12:99','फिर जब वे यूसुफ़ के पास पहुँचे, तो उसने अपनी माता-पिता को अपनी शरण में ले लिया और कहाः नगर (मिस्र) में प्रवेश कर जाओ, यदि अल्लाह ने चाहा, तो शान्ति से रहोगे।');
INSERT INTO "al_umari" VALUES(12,100,'12:100','तथा अपने माता-पिता को उठाकर सिंहासन पर बिठा लिया और सब उसके समक्ष सज्दे में गिर गये और यूसुफ़ ने कहाः हे मेरे पिता! यही मेरे स्वप्न का अर्थ है, जो मैंने पहले देखा था। मेरे पालनहार ने उसे सच कर दिया है तथा मेरे साथ उपकार किया, जब उसने मुझे कारावास से निकाला और आप लोगों को गाँव से मेरे पास (नगर में) ले आया, इसके पश्चात् कि शैतान ने मेरे तथा मेरे भाईयों के बीच विरोध डाल दिया। वास्तव में, मेरा पालनहार जिसके लिए चाहे, उसके लिए उत्तम उपाय करने वाहा है। निश्चय वही अति ज्ञानी, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(12,101,'12:101','हे मेरे पालनहार! तूने मुझे राज प्रदान किया तथा मुझे स्वप्नों का अर्थ सिखाया। हे आकाशों तथा धरती के उत्पत्तिकार! तू लोक तथा परलोक में मेरा रक्षक है। तू मेरा अन्त इस्लाम पर कर और मुझे सदाचारियों में मिला दे।');
INSERT INTO "al_umari" VALUES(12,102,'12:102','(हे नबी!) ये (कथा) परोक्ष के समाचारों में से है, जिसकी वह़्यी हम आपकी ओर कर रहे हैं। और आप उन (भाईयों) के पास नहीं थे, जब वे आपस की सहमति से षड्यंत्र रचते रहे।');
INSERT INTO "al_umari" VALUES(12,103,'12:103','और अधिकांश लोग आप कितनी ही लालसा करें, ईमान लाने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(12,104,'12:104','और आप इस (धर्मप्रचार) पर उनसे कोई पारिश्रमिक (बदला) नहीं माँगते। ये (क़ुर्आन) तो विश्ववासियों के लिए (केवल) एक शिक्षा है।');
INSERT INTO "al_umari" VALUES(12,105,'12:105','तथा आकाशों और धरती में बहुत सी निशानियाँ (लक्षण[) हैं, जिनपर से लोग गुज़रते रहते हैं और उनपर ध्यान नहीं देते ।');
INSERT INTO "al_umari" VALUES(12,106,'12:106','और उनमें से अधिक्तर अल्लाह को मानते तो हैं, परन्तु (साथ ही) मुश्रिक (मिश्रणवादी)[ भी हैं। ');
INSERT INTO "al_umari" VALUES(12,107,'12:107','तो क्या वे निर्भय हो गये हैं कि उनपर अल्लाह की यातना छा जाये अथवा उनपर प्रलय अकस्मात आ जाये और वे अचेत रह जायेँ?');
INSERT INTO "al_umari" VALUES(12,108,'12:108','(हे नबी!) आप कह दें: यही मेरी डगर है, मैं अल्लाह की ओर बुला रहा हूँ। मैं पूरे विश्वास और सत्य पर हूँ और जिसने मेरा अनुसरण किया (वे भी) तथा अल्लाह पवित्र है और मैं मुश्रिकों (मिश्रणवादियों) में से नहीं हूँ।');
INSERT INTO "al_umari" VALUES(12,109,'12:109','और हमने आपसे पहले मानव[ पुरुषों ही को नबी बनाकर भेजा, जिनकी ओर प्रकाशना भेजते रहे, नगर वासियों में से, क्या वे धरती में चले फिरे नहीं, ताकि देखते कि उनका परिणाम क्या हुआ, जो इनसे पहले थे? और निश्चय आख़िरत (परलोक) का घर (स्वर्ग) उनके लिए उत्तम है, जो अल्लाह से डरे, तो क्या तुम समझते नहीं हो? ');
INSERT INTO "al_umari" VALUES(12,110,'12:110','(इससे पहले भी रसूलों के साथ यही हुआ।) यहाँ तक कि जब रसूल निराश हो गये और लोगों को विश्वास हो गया कि उनसे झूठ बोला गया है, तो उनके लिए हमारी सहायता आ गई, फिर हम जिसे चाहते हैं, बचा लेते हैं और हमारी यातना अपराधियों से फेरी नहीं जाती।');
INSERT INTO "al_umari" VALUES(12,111,'12:111','इन कथाओं में, बुध्दिमानों के लिए बड़ी शिक्षा है, ये (क़ुर्आन) ऐसी बातों का संग्रह नहीं है, जिसे स्वयं बना लिया जाता हो, परन्तु इससे पहले की पुस्तकों की सिध्दि और प्रत्येक वस्तु का विवरण (ब्योरा) है तथा मार्गदर्शन और दया है, उन लोगों के लिए जो ईमान (विश्वास) रखते हों।');
INSERT INTO "al_umari" VALUES(13,1,'13:1','अलिफ, लाम, मीम, रा। ये इस पुस्तक (क़ुर्आन) की आयतें हैं और (हे नबी!) जो आपपर, आपके पालनहार की ओर से उतारा गया है, सर्वथा सत्य है। परन्तु अधिक्तर लोग ईमान (विश्वास) नहीं रखते।');
INSERT INTO "al_umari" VALUES(13,2,'13:2','अल्लाह वही है, जिसने आकाशों को ऐसे सहारों के बिना ऊँचा किया है, जिन्हें तुम देख सको। फिर अर्श (सिंहासन) पर स्थिर हो गया तथा सूर्य और चाँद को नियमबध्द किया। सब एक निर्धारित अवधि के लिए चल रहे हैं। वही इस विश्व की व्यवस्था कर रहा है, वह निशानियों का विवरण (ब्योरा) दे रहा है, ताकि तुम अपने पालनहार से मिलने का विश्वास करो।');
INSERT INTO "al_umari" VALUES(13,3,'13:3','तथा वही है, जिसने धरती को फैलाया और उसमें पर्वत तथा नहरें बनायीं और प्रत्येक फल के दो प्रकार बनाये। वह रात्रि से दिन को छुपा देता है। वास्तव में, इसमें बहुत सी निशानियाँ हैं, उन लोगों के लिए, जो सोच-विचार करते हैं।');
INSERT INTO "al_umari" VALUES(13,4,'13:4','और धरती में आपस में मिले हुए कई खण्ड हैं और उद्यान (बाग़) हैं अंगूरों के तथा खेती और खजूर के वृक्ष हैं। कुछ एक्हरे और कुछ दोहरे, सब एक ही जल से सींचे जाते हैं और हम कुछ को स्वाद में कुछ से अधिक कर देते हैं, वास्तव में, इसमें बहुत सी निशानियाँ हैं, उन लोगों के लिए, जो सूझ-बूझ रखते हैं।');
INSERT INTO "al_umari" VALUES(13,5,'13:5','तथा यदि आप आश्चर्य करते हैं, तो आश्चर्य करने योग्य उनका ये[ कथन है कि जब हम मिट्टी हो जायेंगे, तो क्या वास्तव में, हम नई उत्पत्ति में होंगे? उन्होंने ही अपने पालनहार के साथ कुफ़्र किया है तथा उन्हीं के गलों में तोक़ पड़े होंगे और वही नरक वाले हैं, जिसमें वे सदा रहेंगे। ');
INSERT INTO "al_umari" VALUES(13,6,'13:6','और वे आपसे बुराई (यातना) की जल्दी मचा रहे हैं भलाई से पहले। जबकि इनसे पहले यातनाऐं आ चुकी हैं और वास्तव में, आपका पालनहार लोगों को उनके अत्याचार पर क्षमा करने वाला है तथा निश्चय आपका पालनहार कड़ी यातना देने वाला (भी) है।');
INSERT INTO "al_umari" VALUES(13,7,'13:7','तथा जो काफ़िर हो गये, वे कहते हैं कि आपपर आपके पालनहार की ओर से कोई आयत (चमत्कार) क्यों नहीं उतारा[ गया। आपकेवल सावधान करने वाले तथा प्रत्येक जाति को सीधी राह दिखाने वाले हैं। ');
INSERT INTO "al_umari" VALUES(13,8,'13:8','अल्लाह ही जानता है, जो प्रत्येक स्त्री के गर्भ में है तथा गर्भाशय जो कम और अधिक[ करते हैं, प्रत्येक चीज़ की उसके यहाँ एक निश्चित मात्रा है। ');
INSERT INTO "al_umari" VALUES(13,9,'13:9','वह सब छुपे और खुले (प्रत्यक्ष) को जानने वाला, बड़ा महान् सर्वोच्च है।');
INSERT INTO "al_umari" VALUES(13,10,'13:10','(उसके लिए) बराबर है, तुममें से जो बात चुपके बोले और जो पुकार कर बोले तथा कोई रात के अंधेरे में छुपा हो या दिन के उजाले में चल रहा हो।');
INSERT INTO "al_umari" VALUES(13,11,'13:11','उस (अल्लाह) के रखवाले (फरिश्ते) हैं। उसके आगे तथा पीछे, जो अल्लाह के आदेश से, उसकी रक्षा कर रहे हैं। वास्तव में, अल्लाह किसी जाति की दशा नहीं बदलता, जब तक वह स्वयं अपनी दशा न बदल ले तथा जब अल्लाह किसी जाति के साथ बुराई का निश्चय कर ले, तो उसे फेरा नहीं जा सकता और न उनका उस (अल्लाह) के सिवा कोई सहायक है।');
INSERT INTO "al_umari" VALUES(13,12,'13:12','वही है, जो विध्दुत को तुम्हें भय तथा आशा[ बनाकर दिखाता है और भारी बादलों को पैदा करता है। ');
INSERT INTO "al_umari" VALUES(13,13,'13:13','और कड़क, अल्लाह की प्रशंसा के साथ उसकी पवित्रता का वर्णन करती है और फ़रिश्ते उसके भय से काँपते हैं। वह बिजलियाँ भेजता है, फिर जिसपर चाहता है, गिरा देता है तथा वे अल्लाह के बारे में विवाद करते हैं, जबकि उसका उपाय बड़ा प्रबल है[। ');
INSERT INTO "al_umari" VALUES(13,14,'13:14','उसी (अल्लाह) को पुकारना सत्य है और जो उसके सिवा दूसरों को पुकारते हैं, वे उनकी प्रार्थना कुछ नहीं सुनते। जैसे कोई अपनी दोनों हथेलियाँ जल की ओर फैलाया हुआ हो, ताकि उसके मुँह में पहुँच जाये, जबकि वह उसतक पहुँचने वाला नहीं और काफ़िरों की पुकार व्यर्थ (निष्फल) ही है।');
INSERT INTO "al_umari" VALUES(13,15,'13:15','और अल्लाह ही को सज्दा करता है, चाह या न चाह, वह जो आकाशों तथा धरती में है और उनकी प्रछाईयाँ[ भी प्रातः और संध्या ।');
INSERT INTO "al_umari" VALUES(13,16,'13:16','उनसे पूछोः आकाशों तथा धरती का पालनहार कौन है? कह दोः अल्लाह है। कहो कि क्या तुमने अल्लाह के सिवा उन्हें सहायक बना लिया है, जो अपने लिए किसी लाभ का अधिकार नहीं रखते और न किसी हानि का? उनसे कहोः क्या अन्धा और देखने वाला बराबर होता है या अन्धेरे और प्रकाश बराबर होते हैं[? अथवा उन्होंने अल्लाह का साझी बना लिया है ऐसों को, जिन्होंने अल्लाह के उत्पत्ति करने के समान उत्पत्ति की है, अतः उत्पत्ति का विषय उनपर उलझ गया है? आप कह दें कि अल्लाह ही प्रत्येक चीज़ का उत्पत्ति करने वाला है और वही अकेला प्रभुत्वशाली है।');
INSERT INTO "al_umari" VALUES(13,17,'13:17','उसने आकाश से जल बरसाया, जिससे वादियाँ (उपत्यकाएँ) अपनी समाई के अनुसार बह पड़ीं। फिर (जल की) धारा के ऊपर झाग आ गया और जिस चीज़ को वे आभूषण अथवा सामान बनाने के लिए अग्नि में तपाते हैं, उसमें भी ऐसा ही झाग होता है। इसी प्रकार, अल्लाह सत्य तथा असत्य का उदाहरण देता है, फिर जो झाग है, वह सूखकर ध्वस्त हो जाता है और जो चीज़ लोगों को लाभ पहुँचाती है, वह धरती में रह जाती है। इसी प्रकार, अल्लाह उदाहरण देता[ है। ');
INSERT INTO "al_umari" VALUES(13,18,'13:18','जिन लोगों ने अपने पालनहार की बात मान ली, उन्हीं के लिए भलाई है और जिन्होंने नहीं मानी, यदि जो कुछ धरती में है, सब उनका हो जाये और उसके साथ उसके समान और भी, तो वे उसे (अल्लाह के दण्ड से बचने के लिए) अर्थदण्ड के रूप में दे देंगे। उन्हीं से कड़ा ह़िसाब लिया जायेगा तथा उनका स्थान नरक है और वह बुरा रहने का स्थान है।');
INSERT INTO "al_umari" VALUES(13,19,'13:19','तो क्या, जो जानता है कि आपके पालनहार की ओर से, जो (क़ुर्आन) आपपर उतारा गया है, सत्य है, उसके समान है जो अन्धा है? वास्तव में, बुध्दिमान लोग ही शिक्षा ग्रहण करते हैं।');
INSERT INTO "al_umari" VALUES(13,20,'13:20','जो अल्लाह से किया वचन[ पूरा करते हैं और वचन भंग नहीं करते। ');
INSERT INTO "al_umari" VALUES(13,21,'13:21','और उन (संबंधों) को जोड़ते हैं, जिनके जोड़ने का अल्लाह ने आदेश दिया है और अपने पालनहार से डरते हैं तथा बुरे ह़िसाब से डरते हैं।');
INSERT INTO "al_umari" VALUES(13,22,'13:22','तथा जिन लोगों ने अपने पालनहार की प्रसन्नता के लिए धैर्य से काम लिया, नमाज़ की स्थापना की तथा हमने उन्हें जो कुछ प्रदान किया है, उसमें छुपे और खुले तरीक़े से दान करते रहे, तो वही हैं, जिनके लिए परलोक का घर (स्वर्ग) है।');
INSERT INTO "al_umari" VALUES(13,23,'13:23','ऐसे स्थायी स्वर्ग, जिनमें वे और उनके बाप-दादा तथा उनकी पत्नियों और संतान में से जो सदाचारी हों, प्रवेश करेंगे तथा फ़रिश्ते उनके पास प्रत्येक द्वार से (स्वागत के लिए) प्रवेश करेंगे।');
INSERT INTO "al_umari" VALUES(13,24,'13:24','(वे कहेंगेः) तुमपर शान्ति हो, उस धैर्य के कारण, जो तुमने किया, तो क्या ही अच्छा है ये परलोक का घर!');
INSERT INTO "al_umari" VALUES(13,25,'13:25','और जो लोग अल्लाह से किये वचन को, उसे सुदृढ़ करने के पश्चात्, भंग कर देते हैं और अल्लाह ने जिस संबन्ध को जोड़ने का आदेश दिया[ है, उसे तोड़ते हैं और धरती में उपद्रव फैलाते हैं। वही हैं, जिनके लिए धिक्कार है और जिनके लिए बुरा आवास है। ');
INSERT INTO "al_umari" VALUES(13,26,'13:26','और अल्लाह जिसे चाहे, जीविका फैलाकर देता है और जिसे चाहे. नापकर देता है और वे (काफ़िर) सांसारिक जीवन में मगन हैं तथा सांसारिक जीवन परलोक की अपेच्छा तनिक लाभ के सामान के सिवा कुछ भी नहीं है।');
INSERT INTO "al_umari" VALUES(13,27,'13:27','और जो काफ़िर हो गये, वे कहते हैः इसपर इसके पालनहार की ओर से कोई निशानी क्यों नहीं उतारी गयी? (हे नबी!) आप कह दें कि वास्तव में अल्लाह जिसे चाहे, कुपथ करता है और अपनी ओर उसी को राह दिखाता है, जो उसकी ओर ध्यानमग्न हों।');
INSERT INTO "al_umari" VALUES(13,28,'13:28','(अर्थात वे) लोग जो ईमान लाए तथा जिनके दिल अल्लाह के स्मरण से संतुष्ट होते हैं। सुन लो! अल्लाह के स्मरण ही से दिलों को संतोष होता है।');
INSERT INTO "al_umari" VALUES(13,29,'13:29','जो लोग ईमान लाये और सदाचार किये, उनके लिए आनन्द[ और उत्तम ठिकाना है। ');
INSERT INTO "al_umari" VALUES(13,30,'13:30','इसी प्रकार, हमने आपको एक समुदाय में जिससे पहले बहुत-से समुदाय गुज़र चुके हैं रसूल बनाकर भेजा है, ताकि आप उन्हें वो संदेश सुनाएँ, जो हमने आपकी ओर वह़्यी द्वारा भेजा है और वे अत्यंत कृपाशील को अस्वीकार करते हैं? आप कह दें वही मेरा पालनहार है, कोई पूज्य नहीं परन्तु वही। मैंने उसीपर भरोसा किया है और उसी की ओर मुझे जाना है।');
INSERT INTO "al_umari" VALUES(13,31,'13:31','यदि कोई ऐसा क़ुर्आन होता, जिससे पर्वत खिसका[ दिये जाते या धरती खण्ड-खण्ड कर दी जाती या इसके द्वारा मुर्दों से बात की जाती (तो भी वे ईमान नहीं लाते)। बात ये है कि सब अधिकार अल्लाह ही को हैं, तो क्या जो ईमान लाये हैं, वे निराश नहीं हुए कि यदि अल्लाह चाहता, तो सब लोगों को सीधी राह पर कर देता! और काफ़िरों को उनके करतूत के कारण बराबर आपदा पहुँचती रहेगी अथवा उनके घर के समीप उतरती रेहगी, यहाँ तक कि अल्लाह का वचन आ जाये और अल्लाह, वचन का विरुध्द नहीं करता।');
INSERT INTO "al_umari" VALUES(13,32,'13:32','और आपसे पहले भी बहुत-से रसूलों का परिहास किया गया है, तो हमने काफ़िरों को अवसर दिया। फिर उन्हें धर लिया, तो मेरी यातना कैसी रही?');
INSERT INTO "al_umari" VALUES(13,33,'13:33','तो क्या जो प्रत्येक प्राणी के करतूत से अवगत है और उन्होंने उस (अल्लाह) का साझी बना लिया है, आप कहिए कि उनके नाम बताओ या तुम उसे उस चीज़ से सूचित कर रहे हो, जिसे वह धरती में नहीं जानता या ओछी बात[ करते हो? बल्कि काफ़िरों के लिए उनके छल सुशोभित बना दिये गये हैं और सीधी राह से रोक दिये गये हैं और जिसे अल्लाह कुपथ कर दे, तो उसे कोई राह दिखाने वाला नहीं। ');
INSERT INTO "al_umari" VALUES(13,34,'13:34','उन्हींके लिए यातना है सांसारिक जीवन में। और निःसंदेह परलोक की यातना अधिक कड़ी है और उन्हें अल्लाह से कोई बचाने वाला नहीं।');
INSERT INTO "al_umari" VALUES(13,35,'13:35','उस स्वर्ग का उदाहरण, जिसका वचन आज्ञाकारियों को दिया गया है, उसमें नहरें बहती हैं, उसके फल सतत हैं और उसकी छाया। ये उनका परिणाम है, जो अल्लाह से डरे और काफ़िरों का परिणाम नरक है।');
INSERT INTO "al_umari" VALUES(13,36,'13:36','(हे नबी!) जिन्हें हमने पुस्तक दी है, वे उस (क़ुर्आन) से प्रसन्न हो रहे हैं[, जो आपकी ओर उतारा गया है और सम्प्रदायों में कुछ ऐसे भी हैं, जो नहीं मानते । आप कह दें कि मुझे आदेश दिया गया है कि अल्लाह की ईबादत (वंदना) करूँ और उसका साझी न बनाऊँ। मैं उसी की ओर बुलाता हूँ और उसी की ओर मुझे जाना है ।');
INSERT INTO "al_umari" VALUES(13,37,'13:37','और इसी प्रकार, हमने इसे अरबी आदेश के रूप में उतारा है[ और यदि आप उनकी आकांक्षाओं का अनुसरण करेंगे, इसके पश्चात् कि आपके पास ज्ञान आ गया, तो अल्लाह से आपका कोई सहायक और रक्षक न होगा। ');
INSERT INTO "al_umari" VALUES(13,38,'13:38','और हमने आपसे पहले बहुत-से रसूलों को भेजा है और उनकी पत्नियाँ तथा बाल-बच्चे[ बनाये। किसी रसूल के बस में नहीं है कि अल्लाह की अनुमति बिना कोई निशानी ले आये और हर वचन के लिए एक निर्धारित समय है ।');
INSERT INTO "al_umari" VALUES(13,39,'13:39','वह जो (आदेश) चाहे, मिटा देता है और जो चाहे, शेष (साबित) रखता है। उसी के पास मूल[ पुस्तक है। ');
INSERT INTO "al_umari" VALUES(13,40,'13:40','और (हे नबी!) यदि हम आपको उसमें से कुछ दिखा दें, जिसकी धमकी हमने उन (काफ़िरों) को दी है अथवा आपको (पहले ही) मौत दे दें, तो आपका काम उपदेश पहुँचा देना है और ह़िसाब लेना हमारा काम है।');
INSERT INTO "al_umari" VALUES(13,41,'13:41','क्या वे नहीं देखते कि हम धरती को, उसके किनारों से कम करते[ जा रहे हैं और अल्लाह ही आदेश देता है, कोई उसके आदेश की प्रत्यालोचन करने वाला नहीं और वह शीघ्र ह़िसाब लेने वाला है। ');
INSERT INTO "al_umari" VALUES(13,42,'13:42','तथा उससे पहले (भी) लोगों ने रसूलों के साथ षड्यंत्र रचा और षड्यंत्र (को निष्फल करने) का सब अधिकार तो अल्लाह को है, वह जो कुछ प्रत्येक प्राणी करता है, उसे जानता है और काफ़िरों को शीघ्र ही ज्ञान हो जायेगा कि परलोक का घर किसके लिए है?');
INSERT INTO "al_umari" VALUES(13,43,'13:43','(हे नबी!) जो काफ़िर हो गये, वे कहते हैं कि आप अल्लाह के भेजे हुए नहीं हैं। आप कह दें: मेरे तथा तुम्हारे बीच अल्लाह तथा उनकी गवाही, जिन्हें किताब का ज्ञान दिया गया है, काफ़ी है[। ');
INSERT INTO "al_umari" VALUES(14,1,'14:1','अलिफ़, लाम, रा। ये (क़ुर्आन) एक पुस्तक है, जिसे हमने आपकी ओर अवतरित किया है, ताकि आप लोगों को अंधेरों से निकालकर प्रकाश की ओर लायें, उनके पालनहार की अनुमति से, उसकी राह की ओर, जो बड़ा प्रबल सराहा हुआ है।');
INSERT INTO "al_umari" VALUES(14,2,'14:2','अल्लाह की ओर। जिसके अधिकार में आकाश और धरती का सब कुछ है तथा काफ़िरों के लिए कड़ी यातना के कारण विनाश है।');
INSERT INTO "al_umari" VALUES(14,3,'14:3','जो सांसारिक जीवन को परलोक पर प्रधानता देते हैं, अल्लाह की डगर ( इस्लाम) से रोकते हैं और उसे कुटिल बनाना चाहते हैं, वही कुपथ में दूर निकल गये हैं।');
INSERT INTO "al_umari" VALUES(14,4,'14:4','और हमने किसी (भी) रसूल को उसकी जाति की भाषा ही में भेजा, ताकि वह उनके लिए बात उजागर कर दे। फिर अल्लाह जिसे चाहता है, कुपथ करता है और जिसे चाहता है, सुपथ दर्शा देता है और वही प्रभुत्वशाली और ह़िक्मत वाला है।');
INSERT INTO "al_umari" VALUES(14,5,'14:5','और हमने मूसा को अपनी आयतों (चमत्कारों) के साथ भेजा, ताकि अपनी जाति को अन्धेरों से निकालकर प्रकाश की ओर लायें और उन्हें अल्लाह के दिनों (पुरस्कार और यातना) का स्मरण करायें। वास्तव में, इसमें कई निशानियाँ हैं, प्रत्येक अति सहनशील, कृतज्ञ के लिए।');
INSERT INTO "al_umari" VALUES(14,6,'14:6','तथा (याद करो) जब मूसा ने अपनी जाति से कहाः अपने ऊपर अल्लाह के पुरस्कार को याद करो, जब उसने तुम्हें फ़िरऔनियों से मुक्त किया, जो तुम्हें घोर यातना दे रहे थे, तुम्हारे पुत्रों को वध कर रहे थे और तुम्हारी स्त्रियों को जीवित रहने देते[ थे और इसमें तुम्हारे परलनहार की ओर से एक महान परीक्षा थी। ');
INSERT INTO "al_umari" VALUES(14,7,'14:7','तथा (याद करो) जब तुम्हारे पालनहार ने घोषणा कर दी कि यदि तुम कृतज्ञ बनोगे, तो तुम्हें और अधिक दूँगा तथा यदि अकृतज्ञ रहोगे, तो वास्तव में मेरी यातना बहुत कड़ी है।');
INSERT INTO "al_umari" VALUES(14,8,'14:8','और मूसा ने कहाः यदि तुम और सभी लोग जो धरती में हैं, कुफ़्र करें, तो भी अल्लाह निरीह तथा[ सराहा हुआ है। ');
INSERT INTO "al_umari" VALUES(14,9,'14:9','क्या तुम्हारे पास उनका समाचार नहीं आया, जो तुमसे पहले थे; नूह़, आद और समूद की जाति का और जो उनके पश्चात् हुए, जिन्हें अल्लाह ही जानता है? उनके पास उनके रसूल प्रत्यक्ष प्रमाण लाये, तो उन्होंने अपने हाथ अपने मुखों में दे[ लिए और कह दिया कि हम उस संदेश को नहीं मानते, जिसके साथ तुम भेजे गये हो और वास्तव में, उसके बारे में संदेह में हैं, जिसकी ओर हमें बुला रहे हो, (तथा) द्विधा में हैं। ');
INSERT INTO "al_umari" VALUES(14,10,'14:10','उनके रसूलों ने कहाः क्या उस अल्लाह के बारे में संदेह है, जो आकाशों तथा धरती का रचयिता है। वह तुम्हें बुला[ रहा है, ताकि तुम्हारे पाप क्षमा कर दे और तुम्हें एक निर्धारित अवधि तक अवसर दे। उन्होंने कहाः तो तुम हमारे ही जैसे एक मानव पुरुष हो, तुम चाहते हो कि हमें उससे रोक दो, जिसकी पूजा हमारे बाप-दादा कर रहे थे। तुम हमारे पास कोई प्रत्यक्ष प्रमान लाओ।');
INSERT INTO "al_umari" VALUES(14,11,'14:11','उनसे उनके रसूलों ने कहाः हम तुम्हारे जैसे मानव पुरुष ही हैं, परन्तु अल्लाह अपने भक्तों में से जिसपर चाहे, उपकार करता है और हमारे बस में नहीं कि अल्लाह की अनुमति के बिना कोई प्रमाण ले आयें और अल्लाह हीपर ईमान वालों को भरोसा करना चाहिए।');
INSERT INTO "al_umari" VALUES(14,12,'14:12','और क्या कारण है कि हम अल्लाह पर भरोसा न करें, जबकि उसने हमें हमारी राहें दर्शा दी हैं? और हम अवश्य उस दुःख को सहन करेंगे, जो तुम हमें दोगे और अल्लाह हीपर भरोसा करने वालों को निर्भर रहना चाहिए।');
INSERT INTO "al_umari" VALUES(14,13,'14:13','और काफ़िरों ने अपने रसूलों से कहाः हम अवश्य तुम्हें अपने देश से निकाल देंगे अथवा तुम्हें हमारे पंथ में आना होगा। तो उनके पालनहार ने उनकी ओर वह़्यी की कि हम अवश्य अत्याचारियों को विनाश कर देंगे।');
INSERT INTO "al_umari" VALUES(14,14,'14:14','और तुम्हें उनके पश्चात् धरती में बसा देंगे, ये उसके लिए है, जो मेरे महिमा से खड़े[ होने से डरा तथा मेरी चेतावनी से डरा। ');
INSERT INTO "al_umari" VALUES(14,15,'14:15','और उन (रसूलों) ने विजय की प्रार्थना की, तो सभी उद्दंड विरोधी असफल हो गये।');
INSERT INTO "al_umari" VALUES(14,16,'14:16','उसके आगे नरक है और उसे पीप का पानी पिलाया जायेगा।');
INSERT INTO "al_umari" VALUES(14,17,'14:17','वह उसे थोड़ा-थोड़ा गले से उतारेगा, मगर उतार नहीं पायेगा और उसके पास प्रत्येक स्थान से मौत आयेगी जबकि वह मरेगा नहीं और उसके आगे भीषण यातना होगी।');
INSERT INTO "al_umari" VALUES(14,18,'14:18','जिन लोगों ने अपने पालनहार के साथ कुफ़्र किया, उनके कर्म उस राख के समान हैं, जिसे आँधी के दिन की प्रचण्ड वायु ने उड़ा दिया हो। ये लोग अपने किये में से कुछ नहीं पा सकेंगे, यही (सत्य से) दूर का कुपथ है।');
INSERT INTO "al_umari" VALUES(14,19,'14:19','क्या तूने नहीं देखा कि अल्लाह ही ने आकाशों तथा धरती की रचना सत्य के साथ की है, यदि वह चाहे, तो तुम्हें ले जाये और नई उत्पत्ति ले आये?');
INSERT INTO "al_umari" VALUES(14,20,'14:20','और वह अल्लाह पर कठिन नहीं है।');
INSERT INTO "al_umari" VALUES(14,21,'14:21','और सब अल्लाह के सामने खुलकर[ आ जायेंगे, तो निर्बल लोग उनसे कहेंगे, जो बड़े बन रहे थे कि हम तुम्हारे अनुयायी थे, तो क्या तुम अल्लाह की यातना से बचाने के लिए हमारे कुछ काम आ सकोगे? वे कहेंगेः यदि अल्लाह ने हमें मार्गदर्शन दिया होता, तो हम अवश्य तुम्हें मार्गदर्शन दिखा देते। अब तो समान है, चाहे हम अधीर हों या धैर्य से काम लें, हमारे बचने का कोई उपाय नहीं है। ');
INSERT INTO "al_umari" VALUES(14,22,'14:22','और शैतान कहेगा, जब निर्णय कर दिया[ जायेगाः वास्तव में, अल्लाह ने तुम्हें सत्य वचन दिया था और मैंने तुम्हें वचन दिया, तो अपना वचन भंग कर दिया और मेरा तुमपर कोई दबाव नहीं था, परन्तु ये कि मैंने तुम्हें (अपनी ओर) बुलाया और तुमने मेरी बात स्वीकार कर ली। अतः मेरी निन्दा न करो, स्वयं अपनी निंदा करो, न मैं तुम्हारी सहायता कर सकता हूँ और न तुम मेरी सहायता कर सकते हो। वास्तव में, मैंने उसे स्वीकार कर लिया, जो इससे पहले तुमने मुझे अल्लाह का साझी बनाया था। निःसंदेह अत्याचारियों के लिए दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(14,23,'14:23','और जो ईमान लाये और सदाचार करते रहे, उन्हें ऐसे स्वर्गों में प्रवेश दिया जायेगा, जिनमें नहरें बहती होंगी। वे अपने पालनहार की अनुमति से उसमें सदा रहने वाले होंगे और उसमें उनका स्वागत ये होगाः तुमपर शान्ति हो।');
INSERT INTO "al_umari" VALUES(14,24,'14:24','(हे नबी!) क्या आप नहीं जानते कि अल्लाह ने कलिमा तय्येबा[( पवित्र शब्द) का उदाहरण एक पवित्र वृक्ष से दिया है, जिसकी जड़ (भूमि में) सुदृढ़ स्थित है और उसकी शाखा आकाश में है? ');
INSERT INTO "al_umari" VALUES(14,25,'14:25','वह अपने पालनहार की अनुमति से प्रत्येक समय फल दे रहा है और अल्लाह लोगों को उदाहरण दे रहा है, ताकि वे शिक्षा ग्रहण करें।');
INSERT INTO "al_umari" VALUES(14,26,'14:26','और बुरी[ बात का उदाहरण एक बुरे वृक्ष जैसा है, जिसे धरती के ऊपर से उखाड़ दिया गया हो, जिसके लिए कोई स्थिरता नहीं है। ');
INSERT INTO "al_umari" VALUES(14,27,'14:27','अल्लाह ईमान वालों को स्थिर[ कथन के सहारे लोक तथा परलोक में स्थिरता प्रदान करता है तथा अत्याचारियों को कुपथ कर देता है और अल्लाह जो चाहता है, करता है। ');
INSERT INTO "al_umari" VALUES(14,28,'14:28','क्या आपने उन्हें[ नहीं देखा, जिन्होंने अल्लाह के अनुग्रह को कुफ़्र से बदल दिया और अपनी जाति को विनाश के घर में उतार दिया। ');
INSERT INTO "al_umari" VALUES(14,29,'14:29','(अर्थात) नरक में, जिसमें वे झोंके जायेंगे और वह रहने का बुरा स्थान है।');
INSERT INTO "al_umari" VALUES(14,30,'14:30','और उन्होंने अल्लाह के साझी बना लिए, ताकि उसकी राह (सत्धर्म) से कुपथ कर दें। आप कह दें कि तनिक आनन्द ले लो, फिर तुम्हें नरक की ओर ही जाना है।');
INSERT INTO "al_umari" VALUES(14,31,'14:31','(हे नबी!) मेरे उन भक्तों से कह दो, जो ईमान लाये हैं कि नमाज़ की स्थापना करें और उसमें से, जो हमने प्रदान किया है, छुपे और खुले तरीक़े से दान करें, उस दिन के आने से पहले, जिसमें न कोई क्रय-विक्रय होगा और न कोई मैत्री।');
INSERT INTO "al_umari" VALUES(14,32,'14:32','और अल्लाह वही है, जिसने तुम्हारे लिए आकाशों तथा धरती की उत्पत्ति की और आकाश से जल बरसाया, फिर उससे तुम्हारी जीविका के लिए अनेक प्रकार के फल निकाले और नौका को तुम्हारे वश में किया, ताकि सागर में उसके आदेश से चले और नदियों को तुम्हारे लिए वशवर्ती किया।');
INSERT INTO "al_umari" VALUES(14,33,'14:33','तथा तुम्हारे लिए सूर्य और चाँद को काम में लगाया, जो दोनों निरन्तर चल रहे हैं और तुम्हार लिए रात्री और दिवस को वश में[ कर दिया। ');
INSERT INTO "al_umari" VALUES(14,34,'14:34','और तुम्हें उससब में से कुछ दिया, जो तुमने माँगा[ और यदि तुम अल्लाह के पुरस्कारों की गणना करना चाहो, तो भी नहीं कर सकते। वास्तव में, मनुष्य बड़ा अत्याचारी कृतघ्न (ना शुकरा) है। ');
INSERT INTO "al_umari" VALUES(14,35,'14:35','तथा (याद करो) जब इब्राहीम ने प्रार्थना कीः हे मेरे पालनहार! इस नगर (मक्का) को शान्ति का नगार बना दे और मुझे तथा मेरे पुत्रों को मूर्ति-पूजा से बचा ले।');
INSERT INTO "al_umari" VALUES(14,36,'14:36','मेरे पालनहार! इन मूर्तियों ने बहुत-से लोगों को कुपथ किया है, अतः जो मेरा अनुयायी हो, वही मेरा है और जो मेरी अवज्ञा करे, तो वास्तव में, तू अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(14,37,'14:37','हमारे पालनहार! मैंने अपनी कुछ संतान मरुस्थल की एक वादी (उपत्यका) में तेरे सम्मानित घर (काबा) के पास बसा दी है, ताकि वे नमाज़ की स्थापना करें। अतः लोगों के दिलों को उनकी ओर आकर्षित कर दे और उन्हें जीविका प्रदान कर, ताकि वे कृतज्ञ हों।');
INSERT INTO "al_umari" VALUES(14,38,'14:38','हमारे पालनहार! तू जानता है, जो हम छुपाते और जो व्यक्त करते हैं और अल्लाह से कुछ छुपा नहीं रहता, धरती में और न आकाशों में।');
INSERT INTO "al_umari" VALUES(14,39,'14:39','सब प्रशंसा उस अल्लाह के लिए है, जिसने मुझे बुढ़ापे में (दो पुत्र) इस्माईल और इस्ह़ाक़ प्रदान किये। वास्तव में, मेरा पालनहार प्रार्थना अवश्य सुनने वाला है।');
INSERT INTO "al_umari" VALUES(14,40,'14:40','मेरे पालनहार! मुझे नमाज़ की स्थापना करने वाला बना दे तथा मेरी संतान को। हे मेरे पालनहर! और मेरी प्रार्थना स्वीकार कर।');
INSERT INTO "al_umari" VALUES(14,41,'14:41','हे मेरे पालनहार! मूझे क्षमा कर दे तथा मेरे माता-पिता और ईमान वालों को, जिस दिन ह़िसाब लिया जायेगा।');
INSERT INTO "al_umari" VALUES(14,42,'14:42','और तुम कदापि अल्लाह को, उससे अचेत न समझो, जो अत्याचारी कर रहे हैं! वह तो उन्हें उस[ दिन के लिए टाल रहा है, जिस जिन आँखें खुली रह जायेँगी। ');
INSERT INTO "al_umari" VALUES(14,43,'14:43','वे दौड़ते हुए अपने सिर ऊपर किये हुए होंगे, उनकी आँखें उनकी ओर नहीं फिरेंगी और उनके दिल गिरे[ हुए होंगे। ');
INSERT INTO "al_umari" VALUES(14,44,'14:44','(हे नबी!) आप लोगों को उस दिन से डरायें, जब उनपर यातना आ जायेगी। तो अत्याचारी कहेंगेः हमारे पालनहार! हमें कुछ समय तक अवसर दे, हम तेरी बात (आमंत्रण) स्वीकार कर लेंगे और रसूलों का अनुसरण करेंगे, क्या तुम वही नहीं हो, जो इससे पहले शपथ ले रहे थे कि हमारा पतन होना ही नहीं है?');
INSERT INTO "al_umari" VALUES(14,45,'14:45','जबकि तुम उन्हीं की बस्तियों में बसे हो, जिन्होंने अपने ऊपर अत्याचार किया और तुम्हारे लिए उजागर हो गया है कि हमने उनके साथ क्या किया? और हमने तुम्हें बहुत-से उदाहरण भी दिये हैं।');
INSERT INTO "al_umari" VALUES(14,46,'14:46','और उन्होंने अपना षड्यंत्र रच लिया तथा उनका षड्यंत्र अल्लाह के पास[ है और उनका षड्यंत्र ऐसा नहीं था कि उससे पर्वत टल जायें। ');
INSERT INTO "al_umari" VALUES(14,47,'14:47','अतः कदापि ये न समझें कि अल्लाह अपने रसूलों से किया वचन भंग करने वाला है, वास्तव में, अल्लाह प्रभुत्वशाली, बदला लेने वाला है।');
INSERT INTO "al_umari" VALUES(14,48,'14:48','जिस दिन ये धरती दूसरी धरती से तथा आकाश बदल दिये जायेंगे और सब अल्लाह के समक्ष[ उपस्थित होंगे, जो अकेला प्रभुत्वशाली है। ');
INSERT INTO "al_umari" VALUES(14,49,'14:49','और आप उस दिन अपराधियों को ज़ंजीरों में जकड़े हुए देखेंगे।');
INSERT INTO "al_umari" VALUES(14,50,'14:50','उनके वस्त्र तारकोल के होंगे और उनके मुखों पर अग्नि छायी होगी।');
INSERT INTO "al_umari" VALUES(14,51,'14:51','ताकि अल्लाह प्रत्येक प्राणी को, उसके किये का बदला दे। निःसंदेह अल्लाह शीघ्र ह़ीसाब लेने वाला है।');
INSERT INTO "al_umari" VALUES(14,52,'14:52','ये मनुष्यों के लिए एक संदेश है और ताकि इसके द्वारा उन्हें सावधान किया जाये और ताकि वे जान लें कि वही एक सत्य पूज्य है और ताकि मतिमान लोग शिक्षा ग्रहण करें।');
INSERT INTO "al_umari" VALUES(15,1,'15:1','अलिफ़, लाम, रा। वो इस पुस्कत तथा खुले क़ुर्आन की आयतें हैं।');
INSERT INTO "al_umari" VALUES(15,2,'15:2','(एक समय आयेगा) जब काफ़िर ये कामना करेंगे कि क्या ही अच्छा होता, यदि वे मुसलामन[ होते? ');
INSERT INTO "al_umari" VALUES(15,3,'15:3','(हे नबी!)आप उन्हें छोड़ दें, वे खाते तथा आनन्द लेते रहें और उन्हें आशा निश्चेत किये रहे, फिर शीघ्र ही वे जान लेंगे[। ');
INSERT INTO "al_umari" VALUES(15,4,'15:4','और हमने जिस बस्ती को भी ध्वस्त किया, उसके लिए एक निश्चित अवधि अंत थी।');
INSERT INTO "al_umari" VALUES(15,5,'15:5','कोई जाति न अपनी निश्चित अवधि से आगे जा सकती है और न पीछे रह सकती है।');
INSERT INTO "al_umari" VALUES(15,6,'15:6','तथा उन (काफ़िरों) ने कहाः हे वह व्यक्ति जिसपर ये शिक्षा (क़ुर्आन) उतारी गयी है! वास्तव में, तू पागल है।');
INSERT INTO "al_umari" VALUES(15,7,'15:7','क्यों हमारे पास फ़रिश्तों को नहीं लाता, यदि तू सचों में से है?');
INSERT INTO "al_umari" VALUES(15,8,'15:8','जबकि हम फ़रिश्तों को सत्य निर्णय के साथ ही[ उतारते हैं और उन्हें उस समय कोई अवसर नहीं दिया जाता।');
INSERT INTO "al_umari" VALUES(15,9,'15:9','वास्तव में, हमने ही ये शिक्षा (क़ुर्आन) उतारी है और हम ही इसके रक्षक[ हैं। ');
INSERT INTO "al_umari" VALUES(15,10,'15:10','और हमने आपसे पहले भी प्राचीन (विगत) जातियों में रसूल भेजे।');
INSERT INTO "al_umari" VALUES(15,11,'15:11','और उनके पास जो भी रसूल आया, वे उसके साथ परिहास करते रहे।');
INSERT INTO "al_umari" VALUES(15,12,'15:12','इसी प्रकार, हम इसे[ अपराधियों के दिलों में पिरो देते हैं। ');
INSERT INTO "al_umari" VALUES(15,13,'15:13','वे उसपर ईमान नहीं लाते और प्रथम जातियों से यही रीति चली आ रही है।');
INSERT INTO "al_umari" VALUES(15,14,'15:14','और यदि हम उनपर आकाश का कोई द्वार खोल देते, फिर वे उसमें चढ़ने लगते।');
INSERT INTO "al_umari" VALUES(15,15,'15:15','तबभी वे यही कहते कि हमारी आँखें धोखा खा रही हैं, बल्कि हमपर जादू कर दिया गया है।');
INSERT INTO "al_umari" VALUES(15,16,'15:16','हमने आकाश में राशि-चक्र बनाये हैं और उसे देखने वालों के लिए सुसज्जित किया है।');
INSERT INTO "al_umari" VALUES(15,17,'15:17','और उसे प्रत्येक धिक्कारे हुए शैतान से सुरक्षित किया है।');
INSERT INTO "al_umari" VALUES(15,18,'15:18','परन्तु जो (शैतान) चोरी से सुनना चाहे, तो एक खुली ज्वाला उसका पीछा करती[ है। ');
INSERT INTO "al_umari" VALUES(15,19,'15:19','और हमने धरती को फैलाया और उसमें पर्वत बना दिये और उसमें हमने प्रत्येक उचित चीज़ें उगायीं।');
INSERT INTO "al_umari" VALUES(15,20,'15:20','और हमने उसमें तुम्हारे लिए जीवन के संसाधन बना दिये तथा उनके लिए जिनके जीविका दाता तुम नहीं हो।');
INSERT INTO "al_umari" VALUES(15,21,'15:21','और कोई चीज़ ऐसी नहीं है, जिसके कोष हमारे पास न हों और हम उसे एक निश्चित मात्रा ही में उतारते हैं।');
INSERT INTO "al_umari" VALUES(15,22,'15:22','और हमने जलभरी वायुओं को भेजा, फिर आकाश से जल बरसाया और उसे तुम्हें पिलाया तथा तुम उसके कोषाधिकारी नहीं हो।');
INSERT INTO "al_umari" VALUES(15,23,'15:23','तथा हम ही जीवन देते तथा मारते हैं और हम ही सबके उत्तराधिकारी हैं।');
INSERT INTO "al_umari" VALUES(15,24,'15:24','तथा तुममें से विगत लोगों को जानते हैं और भविष्य के लोगों को भी जानते हैं।');
INSERT INTO "al_umari" VALUES(15,25,'15:25','और वास्तव, में आपका पालनहार ही उन्हें एकत्र करेगा[, निश्चय वह सब गुण और सब कुछ जानने वाला है। ');
INSERT INTO "al_umari" VALUES(15,26,'15:26','और हमने मनुष्य को सड़े हुए कीचड़ के सूखे गारे बनाया।');
INSERT INTO "al_umari" VALUES(15,27,'15:27','और इससे पहले जिन्नों को हमने अग्नि की ज्वाला से पैदा किया।');
INSERT INTO "al_umari" VALUES(15,28,'15:28','और (याद करो) जब आपके पालनहार ने फ़रिश्तों से कहाः मैं एक मनुष्य उत्पन्न करने वाला हूँ, सड़े हुए कीचड़ के सूखे गारे से।');
INSERT INTO "al_umari" VALUES(15,29,'15:29','तो जब मैं उसे पूरा बना लूँ और उसमें अपनी आत्मा फूँक दूँ, तो उसके लिए सज्दे में गिर जाना[। ');
INSERT INTO "al_umari" VALUES(15,30,'15:30','अतः उनसब फ़रिश्तों ने सज्दा किया।');
INSERT INTO "al_umari" VALUES(15,31,'15:31','इब्लीस के सिवा। उसने सज्दा करने वालों का साथ देने से इन्कार कर दिया।');
INSERT INTO "al_umari" VALUES(15,32,'15:32','अल्लाह ने पूछाः हे इब्लीस! तुझे क्या हुआ कि सज्दा करने वालों का साथ नहीं दिया?');
INSERT INTO "al_umari" VALUES(15,33,'15:33','उसने कहाः मैं ऐसा नहीं हूँ कि एक मनुष्य को सज्दा करूँ, जिसे तूने सड़े हुए कीचड़ के सूखे गारे से पैदा किया है।');
INSERT INTO "al_umari" VALUES(15,34,'15:34','अल्लाह ने कहाः यहाँ से निकल जा, वास्तव में, तू धिक्कारा हुआ है।');
INSERT INTO "al_umari" VALUES(15,35,'15:35','और तुझपर धिक्कार है, प्रतिकार (प्रलय) के दिन तक।');
INSERT INTO "al_umari" VALUES(15,36,'15:36','(इब्लीस) ने कहाः[ मेरे पालनहार! तू फिर मुझे उस दिन तक अवसर दे, जब सभी पुनः जीवित किये जायेंगे। ');
INSERT INTO "al_umari" VALUES(15,37,'15:37','अल्लाह ने कहाः तुझे अवसर दे दिया गया है।');
INSERT INTO "al_umari" VALUES(15,38,'15:38','विध्दित समय के दिन तक के लिए।');
INSERT INTO "al_umari" VALUES(15,39,'15:39','वह बोलाः मेरे पालनहार! तेरे, मुझको कुपथ कर देने के कारण, मैं अवश्य उनके लिए धरती में (तेरी अवज्ञा को) मनोरम बना दूँगा और उनसभी को कुपथ कर दूँगा।');
INSERT INTO "al_umari" VALUES(15,40,'15:40','उनमें से तेरे शुध्द भक्तों के सिवा।');
INSERT INTO "al_umari" VALUES(15,41,'15:41','अल्लाह ने कहाः यही मुझतक (पहुँचने की) सीधी राह है।');
INSERT INTO "al_umari" VALUES(15,42,'15:42','वस्तुतः, मेरे भक्तों पर तेरा कोई अधिकार नहीं[ चलेगा, सिवाय उसके जो कुपथों में से तेरा अनुसरण करे। ');
INSERT INTO "al_umari" VALUES(15,43,'15:43','और वास्तव में, उनसबके लिए नरक का वचन है।');
INSERT INTO "al_umari" VALUES(15,44,'15:44','उस (नरक) के सात द्वार हैं और उनमें से प्रत्येक द्वार के लिए एक विभाजित भाग[ है। ');
INSERT INTO "al_umari" VALUES(15,45,'15:45','वास्तव में, आज्ञाकारी लोग स्वर्गों तथा स्रोतों में होंगे।');
INSERT INTO "al_umari" VALUES(15,46,'15:46','(उनसे कहा जायेगा) इसमें प्रवेश कर जाओ, शान्ति के साथ निर्भय होकर।');
INSERT INTO "al_umari" VALUES(15,47,'15:47','और हम निकाल देंगे उनके दिलों में जो कुछ बैर होगा। वे भाई-भाई होकर एक-दूसरे के सम्मुख तख़्तों के ऊपर रहेंगे।');
INSERT INTO "al_umari" VALUES(15,48,'15:48','न उसमें उन्हें कोई थकान होगी और न वहाँ से निकाले जायेंगे।');
INSERT INTO "al_umari" VALUES(15,49,'15:49','(हे नबी!) आप मेरे भक्तों को सूचित कर दें कि वास्तव में, मैं बड़ा क्षमाशील दयावान्[ हूँ। ');
INSERT INTO "al_umari" VALUES(15,50,'15:50','और मेरी यातना ही दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(15,51,'15:51','और आप उन्हें इब्राहीम के अतिथियों के बारे में सूचित कर दें।');
INSERT INTO "al_umari" VALUES(15,52,'15:52','जब वे इब्राहीम के पास आये, तो सलाम किया। उसने कहाः वास्तव में, हम तुमसे डर रहे हैं।');
INSERT INTO "al_umari" VALUES(15,53,'15:53','उन्होंने कहाः डरो नहीं, हम तुम्हें एक ज्ञानी बालक की शुभ सूचना दे रहे हैं।');
INSERT INTO "al_umari" VALUES(15,54,'15:54','उसने कहाः क्या तुमने मुझे इस बुढ़ापे में शुभ सूचना दी है, तुम मुझे ये शुभ सूचना कैसे दे रहे हो?');
INSERT INTO "al_umari" VALUES(15,55,'15:55','उन्होंने कहाः हमने तुम्हें सत्य शुभ सूचना दी है, अतः तुम निराश न हो।');
INSERT INTO "al_umari" VALUES(15,56,'15:56','(इब्राहीम) ने कहाः अपने पालनहार की दया से निराश, केवल कुपथ लोग ही हुआ करते हैं।');
INSERT INTO "al_umari" VALUES(15,57,'15:57','उसने कहाः हे अल्लाह के भेजे हुए फ़रिश्तो! तुम्हारा अभियान क्या है?');
INSERT INTO "al_umari" VALUES(15,58,'15:58','उन्होंने उत्तर दिया कि हम एक अपराधी जाति के पास भेजे गये हैं।');
INSERT INTO "al_umari" VALUES(15,59,'15:59','लूत के घराने के सिवा, उनसभी को हम बचाने वाले हैं।');
INSERT INTO "al_umari" VALUES(15,60,'15:60','परन्तु लूत की पत्नि के लिए हमने निर्णय किया है कि वह पीछे रह जाने वालों में होगी।');
INSERT INTO "al_umari" VALUES(15,61,'15:61','फिर जब लूत के घर भेजे हुए (फ़रिश्ते) आये।');
INSERT INTO "al_umari" VALUES(15,62,'15:62','तो लूत ने कहाः तुम (मेरे लिए) अपरिचित हो।');
INSERT INTO "al_umari" VALUES(15,63,'15:63','उन्होंने कहाः डरो नहीं, बल्कि हम तुम्हारे पास वो (यातना) लाये हैं, जिसके बारे में वे संदेह कर रहे थे।');
INSERT INTO "al_umari" VALUES(15,64,'15:64','हम तुम्हारे पास सत्य लाये हैं और वास्तव में, हम सत्यवादी हैं।');
INSERT INTO "al_umari" VALUES(15,65,'15:65','अतः कुछ रात रह जाये, तो अपने घराने को लेकर निकल जाओ और तुम उनके पीछे रहो और तुममें से कोई फिरकर न देखे तथा चले जाओ, जहाँ आदेश दिया जा रहा है।');
INSERT INTO "al_umari" VALUES(15,66,'15:66','और हमने लूत को निर्णय सुना दिया कि भोर होते ही इनका उन्मूलन कर दिया जायेगा।');
INSERT INTO "al_umari" VALUES(15,67,'15:67','और नगर वासी प्रसन्न होकर आ गये[। ');
INSERT INTO "al_umari" VALUES(15,68,'15:68','लूत ने कहाः ये मेरे अतिथि हैं, अतः मेरा अपमान न करो।');
INSERT INTO "al_umari" VALUES(15,69,'15:69','तथा अल्लाह से डरो और मेरा अनादर न करो।');
INSERT INTO "al_umari" VALUES(15,70,'15:70','उन्होंने कहाः क्या हमने तुम्हें विश्व वासियों से नहीं रोका[ था? ');
INSERT INTO "al_umari" VALUES(15,71,'15:71','लूत ने कहाः ये मेरी पुत्रियाँ हैं, यदि तुम कुछ करने वाले[ हो। ');
INSERT INTO "al_umari" VALUES(15,72,'15:72','हे नबी! आपकी आयु की शपथ[! वास्तव में, वे अपने उन्माद में बहक रहे थे। ');
INSERT INTO "al_umari" VALUES(15,73,'15:73','अंततः, सूर्योदय के समय उन्हें एक कड़ी ध्वनि ने पकड़ लिया।');
INSERT INTO "al_umari" VALUES(15,74,'15:74','फिर हमने उस बस्ती के ऊपरी भाग को नीचे कर दिया और उनपर कंकरीले पत्थर बरसा दिये।');
INSERT INTO "al_umari" VALUES(15,75,'15:75','वास्तव में, इसमें कई निशानियाँ हैं, प्रतिभाशालियों[ के लिए। ');
INSERT INTO "al_umari" VALUES(15,76,'15:76','और वह (बस्ती) साधारण[ मार्ग पर स्थित है। ');
INSERT INTO "al_umari" VALUES(15,77,'15:77','निःसंदेह इसमें बड़ी निशानी है, ईमान वलों के लिए।');
INSERT INTO "al_umari" VALUES(15,78,'15:78','और वास्तव में, (ऐय्का) के[ वासी अत्याचारी थे। ');
INSERT INTO "al_umari" VALUES(15,79,'15:79','तो हमने उनसे बदला ले लिया और वे दोनों[ ही साधारण मार्ग पर हैं। ');
INSERT INTO "al_umari" VALUES(15,80,'15:80','और ह़िज्र के[ लोगों ने रसूलों को झुठलाया। ');
INSERT INTO "al_umari" VALUES(15,81,'15:81','और उन्हें हमने अपनी आयतें (निशानियाँ) दीं, तो वे उनसे विमुख ही रहे।');
INSERT INTO "al_umari" VALUES(15,82,'15:82','वे शिलाकारी करके पर्वतों से घर बनाते और निर्भय होकर रहते थे।');
INSERT INTO "al_umari" VALUES(15,83,'15:83','अन्ततः, उन्हें कड़ी ध्वनि ने भोर के समय पकड़ लिया।');
INSERT INTO "al_umari" VALUES(15,84,'15:84','और उनकी कमाई उनके कुछ काम न आयी।');
INSERT INTO "al_umari" VALUES(15,85,'15:85','और हमने आकाशों तथा धरती को और जो कुछ उन दोनों के बीच है, सत्य के आधार पर ही उत्पन्न किया है और निश्चय प्रलय आनी है। अतः (हे नबी!) आप (उन्हें) भले तौर पर क्षमा कर दें।');
INSERT INTO "al_umari" VALUES(15,86,'15:86','वास्तव में, आपका पालनहार ही सबका स्रेष्टा, सर्वज्ञ है।');
INSERT INTO "al_umari" VALUES(15,87,'15:87','तथा (हे नबी!) हमने आपको सात ऐसी आयतें, जो बार-बार दुहराई जाती हैं और महा क़ुर्आन[ प्रदान किया है। ');
INSERT INTO "al_umari" VALUES(15,88,'15:88','और आप, उसकी ओर न देखें, जो सांसारिक लाभ का संसाधन हमने उनमें से विभिन्न प्रकार के लोगों को दे रखा है और न उनपर शोक करें और ईमान वालों के लिए सुशील रहें।');
INSERT INTO "al_umari" VALUES(15,89,'15:89','और कह दें कि मैं प्रत्यक्ष (खुली) चेतावनी[ देने वाला हूँ। ');
INSERT INTO "al_umari" VALUES(15,90,'15:90','जैसे हमने खण्डन कारियों[ पर (यातना) उतारी। ');
INSERT INTO "al_umari" VALUES(15,91,'15:91','जिन्होंने क़ुर्आन को खण्ड-खण्ड कर दिया[। ');
INSERT INTO "al_umari" VALUES(15,92,'15:92','तो शपथ है आपके पालनहार की। हम उनसे अवश्य पूछेंगे।');
INSERT INTO "al_umari" VALUES(15,93,'15:93','तुम क्या करते रहे?');
INSERT INTO "al_umari" VALUES(15,94,'15:94','अतः आपको, जो आदेश दिया जा रहा है, उसे खोलकर सुना दें और मुश्रिकों (मिश्रमवादियों) की चिन्ता न करें।');
INSERT INTO "al_umari" VALUES(15,95,'15:95','हम आपके लिए परिहास करने वालों को काफ़ी हैं।');
INSERT INTO "al_umari" VALUES(15,96,'15:96','जो अल्लाह के साथ दूसरे पूज्य बना लेते हैं, तो उन्हें शीघ्र ज्ञान हो जायेगा।');
INSERT INTO "al_umari" VALUES(15,97,'15:97','और हम जानते हैं कि उनकी बातों से आपका दिल संकुचित हो रहा है।');
INSERT INTO "al_umari" VALUES(15,98,'15:98','अतः आप अपने पालनहार की प्रशंसा के साथ उसकी पवित्रता का वर्णन करें तथा सज्दा करने वालों में रहें।');
INSERT INTO "al_umari" VALUES(15,99,'15:99','और अपने पालनहार की इबादत (वंदना) करते रहें, यहाँ तक कि आपके पास विश्वास आ जाये[। ');
INSERT INTO "al_umari" VALUES(16,1,'16:1','अल्लाह का आदेश आ गया है। अतः (हे काफ़िरो!) उसके शीघ्र आने की माँग न करो। वह (अल्लाह) पवित्र तथा उस शिर्क (मिश्रणवाद) से ऊँचा है, जो वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(16,2,'16:2','वह फ़रिश्तों को वह़्यी के साथ, अपने आदेश से, अपने जिस भक्त पर चाहता है, उतारता है कि (लोगों को) सावधान करो कि मेरे सिवा कोई पूज्य नहीं है, अतः मुझसे ही डरो।');
INSERT INTO "al_umari" VALUES(16,3,'16:3','उसने आकाशों तथा धरती की उत्पत्ति सत्य के साथ की है, वह उनके शिर्क से बहुत ऊँचा है।');
INSERT INTO "al_umari" VALUES(16,4,'16:4','उसने मनुष्य की उत्पत्ति वीर्य से की है, फिर वह अकस्मात् खुला झगड़ालू बन गया।');
INSERT INTO "al_umari" VALUES(16,5,'16:5','तथा चौपायों की उत्पत्ति की, जिनमें तुम्हारे लिए गर्मी[ और बहुत-से लाभ हैं और उनमें से कुछको खाते हो। ');
INSERT INTO "al_umari" VALUES(16,6,'16:6','तथा उनमें तुम्हारे लिए एक शोभा है, जिस समय संध्या को चराकर लाते हो और जब प्रातः चराने ले जाते हो।');
INSERT INTO "al_umari" VALUES(16,7,'16:7','और वे तुम्हारे बोझ, उन नगरों तक लादकर ले जाते हैं, जिनतक तुम बिना कड़े परिश्रम के नहीं पहुँच सकते। वास्तव में, तुम्हारा पालनहार अति करुणामय, दयावान् है।');
INSERT INTO "al_umari" VALUES(16,8,'16:8','तथा घोड़े, खच्चर और गधे पैदा किये, ताकि उनपर सवारी करो और (वे) शोभा (बनें) और (अल्लाह) ऐसी चीज़ों की उत्पत्ति करेगा, जिन्हें (अभी) तुम नहीं जानते हो[। ');
INSERT INTO "al_umari" VALUES(16,9,'16:9','और अल्लाह पर, सीधी राह बताना है और उनमें से कुछ[ टेढ़े हैं तथा यदि अल्लाह चाहता, तो तुमसभी को सीधी राह दिखा देता। ');
INSERT INTO "al_umari" VALUES(16,10,'16:10','वही है, जिसने आकाश से जल बरसाया, जिसमें से कुछ तुम पीते हो तथा कुछसे वृक्ष उपजते हैं, जिसमें तुम (पशुओं को) चराते हो।');
INSERT INTO "al_umari" VALUES(16,11,'16:11','और तुम्हारे लिए उससे खेती उपजाता है और ज़ैतून, खजूर, अंगूर और प्रत्येक प्रकार के फल। वास्तव में इस, में एक बड़ी निशानी है, उन लोगों के लिए, जो सोच-विचार करते हैं।');
INSERT INTO "al_umari" VALUES(16,12,'16:12','और उसने तुम्हारे लिए रात्रि तथा दिवस को सेवा में लगा रखा है तथा सूर्य और चाँद को और (हाँ!) सितारे उसके आदेश के अधीन हैं। वास्तव में, इसमें कई निशानियाँ (लक्षण) हैं, उन लोगों के लिए, जो समझ-बूझ रखते हैं।');
INSERT INTO "al_umari" VALUES(16,13,'16:13','तथा जो तुम्हारे लिए धरती में विभिन्न रंगों की चीजें उत्पन्न की हैं, वास्तव में, इसमें एक बड़ी निशानी लक्षण) है, उन लोगों के लिए, जो शिक्षा ग्रहण करते हैं।');
INSERT INTO "al_umari" VALUES(16,14,'16:14','और वही है, जिसने सागर को वश में कर रखा है, ताकि तुम उससे ताज़ा[ मांस खाओ और उससे अलंकार निकालो, जिसे पहनते हो तथा तुम नौकाओं को देखते हो कि सागर में (जल को) फाड़ती हुई चलती हैं और इसलिए ताकि तुम उस अल्लाह के अनुग्रह की खोज करो और ताकि कृतज्ञ बनो।');
INSERT INTO "al_umari" VALUES(16,15,'16:15','और उसने धरती में पर्वत गाड़ दिये, ताकि तुम्हें लेकर डोलने न लगे तथा नदियाँ और राहें, ताकि तुम राह पाओ।');
INSERT INTO "al_umari" VALUES(16,16,'16:16','तथा बहुत-से चिन्ह (बना दिये) और वे सितारों से (भी) राह[ पाते हैं। ');
INSERT INTO "al_umari" VALUES(16,17,'16:17','तो क्या, जो उत्पत्ति करता है, उसके समान है, जो उत्पत्ति नहीं करता? क्या तुम शिक्षा ग्रहण नहीं करते[? ');
INSERT INTO "al_umari" VALUES(16,18,'16:18','और यदि तुम अल्लाह के पुरस्कारों की गणना करना चाहो, तो कभी नहीं कर सकते। वास्तव में, अल्लाह बड़ा क्षमा तथा दया करने वाला है।');
INSERT INTO "al_umari" VALUES(16,19,'16:19','तथा अल्लाह जानता है, जो तुम छुपाते हो और जो तुम व्यक्त करते हो।');
INSERT INTO "al_umari" VALUES(16,20,'16:20','और जिन्हें वे अल्लाह के सिवा पुकारते हैं, वे किसी चीज़ की उत्पत्ति नहीं कर सकते। जबकि वे स्वयं उत्पन्न किये जाते हैं।');
INSERT INTO "al_umari" VALUES(16,21,'16:21','वे निर्जीव प्राणहीन हैं और (ये भी) नहीं जानते कि कब पुनः जीवित किये जायेंगे।');
INSERT INTO "al_umari" VALUES(16,22,'16:22','तुम्हारा पूज्य बस एक है, फिर जो लोग परलोक पर ईमान नहीं लाते, उनके दिल निवर्ती (विरोधी) हैं और वे अभिमानी हैं।');
INSERT INTO "al_umari" VALUES(16,23,'16:23','जो कुछ वे छुपाते तथा व्यक्त करते हैं, निश्चय अल्लाह उसे जानता है। वास्तव में, वह अभिमानियों से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(16,24,'16:24','और जब उनसे पूछा जाये कि तुम्हारे पालनहार ने क्या उतारा है[? तो कहते हैं कि पूर्वजों की कल्पित कथाएँ हैं। ');
INSERT INTO "al_umari" VALUES(16,25,'16:25','ताकि वे अपने (पापों का) पूरा बोझ प्रलय के दिन उठायें तथा कुछ उन लोगों का बोझ (भी), जिन्हें बिना ज्ञान के कुपथ कर रहे थे, सावधान! वे कितना बुरा बोझ उठायेंगे!');
INSERT INTO "al_umari" VALUES(16,26,'16:26','इनसे पहले के लोग भी षड्यंत्र रचते रहे, तो अल्लाह ने उनके षड्यंत्र के भवन का उनमूलन कर दिया, फिर ऊपर से उनपर छत गिर पड़ी और उनपर ऐसी दिशा से यातना आ गई, जिसे वे सोच भी नहीं रहे थे।');
INSERT INTO "al_umari" VALUES(16,27,'16:27','फिर प्रलय के दिन उन्हें अपमानित करेगा और कहेगा कि मेरे वह साझी कहाँ हैं, जिनके लिए तुम झगड़ रहे थे? वे कहेंगेः जिन्हें ज्ञान दिया गया है कि वास्तव में, आज अपमान तथा बुराई (यातना) काफ़िरों के लिए है।');
INSERT INTO "al_umari" VALUES(16,28,'16:28','जिनके प्राण फ़रिश्ते निकालते हैं, इस दशा में कि वे अपने ऊपर अत्याचार करने वाले हैं, तो वे आज्ञाकारी बन जाते[ हैं, (कहते हैं कि) हम कोई बुराई (शिर्क) नहीं कर रहे थे। क्यों नहीं? वास्तव में, अल्लाह तुम्हारे कर्मों से भली-भाँति अवगत है। ');
INSERT INTO "al_umari" VALUES(16,29,'16:29','तो नरक के द्वारों में प्रवेश कर जाओ, उसमें सदावासी रहोगे, अतः क्या ही बुरा है अभिमानों का निवास स्थान!');
INSERT INTO "al_umari" VALUES(16,30,'16:30','और उनसे पूछा गया, जो अपने पालनहार से डरे कि तुम्हारे पालनहार ने क्या उतारा है? तो उन्होंने कहाः अच्छी चीज़ उतारी है। उनके लिए जिन्होंने इस लोक में सदाचार किये, बड़ी भलाई है और वास्तव में, परलोक का घर (स्वर्ग) अति उत्तम है और आज्ञाकारियों का आवास कितना अच्छा है!');
INSERT INTO "al_umari" VALUES(16,31,'16:31','सदा रहने के स्वर्ग जिसमें प्रवेश करेंगे, जिनमें नहरें बहती होंगी, उनके लिए उसमें जो चाहेंगे (मिलेगा)। इसी प्रकार, अल्लाह आज्ञाकारियों को प्रतिफल (बदला) देता है।');
INSERT INTO "al_umari" VALUES(16,32,'16:32','जिनके प्राण फ़रिश्ते इस दशा में निकालते हैं कि वे स्वच्छ-पवित्र हैं, तो कहते हैं: "तुमपर शान्ति हो।" तुम अपने सुकर्मों के बदले स्वर्ग में प्रवेश कर जाओ।');
INSERT INTO "al_umari" VALUES(16,33,'16:33','क्या वे इसकी प्रतीक्षा कर रहे हैं कि उनके पास फ़रिश्ते[ आ जायें अथवा आपके पालनहार का आदेश आ पहुँचे? ऐसे ही, उनसे पूर्व के लोगों ने किया और अल्लाह ने उनपर अत्याचार नहीं किया, परन्तु वे स्वयं अपने ऊपर अत्याचार कर रहे थे।');
INSERT INTO "al_umari" VALUES(16,34,'16:34','तो उनके कुकर्मों की बुराईयाँ[ उनपर आ पड़ीं और उन्हें उसी (यातना) ने घेर लिया, जिसका वे परिहास कर रहे थे। ');
INSERT INTO "al_umari" VALUES(16,35,'16:35','और कहा, जिन लोगों ने शिर्क (मिश्रणवाद) कियाः यदि अल्लाह चाहता, तो हम उसके सिवा किसी चीज़ की इबादत (वंदना) न करते, न हम और न हमारे बाप-दादा और न उसके आदेश के बिना किसी चीज़ को ह़राम (वर्जित) करते। ऐसे ही, इनसे पूर्व वाले लोगों ने किया। तो रसूलों पर केवल खुले रूप से उपदेश पहुँचा देना है।');
INSERT INTO "al_umari" VALUES(16,36,'16:36','और हमने प्रत्येक समुदाय में एक रसूल भेजा कि अल्लाह की इबादत (वंदना) करो और ताग़ूत (असुर- अल्लाह के सिवा पूज्यों) से बचो, तो उनमें से कुछ को, अल्लाह ने सुपथ दिखा दिया और कुछ पर कुपथ सिध्द हो गया। तो धरती में चलो-फिरो, फिर देखो कि झुठलाने वालों का अन्त कैसा रहा?');
INSERT INTO "al_umari" VALUES(16,37,'16:37','(हे नबी!) आप ऐसे लोगों को सुपथ दिखाने पर लोलुप हों, तो भी अल्लाह उसे सुपथ नहीं दिखायेगा, जिसे कुपथ कर दे और न उनका कोई सहायक होगा।');
INSERT INTO "al_umari" VALUES(16,38,'16:38','और उन (काफ़िरों) ने अल्लाह की भरपूर शपथ ली कि अल्लाह उसे पुनः जीवित नहीं करेगा, जो मर जाता है। क्यों नहीं? ये तो अल्लाह का अपने ऊपर सत्य वचन है, परन्तु अधिक्तर लोग नहीं जानते।');
INSERT INTO "al_umari" VALUES(16,39,'16:39','(ऐसा करना इसलिए आवश्यक है) ताकि अल्लाह उस तथ्य को उजागर कर दे, जिसमें[ वे विभेद कर रहे थे और ताकि काफ़िर जान लें कि वही झूठे थे। ');
INSERT INTO "al_umari" VALUES(16,40,'16:40','हमारा कथन, जब हम किसी चीज़ को अस्तित्व प्रदान करने का निश्चय करें, तो इसके सिवा कुछ नहीं होता कि उसे आदेश दें कि "हो जा" और वह हो जाती है।');
INSERT INTO "al_umari" VALUES(16,41,'16:41','तथा जो लोग अल्लाह के लिए हिजरत (प्रस्थान) कर गये, अत्याचार सहने के पश्चात्, तो हम उन्हें संसार में अच्छा निवास्-स्थान देंगे और परलोक का प्रतिफल तो बहुत बड़ा है, यदि वे[ जानते। ');
INSERT INTO "al_umari" VALUES(16,42,'16:42','जिन लोगों ने धैर्य धारण किया तथा अपने पालनहार पर ही वे भरोसा करते हैं।');
INSERT INTO "al_umari" VALUES(16,43,'16:43','और (हे नबी!) हमने आपसे पहले जो भी रसूल भेजे, वे सभी मानव-पुरुष थे। जिनकी ओर हम वह़्यी (प्रकाशना) करते रहे। तो तुम ज्ञानियों से पूछ लो, यदि (स्वयं) नहीं[ जानते। ');
INSERT INTO "al_umari" VALUES(16,44,'16:44','प्रत्यक्ष (खुले) प्रमाणों तथा पुस्तकों के साथ (उन्हें भेजा) और आपकी ओर ये शिक्षा (क़ुर्आन) अवतरित की, ताकि आप उसे सर्वमानव के लिए उजागर कर दें, जो कुछ उनकी ओर उतारा गया है, ताकि वे सोच-विचार करें।');
INSERT INTO "al_umari" VALUES(16,45,'16:45','तो क्या वे निर्भय हो गये हैं, जिन्होंने बुरे षड्यंत्र रचे हैं कि अल्लाह उन्हें धरती में धंसा दे? अथवा उनपर यातना ऐसी दिशा से आ जाये, जिसे वे सोचते भी न हों?');
INSERT INTO "al_umari" VALUES(16,46,'16:46','या उन्हें चलते-फिरते पकड़ ले, तो वे (अल्लाह को) विवश करने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(16,47,'16:47','अथवा उन्हें भय की दशा में पकड़[ ले? निश्चय तुम्हारा पालनहार अति करुणामय दयावान् है। ');
INSERT INTO "al_umari" VALUES(16,48,'16:48','क्या अल्लाह की उत्पन्न की हुई किसी चीज़ को उन्होंने नहीं देखा? जिसकी छाया दायें तथा बायें झुकती है, अल्लाह को सज्दा करते हुए? और वे सर्व विनयशील हैं।');
INSERT INTO "al_umari" VALUES(16,49,'16:49','तथा अल्लाह ही को सज्दा करते हैं, जो आकाशों में तथा धरती में चर (जीव) तथा फ़रिश्ते हैं और वे अहंकार नहीं करते।');
INSERT INTO "al_umari" VALUES(16,50,'16:50','वे[ अपने पालनहार से डरते हैं, जो उनके ऊपर है और वही करते हैं, जो आदेश दिये जाते हैं। ');
INSERT INTO "al_umari" VALUES(16,51,'16:51','और अल्लाह ने कहाः दो पूज्य न बनाओ, वही अकेला पूज्य है। अतः तुम मुझी से डरो।');
INSERT INTO "al_umari" VALUES(16,52,'16:52','और उसी का है, जो कुछ आकाशों तथा धरती में है और उसी की वंदना स्थायी है, तो क्या तुम अल्लाह के सिवा दूसरे से डरते हो?');
INSERT INTO "al_umari" VALUES(16,53,'16:53','तुम्हें, जोभी सुख-सुविधा प्राप्त है, वह अल्लाह ही की ओर से है। फिर जब तुम्हें दुःख पहुँचता है, तो उसी को पुकारते हो।');
INSERT INTO "al_umari" VALUES(16,54,'16:54','फिर जब तुमसे दुःख दूर कर देता है, तो तुम्हारा एक समुदाय अपने पालनहार का साझी बनाने लगता है।');
INSERT INTO "al_umari" VALUES(16,55,'16:55','ताकि हमने उन्हें, जो कुछ प्रदान किया है, उसके प्रति कृतघ्न हों, तो आनन्द ले लो, तुम्हें शीघ्र ही ज्ञान हो जायेगा।');
INSERT INTO "al_umari" VALUES(16,56,'16:56','और वे जिन्हें जानते[ तक नहीं, उनका एक भाग, उसमें से बनाते हैं, जो जीविका हमने उन्हें दी है। तो अल्लाह की शपथ! तुमसे अवश्य पूछा जायेगा, उसके विषय में, जो तुम झूठी बातें बना रहे थे? ');
INSERT INTO "al_umari" VALUES(16,57,'16:57','और वे अल्लाह के लिए पुत्रियाँ बनाते[ हैं, वह पवित्र है! और उनके लिए वह है, जो वे स्वयं चाहते हों?');
INSERT INTO "al_umari" VALUES(16,58,'16:58','और जब उनमें से किसी को पुत्री (के जन्म) की शुभसूचना दी जाये, तो उसका मुख काला हो जाता है और वह शोकपूर्ण हो जाता है।');
INSERT INTO "al_umari" VALUES(16,59,'16:59','और लोगों से छुपा फिरता है, उस बुरी सूचना के कारण, जो उसे दी गयी है। (सोचता है कि) क्या[ उसे अपमान के साथ रोक ले अथवा भूमि में गाड़ दे? देखो! वे कितना बुरा निर्णय करते हैं। ');
INSERT INTO "al_umari" VALUES(16,60,'16:60','उन्हीं के लिए जो आख़िरत (परलोक) पर ईमान नहीं रखते अपगुण है और अल्लाह के लिए सदगुण हैं तथा वह प्रभुत्वशाली, तत्वदर्शी है।');
INSERT INTO "al_umari" VALUES(16,61,'16:61','और यदि अल्लाह, लोगों को उनके अत्याचार[ पर (तत्क्षण) धरने लगे, तो धरती में किसी जीव को न छोड़े। परन्तु वह एक निर्धारित अवधि तक निलम्बित करता है और जब उनकी अवधि आ जायेगी, तो एक क्षण न पीछे होंगे, न पहले।');
INSERT INTO "al_umari" VALUES(16,62,'16:62','वह अल्लाह के लिए उसे[ बनाते हैं, जिसे स्वयं अप्रिय समझते हैं तथा उनकी ज़ुबानें झूठ बोलती हैं कि उन्हीं के लिए भलाई है। निश्चय उन्हीं के लिए नरक है और वही सबसे पहले (नरक में) झोंके जायेंगे। ');
INSERT INTO "al_umari" VALUES(16,63,'16:63','अल्लाह की शपथ! (हे नबी!) आपसे पहले हमने बहुत-से समुदायों की ओर रसूल भेजे। तो उनके लिए शैतान ने उनके कुकर्मों को सुसज्जित बना दिया। अतः वही आज उनका सहायक है और उन्हीं के लिए दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(16,64,'16:64','और हमने आपपर ये पुस्तक (क़ुर्आन) इसी लिए उतारी है, ताकि आप उनके लिए उसे उजागर कर दें, जिसमें वे विभेद कर रहे हैं तथा मार्गदर्शन और दया है, उन लोगों के लिए, जो ईमान (विश्वास) रखते हैं।');
INSERT INTO "al_umari" VALUES(16,65,'16:65','और अल्लाह ने ही आकाश से जल बरसाया, फिर उसने निर्जीव धरती को जीवित कर दिया। निश्चय इसमें उन लोगों के लिए एक निशानी है, जो सुनते हैं।');
INSERT INTO "al_umari" VALUES(16,66,'16:66','तथा वास्तव में, तुम्हारे लिए पशुओं में एक शिक्षा है। हम तुम्हें उससे, जो उसके भीतर है, गोबर तथा रक्त के बीच से शुध्द दूध पिलाते हैं, जो पीने वालों के लिए रुचिकर होता है।');
INSERT INTO "al_umari" VALUES(16,67,'16:67','तथा खजूरों और अंगूरों के फलों से, जिससे तुम मदिरा बना लेते हो तथा उत्तम जीविका भी, वास्तव में, इसमें एक निशानी (लक्षण) है, उन लोगों के लिए, जो समझ-बूझ रखते हैं।');
INSERT INTO "al_umari" VALUES(16,68,'16:68','और हमने मधुमक्खी को प्रेरणा दी कि पर्वतों में घर (छत्ते) बना तथा वृक्षों में और लोगों की बनायी छतों में।');
INSERT INTO "al_umari" VALUES(16,69,'16:69','फिर प्रत्येक फलों का रस चूस और अपने पालनहार की सरल राहों पर चलती रह। उसके भीतर से एक पेय निकलता है, जो विभिन्न रंगों का होता है, जिसमें लोगों के लिए आरोग्य है। वास्तव में, इसमें एक निशानी (लक्षण) है, उन लोगों के लिए, जो सोच-विचार करते हैं।');
INSERT INTO "al_umari" VALUES(16,70,'16:70','और अल्लाह ही ने तुम्हारी उत्पत्ति की है, फिर तुम्हें मौत देता है और तुममें से कुछको अबोध आयु तक पहुँचा दिया जाता है, ताकि जानने के पश्चात् कुछ न जाने। वास्तव में, अल्लाह सर्वज्ञ, सर्व सामर्थ्यवान्[ है। ');
INSERT INTO "al_umari" VALUES(16,71,'16:71','और अल्लाह ने तुममें से कुछ को, कुछ पर जीविका में प्रधानता दी है, तो जिन्हें प्रधानता दी गयी है, वे अपनी जीविका अपने दासों की ओर फेरने वाले नहीं कि वे उसमें बराबर हो जायें, तो क्या वे अल्लाह के उपकारों को नहीं मानते हैं[? ');
INSERT INTO "al_umari" VALUES(16,72,'16:72','और अल्लाह ने तुम्हारे लिए तुम्हीं में से पत्नियाँ बनायीं और तुम्हारे लिए तुम्हारी पत्नियों से पुत्र तथा पौत्र बनाये और तुम्हें स्वच्छ चीज़ों से जीविका प्रदान की। तो क्या वे असत्य पर विश्वास रखते हैं और अल्लाह के पुरस्कारों के प्रति अविश्वास रखते हैं?');
INSERT INTO "al_umari" VALUES(16,73,'16:73','और अल्लाह के सिवा उनकी वंदना करते हैं, जो उनके लिए आकाशों तथा धरती से कुछ भी जीविका देने का अधिकार नहीं रखते और न इसका सामर्थ्य रखते हैं।');
INSERT INTO "al_umari" VALUES(16,74,'16:74','और अल्लाह के लिए उदाहरण न दो। वास्तव में, अल्लाह जानता है और तुम नहीं जानते[। ');
INSERT INTO "al_umari" VALUES(16,75,'16:75','अल्लाह ने एक उदाहरण[ दिया है; एक प्राधीन दास है, जो किसी चीज़ का अधिकार नहीं रखता और दूसरा (स्वाधीन) व्यक्ति है, जिसे हमने अपनी ओर से उत्तम जीविका प्रदान की है और वह उसमें छुपे और खुले व्यय करता है। क्या वे दोनों समान हो जायेंगे? सब प्रशंसा अल्लाह के लिए है। बल्कि अधिक्तर लोग (ये बात) नहीं जानते।');
INSERT INTO "al_umari" VALUES(16,76,'16:76','तथा अल्लाह ने दो व्यक्तियों का उदाहरण दिया है; दोनों में से एक गूँगा है, वह किसी चीज़ का अधिकार नहीं रखता, वह अपने स्वामी पर बोझ है, वह उसे जहाँ भेजता है, कोई भलाई नहीं लाता। तो क्या वह और जो न्याय का आदेश देता हो और स्वयं सीधी[ राह पर हो, बराबर हो जायेंगे? ');
INSERT INTO "al_umari" VALUES(16,77,'16:77','और अल्लाह ही को आकाशों तथा धरती के परोक्ष[ का ज्ञान है और प्रलय (क़्यामत) का विषय तो बस पलक झपकने जैसा होगा अथवा उससे भी अधिक शीघ्र। वास्तव में, अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(16,78,'16:78','और अल्लाह ही ने तुम्हें तुम्हारी माताओं के गर्भों से निकाला, इस दशा में कि तुम कुछ नहीं जानते थे और तुम्हारे कान और आँख तथा दिल बनाये, ताकि तुम (उसका) उपकार मानो।');
INSERT INTO "al_umari" VALUES(16,79,'16:79','क्या वे पक्षियों को नहीं देखते कि वे अंतरिक्ष में कैसे वशीभूत हैं? उन्हें अल्लाह ही थामता[ है। वास्तव में, इसमें बहुत-सी निशानियाँ हैं, उन लोगों के लिए, जो ईमान लाते हैं। ');
INSERT INTO "al_umari" VALUES(16,80,'16:80','और अल्लाह ही ने तुम्हारे घरों को निवास स्थान बनाया और पशुओं की खालों से तुम्हारे लिए ऐसे घर[ बनाये, जिन्हें तुम अपनी यात्रा तथा अपने विराम के दिन हल्का (अल्पभार) पाते हो और उनकी ऊन और रोम तथा बालों से उपक्रण और लाभ के सामान, जीवन की निश्चित अवधि तक के लिए (बनाये)। ');
INSERT INTO "al_umari" VALUES(16,81,'16:81','और अल्लाह ही ने तुम्हार लिए उस चीज़ में से, जो उत्पन्न की है, छाया बनायी है और तुम्हारे लिए पर्वतों में गुफाएँ बनायी हैं और तुम्हारे लिए ऐसे वस्त्र बनाये हैं, जो तुम्हे धूप से बचायें[। इसी प्रकार, वह तुमपर अपना उपकार पूरा करता है, ताकि तुम आज्ञाकारी बनो। ');
INSERT INTO "al_umari" VALUES(16,82,'16:82','फिर यदि वे विमुख हों, तो आपपर बस प्रत्यक्ष (खुला) उपदेश पहुँचा देना है।');
INSERT INTO "al_umari" VALUES(16,83,'16:83','वे अल्लाह के उपकार पहचानते हैं, फिर उसका इन्कार करते हैं और उनमें अधिक्तर कृतघ्न हैं।');
INSERT INTO "al_umari" VALUES(16,84,'16:84','और जिस[ दिन, हम प्रत्येक समुदाय से एक साक्षी (गवाह) खड़ा करेंगे, फिर काफ़िरों को बात करने की अनुमति नहीं दी जायेगी और न उनसे क्षमा याचना की माँग की जायेगी।');
INSERT INTO "al_umari" VALUES(16,85,'16:85','और जब अत्याचारी यातना देखेंगे, उनकी यातना कुछ कम नहीं की जायेगी और न उन्हें अवकाश दिया[ जायेगा। ');
INSERT INTO "al_umari" VALUES(16,86,'16:86','और जब मुश्रिक अपने (बनाये हुए) साझियों को देखेंगे, तो कहेंगेः हे हमारे पालनहार! यही हमारे साझी हैं, जिन्हें हम, तुझे छोड़कर पुकार रहे थे। तो वे (पूज्य) बोलेंगे कि निश्चय तुम सब मिथ्यावादी (झूठे) हो।');
INSERT INTO "al_umari" VALUES(16,87,'16:87','उस दिन, वे अल्लाह के आगे झुक जायेंगे और उनसे खो जायेँगी, जो मिथ्या बातें वे बनाते थे।');
INSERT INTO "al_umari" VALUES(16,88,'16:88','जो लोग काफिर हो गये और (दूसरों को भी) अल्लाह की डगर (इस्लाम) से रोक दिये, उन्हें हम यातना पर यातना देंगे, उस उपद्रव के बदले, जो वे कर रहे थे।');
INSERT INTO "al_umari" VALUES(16,89,'16:89','और जिस दिन, हम प्रत्येक समुदाय से एक साक्षी उनके विरुध्द उन्हीं में से खड़ा कर देंगे और (हे नबी!) हम आपको उनपर साक्षी (गवाह) बनायेंगे[ और हमने आपपर ये पुस्तक (क़ुर्आन) अवतरित की है, जो प्रत्येक विषय का खुला विवरण है, मार्गदर्शन दया तथा शुभ सूचना है आज्ञाकारियों के लिए। ');
INSERT INTO "al_umari" VALUES(16,90,'16:90','वस्तुतः, अल्लाह तुम्हें न्याय, उपकार और समीपवर्तियों को देने का आदेश दे रहा है और निर्लज्जा, बुराई और विद्रोह से रोक रहा है और तुम्हें सिखा रहा है, ताकि तुम शिक्षा ग्रहण करो।');
INSERT INTO "al_umari" VALUES(16,91,'16:91','और जब अल्लाह से कोई वचन करो, तो उसे पूरा करो और अपनी शपथों को सुदृढ़ करने के पश्चात् भंग न करो, जब तुमने अल्लाह को अपने ऊपर गवाह बनाया है। निश्चय अल्लाह जो कुछ तुम करते हो, उसे जानता है।');
INSERT INTO "al_umari" VALUES(16,92,'16:92','और तुम्हारी दशा उस स्त्री जैसी न हो जाये, जिसने अपना सूत कातने के पश्चात् उधेड़ दिया। तुम अपनी शपथों को आपस में विश्वासघात का साधन बनाते हो, ताकि एक समुदाय दूसरे समुदाय से अधिक लाभ प्राप्त करे। अल्लाह इस[ (वचन) के द्वारा तुम्हारी परीक्षा ले रहा है और प्रलय के दिन तुम्हारे लिए अवश्य उसे उजागर कर देगा, जिसमें तुम विभेद कर रहे थे। ');
INSERT INTO "al_umari" VALUES(16,93,'16:93','और यदि अल्लाह चाहता, तो तुम्हें एक समुदाय बना देता। परन्तु वह जिसे चाहता है, कुपथ कर देता है और जिसे चाहता है, सुपथ दर्शा देता है और तुमसे उसके बारे में अवश्य पूछा जायेगा, जो तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(16,94,'16:94','और अपनी शपथों को आपस में विश्वासघात का साधन न बनाओ, ऐसा न हो कि कोई पग अपने स्थिर (दृढ़) होने के पश्चात् (ईमान से) फिसल[ जाये और तुम उसके बदले बुरा परिणाम चखो कि तुमने अल्लाह की राह से रोका है और तुम्हारे लिए बड़ी यातना हो। ');
INSERT INTO "al_umari" VALUES(16,95,'16:95','और अल्लाह से किये हुए वचन को तनिक मूल्य के बदले न बेचो[। वास्तव में, जो अल्लाह के पास है, वही तुम्हारे लिए उत्तम है, यदि तुम जानो। ');
INSERT INTO "al_umari" VALUES(16,96,'16:96','जो तुम्हारे पास है, वह व्यय (ख़र्च) हो जायेगा और जो अल्लाह के पास है, वह शेष रह जाने वाला है और हम, जो धैर्य धारण करते हैं, उन्हें अवश्य उनका पारिश्रमिक (बदला) उनके उत्तम कर्मों के अनुसार प्रदान करेंगे।');
INSERT INTO "al_umari" VALUES(16,97,'16:97','जो भी सदाचार करेगा, वह नर हो अथवा नारी और ईमान वाला हो, तो हम उसे स्वच्छ जीवन व्यतीत करायेंगे और उन्हें उनका पारिश्रमिक उनके उत्तम कर्मों के अनुसार अवश्य प्रदान करेंगे।');
INSERT INTO "al_umari" VALUES(16,98,'16:98','तो (हे नबी!) जब आप क़ुर्आन का अध्ययन करें, तो धिक्कारे हुए शैतान से अललाह की शरण[ माँग लिया करें। ');
INSERT INTO "al_umari" VALUES(16,99,'16:99','वस्तुतः, उसका वश उनपर नहीं है, जो ईमान लाये हैं और अपने पालनहार ही पर भरोसा करते हैं।');
INSERT INTO "al_umari" VALUES(16,100,'16:100','उसका वश तो केवल उनपर चलता है, जो उसे अपना संरक्षक बनाते हैं और जो मिश्रणवादी (मुश्रिक) हैं।');
INSERT INTO "al_umari" VALUES(16,101,'16:101','और जब हम किसी आयत (विधान) के स्थान पर कोई आयत बदल देते हैं और अल्लाह ही अधिक जानता है उसे, जिसे वह उतारता है, तो कहते हैं कि आप तो केवल घड़ लेते हैं, बल्कि उनमें अधिक्तर जानते ही नहीं।');
INSERT INTO "al_umari" VALUES(16,102,'16:102','आप कह दें कि इसे (रूह़ुल कुदुस)[ ने आपके पालनहार की ओर से सत्य के साथ क्रमशः उतारा है, ताकि उन्हें सुदृढ़ कर दे, जो ईमान लाये हैं तथा मार्गदर्शन और शुभ सूचना है, आज्ञाकारियों के लिए। ');
INSERT INTO "al_umari" VALUES(16,103,'16:103','तथा हम जानते हैं कि वे (काफ़िर) कहते हैं कि उसे (नबी को) कोई मनुष्य सिखा रहा[ है। जबकी उसकी भाषा जिसकी ओर संकेत करते हैं, विदेशी है और ये स्पष्ट अरबी भाषा है।');
INSERT INTO "al_umari" VALUES(16,104,'16:104','वास्तव में, जो अल्लाह की आयतों पर ईमान नहीं लाते, उन्हें अल्लाह सुपथ नहीं दर्शाता और उन्हीं के लिए दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(16,105,'16:105','झूठ केवल वही घड़ते हैं, जो अल्लाह की आयतों पर ईमान नहीं लाते और वही मिथ्यावादी (झूठे) हैं।');
INSERT INTO "al_umari" VALUES(16,106,'16:106','जिसने अल्लाह के साथ कुफ़्र किया, अपने ईमान लाने के पश्चात्, परन्तु जो बाध्य कर दिया गया हो, इस दशा में कि उसका दिल ईमान से संतुष्ट हो, (उसके लिए क्षमा है)। परन्तु जिसने कुफ़्र के साथ सीना खोल दिया[ हो, तो उन्हीं पर अल्लाह का प्रकोप है और उन्हीं के लिए महा यातना है। ');
INSERT INTO "al_umari" VALUES(16,107,'16:107','ये इसलिए कि उन्होंने सांसारिक जीवन को प्रलोक पर प्राथमिकता दी है और वास्तव में अल्लाह, काफ़िरों को सुपथ नहीं दिखाता।');
INSERT INTO "al_umari" VALUES(16,108,'16:108','वही लोग हैं, जिनके दिलों, कानों और आँखों पर अल्लाह ने मुहर लगा दी है तथा यही लोग अचेत हैं।');
INSERT INTO "al_umari" VALUES(16,109,'16:109','निश्चय वही लोग, प्रलोक में क्षतिग्रस्त होने वाले हैं।');
INSERT INTO "al_umari" VALUES(16,110,'16:110','फिर वास्तव में, आपका पालनहार उन लोगों[ के लिए जिन्होंने हिजरत (प्रस्थान) की और उसके पश्चात् परीक्षा में डाले गये, फिर जिहाद किया और सहनशील रहे, वास्तव में, आपका पालनहार इस (परीक्षा) के पश्चात् बड़ा क्षमाशील, दयावान् है। ');
INSERT INTO "al_umari" VALUES(16,111,'16:111','जिस दिन प्रत्येक प्राणी को अपने बचाव की चिन्ता होगी और प्रत्येक प्राणी को उसके कर्मों का पूरा बदला दिया जायेगा और उनपर अत्याचार नहीं किया जायेगा।');
INSERT INTO "al_umari" VALUES(16,112,'16:112','अल्लाह ने एक बस्ती का उदाहरण दिया है, जो शान्त संतुष्ट थी, उसकी जीविका प्रत्येक स्थान से प्राचूर्य के साथ पहुँच रही थी, तो उसने अल्लाह के उपकारों के साथ कुफ़्र किया। तब अल्ल्लाह ने उसे भूख और भय का वस्त्र चखा[ दिया, उसके बदले जो वह कर रहे थे।');
INSERT INTO "al_umari" VALUES(16,113,'16:113','और उनके पास एक[ रसूल उन्हीं में से आया, तो उन्होंने उसे झुठला दिया। अतः उन्हें यातना ने पकड़ लिया और वे अत्याचारी थे। ');
INSERT INTO "al_umari" VALUES(16,114,'16:114','अतः उसमें से खाओ, जो अल्लाह ने तुम्हें ह़लाल (वैध) स्वच्छ जीविका प्रदान की है और अल्लाह का उपकार मानो, यदि तुम उसी की इबादत (वंदना) करते हो।');
INSERT INTO "al_umari" VALUES(16,115,'16:115','जो कुछ उसने तुमपर ह़राम (अवैध) किया है, वह मुर्दार, रक्त और सुअर का मांस है और जिसपर अल्लाह के सिवा दूसरे का नाम लिया गया[ हो, फिर जो भूख से आतुर हो जाये, इस दशा में कि वह नियम न तोड़ रहा हो और न आवश्यक्ता से अधिक खाये, तो वास्तव में, अल्लाह अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(16,116,'16:116','और मत कहो -उस झूठ के कारण, जो तुम्हारी ज़ुबानों पर आ जाये- कि ये ह़लाल (वैध) है और ये ह़राम (अवैध) है, ताकि अल्लाह पर मिथ्यारोप[ करो। वास्तव में, जो लोग अल्लाह पर मिथ्यारोप करते हैं, वे (कभी) सफल नहीं होते। ');
INSERT INTO "al_umari" VALUES(16,117,'16:117','(इस मिथ्यारोपन का) लाभ तो थोड़ा है और उन्हीं के लिए (परलोक में) दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(16,118,'16:118','और उनपर, जो यहूदी हो गये, हमने उसे ह़राम (अवैध) कर दिया, जिसका वर्णन हमने इस[ से पहले आपसे कर दिया है और हमने उनपर अत्याचार नहीं किया, परन्तु वे स्वयं अपने ऊपर अत्याचार कर रहे थे। ');
INSERT INTO "al_umari" VALUES(16,119,'16:119','फिर वास्तव में, आपका पालनहार उन्हें, जो अज्ञानता के कारण बुराई कर बैठे, फिर उसके पश्चात् क्षमा याचना कर ली और अपना सुधार कर लिया, वास्तव में, आपका पालनहार इसके पश्चात् अति क्षमी, दयावान् है।');
INSERT INTO "al_umari" VALUES(16,120,'16:120','वास्तव में, इब्राहीम एक समुदाय[ था, अल्लाह का आज्ञाकारी एकेश्वरवादी था और मिश्रणवादियों (मुश्रिकों) में से नहीं था। ');
INSERT INTO "al_umari" VALUES(16,121,'16:121','उसके उपकारों को मानता था, उसने उसे चुन लिया और उसे सीधी राह दिखा दी।');
INSERT INTO "al_umari" VALUES(16,122,'16:122','और हमने उसे संसार में भलाई दी और वास्तव में वह परलोक में सदाचारियों में से होगा।');
INSERT INTO "al_umari" VALUES(16,123,'16:123','फिर हमने (हे नबी!) आपकी और वह़्यी की कि एकेश्वरवादी इब्राहीम के धर्म का अनुसरण करो और वह मिश्रणवादियों में से नहीं था।');
INSERT INTO "al_umari" VALUES(16,124,'16:124','सब्त[ (शनिवार का दिन) तो उन्हीं पर, निर्धारित किया गया, जिन्होंने उसमें विभेद किया। और वस्तुतः, आपका पालनहार उनके बीच उसमें निर्णय कर देगा, जिसमें वे विभेद कर रहे थे। ');
INSERT INTO "al_umari" VALUES(16,125,'16:125','(हे नबी!) आप उन्हें अपने पालनहार की राह (इस्लाम) की ओर तत्वदर्शिता तथा सदुपदेश के साथ बुलाएँ और उनसे ऐसे अंदाज़ में शास्त्रार्थ करें, जो उत्तम हो। वास्तव में, अल्लाह उसे अधिक जानता है, जो उसकी राह से विचलित हो गया और वही सुपथों को भी अधिक जानता है।');
INSERT INTO "al_umari" VALUES(16,126,'16:126','और यदि तुम लोग बदला लो, तो उतना ही लो, जितना तुम्हें सताया गया हो और यदि सहन कर जाओ, तो सहनशीलों के लिए यही उत्त्म है।');
INSERT INTO "al_umari" VALUES(16,127,'16:127','और (हे नबी!) आप सहन करें और आपका सहन करना अल्लाह ही की सहायता से है और उनके (दुर्व्यवहार) पर शोक न करें और न उनके षड्यंत्र से तनिक भी संकुचित हों।');
INSERT INTO "al_umari" VALUES(16,128,'16:128','वास्तव में, अल्लाह उन लोगों के साथ है, जो सदाचारी हैं और जो उपकार करने वाले हैं।');
INSERT INTO "al_umari" VALUES(17,1,'17:1','पवित्र है वह जिसने रात्रि के कुछ क्षण में अपने भक्त[ को मस्जिदे ह़राम (मक्का) से मस्जिदे अक़्सा तक यात्रा कराई। जिसके चतुर्दिग हमने सम्पन्नता रखी है, ताकि उसे अपनी कुछ निशानियों का दर्शन कराएँ। वास्तव में, वह सब कुछ सुनने-जानने वाला है। ');
INSERT INTO "al_umari" VALUES(17,2,'17:2','और हमने मूसा को पुस्तक प्रदान की और उसे बनी इस्राईल के लिए मार्गदर्शन का साधन बनाया कि मेरे सिवा किसी को कार्यसाधक[ न बनाओ। ');
INSERT INTO "al_umari" VALUES(17,3,'17:3','हे उनकी संतति जिन्हें हमने नूह़ के साथ (नौका) में सवार किया। वास्तव में, वह अति कृतज्ञ[ भक्त था। ');
INSERT INTO "al_umari" VALUES(17,4,'17:4','और हमने बनी इस्राईल को, उनकी पुस्तक में सूचित कर दिया था कि तुम इस[ धरती में दो बार उपद्रव करोगे और बड़ा अत्याचार करोगे। ');
INSERT INTO "al_umari" VALUES(17,5,'17:5','तो जब प्रथम उपद्रव का समय आया, तो हमने तुमपर अपने प्रबल योध्दा भक्तों को भेज दिया, जो नगरों में घुस गये और इस वचन को पूरा होना ही[ था। ');
INSERT INTO "al_umari" VALUES(17,6,'17:6','फिर हमने उनपर तुम्हें पुनः प्रभुत्व दिया तथा धनों और पुत्रों द्वारा तुम्हारी सहायता की और तुम्हारी संख्या बहुत अधिक कर दी।');
INSERT INTO "al_umari" VALUES(17,7,'17:7','यदि तुम भला करोगे, तो अपने लिए और यदि बुरा करोगे, तो अपने लिए। फिर जब दूसरे उपद्रव का समय आया ताकि (शत्रु) तुम्हारे चेहरे बिगाड़ दें और मस्जिद (अक़्सा) में वैसे ही प्रवेश कर जायेँ, जैसे प्रथम बार प्रवेश कर गये और ताकि जो भी उनके हाथ आये, उसे पूर्णता नाश[ कर दे। ');
INSERT INTO "al_umari" VALUES(17,8,'17:8','संभव है कि तुम्हारा पालनहार तुमपर दया करे और यदि तुम प्रथम स्थिति पर आ गये, तो हमभी फिर[ आयेंगे और हमने नरक को काफ़िरों के लिए कारावास बना दिया है। ');
INSERT INTO "al_umari" VALUES(17,9,'17:9','वास्तव में, ये क़ुर्आन वह डगर दिखाता है, जो सबसे सीधी है और उन ईमान वालों को शुभ सूचना देता है, जो सदाचार करते हैं कि उन्हीं के लिए बहुत बड़ा प्रतिफल है।');
INSERT INTO "al_umari" VALUES(17,10,'17:10','और जो आख़िरत (परलोक) पर ईमान नहीं लाते, हमने उनके लिए दुःखदायी यातना तैयार कर रखी है।');
INSERT INTO "al_umari" VALUES(17,11,'17:11','और मनुष्य (क्षुब्ध होकर) अभिशाप करने लगता[ है, जैसे भलाई के लिए प्रार्थना करता है और मनुष्य बड़ा ही उतावला है। ');
INSERT INTO "al_umari" VALUES(17,12,'17:12','और हमने रात्री तथा दिवस को दो प्रतीक बनाया, फिर रात्री के प्रतीक को हमने अन्धकार बनाया तथा दिवस के प्रतीक को प्रकाशयुक्त, ताकि तुम अपने पालनहार के अनुग्रह की (जीविका) की खोज करो और वर्षों तथा ह़िसाब की गिनती जानो तथा हमने प्रत्येक चीज़ का सविस्तार वर्णन कर दिया।');
INSERT INTO "al_umari" VALUES(17,13,'17:13','और प्रत्येक मनुष्य के कर्मपत्र को हमने उसके गले का हार बना दिया है और हम उसके लिए प्रलय के दिन एक कर्मलेख निकालेंगे, जिसे वह खुला हुआ पायेगा।');
INSERT INTO "al_umari" VALUES(17,14,'17:14','अपना कर्मलेख पढ़ लो, आज तू स्वयं अपना ह़िसाब लेने के लिए पर्याप्त है।');
INSERT INTO "al_umari" VALUES(17,15,'17:15','जिसने सीधी राह अपनायी, उसने अपने ही लिए सीधी राह अपनायी और जो सीधी राह से विचलित हो गया, उसका (दुष्परिणाम) उसीपर है और कोई दूसरे का बोझ (अपने ऊपर) नहीं लादेगा[ और हम यातना देने वाले नहीं हैं, जब तक कि कोई रसूल न भेजें ।');
INSERT INTO "al_umari" VALUES(17,16,'17:16','और जब हम किसी बस्ती का विनाश करना चाहते हैं, तो उसके सम्पन्न लोगों को आदेश[ देते हैं, फिर वे उसमें उपद्व करने लगते हैं, तो उसपर यातना की बात सिध्द हो जाती है और हम उसका पूर्णता उनसूलन कर देते हैं।');
INSERT INTO "al_umari" VALUES(17,17,'17:17','और हमने बहुत-सी जातियों का नूह़ के पश्चात् विनाश किया है और आपका पालनहार अपने दासों के पापों से सूचित होने-देखने को बहुत है।');
INSERT INTO "al_umari" VALUES(17,18,'17:18','जो संसार ही चाहता हो, हम उसे यहीं दे देते हैं, जो हम चाहते हैं, जिसके लिए चाहते हैं। फिर हम उसका परिणाम (परलोक में) नरक बना देते हैं, जिसमें वह निंदित-तिरस्कृत होकर प्रवेश करेगा।');
INSERT INTO "al_umari" VALUES(17,19,'17:19','तथा जो परलोक चाहता हो और उसके लिए प्रयास करता हो और वह एकेश्वरवादी हो, तो वही हैं, जिनके प्रयास का आदर सम्मान किया जायेगा।');
INSERT INTO "al_umari" VALUES(17,20,'17:20','हम प्रत्येक की सहायता करते हैं, इनकी भी और उनकी भी और आपके पालनहार का प्रदान (किसी से) निषेधित (रोका हुआ) नहीं[ है। ');
INSERT INTO "al_umari" VALUES(17,21,'17:21','आप विचार करें कि कैसे हमने (संसार में) उनमें से कुछ को कुछ पर प्रधानता दी है और निश्चय परलोक के पद और प्रधानता और भी अधिक होगी।');
INSERT INTO "al_umari" VALUES(17,22,'17:22','(हे मानव!) अल्लाह के साथ कोई दूसरा पूज्य न बना, अन्यथा बुरा और असहाय होकर रह जायेगा।');
INSERT INTO "al_umari" VALUES(17,23,'17:23','और (हे मनुष्य!) तेरे पालनहार ने आदेश दिया है कि उसके सिवा किसी की इबादत (वंदना) न करो तथा माता-पिता के साथ उपकार करो, यदि तेरे पास दोनों में से एक वृध्दावस्था को पहुँच जाये अथवा दोनों, तो उन्हें उफ़ तक न कहो और न झिड़को और उनसे सादर बात बोलो।');
INSERT INTO "al_umari" VALUES(17,24,'17:24','और उनके लिए विनम्रता का बाज़ू दया से झुका[ दो और प्रार्थना करोः हे मेरे पालनहार! उन दोनों पर दया कर, जैसे उन दोनों ने बाल्यावस्था में, मेरा लालन-पालन किया है। ');
INSERT INTO "al_umari" VALUES(17,25,'17:25','तुम्हारा पालनहार अधिक जानता है, जो कुछ तुम्हारी अन्तरात्माओं (मन) में है। यदि तुम सदाचारी रहे, तो वह अपनी ओर ध्यानमग्न रहने वालों के लिए अति क्षमावान् है।');
INSERT INTO "al_umari" VALUES(17,26,'17:26','और समीपवर्तियों को उनका स्वत्व (हिस्सा) दो तथा दरिद्र और यात्री को और अपव्यय[ न करो। ');
INSERT INTO "al_umari" VALUES(17,27,'17:27','वास्तव में, अपव्ययी शैतान के भाई हैं और शैतान अपने पालनहार का अति कृतघ्न है।');
INSERT INTO "al_umari" VALUES(17,28,'17:28','और यदि आप उनसे विमुख हों, अपने पालनहार की दया की खोज के लिए जिसकी आशा रखते हों, तो उनसे सरल[ बात बोलें। ');
INSERT INTO "al_umari" VALUES(17,29,'17:29','और अपना हाथ अपनी गर्दन से न बाँध[ लो और न उसे पूरा खोल दो कि निन्दित, विवश होकर रह जाओ। ');
INSERT INTO "al_umari" VALUES(17,30,'17:30','वास्तव में, आपका पालनहार ही विस्तृत कर देता है जीविका को जिसके लिए चाहता है, तथा संकीर्ण कर देता है। वास्तव में, वही अपने दासों (बंदों) से अति सूचित[ देखने वाला है।');
INSERT INTO "al_umari" VALUES(17,31,'17:31','और अपनी संतान को निर्धन हो जाने के भय से वध न करो, हम उन्हें तथा तुम्हें जीविका प्रदान करेंगे, वास्तव में, उन्हें वध करना महा पाप है।');
INSERT INTO "al_umari" VALUES(17,32,'17:32','और व्यभिचार के समीप भी न जाओ, वास्तव में, वह निर्लज्जा तथा बुरी रीति है।');
INSERT INTO "al_umari" VALUES(17,33,'17:33','और किसी प्राण को जिसे अल्लाह ने ह़राम (अवैध) किया है, वध न करो, परन्तु धर्म विधान[ के अनुसार और जो अत्याचार से वध (निहत) किया गया, हमने उसके उत्तराधिकारी को अधिकार प्रदान किया है। अतः वह वध करने में अतिक्रमण न करे, वास्तव में, उसे सहायता दी गयी है।');
INSERT INTO "al_umari" VALUES(17,34,'17:34','और अनाथ के धन के समीप भी न जाओ, परन्तु ऐसी रीति से, जो उत्तम हो, यहाँ तक कि वह अपनी युवा अवस्था को पहुँच जाये और वचन पूरा करो, वास्तव में, वचन के विषय में प्रश्न किया जायेगा।');
INSERT INTO "al_umari" VALUES(17,35,'17:35','और पूरा नापकर दो, जब नापो और सह़ीह़ तराजू से तोलो। ये अधिक अच्छा और इसका परिणाम उत्तम है।');
INSERT INTO "al_umari" VALUES(17,36,'17:36','और ऐसी बात के पीछे न पड़ो, जिसका तुम्हें कोई ज्ञान न हो, निश्चय कान तथा आँख और दिल, इन सबके बारे में (प्रलय के दिन) प्रश्न किया जायेगा[। ');
INSERT INTO "al_umari" VALUES(17,37,'17:37','और धरती में अकड़कर न चलो, वास्तव में, न तुम धरती को फाड़ सकोगे और न लम्बाई में पर्वतों तक पहुँच सकोगे।');
INSERT INTO "al_umari" VALUES(17,38,'17:38','ये सब बातें हैं। इनमें बुरी बात आपके पालनहार को अप्रिय हैं।');
INSERT INTO "al_umari" VALUES(17,39,'17:39','ये तत्वदर्शिता की वो बातें हैं, जिनकी वह़्यी (प्रकाशना) आपकी ओर आपके पालनहार ने की है और अल्लाह के साथ कोई दूसरा पूज्य न बना लेना, अन्यथा नरक में निन्दित तिरस्कृत करके फेंक दिये जाओगे।');
INSERT INTO "al_umari" VALUES(17,40,'17:40','क्या तुम्हारे पालनहार ने तुम्हें पुत्र प्रदान करने के लिए विशेष कर लिया है और स्वयं फ़रिश्तों को पुत्रियाँ बना लिया है? वास्तव में, तुम बहुत बड़ी बात कह रहे हो[। ');
INSERT INTO "al_umari" VALUES(17,41,'17:41','और हमने विविध प्रकार से इस क़ुर्आन में (तथ्यों का) वर्णन कर दिया है, ताकि लोग शिक्षा ग्रहण करें, परन्तु उसने उनकी घृणा को और अधिक कर दिया।');
INSERT INTO "al_umari" VALUES(17,42,'17:42','आप कह दें कि यदि अल्लाह के साथ दूसरे पूज्य होते, जैसा कि वे (मिश्रणवादी) कहते हैं, तो वे अर्श (सिंहासन) के स्वामी (अल्लाह) की ओर अवश्य कोई राह[ खोजते। ');
INSERT INTO "al_umari" VALUES(17,43,'17:43','वह पवित्र और बहुत उच्च है, उन बातों से जिन्हें वे बनाते हैं।');
INSERT INTO "al_umari" VALUES(17,44,'17:44','उसकी पवित्रता का वर्णन कर रहे हैं सातों आकाश तथा धरती और जो कुछ उनमें है और नहीं है कोई चीज़ परन्तु वह उसकी प्रशंसा के साथ उसकी पवित्रता का वर्णन कर रही है, किन्तु तुम उनके पवित्रता गान को समझते नहीं हो। वास्तव में, वह अति सहिष्णु, क्षमाशील है।');
INSERT INTO "al_umari" VALUES(17,45,'17:45','और जब आप क़ुर्आन पढ़ते हैं, तो हम आपके बीच और उनके बीच, जो आख़िरत (परलोक) पर ईमान नहीं लाते, एक छुपा हुआ आवरण (पर्दा) बना देते[ हैं। ');
INSERT INTO "al_umari" VALUES(17,46,'17:46','तथा उनके दिलों पर ऐसे खोल चढ़ा देते हैं कि उस (क़ुर्आन) को न समझें और उनके कानों में बोझ और जब आप अपने अकेले पालनहार की चर्चा क़ुर्आन में करते हैं, तो वह घृणा से मुँह फेर लेते हैं।');
INSERT INTO "al_umari" VALUES(17,47,'17:47','और हम उनके विचारों से भली-भाँति अवगत हैं, जब वे कान लगाकर आपकी बात सुनते हैं और जब वे आपस में कानाफूसी करते हैं, जब वे अत्याचारी कहते हैं कि तुम लोग तो बस एक जादू किये हुए व्यक्ति का अनुसरण[ करते हो। ');
INSERT INTO "al_umari" VALUES(17,48,'17:48','सोचिए कि वे आपके लिए कैसे उदाहरण दे रहे हैं? अतः वे कुपथ हो गये, वे सीधी राह नहीं पा सकेंगे।');
INSERT INTO "al_umari" VALUES(17,49,'17:49','और उन्होंने कहाः क्या हम, जब अस्थियाँ और चूर्ण-विचूर्ण हो जायेंगे तो क्या हम वास्तव में, नई उत्पत्ति में पुनः जीवित कर दिये[ जायेंगे? ');
INSERT INTO "al_umari" VALUES(17,50,'17:50','आप कह दें कि पत्थर बन जाओ या लोहा।');
INSERT INTO "al_umari" VALUES(17,51,'17:51','अथवा कोई उत्पत्ति, जो तुम्हारे मन में इससे बड़ी हो। फिर वे पूछते हैं कि कौन हमें पुनः जीवित करेगा? आप कह दें: वही, जिसने प्रथम चरण में तुम्हारी उत्पत्ति की है। फिर वे आपके आगे सिर हिलायेंगे[ और कहेंगेः ऐसा कब होगा? आप कह दें कि संभवता वह समीप ही है। ');
INSERT INTO "al_umari" VALUES(17,52,'17:52','जिस दिन वह तुम्हें पुकारेगा, तो तुम उसकी प्रशंसा करते हुए स्वीकार कर लोगे[ और ये सोचोगे कि तुम (संसार में) थोड़े ही समय रहे हो। ');
INSERT INTO "al_umari" VALUES(17,53,'17:53','और आप मेरे भक्तों से कह दें कि वह बात बोलें, जो उत्तम हो, वास्तव में, शैतान उनके बीच बिगाड़ उत्पन्न करना चाहता[ है। निश्चय शैतान मनुष्य का खुला शत्रु है। ');
INSERT INTO "al_umari" VALUES(17,54,'17:54','तुम्हारा पालनहार, तुमसे भली-भाँति अवगत है, यदि चाहे, तो तुमपर दया करे अथवा यदि चाहे, तो तुम्हें यातना दे और हमने आपको उनपर निरीक्षक बनाकर नहीं भेजा[ है। ');
INSERT INTO "al_umari" VALUES(17,55,'17:55','(हे नबी!) आपका पालनहार भली-भाँति अवगत है उससे, जो आकाशों तथा धरती में है और हमने प्रधानता दी है कुछ नबियों को कुछ पर और हमने दावूद को ज़बूर (पुस्तक) प्रदान की।');
INSERT INTO "al_umari" VALUES(17,56,'17:56','आप कह दें कि उन्हें पुकारो, जिन्हें उस (अल्लाह) के सिवा (पूज्य) समझते हो। न वे तुमसे दुःख दूर कर सकते और न (तुम्हारी दशा) बदल सकते हैं।');
INSERT INTO "al_umari" VALUES(17,57,'17:57','वास्तव में, जिन्हें ये लोग[ पुकारते हैं, वे स्वयं अपने पालनहार का सामीप्य प्राप्त करने का साधन खोजते हैं कि कौन अधिक समीप है? और उसकी दया की आशा रखते हैं और उसकी यातना से डरते हैं। वास्तव में, आपके पालनहार की यातना डरने योग्य है।');
INSERT INTO "al_umari" VALUES(17,58,'17:58','और कोई (अत्याचारी) बस्ती नहीं है, परन्तु हम उसे प्रलय के दिन से पहले ध्वस्त करने वाले या कड़ी यातना देने वाले हैं। ये (अल्लाह के) लेख में अंकित है।');
INSERT INTO "al_umari" VALUES(17,59,'17:59','और हमें नहीं रोका इससे कि हम निशानियाँ भेजें, किन्तु इस बात ने कि विगत लोगों ने उन्हें झुठला[ दिया और हमने समूद को ऊँटनी का खुला चमत्कार दिया, तो उन्होंने उसपर अत्याचार किया और हम चमत्कार डराने के लिए ही भेजते हैं। ');
INSERT INTO "al_umari" VALUES(17,60,'17:60','और (हे नबी!) याद करो, जब हमने आपसे कह दिया था कि आपके पालनहार ने लोगों को अपने नियंत्रण में ले रखा है और ये जो कुछ हमने आपको दिखाया,[ उसको और उस वृक्ष को जिसपर क़ुर्आन में धिक्कार की गयी है, हमने लोगों के लिए एक परीक्षा बना दिया है और हम उन्हें चेतावनी पर चेतावनी दे रहे हैं, फिर भी ये उनकी अवज्ञा को ही अधिक करती जा रही है।');
INSERT INTO "al_umari" VALUES(17,61,'17:61','और (याद करो), जब हमने फ़रिश्तों से कहा कि आदम को सज्दा करो, तो इब्लीस के सिवा सबने सज्दा किया। उसने कहाः क्या मैं उसे सज्दा करूँ, जिसे तूने गारे से उत्पन्न किया है?');
INSERT INTO "al_umari" VALUES(17,62,'17:62','(तथा) उसने कहाः तू बता, क्या यही है, जिसे तूने मुझपर प्रधानता दी है? यदि तूने मुझे प्रलय के दिन तक अवसर दिया, तो मैं उसकी संतति को अपने नियंत्रण में कर लूँगा[ कुछ के सिवा। ');
INSERT INTO "al_umari" VALUES(17,63,'17:63','अल्लाह ने कहाः "चले जाओ", जो उनमें से तेरा अनुसरण करेगा, तो निश्चय नरक तुमसबका प्रतिकार (बदला) है, भरपूर बदला।');
INSERT INTO "al_umari" VALUES(17,64,'17:64','तू उनमें से जिसे हो सके, अपनी ध्वनि[ से बहका ले और उनपर अपनी सवार और पैदल (सेना) चढ़ा ले और उनका (उनके) धनों और संतानों में साझी बन जा तथा उन्हें (मिथ्या) वचन दे और शैतान उन्हें धोखे के सिवा (कोई) वचन नहीं देता।');
INSERT INTO "al_umari" VALUES(17,65,'17:65','वास्तव में, जो मेरे भक्त हैं, उनपर तेरा कोई वश नहीं चल सकता और आपके पालनहार का सहायक होना ये बहुत है।');
INSERT INTO "al_umari" VALUES(17,66,'17:66','तुम्हारा पालनहार तो वह है, जो तुम्हारे लिए सागर में नौका चलाता है, ताकि तुम उसकी जीविका की खोज करो, वास्तव में, वह तुम्हारे लिए अति दयावान् है।');
INSERT INTO "al_umari" VALUES(17,67,'17:67','और जब सागर में तुमपर कोई आपदा आ पड़ती है, तो अल्लाह के सिवा जिन्हें तुम पुकारते हो, खो देते (भूल जाते) हो[ और जब तुम्हें बचाकर थल तक पहुँचा देता है, तो मुख फेर लेते हो और मनुष्य है ही अति कृतघ्न। ');
INSERT INTO "al_umari" VALUES(17,68,'17:68','क्या तुम निर्भय हो गये हो कि अल्लाह तुम्हें थल (धरती) ही में धंसा दे अथवा तुमपर फथरीली आँधी भेज दे? फिर तुम अपना कोई रक्षक न पाओ।');
INSERT INTO "al_umari" VALUES(17,69,'17:69','या तुम निर्भय हो गये हो कि फिर उस (सागर) में तुम्हें दूसरी बार ले जाये, फिर तुमपर वायु का प्रचण्ड झोंका भेज दे, फिर तुम्हें डुबा दे, उस कुफ़्र के बदले, जो तुमने किया है। फिर तुम अपने लिए उसे नहीं पाओगे, जो हमपर इसका दोष[ धरे। ');
INSERT INTO "al_umari" VALUES(17,70,'17:70','और हमने बनी आदम (मानव) को प्रधानता दी और उन्हें थल और जल में सवार[ किया और उन्हें स्वच्छ चीज़ों से जीविका प्रदान की और हमने उन्हें बहुत-सी उन चीज़ों पर प्रधानता दी, जिनकी हमने उत्पत्ति की है। ');
INSERT INTO "al_umari" VALUES(17,71,'17:71','जिस दिन हम, सब लोगों को उनके अग्रणी के साथ बुलायेंगे, तो जिनका कर्मलेख उनके सीधे हाथ में दिया जायेगा, तो वही अपना कर्मलेख पढ़ेंगे और उनपर धागे बराबर भी अत्याचार नहीं किया जायेगा।');
INSERT INTO "al_umari" VALUES(17,72,'17:72','और जो इस (संसार) में अंधा[ रह गया, तो वह आख़िरत (परलोक) में भी अन्धा और अधिक कुपथ होगा। ');
INSERT INTO "al_umari" VALUES(17,73,'17:73','और (हे नबी!) वह (काफ़िर) समीप था कि आपको उस वह़्यी से फेर दें, जो हमने आपकी ओर भेजी है, ताकि आप हमारे ऊपर अपनी ओर से कोई दूसरी बात घड़ लें और उस समय वे आपको अवश्य अपना मित्र बना लेते।');
INSERT INTO "al_umari" VALUES(17,74,'17:74','और यदि हम आपको सुदृढ़ न रखते, तो आप उनकी ओर कुछ न कुछ झुक जाते।');
INSERT INTO "al_umari" VALUES(17,75,'17:75','तब हम आपको जीवन की दुगुनी तथा मरण की दोहरी यातना चखाते। फिर आप अपने लिए हमारे ऊपर कोई सहायक न पाते।');
INSERT INTO "al_umari" VALUES(17,76,'17:76','और समीप है कि वे आपको इस धरती (मक्का) से विचला दें, ताकि आपको उससे निकाल दें, तब व आपके पश्चात् कुछ ही दिन रह सकेंगे।');
INSERT INTO "al_umari" VALUES(17,77,'17:77','ये[ उसके लिए नियम रहा है, जिसे हमने आपसे पहले अपने रसूलों में से भेजा है और आप हमारे नियम में कोई परिवर्तन नहीं पायेंगे। ');
INSERT INTO "al_umari" VALUES(17,78,'17:78','आप नमाज़ की स्थापना करें, सूर्यास्त से रात के अन्धेरे[ तक तथा प्रातः (फ़ज्र के समय) क़ुर्आन पढ़िये। वास्तव में, प्रातः क़ुर्आन पढ़ना उपस्थिति का समय है।');
INSERT INTO "al_umari" VALUES(17,79,'17:79','तथा आप रात के कुछ समय जागिए, फिर "तह़जुद[" पढ़िये। ये आपके लिए अधिक (नफ़्ल) है। संभव है आपका पालनहार आपको "मक़ामे मह़मूद " प्रदान कर दे।');
INSERT INTO "al_umari" VALUES(17,80,'17:80','और प्रार्थना करें कि मेरे पालनहार! मुझे प्रवेश[ दे सत्य के साथ, निकाल सत्य के साथ तथा मेरे लिए अपनी ओर से सहायक प्रभुत्व बना दे। ');
INSERT INTO "al_umari" VALUES(17,81,'17:81','तथा कहिए कि सत्य आ गया और असत्य ध्वस्त-निरस्त हो गया, वास्तव में, असत्य को धवस्त-निरस्त होना ही है[। ');
INSERT INTO "al_umari" VALUES(17,82,'17:82','और हम क़ुर्आन में से वह चीज़ उतार रहे हैं, जो आरोग्य तथा दया है, ईमान वालों के लिए और वह अत्याचारियों की क्षति को ही अधिक करता है।');
INSERT INTO "al_umari" VALUES(17,83,'17:83','और जब हम मानव पर उपकार करते हैं, तो मुख फेर लेता है और दूर हो जाता[ है तथा जब उसे दुःख पहुँचता है, तो निराश हो जाता है। ');
INSERT INTO "al_umari" VALUES(17,84,'17:84','आप कह दें कि प्रत्येक अपनी आस्था के अनुसार कर्म कर रहा है, तो आपका पालनहार ही भली-भाँति जान रहा है कि कौन अधिक सीधी डगर पर है।');
INSERT INTO "al_umari" VALUES(17,85,'17:85','(हे नबी!) लोग आपसे रूह़[ के विषय में पूछते हैं, आप कह दें: रूह़ मेरे पालनहार के आदेश से है और तुम्हें जो ज्ञान दिया गया, वह बहुत थोड़ा है। ');
INSERT INTO "al_umari" VALUES(17,86,'17:86','और यदि हम चाहें, तो वह सब कुछ ले जायें, जो आपकी ओर हमने वह़्यी किया है, फिर आप हमपर अपना कोई सहायक नहीं पायेंगे।');
INSERT INTO "al_umari" VALUES(17,87,'17:87','किन्तु आपके पालनहार की दया के कारण (ये आपको प्राप्त है)। वास्तव में, उसका प्रदान आपपर बहुत बड़ा है।');
INSERT INTO "al_umari" VALUES(17,88,'17:88','आप कह दें: यदि सब मनुष्य तथा जिन्न इसपर एकत्र हो जायें कि इस क़ुर्आन के समान ले आयेंगे, तो इसके समान नहीं ला सकेंगे, चाहे वे एक-दूसरे के समर्थक ही क्यों न हो जायें!');
INSERT INTO "al_umari" VALUES(17,89,'17:89','और हमने लोगों के लिए इस क़ुर्आन में प्रत्येक उदाहरण विविध शैली में वर्णित किया है, फिर भी अधिक्तर लोगों ने कुफ़्र के सिवा अस्वीकार ही किया है।');
INSERT INTO "al_umari" VALUES(17,90,'17:90','और उन्होंने कहाः हम आपपर कदापि ईमान नहीं लायेंगे, यहाँ तक कि आप हमारे लिए धरती से एक चश्मा प्रवाहित कर दें।');
INSERT INTO "al_umari" VALUES(17,91,'17:91','अथवा आपके लिए खजूर अथवा अंगूर का कोई बाग़ हो, फिर उसके बीच आप नहरें प्रवाहित कर दें।');
INSERT INTO "al_umari" VALUES(17,92,'17:92','अथवा हमपर आकाश को जैसा आपका विचार है, खण्ड-खण्ड करके गिरा दें या अल्लाह और फ़रिश्तों को साक्षात हमारे सामने ले आयें।');
INSERT INTO "al_umari" VALUES(17,93,'17:93','अथवा आपके लिए सोने का एक घर हो जाये अथवा आकाश में चढ़ जायें और हम आपके चढ़ने का भी कदापि विश्वास नहीं करेंगे, यहाँ तक की हमपर एक पुस्तक उतार लायें, जिसे हम पढ़ें। आप कह दें कि मेरा पालनहार पवित्र है, मैंतो बस एक रसूल (संदेशवाहक) मनुष्य[ हूँ। ');
INSERT INTO "al_umari" VALUES(17,94,'17:94','और नहीं रोका लोगों को कि वे ईमान लायें, जब उनके पास मार्गदर्शन[ आ गया, परन्तु इसने कि उन्होंने कहाः क्या अल्लाह ने एक मनुष्य को रसूल बनाकर भेजा है? ');
INSERT INTO "al_umari" VALUES(17,95,'17:95','(हे नबी!) आप कह दें कि यदि धरती में फ़रिश्ते निश्चिन्त होकर चलते-फिरते होते, तो हम अवश्य उनपर आकाश से कोई फ़रिश्ता रसूल बनाकर उतारते।');
INSERT INTO "al_umari" VALUES(17,96,'17:96','आप कह दें कि मेरे और तुम्हारे बीच अल्लाह का साक्ष्य[ बहुत है। वास्तव में, वह अपने दासों (बन्दों) से सूचित, सबको देखने वाला है। ');
INSERT INTO "al_umari" VALUES(17,97,'17:97','जिसे अल्लाह सुपथ दिखा दे, वही सुपथगामी है और जिसे कुपथ कर दे, तो आप कदापि नहीं पायेंगे, उनके लिए उसके सिवा कोई सहायक और हम उन्हें एकत्र करेंगे प्रलय के दिन उनके मुखों के बल, अंधे, गूँगे और बहरे बनाकर और उनका स्थान नरक है, जबभी वह बुझने लगेगी, तो हम उसे और भड़का देंगे।');
INSERT INTO "al_umari" VALUES(17,98,'17:98','यही उनका प्रतिकार (बदला) है, इसलिए कि उन्होंने हमारी आयतों के साथ कुफ़्र किया और कहाः क्या जब हम अस्थियाँ और चूर-चूर हो जायेंगे, तो नई उत्पत्ति में पुणः जीवित किये जायेंगे[? ');
INSERT INTO "al_umari" VALUES(17,99,'17:99','क्या वे विचार नहीं करते कि जिस अल्लाह ने आकाशों तथा धरती की उत्पत्ति की है, वह समर्थ है इस बात पर कि उनके जैसी उत्पत्ति कर दे[? तथा उसने उनके लिए एक निर्धारित अवधि बनायी है, जिसमें कोई संदेह नहीं। फिर भी अत्याचारियों ने कुफ़्र के सिवा अस्वीकार ही किया। ');
INSERT INTO "al_umari" VALUES(17,100,'17:100','आप कह दें कि यदि तुमही स्वामी होते, अपने पालनहार की दया के कोषों के, तबतो तुम खर्च हो जाने के भय से (अपने ही पास) रोक रखते और मनुष्य बड़ा ही कंजूस है।');
INSERT INTO "al_umari" VALUES(17,101,'17:101','और हमने मूसा को नौ खुली निशानियाँ दीं[, अतः बनी इस्राईल से आप पूछ लें, जब वह (मूसा) उनके पास आया, तो फ़िर्औन ने उससे कहाः हे मूसा! मैं समझता हूँ कि तुझपर जादू कर दिया गया है। ');
INSERT INTO "al_umari" VALUES(17,102,'17:102','उस (मूसा) ने उत्तर दियाः तुझे विश्वास है कि इन्हें आकाशों तथा धरती के पालनहार ही ने सोच-विचार करने के लिए उतारा है और हे फ़िर्औन! मैं तुम्हें निश्चय ध्वस्त समझता हूँ।');
INSERT INTO "al_umari" VALUES(17,103,'17:103','अन्तताः उसने निश्चय किया कि उन[ को धरती से उखाड़ फेंके, तो हमने उसे और उसके सब साथियों को डुबो दिया।');
INSERT INTO "al_umari" VALUES(17,104,'17:104','और हमने उसके पश्चात् बनी इस्राईल से कहाः तुम इस धरती में बस जाओ और जब आख़िरत के वचन का समय आयेगा, तो हम तुम्हें एकत्र कर लायेंगे।');
INSERT INTO "al_umari" VALUES(17,105,'17:105','और हमने सत्य के साथ ही इस (क़ुर्आन) को उतारा है तथा ये सत्य के साथ ही उतरा है और हमने आपको बस शुभ सूचना देने तथा सावधान करने वाला बनाकर भेजा है।');
INSERT INTO "al_umari" VALUES(17,106,'17:106','और इस क़ुर्आन को हमने थोड़ा-थोड़ा करके उतारा है, ताकि आप लोगों को इसे रुक-रुक कर सुनायें और हमने इसे क्रमशः[ उतारा है। ');
INSERT INTO "al_umari" VALUES(17,107,'17:107','आप कह दें कि तुम इसपर ईमान लाओ अथवा न लाओ, वास्तव में, जिन्हें इससे पहले ज्ञान दिया[ गया है, जब उन्हें ये सुनाया जाता है, तो वह मुँह के बल सज्दे में गिर जाते हैं। ');
INSERT INTO "al_umari" VALUES(17,108,'17:108','और कहते हैं: पवित्र है हमारा पालनहार! निश्चय हमारे पालनहार का वचन पूरा होकर रहा।');
INSERT INTO "al_umari" VALUES(17,109,'17:109','और वह मुँह के बल रोते हुए गिर जाते हैं और वह उनकी विनय को अधिक कर देता है।');
INSERT INTO "al_umari" VALUES(17,110,'17:110','हे नबी! आप कह दें कि (अल्लाह) कहकर पुकारो अथवा (रह़मान) कहकर पुकारो, जिस नाम से भी पुकारो, उसके सभी नाम शुभ[ हैं और (हे नबी!) नमाज़ में स्वर न तो ऊँचा करो और न उसे नीचा करो और इन दोनों के बीच की राह अपनाओ।');
INSERT INTO "al_umari" VALUES(17,111,'17:111','तथा कहो कि सब प्रशंसा उस अल्लाह के लिए है, जिसके कोई संतान नहीं और न राज्य में उसका कोई साझी है और न अपमान से बचाने के लिए उसका कोई समर्थक है और आप उसकी महिमा का वर्णन करें।');
INSERT INTO "al_umari" VALUES(18,1,'18:1','सब प्रशंसा उस अल्लाह के लिए है, जिसने अपने भक्त पर ये पुस्तक उतारी और उसमें कोई टेढ़ी बात नहीं रखी।');
INSERT INTO "al_umari" VALUES(18,2,'18:2','अति सीधी (पुस्तक), ताकि वह अपने पास की कड़ी यातना से सावधान कर दे और ईमान वालों को जो सदाचार करते हों, शुभ सूचना सुना दे कि उन्हीं के लिए अच्छा बदला है।');
INSERT INTO "al_umari" VALUES(18,3,'18:3','जिसमें वे नित्य सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(18,4,'18:4','और उन्हें सावधान करे, जिन्होंने कहा कि अल्लाह ने अपने लिए कोई संतान बना ली है।');
INSERT INTO "al_umari" VALUES(18,5,'18:5','उन्हें इसका कुछ ज्ञान है और न उनके पूर्वजों को। बहुत बड़ी बात है, जो उनके मुखों से निकल रही है, वे सरासर झूठ ही बोल रहे हैं।');
INSERT INTO "al_umari" VALUES(18,6,'18:6','तो संभवतः आप इसके पीछे अपना प्राण खो देंगे, संताप के कारण, यदि वे इस ह़दीस (क़ुर्आन) पर ईमान न लायें।');
INSERT INTO "al_umari" VALUES(18,7,'18:7','वास्तव में, जो कुछ धरती के ऊपर है, उसे हमने उसके लिए शोभा बनाया है, ताकि उनकी परीक्षा लें कि उनमें कौन कर्म में सबसे अच्छा है?');
INSERT INTO "al_umari" VALUES(18,8,'18:8','और निश्चय हम कर देने[ वाले हैं, जो उस (धरती) के ऊपर है, उसे (बंजर) धूल। ');
INSERT INTO "al_umari" VALUES(18,9,'18:9','(हे नबी!) क्या आपने समझा है कि गुफा तथा शिला लेख वाले[, हमारे अद्भुत लक्षणों (निशानियों) में से थे ?');
INSERT INTO "al_umari" VALUES(18,10,'18:10','जब नवयुवकों ने गुफा की ओर शरण ली[ और प्रार्थना कीः हे हमारे पालनहार! हमें अपनी विशेष दया प्रदान कर और हमारे लिए प्रबंध कर दे हमारे विषय के सुधार का। ');
INSERT INTO "al_umari" VALUES(18,11,'18:11','तो हमने उन्हें गुफा में सुला दिया कई वर्षों तक।');
INSERT INTO "al_umari" VALUES(18,12,'18:12','फिर हमने उन्हें जगा दिया, ताकि हम ये जान लें कि दो समुदायों में से किसने उनके ठहरे रहने की अवधि को अधिक याद रखा है?');
INSERT INTO "al_umari" VALUES(18,13,'18:13','हम आपको उनकी सत्य कथा सुना रहे हैं। वास्तव में, वे कुछ नवयुवक थे, जो अपने पालनहार पर ईमान लाये और हमने उन्हें मार्गदर्शन में अधिक कर दिया।');
INSERT INTO "al_umari" VALUES(18,14,'18:14','और हमने उनके दिलों को सुदृढ़ कर दिया, जब वे खड़े हुए, फिर कहाः हमारा पालनहार वही है, जो आकाशों तथा धरती का पालनहार है। हम उसके सिवा कदापि किसी पूज्य को नहीं पुकारेंगे। (यदि हमने ऐसा किया) तो (सत्य से) दूर की बात होगी।');
INSERT INTO "al_umari" VALUES(18,15,'18:15','ये हमारी जाति है, जिसने अल्लाह के सिवा बहुत-से पूज्य बना लिए। क्यों वे उनपर कोई खुला प्रमाण प्रस्तुत नहीं करते? उससे बड़ा अत्याचारी कौन होगा, जो अल्लाह पर मिथ्या बात बनाये?');
INSERT INTO "al_umari" VALUES(18,16,'18:16','और जब तुम उनसे विलग हो गये तथा अल्लाह के अतिरिक्त उनके पूज्यों से, तो अब अमुक गुफा की ओर शरण लो, अल्लाह तुम्हारे लिए अपनी दया फैला देगा तथा तुम्हारे लिए तुम्हारे विषय में जीवन के साधनों का प्रबंध करेगा।');
INSERT INTO "al_umari" VALUES(18,17,'18:17','और तुम सूर्य को देखोगे कि जब निकलता है, तो उनकी गूफा से दायें झुक जाता है और जब डूबता है, तो उनसे बायें कतरा जाता है और वे उस (गुफा) के एक विस्तृत स्थान में हैं। ये अल्लाह की निशानियों में से है और जिसे अल्लाह मार्ग दिखा दे, वही सुपथ पाने वाला है और जिसे कुपथ कर दे, तो तुम कदापि उसके लिए कोई सहायक मार्गदर्शक नहीं पाओगे।');
INSERT INTO "al_umari" VALUES(18,18,'18:18','और तुम[ उन्हें समझोगे कि जाग रहे हैं, जबकि वे सोये हुए हैं और हम उन्हें दायें तथा बायें पार्शव पर फिराते रहते हैं और उनका कुत्ता गुफा के द्वार पर अपनी दोनों बाहें फैलाये पड़ा है। यदि तुम झाँककर देख लेते, तो पीठ फेरकर भाग जाते और उनसे भयपूर्ण हो जाते। ');
INSERT INTO "al_umari" VALUES(18,19,'18:19','और इसी प्रकार, हमने उन्हें जगा दिया, ताकि वे आपस में प्रश्न करें। तो एक ने उनमें से कहाः तुम कितने (समय) रहे हो? सबने कहाः हम एक दिन रहे हैं अथवा एक दिन के कुछ (समय)। (फिर) सबने कहाः अल्लाह अधिक जानता है कि तुम कितने (समय) रहे हो, तुम अपने में से किसी को, अपना ये सिक्का देकर नगर में भेजो, फिर देखे कि किसके पास अधिक स्वच्छ (पवित्र) भोजन है और उसमें से कुछ जीविका (भोजन) लाये और चाहिए कि सावधानी बरते। ऐसा न हो कि तुम्हारा किसी को अनुभव हो जाये।');
INSERT INTO "al_umari" VALUES(18,20,'18:20','क्योंकि यदि वे तुम्हें जान जायेंगे तो तुम्हें पथराव करके मार डालेंगे या तुम्हें अपने धर्म में लौटा लेंगे और तब तुम कदापि सफल नहीं हो सकोगे।');
INSERT INTO "al_umari" VALUES(18,21,'18:21','इसी प्रकार, हमने उनसे अवगत करा दिया, ताकि उन (नागरिकों) को ज्ञान हो जाये कि अल्लाह का वचन सत्य है और ये कि प्रलय (होने) में कोई संदेह[ नहीं। जब वे आपस में विवाद करने लगे, तो कुछ ने कहाः उनपर कोई निर्माण करा दो, अल्लाह ही उनकी दशा को भली-भाँति जानता है। परन्तु उन्होंने कहा जो अपना प्रभुत्व रखते थे, हम अवश्य उन (की गुफा के स्थान) पर एक मस्जिद बनायेंगे।');
INSERT INTO "al_umari" VALUES(18,22,'18:22','कुछ[ कहेंगे कि वे तीन हैं और चौथा उनका कुत्ता है और कुछ कहेंगे कि पाँच हैं और छठा उनका कुत्ता है। ये अंधेरे में तीर चलाते हैं और कहेंगे कि सात हैं और आठवाँ उनका कुत्ता है। (हे नबी!) आप कह दें कि मेरा पालनहार ही उनकी संख्या भली-भाँति जानता है, जिसे कुछ लोगों के सिवा कोई नहीं जानता । अतः आप उनके संबन्ध में कोई विवाद न करें, सिवाय सरसरी बात के और न उनके विषय में किसी से कुछ पूछें ।');
INSERT INTO "al_umari" VALUES(18,23,'18:23','और कदापि किसी विषय में न कहें कि मैं इसे कल करने वाला हूँ।');
INSERT INTO "al_umari" VALUES(18,24,'18:24','परन्तु ये कि अल्लाह[ चाहे तथा अपने पालनहार को याद करें, जब भूल जायेँ और कहें: संभव है, मेरा पालनहार मुझे इससे समीप सुधार का मार्ग दर्शा दे। ');
INSERT INTO "al_umari" VALUES(18,25,'18:25','और वे गुफा में तीन सौ वर्ष रहे और नौ वर्ष अधिक[ और। ');
INSERT INTO "al_umari" VALUES(18,26,'18:26','आप कह दें कि अल्लाह उनके रहने की अवधि से सर्वाधिक अवगत है। आकाशों तथा धरती का परोक्ष वही जानता है। क्या ही ख़ूब है वह देखने वाला और सुनने वाला। नहीं है उनका उसके सिवा कोई सहायक और न वह अपने शासन में किसी को साझी बनाता है।');
INSERT INTO "al_umari" VALUES(18,27,'18:27','और आप उसे सुना दें, जो आपकी ओर वह़्यी (प्रकाशना) की गयी है, आपके पालनहार की पुस्तक में से, उसकी बातों को कोई बदलने वाला नहीं है और आप कदापि नहीं पायेंगे उसके सिवा कोई शरण स्थान।');
INSERT INTO "al_umari" VALUES(18,28,'18:28','और आप उनके साथ रहें, जो अपने पालनहार की प्रातः-संध्या बंदगी करते हैं। वे उसकी प्रसन्नता चाहते हैं और आपकी आँखें सांसारिक जीवन की शोभा के लिए[ उनसे न फिरने पायें और उसकी बात न मानें, जिसके दिल को हमने अपनी याद से निश्चेत कर दिया और उसने मनमानी की और जिसका काम ही उल्लंघन (अवज्ञा करना) है। ');
INSERT INTO "al_umari" VALUES(18,29,'18:29','आप कह दें कि ये सत्य है, तुम्हारे पालनहार की ओर से, तो जो चाहे, ईमान लाये और जो चाहे कुफ़्र करे, निश्चय हमने अत्याचारियों के लिए ऐसी अग्नि तैयार कर रखी है, जिसकी प्राचीर[ ने उन्हें घेर लिया है और यदि वे जल के लिए गुहार करेंगे, तो उन्हें तेल की तलछट के समान जल दिया जायेगा, जो मुखों को भून देगा, वह क्या ही बुरा पेय है और वह क्या ही बुरा विश्राम स्थान है! ');
INSERT INTO "al_umari" VALUES(18,30,'18:30','निश्चय जो ईमान लाये तथा सदाचार किये, तो हम उनका प्रतिफल व्यर्थ नहीं करेंगे, जो सदाचारी हैं।');
INSERT INTO "al_umari" VALUES(18,31,'18:31','यही हैं, जिनके लिए स्थायी स्वर्ग हैं, जिनमें नहरें प्रवाहित हैं, उसमें उन्हें सोने के कंगन पहनाये जायेंगे।[ तथा (वे) महीन और गाढ़े रेशम के हरे वस्त्र पहनेंगे, उसमें सिंहासनों के ऊपर आसीन होंगे। ये क्या ही अच्छा प्रतिफल और क्या ही अच्छा विश्राम स्थान है। ');
INSERT INTO "al_umari" VALUES(18,32,'18:32','और (हे नबी!) आप उन्हें एक उदाहरण दो वयक्तियों का दें; हमने जिनमें से एक को दो बाग़ दिये अंगूरों के और घेर दिया दोनों को खजूरों से और दोनों के बीच खेती बना दी।');
INSERT INTO "al_umari" VALUES(18,33,'18:33','दोनों बाग़ों ने अपने पूरे फल दिये और उसमें कुछ कमी नहीं की और हमने जारी कर दी दोनों के बीच एक नहर।');
INSERT INTO "al_umari" VALUES(18,34,'18:34','और उसे लाभ प्राप्त हुआ, तो एक दिन उसने अपने साथी से कहा जबकि वह उससे बात कर रहा थाः मैं तुझसे अधिक धनी हूँ तथा स्वजनों में भी अधिक[ हूँ। ');
INSERT INTO "al_umari" VALUES(18,35,'18:35','और उसने अपने बाग़ में प्रवेश किया, अपने ऊपर अत्याचार करते हुए, उसने कहाः मैं नहीं समझता कि इसका विनाश हो जायेगा कभी।');
INSERT INTO "al_umari" VALUES(18,36,'18:36','और न ये समझता हूँ कि प्रलय होगी और यदि मुझे अपने पालनहार की ओर पुनः ले जाया गया, तो मैं अवश्य ही इससे उत्तम स्थान पाऊँगा।');
INSERT INTO "al_umari" VALUES(18,37,'18:37','उससे, उसके साथी ने कहा और वह उससे बात कर रहा थाः क्या तूने उसके साथ कुफ़्र कर दिया, जिसने तुझे मिट्टी से उत्पन्न किया, फिर वीर्य से, फिर तुझे बना दिया एक पूरा पुरुष?');
INSERT INTO "al_umari" VALUES(18,38,'18:38','रहा मैं, तो वही अल्लाह मेरा पालनहार है और मैं साझी नहीं बनाऊँगा अपने पालनहार का किसी को।');
INSERT INTO "al_umari" VALUES(18,39,'18:39','और क्यों नहीं जब तुमने अपने बाग़ में प्रवेश किया, तो कहा कि "जो अल्लाह चाहे, अल्लाह की शक्ति के बिना कुछ नहीं हो सकता।" यदि तू मुझे देखता है कि मैं तुझसे कम हूँ धन तथा संतान में[, ');
INSERT INTO "al_umari" VALUES(18,40,'18:40','तो आशा है कि मेरा पालनहार मुझे प्रदान कर दे, तेरे बाग़ से अच्छा और इस बाग़ पर आकाश से कोई आपदा भेज दे और वह चिकनी भूमि बन जाये।');
INSERT INTO "al_umari" VALUES(18,41,'18:41','अथवा उसका जल भीतर उतर जाये, फिर तू उसे पा न सके।');
INSERT INTO "al_umari" VALUES(18,42,'18:42','(अन्ततः) उसके फलों को घेर[ लिया गया, फिर वह अपने दोनों हाथ मलता रह गया उसपर, जो उसमें खर्च किया था और वह अपने छप्परों सहित गिरा हुआ था और कहने लगाः क्या ही अच्छा होता कि मैं किसी को अपने पालनहार का साझी न बनाता। ');
INSERT INTO "al_umari" VALUES(18,43,'18:43','और नहीं रह गया उसके लिए कोई जत्था, जो उसकी सहायता करता और न स्वयं अपनी सहायता कर सका।');
INSERT INTO "al_umari" VALUES(18,44,'18:44','यहीं सिध्द हो गया कि सब अधिकार सत्य अल्लाह को है, वही अच्छा है प्रतिफल प्रदान करने में तथा अच्छा है परिणाम लाने में।');
INSERT INTO "al_umari" VALUES(18,45,'18:45','और (हे नबी!) आप उन्हें सांसारिक जीवन का उदाहरण दें, उस जल से, जिसे हमने आकाश से बरसाया। फिर उसके कारण मिल गई धरती की उपज, फिर चूर हो गई, जिसे वायु उड़ाये फिरती[ है और अल्लाह प्रत्येक चीज़ पर सामर्थ्य रखने वाला है। ');
INSERT INTO "al_umari" VALUES(18,46,'18:46','धन और पुत्र सांसारिक जीवन की शोभा हैं और शेष रह जाने वाले सत्कर्म ही अच्छे हैं, आपके पालनहार के यहाँ प्रतिफल में तथा अच्छे हैं, आशा रखने के लिए।');
INSERT INTO "al_umari" VALUES(18,47,'18:47','तथा जिस दिन हम पर्वतों को चलायेंगे तथा तुम धरती को खुला चटेल[ देखोगे और हम उन्हें एकत्र कर देंगे, फिर उनमें से किसी को नहीं छोड़ेंगे। ');
INSERT INTO "al_umari" VALUES(18,48,'18:48','और सभी आपके पालनहार के समक्ष पंक्तियों में प्रस्तुत किये जायेंगे, तुम हमारे पास आ गये, जैसे हमने तुम्हारी उत्पत्ति प्रथम बार की थी, बल्कि तुमने समझा था कि हम तुम्हारे लिए कोई वचन का समय निर्धारित ही नहीं करेंगे।');
INSERT INTO "al_umari" VALUES(18,49,'18:49','और कर्म लेख[ (सामने) रख दिये जायेंगे, तो आप अपराधियों को देखेंगे कि उससे डर रहे हैं, जो कुछ उसमें (अंकित) है तथा कहेंगे कि हाय हमारा विनाश! ये कैसी पुस्तक है, जिसने किसी छोटे और बड़े कर्म को नहीं छोड़ा है, परन्तु उसे अंकित कर रखा है? और जो कर्म उन्होंने किये हैं, उन्हें वह सामने पायेंगे और आपका पालनहार किसी पर अत्याचार नहीं करेगा। ');
INSERT INTO "al_umari" VALUES(18,50,'18:50','तथा (याद करो) जब आपके पालनहार ने फ़रिश्तों से कहाः आदम को सज्दा करो, तो सबने सज्दा किया, इब्लीस के सिवा। वह जिन्नों में से था, अतः उसने उल्लंघन किया अपने पालनहार की आज्ञा का, तो क्या तुम उसे और उसकी संतति को सहायक मित्र बनाते हो, मुझे छोड़कर जबकि वे तुम्हारे शत्रु हैं? अत्याचारियों के लिए बुरा बदला है।');
INSERT INTO "al_umari" VALUES(18,51,'18:51','मैंने उन्हें उपस्थित नहीं किया, आकाशों तथा धरती की उतपत्ति के समय और न स्वयं उनकी उत्पत्ति के समय और न मैं कुपथों को सहायक[ बनाने वाला हूँ। ');
INSERT INTO "al_umari" VALUES(18,52,'18:52','जिस दिन वह (अल्लाह) कहेगा कि मेरे साझियों को पुकारो, जिन्हें (तुम मेरे साझी) समझ रहे थे। वह उन्हें पुकारेंगे, तो वे उनका कोई उत्तर नहीं देंगे और हम बना देंगे उनके बीच एक विनाशकारी खाई।');
INSERT INTO "al_umari" VALUES(18,53,'18:53','और अपराधी नरक को देखेंगे, तो उन्हें विश्वास जो जायेगा कि वे उसमें गिरने वाले हैं और उससे फिरने का कोई स्थान नहीं पायेंगे।');
INSERT INTO "al_umari" VALUES(18,54,'18:54','और हमने इस क़ुर्आन में प्रत्येक उदाहरण से लोगों को समझाया है। और मनुष्य बड़ा ही झगड़ालू है।');
INSERT INTO "al_umari" VALUES(18,55,'18:55','और नहीं रोका लोगों को कि ईमान लायें, जब उनके पास मार्गदर्शन आ गया और अपने पालनहार से क्षमा याचना करें, किन्तु इसीने कि पिछली जातियों की दशा उनकी भी हो जाये अथवा उनके समक्ष यातना आ जाये।');
INSERT INTO "al_umari" VALUES(18,56,'18:56','तथा हम रसूलों को नहीं भेजते, परन्तु शुभ सूचना देने वाले और सावधान करने वाले बनाकर और जो काफ़िर हैं, असत्य (अनृत) के सहारे विवाद करते हैं, ताकि उसके द्वारा वे सत्य को नीचा[ दिखायें और उन्होंने बनाया हमारी आयतों को तथा जिस बात की उन्हें चेतावनी दी गई, परिहास। ');
INSERT INTO "al_umari" VALUES(18,57,'18:57','और उससे बड़ा अत्याचारी कौन है, जिसे उसके पालनहार की आयतें सुनाई जायेँ, फिर (भी) उनसे मुँह फेर ले और अपने पहले किये हुए करतूत भूल जाये? वास्तव में, हमने उनके दिलों पर ऐसे आवरण (पर्दे) बना दिये हैं कि उसे[ समझ न पायें और उनके कानों में बोझ। और यदि आप उन्हें सीधी राह की ओर बुलायें, तब (भी) कभी सीधी राह नहीं पा सकेंग। ');
INSERT INTO "al_umari" VALUES(18,58,'18:58','और आपका पालनहार अति क्षमी दयावान् है। यदि वह उन्हें उनके करतूतों पर पकड़ता, तो तुरन्त यातना दे देता। बल्कि उनके लिए एक निश्चित समय का वचन है और वे उसके सिवा कोई बचाव का स्थान नहीं पायेंगे।');
INSERT INTO "al_umari" VALUES(18,59,'18:59','तथा ये बस्तियाँ हैं। हमने उन (के निवासियों) का विनाश कर दिया, जब उन्होंने अत्याचार किया और हमने उनके विनाश के लिए एक निर्धारित समय बना दिया था।');
INSERT INTO "al_umari" VALUES(18,60,'18:60','तथा (याद करो) जब मूसा ने अपने सेवक से कहाः मैं बराबर चलता रहूँगा, यहाँ तक कि दोनों सागरों के संगम पर पहुँच जाऊँ अथवा वर्षों चलता[ रहूँ। ');
INSERT INTO "al_umari" VALUES(18,61,'18:61','तो जब दोनों उनके संगम पर पहुँचे, तो दोनों अपनी मछली भूल गये और उसने सागर में अपनी राह बना ली, सुरंग के समान।');
INSERT INTO "al_umari" VALUES(18,62,'18:62','फिर, जब दोनों आगे चले गये, तो उस (मूसा) ने अपने सेवक से कहा कि हमारा दिन का भोजन लाओ। हम अपनी इस यात्रा से थक गये हैं।');
INSERT INTO "al_umari" VALUES(18,63,'18:63','उसने कहाः क्या आपने देखा? जब हमने उस शिला खण्ड के पास शरण ली थी, तो मैं मछली भूल गया और मुझे उसे शैतान ही ने भुला दिया कि मैं उसकी चर्चा करूँ और उसने अपनी राह सागर में अनोखे तरीक़े से बना ली।');
INSERT INTO "al_umari" VALUES(18,64,'18:64','मूसा ने कहाः वही है, जो हम चाहते थे। फिर दोनों अपने पद्चिन्हों को देखते हुए वापिस हुए।');
INSERT INTO "al_umari" VALUES(18,65,'18:65','और दोनों ने पाया हमारे भक्तों में से एक भक्त[ को, जिसे हमने अपनी विशेष दया प्रदान की थी और उसे अपने पास से कुछ विशेष ज्ञान दिया था। ');
INSERT INTO "al_umari" VALUES(18,66,'18:66','मूसा ने उससे कहाः क्या मैं आपका अनुसरण करूँ, ताकि मुझे भी उस भलाई में से कुछ सिखा दें, जो आपको सिखाई गई है?');
INSERT INTO "al_umari" VALUES(18,67,'18:67','उसने कहाः तुम मेरे साथ धैर्य नहीं कर सकोगे।');
INSERT INTO "al_umari" VALUES(18,68,'18:68','और कैसे धैर्य करोगे उस बात पर, जिसका तुम्हें पूरा ज्ञान नहीं?');
INSERT INTO "al_umari" VALUES(18,69,'18:69','उसने कहाः यदि अल्लाह ने चाहा, तो आप मुझे सहनशील पायेंगे और मैं आपकी किसी आज्ञा का उल्लंघन नहीं करूँगा।');
INSERT INTO "al_umari" VALUES(18,70,'18:70','उसने कहाः यदि तुम्हें मेरा अनुसरण करना है, तो मुझसे किसी चीज़ के बारे में प्रश्न न करना, जब तक मैं स्वयं तुमसे उसकी चर्चा न करूँ।');
INSERT INTO "al_umari" VALUES(18,71,'18:71','फिर दोनों चले, यहाँ तक कि जब दोनों नौका में सवार हुए, तो उस (ख़िज़्र) ने उसमें छेद कर दिया। मूसा ने कहाः क्या आपने इसमें छेदकर दिया, ताकि उसके सवारों को डुबो दें, आपने अनुचित काम कर दिया।');
INSERT INTO "al_umari" VALUES(18,72,'18:72','उसने कहाः क्या मैंने तुमसे नहीं कहा था कि तुम मेरे साथ सहन नहीं कर सकोगे?');
INSERT INTO "al_umari" VALUES(18,73,'18:73','कहाः मुझे आप मेरी भूल पर न पकड़े और मेरी बात के कारण मुझे असुविधा में न डालें।');
INSERT INTO "al_umari" VALUES(18,74,'18:74','फिर दोनों चले, यहाँ तक कि एक बालक से मिले, तो उस (ख़िज़्र) ने उसे वध कर दिया। मूसा ने कहाः क्या आपने एक निर्दोष प्राण ले लिया, वह भी किसी प्राण के बदले[ नहीं? आपने बहुत ही बुरा काम किया। ');
INSERT INTO "al_umari" VALUES(18,75,'18:75','उसने कहाः क्या मैंने तुमसे नहीं कहा कि वास्तव में, तुम मेरे साथ धैर्य नहीं कर सकोगे?');
INSERT INTO "al_umari" VALUES(18,76,'18:76','मूसा ने कहाः यदि मैं आपसे प्रश्न करूँ, किसी विषय में इसके पश्चात्, तो मुझे अपने साथ न रखें। निश्चय आप मेरी ओर से याचना को पहुँच[ चुके। ');
INSERT INTO "al_umari" VALUES(18,77,'18:77','फिर दोनो चले, यहाँ तक कि जब एक गाँव के वासियों के पास आये, तो उनसे भोजन माँगा। उन्होंने उनका अतिथि सत्कार करने से इन्कार कर दिया। वहाँ उन्होंने एक दीवार पायी, जो गिरा चाहती थी। उसने उसे सीधा कर दिया। कहाः यदि आप चाहते, तो इसपर पारिश्रमिक ले लेते।');
INSERT INTO "al_umari" VALUES(18,78,'18:78','उसने कहाः ये मेरे तथा तुम्हारे बीच वियोग है। मैं तुम्हें उसकी वास्तविक्ता बताऊँगा, जिसे तुम सहन नहीं कर सके।');
INSERT INTO "al_umari" VALUES(18,79,'18:79','रही नाव, तो वह कुछ निर्धनों की थी, जो सागर में काम करते थे। तो मैंने चाहा कि उसे छिद्रित[ कर दूँ और उनके आगे एक राजा था, जो प्रत्येक (अच्छी) नाव का अपहरण कर लेता था। ');
INSERT INTO "al_umari" VALUES(18,80,'18:80','और रहा बालक, तो उसके माता-पिता ईमान वाले थे, अतः हम डरे कि उन्हें अपनी अवज्ञा और अधर्म से दुःख न पहुँचाये।');
INSERT INTO "al_umari" VALUES(18,81,'18:81','इसलिए हमने चाहा कि उन दोनों को उनका पालनहार, इसके बदले उससे अधिक पवित्र और अधिक प्रेमी प्रदान करे।');
INSERT INTO "al_umari" VALUES(18,82,'18:82','और रही दीवार, तो वह दो अनाथ बालकों की थी और उसके भीतर उनका कोष था और उनके माता-पिता पुनीत थे, तो तेरे पालनहार ने चाहा कि वे दोनों अपनी युवा अवस्था को पहुँचें और अपना कोष निकालें, तेरे पालनहार की दया से और मैंने ये अपने विचार तथा अधिकार से नहीं किया[। ये उसकी वास्तविक्ता है, जिसे तुम सहन नहीं कर सके। ');
INSERT INTO "al_umari" VALUES(18,83,'18:83','और (हे नबी!) वे आपसे ज़ुलक़रनैन[ के विषय में प्रश्न करते हैं। आप कह दें कि मैं उनकी कुछ दशा तुम्हें पढ़कर सुना देता हूँ। ');
INSERT INTO "al_umari" VALUES(18,84,'18:84','हमने उसे धरती में प्रभुत्व प्रदान किया तथा उसे प्रत्येक प्रकार का साधन दिया।');
INSERT INTO "al_umari" VALUES(18,85,'18:85','तो वह एक राह के पीछे लगा।');
INSERT INTO "al_umari" VALUES(18,86,'18:86','यहाँ तक कि जब सूर्यास्त के स्थान तक[ पहुँचा, तो उसने पाया कि वह एक काली कीचड़ के स्रोत में डूब रहा है और वहाँ एक जाति को पाया। हमने कहाः हे ज़ुलक़रनैन! तू उन्हें यातना दे अथवा उनमें अच्छा व्यवहार बना। ');
INSERT INTO "al_umari" VALUES(18,87,'18:87','उसने कहाः जो अत्याचार करेगा, हम उसे दण्ड देंगे। फिर वह अपने पालनहार की ओर फेरा[ जायेगा, तो वह उसे कड़ी यातना देगा। ');
INSERT INTO "al_umari" VALUES(18,88,'18:88','परन्तु जो ईमान लाये तथा सदाचार करे, तो उसी के लिए अच्छा प्रतिफल (बदला) है और हम उसे अपना सरल आदेश देंगे।');
INSERT INTO "al_umari" VALUES(18,89,'18:89','फिर वह एक (अन्य) राह की ओर लगा।');
INSERT INTO "al_umari" VALUES(18,90,'18:90','यहाँ तक कि सूर्य़ोदय के स्थान तक पहुँचा। तो उसे पाया कि ऐसी जाति पर उदय हो रहा है, जिससे हमने उनके लिए कोई आड़ नहीं बनायी है।');
INSERT INTO "al_umari" VALUES(18,91,'18:91','उनकी दशा ऐसी ही थी और उस (ज़ुलक़रनैन) के पास जो कुछ था, हम उससे पूर्णतः सूचित हैं।');
INSERT INTO "al_umari" VALUES(18,92,'18:92','फिर वह एक दूसरी राह की ओर लगा।');
INSERT INTO "al_umari" VALUES(18,93,'18:93','यहाँ तक कि जब दो पर्वतों के बीच पहुँचा, तो उन दोनों की उस ओर एक जाति को पाया, जो नहीं समीप थी कि किसी बात को समझे[। ');
INSERT INTO "al_umari" VALUES(18,94,'18:94','उन्होंने कहाः हे ज़ुलक़रनैन! वास्तव में, याजूज तथा माजूज उपद्रवी हैं, इस देश में। तो क्या हम निर्धारित कर दें आपके लिए कुछ धन। इसलिए कि आप हमारे और उनके बीच कोई रोक (बन्ध) बना दें?');
INSERT INTO "al_umari" VALUES(18,95,'18:95','उसने कहाः जो कुछ मुझे मेरे पालनहार ने प्रदान किया है, वह उत्तम है। तो तुम मेरी सहायता बल और शक्ति से करो, मैं बना दूँगा तुम्हारे और उनके मध्य एक दृढ़ भीत।');
INSERT INTO "al_umari" VALUES(18,96,'18:96','मुझे लोहे की चादरें ला दो और जब दोनों पर्वतों के बीच दीवार तैयार कर दी, तो कहा कि आग दहकाओ, यहाँतक कि जब उस दीवार को आग (के समान लाल) कर दिया, तो कहाः मेरे पास लाओ, इसपर पिघला हुआ ताँबा उंडेल दूँ।');
INSERT INTO "al_umari" VALUES(18,97,'18:97','फिर वह उसपर चढ़ नहीं सकते थे और न उसमें कोई सेंध लगा सकते थे।');
INSERT INTO "al_umari" VALUES(18,98,'18:98','उस (ज़ुलक़रनैन) ने कहाः ये मेरे पालनहार की दया है। फिर जब मेरे पालनहार का वचन[ आयेगा, तो वह इसे खण्ड-खण्ड कर देगा और मेरे पालनहार का वचन सत्य है। ');
INSERT INTO "al_umari" VALUES(18,99,'18:99','और हम छोड़ देंगे उस[ दिन लोगों को एक-दूसरे में लहरें लेते हुए तथा निरसिंघा में फूंक दिया जायेगा और हम सबको एकत्र कर देंगे। ');
INSERT INTO "al_umari" VALUES(18,100,'18:100','और हम सामने कर देंगे उस दिन नरक को काफ़िरों के समक्ष।');
INSERT INTO "al_umari" VALUES(18,101,'18:101','जिनकी आँखें मेरी याद से पर्दे में थीं और कोई बात सुन नहीं सकते थे।');
INSERT INTO "al_umari" VALUES(18,102,'18:102','तो क्या उन्होंने सोचा है जो काफ़िर हो गये कि वे बना लेंगे मेरे दासों को मेरे सिवा सहायक? वास्तव में, हमने काफ़िरों के आतिथ्य के लिए नरक तैयार कर दी है।');
INSERT INTO "al_umari" VALUES(18,103,'18:103','आप कह दें कि क्या हम तुम्हें बता दें कि कौन अपने कर्मों में सबसे अधिक क्षतिग्रस्त हैं?');
INSERT INTO "al_umari" VALUES(18,104,'18:104','वह हैं, जिनके सांसारिक जीवन के सभी प्रयास व्यर्थ हो गये तथा वे समझते रहे कि वे अच्छे कर्म कर रहे हैं।');
INSERT INTO "al_umari" VALUES(18,105,'18:105','यही वे लोग हैं, जिन्होंने नहीं माना अपने पालनहार की आयतों तथा उससे मिलने को, अतः हम प्रलय के दिन उनका कोई भार निर्धारित नहीं करेंगे[। ');
INSERT INTO "al_umari" VALUES(18,106,'18:106','उन्हीं का बदला नरक है, इस कारण कि उन्होंने कुफ़्र किया और मेरी आयतों और मेरे रसूलों का उपहास किया।');
INSERT INTO "al_umari" VALUES(18,107,'18:107','निश्चय जो ईमान लाये और सदाचार किये, उन्हीं के आतिथ्य के लिए फ़िरदौस[ के बाग़ होंगे। ');
INSERT INTO "al_umari" VALUES(18,108,'18:108','उसमें वे सदावासी होंगे, उसे छोड़कर जाना नहीं चाहेंगे।');
INSERT INTO "al_umari" VALUES(18,109,'18:109','(हे नबी!) आप कह दें कि यदि सागर मेरे पालनहार की बातें लिखने के लिए स्याही बन जायेँ, तो सागर समाप्त हो जायें इससे पहले कि मेरे पालनहार की बातें समाप्त हों, यद्यपि उतनी ही स्याही और ले आयें।');
INSERT INTO "al_umari" VALUES(18,110,'18:110','आप कह देः मैंतो तुम जैसा एक मनुष्य पुरुष हूँ, मेरी ओर प्रकाशना (वह़्यी) की जाती है कि तुम्हारा पूज्य बस एक ही पूज्य है। अतः, जो अपने पालनहार से मिलने की आशा रखता हो, उसे चाहिए कि सदाचार करे और साझी न बनाये अपने पालनहार की इबादत (वंदना) में किसी को।');
INSERT INTO "al_umari" VALUES(19,1,'19:1','काफ़, हा, या, ऐन, स़ाद।');
INSERT INTO "al_umari" VALUES(19,2,'19:2','ये आपके पालनहार की दया की चर्चा है, अपने भक्त ज़करिय्या पर।');
INSERT INTO "al_umari" VALUES(19,3,'19:3','जबकि उसने अपने पालनहार से विनय की, गुप्त विनय।');
INSERT INTO "al_umari" VALUES(19,4,'19:4','उसने कहाः मेरे पालनहार! मेरी अस्थियाँ निर्बल हो गयीं, सिर बुढ़ापे से सफेद[ हो गया है तथा मेरे पालनहार! कभी ऐसा नहीं हुआ कि तुझसे प्रार्थना करके निष्फल हुआ हूँ। ');
INSERT INTO "al_umari" VALUES(19,5,'19:5','और मुझे अपने भाई-बंदों से भय[ है, अपने (मरण) के पश्चात् तथा मेरी पत्नी बाँझ है, अतः मुझे अपनी ओर से एक उत्तराधिकारी प्रदान कर दे। ');
INSERT INTO "al_umari" VALUES(19,6,'19:6','वह मेरा उत्तराधिकारी हो तथा याक़ूब के वंश का उत्तराधिकारी[ हो और हे मेरे पालनहार! उसे प्रिय बना दे। ');
INSERT INTO "al_umari" VALUES(19,7,'19:7','हे ज़करिय्या! हम तुझे एक बालक की शुभ सूचना दे रहे हैं, जिसका नाम यह़्या होगा। हमने नहीं बनाया है, इससे पहले उसका कोई समनाम।');
INSERT INTO "al_umari" VALUES(19,8,'19:8','उसने (आश्यर्य से) कहाः मेरे पालनहार! कहाँ से मेरे यहाँ कोई बालक होगा, जबकि मेरी पत्नि बाँझ है और मैं बुढ़ापे की चरम सीमा को जा पहुँचा हूँ।');
INSERT INTO "al_umari" VALUES(19,9,'19:9','उसने कहाः ऐसा ही होगा, तेरे पालनहार ने कहा हैः ये मेरे लिए सरल है, इससे पहले मैंने तेरी उत्पत्ति की है, जबकि तू कुछ नहीं था।');
INSERT INTO "al_umari" VALUES(19,10,'19:10','उस (ज़करिय्या) ने कहाः मेरे पालनहार! मेरे लिए कोई लक्षण (चिन्ह) बना दे। उसने कहाः तेरा लक्षण ये है कि तू बोल नहीं सकेगा, लोगों से निरंतर तीन रातें[। ');
INSERT INTO "al_umari" VALUES(19,11,'19:11','फिर वह मेह़राब (चाप) से निकलकर अपनी जाति के पास आया और उन्हें संकेत द्वारा आदेश दिया कि उस (अल्लाह) की पवित्रता का वर्णन करो, प्रातः तथा संध्या।');
INSERT INTO "al_umari" VALUES(19,12,'19:12','हे यह़्या[! इस पुस्तक (तौरात) को थाम ले और हमने उसे बचपन ही में ज्ञान (प्रबोध) प्रदान किया। ');
INSERT INTO "al_umari" VALUES(19,13,'19:13','तथा अपनी ओर से प्रेम भाव तथा पवित्रता (प्रदान की) और वह बड़ा संयमी (सदाचारी) था।');
INSERT INTO "al_umari" VALUES(19,14,'19:14','तथा अपनी माता-पिता के साथ सुशील था। वह क्रुर तथा अवज्ञाकारी नहीं था।');
INSERT INTO "al_umari" VALUES(19,15,'19:15','उसपर शान्ति है, जिस दिन उसने जन्म लिया और जिस दिन मरेगा और जिस दिन पुनः जीवित किया जायेगा।');
INSERT INTO "al_umari" VALUES(19,16,'19:16','तथा आप, इस पुस्तक (क़ुर्आन) में मर्यम[ की चर्चा करें, जब वह अपने परिजनों से अलग होकर एक पूर्वी स्थान की ओर आयीं। ');
INSERT INTO "al_umari" VALUES(19,17,'19:17','फिर उनकी ओर से पर्दा कर लिया, तो हमने उसकी ओर अपनी रूह़ (आत्मा)[ को भेजा, तो उसने उसके लिए एक पूरे मनुष्य का रूप धारण कर लिया। ');
INSERT INTO "al_umari" VALUES(19,18,'19:18','उसने कहाः मैं शरण माँगती हूँ अत्यंत कृपाशील की तुझ से, यदि तुझे अल्लाह का कुछ भी भय हो।');
INSERT INTO "al_umari" VALUES(19,19,'19:19','उसने कहाः मैं तेरे पालनहार का भेजा हुआ हूँ, ताकि तुझे एक पुनीत बालक प्रदान कर दूँ।');
INSERT INTO "al_umari" VALUES(19,20,'19:20','वह बोलीः ये कैसे हो सकता है कि मेरे बालक हों, जबकि किसी पुरुष ने मुझे स्पर्श भी नहीं किया है और न मैं व्यभिचारिणी हूँ?');
INSERT INTO "al_umari" VALUES(19,21,'19:21','फ़रिश्ते ने कहाः ऐसा ही होगा, तेरे पालनहार का वचन है कि वह मेरे लिए अति सरल है और ताकि हम उसे लोगों के लिए एक लक्षण (निशानी)[ बनायें तथा अपनी विशेष दया से और ये एक निश्चित बात है। ');
INSERT INTO "al_umari" VALUES(19,22,'19:22','फिर वह गर्भवती हो गई तथा उस (गर्भ को लेकर) दूर स्थान पर चली गयी।');
INSERT INTO "al_umari" VALUES(19,23,'19:23','फिर प्रसव पीड़ा उसे एक खजूर के तने तक लायी, कहने लगीः क्या ही अच्छा होता, मैं इससे पहले ही मर जाती और भूली-बिसरी हो जाती।');
INSERT INTO "al_umari" VALUES(19,24,'19:24','तो उसके नीचे से पुकारा[ कि उदासीन न हो, तेरे पालनहार ने तेरे नीचे एक स्रोत बहा दिया है।');
INSERT INTO "al_umari" VALUES(19,25,'19:25','और हिला दे अपनी ओर खजूर के तने को, तुझपर गिरायेगा वह ताज़ी पकी खजूरें[। ');
INSERT INTO "al_umari" VALUES(19,26,'19:26','अतः, खा, पी तथा आँख ठण्डी कर। फिर यदि किसी पुरुष को देखे, तो कह देः वास्तव में, मैंने मनौती मान रखी है, अत्यंत कृपाशील के लिए व्रत की। अतः, मैं आज किसी मनुष्य से बात नहीं करूँगी।');
INSERT INTO "al_umari" VALUES(19,27,'19:27','फिर उस (शिशु ईसा) को लेकर अपनी जाति में आयी, सबने कहाः हे मर्यम! तूने बहुत बुरा किया।');
INSERT INTO "al_umari" VALUES(19,28,'19:28','हे हारून की बहन[! तेरा पिता कोई बुरा व्यक्ति न था और न तेरी माँ व्यभिचारिणी थी। ');
INSERT INTO "al_umari" VALUES(19,29,'19:29','मर्यम ने उस (शिशु) की ओर संकेत किया। लोगों ने कहाः हम कैसे उससे बात करें, जो गोद में पड़ा हुआ एक शिशु है?');
INSERT INTO "al_umari" VALUES(19,30,'19:30','वह (शिशु) बोल पड़ाः मैं अल्लाह का भक्त हूँ। उसने मुझे पुस्तक (इन्जील) प्रदान की है तथा मुझे नबी बनाया है[। ');
INSERT INTO "al_umari" VALUES(19,31,'19:31','तथा मुझे शुभ बनाया है, जहाँ रहूँ और मुझे आदेश दिया है नमाज़ तथा ज़कात का, जब तक जीवित रहूँ।');
INSERT INTO "al_umari" VALUES(19,32,'19:32','तथा आपनी माँ का सेवक (बनाया है) और उसने मुझे क्रूर तथा अभागा[ नहीं बनाया है। ');
INSERT INTO "al_umari" VALUES(19,33,'19:33','तथा शान्ति है मुझपर, जिस दिन मैंने जन्म लिया, जिस दिन मरूँगा और जिस दिन पुनः जीवित किया जाऊँगा।');
INSERT INTO "al_umari" VALUES(19,34,'19:34','ये है ईसा मर्यम का सुत, यही सत्य बात है, जिसके विषय में लोग संदेह कर रहे हैं।');
INSERT INTO "al_umari" VALUES(19,35,'19:35','अल्लाह का ये काम नहीं कि अपने लिए कोई संतान बनाये, वह पवित्र है! जब वह किसी कार्य का निर्णय करता है, तो उसके सिवा कुछ नहीं होता कि उसे आदेश दे किः "हो जा" और वह हो जाता है।');
INSERT INTO "al_umari" VALUES(19,36,'19:36','और (ईसा ने कहाः) वास्तव में, अल्लाह मेरा पालनहार तथा तुम्हारा पालनहार है, अतः, उसी की इबादत (वंदना) करो, यही सुपथ (सीधी राह) है।');
INSERT INTO "al_umari" VALUES(19,37,'19:37','फिर सम्प्रदायों[ ने आपस में विभेद किया, तो विनाश है उनके लिए, जो काफ़िर हो गये, एक बड़े दिन के आ जाने के कारण। ');
INSERT INTO "al_umari" VALUES(19,38,'19:38','वे भली-भाँति सुनेंगे और देखेंगे, जिस दिन हमारे पास आयेंगे, परन्तु अत्याचारी आज खुले कुपथ में हैं।');
INSERT INTO "al_umari" VALUES(19,39,'19:39','और (हे नबी!) आप उन्हें संताप के दिन से सावधान कर दें, जब निर्णय[ कर दिया जायेगा, जबकि वे अचेत हैं तथा ईमान नहीं ला रहे हैं। ');
INSERT INTO "al_umari" VALUES(19,40,'19:40','निश्चय हम ही उत्तराधिकारी होंगे धरती के तथा जो उसके ऊपर है और हमारी ही ओर सब प्रत्यागत किये जायेंगे।');
INSERT INTO "al_umari" VALUES(19,41,'19:41','तथा आप चर्चा कर दें इस पुस्तक (क़ुर्आन) में इब्राहीम की। वास्तव में, वह एक सत्यवादी नबी था।');
INSERT INTO "al_umari" VALUES(19,42,'19:42','जब उसने कहा अपने पिता सेः हे मेरे प्रिय पिता! क्यों आप उसे पूजते हैं, जो न सुनता है, न देखता है और न आपके कुछ काम आता?');
INSERT INTO "al_umari" VALUES(19,43,'19:43','हे मेरे पिता! मेरे पास वह ज्ञान आ गया है, जो आपके पास नहीं आया, अतः आप मेरा अनुसरण करें, मैं आपको सीधी राह दिखा दूँगा।');
INSERT INTO "al_umari" VALUES(19,44,'19:44','हे मेरे प्रिय पिता! शैतान की पूजा न करें, वास्तव में, शैतान अत्यंत कृपाशील (अल्लाह) का अवज्ञाकारी है।');
INSERT INTO "al_umari" VALUES(19,45,'19:45','हे मेरे पिता! वास्तव में, मुझे भय हो रहा है कि आपको अत्यंत कृपाशील की कोई यातना आ लगे, तो आप शैतान के मित्र हो जायेंगे[। ');
INSERT INTO "al_umari" VALUES(19,46,'19:46','उसने कहाः क्या तू हमारे पूज्यों से विमुख हो रहा है? हे इब्राहीम! यदि तू (इससे) नहीं रुका, तो मैं तुझे पत्थरों से मार दूँगा और तू मुझसे विलग हो जा, सदा के लिए।');
INSERT INTO "al_umari" VALUES(19,47,'19:47','(इब्राहीम) ने कहाः सलाम[ है आपको! मैं क्षमा की प्रार्थना करता रहूँगा आपके लिए अपने पालनहार से, मेरा पालनहार मेरे प्रति बड़ा करुणामय है। ');
INSERT INTO "al_umari" VALUES(19,48,'19:48','तथा मैं तुम सभीको छोड़ता हूँ और जिसे तुम पुकारते हो अल्लाह के सिवा और प्रार्थना करता रहूँगा अपने पालनहार से। मुझे विश्वास है कि मैं अपने पालनहार से प्रार्थना करके असफल नहीं हूँगा।');
INSERT INTO "al_umari" VALUES(19,49,'19:49','फिर जब उन्हें छोड़ दिया तथा जिन्हें वे अल्लाह के सिवा पुकार रहे थे, तो हमने उसे प्रदान कर दिया इस्ह़ाक़ तथा याक़ूब, और हमने प्रत्येक को नबी बना दिया।');
INSERT INTO "al_umari" VALUES(19,50,'19:50','तथा हमने प्रदान की, उन सबको, अपनी दया में से और हमने बना दी, उनकी शुभ चर्चा सर्वोच्च।');
INSERT INTO "al_umari" VALUES(19,51,'19:51','और आप इस पुस्तक में मूसा की चर्चा करें। वास्तव में, वह चुना हुआ तथा रसूल एवं नबी था।');
INSERT INTO "al_umari" VALUES(19,52,'19:52','और हमने उसे पुकारा तूर पर्वत के दायें किनारे से तथा उसे समीप कर लिया रहस्य की बात करते हूए।');
INSERT INTO "al_umari" VALUES(19,53,'19:53','और हमने प्रदान किया उसे अपनी दया में से, उसके भाई हारून को नबी बनाकर।');
INSERT INTO "al_umari" VALUES(19,54,'19:54','तथा इस पुस्तक में इस्माईल[ की चर्चा करो, वास्तव में, वह वचन का पक्का तथा रसूल-नबी था। ');
INSERT INTO "al_umari" VALUES(19,55,'19:55','और आदेश देता था अपने परिवार को नमाज़ तथा ज़कात का और अपने पालनहार के यहाँ प्रिय था।');
INSERT INTO "al_umari" VALUES(19,56,'19:56','तथा इस पुस्तक में इद्रीस की चर्चा करो, वास्तव में, वह सत्यवादी नबी था।');
INSERT INTO "al_umari" VALUES(19,57,'19:57','तथा हमने उसे उठाया उच्च स्थान पर।');
INSERT INTO "al_umari" VALUES(19,58,'19:58','यही वो लोग हैं, जिनपर अल्लाह ने पुरस्कार किया, नबियों में से, आदम की संतति में से तथा उनमें से, जिन्हें हमने (नाव पर) सवार किया नूह़ के साथ तथा इब्राहीम और इस्राईल की संतति में से तथा उनमें से जिन्हें हमने मार्गदर्शन दिया और चुन लिया, जब इनके समक्ष पढ़ी जाती थी अत्यंत कृपाशील की आयतें, तो वे गिर जाया करते थे सज्दा करते हुए तथा रोते हुए।');
INSERT INTO "al_umari" VALUES(19,59,'19:59','फिर इनके पश्चात् ऐसै कपूत पैदा हुए, जिन्होंने गंवा दिया नमाज़ को तथा अनुसरण किया मनोकांक्षाओं का, तो वे शीघ्र ही कुपथ (के परिणाम) का सामना करेंगे।');
INSERT INTO "al_umari" VALUES(19,60,'19:60','परन्तु जिन्होंने क्षमा माँग ली तथा ईमान लाये और सदाचार किये, तो वही स्वर्ग में प्रवेश करेंगे और उनपर तनिक अत्याचार नहीं किया जायेगा।');
INSERT INTO "al_umari" VALUES(19,61,'19:61','स्थायी बिन देखे स्वर्ग, जिनका परोक्षतः वचन अत्यंत कृपाशील ने अपने भक्तों को दिया है, वास्तव में, उसका वचन पूरा होकर रहेगा।');
INSERT INTO "al_umari" VALUES(19,62,'19:62','वे नहीं सुनेंगे, उसमें कोई बक्वास, सलाम के सिवा तथा उनके लिए उसमें जीविका होगी प्रातः और संध्या।');
INSERT INTO "al_umari" VALUES(19,63,'19:63','यही वो स्वर्ग है, जिसका हम उत्तराधिकारी बना देंगे, अपने भक्तों में से उसे, जो आज्ञाकारी हो।');
INSERT INTO "al_umari" VALUES(19,64,'19:64','और हम[ नहीं उतरते, परन्तु आपके पालनहार के आदेश से, उसी का है, जो हमारे आगे तथा पीछे है और जो इसके बीच है और आपका पालनहार भूलने वाला नहीं है। ');
INSERT INTO "al_umari" VALUES(19,65,'19:65','आकाशों तथा धरती का पालनहार तथा जो उन दोनों के बीच है। अतः उसी की इबादत (वंदना) करें तथा उसकी इबादत पर स्थित रहें। क्या आप उसके समक्ष किसी को जानते हैं?');
INSERT INTO "al_umari" VALUES(19,66,'19:66','तथा मनुष्य कहता है कि क्या जब मैं मर जाऊँगा, तो फिर निकाला जाऊँगा जीवित होकर?');
INSERT INTO "al_umari" VALUES(19,67,'19:67','क्या मनुष्य याद नहीं रखता कि हम ही ने उसे इससे पूर्व उत्पन्न किया है, जबकि वह कुछ (भी) न था?');
INSERT INTO "al_umari" VALUES(19,68,'19:68','तो आपके पालनहार की शपथ! हम उन्हें अवश्य एकत्र कर देंगे और शैतानों को, फिर उन्हें अवश्य उपस्थित कर देंगे, नरक के किनारे मुँह के बल गिरे हुए।');
INSERT INTO "al_umari" VALUES(19,69,'19:69','फिर हम अलग कर लेंगे, प्रत्येक समुदाय से, उनमें से उसे, जो अत्यंत कृपाशील का अधिक अवज्ञाकारी था।');
INSERT INTO "al_umari" VALUES(19,70,'19:70','फिर हम ही भली-भाँति जानते हैं कि कौन अधिक योग्य है उसमें झोंक दिये जाने के।');
INSERT INTO "al_umari" VALUES(19,71,'19:71','और नहीं है तुममें से कोई, परन्तु वहाँ गुज़रने वाला[ है, ये आपके पालनहार पर अनिवार्य है, जो पूरा होकर रहेगा। ');
INSERT INTO "al_umari" VALUES(19,72,'19:72','फिर हम उन्हें बचा लेंगे, जो डरते रहे तथा उसमें छोड़ देंगे अत्याचारियों को मुँह के बल गिरे हुए।');
INSERT INTO "al_umari" VALUES(19,73,'19:73','तथा जब उनके समक्ष हमारी खुली आयतें पढ़ी जाती हैं, तो काफ़िर ईमान वालों से कहते हैं कि (बताओ) दोनों सम्प्रदायों में किसकी दशा अच्छी है और किसकी मज्लिस (सभा) अधिक भव्य है?');
INSERT INTO "al_umari" VALUES(19,74,'19:74','जबकि हम ध्वस्त कर चुके हैं, इनसे पहले बहुत-सी जातियों को, जो इनमें उत्तम थीं संसाधन तथा मान-सम्मान में।');
INSERT INTO "al_umari" VALUES(19,75,'19:75','(हे नबी!) आप कह दें कि जो कुपथ में ग्रस्त होता है, अत्यंत कृपाशील उसे अधिक अवसर देता है। यहाँ तक कि जब उसे देख लें, जिसका वचन दिये जाते हैं; या तो यातना को अथवा प्रलय को, उस समय उन्हें ज्ञान हो जायेगा कि किसकी दशा बुरी और किसका जत्था अधिक निर्बल है।');
INSERT INTO "al_umari" VALUES(19,76,'19:76','और अल्लाह उन्हें, जो सुपथ हों, मार्गदर्शन में आधिक कर देता है और शेष रह जाने वाले सदाचार ही उत्तम हैं, आपके पालनहार के समीप कर्म-फल में तथा उत्तम हैं परिणाम के फलस्वरूप।');
INSERT INTO "al_umari" VALUES(19,77,'19:77','(हे नबी!) क्या आपने उसे देखा, जिसने हमारी आयतों के साथ कुफ़्र (अविश्वास) किया तथा कहाः मैं अवश्य धन तथा संतान दिया जाऊँगा?');
INSERT INTO "al_umari" VALUES(19,78,'19:78','क्या वह अवगत हो गया है परोक्ष से अथवा उसने अत्यंत दयाशील से कोई वचन ले रखा है?');
INSERT INTO "al_umari" VALUES(19,79,'19:79','कदापि नहीं, हम लिख लेंगे, जो वह कहता है और हम अधिक करते जायेंगे उसकी यातना को अत्यधिक।');
INSERT INTO "al_umari" VALUES(19,80,'19:80','और हम ले लेंगे जिसकी वह बात कर रहा है और वह हमारे पास अकेला[ आयेगा। ');
INSERT INTO "al_umari" VALUES(19,81,'19:81','तथा उन्होंने बना लिए हैं अल्लाह के सिवा बहुत-से पूज्य, ताकि वे उनके सहायक हों।');
INSERT INTO "al_umari" VALUES(19,82,'19:82','ऐसा कदापि नहीं होगा, वे सब इसकी पूजा (उपासना) का अस्वीकार कर[ देंगे और उनके विरोधी हो जायेंगे। ');
INSERT INTO "al_umari" VALUES(19,83,'19:83','क्या आपने नहीं देखा कि हमने भेज दिया है शैतानों को काफ़िरों पर, जो उन्हें बराबर उकसाते रहते हैं?');
INSERT INTO "al_umari" VALUES(19,84,'19:84','अतः शीघ्रता न करें उनपर[, हम तो केवल उनके दिन गिन रहे हैं। ');
INSERT INTO "al_umari" VALUES(19,85,'19:85','जिस दिन हम एकत्र कर देंगे, आज्ञाकारियों को, अत्यंत कृपाशील की ओर अतिथि बनाकर।');
INSERT INTO "al_umari" VALUES(19,86,'19:86','तथा हांक देंगे पापियों को नरक की ओर प्यासे पशुओं के समान।');
INSERT INTO "al_umari" VALUES(19,87,'19:87','वह (काफ़िर) अभिस्तावना का अधिकार नहीं रखेंगे, परन्तु जिसने बना लिया हो अत्यंत कृपाशील के पास कोई वचन[। ');
INSERT INTO "al_umari" VALUES(19,88,'19:88','तथा उन्होंने कहा कि बना लिया है अत्यंत कृपाशील ने अपने लिए एक पुत्र[। ');
INSERT INTO "al_umari" VALUES(19,89,'19:89','वास्तव में, तुम एक भारी बात घड़ लाये हो।');
INSERT INTO "al_umari" VALUES(19,90,'19:90','समीप है कि इस कथन के कारण आकाश फट पड़े तथा धरती चिर जाये और गिर जायेँ पर्वत कण-कण होकर।');
INSERT INTO "al_umari" VALUES(19,91,'19:91','कि वे सिध्द करने लगे अत्यंत कृपाशील के लिए संतान।');
INSERT INTO "al_umari" VALUES(19,92,'19:92','तथा नहीं योग्य है अत्यंत कृपाशील के लिए कि वह कोई संतान बनाये।');
INSERT INTO "al_umari" VALUES(19,93,'19:93','प्रत्येक जो आकाशों तथा धरती में हैं, आने वाले हैं, अत्यंत कृपाशील की सेवा में दास बनकर।');
INSERT INTO "al_umari" VALUES(19,94,'19:94','उसने उन्हें नियंत्रण में ले रखा है तथा उन्हें पूर्णतः गिन रखा है।');
INSERT INTO "al_umari" VALUES(19,95,'19:95','और प्रत्येक उसके समक्ष आने वाला है, प्रलय के दिन, अकेला[। ');
INSERT INTO "al_umari" VALUES(19,96,'19:96','निश्चय जो ईमान वाले हैं तथा सदाचार किये हैं, शीघ्र बना देगा, उनके लिए अत्यंत कृपाशील (दिलों में)[ प्रेम। ');
INSERT INTO "al_umari" VALUES(19,97,'19:97','अतः (हे नबी!) हमने सरल बना दिया है, इस (क़ुर्आन) को आपकी भाषा में, ताकि आप इसके द्वारा शुभ सूचना दें संयमियों (आज्ञाकारियों) को तथा सतर्क कर दें विरोधियों को।');
INSERT INTO "al_umari" VALUES(19,98,'19:98','तथा हमने ध्वस्त कर दिया है, इनसे पहले बहुत सी जातियों को, तो क्या आप देखते हैं, उनमें किसी को अथवा सुनते हैं, उनकी कोई ध्वनि?');
INSERT INTO "al_umari" VALUES(20,1,'20:1','ता, हा।');
INSERT INTO "al_umari" VALUES(20,2,'20:2','हमने नहीं अवतरित किया है आपपर क़ुर्आन इस लिए कि आप दुःखी हों[। ');
INSERT INTO "al_umari" VALUES(20,3,'20:3','परन्तु ये उसकी शिक्षा के लिए है, जो डरता[ हो। ');
INSERT INTO "al_umari" VALUES(20,4,'20:4','उतारा जाना उस ओर से है, जिसने उत्पत्ति की है धरती तथा उच्च आकाशों की।');
INSERT INTO "al_umari" VALUES(20,5,'20:5','जो अत्यंत कृपाशील, अर्श पर स्थिर है।');
INSERT INTO "al_umari" VALUES(20,6,'20:6','उसी का[ है, जो आकाशों, जो धरती में, जो दोनों के बीच तथा जो भूमि के नीचे है। ');
INSERT INTO "al_umari" VALUES(20,7,'20:7','यदि तुम उच्च स्वर में बात करो, तो वास्तव में, वह जानता है भेद को तथा अत्यधिक छुपे भेद को।');
INSERT INTO "al_umari" VALUES(20,8,'20:8','वही अल्लाह है, नहीं है कोई वंदनीय (पूज्य) परन्तु वही। उसी के उत्तम नाम हैं।');
INSERT INTO "al_umari" VALUES(20,9,'20:9','और (हे नबी!) क्या आपको मूसा की बात पहुँची?');
INSERT INTO "al_umari" VALUES(20,10,'20:10','जब उसने देखी एक अग्नि, फिर कहा अपने परिवार सेः रुको, मैंने एक अग्नि देखी है, सम्भव है कि मैं तुम्हारे पास उसका कोई अंगार लाऊँ अथवा पा जाऊँ आग पर मार्ग की कोई सूचना[। ');
INSERT INTO "al_umari" VALUES(20,11,'20:11','फिर जब वहाँ पहुँचा, तो पुकारा गयाः हे मूसा!');
INSERT INTO "al_umari" VALUES(20,12,'20:12','वास्तव में, मैं ही तेरा पालनहार हूँ, तू उतार दे अपने दोनों जूते, क्योंकि तू पवित्र वादी (उपत्यका) "तुवा" में है।');
INSERT INTO "al_umari" VALUES(20,13,'20:13','और मैंने तुझे चुन[ लिया है। अतः ध्यान से सुन, जो वह़्यी की जा रही है। ');
INSERT INTO "al_umari" VALUES(20,14,'20:14','निःसंदेह मैं ही अल्लाह हूँ, मेरे सिवा कोई पूज्य नहीं, तो मेरी ही इबादत (वंदना) कर तथा मेरे स्मरण (याद) के लिए नमाज़ की स्थापना[ कर। ');
INSERT INTO "al_umari" VALUES(20,15,'20:15','निश्चय प्रलय आने वाली है, मैं उसे गुप्त रखना चाहता हूँ, ताकि प्रतिकार (बदला) दिया जाये, प्रत्येक प्राणी को उसके प्रयास के अनुसार।');
INSERT INTO "al_umari" VALUES(20,16,'20:16','अतः, तुम्हें न रोक दे, उस ( के विश्वास) से, जो उसपर ईमान (विश्वास) नहीं रखता और अनुसरण किया अपनी इच्छा का, अन्यथा तेरा नाश हो जायेगा।');
INSERT INTO "al_umari" VALUES(20,17,'20:17','और हे मूसा! ये तेरे दाहिने हाथ में क्या है?');
INSERT INTO "al_umari" VALUES(20,18,'20:18','उत्तर दियाः ये मेरी लाठी है; मैं इसपर सहारा लेता हूँ, इससे अपनी बकरियों के लिए पत्ते झाड़ता हूँ तथा मेरी इसमें दूसरी आवश्यक्तायें (भी) हैं।');
INSERT INTO "al_umari" VALUES(20,19,'20:19','कहाः इसे फेंकिए, हे मूसा!');
INSERT INTO "al_umari" VALUES(20,20,'20:20','तो उसने उसे फेंक दिया और सहसा वह एक सर्प थी, जो दोड़ रहा था।');
INSERT INTO "al_umari" VALUES(20,21,'20:21','कहाः पकड़ ले इसे, और डर नहीं, हम इसे फेर देंगे, इसकी प्रथम स्थिति की ओर।');
INSERT INTO "al_umari" VALUES(20,22,'20:22','और अपना हाथ लगा दे अपनी कांख (बग़ल) की ओर, वह निकलेगा चमकता हुआ बिना किसी रोग के, ये दूसरा चमत्कार है।');
INSERT INTO "al_umari" VALUES(20,23,'20:23','ताकि हम तुझे दिखायें, अपनी बड़ी निशानियाँ।');
INSERT INTO "al_umari" VALUES(20,24,'20:24','तुम फ़िरऔन के पास जाओ, वह विद्रोही हो गया है।');
INSERT INTO "al_umari" VALUES(20,25,'20:25','मूसा ने प्रार्थना कीः हे मेरे पालनहार! खोल दे, मेरे लिए मेरा सीना।');
INSERT INTO "al_umari" VALUES(20,26,'20:26','तथा सरल कर दे, मेरे लिए मेरा काम।');
INSERT INTO "al_umari" VALUES(20,27,'20:27','और खोल दे, मेरी ज़ुबान की गाँठ।');
INSERT INTO "al_umari" VALUES(20,28,'20:28','ताकि लोग मेरी बात समझें।');
INSERT INTO "al_umari" VALUES(20,29,'20:29','तथा बना दे, मेरा एक सहायक मेरे परिवार में से।');
INSERT INTO "al_umari" VALUES(20,30,'20:30','मेरे भीई हारून को।');
INSERT INTO "al_umari" VALUES(20,31,'20:31','उसके द्वारा दृढ़ कर दे मेरी शक्ति को।');
INSERT INTO "al_umari" VALUES(20,32,'20:32','और साझी बना दे, उसे मेरे काम में।');
INSERT INTO "al_umari" VALUES(20,33,'20:33','ताकि हम दोनों तेरी पवित्रता का गान अधिक करें।');
INSERT INTO "al_umari" VALUES(20,34,'20:34','तथा तुझे अधिक स्मरण (याद) करें।');
INSERT INTO "al_umari" VALUES(20,35,'20:35','निःसंदेह, तू हमें भली प्रकार देखने-भालने वाला है।');
INSERT INTO "al_umari" VALUES(20,36,'20:36','अल्लाह ने कहाः हे मूसा! तेरी सब माँग पूरी कर दी गयी।');
INSERT INTO "al_umari" VALUES(20,37,'20:37','और हम उपकार कर चुके हैं तुमपर एक बार और[ (भी)। ');
INSERT INTO "al_umari" VALUES(20,38,'20:38','जब हमने उतार दिया, तेरी माँ के दिल में, जिसकी वह़्यी (प्रकाश्ना) की जा रही है।');
INSERT INTO "al_umari" VALUES(20,39,'20:39','कि इसे रख दे ताबूत (सन्दूक़) में, फिर उसे नदी में डाल दे, फिर नदी उसे किनारे लगा देगी, जिसे उठा लेगा मेरा शत्रु तथा उसका शत्रु[ और मैंने डाल दिया तुझपर अपनी ओर से विशेष प्रेम, ताकि तेरा पालन-पोषण मेरी रक्षा में हो।');
INSERT INTO "al_umari" VALUES(20,40,'20:40','जब चल रही थी तेरी बहन[, फिर कह रही थीः क्या मैं तुम्हें उसे बता दूँ, जो इसका लालन-पालन करे? फिर हमने पुनः तुम्हें पहुँचा दिया तुम्हारी माँ के पास, ताकि उसकी आँख ठण्डी हो और उदासीन न हो तथा हे मूसा! तूने मार दिया एक व्यक्ति को, तो हमने तुझे मुक्त कर दिया चिन्ता से और हमने तेरी भली-भाँति परीक्षा ली। फिर तू रह गया वर्षों मद्यन के लोगों में, फिर तू (मद्यन से) अपने निश्चित समय पर आ गया।');
INSERT INTO "al_umari" VALUES(20,41,'20:41','और मैंने बना लिया है तुझे विश्ष अपने लिए।');
INSERT INTO "al_umari" VALUES(20,42,'20:42','जा तू और तेरा भाई, मेरी निशानियाँ लेकर और दोनों आलस्य न करना मेरे स्मरण (याद) में।');
INSERT INTO "al_umari" VALUES(20,43,'20:43','तुम दोनों फ़िरऔन के पास जाओ, वास्तव में, वह उल्लंघन कर गया है।');
INSERT INTO "al_umari" VALUES(20,44,'20:44','फिर उससे कोमल बोल बोलो, कदाचित् वह शिक्षा ग्रहण करे अथवा डरे।');
INSERT INTO "al_umari" VALUES(20,45,'20:45','दोनों ने कहाः हे हमारे पालनहार! हमें भय है कि वह हमपर अत्याचार अथवा अतिक्रमण कर दे।');
INSERT INTO "al_umari" VALUES(20,46,'20:46','उस (अल्लाह) ने कहाः तुम भय न करो, मैं तुम दोनों के साथ हूँ, सुनता तथा देखता हूँ।');
INSERT INTO "al_umari" VALUES(20,47,'20:47','तुम उसके पास जाओ और कहो कि हम तेरे पालनहार के रसूल हैं। अतः, हमारे साथ बनी इस्राईल को जाने दे और उन्हें यातना न दे, हम तेरे पास तेरे पालनहार की निशानी लाये हैं और शान्ति उसके लिए है, जो मार्गदर्शन का अनुसरण करे।');
INSERT INTO "al_umari" VALUES(20,48,'20:48','वास्तव में, हमारी ओर वह़्यी (प्रकाशना) की गई है कि यातना उसी के लिए है, जो झुठलाये और मुख फेरे।');
INSERT INTO "al_umari" VALUES(20,49,'20:49','उसने कहाः हे मूसा! कौन है तुम दोनों का पालनहार?');
INSERT INTO "al_umari" VALUES(20,50,'20:50','मूसा ने कहाः हमारा पालनहार वह है, जिसने प्रत्येक वस्तु को उसका विशेष रूप प्रदान किया, फिर मार्गदर्शन[ दिया। ');
INSERT INTO "al_umari" VALUES(20,51,'20:51','उसने कहाः फिर उनकी दशा क्या होनी है, जो पूर्व के लोग हैं?');
INSERT INTO "al_umari" VALUES(20,52,'20:52','मूसा ने कहाः उसका ज्ञान मेरे पालनहार के पास एक लेख्य में सुरक्षित है, मेरा पालनहार न तो चूकता है और न[ भूलता है। ');
INSERT INTO "al_umari" VALUES(20,53,'20:53','जिसने तुम्हारे लिए धरती को बिस्तर बनाया और तुम्हारे चलने के लिए उसमें मार्ग बनाये और तुम्हारे लिए आकाश से जल बरसाया, फिर उसके द्वारा विभिन्न प्रकार की उपज निकाली।');
INSERT INTO "al_umari" VALUES(20,54,'20:54','तुम स्वयं खाओ तथा अपने पशुओं को चराओ, वस्तुतः, इसमें बहुत-सी निशानियाँ हैं बुध्दिमानों के लिए।');
INSERT INTO "al_umari" VALUES(20,55,'20:55','इसी (धरती) से हमने तुम्हारी उत्पत्ति की है, इसीमें तुम्हें वापस ले जायेंगे और इसीसे तुम सबको पुनः[ निकालेंगे। ');
INSERT INTO "al_umari" VALUES(20,56,'20:56','और हमने उसे दिखा दी अपनी सभी निशानियाँ, फिर भी उसने झुठला दिया और नहीं माना।');
INSERT INTO "al_umari" VALUES(20,57,'20:57','उसने कहाः क्या तू हमारे पास इसलिए आया है कि हमें हमारी धरती (देश) से अपने जादू (के बल) से निकाल दे, हे मूसा?');
INSERT INTO "al_umari" VALUES(20,58,'20:58','फिर तो हम तेरे पास अवश्य इसीके समान जादू लायेंगे, अतः हमारे और अपने बीच एक समय निर्धारित कर ले, जिसके विरुध्द न हम करेंगे और न तुम, एक खुले मैदान में।');
INSERT INTO "al_umari" VALUES(20,59,'20:59','मूसा ने कहाः तुम्हारा निर्धारित समय शोभा (उत्सव) का दिन[ है तथा ये कि लोग दिन चढ़े एकत्र हो जायेँ। ');
INSERT INTO "al_umari" VALUES(20,60,'20:60','फिर फ़िरऔन लौट गया,[ अपने हथकण्डे एकत्र किये और फिर आया। ');
INSERT INTO "al_umari" VALUES(20,61,'20:61','मूसा ने उन (जादूगरों) से कहाः तुम्हारा विनाश हो! अल्लाह पर मिथ्या आरोप न लगाओ कि वह तुम्हारा किसी यातना द्वारा सर्वनाश कर दे और वह निष्फल ही रहा है, जिसने मिथ्यारोपण किया।');
INSERT INTO "al_umari" VALUES(20,62,'20:62','फिर[ उनके बीच विवाद हो गया और वे चुपके-चुपके गुप्त मंत्रणा करने लगे। ');
INSERT INTO "al_umari" VALUES(20,63,'20:63','कुछ ने कहाः ये दोनों वास्तव में, जादूगर हैं, दोनों चाहते हैं कि तुम्हें तुम्हारी धरती से अपने जादू द्वारा निकाल दें और तुम्हारी आदर्श प्रणाली का अन्त कर दें।');
INSERT INTO "al_umari" VALUES(20,64,'20:64','अतः अपने सब उपाय एकत्र कर लो, फिर एक पंक्ति में होकर आ जाओ और आज वही सफल हो गया, जो ऊपर रहा।');
INSERT INTO "al_umari" VALUES(20,65,'20:65','उन्होंने कहाः हे मूसा! तू फेंकता है या पहले हम फेंकें?');
INSERT INTO "al_umari" VALUES(20,66,'20:66','मूसा ने कहाः बल्कि तुम्हीं फेंको। फिर उनकी रस्सियाँ तथा लाठियाँ उसे लग रही थीं कि उनके जादू (के बल) से दौड़ रही हैं।');
INSERT INTO "al_umari" VALUES(20,67,'20:67','इससे मूसा अपने मन में डर गया[। ');
INSERT INTO "al_umari" VALUES(20,68,'20:68','हमने कहाः डर मत, तूही ऊपर रहेगा।');
INSERT INTO "al_umari" VALUES(20,69,'20:69','और फेंक दे, जो तेरे दायें हाथ में है, वह निगल जायेगा, जो कुछ उन्होंने बनाया है। वह केवल जादू का स्वाँग बनाकर लाए हैं तथा जादूगर सफल नहीं होता, जहाँ से आये।');
INSERT INTO "al_umari" VALUES(20,70,'20:70','अन्ततः, जादूगर सज्दे में गिर गये, उन्होंने कहा कि हम ईमान लाये हारून तथा मूसा के पालनहार पर।');
INSERT INTO "al_umari" VALUES(20,71,'20:71','फ़िरऔन बालाः क्या तुमने उसका विश्वास कर लिया, इससे पूर्व कि मैं तुम्हें आज्ञा दूँ? वास्तव में, वह तुम्हारा बड़ा (गुरू) है, जिसने तुम्हें जादू सिखाया है। तो मैं अवश्य कटवा दूँगा तुम्हारे हाथों तथा पावों को, विपरीत दिशा[ से और तुम्हें सूली दे दूँगा खजूर के तनों पर तथा तुम्हें अवश्य ज्ञान हो जायेगा कि हममें से किसकी यातना अधिक कड़ी तथा स्थायी है। ');
INSERT INTO "al_umari" VALUES(20,72,'20:72','उन्होंने कहाः हम तुझे कभी उन खुली निशानियों (तर्कों) पर प्रधानता नहीं देंगे, जो हमारे पास आ गयी हैं और न उस (अल्लाह) पर, जिसने हमें पैदा किया है, तू जो करना चाहे, कर ले, तू बस इसी सांसारिक जीवन में आदेश दे सकता है।');
INSERT INTO "al_umari" VALUES(20,73,'20:73','हम तो अपने पालनहार पर ईमान लाये हैं, ताकि वह क्षमा कर दे, हमारे लिए, हमारे पापों को तथा जिस जादू पर तूने हमें बाध्य किया और अल्लाह सर्वोत्तम तथा अनन्त[ है। ');
INSERT INTO "al_umari" VALUES(20,74,'20:74','वास्तव में, जो जायेगा अपने पालनहार के पास पापी बनकर, तो उसी के लिए नरक है, जिसमें न वह मरेगा और न जीवित रहेगा[। ');
INSERT INTO "al_umari" VALUES(20,75,'20:75','तथा जो उसके पास ईमान लेकर आयेगा, तो उन्हीं के लिए उच्च श्रेणियाँ होंगी।');
INSERT INTO "al_umari" VALUES(20,76,'20:76','स्थायी स्वर्ग, जिनमें नहरें बहती होंगी, जिनमें सदा वासी होंगे और यही उसका प्रतिफल है, जो पवित्र हो गया।');
INSERT INTO "al_umari" VALUES(20,77,'20:77','और हमने मूसी की ओर वह़्यी की कि रातों-रात चल पड़ मेरे भक्तों को लेकर और उनके लिए सागर में सूखा मार्ग बना ले[, तुझे पा लिए जाने का कोई भय नहीं होगा और न डरेगा। ');
INSERT INTO "al_umari" VALUES(20,78,'20:78','फिर उनका पीछा किया फ़िरऔन ने अपनी सेना के साथ, तो उनपर सागर छा गया, जैसा कुछ छा गया।');
INSERT INTO "al_umari" VALUES(20,79,'20:79','और कुपथ कर दिया फ़िरऔन ने अपनी जाति को और सुपथ नहीं दिखाया।');
INSERT INTO "al_umari" VALUES(20,80,'20:80','हे इस्राईल के पुत्रो! हमने तुम्हें मुक्त कर दिया तुम्हारे शत्रु से और वचन दिया तुम्हें तूर पर्वत से दाहिनी[ ओर का तथा तुमपर उतारा "मन्न" तथा "सल्वा" ।');
INSERT INTO "al_umari" VALUES(20,81,'20:81','खाओ उन स्वच्छ चीज़ों में से, जो जीविका हमने तुम्हें दी है तथा उल्लंघन न करो उसमें, अन्यथा उतर जायेगा तुमपर मेरा प्रकोप तथा जिसपर उतर जायेगा मेरा प्रकोप, तो निःसंदेह वह गिर गया।');
INSERT INTO "al_umari" VALUES(20,82,'20:82','और मैं निश्चय बड़ा क्षमाशील हूँ उसके लिए, जिसने क्षमा याचना की तथा ईमान लाया और सदाचार किया फिर सुपथ पर रहा।');
INSERT INTO "al_umari" VALUES(20,83,'20:83','और हे मूसा! क्या चीज़ तुम्हें ले आई अपनी जाति से पहले[? ');
INSERT INTO "al_umari" VALUES(20,84,'20:84','उसने कहाः वे मेरे पीछे आ ही रहे हैं और मैं तेरी सेवा में शीघ्र आ गया, हे मेरे पालनहार! ताकि तू प्रसन्न हो जाये।');
INSERT INTO "al_umari" VALUES(20,85,'20:85','अल्लाह ने कहाः हमने परीक्षा में डाल दिया तेरी जाति को तेरे (आने के) पश्चात् और कुपथ कर दिया है उन्हें सामरी[ ने। ');
INSERT INTO "al_umari" VALUES(20,86,'20:86','तो मूसा वापस आया अपनी जाति की ओर अति क्रुध्द-शोकातुर होकर। उसने कहाः हे मेरी जाति के लोगो! क्या तुम्हें वचन नहीं दिया था तुम्हारे पालनहार ने एक अच्छा वचन[? तो क्या तुम्हें बहुत दिन लग गये? अथवा तुमने चाहा कि उतर जाये कोई प्रकोप तुम्हारे पालनहार की ओर से? अतः तुमने मेरे वचन को भंग कर दिया।');
INSERT INTO "al_umari" VALUES(20,87,'20:87','उन्होंने उत्तर दिया हमने नहीं भंग किया है तेरा वचन अपनी इच्छा से, परन्तु हमपर लाद दिया गया था जाति[ के आभूषणों का बोझ, तो हमने उसे फेंक दिया और ऐसे ही फेंक दिया सामरी ने।');
INSERT INTO "al_umari" VALUES(20,88,'20:88','फिर वह[ निकाल लाया उनके लिए एक बछड़े की मूर्ति, जिसकी गाय जैसी ध्वनि (आवाज़) थी, तो सबने कहाः ये है तुम्हारा पूज्य तथा मूसा का पूज्य, (परन्तु) मूसा इसे भूल गया है। ');
INSERT INTO "al_umari" VALUES(20,89,'20:89','तो क्या वे नहीं देखते कि वह न उनकी किसी बात की उत्तर देता है और न अधिकार रखता है, उनके लिए किसी हानि का, न किसी लाभ का[? ');
INSERT INTO "al_umari" VALUES(20,90,'20:90','और कह दिया था हारून ने इससे पहले ही कि हे मेरी जाति के लोगो! तुम्हारी परीक्षा की गयी है इसके द्वारा और वास्तव में तुम्हारा पालनहार अत्यंत कृपाशील है। अतः मेरा अनुसरण करो तथा मेरे आदेश का पालन करो।');
INSERT INTO "al_umari" VALUES(20,91,'20:91','उन्होंने कहाः हमसब उसी के पुजारी रहेंगे, जब तक (तूर से) हमारे पास मूसा वापस न आ जाये।');
INSERT INTO "al_umari" VALUES(20,92,'20:92','मूसा ने कहाः हे हारून! किस बात ने तुझे रोक दिया, जब तूने उन्हें देखा कि कुपथ हो गये?');
INSERT INTO "al_umari" VALUES(20,93,'20:93','कि मेरा अनुसरण न करे? क्या तूने अवज्ञा कर दी मेरे आदेश की?');
INSERT INTO "al_umari" VALUES(20,94,'20:94','उसने कहाः मेरे माँ जाये भाई! मेरी दाढ़ी न पकड़ और न मेरा सिर। वास्तव में, मुझे भय हुआ कि आप कहेंगे कि तूने विभेद उत्पन्न कर दिया बनी इस्राईल में और[ प्रतीक्षा नहीं की मेरी बात (आदेश) की। ');
INSERT INTO "al_umari" VALUES(20,95,'20:95','(मूसा ने) पूछाः तेरा समाचार क्या है, हे सामरी?');
INSERT INTO "al_umari" VALUES(20,96,'20:96','उसने कहाः मैंने वह चीज़ देखी, जिसे उन्होंने नहीं देखा, तो मैंने ले ली एक मुट्ठी रसूल के पद्चिन्ह से, फिर उसे फेंक दिया और इसी प्रकार सुझा दिया मुझे[ मेरे मन ने। ');
INSERT INTO "al_umari" VALUES(20,97,'20:97','मूसा ने कहाः जा तेरे लिए जीवन में ये होना है कि तू कहता रहेः मुझे स्पर्श न करना[। तथा तेरे लिए एक और वचन है, जिसके विरुध्द कदापि न होगा और अपने पूज्य को देख, जिसका पुजारी बना रहा, हम अवश्य उसे जला देंगे, फिर उसे उड़ा देंगे नदी में चूर-चूर करके।');
INSERT INTO "al_umari" VALUES(20,98,'20:98','निःसंदेह तुम सभी का पूज्य, बस अल्लाह है, कोई पूज्य नहीं है, उसके सिवा। वह समोये हुए है, प्रत्येक वस्तु को (अपने) ज्ञान में।');
INSERT INTO "al_umari" VALUES(20,99,'20:99','इसी प्रकार, (हे नबी!) हम आपके समक्ष विगत समाचारों में से कुछ का वर्णन कर रहे हैं और हमने आपको प्रदान कर दी है अपने पास से एक शिक्षा (क़ुर्आन)।');
INSERT INTO "al_umari" VALUES(20,100,'20:100','जो उससे मुँह फेरेगा, तो वह निश्चय प्रलय के दिन लादे हुए होगा, भारी[ बोझ। ');
INSERT INTO "al_umari" VALUES(20,101,'20:101','वे सदा रहन वाले होंगे उसमें और प्रलय के दिन उनके लिए बुरा बोझ होगा।');
INSERT INTO "al_umari" VALUES(20,102,'20:102','जिस दिन फूंक दिया जायेगा सूर[ (नरसिंघा) में, और हम एकत्र कर देंगे उस दिन पापियों को, इस दशा में कि उनकी आँखें (भय से) नीली होंगी। ');
INSERT INTO "al_umari" VALUES(20,103,'20:103','वे आपस में चुपके-चुपके कहेंगे कि तुम (संसार में) बस दस दिन रहे हो।');
INSERT INTO "al_umari" VALUES(20,104,'20:104','हम भली-भाँति जानते हैं, जो कुछ वे कहेंगे, जिस समय कहेगा उनमें से सबसे चतुर कि तुम केवल एक ही दिन रहे[ हो। ');
INSERT INTO "al_umari" VALUES(20,105,'20:105','वे आपसे प्रश्न कर रहे हैं पर्वतों के संबन्ध में? आप कह दें कि उड़ा देगा उन्हें मेरा पालनहार चूर-चूर करके।');
INSERT INTO "al_umari" VALUES(20,106,'20:106','फिर धरती को छोड़ देगा, समतल मैदान बनाकर।');
INSERT INTO "al_umari" VALUES(20,107,'20:107','तुम नहीं देखोगे उसमें कोई टेढ़ापन और न नीच-ऊँच।');
INSERT INTO "al_umari" VALUES(20,108,'20:108','उस दिन लोग पीछे चलेंगे पुकारने वाले के, कोई उससे कतरायेगा नहीं और धीमी हो जायेँगी आवाजें अत्यंत कृपाशील के लिए, फिर तुम नहीं सुनोगे कानाफूँसी की आवाज़ के सिवा।');
INSERT INTO "al_umari" VALUES(20,109,'20:109','उस दिन लाभ नहीं देगी सिफ़ारिश, परन्तु जिसे आज्ञा दे अत्यंत कृपाशील और प्रसन्न हो उसके[ लिए बात करने से। ');
INSERT INTO "al_umari" VALUES(20,110,'20:110','वह जानता है, जो कुछ उनके आगे तथा पीछे है और वे उसका पूरा ज्ञान नहीं रखते।');
INSERT INTO "al_umari" VALUES(20,111,'20:111','तथा सभी के सिर झुक जायेंगे, जीवित नित्य स्थायी (अल्लाह) के लिए और निश्चय वह निष्फल हो गया, जिसने अत्याचार लाद[ लिया। ');
INSERT INTO "al_umari" VALUES(20,112,'20:112','तथा जो सदाचार करेगा और वह ईमान वाला भी हो, तो वह नहीं डरेगा अत्याचार से, न अधिकार हनन से।');
INSERT INTO "al_umari" VALUES(20,113,'20:113','और इसी प्रकार, हमने इस अरबी क़ुर्आन को अवतरित किया है तथा विभिन्न प्रकार से वर्णन कर दिया है उसमें चेतावनी का, ताकि लोग आज्ञाकारी हो जायेँ अथवा वह उनके लिए उत्पन्न कर दे एक शिक्षा।');
INSERT INTO "al_umari" VALUES(20,114,'20:114','अतः, उच्च है अल्लाह वास्तविक स्वामी और (हे नबी!) आप शीघ्रता[ न करें क़ुर्आन के साथ इससे पूर्व कि पूरी कर दी जाये आपकी ओर इसकी वह़्यी (प्रकाशना) तथा प्रार्थना करें कि हे मेरे पालनहार! मुझे अधिक ज्ञान प्रदान कर। ');
INSERT INTO "al_umari" VALUES(20,115,'20:115','और हमने आदेश दिया आदम को इससे पहले, तो वह भूल गया और हमने नहीं पाया उसमें कोई दृढ़ संकल्प[। ');
INSERT INTO "al_umari" VALUES(20,116,'20:116','तथा जब हमने कहा फ़रिश्तों से कि सज्दा करो आदम को, तो सबने सज्दा किया इब्लीस के सिवा, उसने इन्कार कर दिया।');
INSERT INTO "al_umari" VALUES(20,117,'20:117','तब हमने कहाः हे आदम! वास्तव में, ये शत्रु है तेरा और तेरी पत्नी का, तो ऐसा न हो कि तुम दोनों को निकलवा दे स्वर्ग से और तू आपदा में पड़ जाये।');
INSERT INTO "al_umari" VALUES(20,118,'20:118','यहाँ तुझे ये सुविधा है कि न भूखा रहता है और न नग्न रहता है।');
INSERT INTO "al_umari" VALUES(20,119,'20:119','और न प्यासा होता है और न तुझे धूप सताती है।');
INSERT INTO "al_umari" VALUES(20,120,'20:120','तो फुसलाया उसे शैतान ने, कहाः हे आदम! क्या मैं तुझे न बताऊँ, शाश्वत जीवन का वृक्ष तथा ऐसा राज्य, जो पतनशील न हो?');
INSERT INTO "al_umari" VALUES(20,121,'20:121','तो दोनों ने उस (वृक्ष) से खा लिया, फिर उनके गुप्तांग उन दोनों के लिए खुल गये और दोनों चिपकाने लगे अपने ऊपर स्वर्ग के पत्ते और आदम अवज्ञा कर गया अपने पालनहार की और कुपथ हो गया।');
INSERT INTO "al_umari" VALUES(20,122,'20:122','फिर उस (अल्लाह) ने उसे चुन लिया और उसे क्षमा कर दिया और सुपथ दिखा दिया।');
INSERT INTO "al_umari" VALUES(20,123,'20:123','कहाः तुम दोनों (आदम तथा शैतान) यहाँ से उतर जाओ, तुम एक-दूसरे के शत्रु हो। अब यदि आये तुम्हारे पास मेरी ओर से मार्गदर्शन, तो जो अनुपालन करेगा मेरे मार्गदर्शन का, वह कुपथ नहीं होगा और न दुर्भाग्य ग्रस्त होगा।');
INSERT INTO "al_umari" VALUES(20,124,'20:124','तथा जो मुख फेर लेगा मेरे स्मरण से, तो उसी का सांसारिक जीवन संकीर्ण (तंग)[ होगा तथा हम उसे उठायेंगे प्रलय के दिन अन्धा करके। ');
INSERT INTO "al_umari" VALUES(20,125,'20:125','वह कहेगाः मेरे पालनहार! मुझे अन्धा क्यों उठाया, मैं तो (संसार में) आँखों वाला था?');
INSERT INTO "al_umari" VALUES(20,126,'20:126','अल्लाह कहेगाः इसी प्रकार, तेरे पास हमारी आयतें आयीं, तो तूने उन्हें भुला दिया। अतः इसी प्रकार, आज तू भुला दिया जायेगा।');
INSERT INTO "al_umari" VALUES(20,127,'20:127','तथा इसी प्रकार, हम बदला देते हैं उसे, जो सीमा का उल्लंघन करे और ईमान न लाये अपने पालनहार की आयतों पर और निश्चय आख़िरत की यातना अति कड़ी तथा अधिक स्थायी है।');
INSERT INTO "al_umari" VALUES(20,128,'20:128','तो क्या उन्हें मार्गदर्शन नहीं दिया इस बात ने कि हमने ध्वस्त कर दिया, इनसे पहले बहुत-सी जातियों को, जो चल-फिर रही थीं अपनी बस्तियों में, निःसंदेह इसमें निशानियाँ हैं बुध्दिमानों के लिए।');
INSERT INTO "al_umari" VALUES(20,129,'20:129','और यदि, एक बात पहले से निश्चित न होती आपके पालनहार की ओर से, तो यातना आ चुकी होती और एक निर्धारित समय न होता[। ');
INSERT INTO "al_umari" VALUES(20,130,'20:130','अतः आप सहन करें उनकी बातों को तथा अपने पालनहार की पवित्रता का वर्णन उसकी प्रशंसा के साथ करते रहें, सूर्योदय से पहले[, सुर्यास्त से पहले, रात्रि के क्षणों में और दिन के किनारों में, ताकि आप प्रसन्न हो जायेँ।');
INSERT INTO "al_umari" VALUES(20,131,'20:131','और कदापि न देखिए आप, उस आनन्द की ओर, जो हमने उन[ में से विभिन्न प्रकार के लोगों को दे रखा है, वे सांसारिक जीवन की शोभा है, ताकि हम उनकी परीक्षा लें और आपके पालनहार का प्रदान ही उत्तम तथा अति स्थायी है।');
INSERT INTO "al_umari" VALUES(20,132,'20:132','और आप अपने परिवार को नमाज़ का आदेश दें और स्वयं भी उसपर स्थित रहें, हम आपसे कोई जीविका नहीं माँगते, हम ही आपको जीविका प्रदान करते हैं और अच्छा परिणाम आज्ञाकारियों के लिए है।');
INSERT INTO "al_umari" VALUES(20,133,'20:133','तथा उन्होंने कहाः क्यों वह हमारे पास कोई निशानी अपने पालनहार की ओर से नहीं लाता? क्या उनके पास उसका प्रत्यक्ष प्रमाण (क़र्आन) नहीं आ गया, जिसमें अगली पुस्तकों की (शिक्षायें) हैं?');
INSERT INTO "al_umari" VALUES(20,134,'20:134','और यदि हम ध्वस्त कर देते उन्हें, किसी यातना से, इससे[ पहले, तो वे अवश्य कहते कि हे हमारे पालनहार! तूने हमारी ओर कोई रसूल क्यों नहीं भेजा कि हम तेरी आयतों का अनुपालन करते, इससे पहले कि हम अपमानित और हीन होते। ');
INSERT INTO "al_umari" VALUES(20,135,'20:135','आप कह दें कि प्रत्येक, (परिणाम की) प्रतीक्षा में है। अतः तुमभी प्रतीक्षा करो, शीघ्र ही तुम्हें ज्ञान हो जायेगा कि कौन सीधी राह वाले हैं, और किसने सीधी राह पायी है।');
INSERT INTO "al_umari" VALUES(21,1,'21:1','समीप आ गया है लोगों के ह़साब[ का समय, जबकि वे अचेतना में मुँह फेरे हुए हैं। ');
INSERT INTO "al_umari" VALUES(21,2,'21:2','नहीं आती उनके पास, उनके पालनहार की ओर से कोई नई शिक्षा[, परन्तु उसे सुनते हैं और खेलते रह जाते हैं। ');
INSERT INTO "al_umari" VALUES(21,3,'21:3','निश्चेत हैं उनके दिल और उन्होंने चुपके-चुपके आपस में बातें कीं, जो अत्याचारी हो गयेः ये (नबी) तो बस एक पुरुष है तुम्हारे समान, तो क्या तुम जादू के पास जाते हो, जबकि तुम देखते हो[? ');
INSERT INTO "al_umari" VALUES(21,4,'21:4','आप कह दें कि मेरा पालनहार जानता है प्रत्येक बात को, जो आकाश तथा धरती में है और वह सब सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(21,5,'21:5','बल्कि उन्होंने कह दिया कि ये[ बिखरे स्वप्न हैं। बल्कि उस (नबी) ने इसे स्वयं बना लिया है, बल्कि वह कवि है! अन्यथा उसे चाहिए कि हमारे पास कोई निशानी ले आये, जैसे पूर्व के रसूल (निशानियों के साथ) भेजे गये। ');
INSERT INTO "al_umari" VALUES(21,6,'21:6','नहीं ईमान[ लायी इनसे पहले कोई बस्ती, जिसका हमने विनाश किया, तो क्या ये ईमान लायेंगे? ');
INSERT INTO "al_umari" VALUES(21,7,'21:7','और (हे नबी!) हमने आपसे पहले मनुष्य पुरुषों को ही रसूल बनाकर भेजा, जिनकी ओर वह़्यी भेजते रहे। फिर तुम ज्ञानियों[ से पूछ लो, यदि तुम (स्वयं) नहीं जानते हो।');
INSERT INTO "al_umari" VALUES(21,8,'21:8','तथा नहीं बनाये हमने उनके ऐसे शरीर,[ जो भोजन न करते हों तथा न वे सदावासी थे। ');
INSERT INTO "al_umari" VALUES(21,9,'21:9','फिर हमने पूरे कर दिये, उनसे किये हुए वचन और हमने बचा लिया उन्हें और जिसे हमने चाहा और विनाश कर दिया उल्लंघनकारियों का।');
INSERT INTO "al_umari" VALUES(21,10,'21:10','निःसंदेह, हमने उतार दी है तुम्हारी ओर एक पुस्तक (क़ुर्आन) जिसमें तुम्हारे लिए शिक्षा है। तो क्या तुम समझते नहीं हो?');
INSERT INTO "al_umari" VALUES(21,11,'21:11','और हमने तोड़कर रख दिया बहुत सी बस्तियों को, जो अत्याचारी थीं और हमने पैदा कर दिया उनके पश्चात् दूसरी जाति को।');
INSERT INTO "al_umari" VALUES(21,12,'21:12','फिर जब उन्हें संवेदन हो गया हमारे प्रकोप का, तो अकस्मात् वहाँ से भागने लगे।');
INSERT INTO "al_umari" VALUES(21,13,'21:13','(कहा गया) भागो नहीं! तथा तुम वापस जाओ, जिस सुख-सुविधा में थे तथा अपने घरों की ओर, ताकि तुमसे पूछा[ जाये। ');
INSERT INTO "al_umari" VALUES(21,14,'21:14','उन्होंने कहाःहाय हमारा विनाश! वास्वम में, हम अत्याचारी थे।');
INSERT INTO "al_umari" VALUES(21,15,'21:15','और फिर बराबर यही उनकी पुकार रही, यहाँतक कि हमने बना दिया उन्हें कटी खेती के समान बुझे हुए।');
INSERT INTO "al_umari" VALUES(21,16,'21:16','औप हमने नहीं पैदा किया है आकाश और धरती को तथा जो कुछ दोनों के बीच है, खेल के लिए।');
INSERT INTO "al_umari" VALUES(21,17,'21:17','यदि हम कोई खेल बनाना चाहते, तो उसे अपने पास ही से बना[ लेते, यदि हमें ये करना होता। ');
INSERT INTO "al_umari" VALUES(21,18,'21:18','बल्कि हम मारते हैं सत्य से असत्य पर, तो वह उसका सिर कुचल देता है और वह अकस्मात समाप्त हो जाता है और तुम्हारे लिए विनाश है, उन बातों के कारण, जो तुम बनाते हो।');
INSERT INTO "al_umari" VALUES(21,19,'21:19','और उसी का है, जो आकाशों तथा धरती में है और जो फ़रिश्ते उसके पास हैं, वे उसकी इबादत (वंदना) से अभिमान नहीं करते और न थकते हैं।');
INSERT INTO "al_umari" VALUES(21,20,'21:20','वे रात और दिन उसकी पवित्रता का गान करते हैं तथा आलस्य नहीं करते।');
INSERT INTO "al_umari" VALUES(21,21,'21:21','क्या इनके बनाये हुए पार्थिव पूज्य ऐसे हैं, जो (निर्जीव) को जीवित कर देते हैं?');
INSERT INTO "al_umari" VALUES(21,22,'21:22','यदि होते उन दोनों[ में अन्य पूज्य, अल्लाह के सिवा, तो निश्चय दोनों की व्यवस्था बिगड़ जाती। अतः पवित्र है अल्लाह, अर्श (सिंहासन) का स्वामी, उन बातों से, जो वे बता रहे हैं।');
INSERT INTO "al_umari" VALUES(21,23,'21:23','वह उत्तर दायी नहीं है अपने कार्य का और सभी (उसके समक्ष) उत्तर दायी हैं।');
INSERT INTO "al_umari" VALUES(21,24,'21:24','क्या उन्होंने बना लिए हैं, उसके सिवा अनेक पूज्य? (हे नबी!) आप कहें कि अपना प्रमाण लाओ। ये (क़ुर्आन) उनके लिए शिक्षा है, जो मेरे साथ हैं और ये मुझसे पूर्व के लोगों की शिक्षा[ है, बल्कि उनमें से अधिक्तर सत्य का ज्ञान नहीं रखते। इसी कारण, वे विमुख हैं। ');
INSERT INTO "al_umari" VALUES(21,25,'21:25','और नहीं भेजा हमने आपसे पहले कोई भी रसूल, परन्तु उसकी ओर यही वह़्यी (प्रकाशना) करते रहे कि मेरे सिवा कोई पूज्य नहीं है। अतः मेरी ही इबादत (वंदना) करो।');
INSERT INTO "al_umari" VALUES(21,26,'21:26','और उन (मुश्रिकों) ने कहा कि बना लिया है अत्यंत कृपाशील ने संतति। वह पवित्र है। बल्कि वे (फ़रिश्ते)[ आदरणीय भक्त हैं। ');
INSERT INTO "al_umari" VALUES(21,27,'21:27','वे उसके समक्ष बढ़कर नहीं बोलते और उसके आदेशानुसार काम करते हैं।');
INSERT INTO "al_umari" VALUES(21,28,'21:28','वह जानता है, जो उनके सामने है और जो उनसे ओझल है। वह किसी की सिफ़ारिश नहीं करेंगे, उसके सिवा जिससे वह (अल्लाह) प्रसन्न[ हो तथा वह उसके भय से सहमे रहते हैं। ');
INSERT INTO "al_umari" VALUES(21,29,'21:29','और जो कह दे उनमें से कि मैं पूज्य हूँ अल्लाह के सिवा, तो वही है, जिसे हम दण्ड देंगे नरक का, इसी प्रकार, हम दण्ड दिया करते हैं अत्याचारियों को।');
INSERT INTO "al_umari" VALUES(21,30,'21:30','और क्या उन्होंने विचार नहीं किया, जो काफ़िर हो गये कि आकाश तथा धरती दोनों मिले हुए[ थे, तो हमने दोनों को अलग-अलग किया तथा हमने बनाया पानी से प्रत्येक जीवित चीज़ को? फिर क्या वे (इस बात पर) विश्वास नहीं करते? ');
INSERT INTO "al_umari" VALUES(21,31,'21:31','और हमने बना दिये धरती में पर्वत, ताकि झुक न[ जाये उनके साथ और बना दिये उन (पर्वतों) में चौड़े रास्ते, ताकि लोग राह पायें। ');
INSERT INTO "al_umari" VALUES(21,32,'21:32','और हमने बना दिया आकाश को सुरक्षित छत, फिर भी वे उसके प्रतीकों (निशानियों) से मुँह फेरे हुए हैं।');
INSERT INTO "al_umari" VALUES(21,33,'21:33','तथा वही है, जिसने उत्पत्ति की है रात्रि तथा दिवस की और सूर्य तथा चाँद की, प्रत्येक एक मण्डल में तैर रहे[ हैं। ');
INSERT INTO "al_umari" VALUES(21,34,'21:34','और (हे नबी!) हमने नहीं बनायी है, किसी मनुष्य के लिए आपसे पहले नित्यता। तो यदि, आप मर[ जायें, तो क्या वे नित्य जीवी हैं? ');
INSERT INTO "al_umari" VALUES(21,35,'21:35','प्रत्येक जीव को मरण का स्वाद चखना है और हम तुम्हारी परीक्षा कर रहे हैं, अच्छी तथा बुरी परिस्थितियों से तथा तुम्हें हमारी ही ओर फिर आना है।');
INSERT INTO "al_umari" VALUES(21,36,'21:36','तथा जब देखते हैं आपको, जो काफ़िर हो गये, तो बना लेते हैं आपको उपहास, (वे कहते हैं:) क्या यही है, जो तुम्हारे पूज्यों की चर्चा किया करता है? जबकि वे स्वयं रह़मान (अत्यंत कृपाशील) के स्मरण के[ निवर्ती हैं। ');
INSERT INTO "al_umari" VALUES(21,37,'21:37','मनुष्य जन्मजात व्यग्र (अधीर) है, मैं शीघ्र तुम्हें अपनी निशानियाँ दिखा दूँगा। अतः, तुम जल्दी न करो।');
INSERT INTO "al_umari" VALUES(21,38,'21:38','तथा वे कहते हैं कि कब पूरी होगी ये[ धमकी, यदि तुम लोग सच्चे हो? ');
INSERT INTO "al_umari" VALUES(21,39,'21:39','यदि जान लें, जो काफ़िर हो गये हैं, उस समय को, जब वे नहीं बचा सकेंगे अपने मुखों को अग्नि से और न अपनी पीठों को और न उनकी कोई सहायता की जायेगी (तो ऐसी बातें नहीं करेंगे।)');
INSERT INTO "al_umari" VALUES(21,40,'21:40','बल्कि वह समय उनपर आ जायेगा अचानक और उन्हें आश्चर्य चकित कर देगा, जिसे वे फेर नहीं सकेंगे और न उन्हें समय दिया जायेगा।');
INSERT INTO "al_umari" VALUES(21,41,'21:41','और उपहास किया गया बहुत-से रसूलों का, आपसे पहले, तो घेर लिया उन्हें जिन्होंने उपहास किया उनमें से, उस चीज़ ने, जिस[ का उपहास कर रहे थे। ');
INSERT INTO "al_umari" VALUES(21,42,'21:42','आप पूछिये कि कौन तुम्हारी रक्षा करेगा रात तथा दिन में अत्यंत कृपाशील[ से? बल्कि वे अपने पालनहार की शिक्षा (क़र्आन) से विमुख हैं। ');
INSERT INTO "al_umari" VALUES(21,43,'21:43','क्या उनके पूज्य हैं, जो उन्हें बचायेंगे हम से? वे स्वयं अपनी सहायता नहीं कर सकेंगे और न हमारी ओर से उनका साथ दिया जायेगा।');
INSERT INTO "al_umari" VALUES(21,44,'21:44','बल्कि हमने जीवन का लाभ पहुँचाया है, उनको तथा उनके पूर्वजों को, यहाँतक कि (सुखों में) उनकी बड़ी आयु गुज़र[ गयी, तो क्या वह नहीं देखते कि हम धरती को कम करते आ रहे हैं उसके किनारों से, फिर क्या वे विजय हो रहे हैं? ');
INSERT INTO "al_umari" VALUES(21,45,'21:45','(हे नबी!) आप कह दें कि मैं तो वह़्यी ही के आधार पर तुम्हें सावधान कर रहा हूँ। (परन्तु) बहरे पुकार नहीं सुनते, जब उन्हें सावधान किया जाता है।');
INSERT INTO "al_umari" VALUES(21,46,'21:46','और यदि छू जाये उन्हें आपके पालनहार की तनिक भी यातना, तो अवश्य पुकारेंगे कि हाय हमारा विनाश! निश्चय ही हम अत्याचारी[ थे। ');
INSERT INTO "al_umari" VALUES(21,47,'21:47','और हम रख देंगे न्याय का तराज़ू[ प्रलय के दिन, फिर नहीं अत्याचार किया जायेगा किसी पर कुछ भी तथा यदि होगा राय के दाने के बराबर (किसी का कर्म) तो हम उसे सामने ले आयेंगे और हम बस (काफ़ी) हैं ह़िसाब लेने वाले। ');
INSERT INTO "al_umari" VALUES(21,48,'21:48','और हम दे चुके हैं, मूसा तथा हारून को विवेक, प्रकाश और शिक्षाप्रद पुस्तक आज्ञाकारियों के लिए।');
INSERT INTO "al_umari" VALUES(21,49,'21:49','जो डरते हों अपने पालनहार से बिन देखे और वे प्रलय से भयभीत हों।');
INSERT INTO "al_umari" VALUES(21,50,'21:50','और ये (क़ुर्आन) एक शुभ शिक्षा है, जिसे हमने उतारा है, तो क्या तुम इसके इन्कारी हो?');
INSERT INTO "al_umari" VALUES(21,51,'21:51','और हमने प्रदान की थी इब्राहीम को, उसकी चेतना इससे पहले और हम उससे भली-भाँति अवगत थे।');
INSERT INTO "al_umari" VALUES(21,52,'21:52','जब उसने अपने बाप तथा अपनी जाति से कहाः ये प्रतिमाएँ (मूर्तियाँ) कैसी हैं, जिनकी पूजा में तुम लगे हुए हो?');
INSERT INTO "al_umari" VALUES(21,53,'21:53','उन्होंने कहाः हमने पाया है अपने पूर्वजों को इनकी पूजा करते हुए।');
INSERT INTO "al_umari" VALUES(21,54,'21:54','उस (इब्राहीम) ने कहाः निश्चय तुम और तुम्हारे पूर्वज खुले कुपथ में हो।');
INSERT INTO "al_umari" VALUES(21,55,'21:55','उन्होंने कहाः क्या तुम लाये हो हमारे पास सत्य या तुम उपहास कर रहे हो?');
INSERT INTO "al_umari" VALUES(21,56,'21:56','उसने कहाः बल्कि तुम्हारा पालनहार आकाशों तथा धरती का पालनहार है, जिसने उन्हें पैदा किया है और मैं तो इसीका साक्षी हूँ।');
INSERT INTO "al_umari" VALUES(21,57,'21:57','तथा अल्लाह की शपथ! मैं अवश्य चाल चलूँगा तुम्हारी मूर्तियों के साथ, इसके पश्चात् कि तुम चले जाओ।');
INSERT INTO "al_umari" VALUES(21,58,'21:58','फिर उसने कर दिया उन्हें खण्ड-खण्ड, उनके बड़े के सिवा, ताकि वे उसकी ओर फिरें।');
INSERT INTO "al_umari" VALUES(21,59,'21:59','उन्होंने कहाः किसने ये दशा कर दी है, हमारे पूज्यों ( देवताओं) की? वास्तव में, वह कोई अत्याचारी होगा!');
INSERT INTO "al_umari" VALUES(21,60,'21:60','लोगों ने कहाः हमने सुना है एक नवयुवक को उनकी चर्चा करते, जिसे इब्राहीम कहा जाता है।');
INSERT INTO "al_umari" VALUES(21,61,'21:61','लोगों ने कहाः उसे लाओ लोगों के सामने, ताकि लोग देखें।');
INSERT INTO "al_umari" VALUES(21,62,'21:62','उन्होंने पूछाः क्या तूने ही ये किया है, हमारे पूज्यों के साथ, हे इब्राहीम?');
INSERT INTO "al_umari" VALUES(21,63,'21:63','उसने कहाः बल्कि इसे इनके इस बड़े ने किया[ है, तो इन्हीं से पूछ लो, यदि ये बोलते हों? ');
INSERT INTO "al_umari" VALUES(21,64,'21:64','फिर अपने मन में वे सोच में पड़ गये और (अपने मन में) कहाः वास्तव में, तुम्हीं अत्याचारी हो।');
INSERT INTO "al_umari" VALUES(21,65,'21:65','फिर वह ओंधे कर दिये गये अपने सिरों के बल[ ( और बोलेः) तू जानता है कि ये बोलते नहीं हैं। ');
INSERT INTO "al_umari" VALUES(21,66,'21:66','इब्राहीम ने कहाः तो क्या तुम इबादत (वंदना) अल्लाह के सिवा उसकी करते हो, जो न तुम्हें कुछ लाभ पहुँचा सकते हैं और न तुम्हें हानि पहूँचा सकते हैं?');
INSERT INTO "al_umari" VALUES(21,67,'21:67','तुफ़ (थू) है तुमपर और उसपर जिसकी तुम इबादत (वंदना) करते हो अल्लाह को छोड़कर। तो क्या तुम समझ नहीं रखते हो?');
INSERT INTO "al_umari" VALUES(21,68,'21:68','उन्होंने कहाः इसे जला दो तथा सहायता करो अपने पूज्यों की, यदि तुम्हें कुछ करना है।');
INSERT INTO "al_umari" VALUES(21,69,'21:69','हमने कहाः हे अग्नि! तू शीतल तथा शान्ति बन जा, इब्राहीम पर।');
INSERT INTO "al_umari" VALUES(21,70,'21:70','और उन्होंने उसके साथ बुराई चाही, तो हमने उन्हीं को क्षतिग्रस्त कर दिया।');
INSERT INTO "al_umari" VALUES(21,71,'21:71','और हम, उस (इब्राहीम) को बचाकर ले गये तथा लूत[ को, उस भूमि की ओर, जिसमें हमने सम्पन्नता रखी है, विश्व वासियों के लिए।');
INSERT INTO "al_umari" VALUES(21,72,'21:72','और हमने उसे प्रदान किया (पुत्र) इस्ह़ाक़ और (पौत्र) याक़ूब उसपर अधिक और प्रत्येक को हमने सत्कर्मी बनाया।');
INSERT INTO "al_umari" VALUES(21,73,'21:73','और हमने उन्हें अग्रणी (प्रमुख) बना दिया, जो हमारे आदेशानुसार (लोगों को) सुपथ दर्शाते हैं तथा हमने वह़्यी (प्रकाशना) की, उनकी ओर सत्कर्मों के करने, नमाज़ की स्थापना करने और ज़कात देने की तथा वे हमारे ही उपासक थे।');
INSERT INTO "al_umari" VALUES(21,74,'21:74','तथा लूत को हमने निर्णय शक्ति और ज्ञान दिया और बचा लिया उस बस्ती से, जो दुष्कर्म कर रही थी, वास्तव में, वे बुरे अवज्ञाकारी लोग थे।');
INSERT INTO "al_umari" VALUES(21,75,'21:75','और हमने प्रवेश दिया उसे अपनी दया में, वास्तव में, वह सदाचारियों में से था।');
INSERT INTO "al_umari" VALUES(21,76,'21:76','तथा नूह़ को (याद करो) जब उसने पुकारा इन (नबियों) से पहले। तो हमने उसकी पुकार सुन ली, फिर उसे और उसके घराने को मुक्ति दी महा पीड़ा से।');
INSERT INTO "al_umari" VALUES(21,77,'21:77','और उसकी सहायता की, उस जाति के मुक़ाबले में, जिन्होंने हमारी आयतों (निशानियों) को झुठला दिया, वास्तव में, वे बुरे लोग थे। अतः हमने डुबो दिया उन सभी को।');
INSERT INTO "al_umari" VALUES(21,78,'21:78','तथा दावूद और सुलैमान को (याद करो) जब वे दोनों निर्णय कर रहे थे, खेत के विषय में, जब रात्रि में चर गईं उसे दूसरों की बकरियाँ और हम उनका निर्णय देख रहे थे।');
INSERT INTO "al_umari" VALUES(21,79,'21:79','तो हमने उसका उचित निर्णय समझा दिया सुलैमान[ को और प्रत्येक को हमने प्रदान किया था निर्णय शक्ति तथा ज्ञान और हमने अधीन कर दिया था दावूद के साथ पर्वतों को, जो (अल्लाह की पवित्रता का) वर्णन करते थे तथा पक्षियों को और हम ही इसकार्य के करने वाले थे। ');
INSERT INTO "al_umari" VALUES(21,80,'21:80','तथा हमने उसे (दावूद को) सिखाया तुम्हारे लिए कवच बनाना, ताकि तुम्हें बचाये तुम्हारे आक्रमण से, तो क्या तुम कृतज्ञ हो?');
INSERT INTO "al_umari" VALUES(21,81,'21:81','और सुलैमान के अधीन कर दिया उग्र वायु को, जो चल रही थी उसके आदेश से,[ उस धरती की ओर जिसमें हमने सम्पन्नता (विभूतियाँ) रखी है और हम ही सर्वज्ञ हैं। ');
INSERT INTO "al_umari" VALUES(21,82,'21:82','तथा शैतानों में से उन्हें (उसके अधीन कर दिया) जो उसके लिए डुबकी लगाते[ तथा इसके सिवा दूसरे कार्य करते थे और हम ही उनके निरीक्षक हैं।');
INSERT INTO "al_umari" VALUES(21,83,'21:83','तथा अय्यूब (की उस स्थिति) को (याद करो), जब उसने पुकारा अपने पालनहार को कि मुझे रोग लग गया है और तू सबसे अधिक दयावान् है।');
INSERT INTO "al_umari" VALUES(21,84,'21:84','तो हमने उसकी गुहार सुन ली[ और दूर कर दिया, जो दुःख उसे था और प्रदान कर दिया उसे उसका परिवार तथा उतने ही और उनके साथ, अपनी विशेष दया से तथा शिक्षा के लिए उपासकों की। ');
INSERT INTO "al_umari" VALUES(21,85,'21:85','तथा इस्माईल, इद्रीस तथा ज़ुल किफ़्ल को (याद करो), सभी सहनशीलों में से थे।');
INSERT INTO "al_umari" VALUES(21,86,'21:86','और हमने प्रवेश दिया उनको अपनी दया में, वास्तव में, वे सदाचारी थे।');
INSERT INTO "al_umari" VALUES(21,87,'21:87','तथा ज़ुन्नून[ को, जब वे चला गया क्रोधित होकर और सोचा कि हम उसे पकड़ेंगे नहीं, अन्ततः, उसने पुकारा अन्धेरे में कि नहीं है कोई पूज्य तेरे सिवा, तू पवित्र है, वास्तव में, मैं ही दोषी हूँ।');
INSERT INTO "al_umari" VALUES(21,88,'21:88','तब हमने उसकी पुकार सुन ली तथा मुक्त कर दिया शोक से और इसी प्रकार, हम बचा लिया करते हैं, ईमान वालों को।');
INSERT INTO "al_umari" VALUES(21,89,'21:89','तथा ज़करिय्या को (याद करो), जब पुकारा उसने अपने पालनहार[ को, हे मेरे पालनहार! मुझे मत छोड़ दे अकेला और तू सबसे अच्छा उत्तराधिकारी है। ');
INSERT INTO "al_umari" VALUES(21,90,'21:90','तो हमने सुन ली उसकी पुकार तथा प्रदान कर दिया उसे यह़्या और सुधार दिया उसके लिए उसकी पत्नी को। वास्तव में, वे सभी दौड़-धूप करते थे सत्कर्मों में और हमसे प्रार्थना करते थे रूचि तथा भय के साथ और हमारे आगे झुके हुए थे।');
INSERT INTO "al_umari" VALUES(21,91,'21:91','तथा जिसने रक्षा की अपनी सतीत्व[ की, तो फूँक दी हमने उसके भीतर अपनी आत्मा से और उसे तथा उसके पुत्र को बना दिया एक निशानी संसार वासियों के लिए। ');
INSERT INTO "al_umari" VALUES(21,92,'21:92','वास्तव में, तुम्हारा धर्म एक ही धर्म[ है और मैं ही तुम सबका पालनहार (पूज्य) हूँ। अतः, मेरी ही इबादत (वंदना) करो। ');
INSERT INTO "al_umari" VALUES(21,93,'21:93','और खण्ड-खण्ड कर दिया लोगों ने अपने धर्म को (विभेद करके) आपस में, सबको हमारी ओर ही फिर आना है।');
INSERT INTO "al_umari" VALUES(21,94,'21:94','फिर जो सदाचार करेगा और वह एकेश्वरवादी हो, तो उसके प्रयास की उपेक्षा नहीं की जायेगी और हम उसे लिख रहे हैं।');
INSERT INTO "al_umari" VALUES(21,95,'21:95','और असंभव है किसी भी बस्ती पर, जिसका हमने विनाश कर[ दिया कि वह फिर (संसार में) आ जाये। ');
INSERT INTO "al_umari" VALUES(21,96,'21:96','यहाँ तक कि जब खोल दिये जायेंगे याजूज तथा माजूज[ और वे प्रत्येक ऊँचाई से उतर रहे होंगे। ');
INSERT INTO "al_umari" VALUES(21,97,'21:97','और समीप आ जायेगा सत्य[ वचन, तो अकस्मात खुली रह जायेँगी काफ़िरों की आँखें, ( वे कहेंगेः) "हाय हमारा विनाश!" हम असावधान रह गये इससे, बल्कि हम अत्याचारी थे। ');
INSERT INTO "al_umari" VALUES(21,98,'21:98','निश्चय तुमसब तथा तुम जिन (मूर्तियों) को पूज रहे हो अल्लाह के अतिरिक्त, नरक के ईंधन हैं, तुमसब वहाँ पहुँचने वाले हो।');
INSERT INTO "al_umari" VALUES(21,99,'21:99','यदि वे वास्तव में पूज्य होते, तो नरक में प्रवेश नहीं करते और प्रत्येक उसमें सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(21,100,'21:100','उनकी उसमें चीखें होंगी तथा वे उसमें (कुछ) सुन नहीं सकेंगे।');
INSERT INTO "al_umari" VALUES(21,101,'21:101','(परन्तु) जिनके लिए पहले ही से हमारी ओर से भलाई का निर्णय हो चुका है, वही उससे दूर रखे जायेंगे।');
INSERT INTO "al_umari" VALUES(21,102,'21:102','वे उस (नरक) की सरसर भी नहीं सुनेंगे और अपनी मनचाही चीज़ों में सदा (मगन) रहेंगे।');
INSERT INTO "al_umari" VALUES(21,103,'21:103','उन्हें उदासीन नहीं करेगी (प्रलय के दिन की) बड़ी व्यग्रता तथा फ़रिश्ते उन्हें हाथों-हाथ ले लेंगे, (तथा कहेंगेः) यही तुम्हारा वह दिन है, जिसका तुम्हें वचन दिया जा रहा था।');
INSERT INTO "al_umari" VALUES(21,104,'21:104','जिस दिन हम लपेट[ देंगे आकाश को, पंजिका के पन्नों को लपेट देने के समान, जैसे हमने आरंभ किया था प्रथम उत्पत्ति का, उसी प्रकार, उसे दुहरायेंगे, इस (वचन) को पूरा करना हमपर है और हम पूरा करके रहेंगे।');
INSERT INTO "al_umari" VALUES(21,105,'21:105','तथा हमने लिख दिया है ज़बूर[ में शिक्षा के पश्चात् कि धरती के उत्तराधिकारी मेरे सदाचारी भक्त होंगे। ');
INSERT INTO "al_umari" VALUES(21,106,'21:106','वस्तुतः, इस (बात) में एक बड़ा उपदेश है उपासकों के लिए।');
INSERT INTO "al_umari" VALUES(21,107,'21:107','और (हे नबी!) हमने आपको नहीं भेजा है, किन्तु समस्त संसार के लिए दया बना[ कर। ');
INSERT INTO "al_umari" VALUES(21,108,'21:108','आप कह दें कि मेरी ओर तो बस यही वह़्यी की जा रही है कि तुम सबका पूज्य बस एक ही पूज्य है, फिर क्या तुम उसके आज्ञाकारी[ हो? ');
INSERT INTO "al_umari" VALUES(21,109,'21:109','फिर यदि वे विमुख हों, तो आप कह दें कि मैंने तुम्हें समान रूप से सावधान कर दिया[ और मैं नहीं जानता कि समीप है अथवा दूर जिसका वचन तुम्हें दिया जा रहा है। ');
INSERT INTO "al_umari" VALUES(21,110,'21:110','वास्तव में, वही जानता है खुली बात को तथा जानता है जो कुछ तुम छुपाते हो।');
INSERT INTO "al_umari" VALUES(21,111,'21:111','तथा मुझे ये ज्ञान (भी) नहीं, संभव है ये[ तुम्हारे लिए कोई परीक्षा हो तथा लाभ हो एक निर्धारित समय तक? ');
INSERT INTO "al_umari" VALUES(21,112,'21:112','उस (नबी) ने प्रार्थना कीः हे मेरे पालनहार! सत्य के साथ निर्णय कर दे और हमारा पालनहार अत्यंत कृपाशील है, जिससे सहायता माँगी जाये उन बातों पर, जो तुम लोग बना रहे हो।');
INSERT INTO "al_umari" VALUES(22,1,'22:1','हे मनुष्यो! अपने पालनहार से डरो, वास्तव में, क़्यामत (प्रलय) का भूकम्प बड़ा ही घोर विषय है।');
INSERT INTO "al_umari" VALUES(22,2,'22:2','जिस दिन, तुम उसे देखोगे, सुध न होगी प्रत्येक दूध पिलाने वाली को अपने दूध पीते शिशु की और गिरा देगी प्रत्येक गर्भवती अपना गर्भ तथा तुम देखोगे लोगों को मतवाले, जबकि वे मतवाले नहीं होंगे, परन्तु अल्लाह की यातना बहुत कड़ी[ होगी। ');
INSERT INTO "al_umari" VALUES(22,3,'22:3','और कुछ लोग विवाद करते हैं, अल्लाह के विषय में, बिना किसी ज्ञान के तथा अनुसरण करते हैं प्रत्येक उध्दत शैतान का।');
INSERT INTO "al_umari" VALUES(22,4,'22:4','जिसके भाग में लिख दिया गया है कि जो उसे मित्र बनायेगा, वह उसे कुपथ कर देगा और उसे राह दिखायेगा नरक की यातना की ओर।');
INSERT INTO "al_umari" VALUES(22,5,'22:5','हे लोगो! यदि तुम किसी संदेह में हो, पुनः जीवित होने के विषय में, तो (सोचो कि) हमने तुम्हें मिट्टी से पैदा किया, फिर वीर्य से, फिर रक्त के थक्के से, फिर मांस के खण्ड से, जो चित्रित तथा चित्र विहीन होता है[, ताकि हम उजागर कर दें तुम्हारे लिए और स्थिर रखते हैं गर्भाशयों में जब तक चाहें; एक निर्धारित अवधि तक, फिर तुम्हें निकालते हैं शिशु बनाकर, फिर ताकि तुम पहुँचों अपने योवन को और तुममें से कुछ, पहले ही मर जाते हैं और तुममें से कुछ, जीर्ण आयु की ओर फेर दिये जाते हैं ताकि उसे कुछ ज्ञान न रह जाये, ज्ञान के पश्चात् तथा तुम देखते हो धरती को सूखी, फिर जब, हम उसपर जल-वर्षा करते हैं, तो सहसा लहलहाने और उभरने लगी तथा उगा देती है प्रत्येक प्रकार की सुदृश्य वनस्पतियाँ।');
INSERT INTO "al_umari" VALUES(22,6,'22:6','ये इसलिए है कि अल्लाह ही सत्य है तथा वही जीवित करता है मुर्दों को तथा वास्तव में, वह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(22,7,'22:7','ये इस कारण है कि क़्यामत (प्रलय) अवश्य आनी है, जिसमें कोई संदेग नहीं और अल्लाह ही उन्हें पुनः जीवित करेगा, जो समाधियों (क़ब्रों) में हैं।');
INSERT INTO "al_umari" VALUES(22,8,'22:8','तथा लोगों में वह (भी) है, जो विवाद करता है अल्लाह के विषय में बिना किसी ज्ञान और मार्गदर्शन एवं बिना किसी ज्योतिमय पुस्तक के।');
INSERT INTO "al_umari" VALUES(22,9,'22:9','अपना पहलू फेरकर ताकि अल्लाह की राह[ से कुपथ कर दे। उसी के लिए संसार में अपमान है और हम उसे प्रलय के दिन दहन की यातना चखायेंगे। ');
INSERT INTO "al_umari" VALUES(22,10,'22:10','ये उन कर्मों का परिणाम है, जिन्हें तेरे हाथों ने आगे भेजा है और अल्लाह अत्याचारी नहीं है (अपने) भक्तों के लिए।');
INSERT INTO "al_umari" VALUES(22,11,'22:11','तथा लोगों में वह (भी) है जो इबादत (वंदना) करता है अल्लाह की, एक किनारे पर होकर[ , फिर यदि उसे कोई लाभ पहुँचता है, तो वह संतोष हो जाता है और यदि उसे कोई परीक्षा आ लगे, तो मुँह के बल फिर जाता है। वह क्षति में पड़ गया लोक तथा परलोक की और यही खुली क्षति है। ');
INSERT INTO "al_umari" VALUES(22,12,'22:12','वह पुकारता है अल्लाह के अतिरिक्त उसे, जो न हानि पहुँचा सके उसे और न लाभ, यही दूर[ का कुपथ है। ');
INSERT INTO "al_umari" VALUES(22,13,'22:13','वह उसे पुकारता है, जिसकी हानि अधिक समीप है उसके लाभ से, वास्तव में, वह बुरा संरक्षक तथा बुरा साथी है।');
INSERT INTO "al_umari" VALUES(22,14,'22:14','निश्चय अल्लाह उन्हें प्रवेश देगा, जो ईमान लाये तथा सत्कर्म किये, ऐसे स्वर्गों में, जिनमें नहरें प्रवाहित हैं। वास्तव में, अल्लाह करता है, जो चाहता है।');
INSERT INTO "al_umari" VALUES(22,15,'22:15','जो सोचता है कि उस[ की सहायता नहीं करेगा अल्लाह लोक तथा प्रलोक में, तो उसे चाहिए कि तान ले कोई रस्सी आकाश की ओर, फिर फाँसी देकर मर जाये। फिर देखे कि क्या दूर कर देती है उसका उपाय, उसके रोष (क्रोध) को?');
INSERT INTO "al_umari" VALUES(22,16,'22:16','तथा इसी प्रकार हमने इस (क़ुर्आन) को खुली आयतों में अवतरित किया है और अल्लाह सुपथ दर्शा देता है, जिसे चाहता है।');
INSERT INTO "al_umari" VALUES(22,17,'22:17','जो ईमान लाये, जो यहूदी हुए, जो साबी तथा ईसाई हैं, जो मजूसी हैं तथा जिन्होंने शिर्क किया है, अल्लाह निर्णय[ कर देगा उनके बीच प्रलय के दिन। निश्चय अल्लाह प्रत्येक वस्तु पर साक्षी है। ');
INSERT INTO "al_umari" VALUES(22,18,'22:18','(हे नबी!) क्या आप नहीं जानते कि अल्लाह ही को सज्दा[ करते हैं, जो आकाशों तथा धरती में हैं, सूर्य और चाँद, तारे और पर्वत, वृक्ष और पशु, बहुत-से मनुष्य और बहुत-से वे भी हैं, जिनपर यातना सिध्द हो चुकी है। और जिसे अल्लाह अपमानित कर दे, उसे कोई सम्मान देने वाला नहीं है। निःसंदेह अल्लाह करता है, जो चाहता है। ');
INSERT INTO "al_umari" VALUES(22,19,'22:19','ये दो पक्ष हैं, जिन्होंने विभेद किया[ अपने पालनहार के विषय में, तो इनमें से काफ़िरों के लिए ब्योंत दिये गये हैं अग्नि के वस्त्र, उनके सिरों पर धारा बहायी जायेगी खोलते हुए पानी की। ');
INSERT INTO "al_umari" VALUES(22,20,'22:20','जिससे गला दी जायेँगी उनके पेटों के भीतर की वस्तुएँ और उनकी खालें।');
INSERT INTO "al_umari" VALUES(22,21,'22:21','और उन्हीं के लिए लोहे के आँकुश हैं।');
INSERT INTO "al_umari" VALUES(22,22,'22:22','जबभी उस (अग्नि) से निकलना चाहेंगे व्याकूल होकर, तो उसीमें फेर दिये जायेंगे तथा (कहा जायेगा कि) दहन की यातना चखो।');
INSERT INTO "al_umari" VALUES(22,23,'22:23','निश्चय अल्लाह प्रवेश देगा उन्हें, जो ईमान लाये तथा सत्कर्म किये, ऐसे स्वर्गों में, जिनमें नहरें प्रवाहित होंगी, उनमें उन्हें सोने के कंगन पहनाये जायेंगे तथा मोती और उनका वस्त्र उसमें रेशम का होगा।');
INSERT INTO "al_umari" VALUES(22,24,'22:24','तथा उन्हें मार्ग दर्शा दिया गया पवित्र बात[ का और उन्हें दर्शा दिया गया प्रशंसित (अल्लाह) का मार्ग।');
INSERT INTO "al_umari" VALUES(22,25,'22:25','जो काफ़िर हो गये[ और रोकते हैं अल्लाह की राह से और उस मस्जिदे ह़राम से, जिसे सबके लिए हमने एक जैसा बना दिया है; उसके वासी हों अथवा प्रवासी तथा जो उसमें अत्याचार से अधर्म का विचार करेगा, हम उसे दुःखदायी यातना चखायेंगे ।');
INSERT INTO "al_umari" VALUES(22,26,'22:26','तथा वह समय याद करो, जब हमने निश्चित कर दिया इब्राहीम के लिए इस घर (काबा) का स्थान[ (इस प्रतिबंध के साथ) कि साझी न बनाना मेरा किसी चीज़ को तथा पवित्र रखना मेरे घर को परिकर्मा करने, खड़े होने, रुकूअ (झुकने) और सज्दा करने वालों के लिए। ');
INSERT INTO "al_umari" VALUES(22,27,'22:27','और घोषणा कर दो लोगों में ह़ज की, वे आयेंगे तेरे पास पैदल तथा प्रत्येक दुबली-पतली स्वारियों पर, जो प्रत्येक दूरस्थ मार्ग से आयेंगी।');
INSERT INTO "al_umari" VALUES(22,28,'22:28','ताकि वह उपस्थित हों अपने लाभ प्राप्त करने के लिए और ताकि अल्लाह का नाम[ लें निश्चित दिनों में, उसपर, जो उन्हें प्रदान किया है पालतू चौपायों में से। फिर उसमें से स्वयं खाओ तथा भूखे निर्धन को खिलाओ।');
INSERT INTO "al_umari" VALUES(22,29,'22:29','फिर अपना मैल-कुचैल दूर[ करें तथा अपनी मनौतियाँ पूरी करें और परिकर्मा करें प्राचीन घर की।');
INSERT INTO "al_umari" VALUES(22,30,'22:30','ये है (आदेश) और जो अल्लाह के निर्धारित किये प्रतिबंधों का आदर करे, तो ये उसके लिए अच्छा है, उसके पालनहार के पास और ह़लाल (वैध) कर दिये गये तुम्हारे लिए चौपाये, उनके सिवा जिनका वर्णन तुम्हारे समक्ष कर दिया[ गया है, अतः मूर्तियों की गन्दगी से बचो तथा झूठ बोलने से बचो। ');
INSERT INTO "al_umari" VALUES(22,31,'22:31','अल्लाह के लिए एकेश्वरवादी होतो हुए, उसका साझी न बनाते हुए और जो साझी बनाता हो अल्लाह का, तो मानो वह आकाश से गिर गया, फिर उसे पक्षी उचक ले जाये अथवा वायु का झोंका किसी दूर स्थान में फेंक[ दे। ');
INSERT INTO "al_umari" VALUES(22,32,'22:32','ये (अल्लाह का आदेश है), और जो आदर करे अल्लाह के प्रतीकों (निशानों)[ का, तो ये निःसंदेह दिलों के आज्ञाकारी होने की बात है। ');
INSERT INTO "al_umari" VALUES(22,33,'22:33','तुम्हारे लिए उनमें बहुत-से लाभ[ हैं, एक निर्धारित समय तक, फिर उनके वध करने का स्थान प्राचीन घर के पास है। ');
INSERT INTO "al_umari" VALUES(22,34,'22:34','तथा प्रत्येक समुदाय के लिए हमने क़ुर्बानी की विधि निर्धारित की है, ताकि वे अल्लाह का नाम लें उसपर, जो प्रदान किये हैं उन्हें पालतू चौपायों में से। अतः, तुम्हारा पूज्य एक ही पूज्य है, उसी के आज्ञाकारी रहो और (हे नबी!) आप शुभ सूचना सुना दें विनीतों को।');
INSERT INTO "al_umari" VALUES(22,35,'22:35','जिनकी दशा ये है कि जब अल्लाह की चर्चा की जाये, तो उनके दिल डर जाते हैं तथा धैर्य रखते हैं उस विपदा पर, जो उन्हें पहुँचे और नमाज़ की स्थापना करने वाले हैं तथा उसमें से जो हमने उन्हें दिया है, दान करते हैं।');
INSERT INTO "al_umari" VALUES(22,36,'22:36','और ऊँटों को हमने बनाया है तुम्हारे लिए अल्लाह की निशानियों में, तुम्हारे लिए उनमें भलाई है। अतः अल्लाह का नाम लो उनपर (वध करते समय) खड़े करके और जब धरती से लग जायें[ उनके पहलू, तो स्वयं खाओ उनमें से और खिलाओ उनमें से संतोषी तथा भिक्षु को, इसी प्रकार, हमने उसे वश में कर दिया है तुम्हारे, ताकि तुम कृतज्ञ बनो। ');
INSERT INTO "al_umari" VALUES(22,37,'22:37','नहीं पहुँचते अल्लाह को उनके माँस और न उनके रक्त, परन्तु उसे पहुँचता है तुम्हारा आज्ञा पालन। इसी प्रकार, उस (अल्लाह) ने उन (पशुओं) को तुम्हारे वश में कर दिया है, ताकि तुम अल्लाह की महिमा का वर्णन करो,[ उस मार्गदर्शन पर जो तुम्हें दिया है और आप सत्कर्मियों को शुभ सूचना सुना दें। ');
INSERT INTO "al_umari" VALUES(22,38,'22:38','निश्चय ही अल्लाह प्रतिरक्षा करता है उनकी ओर से, जो ईमान लाये हैं, वास्तव में अल्लाह किसी विश्वासघाती, कृतघ्न से प्रेम नहीं करता।');
INSERT INTO "al_umari" VALUES(22,39,'22:39','उन्हें अनुमति दे दी गयी, जिनसे युध्द किया जा रहा है, क्योंकि उनपर अत्याचार किया गया है और निश्चय अल्लाह उनकी सहायता पर पूर्णतः सामर्थ्यवान है[। ');
INSERT INTO "al_umari" VALUES(22,40,'22:40','जिन्हें उनके घरों से अकारण निकाल दिया गया, केवल इस बात पर कि वे कहते थे कि हमारा पालनहार अल्लाह है और यदि अल्लाह प्रतिरक्षा न कराता कुछ लोगों की, कुछ लोगों द्वारा, तो ध्वस्त कर दिये जाते आश्रम तथा गिरजे और यहूदियों के धर्म स्थल तथा मस्जिदें, जिनमें अल्लाह का नाम अधिक लिया जाता है और अल्लाह अवश्य उसकी सहायता करेगा, जो उस (के सत्य) की सहायता करेगा, वास्तव में, अल्लाह अति शक्तिशाली, प्रभुत्वशाली है।');
INSERT INTO "al_umari" VALUES(22,41,'22:41','ये[ वो लोग हैं कि यदि हम इन्हें धरती में अधिपत्य प्रदान कर दें, तो नमाज़ की स्थापना करेंगे, ज़कात देंगे, भलाई का आदेश देंगे, बुराई से रोकेंगे और अल्लाह के अधिकार में है सब कर्मों का परिणाम। ');
INSERT INTO "al_umari" VALUES(22,42,'22:42','और (हे नबी!) यदि वे आपको झुठलायें, तो इनसे पूर्व झुठला चुकी है नूह़ की जाति और (आद) तथा (समूद)');
INSERT INTO "al_umari" VALUES(22,43,'22:43','तथा इब्राहीम की जाति और लूत की (जाति)।');
INSERT INTO "al_umari" VALUES(22,44,'22:44','तथा मद्यन वाले[ और मूसा (भी) झुठलाये गये, तो मैंने अवसर दिया काफ़िरों को, फिर उन्हें पकड़ लिया, तो मेरा दण्ड कैसा रहा? ');
INSERT INTO "al_umari" VALUES(22,45,'22:45','तो कितनी ही बस्तियाँ हैं, जिन्हें हमने ध्वस्त कर दिया, जो अत्याचारी थीं, वे अपनी छतों के समेत गिरी हुई हैं और बेकार कुएं तथा पक्के ऊँचे भवन।');
INSERT INTO "al_umari" VALUES(22,46,'22:46','तो क्या वे धरती में फिरे नहीं? तो उनके ऐसे दिल होते, जिनसे समझते अथवा ऐसे कान होते, जिनसे सुनते, वास्तव में, आँखें अन्धी नहीं हो जातीं, परन्तु वो दिल अन्धे हो जाते हैं, जो सीनों में[ हैं। ');
INSERT INTO "al_umari" VALUES(22,47,'22:47','तथा वे आपसे शीघ्र यातना की माँग कर रहे हैं और अल्लाह कदापि अपना वचन भंग नहीं करेगा और निश्चय आपके पालनहार के यहाँ एक दिन तुम्हारी गणना से हज़ार वर्ष के बराबर[ है। ');
INSERT INTO "al_umari" VALUES(22,48,'22:48','और बहुत-सी बस्तियाँ हैं, जिन्हें हमने अवसर दिया, जबकि वो अत्याचारी थीं, फिर मैंने उन्हें पकड़ लिया और मेरी ही ओर (सबको) वापस आना है।');
INSERT INTO "al_umari" VALUES(22,49,'22:49','(हे नबी!) आप कह दें कि हे लोगो! मैंतो बस तुम्हें खुला सावधान करने वाला हूँ।');
INSERT INTO "al_umari" VALUES(22,50,'22:50','तो जो ईमान लाये तथा सदाचार किये, उन्हीं के लिए क्षमा और सम्मानित जीविका है।');
INSERT INTO "al_umari" VALUES(22,51,'22:51','और जिन्होंने प्रयास किया हमारी आयतों में विवश करने का, तो वही नारकी हैं।');
INSERT INTO "al_umari" VALUES(22,52,'22:52','और (हे नबी!) हमने नहीं भेजा आपसे पूर्व किसी रसूल और न किसी नबी को, किन्तु जब, उसने (पुस्तक) पढ़ी, तो संशय डाल दिया शैतान ने उसके पढ़ने में। फिर निरस्त कर देता है अल्लाह शैतान के संशय को, फिर सुदृढ़ कर देता है अल्लाह अपनी आयतों को और अल्लाह सर्वज्ञ, तत्वज्ञ[ है। ');
INSERT INTO "al_umari" VALUES(22,53,'22:53','ये इसलिए, ताकि अल्लाह शैतानी संशय को उनके लिए परीक्षा बना दे, जिनके दिलों में रोग (द्विधा) है और जिनके दिल कड़े हैं और वास्तव में, अत्याचारी विरोध में बहुत दूर चले गये हैं।');
INSERT INTO "al_umari" VALUES(22,54,'22:54','और इसलिए (भी) ताकि विश्वास हो जाये उन्हें, जो ज्ञान दिये गये हैं कि ये (क़ुर्आन) सत्य है आपके पालनहार की ओर से और इसपर ईमान लायें और इसके लिए झुक जायें उनके दिल, और निःसंदेह अल्लाह ही पथ प्रदर्शक है उनका, जो ईमान लायें सुपथ की ओर।');
INSERT INTO "al_umari" VALUES(22,55,'22:55','तथा जो काफ़िर हो गये, तो वे सदा संदेह में रहेंगे इस (क़ुर्आन) से, यहाँतक कि उनके पास सहसा प्रलय आ जाये अथवा उनके पास बाँझ[ दिन की यातना आ जाये। ');
INSERT INTO "al_umari" VALUES(22,56,'22:56','राज्य उस दिन अल्लाह ही का होगा, वही उनके बीच निर्णय करेगा, तो जो ईमान लाये और सदाचार किये, तो वे सुख के स्वर्गों में होंगे।');
INSERT INTO "al_umari" VALUES(22,57,'22:57','और जो काफ़िर हो गये और हमारी आयतों को झुठलाया, उन्हीं के लिए अपमानकारी यातना है।');
INSERT INTO "al_umari" VALUES(22,58,'22:58','तथा जिन लोगों ने हिजरत (प्रस्थान) की अल्लाह की राह में, फिर मारे गये अथवा मर गये, तो उन्हें अल्लाह अवश्य उत्तम जीविका प्रदान करेगा और वास्तव में, अल्लाह ही सर्वोत्तम जीविका प्रदान करने वाला है।');
INSERT INTO "al_umari" VALUES(22,59,'22:59','वह उन्हें प्रवेश देगा, ऐसे स्थान में, जिससे वे प्रसन्न हो जायेंगे और वास्तव में अल्लाह सर्वज्ञ, सहनशील है।');
INSERT INTO "al_umari" VALUES(22,60,'22:60','ये वास्तविक्ता है और जिसने बदला लिया वैसा ही, जो उसके साथ किया गया, फिर उसके साथ अत्याचार किया जाये, तो अल्लाह उसकी अवश्य सहायता करेगा, वास्तव में, अल्लाह अति क्षान्त, क्षमाशील है।');
INSERT INTO "al_umari" VALUES(22,61,'22:61','ये इसलिए कि अल्लाह प्रवेश देता है, रात्रि को दिन में और प्रवेश देता है, दिन को रात्रि में और अल्लाह सब कुछ सुनने-देखने वाला[ है। ');
INSERT INTO "al_umari" VALUES(22,62,'22:62','ये इसलिए कि अल्लाह ही सत्य है और जिसे वे अल्लाह के सिवा पुकारते हैं, वही असत्य है और अल्लाह ही सर्वोच्च, महान है।');
INSERT INTO "al_umari" VALUES(22,63,'22:63','क्या आपने नहीं देखा कि अल्लाह अकाश से जल बरसाता है, तो भूमि हरी हो जाती है, वास्तव में, अल्लाह सुक्ष्मदर्शी, सर्वसूचित है।');
INSERT INTO "al_umari" VALUES(22,64,'22:64','उसी का है, जो आकाशों तथा जो धरती में है और वास्तव में, अल्लाह ही निस्पृह, प्रशंसित है।');
INSERT INTO "al_umari" VALUES(22,65,'22:65','क्या आपने नहीं देखा कि अल्लाह ने वश में कर दिया[ है तुम्हारे, जो कुछ धरती में है तथा नाव को जो चलती है सागर में उसके आदेश से और रोकता है आकाश को धरती पर गिरने से, परन्तु उसकी अनुमति से? वास्तव में, अल्लाह लोगों के लिए अति करुणामय, दयान् है। ');
INSERT INTO "al_umari" VALUES(22,66,'22:66','तथा वही है, जिसने तुम्हें जीवित किया, फिर तुम्हें मारेगा, फिर तुम्हे जीवित करेगा, वास्तव में, मनुष्य बड़ा ही कृतघ्न है।');
INSERT INTO "al_umari" VALUES(22,67,'22:67','(हे नबी!) हमने प्रत्येक समुदाय के लिए (इबादत की) विधि निर्धारित कर दी थी, जिसका वे पालन करते रहे, अतः उन्हें आपसे इस (इस्लाम के नियम) के संबन्ध में विवाद नहीं करना चाहिए और आप अपने पालनहार की ओर लोगों को बुलाएँ, वास्तव में, आप सीधी राह पर हैं[। ');
INSERT INTO "al_umari" VALUES(22,68,'22:68','और यदि वे आपसे विवाद करें, तो कह दें कि अल्लाह तुम्हारे कर्मों से भली-भाँति अवगत है।');
INSERT INTO "al_umari" VALUES(22,69,'22:69','अल्लाह ही तुम्हारे बीच निर्णय करेगा क़्यामत (प्रलय) के दिन, जिसमें तुम विभेद कर रहे हो।');
INSERT INTO "al_umari" VALUES(22,70,'22:70','(हे नबी!) क्या आप नहीं जानते कि अल्लाह जानता है, जो आकाश तथा धरती में है, ये सब एक किताब में (अंकित) है। वास्तव में, ये अल्लाह के लिए अति सरल है।');
INSERT INTO "al_umari" VALUES(22,71,'22:71','और वे इबादत (वंदना) अल्लाह के अतिरिक्त उसकी कर रहे हैं, जिसका उसने कोई प्रमाण नहीं उतारा है और न उन्हें उसका कोई ज्ञान है और अत्याचारियों का कोई सहायक नहीं होगा।');
INSERT INTO "al_umari" VALUES(22,72,'22:72','और जब उन्हें सुनाई जाती हैं, हमारी खुली आयतें, तो आप पहचान लेते हैं, उनके चेहरों में, जो काफ़िर हो गये बिगाड़ को और लगता है कि वे आक्रमण कर देंगे उनपर, जो उन्हें हमारी आयतें सुनाते हैं। आप कह दें: क्या मैं तुम्हें इससे बुरी चीज़ बता दूँ? वह, अग्नि है, जिसका वचन अल्लाह ने काफ़िरों को दिया है और वह बहुत ही बुरा आवास है।');
INSERT INTO "al_umari" VALUES(22,73,'22:73','हे लोगो! एक उदाहरण दिया गया है, इसे ध्यान से सुनो, जिन्हें तुम अल्लाह के अतिरिक्त पुकारते हो, वे सब एक मक्खी नहीं पैदा कर सकते, यद्यपि सब इसके लिए मिल जायें और यदि उनसे मक्खी कुछ छीन ले, तो उससे वापस नहीं ला सकते। माँगने वाले निर्बल और जिनसे माँगा जाये, वे दोनों ही निर्बल हैं।');
INSERT INTO "al_umari" VALUES(22,74,'22:74','उन्होंने अल्लाह का आदर किया ही नहीं, जैसे उसका आदर करना चाहिये! वास्तव में, अल्लाह अति शक्तिशाली, प्रभुत्वशीली है।');
INSERT INTO "al_umari" VALUES(22,75,'22:75','अल्लाह ही निर्वाचित करता है फ़रिश्तों में से तथा मनुष्यों में से रसूलों को। वास्तव में, वह सुनने तथा देखने[ वाला है। ');
INSERT INTO "al_umari" VALUES(22,76,'22:76','वह जानता है, जो उनके सामने है और जो कुछ उनसे ओझल है और उसी की ओर सबकाम फेरे जाते हैं।');
INSERT INTO "al_umari" VALUES(22,77,'22:77','हे ईमान वालो! रुकूअ करो तथा सज्दा करो और अपने पालनहार की इबादत (वंदना) करो और भलाई करो, ताकि तुम सफल हो जाओ।');
INSERT INTO "al_umari" VALUES(22,78,'22:78','तथा अल्लाह के लिए जिहाद करो, जैसे जिहाद करना[ चाहिए। उसीने तुम्हें निर्वाचित किया है और नहीं बनाई तुमपर धर्म में कोई संकीर्णता (तंगी)। ये तुम्हारे पिता इब्राहीम का धर्म है, उसीने तुम्हारा नाम मुस्लिम रखा है, इस (क़ुर्आन) से पहले तथा इसमें भी। ताकि रसूल गवाह हूँ तुमपर और तुम गवाह बनो सब लोगों पर। अतः नमाज़ की स्थापना करो तथा ज़कात दो और अल्लाह को सुदृढ़ पकड़ लो। वही तुम्हारा संरक्षक है। तो वह क्या ही अच्छा संरक्षक तथा क्या ही अच्छा सहायक है।');
INSERT INTO "al_umari" VALUES(23,1,'23:1','सफल हो गये ईमान वाले।');
INSERT INTO "al_umari" VALUES(23,2,'23:2','जो अपनी नमाज़ों में विनीत रहने वाले हैं।');
INSERT INTO "al_umari" VALUES(23,3,'23:3','और जो व्यर्थ[ से विमुख रहने वाले हैं। ');
INSERT INTO "al_umari" VALUES(23,4,'23:4','तथा जो ज़कात देने वाले हैं।');
INSERT INTO "al_umari" VALUES(23,5,'23:5','और जो अपने गुप्तांगों की रक्षा करने वाले हैं।');
INSERT INTO "al_umari" VALUES(23,6,'23:6','परन्तु अपनी पत्नियों तथा अपने स्वामित्व में आयी दासियों से, तो वही निन्दित नहीं हैं।');
INSERT INTO "al_umari" VALUES(23,7,'23:7','फिर जो इसके अतिरिक्त चाहें, तो वही उल्लंघनकारी हैं।');
INSERT INTO "al_umari" VALUES(23,8,'23:8','और जो अपनी धरोहरों तथा वचन का पालन करने वाले हैं।');
INSERT INTO "al_umari" VALUES(23,9,'23:9','तथा जो अपनी नमाज़ों की रक्षा करने वाले हैं।');
INSERT INTO "al_umari" VALUES(23,10,'23:10','यही उत्तराधिकारी हैं।');
INSERT INTO "al_umari" VALUES(23,11,'23:11','जो उत्तराधिकारी होंगे फ़िर्दौस[ के, जिसमें वे सदावासी होंगे। ');
INSERT INTO "al_umari" VALUES(23,12,'23:12','और हमने उत्पन्न किया है मनुष्य को मिट्टी के सार[ से। ');
INSERT INTO "al_umari" VALUES(23,13,'23:13','फिर हमने उसे वीर्य बनाकर रख दिया एक सुरक्षित स्थान[ में। ');
INSERT INTO "al_umari" VALUES(23,14,'23:14','फिर बदल दिया वीर्य को जमे हुए रक्त में, फिर हमने उसे मांस का लोथड़ा बना दिया, फिर हमने लोथड़े में हड्डियाँ बनायीं, फिर हमने पहना दिया हड्डियों को मांस, फिर उसे एक अन्य रूप में उत्पन्न कर दिया। तो शुभ है अल्लाह, जो सबसे अच्छी उत्पत्ति करने वाला है।');
INSERT INTO "al_umari" VALUES(23,15,'23:15','फिर तुमसब इसके पश्चात् अवश्य मरने वाले हो।');
INSERT INTO "al_umari" VALUES(23,16,'23:16','फिर निश्चय तुमसब (प्रलय) के दिन जीवित किये जाओगे।');
INSERT INTO "al_umari" VALUES(23,17,'23:17','और हमने बना दिये तुम्हारे ऊपर सात आकाश और हम उत्पत्ति से अचेत नहीं[ हैं। ');
INSERT INTO "al_umari" VALUES(23,18,'23:18','और हमने आकाश से उचित मात्रा में पानी बरसाया और उसे धरती में रोक दिया तथा हम उसे विलुप्त कर देने पर निश्चय सामर्थ्यवान हैं।');
INSERT INTO "al_umari" VALUES(23,19,'23:19','फिर हमने उपजा दिये तुम्हारे लिए उस (पानी) के द्वारा खजूरों तथा अंगूरों के बाग़, तुम्हारे लिए उसमें बहुत-से फल हैं और उसीमें से तुम खाते हो।');
INSERT INTO "al_umari" VALUES(23,20,'23:20','तथा वृक्ष जो निकलता है सैना पर्वत से, जो तेल लिए उगता है तथा सालन है खाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(23,21,'23:21','और वास्तव में, तुम्हारे लिए पशुओं में एक शिक्षा है, हम तुम्हें पिलाते हैं, उसमें से, जो उनके पेटों में[ है तथा तुम्हारे लिए उनमें अन्य बहुत-से लाभ हैं और उनमें से कुछ को तुम खाते हो। ');
INSERT INTO "al_umari" VALUES(23,22,'23:22','तथा उनपर और नावों पर तुम सवार किये जाते हो।');
INSERT INTO "al_umari" VALUES(23,23,'23:23','तथा हमने भेजा नूह़[ को उसकी जाति की ओर, उसने कहाः हे मेरी जाति को लोगो! इबादत (वंदना) अल्लाह की करो, तुम्हारा कोई पूज्य नहीं है उसके सिवा, तो क्या तुम डरते नहीं हो? ');
INSERT INTO "al_umari" VALUES(23,24,'23:24','तो उन प्रमुखों ने कहा, जो काफ़िर हो गये उसकी जाति में से, ये तो एक मनुष्य है, तुम्हारे जैसा, ये तुमपर प्रधानता चाहता है और यदि अल्लाह चाहता, तो किसी फ़रिश्ते को उतारता, हमने तो इसे[ सुना ही नहीं अपने पूर्वजों में। ');
INSERT INTO "al_umari" VALUES(23,25,'23:25','ये बस एक ऐसा पुरुष है, जो पागल हो गया है, तो तुम उसकी प्रतीक्षा करो कुछ समय तक।');
INSERT INTO "al_umari" VALUES(23,26,'23:26','नूह़ ने कहाः हे मेरे पालनहार! मेरी सहायता कर, उनके मुझे झुठलाने पर।');
INSERT INTO "al_umari" VALUES(23,27,'23:27','तो हमने उसकी ओर वह़्यी की कि नाव बना हमारी रक्षा में हमारी वह्यी के अनुसार और जब हमारा आदेश आ जाये तथा तन्नूर उबल पड़े, तो रख ले प्रत्येक जीव के एक-एक जोड़े तथा अपने परिवार को, उसके सिवा जिसपर पहले निर्णय हो चुका है उनमें से, और मुझे संबोधित न करना उनके विषय में जिन्होंने अत्याचार किये हैं, निश्चय वे डुबो दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(23,28,'23:28','और जब स्थिर हो जाये तू और जो तेरे साथी हैं नाव पर, तो कहः सब प्रशंसा उस अल्लाह के लिए है, जिसने हमें मुक्त किया अत्याचारी लोगों से।');
INSERT INTO "al_umari" VALUES(23,29,'23:29','तथा कहः हे मेरे पालनहार! मुझे शुभ स्थान में उतार और तू उत्तम स्थान देने वाला है।');
INSERT INTO "al_umari" VALUES(23,30,'23:30','निश्चय इसमें कई निशानियाँ हैं तथा निःसंदेह हम परीक्ष लेने[ वाले हैं। ');
INSERT INTO "al_umari" VALUES(23,31,'23:31','फिर हमने पैदा किया उनके पश्चात् दूसरे समुदाय को।');
INSERT INTO "al_umari" VALUES(23,32,'23:32','फिर हमने भेजा उनमें रसूल उन्हीं में से कि तुम इबादत (वंदना) करो अल्लाह की, तुम्हारा कोई ( सच्चा) पूज्य नहीं है उसके सिवा, तो क्या तुम डरते नहीं हो?');
INSERT INTO "al_umari" VALUES(23,33,'23:33','और उसकी जाति के प्रमुखों ने कहा, जो काफ़िर हो गये तथा आख़िरत (परलोक) का सामना करने को झुठला दिया तथा हमने उन्हें सम्पन्न किया था, सांसारिक जीवन में: ये तो बस एक मनुष्य है तुम्हारे जैसा, खाता है, जो तुम खाते हो और पीता है, जो तुम पीते हो।');
INSERT INTO "al_umari" VALUES(23,34,'23:34','और यदि तुमने मान लिया अपने जैसे एक मनुज को, तो निश्चय तुम क्षतिग्रस्त हो।');
INSERT INTO "al_umari" VALUES(23,35,'23:35','क्या वह तुम्हें वचन देता है कि जब तुम मर जाओगे और धूल तथा हड्डियाँ हो जाओगे, तो तुम, फिर जीवित निकाले जाओगे?');
INSERT INTO "al_umari" VALUES(23,36,'23:36','बहुत दूर की बात है, जिसका तुम्हें वचन दिया जा रहा है।');
INSERT INTO "al_umari" VALUES(23,37,'23:37','जीवन तो बस सांसारिक जीवन है, हम मरते-जीते हैं और हम फिर जीवित नहीं किये जायेंगे।');
INSERT INTO "al_umari" VALUES(23,38,'23:38','ये तो बस एक व्यक्ति है, जिसने अल्लाह पर एक झूठ घड़ लिया है और हम उसका विश्वास करने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(23,39,'23:39','नबी ने प्रार्थना कीः मेरे पालनहार! मेरी सहायता कर, उनके झुठलाने पर मुझे।');
INSERT INTO "al_umari" VALUES(23,40,'23:40','(अल्लाह ने) कहाः शीघ्र ही वे (अपने किये पर) पछतायेंगे।');
INSERT INTO "al_umari" VALUES(23,41,'23:41','अन्ततः पकड़ लिया उन्हें कोलाहल ने सत्यानुसार और हमने उन्हें कचरा बना दिया, तो दूरी हो अत्याचारियों के लिए।');
INSERT INTO "al_umari" VALUES(23,42,'23:42','फिर हमने पैदा किया उनके पश्चात् दूसरे युग के लोगों को।');
INSERT INTO "al_umari" VALUES(23,43,'23:43','नहीं आगे होती है, कोई जाति अपने समय से और न पीछे[। ');
INSERT INTO "al_umari" VALUES(23,44,'23:44','फिर, हमने भेजा अपने रसूलों को निरन्तर, जब-जबकिसी समुदाय के पास उसका रसूल आया, उन्होंने उसे झुठला दिया, तो हमने पीछे लगा[ दिया उनके, एक को दूसरे के और उन्हें कहानी बना दिया। तो दूरी है उनके लिए, जो ईमान नहीं लाते। ');
INSERT INTO "al_umari" VALUES(23,45,'23:45','फिर हमने भेजा मूसा तथा उसके भाई हारून को अपनी निशानियों तथा खुले तर्क के साथ।');
INSERT INTO "al_umari" VALUES(23,46,'23:46','फ़िरऔन और उसके प्रमुखों की ओर, तो उन्होंने गर्व किया तथा वे थे ही अभिमानी लोग।');
INSERT INTO "al_umari" VALUES(23,47,'23:47','उन्होंने कहाः क्या हम ईमान लायें अपने जैसे दो व्यक्तियों पर, जबकि उन दोनों की जाति हमारे अधीन है?');
INSERT INTO "al_umari" VALUES(23,48,'23:48','तो उन्होंने दोनों को झुठला दिय, तथा हो गये विनाशों में।');
INSERT INTO "al_umari" VALUES(23,49,'23:49','और हमने प्रदान की मूसा को पुस्तक[, ताकि वे मार्गदर्शन पा जायें। ');
INSERT INTO "al_umari" VALUES(23,50,'23:50','और हमने बना दिया मर्यम के पुत्र तथा उसकी माँ को एक निशानी तथा दोनों को शरण दी एक उच्च बसने योग्य तथा प्रवाहित स्रोत के स्थान की ओर[। ');
INSERT INTO "al_umari" VALUES(23,51,'23:51','हे रसूलो! खाओ स्वच्छ[ चीज़ों में से तथा अच्छे कर्म करो, वास्तव में, मैं उससे, जो तुम कर रहे हो, भली-भाँति अवगत हूँ। ');
INSERT INTO "al_umari" VALUES(23,52,'23:52','और वास्तव में, तुम्हारा धर्म एक ही धर्म है और मैं ही तुम सबका पालनहार हूँ, अतः मुझी से डरो।');
INSERT INTO "al_umari" VALUES(23,53,'23:53','तो उन्होंने खण्ड कर लिया, अपने धर्म का, आपस में कई खण्ड, प्रत्येक सम्प्रदाय उसीमें जो उनके पास[ है, मगन है। ');
INSERT INTO "al_umari" VALUES(23,54,'23:54','अतः (हे नबी!) आप उन्हें छोड़ दें, उनकी अचेतना में कुछ समय तक।');
INSERT INTO "al_umari" VALUES(23,55,'23:55','क्या वे समझते हैं कि हम, जो सहायता कर रहे हैं उनकी धन तथा संतान से।');
INSERT INTO "al_umari" VALUES(23,56,'23:56','शीघ्रता कर रहे हैं उनके लिए भलाईयों में? बल्कि वे समझते नहीं हैं[। ');
INSERT INTO "al_umari" VALUES(23,57,'23:57','वास्तव में, जो अपने पालनहार के भय से डरने वाले हैं।');
INSERT INTO "al_umari" VALUES(23,58,'23:58','और जो अपने पालनहार की आयतों पर ईमान रखते हैं।');
INSERT INTO "al_umari" VALUES(23,59,'23:59','और जो अपने पालनहार का साझी नहीं बनाते हैं।');
INSERT INTO "al_umari" VALUES(23,60,'23:60','और जो करते हैं, जो कुछ भी करें और उनके दिल काँपते रहते हैं कि वे अपने पालनहार की ओर फिरकर जाने वाले हैं।');
INSERT INTO "al_umari" VALUES(23,61,'23:61','वही शीघ्रता कर रहे हैं भलाईयों में तथा वही उनके लिए अग्रसर हैं।');
INSERT INTO "al_umari" VALUES(23,62,'23:62','और हम बोझ नहीं रखते किसी प्राणी पर, परन्तु उसके सामर्थ्य के अनुसार तथा हमारे पास एक पुस्तक है, जो सत्य बोलती है और उनपर अत्याचार नहीं किया[ जायेगा। ');
INSERT INTO "al_umari" VALUES(23,63,'23:63','बल्कि उनके दिल अचेत हैं इससे तथा उनके बहुत-से कर्म हैं इसके सिवा, जिन्हें वे करने वाले हैं।');
INSERT INTO "al_umari" VALUES(23,64,'23:64','यहाँतक कि जब हम पकड़ लेंगे उनके सुखियों को यातना में, तो वे विलाप करने लगेंगे।');
INSERT INTO "al_umari" VALUES(23,65,'23:65','आज विलाप न करो, निःसंदेह तुम हमारी ओर से सहायता नहीं दिये जाओगे।');
INSERT INTO "al_umari" VALUES(23,66,'23:66','मेरी आयतें तुम्हें सुनायी जाती रहीं, तो तुम एड़ियों के बल फिरते रहे।');
INSERT INTO "al_umari" VALUES(23,67,'23:67','अभिमान करते हुए, उसे कथा बनाकर बकवास करते रहे।');
INSERT INTO "al_umari" VALUES(23,68,'23:68','क्या उन्होंने इस कथन (क़ुर्आन) पर विचार नहीं किया अथवा इनके पास वह[ आ गया, जो उनके पूर्वजों के पास नहीं आया? ');
INSERT INTO "al_umari" VALUES(23,69,'23:69','अथवा वह अपने रसूल से परिचित नहीं हुए, इसलिए वे उसका इन्कार कर रहे[ हैं? ');
INSERT INTO "al_umari" VALUES(23,70,'23:70','अथवा वे कहते हैं कि वह पागल है? बल्कि वह तो उनके पास सत्य लाये हैं और उनमें से अधिक्तर को सत्य अप्रिय है।');
INSERT INTO "al_umari" VALUES(23,71,'23:71','और यदि अनुसरण करने लगे सत्य उनकी मनमानी का, तो अस्त-व्यस्त हो जाये आकाश तथा धरती और जो उनके बीच है, बल्कि हमने दे दी है उन्हें उनकी शिक्षा, फिर (भी) वे अपनी शिक्षा से विमुख हो रहे हैं।');
INSERT INTO "al_umari" VALUES(23,72,'23:72','(हे नबी!) क्या आप उनसे कुछ धन माँग रहे हैं? आपके लिए तो आपके पालनहार का दिया हुआ ही उत्तम है और वह सर्वोत्तम जीविका देने वाला है।');
INSERT INTO "al_umari" VALUES(23,73,'23:73','निश्चय आप तो उन्हें सुपथ की ओर बुला रहे हैं।');
INSERT INTO "al_umari" VALUES(23,74,'23:74','और जो आख़िरत (परलोक) पर ईमान नहीं रखते, वे सुपथ से कतराने वाले हैं।');
INSERT INTO "al_umari" VALUES(23,75,'23:75','और यदि हम उनपर दया करें और दूर करें, जो दुःख उनके साथ है,[ तो वे अपने कुकर्मों में और अधिक बहकते जायेंगे। ');
INSERT INTO "al_umari" VALUES(23,76,'23:76','और हमने उन्हें यातना में ग्रस्त (भी) किया, तो अपने पालनहार के समक्ष नहीं झुके और न विनय करते हैं।');
INSERT INTO "al_umari" VALUES(23,77,'23:77','यहाँतक कि जब हम उनपर खोल देंगे कड़ी यातना के[ द्वार, तो सहसा वे उस समय निराश हो जायेंगे ।');
INSERT INTO "al_umari" VALUES(23,78,'23:78','वही है, जिसने बनाये हैं तुम्हारे लिए कान तथा आँखें और दिल[, (फिर भी) तुम बहुत कम कृतज्ञ होते हो। ');
INSERT INTO "al_umari" VALUES(23,79,'23:79','और उसीने तुम्हें धरती में फैलाया है और उसी की ओर एकत्र किये जाओगे।');
INSERT INTO "al_umari" VALUES(23,80,'23:80','तथा वही है, जो जीवन देता और मारता है और उसी के अधिकार में है रात्रि तथा दिन का फेर-बदल, तो क्या तुम समझ नहीं रखते?');
INSERT INTO "al_umari" VALUES(23,81,'23:81','बल्कि उन्होंने वही बात कही, जो अगलों ने कही।');
INSERT INTO "al_umari" VALUES(23,82,'23:82','उन्होंने कहाः क्या जब हम मर जायेंगे और मिट्टी तथा हड्डियाँ हो जायेंगे, तो क्या हम फिर अवश्य जीवित किये जायेंगे?');
INSERT INTO "al_umari" VALUES(23,83,'23:83','हमें तथा हमारे पूर्वजों को इससे पहले यही वचन दिया जा चुका है, ये तो बस अगलों की कल्पित कथाएँ हैं।');
INSERT INTO "al_umari" VALUES(23,84,'23:84','(हे नबी!) उनसे कहोः किसकी है धरती और जो उसमें है, यदि तुम जानते हो?');
INSERT INTO "al_umari" VALUES(23,85,'23:85','वे कहेंगे कि अल्लाह की। आप कहिएः फिर तुम क्यों शिक्षा ग्रहण नहीं करते?');
INSERT INTO "al_umari" VALUES(23,86,'23:86','आप पूछिए कि कौन है सातों आकाशों का स्वामी तथा महा सिंहासन का स्वामी?');
INSERT INTO "al_umari" VALUES(23,87,'23:87','वे कहेंगेः अल्लाह है। आप कहिएः फिर तुम उससे डरते क्यों नहीं हो?');
INSERT INTO "al_umari" VALUES(23,88,'23:88','आप उनसे कहिए कि किसके हाथ में है, प्रत्येक वस्तु का अधिकार? और वह शरण देता है और उसे कोई शरण नहीं दे सकता, यदि तुम ज्ञान रखते हो?');
INSERT INTO "al_umari" VALUES(23,89,'23:89','वे अवश्य कहेंगे कि (ये सब गुण) अल्लाह ही के हैं। आप कहिएः फिर तुमपर कहाँ से जादू[ हो जाता है? ');
INSERT INTO "al_umari" VALUES(23,90,'23:90','बल्कि हमने उन्हें सत्य पहुँचा दिया है और निश्चय यही मिथ्यावादी हैं।');
INSERT INTO "al_umari" VALUES(23,91,'23:91','अल्लाह ने नहीं बनाया है अपनी कोई संतान और न उसके साथ कोई अन्य पूज्य है। यदि ऐसा होता, तो प्रत्येक पूज्य अलग हो जाता अपनी उत्पत्ति को लेकर और एक-दूसरे पर चढ़ दौड़ता। पवित्र है अल्लाह उन बातों से, जो ये सब लोग बनाते हैं।');
INSERT INTO "al_umari" VALUES(23,92,'23:92','वह परोक्ष (छुपे) तथा प्रत्यक्ष (खुले) का ज्ञानी है तथा उच्च है, उस शिर्क से, जो वे करते हैं।');
INSERT INTO "al_umari" VALUES(23,93,'23:93','(हे नबी!) आप प्रार्थना करें कि हे मेरे पालनहार! यदि तू मुझे वह दिखाये, जिसकी उन्हें धमकी दी जा रही है।');
INSERT INTO "al_umari" VALUES(23,94,'23:94','तो मेरे पालनहार! मुझे इन अत्याचारियों में सम्मिलित न करना।');
INSERT INTO "al_umari" VALUES(23,95,'23:95','तथा वास्तव में, हम आपको उसे दिखाने पर, जिसकी उन्हें धमकी दी जा रही है, अवश्य सामर्थ्यवान हैं।');
INSERT INTO "al_umari" VALUES(23,96,'23:96','(हे नबी!) आप दूर करें उस (व्यवहार) से जो उत्तम हो, बुराई को। हम भली-भाँति अवगत हैं, उन बातों से जो वे बनाते हैं।');
INSERT INTO "al_umari" VALUES(23,97,'23:97','तथा आप प्रार्थना करें कि हे मेरे पालनहार! मैं तेरी शरण माँगता हूँ, शैतानों की शंकाओं से।');
INSERT INTO "al_umari" VALUES(23,98,'23:98','तथा मैं तेरी शरण माँगता हूँ, मेरे पालनहार! कि वे मेरे पास आयें।');
INSERT INTO "al_umari" VALUES(23,99,'23:99','यहाँतक कि जब उनमें किसी की मौत आने लगे, तो कहता हैः मेरे पालनहार! मुझे (संसार में) वापस कर दे[। ');
INSERT INTO "al_umari" VALUES(23,100,'23:100','संभवतः, मैं अच्छा कर्म करूँगा, उस (संसार में) जिसे छोड़ आया हूँ! कदापि ऐसा नहीं होगा! वह केवल एक कथन है, जिसे वह कह रहा[ है और उनके पीछे एक आड़ है, उनके पुनः जीवित किये जाने के दिन तक।');
INSERT INTO "al_umari" VALUES(23,101,'23:101','तो जब नरसिंघा में फूँक दिया जायेगा, तो कोई संबन्ध नहीं होगा, उनके बीच, उस[ दिन और न वे एक-दूसरे को पूछेंगे। ');
INSERT INTO "al_umari" VALUES(23,102,'23:102','फिर जिसके पलड़े भारी होंगे, वही सफल होने वाले हैं।');
INSERT INTO "al_umari" VALUES(23,103,'23:103','और जिसके पलड़े हल्क होंगे, तो उन्होंने ही स्वयं को क्षतिग्रस्त कर लिया, (तथा वे) नरक में सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(23,104,'23:104','झुलसा देगी उनके चेहरों को अग्नि तथा उसमें उनके जबड़े (झुलसकर) बाहर निकले होंगे।');
INSERT INTO "al_umari" VALUES(23,105,'23:105','(उनसे कहा जायेगाः) क्या जब मेरी आयतें तुम्हें सुनाई जाती थीं, तो तुम उनको झुठलाते नहीं थे?');
INSERT INTO "al_umari" VALUES(23,106,'23:106','वे कहेंगेःहमारे पालनहार! हमरा दुर्भाग्य हमपर छा गया[ और वास्तव में, हम कुपथ थे। ');
INSERT INTO "al_umari" VALUES(23,107,'23:107','हमारे पालनहार! हमें इससे निकाल दे, यदि अब हम ऐसा करें, तो निश्चय हम अत्याचारी होंगे।');
INSERT INTO "al_umari" VALUES(23,108,'23:108','वह (अल्लाह) कहेगाः इसीमें अपमानित होकर पड़े रहो और मुझसे बात न करो।');
INSERT INTO "al_umari" VALUES(23,109,'23:109','मेरे भक्तों में एक समुदाय था, जो कहता था कि हमारे पालनहार! हम ईमान लाये। तू हमें क्षमा कर दे और हमपर दया कर और तू सब दयावानों से उत्तम है।');
INSERT INTO "al_umari" VALUES(23,110,'23:110','तो तुमने उनका उपहास किया, यहाँ तक कि उन्होंने तुम्हें मेरी याद भुला दी और तुम उनपर हँसते रहे।');
INSERT INTO "al_umari" VALUES(23,111,'23:111','मैंने उन्हें आज बदला (प्रतिफल) दे दिया है उनके धैर्य का, वास्तव में, वही सफल हैं।');
INSERT INTO "al_umari" VALUES(23,112,'23:112','(अल्लाह) उनसे कहेगाः तुम धरती में कितने वर्ष रहे?');
INSERT INTO "al_umari" VALUES(23,113,'23:113','वे कहेंगेः हम एक दिन या दिन के कुछ भाग रहे। तो गणना करने वालों से पूछ लें।');
INSERT INTO "al_umari" VALUES(23,114,'23:114','वह कहेगाः तुम नहीं रहे, परन्तु बहुत कम। क्या ही अच्छा होता कि तुमने (पहले ही) जान लिया[ होता। ');
INSERT INTO "al_umari" VALUES(23,115,'23:115','क्या तुमने समझ रखा है कि हमने तुम्हें व्यर्थ पैदा किया है और तुम हमारी ओर फिर नहीं लाये[ जाओगे? ');
INSERT INTO "al_umari" VALUES(23,116,'23:116','तो सर्वोच्च है अल्लाह वास्तविक अधिपति। नहीं है कोई सच्चा पूज्य, परन्तु वही महिमावान अर्श (सिंहासन) का स्वामी।');
INSERT INTO "al_umari" VALUES(23,117,'23:117','और जो (भी) पुकारेगा अल्लाह के साथ किसी अन्य पूज्य को, जिसके लिए उसके पास कोई प्रमाण नहीं, तो उसका ह़िसाब केवल उसके पालनहार के पास है, वास्तव में, काफ़िर सफव नहीं[ होंगे। ');
INSERT INTO "al_umari" VALUES(23,118,'23:118','तथा आप प्रार्थना करें कि मेरे पालनहार! तू क्षमा कर तथा दया कर और तू ही सब दयावानों से उत्तम (दयावान्) है।');
INSERT INTO "al_umari" VALUES(24,1,'24:1','ये एक सूरह है, जिसे हमने उतारा तथा अनिवार्य किया है और उतारी हैं इसमें बहुत-सी खुली आयतें (निशानियाँ), ताकि तुम शिक्षा ग्रहण करो।');
INSERT INTO "al_umari" VALUES(24,2,'24:2','व्यभिचारिणी तथा[ व्यभिचारी, दोनों में से प्रत्येक को सौ कोड़े मारो और तुम्हें उन दोनों पर कोई तरस न आये अल्लाह के धर्म के विषय में, यदि तुम अल्लाह तथा अन्तिम दिन पर ईमान (विश्वास) रखते हो और चाहिए कि उनके दण्ड के समय उपस्थित रहे ईमान वालों का एक गिरोह।');
INSERT INTO "al_umari" VALUES(24,3,'24:3','व्यभिचारी[ नहीं विवाह करता, परन्तु व्यभिचारिणी अथवा मिश्रणवादी से और व्यभिचारिणी नहीं विवाह करती, परन्तु व्यभिचारी अथवा मिश्रणवादी से और इसे ह़राम (अवैध) कर दिया गया है ईमान वालों पर। ');
INSERT INTO "al_umari" VALUES(24,4,'24:4','तथा जो आरोप[ लगायें व्यभिचार का सतवंती स्त्रियों पर, फिर न लायें चार साक्षी, तो उन्हें अस्सी कोड़े मारो और न स्वीकार करो उनका साक्ष्य कभी भी और वे स्वयं अवज्ञाकारी हैं। ');
INSERT INTO "al_umari" VALUES(24,5,'24:5','परन्तु जिन्होंने क्षमा माँग ली इसके पश्चात् तथा अपना सुधार कर लिया, तो निःसंदेह अल्लाह अति क्षमी दयावान्[ है। ');
INSERT INTO "al_umari" VALUES(24,6,'24:6','और जो व्यभिचार का आरोप लगायें अपनी पत्नियों पर और उनके साक्षी न हों,[ परन्तु वे स्वयं, तो चार साक्ष्य अल्लाह की शपथ लेकर देना है कि वास्तव में वह सच्चा है ।');
INSERT INTO "al_umari" VALUES(24,7,'24:7','और पाँचवीं बार ये (कहे) कि उसपर अल्लाह की धिक्कार है, यदि वह झूठा है।');
INSERT INTO "al_umari" VALUES(24,8,'24:8','और स्त्री से दण्ड[ इस प्रकार दूर होगा कि वह चार बार साक्ष्य दे, अल्लाह की शपथ लेकर कि निःसंदेह, वह (पति) मिथ्यावादियों में से है। ');
INSERT INTO "al_umari" VALUES(24,9,'24:9','और पाँचवीं बार ये (कहे) कि उसपर अल्लाह की धिक्कार हो, यदि वह सच्चा[ हो। ');
INSERT INTO "al_umari" VALUES(24,10,'24:10','और यदि तुमपर अल्लाह का अनुग्रह और दया न होती और ये कि अल्लाह अति क्षमी तत्वज्ञ है, (तो समस्या बढ़ जाती)।');
INSERT INTO "al_umari" VALUES(24,11,'24:11','वास्तव[ में, जो कलंक घड़ लाये हैं, (वे) तुम्हारे ही भीतर का एक गिरोह है, तुम इसे बुरा न समझो, बल्कि ये तुम्हारे लिए अच्छा है। उनमें से प्रत्येक के लिए जितना भाग लिया, उतना पाप है और जिसने भार लिया उसके बड़े भाग का, तो उसके लिए बड़ी यातना है।');
INSERT INTO "al_umari" VALUES(24,12,'24:12','क्यों जब उसे ईमान वाले पुरुषों तथा स्त्रियों ने सुना, तो अपने आपमें अच्छा विचार नहीं किया तथा कहा कि ये खुला आरोप है?');
INSERT INTO "al_umari" VALUES(24,13,'24:13','वे क्यों नहीं लाये इसपर चार साक्षी? (जब साक्षी नहीं लाये) तो निःसंदेह अल्लाह के समीप वही झूठे हैं।');
INSERT INTO "al_umari" VALUES(24,14,'24:14','और यदि तुमपर अल्लाह का अनुग्रह और दया न होती लोक तथा परलोक में, तो जिन बातों में तुम पड़ गये, उनके बदले तुमपर कड़ी यातना आ जाती।');
INSERT INTO "al_umari" VALUES(24,15,'24:15','जबकि (बिना सोचे) तुम अपनी ज़बानों पर इसे लाने लगे और अपने मुखों से वह बात कहने लगे, जिसका तुम्हें कोई ज्ञान न था तथा तुम इसे सरल समझ रहे थे, जबकि अल्लाह के के समीप ये बहुत बड़ी बात थी।');
INSERT INTO "al_umari" VALUES(24,16,'24:16','और क्यों नहीं जब तुमने इसे सुना, तो कह दिया कि हमारे लिए योग्य नहीं कि ये बात बोलें? हे अल्लाह! तू पवित्र है! ये तो बहुत बड़ा आरोप है।');
INSERT INTO "al_umari" VALUES(24,17,'24:17','अल्लाह तुम्हें शिक्षा देता है कि पुनः कभी इस जैसी बात न कहना, यदि तुम ईमान वाले हो।');
INSERT INTO "al_umari" VALUES(24,18,'24:18','और अल्लाह उजागर कर रहा है तुम्हारे लिए आयतों (आदेशों) को तथा अल्लाह सर्वज्ञ, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(24,19,'24:19','जो लोग चाहते हैं कि उनमें अश्लीलता[ फैले, जो ईमान लाये हैं, तो उनके लिए दुःखदायी यातना है, लोक तथा परलोक में तथा अल्लाह जानता है और तुम नहीं जानते।');
INSERT INTO "al_umari" VALUES(24,20,'24:20','और यदि तुमपर अल्लाह का अनुग्रह तथा उसकी दया न होती, ( तो तुमपर यातना आ जाती)। और वास्तव में, अल्लाह अति करुणामय, दयावान् है। ');
INSERT INTO "al_umari" VALUES(24,21,'24:21','हे ईमान वालो! शैतान के पद्चिन्हों पर न चलो और जो उसके पदचिन्हों पर चलेगा, तो वह अश्लील कार्य तथा बुराई का ही आदेश देगा और यदि तुमपर अल्लाह का अनुग्रह और उसकी दया न होती, तो तुममें से कोई पवित्र कभी नहीं होता, परन्तु अल्लाह पवित्र करता है, जिसे चाहे और अल्लाह सब कुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(24,22,'24:22','और न शपथ लें[ तुममें से धनी और सुखी कि नहीं देंगे समीपवर्तियों तथा निर्धनों को और (उन्हें) जो हिजरत कर गये अल्लाह की राह में और चाहिये कि क्षमा कर दें तथा जाने दें! क्या तुम नहीं चाहते कि अल्लाह तुम्हें क्षमा कर दे और अल्लाह अति क्षमी, सहनशील हैं। ');
INSERT INTO "al_umari" VALUES(24,23,'24:23','जो लोग आरोप लगाते हैं सतवंती भोली-भाली ईमान वाली स्त्रियों पर, वे धिक्कार दिये गये लोक तथा परलोक में और उन्हीं के लिए बड़ी यातना है।');
INSERT INTO "al_umari" VALUES(24,24,'24:24','जिस दिन साक्ष्य (गवाही) देंगी उनकी जीभें, उनके हाथ और उनके पैर, उनके कर्मों की।');
INSERT INTO "al_umari" VALUES(24,25,'24:25','उस दिन अल्लाह उन्हें उनका पूरा न्याय पूर्वक बदला देगा तथा वे जान लेंगे कि अल्लाह ही सत्य (तथा सच को) उजागर करने वाला है।');
INSERT INTO "al_umari" VALUES(24,26,'24:26','अपवित्र स्त्रियाँ, अपवित्र पुरुषों के लिए हैं तथा अपवित्र पुरुष, अपवित्र स्त्रियों के लिए और पवित्र स्त्रियाँ, पवित्र पुरुषों के लिए हैं तथा पवित्र पुरुष, पवित्र स्त्रियों के[ लिए। वही निर्दोष हैं उन बातों से, जो वे कहते हैं। उन्हीं के लिए क्षमा तथा सम्मानित जीविका है। ');
INSERT INTO "al_umari" VALUES(24,27,'24:27','हे ईमान वालो[! मत प्रवेश करो किसी घर में, अपने घरों के सिवा, यहाँ तक कि अनुमति ले लो और उनके वासियों को सलाम कर लो, ये तुम्हारे लिए उत्तम है, ताकि तुम याद रखो।');
INSERT INTO "al_umari" VALUES(24,28,'24:28','और यदि, उनमें किसी को न पाओ, तो उनमें प्रवेश न करो, यहाँ तक कि तुम्हें अनुमति दे दी जाये और यदि, तुमसे कहा जाये कि वापस हो जाओ, तो वापस हो जाओ, ये तुम्हारे लिए अधिक पवित्र है तथा अल्लाह जो कुछ तुम करते हो, भली-भाँति जानने वाला है।');
INSERT INTO "al_umari" VALUES(24,29,'24:29','तुमपर कोई दोष नहीं है कि प्रवेश करो निर्जन घरों में, जिनमें तुम्हारा सामान हो और अल्लाह जानता है, जो कुछ तुम बोलते हो और जो मन में रखते हो।');
INSERT INTO "al_umari" VALUES(24,30,'24:30','(हे नबी!) आप ईमान वालों से कहें कि अपनी आँखें नीची रखें और अपने गुप्तांगों की रक्षा करें। ये उनके लिए अधिक पवित्र है, वास्तव में, अल्लाह सूचित है उससे, जो कुछ वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(24,31,'24:31','और ईमान वालियों से कहें कि अपनी आँखें नीची रखें और अपने गुप्तांगों की रक्षा करें और अपनी शोभा[ का प्रदर्शन न करें, सिवाय उसके जो प्रकट हो जाये तथा अपनी ओढ़नियाँ अपने वक्षस्थलों (सीनों) पर डाली रहें और अपनी शोभा का प्रदर्शन न करें, परन्तु अपने पतियों के लिए अथवा अपने पिताओं अथवा अपने ससुरों के लिए अथवा अपने पुत्रों अखवा अपने पति के पुत्रों के लिए अथवा अपने भाईयों अथवा भतीजों अथवा अपने भांजों के लिए अथवा अपनी स्त्रियों अथवा अपने दास-दासियों अथवा ऐसे अधीन पुरुषों के लिए, जो किसी और प्रकार का प्रयोजन न रखते हों अथवा उन बच्चों के लिए, जो स्त्रियों की गुप्त बातें ने जानते हों और अपने पैर धरती पर मारती हुई न चलें कि उसका ज्ञान हो जाये, जो शोभा उन्होंने छुपा रखी है और तुमसब मिलकर अल्लाह से क्षमा माँगो, हे ईमान वालो! ताकि तुम सफल हो जाओ।');
INSERT INTO "al_umari" VALUES(24,32,'24:32','तथा तुम विवाह कर दो[ अपने में से अविवाहित पुरुषों तथा स्त्रियों का और अपने सदाचारी दासों और अपनी दासियों का, यदि वे निर्धन होंगे, तो अल्लाह उन्हें धनी बना देगा अपने अनूग्रह से और अल्लाह उदार, सर्वज्ञ है। ');
INSERT INTO "al_umari" VALUES(24,33,'24:33','और उन्हें पवित्र रहना चाहिए, जो विवाह करने का सामर्थ्य नहीं रखते, यहाँ तक कि उन्हें धनी कर दे अल्लाह अपने अनुग्रह से तथा जो स्वाधीनता-लेख की माँग करें, तुम्हारे दास-दासियों में से, तो तुम उन्हें लिख दो, यदि तुम उनमें कुछ भलाई जानो[ और उन्हें अल्लाह के उस माल से दो, जो उसने तुम्हें प्रदान किया है तथा बाध्य न करो अपनी दासियों को व्यभिचार पर, जब वे पवित्र रहना चाहती हैं, ताकि तुम सांसारिक जीवन का लाभ प्राप्त करो और जो उन्हें बाध्य करेगा, तो अल्लाह उनके बाध्य किये जाने के पश्चात्, अति क्षमी, दयावान् है।');
INSERT INTO "al_umari" VALUES(24,34,'24:34','तथा हमने तुम्हारी ओर खुली आयतें उतारी हैं और उनका उदाहरण, जो तुमसे पहले गुज़र गये तथा आज्ञाकारियों के लिए शिक्षा।');
INSERT INTO "al_umari" VALUES(24,35,'24:35','अल्लाह आकाशों तथा धरती का प्रकाश है, उसके प्रकाश की उपमा ऐसी है, जैसे एक ताखा हो, जिसमें दीप हो, दीप काँच के झाड़ में हो, झाड़ मोती जैसे चमकते तारे के समान हो, वह ऐसे शुभ ज़ैतून के वृक्ष के तेल से जलाया जाता हो, जो न पूर्वी हो और न पश्चिमी, उसका तेल समीप (संभव) है कि स्वयं प्रकाश देने लगे, यद्यपि उसे आग न लगे। प्रकाश पर प्रकाश है। अल्लाह अपने प्रकाश का मार्ग दिखा देता है, जिसे चाहे और अल्लाह लोगों को उदाहरण दे रहा है और अल्लाह प्रत्येक वस्तु से भली-भाँति अवगत है। ');
INSERT INTO "al_umari" VALUES(24,36,'24:36','(ये प्रकाश) उन घरों[ में है, अल्लाह ने जिन्हें ऊँचा करने और उनमें अपने नाम की चर्चा करने का आदेश दिया है, उसकी महिमा का गान करते हैं, जिनमें प्रातः तथा संध्या। ');
INSERT INTO "al_umari" VALUES(24,37,'24:37','ऐसे लोग, जिन्हें अचेत नहीं करता व्यापार तथा सौदा, अल्लाह के स्मरण, नमाज़ की स्थापना करने और ज़कात देने से। वे उस दिन[ से डरते हैं जिसमें दिल तथा आँखें उलट जायेँगी। ');
INSERT INTO "al_umari" VALUES(24,38,'24:38','ताकि अल्लाह उन्हें बदला दे, उनके सत्कर्मों का और उन्हें अधिक प्रदान करे अपने अनुग्रह से और अल्लाह जिसे चाहे, अनगिनत जीविका देता है।');
INSERT INTO "al_umari" VALUES(24,39,'24:39','तथा जो काफ़िर[ हो गये, उनके कर्म उस चमकते सुराब के समान हैं, जो किसी मैदान में हो, जिसे प्यासा पानी समझता हो। परन्तु, जब उसके पास आये, तो कुछ न पाये और वहाँ अल्लाह को पाये, जो उसका पूरा ह़िसाब चुका दे और अल्लाह शीघ्र ह़िसाब लेने वाला है।');
INSERT INTO "al_umari" VALUES(24,40,'24:40','अथवा उन अंधकारों के समान हैं, जो किसी गहरे सागर में हो और जिसपर तरंग छायी हो, जिसके ऊपर तरंग हो, उसके ऊपर बादल हो, अन्धकार पर अन्धकार हो, जब अपना हाथ निकाले, तो उसे भी न देख सके और अल्लाह जिसे प्रकाश न दे, उसके लिए कोई प्रकाश[ नहीं। ');
INSERT INTO "al_umari" VALUES(24,41,'24:41','क्या आपने नहीं देखा कि अल्लाह ही की पवित्रता का गान कर रहे हैं, जो आकाशों तथा धरती में हैं तथा पंख फैलाये हुए पक्षी? प्रत्येक ने अपनी बंदगी तथा पवित्रता गान को जान लिया[ है और अल्लाह भली-भाँति जानने वाला है, जो वे कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(24,42,'24:42','अल्लाह ही के लिए है, आकाशों तथा धरती का राज्य और अल्लाह ही की ओर फिरकर[ जाना है। ');
INSERT INTO "al_umari" VALUES(24,43,'24:43','क्या आपने नहीं देखा कि अल्लाह बादलों को चलाता है, फिर उन्हें परस्पर मिला देता है, फिर उन्हें घंघोर मेघ बना देता है, फिर आप देखते हैं बूँद को, उसके मध्य से निकलती हुई और वही पर्वतों जैसे बादल से ओले बरसाता है, फिर जिसपर चाहे, आपदा उतारता है और जिससे चाहे, फेर देता है। उसकी बिजली की चमक संभव है कि आँखों को उचक ले।');
INSERT INTO "al_umari" VALUES(24,44,'24:44','अल्लाह ही रात और दिन को बदलता[ है। बेशक इसमें बड़ी शिक्षा है, समझ-बूझ वालों के लिए। ');
INSERT INTO "al_umari" VALUES(24,45,'24:45','अल्लाह ही ने प्रत्येक जीव धारी को पानी से पैदा किया है। तो उनमें से कुछ अपने पेट के बल चलते हैं और कुछ दो पैर पर तथा कुछ चार पैर पर चलते हैं। अल्लाह जो चाहे, पैदा करता है, वास्तव में, वह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(24,46,'24:46','हमने खुली आयतें (क़ुर्आन) अवतरित कर दी हैं और अल्लाह जिसे चाहता है, सुपथ दिखा देता है।');
INSERT INTO "al_umari" VALUES(24,47,'24:47','और[ वे कहते हैं कि हम अल्लाह तथा रसूल पर ईमान लाये और हम आज्ञाकारी हो गये, फिर मुँह फेर लेता है उनमें से एक गिरोह इसके पश्चात्। वास्तव में, वे ईमान वाले हैं ही नहीं। ');
INSERT INTO "al_umari" VALUES(24,48,'24:48','और जब बुलाये जाते हैं, अल्लाह तथा उसके रसूल की ओर, ताकि (रसूल) निर्णय कर दें उनके बीच (विवाद का), तो अकस्मात उनमें से एक गिरोह मुँह फेर लेता है।');
INSERT INTO "al_umari" VALUES(24,49,'24:49','और यदि उन्हीं को अधिकार पहुँचता हो, तो आपके पास सिर झुकाये चले आते हैं।');
INSERT INTO "al_umari" VALUES(24,50,'24:50','क्या उनके दिलों में रोग है अथवा द्विधा में पड़े हुए हैं अथवा डर रहे हैं कि अल्लाह अत्याचार करेगा, उनपर और उसके रसूल? बल्कि वही अत्याचारी हैं।');
INSERT INTO "al_umari" VALUES(24,51,'24:51','ईमान वालों का कथन तो ये है कि जब अल्लाह और उसके रसूल की ओर बुलाये जायें, ताकि आप उनके बीच निर्णँय कर दें, तो कहें कि हमने सुन लिया तथा मान लिया और वही सफल होने वाले हैं।');
INSERT INTO "al_umari" VALUES(24,52,'24:52','तथा जो अल्लाह और उसके रसूल की आज्ञा का पालन करें, अल्लाह का भय रखें और उसकी (यातना से) डरें, तो वही सफल होने वाले हैं।');
INSERT INTO "al_umari" VALUES(24,53,'24:53','और इन (द्विधावादियों) ने बलपूर्वक शपथ ली कि यदि आप उन्हें आदेश दें, तो अवश्य वे (घरों से) निकल पड़ेंगे। उनसे कह दें: शपथ न लो। तुम्हारे आज्ञापालन की दशा जानी-पहचानी है। वास्तव में, अल्लाह तुम्हारे कर्मों से सूचित है।');
INSERT INTO "al_umari" VALUES(24,54,'24:54','(हे नबी!) आप कह दें कि अल्लाह की आज्ञा का पालन करो तथा रसूल की आज्ञा का पालन करो और यदि वे विमुख हों, तो आपका कर्तव्य केवल वही है, जिसका भार आपपर रखा गया है और तुम्हारा वह है, जिसका भार तुमपर रखा गया है और रसूल का दायित्व केवल खुला आदेश पहुँचा देना है।');
INSERT INTO "al_umari" VALUES(24,55,'24:55','अल्लाह ने वचन[ दिया है उन्हें, जो तुममें से ईमान लायें तथा सुकर्म करें कि उन्हें अवश्य धरती में अधिकार प्रदान करेगा, जैसे उन्हें अधिकार प्रदान किया, जो इनसे पहले थे तथा अवश्य सुदृढ़ कर देगा उनके उस धर्म को, जिसे उनके लिए पसंद किया है तथा उन (की दशा) को उनके भय के पश्चात् शान्ति में बदल देगा, वह मेरी इबादत (वंदना) करते रहें और किसी चीज़ को मेरा साझी न बनायें और जो कुफ़्र करें इसके पश्चात्, तो वही उल्लंघनकारी हैं। ');
INSERT INTO "al_umari" VALUES(24,56,'24:56','यथा नमाज़ की स्थापना करो, ज़कात दो तथा रसूल की आज्ञा का पालन करो, ताकि तुमपर दया की जाये।');
INSERT INTO "al_umari" VALUES(24,57,'24:57','और (हे नबी!) कदापि आप न समझें कि जो काफ़िर हो गये, वे (अल्लाह को) धरती में विवश कर देने वाले हैं और उनका स्थान नरक है और वह बुरा निवास स्थान है।');
INSERT INTO "al_umari" VALUES(24,58,'24:58','हे ईमान वालो! तुम[ से अनुमति लेना आवश्यक है, तुम्हारे स्वामित्व के दास-दासियों को और जो तुममें से (अभी) युवा अवस्था को न पहुँचे हों, तीन समय; फ़ज्र (भोर) की नमाज़ से पहले और जिस समय तुम अपने वस्त्र उतारते हो दोपहर में तथा इशा (रात्रि) की नमाज़ के पश्चात्। ये तीन (एकांत) पर्दे के समय हैं तुम्हारे लिए। (फिर) तुमपर और उनपर कोई दोष नहीं है इनके पश्चात्, तुम अधिक्तर आने-जाने वाले हो एक-दूसरे के पास। अल्लाह तुम्हारे लिए आदेशों का वर्णन कर रहा है और अल्लाह सर्वज्ञ निपुण है। ');
INSERT INTO "al_umari" VALUES(24,59,'24:59','और जब तुममें से बच्चे युवा अवस्था को पहुँचें, तो वे भी वैसे ही अनुमति लें, जैसे उनसे पूर्व के (बड़े) अनुमति माँगते हैं, इसी प्रकार, अल्लाह उजागर करता है तुम्हारे लिए अपनी आयतों को तथा अल्लाह सर्वज्ञ, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(24,60,'24:60','तथा जो बूढ़ी स्त्रियाँ विवाह की आशा न रखती हों, तो उनपर कोई दोष नहीं कि अपनी (पर्दे की) चादरें उतारकर रख दें, प्रतिबंध ये है कि अपनी शोभी का प्रदर्शन करने वाली न हों और यदि सुरक्षित रहें,[ तो उनके लिए अच्छ है। ');
INSERT INTO "al_umari" VALUES(24,61,'24:61','अन्धे पर कोई दोष नहीं है, न लंगड़े पर कोई दोष[ है, न रोगी पर कोई दोष है और न स्वयं तुमपर कि खाओ अपने घरों से, अपने बापों के घरों से, अपनी माँओं के धरों से, अपने भाईयों के घरों से, अपनी बहनों के घरों से, अपने चाचाओं के घरों से, अपनी फूफियों के घरों से, अपने मामाओं के घरों से, अपनी मौसियों के घरों से अथवा जिसकी चाबियों के तुम स्वामी हो अथवा अपने मित्रों के घरों से। तुमपर कोई दोष नहीं, एक साथ खाओ या अलग अलग। फिर जब तुम प्रवेश करो घरों में, तो अपनों को सलाम किया करो, एक आशीर्वाद है अल्लाह की ओर से निर्धारित किया हुआ, जो शुभ पवित्र है। इसी प्रकार, अल्लाह तुम्हारे लिए आयतों का वर्णन करता है, ताकि तुम समझ लो।');
INSERT INTO "al_umari" VALUES(24,62,'24:62','वास्तव में, ईमान वाले वह हैं, जो अल्लाह और उसके रसूल पर ईमान लायें और जब आपके साथ किसी सामूहिक कार्य पर होते हैं, तो जाते नहीं, जब तक आपसे अनुमति न लें, वास्तव में, जो आपसे अनुमति लेते हैं, वही अल्लाह और उसके रसूल पर ईमान रखते हैं, तो जब वे आपसे अपने किसी कार्य के लिए अनुमति माँगें, तो आप उनमें से जिसे चाहें, अनुमति दें और उनके लिए अल्लाह से क्षमा की प्रार्थना करें। वास्तव में, अल्लाह अति क्षमी दयावान् है।');
INSERT INTO "al_umari" VALUES(24,63,'24:63','और तुम मत बनाओ, रसूल के पुकारने को, परस्पर एक-दूसरे को पुकारने जैसा[, अल्लाह तुममें से उन्हें जानता है, जो सरक जाते हैं, एक-दूसरे की आड़ लेकर। तो उन्हें सावधान रहना चाहिए, जो आपके आदेश का विरोध करते हैं कि उनपर कोई आपदा आ पड़े अथवा उनपर कोई दुःखदायी यातना आ जाये। ');
INSERT INTO "al_umari" VALUES(24,64,'24:64','सावधान! अल्लाह ही का है, जो आकाशों तथा धरती में है, वह जानता है, जिस (दशा) पर तुम हो और जिस दिन वे उसकी ओर फेरे जायेंगे, तो उन्हें बता[ देगा, जो उन्होंने किया है और अल्लाह प्रत्येक चीज़ का अति ज्ञानी है। ');
INSERT INTO "al_umari" VALUES(25,1,'25:1','शुभ है वह (अल्लाह), जिसने फ़ुर्क़ान[ अवतरित किया अपने भक्त पर, ताकि पूरे संसार-वासियों को सावधान करने वाला हो।');
INSERT INTO "al_umari" VALUES(25,2,'25:2','जिसके लिए आकाशों तथा धरती का राज्य है तथा उसने अपने लिए कोई संतान नहीं बनायी और न उसका कोई साझी है राज्य में तथा उसने प्रत्येक वस्तु की उत्पत्ति की, फिर उसे एक निर्धारित रूप दिया।');
INSERT INTO "al_umari" VALUES(25,3,'25:3','और उन्होंने उसके अतिरिक्त अनेक पूज्य बना लिए हैं, जो किसी चीज़ की उत्पत्ति नहीं कर सकते और वे स्वयं उत्पन्न किये जाते हैं और न वे अधिकार रखते हैं अपने लिए किसी हानि का, न अधिकार रखते हैं किसी लाभ का, न अधिकार रखते हैं मरण और न जीवन और न पुनः[ जीवित करने का। ');
INSERT INTO "al_umari" VALUES(25,4,'25:4','तथा काफ़िरों ने कहाः ये[ तो बस एक मनघड़त बात है, जिसे इस ने स्वयं घड़ लिया है और इसपर अन्य लोगों ने उसकी सहायता की है। तो वास्तव में, वो काफ़िर बड़ा अत्याचार और झूठ बना लाये हैं।');
INSERT INTO "al_umari" VALUES(25,5,'25:5','और कहा कि ये तो पूर्वजों की कल्पित कथायें हैं जिन्हें उसने स्वयं लिख लिया है और वह पढ़ी जाती हैं, उसके समक्ष प्रातः और संध्या।');
INSERT INTO "al_umari" VALUES(25,6,'25:6','आप कह दें कि इसे उसने अवतरित किया है, जो आकाशों तथा धरती का भेद जानता है। वास्तव में, वह[ अति क्षमाशील, दयावान् है। ');
INSERT INTO "al_umari" VALUES(25,7,'25:7','तथा उन्होंने कहाः ये कैसा रसूल है, जो भोजन करता है तथा बाज़ारों में चलता है? क्यों नहीं उतार दिया गया उसकी ओर कोई फ़रिश्ता, तो वह उसके साथ सावधान करने वाला होता?');
INSERT INTO "al_umari" VALUES(25,8,'25:8','अथवा उसकी ओर कोई कोष उतार दिया जाता अथवा उसका कोई बाग़ होता, जिसमें से वह खाता? तथा अत्याचारियों ने कहाः तुमतो बस एक जादू किये हुए व्यक्ति का अनुसरण कर रहे हो।');
INSERT INTO "al_umari" VALUES(25,9,'25:9','देखो! आपके संबन्ध में ये कैसी कैसी बातें कर रहे हैं? अतः, वे कुपथ हो गये हैं, वे सुपथ पा ही नहीं सकते।');
INSERT INTO "al_umari" VALUES(25,10,'25:10','शुभकारी है वह (अल्लाह), जो यदि चाहे, तो बना दे आपके लिए इससे[ उत्तम बहुत-से बाग़, जिनमें नहरें प्रवाहित हों और बना दे आपके लिए बहुत-से भवन। ');
INSERT INTO "al_umari" VALUES(25,11,'25:11','वास्तविक बात ये है कि उन्होंने झुठला दिया है क़्यामत (प्रलय) को और हमने तैयार किया है, उसके लिए, जो प्रलय को झुठलाये, भड़कती हुई अग्नि।');
INSERT INTO "al_umari" VALUES(25,12,'25:12','जब वह ( नरक) उन्हें दूर स्थान से देखेगी, तो (प्रलय के झुठलाने वाले) सुन लेंगे उसके क्रोध तथा आवेग की ध्वनि को।');
INSERT INTO "al_umari" VALUES(25,13,'25:13','और जब वह फेंक दिये जायेंगे उसके किसी संकीर्ण स्थान में बंधे हुए, (तो) वहाँ विनाश को पुकारेंगे।');
INSERT INTO "al_umari" VALUES(25,14,'25:14','(उनसे कहा जायेगाः) आज एक विनाश को मत पुकारो, बहुत-से विनाशों को पुकारो[। ');
INSERT INTO "al_umari" VALUES(25,15,'25:15','(हे नबी!) आप उनसे कहिए कि क्या ये अच्छा है या स्थायी स्वर्ग, जिसका वचन आज्ञाकारियों को दिया गया है, जो उनका प्रतिफल तथा आवास है?');
INSERT INTO "al_umari" VALUES(25,16,'25:16','उन्हीं को उसमें जो इच्छा वे करेंगे, मिलेगा। वे सदावासी होंगे, आपके पालनहार पर (ये) वचन (पूरा करना) अनिवार्य है।');
INSERT INTO "al_umari" VALUES(25,17,'25:17','तथा जिस दिन वह एकत्र करेगा उन्हें और जिसकी वे इबादत (वंदना) करते थे अल्लाह के सिवाय, तो वह (अल्लाह) कहेगाः क्या तुमही ने मेरे इन भक्तों को कुपथ किया है अथवा वे स्वयं कुपथ हो गये?');
INSERT INTO "al_umari" VALUES(25,18,'25:18','वे कहेंगेः तू पवित्र है! हमारे लिए ये योग्य नहीं था कि तेरे सिवा कोई संरक्षक[ बनायें, परन्तु तूने सुखी बना दिया उनको तथा उनके पूर्वजों को, यहाँ तक कि वे शिक्षा को भूल गये और वे थे ही विनाश के योग्य। ');
INSERT INTO "al_umari" VALUES(25,19,'25:19','उन्हों[ ने तो तुम्हें झुठला दिया तुम्हारी बातों में, तो तुम न यातना को फेर सकोगे और न अपनी सहायता कर सकोगे और जो भी अत्याचार करेगा तुममें से, हम उसे घोर यातना चखायेंगे।');
INSERT INTO "al_umari" VALUES(25,20,'25:20','और नहीं भेजा हमने आपसे पूर्व किसी रसूल को, परन्तु वे भोजन करते और बाज़ारों में (भी) चलते[ फिरते थे तथा हमने बना दिया तुममें से एक को दूसरे के लिए परीक्षा का साधन, तो क्या तुम धैर्य रखोगे? तथा आपका पालनहार सब कुछ देखने वाला है।');
INSERT INTO "al_umari" VALUES(25,21,'25:21','तथा उन्होंने कहा जो हमसे मिलने की आशा नहीं रखतेः हमपर फ़रिश्ते क्यों नहीं उतारे गये या हम अपने पालनहार को देख लेते? उन्होंने अपने में बड़ा अभिमान कर लिया है तथा बड़ी अवज्ञा[ की है। ');
INSERT INTO "al_umari" VALUES(25,22,'25:22','जिस दिन[ वे फ़रिश्तों को देख लेंगे, उस दिन कोई शुभ सूचना नहीं होगी अपराधियों के लिए तथा वे कहेंगेः वंचित, वंचित है!!');
INSERT INTO "al_umari" VALUES(25,23,'25:23','और उनके कर्मों[ को हम लेकर धूल के समान उड़ा देंगे। ');
INSERT INTO "al_umari" VALUES(25,24,'25:24','स्वर्ग के अधिकारी, उस दिन अच्छे स्थान तथा सुखद शयनकक्ष में होंगे।');
INSERT INTO "al_umari" VALUES(25,25,'25:25','जिस दिन, चिर जायेगा आकाश बादल के साथ[ और फ़रिश्ते निरन्तर उतार दिये जायेंगे। ');
INSERT INTO "al_umari" VALUES(25,26,'25:26','उस दिन, वास्तविक राज्य अति दयावान का होगा और काफ़िरों पर एक कड़ा दिन होगा।');
INSERT INTO "al_umari" VALUES(25,27,'25:27','उस दिन, अत्याचारी अपने दोनों हाथ चबायेगा, वह कहेगाः क्या ही अच्छा होता कि मैंने रसूल का साथ दिया होता।');
INSERT INTO "al_umari" VALUES(25,28,'25:28','हाय मेरा दुर्भाग्य! काश मैंने अमुक को मित्र न बनाया होता।');
INSERT INTO "al_umari" VALUES(25,29,'25:29','उसने मुझे कुपथ कर दिया शिक्षा (क़ुर्आन) से, इसके पश्चात् कि मेरे पास आयी और शैतान मनुष्य को (समय पर) धोखा देने वाला है।');
INSERT INTO "al_umari" VALUES(25,30,'25:30','तथा रसूल[ कहेगाः हे मेरे पालनहार! मेरी जाति ने इस क़ुर्आन को त्याग दिया।');
INSERT INTO "al_umari" VALUES(25,31,'25:31','और इसी प्रकार, हमने बना दिया प्रत्येक का शत्रु, कुछ अपराधियों को और आपका पालनहार मार्गदर्शन देने तथा सहायता करने को बहुत है।');
INSERT INTO "al_umari" VALUES(25,32,'25:32','तथा काफ़िरों ने कहाः क्यों नहीं उतार दिया गया आपपर क़ुर्आन पूरा एक ही बार[? इसी प्रकार, (इसलिए किया गया) ताकि हम आपके दिल को दृढ़ता प्रदान करें और हमने इसे क्रमशः प्रस्तुत किया है। ');
INSERT INTO "al_umari" VALUES(25,33,'25:33','(और इसलिए भी कि) वे आपके पास कोई उदाहरण लायें, तो हम आपके पास सत्य ले आयें और उत्तम व्याख्या।');
INSERT INTO "al_umari" VALUES(25,34,'25:34','जो अपने मुखों के बल, नरक की ओर एकत्र किये जायेंगे, उन्हीं का सबसे बुरा स्थान है तथा सबसे अधिक कुपथ हैं।');
INSERT INTO "al_umari" VALUES(25,35,'25:35','तथा हमने ही मूसा को पुस्तक (तौरात) प्रदान की और उसके साथ उसके भाई हारून को सहायक बनाया।');
INSERT INTO "al_umari" VALUES(25,36,'25:36','फिर हमने कहाः तुम दोनों उस जाति की ओर जाओ, जिसने हमारी आयतों (निशानियों) को झुठला दिया। अन्ततः, हमने उन्हें ध्वस्त-निरस्त कर दिया।');
INSERT INTO "al_umari" VALUES(25,37,'25:37','और नूह़ की जाति ने जब रसूलों को झुठलाया, तो हमने उन्हें डुबो दिया और लोगों के लिए उन्हें शिक्षाप्रद प्रतीक बना दिया तथा हमने[ तैयार की है, अत्याचारियों के लिए दुःखदायी यातना। ');
INSERT INTO "al_umari" VALUES(25,38,'25:38','तथा आद, समूद, कुवें वालों तथा बहुत-से समूदायों को, इसके बीच।');
INSERT INTO "al_umari" VALUES(25,39,'25:39','और प्रत्येक को हमने उदाहरण दिये तथा प्रत्येक को पूर्णतः नाश कर[ दिया। ');
INSERT INTO "al_umari" VALUES(25,40,'25:40','तथा ये[ लोग उस बस्ती पर आये गये हैं, जिनपर बुरी वर्षा की गयी, तो क्या उन्होंने उसे नहीं देखा? बल्कि ये लोग पुनः जीवित होने का विश्वास नहीं रखते।');
INSERT INTO "al_umari" VALUES(25,41,'25:41','और (हे नबी!) जब वे आपको देखते हैं, तो आपको उपहास बना लेते हैं (और कहते हैं) कि क्या यही है, जिसे अल्लाह ने रसूल बनाकर भेजा है?');
INSERT INTO "al_umari" VALUES(25,42,'25:42','इसने तो हमें अपने पूज्यों से कुपथ कर दिया होता, यदि हम उनपर अडिग न रहते और वे शीघ्र ही जान लेंगे, जिस समय यातना देखेंगे कि कौन अधिक कुपथ है?');
INSERT INTO "al_umari" VALUES(25,43,'25:43','क्या आपने उसे देखा, जिसने अपना पूज्य अपनी अभिलाषा को बना लिया है, तो क्या आप उसके संरक्षक[ हो सकते हैं? ');
INSERT INTO "al_umari" VALUES(25,44,'25:44','क्या आप समझते हैं कि उनमें से अधिक्तर सुनते और समझते हैं? वे पशुओं के समान हैं, बल्कि उनसे भी अधिक कुपथ हैं। ');
INSERT INTO "al_umari" VALUES(25,45,'25:45','क्या आपने उसे नहीं देखा कि आपके पालनहार ने कैसे छाया को फैला दिया और यदि वह चाहता, तो उसे स्थिर[ बना देता, फिर हमने सूर्य को उसपर प्रमाण बना दिया।');
INSERT INTO "al_umari" VALUES(25,46,'25:46','फिर हम उस (छाया को) समेट लेते हैं, अपनी ओर धीरे-धीरे।');
INSERT INTO "al_umari" VALUES(25,47,'25:47','और वही है, जिसने रात्रि को तुम्हारे लिए वस्त्र[ बनाया तथा निद्रा को शान्ति तथा दिन को जागने का समय। ');
INSERT INTO "al_umari" VALUES(25,48,'25:48','तथा वही है, जिसने वायुयों को भेजा शुभ सूचना बनाकर, अपनी दया (वर्षा) से पूर्व तथा हमने आकाश से स्वच्छ जल बरसाया।');
INSERT INTO "al_umari" VALUES(25,49,'25:49','ताकि जीवित कर दें उसके द्वारा निर्जीव नगर को तथा उसे पिलायें उनमें से, जिन्हें हमने पैदा किया है; बहुत-से पशुओं तथा मानव को।');
INSERT INTO "al_umari" VALUES(25,50,'25:50','तथा हमने विभिन्न प्रकार से इसे वर्णन कर दिया है, ताकि वे शिक्षा ग्रहण करें। परन्तु, अधिक्तर लोगों ने अस्वीकार करते हुए कुफ़्र ग्रहण कर लिया।');
INSERT INTO "al_umari" VALUES(25,51,'25:51','और यदि हम चाहते, तो भेज देते प्रत्येक बस्ती में एक सचेत करने[ वाला। ');
INSERT INTO "al_umari" VALUES(25,52,'25:52','अतः, आप काफ़िरों की बात न मानें और इस (क़ुर्आन के) द्वारा उनसे भारी जिहाद (संघर्ष)[ करें। ');
INSERT INTO "al_umari" VALUES(25,53,'25:53','वही है, जिसने मिला दिया दो सागरों को, ये मीठा रुचिकार है और वो नमकीन खारा और उसने बना दिया दोनों के बीच एक पर्दा[ एवं रोक। ');
INSERT INTO "al_umari" VALUES(25,54,'25:54','तथा वही है, जिसने पानी (वीर्य) से मनुष्य को उत्पन्न किया, फिर उसके वंश तथा ससुराल के संबंध बना दिये, आपका पालनहार अति सामर्थ्यवान है।');
INSERT INTO "al_umari" VALUES(25,55,'25:55','और वे लोग इबादत (वंदना) करते हैं अल्लाह के सिवा उनकी, जो न उन्हें लाभ पहुँचा सकते हैं और न हानि पहुँचा सकते हैं और काफ़िर अपने पालनहार का विरोधी बन गया है।');
INSERT INTO "al_umari" VALUES(25,56,'25:56','और हमने आपको बस शुभसूचना देने, सावधान करने वाला बनाकर भेजा है।');
INSERT INTO "al_umari" VALUES(25,57,'25:57','आप कह दें: मैं इस[ पर तुमसे कोई बदला नहीं माँगता, परन्तु ये कि जो चाहे अपने पालनहार की ओर मार्ग बना ले। ');
INSERT INTO "al_umari" VALUES(25,58,'25:58','तथा आप भरोसा कीजिए उस नित्य जीवी पर, जो मरेगा नहीं और उसकी पवित्रता का गान कीजिए उसकी प्रशंसा के साथ और आपका पालनहार प्रयाप्त है, अपने भक्तों के पापों से सूचित होने को।');
INSERT INTO "al_umari" VALUES(25,59,'25:59','जिसने उत्पन्न कर दिया आकाशों तथा धरती को और जो कुछ उनके बीच है, छः दिनों में, फिर (सिंहासन) पर स्थिर हो गया, अति दयावान्, उसकी महिमा किसी ज्ञानी से पूछो।');
INSERT INTO "al_umari" VALUES(25,60,'25:60','और जब, उनसे कहा जाता है कि रह़मान (अति दयावान्) को सज्दा करो, तो कहते हैं कि रह़मान क्या है? क्या हम (उसे) सज्दा करने लगें, जिसे आप आदेश दें? और (दरअसल) इस (आमंत्रण) ने उनको और अधिक भड़का दिया।');
INSERT INTO "al_umari" VALUES(25,61,'25:61','शूभ है वह, जिसने आकाश में राशि चक्र बनाये तथा उसमें सूर्य और प्रकाशित चाँद बनाया।');
INSERT INTO "al_umari" VALUES(25,62,'25:62','वही है, जिसने रात्रि तथा दिन को, एक-दूसरे के पीछे आते-जाते बनाया, उसके लिए, जो शिक्षा ग्रहण करना चाहे या कृतज्ञ होना चाहे।');
INSERT INTO "al_umari" VALUES(25,63,'25:63','और अति दयावान् के भक्त वो हैं, जो धरती पर नम्रता से चलते[ हैं और जब अशिक्षित (अक्खड़) लोग उनसे बात करते हैं, तो सलाम करके अलग हो जाते हैं।');
INSERT INTO "al_umari" VALUES(25,64,'25:64','और जो रात्रि व्यतीत करते हैं, अपने पालनहार के लिए सज्दा करते हुए तथा खड़े[ होकर। ');
INSERT INTO "al_umari" VALUES(25,65,'25:65','तथा जो प्रार्थना करते हैं कि हे हमारे पालनहार! फेर दे हमसे नरक की यातना को, वास्तव में, उसकी यातना चिपक जाने वाली है।');
INSERT INTO "al_umari" VALUES(25,66,'25:66','वास्तव में, वह बुरा आवास और स्थान है।');
INSERT INTO "al_umari" VALUES(25,67,'25:67','तथा जो व्यय (खर्च) करते समय अपव्यय नहीं करते और न कृपण (कंजूसी) करते हैं और वह इसके बीच, संतुलित रहता है।');
INSERT INTO "al_umari" VALUES(25,68,'25:68','और जो नहीं पुकारते हैं, अल्लाह के साथ किसी दूसरे[ पूज्य को और न वध करते हैं, उस प्राण को, जिसे अल्लाह ने वर्जित किया है, परन्तु उचित कारण से और न व्यभिचार करते हैं और जो ऐसा करेगा, वह पाप का सामना करेगा। ');
INSERT INTO "al_umari" VALUES(25,69,'25:69','दुगनी की जायेगी उसके लिए यातना, प्रलय के दिन तथा सदा उसमें अपमानित[ होकर रहेगा। ');
INSERT INTO "al_umari" VALUES(25,70,'25:70','उसके सिवा, जिसने क्षमा याचना कर ली और ईमान लाया तथा कर्म किया अच्छा कर्म, तो वही हैं, बदल देगा अल्लाह, जिनके पापों को पुण्य से तथा अल्लाह अति क्षमी, दयावान् है।');
INSERT INTO "al_umari" VALUES(25,71,'25:71','और जिसने क्षमा याचना करली और सदाचार किये, तो वास्तव में, वही अल्लाह की ओर झुक जाता है।');
INSERT INTO "al_umari" VALUES(25,72,'25:72','तथा जो मिथ्या साक्ष्य नहीं देते और जब व्यर्थ के पास से गुज़रते हैं, तो सज्जन बनकर गुज़र जाते हैं।');
INSERT INTO "al_umari" VALUES(25,73,'25:73','और जब उन्हें शिक्षा दी जाये उनके पालनहार की आयतों द्वारा, उनपर नहीं गिरते अन्धे तथा बहरे हो[ कर। ');
INSERT INTO "al_umari" VALUES(25,74,'25:74','तथा जो प्रार्थना करते हैं कि हे हमारे पालनहार! हमें हमारी पत्नियों तथा संतानों से आँखों की ठंडक प्रदान कर और हमें आज्ञाकारियों का अग्रणी बना दे।');
INSERT INTO "al_umari" VALUES(25,75,'25:75','यही लोग, उच्च भवन अपने धैर्य के बदले में पायेंगे और स्वागत किये जायेंगे, उसमें आशीर्वाद तथा सलाम के साथ।');
INSERT INTO "al_umari" VALUES(25,76,'25:76','वे उसमें सदावासी होंगे। वह अच्छा निवास तथा स्थान है!');
INSERT INTO "al_umari" VALUES(25,77,'25:77','(हे नबी!) आप कह दें कि यदि तुम्हारा, उसे पुकारना न[ हो, तो मेरा पालनहार तुम्हारी क्या परवाह करेगा? तुमने तो झुठला दिया है, तो शीघ्र ही (उसका दण्ड) चिपक जाने वाला होगा। ');
INSERT INTO "al_umari" VALUES(26,1,'26:1','ता, सीन, मीम।');
INSERT INTO "al_umari" VALUES(26,2,'26:2','ये प्रकाशमय पुस्तक की आयतें हैं।');
INSERT INTO "al_umari" VALUES(26,3,'26:3','संभवतः, आप अपना प्राण[ खो देने वाले हैं कि वे ईमान लाने वाले नहीं हैं? ');
INSERT INTO "al_umari" VALUES(26,4,'26:4','यदि हम चाहें, तो उतार दें उनपर आकाश से ऐसी निशानी कि उनकी गर्दनें उसके आगे झुकी की झुकी रह जायें[। ');
INSERT INTO "al_umari" VALUES(26,5,'26:5','और नहीं आती है उनके पालनहार, अति दयावान् की ओर से कोई नई शिक्षा, परन्तु वे उससे मुख फेरने वाले बन जाते हैं।');
INSERT INTO "al_umari" VALUES(26,6,'26:6','तो उन्होंने झुठला दिया! अब उनके पास शीघ्र ही उसकी सूचनाएँ आ जायेंगी, जिसका उपहास वे कर रहे थे।');
INSERT INTO "al_umari" VALUES(26,7,'26:7','और क्या उन्होंने धरती की ओर नहीं देखा कि हमने उसमें उगाई हैं, बहुत-सी प्रत्येक प्रकार की अच्छी वनस्पतियाँ?');
INSERT INTO "al_umari" VALUES(26,8,'26:8','निश्चय ही, इसमें बड़ी निशानी (लक्षण)[ है। फिर उनमें अधिक्तर ईमान लाने वाले नहीं हैं। ');
INSERT INTO "al_umari" VALUES(26,9,'26:9','तथा वास्तव में, आपका पालनहार ही प्रभुत्वशाली, अति दयावान् है।');
INSERT INTO "al_umari" VALUES(26,10,'26:10','(उन्हें उस समय की कथा सुनाओ) जब पुकारा आपके पालनहार ने मूसा को कि जाओ अत्याचारी जाति[ के पास! ');
INSERT INTO "al_umari" VALUES(26,11,'26:11','फ़िरऔन की जाति के पास, क्या वे डरते नहीं?');
INSERT INTO "al_umari" VALUES(26,12,'26:12','उसने कहाः मेरे पालनहार! वास्तव में, मुझे भय है कि वे मुझे झुठला देंगे।');
INSERT INTO "al_umari" VALUES(26,13,'26:13','और संकुचित हो रहा है मेरा सीना और नहीं चल रही है मेरी ज़ुबान, अतः वह़्यी भेज दे हारून की ओर (भी)।');
INSERT INTO "al_umari" VALUES(26,14,'26:14','और उनका मुझपर एक अपराध भी है। अतः, मैं डरता हूँ कि वे मुझे मार डालेंगे।');
INSERT INTO "al_umari" VALUES(26,15,'26:15','अल्लाह ने कहाः कदापि ऐसा नहीं होगा। तुम दोनों हमारी निशानियाँ लेकर जाओ, हम तुम्हारे साथ सुनने[ वाले हैं। ');
INSERT INTO "al_umari" VALUES(26,16,'26:16','तो तुम दोनों जाओ और कहो कि हम विश्व के पालनहार के भेजे हुए (रसूल) हैं।');
INSERT INTO "al_umari" VALUES(26,17,'26:17','कि तू हमारे साथ बनी इस्राईल को जाने दे।');
INSERT INTO "al_umari" VALUES(26,18,'26:18','(फ़िरऔन ने) कहाः क्या हमने तेरा पालन नहीं किया है, अपने यहाँ बाल्यवस्था में और तू (नहीं) रहा है, हममें अपनी आयु के कई वर्ष?');
INSERT INTO "al_umari" VALUES(26,19,'26:19','और तू कर गया वह कार्य,[ जो किया और तू कृतघनों में से है! ');
INSERT INTO "al_umari" VALUES(26,20,'26:20','(मूसा ने) कहाः मैंने ऐसा उस समय कर दिया, जबकि मैं अनजान था।');
INSERT INTO "al_umari" VALUES(26,21,'26:21','फिर मैं तुमसे भाग गया, जब तुमसे भय हुआ। फिर प्रदान कर दिया मुझे, मेरे पालनहार ने तत्वदर्शिता और मुझे बना दिया रसूलों में से।');
INSERT INTO "al_umari" VALUES(26,22,'26:22','और ये कोई उपकार है, जो तू मुझे जता रहा है कि तूने दास बना लिया है, इस्राईल के पुत्रों को।');
INSERT INTO "al_umari" VALUES(26,23,'26:23','फ़िरऔन ने कहाः विश्व का पालनहार क्या है?');
INSERT INTO "al_umari" VALUES(26,24,'26:24','(मूसा ने) कहाः आकाशों तथा धरती और उसका पालनहार, जो कुछ दोनों के बीच है, यदि तुम विश्वास रखने वाले हो।');
INSERT INTO "al_umari" VALUES(26,25,'26:25','उसने उनसे कहा, जो उसके आस-पास थेः क्या तुम सुन नहीं रहे हो?');
INSERT INTO "al_umari" VALUES(26,26,'26:26','(मुसा ने) कहाः तुम्हारा पालनहार तथा तुम्हारे पूर्वोजों का पालनहार है।');
INSERT INTO "al_umari" VALUES(26,27,'26:27','(फ़िरऔन ने) कहाः वास्तव में, तुम्हारा रसूल, जो तुम्हारी ओर भेजा गया है, पागल है।');
INSERT INTO "al_umari" VALUES(26,28,'26:28','(मूसा ने) कहाः वह, पूर्व तथा पश्चिम तथा दोनों के मध्य जो कुछ है, सबका पालनहार है।');
INSERT INTO "al_umari" VALUES(26,29,'26:29','(फ़िरऔन ने) कहाः यदि तूने कोई पूज्य बना लिया मेरे अतिरिक्त, तो तुझे बंदियों में कर दूँगा।');
INSERT INTO "al_umari" VALUES(26,30,'26:30','(मूसा ने) कहाः क्या यद्यपि मैं ले आऊँ तेरे पास एक खुली चीज़?');
INSERT INTO "al_umari" VALUES(26,31,'26:31','उसने कहाः तू उसे ले आ, यदि सच्चा है।');
INSERT INTO "al_umari" VALUES(26,32,'26:32','फिर उसने अपनी लाठी फेंक दी, तो अकस्मात वह एक प्रत्यक्ष अजगर बन गयी।');
INSERT INTO "al_umari" VALUES(26,33,'26:33','तथा अपना हाथ निकाला, तो अकस्मात वह उज्ज्वल था, देखने वालों के लिए।');
INSERT INTO "al_umari" VALUES(26,34,'26:34','उसने अपने प्रमुखों से कहा, जो उसके पास थेः वास्तव में, ये तो बड़ा दक्ष जादूगर है।');
INSERT INTO "al_umari" VALUES(26,35,'26:35','ये चाहता है कि तुम्हें, तुम्हारी धरती से निकाल[ दे, अपने जादू के बल से, तो अब तुम क्या आदेश देते हो? ');
INSERT INTO "al_umari" VALUES(26,36,'26:36','सबने कहाः अवसर (समय) दो मूसा और उसके भाई (के विषय) को और भेज दो नगरों में एकत्र करने वालों को।');
INSERT INTO "al_umari" VALUES(26,37,'26:37','वे तुम्हारे पास प्रत्येक बड़े दक्ष जादूगर को लायें।');
INSERT INTO "al_umari" VALUES(26,38,'26:38','तो एकत्र कर लिए गये जादूगर एक निश्चित दिन के समय के लिए।');
INSERT INTO "al_umari" VALUES(26,39,'26:39','तथा लोगों से कहा गया कि क्या तुम एकत्र होने वाले[ हो? ');
INSERT INTO "al_umari" VALUES(26,40,'26:40','ताकि हम पीछे चलें जादूगरों के यदि वही प्रभुत्वशाली (विजयी) हो जायें।');
INSERT INTO "al_umari" VALUES(26,41,'26:41','और जब जादूगर आये, तो फ़िरऔन से कहाः क्या हमें कुछ पुरस्कार मिलेगा, यदि हम ही प्रभुत्वशाली होंगे?');
INSERT INTO "al_umari" VALUES(26,42,'26:42','उसने कहाः हाँ! और तुम उस समय (मेरे) समीपवर्तियों में हो जाओगे।');
INSERT INTO "al_umari" VALUES(26,43,'26:43','मूसा ने उनसे कहाः फेंको, जो कुछ तुम फेंकने वाले हो।');
INSERT INTO "al_umari" VALUES(26,44,'26:44','तो उन्होंने फेंक दी उपनी रस्सियाँ तथा अपनी लाठियाँ तथा कहाः फ़िरऔन के प्रभुत्व की शपथ! हम ही अवश्य प्रभुत्वशाली (विजयी) होंगे।');
INSERT INTO "al_umari" VALUES(26,45,'26:45','अब मूसा ने फेंक दी अपनी लाठी, तो तत्क्षण वह निगलने लगी (उसे), जो झूठ वे बना रहे थे।');
INSERT INTO "al_umari" VALUES(26,46,'26:46','तो गिर गये सभी जादूगर[ सज्दा करते हुए। ');
INSERT INTO "al_umari" VALUES(26,47,'26:47','और सबने कह दियाः हम विश्व के पालनहार पर ईमान लाये।');
INSERT INTO "al_umari" VALUES(26,48,'26:48','मूसा तथा हारून के पालनहार पर।');
INSERT INTO "al_umari" VALUES(26,49,'26:49','(फ़िरऔन ने) कहाः तुम उसका विश्वास कर बैठे, इससे पहले कि मैं तुम्हें आज्ञा दूँ? वास्तव में, वह तुम्हारा बड़ा (गुरू) है, जिसने तुम्हें जादू सिखाया है, तो तुम्हें शीघ्र ज्ञान हो जायेगा, मैं अवश्य तुम्हारे हाथों तथा पैरों को विपरीत दिशा[ से काट दूँगा तथा तुम सभी को फाँसी दे दूँगा! ');
INSERT INTO "al_umari" VALUES(26,50,'26:50','सबने कहाः कोई चिन्ता नहीं, हमतो अपने पालनहार हीकी ओर फिरकर जाने वाले हैं।');
INSERT INTO "al_umari" VALUES(26,51,'26:51','हम आशा रखते हैं कि क्षमा कर देगा, हमारे लिए, हमारा पालनहार, हमारे पापों को, क्योंकि हम सबसे पहले ईमान लाने वाले हैं।');
INSERT INTO "al_umari" VALUES(26,52,'26:52','और हमने मूसा की ओर वह़्यी की कि रातों-रात निकल जा मेरे भक्तों को लेकर, तुम सबका पीछा किया जायेगा।');
INSERT INTO "al_umari" VALUES(26,53,'26:53','तो फ़िरऔन ने भेज दिया नगरों में (सेना) एकत्र करने[ वालों को। ');
INSERT INTO "al_umari" VALUES(26,54,'26:54','कि वे बहुत थोड़े लोग हैं।');
INSERT INTO "al_umari" VALUES(26,55,'26:55','और (इसपर भी) वे हमें अति क्रोधित कर रहे हैं।');
INSERT INTO "al_umari" VALUES(26,56,'26:56','और वास्तव में, हम एक गिरोह हैं सावधान रहने वाले।');
INSERT INTO "al_umari" VALUES(26,57,'26:57','अन्ततः, हमने निकाल दिया उन्हें, बागों तथा स्रोतों से।');
INSERT INTO "al_umari" VALUES(26,58,'26:58','तथा कोषों और उत्तम निवास स्थानों से।');
INSERT INTO "al_umari" VALUES(26,59,'26:59','इसी प्रकार हुआ और हमने उनका उत्तराधिकारी बना दिया, इस्राईल की संतान को।');
INSERT INTO "al_umari" VALUES(26,60,'26:60','तो उन्होंने उनका पीछा किया, प्रातः होते ही।');
INSERT INTO "al_umari" VALUES(26,61,'26:61','और जब दोनों गिरोहों ने एक-दूसरे को देख लिया, तो मूसा के साथियों ने कहाः हमतो निश्चय ही पकड़ लिए[ गये। ');
INSERT INTO "al_umari" VALUES(26,62,'26:62','(मूसा ने) कहाः कदापि नहीं, निश्चय मेरे साथ मेरा पालनहार है।');
INSERT INTO "al_umari" VALUES(26,63,'26:63','तो हमने मूसा को वह़्यी की कि मार अपनी लाठी से सागर को, अकस्मात् सागर फट गया तथा प्रत्येक भाग, भारी पर्वत के समान[ हो गया। ');
INSERT INTO "al_umari" VALUES(26,64,'26:64','तथा हमने समीप कर दिया उसी स्थान के, दूसरे गिरोह को।');
INSERT INTO "al_umari" VALUES(26,65,'26:65','और मुक्ति प्रदान कर दी मूसा और उसके सब साथियों को।');
INSERT INTO "al_umari" VALUES(26,66,'26:66','फिर हमने डुबो दिया दूसरों को।');
INSERT INTO "al_umari" VALUES(26,67,'26:67','वास्तव में, इसमें बड़ी शिक्षा है और उनमें से अधिक्तर लोग ईमान वाले नहीं थे।');
INSERT INTO "al_umari" VALUES(26,68,'26:68','तथा वास्तव में, आपका पालनहार निश्चय अत्यंत प्रभुत्वशाली, दयावान् है।');
INSERT INTO "al_umari" VALUES(26,69,'26:69','तथा आप, उन्हें सुना दें, इब्राहीम का समाचार (भी)।');
INSERT INTO "al_umari" VALUES(26,70,'26:70','जब उसने कहा, अपने बाप तथा अपनी जाति से कि तुम क्या पूज रहे हो?');
INSERT INTO "al_umari" VALUES(26,71,'26:71','उन्होंने कहाः हम मूर्तियों की पूजा कर रहे हैं और उन्हीं की सेवा में लगे रहते हैं।');
INSERT INTO "al_umari" VALUES(26,72,'26:72','उसने कहाः क्या वे तुम्हारी सुनती हैं, जब तुम पुकारते हो?');
INSERT INTO "al_umari" VALUES(26,73,'26:73','या तुम्हें लाभ पहुँचाती या हानि पहुँचाती हैं?');
INSERT INTO "al_umari" VALUES(26,74,'26:74','उन्होंने कहाः बल्कि हमने अपने पूर्वोजों को ऐसा ही करते हुए पाया है।');
INSERT INTO "al_umari" VALUES(26,75,'26:75','उसने कहाः क्या तुमने कभी (आँख खोलकर) उसे देखा, जिसे तुम पूज रहे हो।');
INSERT INTO "al_umari" VALUES(26,76,'26:76','तुम तथा तुम्हारे पहले पूर्वज?');
INSERT INTO "al_umari" VALUES(26,77,'26:77','क्योंकि ये सब मेरे शत्रु हैं, पूरे विश्व के पालनहार के सिवा।');
INSERT INTO "al_umari" VALUES(26,78,'26:78','जिसने मुझे पैदा किया, फिर वही मुझे मार्ग दर्शा रहा है।');
INSERT INTO "al_umari" VALUES(26,79,'26:79','और जो मुझे खिलाता और पिलाता है।');
INSERT INTO "al_umari" VALUES(26,80,'26:80','और जब रोगी होता हूँ, तो वही मुझे स्वस्थ करता है।');
INSERT INTO "al_umari" VALUES(26,81,'26:81','तथा वही मुझे मारेगा, फिर[ मुझे जीवित करेगा। ');
INSERT INTO "al_umari" VALUES(26,82,'26:82','तथा मैं आशा रखता हूँ कि क्षमा कर देगा, मेरे लिए, मेरे पाप, प्रतिकार (प्रलय) के दिन।');
INSERT INTO "al_umari" VALUES(26,83,'26:83','हे मेरे पालनहार! प्रदान कर दे मुझे तत्वदर्शिता और मुझे सम्मिलित कर सदाचारियों में।');
INSERT INTO "al_umari" VALUES(26,84,'26:84','और मुझे सच्ची ख्याति प्रदान कर, आगामी लोगों में।');
INSERT INTO "al_umari" VALUES(26,85,'26:85','और बना दे मुझे, सुख के स्वर्ग का उत्तराधिकारी।');
INSERT INTO "al_umari" VALUES(26,86,'26:86','तथा मेरे बाप को क्षमा कर दे,[ वास्तव में, वह कुपथों में से है। ');
INSERT INTO "al_umari" VALUES(26,87,'26:87','तथा मुझे निरादर न कर, जिस दिन सब जीवित किये[ जायेंगे। ');
INSERT INTO "al_umari" VALUES(26,88,'26:88','जिस दिन, लाभ नहीं देगा कोई धन और न संतान।');
INSERT INTO "al_umari" VALUES(26,89,'26:89','परन्तु, जो अल्लाह के पास स्वच्छ दिल लेकर आयेगा।');
INSERT INTO "al_umari" VALUES(26,90,'26:90','और समीप कर दी जायेगी स्वर्ग आज्ञाकारियों के लिए।');
INSERT INTO "al_umari" VALUES(26,91,'26:91','तथा खोल दी जायेगी नरक कुपथों के लिए।');
INSERT INTO "al_umari" VALUES(26,92,'26:92','तथा कहा जायेगाः कहाँ हैं वे, जिन्हें तुम पूज रहे थे?');
INSERT INTO "al_umari" VALUES(26,93,'26:93','अल्लाह के सिवा, क्या वे तुम्हारी सहायता करेंगे अथवा स्वयं अपनी सहायता कर सकते हैं?');
INSERT INTO "al_umari" VALUES(26,94,'26:94','फिर उसमें औंधे झोंक दिये जायेंगे वे और सभी कुपथ।');
INSERT INTO "al_umari" VALUES(26,95,'26:95','और इब्लीस की सेना सभी।');
INSERT INTO "al_umari" VALUES(26,96,'26:96','और वे उसमें आपस में झगड़ते हुए कहंगेः');
INSERT INTO "al_umari" VALUES(26,97,'26:97','अल्लाह की शपथ! वास्तव में, हम खुले कुपथ में थे।');
INSERT INTO "al_umari" VALUES(26,98,'26:98','जब हम तुम्हें, बराबर समझ रहे थे विश्व के पालनहार के।');
INSERT INTO "al_umari" VALUES(26,99,'26:99','और हमें कुपथ नहीं किया, परन्तु अपराधियों ने।');
INSERT INTO "al_umari" VALUES(26,100,'26:100','तो हमारा कोई अभिस्तावक (सिफ़ारिशी) नहीं रह गया।');
INSERT INTO "al_umari" VALUES(26,101,'26:101','तथा न कोई प्रेमी मित्र।');
INSERT INTO "al_umari" VALUES(26,102,'26:102','तो यदि हमें पुनः संसार में जाना होता,[ तो हम ईमान वालों में हो जाते। ');
INSERT INTO "al_umari" VALUES(26,103,'26:103','निःसंदेह, इसमें बड़ी निशानी है और उनमें से अधिक्तर ईमान लाने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(26,104,'26:104','और वास्तव में, आपका पालनहार ही अति प्रभुत्वशाली,[ दयावान् है। ');
INSERT INTO "al_umari" VALUES(26,105,'26:105','नूह़ की जाति ने भी रसूलों को झुठलाया।');
INSERT INTO "al_umari" VALUES(26,106,'26:106','जब उनसे उनके भाई नूह़ ने कहाः क्या तुम (अल्लाह से) डरते नहीं हो?');
INSERT INTO "al_umari" VALUES(26,107,'26:107','वास्तव में, मैं तुम्हारे लिए एक[ रसूल हूँ। ');
INSERT INTO "al_umari" VALUES(26,108,'26:108','अतः, तुम अल्लाह से डरो और मेरी बात मानो।');
INSERT INTO "al_umari" VALUES(26,109,'26:109','मैं नहीं माँगता इसपर तुमसे कोई पारिश्रमिक (बदला), मेरा बदला तो बस सर्वलोक के पालनहार पर है।');
INSERT INTO "al_umari" VALUES(26,110,'26:110','अतः, तुम अल्लाह से डरो और मेरी आज्ञा का पालन करो।');
INSERT INTO "al_umari" VALUES(26,111,'26:111','उन्होंने कहाः क्या हम तुझे मान लें, जबकि तेरा अनुसरण पतित (नीच) लोग[ कर रहे हैं? ');
INSERT INTO "al_umari" VALUES(26,112,'26:112','(नूह़ ने) कहाः मूझे क्य ज्ञान कि वे क्या कर्म करते रहे हैं?');
INSERT INTO "al_umari" VALUES(26,113,'26:113','उनका ह़िसाब तो बस मेरे पालनहार के ऊपर है, यदि तुम समझो।');
INSERT INTO "al_umari" VALUES(26,114,'26:114','और मैं धुतकारने वाला[ नहीं हूँ, ईमान वालों को। ');
INSERT INTO "al_umari" VALUES(26,115,'26:115','मैं तो बस खुला सावधान करने वाला हूँ।');
INSERT INTO "al_umari" VALUES(26,116,'26:116','उन्होंने कहाः यदि रुका नहीं, हे नूह़! तो तू अवश्य पथराव करके मारे हुओं में होगा।');
INSERT INTO "al_umari" VALUES(26,117,'26:117','उसने कहाः मेरे पालनहार! मेरी जाति ने मुझे झुठला दिया।');
INSERT INTO "al_umari" VALUES(26,118,'26:118','अतः, तू निर्णय कर दे मेरे और उनके बीच और मुक्त कर दे मुझे तथा उन्हें जो मेरे साथ हैं, ईमान वालों में से।');
INSERT INTO "al_umari" VALUES(26,119,'26:119','तो हमने उसे मुक्त कर दिया तथा उन्हें जो उसके साथ भरी नाव में थे।');
INSERT INTO "al_umari" VALUES(26,120,'26:120','फिर हमने डुबो दिया उसके पश्चात्, शेष लोगों को।');
INSERT INTO "al_umari" VALUES(26,121,'26:121','वास्तव में, इसमें एक बड़ी निशानी (शिक्षा) है तथा उनमें से अधिक्तर ईमान लाने वाले नहीं।');
INSERT INTO "al_umari" VALUES(26,122,'26:122','और निश्चय आपका पालनहार ही अति प्रभुत्वशाली, दयावान् है।');
INSERT INTO "al_umari" VALUES(26,123,'26:123','झुठला दिया आद (जाति) ने (भी) रसूलों को।');
INSERT INTO "al_umari" VALUES(26,124,'26:124','जब कहा उनसे, उनके भाई हूद[ नेः क्या तुम डरते नहीं हो? ');
INSERT INTO "al_umari" VALUES(26,125,'26:125','वस्तुतः, मैं तुम्हारे लिए एक न्यासिक (अमानतदार) रसूल हूँ।');
INSERT INTO "al_umari" VALUES(26,126,'26:126','अतः, अल्लाह से डरो और मेरा अनुपालन करो।');
INSERT INTO "al_umari" VALUES(26,127,'26:127','और मैं तुमसे कोई पारिश्रमिक (बदला) नहीं माँगता, मेरा बदला तो बस सर्वलोक के पालनहार पर है।');
INSERT INTO "al_umari" VALUES(26,128,'26:128','क्यों तुम बना लेते हो, हर ऊँचे स्थान पर एक यादगार भवन, व्यर्थ में?');
INSERT INTO "al_umari" VALUES(26,129,'26:129','तथा बनाते हो, बड़े-बड़े भवन, जैसे कि तुम सदा रहोगे।');
INSERT INTO "al_umari" VALUES(26,130,'26:130','और जबकिसी को पकड़ते हो, तो पकड़ते हो, महा अत्याचारी बनकर।');
INSERT INTO "al_umari" VALUES(26,131,'26:131','तो अल्लाह से डरो और मेरी आज्ञा का पालन करो।');
INSERT INTO "al_umari" VALUES(26,132,'26:132','तथा उससे भय रखो, जिसने तुम्हारी सहायता की है उससे, जो तुम जानते हो।');
INSERT INTO "al_umari" VALUES(26,133,'26:133','उसने सहायता की है तुम्हारी चौपायों तथा संतान से।');
INSERT INTO "al_umari" VALUES(26,134,'26:134','तथा बाग़ों (उद्यानो) तथा जल स्रोतों से।');
INSERT INTO "al_umari" VALUES(26,135,'26:135','मैं तुमपर डरता हूँ, भीषण दिन की यातना से।');
INSERT INTO "al_umari" VALUES(26,136,'26:136','उन्होंने कहाः नसीह़त करो या न करो, हमपर सब समान है।');
INSERT INTO "al_umari" VALUES(26,137,'26:137','ये बात तो बस प्राचीन लोगों की नीति[ है। ');
INSERT INTO "al_umari" VALUES(26,138,'26:138','और हम उनमें से नहीं हैं, जिन्हें यातना दी जायेगी।');
INSERT INTO "al_umari" VALUES(26,139,'26:139','अन्ततः, उन्होंने हमें झुठला दिया, तो हमने उन्हें ध्वस्त कर दिया। निश्चय इसमें एक बड़ी निशानी (शिक्षा) है और लोगों में अधिक्तर ईमान लाने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(26,140,'26:140','और वास्तव में, आपका पालनहार ही अत्यंत प्रभुत्वशाली, दयावान् है।');
INSERT INTO "al_umari" VALUES(26,141,'26:141','झुठला दिया समूद ने (भी)[ रसूलों को। ');
INSERT INTO "al_umari" VALUES(26,142,'26:142','जब कहा उनसे उनके भाई सालेह़ नेः क्या तुम डरते नहीं हो?');
INSERT INTO "al_umari" VALUES(26,143,'26:143','वास्तव में, मैं तुम्हारा विश्वसनीय रसूल हूँ।');
INSERT INTO "al_umari" VALUES(26,144,'26:144','तो तुम अल्लाह से डरो और मेरा कहा मानो।');
INSERT INTO "al_umari" VALUES(26,145,'26:145','तथा मैं नहीं माँगता इसपर तुमसे कोई पारिश्रमिक, मेरा पारिश्रमिक तो बस सर्वलोक के पालनहार पर है।');
INSERT INTO "al_umari" VALUES(26,146,'26:146','क्या तुम छोड़ दिये जाओगे उसमें, जो यहाँ हैं निश्चिन्त रहकर?');
INSERT INTO "al_umari" VALUES(26,147,'26:147','बाग़ों तथा स्रोतों में।');
INSERT INTO "al_umari" VALUES(26,148,'26:148','तथा खेतों और खजूरों में, जिनके गुच्छे रस भरे हैं।');
INSERT INTO "al_umari" VALUES(26,149,'26:149','तथा तुमपर्वतों को तराशकर घर बनाते हो, गर्व करते हुए।');
INSERT INTO "al_umari" VALUES(26,150,'26:150','अतः, अल्लाह से डरो और मेरा अनुपालन करो।');
INSERT INTO "al_umari" VALUES(26,151,'26:151','और पालन न करो उल्लंघनकारियों के आदेश का।');
INSERT INTO "al_umari" VALUES(26,152,'26:152','जो उपद्रव करते हैं धरती में और सुधार नहीं करते।');
INSERT INTO "al_umari" VALUES(26,153,'26:153','उन्होंने कहाः वास्तव में, तू उनमें से है, जिनपर जादू कर दिया गया है।');
INSERT INTO "al_umari" VALUES(26,154,'26:154','तू तो बस हमारे समान एक मानव है। तो कोई चमत्कार ले आ, यदि तू सच्चा है।');
INSERT INTO "al_umari" VALUES(26,155,'26:155','कहाः ये ऊँटनी है,[ इसके लिए पानी पीने का एक दिन है और तुम्हारे लिए पानी लेने का निश्चित दिन है। ');
INSERT INTO "al_umari" VALUES(26,156,'26:156','तथा उसे हाथ न लगाना बुराई से, अन्यथा तुम्हें पकड़ लेगी एक भीषण दिन की यातना।');
INSERT INTO "al_umari" VALUES(26,157,'26:157','तो उन्होंने वध कर दिया उसे, अन्ततः, पछताने वाले हो गये।');
INSERT INTO "al_umari" VALUES(26,158,'26:158','और पकड़ लिया उन्हें यातना ने। वस्तुतः, इसमें बड़ी निशानी है और नहीं थे उनमें से अधिक्तर ईमान वाले।');
INSERT INTO "al_umari" VALUES(26,159,'26:159','और निश्चय आपका पालनहार ही अत्यंत प्रभुत्वशाली, दयावान् है।');
INSERT INTO "al_umari" VALUES(26,160,'26:160','झुठला दिया लूत की जाति ने (भी) रसूलों को।');
INSERT INTO "al_umari" VALUES(26,161,'26:161','जब कहा उनसे उनके भाई लूत नेः क्या तुम डरते नहीं हो?');
INSERT INTO "al_umari" VALUES(26,162,'26:162','वास्तव में, मैं तुम्हारे लिए एक अमानतदार रसूल हूँ।');
INSERT INTO "al_umari" VALUES(26,163,'26:163','अतः अल्लाह से डरो और मेरा अनुपालन करो।');
INSERT INTO "al_umari" VALUES(26,164,'26:164','और मैं तुमसे प्रश्न नहीं करता, इसपर किसी पारिश्रमिक (बदले) का। मेरा बदला तो बस सर्वलोक के पालनहार पर है।');
INSERT INTO "al_umari" VALUES(26,165,'26:165','क्या तुम जाते[ हो पुरुषों के पास, संसार वासियों में से। ');
INSERT INTO "al_umari" VALUES(26,166,'26:166','तथा छोड़ देते हो उसे, जिसे पैदा किया है तुम्हारे पालनहार ने, अर्थात अपनी प्तनियों को, बल्कि तुम एक जाति हो, सीमा का उल्लंघन करने वाली।');
INSERT INTO "al_umari" VALUES(26,167,'26:167','उन्होंने कहाः यदि तू नहीं रुका, हे लूत! तो अवश्य तेरा बहिष्कार कर दिया जायेगा।');
INSERT INTO "al_umari" VALUES(26,168,'26:168','उसने कहाः वास्तव में, मैं तुम्हारे करतूत से बहुत अप्रसन्न हूँ।');
INSERT INTO "al_umari" VALUES(26,169,'26:169','मेरे पालनहार! मुझे बचा ले तथा मेरे परिवार को उससे, जो वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(26,170,'26:170','तो हमने उसे बचा लिया तथा उसके सभी परिवार को।');
INSERT INTO "al_umari" VALUES(26,171,'26:171','परन्तु, एक बुढ़िया[ को, जो पीछे रह जाने वालों में थी। ');
INSERT INTO "al_umari" VALUES(26,172,'26:172','फिर हमने विनाश कर दिया दूसरों का।');
INSERT INTO "al_umari" VALUES(26,173,'26:173','और वर्षा की उनपर, एक घोर[ वर्षा। तो बुरी हो गयी डराये हुए लोगों की वर्षा। ');
INSERT INTO "al_umari" VALUES(26,174,'26:174','वास्तव में, इसमें एक बड़ी निशानी (शिक्षा) है और उनमें से अधिक्तर ईमान लाने वाले नहीं थे।');
INSERT INTO "al_umari" VALUES(26,175,'26:175','और निश्चय आपका पालनहार ही अत्यंत प्रभुत्वशाली, दयावान् है।');
INSERT INTO "al_umari" VALUES(26,176,'26:176','झुठला दिया ऐय्का[ वालों ने रसूलों को। ');
INSERT INTO "al_umari" VALUES(26,177,'26:177','जब कहा उनसे शोऐब नेः क्या तुम डरते नहीं हो?');
INSERT INTO "al_umari" VALUES(26,178,'26:178','मैं तुम्हारे लिए एक विश्वसनीय रसूल हूँ।');
INSERT INTO "al_umari" VALUES(26,179,'26:179','अतः, अल्लाह से डरो तथा मेरी आज्ञा का पालन करो।');
INSERT INTO "al_umari" VALUES(26,180,'26:180','और मैं नहीं माँगता तुमसे इसपर कोई पारिश्रमिक, मेरा पारिश्रमिक तो बस समस्त विश्व के पालनहार पर है।');
INSERT INTO "al_umari" VALUES(26,181,'26:181','तुम नाप-तोल पूरा करो और न बनो कम देने वालों में।');
INSERT INTO "al_umari" VALUES(26,182,'26:182','और तोलो सीधी तराज़ू से।');
INSERT INTO "al_umari" VALUES(26,183,'26:183','और मत कम दो लोगों को उनकी चीज़ें और मत फिरो धरती में उपद्रव फैलाते।');
INSERT INTO "al_umari" VALUES(26,184,'26:184','और डरो उससे, जिसने पैदा किया है तुम्हें तथा अगले लोगों को।');
INSERT INTO "al_umari" VALUES(26,185,'26:185','उन्होंने कहाःवास्तव में, तू उनमें से है, जिनपर जादू कर दिया गया है।');
INSERT INTO "al_umari" VALUES(26,186,'26:186','और तू तो बस एक पुरुष[ है, हमारे समान और हम तो तुझे झूठों में समझते हैं। ');
INSERT INTO "al_umari" VALUES(26,187,'26:187','तो हमपर गिरा दे कोई खण्ड आकाश का, यदि तू सच्चा है।');
INSERT INTO "al_umari" VALUES(26,188,'26:188','उसने कहाः मेरा पालनहार भली प्रकार जानता है उसे, जो कुछ तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(26,189,'26:189','तो उन्होंने उसे झुठला दिया। अन्ततः, पकड़ लिया उन्हें छाया के[ दिन की यातना ने। वस्तुतः, वह एक भीषण दिन की यातना थी। ');
INSERT INTO "al_umari" VALUES(26,190,'26:190','निश्चय ही, इसमें एक बड़ी निशानी (शिक्षा) है और नहीं थे उनमें अधिक्तर ईमान लाने वाले।');
INSERT INTO "al_umari" VALUES(26,191,'26:191','और वास्तव में, आपका पालनहार ही अत्यंत प्रभुत्वशाली, दयावान् है।');
INSERT INTO "al_umari" VALUES(26,192,'26:192','तथा निःसंदेह, ये (क़ुर्आन) पूरे विश्व के पालनहार का उतारा हुआ है।');
INSERT INTO "al_umari" VALUES(26,193,'26:193','इसे लेकर रूह़ुल अमीन[ उतरा। ');
INSERT INTO "al_umari" VALUES(26,194,'26:194','आपके दिल पर, ताकि आप हो जायें सावधान करने वालों में।');
INSERT INTO "al_umari" VALUES(26,195,'26:195','खुली अरबी भाषा में।');
INSERT INTO "al_umari" VALUES(26,196,'26:196','तथा इसकी चर्चा[ अगले रसूलों की पुस्तकों में (भी) है। ');
INSERT INTO "al_umari" VALUES(26,197,'26:197','क्या और उनके लिए ये निशानी नहीं है कि इस्राईलियों के विद्वान[ इसे जानते हैं। ');
INSERT INTO "al_umari" VALUES(26,198,'26:198','और यदि हम इसे उतार देते किसी अजमी[ पर। ');
INSERT INTO "al_umari" VALUES(26,199,'26:199','और वह, इसे उनके समक्ष पढ़ता, तो वे उसपर ईमान लाने वाले न होते[। ');
INSERT INTO "al_umari" VALUES(26,200,'26:200','इसी प्रकार, हमने घुसा दिया है इस (क़ुर्आन के इन्कार) को पापियों के दिलों में।');
INSERT INTO "al_umari" VALUES(26,201,'26:201','वे नहीं ईमान लायेंगे उसपर, जब तक देख नहीं लेंगे दुःखदायी यातना।');
INSERT INTO "al_umari" VALUES(26,202,'26:202','फिर, वह उनपर सहसा आ जायेगी और वे समझ भी नहीं पायेंगे।');
INSERT INTO "al_umari" VALUES(26,203,'26:203','तो कहेंगेः क्या हमें अवसर दिया जायेगा?');
INSERT INTO "al_umari" VALUES(26,204,'26:204','तो क्या वे हमारी यातना की जल्दी मचा रहे हैं?');
INSERT INTO "al_umari" VALUES(26,205,'26:205','(हे नबी!) तो क्या आपने विचार किया कि यदि हम लाभ पहुँचायें इन्हें वर्षों।');
INSERT INTO "al_umari" VALUES(26,206,'26:206','फिर आ जाये उनपर वह, जिसकी उन्हें धमकी दी जा रही थी।');
INSERT INTO "al_umari" VALUES(26,207,'26:207','तो कुछ काम नहीं आयेगा उनके, जो उन्हें लाभ पहुँचाया जाता रहा?');
INSERT INTO "al_umari" VALUES(26,208,'26:208','और हमने किसी बस्ती का विनाश नहीं किया, परन्तु उसके लिए सावधान करने वाले थे।');
INSERT INTO "al_umari" VALUES(26,209,'26:209','शिक्षा देने के लिए और हम अत्याचारी नहीं हैं।');
INSERT INTO "al_umari" VALUES(26,210,'26:210','तथा नहीं उतरे हैं (इस क़ुर्आन) को लेकर शैतान।');
INSERT INTO "al_umari" VALUES(26,211,'26:211','और न योग्य है उनके लिए और न वे इसकी शक्ति रखते हैं।');
INSERT INTO "al_umari" VALUES(26,212,'26:212','वास्तव में, वे तो (इसके) सुनने से भी दूर[ कर दिये गये हैं। ');
INSERT INTO "al_umari" VALUES(26,213,'26:213','अतः, आप न पुकारें अल्लाह के साथ किसी अन्य पूज्य को, अन्यथा आप दण्डितों में हो जायेंगे।');
INSERT INTO "al_umari" VALUES(26,214,'26:214','और आप सावधान कर दें अपने समीपवर्ती[ संबंधियों को। ');
INSERT INTO "al_umari" VALUES(26,215,'26:215','और झुका दें अपना बाहु[ उसके लिए, जो आपका अनुयायी हो, ईमान वालों में से। ');
INSERT INTO "al_umari" VALUES(26,216,'26:216','और यदि वे आपकी अवज्ञा करें, तो आप कह दें कि मैं निर्दोष हूँ उससे, जो तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(26,217,'26:217','तथा आप भरोसा करें अत्यंत प्रभुत्वशाली, दयावान् पर।');
INSERT INTO "al_umari" VALUES(26,218,'26:218','जो देखता है आपको, जिस समय (नमाज़) में खड़े होते हैं।');
INSERT INTO "al_umari" VALUES(26,219,'26:219','और आपके फिरने को सज्दा करने[ वालों में। ');
INSERT INTO "al_umari" VALUES(26,220,'26:220','निःसंदेह, वही सब कुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(26,221,'26:221','क्या मैं तुम सबको बताऊँ कि किसपर शैतान उतरते हैं?');
INSERT INTO "al_umari" VALUES(26,222,'26:222','वे उतरते हैं, प्रत्येक झूठे पापी[ पर। ');
INSERT INTO "al_umari" VALUES(26,223,'26:223','वे पहुँचा देते हैं, सुनी सुनाई बातों को और उनमें अधिक्तर झूठे हैं।');
INSERT INTO "al_umari" VALUES(26,224,'26:224','और कवियों का अनुसरण बहके हुए लोग करते हैं।');
INSERT INTO "al_umari" VALUES(26,225,'26:225','क्या आप नहीं देखते कि वे प्रत्येक वादी में फिरते[ हैं। ');
INSERT INTO "al_umari" VALUES(26,226,'26:226','और ऐसी बात कहते हैं, जो करते नहीं।');
INSERT INTO "al_umari" VALUES(26,227,'26:227','परन्तु वो (कवि), जो[ ईमान लाये, सदाचार किये, अल्लाह का बहुत स्मरण किया तथा बदला लिया इसके पश्चात् कि उनके ऊपर अत्याचार किया गया! तथा शीघ्र ही जान लेंगे, जिन्होंने अत्याचार किया है कि व किस दुष्परिणाम की ओर फिरते हैं! ');
INSERT INTO "al_umari" VALUES(27,1,'27:1','ता, सीन, मीम। ये क़ुर्आन तथा प्रत्यक्ष पुस्तक की आयतें हैं।');
INSERT INTO "al_umari" VALUES(27,2,'27:2','मार्गदर्शन तथा शुभ सूचना है, उन ईमान वालों के लिए।');
INSERT INTO "al_umari" VALUES(27,3,'27:3','जो नमाज़ की स्थापना करते तथा ज़कात देते हैं और वही हैं, जो अन्तिम दिन (परलोक) पर विश्वास रखते हैं।');
INSERT INTO "al_umari" VALUES(27,4,'27:4','वास्तव में, जो विश्वास नहीं करते परलोक पर, हमने शोभनीय बना दिया है उनके कर्मों को, इसलिए वे बहके जा रहे हैं।');
INSERT INTO "al_umari" VALUES(27,5,'27:5','यही हैं, जिनके लिए बुरी यातना है तथा परलोक में यही सर्वाधिक क्षतिग्रस्त रहने वाले हैं।');
INSERT INTO "al_umari" VALUES(27,6,'27:6','और (हे नबी!) वास्तव में, आपको दिया जा रहा है क़ुर्आन एक तत्वज्ञ, सर्वज्ञ की ओर से।');
INSERT INTO "al_umari" VALUES(27,7,'27:7','(याद करो) जब कहा[ मूसा ने अपने परिजनों सेः मैंने आग देखी है, मैं तुम्हारे पास कोई सूचना लाऊँगा या लाऊँगा आग का कोई अंगार, ताकि तुम तापो। ');
INSERT INTO "al_umari" VALUES(27,8,'27:8','फिर जब आया वहाँ, तो पुकारा गयाः शुभ है वह, जो अग्नि में है और जो उसके आस-पास है और पवित्र है अल्लाह, सर्वलोक का पालनहार।');
INSERT INTO "al_umari" VALUES(27,9,'27:9','हे मूसा! ये मैं हूँ, अल्लाह, अति प्रभुत्वशाली, तत्वज्ञ।');
INSERT INTO "al_umari" VALUES(27,10,'27:10','और फेंक दे अपनी लाठी, फिर जब उसे देखा कि रेंग रही है, जैसे वह कोई सर्प हो, तो पीठ फेरकर भागा और पीछे फिरकर देखा भी नहीं। (हमने कहाः) हे मूसा! भय न कर, वास्तव में, नहीं भय करते मेरे पास रसूल।');
INSERT INTO "al_umari" VALUES(27,11,'27:11','उसके सिवा, जिसने अत्याचार किया हो, फिर जिसने बदल लिया अपना कर्म भलाई से बुराई के पश्चात्, तो निश्चय, मैं अति क्षमि, दयावान् हूँ।');
INSERT INTO "al_umari" VALUES(27,12,'27:12','और डाल दे अपना हाथ अपनी जेब में, वह निकलेगा उज्ज्वल होकर बिना किसी रोग के; नौ निशानियों में से है, फ़िरऔन तथा उसकी जाति की ओर (ले जाने के लिए) वास्तव में, वे उल्लंघनकारियों में हैं।');
INSERT INTO "al_umari" VALUES(27,13,'27:13','फिर जब आयीं उनके पास हमारी निशानियाँ, आँख खोलने वाली, तो कह दिया कि ये तो खुला जादू है।');
INSERT INTO "al_umari" VALUES(27,14,'27:14','तथा उन्होंने नकार दिया उन्हें, अत्याचार तथा अभिमान के कारण, जबकि उनके दिलों ने उनका विश्वास कर लिया, तो देखो कि कैसा रहा उपद्रवियों का परिणाम?');
INSERT INTO "al_umari" VALUES(27,15,'27:15','और हमने प्रदान किया दावूद तथा सुलैमान को ज्ञान[ और दोनों ने कहाः प्रशंसा है, उस अल्लाह के लिए, जिसने हमें प्रधानता दी अपने बहुत-से ईमान वाले भक्तों पर। ');
INSERT INTO "al_umari" VALUES(27,16,'27:16','और उत्तराधिकारी हुआ सुलैमान दावूद का तथा उसने कहाः हे लोगो! हमें सिखाई गयी है पक्षियों की बोली तथा हमें प्रदान की गयी है, सब चीज़ से कुछ। वास्तव में, ये प्रत्यक्ष अनुग्रह है।');
INSERT INTO "al_umari" VALUES(27,17,'27:17','तथा एकत्र कर दी गयी सुलैमान के लिए उसकी सेनायें; जिन्नों तथा मानवों और पक्षी की और वे व्यवस्थित रखे जाते थे।');
INSERT INTO "al_umari" VALUES(27,18,'27:18','यहाँ तक कि वे (एक बार) जब पहुँचे च्युंटियों की घाटी पर, तो एक च्युंटी ने कहाः हे च्युटियो! प्रवेश कर जाओ अपने घरों में, ऐसा न हो कि तुम्हें कुचल दे सुलैमान तथा उसकी सेनायें और उन्हें ज्ञान न हो।');
INSERT INTO "al_umari" VALUES(27,19,'27:19','तो वह (सुलैमान) मुस्कुराकर हँस पड़ा उसकी बात पर और कहाः हे मेरे पालनहार! मुझे क्षमता प्रदानकर कि मैं कृतज्ञ रहूँ तेरे उस पुरस्कार का, जो पुरस्कार तूने मुझपर तथा मेरे माता-पिता पर किया है तथा ये कि मैं सदाचार करता रहूँ, जिससे तू प्रसन्न रहे और मुझे प्रवेश दे अपनी दया से, अपने सदाचारी भक्तों में।');
INSERT INTO "al_umari" VALUES(27,20,'27:20','और उसने निरीक्षण किया पक्षियों का, तो कहाः क्या बात है कि मैं नहीं देख रहा हूँ हुदहुद को या वह अनुपस्थितों में है?');
INSERT INTO "al_umari" VALUES(27,21,'27:21','मैं उसे कड़ी यातना दूँगा या उसे वध कर दूँगा या मेरे पास कोई खुला प्रमाण लाये।');
INSERT INTO "al_umari" VALUES(27,22,'27:22','तो कुछ अधिक समय नहीं बीता कि उसने (आकर) कहाः मैंने ऐसी बात का ज्ञान प्राप्त किया है, जो आपके ज्ञान में नहीं आयी है और मैं लाया हूँ, आपके पास "सबा"[ से एक विश्वसनीय सूचना। ');
INSERT INTO "al_umari" VALUES(27,23,'27:23','मैंने एक स्त्री को पाया, जो उनपर राज्य कर रही है और उसे प्रदान किया गया है कुछ न कुछ प्रत्येक वस्तु से तथा उसके पास एक बड़ा भव्य सिंहासन है।');
INSERT INTO "al_umari" VALUES(27,24,'27:24','मैंने उसे तथा उसकी जाति को पाया कि सज्दा करते हैं सूर्य को अल्लाह के सिवा और शोभनीय बना दिया है, उनके लिए शैतान ने उनके कर्मों को और उन्हें रोक दिया है सुपथ से, अतः, वे सुपथ पर नहीं आते।');
INSERT INTO "al_umari" VALUES(27,25,'27:25','(शैतान ने शोभनीय बना दिया है उनके लिए) कि उस अल्लाह को सज्दा न करें, जो निकालता है गुप्त वस्तु को[ आकाशों तथा धरती में तथा जानता है वह सब कुछ, जिसे तुम छुपाते हो तथा जिसे व्यक्त करते हो। ');
INSERT INTO "al_umari" VALUES(27,26,'27:26','अल्लाह जिसके अतिरिक्त कोई वंदनीय नहीं, जो महा सिंहासन का स्वामी है।');
INSERT INTO "al_umari" VALUES(27,27,'27:27','(सुलैमान ने) कहाः हम देखेंगे कि तू सत्यवादी है अथवा मिथ्यावादियों में से है।');
INSERT INTO "al_umari" VALUES(27,28,'27:28','जाओ, ये मेरा पत्र लेकर और इसे डाल दो उनकी ओर, फिर वापस आ जाओ उनके पास से, फिर देखो कि वे क्या उत्तर देते हैं?');
INSERT INTO "al_umari" VALUES(27,29,'27:29','उसने कहाः हे प्रमुखो! मेरी ओर एक महत्वपूरण पत्र डाला गया है।');
INSERT INTO "al_umari" VALUES(27,30,'27:30','वह सुलैमान की ओर से है और वह अत्यंत कृपाशील दयावान् के नाम से (आरंभ) है।');
INSERT INTO "al_umari" VALUES(27,31,'27:31','(उसमें लिखा है) कि तुम मुझपर अभिमान न करो तथा आ जाओ मेरे पास, आज्ञाकारी होकर।');
INSERT INTO "al_umari" VALUES(27,32,'27:32','उसने कहाः हे प्रमुखो! मुझे प्रामर्श दो मेरे विषय में, मैं कोई निर्णय करने वाला नहीं हूँ, जब तक तुम उपस्थित न रहो।');
INSERT INTO "al_umari" VALUES(27,33,'27:33','सबने उत्तर दिया कि हम शक्तिशाली तथा बड़े याध्दा हैं, आप स्वयं देख लें कि आपको क्या आदेश देना है।');
INSERT INTO "al_umari" VALUES(27,34,'27:34','उसने कहाः राजा जब प्रवेश करते हैं, किसी बस्ती में, तो उसे उजाड़ देते हैं और उसके आदरणीय वासियों को अपमानित बना देते हैं और वे ऐसा ही करेंगे।');
INSERT INTO "al_umari" VALUES(27,35,'27:35','और मैं भेजने वाली हूँ उनकी ओर एक उपहार, फिर देखती हूँ कि क्या लेकर आते हैं दूत?');
INSERT INTO "al_umari" VALUES(27,36,'27:36','तो जब वह (दूत) आया सुलैमान के पास, तो कहाः क्या तुम मेरी सहायता धन से करते हो? मुझे अल्लाह ने जो दिया है, उससे उत्तम है, जो तुम्हें दिया है, बल्कि तुम्हीं अपने उपहार से प्रसन्न हो रहे हो।');
INSERT INTO "al_umari" VALUES(27,37,'27:37','वापस हो जाओ उनकी ओर, हम लायेंगे उनके पास ऐसी सेनाएँ, जिनका वे सामना नहीं कर सकेंगे और हम अवश्य उन्हें उस (बस्ती) से निकाल देंगे, अपमानित करके और वे तुच्छ (हीन) होकर रहेंगे।');
INSERT INTO "al_umari" VALUES(27,38,'27:38','सुलैमान ने कहाः हे प्रमुखो! तुममें से कौन लायेगा[ उसका सिंहासन, इससे पहले कि वह आ जाये, आज्ञाकारा होकर। ');
INSERT INTO "al_umari" VALUES(27,39,'27:39','कहा एक अतिकाय ने, जिन्नों में सेः मैं ला दूँगा, आपके पास उसे, इससे पूर्व कि आप खड़े हों अपने स्थान से और इसपर मुझे शक्ति है, मैं विश्वसनीय हूँ।');
INSERT INTO "al_umari" VALUES(27,40,'27:40','कहा उसने, जिसके पास पुस्तक का ज्ञान थाः मैं ले आऊँगा उसे, आपके पास इससे पहले कि आपकी पलक झपके और जब देखा उसने उसे, अपने पास रखा हुआ, तो कहाः ये मेरे पालनहार का अनुग्रह है, ताकि मेरी परीक्षा ले कि मैं कृतज्ञता दिखाता हूँ या कृतघ्नता और जो कृतज्ञ होता है, वह अपने लाभ के लिए होता है तथा जो कृतघ्न हो, तो निश्चय मेरा पालनहार निस्पृह, महान है।');
INSERT INTO "al_umari" VALUES(27,41,'27:41','कहाः परिवर्तन कर दो उसके लिए उसके सिंहासन में, हम देखेंगे कि वह उसे पहचान जाती है या उनमें से हो जाती है, जो पहचानते न हों।');
INSERT INTO "al_umari" VALUES(27,42,'27:42','तो जब वह आई, तो कहा गयाः क्या ऐसा ही तेरा सिंहासन है? उसने कहाः वह तो मानो वही है और हम तो जान गये थे इससे पहले ही और आज्ञाकारी हो गये थे।');
INSERT INTO "al_umari" VALUES(27,43,'27:43','और रोक रखा था उसे (ईमान से) उन (पूज्यों) ने जिनकी वह इबादत ( वंदना) कर रही थी अल्लाह के सिवा। निश्चय वह काफ़िरों की जाति में से थी।');
INSERT INTO "al_umari" VALUES(27,44,'27:44','उससे कहा गया कि भवन में प्रवेश कर। तो जब उसे देखा, तो उसे कोई जलाशय (हौद) समझी और खोल दीं[ अपनी दोनों पिंडलियाँ, (सुलैमान ने) कहाः ये शीशे से निर्मित भवन है। उसने कहाः मेरे पालनहार! मैंने अत्याचार किया अपने प्राण पर और (अब) मैं इस्लाम लाई सुलैमान के साथ अल्लाह सर्वलोक के पालनहार के लिए।');
INSERT INTO "al_umari" VALUES(27,45,'27:45','और हमने भेजा समूद की ओर उनके भाई सालेह़ को कि तुम सब इबादत (वंदना) करो अल्लाह की, तो अकस्मात् वे दो गिरोह होकर लड़ने लगे।');
INSERT INTO "al_umari" VALUES(27,46,'27:46','उसने कहाः हे मेरी जाति! क्यों तुम शीघ्र चाहते हो बुराई को[ भलाई से पहले? क्यों तुम क्षमा नहीं माँगते अल्लाह से, ताकि तुमपर दया की जाये? ');
INSERT INTO "al_umari" VALUES(27,47,'27:47','उन्होंने कहाः हमने अपशकुन लिया है तुमसे तथा उनसे, जो तेरे साथ हैं। (सालेह़ ने) कहाः तुम्हारा अपशकुन अल्लाह के पास[ है, बल्कि तुम लोगों की परीक्षा हो रही है। ');
INSERT INTO "al_umari" VALUES(27,48,'27:48','और उस नगर में नौ व्यक्तियों का एक गिरोह था, जो उपद्रव करते थे धरती में और सुधार नहीं करते थे।');
INSERT INTO "al_umari" VALUES(27,49,'27:49','उन्होंने कहाः आपस में शपथ लो अल्लाह की कि हम अवश्य रात्रि में छापा मार देंगे सालेह़ तथा उसके परिवार पर, फिर कहेंगे उस (सालेह़) के उत्तराधिकारी से, हम उपस्थित नहीं थे, उसके परिवार के विनाश के समय और निःसंदेह, हम सत्यवादी ( सच्चे) हैं।');
INSERT INTO "al_umari" VALUES(27,50,'27:50','और उन्होंने एक षड्यंत्र रचा और हमने भी एक उपाय किया और वे समझ नहीं रहे थे।');
INSERT INTO "al_umari" VALUES(27,51,'27:51','तो देखो कैसा रहा उनके षड्यंत्र का परिणाम? हमने विनाश कर दिया उनका तथा उनकी पूरी जाति का।');
INSERT INTO "al_umari" VALUES(27,52,'27:52','तो ये उनके घर हैं, उजाड़ पड़े हुए, उनके अत्याचार के कारण, निश्चय इसमें एक बड़ी निशानी है, उन लोगों के लिए, जो ज्ञान रखते हैं।');
INSERT INTO "al_umari" VALUES(27,53,'27:53','तथा हमने बचा लिया उन्हे, जो ईमान लाये और (अल्लाह) से डर रहे थे।');
INSERT INTO "al_umari" VALUES(27,54,'27:54','तथा लूत को (भेजा), जब उसने अपनी जाति से कहाः क्या तुम कुकर्म कर रहे हो, जबकि तुम[ आँखें रखते हो? ');
INSERT INTO "al_umari" VALUES(27,55,'27:55','क्या तुम पुरुषों के पास जाते हो, काम वासना की पूर्ति के लिए? तुम लोग बड़े नासमझ हो।');
INSERT INTO "al_umari" VALUES(27,56,'27:56','तो उसकी जाति का उत्तर बस ये था कि उन्होंने कहाः लूत के परिजनों को निकाल दो अपने नगर से, वास्तव में, ये लोग बड़े पवित्र बन रहे हैं।');
INSERT INTO "al_umari" VALUES(27,57,'27:57','तो हमने बचा लिया उसे तथा उसके परिवार को, उसकी पत्नी के सिवा, जिसे हमने नियत कर दिया पीछे रह जाने वालों में।');
INSERT INTO "al_umari" VALUES(27,58,'27:58','और हमने उनपर बहुत अधिक वर्षा कर दी। तो बुरी हो गयी सावधान किये हुए लोगों की वर्षा।');
INSERT INTO "al_umari" VALUES(27,59,'27:59','आप कह दें: सब प्रशंसा अल्लाह के लिए है और सलाम है उसके उन भक्तों पर, जिन्हें उसने चुन लिया। क्या अल्लाह उत्तम है या वह, जिसे वे साझी बनाते हैं?');
INSERT INTO "al_umari" VALUES(27,60,'27:60','ये वो है, जिसने उत्पत्ति की है आकाशों तथा धरती की और उतारा है तुम्हारे लिए आकाश से जल, फिर हमने उगा दिया उसके द्वारा भव्य बाग़, तुम्हारे बस में न था कि उगा देते उसके वृक्ष, तो क्या कोई पूज्य है अल्लाह के साथ? बल्कि यही लोग (सत्य से) कतरा रहे हैं।');
INSERT INTO "al_umari" VALUES(27,61,'27:61','या वो है, जिसने धरती को रहने योगय्य बनाया तथा उसके बीच नहरें बनायीं और उसके लिए पर्वत बनाये और बना दी, दो सागरों के बीच एक रोक। तो क्या कोई पूज्य है अल्लाह के साथ? बल्कि उनमें से अधिक्तर ज्ञान नहीं रखते।');
INSERT INTO "al_umari" VALUES(27,62,'27:62','या वो है, जो व्याकुल की प्रार्थना सुनता है, जब उसे पुकारे और दूर करता है दुःख तथा तुम्हें बनाता है धरती का अधिकारी, क्या कोई पूज्य है अल्लाह के साथ? तुम बहुत कम ही शिक्षा ग्रहण करते हो।');
INSERT INTO "al_umari" VALUES(27,63,'27:63','या वो है, जो तुम्हें राह दिखाता है सूखे तथा सागर के अंधेरों में तथा भेजता है वायुओं को शुभ सूचना देने के लिए अपनी दया (वर्षा) से पहले, क्या कोई और पूज्य है अल्लाह के साथ? उच्च है अल्लाह उस शिर्क से, जो वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(27,64,'27:64','या वो है, जो आरंभ करता है उत्पत्ति का, फिर उसे दुहरायेगा तथा जो तुम्हें जीविका देता है आकाश तथा धरती से, क्या कोई पूज्य है अल्लाह के साथ? आप कह दें कि अपना प्रमाण लाओ, यदि तुम सच्चे[ हो। ');
INSERT INTO "al_umari" VALUES(27,65,'27:65','आप कह दें कि नहीं जानता है, जो आकाशों तथा धरती में है, परोक्ष को अल्लाह के सिवा और वे नहीं जानते कि कब फिर जीवित किये जायेंगे।');
INSERT INTO "al_umari" VALUES(27,66,'27:66','बल्कि समाप्त हो गया है उनका ज्ञान आख़िरत (परलोक) के विषय में, बल्कि वे द्विधा में हैं, बल्कि वे उससे अंधे हैं।');
INSERT INTO "al_umari" VALUES(27,67,'27:67','और कहा काफ़िरों नेः क्या जब हम हो जायेंगे मिट्टी तथा हमारे पूर्वज, तो क्या हम अवश्य निकाले[ जायेंगे? ');
INSERT INTO "al_umari" VALUES(27,68,'27:68','हमें इसका वचन दिया जा चुका है तथा हमारे पूर्वजों को इससे पहले, ये तो बस अगलों की बनायी हुई कथाएँ हैं।');
INSERT INTO "al_umari" VALUES(27,69,'27:69','(हे नबी!) आप कह दें कि चलो-फिरो धरती में, फिर देखो कि कैसा हुआ अपराधियों का परिणाम!');
INSERT INTO "al_umari" VALUES(27,70,'27:70','और आप शोक न करें उनपर और न किसी संकीर्णता में रहें उससे, जो चालें वे चल रहे हैं।');
INSERT INTO "al_umari" VALUES(27,71,'27:71','तथा वे कहते हैं: कब ये धमकी पूरी होगी, यदि तुम सच्चे हो?');
INSERT INTO "al_umari" VALUES(27,72,'27:72','आप कह दें: संभव है कि तुम्हारे समीप हो उसमें से कुछ, जिसे तुम शीघ्र चाहते हो।');
INSERT INTO "al_umari" VALUES(27,73,'27:73','तथा निःसंदेह, आपका पालनहार बड़ा दयालु है लोगों[ पर। परन्तु, उनमें से अधिक्तर कृतज्ञ नहीं होते। ');
INSERT INTO "al_umari" VALUES(27,74,'27:74','और वास्तव में, आपका पालनहार जानता है उसे, जो छुपाते हैं उनके दिल तथा जो व्यक्त करते हैं।');
INSERT INTO "al_umari" VALUES(27,75,'27:75','और कोई छुपी चीज़ नहीं है आकाश तथा धरती में, परन्तु वह खुली पुस्तक में[ है। ');
INSERT INTO "al_umari" VALUES(27,76,'27:76','निःसंदेह, ये क़ुर्आन वर्णन कर रहा है इस्राईल की संतान के समक्ष, उन अधिक्तर बातों को जिनमें वे विभेद कर रहे हैं।');
INSERT INTO "al_umari" VALUES(27,77,'27:77','और वास्तव में, वह मार्गदर्शन तथा दया है, ईमान वालों के लिए।');
INSERT INTO "al_umari" VALUES(27,78,'27:78','निःसंदेह, आपका पालनहार[ निर्णय कर देगा उनके बीच अपने आदेश से तथा वही प्रबल, सबकुछ जानने वाला है। ');
INSERT INTO "al_umari" VALUES(27,79,'27:79','अतः, आप भरोसा करें अल्लाह पर, वस्तुतः, आप खुले सत्य पर हैं।');
INSERT INTO "al_umari" VALUES(27,80,'27:80','वास्तव में, आप नहीं सुना सकेंगे मुर्दौं को और न सुना सकेंगे बहरों को अपनी पुकार, जब वे भागे जा रहे हों पीठ फेर[ कर। ');
INSERT INTO "al_umari" VALUES(27,81,'27:81','तथा आप अंधे को मार्गदर्शन नहीं दे सकते उनके कुपथ से, आप तो बस उसी को सुना सकते हैं, जो ईमान रखता हो हमारी आयतों पर, फिर वह आज्ञाकारी हो।');
INSERT INTO "al_umari" VALUES(27,82,'27:82','और जब आ जायेगा बात पूरी होने का समय उनके ऊपर[, तो हम निकालेंगे उनके लिए एक पशु धरती से, जो बात करेगा उनसे कि लोग हमारी आयतों पर विश्वास नहीं करते थे।');
INSERT INTO "al_umari" VALUES(27,83,'27:83','तथा जिस दिन हम घेर लायेंग प्रत्येक समुदाय से एक गिरोह, उनका, जो झुठलाते रहे हमारी आयतों को, फिर वे सब (एकत्र किये जाने के लिए) रोक दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(27,84,'27:84','यहाँतक कि जब सब आ जायेंगे, तो अल्लाह उनसे कहेगाः क्या तुमने मेरी आयतों को झुठला दिया, जबकि तुमने उनका पूरा ज्ञान नहीं किया, अन्यथा तुम और क्या कर रहे थे?');
INSERT INTO "al_umari" VALUES(27,85,'27:85','और सिध्द हो जायेगा यातना का वचन, उनके ऊपर, उनके अत्याचार के कारण। तब वे बात नहीं कर सकेंगे।');
INSERT INTO "al_umari" VALUES(27,86,'27:86','क्या उन्होंने नहीं दखा कि हमने रात बनाई, ताकि वे शान्त रहें उसमें तथा दिन को दिखाने वाला[। वास्तव में, इसमें बड़ी निशानियाँ (लक्षण) हैं, उन लोगों के लिए, जो ईमान लाते हैं। ');
INSERT INTO "al_umari" VALUES(27,87,'27:87','और जिस दिन फूँका जायेगा[ सूर (नरसिंघा) में, तो घबरा जायेंगे वे, जो आकाशों तथा धरती में हैं। परन्तु वह, जिसे अल्लाह चाहे तथा सब उस (अल्लाह) के समक्ष आ जायेंगे, विवश होकर। ');
INSERT INTO "al_umari" VALUES(27,88,'27:88','और तुम देखते हो पर्वतों को, तो उन्हें समझते हो स्थिर (अचल) हैं, जबकि वे उस दिन उड़ेंगे बादल के समान, ये अल्लाह की रचना है, जिसने सुदृढ़ किया है प्रत्येक चीज़ को, निश्चय वह भली-भाँति सूचित है उससे, जो तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(27,89,'27:89','जो भलाई[ लायेगा, तो उसके लिए उससे उत्तम (प्रतिफल) है और वह उस दिन की व्यग्रता से निर्भय रहने वाले होंगे। ');
INSERT INTO "al_umari" VALUES(27,90,'27:90','और जो बुराई लायेगा, तो वही झोंक दिये जायेंगे औंधे मुँह नरक में, (तथा कहा जायेगाः) तुम्हें वही बदला दिया जा रहा है, जो तुम करते रहे हो।');
INSERT INTO "al_umari" VALUES(27,91,'27:91','मुझे तो बस यही आदेश दिया गया है कि इस नगर (मक्का) के पालनहार की इबादत (वंदना) करूँ, जिसने इसे आदरणीय बनाया है तथा उसी के अधिकार में है प्रत्येक चीज़ और मुझे आदेश दिया गया है कि आज्ञाकारियों में रहूँ।');
INSERT INTO "al_umari" VALUES(27,92,'27:92','तथा क़ुर्आन पढ़ता रहूँ, तो जिसने सुपथ अपनाया, तो वह अपने ही लाभ के लिए सुपथ अपनायेगा और जो कुपथ हो जाये, तो आप कह दें कि वास्तव में, मैं तो बस सावधान करने वालों में से हूँ।');
INSERT INTO "al_umari" VALUES(27,93,'27:93','तथा आप कह दें कि सब प्रशंसा अल्लाह के लिए है, वह शीघ्र तुम्हें दिखा देगा अपनी निशानियाँ, जिन्हें तुम पहचान[ लोगे और तुम्हारा पालनहार उससे अचेत नहीं है, जो कुछ तुम कर रहे हो। ');
INSERT INTO "al_umari" VALUES(28,1,'28:1','ता, सीन, मीम।');
INSERT INTO "al_umari" VALUES(28,2,'28:2','ये इस खुली पुस्तक की आयतें हैं।');
INSERT INTO "al_umari" VALUES(28,3,'28:3','हम आपके समक्ष सुना रहे हैं, मूसा तथा फ़िरऔन के कुछ समाचार, सत्य के साथ, उन लोगों के लिए, जो ईमान रखते हैं।');
INSERT INTO "al_umari" VALUES(28,4,'28:4','वास्तव में, फ़िरऔन ने उपद्रव किया धरती में और कर दिया उसके निवासियों को कई गिरोह। वह निर्बल बना रहा था एक गिरोह को उनमें से, वध कर रहा था उनके पुत्रों को और जीवित रहने देता था उनकी स्त्रियों को। निश्चय वह उपद्रवियों में से था।');
INSERT INTO "al_umari" VALUES(28,5,'28:5','तथा हम चाहते थे कि उनपर दया करें, जो निर्बल बना दिये गये धरती में तथा बना दें उन्हीं को प्रमुख और बना दें उन्हीं को[ उत्तराधिकारी। ');
INSERT INTO "al_umari" VALUES(28,6,'28:6','तथा उन्हें शक्ति प्रदान कर दें और दिखा दें फ़िरऔन तथा हामान और उनकी सेनाओं को उनकी ओर से वह, जिससे वे डर रहे[ थे। ');
INSERT INTO "al_umari" VALUES(28,7,'28:7','और हमने वह़्यी[ की मूसा की माता की ओर कि उसे दूध पिलाती रह और जब तुझे उसपर भय हो, तो उसे सागर में डाल दे और भय न कर और न चिन्ता कर, निःसंदेह, हम वापस लायेंगे उसे तेरी ओर और बना देंगे उसे रसूलों में से। ');
INSERT INTO "al_umari" VALUES(28,8,'28:8','तो ले लिया उसे फ़िरऔन के कर्मचारियों ने,[ ताकि वह बने उनके लिए शत्रु तथा दुःख का कारण। वास्तव में, फ़िरऔन तथा हामान और उनकी सेनाएँ दोषी थीं। ');
INSERT INTO "al_umari" VALUES(28,9,'28:9','और फ़िरऔन की पत्नि ने कहाः ये मेरी तथा आपकी आँखों की ठण्डक है। इसे वध न कीजिये, संभव है हमें लाभ पहुँचाये या उसे हम पुत्र बना लें और वे समझ नहीं रहे थे।');
INSERT INTO "al_umari" VALUES(28,10,'28:10','और हो गया मूसा की माँ का दिल व्याकूल, समीप था कि वह उसका भेद खोल देती, यदि हम आश्वासन न देते उसके दिल को, ताकि वह हो जाये विश्वास करने वालों में।');
INSERT INTO "al_umari" VALUES(28,11,'28:11','तथा (मूसा की माँ ने) कहा उसकी बहन से कि तू इसके पीछे-पीछे जा। और उसने उसे दूर ही दूर से देखा और उन्हें इसका आभास तक न हुआ।');
INSERT INTO "al_umari" VALUES(28,12,'28:12','और हमने अवैध (निषेध) कर दिया उस (मूसा) पर दाइयों को इससे[ पूर्व। तो उस (की बहन) ने कहाः क्या मैं तुम्हें न बताऊँ ऐसा घराना, जो पालनपोषण करें इसका तुम्हारे लिए तथा वे उसके शुभचिनतक हों? ');
INSERT INTO "al_umari" VALUES(28,13,'28:13','तो हमने फेर दिया उसे उसकी माँ की ओर, ताकि ठण्डी हो उसकी आँख और चिन्ता न करे और ताकि उसे विश्वास हो जाये कि अल्लाह का वचन सच है, परन्तु अधिक्तर लोग विश्वास नहीं रखते।');
INSERT INTO "al_umari" VALUES(28,14,'28:14','और जब वह अपनी युवावस्था को पहुँचा और उसका विकास पूरा हो गया, तो हमने उसे प्रबोध तथा ज्ञान दिया और इसी प्रकार, हम बदला देते हैं सदाचारियों को।');
INSERT INTO "al_umari" VALUES(28,15,'28:15','और उसने प्रवेश किया नगर में उसके वासियों की अचेतना के समय और उसमें दो व्यक्तियों को लड़ते हुए पाया, ये उसके गिरोह से था और दूसरा उसके शत्रु में[ से। तो पुकारा उसने, जो उसके गिरोह से था, उसके विरुध्द, जो उसके शत्रु में से था। जिसपर मूसा ने उसे घूँसा मारा और वह मर गया। मूसा ने कहाः ये शैतानी कर्म है। वास्तव में, वह शत्रु है खुला, कुपथ करने वाला। ');
INSERT INTO "al_umari" VALUES(28,16,'28:16','उसने कहाः हे मेरे पालनहार! मैंने अपने ऊपर अत्याचार कर लिया, तू मुझे क्षमा कर दे। फिर अल्लाह ने उसे क्षमा कर दिया। वास्तव में, वह क्षमाशील, अति दयावान् है।');
INSERT INTO "al_umari" VALUES(28,17,'28:17','उसने कहाः उसके कारण, जो तूने मुझपर पुरस्कार किया है, अब मैं कदापि अपराधियों का सहायक नहीं बनूँगा।');
INSERT INTO "al_umari" VALUES(28,18,'28:18','फिर प्रातः वह नगर में डरता हुआ समाचार लेने गया, तो सहसा वही जिसने उससे कल सहायता माँगी थी, उसे पुकार रहा है। मूसा ने उससे कहाः वास्तव में, तूही खुला कुपथ है।');
INSERT INTO "al_umari" VALUES(28,19,'28:19','फिर जब पकड़ना चाहा उसे, जो उन दोनों का शत्रु था, तो उसने कहाः हे मूसा! क्या तू मुझे मार देना चाहता है, जैसे मार दिया एक व्यक्ति को कल? तू तो चाहता है कि बड़ा उपद्रवी बनकर रहे इस धरती में और तू नहीं चाहता कि सुधार करने वालों में से हो।');
INSERT INTO "al_umari" VALUES(28,20,'28:20','और आया एक पुरुष नगर के किनारे से दौड़ता हुआ, उसने कहाः हे मूसा! (राज्य के) प्रमुख प्रामर्श कर रहे हैं तेरे विषय में कि तुझे वध कर दें, अतः तू निकल जा। वास्तव में, मैं तेरे शुभचिन्तकों में से हूँ।');
INSERT INTO "al_umari" VALUES(28,21,'28:21','तो वह निकल गया उस (नगर) से डरा सहमा हुआ। उसने प्रार्थना कीः हे मेरे पालनहार! मुझे बचा ले अत्याचारी जाति से।');
INSERT INTO "al_umari" VALUES(28,22,'28:22','और जब वह जाने लगा मद्यन की ओर, तो उसने कहाः मूझे आशा है कि मेरा पालनहार मुझे दिखायेगा सीधा मार्ग।');
INSERT INTO "al_umari" VALUES(28,23,'28:23','और जब उतरा मद्यन के पानी पर, तो पाया उसपर लोगों का एक समूह, जो (अपने पशुओं को) पानी पिला रहा था तथा पाया उसके पीछे दो स्त्रियों को (अपने पशुओं को) रोकती हुईं। उसने कहाः तुम्हारी समस्या क्या है? दोनों ने कहाः हम पानी नहीं पिलातीं, जब तक चरवाहे चले न जायें और हमारे पिता बहुत बूढ़े हैं।');
INSERT INTO "al_umari" VALUES(28,24,'28:24','तो उसने पिला दिया दोनों के लिए। फिर चल दिया छाया की ओर और कहने लगाः हे मेरे पालनहार! तू, जोभी भलाई मुझपर उतार दे, मैं उसका आकांक्षी हूँ।');
INSERT INTO "al_umari" VALUES(28,25,'28:25','तो आई उसके पास दोनों में से एक स्त्री चलती हुई लज्जा के साथ, उसने कहाः मेरे पिता[ आपको बुला रहे हैं। ताकि आपको उसका पारिश्रमिक दें, जो आपने पानी पिलाया है हमारे लिए। फिर जब (मूसा) उसके पास पहुँचा और पूरी कथा उसे सुनाई, तो उसने कहाः भय न कर। तू मुक्त हो गया अत्याचारी जाति से।');
INSERT INTO "al_umari" VALUES(28,26,'28:26','कहा उन दोनों में से एक नेः हे पिता! आप इन्हें सेवक रख लें, सबसे उत्तम जिसे आपसेवक बनायें वही हो सकता है, जो प्रबल विश्वसनीय हो।');
INSERT INTO "al_umari" VALUES(28,27,'28:27','उसने कहाः मैं चाहता हूँ कि विवाह दूँ तुम्हें अपनी इन दो पुत्रियों में से एक से, इसपर कि मेरी सेवा करोगे आठ वर्ष, फिर यदि तुम पूरा कर दो दस (वर्ष) तो ये तुम्हारी इच्छा है। मैं नहीं चाहता कि तुमपर बोझ डालूँ और तुम मुझे पाओगे यदि अल्लाह ने चाहा, तो सदाचारियों में से।');
INSERT INTO "al_umari" VALUES(28,28,'28:28','मूसा ने कहाः ये मेरे और आपके बीच (निश्चित) है। मैं दो में से जो भी अवधि पूरी कर दूँ, मुझपर कोई अत्याचार न हो और अल्लाह उसपर, जो हम कह रहे हैं निरीक्षक है।');
INSERT INTO "al_umari" VALUES(28,29,'28:29','फिर जब पूरी कर ली मूसा ने अवधि और चला अपने परिवार के साथ, तो उसने देखी तूर (पर्वत) की ओर एक अग्नि। उसने अपने परिवार से कहाः रुको मैंने देखी है एक अग्नि, संभव है तुम्हारे पास लाऊँ वहाँ से कोई समाचार अथवा कोई अंगार अग्नि का, ताकि तुम ताप लो।');
INSERT INTO "al_umari" VALUES(28,30,'28:30','फिर जब वह वहाँ आया, तो पुकारा गया वादी के दायें किनारे से, शुभ क्षेत्र में वृक्ष सेः हे मूसा! निःसंदेह, मैं ही अल्लाह हूँ, सर्वलोक का पालनहार।');
INSERT INTO "al_umari" VALUES(28,31,'28:31','और फेंक दो अपनी लाठी, फिर जब उसे देखा कि रेंग रही है, मानो वह कोई सर्प हो, तो भागने लगा पीठ फेरकर और पीछे फिरकर नहीं देखा। हे मूसा! आगे आ तथा भय न कर, वास्तव में, तू सुरक्षितों में से है।');
INSERT INTO "al_umari" VALUES(28,32,'28:32','डाल अपना हाथ अपनी जेब में, वह निकलेगा उज्ज्वल होकर बिना किसी रोग के और चिमटा ले अपनी ओर अपनी भुजा, भय दूर करने के लिए, तो ये दो खुली निशानियाँ हैं तेरे पालनहार की ओर से फ़िरऔन तथा उसके प्रमुखों के लिए, वास्तव में, वह उल्लंघनकारी जाति हैं।');
INSERT INTO "al_umari" VALUES(28,33,'28:33','उसने कहाः मेरे पालनहार! मैंने वध किया है उनके एक व्यक्ति को। अतः, मैं डरता हूँ कि वे मुझे मार देंगे।');
INSERT INTO "al_umari" VALUES(28,34,'28:34','और मेरा भाई हारून मुझसे अधिक सुभाषी है, तू उसे भी भेज दे मेरे साथ सहायक बनाकर, ताकि वह मेरा समर्थन करे, मैं डरता हूँ कि वे मुझे झुठला देंगे।');
INSERT INTO "al_umari" VALUES(28,35,'28:35','उसने कहाः हम तुझे बाहुबल प्रदान करेंगे तेरे भाई द्वारा और बनायेंगे तुम दोनों के लिए ऐसा प्रभाव कि वे तुम दोनों तक नहीं पहुँच सकेंगे अपनी निशानियों द्वारा, तुम दोनों तथा तुम्हारे अनुयायी ही ऊपर रहेंगे।');
INSERT INTO "al_umari" VALUES(28,36,'28:36','फिर जब मूसा उनके पास हमारी खुली निशानियाँ लाया, तो उन्होंने कह दिया कि ये तो केवल घड़ा हुआ जादू है और हमने कभी नहीं सुनी ये बात अपने पूर्वजों के युग में।');
INSERT INTO "al_umari" VALUES(28,37,'28:37','तथा मूसा ने कहाः मेरा पालनहार अधिक जानता है उसे, जो मार्गदर्शन लाया है उसके पास से और किसका अन्त अच्छा होना है? वास्तव में, अत्याचारी सफल नहीं होंगे।');
INSERT INTO "al_umari" VALUES(28,38,'28:38','तथा फ़िरऔन ने कहाः हे प्रमुखो! मैं नहीं जानता तुम्हारा कोई पूज्य अपने सिवा। तो हे हामान! ईंटें पकवाकर मेरे लिए एक ऊँचा भवन बना दे। संभव है, मैं झाँककर देख लूँ मूसा के पूज्य को और निश्चय मैं उसे समझता हूँ झूठों में से।');
INSERT INTO "al_umari" VALUES(28,39,'28:39','तथा घमंड किया उसने तथा उसकी सेनाओं ने धरती में अवैध और उन्होंने समझा कि वह हमारी ओर वापस नहीं लाये जायेंगे।');
INSERT INTO "al_umari" VALUES(28,40,'28:40','तो हमने पकड़ लिया उसे और उसकी सेनाओं को, फिर फेंक दिया हमने उन्हें सागर में, तो देखो कि कैसा रहा अत्याचारियों का अन्त (परिणाम)।');
INSERT INTO "al_umari" VALUES(28,41,'28:41','और हमने उन्हें बना दिया ऐसा अगवा, जो बुलाते हों नरक की ओर तथा प्रलय के दिन उनकी सहायता नहीं की जायेगी।');
INSERT INTO "al_umari" VALUES(28,42,'28:42','और हमने पीछे लगा दिया उनके संसार में धिक्कार को और प्रलय के दिन वे बड़ी दुर्दशा में होंगे।');
INSERT INTO "al_umari" VALUES(28,43,'28:43','और हमने मूसा को पुस्तक प्रदान की इसके पश्चात् कि हमने विनाश कर दिया प्रथम समुदायों का, ज्ञान का साधन बनाकर लोगों के लिए तथा मार्गदर्शन और दया, ताकि वे शिक्षा लें।');
INSERT INTO "al_umari" VALUES(28,44,'28:44','और (हे नबी!) आप नहीं थे पश्चिमी दिशा में,[ जब हमने पहुँचाया मूसा की ओर ये आदेश और आप नहीं थे उपस्थितों में।');
INSERT INTO "al_umari" VALUES(28,45,'28:45','परन्तु (आपके समय तक) हमने बहुत-से समुदायों को पैदा किया, फिर उनपर लम्बी अवधि बीत गयी तथा आप उपस्थित न थे मद्यन के वासियों में कि सुनाते उन्हें हमारी आयतें और परन्तु हमभी रसूलों को भेजने[ वाले हैं। ');
INSERT INTO "al_umari" VALUES(28,46,'28:46','तथा नहीं थे आप तूर के अंचल में, जब हमने उसे पुकारा, परन्तु आपके पालनहार की दया है, ताकि आप सतर्क करें जिनके पास नहीं आया कोई सचेत करने वाला आपसे पूर्व, ताकि वे शिक्षा ग्रहण करें।');
INSERT INTO "al_umari" VALUES(28,47,'28:47','तथा यदि ये बात न होती कि उनपर कोई आपदा आ जाती उनके करतूतों के कारण, तो कहते कि हमारे पालनहार! तूने क्यों नहीं भेजा हमारे पास कोई रसूल कि हम पालन करते तेरी आयतों का और हो जाते ईमान वालों में से[। ');
INSERT INTO "al_umari" VALUES(28,48,'28:48','फिर जब आ गया उनके पास सत्य हमारे पास से, तो कह दिया कि क्यों नहीं दिया गया उसे वही, जो मूसा को (चमत्कार) दिया गया? तो क्या उन्होंने कुफ़्र (इन्कार) नहीं किया उसका, जो मूसा दिये गये इससे पूर्व? उन्होंने कहाः दो[ जादूगर हैं, दोनो एक-दूसरे के सहायक हैं और कहाः हम किसी को नहीं मानते। ');
INSERT INTO "al_umari" VALUES(28,49,'28:49','(हे नबी!) आप कह दें: तब तुम्हीं ले आओ कोई पुस्तक अल्लाह की ओर से, जो अधिक मार्गदर्शक हो इन दोनों[ से, मैं चलूँगा उसपर, यदि तुम सच्चे हो। ');
INSERT INTO "al_umari" VALUES(28,50,'28:50','फिर यदि वे पूरी न करें आपकी माँग, तो आप जान लें कि वे मनमानी कर रहे हैं और उससे अधिक कुपथ कौन है, जो अपनी मनमानी करे, अल्लाह की ओर से बिना किसी मार्गदर्शन के? वास्तव में, अल्लाह सुपथ नहीं दिखाता है अत्याचारी लोगों को।');
INSERT INTO "al_umari" VALUES(28,51,'28:51','और (हे नबी!) हमने निरन्तर पहुँचा दिया है उन्हें अपनी बात (क़ुर्आन), ताकि वे शिक्षा ग्रहण करें।');
INSERT INTO "al_umari" VALUES(28,52,'28:52','जिन्हें हमने प्रदान की है पुस्तक[ इस (क़ुर्आन) से पहले, वे इसपर ईमान लाते हैं।');
INSERT INTO "al_umari" VALUES(28,53,'28:53','तथा जब उन्हें सुनाया जाता है, तो कहते हैं: हम इस (क़ुर्आन) पर ईमान लाये, वास्तव में, ये सत्य है, हमारे पालनहार की ओर से, हम तो इसके (उतरने के) पहले ही से मुस्लिम हैं[। ');
INSERT INTO "al_umari" VALUES(28,54,'28:54','यही दिये जायेंगे अपना बदला दुहरा,[ अपने धैर्य के कारण और वे दूर करते हैं अच्छाई के द्वारा बुराई को और उसमें से, जो हमने उन्हें दिया है, दान करते हैं। ');
INSERT INTO "al_umari" VALUES(28,55,'28:55','और जब वह सुनते हैं व्यर्थ बात, तो विमुख हो जाते हैं, उससे तथा कहते हैं: हमारे लिए हमारे कर्म हैं और तुम्हारे लिए तुम्हारे कर्म। सलाम है तुमपर, हम (उलझना) नहीं चाहते अज्ञानों से ।');
INSERT INTO "al_umari" VALUES(28,56,'28:56','(हे नबी!) आप सुपथ नहीं दर्शा सकते, जिसे चाहें[, परन्तु अल्लाह सुपथ दर्शाता है, जिसे चाहे और वह भली-भाँति जानता है सुपथ प्राप्त करने वालों को। ');
INSERT INTO "al_umari" VALUES(28,57,'28:57','तथा उन्होंने कहाः यदि हम अनुसरण करें मार्गदर्शन का आपके साथ, तो अपनी धरती से उचक[ लिए जायेंगे। क्या हमने निवास स्थान नहीं बनाया है भयरहित "ह़रम" को उनके लिए, खिंचे चले आ रहे हैं जिसकी ओर प्रत्येक प्रकार के फल, जीविका स्वरूप, हमारे पास से? और परन्तु उनमें से अधिक्तर लोग नहीं जानते।');
INSERT INTO "al_umari" VALUES(28,58,'28:58','और हमने विनाश कर दिया बहुत-सी बस्तियों का, इतराने लगी जिनकी जीविका। तो ये हैं उनके घर, जो आबाद नहीं किये गये उनके पश्चात्, परन्तु बहुत थोड़े और हम ही उत्तराधिकारी रह गये।');
INSERT INTO "al_umari" VALUES(28,59,'28:59','और नहीं है आपका पालनहार विनाश करने वाला बस्तियों को, जब तक उनके केंद्र में कोई रसूल नहीं भेजता, जो पढ़कर सुनायें उनके समक्ष हमारी आयतें और हम बस्तियों का विनाश करने वाले नहीं हैं, परन्तु जब उसके निवासी अत्याचारी हों।');
INSERT INTO "al_umari" VALUES(28,60,'28:60','तथा जो कुछ तुम दिये गये हो, वह सांसारिक जीवन का सामान तथा उसकी शोभा है और जो अल्लाह के पास है उत्तम तथा स्थायी है, तो क्या तुम समझते नहीं हो?');
INSERT INTO "al_umari" VALUES(28,61,'28:61','तो क्या जिसे हमने वचन दिया है एक अच्छा वचन और वह पाने वाला हो उसे, उसके जैसा हो सकता है, जिसे हमने दे रखा है सांसारिक जीवन का सामान, फिर वह प्रलय के दिन उपस्थित किये लोगों में होगा[? ');
INSERT INTO "al_umari" VALUES(28,62,'28:62','और जिस दिन वह[ उन्हें पुकारेगा, तो कहेगाः कहाँ हैं मेरे साझी, जिन्हें तुम समझ रहे थे? ');
INSERT INTO "al_umari" VALUES(28,63,'28:63','कहेंगे वे जिनपर सिध्द हो चुकी है ये बातः[ हे हमारे पालनहार! यही हैं, जिन्हें हमने बहका दिया और हमने इन्हें बहकाया, जैसे हम बहके, हम उनसे अलग हो रहे हैं तेरे समक्ष, ये हमारी पूजा नहीं कर रहे थे।');
INSERT INTO "al_umari" VALUES(28,64,'28:64','तथा कहा जायेगाः पुकारो अपने साझियों को। तो वे पुकारेंगे और वे उन्हें उत्तर तक नहीं देंगे तथा वे यातना देख लेंगे, तो कामना करेंगे कि उन्होंने सुपथ अपनाया होता।');
INSERT INTO "al_umari" VALUES(28,65,'28:65','और वह (अल्लाह) उस दिन उन्हें पुकारेगा, फिर कहेगाः तुमने क्या उत्तर दिया रसूलों को?');
INSERT INTO "al_umari" VALUES(28,66,'28:66','तो नहीं सूझेगा उन्हें कोई उत्तर उस दिन और न वह एक-दूसरे से प्रश्न कर सकेंगे।');
INSERT INTO "al_umari" VALUES(28,67,'28:67','फिर जिसने क्षमा माँग ली[ तथा ईमान लाया और सदाचार किया, तो आशा कर सकता है कि वह सफल होने वालों में से होगा। ');
INSERT INTO "al_umari" VALUES(28,68,'28:68','और आपका पालनहार उत्पन्न करता है जो चाहे तथा निर्वाचित करता है। नहीं है उनके लिए कोई अधिकार, पवित्र है अल्लाह तथा उच्च है उनके साझी बनाने से।');
INSERT INTO "al_umari" VALUES(28,69,'28:69','और आपका पालनहार ही जानता है, जो छुपाते हैं उनके दिल तथा जो व्यक्त करते हैं।');
INSERT INTO "al_umari" VALUES(28,70,'28:70','तथा वही अल्लाह[ है, कोई वन्दनीय (सत्य पूज्य) नहीं है उसके सिवा, उसी के लिए सब प्रशंसा है लोक तथा परलोक में तथा उसी के लिए शासन है और तुम उसी की ओर फेरे जाओगो।');
INSERT INTO "al_umari" VALUES(28,71,'28:71','(हे नबी!) आप कहियेः तुम बताओ कि यदि बना दे तुमपर रात्रि को निरन्तर क़्यामत के दिन तक, तो कौन पूज्य है अल्लाह कि सिवा, जो ले आये तुम्हारे पास प्रकाश? तो क्या तुम सुनते नहीं हो?');
INSERT INTO "al_umari" VALUES(28,72,'28:72','आप कहियेः तुम बताओ, यदि अल्लाह कर दे तुमपर दिन को निरन्तर क़्यामत के दिन तक, तो कौन पूज्य है अल्लाह के सिवा, जो ले आये तुम्हारे लिए रात्रि, जिसमें तुम शान्ति प्राप्त करो, तो क्या तुम देखते नहीं[ हो? ');
INSERT INTO "al_umari" VALUES(28,73,'28:73','तथा अपनी दया ही से उसने बनाये हैं तुम्हारे लिए रात्रि तथा दिन, ताकि तुम शान्ति प्राप्त करो उसमें और ताकि तुम खोज करो उसके अनुग्रह (जीविका) की और ताकि तुम उसके कृतज्ञ बनो।');
INSERT INTO "al_umari" VALUES(28,74,'28:74','और अल्लाह, जिस दिन उन्हें पुकारेगा, तो कहेगाः कहाँ हैं वे, जिन्हें तुम मेरा साझी समझ रहे थे?');
INSERT INTO "al_umari" VALUES(28,75,'28:75','और हम निकाल लायेंगे प्रत्येक समुदाय से एक गवाह, फिर कहेंगेः लाओ अपने[ तर्क? तो उन्हें ज्ञान हो जायेगा कि सत्य अल्लाह ही की ओर है और उनसे खो जायेंगी, जो बातें वे घड़ रहे थे। ');
INSERT INTO "al_umari" VALUES(28,76,'28:76','क़ारून[ था मूसा की जाति में से। फिर उसने अत्याचार किया उनपर और हमने उसे प्रदान किया इतने कोष कि उसकी कुंजियाँ भारी थीं एक शक्तिशाली समुदाय पर। जब कहा उससे उसकी जाति नेः मत इतरा, वास्तव में, अल्लाह प्रेम नहीं करता है इतराने वालों से। ');
INSERT INTO "al_umari" VALUES(28,77,'28:77','तथा खोजकर उससे, जो दिया है अल्लाह ने तुझे आख़िरत (परलोक) का घर और मत भूल अपना सांसारिक भाग और उपकार कर, जैसे अल्लाह ने तुझपर उपकार किया है तथा मत खोज कर धरती में उपद्रव की, निश्चय अल्लाह प्रेम नहीं करता है उपद्रवियों से।');
INSERT INTO "al_umari" VALUES(28,78,'28:78','उसने कहाः मैं तो उसे दिया गया हूँ बस अपने ज्ञान के कारण। क्या उसे ये ज्ञान नहीं हुआ कि अल्लाह ने विनाश किया है उससे पहले बहुत-से समुदायों को, जो उससे अधिक थे धन तथा समूह में और प्रश्न नहीं किया जाता[ अपने पापों के संबन्ध में अपराधियों से। ');
INSERT INTO "al_umari" VALUES(28,79,'28:79','एक दिन, वह निकला अपनी जाति पर, अपनी शोभा में, तो कहा उन लोगों ने, जो चाहते थे सांसारिक जीवनः क्या ही अच्छा होता कि हमारे लिए (भी) उसी के समान (धन-धान्य) होता, जो दिया गया है क़ारून को! वास्तव में, वह बड़ा सौभाग्यशाली है।');
INSERT INTO "al_umari" VALUES(28,80,'28:80','तथा उन्होंने कहा जिन्हें ज्ञान दिया गयाः तुम्हारा बुरा हो! अल्लाह का प्रतिकार उसके लिए उत्तम है, जो ईमान लाये तथा सदाचार करे और ये सोच, धैर्यवानों ही को मिलती है।');
INSERT INTO "al_umari" VALUES(28,81,'28:81','तथा हमने धंसा दिया उसके तथा उसके घर सहित, धरती को, तो नहीं रह गया उसका कोई समुदाय, जो सहायता करे उसकी अल्लाह के आगे और न वह स्वयं अपनी सहायता कर सका।');
INSERT INTO "al_umari" VALUES(28,82,'28:82','और जो कामना कर रहे थे उसके स्थान की कल, कहन लगेः क्या तुम देखते नहीं कि अल्लाह अधिक कर देता है जीविका, जिसके लिए चाहता हो अपने दासों में से और नापकर देता है (जिसे चाहता है)। यदि हमपर उपकार न होता अल्लाह का, तो हमें भी धंसा देता। क्या तुम देखते नहीं कि काफ़िर (कृतघ्न) सफल नहीं होते?');
INSERT INTO "al_umari" VALUES(28,83,'28:83','ये परलोक का घर (स्वर्ग) है, हम उसे विशेष कर देंगे उनके लिए, जो नहीं चाहते बड़ाई करना धरती में और न उपद्रव करना और अच्छा परिणाम अज्ञाकारियों[ के लिए है। ');
INSERT INTO "al_umari" VALUES(28,84,'28:84','जो भलाई लायेगा, उसके लिए उससे उत्म (भलाई) है और जो बुराई लायेगा, तो नहीं बदला दिये जायेंगे वे, जिन्होंने बुराईयाँ की हैं, परन्तु वही जो वे करते रहे।');
INSERT INTO "al_umari" VALUES(28,85,'28:85','और (हे नबी!) जिसने आपपर क़ुर्आन उतारा है, वह आपको लौटाने वाला है आपके नगर (मक्का) की[ ओर। आप कह दें कि मेरा पालनहर भली-भाँति जानने वाला है कि कौन मार्गदरशन लाया है और कौन खुले कुपथ में है? ');
INSERT INTO "al_umari" VALUES(28,86,'28:86','और आप आशा नहीं रखते थे कि अवतरित की जायेगी आपकी ओर ये पुस्तक[, परन्तु ये दया है, आपके पालनहार की ओर से, अतः आप कदापि न हों सहायक, काफ़िरों के। ');
INSERT INTO "al_umari" VALUES(28,87,'28:87','और वह आपको न रोकें अल्लाह की आयतों से, इसके पश्चात् कि उतार दी गई आपकी ओर और आप बुलाते रहें अपने पालनहार की ओर और कदापि आप न हों मुश्रिकों में से।');
INSERT INTO "al_umari" VALUES(28,88,'28:88','और आप न पुकारें किसी अन्य पूज्य को अल्लाह के साथ, नहीं है कोई वंदनीय (सत्य पूज्य) उस (अल्लाह) के सिवा। प्रत्येक वस्तु नाशवान है सिवाय उसके स्वरूप के। उसी का शासन है और उसी की ओर तुमसब फेरे[ जाओगे। ');
INSERT INTO "al_umari" VALUES(29,1,'29:1','अलिफ़, लाम, मीम।');
INSERT INTO "al_umari" VALUES(29,2,'29:2','क्या लोगों ने समझ रखा है कि वे छोड़ दिये जायेंगे कि वे कहते हैं: हम ईमान लाये! और उनकी परीक्षा नहीं ली जायेगी?');
INSERT INTO "al_umari" VALUES(29,3,'29:3','और हमने परीक्षा ली है उनसे पूर्व के लोगों की, तो अल्लाह अवश्य जानेगा[ उन्हें, जो सच्चे हैं तथा अवश्य जानेगा झूठों को। ');
INSERT INTO "al_umari" VALUES(29,4,'29:4','क्या समझ रखा है उन लोगों ने, जो कुकर्म कर रहे हैं कि हम से अग्रसर[ हो जायेंगे? क्या ही बुरा निर्णय कर रहे हैं! ');
INSERT INTO "al_umari" VALUES(29,5,'29:5','जो आशा रखता हो अल्लाह से मिलने[ की, तो अल्लाह की ओर से निर्धारित किया हुआ समय अवश्य आने वाला है। और वह सब कुछ सुनने जानने वाला है।');
INSERT INTO "al_umari" VALUES(29,6,'29:6','और जो प्रयास करता है, तो वह प्रयास करता है अपने ही भले के लिए, निश्चय अल्लाह निस्पृह है संसार वासियों से।');
INSERT INTO "al_umari" VALUES(29,7,'29:7','तथा जो लोग ईमान लाये और सदाचार किये, हम अवश्य दूर कर देंगे उनसे उनकी बुराईयाँ तथा उन्हें प्रतिफल देंगे उनके उत्तम कर्मों का।');
INSERT INTO "al_umari" VALUES(29,8,'29:8','और हमने निर्देश दिया मनुष्य को अपने माता-पिता के साथ उपकार करने[ का और यदि दोनों दबाव डालें तुमपर कि तुम साझी बनाओ मेरे साथ उस चीज़ को, जिसका तुमको ज्ञान नहीं, तो उन दोनों की बात न मानो। मेरी ओर ही तुम्हें फिरकर आना है, फिर मैं तुम्हें सूचित कर दूँगा उस कर्म से, जो तुम करते रहे हो।');
INSERT INTO "al_umari" VALUES(29,9,'29:9','और जो ईमान लाये तथा सदाचार किये, हम उन्हें अवश्य सम्मिलित कर देंगे सदाचारियों में।');
INSERT INTO "al_umari" VALUES(29,10,'29:10','और लोगों में वे (भी) हैं, जो कहते हैं कि हम ईमान लाये अल्लाह पर। फिर जब सताये गये अल्लाह के बारे में, तो समझ लिया लोगों की परीक्षा को अल्लाह की यातना के समान। और यदि आ जाये कोई सहायता आपके पालनहार की ओर से, तो अवश्य कहेंगे कि हम तुम्हारे साथ थे। तो क्या अल्लाह भली-भाँति अवगत नहीं है उससे, जो संसार वासियों के दिलों में हैं?');
INSERT INTO "al_umari" VALUES(29,11,'29:11','और अल्लाह अवश्य जान लेगा उन्हें जो ईमान लाये हैं तथा अवश्य जान लेगा द्विधावादियों[ को। ');
INSERT INTO "al_umari" VALUES(29,12,'29:12','और कहा काफ़िरों ने उनसे जो ईमान लाये हैं: अनुसरण करो हमारे पथ का और हम भार ले लेंगे तुम्हारे पापों का, जबकि वे भार लेने वाले नहीं हैं उनके पापों का कुछ भी, वास्तव में, वे झूठे हैं।');
INSERT INTO "al_umari" VALUES(29,13,'29:13','और वे अवश्य प्रभारी होंगे अपने बोझों के और कुछ[ (अन्य) बोझों के अपने बोझों के साथ और उनसे अवश्य प्रश्न किया जायेगा, प्रलय के दिन, उस झूठ के बारे में, जो वे घड़ते रहे। ');
INSERT INTO "al_umari" VALUES(29,14,'29:14','तथा हम[ ने भेजा नूह़ को उसकी जाति की ओर, तो वह रहा उनमें हज़ार वर्ष किन्तु पचास कर्ष, फिर उन्हें पकड़ लिया तूफ़ान ने तथा वे अत्याचारी थे।');
INSERT INTO "al_umari" VALUES(29,15,'29:15','तो हमने बचा लिया उसे और नाव वालों को और बना दिया उसे एक निशानी (शिक्षा), विश्व वासियों के लिए।');
INSERT INTO "al_umari" VALUES(29,16,'29:16','तथा इब्राहीम को जब उसने अपनी जाति से कहाः इबादत (वंदना) करो अल्लाह की तथा उससे डरो, ये तुम्हारे लिए उत्तम है, यदि तुम जोनो।');
INSERT INTO "al_umari" VALUES(29,17,'29:17','तुम तो अल्लाह के सिवा बस उनकी वंदना कर रहे हो, जो मूर्तियाँ हैं तथा तुम झूठ घड़ रहे हो, वास्तव में, जिन्हें तुम पूज रहे हो अल्लाह के सिवा, वे नहीं अधिकार रखते हैं तुम्हारे लिए जीविका देने का। अतः, खोज करो अल्लाह के पास जीविका की तथा इबादत (वंदना) करो उसकी और कृतज्ञ बनो उसके, उसी की ओर तुम फेरे जाओगे।');
INSERT INTO "al_umari" VALUES(29,18,'29:18','और यदि तुम झुठलाओ, तो झुठलाया है बहुत-से समुदायों ने तुमसे पहले और नहीं है रसूल[ का दायित्व, परन्तु खुला उपदेश पहुँचा देना। ');
INSERT INTO "al_umari" VALUES(29,19,'29:19','क्या उन्होंने नहीं दखा कि अल्लाह ही उत्पत्ति का आरंभ करता है, फिर उसे दुहरायेगा?[ निश्चय ये अल्लाह पर अति सरल है। ');
INSERT INTO "al_umari" VALUES(29,20,'29:20','(हे नबी!) कह दें कि चलो-फिरो धरती में, फिर देखो कि उसने कैसे उतपत्ति का आरंभ किया है? फिर अल्लाह दूसरी बार भी उत्पन्न[ करेगा, वास्तव में, अल्लाह जो चाहे, कर सकता है। ');
INSERT INTO "al_umari" VALUES(29,21,'29:21','वह यातना देगा, जिसे चाहेगा तथा दया करेगा, जिसपर चाहेगा और उसी की ओर तुम फेरे जाओगे।');
INSERT INTO "al_umari" VALUES(29,22,'29:22','तुम उसे विवश करने वाले नहीं हो, न धरती में, न आकाश में तथा नहीं है तुमहारा उसके सिवा कोई संरक्षक और न सहायक।');
INSERT INTO "al_umari" VALUES(29,23,'29:23','तथा जिन लोगों ने इन्कार किया अल्लाह की आयतों और उससे मिलने का, वही निराश हो गये हैं मेरी दया से और उन्हीं के लिए दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(29,24,'29:24','तो उस (इब्राहीम) की जाति का उत्तर बस यही था कि उन्होंने कहाः इसे वध कर दो या इसे जला दो, तो अल्लाह ने उसे बचा लिया अग्नि से। वास्तव में, इसमें बड़ी निशानियाँ हैं उनके लिए, जो ईमान रखते हैं।');
INSERT INTO "al_umari" VALUES(29,25,'29:25','और कहाः तुमने तो अल्लाह को छोड़कर मूर्तियों को प्रेम का साधन बना लिया है अपने बीच, सांसारिक जीवन में, फिर प्रलय के दिन तुम एक-दूसरे का इन्कार करोगे तथा धिक्करोगे एक-दूसरे को और तुम्हारा आवास नरक होगा और नहीं होगा तुम्हारा कोई सहायक।');
INSERT INTO "al_umari" VALUES(29,26,'29:26','तो मान लिया उसे (इब्राहीम को) लूत[ ने और इब्राहीम ने कहाः मैं हिजरत कर रहा हूँ अपने पालनहार की ओर। निश्य वही प्रबल तथा गुणी है।');
INSERT INTO "al_umari" VALUES(29,27,'29:27','और हमने प्रदान किया उसे इस्ह़ाक़ तथा याक़ूब तथा हमने रख दी उसकी संतान में नबूवत तथा पुस्तक और हमने प्रदान किया उसे उसका प्रतिफल संसार में और निश्य वह परलोक में सदाचारियों में होगा।');
INSERT INTO "al_umari" VALUES(29,28,'29:28','तथा लूत को (भेजा)। जब उसने अपनी जाति से कहाः तुम तो वह निर्लज्जा कर रहे हो, जो तुमसे पहले नहीं किया है किसी ने संसार वासियों में से।');
INSERT INTO "al_umari" VALUES(29,29,'29:29','क्या तुम पुरुषों के पास जाते हो और डकैती करते हो तथा अपनी सभाओं में निर्लज्जा के कार्य करते हो? तो नहीं था उसकी जाति का उत्तर इसके अतिरिक्त कि उन्होंने कहाः तू ले आ हमारे पास अल्लाह की यातना, यदि तू सचों में से है।');
INSERT INTO "al_umari" VALUES(29,30,'29:30','लूत ने कहाः मेरे पालनहार! मेरी सहायता कर, उपद्रवी जाति पर।');
INSERT INTO "al_umari" VALUES(29,31,'29:31','और जब आये हमारे भेजे हुए (फ़रिश्ते) इब्राहीम के पास शुभ सूचना लेकर, तो उन्होंने कहाः हम विनाश करने वाले हैं इस बस्ती के वासियों का। वस्तुतः, इसके वासी अत्याचारी हैं।');
INSERT INTO "al_umari" VALUES(29,32,'29:32','इब्राहीम ने कहाः उसमें तो लूत है। उन्होंने कहाः हम भली-भाँति जानने वाले हैं, जो उसमें है। हम अवश्य बचा लेंगे उसे और उसके परिवार को, उसकी पत्नि के सिवा, वह पीछे रह जाने वालों में थी।');
INSERT INTO "al_umari" VALUES(29,33,'29:33','और जब आ गये हमारे भेजे हुए लूत के पास, तो उसे बुरा लगा और वह उदासीन हो गया[ उनके आने पर और उन्होंने कहाः भय न कर और न उदासीन हो, हम तुझे बचा लेने वाले हैं तथा तेरे परिवार को, परन्तु तेरी पत्नी को, वह पीछो रह जाने वालों में है। ');
INSERT INTO "al_umari" VALUES(29,34,'29:34','वास्तव में, हम उतारने वाले हैं इस बस्ती के वासियों पर आकाश से यातना, इस कारण कि वे उल्लंघन कर रहे हैं।');
INSERT INTO "al_umari" VALUES(29,35,'29:35','तथा हमने छोड़ दी है उसमें एक खुली निशानी उन लोगों के लिए, जो समझ-बूझ रखते हैं।');
INSERT INTO "al_umari" VALUES(29,36,'29:36','तथा मद्यन की ओर उनके भाई शुऐब को (भेजा), तो उसने कहाः हे मेरी जाति के लोगो! इबादत (वंदना) करो अल्लाह की तथा आश रखो प्रयल के दिन[ की और मत फिरो धरती में उपद्रव करते हूए। ');
INSERT INTO "al_umari" VALUES(29,37,'29:37','किन्तु उन्होंने उन्हें झुठला दिया, तो पकड़ लिया उन्हें भूकम्प ने और वह अपने घरों में औंधे पड़े रह गये।');
INSERT INTO "al_umari" VALUES(29,38,'29:38','तथा आद और समूद का (विनाश किया) और उजागर हैं तुम्हारे लिए उनके घरों के कुछ अवशेष और शोभनीय बना दिया शैतान ने उनके कर्मों को और रोक दिया उन्हें सुपथ से, जबकि वह समझ-बूझ रखते थे।');
INSERT INTO "al_umari" VALUES(29,39,'29:39','और क़ारून, फ़िरऔन और हामान का और लाये उनके पास मूसा खुली निशानियाँ, तो उन्होंने अभिमान किया और वे हमसे आगे[ होने वाले न थे। ');
INSERT INTO "al_umari" VALUES(29,40,'29:40','तो प्रत्येक को हमने पकड़ लिया उसके पाप के कारण, तो इनमें से कुछ पर पत्थर बरसाये[ और उनमें से कुछ को पकड़ा कड़ी ध्वनि ने तथा कुछ को धंसा दिया धरती में और कुछ को डुबो दिया तथा नहीं था अल्लाह कि उनपर अत्याचार करता, परन्तु वे स्वयं अपने ऊपर अत्याचार कर रहे थे।');
INSERT INTO "al_umari" VALUES(29,41,'29:41','उनका उदाहरण जिन्होंने बना लिए अल्लाह को छोड़कर संरक्षक, मकड़ी जैसा है, जिसने एक घर बनाया और वास्तव में, घरों में सबसे अधिक निर्बल घर[ मकड़ी का है, यदि वे जानते। ');
INSERT INTO "al_umari" VALUES(29,42,'29:42','वास्तव में, अल्लाह जानता है[ कि वे जिसे पुकारते हैं, अल्लाह को छोड़कर वे कुछ नहीं हैं और वही प्रबल, गुणी (प्रवीण) है। ');
INSERT INTO "al_umari" VALUES(29,43,'29:43','और ये उदाहरण हम लोगों के लिए दे रहे हैं और इसे नहीं समझेंगे, परन्तु ज्ञानी लोग (ही)।');
INSERT INTO "al_umari" VALUES(29,44,'29:44','उत्पत्ति की है अल्लाह ने आकाशों तथा धरती की सत्य के साथ। वास्तव में, इसमें एक बड़ी निशानी (लक्षण) है, ईमान लाने वालों के[ लिए। ');
INSERT INTO "al_umari" VALUES(29,45,'29:45','आप उस पुस्तक को पढ़ें, जो वह़्यी (प्रकाशना) की गयी है आपकी ओर तथा स्थापना करें नमाज़ की। वास्तव में, नमाज़ रोकती है निर्लज्जा तथा दुराचार से और अल्लाह का स्मरण ही सर्व महान है और अल्लाह जानता है, जो कुछ तुम करते[ हो। ');
INSERT INTO "al_umari" VALUES(29,46,'29:46','और तुम वाद-विवाद न करो अह्ले किताब[ से, परन्तु ऐसी विधि से, जो सर्वोत्तम हो, उनके सिवा, जिन्होंने अत्याचार किया है उनमें से तथा तुम कहो कि हम ईमान लाये उसपर, जो हमारी ओर उतारा गया और उतारा गया तुम्हारी ओर तथा हमारा पूज्य और तुम्हारा पूज्य एक ही है और हम उसी के आज्ञाकारी हैं। ');
INSERT INTO "al_umari" VALUES(29,47,'29:47','और इसी प्रकार, हमने उतारी है आपकी ओर ये पुस्तक, तो जिन्हें हमने ये पुस्तक प्रदान की है, वे इस (क़ुर्आन) पर ईमान लाते[ हैं और इनमें से (भी) कुछ इस (क़ुर्आन) पर ईमान ला रहे हैं और हमारी आयतों को काफ़िर ही नहीं मानते हैं।');
INSERT INTO "al_umari" VALUES(29,48,'29:48','और आप इससे पूर्व न कोई पुस्तक पढ़ सकते थे और न अपने हाथ से लिख सकते थे। यदि ऐसा होता, तो झूठे लोग संदेह[ में पड़ सकते थे। ');
INSERT INTO "al_umari" VALUES(29,49,'29:49','बल्कि ये खुली आयतें हैं, जो उनके दिलों में सुरक्षित हैं, जिन्हें ज्ञान दिया गया है तथा हमारी आयतों (क़ुर्आन) का इन्कार[ अत्याचारी ही करते हैं। ');
INSERT INTO "al_umari" VALUES(29,50,'29:50','तथा (अत्याचारियों) ने कहाः क्यों नहीं उतारी गयीं आपपर निशानियाँ आपके पालनहार की ओर से? आप कह दें कि निशानियाँ तो अल्लाह के पास[ हैं और मैं तो खुला सावधान करने वाला हूँ। ');
INSERT INTO "al_umari" VALUES(29,51,'29:51','क्या उन्हें पर्याप्त नहीं कि हमने उतारी है आपपर ये पुस्तक (क़ुर्आन) जो पढ़ी जा रही है उनपर। वास्तव में, इसमें दया और शिक्षा है, उन लोगों के लिए जो ईमान लाते हैं।');
INSERT INTO "al_umari" VALUES(29,52,'29:52','आप कह दें: पर्याप्त है अल्लाह मेरे तथा तुम्हारे बीच साक्षी।[ वह जानता है जो आकाशों तथा धरती में है और जिन लोगों ने मान लिया है असत्य को और अल्लाह से कुफ़्र किया है वही विनाश होने वाले हैं। ');
INSERT INTO "al_umari" VALUES(29,53,'29:53','और वे[ आपसे शीघ्र माँग कर रहे हैं यातना की और यदि एक निर्धारित समय न होता, तो आ जाती उनके पास यातना और अवश्य आयेगी उनके पास अचानक और उन्हें ज्ञान (भी) न होगा। ');
INSERT INTO "al_umari" VALUES(29,54,'29:54','वे शीघ्र माँग[ कर रहै हैं आपसे यातना की और निश्चय नरक घेरने वाली है काफ़िरों को।');
INSERT INTO "al_umari" VALUES(29,55,'29:55','जिस दिन छा जायेगी उनपर यातना, उनके ऊपर से तथा उनके पैरों के नीचे से और अल्लाह कहेगाः चखो, जो कुछ तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(29,56,'29:56','हे मेरे भक्तो जो ईमान लाये हो! वास्तव में, मेरी धरती विशाल है, अतः, तुम मेरी ही इबादत (वंदना)[ करो। ');
INSERT INTO "al_umari" VALUES(29,57,'29:57','प्रत्येक प्राणी मौत का स्वाद चखने वाला है, फिर तुम हमारी ही ओर फेरे[ जाओगे। ');
INSERT INTO "al_umari" VALUES(29,58,'29:58','तथा जो ईमान लाये और सदाचार किये, तो हम अवश्य उन्हें स्थान देंगे स्वर्ग के उच्च भवनों में, प्रवाहित होंगी जिनमें नहरें, वे सदावासी होंगे उनमें, तो क्या ही उत्तम है कर्म करने वालों का प्रतिफल!');
INSERT INTO "al_umari" VALUES(29,59,'29:59','जिन लोगों ने सहन किया तथा वे अपने पालनहार ही पर भरोसा करते हैं।');
INSERT INTO "al_umari" VALUES(29,60,'29:60','कितने ही जीव हैं, जो नहीं लादे फिरते[ अपनी जीविका, अल्लाह ही उन्हें जीविका प्रदान करता है तथा तुम्हें! और वह सब कुछ सुनने-जनने वाला है। ');
INSERT INTO "al_umari" VALUES(29,61,'29:61','और यदि आप उनसे प्रश्न करें कि किसने उत्पत्ति की है आकाशों तथा धरती की और (किसने) वश में कर रखा है सूर्य तथा चाँद को? तो वे अवश्य कहेंगे कि अल्लाह ने। तो फिर वे कहाँ बहके जा रहे हैं?');
INSERT INTO "al_umari" VALUES(29,62,'29:62','अल्लाह ही फैलाता है जीविका को जिसके लिए चाहता है अपने भक्तों में से और नापकर देता है उसके लिए। वास्तव में, अल्लाह प्रत्येक वस्तु का अति ज्ञानी है।');
INSERT INTO "al_umari" VALUES(29,63,'29:63','और यदि आप उनसे प्रश्न करें कि किसने उतारा है आकाश से जल, फिर उसके द्वारा जीवित किया है धरती को, उसके मरण के पश्चात्? तो वे अवश्य कहेंगे कि अल्लाह ने। आप कह दें कि सब प्रशंसा अल्लाह के लिए है। किन्तु उनमें से अधिक्तर लोग समझते नहीं।[ ');
INSERT INTO "al_umari" VALUES(29,64,'29:64','और नहीं है ये सांसारिक[ जीवन, किन्तु मनोरंजन और खेल और परलोक का घर ही वास्तविक जीवन है। क्या ही अच्छा होता, यदि वे जानते! ');
INSERT INTO "al_umari" VALUES(29,65,'29:65','और जब वे नाव पर सवार होते हैं, तो अल्लाह के लिए धर्म को शुध्द करके उसे पुकारते हैं। फिर जब वह बचा लाता है उन्हें थल तक, तो फिर शिर्क करने लगते हैं।');
INSERT INTO "al_umari" VALUES(29,66,'29:66','ताकि वे कुफ़्र करें उसके साथ, जो हमने उन्हें प्रदान किया है और ताकि आनन्द लेते रहें, तो शीघ्र ही इन्हें ज्ञान हो जायेगा।');
INSERT INTO "al_umari" VALUES(29,67,'29:67','क्या उन्होंने नहीं देखा कि हमने बना दिया है ह़रम (मक्का) को शान्ति स्थल, जबकि उचक लिए जाते हैं लोग उनके आस-पास से? तो क्या वे असत्य ही को मानते हैं और अल्लाह के पुरस्कार को नहीं मानते?');
INSERT INTO "al_umari" VALUES(29,68,'29:68','तथा कौन अधिक अत्याचारी होगा उससे, जो अल्लाह पर झूठ घड़े या झूठ कहे सच को, जब उसके पास आ जाये, तो क्या नहीं होगा नरक में आवास काफ़िरों को?');
INSERT INTO "al_umari" VALUES(29,69,'29:69','तथा जिन्होंने हमारी राह में प्रयास किया, तो हम अवश्य दिखा[ देंगे उन्हें अपनी राह और निश्चय अल्लाह सदाचारियों के साथ है। ');
INSERT INTO "al_umari" VALUES(30,1,'30:1','अलिफ, लाम, मीम।');
INSERT INTO "al_umari" VALUES(30,2,'30:2','पराजित हो गये रूमी।');
INSERT INTO "al_umari" VALUES(30,3,'30:3','समीप की धरती में और वे अपने पराजित होने के पश्चात् जल्द ही विजयी हो जायेंगे!');
INSERT INTO "al_umari" VALUES(30,4,'30:4','कुछ वर्षों में, अल्लाह ही का अधिकार है पहले (भी) और बाद में (भी) और उस दिन प्रसन्न होंगे ईमान वाले।');
INSERT INTO "al_umari" VALUES(30,5,'30:5','अल्लाह की सहायता से तथा वही अति प्रभुत्वशाली, दयावान् है।');
INSERT INTO "al_umari" VALUES(30,6,'30:6','ये अल्लाह का वचन है, नहीं विरुध्द करेगा अल्लाह अपने वचन[ के और परन्तु अधिक्तर लोग ज्ञान नहीं रखते। ');
INSERT INTO "al_umari" VALUES(30,7,'30:7','वे तो जानते हैं बस ऊपरी सांसारिक जीवन को तथा[ वे परलोक से अचेत हैं। ');
INSERT INTO "al_umari" VALUES(30,8,'30:8','क्या और उन्होंने अपने में सोच-विचार नहीं किया कि नहीं उत्पन्न किया है अल्लाह ने आकाशों तथा धरती को और जो कुछ उन[ दोनों के बीच है, परन्तु सत्यानुसार और एक निश्चित अवधि के लिए? और बहुत-से लोग अपने पालनहार से मिलने का इन्कार करने वाले हैं। ');
INSERT INTO "al_umari" VALUES(30,9,'30:9','क्या वे चले-फिरे नहीं धरती में, फिर देखते कि कैसा रहा उनका परिणाम, जो इनसे पहले थे? वे इनसे अधिक थे शक्ति में। उन्होंने जोता-बोया धरती को और उसे आबाद किया, उससे अधिक, जितना इन्होंने आबाद किया और आये उनके पास उनके रसूल खुली निशानियाँ (प्रमाण) लेकर। तो नहीं था अल्लाह कि उनपर अत्याचार करता और परन्तु वे स्वयं अपने ऊपर अत्याचार कर रहे थे।');
INSERT INTO "al_umari" VALUES(30,10,'30:10','फिर हो गया उनका बुरा अन्त, जिन्होंने बुराई की, इसलिए कि उन्होंने झूठ कहा अल्लाह की आयतों को और वे उनका उपहास कर रहे थे।');
INSERT INTO "al_umari" VALUES(30,11,'30:11','अल्लाह ही उत्पत्ति का आरंभ करता है, फिर उसे दुहरायेगा तथा उसी की ओर, तुम फेरे[ जाओगे। ');
INSERT INTO "al_umari" VALUES(30,12,'30:12','और जब स्थापित होगी प्रलय, तो निराश[ हो जायेंगे अपराधी। ');
INSERT INTO "al_umari" VALUES(30,13,'30:13','और नहीं होगा उनके साझियों में उनका अभिस्तावक (सिफ़ारिशी) और वे अपने साझियों का इन्कार करने वाले[ होंगे। ');
INSERT INTO "al_umari" VALUES(30,14,'30:14','और जिस दिन स्थापित होगी प्रलय, तो उस दिन सब अलग अलग हो जायेंगे।');
INSERT INTO "al_umari" VALUES(30,15,'30:15','तो जो ईमान लाये तथा सदाचार किये, वही स्वर्ग में प्रसन्न किये जायेंगे।');
INSERT INTO "al_umari" VALUES(30,16,'30:16','और जिन्होंने क्फ़्र किया और झुठलाया हमारी आयतों को और परलोक के मिलन को, तो वही यातना में उपस्थित किये हुए होंगे।');
INSERT INTO "al_umari" VALUES(30,17,'30:17','अतः, तुम अल्लाह की पवित्रता का वर्णन संध्या तथा सवेरे किया करो।');
INSERT INTO "al_umari" VALUES(30,18,'30:18','तथा उसी की प्रशंसा है आकाशों तथा धरती में तीसरे पहर तथा जब दो पहर हो।');
INSERT INTO "al_umari" VALUES(30,19,'30:19','वह निकालता है[ जीवित से निर्जीव को तथा निकालता है निर्जीव से जीव को और जीवित कर देता है धरती को, उसके मरण (सूखने) के पश्चात् और इसी प्रकार, तुम (भी) निकाले जाओगे। ');
INSERT INTO "al_umari" VALUES(30,20,'30:20','और उसकी (शक्ति) के लक्षणों में से ये भी है कि तुम्हें उत्पन्न किया मिट्टी से, फिर अब तुम मनुष्य हो (कि धरती में) फैलते जा रहे हो।');
INSERT INTO "al_umari" VALUES(30,21,'30:21','तथा उसकी निशानियों (लक्षणें) में से ये (भी) है कि उत्पन्न किये, तुम्हारे लिए, तुम्हीं में से जोड़े, ताकि तुम शान्ति प्राप्त करो उनके पास तथा उत्पन्न कर दिया तुम्हारे बीच प्रेम तथा दया, वास्तव में, इसमें कई निशाननियाँ हैं उन लोगों के लिए, जो सोच-विचार करते हैं।');
INSERT INTO "al_umari" VALUES(30,22,'30:22','तथा उसकी निशानियों में से है, आकाशों तथा धरती को पैदा करना तथा तुम्हारी बोलियों और रंगों का विभिन्न होना। निश्चय इसमें कई निशानियाँ हैं, ज्ञानियों[ के लिए। ');
INSERT INTO "al_umari" VALUES(30,23,'30:23','तथा उसकी निशानियों में से है, तुम्हारा सोना रात्रि में तथा दिन में और तुम्हारा खोज करना उसकी अनुग्रह (जीविका) का। वास्तव में, इसमें कई निशानियाँ हैं, उन लोगों के लिए, जो सुनते हैं।');
INSERT INTO "al_umari" VALUES(30,24,'30:24','और उसकी निशानियों में से (ये भी) है कि वह दिखाता है तुम्हें बिजली को, भय तथा आशा बनाकर और उतारता है आकाश से जल, फिर जीवित करता है उसके द्वारा धरती को, उसके मरण के पश्चात्, वस्तुतः, इसमें कई निशानियाँ हैं, उन लोगों के लिए, जो सोचते हैं।');
INSERT INTO "al_umari" VALUES(30,25,'30:25','और उसकी निशानियों में से है कि स्थापित हैं आकाश तथा धरती उसके आदेश से। फिर जब तुम्हें पुकारेगा एक बार धरती से, तो सहसा तुम निकल पड़ोगे।');
INSERT INTO "al_umari" VALUES(30,26,'30:26','और उसी का है, जो आकाशों तथा धरती में है। सब उसी के अधीन हैं।');
INSERT INTO "al_umari" VALUES(30,27,'30:27','तथा वही है, जो आरंभ करता है उत्पत्ति का, फिर वह उसे दुहरायेगा और वह अति सरल है उसपर और उसी का सर्वोच्च गुण है आकाशों तथा धरती में और वही प्रभुत्वशाली, तत्वज्ञ है।');
INSERT INTO "al_umari" VALUES(30,28,'30:28','उसने एक उदाहरण दिया है स्वयं तुम्हाराः क्या तुम्हारे[ दासों में से तुम्हारा कोई साझी है उसमें, जो जीविका प्रदान की है हमने तुम्हें, तो तुम उसमें उसके बराबर हो, उनसे डरते हो जैसे अपनों से डरते हो? इसी प्रकार, हम वर्णन करते हैं आयतों का, उन लोगों के लिए, जो समझ रखते हैं। ');
INSERT INTO "al_umari" VALUES(30,29,'30:29','बल्कि चले हैं अत्याचारी अपनी मनमानी पर बिना समझे, तो कौन राह दिखाये उसे, जिसे अल्लाह ने कुपथ कर दिया हो? और नहीं है उनका कोई सहायक।');
INSERT INTO "al_umari" VALUES(30,30,'30:30','तो (हे नबी!) आप सीधा रखें अपना मुख इस धर्म की दिशा में, एक ओर होकर, उस स्वभाव पर, पैदा किया है अल्लाह ने मनुष्यों को जिस[ पर। बदलना नहीं है अल्लाह के धर्म को, यही स्वभाविक धर्म है, किन्तु अधिक्तर लोग नहीं जानते।');
INSERT INTO "al_umari" VALUES(30,31,'30:31','ध्यान करके अल्लाह की ओर और डरो उससे तथा स्थापना करो नमाज़ की और न हो जाओ मुश्रिकों में से।');
INSERT INTO "al_umari" VALUES(30,32,'30:32','उनमें से जिन्होंने अलग बना लिया अपना धर्म और हो गये कई गिरोह, प्रत्येक गिरोह उसीमें[ जो उसके पास है, मगन है। ');
INSERT INTO "al_umari" VALUES(30,33,'30:33','और जब पहुँचता है मनुष्यों को कोई दुःख, तो वह पुकारते हैं अपने पालनहार को ध्यान लगाकर उसकी ओर। फिर जब वह चखाता है उनको, अपनी ओर से कोई दया, तो सहसा एक गिरोह उनमें से अपने पालनहार के साथ शिर्क करने लगता है।');
INSERT INTO "al_umari" VALUES(30,34,'30:34','ताकि वे कृतघ्न हो जायें (उसके), जो हमने प्रदान किया है उन्हें। तो तुम आन्नद ले लो, तुम्हें शीघ्र ही ज्ञान हो जायेगा।');
INSERT INTO "al_umari" VALUES(30,35,'30:35','क्या हमने उतारा है उनपर कोई प्रमाण, जो वर्णन करता है उसका, जिसे वे अल्लाह का साझी बना[ रहे हैं। ');
INSERT INTO "al_umari" VALUES(30,36,'30:36','और जब हम चखाते हैं लोगों को कुछ दया, तो वे उसपर इतराने लगते हैं और यदि पहुँचता है उन्हें कोई दुःख उनके करतूतों के कारण, तो वह सहसा निराश हो जाते हैं।');
INSERT INTO "al_umari" VALUES(30,37,'30:37','क्या उन्होंने नहीं देखा कि अल्लाह फैला देता है जीविका, जिसके लिए चाहता है और नापकर देता है? निश्चय इसमें बहुत-सी निशानियाँ हैं, उन लोगों के लिए, जो ईमान लाते हैं।');
INSERT INTO "al_umari" VALUES(30,38,'30:38','तो दो समीपवर्तियों को उनका अधिकार तथा निर्धनों और यात्रियों को। ये उत्तम है उन लोगों के लिए, जो चाहते हों अल्लाह की प्रसन्नता और वही सफल होने वाले हैं।');
INSERT INTO "al_umari" VALUES(30,39,'30:39','और जो तुम व्याज देते हो, ताकि अधिक हो जाये लोगों के धनों[ में मिलकर, तो वह अधिक नहीं होता अल्लाह के यहाँ तथा तुम जो ज़कात देते हो, चाहते हुए अल्लाह की प्रसन्नता, तो वही लोग सफल होने वाले हैं। ');
INSERT INTO "al_umari" VALUES(30,40,'30:40','अल्लाह ही है, जिसने उत्पन्न किया है तुम्हें, फिर तुम्हें जीविका प्रदान की, फिर तुम्हें मारेगा, फिर जीवित करेगा, तो क्या तुम्हारे साझियों में से कोई है, जो इसमें से कुछ कर सके? वह पवित्र है और उच्च है, उनके साझी बनाने से। ');
INSERT INTO "al_umari" VALUES(30,41,'30:41','फैल गया उपद्रव जल तथा[ थल में लोगों के करतूतों के कारण, ताकि वह चखाये उन्हें उनका कुछ कर्म, संभवतः वे रूक जायें। ');
INSERT INTO "al_umari" VALUES(30,42,'30:42','आप कह दें कि चलो-फिरो धरती में, फिर देखो कि कैसा रहा उनका अन्त, जो इनसे पहले थे। उनमें अधिक्तर मुश्रिक थे।');
INSERT INTO "al_umari" VALUES(30,43,'30:43','अतः, आप सीधा रखें अपना मुख सत्धर्म की दिशा में, इससे पहले कि आ जाये वह दिन, जिसे फिरना नहीं है अल्लाह की ओर से, उस दिन लोग अलग-अलग हो[ जायेंगे। ');
INSERT INTO "al_umari" VALUES(30,44,'30:44','जिसने कुफ़्र किया, तो उसीपर उसका कुफ़्र है और जिसने सदाचार किया, तो वे अपने ही लिए (सफलता का मार्ग) बना रहे हैं।');
INSERT INTO "al_umari" VALUES(30,45,'30:45','ताकि अल्लाह बदला दे उन्हें, जो ईमान लाये तथा सदाचार किये, अपने अनुग्रह से। निश्चय वह प्रेम नहीं करता काफ़िरों से।');
INSERT INTO "al_umari" VALUES(30,46,'30:46','और उसकी निशानियों में से है कि भेजता है वायु को शुभ सूचना देने के लिए और ताकि चखाये तुम्हें अपनी दया (वर्षा) में से और ताकि नाव चले उसके आदेश से और ताकि तुम खोजो उसकी जीविका और ताकि तुम कृतज्ञ बनो।');
INSERT INTO "al_umari" VALUES(30,47,'30:47','और हमने भेजा आपसे पहले रसूलों को, उनकी जातियों की ओर। तो वे लाये उनके पास खुली निशानियाँ, अन्ततः, हमने बदला ले लिया उनसे, जिन्होंने अपराध किया और अनिवार्य था हमपर ईमान वालों की सहायता[ करना। ');
INSERT INTO "al_umari" VALUES(30,48,'30:48','अल्लाह ही है, जो वायुओं को भेजता है, फिर वह बादल उठाती हैं, फिर वह उसे फैलाता है आकाश में, जैसे चाहता है और उसे घंघोर बना देता है। तो तुम देखते हो बूंदों को निकलते उसके बीच से, फिर जब उसे पहुँचाता है जिसे चाहता है, अपने भक्तों में से, तो सहसा वे प्रफुल्ल हो जाते हें।');
INSERT INTO "al_umari" VALUES(30,49,'30:49','यद्यपि वे थे इससे पहले कि उनपर उतारी जाये, अति निराश।');
INSERT INTO "al_umari" VALUES(30,50,'30:50','तो देखो अल्लाह की दया के लक्षणों को, वह कैसे जीवित करता है धरती को, उसके मरण के पश्चात्, निश्चय वही जीवित करने वाला है मुर्दों को तथा वह सब कुछ कर सकता है।');
INSERT INTO "al_umari" VALUES(30,51,'30:51','और यदि हम भेज दें उग्र वायु, फिर वे देख लें उसे (खेती को) पीली, तो इसके पश्चात् कुफ्र करने लगते हैं।');
INSERT INTO "al_umari" VALUES(30,52,'30:52','तो (हे नबी!) आप नहीं सुना सकेंगे मुर्दों[ को और नहीं सुना सकेंगे बहरों को पुकार, जब वे भाग रहे हों, पीठ फेरकर। ');
INSERT INTO "al_umari" VALUES(30,53,'30:53','तथा नहीं हैं आप मार्ग दर्शाने वाले अंधों को उनके कुपथ से, आप सुना सकेंगे उन्हींको, जो ईमान लाते हैं हमारी आयतों पर, फिर वही मुस्लिम हैं।');
INSERT INTO "al_umari" VALUES(30,54,'30:54','अल्लाह ही है, जिसने उत्पन्न किया तुम्हें निर्बल दशा से, फिर प्रदान किया निर्बलता के पश्चात् बल, फिर कर दिया बल के पश्चात् निर्बल तथा बूढ़ा,[ वह उत्पन्न करता है, जो चाहता है और वही सर्वज्ञ, सब सामर्थ्य रखने वाला है। ');
INSERT INTO "al_umari" VALUES(30,55,'30:55','और जिस दिन व्याप्त होगी प्रलय, तो शपथ लेंगे अपराधी कि वे नहीं रहे क्षणभर[ के सिवा और इसी प्रकार, वे बहकते रहे। ');
INSERT INTO "al_umari" VALUES(30,56,'30:56','तथा कहेंगे, जो ज्ञान दिये गये तथा ईमान कि तुम रहे हो अल्लाह के लेख में प्रलय के दिन तक, तो अब ये प्रलय का दिन है और परन्तु, तुम विश्वास नहीं रखते थे।');
INSERT INTO "al_umari" VALUES(30,57,'30:57','तो उस दिन, नहीं काम देगा अत्याचारियों को उनका तर्क और न उनसे क्षमायाचना करयी जायेगी।');
INSERT INTO "al_umari" VALUES(30,58,'30:58','और हमने वर्णन कर दिया है लोगों के लिए इस क़ुर्आन में प्रत्येक उदाहरण का और यदि आप ले आयें उनके पास कोई निशानी, तो भी अवश्य कह देंगे, जो काफ़िर हो गये कि तुम तो केवल झूठ बनाते हो।');
INSERT INTO "al_umari" VALUES(30,59,'30:59','इसी प्रकार, मुहर लगा देता है अल्लाह उनके दिलों पर, जो समझ नहीं रखते।');
INSERT INTO "al_umari" VALUES(30,60,'30:60','तो आप सहन करें, वास्तव में, अल्लाह का वचन सत्य है और कदापि वो आप[ को हल्का न समझें, जो विश्वास नहीं रखते। ');
INSERT INTO "al_umari" VALUES(31,1,'31:1','अलिफ, लाम, मीम।');
INSERT INTO "al_umari" VALUES(31,2,'31:2','ये आयतें हैं ज्ञानपूर्ण पुस्तक की।');
INSERT INTO "al_umari" VALUES(31,3,'31:3','मार्गदर्शन तथा दया है सदाचारियों के लिए।');
INSERT INTO "al_umari" VALUES(31,4,'31:4','जो नमाज़ की स्थापना करते हैं, ज़कात देते हैं और परलोक पर (पूरा) विश्वास रखते हैं।');
INSERT INTO "al_umari" VALUES(31,5,'31:5','वही लोग, अपने पालनहार के सुपथों पर हैं तथा वही लोग सफल होने वाले हैं।');
INSERT INTO "al_umari" VALUES(31,6,'31:6','तथा लोगों में वो (भी) है, जो खरीदता है खेल की[ बात, ताकि कुपथ करे अल्लाह की राह (इस्लाम) से बिना किसी ज्ञान के और उसे उपहास बनाये। यही हैं, जिनके लिए अपमानकारी यातना है। ');
INSERT INTO "al_umari" VALUES(31,7,'31:7','और जब पढ़ी जायें उसके समक्ष हमारी आयतें, तो वह मुख फेर लेता है घमण्ड करते हुए। जैसे उसके दोनों कान बहरे हों, तो आप उसे शुभसूचना सुना दें दुःखदायी यातना की।');
INSERT INTO "al_umari" VALUES(31,8,'31:8','वस्तुतः, जो ईमान लाये तथा सदाचार किये, तो उन्हीं के लिए सुख के बाग़ हैं।');
INSERT INTO "al_umari" VALUES(31,9,'31:9','वे सदावासी होंगे उनमें, अल्लाह का सत्य वचन है और वही प्रभुत्वशाली, सर्व ज्ञानी है।');
INSERT INTO "al_umari" VALUES(31,10,'31:10','उसने उत्पन्न किया है आकाशों को बिना किसी स्तस्भ के, जिन्हें तुम देख रहे हो और बना दिये धरती में पर्वत, ताकि डोल न जाये तुम्हें लेकर और फैला दिये उनमें हर प्रकार के जीव तथा हमने उतारा आकाश से जल, फिर हमने उगाये उसमें प्रत्येक प्रकार के सुन्दर जोड़।');
INSERT INTO "al_umari" VALUES(31,11,'31:11','ये अल्लाह की उत्पत्ति है, तो तुम दिखाओ क्या उत्पन्न किया है उन्होंने, जो उसके अतिरिक्त हैं? बल्कि अत्याचारी खुले कुपथ में हैं।');
INSERT INTO "al_umari" VALUES(31,12,'31:12','और हमने लुक़मान को प्रबोध प्रदान किया कि कृतज्ञ बनो अल्लाह के तथा जो (अल्लाह का) आभारी हो, वह आभारी है अपने ही (लाभ) के लिए और जो आभारी न हो, तो अल्लाह निःस्वार्थ सराहनीय है।');
INSERT INTO "al_umari" VALUES(31,13,'31:13','तथा (याद करो) जब लुक़मान ने कहा अपने पुत्र से, जब वह समझा रहा था उसेः हे मेरे पुत्र! साझी मत बना अल्लाह का, वास्तव में, शिर्क (मिश्रणवाद) बड़ा घोर अत्याचार[ है। ');
INSERT INTO "al_umari" VALUES(31,14,'31:14','और हमने आदेश दिया है मनुष्यों को अपने माता-पिता के संबन्ध में, अपने गर्भ में रखा उसे उसकी माता ने दुःख पर दुःख झेलकर और उसका दूध छुड़ाया दो वर्ष में कि तुम कृतज्ञ रहो मेरे और अपनी माता-पिता के और मेरी ओर (तुम्हें) फिर आना है।');
INSERT INTO "al_umari" VALUES(31,15,'31:15','और यदि वे दोनों दबाव डालें तुमपर कि तुम साझी बनाओ मेरा उसे, जिसका तुम्हें कोई ज्ञान नहीं, तो न[ मानो उन दोनों की बात और उनके साथ रहो संसार में सुचारु रूप से तथा राह चलो उसकी, जो ध्यानमग्न हो मेरी ओर, फिर मेरी ही ओर तुम्हें फिरकर आना है, तो मैं तुम्हें सूचित कर दूँगा उससे, जो तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(31,16,'31:16','हे मेरे पुत्र! यदि (हो कोई कर्म) राई के दाने के बराबर, फिर वह यदि हो किसी पत्थर के भीतर, आकाशों में या धरती में, तो उसे भी उपस्थित करेगा[ अल्लाह। वास्तव में, वह, सब महीन बातों से सूचित है। ');
INSERT INTO "al_umari" VALUES(31,17,'31:17','हे मेरे पुत्र! स्थापना कर नमाज़ की, आदेश दे भलाई का, रोक बुराई से और सहन कर उस (दुःख) पर, जो तुझे पहुँचे, वास्तव में, ये बड़े साहस की बात है।');
INSERT INTO "al_umari" VALUES(31,18,'31:18','और मत बल दे, अपने माथे पर,[ लोगों के लिए तथा मत चल धरती में अकड़कर। निःसंदेह, अल्लाह प्रेम नहीं करता किसी अहंकारी, गर्व करने वाले से।');
INSERT INTO "al_umari" VALUES(31,19,'31:19','और संतुलन रख अपनी चाल[ में तथा धीमी रख अपनी आवाज़, वास्तव में, सबसे बुरी आवाज़ गधे की आवाज़ है। ');
INSERT INTO "al_umari" VALUES(31,20,'31:20','क्या तुमने नहीं देखा कि अल्लाह ने वश में कर दिया[ है तुम्हारे लिए, जो कुछ आकाशों तथा धरती में है तथा पूर्ण कर दिया है तुमपर अपना पुरस्कार, खुला तथा छुपा? और कुछ लोग विवाद करते हैं अल्लाह के विषय में, बिना किसी ज्ञान, बिना किसी मार्गदर्शन और बिना किसी दिव्य (रौशन) पुस्तक के।');
INSERT INTO "al_umari" VALUES(31,21,'31:21','और जब कहा जाता है उनसे कि पालन करो उस क़ुर्आन का, जिसे उतारा है अल्लाह ने, तो कहते हैं: बल्कि हम तो उसी का पालन करेंगे, जिसपर अपने पूर्वजों को पाया है। क्या यद्यपि शैतान उन्हें बुला रहा हो अल्लाह की यातना की[ ओर? ');
INSERT INTO "al_umari" VALUES(31,22,'31:22','और समर्पित कर देगा स्वयं को अल्लाह के तथा वह एकेश्वरवादी हो, तो उसने पकड़ लिया सुदृढ़ कड़ा तथा अल्लाह हीकी ओर कर्मों का परिणाम है।');
INSERT INTO "al_umari" VALUES(31,23,'31:23','तथा जो काफ़िर हो गया, तो आपको उदासीन न करे उसका कुफ़्र। हमारी ओर ही उन्हें लौटना है, फिर हम सूचित कर देंगे उन्हें उनके कर्मों से। निःसंदेह अल्लाह अति ज्ञानि है दिलों के भेदों का।');
INSERT INTO "al_umari" VALUES(31,24,'31:24','हम उन्हें लाभ पहुँचायेंगे बहुत[ थोड़ा, फिर हम विवश कर देंगे उन्हें, घोर यातना की ओर। ');
INSERT INTO "al_umari" VALUES(31,25,'31:25','और यदि आप उनसे प्रश्न करें कि किसने उत्पन्न किया है आकाशों तथा धरती को? तो अवश्य कहेंगे कि अल्लाह ने। आप कह दें कि सब प्रशंसा अल्लाह के लिए[ है, बल्कि उनमें अधिक्तर ज्ञान नहीं रखते। ');
INSERT INTO "al_umari" VALUES(31,26,'31:26','अल्लाह ही का है, जो कुछ आकाशों तथा धरती में है, वास्तव में, अल्लाह निस्पृह, सराहनीय है।');
INSERT INTO "al_umari" VALUES(31,27,'31:27','और यदि जो भी धरती में वृक्ष हैं, सब लेखनियाँ बन जायें तथा उसके पश्चात् सागर स्याही हो जायें सात सागरों तक, तो भी समाप्त नहीं होंगे अल्लाह (की प्रशंसा) के शब्द, वास्तव में, अल्लाह प्रभावशाली, गुणी है।');
INSERT INTO "al_umari" VALUES(31,28,'31:28','और तुम्हें उत्पन्न करना और पुनः जीवित करना केवल एक प्राण के समान[ है। निःसंदेह, अल्लाह सब कुछ सुनने-जानने वाला है। ');
INSERT INTO "al_umari" VALUES(31,29,'31:29','क्या तुमने नहीं देखा कि अल्लाह मिला[ देता है, रात्रि को दिनमें और मिला देता है दिन को रात्रि में तथा वश में कर रखा है सूर्य तथा चाँद को, प्रत्येक चल रहा है एक निर्धारित समय तक और अल्लाह उससे, जो तुम कर रहे हो भली-भाँति अवगत है।');
INSERT INTO "al_umari" VALUES(31,30,'31:30','ये सब इस कारण है कि अल्लाह ही सत्य है और जिसे वे पुकारते हैं अल्लाह के सिवा असत्य है तथा अल्लाह ही सबसे ऊँचा, सबसे बड़ा है।');
INSERT INTO "al_umari" VALUES(31,31,'31:31','क्या तुमने नहीं देखा कि नाव चलती है सागर में अल्लाह के अनुग्रह के साथ, ताकि वे तुम्हें अपनी निशानियाँ दिखाये। वास्तव में, इसमें कई निशानियाँ हैं प्रत्येक सहनशील, कृतज्ञ के लिए।');
INSERT INTO "al_umari" VALUES(31,32,'31:32','और जब छा जाती है उनपर लहर छत्रों के समान, तो पुकारने लगते हैं अल्लाह को, उसके लिए शुध्द करके धर्म को और जब उन्हें सुरक्षित पहुँचा देता है थल तक, तो उनमें से कुछ संतुलित रहने वाले होते हैं और हमारी निशानियों को प्रत्येक वचनभंगी, अति कृतघ्न ही नकारते हैं।');
INSERT INTO "al_umari" VALUES(31,33,'31:33','हे लोगो! डरो अपने पालनहार से तथा भय करो उस दिन का, जिस दिन नहीं काम आयेगा कोई पिता अपनी संतान के और न कोई पुत्र काम आने वाला होगा, अपने पिता के कुछ[ भी। निश्चय अल्लाह का वचन सत्य है। अतः, तुम्हें कदापि धोखे में न रखे सांसारिक जीवन और न धोखे में रखे अल्लाह से प्रवंचक (शैतान)। ');
INSERT INTO "al_umari" VALUES(31,34,'31:34','निःसंदेह, अल्लाह ही के पास है प्रलय[ का ज्ञान, वही उतारता है वर्षा और जानता है जो कुछ गर्भाशयों में है और नहीं जानता कोई प्राणी कि वह क्या कमायेगा कल और नहीं जानता कोई प्राणी कि किस धरती में मरेगा। वास्तव में, अल्लाह ही सब कुछ जानने वाला, सबसे सूचित है। ');
INSERT INTO "al_umari" VALUES(32,1,'32:1','अलिफ लाम मीम।');
INSERT INTO "al_umari" VALUES(32,2,'32:2','इस पुस्तक का उतारना, जिसमें कोई संदेह नहीं पूरे संसार के पालनहार की ओर से है।');
INSERT INTO "al_umari" VALUES(32,3,'32:3','क्या वे कहते हैं कि इसे, इसने घड़ लिया है? बल्कि ये सत्य है आपके पालनहार की ओर से, ताकि आप सावधान करें उन लोगों को, जिन[ के पास नहीं आया है कोई सावधान करने वाला आपसे पहले। संभव है वे सीधी राह पर आ जायें। ');
INSERT INTO "al_umari" VALUES(32,4,'32:4','अल्लाह वही है, जिसने पैदा किया आकाशों तथा धरती को और जो दोनों के मध्य है, छः दिनों में। फिर स्थित हो गया अर्श पर। नहीं है उसके सिवा तुम्हारा कोई संरक्षक और न कोई अभिस्तावक (सिफ़ारिशी), तो क्या तुम शिक्षा नहीं लेते?');
INSERT INTO "al_umari" VALUES(32,5,'32:5','वह उपाय करता है प्रत्येक कार्य की आकाश से धरती तक, फिर प्रत्येक कार्य, ऊपर उसके पास जाता है, एक दिन में, जिसका माप एक हज़ार वर्ष है, तुम्हारी गणना से।');
INSERT INTO "al_umari" VALUES(32,6,'32:6','वही है ज्ञानी छुपे तथा खुले का, अति प्रभुत्वशाली, दयावान्।');
INSERT INTO "al_umari" VALUES(32,7,'32:7','जिनने सुन्दर बनाई प्रत्येक चीज़, जो उत्पन्न की और आरंभ की मनुष्य की उत्पत्ति मिट्टी से।');
INSERT INTO "al_umari" VALUES(32,8,'32:8','फिर बनाया उसका वंश एक तुच्छ जल के निचोड़ (वीर्य) से।');
INSERT INTO "al_umari" VALUES(32,9,'32:9','फिर बराबर किया उसे और फूंक दिया उसमें अपनी आत्मा (प्राण) तथा बनाये तुम्हारे लिए कान और आँख तथा दिल। तुम कम ही कृतज्ञ होते हो।');
INSERT INTO "al_umari" VALUES(32,10,'32:10','तथा उन्होंने कहाः क्या जब हम खो जायेंगे धरती में, तो क्या हम नई उत्पत्ति में होंगे? बल्कि वे अपने पालनहार से मिलने का इन्कार करने वाले हैं।');
INSERT INTO "al_umari" VALUES(32,11,'32:11','आप कह दें कि तुम्हारा प्राण निकाल लेगा मौत का फ़रिश्ता, जो तुमपर नियुक्त किया गया है, फिर अपने पालनहार की ओर फेर दिये जाओगे।[ ');
INSERT INTO "al_umari" VALUES(32,12,'32:12','और यदि आप देखते, जब अपराधी अपने सिर झुकाये होंगे अपने पालनहार के समक्ष, (वे कह रहे होंगेः) हे हमारे पालनहार! हमने देख लिया और सुन लिया, अतः, हमें फेर दे (संसार में) हम सदाचार करेंगे। हमें पूरा विश्वास हो गया।');
INSERT INTO "al_umari" VALUES(32,13,'32:13','और यदि हम चाहते, तो प्रदान कर देते प्रत्येक प्राणी को उसका मार्गदर्शन। परन्तु, मेरी ये बात सत्य होकर रही कि मैं अवश्य भरूँगा नरक को जिन्नों तथा मानव से।');
INSERT INTO "al_umari" VALUES(32,14,'32:14','तो चखो, अपने भूल जाने के कारण अपने इस दिन के मिलने को, हमने (भी) तुम्हें भुला दिया[ है। चखो, सदा की यातना उसके बदले, जो तुम कर रहे थे। ');
INSERT INTO "al_umari" VALUES(32,15,'32:15','हमारी आयतों पर बस वही ईमान लाते हैं, जिनको जब समझाया जाये उनसे, तो गिर जाते हैं सज्दा करते हुए और पवित्रता का गान करते हैं, अपने पालनहार की प्रशंसा के साथ और अभिमान नहीं करते।[ ');
INSERT INTO "al_umari" VALUES(32,16,'32:16','अलग रहते हैं उनके पार्शव (पहलू) बिस्तर से, वह प्रार्थना करते रहते हैं अपने पालनहार से भय तथा आशा रखते हुए तथा उसमें से, जो हमने उन्हें प्रदान किया है, दान करते रहते हैं।');
INSERT INTO "al_umari" VALUES(32,17,'32:17','तो नहीं जानता कोई प्राणी उसे, जो हमने छुपा रखा है उनके लिए आँखों की ठण्डक[ उसके प्रतिफल में, जो वह कर रहे थे। ');
INSERT INTO "al_umari" VALUES(32,18,'32:18','फिर क्या, जो ईमान वाला हो, उसके समान है, जो अवज्ञाकारी हो? वे सब समान नहीं हो सकेंगे।');
INSERT INTO "al_umari" VALUES(32,19,'32:19','जो ईमान लाये तथा सदाचार किये, तो उन्हीं के लिए स्थायी स्वर्ग हैं, अतिथि सत्कार के लिए, उसके बदले, जो वे करते रहे।');
INSERT INTO "al_umari" VALUES(32,20,'32:20','और जो अवज्ञा कर गये, उनका आवास नरक है। जब-जब वे निकलना चाहेंगे उसमें से, तो फेर दिये जायेंगे उसमें तथा कहा जायेगा उनसे कि चखो उस अग्नि की यातना, जिसे तुम झुठला रहे थे।');
INSERT INTO "al_umari" VALUES(32,21,'32:21','और हम अवश्य चखायेंगे उन्हें सांसारिक यातना, बड़ी यातना से पूर्व ताकि वे फिर[ आयें। ');
INSERT INTO "al_umari" VALUES(32,22,'32:22','और उससे अधिक अत्याचारी कौन है, जिसे शिक्षा दी जाये उसके पालनहार की आयतों द्वारा, फिर विमुख हो जाये उनसे? वास्तव में, हम अपराधियों से बदला लेने वाले हैं।');
INSERT INTO "al_umari" VALUES(32,23,'32:23','तथा हमने मूसा को प्रदान की (तौरात) तो आप न हों किसी संदेह में, उस[ से मिलने में तथा बनाया हमने उसे (तौरात को) मार्गदर्शन इस्राईल की संतान के लिए। ');
INSERT INTO "al_umari" VALUES(32,24,'32:24','तो हमने उनमें से अग्रणी बनाये, जो मार्गदर्शन देते रहे हमारे आदेश द्वारा, जब उन्होंने सहन किया तथा हमारी आयतों पर विश्वास[ करते रहे। ');
INSERT INTO "al_umari" VALUES(32,25,'32:25','वस्तुतः, आपका पालनहार ही निर्णय करेगा, उनके बीच प्रलय के दिन जिसमें वे विभेद करते रहे।');
INSERT INTO "al_umari" VALUES(32,26,'32:26','तो क्या मार्गदर्शन नहीं कराया उन्हें कि हमने ध्वस्त कर दिया इससे पूर्व बहुत-से युग के लोगों को, जो चल-फिर रहे थे अपने घरों में। वास्तव में, इसमें बहुत-सी निशानियाँ (शिक्षायें) हैं, तो क्या वे सुनते नहीं हैं?');
INSERT INTO "al_umari" VALUES(32,27,'32:27','क्या उन्होंने नहीं देखा कि हम बहा ले जाते हैं, जल को, सूखी भूमि की ओर, फिर उपजाते हैं उसके द्वारा खेतियाँ, खाते हैं जिनमें से उनके चौपाये तथा वे स्वयं। तो क्या वे ग़ौर नहीं करते?');
INSERT INTO "al_umari" VALUES(32,28,'32:28','तथा कहते हैं कि कब होगा वह निर्णय यदि तुम सच्चे हो?');
INSERT INTO "al_umari" VALUES(32,29,'32:29','आप कह दें: निर्णय के दिन लाभ नहीं देगा काफ़िरों को, उनका ईमान लाना[ और न उन्हें अवसर दिया जायेगा। ');
INSERT INTO "al_umari" VALUES(32,30,'32:30','अतः, आप विमुख हो जायें उनसे तथा प्रतीक्षा करें। ये भी प्रतीक्षा करने वाले हैं।');
INSERT INTO "al_umari" VALUES(33,1,'33:1','हे नबी! अल्लाह से डरो और काफ़िरों और मुनाफ़िक़ों की आज्ञापालन न करो। वास्तव में, अल्लाह ह़िक्मत वाला, सब कुछ जानने[ वाला है। ');
INSERT INTO "al_umari" VALUES(33,2,'33:2','तथा पालन करो उसका, जो वह़्यी (प्रकाशना) की जा रही है आपकी ओर आपके पालनहार की ओर से। निश्चय अल्लाह जो तुम कर रहे हो, उससे सूचित है।');
INSERT INTO "al_umari" VALUES(33,3,'33:3','और आप भरोसा करें अल्लाह पर तथा अल्लाह प्रयाप्त है, रक्षा करने वाला।');
INSERT INTO "al_umari" VALUES(33,4,'33:4','और नहीं रखे हैं अल्लाह ने किसी को, दो दिल, उसके भीतर और नहीं बनाया है तुम्हारी पत्नियों को, जिनसे तुम ज़िहार[ करते हो, उनमें से, तुम्हारी मातायें तथा नहीं बनाया है तुम्हारे मुँह बोले पुत्रों को तुम्हारा पुत्र। ये तुम्हारी मौखिक बाते हैं और अल्लाह सच कहता है तथा वही सुपथ दिखाता है। ');
INSERT INTO "al_umari" VALUES(33,5,'33:5','उन्हें पुकारो, उनके बापों से संबन्धित करके, ये अधिक न्याय की बात है अल्लाह के समीप और यदि तुम नहीं जानते उनके बापों को, तो वे तुम्हारे धर्म बन्धु तथा मित्र हैं और तुम्हारे ऊपर कोई दोष नहीं है उसमें, जो तुमसे चूक हुई है, परन्तु (उसमें है) जिसका निश्चय तुम्हारे दिल करें तथा अल्लाह अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(33,6,'33:6','नबी[ अधिक समीप (प्रिय) है ईमान वालों से, उनके प्राणों से और आपकी पत्नियाँ उनकी मातायें हैं और समीपवर्ती संबन्धी एक-दूसरे से अधिक समीप हैं, अल्लाह के लेख में ईमान वालों और मुहाजिरों से। परन्तु, ये कि करते रहो अपने मित्रों के साथ भलाई और ये पुस्तक में लिखा हूआ है।');
INSERT INTO "al_umari" VALUES(33,7,'33:7','तथा (याद करो) जब हमने नबियों से उनका वचन[ लिया तथा आपसे और नूह़, इब्रीम, मूसा, मर्यम के पुत्र ईसा से और हमने लिया उनसे दृढ़ वचन। ');
INSERT INTO "al_umari" VALUES(33,8,'33:8','ताकि वह प्रश्न[ करे सचों से उनके सच के संबन्ध में तथा तैयार की है काफ़िरों के लिए दुःखदायी यातना। ');
INSERT INTO "al_umari" VALUES(33,9,'33:9','हे ईमान वालो! याद करो अल्लाह के पुरस्कार को अपने ऊपर, जब आ गये तुम्हारे पास जत्थे, तो भेजी हमने उनपर आँधी और ऐसी सेनायें जिन्हें तुमने नहीं देखा और अल्लाह जो तुम कर रहे थे, उसे देख रहा था।');
INSERT INTO "al_umari" VALUES(33,10,'33:10','जब वे तुम्हारे पास आ गये, तुम्हारे ऊपर से तथा तुम्हारे नहीचे से और जब पथरा गयीं आँखें तथा आने लगे दिल मुँह[ को तथा तुम विचारने लगे अल्लाह के संबन्ध में विभिन्न विचार। ');
INSERT INTO "al_umari" VALUES(33,11,'33:11','यहीं परीक्षा ली गयी ईमान वालों की और वे झंझोड़ दिये गये पूर्ण रूप से।');
INSERT INTO "al_umari" VALUES(33,12,'33:12','और जब कहने लगे मुश्रिक़ और जिनके दिलों में कुछ रोग था कि अल्लाह तथा उसके रसूल ने नहीं वचन दिया हमें, परन्तु धोखे का।');
INSERT INTO "al_umari" VALUES(33,13,'33:13','और जब कहा उनके एक गिरोह नेः हे यस्रिब[ वालो! कोई स्थान नहीं तुम्हारे लिए, अतः, लौट चलो तथा अनुमति माँगने लगा उनमें से एक गिरोह नबी से, कहने लगाः हमारे घर खाली हैं, जबकि वह खाली न थे। वे तो बस निश्चय कर रहे थे भाग जाने का।');
INSERT INTO "al_umari" VALUES(33,14,'33:14','और यदि प्रवेश कर जातीं उनपर मदीने के चारों ओर से (सेनायें), फिर उनसे माँग की जाती उपद्रव[ की, तो अवश्य उपद्रव कर देते और उसमें तनिक भी देर नहीं करते। ');
INSERT INTO "al_umari" VALUES(33,15,'33:15','जबकि उन्होंने वचन दिया था अल्लाह को इससे पूर्व कि पीछा नहीं दिखायेंगे और अल्लाह के वचन का प्रश्न अवश्य किया जायेगा।');
INSERT INTO "al_umari" VALUES(33,16,'33:16','आप कह दें: कदापि लाभ नहीं पहुँचायेगा तुम्हें भागना, यदि तुम भाग जाओ मरण से और तब तुम थोड़ा ही[ लाभ प्राप्त कर सकोगे। ');
INSERT INTO "al_umari" VALUES(33,17,'33:17','आप पूछिये कि वह कौन है, जो तुम्हें बचा सके अल्लाह से, यदि वह तुम्हारे साथ बुराई चाहे अथवा तुम्हारे साथ भलाई चाहे? और वह अपने लिए नहीं पायेंगे अल्लाह के सिवा कोई संरक्षक और न कोई सहायक।');
INSERT INTO "al_umari" VALUES(33,18,'33:18','जानता है अल्लाह उन्हें, जो रोकने वाले हैं तुममें से तथा कहने वाले हैं अपने भाईयों से कि हमारे पास चले आओ तथा नहीं आते हैं युध्द में, परन्तु कभी-कभी।');
INSERT INTO "al_umari" VALUES(33,19,'33:19','वह बड़े कंजूस हैं तुमपर। फिर जब आ जाये भय का[ समय, तो आप उन्हें देखेंगे कि आपकी ओर तक रहे हैं, फिर रही हैं उनकी आँखें, उसके समान, जो मरणासन्न दशा में हो और जब दूर हो जाये भय, तो वह मिलेंगे तुमसे तेज़ ज़ुबानों से, बड़े लोभी होकर धन के। वे ईमान नहीं लाये हैं। अतः, व्यर्थ कर दिये अल्लाह ने उनके सभी कर्म तथा ये अल्लाह पर अति सरल है।');
INSERT INTO "al_umari" VALUES(33,20,'33:20','वे समझते हैं कि जत्थे नहीं[ गये और यदि आ जायें सेनायें, तो वे चाहेंगे कि वे गाँव में हों, गाँव वालों के बीच तथा पूछते रहें तुम्हारे समाचार और यदि तुममें होते भी, तो वे युध्द में कम ही भाग लेते। ');
INSERT INTO "al_umari" VALUES(33,21,'33:21','तुम्हारे लिए अल्लाह के रसूल में उत्तम[ आदर्श है, उसके लिए, जो आशा रखता हो अल्लाह और अन्तिम दिन (प्रलय) की तथा याद करो अल्लाह को अत्यधिक। ');
INSERT INTO "al_umari" VALUES(33,22,'33:22','और जब ईमान वालों ने सेनायें देखीं, तो कहाः यही है, जिसका वचन दिया था हमें अल्लाह और उसके रसूल ने और सच कहा अल्लाह और उसके रसूल ने और इसने अधिक नहीं किया, परन्तु (उनके) ईमान तथा स्वीकार को।');
INSERT INTO "al_umari" VALUES(33,23,'33:23','ईमान वालों में कुछ वे भी हैं, जिन्होंने सच कर दिखाया अल्लाह से किये हुए अपने वचन को। तो उनमें कुछ ने अपना वचन[ पूरा कर दिया और उनमें से कुछ प्रतीक्षा कर रहे हैं और उन्होंने तनिक भी परिवर्तन नहीं किया। ');
INSERT INTO "al_umari" VALUES(33,24,'33:24','ताकि अल्लाह प्रतिफल प्रदान करे सचों को उनके सच का तथा यातना दे मुनाफ़िक़ों को अथवा उन्हें क्षमा कर दे। वास्तव में, अल्लाह अति क्षमाशील और दयावान् है।');
INSERT INTO "al_umari" VALUES(33,25,'33:25','तथा फेर दिया अल्लाह ने काफ़िरों को (मदीना से) उनके क्रोध के साथ। वे नहीं प्राप्त कर सके कोई भलाई और पर्याप्त हो गया अल्लाह ईमान वालों के लिए युध्द में और अल्लाह अति शक्तिशाली तथा प्रभुत्वशाली है।');
INSERT INTO "al_umari" VALUES(33,26,'33:26','और उतार दिया अल्लाह ने उन अह्ले किताब को, जिन्होंने सहायता की उन (सेनाओं) की, उनके दुर्गों से तथा डाल दिया उनके दिलों में भय।[ उनके एक गिरोह को तुम वध कर रहे थे तथा बंदी बना रहे थे एक-दूसरे गिरोह को। ');
INSERT INTO "al_umari" VALUES(33,27,'33:27','और तुम्हारे अधिकार में दे दी उनकी भूमि तथा उनके घरों और धनों को और ऐसी धरती को, जिसपर तुमने पग नहीं रखे थे तथा अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(33,28,'33:28','हे नबी! आप अपनी पत्नियों से कह दो कि यदि तुम चाहती हो सांसारिक जीवन तथा उसकी शोभा, तो आओ, मैं तुम्हें कुछ दे दूँ तथा विदा कर दूँ, अच्छाई के साथ।');
INSERT INTO "al_umari" VALUES(33,29,'33:29','और यदि तुम चाहती हो अल्लाह और उसके रसूल तथा आख़िरत के घर को, तो अल्लाह ने तैयार कर रखा है तुममें से सदाचारिणियों के लिए भारी प्रतिफल।[ ');
INSERT INTO "al_umari" VALUES(33,30,'33:30','हे नबी की पत्नियो! जो तुममें से खुला दुराचार करेगी उसके लिए दुगनी कर दी जायेगी यातना और ये अल्लाह पर अति सरल है।');
INSERT INTO "al_umari" VALUES(33,31,'33:31','तथा जो मानेंगी तुममें से अल्लाह तथा उसके रसूल की बात और सदाचार करेंगी, हम उन्हें प्रदामन करेंगे उनका प्रतिफल दोहरा और हमने तैयार की है उनके लिए उत्तम जीविका।[ ');
INSERT INTO "al_umari" VALUES(33,32,'33:32','हे नबी की पत्नियो! तुम नहीं हो अन्य स्त्रियों के समान। यदि तुम अल्लाह से डरती हो, तो कोमल भाव से बात न करो कि लोभ करने लगे वे, जिनके दिल में रोग हो और सभ्य बात बोलो।');
INSERT INTO "al_umari" VALUES(33,33,'33:33','और रहो अपने घरों में और सौन्दर्य का प्रदर्शन न करो, प्रथम अज्ञान युग के प्रदर्शन के समान तथा नमाज़ की स्थापना करो, ज़कात दो तथा आज्ञा पालन करो अल्लाह और उसके रसूल की। अल्लाह चाहता है कि मलिनता को दूर कर दे तुमसे, हे नबी की घर वालियो! तथा तुम्हें पवित्र कर दे,, अति पवित्र।');
INSERT INTO "al_umari" VALUES(33,34,'33:34','तथा याद रखो उसे, जो पढ़ी जाती है तुम्हारे घरों में, अल्लाह की आयतों तथा हिक्मत में से।[ वास्तव में अल्लाह सूक्ष्मदर्शी, सर्व सूचित है। ');
INSERT INTO "al_umari" VALUES(33,35,'33:35','निःसंदेह, मुसलमान पुरुष और मुसलमान स्त्रियाँ, ईमान वाले पुरुष और ईमान वाली स्त्रियाँ, आज्ञाकारी पुरुष और आज्ञाकारिणी स्त्रियाँ, सच्चे पुरुष तथा सच्ची स्त्रियाँ, सहनशील पुरुष और सहनशील स्त्रियाँ, विनीत पुरुष और विनीत स्त्रियाँ, दानशील पुरुष और दानशील स्त्रियाँ, रोज़ा रखने वाले पुरुष और रोज़ा रखने वाली स्त्रियाँ, अपने गुप्तांगों की रक्षा करने वाले पुरुष तथा रक्षा करने वाली स्त्रियाँ तथा अल्लाह को अत्यधिक याद करने वाले पुरुष और याद करने वाली स्त्रियाँ, तैयार कर रखा है अल्लाह ने इन्हीं के लिए क्षमा तथा महान प्रतिफल।[ ');
INSERT INTO "al_umari" VALUES(33,36,'33:36','तथा किसी ईमान वाले पुरुष और ईमान वाली स्त्री के लिए योग्य नहीं कि जब निर्णय कर दे अल्लाह तथा उसके रसूल किसी बात का, तो उनके लिए अधिकार रह जाये अपने विषय में और जो अवज्ञा करेगा अल्लाह एवं उसके रसूल की, तो वह खुले कुपथ में[ पड़ गया। ');
INSERT INTO "al_umari" VALUES(33,37,'33:37','तथा (हे नबी!) आप वह समय याद करें, जब आप उससे कह रहे थे, उपकार किया अल्लाह ने जिसपर तथा आपने उपकार किया जिसपर, रोक ले अपनी पत्नी को तथा अल्लाह से डर और आप छुपा रहे थे अपने मन में जिसे, अल्लाह उजागर करने वाला[ था तथा डर रहे थे तुम लोगों से, जबकि अल्लाह अधिक योग्य था कि उससे डरते, तो जब ज़ैद ने पूरी करली उस (स्त्री) से अपनी आवश्यक्ता, तो हमने विवाह दिया उसे आपसे, ताकि ईमान वालों पर कोई दोष न रहे, अपने मुँह बोले पुत्रों की पत्नियों कि विषय में, जब वह पूरी करलें उनसे अपनी आवश्यक्ता तथा अल्लाह का आदेश पूरा होकर रहा।');
INSERT INTO "al_umari" VALUES(33,38,'33:38','नहीं है नबी पर कोई तंगी उसमें जिसका आदेश दिया है अल्लाह ने उनके लिए।[ अल्लाह का यही नियम रहा है उन नबियों में, जो हुए हैं आपसे पहले तथा अल्लाह का निश्चित किया आदेश पूरा होना ही है। ');
INSERT INTO "al_umari" VALUES(33,39,'33:39','जो पहुँचाते हैं अल्लाह के आदेश तथा उससे डरते हैं, वे नहीं डरते हैं किसी से, उसके सिवा और पर्याप्त है अल्लाह ह़िसाब लेने के लिए।');
INSERT INTO "al_umari" VALUES(33,40,'33:40','मुह़म्मद तुम्हारे पुरुषों मेंसे किसी के पिता नहीं हैं। किन्तु, वे[ अल्लाह के रसूल और सब नबियों में अन्तिम हैं और अल्लाह प्रत्येक वस्तु का अति ज्ञानी है।');
INSERT INTO "al_umari" VALUES(33,41,'33:41','हे ईमान वालो! याद करते रहो अल्लाह को, अत्यधिक।[ ');
INSERT INTO "al_umari" VALUES(33,42,'33:42','तथा पवित्रता बयान करते रहो उसकी प्रातः तथा संध्या।');
INSERT INTO "al_umari" VALUES(33,43,'33:43','वही है, जो दया कर रहा है तुमपर तथा प्रार्थना कर रहे हैं (तुम्हारे लिए) उसके फ़रिश्ते। ताकि वह निकाल दे तुम्हें अंधेरों से, प्रकाश[ की ओर तथा ईमान वालों पर अत्यंत दयावान् है। ');
INSERT INTO "al_umari" VALUES(33,44,'33:44','उनका स्वागत, जिस दिन उससे मिलेंगे, सलाम से होगा और उसने तैयार कर रखा है उनके लिए सम्मानित प्रतिफल।');
INSERT INTO "al_umari" VALUES(33,45,'33:45','हे नबी! हमने भेजा है आपको साक्षी,[ शुभसूचक और सचेतकर्ता बनाकर।');
INSERT INTO "al_umari" VALUES(33,46,'33:46','तथा बुलाने वाला बनाकर अल्लाह की ओर उसकी अनुमति से तथा प्रकाशित प्रदीप बनाकर।[ ');
INSERT INTO "al_umari" VALUES(33,47,'33:47','तथा आप शुभ सूचना सुना दें ईमान वालों को कि उनके लिए अल्लाह की ओर से बड़ा अनुग्रह है।');
INSERT INTO "al_umari" VALUES(33,48,'33:48','तथा न बात मानें काफ़िरों और मुनाफ़िक़ों की तथा न चिन्ता करें उनके दुःख पहुँचाने की और भरोसा करें अल्लाह पर तथा पर्याप्त है अल्लाह काम बनाने के लिए।');
INSERT INTO "al_umari" VALUES(33,49,'33:49','हे ईमान वालो! जब तुम विवाह करो ईमान वालियों से, फिर तलाक़ दो उन्हें, इससे पूर्व कि हाथ लगाओ उनको, तो नहीं है तुम्हारे लिए उनपर कोई इद्दत,[ जिसकी तुम गणना करो। तो तुम उन्हें कुछ लाभ पहुँचाओ और उन्हें विदा करो भलाई के साथ। ');
INSERT INTO "al_umari" VALUES(33,50,'33:50','हे नबी! हमने ह़लाल (वैध) कर दिया है आपके लिए आपकी पत्नियों को, जिन्हें चुका दिया हो आपने उनका महर (विवाह उपहार) तथा जो आपके स्वामित्व में हो, उसमें से, जो प्रदान किया है अल्लाह ने आप[ को तथा आपके चाचा की पुत्रियों, आपकी फूफी की पुत्रियों, आपके मामा की पुत्रियों तथा मौसी की पुत्रियों को, जिन्होंने हिजरत की है आपके साथ तथा किसी भी ईमान वाली नारी को, यदि वह स्वयं को दान कर दे नबी के लिए, यदि नबी चाहें कि उससे विवाह कर लें। ये विशेष है आपके लिए अन्य ईमान लालों को छोड़कर। हमें ज्ञान है उसका, जो हमने अनिवार्य किया है उनपर, उनकी पत्नियों तथा उनके स्वामित्व में आयी दासियों के सम्बंध में। ताकि तुमपर कोई संकीर्णता (तंगी) न हो और अल्लाह अति क्षमी, दयावान् है।');
INSERT INTO "al_umari" VALUES(33,51,'33:51','(आपको अधिकार है कि) जिसे आप चाहें, अलग रखें अपनी पत्नियों में से और अपने साथ रखें, जिसे चाहें और जिसे आप चाहें, बुला लें उनमें से, जिन्हें अलग किया है। आपपर कोई दोष नहीं है। इस प्रकार, अधिक आशा है कि उनकी आँखें शीतल हों और वे उदासीन न हों तथा प्रसन्न रहें उससे, जो आप उनसब को दें और अल्लाह जानता है जो तुम्हारे दिलों[ में है और अल्लाह अति ज्ञानी, सहनशील है।');
INSERT INTO "al_umari" VALUES(33,52,'33:52','(हे नबी!) नहीं ह़लाल (वैध) हैं आपके लिए पत्नियाँ इसके पश्चात् और न ये कि आप बदलें उन्हें दूसरी पत्नियों[ से, यद्यपि आपको भाये उनका सौन्दर्य। परन्तु, जो दासी आपके स्वामित्व में आ जाये तथा अल्लाह प्रत्येक वस्तु का पूर्ण रक्षक है। ');
INSERT INTO "al_umari" VALUES(33,53,'33:53','हे ईमान वालो! मत प्रवेश करो नबी के घरों में, परन्तु ये कि अनुमति दी जाये तुम्हें भोज के लिए, परन्तु, भोजन पकने की प्रतीक्षा न करते रहो। किन्तु, जब तुम बुलाये जाओ, तो प्रवेश करो, फिर जब भोजन करलो, तो निकल जाओ। लीन न रहो बातों में। वास्तव में, इससे नबी को दुःख होता है, अतः, वह तुमसे लजाते हैं और अल्लाह नहीं लजाता है सत्य[ से तथा जबतुम नबी की पत्नियों से कुछ माँगो, तो पर्दे के पीछे से माँगो, ये अधिक पवित्रता का कारण है तुम्हारे दिलों तथा उनके दिलों के लिए और तुम्हारे लिए उचित नहीं है कि नबी को दुःख दो, न ये कि विवाह करो उनकी पत्नियों से, आपके पश्चात् कभी भी। वास्तव में, ये अल्लाह के समीप महा (पाप) है। ');
INSERT INTO "al_umari" VALUES(33,54,'33:54','यदि तुम कुछ बोलो अथवा उसे मन में रखो, तो अल्लाह प्रत्येक वस्तु का अत्यंत ज्ञानी है।');
INSERT INTO "al_umari" VALUES(33,55,'33:55','कोई दोष नहीं है उन (स्त्रियों) पर अपने पिताओं, न अपने पुत्रों, न अपने भाईयों, न अपने भतीजों, न अपनी (मेल-जोल की) स्त्रियों और न अपने स्वामित्व (दासी तथा दास) के सामने होने में, यदि वे अल्लाह से डरती रहें। वास्तव में, अल्लाह प्रत्येक वस्तु पर साक्षी है।');
INSERT INTO "al_umari" VALUES(33,56,'33:56','अल्लाह तथा उसके फ़रिश्ते दरूद[ भेजते हैं नबी पर। हे ईमान वालो! उनपर दरूद तथा बहुत सलाम भेजो। ');
INSERT INTO "al_umari" VALUES(33,57,'33:57','जो लोग दुःख देते हैं अल्लाह तथा उसके रसूल को, तो अल्लाह ने उन्हें धिक्कार दिया है लोक तथा परलोक में और तैयार की है उनके लिए अपमानकारी यातना।');
INSERT INTO "al_umari" VALUES(33,58,'33:58','और जो दुःख देते हैं ईमान वालों तथा ईमान वालियों को, बिना किसी दोष के, जो उन्होंने किया हो, तो उन्होंने लाद लिया आरोप तथा खुले पाप को।');
INSERT INTO "al_umari" VALUES(33,59,'33:59','हे नबी! कह दो अपनी पत्नियों, अपनी पुत्रियों और ईमान वालों की स्त्रियों से कि डाल लिया करें अपने ऊपर अपनी चादरें। ये अधिक समीप है कि वे पहचान ली जायें। फिर उन्हें दुःख न दिया[ जाये और अल्लाह अति क्षमि, दयावान् है। ');
INSERT INTO "al_umari" VALUES(33,60,'33:60','यदि न रुके मुनाफ़िक़[ तथा जिनके दिलों में रोग है और मदीना में अफ़्वाह फैलाने वाले, तो हम आपको भड़का देंगे उनपर। फिर वे आपके साथ नहीं रह सकेंगे उसमें, परन्तु कुछ ही दिन। ');
INSERT INTO "al_umari" VALUES(33,61,'33:61','धिक्कारे हुए। वे जहाँ पाये जायें, पकड़ लिए जायेंगे तथा जान से मार दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(33,62,'33:62','यही अल्लाह का नियम रहा है उनमें, जो इनसे पूर्व रहे तथा आप कदापि नहीं पायेंगे अल्लाह के नियम में कोई परिवर्तन।');
INSERT INTO "al_umari" VALUES(33,63,'33:63','प्रश्न करते हैं आपसे लोग[ प्रलय विषय में। तो आप कह दें कि उसका ज्ञान तो अल्लाह ही को है। संभव है कि प्रलय समीप हो। ');
INSERT INTO "al_umari" VALUES(33,64,'33:64','अल्लाह ने धिक्कार दिया है काफ़िरों को और तैयार कर रखी है, उनके लिए, दहकती अग्नि।');
INSERT INTO "al_umari" VALUES(33,65,'33:65','वे सदावासी होंगे उसमें। नहीं पायेंगे कोई रक्षक और न कोई सहायक।');
INSERT INTO "al_umari" VALUES(33,66,'33:66','जिस दिन उलट-पलट किये जायेंगे उनके मुख अग्नि में, वे कहेंगेः हमारे लिए क्या ही अच्छा होता कि हम कहा मानते अल्लाह का तथा कहा मानते रसूल का!');
INSERT INTO "al_umari" VALUES(33,67,'33:67','तथा कहेंगेः हमारे पालनहार! हमने कहा माना अपने प्रमुखों एवं बड़ों का। तो उन्होंने हमें कुपथ कर दिया, सुपथ से।');
INSERT INTO "al_umari" VALUES(33,68,'33:68','हमारे पालनहार! उन्हें दुगुनी यातना दे तथा उन्हें धिक्कार दे, बड़ी धिक्कार।');
INSERT INTO "al_umari" VALUES(33,69,'33:69','हे ईमान वालो! न हो जाओ उनके समान जिन्होंने मूसा को दुःख दिया, तो अल्लाह ने निर्दोष कर दिया[ उसे उनकी बनाई बातों से और वह था अल्लाह के समक्ष सम्मानित। ');
INSERT INTO "al_umari" VALUES(33,70,'33:70','हे ईमान वालो! अल्लाह से डरो तथा सही और सीधी बात बोलो।');
INSERT INTO "al_umari" VALUES(33,71,'33:71','वह सुधार देगा तुम्हारे लिए तुम्हारे कर्मों को तथा क्षमा कर देगा तुम्हारे पापों को और जो अनुपालन करेगा अल्लाह तथा उसके रसूल का, तो उसने बड़ी सफलता प्राप्त कर ली।');
INSERT INTO "al_umari" VALUES(33,72,'33:72','हमने प्रस्तुत किया अमानत[ को आकाशों, धरती एवं पर्वतों पर, तो उनसबने इन्कार कर दिया उसका भार उठाने से तथा डर गये उससे। किन्तु, उसका भार ले लिया मनुष्य ने। वास्तव में, वह बड़ा अत्याचारी, अज्ञान है।');
INSERT INTO "al_umari" VALUES(33,73,'33:73','( ये अमानत का भार इसलिए लिया है) ताकि अल्लाह दण्ड दे मुनाफ़िक़ पुरुष तथा मुनाफ़िक़ स्त्रियों को और मुश्रिक पुरुष तथा स्त्रियों को तथा क्षमा कर दे अल्लाह ईमान वालों तथा ईमान वालियों को और अल्लाह अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(34,1,'34:1','सब प्रशंसा अल्लाह के लिए है, जिसके अधिकार में है, जो आकाशों तथा धरती में है और उसी की प्रशंसा है आख़िरत (परलोक) में और वही उपाय जानने वाला, सबसे सूचित है।');
INSERT INTO "al_umari" VALUES(34,2,'34:2','वह जानता है, जो कुछ घुसता है धरती के भीतर तथा जो[ निकलता है उससे तथा जो उतरता है आकाश से और चढ़ता है उसमें तथा वह अति दयावान्, क्षमी है।');
INSERT INTO "al_umari" VALUES(34,3,'34:3','तथा कहा काफ़िरों ने कि हमपर प्रलय नहीं आयेगी। आप कह दें: क्यों नहीं? मेरे पालनहार की शपथ! -वह तुमपर अवश्य आयेगी- जो परोक्ष का ज्ञानी है। नहीं छुपा रह सकता उससे कण बराबर (भी) आकाशों तथा धरती में, न उससे छोटी कोई चीज़ और न बड़ी, किन्तु, वह खुली पुस्तक में (अंकित) है।[ ');
INSERT INTO "al_umari" VALUES(34,4,'34:4','ताकि[ वह बदला दे उन्हें, जो ईमान लाये तथा सुकर्म किये। उन्हीं के लिए क्षमा तथा सम्मानित जीविका है। ');
INSERT INTO "al_umari" VALUES(34,5,'34:5','तथा जिन्होंने प्रयत्न किये हमारी आयतों में विवश[ करने का, तो यही हैं, जिनके लिए यातना है अति घोर दुःखदायी। ');
INSERT INTO "al_umari" VALUES(34,6,'34:6','तथा (साक्षात) देख[ लेंगे वे जिन्हें उसका ज्ञान दिया गया है, जो अवतरित किया गया है आपकी ओर आपके पालनहार की ओर से। वही सत्य है तथा सुपथ दर्शाता है, अति प्रभुत्वशाली, प्रशंसित का सुपथ। ');
INSERT INTO "al_umari" VALUES(34,7,'34:7','तथा काफ़िरों ने कहाः क्या हम तुम्हें एक ऐसे व्यक्ति को बतायें, जो तुम्हें सूचना देता है कि जब तुम पूर्णतः चूर-चूर हो जाओगे, तो अवश्य तुम एक नई उत्पत्ति में होगे?');
INSERT INTO "al_umari" VALUES(34,8,'34:8','उसने बना ली है अल्लाह पर एक मिथ्या बात अथवा वह पागल हो गया है। बल्कि जो विश्वास (ईमान) नहीं रखते आख़िरत (परलोक) पर, वे यातना[ तथा दूर के कुपथ में हैं। ');
INSERT INTO "al_umari" VALUES(34,9,'34:9','क्या उन्होंने नहीं देखा उसकी ओर, जो उनके आगे तथा उनके पीछे आकाश और धरती है। यदि हम चाहें, तो धंसा दें उनके सहित धरती को अथवा गिरा दें उनपर कोई खण्ड आकाश से। वास्तव में, इसमें एक बड़ी निशानी है प्रत्येक भक्त के लिए, जो ध्यानमग्न हो।');
INSERT INTO "al_umari" VALUES(34,10,'34:10','तथा हमने प्रदान किया दावूद को अपना कुछ अनुग्रह,[ हे पर्वतो! सरुचि महिमा गान करो उसके साथ तथा हे पक्षियो! तथा हमने कोमल कर दिया उसके लिए लाहा को।');
INSERT INTO "al_umari" VALUES(34,11,'34:11','कि बनाओ भरपूर कवचें तथा अनुमान रखो उसकी कड़ियों का तथा सदाचार करो। जो कुछ तुम कर रहे हो, उसे मैं देख रहा हूँ।');
INSERT INTO "al_umari" VALUES(34,12,'34:12','तथा ( हमने वश में कर दिया) सुलैमान[ के लिए वायु को। उसका प्रातः चलना एक महीने का तथा संध्या का चलना एक महीने का होता था तथा हमने बहा दिये उसके लिए तांबे के स्रोत तथा कुछ जिन्न कार्यरत थे उसके समक्ष, उसके पालनहार की अनुमति से तथा उनमें से जो फिरेगा हमारे आदेश से, तो हम चखायेंगे उसे भड़कती अग्नि की यातना।');
INSERT INTO "al_umari" VALUES(34,13,'34:13','वह बनाते थे उसके लिए, जो वह चाहता था; भवन (मस्जिदें), चित्र, बड़े लगन जलाशयों (तालाबों) के समान तथा भारी देगें, जो हिल न सकें। हे दावूद के परिजनो! कर्म करो कृतज्ञ होकर और मेरे भक्तों में थोड़े ही कृतज्ञ होते हैं।');
INSERT INTO "al_umari" VALUES(34,14,'34:14','फिर जब हमने उस (सुलैमान) पर मौत का निर्णय कर दिया, तो जिन्नों को उनके मरण पर एक घुन के सिवा किसी ने सूचित नहीं किया, जो उसकी छड़ी खा रहा था।[ फिर जब वह गिर गया, तो जिन्नों पर ये बात खुली कि यदि वे परोक्ष का ज्ञान रखते, तो इस अपमानकारी यातना में नहीं पड़े रहते।');
INSERT INTO "al_umari" VALUES(34,15,'34:15','सबा[ की जाति के लिए उनकी बस्तियों में एक निशानी थीः बाग़ थे दायें और बायें। खाओ अपने पालनहार का दिया हुआ और उसके कृतज्ञ रहो। स्वच्छ नगर है तथा अति क्षमि पालनहार।');
INSERT INTO "al_umari" VALUES(34,16,'34:16','परन्तु, उन्होंने मुँह फेर लिया, तो हमने भेज दी उनपर बाँध तोड़ बाढ़ तथा बदल दिया हमने उनके दो बाग़ों को, दो कड़वे फलों के बाग़ों और झाऊ तथा कुछ बैरी से।');
INSERT INTO "al_umari" VALUES(34,17,'34:17','ये कुफल दिया हमने उनके कृतघ्न होने के कारण तथा हम कृतघ्नों ही को कुफल दिया करते हैं।');
INSERT INTO "al_umari" VALUES(34,18,'34:18','और हमने बना दी थी उनके बीच तथा उनकी बस्तियों के बीच, जिनमें हमने सम्पन्नता[ प्रदान की थी, खुली बस्तियाँ तथा नियत कर दिया था उनमें, चलने का स्थान (कि) चलो उसमें रात्रि तथा दिनों के समय शान्त होकर।');
INSERT INTO "al_umari" VALUES(34,19,'34:19','तो उन्होंने कहाः हे हमारे पालनहार! दूरी[ करदे हमारी यात्राओं के बीच तथा उन्होंने अत्याचार किया अपने ऊपर। अन्ततः, हमने उन्हें कहानियाँ बना दिया और तित्तर-वित्तर कर दिया। वास्तव में, इसमें कई निशानियाँ (शिक्षायें) हैं प्रत्येक अति धैर्यवान, कृतज्ञ के लिए।');
INSERT INTO "al_umari" VALUES(34,20,'34:20','तथा सच कर दिया इब्लीस ने उनपर अपना अंकलन।[ तो उन्होंने अनुसरण किया उसका एक समुदाय को छोड़कर ईमान वालों के। ');
INSERT INTO "al_umari" VALUES(34,21,'34:21','और नहीं था उसका उनपर कुछ अधिकार (दबाव), किन्तु, ताकि हम जान लें कि कौन ईमान रखता है आख़िरत (परलोक) पर उनमें से, जो उसके विषय में किसी संदेह में हैं तथा आपका पालनहार प्रत्येक चीज़ का निरीक्षक है।[ ');
INSERT INTO "al_umari" VALUES(34,22,'34:22','आप कह दें: उन्हें पुकारो[ जिन्हें तुम (पूज्य) समझते हो अल्लाह के सिवा। वह नहीं अधिकार रखते कण बराबर भी, न आकाशों में न धरती में तथा नहीं है उनका उन दोनों में कोई भाग और नहीं है उस अल्लाह का उनमें से कोई सहायक। ');
INSERT INTO "al_umari" VALUES(34,23,'34:23','तथा नहीं लाभ देगी अभिस्तावना (सिफ़ारिश) अल्लाह के पास, परन्तु जिसके लिए अनुमति देगा।[ यहाँ तक कि जब दूर कर दिया जाता है उद्वेग उनके दिलों से, तो वे (फ़रिश्ते) कहते हैं कि तुम्हारे पालनहार ने क्या कहा? वे कहते हैं कि सत्य कहा तथा वह अति उच्च, महान है।');
INSERT INTO "al_umari" VALUES(34,24,'34:24','आप (मुश्रिकों) से प्रश्न करें कि कौन जीविका प्रदान करता है तुम्हें, आकाशों[ तथा धरती से? आप कह दें कि अल्लाह! तथा हम अथवा तुम अवश्य सुपथ पर हैं अथवा खुले कुपथ में हैं। ');
INSERT INTO "al_umari" VALUES(34,25,'34:25','आप कह दें: तुमसे नहीं प्रश्न किया जायेगा हमारे अपराधों के विषय में और न हमसे प्रश्न किया जायेगा, तुम्हारे कर्मों के[ संबन्ध में। ');
INSERT INTO "al_umari" VALUES(34,26,'34:26','आप कह दें कि एकत्र[ कर देगा हमें हमारा पालनहार। फिर निर्णय कर देगा हमारे बीच सत्य के साथ तथा वही अति निर्णयकारी, सर्वज्ञ है। ');
INSERT INTO "al_umari" VALUES(34,27,'34:27','आप कह दें कि तनिक मुझे उन्हें दिखा दो, जिन्हें तुमने मिला दिया है अल्लाह के साथ साझी[ बनाकर? ऐसा कदापि नहीं। बल्कि वही अल्लाह है अत्यंत प्रभावशाली तथा गुणी। ');
INSERT INTO "al_umari" VALUES(34,28,'34:28','तथा नहीं भेजा है हमने आप[ को, परन्तु सब मनुष्यों के लिए शुभ सूचना देने तथा सचेत करने वाला बनाकर। किन्तु, अधिक्तर लोग ज्ञान नहीं रखते। ');
INSERT INTO "al_umari" VALUES(34,29,'34:29','तथा वह कहते[ हैं कि ये वचन कब पूरा होगा, यदि तुम सत्यवादी हो? ');
INSERT INTO "al_umari" VALUES(34,30,'34:30','आप उनसे कह दें कि एक दिन वचन का निश्चित[ है। वे नहीं पीछे होंगे उससे क्षण भर और न आगे होंगे। ');
INSERT INTO "al_umari" VALUES(34,31,'34:31','तथा काफ़िरों ने कहा कि हम कदापि ईमान नहीं लायेंगे इस क़ुर्आन पर और न उसपर, जो इससे पूर्व की पुस्तक हैं और यदि आप देखेंगे इन अत्याचारियों को खड़े हुए अपने पालनहार के समक्ष, तो वे दोषारोपण कर रहे होंगे एक-दूसरे पर। जो निर्बल समझे जा रहे थे, वे कहेंगे उनसे, जो बड़े बन रहे थेः यदि तुम न होते, तो हम अवश्य ईमान लाने वालों[ में होते। ');
INSERT INTO "al_umari" VALUES(34,32,'34:32','वे कहेंगे जो बड़े बने हुए थे, उनसे, जो निर्बल समझे जा रहे थेः क्या हमने तुम्हे रोका सुपथ से, जब वह तुम्हारे पास आया? बल्कि तुमही अपराधी थे।');
INSERT INTO "al_umari" VALUES(34,33,'34:33','तथा कहेंगे जो निर्बल होंगे, उनसे, जो बड़े (अहंकारी) होंगेःबल्कि रात-दिन के षड्यंत्र[ ने (ऐसा किया), जब तुम हमें आदेश दे रहे थे कि हम कुफ़्र करें अल्लाह के साथ तथा बनायें उसके साझी तथा वे अपने मन में पछतायेंगे, जब यातना देखेंगे और हम तौक़ डाल देंगे उनके गलों में, जो काफ़िर हो गये, वे नहीं बदला दिये जायेंगे, परन्तु उसी का, जो वे कर रहे थे! ');
INSERT INTO "al_umari" VALUES(34,34,'34:34','और नहीं भेजा हमने किसी बस्ती में कोई सचेतकर्ता (नबी), परन्तु कहा उसके सम्पन्न लोगों नेः हम जिस चीज़ के साथ तुम भेजे गये हो, उसे नहीं मानते हैं।[ ');
INSERT INTO "al_umari" VALUES(34,35,'34:35','तथा कहा कि हम अधिक हैं तुमसे धन और संतान में तथा हम यातना ग्रस्त होने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(34,36,'34:36','आप कह दें कि वास्तव में, मेरा पालनहार फैला देता है जीविका को, जिसके लिए चाहता है और नापकर देता है। किन्तु, अधिक्तर लोग ज्ञान नहीं रखते।');
INSERT INTO "al_umari" VALUES(34,37,'34:37','और तुम्हारे धन और तुम्हारी संतान ऐसी नहीं हैं कि तुम्हें हमारे कुछ समीप[ कर दें। परन्तु, जो ईमान लाये तथा सदाचार करे, तो यही हैं, जिनके लिए दोहरा प्रतिफल है और यही ऊँचे भवनों में शान्त रहने वाले हैं। ');
INSERT INTO "al_umari" VALUES(34,38,'34:38','तथा जो प्रयास करते हैं हमारी आयतों में विवश करने के लिए,[ तो वही यातना में ग्रस्त होंगे। ');
INSERT INTO "al_umari" VALUES(34,39,'34:39','आप कह दें: मेरा पालनहार ही फैलाता है जीविका को जिसके लिए चाहता है, अपने भक्तों में से और तंग करता है उसके लिए और जो भी तुम दान करोगे, तो वह उसका पूरा बदला देगा और वही उत्तम जीविका देने वाला है।');
INSERT INTO "al_umari" VALUES(34,40,'34:40','तथा जिस दिन एकत्र करेगा उनसब को, फिर कहेगा फ़रिश्तों सेः क्या यही तुम्हारी इबादत (वंदना) कर रहे थे।');
INSERT INTO "al_umari" VALUES(34,41,'34:41','वह कहेंगेः तू पवित्र है! तू ही हमारा संरक्षक है, न कि ये। बल्कि ये इबादत करते रहे जिन्नों[ की। इनमें अधिक्तर उन्हींपर ईमान लाने वाले हैं। ');
INSERT INTO "al_umari" VALUES(34,42,'34:42','तो आज तुम[ में से कोई एक-दूसरे को लाभ अथवा हानि पहुँचाने का अधिकार नहीं रखेगा तथा हम कह देंगे अत्याचारियों से कि तुम अग्नि की यातना चखो, जिसे तुम झुठला रहे थे। ');
INSERT INTO "al_umari" VALUES(34,43,'34:43','और जब सुनाई जाती हैं उनके समक्ष हमारी खुली आयतें, तो कहते हैं: ये तो एक पुरुष है, जो चाहता है कि तुम्हें रोक दे उन पूज्यों से, जिनकी इबादत करते रहे हैं तुम्हारे पूर्वज तथा उन्होंने कहा कि ये तो बस एक झूठी बनायी हुई बात है तथा कहा काफ़िरों ने इस सत्य को कि ये तो बस एक प्रत्यक्ष (खुला) जादू है।');
INSERT INTO "al_umari" VALUES(34,44,'34:44','जबकि हमने नहीं प्रदान की है इन (मक्का वासियों) को कोई पुस्तक, जिसे ये पढ़ते हों तथा न हमने भेजा है इनकी ओर आपसे पहले कोई सचेत करने वाला।[ ');
INSERT INTO "al_umari" VALUES(34,45,'34:45','तथा झुठलाया था इनसे पूर्व के लोगों ने और नहीं पहुँचे ये उसके दसवें भाग को भी, जो हमने प्रदान किया था उन्हें। तो उन्होंने झुठला दिया मेरे रसूलों को, अन्ततः, मेरा इन्कार कैसा रहा?[ ');
INSERT INTO "al_umari" VALUES(34,46,'34:46','आप कह दें कि मैं बस तुम्हें एक बात की नसीह़त कर रहा हूँ कि तुम अल्लाह के लिए दो-दो तथा अकेले-अकेले खड़े हो जाओ। फिर सोचो। तुम्हारे साथी को कोई पागलपन नहीं है।[ वह तो बस सचेत करने वाले हैं तुम्हें, आगामी कड़ी यातना से। ');
INSERT INTO "al_umari" VALUES(34,47,'34:47','आप कह दें: मैंने तुमसे कोई बदला माँगा है, तो वह तुम्हारे[ ही लिए है। मेरा बदला तो बस अल्लाह पर है और वह प्रत्येक वस्तु पर साक्षी है। ');
INSERT INTO "al_umari" VALUES(34,48,'34:48','आप कह दें कि मेरा पालनहार वह़्यी करता है सत्य की। वह परोक्षों का अति ज्ञानी है।');
INSERT INTO "al_umari" VALUES(34,49,'34:49','आप कह दें कि सत्य आ गया और असत्य न (कुछ का) आरंभ कर सकता है और न (उसे) पुनः ला सकता है।');
INSERT INTO "al_umari" VALUES(34,50,'34:50','आप कह दें कि यदि मैं कुपथ हो गया, तो मेरे कुपथ होने का (भार) मुझपर है और यदि मैं सुपथ पर हूँ, तो उस वह़्यी के कारण जिसे मेरी ओर मेरा पालनहार उतार रहा है। वह सब कुछ सुनने वाला, समीप है।');
INSERT INTO "al_umari" VALUES(34,51,'34:51','तथा यदि आप देखेंगे, जब वे घबराये हुए[ होंगे, तो उनके खो जाने का कोई उपाय न होगा तथा पकड़ लिए जायेंगे समीप स्थान से। ');
INSERT INTO "al_umari" VALUES(34,52,'34:52','और कहेंगेः हम उस[ पर ईमान लाये तथा कहाँ हाथ आ सकता है उनके (ईमान), इतने दूर स्थान से?');
INSERT INTO "al_umari" VALUES(34,53,'34:53','जबकि उन्होंने कुफ़्र कर दिया पहले उसके साथ और तीर मारते रहे बिन देखे, दूर[ से। ');
INSERT INTO "al_umari" VALUES(34,54,'34:54','और रोक बना दी जायेगी उनके तथा उसके बीच, जिसकी वे कामना करेंगे जैसे किया गया इनके जैसों के साथ, इससे पहले। वास्तव में, वे संदेह में पड़े थे।');
INSERT INTO "al_umari" VALUES(35,1,'35:1','सब प्रशंसा अल्लाह के लिए हैं, जो उत्पन्न करने वाला है आकाशों तथा धरती का, (और) बनाने वाला[ है संदेशवाहक फ़रिश्तों को दो-दो, तीन-तीन, चार-चार परों वाले। वह अधिक करता है उत्पत्ति में, जो चाहता है, निःसंदेह अल्लाह जो चाहे, कर सकता है। ');
INSERT INTO "al_umari" VALUES(35,2,'35:2','जो खोल दे अल्लाह लोगों के लिए अपनी दया,[ तो उसे कोई रोकने वाला नहीं तथा जिसे रोक दे, तो कोई खोलने वाला नहीं उसका, उसके पश्चात् तथा वही प्रभावशाली चतुर है। ');
INSERT INTO "al_umari" VALUES(35,3,'35:3','हे मनुष्यो! याद करो अपने ऊपर अल्लाह के पुरस्कार को, क्या कोई उत्पत्तिकर्ता है अल्लाह कि सिवा, जो तुम्हें जीविका प्रदान करता हो आकाश तथा धरती से? नहीं है कोई वंदनीय, परन्तु वही। फिर तुम कहाँ फिरे जा रहे हो?');
INSERT INTO "al_umari" VALUES(35,4,'35:4','और यदि वे आपको झुठलाते हैं, तो झुठलाये जा चुके हैं बहुत-से रसूल आपसे पहले और अल्लाह ही की ओर फेरे जायेंगे सब विषय।[ ');
INSERT INTO "al_umari" VALUES(35,5,'35:5','हे लोगो! निश्चय अल्लाह का वचन सत्य है। अतः, तुम्हें धोखे में न रखे सांसारिक जीवन और न धोखे में रखें अल्लाह से, अति प्रवंचक (शैतान)।');
INSERT INTO "al_umari" VALUES(35,6,'35:6','वास्तव में, शैतान तुम्हारा शत्रु है। अतः, तुम उसे अपना शत्रु ही समझो। वह बुलाता है अपने गिरोह को इसीलिए ताकि वे नारकियों में हो जायें।');
INSERT INTO "al_umari" VALUES(35,7,'35:7','जो काफ़िर हो गये, उन्हीं के लिए कड़ी यातना है तथा जो ईमान लाये और सदाचार किये, तो उनके लिए क्षमा तथा बड़ा प्रतिफल है।');
INSERT INTO "al_umari" VALUES(35,8,'35:8','तथा क्या शोभनीय बना दिया गया हो जिसके लिए उसका कुकर्म और वह उसे अच्छा समझता हो? तो अल्लाह ही कुपथ करता है, जिसे चाहे और सुपथ दिखाता है, जिसे चाहे। अतः, न खोयें आप अपना प्राण इनपर संताप के कारण। वास्तव में, अल्लाह जानता है, जो कुछ वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(35,9,'35:9','तथा अल्लाह वही है, जो वायु को भेजता है, जो बादलों को उठाती है, फिर हम हाँक देते हैं उन्हें, निर्जीव नगर की ओर। फिर जीवित कर देते हैं उनके द्वारा धरती को, उसके मरण के पश्चात्। इसी प्रकार, फिर जीना (भी)[ होगा। ');
INSERT INTO "al_umari" VALUES(35,10,'35:10','जो सम्मान चाहता हो, तो अल्लाह ही के लिए है सब सम्मान और उसी की ओर चढ़ते हैं पवित्र वाक्य[ तथा सत्कर्म ही उनको ऊपर ले जाता है तथा जो दाव-घात में लगे रहते हैं बुराईयों की, तो उन्हीं के लिए कड़ी यातना है और उन्हीं के षड्यंत्र नाश हो जायेंगे।');
INSERT INTO "al_umari" VALUES(35,11,'35:11','अल्लाह ने उत्पन्न किया तुम्हें मिट्टी से, फिर वीर्य से, फिर बनाये तुम्हें जोड़े और नहीं गर्भ धारण करती कोई नारी और न जन्म देती है, परन्तु उसके ज्ञान से और नहीं आयु दिया जाता कोई अधिक और न कम की जाती है उसकी आयु, परन्तु वह एक लेख में[ है। वास्तव में, ये अल्लाह पर अति सरल है। ');
INSERT INTO "al_umari" VALUES(35,12,'35:12','तथा बराबर नहीं होते दो सागर, ये मधुर प्यास बुझाने वाला है, रुचिकर है इसका पीना और वो (दूसरा) खारी कड़वा है तथा प्रत्येक में से तुम खाते हो ताज़ा माँस तथा निकालते हो आभूषण, जिसे पहनते हो और तुम देखते हो नाव को उसमें पानी फाड़ती हुई, ताकि तुम खोज करो अल्लाह के अनुग्रह की और ताकि तुम कृतज्ञ बनो।');
INSERT INTO "al_umari" VALUES(35,13,'35:13','वह प्रवेश करता है रात को दिन में तथा प्रवेश करता है दिन को रात्रि में तथा वश में कर रखा है सूर्य तथा चन्द्रा को, प्रत्येक चलते रहेंगे एक निश्चित समय तक। वही अल्लाह तुम्हारा पालनहार है। उसी का राज्य है तथा जिन्हें तुम पुकारते हो, उसके सिवा, वे स्वामी नहीं हैं एक तिन्के के भी।');
INSERT INTO "al_umari" VALUES(35,14,'35:14','यदि तुम उन्हें पुकारते हो, तो वे नहीं सुनते तुम्हारी पुकार को और यदि सुन भी लें, तो नहीं उत्तर दे सकते तुम्हें और प्रलय के दिन वे नकार देंगे तुम्हारे शिर्क (साझी बनाने) को और आपको कोई सूचना नहीं देगा सर्वसूचित जैसी।[ ');
INSERT INTO "al_umari" VALUES(35,15,'35:15','हे मनुष्यो! तुम सभी भिक्षु हो अल्लाह के तथा अल्लाह ही निःस्वार्थ, प्रशंसित है।');
INSERT INTO "al_umari" VALUES(35,16,'35:16','यदि वह चाहे, तो तुम्हें ध्वस्त कर दे और नई[ उतपत्ति ले आये। ');
INSERT INTO "al_umari" VALUES(35,17,'35:17','और ये नहीं है अल्लाह पर कुछ कठिन।');
INSERT INTO "al_umari" VALUES(35,18,'35:18','तथा नहीं लादेगा कोई लादने वाला, दूसरे का बोझ, अपने ऊपर[ और यदि पुकारेगा कोई बोझल, उसे लादने के लिए, तो वह नहीं लादेगा उसमें से कुछ, चाहे वह उसका समीपवर्ती ही क्यों न हो। आप तो बस उन्हीं को सचेत कर रहे हैं, जो डरते हों अपने पालनहार से बिन देखे तथा जो स्थापना करते हैं नमाज़ की तथा जो पवित्र हुआ, तो वह पवित्र होगा अपने ही लाभ के लिए और अल्लाह ही की ओर (सबको) जाना है। ');
INSERT INTO "al_umari" VALUES(35,19,'35:19','तथा समान नहीं हो सकता अंधा तथा आँख वाला।');
INSERT INTO "al_umari" VALUES(35,20,'35:20','और न अंधकार तथा प्रकाश।');
INSERT INTO "al_umari" VALUES(35,21,'35:21','और न छाया तथा न धूप।');
INSERT INTO "al_umari" VALUES(35,22,'35:22','तथा समान नहीं हो सकते जीवित तथा निर्जीव।[ वास्तव में, अल्लाह ही सुनाता है जिसे चाहता है और आप नहीं सुना सकते उन्हें, जो क़ब्रों में हों। ');
INSERT INTO "al_umari" VALUES(35,23,'35:23','आप तो बस सचेतकर्ता हैं।');
INSERT INTO "al_umari" VALUES(35,24,'35:24','वास्तव में, हमने आपको सत्य के साथ शुभ सूचक तथा सचेतकर्ता बनाकर भेजा है और कोई ऐसा समुदाय नहीं, जिसमें कोई सचेतकर्ता न आया हो।');
INSERT INTO "al_umari" VALUES(35,25,'35:25','और यदि ये आपको झुठलायें, तो इनसे पूर्व के लोगों ने भी झुठलाया है, जिनके पास हमारे रसूल खुले प्रमाण तथा ग्रंथ और प्रकाशित पुस्तकें लाये।');
INSERT INTO "al_umari" VALUES(35,26,'35:26','फिर मैंने पकड़ लिया उन्हें, जो काफ़िर हो गये। तो कैसा रहा मेरा इन्कार।');
INSERT INTO "al_umari" VALUES(35,27,'35:27','क्या आपने नहीं देखा कि अल्लाह ने उतारा आकाश से जल, फिर हमने निकाल दिये उसके द्वारा बहुत-से फल विभिन्न रंगों के तथा पर्वतों के विभिन्न भाग हैं; स्वेत तथा लाल, विभिन्न रंगों के तथा गहरे काले।');
INSERT INTO "al_umari" VALUES(35,28,'35:28','तथा मनुष्य, जीवों तथा पशुओं में भी विभिन्न रंगों के हैं, इसी प्रकार। वास्तव में, डरते हैं अल्लाह से उसके भक्तों में से वही जो ज्ञानी[ हों। निःसंदेह अल्लाह अति प्रभुत्वशाली, क्षमी है। ');
INSERT INTO "al_umari" VALUES(35,29,'35:29','वास्तव में, जो पढ़ते हैं अल्लाह की पुस्तक (क़ुर्आन), उन्होंने स्थापना की नमाज़ की एवं दान किया उसमें से, जो हमने उन्हें प्रदान किया है, खुले तथा छुपे, तो वही आशा रखते हैं ऐसे व्यापार की, जो कदापि हानिकर नहीं होगा।');
INSERT INTO "al_umari" VALUES(35,30,'35:30','ताकि अल्लाह प्रदान करे उन्हें भरपूर उनका प्रतिफल तथा उन्हें अधिक दे अपने अनुग्रह से। वास्तव में, वह अति क्षमी, आदर करने वाला है।');
INSERT INTO "al_umari" VALUES(35,31,'35:31','तथा जो हमने प्रकाशना की है आपकी ओर ये पुस्तक, वही सर्वथा सच है और सच बताती है अपने पूर्व की पुस्तकों को। वास्तव में, अल्लाह अपने भक्तों से सूचित, भली-भाँति देखने वाला है।[ ');
INSERT INTO "al_umari" VALUES(35,32,'35:32','फिर हमने उत्तराधिकारी बनाया इस पुस्तक का उन्हें जिन्हें हमने चुन लिया अपने भक्तों में[ से। तो उनमें कुछ अत्याचारी हैं अपने ही लिए, उनमें से कुछ मध्यवर्ती हैं और कुछ अग्रसर हैं भलाई में अल्लाह की अनुमति से तथा यही महान अनुग्रह है। ');
INSERT INTO "al_umari" VALUES(35,33,'35:33','सदावास के स्वर्ग हैं, वे प्रवेश करेंगे उनमें और पहनाये जायेंगे उनमें सोने के कंगन तथा मोती और उनके वस्त्र उनमें रेशम के होंगे।');
INSERT INTO "al_umari" VALUES(35,34,'35:34','तथा वे कहेंगेः सब प्रशंसा उस अल्लाह के लिए हैं, जिसने दूर कर दिया हमसे शोक। वास्तव में, हमारा पालनहार अति क्षमी, गुणग्राही है।');
INSERT INTO "al_umari" VALUES(35,35,'35:35','जिसने हमें उतार दिया स्थायी घर में अपने अनुग्रह से। नहीं छूएगी उसमें हमें कोई आपदा और न छूएगी उसमें कोई थकान।');
INSERT INTO "al_umari" VALUES(35,36,'35:36','तथा जो काफ़िर हैं, उन्हीं के लिए नरक की अग्नि है। न तो उनकी मौत ही आयेगी, न वे मर जायें और न हल्की की जायेगी उनसे उसकी कुछ यातना। इसी प्रकार, हम बदला देते हैं प्रत्येक नाशुक्रे को।');
INSERT INTO "al_umari" VALUES(35,37,'35:37','और वे उसमें चिल्लायेंगेः हे हमारे पालनहार! हमें निकाल दे, हम सदाचार करेंगे उसके अतिरिक्त, जो कर रहे थे। क्या हमने तुम्हें इतनी आयु नहीं दी, जिसमें शिक्षा ग्रहण कर ले, जो शिक्षा ग्रहण करे तथा आया तुम्हारे पास सचेतकर्ता (नबी)? अतः, तुम चखो, अत्याचारियों का कोई सहायक नहीं है।');
INSERT INTO "al_umari" VALUES(35,38,'35:38','वास्तव में, अल्लाह ही ज्ञानी है आकाशों तथा धरती के भेद का। वास्तव में, वही भली-भाँति जानने वाला है सीनों की बातों का।');
INSERT INTO "al_umari" VALUES(35,39,'35:39','वही है, जिसने तुम्हें एक-दूसरे के पश्चात् बसाया है धरती में, तो जो कुफ़्र करेगा, तो उसके लिए है उसका कुफ़्र और नहीं बढ़ायेगा काफ़िरों के लिए उनका कुफ़् उनके पालनहार के यहाँ, परन्तु क्रोध ही और नहीं बढ़ायेगा काफ़िरों के लिए उनका क़फ़्र, परन्तु क्षति ही।');
INSERT INTO "al_umari" VALUES(35,40,'35:40','(हे नबी!)[ उनसे कहोः क्या तुमने देखा है अपने साझियों को, जिन्हें तुम पुकारते हो अल्लाह के अतिरिक्त? मुझे भी दिखाओ कि उन्होंने कितना भाग बनाया है धरती में से? या उनका आकाशों में कुछ साझा है? या हमने प्रदान की है उन्हें कोई पुस्तक, तो ये उसके खुले प्रमाणों पर हैं? बल्कि (बात ये है कि) अत्याचारी एक-दूसरे को केवल धोखे का वचन दे रहे हैं। ');
INSERT INTO "al_umari" VALUES(35,41,'35:41','अल्लाह ही रोकता[ है आकाशो तथा धरती को खिसक जाने से और यदि खिसक जायें वे दोनों, तो नहीं रोक सकेगा उन्हें कोई उस (अल्लाह) के पश्चात्। वास्तव में, वह अत्यंत सहनशील, क्षमाशील है। ');
INSERT INTO "al_umari" VALUES(35,42,'35:42','और उनकाफ़िरों ने शपथ ली थी अल्लाह की पक्की शपथ कि यदि आ गया, उनके पास कोई सचेतकर्ता (नबी), तो वे अवश्य हो जायेंगे सर्वाधिक संमार्ग पर समुदायों में से किसी एक से। फिर जब आ गये उनके पास एक रसूल,[ तो उनकी दूरी ही अधिक हुई। ');
INSERT INTO "al_umari" VALUES(35,43,'35:43','अभिमान के कारण धरती में तथा बुरे षड्यंत्र के कारण और नहीं घेरता है बुरा षड्यंत्र, परन्तु अपने करने वाले ही को। तो क्या वे प्रतीक्षा कर रहे हैं पूर्व के लोगों की नीति की?[ तो नहीं पायेंगे आप अल्लाह के नियम में कोई अन्तर। ');
INSERT INTO "al_umari" VALUES(35,44,'35:44','और क्या वे नहीं चले-फिरे धरती में, तो देख लेते कि कैसा रहा उनका दुष्परिणाम, जो इनसे पूर्व रहे, जबकि वह इनसे कड़े थे बल में? तथा अल्लाह ऐसा नहीं, वास्तव में, वह सर्वज्ञ, अति सामर्थ्यवान है।');
INSERT INTO "al_umari" VALUES(35,45,'35:45','और यदि पकड़ने लगता अल्लाह लोगों को उनके कर्मों के कारण, तो नहीं छोड़ता धरती के ऊपर कोई जीव। किन्तु, अवसर दे रहा है उन्हें एक निश्चित अवधि तक, फिर जब आ जायेगा उनका निश्चित समय, तो निश्चय अल्लाह अपने भक्तों को देख रहा[ है। ');
INSERT INTO "al_umari" VALUES(36,1,'36:1','या सीन।');
INSERT INTO "al_umari" VALUES(36,2,'36:2','शपथ है सुदृढ़ क़ुर्आन की!');
INSERT INTO "al_umari" VALUES(36,3,'36:3','वस्तुतः, आप रसूलों में से हैं।');
INSERT INTO "al_umari" VALUES(36,4,'36:4','सुपथ पर हैं।');
INSERT INTO "al_umari" VALUES(36,5,'36:5','(ये क़ुर्आन) प्रभुत्वशाली, अति दयावान् का अवतरित किया हुआ है।');
INSERT INTO "al_umari" VALUES(36,6,'36:6','ताकि आप सावधान करें उस जाति[ को, नहीं सावधान किये गये हैं जिनके पूर्वज। इस लिए वे अचेत हैं। ');
INSERT INTO "al_umari" VALUES(36,7,'36:7','सिध्द हो चुका है वचन[ उनमें से अधिक्तर लोगों पर। अतः, वे ईमान नहीं लायेंगे। ');
INSERT INTO "al_umari" VALUES(36,8,'36:8','तथा हमने डाल दिये हैं तौक़ उनके गलों में, जो हड्डियों तक[ हैं। इसलिए वे सिर ऊपर किये हुए हैं। ');
INSERT INTO "al_umari" VALUES(36,9,'36:9','तथा हमने बना दी है उनके आगे एक आड़ और उनके पीछे एक आड़। फिर ढाँक दिया है उनको, तो[ वे देख नहीं रहे हैं। ');
INSERT INTO "al_umari" VALUES(36,10,'36:10','तथा समान है उनपर कि आप उन्हें सावधान करें अथवा सावधान न करें, वे ईमान नहीं लायेंगे।');
INSERT INTO "al_umari" VALUES(36,11,'36:11','आप तो बस उसे सचेत कर सकेंगे, जो माने इस शिक्षा (क़ुर्आन) को तथा डरे अत्यंत कृपाशील से, बिन देखे। तो आप शुभ सूचना सुना दें उसे, क्षमा की तथा सम्मानित प्रतिफल की।');
INSERT INTO "al_umari" VALUES(36,12,'36:12','निश्चय हम ही जीवित करेंगे मुर्दों को तथा लिख रहे हैं, जो कर्म उन्होंने किया है और उनके पद् चिन्हों[ को तथा प्रत्येक वस्तु को हमने गिन रखा है, खुली पुस्तक में। ');
INSERT INTO "al_umari" VALUES(36,13,'36:13','तथा आप उन्हें[ एक उदाहरण दीजिये नगर वासियों का। जब आये उसमें कई रसूल। ');
INSERT INTO "al_umari" VALUES(36,14,'36:14','जब हमने भेजा उनकी ओर दो को। तो उन्होंने झुठला दिया उन दोनों को। फिर हमने समर्थन दिया तीसरे के द्वारा। तो तीनों ने कहाः हम तुम्हारी ओर भेजे गये हैं।');
INSERT INTO "al_umari" VALUES(36,15,'36:15','उन्होंने कहाः तुम सब तो मनुष्य ही हो, हमारे[ समान और नहीं अवतरित किया है अत्यंत कृपाशील ने कुछ भी। तुम सब तो बस झूठ बोल रहे हो। ');
INSERT INTO "al_umari" VALUES(36,16,'36:16','उन रसूलों ने कहाः हमारा पालनहार जानता है कि वास्तव में हम तुम्हारी ओर रसूल बनाकर भेजे गये हैं।');
INSERT INTO "al_umari" VALUES(36,17,'36:17','तथा हमारा दायित्व नहीं है खुला उपदेश पहुँचा देने के सिवा।');
INSERT INTO "al_umari" VALUES(36,18,'36:18','उन्होंने कहाः हम तुम्हें अशुभ समझ रहे हैं। यदि तुम रुके नहीं, तो हम तुम्हें अवश्य पथराव करके मार डालेंगे और तुम्हें अवश्य हमारी ओर से पहुँचेगी दुःखदायी यातना।');
INSERT INTO "al_umari" VALUES(36,19,'36:19','उन्होंने कहाः तुम्हारा अशुभ तुम्हारे साथ है। क्या यदि तुम्हें शिक्षा दी जाये (तो अशुभ समझते हो)? बल्कि तुम उल्लंघनकारी जाति हो।');
INSERT INTO "al_umari" VALUES(36,20,'36:20','तथा आया नगर के अन्ति किनारे से, एक पुरुष दौड़ता हुआ। उसने कहाः हे मेरी जाति के लोगो! अनुसरण करो रसूलों का।');
INSERT INTO "al_umari" VALUES(36,21,'36:21','अनुसरण करो उनका, जो तुमसे नहीं माँगते कोई पारिश्रमिक (बदला) तथा वे सुपथ पर हैं।');
INSERT INTO "al_umari" VALUES(36,22,'36:22','तथा मुझे क्या हुआ है कि मैं उसकी इबादत (वंदना) न करूँ, जिसने मुझे पैदा किया है? और तुमसब उसी की ओर फेरे जाओगे।[ ');
INSERT INTO "al_umari" VALUES(36,23,'36:23','क्या मैं बना लूँ उसे छोड़कर बहुत-से पूज्य? यदि अत्यंत कृपाशील मुझे कोई हानि पहुँचाना चाहे, तो नहीं लाभ पहूँचायेगी मुझे उनकी अनुशंसा (सिफ़ारिश) कुछ और न वे मुझे बचा सकेंगे।');
INSERT INTO "al_umari" VALUES(36,24,'36:24','वास्तव में, तब तो मैं खुले कुपथ में हूँ।');
INSERT INTO "al_umari" VALUES(36,25,'36:25','निश्चय, मैं ईमान लाया तुम्हारे पालनहार पर, अतः, मेरी सुनो।');
INSERT INTO "al_umari" VALUES(36,26,'36:26','(उससे) कहा गयाः तुम प्रवेश कर जाओ स्वर्ग में। उसने कहाः काश मेरी जाति जानती!');
INSERT INTO "al_umari" VALUES(36,27,'36:27','जिसकारण क्षमा[ कर दिया मुझे मेरे पालनहार ने और मुझे सम्मिलित कर दिया सम्मानितों में। ');
INSERT INTO "al_umari" VALUES(36,28,'36:28','तथा हमने नहीं उतारी उसकी जाति पर उसके पश्चात् कोई सेना[ आकाश से और न हमें उतारने की आवश्यक्ता थी। ');
INSERT INTO "al_umari" VALUES(36,29,'36:29','वह तो बस एक कड़ी ध्वनि थी। फिर सहसा सबके सब बुझ गये।[ ');
INSERT INTO "al_umari" VALUES(36,30,'36:30','हाय संताप है[ भक्तों पर! नहीं आया उनके पास रसूल, परन्तु वे उसका उपहास करते रहे। ');
INSERT INTO "al_umari" VALUES(36,31,'36:31','क्या उन्होंने नहीं देखा कि उनसे पहले विनाश कर दिया बहुत-से समुदायों का। वे उनकी ओर दोबारा फिरकर नहीं आयेंगे।');
INSERT INTO "al_umari" VALUES(36,32,'36:32','तथा सबके सब हमारे समक्ष उपस्थित किये[ जायेंगे। ');
INSERT INTO "al_umari" VALUES(36,33,'36:33','तथा उन[ के लिए एक निशानी है निर्जीव (सूखी) धरती। जिसे हमने जीवित कर दिया और हमने निकाले उससे अन्न, तो तुम उसीमें से खाते हो। ');
INSERT INTO "al_umari" VALUES(36,34,'36:34','तथा पैदा कर दिये उसमें बाग़ खजूरों तथा अंगूरों के और फाड़ दिये उसमें जल स्रोत।');
INSERT INTO "al_umari" VALUES(36,35,'36:35','ताकि वे खायें उसके फल और नहीं बनाया है उसे उनके हाथों ने। तो क्या वे कृतज्ञ नहीं होते?');
INSERT INTO "al_umari" VALUES(36,36,'36:36','पवित्र है वह, जिसने पैदा किये प्रत्येक जोड़े, उसके जिसे उगाती है धरती तथा स्वयं उनकी अपनी जाति के और उसके, जिसे तुम नहीं जानते हो।');
INSERT INTO "al_umari" VALUES(36,37,'36:37','तथा एक निशानी (चिन्ह) है उनके लिए रात्रि। खींच लेते हैं हम जिससे दिन को, तो सहसा वह अंधेरों में हो जाते हैं।');
INSERT INTO "al_umari" VALUES(36,38,'36:38','तथा सूर्य चला जा रहा है अपने निर्धारित स्थान की ओर। ये प्रभुत्वशाली सर्वज्ञ का निर्धारित किया हुआ है।');
INSERT INTO "al_umari" VALUES(36,39,'36:39','तथा चन्द्रमा के हमने निर्धारित कर दिये हैं गंतव्य स्थान। यहाँतक कि फिर वह हो जाता है पुरानी खजूर की सूखी शाखा के समान।');
INSERT INTO "al_umari" VALUES(36,40,'36:40','न तो सूर्य के लिए ही उचित है कि चन्द्रमा को पा जाये और न रात अग्रगामी हो सकती है दिन से। सब एक मण्डल में तैर रहे हैं।');
INSERT INTO "al_umari" VALUES(36,41,'36:41','तथा उनके लिए एक निशानी (लक्षण) (ये भी) है कि हमने सवार किया उनकी संतान को भरी हुई नाव में।');
INSERT INTO "al_umari" VALUES(36,42,'36:42','तथा हमने पैदा किया उनके लिए उसके समान वह चीज़, जिसपर वे सवार होते हैं।');
INSERT INTO "al_umari" VALUES(36,43,'36:43','और यदि हम चाहें, तो उन्हें जलमगन कर दें। तो न कोई सहायक होगा उनका और न वे निकाले (बचाये) जायेंगे।');
INSERT INTO "al_umari" VALUES(36,44,'36:44','परन्तु, हमारी दया से तथ लाभ देने के लिए एक समय तक।');
INSERT INTO "al_umari" VALUES(36,45,'36:45','और[ जब उनसे कहा जाता है कि डरो उस (यातना) से, जो तुम्हारे आगे तथा तुम्हारे पीछे है, ताकि तुमपर दया की जाये। ');
INSERT INTO "al_umari" VALUES(36,46,'36:46','तथा नहीं आती उनके पास कोई निशानी उनके पालनहार की निशानियों में से, परन्तु वे उससे मुँह फेर लेते हैं।');
INSERT INTO "al_umari" VALUES(36,47,'36:47','तथा जब उनसे कहा जाता है कि दान करो उसमें से, जो प्रदान किया है अल्लाह ने तुम्हें, तो कहते हैं जो काफ़िर हो गये उनसे, जो ईमान लाये हैं: क्या हम उसे खाना खिलायें, जिसे यदि अल्लाह चाहे, तो खिला सकता है? तुमतो खुले कुपथ में हो।');
INSERT INTO "al_umari" VALUES(36,48,'36:48','और वे कहते हैं कि कब ये (प्रलय) का वचन पूरा होगा, यदि तुम सत्यवादी हो?');
INSERT INTO "al_umari" VALUES(36,49,'36:49','वह नहीं प्रतीक्षा कर रहे हैं, परन्तु एक कड़ी ध्वनि[ की, जो उन्हें पकड़ लेगी और वह झगड़ रहे होंगे। ');
INSERT INTO "al_umari" VALUES(36,50,'36:50','तो न वह कोई वसिय्यत कर सकेंगे और न अपने परिजनों में वापस आ सकेंगे।');
INSERT INTO "al_umari" VALUES(36,51,'36:51','तथा फूँका[ जायेगा सूर (नरसिंघा) में, तो वह सहसा समाधियों से अपने पालनहार की ओर भागते हुए चलने लगेंगे। ');
INSERT INTO "al_umari" VALUES(36,52,'36:52','वे कहेंगेः हाय हमारा विनाश! किसने हमें जगा दिया हमारे विश्राम गृह से? ये वो है, जिसका वचन दिया था अत्यंत कृपाशील ने तथा सच कहा था रसूलों ने।');
INSERT INTO "al_umari" VALUES(36,53,'36:53','नहीं होगी वह, परन्तु एक कड़ी ध्वनि। फिर सहसा वे सबके सब, हमारे समक्ष उपस्थित कर दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(36,54,'36:54','तो आज नहीं अत्याचार किया जायेगा किसी प्राणी पर कुछ और तुम्हें उसी का प्रतिफल (बदला) दिया जायेगा, जो तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(36,55,'36:55','वास्तव में, स्वर्गीय आज अपने आनन्द में लगे हुए हैं।');
INSERT INTO "al_umari" VALUES(36,56,'36:56','वे तथा उनकी पत्नियाँ सायों में हैं, मस्नदों पर तकिये लगाये हुए।');
INSERT INTO "al_umari" VALUES(36,57,'36:57','उनके लिए उसमें प्रत्येक प्रकार के फल हैं तथा उनके लिए वो है, जिसकी वे माँग करें।');
INSERT INTO "al_umari" VALUES(36,58,'36:58','(उन्हें) सलाम कहा गया है अति दयावान् पालनहार की ओर से।');
INSERT INTO "al_umari" VALUES(36,59,'36:59','तथा तुम अलग[ हो जाओ आज, हे अपराधियो! ');
INSERT INTO "al_umari" VALUES(36,60,'36:60','हे आदम की संतान! क्या मैंने तुमसे बल देकर नहीं कहा था कि इबादत (वंदना) न करना शैतान की? वास्तव में, वह तुम्हारा खुला शत्रु है। ');
INSERT INTO "al_umari" VALUES(36,61,'36:61','तथा इबादत (वंदना) करना मेरी ही, यही सीधी डगर है।');
INSERT INTO "al_umari" VALUES(36,62,'36:62','तथा वह कुपथ कर चुका है, तुममें से बहुत-से समुदायों को, तो क्या तुम समझते नही हो?');
INSERT INTO "al_umari" VALUES(36,63,'36:63','यही नरक है, जिसका वचन तुम्हें दिया जा रहा था।');
INSERT INTO "al_umari" VALUES(36,64,'36:64','आज, प्रवेश कर जाओ उसमें, उस कुफ़्र के बदले, जो तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(36,65,'36:65','आज, हम मुहर (मुद्रा) लगा देंगे उनके मुखों पर और हमसे बात करेंगे उनके हाथ तथा साक्ष्य (गवाही) देंगे उनके पैर, उनके कर्मों की, जो वे कर रहे थे।[ ');
INSERT INTO "al_umari" VALUES(36,66,'36:66','और यदि हम चाहते, तो उनकी आँखें अंधी कर देते। फिर वे दौड़ते संगार्ग की ओर, परन्तु कहाँ से देखते?');
INSERT INTO "al_umari" VALUES(36,67,'36:67','और यदि हम चाहते, तो विकृत कर देते उन्हें, उनके स्थान पर, तो न वे आगे जा सकते थे, न पीछे फिर सकते थे।');
INSERT INTO "al_umari" VALUES(36,68,'36:68','तथा जिसे हम अधिक आयु देते हैं, उसे उत्पत्ति में, प्रथम दशा[ की ओर फेर देते हैं। तो क्या वे समझते नहीं हैं? ');
INSERT INTO "al_umari" VALUES(36,69,'36:69','और हमने नहीं सिखाया नबी को काव्य[ और न ये उनके लिए योग्य है। ये तो मात्र, एक शिक्षा तथा खुला क़ुर्आन है। ');
INSERT INTO "al_umari" VALUES(36,70,'36:70','ताकि वो सचेत करें, उसे जो जीवित हो[ तथा सिध्द हो जाये यातना की बात, काफ़िरों पर। ');
INSERT INTO "al_umari" VALUES(36,71,'36:71','क्या मनुष्य ने नहीं देखा कि हमने पैदा किये हैं उनके लिए उसमें से, जिसे बनाया है हमारे हाथों ने चौपाये। तो वे उनके स्वामी हैं?');
INSERT INTO "al_umari" VALUES(36,72,'36:72','तथा हमने वश में कर दिया उन्हें, उनके, तो उनमें से कुछ उनकी सवारी हैं तथा उनमें से कुछ को वे खाते हैं।');
INSERT INTO "al_umari" VALUES(36,73,'36:73','तथा उनके लिए उनमें बहुत-से लाभ तथा पेय हैं। तो क्या (फिर भी) वे कृतज्ञ नहीं होते?');
INSERT INTO "al_umari" VALUES(36,74,'36:74','और उन्होंने बना लिया अल्लाह के सिवा बहुत-से पूज्य कि संभवतः, वे उनकी सहायता करेंगे।');
INSERT INTO "al_umari" VALUES(36,75,'36:75','वे स्वयं अपनी सहायता नहीं कर सकेंगे तथा वे उनकी सेना हैं, (यातना) में[ उपस्थित। ');
INSERT INTO "al_umari" VALUES(36,76,'36:76','अतः, आपको उदासीन न करे उनकी बात। वस्तुतः, हम जानते हैं, जो वे मन में रखते हैं तथा जो बोलते हैं।');
INSERT INTO "al_umari" VALUES(36,77,'36:77','और क्या नहीं देखा मनुष्य ने कि पैदा किया हमने उसे वीर्य से? फिर भी वह खुला झगड़ालू है।');
INSERT INTO "al_umari" VALUES(36,78,'36:78','और उसने वर्णन किया हमारे लिए एक उदाहरण, और अपनी उत्पत्ति को भूल गया। उसने कहाः कौन जीवित करेगा इन अस्थियों को, जबकि वे जीर्न हो चुकी होंगी?');
INSERT INTO "al_umari" VALUES(36,79,'36:79','आप कह दें: वही, जिसने पैदा किया है प्रथम बार और वह प्रत्येक उत्पत्ति को भली-भाँति जानने वाला है।');
INSERT INTO "al_umari" VALUES(36,80,'36:80','जिसने बना दी तुम्हारे लिए हरे वृक्ष से अग्नि, तो तुम उससे आग[ सुलगाते हो। ');
INSERT INTO "al_umari" VALUES(36,81,'36:81','तथा क्या जिसने आकाशों तथा धरती को पैदा किया है वह सामर्थ्य नहीं रखता इसपर कि पैदा करे उसके समान? क्यों नहीं जबकि वह रचयिता, अति ज्ञाता है?');
INSERT INTO "al_umari" VALUES(36,82,'36:82','उसका आदेश, जब वह किसी चीज़ को अस्तित्व प्रदान करना चाहे, तो बस ये कह देना हैः हो जा। तत्क्षण वह हो जाती है।');
INSERT INTO "al_umari" VALUES(36,83,'36:83','तो पवित्र है वह, जिसके हाथ में प्रत्येक वस्तु का राज्य है और तुमसब उसी की ओर फेरे[ जाओगे। ');
INSERT INTO "al_umari" VALUES(37,1,'37:1','शपथ है पंक्तिवध्द (फ़रिश्तों) की!');
INSERT INTO "al_umari" VALUES(37,2,'37:2','फिर झिड़कियाँ देने वालों की!');
INSERT INTO "al_umari" VALUES(37,3,'37:3','फिर स्मरण करके पढ़ने वालों[ की! ');
INSERT INTO "al_umari" VALUES(37,4,'37:4','निश्चय तुम्हारा पूज्य, एक ही है।');
INSERT INTO "al_umari" VALUES(37,5,'37:5','आकाशों तथा धरती का पालनहार तथा जो कुछ उनके मध्य है और सुर्योदय होने के स्थानों का रब।');
INSERT INTO "al_umari" VALUES(37,6,'37:6','हमने अलंकृत किया है संसार (समीप) के आकाश को, तारों की शोभा से।');
INSERT INTO "al_umari" VALUES(37,7,'37:7','तथा रक्षा करने के लिए प्रत्येक उध्दत शैतान से।');
INSERT INTO "al_umari" VALUES(37,8,'37:8','वह नहीं सुन सकते (जाकर) उच्च सभा तक फ़रिश्तों की बात तथा मारे जाते हैं, प्रत्येक दिशा से।');
INSERT INTO "al_umari" VALUES(37,9,'37:9','राँदने के लिए तथा उनके लिए स्थायी यातना है।');
INSERT INTO "al_umari" VALUES(37,10,'37:10','परन्तु, जो ले उड़े कुछ, तो पीछा करती है उसका दहकती ज्वाला।[ ');
INSERT INTO "al_umari" VALUES(37,11,'37:11','तो आप इन (काफ़िरों) से प्रश्न करें कि क्या उन्हें पैदा करना अधिक कठिन है या जिन्हें[ हमने पैदा किया है? हमने उन्हें पैदा किया है, लेसदार मिट्टी से।');
INSERT INTO "al_umari" VALUES(37,12,'37:12','बल्कि आपने आश्चर्य किया (उनके अस्वीकार पर) तथा वे उपहास करते हैं।');
INSERT INTO "al_umari" VALUES(37,13,'37:13','और जब शिक्षा दी जाये, तो वे शिक्षा ग्रहण नहीं करते।');
INSERT INTO "al_umari" VALUES(37,14,'37:14','और जब देखते हैं कोई निशानी, तो उपहास करने लगते हैं।');
INSERT INTO "al_umari" VALUES(37,15,'37:15','तथा कहते हें कि ये तो मात्र खुला जादू है।');
INSERT INTO "al_umari" VALUES(37,16,'37:16','(कहते हैं कि) क्या, जब हम मर जायेंगे तथा मिट्टी और हड्डियाँ हो जायेंगे, तो हम निश्चय पुनः जीवित किये जायेंगे?');
INSERT INTO "al_umari" VALUES(37,17,'37:17','और क्या, हमारे पहले पूर्वज भी (जीवित किये जायेंगे)?');
INSERT INTO "al_umari" VALUES(37,18,'37:18','आप कह दें कि हाँ तथा तुम अपमानित (भी) होगे!');
INSERT INTO "al_umari" VALUES(37,19,'37:19','वो तो बस एक झिड़की होगी, फिर सहसा वे देख रहे होंगे।');
INSERT INTO "al_umari" VALUES(37,20,'37:20','तथा कहेंगेः हाय हमारा विनाश! ये तो बदले (प्रलय) का दिन है।');
INSERT INTO "al_umari" VALUES(37,21,'37:21','यही निर्णय का दिन है, जिसे तुम झुठला रहे थे।');
INSERT INTO "al_umari" VALUES(37,22,'37:22','(आदेश होगा कि) घेर लाओ सब अत्याचारियों को तथा उनके साथियों को और जिसकी वे इबादत (वंदना) कर रहे थे।');
INSERT INTO "al_umari" VALUES(37,23,'37:23','अल्लाह के सिवा। फिर दिखा दो उन्हें नरक की राह।');
INSERT INTO "al_umari" VALUES(37,24,'37:24','और उन्हें रोक[ लो। उनसे प्रश्न किया जाये। ');
INSERT INTO "al_umari" VALUES(37,25,'37:25','क्या हो गया है तुम्हें कि एक-दूसरे की सहायता नहीं करते?');
INSERT INTO "al_umari" VALUES(37,26,'37:26','बल्कि वे उस दिन, सिर झुकाये खड़े होंगे।');
INSERT INTO "al_umari" VALUES(37,27,'37:27','और एक-दूसरे के सम्मुख होकर परस्पर प्रश्न करेंगेः[ ');
INSERT INTO "al_umari" VALUES(37,28,'37:28','कहेंगे कि तुम हमारे पास आया करते थे दायें[ से। ');
INSERT INTO "al_umari" VALUES(37,29,'37:29','वे[ कहेंगेः बल्कि तुम स्वयं ईमान वाले न थे। ');
INSERT INTO "al_umari" VALUES(37,30,'37:30','तथा नहीं था हमारा तुमपर कोई अधिकार,[ बल्कि तुम सवंय अवज्ञाकारी थे। ');
INSERT INTO "al_umari" VALUES(37,31,'37:31','तो सिध्द हो गया हमपर हमारे पालनहार का कथन कि हम (यातना) चखने वाले हैं।');
INSERT INTO "al_umari" VALUES(37,32,'37:32','तो हमने तुम्हें कुपथ कर दिया। हम तो स्वयं कुपथ थे।');
INSERT INTO "al_umari" VALUES(37,33,'37:33','फिर वे सभी, उस दिन यातना में साझी होंगे।');
INSERT INTO "al_umari" VALUES(37,34,'37:34','हम, इसी प्रकार, किया करते हैं अपराधियों के साथ।');
INSERT INTO "al_umari" VALUES(37,35,'37:35','ये वो हैं कि जब कहा जाता था उनसे कि कोई पूज्य (वंदनीय) नहीं अल्लाह के अतिरिक्त, तो वे अभिमान करते थे।');
INSERT INTO "al_umari" VALUES(37,36,'37:36','तथा कह रहे थेः क्या हम त्याग देने वाले हैं अपने पूज्यों को, एक उन्मत कवि के कारण?');
INSERT INTO "al_umari" VALUES(37,37,'37:37','बल्कि वह (नबी) सच लाये हैं तथा पुष्टि की है, सब रसूलों की।');
INSERT INTO "al_umari" VALUES(37,38,'37:38','निश्चय तुम दुःखदायी यातना चखने वाले हो।');
INSERT INTO "al_umari" VALUES(37,39,'37:39','तथा तुम उसका प्रतिकार (बदला) दिये जाओगे, जो तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(37,40,'37:40','परन्तु अल्लाह के शुध्द भक्त।');
INSERT INTO "al_umari" VALUES(37,41,'37:41','यही हैं, जिनके लिए विदित जीविका है।');
INSERT INTO "al_umari" VALUES(37,42,'37:42','प्रत्येक प्रकार के फल तथा यही आदरणीय होंगे।');
INSERT INTO "al_umari" VALUES(37,43,'37:43','सुख के स्वर्गों में।');
INSERT INTO "al_umari" VALUES(37,44,'37:44','आसनों पर एक-दूसरे के सम्मुख असीन होंगे।');
INSERT INTO "al_umari" VALUES(37,45,'37:45','फिराये जायेंगे इनपर प्याले, प्रवाहित पेय के।');
INSERT INTO "al_umari" VALUES(37,46,'37:46','श्वेत आस्वात पीने वालों के लिए।');
INSERT INTO "al_umari" VALUES(37,47,'37:47','नहीं होगी उसमें शारिरिक पीड़ा, न वे उसमें बहकेंगे।');
INSERT INTO "al_umari" VALUES(37,48,'37:48','तथा उनके पास आँखे झुकाये, (सति) बड़ी आँखों वाली (नारियाँ) होंगी।');
INSERT INTO "al_umari" VALUES(37,49,'37:49','वह छुपाये हुए अन्डों के मानिन्द होंगी।[ ');
INSERT INTO "al_umari" VALUES(37,50,'37:50','वह एक-दूसरे से सम्मुख होकर प्रश्न करेंगे।');
INSERT INTO "al_umari" VALUES(37,51,'37:51','तो कहेगा एक कहने वाला उनमें सेः मेरा एक साथी था।');
INSERT INTO "al_umari" VALUES(37,52,'37:52','जो कहता था कि क्या तुम (प्रलय का) विश्वास करने वालों में से हो?');
INSERT INTO "al_umari" VALUES(37,53,'37:53','क्या जब हम, मर जायेंगे तथा मिट्टी और अस्थियाँ हो जायेंगे, तो क्या हमें (कर्मों) का प्रतिफल दिया जायेगा?');
INSERT INTO "al_umari" VALUES(37,54,'37:54','वह कहेगाः क्या तुम झाँककर देखने वाले हो?');
INSERT INTO "al_umari" VALUES(37,55,'37:55','फिर झाँकते ही उसे देख लेगा, नरक के बीच।');
INSERT INTO "al_umari" VALUES(37,56,'37:56','उससे कहेगाः अल्लाह की शपथ! तुम तो मेरा विनाश कर देने के समीप थे।');
INSERT INTO "al_umari" VALUES(37,57,'37:57','और यदि मेरे पालनहार का अनुग्रह न होता, तो मैं (नरक के) उपस्थितों में होता।');
INSERT INTO "al_umari" VALUES(37,58,'37:58','फिर वह कहेगाः क्या (ये सही नहीं है कि) हम मरने वाले नहीं हैं?');
INSERT INTO "al_umari" VALUES(37,59,'37:59','सिवाय अपनी प्रथम मौत के और न हमें यातना दी जायेगी।');
INSERT INTO "al_umari" VALUES(37,60,'37:60','वास्तव में, यही बड़ी सफलता है।');
INSERT INTO "al_umari" VALUES(37,61,'37:61','इसी (जैसी सफलता) के लिए चाहिये कि कर्म करें, कर्म करने वाले।');
INSERT INTO "al_umari" VALUES(37,62,'37:62','क्या ये आतिथ्य उत्तम है अथवा थोहड़ का वृक्ष?');
INSERT INTO "al_umari" VALUES(37,63,'37:63','हमने उसे अत्याचारियों के लिए एक परीक्षा बनाया है।');
INSERT INTO "al_umari" VALUES(37,64,'37:64','वह एक वृक्ष है, जो नरक की जड़ (तह) से निकलता है।');
INSERT INTO "al_umari" VALUES(37,65,'37:65','उसके गुच्छे शैतानों के सिरों के समान हैं।');
INSERT INTO "al_umari" VALUES(37,66,'37:66','तो वे (नरक वासी) खाने वाले हैं, उससे। फिर भरने वाले हैं, उससे अपने पेट।');
INSERT INTO "al_umari" VALUES(37,67,'37:67','फिर उनके लिए उसके ऊपर से खौलता गरम पानी है।');
INSERT INTO "al_umari" VALUES(37,68,'37:68','फिर उन्हें प्रत्यागत होना है, नरक की ओर।');
INSERT INTO "al_umari" VALUES(37,69,'37:69','वास्तव में, उन्होंने पाया अपने पूर्वजों को कुपथ।');
INSERT INTO "al_umari" VALUES(37,70,'37:70','फिर वे उन्हीं के पद्चिन्हों पर[ दौड़े चले जा रहे हैं। ');
INSERT INTO "al_umari" VALUES(37,71,'37:71','और कुपथ हो चुके हैं, इनसे पूर्व अगले लोगों में से अधिक्तर।');
INSERT INTO "al_umari" VALUES(37,72,'37:72','तथा हम भेज चुके हैं उनमें, सचेत (सावधान) करने वाले।');
INSERT INTO "al_umari" VALUES(37,73,'37:73','तो देखो कि कैसा रहा सावधान किये हुए लोगों का परिणाम?[ ');
INSERT INTO "al_umari" VALUES(37,74,'37:74','हमारे शुध्द भक्तों के सिवा।');
INSERT INTO "al_umari" VALUES(37,75,'37:75','तथा हमें पुकारा नूह़ नेः तो हम क्या ही अच्छे प्रार्थना स्वीकार करने वाले हैं।');
INSERT INTO "al_umari" VALUES(37,76,'37:76','और हमने बचा लिया उसे और उसके परिजनों को, घोर आपदा से।');
INSERT INTO "al_umari" VALUES(37,77,'37:77','तथा कर दिया हमने उसकी संतति को, शेष[ रह जाने वालों में से। ');
INSERT INTO "al_umari" VALUES(37,78,'37:78','तथा शेष रखा हमने उसकी सराहना तथा प्रशंसा को पिछलों में।');
INSERT INTO "al_umari" VALUES(37,79,'37:79','सलाम (सुरक्षा)[ है नूह़ के लिए समस्त विश्ववासियों में। ');
INSERT INTO "al_umari" VALUES(37,80,'37:80','इसी प्रकार, हम प्रतिफल प्रदान करते हैं सदाचारियों को।');
INSERT INTO "al_umari" VALUES(37,81,'37:81','वास्तव में, वह हमारे ईमान वाले भक्तों में से था।');
INSERT INTO "al_umari" VALUES(37,82,'37:82','फिर हमने जलमगन कर दिया दूसरों को।');
INSERT INTO "al_umari" VALUES(37,83,'37:83','और उसके अनुयायियों में निश्चय इब्राहीम है।');
INSERT INTO "al_umari" VALUES(37,84,'37:84','जब लाया वह अपने पालनहार के पास स्वच्छ दिल।');
INSERT INTO "al_umari" VALUES(37,85,'37:85','जब कहा उसने अपने पिता तथा अपनी जाति सेः तुम किसकी इबादत (वंदना) कर रहे हो?');
INSERT INTO "al_umari" VALUES(37,86,'37:86','क्या अपने बनाये हुए पूज्यों को अल्लाह के सिवा चाहते हो?');
INSERT INTO "al_umari" VALUES(37,87,'37:87','तो तुम्हारा क्या विचार है, विश्व के पालनहार के विषय में?');
INSERT INTO "al_umari" VALUES(37,88,'37:88','फिर उसने देखा तोरों की[ ओर। ');
INSERT INTO "al_umari" VALUES(37,89,'37:89','तथा उनसे कहाः मैं रोगी हूँ।');
INSERT INTO "al_umari" VALUES(37,90,'37:90','तो उसे छोड़कर चले गये।');
INSERT INTO "al_umari" VALUES(37,91,'37:91','फिर वह जा पहुँचा, उनके उपास्यों (पूज्यों) की ओर। कहा कि (वे प्रसाद) क्यों नहीं खाते?');
INSERT INTO "al_umari" VALUES(37,92,'37:92','तुम्हें क्या हुआ है कि बोलते नहीं?');
INSERT INTO "al_umari" VALUES(37,93,'37:93','फिर पिल पड़ा उनपर मारते हुए, दायें हाथ से।');
INSERT INTO "al_umari" VALUES(37,94,'37:94','तो वे आये उसकी ओर दौड़ते हुए।');
INSERT INTO "al_umari" VALUES(37,95,'37:95','इब्राहीम ने कहाः क्या तुम इबादत (वंदना) करते हो उसकी जिसे, पत्थरों से तराश्ते हो?');
INSERT INTO "al_umari" VALUES(37,96,'37:96','जबकि अल्लाह ने पैदा किया है तुम्हें तथा जो तुम करते हो।');
INSERT INTO "al_umari" VALUES(37,97,'37:97','उन्होंने कहाः इसके लिए एक (अग्निशाला का) निर्माण करो और उसे झोंक दो दहकती अग्नि में।');
INSERT INTO "al_umari" VALUES(37,98,'37:98','तो उन्होंने उसके साथ षड्यंत्र रचा, तो हमने उन्हीं को नीचा कर दिया।');
INSERT INTO "al_umari" VALUES(37,99,'37:99','तथा उसने कहाः मैं जाने वाला हूँ अपने पालनहार की[ ओर। वह मुझे सुपथ दर्शायेगा। ');
INSERT INTO "al_umari" VALUES(37,100,'37:100','हे मेरे पालनहार! प्रदान कर मुझे, एक सदाचारी (पुनीत) पुत्र।');
INSERT INTO "al_umari" VALUES(37,101,'37:101','तो हमने शुभ सूचना दी उसे, एक सहनशील पुत्र की।');
INSERT INTO "al_umari" VALUES(37,102,'37:102','फिर जब वह पहुँचा उसके साथ चलने-फिरने की आयु को, तो इब्राहीम ने कहाः हे मेरे प्रिय पुत्र! मैं देख रहा हूँ स्वप्न में कि मैं तुझे वध कर रहा हूँ। अब, तू बता कि तेरा क्या विचार है? उसने कहाः हे पिता! पालन करें, जिसका आदेश आपको दिया जा रहा है। आप पायेंगे मुझे सहनशीलों में से, यदि अल्लाह की इच्छा हूई।');
INSERT INTO "al_umari" VALUES(37,103,'37:103','अन्ततः, जब दोनों ने स्वयं को अर्पित कर दिया और उस (पिता) ने उसे गिरा दिया माथे के बल।');
INSERT INTO "al_umari" VALUES(37,104,'37:104','तब हमने उसे आवाज़ दी कि हे इब्राहीम!');
INSERT INTO "al_umari" VALUES(37,105,'37:105','तूने सच कर दिया अपना स्वप्न। इसी प्रकार, हम प्रतिफल प्रदान करते हैं सदाचारियों को।');
INSERT INTO "al_umari" VALUES(37,106,'37:106','वास्तव में, ये खुली परीक्षा थी।');
INSERT INTO "al_umari" VALUES(37,107,'37:107','और हमने उसके मुक्ति-प्रतिदान के रूप में, प्रदान कर दी एक महान[ बली। ');
INSERT INTO "al_umari" VALUES(37,108,'37:108','तथा हमने शेष रखी उसकी शुभ चर्चा पिछलों में।');
INSERT INTO "al_umari" VALUES(37,109,'37:109','सलाम है इब्रीम पर।');
INSERT INTO "al_umari" VALUES(37,110,'37:110','इसी प्रकार, हम प्रतिफल प्रदान करते हैं सदाचारियों को।');
INSERT INTO "al_umari" VALUES(37,111,'37:111','निश्चय ही वह हमारे ईमान वाले भक्तों में से था।');
INSERT INTO "al_umari" VALUES(37,112,'37:112','तथा हमने उसे शुभसूचना दी इस्ह़ाक़ नबी की, जो सदाचारियों में[ होगा। ');
INSERT INTO "al_umari" VALUES(37,113,'37:113','तथा हमने बरकत (विभूति) अवतरिक की उसपर तथा इस्ह़ाक़ पर और उन दोनों की संतति में से कोई सदाचारी है और कोई अपने लिए खुला अत्याचारी।');
INSERT INTO "al_umari" VALUES(37,114,'37:114','तथा हमने उपकार किया मूसा और हारून पर।');
INSERT INTO "al_umari" VALUES(37,115,'37:115','तथा मुक्त किया दोनों को और उनकी जाति को, घोर व्यग्रता से।');
INSERT INTO "al_umari" VALUES(37,116,'37:116','तथा हमने सहायता की उनकी, तो वही प्रभावशाली हो गये।');
INSERT INTO "al_umari" VALUES(37,117,'37:117','तथा हमने प्रदान की दोनों को प्रकाशमय पुस्तक (तौरात)।');
INSERT INTO "al_umari" VALUES(37,118,'37:118','और हमने दर्शाई दोनों को सीधी डगर।');
INSERT INTO "al_umari" VALUES(37,119,'37:119','तथा शेष रखी दोनों की शुभ चर्चा, पिछलों में।');
INSERT INTO "al_umari" VALUES(37,120,'37:120','सलाम है मूसा तथा हारून पर।');
INSERT INTO "al_umari" VALUES(37,121,'37:121','हम इसी प्रकार प्रतिफल प्रदान करते हैं, सदाचारियों को।');
INSERT INTO "al_umari" VALUES(37,122,'37:122','वस्तुतः, वे दोनों हमारे ईमान वाले भक्तों में थे।');
INSERT INTO "al_umari" VALUES(37,123,'37:123','तथा निश्चय इल्यास, नबियों में से था।');
INSERT INTO "al_umari" VALUES(37,124,'37:124','जब कहा उसने अपनी जाति सेः क्या तुम डरते नहीं हो?');
INSERT INTO "al_umari" VALUES(37,125,'37:125','क्या तुम बअल ( नामक मूर्ति) को पुकारते हो? तथा त्याग रहे हो सर्वोत्तम उत्पत्तिकर्ता को?');
INSERT INTO "al_umari" VALUES(37,126,'37:126','अल्लाह ही तुम्हारा पालनहार है तथा तुम्हारे प्रथम पूर्वजों का पालनहार है।');
INSERT INTO "al_umari" VALUES(37,127,'37:127','अन्ततः, उन्होंने झुठला दिया उसे। तो निश्चय वही (नरक में) उपस्थित होंगे।');
INSERT INTO "al_umari" VALUES(37,128,'37:128','किन्तु, अल्लाह के शुध्द भक्त।');
INSERT INTO "al_umari" VALUES(37,129,'37:129','तथा शेष रखी हमने उसकी शुभ चर्चा पिछलों में।');
INSERT INTO "al_umari" VALUES(37,130,'37:130','सलाम है इल्यासीन[ पर। ');
INSERT INTO "al_umari" VALUES(37,131,'37:131','वास्तव में, हम इसी प्रकार प्रतिफल प्रदान करते हैं, सदाचारियों को।');
INSERT INTO "al_umari" VALUES(37,132,'37:132','वस्तुतः, वह हमारे ईमान वाले भक्तों में से था।');
INSERT INTO "al_umari" VALUES(37,133,'37:133','तथा निश्चय लूत नबियों में से था।');
INSERT INTO "al_umari" VALUES(37,134,'37:134','जब हमने मुक्त किया उसे तथा उसके सबपरिजनों को।');
INSERT INTO "al_umari" VALUES(37,135,'37:135','एक बुढ़िया[ के सिवा, जो पीछे रह जाने वालों में थी। ');
INSERT INTO "al_umari" VALUES(37,136,'37:136','फिर हमने अन्यों को तहस-नहस कर दिया।');
INSERT INTO "al_umari" VALUES(37,137,'37:137','तथा तुम[ ग़ुज़रते हो उन (की निर्जीव बस्तियों) पर, प्रातः के समय। ');
INSERT INTO "al_umari" VALUES(37,138,'37:138','तथा रात्रि में। तो क्या तुम समझते नहीं हो?');
INSERT INTO "al_umari" VALUES(37,139,'37:139','तथा निश्चय यूनुस नबियों में से था।');
INSERT INTO "al_umari" VALUES(37,140,'37:140','जब वह भाग[ गया भरी नाव की ओर। ');
INSERT INTO "al_umari" VALUES(37,141,'37:141','फिर नाम निकाला गया, तो वह हो गया फेंके हुओं में से।');
INSERT INTO "al_umari" VALUES(37,142,'37:142','तो निगल लिया उसे मछली ने और वह निन्दित था।');
INSERT INTO "al_umari" VALUES(37,143,'37:143','तो यदि न होता अल्लाह की पवित्रता का वर्णन करने वालों में।');
INSERT INTO "al_umari" VALUES(37,144,'37:144','तो वह रह जाता उसके उदर में उस दिन तक, जब सब पुनः जीवित किये[ जायेंगे। ');
INSERT INTO "al_umari" VALUES(37,145,'37:145','तो हमने फेंक दिया उसे खुले मैदान में और वह रोगी[ था। ');
INSERT INTO "al_umari" VALUES(37,146,'37:146','और उगा दिया उस[ पर लताओं का एक वृक्ष। ');
INSERT INTO "al_umari" VALUES(37,147,'37:147','तथा हमने उसे रसूल बनाकर भेजा एक लाख, बल्कि अधिक की ओर।');
INSERT INTO "al_umari" VALUES(37,148,'37:148','तो वे ईमान लाये। फिर हमने उन्हें सुख-सुविधा प्रदान की एक समय[ तक। ');
INSERT INTO "al_umari" VALUES(37,149,'37:149','तो (हे नबी!) आप उनसे प्रश्न करें कि क्या आपके पालनहार के लिए तो पुत्रियाँ हों और उनके लिए पुत्र?');
INSERT INTO "al_umari" VALUES(37,150,'37:150','अथवा किया हमने पैदा किया है फ़रिश्तों को नारियाँ और वे उस समय उपस्थित[ थे? ');
INSERT INTO "al_umari" VALUES(37,151,'37:151','सावधान! वास्तव में, वे अपने मन से बनाकर ये बात कह रहे हैं।');
INSERT INTO "al_umari" VALUES(37,152,'37:152','कि अल्लाह ने संतान बनाई है और निश्चय ये मिथ्या भाषी हैं।');
INSERT INTO "al_umari" VALUES(37,153,'37:153','क्या अल्लाह ने प्राथमिक्ता दी है पुत्रियों को, पुत्रों पर?');
INSERT INTO "al_umari" VALUES(37,154,'37:154','तुम्हें क्या हो गया है, तुम कैसा निर्णय दे रहे हो?');
INSERT INTO "al_umari" VALUES(37,155,'37:155','तो क्या तुम शिक्षा ग्रहण नहीं करते?');
INSERT INTO "al_umari" VALUES(37,156,'37:156','अथवा तुम्हारे पास कोई प्रत्यक्ष प्रमाण है?');
INSERT INTO "al_umari" VALUES(37,157,'37:157','तो अपनी पुस्तक लाओ, यदि तुम सत्यवादी हो?');
INSERT INTO "al_umari" VALUES(37,158,'37:158','और उन्होंने बना दिया अल्लाह तथा जिन्नों के मध्य, वंश-संबंध। जबकि जिन्न स्वयं जानते हैं कि वे अल्लाह के समक्ष निश्चय उपस्थित किये[ जायेंगे। ');
INSERT INTO "al_umari" VALUES(37,159,'37:159','अल्लाह पवित्र है उन गुणों से, जिनका वे वर्णन कर रहे हैं।');
INSERT INTO "al_umari" VALUES(37,160,'37:160','परन्तु, अल्लाह के शुध्द भक्त।[ ');
INSERT INTO "al_umari" VALUES(37,161,'37:161','तो निश्चय तुम तथा तुम्हारे पूज्य।');
INSERT INTO "al_umari" VALUES(37,162,'37:162','तुम सब किसी एक को भी कुपथ नहीं कर सकते।');
INSERT INTO "al_umari" VALUES(37,163,'37:163','उसके सिवा, जो नरक में झोंका जाने वाला है।');
INSERT INTO "al_umari" VALUES(37,164,'37:164','और नहीं है हम (फ़रिश्तों) में से कोई, परन्तु उसका एक नियमित स्थान है।');
INSERT INTO "al_umari" VALUES(37,165,'37:165','तथा हम ही (आज्ञापालन के लिए) पंक्तिवध्द हैं।');
INSERT INTO "al_umari" VALUES(37,166,'37:166','और हम ही तस्बीह़ (पवित्रता गान) करने वाले हैं।');
INSERT INTO "al_umari" VALUES(37,167,'37:167','तथा वे (मुश्रिक) तो कहा करते थे किः');
INSERT INTO "al_umari" VALUES(37,168,'37:168','यदि हमारे पास कोई स्मृति (पुस्तक) होती, जो पहले लोगों में आई......');
INSERT INTO "al_umari" VALUES(37,169,'37:169','तो हम अवश्य अल्लाह के शुध्द भक्तों में हो जाते।');
INSERT INTO "al_umari" VALUES(37,170,'37:170','(फिर जब आ गयी) तो उन्होंने क़ुर्आन के साथ कुफ़्र कर दिया, अतः, शीघ्र ही उन्हें ज्ञान हो जायेगा।');
INSERT INTO "al_umari" VALUES(37,171,'37:171','और पहले ही हमारा वचन हो चुका है अपने भेजे हुए भक्तों के लिए।');
INSERT INTO "al_umari" VALUES(37,172,'37:172','कि निश्चय उन्हीं की सहायता की जायेगी।');
INSERT INTO "al_umari" VALUES(37,173,'37:173','तथा वास्तव में हमारी सेना ही प्रभावशाली (विजयी) होने वाली है।');
INSERT INTO "al_umari" VALUES(37,174,'37:174','तो आप मुँह फेर लें उनसे, कुछ समय तक।');
INSERT INTO "al_umari" VALUES(37,175,'37:175','तथा उन्हें देखते रहें। वे भी शीघ्र ही देख लेंगे।');
INSERT INTO "al_umari" VALUES(37,176,'37:176','तो क्या, वे हमारी यातना की शीघ्र माँग कर रहे हैं।');
INSERT INTO "al_umari" VALUES(37,177,'37:177','तो जब वह उतर आयेगी उनके मैदानों में, तो बुरा हो जायेगा सावधान किये हुओं का सवेरा।');
INSERT INTO "al_umari" VALUES(37,178,'37:178','और आप मुँह फेर लें उनसे, कुछ समय तक।');
INSERT INTO "al_umari" VALUES(37,179,'37:179','तथा देखते रहें, अन्ततः वे (भी) देख लेंगे।');
INSERT INTO "al_umari" VALUES(37,180,'37:180','पवित्र है आपका पालनहार, गौरव का स्वामी, उस बात से, जो वे बना रहे हैं।');
INSERT INTO "al_umari" VALUES(37,181,'37:181','तथा सलाम है रसूलों पर।');
INSERT INTO "al_umari" VALUES(37,182,'37:182','तथा सभी प्रशंसा, अल्लाह, सर्वलोक के पालनहार के लिए है।');
INSERT INTO "al_umari" VALUES(38,1,'38:1','साद। शपथ है शिक्षाप्रद क़ुर्आन की!');
INSERT INTO "al_umari" VALUES(38,2,'38:2','बल्कि, जो काफ़िर हो गये, वे एक गर्व तथा विरोध में ग्रस्त हैं।');
INSERT INTO "al_umari" VALUES(38,3,'38:3','हमने विनाश किया है इनसे पूर्व, बहुत-से समुदायों का। तो वे पुकारने लगे और नहीं होता वह बचने का समय।');
INSERT INTO "al_umari" VALUES(38,4,'38:4','तथा उन्हें आश्चर्य हुआ कि आ गया उनके पास, उन्हीं मेंसे एक सचेत करने[ वाला! और कह दिया काफ़िरों ने कि ये तो बड़ा झूठा जादूगर है। ');
INSERT INTO "al_umari" VALUES(38,5,'38:5','क्या उसने बना दिया है सब पूज्यों को एक पूज्य? ये तो बड़े आश्चर्य का विषय है।');
INSERT INTO "al_umari" VALUES(38,6,'38:6','तथा चल दिये उनके प्रमुख, (ये) कहते हुए कि चलो, दृढ़ रहो अपने पूज्यों पर। इस बात का कुछ और ही लक्ष्य[ है। ');
INSERT INTO "al_umari" VALUES(38,7,'38:7','हमने नहीं सुनी ये बात प्राचीन धर्मों में, ये तो बस मनघड़त बात है।');
INSERT INTO "al_umari" VALUES(38,8,'38:8','क्या उसीपर उतारी गई है ये शिक्षा हमारे बीच में से? बल्कि वे संदेह में हैं मेरी शिक्षा से। बल्कि उन्होंने अभी यातना नहीं चखी है।');
INSERT INTO "al_umari" VALUES(38,9,'38:9','अथवा उनके पास हैं, आपके अत्यंत प्रभुत्वशाली प्रदाता पालनहार की दया के कोष?[ ');
INSERT INTO "al_umari" VALUES(38,10,'38:10','अथवा उन्हीं का है राज्य, आकाशों तथा धरती का और जो कुछ उन दोनों के मध्य है? तो उन्हें चाहिये कि चढ़ जायें (आकाशों में) रस्सियाँ तान[ कर। ');
INSERT INTO "al_umari" VALUES(38,11,'38:11','ये एक तुच्छ सेना है, यहाँ प्राजित सेनाओं[ में से। ');
INSERT INTO "al_umari" VALUES(38,12,'38:12','झुठलाया इनसे पहले नूह़ तथा आद और शक्तिवान फ़िरऔन की जाति ने।');
INSERT INTO "al_umari" VALUES(38,13,'38:13','तथा समूद और लूत की जाति एवं वन के वासियों[ ने। यही सेनायें हैं। ');
INSERT INTO "al_umari" VALUES(38,14,'38:14','इन सभी ने झुठलाया रसूलों को, तो मेरी यातना सिध्द हो गयी।');
INSERT INTO "al_umari" VALUES(38,15,'38:15','और ये नहीं प्रतीक्षा कर रहे हैं, परन्तु एक कर्कश ध्वनि की जिसके लिए कुछ भी देर नहीं होगी।');
INSERT INTO "al_umari" VALUES(38,16,'38:16','तथा उन्होंने कहा कि हे हमारे पालनहार! शीघ्र प्रदान कर दे हमारे लिए हमारी (यातना का) भाग ह़िसाब के दिन से पहले।[ ');
INSERT INTO "al_umari" VALUES(38,17,'38:17','आप सहन करें उसपर, जो वे कह रहे हैं तथा याद करें हमारे भक्त दावूद को, जो अत्यंत शक्तिशाली था। निश्चय वह ध्यानमग्न था।');
INSERT INTO "al_umari" VALUES(38,18,'38:18','हमने वश्वर्ती कर दिया था पर्वतों को, जो उसके साथ पवित्रता गान करते थे, संध्या तथा प्रातः।');
INSERT INTO "al_umari" VALUES(38,19,'38:19','तथा पक्षियों को एकत्रित किये हुए, प्रत्येक उसके अधीन ध्यानमग्न रहते थे।');
INSERT INTO "al_umari" VALUES(38,20,'38:20','और हमने दृढ़ किया उसके राज्य को और हमने प्रदान की उसे नबूवत तथा निर्णय शक्ति।');
INSERT INTO "al_umari" VALUES(38,21,'38:21','तथा क्या आया आपके पास दो पक्षों का समाचार, जब वे दीवार फाँदकर मेह़राब (वंदना स्थल) में आ गये?');
INSERT INTO "al_umari" VALUES(38,22,'38:22','जब उन्होंने प्रवेश किया दावूद पर, तो वह घबरा गया उनसे। उन्होंने कहाः डरिये नहीं। हम दो पक्ष हैं, अत्याचार किया है, हममें से एक ने, दूसरे पर। तो आप निर्णय कर दें हमारे बीच सत्य (न्याय) के साथ तथा अन्याय न करें तथा हमें दर्शा दें सीधी राह।');
INSERT INTO "al_umari" VALUES(38,23,'38:23','ये मेरा भाई है, इसके पास निन्नान्वे भेड़ हैं और मेरे पास भेड़ है। ये कहता है कि वह (भी) मुझे दे दो और ये प्रभावशाली हो गया मुझपर बात करने में।');
INSERT INTO "al_umari" VALUES(38,24,'38:24','दावूद ने कहाः उसने तुमपर अवश्य अत्याचार किया, तुम्हारी भेड़ को (मिलाने की) माँग करके अपनी भेड़ों में तथा बहुत-से साझी एक-दूसरे पर एत्याचार करते हैं, उनके सिवा, जो ईमान लाये तथा सदाचार किये और बहुत थोड़े हैं ऐसे लोग और दावूद ने भाँप ली कि हमने उसकी परीक्षा ली है, तो सहसा उसने क्षमा याचना कर ली और गिर गया सज्दे में तथा ध्यानमग्न हो गया।');
INSERT INTO "al_umari" VALUES(38,25,'38:25','तो हमने क्षमा कर दिया उसके लिए वह तथा उसके लिए हमारे पास निश्चय सामिप्य है तथा अच्छा स्थान।');
INSERT INTO "al_umari" VALUES(38,26,'38:26','हे दाऊद! हमने तुझे राज्य दिया है धरती में। अतः, निर्णय कर, लोगों के बीच सत्य (न्याय) के साथ तथा अनुसरण न कर आकांक्षा का। अन्यथा, वह कुपथ कर देगी तुझे अल्लाह की राह से। निःसंदेह, जो कुपथ हो जायेंगे अल्लाह की राह[ से, तो उन्हीं के लिए घोर यातना है, इस कारण कि वे भूल गये ह़िसाब का दिन। ');
INSERT INTO "al_umari" VALUES(38,27,'38:27','तथा नहीं पैदा किया है हमने आकाश और धरती को तथा जो कुछ उनके बीच है, व्यर्थ। ये तो उनका विचार है, जो काफ़िर हो गये। तो विनाश है उनके लिए, जो काफ़िर हो गये अग्नि से।');
INSERT INTO "al_umari" VALUES(38,28,'38:28','क्या हम कर देंगे उन्हें, जो ईमान लाये तथा सदाचार किये, उनके समान, जो उपद्रवी हैं धरती में? या कर देंगे आज्ञाकारियों को उल्लंघनकारियों के समान?[ ');
INSERT INTO "al_umari" VALUES(38,29,'38:29','ये (क़ुर्आन) एक शूभ पुस्तक है, जिसे हमने अवतरित किया है आपकी ओर, ताकि लोग विचार करें उसकी आयतों पर और ताकि शिक्षा ग्रहण करें मतिमान।');
INSERT INTO "al_umari" VALUES(38,30,'38:30','तथा हमने प्रदान किया दावूद को सुलैमान (नामक पुत्र)। वह अति ध्यानमग्न था।');
INSERT INTO "al_umari" VALUES(38,31,'38:31','जब प्रस्तुत किये गये उसके समक्ष संध्या के समय सधे हुए वेगगामी घोड़े।');
INSERT INTO "al_umari" VALUES(38,32,'38:32','तो कहाः मैंने प्राथमिक्ता दी इन घोड़ों के प्रेम को अपने पालनहार के स्मरण पर। यहाँ तक कि वे ओझल हो गये।');
INSERT INTO "al_umari" VALUES(38,33,'38:33','उन्हें वापिस लाओ मेरे पास। फिर हाथ फेरने लगे उनकी पिंडलियों तथा गर्दनों पर।');
INSERT INTO "al_umari" VALUES(38,34,'38:34','और हमने परीक्षा[ ली सुलैमान की तथा डाल दिया उसके सिंहासन पर एक धड़। फिर वह ध्यानमग्न हो गया। ');
INSERT INTO "al_umari" VALUES(38,35,'38:35','उसने प्रार्थना कीः हे मेरे पालनहार! मुझे क्षमा कर दे तथा मुझे प्रदान कर ऐसा राज्य, जो उचित न हो किसी के लिए मेरे पश्चात्। वास्तव में, तू ही अति प्रदाता है।');
INSERT INTO "al_umari" VALUES(38,36,'38:36','तो हमने वश में कर दिया उसके लिए वायु को, जो चल रही थी धीमी गति से उसके आदेश से, वह जहाँ चाहता।');
INSERT INTO "al_umari" VALUES(38,37,'38:37','तथा शैतानों को प्रत्येक प्रकार के निर्माता तथा ग़ोता ख़ोर को।');
INSERT INTO "al_umari" VALUES(38,38,'38:38','तथा दूसरों को बंधे हुए बेड़ियों में।');
INSERT INTO "al_umari" VALUES(38,39,'38:39','ये हमारा प्रदान है। तो उपकार करो अथवा रोक लो, कोई ह़िसाब नहीं।');
INSERT INTO "al_umari" VALUES(38,40,'38:40','और वास्तव में, उसके लिए हमारे पास सामिप्य तथा उत्तम स्थान है।');
INSERT INTO "al_umari" VALUES(38,41,'38:41','तथा याद करो हमारे भक्त अय्यूब को। जब उसने पुकारा अपने पालनहार को कि शैतान ने मुझे पहुँचाया[ है दुःख तथा यातना। ');
INSERT INTO "al_umari" VALUES(38,42,'38:42','अपना पाँव (धरती पर) मार। ये है शीतल स्नान तथा पीने का जल।');
INSERT INTO "al_umari" VALUES(38,43,'38:43','और हमने प्रदान किया उसे उसका परिवार तथा उनके साथ और उनके समान, अपनी दया से और मतिमानों की शिक्षा के लिए।');
INSERT INTO "al_umari" VALUES(38,44,'38:44','तथा ले अपने हाथ में तीलियों की एक झाड़ तथा उससे मार और अपनी शपथ भंग न कर। वास्तव[ में, हमने उसे पाया धैर्यवान। निश्चय, वह बड़ा ध्यानमग्न था। ');
INSERT INTO "al_umari" VALUES(38,45,'38:45','तथा याद करो हमारे भक्त इब्राहीम, इस्ह़ाक़ एवं याक़ूब को, जो कर्म शक्ति तथा ज्ञानचक्षू[ वाले थे। ');
INSERT INTO "al_umari" VALUES(38,46,'38:46','हमने उसे विशेष कर लिया बड़ी विशेषता, परलोक (आख़िरत) की याद के साथ।');
INSERT INTO "al_umari" VALUES(38,47,'38:47','वास्तव में, वह हमारे यहाँ उत्तम निर्वाचितों में से थे।');
INSERT INTO "al_umari" VALUES(38,48,'38:48','तथा आप चर्चा करें इस्माईल, यसअ एवं ज़ुल्किफ़्ल की और ये सभी निर्वाचितों में से थे।');
INSERT INTO "al_umari" VALUES(38,49,'38:49','ये (क़ुर्आन) एक शिक्षा है तथा निश्चय आज्ञाकारियों के लिए उत्तम स्थान है।');
INSERT INTO "al_umari" VALUES(38,50,'38:50','स्थायी स्वर्ग, खुले हुए हैं उनके लिए (उनके) द्वार।');
INSERT INTO "al_umari" VALUES(38,51,'38:51','वे तकिये लगाये होंगे उनमें। माँगेंगे उनमें बहुत-से फल तथा पेय पदार्थ।');
INSERT INTO "al_umari" VALUES(38,52,'38:52','तथा उनके पास आँखे सीमित रखनी वाली समायु पत्नियाँ होंगी।');
INSERT INTO "al_umari" VALUES(38,53,'38:53','ये है जिसका वचन दिया जा रहा था तुम्हें, ह़िसाब के दिन।');
INSERT INTO "al_umari" VALUES(38,54,'38:54','ये है हमारी जीविका, जिसका कोई अन्त नहीं है।');
INSERT INTO "al_umari" VALUES(38,55,'38:55','ये है और अवज्ञाकारियों के लिए निश्चय बुरा स्थान है।');
INSERT INTO "al_umari" VALUES(38,56,'38:56','नरक है, जिसमें वे जायेंगे, क्या ही बुरा आवास है।');
INSERT INTO "al_umari" VALUES(38,57,'38:57','ये है। तो तुम चखो, खौलता पानी तथा पीप।');
INSERT INTO "al_umari" VALUES(38,58,'38:58','तथा कुछ अन्य इसी प्रकार की विभिन्न यातनायें।');
INSERT INTO "al_umari" VALUES(38,59,'38:59','ये[ एक और जत्था है, जो घुसा आ रहा है तुम्हारे साथ। कोई स्वागत नहीं है उनका। वास्तव में, वे नरक में प्रवेश करने वाले हैं। ');
INSERT INTO "al_umari" VALUES(38,60,'38:60','वे उत्तर देंगेः बल्कि तुम। तुम्हारा कोई स्वागत नहीं। तुम ही आगे लाये हो इस (यातना) को हमारे। तो ये बुरा निवास है।');
INSERT INTO "al_umari" VALUES(38,61,'38:61','(फिर) वे कहेंगेः हमारे पालनहार! जो हमारे आगे लाया है इसे, उसे दुगनी यातना दे नरक में।');
INSERT INTO "al_umari" VALUES(38,62,'38:62','तथा (नारकी) कहेंगेः हमें क्या हुआ है कि हम कुछ लोगों को नहीं देख रहे हैं, जिनकी गणना हम बुरे लोगों में कर[ रहे थे? ');
INSERT INTO "al_umari" VALUES(38,63,'38:63','क्या हमने उन्हें उपहास बना रखा था अथवा चूक रही हैं उनसे हमारी आँखें?');
INSERT INTO "al_umari" VALUES(38,64,'38:64','निश्चय सत्य है नारकियों का आपस में झगड़ना।');
INSERT INTO "al_umari" VALUES(38,65,'38:65','हे नबी! आप कह दें: मैं तो मात्र सचेत करने वाला[ हूँ तथा कोई ( सच्चा) पूज्य नहीं है, अकेले प्रभावशाली अल्लाह के सिवा। ');
INSERT INTO "al_umari" VALUES(38,66,'38:66','वह आकाशों तथा धरती का और जो कुछ उन दोनों के मध्य है, सबका पालनहार, अति प्रभावशाली, क्षमी है।');
INSERT INTO "al_umari" VALUES(38,67,'38:67','आप कह दें कि ये[ बहुत बड़ी सूचना है। ');
INSERT INTO "al_umari" VALUES(38,68,'38:68','और तुम हो कि उससे मुँह फेर रहे हो।');
INSERT INTO "al_umari" VALUES(38,69,'38:69','मुझे कोई ज्ञान नहीं है, उच्च सभा वाले (फ़रिश्ते) जब वाद-विवाद कर रहे थे।');
INSERT INTO "al_umari" VALUES(38,70,'38:70','मेरी ओर तो मात्र इसलिए वह़्यी (प्रकाशना) की जा रही है कि मैं खुला सचेत करने वाला हूँ।');
INSERT INTO "al_umari" VALUES(38,71,'38:71','जबकि कहा आपके पालनहार ने फ़रिश्तों सेः मैं पैदा करने वाला हूँ एक मनुष्य, मिट्टी से।');
INSERT INTO "al_umari" VALUES(38,72,'38:72','तो जब मैं उसे बराबर कर दूँ तथा फूँक दूँ उसमें, अपनी ओर से रूह़ (प्राण), तो गिर जाओ उसके लिए सज्दा करते हुए।');
INSERT INTO "al_umari" VALUES(38,73,'38:73','तो सज्दा किया सभी फ़रिश्तों ने एक साथ।');
INSERT INTO "al_umari" VALUES(38,74,'38:74','इब्लीस के सिवा, उसने अभिमान किया और हो गया काफ़िरों में से।');
INSERT INTO "al_umari" VALUES(38,75,'38:75','अल्लाह ने कहाः हे इब्लीस! किस चीज़ ने तुझे रोक दिया सज्दा करने से उसके लिए, जिसे मैंने पैदा किया अपने हाथ से? क्या तू अभिमान कर गया अथवा वास्तव में तू ऊँचे लोगों में से है?');
INSERT INTO "al_umari" VALUES(38,76,'38:76','उसने कहाः मैं उससे उत्तम हूँ। तूने मुझे पैदा किया है अग्नि से तथा उसे पैदा किया मिट्टी से।');
INSERT INTO "al_umari" VALUES(38,77,'38:77','अल्लाह ने कहाः तू निकल जा यहाँ से, तू वास्तव में धिक्कृत है।');
INSERT INTO "al_umari" VALUES(38,78,'38:78','तथा तुझपर मेरी दया से दूरी है, प्रलय के दिन तक।');
INSERT INTO "al_umari" VALUES(38,79,'38:79','उसने कहाः मेरे पालनहार! मुझे अवसर दे उस दिन तक, जब लोग पुनः जीवित किये जायेंगे।');
INSERT INTO "al_umari" VALUES(38,80,'38:80','अल्लाह ने कहाः तुझे अवसर दे दिया गया।');
INSERT INTO "al_umari" VALUES(38,81,'38:81','निर्धारित समय के दिन तक।');
INSERT INTO "al_umari" VALUES(38,82,'38:82','उसने कहाः तो तेरे प्रताप की शपथ! मैं अवश्य कुपथ करके रहूँगा सबको।');
INSERT INTO "al_umari" VALUES(38,83,'38:83','तेरे शुध्द भक्तों के सिवा उनमें से।');
INSERT INTO "al_umari" VALUES(38,84,'38:84','अल्लाह ने कहाः तो ये सत्य है और मैं सत्य ही कहा करता हूँ:');
INSERT INTO "al_umari" VALUES(38,85,'38:85','कि अवश्य भर दूँगा नरक को तुझसे तथा जो तेरा अनुसरण करेंगे उनसबसे।');
INSERT INTO "al_umari" VALUES(38,86,'38:86','(हे नबी!) कह दें कि मैं नहीं माँग करता हूँ तुमसे इसपर किसी पारिश्रमिक की तथा मैं नहीं हूँ अपनी ओर से कुछ बनाने वाला।');
INSERT INTO "al_umari" VALUES(38,87,'38:87','नहीं है ये (क़ुर्आन), परन्तु एक शिक्षा, सर्वलोक वासियों के लिए।');
INSERT INTO "al_umari" VALUES(38,88,'38:88','तथा तुम्हें अवश्य ज्ञान हो जायेगा उसके समाचार (तथ्य) का, एक समय के पश्चात्।');
INSERT INTO "al_umari" VALUES(39,1,'39:1','इस पुस्तक का अवतरित होना अल्लाह अति प्रभावशाली, तत्वज्ञ की ओर से है।');
INSERT INTO "al_umari" VALUES(39,2,'39:2','हमने आपकी ओर ये पुस्तक सत्य के साथ अवतरित की है। अतः, इबादत (वंदना) करो अल्लाह की शुध्द करते हुए उसके लिए धर्म को।');
INSERT INTO "al_umari" VALUES(39,3,'39:3','सुन लो! शुध्द धर्म अल्लाह ही के लिए (योग्य) है तथा जिन्होंने बना रखा है अल्लाह के सिवा संरक्षक, वे कहते हैं कि हम तो उनकी वंदना इसलिए करते हैं कि वह समीप कर देंगें हमें अल्लाह[ से। वास्तव में, अल्लाह ही निर्णय करेगा उनके बीच जिसमें वे विभेद कर रहे हैं। वास्तव में, अल्लाह उसे सुपथ नहीं दर्शाता जो बड़ा मिथ्यावादी, कृतघ्न हो। ');
INSERT INTO "al_umari" VALUES(39,4,'39:4','यदि अल्लाह चाहता कि अपने लिए संतान बनाये, तो चुन लेता उसमें से, जिसे पैदा करता है, जिसे चाहता। वह पवित्र है! वही अल्लाह अकेला सबपर प्रभावशाली है।');
INSERT INTO "al_umari" VALUES(39,5,'39:5','उसने पैदा किया है आकाशों तथा धरती को सत्य के आधार पर। वह लपेट देता है रात्रि को दिन पर तथा दिन को रात्रि पर तथा वशवर्ती किया सूर्य और चन्द्रमा को। प्रत्येक चल रहा है, अपनी निर्धारित अवधि के लिए। सावधान! वही अत्यंत प्रभावशाली, क्षमी है।');
INSERT INTO "al_umari" VALUES(39,6,'39:6','उसने तुम्हें पैदा किया एक प्राण से, फिर बनाया उसीसे उसका जोड़ा तथा अवतरित किये तुम्हारे लिए पशुओं में से आठ जोड़े। वह पैदा करता है तुम्हें तुम्हारी माताओं के गर्भाषयों में एक रूप में, एक रूप के पश्चात्, तीन अंधेरों में, यही अल्लाह है तुम्हारा पालनहार, उसी का राज्य है। कोई ( सच्चा) वंदनीय नहीं, उसके सिवा। तो तुम कहाँ फिराये जा रहे हो?');
INSERT INTO "al_umari" VALUES(39,7,'39:7','यदि तुम कृतघ्न बनो, तो अल्लाह निस्पृह है तुमसे और वह प्रसन्न नहीं होता अपने भक्तों की कृतघ्नता से और यदि कृतज्ञता करो, तो वह प्रसन्न हो जायेगा तुमसे और नहीं बोझ उठायेगा कोई उठाने वाला दूसरों का बोझ। फिर तुम्हारे पालनहार ही की ओर तुम्हारा फिरना है। तो वह तुम्हें सूचित कर देगा तुम्हारे कर्मों से। वास्तव में, वह भली-भाँति जानने वाला है दिलों के भेदों को।');
INSERT INTO "al_umari" VALUES(39,8,'39:8','तथा जब पहुँचता है मनुष्य को कोई दुःख तो पुकारता है अपने पालनहार को, ध्यानमग्न होकर उसकी ओर। फिर जब हम उसे प्रदान करते हैं कोई सुख अपनी ओर से, तो भूल जाता है उसे, जिसके लिए वह पुकार रहा था इससे पूर्व तथा बना लेता है अल्लाह का साझी, ताकि कुपथ करे उसकी डगर से। आप कह कदें कि आनन्द ले ले अपने कुफ़्र का, थोड़ा सा। वास्तव में, तू नारकियों में है।');
INSERT INTO "al_umari" VALUES(39,9,'39:9','तो क्या जो आज्ञाकारी रहा हो रात्रि के क्षणों में सज्दा करते हुए तथा खड़ा रहकर, (और) डर रहा हो परलोक से तथा आशा रखता हो अल्लाह की दया की, आप कहें कि क्या समान हो जायेंगे जो ज्ञान रखते हों तथा जो ज्ञान नहीं रखते? वास्तव में, शिक्षा ग्रहण करते हैं मतिमान लोग ही।');
INSERT INTO "al_umari" VALUES(39,10,'39:10','आप कह दें उन भक्तों से, जो ईमान लाये तथा डरे अपने पालनहार से कि उन्हीं के लिए जिन्होंने सदाचार किये इस संसार में, बड़ी भलाई है तथा अल्लाह की धरती विस्तृत है और धैर्यवान ही अपना पूरा प्रतिफल अगणित दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(39,11,'39:11','आप कह दें कि मुझे आदेश दिया गया है कि इबादत (वंदना) करूँ अल्लाह की, शुध्द करके उसके लिए धर्म को।');
INSERT INTO "al_umari" VALUES(39,12,'39:12','तथा मुझे आदेश दिया गया है कि प्रथम आज्ञाकारी हो जाऊँ।');
INSERT INTO "al_umari" VALUES(39,13,'39:13','आप कह दें: मैं डरता हूँ, यदि मैं अवैज्ञा करूँ अपने पालनहार की, एक बड़े दिन की यातना से।');
INSERT INTO "al_umari" VALUES(39,14,'39:14','आप कह दें: अल्लाह ही की इबादत (वंदना) मैं कर रहा हूँ, शुध्द करके उसके लिए अपने धर्म को।');
INSERT INTO "al_umari" VALUES(39,15,'39:15','अतः तुम इबादत (वंदना) करो जिसकी चाहो, उसके सिवा। आप कह दें: वास्तव में, क्षतिग्रस्त वही हैं, जिन्होंने क्षतिग्रस्त कर लिया स्वयं को तथा अपने परिवार को प्रलय के दिन। सावधान! यही खुली क्षति है।');
INSERT INTO "al_umari" VALUES(39,16,'39:16','उन्हीं के लिए छत्र होंगे अग्नि के, उनके ऊपर से तथा उनके नीचे से छत्र होंगे। यही है, डरा रहा है अल्लाह जिससे, अपने भक्तों को। हे मेरे भक्तो! मुझी से डरो।');
INSERT INTO "al_umari" VALUES(39,17,'39:17','जो बचे रहे ताग़ूत (असुर)[ की पूजा से तथा ध्यानमग्न हो गये अल्लाह की ओर, तो उन्हीं के लिए शुभ सूचना है। अतः, आप शुभ सूचना सुना दें मेरे भक्तों को। ');
INSERT INTO "al_umari" VALUES(39,18,'39:18','जो ध्यान से सुनते हैं इस बात को, फिर अनुसरण करते हैं इस सर्वोत्तम बात का, तो वही हैं, जिन्हें सुपथ दर्शा दिया है अल्लाह ने तथा वही मतिमान हैं।');
INSERT INTO "al_umari" VALUES(39,19,'39:19','तो क्या जिसपर यातना की बात सिध्द हो गयी, क्या आप निकाल सकेंगे उसे, जो नरक में हैं?');
INSERT INTO "al_umari" VALUES(39,20,'39:20','किन्तु, जो अपने पालनहार से डरे, उन्हीं के लिए उच्च भवन हैं। जिनके ऊपर निर्मित भवन हैं। प्रवाहित हैं जिनमें नहरें, ये अल्लाह का वचन है और अल्लाह वचन भंग नहीं करता।');
INSERT INTO "al_umari" VALUES(39,21,'39:21','क्या तुमने नहीं देखा[ कि अल्लाह ने उतारा आकाश से जल? फिर प्रवाहित कर दिये उसके स्रोत धरती में। फिर निकालता है उससे खेतियाँ विभिन्न रंगों की। फिर सूख जाती हैं, तो तुम देखते हो उन्हें पीली, फिर उसे चूर-चूर कर देता है। निश्चय इसमें बड़ी शिक्षा है मतिमानों के लिए। ');
INSERT INTO "al_umari" VALUES(39,22,'39:22','तो क्या खोल दिया हो अल्लाह ने जिसका सीना इस्लाम के लिए, तो वह एक प्रकाश पर हो अपने पालनहार की ओर से। तो विनाश है जिनके दिल कड़े हो गये अल्लाह के स्मरण से, वही खुले कुपथ में हैं।');
INSERT INTO "al_umari" VALUES(39,23,'39:23','अल्लाह ही है, जिसने सर्वोत्तम ह़दीस (क़ुर्आन) को अवतरित किया है। ऐसी पुस्तक, जिसकी आयतें मिलती-जुलती बार-बार दुहराई जाने वाली हैं। जिसे (सुनकर) खड़े हो जाते हैं उनके रूँगटे, जो डरते हैं अपने पालनहार से। फिर कोमल हो जाते हैं उनके अंग तथा दिल, अल्लाह के स्मरण की ओर। यही है अल्लाह का मार्गदर्शन, जिसके द्वारा वह संमार्ग पर लगा देता है, जिसे चाहता है और जिसे अल्लाह कुपथ कर दे, तो उसका कोई पथदर्शक नहीं है।');
INSERT INTO "al_umari" VALUES(39,24,'39:24','तो क्या जो अपनी रक्षा करेगा अपने मुख[ से, बुरी यातना से, प्रलय के दिन? तथा कहा जायेगा अत्याचारियों सेः चखो, जो तुम कर रहे थे। ');
INSERT INTO "al_umari" VALUES(39,25,'39:25','झुठला दिया उन्होंने, जो इनसे पूर्व थे। तो आ गयी यातना उनके पास, जहाँ से उन्हें अनुमान (भी) न था।');
INSERT INTO "al_umari" VALUES(39,26,'39:26','तो चखा दिया अल्लाह ने उन्हें अपमान, सांसारिक जीवन में और आख़िरत (परलोक) की यातना निश्चय अत्यधिक बड़ी है। क्या ही अच्छा होता, यदि वे जानते।');
INSERT INTO "al_umari" VALUES(39,27,'39:27','और हमने मनुष्य के लिए इस क़ुर्आन में प्रत्येक उदाहरण दिये हैं, ताकि वे शिक्षा ग्रहण करें।');
INSERT INTO "al_umari" VALUES(39,28,'39:28','अरबी भाषा में क़ुर्आन, जिसमें कोई टेढ़ापन नहीं है, ताकि वे अल्लाह से डरें।');
INSERT INTO "al_umari" VALUES(39,29,'39:29','अल्लाह ने एक उदाहरण दिया है एक व्यक्ति का, जिसमें बहुत-से परस्पर विरोधी साझी हैं तथा एक व्यक्ति पूरा एक व्यक्ति का (दास) है। तो क्या दशा में दोनों समान हो जायेंगे?[ सब प्रशंसा अल्लाह के लिए है, बल्कि उनमें से अधिक्तर नहीं जानते। ');
INSERT INTO "al_umari" VALUES(39,30,'39:30','(हे नबी!) निश्चय आपको मरना है तथा उन्हें भी मरना है।');
INSERT INTO "al_umari" VALUES(39,31,'39:31','फिर तुम सभी[ प्रलय के दिन अल्लाह के समक्ष झगड़ोगे। ');
INSERT INTO "al_umari" VALUES(39,32,'39:32','तो उससे बड़ा अत्याचारी कौन हो सकता है जो अल्लाह पर झूठ बोले तथा सच को झुठलाये, जब उसके पास आ गया? तो क्या नरक में नहीं है, ऐसे काफ़िरों का स्थान?');
INSERT INTO "al_umari" VALUES(39,33,'39:33','तथा जो सत्य लाये[ और जिसने उसे सच माना, तो वही (यातना से) सुरक्षित रहने वाले हैं। ');
INSERT INTO "al_umari" VALUES(39,34,'39:34','उन्हीं के लिए है, जो वह चाहेंगे उनके पालनहार के यहाँ और यही सदाचारियों का प्रतिफल है।');
INSERT INTO "al_umari" VALUES(39,35,'39:35','ताकि अल्लाह क्षमा कर दे, जो कुकर्म उन्होंने किये हैं तथा उन्हें प्रदान करे उनका प्रतिफल उनके उत्तम कर्मों के बदले, जो वे कर रहे थे।');
INSERT INTO "al_umari" VALUES(39,36,'39:36','क्या अल्लाह प्रयाप्त नहीं है अपने भक्त के लिए? तथा वह आपको डराते हैं उनसे, जो उसके सिवा हैं तथा जिसे अल्लाह कुपथ कर दे, तो नहीं है उसे कोई सुपथ दर्शाने वाला।');
INSERT INTO "al_umari" VALUES(39,37,'39:37','और जिसे अल्लाह सुपथ दर्शा दे, तो नहीं है उसे कोई कुपथ करने वाला। क्या नहीं है अल्लाह प्रभुत्वशाली, बदला लेने वाला?');
INSERT INTO "al_umari" VALUES(39,38,'39:38','और यदि आप, उनसे प्रश्न करें कि किसने पैदा किया है आकाशों तथा धरती को? तो वे अवश्य कहेंगे कि अल्लाह ने। आप कहिये कि तुम बताओ, जिसे तुम अल्लाह के सिवा पुकारते हो, यदि अल्लाह मुझे कोई हानि पहुँचाना चाहे, तो क्या ये उसकी हानि दूर कर सकता हैं? अथवा मेरे साथ दया करना चाहे, तो क्या ये रोक सकते हैं उसकी दया को? आप कह दें कि मुझे पर्याप्त है अल्लाह और उसीपर भरोसा करते हैं, भरोसा करने वाले।');
INSERT INTO "al_umari" VALUES(39,39,'39:39','आप कह दें कि हे मेरी जाति के लोगो! तुम काम करो अपने स्थान पर, मैंभी काम कर रहा हूँ। तो शीघ्र ही तुम्हें ज्ञान हो जायेगा।');
INSERT INTO "al_umari" VALUES(39,40,'39:40','कि किसके पास आती है ऐसी यातना, जो उसे अपमानित कर दे तथा उतरती है किसके ऊपर स्थायी यातना?');
INSERT INTO "al_umari" VALUES(39,41,'39:41','वास्तव में, हमने ही अवतरित की है आपपर ये पुस्तक, लोगों के लिए सत्य के साथ। तो जिसने मार्गदर्शन प्राप्त कर लिया, तो उसके अपने (लाभ के) लिए है तथा जो कुपथ हो गया, तो वह कुपथ होता है अपने ऊपर तथा आप उनपर संरक्षक नहीं हैं।');
INSERT INTO "al_umari" VALUES(39,42,'39:42','अल्लाह ही खींचता है प्राणों को, उनके मरण के समय तथा जिसके मरण का समय नहीं आया, उसकी निद्रा में। फिर रोक लेता है जिसपर निर्णय कर दिया हो मरण का तथा भेज देता है अन्य को, एक निर्धारित समय तक के लिए। वास्तव में, इसमें कई निशानियाँ हैं उनके लिए, जो मनन-चिन्तन[ करते हों। ');
INSERT INTO "al_umari" VALUES(39,43,'39:43','क्या उन्होंने बना लिए हैं अल्लाह के अतिरिक्त बहुत-से अभिस्तावक (सिफ़ारिशी)? आप कह दें: क्या (ये सिफ़ारिश करेंगे) यदि वे अधिकार न रखते हों किसी चीज़ का और न ही समझ सकते हों?');
INSERT INTO "al_umari" VALUES(39,44,'39:44','आप कह दें कि अनुशंसा (सिफ़ारिश) तो सब अल्लाह के अधिकार में है। उसी के लिए है आकाशों तथा धरती का राज्य। फिर उसी की ओर तुम फिराये जाओगे।');
INSERT INTO "al_umari" VALUES(39,45,'39:45','तथा जब वर्णन किया जाता है अकेले अल्लाह का, तो संकीर्ण होने लगते हैं उनके दिल, जो ईमान नहीं रखते आख़िरत[ पर तथा जब वर्णन किया जाता है उनका, जो उसके सिवा हैं, तो वे सहसा प्रसन्न हो जाते हैं। ');
INSERT INTO "al_umari" VALUES(39,46,'39:46','(हे नबी!) आप कहें: हे अल्लाह आकाशों तथा धरती के पैदा करने वाले, परोक्ष तथा प्रत्यक्ष के ज्ञानी! तू ही निर्णय करेगा अपने भक्तों के बीच, जिस बात में वे झगड़ रहे थे।');
INSERT INTO "al_umari" VALUES(39,47,'39:47','और यदि उनका, जिन्होंने अत्याचार किया है, जो कुछ धरती में है, सब हो जाये तथा उसके समान उसके साथ और आ जाये, तो वे उसे दण्ड में दे देंगे[ घोर यातना के बदले, प्रलय के दिन तथा खुल जायेगी उनके लिए अल्लाह की ओर, से वो बात, जिसे वे समझ नहीं रहे थे। ');
INSERT INTO "al_umari" VALUES(39,48,'39:48','तथा खुल जायेंगी उनके लिए उनके करतूतों की बुराईयाँ और उन्हें घेर लेगा, जिसका वे उपहास कर रहे थे।');
INSERT INTO "al_umari" VALUES(39,49,'39:49','और जब पहुँचता है मनुष्यों को कोई दुःख, तो हमें पुकारता है। फिर जब हम प्रदान करते हैं कोई सुख अपनी ओर से, तो कहता हैः ये तो मुझे प्रदान किया गया है ज्ञान के कारण। बल्कि, ये एक परीक्षा है। किन्तु, लोगों में से अधिक्तर (इसे) नहीं जानते।');
INSERT INTO "al_umari" VALUES(39,50,'39:50','यही बात, उन लोगों ने भी कही थी, जो इनसे पूर्व थे। तो नहीं काम आया उनके, जो कुछ वे कमा रहे थे।');
INSERT INTO "al_umari" VALUES(39,51,'39:51','फिर आ पड़े उनपर उनके सब कुकर्म और जो अत्याचार किये हैं इनमें से, आ पड़ेंगे उनपर (भी) उनके कुकर्म तथा वे (हमें) विवश करने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(39,52,'39:52','क्या उन्हें ज्ञान नहीं कि अल्लाह फैलाता है जीविका, जिसके लिए चाहता है तथा नापकर देता है (जिसके लिए चाहता है)? निश्चय इसमें कई निशानियाँ हैं, उन लोगों के लिए, जो ईमान रखते हैं।');
INSERT INTO "al_umari" VALUES(39,53,'39:53','आप कह दें मेरे उन भक्तों से, जिन्होंने अपने ऊपर अत्याचार किये हैं कि तुम निराश[ न हो अल्लाह की दया से। वास्तव में, अल्लाह क्षमा कर देता है सब पापों को। निश्चय वह अति क्षमी, दयावान् है। ');
INSERT INTO "al_umari" VALUES(39,54,'39:54','तथा झुक पड़ो अपने पालनहार की ओर और आज्ञाकारी हो जाओ उसके, इससे पूर्व कि तुमपर यातना आ जाये, फिर तुम्हारी सहायता न की जाये।');
INSERT INTO "al_umari" VALUES(39,55,'39:55','तथा पालन करो उस सर्वोत्तम (क़र्आन) का, जो अवतरित किया गया है तुम्हारी ओर तुम्हारे पालनहार की ओर से, इससे पूर्व कि आ पड़े तुमपर यातना और तुम्हें ज्ञान न हो।');
INSERT INTO "al_umari" VALUES(39,56,'39:56','(ऐसा न हो कि) कोई व्यक्ति कहे कि हाय संताप! इस बात पर कि मैं आलस्य किया अल्लाह के पक्ष में तथा मैं उपहास करने वालों में रह गया।');
INSERT INTO "al_umari" VALUES(39,57,'39:57','अथवा कहे कि यदि अल्लाह मुझे सुपथ दिखाता, तो मैं डरने वालों में से हो जाता।');
INSERT INTO "al_umari" VALUES(39,58,'39:58','अथवा कहे जब देख ले यातना को कि यदि मुझे (संसार में) फिरकर जाने का अवसर हो जाये, तो मैं अवश्य सदाचारियों में से हो जाऊँगा।');
INSERT INTO "al_umari" VALUES(39,59,'39:59','हाँ, आईं तुम्हारे पास मेरी निशानियाँ, तो तुमने उन्हें झुठला दिया और अभिमान किया तथा तुम थे ही काफ़िरों में से।');
INSERT INTO "al_umari" VALUES(39,60,'39:60','और प्रलय के दिन आप उन्हें देखेंगे, जिन्होंने अल्लाह पर झूठ बोले कि उनके मुख काले होंगे। तो क्या नरक में नहीं है अभिमानियों का स्थान?');
INSERT INTO "al_umari" VALUES(39,61,'39:61','तथा बचा लेगा अल्लाह जो आज्ञाकारी रहे, उन्हें, उनकी सफलता के साथ। नहीं लगेगा उन्हें कोई दुःख और न वे उदासीन होंगे।');
INSERT INTO "al_umari" VALUES(39,62,'39:62','अल्लाह ही प्रत्येक वस्तु का पैदा करने वाला तथा वही प्रत्येक वस्तु का रक्षक है।');
INSERT INTO "al_umari" VALUES(39,63,'39:63','उसी के अधिकार में हैं आकाशों तथा धरती की कुंजियाँ[ तथा जिन्होंने नकार दिया अल्लाह की आयतों को, वही क्षति में हैं। ');
INSERT INTO "al_umari" VALUES(39,64,'39:64','आप कह दें: तो क्या अल्लाह से अन्य की तुम मुझे इबादत (वंदना) करने का आदेश देते हो, हे अज्ञानो?');
INSERT INTO "al_umari" VALUES(39,65,'39:65','तथा वह़्यी की गई है आपकी ओर तथा उन (नबियों) की ओर, जो आपसे पूर्व (हुए) कि यदि आपने शिर्क किया, तो अवश्य व्यर्थ हो जायेगा आपका कर्म तथा आप हो जायेंगे[ क्षतिग्रस्तों में से। ');
INSERT INTO "al_umari" VALUES(39,66,'39:66','बल्कि आप अल्लाह ही की इबादत (वंदना) करें तथा कृज्ञयों में रहें।');
INSERT INTO "al_umari" VALUES(39,67,'39:67','तथा उन्होंने अल्लाह का सम्मान नहीं किया, जैसे उसका सम्मान करना चाहिये था और धरती पूरी उसकी एक मुट्ठी में होगी, प्रलय के दिन तथा आकाश लपेटे हुए होंगे उसके हाथ[ में। वह पवित्र तथा उच्च है उस शिर्क से, जो वे कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(39,68,'39:68','तथा सूर (नरसिंधघा) फूँका[ जायेगा, तो निश्चेत होकर गिर जायेंगे, जो आकाशों तथा धरती में हैं। परन्तु जिसे अल्लाह चाहे, फिर उसे पुनः फूँका जायेगा, तो सहसा सब खड़े देख रहे होंगे। ');
INSERT INTO "al_umari" VALUES(39,69,'39:69','तथा जगमगाने लगेगी धरती, अपने पालनहार की ज्योति से और प्रस्तुत किये जायेंगे कर्म लेख तथा लाया जायेगा नबियों और साक्षियों को तथा निर्णय किया जायेगा उनके बीच सत्य (न्याय) के साथ और उनपर अत्याचार नहीं किया जायेगा।');
INSERT INTO "al_umari" VALUES(39,70,'39:70','तथा पूरा-पूरा दिया जायेगा प्रत्येक जीव को, उसका कर्मफल तथा वह भली-भाँति जानने वाला है उसे, जो वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(39,71,'39:71','तथा हाँके जायेंगे, जो काफ़िर हो गये नरक की ओर, झुण्ड बनाकर। यहाँतक कि जब वे उसके पास आयेंगे, तो खोल दिये जायेंगे उसके द्वार तथा उनसे कहेंगे उसके रक्षक (फ़रिश्तेः) क्या नहीं आये तुम्हारे पास रसूल तुममें से, जो तुम्हें सुनाते, तुम्हारे पालनहार की आयतें तथा सचेत करते तुम्हें, इस दिन का सामना करने से? वे कहेंगेः क्यों नहीं? परन्तु, सिध्द हो गया यातना का शब्द, काफ़िरों पर।');
INSERT INTO "al_umari" VALUES(39,72,'39:72','कहा जायेगा कि प्रवेश कर जाओ नरक के द्वारों में, सदावासी होकर उसमें। तो बुरा है घमंडियों का निवास स्थान।');
INSERT INTO "al_umari" VALUES(39,73,'39:73','तथा भेज दिये जायेंगे, जो लोग डरते रहे अपने पालनहार से, स्वर्ग की ओर झुण्ड बनाकर। यहाँतक कि जब वे आ जायेंगे उसके पास तथा खोल दिये जायेंगे उसके द्वार और कहेंगे उनसे उसके रक्षकः सलाम है तुमपर, तुम प्रसन्न रहो। तुम प्रवेश कर जाओ उसमें, सदावासी होकर।');
INSERT INTO "al_umari" VALUES(39,74,'39:74','तथा वे कहेंगेः सब प्रशंसा अल्लाह के लिए हैं, जिसने सच कर दिखाया हमसे अपना वचन तथा हमें उत्तराधिकारी बना दिया इस धरती का, हम रहें स्वर्ग में, जहाँ चाहें। क्या ही अच्छा है कार्यकर्ताओं[ का प्रतिफल। ');
INSERT INTO "al_umari" VALUES(39,75,'39:75','तथा आप देखेंगे फ़रिश्तों को घेरे हुए अर्श (सिंहासन) के चतुर्दिक, वे पवित्रता गान कर रहे होंगे अपने पालनहार की प्रशंसा के साथ, निर्णय कर दिया जायेगा लोगों के बीच सत्य के साथ तथा कह दिया जायेगा कि सब प्रशंसा अल्लाह, सर्वलोक के पालनहार के लिए है।[ ');
INSERT INTO "al_umari" VALUES(40,1,'40:1','ह़ा मीम।');
INSERT INTO "al_umari" VALUES(40,2,'40:2','इस पुस्तक का उतरना अल्लाह की ओर से है, जो सब चीज़ों और गुणों को जानने वाला है।');
INSERT INTO "al_umari" VALUES(40,3,'40:3','पाप क्षमा करने, तौबा स्वीकार करने, क्षमायाचना का स्वीकारी, कड़ी यातना देने वाला, समाई वाला, जिसके सिवा कोई ( सच्चा) वंदनीय नहीं। उसी की ओर (सबको) जाना है।');
INSERT INTO "al_umari" VALUES(40,4,'40:4','नहीं झगड़ते हैं अल्लाह की आयतों में उनके सिवा, जो काफ़िर हो गये। अतः, धोखे में न डाल दे आपको उनकी यातायात देशों में।');
INSERT INTO "al_umari" VALUES(40,5,'40:5','झुठलाया इनसे पूर्व, नूह़ की जाति ने तथा बहुत-से समुदायों ने उनके पश्चात् तथा विचार किया प्रत्येक समुदाय ने अपने रसूल को बंदी बना लेने का तथा विवाद किया असत्य के सहारे, ताकि असत्य बना दें सत्य को। तो हमने उन्हें पकड़ लिया। फिर कैसी रही हमारी यातना?');
INSERT INTO "al_umari" VALUES(40,6,'40:6','और इसी प्रकार, सिध्द हो गई आपके पालनहार की बात उनपर, जो काफ़िर हो गये कि वही नारकी हैं।');
INSERT INTO "al_umari" VALUES(40,7,'40:7','वे (फ़रिश्ते) जो अपने ऊपर उठाये हुए हैं अर्श (सिंहासन) को तथा जो उसके आस-पास हैं, वे पवित्रता गान करते रहते हैं अपने पालनहार की प्रशंसा के साथ तथा उसपर ईमान रखते हैं और क्षमा याचना करते रहते हैं उनके लिए, जो ईमान लाये हैं।[ हे हमारे पालनहार! तूने घेर रखा है प्रत्येक वस्तु को (अपनी) दया तथा ज्ञान से। अतः, क्षमा कर दे उनको जो क्षमा माँगें तथा चलें तेरे मार्ग पर तथा बचा ले उन्हें, नरक की यातना से। ');
INSERT INTO "al_umari" VALUES(40,8,'40:8','हे हमारे पालनहार! तथा प्रवेश कर दे उन्हें ,उन स्थायी स्वर्गों में, जिनका तूने उन्हें वचन दिया है तथा जो सदाचारी हैं, उनके पूर्वजों, पत्नियों और उनकी संतानों में से। निश्चय तू सब चीज़ों और गुणों को जानने वाला है।');
INSERT INTO "al_umari" VALUES(40,9,'40:9','तथा उन्हें सुरक्षित रख दुष्कर्मों से तथा तूने जिसे बचा लिया दुष्कर्मों से उस दिन, तो दया कर दी उसपर और यही बड़ी सफलता है।');
INSERT INTO "al_umari" VALUES(40,10,'40:10','जिन लोगों ने कुफ़्र किया है, उन्हें (प्रलय के दिन) पुकारा जायेगा कि अल्लाह का क्रोध तुमपर उससे अधिक था, जितना तुम्हें आज अपने ऊपर क्रोध आ रहा है, जब तुम संसार में ईमान की ओर बुलाये[ जा रहे थे। ');
INSERT INTO "al_umari" VALUES(40,11,'40:11','वे कहेंगेः हे हमारे पालनहार! तूने हमें दो बार मारा[ तथा जीवित (भी) दो बार किया। अतः, हमने मान लिया अपने पापों को। तो क्या (यातना से) निकलने की कोई राह (उपाय) है? ');
INSERT INTO "al_umari" VALUES(40,12,'40:12','(ये यातना) इस कारण है कि जब तुम्हें (संसार में) बुलाया गया अकेले अल्लाह की ओर, तो तुमने कुफ़्र कर दिया और यदि शिर्क किया जाता उसके साथ, तो तुम मान लेते थे। तो आदेश देने का अधिकार अल्लाह को है, जो सर्वोच्च, सर्वमहान् है।');
INSERT INTO "al_umari" VALUES(40,13,'40:13','वही दिखाता है तुम्हें अपनी निशानियाँ तथा उतारता है तुम्हारे लिए आकाश से जीविका और शिक्षा ग्रहण नहीं करता, परन्तु वही, जो (उसकी ओर) ध्यान करता है।');
INSERT INTO "al_umari" VALUES(40,14,'40:14','तो तुम पुकारो अल्लाह को शुध्द करके उसके लिए धर्म को, यद्यपि बुरा लगे काफ़िरों को।');
INSERT INTO "al_umari" VALUES(40,15,'40:15','वह उच्च श्रेणियों वाला अर्श का स्वामी है। वह उतारता है अपने आदेश से रूह़[ (वह़्यी), जिसपर चाहता है, अपने भक्तों में से, ताकि वह सचेत करे, मिलने के दिन से। ');
INSERT INTO "al_umari" VALUES(40,16,'40:16','जिस दिन सब लोग (जीवित होकर) निकल पड़ेंगे। नहीं छुपी होगी अल्लाह पर उनकी कोई चीज़। किसका राज्य है आज?[ अकेले प्रभुत्वशाली अल्लाह का। ');
INSERT INTO "al_umari" VALUES(40,17,'40:17','आज प्रतिकार दिया जायेगा प्रत्येक प्राणी को, उसके करतूत का। कोई अत्याचार नहीं है आज। वास्तव में, अल्लाह अति शीघ्र ह़िसाब लेने वाला है।');
INSERT INTO "al_umari" VALUES(40,18,'40:18','तथा आप सावधान कर दें उन्हें आगामी समीप दिन से, जब दिल मुँह को आ रहे होंगे। लोग शोक से भरे होंगे। नहीं होगा अत्याचारियों का कोई मित्र, न कोई सिफ़ारिशी, जिसकी बात मानी जाये।');
INSERT INTO "al_umari" VALUES(40,19,'40:19','वह जानता है आँखों की चोरी तथा जो (भेद) सीने छुपाते हैं।');
INSERT INTO "al_umari" VALUES(40,20,'40:20','अल्लाह ही निर्णय करेगा सत्य के साथ तथा जिन्हें वे पुकारते हैं अल्लाह के अतिरिक्त, वे कोई निर्णय नहीं कर सकते। निश्चय अल्लाह ही भली-भाँति सुनने-देखने वाला है।');
INSERT INTO "al_umari" VALUES(40,21,'40:21','क्या वह चले-फिरे नहीं धरती में, ताकि देखते कि कैसा रहा उनका परिणाम, जो इनसे पूर्व थे? वे इनसे अधिक थे बल में तथा अधिक चिन्ह छोड़ गये धरती में। तो पकड़ लिया अल्लाह ने उन्हें उनके पापों के कारण और नहीं था उनके लिए अल्लाह से कोई बचाने वाला।');
INSERT INTO "al_umari" VALUES(40,22,'40:22','ये इस कारण हुआ कि उनके पास लाते थे हमारे रसूल खुली निशानियाँ, तो उन्होंने कुफ़्र किया। अन्ततः, पकड़ लिया उन्हें अल्लाह ने। वस्तुतः, वह अति शक्तिशाली, घोर यातना देने वाला है।');
INSERT INTO "al_umari" VALUES(40,23,'40:23','तथा हमने भेजा मूसा को अपनी निशानियों और हर प्रकार के प्रमाण के साथ।');
INSERT INTO "al_umari" VALUES(40,24,'40:24','फ़िरऔन और (उसके मंत्री) हामान तथा क़ारून के पास। तो उन्होंने कहाः ये तो बड़ा झूठा जादूगर है।');
INSERT INTO "al_umari" VALUES(40,25,'40:25','तो जब वह उनके पास सत्य लाया हमारी ओर से, तो सबने कहाः वध कर दो उनके पुत्रों को, जो ईमान लोये हैं उसके साथ तथा जीवित रहने दो उनकी स्त्रियों को और काफ़िरों का षड्यंत्र निष्फल (व्यर्थ) ही हुआ।[ ');
INSERT INTO "al_umari" VALUES(40,26,'40:26','और कहा फ़िरऔन ने (अपने प्रमुखों सेः) मुझे छोड़ो, मैं वध कर दूँ मूसा को और उसे चाहिये कि पुकारे अपने पालनहार को। वास्तव में, मैं डरता हूँ कि वह बदल देगा तुम्हारे धर्म[ को अथवा पैदा कर देगा इस धरती (मिस्र) में उपद्रव। ');
INSERT INTO "al_umari" VALUES(40,27,'40:27','तथा मूसा ने कहाः मैंने शरण ली है अपने पालनहार तथा तुम्हारे पालनहार की प्रत्येक अहंकारी से, जो ईमान नहीं रखता ह़िसाब के दिन पर।');
INSERT INTO "al_umari" VALUES(40,28,'40:28','तथा कहा एक ईमान वाले व्यक्ति ने फ़िरऔन के घराने के, जो छुपा रहा था अपना ईमानः क्या तुम वध कर दोगे एक व्यक्ति को कि वह कह रहा हैः मेरा पालनहार अल्लाह है? जबकि वह तुम्हारे पास लाया है खुली निशानियाँ तुम्हारे पालनहार की ओर से? और यदि वह झूठा हो, तो उसी के ऊपर है उनका झूठ और यदि सच्चा हो, तो आ पड़ेगा वह कुछ, जिसकी तुम्हें धमकी दे रहा है। वास्तव में, अल्लाह मार्गदर्शन नहीं देता उसे, जो उल्लंघनकारी बहुत झूठा हो।');
INSERT INTO "al_umari" VALUES(40,29,'40:29','हे मेरी जाति के लोगो! तुम्हारा राज्य है आज, तुम प्रभावशाली हो धरती में, तो कौन हमारी रक्षा करेगा अल्लाह की यातना से, यदि वह हमपर आ जाये? फ़िरऔन ने कहाः मैं तुम सब को वही समझा रहा हूँ, जिसे मैं उचित समझता हूँ और तुम्हें सीधी ही राह दिखा रहा हूँ।');
INSERT INTO "al_umari" VALUES(40,30,'40:30','तथा उसने कहा, जो ईमान लायाः हे मेरी जाति! मैं तुमपर डरता हूँ (अगले) समुदायों के दिन जैसे (दिन)[ से। ');
INSERT INTO "al_umari" VALUES(40,31,'40:31','नूह़ की जाति की जैसी दशा से तथा आद और समूद की एवं जो उनके पश्चात् हुए तथा अल्लाह नहीं चाहता कोई अत्याचार भक्तों के लिए।');
INSERT INTO "al_umari" VALUES(40,32,'40:32','तथा हे मेरी जाति! मैं डर रहा हूँ तुमपर, एक-दूसरे को पुकारने के दिन[ से। ');
INSERT INTO "al_umari" VALUES(40,33,'40:33','जिस दिन तुम पीछे फिरकर भागोगे, नहीं होगा तुम्हें अल्लाह से कोई बचाने वाला तथा जिसे अल्लाह कुपथ कर दे, तो उसका कोई पथ पर्दर्शक नहीं।');
INSERT INTO "al_umari" VALUES(40,34,'40:34','तथा आये यूसुफ़ तुम्हारे पास इससे पूर्व खुले प्रमाणों के साथ, तो तुम बराबर संदेह में रहे उससे, जो तुम्हारे पास लाये। यहाँतक कि जब वे मर गये, तो तुमने कहा कि कदापि नहीं भेजेगा अल्लाह उनके पश्चात् कोई रसूल।[ इसी प्रकार अल्लाह कुपथ कर देता है उसे, जो उल्लंघनकारी डाँवाडोल हो। ');
INSERT INTO "al_umari" VALUES(40,35,'40:35','जो झगड़ते हैं अल्लाह की आयतों में, बिना किसी ऐसे प्रमाण के, जो उनके पास आया हो। तो ये बड़े क्रोध की बात है अल्लाह के समीप तथा उनके समीप, जो ईमान लाये हैं। इसी प्रकार, अल्लाह मुहर लगा देता है प्रत्येक अहंकारी अत्याचारी के दिल पर।');
INSERT INTO "al_umari" VALUES(40,36,'40:36','तथा कहा फ़िरऔन ने कि हे हामान! मेरे लिए बना दो एक उच्च भवन, संभवतः मैं उन मार्गों तक पहुच सकूँ।');
INSERT INTO "al_umari" VALUES(40,37,'40:37','आकाश के मार्गों तक, मैं देखूँ मूसा के पूज्य (उपास्य) को और निश्चय मैं उसे झूठा समझ रहा हूँ और इसी प्रकार, शोभनीय बना दिया गया फ़िरऔन के लिए उसका दुष्कर्म तथा रोक दिया गया संमार्ग से और फ़िरऔन का षड्यंत्र विनाश ही में रहा।');
INSERT INTO "al_umari" VALUES(40,38,'40:38','तथा उसने कहा जो ईमान लायाः हे मेरी जाति! मेरी बात मानो, मैं तुम्हें सीधी राह बता रहा हूँ।');
INSERT INTO "al_umari" VALUES(40,39,'40:39','हे मेरी जाति! ये सांसारिक जीवन कुछ साम्यिक लाभ है तथा वास्तव में, परलोक ही स्थायी निवास है।');
INSERT INTO "al_umari" VALUES(40,40,'40:40','जिसने दुष्कर्म किया, तो उसे उसी के समान प्रतिकार दिया जायेगा तथा जो सुकर्म करेगा; नर अथवा नारी में से और वह ईमान वाला (एकेश्वरवादी) हो, तो वही प्रवेश करेंगे स्वर्ग में। जीविका दिये जायेंगे उसमें अगणित।');
INSERT INTO "al_umari" VALUES(40,41,'40:41','तथा हे मेरी जाति! क्या बात है कि मैं बुला रहा हूँ तुम्हें मुक्ति की ओर तथा तुम बुला रहे हो मुझे नरक की ओर?');
INSERT INTO "al_umari" VALUES(40,42,'40:42','तुम मुझे बुला रहे हो, ताकि मैं कुफ़्र करूँ अल्लाह के साथ और साझी बनाऊँ उसका उसे, जिसका मुझे कोई ज्ञान नहीं है तथा मैं बुला रहा हूँ तुम्हें, प्रभावशाली, अति क्षमी की ओर।');
INSERT INTO "al_umari" VALUES(40,43,'40:43','निश्चित है कि तुम जिसकी ओर मूझे बुला[ रहे हो, वह पुकारने योग्य नहीं है, न लोक में, न परलोक में तथा हमें जाना है अल्लाह ही की ओर तथा वास्तव में, अतिक्रमी ही नारकी हैं। ');
INSERT INTO "al_umari" VALUES(40,44,'40:44','तो तुम याद करोगे, जो मैं कह रहा हूँ तथा मैं समर्पित करता हूँ अपना मामला अल्लाह को। वास्तव में, अल्लाह देख रहा है भक्तों को।');
INSERT INTO "al_umari" VALUES(40,45,'40:45','तो अल्लाह ने उसे सुरक्षित कर दिया, उनके षड्यंत्र की बुराईयों से और घेर लिया फ़िरऔनियों को बुरी यातना ने।');
INSERT INTO "al_umari" VALUES(40,46,'40:46','वे[ प्रस्तुत किये जाते हैं अग्नि पर, प्रातः तथा संध्या तथा जिस दिन प्रलय स्थापित होगी, (ये आदेश होगा) कि डाल दो फ़िरऔनियों को कड़ी यातना में। ');
INSERT INTO "al_umari" VALUES(40,47,'40:47','तथा जब वे झगड़ेंगे अग्नि में, तो कहेंगे निर्बल उनसे, जो बड़े बनकर रहेः हम तुम्हारे अनुयायी थे, तो क्या तुम, दूर करोगे हमसे अग्नि का कुछ भाग?');
INSERT INTO "al_umari" VALUES(40,48,'40:48','वे कहेंगे, जो बड़े बनकर रहेः हमसब इसीमें हैं। अल्लाह निर्णय कर चुका है भक्तों (बंदों) के बीच।');
INSERT INTO "al_umari" VALUES(40,49,'40:49','तथा कहेंगे जो अग्नि में हैं, नरक के रक्षकों सेः अपने पालनहार से प्रार्थना करो कि हमसे हल्की कर दे किसी दिन, कुछ यातना।');
INSERT INTO "al_umari" VALUES(40,50,'40:50','वे कहेंगेः क्या नहीं आये तुम्हारे पास, तुम्हारे रसूल, खुले प्रमाण लेकर? वे कहेंगेः क्यों नहीं? वे कहेंगेः तो तुम ही प्रार्थना करो और काफ़िरों की प्रार्थना व्यर्थ ही होगी।');
INSERT INTO "al_umari" VALUES(40,51,'40:51','निश्चय हम सहायता करेंगे अपने रसूलों की तथा उनकी, जो ईमान लायें, सांसारिक जीवन में तथा जिस दिन[ साक्षी खड़े होंगे। ');
INSERT INTO "al_umari" VALUES(40,52,'40:52','जिस दिन नहीं लाभ पहुँचायेगी अत्याचारियों को उनकी क्षमा याचना तथा उन्हीं के लिए धिक्कार और उन्हीं के लिए बुरा घर है।');
INSERT INTO "al_umari" VALUES(40,53,'40:53','तथा हमने प्रदान किया मूसा को मार्गदर्शन और हमने उत्तराघिकारी बनाया इस्राईल की संतान को पुस्तक (तौरात) का।');
INSERT INTO "al_umari" VALUES(40,54,'40:54','जो मार्गदर्शन तथा शिक्षा थी समझ वालों के लिए।');
INSERT INTO "al_umari" VALUES(40,55,'40:55','तो (हे नबी!) आप धैर्य रखें। वास्तव में, अल्लाह का वचन[ सत्य है तथा क्षमा माँगें अपने पाप की तथा पवित्रता का वर्णन करते रहें अपने पालनहार की प्रशंसा के साथ, संध्या और प्रातः।');
INSERT INTO "al_umari" VALUES(40,56,'40:56','वास्तव में, जो झगड़ते हैं अल्लाह की आयतों में, बिना किसी प्रमाण के, जो आया[ हो उनके पास, तो उनके दिलों में बड़ाई के सिवा कुछ नहीं है, जिस तक वे पहुचने वाले नहीं हैं। अतः, आप अल्लाह की शरण लें। वास्तव में, वही सब कुछ सुनने-जानने वाला है। ');
INSERT INTO "al_umari" VALUES(40,57,'40:57','निश्चय आकाशों तथा धरती को पैदा करना, अधिक बड़ा है, मनुष्य को पैदा करने से। परन्तु, अधिक्तर लोग ज्ञान नहीं रखते।[ ');
INSERT INTO "al_umari" VALUES(40,58,'40:58','तथा समान नहीं होता अंधा तथा आँख वाला और न जो ईमान लाये और सत्कर्म किये हैं और दुष्कर्मी। तुम (बहुत) कम ही शिक्षा ग्रहण करते हो।');
INSERT INTO "al_umari" VALUES(40,59,'40:59','निश्चय, प्रलय आनी ही है। जिसमें कोई संदेह नहीं। परन्तु, अधिक्तर लोग ईमान (विश्वास) नहीं रखते।');
INSERT INTO "al_umari" VALUES(40,60,'40:60','तथा कहा है तुम्हारे पालनहार ने कि मुझीसे प्रार्थना[ करो, मैं तुम्हारी प्रार्थना स्वीकार करूँगा। वास्तव में, जो अभिमान (अहंकार) करेंगे मेरी इबादत (वंदना-प्रार्थना) से, तो वे प्रवेश करेंगे नरक में अपमानित होकर। ');
INSERT INTO "al_umari" VALUES(40,61,'40:61','अल्लाह ही ने तुम्हारे लिए रात्रि बनाई, ताकि तुम विश्राम करो उसमें तथा दिन को प्रकाशमान बनाया।[ वस्तुतः, अल्लाह बड़ा उपकारी है लोगों के लिए। किन्तु, अधिक्तर लोग कृतज्ञ नहीं होते। ');
INSERT INTO "al_umari" VALUES(40,62,'40:62','यही अल्लाह तुम्हारा पालनहार है, प्रत्येक वस्तु का रचयिता, उत्पत्तिकार। नहीं है कोई ( सच्चा) वंदनीय उसके सिवा, फिर तुम कहाँ बहके जाते हो?');
INSERT INTO "al_umari" VALUES(40,63,'40:63','इसी प्रकार, बहका दिये जाते हैं वे, जो अल्लाह की आयतों को नकारते हैं।');
INSERT INTO "al_umari" VALUES(40,64,'40:64','अल्लाह ही है, जिसने बनाया तुम्हारे लिए धरती को निवास स्थान तथा आकाश को छत और तुम्हारा रूप बनाया, तो सुन्दर रूप बनाया तथा तुम्हें जीविका प्रदान की स्व्छ चीज़ों से। वही अल्लाह तुम्हारा पालनहार है, तो शुभ है अल्लाह, सर्वलोक का पालनहार।');
INSERT INTO "al_umari" VALUES(40,65,'40:65','वह जीवित है, कोई ( सच्चा) वंदनीय नहीं है, उसके सिवा। अतः, विशेष रूप से उसकी इबादत करते हुए उसी को पुकारो। सब प्रशंसा सर्वलोक के पालनहार, अल्लाह के लिए हैं।');
INSERT INTO "al_umari" VALUES(40,66,'40:66','आप कह दें: निश्चय मुझे रोक दिया गया है कि इबादत करूँ उनकी, जिन्हें तुम पुकारते हो अल्लाह के सिवा, जब आ गये मेरे पास खुले प्रमाण तथा मुझे आदेश दिया गया है कि मैं सर्वलोक के पालनहार का आज्ञाकारी रहूँ।');
INSERT INTO "al_umari" VALUES(40,67,'40:67','वही है, जिसने तुम्हें पैदा किया मिट्टी से, फिर वीर्य से, फिर बंधे रक्त से, फिर तुम्हें निकालता है (गर्भाशयों से) शिशु बनाकर। फिर बड़ा करता है, ताकि तुम अपनी पूरी शक्ति को पहुँचो। फिर बूढ़े हो जाओ तथा तुममें कुछ इससे पहले ही मर जाते हैं और ये इसलिए होता है, ताकि तुम अपनी निश्चित आयु को पहुँच जाओ तथा ताकि तुम समझो।[ ');
INSERT INTO "al_umari" VALUES(40,68,'40:68','वही है, जो तुम्हें जीवन देता तथा मारता है, फिर जब वह किसी कार्य का निर्णय करता है, तो कहता हैः "हो जा", तो वह हो जाता है।');
INSERT INTO "al_umari" VALUES(40,69,'40:69','क्या आपने नहीं देखा कि जो झगड़ते[ हैं अल्लाह की आयतों में, वे कहाँ बहकाये जा रहे हैं? ');
INSERT INTO "al_umari" VALUES(40,70,'40:70','जिन्होंने झुठला दिया पुस्तक को और उसे, जिसके साथ हमने भेजा अपने रसूलों को, तो शीघ्र ही वे जान लेंगे।');
INSERT INTO "al_umari" VALUES(40,71,'40:71','जब तौक़ होंगे उनके गलों में तथा बेड़ियाँ, वे खींचे जायेंगे।');
INSERT INTO "al_umari" VALUES(40,72,'40:72','खौलते पानी में, फिर अग्नि में झोंक दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(40,73,'40:73','फिर कहा जायेगा उनसेः कहाँ हैं वे, जिन्हें तुम साझी बना रहे थे।');
INSERT INTO "al_umari" VALUES(40,74,'40:74','अल्लाह के सिवा? वे कहेंगे कि वे खो गये हम से, बल्कि, हम नहीं पुकारते थे इससे पूर्व, किसी चीज़ को। इसी प्रकार, अल्लाह कुपथ कर देता है काफ़िरों को।');
INSERT INTO "al_umari" VALUES(40,75,'40:75','ये यातना इसलिए है कि तुम धरती में अवैध इतराते थे तथा इस कारण कि तुम अकड़ते थे।');
INSERT INTO "al_umari" VALUES(40,76,'40:76','प्रवेश कर जाओ नरक के द्वारों में, सदावासी होकर उसमें। तो बुरा स्थान है अभिमानियों का।');
INSERT INTO "al_umari" VALUES(40,77,'40:77','तो आप धैर्य रखें निश्चय अल्लाह का वचन सत्य है। फिर यदि आपको दिखा दें उस (यातना) में से, जिसका वचन उन्हें दे रहे हैं या आपका निधन कर दें, तो वह हमारी ओर ही फेरे जायेंगे।[ ');
INSERT INTO "al_umari" VALUES(40,78,'40:78','तथा (हे नबी!) हम भेज चुके हैं बहुत-से रसूलों को आपसे पूर्व, जिनमें से कुछ का वर्णन हम आपसे कर चुके हैं तथा कुछ का वर्णन आपसे नहीं किया है तथा किसी रसूल के (वश)[ में ये नहीं था कि वह कोई आयत (चमत्कार) ले आये, परन्तु अल्लाह की अनुमति से। फिर जब आ जायेगा अल्लाह का आदेश, तो निर्णय कर दिया जायेगा सत्य के साथ और क्षति में पड़ जायेंगे वहाँ, क्षूठे लोग। ');
INSERT INTO "al_umari" VALUES(40,79,'40:79','अल्लाह ही है, जिसने बनाये तुम्हारे लिए चौपाये, ताकि सवारी करो कुछ पर और कुछ को खाओ।');
INSERT INTO "al_umari" VALUES(40,80,'40:80','तथा तुम्हारे लिए उनमें बहुत लाभ हैं और ताकि तुम उनपर पहुँचो, उस आवश्यक्ता को, जो तुम्हारे[ दिलों में है तथा उनपर और नावों पर तुम्हें सवार किया जाता है। ');
INSERT INTO "al_umari" VALUES(40,81,'40:81','तथा वह दिखाता है तुम्हें अपनी निशानियाँ। तो तुम अल्लाह की किन-किन निशानियों का इन्कार करोगे?');
INSERT INTO "al_umari" VALUES(40,82,'40:82','तो क्या वह चले-फिरे नहीं धरती में, ताकि देखते कि कैसा रहा उनका परिणाम, जो उनसे पूर्व थे? वे उनसे अधिक कड़े थे, शक्ति में और धरती में अधिक चिन्ह[ छोड़ गये। तो नहीं आया उनके काम, जो वे कर रहे थे। ');
INSERT INTO "al_umari" VALUES(40,83,'40:83','जब आये उनके पास हमारे रसूल, निशानियाँ लेकर, तो वे इतराने लगे उस ज्ञान पर,[ जो उनके पास था और घेर लिया उन्हें उसने जिसका वे उपहास कर रहे थे। ');
INSERT INTO "al_umari" VALUES(40,84,'40:84','तो जब उन्होंने देखा हमारी यातना को, तो कहने लगेः हम ईमान लाये अकेले अल्लाह पर तथा नकार दिया उसे, जिसे उसका साझी बना रहे थे।');
INSERT INTO "al_umari" VALUES(40,85,'40:85','तो ऐसा नहीं हुआ कि उन्हें लाभ पहुँचाता उनका ईमान, जब उन्होंने देख लिया हमारी यातना को। यही अल्लाह का नियम है, जो उसके भक्तों में चला आ रहा है और क्षति में पड़ गये यहीं काफ़िर।');
INSERT INTO "al_umari" VALUES(41,1,'41:1','ह़ा, मीम।');
INSERT INTO "al_umari" VALUES(41,2,'41:2','अवतरित है अत्यंत कृपाशील, दयावान् की ओर से।');
INSERT INTO "al_umari" VALUES(41,3,'41:3','(ये ऐसी) पुस्तक है, सविस्तार वर्णित की गई हैं जिसकी आयतें। क़ुर्आन अरबी (भाषा में) है उनके लिए, जो ज्ञान रखते हों।[ ');
INSERT INTO "al_umari" VALUES(41,4,'41:4','वह शुभ सूचना देने तथा सचेत करने वाला है फिर भी मुँह फेर लिया है उनमें से अधिक्तर ने और सुन नहीं रहे हैं।');
INSERT INTO "al_umari" VALUES(41,5,'41:5','तथा उन्होंने कहाः[ हमारे दिल आवरण (पर्दे) में हैं उससे, आप हमें जिसकी ओर बुला रहे हैं तथा हमारे कानों में बोझ है तथा हमारे और आपके बीच एक आड़ है। तो आप अपना काम करें और हम अपना काम कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(41,6,'41:6','आप कह दें कि मैं तो एक मनुष्य हूँ तुम्हारे जैसा। मेरी ओर वह़्यी की जा रही है कि तुम्हारा वंदनीय (पूज्य) केवल एक ही है। अतः, सीधे हो जाओ उसी की ओर तथा क्षमा माँगो उससे और विनाश है मुश्रिकों के लिए।');
INSERT INTO "al_umari" VALUES(41,7,'41:7','जो ज़कात नहीं देते तथा आख़िरत को (भी) नहीं मानते।');
INSERT INTO "al_umari" VALUES(41,8,'41:8','निःसंदेह, जो ईमान लाये तथा सदाचार किये, उन्हीं के लिए अनन्त प्रतिफल है।');
INSERT INTO "al_umari" VALUES(41,9,'41:9','आप कहें कि क्या तुम उसे नकारते हो, जिसने पैदा किया धरती को दो दिन में और बनाते हो उसके साझी? वही है, सर्वलोक का परलनहार।');
INSERT INTO "al_umari" VALUES(41,10,'41:10','तथा बनाये उस (धरती) में पर्वत उसके ऊपर तथा बरकत रख दी उसमें और अंकन किया उसमें उसके वासियों के आहारों का चार[ दिनों में समान रूप से, प्रश्न करने वालों के लिए।');
INSERT INTO "al_umari" VALUES(41,11,'41:11','फिर आकर्षित हुआ आकाश की ओर तथा वह धुआँ था। तो उसे तथा धरती को आदेश दिया कि तुम दोनों आ जाओ प्रसन्न होकर अथवा दबाव से। तो दोनों ने कहाः हम प्रसन्न होकर आ गये।');
INSERT INTO "al_umari" VALUES(41,12,'41:12','तथा बना दिया उनको सात आकाश, दो दिनों में, वह़्यी कर दिया प्रत्येक आकाश में उसका आदेश तथा हमने सुसज्जित किया समीप (संसार) के आकाश को, दीपों (तारों) से तथा सुरक्षा के[ लिए। ये अति प्रभालशाली सर्वज्ञ की योजना है। ');
INSERT INTO "al_umari" VALUES(41,13,'41:13','फिर भी यदि वह विमुख हों, तो आप कह दें कि मैंने तुम्हें सावधान कर दिया कड़ी यातना से, जो आद तथा समूद की कड़ी यातना जैसी होगी।');
INSERT INTO "al_umari" VALUES(41,14,'41:14','जब आये उनके पास, उनके रसूल, उनके आगे तथा उनके पीछे[ से कि न इबादत (वंदना) करो अल्लाह के सिवा की। तो उन्होंने कहाः यदि हमारा पालनहार चाहता, तो किसी फ़रिश्ते को उतार देता। अतः, तुम जिस बात के साथ भेजे गये हो, हम उसे नहीं मानते।');
INSERT INTO "al_umari" VALUES(41,15,'41:15','रहे आद, तो उन्होंने अभिमान किया धरती में अवैध तथा कहा कि कौन हमसे अधिक है बल में? क्या उन्होंने नहीं देखा कि अल्लाह, जिसने उनको पैदा किया उनसे अधिक है बल में तथा हमारी आयतों को नकारते रहे।');
INSERT INTO "al_umari" VALUES(41,16,'41:16','अन्ततः, हमने भेज दी उनपर प्रचण्ड वायु, कुछ अशुभ दिनों में। ताकि चखायें उन्हें अपमानकारी यातना सांसारिक जीवन में और आख़िरत (प्रलोक) की यातना अधिक अपमानकारी है तथा उन्हें कोई सहायता नहीं दी जायेगी।');
INSERT INTO "al_umari" VALUES(41,17,'41:17','और रहे समूद, तो हमने उन्हें मार्ग दिखाया, फिर भी उन्होंने अन्धे बने रहने को मार्गदर्शन से प्रिय समझा। अन्ततः, पकड़ लिया उन्हें अपमानकारी यातना की कड़क ने, उसके कारण जो वे कर रहे थे।');
INSERT INTO "al_umari" VALUES(41,18,'41:18','तथा हमने बचा लिया उन्हें, जो ईमान लाये तथा (अवज्ञा से) डरते रहे।');
INSERT INTO "al_umari" VALUES(41,19,'41:19','और जिस दिन अल्लाह के शत्रु एकत्र किये जायेंगे, तो वे रोक लिए जायेंगे।');
INSERT INTO "al_umari" VALUES(41,20,'41:20','यहाँतक कि जब आ जायेंगे उस (नरक) के पास, तो साक्ष्य देंगे उनपर उनके कान तथा उनकी आँखें और उनकी खालें उस कर्म का, जो वे किया करते थे।');
INSERT INTO "al_umari" VALUES(41,21,'41:21','और वे कहेंगे अपनी खालों सेः क्यों साक्ष्य दिया तुमने हमारे विरुध्द? वे उत्तर देंगी कि हमें बोलने की शक्ति प्रदान की है उसने, जिसने प्रत्येक वस्तु को बोलने की शक्ति दी है तथा उसीने तुम्हें पैदा किया प्रथम बार और उसी की ओर तुमसब फेरे जा रहे हो।');
INSERT INTO "al_umari" VALUES(41,22,'41:22','तथा तुम (पाप करते समय)[ छुपाते नहीं थे कि कहीं साक्ष्य न दें, तुमपर, तुम्हारे कान, तुम्हारी आँख एवं तुम्हारी खालें। परन्तु, तुम समझते रहे कि अल्लाह नहीं जानता उसमें से अधिक्तर बातों को, जो तुम करते हो। ');
INSERT INTO "al_umari" VALUES(41,23,'41:23','इसी कुविचार ने, जो तुमने किया अपने पालनहार के विषय में, तुम्हें नाश कर दिया और तुम विनाशों में हो गये।');
INSERT INTO "al_umari" VALUES(41,24,'41:24','तो यदि वे धैर्य रखें, तबभी नरक ही उनका आवास है और यदि वे क्षमा माँगें, तबभी वे क्षमा नहीं किये जायेंगे।');
INSERT INTO "al_umari" VALUES(41,25,'41:25','और हमने बना दिये उनके लिए ऐसे साथी, जो शोभनीय बना रहे थे उनके लिए, उनके अगले तथा पिछले दुष्कर्मों को तथा सिध्द हो गया उनपर, अल्लाह (की यातना) का वचन, उन समुदायों में, जो गुज़र गये इनसे पूर्व, जिन्नों तथा मनुष्यों में से। वास्तव में, वही क्षतिग्रस्त थे।');
INSERT INTO "al_umari" VALUES(41,26,'41:26','तथा काफ़िरों ने कहा[ कि इस क़ुर्आन को न सुनो और कोलाहल (शोर) करो इस (के सुनाने) के समय। सम्भवतः, तुम प्रभुत्वशाली हो जाओ। ');
INSERT INTO "al_umari" VALUES(41,27,'41:27','तो हम अवश्य चखायेंगे उन्हें, जो काफ़िर हो गये, कड़ी यातना और अवश्य उन्हें कुफ़ल देंगे, उस दुष्कर्म का, जो वे करते रहे।');
INSERT INTO "al_umari" VALUES(41,28,'41:28','ये अल्लाह के शत्रुओं का प्रतिकार नरक है। उनके लिए उसमें स्थायी घर होंगे, उसके बदले, जो हमारी अयतों को नकार रहे हैं।');
INSERT INTO "al_umari" VALUES(41,29,'41:29','तथा वो कहेंगे जो काफ़िर हो गये कि हमारे पालनहर! हमें दिखा दे उन्हें, जिन्हों ने हमें कुपथ किया है, जिन्नों तथा मनुष्यों में से। ताकी हम रौंद दें उन दोनों को, अपने पैरों से। ताकि वे दोनों अधिक नीचे हो जायें।');
INSERT INTO "al_umari" VALUES(41,30,'41:30','निश्चय जिन्होंने कहा कि हमारा पालनहार अल्लाह है, फिर इसीपर स्थिर रह[ गये, तो उनपर फ़रिश्ते उतरते हैं कि भय न करो और न उदासीन रहो तथा उस स्वर्ग से प्रसन्न हो जाओ, जिसका वचन तुम्हें दिया जा रहा है।');
INSERT INTO "al_umari" VALUES(41,31,'41:31','हम तुम्हारे सहायक हैं, सांसारिक जीवन में तथा परलोक में और तुम्हारे लिए उस (स्वर्ग) में वह चीज़ है, जो तुम्हारा मन चाहे तथा उसमें तुम्हारे लिए वह है, जिसकी तुम माँग करोगे।');
INSERT INTO "al_umari" VALUES(41,32,'41:32','अतिथि-सत्कार स्वरूप, अति क्षमी, दयावान् की ओर से।');
INSERT INTO "al_umari" VALUES(41,33,'41:33','और किसकी बात उससे अच्छी होगी, जो अल्लाह की ओर बुलाये तथा सदाचार करे और कहे कि मैं मुसलमानों में से हूँ।');
INSERT INTO "al_umari" VALUES(41,34,'41:34','और समान नहीं होते पुण्य तथा पाप, आप दूर करें (बुराई को) उसके द्वारा, जो सर्वोत्तम हो। तो सहसा आपके तथा जिसके बीच बैर हो, मानो वह हार्दिक मित्र हो गया।[ ');
INSERT INTO "al_umari" VALUES(41,35,'41:35','और ये गुण उन्हीं को प्राप्त होता है, जो सहन करें तथा उन्हीं को होता है, जो बड़े भाग्यशाली हों।');
INSERT INTO "al_umari" VALUES(41,36,'41:36','और यदि आपको शैतान की ओर से कोई संशय हो, तो अल्लाह की शरण लें। वास्तव में, वही सब कुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(41,37,'41:37','तथा उसकी निशानियों में से है रात्रि, दिवस, सूर्य तथा चन्द्रमा, तुम सज्दा न करो सूर्य तथा चन्द्रमा को और सज्दा करो उस अल्लाह को, जिसने पैदा किया है उनको, यदि तुम उसी (अल्लाह) की इबादत (वंदना) करते हो।[ ');
INSERT INTO "al_umari" VALUES(41,38,'41:38','तथा यदि वे अभिमान करें, तो जो (फ़रिश्ते) आपके पालनहार के पास हैं, वे उसकी पवित्रता का वर्णन करते रहते हैं, रात्रि तथा दिवस में और वे थकते नहीं हैं।');
INSERT INTO "al_umari" VALUES(41,39,'41:39','तथा उसी की निशानियों में से है कि आप देखते हैं धरती को सहमी हुई। फिर जैसे ही हमने उसपर जल बरसाया, तो वह लहलहाने लगी तथा उभर गयी। निश्चय जिसने जीवित किया है उसे, अवश्य वही जीवित करने वाला है मुर्दों को। वास्तव में, वह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(41,40,'41:40','जो टेढ़ निकालते हैं हमारी आयतों में, वे हमपर छुपे नहीं रहते। तो क्या जो फेंक दिया जायेगा अग्नि में, उत्तम है अथवा जो निर्भय होकर आयेगा प्रलय के दिन? करो जो चाहो, वास्तव में, वह जो तुम करते हो, उसे देख रहा है।[ ');
INSERT INTO "al_umari" VALUES(41,41,'41:41','निश्चय उन्होंने कुफ़्र कर दिया इस शिक्षा (क़ुर्आन) के साथ, जब आ गयी उनके पास और सच ये है कि ये एक अति सम्मानित पुस्तक है।');
INSERT INTO "al_umari" VALUES(41,42,'41:42','नहीं आ सकता झुठ इसके आगे से और न इसके पीछे से। उतरा है तत्वज्ञ, प्रशंसित (अल्लाह) की ओर से।');
INSERT INTO "al_umari" VALUES(41,43,'41:43','आपसे वही कहा जा रहा है, जो आपसे पूर्व रसूलों से कहा गया।[ वास्तव में, आपका पालनहार क्षमा करने (तथा) दुःखदायी य़ातना देने वाला है। ');
INSERT INTO "al_umari" VALUES(41,44,'41:44','और यदि हम इसे बनाते अरबी (के अतिरिक्त किसी) अन्य भाषा में, तो वे अवश्य कहते कि क्यों नहीं खोल दी गयीं उसकी आयतें? ये क्या कि (पुस्तक) ग़ैर अरबी और (नबी) अरबी? आप कह दें कि वह उनके लिए, जो ईमान लाये, मार्गदर्शन तथा आरोग्यकर है और जो ईमान न लायें, उनके कानों में बोझ है और वह उनपर अंधापन है और वही पुकारे जा रहे हैं दूर स्थानों से।[ ');
INSERT INTO "al_umari" VALUES(41,45,'41:45','तथा हम प्रदान कर चुके हैं मूसा को पुस्तक (तौरात)। तो उसमें भी विभेद किया गया और यदि एक बात पहले ही से निर्धारित न होती[ आपके पालनहार की ओर से, तो निर्णय कर दिया जाता उनके बीच। निःसंदेह, वह उनके विषय में संदेह में डाँवाडोल हैं। ');
INSERT INTO "al_umari" VALUES(41,46,'41:46','जो सदाचार करेगा, तो वह अपने ही लाभ के लिए करेगा और जो दुराचार करेगा, तो उसका दुष्परिणाम उसीपर होगा और आपका पालनहार तनिक भी अत्याचार करने वाला नहीं है भक्तों पर।[ ');
INSERT INTO "al_umari" VALUES(41,47,'41:47','उसी की ओर फेरा जाता है प्रलय का ज्ञान तथा नहीं निकलते कोई फल अपने गाभों से और नहीं गर्भ धारण करती कोई मादा और न जन्म देती है, परन्तु उसके ज्ञान से और जिस दिन वह पूकारेगा उन्हें कि कहाँ हैं मेरे साझी? तो वे कहेंगे कि हमने तुझे बता दिया था कि हममें से कोई उसका गवाह नहीं है।');
INSERT INTO "al_umari" VALUES(41,48,'41:48','और खो जायेंगे[ उनसे वे, जिन्हें पुकारते थे इससे पूर्व तथा वे विश्वास कर लेंगे कि नहीं है उनके लिए कोई शरण का स्थान। ');
INSERT INTO "al_umari" VALUES(41,49,'41:49','नहीं थकता मनुष्य भलाई (सुख) की प्रार्थना से और यदि उसे पहुँच जाये बुराई (दुःख), तो (हताश) निराश[ हो जाता है। ');
INSERT INTO "al_umari" VALUES(41,50,'41:50','और यदि हम उसे[ चखा दें अपनी दया, दुःख के पश्चात्, जो उसे पहुँचा हो, तो अवश्य कह देता है कि मैं तो इसके योग्य ही था और मैं नहीं समझता कि प्रलय होनी है और यदि मैं पुनः अपने पालनहार की ओर गया, तो निश्चय ही मेरे लिए उसके पास भलाई होगी। तो हम अवश्य अवगत कर देंगे काफ़िरों को उनके कर्मों से तथा उन्हें अवश्य घोर यातना चखायेंगे। ');
INSERT INTO "al_umari" VALUES(41,51,'41:51','तथा जब हम उपकार करते हैं मनुष्य पर, तो वह विमुख हो जाता है तथा अकड़ जाता है और जब उसे दुःख पहुँचे, तो लम्बी-चौड़ी प्रार्थना करने लगता है।');
INSERT INTO "al_umari" VALUES(41,52,'41:52','आप कह दें: भला तुम ये तो बताओ, यदि ये क़ुर्आन अल्लाह की ओर से हो, फ़िर तुम कुफ़्र कर जाओ उसके साथ, तो कौन उससे अधिक कुपथ होगा, जो उसके विरोध में दूर तक चला जाये?');
INSERT INTO "al_umari" VALUES(41,53,'41:53','हम शीघ्र ही दिखा देंगे उन्हें अपनी निशानियाँ संसार के किनारों में तथा स्वयं उनके भीतर। यहाँतक कि खुल जायेगी उनके लिए ये बात कि यही सच है[ और क्या ये बात प्रयाप्त नहीं कि आपका पालनहार ही प्रत्येक वस्तु का साक्षी (गवाह) है? ');
INSERT INTO "al_umari" VALUES(41,54,'41:54','सावधान! वही संदेह में हैं, अपने पालनहार से मिलने के विषय में। सावधान! वही (अल्लाह), प्रत्येक वस्तु को घेरे हुए है।');
INSERT INTO "al_umari" VALUES(42,1,'42:1','ह़ा मीम।');
INSERT INTO "al_umari" VALUES(42,2,'42:2','ऐन सीन क़ाफ़।');
INSERT INTO "al_umari" VALUES(42,3,'42:3','इसी प्रकार, (अल्लाह) ने प्रकाशना[ भेजी है आप तथा उन रसूलों की ओर, जो आपसे पूर्व हुए हैं। अल्लाह सबसे प्रबल और सब गुणों को जानने वाला है। ');
INSERT INTO "al_umari" VALUES(42,4,'42:4','उसी का है, जो आकाशों तथा धरती में है और वह बड़ा उच्च, महान है।');
INSERT INTO "al_umari" VALUES(42,5,'42:5','समीप है कि आकाश फट[ पड़ें अपने ऊपर से, जबकि फ़रिश्ते पवित्रता का गान करते हैं अपने पालनहार की प्रशंसा के साथ तथा क्षमायाचना करते हैं उनके लिए, जो धरती में हैं। सुनो! वास्तव में, अल्लाह ही अत्यंत क्षमा करने तथा दया करने वाला है। ');
INSERT INTO "al_umari" VALUES(42,6,'42:6','तथा जिन लोगों ने बना लिए हैं अल्लाह के सिवा संरक्षक, अल्लाह ही उनपर निरीक्षक (निगराँ) है और आप उनके उत्तर दायी[ नहीं हैं। ');
INSERT INTO "al_umari" VALUES(42,7,'42:7','तथा इसी प्रकार, हमने वह़्यी (प्रकाशना) की है आपकी ओर अरबी क़ुर्आन की। ताकि आप सावधान कर दें मक्का[ वासियों को और जो उसके आस-पास हैं तथा सावधान कर दें एकत्र होने के दिन से, जिस दिन के होने में कोई संशय नहीं। एक पक्ष स्वर्ग में तथा एक पक्ष नरक में होगा।');
INSERT INTO "al_umari" VALUES(42,8,'42:8','और यदि अल्लाह चाहता, तो सभी को एक समुदाय[ बना देता। परन्तु, वह प्रवेश कराता है जिसे चाहे, अपनी दया में तथा अत्याचारियों का कोई संरक्षक तथा सहायक न होगा। ');
INSERT INTO "al_umari" VALUES(42,9,'42:9','क्या उन्होंने बना लिए हैं उसके सिवा संरक्षक? तो अल्लाह ही संरक्षक है और जीवित करेगा मुर्दों को और वही जो चाहे, कर सकता है।[ ');
INSERT INTO "al_umari" VALUES(42,10,'42:10','और जिस बात में भी तुमने विभेद किया है, उसका निर्णय अल्लाह ही को करना है।[ वही अल्लाह मेरा पालनहार है, उसीपर मैंने भरोसा किया है तथा उसी की ओर ध्यानमग्न होता हूँ। ');
INSERT INTO "al_umari" VALUES(42,11,'42:11','वह आकाशों तथा धरती का रचयिता है। उसने बनाये हैं तुम्हारी जाति में से तुम्हारे जोड़े तथा पशुओं के जोड़े। वह फैला रहा है तुम्हें इस प्रकार। उसकी कोई प्रतिमा[ नहीं और वह सब कुछ सुनने-जानने वाला है। ');
INSERT INTO "al_umari" VALUES(42,12,'42:12','उसी के[ अधिकार में हैं आकाशों तथा धरती की कुंजियाँ। वह फैला देता है जीविका, जिसके लिए चाहे तथा नाप कर देता है। वास्तव में, वही प्रत्येक वस्तु का जानने वाला है। ');
INSERT INTO "al_umari" VALUES(42,13,'42:13','उसने नियत[ किया है तुम्हारे लिए वही धर्म, जिसका आदेश दिया था नूह़ को और जिसे वह़्यी किया है आपकी ओर तथा जिसका आदेश दिया था इब्राहीम तथा मूसा और ईसा को कि इस धर्म की स्थापना करो और इसमें भेद भाव न करो। यही बात अप्रिय लगी है मुश्रिकों को, जिसकी ओर आप बुला रहे हैं। अल्लाह ही चुनता है इसके लिए जिसे चाहे और सीधी राह उसी को दिखाता है, जो उसी की ओर ध्यानमग्न हो। ');
INSERT INTO "al_umari" VALUES(42,14,'42:14','और उन्होंने[ इसके पश्चात् ही विभेद किया, जब उनके पास ज्ञान आ गया, आपस के विरोध के कारण तथा यदि एक बात पहले से निश्चित न होती आपके पालनहार की ओर से, तो अवश्य निर्णय कर दिया गया होता उनके बीच और जो पुस्तक के उत्तराधिकारी बनाये गये उनके पश्चात्, उसकी ओर से संदेह में उलझे हुए हैं।');
INSERT INTO "al_umari" VALUES(42,15,'42:15','तो आप लोगों को इसी धर्म की ओर बुलाते रहें तथा जैसे आपको आदेश दिया गया है उसपर स्थित रहें और उनकी इच्छाओं पर न चलें तथा कह दें कि मैं ईमान लाया उन सभी पुस्तकों पर, जो अल्लाह ने उतारी[ हैं तथा मूझे आदेश दिया गया है कि तुम्हारे बीच न्याय करूँ। अल्लाह हमारा तथा तुम्हारा पालनहार है। हमारे लिए हमारे कर्म हैं तथा तुम्हारे लिए तुम्हारे कर्म। हमारे और तुम्हारे बीच कोई झगड़ा नहीं। अल्लाह ही हमें एकत्र करेगा तथा उसी की ओर सब को जाना है। ');
INSERT INTO "al_umari" VALUES(42,16,'42:16','तथा जो लोग झगड़ते हैं अल्लाह (के धर्म के बारे) में, जबकि उसे[ मान लिया गया है। उनका विवाद (कुतर्क) असत्य है अल्लाह के समीप तथा उन्हीं पर क्रोध है और उन्हीं के लिए कड़ी यातना है। ');
INSERT INTO "al_umari" VALUES(42,17,'42:17','अल्लाह ही ने उतारी हैं सब पुस्तकें सत्य के साथ तथा तराजू[ को और आपको क्या पता शायद प्रलय का समय समीप हो। ');
INSERT INTO "al_umari" VALUES(42,18,'42:18','शीघ्र माँग कर रहे हैं उस (प्रलय) की, जो ईमान नहीं रखते उसपर और जो ईमान लाये हैं, वे उससे डर रहे हैं तथा विश्वास रखते हैं कि वह सच है। सुनो! निश्चय जो विवाद कर रहे हैं, प्रलय के विषय में, वे कुपथ में बहुत दूर चले गये हैं।');
INSERT INTO "al_umari" VALUES(42,19,'42:19','अल्लाह बड़ा दयालु है अपने भक्तों पर। वह जीविका प्रदान करता है, जिसे चाहे तथा वह बड़ा प्रबल, प्रभावशाली है।');
INSERT INTO "al_umari" VALUES(42,20,'42:20','जो आख़िरत (परलोक) की खेती[ चाहता हो, तो हम उसके लिए उसकी खेती बढ़ा देते हैं और जो संसार की खेती चाहता हो, तो हम उसे उसमें से कुछ दे देते हैं और उसके लिए परलोक में कोई भाग नहीं। ');
INSERT INTO "al_umari" VALUES(42,21,'42:21','क्या इन (मुश्रिकों) के कुछ ऐसे साझी[ हैं, जिन्होंने उनके लिए कोई ऐसा धार्मिक नियम बना दिया है, जिसकी अनुमति अल्लाह ने नहीं दी है? और यदि निर्णय की बात निश्चित न होती, तो (अभी) इनके बीच निर्णय कर दिया जाता तथा निश्चय अत्याचारियों के लिए ही दुःखदायी यातना है। ');
INSERT INTO "al_umari" VALUES(42,22,'42:22','तुम अत्याचारियों को डरते हुए देखोगो उन दुष्कर्मों के कारण, जो उन्होंने किये हैं और वह उनपर आकर रहेगा तथा जो ईमान लाये और सदाचार किये, वे स्वर्ग के बागों में होंगे। वे जिसकी इच्छा करेंगे उनके पालनहार के यहाँ मिलेगा। ये बड़ी दया है।');
INSERT INTO "al_umari" VALUES(42,23,'42:23','ये वह (दया) है, जिसकी शुभ सूचना देता है अल्लाह अपने भक्तों को, जो ईमान लाये तथा सदाचार किये। आप कह दें कि मैं नहीं माँगता हूँ इसपर तुमसे कोई बदला, उस प्रेम के सिवा, जो संबन्धियों[ में (होता) है। ');
INSERT INTO "al_umari" VALUES(42,24,'42:24','क्या वे कहते हैं कि उसने अल्लाह पर झूठ घड़ लिया है? तो यदि अल्लाह चाहे, तो आपके दिल पर मुहर लगा दे।[ और अल्लाह मिटा देता है झूठ को और सच को अपने आदेशों द्वारा सच कर दिखाता है। वह सीनों (दिलों) के भेदों का जानने वाला है। ');
INSERT INTO "al_umari" VALUES(42,25,'42:25','वही है, जो स्वीकार करता है अपने भक्तों की तौबा तथा क्षमा करता है दोषों[ को और जानता है, जो कुछ तुम करते हो। ');
INSERT INTO "al_umari" VALUES(42,26,'42:26','और उनकी प्रार्थना स्वीकार करता है, जो ईमान लाये और सदाचार किये तथा उन्हें अधिक प्रदान करता है अपनी दया से और काफ़िरों ही के लिए कड़ी यातना है।');
INSERT INTO "al_umari" VALUES(42,27,'42:27','और यदि फैला देता अल्लाह जीविका अपने भक्तों के लिए, तो वे विद्रोह[ कर देते धरती में। परन्तु, वह उतारता है एक अनुमान से जैसे वह चाहता है। वास्तव में, वह अपने भक्तों से भली-भाँति सूचित है। (तथा) उन्हें देख रहा है। ');
INSERT INTO "al_umari" VALUES(42,28,'42:28','तथा वही है, जो वर्षा करता है इसके पश्चात् कि लोग निराश हो जायें तथा फैला[ देता है अपनी दया और वही संरक्षक, सराहनीय है। ');
INSERT INTO "al_umari" VALUES(42,29,'42:29','तथा उसकी निशानियों में से है, आकाशों और धरती की उत्पत्ति तथा जो फैलायें हैं उन दोनों में जीव और वह उन्हें एकत्र करने पर जब चाहे,[ सामर्थ्य रखने वाला है। ');
INSERT INTO "al_umari" VALUES(42,30,'42:30','और जो भी दुःख तुम्हें पहुँचता है, वह तुम्हारे अपने करतूत से पहुचता है तथा वह क्षमा कर देता है तुम्हारे बहुत-से पापों को।[ ');
INSERT INTO "al_umari" VALUES(42,31,'42:31','और तुम विवश करने वाले नहीं हो धरती में और न तुम्हारा अल्लाह के सिवा कोई संरक्षक और न सहायक है।');
INSERT INTO "al_umari" VALUES(42,32,'42:32','तथा उसके (सामर्थ्य) की निशानियों में से हैं चलती हुई नाव सागरों में, पर्वतों के समान।');
INSERT INTO "al_umari" VALUES(42,33,'42:33','यदि वह चाहे, तो रोक दे वायु को और वह खड़ी रह जायें उसके ऊपर। निश्चय इसमें बड़ी निशानियाँ हैं प्रत्येक बड़े धैर्यवान[ कृतज्ञ के लिए। ');
INSERT INTO "al_umari" VALUES(42,34,'42:34','अथवा विनाश[ कर दे उन (नावों) का, उनके करतूतों के बदले और वह क्षमा करता है बहुत कुछ। ');
INSERT INTO "al_umari" VALUES(42,35,'42:35','तथा वह जानता है उन्हें, जो झगड़ते हैं हमारी आयतों में। उन्हीं के लिए कोई भागने का स्थान नहीं है।');
INSERT INTO "al_umari" VALUES(42,36,'42:36','तुम्हें जो कुछ दिया गया है, वह सांसारिक जीवन का संसाधन है तथा जो कुछ अल्लाह के पास है, वह उत्तम और स्थायी[ है उनके लिए, जो अल्लाह पर ईमान लाये तथा अपने पालनहार ही पर भरोसा रखते हैं। ');
INSERT INTO "al_umari" VALUES(42,37,'42:37','तथा जो बचते हैं बड़े पापों तथा निर्लज्जा के कर्मों से और जब क्रोध आ जाये तो क्षमा कर देते हैं।');
INSERT INTO "al_umari" VALUES(42,38,'42:38','तथा जिन्होंने अपने पालनहार के आदेश को मान लिया, स्थापना की नमाज़ की और उनके प्रत्येक कार्य आपस के विचार-विमर्श से होते हैं[ और जो कुछ हमने उन्हें प्रदान किया है उसमें से दान करते हैं। ');
INSERT INTO "al_umari" VALUES(42,39,'42:39','और यदि उनपर अत्याचार किया जाये, तो वे बराबरी का बदला लेते हैं।');
INSERT INTO "al_umari" VALUES(42,40,'42:40','और बुराई का प्रतिकार (बदला) बुराई है, उसी जैसी।[ फिर जो क्षमा कर दे तथा सुधार करले, तो उसका प्रतिफल अल्लाह के ऊपर है। वास्तव में, वह प्रेम नहीं करता है अत्याचारियों से। ');
INSERT INTO "al_umari" VALUES(42,41,'42:41','तथा जो बदला लें अपने ऊपर अत्याचार होने के पश्चात्, तो उनपर कोई दोष नहीं है।');
INSERT INTO "al_umari" VALUES(42,42,'42:42','दोष केवल उनपर है, जो लोगों पर अत्याचार करते हैं और नाह़क़ ज़मीन में उपद्रव करते हैं। उन्हीं के लिए दर्दनाक यातना है।');
INSERT INTO "al_umari" VALUES(42,43,'42:43','और जो सहन करे तथा क्षमा कर दे, तो ये निश्चय बड़े साहस[ का कार्य है। ');
INSERT INTO "al_umari" VALUES(42,44,'42:44','तथा जिसे अल्लाह कुपथ कर दे, तो उसका कोई रक्षक नहीं है, उसके पश्चात तथा आप देखेंगे अत्याचारियों को जब वे देखेंगे यातना को, वे कह रहे होंगेः क्या वापसी की कोई राह है?[ ');
INSERT INTO "al_umari" VALUES(42,45,'42:45','तथा आप उन्हें देखेंगे कि वे प्रस्तुत किये जा रहे हैं नरक पर, सिर झुकाये, अपमान के कारण। वे देख रहे होंगे कन्खियों से तथा कहेंगे जो ईमान लाये कि वास्तव में घाटे में वही हैं, जिन्होंने घाटे में डाल दिया स्वयं को तथा अपने परिवार को, प्रलय के दिन। सुनो! अत्याचारी ही स्थायी यातना में होंगे।');
INSERT INTO "al_umari" VALUES(42,46,'42:46','तथा नहीं होंगे उनके कोई सहायक, जो अल्लाह के मुक़ाबले में उनकी सहायता करें और जिसे कुपथ कर दे अल्लाह, तो उसके लिए कोई मार्ग नहीं।');
INSERT INTO "al_umari" VALUES(42,47,'42:47','मान लो अपने पालनहार की बात, इससे पूर्व कि आ जाये वह दिन, जिसे टलना नहीं है अल्लाह की ओर से। नहीं होगा तुम्हारे लिए कोई शरण का स्थान, उस दिन और न छिपकर अनजान बन जाने का।');
INSERT INTO "al_umari" VALUES(42,48,'42:48','फिर भी यदि वे विमुख हों, तो (हे नबी!) हमने नहीं भेजा है आपको उनपर रक्षक बनाकर। आपका दायित्व केवल संदेश पहुँचा देना है और वास्तव में, जब हम चखा देते हैं मनुष्य को अपनी दया, तो वह इतराने लगता है, उसपर और यदि पहुँचता है उन्हें कोई दुःख, उनके करतूत के कारण, तो मनुष्य बड़ा कृतघ्न बन जाता है।');
INSERT INTO "al_umari" VALUES(42,49,'42:49','अल्लाह ही का है आकाशों तथा धरती का राज्य। वह पैदा करता है, जो चाहता है। जिसे चाहे, पुत्रियाँ प्रदान करता है तथा जिसे चाहे, पुत्र प्रदान करता है।');
INSERT INTO "al_umari" VALUES(42,50,'42:50','अथवा उन्हें पुत्र और[ पुत्रियाँ मिलाकर देता है और जिसे चाहे, बाँझ बना देता है। वास्तव में, वह सब कुछ जानने वाला (तथा) सामर्थ्य रखने वाला है। ');
INSERT INTO "al_umari" VALUES(42,51,'42:51','और नहीं संभव है किसी मनुष्य के लिए कि बात करे अल्लाह उससे, परन्तु वह़्यी[ द्वारा, अथवा पर्दे के पीछे से अथवा भेज दे कोई रसूल (फ़रिश्ता), जो वह़्यी करे उसकी अनुमति से, जो कुछ वह चाहता हो। वास्तव में, वह सबसे ऊँचा (तथा) सभी गुण जानने वाला है। ');
INSERT INTO "al_umari" VALUES(42,52,'42:52','और इसी प्रकार, हमने वह़्यी (प्रकाशना) की है, आपकी ओर, अपने आदेश की रूह़ (क़ुर्आन)। आप नहीं जानते थे कि पुस्तक क्या है और ईमान[ क्या है। परन्तु, हमने इसे बना दिया एक ज्योति। हम मार्ग दिखाते हैं इसके द्वारा, जिसे चाहते हैं अपने भक्तों में से और वस्तुतः, आप सीधी राह दिखा रहे हैं।');
INSERT INTO "al_umari" VALUES(42,53,'42:53','अल्लाह की राह, जिसके अधिकार में है, जो कुछ आकाशों में तथा जो कुछ धरती में है। सावधान! अल्लाह ही की ओर फिरते हैं, सभी कार्य।');
INSERT INTO "al_umari" VALUES(43,1,'43:1','ह़ा मीम।');
INSERT INTO "al_umari" VALUES(43,2,'43:2','शपथ है प्रत्यक्ष (खुली) पुस्तक की।');
INSERT INTO "al_umari" VALUES(43,3,'43:3','इसे हमने बनाया है अरबी क़ुर्आन, ताकि वे इसे समझ सकें।');
INSERT INTO "al_umari" VALUES(43,4,'43:4','तथा वह मूल पुस्तक[ में है, हमारे पास, बड़ा उच्च तथा ज्ञान से परिपूर्ण है। ');
INSERT INTO "al_umari" VALUES(43,5,'43:5','तो क्या हम फेर दें, इस शिक्षा को, तुमसे, इसलिए कि तुम उल्लंघनकारी लोग हो?');
INSERT INTO "al_umari" VALUES(43,6,'43:6','तथा हमने भेजे हैं बहुत-से नबी (गुज़री हुयी) जातियों में।');
INSERT INTO "al_umari" VALUES(43,7,'43:7','और नहीं आता रहा उनके पास कोई नबी, परन्तु वे उसके साथ उपहास करते रहे।');
INSERT INTO "al_umari" VALUES(43,8,'43:8','तो हमने विनाश कर दिया इनसे[ अधिक शक्तिवानों का तथा ग़ुज़र चुका है अगलों का उदाहरण। ');
INSERT INTO "al_umari" VALUES(43,9,'43:9','और यदि आप प्रश्न करें उनसे कि किसने पैदा किया है आकाशों तथा धरती को? तो अवश्य कहेंगेः उन्हें पैदा किया है बड़े प्रभावशाली, सब कुछ जानने वाले ने।');
INSERT INTO "al_umari" VALUES(43,10,'43:10','जिसने बनाया तुमहारे लिए धरती को पालना और बनाये उसमें तुम्हारे लिए मार्ग, ताकि तुम मार्ग पा सको।[ ');
INSERT INTO "al_umari" VALUES(43,11,'43:11','तथा जिसने उतारा आकाश से जल, एक विशेष मात्रा में। फिर जीवित कर दिया उसके दवारा मुर्दा भूमि को। इसी प्रकार, तुम (धरती से) निकाले जाओगे।');
INSERT INTO "al_umari" VALUES(43,12,'43:12','तथा जिसने पैदा किये सब प्रकार के जोड़े तथा बनाईं तुम्हारे लिए नौकायें तथा पशु जिनपर तुम सवार होते हो।');
INSERT INTO "al_umari" VALUES(43,13,'43:13','ताकि तुम सवार हो उनके ऊपर, फिर याद करो अपने पालनहार के प्रदान को, जब सवार हो जाओ उसपर और ये[ कहोः पवित्र है वह, जिसने वश में कर दिया हमारे लिए इसे। अन्यथा हम इसे वश में नहीं कर सकते थे। ');
INSERT INTO "al_umari" VALUES(43,14,'43:14','तथा हम अवश्य ही अपने पालनहार ही की ओर फिरकर जाने वाले हैं।');
INSERT INTO "al_umari" VALUES(43,15,'43:15','और बना लिया उन्होंने[ उसके भक्तों में से कुछ को उसका अंश। वास्तव में, मनुष्य खुला कृतघ्न है। ');
INSERT INTO "al_umari" VALUES(43,16,'43:16','क्या अल्लाह ने उसमें से, जो पैदा करता है, पुत्रियाँ बना ली हैं तथा तुम्हें विशेष कर दिया है पुत्रों के साथ?');
INSERT INTO "al_umari" VALUES(43,17,'43:17','जबकि उनमें से किसी को शुभ सूचना दी जाये उस (के जन्म लेने) की, जिसका उसने उदाहरण दिया है अत्यंत कृपाशील के लिए, तो उसका मुख काला[ हो जाता है और शोक से भर जाता है। ');
INSERT INTO "al_umari" VALUES(43,18,'43:18','क्या (अल्लाह के लिए) वह है, जिसका पालनपोषण आभूषण में किया जाता है तथा वह विवाद में खुलकर बात नहीं कर सकती?');
INSERT INTO "al_umari" VALUES(43,19,'43:19','और उन्होंने बना दिया फ़रिश्तों को, जो अत्यंत कृपाशील के भक्त हैं, पुत्रियाँ। क्या वे उपस्थित थे उनकी उत्पत्ति के समय? लिख ली जायेगी उनकी गवाही और उनसे पूछ होगी।');
INSERT INTO "al_umari" VALUES(43,20,'43:20','तथा उन्होंने कहा कि यदि अत्यंत कृपाशील चाहता, तो हम उनकी इबादत नहीं करते। उन्हें इसका कोई ज्ञान नहीं। वह केवल तीर-तुक्के चला रहे हैं।');
INSERT INTO "al_umari" VALUES(43,21,'43:21','क्या हमने उन्हें प्रदान की है कोई पुस्तक इससे पहले, जिसे वे दृढ़ता से पकड़े हुए हैं?[ ');
INSERT INTO "al_umari" VALUES(43,22,'43:22','बल्कि, ये कहते हैं कि हमने पाया है अपने पूर्वजों को एक रीति पर और हम उन्हीं के पद्चिन्हों पर चल रहे हैं।');
INSERT INTO "al_umari" VALUES(43,23,'43:23','तथा (हे नबी!) इसी प्रकार, हमने नहीं भेजी आपसे पूर्व किसी बस्ती में कोई सावधान करने वाला, परन्तु कहा उसके सुखी लोगों नेः हमने पाया है अपने पूर्वजों को एक रीति पर और हम निश्चय उन्हीं के पद्चिन्हों पर चल रहे हैं।[ ');
INSERT INTO "al_umari" VALUES(43,24,'43:24','नबी ने कहाः क्या (तुम उन्हीं का अनुगमन करोगे) यद्पि मैं लाया हूँ तुम्हारे पास उससे अधिक सीधा मार्ग, जिसपर तुमने पाया है अपने पूर्वजों को? तो उन्होंने कहाः हम, जिस (धर्म) के साथ तुम भेजे गये हो, उसे मानने वाले नहीं है।');
INSERT INTO "al_umari" VALUES(43,25,'43:25','अन्ततः, हमने बदला चुका लिया उनसे। तो देखो कि कैसा रहा झुठलाने वालों का दुष्परिणाम।');
INSERT INTO "al_umari" VALUES(43,26,'43:26','तथा याद करो, जब कहा इब्राहीम ने अपने पिता तथा अपनी जाति सेः निश्चय मैं विरक्त हूँ उससे, जिसकी वंदना तुम करते हो।');
INSERT INTO "al_umari" VALUES(43,27,'43:27','उसके अतिरिक्त, जिसने मुझे पैदा किया है। वही मुझे राह दिखायेगा।');
INSERT INTO "al_umari" VALUES(43,28,'43:28','तथा छोड़ गया वह इस बात (एकेश्वरवाद) को[ अपनी संतान में, ताकि वे शिर्क से बचते रहें। ');
INSERT INTO "al_umari" VALUES(43,29,'43:29','बल्कि, मैंने इन्हें तथा इनके बाप-दादा को जीवन का सामान दिया। यहाँ तक कि आ गया उनके पास सत्य (क़ुर्आन) और एक खुला रसूल।[ ');
INSERT INTO "al_umari" VALUES(43,30,'43:30','तथा जब आ गया उनके पास सत्य, तो उन्होंने कह दिया कि ये जादू है तथा हम, इसे मानने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(43,31,'43:31','तथा उन्होंने कहा कि क्यों नहीं उतारा[ गया ये क़ुर्आन, दो बस्तियों में से किसी बड़े व्यक्ति पर? ');
INSERT INTO "al_umari" VALUES(43,32,'43:32','कया वही बाँटते[ हैं आपके पालनहार की दया? हमने बाँटी है, उनके बीच, उनकी जीविका सांसारिक जीवन में तथा हमने उच्च किया है, उनमें से एक को दूसरे पर, कई श्रेणियाँ। ताकि एक-दूसरे से सेवा कार्य लें तथा आपके पालनहार की दया उससे उत्तम है, जिसे वे इकट्ठा कर रहे हैं।');
INSERT INTO "al_umari" VALUES(43,33,'43:33','और यदि ये बात न होती कि सभी लोग एक ही नीति पर हो जाते, तो हम अवश्य बना देते उनके लिए, जो कुफ्र करते हैं अत्यंत कृपाशील के साथ, उनके घरों की छतें चाँदी की तथा सीढ़ियाँ जिनपर वे चढ़ते हैं।');
INSERT INTO "al_umari" VALUES(43,34,'43:34','तथा उनके घरों के द्वार और तख़्त जिनपर वे तकिये लगाये[ रहते हैं। ');
INSERT INTO "al_umari" VALUES(43,35,'43:35','तथा बना देते शोभा। नहीं हैं ये सब कुछ, परन्तु सांसारिक जीवन के सामान तथा आख़िरत[ (परलोक) आपके पालनहार के यहाँ केवल आज्ञाकारियों के लिए है। ');
INSERT INTO "al_umari" VALUES(43,36,'43:36','और जो व्यक्ति अत्यंत कृपाशील (अल्लाह) के स्मरण से अंधा हो जाता है, तो हम उसपर एक शैतान नियुक्त कर देते हैं, जो उसका साथी हो जाता है।');
INSERT INTO "al_umari" VALUES(43,37,'43:37','और वे (शैतान) उन्हें रोकते हैं सीधी राह से तथा वे समझते हैं कि वे सीधी राह पर हैं।');
INSERT INTO "al_umari" VALUES(43,38,'43:38','यहाँ तक कि जब वह हमारे पास आयेगा, तो ये कामना करेगा कि मेरे तथा तेरे (शैतान के) बीच पश्चिम तथा पूर्व की दूरी होती। तू बुरा साथी है।');
INSERT INTO "al_umari" VALUES(43,39,'43:39','(उनसे कहा जायेगाः) और तुम्हें कदापि कोई लाभ नहीं होगा आज, जबकि तुमने अत्याचार कर लिया है। वास्तव में, तुम सब यातना में साझी रहोगे।');
INSERT INTO "al_umari" VALUES(43,40,'43:40','तो (हे नबी!) क्या आप सुना लेंगे बहरों को या सीधी राह दिखा देंगे अंधों को तथा उन्हें, जो खुले कुपथ[ में हों? ');
INSERT INTO "al_umari" VALUES(43,41,'43:41','फिर यदि हम आपको (संसार से) ले जायें, तो भी हम उनसे बदला लेने वाले हैं।');
INSERT INTO "al_umari" VALUES(43,42,'43:42','अथवा आपको दिखा दें, जिस (यातना) का हमने उनको वचन दिया है, तो निश्चय हम उनपर सामर्थ्य रखने वाले हैं।');
INSERT INTO "al_umari" VALUES(43,43,'43:43','तो (हे नबी!) आप दृढ़ता से पकड़े रहें उसे, जो हम आपकी ओर वह़्यी कर रहे हैं। वास्तव में, आप सीधी राह पर हैं।');
INSERT INTO "al_umari" VALUES(43,44,'43:44','निश्चय ये क़ुर्आन, आपके लिए तथा आपकी जाति के लिए एक शिक्षा[ है और जल्द ही तुमसे प्रश्न किया जायेगा।');
INSERT INTO "al_umari" VALUES(43,45,'43:45','तथा हे नबी! आप पूछ लें उनसे जिन्हें हमने भेजा है, आपसे पहले, अपने रसूलों में से कि क्या हमने बनाये हैं अत्यंत कृपाशील के अतिरिक्त वंदनीय, जिनकी वंदना की जाये?');
INSERT INTO "al_umari" VALUES(43,46,'43:46','तथा हमने भेजा मूसा को अपनी निशानियों के साथ फ़िरऔन और उसके प्रमुखों की ओर। तो उसने कहाः वास्तव में, मैं सर्वलोक के पालनहार का रसूल हूँ।');
INSERT INTO "al_umari" VALUES(43,47,'43:47','और जब वह उनके पास लाया हमारी निशानियाँ, तो सहसा वे उनकी हँसी उड़ाने लगे।');
INSERT INTO "al_umari" VALUES(43,48,'43:48','तथा हम उन्हें एक से बढ़ कर एक निशानी दिखाते रहे और हमने पकड़ लिया उन्हें यातना में, ताकि वे (ठट्ठा) से रुक जायें।');
INSERT INTO "al_umari" VALUES(43,49,'43:49','और उन्होंने कहाः हे जादूगर! प्रार्थना कर हमारे लिए अपने पालनहार से, उस वचन के आधार पर, जो तुझसे किया है। वास्तव में, हम सीधी राह पर आ जायेंगे।');
INSERT INTO "al_umari" VALUES(43,50,'43:50','तो जैसे ही हमने दूर की उनसे यातना, तो वे सहसा वचन तोड़ने लगे।');
INSERT INTO "al_umari" VALUES(43,51,'43:51','तथा पुकारा फ़िरऔन ने अपनी जाति में। उसने कहाः हे मेरी जाति! क्या नहीं है मेरे लिए मिस्र का राज्य तथा ये नहरें, जो बह रही हैं मेरे नीचे से? तो क्या तुम देख नहीं रहे हो?');
INSERT INTO "al_umari" VALUES(43,52,'43:52','मैं अच्छा हूँ या वह जो अपमानित (हीन) है और खुलकर बोल भी नहीं सकता?');
INSERT INTO "al_umari" VALUES(43,53,'43:53','क्यों नहीं उतारे गये उसपर सोने के कंगन अथवा आये फ़रिश्ते उसके साथ पंक्ति बाँधे हुए?[ ');
INSERT INTO "al_umari" VALUES(43,54,'43:54','तो उसने झाँसा दे दिया अपनी जाति को और सबने उसकी बात मान ली। वास्तव में, वे थे ही अवज्ञाकारी लोग।');
INSERT INTO "al_umari" VALUES(43,55,'43:55','फिर जब उन्होंने हमें क्रोधित कर दिया, तो हमने उनसे बदला ले लिया और सबको डुबो दिया।');
INSERT INTO "al_umari" VALUES(43,56,'43:56','और बना दिया हमने उनको गया-गुज़रा और एक उदाहरण, पश्चात् के लोगों के लिए।');
INSERT INTO "al_umari" VALUES(43,57,'43:57','तथा जब दिया गया मर्यम के पुत्र का[ उदाहरण, तो सहसा आपकी जाति उससे प्रसन्न होकर शोर मचाने लगी। ');
INSERT INTO "al_umari" VALUES(43,58,'43:58','तथा मुश्रिकों ने कहा कि हमारे देवता अच्छे हैं या वे? उन्होंने नहीं दिया ये (उदाहरण) आपको, परन्तु कुतर्क (झगड़ने) के लिए। बल्कि, वे हैं ही बड़े झगड़ालू लोग।');
INSERT INTO "al_umari" VALUES(43,59,'43:59','नहीं है वह[ (ईसा), परन्तु एक भक्त (दास) जिसपर हमने उपकार किया तथा उसे इस्राईल की संतान के लिए एक आदर्श बनाया। ');
INSERT INTO "al_umari" VALUES(43,60,'43:60','और यदि हम चाहते, तो बना देते तुम्हारे बदले फ़रिश्ते धरती में, जो एक-दूसरे का स्थान लेते।');
INSERT INTO "al_umari" VALUES(43,61,'43:61','तथा वास्तव में, वह (ईसा) एक बड़ा लक्षण[ हैं प्रलय का। अतः, कदापि संदेह न करो प्रलय के विषय में और मेरी ही बात मानो। यही सीधी राह है। ');
INSERT INTO "al_umari" VALUES(43,62,'43:62','तथा तुम्हें कदापि न रोक दे शैतान। निश्चय वह तुम्हारा खुला शत्रु है।');
INSERT INTO "al_umari" VALUES(43,63,'43:63','और जब आ गया ईसा खुली निशानियाँ लेकर, तो कहाः मैं लाया हूँ तुम्हारे पास ज्ञान और ताकि उजागर कर दूँ तुम्हारे लिए वह कुछ बातें, जिनमें तुम विभेद कर रहे हो। अतः, अल्लाह से डरो और मेरा ही कहा मानो।');
INSERT INTO "al_umari" VALUES(43,64,'43:64','वास्तव में, अल्लाह ही मेरा पालनहार तथा तुम्हारा पालनहार है। अतः, उसी की वंदना (इबादत) करो यही सीधी राह है।');
INSERT INTO "al_umari" VALUES(43,65,'43:65','फिर विभेद कर लिया गिरोहों[ ने आपस में। तो विनाश है उनके लिए जिन्होंने अत्याचार किया, दुःखदायी दिन की यातना से। ');
INSERT INTO "al_umari" VALUES(43,66,'43:66','क्या वे बस इसकी प्रतीक्षा कर रहे हैं कि प्रलय उनपर सहसा आ पड़े और उन्हें (उसका) संवेदन (भी) न हो?');
INSERT INTO "al_umari" VALUES(43,67,'43:67','सभी मित्र उस दिन एक-दूसरे के शत्रु हो जायेंगे, आज्ञाकारियों के सिवा।');
INSERT INTO "al_umari" VALUES(43,68,'43:68','हे मेरे भक्तो! कोई भय नहीं है तुमपर आज और न तुम उदासीन होगे।');
INSERT INTO "al_umari" VALUES(43,69,'43:69','जो ईमान लाये हमारी आयतों पर तथा आज्ञाकारी बनकर रहे।');
INSERT INTO "al_umari" VALUES(43,70,'43:70','प्रवेश कर जाओ स्वर्ग में, तुम तथा तुम्हारी पत्नियाँ। तुम्हें प्रसन्न रखा जायेगा।');
INSERT INTO "al_umari" VALUES(43,71,'43:71','फिरायी जायेंगी उनपर सोने की थालें तथा प्याले और उसमें वह सब कुछ होगा, जिसे उनका मन चाहेगा और जिसे उनकी आँखें देखकर आनन्द लेंगी और तुमसब उसमें सदैव रहोगे।');
INSERT INTO "al_umari" VALUES(43,72,'43:72','और ये स्वर्ग है, जिसके तुम उत्तराधिकारी बनाये गये हो, अपने कर्मों के बदले, जो तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(43,73,'43:73','तुम्हारे लिए इसमें बहुत-से मेवे हैं जिनमें से तुम खाते रहोगे।');
INSERT INTO "al_umari" VALUES(43,74,'43:74','निःसंदेह अपराधी नरक की यातना में सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(43,75,'43:75','उनसे (यातना) हल्की नहीं की जायेगी तथा वे उसमें निराश होंगे।');
INSERT INTO "al_umari" VALUES(43,76,'43:76','और हमने अत्याचार नहीं किया उनपर, परन्तु वही अत्याचारी थे।');
INSERT INTO "al_umari" VALUES(43,77,'43:77','तथा वे पुकारेंगे कि हे मालिक![ हमारा काम ही तमाम कर दे तेरा परालनहार। वह कहेगाः तुम्हें इसी दशा में रहना है। ');
INSERT INTO "al_umari" VALUES(43,78,'43:78','(अल्लाह कहेगाः) हम तुम्हारे पास सत्य[ लाये, किन्तु तुममें से अधिक्तर को सत्य अप्रिय था। ');
INSERT INTO "al_umari" VALUES(43,79,'43:79','क्या उन्होंने किसी बात का निर्णय कर लिया है?[ तो हम भी निर्णय कर देंगे। ');
INSERT INTO "al_umari" VALUES(43,80,'43:80','क्या वे समझते हैं कि हम नहीं सुनते हैं उनकी गुप्त बातों तथा प्रामर्श को? क्यों नहीं, बल्कि हमारे फ़रिश्ते उनके पास ही लिख रहे हैं।');
INSERT INTO "al_umari" VALUES(43,81,'43:81','(हे नबी!) आप उनसे कह दें कि यदि अत्यंत कृपाशील (अल्लाह) की कोई संतान होती, तो सबसे पहले मैं उसका पुजारी होता।');
INSERT INTO "al_umari" VALUES(43,82,'43:82','पवित्र है आकाशों तथा धरती का पालनहार, सिंहासन का स्वामी उन बातों से, जो वे कहते हैं!');
INSERT INTO "al_umari" VALUES(43,83,'43:83','तो आप उन्हें छोड़ दें, वे वाद-विवाद तथा खेल-कूद करते रहें, यहाँ तक कि अपने उस दिन से मिल जायें, जिससे उन्हें डराया जा रहा है।');
INSERT INTO "al_umari" VALUES(43,84,'43:84','वही है, जो आकाश में वंदनीय और धरती में वंदनीय है और वही ह़िक्मत और ज्ञान वाला है।');
INSERT INTO "al_umari" VALUES(43,85,'43:85','शुभ है वह, जिसके अधिकार में आकाशों तथा धरती का राज्य है तथा जो कुछ दोनों के मध्य है तथा उसी के पास प्रलय का ज्ञान है और उसी की ओर तुमसब प्रत्यागत किये जाओगे।');
INSERT INTO "al_umari" VALUES(43,86,'43:86','तथा नहीं अधिकार रखते हैं, जिन्हें वे पुकारते हैं अल्लाह के अतिरिक्त, सिफ़ारिश का। हाँ, (सिफ़ारिश के योग्य वे हैं) जो सत्य[ की गवाही दें और (उसे) जानते भी हों। ');
INSERT INTO "al_umari" VALUES(43,87,'43:87','और यदि आप उनसे प्रश्न करें कि किसने पैदा किया है उन्हें? तो वे अवश्य कहेंगे कि अल्लाह ने। तो फिर वे कहाँ फिरे जा रहे हैं?[ ');
INSERT INTO "al_umari" VALUES(43,88,'43:88','तथा रसूल की ये बात कि, हे मेरे पालनहार! ये वे लोग हैं, जो ईमान नहीं लाते।');
INSERT INTO "al_umari" VALUES(43,89,'43:89','तो आप उनसे विमुख हो जायें तथा कह दें कि सलाम[ है। शीघ्र ही उन्हें ज्ञान हो जायेगा। ');
INSERT INTO "al_umari" VALUES(44,1,'44:1','ह़ा मीम।');
INSERT INTO "al_umari" VALUES(44,2,'44:2','शपथ है इस खुली पुस्तक की।');
INSERT INTO "al_umari" VALUES(44,3,'44:3','हमने ही उतारा है इसे[ एक शुभ रात्रि में। वास्तव में, हम सावधान करने वाले हैं। ');
INSERT INTO "al_umari" VALUES(44,4,'44:4','उसी (रात्रि) में निर्णय किया जाता है, प्रत्येक सुदृढ़ कर्म का।');
INSERT INTO "al_umari" VALUES(44,5,'44:5','ये (आदेश) हमारे पास से है। हम ही भेजने वाले हैं, रसूलों को।');
INSERT INTO "al_umari" VALUES(44,6,'44:6','आपके पालनहार की दया से, वास्तव में, वह सब कुछ सुनने-जानने वाला है।');
INSERT INTO "al_umari" VALUES(44,7,'44:7','जो आकाशों तथा धरती का पालनहार है तथा जो कुछ उन दोनों के बीच है, यदि तुम विश्वास करने वाले हो।');
INSERT INTO "al_umari" VALUES(44,8,'44:8','नहीं है कोई वंदनीय, परन्तु वही, जो जीवन देता तथा मारता है। तुम्हारा पालनहार तथा तुम्हारे गुज़रे हुए पूर्वजों का पालनहार है।');
INSERT INTO "al_umari" VALUES(44,9,'44:9','बल्कि, वे (मुश्रिक) संदेह में खेल रहे हैं।');
INSERT INTO "al_umari" VALUES(44,10,'44:10','तो आप प्रतीक्षा करें, उस दिन का, जब आकाश खुला धूँवा[ लायेगा। ');
INSERT INTO "al_umari" VALUES(44,11,'44:11','जो छा जायेगा सब लोगों पर। यही दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(44,12,'44:12','(वे कहेंगेः) हमारे पालनहार! हमसे यातना दूर कर दे। निश्चय हम ईमान लाने वाले हैं।');
INSERT INTO "al_umari" VALUES(44,13,'44:13','और उनके लिए शिक्षा का समय कहाँ रह गया? जबकि उनके पास आ गये एक रसूल (सत्य को) उजागर करने वाले।');
INSERT INTO "al_umari" VALUES(44,14,'44:14','फिर भी वे आपसे मुँह फेर गये तथा कह दिया कि एक सिखाया हुआ पागल है।');
INSERT INTO "al_umari" VALUES(44,15,'44:15','हम दूर कर देने वाले हैं कुछ यातना, वास्तव में तुम, फिर अपनी प्रथम स्थिति पर आ जाने वाले हो।');
INSERT INTO "al_umari" VALUES(44,16,'44:16','जिस दिन हम अत्यंत कड़ी पकड़[ में ले लेंगे। तो हम निश्चय बदला लेने वाले हैं। ');
INSERT INTO "al_umari" VALUES(44,17,'44:17','तथा हमने परीक्षा ली इनसे पूर्व फ़िरऔन की जाति की तथा उनके पास एक आदरणीय रसूल आया।');
INSERT INTO "al_umari" VALUES(44,18,'44:18','कि मुझे सौंप दो अल्लाह के भक्तों को। निश्चय मैं तुम्हारे लिए एक अमानतदार रसूल हूँ।');
INSERT INTO "al_umari" VALUES(44,19,'44:19','तथा अल्लाह के विपरीत घमंड न करो। मैं तुम्हारे सामने खुला प्रमाण प्रस्तुत करता हूँ।');
INSERT INTO "al_umari" VALUES(44,20,'44:20','तथा मैंने शरण ली है, अपने पालनहार की तथा तुम्हारे पालनहार की इससे कि तुम मुझपर पथराव कर दो।');
INSERT INTO "al_umari" VALUES(44,21,'44:21','और यदि तुम मेरा विश्वास न करो, तो मुझसे परे हो जाओ।');
INSERT INTO "al_umari" VALUES(44,22,'44:22','अन्ततः, मूसा ने पुकारा अपने पालनहार को, कि वास्तव में ये लोग अपराधी हैं।');
INSERT INTO "al_umari" VALUES(44,23,'44:23','(हमने आदेश दिया) कि निकल जा रातों-रात, मेरे भक्तों को लेकर। निश्चय तुम्हारा पीछा किया जायेगा।');
INSERT INTO "al_umari" VALUES(44,24,'44:24','तथा छोड़ दे सागर को उसकी दशा पर, खुला। वास्तव में, ये डूब जाने वाली सेना है।');
INSERT INTO "al_umari" VALUES(44,25,'44:25','वे छोड़ गये बहुत-से बाग़ तथा जल स्रोत।');
INSERT INTO "al_umari" VALUES(44,26,'44:26','तथा खेतियाँ और सुखदायी स्थान।');
INSERT INTO "al_umari" VALUES(44,27,'44:27','तथा सुख के साधन, जिनमें वे आन्नद ले रहे थे।');
INSERT INTO "al_umari" VALUES(44,28,'44:28','इसी प्राकार हुआ और हमने उनका उत्तराधिकारी बना दिया दूसरे[ लोगों को। ');
INSERT INTO "al_umari" VALUES(44,29,'44:29','तो नहीं रोया उनपर आकाश और न धरती और न उन्हें अवसर (समय) दिया गया।');
INSERT INTO "al_umari" VALUES(44,30,'44:30','तथा हमने बचा लिया इस्राईल की संतान को, अपमानकारी यातना से।');
INSERT INTO "al_umari" VALUES(44,31,'44:31','फ़िरऔन से। वास्तव में, वह चढ़ा हुआ उल्लंघनकारियों में से था।');
INSERT INTO "al_umari" VALUES(44,32,'44:32','तथा हमने प्रधानता दी उन्हें, जानते हुए, संसार वासियों पर।');
INSERT INTO "al_umari" VALUES(44,33,'44:33','तथा हमने उन्हें प्रदान कीं ऐसी निशानियाँ, जिनमें खुली परीक्षा थी।');
INSERT INTO "al_umari" VALUES(44,34,'44:34','वास्तव में, ये[ कहते हैं कि ');
INSERT INTO "al_umari" VALUES(44,35,'44:35','हमें तो बस प्रथम बार मरना है तथा हम फिर जीवित नहीं किये जायेंगे।');
INSERT INTO "al_umari" VALUES(44,36,'44:36','फिर यदि तुम सच्चे हो, तो हमारे पूर्वजों को (जीवित करके) ला दो।');
INSERT INTO "al_umari" VALUES(44,37,'44:37','ये अच्छे हैं अथवा तुब्बअ की जाति[ तथा जो उनसे पूर्व रहे हैं? हमने उनका विनाश कर दिया। निश्चय वे अपराधि थे। ');
INSERT INTO "al_umari" VALUES(44,38,'44:38','तथा हमने आकाशों और धरती को एवं जो कुछ उन दोनों के बीच है, खेल नहीं बनाया है।');
INSERT INTO "al_umari" VALUES(44,39,'44:39','हमने नहीं पैदा किया है उन दोनों को, परन्तु सत्य के आधार पर। किन्तु अधिक्तर लोग इसे नहीं जानते हैं।');
INSERT INTO "al_umari" VALUES(44,40,'44:40','निःसंदेह निर्णय[ का दिन, उन सबका निश्चित समय है। ');
INSERT INTO "al_umari" VALUES(44,41,'44:41','जिस दिन, कोई साथी किसी साथी के कुछ काम नहीं आयेगा और न उनकी सहायता की जायेगी।');
INSERT INTO "al_umari" VALUES(44,42,'44:42','परन्तु, जिसपर अल्लाह की दया हो जाये, तो वास्तव में वह बड़ा प्रभावशाली, दयावान है।');
INSERT INTO "al_umari" VALUES(44,43,'44:43','निःसंदेह ज़क्कूम (थोहड़) का वृक्ष।');
INSERT INTO "al_umari" VALUES(44,44,'44:44','पापियों का भोजन है।');
INSERT INTO "al_umari" VALUES(44,45,'44:45','पिघले हुए ताँबे जैसा, जो खौलेगा पेटों में।');
INSERT INTO "al_umari" VALUES(44,46,'44:46','गर्म पानी के खौलने के समान।');
INSERT INTO "al_umari" VALUES(44,47,'44:47','(आदेश होगा कि) उसे पकड़ो तथा धक्का देते हुए नरक के बीच तक पहुँचा दो।');
INSERT INTO "al_umari" VALUES(44,48,'44:48','फिर बहाओ उसके सिर के ऊपर अत्यंत गर्म जल की यातना।[ ');
INSERT INTO "al_umari" VALUES(44,49,'44:49','(तथा कहा जायेगा कि) चख, क्योंकि तू बड़ा आदरणीय सम्मानित था।');
INSERT INTO "al_umari" VALUES(44,50,'44:50','यही वह चीज़ है, जिसमें तुम संदेह कर रहे थे।');
INSERT INTO "al_umari" VALUES(44,51,'44:51','निःसंदेह आज्ञाकारी शान्ति के स्थान में होंगे।');
INSERT INTO "al_umari" VALUES(44,52,'44:52','बाग़ों तथा जल स्रोतों में।');
INSERT INTO "al_umari" VALUES(44,53,'44:53','वस्त्र धारण किये हुए महीन तथा कोमल रेशम के, एक-दूसरे के सामने (आसीन) होंगे।');
INSERT INTO "al_umari" VALUES(44,54,'44:54','इसी प्रकार होगा तथा हम विवाह देंगे उनको ह़ूरों से।[ ');
INSERT INTO "al_umari" VALUES(44,55,'44:55','वे माँग करेंगे उसमें, प्रत्येक प्रकार के मेवों की निश्चिन्त होकर।');
INSERT INTO "al_umari" VALUES(44,56,'44:56','वे उस स्वर्ग में मौत[ नहीं चखेंगे, प्रथम (सांसारिक) मौत के सिवा तथा (अल्लाह) बचा लेगा उन्हें, नरक की यातना से। ');
INSERT INTO "al_umari" VALUES(44,57,'44:57','आपके पालनहार की दया से, वही बड़ी सफलता है।');
INSERT INTO "al_umari" VALUES(44,58,'44:58','तो हमने सरल कर दिया इस (क़ुर्आन) को आपकी भाषा में, ताकि वे शिक्षा ग्रहण करें।');
INSERT INTO "al_umari" VALUES(44,59,'44:59','अतः, आप प्रतीक्षा करें,[ वे भी प्रतीक्षा कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(45,1,'45:1','ह़ा मीम।');
INSERT INTO "al_umari" VALUES(45,2,'45:2','इस पुस्तक[ का उतरना अल्लाह, सब चीज़ों और गुणों को जानने वाले की ओर से है। ');
INSERT INTO "al_umari" VALUES(45,3,'45:3','वास्तव में, आकाशों तथा धरती में बहुत-सी निशानियाँ (लक्षण) हैं, ईमान लाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(45,4,'45:4','तथा तुम्हारी उत्पत्ति में तथा जो फैला[ दिये हैं उसने जीव, बहुत-सी निशानियाँ हैं, उन लोगों के लिए, जो विश्वास रखते हों। ');
INSERT INTO "al_umari" VALUES(45,5,'45:5','तथा रात और दिन के आने जाने में तथा अल्लाह ने आकाश से जो जीविका उतारी है, फिर जीवित किया है उसके द्वारा धरती को, उसके मरने के पश्चात् तथा हवाओं के फेरने में, बड़ी निशानियाँ हैं, उनके लिए, जो समझ-बूझ रखते हों।');
INSERT INTO "al_umari" VALUES(45,6,'45:6','ये अल्लाह की आयतें हैं, जो वास्तव में हम तुम्हें सुना रहे हैं। फिर कौन सी बात रह गई है, अल्लाह तथा उसकी आयतों के पश्चात्, जिसपर वे ईमान लायेंगे?');
INSERT INTO "al_umari" VALUES(45,7,'45:7','विनाश है प्रत्येक झूठे पापी के लिए।');
INSERT INTO "al_umari" VALUES(45,8,'45:8','जो अल्लाह की उन आयतों को, जो उसके सामने पढ़ी जायें सुने, फिर भी वह अकड़ता हुआ (कुफ़्र पर) अड़ा रहे, जैसे कि उन्हें सुना ही न हो! तो आप उसे दुःखदायी यातना की सूचना पहुँचा दें।');
INSERT INTO "al_umari" VALUES(45,9,'45:9','और जब, उसे ज्ञान हो हमारी किसी आयत का, तो उसे उपहास बना ले। यही हैं, जिनके लिए अपमानकारी यातना है।');
INSERT INTO "al_umari" VALUES(45,10,'45:10','तथा उनके आगे नरक है और नहीं काम आयेगा उनके, जो कुछ उन्होंने कमाया है और न जिसे उन्होंने अल्लाह के सिवा संरक्षक बनाया है और उन्हीं के लिए कड़ी यातना है।');
INSERT INTO "al_umari" VALUES(45,11,'45:11','ये (क़ुर्आन) मार्गदर्शन है तथा जिन्होंने कुफ़्र किया अपने पालनहार की आयतों के साथ, तो उन्हीं के लिए यातना है, दुःखदायी यातना।');
INSERT INTO "al_umari" VALUES(45,12,'45:12','अल्लाह ही ने वश में किया है तुम्हारे लिए सागर को, ताकि नाव चलें उसमें उसके आदेश से और ताकि तुम खोज करो उसके अनुग्रह (दया) की और ताकि तुम उसके कृतज्ञ (आभारी) बनो।');
INSERT INTO "al_umari" VALUES(45,13,'45:13','तथा उसने तुम्हारी सेवा में लगा रखा है, जो कुछ आकाशों तथा धरती में है, सबको अपनी ओर से। वास्तव में, इसमें बहुत-सी निशानियाँ हैं, उनके लिए, जो सोच-विचार करें।');
INSERT INTO "al_umari" VALUES(45,14,'45:14','(हे नबी!) आप उनसे कह दें जो ईमान लाये हैं कि क्षमा कर[ दें उन्हें, जो आशा नहीं रखते हैं अल्लाह के दिनों की, ताकि वह बदला दे एक समुदाय को उनकी कमाई का।');
INSERT INTO "al_umari" VALUES(45,15,'45:15','जिसने सदाचार किया, तो अपने भले के लिए किया तथा जिसने दुराचार किया, तो अपने ऊपर किया। फिर तुम (प्रतिफल के लिए) अपने पालनहार की ओर ही फेरे[ जाओगे। ');
INSERT INTO "al_umari" VALUES(45,16,'45:16','तथा हमने प्रदान की इस्राईल की संतान को पुस्तक तथा राज्य और नबूअत (दूतत्व) और जीविका दी उन्हें, स्वच्छ चीज़ों से तथा प्रधानता दी उन्हें (उनके युग के) संसारवासियों पर।');
INSERT INTO "al_umari" VALUES(45,17,'45:17','तथा दिये हमने उन्हें, खुले आदेश। तो उन्होंने विभेद नहीं किया, परन्तु अपने पास ज्ञान[ आ जाने के पश्चात्, आपस के द्वेष के कारण। निःसंदेह आपका पालनहार ही निर्णय करेगा उनके बीच परलय के दिन, जिस बात में वे विभेद कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(45,18,'45:18','फिर (हे नबी!) हमने कर दिया आपको एक खुले धर्म विधान पर, तो आप अनुसरण करें इसका तथा न चलें उनकी आकांक्षाओं पर, जो ज्ञान नहीं रखते।');
INSERT INTO "al_umari" VALUES(45,19,'45:19','वास्तव में, वे आपके काम न आयेंगे अल्लाह के सामने कुछ। ये अत्याचारी एक-दूसरे के मित्र हैं और अल्लाह आज्ञाकारियों का साथी है।');
INSERT INTO "al_umari" VALUES(45,20,'45:20','ये (क़ुर्आन) सूझ की बातें हैं, सब मनुष्यों के लिए तथा मार्गदर्शन एवं दया है, उनके लिए, जो विश्वास करें।');
INSERT INTO "al_umari" VALUES(45,21,'45:21','क्या समझ रखा है जिन्होंने दुष्कर्म किया है कि हम कर देंगे उन्हें उनके समान, जो ईमान लाये तथा सदाचार किये हैं कि उनका जीवन तथा मरण समान[ हो जाये? वे बुरा निर्णय कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(45,22,'45:22','तथा पैदा किया है अल्लाह ने आकाशों एवं धरती को, न्याय के साथ और ताकि बदला दिया जाये प्रत्येक प्राणी को, उसके कर्म का तथा उनपर अत्याचार नहीं किया जायेगा।');
INSERT INTO "al_umari" VALUES(45,23,'45:23','क्या आपने उसे देखा, जिसने बना लिया अपना पूज्य अपनी इच्छा को तथा कुपथ कर दिया अल्लाह ने उसे जानते हुए और मुहर लगा दी उसके कान तथा दिल पर और बना दिया उसकी आँख पर आवरण (पर्दा)? फिर कौन है, जो सीधी राह दिखायेगा उसे अल्लाह के पश्चात्? तो क्या तुम शिक्षा ग्रहण नहीं करते?');
INSERT INTO "al_umari" VALUES(45,24,'45:24','तथा उन्होंने कहा कि हमारा यही सांसारिक जीवन है। हम यहीं मरते और जीते हैं और हमारा विनाश, युग (काल) ही करता है। उन्हें इसका ज्ञान नहीं। वे केवल अनुमान की बात[ कर रहे हैं। ');
INSERT INTO "al_umari" VALUES(45,25,'45:25','और पढ़कर सुनाई जाती हैं उन्हें, हमारी खुली आयतें, तो उनका तर्क केवल ये होता है कि ले आओ हमारे पूर्वजों को, यदि तुम सच्चे हो।');
INSERT INTO "al_umari" VALUES(45,26,'45:26','आप कह दें: अल्लाह ही तुम्हें जीवन देता तथा मारता है, फिर एकत्र करेगा तुम्हें प्रलय के दिन, जिसमें कोई संदेह नहीं। परन्तु अधिक्तर लोग (इस तथ्य को) नहीं[ जानते। ');
INSERT INTO "al_umari" VALUES(45,27,'45:27','तथा अल्लाह ही का है आकाशों तथा धरती का राज्य और जिस दिन स्थापना होगी प्रलय की, तो उस दिन क्षति में पड़ जायेंगे झूठे।');
INSERT INTO "al_umari" VALUES(45,28,'45:28','तथा देखेंगे आप प्रत्येक समुदाय को घुटनों के बल गिरा हुआ। प्रत्येक समुदाय पुकारा जायेगा अपने कर्म-पत्र की ओर। आज, बदला दिया जायेगा तुम लोगों को, तुम्हारे कर्मों का।');
INSERT INTO "al_umari" VALUES(45,29,'45:29','ये हमारा कर्म-पत्र है, जो बोल रहा है तुमपर सह़ीह़ बात। वास्तव में, हम लिखवा रहे थे, जो कुछ तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(45,30,'45:30','तो जो ईमान लाये तथा सदाचार किये, उन्हें प्रवेश देगा उनका पालनहार अपनी दया में, यही प्रत्यक्ष (खुली) सफलता है।');
INSERT INTO "al_umari" VALUES(45,31,'45:31','परन्तु जिन्होंने कुफ़्र किया, (उनसे कहा जायेगाः) क्या मेरी आयतें तुम्हें पढ़कर नहीं सुनायी जा रही थीं? तो तुमने घमंड किया तथा तुम अपराधी बनकर रहे?');
INSERT INTO "al_umari" VALUES(45,32,'45:32','तो जब कहा जाता था कि निश्चय अल्लाह का वचन सच है तथा प्रलय होने में तनिक भी संदेह नहीं, तो तुम कहते थे कि प्रलय क्या है? हम तो केवल एक अनुमान रखते हैं तथा हम विश्वास करने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(45,33,'45:33','तथा खुल जायेंगी उनके लिए, उनके दुष्कर्मों की बुराईयाँ और घेर लेगा उन्हें, जिसका वे उपहास कर रहे थे।');
INSERT INTO "al_umari" VALUES(45,34,'45:34','और कहा जायेगा कि आज हम तुम्हें भुला देंगे,[ जैसे तुमने इस दिन से मिलने को भुला दिया और तुम्हारा कोई सहायक नहीं है। ');
INSERT INTO "al_umari" VALUES(45,35,'45:35','ये (यातना) इस कारण है कि तुमने बना लिया था अल्लाह की आयतों को उपहास तथा धोखे में रखा तुम्हें सांसारिक जीवन ने। तो आज वे नहीं निकाले जायेंगे (यातना से) और न उन्हें क्षमा माँगने का अवसर दिया जायेगा।[ ');
INSERT INTO "al_umari" VALUES(45,36,'45:36','तो अल्लाह के लिए सब प्रशंसा है, जो आकाशों तथा धरती का पालनहार एवं सर्वलोक का पनालनहार है।');
INSERT INTO "al_umari" VALUES(45,37,'45:37','और उसी की महिमा[ है आकाशों तथा धरती में और वही प्रबल और सब गुणों को जानने वाला है। ');
INSERT INTO "al_umari" VALUES(46,1,'46:1','ह़ा मीम।');
INSERT INTO "al_umari" VALUES(46,2,'46:2','इस पुस्तक का उतरना अल्लाह प्रभुत्वशाली, तत्वदर्शी की ओर से है।');
INSERT INTO "al_umari" VALUES(46,3,'46:3','हमने नहीं उत्पन्न किया है आकाशों तथा धरती को और जो कुछ उनके बीच है, परन्तु सत्य के साथ, एक निश्चित अवधि तक के लिए तथा जो काफ़िर हैं, उन्हें जिस बात से सावधान किया जाता है, वे उससे मुँह मोड़े हुए हैं।');
INSERT INTO "al_umari" VALUES(46,4,'46:4','आप कहें कि भला देखो कि जिसे तुम पुकारते हो अल्लाह के सिवा, तनिक मुझे दिखा दो कि उन्होंने क्या उत्पन्न किया है धरती में से? अथवा उनका कोई साझा है आकाशों में? मेरे पास कोई पुस्तक[ प्रस्तुत करो इससे पूर्व की अथवा बचा हुआ कुछ ज्ञान, यदि तुम सच्चे हो।');
INSERT INTO "al_umari" VALUES(46,5,'46:5','तथा उससे अधिक बहका हुआ कौन हो सकता है, जो अल्लाह के सिवा उसे पुकारता हो, जो उसकी प्रार्थना स्वीकार न कर सके, प्रलय तक और वे उसकी प्रार्थना से निश्चेत (अनजान्) हों?');
INSERT INTO "al_umari" VALUES(46,6,'46:6','तथा जब लोग एकत्र किये जायेंगे, तो वे उनके शत्रु हो जायेंगे और उनकी इबादत का इन्हार कर[ देंगे। ');
INSERT INTO "al_umari" VALUES(46,7,'46:7','और जब पढ़कर सुनाई गईं उन्हें हमारी खुली आयतें, तो काफ़िरों ने उस सत्य को, जो उनके पास आ चुका है, कह दिया कि ये तो खुला जादू है।');
INSERT INTO "al_umari" VALUES(46,8,'46:8','क्या वे कहते हैं कि आपने इसे[ स्वयं बना लिया है? आप कह दें कि यदि मैंने इसे स्वयं बना लिया है, तो तुम मुझे अल्लाह की पकड़ से बचाने का कोई अधिकार नहीं रखते। वही अधिक ज्ञानि है उन बातों का, जो तुम बना रहे हो। वही पर्याप्त है गवाह के लिए मेरे तथा तुम्हारे बीच और वह बड़ा क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(46,9,'46:9','आप कह दें कि मैं कोई नया रसूल नहीं हूँ और न मैं जानता हूँ कि मेरे साथ क्या होगा[ और न तुम्हारे साथ। मैं तो केवल अनुसरण कर रहा हूँ उसका, जो मेरी ओर वह़्यी (प्रकाशना) की जा रही है। मैं तो केवल खुला सावधान करने वाला हूँ। ');
INSERT INTO "al_umari" VALUES(46,10,'46:10','आप कह दें: तुम बताओ यदि ये (क़ुर्आन) अल्लाह की ओर से हो और तुम उसे न मानो, जबकि गवाही दे चुका है एक गवाह, इस्राईल की संतान में से इसी बात[ पर, फिर वह ईमान लाया तथा तुम घमंड कर गये? तो वास्तव में अल्लाह सुपथ नहीं दिखाता अत्याचारी जाति को। ');
INSERT INTO "al_umari" VALUES(46,11,'46:11','और काफ़िरों ने कहा, उनसे जो ईमान लाये, यदि ये (धर्म) उत्तम होता, तो वह पहले नहीं आते हमसे, उसकी ओर और जब नहीं पाया मार्गदर्शन उन्होंने इस (क़ुर्आन) से, तो अब यही कहेंगे कि ये तो पुराना झूठ है।');
INSERT INTO "al_umari" VALUES(46,12,'46:12','जबकि इससे पूर्व मूसा की पुस्तक मार्गदर्शक तथा दया बनकर आ चुकी और ये पुस्तक (क़ुर्आन) सच[ बताने वाली है अरबी भाषा में। ताकि वह सावधान कर दे, अत्याचारियों को और शुभ सूचना हो, सदाचारियों के लिए।');
INSERT INTO "al_umari" VALUES(46,13,'46:13','निश्चय जिन्होंने कहा कि हमारा पालनहार अल्लाह है। फिर उसपर स्थिर रह गये, तो कोई भय नहीं होगा उनपर और न वे[ उदासीन होंगे। ');
INSERT INTO "al_umari" VALUES(46,14,'46:14','यही स्वर्गीय हैं, जो सदावासी होंगे उसमें, उन कर्मों के प्रतिफल (बदले) में, जो वे करते रहे।');
INSERT INTO "al_umari" VALUES(46,15,'46:15','और हमने निर्देश दिया है मनुष्य को, अपने माता-पिता के साथ उपकार करने का। उसे गर्भ में रखा है उसकी माँ ने दुःख झेलकर तथा जन्म दिया उसे दुःख झेलकर तथा उसके गर्भ में रखने तथा दूध छुड़ाने की अवधि तीस महीने रही।[ यहाँ तक कि जब वह अपनी पूरी शक्ति को पहुँचा और चालीस वर्ष का हुआ, तो कहने लागाः हे मेरे पालनहार! मुझे क्षमता दे कि कृतज्ञ रहूँ तेरे उस पुरस्कार का, जो तूने प्रदान किया है मुझे तथा मेरे माता-पिता को। तथा ऐसा सत्कर्म करूँ, जिससे तू प्रसन्न हो जाये तथा सुधार दे मेरे लिए मेरी संतान को, मैं ध्यानमग्न हो गया तेरी ओर तथा मैं निश्चय मुस्लिमों में से हूँ। ');
INSERT INTO "al_umari" VALUES(46,16,'46:16','वही हैं, स्वीकार कर लेंगे हम जिनसे, उनके सर्वोत्तम कर्मों को तथा क्षमा कर देंगे, उनके दुष्कर्मों को। (वे) स्वर्ग वासियों में से हैं, उस सत्य वचन के अनुसार, जो उनसे किया जाता था।');
INSERT INTO "al_umari" VALUES(46,17,'46:17','तथा जिसने कहा अपने माता पिता सेः धिक है तुम दोनों पर! क्या मुझे डरा रहे हो कि मैं धरती से निकाला[ जाऊँगा, जबकि बहुत-से युग बीत गये इससे पूर्व? और वो दोनों दुहाई दे रहे थे अल्लाह कीः तेरा विनाश हो! तू ईमान ला! निश्चय अल्लाह का वचन सच है। तो वह कह रहा था कि ये अगलों की कहानियाँ हैं। ');
INSERT INTO "al_umari" VALUES(46,18,'46:18','यही वो लोग हैं, जिनपर अल्लाह की यातना का वचन सिध्द हो गया, उन समुदायों में, जो गुज़र चुके इनसे पूर्व, जिन्न तथा मनुष्यों में से। वास्तव में, वही क्षति में हैं।');
INSERT INTO "al_umari" VALUES(46,19,'46:19','तथा प्रत्येक के लिए श्रेणियाँ हैं, उनके कर्मानुसार और उन्हें भरपूर बदला दिया जायेगा उनके कर्मों का तथा उनपर अत्याचार नहीं किया जायेगा।');
INSERT INTO "al_umari" VALUES(46,20,'46:20','और जिस दिन सामने लाये जायेंगे, जो काफ़िर हो गये, अग्नि के। (उनसे कहा जायेगाः) तुम ले चुके अपना आन्नद अपने सांसारिक जीवन में और लाभान्वित हो चुके उनसे। तो आज तुम्हें अपमान की यातना दी जायेगी उसके बदले, जो तुम घमंड करते रहे धरती में अनुचित तथा उसके बदले, जो उल्लंघन करते रहे।');
INSERT INTO "al_umari" VALUES(46,21,'46:21','तथा याद करो आद के भाई (हूद[) को। जब उसने अपनी जाति को सावधान किया, अह़्क़ाफ़ में, जबकि गुज़र चुके सावधान करने वाले (रसूल) उसके पहले और उसके पश्चात् कि इबादत (वंदना) न करो अल्लाह के अतिरिक्त की। मैं डरता हूँ तुमपर, एक बड़े दिन की यातना से।');
INSERT INTO "al_umari" VALUES(46,22,'46:22','तो उन्होंने कहा कि क्या तुम हमें फेरने आये हो हमारे पूज्यों से? तो ले आओ हमारे पास, जिसकी हमें धमकी दे रहे हो, यदि तुम सच्चे हो।');
INSERT INTO "al_umari" VALUES(46,23,'46:23','हूद ने कहाः उसका ज्ञान तो अल्लाह ही को है और मैं तुम्हें वही उपदेश पहुँचा रहा हूँ, जिसके साथ मैं भेजा गया हूँ। परन्तु, मैं देख रहा हूँ तुम्हें कि तुम अज्ञानता की बातें कर रहे हो।');
INSERT INTO "al_umari" VALUES(46,24,'46:24','फिर जब उन्होंने देखा एक बादल आते हुए अपनी वादियों की ओर, तो कहाः ये एक बादल है हमपर बरसने वाला। बल्कि ये वही है, जिसकी तुमने जल्दी मचायी है। ये आँधी है, जिसमें दुःखदायी यातना है।[ ');
INSERT INTO "al_umari" VALUES(46,25,'46:25','वह विनाश कर देगी प्रत्येक वस्तु को अपने पालनहार के आदेश से, तो वे हो गये ऐसे कि नहीं दिखाई देता था कुछ, उनके घरों के अतिरिक्त। इसी प्रकार, हम बदला दिया करते हैं अपराधी लोगों को।');
INSERT INTO "al_umari" VALUES(46,26,'46:26','तथा हमने उन्हें वह शक्ति दी थी, जो इन्हें[ नहीं दी है। हमने बनाये थे उनके कान, आँखें और दिल, तो नहीं काम आये उनके कान, उनकी आँखें तथा न उनके दिल कुछ भी। क्योंकि वे इन्कार करते थे अल्लाह की आयतों का तथा घेर लिया उन्हें उसने, जिसका वे उपहास कर रहे थे। ');
INSERT INTO "al_umari" VALUES(46,27,'46:27','तथा हम ध्वस्त कर चुके हैं तुम्हारे आस-पास की बस्तियों को तथा हमने उन्हें अनेक प्रकार से आयतें सुना दीं, ताकि वे वापस आ जायें।');
INSERT INTO "al_umari" VALUES(46,28,'46:28','तो क्यों नहीं सहायता की उनकी, उन्होंने जिन्हें बनाया था अल्लाह के अतिरिक्त (अल्लाह के) सामीप्य के लिए पूज्य (उपास्य)? बल्कि वे खो गये उनस और ये[ उनका झूठ था तथा जिसे स्वयं वे घड़ रहे थे। ');
INSERT INTO "al_umari" VALUES(46,29,'46:29','तथा याद करें, जब हमने फेर दिया आपकी ओर जिन्नों के एक[ गिरोह को, ताकि वे क़ुर्आन सुनें। तो जब वे उपस्थित हुए आपके पास, तो उन्होंने कहा कि चुप रहो और जब पढ़ लिया गया, तो वे फिर गये अपनी जाति की ओर सावधान करने वाले होकर। ');
INSERT INTO "al_umari" VALUES(46,30,'46:30','उन्होंने कहाः हे हमारी जाति! हमने सूनी है एक पुस्तक, जो उतारी गयी है मुसा के पश्चात्। वह अपने से पूर्व की किताबों की पुष्टि करती है और सत्य तथा सीधी राह दिखाती है।');
INSERT INTO "al_umari" VALUES(46,31,'46:31','हे हमारी जाति! मान लो अल्लाह की ओर बुलाने वाले की बात तथा ईमान लाओ उसपर, वह क्षमा कर देगा तुम्हारे लिए तुम्हारे पापों को तथा बचा लेगा तुम्हें दुःखदायी यातना से।');
INSERT INTO "al_umari" VALUES(46,32,'46:32','तथा जो मानेगा नहीं अल्लाह की ओर बुलाने वाले की बात, तो नहीं है वह विवश करने वाला धरती में और नहीं है उसके लिए अल्लाह के अतिरिक्त कोई सहायक। यही लोग खुले कुपथ में हैं।');
INSERT INTO "al_umari" VALUES(46,33,'46:33','और क्या उन लोगों ने नहीं समझा कि अल्लाह, जिसने उत्पन्न किया है आकाशों तथा धरती को और नहीं थका उनको बनाने से, वह सामर्थ्वान है कि जीवीत कर दे मुर्दों को? क्यों नहीं? वास्तव में, वह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(46,34,'46:34','और जिस दिन सामने लाये जायेंगे, जो काफ़िर हो गये, नरक के, (और उनसे कहा जायेगाः) क्या ये सच नहीं है? वे कहेंगे: क्यों नहीं? हमारे पालनहार की शपथ! वह कहेगाः तब चखो यातना, उस कुफ़्र के बदले, जो तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(46,35,'46:35','तो (हे नबी!) आप सहन करें, जैसे साहसी रसूलों ने सहन किया तथा जल्दी न करें उन (की यातना) के लिए। जिस दिन वे देख लेंगे, जिसका उन्हें वचन दिया जा रहा है, तो समझेंगे कि जैसे वे नहीं रहे हैं, परन्तु दिन के कुछ[ क्षण। बात पूहुँचा दी गयी है, तो अब उन्हीं का विनाश होगा, जो अवज्ञाकारी हैं। ');
INSERT INTO "al_umari" VALUES(47,1,'47:1','जिन लोगों ने कुफ़्र (अविश्वास) किया तथा अल्लाह की राह से रोका, (अल्लाह ने) व्यर्थ (निष्फल) कर दिया उनके कर्मों को।');
INSERT INTO "al_umari" VALUES(47,2,'47:2','तथा जो ईमान लाये और सदाचार किये तथा उस (क़ुर्आन) पर ईमान लाये, जो उतारा गया है मुह़म्मद पर और (दरअसल) वह सच है उनके पालनहार की ओर से, तो दूर कर दिया उनसे, उनके पापों को तथा सुधार दिया उनकी दशा को।');
INSERT INTO "al_umari" VALUES(47,3,'47:3','ये इस कारण कि उन्होंने कुफ़्र किया और चले असत्य पर तथा जो ईमान लाये, वे चले सत्य पर अपने पालनहार की ओर से (आये हुए), इसी प्रकार, अल्लाह बता देता है लोगों को, उनकी सही दशायें।[ ');
INSERT INTO "al_umari" VALUES(47,4,'47:4','तो जब (युध्द में) भिड़ जाओ काफ़िरों से, तो गर्दन उड़ाओ, यहाँ तक कि जब कुचल दो उन्हें, तो उन्हें दृढ़ता से बाँधो। फिर उसके बाद या तो उपकार करके छोड़ दो या अर्थदण्ड लेकर। यहाँ तक कि युध्द अपने हथियार रख दे।[ ये आदेश है और यदि अल्लाह चाहता, तो स्वयं उनसे बदला ले लेता। किन्तु, (ये आदेश इसलिए दिया) ताकि तुम्हारी एक-दूसरे द्वारा परीक्षा ले और जो मार दिये गये अल्लाह की राह में, तो वह कदापि व्यर्थ नहीं करेगा उनके कर्मों को। ');
INSERT INTO "al_umari" VALUES(47,5,'47:5','वह उन्हें मार्गदर्शन देगा तथा सुधार देगा उनकी दशा।');
INSERT INTO "al_umari" VALUES(47,6,'47:6','और प्रवेश करायेगा उन्हें स्वर्ग में, जिसकी पहचान दे चुका है उन्हें।');
INSERT INTO "al_umari" VALUES(47,7,'47:7','हे ईमान वालो! यदि तुम सहायता करोगे अल्लाह (के धर्म) की, तो वह सहायता करेगा तुम्हारी तथा दृढ़ (स्थिर) कर देगा तुम्हारे पैरों को।');
INSERT INTO "al_umari" VALUES(47,8,'47:8','और जो काफ़िर हो गये, तो विनाश है उन्हीं के लिए और उसने व्यर्थ कर दिया उनके कर्मों को।');
INSERT INTO "al_umari" VALUES(47,9,'47:9','ये इसलिए कि उन्होंने बुरा माना उसे, जो अल्लाह ने उतारा और उसने उनके कर्म व्यर्थ कर[ दिये। ');
INSERT INTO "al_umari" VALUES(47,10,'47:10','तो क्या वह चले-फिरे नहीं धरती में कि देखते उन लोगों का परिणाम, जो इनसे पहले गुजरे? विनाश कर दिया अल्लाह ने उनका तथा काफ़िरों के लिए इसी के समान (यातनायें) हैं।');
INSERT INTO "al_umari" VALUES(47,11,'47:11','ये इसलिए कि अल्लाह संरक्षक (सहायक) है उनका, जो ईमान लाये और काफ़िरों का कोई संरक्षक (सहायक)[ नहीं। ');
INSERT INTO "al_umari" VALUES(47,12,'47:12','निःसंदेह अल्लाह प्रवेश देगा उन्हें, जो ईमान लाये तथा सदाचार किये, ऐसे स्वर्गों में, जिनमें नहरें बहती होंगी तथा जो काफ़िर हो गये, वे आनन्द लेते तथा खाते हैं, जैसे[ पशु खाते हैं और अग्नि उनका आवास (स्थान) है। ');
INSERT INTO "al_umari" VALUES(47,13,'47:13','तथा बहुत-सी बस्तियों को, जो अधिक शक्तिशाली थीं आपकी उस बस्ती से, जिसने आपको निकाल दिया, हमने ध्वस्त कर दिया, तो कोई सहायक न हुआ उनका।');
INSERT INTO "al_umari" VALUES(47,14,'47:14','तो क्या, जो अपने पालनहार के खुले प्रमाण पर हो, वह उसके समान हो सकता है, शोभनीय बना दिया गया हो, जिसके लिए उसका दुष्कर्म तथा चलता हो अपनी मनमानी पर?');
INSERT INTO "al_umari" VALUES(47,15,'47:15','उस स्वर्ग की विशेषता, जिसका वचन दिया गया है आज्ञाकारियों को, उसमें नहरें हैं निर्मल जल की तथा नहरें हैं दूध की, नहीं बदलेगा जिसका स्वाद तथा नहरें हैं मदिरा की, पीने वालों के स्वाद के लिए तथा नहरें हैं मधू की स्वच्छ तथा उन्हीं के लिए उनमें प्रत्येक प्रकार के फल हैं तथा उनके पालनहार की ओर से क्षमा। (क्या ये) उसके समान होंगे, जो सदावासी होंगे नरक में तथा पिलाये जायेंग खौलता जल, जो खण्ड-खण्ड कर देगा उनकी आँतों को?');
INSERT INTO "al_umari" VALUES(47,16,'47:16','तथा उनमें से कुछ वो हैं, जो कान धरते हैं आपकी ओर यहाँ तक कि जब निकलते हैं आपके पास से, तो कहते हैं उनसे, जिन्हें ज्ञान दिया गया है कि अभी क्या[ कहा है? यही वो हैं कि मुहर लगा दी है अल्लाह ने उनके दिलों पर और वही चल रहे हैं अपनी मनोकांक्षाओं पर। ');
INSERT INTO "al_umari" VALUES(47,17,'47:17','और जो सीधी राह पर हैं, अल्लाह ने अधिक कर दिया है उन्हें, मार्गदर्शन में और प्रदान किया है उन्हें, उनका सदाचार।');
INSERT INTO "al_umari" VALUES(47,18,'47:18','तो क्या वे प्रतीक्षा कर रहे हैं प्रलय ही की कि आ जाये उनके पास सहसा? तो आ चुके हैं उसके लक्षण।[ फिर कहाँ होगा उनके शिक्षा लेने का समय, जब वह (क़्यामत) आ जायेगी उनके पास? ');
INSERT INTO "al_umari" VALUES(47,19,'47:19','तो (हे नबी!) आप विश्वास रखिये कि नहीं है कोई वंदनीय अल्लाह के सिवा तथा क्षमा[ माँगिये अपने पाप के लिए तथा ईमान वाले पुरुषों और स्त्रियों के लिए और अल्लाह जानता है तुम्हारे फिरने तथा रहने के स्थान को। ');
INSERT INTO "al_umari" VALUES(47,20,'47:20','तथा जो ईमान लाये, उन्होंने कहा कि क्यों नहीं उतारी जाती कोई सूरह (जिसमें युध्द का आदेश हो?) तो जब एक दृढ़ सूरह उतार दी गयी तथा उसमें वर्णन कर दिया गया युध्द का, तो आपने उन्हें देख लिया, जिनके दिलों में रोग (द्विधा) है कि वे आपकी ओर उसके समान देख रहे हैं, जो मौत के समय अचेत पड़ा हुआ हो। तो उनके लिए उत्तम है।');
INSERT INTO "al_umari" VALUES(47,21,'47:21','आज्ञा पालन तथा उचित बात बोलना। तो जब (युध्द का) आदेश निर्धारित हो गया, तो यदि वे अल्लाह के साथ सच्चे रहें, तो उनके लिए उत्तम है।');
INSERT INTO "al_umari" VALUES(47,22,'47:22','फिर यदि तुम विमुख[ हो गये, तो दूर नहीं कि तुम उपद्रव करोगे धरती में तथा तोड़ेगे अपने रिश्तों (संबंधों) को। ');
INSERT INTO "al_umari" VALUES(47,23,'47:23','यही हैं, जिन्हें अपनी दया से दूर कर दिया है अल्लाह ने और उन्हें बहरा तथा उनकी आँखें अंधी कर दी हैं।[ ');
INSERT INTO "al_umari" VALUES(47,24,'47:24','तो क्या लोग सोच-विचार नहीं करते या उनके दिलों पर ताले लगे हुए हैं?');
INSERT INTO "al_umari" VALUES(47,25,'47:25','वास्तव में, जो फिर गये पीछे इसके पश्चात् कि उजागर हो गया उनके लिए मार्गदर्शन, तो शौतान ने सुन्दर बना दिया (पापों को) उनके लिए तथा उन्हें बड़ी आशा दिलायी है।');
INSERT INTO "al_umari" VALUES(47,26,'47:26','ये इस कारण हुआ कि उन्होंने कहा उनसे, जिन्होंने बुरा माना उस (क़ुर्आन) को, जिसे उतारा अल्लाह ने कि हम तुम्हारी बात मानेंगे कुछ कार्य में, जबकि अल्लाह जानता है उनकी गुप्त बातों को।');
INSERT INTO "al_umari" VALUES(47,27,'47:27','तो कैसी दुर्गत होगी उनकी जब प्राण निकाल रहे होंगे फ़रिश्ते मारते हुए उनके मुखों तथा उनकी पीठों पर।');
INSERT INTO "al_umari" VALUES(47,28,'47:28','ये इसलिए कि वे चले उस राह पर, जिसने अप्रसन्न कर दिया अल्लाह को तथा उन्होंने बुरा माना उसकी प्रसन्नता को, तो उसने व्यर्थ कर दिया उनके कर्मों को।[ ');
INSERT INTO "al_umari" VALUES(47,29,'47:29','क्या समझ रखा है उन्होंने, जिनके दिलों में रोग है कि नहीं खोलेगा अल्लाह उनके द्वेषों को?[ ');
INSERT INTO "al_umari" VALUES(47,30,'47:30','और (हे नबी!) यदि हम चाहें, तो दिखा दें आपको उन्हें, तो पहचान लेंगे आप उन्हें, उनके मुख से और आप अवश्य पहचान लेंगे उन्हें[ (उनकी) बात के ढंग से तथा अल्लाह जानता है उनके कर्मों को। ');
INSERT INTO "al_umari" VALUES(47,31,'47:31','और हम अवश्य परीक्षा लेंगे तुम्हारी, ताकि जाँच लें, तुममें से मुजाहिदों तथा धैर्यवानों को तथा जाँच लें तुम्हारी दशाओं को।');
INSERT INTO "al_umari" VALUES(47,32,'47:32','जिन लोगों ने कुफ़्र किया और रोका अल्लाह की राह (धर्म) से तथा विरोध किया रसूल का, इसके पश्चात् कि उजागर हो गया उनके लिए मार्गदर्शन, वे कदापि हानि नहीं पहुँचा सकेंगे अल्लाह को कुछ तथा वह व्यर्थ कर देगा उनके कर्मों को।');
INSERT INTO "al_umari" VALUES(47,33,'47:33','हे लोगो, जो ईमान लाये हो! आज्ञा मानो अल्लाह की तथा आज्ञा मानो[ रसूल की तथा व्यर्थ न करो अपने कर्मों को। ');
INSERT INTO "al_umari" VALUES(47,34,'47:34','जिन लोगों ने कुफ़्र किया तथा रोका अल्लाह की राह से, फिर वे मर गये कुफ़्र की स्थिति में, तो कदापि क्षमा नहीं करेगा अल्लाह उनको।');
INSERT INTO "al_umari" VALUES(47,35,'47:35','अतः, तुम निर्बल न बनो और न (शत्रु को) संधि की ओर[ पुकारो तथा तुम ही उच्च रहने वाले हो और अल्लाह तुम्हारे साथ है और वह कदापि व्यर्थ नहीं करेगा तुम्हारे कर्मों को। ');
INSERT INTO "al_umari" VALUES(47,36,'47:36','ये सांसारिक जीवन तो एक खेल-कूद है और यदि तुम ईमान लाओ और अल्लाह से डरते रहो, तो वह प्रदान करेगा तुम्हें तुम्हारा प्रतिफल और नहीं माँग करेगा तुमसे तुम्हारे धनों की।');
INSERT INTO "al_umari" VALUES(47,37,'47:37','और यदि वह तुमसे माँगे और तुम्हारा पूरा धन माँगे, तो तुम कंजूसी करने लगोगे और वह खोल[ देगा तुम्हारे द्वेषों को। ');
INSERT INTO "al_umari" VALUES(47,38,'47:38','सुनो! तुम लोग हो, जिन्हें बुलाया जा रहा है, ताकि दान करो अल्लाह की राह में, तो तुममें से कुछ कंजूसी करने लगते हैं और जो कंजूसी करता[ है, तो वह अपने आप ही से कंजूसी करता है और अल्लाह धनी है तथा तुम निर्धन हो और यदि तुम मूँह फेरोगे, तो वह तुम्हारे स्थान पर दूसरों को ले आयेगा फिर वे नहीं होंगे तुम्हारे जैसे। ');
INSERT INTO "al_umari" VALUES(48,1,'48:1','हे नबी! हमने विजय[ प्रदान कर दी आपको खुली विजय। ');
INSERT INTO "al_umari" VALUES(48,2,'48:2','ताकि क्षमा कर दे[ अल्लाह आपके लिए, आपके अगले तथा पिछले दोषों को तथा पूरा करे अपना पुरस्कार, आपके ऊपर और दिखाये आपको सीधी राह। ');
INSERT INTO "al_umari" VALUES(48,3,'48:3','तथा अल्लाह आपकी सहायता करे भरपूर सहायता।');
INSERT INTO "al_umari" VALUES(48,4,'48:4','वही है, जिसने उतारी शान्ति ईमान वालों के दिलों में, ताकि अधिक हो जाये उनका ईमान अपने ईमान के साथ तथा अल्लाह ही की हैं, आकाशों तथा धरती की सेनायें तथा अल्लाह सब कुछ और सब गुणों को जानने वाला है।');
INSERT INTO "al_umari" VALUES(48,5,'48:5','ताकि वह प्रवेश कराये ईमान वाले पुरुषों तथा स्त्रियों को ऐसे स्वर्गों में, बह रही हैं जिनमें नहरें और वे सदैव रहेंगे उनमें और ताकि दूर कर दे उनसे, उनकी बुराईयों को और अल्लाह के यहाँ यही बहुत बड़ी सफलता है।');
INSERT INTO "al_umari" VALUES(48,6,'48:6','तथा यातना दे मुनाफ़िक़ पुरुषों तथा स्त्रियों को, जो बुरा विचार रखने वाले हैं अल्लाह के संबंध में। उन्हीं पर बुरी आपदा आ पड़ी तथा अल्लाह का प्रकोप हुआ उनपर और उसने धिक्कार दिया उन्हें तथा तैयार कर दी उनके लिए नरक और वह बुरा जाने का स्थान है।');
INSERT INTO "al_umari" VALUES(48,7,'48:7','तथा अल्लाह ही की हैं आकाशों तथा धरती की सेनायें और अल्लाह प्रबल तथा सब गुणों को जानने वाला है।[ ');
INSERT INTO "al_umari" VALUES(48,8,'48:8','(हे नबी!) हमने भेजा है आपको गवाह बनाकर तथा शुभ सूचना देने एवं सावधान करने वाला बनाकर।');
INSERT INTO "al_umari" VALUES(48,9,'48:9','ताकि तुम ईमान लाओ अल्लाह एवं उसके रसूल पर और सहायता करो आपकी तथा आदर करो आपका और अल्लाह की पवित्रता का वर्णन करते रहो, प्रातः तथा संध्या।');
INSERT INTO "al_umari" VALUES(48,10,'48:10','(हे नबी!) जो बैअत कर रहे हैं आपसे, वे वास्तव[ में बैअत कर रहे हैं अल्लाह से। अल्लाह का हाथ उनके हाथों के ऊपर है। फिर जिसने वचन तोड़ा, तो वह अपने ऊपर ही वचन तोड़ेगा तथा जिसने पूरा किया जो वचन अल्लाह से किया है, तो वह उसे बड़ा प्रतिफल (बदला) प्रदान करेगा। ');
INSERT INTO "al_umari" VALUES(48,11,'48:11','(हे नबी!) वे[ शीघ्र ही आपसे कहेंगे, जो पीछे छोड़ दिये गये बद्दुओं में से कि हम लगे रह गये अपने धनों तथा परिवार में। अतः, आप क्षमा की प्रार्थना कर दें हमारे लिए। वे अपने मुखों से वो बात कहेंगे, जो उनके दिलों में नहीं है। आप उनसे कहिये कि कौन है, जो अधिकार रखता हो तुम्हारे लिए, अल्लाह के सामने किसी चीज़ का, यदि अल्लाह तुम्हें कोई हानि पहुँचाना चाहे या कोई लाभ पहुँचाना चाहे? बल्कि अल्लाह सूचित है उससे, जो तुम कर रहे हो। ');
INSERT INTO "al_umari" VALUES(48,12,'48:12','बल्कि, तुमने सोचा था कि कदापि वापस नहीं आयेंगे रसूल और न ईमान वाले, अपने परिजनों की ओर, कभी भी और भली लगी ये बात तुम्हारे दिलों को और तुमने बुरी सोच सोची और थे ही तुम विनाश होने वाले लोग।');
INSERT INTO "al_umari" VALUES(48,13,'48:13','और जो ईमान नहीं लाये अल्लाह तथा उसके रसूल पर, तो हमने तैयार कर रखी है काफ़िरों के लिए दहकती अग्नि।');
INSERT INTO "al_umari" VALUES(48,14,'48:14','अल्लाह के लिए है आकाशों तथा धरती का राज्य। वह क्षमा कर दे जिसे चाहे और यातना दे जिसे चाहे और अल्लाह अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(48,15,'48:15','वो लोग जो पीछे छोड़ दिये गये, कहेंगे, जब तुम चलोगे ग़नीमतों की ओर ताकि उन्हें प्राप्त करो कि हमें (भी) अपने साथ[ चलने दो। वे चाहते हैं कि बदल दें अल्लाह के आदेश को। आप कह दें कि कदापि हमारे साथ न चल। इसी प्रकार, कहा है अल्लाह ने इससे पहले। फिर वह कहेंगे कि बल्कि तुम द्वेष (जलन) रखते हो, हम से। बल्कि, वे कम ही बात समझते हैं। ');
INSERT INTO "al_umari" VALUES(48,16,'48:16','आप कह दें, पीछे छोड़ दिये गये बद्दुओं से कि शीघ्र तुम बुलाये जाओगे, एक अति योध्दा जाति (से युध्द) की ओर।[ जिनसे तुम युध्द करोगे अथवा वह इस्लाम ले आयें। तो यदि तुम आज्ञा का पालन करोगे, तो प्रदान करेगा अल्लाह तुम्हें उत्तम बदला तथा यदि तुम विमुख हो गये, जैसे इससे पूर्व (मक्का जाने से) विमुख हो गये, तो तुम्हें यातना देगा दुःखदायी यातना। ');
INSERT INTO "al_umari" VALUES(48,17,'48:17','नहीं है अंधे पर कोई दोष,[ न लंगड़े पर कोई दोष और न रोगी पर कोई दोष तथा जो आज्ञा का पालन करेगा अल्लाह एवं उसके रसूल की, तो वह प्रवेश देगा ऐसे स्वर्गों में, बहती हैं जिनमें नहरें तथा जो मुख फेरेगा, तो वह यातना देगा उसे, दुःखदायी यातना। ');
INSERT INTO "al_umari" VALUES(48,18,'48:18','अल्लाह प्रसन्न हो गया ईमान वालों से, जब वे आप (नबी) से बैअत कर रहे थे, वृक्ष के नीचे। उसने जान लिया जो कुछ उनके दिलों में था, इसलिए उतार दी शान्ति उनपर तथा उन्हें बदले में दी समीप की विजय।[ ');
INSERT INTO "al_umari" VALUES(48,19,'48:19','तथा बहुत-से ग़नीमत के धन (परिहार), जो वह प्राप्त करेंगे और अल्लाह प्रभुत्वशाली, गुणी है।');
INSERT INTO "al_umari" VALUES(48,20,'48:20','अल्लाह ने वचन दिया है तुम्हें बहुत-से परिहार (ग़नीमतों) का, जिसे तुम प्राप्त करोगे। तो शीघ्र प्रदान कर दी तुम्हें ये (ख़ैबर की ग़नीमत) तथा रोक दिया लोगों के हाथों को तुमसे, ताकि[ वह एक निशानी बन जाये ईमान वालों के लिए और तुम्हें सीधी राह चलाये। ');
INSERT INTO "al_umari" VALUES(48,21,'48:21','और दूसरी ग़नीमतें भी, जो तुम प्राप्त नहीं कर सके हो, अल्लाह ने उन्हें नियंत्रण में कर रखा है तथा अल्लाह जो कुछ चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(48,22,'48:22','और यदि तुमसे युध्द करते जो काफ़िर[ हैं, तो अवश्य पीछा दिखा देते, फिर नहीं पाते कोई संरक्षक और न कोई सहायक। ');
INSERT INTO "al_umari" VALUES(48,23,'48:23','ये अल्लाह का नियम है उनमें, जो चला आ रहा है पहले से और तुम कदापि नहीं पाओगे अल्लाह के नियम में परिवर्तन।');
INSERT INTO "al_umari" VALUES(48,24,'48:24','तथा वही है, जिसने रोक दिया उनके हाथों को तुमसे तथा तुम्हारे हाथों को उनसे मक्का की वादी[ में, इसके पश्चात् कि तुम्हें विजय प्रदान कर दी, उनपर तथा अल्लाह देख रहा था, जो कुछ तुम कर रहे थे। ');
INSERT INTO "al_umari" VALUES(48,25,'48:25','ये वे लोग हैं, जिन्होंने कुफ़्र किया और रोक दिया तुम्हें मस्जिदे ह़राम से तथा बलि के पशु को, उनके स्थान तक पुहुँचने से रोक दिया और यदि ये भय न होता कि तुम कुछ मुसलमान पुरुषों तथा कुछ मुसलमान स्त्रियों को, जिन्हें तुम नहीं जानते थे, रौंद दोगे जिससे तुमपर दोष आ जायेगा[ (तो युध्द से न रोका जाता)। ताकि प्रवेश कराये अल्लाह, जिसे चाहे, अपनी दया में। यदि वे (मुसलमान) अलग होते, तो हम अवश्य यातना देते उन्हें, जो काफ़िर हो गये उनमें से, दुःखदायी यातना। ');
INSERT INTO "al_umari" VALUES(48,26,'48:26','जब काफ़िरों ने अपने दिलों में पक्षपात को स्थान दे दिया, जो वास्तव में जाहिलाना पक्षपात है, तो अल्लाह ने अपने रसूल पर तथा ईमान वालों पर शान्ति उतार दी तथा उन्हें पाबन्द रखा सदाचार की बात का तथा वे[ उसके अधिक योग्य और पात्र थे तथा अल्लाह प्रत्येक वस्तु को भली-भाँति जानने वाला है। ');
INSERT INTO "al_umari" VALUES(48,27,'48:27','निश्चय अल्लाह ने अपने रसूल को सच्चा सपना दिखाया, सच के अनुसार। तुम अवश्य प्रवेश करोगे मस्जिदे ह़राम में, यदि अल्लाह ने चाहा, निर्भय होकर, अपने सिर मुंडाते तथा बाल कतरवाते हुए, तुम्हें किसी प्रकार का भय नहीं होगा,[ वह जानता है जिसे तुम नहीं जानते। इसिलए प्रदान कर दी तुम्हें इस (मस्जिदे ह़राम में प्रवेश) से पहले, एक समीप (जल्दी) की विजय।');
INSERT INTO "al_umari" VALUES(48,28,'48:28','वही है, जिसने भेजा अपने रसूल को मार्गदर्शन एवं सत्धर्म के साथ, ताकि उसे प्रभुत्व प्रदान कर दे प्रत्येक धर्म पर तथा प्रयाप्त है (इसपर) अल्लाह का गवाह होना।');
INSERT INTO "al_umari" VALUES(48,29,'48:29','मुह़म्मद[ अल्लाह के रसूल हैं तथा जो लोग आपके साथ हैं, वे काफ़िरों के लिए कड़े और आपस में दयालु हैं। तुम देखोगे उन्हें, रुकूअ-सज्दा करते हुए, वे खोज कर रहे होंगे अल्लाह की दया तथा प्रसन्नता की। उनके लक्षण, उनके चेहरों पर सज्दों के चिन्ह होंगे। ये उनकी विशेषता, तौरात में है तथा उनके गुण, इंजील में उस खेती के समान बताये गये हैं, जिसने निकाला अपना अंकुर, फिर उसे बल दिया, फिर वह कड़ा हो गया, फिर वह (खेती) खड़ी हो गयी अपने तने पर। प्रसन्न करने लगी किसानों को, ताकि काफ़िर उनसे जलें। वचन दे रखा है अल्लाह ने उन लोगों को, जो ईमान लाये तथा सदाचार किये, उनमें से क्षमा तथा बड़े प्रतिफल का। ');
INSERT INTO "al_umari" VALUES(49,1,'49:1','हे लोगो जो ईमान लाये हो! आगे न बढ़ो अल्लाह और उसके रसूल[ से और डरो अल्लाह से। वास्तव में, अल्लाह सब कुछ सुनने-जानने वाला है। ');
INSERT INTO "al_umari" VALUES(49,2,'49:2','हे लोगो जो ईमान लाये हो! अपनी आवाज़, नबी की आवाज़ से ऊँची न करो और न आपसे ऊँची आवाज़ में बात करो, जैसे एक-दूसरे से ऊँची आवाज़ में बात करते हो। ऐसा न हो कि तुम्हारे कर्म व्यर्थ हो जायें और तुम्हें पता (भी) न हो।');
INSERT INTO "al_umari" VALUES(49,3,'49:3','निःसंदेह, जो धीमी रखते हैं अपनी आवाज़, अल्लाह के रसूल के सामने, वही लोग हैं, जाँच लिया है अल्लाह ने जिनके दिलों को, सदाचार के लिए। उन्हीं के लिए क्षमा तथा बड़ा प्रतिफल है।');
INSERT INTO "al_umari" VALUES(49,4,'49:4','वास्तव में जो आपको पुकारते[ हैं कमरों के पीछे से, उनमें से अधिक्तर निर्बोध हैं। ');
INSERT INTO "al_umari" VALUES(49,5,'49:5','और यदि वे सहन[ करते, यहाँ तक कि आप निकलकर आते उनकी ओर, तो ये उत्तम होता उनके लिए तथा अल्लाह बड़ा क्षमा करने वाला, दयावान् है। ');
INSERT INTO "al_umari" VALUES(49,6,'49:6','हे ईमान वालो! यदि तुम्हारे पास कोई दुराचारी[ कोई सूचना लाये, तो भली-भाँति उसका अनुसंधान (छान-बीन) कर लिया करो। ऐसा न हो कि तुम हानि पहुँचा दो किसी समुदाय को, अज्ञानता के कारण, फिर अपने किये पर पछताओ। ');
INSERT INTO "al_umari" VALUES(49,7,'49:7','तथा जान लो कि तुम में अल्लाह के रसूल मौजूद हैं। यदि, वह तुम्हारी बात मानते रहे बहुत-से विषय में, तो तुम आपदा में पड़ जाओगे। परन्तु, अल्लाह ने प्रिय बना दिया है तुम्हारे लिए ईमान को तथा सुशोभित कर दिया है उसे तुम्हारे दिलों में और अप्रिय बना दिया है तुम्हारे लिए कुफ़्र तथा उल्लंघन और अवज्ञा को और यही लोग संमार्ग पर हैं।');
INSERT INTO "al_umari" VALUES(49,8,'49:8','अल्लाह की दया तथा उपकार से और अल्लाह सब कुछ तथा सब गुणों को जानने वाला है।');
INSERT INTO "al_umari" VALUES(49,9,'49:9','और यदि ईमान वालों के दो गिरोह लड़[ पड़ें, तो संधि करा दो उनके बीच। फिर दोनों में से एक, दूसरे पर अत्याचार करे, तो उससे लड़ो जो अत्याचार कर रहा है, यहाँ तक कि फिर जाये अल्लाह के आदेश की ओर। फिर, यदि वह फिर आये, तो उनके बीच संधि करा दो न्याय के साथ तथा न्याय करो, वास्तव में अल्लाह प्रेम करता है, न्याय करने वालों से।');
INSERT INTO "al_umari" VALUES(49,10,'49:10','वास्तव में, सब ईमान वाले भाई-भाई हैं। अतः संधि (मेल) करा दो अपने दो भाईयों के बीच तथा अल्लाह से डरो, ताकि तुम पर दया की जाये।');
INSERT INTO "al_umari" VALUES(49,11,'49:11','हे लोगो जो ईमान लाये हो![ हँसी न उड़ाये कोई जाति किसी अन्य जाति की। हो सकता है कि वह उनसे अच्छी हो और न नारी अन्य नारियों की। हो सकता है कि वो उनसे अच्छी हों तथा आक्षेप न लगाओ एक-दूसरे को और न किसी को बुरी उपाधि दो। बुरा नाम है अपशब्द ईमान के पश्चात् और जो क्षमा न माँगें, तो वही लोग अत्याचारी हैं। ');
INSERT INTO "al_umari" VALUES(49,12,'49:12','हे लोगो जो ईमान लाये हो! बचो अधिकांश गुमानों से। वास्व में, कुछ गुमान पाप हैं और किसी का भेद न लो और न एक-दूसरे की ग़ीबत[ करो। क्या चाहेगा तुममें से कोई अपने मरे भाई का मांश खाना? अतः, तुम्हें इससे घृणा होगी तथा अल्लाह से डरते रहो। वास्तव में, अल्लाह अति दयावान्, क्षमावान् है। ');
INSERT INTO "al_umari" VALUES(49,13,'49:13','हे मनुष्यो![ हमने तुम्हें पैदा किया एक नर तथा नारी से तथा बना दी हैं तुम्हारी जातियाँ तथा प्रजातियाँ, ताकि एक-दूसरे को पहचानो। वास्तव में, तुममें अल्लाह के समीप सबसे अधिक आदरणीय वही है, जो तुममें अल्लाह से सबसे अधिक डरता हो। वास्तव में अल्लाह सब जानने वाला है, सबसे सूचित है। ');
INSERT INTO "al_umari" VALUES(49,14,'49:14','कहा कुछ बद्दुओं (देहातियों) ने कि हम ईमान लाये। आप कह दें कि तुम ईमान नहीं लाये। परन्तु कहो कि हम इस्लाम लाये और ईमान अभी तक तुम्हारे दिलों में प्रवेश नहीं किया है तथा यदि तुम आज्ञा का पालन करते रहे अल्लाह तथा उसके रसूल की, तो नहीं कम करेगा वह (अल्लाह) तुम्हारे कर्मों में से कुछ। वास्तव में, अल्लाह अति क्षमाशील, दयावान्[ है। ');
INSERT INTO "al_umari" VALUES(49,15,'49:15','वास्तव में, ईमान वाले वही हैं, जो ईमान लाये अल्लाह तथा उसके रसूल पर, फिर संदेह नहीं किया और जिहाद किया अपने प्राणों तथा धनों से, अल्लाह की राह में, यही सच्चे हैं।');
INSERT INTO "al_umari" VALUES(49,16,'49:16','आप कह दें कि क्या तुम अवगत करा रहे हो अल्लाह को अपने धर्म से? जबकि अल्लाह जानता है जो कुछ (भी) आकाशों तथा धरती में है तथा वह प्रत्येक वस्तु का अति ज्ञानी है।');
INSERT INTO "al_umari" VALUES(49,17,'49:17','वे उपकार जता रहे हैं आपके ऊपर कि वे इस्वलाम लाये हैं। आप कह दें के उपकार न जताओ मुझपर अपने इस्लाम का। बल्कि अल्लाह का उपकार है तुमपर कि उसने राह दिखायी है तुम्हें ईमान की, यदि तुम सच्चे हो।');
INSERT INTO "al_umari" VALUES(49,18,'49:18','निःसंदेह, अल्लाह ही जानता है आकाशों तथा धरती के ग़ैब (छुपी बात) को तथा अल्लाह देख रहा है जो कुछ तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(50,1,'50:1','क़ाफ़। शपथ है आदरणीय क़ुर्आन की!');
INSERT INTO "al_umari" VALUES(50,2,'50:2','बल्कि उन्हें आश्चर्य हुआ कि आ गया उनके पास एक सावधान करने वाला, उन्हीं मे से। तो कहा काफ़िरों नेः ये तो बड़े आश्चर्य[ की बात है। ');
INSERT INTO "al_umari" VALUES(50,3,'50:3','क्या जब हम मर जायेंगे और धूल हो जायेंगे (तो पुनः जीवित किये जायेंगे)? ये वापसी तो दूर की बात[ (असंभव) है।');
INSERT INTO "al_umari" VALUES(50,4,'50:4','हमें ज्ञान है जो कम करती है धरती उनका अंश तथा हमारे पास एक सुरक्षित पुस्तक[ है। ');
INSERT INTO "al_umari" VALUES(50,5,'50:5','बल्कि उनहोंने झुठला दिया सत्य को, जब आ गया उनके पास। इसलिए उलझन में पड़े हुए हैं।');
INSERT INTO "al_umari" VALUES(50,6,'50:6','क्या उन्होंने नहीं देखा आकाश की ओर अपने ऊपर कि कैसा बनाया है हमने उसे और सजाया है उसको और नहीं है उसमें कोई दराड़?');
INSERT INTO "al_umari" VALUES(50,7,'50:7','तथा हमने धरती को फैलाया और डाल दिये उसमें पर्वत तथा उपजायी उसमें प्रत्येक प्रकार की सुन्दर वनस्पतियाँ।');
INSERT INTO "al_umari" VALUES(50,8,'50:8','आँख खोलने तथा शिक्षा देने के लिए, प्रत्येक अल्लाह की ओर ध्यानमग्न भक्त के लिए।');
INSERT INTO "al_umari" VALUES(50,9,'50:9','तथा हमने उतारा आकाश से शुभ जल, फिर उगाये उसके द्वारा बाग़ तथा अन्न, जो काटे जायें।');
INSERT INTO "al_umari" VALUES(50,10,'50:10','तथा खजूर के ऊँचे वृक्ष, जिनके गुच्छे गुथे हुए हैं।');
INSERT INTO "al_umari" VALUES(50,11,'50:11','जीविका के लिए भक्तों की तथा हमने जीवित कर दिया निर्जीव नगर को। इसी प्रकार (तुम्हें भी) निकलना है।');
INSERT INTO "al_umari" VALUES(50,12,'50:12','झुठलाया इससे पहले नूह़ की जाति तथा कुवें के वासियों एवं समूद ने।');
INSERT INTO "al_umari" VALUES(50,13,'50:13','तथा आद और फ़िरऔन एवं लूत के भाईयों ने।');
INSERT INTO "al_umari" VALUES(50,14,'50:14','तथा ऐका के वासियों ने और तुब्बअ[ की जाति ने। प्रत्येक ने झुठलाया रसूलों को। अन्ततः, सच हो गयी (उनपर) हमारी धमकी।');
INSERT INTO "al_umari" VALUES(50,15,'50:15','तो क्या हम थक गये हैं प्रथम बार पैदा करके? बल्कि, ये लोग संदेह में पड़े हुए हैं नये जीवन के बारे में।');
INSERT INTO "al_umari" VALUES(50,16,'50:16','जबकि हमने ही पैदा किया है मनुष्य को और हम जानते हैं जो विचार आते हैं उसके मन में तथा हम अधिक समीप हैं उससे (उसकी) प्राणनाड़ी[ से। ');
INSERT INTO "al_umari" VALUES(50,17,'50:17','जबकि[ (उसके) दायें-बायें दो फ़रिश्ते लिख रहे हैं। ');
INSERT INTO "al_umari" VALUES(50,18,'50:18','वह नहीं बोलता कोई बात, मगर उसे लिखने के लिए उसके पास एक निरीक्षक तैयार होता है।');
INSERT INTO "al_umari" VALUES(50,19,'50:19','आ पहुँची मौत की अचेतना (बे होशी) सत्य लेकर। ये वही है, जिस से तू भाग रहा था।');
INSERT INTO "al_umari" VALUES(50,20,'50:20','और फूँक दिया गया सूर (नरसिंघा) में। यही यातना के वचन का दिन है।');
INSERT INTO "al_umari" VALUES(50,21,'50:21','तथा आयेगा प्रत्येक प्राणी इस दशा में कि उसके साथ एक हाँकने[ वाला और एक गवाह होगा। ');
INSERT INTO "al_umari" VALUES(50,22,'50:22','तू इसी से अचेत था, तो हमने दूर कर दिया तेरा पर्दा, तो तेरी आँख आज ख़ूब देख रही है।');
INSERT INTO "al_umari" VALUES(50,23,'50:23','तथा कहा उसके साथी[ नेः ये है, जो मेरे पास तैयार है। ');
INSERT INTO "al_umari" VALUES(50,24,'50:24','दोनों (फ़रिश्तों को आदेश होगा कि) फेंक दो नरक में प्रत्येक काफ़िर (सत्य के) विरोधी को।');
INSERT INTO "al_umari" VALUES(50,25,'50:25','भलाई के रोकने वाले, अधर्मी, संदेह करने वाले को।');
INSERT INTO "al_umari" VALUES(50,26,'50:26','जिसने बना लिए अल्लाह के साथ दूसरे पूज्य, तो दोनों को फेंक दो, कड़ी यातना में।');
INSERT INTO "al_umari" VALUES(50,27,'50:27','उसके साथी (शैतान) ने कहाः हे हमारे पालनहार! मैंने इसे कुपथ नहीं किया, परन्तु, वह स्वयं दूर के कुपथ में था।');
INSERT INTO "al_umari" VALUES(50,28,'50:28','अल्लाह ने कहाः झगड़ा न करो मेरे पास। मैंने तो पहले ही (संसार में) तुम्हारी ओर चेतावनी भेज दी थी।');
INSERT INTO "al_umari" VALUES(50,29,'50:29','नहीं बदली जाती बात मेरे पास[ और न मैं तनिक भी अत्याचारी हूँ भक्तों के लिए। ');
INSERT INTO "al_umari" VALUES(50,30,'50:30','जिस दिन हम कहेंगे नरक से कि तू भर गयी? और वह कहेगी क्या कुछ और है?[ ');
INSERT INTO "al_umari" VALUES(50,31,'50:31','तथा समीप कर दी जायेगी स्वर्ग, वह सदाचारियों से कुछ दूर न होगी।');
INSERT INTO "al_umari" VALUES(50,32,'50:32','ये है जिसका तुम्हें वचन दिया जाता था, प्रत्येक ध्यानमग्न रक्षक[ के लिए। ');
INSERT INTO "al_umari" VALUES(50,33,'50:33','जो डरा अत्यंत कृपाशील से बिन देखे तथा लेकर आया ध्यानमग्न दिल।');
INSERT INTO "al_umari" VALUES(50,34,'50:34','प्रवेश कर जाओ इसमें, शान्ति के साथ। ये सदैव रहने का दिन है।');
INSERT INTO "al_umari" VALUES(50,35,'50:35','उन्हीं के लिए, जो वे इच्छा करेंगे उसमें मिलेगा तथा हमारे पास (इससे भी) अधिक है।[ ');
INSERT INTO "al_umari" VALUES(50,36,'50:36','तथा हम विनाश कर चुके हैं इनसे पूर्व, बहुत-से समुदायों का, जो इनसे अधिक थे शक्ति में। तो वे फिरते रहे नगरों में, तो क्या कहीं कोई भागने की जगह पा सके?[ ');
INSERT INTO "al_umari" VALUES(50,37,'50:37','वास्तव में, इसमें निश्चय शिक्षा है उसके लिए, जिसके दिल हों अथवा कान धरे और वह उपस्थित[ हो। ');
INSERT INTO "al_umari" VALUES(50,38,'50:38','तथा निश्चय हमने पैदा किया है आकाशों तथा धरती को और जो कुछ दोनों के बीच है, छः दिनों में और हमें कोई थकान नहीं हुई।');
INSERT INTO "al_umari" VALUES(50,39,'50:39','तो आप सहन करें उनकी बातों को तथा पवित्रता का वर्णन करें अपने पालनहार की प्रशंसा के साथ, सूर्य के निकलने से पहले तथा डूबने से पहले।[ ');
INSERT INTO "al_umari" VALUES(50,40,'50:40','तथा रात के कुछ भाग में उसकी पवित्रता का वर्णन करें और सज्दों (नमाज़ों) के पश्चात् (भी)।');
INSERT INTO "al_umari" VALUES(50,41,'50:41','तथा ध्यान से सुनो, जिस दिन पुकारने वाला[ पुकारेगा समीप स्थान से। ');
INSERT INTO "al_umari" VALUES(50,42,'50:42','जिस दिन सब सुनेंगे कड़ी आवाज़ सत्य के साथ, वही निकलने का दिन होगा।');
INSERT INTO "al_umari" VALUES(50,43,'50:43','वास्तव में, हम ही जीवन देते तथा मारते हैं और हमारी ओर ही फिरकर आना है।');
INSERT INTO "al_umari" VALUES(50,44,'50:44','जिस दिन फट जायेगी धरती उनसे, वे दौड़ते हुए (निकलेंगे), ये एकत्र करना हमपर बहुत सरल है।');
INSERT INTO "al_umari" VALUES(50,45,'50:45','तथा हम भली-भाँति जानते हैं उसे, जो कुछ वे कर रहे हैं और आप उन्हें बलपूर्वक मनवाने के लिए नहीं हैं। तो आप शिक्षा दें क़ुर्आन द्वारा उसे, जो डरता हो मेरी यातना से।');
INSERT INTO "al_umari" VALUES(51,1,'51:1','शपथ है (बादलों को) बिखेरने वालियों की!');
INSERT INTO "al_umari" VALUES(51,2,'51:2','फिर (बादलों का) बोझ लादने वालियों की!');
INSERT INTO "al_umari" VALUES(51,3,'51:3','फिर धीमी गति से चलने वालियों की!');
INSERT INTO "al_umari" VALUES(51,4,'51:4','फिर (अल्लाह का) आदेश बाँटने वाले (फ़रिश्तों की)!');
INSERT INTO "al_umari" VALUES(51,5,'51:5','निश्चय जिस (प्रलय) से तुम्हें डराया जा रहा है, वह सच्ची है।[ ');
INSERT INTO "al_umari" VALUES(51,6,'51:6','तथा कर्मों का फल अवश्य मिलने वाला है।');
INSERT INTO "al_umari" VALUES(51,7,'51:7','शपथ है रास्तों वाले आकाश की!');
INSERT INTO "al_umari" VALUES(51,8,'51:8','वास्तव में, तुम विभिन्न[ बातों में हो। ');
INSERT INTO "al_umari" VALUES(51,9,'51:9','उससे वही फेर दिया जाता है, जो (सत्य से) फिरा हुआ हो।');
INSERT INTO "al_umari" VALUES(51,10,'51:10','नाश कर दिये गये अनुमान लगाने वाले।');
INSERT INTO "al_umari" VALUES(51,11,'51:11','जो अपनी अचेतना में भूले हुए हैं।');
INSERT INTO "al_umari" VALUES(51,12,'51:12','वे प्रश्न[ करते हैं कि प्रतिकार का दिन कब है? ');
INSERT INTO "al_umari" VALUES(51,13,'51:13','(उस दिन है) जिस दिन वह अग्नि पर तपाये जायेंगे।');
INSERT INTO "al_umari" VALUES(51,14,'51:14','(उनसे कहा जायेगाः) स्वाद चखो अपने उपद्रव का। यही वह है, जिसकी तुम शीघ्र माँग कर रहे थे।');
INSERT INTO "al_umari" VALUES(51,15,'51:15','वास्तव में, आज्ञाकारी स्वर्गों तथा जल स्रोतों में होंगे।');
INSERT INTO "al_umari" VALUES(51,16,'51:16','लेते हुए जो कुछ प्रदान किया है उनहें, उनके पालनहारन ने। वस्तुतः, वे इससे पहले (संसार में) सदाचारी थे।');
INSERT INTO "al_umari" VALUES(51,17,'51:17','वे रात्रि में बहुत कम सोया करते थे।[ ');
INSERT INTO "al_umari" VALUES(51,18,'51:18','तथा भोरों[ में क्षमा माँगते थे। ');
INSERT INTO "al_umari" VALUES(51,19,'51:19','और उनके धनों में माँगने वाले[ तथा न पाने वाले का ह़क़ था। ');
INSERT INTO "al_umari" VALUES(51,20,'51:20','तथा धरती में बहुत-सी निशानियाँ हैं विश्वास करने वालों के लिए।');
INSERT INTO "al_umari" VALUES(51,21,'51:21','तथा स्वयं तुम्हारे भीतर (भी)। फिर क्यों तुम देखते नहीं?');
INSERT INTO "al_umari" VALUES(51,22,'51:22','और आकाश में तुम्हारी जीविका[ है तथा जिसका तुम्हें वचन दिया जा रहा है। ');
INSERT INTO "al_umari" VALUES(51,23,'51:23','तो शपथ है आकाश एवं धरती के पालनहार की! ये (बात) ऐसे ही सच है, जैसे तुम बोल रहे हो।[ ');
INSERT INTO "al_umari" VALUES(51,24,'51:24','(हे नबी!) क्या आयी आपके पास इब्राहीम के सम्मानित अतिथियों की सूचना?');
INSERT INTO "al_umari" VALUES(51,25,'51:25','जब वे आये उसके पास, तो सलाम किया। इब्राहीम ने (भी) सलाम किया (तथा कहाः) अपरिचित लोग हैं।');
INSERT INTO "al_umari" VALUES(51,26,'51:26','फिर चुपके से अपने परिजनों की ओर गया और एक मोटा (भुना हुआ) बछड़ा लाया।');
INSERT INTO "al_umari" VALUES(51,27,'51:27','फिर रख दिया उनके पास, उसने कहाः तुम क्यों नहीं खाते हो?');
INSERT INTO "al_umari" VALUES(51,28,'51:28','फिर अपने दिल में उनसे कुछ डरा, उन्होंने कहाः डरो नहीं और उसे शुभ सूचना दी एक ज्ञानी पुत्र की।');
INSERT INTO "al_umari" VALUES(51,29,'51:29','तो सामने आयी उसकी पत्नी और उसने मार लिया (आश्चर्य से) अपने मुँह पर हाथ तथा कहाः मैं बाँझ बुढ़िया हूँ।');
INSERT INTO "al_umari" VALUES(51,30,'51:30','उन्होंने कहाः इसी प्रकार, तेरे पालनहार ने कहा है। वास्तव में, वह सब गुण और सब कुछ जानने वाला है।');
INSERT INTO "al_umari" VALUES(51,31,'51:31','उस (इब्राहीम) ने कहाः तो तुम्हारा क्या अभियान है, हे भेजे हुए (फ़रिश्तो)!');
INSERT INTO "al_umari" VALUES(51,32,'51:32','उन्होंने कहाः वास्तव में, हम भेजे गये हैं एक अपरीधी जाति की ओर।');
INSERT INTO "al_umari" VALUES(51,33,'51:33','ताकि हम बरसायें उनपर पत्थर की कंकरी।');
INSERT INTO "al_umari" VALUES(51,34,'51:34','नामांकित[ तुम्हारे पालनहार की ओर से उल्लंघनकारियों के लिए। ');
INSERT INTO "al_umari" VALUES(51,35,'51:35','फिर हमने निकाल दिया जो भी उस (बस्ती) में ईमान वाले थे।');
INSERT INTO "al_umari" VALUES(51,36,'51:36','और हमने उसमें मोमिनों का केवल एक ही घर[ पाया। ');
INSERT INTO "al_umari" VALUES(51,37,'51:37','तथा छोड़ दी हमने उस (बस्ती) में एक निशानी उनके लिए, जो डरते हों दुःखदायी यातना से।');
INSERT INTO "al_umari" VALUES(51,38,'51:38','तथा मूसा (की कथा) में, जब हमने भेजा उसे फ़िरऔन की ओर प्रत्यक्ष (खुले) प्रमाण के साथ।');
INSERT INTO "al_umari" VALUES(51,39,'51:39','तो वह विमुख हो गया अपने बल-बूते के कारण और कह दिया कि जादूगर अथवा पागल है।');
INSERT INTO "al_umari" VALUES(51,40,'51:40','अन्ततः, हमने पकड़ लिया उसको तथा उसकी सेनाओं को, फिर फेंक दिया उन्हें सागर में और वह निन्दित होकर रह गया।');
INSERT INTO "al_umari" VALUES(51,41,'51:41','तथा आद में (शिक्षाप्रद निशानी है)। जब हमने भेज दी उनपर बाँझ[ आँधी। ');
INSERT INTO "al_umari" VALUES(51,42,'51:42','वह नहीं छोड़ती थी किसी वस्तु को जिसपर गुज़रती, परन्तु उसे बना देती थी जीर्ण चूर-चूर हड्डी के समान।');
INSERT INTO "al_umari" VALUES(51,43,'51:43','तथा समूद में जब उनसे कहा गया कि लाभान्वित हो लो, एक निश्चित समय तक।');
INSERT INTO "al_umari" VALUES(51,44,'51:44','तो उन्होंने अवज्ञा की अपने पालनहार के आदेश की, तो सहसा पकड़ लिया उन्हें कड़क ने और वे देखते रह गये।');
INSERT INTO "al_umari" VALUES(51,45,'51:45','तो वे न खड़े हो सके और न (हमसे) बदला ले सके।');
INSERT INTO "al_umari" VALUES(51,46,'51:46','तथा नूह़[ की जाति को इससे पहले (याद करो)। वास्तव में, वे अवज्ञाकारी जाति थे। ');
INSERT INTO "al_umari" VALUES(51,47,'51:47','तथा आकाश को हमने बनाया है हाथों[ से और हम निश्चय विस्तार करने वाले हैं। ');
INSERT INTO "al_umari" VALUES(51,48,'51:48','तथा धरती को हमने बिछाया है, तो हम क्या[ ही अच्छे बिछाने वाले हैं। ');
INSERT INTO "al_umari" VALUES(51,49,'51:49','तथा प्रत्येक वस्तु को हमने उत्पन्न किया है जोड़ा, ताकि तुम शिक्षा ग्रहण करो।');
INSERT INTO "al_umari" VALUES(51,50,'51:50','तो तुम दौड़ो अल्लाह की ओर, वास्तव में, मैं तुम्हें उसकी ओर से प्रत्यक्ष रूप से (खुला) सावधान कर ने वाला हूँ।');
INSERT INTO "al_umari" VALUES(51,51,'51:51','और मत बनाओ अल्लाह के साथ कोई दूसरा पूज्य। वास्तव में, मैं तुम्हें इससे खुला सावधान करने वाला हूँ।');
INSERT INTO "al_umari" VALUES(51,52,'51:52','इसी प्रकार नहीं आया उनके पास जो इन (मक्का वासियों) से पूर्व रहे कोई रसूल, परन्तु उन्होंने कहा कि जादूगर या पागल है।');
INSERT INTO "al_umari" VALUES(51,53,'51:53','क्या वे एक-दूसरे को वसिय्यत[ कर चुके हैं इसकी? बल्कि वे उल्लंघनकारी लोग हैं। ');
INSERT INTO "al_umari" VALUES(51,54,'51:54','तो आप मुख फेर लें उनसे। आपकी कोई निन्दा नहीं है।');
INSERT INTO "al_umari" VALUES(51,55,'51:55','और आप शिक्षा देते रहें। इसलिए कि शिक्षा लाभप्रद है इमान वालों के लिए।');
INSERT INTO "al_umari" VALUES(51,56,'51:56','और नहीं उत्पन्न किया है मैंने जिन्न तथा मनुष्य को, परन्तु ताकि मेरी ही इबादत करें।');
INSERT INTO "al_umari" VALUES(51,57,'51:57','मैं नहीं चाहता हूँ उनसे कोई जीविका और न चाहता हूँ कि वे मुझे खिलायें।');
INSERT INTO "al_umari" VALUES(51,58,'51:58','अवश्य अल्लाह ही जीविका दाता, शक्तिशाली, बलवान् है।');
INSERT INTO "al_umari" VALUES(51,59,'51:59','तो इन अत्याचारियों के पाप हैं इनके साथियों के पापों के समान, अतः उतावले न बनें।');
INSERT INTO "al_umari" VALUES(51,60,'51:60','अन्ततः, विनाश है काफ़िरों के लिए उनके उस दिन[ से, जिससे हे डराये जा रहे हैं। ');
INSERT INTO "al_umari" VALUES(52,1,'52:1','शपथ है तूर[ (पर्वत) की! ');
INSERT INTO "al_umari" VALUES(52,2,'52:2','और लिखी हुई पुस्तक[ की! ');
INSERT INTO "al_umari" VALUES(52,3,'52:3','जो झिल्ली के खुले पन्नों में लिखी हुई है।');
INSERT INTO "al_umari" VALUES(52,4,'52:4','तथा बैतुल मअमूर (आबाद[ घर) की! ');
INSERT INTO "al_umari" VALUES(52,5,'52:5','तथा ऊँची छत (आकाश) की!');
INSERT INTO "al_umari" VALUES(52,6,'52:6','और भड़काये हुए सागर[ की! ');
INSERT INTO "al_umari" VALUES(52,7,'52:7','वस्तुतः, आपके पालनहार की यातना होकर रहेगी।');
INSERT INTO "al_umari" VALUES(52,8,'52:8','नहीं है उसे कोई रोकने वाला।');
INSERT INTO "al_umari" VALUES(52,9,'52:9','जिस दिन आकाश डगमगायेगा।');
INSERT INTO "al_umari" VALUES(52,10,'52:10','तथा पर्वत चलेंगे।');
INSERT INTO "al_umari" VALUES(52,11,'52:11','तो विनाश है उस दिन, झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(52,12,'52:12','जो विवाद में खेल रहे हैं।');
INSERT INTO "al_umari" VALUES(52,13,'52:13','जिस दिन वे धक्का दिये जायेंगे नरक की अग्नि की ओर।');
INSERT INTO "al_umari" VALUES(52,14,'52:14','(उनसे कहा जायेगाः) यही वह नरक है, जिसे तुम झुठला रहे थे।');
INSERT INTO "al_umari" VALUES(52,15,'52:15','तो क्या ये जादू है या तुम्हें सुझाई नहीं देता?');
INSERT INTO "al_umari" VALUES(52,16,'52:16','इसमें प्रवेश कर जाओ, फिर सहन करो या सहन न करो, तुमपर समान है। तुम उसी का बदला दिये जा रहे हो, जो तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(52,17,'52:17','निश्चय, आज्ञाकारी बाग़ों तथा सुखों में होंगे। प्रसन्न होकर उससे, जो प्रदान किया होगा उन्हें उनके पालनहार ने तथा बचा लेगा उन्हें, उनका पालनहार नरक की यातना से।');
INSERT INTO "al_umari" VALUES(52,18,'52:18','प्रसन्न होकर उससे, जो प्रदान किया है उनहें उनके पालनहार ने तथा बचा लेगा उनहें उनका पालनहार नरक की यातना से।');
INSERT INTO "al_umari" VALUES(52,19,'52:19','(उनसे कहा जायेगाः) खाओ और पियो मनमानी, उसके बदले में, जो तुम कर रहे थे।');
INSERT INTO "al_umari" VALUES(52,20,'52:20','तकिये लगाये हुए होंगे तख़्तों पर बराबर बिछे हुए तथा हम विवाह देंगे उनको बड़ी आँखों वाली स्त्रियों से।');
INSERT INTO "al_umari" VALUES(52,21,'52:21','और जो लोग ईमान लाये और अनुसरण किया उनका, उनकी संतान ने ईमान के साथ, तो ह्म मिला देंगे उनकी संतान को उनके साथ तथा नहीं कम करेंगे उनके कर्मों में से कुछ, प्रत्येक व्यक्ति अपने कर्मों का बंधक[ है। ');
INSERT INTO "al_umari" VALUES(52,22,'52:22','तथा अधिक देंगे उन्हें मेवे तथा मांस जिसकी वे रूचि रखेंगे।');
INSERT INTO "al_umari" VALUES(52,23,'52:23','वे एक-दूसरे से उसमें लेते रहेंगे मदिरा के प्याले, जिसमें न कोई व्यर्थ बात होगी, न कोई पाप की बात।');
INSERT INTO "al_umari" VALUES(52,24,'52:24','और फिरते रहेंगे उनकी सेवा में (सुन्दर) बालक, जैसे वह छुपाये हुए मोती हों।');
INSERT INTO "al_umari" VALUES(52,25,'52:25','और वे (स्वर्ग वासी) सम्मुख होंगे एक-दूसरे के प्रश्न करते हुए।');
INSERT INTO "al_umari" VALUES(52,26,'52:26','वे कहेंगेः इससे पूर्व[ हम अपने परिजनों में डरते थे। ');
INSERT INTO "al_umari" VALUES(52,27,'52:27','तो अल्लाह ने उपकार किया हमपर तथा हमें सुरक्षित कर दिया तापलहरी की यातना से।');
INSERT INTO "al_umari" VALUES(52,28,'52:28','इससे पूर्व[ हम वंदना किया करते थे उसकी। निश्चय वह अति परोपकारी, दयावान् है। ');
INSERT INTO "al_umari" VALUES(52,29,'52:29','तो आप शिक्षा देते रहें। क्योंकि आपके पालनहार की अनुग्रह से न आप काहिन (ज्योतिषि) हैं और न पागल।[ ');
INSERT INTO "al_umari" VALUES(52,30,'52:30','क्या वे कहते हैं कि ये कवि हैं, हम प्रतीक्षा कर रहे हैं उसके साथ कालचक्र की?[ ');
INSERT INTO "al_umari" VALUES(52,31,'52:31','आप कह दें कि तुम प्रतीक्षा करते रहो, मैं (भी) तुम्हारे साथ प्रतीक्षा करता हूँ।');
INSERT INTO "al_umari" VALUES(52,32,'52:32','क्या उन्हें सिखाती हैं उनकी समझ ये बातें अथवा वह उल्लंघनकारी लोग हैं?');
INSERT INTO "al_umari" VALUES(52,33,'52:33','क्या वे कहते हैं कि इस (नबी) ने इस (क़ुर्आन) को स्वयं बना लिया है? वास्तव में, वे ईमान लाना नहीं चाहते।');
INSERT INTO "al_umari" VALUES(52,34,'52:34','तो वे ले आयें इस (क़ुर्आन) के समान कोई एक बात, यदि वे सच्चे हैं।');
INSERT INTO "al_umari" VALUES(52,35,'52:35','क्या वे पैदा हो गये हैं बिना[ किसी के पैदा किये अथवा वे स्वयं पैदा करने वाले हैं? ');
INSERT INTO "al_umari" VALUES(52,36,'52:36','या उन्होंने ही उत्पत्ति की है आकाशों तथा धरती की? वास्तव में, वे विश्वास ही नहीं रखते।');
INSERT INTO "al_umari" VALUES(52,37,'52:37','अथवा उनके पास आपके पालनहार के कोषागार हैं या वही (उसके) अधिकारी हैं?');
INSERT INTO "al_umari" VALUES(52,38,'52:38','अथवा उनके पास कोई सीढ़ी है, जिसे लगाकर सुनते[ हैं? तो उनका सुनने वाला कोई खुला प्रमाण प्रस्तुत करे। ');
INSERT INTO "al_umari" VALUES(52,39,'52:39','क्या अल्लाह के लिए पुत्रियाँ हों तुम्हारे लिए पुत्र हों।');
INSERT INTO "al_umari" VALUES(52,40,'52:40','या आप माँग कर रहे हैं उनसे किसी पारिश्रमिक[ की, तो वे उसके बोझ से दबे जा रहे हैं? ');
INSERT INTO "al_umari" VALUES(52,41,'52:41','अथवा उनके पास परोक्ष (का ज्ञान) है, जिसे वे लिख[ रहे हैं? ');
INSERT INTO "al_umari" VALUES(52,42,'52:42','या वे चाहते हैं कोई चाल चलना? तो जो काफ़िर हो गये, वे उस चाल में ग्रस्त होंगे।');
INSERT INTO "al_umari" VALUES(52,43,'52:43','अथवा उनका कोई ओर उपास्य (पूज्य) है अल्लाह के सिवा? अल्लाह पवित्र है उनके शिर्क से।');
INSERT INTO "al_umari" VALUES(52,44,'52:44','यदि वे देख लें कोई खण्ड आकाश से गिरता हुआ, तो कहेंगे कि तह पर तह बादल है।[ ');
INSERT INTO "al_umari" VALUES(52,45,'52:45','अतः, आप छोड़ दें उन्हें, यहाँ तक कि वे मिल जायें अपने उस दिन से, जिसमें[ इन्हें अपनी सुध्द नहीं होगी। ');
INSERT INTO "al_umari" VALUES(52,46,'52:46','उस दिन नहीं काम आयेगी उनके, उनकी चाल कुछ और न उनकी सहायता की जायेगी।');
INSERT INTO "al_umari" VALUES(52,47,'52:47','तथा निश्चय अत्याचारियों के लिए एक यातना है इसके अतिरिक्त[ (भी)। परन्तु, उनमें से अधिक्तर ज्ञान नहीं रखते हैं। ');
INSERT INTO "al_umari" VALUES(52,48,'52:48','और (हे नबी!) आप सहन करें अपने पालनहार का आदेश आने तक। वास्तव में, आप हमारी रक्षा में हैं तथा पवित्रता का वर्णन करें अपने पालनहार की प्रशंसा के साथ जब जागते हों।[ ');
INSERT INTO "al_umari" VALUES(52,49,'52:49','तथा रात्री में (भी) उसकी पवित्रता का वर्णन करें और तारों के डूबने के[ पश्चात् (भी)। ');
INSERT INTO "al_umari" VALUES(53,1,'53:1','शपथ है तारे की, जब वह डूबने लगे!');
INSERT INTO "al_umari" VALUES(53,2,'53:2','नहीं कुपथ हुआ है तुम्हारा साथी और न कुमार्ग हुआ है।');
INSERT INTO "al_umari" VALUES(53,3,'53:3','और वह नहीं बोलते अपनी इच्छा से।');
INSERT INTO "al_umari" VALUES(53,4,'53:4','वह तो बस वह़्यी (प्रकाशना) है। जो (उनकी ओर) की जाती है।');
INSERT INTO "al_umari" VALUES(53,5,'53:5','सिखाया है जिसे उन्हें शक्तिवान ने।[ ');
INSERT INTO "al_umari" VALUES(53,6,'53:6','बड़े बलशाली ने, फिर वह सीधा खड़ा हो गया।');
INSERT INTO "al_umari" VALUES(53,7,'53:7','तथा वह आकाश के ऊपरी किनारे पर था।');
INSERT INTO "al_umari" VALUES(53,8,'53:8','फिर समीप हुआ और फिर लटक गया।');
INSERT INTO "al_umari" VALUES(53,9,'53:9','फिर हो गया दो कमान के बराबर अथवा उससे भी समीप।');
INSERT INTO "al_umari" VALUES(53,10,'53:10','फिर उसने वह़्यी की उस (अल्लाह) के भक्त[ की ओर, जो भी वह़्यी की। ');
INSERT INTO "al_umari" VALUES(53,11,'53:11','नहीं झुठलाया उनके दिल ने, जो कुछ उन्होंने देखा।');
INSERT INTO "al_umari" VALUES(53,12,'53:12','तो क्या तुम उनसे झगड़ते हो उसपर, जिसे वे (आँखों से) देखते हैं?');
INSERT INTO "al_umari" VALUES(53,13,'53:13','निःसंदेह, उन्होंने उसे एक बार और भी उतरते देखा।');
INSERT INTO "al_umari" VALUES(53,14,'53:14','सिद्-रतुल मुन्हा[ के पास। ');
INSERT INTO "al_umari" VALUES(53,15,'53:15','जिसके पास जन्नतुल[ मावा है। ');
INSERT INTO "al_umari" VALUES(53,16,'53:16','जब सिद्-रह पर छा रहा था, जो कुछ छा रहा था।[ ');
INSERT INTO "al_umari" VALUES(53,17,'53:17','न तो निगाह चुंधियाई और न सीमा से आगे हुई।');
INSERT INTO "al_umari" VALUES(53,18,'53:18','निश्चय आपने अपने पालनहार की बड़ी निशानियाँ देखीं।[ ');
INSERT INTO "al_umari" VALUES(53,19,'53:19','तो (हे मुश्रिको!) क्या तुमने देख लिया लात्त तथा उज़्ज़ा को।');
INSERT INTO "al_umari" VALUES(53,20,'53:20','तथा एक तीसरे मनात को?[ ');
INSERT INTO "al_umari" VALUES(53,21,'53:21','क्या तुम्हारे लिए पुत्र हैं और उस अल्लाह के लिए पुत्रियाँ?');
INSERT INTO "al_umari" VALUES(53,22,'53:22','ये तो बड़ा भोंडा विभाजन है।');
INSERT INTO "al_umari" VALUES(53,23,'53:23','वास्तव में, ये कुछ केवल नाम हैं, जो तुमने तथा तुम्हारे पूर्वजों ने रख लिये हैं। नहीं उतारा है अल्लाह ने उनका कोई प्रमाण। वे केवल अनुमान[ पर चल रहे हैं तथा अपनी मनमानी पर। जबकि आ चुका है उनके पालनहार की ओर से मार्गदर्शन। ');
INSERT INTO "al_umari" VALUES(53,24,'53:24','क्या मनुष्य को वही मिल जायेगा, जिसकी वह कामना करे?');
INSERT INTO "al_umari" VALUES(53,25,'53:25','(नहीं, ये बात नहीं है) क्योंकि अल्लाह के अधिकार में है आख़िरत (परलोक) तथा संसार।');
INSERT INTO "al_umari" VALUES(53,26,'53:26','और आकाशों में बहुत-से फ़रिश्ते हैं, जिनकी अनुशंसा कुछ लाभ नहीं देती, परन्तु इसके पश्चात् कि अनुमति दे अल्लाह जिसके लिए चाहे तथा उससे प्रसन्न हो।[ ');
INSERT INTO "al_umari" VALUES(53,27,'53:27','वास्तव में, जो ईमान नहीं लाते परलोक पर, वे नाम देते हैं फ़रिश्तों के, स्त्रियों के नाम।');
INSERT INTO "al_umari" VALUES(53,28,'53:28','उन्हें इसका कोई ज्ञान नहीं। वे अनुसरण कर रहे हैं मात्र गुमान का और वस्तुतः गुमान नहीं लाभप्रद होता सत्य के सामने कुछ भी।');
INSERT INTO "al_umari" VALUES(53,29,'53:29','अतः, आप विमुख हो जायें उससे, जिसने मुँह फेर लिया है हमारी शिक्षा से तथा वह सांसारिक जीवन ही चाहता है।');
INSERT INTO "al_umari" VALUES(53,30,'53:30','यही उनके ज्ञान की पहुँच है। वास्तव में, आपका पालनहार ही अधिक जानता है उसे, जो कुपथ हो गया उसके मार्ग से तथा उसे, जिसने संमार्ग अपना लिया।');
INSERT INTO "al_umari" VALUES(53,31,'53:31','तथा अल्लाह ही का है जो आकाशों तथा धरती में है, ताकि वह बदला दे उसे, जिसने बुराई की उसके कुकर्म का और बदला दे उसे, जिसने सुकर्म किया अच्छा बदला।');
INSERT INTO "al_umari" VALUES(53,32,'53:32','उन लोगों को जो बचते हैं महा पापों तथा निर्लज्जा[ से, कुछ चूक के सिवा। वास्तव में, आपका पालनहार उदार, क्षमाशील है। वह भली-भाँति जानता है तुम्हें, जबकि उसने पैदा किया तुम्हें धरती से तथा जब तुम भ्रुण थे अपनी माताओं के गर्भ में। अतः, अपने में पवित्र न बनो। वही भली-भाँति जानता है उसे, जिसने सदाचार किया है।');
INSERT INTO "al_umari" VALUES(53,33,'53:33','तो क्या आपने उसे देखा जिसने मुँह फेर लिया?');
INSERT INTO "al_umari" VALUES(53,34,'53:34','और तनिक दान किया फिर रुक गया।');
INSERT INTO "al_umari" VALUES(53,35,'53:35','क्या उसके पास परोक्ष का ज्ञान है कि वह (सब कुछ) देख[ रहा है? ');
INSERT INTO "al_umari" VALUES(53,36,'53:36','क्या उसे सूचना नहीं हुई उन बातों की, जो मूसा के ग्रन्थों में हैं?');
INSERT INTO "al_umari" VALUES(53,37,'53:37','और इब्राहीम की, जिसने (अपना वचन) पूरा कर दिया।');
INSERT INTO "al_umari" VALUES(53,38,'53:38','कि कोई दूसरे का भार नहीं लादेगा।');
INSERT INTO "al_umari" VALUES(53,39,'53:39','और ये कि मनुष्य के लिए वही है, जो उसने प्रयास किया।');
INSERT INTO "al_umari" VALUES(53,40,'53:40','और ये कि उसका प्रयास शीघ्र देखा जायेगा।');
INSERT INTO "al_umari" VALUES(53,41,'53:41','फिर प्रतिफल दिया जायेगा उसे पूरा प्रतिफल।');
INSERT INTO "al_umari" VALUES(53,42,'53:42','और ये कि आपके पालनहार की ओर ही (सबको) पहुँचना है।');
INSERT INTO "al_umari" VALUES(53,43,'53:43','तथा वही है, जिसने (संसार में) हँसाया तथा रुलाया।');
INSERT INTO "al_umari" VALUES(53,44,'53:44','तथा उसीने मारा और जिवाया।');
INSERT INTO "al_umari" VALUES(53,45,'53:45','तथा उसीने दोनों प्रकार उत्पन्न किये; नर और नारी।');
INSERT INTO "al_umari" VALUES(53,46,'53:46','वीर्य से, जब (गर्भाशय में) गिरा।');
INSERT INTO "al_umari" VALUES(53,47,'53:47','तथा उसी के ऊपर दूसरी बार[ उत्पन्न करना है। ');
INSERT INTO "al_umari" VALUES(53,48,'53:48','तथा उसीने धनी बनाया और धन दिया।');
INSERT INTO "al_umari" VALUES(53,49,'53:49','और वही शेअरा[ का स्वामी है। ');
INSERT INTO "al_umari" VALUES(53,50,'53:50','तथा उसीने ध्वस्त किया प्रथम[ आद को। ');
INSERT INTO "al_umari" VALUES(53,51,'53:51','तथा समूद[ को। किसी को शेष नहीं रखा। ');
INSERT INTO "al_umari" VALUES(53,52,'53:52','तथा नूह़ की जाति को इससे पहले, वस्तुतः, वे बड़े अत्याचारी, अवज्ञाकारी थे।');
INSERT INTO "al_umari" VALUES(53,53,'53:53','तथा औंधी की हुई बस्ती[ को उसने गिरा दिया। ');
INSERT INTO "al_umari" VALUES(53,54,'53:54','फिर उसपर छा दिया, जो छा[ दिया। ');
INSERT INTO "al_umari" VALUES(53,55,'53:55','तो (हे मनुष्य!) तू अपने पालनहार के किन किन पुरस्कारों में संदेह करता रहेगा?');
INSERT INTO "al_umari" VALUES(53,56,'53:56','ये[ सचेतकर्ता है, प्रथम सचेतकर्ताओं में से। ');
INSERT INTO "al_umari" VALUES(53,57,'53:57','समीप आ लगी समीप आने वाली।');
INSERT INTO "al_umari" VALUES(53,58,'53:58','नहीं है अल्लाह के सिवा उसे कोई दूर करने वाला।');
INSERT INTO "al_umari" VALUES(53,59,'53:59','तो क्या तुम इस[ क़ुर्आन पर आश्चर्य करते हो?');
INSERT INTO "al_umari" VALUES(53,60,'53:60','तथा हँसते हो और रोते नहीं।');
INSERT INTO "al_umari" VALUES(53,61,'53:61','तथा विमुख हो रहे हो।');
INSERT INTO "al_umari" VALUES(53,62,'53:62','अतः, सज्दा करो अल्लाह के लिए तथा उसी की वंदना[ करो। ');
INSERT INTO "al_umari" VALUES(54,1,'54:1','समीप आ गयी[ प्रलय तथा दो खण्ड हो गया चाँद। ');
INSERT INTO "al_umari" VALUES(54,2,'54:2','और यदि वे देखते हैं कोई निशानी, तो मुँह फेर लेते हैं और कहते हैं: ये तो जादू है, जो होता रहा है।');
INSERT INTO "al_umari" VALUES(54,3,'54:3','और उन्होंने झुठलाया और अनुसरण किया अपनी आकांक्षाओं का और प्रत्येक कार्य का एक निश्चित समय है।');
INSERT INTO "al_umari" VALUES(54,4,'54:4','और निश्चय आ चुके हैं उनके पास कुछ ऐसे समाचार, जिनमें चेतावनी है।');
INSERT INTO "al_umari" VALUES(54,5,'54:5','ये (क़ुर्आन) पूर्णतः तत्वदर्शिता (ज्ञान) है, फिर भी नहीं काम आयी उनके, चेतावनियाँ।');
INSERT INTO "al_umari" VALUES(54,6,'54:6','तो आप विमुख हो जायें उनसे, जिस दिन पुकारने वाला पुकारेगा एक अप्रिय चीज़ की[ ओर। ');
INSERT INTO "al_umari" VALUES(54,7,'54:7','झुकी होंगी उनकी आँखें। वे निकल रहे होंगे समाधियों से, जैसे कि वे टिड्डी दल हों बिखरे हुए।');
INSERT INTO "al_umari" VALUES(54,8,'54:8','तो उसने प्रार्थना की अपने पालनहार से कि मैं विवश हूँ, अतः, मेरा बदला ले ले।');
INSERT INTO "al_umari" VALUES(54,9,'54:9','झुठलाया इनसे पहले नूह़ की जाति ने। तो झुठलाया उन्होंने हमारे भक्त को और कहा कि पागल है और (उसे) झड़का गया।');
INSERT INTO "al_umari" VALUES(54,10,'54:10','तो उसने प्रार्थना की अपने पालनहार से कि मैं विवश हूँ, अतः मेरा बदला ले ले।');
INSERT INTO "al_umari" VALUES(54,11,'54:11','तो हमने खोल दिये आकाश के द्वार धारा प्रवाह जल के साथ।');
INSERT INTO "al_umari" VALUES(54,12,'54:12','तथा फाड़ दिये धरती के स्रोत, तो मिल गया (आकाश और धरती का) जल उस कार्य के अनुसार जो निश्चित किया गया।');
INSERT INTO "al_umari" VALUES(54,13,'54:13','और सवार कर दिया हमने उसे (नूह़ को) तख़्तों तथा कीलों वाली (नाव) पर।');
INSERT INTO "al_umari" VALUES(54,14,'54:14','जो चल रही थी हमारी रक्षा में, उसका बदला लेने के लिए, जिसके साथ कुफ़्र किया गया था।');
INSERT INTO "al_umari" VALUES(54,15,'54:15','और हमने छोड़ दिया इसे एक शिक्षा बनाकर। तो क्या, है कोई शिक्षा ग्रहण करने वाला?');
INSERT INTO "al_umari" VALUES(54,16,'54:16','फिर (देख लो!) कैसी रही मेरी यातना तथा मेरी चेतावनियाँ?');
INSERT INTO "al_umari" VALUES(54,17,'54:17','और हमने सरल कर दिया है क़ुर्आन को शिक्षा के लिए। तो क्या, है कोई शिक्षा ग्रहण करने वाला?');
INSERT INTO "al_umari" VALUES(54,18,'54:18','झुठलाया आद ने, तो कैसी रही मेरी यातना तथा मेरी चेतावनियाँ?');
INSERT INTO "al_umari" VALUES(54,19,'54:19','हमने भेज दी उनपर कड़ी आँधी, एक निरन्तर अशुभ दिन में।');
INSERT INTO "al_umari" VALUES(54,20,'54:20','जो उखाड़ रही थी लोगों को, जैसे वे खजूर के खोखले तने हों।');
INSERT INTO "al_umari" VALUES(54,21,'54:21','तो कैसी रही मेरी यातना तथा मेरी चेतावनियाँ?');
INSERT INTO "al_umari" VALUES(54,22,'54:22','और हमने सरल कर दिया है क़ुर्आन को शिक्षा के लिए। तो क्या, है कोई शिक्षा ग्रहण करने वाला?');
INSERT INTO "al_umari" VALUES(54,23,'54:23','झुठला दिया समूद[ ने चेतावनियों को। ');
INSERT INTO "al_umari" VALUES(54,24,'54:24','और कहाः क्या अपने ही में से एक मनुष्य का हम अनुसरण करें? वास्तव में, तब तो हम निश्चय बड़े कुपथ तथा पागलपन में हैं।');
INSERT INTO "al_umari" VALUES(54,25,'54:25','क्या उतारी गयी है शिक्षा उसीपर हमारे बीच में से? (नहीं) बल्कि वह बड़ा झूठा अहंकारी है।');
INSERT INTO "al_umari" VALUES(54,26,'54:26','उन्हें कल ही ज्ञान हो जायेगा कि कौन बड़ा झूठा अहंकारी है?');
INSERT INTO "al_umari" VALUES(54,27,'54:27','वास्तव में, हम भेजने वाले हैं ऊँटनी उनकी परीक्षा के लिए। अतः, (हे सालेह!) तुम उनके (परिणाम की) प्रतीक्षा करो तथा धैर्य रखो।');
INSERT INTO "al_umari" VALUES(54,28,'54:28','और उन्हें सूचित कर दो कि जल विभाजित होगा उनके बीच और प्रत्येक अपनी बारी के दिन[ उपस्थित होगा। ');
INSERT INTO "al_umari" VALUES(54,29,'54:29','तो उन्होंने पुकारा अपने साथी को। तो उसने आक्रमण किया और उसे वध कर दिया।');
INSERT INTO "al_umari" VALUES(54,30,'54:30','फिर कैसी रही मेरी यातना और मेरी चेतावनियाँ?');
INSERT INTO "al_umari" VALUES(54,31,'54:31','हमने भेज दी उनपर कर्कश ध्वनि, तो वे हो गये बाड़ा बनाने वाले की रौंदी हुई बाढ़ के समान (चूर-चूर)।');
INSERT INTO "al_umari" VALUES(54,32,'54:32','और हमने सरल कर दिया है क़ुर्आन को शिक्षा के लिए। तो क्या, है कोई शिक्षा ग्रहण करने वाला?');
INSERT INTO "al_umari" VALUES(54,33,'54:33','झुठला दिया लूत की जाति ने चेतावनियों को।');
INSERT INTO "al_umari" VALUES(54,34,'54:34','तो हमने भेज दिये उनपर पत्थर लूत के परिजनों के सिवा, हमने उन्हें बचा लिया रात्रि के पिछले पहर।');
INSERT INTO "al_umari" VALUES(54,35,'54:35','अपने विशेष अनुग्रह से। इसी प्रकार हम बदला देते हैं उसको जो कृतज्ञ हो।');
INSERT INTO "al_umari" VALUES(54,36,'54:36','और निःसंदेह, लूत ने सावधान किया उनको हमारी पकड़ से। परन्तु, उन्होंने संदेह किया चेतावनियों के विषय में।');
INSERT INTO "al_umari" VALUES(54,37,'54:37','और बहलाना चाहा उस (लूत) को उसके अतिथियों[ से तो हमने अंधी कर दी उनकी आँखें कि चखो मेरी यातना तथा मेरी चेतावनियों (का परिणाम)। ');
INSERT INTO "al_umari" VALUES(54,38,'54:38','और उनपर आ पहुँची प्रातः भोर ही में स्थायी यातना।');
INSERT INTO "al_umari" VALUES(54,39,'54:39','तो चखो मेरी यातना तथा मेरी चेतावनियाँ।');
INSERT INTO "al_umari" VALUES(54,40,'54:40','और हमने सरल कर दिया है क़ुर्आन को शिक्षा के लिए। तो क्या, है कोई शिक्षा ग्रहण करने वाला?');
INSERT INTO "al_umari" VALUES(54,41,'54:41','तथा फ़िरऔनियों के पास भी चेतावनियाँ आयीं।');
INSERT INTO "al_umari" VALUES(54,42,'54:42','उन्होंने झुठलाया हमारी प्रत्येक निशानी को तो हमने पकड़ लिया उन्हें अति प्रभावी आधिपति के पकड़ने के समान।');
INSERT INTO "al_umari" VALUES(54,43,'54:43','(हे मक्का वासियों!) क्या तुम्हारे काफ़िर उत्तम हैं उनसे अथवा तुम्हारी मुक्ति लिखी हुई है आकाशीय पुस्तकों में?');
INSERT INTO "al_umari" VALUES(54,44,'54:44','अथवा वे कहते हैं कि हम विजेता समूह हैं।');
INSERT INTO "al_umari" VALUES(54,45,'54:45','शीध्र ही प्राजित कर दिया जायेगा ये समूह और वे पीठ दिखा[ देंगे। ');
INSERT INTO "al_umari" VALUES(54,46,'54:46','बल्कि प्रलय उनके वचन का समय है तथा प्रलय अधिक कड़ी और तीखी है।');
INSERT INTO "al_umari" VALUES(54,47,'54:47','वस्तुतः, ये पापी, कुपथ तथा अग्नि में हैं।');
INSERT INTO "al_umari" VALUES(54,48,'54:48','जिस दिन वे घसीटे जायेंगे यातना में अपने मुखों के बल (उनसे कहा जायेगा कि) चखो नरक की यातना का स्वाद।');
INSERT INTO "al_umari" VALUES(54,49,'54:49','निश्चय हमने प्रत्येक वस्तु को उत्पन्न किया है एक अनुमान से।');
INSERT INTO "al_umari" VALUES(54,50,'54:50','और हमारा आदेश बस एक ही बार होता है आँख झपकने के समान।[ ');
INSERT INTO "al_umari" VALUES(54,51,'54:51','और हम ध्वस्त कर चुके हैं तुम्हारे जैसे बहुत-से समुदायों को।');
INSERT INTO "al_umari" VALUES(54,52,'54:52','जो कुछ उन्होंने किया है कर्मपत्र में है।[ ');
INSERT INTO "al_umari" VALUES(54,53,'54:53','और प्रत्येक तुच्छ तथा बड़ी बात अंकित है।');
INSERT INTO "al_umari" VALUES(54,54,'54:54','वस्तुतः, सदाचारी लोग स्वर्गों तथा नहरों में होंगे।');
INSERT INTO "al_umari" VALUES(54,55,'54:55','सत्य के स्थान में, अति सामर्थ्यवान स्वामी के पास।');
INSERT INTO "al_umari" VALUES(55,1,'55:1','अत्यंत कृपाशील ने।');
INSERT INTO "al_umari" VALUES(55,2,'55:2','शिक्षा दी क़ुर्आन की।');
INSERT INTO "al_umari" VALUES(55,3,'55:3','उसीने उत्पन्न किया मनुष्य को।');
INSERT INTO "al_umari" VALUES(55,4,'55:4','सिखाया उसे साफ़-साफ़ बोलना।');
INSERT INTO "al_umari" VALUES(55,5,'55:5','सूर्य तथा चन्द्रमा एक (नियमित) ह़िसाब से हैं।');
INSERT INTO "al_umari" VALUES(55,6,'55:6','तथा तारे और वृक्ष दोनों (उसे) सज्दा करते हैं।');
INSERT INTO "al_umari" VALUES(55,7,'55:7','और आकाश को ऊँचा किया और रख दी तराजू।[ ');
INSERT INTO "al_umari" VALUES(55,8,'55:8','ताकि तुम उल्लंघन न करो तराजू (न्याय) में।');
INSERT INTO "al_umari" VALUES(55,9,'55:9','तथा सीधी रखो तराजू न्याय के साथ और कम न तोलो।');
INSERT INTO "al_umari" VALUES(55,10,'55:10','धरती को उसने (रहने योग्य) बनाया पूरी उत्पत्ति के लिए।');
INSERT INTO "al_umari" VALUES(55,11,'55:11','जिसमें मेवे तथा गुच्छे वाले खजूर हैं।');
INSERT INTO "al_umari" VALUES(55,12,'55:12','और भूसे वाले अन्न तथा सुगंधित (पुष्प) फूल हैं।');
INSERT INTO "al_umari" VALUES(55,13,'55:13','तो (हे मनुष्य तथा जिन्न!) तुम अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,14,'55:14','उसने उत्पन्न किया मनुष्य को खनखनाते ठीकरी जैसे सूखे गारे से।');
INSERT INTO "al_umari" VALUES(55,15,'55:15','तथा उत्पन्न किया जिन्नों को अग्नि की ज्वाला से।');
INSERT INTO "al_umari" VALUES(55,16,'55:16','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,17,'55:17','वह दोनों सूर्योदय[ के स्थानों तथा दोनों सूर्यास्त के स्थानों का स्वामी है। ');
INSERT INTO "al_umari" VALUES(55,18,'55:18','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,19,'55:19','उसने दो सागर बहा दिये, जिनका संगम होता है।');
INSERT INTO "al_umari" VALUES(55,20,'55:20','उन दोनों के बीच एक आड़ है। वह एक-दूसरे से मिल नहीं सकते।');
INSERT INTO "al_umari" VALUES(55,21,'55:21','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,22,'55:22','निकलता है उन दोनों से मोती तथा मूँगा।');
INSERT INTO "al_umari" VALUES(55,23,'55:23','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,24,'55:24','तथा उसी के अधिकार में हैं जहाज़, खड़े किये हुए सागर में पर्वतों जैसे।');
INSERT INTO "al_umari" VALUES(55,25,'55:25','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,26,'55:26','प्रत्येक, जो धरती पर हैं, नाशवान हैं।');
INSERT INTO "al_umari" VALUES(55,27,'55:27','तथा शेष रह जायेगा आपके प्रतापी सम्मानित पालनहार का मुख (अस्तित्व)।');
INSERT INTO "al_umari" VALUES(55,28,'55:28','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,29,'55:29','उसीसे माँगते हैं, जो आकाशों तथा धरती में हैं। प्रत्येक दिन वह एक नये कार्य में है।[ ');
INSERT INTO "al_umari" VALUES(55,30,'55:30','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,31,'55:31','और शीघ्र ही हम पूर्णतः आकर्षित हो जायेंगे तुम्हारी ओर, हे (धरती के) दोनों बोझ[ (जन्नो और मनुष्यो!) ');
INSERT INTO "al_umari" VALUES(55,32,'55:32','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,33,'55:33','हे जिन्न तथा मनुष्य के समूह! यदि निकल सकते हो आकाशों तथा थरती के किनारों से, तो निकल भागो और तुम निकल नहीं सकोगे बिना बड़ी शक्ति[ के। ');
INSERT INTO "al_umari" VALUES(55,34,'55:34','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,35,'55:35','तुम दोनों पर अग्नि की ज्वाला तथा धुवाँ छोड़ा जायेगा। तो तुम अपनी सहायता नहीं कर सकोगे।');
INSERT INTO "al_umari" VALUES(55,36,'55:36','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,37,'55:37','जब आकाश (प्रलय के दिन) फट जायेगा, तो लाल हो जायेगा लाल चमड़े के समान।');
INSERT INTO "al_umari" VALUES(55,38,'55:38','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,39,'55:39','तो उस दिन नहीं प्रश्न किया जायेगा अपने पाप का किसी मनुष्य से और न जिन्न से।');
INSERT INTO "al_umari" VALUES(55,40,'55:40','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,41,'55:41','पहचान लिये जायेंगे अपराधी अपने मुखों से, तो पकड़ा जायेगा उनके माथे के बालों और पैरों को।');
INSERT INTO "al_umari" VALUES(55,42,'55:42','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,43,'55:43','यही वो नरक है, जिसे झूठ कह रहे थे अपराधी।');
INSERT INTO "al_umari" VALUES(55,44,'55:44','वे फिरते रहेंगे उसके बीच तथा खौलते पानी के बीच।');
INSERT INTO "al_umari" VALUES(55,45,'55:45','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,46,'55:46','और उसके लिए, जो डरा अपने पालनहार के समक्ष खड़े होने से, दो बाग़ हैं।');
INSERT INTO "al_umari" VALUES(55,47,'55:47','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,48,'55:48','दो बाग़, हरी-भरी शाखाओं वाले।');
INSERT INTO "al_umari" VALUES(55,49,'55:49','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,50,'55:50','उन दोनों में, दो जल स्रोत बहते होंगे।');
INSERT INTO "al_umari" VALUES(55,51,'55:51','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,52,'55:52','उनमें, प्रत्येक फल के दो प्रकार होंगे।');
INSERT INTO "al_umari" VALUES(55,53,'55:53','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,54,'55:54','वे ऐसे बिस्तरों पर तकिये लगाये हुए होंगे, जिनके स्तर दबीज़ रेशम के होंगे और दोनों बाग़ों (की शाखायें) फलों से झुकी हुई होंगी।');
INSERT INTO "al_umari" VALUES(55,55,'55:55','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,56,'55:56','उनमें लजीली आँखों वाली स्त्रियाँ होंगी, जिन्हें हाथ नहीं लगाया होगा किसी मनुष्य ने इससे पूर्व और न किसी जिन्न ने।');
INSERT INTO "al_umari" VALUES(55,57,'55:57','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,58,'55:58','जैसे वह हीरे और मोंगे हों।');
INSERT INTO "al_umari" VALUES(55,59,'55:59','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,60,'55:60','उपकार का बदला उपकार ही है।');
INSERT INTO "al_umari" VALUES(55,61,'55:61','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,62,'55:62','तथा उन दोनों के सिवा[ दो बाग़ होंगे। ');
INSERT INTO "al_umari" VALUES(55,63,'55:63','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,64,'55:64','दोनों हरे-भरे होंगे।');
INSERT INTO "al_umari" VALUES(55,65,'55:65','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,66,'55:66','उन दोनों में, दो जल स्रोत होंगे उबलते हुए।');
INSERT INTO "al_umari" VALUES(55,67,'55:67','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,68,'55:68','उनमें, फल तथा खजूर और अनार होंगे।');
INSERT INTO "al_umari" VALUES(55,69,'55:69','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,70,'55:70','उनमें, सुचरिता सुन्दरियाँ होंगी।');
INSERT INTO "al_umari" VALUES(55,71,'55:71','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,72,'55:72','गोरियाँ सुरक्षित होंगी ख़ेमों में।');
INSERT INTO "al_umari" VALUES(55,73,'55:73','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,74,'55:74','नहीं हाथ लगाया होगा[ उन्हें किसी मनुष्य ने इससे पूर्व और न किसी जिन्न ने। ');
INSERT INTO "al_umari" VALUES(55,75,'55:75','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,76,'55:76','वे तकिये लगाये हुए होंगे हरे ग़लीचों तथा सुन्दर बिस्तरों पर।');
INSERT INTO "al_umari" VALUES(55,77,'55:77','तो तुम दोनों अपने पालनहार के किन-किन उपकारों को झुठलाओगे?');
INSERT INTO "al_umari" VALUES(55,78,'55:78','शुभ है आपके प्रतापी सम्मानित पालनहार का नाम।');
INSERT INTO "al_umari" VALUES(56,1,'56:1','जब होने वाली, हो जायेगी।');
INSERT INTO "al_umari" VALUES(56,2,'56:2','उसका होना कोई झूठ नहीं है।');
INSERT INTO "al_umari" VALUES(56,3,'56:3','नीचा-ऊँचा करने[ वाली। ');
INSERT INTO "al_umari" VALUES(56,4,'56:4','जब धरती तेज़ी से डोलने लगेगी।');
INSERT INTO "al_umari" VALUES(56,5,'56:5','और चूर-चूर कर दिये जायेंगे पर्वत।');
INSERT INTO "al_umari" VALUES(56,6,'56:6','फिर हो जायेंगे बिखरी हुई धूल।');
INSERT INTO "al_umari" VALUES(56,7,'56:7','तथा तुम हो जाओगे तीन समूह।');
INSERT INTO "al_umari" VALUES(56,8,'56:8','तो दायें वाले, तो क्या हैं दायें वाले![ ');
INSERT INTO "al_umari" VALUES(56,9,'56:9','और बायें वाले, तो क्या हैं बायें वाले!');
INSERT INTO "al_umari" VALUES(56,10,'56:10','और अग्रगामी तो अग्रगामी ही हैं।');
INSERT INTO "al_umari" VALUES(56,11,'56:11','वही समीप किये[ हुए हैं। ');
INSERT INTO "al_umari" VALUES(56,12,'56:12','वे सुखों के स्वर्गों में होंगे।');
INSERT INTO "al_umari" VALUES(56,13,'56:13','बहुत-से अगले लोगों में से।');
INSERT INTO "al_umari" VALUES(56,14,'56:14','तथा कुछ पिछले लोगों में से होंगे।');
INSERT INTO "al_umari" VALUES(56,15,'56:15','स्वर्ण से बुने हुए तख़्तों पर।');
INSERT INTO "al_umari" VALUES(56,16,'56:16','तकिये लगाये उनपर, एक-दूसरे के सम्मुख (आसीन) होंगे।');
INSERT INTO "al_umari" VALUES(56,17,'56:17','फिरते होंगे उनकी सेवा के लिए बालक, जो सदा (बालक) रहेंगे।');
INSERT INTO "al_umari" VALUES(56,18,'56:18','प्याले तथा सुराह़ियाँ लेकर तथा मदिरा के छलकते प्याले।');
INSERT INTO "al_umari" VALUES(56,19,'56:19','न तो सिर चकरायेगा उनसे, न वे निर्बोध होंगे।');
INSERT INTO "al_umari" VALUES(56,20,'56:20','तथा जो फल वे चाहेंगे।');
INSERT INTO "al_umari" VALUES(56,21,'56:21','तथा पक्षी का जो मांस वे चाहेंगे।');
INSERT INTO "al_umari" VALUES(56,22,'56:22','और गोरियाँ बड़े नैनों वाली।');
INSERT INTO "al_umari" VALUES(56,23,'56:23','छुपाकर रखी हुईं मोतियों के समान।');
INSERT INTO "al_umari" VALUES(56,24,'56:24','उसके बदले, जो वे (संसार में) करते रहे।');
INSERT INTO "al_umari" VALUES(56,25,'56:25','नहीं सुनेंगे उनमें व्यर्थ बात और न पाप की बात।');
INSERT INTO "al_umari" VALUES(56,26,'56:26','केवल सलाम ही सलाम की ध्वनि होगी।');
INSERT INTO "al_umari" VALUES(56,27,'56:27','और दायें वाले, क्या (ही भाग्यशाली) हैं दायें वाले!');
INSERT INTO "al_umari" VALUES(56,28,'56:28','बिन काँटे की बैरी में होंगे।');
INSERT INTO "al_umari" VALUES(56,29,'56:29','तथा तह पर तह केलों में।');
INSERT INTO "al_umari" VALUES(56,30,'56:30','फैली हुई छाया[ में। ');
INSERT INTO "al_umari" VALUES(56,31,'56:31','और प्रवाहित जल में।');
INSERT INTO "al_umari" VALUES(56,32,'56:32','तथा बहुत-से फलों में।');
INSERT INTO "al_umari" VALUES(56,33,'56:33','जो न समाप्त होंगे, न रोके जायेंगे।');
INSERT INTO "al_umari" VALUES(56,34,'56:34','और ऊँचे बिस्तर पर।');
INSERT INTO "al_umari" VALUES(56,35,'56:35','हमने बनाया है (उनकी) पत्नियों को एक विशेष रूप से।');
INSERT INTO "al_umari" VALUES(56,36,'56:36','हमने बनाय है उन्हें कुमारियाँ।');
INSERT INTO "al_umari" VALUES(56,37,'56:37','प्रेमिकायें समायु।');
INSERT INTO "al_umari" VALUES(56,38,'56:38','दाहिने वालों के लिए।');
INSERT INTO "al_umari" VALUES(56,39,'56:39','बहुत-से अगलों में से होंगे।');
INSERT INTO "al_umari" VALUES(56,40,'56:40','तथा बहुत-से पिछलों में से।');
INSERT INTO "al_umari" VALUES(56,41,'56:41','और बायें वाले, तो क्या हैं बायें वाले!');
INSERT INTO "al_umari" VALUES(56,42,'56:42','वे गर्म वायु तथा खौलते जल में (होंगे)।');
INSERT INTO "al_umari" VALUES(56,43,'56:43','तथा काले धुवें की छाया में।');
INSERT INTO "al_umari" VALUES(56,44,'56:44','जो न शीतल होगा और न सुखद।');
INSERT INTO "al_umari" VALUES(56,45,'56:45','वास्तव में, वे इससे पहले (संसार में) सम्पन्न (सुखी) थे।');
INSERT INTO "al_umari" VALUES(56,46,'56:46','तथा दुराग्रह करते थे महा पापों पर।');
INSERT INTO "al_umari" VALUES(56,47,'56:47','तथा कहा करते थे कि क्या जब हम मर जायेंगे तथा हो जायेंगे धूल और अस्थियाँ, तो क्या हम अवश्य पुनः जीवित होंगे?');
INSERT INTO "al_umari" VALUES(56,48,'56:48','और क्या हमारे पूर्वज (भी)?');
INSERT INTO "al_umari" VALUES(56,49,'56:49','आप कह दें कि निःसंदेह सब अगले तथा पिछले।');
INSERT INTO "al_umari" VALUES(56,50,'56:50','अवश्य एकत्र किये जायेंगे एक निर्धारित दिन के समय।');
INSERT INTO "al_umari" VALUES(56,51,'56:51','फिर तुम, हे कुपथो! झुठलाने वालो!');
INSERT INTO "al_umari" VALUES(56,52,'56:52','अवश्य खाने वाले हो ज़क़्क़ूम (थोहड़) के वृक्ष से।[ ');
INSERT INTO "al_umari" VALUES(56,53,'56:53','तथा भरने वाले हो उससे (अपने) उदर।');
INSERT INTO "al_umari" VALUES(56,54,'56:54','तथा पीने वाले हो उसपर से खौलता जल।');
INSERT INTO "al_umari" VALUES(56,55,'56:55','फिर पीने वाले हो प्यासे[ ऊँट के समान। ');
INSERT INTO "al_umari" VALUES(56,56,'56:56','यही उनका अतिथि सत्कार है, प्रतिकार (प्रलय) के दिन।');
INSERT INTO "al_umari" VALUES(56,57,'56:57','हमने ही उत्पन्न किया है तुम्हें, फिर तुम विश्वास क्यों नहीं करते?');
INSERT INTO "al_umari" VALUES(56,58,'56:58','क्या तुमने ये विचार किया कि जो वीर्य तुम (गर्भाशयों में) गिराते हो।');
INSERT INTO "al_umari" VALUES(56,59,'56:59','क्या तुम उसे शिशु बनाते हो या हम बनाने वाले हैं?');
INSERT INTO "al_umari" VALUES(56,60,'56:60','हमने निर्धारित किया है तुम्हारे बीच मरण को तथा हम विवश होने वाले नहीं हैं।');
INSERT INTO "al_umari" VALUES(56,61,'56:61','कि बदल दें तुम्हारे रूप और तुम्हें बना दें उस रूप में, जिसे तुम नहीं जानते।');
INSERT INTO "al_umari" VALUES(56,62,'56:62','तथा तुमने तो जान लिया है प्रथम उत्पत्ति को फिर तुम शिक्षा ग्रहण क्यों नहीं करते?');
INSERT INTO "al_umari" VALUES(56,63,'56:63','फिर क्या तुमने विचार किया कि उसमें जो तुम बोते हो?');
INSERT INTO "al_umari" VALUES(56,64,'56:64','क्या तुम उसे उगाते हो या हम उसे उगाने वाले हैं?');
INSERT INTO "al_umari" VALUES(56,65,'56:65','यदि हम चाहें, तो उसे भुस बना दें, फिर तुम बातें बनाते रह जाओ।');
INSERT INTO "al_umari" VALUES(56,66,'56:66','वस्तुतः, हम दण्डित कर दिये गये।');
INSERT INTO "al_umari" VALUES(56,67,'56:67','बल्कि हम (जीविका से) वंचित कर दिये गये।');
INSERT INTO "al_umari" VALUES(56,68,'56:68','फिर तुमने विचार किया उस पानी में, जो तुम पीते हो?');
INSERT INTO "al_umari" VALUES(56,69,'56:69','क्या तुमने उसे बरसाया है बादल से अथवा हम उसे बरसाने वाले हैं।?');
INSERT INTO "al_umari" VALUES(56,70,'56:70','यदि हम चाहें, तो उसे खारी कर दें, फिर तुम आभारी (कृतज्ञ) क्यों नहीं होते?');
INSERT INTO "al_umari" VALUES(56,71,'56:71','क्या तुमने उस अग्नि को देखा, जिसे तुम सुलगाते हो।');
INSERT INTO "al_umari" VALUES(56,72,'56:72','क्या तुमने उत्पन्न किया है उसके वृक्ष को या हम उत्पन्न करने वाले हैं?');
INSERT INTO "al_umari" VALUES(56,73,'56:73','हमने ही बनाया उसे शिक्षाप्रद तथा यात्रियों के लाभदायक।');
INSERT INTO "al_umari" VALUES(56,74,'56:74','अतः, (हे नबी!) आप पवित्रता का वर्णन करें अपने महा पालनहार के नाम की।');
INSERT INTO "al_umari" VALUES(56,75,'56:75','मैं शपथ लेता हूँ सितारों के स्थानों की!');
INSERT INTO "al_umari" VALUES(56,76,'56:76','और ये निश्चय एक बड़ी शपथ है, यदि तुम समझो।');
INSERT INTO "al_umari" VALUES(56,77,'56:77','वास्तव में, ये आदरणीय[ क़ुर्आन है। ');
INSERT INTO "al_umari" VALUES(56,78,'56:78','सुरक्षित[ पुस्तक में। ');
INSERT INTO "al_umari" VALUES(56,79,'56:79','इसे पवित्र लोग ही छूते हैं।[ ');
INSERT INTO "al_umari" VALUES(56,80,'56:80','अवतरित किया गया है सर्वलोक के पालनहार की ओर से।');
INSERT INTO "al_umari" VALUES(56,81,'56:81','फिर क्या तुम इस वाणि (क़ुर्आन) की अपेक्षा करते हो?');
INSERT INTO "al_umari" VALUES(56,82,'56:82','तथा बनाते हो अपना भाग कि इसे तुम झुठलाते हो?');
INSERT INTO "al_umari" VALUES(56,83,'56:83','फिर क्यों नहीं जब प्राण गले को पहुँचते हैं।');
INSERT INTO "al_umari" VALUES(56,84,'56:84','और तुम उस समय देखते रहते हो।');
INSERT INTO "al_umari" VALUES(56,85,'56:85','तथा हम अधिक समीप होते हैं उसके तुमसे, परन्तु तुम नहीं देख सकते।');
INSERT INTO "al_umari" VALUES(56,86,'56:86','तो यदि तुम किसी के आधीन न हो।');
INSERT INTO "al_umari" VALUES(56,87,'56:87','तो उस (प्राण) को फेर क्यों नहीं लाते, यदि तुम सच्चे हो?');
INSERT INTO "al_umari" VALUES(56,88,'56:88','फिर यदि वह (प्राणी) समीपवर्तियों में है।');
INSERT INTO "al_umari" VALUES(56,89,'56:89','तो उसके लिए सुख तथा उत्तम जीविका तथा सुख भरा स्वर्ग है।');
INSERT INTO "al_umari" VALUES(56,90,'56:90','और यदि वह दायें वालों में से है।');
INSERT INTO "al_umari" VALUES(56,91,'56:91','तो सलाम है तेरे लिए दायें वालों में होने के कारण।[ ');
INSERT INTO "al_umari" VALUES(56,92,'56:92','और यदि वह है झुठलाने वाले कुपथों में से।');
INSERT INTO "al_umari" VALUES(56,93,'56:93','तो अतिथि सत्कार है खौलते पानी से।');
INSERT INTO "al_umari" VALUES(56,94,'56:94','तथा नरक में प्रवेश।');
INSERT INTO "al_umari" VALUES(56,95,'56:95','वास्तव में, यही निश्चय सत्य है।');
INSERT INTO "al_umari" VALUES(56,96,'56:96','अतः, (हे नबी!) आप पवित्रता का वर्णन करें अपने महा पालनहार के नाम की।');
INSERT INTO "al_umari" VALUES(57,1,'57:1','अल्लाह की पवित्रता का गान करता है, जो भी आकाशों तथा धरती में है और वह प्रबल, गुणी है।');
INSERT INTO "al_umari" VALUES(57,2,'57:2','उसी का है आकाशों तथा धरती का राज्य। वह जीवन देता है तथा मारता है और वह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(57,3,'57:3','वही प्रथम, वही अन्तिम और प्रत्यक्ष तथा गुप्त है और वह प्रत्येक वस्तु का जानने वाला है।');
INSERT INTO "al_umari" VALUES(57,4,'57:4','उसीने उत्पन्न किया है आकाशों तथा धरती को छः दिनों में, फिर स्थित हो गया अर्श (सिंहासन) पर। वह जानता है उसे, जो प्रवेश करता है धरती में, जो निकलता है उससे, जो उतरता है आकाश से तथा चढ़ता है उसमें और वह तुम्हारे साथ[ है जहाँ भी तुम रहो और अल्लाह जो कुछ तुम कर रहे हो, उसे देख रहा है। ');
INSERT INTO "al_umari" VALUES(57,5,'57:5','उसी का है आकाशों तथा धरती का राज्य और उसी की ओर फेरे जाते हैं सब मामले (निर्णय के लिए)।');
INSERT INTO "al_umari" VALUES(57,6,'57:6','वह प्रवेश करता है रात्रि को दिन में और प्रवेश करता है दिन को रात्रि में तथा वह सीनों के भेदों से पूर्णतः अवगत है।');
INSERT INTO "al_umari" VALUES(57,7,'57:7','तुम सभी ईमान लाओ अल्लाह तथा उसके रसूल पर और व्यय करो उसमें से जिसमें उसने अधिकार दिया है तुम्हें। तो जो लोग ईमान लायेंगे तुममें से तथा दान करेंगे, तो उन्हीं के लिए बड़ा प्रतिफल है।');
INSERT INTO "al_umari" VALUES(57,8,'57:8','और तुम्हें क्या हो गया है कि ईमान नहीं लाते अल्लाह पर, जबकि रसूल[ तुम्हें पुकार रहा है, ताकि तुम ईमान लाओ अपने पालनहार पर, जबकि अल्लाह ले चुका है तुमसे वचन, यदि तुम ईमान वाले हो।');
INSERT INTO "al_umari" VALUES(57,9,'57:9','वही है, जो उतार रहा है अपने भक्त पर खुली आयतें, ताकि वह तुम्हें निकाले अंधेरों से प्रकाश की ओर तथा वास्तव में, अल्लाह तुम्हारे लिए अवश्य करुणामय, दयावान् है।');
INSERT INTO "al_umari" VALUES(57,10,'57:10','और क्या कारण है कि तुम व्यय नहीं करते अल्लाह की राह में, जबकि अल्लाह ही के लिए है आकाशों और धरती का उत्तराधिकार। नहीं बराबर हो सकते तुममें से वे जिन्होंने दान किया (मक्का) की विजय से पहले तथा धर्मयुध्द किया। वही लोग पद में अधिक ऊँचे हैं उनसे, जिन्होंने दान किया उसके पश्चात्[ तथा धर्मयुध्द किया तथा प्रत्येक को अल्लाह ने वचन दिया है भलाई का तथा अल्लाह जो कुछ तुम करते हो, उससे पूर्णतः सूचित है। ');
INSERT INTO "al_umari" VALUES(57,11,'57:11','कौन है, जो ऋण दे अल्लाह को अच्छा ऋण? जिसे वह दुगुना कर दे उसके लिए और उसी के लिए अच्छा प्रतिदान है। ');
INSERT INTO "al_umari" VALUES(57,12,'57:12','जिस दिन तुम देखोगे ईमान वालों तथा ईमान वालियों को कि दौड़ रहा[ होगा उनका प्रकाश उनके आगे तथा उनके दायें। तुम्हें शुभ सूचना है ऐसे स्वर्गों की, बहती हैं जिनमें नहरें, जिनमें तुम सदावासी होगे, वही बड़ी सफलता है। ');
INSERT INTO "al_umari" VALUES(57,13,'57:13','जिस दिन कहेंगे मुनाफ़िक़ पुरुष तथा मुनाफ़िक़ स्त्रियाँ उनसे, जो ईमान लाये कि हमारी प्रतीक्षा करो, हम प्राप्त कर लें तुम्हारे प्रकाश में से कुछ। उनसे कहा जायेगाः तुम पीछे वापस जाओ और प्रकाश की खोज करो।[ फिर बना दी जायेगी उनके बीच एक दीवार, जिसमें एक द्वार होगा। उसके भीतर दया होगी तथा उसके बाहर यातना होगी। ');
INSERT INTO "al_umari" VALUES(57,14,'57:14','वे उन्हें पुकारेंगेः क्या हम (संसार में) तुम्हारे साथ नहीं थे? (वे कहेंगेः) परन्तु तुमने उपद्रव में डाल दिया अपने आपको और प्रतीक्षा[ में रहे तथा संदेह किया और धोखे में रखा तुम्हें तुम्हारी कामनाओं ने। यहाँ तक कि आ पहुँचा अल्लाह का आदेश और धोखे ही में रखा तुम्हें बड़े वंचक (शैतान) ने। ');
INSERT INTO "al_umari" VALUES(57,15,'57:15','तो आज तुमसे कोई अर्थदण्ड नहीं लिया जायेगा और न काफ़िरों से। तुम्हारा आवास नरक है, वही तुम्हारे योग्य है और वह बुरा निवास है।');
INSERT INTO "al_umari" VALUES(57,16,'57:16','क्या समय नहीं आया ईमान वालों के लिए कि झुक जायें उनके दिल अल्लाह के स्मरण (याद) के लिए तथा जो उतरा है सत्य और न हो जायें उनके समान, जिन्हें प्रदान की गयीं पुस्तकें इससे पूर्व, फिर लम्बी अवधि व्यतीत हो गयी उनपर, तो कठोर हो गये उनके दिल[ तथा उनमें अधिक्तर अवज्ञाकारी हैं। ');
INSERT INTO "al_umari" VALUES(57,17,'57:17','जान लो कि अल्लाह ही जीवित करता है धरती को, उसके मरण के पश्चात्, हमने उजागर कर दी हैं तुम्हारे लिए निशानियाँ, ताकि तुम समझो।');
INSERT INTO "al_umari" VALUES(57,18,'57:18','वस्तुतः, दान करने वाले पुरुष तथा दान करने वाली स्त्रियाँ तथा जिन्होंने ऋण दिया है अल्लाह को अच्छा ऋण,[ उसे बढ़ाया जायेगा उनके लिए और उन्हीं के लिए अच्छा प्रतिदान है। ');
INSERT INTO "al_umari" VALUES(57,19,'57:19','तथा जो ईमान लाये अल्लाह और उसके रसूलों[ पर, वही सिद्दीक़ तथा शहीद हैं अपने पालनहार के समीप। उन्हीं के लिए उनका प्रतिफल तथा उनकी दिव्या ज्योति है और जो काफ़िर हो गये और झ़ठलाया हमारी आयतों को, तो वही नारकीय हैं।');
INSERT INTO "al_umari" VALUES(57,20,'57:20','जान लो कि सांसारिक जीवन एक खेल, मनोरंजन, शोभा,[ आपस में गर्व तथा एक-दूसरे से बढ़ जाने का प्रयास है, धनों तथा संतान में। उस वर्षा के समान भा गयी किसानों को जिसकी उपज, फिर वह पक गयी, तो तुम उसे देखने लगे पीली, फिर वह हो जाती है चूर-चूर और परलोक में कड़ी यातना है तथा अल्लाह की क्षमा और प्रसन्नता है और सांसारिक जीवन तो बस धोखे का संसाधन है। ');
INSERT INTO "al_umari" VALUES(57,21,'57:21','एक-दूसरे से आगे बढ़ो अपने पालनहार की क्षमा तथा उस स्वर्ग की ओर, जिसका विस्तार आकाश तथा धरती के विस्तार के[ समान है। जो तैयार की गयी है उनके लिए, जो ईमान लायें अल्लाह और उसके रसूलों पर। ये अल्लाह का अनुग्रह है, वह प्रदान करता है उसे, जिसे चाहता है और अल्लाह बड़ा उदार (दयाशील) है। ');
INSERT INTO "al_umari" VALUES(57,22,'57:22','नहीं पहुँचती कोई आपदा धरती में और न तुम्हारे प्राणों में, परन्तु वह एक पुस्तक में लिखी है, इससे पूर्व की हम उसे उत्पन्न करें[ और ये अल्लाह के लिए अति सरल है। ');
INSERT INTO "al_umari" VALUES(57,23,'57:23','ताकि तुम शोक न करो उसपर, जो तुमसे खो जाये और न इतराओ उसपर, जो तुम्हें प्रदान किया है और अल्लाह प्रेम नहीं करता किसी इतरान, गर्व करने वाले से।');
INSERT INTO "al_umari" VALUES(57,24,'57:24','जो कंजूसी करते हैं और आदेश देते हैं लोगों को कंजूसी करने का तथा जो विमुख होगा, तो निश्चय अल्लाह निस्पृह, सराहनीय है।');
INSERT INTO "al_umari" VALUES(57,25,'57:25','निःसंदेह, हमने भेजा है अपने रसूलों को खुले प्रमाणों के साथ तथा उतारी है उनके साथ पुस्तक तथा तुला (न्याय का नियम), ताकि लोग स्थित रहें न्याय पर तथा हमने उतारा लोहा जिसमें बड़ा बल[ है तथा लोगों के लिए बहुत-से लाभ और ताकि अल्लाह जान ले कि कौन उसकी सहायता करता है तथा उसके रसूलों की, बिना देखे। वस्तुतः, अल्लाह अति शक्तिशाली, प्रभावशाली है। ');
INSERT INTO "al_umari" VALUES(57,26,'57:26','हमने (रसूल बनाकर) भेजा नूह़ को तथा इब्राहीम को और रख दी उनकी संतति में नबुवत (दुतत्व) तथा पुस्तक। तो उनमें से कुछ ने मार्गदर्शन अपनाया और उनमें से बहुत-से अवज्ञाकारी हैं।');
INSERT INTO "al_umari" VALUES(57,27,'57:27','फिर हमने, निरन्तर उनके पश्चात् अपने रसूल भेजे और उनके पश्चात् भेजा मर्यम के पुत्र ईसा को तथा प्रदान की उसे इन्जील और कर दिया उसका अनुसरण करने वालों के दिलों में करुणा तथा दया और संसार[ त्याग को उन्होंने स्वयं बना लिया, हमने नहीं अनिवार्य किया उसे उनके ऊपर। परन्तु अल्लाह की प्रसन्नता के लिए (उन्होंने ऐसा किया), तो उन्होंने नहीं किया उसका पूर्ण पालन। फिर (भी) हमने प्रदान किया उन्हें जो ईमान लाये उनमें से उनका बदला और उनमें से अधिक्तर अवज्ञाकारी हैं। ');
INSERT INTO "al_umari" VALUES(57,28,'57:28','हे लोगो जो ईमान लाये हो! अल्लाह से डरो और ईमान लाओ उसके रसूल पर, वह तुम्हें प्रदान करेगा दोहारा[ प्रतिफल अपनी दया से तथा प्रदान करेगा तुम्हें ऐसा प्रकाश, जिसके साथ तुम चलोगे तथा क्षमा कर देगा तुम्हें और अल्लाह अति क्षमी, दयावान् है। ');
INSERT INTO "al_umari" VALUES(57,29,'57:29','ताकि ज्ञान हो जाये इन बातों से अह्ले[ किताब को कि वह कुछ शक्ति नहीं रखते अल्लाह के अनुग्रह पर और ये कि अनुग्रह अल्लाह ही के हाथ में है। वह प्रदान करता है, जिसे चाहे और अल्लाह बड़े अनुग्रह वाला है। ');
INSERT INTO "al_umari" VALUES(58,1,'58:1','(हे नबी!) अल्लाह ने सुन ली है उस स्त्री की बात, जो आपसे झगड़ रही थी अपने पति के विषय में तथा गुहार रही थी अल्लाह को और अल्लाह सुन रहा था तुम दोनों का वार्तालाप, वास्तव में, वह सब कुछ सुनने-देखने वाला है।');
INSERT INTO "al_umari" VALUES(58,2,'58:2','जो ज़िहार[ करते हैं तुममें से अपनी पत्नियों से, तो वे उनकी माँ नहीं हैं। उनकी माँ तो वे हैं, जिन्होंने उनहें जन्म दिया है और वह बोलते हैं अप्रिय तथा झूठी बात और वास्तव में अल्लाह माफ़ करने वाला, क्षमाशील है। ');
INSERT INTO "al_umari" VALUES(58,3,'58:3','और जो ज़िहार कर लेते हैं अपनी पत्नियों से, फिर वापस लेना चाहते हैं अपनी बात, तो (उसका दण्ड) एक दास मुक्त करना है, इससे पूर्व कि एक-दूसरे को हाथ लगायें।[ इसीकी तुम्हें शिक्षा दी जा रही है और अल्लाह उससे जो तुम करते हो, भली-भाँति सूचित है। ');
INSERT INTO "al_umari" VALUES(58,4,'58:4','फिर जो (दास) न पाये, तो दो महीने निरन्तर रोज़ा (व्रत) रखना है, इससे पूर्व की एक-दूसरे को हाथ लगाये। फिर जो सकत न रखे, तो साठ निर्धनों को भोजन कराना है। ये आदेश इसलिए है, ताकि तुम ईमान लाओ अल्लाह तथा उसके रसूल पर और ये अल्लाह की सीमायें हैं तथा काफ़िरों के लिए दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(58,5,'58:5','वास्तव में, जो विरोध करते हैं अल्लाह तथा उसके रसूल का, वे अपमानित कर दिये जायेंगे, जैसे अपमानित कर दिये गये, जो इनसे पूर्व हुए और हमने उतार दी हैं खुली आयतें और काफ़िरों के लिए अपमानकारी यातना है।');
INSERT INTO "al_umari" VALUES(58,6,'58:6','जिस दिन जीवित करेगा उन सब को अल्लाह, तो उन्हें सूचित कर देगा उनके कर्मों से। गिन रखा है उसे अल्लाह ने और वह भूल गये हैं उसे और अल्लह प्रत्येक वस्तु पर गवाह है।');
INSERT INTO "al_umari" VALUES(58,7,'58:7','क्या आपने नहीं देखा कि अल्लाह जानता है, जो (भी) आकाशों तथा धरती में है? नहीं होती किसी तीन की काना-फूसी, परन्तु वह उनका चौथा होता है और न पाँच की, परन्तु वह उनका छठा होता है और न इससे कम की और न इससे अधिक की, परन्तु वह उनके साथ होता[ है, वे जहाँ भी हों। फिर वह उन्हें सूचित कर देगा उनके कर्मों से प्रलय के दिन। वास्तव में, अल्लाह प्रत्येक वस्तु से भली-भाँति अवगत है। ');
INSERT INTO "al_umari" VALUES(58,8,'58:8','क्या आपने नहीं देखा उन्हें, जो रोके गये हैं काना-फूसी[ से? फिर (भी) वही करते हैं जिससे रोके गये हैं तथा काना-फूसी करते हैं पाप और अत्याचार तथा रसूल की अवज्ञा की और वे जब आपके पास आते हैं, तो आपको ऐसे (शब्द से) सलाम करते हैं, जिससे आपपर सलाम नहीं भेजा अल्लाह ने तथा कहते हैं अपने मनों में: क्यों अल्लाह हमें यातना नहीं देता उसपर जो हम कहते हैं? पर्याप्त है उन्हें नरक, जिसमें वे प्रवेश करेंगे, तो बुरा है उनका ठिकाना।');
INSERT INTO "al_umari" VALUES(58,9,'58:9','हे लोगो जो ईमान लाये हो! जब तुम काना-फूसी करो, तो काना-फूसी न करो पाप तथा अत्याचार एवं रसूल की अवज्ञा की और काना फूसी करो पुण्य तथा सदाचार की और डरते रहो अल्लाह से, जिसकी ओर ही तुम एकत्र किये जोओगे।');
INSERT INTO "al_umari" VALUES(58,10,'58:10','वास्तव में, काना-फूसी शैतानी काम हैं, ताकि वो उदासीन हों,[ जो ईमान लाये। जबकि नहीं है वह हानिकर उन्हें कुछ, परन्तु अल्लाह की अनुमति से और अल्लाह ही पर चाहिये कि भरोसा करें ईमान वाले। ');
INSERT INTO "al_umari" VALUES(58,11,'58:11','हे ईमान वालो! जब तुमसे कहा जाये कि विस्तार कर दो अपनी सभाओं में, तो विस्तार[ कर दो, विस्तार कर देगा अल्लाह तुम्हारे लिए तथा जब कहा जाये कि सुकड़ जाओ, तो सुकड़ जाओ। ऊँचा कर देगा अल्लाह उन्हें, जो ईमान लाये हैं तुममें से तथा जिन्हें ज्ञान प्रदान किया गया है, कई श्रेणियाँ तथा अल्लाह उससे जो तुम करते हो, भली-भाँति अवगत है।');
INSERT INTO "al_umari" VALUES(58,12,'58:12','हे ईमान वालो! जब तुम अकेले बात करो रसूल से, तो बात करने से पहले कुछ दान करो।[ ये तुम्हारे लिए उत्तम तथा अधिक पवित्र है। फिर यदि तुम (दान के लिए कुछ) न पाओ, तो अल्लाह अति क्षमाशील, दयावान् है। ');
INSERT INTO "al_umari" VALUES(58,13,'58:13','क्या तुम (इस आदेश से) डर गये कि एकान्त में बात करने से पहले कुछ दान कर दो? फिर जब तुमने ऐसा नहीं किया, तो स्थापना करो नमाज़ की तथा ज़कात दो और आज्ञा पालन करो अल्लाह तथा उसके रसूल की और अल्लाह सूचित है उससे, जो कुछ तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(58,14,'58:14','क्या आपने उन्हें देखा[ जिन्होंने मित्र बना लिया उस समुदाय को, जिसपर क्रोधित हो गया अल्लाह? न वे तुम्हारे हैं और न उनके और वे शपथ लेते हैं झूठी बात पर, जान-बूझ कर। ');
INSERT INTO "al_umari" VALUES(58,15,'58:15','तैयार की है अल्लाह ने उनके लिए कड़ी यातना, वास्तव में, वह बुरा है, जो वे कर रहे हैं।');
INSERT INTO "al_umari" VALUES(58,16,'58:16','उन्होंने बना लिया अपनी शपथों को एक ढाल। फिर रोक दिया (लोगों को) अल्लाह की रोह से, तो उन्हीं के लिए अपमानकारी यातना है।');
INSERT INTO "al_umari" VALUES(58,17,'58:17','कदापि नहीं काम आयेंगे उनके धन और न उनकी संतान अल्लाह के समक्ष कुछ। वही नारकी हैं, वे उसमें सदावासी होंगे।');
INSERT INTO "al_umari" VALUES(58,18,'58:18','जिस दिन खड़ा करेगा उन्हें अल्लाह, तो वे शपथ लेंगे अल्लाह के समक्ष, जैसे वे शपथ ले रहे हैं तुम्हारे समक्ष और वे समझ रहे हैं कि वे कुछ (तर्क)[ पर हैं। सुन लो! वास्तव में वही झूठे हैं। ');
INSERT INTO "al_umari" VALUES(58,19,'58:19','छा[ गया है उनपर शैतान और भुला दी है उन्हें अल्लाह की याद। यही शैतान की सेना है। सुन लो! शैतान की सेना ही क्षतिग्रस्त होने वाली है। ');
INSERT INTO "al_umari" VALUES(58,20,'58:20','वास्तव में, जो विरोध करते हैं अल्लाह तथा उसके रसूल का, वही अपमानितों में से हैं।');
INSERT INTO "al_umari" VALUES(58,21,'58:21','लिख रखा है अल्लाह ने कि अवश्य मैं प्रभावशाली (विजयी) रहूँगा[ तथा मेरे रसूल। वास्तव में, अल्लाह अति शक्तिशाली, प्रभावशाली है। ');
INSERT INTO "al_umari" VALUES(58,22,'58:22','आप नहीं पायेंगे उन्हें, जो ईमान रखते हों अल्लाह तथा अन्त-दिवस (प्रलय) पर कि वे मैत्री करते हों उनसे, जिन्होंने विरोध किया अल्लाह और उसके रसूल का, चाहे वे उनके पिता हों, उनके पुत्र, उनके भाई अथवा उनके परिजन[ हों। वही हैं, लिख दिया है (अल्लाह ने) जिनके दिलों में ईमान और समर्थन दिया है जिन्हें अपनी ओर से रूह़ (आत्मा) द्वारा तथा प्रवेश देगा उन्हें ऐसे स्वर्गों में, बहती हैं जिनमें नहरें, वे सदावासी होंगे जिनमें। प्रसन्न हो गया अल्लाह उनसे तथा वे प्रसन्न हो गये उससे। वह अल्लाह का समूह है। सुन लो, अल्लाह का समूह ही सफल होने वाला है। ');
INSERT INTO "al_umari" VALUES(59,1,'59:1','अल्लाह की पवित्रता का गान किया है उसने, जो भी आकाशों तथा धरती में है और वह प्रभुत्वशाली, गुणी है।');
INSERT INTO "al_umari" VALUES(59,2,'59:2','वही है, जिसने अह्ले किताब में से काफ़िरों को उनके घरों से पहले ही आक्रमण में निकाल दिया। तुमने नहीं समझा था कि वे निकल जायेंगे और उन्होंने समझा था कि रक्षक होंगे उनके दुर्ग[ अल्लाह से। तो आ गया उनके पास अल्लाह (का निर्णय)। ऐसा उन्होंने सोचा भी न था तथा डाल दिया उनके दिलों में भय। वे उजाड़ रहे थे अपने घरों को अपने हाथों से तथा ईमान वालों के हाथों से। तो शिक्षा लो, हे आँख वालो!');
INSERT INTO "al_umari" VALUES(59,3,'59:3','और यदि अल्लाह ने न लिख दिया होता उन (के भाग्य में) देश निकाला, तो उन्हें यातना दे देता संसार (ही) में तथा उनके लिए अख़िरत (परलोक) में नरक की यातना है।');
INSERT INTO "al_umari" VALUES(59,4,'59:4','ये इसलिए कि उन्होंने विरोध किया अल्लाह तथा उसके रसूल का और जो विरोध करेगा अल्लाह का, तो निश्चय अल्लाह कड़ी यातना देने वाला है।');
INSERT INTO "al_umari" VALUES(59,5,'59:5','(हे मुसलमानो!) तुमने नहीं काटा[ कोई खजूर का वृक्ष और न छोड़ा उसे खड़ा अपने तने पर, परन्तु ये सब अल्लाह के आदेश से हुआ और ताकि वह अपमानित करे पथभ्रष्टों को। ');
INSERT INTO "al_umari" VALUES(59,6,'59:6','और जो धन दिला दिया अल्लाह ने अपने रसूल को उनसे, तो नहीं दौड़ाये तुमने उसके लिए घोड़े और न ऊँट। परन्तु अल्लाह प्रभुत्व प्रदान कर देता है अपने रसूल को, जिसपर चाहता है तथा अल्लाह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(59,7,'59:7','अल्लाह ने जो धन दिलाया है अपने रसूल को इस बस्ती वालों[ से, वह अल्लाह, रसूल, (आपके) समीपवर्तियों, अनाथों, निर्धनों तथा यात्रियों के लिए है; ताकि वह (धन) फिरता न रह जाये तुम्हारे धनवानों के बीच और जो प्रदान कर दे रसूल, तुम उसे ले लो और रोक दें तुम्हें जिससे, तुम उससे रुक जाओ तथा अल्लाह से डरते रहो, निश्चय अल्लाह कड़ी यातना देने वाला है।');
INSERT INTO "al_umari" VALUES(59,8,'59:8','उन निर्धन मुहाजिरों के लिए है, जो निकाल दिये गये अपने घरों तथा धनों से। वे चाहते हैं अल्लाह का अनुग्रह तथा प्रसन्नता और सहायता करते हैं अल्लाह तथा उसके रसूल की, यही सच्चे हैं।');
INSERT INTO "al_umari" VALUES(59,9,'59:9','तथा उन लोगों[ के लिए (भी), जिन्होंने आवास बना लिया इस घर (मदीना) को तथा उन (मुहाजिरों के आने) से पहले ईमान लाये, वे प्रेम करते हैं उनसे, जो हिजरत करके आ गये उनके यहाँ और वे नहीं पाते अपने दिलों में कोई आवश्यक्ता उसकी, जो उन्हें दिया जाये और प्राथमिक्ता देते हैं दूसरों को अपने ऊपर, चाहे स्वयं भूखे हों और जो बचा लिये गये अपने मन की तंगी से, तो वही सफल होने वाले हैं।');
INSERT INTO "al_umari" VALUES(59,10,'59:10','और जो आये उनके पश्चात्, वे कहते हैं: हे हमारे पालनहार! हमें क्षमा कर दे तथा हमारे उन भाईयों को, जो हमसे पहले ईमान लाये और न रख हमारे दिलों में कोई बैर उनके लिए, जो ईमान लाये। हे हमारे पालनहार! तू अति करुणामय, दयावान् है।');
INSERT INTO "al_umari" VALUES(59,11,'59:11','क्या आपने उन्हें[ नहीं देखा, जो मुनाफ़िक़ (अवसरवादी) हो गये और कहते हैं अपने अह्ले किताब भाईयों से कि यदि तुम्हें देश निकाला दिया गया, तो हम अवश्य निकल जायेंगे तुम्हारे साथ और नहीं मानेंगे तुम्हारे बारे में किसी की (बात) कभी और यदि तुमसे युध्द हुआ तो हम अवश्य तुम्हारी सहायता करेंगे तथा अल्लाह गवाह है कि वे झूठे हैं। ');
INSERT INTO "al_umari" VALUES(59,12,'59:12','यदि वे निकाले गये तो ये उनके साथ नहीं निकलेंगे और यदि उनसे युध्द हो, तो वे उनकी सहायता नहीं करेंगे और यदि उनकी सहायता की (भी,) तो अवश्य पीठ दिखा देंगे, फिर कहीं से कोई सहायता नहीं पायेंगे।');
INSERT INTO "al_umari" VALUES(59,13,'59:13','निश्चय अधिक भय है तुम्हारा उनके दिलों में अल्लाह (के भय) से। ये इसलिए कि वे समझ-बूझ नहीं रखते।');
INSERT INTO "al_umari" VALUES(59,14,'59:14','वे नहीं युध्द करेंगे तुमसे एकत्र होकर, परन्तु ये कि दुर्ग बंद बस्तियों में हों अथवा किसी दीवार की आड़ से। उनका युध्द आपस में बहुत कड़ा है। आप उन्हें एकत्र समझते हैं, जबकि उनके दिल अलग अलग हैं। ये इसलिए कि वे निर्बोध होते हैं।');
INSERT INTO "al_umari" VALUES(59,15,'59:15','उनके समान, जो उनसे कुछ ही पूर्व चख चुके[ हैं अपने किये का स्वाद और इनके लिए दुःखदायी यातना है। ');
INSERT INTO "al_umari" VALUES(59,16,'59:16','(उनका उदाहरण) शैतान जैसा है कि वह कहता है मनुष्य से कि कुफ़्र कर, फिर जब वह काफ़िर हो गया, तो कह दिया कि मैं तुझसे विरक्त (अलग) हूँ। मैं तो डरता हूँ अल्लाह, सर्वलोक के पालनहार से।');
INSERT INTO "al_umari" VALUES(59,17,'59:17','तो हो गया उन दोनों का दुष्परिणाम ये कि वे दोनों नरक में सदावासी रहेंगे और यही है अत्याचारियों का कुफल।');
INSERT INTO "al_umari" VALUES(59,18,'59:18','हे लोगो जो ईमान लाये हो! अल्लाह से डरो और देखना चाहिये प्रत्येक को कि उसने क्या भेजा है कल के लिए तथा डरते रहो अल्लाह से, निश्चय अल्लाह सूचित है उससे, जो तुम करते हो।');
INSERT INTO "al_umari" VALUES(59,19,'59:19','और न हो जाओ उनके समान, जो भूल गये अल्लाह को, तो भुला दिया (अल्लाह ने) उन्हें अपने आपसे, यही अवज्ञाकारी हैं।');
INSERT INTO "al_umari" VALUES(59,20,'59:20','नहीं बराबर हो सकते नारकी तथा स्वर्गी। स्वार्गी ही वास्तव में सफल होने वाले हैं।');
INSERT INTO "al_umari" VALUES(59,21,'59:21','यदि हम अवतरित करते इस क़ुर्आन को किसी पर्वत पर, तो आप उसे देखते कि झुका जा रहा है तथा कण-कण होता जा रहा है, अल्लाह के भय से और इन उदाहरणों का वर्णन हम लोगों के लिए कर रहे हैं, ताकि वे सोच-विचार करें। वह खुले तथा छुपे का जानने वाला है। वही अत्यंत कृपाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(59,22,'59:22','वह अल्लाह ही है, जिसके अतिरिक्त कोई (सत्य) पूज्य नहीं है।');
INSERT INTO "al_umari" VALUES(59,23,'59:23','वह अल्लाह ही है, जिसके अतिरिक्त नहीं है[ कोई सच्चा वंदनीय। वह सबका स्वामी, अत्यंत पवित्र, सर्वथा शान्ति प्रदान करने वाला, रक्षक, प्रभावशाली, शक्तिशाली बल पूर्वक आदेश लागू करने वाला, बड़ाई वाला है। पवित्र है अल्लाह उससे, जिसे वे (उसका) साझी बनाते हैं। ');
INSERT INTO "al_umari" VALUES(59,24,'59:24','वही अल्लाह है, पैदा करने वाला, बनाने वाला, रूप देने वाला। उसी के लिए शुभ नाम हैं, उसकी पवित्रता का वर्णन करता है जो (भी) आकाशों तथा धरती में है और वह प्रभावशाली, ह़िक्मत वाला है।');
INSERT INTO "al_umari" VALUES(60,1,'60:1','हे लोगो जो ईमान लाये हो! मेरे शत्रुओं तथा अपने शत्रुओं को मित्र ने बनाओ। तुम संदेश भेजते हो उनकी ओर मैत्री[ का, जबकि उन्होंने कुफ़्र किया है उसका, जो तुम्हारे पास सत्य आया है। वे देश निकाला देते हैं रसूल को तथा तुमको इस कारण कि तुम ईमान लाये हो अल्लाह, अपने पालनहार पर? यदि तुम निकले हो जिहाद के लिए मेरी राह में और मेरी प्रसन्नता की खोज के लिए, तो गुप्त रूप से उन्हें मैत्री का संदेश भेजते हो? जबकि मैं भली-भाँति जानता हूँ उसे, जो तुम छुपाते हो और जो खुलकर करते हो? तथा जो करेगा ऐसा, तो निश्चय वह कुपथ हो गया सीधी राह से। ');
INSERT INTO "al_umari" VALUES(60,2,'60:2','और यदि वश में पा जायें तुम्हें, तो तुम्हारे शत्रु बन जायें तथा तुम्हें अपने हाथों और ज़ुबानों से दुःख पहुँचायें और चाहने लगेंगे कि तुम (फिर) काफ़िर हो जाओ।');
INSERT INTO "al_umari" VALUES(60,3,'60:3','तुम्हें लाभ नहीं देंगे तुम्हारे सम्बंधी और न तुम्हारी संतान प्रलय के दिन। वह (अल्लाह) अलगाव कर देगा तुम्हारे बीच और अल्लाह जो कुछ तुम कर रहे हो, उसे देख रहा है।');
INSERT INTO "al_umari" VALUES(60,4,'60:4','तुम्हारे लिए इब्राहीम तथा उसके साथियों में एक अच्छा आदर्श है। जबकि उन्होंने अपनी जाति से कहाः निश्चय हम विरक्त हैं तुमसे तथा उनसे, जिनकी तुम इबादत (वंदना) करते हो अल्लाह के अतिरिक्त। हमने तुमसे कुफ़्र किया। खुल चुका है बैर हमारे तथा तुम्हारे बीच और क्रोध सदा के लिए। जब तक तुम ईमान न लाओ अकेले अल्लाह पर, परन्तु इब्राहीम का (ये) कथन अपने पिता से कि मैं अवश्य तेरे लिए क्षमा की प्रार्थना[ करूँगा और मैं नहीं अधिकार रखता हूँ अल्लाह के समक्ष कुछ। हे हमारे पालनहार! हमने तेरे ही ऊपर भरोसा किया और तेरी ही ओर ध्यान किया है और तेरी ही ओर फिर आना है। ');
INSERT INTO "al_umari" VALUES(60,5,'60:5','हे हमारे पालनहार! हमें न बना परीक्षा[ (का साधन) काफ़िरों के लिए और हमें क्षमा कर दे, हे हमारे पालनहार! वास्तव में, तू ही प्रभुत्वशाली, गुणी है। ');
INSERT INTO "al_umari" VALUES(60,6,'60:6','निःसंदेह, तुम्हारे लिए उनमें एक अच्छा आदर्श है, उसके लिए, जो आशा रखता हो अल्लाह तथा अन्तिम दिवस (प्रलय) की और जो विमुख हो, तो निश्चय अल्लाह निस्पृह प्रशंसित है।');
INSERT INTO "al_umari" VALUES(60,7,'60:7','कुछ दूर नहीं कि अल्लाह बना दे तुम्हारे बीच तथा उनके बीच, जिनसे तुम बैर रखते हो प्रेम[ और अल्लाह बड़ा सामर्थ्यवान है और अल्लाह अति क्षमाशील, दयावान है। ');
INSERT INTO "al_umari" VALUES(60,8,'60:8','अल्लाह तुम्हें नहीं रोकता उनसे, जिन्होंने तुमसे युध्द न किया हो धर्म के विषय में और न बहिष्कार किया हो तुम्हारा, तम्हारे देश से, इससे कि तुम उनसे अच्छा व्यवहार करो और न्याय करो उनसे। वास्तव में अल्लाह प्रेम करता है न्यायकारियों[ से। ');
INSERT INTO "al_umari" VALUES(60,9,'60:9','तुम्हें अल्लाह बस उनसे रोकता है, जिन्होंने युध्द किया हो तुमसे धर्म के विषय में तथा बहिष्कार किया हो तुम्हारा तुम्हारे घरों से और सहायता की हो, तुम्हारा बहिष्कार कराने में कि तुम मैत्री रखो उनसे और जो मैत्री करेंगे उनसे, तो वही अत्याचारी हैं।');
INSERT INTO "al_umari" VALUES(60,10,'60:10','हे ईमान वालो! जब तुम्हारे पास मुसलमान स्त्रियाँ हिजरत करके आयें, तो उनकी परीक्षा ले लिया करो। अल्लाह अधिक जानता है उनके ईमान को, फिर यदि तुम्हें ये ज्ञान हो जाये कि वे ईमान वालियाँ हैं, तो उन्हें वापस न करो[ काफ़िरों की ओर। न वे औरतें ह़लाल (वैध) हैं उनके लिए और न वे (काफ़िर) ह़लाल (वैध) हैं उन औरतों के लिए। और चुका दो उन (काफ़िरों) को, जो उन्होंने ख़र्च किया हो तथा तुमपर कोई दोष नहीं है कि विवाह कर लो उनसे, जब दे दो उन्हें उनका महर (स्त्री उपहार) तथा न रखो काफ़िर स्त्रियों को अपने विवाह में तथा माँग लो जो तुमने ख़र्च किया हो और चाहिये कि वे (काफ़िर) माँग लें, जो उन्होंने ख़र्च किया हो। ये अल्लाह का आदेश है, वह निर्णय कर रहा है तुम्हारे बीच तथा अल्लाह सब जानने वाला, गुणी है।');
INSERT INTO "al_umari" VALUES(60,11,'60:11','और यदि तुम्हारे हाथ से निकल जाये तुम्हारी कोई पत्नी काफ़िरों की ओर और तुम्हें बदले[ का अवसर मिल जाये, तो चुका दो उन्हें, जिनकी पत्नियाँ चली गई हैं, उसके बराबर, जो उन्होंने ख़र्च किया है तथा डरते रहो उस अल्लाह से जिस पर तुम ईमान रखते हो। ');
INSERT INTO "al_umari" VALUES(60,12,'60:12','हे नबी! जब आयें आपके पास ईमान वालियाँ, ताकि[ वचन दें आपको इस बात पर कि वे साझी नहीं बनायेंगी अल्लाह का किसी को, न चोरी करेंगी, न व्यभिचार करेंगी, न वध करेंगी अपनी संतान को, न कोई ऐसा आरोप (कलंक) लगायेंगी जिसे उन्होंने घड़ लिया हो अपने हाथों तथा पैरों के आगे और न अवज्ञा करेंगी आपकी किसी भले काम में, तो आप वचन ले लिया करें उनसे तथा क्षमा की प्रार्थना करें उनके लिए अल्लाह से। वास्तव में अल्लाह अति क्षमाशील तथा दयावान् है। ');
INSERT INTO "al_umari" VALUES(60,13,'60:13','हे ईमान वालो! तुम उन लोगों को मित्र न बनाओ, क्रोधित हो गया है अल्लाह, जिनपर। वे निराश हो चुके हैं आख़िरत[ (परलोक) से, उसी प्रकार, जैसे काफ़िर समाधियों में पड़े हुए लोगों (के जीवित होने) से निराश हैं। ');
INSERT INTO "al_umari" VALUES(61,1,'61:1','अल्लाह की पवित्रता का गान करती है, जो वस्तु आकाशों तथा धरती में है और वह प्रभुत्वशाली, गुणी है।');
INSERT INTO "al_umari" VALUES(61,2,'61:2','हे ईमान वालो! तुम वह बात क्यों कहते हो जो करते नहीं?');
INSERT INTO "al_umari" VALUES(61,3,'61:3','अत्यंत अप्रिय है अल्लाह को तुम्हारी वह बात कहना, जिसे तुम (स्वयं) करते नहीं।');
INSERT INTO "al_umari" VALUES(61,4,'61:4','निःसंदेह अल्लाह प्रेम करता है उनसे, जो युध्द करते हैं उसकी राह में पंक्तिबंद होकर, जैसे कि वह सीसा पिलाई दीवार हों।');
INSERT INTO "al_umari" VALUES(61,5,'61:5','तथा याद करो जब कहा मूसा ने अपनी जाति सेः हे मेरे समुदाय! तुम क्यों दुःख देते हो मुझे जबकि तुम जानते हो कि मैं अल्लाह का रसूल हूँ तुम्हारी ओर? फिर जब वह टेढ़े ही रह गये, तो टेढ़े कर दिये अल्लाह ने उनके दिल और अल्लाह संमार्ग नहीं दिखाता उल्लंघनकारियों को।');
INSERT INTO "al_umari" VALUES(61,6,'61:6','तथा याद करो जब कहा मर्यम के पुत्र ईसा नेः हे इस्राईल की संतान! मैं तुम्हारी ओर रसूल हूँ और पुष्टि करने वाला हूँ उस तौरात की जो मुझसे पूर्व आयी है तथा शुभ सूचना देने वाला हूँ एक रसूल की, जो आयेगा मेरे पश्चात्, जिसका नाम अह़्मद है। फिर जब वह आ गये उनके पास खुले प्रमाणों को लेकर, तो उन्होंने कह दिया कि ये तो खुला जादू है।');
INSERT INTO "al_umari" VALUES(61,7,'61:7','और उससे अधिक अत्याचारी कौन होगा, जो झूठ घड़े अल्लाह पर, जबकि वह बुलाया जा रहा हो इस्लाम की ओर और अल्लाह मार्गदर्शन नहीं देता अत्याचारी जाति को।');
INSERT INTO "al_umari" VALUES(61,8,'61:8','वे चाहते हैं कि बुझा दें अल्लाह के प्रकाश को अपने मुखों से तथा अल्लाह पूरा करने वाला है अपने प्रकाश को, यद्यपि बुरा लगे काफ़िरों को।');
INSERT INTO "al_umari" VALUES(61,9,'61:9','वही है, जिसने भेजा है अपने रसूल को संमार्ग तथा सत्धर्म के साथ ताकि प्रभावित कर दे उसे प्रत्येक धर्म पर, चाहे बुरा लगे मुश्रिकों को।');
INSERT INTO "al_umari" VALUES(61,10,'61:10','हे ईमान वालो! क्या मैं बता दूँ तुम्हें ऐसा व्यापार, जो बचा ले तुम्हें दुःखदायी यातना से?');
INSERT INTO "al_umari" VALUES(61,11,'61:11','तुम ईमान लाओ अल्लाह तथा उसके रसूल पर और जिहाद करो अल्लाह की राह में अपने धनों और प्राणों से, यही तुम्हारे लिए उत्तम है, यदि तुम जानो।');
INSERT INTO "al_umari" VALUES(61,12,'61:12','वह क्षमा कर देगा तुम्हारे पाप और प्रवेश देगा तुम्हें ऐसे स्वर्गों में, बहती हैं जिनमें नहरें तथा स्वच्छ घरों में स्थायी स्वर्गों में। यही बड़ी सफलता है।');
INSERT INTO "al_umari" VALUES(61,13,'61:13','और एक अन्य (प्रदान) जिससे तुम प्रेम करते हो। वह अल्लाह की सहायता तथा शीघ्र विजय है तथा शुभ सूचना सुना दो ईमान वालों को।');
INSERT INTO "al_umari" VALUES(61,14,'61:14','हे ईमान वालो! तुम बन जाओ अल्लाह (के धर्म) के सहायक, जैसे मर्यम के पुत्र ईसा ने ह़वारियों से कहा था कि कौन मेरा सहायक है अल्लाह (के धर्म के प्रचार में)? तो ह़वारियों ने कहाः हम हैं अल्लाह के (धर्म के) सहायक। तो ईमान लाया इस्राईलियों का एक समूह और कुफ़्र किया दूसरे समूह ने। तो हमने समर्थन दिया उनको, जो ईमान लाये, उनके शत्रु के विरुध्द, तो वही विजयी रहे।');
INSERT INTO "al_umari" VALUES(62,1,'62:1','अल्लाह की पवित्रता का वर्णन करती हैं, वह सब चीज़ें, जो आकाशों तथा धरती में हैं। जो अधिपति, अति पवित्र, प्रभावशाली, गुणी (दक्ष) है।');
INSERT INTO "al_umari" VALUES(62,2,'62:2','वही है, जिसने निरक्षरों[ में एक रसूल भेजा उन्हीं में से। जो पढ़कर सुनाते हैं उन्हें अल्लाह की आयतें और पवित्र करते हैं उन्हें तथा शिक्षा देते हैं उन्हें पुस्तक (क़ुर्आन) तथा तत्वदर्शिता (सुन्नत) की। यद्यपि वे इससे पूर्व खुले कुपथ में थे।');
INSERT INTO "al_umari" VALUES(62,3,'62:3','तथा दूसरों के लिए भी उनमें से, जो उनसे अभी नहीं[ मिले हैं। वह अल्लाह प्रभुत्वशाली, गुणी है। ');
INSERT INTO "al_umari" VALUES(62,4,'62:4','ये[ अल्लाह का अनुग्रह है, जिसे वह प्रदान करता है उसे, जिसे वह चाहता है और अल्लाह बड़े अनुग्रह वाला है। ');
INSERT INTO "al_umari" VALUES(62,5,'62:5','उनकी दशा जिनपर तौरात का भार रखा गया, फिर तदानुसार कर्म नहीं किया, उस गधे के समान है, जिसके ऊपर पुस्तकें[ लदी हुई हों। बुरा है उस जाति का उदाहरण, जिन्होंने झुठला दिया अल्लाह की आयतों को और अल्लाह मार्गदर्शन नहीं देता अत्याचारियों को। ');
INSERT INTO "al_umari" VALUES(62,6,'62:6','आप कह दें कि हे यहूदियो! यदि तुम समझते हो कि तुम ही अल्लाह के मित्र हो अन्य लोगों के अतिरिक्त, तो कामना करो मरण की यदि तुन सच्चे[ हो? ');
INSERT INTO "al_umari" VALUES(62,7,'62:7','तथा वे अपने किये हुए करतूतों के कारण, कदापि उसकी कामना नहीं करेंगे और अल्लाह भली-भाँति अवगत है अत्याचारियों से।');
INSERT INTO "al_umari" VALUES(62,8,'62:8','आप कह दें कि जिस मौत से तुम भाग रहे हो, वह अवश्य तुमसे मिलकर रहेगी। फिर तुम अवश्य फेर दिये जाओगे परोक्ष (छुपे) तथा प्रत्यक्ष (खुले) के ज्ञानी की ओर। फिर वह तुम्हें सूचित कर देगा उससे, जो तुम करते हो।[ ');
INSERT INTO "al_umari" VALUES(62,9,'62:9','हे ईमान वालो! जब अज़ान दी जाये नमाज़ के लिए जुमुआ के दिन, तो दौड़[ जाओ अल्लाह की याद की ओर तथा त्याग दो क्रय-विक्रय। ये उत्तम है तुम्हारे लिए, यदि तुम जानो।');
INSERT INTO "al_umari" VALUES(62,10,'62:10','फिर जब नमाज़ हो जाये, तो फैल जाओ धरती में तथा खोज करो अल्लाह के अनुग्रह की तथा वर्णन करते रहो अल्लाह का अत्यधिक, ताकि तुम सफल हो जाओ।');
INSERT INTO "al_umari" VALUES(62,11,'62:11','और जब वे देख लेते हैं कोई व्यापार अथवा खेल, तो उसकी ओर दौड़ पड़ते हैं।[ तथा आपको छोड़ देते हैं खड़े। आप कह दें कि जो कुछ अल्लाह के पास है, वह उत्तम है खेल तथा व्यापार से और अल्लाह सर्वोत्तम जीविका प्रदान करने वाला है। ');
INSERT INTO "al_umari" VALUES(63,1,'63:1','जब आते हैं आपके पास मुनाफ़िक़, तो कहते हैं कि हम साक्ष्य (गवाही) देते हैं कि वास्तव में आप अल्लाह के रसूल हैं तथा अल्लाह जानता है कि वास्तव में आप अल्लाह के रसूल हैं और अल्लाह गवाही देता है कि मुनाफ़िक़ निश्चय झूठे[ हैं। ');
INSERT INTO "al_umari" VALUES(63,2,'63:2','उन्होंने बना रखा है अपनी शपथों को एक ढाल और रुक गये अल्लाह की राह से। वास्तव में, वे बड़ा दुष्कर्म कर रहे हैं।');
INSERT INTO "al_umari" VALUES(63,3,'63:3','ये सब कुछ इस कारण है कि वे ईमान लाये, फिर कुफ़्र कर गये, तो मुहर लगा दी अल्लाह ने उनके दिलों पर, अतः, वे समझते नहीं।');
INSERT INTO "al_umari" VALUES(63,4,'63:4','और यदि आप उन्हें देखें, तो आपको भा जायें उनके शरीर और यदि वे बात करें, तो आप सुनने लगें उनकी बात, जैसे कि वे लकड़ियाँ हों दीवार के सहारे लगायी[ हुईं। वे प्रत्येक कड़ी ध्वनि को अपने विरुध्द समझते हैं। वही शत्रु हैं, आप उनसे सावधान रहें। अल्लाह उन्हें नाश करे, वे किधर फिरे जा रहे हैं!');
INSERT INTO "al_umari" VALUES(63,5,'63:5','जब उनसे कहा जाता है कि आओ, ताकि क्षमा की प्रार्थना करें तुम्हारे लिए अल्लाह के रसूल, तो मोड़ लेते हैं अपने सिर तथा आप उन्हें देखते हैं कि वे रुक जाते हैं अभिमान (घमंड) करते हुए।');
INSERT INTO "al_umari" VALUES(63,6,'63:6','हे नबी! उनके समीप समान है कि आप क्षमा की प्रार्थना करें उनके लिए अथवा क्षमा की प्रार्थना न करें उनके लिए। कदापि नहीं क्षमा करेगा अल्लाह उन्हें। वास्तव में अल्लाह सुपथ नहीं दिखाता है अवज्ञाकारियों को।');
INSERT INTO "al_umari" VALUES(63,7,'63:7','यही वो लोग हैं, जो कहते हैं कि मत ख़र्च करो उनपर, जो अल्लाह के रसूल के पास रहते हैं, ताकि वे बिखर जायें। जबकि अल्लाह ही के अधिकार में हैं आकाशों तथा धरती के सभी कोष (ख़ज़ाने)। परन्तु मुनाफ़िक़ समझते नहीं हैं।');
INSERT INTO "al_umari" VALUES(63,8,'63:8','वे कहते हैं कि यदि हम वापस पहुँच गये मदीना तक, तो निकाल[ देगा सम्मानित उससे अपमानित को। जबकि अल्लाह ही के लिए सम्मान हैं एवं उसके रसूल तथा ईमान वालों के लिए। परन्तु मुनाफ़िक़ जानते नहीं। ');
INSERT INTO "al_umari" VALUES(63,9,'63:9','हे ईमान वालो! तुम्हें अचेत न करें तुम्हारे धन तथा तुम्हारी संतान अल्लाह के स्मर्ण (याद) से और जो ऐसा करेंगे, वही क्षति ग्रस्त हैं।');
INSERT INTO "al_umari" VALUES(63,10,'63:10','तथा दान करो उसमें से, जो प्रदान किया है हमने तुम्हें, इससे पूर्व कि आ जाये तुममें से किसी के मरण का[ समय, तो कहे कि मेरे पालनहार! क्यों नहीं अवसर दे दिया मुझे कुछ समय का। ताकि मैं दान करता तथा सदाचारियों में हो जाता। ');
INSERT INTO "al_umari" VALUES(63,11,'63:11','और कदापि अल्लाह अवसर नहीं देता किसी प्राणी को, जब आ जाये उसका निर्धारित समय और अल्लाह भली-भाँति सूचित है उससे, जो कुछ तुम कर रहे हो।');
INSERT INTO "al_umari" VALUES(64,1,'64:1','अल्लाह की पवित्रता वर्णन करती है प्रत्येक चीज़, जो आकाशों में है तथा जो धरती में है। उसी का राज्य है और उसी के लिए प्रशंसा है तथा वह जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(64,2,'64:2','वही है, जिसने उत्पन्न किया है तुम्हें, तो तुममें से कुछ काफ़िर हैं और तुममें से कोई ईमान वाला है तथा अल्लाह जो कुछ तुम करते हो, उसे देख रहा है।[ ');
INSERT INTO "al_umari" VALUES(64,3,'64:3','उसने उत्पन्न किया आकाशों तथा धरती को सत्य के साथ तथा रूप बनाया तुम्हारा तो सुन्दर बनाया तुम्हारा रूप और उसी की ओर फिरकर जाना है।[ ');
INSERT INTO "al_umari" VALUES(64,4,'64:4','वह जानता है, जो कुछ आकाशों तथा धरती में है और जानता है, जो तुम मन में रखते हो और जो बोलते हो तथा अल्लाह भली-भाँति अवगत है दिलों के भेदों से।');
INSERT INTO "al_umari" VALUES(64,5,'64:5','क्या नहीं आई तुम्हारे पास उनकी सूचना, जिन्होंने कुफ़्र किया इससे पूर्व? तो उन्होंने चख लिया अपने कर्म का दुष्परिणाम और उन्हीं के लिए दुःखदायी यातना है।[ ');
INSERT INTO "al_umari" VALUES(64,6,'64:6','ये इसलिए कि आते रहे उनके पास उनके रसूल खुली निशानियाँ लेकर। तो उन्होंने कहाः क्या कोई मनुष्य हमें मार्गदर्शन[ देगा? अतः उन्होंने कुफ़्र किया तथा मुँह फेर लिया और अल्लाह (भी उनसे) निश्चिन्त हो गया तथा अल्लाह निस्पृह, प्रशंसित है। ');
INSERT INTO "al_umari" VALUES(64,7,'64:7','समझ रखा है काफ़िरों ने कि वे कदापि फिर जीवित नहीं किये जायेंगे। आप कह दें कि क्यों नहीं? मेरे पालनहार की शपथ! तुम अवश्य जीवित किये जाओगे। फिर तुम्हें बताया जायेगा कि तुमने (संसार में) क्या किया है तथा ये अल्लाह पर अति सरल है।');
INSERT INTO "al_umari" VALUES(64,8,'64:8','अतः तुम ईमान लाओ अल्लाह तथा उसके रसूल[ पर तथा उस नूर (ज्योति) पर, जिसे हमने उतारा है तथा अल्लाह उससे, जो तुम करते हो भली-भाँति सूचित है।');
INSERT INTO "al_umari" VALUES(64,9,'64:9','जिस दिन वह तुम्हें एकत्र करेगा, एकत्र किये जाने वाले दिन। तो वह क्षति (हानि) के खुल जाने[ का दिन होगा और जो ईमान लाया अल्लाह पर तथा सदाचार करता है, तो वह क्षमा कर देगा उसके दोषों को और प्रवेश देगा उसे ऐसे स्वर्गों में, बहती होंगी जिनमें नहरें, वे सदावासी होंगे उनमें। यही बड़ी सफलता है। ');
INSERT INTO "al_umari" VALUES(64,10,'64:10','और जिन लोगों ने कुफ़्र किया और झुठलाया हमारी आयतों (निशानियों) को, तो वही नारकी हैं, जो सदावासी होंगे उस (नरक) में तथा वह बुरा ठिकाना है।');
INSERT INTO "al_umari" VALUES(64,11,'64:11','जो आपदा आती है, वह अल्लाह ही की अनुमति से आती है तथा जो अल्लाह पर ईमान[ लाये, तो वह मार्गदर्शन देता है उसके दिल को तथा अल्लाह प्रत्येक चीज़ को जानता है।');
INSERT INTO "al_umari" VALUES(64,12,'64:12','तथा आज्ञा का पालन करो अल्लाह की तथा आज्ञा का पालन करो उसके रसूल की। फिर यदि तुम विमुख हुए, तो हमारे रसूल का दायित्व केवल खुले रूप से (उपदेश) पहुँचा देना है।');
INSERT INTO "al_umari" VALUES(64,13,'64:13','अल्लाह वह है, जिसके सिवा कोई वंदनीय ( सच्चा पूज्य) नहीं है। अतः, अल्लाह ही पर भरोसा करना चाहिये ईमान वालों को।');
INSERT INTO "al_umari" VALUES(64,14,'64:14','हे लोगो जो ईमान लाये हो! वास्तव में, तुम्हारी कुछ पत्नियाँ तथा संतान तुम्हारी शत्रु[ हैं। अतः, उनसे सावधान रहो और यदि तुम क्षमा से काम लो तथा सुधार करो और क्षमा कर दो, तो वास्तव में अल्लाह अति क्षमाशील, दयावान् है। ');
INSERT INTO "al_umari" VALUES(64,15,'64:15','तुम्हारे धन तथा तुम्हारी संतान तो तुम्हारे लिए एक परीक्षा हैं तथा अल्लाह के पास बड़ा प्रतिफल[ (बदला) है। ');
INSERT INTO "al_umari" VALUES(64,16,'64:16','तो अल्लाह से डरते रहो, जितना तुमसे हो सके तथा सुनो, आज्ञा पालन करो और दान करो। ये उत्तम है तुम्हारे लिए और जो बचा लिया गया अपने मन की कंजूसी से, तो वही सफल होने वाले हैं।');
INSERT INTO "al_umari" VALUES(64,17,'64:17','यदि तुम अल्लाह को उत्तम ऋण[ दोगो, तो वह तुम्हें कई गुना बढ़ाकर देगा और क्षमा कर देगा तुम्हें और अल्लाह बड़ा गुणग्राही, सहनशील है। ');
INSERT INTO "al_umari" VALUES(64,18,'64:18','वह परोक्ष और ह़ाज़िर का ज्ञान रखने वाला है। वह अति प्रभावी तथा गुणी है।');
INSERT INTO "al_umari" VALUES(65,1,'65:1','हे नबी! जब तुम लोग त़लाक़ दो अपनी पत्नियों को, तो उन्हें तलाक़ दो उनकी ''इद्दत'' के लिए, और गणना करो ''इद्दत'' की तथा डरो अपने पालनहार अल्लाह से और न निकालो उन्हें उनके घरों से और न वह स्वयं निकलें, परन्तु ये कि वे कोई खुली बुराई कर जायें तथा ये अल्लाह की सीमायें हैं और जो उल्लंघन करेगा अल्लाह की सीमाओं का, तो उसने अत्याचार कर लया अपने ऊपर। तुम नहीं जानते संभवतः अल्लाह कोई नई बात उत्पन्न कर दे इसके पश्चात्।');
INSERT INTO "al_umari" VALUES(65,2,'65:2','फिर जब पहुचने लगें अपने निर्धारित अवधि को, तो उन्हें रोक लो नियमानुसार अथवा अलग कर दो नियमानुसार[ और गवाह (साक्षी) बना लो अपने में से दो न्यायकारियों को तथा सीधी गवाही दो अल्लाह के लिए। इसकी शिक्षा दी जा रही है उसे, जो ईमान रखता हो अल्लाह तथा अन्त-दिवस (प्रलय) पर और जो कोई डरता हो अल्लाह से, तो वह बना देगा उसके लिए कोई निकलने का उपाय।');
INSERT INTO "al_umari" VALUES(65,3,'65:3','और उसे जीविका प्रदान करेगा, उस स्थान से, जिसका उसे अनुमान (भी) न हो तथा जो अल्लाह पर निर्भर रहेगा, तो वही उसे पर्याप्त है। निश्चय अल्लाह अपना कार्य पूरा करके रहेगा।[ अल्लाह ने प्रत्येक वस्तु के लिए एक अनुमान (समय) नियत कर रखा है। ');
INSERT INTO "al_umari" VALUES(65,4,'65:4','तथा जो निराश[ हो जाती हैं मासिक धर्म से तुम्हारी स्त्रियों में से, यदि तुम्हें संदेह हो तो उनकी निर्धारित अवधि तीन मास है तथा उनकी, जिन्हें मासिक धर्म न आता हो और गर्भवती स्त्रियों की निर्धारित अवधि ये है कि प्रसव हो जाये तथा जो अल्लाह से डरेगा, वह उसके लिए उसका कार्य सरल कर देगा। ');
INSERT INTO "al_umari" VALUES(65,5,'65:5','ये अल्लाह का आदेश है, जिसे उतारा है तुम्हारी ओर, अतः, जो अल्लाह से डरेगा[ वह क्षमा कर देगा उससे उसके दोषों को तथा प्रदान करेगा उसे बड़ा प्रतिफल। ');
INSERT INTO "al_umari" VALUES(65,6,'65:6','और उन्हें (निर्धारित अवधि में) रखो, जहाँ तुम रहते हो, अपनी शक्ति अनुसार और उन्हें हानि न पहुँचाओ, उन्हें तंग करने के लिए और यदि वे गर्भवती हों, तो उनपर ख़र्च करो, यहाँ तक कि प्रसव हो जाये। फिर यदि दूध पिलायें तुम्हारे (शिशु) के लिए, तो उन्हें उनका पारिश्रमिक दो और विचार-विमर्श कर लो, आपस में उचित रूप[ से और यदि तुम दोनों में तनाव हो जाये, तो दूध पिलायेगी उसे कोई दूसरी स्त्री। ');
INSERT INTO "al_umari" VALUES(65,7,'65:7','चाहिये कि सम्पन्न, ख़र्च दे अपनी कमाई के अनुसार और तंग हो जिसपर उसकी जीविका, उसे चाहिये कि ख़र्च दे उसमें से, जो दिया है उसे अल्लाह ने। अल्लाह भार नहीं रखता किसी प्राणी पर, परन्तु उतना ही, जो उसे दिया है। शीघ्र ही कर देगा अल्लाह तंगी के पश्चात् सुविधा।');
INSERT INTO "al_umari" VALUES(65,8,'65:8','कितनी बस्तियाँ[ थीं जिनके वासियों ने अवज्ञा की अपने पालनहार और उसके रसूलों के आदेश की, तो हमने ह़िसाब ले लिया उनका कड़ा ह़िसाब और उन्हें यातना दी बुरी यातना। ');
INSERT INTO "al_umari" VALUES(65,9,'65:9','तो उसने चख लिया अपने कर्म का दुष्परिणाम और उनका कार्य-परिणाम विनाश ही रहा।');
INSERT INTO "al_umari" VALUES(65,10,'65:10','तैयार कर रखी है अल्लाह ने उनके लिए भीषण यातना। अतः, अल्लाह से डरो, हे समझ वालो, जो ईमान लाये हो! निःसंदेह, अल्लाह ने उतार दी है तुम्हारी ओर एक शिक्षा।');
INSERT INTO "al_umari" VALUES(65,11,'65:11','(अर्थात) एक रसूल[ जो पढ़कर सुनाते हैं तुम्हें अल्लाह की खुली आयतें ताकि वह निकाले उन्हें, जो ईमान लाये तथा सदाचार किये, अन्धकारों से प्रकाश की ओर और जो ईमान लाये तथा सदाचार करेगा, वह उसे प्रवेश देगा ऐसे स्वर्गों में, प्रवाहित हैं जिनमें नहरें, वे सदावासी होंगे उनमें। अल्लाह ने उसके लिए उत्तम जीविका तैयार कर रखी है। ');
INSERT INTO "al_umari" VALUES(65,12,'65:12','अल्लाह वह है, जिसने उत्पन्न किये सात आकाश तथा धरती में से उन्हीं के समान। वह उतारता है आदेश उनके बीच, ताकि तुम विश्वास करो कि अल्लाह जो कुछ चाहे, कर सकता है और ये कि अल्लाह ने घेर रखा है प्रत्येक वस्तु को अपने ज्ञान की परिधि में।');
INSERT INTO "al_umari" VALUES(66,1,'66:1','हे नबी! क्यों ह़राम (अवैध) करते हैं आप उसे, जिसे ह़लाल (वैध) किया है अल्लाह ने आपके लिए? आप अपनी पत्नियों की प्रसन्नता[ चाहते हैं? तथा अल्लाह अति क्षमी, दयावान् है। ');
INSERT INTO "al_umari" VALUES(66,2,'66:2','नियम बना दिया है अल्लाह ने तुम्हारे लिए तुम्हारी शपथों से निकलने[ का तथा अल्लाह संरक्षक है तुम्हारा और वही सर्वज्ञानी गुणी है। ');
INSERT INTO "al_umari" VALUES(66,3,'66:3','और जब नबी ने अपनी कुछ पत्नियों से एक[ बात कही, तो उसने उसे बता दिया और अल्लाह ने उसे खोल दिया नबी पर, तो नबी ने कुछ से सूचित किया और कुछ को छोड़ दिया। फिर जब सूचित किया आपने पत्नि को उससे, तो उसने कहाः किसने सूचित किया आपको इस बात से? आपने कहाः मूझे सूचित किया है सब जानने और सबसे सूचित रहने वाले ने। ');
INSERT INTO "al_umari" VALUES(66,4,'66:4','यदि तुम[ दोनों (हे नबी की पत्नियो!) क्षमा माँग लो अल्लाह से (तो तुम्हारे लिए उत्तम है), क्योंकि तुम दोनों के दिल कुछ झुक गये हैं और यदि तुम दोनों एक-दूसरे की सहायता करोगी आपके विरूध्द, तो निःसंदेह अल्लाह आपका सहायक है तथा जिब्रील और सदाचारी ईमान वाले और फ़रिश्ते (भी) इनके अतिरिक्त सहायक हैं। ');
INSERT INTO "al_umari" VALUES(66,5,'66:5','कुछ दूर नहीं कि आपका पालनहार, यदि आप तलाक़ दे दें तुम सभी को, तो बदले में दे आपको पत्नियाँ तुमसे उत्तम, इस्लाम वालियाँ, इबादत करने वालियाँ, आज्ञा पालन करने वालियाँ, क्षमा माँगने वालियाँ, व्रत रखने वालियाँ, विधवायें तथा कुमारियाँ।');
INSERT INTO "al_umari" VALUES(66,6,'66:6','हे लोगो जो ईमान लाये हो! बचाओ[ अपने आपको तथा अपने परिजनों को उस अग्नि से, जिसका ईंधन मनुष्य तथा पत्थर होंगे। जिसपर फ़रिश्ते नियुक्त हैं कड़े दिल, कड़े स्वभाव वाले। वे अवज्ञा नहीं करते अल्लाह के आदेश की तथा वही करते हैं, जिसका आदेश उन्हें दिया जाये। ');
INSERT INTO "al_umari" VALUES(66,7,'66:7','हे काफ़िरो! बहाना न बनाओ आज, तुम्हें उसी का बदला दिया जा रहा है, जो तुम करते रहे।');
INSERT INTO "al_umari" VALUES(66,8,'66:8','हे ईमान वालो! अल्लाह के आगे सच्ची[ तौबा करो। संभव है कि तुम्हारा पालनहार दूर कर दे तुम्हारी बुराईयाँ तुमसे तथा प्रवेश करा दे तुम्हें ऐसे स्वर्गों में, बहती हैं जिनमें नहरें। जिस दिन वह अपमानित नहीं करेगा नबी को और न उन्हें, जो ईमान लाये हैं उनके साथ। उनका प्रकाश दौड़ रहा होगा, उनके आगे तथा उनके दायें, वे प्रार्थना कर रहे होंगेः हे हमारे पालनहार! पूर्ण कर दे हमारे लिए हमारा प्रकाश तथा क्षमा कर दे हमें। वास्तव में, तू जो चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(66,9,'66:9','हे नबी! आप जिहाद करें काफ़िरों और मुनाफ़िक़ों से और उनपर कड़ाई करें।[ उनका स्थान नरक है और वह बुरा स्थान है। ');
INSERT INTO "al_umari" VALUES(66,10,'66:10','अल्लाह ने उदाहरण दिया है उनके लिए, जो काफ़िर हो गये नूह़ की पत्नी तथा लूत की पत्नी का। जो दोनों विवाह में थीं दो भक्तों के, हमारे सदाचारी भक्तों में से। फिर दोनों ने विश्वासघात[ किया उनसे। तो दोनों उनके, अल्लाह के यहाँ कुछ काम नहीं आये तथा (दोनों स्त्रियों से) कहा गया कि प्रवेश कर जाओ नरक में प्रवेश करने वालों के साथ। ');
INSERT INTO "al_umari" VALUES(66,11,'66:11','तथा उदाहरण[ दिया है अल्लाह ने उनके लिए, जो ईमान लाये, फ़िरऔन की पत्नी का। जब उसने प्रार्थना कीः हे मेरे पालनहार! बना दे मेरे लिए अपने पास एक घर स्वर्ग में तथा मुझे मुक्त कर दे फ़िरऔन तथा उसके कर्म से और मुझे मुक्त कर दे अत्याचारी जाति से। ');
INSERT INTO "al_umari" VALUES(66,12,'66:12','तथा मर्यम, इमरान की पुत्री का, जिसने रक्षा की अपने सतीत्व की, तो फूँक दी हमने उसमें अपनी ओर से रूह़ (आत्मा) तथा उस (मर्यम) ने सच माना अपने पालनहार की बातों और उसकी पुस्तकों को और वह इबादत करने वालों में से थी।');
INSERT INTO "al_umari" VALUES(67,1,'67:1','शुभ है वह अल्लाह, जिसके हाथ में राज्य है तथा जो कुछ वह चाहे, कर सकता है।');
INSERT INTO "al_umari" VALUES(67,2,'67:2','जिसने उत्पन्न किया है मृत्यु तथा जीवन को, ताकि तुम्हारी परीक्षा ले कि तुममें किसका कर्म अधिक अच्छा है? तथा वह प्रभुत्वशाली, अति क्षमावान् है।[ ');
INSERT INTO "al_umari" VALUES(67,3,'67:3','जिसने उत्पन्न किये सात आकाश, ऊपर तले। तो क्या तुम देखते हो अत्यंत कृपाशील की उत्पत्ति में कोई असंगति? फिर पुनः देखो, क्या तुम देखते हो कोई दराड़?');
INSERT INTO "al_umari" VALUES(67,4,'67:4','फिर बार-बार देखो, वापस आयेगी तुम्हारी ओर निगाह थक-हार कर।');
INSERT INTO "al_umari" VALUES(67,5,'67:5','और हमने सजाया है संसार के आकाश को प्रदीपों (ग्रहों) से तथा बनाया है उन्हें (तारों को) मार भगाने का साधन शैतानों[ को, और तैयार की है हमने उनके लिए दहकती अग्नि की यातना। ');
INSERT INTO "al_umari" VALUES(67,6,'67:6','और जिन्होंने कुफ़्र किया अपने पालनहार के साथ तो उनके लिए नरक की यातना है। और वह बुरा स्थान है।');
INSERT INTO "al_umari" VALUES(67,7,'67:7','जब वह फेंके जायेंगे उसमें तो सुनेंगे उसकी दहाड़ और वह खौल रही होगी।');
INSERT INTO "al_umari" VALUES(67,8,'67:8','प्रतीत होगा कि फट पड़ेगी रोष (क्रोध) सेर, जब-जब फेंका जायेगा उसमें कोई समूह तो प्रश्न करेंगे उनसे उसके प्रहरीः क्या नहीं आया तुम्हारे पास कोई सावधान करने वाला (रसूल)?');
INSERT INTO "al_umari" VALUES(67,9,'67:9','वह कहेंगेः हाँ हमारे पास आया सावधान करने वाला। पर हमने झुठला दिया और कहा कि नहीं उतारा है अल्लाह ने कुछ। तुम ही बड़े कुपथ में हो।');
INSERT INTO "al_umari" VALUES(67,10,'67:10','तथा वह कहेंगेः यदि हमने सुना और समझा होता तो नरक के वासियों में न होते।');
INSERT INTO "al_umari" VALUES(67,11,'67:11','ऐसे वह स्वीकार कर लेंगे अपने पापों को। तो दूरी[ है नरक वासियों के लिए। ');
INSERT INTO "al_umari" VALUES(67,12,'67:12','निःसंदेह जो डरते हों अपने पालनहार से बिन देखे उन्हीं के लिए क्षमा है तथा बड़ा प्रतिफल है।[ ');
INSERT INTO "al_umari" VALUES(67,13,'67:13','तुम चुपके बोलो अपनी बात अथवा ऊँचे स्वर में। वास्तव में वह भली-भाँति जानता है सीनों के भेदों को।');
INSERT INTO "al_umari" VALUES(67,14,'67:14','क्या वह नहीं जानेगा जिस ने उत्न्न किया? और वह सूक्ष्मदर्शक[ सर्व सूचित है? ');
INSERT INTO "al_umari" VALUES(67,15,'67:15','वही है जिस ने बनाया है तुम्हारे लिए धरती को वश्वर्ती, तो चलो-फिरो उसके क्षेत्रों में तथा खाओ उसकी प्रदान की हुई जीविका। और उसी की ओर तुम्हें फिर जीवित हो कर जाना है।');
INSERT INTO "al_umari" VALUES(67,16,'67:16','क्या तुम निर्भय हो गये हो उससे जो आकाश में है कि वह धँसा दे धरती में फिर वह अचानक काँपने लगे।');
INSERT INTO "al_umari" VALUES(67,17,'67:17','अथवा निर्भय हो गये उससे जो आकाश में है कि वह भेज दे तुम पर पथरीली वायु तो तुम्हें ज्ञान हो जायेगा कि कैसा रहा मेरा सावधान करना?');
INSERT INTO "al_umari" VALUES(67,18,'67:18','झुठला चुके हैं इन[ से पूर्व के लोग तो कैसी रही मेरी पकड़? ');
INSERT INTO "al_umari" VALUES(67,19,'67:19','क्या उन्होंने नहीं देखा पक्षियों की ओर अपने ऊपर पंख फैलाते तथा सिकोड़ते। उन को अत्यंत कृपाशील ही थामता है। निसंदेह वह प्रत्येक वस्तु को देख रहा है।');
INSERT INTO "al_umari" VALUES(67,20,'67:20','कौन है वह तुम्हारी सेना जो तुम्हारी सहायता कर सकेगी अल्लाह के मुक़ाबले में? बल्कि वह घुस गये हैं अवैज्ञा तथा घृणा में।[');
INSERT INTO "al_umari" VALUES(67,21,'67:21','या कौन है जो तुम्हें जीविका प्रदान कर सके, यदि रोक ले वह अपनी जीविका? बल्कि वे घुस गये हैं अवैज्ञा तथा घृणा में।[ ');
INSERT INTO "al_umari" VALUES(67,22,'67:22','तो क्या जो चल रहा हो औंधा हो कर अपने मुँह के बल वह अधिक मार्गदर्शन पर है ये जो सीधा हो कर चल रहा हो सीधी राह पर?[ ');
INSERT INTO "al_umari" VALUES(67,23,'67:23','हे नबी! आप कह दें कि वही है जिस ने पैदा किया है तुम्हें और बनाये हैं तुम्हारे कान तथा आँख और दिल। बहुत ही कम आभारी (कृतज्ञ) होते हो।');
INSERT INTO "al_umari" VALUES(67,24,'67:24','आप कह देः उसीने फैलाया है तुम्हें धरती में और उसी की ओर एकत्रित[ किये जाओगे। ');
INSERT INTO "al_umari" VALUES(67,25,'67:25','तथा वह कहते हैं कि ये वचन कब पूरा होगा यदि तुम सच्चे हो?');
INSERT INTO "al_umari" VALUES(67,26,'67:26','आप कह देः उस का ज्ञान बस अल्लाह ही को है। और मैं केवल खुला सावधान करने वाला हूँ।');
INSERT INTO "al_umari" VALUES(67,27,'67:27','फिर जब वह देखेंगे उसे समीप, तो बिगड़ जायेंगे उनके चेहरे जो काफ़िर हो गये। तथा कहा जायेगाः ये वही है जिसकी तुम माँग कर रहे थे।');
INSERT INTO "al_umari" VALUES(67,28,'67:28','आप कह देः देखो यदि अल्लाह नाश कर दे मुझ को तथा मेरे साथियों को अथवा दया करे हम पर, तो (बताओ कि) कौन है जो शरण देगा काफ़िरों को दुःखदायी[ यातना से? ');
INSERT INTO "al_umari" VALUES(67,29,'67:29','आप कह देः वह अत्यंत कृपाशील है। हम उसपर ईमान लाये तथा उसीपर भरोसा किया, तो तुम्हें ज्ञान हो जायेगा कि कौन खुले कुपथ में है।');
INSERT INTO "al_umari" VALUES(67,30,'67:30','आप कह देः भला देखो यदि तुम्हारा पानी गहराई में चला जाये, तो कौन है जो तुम्हें ला कर देगा बहता हुआ जल?');
INSERT INTO "al_umari" VALUES(68,1,'68:1','नून और शपथ है लेखनी (क़लम) की तथा उसकी[ जिसे वो लिखते हैं। ');
INSERT INTO "al_umari" VALUES(68,2,'68:2','नहीं है आप, अपने पालनहार के अनुग्रह से पागल।');
INSERT INTO "al_umari" VALUES(68,3,'68:3','तथा निश्चय प्रतिफल (बदला) है आपके लिए अनन्त।');
INSERT INTO "al_umari" VALUES(68,4,'68:4','तथा निश्चय ही आप बड़े सुशील हैं।');
INSERT INTO "al_umari" VALUES(68,5,'68:5','तो शीघ्र आप देख लेंगे तथा वे (काफ़िर भी) देख लेंगे।');
INSERT INTO "al_umari" VALUES(68,6,'68:6','कि पागल कौन है।');
INSERT INTO "al_umari" VALUES(68,7,'68:7','वास्तव में, आपका पालनहार ही अधिक जानता है उसे, जो कुपथ हो गया उसकी राह से और वही अधिक जानता है उन्हें, जो सीधी राह पर हैं।');
INSERT INTO "al_umari" VALUES(68,8,'68:8','तो आप बात न मानें झुठलाने वालों की।');
INSERT INTO "al_umari" VALUES(68,9,'68:9','वे चाहते हैं कि आप ढीले हो जायें, तो वे भी ढीले हो[ जायें। ');
INSERT INTO "al_umari" VALUES(68,10,'68:10','और बात न मानें[ आप किसी अधिक शपथ लेने वाले, हीन व्यक्ति की। ');
INSERT INTO "al_umari" VALUES(68,11,'68:11','जो व्यंग करने वाला, चुगलियाँ खाता फिरता है।');
INSERT INTO "al_umari" VALUES(68,12,'68:12','भलाई से रोकने वाला, अत्याचारी, बडा पापी है।');
INSERT INTO "al_umari" VALUES(68,13,'68:13','घमंडी है और इसके पश्चात् कुवंश (वर्ण संकर) है।');
INSERT INTO "al_umari" VALUES(68,14,'68:14','इसलिए कि वह धन तथा पुत्रों वाला है।');
INSERT INTO "al_umari" VALUES(68,15,'68:15','जब पढ़ी जाती है उसपर हमारी आयतें, तो कहता हैः ये पूर्वजों की कल्पित कथायें हैं।');
INSERT INTO "al_umari" VALUES(68,16,'68:16','शीघ्र ही हम दाग़ लगा देंगे उसके सूंड[ पर। ');
INSERT INTO "al_umari" VALUES(68,17,'68:17','निःसंदेह, हमने उन्हें परीक्षा में डाला[ है, जिस प्रकार बाग़ वालों को परीक्षा में डाला था। जब उन्होंने शपथ ली कि अवश्य तोड़ लेंगे उसके फल भोर होते ही। ');
INSERT INTO "al_umari" VALUES(68,18,'68:18','और इन् शा अल्लाह (यदि अल्लाह ने चाहा) नहीं कहा।');
INSERT INTO "al_umari" VALUES(68,19,'68:19','तो फिर गया उस बाग़ पर एक कुचक्र, आपके पालनहार की ओर से और वे सोये हुए थे।');
INSERT INTO "al_umari" VALUES(68,20,'68:20','तो वह हो गया जैसे उजाड़ खेती हो।');
INSERT INTO "al_umari" VALUES(68,21,'68:21','अब वे एक-दूसरे को पुकारने लगे भोर होते हीः');
INSERT INTO "al_umari" VALUES(68,22,'68:22','कि तड़के चलो अपनी खेती पर, यदि फल तोड़ने हैं।');
INSERT INTO "al_umari" VALUES(68,23,'68:23','फिर वे चल दिये आपस में चुपके-चुपके बातें करते हुए।');
INSERT INTO "al_umari" VALUES(68,24,'68:24','कि कदापि न आने पाये उस (बाग़) के भीतर आज तुम्हारे पास कोई निर्धन।[ ');
INSERT INTO "al_umari" VALUES(68,25,'68:25','और प्रातः ही पहुँच गये कि वे फल तोड़ सकेंगे।');
INSERT INTO "al_umari" VALUES(68,26,'68:26','फिर जब उसे देखा तो कहाः निश्चय हम राह भूल गये।');
INSERT INTO "al_umari" VALUES(68,27,'68:27','बल्कि हम वंचित हो[ गये। ');
INSERT INTO "al_umari" VALUES(68,28,'68:28','तो उनमें से बिचले भाई ने कहाः क्या मैंने तुमसे नहीं कहा था कि तुम (अल्लाह की) पवित्रता का वर्णन क्यों नहीं करते?');
INSERT INTO "al_umari" VALUES(68,29,'68:29','वे कहने लगेः पवित्र है हमारा पालनहार! वास्तव में, हम ही अत्याचारी थे।');
INSERT INTO "al_umari" VALUES(68,30,'68:30','फिर सम्मुख हो गये, एक-दूसरे की निन्दा करते हुए।');
INSERT INTO "al_umari" VALUES(68,31,'68:31','कहने लगेः हाय अफ़्सोस! हम ही विद्रोही थे।');
INSERT INTO "al_umari" VALUES(68,32,'68:32','संभव है हमारा पालनहार हमें बदले में प्रदान करे इससे उत्तम (बाग)। हम अपने पालनहार ही की ओर रूचि रखते हैं।');
INSERT INTO "al_umari" VALUES(68,33,'68:33','ऐसे ही यातना होती है और आख़िरत (परलोक) की यातना इससे भी बड़ी है। काश वे जानते!');
INSERT INTO "al_umari" VALUES(68,34,'68:34','निःसंदेह, सदाचारियों के लिए उनके पालनहार के पास सुखों वाले स्वर्ग हैं।');
INSERT INTO "al_umari" VALUES(68,35,'68:35','क्या हम आज्ञाकारियों[ को पापियों के समान कर देंगे? ');
INSERT INTO "al_umari" VALUES(68,36,'68:36','तुम्हें क्या हो गया है? तुम कैसा निर्णय कर रहे हो?');
INSERT INTO "al_umari" VALUES(68,37,'68:37','क्या तुम्हारे पास कोई पुस्तक है, जिसमें तुम पढ़ते हो?');
INSERT INTO "al_umari" VALUES(68,38,'68:38','कि तुम्हें वही मिलेगा, जो तुम चाहोगे?');
INSERT INTO "al_umari" VALUES(68,39,'68:39','या तुमने हमसे शपथें ले रखी हैं, जो प्रलय तक चली जायेंगी कि तुम्हें वही मिलेगा जिसका तुम निर्णय करोगे?');
INSERT INTO "al_umari" VALUES(68,40,'68:40','आप उनसे पूछिये कि उनमें कौन इसकी ज़मानत लेता है?');
INSERT INTO "al_umari" VALUES(68,41,'68:41','क्या उनके कुछ साझी हैं? फिर तो वे अपने साझियों को लायें,[ यदि वे सच्चे हैं। ');
INSERT INTO "al_umari" VALUES(68,42,'68:42','जिस दिन पिंडली खोल दी जायेगी और वह बुलाये जायेंगे सज्दा करने के लिए, तो (सज्दा) नहीं कर सकेंगे।[ ');
INSERT INTO "al_umari" VALUES(68,43,'68:43','उनकी आँखें झुकी होंगी और उनपर अपमान छाया होगा। वे (संसार में) सज्दा करने के लिए बुलाये जाते रहे जबकि वे स्वस्थ थे।');
INSERT INTO "al_umari" VALUES(68,44,'68:44','अतः, आप छोड़ दें मुझे तथा उन्हें, जो झुठला रहे हैं इस बात (क़ुर्आन) को, हम उन्हें धीरे-धीरे खींच लायेंगे,[ इस प्रकार कि उन्हें ज्ञान भी नहीं होगा। ');
INSERT INTO "al_umari" VALUES(68,45,'68:45','तथा हम उन्हें अवसर दे रहे हैं।[ वस्तुतः, हमारा उपाय सुदृढ़ है। ');
INSERT INTO "al_umari" VALUES(68,46,'68:46','तो क्या आप माँग कर रहे हैं किसी पारिश्रमिक[ की, तो वे बोझ से दबे जा रहे हैं। ');
INSERT INTO "al_umari" VALUES(68,47,'68:47','या उनके पास ग़ैब का ज्ञान है, जिसे वे लिख[ रहे हैं? ');
INSERT INTO "al_umari" VALUES(68,48,'68:48','तो आप धैर्य रखें अपने पालनहार के निर्णय तक और न हो जायें मछली वाले के समान।[ जब उसने पुकारा और वह शोकपूर्ण था। ');
INSERT INTO "al_umari" VALUES(68,49,'68:49','और यदि न पा लेती उसे उसके पालनहार की दया, तो वह फेंक दिया जाता बंजर में और वह बुरी दशा में होता।');
INSERT INTO "al_umari" VALUES(68,50,'68:50','फिर चुन लिया उसे उसके पालनहार ने और बना दिया उसे सदाचारियों में से।');
INSERT INTO "al_umari" VALUES(68,51,'68:51','और ऐसा लगता है कि जो काफ़िपर हो गये, वे अवश्य फिसला देंगे आपको अपनी आँखों से (घूर कर) जब वे सुनते हों क़ुर्आन को तथा कहते हैं कि वह अवशय् पागल है।');
INSERT INTO "al_umari" VALUES(68,52,'68:52','जबकि ये क़ुर्आन तो बस एक[ शिक्षा है, पूरे संसार वासियों के लिए। ');
INSERT INTO "al_umari" VALUES(69,1,'69:1','जिसका होना सच है।');
INSERT INTO "al_umari" VALUES(69,2,'69:2','वह क्या है, जिसका होना सच है?');
INSERT INTO "al_umari" VALUES(69,3,'69:3','तथा आप क्या जानें कि क्या है, जिसका होना सच है?');
INSERT INTO "al_umari" VALUES(69,4,'69:4','झुठलाया समूद तथा आद (जाति) ने अचानक आ पड़ने वाली (प्रलय) को।');
INSERT INTO "al_umari" VALUES(69,5,'69:5','फिर समूद, तो वे ध्वस्त कर दिये गये अति कड़ी ध्वनि से।');
INSERT INTO "al_umari" VALUES(69,6,'69:6','तथा आद, तो वे ध्वस्त कर दिये गये एक तेज़ शीतल आँधी से।');
INSERT INTO "al_umari" VALUES(69,7,'69:7','लगाये रखा उसे उनपर सात रातें और आठ दिन निरन्तर, तो आप देखते कि वे जाति उसमें ऐसे पछाड़ी हुई है, जैसे खजूर के खोखले तने।[ ');
INSERT INTO "al_umari" VALUES(69,8,'69:8','तो क्या आप देखते हैं कि उनमें कोई शेष रह गया है?');
INSERT INTO "al_umari" VALUES(69,9,'69:9','और किया यही पाप फ़िरऔन ने और जो उसके पूर्व थे तथा जिनकी बस्तियाँ औंधी कर दी गयीं।');
INSERT INTO "al_umari" VALUES(69,10,'69:10','उन्होंने नहीं माना अपने पालनहार के रसूल को। अन्ततः, उसने पकड़ लिया उन्हें, कड़ी पकड़।');
INSERT INTO "al_umari" VALUES(69,11,'69:11','हमने, जब सीमा पार कर गया जल, तो तुम्हें सवार कर दिया नाव[ में। ');
INSERT INTO "al_umari" VALUES(69,12,'69:12','ताकि हम बना दें उसे तुम्हारे लिए एक शिक्षाप्रद यादगार और ताकि सुरक्षित रख लें इसे सुनने वाले कान।');
INSERT INTO "al_umari" VALUES(69,13,'69:13','फिर जब फूँक दी जायेगी सूर (नरसिंघा) में एक फूँक।');
INSERT INTO "al_umari" VALUES(69,14,'69:14','और उठाया जायेगा धरती तथा पर्वतों को, तो दोनों चूर-चूर कर दिये जायेंगे[ एक ही बार में। ');
INSERT INTO "al_umari" VALUES(69,15,'69:15','तो उसी दिन होनी हो जायेगी।');
INSERT INTO "al_umari" VALUES(69,16,'69:16','तथा फट जायेगा आकाश, तो वह उस दिन क्षीण निर्बल हो जायेगा।');
INSERT INTO "al_umari" VALUES(69,17,'69:17','और फ़रिश्ते उसके किनारों पर होंगे तथा उठाये होंगे आपके पालनहार के अर्श (सिंहासन) को अपने ऊपर उस दिन, आठ फ़रिश्ते।');
INSERT INTO "al_umari" VALUES(69,18,'69:18','उस दिन तुम अल्लाह के पास उपस्थित किये जाओगे, नहीं छुपा रह जायेगा तुममें से कोई।');
INSERT INTO "al_umari" VALUES(69,19,'69:19','फिर जिसे दिया जायेगा उसका कर्मपत्र दायें हाथ में, वह कहेगाः ये लो मेरा कर्मपत्र पढ़ो।');
INSERT INTO "al_umari" VALUES(69,20,'69:20','मुझे विश्वास था कि मैं मिलने वाला हूँ अपने ह़िसाब से।');
INSERT INTO "al_umari" VALUES(69,21,'69:21','तो वह अपने मन चाहे सुख में होगा।');
INSERT INTO "al_umari" VALUES(69,22,'69:22','उच्च श्रेणी के स्वर्ग में।');
INSERT INTO "al_umari" VALUES(69,23,'69:23','जिसके फलों के गुच्छे झुक रहे होंगे।');
INSERT INTO "al_umari" VALUES(69,24,'69:24','(उनसे कहा जायेगाः) खाओ तथा पियो आनन्द लेकर उसके बदले, जो तुमने किया है विगत दिनों (संसार) में।');
INSERT INTO "al_umari" VALUES(69,25,'69:25','और जिसे दिया जायेगा उसका कर्मपत्र उसके बायें हाथ में, तो वह कहेगाः हाय! मुझे मेरा कर्मपत्र दिया ही न जाता!');
INSERT INTO "al_umari" VALUES(69,26,'69:26','तथा मैं न जानता कि क्या है मेरा ह़िसाब!');
INSERT INTO "al_umari" VALUES(69,27,'69:27','काश मेरी मौत ही निर्णायक[ होती! ');
INSERT INTO "al_umari" VALUES(69,28,'69:28','नहीं काम आया मेरा धन।');
INSERT INTO "al_umari" VALUES(69,29,'69:29','मुझसे समाप्त हो गया, मेरा प्रभुत्व।[ ');
INSERT INTO "al_umari" VALUES(69,30,'69:30','(आदेश होगा कि) उसे पकड़ो और उसके गले में तौक़ डाल दो।');
INSERT INTO "al_umari" VALUES(69,31,'69:31','फिर नरक में उसे झोंक दो।');
INSERT INTO "al_umari" VALUES(69,32,'69:32','फिर उसे एक जंजीर जिसकी लम्बाई सत्तर गज़ है, में जकड़ दो।');
INSERT INTO "al_umari" VALUES(69,33,'69:33','वह ईमान नहीं रखता था महिमाशाली अल्लाह पर।');
INSERT INTO "al_umari" VALUES(69,34,'69:34','और न प्रेरणा देता था दरिद्र को भोजन कराने की।');
INSERT INTO "al_umari" VALUES(69,35,'69:35','अतः, नहीं है उसका आज यहाँ कोई मित्र।');
INSERT INTO "al_umari" VALUES(69,36,'69:36','और न कोई भोजन, पीप के सिवा।');
INSERT INTO "al_umari" VALUES(69,37,'69:37','जिसे पापी ही खायेंगे।');
INSERT INTO "al_umari" VALUES(69,38,'69:38','तो मैं शपथ लेता हूँ उसकी, जो तुम देखते हो।');
INSERT INTO "al_umari" VALUES(69,39,'69:39','तथा जो तुम नहीं देखते हो।');
INSERT INTO "al_umari" VALUES(69,40,'69:40','निःसंदेह, ये (क़ुर्आन) आदरणीय रसूल का कथन[ है। ');
INSERT INTO "al_umari" VALUES(69,41,'69:41','और वह किसी कवि का कथन नहीं है। तुम लोग कम ही विश्वास करते हो।');
INSERT INTO "al_umari" VALUES(69,42,'69:42','और न वह किसी ज्योतिषी का कथन है, तुम कम की शिक्षा ग्रहण करते हो।');
INSERT INTO "al_umari" VALUES(69,43,'69:43','सर्वलोक के पालनहार का उतारा हुआ है।');
INSERT INTO "al_umari" VALUES(69,44,'69:44','और यदि इसने (नबी ने) हमपर कोई बात बनाई[ होती। ');
INSERT INTO "al_umari" VALUES(69,45,'69:45','तो अवश्य हम पकड़ लेते उसका सीधा हाथ।');
INSERT INTO "al_umari" VALUES(69,46,'69:46','फिर अवश्य काट देते उसके गले की रग।');
INSERT INTO "al_umari" VALUES(69,47,'69:47','फिर तुममें से कोई (मुझे) उससे रोकने वाला न होता।');
INSERT INTO "al_umari" VALUES(69,48,'69:48','निःसंदेह, ये एक शिक्षा है सदाचारियों के लिए।');
INSERT INTO "al_umari" VALUES(69,49,'69:49','तथा वास्तव में हम जानते हैं कि तुममें कुछ झुठलाने वाले हैं।');
INSERT INTO "al_umari" VALUES(69,50,'69:50','और निश्चय ये पछतावे का कारण होगा काफ़िरों[ के लिए। ');
INSERT INTO "al_umari" VALUES(69,51,'69:51','वस्तुतः, ये विश्वसनीय सत्य है।');
INSERT INTO "al_umari" VALUES(69,52,'69:52','अतः, आप पवित्रता का वर्णन करें अपने महिमावान पालनहार के नाम की।');
INSERT INTO "al_umari" VALUES(70,1,'70:1','प्रश्न किया एक प्रश्न करने[ वाले ने उस यातना के बारे में, जो आने वाली है। ');
INSERT INTO "al_umari" VALUES(70,2,'70:2','काफ़िरों पर। नहीं है जिसे कोई दूर करने वाला।');
INSERT INTO "al_umari" VALUES(70,3,'70:3','अल्लाह ऊँचाईयों वाले की ओर से।');
INSERT INTO "al_umari" VALUES(70,4,'70:4','चढ़ते हैं फ़रिश्ते तथा रूह़[ जिसकी ओर, एक दिन में, जिसका माप पचास हज़ार वर्ष है। ');
INSERT INTO "al_umari" VALUES(70,5,'70:5','अतः, (हे नबी!) आप सहन[ करें अच्छे प्रकार से। ');
INSERT INTO "al_umari" VALUES(70,6,'70:6','वे समझते हैं उसे दूर।');
INSERT INTO "al_umari" VALUES(70,7,'70:7','और हम देख रहे हैं उसे समीप।');
INSERT INTO "al_umari" VALUES(70,8,'70:8','जिस दिन हो जायेगा आकाश पिघली हुई धातु के समान।');
INSERT INTO "al_umari" VALUES(70,9,'70:9','तथा हो जायेंगे पर्वत, रंगारंग धुने हुए ऊन के समान।[ ');
INSERT INTO "al_umari" VALUES(70,10,'70:10','और नहीं पूछेगा कोई मित्र किसी मित्र को।');
INSERT INTO "al_umari" VALUES(70,11,'70:11','(जबकि) वे उन्हें दिखाये जायेंगे। कामना करेगा पापी कि दण्ड के रूप में दे दे, उस दिन की यातना के, अपने पुत्रों को।');
INSERT INTO "al_umari" VALUES(70,12,'70:12','तथा अपनी पत्नी और अपने भाई को।');
INSERT INTO "al_umari" VALUES(70,13,'70:13','तथा अपने समीपवर्ती परिवार को, जो उसे शरण देता था।');
INSERT INTO "al_umari" VALUES(70,14,'70:14','और जो धरती में है, सभी[ को, फिर वह उसे यातना से बचा ले। ');
INSERT INTO "al_umari" VALUES(70,15,'70:15','कदापि (ऐसा) नहीं (होगा)।');
INSERT INTO "al_umari" VALUES(70,16,'70:16','वह अग्नि की ज्वाला होगी।');
INSERT INTO "al_umari" VALUES(70,17,'70:17','खाल उधेड़ने वाली।');
INSERT INTO "al_umari" VALUES(70,18,'70:18','वह पुकारेगी उसे, जिसने पीछा दिखाया[ तथा मुँह फेरा। ');
INSERT INTO "al_umari" VALUES(70,19,'70:19','तथा (धन) एकत्र किया, फिर सौंत कर रखा।');
INSERT INTO "al_umari" VALUES(70,20,'70:20','वास्तव में, मनुष्य अत्यंत कच्चे दिल का पैदा किया गया है।');
INSERT INTO "al_umari" VALUES(70,21,'70:21','जब उसे पहुँचता है दुःख, तो उद्विग्न हो जाता है।');
INSERT INTO "al_umari" VALUES(70,22,'70:22','और जब उसे धन मिलता है, तो कंजूसी करने लगता है।');
INSERT INTO "al_umari" VALUES(70,23,'70:23','परन्तु, जो नमाज़ी हैं।');
INSERT INTO "al_umari" VALUES(70,24,'70:24','जो अनपी नमाज़ का सदा पालन[ करते हैं। ');
INSERT INTO "al_umari" VALUES(70,25,'70:25','और जिनके धनों में निश्चित भाग है, याचक (माँगने वाला) तथा वंचित[ का। ');
INSERT INTO "al_umari" VALUES(70,26,'70:26','तथा जो सत्य मानते हैं प्रतिकार (प्रलय) के दिन को।');
INSERT INTO "al_umari" VALUES(70,27,'70:27','तथा जो अपने पालनहार की यातना से डरते हैं।');
INSERT INTO "al_umari" VALUES(70,28,'70:28','वास्तव में, आपके पालनहार की यातना निर्भय रहने योग्य नहीं है।');
INSERT INTO "al_umari" VALUES(70,29,'70:29','तथा जो अपने गुप्तांगों की रक्षा करने वाले हैं।');
INSERT INTO "al_umari" VALUES(70,30,'70:30','सिवाय अपनी पत्नियों और अपने स्वामित्व में आये दासियों[ के, तो वही निन्दित नहीं हैं। ');
INSERT INTO "al_umari" VALUES(70,31,'70:31','और जो चाहे इसके अतिरिक्त, तो वही सीमा का उल्लंघन करने वाले हैं।');
INSERT INTO "al_umari" VALUES(70,32,'70:32','और जो अपनी अमानतों तथा अपने वचन का पालन करते हैं।');
INSERT INTO "al_umari" VALUES(70,33,'70:33','और जो अपने साक्ष्यों (गवाहियों) पर स्थित रहने वाले हैं।');
INSERT INTO "al_umari" VALUES(70,34,'70:34','तथा जो अपनी नमाज़ों की रक्षा करते हैं।');
INSERT INTO "al_umari" VALUES(70,35,'70:35','वही स्वर्गों में सम्मानित होंगे।');
INSERT INTO "al_umari" VALUES(70,36,'70:36','तो क्या हो गया है उनकाफ़िरों को कि आपकी ओर दौड़े चले आ रहे हैं?');
INSERT INTO "al_umari" VALUES(70,37,'70:37','दायें तथा बायें समूहों में होकर।[ ');
INSERT INTO "al_umari" VALUES(70,38,'70:38','क्या उनमें से प्रत्येक व्यक्ति लोभ (लालच) रखता है कि उसे प्रवेश दे दिया जायेगा सुख के स्वर्गों में?');
INSERT INTO "al_umari" VALUES(70,39,'70:39','कदापि ऐसा न होगा, हमने उनकी उत्पत्ति उस चीज़ से की है, जिसे वे[ जानते हैं। ');
INSERT INTO "al_umari" VALUES(70,40,'70:40','तो मैं शपथ लेता हूँ पूर्वों (सूर्योदय के स्थानों) तथा पश्चिमों (सूर्यास्त के स्थानों) की, वास्तव में हम अवश्य सामर्थ्यवान हैं।');
INSERT INTO "al_umari" VALUES(70,41,'70:41','इस बात पर कि बदल दें उनसे उत्तम (उत्पत्ति) को तथा हम विवश नहीं हैं।');
INSERT INTO "al_umari" VALUES(70,42,'70:42','अतः, आप उन्हें झगड़ते तथा खेलते छोड़ दें, यहाँ तक कि वे मिल जायें अपने उस दिन से, जिसका उन्हें वचन दिया जा रहा है।');
INSERT INTO "al_umari" VALUES(70,43,'70:43','जिस दिन वे निकलेंगे क़ब्रों (और समाधियों) से, दौड़ते हुए, जैसे वे अपनी मूर्तियों की ओर दौड़ रहे हों।[ ');
INSERT INTO "al_umari" VALUES(70,44,'70:44','झुकी होंगी उनकी आँखें, छाया होगा उनपर अपमान, यही वह दिन है जिसका वचन उन्हें दिया जा[ रहा था। ');
INSERT INTO "al_umari" VALUES(71,1,'71:1','निःसंदेह, हमने भेजा नूह़ को उसकी जाति की ओर कि सावधान कर अपनी जाति को, इससे पूर्व कि आये उनके पास, दूःखदायी यातना।');
INSERT INTO "al_umari" VALUES(71,2,'71:2','उसने कहाः हे मेरी जाति! वास्तव में, मैं खुला सावधान करने वाला हूँ, तुम्हें।');
INSERT INTO "al_umari" VALUES(71,3,'71:3','कि इबादत (वंदना) करो अल्लाह की तथा डरो उससे और बात मानो मेरी।');
INSERT INTO "al_umari" VALUES(71,4,'71:4','वह क्षमा कर देगा तुमहारे लिए तुम्हारे पापों को तथा अवसर देगा तुम्हें निर्धारित समय[ तक। वास्तव में, जब अल्लाह का निर्धारित समय आ जायेगा, तो उसमें देर न होगी। काश तुम जानते! ');
INSERT INTO "al_umari" VALUES(71,5,'71:5','नूह़ ने कहाः मेरे पालनहार! मैंने बुलाया अपनी जाति को (तेरी ओर) रात और दिन।');
INSERT INTO "al_umari" VALUES(71,6,'71:6','तो मेरे बुलावे ने उनके भागने ही को अधिक किया।');
INSERT INTO "al_umari" VALUES(71,7,'71:7','और मैंने जब-जब उन्हें बुलाया, तो उन्होंने दे लीं अपनी उँगलियाँ अपने कानों में तथा ओढ़ लिए अपने कपड़े[ तथा अड़े रह गये और बड़ा घमण्ड किया। ');
INSERT INTO "al_umari" VALUES(71,8,'71:8','फिर मैंने उन्हें उच्च स्वर में बुलाया।');
INSERT INTO "al_umari" VALUES(71,9,'71:9','फिर मैंने उनसे खुलकर कहा और उनसे धीरे-धीरे (भी) कहा।');
INSERT INTO "al_umari" VALUES(71,10,'71:10','मैंने कहाः क्षमा माँगो अपने पालनहार से, वास्तव में वह बड़ा क्षमाशील है।');
INSERT INTO "al_umari" VALUES(71,11,'71:11','वह वर्षा करेगा आकाश से तुमपर धाराप्रवाह वर्षा।');
INSERT INTO "al_umari" VALUES(71,12,'71:12','तथा अधिक देगा तुम्हें पुत्र तथा धन और बना देगा तुम्हारे लिए बाग़ तथा नहरें।');
INSERT INTO "al_umari" VALUES(71,13,'71:13','क्या हो गया है तुम्हें कि नहीं डरते हो अल्लाह की महिमा से?');
INSERT INTO "al_umari" VALUES(71,14,'71:14','जबकि उसने पैदा किया है तुम्हें विभिन्न प्रकार[ से। ');
INSERT INTO "al_umari" VALUES(71,15,'71:15','क्या तुमने नहीं देखा कि कैसे पैदा किये हैं अल्लाह ने सात आकाश, ऊपर-तले?');
INSERT INTO "al_umari" VALUES(71,16,'71:16','और बनाया है चन्द्रमा को उनमें प्रकाश और बनाया है सूर्य को प्रदीप।');
INSERT INTO "al_umari" VALUES(71,17,'71:17','और अल्लाह ही ने उगाया है तुम्हें धरती[ से अद्भुत रूप से। ');
INSERT INTO "al_umari" VALUES(71,18,'71:18','फिर वह वापस ले जायेगा तुम्हें उसमें और निकालेगा तुम्हें उससे।');
INSERT INTO "al_umari" VALUES(71,19,'71:19','और अल्लाह ने बनाया है तुम्हारे लिए धरती को बिस्तर।');
INSERT INTO "al_umari" VALUES(71,20,'71:20','ताकि तुम चलो उसकी खुली राहों में।');
INSERT INTO "al_umari" VALUES(71,21,'71:21','नूह ने निवेदन कियाः मेरे पालनहार! उन्होंने मेरी अवज्ञा की और अनुसरण किया उसका[ जिसके धन और संतान ने उसकी क्षति ही को बढ़ाया। ');
INSERT INTO "al_umari" VALUES(71,22,'71:22','और उन्होंने बड़ी चाल चली।');
INSERT INTO "al_umari" VALUES(71,23,'71:23','और उन्होंने कहाः तुम कदापि न छोड़ना अपने पूज्यों को और कदापि न छोड़ना वद्द को, न सुवाअ को और न यग़ूस को और न यऊक़ को तथा न नस्र[ को। ');
INSERT INTO "al_umari" VALUES(71,24,'71:24','और कुपथ (गुमराह) कर दिया है उन्होंने बहुतों को और अधिक कर दे तू भी अत्याचारियों के कुपथ[ (कुमार्ग) को। ');
INSERT INTO "al_umari" VALUES(71,25,'71:25','वे अपने पापों के कारण डुबो दिये गये, फिर पहुँचा दिये गये नरक में और नहीं पाया उन्होंने अपने लिए अल्लाह के मुक़ाबले में कोई सहायक। ');
INSERT INTO "al_umari" VALUES(71,26,'71:26','तथा कहा नूह़ नेः मेरे पालनहार! न छोड़ धरती पर काफ़िरों का कोई घराना।');
INSERT INTO "al_umari" VALUES(71,27,'71:27','क्योंकि यदि तू उन्हें छोड़ेगा, तो वे कुपथ करेंगे तेरे भक्तों को और नहीं जन्म देंगे, परन्तु दुष्कर्मी, बड़े काफ़िर को।');
INSERT INTO "al_umari" VALUES(71,28,'71:28','मेर पालनहार! क्षमा कर दे मुझे तथा मेरे माता-पिता को और उसे, जो प्रवेश करे मेरे घर में ईमान लाकर तथा ईमान वालों और ईमान वालियों को तथा काफ़िरों के विनाश ही को अधिक कर।');
INSERT INTO "al_umari" VALUES(72,1,'72:1','(हे नबी!) कहोः मेरी ओर वह़्यी (प्रकाश्ना[) की गयी है कि ध्यान से सुना जिन्नों के एक समूह ने। फिर कहा कि हमने सुना है एक विचित्र क़ुर्आन। ');
INSERT INTO "al_umari" VALUES(72,2,'72:2','जो दिखाता है सीधी राह, तो हम ईमान लाये उसपर और हम कदापि साझी नहीं बनायेंगे अपने पालनहार के साथ किसी को।');
INSERT INTO "al_umari" VALUES(72,3,'72:3','तथा निःसंदेह महान है हमारे पालनहार की महिमा, नहीं बनाई है उसने कोई संगिनी (पत्नी) और न कोई संतान।');
INSERT INTO "al_umari" VALUES(72,4,'72:4','तथा निश्चय हम अज्ञान में कह रहे थे अल्लाह के संबंध में झूठी बातें।');
INSERT INTO "al_umari" VALUES(72,5,'72:5','और ये कि हमने समझा कि मनुष्य तथा जिन्न नहीं बोल सकते अल्लाह पर कोई झूठ बात।');
INSERT INTO "al_umari" VALUES(72,6,'72:6','और वास्तविक्ता ये है कि मनुष्य में से कुछ लोग, शरण माँगते थे जिन्नों में से कुछ लोगों की, तो उन्होंने अधिक कर दिया उनके गर्व को।');
INSERT INTO "al_umari" VALUES(72,7,'72:7','और ये कि मनुष्यों ने भी वही समझा, जो तुमने अनुमान लगाया कि कभी अल्लाह फिर जीवित नहीं करेगा, किसी को।');
INSERT INTO "al_umari" VALUES(72,8,'72:8','तथा हमने स्पर्श किया आकाश को, तो पाया कि भर दिया गया है प्रहरियों तथा उल्काओं से।');
INSERT INTO "al_umari" VALUES(72,9,'72:9','और ये कि हम बैठते थे उस (आकाश) में सुन-गुन लेने के स्थानों में और जो अब सुनने का प्रयास करेगा, वह पायेगा अपने लिए एक उल्का घात में लगा हुआ।');
INSERT INTO "al_umari" VALUES(72,10,'72:10','और ये कि हम नहीं समझ पाते कि क्या किसी बुराई का इरादा किया गया धरती वालों के साथ या इरादा किया है, उनके साथ उनके पालनहार ने सीधी राह पर लाने का?');
INSERT INTO "al_umari" VALUES(72,11,'72:11','और हममें से कुछ सदाचारी हैं और हममें से कुछ इसके विपरीत हैं। हम विभिन्न प्रकारों में विभाजित हैं।');
INSERT INTO "al_umari" VALUES(72,12,'72:12','तथा हमें विश्वास हो गया है कि हम कदापि विवश नहीं कर सकते अल्लाह को धरती में और न विवश कर सकते हैं उसे भागकर।');
INSERT INTO "al_umari" VALUES(72,13,'72:13','तथा जब हमने सुनी मार्गदर्शन की बात, तो उसपर ईमान ले आये, अब जो भी ईमान लायेगा अपने पालनहार पर, तो नहीं भय होगा उसे अधिकार हनन का और न किसी अत्याचार का।');
INSERT INTO "al_umari" VALUES(72,14,'72:14','और ये कि हममें से कुछ मुस्लिम (आज्ञाकारी) हैं और कुछ अत्याचारी हैं। तो जो आज्ञाकारी हो गये, तो उन्होंने खोज ली सीधी राह।');
INSERT INTO "al_umari" VALUES(72,15,'72:15','तथा जो अत्याचारी हैं, तो वे नरक के ईंधन हो गये।');
INSERT INTO "al_umari" VALUES(72,16,'72:16','और ये कि यदि वे स्थित रहते सीधी राह ( अर्थात इस्लाम) पर, तो हम सींचते उन्हें भरपूर जल से।');
INSERT INTO "al_umari" VALUES(72,17,'72:17','ताकि उनकी परीक्षा लें इसमें, और जो विमुख होगा अपने पालनहार के स्मरण (याद) से, तो उसे उसका पालनहार ग्रस्त करेगा कड़ी यातना में।');
INSERT INTO "al_umari" VALUES(72,18,'72:18','और ये कि मस्जिदें[ अल्लाह के लिए हैं। अतः, मत पुकारो अल्लाह के साथ किसी को। ');
INSERT INTO "al_umari" VALUES(72,19,'72:19','और ये कि जब खड़ा हुआ अल्लाह का भक्त[ उसे पुकारता हुआ, तो समीप था कि वे लोग उसपर पिल पड़ते। ');
INSERT INTO "al_umari" VALUES(72,20,'72:20','आप कह दें कि मैं तो केवल अपने पालनहार को पुकारता हूँ और साझी नहीं बनाता उसका किसी अन्य को।');
INSERT INTO "al_umari" VALUES(72,21,'72:21','आप कह दें कि मैं अधिकार नहीं रखता तुम्हारे लिए किसी हानि का, न सीधी राह पर लगा देने का।');
INSERT INTO "al_umari" VALUES(72,22,'72:22','आप कह दें कि मुझे कदापि नहीं बचा सकेगा अल्लाह से कोई[ और न मैं पा सकूँगा उसके सिवा कोई शरणगार (बचने का स्थान)। ');
INSERT INTO "al_umari" VALUES(72,23,'72:23','परन्तु, पहुँचा सकता हूँ अल्लाह का आदेश तथा उसका उपदेश और जो अवज्ञा करेगा अल्लाह तथा उसके रसूल की, तो वास्तव में उसी के लिए नरक की अग्नि है, जिसमें वह नित्य सदावासी होगा।');
INSERT INTO "al_umari" VALUES(72,24,'72:24','यहाँ तक कि जब वे देख लेंगे, जिसका उन्हें वचन दिया जाता है, तो उन्हें विश्वास हो जायेगा कि किसके सहायक निर्बल और किसकी संख्या कम है।');
INSERT INTO "al_umari" VALUES(72,25,'72:25','आप कह दें कि मैं नहीं जानता कि समीप है, जिसका वचन तुम्हें दिया जा रहा है अथाव बनायेगा मेरा पालनहार उसके लिए कोई अवधि?');
INSERT INTO "al_umari" VALUES(72,26,'72:26','वह ग़ैब (परोक्ष) का ज्ञानी है, अतः, वह अवगत नहीं कराता है अपने परोक्ष पर किसी को।');
INSERT INTO "al_umari" VALUES(72,27,'72:27','सिवाये रसूल के, जिसे उसने प्रिय बना लिया है, फिर वह लगा देता है उस वह़्यी के आगे तथा उसके पीछे रक्षक।[ ');
INSERT INTO "al_umari" VALUES(72,28,'72:28','ताकि वह देख ले कि उन्होंने पहुँचा दिये हैं अपने पालनहार के उपदेश[ और उसने घेर रखा है, जो कुछ उनके पास है और प्रत्येक वस्तु को गिन रखा है। ');
INSERT INTO "al_umari" VALUES(73,1,'73:1','हे चादर ओढ़ने वाले!');
INSERT INTO "al_umari" VALUES(73,2,'73:2','खड़े रहो (नमाज़ में) रात्रि के समय, परन्तु कुछ[ समय। ');
INSERT INTO "al_umari" VALUES(73,3,'73:3','(अर्थात) आधी रात अथवा उससे कुछ कम।');
INSERT INTO "al_umari" VALUES(73,4,'73:4','या उससे कुछ अधिक और पढ़ो क़ुर्आन रुक-रुक कर।');
INSERT INTO "al_umari" VALUES(73,5,'73:5','हम उतारेंगे (हे नबी!) आप पर एक भारी बात (क़ुर्आन)।');
INSERT INTO "al_umari" VALUES(73,6,'73:6','वास्तव में, रात में जो इबादत होती है, वह अधिक प्रभावी है (मन को) एकाग्र करने में तथा अधिक उचित है बात प्रार्थना के लिए।');
INSERT INTO "al_umari" VALUES(73,7,'73:7','आपके लिए दिन में बहुत-से कार्य हैं।');
INSERT INTO "al_umari" VALUES(73,8,'73:8','और स्मरण (याद) करें अपने पालनहार के नाम की और सबसे अलग होकर उसी के हो जायें।');
INSERT INTO "al_umari" VALUES(73,9,'73:9','वह पूर्व तथा पश्चिम का पालनहार है। नहीं है कोई पूज्य (वंदनीय) उसके सिवा, अतः, उसी को अपना करता-धरता बना लें।');
INSERT INTO "al_umari" VALUES(73,10,'73:10','और सहन करें उन बातों को, जो वे बना रहे हैं[ और अलग हो जायें उनसे सुशीलता के साथ। ');
INSERT INTO "al_umari" VALUES(73,11,'73:11','तथा छोड़ दें मुझे तथा झुठलाने वाले सुखी (सम्पन्न) लोगों को और उन्हें अवसर दें कुछ देर।');
INSERT INTO "al_umari" VALUES(73,12,'73:12','वस्तुतः, हमारे पास (उनके लिए) बहुत-सी बेड़ियाँ तथा दहकती अग्नि है।');
INSERT INTO "al_umari" VALUES(73,13,'73:13','और भोजन, जो गले में फंस जाये और दुःखदायी यातना है।');
INSERT INTO "al_umari" VALUES(73,14,'73:14','जिस दिन काँपेगी धरती और पर्वत तथा हो जायेंगे पर्वत, भुरभुरे रेत के ढेर।');
INSERT INTO "al_umari" VALUES(73,15,'73:15','हमने भेजा है तुम्हारी ओर एक रसूल[ तुमपर गवाह (साक्षी) बनाकर, जैसे फ़िरऔन की ओर एक रसूल (मूसा) को। ');
INSERT INTO "al_umari" VALUES(73,16,'73:16','तो अवज्ञा की फ़िरऔन ने उस रसूल की और हमने पकड़ लिया उसे कड़ी पकड़।');
INSERT INTO "al_umari" VALUES(73,17,'73:17','तो कैसे बचोगे, यदि कुफ़्र किया तुमने उस दिन से, जो बना देगा बच्चों को (शोक के कारण) बूढ़ा?');
INSERT INTO "al_umari" VALUES(73,18,'73:18','आकाश फट जायेगा उस दिन। उसका वचन पूरा होकर रहेगा।');
INSERT INTO "al_umari" VALUES(73,19,'73:19','वास्तव में, ये (आयतें) एक शिक्षा हैं। तो जो चाहे, अपने पालनहार की ओर राह बना ले।[ ');
INSERT INTO "al_umari" VALUES(73,20,'73:20','निःसंदेह, आपका पालनहार जानता है कि आप खड़े होते हैं (तहुज्जुद की नमाज़ के लिए) दो तिहाई रात्रि के लग-भग तथा आधी रात और तिहाई रात तथा एक समूह उन लोगों का, जो आपके साथ हैं और अल्लाह ही ह़िसाब रखता है रात तथा दिन का। वह जानता है कि तुम पूरी रात नमाज़ के लिए खड़े नहीं हो सकोगे, अतः, उसने दया कर दी तुमपर। तो पढ़ो जितना सरल हो क़ुर्आन में से।[ वह जानता है कि तुममें कुछ रोगी होंगे और कुछ दुसरे यात्रा करेंगे धरती में खोज करते हुए अल्लाह के अनुग्रह (जीविका) की और कुछ दूसरे युध्द करेंगे अल्लाह की राह में, अतः, पढ़ो जितना सरल हो उसमें से तथा स्थापना करो नमाज़ की, ज़कात देते रहो और ऋण दो अल्लाह को अच्छा ऋण तथा जो भी आगे भेजोगे भलाई में से, तो उसे अल्लाह के पास पाओगे। वही उत्तम और उसका बहुत बड़ा प्रतिफल होगा और क्षमा माँगते रहो अल्लाह से, वास्तव में वह अति क्षमाशील, दयावान् है।');
INSERT INTO "al_umari" VALUES(74,1,'74:1','हे चादर ओढ़ने[ वाले! ');
INSERT INTO "al_umari" VALUES(74,2,'74:2','खड़े हो जाओ, फिर सावधान करो।');
INSERT INTO "al_umari" VALUES(74,3,'74:3','तथा अपने पालनहार की महिमा का वर्णन करो।');
INSERT INTO "al_umari" VALUES(74,4,'74:4','तथा अपने कपड़ों को पवित्र रखो।');
INSERT INTO "al_umari" VALUES(74,5,'74:5','और मलीनता को त्याग दो।');
INSERT INTO "al_umari" VALUES(74,6,'74:6','तथा उपकार न करो इसलिए कि उसके द्वारा अधिक लो।');
INSERT INTO "al_umari" VALUES(74,7,'74:7','और अपने पालनहार ही के लिए सहन करो।');
INSERT INTO "al_umari" VALUES(74,8,'74:8','फिर जब फूँका जायेगा[ नरसिंघा में। ');
INSERT INTO "al_umari" VALUES(74,9,'74:9','तो उस दिन अति भीषण दिन होगा।');
INSERT INTO "al_umari" VALUES(74,10,'74:10','काफ़िरों पर सरल न होगा।');
INSERT INTO "al_umari" VALUES(74,11,'74:11','आप छोड़ दें मुझे और उसे, जिसे मैंने पैदा किया अकेला।');
INSERT INTO "al_umari" VALUES(74,12,'74:12','फिर दे दिया उसे अत्यधिक धन।');
INSERT INTO "al_umari" VALUES(74,13,'74:13','और पुत्र उपस्थित रहने[ वाले। ');
INSERT INTO "al_umari" VALUES(74,14,'74:14','और दिया मैंने उसे प्रत्येक प्रकार का संसाधन।');
INSERT INTO "al_umari" VALUES(74,15,'74:15','फिर भी वह लोभ रखता है कि उसे और अधिक दूँ।');
INSERT INTO "al_umari" VALUES(74,16,'74:16','कदापि नहीं। वह हमारी आयतों का विरोधी है।');
INSERT INTO "al_umari" VALUES(74,17,'74:17','मैं उसे चढ़ाऊँगा कड़ी[ चढ़ाई। ');
INSERT INTO "al_umari" VALUES(74,18,'74:18','उसने विचार किया और अनुमान लगाया।[ ');
INSERT INTO "al_umari" VALUES(74,19,'74:19','वह मारा जाये! फिर उसने कैसा अनुमान लगाया?');
INSERT INTO "al_umari" VALUES(74,20,'74:20','फिर (उसपर अल्लाह की) मार! उसने कैसा अनुमान लगाया?');
INSERT INTO "al_umari" VALUES(74,21,'74:21','फिर पुनः विचार किया।');
INSERT INTO "al_umari" VALUES(74,22,'74:22','फिर माथे पर बल दिया और मुँह बिदोरा।');
INSERT INTO "al_umari" VALUES(74,23,'74:23','फिर (सत्य से) पीछे फिरा और घमंड किया।');
INSERT INTO "al_umari" VALUES(74,24,'74:24','और बोला कि ये तो पहले से चला आ रहा है, एक जादू है।[ ');
INSERT INTO "al_umari" VALUES(74,25,'74:25','ये तो बस मनुष्य[ का कथन है। ');
INSERT INTO "al_umari" VALUES(74,26,'74:26','मैं उसे शीघ्र ही नरक में झोंक दूँगा।');
INSERT INTO "al_umari" VALUES(74,27,'74:27','और आप क्या जानें कि नरक क्या है।');
INSERT INTO "al_umari" VALUES(74,28,'74:28','न शेष रखेगी और न छोड़ेगी।');
INSERT INTO "al_umari" VALUES(74,29,'74:29','वह खाल झुलसा देने वाली।');
INSERT INTO "al_umari" VALUES(74,30,'74:30','नियुक्त हैं उनपर उन्नीस (रक्षख फ़रिश्ते)।');
INSERT INTO "al_umari" VALUES(74,31,'74:31','और हमने नरक के रक्षक फ़रिश्ते ही बनाये हैं और उनकी संख्या को काफ़िरों के लिए परीक्षा बना दिया गया है। ताकि विश्वास कर लें अह्ले[ किताब और बढ़ें जो ईमान लाये हैं ईमान में और संदेह न करें जो पुस्तक दिये गये हैं और ईमान वाले और ताकि कहें वे जिनके दिलों में (द्विधा का) रोग है तथा काफ़िर कि क्या तात्पर्य है अल्लाह का इस उदाहरण से? ऐसे ही कुपथ करता है अल्लाह जिसे चाहता है और संमार्ग दर्शाता है, जिसे चाहता है और नहीं जानता है आपके पालनहार की सेनाओं को उसके सिवा कोई और तथा नहीं है ये नरक की चर्चा, किन्तु मनुष्य की शिक्षा के लिए।');
INSERT INTO "al_umari" VALUES(74,32,'74:32','ऐसी बात नहीं, शपथ है चाँद की!');
INSERT INTO "al_umari" VALUES(74,33,'74:33','तथा रात्रि की, जब व्यतीत होने लगे!');
INSERT INTO "al_umari" VALUES(74,34,'74:34','और प्रातः की, जब प्रकाशित हो जाये!');
INSERT INTO "al_umari" VALUES(74,35,'74:35','वास्तव में, (नरक) एक[ बहुत बड़ी चीज़ है। ');
INSERT INTO "al_umari" VALUES(74,36,'74:36','डराने के लिए लोगों को।');
INSERT INTO "al_umari" VALUES(74,37,'74:37','उसके लिए तुममें से, जो चाहे[ आगे होना अथवा पीछे रहना। ');
INSERT INTO "al_umari" VALUES(74,38,'74:38','प्रत्येक प्राणी अपने कर्मों के बदले में बंधक है।[ ');
INSERT INTO "al_umari" VALUES(74,39,'74:39','दाहिने वालों के सिवा।');
INSERT INTO "al_umari" VALUES(74,40,'74:40','वे स्वर्गों में होंगे। वे प्रश्न करेंगे।');
INSERT INTO "al_umari" VALUES(74,41,'74:41','अपराधियों से।');
INSERT INTO "al_umari" VALUES(74,42,'74:42','तुम्हें क्या चीज़ ले गयी नरक में।');
INSERT INTO "al_umari" VALUES(74,43,'74:43','वे कहेंगेः हम नहीं थे नमाज़ियों में से।');
INSERT INTO "al_umari" VALUES(74,44,'74:44','और नहीं भोजन कराते थे निर्धन को।');
INSERT INTO "al_umari" VALUES(74,45,'74:45','तथा कुरेद करते थे कुरेद करने वालों के साथ।');
INSERT INTO "al_umari" VALUES(74,46,'74:46','और हम झुठलाया करते थे प्रतिफल के दिन (प्रलय) को।');
INSERT INTO "al_umari" VALUES(74,47,'74:47','यहाँ तक कि हमारी मौत आ गई।');
INSERT INTO "al_umari" VALUES(74,48,'74:48','तो उन्हें लाभ नहीं देगी शिफ़ारिशियों (अभिस्तावकों) की शिफ़ारिश।[ ');
INSERT INTO "al_umari" VALUES(74,49,'74:49','तो उन्हें क्या हो गया है कि इस शिक्षा (क़ुर्आन) से मुँह फेर रहे हैं?');
INSERT INTO "al_umari" VALUES(74,50,'74:50','मानो वे (जंगली) गधे हैं, बिदकाये हुए।');
INSERT INTO "al_umari" VALUES(74,51,'74:51','जो शिकारी से भागे हैं।');
INSERT INTO "al_umari" VALUES(74,52,'74:52','बल्कि चाहता है प्रत्येक व्यक्ति उनमें से कि उसे खुली[ पुस्तक दी जाये। ');
INSERT INTO "al_umari" VALUES(74,53,'74:53','कदापि ये नहीं (हो सकता) बल्कि वे आख़िरत (परलोक) से नहीं डरते हैं।');
INSERT INTO "al_umari" VALUES(74,54,'74:54','निश्चय ये (क़ुर्आन) तो एक शिक्षा है।');
INSERT INTO "al_umari" VALUES(74,55,'74:55','अब जो चाहे, शिक्षा ग्रहण करे।');
INSERT INTO "al_umari" VALUES(74,56,'74:56','और वे शिक्षा ग्रहण नहीं कर सकते, परन्तु ये कि अल्लाह चाह ले। वही योग्य है कि उससे डरा जाये और योग्य है कि क्षमा कर दे।');
INSERT INTO "al_umari" VALUES(75,1,'75:1','मैं शपथ लेता हूँ क़्यामत (प्रलय) के दिन[ की! ');
INSERT INTO "al_umari" VALUES(75,2,'75:2','तथा शपथ लेता हूँ निन्दा[ करने वाली अन्तरात्मा की। ');
INSERT INTO "al_umari" VALUES(75,3,'75:3','क्या मनुष्य समझता है कि हम एकत्र नहीं कर सकेंगे दोबारा उसकी अस्थियों को?');
INSERT INTO "al_umari" VALUES(75,4,'75:4','क्यों नहीं? हम सामर्थ्वान हैं इस बात पर कि सीधी कर दें, उसकी उंगलियों की पोर-पोर।');
INSERT INTO "al_umari" VALUES(75,5,'75:5','बल्कि मनुष्य चाहता है कि वह कुकर्म करता रहे अपने आगे[ भी। ');
INSERT INTO "al_umari" VALUES(75,6,'75:6','वह प्रश्न करता है कि कब आना है प्रलय का दिन?');
INSERT INTO "al_umari" VALUES(75,7,'75:7','तो जब चुंधिया जायेगी आँख।');
INSERT INTO "al_umari" VALUES(75,8,'75:8','और गहना जायेगा चाँद।');
INSERT INTO "al_umari" VALUES(75,9,'75:9','और एकत्र कर दिये[ जायेंगे सूर्य और चाँद। ');
INSERT INTO "al_umari" VALUES(75,10,'75:10','कहेगा मनुष्य उस दिन कि कहाँ है भागने का स्थान?');
INSERT INTO "al_umari" VALUES(75,11,'75:11','कदापि नहीं, कोई शरणागार नहीं।');
INSERT INTO "al_umari" VALUES(75,12,'75:12','तेरे पालनहार की ओर ही उस दिन जाकर रुकना है।');
INSERT INTO "al_umari" VALUES(75,13,'75:13','सूचित कर दिया जायेगा मनुष्य को उस दिन उससे, जो उसने आगे भेजा तथा जो पीछे[ छोड़ा। ');
INSERT INTO "al_umari" VALUES(75,14,'75:14','बल्कि मनुष्य स्वयं अपने विरुध्द एक खुला[ प्रमाण है। ');
INSERT INTO "al_umari" VALUES(75,15,'75:15','चाहे वह कितने ही बहाने बनाये।');
INSERT INTO "al_umari" VALUES(75,16,'75:16','हे नबी! आप न हिलायें[ अपनी ज़ुबान, ताकि शीघ्र याद कर लें इस क़ुर्आन को। ');
INSERT INTO "al_umari" VALUES(75,17,'75:17','निश्चय हमपर है उसे याद कराना और उसे पढ़ाना।');
INSERT INTO "al_umari" VALUES(75,18,'75:18','अतः, जब हम उसे पढ़ लें, तो आप उसके पीछे पढ़ें।');
INSERT INTO "al_umari" VALUES(75,19,'75:19','फिर हमारे ही ऊपर है, उसका अर्थ बताना।');
INSERT INTO "al_umari" VALUES(75,20,'75:20','कदापि नहीं[, बल्कि तुम प्रेम करते हो शीघ्र प्राप्त होने वाली चीज़ (संसार) से। ');
INSERT INTO "al_umari" VALUES(75,21,'75:21','और छोड़ देते हो परलोक को।');
INSERT INTO "al_umari" VALUES(75,22,'75:22','बहुत-से मुख उस दिन प्रफुल्ल होंगे।');
INSERT INTO "al_umari" VALUES(75,23,'75:23','अपने पालनहार की ओर देख रहे होंगे।');
INSERT INTO "al_umari" VALUES(75,24,'75:24','और बहुत-से मुख उदास होंगे।');
INSERT INTO "al_umari" VALUES(75,25,'75:25','वह समझ रहे होंगे कि उनके साथ कड़ा व्यवहार किया जायेगा।');
INSERT INTO "al_umari" VALUES(75,26,'75:26','कदापि नहीं[, जब पहुँचेगी प्राण हंसलियों (गलों) तक। ');
INSERT INTO "al_umari" VALUES(75,27,'75:27','और कहा जायेगाः कौन झाड़-फूँक करने वाला है?');
INSERT INTO "al_umari" VALUES(75,28,'75:28','और विश्वास हो जायेगा कि ये (संसार से) जुदाई का समय है।');
INSERT INTO "al_umari" VALUES(75,29,'75:29','और मिल जायेगी पिंडली, पिंडली[ से। ');
INSERT INTO "al_umari" VALUES(75,30,'75:30','तेरे पालनहार की ओर उसी दिन जाना है।');
INSERT INTO "al_umari" VALUES(75,31,'75:31','तो न उसने सत्य को माना और न नमाज़ पढ़ी।');
INSERT INTO "al_umari" VALUES(75,32,'75:32','किन्तु झुठलाया और मुँह फेर लिया।');
INSERT INTO "al_umari" VALUES(75,33,'75:33','फिर, गया अपने परिजनों की ओर अकड़ता हआ।');
INSERT INTO "al_umari" VALUES(75,34,'75:34','शोक है तेरे लिए, फिर शोक है।');
INSERT INTO "al_umari" VALUES(75,35,'75:35','फिर शोक है तेरे लिए, फिर शोक है।');
INSERT INTO "al_umari" VALUES(75,36,'75:36','क्या मनुष्य समझता है कि वह छोड़ दिया जायेगा व्यर्थ?[ ');
INSERT INTO "al_umari" VALUES(75,37,'75:37','क्या वह नहीं था वीर्य की बूंद, जो (गर्भाशय में) बूँद-बूँद गिराई जाती है।?');
INSERT INTO "al_umari" VALUES(75,38,'75:38','फिर वह बंधा रक्त हुआ, फिर अल्लाह ने उसे पैदा किया और उसे बराबर बनाया।');
INSERT INTO "al_umari" VALUES(75,39,'75:39','फिर उसका जोड़ाः नर और नारी बनाया।');
INSERT INTO "al_umari" VALUES(75,40,'75:40','तो क्या वह सामर्थ्यवान नहीं कि मुर्दों को जीवित कर दे?');
INSERT INTO "al_umari" VALUES(76,1,'76:1','क्या व्यतीत हुआ मनुष्य पर युग का एक समय, जब वह कोई विचर्चित[ वस्तु न था? ');
INSERT INTO "al_umari" VALUES(76,2,'76:2','हमने ही पैदा किया मनुष्य को मिश्रित (मिले हुए) वीर्य[ से, ताकि उसकी परीक्षा लें और बनाया उसे सुनने तथा देखने वाला। ');
INSERT INTO "al_umari" VALUES(76,3,'76:3','हमने उसे राह दर्शा दी।[ (अब) वह चाहे तो कृतज्ञ बने अथवा कृतघ्न। ');
INSERT INTO "al_umari" VALUES(76,4,'76:4','निःसंदेह, हमने तैयार की काफ़िरों (कृतघ्नों) के लिए ज़ंजीर तथा तौक़ और दहकती अग्नि।');
INSERT INTO "al_umari" VALUES(76,5,'76:5','निश्चय सदाचारी (कृतज्ञ) पियेंगे ऐसे प्याले से जिसमें कपूर मिश्रित होगा।');
INSERT INTO "al_umari" VALUES(76,6,'76:6','ये एक स्रोत होगा, जिससे अल्लाह के भक्त पियेंगे। उसे बहा ले जायेंगे (जहाँ चाहेंगे)।[ ');
INSERT INTO "al_umari" VALUES(76,7,'76:7','जो (संसार में) पूरी करते रहे मनोतियाँ[ और डरते रहे उस दिन से जिसकी आपदा चारों ओर फैली हुई होगी।');
INSERT INTO "al_umari" VALUES(76,8,'76:8','और भोजन कराते रहे उस (भोजन) को प्रेम करने के बावजूद, निर्धन, अनाथ और बंदी को।');
INSERT INTO "al_umari" VALUES(76,9,'76:9','(अपने मन में ये सोचकर) हम तुम्हें भोजन कराते हैं केवल अल्लाह की प्रसन्नता के लिए। तुमसे नहीं चाहते हैं कोई बदला और न कोई कृतज्ञता।');
INSERT INTO "al_umari" VALUES(76,10,'76:10','हम डरते हैं अपने पालनहार से, उस दिन से, जो अति भीषण तथा घोर होगा।');
INSERT INTO "al_umari" VALUES(76,11,'76:11','तो बचा लिया अल्लाह ने उन्हें उस दिन की आपदा से और प्रदान कर दिया प्रफुल्लता तथा प्रसन्नता।');
INSERT INTO "al_umari" VALUES(76,12,'76:12','और उन्हें प्रतिफल दिया उनके धैर्य के बदले स्वर्ग तथा रेशमी वस्त्र।');
INSERT INTO "al_umari" VALUES(76,13,'76:13','वे तकिये लगाये उसमें तख़्तों पर बैठे होंगे। न उसमें धूप देखेंगे न कड़ा शीत।');
INSERT INTO "al_umari" VALUES(76,14,'76:14','और झुके होंगे उनपर उस (स्वर्ग) के साये और बस में किये होंगे उसके फलों के गुच्छे पूर्णतः।');
INSERT INTO "al_umari" VALUES(76,15,'76:15','तथा फिराये जायेंगे उनपर चाँदी के बर्तन तथा प्याले जो शीशों के होंगे।');
INSERT INTO "al_umari" VALUES(76,16,'76:16','चाँदी के शीशों के, जो एक अनुमान से भरेंगे।[ ');
INSERT INTO "al_umari" VALUES(76,17,'76:17','और पिलाये जायेंगे उसमें ऐसे भरे प्याले, जिसमें सोंठ मिली होगी।');
INSERT INTO "al_umari" VALUES(76,18,'76:18','ये एक स्रोत है उस (स्वर्ग) में, जिसका नाम सलसबील है।');
INSERT INTO "al_umari" VALUES(76,19,'76:19','और (सेवा के लिए) फिर रहे होंगे उनपर सदावासी बालक, जब तुम उन्हें देखोगे, तो उन्हें समझोगे कि बिखरे हुए मोती हैं।');
INSERT INTO "al_umari" VALUES(76,20,'76:20','तथा जब तुम वहाँ देखोगे, तो देखोगे बड़ा सुख तथा भारी राज्य।');
INSERT INTO "al_umari" VALUES(76,21,'76:21','उनके ऊपर रेशमी हरे महीन तथा दबीज वस्त्र होंगे और पहनाये जायेंगे उन्हें चाँदी के कंगन और पिलायेगा उन्हें उनका पालनहार पवित्र पेय।');
INSERT INTO "al_umari" VALUES(76,22,'76:22','(तथा कहा जायेगाः) यही है तुम्हारे लिए प्रतिफल और तुम्हारे प्रयास का आदर किया गया।');
INSERT INTO "al_umari" VALUES(76,23,'76:23','वास्तव में, हमने ही उतारा है आपपर क़ुर्आन थोड़ा-थोड़ा करके।[ ');
INSERT INTO "al_umari" VALUES(76,24,'76:24','अतः, आप धैर्य से काम लें अपने पालनहार के आदेशानुसार और बात न मानें, उनमें से किसी पापी तथा कृतघ्न की।');
INSERT INTO "al_umari" VALUES(76,25,'76:25','तथा स्मरण करें अपने पालनहार के नाम का, प्रातः तथा संध्या (के समय)।');
INSERT INTO "al_umari" VALUES(76,26,'76:26','तथा रात्रि में सज्दा करें उसके समक्ष और उसकी पवित्रता का वर्णन करें, रात्रि के लम्बे समय तक।');
INSERT INTO "al_umari" VALUES(76,27,'76:27','वास्तव में, ये लोग मोह रखते हैं संसार से और छोड़ रहे हैं अपने पीछे एक भारी दिन[ को। ');
INSERT INTO "al_umari" VALUES(76,28,'76:28','हमने ही उन्हें पैदा किया है और सुदृढ़ किये हैं उनके जोड़-बंद तथा जब हम चाहें बदला दें उनके[ जैसे (दूसरों को)। ');
INSERT INTO "al_umari" VALUES(76,29,'76:29','निश्चय ये (सूरह) एक शिक्षा है। अतः, जो चाहे अपने पालनहार की ओर (जाने की) राह बना ले।');
INSERT INTO "al_umari" VALUES(76,30,'76:30','और तुम अल्लाह की इच्छा के बिना कुछ भी नहीं चाह सकते।[ वास्तव में, अल्लाह सब चीज़ों और गुणों को जानने वाला है। ');
INSERT INTO "al_umari" VALUES(76,31,'76:31','वह प्रवेश देता है जिसे चाहे अपनी दया में और अत्याचारियों के लिए उसने तैयार की है दुःखदायी यातना।');
INSERT INTO "al_umari" VALUES(77,1,'77:1','शपथ है भेजी हुई निरन्तर धीमी वायुओं की!');
INSERT INTO "al_umari" VALUES(77,2,'77:2','फिर झक्कड़ वाली हवाओं की!');
INSERT INTO "al_umari" VALUES(77,3,'77:3','और बादलों को फैलाने वालियों की![ ');
INSERT INTO "al_umari" VALUES(77,4,'77:4','फिर अन्तर करने[ वालों की! ');
INSERT INTO "al_umari" VALUES(77,5,'77:5','फिर पहुँचाने वालों की वह़्यी (प्रकाशना[) को! ');
INSERT INTO "al_umari" VALUES(77,6,'77:6','क्षमा के लिए अथवा चेतावनी[ के लिए! ');
INSERT INTO "al_umari" VALUES(77,7,'77:7','निश्चय जिसका वचन तुम्हें दिया जा रहा है, वह अवश्य आनी है।');
INSERT INTO "al_umari" VALUES(77,8,'77:8','फिर जब तारे धुमिल हो जायेंगे।');
INSERT INTO "al_umari" VALUES(77,9,'77:9','तथा जब आकाश खोल दिया जायेगा।');
INSERT INTO "al_umari" VALUES(77,10,'77:10','तथा जब पर्वत चूर-चूर करके उड़ा दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(77,11,'77:11','और जब रसूलों का एक समय निर्धारित किया जायेगा।[ ');
INSERT INTO "al_umari" VALUES(77,12,'77:12','किस दिन के लिए इसे निलम्बित रखा गया है?');
INSERT INTO "al_umari" VALUES(77,13,'77:13','निर्णय के दिन के लिए।');
INSERT INTO "al_umari" VALUES(77,14,'77:14','आप क्या जानें कि क्या है वह निर्णय का दिन?');
INSERT INTO "al_umari" VALUES(77,15,'77:15','विनाश है उस दिन झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(77,16,'77:16','क्या हमने विनाश नहीं कर दिया (अवज्ञा के कारण) अगली जातियों का?');
INSERT INTO "al_umari" VALUES(77,17,'77:17','फिर पीछे लगा[ देंगे उनके पिछलों को। ');
INSERT INTO "al_umari" VALUES(77,18,'77:18','इसी प्रकार, हम करते हैं अपराधियों के साथ।');
INSERT INTO "al_umari" VALUES(77,19,'77:19','विनाश है उस दिन झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(77,20,'77:20','क्या हमने पैदा नहीं किया है तुम्हें तुच्छ जल (वीर्य) से?');
INSERT INTO "al_umari" VALUES(77,21,'77:21','फिर हमने रख दिया उसे एक सुदृढ़ स्थान (गर्भाशय) में।');
INSERT INTO "al_umari" VALUES(77,22,'77:22','एक निश्चित अवधि तक।[ ');
INSERT INTO "al_umari" VALUES(77,23,'77:23','तो हमने सामर्थ्य[ रखा, अतः हम अच्छा सामर्थ्य रखने वाले हैं। ');
INSERT INTO "al_umari" VALUES(77,24,'77:24','विनाश है उस दिन झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(77,25,'77:25','क्या हमने नहीं बनाया धरती को समेटकर[ रखने वाली? ');
INSERT INTO "al_umari" VALUES(77,26,'77:26','जीवित तथा मुर्दों को।');
INSERT INTO "al_umari" VALUES(77,27,'77:27','तथा बना दिये हमने उसमें बहुत-से ऊँचे पर्वत और पिलाया हमने तुम्हें मीठा जल।');
INSERT INTO "al_umari" VALUES(77,28,'77:28','विनाश है उस दिन झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(77,29,'77:29','(कहा जायेगाः) चलो उस (नरक) की ओर जिसे तुम झुठलाते रहे।');
INSERT INTO "al_umari" VALUES(77,30,'77:30','चलो ऐसी छाया[ की ओर जो तीन शाखाओं वाली है। ');
INSERT INTO "al_umari" VALUES(77,31,'77:31','जो न छाया देगी और न ज्वाला से बचायेगी।');
INSERT INTO "al_umari" VALUES(77,32,'77:32','वह (अग्नि) फेंकती होगी चिँगारियाँ भवन के समान।');
INSERT INTO "al_umari" VALUES(77,33,'77:33','जैसे वह पीले ऊँट हों।');
INSERT INTO "al_umari" VALUES(77,34,'77:34','विनाश है उस दिन झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(77,35,'77:35','ये वो दिन है कि वे बोल[ नहीं सकेंगे। ');
INSERT INTO "al_umari" VALUES(77,36,'77:36','और न उन्हें अनुमति दी जायेगी कि वे बहाने बना सकें।');
INSERT INTO "al_umari" VALUES(77,37,'77:37','विनाश है उस दिन झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(77,38,'77:38','ये निर्णय का दिन है, हमने एकत्र कर लिया है तुम्हें तथा पूर्व के लोगों को।');
INSERT INTO "al_umari" VALUES(77,39,'77:39','तो यदि तुम्हारे पास कोई चाल[ हो, तो चल लो। ');
INSERT INTO "al_umari" VALUES(77,40,'77:40','विनाश है उस दिन झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(77,41,'77:41','निःसंदेह, आज्ञाकारी उस दिन छाँव तथा जल स्रोतों में होंगे।');
INSERT INTO "al_umari" VALUES(77,42,'77:42','तथा मन चाहे फलों में।');
INSERT INTO "al_umari" VALUES(77,43,'77:43','खाओ तथा पिओ मनमानी उन कर्मों के बदले, जो तुम करते रहे।');
INSERT INTO "al_umari" VALUES(77,44,'77:44','हम इसी प्रकार प्रतिफल देते हैं।');
INSERT INTO "al_umari" VALUES(77,45,'77:45','विनाश है उस दिन झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(77,46,'77:46','(हे झुठलाने वालो!) तुम खा लो तथा आनन्द ले लो कुछ[ दिन। वास्तव में, तुम अपराधी हो। ');
INSERT INTO "al_umari" VALUES(77,47,'77:47','विनाश है उस दिन झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(77,48,'77:48','जब उनसे कहा जाता है कि (अल्लाह के समक्ष) झुको, तो झुकते नहीं।');
INSERT INTO "al_umari" VALUES(77,49,'77:49','विनाश है उस दिन झुठलाने वालों के लिए।');
INSERT INTO "al_umari" VALUES(77,50,'77:50','तो (अब) वे किस बात पर इस (क़ुर्आन) के पश्चात् ईमान[ लायेंगे? ');
INSERT INTO "al_umari" VALUES(78,1,'78:1','वे आपस में किस विषय में प्रश्न कर रहे हैं?');
INSERT INTO "al_umari" VALUES(78,2,'78:2','बहुत बड़ी सूचना के विषय में।');
INSERT INTO "al_umari" VALUES(78,3,'78:3','जिसमें मतभेद कर रहे हैं।');
INSERT INTO "al_umari" VALUES(78,4,'78:4','निश्चय वे जान लेंगे।');
INSERT INTO "al_umari" VALUES(78,5,'78:5','फिर निश्चय वे जान लेंगे।[ ');
INSERT INTO "al_umari" VALUES(78,6,'78:6','क्या हमने धरती को पालना नहीं बनाया?');
INSERT INTO "al_umari" VALUES(78,7,'78:7','और पर्वतों को मेख?');
INSERT INTO "al_umari" VALUES(78,8,'78:8','तथा तुम्हें जोड़े-जोड़े पैदा किया।');
INSERT INTO "al_umari" VALUES(78,9,'78:9','तथा तुम्हारी निद्रा को स्थिरता (आराम) बनाया।');
INSERT INTO "al_umari" VALUES(78,10,'78:10','और रात को वस्त्र बनाया।');
INSERT INTO "al_umari" VALUES(78,11,'78:11','और दिन को कमाने के लिए बनाया।');
INSERT INTO "al_umari" VALUES(78,12,'78:12','तथा हमने तुम्हारे ऊपर सात दृढ़ आकाश बनाये।');
INSERT INTO "al_umari" VALUES(78,13,'78:13','और एक दमकता दीप (सूर्य) बनाया।');
INSERT INTO "al_umari" VALUES(78,14,'78:14','और बादलों से मूसलाधार वर्षा की।');
INSERT INTO "al_umari" VALUES(78,15,'78:15','ताकि उससे अन्न और वनस्पति उपजायें।');
INSERT INTO "al_umari" VALUES(78,16,'78:16','और घने-घने बाग़।[ ');
INSERT INTO "al_umari" VALUES(78,17,'78:17','निश्चय निर्णय (फ़ैसले) का दिन निश्चित है।');
INSERT INTO "al_umari" VALUES(78,18,'78:18','जिस दिन सूर में फूँका जायेगा। फिर तुम दलों ही दलों में चले आओगे।');
INSERT INTO "al_umari" VALUES(78,19,'78:19','और आकाश खोल दिया जायेगा, तो उसमें द्वार ही द्वार हो जायेंगे।');
INSERT INTO "al_umari" VALUES(78,20,'78:20','और पर्वत चला दिये जायेंगे, तो वे मरिचिका बन जायेंगे।[ ');
INSERT INTO "al_umari" VALUES(78,21,'78:21','वास्तव में, नरक घात में है।');
INSERT INTO "al_umari" VALUES(78,22,'78:22','जो दुराचारियों का स्थान है।');
INSERT INTO "al_umari" VALUES(78,23,'78:23','जिसमें वे असंख्य वर्षों तक रहेंगे।');
INSERT INTO "al_umari" VALUES(78,24,'78:24','उसमें ठणडी तथा पेय (पीने की चीज़) नहीं चखेंगे।');
INSERT INTO "al_umari" VALUES(78,25,'78:25','सिवाये गर्म पानी और पीप रक्त के।');
INSERT INTO "al_umari" VALUES(78,26,'78:26','ये पूरा-पूरा प्रतिफल है।');
INSERT INTO "al_umari" VALUES(78,27,'78:27','निःसंदेह वे ह़िसाब की आशा नहीं रखते थे।');
INSERT INTO "al_umari" VALUES(78,28,'78:28','तथा वे हमारी आयतों को झुठलाते थे।');
INSERT INTO "al_umari" VALUES(78,29,'78:29','और हमने सब विषय लिखकर सुरक्षित कर लिये हैं।');
INSERT INTO "al_umari" VALUES(78,30,'78:30','तो चखो, हम तुम्हारी यातना अधिक ही करते रहेंगे।[ ');
INSERT INTO "al_umari" VALUES(78,31,'78:31','वास्तव में, जो डरते हैं उन्हीं के लिए सफलता है।');
INSERT INTO "al_umari" VALUES(78,32,'78:32','बाग़ तथा अँगूर हैं।');
INSERT INTO "al_umari" VALUES(78,33,'78:33','और नवयुवति कुमारियाँ।');
INSERT INTO "al_umari" VALUES(78,34,'78:34','और छलकते प्याले।');
INSERT INTO "al_umari" VALUES(78,35,'78:35','उसमें बकवास और मिथ्या बातें नहीं सुनेंगे।');
INSERT INTO "al_umari" VALUES(78,36,'78:36','ये तुम्हारे पालनहार की ओर से भरपूर पुरस्कार है।');
INSERT INTO "al_umari" VALUES(78,37,'78:37','जो आकाश, धरती तथा जो उनके बीच है, सबका अति करुणामय पालनहार है। जिससे बात करने का वे साहस नहीं कर सकेंगे।');
INSERT INTO "al_umari" VALUES(78,38,'78:38','जिस दिन रूह़ (जिब्रील) तथा फ़रिश्ते पंक्तियों में खड़े होंगे, वही बात कर सकेगा जिसे रहमान (अल्लाह) आज्ञा देगा और सह़ीह़ बात करेगा।');
INSERT INTO "al_umari" VALUES(78,39,'78:39','वह दिन निःसंदेह होना ही है। अतः जो चाहे अपने पालनहार की ओर (जाने का) ठिकाना बना ले।[ ');
INSERT INTO "al_umari" VALUES(78,40,'78:40','हमने तुम्हें समीप यातना से सावधान कर दिया, जिस दिन इन्सान अपना करतूत देखेगा और काफ़िर (विश्वासहीन) कहेगा कि काश मैं मिट्टी हो जाता![ ');
INSERT INTO "al_umari" VALUES(79,1,'79:1','शपथ है उन फ़रिश्तों की जो डूबकर (प्राण) निकालते हैं!');
INSERT INTO "al_umari" VALUES(79,2,'79:2','और जो सरलता से (प्राण) निकालते हैं।');
INSERT INTO "al_umari" VALUES(79,3,'79:3','और जो तैरते रहते हैं।');
INSERT INTO "al_umari" VALUES(79,4,'79:4','फिर जो आगे निकल जाते हैं।');
INSERT INTO "al_umari" VALUES(79,5,'79:5','फिर जो कार्य की व्यवस्था करते हैं।[ ');
INSERT INTO "al_umari" VALUES(79,6,'79:6','जिस दिन धरती काँपेगी।');
INSERT INTO "al_umari" VALUES(79,7,'79:7','जिसके पीछे ही दूसरी कम्प आ जायेगी।');
INSERT INTO "al_umari" VALUES(79,8,'79:8','उस दिन बहुत-से दिल धड़क रहे होंगे।');
INSERT INTO "al_umari" VALUES(79,9,'79:9','उनकी आँखें झुकी होंगी।');
INSERT INTO "al_umari" VALUES(79,10,'79:10','वे कहते हैं कि क्या हम फिर पहली स्थिति में लाये जायेंगे?');
INSERT INTO "al_umari" VALUES(79,11,'79:11','जब हम (भुरभुरी) (खोखली) अस्थियाँ (हड्डियाँ) हो जायेंगे।');
INSERT INTO "al_umari" VALUES(79,12,'79:12','उन्होंने कहाः तब तो इस वापसी में क्षति है।');
INSERT INTO "al_umari" VALUES(79,13,'79:13','बस वह एक झिड़की होगी।');
INSERT INTO "al_umari" VALUES(79,14,'79:14','तब वे अकस्मात धरती के ऊपर होंगे।');
INSERT INTO "al_umari" VALUES(79,15,'79:15','(हे नबी!) क्या तुम्हें मूसा का समाचार पहुँचा?[ ');
INSERT INTO "al_umari" VALUES(79,16,'79:16','जब पवित्र वादी ''तुवा'' में उसे उसके पालनहार ने पुकारा।');
INSERT INTO "al_umari" VALUES(79,17,'79:17','फ़िरऔन के पास जाओ, वह विद्रोही हो गया है।');
INSERT INTO "al_umari" VALUES(79,18,'79:18','तथा उससे कहो कि क्या तुम पवित्र होना चाहोगे?');
INSERT INTO "al_umari" VALUES(79,19,'79:19','और मैं तुम्हें तुम्हारे पालनहार की सीधी राह दिखाऊँ, तो तुम डरोगे?');
INSERT INTO "al_umari" VALUES(79,20,'79:20','फिर उसे सबसे बड़ा चिन्ह (चमत्कार) दिखाया।');
INSERT INTO "al_umari" VALUES(79,21,'79:21','तो उसने उसे झुठला दिया और बात न मानी।');
INSERT INTO "al_umari" VALUES(79,22,'79:22','फिर प्रयास करने लगा।');
INSERT INTO "al_umari" VALUES(79,23,'79:23','फिर लोगों को एकत्र किया, फिर पुकारा।');
INSERT INTO "al_umari" VALUES(79,24,'79:24','और कहाः मैं तुम्हारा परम पालनहार हूँ।');
INSERT INTO "al_umari" VALUES(79,25,'79:25','तो अल्लाह ने उसे संसार तथा परलोक की यातना में घेर लिया।');
INSERT INTO "al_umari" VALUES(79,26,'79:26','वास्तव में, इसमें उसके लिए शिक्षा है, जो डरता है।');
INSERT INTO "al_umari" VALUES(79,27,'79:27','क्या तुम्हें पैदा करना कठिन है अथवा आकाश को, जिसे उसने बनाया।[ ');
INSERT INTO "al_umari" VALUES(79,28,'79:28','उसकी छत ऊँची की और चौरस किया।');
INSERT INTO "al_umari" VALUES(79,29,'79:29','और उसकी रात को अंधेरी तथा दिन को उजाला किया।');
INSERT INTO "al_umari" VALUES(79,30,'79:30','और इसके बाद धरती को फैलाया।');
INSERT INTO "al_umari" VALUES(79,31,'79:31','और उससे पानी और चारा निकाला।');
INSERT INTO "al_umari" VALUES(79,32,'79:32','और पर्वतों को गाड़ दिया।');
INSERT INTO "al_umari" VALUES(79,33,'79:33','तुम्हारे तथा तुम्हारे पशुओं के लाभ के लिए।');
INSERT INTO "al_umari" VALUES(79,34,'79:34','तो जब प्रलय आयेगी।[ ');
INSERT INTO "al_umari" VALUES(79,35,'79:35','उस दिन इन्सान अपना करतूत याद करेगा।[ ');
INSERT INTO "al_umari" VALUES(79,36,'79:36','और देखने वाले के लिए नरक सामने कर दी जायेगी।');
INSERT INTO "al_umari" VALUES(79,37,'79:37','तो जिसने विद्रोह किया।');
INSERT INTO "al_umari" VALUES(79,38,'79:38','और सांसारिक जीवन को प्राथमिक्ता दी।');
INSERT INTO "al_umari" VALUES(79,39,'79:39','तो नरक ही उसका आवास होगी।');
INSERT INTO "al_umari" VALUES(79,40,'79:40','परन्तु, जो अपने पालनहार की महानता से डरा तथा अपने आपको मनमानी करने से रोका।');
INSERT INTO "al_umari" VALUES(79,41,'79:41','तो निश्चय ही उसका आवास स्वर्ग है।');
INSERT INTO "al_umari" VALUES(79,42,'79:42','वे आपसे प्रश्न करते हैं कि वह समय कब आयेगा?[ ');
INSERT INTO "al_umari" VALUES(79,43,'79:43','तुम उसकी चर्चा में क्यों पड़े हो?');
INSERT INTO "al_umari" VALUES(79,44,'79:44','उसके होने के समय का ज्ञान तुम्हारे पालनहार के पास है।');
INSERT INTO "al_umari" VALUES(79,45,'79:45','तुम तो उसे सावधान करने के लिए हो, जो उससे डरता है।[ ');
INSERT INTO "al_umari" VALUES(79,46,'79:46','वे जिस दिन उसका दर्शन करेंगे, उन्हें ऐसा लगेगा कि वे संसार में एक संध्या या उसके सवेरे से अधिक नहीं ठहरे।');
INSERT INTO "al_umari" VALUES(80,1,'80:1','(नबी ने) त्योरी चढ़ाई तथा मुँह फेर लिया।');
INSERT INTO "al_umari" VALUES(80,2,'80:2','इस कारण कि उसके पास एक अँधा आया।');
INSERT INTO "al_umari" VALUES(80,3,'80:3','और तुम क्या जानो शायद वह पवित्रता प्राप्त करे।');
INSERT INTO "al_umari" VALUES(80,4,'80:4','या नसीह़त ग्रहण करे, जो उसे लाभ देती।');
INSERT INTO "al_umari" VALUES(80,5,'80:5','परन्तु, जो विमुख (निश्चिन्त) है।');
INSERT INTO "al_umari" VALUES(80,6,'80:6','तुम उनकी ओर ध्यान दे रहे हो।');
INSERT INTO "al_umari" VALUES(80,7,'80:7','जबकि तुमपर कोई दोष नहीं, यदि वह पवित्रता ग्रहण न करे।');
INSERT INTO "al_umari" VALUES(80,8,'80:8','तथा जो तुम्हारे पास दौड़ता आया।');
INSERT INTO "al_umari" VALUES(80,9,'80:9','और वह डर भी रहा है।');
INSERT INTO "al_umari" VALUES(80,10,'80:10','तुम उसकी ओर ध्यान नहीं देते।[ ');
INSERT INTO "al_umari" VALUES(80,11,'80:11','कदापि ये न करो, ये (अर्थात क़ुर्आन) एक स्मृति (याद दहानी) है।');
INSERT INTO "al_umari" VALUES(80,12,'80:12','अतः, जो चाहे स्मरण (याद) करे।');
INSERT INTO "al_umari" VALUES(80,13,'80:13','माननीय शास्त्रों में है।');
INSERT INTO "al_umari" VALUES(80,14,'80:14','जो ऊँचे तथा पवित्र हैं।');
INSERT INTO "al_umari" VALUES(80,15,'80:15','ऐसे लेखकों (फ़रिश्तों) के हाथों में है।');
INSERT INTO "al_umari" VALUES(80,16,'80:16','जो सम्मानित और आदरणीय हैं।[ ');
INSERT INTO "al_umari" VALUES(80,17,'80:17','इन्सान मारा जाये, वह कितना कृतघ्न (नाशुक्रा) है।');
INSERT INTO "al_umari" VALUES(80,18,'80:18','उसे किस वस्तु से (अल्लाह) ने पैदा किया?');
INSERT INTO "al_umari" VALUES(80,19,'80:19','उसे वीर्य से पैदा किया, फिर उसका भाग्य बनाया।');
INSERT INTO "al_umari" VALUES(80,20,'80:20','फिर उसके लिए मार्ग सरल किया।');
INSERT INTO "al_umari" VALUES(80,21,'80:21','फिर मौत दी, फिर समाधि में डाल दिया।');
INSERT INTO "al_umari" VALUES(80,22,'80:22','फिर जब चाहेगा, उसे जीवित कर लेगा।');
INSERT INTO "al_umari" VALUES(80,23,'80:23','वस्तुतः, उसने उसकी आज्ञा का पालन नहीं किया।[ ');
INSERT INTO "al_umari" VALUES(80,24,'80:24','इन्सान अपने भोजन की ओर ध्यान दे।');
INSERT INTO "al_umari" VALUES(80,25,'80:25','हमने मूसलाधार वर्षा की।');
INSERT INTO "al_umari" VALUES(80,26,'80:26','फिर धरती को चीरा फाड़ा।');
INSERT INTO "al_umari" VALUES(80,27,'80:27','फिर उससे अन्न उगाया।');
INSERT INTO "al_umari" VALUES(80,28,'80:28','तथा अंगूर और तरकारियाँ।');
INSERT INTO "al_umari" VALUES(80,29,'80:29','तथा ज़ैतून एवं खजूर।');
INSERT INTO "al_umari" VALUES(80,30,'80:30','तथा घने बाग़।');
INSERT INTO "al_umari" VALUES(80,31,'80:31','एवं फल तथा वनस्पतियाँ।');
INSERT INTO "al_umari" VALUES(80,32,'80:32','तुम्हारे तथा तुम्हारे पशुओं के लिए।[ ');
INSERT INTO "al_umari" VALUES(80,33,'80:33','तो जब कान फाड़ देने वाली (प्रलय) आ जायेगी।');
INSERT INTO "al_umari" VALUES(80,34,'80:34','उस दिन इन्सान अपने भाई से भागेगा।');
INSERT INTO "al_umari" VALUES(80,35,'80:35','तथा अपने माता और पिता से।');
INSERT INTO "al_umari" VALUES(80,36,'80:36','एवं अपनी पत्नी तथा अपने पुत्रों से।');
INSERT INTO "al_umari" VALUES(80,37,'80:37','प्रत्येक व्यक्ति को उस दिन अपनी पड़ी होगी।');
INSERT INTO "al_umari" VALUES(80,38,'80:38','उस दिन बहुत से चेहरे उज्ज्वल होंगे।');
INSERT INTO "al_umari" VALUES(80,39,'80:39','हंसते एवं प्रसन्न होंगे।');
INSERT INTO "al_umari" VALUES(80,40,'80:40','तथा बहुत-से चेहरों पर धूल पड़ी होगी।');
INSERT INTO "al_umari" VALUES(80,41,'80:41','उनपर कालिमा छाई होगी।');
INSERT INTO "al_umari" VALUES(80,42,'80:42','वही काफ़िर और कुकर्मी लोग हैं।[ ');
INSERT INTO "al_umari" VALUES(81,1,'81:1','जब सूर्य लपेट दिया जायेगा।');
INSERT INTO "al_umari" VALUES(81,2,'81:2','और जब तारे धुमिल हो जायेंगे।');
INSERT INTO "al_umari" VALUES(81,3,'81:3','जब पर्वत चलाये जायेंगे।');
INSERT INTO "al_umari" VALUES(81,4,'81:4','और जब दस महीने की गाभिन ऊँटनियाँ छोड़ दी जायेंगी।');
INSERT INTO "al_umari" VALUES(81,5,'81:5','और जब वन् पशु एकत्र कर दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(81,6,'81:6','और जब सागर भड़काये जायेंगे।[ ');
INSERT INTO "al_umari" VALUES(81,7,'81:7','और जब प्राण जोड़ दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(81,8,'81:8','और जब जीवित गाड़ी गयी कन्या से प्रश्न किया जायेगाः');
INSERT INTO "al_umari" VALUES(81,9,'81:9','कि वह किस अपराध के कारण वध की गयी।');
INSERT INTO "al_umari" VALUES(81,10,'81:10','तथा जब कर्मपत्र फैला दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(81,11,'81:11','और जब आकाश की खाल उतार दी जायेगी।');
INSERT INTO "al_umari" VALUES(81,12,'81:12','और जब नरक दहकाई जायेगी।');
INSERT INTO "al_umari" VALUES(81,13,'81:13','और जब स्वर्ग समीप लाई जायेगी।');
INSERT INTO "al_umari" VALUES(81,14,'81:14','तो प्रत्येक प्राणी जान लेगा कि वह क्या लेकर आया है।[ ');
INSERT INTO "al_umari" VALUES(81,15,'81:15','मैं शपथ लेता हूँ उन तारों की, जो पीछे हट जाते हैं।');
INSERT INTO "al_umari" VALUES(81,16,'81:16','जो चलते-चलते छुप जाते हैं।');
INSERT INTO "al_umari" VALUES(81,17,'81:17','और रात की (शपथ), जब समाप्त होने लगती है।');
INSERT INTO "al_umari" VALUES(81,18,'81:18','तथा भोर की, जब उजाला होने लगता है।');
INSERT INTO "al_umari" VALUES(81,19,'81:19','ये (क़ुर्आन) एक मान्यवर स्वर्ग दूत का लाया हुआ कथन है।');
INSERT INTO "al_umari" VALUES(81,20,'81:20','जो शक्तिशाली है। अर्श (सिंहासन) के मालिक के पास उच्च पद वाला है।');
INSERT INTO "al_umari" VALUES(81,21,'81:21','जिसकी बात मानी जाती है और बड़ा अमानतदार है।[ ');
INSERT INTO "al_umari" VALUES(81,22,'81:22','और तुम्हारा साथी उन्मत नहीं है।');
INSERT INTO "al_umari" VALUES(81,23,'81:23','उसने उसे आकाश में खुले रूप से देखा है।');
INSERT INTO "al_umari" VALUES(81,24,'81:24','वह परोक्ष (ग़ैब) की बात बताने में प्रलोभी नहीं है।[ ');
INSERT INTO "al_umari" VALUES(81,25,'81:25','ये धिक्कारी शैतान का कथन नहीं है।');
INSERT INTO "al_umari" VALUES(81,26,'81:26','फिर तुम कहाँ जा रहे हो?');
INSERT INTO "al_umari" VALUES(81,27,'81:27','ये संसार वासियों के लिए एक स्मृति (शास्त्र) है।');
INSERT INTO "al_umari" VALUES(81,28,'81:28','तुममें से उसके लिए, जो सुधरना चाहता हो।');
INSERT INTO "al_umari" VALUES(81,29,'81:29','तथा तुम विश्व के पालनहार के चाहे बिना कुछ नहीं कर सकते।[ ');
INSERT INTO "al_umari" VALUES(82,1,'82:1','जब आकाश फट जायेगा।');
INSERT INTO "al_umari" VALUES(82,2,'82:2','तथा जब तारे झड़ जायेंगे।');
INSERT INTO "al_umari" VALUES(82,3,'82:3','और जब सागर उबल पड़ेंगे।');
INSERT INTO "al_umari" VALUES(82,4,'82:4','और जब समाधियाँ (क़बरें) खोल दी जायेंगी।');
INSERT INTO "al_umari" VALUES(82,5,'82:5','तब प्रत्येक प्राणी को ज्ञान हो जायेगा, जो उसने किया है और नहीं किया है।[ ');
INSERT INTO "al_umari" VALUES(82,6,'82:6','हे इन्सान! तुझे किस वस्तु ने तेरे उदार पालनहार से बहका दिया?');
INSERT INTO "al_umari" VALUES(82,7,'82:7','जिसने तेरी रचना की, फिर तुझे संतुलित बनाया।');
INSERT INTO "al_umari" VALUES(82,8,'82:8','जिस रूप में चाहा बना दिया।[ ');
INSERT INTO "al_umari" VALUES(82,9,'82:9','वास्तव में तुम प्रतिफल (प्रलय) के दिन को नहीं मानते।');
INSERT INTO "al_umari" VALUES(82,10,'82:10','जबकि तुमपर निरीक्षक (पासबान) हैं।');
INSERT INTO "al_umari" VALUES(82,11,'82:11','जो माननीय लेखक हैं।');
INSERT INTO "al_umari" VALUES(82,12,'82:12','वे जो कुछ तुम करते हो, जानते हैं।[ ');
INSERT INTO "al_umari" VALUES(82,13,'82:13','निःसंदेह, सदाचारी सुखों में होंगे।');
INSERT INTO "al_umari" VALUES(82,14,'82:14','और दुराचारी नरक में।');
INSERT INTO "al_umari" VALUES(82,15,'82:15','प्रतिकार (बदले) के दिन उसमें झोंक दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(82,16,'82:16','और वे उससे बच रहने वाले नहीं।[ ');
INSERT INTO "al_umari" VALUES(82,17,'82:17','और तुम क्या जानो कि बदले का दिन क्या है?');
INSERT INTO "al_umari" VALUES(82,18,'82:18','फिर तुम क्या जानो कि बदले का दिन क्या है?');
INSERT INTO "al_umari" VALUES(82,19,'82:19','जिस दिन किसी का किसी के लिए कोई अधिकार नहीं होगा और उस दिन सब अधिकार अल्लाह का होगा।[ ');
INSERT INTO "al_umari" VALUES(83,1,'83:1','विनाश है डंडी मारने वालों का।');
INSERT INTO "al_umari" VALUES(83,2,'83:2','जो लोगों से नाप कर लें,, तो पूरा लेते हैं।');
INSERT INTO "al_umari" VALUES(83,3,'83:3','और जब उन्हें नाप या तोल कर देते हैं, तो कम देते हैं।');
INSERT INTO "al_umari" VALUES(83,4,'83:4','क्या वे नहीं सोचते कि फिर जीवित किये जायेंगे?');
INSERT INTO "al_umari" VALUES(83,5,'83:5','एक भीषण दिन के लिए।');
INSERT INTO "al_umari" VALUES(83,6,'83:6','जिस दिन सभी, विश्व के पालनहार के सामने खड़े होंगे।[ ');
INSERT INTO "al_umari" VALUES(83,7,'83:7','कदापि ऐसा न करो, निश्चय बुरों का कर्म पत्र "सिज्जीन" में है।');
INSERT INTO "al_umari" VALUES(83,8,'83:8','और तुम क्या जानो कि "सिज्जीन" क्या है?');
INSERT INTO "al_umari" VALUES(83,9,'83:9','वह लिखित महान पुस्तक है।');
INSERT INTO "al_umari" VALUES(83,10,'83:10','उस दिन झुठलाने वालों के लिए विनाश है।');
INSERT INTO "al_umari" VALUES(83,11,'83:11','जो प्रतिकार (बदले) के दिन को झुठलाते हैं।');
INSERT INTO "al_umari" VALUES(83,12,'83:12','तथा उसे वही झुठलाता है, जो महा अत्याचारी और पापी है।');
INSERT INTO "al_umari" VALUES(83,13,'83:13','जब उनके सामने हमारी आयतों का अध्ययन किया जाता है, तो कहते हैं: पूर्वजों की कल्पित कथायें हैं।');
INSERT INTO "al_umari" VALUES(83,14,'83:14','सुनो! उनके दिलों पर कुकर्मों के कारण लोहमल लग गया है।');
INSERT INTO "al_umari" VALUES(83,15,'83:15','निश्चय वे उस दिन अपने पालनहार (के दर्शन) से रोक दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(83,16,'83:16','फिर वे नरक में जायेंगे।');
INSERT INTO "al_umari" VALUES(83,17,'83:17','फिर कहा जायेगा कि यही है, जिसे तुम मिथ्या मानते थे।[ ');
INSERT INTO "al_umari" VALUES(83,18,'83:18','सच ये है कि सदाचारियों के कर्म पत्र "इल्लिय्यीन" में हैं।');
INSERT INTO "al_umari" VALUES(83,19,'83:19','और तुम क्या जानो कि "इल्लिय्यीन" क्या है?');
INSERT INTO "al_umari" VALUES(83,20,'83:20','एक अंकित पुस्तक है।');
INSERT INTO "al_umari" VALUES(83,21,'83:21','जिसके पास समीपवर्ती (फरिश्ते) उपस्थित रहते हैं।');
INSERT INTO "al_umari" VALUES(83,22,'83:22','निशचय, सदाचारी आनन्द में होंगे।');
INSERT INTO "al_umari" VALUES(83,23,'83:23','सिंहासनों के ऊपर बैठकर सब कुछ देख रहे होंगे।');
INSERT INTO "al_umari" VALUES(83,24,'83:24','तुम उनके मुखों से आनंद के चिन्ह अनुभव करोगे।');
INSERT INTO "al_umari" VALUES(83,25,'83:25','उन्हें मुहर लगी शुध्द मदिरा पिलाई जायेगी।');
INSERT INTO "al_umari" VALUES(83,26,'83:26','ये मुहर कस्तूरी की होगी। तो इसकी अभिलाषा करने वालों को इसकी अभिलाषा करनी चाहिये।');
INSERT INTO "al_umari" VALUES(83,27,'83:27','उसमें तसनीम मिली होगी।');
INSERT INTO "al_umari" VALUES(83,28,'83:28','वह एक स्रोत है, जिससे अल्लाह के समीपवर्ती पियेंगे।[ ');
INSERT INTO "al_umari" VALUES(83,29,'83:29','पापी (संसार में) ईमान लाने वालों पर हंसते थे।');
INSERT INTO "al_umari" VALUES(83,30,'83:30','और जब उनके पास से गुज़रते, तो आँखें मिचकाते थे।');
INSERT INTO "al_umari" VALUES(83,31,'83:31','और जब अपने परिवार में वापस जाते, तो आनंद लेते हुए वापस होते थे।');
INSERT INTO "al_umari" VALUES(83,32,'83:32','और जब उन्हें (मोमिनों को) देखते, तो कहते थेः यही भटके हुए लोग हैं।');
INSERT INTO "al_umari" VALUES(83,33,'83:33','जबकि वे उनके निरीक्षक बनाकर नहीं भेजे गये थे।');
INSERT INTO "al_umari" VALUES(83,34,'83:34','तो जो ईमान लाये, आज काफ़िरों पर हंस रहे हैं।');
INSERT INTO "al_umari" VALUES(83,35,'83:35','सिंहासनों के ऊपर से उन्हें देख रहे हैं।');
INSERT INTO "al_umari" VALUES(83,36,'83:36','क्या काफ़िरों (विश्वास हीनों) को उनका बदला दे दिया गया?[ ');
INSERT INTO "al_umari" VALUES(84,1,'84:1','जब आकाश फट जायेगा।');
INSERT INTO "al_umari" VALUES(84,2,'84:2','और अपने पालनहार की सुनेगा और यही उसे करना भी चाहिये।');
INSERT INTO "al_umari" VALUES(84,3,'84:3','तथा जब धरती फैला दी जायेगी।');
INSERT INTO "al_umari" VALUES(84,4,'84:4','और जो उसके भीतर है, फैंक देगी तथा ख़ाली हो जायेगी।');
INSERT INTO "al_umari" VALUES(84,5,'84:5','और अपने पालनहार की सुनेगी और यही उसे करना भी चाहिये।[ ');
INSERT INTO "al_umari" VALUES(84,6,'84:6','हे इन्सान! वस्तुतः, तू अपने पालनहार से मिलने के लिए परिश्रम कर रहा है और तू उससे अवश्य मिलेगा।');
INSERT INTO "al_umari" VALUES(84,7,'84:7','फिर जिस किसी को उसका कर्मपत्र दाहिने हाथ में दिया जायेगा।');
INSERT INTO "al_umari" VALUES(84,8,'84:8','तो उसका सरल ह़िसाब लिया जायेगा।');
INSERT INTO "al_umari" VALUES(84,9,'84:9','तथा वह अपनों में प्रसन्न होकर वापस जायेगा।');
INSERT INTO "al_umari" VALUES(84,10,'84:10','और जिन्हें उनका कर्मपत्र बायें हाथ में दिया जायेगा।');
INSERT INTO "al_umari" VALUES(84,11,'84:11','तो वह विनाश (मृत्यु) को पुकारेगा।');
INSERT INTO "al_umari" VALUES(84,12,'84:12','तथा नरक में जायेगा।');
INSERT INTO "al_umari" VALUES(84,13,'84:13','वह अपनों में प्रसन्न रहता था।');
INSERT INTO "al_umari" VALUES(84,14,'84:14','उसने सोचा था कि कभी पलट कर नहीं आयेगा।');
INSERT INTO "al_umari" VALUES(84,15,'84:15','क्यों नहीं? निश्चय उसका पालनहार उसे देख रहा था।[ ');
INSERT INTO "al_umari" VALUES(84,16,'84:16','मैं सन्ध्या लालिमा की शपथ लेता हूँ!');
INSERT INTO "al_umari" VALUES(84,17,'84:17','तथा रात की और जिसे वह एकत्र करे!');
INSERT INTO "al_umari" VALUES(84,18,'84:18','तथा चाँद की, जब वह पूरा हो जाये।');
INSERT INTO "al_umari" VALUES(84,19,'84:19','फिर तुम अवश्य एक दशा से दूसरी दशा में सवार होगे।');
INSERT INTO "al_umari" VALUES(84,20,'84:20','फिर क्यों वे विश्वास नहीं करते?');
INSERT INTO "al_umari" VALUES(84,21,'84:21','और जब उनके पास क़ुर्आन पढ़ा जाता है, तो सज्दा नहीं करते।[ ');
INSERT INTO "al_umari" VALUES(84,22,'84:22','बल्कि काफ़िर तो उसे झुठलाते हैं।');
INSERT INTO "al_umari" VALUES(84,23,'84:23','और अल्लाह उनके विचारों को भली-भाँति जानता है।');
INSERT INTO "al_umari" VALUES(84,24,'84:24','अतः, उन्हें दुःखदायी यातना की शुभ सूचना दे दो।');
INSERT INTO "al_umari" VALUES(84,25,'84:25','परन्तु, जो ईमान लाये तथा सदाचार किये, उनके लिए समाप्त न होने वाला बदला है।[ ');
INSERT INTO "al_umari" VALUES(85,1,'85:1','शपथ है बुर्जों वाले आकाश की!');
INSERT INTO "al_umari" VALUES(85,2,'85:2','शपश है उस दिन की, जिसका वचन दिया गया!');
INSERT INTO "al_umari" VALUES(85,3,'85:3','शपथ है साक्षी की और जिसपर साक्ष्य देगा!');
INSERT INTO "al_umari" VALUES(85,4,'85:4','खाईयों वालों का नाश हो गया![ ');
INSERT INTO "al_umari" VALUES(85,5,'85:5','जिनमें भड़कते हुए ईंधन की अग्नि थी।');
INSERT INTO "al_umari" VALUES(85,6,'85:6','जबकि वे उनपर बैठे हुए थे।');
INSERT INTO "al_umari" VALUES(85,7,'85:7','और वे ईमान वालों के साथ जो कर रहे थे, उसे देख रहे थे।');
INSERT INTO "al_umari" VALUES(85,8,'85:8','और उनका दोष केवल यही था कि वे प्रभावी प्रशंसा किये अल्लाह के प्रति विश्वास किये हुए थे।');
INSERT INTO "al_umari" VALUES(85,9,'85:9','जो आकाशों तथा धरती के राज्य का स्वामी है और अल्लाह सब कुछ देख रहा है।');
INSERT INTO "al_umari" VALUES(85,10,'85:10','जिन्होंने ईमान लाने वाले नर नारियों को परीक्षा में डाला, फिर क्षमा याचना न की, उनके लिए नरक का दण्ड तथा भड़कती आग की यातना है।');
INSERT INTO "al_umari" VALUES(85,11,'85:11','वास्तव में, जो ईमान लाये और सदाचार बने, उनके लिए ऐसे स्वर्ग हैं, जिनके तले नहरें बह रही हैं और यही बड़ी सफलता है।[ ');
INSERT INTO "al_umari" VALUES(85,12,'85:12','निश्चय तेरे पालनहार की पकड़ बहुत कड़ी है।');
INSERT INTO "al_umari" VALUES(85,13,'85:13','वही पहले पैदा करता है और फिर दूसरी बार पैदा करेगा।');
INSERT INTO "al_umari" VALUES(85,14,'85:14','और वह अति क्षमा तथा प्रेम करने वाला है।');
INSERT INTO "al_umari" VALUES(85,15,'85:15','वह सिंहासन का महान स्वामी है।');
INSERT INTO "al_umari" VALUES(85,16,'85:16','वह जो चाहे करता है।[ ');
INSERT INTO "al_umari" VALUES(85,17,'85:17','हे नबी! क्या तुम्हें सेनाओं की सूचना मिली?');
INSERT INTO "al_umari" VALUES(85,18,'85:18','फ़िरऔन तथा समूद को।[ ');
INSERT INTO "al_umari" VALUES(85,19,'85:19','बल्कि काफ़िर (विश्वासहीन) झुठलाने में लगे हुए हैं।');
INSERT INTO "al_umari" VALUES(85,20,'85:20','और अल्लाह उन्हें हर ओर से घेरे हुए है।[ ');
INSERT INTO "al_umari" VALUES(85,21,'85:21','बल्कि, वह गौरव वाला क़ुर्आन है।');
INSERT INTO "al_umari" VALUES(85,22,'85:22','जो लेखपत्र (लौह़े मह़फ़ूज़) में सुरक्षित है।[ ');
INSERT INTO "al_umari" VALUES(86,1,'86:1','शपथ है आकाश तथा रात में "प्रकाश प्रदान करने वाले" की!');
INSERT INTO "al_umari" VALUES(86,2,'86:2','और तुम क्या जानो कि वह "रात में प्रकाश प्रदान करने वाला" क्या है?');
INSERT INTO "al_umari" VALUES(86,3,'86:3','वह ज्योतिमय सितारा है।');
INSERT INTO "al_umari" VALUES(86,4,'86:4','प्रत्येक प्राणी पर एक रक्षक है।[ ');
INSERT INTO "al_umari" VALUES(86,5,'86:5','इन्सान, ये तो विचार करे कि वह किस चीज़ से पैदा किया गया है?');
INSERT INTO "al_umari" VALUES(86,6,'86:6','उछलते पानी (वीर्य) से पैदा किया गया है।');
INSERT INTO "al_umari" VALUES(86,7,'86:7','जो पीठ तथा सीने के पंजरों के मध्स से निकलता है।');
INSERT INTO "al_umari" VALUES(86,8,'86:8','निश्चय वह, उसे लौटाने की शक्ति रखता है।[ ');
INSERT INTO "al_umari" VALUES(86,9,'86:9','जिस दिन मन के भेद परखे जायेंगे।');
INSERT INTO "al_umari" VALUES(86,10,'86:10','तो उसे न कोई बल होगा और न उसका कोई सहायक।[ ');
INSERT INTO "al_umari" VALUES(86,11,'86:11','शपथ है आकाश की, जो बरसता है!');
INSERT INTO "al_umari" VALUES(86,12,'86:12','तथा फटने वाली धरती की।');
INSERT INTO "al_umari" VALUES(86,13,'86:13','वास्तव में, ये (क़ुर्आन) दो-टूक निर्णय (फ़ैसला) करने वाला है।');
INSERT INTO "al_umari" VALUES(86,14,'86:14','हँसी की बात नहीं।[ ');
INSERT INTO "al_umari" VALUES(86,15,'86:15','वह चाल बाज़ी करते हैं।');
INSERT INTO "al_umari" VALUES(86,16,'86:16','मैं भी चाल बाज़ी कर रहा हूँ।');
INSERT INTO "al_umari" VALUES(86,17,'86:17','अतः, काफ़िरों को कुछ थोड़ा अवसर दे दो।[ ');
INSERT INTO "al_umari" VALUES(87,1,'87:1','अपने सर्वोच्च प्रभु के नाम की पवित्रता का स्मरण करो।');
INSERT INTO "al_umari" VALUES(87,2,'87:2','जिसने पैदा किया और ठीक-ठीक बनाया।');
INSERT INTO "al_umari" VALUES(87,3,'87:3','और जिसने अनुमान लगाकर निर्धारित किया, फिर सीधी राह दिखायी।');
INSERT INTO "al_umari" VALUES(87,4,'87:4','और जिसने चारा उपजाया।[ ');
INSERT INTO "al_umari" VALUES(87,5,'87:5','फिर उसे (सुखा कर) कूड़ा बना दिया।[ ');
INSERT INTO "al_umari" VALUES(87,6,'87:6','(हे नबी!) हम तुम्हें ऐसा पढ़ायेंगे कि भूलोगे नहीं।');
INSERT INTO "al_umari" VALUES(87,7,'87:7','परन्तु, जिसे अल्लाह चाहे। निश्चय ही वह सभी खुली तथा छिपी बातों को जानता है।');
INSERT INTO "al_umari" VALUES(87,8,'87:8','और हम तुम्हें सरल मार्ग का साहस देंगे।[ ');
INSERT INTO "al_umari" VALUES(87,9,'87:9','तो आप धर्म की शिक्षा देते रहें। अगर शिक्षा लाभदायक हो।');
INSERT INTO "al_umari" VALUES(87,10,'87:10','डरने वाला ही शिक्षा ग्रहण करेगा।');
INSERT INTO "al_umari" VALUES(87,11,'87:11','और दुर्भाग्य उससे दूर रहेगा।');
INSERT INTO "al_umari" VALUES(87,12,'87:12','जो भीषण अग्नि में जायेगा।');
INSERT INTO "al_umari" VALUES(87,13,'87:13','फिर उसमें न मरेगा, न जीवित रहेगा।[ ');
INSERT INTO "al_umari" VALUES(87,14,'87:14','वह सफल हो गया, जिसने अपना शुध्दिकरण किया।');
INSERT INTO "al_umari" VALUES(87,15,'87:15','तथा अपने पालनहार के नाम का स्मरण किया और नमाज़ पढ़ी।[ ');
INSERT INTO "al_umari" VALUES(87,16,'87:16','बल्कि तुम लोग तो सांसारिक जीवन को प्राथमिकता देते हो।');
INSERT INTO "al_umari" VALUES(87,17,'87:17','जबकि आख़िरत का जीवन ही उत्त्म और स्थायी है।');
INSERT INTO "al_umari" VALUES(87,18,'87:18','यही बात, प्रथम ग्रन्थों में है।');
INSERT INTO "al_umari" VALUES(87,19,'87:19','(अर्थात) इब्राहीम तथा मूसा के ग्रन्थों में।[ ');
INSERT INTO "al_umari" VALUES(88,1,'88:1','क्या तेरे पास पूरी सृष्टी पर छा जाने वाली (क्यामत) का समाचार आया?');
INSERT INTO "al_umari" VALUES(88,2,'88:2','उस दिन कितने मूँह सहमे होंगे।');
INSERT INTO "al_umari" VALUES(88,3,'88:3','परिश्रम करते थके जा रहे होंगे।');
INSERT INTO "al_umari" VALUES(88,4,'88:4','पर वे दहकती आग में जायेंगे।');
INSERT INTO "al_umari" VALUES(88,5,'88:5','उन्हें खोलते सोते का जल पिलाया जायेगा।');
INSERT INTO "al_umari" VALUES(88,6,'88:6','उनके लिए कटीली झाड़ के सिवा, कोई भोजन सामग्री नहीं होगी।');
INSERT INTO "al_umari" VALUES(88,7,'88:7','जो न मोटा करेगी और न भूख दूर करेगी।[ ');
INSERT INTO "al_umari" VALUES(88,8,'88:8','कितने मुख उस दिन निर्मल होंगे।');
INSERT INTO "al_umari" VALUES(88,9,'88:9','अपने प्रयास से प्रसन्न होंगे।');
INSERT INTO "al_umari" VALUES(88,10,'88:10','ऊँचे स्वर्ग में होंगे।');
INSERT INTO "al_umari" VALUES(88,11,'88:11','उसमें कोई बकवास नहीं सुनेंगे।');
INSERT INTO "al_umari" VALUES(88,12,'88:12','उसमें बहता जल स्रोत होगा।');
INSERT INTO "al_umari" VALUES(88,13,'88:13','और उसमें ऊँचे-ऊँचे सिंहासन होंगे।');
INSERT INTO "al_umari" VALUES(88,14,'88:14','उसमें बहुत सारे प्याले रखे होंगे।');
INSERT INTO "al_umari" VALUES(88,15,'88:15','पंक्तियों में गलीचे लगे होंगे।');
INSERT INTO "al_umari" VALUES(88,16,'88:16','और मख़्मली क़ालीनें बिछी होंगी।[ ');
INSERT INTO "al_umari" VALUES(88,17,'88:17','क्या वह ऊँटों को नहीं देखते कि कैसे पैदा किये गये हैं?');
INSERT INTO "al_umari" VALUES(88,18,'88:18','और आकाश को कि किस प्रकार ऊँचा किया गया?');
INSERT INTO "al_umari" VALUES(88,19,'88:19','और पर्वतों को कि कैसे गाड़े गये?');
INSERT INTO "al_umari" VALUES(88,20,'88:20','तथा धरती को कि कैसे पसारी गयी?[ ');
INSERT INTO "al_umari" VALUES(88,21,'88:21','अतः आप शिक्षा (नसीह़त) दें कि आप शिक्षा देने वाले हैं।');
INSERT INTO "al_umari" VALUES(88,22,'88:22','आप उनपर अधिकारी नहीं हैं।');
INSERT INTO "al_umari" VALUES(88,23,'88:23','परन्तु, जो मुँह फेरेगा और नहीं मानेगा,');
INSERT INTO "al_umari" VALUES(88,24,'88:24','तो अल्लाह उसे भारी यातना देगा।');
INSERT INTO "al_umari" VALUES(88,25,'88:25','उन्हें हमारी ओर ही वापस आना है।');
INSERT INTO "al_umari" VALUES(88,26,'88:26','फिर हमें ही उनका ह़िसाब लेना है।[ ');
INSERT INTO "al_umari" VALUES(89,1,'89:1','शपथ है भोर की!');
INSERT INTO "al_umari" VALUES(89,2,'89:2','तथा दस रात्रियों की!');
INSERT INTO "al_umari" VALUES(89,3,'89:3','और जोड़े तथा अकेले की!');
INSERT INTO "al_umari" VALUES(89,4,'89:4','और रात्रि की जब जाने लगे!');
INSERT INTO "al_umari" VALUES(89,5,'89:5','क्या उसमें किसी मतिमान (समझदार) के लिए कोई शपथ है?[ ');
INSERT INTO "al_umari" VALUES(89,6,'89:6','क्या तुमने नहीं देखा कि तुम्हारे पालनहार ने "आद" के सात क्या किया?');
INSERT INTO "al_umari" VALUES(89,7,'89:7','स्तम्भों वाले "इरम" के साथ?');
INSERT INTO "al_umari" VALUES(89,8,'89:8','जिनके समान देशों में लोग नहीं पैदा किये गये।');
INSERT INTO "al_umari" VALUES(89,9,'89:9','तथा "समूद" के साथ जिन्होंने घाटियों मे चट्टानों को काट रखा था।');
INSERT INTO "al_umari" VALUES(89,10,'89:10','और मेखों वाले फ़िरऔन के साथ।');
INSERT INTO "al_umari" VALUES(89,11,'89:11','जिन्होंने नगरों में उपद्रव कर रखा था।');
INSERT INTO "al_umari" VALUES(89,12,'89:12','और नगरों में बड़ा उपद्रव फैला रखा था।');
INSERT INTO "al_umari" VALUES(89,13,'89:13','फिर तेरे पालनहार ने उनपर दण्ड का कोड़ा बरसा दिया।');
INSERT INTO "al_umari" VALUES(89,14,'89:14','वास्तव में, तेरा पालनहार घात में है।[ ');
INSERT INTO "al_umari" VALUES(89,15,'89:15','परन्तु, जब इन्सान की उसका पालनहार परीक्षा लेता है और उसे सम्मान और धन देता है, तो कहता है कि मेरे पालनहार ने मेरा सम्मान किया।');
INSERT INTO "al_umari" VALUES(89,16,'89:16','परन्तु, जब उसकी परीक्षा लेने के लिए उसकी जीविका संकीर्ण (कम) कर देता है, तो कहता है कि मेरे पालनहार ने मेरा अपमान किया।');
INSERT INTO "al_umari" VALUES(89,17,'89:17','ऐसा नहीं, बल्कि तुम अनाथ का आदर नहीं करते।');
INSERT INTO "al_umari" VALUES(89,18,'89:18','तथा ग़रीब को खाना खिलाने के लिए एक-दूसरे को नहीं उभारते।');
INSERT INTO "al_umari" VALUES(89,19,'89:19','और मीरास (मृतक सम्पत्ति) के धन को समेट-समेट कर खा जाते हो।');
INSERT INTO "al_umari" VALUES(89,20,'89:20','और धन से बड़ा मोह रखते हो।[ ');
INSERT INTO "al_umari" VALUES(89,21,'89:21','सावधान! जब धरती खण्ड-खण्ड कर दी जायेगी।');
INSERT INTO "al_umari" VALUES(89,22,'89:22','और तेरा पालनहार स्वयं पदार्वण करेगा और फ़रिश्ते पंक्तियों में होंगे।');
INSERT INTO "al_umari" VALUES(89,23,'89:23','और उस दिन नरक लायी जायेगी, उस दिन इन्सान सावधान हो जायेगा, किन्तु सावधानी लाभ-दायक न होगी।');
INSERT INTO "al_umari" VALUES(89,24,'89:24','वह कामना करेगा के काश! अपने सदा कि जीवन के लिए कर्म किये होते।');
INSERT INTO "al_umari" VALUES(89,25,'89:25','उस दिन (अल्लाह) के दण्ड के समान कोई दण्ड नहीं होगा।');
INSERT INTO "al_umari" VALUES(89,26,'89:26','और न उसके जैसी जकड़ कोई जकड़ेगा।[ ');
INSERT INTO "al_umari" VALUES(89,27,'89:27','हे शान्त आत्मा!');
INSERT INTO "al_umari" VALUES(89,28,'89:28','अपने पालनहार की ओर चल, तू उससे प्रसन्न, और वह तुझ से प्रसन्न।');
INSERT INTO "al_umari" VALUES(89,29,'89:29','तू मेरे भक्तों में प्रवेश कर जा।');
INSERT INTO "al_umari" VALUES(89,30,'89:30','और मेरे स्वर्ग में प्रवेश कर जा।[ ');
INSERT INTO "al_umari" VALUES(90,1,'90:1','मैं इस नगर मक्का की शपथ लेता हूँ!');
INSERT INTO "al_umari" VALUES(90,2,'90:2','तथा तुम इस नगर में प्रवेश करने वाले हो।');
INSERT INTO "al_umari" VALUES(90,3,'90:3','तथा सौगन्ध है पिता एवं उसकी संतान की!');
INSERT INTO "al_umari" VALUES(90,4,'90:4','हमने इन्सान को कष्ट में घिरा हुआ पैदा किया है।');
INSERT INTO "al_umari" VALUES(90,5,'90:5','क्या वह समझता है कि उसपर किसी का वश नहीं चलेगा?[ ');
INSERT INTO "al_umari" VALUES(90,6,'90:6','वह कहता है कि मैंने बहुत धन ख़र्च कर दिया।');
INSERT INTO "al_umari" VALUES(90,7,'90:7','क्या वह समझता है कि उसे किसी ने देखा नहीं?[ ');
INSERT INTO "al_umari" VALUES(90,8,'90:8','क्या हमने उसे दो आँखें नहीं दीं?');
INSERT INTO "al_umari" VALUES(90,9,'90:9','और एक ज़बान तथा दो होंट नहीं दिये?');
INSERT INTO "al_umari" VALUES(90,10,'90:10','और उसे दोनों मार्ग दिखा दिये।');
INSERT INTO "al_umari" VALUES(90,11,'90:11','तो वह घाटी में घुसा ही नहीं।');
INSERT INTO "al_umari" VALUES(90,12,'90:12','और तुम क्या जानो कि घाटी क्या है?');
INSERT INTO "al_umari" VALUES(90,13,'90:13','किसी दास को मुक्त करना।');
INSERT INTO "al_umari" VALUES(90,14,'90:14','अथवा भूक के दिन (अकाल) में खाना खिलाना।');
INSERT INTO "al_umari" VALUES(90,15,'90:15','किसी अनाथ संबंधी को।');
INSERT INTO "al_umari" VALUES(90,16,'90:16','अथवा मिट्टी में पड़े निर्धन को।[ ');
INSERT INTO "al_umari" VALUES(90,17,'90:17','फिर वह उन लोगों में होता है जो ईमान लाये और जिन्होंने धैर्य (सहनशीलता) एवं उपकार के उपदेश दिये।');
INSERT INTO "al_umari" VALUES(90,18,'90:18','यही लोग सौभाग्यशाली (दायें हाथ वाले) हैं।');
INSERT INTO "al_umari" VALUES(90,19,'90:19','और जिन लोगों ने हमारी आयतों को नहीं माना, यही लोग दुर्भाग्य (बायें हाथ वाले) हैं।');
INSERT INTO "al_umari" VALUES(90,20,'90:20','ऐसे लोग, हर ओर से आग में घिरे होंगे।');
INSERT INTO "al_umari" VALUES(91,1,'91:1','सूर्य तथा उसकी धूप की शपथ है!');
INSERT INTO "al_umari" VALUES(91,2,'91:2','और चाँद की शपथ, जब उसके पीछे निकले!');
INSERT INTO "al_umari" VALUES(91,3,'91:3','और दिन की शपथ, जब उसे (अर्थात सूर्य को) प्रकट कर दे!');
INSERT INTO "al_umari" VALUES(91,4,'91:4','और रात्रि की सौगन्ध, जब उसे (सूर्य को) छुपा ले!');
INSERT INTO "al_umari" VALUES(91,5,'91:5','और आकाश की सौगन्ध तथा उसकी जिसने उसे बनाया!');
INSERT INTO "al_umari" VALUES(91,6,'91:6','तथा धरती की सौगन्ध और जिसने उसे फैलाया![ ');
INSERT INTO "al_umari" VALUES(91,7,'91:7','और जीव की सौगन्ध, तथा उसकी जिसने उसे ठीक ठीक सुधारा।');
INSERT INTO "al_umari" VALUES(91,8,'91:8','फिर उसे दुराचार तथा सदाचार का विवेक दिया है।[ ');
INSERT INTO "al_umari" VALUES(91,9,'91:9','वह सफल हो गया, जिसने अपने जीव का शुध्दिकरण किया।');
INSERT INTO "al_umari" VALUES(91,10,'91:10','तथा वह क्षति में पड़ गया, जिसने उसे (पाप में) धंसा दिया।[ ');
INSERT INTO "al_umari" VALUES(91,11,'91:11','"समूद" जाति ने अपने दुराचार के कारण (ईशदूत) को झुठलाया।');
INSERT INTO "al_umari" VALUES(91,12,'91:12','जब उनमें से एक हत्भागा तैयार हुआ।');
INSERT INTO "al_umari" VALUES(91,13,'91:13','(ईशदूत सालेह ने) उनसे कहा कि अल्लाह की ऊँटनी और उसके पीने की बारी की रक्षा करो।');
INSERT INTO "al_umari" VALUES(91,14,'91:14','किन्तु, उन्होंने नहीं माना और उसे वध कर दिया, जिसके कारण उनके पालनहार ने यातना भेज दी और उन्हें चौरस कर दिया।');
INSERT INTO "al_umari" VALUES(91,15,'91:15','और वह उसके परिणाम से नहीं डरता।[ ');
INSERT INTO "al_umari" VALUES(92,1,'92:1','रात्रि की शपथ, जब छा जाये!');
INSERT INTO "al_umari" VALUES(92,2,'92:2','तथा दिन की शपथ, जब उजाला हो जाये!');
INSERT INTO "al_umari" VALUES(92,3,'92:3','और उसकी शपथ जिसने नर और मदा पैदा किये!');
INSERT INTO "al_umari" VALUES(92,4,'92:4','वास्तव में, तुम्हारे प्रयास अलग-अलग हैं।[ ');
INSERT INTO "al_umari" VALUES(92,5,'92:5','फिर जिसने दान दिया और भक्ति का मार्ग अपनाया,');
INSERT INTO "al_umari" VALUES(92,6,'92:6','और भली बात की पूष्टि करता रहा,');
INSERT INTO "al_umari" VALUES(92,7,'92:7','तो हम उसके लिए सरलता पैदा कर देंगे।');
INSERT INTO "al_umari" VALUES(92,8,'92:8','परन्तु, जिसने कंजूसी की और ध्यान नहीं दिया,');
INSERT INTO "al_umari" VALUES(92,9,'92:9','और भली बात को झुठला दिया।');
INSERT INTO "al_umari" VALUES(92,10,'92:10','तो हम उसके लिए कठिनाई को प्राप्त करना सरल कर देंगे।[ ');
INSERT INTO "al_umari" VALUES(92,11,'92:11','और जब वह गढ़े में गिरेगा, तो उसका धन उसके काम नहीं आयेगा।');
INSERT INTO "al_umari" VALUES(92,12,'92:12','हमारा कर्तव्य इतना ही है कि हम सीधा मार्ग दिखा दें।');
INSERT INTO "al_umari" VALUES(92,13,'92:13','जबकि आलोक-परलोक हमारे ही हाथ में है।');
INSERT INTO "al_umari" VALUES(92,14,'92:14','मैंने तुम्हें भड़कती आग से सावधान कर दिया है।[ ');
INSERT INTO "al_umari" VALUES(92,15,'92:15','जिसमें केवल बड़ा हत्भागा ही जायेगा।');
INSERT INTO "al_umari" VALUES(92,16,'92:16','जिसने झुठला दिया तथा (सत्य से) मुँह फेर लिया।');
INSERT INTO "al_umari" VALUES(92,17,'92:17','परन्तु, संयमी (सदाचारी) उससे बचा लिया जायेगा।');
INSERT INTO "al_umari" VALUES(92,18,'92:18','जो अपना धन, दान करता है, ताकि पवित्र हो जाये।');
INSERT INTO "al_umari" VALUES(92,19,'92:19','उसपर किसी का कोई उपकार नहीं, जिसे उतारा जा रहा है।');
INSERT INTO "al_umari" VALUES(92,20,'92:20','वह तो केवल अपने परम पालनहार की प्रसन्नता प्राप्त करने के लिए है।');
INSERT INTO "al_umari" VALUES(92,21,'92:21','निःसंदेह, वह प्रसन्न हो जायेगा।[ ');
INSERT INTO "al_umari" VALUES(93,1,'93:1','शपथ है दिन चढ़े की!');
INSERT INTO "al_umari" VALUES(93,2,'93:2','और शपथ है रात्रि की, जब उसका सन्नाटा छा जाये!');
INSERT INTO "al_umari" VALUES(93,3,'93:3','(हे नबी!) तेरे पालनहार ने तुझे न तो छोड़ा और ने ही विमुख हुआ।');
INSERT INTO "al_umari" VALUES(93,4,'93:4','और निश्चय ही आगामी युग तेरे लिए प्रथम युग से उत्तम है।');
INSERT INTO "al_umari" VALUES(93,5,'93:5','और तेरा पालनहार तुझे इतना देगा कि तू प्रसन्न हो जायेगा।');
INSERT INTO "al_umari" VALUES(93,6,'93:6','क्या उसने तुझे अनाथ पाकर शरण नहीं दी?');
INSERT INTO "al_umari" VALUES(93,7,'93:7','और तुझे पथ भूला हुआ पाया, तो सीधा मार्ग नहीं दिखाया?');
INSERT INTO "al_umari" VALUES(93,8,'93:8','और निर्धन पाया, तो धनी नहीं कर दिया?');
INSERT INTO "al_umari" VALUES(93,9,'93:9','तो तुम अनाथ पर क्रोध न करना।[ ');
INSERT INTO "al_umari" VALUES(93,10,'93:10','और माँगने वाले को न झिड़कना।');
INSERT INTO "al_umari" VALUES(93,11,'93:11','और अपने पालनहार के उपकार का वर्णन करना।[ ');
INSERT INTO "al_umari" VALUES(94,1,'94:1','(हे नबी!) क्या हमने तुम्हारे लिए तुम्हारा वक्ष (सीना) नहीं खोल दिया?');
INSERT INTO "al_umari" VALUES(94,2,'94:2','और तुम्हारा बोझ नहीं उतार दिया?');
INSERT INTO "al_umari" VALUES(94,3,'94:3','जिसने तुम्हारी पीठ तोड़ दी थी।');
INSERT INTO "al_umari" VALUES(94,4,'94:4','और तुम्हारी चर्चा को ऊँचा कर दिया।[ ');
INSERT INTO "al_umari" VALUES(94,5,'94:5','निश्चय कठिनाई के साथ आसानी भी है।');
INSERT INTO "al_umari" VALUES(94,6,'94:6','निश्चय कठिनाई के साथ आसानी भी है।[ ');
INSERT INTO "al_umari" VALUES(94,7,'94:7','अतः, जब अवसर मिले, जो आराधना में प्रयास करो।');
INSERT INTO "al_umari" VALUES(94,8,'94:8','और अपने पालनहार की ओर ध्यानमग्न हो जाओ।[ ');
INSERT INTO "al_umari" VALUES(95,1,'95:1','इंजीर तथा ज़ैतून की शपथ!');
INSERT INTO "al_umari" VALUES(95,2,'95:2','एवं "तूरे सीनीन" की शपथ!');
INSERT INTO "al_umari" VALUES(95,3,'95:3','और इस शान्ति के नगर की शपथ!');
INSERT INTO "al_umari" VALUES(95,4,'95:4','हमने इनसान को मनोहर रूप में पैदा किया है।');
INSERT INTO "al_umari" VALUES(95,5,'95:5','फिर उसे सबसे नीचे गिरा दिया।');
INSERT INTO "al_umari" VALUES(95,6,'95:6','परन्तु, जो ईमान लाये तथा सदाचार किये, उनके लिए ऐसा बदला है, जो कभी समाप्त नहीं होगा।');
INSERT INTO "al_umari" VALUES(95,7,'95:7','फिर (तुम मानव जाति) प्रतिफल (बदले) के दिन को क्यों झुठलाते हो?');
INSERT INTO "al_umari" VALUES(95,8,'95:8','क्या अल्लाह सब अधिकारियों से बढ़ कर अधिकारी नहीं है?');
INSERT INTO "al_umari" VALUES(96,1,'96:1','अपने पालनहार के नाम से पढ़, जिसने पैदा किया।');
INSERT INTO "al_umari" VALUES(96,2,'96:2','जिस ने मनुष्य को रक्त को लोथड़े से पैदा किया।');
INSERT INTO "al_umari" VALUES(96,3,'96:3','पढ़, और तेरा पालनहार बड़ा दया वाला है।');
INSERT INTO "al_umari" VALUES(96,4,'96:4','जिस ने लेखनी के द्वारा ज्ञान सिखाया।');
INSERT INTO "al_umari" VALUES(96,5,'96:5','इन्सान को उसका ज्ञान दिया जिस को वह नहीं जानता था।[ ');
INSERT INTO "al_umari" VALUES(96,6,'96:6','वास्तव में, इन्सान सरकशी करता है।');
INSERT INTO "al_umari" VALUES(96,7,'96:7','इसलिए कि वह स्वयं को निश्चिन्त (धनवान) समझता है।');
INSERT INTO "al_umari" VALUES(96,8,'96:8','निःसंदेह, फिर तेरे पालनहार की ओर पलट कर जाना है।[ ');
INSERT INTO "al_umari" VALUES(96,9,'96:9','क्या तुमने उसे देखा जो रोकता है।');
INSERT INTO "al_umari" VALUES(96,10,'96:10','एक भक्त को, जब वह नमाज़ अदा करे।');
INSERT INTO "al_umari" VALUES(96,11,'96:11','भला देखो तो, यदि वह सीधे मार्ग पर हो।');
INSERT INTO "al_umari" VALUES(96,12,'96:12','या अल्लाह से डरने का आदेश देता हो?');
INSERT INTO "al_umari" VALUES(96,13,'96:13','और देखो तो, यदि उसने झुठलाया तथा मुँह फेरा हो?[ ');
INSERT INTO "al_umari" VALUES(96,14,'96:14','क्या वह नहीं जानता कि अल्लाह उसे देख रहा है?');
INSERT INTO "al_umari" VALUES(96,15,'96:15','निश्चय यदि वह नहीं रुकता, तो हम उसे माथे के बल घसेटेंगे।');
INSERT INTO "al_umari" VALUES(96,16,'96:16','झूठे और पापी माथे के बल।');
INSERT INTO "al_umari" VALUES(96,17,'96:17','तो वह अपनी सभा को बुला ले।');
INSERT INTO "al_umari" VALUES(96,18,'96:18','हम भी नरक के फ़रिश्तों को बुलायेंगे।[ ');
INSERT INTO "al_umari" VALUES(96,19,'96:19','(हे भक्त) कदापि उसकी बात न सुनो तथा सज्दा करो और मेरे समीप हो जाओ।[ ');
INSERT INTO "al_umari" VALUES(97,1,'97:1','निःसंदेह, हमने उस (क़ुर्आन) को ''लैलतुल क़द्र'' (सम्मानित रात्रि) में उतारा।');
INSERT INTO "al_umari" VALUES(97,2,'97:2','और तुम क्या जानो कि वह ''लैलतुल क़द्र'' (सम्मानित रात्रि) क्या है?');
INSERT INTO "al_umari" VALUES(97,3,'97:3','लैलतुल क़द्र (सम्मानित रात्रि) हज़ार मास से उत्तम है।[ ');
INSERT INTO "al_umari" VALUES(97,4,'97:4','उसमें (हर काम को पूर्ण करने के लिए) फ़रिश्ते तथा रूह़ (जिब्रील) अपने पालनहार की आज्ञा से उतरते हैं।[ ');
INSERT INTO "al_umari" VALUES(97,5,'97:5','वह शान्ति की रात्रि है, जो भोर होने तक रहती है।[ ');
INSERT INTO "al_umari" VALUES(98,1,'98:1','अह्ले किताब के काफ़िर और मुश्रिक लोग ईमान लाने वाले नहीं थे जब तक कि उनके पास खुला प्रमाम न आ जाये।');
INSERT INTO "al_umari" VALUES(98,2,'98:2','अर्थात अल्लाह का एक रसूल, जो पवित्र ग्रन्थ पढ़कर सुनाये।');
INSERT INTO "al_umari" VALUES(98,3,'98:3','जिसमें उचित आदेश है।[ ');
INSERT INTO "al_umari" VALUES(98,4,'98:4','और जिन लोगों को ग्रन्थ दिये गये, उन्होंने इस खुले प्रमाण के आ जाने के पश्चात ही मतभेद किया।[ ');
INSERT INTO "al_umari" VALUES(98,5,'98:5','और उन्हें केवल यही आदेश दिया गया था कि वे धर्म को शुध्द रखें और सबको तज कर केवल अल्लाह की उपासना करें, नमाज़ अदा करें और ज़कात दें और यही शाश्त धर्म है।[ ');
INSERT INTO "al_umari" VALUES(98,6,'98:6','निःसंदेह, जो लोग अह्ले किताब में से काफ़िर हो गये तथा मुश्रिक (मिश्रणवादी), तो वे सदा नरक की आग में रहेंगे और वही दुष्टतम जन हैं।');
INSERT INTO "al_umari" VALUES(98,7,'98:7','जो लोग ईमान लाये तथा सदाचार करते रहे, तो वही सर्वश्रेष्ठ जन हैं।');
INSERT INTO "al_umari" VALUES(98,8,'98:8','उनका प्रतिफल उनके पालनहार की ओर से सदा रहने वाले बाग़ हैं। जिनके नीचे नहरें बहती होंगी। वे उनमें सदा निवास करेंगे। अल्लाह उनसे प्रसन्न हुआ और वे अल्लाह से प्रसन्न हुए। ये उसके लिए है, जो अपने पालनहार से डरा।[ ');
INSERT INTO "al_umari" VALUES(99,1,'99:1','जब धरती को पूरी तरह झंझोड़ दिया जायेगा।');
INSERT INTO "al_umari" VALUES(99,2,'99:2','तथा भूमि अपने बोझ बाहर निकाल देगी।');
INSERT INTO "al_umari" VALUES(99,3,'99:3','और इन्सान कहेगा कि इसे क्या हो गया?');
INSERT INTO "al_umari" VALUES(99,4,'99:4','उस दिन वह अपनी सभी सूचनायें वर्णन कर देगी।');
INSERT INTO "al_umari" VALUES(99,5,'99:5','क्योंकि तेरे पालनहार ने उसे यही आदेश दिया है।');
INSERT INTO "al_umari" VALUES(99,6,'99:6','उस दिन लोग तितर-बितर होकर आयेंगे, ताकि वे अपने कर्मों को देख लें।[ ');
INSERT INTO "al_umari" VALUES(99,7,'99:7','तो जिसने एक कण के बराबर भी पुण्य किया होगा, उसे देख लेगा।');
INSERT INTO "al_umari" VALUES(99,8,'99:8','और जिसने एक कण के बराबर भी बुरा किया होगा, उसे देख लेगा।[ ');
INSERT INTO "al_umari" VALUES(100,1,'100:1','उन घोड़ों की शपथ, जो दौड़कर हाँफ जाते हैं!');
INSERT INTO "al_umari" VALUES(100,2,'100:2','फिर पत्थरों पर टाप मारकर चिंगारियाँ निकालने वालों की शपथ!');
INSERT INTO "al_umari" VALUES(100,3,'100:3','फिर प्रातः काल में धावा बोलने वालों की शपथ!');
INSERT INTO "al_umari" VALUES(100,4,'100:4','जो धूल उड़ाते हैं।');
INSERT INTO "al_umari" VALUES(100,5,'100:5','फिर सेना के बीच घुस जाते हैं।');
INSERT INTO "al_umari" VALUES(100,6,'100:6','वास्तव में, इन्सान अपने पालनहार का बड़ा कृतघ्न (नाशुकरा) है।');
INSERT INTO "al_umari" VALUES(100,7,'100:7','निश्चित रूप से, वह इसपर स्वयं साक्षी (गवाह) है।[ ');
INSERT INTO "al_umari" VALUES(100,8,'100:8','वह धन का बड़ा प्रेमी है।[ ');
INSERT INTO "al_umari" VALUES(100,9,'100:9','क्या वह उस समय को नहीं जानता, जब क़ब्रों में जो कुछ है, निकाल लिया जायेगा?');
INSERT INTO "al_umari" VALUES(100,10,'100:10','और सीनों के भेद प्रकाश में लाये जायेंगे?[ ');
INSERT INTO "al_umari" VALUES(100,11,'100:11','निश्चय उनका पालनहार उस दिन उनसे पूर्ण रूप से सूचित होगा।[ ');
INSERT INTO "al_umari" VALUES(101,1,'101:1','वह खड़खड़ा देने वाली।');
INSERT INTO "al_umari" VALUES(101,2,'101:2','क्या है वह खड़ख़ा देने वाली?');
INSERT INTO "al_umari" VALUES(101,3,'101:3','और तुम क्या जानो कि वह खड़खड़ा देने वाली क्या है?[ ');
INSERT INTO "al_umari" VALUES(101,4,'101:4','जिस दिन लोग, बिखरे पतिंगों के समान (व्याकूल) होंगे।');
INSERT INTO "al_umari" VALUES(101,5,'101:5','और पर्वत, धुनी हुई ऊन के समान उडेंगे।[ ');
INSERT INTO "al_umari" VALUES(101,6,'101:6','तो जिसके पलड़े भारी हुए,');
INSERT INTO "al_umari" VALUES(101,7,'101:7','तो वह मनचाहे सुख में होगा।');
INSERT INTO "al_umari" VALUES(101,8,'101:8','तथा जिसके पलड़ हल्के हुए,');
INSERT INTO "al_umari" VALUES(101,9,'101:9','तो उसका स्थान ''हाविया'' है।');
INSERT INTO "al_umari" VALUES(101,10,'101:10','और तुम क्या जानो कि वह (हाविया) क्या है?');
INSERT INTO "al_umari" VALUES(101,11,'101:11','वह दहक्ती आग है।[ ');
INSERT INTO "al_umari" VALUES(102,1,'102:1','तुम्हें अधिक (धन) के लोभ ने मगन कर दिया।');
INSERT INTO "al_umari" VALUES(102,2,'102:2','यहाँ तक कि तुम क़ब्रिस्तान जा पहुँचे।[ ');
INSERT INTO "al_umari" VALUES(102,3,'102:3','निश्चय तुम्हें ज्ञान हो जायेगा।');
INSERT INTO "al_umari" VALUES(102,4,'102:4','फिर निश्चय ही तुम्हें ज्ञान हो जायेगा।');
INSERT INTO "al_umari" VALUES(102,5,'102:5','वास्तव में, यदि तुम्हें विश्वास होता (तो ऐसा न करते)।[ ');
INSERT INTO "al_umari" VALUES(102,6,'102:6','तुम नरक को अवश्य देखोगे।');
INSERT INTO "al_umari" VALUES(102,7,'102:7','फिर उसे विश्वास की आँख से देखोगे।');
INSERT INTO "al_umari" VALUES(102,8,'102:8','फिर उस दिन तुमसे सुख सम्पदा के विषय में अवश्य पूछ गछ होगी।[ ');
INSERT INTO "al_umari" VALUES(103,1,'103:1','निचड़ते दिन की शपथ!');
INSERT INTO "al_umari" VALUES(103,2,'103:2','निःसंदेह, इन्सान क्षति में है।[ ');
INSERT INTO "al_umari" VALUES(103,3,'103:3','अतिरिक्त उनके, जो ईमान लाये तथा सदाचार किये एवं एक-दूसरे को सत्य का उपदेश तथा धैर्य का उपदेश देते रहे।[ ');
INSERT INTO "al_umari" VALUES(104,1,'104:1','विनाश हो उस व्यक्ति का, जो कचोके लगाता रहता है और चौटे करता रहता है।');
INSERT INTO "al_umari" VALUES(104,2,'104:2','जिसने धन एकत्र किया और उसे गिन-गिन कर रखा।');
INSERT INTO "al_umari" VALUES(104,3,'104:3','क्या वह समझता है कि उसका धन उसे संसार में सदा रखेगा?[ ');
INSERT INTO "al_umari" VALUES(104,4,'104:4','कदापि ऐसा नहीं होगा। वह अवश्य ही ''ह़ुतमा'' में फेंका जायेगा।');
INSERT INTO "al_umari" VALUES(104,5,'104:5','और तुम क्या जानो कि ''ह़ुतमा'' क्या है?');
INSERT INTO "al_umari" VALUES(104,6,'104:6','वह अल्लाह की भड़काई हुई अग्नि है।');
INSERT INTO "al_umari" VALUES(104,7,'104:7','जो दिलों तक जा पहूँचेगी।');
INSERT INTO "al_umari" VALUES(104,8,'104:8','वह, उसमें बन्द कर दिये जायेंगे।');
INSERT INTO "al_umari" VALUES(104,9,'104:9','लँबे-लँबे स्तंभों में।[ ');
INSERT INTO "al_umari" VALUES(105,1,'105:1','क्या तुम नहीं जानते कि तेरे पालनहार ने हाथी वाले के साथ क्या किया?');
INSERT INTO "al_umari" VALUES(105,2,'105:2','क्या उसने उनकी चाल को विफल नहीं कर दिया?');
INSERT INTO "al_umari" VALUES(105,3,'105:3','और उनपर पंक्षियों के दल भेजे।');
INSERT INTO "al_umari" VALUES(105,4,'105:4','जो उनपर पकी कंकरी के पत्थर फेंक रहे थे।');
INSERT INTO "al_umari" VALUES(105,5,'105:5','तो उन्हें ऐसा कर दिया, जैसे खाने का भूसा।[ ');
INSERT INTO "al_umari" VALUES(106,1,'106:1','क़ुरैश के स्वभाव बनाने के कारण।');
INSERT INTO "al_umari" VALUES(106,2,'106:2','उनके जाड़े तथा गर्मी की यात्रा का स्वभाव बनाने के कारण।[ ');
INSERT INTO "al_umari" VALUES(106,3,'106:3','उन्हें चाहिये कि इस घर (काबा) के प्रभु की पूजा करें।[ ');
INSERT INTO "al_umari" VALUES(106,4,'106:4','जिसने उन्हें भूख में खिलाया तथा डर से निडर कर दिया।');
INSERT INTO "al_umari" VALUES(107,1,'107:1','( हे नबी!) क्या तुमने उसे देखा, जो प्रतिकार (बदले) के दिन को झुठलाता है?');
INSERT INTO "al_umari" VALUES(107,2,'107:2','यही वह है, जो अनाथ (यतीम) को धक्का देता है।');
INSERT INTO "al_umari" VALUES(107,3,'107:3','और ग़रीब को भोजन देने पर नहीं उभारता।[ ');
INSERT INTO "al_umari" VALUES(107,4,'107:4','विनाश है उन नमाज़ियों के लिए[ ');
INSERT INTO "al_umari" VALUES(107,5,'107:5','जो अपनी नमाज़ से अचेत हैं।');
INSERT INTO "al_umari" VALUES(107,6,'107:6','और जो दिखावे (आडंबर) के लिए करते हैं।');
INSERT INTO "al_umari" VALUES(107,7,'107:7','तथा माऊन (प्रयोग में आने वाली मामूली चीज़) भी माँगने से नहीं देते।[ ');
INSERT INTO "al_umari" VALUES(108,1,'108:1','(हे नबी!) हमने तुम्हें कौसर प्रदान किया है।[ ');
INSERT INTO "al_umari" VALUES(108,2,'108:2','तो तुम अपने पालनहार के लिए नमाज़ पढ़ो तथा बलि दो।[ ');
INSERT INTO "al_umari" VALUES(108,3,'108:3','निःसंदेह तुम्हारा शत्रु ही बे नाम निशान है।[ ');
INSERT INTO "al_umari" VALUES(109,1,'109:1','(हे नबी!) कह दोः हे काफ़िरो!');
INSERT INTO "al_umari" VALUES(109,2,'109:2','मैं उन (मूर्तियों) को नहीं पूजता, जिन्हें तुम पूजते हो।');
INSERT INTO "al_umari" VALUES(109,3,'109:3','और न तुम उसे पूजते हो, जिसे मैं पूजता हूँ।');
INSERT INTO "al_umari" VALUES(109,4,'109:4','और न मैं उसे पूजूँगा, जिसे तुम पूजते हो।');
INSERT INTO "al_umari" VALUES(109,5,'109:5','और न तुम उसे पूजोगे, जिसे मैं पूजता हूँ।');
INSERT INTO "al_umari" VALUES(109,6,'109:6','तुम्हारे लिए तम्हारा धर्म तथा मेरे लिए मेरा धर्म है।[ ');
INSERT INTO "al_umari" VALUES(110,1,'110:1','(हे नबी!) जब अल्लाह की सहायता एवं विजय आ जाये।');
INSERT INTO "al_umari" VALUES(110,2,'110:2','और तुम लोगों को अल्लाह के धर्म में दल के दल प्रवेश करते देख लो।[ ');
INSERT INTO "al_umari" VALUES(110,3,'110:3','तो अपने पालनहार की प्रशंसा के साथ उसकी पवित्रता का वर्णन करो और उससे क्षमा माँगो, निःसंदेह वह बड़ा क्षमी है।[ ');
INSERT INTO "al_umari" VALUES(111,1,'111:1','अबू लहब के दोनों हाथ नाश हो गये और वह स्वयं भी नाश हो गया![ ');
INSERT INTO "al_umari" VALUES(111,2,'111:2','उसका धन तथा जो उसने कमाया उसके काम नहीं आया।');
INSERT INTO "al_umari" VALUES(111,3,'111:3','वह शीघ्र लावा फेंकती आग में जायेगा।[ ');
INSERT INTO "al_umari" VALUES(111,4,'111:4','तथा उसकी पत्नी भी, जो ईंधन लिए फिरती है।');
INSERT INTO "al_umari" VALUES(111,5,'111:5','उसकी गर्दन में मूँज की रस्सी होगी।[ ');
INSERT INTO "al_umari" VALUES(112,1,'112:1','(हे ईश दूत!) कह दोः अल्लाह अकेला है।[ ');
INSERT INTO "al_umari" VALUES(112,2,'112:2','अल्लाह निःछिद्र है।');
INSERT INTO "al_umari" VALUES(112,3,'112:3','न उसकी कोई संतान है और न वह किसी की संतान है।');
INSERT INTO "al_umari" VALUES(112,4,'112:4','और न उसके बराबर कोई है।[ ');
INSERT INTO "al_umari" VALUES(113,1,'113:1','(हे नबी!) कहो कि मैं भोर के पालनहार की शरण लेता हूँ।');
INSERT INTO "al_umari" VALUES(113,2,'113:2','हर उसकी बुराई से, जिसे उसने पैदा किया।');
INSERT INTO "al_umari" VALUES(113,3,'113:3','तथा रात्रि की बुराई से, जब उसका अंधेरा छा जाये।[ ');
INSERT INTO "al_umari" VALUES(113,4,'113:4','तथा गाँठ लगाकर उनमें फूँकने वालियों की बुराई से।');
INSERT INTO "al_umari" VALUES(113,5,'113:5','तथा द्वेष करने वाले की बुराई से, जब वह द्वेष करे।[ ');
INSERT INTO "al_umari" VALUES(114,1,'114:1','(हे नबी!) कहो कि मैं इन्सानों के पालनहार की शरण में आता हूँ।');
INSERT INTO "al_umari" VALUES(114,2,'114:2','जो सारे इन्सानों का स्वामी है।');
INSERT INTO "al_umari" VALUES(114,3,'114:3','जो सारे इन्सानों का पूज्य है।[ ');
INSERT INTO "al_umari" VALUES(114,4,'114:4','भ्रम डालने वाले और छुप जाने वाले (राक्षस) की बुराई से।');
INSERT INTO "al_umari" VALUES(114,5,'114:5','जो लोगों के दिलों में भ्रम डालता रहता है।');
INSERT INTO "al_umari" VALUES(114,6,'114:6','जो जिन्नों में से है और मनुष्यों में से भी।[ ');
