INSERT INTO "altay" VALUES(82,1,'82:1','Көк жарылған сәтте,');
INSERT INTO "altay" VALUES(82,2,'82:2','Жұлдыздар төгілген сәтте,');
INSERT INTO "altay" VALUES(82,3,'82:3','Теңіздер тасыған сәтте,');
INSERT INTO "altay" VALUES(82,4,'82:4','Қабырлар қопарылған сәтте,');
INSERT INTO "altay" VALUES(82,5,'82:5','Әркім ілгері не жіберілгенін,кейін не қалдырылғанын біледі.');
INSERT INTO "altay" VALUES(82,6,'82:6','Әй адамзат! Ардақты Раббың туралы сені не алдады?');
INSERT INTO "altay" VALUES(82,7,'82:7','Ол сондай Алла,сені жаратты.Толықтап теңестірді.');
INSERT INTO "altay" VALUES(82,8,'82:8','Ол,сені қалаған бейнеде құрастырды.');
INSERT INTO "altay" VALUES(82,9,'82:9','Жоқ,олай емес,дінді өтіріксінесіңдер.');
INSERT INTO "altay" VALUES(82,10,'82:10','Негізінен сендерге әлбетте бақылаушылар бар.');
INSERT INTO "altay" VALUES(82,11,'82:11','Ардақты жазушылар,');
INSERT INTO "altay" VALUES(82,12,'82:12','Олар не істегендеріңді біледі.');
INSERT INTO "altay" VALUES(82,13,'82:13','Шын мәнінде ізгілер,әлбетте нығымет ішінде болады.');
INSERT INTO "altay" VALUES(82,14,'82:14','Негізінен бұзықтар тозақта болады.');
INSERT INTO "altay" VALUES(82,15,'82:15','Олар қиямет күні тозаққа салынады.');
INSERT INTO "altay" VALUES(82,16,'82:16','Олар,одан ғайып бола алмайды.');
INSERT INTO "altay" VALUES(82,17,'82:17','Қиямет күнінің не екенін қайдан білесің?');
INSERT INTO "altay" VALUES(82,18,'82:18','Я,қиямет күннің не екенін қайдан білесің.');
INSERT INTO "altay" VALUES(82,19,'82:19','Ол күні,Біреуге біреу қараяласуға еш нәрсе қолдан келмейді. Ол күні,әміп Аллаға тән. (С.88-А.)');
INSERT INTO "altay" VALUES(83,1,'83:1','Өлшеу,тартуда кеміткендерге нендей өкініш!');
INSERT INTO "altay" VALUES(83,2,'83:2','Олар қашан өлшеп алса,толық өлшеп алады да;');
INSERT INTO "altay" VALUES(83,3,'83:3','Қашан олар,өлшеп немесе тартып беретін болса; кемітеді.');
INSERT INTO "altay" VALUES(83,4,'83:4','Олар қайта тірілетіндіктерін ойламай ма?');
INSERT INTO "altay" VALUES(83,5,'83:5','Ол зор күн үшін ;');
INSERT INTO "altay" VALUES(83,6,'83:6','Ол күні адамдар,бүкіл әлменің Раббының алдында тұрады.');
INSERT INTO "altay" VALUES(83,7,'83:7','Олай емес. Негізінен бұзықтардың дәптері төменгі орында болады.');
INSERT INTO "altay" VALUES(83,8,'83:8','Төменгі орынның не екенін қайдан білесің?');
INSERT INTO "altay" VALUES(83,9,'83:9','Ол жазулы бір кітап.');
INSERT INTO "altay" VALUES(83,10,'83:10','Ол күні,жасынға шығарушыларға нендей өкініш!');
INSERT INTO "altay" VALUES(83,11,'83:11','Олар,сондай қиямет күнін өтіріксінгендер.');
INSERT INTO "altay" VALUES(83,12,'83:12','Оны,шектен шыққан,күнәкар өтіріксінеді.');
INSERT INTO "altay" VALUES(83,13,'83:13','Қашан оған аяттарымыз оқылса: ''''Бұрынғылардың ертегісі'''' деді.');
INSERT INTO "altay" VALUES(83,14,'83:14','Жоқ,бәкім олардың жүректерін істеген қылықтарының таты басты.');
INSERT INTO "altay" VALUES(83,15,'83:15','Жоқ,олай емес. Негізінде олар ол күні Раббыларының дидарыннан махрұм қалады.');
INSERT INTO "altay" VALUES(83,16,'83:16','Сосын олар,әлбетте тозаққа кіреді.');
INSERT INTO "altay" VALUES(83,17,'83:17','Сосын: ''''Міне сендердің өтірік дегендерің осы!'''', -деліндеді.');
INSERT INTO "altay" VALUES(83,18,'83:18','Жоқ, олай емес. Негізінен ізгілердің дәптері әрине жоғарғы орында болады.');
INSERT INTO "altay" VALUES(83,19,'83:19','Жоғарғы орынның не екенін қайдан білесің?');
INSERT INTO "altay" VALUES(83,20,'83:20','Ол жазулы бір кітап.');
INSERT INTO "altay" VALUES(83,21,'83:21','Оны,Аллаға жақын періштелер бақылайды.');
INSERT INTO "altay" VALUES(83,22,'83:22','Негізінен ізгілер,әрине нығымет ішінде болады.');
INSERT INTO "altay" VALUES(83,23,'83:23','Олар диувандардың үстінде (Алланың нығыметтеріне) қарайды.');
INSERT INTO "altay" VALUES(83,24,'83:24','Олардың бақытқа бөленгендіктерін шырайларынан таныйсың.');
INSERT INTO "altay" VALUES(83,25,'83:25','Олар,мөрленген ішімдіктен сусындатылады.');
INSERT INTO "altay" VALUES(83,26,'83:26','Оның сарқыны жұпар аңқиды.Жарысушылар,осыған жарыссын.');
INSERT INTO "altay" VALUES(83,27,'83:27','Оның қоры, Тәсним бастауынан.');
INSERT INTO "altay" VALUES(83,28,'83:28','Ол бастаудан Аллаға жақын болғандар ішеді.');
INSERT INTO "altay" VALUES(83,29,'83:29','Расында күнәкарлар,иман келтіргендерге күлетін еді.');
INSERT INTO "altay" VALUES(83,30,'83:30','Олар жандарынан өткенде,ымдасатын еді.');
INSERT INTO "altay" VALUES(83,31,'83:31','Олар үйлеріне қайтып барған кезде,күңкілдесетін еді.');
INSERT INTO "altay" VALUES(83,32,'83:32','Қашан олар Мұсылмандарды көрсе : «Сөз жоқ, адасқандар » дейтін еді.');
INSERT INTO "altay" VALUES(83,33,'83:33','Олар,Мұсылмандарға бақылаушы етіліп жіберілмеді.');
INSERT INTO "altay" VALUES(83,34,'83:34','Ал енді бүгін мүміндер.кәпірлерге күледі.');
INSERT INTO "altay" VALUES(83,35,'83:35','Олар тақтардың үстінде қарайды;');
INSERT INTO "altay" VALUES(83,36,'83:36','«Кәпірлер,істегендерінің пайдасын көрді ме?»');
INSERT INTO "altay" VALUES(84,1,'84:1','Аспан жарылған сәтте,');
INSERT INTO "altay" VALUES(84,2,'84:2','Лайықты болып,Раббына бой ұсынған кезде,');
INSERT INTO "altay" VALUES(84,3,'84:3','Жер созылған сәтте,');
INSERT INTO "altay" VALUES(84,4,'84:4','Ол,ішіндегіні шығарып босатқан сәтте,');
INSERT INTO "altay" VALUES(84,5,'84:5','Раббына бой ұсынып,лайықты болған кезде;');
INSERT INTO "altay" VALUES(84,6,'84:6','Әй адамзат! Негізінде сен Раббыңа қарай өте тырыссаң; сонда Оған жолығасың.');
INSERT INTO "altay" VALUES(84,7,'84:7','Ал енді кімнің дәптері оңынан берілсе;');
INSERT INTO "altay" VALUES(84,8,'84:8','Сонда Ол,дереу жеңіл есеппен есептеледі.');
INSERT INTO "altay" VALUES(84,9,'84:9','Ол,үй ішіне-қуанып кайтады.');
INSERT INTO "altay" VALUES(84,10,'84:10','Ал және кімнің дәптері арт жағынан берілсе;');
INSERT INTO "altay" VALUES(84,11,'84:11','Сонда ол,дереу өлім шақырады.');
INSERT INTO "altay" VALUES(84,12,'84:12','Қайнаған тозаққа түседі.');
INSERT INTO "altay" VALUES(84,13,'84:13','Өйткені ол.өз үйінде мәз-мейрам еді.');
INSERT INTO "altay" VALUES(84,14,'84:14','Негізінде ол,әсте (Аллаға) қайтпаймын деп,ойлады.');
INSERT INTO "altay" VALUES(84,15,'84:15','Олай емес.Расында Раббы оны көздеуде еді.');
INSERT INTO "altay" VALUES(84,16,'84:16','Жоқ,шапақ уақытына,');
INSERT INTO "altay" VALUES(84,17,'84:17','Түнге әрі ішіндегі нәрсеге,');
INSERT INTO "altay" VALUES(84,18,'84:18','Толған айға серт.');
INSERT INTO "altay" VALUES(84,19,'84:19','Әлбетте сендер,бір жағдайдан бір жағдайға өтесіңдер.');
INSERT INTO "altay" VALUES(84,20,'84:20','Сонда оларға иман келтірмейтін не болды?');
INSERT INTO "altay" VALUES(84,21,'84:21','Оларға Құран оқылған кезде,сәжде қылмайды.');
INSERT INTO "altay" VALUES(84,22,'84:22','Әрине кәпірлер «Өтірік» дейді.');
INSERT INTO "altay" VALUES(84,23,'84:23','Алла олардың бүкпелеген нәрселерін жақсы біледі.');
INSERT INTO "altay" VALUES(84,24,'84:24','(Мұхаммед Ғ.С) оларды күйзелтетін азаппен шүйіншіле.');
INSERT INTO "altay" VALUES(84,25,'84:25','Бірақ иман келтіріп,түзу ғамал істеушілерге таусылмайтын сыйлық бар.');
INSERT INTO "altay" VALUES(85,1,'85:1','Жұлдызды аспанға ант,');
INSERT INTO "altay" VALUES(85,2,'85:2','Уәделі күнге,');
INSERT INTO "altay" VALUES(85,3,'85:3','Куәлік берушіге куәлік берілгенге серт.');
INSERT INTO "altay" VALUES(85,4,'85:4','Жер ошақшылардың жаны шықсын!');
INSERT INTO "altay" VALUES(85,5,'85:5','Отынмен жанған от.');
INSERT INTO "altay" VALUES(85,6,'85:6','Ол уақытта олар оның маңында отырған;');
INSERT INTO "altay" VALUES(85,7,'85:7','Олар,мүміндерге істегендерін көрген.');
INSERT INTO "altay" VALUES(85,8,'85:8','Олардың мүміндерге өшігулерінің себебі; үстем,мақтаулы Аллаға сенулреі ғана еді. (Бір дәуірдегі кәпір патша,Мұсылмандарды ор қазып ішіне,жаққан отқа салады екен.Б.М. басқа рауаяттар да бар.)');
INSERT INTO "altay" VALUES(85,9,'85:9','Ол сондай Алла,аспандар мен жердің иелігі Оған тән. Ол Алла.әр нәрсені көруші.');
INSERT INTO "altay" VALUES(85,10,'85:10','Негізінде мүмін ерлер,мүмін әйелдерге жапа беріп.сосын тәубе қылмағандар,оларға тозақтың азабы; жандыратын азап бар.');
INSERT INTO "altay" VALUES(85,11,'85:11','Негізінен иман келтіріп,ізгі іс істегендер,оларға астарынан өзендер ағатын бейіштер бар. Осы,зор мұратқа жетушілік.');
INSERT INTO "altay" VALUES(85,12,'85:12','Негізінен Раббыңның қолға алуы орасан қатты.');
INSERT INTO "altay" VALUES(85,13,'85:13','Өйткені Ол,бастапта жаратып,әрі қайта тірілтеді.');
INSERT INTO "altay" VALUES(85,14,'85:14','Ол,тым жарылқаушы,өте сүюші.');
INSERT INTO "altay" VALUES(85,15,'85:15','Ғаршының иесі,аса ұлы.');
INSERT INTO "altay" VALUES(85,16,'85:16','Ол,қалағанын бұлжытпай істеуші.');
INSERT INTO "altay" VALUES(85,17,'85:17','(Мұхаммед Ғ.С) саған ләшкерлердің хабары келді ме?');
INSERT INTO "altay" VALUES(85,18,'85:18','Перғауын және Сәмүд ләшкерлерінің ?');
INSERT INTO "altay" VALUES(85,19,'85:19','Дегенмен қарсы болғандар,өтіріксінуде.');
INSERT INTO "altay" VALUES(85,20,'85:20','Алла,оларды сырттай орап алған.');
INSERT INTO "altay" VALUES(85,21,'85:21','Әрине ол,ұлы бір Құран.');
INSERT INTO "altay" VALUES(85,22,'85:22','Лаухы Махфұзда сақтаулы.');
INSERT INTO "altay" VALUES(86,1,'86:1','Аспанға,Тарыққа серт.');
INSERT INTO "altay" VALUES(86,2,'86:2','(Мұхаммед Ғ.С) Тарық не екенін қайдан білесің?');
