INSERT INTO "altay" VALUES(98,1,'98:1','Кітап иелерінен,мүшіріктерден қарсы шыққандар,өздеріне бір ашық дәлел келгенге дейін қарсылықтарынан айрылмақшы емес еді.');
INSERT INTO "altay" VALUES(98,2,'98:2','(Ал бұл дәлел) таптаза нұсқаларды оқитын Алла тарапынан жіберілген бір Пайғамбар (еді.)');
