INSERT INTO "altay" VALUES(109,1,'109:1','(Құрайш кәрірлері: «Әй Мұхаммед! Бір жыл Аллаға,бір жыл бұтқа табынайық» десті. Б.Ж.М.Х.Т) (Мұхаммед Ғ.С) : «Әй Кәпірлер!»');
INSERT INTO "altay" VALUES(109,2,'109:2','«Сендердің табынғандарыңа табынбаймын;»');
INSERT INTO "altay" VALUES(109,3,'109:3','«Әрі сендер де менің құлшышлық қылғаныма құлшылық қылмайсыңдар.»');
INSERT INTO "altay" VALUES(109,4,'109:4','«Сірә мен седердің шоқынғандарыңа шоқынушы емеспін»');
INSERT INTO "altay" VALUES(109,5,'109:5','«Сондай ақ сендер де менің құлшылық қылғаныма,құлшылық қылушы емессіңдер.»');
INSERT INTO "altay" VALUES(109,6,'109:6','«Сендердің діндерің өздеріңе менің дінім өзіме тән» де.');
