INSERT INTO "altay" VALUES(114,2,'114:2','«Адамдардың иесіне,»');
INSERT INTO "altay" VALUES(114,3,'114:3','«Адамдардың иесіне,»');
INSERT INTO "altay" VALUES(114,4,'114:4','«Сыбырлап азғырушының кесірінен,»');
INSERT INTO "altay" VALUES(114,5,'114:5','«Сондай адамдардың көкейлеріне салатын;»');
INSERT INTO "altay" VALUES(114,6,'114:6','«Мейлі жыннан,мейлі адамдардан болсын, (бүкіл азғырушылардың кесірінен Аллаға) сиынамын» де.');
