INSERT INTO "amroti" VALUES(1,1,'1:1','الله ٻاجھاري مھربان جي نالي سان (شروع)');
INSERT INTO "amroti" VALUES(1,2,'1:2','سڀ ساراھ (خاص) جھانن جي پالڻھار الله کي جڳائي.');
INSERT INTO "amroti" VALUES(1,3,'1:3','(جو) ٻاجھارو مھربان.');
INSERT INTO "amroti" VALUES(1,4,'1:4','قيامت جي ڏينھن جو مالڪ (آھي).');
INSERT INTO "amroti" VALUES(1,5,'1:5','تنھنجي ئي عبادت ڪيون ٿا ۽ تو کان ئي مدد گھرون ٿا.');
INSERT INTO "amroti" VALUES(1,6,'1:6','اسان کي سڌي واٽ ڏيکار.');
INSERT INTO "amroti" VALUES(1,7,'1:7','جا انھن جي واٽ آھي جن تي فضل ڪيو اٿئي، نه انھن جي (واٽ) جن تي (تنھنجو) ڏمر ٿيل آھي ۽ نه گمراھن جي (واٽ).');
INSERT INTO "amroti" VALUES(2,1,'2:1','الٓمٓ');
INSERT INTO "amroti" VALUES(2,2,'2:2','ھيءُ ڪتاب، جنھن ۾ ڪو شڪ ڪونھي، اُنھن خدا ترسن کي سِڌو رستو ڏيکاريندڙ آھي،');
INSERT INTO "amroti" VALUES(2,3,'2:3','جيڪي اڻ ڏٺي تي ايمان آڻيندا آھن ۽ نماز پڙھندا آھن ۽ کين جيڪي رزق ڏنوسين تنھن مان خرچيندا آھن.');
INSERT INTO "amroti" VALUES(2,4,'2:4','۽ اُھي (اي پيغمبر) جيڪو (قرآن) تو ڏانھن لاٿو ويو ۽ جيڪي توکان اڳ (ٻـين پيغمبرن تي) لاٿو ويو تنھن کي مڃيندا آھن ۽ اُھي آخرت تي يقين رکندا آھن.');
INSERT INTO "amroti" VALUES(2,5,'2:5','اِھي ئي (انھيءَ) سڌي واٽ تي آھن جا سندن پالڻھار کان (ڏسيل) آھي ۽ اِھي ئي ڇوٽڪاري وارا آھن.');
INSERT INTO "amroti" VALUES(2,6,'2:6','بيشڪ جن انڪار ڪيو تن تي (اي پيغمبر!) تنھنجو کين ڊيڄارين يا نه ڊيڄارين (سو) ھڪجھڙو آھي اِھي ايمان نه آڻيندا.');
INSERT INTO "amroti" VALUES(2,7,'2:7','الله سندن دلين تي ۽ سندن ڪنن تي مُھر ھنئي آھي، ۽ سندن اکين تي ڇَوڙُ (چڙھيل) آھي ۽ اُنھن لاءِ وڏو عذاب آھي.');
INSERT INTO "amroti" VALUES(2,8,'2:8','۽ ماڻھن مان ڪي (اھڙا) آھن جي چوندا آھن ته الله ۽ قيامت جي ڏينھن کي مڃيوسين ۽ حقيقت ۾ اُھي مؤمن نه آھن.');
INSERT INTO "amroti" VALUES(2,9,'2:9','الله ۽ مومنن کي دلبو ڏيندا آھن، ۽ حالانڪ پاڻ کان سواءِ (ٻئي ڪنھن کي) دلبو نه ڏيندا آھن ۽ نه سمجھندا آھن.');
INSERT INTO "amroti" VALUES(2,10,'2:10','سندن دلين ۾ (منافقيءَ جي) بيماري آھي پوءِ الله سندين بيماري وڌائي، ۽ انھيءَ ڪري جو (ھو) ڪُوڙ ڳالھائيندا آھن انھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(2,11,'2:11','۽ جڏھن کين چئبو آھي ته مُلڪ ۾ فساد نه وجھو، چوندا آھن ته اسين سڌاريندڙ ئي آھيون.');
INSERT INTO "amroti" VALUES(2,12,'2:12','خبردار! اِھي پاڻ فسادي آھن پر نه سمجھندا آھن.');
INSERT INTO "amroti" VALUES(2,13,'2:13','۽ جڏھن کين چئبو آھي ته جھڙي طرح ٻـين ماڻھن ايمان آندو آھي (تھڙي طرح اوھين به) ايمان آڻيو (تڏھن) چوندا آھن ته جئن بي سمجھن ايمان آندو آھي تئن ايمان آڻيون ڇا؟ خبردار، اُھي ئي بي سمجھ آھن پر نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(2,14,'2:14','۽ جڏھن مؤمنن کي ملندا آھن (تڏھن) چوندا آھن ته اسان ايمان آندو آھي، ۽ جڏھن پنھنجن شيطانن (سردارن) کي ھيڪلو ملندا آھن (تڏھن) چوندا آھن ته اسين (پر ۾) اوھان سان آھيون اسين (مؤمنن سان) رڳو چٿر ڪندڙ آھيون.');
INSERT INTO "amroti" VALUES(2,15,'2:15','الله ساڻن چٿر ڪندو آھي ۽ انھن کي پنھنجي سرڪشي ۾ ڊيگھ ڏيندو آھي جو عقل جا انڌا آھن.');
INSERT INTO "amroti" VALUES(2,16,'2:16','اِھي اُھي آھن جن ھدايت جي بدران گمراھي ڳڌي، پوءِ سندن واپار کين فائدو نه ڏنو ۽ نڪي اُھي ھدايت وارا ٿيا.');
INSERT INTO "amroti" VALUES(2,17,'2:17','سندن مثال انھيءَ جي مثال جھڙو آھي جنھن باھ ٻاري، پوءِ جنھن مھل باھ پنھنجي آس پاس کي روشن ڪيو (تنھن مھل) الله سندن سوجھرو اجھايو ۽ کين اونداھيءَ ۾ ڇڏي ڏنائين جو نه ڏسن.');
INSERT INTO "amroti" VALUES(2,18,'2:18','(اُھي) ٻوڙا، گونگا، انڌا آھن پوءِ اُھي نه موٽندا.');
INSERT INTO "amroti" VALUES(2,19,'2:19','يا (سندن مثال اُنھي) آسماني برسات (وارن) جھڙو آھي جنھن ۾ اونداھيون ۽ گوڙ ۽ وڄ ھجي، اُھي پنھنجيون آڱريون پنھنجن ڪنن ۾، ڪڙڪن کان موت جي ڀَو ڪري وجھن، ۽ الله ڪافرن کي گھيرو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(2,20,'2:20','وڄ سندن اکين (جي سوجھري کي) اُمالڪ کسڻ تي ھُجي، جڏھن انھن لاءِ چمڪي ته منجھس ھلن ٿا، ۽ جڏھن مٿن اوندھ ڪري تڏھن بيھي رھن ٿا، ۽ جيڪڏھن الله گھري ته سندن اکيون ۽ سندن ڪن ضرور وڃائي ڇڏي، بيشڪ الله سڀ ڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(2,21,'2:21','اي ماڻھؤ پنھنجي انھيءَ پالڻھار جي عبادت ڪريو جنھن اوھان کي ۽ جي اوھان کان اڳ هُئا تن کي پيدا ڪيوته مانَ اوھين ڊڄو.');
INSERT INTO "amroti" VALUES(2,22,'2:22','جنھن اوھان لاءِ زمين کي وڇاڻو بڻايو ۽ آسمان کي ڇت، ۽ (مينھن جو) پاڻي آسمان کان وسايائين پوءِ اُن سان (ھر جنس جي) ميون منجھان اوھان لاءِ روزي پيدا ڪيائين، پوءِ اوھين ڄاڻ ھوندي الله سان ڪنھن کي شريڪ نه ڪريو.');
INSERT INTO "amroti" VALUES(2,23,'2:23','۽ پنھنجي ٻانھي (محمد صلى الله عليه وسلم) تي جيڪو (قرآن) لاٿوسون تنھن کان جيڪڏھن اوھين شڪ ۾ آھيوته جھڙيس ڪائي سُورة بڻائي آڻيو، ۽ الله کان سواءِ پنھنجا مددگار (به) سڏيو جيڪڏھن سچا آھيو.');
INSERT INTO "amroti" VALUES(2,24,'2:24','پوءِ جيڪڏھن نه ڪندؤ، ۽ ڪري به ڪڏھن نه سگھندؤ، ته انھيءَ باھ کان ڊڄو جنھن جو ٻل ماڻھو ۽ پھڻ آھن، اُھا نه مڃيندڙن لاءِ تيار ڪئي وئي آھي.');
INSERT INTO "amroti" VALUES(2,25,'2:25','۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي ھيءَ خوشخبري ڏي ته انھن لاءِ بھشت آھن جن جي ھيٺان واھيون پيون وھن، جڏھن اُن مان ڪو به ميوو کاڄ لاءِ ڏنو پيو ويندن (تنھن مھل) پيا چوندا ته ھيءُ (ميوو) اُھو آھي جو اڳ اسان کي ڏنو ويو ھو ۽ اُھو (شڪل ۾) ھڪ جھڙو (پيو) ڏبن، ۽ انھن لاءِ منجھس پاڪ زالون آھن ۽ اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(2,26,'2:26','الله مڇر ۽ اُن کان وڌيڪ جي مثال ڏيڻ کان حياء ئي نه ڪندوآھي، پوءِ جن ايمان آندو آھي سي (پڪ) ڄاڻندا آھن ته اُھو سندن پالڻھار وٽان (آيل) سچ آھي، ۽ جن انڪار ڪيو سي چوندا آھن ته ھن مثال ڏيڻ ۾ الله جو ڇا مطلب آھي؟ (الله) ان مثال (ڏيڻ) سببان گھڻن کي ڀلائيندو آھي ۽ ان سببان گھڻن کي سڌو رستو ڏيکاريندو آھي، اُن سان اھڙن بي دينن کانسواءِ (ٻي ڪنھين کي) نه ڀلائيندو آھي.');
INSERT INTO "amroti" VALUES(2,27,'2:27','جيڪي الله جو انجام ان جي پڪي ڪرڻ کانپوءِ ڀڃندا آھن، ۽ الله جن ڪمن جي ڳنڍڻ جو حڪم ڪيو آھي تن کي ڇنندا آھن ۽ مُلڪ ۾ فساد وجھندا آھن، اُھي ئي ڇيئي وارا آھن.');
INSERT INTO "amroti" VALUES(2,28,'2:28','اوھين ھن ھوندي به الله کي ڪھڙي طرح نه ٿا مڃيو؟ جو بي ساھا ھُيؤ پوءِ اوھان کي جيئرو ڪيائين، وري اوھان کي ماريندو، وري اوھان کي جياريندو ۽ وري ڏانھس موٽايا ويندؤ.');
INSERT INTO "amroti" VALUES(2,29,'2:29','اُھو (الله) آھي جنھن توھان (جي فائدي) لاءِ جيڪي مڙيئي (شيون) زمين ۾ آھن سي پيدا ڪيون، وري آسمان ڏانھن متوجه ٿيو پوءِ اُنھن کي ست آسمان ڪري برابر بيھاريائين، ۽ اُھو (الله) سڀڪنھن شيءِ کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,30,'2:30','۽ (اي پيغمبر) جڏھن تنھنجي پالڻھار ملائڪن کي چيو ته آءٌ زمين ۾ ھڪ نائب مُقرر ڪرڻ وارو آھيان، (تڏھن) چيائون ته ڇا منجھس اھڙي کي ٿو پيدا ڪرين جو منجھس فساد ڪندو ۽ (ناحق) رت ھاريندو، ۽ اسين تنھنجي ساراہ سان (تنھنجي) پاڪائي واکاڻيون ٿا ۽ توکي پاڪ ڪري مڃيون ٿا، فرمايائين ته جيڪي آءٌ ڄاڻندو آھيان سو توھان نٿا ڄاڻو.');
INSERT INTO "amroti" VALUES(2,31,'2:31','۽ (الله) آدم کي سڀني شين جا نالا سيکاريا وري اُھي شيون ملائڪن جي آڏو ڪيائين پوءِ فرمايائين ته جيڪڏھن سچا آھيو ته ھنن جا نالا ڏسيوم.');
INSERT INTO "amroti" VALUES(2,32,'2:32','چيائون ته تون پاڪ آھين جيڪي اسان کي ڄاڻايو اٿيئي تنھن کانسواءِ اسان کي (ٻي) ڪا خبر نه آھي، بيشڪ تون ئي ڄاڻندڙ حڪمت وارو آھين.');
INSERT INTO "amroti" VALUES(2,33,'2:33','(الله) چيو ته اي آدم انھن جا نالا کين ڏس، پوءِ جڏھن کين انھن جا نالا ڏسيائين (تڏھن الله) چيو ته اوھان کي نه چيو ھوم ڇا ته آءٌ آسمانن ۽ زمين جو ڳجھ ڄاڻندو آھيان ۽ جيڪي پڌرو ڪيو ٿا ۽ جيڪي لڪايو ٿا سو به ڄاڻندو آھيان.');
INSERT INTO "amroti" VALUES(2,34,'2:34','۽ جڏھن ملائڪن کي چيوسون ته آدم کي سجدو ڪريو تڏھن شيطان ڌاران (ٻـين) سجدو ڪيو، ھن انڪار ڪيو ۽ ھٺ ڪيائين ۽ ڪافرن مان ٿيو.');
INSERT INTO "amroti" VALUES(2,35,'2:35','۽ چيوسون ته اي آدم تون ۽ تنھنجي زال بھشت ۾ ٽڪو ۽ جتان وڻيوَ تتان مزي سان کائو، ۽ ھن وڻ کي ويجھا نه وڃجو نه ته ظالمن (جي شمار) مان ٿيندؤ.');
INSERT INTO "amroti" VALUES(2,36,'2:36','پوءِ شيطان اُتاھون انھن کي ٿيڙيو پوءِ جنھن (مزي) ۾ ھئا تنھن مان کين (ٻاھر) ڪڍيائين، ۽ چيوسون ته (بھشت مان) نڪري ھيٺ ٿيو اوھين ھڪ ٻئي جا ويري آھيو، ۽ اوھان لاءِ ڪنھن وقت تائين زمين ۾ رھڪ جو ھنڌ ۽ (گذران جو) سامان (ٺھرايل) آھي.');
INSERT INTO "amroti" VALUES(2,37,'2:37','پوءِ آدم پنھنجي پالڻھار وٽان ڪي لفظ سکيا پوءِ (الله) سندس توبه قبول ڪئي، بيشڪ اُھو ئي توبه قبوليندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(2,38,'2:38','چيوسين ته منجھانئس سڀ لھي ھيٺ وڃو، پوءِ جڏھن مون وٽان اوھان وٽ ڪا ھدايت اچي تڏھن جيڪي منھنجي ھدايت تي ھلندا تن کي ڪو ڀؤ ڪونھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(2,39,'2:39','۽ جن نه مڃيو ۽ اسانجي آيتن کي ڪُوڙو ڄاتو سي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(2,40,'2:40','اي يعقوب جي اولاد اُھو منھنجو ڳڻ ياد ڪريو جيڪو مون توھان تي ڪيو ۽ منھنجو انجام پاڙيو ته آءٌ (به) اوھان جو انجام پاڙيان ۽ رڳو مون کان ڊپ رکو.');
INSERT INTO "amroti" VALUES(2,41,'2:41','۽ (اي بني اسرائيل) جيڪو اوھان وٽ آھي (يعني توريت) تنھن کي سچو ڪندڙ جيڪو (قرآن محمد صلى الله عليه وسلم تي) لاٿم تنھنکي مڃيو ۽ ان جا پھريان منڪر نه ٿيو، ۽ منھنجي آيتن کي (دنيا جي) ٿوري ملھ سان نه وڪڻو، ۽ رڳو مون کا ڊڄو.');
INSERT INTO "amroti" VALUES(2,42,'2:42','۽ سچ کي ڪوڙ سان نه ملايو ۽ نڪي اوھين ڄاڻ ھوندي به سچ لڪايو.');
INSERT INTO "amroti" VALUES(2,43,'2:43','۽ نماز پڙھو ۽ زڪوٰة ڏيو ۽ رڪوع ڪندڙن سان گڏجي رڪوع ڪيو.');
INSERT INTO "amroti" VALUES(2,44,'2:44','ماڻھن کي چڱائيءَ جو حڪم ڪريو ٿا ۽ پاڻ کي وساريو ٿا؟ ھن ھوندي به جو توھين ڪتاب پڙھو ٿا، پوءِ ڇو نه ٿا سمجھو؟');
INSERT INTO "amroti" VALUES(2,45,'2:45','۽ صبر ۽ نماز سان مدد وٺو، ۽ اُھا انھن آزي ڪندڙن کانسواءِ ٻـين کي ضرور ڏکي (لڳندي) آھي.');
INSERT INTO "amroti" VALUES(2,46,'2:46','جيڪي پڪ ڀائيندا آھن ته اُھي پنھنجي پالڻھار کي ملڻ وارا آھن ۽ اُھي ڏانھس موٽڻ وارا آھن.');
INSERT INTO "amroti" VALUES(2,47,'2:47','اي بني اسرائيلو اُھو منھنجو ڳڻ ياد ڪريو جيڪو مون اوھان تي ڪيو ۽ مون اوھان کي جھان (وارن) تي سڳورو ڪيو.');
INSERT INTO "amroti" VALUES(2,48,'2:48','۽ اُنھيءَ ڏينھن کان ڊڄو جنھن ۾ ڪو (به) ڪنھين کان ڪجھ به ٽاري نه سگھندو ۽ نڪي کانئس ڪا پارت قبول ڪبي ۽ نڪي کانئس ڪو عِوض وٺبو نڪي کين مدد ڏبي.');
INSERT INTO "amroti" VALUES(2,49,'2:49','۽ (ھي ڳڻ به ياد ڪريو) ته جڏھن اوھان کي فرعون جي ماڻھن کان ڇڏايوسون جي اوھان کي ڏاڍو ايذاءُ چکائيندا ھئا، جو اوھان جا پُٽ ڪھندا ھئا ۽ اوھان جون ڌيئرون جيئريون ڇڏيندا ھئا، ۽ اُن ۾ اوھان جي پالڻھار کان اوھان لاءِ وڏي پرک ھُئي.');
INSERT INTO "amroti" VALUES(2,50,'2:50','۽ (ياد ڪريو ته) جڏھن اوھان لاءِ سمنڊ کي چيريوسون پوءِ اوھان کي (ٻڏڻ کان) بچايوسون ۽ فرعون جي ماڻھن کي ٻوڙيوسون ۽ اوھين ڏسندا رھيؤ.');
INSERT INTO "amroti" VALUES(2,51,'2:51','۽ (ياد ڪريو ته) جڏھن موسىٰ کي چاليھن راتين جو انجام ڏنوسون وري ان (جي وڃڻ) کانپوءِ گابي کي (خدا ڪري) ورتؤ ۽ اوھين ظلم ڪندڙ ھيؤ.');
INSERT INTO "amroti" VALUES(2,52,'2:52','وري اُن کان پوءِ اوھان کان ٽارو ڪيوسون ته مان اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(2,53,'2:53','۽ (ياد ڪريو ته) جڏھن موسىٰ کي ڪتاب ۽ (سچ ۽ ڪوڙجي وچ ۾) سنڌو وجھندڙ ڏنوسين ته مان اوھين ھدايت وارا ٿيو.');
INSERT INTO "amroti" VALUES(2,54,'2:54','۽ (ياد ڪريو ته) جڏھن موسىٰ پنھنجي قوم کي چيو ته اي منھنجي قوم اوھان گابي کي خُدا ڪري وٺڻ سببان پاڻ تي ظلم ڪيو آھي تنھنڪري پنھنجي پالڻھار ڏانھن موٽو ۽ پاڻ کي ڪُھو، اھو اوھان لاءِ توھان جي پيدا ڪندڙ وٽ چڱو آھي، پوءِ اوھان تي ٻاجھ ڪيائين، بيشڪ اُھو ئي معافي ڏيندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(2,55,'2:55','۽ (ياد ڪريو ته) جڏھن اوھان چيو ته اي موسىٰ جيستائين الله چٽو (نه) ڏسنداسون تيستائين توکي ڪڏھن به نه مڃينداسون پوءِ اوھان تي وڄ اچي ڪڙڪي ۽ اوھين ڏسندا رھيؤ.');
INSERT INTO "amroti" VALUES(2,56,'2:56','وري اوھان جي مرڻ کان پوءِ اوھان کي جيئرو ڪيوسون ته مانَ اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(2,57,'2:57','۽ اوھان تي ڪڪر جي ڇانوَ ڪئي سون ۽ اوھان تي مَنّ ۽ سلوىٰ لاٿاسون، ته جيڪي اوھان کي سٺين شين مان رزق ڏنوسين سو کائو، ۽ اسان تي (ڪي) ظلم نه ڪيائون پر پاڻ تي ظلم ڪندا ھئا.');
INSERT INTO "amroti" VALUES(2,58,'2:58','۽ جڏھن چيوسون ته ھِن ڳوٺ ۾ گھِڙو پوءِ منجھانئس جتان وڻيوَ تتان مزي سان کائو ۽ دروازي کان سجدو ڪندڙ ٿي لنگھو ۽ چئو ته اسان جا ڏوھ بخش ته اوھان جون مدايون اوھان کي بخشيون، ۽ چڱائي ڪندڙن کي سگھو وڌائينداسون.');
INSERT INTO "amroti" VALUES(2,59,'2:59','پوءِ ظالمن جيڪي کين چيو ويو تنھن ڳالھ کي مٽائي ٻيو چيو پوءِ اُنھن ظالمن تي سندن بڇڙي ھجڻ سببان آسمان کان عذاب لاٿوسون.');
INSERT INTO "amroti" VALUES(2,60,'2:60','۽ (ياد ڪر ته) جڏھن موسىٰ پنھنجي قوم لاءِ پاڻي گھريو تڏھن چيوسون ته پنھنجي لٺ پھڻ کي ھڻ، پوءِ منجھانئس ٻارھن واھيون ڦاٽي نڪتيون، سڀڪنھن ماڻھو بيشڪ پنھنجو تڙ سُڃاتو، (چيوسون ته) الله جي رزق مان کائو پيو ۽ زمين ۾ فسادي ٿي بگيڙ نه وجھو.');
INSERT INTO "amroti" VALUES(2,61,'2:61','۽ جڏھن چيوَ ته اي موسىٰ ھڪ طعام تي ڪڏھن به صبر نه ڪنداسون تنھنڪري پنھنجي پالڻھار کان اسان لاءِ (دُعا) گھر ته زمين جيڪو پنھنجو ساڳ ۽ پنھنجا بادرنگ ۽ پنھنجي ڪڻڪ ۽ پنھنجي مُھري ۽ پنھنجا بصر ڄمائيندي آھي سي اسان لاءِ پيدا ڪري، چيائين ته جيڪي چڱيون آھن سي سادين سان ڇو ٿا مٽايو؟ (جي نٿا رھو ته) ڪنھن شھر ۾ لھي وڃو ۽ پوءِ جيڪي گھرو ٿا سو سڀ اوھان لاءِ (اُتي موجود) آھي، خواري ۽ محتاجي مٿن ھنئي وئي ۽ الله جي ڏمر ھيٺ وريا، ھِيءُ ھن ڪري آھي جو اُنھن الله جي حڪمن کي نه ٿي مڃيو ۽ پيغمبرن کي ناحق ٿي ڪُٺائون، اھو ڪم بي فرمانيءَ سببان ٿي ڪيائون ۽ حد کان لنگھندڙ ھوا.');
INSERT INTO "amroti" VALUES(2,62,'2:62','مؤمنن ۽ يھودين ۽ نصارن ۽ صابين منجھان جن الله ۽ قيامت جي ڏينھن کي مڃيو ۽ چڱا ڪم ڪيا تن لاءِ سندن پالڻھار وٽ سندن اجر آھي، ۽ کين نڪي ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(2,63,'2:63','۽ جڏھن اوھان کان انجام ورتوسين ۽ اوھان جي مٿان (جبل) طور کي کڙو ڪيوسون، (تڏھن چيوسون ته) جيڪو (توريت) اوھانکي ڏنوسون سوگھو وٺو ۽ جيڪي منجھس آھي سو ياد ڪريو مانَ اوھين ڊڄو.');
INSERT INTO "amroti" VALUES(2,64,'2:64','وري اُن کانپوءِ اوھين ڦريؤ، پوءِ جڏھن اوھان تي الله جو فضل ۽ سندس ٻاجھ نه ٿئي ھا ته اوھين ضرور ڇيئي وارن مان ٿيو ھا.');
INSERT INTO "amroti" VALUES(2,65,'2:65','۽ انھن کي بيشڪ ڄاتو اٿو جي اوھان مان ڇنڇر بابت حد کان لنگھيا پوءِ کين چيوسين ته خوار ڀولڙا ٿيو.');
INSERT INTO "amroti" VALUES(2,66,'2:66','پوءِ اُن (قصّه) کي اُن ڳوٺ جي آس پاس وارن ۽ اُنھن جي پوين لاءِ عبرت ۽ خُدا ترسن لاءِ نصيحت ڪئي سون.');
INSERT INTO "amroti" VALUES(2,67,'2:67','۽ جڏھن موسىٰ پنھنجي قوم کي چيو ته الله اوھان کي ڳـئون ڪُھڻ جو حڪم ڪري ٿو، (تڏھن) چيائون ته تون اسان سان چٿر ڪرڻ لڳين ٿو ڇا؟ (موسىٰ) چيو ته آءٌ الله کان جاھلن مان ھجڻ جي پناہ گُھران ٿو.');
INSERT INTO "amroti" VALUES(2,68,'2:68','چيائون ته اسان لاءِ پنھنجي پالڻھار کان دُعا گھر ته اسان لاءِ چٽائي ڪري ته اُھا (ڳـئون) ڪھڙي (قسم جي) آھي، (موسىٰ) چيو ته الله چوي ٿو ته اُھا ڳـئون نڪي پوڙھي ۽ نڪي جوان آھي، اِنھي وچ ۾ وچٿري آھي، پوءِ جيڪي اوھان کي حڪم ڏنو ويو سو ڪريو.');
INSERT INTO "amroti" VALUES(2,69,'2:69','چيائون ته پنھنجي پالڻھار کان اسان لاءِ دُعا گُھرته اسان لاءِ چٽائي ڪري ته ان جو رنگ ڪھڙو آھي، (موسىٰ) چيو ته الله چوي ٿو ته اُھا ڳـئون ھيڊي ڪڪڙي آھي سندس رنگ ڳوڙھو ھيڊو آھي ڏسندڙن کي سُرھائي ڏيندي آھي.');
INSERT INTO "amroti" VALUES(2,70,'2:70','چيائون ته پنھنجي پالڻھار کان اسان لاءِ دُعا گھر ته اسان لاءِ چٽائي ڪري ته اُھا (ڳـئون) ڪھڙي (پارين) آھي ڇوته ڳـئون اسان وٽ (پاڻ جھڙين ۾) گڏيل آھي، ۽ جيڪڏھن الله گھريو ته اسين ضرور لھنداسين.');
INSERT INTO "amroti" VALUES(2,71,'2:71','(موسىٰ) چيو ته الله چوي ٿو ته اُھا اھڙي ڳـئون آھي (جو) نڪي وھو آھي جو ھر کيڙي ۽ نڪي پوک پياري، اڻ وڍ آھي منجھس ڪو دٻڪ ڪونھي، چيائون ته ھاڻي تو پورو پار آندو، پوءِ اُن کي ڪُٺائون ۽ (انھي ڪم) ڪرڻ تي نه ھوا.');
INSERT INTO "amroti" VALUES(2,72,'2:72','۽ جڏھن ھڪ شخص کي ڪُٺوَ تڏھن ان (جي ماريندڙ) بابت تڪرار ڪيوَ، ۽ جيڪي لڪائيندا آھيو سو الله پڌرو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(2,73,'2:73','پوءِ چيوسين ته سندس (گوشت جو) ڪُجھ ڀاڱو اُن (مئل) کي ھڻو، اھڙي طرح الله مئلن کي جياريندو آھي ۽ اوھان کي پنھنجيون نشانيون ڏيکاريندو آھي ته مانَ اوھين سمجھو.');
INSERT INTO "amroti" VALUES(2,74,'2:74','وري ان کانپوءِ اوھان جون دليون ڏاڍيون ٿيون پوءِ پھڻ وانگر بلڪ ڏاڍيون سخت آھن، ۽ پھڻن مان ڪي اھڙا (به) آھن جو منجھائن واھيون ڦاٽي نڪرنديون آھن، ۽ منجھائن ڪو ڦاٽندو آھي پوءِ منجھانئس پاڻي نڪرندو آھي، ۽ منجھائس ڪو الله جي ڀؤ کان ڪرندو آھي، ۽ جيڪي ڪندا آھيو تنھن کان الله بيخبر نه آھي.');
INSERT INTO "amroti" VALUES(2,75,'2:75','(اي مسلمانؤ! اوھين يھودين ۾) پاڻ لاءِ فرمانبردار ھجڻ جو ھِن ھوندي به اڃان آسرو رکو ٿا ڇا؟ جو بيشڪ منجھائن ھڪ ٽولي اِھڙي آھي جو الله جو ڪلام ٻڌندا آھن وري ان جي سمجھڻ کان پوءِ اُھي ڄاڻ ھوندي ان کي مٽائيندا آھن.');
INSERT INTO "amroti" VALUES(2,76,'2:76','۽ جڏھن مؤمنن کي ملندا آھن تڏھن چوندا آھن ته مڃيوسين، ۽ جڏھن ھڪ ٻئي کي ھيڪلو ملندا آھن (تڏھن پاڻ ۾) چوندا آھن ته جيڪي الله اوھان تي کوليو آھي تنھن جي ڳالھ ساڻن ڇو ڪندا آھيو ته اِن سببان اوھان جي پالڻھار وٽ اوھان تي حجت وٺندا رھن، اوھين ڇونه سمجھندا آھيو؟');
INSERT INTO "amroti" VALUES(2,77,'2:77','نه ڄاڻندا آھن ڇا ته جيڪي لڪائيندا آھن ۽ جيڪي پڌرو ڪندا آھن سو الله ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(2,78,'2:78','۽ منجھائن ڪي اڻ پڙھيا آھن جي ڪوڙن خيالن کانسواءِ ڪتاب کي نه ڄاڻندا آھن ۽ اُھي رڳو اٽڪل ھلندا آھن.');
INSERT INTO "amroti" VALUES(2,79,'2:79','پوءِ اُنھن لاءِ خرابي آھي جيڪي پنھنجو ھٿراڌو ڪتاب لکندا آھن، موٽي (ماڻھن کي) چوندا آھن ته ھيءُ الله وٽان (آيو) آھي ھِن لاءِ ته ان سان (دنيا جو) ٿورو ملھ وٺن، پوءِ جيڪي سندن ھٿن لکيو تنھن سببان انھن لاءِ خرابي آھي ۽ جيڪي ڪمائيندا آھن تنھن سببان (پڻ) انھن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(2,80,'2:80','۽ چون ٿا ته باھ ڳڻيلن ڏينھن کان سواءِ اسان کي ڪڏھن نه ڇھندي. اي (پيغمبرکين) چئو ته الله وٽان اھڙو انجام ورتو اٿوَ ڇا؟ ته الله پنھنجي انجام کي ڪڏھن نه ڦيريندو يا الله تي اڻ ڄاتو (ڪوڙ) ڳالھائيندا آھيو.');
INSERT INTO "amroti" VALUES(2,81,'2:81','ھائو جن مدائي ڪمائي ۽ سندن بڇڙائي کين ويڙھي وئي سي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(2,82,'2:82','۽ جن ايمان آندو ۽ چڱا ڪم ڪيا سي بھشتي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(2,83,'2:83','۽ جڏھن بني اسرائيلن کان انجام ورتوسين ته الله کانسواءِ ٻئي ڪنھن جي عبادت نه ڪريو، ۽ ماءُ پيءُ ۽ مِٽن ۽ ڇورن (ٻارن) ۽ مسڪينن سان چڱائي ڪريو ۽ ماڻھن سان مِٺو ڳالھايو ۽ نماز پڙھو ۽ زڪوٰة ڏيو، وري اوھان منجھان ٿورن کانسواءِ (سڀ) ڦِري ويؤ ۽ اوھين منھن موڙيندا ھيؤ.');
INSERT INTO "amroti" VALUES(2,84,'2:84','۽ جڏھن اوھان کان انجام ورتوسون ته پنھنجا خون نه ڪندؤ ۽ نڪي پنھنجي قوم کي پنھنجن ديسن مان لڏائيندؤ پوءِ باسيوَ ۽ (ان جا) اوھين گواھ آھيو.');
INSERT INTO "amroti" VALUES(2,85,'2:85','(تنھن کان) پوءِ اوھين اھڙا ٿيا آھيو جو پنھنجي قوم کي ڪُھو ٿا ۽ پنھنجي قوم مان ھڪ ٽوليءَ تي ايذاء ۽ ڏاڍ ڪرڻ لاءِ (پاڻ ۾) ھڪ ٻئي کي مدد ڏئي کين سندن ديس مان لڏايو ٿا، ۽ جيڪڏھن (ڪي) باندي ٿي اوھان وٽ ايندا آھن ته چَٽي ڀري کين ڇڏائيندا آھيو ھن ھوندي جو اُھو سندن لوڌڻ اوھان تي حرام ٿيل آھي، پوءِ ڇاکون ڪي (حڪم) ڪتاب (توريت) جا مڃيندا آھيو ۽ ڪي نه مڃيندا آھيو؟ پوءِ اوھان مان جيڪو اھڙو ڪم ڪري تنھن جي سزا دنيا جي حياتيءَ ۾ خواريءَ کانسواءِ ڪانه آھي، ۽ قيامت جي ڏينھن سخت عذاب ڏانھن ورايا ويندا، ۽ جيڪي ڪندا آھيو تنھن کان الله بي خبر نه آھي.');
INSERT INTO "amroti" VALUES(2,86,'2:86','اِھي اُھي آھن جن آخرت جي بدران دنيا جي حياتي ڳڌي، پوءِ کانئن عذاب ھلڪو نه ڪيو ويندو ۽ نڪي کين مدد ڏبي.');
INSERT INTO "amroti" VALUES(2,87,'2:87','۽ بيشڪ مُوسىٰ کي ڪتاب (توريت) ڏنوسون ۽ اُن کان پوءِ (ٻيا) پيغمبر ھڪ ٻئي پٺيان موڪلياسون، ۽ عيسىٰ پٽ مريم کي پڌرا مُعجزا ڏناسون ۽ کين پاڪ روح سان مدد ڏني سون، پوءِ جڏھن به ڪنھن پيغمبر اوھان وٽ اُھي حُڪم آندا ٿي جن کي اوھان جي دلين نٿي گھريو تڏھن اوھان (ان جي مڃڻ کان) وڏائي ڪئي، پوءِ (پيغمبرن جي) ھڪ ٽولي کي ڪُوڙو ڄاتوَ، ۽ ٻئي کي ڪُٺَوَ.');
INSERT INTO "amroti" VALUES(2,88,'2:88','۽ چون ٿا ته اسان جون دليون ڍڪيل آھن، (نه) بلڪ الله سندن ڪفر سببان مٿن لعنت ڪئي آھي پوءِ ٿورا ايمان آڻيندا.');
INSERT INTO "amroti" VALUES(2,89,'2:89','۽ جڏھن الله وٽان ڪتاب (قرآن) انھي (توريت) جو سچو ڪندڙ آين جيڪو وٽن آھي ۽ (ھن کان) اڳ ڪافرن تي سوڀ جون (دعائون) گھرندا ھئا، پوءِ جڏھن وٽن آيو جنھن کي سڃاتائون تڏھن ان جو انڪار ڪيائون، پوءِ ڪافرن تي الله جي لعنت آھي.');
INSERT INTO "amroti" VALUES(2,90,'2:90','اُھو بڇڙو (سودو) آھي جنھن ۾ پاڻ ڏئي جيڪي الله (محمد صلى الله عليه وسلم تي) لاٿو آھي تنھن جو نه مڃڻ ھن (ڳالھ) جي ساڙ ڪري ڳڌائون ته الله پنھنجي ٻانھن مان جنھن تي گھرندو آھي تنھن تي پنھنجي ٻاجھ سان ڪتاب لاھيندو آھي، پوءِ (الله جي) ڏمر تي ڏمر ھيٺ وريا، ۽ ڪافرن لاءِ خواري ڏيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(2,91,'2:91','۽ جڏھن کين چئبو آھي ته جيڪو (قرآن) الله لاٿو تنھن کي مڃيو تڏھن چون ٿا ته جيڪو (توريت) اسان تي لٿل آھي تنھن کي مڃيندا آھيون ۽ جيڪي ان کانسواءِ آھي تنھن کي نه مڃيندا آھن، ۽ (حقيقت ڪري) اھو (قرآن) سچو آھي جيڪو (توريت) وٽن آھي تنھنجو سچو ڪندڙ (به) آھي، (اي پيغمبر) چؤ ته جيڪڏھن (توريت جا) مڃيندڙ آھيو ته الله جي پيغمبرن کي ڇو ڪُٺوَ.');
INSERT INTO "amroti" VALUES(2,92,'2:92','بيشڪ مُوسىٰ اوھان وٽ چٽا معجزا آندا وري کانئس پوءِ گابي کي (خدا ڪري) ورتوَ ۽ اوھين ظالم آھيو.');
INSERT INTO "amroti" VALUES(2,93,'2:93','۽ جڏھن اوھان کان انجام ورتوسون ۽ اوھان جي مٿان طُور (جبل) کڙو ڪيوسون، (چيوسون ته) جيڪو اوھان کي ڏنوسون (يعني توريت) سو سوگھو وٺو ۽ ٻڌو، تڏھن چيائون ته ٻڌوسون ۽ نه مڃيوسون، ۽ انھن جي دلين ۾ سندن ڪفر سببان گابي جي پريت وڌي وئي، (اي پيغمبر کين) چؤ ته اوھان جو ايمان اوھان کي جنھن شيءِ جو حڪم ڪندو آھي سا بڇڙي آھي جيڪڏھن اوھين (توريت) جا مڃيندڙ آھيو.');
INSERT INTO "amroti" VALUES(2,94,'2:94','(اي پيغمبرکين) چؤ ته جيڪڏھن آخرت جو گَھرُ الله وٽ ٻـين ماڻھن کان سواءِ خلاصو اوھان لاءِ آھي ته جيڪڏھن (پنھنجي چوڻ ۾) سچا آھيو ته موت گھرو.');
INSERT INTO "amroti" VALUES(2,95,'2:95','۽ جيڪي ڪرتوت سندن ھٿن اڳي موڪليا آھن تن سببان اُن (موت) کي ڪڏھن به نه گھرندا، ۽ الله ظالمن کي (چڱي طرح) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,96,'2:96','۽ (اي پيغمبر) انھن (يھودين) کي جيئڻ تي ماڻھن ۽ مشرڪن کان وڌيڪ حرص وارو لھندين، منجھائن ھر ڪو گھرندو آھي ته جيڪر ھزار ورہيه جياريو وڃي ھا، ۽ اھو (ايڏو جيئڻ) کيس عذاب کان پري ڪرڻ وارو نه آھي، ۽ جيڪي ڪندا آھن تنھن کي الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(2,97,'2:97','(اي پيغمبرکين) چئو ته جيڪو جبرائيل جو ويري ٿيندو (سو الله جو ويري آھي) ڇوته جبرائيل قرآن کي الله جي حڪم سان تنھنجي دل تي لاٿو آھي جيڪي کانئس اڳي آھي تنھن کي سچو ڪندڙ آھي ۽ مؤمنن لاءِ ھدايت ۽ خوشخبري آھي.');
INSERT INTO "amroti" VALUES(2,98,'2:98','جيڪو الله ۽ سندس ملائڪن ۽ سندس پيغمبرن ۽ جبرائيل ۽ ميڪائيل جو ويري بڻبو ته بيشڪ الله (به اھڙن) ڪافرن جو ويري آھي.');
INSERT INTO "amroti" VALUES(2,99,'2:99','۽ (اي پيغمبر) بي شڪ تو ڏانھن صاف آيتون موڪليون سون، ۽ بي دينن کانسواءِ ڪوبه اُن جو منڪر نه ٿيندو.');
INSERT INTO "amroti" VALUES(2,100,'2:100','جڏھن به ڪو انجام ٻڌائون ٿي ته ان کي ڇو منجھائن ھڪ ٽوليءَ ڀڳو ٿي، بلڪ منجھائن گھڻا ايمان نه آڻيندا آھن.');
INSERT INTO "amroti" VALUES(2,101,'2:101','۽ جڏھن پيغمبر (مُحمّدصلى الله عليه وسلم) انھي (يعني توريت) جو سچو ڪندڙ جيڪو وٽن آھي الله وٽان آين (تڏھن) جن کي ڪتاب (توريت) ڏنو ويو تن مان ھڪ ٽوليءَ الله جي ڪتاب کي پنھنجن پُٺين پويان اُڇليو ڄڻڪ اُھي ڄاڻندا ئي نه آھن.');
INSERT INTO "amroti" VALUES(2,102,'2:102','۽ اُھي (يھودي توريت جي پيروي ڇڏي) اھڙن (باطل علمن) جي پٺيان لڳا جيڪي سليمان جي واري ۾ شيطان پڙھندا ھوا، ۽ سليمان (جادو ڪرڻ جو) ڪفر نه ڪيو پر شيطانن ڪفر ڪيو جو ماڻھن کي جادو سيکاريندا ھوا، ۽ پڻ (جادوءَ کي اسماء الٰهيه ساڻ بيجا استعمال ڪرڻ جي پٺيان پيا) جيڪي (اسماء الٰهيه جا اسرار) ٻن ملائڪن ھاروت ۽ ماروت تي بابل (شھر) ۾ نازل ٿيا ھئا، ۽ اُھي ٻئي (انھن سڳورن علمن مان) ڪجھ به ايسين ڪنھن ھڪڙي کي نه سيکاريندا ھوا جيسين (ھي) چوندا ھئا ته اسين رڳو پرکه آھيون تنھنڪري (انکي بيجا ڪم ۾ آڻي) ڪافر نه بڻج (جو ناجائز ڪمن لاءِ اسماءِ الٰهي ڪم آڻڻ ڪفر آھي) پوءِ کانئن اُھو سکندا هُئا جنھن سان مڙس ۽ سندس زال جي وچ ۾ ڦيٽو وجھندا هُئا، ۽ اُھي اُن سان ڪنھين ھڪڙي کي الله جي حڪم ڌاران ڏکوئيندڙ نه هُئا، جيڪا کين ڏکوئيندي ۽ فائدو نه ڏيندي سا سکندا هُئا، ۽ بيشڪ ڄاتائون ٿي ته جنھن اُھا ڳڌي تنھن کي آخرت ۾ ڪو نصيب نه آھي، ۽ جنھن سان پاڻ کي وڪيائون سا ضرور بڇڙي آھي، جيڪر ڄاڻن ھا (ته پاڻ پلين ھا).');
INSERT INTO "amroti" VALUES(2,103,'2:103','۽ جيڪڏھن اُھي (يھودي) ايمان آڻين ھا ۽ پرھيزگار بڻجن ھا ته الله وٽان ضرور چڱو بدلو (ملين ھا)، جيڪڏھن ڄاڻن ھا.');
INSERT INTO "amroti" VALUES(2,104,'2:104','اي ايمان وارؤ (پيغمبر کي عرض ڪرڻ مھل) راعِنا (جو لفظ) نه چئو ۽ اُنظرنا (جو لفظ) چئو ۽ (اھو حڪم دل سان) ٻڌو، ۽ ڪافرن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(2,105,'2:105','نڪي ڪو ڪتاب وارن ڪافرن مان ۽ نڪي ڪو مشرڪن مان اوھان جي پالڻھار کان اوھان تي ڪنھن چڱائيءَ جو لھڻ گھرندو آھي، ۽ الله پنھنجي ٻاجھ سان جنھن کي وڻيس تنھن کي چونڊيندو آھي، ۽ الله وڏي فضل وارو آھي.');
INSERT INTO "amroti" VALUES(2,106,'2:106','(اي پيغمبر) جي ڪا آيت منسوخ ڪندا آھيون يا (تنھنجي دل تان) اُھا وسارائيندا آھيون ته انھي کان چڱي يا جھڙيس آڻيندا آھيون. نه ڄاڻندو آھين ڇا ته الله سڀڪنھن شيء تي وس وارو آھي؟');
INSERT INTO "amroti" VALUES(2,107,'2:107','(اي پيغمبر) نه ڄاڻندو آھين ڇا ته آسمانن ۽ زمين جي بادشاھي الله جي آھي، ۽ الله کانسواءِ اوھان جو نڪو سنڀاليندڙ ۽ نڪو واھرو آھي؟');
INSERT INTO "amroti" VALUES(2,108,'2:108','(اي مسلمانؤ) اوھين پنھنجي پيغمبر کان ايئن سوال ڪرڻ ڇو ٿا گھرو جيئن اڳ (يھودين) موسىٰ کان پڇيو ھو؟ ۽ جيڪو ايمان کي ڪفر سان مٽائيندو سو بيشڪ سِڌيءَ واٽ کان ڀُلو.');
INSERT INTO "amroti" VALUES(2,109,'2:109','ڪتاب وارن مان گھڻا گھرندا آھن ته جيڪر اوھان جي ايمان آڻڻ کان پوءِ به اوھان کي ساڙ سببان جو سندين دلين ۾ آھي کين جو حق پڌرو ٿي چڪو آھي ان کانپوءِ به موٽائي ڪافر ڪن، پوءِ ايسين ٽري وڃو ۽ مُڙي وڃو جيسين الله پنھنجو حڪم موڪلي، ڇو ته الله سڀ ڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(2,110,'2:110','۽ (اي مسلمانؤ) نماز پڙھو ۽ زڪوٰت ڏيو، ۽ جيڪا چڱائي پاڻ لاءِ اڳي موڪليندؤ سا الله وٽ لھندؤ جيڪي اوھان ڪندا آھيو تنھن کي الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(2,111,'2:111','۽ (يھودي ۽ نصارىٰ) چون ٿا ته يھودين ۽ نصارن کانسواءِ بھشت ۾ ڪوئي نه گھڙندو. اِھي سندن سڌون آھن. (اي پيغمبر کين) چئو ته جيڪڏھن سچا آھيو ته پنھنجو ڪو دليل آڻيو.');
INSERT INTO "amroti" VALUES(2,112,'2:112','ھائو، جنھن پنھنجو مُنھن الله لاءِ نواڙيو ۽ اُھو ڀلارو ھوندو تنھن لاءِ سندس پالڻھار وٽ سندس اجر آھي، ۽ نڪي کين ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(2,113,'2:113','۽ يھودي چون ٿا ته نصارىٰ ڪنھن دين تي نه آھن، ۽ نصارىٰ چون ٿا ته يھودي ڪنھن دين تي نه آھن ۽ (حقيقت ڪري) اُھي ڪتاب (توريت ۽ انجيل) پڙھندا آھن، اھڙي طرح جيڪي نه ڄاڻندا آھن سي (به) سندن چوڻ جھڙو چوندا آھن. پر الله قيامت جي ڏينھن سندن وچ ۾ جنھن (ڳالھ) بابت جھڳڙو ڪندا آھن تنھن جو نبيرو ڪندو.');
INSERT INTO "amroti" VALUES(2,114,'2:114','۽ ڪير اُن کان وڌيڪ ظالم آھي جنھن الله جي مسجدين کي منجھس سندس نالي جي ياد ڪرڻ کان جھليو ۽ انھن (مسجدين) جي ڦٽائڻ ۾ ڪوشش ڪيائين؟ اھڙن کي انھن (مسجدين) ۾ ڊڄندڙ ٿي گھڙڻ ڌاران (ٻئي طرح گھڙڻ) نه جڳائيندو آھي، انھن لاءِ دُنيا ۾ خواري آھي ۽ انھن لاءِ آخرت ۾ وڏو عذاب آھي.');
INSERT INTO "amroti" VALUES(2,115,'2:115','۽ اوڀر ۽ اولھ الله جو آھي، پوءِ جاڏي پنھنجي مھاڙ کي ڦيرايو تاڏي الله جي سامھانئي آھي، ڇو ته الله (مھرباني ۾) ڪشادگي وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,116,'2:116','۽ (ڪافر) چون ٿا ته الله پنھنجي لاءِ اولاد ورتو آھي. (نه) اھو پاڪ آھي، بلڪ جيڪي آسمانن ۽ زمين ۾ آھي سو سندس (مِلڪ) آھي، مڙيئي سندس فرمانبردار آھن.');
INSERT INTO "amroti" VALUES(2,117,'2:117','آسمانن ۽ زمين جو ٺاھيندڙ آھي، ۽ جڏھن ڪوئي ڪم ڪندو آھي تڏھن رڳو چوندو آھي ته ٿيء ته ٿي پوندو آھي.');
INSERT INTO "amroti" VALUES(2,118,'2:118','۽ جيڪي نه ڄاڻندا آھن سي چوندا آھن ته الله اسان سان ڇو نه ڳالھائيندو آھي يا ڪا نشاني اسان وٽ ڇو نه ايندي آھي؟ اھڙي طرح جيڪي کانئن اڳ هُئا تن سندن چوڻ وانگر چيو، سندن دليون ھڪ جھڙيون آھن، بيشڪ انھيءَ قوم لاءِ نشانيون بيان ڪيون اٿئون جيڪي ويساھ رکندا آھن.');
INSERT INTO "amroti" VALUES(2,119,'2:119','(اي پيغمبر) بيشڪ اسان توکي سچ سان خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو ۽ (تون) دوزخين بابت نه پڇبين.');
INSERT INTO "amroti" VALUES(2,120,'2:120','۽ (اي پيغمبر) يھودي ايسين توکان ڪڏھن راضي نه ٿيندا ۽ نڪي نصارىٰ جيسين تون سندن دين جي تابعداري ڪرين، (کين) چؤ ته الله جي ھدايت اھا (اسلام) جي ھدايت آھي، ۽ (اي پيغمبر) تو وٽ جو علم آيو آھي تنھن کان پوءِ به جيڪڏھن (تون) سندن سَڌن جي تابعداري ڪندين ته تو لاءِ الله وٽان نڪو سنڀاليندڙ ۽ نڪو واھرو ٿيندو.');
INSERT INTO "amroti" VALUES(2,121,'2:121','جن کي ڪتاب ڏنوسون سي ان کي اھڙي طرح پڙھندا آھن جھڙو سندس پڙھڻ جو حق آھي، اھي ان کي مڃيندا آھن، ۽ جيڪي اُن کي نه مڃيندا سي ئي ڇيئي وارا آھن.');
INSERT INTO "amroti" VALUES(2,122,'2:122','اي بني اسرائيلؤ اُھي مُنھنجا ڳُڻ ياد ڪريو جيڪي مون اوھان تي ڪيا ۽ مون جھان (وارن) تي اوھان کي سڳورو ڪيو.');
INSERT INTO "amroti" VALUES(2,123,'2:123','۽ انھيءَ ڏينھن کا ڊڄو جنھن ۾ ڪو جيءُ ڪنھن جيءَ کان ڪجھ به نه ٽاري سگھندو ۽ نڪي کانئس ڪو بدلو قبول ڪبو ۽ نڪي کيس ڪا پارت نفعو ڏيندي ۽ نڪي کين مدد ڏبي.');
INSERT INTO "amroti" VALUES(2,124,'2:124','۽ جڏھن ابراھيمؑ کي سندس پالڻھار ڪن ڳالھين سان پرکيو پوءِ اُھي پوريون ڪيائين، ۽ (الله) فرمايو ته آءٌ توکي ماڻھن لاءِ امام مُقرّر ڪرڻ وارو آھيان، (تڏھن ابراھيم) چيو ته منھنجي اولاد مان به (امام ڪندين؟ الله) فرمايو ته ظالمن کي اھو منھنجو انجام نه پھچندو.');
INSERT INTO "amroti" VALUES(2,125,'2:125','۽ جڏھن بيت الله کي ماڻھن جي موٽڻ جو ماڳ ۽ امن جو ھنڌ مقرر ڪيوسون، ۽ (ھن امت کي حُڪم ڪيوسون) ته مقام ابراھيم کي (به) نماز پڙھڻ جي جاءِ ڪري وٺو، ۽ ابراھيم ۽ اسمٰعيل کي وصيّت ڪئي سون ته منھنجي گھر کي طواف ڪندڙن ۽ اعتڪاف وارن ۽ رڪوع (۽) سجدي ڪندڙن لاءِ پاڪ رکو.');
INSERT INTO "amroti" VALUES(2,126,'2:126','۽ جڏھن ابراھيم چيو ته اي منھنجا پالڻھار ھن شھر (مڪي) کي امن جو ھنڌ ڪر ۽ سندس رھندڙن مان انھن کي ميون جي روزي ڏي جيڪي منجھائن الله ۽ قيامت جي ڏينھن کي مڃين، (تڏھن الله) فرمايو ته جنھن نه مڃيو (تنھن کي به) پوءِ اُن کي (حياتيءَ جا) ٿورڙا ڏينھن آسودو ڪندس وري کيس باھ جي عذاب ڏانھن بيوس ڪندس، ۽ اُھا بُڇڙي جاءِ آھي.');
INSERT INTO "amroti" VALUES(2,127,'2:127','۽ جڏھن ابراھيم ۽ اسمٰعيل بيت (الله) جا بنياد اوساريندا هُئا (تڏھن چوندا هُئا ته) اي اسان جا پالڻھار (ھيءُ پورھيو) اسان کان قبول ڪر، ڇوته تون ئي ٻڌندڙ ڄاڻندڙ آھين.');
INSERT INTO "amroti" VALUES(2,128,'2:128','۽ اسان جا پالڻھار اسان کي پنھنجو فرمانبردار ڪر ۽ اسان جي اولاد مان (به) ھڪ ٽوليءَ کي پنھنجو فرمانبردار ڪر، ۽ اسان کي اسان جي حج جا ارڪان سيکار ۽ اسان جون (خطائون) معاف ڪر، ڇو ته تون ئي وڏو معاف ڪندڙ مھربان آھين.');
INSERT INTO "amroti" VALUES(2,129,'2:129','۽ اي اسان جا پالڻھار منجھانئن ھڪ پيغمبر منجھن موڪل جو تنھنجون آيتون مٿن پڙھي ۽ کين ڪتاب ۽ حڪمت سيکاري ۽ کين سڌاري، ڇو ته تون ئي غالب حڪمت وارو آھين.');
INSERT INTO "amroti" VALUES(2,130,'2:130','۽ جنھن پاڻ کي بي سمجھ ڪيو تنھن کانسواءِ (ٻيو) ڪير ابراھيم جي دين کان مُڙندو، ۽ بيشڪ کيس دنيا ۾ چونڊيو سون، ۽ اُھو آخرت ۾ ضرور ڀلارن مان آھي.');
INSERT INTO "amroti" VALUES(2,131,'2:131','جڏھن کيس سندس پالڻھار چيو ته منھنجا حڪم مڃ (تڏھن) چيائين ته جھان جي پالڻھار جا (سڀ) حڪم مڃيم.');
INSERT INTO "amroti" VALUES(2,132,'2:132','۽ ابراھيم ۽ يعقوب پنھنجي پٽن کي ان ڳالھ جي وصيت ڪئي، ته اي اسانجا اولاد (ھن) دين کي الله اوھان لاءِ چونڊيو آھي پوءِ اوھين مسلمان ھجڻ کانسواءِ نه مرجو.');
INSERT INTO "amroti" VALUES(2,133,'2:133','(اي ڪتاب وارؤ) اوھين تڏھن حاضر ھيؤ ڇا؟ جڏھن يعقوب کي موت ويجھو ٿيو جڏھن پنھنجن پٽن کي چيائين ته مون کان پوءِ ڪنھن جي عبادت ڪندؤ؟ (تڏھن) چيائون ته تنھنجي خُدا ۽ تنھنجي پيءُ ڏاڏي ابراھيم ۽ اسمٰعيل ۽ اسحٰق جي خدا جي جو اڪيلو خدا آھي عبادت ڪنداسون، ۽ اسين سندس حڪم مڃيندڙ آھيون.');
INSERT INTO "amroti" VALUES(2,134,'2:134','اھا ٽولي بيشڪ گذري وئي، جيڪي انھن ڪمايو سو انھن لاءِ آھي ۽ جيڪي اوھان ڪمايو سو اوھان لاءِ آھي ۽ جيڪي (اُھي) ڪندا تنھن بابت اوھين نه پڇبؤ.');
INSERT INTO "amroti" VALUES(2,135,'2:135','۽ (ڪتاب وارا مسلمانن کي) چون ٿا ته يھودي يا نصارىٰ ٿيو ته ھدايت وارا ٿيندؤ. (اي پيغمبر کين) چؤ ته اسين ابراھيم حنيف (ھڪ طرفي) جي دين جا تابعدار آھيون، ۽ اُھو مشرڪن مان نه ھو.');
INSERT INTO "amroti" VALUES(2,136,'2:136','(کين) چئو ته الله کي مڃيو اٿئون ۽ (پڻ) جيڪو (قرآن) اسان ڏانھن لاٿو ويو ۽ جيڪي (صحيفا) ابراھيم ۽ اسمٰعيل ۽ اسحٰق ۽ يعقوب ۽ يعقوب جي اولاد ڏانھن لاٿا ويا آھن ۽ جيڪي موسىٰ ۽ عيسىٰ کي ڏنو ويو ۽ جيڪي ٻـين پيغمبرن کي سندن پالڻھار کان ڏنو ويو، تنھن (سڀ کي مڃيون ٿا) منجھائن ڪنھن ھڪ جي وچ ۾ وِٿي نه ٿا ڀانيون، ۽ اسين اُن (ھڪ الله) کي مڃيندڙ آھيون.');
INSERT INTO "amroti" VALUES(2,137,'2:137','پوءِ جيڪڏھن الله کي اوھان جي مڃڻ جھڙو مڃين ته بيشڪ ھدايت وارا ٿيندا، ۽ جيڪڏھن ڦرندا ته اُھي ڦيٽي ۾ ئي پوندا، پوءِ الله توکي انھن (جي شر) کان ڪافي ٿيندو، ۽ اھو ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,138,'2:138','الله (پنھنجي دين جي رنگ سان) اسان کي رنڱيو آھي، ۽ رڱڻ ۾ الله کان وڌيڪ چڱو ڪير آھي؟ ۽ اسين سندس عبادت ڪندڙ آھيون.');
INSERT INTO "amroti" VALUES(2,139,'2:139','(اي پيغمبر کين) چؤ ته الله (جي دين) بابت اسان سان تڪرار ڪريو ٿا ڇا؟ ۽ (حقيقت ڪري) اُھو اسانجو پالڻھار ۽ اوھانجو پالڻھار آھي، ۽ اسان لاءِ اسانجا ڪم ۽ اوھان لاءِ اوھان جا ڪرتوت آھن، ۽ سندس نِلا ٻانھا آھيون.');
INSERT INTO "amroti" VALUES(2,140,'2:140','يا چئو ٿا ته ابراھيم ۽ اسمٰعيل ۽ اسحٰق ۽ يعقوب ۽ يعقوب جو اولاد يھودي يا نصارىٰ هُئا. (اي پيغمبر کين) چؤ ته اوھين وڌيڪ ڄاڻندڙ آھيو يا الله (وڌيڪ ڄاڻندڙ آھي)؟ ۽ جيڪو اھڙي شاھدي لڪائي جا الله وٽان وٽس ھجي تنھن کان وڌيڪ ظالم ڪير آھي؟ ۽ جيڪي ڪندا آھيو تنھن کان الله بيخبر نه آھي.');
INSERT INTO "amroti" VALUES(2,141,'2:141','اِھا ٽولي بيشڪ گذري وئي، جيڪي ڪمايائون سو انھن لاءِ آھي ۽ جيڪي اوھان ڪمايو سو اوھان لاءِ آھي، ۽ جيڪي اُھي ڪندا هُئا تنھن بابت (اوھين) نه پڇيا ويندؤ.');
INSERT INTO "amroti" VALUES(2,142,'2:142','ماڻھن منجھان ڪي بي سمجھ سگھو چوندا ته اُنھن (يعني مسلمانن) کي سندن اُھو قبلو جنھن ڏي (نماز پڙھندا) ھئا تنھن کان ڪھڙيءَ ڳالھ ڦيرايوَ؟ (اي پيغمبر کين) چؤ ته اوڀر ۽ اولھ الله جو آھي، جنھن کي گھرندو آھي تنھن کي سِڌيءَ واٽ ڏانھن دڳ ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(2,143,'2:143','۽ (جھڙي طرح اوھان جو قبلو ڀلو ڪيوسين) اھڙيءَ طرح اوھان کي ڀلي ٽولي ڪئي سون ته ماڻھن تي شاھد ھجو ۽ پيغمبر (محمد صلى الله عليه وسلم) اوھان تي شاھد ھجي، ۽ (اي پيغمبر) جنھن (طرف) تي (اڳي) ھئين تنھن کي قبلو مقرر نه ڪيو ھو سون مگر ھن لاءِ ته جيڪو پيغمبر جي پٺيان لڳي تنھن کي اُنھيءَ کان نکيڙيون جيڪو پنھنجين کڙين (ڀر پوءِ) تي ڦري، ۽ بيشڪ (اِھو) جن کي الله ھدايت ڪئي تن کان سواءِ (ٻـين) تي ضرور ڏکيو آھي، ۽ الله جو شان اھڙو نه آھي جو اوھان جي ايمان کي وڃائي، ڇو ته الله ماڻھن سان ضرور شفقت ڪندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(2,144,'2:144','(اي پيغمبر) آسمان ڏانھن تنھنجو منھن (ھرھر) ڦيرائڻ بيشڪ ڏسون ٿا، پوءِ جنھن قبلي کي پسند ڪرين ٿو (اوڏانھن) توکي ضرور ڦيرائينداسين، پوءِ (اي پيغمبر) پنھنجي مُنھن کي تعظيم واريءَ مسجد ڏانھن ڦيراءِ، ۽ (اي مسلمانؤ) اوھين جتي ھجو تتي پنھنجا منھن اوڏانھن ڦيرايو، ۽ جن کي ڪتاب ڏنو ويو آھي سي ضرور ڄاڻن ٿا ته اِھو (ڦيرائڻ) سندن پالڻھار وٽان بلڪل سچ آھي، ۽ جيڪي ڪندا آھن تنھن کان الله بي خبر نه آھي.');
INSERT INTO "amroti" VALUES(2,145,'2:145','۽ (قسم آھي) ته جن کي ڪتاب ڏنو ويو آھي تن وٽ جيڪڏھن (تون) سڀ دليل آڻين ته (به) تنھنجي قبلي جي تابعداري نه ڪندا، ۽ نڪي تون سندن قبلي جي تابعداري ڪندڙ آھين، ۽ نڪي اُنھن مان ڪي ڪن جي قبلي جي تابعداري ڪندڙ آھن، ۽ (قسم آھي) ته ان کانپوءِ جو تو وٽ علم آيو جيڪڏھن سندن سَڌن جي تابعداري ڪندين ته تون اُن مھل ظالمن مان آھين.');
INSERT INTO "amroti" VALUES(2,146,'2:146','جن کي ڪتاب (توريت) ڏنوسون سي اُن (قبلي جي ڦير) کي اِئين سڃاڻندا آھن جئن پنھنجن پٽن کي سڃاڻندا آھن، ۽ منجھانئن ڪا ٽولي ڄاڻ ھوندي بيشڪ سچ کي لڪائيندي آھي.');
INSERT INTO "amroti" VALUES(2,147,'2:147','اِھو تنھنجي پالڻھار وٽان سچ آھي تنھنڪري تون شڪ وارن مان بنھ نه ٿيءُ.');
INSERT INTO "amroti" VALUES(2,148,'2:148','۽ سڀ ڪنھن لاءِ (ڪو) ھڪڙو قبلو آھي جنھن ڏانھن اُھو مھاڙ ڪندو آھي پوءِ چڱاين ڏانھن اڳرائي ڪريو، جتي ھوندؤ (اُتان) الله اُوھان کي گڏ ڪري آڻيندو، ڇوته الله سڀ ڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(2,149,'2:149','۽ (اي پيغمبر) جتان (به) نڪرين ته پنھنجو منھن تعظيم واريءَ مسجد ڏانھن ڦيراءِ، ۽ بيشڪ اِھو (ڦيرائڻ) تنھنجي پالڻھار وٽان بلڪل سچ آھي، ۽ جيڪي ڪندا آھيو تنھن کان الله بي خبر نه آھي.');
INSERT INTO "amroti" VALUES(2,150,'2:150','۽ جتان (به) نڪرين ته پنھنجو منھن تعظيم واريءَ مسجد ڏانھن ڦيراءِ، ۽ جتي ھجو (تتي) پنھنجا منھن ان ڏانھن ڦيرايو (ھن لاءِ) ته منجھانئن جن ظلم ڪيو تن کان سواءِ ٻـين ماڻھن کي اوھان تي ڪا حجت نه رھي، پوءِ کانئن نه ڊڄو، ۽ (ھن لاءِ) ته (آءٌ) پنھنجي نعمت اوھان تي پوري ڪريان ۽ مانَ اوھين ھدايت وارا ٿيو.');
INSERT INTO "amroti" VALUES(2,151,'2:151','جھڙيءَ طرح اوھان تي نعمت پوري ڪئي سون (تھڙيءَ طرح) اوھان منجھان پيغمبر اوھان ۾ موڪليوسون جو اسان جون آيتون اوھان تي پڙھندڙ آھي ۽ اوھان کي پاڪ ڪندو آھي ۽ اوھان کي ڪتاب ۽ حڪمت سيکاريندو آھي ۽ جيڪي نه ڄاڻندا ھيؤ سو (پڻ) اوھان کي سيکاريندو آھي.');
INSERT INTO "amroti" VALUES(2,152,'2:152','تنھنڪري مون کي ياد ڪريو ته آءٌ اوھان کي ياد ڪريان ۽ منھن جو شڪر ڪريو ۽ منھن جي بي شڪري نه ڪريو.');
INSERT INTO "amroti" VALUES(2,153,'2:153','اي مؤمنؤ صبر ۽ نماز سان مدد وٺو، ڇوته الله صابرن سان آھي.');
INSERT INTO "amroti" VALUES(2,154,'2:154','۽ جيڪي الله جي واٽ ۾ ڪُسن تن کي مئل نه چئو، بلڪ (اُھي) جيئرا آھن پر اوھين نه ٿا پروڙيو.');
INSERT INTO "amroti" VALUES(2,155,'2:155','۽ اوھان کي ڀوَ ۽ بک ۽ مالن جي ۽ جندن جي ۽ ميون جي گھٽتائيءَ مان ڪنھن شيء سان ضرور پرکينداسون، ۽ (اي پيغمبر) اُنھن صابرن کي خُوشخبري ڏي.');
INSERT INTO "amroti" VALUES(2,156,'2:156','جن کي جڏھن ڪا اوکائي پھچي ٿي (تڏھن) چون ٿا ته اسين الله جا (ٻانھا) آھيون ۽ اسين ان ڏانھن موٽندڙ آھيون.');
INSERT INTO "amroti" VALUES(2,157,'2:157','اُنھن تي سندن پالڻھار وٽان مھربانيون ۽ ٻاجھ آھي، ۽ اِھي ئي سڌيءَ واٽ وارا آھن.');
INSERT INTO "amroti" VALUES(2,158,'2:158','صفا ۽ مروہ (جبل) الله (جي عبادت) جي نشانين مان آھن، پوءِ جيڪو بيت الله جو حج ڪري يا عمرو بجاءِ آڻي تنھن کي ٻنھي جي طواف ڪرڻ ۾ گناھ نه آھي، ۽ جيڪو وڌيڪ چڱائي ڪندو (تنھن جي الله قبول ڪندو) ڇو ته الله قبول ڪندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,159,'2:159','جيڪي پڌريون ڳالھيون ۽ ھدايتون لاٿيون اٿئون تن کي جيڪي (ماڻھو) اُن کانپوءِ جي اُنھن کي ماڻھن لاءِ ڪتاب (توريت) ۾ ظاھر ڪيوسون، لڪائيندا آھن تن تي الله لعنت ڪندو آھي ۽ لعنت ڪندڙ (به) انھن تي لعنت ڪندا آھن.');
INSERT INTO "amroti" VALUES(2,160,'2:160','پر جن توبه ڪئي ۽ (پاڻ) سڌاريائون ۽ بيان ڪيائون تن تي (ٻاجھ سان) موٽندس، ۽ آءٌ ڏاڍو ٻاجھ سان موٽندڙ مھربان آھيان.');
INSERT INTO "amroti" VALUES(2,161,'2:161','جن ڪفر ڪيو ۽ اُھي ڪافر ٿي مُئا تن تي الله ۽ ملائڪن ۽ مڙني ماڻھن جي لعنت آھي.');
INSERT INTO "amroti" VALUES(2,162,'2:162','اُن لعنت ۾ سدائين رھڻ وارا آھن، کانئن عذاب ھلڪو نه ڪبو ۽ نڪي کين مھلت ڏبي.');
INSERT INTO "amroti" VALUES(2,163,'2:163','۽ اوھان جو خدا اڪيلو خدا آھي، ان کانسواءِ ڪوئي عبادت جو لائق نه آھي، (اُھو) ٻاجھارو مھربان آھي.');
INSERT INTO "amroti" VALUES(2,164,'2:164','آسمانن ۽ زمين جي بڻائڻ (۾)، ۽ رات ۽ ڏينھن جي ڦيرگھير (۾)، ۽ ٻيڙيون جي اُنھيءَ (سامان) سان جو ماڻھن کي نفعو ڏيندو آھي درياءَ ۾ ترنديون آھن تن (۾)، ۽ جيڪي آسمان کان (مينھن جو) پاڻي الله وسايو پوءِ اُن سان زمين کي ان جي وسامڻ کانپوءِ آباد ڪيائين تنھن (۾)، ۽ اُن (زمين) ۾ سڀ جنس جا جانور پکيڙيائين تنھن (۾)، ۽ ھوائن جي گھلڻ (۾)، ۽ آسمانن ۽ زمين جي وچ ۾ نوايلن ڪڪرن ۾، سمجھ واريءَ قوم لاءِ ضرور نشانيون آھن.');
INSERT INTO "amroti" VALUES(2,165,'2:165','۽ ماڻھن مان ڪي (اھڙا) آھن جي الله کانسواءِ (ٻـين کي) الله جھڙو ڪري وٺندا آھن الله جي پيار وانگر پيارو رکندا اٿن، ۽ جن ايمان آندو سي الله جي دوستيءَ ۾ ڏاڍا زور آھن، ۽ جن ظلم ڪيو سي جيڪڏھن (ھن ڳالھ کي ھاڻي) سمجھن ته جنھن مھل (قيامت جي ڏينھن) عذاب ڏسندا (۽ سمجھندا) ته سموري سگھ الله کي آھي ۽ الله سخت عذاب ڪرڻ وارو آھي (ته جيڪر ڏاڍا پشيمان ٿين).');
INSERT INTO "amroti" VALUES(2,166,'2:166','جنھن مھل اڳواڻ پٺيان لڳندڙن کان بيزار ٿيندا ۽ عذاب کي ڏسندا ۽ سندن (سڀ) لاڳاپا ڇڄندا،');
INSERT INTO "amroti" VALUES(2,167,'2:167','۽ (تنھن مھل) پٺيءَ لڳندڙ چوندا ته جيڪر اسان کي (وري دنيا ۾) موٽڻو ھجي ھا ته کانئن ائين بيزار ٿيون ھا جئن اسان کان بيزار ٿيا آھن، اھڙي طرح الله سندن ڪرتوتن کي مٿن پشيمانيون بڻائي ڏيکاريندو، ۽ اُھي باھ مان نڪرڻ وارا نه آھن.');
INSERT INTO "amroti" VALUES(2,168,'2:168','اي ماڻھؤ جيڪي شيون زمين ۾ آھن تن مان پاڪ حلال کائو، ۽ شيطان جي وکن تي نه ھلو، ڇوته اُھو اوھان جو پڌرو ويري آھي.');
INSERT INTO "amroti" VALUES(2,169,'2:169','(شيطان) رڳو اوھان کي بڇڙائيءَ ۽ بي حيائيءَ جو حڪم ڪري ٿو ۽ ھيءُ ته جيڪي ڳالھيون نه ڄاڻندا آھيو سي الله جي نسبت ۾ چئو.');
INSERT INTO "amroti" VALUES(2,170,'2:170','۽ جڏھن کين چئجي ٿو ته جيڪي الله لاٿو تنھن جي تابعداري ڪريو (تڏھن) چون ٿا ته (نه) بلڪ جنھن تي پنھنجن پئن ڏاڏن کي ڏٺوسون تنھن جي پٺيان لڳنداسون، جيتوڻيڪ سندن ابا ڏاڏا ڪجھ به نه ڄاڻندا هُئا ۽ نڪي سڌيءَ واٽ تي هُئا.');
INSERT INTO "amroti" VALUES(2,171,'2:171','۽ ڪافرن (کي سڏيندڙ) جو مثال اُن ماڻھوءَ جي مثال وانگر آھي جو اھڙِيءَ شي کي واڪو ڪري ٻڌائي جا سڏ ۽ ڪوڪ کانسواءِ (ڪجھ) نه ٻڌي. (ڪافر) ٻوڙا، گونگا، انڌا آھن تنھنڪري اُھي (ڪجھ به) نه ٿا سمجھن.');
INSERT INTO "amroti" VALUES(2,172,'2:172','اي مؤمنؤ جيڪا اوھان کي روزي ڏني سون تنھن مان سٺيون شيون کائو ۽ جيڪڏھن اوھين نلو الله جي عبادت ڪندڙ آھيو ته سندس شڪرانو ادا ڪريو.');
INSERT INTO "amroti" VALUES(2,173,'2:173','اوھان تي رڳو مُئل ۽ رَت ۽ سُوئر جو ماس ۽ جنھن شي تي الله کانسواءِ ٻئي جو نالو پڪارجي سا حرام ڪيائين، پوءِ جيڪو نڪي بي فرماني ڪندڙ ۽ نڪي حد کان لنگھندڙ ٿي لاچار ٿئي (۽ کائي) تنھن تي گناہ نه آھي، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(2,174,'2:174','الله ڪتاب مان جيڪي لاٿو سو جيڪي ماڻھو لڪائيندا آھن ۽ اُن جي بدلي ٿورو ملھ وٺندا آھن اِھي پنھنجن پيٽن ۾ باھ کانسواءِ نه کائيندا آھن ۽ الله ساڻن قيامت جي ڏينھن نه ڳالھائيندو ۽ نڪي کين سنواريندو، ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(2,175,'2:175','اِھي (اُھي) آھن جن ھدايت جي بدران گمراھي ۽ بخشش جي بدران عذاب ڳڌو، پوءِ عجب آھي ته کين (دوزخ جي) باہ تي ڪھڙيءَ (ڳالھ) صبر ڪرايو.');
INSERT INTO "amroti" VALUES(2,176,'2:176','اِھو ھِن ڪري (آھي) جو الله سچ سان ڪتاب (توريت) لاٿو، ۽ جن ڪتاب بابت تڪرار ڪيو سي ضرور وڏي ڦيٽي ۾ آھن.');
INSERT INTO "amroti" VALUES(2,177,'2:177','اوڀر ۽ اولھ ڏانھن اوھان جو پنھنجن مُنھَن کي ڦيرائڻ ڪا چڱائي نه آھي پر (اصل) چڱائي (ان لاءِ) آھي جنھن الله ۽ قيامت جي ڏينھن ۽ ملائڪن ۽ ڪتابن ۽ پيغمبرن کي مڃيو، ۽ جنھن (ماڻھوءَ پنھنجو) مال اُن جي پيار ھوندي (به) مٽيءَ وارن ۽ ڇورن ۽ مسڪينن ۽ مسافرن ۽ سوال ڪندڙن کي ۽ ٻانھَن جي گردنين (آجي ڪرڻ) ۾ ڏنو، ۽ نماز پڙھيائين ۽ زڪوٰة ڏنائين، ۽ (اُھي ماڻھو جي) جڏھن انجام ڪندا آھن تڏھن پنھنجو انجام پاڙيندا آھن، ۽ سڃائيءَ ۽ اوکائيءَ ۾ ۽ جنگ مھل صبر ڪرڻ وارا آھن، اِھي سچا آھن، ۽ اِھي ئي پرھيزگار آھن.');
INSERT INTO "amroti" VALUES(2,178,'2:178','اي مؤمنؤ خون ٿيلن بابت اوھان تي بدلي وٺڻ ۾ برابري لازم ڪئي ويئي، (جئن ته) آجي جي عيوض آجو ٻانھي جي عيوض ٻانھو ۽ زال جي عيوض زال آھي، پوءِ جنھن کي سندس ڀاءُ جي طرفان ڪجھ ڇڏيو وڃي ته پوءِ (خون بھا وٺڻ ۾) چڱيءَ ريت سان (ان جي) پويان پوڻ گھرجي ۽ ان (وارث) ڏانھن (خون جي بھا) چڱيءَ ريت سان پھچائڻ گھرجي، اِھو (حڪم) اوھان جي پالڻھار وٽان ھلڪائي ۽ ٻاجھ آھي، پوءِ جيڪو ھن (حڪم) کانپوءِ حد کان لنگھندو تنھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(2,179,'2:179','اي ڌيان وارؤ اوھان لاءِ قصاص (وٺڻ) ۾ حياتي آھي ته مانَ اوھين پرھيزگاري ڪريو.');
INSERT INTO "amroti" VALUES(2,180,'2:180','جڏھن اوھان مان (ڪنھن) ھڪ کي موت ويجھو ٿئي (اُھو) جيڪڏھن (ڪُجھ) مال ڇڏي ته تڏھن اوھان تي ماءُ پيءُ ۽ مِٽن لاءِ چڱائيءَ سان وصيّت ڪرڻ لازم ڪئي ويئي، اِھو پرھيزگارن تي لازم آھي.');
INSERT INTO "amroti" VALUES(2,181,'2:181','پوءِ جيڪو اُن (وصيّت) کي اُن جي ٻڌڻ کانپوءِ ڦيرائيندو ته اُن (ڦيرائڻ) جو گُناہ اُن جي ڦيرائيندڙن کانسواءِ ٻـين تي نه آھي، ڇوته الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,182,'2:182','پوءِ جيڪو وصيّت ڪندڙ جي طرفداري يا گناہ ڄاڻي سو انھن (وصيّت ڪيل ۽ وارثن) جي وچ ۾ پرچاءُ ڪري ته مٿس گناہ نه آھي، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(2,183,'2:183','اي مؤمنؤ جئن اوھان کان اڳين تي (روزا) فرض ڪيا ويا ھئا تئن اوھان تي (به) روزا فرض ڪيا ويا آھن ته مانَ اوھين پرھيزگاري ڪريو.');
INSERT INTO "amroti" VALUES(2,184,'2:184','(روزا) ڳڻيل ڏينھن آھن، پوءِ اوھان مان جيڪو بيمار (ھجي) يا مسافريءَ تي ھجي (تنھن تي) ٻـين ڏينھن مان ڳڻي رکڻ (لازم) آھي، ۽ جن کي (مسڪين جي کاڌي ڏيڻ جي) سگھ آھي تن تي ھڪ مسڪين جو کاڌو فديه (يعني فطرانو ڏيڻ) لازم آھي، پوءِ جيڪو چڱائي وڌيڪ ڪندو تنھن لاءِ اُھا ڀلي آھي، ۽ جيڪڏھن عِلم وارا آھيو (ته اوھين سمجھي چڪا ھوندؤ) اوھان جو روزو رکڻ اوھان لاءِ ڀلو آھي.');
INSERT INTO "amroti" VALUES(2,185,'2:185','رمضان جو مھينو اھڙو (ڀلو) آھي جنھن ۾ قرآن لاٿو ويو جو ماڻھن لاءِ سڌو رستو ڏيکاريندڙ آھي ۽ (اُن ۾) ھدايت ڪرڻ ۽ (سچ ۽ ڪوڙ جي وچ ۾) سَنڌي وجھڻ جون چٽيون ڳالھيون آھن، پوءِ اوھان مان جيڪو اُنھيءَ مھيني ۾ (گھر ۾) حاضر ھجي تنھن کي اُن ۾ روزو رکڻ گهرجي، ۽ جيڪو بيمار ھُجي يا سفر تي (ھُجي) تنھن کي ٻـين ڏينھن مان (اوترو) ڳاڻاٽو (لازم) آھي. الله اوھان تي آساني ڪرڻ گھرندو آھي ۽ اوھان تي (ڪا) اوکائي ڪرڻ نه گھرندو آھي، ۽ (گھرندو آھي) ته اوھين ڳَڻ پوري ڪريو ۽ جيڪا اوھان کي ھدايت ڪيائين تنھن تي الله کي وڏائيندا رھو ۽ مانَ اوھين شُڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(2,186,'2:186','۽ (اي پيغمبر) جڏھن مُنھنجا ٻانھا توکان مُنھنجي پُڇا ڪن (تڏھن چؤ) ته آءٌ ويجھو آھيان، جڏھن (ڪو) مُون کي سڏي ٿو تڏھن سڏيندڙ جو سڏ ورنايان ٿو تنھنڪري مُنھنجو حُڪم قبول ڪرڻ گھرجين ۽ مون تي ايمان آڻڻ گھرجين ته مانَ اُھي سُڌرن.');
INSERT INTO "amroti" VALUES(2,187,'2:187','روزي جي رات ۾ اوھان لاءِ پنھنجين زالن سان صُحبت ڪرڻ حلال ڪئي ويئي، (جو) اُھي اوھان جو ڍڪ آھن ۽ اوھين اُنھن جو ڍڪ آھيو. الله ڄاتو ته اوھين پاڻ سان خيانت ڪندا ھيؤ پوءِ اوھان تي (ٻاجھ سان) موٽيو ۽ اوھان کي معاف ڪيائين، پوءِ ھاڻي ساڻن صُحبت ڪريو ۽ الله اوھان لاءِ جيڪي (اولاد) لکيو آھي تنھن جي طلب ڪريو ۽ (ايستائين) کائو ۽ پيئو جيستائين ڪاري ڌاڳي کان باکه جو اڇو ڌاڳو اوھان لاءِ پڌرو ٿئي، پوءِ روزو رات توڻي پورو ڪريو، ۽ جنھن حالت ۾ اوھين مسجدين ۾ اعتڪاف وارا ھجو ته اُنھن (زالن) سان صُحبت نه ڪريو. اِھي الله جون حدون آھن پوءِ اُنھن کي ويجھا نه وڃو، اھڙيءَ طرح الله ماڻھن لاءِ پنھنجا حُڪم پڌرا ڪندو آھي ته مانَ اُھي پرھيزگاري ڪن.');
INSERT INTO "amroti" VALUES(2,188,'2:188','۽ اوھين پنھنجا مال پاڻ ۾ ناحق سان نه کائو ۽ اُن (مال) کي حاڪمن ڏانھن ھن لاءِ (وڍيءَ طور) نه پھچايو ته ماڻھن جي مال مان ڪجھ (ڀاڱو) ڏوھ سان کائو ھن ھوندي به جو اوھين ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(2,189,'2:189','(اي پيغمبر) توکان چنڊن (يعني مھينن) بابت پڇن ٿا، چؤ ته اِھي ماڻھن ۽ حج لاءِ وقت مُقرر ٿيل آھن، ۽ چڱائي (ھن ۾) نه آھي ته (احرام جي حالت ۾) گھرن ۾ سندن پٺين پاسن کان اچو پر (اصل) چڱائي (اُنھيءَ لاءِ) آھي جيڪو (الله کان) ڊڄي، ۽ گھرن ۾ سندن درن کان اچو، ۽ الله کان ڊڄو ته مانَ اوھين ڇٽو.');
INSERT INTO "amroti" VALUES(2,190,'2:190','جيڪي اوھان سان وڙھن تن سان الله جي واٽ ۾ وڙھو ۽ حد کان نه لنگھو، ڇوته الله حد کان لنگھندڙن کي پيارو نه رکندو آھي.');
INSERT INTO "amroti" VALUES(2,191,'2:191','۽ جتي لَھونِ اتي ڪُھونِ ۽ جتان اوھان کي لوڌيائون اُتان لوڌيونِ ۽ فتنو (يعني شرڪ) ويڙھ کان ڏاڍو (بڇڙو) آھي، ۽ اوھين تعظيم واريءَ مسجد وٽ (ايسين) نه وڙھونِ جيسين منجھس اوھان سان (نه) وڙھن. پوءِ جيڪڏھن اوھان سان وڙھن ته اُنھن کي ماريو، ڪافرن جي سزا اِئين آھي.');
INSERT INTO "amroti" VALUES(2,192,'2:192','۽ جيڪڏھن بس ڪن (ته اوھين به بس ڪريو ڇو) ته الله بخشڻھار مھربا ن آھي.');
INSERT INTO "amroti" VALUES(2,193,'2:193','۽ ايستائين اُنھن سان وڙھو جيستائين فتنو (يعني شرڪ) نه رھي ۽ خاص الله جو دين ٿئي، پوءِ جيڪڏھن بس ڪن ته ظالمن کانسواءِ ڪنھين تي وڌيڪ ھلت ڪرڻ نه جڳائي.');
INSERT INTO "amroti" VALUES(2,194,'2:194','تعظيم وارو مھينو تعظيم واري مھيني جي بدلي آھي ۽ تعظيمون (ھڪ ٻئي جون) آڙ پاڙ آھن، پوءِ جيڪو اوھان تي وڌيڪ ھلت ڪري تنھن تي اوتري وڌيڪ ھلت ڪريو جيتري اوھان تي وڌيڪ ھلت ڪيائين، ۽ الله کان ڊڄو ۽ ڄاڻو ته الله پرھيزگارن ساڻ آھي.');
INSERT INTO "amroti" VALUES(2,195,'2:195','۽ الله جي واٽ ۾ خرچيو ۽ (پاڻ کي) پنھنجي ھٿن سان ھلاڪت ۾ نه وجھو، ۽ چڱائي ڪريو، ڇوته الله چڱائي ڪندڙن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(2,196,'2:196','۽ حج ۽ عُمرو الله (جي راضپي) لاءِ پورو ڪريو، پوءِ جيڪڏھن روڪيا وڃو ته جا قرباني آسانيءَ سان لڀي (سا لازم آھي)، ۽ پنھنجا مٿا تيسين نه ڪوڙايو جيسين قرباني پنھنجيءَ جاءِ تي (نه) پھچي. پوءِ اوھان مان جيڪو بيمار ھجي يا سندس مٿي ۾ اھنج ھجي (ته جي وقت کان اڳيئي ئي ڪوڙايائين) ته روزا يا خيرات يا قرباني عوض ڏيڻ (لازم) آھي، پوءِ جڏھن بي ڀَوَا ٿيو تڏھن جيڪو فائدو وٺي (يعني) عمري کي حج سان (گڏي) سو جا قرباني آسانيءَ سان لڀي (سا ڪري). پوءِ جيڪو (قرباني) نه لھي (تنھن تي) حج (جي ڏينھن) ۾ ٽي ڏينھن روزا لازم آھن ۽ ست تڏھن جڏھن (سفرکان) موٽو، اِھي پورا ڏھ (روزا) آھن، اِھو (حُڪم) اُنھيءَ لاءِ آھي جنھنجو ڪٽنب تعظيم واريءَ مسجد جي ويجھو رھندڙ نه ھجي، ۽ الله کان ڊڄو ۽ ڄاڻو ته الله ڏاڍو عذاب ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(2,197,'2:197','حج (جو وقت) معلوم مھينا آھن، پوءِ جنھن منجھن (احرام ٻڌڻ سان پاڻتي) حج فرض ڪيو ته حج ۾ نه جماع جائز آھي ۽ نه گار ۽ نه جھيڙو، ۽ جيڪا چڱائي ڪريو ٿا تنھن کي الله ڄاڻندڙ آھي، ۽ سَمر کڻو ڇو ته ڀلو سَمر پرھيزگاري آھي، ۽ ڌيان وارؤ مون کان ڊڄو.');
INSERT INTO "amroti" VALUES(2,198,'2:198','(حج ۾) پنھنجي پالڻھار کان روزيءَ گھرڻ ۾ اوھان تي ڪو گناھ ڪونھي، پوءِ جڏھن عَرفات کان موٽو تڏھن مَشۡعَرالۡحَرام (يعني مزدلفہ) وٽ الله کي ياد ڪريو، ۽ جئن اوھان کي سڌو رستو ڏيکاريائين تئن اوھين (به) کيس ياد ڪريو، ۽ بيشڪ اُن کان اڳ (اوھين) اڻڄاڻن مان ھيؤ.');
INSERT INTO "amroti" VALUES(2,199,'2:199','پوءِ (اوھين) اُتان (عرفات کان) موٽو جتان عام ماڻھو موٽن ٿا ۽ الله کان بخشش گھرو، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(2,200,'2:200','پوءِ جڏھن پنھنجي حج جا اَرڪان پُورا ڪريو تڏھن (مِنىٰ ۾) اوھين پنھنجن اَبن ڏاڏن جي ياد ڪرڻ وانگر بلڪ (اُن کان وڌيڪ) گھڻي قدر الله کي ياد ڪريو. پوءِ ماڻھن مان ڪي (اھڙا) آھن جي چوندا آھن ته اسان جا پالڻھار اسان کي دنيا ۾ (ئي چڱائي) ڏي تن کي آخرت ۾ (ڪا) چڱائي نصيب نه آھي.');
INSERT INTO "amroti" VALUES(2,201,'2:201','۽ منجھائن ڪي (اھڙا) آھن جي چوندا آھن ته اي اسان جا پالڻھار اسان کي دنيا ۾ (به) چڱائي ۽ آخرت ۾ (به) چڱائي ڏي ۽ اسان کي باھ جي عذاب کان بچاءِ.');
INSERT INTO "amroti" VALUES(2,202,'2:202','اُھي ماڻھو جيڪي ڪمايائون تنھن مان کين نصيب آھي، ۽ الله سگھو حساب ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(2,203,'2:203','۽ ڳڻيلن ڏينھن (مِنىٰ وارن) ۾ الله کي ياد ڪريو، پوءِ جيڪو ٻن ڏينھن ۾ تڪڙو نڪري تنھن تي گناھ ڪونھي، ۽ جيڪو دير ڪري تنھن تي (به) گناھ نه آھي (اھو حُڪم) اُن لاءِ آھي جيڪو پرھيزگاري ڪري، ۽ الله کان ڊڄو ۽ ڄاڻو ته اوھين ڏانھس گڏ ڪيا ويندؤ.');
INSERT INTO "amroti" VALUES(2,204,'2:204','۽ ماڻھن مان ڪو (اِھڙو) آھي جو سندس ڳالھائڻ دنيا جي حياتيءَ ۾ توکي عجب ۾ وجھندو آھي ۽ جيڪي سندس دل ۾ آھي تنھن تي الله کي شاھد ڪندو آھي ھن ھوندي به جو اُھو وڏو جھيڙاگر آھي.');
INSERT INTO "amroti" VALUES(2,205,'2:205','۽ جڏھن توکان موٽندو آھي تڏھن ملڪ ۾ ھن لاءِ ڪوشش ڪندو آھي ته منجھس فساد وجھي ۽ پوک ۽ نسل ناس ڪري، ۽ الله فساد کي پسند نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(2,206,'2:206','۽ جڏھن اُن کي چئبو آھي ته الله کان ڊڄ تڏھن وڏائي کيس گناہ تي آڪڙائيندي آھي پوءِ کيس (سزا) دوزخ بس آھي، ۽ اُھو ضرور بڇڙو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(2,207,'2:207','ماڻھن مان ڪو اھڙو آھي جو الله جي راضپي حاصل ڪرڻ لاءِ پاڻ کي وڪڻندو آھي، ۽ الله ٻانھن تي مھربان آھي.');
INSERT INTO "amroti" VALUES(2,208,'2:208','اي ايمان وارؤ پورا اسلام ۾ داخل ٿيو، ۽ شيطان جي وِکن تي نه ھلو، ڇوته اُھو اوھان جو پڌرو ويري آھي.');
INSERT INTO "amroti" VALUES(2,209,'2:209','پوءِ جيڪڏھن اوھين ان کانپوءِ جو اوھان وٽ چٽا دليل آيا (اِسلام کان) ٿڙندؤ ته ڄاڻو ته الله غالِب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(2,210,'2:210','(ڪافر) ھن کان سواءِ انتظار نه ڪندا آھن ته الله (جو عذاب) جُھڙ جي پاڇن ۾ وٽن اچي ۽ ملائڪ (به) ۽ ڪم فيصل ڪيو وڃي، ۽ الله ڏانھن سڀ ڪم موٽايا ويندا آھن.');
INSERT INTO "amroti" VALUES(2,211,'2:211','(اي پيغمبر) بني اِسرائيلن کان پُڇا ڪر ته کين ڪيتريون چٽيون نشانيون ڏنيون سين، ۽ جيڪي الله جي نعمت کي پاڻ وٽ اچڻ کانپوءِ مٽائيندو ته بيشڪ الله سخت عذاب ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(2,212,'2:212','ڪافرن لاءِ دُنيا جي حياتي سينگاري ويئي ۽ مؤمنن سان ٺـٺوليون ڪندا آھن، ۽ جيڪي پرھيزگار آھن سي قيامت جي ڏينھن اُنھن (ڪافرن) جي مٿئون ھوندا، ۽ الله جنھن کي گھرندو آھي تنھن کي اَڻ ڳڻي روزي ڏيندو آھي.');
INSERT INTO "amroti" VALUES(2,213,'2:213','سڀ ماڻھو ھڪ ٽولي هُئا، پوءِ (ڦري اِختلاف ۾ پيا تنھنڪري) الله پيغمبرن کي خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو، ۽ ساڻن سچ سان ڪتاب ھن لاءِ لاٿائين ته (اُھو ڪتاب) ماڻھن جي وچ ۾ جنھن (ڳالھ) بابت جھڳڙو ڪندا آھن (تنھن بابت) نبيرو ڪري، ۽ اُنھيءَ بابت (ٻئي ڪنھن به) تڪرار نه ڪيو پر جن کي اُھو (ڪتاب) ڏنو ويو تن پڌرن حڪمن اچڻ کانپوءِ پاڻ ۾ ساڙ پيل سببان (تڪرار ڪيو)، پوءِ الله اُھا سچي ڳالھ جنھن بابت جھڳڙو ڪيائون تنھن ڏانھن پنھنجي حُڪم سان مؤمنن کي سڌو رستو ڏيکاريو، ۽ الله جنھن کي گھرندو آھي تنھن کي سڌو رستو ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(2,214,'2:214','(اي مسلمانؤ اوھين) ھن ھوندي به بھشت ۾ گھڙڻ جو گمان ڪندا آھيو ڇا؟ جو اڃان اوھان کي اُنھن جي حالت نه آئي آھي جيڪي اوھان کان اڳ گذريا، جن کي سڃائي ۽ ڏُک پُھتو ۽ ايستائين لوڌيا ويا جيستائين پيغمبر ۽ جي مؤمن ساڻس هُئا (تن دُعا گُھري) چيو ٿي ته الله جي مدد ڪڏھن پھچندي؟ (چيو وين ته) ھوشيار رھو ته الله جي مدد ويجھي آھي.');
INSERT INTO "amroti" VALUES(2,215,'2:215','(اي پيغمبر) توکان پڇن ٿا ته (اُھي) ڇا خرچين؟ (کين) چؤ ته جيڪو مال خرچي سو ماءُ پيءُ ۽ مائٽن ۽ ڇورن (ٻارن) ۽ مسڪينن ۽ مسافرن کي (ڏيڻ) جڳائي، ۽ جيڪا چڱائي ڪندؤ (سا لھندؤ) ڇوته الله اُنکي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,216,'2:216','(اي مؤمنؤ) اوھان تي (ڪافرن سان) ويڙھ ڪرڻ فرض ڪئي ويئي آھي ۽ اُھا اوھان کي ناخوش لڳندي آھي، ۽ شايد (اوھين) ڪنھن شي کي ناخوش ڀانيو ۽ (حقيقت ڪري) اُھا اوھان لاءِ ڀلي ھُجي، ۽ شايد ڪنھن شي کي پيارو رکو ۽ اُھا اوھان لاءِ بڇڙي ھجي، ۽ الله ڄاڻندو آھي ۽ اوھين نه ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(2,217,'2:217','(اي پيغمبر) توکان تعظيم وارن مھينن ۾ وڙھڻ بابت پُڇن ٿا، چؤ ته مَنجھن ويڙھ ڪرڻ وَڏو گناھ آھي، ۽ الله جي واٽ کان جھلڻ ۽ الله کي نه مڃڻ ۽ تعظيم واريءَ مسجد کان روڪڻ، ۽ سندس رھڻ وارن کي منجھائنس لوڌڻ الله وٽ (اُن کان) تمام وڏو گناھ آھي، ۽ فتنو (يعني شرڪ) خون ڪرڻ کان بلڪل وڏو ڏوھ آھي. ۽ (ڪافر) جيڪڏھن پڄي سگھندا ته اوھان سان ايستائين سدائين پيا وڙھندا جيستائين اوھان کي اوھان جي دين (اسلام) کان ڦيرائين، ۽ اوھان مان جيڪي پنھنجي دين کان ڦرندا پوءِ اُھي ڪافر ٿي مرندا تن جي ڪئي ڪمائي دُنيا ۽ آخرت ۾ ڪُٽ ٿي ويئي، ۽ اُھي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(2,218,'2:218','جن ايمان آندو ۽ جن الله جي واٽ ۾ وطن ڇڏيو ۽ جھاد ڪيو سي الله جي ٻاجھ جا اميدوار آھن، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(2,219,'2:219','(اي پيغمبر) توکان شراب ۽ جؤا بابت پڇن ٿا، چؤ ته اُنھن ۾ وڏو گناھ ۽ ماڻھن لاءِ (ڪجھ) فائدا آھن، ۽ سندن فائدي کان اُنھن جو گناھ تمام وڏو آھي. ۽ (پڻ) توکان پڇن ٿا ته (اُھي) ڇا خرچين، چؤ ته (گھرج کان) وڌيڪ (خرچيو) اھڙيءَ طرح الله اوھان لاءِ آيتون بيان ڪندو آھي ته مانَ اوھين ڌيان رکو.');
INSERT INTO "amroti" VALUES(2,220,'2:220','دنيا ۽ آخرت (جي ڪمن) ۾، ۽ (اي پيغمبر) توکان يتيمن بابت پڇن ٿا، چؤ ته اُنھن لاءِ سڌار ڀلي آھي، جيڪڏھن کين (کاڌي پيتي ۾ پاڻ سان) گڏيو ته اوھان جا ڀائر آھن، ۽ الله بگاڙيندڙ کي سڌاريندڙ کان ڄاڻندو آھي، ۽ جيڪڏھن الله گھري ھا ته اوھان کي سختيءَ ۾ وجھي ھا، ڇوته الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(2,221,'2:221','۽ (اي مسلمانؤ ايستائين) مشرڪياڻين کي نه پرڻيو جيستائين ايمان آڻين، ۽ بيشڪ مؤمنياڻي ٻانھي مشرڪياڻيءَ کان ڀلي آھي جيتوڻيڪ اوھان کي (مشرڪياڻي) زيادہ وڻي، ۽ اوھين (ايسين) مشرڪن کي نه پرڻايو جيسين ايمان آڻين، ۽ بيشڪ مؤمن ٻانھو مُشرڪ کان ڀلو آھي جيتوڻيڪ اوھان کي (مشرڪ) زيادہ وڻي، اِھي (مُشرڪ) باھ ڏانھن سڏيندا آھن، ۽ الله پنھنجي ارادي مُوجب بھشت ۽ بخشش ڏانھن سڏيندو آھي، ۽ پنھنجون آيتون ماڻھن لاءِ بيان ڪندو آھي ته مانَ اُھي نصيحت وٺن.');
INSERT INTO "amroti" VALUES(2,222,'2:222','۽ (اي پيغمبر) توکان حيض (جي حڪم) بابت پُڇن ٿا، چؤ ته اُھا پليتي آھي تنھنڪري زالن کان حيض ۾ پري ٿيو ۽ ايستائين کين ويجھا نه وڃو جيستائين پاڪ ٿين، پوءِ جڏھن بلڪل پاڪ ٿين تڏھن جنھن واٽ کان الله اوھان کي موڪل ڏني آھي (اُنھيءَ کان) اُنھن ڏانھن اچو، ڇوته الله توبه ڪندڙن کي دوست رکندو آھي ۽ (پڻ) پاڪائي ڪندڙن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(2,223,'2:223','اوھان جو زالون اوھان لاءِ پوک آھن، پوءِ پنھنجيءَ پوک ڏانھن جئن وڻيوَ تئن اچو، ۽ پاڻ لاءِ (چڱا ڪم) اڳي موڪليو، ۽ الله کان ڊڄو ۽ ڄاڻو ته اوھين ساڻس ملاقي ٿيندڙ آھيو، ۽ مُؤمنن کي خوشخبري ڏي.');
INSERT INTO "amroti" VALUES(2,224,'2:224','۽ اوھين پنھنجن قسمن لاءِ چڱائي ڪرڻ ۽ پرھيزگاري ڪرڻ ۽ ماڻھن جي وچ ۾ صُلح ڪرائڻ کان (پاسو ڪرڻ واسطي) الله جو اِسم پيش نه ڪريو، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,225,'2:225','الله اوھان کي اوھان جي اجاين قسمن کڻڻ ۾ نه وٺندو آھي پر اوھان جي دلين جيڪا نيت ڪئي آھي تنھن تي پڪڙيندو آھي، ۽ الله بخشڻھار بُردبار آھي.');
INSERT INTO "amroti" VALUES(2,226,'2:226','جيڪي پنھنجي زالن (جي صُحبت) کان قسم کڻن تن کي چار مھينا ترسڻو آھي، پوءِ جيڪڏھن (اُنھن ڏانھن اُن وقت ۾) موٽيا ته (ڀلي موٽن) ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(2,227,'2:227','۽ جيڪڏھن طلاق جو پڪو ارادو ڪيائون (ته ڀلي ڪن) ڇوته الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,228,'2:228','۽ طلاق ڏنلون زالون پاڻ کي (ٻئي نڪاح ڪرڻ کان) ٽي حيض جھلين، ۽ جيڪڏھن الله ۽ قيامت جي ڏينھن کي مڃينديون آھن ته الله سندن ڳڀيرڻن ۾ جيڪي (ٻار يا حيض) پيدا ڪيو آھي سو کين لڪائڻ روا نه آھي، ۽ اِن (مدت) ۾ سندن مڙس جيڪڏھن سڌارو گھرن ته سندن موٽائڻ جا وڌيڪ حقدار آھن، ۽ جھڙا (مُڙس جا حق) اُنھن (زالن) تي آھن تھڙا اُنھن (زالن) جا (حق مڙسن تي) چڱيءَ ريت سان آھن، مُڙسن کي مٿن وڏو مانُ (يعني حڪومت) آھي، ۽ الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(2,229,'2:229','طلاق (رجعي پورا) ٻه ڀيرا آھي، پوءِ چڱيءَ طرح سان جھلڻ يا اِحسان سان ڇڏڻ (لازم) آھي، ۽ جيڪو کين ڏنو اَٿوَ تنھن مان ڪجھ (ورائي) وٺڻ اوھان کي ھن (حالت) کان سواءِ حلال نه آھي جو ٻئي الله جي حدن کي نه پوري ڪرڻ کان ڊڄن، پوءِ جيڪڏھن (اي مسلمانؤ) اوھين ڊڄو ته ٻئي الله جي حدن کي پورو ڪري نه سگھندا ته (زال) جيڪا شي عيوض ۾ ڏئي تنھن (جي ڏيڻ وٺڻ) ۾ ٻنھي تي گناھ نه آھي. اِھي الله جون حدون آھن اُنھن کان لنگھي نه وڃو، ۽ جيڪي الله جي حدن کان لنگھي ويندا سي ئي ظالم آھن.');
INSERT INTO "amroti" VALUES(2,230,'2:230','پوءِ جيڪڏھن اُن کي (ٽين) طلاق (به) ڏنائين ته اُن کانپوءِ (اُھا زال) اُن کي (ايستائين) حلال نه ٿيندي جيستائين (اُھا) ٻيو مُڙس پرڻي، پوءِ جيڪڏھن (اُن به) کيس طلاق ڏني ته جيڪڏھن ٻئي (ڄڻا) الله جي حدن کي قائم رکي سگھڻ (جي پڪ) ڀانئين ٿا ته (تڏھن پاڻ ۾ نڪاح ڪري) موٽن ته ٻنھي تي گناھ نه آھي، ۽ اِھي اَلله جون حدون آھن اُنھن کي اُن قوم لاءِ بيان ڪندو آھي جي ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(2,231,'2:231','۽ جڏھن زالن کي (ٻه ڀيرا) طلاق ڏيو پوءِ اُھي پنھنجي (عدت جي) مدي کي (اچي) پھچڻ لڳن ته (تڏھن يا) اُنھن کي چڱيءَ ريت سان رکو يا اُنھن کي چڱيءَ ريت سان ڇڏيو، ۽ ڏکوئڻ لاءِ کين نه جھليو نه زيادتي ڪريو، ۽ جيڪو ائين ڪندو تنھن بيشڪ پاڻ تي ظلم ڪيو، ۽ الله جي حُڪمن کي ٺـٺوليون ڪري نه وٺو، ۽ پاڻ تي الله جي نعمت ياد ڪريو ۽ جيڪي اوھان تي ڪتاب ۽ دانائيءَ مان لاٿائين جنھن سان اوھان کي نصيحت ڏيندو آھي (اُھو احسان به ياد ڪريو)، ۽ الله کان ڊڄو ۽ ڄاڻو ته الله سڀڪنھن شيء کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,232,'2:232','۽ جڏھن زالن کي طلاق ڏيو پوءِ پنھنجي (عدت جي) مُدّت کي پھچن ته کين پنھنجن مُڙسن سان پرڻجڻ کان نه جھليو جڏھن ته پاڻ ۾ چڱيءَ ريت سان راضي ٿين، اِھو (حُڪم) آھي جيڪو اوھان مان الله ۽ قيامت جي ڏينھن کي مڃي تنھن کي اُن سان نصيحت ڏجي ٿي، اِھو (حُڪم) اوھان کي صاف ڪندڙ ۽ بلڪل پاڪ ڪندڙ آھي، ۽ الله ڄاڻندو آھي ۽ اوھين نه ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(2,233,'2:233','۽ مائر پنھنجن ويائن کي پورا ٻه ورھيه ٿڃ پيارين (اِھو حُڪم) اُنھيءَ لاءِ آھي جيڪو ٿڃ پيارڻ جي مُدّت پُوري ڪرڻ گُھري، ۽ اُنھن (مائرن) جي خوراڪ ۽ اُنھن جي پوشاڪ ھليءَ چليءَ وانگر پيءُ تي آھي، ڪنھن کي سندس وس کان وڌيڪ تڪليف نه ٿي ڏجي، ماءُ کي وياءُ سببان تڪليف نه ٿي ڏجي ۽ نڪي پيءُ کي وياءُ سببان (تڪليف ڏجي)، ۽ وارث تي (به) اھڙو ئي (حق) آھي، پوءِ جيڪڏھن ٻئي پنھنجي رضامندي ۽ صَلاح سان (ٻار جي ٿڃ) ڇڏائڻ گھرن ته مٿن گناھ نه آھي، ۽ جيڪڏھن پنھنجي اولاد کي (ٻئي کان) کير پيارڻ گھرو ته جڏھن اوھان ھليءَ چليءَ وانگر (پياريندڙ کي) جيڪي ڏيڻو ڪيو سو پھچايو تڏھن اوھان تي ڪو گناھ نه آھي، ۽ الله کان ڊڄو ۽ ڄاڻو ته جيڪي اوھين ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(2,234,'2:234','۽ اوھان مان جيڪي مرن ۽ زالون ڇڏين ته اُھي (زالون) پاڻ کي چار مھينا ۽ ڏھ ڏينھن ترسائين، پوءِ جڏھن پنھنجيءَ (عدّت جي) مُدّت کي پُھچن تڏھن جيڪي ھليءَ چليءَ وانگر (يعني شرعي نڪاح) پنھنجي حق ۾ ڪن تنھن ۾ اوھان تي ڪو گناھ ڪونھي، ۽ جيڪي اوھين ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(2,235,'2:235','۽ زالن جي سڱ ڇڪائڻ بابت (عدت ۾) جيڪي آڇ ڪريو يا دلين ۾ لڪايو تنھن ۾ اوھان تي گناھ نه آھي، الله ڄاتو آھي اوھين اُنھن (زالن) کي سگھو ئي ياد ڪندؤ پر رواجيءَ طرح چئي چوڻ کانسواءِ کين ڳجھو انجام نه ڏيو، ۽ نڪي ايستائين نڪاح جي پڪي نيت ڪريو جيستائين عدت پنھنجيءَ مدت کي پھچي، ۽ ڄاڻو ته جيڪي اوھان جي دلين ۾ آھي سو الله ڄاڻندو آھي تنھنڪري کانئس ڊڄو، ۽ ڄاڻو ته الله بخشڻھار بُردبار آھي.');
INSERT INTO "amroti" VALUES(2,236,'2:236','جيڪڏھن زالن کي (اُنھيءَ وقت) طلاق ڏيو جڏھن اڃا کين ھٿ نه لاتو اَٿوَ يا اُنھن لاءِ ڪابين مُقرر نه ڪيو اَٿوَ ته اوھان تي گناھ ڪونھي، ۽ کين نفعي وٺڻ لاءِ ھليءَ چليءَ وانگر جورو ڏيو، آسودي تي پنھنجي وس آھر ۽ سڃي تي پنھنجي وس آھر آھي، (اِھو ڪم) ڀلارن تي لازم آھي.');
INSERT INTO "amroti" VALUES(2,237,'2:237','۽ جيڪڏھن کين ھٿ لائڻ کان اڳ ھن حالت ۾ انھن کي طلاق ڏيو جو ڪابين مُقرّر ڪيو اٿو تنھن جو اَڌ (ڏيڻ لازم آھي) پر جي (زالون) بخشين يا جنھن جي ھٿ ۾ نڪاح جي ڳنڍ آھي (يعني مڙس) سو بخشي (يعني پورو ڏي)، ۽ اوھان جو بخشڻ پرھيزگاريءَ کي ڏاڍو ويجھو آھي، ۽ اوھين پاڻ ۾ نيڪيءَ کي نه وساريو، ڇوته جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(2,238,'2:238','سڀني نمازن تي ۽ (خصوصاً) وچين نماز تي نگھباني ڪريو، ۽ الله لاءِ فرمانبردار ٿي بيھو.');
INSERT INTO "amroti" VALUES(2,239,'2:239','پوءِ جيڪڏھن دشمنن کان ڊڄو ته پيادا ٿي يا سوار ٿي (اشارن سان نماز پڙھو)، پوءِ جڏھن بي ڀَوَا ٿيو تڏھن جئن اوھان کي (نماز جا ارڪان) جي نه ڄاڻندا ھيؤ سيکاريائين تئن (سڀني رڪنن جي بجا آوريءَ سان) الله کي ياد ڪريو.');
INSERT INTO "amroti" VALUES(2,240,'2:240','۽ اوھان مان جيڪي مرن ۽ زالون ڇڏين، تن کي ھڪ ورھيه تائين پنھنجين زالن لاءِ (کين) فائدي ڏيڻ (۽) نه لوڌڻ جي وصيّت ڪرڻ لازم آھي، پوءِ جيڪڏھن (اُھي) نڪرن ته جيڪي پنھنجي حق ۾ (شريعت جي) دستور مُوجب ڪن تنھن ۾ اوھان تي گُناھ نه آھي، ۽ الله غالب حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(2,241,'2:241','۽ طلاق ڏنلن لاءِ ھليءَ چليءَ وانگر پرھيزگارن تي خرچ لازم آھي.');
INSERT INTO "amroti" VALUES(2,242,'2:242','اِھڙيءَ طرح الله پنھنجا حُڪم اوھان لاءِ پَڌرا ڪري ٿو ته مانَ اوھين سمجھو.');
INSERT INTO "amroti" VALUES(2,243,'2:243','(اي ڏسندڙ) اُنھن ڏانھن نه ڏٺو اٿئي ڇا؟ جيڪي پنھنجي ديس مان موت جي ڀؤ کان نڪتا ۽ اُھي (ڪيئي) ھزار هُئا، پوءِ الله اُنھن کي فرمايو ته مرو (ته مري ويا)، وري کين جياريائين، ڇوته الله ماڻھن تي وڏي فضل ڪرڻ وارو آھي پر گھڻا ماڻھو شڪر نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(2,244,'2:244','۽ (اي مُسلمانؤ) الله جي واٽ ۾ (ڪافرن سان) ويڙھ ڪريو ۽ ڄاڻو ته الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,245,'2:245','اُھو ڪير آھي جو الله کي چڱو قرض اُڌارو ڏئي؟ ته (الله) اُن لاءِ گھڻي قدر وڌائي، ۽ (رزق) الله تنگ ڪري ٿو ۽ (الله) ڪشادو ڪري ٿو، ۽ ڏانھنس موٽايا ويندؤ.');
INSERT INTO "amroti" VALUES(2,246,'2:246','موسىٰؑ کان پوءِ بني اِسرائيلن جي ٽوليءَ ڏانھن نه ڏٺو اٿيئي ڇا؟ جڏھن پنھنجي پيغمبر کي چيائون ته اسان لاءِ ڪو بادشاھ مُقرّر ڪر ته الله جي واٽ ۾ ويڙھ ڪريون، (تڏھن پيغمبر) چيو ته جيڪڏھن اوھان تي ويڙھ لازم ٿي ته اوھين وڙھڻ کي ويجھا نه ويندؤ، چيائون ته اسان کي ڇا (ٿيو) آھي جو الله جي واٽ ۾ اِنھيءَ ھوندي به نه وڙھنداسون جو (اسين) پنھنجن ديسن ۽ پنھنجن اولادن مان لوڌيا ويا آھيون، پوءِ جڏھن مٿن ويڙھ لازم ڪئي ويئي تڏھن منجھانئن ٿورڙن کانسواءِ (سڀ) ڦري ويا، ۽ الله ظالمن کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,247,'2:247','۽ سندن پيغمبر کين چيو ته الله طالوت کي اوھان لاءِ بادشاہ مُقرّر ڪيو آھي، چيائون ته اُن جي بادشاھي اسان تي ڪئن ٿيندي ھن حالت ۾ جو اسين کانئس بادشاھيءَ جا وڌيڪ حقدار آھيون ۽ مال مان گھڻائي نه ڏني ويئي اٿس. (پيغمبر کين) چيو ته الله اُن کي اوھان تي پسند ڪيو آھي ۽ کيس عِلم ۽ جسم ۾ گھڻو وڌايو اٿس، ۽ الله جنھن کي گھُرندو آھي تنھن کي پنھنجو مُلڪ ڏيندو آھي، ۽ الله (مھربانيءَ ۾) ڪشادگيءَ وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,248,'2:248','۽ کين سندن پيغمبر چيو ته اُن جي بادشاھيءَ جي نشاني اوھان وٽ صندوق جو اچڻ آھي جنھن ۾ اوھان جي پالڻھار کان ھڪ دِل جو آرام ۽ جيڪي مُوسىٰ جي خاندان ۽ ھارون جي خاندان (تبرڪ) ڇڏيو تنھن جي بچت آھي اُن کي مَلائِڪ کڻي ايندا، ڇوته اُن ۾ اوھان لاءِ بي شڪ (وڏي) نشاني آھي جيڪڏھن اوھين مڃيندڙ آھيو.');
INSERT INTO "amroti" VALUES(2,249,'2:249','پوءِ جڏھن طالُوت لشڪر سوڌو نڪتو (تڏھن لشڪر کي) چيائين ته الله نديءَ جي پاڻيءَ سان توھان کي پرکيندڙ آھي، پوءِ جيڪو منجھانئس پيئندو سو منھنجو نه آھي، ۽ جيڪو منجھانئس نه چکندو سواءِ ھِن جي جو پنھنجي ھٿ سان لَپ ڀري (پيئي) سو بيشڪ مُنھنجو آھي. پوءِ منجھانئن ٿورڙن کانسواءِ (سڀني) اُن مان پيتو، پوءِ جنھن مھل اُھو (طالوت) ۽ سندس سنگتي مُؤمن اُن (نديءَ) کان لنگھيا (تنھن مھل پيئندڙن) چيو ته اَڄ اسان کي جالوت ۽ سندس لشڪر سان (وڙھڻ جي) سگھ نه آھي، (۽ تنھن مھل) جيڪي پڪو ڀروسو رکندا هُئا ته اُھي الله کي ملندا تن چيو ته ڪيتريون ٿوريون ٽوليون گھڻن ٽولين تي الله جي حُڪم سان غالب ٿي ويون آھن، ۽ الله صابرن ساڻ آھي.');
INSERT INTO "amroti" VALUES(2,250,'2:250','۽ جنھن مھل (اُھي طالُوتي) جالُوت ۽ سندس لشڪر (جي وڙھڻ) لاءِ نڪتا (تنھن مھل) چيائون ته اي اسان جا پالڻھار اسان تي صبر پلٽ ۽ اسان جان پير ثابت رَک ۽ ڪافر ٽوليءَ تي اسان کي سوڀ ڏئي.');
INSERT INTO "amroti" VALUES(2,251,'2:251','پوءِ الله جي حُڪم سان کين ڀڄايائون، ۽ دائُود جالُوت کي ڪُٺو ۽ الله اُن (دائُود) کي بادشاھي ۽ حِڪمت ڏِني ۽ جيڪي اُن لاءِ گھريائين سو سيکاريائين، ۽ جيڪڏھن الله جو ٽارڻ ھڪڙن ماڻھن سان ٻـين کي نه ھُجي ھا ته زمين ضرور خراب ٿئي ھا پر الله جھان (وارن) تي ڀلائي ڪرڻ وارو آھي.');
INSERT INTO "amroti" VALUES(2,252,'2:252','اِھي الله جا حُڪم آھن ھي توتي سَچ سان پڙھون ٿا، ۽ تون ضرور پيغمبرن مان آھين.');
INSERT INTO "amroti" VALUES(2,253,'2:253','اِھي پيغمبر آھن اُنھن مان ھڪڙن کي ٻـين کان ڀلو ڪيوسون، منجھانئن ڪن سان الله ڳالھايو ۽ ڪن کي درجن ۾ وڏو ڪيائين، ۽ عيسىٰ پٽ مريم جي کي چٽا مُعجزا ڏناسون ۽ کيس پاڪ رُوح سان مدد ڏني سون، ۽ جيڪڏھن الله گُھري ھا ته جيڪي (ماڻھو) اُنھن (پيغمبرن) کان پوءِ هُئا سي پاڻ وٽ چِٽن مُعجزن اچڻ پڄاڻان (پاڻ ۾) نه وڙھن ھا پر اختلاف ۾ پيا پوءِ منجھانئن ڪن ايمان آندو ۽ منجھانئن ڪن ڪفر ڪيو، ۽ جيڪڏھن الله گھري ھا ته (پاڻ ۾) نه وڙھن ھا، پر الله جيڪي گھرندو آھي سو ڪندو آھي.');
INSERT INTO "amroti" VALUES(2,254,'2:254','اي مؤمنؤ جيڪي اوھان کي ڏنوسون تنھن مان اُن ڏينھن جي اچڻ کان اڳ خرچ ڪريو جنھن ۾ نه ڪو واپار ۽ نه ڪا ياراڻي آھي ۽ نه ڪا پارت ھلندي، ۽ ڪافر ئي ظالم آھن.');
INSERT INTO "amroti" VALUES(2,255,'2:255','الله (اُھو آھي جو) اُن کان سواءِ ڪو عبادت جي لائق نه آھي، (اُھو سدائين) جيئرو (جھانن کي) بيھڪ ڏيندڙ آھي، کيس نڪي پنڪي ۽ نڪي ننڊ وٺندي آھي، جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) سندس (ملڪيت) آھي. ڪير آھي جو سندس موڪل ڌاران وٽس پارت ڪري سگھي؟ جيڪي اُنھن (مخلوقن) جي اڳيان آھي ۽ جيڪي سندن پوئتان آھي سو (سڀ) ڄاڻندو آھي، ۽ جيڪي گھري تنھن کان سواءِ سندس علم مان ڪنھن ذري کي (اُھي) گھيرو ڪري نه سگھندا آھن، سندس (بادشاھيءَ جي) ڪرسيءَ آسمانن ۽ زمين کي لپيٽيو آھي، ۽ اُنھن جي سنڀال کيس نه ٿڪائيندي آھي، ۽ اُھو (سڀ کان) مٿاھون وڏي وڏائيءَ وارو آھي.');
INSERT INTO "amroti" VALUES(2,256,'2:256','دين مڃائڻ ۾ ڪا زبردستي نه آھي، (ڇوته) بيشڪ گمراھيءَ کان ھدايت (جدا ٿي) پڌري ٿي آھي، تنھنڪري جيڪو طاغوت (يعني خُدا کانسواءِ ٻـين معبودن) کي نه مڃي ۽ الله کي مڃي تنھن بيشڪ اھڙي مُحڪم ڪڙي کي ورتو، جنھن کي ڇڄڻ نه آھي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,257,'2:257','الله مؤمنن جو سنڀاليندڙ آھي کين اونداھين مان سوجھري ڏانھن ڪڍندو آھي، ۽ جيڪي ڪافر آھن تن جا مددگار (خُدا کانسواءِ جن جي عبادت ڪن ٿا سي) بت آھن کين سوجھري مان اونداھين ڏانھن ڪڍندا آھن، اُھي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(2,258,'2:258','(اي پيغمبر) تو اُنھن ڏانھن نه ڏٺو ڇا؟ جنھن پنھنجي پالڻھار بابت ابراھيم سان (انھيءَ مغروريءَ سبب) تڪرار ڪيو جو الله کيس بادشاھي ڏني ھئي، جڏھن ابراھيم چيو ته منھنجو پالڻھار اُھو آھي جو جياريندو آھي ۽ ماريندو آھي، (ڪافر) چيو ته آءٌ (به) جياريان ٿو ۽ ماريان ٿو. ابراھيم چيو ته الله سج کي اوڀر کان اوڀاريندو آھي تنھنڪري (تون) اُن کي اولھ کان اوڀار پوءِ ڪافر ھَڪو ٻَڪو ٿي ويو، ۽ الله ظالم ٽوليءَ کي سڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(2,259,'2:259','يا اِنھيءَ طرح اُنھي (پيغمبر) کي (ڏٺئي ڇا؟) جو ھڪ ڳوٺ وٽان ھن حالت ۾ لنگھيو جو اُھو پنھنجي ڇتين سوڌو ڊٺل ھو، (ڏسي ڪري) چيائين ته ھن کي الله ڪھڙي طرح سندس ڦٽڻ کانپوءِ وسائيندو، پوءِ الله کيس سؤ ورھيه ماريو وري کيس (جياري) کڙو ڪيائين، الله چيو ته ڪيترو رھينءِ؟ چيائين ته ھڪ ڏينھن يا ڪجھ پھر دير ڪئي اٿم، الله چيو ته بلڪ سؤ ورھيه دير ڪئي اٿيئي پوءِ پنھنجي طعام ۽ پاڻيءَ ڏانھن نھار جو اُھو نه سينواريو آھي، ۽ پنھنجي (مئل) گڏھ ڏانھن نھار (ته جياري توکي ڏيکاريون) ۽ (اِھو ھِن لاءِ ڪيوسين) ته توکي ماڻھن لاءِ نشاني ڪريون ۽ (ھاڻي گڏھ جي) ھڏن ڏانھن ڏس ته کين ڪئن چوريون ٿا وري اُنھن (ھڏن) کي گوشت پھرايون ٿا. پوءِ جنھن مھل ان تي (اِھو حال) پڌرو ٿيو (تڏھن) چيائين ته ڄاڻان ٿو ته الله سڀ ڪنھن شيءِ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(2,260,'2:260','۽ (ياد ڪر) جڏھن ابراھيم چيو ته اي مُنھنجا پالڻھار مون کي ڏيکار ته ڪھڙي طرح مئلن کي جياريندو آھين، (الله) چيو ته نه ٿو وسھين ڇا؟ (ابراھيم) چيو ته ھائو (وسھان ٿو) پر ھن لاءِ ته منھنجي دل کي تسلّىٰ ٿئي. (الله) چيو ته چار پکي وٺ پوءِ کين پاڻ وٽ ذرا ذرا ڪر پوءِ منجھانئن ڪو ڀاڱو ھر ھڪ جبل تي رک وري کين سڏ ته توڏانھن ڊوڙندا ايندا، ۽ ڄاڻ ته الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(2,261,'2:261','جيڪي (ماڻھو) پنھنجا مال الله جي واٽ ۾ خرچيندا آھن تن جو مثال اُنھيءَ داڻي جي مثال وانگر آھي جو ست سنگ ڄمائي ھر ھڪ سنگ ۾ سڀ داڻو ھجي، ۽ الله جنھن کي گھرندو آھي (تنھن کي) ٻيڻو ڏيندو آھي، ۽ الله (مھربانيءَ ۾) ڪشادگي وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,262,'2:262','جيڪي (ماڻھو) پنھنجو مال الله جي واٽ ۾ خرچيندا آھن وري پنھنجي ڏيڻ پٺيان نڪي ٿورو رکندا آھن ۽ نڪي ايذائيندا آھن تن لاءِ سندن پالڻھار وٽ سندن ثواب آھي، ۽ نڪي کين ڪو ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(2,263,'2:263','مِٺو ڳالھائڻ ۽ معاف ڪرڻ اُنھيءَ خيرات کان ڀَلو آھي جنھن جي پٺيان ايذاءُ ھُجي، ۽ الله بي پرواہ بُردبار آھي.');
INSERT INTO "amroti" VALUES(2,264,'2:264','اي مؤمنؤ پنھنجين خيراتن کي اِحسان رکڻ ۽ ايذائڻ سان اُنھيءَ ماڻھوءَ وانگر نه وڃايو جيڪو پنھنجو مال ماڻھن جي ڏيکاري لاءِ خرچي ۽ الله ۽ قيامت جي ڏينھن کي نه مڃي، پوءِ سندس مثال (اُنھيءَ) لسي پھڻ وانگر آھي جنھن تي ٿوري مٽي (پيل) ھجي ۽ مٿس وڏو مينھن وسي پوءِ ڇنڊي ڇڏيس، (ته رياء وارن) جيڪي ڪمايو تنھن مان ڪنھن ذري تي (اُھي) وس وارا نه ٿيندا، ۽ الله ڪافر ٽوليءَ کي سڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(2,265,'2:265','۽ جيڪي پنھنجن مالن کي الله جي رضامندي حاصل ڪرڻ ۽ پنھنجين دلين ۾ پڪي يقين پيدا ڪرڻ لاءِ خرچيندا آھن تن جو مثال ھڪ باغ وانگر آھي جو دڙي تي ھجي اُن تي وڏڦڙو مينھن وسي ۽ پنھنجو ڦر ٻيڻو ڪري، پر جيڪڏھن مٿس وڏڦڙو مينھن نه وسي ته ماڪ بس ھجيس، ۽ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(2,266,'2:266','اوھان مان ڪو ھڪ (دل سان) گھرندو آھي ڇا؟ ته اُن کي کجين ۽ ڊاکن جو ھڪ باغ ھجي جنھن جي ھيٺان واھيون وھنديون ھجن اُن لاءِ منجھس سڀ ڪنھن (جنس جا) ميوا ھجن ۽ کيس پوڙھائي اچي وٺي ۽ ان کي ھيڻو اولاد ھجي، پوءِ اُن (باغ) کي (اھڙو) واچوڙو لڳي جنھن ۾ باھه ھجي تنھنڪري (سندس باغ) سڙي وڃي، اھڙي طرح الله اوھان لاءِ (چٽيون) نشانيون بيان ڪندو آھي ته مانَ اوھين سمجھو.');
INSERT INTO "amroti" VALUES(2,267,'2:267','اي مؤمنؤ جيڪي ڪمايوَ تنھن مان (۽ پڻ) جيڪي اوھان لاءِ زمين مان پيدا ڪيوسين تنھن مان ڪي سٺيون شيون (خدا ڪارڻ) خرچ ڪيو، ۽ ان مان خراب شيء خرچ ڪرڻ جي نيت ھن ھوندي نه ڪريو جو (پاڻ) اُن ۾ پنھنجين اکين ٻوٽڻ کانسواءِ کيس وٺڻ وارا نه ھجو، ۽ ڄاڻو ته الله بي پرواہ ساراھيل آھي.');
INSERT INTO "amroti" VALUES(2,268,'2:268','(اي مسلمانؤ) شيطان اوھان کي سڃائيءَ جو ڀؤ ڏيندو آھي ۽ اوھان کي بي حيائيءَ (جي ڪمن) جو حڪم ڪندو آھي، ۽ الله اوھان کي پنھنجي پاران بخشش ۽ ڀلائيءَ جو وعدو ڏيندو آھي، ۽ الله (مھربانيءَ ۾) ڪشادگيءَ وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,269,'2:269','جنھن کي گھرندو آھي تنھن کي سمجھ ڏيندو آھي، ۽ جنھن کي سمجھ ڏني ويئي تنھن کي بيشڪ گھڻي چڱائِي ڏِني ويئي، ۽ سمجھ وارن کانسواءِ ٻيو ڪو نصيحت نه مڃيندو آھي.');
INSERT INTO "amroti" VALUES(2,270,'2:270','۽ (اي مسلمانؤ) جيڪي اوھان مال خرچيو يا باس باسيو تنھن کي الله ڄاڻندو آھي، ۽ ظالمن جو ڪو مددگار نه آھي.');
INSERT INTO "amroti" VALUES(2,271,'2:271','جيڪڏھن خيراتون پڌريون ڪري ڏيندؤ ته اُھي (ڪھڙيون نه) چڱيون آھن، ۽ جيڪڏھن اُنھن کي ڳجھو ڪندؤ ۽ اُھي محتاجن کي ڏيندؤ ته اُھي اوھان لاءِ (ويتر) ڀليون آھن، ۽ الله اوھان جا ڪي گناہ اوھان کان ميٽيندو، ۽ جيڪي اوھان ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(2,272,'2:272','(اي پيغمبر) توتي اُنھن (ڪافرن) کي ھدايت (يعني دل ۾ اثر پيدا) ڪرڻ لازم نه آھي پر الله جنھن کي گھرندو آھي تنھن کي ھدايت ڪندو آھي ۽ جيڪي ڪجھ مال خرچيندا آھيو سو اوھان لاءِ آھي، ۽ الله جي رضامندي طلبڻ کانسواءِ (اوھين) خرچ نه ڪريو، ۽ جيڪي ڪجھ مال خرچيو (اُن جو ثواب) اوھان کي پورو ڏبو ۽ اوھان تي ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(2,273,'2:273','اھڙن فقيرن کي (خيرات ڏيڻ لازم آھي) جيڪي الله جي واٽ ۾ روڪيل آھن (واپار لاءِ) مُلڪ ۾ سفر ڪري نه سگھندا آھن، ناواقف (سندن) بي طمعي سببان کين آسودا ڀائيندا آھن، سندين پيشانين مان کين سڃاڻندو آھين، چنبڙي ماڻھن کان نه پنندا آھن، ۽ جيڪي ڪجھ مال خرچيندا آھيو سو الله ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,274,'2:274','جيڪي پنھنجا مال رات جو ۽ ڏينھن جو ڳجھو ۽ پَڌرو خرچيندا آھن تن لاءِ سندن پالڻھار وٽ سندن اَجر آھي، ۽ نڪي کين ڪو ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(2,275,'2:275','جيڪي وياج کائيندا آھن سي (قبرن مان) رڳو اُنھيءَ (ماڻھوءَ) جي اُٿڻ جھڙو اُٿندا جنھن کي جِنّ اثر سببان چريو ڪيو ھجي، اِھو ھن ڪري آھي جو اُھي (وياج خور) چون ٿا ته واپار وياج جھڙو ئي آھي، ھن ھوندي جو الله واپار کي حلال ڪيو آھي ۽ وياج کي حرام ڪيو آھي، پوءِ جنھن کي پنھنجي پالڻھار وٽان نصيحت آئي ۽ (وياج) ڇڏيائين تنھن لاءِ جيڪي اڳي ڪيائين سو (ھينئر معاف) آھي، ۽ (آخرت ۾) سندس ڪم الله ڏانھن (سونپيل) آھي، ۽ جن ھيءَ عادت نه ڇڏي سي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(2,276,'2:276','الله وياج (جو کٽيو) ناس ڪري ڇڏيندو آھي ۽ خيراتن کي وڌائيندو آھي، ۽ الله سڀ ڪنھن بي شڪر گنھگار کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(2,277,'2:277','جن ايمان آندو ۽ چڱا ڪم ڪيا ۽ نماز پڙھيائون ۽ زڪوٰة ڏنائون تن لاءِ سندن پالڻھار وٽ سندن اَجر آھي، ۽ نڪي کين ڪو ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(2,278,'2:278','اي ايمان وارؤ الله کان ڊڄو ۽ جيڪڏھن اوھين (الله ۽ رسول کي) مڃيندڙ آھيو ته جيڪي وياج مان رھيل آھي سو ڇڏيو.');
INSERT INTO "amroti" VALUES(2,279,'2:279','پوءِ جيڪڏھن (ائين) نه ڪندؤ ته الله ۽ سندس رسول جي پاران اوھان کي جنگ جي سُڌ ھجي، ۽ جيڪڏھن توبه ڪيوَ ته اوھان لاءِ اوھان جي مالن جي موڙي آھي، نڪي وڌيڪ وٺو ۽ نڪي اوھان کي گھٽ ڏبو.');
INSERT INTO "amroti" VALUES(2,280,'2:280','۽ جيڪڏھن (قرضي) سڃو ھجي ته سگھ تائين مُھلت ڏيڻ لازم آھي، ۽ جيڪڏھن اوھين ڄاڻندا آھيو ته اوھان جو (قرض) بخشڻ اوھان لاءِ ڀلو آھي.');
INSERT INTO "amroti" VALUES(2,281,'2:281','۽ (اي ماڻھو) اُنھيءَ ڏينھن کان ڊڄو جنھن ۾ الله ڏانھن موٽايا ويندؤ، پوءِ سڀڪنھن ماڻھوءَ کي جيڪي ڪمايائين تنھن جو پورو بدلو ڏبو ۽ اُنھن تي ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(2,282,'2:282','اي مؤمنؤ جڏھن ڪنھن ٺھرايل (مُدت) تائين قرض جو معاملو ڪريو تڏھن اُن کي لکو، ۽ اوھان جي وچ ۾ لکندڙ کي انصاف سان لکڻ گھرجي، ۽ جئن کيس الله سيکاريو تئن لکندڙ لکڻ کان انڪار نه ڪري پوءِ لکڻ گھرجيس، ۽ جنھن تي قرض آھي سو (مطلب) بيان ڪري ۽ پنھنجي پالڻھار الله کان ڊڄڻ گھرجيس ۽ منجھانئس ڪُجھ نه گھٽائي، پوءِ جنھن تي قرض آھي سو جيڪڏھن اَٻوجھ يا ھيڻو ھجي يا بيان نه ڪري سگھي ته سندس سنڀاليندڙ کي اِنصاف سان بيان ڪرڻ گھرجي، ۽ پنھنجن (مسلمانن) مڙسن مان ٻه گواھ شاھد ڪريو، پوءِ جيڪڏھن ٻه مڙس نه ھجن ته ھڪ مڙس ۽ ٻه زالون (اُنھن) شاھدن مان جيڪي اوھان کي وڻن (شاھد ڪريو ھن لاءِ) ته جي اُنھن (ٻنھي) مان ھڪڙي وساريندي ته اُنھن مان ٻي کيس ياد ڏياريندي، ۽ جڏھن شاھد (شاھديءَ ڏانھن) سڏيا وڃن ته انڪار نه ڪن، ۽ اُن جي مدت تائين ٿوري يا گھڻي (قرض) جي لکي ڏيڻ کان نه ٿڪجو، اِھو (لکڻ) الله وٽ انصاف کي تمام ويجھو ۽ شاھديءَ لاءِ بلڪل بيھڪ وارو آھي ۽ اوھان جي شڪ ۾ نه پوڻ کي (به) ڏاڍو ويجھو آھي پر جيڪڏھن اوھان جو واپار رُوبرو ھجي جو اوھين ھٿون ھٿ پاڻ ۾ پيا ڪريو ته اُن جي نه لکڻ ۾ اوھان تي ڪو گناھ نه آھي، ۽ جڏھن ڳنھو وڪڻو ته شاھد ڪريو، ۽ لکندڙ ۽ شاھد کي نه ڏکوئجي، ۽ جيڪڏھن (اھڙو ڪم) ڪندؤ ته اُھو اوھان کي گُناہ آھي، ۽ الله کان ڊڄو، ۽ الله اوھان کي سيکاري ٿو، ۽ الله سڀڪنھن شيء کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,283,'2:283','۽ جيڪڏھن اوھين مسافريءَ ۾ ھُجو ۽ لکندڙ نه لھو ته ھٿڻ گروي وٺڻ لازم آھي، پوءِ جيڪڏھن اوھان مان ھڪڙا ٻـين تي اعتبار رکن ته جنھن تي اعتبار رکيو ويو تنھن کي پنھنجي امانت (يعني قرض) ادا ڪرڻ گھرجي ۽ پنھنجي پالڻھار الله کان ڊڄڻ گھرجي، ۽ شاھدي نه لڪايو، ۽ جيڪو اُن کي لڪائيندو ته بيشڪ سندس دل ڏوھارڻ آھي، ۽ جيڪي اوھين ڪندا آھيو سو الله ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(2,284,'2:284','جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سا الله جي ئي (ملڪيت) آھي، ۽ جيڪي اوھان جي دلين ۾ آھي سو جيڪڏھن پڌرو ڪريو توڻي اُھو لڪايو (ته به) الله اوھان سان اُن جو حساب ڪندو، پوءِ جنھن لاءِ گھرندو تنھن کي بخشيندو، ۽ جنھن لاءِ گھرندو تنھن کي عذاب ڪندو، ۽ الله سڀڪنھن شيءِ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(2,285,'2:285','پيغمبر (محمد صلى الله عليه وسلم) ڏانھن جيڪي سندس پالڻھار کان لاٿو ويو سو اُن مڃيو آھي ۽ مؤمنن (به)، سڀڪنھن الله ۽ سندس ملائڪن ۽ سندس ڪتابن ۽ سندس رسولن کي مڃيو آھي، (چون ٿا ته) سندس پيغمبرن مان ڪنھن ھڪ (جي مڃڻ) ۾ فرق نه ڪندا آھيون، ۽ چيائون ته ٻڌوسون ۽ سندس (حُڪم) مڃيوسون، اي اسان جا پالڻھار تنھنجي بخشش گھرون ٿا ۽ تو ڏانھن موٽڻو آھي.');
INSERT INTO "amroti" VALUES(2,286,'2:286','الله ڪنھن کي سندس وس ڌاران تڪليف نه ڏيندو آھي، جيڪي (چڱو) ڪمايائين سو سندس (فائدي) لاءِ آھي ۽ جيڪي (بڇڙو) ڪمايائين سو مٿس آھي، اي اسان جا پالڻھار جيڪڏھن وساريون يا چوڪ ڪريون ته اسان کي نه پڪڙ، ۽ اي اسان جا پالڻھار اسان تي اھڙو بار نه رک جھڙو اسان کان اڳين تي رکيو ھيئي، ۽ اي اسان جا پالڻھار (جنھن بار) جي کڻڻ جي اسان کي سگھ ڪانھي سو اسان تي نه رک، ۽ اسان کي معاف ڪر، ۽ اسان کي بخش، ۽ اسان تي مِھر ڪر، تون اسان جو سائين آھين تنھنڪري اسان کي ڪافر ٽوليءَ تي سوڀ ڏي.');
INSERT INTO "amroti" VALUES(3,1,'3:1','الٓمَّٓ.');
INSERT INTO "amroti" VALUES(3,2,'3:2','الله (اُھو آھي جو) اُن کانسواءِ ڪوئي عبادت جو لائق نه آھي (جو سدائين) جيئرو (جڳ جي) بيھڪ ڏيڻ وارو آھي.');
INSERT INTO "amroti" VALUES(3,3,'3:3','توتي (اُھو) ڪتاب سچ سان لاٿائين (جو) جيڪي (ڪتاب) کانئس اڳي آھن تن جو سچو ڪندڙ آھي ۽ توريت ۽ اِنجيل کي لاٿائين.');
INSERT INTO "amroti" VALUES(3,4,'3:4','اڳي ئي ماڻھن جي ھدايت لاءِ ۽ (سچ ۽ ڪُوڙ ۾) سنڌو وجھندڙ لاٿائين، جن الله جي آيتن جو اِنڪار ڪيو تن لاءِ سخت عذاب آھي، ۽ الله غالب بدلي وٺڻ وارو آھي.');
INSERT INTO "amroti" VALUES(3,5,'3:5','بيشڪ الله تي ڪائي شيء نڪي زمين ۾ ۽ نڪي آسمان ۾ ڳجھي رھندي آھي.');
INSERT INTO "amroti" VALUES(3,6,'3:6','اُھو (الله) اھو آھي جيڪو (مائن) ڳڀيرڻن ۾ جئن گھرندو آھي (تئن) اوھان جي شڪل ٺاھيندو آھي، اُن کانسواءِ ڪوئي عبادت جو لائق نه آھي (اُھوئي) غالب حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(3,7,'3:7','اُھو (الله) آھي جنھن توتي ڪتاب لاٿو جنھن مان ڪي آيتون مُحڪم آھن (يعني اُنھن جي معنىٰ صاف آھي) اُھي ڪتاب جو اصل آھن ۽ ٻيون متشابه آھن (يعني اُنھن جون معنائون گھڻيون ٿي سگھن ٿيون)، پوءِ جن دلين ۾ ڏنگائي آھي سي مَنجھانئس جيڪي مُتشابه آھن تن جي پُٺيان خرابي ڳولڻ ۽ سندس مراد ڳولڻ لاءِ لڳندا آھن، ۽ الله کانسواءِ اُن جي مُراد ڪوئي نه ڄاڻندو آھي، ۽ (جيڪي) علم ۾پڪا (آھن سي) چوندا آھن ته اُنھن کي مڃيوسُون اِھي مڙئي اسان جي پالڻھار وٽان آھن، ۽ ڌيان وارن کانسواءِ (ٻيو) ڪوبه نصيحت نه وٺندو آھي.');
INSERT INTO "amroti" VALUES(3,8,'3:8','اي اسان جا پالڻھار جڏھن تو اسان کي ھدايت ڪئي تنھن کان پوءِ اسان جي دلين کي ڏنگائي نه ڏي ۽ پاڻ وٽان اسان کي ٻاجھ عطا ڪر، ڇوته تون ئي بخشڻھار آھين.');
INSERT INTO "amroti" VALUES(3,9,'3:9','اي اسان جا پالڻھار تون اُن ڏينھن ۾ ماڻھن کي گڏ ڪندڙ آھين جنھن ۾ ڪو شڪ نه آھي، ڇوته الله (پنھنجي) انجام کي نه ڦيرائيندو آھي.');
INSERT INTO "amroti" VALUES(3,10,'3:10','بيشڪ جن ڪفر ڪيو تن کان ڪڏھن نه سندن مال ۽ نڪي سندن اولاد الله (جي عذاب) مان ڪجھ نه ٽاريندو، ۽ اِھي دوزخ جو ٻارڻ آھن.');
INSERT INTO "amroti" VALUES(3,11,'3:11','(اُنھن جو حال) فرعونين جي ۽ اُنھن جي حال جھڙو آھي جي کانئن اڳ ھؤا، (اُنھن) اسان جي آيتن کي ڪُوڙو ڀانيو، پوءِ الله سندن گناھن سببان کين پڪڙيو، ۽ الله سخت سزا ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(3,12,'3:12','(اي پيغمبر) ڪافرن کي چؤ ته سگھو ھيڻا ٿيندؤ ۽ دوزخ ڏانھن گڏ ڪيا ويندؤ، ۽ (اُھو) بڇڙو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(3,13,'3:13','اُنھن ٻن ٽولين جي (پاڻ ۾) ويڙھ ڪرڻ ۾ اوھان لاءِ بيشڪ نشاني آھي، ھڪڙي ٽولي الله جي واٽ ۾ وڙھي ۽ ٻي (ڪافرن جي) ھئي (مسلمانن) کين پنھنجو ٻيڻو اکين جي ڏيٺ سان ڏٺو ٿي، ۽ الله جنھن کي وڻيس (تنھن کي) پنھنجيءَ مدد سان سگھ ڏيندو آھي، ڇوته اُن ۾ (سمجھ جي) اکين وارن لاءِ ضرور عبرت آھي.');
INSERT INTO "amroti" VALUES(3,14,'3:14','ماڻھن لاءِ زالن ۽ پُٽن ۽ سونَ ۽ چانديءَ جي (وڏن وڏن) سٿيلن ڍيرن ۽ نشان ڪيلن گھوڙن ۽ ڍورن ۽ پوک جي سڌن جو پيار سينگاريو ويو آھي، اِھو دنيا جي حياتيءَ جو سامان آھي، ۽ الله وٽ چڱو موٽڻ جو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(3,15,'3:15','(اي پيغمبر کين) چؤ ته آءٌ ان کان ڀليءَ (شيء) جي سڌ ڏيان؟ جن (شرڪ کان) پاڻ بچايو تن لاءِ سندن پالڻھار وٽ بھشت آھن جن جي ھيٺان واھيون وھنديون آھن منجھن سدائين رھڻ وارا آھن ۽ پاڪ زالون ۽ (پڻ) الله جي پاران راضپو آھي، ۽ الله ٻانھن کي ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(3,16,'3:16','جيڪي چوندا آھن ته اي اسان جا پالڻھار اسان توکي مڃيو آھي تنھنڪري اسان جا ڏوھ اسان کي بخش ۽ باہ جي عذاب کان اسان کي بچاءِ.');
INSERT INTO "amroti" VALUES(3,17,'3:17','اُھي صابر ۽ سَچا ۽ تابعداري ڪندڙ ۽ سخي ۽ پرڀاتين جو بخشش گھرندڙ آھن.');
INSERT INTO "amroti" VALUES(3,18,'3:18','الله (ھي) گواھي ڏني آھي ته الله کانسواءِ ڪوئي عبادت جو لائق نه آھي، ۽ مَلائِڪ ۽ علم وارا (به) انصاف تي بيھڪ وارا ٿي (اِھا شاھدي ڏيندا آھن)، اُن کانسواءِ ڪوئي عبادت جو لائق نه آھي اُھو غالب حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(3,19,'3:19','بيشڪ وڻندڙ دين الله وَٽ اِسلام آھي، ۽ جن کي ڪتاب (توريت ۽ انجيل) ڏنو ويو تن (اڳ) تڪرار نه ڪيو پر وٽن علم آيو تنھن کانپوءِ پاڻ ۾ ضد سببان (تڪرار ڪيائون)، ۽ جيڪو الله جي حڪمن کي نه مڃيندو ته بيشڪ الله (به) سگھو حساب وٺندڙ آھي.');
INSERT INTO "amroti" VALUES(3,20,'3:20','پوءِ جيڪڏھن توسان جھيڙين ته چؤ پنھنجو مُنھن (يعني سر) مُون ۽ جنھن منھنجي تابعداري ڪئي تنھن الله لاءِ نِوايو آھي، ۽ (اي پيغمبر) جن کي ڪتاب (توريت ۽ انجيل) ڏنو ويو تن کي ۽ اڻ پڙھيلن کي چؤ ته اسلام آندو اَٿوَ؟ (يا نه) پوءِ جيڪڏھن اسلام آڻين ته بيشڪ سِڌيءَ واٽ وارا ٿيا، ۽ جيڪڏھن ڦري وڃن ته توتي رڳو پيغام پُھچائڻ آھي، ۽الله ٻانھن کي ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(3,21,'3:21','بيشڪ جيڪي الله جي آيتن کي نٿا مڃين ۽ پيغمبرن کي ناحق ٿا ڪُھن ۽ ماڻھن مان جيڪي (کين) اِنصاف جو حُڪم ڪن ٿا تن کي ٿا ڪُھن پوءِ کين ڏکوئيندڙ عذاب جي سُڌ ڏي.');
INSERT INTO "amroti" VALUES(3,22,'3:22','اِھي (اُھي) آھن جن جي ڪمائي دُنيا ۽ آخرت ۾ ناس ٿي ويئي، ۽ اُنھن جو ڪو واھرو ڪونھي.');
INSERT INTO "amroti" VALUES(3,23,'3:23','(اي پيغمبر) جن کي ڪتاب مان ڪو ڀاڱو ڏنو ويو تن کي نه ڏٺو اَٿئي ڇا؟ (جو) الله جي ڪتاب ڏانھن (ھن لاءِ) سڏيا وڃن ٿا ته سندن وچ ۾ نبيرو ڪري پوءِ منجھانئن ھڪ ٽولي ڦري ويندي آھي ۽ اُھي مُنھن موڙيندڙ (به) آھن.');
INSERT INTO "amroti" VALUES(3,24,'3:24','اِھو ھِن سببان آھي جو اُھي چون ٿا ته باھ ڪن ڳاڻوَن ڏينھن کانسواءِ اسان کي ڪڏھن نه ڇھندي، ۽ جيڪو ٺاھ ٺاھيندا رھيا تنھن کين سندن دين ۾ ٺڳيو آھي.');
INSERT INTO "amroti" VALUES(3,25,'3:25','پوءِ ڪھڙو (حال ٿيندن) جڏھن کين اُھو ڏينھن جنھن ۾ ڪو شڪ نه آھي تنھن ۾ گڏ ڪنداسين، ۽ سڀڪنھن ماڻھوءَ جيڪي ڪمايو تنھن جو کيس پُورو بَدلو ڏبو ۽ اُنھن سان ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(3,26,'3:26','(اي پيغمبر) چؤ ته اي الله مُلڪ جا مالِڪ جنھن کي گھرين تنھن کي بادشاھي ڏئين ۽ جنھن کان وڻيئي تنھن کان بادشاھي کسين، ۽ جنھن کي گھرين تنھن کي مانوارو ڪرين ۽ جنھن کي وڻيئي تنھن کي بي مانو ڪرين، تنھنجي ھٿ ۾ چڱائي آھي، ڇوته تون سڀڪنھن شيء تي وسوارو آھين.');
INSERT INTO "amroti" VALUES(3,27,'3:27','رات کي (گھٽائي) ڏينھن آڻين ٿو ۽ ڏينھن کي (گھٽائي) رات ۾ آڻين ٿو، ۽ مُئي مان جِيئري کي ڪڍين ٿو ۽ جيئري مان مُئي کي ڪڍين ٿو، ۽ جنھن کي گھُرين تنھن کي اَڻ ڳڻي روزي ڏيندو آھين.');
INSERT INTO "amroti" VALUES(3,28,'3:28','(گھُرجي ته) مُؤمن مُؤمنن کانسواءِ ڪافرن کي دوست نه بڻائين، ۽ جيڪو اِئين ڪندو تنھن جو الله (جي دين) سان (ڪُجھ) واسطو نه آھي ھِن کانسواءِ ته اِنھن (ڪافرن) کان پاڻ بچائڻ لاءِ بچاءُ ڪريو، ۽ الله پاڻ کان اوھان کي ڊيڄاريندو آھي، ۽ الله ڏانھن موٽڻو آھي.');
INSERT INTO "amroti" VALUES(3,29,'3:29','(اي پيغمبر کين) چؤ ته جيڪي اوھان جي دلين ۾ آھي سو جي لڪائيندؤ يا اُھو پڌرو ڪندؤ ته (به) اُن کي الله ڄاڻندو آھي، ۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي (سوبه) ڄاڻندو آھي، ۽ الله سڀ ڪنھن شيءِ تي وسوارو آھي.');
INSERT INTO "amroti" VALUES(3,30,'3:30','اُنھيءَ ڏينھن سڀڪو (ماڻھو) جيڪو چڱو ڪم ڪيائين ۽ جيڪو بڇڙو ڪم ڪيائين تنھن کي آڏو لھندو، (بڇڙي ڪم کي ڏسي) گھرندو ته جيڪر سندس ۽ اُن جي وچ ۾ گھڻي ڊيگھ ھجي ھا، ۽ الله پاڻ کان اوھان کي ڊيڄاريندو آھي، ۽ الله ٻانھن تي گھڻو مھربان آھي.');
INSERT INTO "amroti" VALUES(3,31,'3:31','(اي پيغمبر) چؤ ته جي اوھين الله کي دوست رکندا آھيو ته منھنجي تابعداري ڪريو ته الله اوھان کي دوست رکندو ۽ اوھان جا گناھ اوھان کي بخشيندو، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(3,32,'3:32','(اي پيغمبر) چؤ ته الله ۽ پيغمبر جي فرمانبرداري ڪريو، پوءِ جيڪڏھن ڦري ويا ته بيشڪ الله (به) ڪافرن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(3,33,'3:33','بيشڪ الله آدم ۽ نُوح ۽ ابراھيم جي اولاد ۽ عِمران جي اولاد کي جھانن (وارن) تي سدورو ڪيو آھي.');
INSERT INTO "amroti" VALUES(3,34,'3:34','جو پاڻ ۾ ھڪ ٻئي جو نسل آھن، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(3,35,'3:35','جڏھن عِمران جي زال چيو ته اي مُنھنجا پالڻھار جيڪي مُنھنجي پيٽ ۾ آھي سو تولاءِ آجو ڪيل نذر ڪيم تنھنڪري مون کان قبول ڪر، ڇوته تون ئي ٻڌندڙ ڄاڻندڙ آھين.');
INSERT INTO "amroti" VALUES(3,36,'3:36','پوءِ جنھن مھل اُن کي ڄڻيائين تڏھن چيائين ته اي مُنھنجا پالڻھار مون ڌيءُ ڄڻي، ۽ جيڪي اُن (بي بيءَ) ڄڻيو تنھن کي الله چڱو ڄاڻندڙ آھي، ۽ (چيائين ته) پُٽ ڌيءُ جھڙو نه آھي، ۽ مون سندس نالو مريم رکيو ۽ آءٌ اُن کي ۽ سندس نسل کي تڙيل شيطان (جي شر) کان تنھنجي پناھ ۾ سونپيان ٿي.');
INSERT INTO "amroti" VALUES(3,37,'3:37','پوءِ اُن کي سندس پالڻھار چڱي قبولڻ سان قبوليو ۽ اُن کي چڱي پالڻ طرح پاليائين ۽ سندس سنڀاليندڙ زڪريا کي ڪيائين، جڏھن ڪڏھن زڪريا مٿس حُجري ۾ لنگھندو ھو ته وٽس طعام (رکيل) لھندو ھو، (ھڪ وقت) چيائين ته اي مريم ھيءُ ڪٿان تولاءِ (آيو) آھي چيائين ته اِھو الله وٽان (آيو) آھي، چوته الله جنھن کي وڻيس (تنھن کي) اَڻ ڳڻي روزي ڏيندو آھي.');
INSERT INTO "amroti" VALUES(3,38,'3:38','اُتي زڪريا پنھنجي پالڻھار کي سڏيو، چيائين اي منھنجا پالڻھار پاڻ وٽان مون کي چڱو اولاد بخش، ڇو ته تون دُعا جو ٻڌندڙ آھين.');
INSERT INTO "amroti" VALUES(3,39,'3:39','پوءِ مَلائڪن سڏيس ۽ اُن مَسجد ۾ بيٺي نماز پڙھي ٿي (ملائڪن چيس ته) الله توکي يحيىٰ (جي ڄمڻ) جي خوشخبري ڏئي ٿو جو الله جي حُڪم (سان پيدا ٿيل عيسىٰ) جو سَچو ڪندڙ ۽ سردار ۽ جَتو سَتو ۽ صالحن مان پيغمبر آھي.');
INSERT INTO "amroti" VALUES(3,40,'3:40','چيائين ته اي مُنھنجا پالڻھار مون کي پُٽ ڪئن ٿيندو جو بيشڪ مُون کي ته پوڙھائي اچي رسي آھي ۽ مُنھنجي زال سَنڍ آھي، (ملائڪ) چيو ته الله جيڪي گھرندو آھي سو اھڙيءَ طرح ڪندو آھي.');
INSERT INTO "amroti" VALUES(3,41,'3:41','چيائين ته اي منھنجا پالڻھار مُون کاءِ ڪا نشاني مُقرّر ڪر، (الله) فرمايو ته تنھنجي نشاني (ھيءَ) آھي ته ٽي ڏينھن اشاري کانسواءِ ماڻھن سان ڳالھائي نه سگھندين، ۽ پنھنجي پالڻھار کي گھڻو ياد ڪر ۽ صُبح جو ۽ سانجھيءَ جو ساراھِ.');
INSERT INTO "amroti" VALUES(3,42,'3:42','۽ (ياد ڪر) جڏھن ملائڪن چيو ته اي مريم الله توکي سڳورو ڪيو ۽ توکي پاڪ ڪيائين ۽ توکي جھانن جي زالن تي سڳورو ڪيائين.');
INSERT INTO "amroti" VALUES(3,43,'3:43','اي مريم پنھنجي پالڻھار جي فرمانبرداري ڪر ۽ سجدو ڪر ۽ رُڪُوع ڪندڙن سان گڏجي رُڪُوع ڪر.');
INSERT INTO "amroti" VALUES(3,44,'3:44','اِھا ڳجھين خبرن مان آھي اُن کي توڏانھن وحي ڪيوسون، ۽ (تون) تڏھن وٽن نه ھئين جڏھن پنھنجا قلم (پُکن لاءِ واھيءَ ۾) وڌائون ٿي ته اُنھن مان ڪير مريم کي سنڀاليندو، ۽ تڏھن به وٽن نه ھئين جڏھن تڪرار ڪندا رھيا.');
INSERT INTO "amroti" VALUES(3,45,'3:45','جڏھن ملائڪن مريم کي چيو ته اي مريم الله پاڻ وٽان توکي ھڪ (خاص) حُڪم (سان پيدا ڪيل پٽ) جي خوشخبري ڏئي ٿو، سندس نالو عِيسىٰ پُٽ مريم جو مرتبي وارو دنيا ۽ آخرت ۾ ٿيندو ۽ ويجھن مان آھي.');
INSERT INTO "amroti" VALUES(3,46,'3:46','۽ پينگھي ۾ (خاص طرح) ۽ جوانيءَ ۾ ماڻھن سان ڳالھائيندو ۽ صالحن مان آھي.');
INSERT INTO "amroti" VALUES(3,47,'3:47','چيائين ته اي مُنھنجا پالڻھار مون کي ڪئن وياءُ ٿيندو؟ جو مون کي (ڪنھين) ماڻھوءَ نه ڇھيو آھي، (ملائڪن) چيو ته الله جيڪي گھرندو آھي سو اھڙي طرح پيدا ڪندو آھي، جڏھن ڪو ڪم ڪندو آھي ته اُن لاءِ ڇڙو چوندو آھي ته ٿيءُ ته ٿي پوندو آھي.');
INSERT INTO "amroti" VALUES(3,48,'3:48','۽ کيس ڪتاب ۽ دانائي ۽ توريت ۽ انجيل سيکاريندو.');
INSERT INTO "amroti" VALUES(3,49,'3:49','۽ (اُن کي) بني اسرائيلن ڏانھن پيغمبر ڪري موڪليندو، (عيسىٰ وڏو ٿيو ۽ چيائين ته) مون اوھان جي پالڻھار وٽان نشاني آندي آھي، ته آءٌ اوھان لاءِ مٽيءَ مان پکيءَ جي شڪل جھڙو (بوتو) بڻائيندس پوءِ منجھس ڦوڪيندس ته الله جي حُڪم سان پکي ٿيندو، ۽ (ماءُ ڄاول) اَنڌي ۽ ڪُوڙھي کي ڇُٽائيندس ۽ الله جي حُڪم سان مُئي کي جياريندس، ۽ جيڪي کائيندا آھيو ۽ جيڪي پنھنجن گھرن ۾ سانڀيندا آھيو سو اوھان کي ڏسيندس، جيڪڏھن اوھين مڃيندڙ آھيو ته اوھان لاءِ اُن ۾ نشانيون آھن.');
INSERT INTO "amroti" VALUES(3,50,'3:50','۽ جيڪو مون کان اڳ توريت (جو ڪتاب) آيل آھي تنھن جو سچو ڪندڙ آھيان ۽ جيڪي (شيون) اوھان تي حرام ڪيل آھن تن مان ڪي اوھان لاءِ حلال ڪندس ۽ اوھان جي پالڻھار وٽان اوھان لاءِ نشانيون آنديون اٿم، پوءِ الله کان ڊڄو ۽ مُنھنجي تابعداري ڪريو.');
INSERT INTO "amroti" VALUES(3,51,'3:51','بيشڪ الله مُنھنجو پالڻھار ۽ اوھان جو پالڻھار آھي تنھنڪري سندس عبادت ڪريو، اھا سنئين واٽ آھي.');
INSERT INTO "amroti" VALUES(3,52,'3:52','پوءِ جنھن مھل عيسىٰ کائن ڪُفر (جا ڪم) اکين سان ڏٺا (تنھن مھل) چيائين ته الله (جي دين) ڏانھن مُنھنجا واھرو ڪير آھن؟ حوارين چيو ته اسين الله (جي دين) جا واھرو آھيون، الله کي مڃيوسون، ۽ شاھد ھُج ته اسين پَڪ مُسلمان آھيون.');
INSERT INTO "amroti" VALUES(3,53,'3:53','(چيائون) اي اسانجا پالڻھار جيڪي تو لاٿو سو مڃيوسون ۽ پيغمبر جي تابعداري ڪئي سون تنھنڪري اسانکي شاھدن ۾ لک.');
INSERT INTO "amroti" VALUES(3,54,'3:54','۽ ((يھودين) رٿ رٿي ۽ الله به (ڳجھي) رٿ رٿي، ۽ الله چڱو (ڳجھي) رٿ رٿيندڙ آھي.');
INSERT INTO "amroti" VALUES(3,55,'3:55','(ياد ڪر) جڏھن الله چيو ته اي عيسىٰ آءٌ توکي ماريندڙ ۽ پاڻ ڏانھن مٿاھون ڪندڙ آھيان ۽ جن ڪُفر ڪيو تن (جي بھتانن) کان توکي پاڪ ڪندڙ آھيان ۽ جن تنھنجي تابعداري ڪئي تن کي قيامت تائين ڪافرن تي مٿاھون (غالب) ڪندڙ آھيان، وري مون ڏانھن اوھان جو موٽڻ آھي پوءِ اوھان جي وچ ۾ جنھن ڳالھ بابت اوھين تڪرار ڪندا آھيو (تنھن بابت) نبيرو ڪندس.');
INSERT INTO "amroti" VALUES(3,56,'3:56','پوءِ (اُنھن مان) جن ڪُفر ڪيو تن کي دُنيا ۽ آخرت ۾ بلڪل سخت سزا ڏيندس، ۽ اُنھن جو ڪو واھرو نه آھي.');
INSERT INTO "amroti" VALUES(3,57,'3:57','۽ (اُنھن مان) جن ايمان آندو۽ چڱا ڪم ڪيا تن کي (الله) سندن پورھيو پُورو ڏيندو، ۽ الله ظالمن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(3,58,'3:58','اھو (قصّو) جو توتي پڙھون ٿا سو نشانين ۽ حِڪمت واري ڪتاب مان آھي.');
INSERT INTO "amroti" VALUES(3,59,'3:59','الله وٽ عيسىٰ (جي پيدا ڪرڻ) جو مثال آدم جي مثال جھڙوئي آھي، اُن (آدم) کي مِٽيءَ مان بڻايائين وري اُن چيائين ته ٿي پؤ ته ٿي پيو.');
INSERT INTO "amroti" VALUES(3,60,'3:60','(اِھا ڳالھ) تنھنجي پالڻھار وٽان سچي آھي تنھنڪري شڪ ڪندڙن مان نه ھُج.');
INSERT INTO "amroti" VALUES(3,61,'3:61','پوءِ اُن پڄاڻا جو تو وٽ ڄاڻپ آئي جيڪو توسان اِن بابت جھڳڙو ڪري ته چؤ ته اچو ته (اسين) پنھنجن پُٽن ۽ اوھان جي پُٽن ۽ پنھنجين زالن ۽ اوھان جي زالن ۽ پاڻ کي ۽ اوھان کي سڏيون، پوءِ زاريءَ سان دُعا گھرون پوءِ الله جي لَعنت ڪُوڙن تي ڪريون.');
INSERT INTO "amroti" VALUES(3,62,'3:62','اِھو قِصو ضرور سَچّو آھي، ۽ الله کانسواءِ ڪوبه عبادت جو لائق نه آھي، ۽ الله ئي غالب حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(3,63,'3:63','پوءِ جيڪڏھن ڦرن ته بيشڪ الله به فسادين کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(3,64,'3:64','(اي پيغمبر) چؤ ته اي ڪتاب وارؤ ھڪ ڳالھ ڏانھن اچو جا اسان ۽ اوھان جي وچ ۾ ھڪ جھڙي آھي ته الله کانسواءِ ڪنھن جي عبادت نه ڪريون ۽ نڪي ساڻس ڪنھن شيء کي شريڪ ڪريون ۽ نڪي اسان مان ڪو ڪنھن کي (به) الله ڌاران پالڻھار ڪري مڃي، پوءِ جيڪڏھن ڦرن ته چؤ ته شاھد ھُجو ته اسين پڪ مُسلمان آھيون.');
INSERT INTO "amroti" VALUES(3,65,'3:65','اي ڪتاب وارؤ ابراھيم بابت ڇالاءِ جھڳڙو ڪريو ٿا؟ ۽ توريت ۽ انجيل نه لاٿا ويا پر کانئس پوءِ، پوءِ اوھين نه سمجھندا آھيو ڇا؟');
INSERT INTO "amroti" VALUES(3,66,'3:66','(اي يھوديؤ) اوھين اُھي آھيو جو جنھن شيء جي اوھان کي ڄاڻپ آھي تنھن بابت ته جھڳڙو ڪندا آھيو پر جي اوھان کي ڪا ڄاڻپ آھي ئي نه تنھن بابت ڇو جھڳڙو ڪندا آھيو؟ ۽ الله ڄاڻندو آھي ۽ اوھين نه ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(3,67,'3:67','ابراھيم نڪو يھودي ھو ۽ نڪي نصراني پر حَنيف (ھڪ طرفو) مُسلمان ھو، ۽ مُشرڪن مان نه ھو.');
INSERT INTO "amroti" VALUES(3,68,'3:68','ابراھيم کي اُھي ماڻھو ويجھا آھن جن سندس تابعداري ڪئي ۽ ھيءُ پيغمبر ۽ جن مٿس ايمان آندو، ۽ الله مؤمنن جو سنڀاليندڙ آھي.');
INSERT INTO "amroti" VALUES(3,69,'3:69','ڪتاب وارن مان ڪا ٽولي گھرندي آھي ته جيڪر اوھان کي ڀُلائين، ۽ اُھي پاڻ کانسواءِ (ٻئي) ڪنھن کي نه ڀلائيندا آھن ۽ نه پروڙيندا آھن.');
INSERT INTO "amroti" VALUES(3,70,'3:70','اي ڪتاب وارؤ الله جي آيتن کي ڇالاءِ نه ٿا مڃو؟ ۽ حالانڪ اوھين شاھدي ڏيو ٿا.');
INSERT INTO "amroti" VALUES(3,71,'3:71','اي ڪتاب وارؤ ڇالاءِ سچ کي ڪُوڙ سان ملائيندا آھيو ۽ سچ کي لڪائيندا آھيو؟ ھن ھوندي (به) جو اوھين ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(3,72,'3:72','۽ ڪتاب وارن مان ڪا ٽولي (پاڻ ۾) چوي ٿي ته جيڪي مؤمنن تي لاٿو ويو آھي تنھن کي ڏينھن جي اڳياڙيءَ ۾ مڃيو ۽ ان جي پوياڙيءَ ۾ انڪار ڪريو ته مانَ اُھي ڦري وڃن.');
INSERT INTO "amroti" VALUES(3,73,'3:73','۽ جيڪو اوھان جي دين جي تابعداري ڪري تنھن کانسواءِ ٻئي ڪنھن کي نه مڃيو، چئو ته ھدايت الله واري ھدايت آھي ۽ چون ٿا ته ھيءَ به نه مڃيو ته جھڙو (دين) اوھان کي ڏنو ويو آھي تھڙو دين اُنھن کي ڏنو ويندو يا اوھان جي پالڻھار وٽ اوھان کي الزام ڏيندا، چؤ ته ڀلائي الله جي ھٿ ۾ آھي، جنھن کي وڻيس تنھن کي اُھا ڏيندو آھي، ۽ الله (مھربانيءَ ۾) ڪشادگيءَ وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(3,74,'3:74','جنھن کي وڻيس تنھنکي پنھنجي ٻاجھه سان خاص ڪندو آھي، ۽ الله وڏي فضل وارو آھي.');
INSERT INTO "amroti" VALUES(3,75,'3:75','۽ ڪتاب وارن مان ڪي اھڙا آھن جو جيڪڏھن وٽن تمام گھڻو مال امانت رکين ته اُھو (سمورو) توکي ورائي ڏيندا، ۽ منجھائن ڪي اھڙا آھن جو جيڪڏھن وٽن ھِڪ مُھر امانت رکين ته مٿن سدائين بيھڻ کانسواءِ توکي ورائي نه ڏيندا، اِھو (نه ڏئڻ) ھن سببان آھي جو چون ٿا ته اڻ پڙھيلن (جي مال کسڻ) ۾ اسان تي ڪا پڪڙ پڇاڙ نه آھي، ۽ الله تي اھي ڄاڻڻ ھوندي به ڪُوڙ چوندا آھن.');
INSERT INTO "amroti" VALUES(3,76,'3:76','ھائو جنھن پنھنجو انجام پاڙيو ۽ ڊنو ته بيشڪ الله ڊڄندڙن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(3,77,'3:77','جيڪي الله سان ڪيل انجامن ۽ پنھنجن قسمن جي عوض ٿورو مُلھ وٺندا آھن اِھي (اُھي) آھن جن کي آخرت ۾ ڪو بھرو نه آھي ۽ نڪي قيامت جي ڏينھن الله ساڻن ڳالھائيندو ۽ نڪي اُنھن ڏانھن نھاريندو ۽ نڪي کين (گناھن کان) پاڪ ڪندو، ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(3,78,'3:78','۽ منجھائن ھڪ ٽولي آھي جا ڪتاب (پڙھڻ) ۾ پنھنجين زبانن کي ھن لاءِ وچڙائيندي آھي ته ان کي اوھين ڪتاب مان ڀانيو ۽ (حقيقت ڪري) اُھو ڪتاب مان نه آھي، ۽ (پڻ) چوندا آھن ته اُھو الله وٽان آھي ۽ (حقيقت ڪري) اُھو الله وٽان نه آھي، ۽ اُھي ڄاڻڻ ھوندي به الله تي ڪُوڙ چوندا آھن.');
INSERT INTO "amroti" VALUES(3,79,'3:79','ڪنھن به ماڻھوءَ کي نه جڳائيندو آھي جو الله کيس ڪتاب ۽ دانائي ۽ پيغمبري ڏئي اُھو وري ماڻھن کي چوي ته الله کانسواءِ مُنھنجا ٻانھا ٿيو پر (چوندو ته) رب جا (سچا ٻانھان) ٿيو انھيءَ ڪري جو ڪتاب سيکاريو ٿا ۽ پڻ انھيءَ ڪري جو پڙھو ٿا.');
INSERT INTO "amroti" VALUES(3,80,'3:80','۽ ملائڪن ۽ پيغمبرن کي پالڻھار ڪري مڃڻ جو اوھان کي حڪم نه ڪندو، جڏھن اوھين خُدا جا فرمانبردار ٿيا ھجو ته پوءِ اوھان کي ڇو ڪُفر جو حُڪم ڪندو.');
INSERT INTO "amroti" VALUES(3,81,'3:81','۽ (ياد ڪر) جڏھن الله (سڀني) پيغمبرن کان انجام ورتو ته جيڪي ڪتاب ۽ حڪمت اوھان کي ڏيان تنھن کانپوءِ ھڪ پيغمبر جيڪو (علم) اوھان وٽ آھي تنھنجو سچو ڪندڙ اوھان وٽ اچي ته کيس ضرور مڃجو ۽ کيس ضرور مدد ڏجو، (الله) چيو ته اوھين اقرار ڪري چڪؤ ۽ اُن تي مُنھنجي (حُڪم جي) بار کي کنيوَ، چيائون ته اقرار ڪيوسون، (الله) چيو ته اوھين شاھد ھجو، ۽ آءٌ (به) اوھان سان گڏ شاھد آھيان.');
INSERT INTO "amroti" VALUES(3,82,'3:82','پوءِ جيڪي ھن پڃاڻان ڦرندا سي ئي بي دين آھن.');
INSERT INTO "amroti" VALUES(3,83,'3:83','الله جي دين کانسواءِ ڇو ٻيو (دين) طلبيندا آھن ۽ جيڪي آسمانن ۽ زمين ۾ آھن تن سرھو توڙي ارھو ٿي کيس مڃيو آھي ۽ ڏانھس موٽايا ويندا.');
INSERT INTO "amroti" VALUES(3,84,'3:84','(اي پيغمبر) چؤ ته اسان الله کي ۽ جيڪي اسان تي لاٿو ويو آھي تنھن کي ۽ جيڪي ابراھيم ۽ اسماعيل ۽ اسحٰق ۽ يعقوب ۽ (اُن جي) اولاد تي لاٿو ويو ۽ جيڪي موسٰي ۽ عيسٰي ۽ (ٻين) پيغمبرن کي سندن پالڻھار کان ڏنو ويو تنھن کي مڃيون ٿا، اُنھن مان ڪنھن ھڪ ۾ فرق نه ٿا ڀانيون، ۽ اسان اُن (الله) کي مڃيندڙ آھيون.');
INSERT INTO "amroti" VALUES(3,85,'3:85','۽ جيڪو اسلام ڌاران ٻيو دين طلبيندو تنھن کان اُھو ڪڏھن نه قبول ڪبو، ۽ اُھو آخرت ۾ ڇيئي وارن مان آھي.');
INSERT INTO "amroti" VALUES(3,86,'3:86','الله اُن قوم کي ڪئن ھدايت ڪندو جي پنھنجي اِيمان آڻڻ کانپوءِ ڪافر ٿيا ۽ (پھريائين) اقرار ڪيائون ته پيغمبر سچو آھي ۽ وٽن معجزا (به) آيا، ۽ الله ظالم ٽولي کي ھدايت نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(3,87,'3:87','اِھي اُھي آھن جن تي سزا (ھيءَ) آھي ته مٿن الله ۽ ملائڪن ۽ مڙني ماڻھن جي لَعنت آھي.');
INSERT INTO "amroti" VALUES(3,88,'3:88','اُن ۾ سدائين رھڻ وارا آھن، کانئن عذاب ھلڪو نه ڪبو ۽ نه اُنھن کي مُھلت ڏبي.');
INSERT INTO "amroti" VALUES(3,89,'3:89','پر جن اُن کانپوءِ توبه ڪئي ۽ (پاڻ) سڌاريائون، ته بيشڪ الله (به) بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(3,90,'3:90','جن پنھنجي ايمان آڻڻ کانپوءِ ڪُفر ڪيو وري ڪُفر ۾ وڌيا تن جي توبه ڪڏھن قبول نه ڪبي، ۽ اُھي ئي ڀليل آھن.');
INSERT INTO "amroti" VALUES(3,91,'3:91','جن ڪُفر ڪيو ۽ اُھي ڪافر ٿي مُئا تن مان ڪو ھڪڙو اگرچه سڄيءَ زمين جيترو سونُ (پنھنجن گناھن جي) عوض ڏئي ته به اُھو اُن کان قبول نه ڪبو، اِھي (اُھي) آھن جن لاءِ ڏکوئيندڙ عذاب آھي ۽ اُنھن جو ڪو واھرو ڪونھي.');
INSERT INTO "amroti" VALUES(3,92,'3:92','چڱائيءَ کي ايسين ڪڏھن نه پھچندؤ جيسين جن شين کي دوست رکندا آھيو تن مان (الله جي واٽ ۾ نه) خرچيو، ۽ جيڪا شيء (الله لڳ خرچيو سا ڀَلي ھئڻ گھرجي) ڇوته الله اُن کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(3,93,'3:93','سڀ (ڪنھن قسم جو) طعام بني اسرائيلن لاءِ حلال ھو پر جيڪي يعقوب پاڻ تي حرام ڪيو ھو سو توريت جي لھڻ کان اڳ ھو، (اي پيغمبر) چؤ ته جيڪڏھن اوھين سچا آھيو ته توريت آڻيو پوءِ اُھو پڙھو.');
INSERT INTO "amroti" VALUES(3,94,'3:94','پوءِ جيڪي ھن کانپوءِ الله تي ڪوڙ ٺاھيندا سي ئي ظالم آھن.');
INSERT INTO "amroti" VALUES(3,95,'3:95','(اي پيغمبر) چؤ ته الله سچ چيو آھي، پوءِ ابراھيم حنيف (ھڪ طرفي) جي دين جي تابعداري ڪريو، ۽ (ابراھيم) مشرڪن مان نه ھو.');
INSERT INTO "amroti" VALUES(3,96,'3:96','(عبادت جو) پھريون گھرُ جو ماڻھن لاءِ بڻايو ويو سو اُھو ئي آھي جو مَڪي ۾ برڪت وارو ۽ جڳ لاءِ ھدايت آھي.');
INSERT INTO "amroti" VALUES(3,97,'3:97','جنھن ۾ پڌريون نشانيون آھن (ھڪڙي) مقام ابراھيم آھي (جو اُن ۾ ابراھيم جي پيرن جو نشان آھي)، ۽ جيڪو اُتي داخل ٿيو سو امن وارو آھي، ۽ الله لاءِ خانه ڪعبه جو حج اُنھن ماڻھن تي لازم آھي جنھن کي ڏانھس واٽ وڃڻ جي سگھه آھي، ۽ جنھن انڪار ڪيو (ته الله کي پرواہ نه آھي) ڇو ته الله جھانن کان بي پرواہ آھي.');
INSERT INTO "amroti" VALUES(3,98,'3:98','(اي پيغمبر کين) چؤ ته اي ڪتاب وارؤ الله جي آيتن کي ڇونه ٿا مڃيو؟ ۽ (حقيقت ڪري) جيڪي ڪندا آھيو تنھن تي الله حاضر آھي.');
INSERT INTO "amroti" VALUES(3,99,'3:99','(اي پيغمبر) چؤ ته اي ڪتاب وارؤ جيڪو ايمان آڻي تنھن کي الله جي واٽ کان اُن (واٽ) جي ڏنگائي گھُري ڇو ٿا جھليو؟ ھن ھوندي به جو اوھين ڄاڻندڙ آھيو، ۽ جيڪي (اوھين) ڪندا آھيو تنھن کان الله بي خبر نه آھي.');
INSERT INTO "amroti" VALUES(3,100,'3:100','اي ايمان وارؤ جيڪڏھن ڪتاب وارن مان ڪنھن به ٽوليءَ جي فرمانبرداري ڪندؤ ته اوھان جي ايمان آڻڻ کان پوءِ اوھان کي ڪافر ڪري ڦيرائيندا.');
INSERT INTO "amroti" VALUES(3,101,'3:101','۽ (اوھين) ھن ھوندي ڪئن ڪافر ٿا ٿيو؟ جو الله جون آيتون اوھان تي پڙھجن ٿيون ۽ سندس پيغمبر اوھان ۾ آھي، ۽ جيڪو الله (جي دين) کي چنبڙي پٺندو تنھن کي بيشڪ سڌيءَ واٽ ڏانھن رستو ڏيکاريو ويندو.');
INSERT INTO "amroti" VALUES(3,102,'3:102','اي مؤمنؤ الله کان (اِئين) ڊڄو جئن کانئس ڊڄڻ جو حق آھي ۽ اوھين مسلمان ھجڻ کانسواءِ نه مرجو.');
INSERT INTO "amroti" VALUES(3,103,'3:103','۽ الله جي رسيءَ (يعني قرآن) کي سڀئي چنبڙي وٺو ۽ ڌاروڌار نه ٿيو، ۽ پاڻ تي الله جي نعمت کي ياد ڪريو جو جڏھن پاڻ ۾ ويري ھيؤ پوءِ اوھان جي دلين ۾ ميلاپ وڌائين پوءِ سندس فضل سان ھڪ ٻئي جا ڦري ڀائر ٿيؤ، ۽ اوھين دوزخ جي کڏ جي ڀَر تي ھيؤ پوءِ اوھان کي کانئس ڇڏايائين، اھڙيءَ طرح الله اوھان لاءِ پنھنجيون نشانيون بيان ڪندو آھي ته مانَ اوھين ھدايت لھو.');
INSERT INTO "amroti" VALUES(3,104,'3:104','۽ اوھان ۾ اھڙي ٽولي ھئڻ گھرجي جي چڱائيءَ ڏانھن سڏين ۽ چڱن ڪمن جو حُڪم ڪن ۽ مَدن ڪمن کان جھلين، ۽ اھي ئي ڇٽل آھن.');
INSERT INTO "amroti" VALUES(3,105,'3:105','۽ اوھين اُنھن جھڙا نه ٿيو جي فرقو فرقو ٿي ويا ۽ وٽن معجزا آيا اُن کانپوءِ بهتڪرار ڪيائون، ۽ اُنھن لاءِ وڏو عذاب آھي.');
INSERT INTO "amroti" VALUES(3,106,'3:106','اُنھيءَ ڏينھن جو ڪي مُنھن اڇا ٿيندا ۽ ڪي مُنھن ڪارا ٿيندا، پوءِ جن جا مُنھن ڪارا ٿيندا (چيو ويندن ته) اوھين پنھنجي اِيمان (آڻڻ) کانپوءِ ڇو ڪافر ٿيؤ؟ پوءِ اُنھيءَ سببان جو اوھين ڪافر ٿيا ھيؤ عذاب (جو مزو) چکو.');
INSERT INTO "amroti" VALUES(3,107,'3:107','۽ جن جا منھن اڇا ھوندا سي الله جي رحمت ھيٺ رھندا، اُھي منجھس سدائين رھڻ وارا ھوندا.');
INSERT INTO "amroti" VALUES(3,108,'3:108','(اي پيغمبر) اِھي الله جون آيتون آھن جي (اسين) توتي سچيءَ طرح پڙھون ٿا، ۽ الله جھانن لاءِ ظُلم ڪرڻ نه گھرندو آھي.');
INSERT INTO "amroti" VALUES(3,109,'3:109','۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي (سو خاص) الله جو آھي، ۽ (سڀ) ڪم الله ڏانھن موٽايا ويندا.');
INSERT INTO "amroti" VALUES(3,110,'3:110','اوھين اھڙي ڀَلي اُمت ماڻھن لاءِ پيدا ٿيل آھيو جو چڱن ڪمن جو حُڪم ڪندا آھيو ۽ مدن ڪمن کان جھليندا آھيو ۽ الله کي مڃيندا آھيو، ۽ جيڪڏھن ڪتاب وارا ايمان آڻين ھا ته ضرور کين ڀَلو ھُجي ھا، منجھانئن ڪي مؤمن آھن ۽ منجھانئن گھڻا بي دين آھن.');
INSERT INTO "amroti" VALUES(3,111,'3:111','(اِھي زباني) اِيذاءُ کانسواءِ اوھان کي ڪڏھن ڏکوئي نه سگھندا، ۽ جيڪڏھن اوھان سان وڙھندا ته اوھان کي پُٺي ڏيئي ڀڄندا، وري مدد نه ڏبن.');
INSERT INTO "amroti" VALUES(3,112,'3:112','الله جي ذمي ۽ ماڻھن جي پناھ کانسواءِ جتي لڌا ويندا اُتي مٿن خواري ھنئي ويئي آھي ۽ الله جي ڏمر ھيٺ وري وريا ۽ مٿن محتاجي ھنئي ويئي آھي، اِھا (سزا) ھن سببان آھي جو الله جي آيتن کي نه ٿي مڃيائون ۽ پيغمبرن کي ناحق ٿي ڪُٺائون، اِھي (عادتون) اِنھيءَ سبب آھن جو بي فرماني ڪيائون ۽ حد کان لنگھندڙ ھئا.');
INSERT INTO "amroti" VALUES(3,113,'3:113','اِھي (سڀ) ھڪ جھڙا نه آھن، ڪتاب وارن مان ھڪڙي اھڙي سڌي ٽولي آھي جو الله جون آيتون رات جي وقتن ۾ پڙھندا آھن ۽ اُھي نماز (به) پڙھندا آھن.');
INSERT INTO "amroti" VALUES(3,114,'3:114','الله ۽ قيامت جي ڏينھن کي مڃيندا آھن ۽ چڱن ڪمن جو حڪم ڪندا آھن ۽ مَدن ڪمن کان جھليندا آھن ۽ چڱاين (يعني دين مڃڻ) ۾ اڳرائي ڪندا آھن، ۽ اِھي ڀلارن مان آھن.');
INSERT INTO "amroti" VALUES(3,115,'3:115','۽ جيڪا ڪا به چڱائي ڪندا آھن تنھن جي بي قدري پٽيءَ نه ڪبي، ۽ الله پرھيزگارن کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(3,116,'3:116','جن اِنڪار ڪيو تن کان نڪي سندن مال سندن اولاد الله جو ڪُجھ (عذاب) ٽاري سگھندا، ۽ اُھي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(3,117,'3:117','ھن دنيا جي حياتيءَ ۾ جيڪي (رياءُ لاءِ) خرچيندا آھن تنھن جو مثال اُنھيءَ واءُ جي مثال وانگر آھي جنھن ۾ پارو ھجي جو اُن ٽوليءَ جي پوکه کي پھچي جن پاڻ تي ظلم ڪيو پوءِ اُن (سندن پوکه) کي ناس ڪري، ۽ الله ساڻن ظلم نه ڪيو آھي پر اُھي پاڻ تي ظلم ڪن ٿا.');
INSERT INTO "amroti" VALUES(3,118,'3:118','اي اِيمان وارؤ پنھنجن (مسلمانن) کانسواءِ ٻين (قومن) کي ڳجھو دوست ڪري نه مڃيو (جو) اوھان جي خرابي ڪرڻ ۾ ڪا گھٽتائي نه ڪندا آھن، اوھان جي ڏکه کي دوست رکيائون، بيشڪ سندن واتن مان دشمني پڌري ٿي آھي، ۽ جيڪي (ضد) سندن سينن لڪايو آھي سو تمام وڏو آھي، بيشڪ اسان اوھان لاءِ نشانيون بيان ڪيون آھن جيڪڏھن سمجھندا آھيو.');
INSERT INTO "amroti" VALUES(3,119,'3:119','اوھين اُھي آھيو جو اُنھن کي دوست رکندا آھيو ۽ اُھي اوھان کي دوست نه رکندا آھن ۽ اوھين الله جي سڄي ڪتاب کي مڃيندا آھيو، ۽ جڏھن اوھان کي مِلن ٿا (تڏھن) چون ٿا ته مڃيوسين، ۽ جڏھن ھيڪلا ٿين ٿا (تڏھن) اوھان تي ڪاوڙ کان آڱرين کي چٻين ٿا، (اي پيغمبر کين) چؤ ته (اوھين) پنھنجيءَ ڪاوڙ سببان مرو، ڇوته الله سينن وارو (ڀيد) ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(3,120,'3:120','(اي مؤمنؤ) جيڪڏھن اوھان کي ڪا چڱائي پھچندي آھي ته کين ڏکوئيندي آھي، ۽ جيڪڏھن اوھان کي ڪو ڏکه پھچندو آھي ته اُن سببان سرھا ٿيندا آھن، ۽ جيڪڏھن اوھين صبر ڪريو ۽ پرھيزگاري ڪريو ته سندن مڪر اوھان کي ڪجھ به نه ڏکوئيندو، ڇوته جيڪي ڪندا آھن تنھن کي الله گھيرو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(3,121,'3:121','(اي پيغمبر ياد ڪر) جنھن مھل (تون) پنھنجي گھر مان سويرو نڪتين جو مؤمنن کي جنگ لاءِ مورچن تي وھاريئي ٿي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(3,122,'3:122','(ياد ڪر) جنھن مھل اوھان مان ٻن ٽولين بي ھمت ٿيڻ جو ارادو ڪيو ۽ الله سندن مددگار ھو، ۽ مؤمنن کي جڳائي ته الله تي ڀروسو ڪن.');
INSERT INTO "amroti" VALUES(3,123,'3:123','۽ بيشڪ الله اوھان کي اُنھيءَ حالت ۾ جو اوھين بي زور ھيؤ بدر ۾ سوڀ ڏني، پوءِ الله کان ڊڄو مانَ اوھين شڪر ڪريو.');
INSERT INTO "amroti" VALUES(3,124,'3:124','(اي پيغمبر اُنھيءَ مھل کي ياد ڪر) جنھن مھل تو مؤمنن کي چيو ٿي ته اوھان کي اوھان جي پالڻھار جي ٽن ھزارن ملائڪن (آسمان کان) لاٿلن سان مدد ڪرڻ ڪافي نه آھي ڇا؟');
INSERT INTO "amroti" VALUES(3,125,'3:125','ھائو جيڪڏھن اوھين صبر ڪريو ۽ پرھيزگار بڻجو ۽ ھڪدم اھي (ڪافر) اوھان تي اچي ڪڙڪن ته اوھان جو پاڻھار پنجن ھزارن ملائڪن نشان ڪيلن سان اوھان کي مدد ڏيندو.');
INSERT INTO "amroti" VALUES(3,126,'3:126','۽ الله اُن (مدد) کي رڳو اوھان جي خوشخبريءَ لاءِ ڪيو آھي ۽ ھن لاءِ ته اوھان جون دليون اُن سان (ڀَلي ته) آرام وٺن، ۽ الله غالب حڪمت واري جي پار کانسواءِ ڪا مدد (ملڻي) نه آھي.');
INSERT INTO "amroti" VALUES(3,127,'3:127','(۽) ھن لاءِ ته ھڪڙو پاسو (يعني ڀاڱو) ڪافرن جو ڇني ڇڏي يا کين ھيڻو ڪري ڇڏي ته بي نصيب ٿي ڦِرن.');
INSERT INTO "amroti" VALUES(3,128,'3:128','(اي پيغمبر) اُن ڪم ۾ تنھنجو ڪو واسطو نه آھي (خداءِ) يا مٿن ٻاجھ سان موٽندو يا کين عذاب ڪندو ڇو ته اُھي ظالم آھن.');
INSERT INTO "amroti" VALUES(3,129,'3:129','۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي (سو خاص) الله جو آھي، جنھن کي گھرندو آھي تنھن کي بخشيندو آھي ۽ جنھن کي گھرندو آھي تنھن کي عذاب ڪندو آھي، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(3,130,'3:130','اي ايمان وارؤ (اوھين) وياج ٻيڻو چؤڻو نه کائو، ۽ الله کان ڊڄو مانَ اوھين ڇٽو.');
INSERT INTO "amroti" VALUES(3,131,'3:131','۽ اُن باھ کان ڊڄو جا ڪافرن لاءِ تيار ڪئي ويئي آھي.');
INSERT INTO "amroti" VALUES(3,132,'3:132','۽ الله ۽ رسول جي فرمانبرداري ڪريو ته مانَ اوھان تي ٻاجھ ڪئي وڃي.');
INSERT INTO "amroti" VALUES(3,133,'3:133','۽ (اوھين) پنھنجي پالڻھار جي بخشش ۽ اُنھيءَ بھشت ڏانھن اڳرائي ڪريو جنھن جي ويڪر آسمانن ۽ زمين جيتري آھي اُنھن پرھيزگارن لاءِ تيار ڪيو ويو.');
INSERT INTO "amroti" VALUES(3,134,'3:134','جيڪي سُک ۽ ڏک ۾ (الله ڪارڻ) خرچيندا آھن ۽ ڪاوڙ کي کائيندڙ ۽ ماڻھن (جي ڏوہ) کان ٽارو ڪندڙ آھن، ۽ الله ڀلارن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(3,135,'3:135','۽ اُھي جڏھن ڪو بي حيائيءَ جو ڪم ڪندا آھن يا پاڻ تي ظلم ڪندا آھن (تڏھن) الله کي ياد ڪندا آھن ۽ پنھنجن گناھن جي بخشش گھرندا آھن، ۽ الله کانسواءِ ڪير گناہ بخشيندو؟ ۽ جيڪي ڪيائون تنھن تي ڄاڻي ٻجھي ھميشگي نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(3,136,'3:136','اُنھن جو اَجر سندن پالڻھار وٽان بخشش ۽ بھشت آھن جن جي ھيٺان واھيون وھنديون آھن منجھن سدائين رھڻ وارا آھن، ۽ ڪمائيندڙن جو اَجر چَڱو آھي.');
INSERT INTO "amroti" VALUES(3,137,'3:137','بيشڪ اوھان کان اڳ ڪيترا واقعا گذريا آھن پوءِ (اوھين) ملڪ ۾ گھمو ڦرو پوءِ نھاريو ته نه مڃيندڙن جي پڇاڙي ڪئن ھئي.');
INSERT INTO "amroti" VALUES(3,138,'3:138','ھيءُ ماڻھن لاءِ بيان آھي ۽ پرھيزگارن لاءِ ھدايت ۽ نصيحت آھي.');
INSERT INTO "amroti" VALUES(3,139,'3:139','۽ نڪي سُست ٿيو ۽ نڪي غمگين ٿيو ۽ جيڪڏھن مؤمن آھيو ته (رڳو) اوھين غالب (ٿيڻ وارا) آھيو.');
INSERT INTO "amroti" VALUES(3,140,'3:140','جيڪڏھن اوھان کي (اُحُد ۾) ڪو گھاءُ پھتو آھي ته بيشڪ اُن جھڙو گھاءُ ڪافرن کي (بَدۡر ۾) پُھتو ھو، ۽ ھيءُ اھڙا واقعا آھن جو (اسين) اُنھن ماڻھن جي وچ ۾ ڦيرائيندا آھيون (ته نصيحت وٺن)، ۽ ھن لاءِ ته الله مؤمنن کي نِکيڙي ۽ اوھان مان ڪن کي شھيد ڪري، ۽ الله ظالمن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(3,141,'3:141','۽ ھن لاءِ ته الله مؤمنن کي کرو ڪري ۽ ڪافرن کي ناس ڪري.');
INSERT INTO "amroti" VALUES(3,142,'3:142','اوھان بھشت ۾ گِھڙڻ جو گمان ڪيو آھي ڇا؟ ھن ھوندي جو الله اڃا اوھان مان مجاھدن کي نه نِکيڙيو آھي ۽ نڪي صابرن کي نِکيڙيو آھي.');
INSERT INTO "amroti" VALUES(3,143,'3:143','۽ بيشڪ اوھين موت کي اُن جي ملڻ کان اڳ گھُرندا ھيؤ، پوءِ بيشڪ ان کي ڏٺوَ ۽ اوھين نھاريندا ھيؤ.');
INSERT INTO "amroti" VALUES(3,144,'3:144','۽ محمد (صلى الله عليه وسلم الله جو) پيغمبر آھي، بيشڪ اُن کان اڳ ڪيترا پيغمبر گذري ويا، پوءِ جيڪڏھن (اُھو) مري وڃي يا قتل ٿئي ته (اوھين) پنھنجين کڙين (ڀَر پوءِ) تي ڦرندؤ ڇا؟ ۽ جيڪو پنھنجين کڙين (ڀَرپوءِ) تي ڦرندو سو الله کي ڪجھ نقصان ڪڏھن نه لائيندو، ۽ الله شاڪرن (احسان مڃيندڙن) کي ستت ثواب ڏيندو.');
INSERT INTO "amroti" VALUES(3,145,'3:145','۽ الله جي ٺھرايل وقت لکيل حُڪم کانسواءِ ڪنھن کي مرڻو نه آھي، ۽ جيڪو دنيا جو ثواب گھرندو تنھن کي اُن مان ڏينداسون، ۽ جيڪو آخرت جو ثواب گھرندو تنھن کي اُن مان ڏينداسون، ۽ شاڪرن کي (چڱو) ثواب ستت ڏينداسون.');
INSERT INTO "amroti" VALUES(3,146,'3:146','۽ پيغمبرن مان گھڻا (ڪافرن سان) وڙھيا ساڻن گھڻا خداءِ وارا ھئا، پوءِ جيڪي الله جي واٽ ۾ کين پھتو تنھن سببان نڪي سُست ٿيا ۽ نڪي ھيڻا ٿيا ۽ نڪي عاجزي ڪيائون، ۽ الله صابرن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(3,147,'3:147','۽ سندن چوڻ (ھن) چوڻ کانسواءِ نه ھو ته اي اسان جا پالڻھار اسان کي اسان جاگناہ (بخش) ۽ اسان جي ڪم ۾ اسان جي بيجا ھلت بخش ۽ اسان جا پير ثابت رک ۽ اسان کي ڪافرن جي ٽوليءَ تي فتح ڏي.');
INSERT INTO "amroti" VALUES(3,148,'3:148','پوءِ الله کين دنيا جو ثواب ۽ آخرت جو چڱو ثواب ڏنو، ۽ الله ڀلارن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(3,149,'3:149','اي اِيمان وارؤ جيڪڏھن اوھين ڪافرن جو چيو مڃيندؤ ته اوھان کي اوھان جي کڙين (ڀَرپوءِ) تي ڦيرائيندا پوءِ اوھين ڦِري ڇيئي وارا ٿيندؤ.');
INSERT INTO "amroti" VALUES(3,150,'3:150','بلڪ الله اوھان جو سائين آھي، ۽ اُھو چڱو مدد ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(3,151,'3:151','ڪافرن جي دلين ۾ ھن سببان سگھو دھشت وجھنداسون جو الله سان اھڙيءَ شيءِ کي شريڪ ڪيائون جنھن لاءِ الله ڪا سَنَد نه لاٿي آھي، ۽ سندن جاءِ باہ آھي، ۽ (اُھو) ظالمن جو ھنڌ بڇڙو آھي.');
INSERT INTO "amroti" VALUES(3,152,'3:152','۽ بيشڪ الله اوھان سان پنھنجو وعدو سچو ڪيو آھي جڏھن ته اُنھن (ڪافرن) کي ايستائين سندس حُڪم سان ڪُٺوَ، جيستائين بي ھمت ٿيؤ ۽ ڪم (پوري ڪرڻ) ۾ تڪرار ڪيوَ ۽ جيڪي دوست رکندا ھيؤ سو اوھان کي ڏيکاريائين تنھن کانپوءِ بي فرماني ڪيوَ، اوھان مان ڪو (اھڙو) آھي جو دُنيا کي گھرندو آھي ۽ اوھان مان ڪو (اھڙو) آھي جو آخرت کي گھرندو آھي، وري اوھان کي اُنھن کان ھن لاءِ ڦيرايائين ته اوھان کي پرکي، ۽ بيشڪ اوھان کي معافي ڏنائين، ۽ الله مؤمنن تي فضل ڪرڻ وارو آھي.');
INSERT INTO "amroti" VALUES(3,153,'3:153','جڏھن اوھين پري ٿي ڀَڳؤ ۽ ڪنھن ھڪ تي (ڪنڌ ڦيري) نه ٿي واجھايوَ ۽ پيغمبر اوھان کي اوھان جي پُٺيان سڏيندو رھيو پوءِ (خداءِ) اوھان کي ڏک تي ڏک جو بدلو ڏنو (اِھو) ھن لاءِ ته جيڪي اوھان کان ٿي ويو نڪي تنھن تي ۽ جيڪي اوھان کي پھتو نڪي تنھن تي غمگين ٿيو، ۽ جيڪي اوھان ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(3,154,'3:154','وري ڏک کانپوءِ اوھان تي آرام (جا) اوجھراڪي (ھئي) لاٿائين جو اوھان مان ھڪ ٽوليءَ کي ڍڪيندي رھي ۽ ٻي ٽولي ھئي جنھن کي سندن نفسن غمگين ڪيو ھو ناحق جاھليت جو گمان الله تي ڀانيائون ٿي، چيائون ٿي ته اسان لاءِ اِنھيءَ ڪم مان ڇا ڪجھ (ٿيڻو) آھي، (اي پيغمبر) چؤ ته سڀ اختيار الله جو ئي آھي، جيڪي توتي پڌرو نه ڪندا آھن سو پنھنجين دلين ۾ لڪائيندا آھن، چوندا آھن ته جيڪڏھن اسان جي وس ۾ ڪجھ به ڪم ھجي ھا ته اسين ھِتِ نه ڪُسون ھا، (اي پيغمبر کين) چؤ ته جيڪڏھن اوھين پنھنجن گھرن ۾ ھجو ھا ته جن ڪُسڻ لکيل ھو سي ضرور پنھنجي ڪوس جي جاين ڏانھن نڪرن ھا، ۽ (ھن لاءِ) ته جيڪي اوھان جي سينن ۾ آھي سو الله پرکي ۽ جيڪي اوھان جي دلين ۾ آھي سو کرو ڪري، ۽ الله سينن وارو (ڀيد) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(3,155,'3:155','اوھان مان جن اُنھيءَ ڏينھن مُنھن موڙيو جو ٻه ٽوليون (ھڪ ٻئي جي) سامھون ٿيون ته اُنھن کي شيطان سندن ڪجھ ڪِئي جي شامت کان سواءِ نه ٿيڙيو، ۽ بيشڪ الله اُنھن کي معافي ڏني، ڇوته الله بخشڻھار بُردبار آھي.');
INSERT INTO "amroti" VALUES(3,156,'3:156','اي ايمان وارؤ اُنھن ڪافرن وانگر نه ٿيو جي پنھنجن ڀائرن لاءِ جڏھن اُھي ملڪ ۾ مسافري ڪن يا لڙائي ڪندڙ ھجن (تڏھن) چون ٿا ته جيڪڏھن اسان وٽ ھجن ھا ته نه مرن ھا ۽ نڪي ڪُسن ھا، ته الله اُھو اَرمان سندن دلين ۾ ڪري، ۽ الله جياريندو ۽ ماريندو آھي، ۽ جيڪي اوھين ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(3,157,'3:157','۽ جيڪڏھن اوھين الله جي واٽ ۾ ڪُسي پيؤ يا مري پيؤ ته جيڪي گڏ ڪندا آھن تنھن کان الله جي بخشش ۽ ٻاجھ (بلڪل) ڀلي آھي.');
INSERT INTO "amroti" VALUES(3,158,'3:158','۽ جيڪڏھن (اوھين) مري وڃو يا ڪُسي پئو ته ضرور الله ڏانھن گڏ ڪيا ويندؤ.');
INSERT INTO "amroti" VALUES(3,159,'3:159','پوءِ (اي پيغمبر) الله جي ٻاجھ سببان اُنھن لاءِ نرم ٿيو آھين، ۽ جيڪڏھن ڪاوڙيل سخت سخت دل ھجين ھا ته ضرور تنھنجي آسپاس کان ڀڄي اُٿن ھا، پوءِ کين معافي ڏي ۽ اُنھن لاءِ بخشش گھُر ۽ ساڻن ڪم ۾ صلاح ڪر، پوءِ جڏھن تون پڪو اِرادو ڪرين تڏھن الله تي ڀروسو ڪر، ڇوته الله ڀروسي ڪرڻ وارن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(3,160,'3:160','جيڪڏھن الله اوھان کي مدد ڏيندو ته اوھان تي ڪو غالب (ٿيڻو) نه آھي، ۽ جيڪڏھن اوھان کي مدد نه ڏيندو ته اُن کانپوءِ ڪير اوھان کي مدد ڏيندو؟ ۽ مؤمنن کي جڳائي ته الله تي ڀروسو ڪن.');
INSERT INTO "amroti" VALUES(3,161,'3:161','۽ ڪنھين پيغمبر کي (غنيمت جي مال ۾) خيانت ڪرڻ نه جڳائي، ۽ جيڪو خيانت ڪندو سو جيڪا شيء خيانت ڪيائين تنھن سميت قيامت جي ڏينھن ايندو، پوءِ سڀڪنھن ماڻھوءَ جيڪي ڪمايو تنھن جو پورو بدلو کيس ڏنو ويندو ۽ اُنھن تي ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(3,162,'3:162','جيڪو الله جي رضامنديءَ جي پٺيان لڳو سو اُن جھڙو آھي ڇا؟ جيڪو الله جي ڪاوڙ ھيٺ ڦري آيو ۽ سندس جاءِ دوزخ آھي، ۽ (اُھو) ورڻ جو ھنڌ بُڇڙو آھي.');
INSERT INTO "amroti" VALUES(3,163,'3:163','اُھي الله وٽ (وڏن) درجن وارا آھن، ۽ جيڪي ڪندا آھن سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(3,164,'3:164','بيشڪ الله مؤمنن تي وڏو احسان ڪيو آھي جڏھن ته انھن ۾ سندن قوم مان پيغمبر موڪليائين جو کين سندس آيتون پڙھي ٻڌائيندو آھي ۽ کين پاڪ ڪندو آھي ۽ کين ڪتاب ۽ دانائي سيکاريندو آھي، بيشڪ ھن کان اڳ پڌريءَ ڀل ۾ (ڀليل) ھؤا.');
INSERT INTO "amroti" VALUES(3,165,'3:165','۽ جڏھن اوھانکي (جنگ اُحد ۾) ڏک پھتو توڻيڪ (پنھنجن دشمنن کي) اُن جو ٻيڻو (ڏُک بَدۡر ۾) پھچايو ھيو ته به چيوَ ٿي ته ھي (مصيبت) ڪٿان (پھتي) آھي، (اي پيغمبر) چؤ ته اُھا اوھان کي پاڻ وٽان (پھتي) آھي، ڇوته الله سڀڪنھن شيء تي وسوارو آھي.');
INSERT INTO "amroti" VALUES(3,166,'3:166','۽ اُنھيءَ ڏينھن جو ٻه ٽوليون پاڻ ۾ سامھون ٿيون (تنھن ڏينھن) جيڪي اوھانکي پھتو سو الله جي حُڪم سان ھو ۽ ھن لاءِ ته مؤمنن کي نِکيڙي.');
INSERT INTO "amroti" VALUES(3,167,'3:167','۽ منافقن کي به نِکيڙي، ۽ کين چيو ويو ته اچو الله جي واٽ ۾ وڙھو يا (ڪافرن کي) جواب ڪريو، چيائون ته جيڪڏھن اسان کي ڪنھن جنگ ٿيڻ جي خبر ھجي ھا ته ضرور اوھان سان ھجون ھا، اُھي اُن ڏينھن پاڻ اِيمان کان ڪفر ڏي بلڪل ويجھو ھؤا، پنھنجن واتن سان اُھي (ڳالھيون) چوندا آھن جيڪي سندين دلين ۾ نه آھن، ۽ جيڪي لڪائيندا آھن سو الله بلڪل ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(3,168,'3:168','(ته) اِھي اُھي آھن جن پنھنجن ڀائرن کي چيو ۽ ويٺا رھيا ته جيڪڏھن (اُھي) اسان جو چيو مڃن ھا ته نه ڪُسن ھا، (اي پيغمبر کين) چؤ ته جيڪڏھن اوھين سچا آھيو ته پاڻ کان موت کي ٽاريو.');
INSERT INTO "amroti" VALUES(3,169,'3:169','۽ جيڪي الله جي واٽ ۾ ڪُٺا ويا تن کي مئل پٽيءَ نه ڀانءِ، بلڪ (اُھي) جيئرا آھن (۽) پنھنجي پالڻھار وٽ روزي ڏنل آھن.');
INSERT INTO "amroti" VALUES(3,170,'3:170','جيڪي الله اُنھن کي پنھنجي فضل سان ڏنو آھي تنھن (حال) سان خوش ٿيندڙ آھن ۽ جيڪي سندن پٺيان آھن تن مان جيڪي ساڻن نه گڏيا آھن اُنھن لاءِ ھن ڪري خوش ٿيندا آھن ته کين ڪو ڀؤ نه آھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(3,171,'3:171','الله جي نعمت ۽ فضل سان خوش ٿيندا آھن ۽ الله مؤمنن جو اجر نه وڃائيندو آھي.');
INSERT INTO "amroti" VALUES(3,172,'3:172','جن پاڻ کي ڦٽ پھچڻ کان پوءِ به الله ۽ رسول جو حُڪم قبول ڪيو، انھن مان جن چڱائي ڪئي ۽ ڊنا تن لاءِ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(3,173,'3:173','جن کي ماڻھن چيو ته (ڪافرن) ماڻھن اوھان لاءِ لشڪر گڏ ڪيو آھي تنھن ڪري کانئن ڊڄو پوءِ (اُن چوڻ) ھٿون اُنھن جو ايمان وڌايو ۽ چيائون ته اسان کي الله ڪافي آھي ۽ (اُھو) چڱو وڪيل (ڀرجھلو) آھي.');
INSERT INTO "amroti" VALUES(3,174,'3:174','پوءِ (اُھي مسلمان) الله جي نعمت ۽ فضل سان موٽيا کين ڪا سختي نه پھتي ۽ الله جي راضپي پٺيان لڳا، ۽ الله وڏي فضل وارو آھي.');
INSERT INTO "amroti" VALUES(3,175,'3:175','(اُھو اوھان کي جنگ کان جھليندڙ) شيطان کان سواءِ ٻيو نه آھي جو پنھنجن دوستن کي ڊيڄاريندو آھي، جيڪڏھن (اوھين) مؤمن آھيو ته اُنھن (ڪافرن) کان نه ڊڄو ۽ مون کان ڊڄو.');
INSERT INTO "amroti" VALUES(3,176,'3:176','۽ (اي پيغمبر) جيڪي ڪفر (جي سوڀ) ۾ اڳرائي ڪندا آھن سي توکي غمگين نه ڪن، ڇو ته اُھي الله کي ڪجھ به نقصان نه لائيندا، الله گھرندو آھي ته اُنھن کي آخرت ۾ ڪجھ بھرو نه ڏي، ۽ اُنھن لاءِ وڏو عذاب آھي.');
INSERT INTO "amroti" VALUES(3,177,'3:177','جن اِيمان جي بدران ڪفر ڳڌو سي الله کي ڪجھ به نقصان نه لائيندا، ۽ اُنھن لاءِ ڏکوئيندڙ عڊاب آھي.');
INSERT INTO "amroti" VALUES(3,178,'3:178','۽ ڪافر (ھيءُ) نه ڀانئن ته کين اسان جي مُھلت ڏيڻ اُنھن لاءِ ڀلي آھي، اُنھن کي ھن کانسواءِ ڍر نه ڏني سون ته گناھ ۾ وڌن، ۽ انھن لاءِ خوار ڪندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(3,179,'3:179','(اي منافقؤ) الله کي نه جڳائيندو آھي ته جنھن تي اوھين آھيو تنھن تي مؤمنن کي (به) ڇڏي ڏي تان جو پليت کي پاڪ کان ڌار ڪري، ۽ الله کي نه جڳائيندو آھي جو اوھان کي ڳُجھ تي واقف ڪري پر الله پنھنجن پيغمبرن مان جنھن کي گھرندو آھي تنھن کي چونڊيندو آھي، پوءِ الله ۽ سندس پيغمبرن کي مڃيو، ۽ جيڪڏھن (اوھين) مڃيندؤ ۽ ڊڄندؤ ته اوھان لاءِ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(3,180,'3:180','۽ جيڪي الله پنھنجي فضل سان کين ڏنو آھي تنھن ۾ جيڪي بخل ڪن سي (اِئين) پٽيءَ نه ڀانئين ته اُھو (بُخل) کين ڪو چڱو آھي، بلڪ اُھو اُنھن لاءِ بڇڙو آھي، جنھن شيء جو بخل ڪيائون سا قيامت جي ڏينھن ڳٽ ڪري وجھبين، ۽ آسمانن ۽ زمين جي ميراث الله جي آھي، ۽ جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(3,181,'3:181','بيشڪ الله اُنھن جو چوڻ ٻڌو جن چيو ته الله سڃو آھي ۽ اَسين شاھوڪار آھيون، جيڪي چيائون سو لکنداسون ۽ اُنھن جو پيغمبرن کي ناحق ڪھڻ به ۽ (آخرت ۾) چونداسون ته ساڙيندڙ عذاب (جو مزو) چکو.');
INSERT INTO "amroti" VALUES(3,182,'3:182','اھا (سزا) اِنھيءَ سببان آھي جو اوھان جي ھٿن اڳ موڪليو آھي ۽ (ھن سببان) ته الله ٻانھن تي ظلم ڪندڙ نه آھي.');
INSERT INTO "amroti" VALUES(3,183,'3:183','جيڪي چون ٿا ته الله اسان سان انجام ڪيو آھي ته ڪنھن پيغمبر تي ايستائين ايمان نه آڻيون جيستائين (ھو اھڙي) قرباني اسان وٽ (نه) آڻي جو اُن (قربانيءَ) کي باہ کائي، (اي پيغمبر کين) چئو ته بيشڪ مون کان اڳ ڪيترن پيغمبرن مُعجزا ۽ (ٻيو) جيڪي چيوَ سو اوھان وٽ آندو پوءِ جيڪڏھن اوھين سچا آھيو ته اُنھن (پيغمبرن) کي ڇالاءِ ڪٺوَ؟');
INSERT INTO "amroti" VALUES(3,184,'3:184','پوءِ (اي پيغمبر) جيڪڏھن توکي ڪوڙو ڀانيائون ته بيشڪ توکان اڳ ڪيترا پيغمبر ڪوڙا ڀانيا ويا ھؤا (جن) ڪيتريون حجتون ۽ ڪيترا صحيفا ۽ نوراني ڪتاب (وٽن) آندا.');
INSERT INTO "amroti" VALUES(3,185,'3:185','سڀڪو جيءُ موت (جو مزو) چکندڙ آھي، ۽ ھن کان سواءِ (ٻيوڪجھ ڪرڻو) نه آھي جو اوھان کي قيامت جي ڏينھن پورو اجر ڏبو، پوءِ جنھن کي باہ کان پري ڪيو ويو ۽ بھشت ۾ گھيڙيو ويو سو بيشڪ مراد کي پھتو، ۽ دنيا جي حياتي ٺڳيءَ جي سامان کانسواءِ نه آھي.');
INSERT INTO "amroti" VALUES(3,186,'3:186','(اوھين) پنھنجن مالن ۽ پنھنجين جانين ۾ ضرور پرکبو، ۽ جن کي اوھان کان اڳ ڪتاب ڏنو ويو آھي تن کان ۽ مشرڪن کان گھڻيون گاريون ضرور ٻڌندؤ، ۽ جيڪڏھن صبر ڪندؤ ۽ الله کان ڊڄندؤ ته اِھا (عادت) ھمت جي ڪمن مان آھي.');
INSERT INTO "amroti" VALUES(3,187,'3:187','۽ (ياد ڪر) جڏھن الله ڪتاب وارن کان ھن لاءِ (پڪو) انجام ورتو ته اُن (ڪتاب) کي ماڻھن لاءِ بيان ڪجو ۽ اُن کي نه لڪائجو، پوءِ اُن (انجام) کي پنھنجين پٺين پوئتان اُڇلايائون ۽ اُن جي عيوض ٿورو مُلھ ڳڌائون، پوءِ جيڪي ڳنھندا آھن سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(3,188,'3:188','(اي پيغمبر تون) اُنھن کي چڱو نه ڀانءِ جيڪي پنھنجي ڪئي تي خوش ٿيندا آھن ۽ جيڪي نه ڪيائون تنھن تي پنھنجي ساراھڻ کي دوست رکندا آھن پوءِ تون اُنھن کي عذاب کان ڇٽل نه ڀانءِ، ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(3,189,'3:189','۽ آسمانن ۽ زمين جي بادشاھي الله جي آھي، ۽ الله سڀڪنھن شيءِ تي وسوارو آھي.');
INSERT INTO "amroti" VALUES(3,190,'3:190','آسمانن ۽ زمين جي بڻاوت (۾) ۽ رات ۽ ڏينھن جي ڦير گھير ۾ اُنھن سمجھ وارن لاءِ ضرور نشانيون آھن.');
INSERT INTO "amroti" VALUES(3,191,'3:191','جيڪي بيٺي ۽ ويٺي ۽ پنھنجن پاسن ڀر الله کي ياد ڪندا آھن ۽ آسمانن ۽ زمين جي بڻاوت ۾ فڪر ڪندا آھن، (۽ چوندا آھن ته) اي اسانجا پالڻھار ھنن کي بي رٿو نه بڻايو اَٿيئي، توکي پاڪ ڄاڻون ٿا تنھنڪري اسان کي باہ جي عذاب کان بچاءِ.');
INSERT INTO "amroti" VALUES(3,192,'3:192','اي اسانجا پالڻھار جنھن کي تو باہ ۾ گھيڙيو تنھن کي بيشڪ خوار ڪيئي، ۽ ظالمن جو ڪو مددگار نه آھي.');
INSERT INTO "amroti" VALUES(3,193,'3:193','اي اسانجا پالڻھار اسان سڏيندڙ جي سڏ کي ٻڌو جو ايمان ڏانھن سڏيندو آھي ته پنھنجي پالڻھار تي ايمان آڻيو پوءِ مڃيوسون، اي اسانجا پالڻھار اسان کي اسانجا گناہ بخش ۽ اسان کان اسان جون مدايون ميٽ ۽ اسان کي ڀلارن سان گڏ مار.');
INSERT INTO "amroti" VALUES(3,194,'3:194','اي اسانجا پالڻھار اسان کي پنھنجن پيغمبرن (جي زبان) سان جيڪي انجام ڏنو اٿيئي سو پار ۽ قيامت جي ڏينھن اسان کي خوار نه ڪج، ڇوته تون وعدي خلافي نه ڪندو آھين.');
INSERT INTO "amroti" VALUES(3,195,'3:195','پوءِ سندن پالڻھار اُنھن جي دعا قبول ڪئي ته آءٌ اوھان مان عمل ڪندڙ مڙس يا زال جو عمل نه وڃائيندس، اوھان مان ڪي ڪن مان (پيدا ٿيل) آھن (يعني سڀ ھڪ جنس آھيو) پوءِ جن وطن ڇڏيو ۽ پنھنجن گھرن مان ڪڍيا ويا ۽ مُنھنجي واٽ ۾ ڏکويا ويا ۽ وڙھيا ۽ ڪُٺا ويا تن کان سندن مدايون ضرور ميٽيندس ۽ اُنھن کي (بھشت جي اھڙن) باغن ۾ ضرور داخل ڪندس جن جي ھيٺان واھيون وھنديون آھن، (اِھو) بدلو الله وٽان آھي، ۽ الله وٽ چڱو بدلو آھي.');
INSERT INTO "amroti" VALUES(3,196,'3:196','(اي پيغمبر) ملڪن ۾ ڪافرن جي اَچ وڃ توکي نه ٺڳي.');
INSERT INTO "amroti" VALUES(3,197,'3:197','اِھو فائدو ٿورو آھي، وري سندن جاءِ دوزخ آھي، ۽ (اُھو) بڇڙو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(3,198,'3:198','پر جيڪي پنھنجي پالڻھار کان ڊنا تن لاءِ باغ آھن جن جي ھيٺان واھيون وھنديون آھن اُن ۾ سدائين رھڻ وارا آھن اِھا مھماني الله وٽان آھي، ۽ جيڪي الله وٽ آھي سو ڀلارن لاءِ چڱو آھي.');
INSERT INTO "amroti" VALUES(3,199,'3:199','۽ ڪتاب وارن مان ڪي اُھي آھن جيڪي الله کي ۽ جيڪي توڏانھن (قرآن) لاٿو ويو تنھن کي ۽ جيڪي (توريت) اُنھن ڏانھن لاٿو ويو تنھن کي الله جي عاجزي ڪندڙ ٿي مڃيندا آھن الله جي آيتن کي ٿوري مُلھ سان نه وڪڻندا آھن، اِھي (اُھي) آھن جن لاءِ سندن پالڻھار وٽ سندن اجر آھي، ڇوته الله سگھو حساب وٺندڙ آھي.');
INSERT INTO "amroti" VALUES(3,200,'3:200','اي ايمان وارؤ صبر ڪريو ۽ (جھاد لاءِ) بيھڪ وارا ٿيو ۽ مُحڪم رھو، ۽ الله کان ڊڄو ته مانَ اوھين ڇٽو.');
INSERT INTO "amroti" VALUES(4,1,'4:1','اي ماڻھؤ اوھين اُنھيءَ پنھنجي پالڻھار کان ڊڄو جنھن اوھان کي ھڪ جِيءَ (آدم) مان پيدا ڪيو ۽ منجھانئس سندس زال پيدا ڪيائين ۽ اُنھن مان گھڻا مڙس ۽ زالون پکيڙيائين، ۽ اُنھيءَ الله کان ڊڄو جنھن (جي نالي) سان (پاڻ ۾) ھڪ ٻئي کان گھرندا آھيو ۽ مائٽي (ڇنڻ) کان (به ڊڄو)، ڇوته الله اوھان تي نگھبان آھي.');
INSERT INTO "amroti" VALUES(4,2,'4:2','۽ يتيمن کي سندن مال ڏيو ۽ سٺي شيءِ کي بڇڙيءَ شيءِ سان نه مٽايو، ۽ اُنھن (يتيمن) جو مال پنھنجن مالن سان (گڏي) نه کائو، ڇوته اُھو وڏو ڏوہ آھي.');
INSERT INTO "amroti" VALUES(4,3,'4:3','۽ جيڪڏھن ڊڄو ته يتيمن ڇوڪرين (جي نڪاح ڪرڻ) ۾ عدل نه ڪندؤ ته (ٻـين) زالن مان جيڪي اوھان کي وڻن سي ٻه ٻه ۽ ٽي ٽي ۽ چار چار پرڻيو، پوءِ جيڪڏھن ڊٺو ٿا ته اوھان کان انصاف ٿي نه سگھندو ته ھڪ (پرڻيو) يا اوھان جا ھٿ جنھن جا مالڪ ٿيا (سا ٻانھي رکو) اِھو ھن کي ويجھو آھي ته ڏاڍائي نه ڪريو.');
INSERT INTO "amroti" VALUES(4,4,'4:4','۽ زالن کي سندن ٺھرايل ڪابين خوشيءَ سان ڏيو، پوءِ جيڪڏھن اوھان کي پاڻ خوشيءَ سان اُنھيءَ مان ڪجھ ڇڏين ته اُھا سُھائيندڙ وڻندڙ ڪري کائو.');
INSERT INTO "amroti" VALUES(4,5,'4:5','۽ بي سمجھن کي پنھنجا اُھي مال نه ڏيو جن کي الله اوھان لاءِ گذران جو سبب بڻايو آھي ۽ منجھانئس کين کارايو ۽ کين پھرايو ۽ کين چڱي ڳالھ چئو.');
INSERT INTO "amroti" VALUES(4,6,'4:6','۽ ايستائين يتيمن کي پرکو جيستائين پرڻي (جي عمر) کي پھچن، پوءِ جيڪڏھن اُنھن ۾ ڪا سياڻپ لھو ته سندن مال کين ورائي ڏيو، ۽ اُھي (مال) اجايو ۽ (سندن) وڏي ٿيڻ جي ڀؤ کان تڪڙ ڪري نه کائو، ۽ جيڪو (سنڀاليندڙ) آسودو ھجي تنھن کي گھرجي ته پاسي رھي، ۽ جيڪو مسڪين ھجي سو مناسب (خدمت) آھر کائيندو رھي، پوءِ جڏھن سندس مال کين ورائي ڏيو تڏھن مٿن شاھد ڪريو، ۽ الله حساب ڪندڙ بس آھي.');
INSERT INTO "amroti" VALUES(4,7,'4:7','جيڪو (مال) ماءُ پيءُ ۽ مٽن ڇڏيو تنھن مان مڙسن لاءِ حصّو آھي، ۽ جيڪو (مال) ماءُ پيءُ ۽ مٽن ڇڏيو تنھن مان زالن لاءِ (به) حصو آھي اھو ڇڏيل مال ٿورو ھجي يا گھڻو، حصّا (الله جا) مقرر ڪيل آھن.');
INSERT INTO "amroti" VALUES(4,8,'4:8','۽ جڏھن ورھاڱي مھل مائٽيءَ وارا ۽ يتيم ۽ مسڪين حاضر ٿين تڏھن اُن (مال) مان اُنھن ماڻھن کي (ڪجھ) ڏيو ۽ اُنھن کي مٺي ڳالھ چئو.');
INSERT INTO "amroti" VALUES(4,9,'4:9','۽ اُنھن کي (الله کان) ڊڄڻ گھرجي جيڪي جيڪڏھن پنھنجي پٺيان ھيڻو اولاد ڇڏين ته مٿن ڊڄن (ته ڪھڙو حال ٿيندن)، پوءِ گھرجي ته الله کان ڊڄن ۽ گھرجي ته سڌي ڳالھه چون.');
INSERT INTO "amroti" VALUES(4,10,'4:10','جيڪي يتيمن جو مال ڏاڍ سان کائيندا آھن سي پنھنجن پيٽن ۾ رڳو باھ کائيندا آھن، ۽ دوزخ ۾ سگھو گھِڙندا.');
INSERT INTO "amroti" VALUES(4,11,'4:11','الله اوھان جي اولاد بابت اوھان کي وصيت ٿو ڪري، ته پُٽ لاءِ (حصّو) ٻن ڌيئرن جي ڀاڱي جيترو آھي، ۽ جيڪڏھن (فقط) ڌيئرون ٻن کان وڌيڪ ھجن ته (مُئَل) جيڪي ڇڏيو تنھن جون ٻه ٽھايون اُنھن لاءِ آھن، ۽ جيڪڏھن (فقط) ھڪ آھي ته اُن لاءِ اڌ آھي، ۽ مئل جيڪي ڇڏيو تنھن مان اُن جي ماءُ پيءُ ھر ھڪ لاءِ ڇھون ڀاڱو آھي (سو تڏھن) جڏھن اُن (مئل) کي اولاد ھجي، ۽ جيڪڏھن اُن کي اولاد نه ھُجي ۽ اُن جا وارث ماءُ پيءُ ھجن ته ماڻس لاءِ ٽيون ڀاڱو (۽ باقي پڻس لاءِ) آھي، ۽ جيڪڏھن اُن (مئل) کي ڀائر ھجن ته ماڻس لاءِ ڇھون حصّو آھي (اِھو ورھاڱو) وصيّت (جي ادائيءَ کان پوءِ) جنھن لاءِ اُھا وصيّت ڪيائين يا قرض جي ادائيءَ کانپوءِ (ڪرڻو) آھي، اوھان جا پيئر ۽ اوھان جا پُٽ، (اوھين) نه ڄاڻندا آھيو ته اُنھن مان ڪھڙو اوھان لاءِ فائدي جي ڪري وڌيڪ ويجھو آھي، (اِھو ورھاڱو) الله جي (پارکان) فرض ڪيل آھي، ڇوته الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,12,'4:12','۽ جيڪي (مال) اوھان جي زالن ڇڏيو تنھن جو اَڌ اوھان لاءِ آھي جيڪڏھن انھن کي ڪو وياءُ ڪونه ھجي، پر جيڪڏھن اُنھن کي ڪو وياءُ ھجي ته جيڪي ڇڏيائون تنھن مان چوٿون ڀاڱو وصيت (جي ادائيءَ کانپوءِ) جنھن لاءِ اُھا وصيت ڪيائون يا قرض جي ادائيءَ کان پوءِ اوھان لاءِ آھي، ۽ جيڪي (مال) اوھان ڇڏيو تنھن مان جيڪڏھن اوھان کي ڪو وياءُ ڪونھي ته اُنھن (زالن) لاءِ چوٿون ڀاڱو آھي، ۽ جيڪڏھن اوھان کي وياءُ آھي ته جيڪي (مال) اوھان ڇڏيو تنھن مان وصيت (جي ادائيءَ) جنھن لاءِ اُھا وصيت ڪئي اَٿَوَ يا قرض جي ادائيءَ کانپوءِ اُنھن (زالن) لاءِ اٺون ڀاڱو آھي، ۽ جيڪڏھن ڪو (مئل) مڙس يا زال ورثو ڇڏي (۽ اُھو) ڪلاله ھجي (يعني جنھنکي ماءُ پيءُ ۽ وياءُ نه ھجي) ۽ اُن کي (مائيتو) ھڪ ڀاءُ يا ھڪ ڀيڻ ھجي ته اُنھن منجھان ھر ھڪ لاءِ ڇھون حصو آھي، ۽ جيڪڏھن اِنھن کان وڌيڪ ھجي ته اُھي (سڀ) جا وصيت نقصان واري نه ڪئي ويئي ھجي تنھن يا قرض جي ادائيءَ کانپوءِ ٽھائيءَ ۾ ڀائيوار آھن، اُھو الله جو حُڪم آھي، ۽ الله ڄاڻندڙ بُردبار آھي.');
INSERT INTO "amroti" VALUES(4,13,'4:13','اِھي الله جون حدون آھن، ۽ جيڪو الله ۽ سندس رسول جي فرمانبرداري ڪندو تنھن کي (الله اھڙن) باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن اُن ۾ سدائين رھڻ وارا آھن، ۽ اِھا وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(4,14,'4:14','۽ جيڪو الله ۽ سندس رسول جي بي فرماني ڪندو ۽ سندس حدن کان ٻاھر نڪرندو تنھن کي باہ ۾ داخل ڪندو اُن ۾ سدائين رھڻ وارو آھي، ۽ اُن لاءِ خوار ڪندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(4,15,'4:15','۽ اُوھان جي زالن مان جيڪي بي حيائيءَ جو ڪم ڪن تن تي پاڻ مان چار (مڙس) شاھد ڪريو، پوءِ جيڪڏھن (شاھد) شاھدي ڏين ته اُنھن کي ايستائين گھرن ۾ جھليو جيستائين کين موت ماري يا الله اُنھن لاءِ ڪا واٽ ڪري.');
INSERT INTO "amroti" VALUES(4,16,'4:16','۽ اوھان مان جيڪي ٻه (زاني ۽ زانياڻي) زنا ڪن تن کي مار ڏيو، پوءِ جيڪڏھن توبه ڪن ۽ پاڻ سڌارين ته اُنھن (جي مار ڏيڻ) کان مُڙو، ڇوته الله توبه قبول ڪندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(4,17,'4:17','الله کي رڳو اُنھن جي توبه ڪرڻ جڳائيندي آھي جيڪي بي سمجھائيءَ سان گناہ ڪندا آھن وري جھٽ پٽ توبه ڪندا آھن اُنھن تي الله (ٻاجھ سان) موٽندو آھي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,18,'4:18','۽ اُنھن جي توبه قبول ٿيڻي نه آھي جيڪي بڇڙايون ڪندا (رھندا) آھن (۽ نه ڊڄندا آھن) تان جو جنھن مھل اُنھن مان ڪنھن کي موت پھچي تنھن مھل (ڊڄي) چوي ته ھاڻي آءٌ توبه ڪريان ٿو ۽ نڪي اُنھن جي (توبه قبول آھي) جيڪي ڪافر ٿي مرندا آھن، اُنھن لاءِ ڏکوئيندڙ عذاب تيار ڪيو اٿئون.');
INSERT INTO "amroti" VALUES(4,19,'4:19','اي ايمان وارؤ! اوھان کي زالن جو (سندن) بي مرضيءَ وارث ٿيڻ روا نه آھي، ۽ نڪي سندن پڌري گناہ ڪرڻ کان سواءِ کين ھن لاءِ جھليو ته جيڪي کين ڏنو ھجيوَ تنھن مان ڪجھ ورائي وٺو، ۽ ساڻن ھلي چلي وانگر گذاريو، پوءِ جيڪڏھن اُھي اوھان کي نه وڻن ته (ڌيرج ڪريو ڇوته) جا شي اوھان کي نه وڻندي ھجي تنھن ۾ ٿيڻي آھي ته الله گھڻي چڱائي ڪري.');
INSERT INTO "amroti" VALUES(4,20,'4:20','۽ جيڪڏھن (ھڪ) زال جي بجاءِ ٻي زال پرڻڻ جو ارادو ڪريو ۽ اُنھن مان ھڪڙيءَ کي گھڻو مال (ڪابين ۾) ڏنو ھجيوَ ته منجھانئس ڪجھ موٽائي نه وٺو، (اوھين) بھتان ۽ پڌري بدي لائي اُھو (مال) ڇو کسيندا آھيو؟');
INSERT INTO "amroti" VALUES(4,21,'4:21','۽ بيشڪ اوھان ھڪ ٻئي کان نفعو وٺي چڪؤ ۽ (زالن به) اوھان کان (مھر ڏيڻ جو) پڪو انجام ورتو آھي اِنھيءَ ھوندي به اُھو (مال) ڪھڙي طرح کسيندا آھيو؟');
INSERT INTO "amroti" VALUES(4,22,'4:22','۽ جيڪي زالون اوھان جي پيئن پرڻيون سي اوھين نه پرڻيو پر جيڪي اڳي گذريو آھي (سو گذريو) ڇوته اُھا بي حيائي ۽ گناہ آھي، ۽ اُھا بُڇڙي واٽ آھي.');
INSERT INTO "amroti" VALUES(4,23,'4:23','(اي مسلمانؤ) اوھان تي اوھان جون مائر ۽ اوھانجون ڌيئر ۽ اوھانجون ڀينر ۽ اوھانجون پڦيون ۽ اوھانجون ماسيون ۽ ڀائٽيون ۽ ڀاڻيجيون ۽ اُھي اوھان جون مائر جن اوھان کي ٿڃ پياري ۽ اوھان جون رضائي (ٿڃ گڏ پيتل) ڀينر ۽ اوھان جي زالن جون مائر (يعني سَسون) ۽ اُھي اوھانجون اڳ ڄايون جيڪي اوھانجي ھنجن ۾ پليون (۽) اوھان جي اُنھن زالن مان ھجن جن کي اوھين ويجھا ويا آھيو پر جيڪڏھن اوھين اُنھن (سندن مائرن) کي ويجھا نه ويا آھيو ته اوھان تي گناہ نه آھي (اِھي سڀ) حرام ڪيون ويون آھن، ۽ اوھان جي اُنھن پُٽن جون زالون جيڪي (پٽ) اوھانجي پٺين مان (پيدا ٿيل) آھن ۽ ٻن ڀينرن جو گڏ پرڻڻ (حرام ڪيو ويو آھي) پر جيڪي اڳ گذريو آھي (سو گذريو) ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(4,24,'4:24','۽ پرڻيل زالون (به حرام آھن) پر اوھان جا ھٿ جن جا مالڪ ٿيا آھن (سي حرام نه آھن)، اُھو حڪم الله اوھان تي لکيو آھي، ۽ اُنھن (حرام ڪيلن) کانسواءِ (ٻيون سڀ زالون) اوھان لاءِ حلال آھن جڏھن پنھنجن مالن سان (کين) پرڻيندڙ نه زنا جو ارادو ڪندڙ ھجو، اھو مال جنھن جي عوض انھن (زالن) کان فائدو ورتوَ تن کي سندن ڪابين مُقرر ڪيل ڏيو، ۽ ڪابين مقرر ڪرڻ کان پوءِ جيتري (ڪابين) ۾ اوھين پاڻ ۾ راضي ٿيو ته اوھان تي گناہ نه آھي، ڇوته الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,25,'4:25','۽ اوھان مان جيڪو آجين مؤمنياڻين جي پرڻجڻ جي سگھ نه رکندو ھجي سو اُنھن مؤمنياڻين ٻانھين مان جن جا مالڪ اوھان (مؤمنن) مان آھن (ڪا پرڻجي ته عار نه آھي)، ۽ الله اوھان جي ايمان کي وڌيڪ ڄاڻندڙ آھي، اوھانجا ھڪڙا ٻـين مان (پيدا ٿيل) آھن (يعني پاڻ ۾ ھڪ جنس آھيو)، پوءِ کين سندن مالڪن جي موڪل سان پرڻجو ۽ سندن ڪابين ھلي چليءَ وانگر کين ڏيو (اُھي) نڪاح ٻَڌلون نه زنا ڪندڙيون ۽ نڪي ڳجھن يارن وٺڻ واريون ھجن، پوءِ جڏھن نڪاح ۾ آيون تڏھن جيڪڏھن (اُھي) ڪو بي حيائيءَ جو ڪم ڪن ته جيڪا سزا آجين تي آھي تنھن جو اڌ مٿن آھي، (اِھا ٻانھيءَ کي پرڻجڻ جي اجازت) اوھان مان انھيءَ لاءِ آھي جيڪو زنا (جي گناہ ۾ پوڻ) کان ڊڄي، ۽ اوھان جو صبر ڪرڻ اوھان لاءِ ڀلو آھي، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(4,26,'4:26','الله گھرندو آھي ته اوھان لاءِ کولي بيان ڪري ۽ جيڪي اوھان کان اڳ (گذريا) آھن تن جون واٽون اوھان کي ڏيکاري ۽ اوھان تي ٻاجھ سان موٽي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,27,'4:27','۽ الله اوھان تي ٻاجھ سان موٽڻ گھرندو آھي، ۽ جيڪي پنھنجي سَڌن جي پٺيان لڳندا آھن سي اوھان جو (سڌيءَ واٽ کان) تمام پري ٿڙي وڃڻ گھرندا آھن.');
INSERT INTO "amroti" VALUES(4,28,'4:28','الله اوھان تان بار ھلڪو ڪرڻ گھرندو آھي، ۽ ماڻھو (اصل) ھيڻو پيدا ڪيو ويو آھي.');
INSERT INTO "amroti" VALUES(4,29,'4:29','اي ايمان وارؤ! اوھين پاڻ ۾ ھڪ ٻئي جو مال ناحق نه کائو پر پاڻ ۾ رضامندي سان واپار جي ڏيتي ليتي ھجي ته (ڀلي کائو)، ۽ اوھان پاڻ کي ھلاڪ نه ڪريو، ڇوته الله اوھان تي مھربان آھي.');
INSERT INTO "amroti" VALUES(4,30,'4:30','۽ جيڪو حد کان ٻاھر نڪرڻ ۽ ظلم سان ايئن ڪندو تنھن کي باہ ۾ سگھو وجھنداسين، ۽ اھو ڪم الله تي سھنجو آھي.');
INSERT INTO "amroti" VALUES(4,31,'4:31','جيڪڏھن وڏن گناھن کان جن کان اوھانکي جھليو ويو پاسو ڪندا رھندؤ ته اوھان جيون (ھلڪيون) مدايون اوھان کان ميٽينداسين ۽ اوھان کي عزت واريءَ جاءِ ۾ داخل ڪنداسين.');
INSERT INTO "amroti" VALUES(4,32,'4:32','جن شين سان الله اوھان مان ھڪڙن کي ٻـين کان فضيلت ڏني آھي تن جي ريس نه ڪريو، مڙسن لاءِ جيترو (پاڻ) ڪمايائون تنھن مان حصّو آھي، ۽ زالن لاءِ جيترو پاڻ ڪمايائون تنھن مان حصّو آھي، ۽ الله کان سندس فضل گھرو، ڇوته الله سڀڪنھن شيءِ کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(4,33,'4:33','۽ ماءُ پيءُ ۽ مائٽن جيڪي (مال) ڇڏيو تنھن سڀ لاءِ وارث مُقرّر ڪياسون، ۽ جن کي (مائٽيءَ جھڙي دوستيءَ ۾) اوھانجي قَسمن ٻَڌو آھي تن کي سندن حصّو ڏيو، ڇوته الله سڀڪنھن شي تي حاضر آھي.');
INSERT INTO "amroti" VALUES(4,34,'4:34','مڙس زالن تي انھيءَ سببان حاڪم آھن جو الله انھن مان ھڪڙن کي ٻـين کان ڀلو ڪيو آھي ۽ (ٻيو) ھن سببان جو پنھنجن مالن مان خرچيندا آھن، پوءِ ڀلاريون فرمانبردارڻيون پرپٺ نگاہ رکڻ واريون الله جي نگھباني موجب آھن، ۽ جن (زالن) جي بي فرماني معلوم ڪريو تن کي (زباني) نصيحت ڪريو ۽ پاٿارين ۾ کين ڌار ڪريو ۽ مار ڏيونِ، پوءِ جيڪڏھن اوھان جي فرمانبرداري ڪن ته مٿن ڪا (ايذاءُ جي) واٽ نه ڳوليو، ڇوته الله وڏو وڏيءَ وڏائيءَ وارو آھي.');
INSERT INTO "amroti" VALUES(4,35,'4:35','۽ جيڪڏھن سندن وچ ۾ ڦيٽي کان ڊڄو ته ھِن جي گھراڻي مان ھڪ امين ۽ ھُن جي گھراڻي مان (به) ھڪ امين مُقرّر ڪريو، جيڪڏھن ٻئي (امين) صُلح ڪرائڻ گھرندا ته الله سندن وچ ۾ ميلاپ ڪندو، ڇوته الله ڄاڻندڙ خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(4,36,'4:36','۽ (خاص) الله جي عبادت ڪريو ۽ ساڻس ڪنھن شيء کي شريڪ نه ڪريو ۽ ماءُ پيءُ سان ۽ مٽيءَ وارن سان ۽ ڇورن (ٻارن) ۽ مسڪينن ۽ مٽيءَ واريءَ پاڙيچي ۽ ڌاريي پاڙيچي ۽ ويجھي سنگتي ۽ مسافر ۽ پنھنجن ٻانھن سان چڱائي ڪريو، ڇوته جيڪو ھٺيلو وڏائي ڪندڙ ھجي تنھن کي الله دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(4,37,'4:37','(۽ نڪي اُنھن کي) جيڪي (پاڻ) ڪنجوسي ڪندا آھن ۽ ماڻھن کي به ڪنجوسي ڪرڻ جو حُڪم ڪندا آھن ۽ الله پنھنجي فضل سان کين جيڪي ڏنو آھي سو لڪائيندا آھن، ۽ اھڙن نه مڃيندڙن لاءِ خواريءَ وارو عذاب تيار ڪيو اٿئون.');
INSERT INTO "amroti" VALUES(4,38,'4:38','۽ (اُنھن لاءِ به) جيڪي پنھنجن مالن کي ماڻھن جي ڏيکاريءَ لاءِ خرچيندا آھن ۽ نڪي الله کي ۽ نڪي قيامت جي ڏينھن کي مڃيندا آھن، ۽ جنھن جو سنگتي شيطان ٿيو ته اُھو سنگتي بڇڙو آھي.');
INSERT INTO "amroti" VALUES(4,39,'4:39','۽ مٿن ڪھڙي (ڏکيائي) ھئي جيڪڏھن الله ۽ قيامت جي ڏينھن کي مڃين ھا ۽ الله جيڪي کين ڏنو آھي ان مان خرچين ھا، ۽ الله انھن (جي حال) کي (چڱو) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(4,40,'4:40','الله ذري جيترو به ظلم نه ڪندو آھي، ۽ جيڪڏھن (ڪا) چڱائي ھوندي ته ان کي ٻيڻو ڪندو آھي ۽ پاڻ وٽان وڏو اجر ڏيندو آھي.');
INSERT INTO "amroti" VALUES(4,41,'4:41','پوءِ (تڏھن) ڪئن (ڪندا) جڏھن سڀڪنھن ٽوليءَ مان (ھڪ) شاھد آڻينداسون ۽ (اي پيغمبر) توکي (به) ھن ٽوليءَ تي شاھد ڪري آڻينداسون.');
INSERT INTO "amroti" VALUES(4,42,'4:42','اُنھيءَ ڏينھن ڪافر ۽ جن پيغمبر جو چيو نه مڃيو سي گھرندا ته جيڪر مٿن زمين سنئين ڪئي وڃي، ۽ الله کان ڪائي ڳالھ لڪائي نه سگھندا.');
INSERT INTO "amroti" VALUES(4,43,'4:43','اي ايمان وارؤ! اوھان نشي ۾ ٿي ايستائين نماز کي ويجھا نه وڃو جيستائين جيڪي ڳالھايو ٿا سو سمجھو ۽ نڪي مسافرن کانسواءِ (مقيم) تڙ وارو (نماز پڙھي) جيسين تڙ ڪريو، ۽ جيڪڏھن اوھين بيمار ھجو يا مسافري تي ھجو يا اوھان مان ڪوئي جاءِ ضرور کان اچي يا زالن کي ويجھا ويا ھجو ۽ پاڻي نه لھو ته پاڪ مٽيءَ سان تيمّم ڪريو پوءِ پنھنجن مُنھن ۽ پنھنجن ھٿن کي مک ڪريو، ڇوته الله معافي ڏيندڙ بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(4,44,'4:44','(اي پيغمبر) اُنھن ڏانھن نه ڏٺو اٿئي ڇا؟ جن کي ڪتاب مان حصّو ڏنو ويو آھي اُھي گمراھي ڳنھندا آھن ۽ اوھان جو واٽ کان ڀلڻ (به) گھُرندا آھن.');
INSERT INTO "amroti" VALUES(4,45,'4:45','۽ الله اوھان جي ويرين کي وڌيڪ ڄاڻندڙ آھي، ۽ الله (سڀني ڪمن جو) ڪارساز بس آھي، ۽ الله مددگار بس آھي.');
INSERT INTO "amroti" VALUES(4,46,'4:46','يھودين مان ڪي (توريت ۾) لفظن کي سندن جاين کان مٽائيندا آھن ۽ سَمِعۡنَا وَ عَصَيۡنَا ۽ اِسۡمَعۡ غَيۡرَ مُسۡمَع ۽ رَاعِنَا (جي لفظن) کي (چوڻ وقت) پنھنجيون زبانون وچڙائي ۽ دين ۾ طعني ھڻڻ لاءِ چوندا آھن، ۽ جيڪڏھن (سَمِعۡنَا وَعَصَيۡنَا جي بجاءِ) سَمِعۡنَا وَاَطَعۡنَا (يعني ٻڌوسون ۽ فرمانبرداري ڪئيسون) ۽ (اِسۡمَعۡ غَيۡرَ مُسۡمَع جي ھنڌ) اِسۡمَعۡ (يعني ٻڌ) ۽ (رَاعِنَا لفظ جي بجاءِ لفظ) اُنۡظُرۡنَا (يعني اسان جو خيال رک) چون ھا ته اُنھن لاءِ ڀلو ۽ ڏاڍو سنئون ھجي ھا پر الله سندن ڪُفر سببان مٿن لعنت ڪئي آھي پوءِ ٿورن کانسواءِ (ٻيا) ايمان نه ٿا آڻين.');
INSERT INTO "amroti" VALUES(4,47,'4:47','اي ڪتاب وارؤ جيڪو (قرآن) لاٿوسون جيڪي اوھان وٽ آھي تنھن جو (اُھو) سچو ڪندڙ (به) آھي تنھنکي انھي کان اڳ مڃيو جو (اوھان جي) منھن جي صورت ميٽي ڇڏيون پوءِ ڦيرائي انھن کي ڪياڙي جي شڪل ڪريون يا مٿن اھڙي لعنت ڪريون جھڙي ڇنڇر وارن تي لعنت ڪئيسون، ۽ الله جو (جيڪو) حُڪم (جاري ٿيندو آھي) سو ٿي چڪندو آھي.');
INSERT INTO "amroti" VALUES(4,48,'4:48','بيشڪ الله ھيءُ (ڏوہ) نه بخشيندو جو (ڪنھن کي به) ساڻس شريڪ ڪيو وڃي ۽ اُن کانسواءِ ٻيا (ڏوہ) جنھن کي وڻيس تنھن کي بخشيندو، ۽ جيڪو الله سان (ڪنھن کي به) شريڪ بڻائيندو تنھن بيشڪ وڏي گناہ جو بُھتان ٻڌو.');
INSERT INTO "amroti" VALUES(4,49,'4:49','(اي پيغمبر) انھن ڏانھن نه ڏٺو اٿيئي ڇا؟ جيڪي پاڻون پاڻ کي پاڪ چوندا آھن، (نه) بلڪ الله جنھن کي گھرندو آھي پاڪ ڪندو آھي ۽ تند جيترو به ظلم نه ڪبن.');
INSERT INTO "amroti" VALUES(4,50,'4:50','(اي پيغمبر) ڏس ته الله تي ڪيئن ڪوڙ ٺاھيندا آھن؟ ۽ اھو پڌرو گناہ بس آھي.');
INSERT INTO "amroti" VALUES(4,51,'4:51','(اي پيغمبر) اُنھن ڏانھن نه ڏٺو اٿيئي ڇا؟ جن کي ڪتاب مان ڀاڱو ڏنو ويو ته اُھي بتن ۽ شيطان کي مڃيندا آھن ۽ ڪافرن لاءِ چوندا آھن ته ھيءَ ٽولي مؤمنن کان وڌيڪ سنئين واٽ واري آھي.');
INSERT INTO "amroti" VALUES(4,52,'4:52','اِھي (اُھي) آھن جن تي الله لعنت ڪئي آھي، ۽ جنھن تي الله لعنت ڪندو آھي تنھن جو ڪو مددگار نه لھندين.');
INSERT INTO "amroti" VALUES(4,53,'4:53','اُنھن کي بادشاھيءَ مان ڪو ڀاڱو آھي ڇا؟ (جي کين کڻي ھجي به ته) اُن مھل ماڻھن کي تر جيترو (به) نه ڏين.');
INSERT INTO "amroti" VALUES(4,54,'4:54','ويتر ماڻھن سان انھيءَ تي حسد ڪندا آھن جيڪي الله پنھنجي فضل سان کين ڏنو آھي، پوءِ بيشڪ ابراھيم جي اولاد کي ڪتاب ۽ دانائي ڏنيسون ۽ کين وڏي بادشاھي ڏني سون.');
INSERT INTO "amroti" VALUES(4,55,'4:55','پوءِ منجھائن ڪي آھن جن اُن ڪتاب کي مڃيو ۽ منجھائن ڪي آھن جي کانئس اٽڪي رھيا، ۽ (انھن لاءِ) ٻرندڙ دوزخ بس آھي.');
INSERT INTO "amroti" VALUES(4,56,'4:56','جن اسان جي آيتن کي نه مڃيو تن کي سگھو باہ ۾ گھيرينداسون، جڏھن ڪڏھن سندن کلون ڀُڄنديون (تڏھن) سندن ٻيون کلون ھن لاءِ پيا مٽائينداسون ته ڀلي عذاب (جو مزو) چکن، ڇوته الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,57,'4:57','جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي (اُنھن) باغن ۾ سگھو داخل ڪنداسون جن جي ھيٺان واھيون وھنديون آھن منجھن اصلي سدائين رھڻ وارا آھن، اُنھن لاءِ منجھن پاڪ زالون آھن، ۽ کين گھاٽين ڇانون ۾ داخل ڪنداسون.');
INSERT INTO "amroti" VALUES(4,58,'4:58','بيشڪ الله اوھان کي حُڪم ٿو ڪري ته امانتون سندن مالڪن کي پھچائي ڏيو ۽ جڏھن ماڻھن جي وچ ۾ فيصلو ڪريو (تڏھن) انصاف سان حڪم ڪريو، بيشڪ الله اوھان کي جيڪا نصيحت ڪري ٿو سا چڱي آھي، ڇوته الله ٻڌندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(4,59,'4:59','اي ايمان وارؤ الله جي فرمانبرداري ڪريو ۽ پيغمبر جي فرمانبرداري ڪريو ۽ اوھان مان جيڪو حاڪم ھجي تنھن جي (به) پوءِ جيڪڏھن ڪنھن شيءِ بابت جھڳڙو ڪريو ته جيڪڏھن الله ۽ قيامت جي ڏينھن کي مڃيندا آھيو ته ان کي الله ۽ پيغمبر جي حُڪم ڏانھن رجوع ڪريو، اِھو ڀلو ۽ نتيجي جي ڪري ڏاڍو چڱو آھي.');
INSERT INTO "amroti" VALUES(4,60,'4:60','(اي پيغمبر) اُنھن ڏانھن نه ڏٺو اٿيئي ڇا؟ جيڪي ڀائيندا آھن ته جيڪي تو ڏانھن لاٿو ويو ۽ جيڪي توکان اڳ لاٿو ويو تنھن کي پاڻ مڃيو اٿن اھي ھن ھوندي به طاغوت (يعني غير خدا) ڏانھن (مقد مونيئي) فيصلو ڪرائڻ گھرندا آھن حالانڪ ان جي نه مڃڻ جو حڪم ڪيو ويو اٿن، ۽ شيطان کين وڏي ڀُل ۾ ڀُلائڻ گھرندو آھي.');
INSERT INTO "amroti" VALUES(4,61,'4:61','۽ جڏھن کين چئجي ته جيڪي الله لاٿو تنھن ڏانھن ۽ پيغمبر ڏانھن اچو ته منافقن کي ڏٺئي ته توکان پوري طرح جھلجي ويندا آھن.');
INSERT INTO "amroti" VALUES(4,62,'4:62','پوءِ (تڏھن) ڪئن (ڪندا) جڏھن کين ڪا اوکائي اُنھيءَ سببان پھچندي جو سندن ھٿن اڳي ڪيو آھي ته وري تو وٽ اچي الله جو قسم کڻندا ته اسان جو چڱائي ۽ ٺھراءَ کانسواءِ (ٻيو) ڪو ارادو نه ھو.');
INSERT INTO "amroti" VALUES(4,63,'4:63','اِھي (اُھي) آھن جن جي دلين ۾ جيڪي (ڪفر ۽ نفاق) آھي سو الله ڄاڻندو آھي، پوءِ کانئن مُڙ ۽ کين نصيحت ڪر ۽ کين سندين دلين ۾ پھچندڙ ڳالھه چؤ.');
INSERT INTO "amroti" VALUES(4,64,'4:64','ڪوئي پيغمبر نه موڪليوسون پر ھن لاءِ ته الله جي حڪم سان سندس تابعداري ڪئي وڃي، ۽ جڏھن انھن (ماڻھن) پاڻ تي ظلم ڪيو تڏھن جيڪڏھن تو وٽ اچن ھا پوءِ الله کان بخشش گھرن ھا ۽ پيغمبر انھن لاءِ بخشش گھري ھا ته الله کي ٻاجھ سان موٽندڙ مھربان لھن ھا.');
INSERT INTO "amroti" VALUES(4,65,'4:65','پوءِ تنھنجي پالڻھار جو قسم آھي ته (اھي ماڻھو) مسلمان نه آھن جيستائين انھي معاملي ۾ جو سندن وچ ۾ پيو آھي توکان فيصلو ڪرائن پوءِ (تون) جيڪو نبيرو ڪرين تنھن کان پنھنجي دلين ۾ اڙکُڙ نه ڏسن ۽ پوري طرح مڃين.');
INSERT INTO "amroti" VALUES(4,66,'4:66','۽ جيڪڏھن اسين مٿن لازم ڪريون ھا ته اوھين پاڻ کي ڪُھو يا پنھنجن ديسن مان نڪرو ته منجھانئن ٿورڙن کانسواءِ (ٻِيا) اُھو نه ڪن ھا، ۽ جيڪا نصيحت ڪئي وين تنھن موجب جيڪڏھن اُھي ڪم ڪن ھا ته اُنھن لاءِ ضرور ڀلو ۽ (ايمان کي) زيادہ پڪي ڪرڻ وارو ھجي ھا.');
INSERT INTO "amroti" VALUES(4,67,'4:67','۽ انھيءَ وقت پاڻ وٽان وڏو اجر کين ضرور ڏيون ھا.');
INSERT INTO "amroti" VALUES(4,68,'4:68','۽ ضرور کين سڌي واٽ وٺايون ھا.');
INSERT INTO "amroti" VALUES(4,69,'4:69','۽ جيڪي الله ۽ پيغمبر جي تابعداري ڪندا سي انھن سان گڏ رھندڙ آھن جن تي الله فضل ڪيو آھي (يعني) پيغمبر ۽ صديق ۽ شھيد ۽ صالح، ۽ اُھي چڱا سنگتي آھن.');
INSERT INTO "amroti" VALUES(4,70,'4:70','اِھو فضل الله جي طرفان آھي، ۽ الله ڄاڻندڙ بس آھي.');
INSERT INTO "amroti" VALUES(4,71,'4:71','اي ايمان وارؤ پنھنجي بچاءُ جو سامان وٺو پوءِ لڙائيءَ لاءِ ٽولي ٽولي ٿي نڪرو يا گڏجي نڪرو.');
INSERT INTO "amroti" VALUES(4,72,'4:72','۽ بيشڪ اوھان مان ڪو (اھڙو) آھي جو (ڄاڻي واڻي) دير ڪندو آھي، پوءِ جيڪڏھن اوھان کي ڪو اھنج پھچندو ته چوندو آھي ته بيشڪ الله مون تي احسان ڪيو جو (آءٌ) ساڻن نه حاضر ھوس.');
INSERT INTO "amroti" VALUES(4,73,'4:73','۽ جيڪڏھن الله (جي پار) کان ڪو احسان اوھان کي پھچي ته (انھيءَ وانگر جو) ڄڻ ته اوھان جي ۽ سندس وچ ۾ دوستي نه ھئي ضرور چوندو آھي ته مون کي ارمان آھي جيڪر ساڻن ھجان ھا ته وڏي مراد ماڻيان ھا.');
INSERT INTO "amroti" VALUES(4,74,'4:74','پوءِ اُنھن (مؤمنن) کي جڳائي ته الله جي واٽ ۾ وڙھن جيڪي دنيا جي حياتيءَ کي آخرت سان وڪڻندا آھن، ۽ جيڪو الله جي واٽ ۾ وڙھندو پوءِ ماريو ويو يا غالب ٿيو تنھن کي وڏو اجر سگھوئي ڏينداسون.');
INSERT INTO "amroti" VALUES(4,75,'4:75','۽ (اي مسلمانؤ) اوھان کي ڇا (ٿيو) آھي؟ جو الله جي واٽ ۾ نٿا وڙھو؟ حالانڪ ھيڻن مڙسن ۽ زالن ۽ ٻارن مان اُھي آھن جي چون ٿا ته اي اسان جا پالڻھار ھن شھر مان جنھن جا رھاڪو ظالم آھن اسان کي (ٻاھر) ڪڍ، ۽ پاڻ وٽان اسان جو (ڪو) سنڀاليندڙ ڪر، ۽ پاڻ وٽان اسان جو ڪو مددگار ڪر.');
INSERT INTO "amroti" VALUES(4,76,'4:76','جيڪي مؤمن آھن سي الله جي واٽ ۾ وڙھندا آھن، ۽ جيڪي ڪافر آھن سي شيطان جي واٽ ۾ وڙھندا آھن تنھنڪري شيطان جي ساٿين کي ماريو، ڇوته شيطان جي ٺڳي ھيڻي آھي.');
INSERT INTO "amroti" VALUES(4,77,'4:77','(اي پيغمبر) اُنھن ڏانھن نه ڏٺو اٿيئي ڇا؟ جن کي چيو ويو ته پنھنجن ھٿن کي (ويڙھ کان) جھليو ۽ نماز پڙھو ۽ زڪٰوة ڏيو، پوءِ جنھن مھل مٿن ويڙھ لازم ڪئي ويئي تنھن مھل منجھانئن اوچتو ھڪ ٽولي الله جي خوف وانگر ماڻھن کان ڊڄي ٿي بلڪ ان کان به وڌيڪ ڊڄي ٿي، ۽ چوڻ لڳا ته اي اسانجا پالڻھار ڇالاءِ اسان تي ويڙھ لازم ڪئي اٿيئي، ويجھي وقت تائين تو اسان کي ڇونه ڍر ڏني؟ (اي پيغمبر) چؤ ته دنيا جو سامان ٿورڙو آھي، ۽ آخرت انھيءَ لاءِ ڀلي آھي جيڪو پرھيزگاري ڪندو، ۽ تند جيترو به ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(4,78,'4:78','جتي ھوندؤ اُتي اوھان کي موت پھچندو جيتوڻيڪ مُحڪم ڪوٽن ۾ ھوندؤ، ۽ جيڪڏھن کين ڪا چڱائي پھچندي آھي ته چوندا آھن ته ھي الله وٽان (پھتي) آھي، ۽ جيڪڏھن کين ڪو اھنج پھچندو آھي ته ھي تو وٽان پھتو آھي، (کين) چؤ ته (ڏک سک) سڀ الله وٽان آھي، پوءِ ھن قوم کي ڇا (ٿيو) آھي؟ جو ڳالھ جي سمجھڻ کي ويجھو (ئي) نه آھن.');
INSERT INTO "amroti" VALUES(4,79,'4:79','توکي جيڪا ڪا چڱائي پھچي ٿي سا الله وٽان (ڄاڻ)، ۽ توکي جيڪو ڪو اھنج پھچي ٿو سو پنھنجي نفس (جي ڪمن جي شامت) کان (ڀانءِ)، ۽ توکي ماڻھن لاءِ پيغمبر ڪري موڪليوسون، ۽ الله شاھد بس آھي.');
INSERT INTO "amroti" VALUES(4,80,'4:80','جيڪو پيغمبر جي فرمانبرداري ڪندو تنھن بيشڪ الله جي فرمانبرداري ڪئي، ۽ جيڪو ڦرندو تن تي توکي نگھبان ڪري نه موڪليو اٿئون.');
INSERT INTO "amroti" VALUES(4,81,'4:81','۽ (منافق تو وٽ) چوندا آھن فرمانبرداري (ڪنداسون) پوءِ جڏھن تو وٽان ھليا ويندا آھن تڏھن منجھانئن ھڪ ٽولي تنھنجي ڳالھين جي ابتڙ رات جو صلاحون ڪندي آھي، ۽ جيڪي رات جو صلاحون ڪندا آھن سي الله لکندو آھي، تنھنڪري کانئن مُنھن موڙ ۽ الله تي ڀروسو ڪر، ۽ الله وڪيل بس آھي.');
INSERT INTO "amroti" VALUES(4,82,'4:82','قرآن ۾ ڇونه سوچ ڪندا آھن، جيڪڏھن الله کانسواءِ ٻئي (ڪنھن) وٽان ھجي ھا ته منجھس گھڻو اختلاف لھن ھا.');
INSERT INTO "amroti" VALUES(4,83,'4:83','۽ جڏھن وٽن امن جي يا ڀَو جي ڪا خبر ايندي آھي ته ان کي پڌرو ڪندا آھن، ۽ جيڪڏھن ان کي پيغمبر ڏانھن ۽ پنھنجن (مسلمانن) مان اميرن ڏانھن پھچائين ھا ته منجھائن جيڪي اُنکي جاچيندا آھن سي ضرور ان کي (چڱي طرح) ڄاڻن ھا، ۽ جيڪڏھن الله جو فضل ۽ سندس ٻاجھ اوھان تي نه ھجي ھا ته ٿورڙن کانسواءِ ضرور شيطان جي پٺيان لڳو ھا.');
INSERT INTO "amroti" VALUES(4,84,'4:84','پوءِ (اي پيغمبر) الله جي واٽ ۾ ويڙھ ڪر، تنھنجي سِر کانسواءِ توتي بار نه رکيو ويو آھي ۽ مؤمنن کي ھمٿاءِ، ويجھو ئي الله ڪافرن جي ويڙھ کي روڪڻ تي آھي، ۽ الله ويڙھ جي ڪري ڏاڍو زور وارو ۽ سزا ڏيڻ ۾ (به) ڏاڍو زور وارو آھي.');
INSERT INTO "amroti" VALUES(4,85,'4:85','جيڪو چڱيءَ ڳالھ جي سفارش ڪندو تنھن لاءِ منجھانئس حصّو آھي، جيڪي بڇڙي ڳالھ جي سفارش ڪندو تنھن لاءِ منجھانئس حصّو آھي، ۽ الله سڀ شيءِ تي نگھبان آھي.');
INSERT INTO "amroti" VALUES(4,86,'4:86','۽ جڏھن اوھانکي سلام ڪرڻ سان تعظيم ڏني وڃي ته ان کان بھتر تعظيم سان (سلام) ورايو يا اوترو ورايو، ڇوته الله سڀڪنھن شيءِ جو حساب ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(4,87,'4:87','الله (اُھو آھي) جو اُن کانسواءِ ڪو عبادت جو لائق نه آھي، قيامت جي ڏينھن جنھن ۾ (ڪو) شڪ نه آھي اوھان کي ضرور گڏ ڪندو، ۽ الله کان ڪير (پنھنجي) ڳالھ ۾ زور سچو آھي.');
INSERT INTO "amroti" VALUES(4,88,'4:88','اوھان کي ڇا (ٿيو) آھي جو منافقن جي بابت ٻه ٽوليون (ٿي پيا) آھيو ۽ الله سندن ڪرتوتن سببان کين اونڌو ڪيو آھي، جنھن کي الله ڀلايو تنھن کي اوھين ھدايت ڪرڻ گھُرندا آھيو ڇا؟ جنھن کي الله ڀلائيندو آھي تنھن لاءِ ڪا واٽ ڪڏھن نه لھندين.');
INSERT INTO "amroti" VALUES(4,89,'4:89','(اُھي) گھرندا آھن ته جيڪر اوھين ڪافر ٿيو جھڙا پاڻ ڪافر آھن ته سڀ ھڪ جھڙا ٿيون پوءِ منجھائن ڪنھن کي ايستائين دوست ڪري نه وٺو جيستائين الله جي واٽ ۾ وطن ڇڏين، پوءِ جيڪڏھن ڦري وڃن ته پڪڙيونِ ۽ جتي لھونِ تتي قتل ڪريونِ، ۽ منجھائن ڪنھنکي نڪي دوست ۽ نڪي مددگار ڪري وٺو.');
INSERT INTO "amroti" VALUES(4,90,'4:90','پر (انھن سان نه وڙھو) جيڪي اھڙيءَ قوم سان ڳنڍيل ھجن جو اوھان جي ۽ انھن جي وچ ۾ انجام ٿيل ھجي يا اوھان وٽ ھن حال ۾ اچن جو اوھان سان وڙھڻ يا پنھنجي قوم سان وڙھڻ کان سندن سينا دِڪ ٿيا ھجن، ۽ جيڪڏھن الله گھري ھا ته کين اوھان تي غالب ڪري ھا پوءِ اوھان کي ضرور مارين ھا، پوءِ جيڪڏھن اوھان کان پاسو ڪن ۽ اوھان سان نه وڙھن ۽ اوھان ڏانھن صُلح (جي پيغام) کي اُڇلين پوءِ الله اوھان لاءِ مٿن ڪا ويڙھ جي واٽ نه ڪئي آھي.');
INSERT INTO "amroti" VALUES(4,91,'4:91','ٻيءَ ٽوليءَ کي (اوھين) سگھوئي لھندؤ جو اوھان کان امن ۾ ھجڻ گھرندا ۽ پنھنجيءَ قوم کان (به) امن ۾ ھجڻ گھرندا، جڏھن ڪڏھن ڪنھن فتني ڏانھن سڏيا ويندا تڏھن منجھس جھٽ پٽ اونڌي منھن ڪري پوندا، پوءِ جيڪڏھن اوھان کان پاسو نه ڪن ۽ نڪي اوھان ڏانھن صُلح جي پيغام کي اُڇلين ۽ نڪي پنھنجي ھٿن کي (ويڙھ کان) جھلين ته انھن کي پڪڙيو ۽ جتي انھن کي لھو تِتي اُنھن کي ماريو، ۽ انھن تي اسان اوھان کي پڌرو غلبو ڏنو آھي.');
INSERT INTO "amroti" VALUES(4,92,'4:92','۽ مؤمنن جو مارڻ ڪنھن مؤمن کي ڀُل چُڪ کانسواءِ (ڪنھن حال ۾) نه جڳائي، ۽ جيڪو مؤمن ڀُل چُڪ سان ماري ته (ھڪ ته) مؤمن ٻانھي کي آجو ڪري ۽ (ٻيو ته) چَٽي سندس وارثن کي پھچائي پر جي بخشين (ته انھن کي اختيار آھي)، پوءِ جيڪڏھن اوھان جي ويرين جي قوم مان آھي ۽ اُھو مؤمن ھجي ته (ڇڙو) مؤمن ٻانھي کي آجو ڪرڻ لازم آھي (۽ چٽي معاف آھي)، ۽ جيڪڏھن انھيءَ قوم مان آھي جو اوھان جي ۽ انھن جي وچ ۾ پڪو انجام ٿيل آھي ته چٽي سندس وارثن کي پھچائجي ۽ مؤمن ٻانھي کي آجو ڪرڻ لازم آھي، ۽ جيڪو نه لھي ته ٻه مھينا لڳو لڳ روزا رکي، (اھو ڪُفّارو) الله (جي پار) کان توبه جي (قبول ٿيڻ) ٿيڻ لاءِ آھي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,93,'4:93','۽ جيڪو مؤمن کي ڄاڻي واڻي ماريندو تنھن جي سزا دوزخ آھي منجھس سدائين رھڻ وارو آھي ۽ الله مٿس ڏمربو ۽ مٿس لعنت ڪندو ۽ اُن لاءِ وڏو عذاب تيار ڪيو اٿس.');
INSERT INTO "amroti" VALUES(4,94,'4:94','اي ايمان وارؤ جڏھن اوھين الله جي واٽ ۾ (جھاد لاءِ) مسافري ڪريو تڏھن پوري طرح جاچيو ۽ جيڪو اوھانکي اَلسَّلام ڪري تنھن کي اوھين دنيا جي حياتيءَ جو سامان طلبيندڙ ٿي نه چؤ ته تون مؤمن نه آھين، ڇوته الله وٽ گھڻيون غنيمتون آھن، اڳ اوھين به اھڙا ھيؤ پوءِ الله اوھان تي احسان ڪيو پوءِ اوھين (پوري طرح) جاچيو، ڇوته جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(4,95,'4:95','مؤمنن مان ڪنھن عذر ڌاران (جھاد کان گھر ۾) ويھي رھندڙ ۽ الله جي واٽ ۾ پنھنجي مالن ۽ پنھنجن سِرن سان جھاد ڪندڙ (اِھي ٻئي) برابر نه آھن، پنھنجن مالن ۽ پنھنجن سِرن سان جھاد ڪندڙن کي الله (گھر ۾) ويھي رھندڙن کان مرتبي ۾ ڀلو ڪيو آھي، ۽ الله ھر ھڪ کي چڱائيءَ جو وعدو ڏنو آھي، ۽ الله جھاد ڪندڙن کي ويھي رھندڙن کان وڏي اجر جي ڪري ڀلو ڪيو آھي.');
INSERT INTO "amroti" VALUES(4,96,'4:96','پاڻ وٽان درجا ۽ بخشش ۽ ٻاجھ ڪئي اٿس، ۽ الله بخشڻھار ۽ مھربان آھي.');
INSERT INTO "amroti" VALUES(4,97,'4:97','بيشڪ جن پاڻ تي ظلم ڪيو آھي تن کي ملائڪ مارڻ مھل چوندا آھن ته اوھين ڪھڙي (حال) ۾ ھيؤ؟ اُھي چوندا ته اسين مُلڪ ۾ عاجز هُئاسون، (ملائڪ کين) چوندا ته الله جو مُلڪ ويڪرو نه ھو ڇا؟ جو اُن ۾ لڏي وڃو ھا، پوءِ اُنھن جي جاءِ دوزخ آھي، ۽ اُھو ھنڌ بڇڙو آھي.');
INSERT INTO "amroti" VALUES(4,98,'4:98','پر مڙسن ۽ زالن ۽ ٻارن مان ھيڻا جو ڪا واہ لھي نه سگھندا آھن ۽ نڪا نڪرڻ جي ڪا واٽ وٺي سگھندا آھن.');
INSERT INTO "amroti" VALUES(4,99,'4:99','ويجھو انھن کي الله معاف ڪرڻ تي آھي، ۽ الله معافي ڏيندڙ بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(4,100,'4:100','جيڪو الله جي واٽ ۾ وطن ڇڏيندو سو زمين ۾ گھڻيون رھڻ جون جايون ۽ (روزيءَ) جي ڪشادگي لھندو، ۽ جيڪو الله (جي واٽ ۾) ۽ سندس پيغمبر ڏانھن ھجرت ڪندڙ ٿي پنھنجي گھر کان نڪرندو پوءِ کيس موت پھچندو تنھن جو اجر بيشڪ الله تي (لازم) آھي، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(4,101,'4:101','۽ جڏھن اوھين ملڪ ۾ مسافري ڪريو تڏھن جيڪڏھن ڊڄو ٿا ته ڪافر اوھان کي پريشان ڪندا ته نماز مان ڪجھ گھٽائڻ ۾ اوھان تي گناہ ڪونھي، ڇوته ڪافر اوھان جا پڌرا ويري آھن.');
INSERT INTO "amroti" VALUES(4,102,'4:102','۽ (اي پيغمبر ويڙھ مھل) جڏھن منجھن ھجين پوءِ کين نماز پڙھائين ته منجھائن ھڪ ٽوليءَ کي توسان (نماز ۾) بيھڻ گھرجي ۽ پنھنجن ھٿيارن کي (ساڻ) وٺڻ گھرجين، پوءِ جنھن مھل (اُھي) سجدو ڏيئي رھن (تنھن مھل) اوھان جي پوئتان ٿين، ۽ جنھن ٻئي ٽوليءَ نماز نه پڙھي آھي سي اچن تن کي توسان نماز پڙھڻ گھرجي ۽ پنھنجي بچاءَ (جي شين) ۽ پنھنجن ھٿيارن کي وٺڻ گھرجين، ڪافر گھرندا آھن ته جيڪر اوھين پنھنجن ھٿيارن ۽ پنھنجي سامان کان (ڪجھ) بي خبر ٿيو ته اوھان تي ھڪ ئي ڀيري حملو ڪن، ۽ جيڪڏھن اوھان کي مينھن کان ڪو اھنج ھجي يا اوھين بيمار ھجو ته اوھان تي گناہ نه آھي ته پنھنجا ھٿيار لاھي رکو، ۽ پنھنجي بچاءَ جون شيون (ساڻ) وٺو، بيشڪ الله ڪافرن لاءِ خواري ڏيندڙ عذاب تيار ڪيو آھي.');
INSERT INTO "amroti" VALUES(4,103,'4:103','پوءِ جڏھن نماز پوري ڪريو (تڏھن) بيٺي ۽ ويٺي ۽ پنھنجي پاسن ڀر الله کي ياد ڪريو، پوءِ جڏھن اوھين دلجاءِ ڪريو ته پوءِ (اصلي دستور تي) نماز پڙھو، بيشڪ نماز مؤمنن تي ٺھرايل وقت ۾ فرض آھي.');
INSERT INTO "amroti" VALUES(4,104,'4:104','۽ ڪافرن جي ڳول ڦول ۾ سُستي نه ڪريو، جيڪڏھن اوھين ڏکوئبا آھيو ته اُھي به اوھان جھڙا ڏکوئبا آھن، ۽ اوھين الله ۾ اھا اميد ٿا رکو جا اميد انھن کي نه آھي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,105,'4:105','(اي پيغمبر) بيشڪ اسان توڏانھن سچو ڪتاب ھن لاءِ لاٿو آھي ته جئن الله توکي سيکاريو آھي تئن ماڻھن جي وچ ۾ فيصلو ڪرين، ۽ خيانت ڪندڙن جي پاس نه ڪر.');
INSERT INTO "amroti" VALUES(4,106,'4:106','۽ الله کان بخشش گُھر، ڇوته الله تعالىٰ بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(4,107,'4:107','۽ جيڪي پنھنجن نفسن سان خيانت ڪندا آھن تن جي پاران (تون) نه جھيڙ، بيشڪ الله خيانتي گنھگار کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(4,108,'4:108','ماڻھن کان لڪندا آھن ۽ الله کان لڪي نه سگھندا ۽ جڏھن رات جو اُھا صلاح ڪندا آھن جنھن صلاح کان الله راضي نه آھي (تڏھن) اُھو (الله) ساڻن آھي، ۽ جيڪي ڪندا آھن تنھن کي الله گھيرو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(4,109,'4:109','خبردار اوھين اھڙي قوم آھيو جو دنيا جي حياتي ۾ (ته) سندن پاران بحث ڪندا آھيو، پوءِ قيامت جي ڏينھن سندن پاران الله سان ڪير جھيڙو ڪندو؟ يا مٿن ڪير ڀرجھلو ٿيندو؟');
INSERT INTO "amroti" VALUES(4,110,'4:110','۽ جيڪو بڇڙو ڪم ڪندو يا پاڻ تي ظلم ڪندو وري الله کان بخشش گھرندو سو الله کي بخشڻھار مھربان لھندو.');
INSERT INTO "amroti" VALUES(4,111,'4:111','۽ جيڪوئي گناہ ڪري ٿو سو پاڻ لاءِ ئي ڪري ٿو، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,112,'4:112','۽ جيڪو ڪا خطا يا گناہ ڪري وري اُھا (تھمت) ڪنھن بيگناہ تي ھڻي تنھن بيشڪ بُھتان ۽ پڌرو گناہ کنيو.');
INSERT INTO "amroti" VALUES(4,113,'4:113','۽ جيڪڏھن توتي الله جو فضل ۽ سندس ٻاجھ نه ھجي ھا ته منجھانئن ھڪ ٽوليءَ توکي غلطي ۾ وجھڻ جي پڪي نيت ڪئي ھئي، ۽ (اُھي) پاڻ کانسواءِ (ڪنھن کي) نه ڀلائيندا آھن ۽ توکي ڪوبه ضرر نه پھچائيندا، ۽ الله توتي ڪتاب ۽ دانائي لاٿي آھي ۽ جيڪي تون نه ڄاڻندو ھئين سو توکي سيکاريائين، ۽ توتي الله جو وڏو فضل آھي.');
INSERT INTO "amroti" VALUES(4,114,'4:114','سندين گھڻين ڳجھين صلاحن ۾ ڪا چڱائي نه آھي پر جنھن خيرات ڏيڻ يا چڱي ڪم يا ماڻھن جي وچ ۾ سڌاري ڪرڻ جو حڪم ڪيو (تنھن جي صلاح ۾ چڱائي آھي)، ۽ جيڪو اُھو ڪم الله جي رضامندي ڳولڻ لاءِ ڪندو پوءِ سگھوئي اُن کي وڏو اجر ڏينداسون.');
INSERT INTO "amroti" VALUES(4,115,'4:115','۽ جيڪو رستي جي پڌري ٿيڻ کانپوءِ پيغمبر جي مخالفت ڪندو ۽ مؤمنن جي واٽ کانسواءِ ٻي واٽ وٺندو سو جيڏانھن متوجه ٿيو آھي تيڏانھن کيس ڦيرينداسون ۽ کيس دوزخ ۾ گھيرينداسون، ۽ (اھا) جاءِ بڇڙي آھي.');
INSERT INTO "amroti" VALUES(4,116,'4:116','بيشڪ الله ھيءُ (ڏوہ) نه بخشيندو جو (ڪنھن کي) ساڻس شريڪ ڪيو وڃي ۽ اُن کانسواءِ (ٻيو گناہ) جنھن لاءِ گھرندو تنھن کي بخشيندو، ۽ جيڪو الله سان (ڪنھن کي) شريڪ بڻائيندو سو بيشڪ وڏي ڀُل ۾ ڀلو.');
INSERT INTO "amroti" VALUES(4,117,'4:117','(ھيءُ مشرڪ) ان (الله) کانسواءِ عورتن کي ئي سڏيندا آھن، ۽ (حقيقت ۾) تڙيل شيطان کانسواءِ ٻئي کي نه سڏيندا آھن.');
INSERT INTO "amroti" VALUES(4,118,'4:118','ان تي الله لعنت ڪئي آھي، ۽ (شيطان) چيو ته تنھنجي ٻانھن مان مُقرّر ڪيل حصّو ضرور وٺندس.');
INSERT INTO "amroti" VALUES(4,119,'4:119','۽ کين ضرور گمراہ ڪندس ۽ کين ضرور سَڌن ۾ وجھندس ۽ کين ضرور حُڪم ڪندس پوءِ ضرور ڍورن جي ڪنن کي چيريندا ۽ کين ضرور حُڪم ڪندس ته الله جي جوڙيل (صورت) کي ڦٽائيندا (رھندا) ۽ جيڪو الله کانسواءِ شيطان کي دوست ڪري وٺندو تنھن بيشڪ پڌرو ڇيو پاتو.');
INSERT INTO "amroti" VALUES(4,120,'4:120','شيطان کين انجام ڏيندو آھي ۽ کين سَڌن ۾ وجھندو آھي، ۽ شيطان ٺڳيءَ کانسواءِ (ٻيو ڪو) انجام نه ڏيندو اٿن.');
INSERT INTO "amroti" VALUES(4,121,'4:121','اھڙن (ماڻھن) جي جاءِ دوزخ آھي، ۽ اُن کان ڀڄڻ جي ڪا واہ نه لھندا.');
INSERT INTO "amroti" VALUES(4,122,'4:122','۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي سگھوئي (اھڙن) باغن ۾ داخل ڪنداسين جن جي ھيٺان واھيون وھنديون آھن منجھن سدائين رھڻ وارا آھن، (اھو) الله جو پڪو انجام آھي، ۽ انجام ڏيڻ ۾ الله کان وڌيڪ سچو ڪير آھي.');
INSERT INTO "amroti" VALUES(4,123,'4:123','(۽) نڪي اوھان جي سَڌن تي نڪي ڪتاب وارن جي سَڌن تي (ڇوٽڪارو) آھي، جيڪو ڪا مدائي ڪندو تنھن کي ان جي سزا ڏبي ۽ الله کانسواءِ پاڻ لاءِ نڪو سنڀاليندڙ ۽ نڪو مددگار لھندو.');
INSERT INTO "amroti" VALUES(4,124,'4:124','۽ جيڪي (ماڻھو) مرد يا زال مؤمن ٿي چڱا ڪم ڪندا سي بھشت ۾ گھڙندا ۽ تر جيترو به ظلم نه ڪبن.');
INSERT INTO "amroti" VALUES(4,125,'4:125','۽ دين جي ڪري انھيءَ کان وڌيڪ چڱو ڪير آھي؟ جنھن پنھنجو مُنھن (سر) الله لاءِ نوايو ۽ اُھو چڱائي ڪندڙ آھي ۽ ابراھيم حنيف (ھڪ طرفي) جي دين جي پيروي ڪئي اٿس، ۽ الله ابراھيم کي پنھنجو خاص دوست بڻايو ھو.');
INSERT INTO "amroti" VALUES(4,126,'4:126','جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله جو آھي، ۽ الله سڀڪنھن شيءِ تي گھيرو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(4,127,'4:127','(اي پيغمبر) زالن بابت توکان پڇن ٿا، چؤ ته الله انھن بابت اوھان کي حُڪم ڪري ٿو ۽ (پڻ) جيڪي اوھان کي (ھن) ڪتاب ۾ (اڳ) پڙھي ٻڌايو ويو آھي سو انھن يتيمن زالن بابت آھي جن کي سندن حق جيڪو انھن لاءِ لازم ڪيو ويو سو نه ڏيندا آھيو ۽ کين پرڻجڻ جي خواھش ڪندا آھيو ۽ (پڻ) ھيڻن ٻارن بابت (سو اُھو حُڪم قائم آھي) ۽ يتيمن (جي حقن ادا ڪرڻ) لاءِ انصاف تي بيھڪ وٺو، ۽ اوھين جيڪا ڪا چڱائي ڪندؤ تنھن کي الله ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(4,128,'4:128','۽ جيڪڏھن ڪا زال پنھنجي مڙس جي بي پرواھيءَ يا منھن موڙڻ کان ڊڄي ته ٻئي ڄڻا پاڻ ۾ صُلح ڪن (ڪنھن به قسم جو صُلح) ته ٻنھين تي گناھ نه آھي، ۽ صُلح ڀلو آھي، ۽ نفس (ته) بخل کي ويجھو ڪيا ويا آھن، ۽ جيڪڏھن اوھين چڱائي ڪندؤ ۽ پرھيزگار ٿيندؤ ته جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(4,129,'4:129','۽ اوھين زالن جي وچ ۾ ڪڏھن به برابري رکي نه سگھندؤ توڻيڪ گھڻي خواھش ڪريو تنھنڪري اوھين (ھڪ پاسي اھڙو) سارو لاڙو نه لڙو جو ٻيءَ کي لڙڪيل وانگر ڇڏيو، ۽ جيڪڏھن اوھين (پاڻ ۾) صُلح ڪريو ۽ پرھيزگار ٿيو ته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(4,130,'4:130','۽ جيڪڏھن ٻئي جدا ٿيندا ته الله پنھنجي فضل سان ھر ڪنھن کي آسودو ڪندو، ۽ الله (مھربانيءَ ۾) ڪشادگيءَ وارو حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,131,'4:131','جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله جو آھي، ۽ اوھان کان اڳ ڪتاب وارن کي ۽ (ھاڻي) خلاصو اوھان کي بيشڪ وصيت ڪئي اٿئون ته الله کان ڊڄو، ۽ جيڪڏھن نه مڃيندؤ ته (سمجھو ته) جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (خاص) الله جو آھي، ۽ الله بي پرواہ ساراھيل آھي.');
INSERT INTO "amroti" VALUES(4,132,'4:132','۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (خاص) الله جو آھي، ۽ الله ڀرجھلو بس آھي.');
INSERT INTO "amroti" VALUES(4,133,'4:133','اي ماڻھؤ جيڪڏھن الله گھري ته اوھان کي فنا ڪري ڇڏي ۽ ٻـين کي پيدا ڪري، ۽ الله انھيءَ (ڪم) تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(4,134,'4:134','جيڪو دنيا ۾ (پنھنجين چڱائين جو) ثواب گھرندو آھي ته الله وٽ دنيا ۽ آخرت جو ثواب آھي، ۽ الله ٻڌندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(4,135,'4:135','اي ايمان وارؤ! انصاف تي بيھندڙ الله ڪارڻ شاھدي ڏيندڙ ھجو توڻيڪ اوھان کي پاڻ تي يا ماءُ پيءُ يا مٽن تي ھجي، جيڪڏھن (اُھو ماڻھو جنھن تي حق ثابت ٿئي ٿو سو) آسودو ھجي يا سڃو ته الله اُنھن تي وڌيڪ مھربان آھي، پوءِ انصاف ڪرڻ ۾ سَڌن جي پٺيان نه لڳو، ۽ جيڪڏھن اوھين وچڙائي ڳالھائيندؤ يا منھن موڙيندؤ ته جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(4,136,'4:136','اي مسلمانؤ الله کي ۽ سندس پيغمبر کي ۽ جيڪو ڪتاب پنھنجي پيغمبر تي لاٿائين ۽ جيڪو کانئس اڳ لاٿائين تنھن کي (به مڃيو)، ۽ جيڪو الله ۽ سندس ملائڪن ۽ سندس ڪتابن ۽ سندس پيغمبرن ۽ قيامت جي ڏينھن کي نه مڃيندو سو بيشڪ وڏيءَ ڀل ۾ ڀلو.');
INSERT INTO "amroti" VALUES(4,137,'4:137','جن ايمان آندو وري ڪفر ڪيائون وري ايمان آندائون وري ڪفر ڪيائون وري ڪفر ۾ وڌندا ويا تن کي الله ڪڏھن نه بخشيندو ۽ نڪي کين ڪا واٽ ڏيکاريندو.');
INSERT INTO "amroti" VALUES(4,138,'4:138','(اي پيغمبر) انھن منافقن کي (ھيءَ) سڌ ڏي ته انھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(4,139,'4:139','جيڪي مؤمنن ڌاران ڪافرن کي دوست ڪري وٺندا آھن، اُنھن وٽان مانُ گھرندا آھن ڇا؟ ڇوته ھيڪاندو مانُ الله جوئي آھي.');
INSERT INTO "amroti" VALUES(4,140,'4:140','۽ بيشڪ الله اوھان تي ڪتاب ۾ (ھيءُ حڪم) لاٿو آھي ته جڏھن (اوھين) ٻڌو ته الله جي آيتن جو انڪار ڪيو وڃي ٿو ۽ مٿن ٺـٺولي ڪئي وڃي ٿي تڏھن (اوھين) ساڻن ايسين نه ويھو جيسين ٻيءَ ڪنھن ڳالھ ۾ بحث نه ڪن، اوھين اُنھيءَ مھل (جي وھندؤ ته) انھن جھڙا ٿيندؤ، ڇوته الله منافقن ۽ ڪافرن سڀني کي دوزخ ۾ گڏ ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(4,141,'4:141','اھي (منافق) اوھان جو انتظار ڪڍندا آھن، پوءِ جيڪڏھن الله (جي پارکان) اوھان لاءِ ڪا فتح آھي ته چون ٿا ته (اسين) اوھان سان گڏ نه هُئاسون ڇا؟ ۽ جيڪڏھن (سوڀ جو) ڀاڱو ڪافرن جو آھي ته ڪافرن کي چون ٿا ته اسين اوھان تي غالب نه آھيون ڇا؟ ۽ مؤمنن کان اوھان کي نه بچايوسون ڇا؟ پوءِ الله قيامت جي ڏينھن اوھانجي وچ ۾ نبيرو ڪندو، ۽ الله ڪافرن کي مؤمنن تي غلبي جي (ڪا) واٽ نه ڪندو.');
INSERT INTO "amroti" VALUES(4,142,'4:142','منافق (پنھنجي ڀانئڻ ۾) الله کي دلبو ڏيندا آھن ۽ (حقيقت ڪري) الله کين دلبو ڏيندڙ آھي، ۽ جڏھن نماز ڏانھن اٿندا آھن تڏھن آرسي ٿي اُٿندا آھن رڳو ماڻھن کي ڏيکاريندا آھن ۽ ٿورڙي کانسواءِ الله کي ياد نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(4,143,'4:143','نه ھِن ٽولي ڏانھن آھن ۽ نڪي ھُن ٽولي ڏانھن آھن انھيءَ وچ ۾ حيران آھن، ۽ جنھن کي الله ڀلائيندو تنھن لاءِ ڪڏھن واٽ نه لھندين.');
INSERT INTO "amroti" VALUES(4,144,'4:144','اي ايمان وارؤ اوھين ڪافرن کي مؤمنن کانسواءِ دوست ڪري نه وٺو، پاڻ تي الله جو پڌرو الزام ثابت ڪرڻ گھرندا آھيو ڇا؟');
INSERT INTO "amroti" VALUES(4,145,'4:145','بيشڪ مُنافق باھ جي ھيٺين طبقي ۾ ھوندا، ۽ انھن جو مددگار ڪوئي نه لھندين.');
INSERT INTO "amroti" VALUES(4,146,'4:146','پر جن توبه ڪئي ۽ پاڻ سڌاريو ۽ الله کي چنبڙي ورتو ۽ خالص الله جا حُڪم مڃيا سي مؤمنن سان آھن، ۽ الله مؤمنن کي وڏو اجر سگھو ڏيندو.');
INSERT INTO "amroti" VALUES(4,147,'4:147','جيڪڏھن (سندس نعمتون ڄاڻي) شڪر ڪندؤ ۽ ايمان آڻيندؤ ته الله اوھانکي ڇالاءِ عذاب ڪندو؟ ۽ الله ته قدر سڃاڻندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(4,148,'4:148','الله مظلوم کانسواءِ ٻئي ڪنھن جو (ڪنھين کي) وڏي سڏ (۽ کليل طرح) بڇڙو ڳالھائڻ پسند نه ڪندو آھي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(4,149,'4:149','جيڪڏھن اوھين ڪا چڱائي پڌري طرح ڪندا آھيو يا اُھا لڪائي ڪندا آھيو يا مدائي کان ٽارو ڪندا آھيو ته بيشڪ الله معافي ڏيندڙ وس وارو آھي.');
INSERT INTO "amroti" VALUES(4,150,'4:150','بيشڪ جيڪي الله ۽ سندس پيغمبرن کي نه مڃيندا آھن ۽ الله ۽ سندس پيغمبرن جي وچ ۾ فرق ڪرڻ گھرندا آھن ۽ چوندا آھن ته ڪن پيغمبرن کي مڃيون ٿا ۽ ڪن (پيغمبرن) کي نه ٿا مڃيون ۽ اُنھيءَ وچ ۾ ڪا ٻي واٽ وٺڻ گھرندا آھن.');
INSERT INTO "amroti" VALUES(4,151,'4:151','اُھي شخص سي پڪ ڪافر آھن، ۽ ڪافرن لاءِ خواري ڏيندڙ عذاب تيار ڪيو اٿئون.');
INSERT INTO "amroti" VALUES(4,152,'4:152','۽ جن الله ۽ سندس پيغمبرن کي مڃيو ۽ منجھانئن ڪنھن ھڪ جي وچ ۾ فرق نه ڪيو اُنھن کي الله سندن اجر سگھو ڏيندو، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(4,153,'4:153','(اي پيغمبر) ڪتاب وارا توکي سوال ٿا ڪن ته آسمان کان (ھڪ ئي ڀيري) مٿن ڪتاب لاھين (ان جو خيال نه ڪر) پوءِ تحقيق ان کان (به) وڏو سوال موسىٰ کان ڪيو ھوائون پوءِ چيائون ته اسان کي الله (اکين سان) چٽو ڏيکار پوءِ سندن گناہ سببان مٿن وڄ ڪري، وري پاڻ وٽ مُعجزن جي اچڻ کانپوءِ گابي کي (خُدا ڪري) ورتائون پوءِ اُن (سندن ڏوھ) کان معافي ڏنيسون، ۽ موسىٰ کي پڌرو غلبو ڏنوسون.');
INSERT INTO "amroti" VALUES(4,154,'4:154','۽ کانئن انجام وٺڻ لاءِ سندن مٿان (جبل) طُور کڙو ڪيوسون ۽ کين چيوسون ته (شھر جي) دروازي کان سجدو ڪندڙ ٿي گھڙو ۽ کين (ھيءُ به) فرمايوسون ته ڇنڇر (جي ڏينھن مڇي نه مارڻ جي حڪم ڪيل) ۾ حد کان نه لنگھو ۽ کانئن پڪو انجام ورتوسون.');
INSERT INTO "amroti" VALUES(4,155,'4:155','پوءِ سندن انجام ڀڃڻ ۽ الله جي آيتن کي سندن نه مڃڻ ۽ پيغمبرن کي سندن ناحق مارڻ (سببان سزا جوڳا ٿيا) ۽ سندن (انھيءَ) چوڻ سببان ته اسان جو دليون ڍڪيل آھن، (نه!) بلڪ الله سندن ڪفر سببان مٿن مُھر ھنئي آھي تنھنڪري ٿورن کانسواءِ ايمان نه آڻيندا.');
INSERT INTO "amroti" VALUES(4,156,'4:156','۽ سندن ڪفر سببان ۽ مريم تي سندن وڏي بُھتان چوڻ سببان.');
INSERT INTO "amroti" VALUES(4,157,'4:157','۽ سندن ھن چوڻ سببان ته الله جي پيغمبر مَسِيح عيسىٰ پٽ مريم جي کي ڪُٺو اٿئون، حالانڪ نه کيس ڪُٺائون ۽ نڪي کيس سوريءَ تي چاڙھيائون پر انھن کي شُبه ۾ وڌو ويو، ۽ بيشڪ جن ان بابت تڪرار ڪيو سي کانئس شڪ ۾ آھن، گمان کانسواءِ کين ان جي ڪا پڪي ڄاڻپ نه آھي، ۽ پڪ سان نه ڪُٺو اٿن.');
INSERT INTO "amroti" VALUES(4,158,'4:158','بلڪ الله کيس پاڻ ڏانھن مٿي کڻي ورتو، ۽ الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,159,'4:159','۽ ڪتاب وارن مان ڪوئي نه رھندو پر سندس موت کان اڳ (سڀئي) مٿس ضرور ايمان آڻيندا، ۽ قيامت جي ڏينھن (عيسى) مٿن شاھد ھوندو.');
INSERT INTO "amroti" VALUES(4,160,'4:160','پوءِ يھودين جي ظلم سببان (گھڻيون) سٺيون شيون جي انھن لاءِ حلال ڪيل ھيون سي مٿن حرام ڪيوسون ۽ (پڻ) گھڻن ماڻھن کي الله جي واٽ کان سندن جھلڻ سببان.');
INSERT INTO "amroti" VALUES(4,161,'4:161','۽ پڻ سندن وياج وٺڻ سببان حالانڪ بيشڪ کانئس جھليا (به) ويا ھئا ۽ (پڻ) ماڻھن جي مالن کي سندن ناحق کائڻ سببان (گھڻيون حلال شيون مٿن حرام ڪيون ويون)، ۽ اُنھن مان ڪافرن لاءِ ڏکوئيندڙ عذاب تيار ڪيو اٿئون.');
INSERT INTO "amroti" VALUES(4,162,'4:162','پر منجھانئن علم ۾ پڪا ۽ مؤمن جيڪي توڏانھن نازل ڪيو ويو تنھن کي ۽ جيڪي توکان اڳ نازل ڪيو ويو تنھن کي مڃيندا آھن ۽ نماز پڙھندڙ ۽ زڪٰوة ڏيندڙ ۽ الله ۽ قيامت جي ڏينھن کي مڃيندڙ آھن، سگھوئي انھن کي وڏو اجر ڏينداسون.');
INSERT INTO "amroti" VALUES(4,163,'4:163','اسان توڏانھن (اھڙيءَ طرح) وحي ڪيو جھڙي طرح نوح ڏانھن ۽ کانئس پوءِ (ٻـين) پيغمبرن ڏانھن وحي ڪيوھوسون، ۽ ابراھيم ۽ اسماعيل ۽ اسحاق ۽ يعقوب ۽ يعقوب جي اولاد ۽ عيسىٰ ۽ اَيُّوب ۽ يُونس ۽ ھارون ۽ سليمان ڏانھن وحي ڪيوسون، ۽ داؤد کي زبور ڏنوسون.');
INSERT INTO "amroti" VALUES(4,164,'4:164','۽ ٻيا (گھڻا) پيغمبر آھن جن جو ھن کان اڳ توکي بيان ٻڌايوسون ۽ (ٻيا گھڻا) پيغمبر آھن جن جو بيان توکي نه ٻڌايوسون، ۽ الله موسىٰ سان گفتگو ڪئي.');
INSERT INTO "amroti" VALUES(4,165,'4:165','پيغمبر خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ھن لاءِ آھن ته پيغمبرن (جي اچڻ) کانپوءِ ماڻھن جي ڪا حُجّت الله تي نه رھي، ۽ الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,166,'4:166','پر الله جيڪو (ڪتاب) توڏانھن لاٿو آھي تنھن لاءِ (پاڻ) شاھدي ٿو ڏئي ته اُھو پنھنجي علم سان نازل ڪيو اٿس، ۽ ملائڪ به شاھدي ٿا ڏين، ۽ الله گواہ بس آھي.');
INSERT INTO "amroti" VALUES(4,167,'4:167','بيشڪ جن انڪار ڪيو ۽ الله جي واٽ کان (ماڻھن) کي جھليو سي بيشڪ وڏي ڀُل ۾ ڀُلا.');
INSERT INTO "amroti" VALUES(4,168,'4:168','تحقيق جن انڪار ڪيو ۽ ظُلم ڪندا رھيا ته الله (جو شان اھڙو) نه آھي جو کين بخشي ۽ نڪي (اھڙو) آھي جو کين (ٻي) ڪا (سڌي) واٽ ڏيکاري.');
INSERT INTO "amroti" VALUES(4,169,'4:169','دوزخ جي واٽ کانسواءِ منجھس سدائين رھڻ وارا آھن، ۽ اھو الله تي سھنجو آھي.');
INSERT INTO "amroti" VALUES(4,170,'4:170','اي ماڻھو اوھان جي پالڻھار وٽان پيغمبر (مُحَمَّد صَلَّي اللهُ عَلَيۡہِ وَسَلَّم) اوھان وٽ سچ وٺي آيو آھي تنھنڪري (مٿس) ايمان آڻيو جو اوھان لاءِ چڱو آھي ۽ جيڪڏھن نه مڃيندؤ (ته اوھان جو نقصان آھي) ڇوته جيڪي آسمانن ۽ زمين ۾ آھي سو الله جو آھي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(4,171,'4:171','اي ڪتاب وارؤ پنھنجي دين ۾ حد کان نه لنگھو ۽ نڪي سچ کانسواءِ الله تي (ڪي ٻيو) چئو، عيسىٰ پُٽ مريم جو الله جو پيغمبر ۽ سندس (خاص) حُڪم (سان پيدا ڪيل) آھي، اُھو مريم ڏانھن ھَلايائين ۽ سندس پار کان ھڪ رُوح آھي، تنھنڪري الله ۽ سندس پيغمبرن کي مڃيو، ۽ (اوھين) ٽي (خدا) نه چئو، (اھڙي چوڻ کان پاڻ کي) جھليو (اِھو) اوھان لاءِ ڀلو آھي، ھڪ الله کانسواءِ ڪو عبادت جو لائق نه آھي، پُٽن جي ھجڻ کان اُھو پاڪ آھي، جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو اُن جو آھي، ۽ الله سنڀاليندڙ بس آھي.');
INSERT INTO "amroti" VALUES(4,172,'4:172','عيسىٰ الله جي ٻانھي ھُجڻ کان بنھ عار نه ڪندو آھي ۽ نڪي مُقرّب مَلائِڪ، ۽ جيڪو سندس ٻانھپ کان عار ڪندو ۽ وڏائي ڪندو تن سڀني کي سگھوئي (الله) پاڻ وٽ گڏ ڪندو.');
INSERT INTO "amroti" VALUES(4,173,'4:173','پوءِ جن ايمان آندو آھي ۽ چڱا ڪم ڪيا آھن تن کي سندن اجر پُورو ڏيندو ۽ (بلڪ) پنھنجي فضل سان کين وڌيڪ ڏيندو، ۽ جن (ٻانھي ھجڻ کان) عار ڪيو ۽ وڏائي ڪئي تن کي ڏکوئيندڙ عذاب جي سزا ڏيندو.');
INSERT INTO "amroti" VALUES(4,174,'4:174','اي ماڻھؤ بيشڪ اوھان وٽ اوھان جي پالڻھار کان ھڪ پڪي سند آئي آھي ۽ اوھان ڏانھن چِٽو سوجھرو (قرآن) لاٿوسون.');
INSERT INTO "amroti" VALUES(4,175,'4:175','پوءِ جن الله کي مڃيو ۽ کيس چنبڙي ورتو تن کي پنھنجي پاران ٻاجھ ۽ فضل ھيٺ سگھو داخل ڪندو ۽ پاڻ ڏانھن کين سڌو رستو ڏيکاريندو.');
INSERT INTO "amroti" VALUES(4,176,'4:176','(اي پيغمبر جنھن کي اولاد ۽ ماءُ پيءُ نه ھجي تنھنجي ميراث بابت) توکان پڇن ٿا، چؤ ته الله ڪلاله (جي ميراث) بابت اوھان کي فتوىٰ ٿو ڏئي، جيڪڏھن (ڪو اھڙو) مڙس مري جنھنکي اولاد نه آھي (۽ نه ماءُ پيءُ) ۽ کيس ھڪ ڀيڻ آھي ته جيڪي ڇڏيائين تنھنجو اڌ اُن لاءِ آھي، ۽ (جيڪڏھن اھڙي زال مري وڃي ۽ ڀاءُ جيئرو اٿن ته) ڀاءُ سندس وارث آھي سو تڏھن جو کيس اولاد نه ھجي، پوءِ جيڪڏھن ٻه ڀينر ھجن ته جيڪي ڇڏيائين تنھن مان ٻنھي لاءِ ٻه ٽھايون آھن، ۽ جيڪڏھن (وارث) ڀينرون ۽ ڀائر ھجن ته مڙس (يعني ڀاءُ) لاءِ ٻن زالن (ڀينرن) جي حصّي جيترو آھي، الله اوھان لاءِ بيان ڪندو آھي ته متان (اوھين) گمراہ ٿيو، ۽ الله سڀڪنھن شيءِ کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(5,1,'5:1','اي ايمان وارؤ (پنھنجن) انجامن کي پاڙيو، جيڪي اوھان تي پڙھبو تنھن کانسواءِ (ٻيا) چوپايا ڍور اوھان لاءِ حلال ڪيا ويا آھن پر اوھين احرام وارا ٿي شڪار کي حلال سمجھندڙ نه ٿيو، بيشڪ الله جيڪي گھرندو آھي سو حُڪم ڪندو آھي.');
INSERT INTO "amroti" VALUES(5,2,'5:2','اي ايمان وارؤ الله جي عبادت جي نشانين جي بي تعظيمي نه ڪريو ۽ نڪي تعظيم واري مھيني ۾ ۽ نڪي (ڪعبه ڏانھن موڪليل) قربانيءَ (جي جانور) جي ۽ نڪي قلائد (الله لاءِ نذر ڪيل جانورن جي ڳچين ۾ ٻڌل ڳانن) جي ۽ نڪي تعظيم واري گھر (بيت الله) ڏانھن ويندڙن جي جيڪي پنھنجي پالڻھار جو فضل ۽ رضامندي گھرندڙ آھن، ۽ جڏھن اوھين احرام کوليو تڏھن (ڀَلي) شڪار ڪريو، ۽ اوھان کي انھن ماڻھن جي دشمني جن اوھان کي تعظيم واري مسجد کان جھليو ھن ڳالھ جو سبب نه ٿئي جو (اوھين) حد کان لنگھو، ۽ چڱائي ۽ پرھيزگاريءَ تي پاڻ ۾ مدد ڏيو، ۽ گناہ ۽ حد کان لنگھڻ تي ھڪ ٻئي کي مدد نه ڏيو، ۽ الله کان ڊڄو، بيشڪ الله سخت عذاب ڪرڻ وارو آھي.');
INSERT INTO "amroti" VALUES(5,3,'5:3','اوھان تي حرام ڪيو ويو آھي مُئل جانور ۽ (وھندڙ) رت ۽ سُوئر جو گوشت ۽ جنھن شيءِ کي الله جي نالي کانسواءِ ٻئي جي نالي سان سڏيو وڃي (يعني الله کان سواءِ ٻئي جي نالي مڃتا ڪيل ھجي) ۽ گھُٽو ڏيئي ماريل ۽ ڌڪ سان مُئل ۽ ڪِرڻ سان مُئل ۽ ڍور جي اُکل سان مُئل (حرام ڪيو ويو آھي) ۽ جيڪو (جانور) ڦاڙيندڙ کاڌو (سو به) پر جنھنکي (مرڻ کان اڳ) ذبح ڪيوَ (سو حرام نه آھي) ۽ جو (جانور) آستانن تي ذبح ڪجي (سو حرام ڪيو ويو آھي) ۽ اوھانجو تيرن سان وراھڻ (به)، اِھي (مڙئي) گناہ جا ڪم آھن، اڄ ڪافر اوھان جي دين کان نا اميد ٿيا تنھنڪري کانئن نه ڊڄو ۽ مون کان ڊڄو، اڄ اوھان جو دين اوھان لاءِ ڪامل ڪيم ۽ اوھان تي پنھنجي نعمت پوري ڪيم ۽ اوھان لاءِ دين اسلام پسند ڪيم، پوءِ جيڪو گناہ ڏانھن نه لاڙو ڪندڙ ٿي بک ۾ لاچار ٿئي (۽ مٿيون حرام شيون کائي ته روا آھي)، پوءِ تحقيق الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(5,4,'5:4','(اي پيغمبر) توکان پڇن ته اُنھن لاءِ ڪھڙيون شيون حلال ڪيون ويون آھن، چؤ ته اوھان لاءِ (سڀ) سٺيون (شيون) حلال ڪيون ويون آھن ۽ (اُھو شڪار به حلال آھي) جيڪي شڪاري جانورن کي شڪار سيکاريندڙ ٿي (اوھان) سيکاريو آھي (يعني) جيڪي الله (شڪار ڪرڻ جو دستور) اوھان کي سيکاريو تنھن مان انھن کي سيکاريو ھجي، ته جيڪو (شڪار اُھي) اوھان لاءِ جھلين تنھن مان کائو ۽ اُن (جانور کي بڇڻ مھل) تي الله جو نالو (يعني بِسۡمِ اللهِ اَللهُ اَڪۡبرُ) چئو ۽ الله کان ڊڄو، بيشڪ الله سخت حساب وٺندڙ آھي.');
INSERT INTO "amroti" VALUES(5,5,'5:5','اڄ اوھان لاءِ سٺيون شيون حلال ڪيون ويون آھن، ۽ ڪتاب وارن جو طعام (به) اوھان لاءِ حلال آھي، ۽ اوھان جو طعام انھن لاءِ حلال آھي، ۽ (پڻ) پاڪدامن مؤمنياڻيون ۽ جن کي اوھان کان اڳ ڪتاب ڏنو ويو تن مان پاڪدامن (عورتون) جڏھن پرڻو ڪندڙ نه زنا ڪندڙ ۽ نه ڳجھي آشنائي رکندڙ ٿي کين سندن ڪابين ڏيو (ته حلال ڪيون ويون)، ۽ جيڪو ايمان (يعني اُنھن حُڪمن مڃڻ) جو انڪار ڪندو تنھنجو ڪيو ڪمايو بيشڪ ناس ٿي ويو، ۽ اُھو آخرت ۾ ڇيئي وارن مان آھي.');
INSERT INTO "amroti" VALUES(5,6,'5:6','اي ايمان وارؤ جڏھن اوھين نماز لاءِ اُٿو تڏھن پنھنجن مُنھن کي ۽ پنھنجن ھٿن کي ٺونٺين تائين ڌوئو ۽ پنھنجن مٿن کي مسح ڪريو ۽ پنھنجن پيرن کي ڀيڏين تائين (ڌوئو) ۽ جيڪڏھن اوھين جُنبي (يعني مٿي ميرا) ھجو ته پاڪ ٿيو (يعني تڙ ڪريو)، ۽ جيڪڏھن بيمار ھجو يا مسافريءَ تي (ھجو) يا اوھان مان ڪوئي پائخانه مان اچي يا اوھين زالن کي ويجھا ويا ھجو پوءِ اوھين پاڻي نه لھو ته پاڪ مٽيءَ سان تيمم ڪريو پوءِ پنھنجن مُنھن کي ۽ پنھنجن ھٿن کي منجھانئس مَسح ڪريو، ۽ الله اوھان تي ڪا تنگي نه گھرندو آھي پر گھُرندو آھي ته اوھان کي پاڪ ڪري ۽ اوھان تي پنھنجي نعمت پوري ڪري مانَ اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(5,7,'5:7','۽ الله جي نعمت جا اوھان تي آھي سا ياد ڪريو ۽ سندس اُھو اَنجام جيڪو اوھان سان پڪيءَ طرح ڪيائين تنھن کي (به ياد ڪريو) جڏھن اوھان چيو ته ٻڌوسون ۽ مڃيوسون، ۽ الله کان ڊڄو، ڇوته الله سينن وارو (ڀيد) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(5,8,'5:8','اي ايمان وارؤ انصاف سان شاھدي ڏيندڙ ٿي الله لڳ بيھڪ وٺندڙ ھُجو، ۽ ڪنھن قوم جي دشمني اوھان کي اوھان جي بي انصافي جو سبب نه بڻي، انصاف ڪريو، اُھو پرھيزگاري کي ڏاڍو ويجھو آھي، ۽ الله کان ڊڄو، ڇوته جيڪي اوھين ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(5,9,'5:9','جن ايمان آندو ۽ چڱا ڪم ڪيا تن سان الله وعدو ڪيو آھي ته اُنھن لاءِ بخشش ۽ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(5,10,'5:10','۽ جن ڪفر ڪيو ۽ اسان جي آيتن کي ڪُوڙو ڀانيو اُھي دوزخي آھن.');
INSERT INTO "amroti" VALUES(5,11,'5:11','اي ايمان وارؤ الله جي نعمت جا اوھان تي آھي سا ياد ڪريو جڏھن ھڪ قوم پنھنجن ھٿن کي اوھان ڏانھن (ويڙھ لاءِ) ڊگھي ڪرڻ جو ارادو ڪيو تڏھن سندس ھٿ اوھان کان جھليائين، ۽ الله کان ڊڄو، ۽ الله تي ڀلي ته مؤمن ڀروسو ڪن.');
INSERT INTO "amroti" VALUES(5,12,'5:12','۽ الله بني اسرائيلن کان بيشڪ انجام ورتو، ۽ منجھن ٻارھن سردار مقرر ڪياسون، ۽ الله چيو ته تحقيق آءٌ اوھان ساڻ آھيان، جيڪڏھن نماز پڙھيو ۽ زڪٰوة ڏنوَ ۽ مُنھنجن پيغمبرن کي مڃيوَ ۽ کين مدد ڪيو ۽ الله کي چڱو قرض ڏنوَ ته البت اوھان جون مدايون اوھان کان ضرور ميٽيندس ۽ البت اوھان کي اھڙن باغن ۾ ضرور داخل ڪندس جن جي ھيٺان واھيون وھنديون آھن، وري ھن کان پوءِ اوھان مان جيڪو ڪافر ٿيندو سو سڌيءَ واٽ کان بيشڪ ڀُلو.');
INSERT INTO "amroti" VALUES(5,13,'5:13','پوءِ سندن انجام ڀڃڻ سببان مٿن لعنت ڪئي سون ۽ سندين دلين کي سخت ڪيوسون، (اُھي توريت ۾) ڪن لفظن کي پنھنجن جاين کان بدلائين ٿا ۽ جيڪا نصيحت ڪئي وئي تنھن مان حصو وساريائون، ۽ منجھائن ٿورڙن کانسواءِ انھن (سڀني) جي (ھڪ نه ٻي) خيانت تي ھميشہ واقف ٿيندو رھندين تنھنڪري کين معاف ڪر ۽ مُڙي وڃ، بيشڪ الله ڀلارن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(5,14,'5:14','۽ جيڪي چون ٿا ته اسين نصارىٰ آھيون تن کان (به) سندن انجام ورتوسون ۽ جيڪا نصيحت ڪئي ويئي تنھن مان ڀاڱو وساريائون، انھيءَ ڪري سندن وچ ۾ ساڙ ۽ دُشمني قيامت جي ڏينھن تائين وڌي سون، ۽ جيڪي ڪندا آھن تنھن جي الله کين سگھو سُڌ ڏيندو.');
INSERT INTO "amroti" VALUES(5,15,'5:15','اي ڪتاب وارؤ بيشڪ اسان جو پيغمبر (محمد صلى الله عليه وسلم) اوھان وٽ آيو اوھين (الله جي) ڪتاب مان گھڻيون ڳالھيون لڪائيندا ھيؤ تن مان گھڻيون (ڳالھيون) اوھان کي بيان ڪري ٿو ۽ گھڻين (ڳالھين) کان ٽارو ڪري ٿو، بيشڪ اوھان وٽ الله وٽان نُور ۽ پڌرو ڪتاب (قرآن) آيو آھي.');
INSERT INTO "amroti" VALUES(5,16,'5:16','انھيءَ جي سببان الله انھن کي سڌو رستو ڏيکاريندو آھي جيڪي سلامتي جي واٽن ڏانھن سندس راضپو ڳولين ۽ کين اونداھين مان سوجھري ڏانھن پنھنجي حڪم سان ٻاھر ڪڍي ٿو ۽ کين سڌيءَ واٽ ڏانھن رستو ڏيکاري ٿو.');
INSERT INTO "amroti" VALUES(5,17,'5:17','بيشڪ جن چيو ته الله پاڻ مسيح پٽ مريم جو آھي سي بيشڪ ڪافر ٿيا، چؤ ته جيڪڏھن الله عيسىٰ پُٽ مريم جي کي ۽ سندس ماءُ کي ۽ جيڪي سڀيئي زمين ۾ آھن ناس ڪرڻ گھري ته ڪير الله کان انھن کي ڪُجھ بچائي سگھندو؟ ۽ آسمانن ۽ زمين جي ۽ جيڪي اُنھن جي وچ ۾ آھي تنھنجي بادشاھي الله جي آھي، جيڪي گھري ٿو سو پيدا ڪري ٿو، ۽ الله سڀڪنھن شيءِ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(5,18,'5:18','يھودي ۽ نصارىٰ چوندا آھن ته اسين الله جا پُٽ ۽ سندس پيارا آھيون، (اي پيغمبر کين) چؤ ته پوءِ سندوَ گناھن سببان ڇو عذاب ڪندا اٿوَ؟ بلڪ اوھين به انھيءَ جنس مان ماڻھو آھيو جيڪي خلقيائين، جنھن کي وڻيس (تنھنکي) بخشي ۽ جنھن وڻيس (تنھنکي) عذاب ڪري، ۽ آسمانن ۽ زمين جي ۽ جيڪي سندن وچ ۾ آھي تنھنجي بادشاھي الله جي آھي، ۽ ڏانھس موٽڻ آھي.');
INSERT INTO "amroti" VALUES(5,19,'5:19','اي ڪتاب وارؤ بيشڪ اوھان وٽ اسان جو پيغمبر اھڙي وقت ۾ آيو جو (ڪجھ وقت تائين) پيغمبرن جو اچڻ بند ٿي چڪو ھو اوھان تي بيان ٿو ڪري متان چئو ته نڪو خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ اسان وٽ آيو، (ھائو) انھي ڪري بيشڪ اوھان وٽ خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ آيو آھي، ۽ الله سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(5,20,'5:20','۽ (ياد ڪر) جڏھن مُوسىٰ پنھنجي قوم کي چيو ته اي منھنجي قوم پاڻ تي الله جي نعمت ياد ڪريو جو اوھان ۾ پيغمبر مُقرّر ڪيائين ۽ اوھان کي بادشاہ ڪيائين، ۽ اوھان کي اھي (نعمتون) ڏنائين جي جھانن وارن مان ڪنھن کي (به) نه ڏنائين.');
INSERT INTO "amroti" VALUES(5,21,'5:21','اي منھنجي قوم پاڪ زمين ۾ گھڙو جا الله اوھان لاءِ ٺھرائي آھي ۽ پوئتي نه ڦرو نه ته ڦري ڇيئي وارا ٿيندؤ.');
INSERT INTO "amroti" VALUES(5,22,'5:22','چيائون ته اي مُوسىٰ بيشڪ اتي ڏاڍي زور واري قوم آھي، ۽ اسين منجھس ايسين ڪڏھن نه گھڙنداسون جيسين اُھي منجھانس (نه) نڪرن، پوءِ جيڪڏھن اُھي اُتان نڪرن ته اسين گھِڙڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(5,23,'5:23','جيڪي خدا کان ڊڄندا ھئا تن مان ٻه ڄڻا جن تي الله نعمت ڪئي ھئي سي چوڻ لڳا ته در کان مٿن گھڙو، پوءِ جڏھن در کان لنگھيؤ تڏھن اوھين پڪ غالب آھيو، ۽ جيڪڏھن مؤمن آھيو ته الله تي ڀروسو ڪريو.');
INSERT INTO "amroti" VALUES(5,24,'5:24','چيائون ته اي موسىٰ بيشڪ اسين اصلي ڪڏھن منجھس نه گھڙنداسون جيسين اُھي منجھس رھندا تنھنڪري تون ۽ تنھنجو پالڻھار وڃو پوءِ وڙھو بيشڪ اسين ھتي (ئي) ويٺا آھيون.');
INSERT INTO "amroti" VALUES(5,25,'5:25','مُوسىٰ چيو ته اي منھنجا پالڻھار آءٌ پنھنجي سِرَ ۽ پنھنجي ڀاءُ کانسواءِ (ٻئي) ڪنھن تي (به) اختيار نٿو رکان تنھنڪري اسان جي ۽ بي فرمان قوم جي وچ ۾ جدائي ڪر.');
INSERT INTO "amroti" VALUES(5,26,'5:26','(الله) چيو ته اُھا (پاڪ زمين) چاليھ ورھيه مٿن حرام ڪئي ويئي آھي، زمين (يعني بر) ۾ حيران پريشان رھندا، تنھنڪري بي دين قوم تي ڏک نه ڪر.');
INSERT INTO "amroti" VALUES(5,27,'5:27','(اي پيغمبر) آدم جي ٻن پُٽن (ھابيل ۽ قابيل) جي خبر کين سچي طرح پڙھي ٻُڌاءِ، جڏھن ٻنھي (ھڪ ھڪ) قرباني نذر ڪئي تڏھن تڏھن ٻنھي مان ھڪڙي جي قبول ڪئي وئي ۽ ٻئي جي قبول نه ڪئي وئي، (قابيل) چيو ته ضرور ماريندوسانءِ، ۽ (ھابيل) چيو ته الله رڳو پرھيزگارن جي (قرباني) قبول ڪندو آھي.');
INSERT INTO "amroti" VALUES(5,28,'5:28','البت جيڪڏھن پنھنجو ھٿ منھنجي مارڻ لاءِ مون ڏانھن ڊگھو ڪندين ته آءٌ پنھنجو ھٿ تو ڏانھن ڪڏھن به ڊگھو نه ڪندس ته توکي ماريان، بيشڪ آءٌ جھانن جي پالڻھار الله کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(5,29,'5:29','مُنھنجو ارادو آھي ته مُنھنجو گُناھ ۽ پنھنجو گُناھ تون کڻين ته دوزخين مان ٿئين، ۽ ظالمن جي اِھا سزا آھي.');
INSERT INTO "amroti" VALUES(5,30,'5:30','پوءِ سندس نفس کيس ڀاءُ جي مارڻ تي رغبت ڏياري پوءِ کيس ڪُٺائين پوءِ ڇيئي وارن مان ٿيو.');
INSERT INTO "amroti" VALUES(5,31,'5:31','پوءِ الله ھڪ ڪانوَ کي موڪليو جنھن زمين ۾ ھن لاءِ کوٽيو ٿي ته کيس ڏيکاري ته پنھنجي ڀاءُ جي لاش کي ڪئن لڪائي، (ڪانوَ کي ڏسي) چيائين ته مون لاءِ ارمان آھي ڇا آءٌ ھن ڳالھ کان (به) عاجز ٿيس جو ھن ڪانوَ جھڙو ھُجان ھا ته پنھنجي ڀاءُ جو لاشو لڪايان ھا، پوءِ پشيمانن مان ٿيو.');
INSERT INTO "amroti" VALUES(5,32,'5:32','انھيءَ ڪري بني اسرائيلن تي لازم ڪيوسون ته جيڪو ڪنھن جان جي عوض يا زمين ۾ فساد وجھڻ (واري) کانسواءِ ٻئي ڪنھن کي ڪُھندو تنھن ڄڻڪ مڙني ماڻھن کي ڪُٺو، ۽ جنھن اُن کي بچايو تنھن ڄڻڪ مڙني ماڻھن کي بچايو، ۽ البت بيشڪ اسان جا (گھڻا) پيغمبر چٽن مُعجزن سان وٽن آيا، وري بيشڪ اُن کانپوءِ منجھائن گھڻا مُلڪ ۾ حد کان لنگھندڙ ٿيا.');
INSERT INTO "amroti" VALUES(5,33,'5:33','جيڪي الله ۽ سندس پيغمبر سان وڙھندا ۽ ملڪ ۾ فساد (وجھڻ) لاءِ ڪوشش ڪندا تن جي سزا ھن کانسواءِ (ٻي ڪا) نه آھي ته ڪُھجينِ يا سُوريءَ چاڙھجينِ يا سندن ھٿ ۽ سندن پير اُبتڙ سبتڙ وڍجن يا ديس نيڪالي ڏجينِ، دُنيا ۾ اُنھن لاءِ اِھا خواري آھي ۽ آخرت ۾ اُنھن لاءِ وڏو عذاب آھي.');
INSERT INTO "amroti" VALUES(5,34,'5:34','پر جن اوھان جي قابو پوڻ کان اڳ توبه ڪئي، پوءِ ڄاڻو ته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(5,35,'5:35','اي ايمان وارؤ الله کان ڊڄو ۽ ڏانھس وسيلو ڳوليو (يعني نيڪ عملن جي وسيلي سان الله تعالىٰ ڏي ويجھا ٿيو) ۽ سندس واٽ ۾ ڪوشش ڪريو مانَ اوھين ڇٽو.');
INSERT INTO "amroti" VALUES(5,36,'5:36','بيشڪ جن ڪفر ڪيو تن کي جيڪي زمين ۾ آھي سو مڙئي جيڪڏھن ھجي ۽ اُن جيترو ٻيو به گڏ (ھجين) اُھو قيامت جي ڏينھن جي عذاب کان (بچاءَ) لاءِ عوض ڏين ته کانئن قبول نه ڪبو، ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(5,37,'5:37','باہ مان نڪرڻ گُھرندا ۽ (حقيقت ڪري) منجھانئس اُھي نڪرڻ وارا نه آھن، ۽ اُنھن لاءِ سدائين جو عذاب آھي.');
INSERT INTO "amroti" VALUES(5,38,'5:38','چورُ (مڙس) ۽ چورِ (زال) انھن جا ھٿ سندن ڪئي سببان سزا لاءِ وڍيو الله (جي پار) کان اُھو عذاب آھي، ۽ الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(5,39,'5:39','پوءِ جنھن پنھنجي ڪيل پڃاڻا توبه ڪئي ۽ چڱا ڪم ڪيا تنھن تي الله ٻاجھ سان موٽندو، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(5,40,'5:40','(اي پيغمبر) نه معلوم اٿئي ڇا؟ ته آسمانن ۽ زمين جي بادشاھي الله جي آھي، جنھن کي وڻيس تنھن کي عذاب ڪري ۽ جنھن کي وڻيس تنھن کي بخشي، ۽ الله سڀڪنھن شيءِ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(5,41,'5:41','اي پيغمبر انھن جو حال توکي غمگين نه ڪري جيڪي اھڙن (ماڻھن) مان ڪفر ۾ ڪاھي پوندا آھن جو پنھنجن واتن سان چوندا آھن ته ايمان آندوسون ۽ سندن دلين ايمان نه آندو آھي، ۽ (پڻ) يھودين مان، جو ڏاڍا ڪُوڙ ٻڌندڙ آھن ٻيءَ قوم (ڪافرن) لاءِ جي اڃا تو وٽ نه آيا آھن جاسُوسي ڪندڙ آھن، ڪن (صحيح) ڳالھين کي پنھنجن ھنڌن تي قائم ھجڻ کانپوءِ بدلائيندا آھن، (ماڻھن کي) چوندا آھن ته جيڪڏھن ھيءُ (حُڪم) ڏنو وڃيوَ ته اھو قبول ڪجو ۽ جيڪڏھن اُھو نه ڏنو وڃيوَ ته پري ٿجو، ۽ الله جنھن جي گمراہ ڪرڻ جو ارادو ڪندو آھي تنھن لاءِ الله (جي پار) کان (ھدايت ڪرڻ جو) ڪُجھ اختيار نه اٿيئي، اُھي (اھڙا) آھن جو انھن جي دلين کي الله پاڪ ڪرڻ نه گھرندو آھي،، دنيا ۾ انھن لاءِ خواري آھي ۽ آخرت ۾ انھن لاءِ وڏو عذاب آھي.');
INSERT INTO "amroti" VALUES(5,42,'5:42','ڪوڙين ڳالھين جا ٻڌندڙ آھن حرام (جو مال) کائيندڙ آھن، پوءِ جيڪڏھن تو وٽ اچن ته سندن (تڪرار جي) وچ ۾ فيصلو ڪر يا کانئن مُنھن موڙ، ۽ جيڪڏھن کانئن مُنھن موڙين ته توکي ڪجھ نقصان رسائي نه سگھندا، ۽ جيڪڏھن نبيرو ڪرين ته سندن وچ ۾ انصاف سان فيصلو ڪر، بيشڪ الله انصاف ڪندڙن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(5,43,'5:43','۽ ڪئن توکان ھن حالت ۾ فيصلو ڪرائيندا؟ جو وٽن توريت آھي اُن ۾ الله جو حُڪم آھي وري اُن کانپوءِ ڦري ويندا آھن، ۽ اُھي مُؤمن نه آھن.');
INSERT INTO "amroti" VALUES(5,44,'5:44','تحقيق اسان توريت لاٿو آھي منجھس ھدايت ۽ نُور آھي، فرمانبردارن پيغمبرن ۽ الله وارن ۽ عالمن اُن سان يھودين لاءِ حُڪم ڪيو آھي (يعني) انھيءَ سان جيڪي الله جي ڪتاب مان ياد ڪرايو وين ۽ مٿن شاھد ھوا، پوءِ (کين اسان سمجھايو ھوته) ماڻھن کان نه ڊڄو ۽ مون کان ڊڄو ۽ مُنھنجي آيتن کي ٿوري مُلھ سان نه وڪڻو، ۽ جيڪي الله لاٿو آھي تنھن سان جيڪي حُڪم نه ڪندا سي ئي ڪافر آھن.');
INSERT INTO "amroti" VALUES(5,45,'5:45','۽ انھن (يھودين) تي توريت ۾ لازم ڪيوسون ته ماڻھوءَ جو عوض ماڻھو ۽ اک جو عوض اک ۽ نڪ جو عوض نڪ ۽ ڪنّ جو عوض ڪنّ ۽ ڏند جو عوض ڏند ۽ ڦٽ جو عوض اوترو ڦٽ آھي، پوءِ جيڪو ان کي بخشيندو تنھن لاءِ اُھو (بخشڻ سندس گناھن جو) ڪفّارو آھي، ۽ جيڪو الله لاٿو آھي تنھن سان جيڪي حُڪم نه ڪندا سي ئي ظالم آھن.');
INSERT INTO "amroti" VALUES(5,46,'5:46','۽ اُنھن (پيغمبرن) جي طريقي تي عيسىٰ پٽ مريم جي کي پوئتان موڪليوسون جو جيڪو کانئس اڳ (توريت) ھو، تنھنجو سچو ڪندڙ ھو، ۽ کيس انجيل ڏنوسون جنھن ۾ ھدايت ۽ نُور آھي، ۽ توريت جيڪو کانئس اڳ ھو تنھنجو سچو ڪندڙ ۽ پرھيزگارن لاءِ ھدايت ۽ نصيحت ھو.');
INSERT INTO "amroti" VALUES(5,47,'5:47','۽ انجيل وارن کي جڳائي ته جيڪي الله منجھس لاٿو تنھن سان نبيرو ڪن، ۽ جيڪي الله لاٿو تنھن موجب جيڪي نبيرو نه ڪندا سي ئي بي دين آھن.');
INSERT INTO "amroti" VALUES(5,48,'5:48','۽ (اي پيغمبر) توڏانھن سچائيءَ سان ڪتاب لاٿوسون جيڪي ڪتاب کانئس اڳ آھن تن جو سچو ڪندڙ ۽ مٿن نگھبان آھي تنھنڪري الله جيڪي لاٿو آھي تنھن موجب سندن وچ ۾ حُڪم ڪر ۽ جيڪي حق تو وٽ آيو آھي تنھن کان مُڙي سندين سَڌن جي تابعداري نه ڪر، اوھان مان ھر ڪنھن ٽوليءَ لاءِ شريعت ۽ (ھڪ سنئين) واٽ مُقرّر ڪئي سون، ۽ جيڪڏھن الله گھري ھا ته اوھان مڙني کي ھڪ ٽولي ڪري ھا پر (گھريائين) ته جيڪي اوھان کي ڏنائين تنھن ۾ اوھان کي پرکي تنھنڪري چڱائيءَ ڏانھن اڳرائي ڪريو، اوھان مڙني جو الله ڏانھن موٽڻ آھي پوءِ جنھن (ڳالھ) بابت اوھين تڪرار ڪندڙ ھيؤ تنھنجي فيصلي جي اوھان کي خبر ڏيندو.');
INSERT INTO "amroti" VALUES(5,49,'5:49','۽ (وري به توکي حُڪم ٿو ڪجي) ته جيڪي الله لاٿو آھي تنھن سان سندن وچ ۾ حُڪم ڪر ۽ سندين سڌن جي تابعداري نه ڪر ۽ اُنھن کان خوف رک جيڪي الله توڏانھن لاٿو آھي تنھن جي ڪنھن (حُڪم) کان متان توکي ٿيڙين، پوءِ جيڪڏھن ڦِرن ته ڄاڻ ته الله سندن ڪن گناھن سببان کين عذاب پُھچائڻ گھُرندو آھي، ۽ ماڻھن مان گھڻا بي دين آھن.');
INSERT INTO "amroti" VALUES(5,50,'5:50','اڃا جاھليت وارو حُڪم گھُرندا آھن ڇا؟ ۽ (ڪتاب الٰھي تي) يقين ڪندڙ قوم لاءِ حُڪم ڪرڻ ۾ الله کان وڌيڪ چڱو ڪير آھي؟');
INSERT INTO "amroti" VALUES(5,51,'5:51','اي مؤمنؤ يھودين ۽ نصارن کي دوست ڪري نه وٺو، انھن مان ھڪڙا ٻـين جا دوست آھن، ۽ اوھان منجھان جيڪو کين دوست ڪري وٺندو سو بيشڪ منجھانئن آھي، بيشڪ الله ظالم ٽوليءَ کي سِڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(5,52,'5:52','پوءِ جن جي دلين ۾ (منافقيءَ جي) بيماري آھي تن کي ڏسين ٿو اُنھن (يھودين ۽ نصارن جي دوستيءَ) ۾ ڊوڙندا ويندا آھن چوندا آھن ته پاڻ تي زماني جي ڦيري پُھچڻ کان ڊڄون ٿا، پوءِ ستت ئي الله فتح يا ٻيو حُڪم پاڻ وٽان موڪليندو پوءِ پنھنجي دلين ۾ جيڪي لڪائيندا آھن تنھن تي پشيمان ٿيندا.');
INSERT INTO "amroti" VALUES(5,53,'5:53','۽ (قيامت ۾) مؤمن چوندا ته ھي اُھي آھن ڇا جي (دنيا ۾) الله جا وڏا وڏا قسم کڻندا ھئا ته اسين اوھان سان آھيون، (اڄ منافقيءَ سببان) سندن (سڀ) ڪئي ڪمائي ناس ٿي پوءِ اُھي ڇيئي وارا ٿيا.');
INSERT INTO "amroti" VALUES(5,54,'5:54','اي مؤمنؤ اوھان منجھان جيڪو دين کان ڦرندو ته سگھو ئي الله اھڙي قوم کي پيدا ڪندو جن کي (پاڻ) دوست رکندو ۽ اُھي کيس دوست رکندا اُھي مؤمنن سان نوڙت ڪندڙ (۽) ڪافرن تي سخت ھوندا، الله جي واٽ ۾ جھاد ڪندا ۽ ڪنھن ملامت ڪندڙ جي ملامت کان نه ڊڄندا، اھو الله جو فضل آھي جنھن کي وڻيس ڏيندو آھي، ۽ الله ڪشادي فضل وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(5,55,'5:55','(اي مؤمنؤ) الله ۽ سندس پيغمبر ۽ اُنھن مؤمنن کان سواءِ اوھانجو ڪو دوست نه آھي جيڪي نماز پڙھندا آھن ۽ زڪوٰة ڏيندا آھن ۽ اُھي رڪوع ڪندڙ آھن.');
INSERT INTO "amroti" VALUES(5,56,'5:56','۽ جيڪو الله ۽ سندس پيغمبر ۽ مؤمنن کي دوست ڪري وٺندو (سو الله جي ٽولي مان ٿيندو) ڇوته الله جي ٽولي ئي غالب آھي.');
INSERT INTO "amroti" VALUES(5,57,'5:57','اي ايمان وارؤ جن کي اوھان کان اڳ ڪتاب ڏنو ويو تن منجھان جن اوھان جي دين کي چٿر ۽ راند بڻايو آھي تن کي ۽ ڪافرن کي دوست نه بڻايو، ۽ جيڪڏھن مؤمن آھيو ته الله کان ڊڄو.');
INSERT INTO "amroti" VALUES(5,58,'5:58','۽ جڏھن (اوھين) نماز لاءِ بانگ ڏيندا آھيو (تڏھن اُھي) اُن کي چٿر ۽ راند ڀائيندا آھن، اِھو ھِن سببان آھي جو اُھي بي سمجھه قوم آھن.');
INSERT INTO "amroti" VALUES(5,59,'5:59','(اي پيغمبر) چؤ ته اي ڪتاب وارؤ ھن کانسواءِ اسان تي (ٻيو) ڪو عيب رکي سگھو ٿا ڇا؟ ته الله کي ۽ جيڪي اسان ڏانھن لاٿو ويو ۽ جيڪي اڳ لاٿو ويو آھي تنھن کي مڃيو اٿئون ۽ بيشڪ اوھان منجھان گھڻا بي دين آھن.');
INSERT INTO "amroti" VALUES(5,60,'5:60','(اي پيغمبر کين) چؤ ته الله وٽ ھن کان وڌيڪ بُڇڙي عاقبت واري جي اوھان کي خبر ڏيان؟ (بڇڙي عاقبت وارا) اُھي آھن جن تي الله لعنت ڪئي آھي ۽ مٿن ڏمر ڪيو آھي ۽ منجھانئن ڪي ڀولڙا ۽ سوئر ڪيائين جنھن شيطان جي پوڄا ڪئي، اِھي بڇڙي ھنڌ رھڻ وارا آھن ۽ سڌي واٽ کان بلڪل ڀُلا آھن.');
INSERT INTO "amroti" VALUES(5,61,'5:61','۽ جڏھن اوھان وٽ ايندا آھن تڏھن چوندا آھن ته ايمان آندوسون ھن ھوندي جو (اُھي) ڪُفر سان ايندا آھن ۽ اُھي اُن (ڪفر) سان ويندا آھن، ۽ جيڪي لڪائيندا آھن تن کي الله چڱو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(5,62,'5:62','۽ (اي پيغمبر) منجھانئن گھڻن کي ڏسين ٿو ته گناہ ۽ حد کان لنگھڻ ۽ پنھنجي حرامخوريءَ ۾ تڪڙ ڪندا آھن، ۽ جيڪي ڪندا آھن سو ضرور بڇڙو آھي.');
INSERT INTO "amroti" VALUES(5,63,'5:63','الله وارا ۽ عالم کين سندن ڪُوڙ ڳالھائڻ ۽ سندن حرامخوريءَ کان ڇونه جھليندا آھن؟ جيڪي ڪندا آھن سو ضرور بڇڙو آھي.');
INSERT INTO "amroti" VALUES(5,64,'5:64','۽ يھودي چوندا آھن ته الله جو ھٿ تنگ آھي (يعني بخيل آھي)، سندن ھٿ بنديل ھجن جيڪي چيائون تنھن سببان لعنت ڪئي وين، (ايئن نه آھي) بلڪ سندس ٻئي ھٿ ڪشادا آھن جئن وڻندو اٿس تئن خرچيندو آھي، ۽ تنھنجي پالڻھار وٽان توڏانھن جيڪو (قرآن) لاٿو ويو آھي سو منجھانئن گھڻن (يھودين) کي بي فرمانيءَ ۽ ڪفر ۾ ضرور وڌائيندو، ۽ قيامت جي ڏينھن تائين سندن وچ ۾ دشمني ۽ بغض وجھي ڇڏيوسون، جنھن مھل جنگ لاءِ باھ ٻاريندا (تنھن مھل) الله اُھا وسائيندو آھي ۽ فساد لاءِ مُلڪ ۾ ڪوشش ڪندا آھن، ۽ الله فسادين کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(5,65,'5:65','۽ جيڪڏھن ڪتاب وارا ايمان آڻين ھا ۽ پرھيزگاري ڪن ھا ته سندن گناہ کانئن ضرور ميٽيون ھا ۽ کين نعمت جي باغن ۾ ضرور داخل ڪريون ھا.');
INSERT INTO "amroti" VALUES(5,66,'5:66','۽ جيڪڏھن اُھي توريت ۽ انجيل ۽ جيڪي سندن پالڻھار وٽان اُنھن ڏانھن لاٿو ويو (تن جي حڪمن کي) پورو بيھارين ھا ته پنھنجي مٿان ۽ پنھنجي پيرن ھيٺان ضرور (روزي) کائين ھا، منجھانئن ھڪ ٽولي سئين رستي آھي، ۽ اُنھن مان گھڻا جيڪي ڪندا آھن سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(5,67,'5:67','اي پيغمبر جيڪو (قرآن) توڏانھن تنھنجي پالڻھار وٽان لاٿو ويو آھي سو پُھچاءِ، ۽ جيڪڏھن (ايئن) نه ڪيئي ته سندس پيغام نه پھچايئي، ۽ الله توکي ماڻھن کان پناہ ڏيندو، ۽ الله ڪافرن جي قوم کي سِڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(5,68,'5:68','(اي پيغمبر کين) چؤ ته اي ڪتاب وارؤ اوھين ايستائين ڪنھن واٽ تي نه آھيو جيستائين توريت ۽ انجيل ۽ جيڪي پالڻھار کان اوھان ڏانھن لاٿو ويو تنھن (جي حڪمن) تي بيھڪ نه ڪريو، ۽ جيڪي تنھنجي پالڻھار کان توڏانھن لاٿو ويو آھي سو منجھانئن گھڻن (يھودين) جي بي فرمَانيءَ ۽ ڪُفر کي وڌائيندو، تنھنڪري ڪافرن جي قوم تي ارمان نه ڪر.');
INSERT INTO "amroti" VALUES(5,69,'5:69','(جيڪي) مؤمن ۽ يھودي ۽ صابي ۽ نصارىٰ (آھن اُنھن مان) جن الله کي ۽ قيامت جي ڏينھن کي مڃيو ۽ چڱا ڪم ڪيا تن کي ڪو ڀؤ ڪونه آھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(5,70,'5:70','بيشڪ بني اسرائيلن کان انجام ورتوسون ۽ اُنھن ڏانھن (گھڻا) پيغمبر موڪلياسون، جڏھن ڪڏھن وٽن ڪنھن پيغمبر جيڪي سندن دلين نه ٿي گھريو سو حُڪم ٿي آندو تڏھن ھڪ ٽوليءَ کي ڪُوڙو ٿي ڀانيائون ۽ ھڪ ٽوليءَ کي ڪُٺائون ٿي.');
INSERT INTO "amroti" VALUES(5,71,'5:71','۽ ڀانيائون ٿي ته ڪا خرابي ٿيڻي نه آھي پوءِ (حق کان) انڌا ٿيا ۽ ٻوڙا ٿيا وري (به) الله مٿن ٻاجھ سان موٽيو (جو پيغمبر موڪليائين) وري منجھانئن گھڻا (حق کان) انڌا ٿيا ۽ ٻوڙا ٿيا، ۽ جيڪي ڪندا آھن سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(5,72,'5:72','بيشڪ جن چيو ته عيسىٰ پٽ مريم جو پاڻ الله آھي سي ڪافر ٿيا، ۽ عيسىٰ چوندو ھو ته اي بني اسرائيلؤ الله جي عبادت ڪريو جو منھنجو پالڻھار ۽ اوھان جو پالڻھار آھي، (ھيءَ پڪي ڳالھ آھي ته) جيڪو الله سان شريڪ مقرر ڪندو تنھن تي بيشڪ الله بھشت حرام ڪيو آھي ۽ سندس ھنڌ باھ آھي، ۽ ظالمن جو ھو مددگار آھي.');
INSERT INTO "amroti" VALUES(5,73,'5:73','جن چيو ته الله ٽن مان ھڪڙو آھي سي بيشڪ ڪافرٿيا، حالانڪ ھڪ الله کانسواءِ ڪوئي عبادت جو لائق نه آھي، جيڪي چوندا آھن تنھن کان جيڪڏھن پاڻ کي نه جھليندا ته منجھانئن ڪافرن کي ڏکوئيندڙ عذاب ضرور پھچندو.');
INSERT INTO "amroti" VALUES(5,74,'5:74','پوءِ الله ڏانھن ڇونه موٽندا آھن ۽ سندس بخشش ڇونه گھرندا آھن؟ ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(5,75,'5:75','عيسىٰ پٽ مريم جو رڳو پيغمبر آھي، بيشڪ کانئس اڳ (گھڻا) پيغنبر گذري ويا، ۽ سندس ماءُ سچي (فرمانبردار) ھئي، اُھي کاڄ کائيندا ھئا، (اي پيغمبر) ڏس ته ڪئن انھن لاءِ نشانيون بيان ڪريون ٿا موٽي ڏس ته اُھي ڪاڏي اُلٽا ڪيا ويندا آھن.');
INSERT INTO "amroti" VALUES(5,76,'5:76','(اي پيغمبر کين) چؤ ته الله کانسواءِ اُنھن جي عبادت ڇو ڪندا آھيو؟ جي اوھان لاءِ نڪي ڏک ۽ نڪي سُک جا مالڪ آھن، ۽ الله ئي ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(5,77,'5:77','(اي پيغمبر کين) چؤ ته اي ڪتاب وارؤ حق کانسواءِ پنھنجي دين ۾ حد کان نه لنگھو ۽ انھيءَ قوم جي سڌن جي پٺيان نه لڳو جيڪي بيشڪ (پاڻ به) اڳ ڀُلا آھن ۽ (ٻـين) گھڻن کي (به) ڀُلايو اٿن ۽ سڌيءَ واٽ کان ڀُلا آھن.');
INSERT INTO "amroti" VALUES(5,78,'5:78','بني اسرائيلن مان جيڪي ڪافر ٿيا تن تي داؤد ۽ عيسىٰ پٽ مريم جي زبان سان لعنت ڪئي ويئي آھي، اُھو ھن سببان آھي جو بي فرماني ڪيائون ۽ حد کان لنگھندا ھئا.');
INSERT INTO "amroti" VALUES(5,79,'5:79','جيڪي بد ڪم ڪندا ھئا تن کان ھڪ ٻئي کي نه جھليندا ھوا، جيڪي ڪندا ھئا سو ضرور بد آھي.');
INSERT INTO "amroti" VALUES(5,80,'5:80','(اي پيغمبر) منجھانئن گھڻن کي ڏسين ٿو ته ڪافرن سان دوستي رکندا آھن، جيڪي اُنھن پاڻ لاءِ اڳ موڪليو آھي سو بڇڙو آھي جو مٿن الله جو ڏمرجڻ آھي ۽ اُن عذاب ۾ سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(5,81,'5:81','۽ جيڪڏھن (اُھي) الله ۽ (پنھنجي) پيغمبر کي ۽ جيڪي پيغمبر ڏانھن لاٿو ويو تنھن کي مڃين ھا ته انھن (مشرڪن) کي دوست ڪري نه وٺن ھا پر منجھانئن گھڻا ايمان کان نڪتل آھن.');
INSERT INTO "amroti" VALUES(5,82,'5:82','(اي پيغمبر) يھودين ۽ مشرڪن کي مؤمنن لاءِ ٻـين ماڻھن کان بلڪل وڌيڪ دشمني ڪرڻ وارو ضرور ڏسندين، ۽ جيڪي چون ٿا ته اسين نصارىٰ آھيون تن کي مسلمانن سان دوستي ڪرڻ ۾ ڏاڍو ويجھو ڏسندين، اِھو ھن سببان آھي جو منجھانئن ڪي عالم ۽ ڪي فقير آھن ۽ بيشڪ اُھي وڏائي نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(5,83,'5:83','۽ جيڪي پيغمبر ڏانھن لاٿو ويو سو جڏھن ٻڌندا آھن تڏھن سندين اکين کي انھيءَ سببان جو سچ کي سڃاتائون ڳوڙھا ڳاڙيندو ڏسندين، چوندا آھن ته اي اسان جا پالڻھار ايمان آندوسين تنھنڪري اسان کي شاھدن ۾ لِک.');
INSERT INTO "amroti" VALUES(5,84,'5:84','۽ اسان کي ڇا (ٿيو) آھي؟ جو الله کي ۽ جيڪي اسان وٽ سچ (يعني قرآن) آيو تنھن کي ھن ھوندي به نه مڃيون جو اُميد ٿا رکون ته اسان جو پالڻھار اسان کي صالحن جي ٽوليءَ سان (گڏي بھشت ۾) گھيريندو.');
INSERT INTO "amroti" VALUES(5,85,'5:85','پوءِ جيڪي چيائون تنھن سببان الله کين بھشت ڏنو جنھن جي ھيٺان واھيون وھنديون آھن منجھس سدائين رھڻ وارا آھن، ۽ اِھو ڀلارن جو بدلو آھي.');
INSERT INTO "amroti" VALUES(5,86,'5:86','۽ جن ڪفر ڪيو ۽ اسان جي آيتن کي ڪوڙو ڀانيو اُھي دوزخي آھن.');
INSERT INTO "amroti" VALUES(5,87,'5:87','اي ايمان وارؤ جيڪي پاڪ شيون الله اوھان لاءِ حلال ڪيون آھن سي حرام نه بڻايو ۽ نڪي حد کان لنگھو، بيشڪ الله حد کان لنگھندڙن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(5,88,'5:88','۽ جيڪي الله اوھان کي پاڪ حلال رزق ڏنو آھي تنھن مان کائو، ۽ اُن الله کان ڊڄو جنھن کي مڃيندڙ آھيو.');
INSERT INTO "amroti" VALUES(5,89,'5:89','الله اوھان جي اجاين قسمن کڻڻ ۾ اوھان کي نه پڪڙيندو پر جيڪي قسم اوھان پڪي نيت سان کنيا تنھن سببان اوھانکي وَٺ ڪندو، پوءِ ان جي ڪفارت وچٿرو (کاڌو) جيڪي پنھنجي عيال کي کارائيندا آھيو تنھن مان ڏھن مسڪينن کي کاڌو ڏيڻ آھي يا کين ويس ڍڪائڻ يا ٻانھون آجو ڪرڻ (جڳائي)، پوءِ جيڪو نه لھي ته ٽي ڏينھن روزو رکڻ لازم آھي، جڏھن اوھين قسم کڻو (۽ ڀڃو تڏھن) اِھا ڪفارت اوھان جي قسمن جي آھي، ۽ پنھنجي قسمن کي (ڀڃڻ کان) سوگھو رکو، اھڙي طرح الله اوھان لاءِ پنھنجا حڪم بيان ڪندو آھي مانَ اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(5,90,'5:90','اي ايمان وارؤ شراب ۽ جؤا ۽ پوڄا آستانا ۽ ڍارا پليتُ شيطاني ڪمن مان آھن تنھنڪري اوھين کانئن پرھيز ڪريو ته مانَ ڇٽو.');
INSERT INTO "amroti" VALUES(5,91,'5:91','شيطان جو ارادو شراب (پيارڻ) ۽ جؤا (کيڏائڻ) ۾ ھن کانسواءِ نه آھي ته اوھان جي وچ ۾ دشمني ۽ بغض وجھي ۽ الله جي ياد ڪرڻ کان ۽ نماز کان اوھان کي جھلي، پوءِ (ھاڻي به) اوھين (نه) جھلبؤ ڇا؟');
INSERT INTO "amroti" VALUES(5,92,'5:92','۽ الله جي فرمانبرداري ڪريو ۽ پيغمبر جي فرمانبرداري ڪريو ۽ ڊڄو، پوءِ جيڪڏھن اوھين ڦرندؤ ته (پڪ) ڄاڻو ته اسان جي پيغمبر تي رڳو پڌرو پيغام پھچائڻ آھي.');
INSERT INTO "amroti" VALUES(5,93,'5:93','جن ايمان آندو ۽ چڱا ڪم ڪيا تن تي جيڪي (اڳ) کاڌائون تنھن ۾ گناہ نه آھي جڏھن پرھيزگاري ڪيائون ۽ ايمان آندائون ۽ چڱا ڪم ڪيائون وري پرھيزگاري ڪيائون ۽ ايمان آندائون وري پرھيزگاري ڪيائون ۽ چڱائي ڪيائون، ۽ الله ڀلارن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(5,94,'5:94','اي ايمان وارؤ الله ڪنھن شڪار (جي حڪم) سان جنھن کي اوھان جا ھٿ يا اوھان جا تير پھچن اوھان کي ھن لاءِ ضرور پرکيندو ته الله نکيڙي ته ڪير کانئس پرپُٺ ڊڄندو آھي، پوءِ جيڪو ھن کانپوءِ حد کان لنگھندو تنھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(5,95,'5:95','اي ايمان وارؤ اوھين احرام (جي حالت) ۾ شڪار نه ڪريو، ۽ اوھان مان جنھن ڄاڻي واڻي ان کي ماريو پوءِ جيڪي ماريو اٿس تنھن جھڙو جانور جنھن لاءِ اوھان مان ٻه معتبر حُڪم ڪن سزا لازم آھي اھا قرباني ڪعبي وٽ پھچڻي آھي يا (سزا) مسڪينن جو کاڌو ڪفارت آھي يا ان جي برابر روزا رکي ھن لاءِ ته پنھنجي ڪم جي سزا چکي، جيڪي اڳي گذريو سو اَلله معاف ڪيو، ۽ جنھن موٽي ڪيو تنھن کان الله بدلو وٺندو، ۽ الله غالب بدلي وٺڻ وارو آھي.');
INSERT INTO "amroti" VALUES(5,96,'5:96','درياءَ جو شڪار اوھان لاءِ حلال ڪيو ويو ۽ ان جو کائڻ اوھان لاءِ ۽ مسافرن لاءِ نفعي وارو آھي، ۽ جھنگ جو شڪار جيستائين اوھين احرام وارا ھجو تيستائين اوھان تي حرام ڪيو ويو، ۽ اُن الله کان ڊڄو جنھن ڏانھن گڏ ڪيا ويندؤ.');
INSERT INTO "amroti" VALUES(5,97,'5:97','الله تعظيم واري گھر ڪعبه تعظيم واري مھيني ۽ (ڪعبي ڏانھن موڪليل) قرباني ۽ قلائد کي ماڻھن لاءِ بيھڪ جو سبب ڪيو، اِھو (ھن لاءِ) ته اوھين ڄاڻو ته جيڪي آسمانن ۽ جيڪي زمين ۾ آھي سو بيشڪ الله ڄاڻندو آھي ۽ الله سڀڪنھن شيء کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(5,98,'5:98','ڄاڻو ته الله سخت سزا ڏيندڙ آھي ۽ (ھيءبه ته) الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(5,99,'5:99','پيغمبر تي رڳو پيغام پھچائڻ آھي، ۽ جيڪي پڌرو ڪندا آھيو ۽ جيڪي لڪائيندا آھيو (سو سڀ) الله ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(5,100,'5:100','(اي پيغمبر) چؤ ته پليت ۽ پاڪ برابر نه آھن جيتوڻيڪ پليت جي گھڻائي توکي زيادہ وڻي، پوءِ اي ڌيان وارؤ الله کان ڊڄو ته مانَ اوھين ڇٽو.');
INSERT INTO "amroti" VALUES(5,101,'5:101','اي ايمان وارؤ انھن شين بابت سوال نه ڪريو جن جي حقيقت جيڪڏھن اوھان تي پڌري ڪئي وڃي ته اوھان کي ڏکي لڳي، ۽ جيڪڏھن قرآن لھڻ مھل ان بابت سوال ڪريو ته اوھان لاءِ پڌرو ڪيو ويندو، انھن (سوالن) کان الله اوھان کي معافي ڏني، ۽ الله بخشڻھار بردبار آھي.');
INSERT INTO "amroti" VALUES(5,102,'5:102','بيشڪ ھڪ قوم اوھان کان اڳ اھڙين (ڳالھين) جو سوال ڪيو ھو (پر) سمجھائڻ تي وري اُن جا ڦري مُنڪر ٿيا.');
INSERT INTO "amroti" VALUES(5,103,'5:103','الله نڪي بحيرہ ۽ نڪي سائبہ ۽ نڪي وصيلہ ۽ نڪي حام مُقرّر ڪيو آھي پر ڪافر الله تي ڪوڙ ٻڌندا آھن، ۽ منجھانئن گھڻا نٿا سمجھن.');
INSERT INTO "amroti" VALUES(5,104,'5:104','۽ جڏھن کين چئبو آھي ته جيڪي الله لاٿو آھي تنھن ڏانھن ۽ پيغمبر ڏانھن اچو (تڏھن) چوندا آھن ته جنھن تي پنھنجن ابن ڏاڏن کي (ھلندو) ڏٺوسون سو اسان کي بس آھي، جيتوڻيڪ سندن ابا ڏاڏا ڪجھ نه ڄاڻندا ھئا ۽ نڪي ھدايت وارا ھئا.');
INSERT INTO "amroti" VALUES(5,105,'5:105','اي مؤمنؤ اوھين پنھنجن نفسن جي ڪريو، جڏھن اوھين ھدايت وارا ٿيؤ تڏھن اُھو اوھان کي ضرر نه رسائيندو جيڪو ڀُلو، الله ڏانھن اوھان مڙني جو موٽڻ آھي پوءِ جيڪي اوھين ڪندا آھيو تنھنجي اوھان کي سُڌ ڏيندو.');
INSERT INTO "amroti" VALUES(5,106,'5:106','اي ايمان وارؤ جڏھن اوھان مان ڪنھن کي موت ويجھو ٿئي ته وصيّت ڪرڻ مھل ٻه معتبر وصّي ڪريو جي اوھان (مسلمانن) مان ھجن يا اوھان کانسواءِ ٻيا (يعني غيرقوم جا) ھجن (ھي تڏھن) جيڪڏھن اوھين مُلڪ ۾ مسافر ھجو پوءِ اوھان کي موت جو واقعو پُھچي، جيڪڏھن (اوھين سندن گواھي ڏيڻ ۾ ڪو) شڪ رکو ٿا ته (وچينءَ) نماز کانپوءِ اوھين ٻنھي کي جھليو پوءِ اُھي (ٻئي) الله جو قسم کڻي چون ته توڻيڪ مائٽ ھوندا ته به اسين قسم جي عوض ڪا بھا نه وٺنداسون ۽ نڪي الله جي (حڪم واري) شاھدي لڪائينداسون (جي لڪائي سون) ته اُتي جو اُتي ضرور گنھگارن مان ٿينداسون.');
INSERT INTO "amroti" VALUES(5,107,'5:107','پوءِ جيڪڏھن معلوم ٿئي ته اُھي ٻئي گنھگار ٿيا ته جن جي مال تي ٻئي حق رکيو آھي تن جي ويجھي مائٽي وارن مان ٻيا ٻه ڄڻا انھن جي بجاءِ کڙا ٿين پوءِ الله جو قسم کڻي چون ته ٻنھي جي شاھديءَ کان اسان جي شاھدي ڏاڍي سچي آھي ۽ حد کان نه لنگھنداسون، نه ته اتي جو اتي ضرور ظالمن مان ٿينداسون.');
INSERT INTO "amroti" VALUES(5,108,'5:108','اِھو (حُڪم ھن ڳالھ کي) ويجھو آھي ته اُھي (وصّي ماڻھو) سچي ڳالھه جي شاھدي ڏيڻ يا پنھنجن قسمن کڻڻ کانپوءِ قسم جي رد ٿيڻ کان ڊڄن، ۽ الله کان ڊڄو ۽ ھي (حُڪم دل سان) ٻڌو، ۽ الله بي دين قوم کي سڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(5,109,'5:109','(ياد ڪر) جنھن ڏينھن الله سڀني پيغمبرن کي گڏ ڪندو (تنھن ڏينھن) چوندو ته اوھان کي ڇا جواب ڏنو ويو، چوندا ته اسان کي (ماڻھن جي دلين جي) خبر نه آھي، ڇوته تون ئي ڳجھن جو وڏو ڄاڻندڙ آھين.');
INSERT INTO "amroti" VALUES(5,110,'5:110','جڏھن (اُن ڏينھن) الله عيسىٰ پٽ مريم جي کي چوندو ته پاڻ تي ۽ پنھنجي ماءُ تي مُنھنجي نعمت ياد ڪر، جڏھن توکي پاڪ رُوح سان مدد ڏنم، (جو) ماڻھن سان پينگھي ۾ (خاص طرح) ۽ وڏي وھيءَ ۾ ڳالھايُئي ٿي، ۽ جڏھن توکي ڪتاب ۽ دانائي ۽ توريت ۽ انجيل سيکاريم، ۽ جڏھن منھنجي حڪم سان مٽيءَ مان پکيءَ جي شڪل جھڙو بڻايئي پوءِ منجھس ڦوڪيئي ته مُنھنجي حڪم سان پکي ٿيو ۽ مُنھنجي حُڪم سان ڄاول انڌي ۽ ڪوڙھي کي ڇٽايئي ٿي، ۽ جڏھن مئل کي منھنجي حُڪم سان (جيئرو ڪري) ڪڍيئي ٿي، ۽ جڏھن بني اسرائيلن (جي شر) کي توکان جھليم جڏھن وٽم معجزن سان آئين تڏھن منجھانئن ڪافرن چيو ته ھيءُ رڳو پڌرو جادو آھي.');
INSERT INTO "amroti" VALUES(5,111,'5:111','۽ جڏھن حوارين ڏانھن الھام ڪيم ته مون تي ۽ مُنھنجي پيغمبر تي ايمان آڻيو، چيائون ته ايمان آندوسون ۽ (اي عيسى) شاھد ھج ته اسان مسلمان آھيون.');
INSERT INTO "amroti" VALUES(5,112,'5:112','(ياد ڪر) جڏھن حوارين چيو ته اي عيسىٰ پُٽ مريم جا تنھنجو پالڻھار اسان تي آسمان کان ڪو (طعام جو) خونچو لاھي سگھندو ڇا؟ (عيسى) چيو ته جيڪڏھن مؤمن آھيو ته الله کان ڊڄو.');
INSERT INTO "amroti" VALUES(5,113,'5:113','چيائون (اسين ھيءُ) گھرون ٿا ته منجھانئس کائون ۽ اسان جون دليون آرام وٺن ۽ ڄاڻون ته اسان کي سچ چيو اٿيئي ۽ مٿس شاھد ھجون.');
INSERT INTO "amroti" VALUES(5,114,'5:114','(تڏھن) عيسىٰ پٽ مريم جي چيو ته اي اسان جا پالڻھار الله اسان تي ھڪ خونچو آسمان کان لاھ ته اسان جي اڳين ۽ اسان جي پوين (سڀني) لاءِ عيد ٿئي ۽ تنھنجي نشاني ٿئي، ۽ اسان کي روزي ڏي ۽ تون چڱو روزي ڏيندڙ آھين.');
INSERT INTO "amroti" VALUES(5,115,'5:115','الله چيو ته آءٌ اوھان تي (خونچو) لاھيندس، پوءِ اوھان مان جيڪو ھن کان پوءِ بي فرماني ڪندو تنھن کي آءٌ اھڙو سخت عذاب ڪندس جو جھانن (جي رھندڙن) مان ڪنھن ھڪ کي عذاب نه ڪندس.');
INSERT INTO "amroti" VALUES(5,116,'5:116','۽ (اُھو وقت ياد ڪر) جڏھن الله چوندو ته اي عيسىٰ پٽ مريم جا تو ماڻھن کي چيو ھو ڇا؟ ته الله کانسواءِ مونکي ۽ منھنجي ماءُ کي به خدا ڪري مڃيو، (عيسى) چوندو ته تون پاڪ آھين جنھن چوڻ جو مون کي ڪو حق نه آھي سو چوڻ مون کي ڪئن جڳائيندو ھو، جيڪڏھن مون اھو چيو ھوندو ته بيشڪ اُھو توکي معلوم ھوندو، جيڪي منھنجي دل ۾ آھي سو (تون) ڄاڻندو آھين ۽ جيڪي تنھنجي دل ۾ آھي سو (آءٌ) نه ڄاڻندو آھيان، بيشڪ تون ئي ڳُجھن جو ڏاڍو ڄاڻندڙ آھين.');
INSERT INTO "amroti" VALUES(5,117,'5:117','جنھن لاءِ مون کي حُڪم ڪيو اٿيئي تنھن کانسواءِ (ٻيو ڪي) کين نه چيو اٿم (يعني) ھي ته الله جي عبادت ڪريو جو منھنجو پالڻھار ۽ اوھان جو پالڻھار آھي، جيسين منجھن رھيس ته (آءٌ) مٿن نگھبان رھيس، پوءِ جنھن مھل تو مونکي کڻي ورتو ته تون ئي مٿن نگھبان رھيئن، ۽ تون سڀڪنھن شيء تي خبردار آھين.');
INSERT INTO "amroti" VALUES(5,118,'5:118','جيڪڏھن کين عذاب ڪرين ته اُھي تنھنجا ٻانھا آھن، جيڪڏھن کين بخشين ته تون ئي غالب حڪمت وارو آھين.');
INSERT INTO "amroti" VALUES(5,119,'5:119','الله فرمائيندو ته ھي اُھو ڏينھن آھي جو سچن کي سندين سچائي فائدو ڏيندي، اُنھن لاءِ (اُھي) بھشت آھن جن جي ھيٺان واھيون وھنديون آھن منجھس اصلي سداءِ رھڻ وارا آھن، الله کانئن راضي آھي ۽ اُھي کانئس راضي آھن، اِھا وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(5,120,'5:120','آسمانن ۽ زمين جي ۽ جيڪي منجھن آھي تنھنجي بادشاھي الله جي آھي، ۽ اُھو سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(6,1,'6:1','سڀ ساراہ (خاص) انھيءَ الله کي جڳائي جنھن آسمان ۽ زمين کي بڻايو ۽ اوندھ ۽ سوجھرو پيدا ڪيو، وري (به) ڪافر پنھنجي پالڻھار سان ٻـين کي برابر ڪندا آھن.');
INSERT INTO "amroti" VALUES(6,2,'6:2','اُھو (ئي الله) آھي جنھن اوھان کي مٽيءَ مان بڻايو وري (اوھان جي جئڻ ۽ موت جي) مُدّت ٺھرايائين، ۽ (ھڪڙي ٻي) مُدّت وٽس مُقرّر ٿيل آھي وري (اي ڪافرؤ الله بابت) اوھين شڪ ڪندا آھيو.');
INSERT INTO "amroti" VALUES(6,3,'6:3','۽ اھوئي الله آسمانن ۽ زمين ۾ (سڀڪنھن جو معبود) آھي، اوھان جو ڳجھ ۽ اوھان جو پڌرو ڄاڻندو آھي ۽ جيڪي ڪندا آھيو سوبه ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(6,4,'6:4','سندن پالڻھار جي آيتن مان ڪا آيت وٽن نه ايندي آھي پر کانئس منھن موڙيندا آھن.');
INSERT INTO "amroti" VALUES(6,5,'6:5','پوءِ بيشڪ سچ (يعني قرآن) کي جنھن مھل وٽن آيو (تنھن مھل) ڪوڙ ڀانيائون، پوءِ جنھن (ڳالھ) بابت چٿر ڪندا آھن تنھنجي (سزا جون) خبرون وٽن سگھو پھچنديون.');
INSERT INTO "amroti" VALUES(6,6,'6:6','(ڪافر) نه ڏسندا آھن ڇا؟ ته کانئن اڳي ڪيترا طبقا ناس ڪياسون جن کي زمين ۾ اھڙي قدرت ڏني ھئي سون جو اوھان کي به نه ڏني اٿئون ۽ مٿن آسمانن مينھن ساندہ وساياسون، ۽ سندن ھيٺان واھيون وھندڙ ڪيون سون پوءِ انھن کي سندن گناھن سببان ھلاڪ ڪيوسون ۽ سندن پوئتان ٻـين قومن کي پيدا ڪيوسون.');
INSERT INTO "amroti" VALUES(6,7,'6:7','۽ جيڪڏھن توتي ڪاغذن ۾ (لکيل) ڪتاب (قرآن) نازل ڪريون ھا پوءِ اُھي پنھنجا ھٿ کيس لائين ھا ته جيڪي ڪافر آھن سي ضرور چون ھا ته ھيءُ رڳو پڌرو جادو آھي.');
INSERT INTO "amroti" VALUES(6,8,'6:8','۽ چون ٿا ته مٿس مَلڪ ڇونه لاٿو ويو؟ ۽ جيڪڏھن اسين مَلڪ نازل ڪريون ھا ته ضرور ڪم پورو ٿي وڃي ھا (يعني مري وڃن ھا) وري کين مُھلت (مور) نه ڏجي ھا.');
INSERT INTO "amroti" VALUES(6,9,'6:9','۽ جيڪڏھن کيس مَلڪ ڪريون ھا ته ضرور کيس مڙس (جي شڪل ۾) بڻايون ھا ۽ جھڙو شبہ (ھاڻي) ڪندا آھن تھڙو شبہ مٿن قائم رکون ھا.');
INSERT INTO "amroti" VALUES(6,10,'6:10','۽ بيشڪ توکان اڳ (ٻـين) پيغمبرن سان (به) چٿر ڪئي ويئي (آھي) پوءِ منجھانئن جن چٿرون ڪيون تن تي جنھن (ڳالھ) تي چٿر ڪندا ھئا تنھن (جي سزا اچي) گھيرو ڪيو.');
INSERT INTO "amroti" VALUES(6,11,'6:11','(اي پيغمبر کين) چؤ ته ملڪ ۾ گھمو وري ڏسو ته ڪوڙن جي پڇاڙي ڪيئن ٿي؟');
INSERT INTO "amroti" VALUES(6,12,'6:12','(اي پيغمبر کانئن) پُڇا ڪر ته جيڪي آسمانن ۽ زمين ۾ آھي سو ڪنھنجو آھي؟ چؤ ته الله جو آھي، جنھن (ٻانِھن تي) ٻاجھه (ڪرڻ) پاڻ تي لازم ڪئي آھي، قيامت جي ڏينھن جنھن ۾ شڪ نه آھي ضرور اوھان کي گڏ ڪندو، جنھن پاڻ کي ڇيئي ۾ وڌو سي ڪڏھن ايمان نه آڻيندا.');
INSERT INTO "amroti" VALUES(6,13,'6:13','۽ جيڪو رات ۽ ڏينھن ۾ رھندو آھي، سو ان جي مِلڪ آھي، ۽ اُھو ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(6,14,'6:14','(اي پيغمبر کين) چؤ ته آسمانن ۽ زمين جي بڻائيندڙ الله کانسواءِ ٻئي کي سنڀاليندڙ ڪري ڇو وٺان؟ ۽ اُھو (سڀني کي) روزي ڏيندو آھي ۽ کيس روزي ڪونه ڏيندو آھي، چؤ ته مون کي حُڪم ڏنو ويو ته پھريون مسلمان آءٌ ھجان ۽ (فرمايو ويو ته) تون مشرڪن مان مُور نه ھج.');
INSERT INTO "amroti" VALUES(6,15,'6:15','چؤ ته جيڪڏھن پنھنجي پالڻھار جو چيو نه مڃيان ته آءٌ وڏي ڏينھن جي عذاب (پھچڻ) کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(6,16,'6:16','اُن ڏينھن جنھن (ماڻھو) کان (عذاب) ٽاريو ويندو تنھن تي بيشڪ الله ٻاجھ ڪئي، ۽ اھا پڌري مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(6,17,'6:17','۽ جيڪڏھن الله توکي ڪو ڏک پُھچائي ته اُن (الله) کانسواءِ ان جو ڪو لاھيندڙ نه آھي، ۽ جيڪڏھن اُھو توکي سُک پُھچائي ته اُھو سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(6,18,'6:18','۽ اُھو ٻانھن تي غالب آھي، ۽ اُھو حڪمت وارو خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(6,19,'6:19','پُڇا ڪر ته شاھديءَ ۾ ڪير وڏو آھي، چؤ منھنجي ۽ اوھان جي وچ ۾ الله ئي گواہ آھي، ۽ مون ڏانھن ھيءُ قرآن ھن لاءِ وحي ڪيو ويو آھي ته اُن سان اوھان کي ۽ جن جن کي (ھيءُ قرآن) پھچي (تن سڀني کي) ڊيڄاريان، (ھيءَ ڳالھ) اوھين ثابت ڪري سگھندؤ ڇا؟ ته الله ساڻ ٻيا (به) عبادت جا لائق آھن، چؤ ته آءٌ (اھڙي باطل ڪم جي) شاھدي نٿو ڏيان، (بلڪ سچي ڳالھ جي ثابتيءَ لاءِ) چؤ ته ھڪ الله کان سواءِ ڪوئي عبادت جو لائق ڪونھي ۽ جنکي اوھين شريڪ بڻائيندا آھيو تن کان آءٌ بيزار آھيان.');
INSERT INTO "amroti" VALUES(6,20,'6:20','جن کي ڪتاب ڏنوسين سي اُن (پيغمبر) کي اھڙيءَ طرح سڃاڻندا آھن جھڙيءَ طرح پنھنجن پٽن کي سڃاڻندا آھن، جن پاڻ کي ڇيئي ۾ وڌو سي ايمان نه آڻيندا.');
INSERT INTO "amroti" VALUES(6,21,'6:21','ان کان وڌيڪ ظالم ڪير آھي؟ جنھن الله تي ڪوڙو ٺاھ ٺاھيو يا سندس آيتن کي ڪوڙو ڄاتو، ڪو شڪ نه آھي ته ظالم نه ڇٽندا.');
INSERT INTO "amroti" VALUES(6,22,'6:22','۽ (اُھو وقت ياد ڪر) جنھن ڏينھن اُھي مڙئي گڏ ڪنداسين وري مشرڪن کي چونداسين ته اوھان جا اُھي شريڪ جن کي اوھين (الله جو شريڪ) ڀائيندا ھيؤ ڪٿي آھن؟');
INSERT INTO "amroti" VALUES(6,23,'6:23','وري سندن ڪوئي عذر ھن چوڻ کان سواءِ نه رھندو ته پنھنجي پالڻھار الله جو قسم اٿئون ته اسين شريڪ ڪندڙ نه ھئاسون.');
INSERT INTO "amroti" VALUES(6,24,'6:24','(اي پيغمبر) ڏس ته پاڻ تي ڪھڙو ڪوڙ چيائون جيڪي ٺاھ ٺاھيندا آھن سي کانئن ڀلجي ڀڄي ويا.');
INSERT INTO "amroti" VALUES(6,25,'6:25','۽ منجھائن ڪو اھڙو آھي جو تو ڏانھن (قرآن ٻڌڻ لاءِ) ڪَن ڏيندو آھي، ۽ سندين دلين تي ان جي سمجھڻ کان ڍڪ ۽ سندن ڪنن ۾ گھٻرائي ڪئي اٿئون، ۽ جيڪڏھن سڀ مُعجزا ڏسندا ته (به) کين نه مڃيندا، تان جو جنھن مھل توسان جھڳڙو ڪندڙ ٿي تو وٽ ايندا (تنھن مھل) ڪافر چوندا ته ھيءُ اڳين جي آکاڻين کانسواءِ ٻيو ڪجھ به نه آھي.');
INSERT INTO "amroti" VALUES(6,26,'6:26','۽ اُھي اُن (جي ٻڌڻ) کان (ٻـين کي) جھليندا آھن ۽ (پاڻ به) کانئن پري ڀڄندا آھن، ۽ پاڻ کانسواءِ (ٻئي ڪنھن کي) ھلاڪ نه ٿا ڪن ۽ نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(6,27,'6:27','۽ جڏھن (اُھي) باہ تي بيھاربا تڏھن جيڪر کين ڏسين (ته عجب ڪرين) پوءِ چوندا اسان کي ارمان آھي (جيڪر دنيا ۾) موٽايا وڃون ۽ پنھنجي پالڻھار جي آيتن کي ڪوڙو نه ڀانيون ۽ مؤمنن مان ٿيون.');
INSERT INTO "amroti" VALUES(6,28,'6:28','(نه!) بلڪ ھن کان اڳ جيڪي (ڪفر ۽ گناہ) لڪائيندا ھئا سي (اڄ) کين پڌرا نظر آيا، ۽ جيڪڏھن (دُنيا ڏانھن) موٽايا وڃن ته جنھن (ڪم) کان جھليا ويا سوئي وري ڪرڻ لڳندا ۽ بيشڪ اُھي ضرور ڪوڙا آھن.');
INSERT INTO "amroti" VALUES(6,29,'6:29','۽ چون ٿا ته ھن دُنيا جي حياتي کانسواءِ (ٻيو ڪجھ) نه آھي ۽ اسين (مرڻ کانپوءِ وري) اُٿڻ وارا نه آھيون.');
INSERT INTO "amroti" VALUES(6,30,'6:30','۽ جنھن مھل پنھنجي پالڻھار جي آڏو بيھاربا (تنھن مھل) جيڪڏھن کين ڏسين (ته عجب ڪرين، الله) چوندو ته ھي (وري جيئرو ٿيڻ) سچ نه آھي ڇا؟ چوندا ھائو اسانکي پنھنجي پالڻھار جو قسم آھي (ته سچ آھي، الله) چوندو ته جيڪي اوھين ڪفر ڪندا ھيؤ تنھن سببان عذاب (جو مزو) چکو.');
INSERT INTO "amroti" VALUES(6,31,'6:31','جن الله جي ملڻ کي ڪوڙ ڀانيو سي بيشڪ ڇيئي وارا ٿيا، ايتري قدر جڏھن اوچتو انھن تي قيامت ايندي (تڏھن) چوڻ لڳندا ته اسان اُن (قيامت) بابت جيڪا ڪوتاھي ڪئي تنھن تي اسان کي افسوس آھي ۽ اُھي پنھنجو بار پنھنجي پٺين تي کڻندا، خبردار جيڪي کڻندا سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(6,32,'6:32','۽ دُنيا جي حياتي رڳو راند ۽ چرچو آھي، ۽ جيڪي پرھيزگار آھن تن لاءِ آخرت جي جاءِ ڀلي آھي، پوءِ ڇونه ٿا سمجھو؟');
INSERT INTO "amroti" VALUES(6,33,'6:33','بيشڪ ڄاڻون ٿا ته جيڪي (اُھي) چوندا آھن سو توکي ضرور غمگين ڪندو پوءِ اُھي نه (رڳو) توکي ڪوڙو چوندا آھن پر ظالم الله جي آيتن جو انڪار ڪندا آھن.');
INSERT INTO "amroti" VALUES(6,34,'6:34','۽ بيشڪ توکان اڳ (گھڻا) پيغمبر ڪوڙا سمجھيا ويا ھئا پوءِ پنھنجي ڪوڙي ڀائنجڻ ۽ پنھنجي ڏکوئجڻ تي ايسين صبر ڪندا رھيا جيسين کين اسان جي مدد پُھتي، ۽ الله جي ڳالھين (يعني انجامن) کي (ڪوبه) ڦيريندو نه آھي، ۽ بيشڪ تو وٽ پيغمبرن جي ڪُجھ خبر آئي آھي.');
INSERT INTO "amroti" VALUES(6,35,'6:35','۽ جيڪڏھن سندن منھن موڙڻ توکي ڏکيو لڳي ٿو ته جيڪڏھن زمين ۾ ڪا سُرنگ ھڻي سگھين يا آسمان تي ڪا ڏاڪڻ ٺاھي سگھين يا وٽن ڪو معجزو آڻين ته آڻي انھن کي ڏيکار، ۽ جيڪڏھن الله گھري ھا ته مڙني کي ھدايت تي گڏ ڪري ھا پوءِ اڻ ڄاڻن مان نه ھُج.');
INSERT INTO "amroti" VALUES(6,36,'6:36','جيڪي (حق طلبيندڙ ٿي حق ڳالھ کي) ٻڌندا آھن سي ئي (اسلام جي دعوت) قبول ڪندا آھن (اُنھن کي الله ھدايت ڪندو آھي)، ۽ مئلن (يعني ڪافرن) کي الله قيامت جي ڏينھن کڙو ڪندو وري ڏانھس موٽايا ويندا.');
INSERT INTO "amroti" VALUES(6,37,'6:37','۽ چون ٿا ته سندس پالڻھار وٽان اُن (پيغمبر) تي ڪا نشاني ڇونه لاٿي وئي؟ (اي پيغمبر) کين چؤ ته الله نشاني جي لاھڻ تي وس وارو آھي پر منجھانئن گھڻا (ماڻھو) نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(6,38,'6:38','۽ نڪو زمين ۾ ڪوئي چرندڙ آھي ۽ نڪو پکي آھي جو پنھنجين کنڀڙاٽين سان اُڏامي ٿو پر اوھان جھڙيون ئي ٽوليون آھن، (سڀ) ڪا شيء ڪتاب (يعني لَوح مَحفُوظ) ۾ لکڻ کانسواءِ نه ڇڏي سون وري پنھنجي پالڻھار ڏانھن گڏ ڪيون وينديون.');
INSERT INTO "amroti" VALUES(6,39,'6:39','۽ جن اسان جي آيتن کي ڪُوڙو ڄاتو سي گونگا ۽ ٻوڙا اونداھين ۾ (پيل) آھن، الله جنھن کي گھُري تنھن کي ڀلائيندو آھي، ۽ جنھن کي گھُري تنھن کي سڌي واٽ تي بيھڪ ڏيندو آھي.');
INSERT INTO "amroti" VALUES(6,40,'6:40','(اي پيغمبر کين) چؤ ته (اوھين) ڏسو جيڪڏھن الله جو عذاب اوھان کي پُھچي يا قيامت اوھان وٽ اچي ته جيڪڏھن اوھان سچا آھيو ته الله کانسواءِ ڪنھن کي سڏيندؤ؟');
INSERT INTO "amroti" VALUES(6,41,'6:41','(نه!) بلڪ خاص ان کي سڏيندا پوءِ جيڪڏھن گھري ته جنھن (مصيبت جي لاھڻ) لاءِ اوھين کيس سڏيندا آھيو سا لاھي ڇڏي ۽ جنھن کي الله سان شريڪ ڪندا آھيو (سو انھيءَ مھل) وساريندا آھيو.');
INSERT INTO "amroti" VALUES(6,42,'6:42','۽ بيشڪ توکان اڳ (گھڻين) اُمتن ڏانھن (پيغمبر) موڪلياسون پوءِ (اُنھن جي بي فرمانيءَ سبب) کين اسان سختي ۽ تڪليف ۾ پڪڙيو ته مانَ اُھي زاري ڪن.');
INSERT INTO "amroti" VALUES(6,43,'6:43','پوءِ جنھن مھل اسان جو عذاب وٽن پُھتو ٿي (تنھن مھل) ڇونه ٿي زاري ڪيائون؟ پر سندن دليون سخت ٿي ويون ھيون ۽ جيڪي ڪم ڪندا ھئا سي شيطان انھن لاءِ سينگاريا.');
INSERT INTO "amroti" VALUES(6,44,'6:44','پوءِ جيڪا نصيحت ڏني وين سا جنھن مھل وساريائون (تنھن مھل) مٿن ھر شيء جا دروازا کولياسون، ايتريقدر جو جيڪي ڏنو وين تنھن سان جڏھن (چڱي طرح) خوش ٿيا (ته) کين اوچتو پڪڙيوسون پوءِ اُھي اُنھيءَ مھل نا اُميد ٿيا.');
INSERT INTO "amroti" VALUES(6,45,'6:45','پوءِ ظالم قوم جي پاڙ پٽي ويئي، ۽ سڀ ساراہ الله جھانن جي پالڻھار کي جڳائي.');
INSERT INTO "amroti" VALUES(6,46,'6:46','(اي پيغمبر کين) چؤ ته ڏسو ته جيڪڏھن الله اوھان جا ڪن ۽ اوھان جون اکيون کسي وٺي ۽ اوھان جي دلين تي مُھر ھڻي ته انھيءَ الله کانسواءِ ڪير ٻيو خُدا اوھان کي اُھو ڏيندو؟ ڏس ته ڪھڙا دليل بيان ڪريون ٿا وري به اُھي پاسو ڪندا آھن.');
INSERT INTO "amroti" VALUES(6,47,'6:47','(اي پيغمبر کين) چؤ ته اوھين خبر ڏيو ته جيڪڏھن الله جو عذاب اوچتو يا پڌرو اوھان کي پُھچي ته ظالم قوم کانسواءِ (ٻيو ڪو) ھلاڪ ٿيندو ڇا؟');
INSERT INTO "amroti" VALUES(6,48,'6:48','۽ پيغمبرن کي خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ئي ڪري موڪليوسون، پوءِ جن مڃيو ۽ پاڻ سڌاريو تن کي ڪو ڀؤ نه آھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(6,49,'6:49','۽ جن اسان جي آيتن کي ڪوڙو ڀانيو تن کي انھيءَ سببان جو بدڪاري ڪندا ھئا عذاب پھچندو.');
INSERT INTO "amroti" VALUES(6,50,'6:50','(اي پيغمبر کين) چؤ ته (آءٌ) اوھان کي نه ٿو چوان ته مون وٽ الله جا خزانا آھن ۽ نڪي (آءٌ) ڳُجھ ڄاڻندو آھيان ۽ نڪي اوھان کي چوان ٿو ته آءٌ مَلَڪ آھيان، ۽ جيڪي مون ڏانھن وحي ڪيو ويندو آھي تنھن کان سواءِ (ٻئي جي) تابعداري نه ڪندو آھيان، چؤ ته انڌو ۽ سڄو ھڪ جھڙا آھن ڇا؟ پوءِ اوھين ڇونه فڪر ڪندا آھيو.');
INSERT INTO "amroti" VALUES(6,51,'6:51','۽ (اي پيغمبر) جيڪي (ماڻھو) پنھنجي پالڻھار وٽ گڏ ٿيڻ کان ڊڄندا آھن تن کي ھن قرآن سان ڊيڄار الله کان سواءِ انھن جو نڪو سنڀاليندڙ ۽ نڪو سفارش ڪندڙ آھي مانَ اُھي پرھيزگاري ڪن.');
INSERT INTO "amroti" VALUES(6,52,'6:52','۽ جيڪي پنھنجي پالڻھار کي صُبح ۽ سانجھيءَ جو سندس رضامندي گھرندڙ ٿي سڏيندا آھن تن کي نه تَڙ، توتي سندن ڪجھ به حساب نه آھي ۽ نڪي مٿن تنھنجو ڪُجھ به حساب آھي جو انھن کي تڙيندين (جي کين تڙيندين) ته نامناسب ڪم ڪندڙن مان ٿيندين.');
INSERT INTO "amroti" VALUES(6,53,'6:53','۽ اھڙي طرح منجھانئن ڪن کي ڪن سان پرکيوسون ته چون ته ھيءَ ٽولي اُھا آھي ڇا جن تي اسان منجھان الله احسان ڪيو آھي، الله شڪر ڪندڙن کي نه ڄاڻندو آھي ڇا؟');
INSERT INTO "amroti" VALUES(6,54,'6:54','۽ جيڪي اسان جي آيتن کي مڃيندا آھن سي جڏھن تو وٽ اچن ته (انھن کي) چؤ ته اَلسَّلاَمُ عَلَيۡڪُمۡ اوھان جي پالڻھار پاڻ تي ٻاجھ لازم ڪئي آھي ته اوھان مان جيڪو بي علمي سان ڪا بڇڙائي ڪندو وري اُن کانپوءِ توبه ڪيائين ۽ سُڌريو ته اُھو (الله) بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(6,55,'6:55','۽ اھڙي طرح آيتون بيان ڪندا آھيون (ته عمل ڪريو) ۽ (ھن لاءِ) ته گنھگارن جو رستو (ماڻھن کي) پڌرو ٿئي.');
INSERT INTO "amroti" VALUES(6,56,'6:56','(اي پيغمبر کين) چؤ ته الله کانسواءِ جن کي سڏيو ٿا تن جي عبادت ڪرڻ کان مون کي منع ڪئي وئي آھي، ۽ چؤ ته آءٌ اوھانجي سَڌن جي تابعداري نه ڪندس (نه ته) بيشڪ اُنھي مھل گمراہ ٿيندس ۽ آءٌ ھدايت وارن مان نه ھوندس.');
INSERT INTO "amroti" VALUES(6,57,'6:57','چؤ ته آءٌ پنھنجي پالڻھار جي پڌري حُجّت تي آھيان ۽ اوھان ان کي ڪوڙ ڀانيو آھي، جنھن شيء جي اٻھر ڪندا آھيو سا مون وٽ نه آھي، (اھڙو) حُڪم الله کانسواءِ ٻئي ڪنھن جي اختيار ۾ نه آھي، اُھو سچ کي بيان ڪندو آھي ۽ اُھو (سڀني) فيصلي ڪندڙن کان ڀلو آھي.');
INSERT INTO "amroti" VALUES(6,58,'6:58','چؤ ته جنھن جي اٻھر ڪندا آھيو سا جيڪڏھن مون وٽ ھجي ھا ته منھنجي ۽ اوھان جي وچ ۾ فيصلو ٿي وڃي ھا، ۽ الله ظالمن کي چڱي طرح ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(6,59,'6:59','۽ وٽس ڳُجھ جون ڪنجيون آھن ان (ڳجھ جي ڪنجين) کي الله کانسواءِ (ٻيو ڪوبه) ڪونه ڄاڻندو آھي، ۽ جيڪي پٽن ۽ دريائن ۾ آھي سو (به اُھو) ڄاڻندو آھي، ۽ ڪوئي پَن نه ڪرندو آھي پر اُن کي اُھو ڄاڻندو آھي ۽ نڪي ڪو داڻو زمين جي اونداھين ۾ ۽ نڪي ڪا آلي ۽ نڪي ڪا سُڪي شيء پڌري ڪتاب ۾ (لکيل ھُجڻ) کانسواءِ آھي.');
INSERT INTO "amroti" VALUES(6,60,'6:60','۽ اُھو (الله) آھي جيڪو اوھان کي رات جو (ڄڻڪ) ماري ٿو ۽ جيڪي اوھين ڏينھن جو ڪمايو ٿا سو (به) ڄاڻندو آھي وري اوھان کي ان مان ھن لاءِ (جاڳائي) اٿاريندو آھي ته ٺھرايل مُدّت پوري ڪرائي وڃي، وري اوھان (سڀني) جو ڏانھس موٽڻ آھي وري جيڪي اوھين ڪندا آھيو تنھنجي اوھان کي سُڌ ڏيندو.');
INSERT INTO "amroti" VALUES(6,61,'6:61','۽ اُھو پنھنجن ٻانھن تي غالب آھي ۽ اوھان تي پنھنجا نگھبان موڪليندو آھي، ته جڏھن اوھان منجھان ڪنھن ھڪ کي موت اچي تڏھن اسان جا قاصد (ملائڪ) ان کي ماريندا آھن ۽ اُھي ڪوتاھي نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(6,62,'6:62','وري (سڀ مئل) سندن سچي ڌڻي الله ڏانھن موٽايا ويندا، خبردار ان جو حُڪم (غالب) آھي، ۽ اُھو جلد حساب وٺندڙ آھي.');
INSERT INTO "amroti" VALUES(6,63,'6:63','(اي پيغمبر) چؤ ته اوھان کي پَٽن ۽ دريائن جي اونداھين کان ڪير ڇڏائيندو آھي (جڏھن) کيس زاريءَ سان ۽ ڳجھو سڏيندا آھيو؟ (۽ چوندا آھيو) ته جيڪڏھن اسان کي ھن (ڏُک) کان الله ڇڏايو ته شڪر ڪندڙن مان ضرور ٿينداسون.');
INSERT INTO "amroti" VALUES(6,64,'6:64','(اي پيغمبر کين) چؤ ته الله اوھان کي ان کان ۽ سڀڪنھن ڏک کان ڇڏائيندو آھي وري به اوھين (ان سان) شرڪ ڪندا آھيو.');
INSERT INTO "amroti" VALUES(6,65,'6:65','(اي پيغمبر کين) چؤ ته اُھو (الله) اوھان تي اوھان جي مٿان يا اوھان جي پيرن جي ھيٺان عذاب موڪلڻ لاءِ وس وارو آھي يا اوھان کي ٽولي ٽولي ڪري وِڇڙائي ۽ اوھان مان ڪن کي ڪن جي لڙائي (جو مزو) چکائي، ڏس ته اسين ڪھڙي طرح (طرحين طرحين) دليل بيان ٿا ڪريون ته مانَ اُھي سمجھن.');
INSERT INTO "amroti" VALUES(6,66,'6:66','۽ تنھنجي قوم اُن (قرآن) کي ڪوڙ ڀانيو ۽ (حقيقت ڪري) اُھو بلڪل سچ آھي، (اي پيغمبر کين) چؤ ته آءٌ اوھان جو ذمي وار نه آھيان.');
INSERT INTO "amroti" VALUES(6,67,'6:67','(۽) ھر ھڪ شيء لاءِ ھڪ وقت مقرّر آھي ۽ سگھو ڄاڻندؤ.');
INSERT INTO "amroti" VALUES(6,68,'6:68','۽ جيڪي اسان جي آيتن بابت اجايو بحث ڪندا آھن تن کي جڏھن ڏسين ته کانئن مُنھن موڙ جيستائين اُن ڌاران ٻيءَ ڪنھن ڳالھه ۾ بحث ڪن، ۽ جيڪڏھن شيطان توکي وسارائي ته ياد اچڻ کانپوءِ ظالم ٽوليءَ سان نه ويھ.');
INSERT INTO "amroti" VALUES(6,69,'6:69','۽ جيڪي پرھيزگاري ڪندا آھن تن تي اُنھن (ظالمن) جي حساب جي ڪجھ به (ڇڪ تاڻ) نه آھي پر (انھن تي) نصيحت ڪرڻ لازم آھي مانَ اُھي ڊڄن.');
INSERT INTO "amroti" VALUES(6,70,'6:70','۽ (اي پيغمبر) جن پنھنجي دين کي راند ۽ تماشو بڻايو آھي ۽ کين دُنيا جي حياتيءَ ٺڳيو آھي تن کي ڇڏي ڏي ۽ قرآن سان نصيحت ڪر متان (قيامت جي ڏينھن) ڪوئي پنھنجي ڪئي جي سببان (ھلاڪت ۾) ڦاسي، الله کانسواءِ ان جو نڪو مددگار ۽ نڪو سفارش ڪندڙ آھي، ۽ جيڪڏھن سڀڪا شيء عوض ڏيندو ته کانئس نه ورتو ويندو، اِھي اُھي آھن جو جيڪي ڪيائون تنھن سببان (ھلاڪت ۾) ڦاسي ويا، اُنھن لاءِ ٽھڪندڙ پاڻي مان پيڻ ۽ ڏکوئيندڙ عذاب انھيءَ ڪري آھي جو ڪُفر ڪندا ھوا.');
INSERT INTO "amroti" VALUES(6,71,'6:71','(اي پيغمبر کين) چؤ ته الله کانسواءِ اھڙي کي سڏيون ڇا جيڪو اسان کي نڪي سُک ڏيندو آھي ۽ نڪي ڏُک ڏيندو آھي ۽ اُنھيءَ کان پوءِ جو الله اسان کي ھدايت ڪئي انھيءَ وانگر پنھنجين کڙين (ڀرپوءِ) تي موٽايا وڃون ڇا جنھن کي شيطانن جھنگ ۾ ڀلائي ڇڏيو ھجي اھو حيران پيو ڦري، سندس ڪيئي دوست ھُجن جي ان کي سڌي رستي ڏانھن پيا سڏين ته اسان ڏانھن اچ (۽ اھو اچي ئي نه، اي پيغمبر کين) چؤ ته الله جي ڏسيل واٽ اھائي سڌي واٽ آھي، ۽ اسان کي حڪم ڏنو ويو آھي ته جھانن جي پالڻھار جي فرمانبرداري ڪريون.');
INSERT INTO "amroti" VALUES(6,72,'6:72','۽ (ھيءُ به حڪم ڏنو ويو آھي) ته نماز پڙھو ۽ کانئس ڊڄو، ۽ اُھو (الله) آھي جنھن ڏانھن اُٿاريا ويندؤ.');
INSERT INTO "amroti" VALUES(6,73,'6:73','۽ اُھو ئي آھي جنھن آسمانن ۽ زمين کي رِٿ سان بڻايو آھي، ۽ جنھن ڏينھن (قيامت لاءِ) چوندو ته ٿيءُ ته ٿي پوندي. سندس ڳالھ سچي آھي، ۽ جنھن ڏينھن صور ۾ ڦوڪيو ويندو (تنھن ڏينھن) رڳو اُن جي بادشاھي آھي، ڳجھ ۽ ظاھر جو ڄاڻندڙ آھي، ۽ اُھو حِڪمت وارو خبردار آھي.');
INSERT INTO "amroti" VALUES(6,74,'6:74','۽ (ياد ڪر) جڏھن ابراھيم پنھنجي پيءُ آزر کي چيو ته بُتن کي خدا ڪري ڇوٿا وٺو؟ بيشڪ آءٌ توکي ۽ تنھنجي قوم کي پڌري گمراھيءَ ۾ ڏسان ٿو.');
INSERT INTO "amroti" VALUES(6,75,'6:75','۽ اھڙي طرح ابراھيم کي آسمانن ۽ زمين جي بادشاھي ڏيکاري سون ٿي (ھِن لاءِ) ته (اُھو) يقين ڪندڙن مان ٿيئي.');
INSERT INTO "amroti" VALUES(6,76,'6:76','پوءِ جنھن مھل رات مٿس اونداھي ڪئي (ته آسمان ۾) ھڪ تارو ڏٺائين، چيائين ھي منھنجو پالڻھار آھي، پوءِ جنھن مھل لٿو (ته) چيائين ته لھندڙن کي پيارو نه ٿو رکان.');
INSERT INTO "amroti" VALUES(6,77,'6:77','پوءِ جنھن مھل چنڊ کي چمڪندڙ ڏٺائين ته چيائين ھي منھنجو پالڻھار آھي، پوءِ جنھن مھل لٿو (ته) چيائين ته جيڪڏھن منھنجو پالڻھار مون کي ھدايت نه ڪندو ته آءٌ ضرور گمراہ قوم مان ٿيندس.');
INSERT INTO "amroti" VALUES(6,78,'6:78','پوءِ جنھن مھل سج کي چمڪندڙ ڏٺائين (ته) چيائين ھي منھنجو پالڻھار آھي (جو) ھي وڏو آھي، پوءِ جنھن مھل لٿو (ته) چيائين اي منھنجي قوم جن کي اوھين خُدا سان شريڪ بڻائيندا آھيو تن کان آءٌ بيزار آھيان.');
INSERT INTO "amroti" VALUES(6,79,'6:79','مون (سڀني کان) ھڪ طرفو ٿي پنھنجي مھاڙ کي انھيءَ الله ڏانھن سامھون ڪيو آھي جنھن آسمانن ۽ زمين کي بڻايو آھي ۽ آءٌ مشرڪن مان نه آھيان.');
INSERT INTO "amroti" VALUES(6,80,'6:80','۽ سندس قوم ساڻس تڪرار ڪيو، چيائين ته اوھين الله بابت مون سان ھن حالت ۾ ڇوٿا تڪرار ڪيو جو بيشڪ مونکي سڌي رستي لاتو اٿس جنھن کي ساڻس شريڪ ڪريو ٿا تنھن کان ڪين ڊڄندس پر منھنجي پالڻھار جو ڪجھ (ڪرڻ) گھريو (سو ٿيڻو آھي)، سڀڪا شيء منھنجي پالڻھار جي عِلم ۾ سمائي آھي، پوءِ اوھين ڇونه نصيحت مڃيندا آھيو.');
INSERT INTO "amroti" VALUES(6,81,'6:81','۽ جنھن کي الله سان شريڪ ڪريو ٿا تنھن کان ڪئن ڊڄندس ۽ (اوھين) ھن کان نه ٿا ڊڄو اوھين الله ساڻ اھو شريڪ ڪندا آھيو جنھن جي اوھان وٽ ڪا حُجّت لاٿل نه آھي، جيڪڏھن اوھين ڄاڻندا آھيو (ته ٻڌايو ته) ٻنھي ٽولين مان ڪھڙي امن جي وڌيڪ حقدار آھي؟');
INSERT INTO "amroti" VALUES(6,82,'6:82','جن ايمان آندو ۽ پنھنجي ايمان کي ڪفر سان نه وچڙايو اُنھن لاءِ امن آھي ۽ اُھي ھدايت وارا آھن.');
INSERT INTO "amroti" VALUES(6,83,'6:83','۽ ھي اسان جو دليل آھي جو ابراھيم کي سندس قوم تي (مقابلي لاءِ) ڏنوسون، جنھن کي گھرندا آھيون تنھنکي درجن ۾ مٿاھون ڪندا آھيون، ڇوته تنھنجو پالڻھار حڪمت وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(6,84,'6:84','۽ ابراھيم کي اسحاق ۽ يعقوب عطا ڪيوسون، سڀني کي ھدايت ڪئي سون، ۽ اُن کان اڳ نُوح کي ھدايت ڪئي سون ۽ سندس اولاد مان داؤد کي ۽ سُلَيمان ۽ اَيُّوب ۽ يُوسف ۽ مُوسىٰ ۽ ھارُون کي (ھدايت ڪئي سون)، ۽ اھڙي طرح اسين ڀلارن کي بدلو ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(6,85,'6:85','۽ ذڪريّا ۽ يَحۡيىٰ ۽ عيسىٰ ۽ الياس کي به، جو ھر ھڪ ڀلارن مان ھو.');
INSERT INTO "amroti" VALUES(6,86,'6:86','۽ اسماعيل ۽ يَسَعَ ۽ يُونس ۽ لُوط کي به، ۽ سڀني کي جڳ (جي رھندڙن) کان ڀلائي ڏني سون.');
INSERT INTO "amroti" VALUES(6,87,'6:87','۽ سندن پين ڏاڏن ۽ سندن اولاد ۽ سندن ڀائرن مان به، ۽ کين چونڊيوسون ۽ کين سڌي واٽ ڏانھن ھدايت ڪئي سون.');
INSERT INTO "amroti" VALUES(6,88,'6:88','اھا الله جي ھدايت آھي پنھنجن ٻانھن مان جنھن لاءِ گھري تنھن کي ان رستي لائي، ۽ جيڪڏھن (اھي ماڻھو) شرڪ ڪن ھا ته جيڪي ڪمايائون سو کانئن ضرور ناس ٿئي ھا.');
INSERT INTO "amroti" VALUES(6,89,'6:89','اِھي اُھي آھن جن کي ڪتاب ۽ حِڪمت ۽ پيغمبري ڏني سون، پوءِ جيڪڏھن ھي ماڻھو (قرآن جي انھن) ڳالھين کي نه مڃين ته بيشڪ اسان ھڪ قوم کي مقرّر ڪيو آھي جي (قرآن جي انھن) ڳالھين جا انڪار ڪندڙ نه ٿيندا.');
INSERT INTO "amroti" VALUES(6,90,'6:90','اِھي اُھي آھن جن کي الله ھدايت ڪئي آھي پوءِ تون به سندن طريقي جي پيروي ڪر، چؤ ته اوھان کان اُن (پيغام پُھچائڻ) تي ڪو اجورو نٿو گھُران، اِھو قرآن خاص جھانن لاءِ نصيحت آھي.');
INSERT INTO "amroti" VALUES(6,91,'6:91','۽ اُنھن (يھودين) الله جو سندس شان مطابق قدر نه ڪيو ھن ڪري جو چيائون ته الله ڪنھن ماڻھوءَ تي ڪُجھ نه لاٿو آھي، (اي پيغمبر کين) چؤ ته اُھو ڪتاب ڪنھن لاٿو جيڪو موسىٰ آندو ھو؟ جو ماڻھن لاءِ نُور ۽ ھدايت ھو جنھن کي اوھين جزون جزون ڪريو ٿا اُن جي ڪنھن ڀاڱي کي پڌرو ڪندا آھيو ۽ گھڻو لڪائيندا آھيو، ۽ جيڪي (ڳالھيون) نڪي اوھين ۽ نڪي اوھان جا پيءُ ڏاڏا ڄاڻندا ھئا سي اوھان کي سيکاريون ويون، چؤ ته الله (ڪتاب لاٿو آھي) وري انھن کي سندن اجائي بحث ۾ راند ڪندو ڇڏي ڏي.');
INSERT INTO "amroti" VALUES(6,92,'6:92','۽ ھي ڪتاب آھي جو ان کي برڪت وارو جيڪي اُن کان اڳ آھن تن کي سچو ڪندڙ لاٿوسون ھن لاءِ ته (تون) مڪّي وارن ۽ سندس آس پاس وارن کي ڊيڄارين، ۽ جيڪي آخرت کي مڃيندا آھن سي اُن (قرآن) کي مڃيندا آھن ۽ اُھي پنھنجي نمازن جي سنڀال ڪندا آھن.');
INSERT INTO "amroti" VALUES(6,93,'6:93','۽ اُن کان وڌيڪ ظالم ڪير آھي جيڪو الله تي ڪوڙو ٺاھ ٺاھي يا چوي ته مون ڏانھن وحي ڪيو ويو آھي ۽ ڏانھنس ڪُجھ به وحي نه ڪيو ويو ھجي يا چوي ته جئن الله (ڪتاب) نازل ڪيو آھي تئن آءٌ (به) ڪتاب نازل ڪندس، ۽ (اي پيغمبر) جيڪڏھن ڏسين (ته جيڪر عجب لڳيئي) جڏھن ظالم موت جي سڪراتين ۾ ھوندا ۽ ملائڪ پنھنجن ھٿن کي ڊگھو ڪندا، (۽ چوندا ته) پنھنجن ساھن کي ٻاھر ڪڍو، اڄ اوھان کي خواري واري عذاب جي سزا انھيءَ سببان ڏني ويندي جو اوھين الله تي ناحق چوندا ھيؤ ۽ سندس آيتن کان وڏائي ڪندا ھيؤ.');
INSERT INTO "amroti" VALUES(6,94,'6:94','۽ بيشڪ اسان وٽ (ائين) ھڪ ھڪ ٿي آيؤ جئن اوھان کي پھرين ڀيري پيدا ڪيو ھوسون ۽ جيڪي اوھان کي عطا ڪيو ھوسون سو اوھان پنھنجين پٺين پوءِ تان ڇڏي ڏنو، ۽ اوھان جا سفارش ڪندڙ اوھان سان نه ٿا ڏسون جن کي اوھين پنھنجن (احتياجن پوري ڪرڻ) ۾ (الله سان) شريڪ ڀائيندا ھيؤ، بيشڪ اوھان جو پاڻ ۾ ڳانڍاپو ڇنو ۽ جن کي اوھين ڀائيندا ھيؤ سي اوھان کان ڀُلجي ويا.');
INSERT INTO "amroti" VALUES(6,95,'6:95','الله ٻج ۽ ڪڪڙيءَ کي ڦٽائيندڙ آھي، مُئي مان جيئرو ڪڍندو آھي ۽ جيئري مان مُئو ڪڍندڙ آھي، اُھو (ئي) الله آھي پوءِ اوھين ڪيڏانھن ڀلايا ويندا آھيو.');
INSERT INTO "amroti" VALUES(6,96,'6:96','پرھ کي ڦٽائيندڙ آھي، ۽ رات کي آرام ۽ سج ۽ چنڊ کي (وقتن جي) حساب لاءِ (پيدا) ڪيائين، اھو اندازو (الله) غالب ڄاڻندڙ ٺاھيو آھي.');
INSERT INTO "amroti" VALUES(6,97,'6:97','۽ اُھو (الله) آھي جنھن تارن کي اوھان لاءِ ھن ڪري (پيدا) ڪيو ته پٽن ۽ دريائن جي اونداھين ۾ واٽون لھو، بيشڪ ڄاڻندڙ قوم لاءِ نشانيون (کولي) بيان ڪيون سون.');
INSERT INTO "amroti" VALUES(6,98,'6:98','۽ اُھو (الله) آھي جنھن اوھان کي ھِڪ جِيءَ مان پيدا ڪيو پوءِ (اوھان لاءِ) ٽڪڻ جو ھنڌ ۽ سانڀڻ جي جاءِ ڪيائين، بيشڪ سمجھندڙ ٽولي لاءِ نشانيون (کولي) بيان ڪيون سون.');
INSERT INTO "amroti" VALUES(6,99,'6:99','۽ اُھو (الله) آھي جنھن آسمان مان پاڻي وسايو، پوءِ اُن سان ھر شي جا اوڀڙ ڄماياسون پوءِ اُن مان ساوڪ کي ڪڍيوسون پوءِ منجھانئس (سنگن ۾) گڏيل داڻا ڪڍون ٿا، ۽ کجيءَ جي چپڙن مان گوشا لڙڪيل ۽ ڊاکن جا باغ ۽ زيتون ۽ ڏاڙھون ھڪ ٻئي جھڙا ۽ ڌار ڌار (پيدا ڪياسون)، جڏھن ڦرجن ته سندن ڦر ۽ ان جي پچڻ ڏانھن نھاريو، ڇوته اُن ۾ مڃيندڙ ٽولي لاءِ ضروري نشانيون آھن.');
INSERT INTO "amroti" VALUES(6,100,'6:100','۽ (ڪافرن) جنن کي الله سان شريڪ بڻايو آھي ھن ھوندي جو کين (الله) خلقيو آھي ۽ اُن لاءِ بي علميءَ سان پُٽ ۽ ڌيئرون ٺاھيون اٿن، اھو پاڪ آھي ۽ جيڪي بيان ڪندا آھن تنھن کان مٿاھون آھي.');
INSERT INTO "amroti" VALUES(6,101,'6:101','آسمانن ۽ زمين جو بڻائيندڙ آھي، انھيءَ کي اولاد جو احتياج ڪئن ٿيندو؟ ۽ اُن کي زال (جو احتياج) نه آھي، ۽ سڀڪا شي (پاڻ) خلقيائين، ۽ اُھو سڀڪنھن شي کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(6,102,'6:102','اھو اوھان جو پالڻھار آھي، اُن کانسواءِ ڪو عبادت جو لائق نه آھي، سڀڪنھن شي کي پيدا ڪندڙ آھي تنھنڪري سندس عبادت ڪريو، ۽ اُھو سڀڪنھن شي کي سنڀاليندڙ آھي.');
INSERT INTO "amroti" VALUES(6,103,'6:103','(دنيا ۾) اکيون کيس ڏسي نه سگھنديون ۽ اُھو اکين کي ڏسندو آھي، ۽ اُھو باريڪ بين خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(6,104,'6:104','بيشڪ اوھان جي پالڻھار کان اوھان وٽ نشان آيا آھن، پوءِ جنھن ڏٺو (يعني سمجھيو) تنھن پاڻ لاءِ (نفع) ڪمايو، ۽ جيڪو انڌو ٿيو تنھن تي ان جو نقصان آھي، ۽ (اي پيغمبر چؤ ته) آءٌ اوھان تي نگھبان نه آھيان.');
INSERT INTO "amroti" VALUES(6,105,'6:105','۽ اھڙي طرح طرحين طرحين دليل بيان ڪريون ٿا ۽ ھن لاءِ متان چون ته تون پڙھيو آھين ۽ ھن لاءِ ته ھن (دين) کي اُنھيءَ قوم لاءِ پڌرو ڪريون جي ڄاڻن ٿا.');
INSERT INTO "amroti" VALUES(6,106,'6:106','(اي پيغمبر) جيڪي تنھنجي پالڻھار وٽان توڏانھن وحي ڪيو ويو آھي تنھنجي تابعداري ڪر، اُن کانسواءِ ڪو عبادت جو لائق نه آھي، ۽ مُشرڪن کان مُنھن موڙ.');
INSERT INTO "amroti" VALUES(6,107,'6:107','۽ جيڪڏھن الله گُھري ھا ته شرڪ نه ڪن ھا، ۽ توکي مٿن نگھبان نه ڪيو اٿئون، ۽ نڪي تون مٿن ڪو نظر ڪندڙ آھين.');
INSERT INTO "amroti" VALUES(6,108,'6:108','(اي مسلمانؤ) جن کي (مُشرڪ) الله کانسواءِ سڏيندا آھن تن کي گاريون نه ڏيو جي متان (اُھي) بي سمجھائي جي ڪري دشمنيءَ کان الله کي ڳالھائين، اھڙي طرح ھر ٽوليءَ لاءِ سندن ڪم سينگارياسون، وري سندن پالڻھار ڏانھن سندن موٽڻ آھي پوءِ جيڪي ڪندا آھن تنھن جي کين سُڌ ڏيندو.');
INSERT INTO "amroti" VALUES(6,109,'6:109','۽ ڪافر پنھنجي سخت سُنھن سان الله جو قسم کڻندا آھن ته وٽن جيڪڏھن ڪو معجزو اچي ته ان کي ضرور مڃيندا، چؤ ته مُعجزا ته (خاص) الله وٽ آھن (اي مسلمانؤ) اوھان کي ڪنھن سمجھايو آھي ته اُھي مڃيندا (سو معجزا وٽن) جڏھن آيا ته به نه مڃيندا؟');
INSERT INTO "amroti" VALUES(6,110,'6:110','۽ سندن دلين کي ۽ سندن اکين کي ڦيرائينداسون جئن ان کي پھرين ڀيري نه مڃيائون ۽ کين سندين گُمراھي ۾ حيران ٿيل ڇڏينداسون.');
INSERT INTO "amroti" VALUES(6,111,'6:111','۽ جيڪڏھن انھن تي اسين ملائڪ لاھيون ھا ۽ ساڻن مُئا (به) ڳالھائين ھا ۽ ھر شيء سندن آمھون سامھون ڪري گڏ (به) ڪريون ھا ته به الله جي گھُر کانسواءِ ڪڏھن ايمان نه آڻين ھا پر اُنھن مان گھڻا بي سمجھائي ڪن ٿا.');
INSERT INTO "amroti" VALUES(6,112,'6:112','۽ اھڙي طرح ھر ڪنھن پيغمبر لاءِ ماڻھن ۽ جنن مان شيطان ويري بڻاياسون (ته پاڻ ۾) ھڪ ٻئي کي وسوسو وجھي ڳالھ جي مُلَمَّعۡ سان ٺڳين، ۽ جيڪڏھن تنھنجو پالڻھار گھُري ھا ته اھو ڪم نه ڪن ھا تنھنڪري کين ۽ جيڪي ٺاہ ٺاھيندا تنھن کي ڇڏي (ڏي).');
INSERT INTO "amroti" VALUES(6,113,'6:113','۽ (ھن لاءِ وسوسو وجھندا آھن) ته جيڪي آخرت کي نه مڃيندا آھن تن جون دليون ڏانھس لڙن ۽ ان کي پسند ڪن ۽ (ھن لاءِ) ته اُھي جيڪي ڪم ڪندڙ آھن سي ڪندا رھن.');
INSERT INTO "amroti" VALUES(6,114,'6:114','(اي پيغمبر چؤ ته) الله ڌاران ٻيو فيصلو ڪندڙ ھن ھوندي ڇو گُھران؟ جو اُنھيءَ اوھان ڏانھن کولي بيان ڪندڙ ڪتاب لاٿو آھي، ۽ جن کي ڪتاب (توريت) ڏنوسون سي (چڱي طرح) ڄاڻندا آھن ته اُھو تنھنجي پالڻھار کان جائي لاٿل آھي تنھنڪري تون شڪ ڪندڙن مان مور نه ٿج.');
INSERT INTO "amroti" VALUES(6,115,'6:115','۽ تنھنجي پالڻھار جو سخن سچائي ۽ انصاف ۾ پورو آھي، سندس سخنن کي ڪو مٽائيندڙ نه آھي، ۽ اُھو ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(6,116,'6:116','۽ جيڪڏھن زمين وارن (ماڻھن) مان گھڻن (يعني ڪافرن) جو چيو مڃيندين ته توکي الله جي واٽ کان ڀُلائيندا، (اُھي) رڳو گمان تي ھلندا آھن ۽ اُھي رڳو اٽڪل ھڻندا آھن.');
INSERT INTO "amroti" VALUES(6,117,'6:117','بيشڪ تنھنجو پالڻھار اُنھن کي (به) چڱو ڄاڻندڙ آھي جيڪي سندس واٽ کان ڀُلا، ۽ اُھو ھدايت وارن کي (به) چڱو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(6,118,'6:118','پوءِ جيڪڏھن اوھين سندس آيتن کي مڃيندا آھيو ته جنھن تي الله جو نالو ياد ڪجي تنھن مان کائو.');
INSERT INTO "amroti" VALUES(6,119,'6:119','۽ اوھان کي ڇا (ٿيو) آھي جو جنھن (شيء) تي الله جو نالو ياد ڪجي تنھن مان نه کائيندا آھيو ھن ھوندي جو جيڪي (شيون) الله اوھان تي حرام ڪيون آھن سي اوھان لاءِ بيشڪ بيان ڪيون اٿس؟ پر جنھن (حرام جي کائڻ) لاءِ اوھين لاچار ٿيو (سو به روا آھي) ۽ گھڻا اڻ ڄاڻائيءَ سان پنھنجن سَڌن سببان ضرور ڀلائين ٿا، ڇوته تنھنجو پالڻھار حد کان لنگھندڙن کي چڱي طرح ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(6,120,'6:120','۽ ظاھري گناہ ۽ باطني گناہ (سڀ طرح) ڇڏي ڏيو، جيڪي گناہ ڪندا آھن سي جيڪي ڪن ٿا تنھن سببان سگھو سزا ڏبين.');
INSERT INTO "amroti" VALUES(6,121,'6:121','۽ جنھن (شيء) تي الله جو نالو ياد نه ڪجي تنھن مان نه کائو جو اُھو (کائڻ) ضرور گُناہ آھي، ۽ شيطان پنھنجن يارن کي ھن لاءِ وسوسو وجھندا آھن ته اوھان سان جھيڙو ڪن، ۽ جيڪڏھن سندن چيو مڃيندؤ ته اوھين (به) ضرور مشرڪ ٿيندؤ.');
INSERT INTO "amroti" VALUES(6,122,'6:122','جيڪو مئل ھو تنھن کي جياريوسون ۽ اُن لاءِ سوجھرو ڪيوسون جنھن سان ماڻھن ۾ گھُمي ٿو اُھو اُن جي مثال جھڙو آھي ڇا جيڪو اونداھين ۾ آھي منجھانئس نڪرڻ وارو نه آھي، اھڙي طرح ڪافر جيڪي ڪندا آھن سو انھن لاءِ سينگاريو ويو آھي.');
INSERT INTO "amroti" VALUES(6,123,'6:123','۽ اھڙي طرح ھر ڳوٺ ۾ اُنھي (ڳوٺ) جا وڏيرا ھن لاءِ بدڪار ڪياسون ته اُن (ڳوٺ) ۾ فساد وجھندا رھن، ۽ (اُھي) پاڻ کانسواءِ ٻئي ڪنھن لاءِ فساد نه ڪندا آھن ۽ نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(6,124,'6:124','۽ جڏھن وٽن ڪا آيت ايندي آھي (ته) چوندا آھن ته جھڙي (پيغمبري) الله جي پيغمبرن کي ڏني وئي آھي تھڙي جيستائين اسان کي (نه) ڏني ويندي تيسين ڪڏھن نه مڃينداسون، الله جنھن ھنڌ پنھنجي پيغمبري کي موڪلي (سو ھنڌ) چڱو ڄاڻندڙ آھي، جن ڏوہ ڪيو تن کي الله وٽان خواري ۽ سخت عذاب انھي ڪري سگھو پھچندو جو شرارتون ڪندا آھن.');
INSERT INTO "amroti" VALUES(6,125,'6:125','پوءِ الله جنھن کي گھرندو آھي ته ھدايت ڪري تنھن جو سينو اسلام لاءِ ڪشادو ڪندو آھي، ۽ جنھن کي ڀلائڻ گھرندو آھي تنھن جي سيني کي اھڙو سخت سوڙھو ڪندو آھي جو ڄڻڪ آسمان تي چڙھي ٿو، جيڪي نه مڃيندا آھن تن تي الله اھڙي پليتي رکندو آھي.');
INSERT INTO "amroti" VALUES(6,126,'6:126','۽ تنھنجي پالڻھار جي اِھا سِڌي واٽ آھي، بيشڪ اسان نشانيون انھيءَ قوم لاءِ کولي بيان ڪيون آھن جي نصيحت مڃيندا آھن.');
INSERT INTO "amroti" VALUES(6,127,'6:127','اُنھن لاءِ سندن پالڻھار وٽ بھشت آھي ۽ اُھو سندن سنڀاليندڙ آھي (ھيءُ سڀ) انھيءَ ڪري (آھي) جو (چڱا) ڪم ڪندا ھوا.');
INSERT INTO "amroti" VALUES(6,128,'6:128','۽ جنھن ڏينھن اُنھن مڙني کي گڏ ڪندو، (چوندو) ته اي جنن جون ٽوليون بيشڪ ماڻھن مان گھڻا پنھنجا (تابع) ڪيا ھيوَ، ۽ ماڻھن مان سندن سنگتي چوندا ته اي اسان جا پالڻھار اسان منجھان ھڪڙن ٻـين کان فائدو ورتو ۽ انھي پنھنجي ٺھرايل مُدّت کي پُھتاسون جيڪا تون اسان لاءِ ٺھرائي، ھيءُ (الله) چوندو ته اوھان جو رھڻ جو ھنڌ دوزخ آھي ان ۾ ھميشہ رھڻ وارا ھوندؤ مگر جيڪي خُدا گھريو، ڇوته تنھنجو پالڻھار حڪمت وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(6,129,'6:129','۽ اھڙي طرح ظالمن مان ڪن کي ڪن مان جيڪي ڪندا آھن تنھن جي (شامت) سببان پاڻ ۾ گڏينداسون.');
INSERT INTO "amroti" VALUES(6,130,'6:130','(۽) اي جنّن ۽ ماڻھن جون ٽوليون اوھان مان اوھان وٽ پيغمبر نه آيا ھئا ڇا؟ جو منھنجون آيتون اوھان تي پڙھيائون ٿي ۽ ھن ڏينھن جي آڏي ٿيڻ کان اڳ اوھان کي ڊيڄاريائون ٿي، چوندا ته پاڻ تي شاھدي ڏنيسون ۽ کين دنيائي حياتيءَ ٺڳيو ھو ۽ (ھاڻي) پاڻ تي شاھدي ڏنائون ته پاڻ ڪافر ھوا.');
INSERT INTO "amroti" VALUES(6,131,'6:131','اھو (پيغمبر جو موڪلڻ) ھن سببان آھي ته تنھنجو پالڻھار ڪنھن ڳوٺ کي ھن حالت ۾ ظُلم سان ڪڏھن ناس ڪرڻ وارو نه آھي جو سندس رھاڪو بيخبر ھجن.');
INSERT INTO "amroti" VALUES(6,132,'6:132','۽ ھر ڪنھن جيڪي عمل ڪيا تنھن مان انھن لاءِ درجا آھن، ۽ جيڪي ڪندا آھن تنھن کان تنھنجو پالڻھار بي خبر نه آھي.');
INSERT INTO "amroti" VALUES(6,133,'6:133','۽ تنھنجو پالڻھار بي پرواہ ٻاجھ وارو آھي، جيڪڏھن گھري ته اوھان کي ناس ڪري ۽ اوھان کان پوءِ جنھن لاءِ گھري تنھن کي (اُن ھنڌ) پيڙھيءَ تي وھاري جئن اوھان کي ٻي قوم جي نسل مان پيدا ڪيائين.');
INSERT INTO "amroti" VALUES(6,134,'6:134','بيشڪ جيڪو انجام اوھان سان ڪجي ٿو سو ضرور اچڻو آھي اوھين ٿڪائڻ وارا نه آھيو.');
INSERT INTO "amroti" VALUES(6,135,'6:135','(اي پيغمبر) چؤ ته اي منھنجي قوم اوھين پنھنجي ھنڌ عمل ڪريو آءٌ (به) عمل ڪندڙ آھيان، پوءِ سگھو ڄاڻندؤ ته آخرت ۾ ڪنھن جي عاقبت چڱي ھوندي، سچ آھي ته ظالم نه ڇٽندا.');
INSERT INTO "amroti" VALUES(6,136,'6:136','۽ جيڪي پوک ۽ ڍورن مان الله پيدا ڪيو آھي تنھن مان ھڪ حصّو (ڪافر) الله جو مقرّر ڪندا آُھن ۽ پنھنجي گمان سان چوندا آھن ته ھيءُ الله جو آھي ۽ ھي اسان جي بتن جو آھي، وري جيڪا (شيء) سندن بتن جي ھوندي آھي سا الله وٽ پھچي نه سگھندي آھي، ۽ جيڪا (شيء) الله جي ھوندي آھي سا سندن بتن ڏانھن وڃي سگھندي آھي، جيڪي فيصلو ڪندا آھن سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(6,137,'6:137','۽ اھڙي طرح گھڻن مشرڪن لاءِ سندن شريڪن (جن کي خدا ڪري مڃيندا آھن) پنھنجي اولاد جو مارڻ سينگاريو آھي ته کين ھلاڪ ڪن ۽ سندن دين مٿن رلائي ملائي ڇڏين، ۽ جي الله گھُري ھا ته اُھو نه ڪن ھا تنھنڪري کين ۽ جيڪي ٺاھ ٺاھيندا آھن تنھن کي ڇڏي ڏي.');
INSERT INTO "amroti" VALUES(6,138,'6:138','۽ پنھنجي خيال سان ھي (به) چوندا آھن ته ھيءُ ڍور ۽ پوک حرام آھن جنھن لاءِ گھرون تنھن کانسواءِ ان کي ڪوئي نه کائي ۽ (چوندا آھن) ته ھڪڙن ڍورن جي پُٺي (سواريءَ لاءِ) حرام ڪئي ويئي آھي ۽ (ٻـين) ڍورن تي (ذبح وقت) الله جو نالو ياد نه ڪندا آھن (اھي ڳالھيون) الله تي ڪوڙي ٺاہ ٺاھڻ سان (ڪندا آھن) جيڪي ٺاہ ٺاھيندا آھن تنھنجي کين سگھو سزا ڏيندو.');
INSERT INTO "amroti" VALUES(6,139,'6:139','۽ چوندا آھن ته جيڪي ھِنن ڍورن جي پيٽن ۾ آھي سو خلاصو اسان جي مڙسن لاءِ آھي ۽ اسان جي زالن تي حرام ڪيل آھي، ۽ جيڪڏھن مُئل ھوندو ته اُھي (سڀ) اُن ۾ ڀائيوار آھن، سندن تقرير جي کين سگھي سزا ڏيندو، بيشڪ الله حڪمت وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(6,140,'6:140','جن پنھنجي اولاد کي بي سمجھائي ڪري ڄاڻپ کانسواءِ ڪُٺو ۽ جيڪي الله سندن رزق ڪيو سو الله تي ڪوڙي ٺاہ ٺاھڻ سان حرام ڪيائون سي بيشڪ ڇيئي وارا ٿيا، (اُھي) بيشڪ ڀُلا ۽ ھدايت وارا نه آھن.');
INSERT INTO "amroti" VALUES(6,141,'6:141','۽ اُھو (الله) آھي جنھن ڇَٽئي وارا ۽ ريء ڇَٽئي وارا باغ ۽ کجيون ۽ پوکون جن جا ڦر قسم قسم ۽ زيتون ۽ ڏاڙھون ھڪ جھڙا ۽ جُدا جُدا قسم جا پيدا ڪيا، جڏھن ڦر جھلين تڏھن سندن ڦر مان کائو ۽ سندن لاباري وقت الله جو حق (سندن زڪوٰة) ڏيو، ۽ اجايو نه وڃايو، ڇوته الله وڃائيندڙن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(6,142,'6:142','۽ ڍورن مان بار کڻڻ وارا ۽ زمين سان لڳل (ننڍا پيدا ڪيائين)، الله جيڪا اوھان جي روزي ڪئي آھي تنھن مان کائو ۽ شيطان جي وکن تي نه ھلو، ڇوته اُھو اوھان جو پڌرو ويري آھي.');
INSERT INTO "amroti" VALUES(6,143,'6:143','(ڍور) اَٺ قسم آھن، رڍن مان ٻه ۽ ٻڪرين مان ٻه (نر ۽ ماديون) آھن، (اي پيغمبر کانئن) پُڇ ته الله ٻنھي نرن کي حرام ڪيو آھي يا مادين کي يا جنھن کي مادين جي ٻنھي قسمن جي ڳڀيرڻين لپيٽيو آھي؟ جيڪڏھن اوھين سچا آھيو ته مون کي دليل سان خبر ڏيو.');
INSERT INTO "amroti" VALUES(6,144,'6:144','۽ اُٺن مان ٻه قسم ۽ ڳونئين مان ٻه قسم، (اي پيغمبر کانئن) پُڇ ته نرن کي الله حرام ڪيو آھي يا مادين کي يا جنھن کي مادين جي ٻنھي قسمن جي ڳڀيرڻين لپيٽيو آھي، يا جنھن مھل الله اوھان کي اھا وصيت ڪئي ته (اُوھين) اُن وقت حاضر ھيؤ؟ پوءِ جيڪو الله ڪوڙو ٺاہ ھن لاءِ ٺاھي ته ڄاڻپ کانسواءِ ماڻھن کي ڀُلائي تنھن کان وڌيڪ ظالم ڪير آھي؟ بيشڪ الله ظالم قوم کي سڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(6,145,'6:145','(اي پيغمبر) چؤ ته جيڪي حُڪم مُون ڏانھن نازل ٿيا آھن تن ۾ ڪا چيز جنھن کي کائيندڙ کائي حرام نٿو لھان ھن کانسواءِ ته (اُھو) مُئل ھجي يا وھندڙ رَتُ يا سُوئر جو گوشت جو اھي (سڀ) حرام آھن يا گناہ طرح جنھن (شيء) کي الله جي نالي کانسواءِ ٻئي جي نالي سان سڏيو وڃي، پوءِ جيڪو لاچاري حالت ۾ نڪي بي فرمان ۽ نڪي حد کان لنگھندڙ ٿيء (۽ کائي) ته بيشڪ تنھنجو پالڻھار بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(6,146,'6:146','يَھُودين تي سڀئي نَھن وارا جانور حرام ڪيا ھواسون، ۽ (پڻ) ڳئن ۽ ٻڪرين مان انھن جي پنيءَ واري لڳل وَہ يا آنڊي واري يا ھڏن سان گڏيل وھ کانسواءِ (ٻي سڀ وھ) مٿن حرام ڪئي ھئي سون، اِھا سندن بي فرمانيءَ سببان کين سزا ڏني سون، ۽ اسين ته ضرور سچ چوڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(6,147,'6:147','پوءِ (اي پيغمبر) جيڪڏھن توکي ڪوڙو ڀانئين ته چؤ ته اوھان جو پالڻھار گھڻي رحمت وارو آھي، ۽ ڏوھارڻ قوم کان سندس عذاب نه ٽاربو.');
INSERT INTO "amroti" VALUES(6,148,'6:148','مشرڪ سگھو چوندا ته جي الله گھري ھا ته نڪي اسين شرڪ ڪريون ھا ۽ نڪي اسان جا اَبا ڏاڏا (شرڪ ڪن ھا) ۽ نڪي اسين ڪا شيء حرام ڪريون ھا، اھڙي طرح کانئن جيڪي اڳ ھوا تن به ڪوڙ ڀانيو ايتري قدر جو اسان جي سزا (جو مزو) چکيائون، (اي پيغمبر کين) چؤ ته اوھان وٽ ڪو دليل آھي ته اُھو اسان لاءِ ڪڍي آڻين، (اوھين) رڳو گمان تي ھلندا آھيو ۽ اوھين رڳو اٽڪل ھڻندا آھيو.');
INSERT INTO "amroti" VALUES(6,149,'6:149','(اي پيغمبر کين) چؤ ته الله جي حُجّت مُحڪم آھي، جيڪڏھن گھري ھا ته ضرور اوھان مڙني کي سڌو رستو ڏيکاري ھا.');
INSERT INTO "amroti" VALUES(6,150,'6:150','(اي پيغمبر کين) چؤ ته پنھنجن شاھدن کي سڏيو جيڪي شاھدي ڏين ته (تون) انھن سان شاھدي نه ڏي، ۽ جن اسان جي آيتن کي ڪُوڙ ڄاتو ۽ جيڪي آخرت کي نه مڃيندا آھن تن جي چوڻ تي نه ھل ۽ اُھي پنھنجي پالڻھار سان ٻـين کي برابر ڪن ٿا.');
INSERT INTO "amroti" VALUES(6,151,'6:151','(اي پيغمبر کين) چؤ ته اوھان جي پالڻھار اوھان تي جيڪي حرام ڪيو آھي سو اچو ته آءٌ پڙھي ٻُڌايان (اُھو ھيء آھي ته) اوھين ڪنھن شيء کي ساڻس شريڪ نه ڪريو ۽ ماءُ پيءُ سان چڱائي ڪريو، ۽ اوھين پنھنجي اولاد کي سڃائي سببان نه ڪُھو، اسين اوھانکي (به) ۽ خاص انھن کي (به) روزي ڏيندا آھيون، ۽ بي حيائي جي ڪمن مان جيڪي پڌرو ھجي ۽ جيڪي ڳجھو ھجي تنھن (سڀ) کي (اوھين) ويجھا نه وڃو، ۽ نڪي (اھڙي) ماڻھوءَ کي ماريو جنھنجو مارڻ حق (شرعي قانون) کانسواءِ الله حرام ڪيو ھجي، اِھو (حُڪم) آھي جيڪو الله اوھان کي ڏنو آھي ته اوھين سمجھو.');
INSERT INTO "amroti" VALUES(6,152,'6:152','۽ جيڪا (واٽ) بلڪل چڱي ھجي تنھن کانسواءِ ٻيءَ طرح سان يتيم جي مال کي ويجھا نه وڃو جيسين اُھو پنھنجي جوانيءَ کي پھچي، ۽ ماپ ۽ تور انصاف سان پُوري ڏيو وٺو، اسين ڪنھن ماڻھوءَ کي سندس وس کان (وڌيڪ) تڪليف نه ٿا ڏيون، ۽ جڏھن اوھين (پاڻ ۾) ڳالھايو تڏھن انصاف سان ھلو جيتوڻيڪ مائٽي وارو ھجي، ۽ الله جو انجام پاڙيو، اھو (حُڪم) اوھان کي ڏنو اٿس ته مانَ اوھين نصيحت وٺو.');
INSERT INTO "amroti" VALUES(6,153,'6:153','۽ ھيءَ ته اِھا مُنھنجي سِڌي واٽ آھي تنھنڪري اُن تي ھلو، ۽ ٻـين واٽن تي نه ھلو جو اُھي واٽون اوھان کي سندس واٽ کان جدا ڪنديون، اھو اوھان کي حُڪم فرمايو اٿس مانَ اوھين پرھيزگاري ڪريو.');
INSERT INTO "amroti" VALUES(6,154,'6:154','وري مُوسىٰ کي ڪتاب اُنھيءَ ماڻھو تي پنھنجي نعمت جي پوري ڪرڻ لاءِ جو ڀلارو ھجي ۽ سڀڪنھن شيء جي چٽائيءَ لاءِ ۽ ھدايت ۽ ٻاجھ لاءِ ڏنوسون ته مانَ اُھي پنھنجي پالڻھار جي ديدار کي مڃين.');
INSERT INTO "amroti" VALUES(6,155,'6:155','ھيءُ برڪت وارو ڪتاب (يعني قرآن) لاٿوسون تنھنڪري ان جي تابعداري ڪريو ۽ ڊڄو ته اوھان تي ٻاجھ ڪئي وڃي.');
INSERT INTO "amroti" VALUES(6,156,'6:156','(ھن لاءِ لاٿوسون ته اي ڪافرؤ) متان چوڻ لڳو ته اسان کان اڳ وارين ٻن ٽولين کانسواءِ ٻئي (ڪنھن) تي ڪتاب نه لاٿو ويو آھي، ۽ اسين سندس پڙھي پڙھائي کان بيخبر ھئاسون.');
INSERT INTO "amroti" VALUES(6,157,'6:157','يا چوڻ لڳو ته جيڪڏھن اسان تي ڪو ڪتاب نازل ٿئي ھا ته (اسين) ضرور اُنھن کان وڌيڪ ھدايت وارا ٿيون ھا، پوءِ بيشڪ اوھان جي پالڻھار کان (روشن) دليل ۽ ھدايت ۽ ٻاجھ اوھان وٽ آئي آھي، پوءِ اُن کان کان وڌيڪ ظالم ڪير آھي جو الله جي آيتن کي ڪُوڙ چوي ۽ کانئن مُنھن موڙي؟ جيڪي اسان جي آيتن کان مُنھن موڙيندا تن کي انھيءَ سببان بُڇڙي عذاب جي سزا ڏينداسون جو مُنھن موڙيندا ھوا.');
INSERT INTO "amroti" VALUES(6,158,'6:158','ھن کانسواءِ انتظار نه ڪندا آھن ته وٽن ملائڪ اچن يا تنھنجو پالڻھار اچي يا تنھنجي پالڻھار جون ڪي نشانيون اچن، جنھن ڏينھن تنھنجي پالڻھار جون ڪي نشانيون اينديون (تنھن ڏينھن) اھڙي ڪنھن ماڻھو کي سندس ايمان آڻڻ فائدو نه ڏيندو جنھن اڳ ايمان نه آندو ھوندو يا پنھنجي ايمان ۾ چڱائي نه ڪئي ھوندي، (اي پيغمبر کين) چؤ ته انتظار ڪريو اسين به انتظار ڪندڙ آھيون.');
INSERT INTO "amroti" VALUES(6,159,'6:159','جن پنھنجي دين کي ڌاروڌار ڪري ڇڏيو ۽ ٽوليون ٽوليون ٿي ويا تن سان تُنھنجو ڪو واسطو نه آھي، سندن ڪم الله جي ئي حوالي آھي وري جيڪي ڪندا آھن تنھن جي کين سُڌ ڏيندو.');
INSERT INTO "amroti" VALUES(6,160,'6:160','جيڪو ڪا چڱائي (الله جي حضور ۾) آڻيندو تنھن لاءِ اُن جھڙيون ڏھوڻيون آھن، ۽ جيڪو بڇڙائي آڻيندو تنھن کي اُن جھڙي کانسواءِ (ٻي) سزا نه ڏبي ۽ اُنھن تي ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(6,161,'6:161','(اي پيغمبر کين) چؤ ته منھنجي پالڻھار مون کي سڌي واٽ ڏانھن ھدايت ڪئي آھي، جو مُحڪم دين ابراھيم حنيف (خُدا ڏانھن ھڪ طرفي) جو طريقو آھي، ۽ (ابراھيم) مُشرڪن مان نه ھو.');
INSERT INTO "amroti" VALUES(6,162,'6:162','(اي پيغمبر کين ھيء به) چؤ ته مُنھنجي نماز ۽ منھنجون مڙئي عبادتون ۽ منھنجو جيئڻ ۽ منھنجو مرڻ (سڀ) جھانن جي پالڻھار الله لاءِ آھي.');
INSERT INTO "amroti" VALUES(6,163,'6:163','کيس ڪو شريڪ نه آھي، ۽ مون کي ان لاءِ حُڪم ٿيل آھي ۽ آءٌ پھريون مسلمان آھيان.');
INSERT INTO "amroti" VALUES(6,164,'6:164','چؤ الله کانسواءِ ٻيو پالڻھار ڇو گھران؟ ۽ اُھو ھر شيء جو پالڻھار آھي، ۽ سڀڪو ماڻھو جيڪي ڪندو سو (بار) رڳو مٿس ھوندو، ۽ ڪو کڻندڙ ٻئي جو بار نه کڻندو، وري اوھان کي پنھنجي پالڻھار ڏانھن موٽڻ جو ھنڌ آھي پوءِ جنھن ڳالھ بابت اوھين تڪرار ڪندا آھيو تنھنجي اوھان کي سُڌ ڏيندو.');
INSERT INTO "amroti" VALUES(6,165,'6:165','۽ اُھو (الله) آھي جنھن اوھان کي زمين جو بادشاہ ڪيو آھي ۽ اوھان مان ڪن جا درجا ڪن کان ھن لاءِ بلند ڪيائين ته جيڪي اوھان کي ڏنو اٿس تنھن ۾ اوھان کي پرکي، ڇوته تنھنجو پالڻھار جلد سزا ڏيندڙ (به) آھي، ۽ بيشڪ اُھو بخشڻھار مھربان (به) آھي.');
INSERT INTO "amroti" VALUES(7,1,'7:1','الٓمّٓصٓ.');
INSERT INTO "amroti" VALUES(7,2,'7:2','(اي پيغمبر ھيء) ڪتاب توڏانھن لاٿو ويو ته ساڻس (ماڻھن) کي ڊيڄارين تنھنڪري ان بابت تنھنجي سيني ۾ ڪا اوکائي ٿيڻ نه گھرجي ۽ مؤمنن لاءِ نصيحت آھي.');
INSERT INTO "amroti" VALUES(7,3,'7:3','جيڪي اوھان ڏانھن اوھان جي پالڻھار کان لاٿو ويو تنھن جي پيروي ڪريو ۽ اُن (الله) کانسواءِ ٻـين دوستن جي پيروي نه ڪريو، اوھان (تمام) ٿوري نصيحت وٺندا آھيو.');
INSERT INTO "amroti" VALUES(7,4,'7:4','۽ گھڻا ئي ڳوٺ ھئا جي ويران ڪياسون پوءِ (يا) راتوواہ (سمھڻ مھل) اُنھن کي اسان جو عذاب پُھتو يا اُھي منجھند جو ننڊ ڪرڻ وارا ھوا.');
INSERT INTO "amroti" VALUES(7,5,'7:5','پوءِ جڏھن کين اسان جو عذاب پھتو (تڏھن) سندن پڪار ھن چوڻ کانسواءِ نه ھئي ته اسين پاڻ تي ظلم ڪندا ھواسون.');
INSERT INTO "amroti" VALUES(7,6,'7:6','پوءِ جن ڏانھن پيغمبر موڪليا ويا تن کان (به) ضرور پُڇنداسون ۽ پيغمبرن کان (به) ضرور پُڇنداسون.');
INSERT INTO "amroti" VALUES(7,7,'7:7','پوءِ (سندن ڪرتوت) مٿن علم سان بيان ڪنداسون ۽ اسين پري نه ھواسون.');
INSERT INTO "amroti" VALUES(7,8,'7:8','۽ اُن ڏينھن (عملن جي) تور سچ آھي، پوءِ جن جا تراڙا ڳرا ٿيا سي ئي ڇُٽل آھن.');
INSERT INTO "amroti" VALUES(7,9,'7:9','۽ جن جا تراڙا ھلڪا ٿيا سي اُھي آھن جن پاڻ کي انھيءَ سببان ڇيئو لاتو جو اسان جي آيتن بابت انڪار ڪندا ھئا.');
INSERT INTO "amroti" VALUES(7,10,'7:10','۽ بيشڪ اوھان کي زمين ۾ ٽڪايوسون ۽ منجھس اوھان لاءِ گذران (جو اُپاءُ) رکيوسون، (پر اوھين) ٿورو شڪر ڪندا آھيو.');
INSERT INTO "amroti" VALUES(7,11,'7:11','۽ بيشڪ اوھان کي پيدا ڪيوسون وري اوھان جي شڪل بڻائي سون وري ملائڪن کي چيوسون ته آدم کي سجدو ڪريو، پوءِ شيطان کانسواءِ (ٻـين) سجدو ڪيو، شيطان سجدي ڪندڙن مان نه ٿيو.');
INSERT INTO "amroti" VALUES(7,12,'7:12','(الله) چيو ته توکي سجدي ڪرڻ کان ڪھڙي (ڳالھ) جھليو؟ جڏھن ته توکي حُڪم (به) ڪيم، چيائين ته آءٌ کانئس ڀلو آھيان، مون کي باہ مان پيدا ڪيو اٿئي ۽ ان کي مٽيءَ مان پيدا ڪيو اٿئي.');
INSERT INTO "amroti" VALUES(7,13,'7:13','(الله) چيو ته اُن (آسمان) مان ھيٺ لھي وڃ توکي منجھس وڏائي ڪرڻ نه جڳائيندي آھي تنھنڪري (تون) نِڪر ڇوته تون خوار ٿيلن مان آھين.');
INSERT INTO "amroti" VALUES(7,14,'7:14','چيائين ته قيامت جي ڏينھن تائين مون کي مُھلت ڏي.');
INSERT INTO "amroti" VALUES(7,15,'7:15','چيائين ته تون مُھلت ڏنلن مان آھين.');
INSERT INTO "amroti" VALUES(7,16,'7:16','چيائين انھيءَ سببان جو تو مونکي گمراہ ڪيو (آءٌ ته) ماڻھن لاءِ تنھنجي سڌي واٽ تي ضرور وھندس.');
INSERT INTO "amroti" VALUES(7,17,'7:17','پوءِ سندن اڳيان ۽ سندن پوئتان ۽ سندن سڄي پاسي کان ۽ سندن کٻي پاسي کان وٽن ايندس، ۽ منجھائن گھڻا شڪر ڪندڙ نه لھندين.');
INSERT INTO "amroti" VALUES(7,18,'7:18','(الله) فرمايو ته تون آسمانن مان خوار (۽) تڙيل ٿي نڪري وڃ، انھن مان جيڪو تنھنجي تابعداري ڪندو (تنھنکي ۽ توکي دوزخ ۾ وجھي) اوھان مڙني مان دوزخ ضرور ڀريندس.');
INSERT INTO "amroti" VALUES(7,19,'7:19','۽ (چيوسون ته) اي آدم تون ۽ تنھنجي زال بھشت ۾ وڃي رھو ۽ (جيڪي وڻيوَ سو) جتان وڻيوَ تِتان کائو ۽ ھن وڻ کي ويجھا نه وڃو نه ته ظالمن مان ٿيندؤ.');
INSERT INTO "amroti" VALUES(7,20,'7:20','پوءِ شيطان انھن کي ھن لاءِ وسوسو وڌو ته سندن اوگھڙن مان جيڪي ڍڪيل ھو سو اُنھن لاءِ پڌرو ڪري ۽ چيائين ته اوھان جي پالڻھار اوھان کي ھن وڻ کان رڳو ھن ڪري جھليو آھي ته (متان) اوھين ملائڪ ٿي پئو يا (اُن ۾) سدائين رھڻ وارا ٿي پئو.');
INSERT INTO "amroti" VALUES(7,21,'7:21','۽ سُنھن کڻي انھن کي چيائين ته آءٌ (ته) اوھان جو خيرخواہ آھيان.');
INSERT INTO "amroti" VALUES(7,22,'7:22','پوءِ اُنھن کي ٺڳيءَ سان لاڙي وڌائين، پوءِ جنھن مھل وڻ مان چکيائون (ته) سندين اوگھڙ کين پڌري ٿي پئي ۽ بھشت جي وڻ مان پن ھڪ ٻئي سان ڳنڍي پاڻ تي ويڙھڻ لڳا، ۽ سندن پالڻھار انھن کي سڏيو (۽ چيائين ته) اوھان کي ھن وڻ کان نه جھليو ھوم ڇا؟ ۽ اوھان کي نه چيو ھوم ڇا ته شيطان اوھان جو پڌرو ويري آھي؟');
INSERT INTO "amroti" VALUES(7,23,'7:23','چيائون ته اي اسان جا پالڻھار اسان پاڻ تي ظلم ڪيو آھي ۽ جيڪڏھن (تون) اسان کي نه بخشيندين ۽ اسان تي ٻاجھ نه ڪندين ته ضرور ڇيئي وارن مان ٿينداسين.');
INSERT INTO "amroti" VALUES(7,24,'7:24','(الله) فرمايو ته ھيٺ لھي وڃو اوھين پاڻ ۾ ھڪ ٻئي جا ويري آھيو، ۽ اوھان لاءِ زمين ۾ رھڻ جو ھنڌ ۽ (گذران جو) سامان (ھڪ) وقت تائين (ٺھرايل) آھي.');
INSERT INTO "amroti" VALUES(7,25,'7:25','(الله) چيو ته اوھين ان ۾ حياتي گذاريندؤ ۽ منجھس مرندؤ ۽ منجھانئس (جيئرا ٿي) نڪرندؤ.');
INSERT INTO "amroti" VALUES(7,26,'7:26','اي آدم جي اولاد تحقيق اوھان تي ھڪ پوشاڪ بيشڪ لاٿي سون جا اوھان جي اوگھڙن کي ڍڪيندي ۽ سينگار جا ڪپڙا، ۽ پرھيزگاري جي پوشاڪ اھا (سڀني کان) ڀلي آھي، اھا الله جي نشانين مان آھي مانَ اُھي نصيحت وٺن.');
INSERT INTO "amroti" VALUES(7,27,'7:27','اي آدم جا اولاد شيطان اوھان کي اھڙيءَ طرح خرابيءَ ۾ نه وجھي جھڙي طرح اوھان جي ماءُ پيءُ کي بھشت مان ٻاھر ڪڍيائين جو سندين پوشاڪ کانئن ھن لاءِ ڇڪي لاٿائين ٿي ته کين سندين اوگھڙ ڏيکاري، سچ آھي ته اُھو پاڻ ۽ سندس لشڪر جتان اوھين انھن کي نه ڏسي سگھندا آھيو (تِتان اُھي) اوھان کي ڏسندا آھن، جيڪي نه مڃيندا آھن تن جو اسان شيطانن کي دوست بڻايو آھي.');
INSERT INTO "amroti" VALUES(7,28,'7:28','۽ (پڻ انھن جا دوست جي) جڏھن ڪو بي حيائي جو ڪم ڪن ٿا (ته) چون ٿا ته اُن (واٽ) تي اسان جا ابا ڏاڏا (ھلندا) ڏٺاسون ۽ الله اسانکي اُنھن (جي ڪرڻ) جو حُڪم ڪيو آھي، (اي پيغمبر کين) چؤ ته بيشڪ الله بيحيائيءَ جي ڪمن جو حُڪم نه ڪندو آھي، اوھين الله تي (اھو ڪوڙ) ڇو چوندا آھيو جنھن جي (سند) اوھين نه ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(7,29,'7:29','چؤ ته منھنجي پالڻھار انصاف جو حُڪم ڪيو آھي، ۽ (حُڪم ڪيو اٿس ته) پنھنجا مُنھن سڀڪنھن نماز مھل (الله ڏانھن) سِڌا ڪريو ۽ اُن لاءِ سچا فرمانبردار ٿي سندس عبادت ڪريو، جئن پھريون اوھان کي پيدا ڪيائين تئن (اوھين) موٽندؤ.');
INSERT INTO "amroti" VALUES(7,30,'7:30','ھڪ ٽوليءَ کي ھدايت ڪيائين ۽ ٻي ٽوليءَ تي گمراھي لازم ٿي چڪي آھي، ڇوته اُنھن الله کانسواءِ شيطانن کي مددگار ڪري ورتو آھي ۽ ڀائيندا آھن ته پاڻ ھدايت وارا آھن.');
INSERT INTO "amroti" VALUES(7,31,'7:31','اي آدم جا اولاد سڀڪنھن نماز مھل پنھنجي زينت (جي پوشاڪ) ڍڪيو ۽ کائو ۽ پيئو ۽ اجايو نه وڃايو، ڇوته الله اجائي وڃائيندڙن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(7,32,'7:32','(اي پيغمبر) چؤ ته الله جي (بڻايل) زينت جيڪا جيڪا پنھنجن ٻانھن لاءِ پيدا ڪئي اٿس ۽ روزيءَ مان سٺيون شيون ڪنھن حرام ڪيون آھن؟ چؤ ته اُھي (شيون) دُنيا جي حياتي ۾ (۽) خلاصيون قيامت جي ڏينھن مؤمنن لاءِ آھن، اھڙيءَ طرح جا قوم ڄڻندي آھي تنھن لاءِ نشانيون بيان ڪندا آھيون.');
INSERT INTO "amroti" VALUES(7,33,'7:33','(اي پيغمبر) چؤ ته منھنجي پالڻھار بي حيائيءَ جي ڪمن مان جيڪي پڌرو ھجي ۽ جيڪي ڳجھو ھجي ۽ (سڀ) گناہ ۽ ناحق حد کان لنگھڻ ۽ اھڙي شيء کي اوھان جو الله سان شريڪ ڪرڻ جنھن بابت ڪا حُجّت نه لاٿي اٿس ۽ جيڪي نه ڄاڻندا آھيو سو اوھان جو الله تي چوڻ اھي ئي حرام ڪيون آھن.');
INSERT INTO "amroti" VALUES(7,34,'7:34','۽ ھر ڪنھن اُمّت جو ھڪ وقت ٺھرايل آھي، پوءِ جڏھن سندن مُدّت پھچندي (ته) ھڪ گھڙي نڪي دير ڪندا ۽ نڪي اڳڀرو ويندا.');
INSERT INTO "amroti" VALUES(7,35,'7:35','(چيو ويو ته) اي آدم جا اولاد جيڪڏھن اوھان وٽ اوھان مان پيغمبر اچن (جي) اوھان کي منھنجون آيتون پڙھي ٻڌائين ته جيڪي ڊڄندا ۽ پاڻ سڌاريندا تن کي نڪي ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(7,36,'7:36','۽ جن اسان جي آيتن کي ڪوڙ ڀانيو ۽ ان جي (قبولڻ) کان وڏائي ڪئي سي دوزخي آھن، اُھي اُن ۾ سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(7,37,'7:37','جنھن الله تي ڪوڙو ٺاھ ٺاھيو يا سندس آيتن کي ڪوڙ ڀانيو تنھن کان وڌيڪ ظالم ڪير آھي؟ اُنھن کي (لوح محفوظ ۾) لکيل مان سندن حِصّو (سُک ۽ ڏُک جو) ملندو رھندو، ايتري قدر جو جنھن مھل اسان جا قاصد (ملائڪ) سندن رُوح ڪڍڻ لاءِ ايندا چوندا ته اوھين الله کانسواءِ جن کي سڏيندا ھيؤ سي ڪٿي آھن؟ (تنھن مھل چوندا) ته اسان کان لڪي ويا ۽ پاڻ تي شاھدي ڏيندا ته پاڻ ڪافر ھوا.');
INSERT INTO "amroti" VALUES(7,38,'7:38','(الله) چوندو ته اُنھي ٽولي سان دوزخ ۾ گھڙو جيڪي اوھان کان اڳ جنن ۽ ماڻھن مان گذري ويا، جنھن مھل ھڪ ٽولي پئي گھڙندي تنھن مھل پنھنجي سنگت تي لعنت پئي ڪندي، تان جو جڏھن منجھس مڙئي گھڙندا ته (اُن وقت) سندين پوئين ٽولي پنھنجي پھرين ٽوليءَ لاءِ چوندي ته اي اسانجا پالڻھار ھنن اسان کي گمراہ ڪيو ھو تنھنڪري اُنھن کي باھ جو ٻيڻو عذاب ڏي، (الله) چوندو ته سڀڪنھن لاءِ ٻيڻو عذاب آھي پر اوھين نه ٿا ڄاڻو.');
INSERT INTO "amroti" VALUES(7,39,'7:39','۽ منجھانئن پھرين (ٽولي) اُنھن مان پوئينءَ لاءِ چوندي ته اسان تي اوھان جي ڪا وڏائي نه آھي تنھنڪري جيڪي ڪمايو ھيؤ تنھن سببان عذاب (جو مزو) چکو.');
INSERT INTO "amroti" VALUES(7,40,'7:40','جن اسان جي حُڪمن کي ڪوڙ ڀانيو ۽ کانئن وڏائي ڪئي تن لاءِ آسمان جا دروازا نه کوليا ويندا ۽ نڪي ايستائين بھشت ۾ گھڙندا جيستائين اُٺ سُئي جي پاکي مان لنگھي، ۽ اھڙي طرح ڏوھارين کي سزا ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(7,41,'7:41','اُنھن لاءِ وڇاڻو (به) دوزخ جو ھوندو ۽ سندن مٿان پوش (به دوزخ جو) رھندو، ۽ اھڙي طرح ظالمن کي سزا ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(7,42,'7:42','۽ جن ايمان آندو ۽ چڱا ڪم ڪيا ڪنھن ماڻھوءَ کي سندس وس کان وڌيڪ تڪليف ڏيندا ئي نه آھيون، اُھي بھشتي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(7,43,'7:43','۽ جيڪو غير سندين دلين ۾ ھوندو سو ڪڍي ڇڏينداسون سندين (ماڙين جي) ھيٺان واھيون پيون وھنديون، ۽ چوندا ته سڀ ساراہ انھي کي جڳائي جنھن اسان کي ھن (بھشت) ڏانھن رستو لاتو، جيڪڏھن الله اسان کي ھدايت نه ڪري ھا ته ڪڏھن اسين ھدايت نه لھون ھا، بيشڪ اسان وٽ اسان جي پالڻھار جا پيغمبر سچ وٺي آيا، ۽ سڏيو ويندو ته ھي بھشت (اوھان کي ڏنو ويو) اُن جا اُنھيءَ سببان (اوھين) وارث آھيو جو (چڱائي) ڪندا ھيؤ.');
INSERT INTO "amroti" VALUES(7,44,'7:44','۽ بھشتي دوزخين کي سڏيندا (۽ چوندا) ته اسان جي پالڻھار اسان سان جيڪي انجام ڪيو ھو سو بيشڪ سچو لڌوسون پوءِ اوھان جي پالڻھار اوھان سان جيڪو انجام ڪيو ھو سو سچو (نه) لڌوَ ڇا؟ چوندا ته ھائو! پوءِ سڏيندڙ سندن وچ ۾ سڏيندو ته اُنھن ظالمن تي الله جي لعنت آھي.');
INSERT INTO "amroti" VALUES(7,45,'7:45','جيڪي الله جي واٽ کان (ماڻھن کي) جھليندا رھيا ۽ ان جي واٽ جي ڏنگائي گھرندا رھيا، ۽ اُھي آخرت کي نه مڃيندڙ ھوا.');
INSERT INTO "amroti" VALUES(7,46,'7:46','۽ سندن وچ ۾ پردو ڪيو ويندو، ۽ اَعراف ۾ اھڙا ماڻھو ھوندا جو سڀڪنھن کي سندين پيشانين مان سڃاڻندا، ۽ بھشتين کي سڏي اَلسّلَامُ عَلَيۡڪُمۡ چوندا، (اڃا) اُن ۾ (پاڻ) نه گھڙيا ھوندا ۽ اُھي اميدوار ھوندا.');
INSERT INTO "amroti" VALUES(7,47,'7:47','۽ جڏھن سندن اکيون دوزخين ڏانھن ڦيرايون وينديون (ته) چوندا اي اسان جا پالڻھار اسان کي ظالم ٽوليءَ سان گڏ نه ڪر.');
INSERT INTO "amroti" VALUES(7,48,'7:48','۽ اَعراف وارا جن مُڙسن کي سندين پيشانين مان سڃاڻندا تن کي سڏيندا چوندا ته اوھان جيڪي گڏ ڪيو ھو سو ۽ جيڪا وڏائي ڪندا ھيؤ تنھن اوھان کان ڪجھ به نه ٽاريو.');
INSERT INTO "amroti" VALUES(7,49,'7:49','ھيءُ اُھي (ماڻھو) آھن ڇا جن لاءِ اوھين (دنيا ۾) قسم کڻندا ھيؤ ته الله مٿن ٻاجھ نه ڪندو، (کين چئبو ته) بھشت ۾ گھڙو اوھان کي ڪو ڀؤ نه آھي ۽ نڪي اوھين غمگين رھندؤ.');
INSERT INTO "amroti" VALUES(7,50,'7:50','۽ دوزخي بھشتين کي سڏيندا (چوندا) ته ڪجھ پاڻي اسان تي پلٽيو يا الله اوھان کي جيڪي رزق ڏنو آھي تنھن مان (ڪجھ ڏيو)، چوندا ته اُھي ٻئي ڪافرن تي الله حرام ڪيا آھن.');
INSERT INTO "amroti" VALUES(7,51,'7:51','جن پنھنجي دين کي کِل ۽ راند ڪري ورتو ۽ انھن کي دُنيا جي حياتي ٺڳيو، پوءِ اُنھن کي اڄ ايئن وساريوسون جئن (اُنھن) منھنجي ھن ڏينھن جي ملڻ کي وساريو ھو ۽ (جئن) اسان جي آيتن جو انڪار ڪندا ھوا.');
INSERT INTO "amroti" VALUES(7,52,'7:52','۽ بيشڪ انھن کي ھڪ ڪتاب ڏنوسون جنھن کي دانائيءَ سان کولي بيان ڪيوسون (۽) اُنھيءَ قوم لاءِ ھدايت ۽ ٻاجھ آھي جي مڃيندا آھن.');
INSERT INTO "amroti" VALUES(7,53,'7:53','(ڪافر) ھن کانسواءِ انتظار نه ڪندا آھن ته اُن (جي وعدہ) جي مُراد اچي، جنھن ڏينھن سندس مراد ايندي (تنھن ڏينھن) جن ان کي اڳي وساريو سي چوندا ته بيشڪ اسان جي پالڻھار جا پيغمبر سچ سان آيا، پوءِ (اڄ) اسان لاءِ ڪي سفارش ڪرڻ وارا آھن؟ جو اسان لاءِ پارت ڪن يا وري موٽايا وڃون ته جيڪي (عمل) ڪياسون تن کانسواءِ ٻيا (چڱا) عمل ڪريون، بيشڪ پاڻ کي ڇيئو لاتائون ۽ جيڪو ٺاھ ٺاھيندا ھوا سو کانئن ڀلجي ويو.');
INSERT INTO "amroti" VALUES(7,54,'7:54','اوھان جو پالڻھار اھو الله آھي جنھن آسمانن ۽ زمين کي ڇھن ڏينھن ۾ بڻايو وري عرش ڏي متوجه ٿيو، رات کي ڏينھن سان ڍڪيندو آھي ۽ سج ۽ چنڊ ۽ تارا سندس حُڪم سان نوايل آھن، خبردار پيدا ڪرڻ ۽ حُڪم ڪرڻ اُن جو (ڪم) آھي، جھانن جو پالڻھار وڏي برڪت وارو آھي.');
INSERT INTO "amroti" VALUES(7,55,'7:55','پنھنجي پالڻھار کي زاري ڪندڙ ۽ ڳجھا ٿي سڏيو، ڇوته اُھو حد کان لنگھندڙن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(7,56,'7:56','۽ اوھين مُلڪ ۾ ان جي سڌاري کانپوءِ فساد نه وجھو ۽ اُن (الله) کي (کانئس) ڊڄي ۽ طمع رکي سڏيو، بيشڪ الله جي ٻاجھ ڀلارن کي ويجھي آھي.');
INSERT INTO "amroti" VALUES(7,57,'7:57','۽ اُھو (الله) آھي جو ھوائن کي پنھنجي رحمت (واريءَ برسات) جي اڳيان خوشخبري ڏيندڙ ڪري گھلائيندو آھي، ايتري قدر جو جنھن مھل (اُھي) ڳرن ڪڪرن کي کڻن ٿيون ته انھن کي ڪنھن غير آباد ٿيل ڏيہ ڏانھن روانو ڪريون ٿا پوءِ انھن (ڪڪرن) مان پاڻي وسايون ٿا پوءِ اُن سان ھر جنس جا ميوا ڄمايون ٿا، اھڙي طرح اسين مُئن کي (قبرن مان) ٻاھر ڪڍنداسون ته مانَ اوھين نصيحت وٺو.');
INSERT INTO "amroti" VALUES(7,58,'7:58','۽ (جا) سٺي زمين (آھي تنھن) جا سَلا سندس پالڻھار جي حُڪم سان ڄمندا آھن، ۽ جيڪا اَدنىٰ آھي (تنھن جا سَلَا) خراب سَلَن کان سواءِ ٻيا نه ڄمندا آھن، اھڙي طرح جيڪا قوم شُڪرانو ڪندڙ آھي تنھن لاءِ نشانيون طرحين طرحين بيان ڪندا آھيون.');
INSERT INTO "amroti" VALUES(7,59,'7:59','بيشڪ نوح کي سندس قوم ڏانھن پيغمبر ڪري موڪليوسون پوءِ چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اُن (الله) کانسواءِ ڪو اوھان جو خدا نه آھي، آءٌ اوھان تي وڏي ڏينھن جي عذاب (اچڻ) کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(7,60,'7:60','سندس قوم جي سردارن چيو ته اسين توکي پڌريءَ گمراھيءَ ۾ (پيل) ڏسون ٿا.');
INSERT INTO "amroti" VALUES(7,61,'7:61','چيائين ته اي منھنجي قوم مون کي ڪا گمراھي ڪانه آھي پر آءٌ جھانن جي پالڻھار جو پيغمبر آھيان.');
INSERT INTO "amroti" VALUES(7,62,'7:62','اوھان کي پنھنجي پالڻھار جو پيغام پھچائيندو آھيان ۽ اوھان کي نصيحت ڪندو آھيان ۽ الله (جي پار) کان اُھو ڄاڻندو آھيان جيڪي اوھين نه ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(7,63,'7:63','اوھان کي (ھن ڳالھ جو) عجب لڳو ڇا ته اوھان جي پالڻھار کان ھڪ سمجھاڻي اوھان مان ھڪ مڙس جي ھٿان اوھان وٽ ھِن لاءِ آئي ته اوھان کي ڊيڄاري ۽ تان ته اوھين پرھيزگار ٿيو ۽ ته اوھان تي ٻاجھ ڪئي وڃي.');
INSERT INTO "amroti" VALUES(7,64,'7:64','پوءِ کيس ڪوڙو ڀانيائون پوءِ اُن کي ۽ جيڪي ساڻس ھُيا تن کي ٻيڙيءَ ۾ بچايوسون ۽ اسان جي آيتن کي جن ڪُوڙ ڄاتو تن کي ٻوڙيوسون، ڇوته اُھا انڌي قوم ھُئي.');
INSERT INTO "amroti" VALUES(7,65,'7:65','۽ عاد (قوم) ڏانھن سندن ڀاءُ ھود (پيغمبر ڪري موڪليوسون) چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اُن (الله) کانسواءِ ڪو ٻيو اوھان جو خُدا نه آھي، پوءِ ڇونه ڊڄندا آھيو؟');
INSERT INTO "amroti" VALUES(7,66,'7:66','سندس قوم مان ڪافرن سردارن چيو ته اسين بلاشڪ توکي بي سمجھيءَ ۾ ڏسندا آھيون ۽ اسين بلاشڪ توکي ڪوڙن مان ڀائيندا آھيون.');
INSERT INTO "amroti" VALUES(7,67,'7:67','چيائين ته اي منھنجي قوم مونکي ڪابه بي سمجھي ڪونھي پر آءٌ جھانن جي پالڻھار جو موڪليل آھيان.');
INSERT INTO "amroti" VALUES(7,68,'7:68','اوھان کي پنھنجي پالڻھار جو پيغام پھچايان آءٌ اوھان لاءِ امين نصيحت ڪندڙ آھيان.');
INSERT INTO "amroti" VALUES(7,69,'7:69','(ھِن ڳالھ جو) عجب لڳو ڇا جو اوھان جي پالڻھار کان اوھان مان ھڪ مڙس جي ھٿان سمجھاڻي ھن لاءِ اوھان وٽ آئي ته اوھان کي ڊيڄاري؟ ۽ (اوھين اُھو وقت به) ياد ڪريو جڏھن اوھان کي نُوح جي قوم کان پوءِ (سندن) پيڙھي تي وھاريائين ۽ اوھان جي جُسن ۾ سگھ وڌايائين، تنھنڪري الله جون نعمتون ياد ڪريو ته مانَ اوھين ڇٽو.');
INSERT INTO "amroti" VALUES(7,70,'7:70','چيائون ته (تون) ھن لاءِ اسان وٽ آيو آھين ڇا؟ ته رڳو ھڪ الله جي عبادت ڪريون ۽ اسانجا ابا ڏاڏا جن کي پوڄيندا ھئا تن کي ڇڏي ڏيون؟ جيڪڏھن تون سچو آھين ته جنھن (عذاب) کان اسانکي ڊيڄارين ٿو سو آڻ.');
INSERT INTO "amroti" VALUES(7,71,'7:71','چيائين ته بيشڪ اوھان تي اوھان جي پالڻھار وٽان عذاب ۾ ڏمر لازم ٿي چڪو آھي، اوھين مون ساڻ رڳو (انھن) نالن ۾ ڇو جھيڙو ڪندا آھيو جن تي اوھان ۽ اوھان جي ابن ڏاڏن ڇڙو نالو رکيو آھي جن جي مڃڻ بابت الله ڪو دليل نه لاٿو آھي؟ پوءِ انتظار ڪريو بيشڪ آءٌ (به) اوھان سان گڏ انتظار ڪندڙ آھيان.');
INSERT INTO "amroti" VALUES(7,72,'7:72','پوءِ ان کي ۽ جيڪي ساڻس ھوا تن کي پنھنجي ٻاجھ سان بچايوسين ۽ جن اسان جي آيتن کي ڪوڙ ڄاتو تن جي پاڙ پٽي ڇڏي سون ۽ اُھي مؤمن نه هُئا.');
INSERT INTO "amroti" VALUES(7,73,'7:73','۽ ثمودين ڏانھن سندن ڀاءُ صالِح (پيغمبر ڪري موڪليوسون)، چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اوھان جو الله کانسواءِ ٻيو ڪو خُدا نه آھي، بيشڪ اوھان وٽ اوھان جي پالڻھار کان ھڪ مُعجزو آيو آھي، ھيءَ الله جي ڏاچي اوھان لاءِ نشاني آھي تنھنڪري اُنھيءَ کي ڇڏي ڏيو (ته) الله جي زمين ۾ چَرندي رھي ۽ اُن کي ڪو ايذاءُ نه پُھچائجو نه ته اوھان کي ڏکوئيندڙ عذاب وٺندو.');
INSERT INTO "amroti" VALUES(7,74,'7:74','۽ جڏھن اوھان کي عادين کانپوءِ پيڙھيءَ تي وھاريائين (سو احسان به) ياد ڪريو ۽ اوھان کي مُلڪ ۾ جاءِ ڏنائين (۽) اُن جي پٽن مان ماڙيون بڻائيندا آھيو ۽ ٽڪرن کي ٽُڪي گھر بڻائيندا آھيو، تنھنڪري الله جو نعمتون ياد ڪريو ۽ (اوھين) مُلڪ ۾ فسادي ٿي بگيڙو نه وجھو.');
INSERT INTO "amroti" VALUES(7,75,'7:75','سندس قوم مان وڏائي ڪندڙ سردارن انھن کي چيو جن منجھائن ھيڻن (ماڻھن) ايمان آندو ھو ته اوھين ڄاڻندا آھيو ڇا ته صالِح پنھنجي پالڻھار (جي طرف) کان موڪليل آھي، اُنھن چيو ته جنھن (شريعت) سان اُنکي موڪليو ويو آھي تنھن تي اسين ايمان آڻيندڙ آھيون.');
INSERT INTO "amroti" VALUES(7,76,'7:76','وڏائي ڪندڙن چيو ته جنھن تي اوھان ايمان آندو آھي تنھن جا اسين مُنڪر آھيون.');
INSERT INTO "amroti" VALUES(7,77,'7:77','پوءِ ڏاچيءَ جون کُچون ڪپي ڇڏيائون ۽ پنھنجي پالڻھار جي حُڪم کان بي فرمان ٿيا ۽ (چٿر ڪري) چيائون ته اي صالِح جيڪڏھن پيغمبرن مان آھين ته جنھن (عذاب) جو اسان سان انجام ڪيو اٿيئي سو اسان تي آڻ.');
INSERT INTO "amroti" VALUES(7,78,'7:78','پوءِ زلزلي (جي عذاب) ورتن پوءِ جنھن گھرن ۾ گوڏن ڀر ڪرڻ وارا ٿيا.');
INSERT INTO "amroti" VALUES(7,79,'7:79','پوءِ (صالِح) کانئن مُنھن موڙيو ۽ چيائين ته اي منھنجي قوم بيشڪ اوھان کي پنھنجي پالڻھار جو پيغام پھچايم ۽ اوھان کي نصيحت ڪيم پر اوھين نصيحت ڪندڙن کي دوست نه رکندا آھيو.');
INSERT INTO "amroti" VALUES(7,80,'7:80','۽ لُوط کي (پيغمبر ڪري موڪليوسون) جڏھن پنھنجي قوم کي چيائين ته اوھين اھڙي بي حيائي ڇو ڪندا آھيو جا اوھان کان اڳ ساري جھان (جي ماڻھن) مان ڪنھن ھڪ به نه ڪئي آھي؟');
INSERT INTO "amroti" VALUES(7,81,'7:81','جو اوھين زالن کي ڇڏي مڙسن ڏانھن شھوت سان ويندا آھيو، بلڪ اوھين حد کان لنگھندڙ قوم آھيو.');
INSERT INTO "amroti" VALUES(7,82,'7:82','۽ سندس قوم جي ورندي ھن کانسواءِ نه ھُئي ته اُنھن کي (لُوط سميت) پنھنجن ڳوٺن مان لوڌي ڇڏيو، ڇوته اُھي ماڻھو پاڪ بڻبا آھن.');
INSERT INTO "amroti" VALUES(7,83,'7:83','پوءِ اسان لُوط کي ۽ سندس گھر وارن کي سندس زال کانسواءِ بچايوسون (اُھا) رھيلن مان ھُئي.');
INSERT INTO "amroti" VALUES(7,84,'7:84','۽ مٿن (پھڻن جو) مينھن وسايوسون، پوءِ (اي پيغمبر) ڏس ته ڏوھارين جي پڇاڙي ڪھڙي (خراب) ٿي.');
INSERT INTO "amroti" VALUES(7,85,'7:85','۽ مَدين (وارن) ڏانھن سندن ڀاءُ شعيب کي (پيغمبر ڪري موڪليوسون)، چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اوھان جو الله کان سواءِ ٻيو ڪو خُدا ڪونه آھي، بيشڪ اوھان وٽ اوھان جي پالڻھار کان (چٽيون) نشانيون آيون آھن تنھنڪري ماڻ ۽ تور پوري ڏيو وٺو ۽ ماڻھن کي سنديون شيون گھٽ نه ڏيو ۽ مُلڪ ۾ اُن جي سُڌاري کان پوءِ فساد نه وجھو، جيڪڏھن اوھين مؤمن آھيو ته اِھو اوھان لاءِ ڀلو آھي.');
INSERT INTO "amroti" VALUES(7,86,'7:86','۽ سڀ ڪنھن رستي تي ھن لاءِ نه وھو ته جيڪو الله کي کي مڃي تنھن کي ڌمڪايو ۽ الله جي واٽ کان جھليو ۽ اُن (واٽ) جي ڏنگائي گھرو، ۽ ياد ڪريو جڏھن اوھين ٿورڙا ھيؤ تڏھن اوھان کي وڌايائين، ۽ ڏسو ته فساد وجھندڙن جي پڇاڙي ڪھڙي (بڇڙي) ٿي.');
INSERT INTO "amroti" VALUES(7,87,'7:87','۽ جيڪڏھن اوھان مان ھڪ ٽوليءَ انھن کي مڃيو آھي جنھن سان موڪليو ويو آھيان ۽ ٻي ٽوليءَ نه مڃيو آھي ته (اوھان کي جڳائي ته ايستائين) صبر ڪريو جيسين اسان جي وچ ۾ الله حُڪم ڪري، ۽ اُھو چڱو حُڪم ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(7,88,'7:88','سندس قوم مان ھٺيلن سردارن چيو ته اي شعيب توکي ۽ جن توکي مڃيو آُھي تن کي پنھنجي شھر مان ضرور لوڌيندا سون يا ته اوھين اسان جي پر ۾ موٽي اچو، (شعيب) چيو ته جيتوڻيڪ اسان اوھانجي دين کي بُڇان ڀائيندڙ آھيون (ته به موٽنداسون ڇا؟)');
INSERT INTO "amroti" VALUES(7,89,'7:89','جيڪڏھن اسين اوھان جي پر ۾ اُنھيءَ کانپوءِ جو الله اسان کي کانئس ڇڏايو موٽي اچون ته بيشڪ الله تي ڪُوڙ چيوسون، ۽ اسان جي پالڻھار الله جي گھُر کان سواءِ اسان کي منجھس موٽڻ نه جڳائيندو آھي، اسان جي پالڻھار جي علم سڀني شيئن کي سمايو آھي، اسين الله تي ڀروسو ڪريون ٿا، اي اسانجا پالڻھار اسان جي ۽ اسان جي قوم جي وچ ۾ انصاف سان فيصلو ڪر ۽ تون چڱو فيصلو ڪندڙ آھين.');
INSERT INTO "amroti" VALUES(7,90,'7:90','۽ سندس قوم مان ڪافرن جي ٽوليءَ (پنھنجي يارن کي) چيو ته جيڪڏھن اوھين شعيب جي تابعداري ڪندؤ ته اوھين اُتي جو اُتي ڇيئي وارا ٿيندؤ.');
INSERT INTO "amroti" VALUES(7,91,'7:91','پوءِ اُنھن کي زلزلي (جي عذاب) ورتو پوءِ پنھنجي جاين ۾ گوڏن ڀر ڪري مري ويا.');
INSERT INTO "amroti" VALUES(7,92,'7:92','جن شعيب کي ڪوڙو ڀانيو سي ڄڻڪ اُنھن (ڳوٺن) ۾ رھيا ئي نه ھوا، جن شعيب کي ڪوڙو ڀانيو سي ڇيئي وارا ھوا.');
INSERT INTO "amroti" VALUES(7,93,'7:93','پوءِ کائن مُنھن موڙيائين ۽ چيائين ته اي منھنجي قوم بيشڪ اوھان کي پنھنجي پالڻھار جا پيغام پھچايا اٿم ۽ اوھانجي خيرخواھي ڪيم، تنھنڪري (ھاڻي) ڪافر قوم تي ڇالاءِ ڏک ڪريان.');
INSERT INTO "amroti" VALUES(7,94,'7:94','۽ ڪنھن ڳوٺ ۾ پيغمبرن مان ڪوبه نه موڪليوسون پر اُتي جي رھاڪن کي (پيغمبرن جي نافرماني سبب) سختي ۽ مصيبت ۾ پڪڙيندا رھياسون ته مانَ اُھي عاجزي ڪن.');
INSERT INTO "amroti" VALUES(7,95,'7:95','وري (ايستائين) ڏک جي بجاءِ سُک مٽائي ڏنوسون جيستائين آسودا ٿيا ۽ چيائون ته بيشڪ اسان جن ابن ڏاڏن کي (ايئن) ڏک سُک پھتو ھو پوءِ اوچتو ئي اُنھن کي سندن بيخبريءَ جي حالت ۾ پڪڙيوسون.');
INSERT INTO "amroti" VALUES(7,96,'7:96','۽ جيڪڏھن اُنھن ڳوٺن وارا ايمان آڻين ھا ۽ پرھيزگار بڻجن ھا ته مٿن آسمانن ۽ زمين کان برڪتون ضرور کوليون ھا پر ڪوڙ ڀانيائون تنھنڪري انھن جيڪي ڪيو تنھن سببان کين پڪڙيوسون.');
INSERT INTO "amroti" VALUES(7,97,'7:97','ڳوٺن وارا جنھن حال ۾ سمھيل ھجن تنھن مھل راتوواہ پاڻ تي اسانجي عذاب پھچڻ کان ڇو بي پرواہ ٿيا آھن؟');
INSERT INTO "amroti" VALUES(7,98,'7:98','يا ڳوٺن وارا ڇو بي ڀوا ٿيا آھن؟ جو اسان جو عذاب ڏينھن چڙھي مھل وٽن اچي ۽ اُھي راند ۾ مشغول ھجن؟');
INSERT INTO "amroti" VALUES(7,99,'7:99','الله جي رٿ کان ڇو بي ڀوا ٿيا آھن؟ جو ڇيئي واريءَ قوم کانسواءِ (ٻيو) ڪو الله جي رٿ کان بي ڀئو نه ٿيندو آھي.');
INSERT INTO "amroti" VALUES(7,100,'7:100','جيڪي مُلڪ جي رھندڙن کانپوءِ وارث ٿين ٿا تن کي پڌرو نه ٿيو آھي ڇا ته جيڪڏھن گھرون ھا ته کين سندن گناھن سببان سزا ڏيون ھا، ۽ سندين دلين تي مُھر ھئين ان کان جنھنڪري اُھي نٿا ٻُڌن.');
INSERT INTO "amroti" VALUES(7,101,'7:101','(اي پيغمبر) اُھي ڳوٺ آھن جن جون خبرون توکي بيان ڪريون ٿا، ۽ بيشڪ وٽن سندن پيغمبر پڌرن معجزن سان آيا، پوءِ اُنھيءَ سببان جو اڳي ڪُوڙ ڀانيائون اُھي ڪڏھن به نه مڃيندا ھئا، اھڙي طرح الله ڪافرن جي دلين تي مُھر ھڻندو آھي.');
INSERT INTO "amroti" VALUES(7,102,'7:102','۽ منجھائن گھڻن کي انجام پاڙيندڙ نه ڏٺوسون، ۽ منجھائن گھڻن کي بي دين ڏٺوسون.');
INSERT INTO "amroti" VALUES(7,103,'7:103','وري کائن پوءِ مُوسىٰ کي فرعون ۽ سندس سردارن ڏانھن پنھنجن مُعجزن سان پيغمبر ڪري موڪليوسون پوءِ اُنھن (نه مڃڻ سببان) آيتن تي ظلم ڪيو، (اي پيغمبر) ڏس ته فساد ڪندڙن جي پڇاڙي ڪھڙي طرح ٿي.');
INSERT INTO "amroti" VALUES(7,104,'7:104','۽ موسىٰ (فرعون کي) چيو ته اي فرعون آءٌ جھانن جي پالڻھار جو پيغمبر آھيان.');
INSERT INTO "amroti" VALUES(7,105,'7:105','ھن (ڳالھ) تي قائم آھيان ته الله تي سچ کانسواءِ ڪي ڪين چوان، بيشڪ اوھان وٽ اوھان جي پالڻھار کان چٽي مُعجزي سان آيو آھيان تنھنڪري بني اسرائيلن کي مون سان (ھلڻ جي) موڪل ڏي.');
INSERT INTO "amroti" VALUES(7,106,'7:106','(فرعون) چيو ته جيڪڏھن ڪو معجزو آندو اٿئي ته جيڪڏھن سچن مان آھين ته اُھو آڻ.');
INSERT INTO "amroti" VALUES(7,107,'7:107','پوءِ پنھنجي لٺ اُڇليائين ته اُڇلڻ سان ئي اُھو پڌرو واسينگ (نانگ) ٿي پيئي.');
INSERT INTO "amroti" VALUES(7,108,'7:108','۽ پنھنجو ھٿ ٻاھر ڪڍيائين ته اُھو ڪڍڻ سان ئي ڏسندڙن لاءِ روشن ٿيو.');
INSERT INTO "amroti" VALUES(7,109,'7:109','فرعون جي قوم مان سردار ماڻھو چوڻ لڳا ته ھيءُ پڪو داناءُ جادوگر آھي.');
INSERT INTO "amroti" VALUES(7,110,'7:110','اوھان کي اوھان جي مُلڪ مان ڪڍي ڇڏڻ گھري ٿو، تنھنڪري اوھين ڇا ٿا صلاح ڏيو؟');
INSERT INTO "amroti" VALUES(7,111,'7:111','(فرعون کي) چيائون ته کيس ۽ ڀاڻس کي ترساءِ ۽ ڪوٺيندڙ شھرن ڏانھن موڪل.');
INSERT INTO "amroti" VALUES(7,112,'7:112','ته تو وٽ سڀئي داناء جادوگر آڻين.');
INSERT INTO "amroti" VALUES(7,113,'7:113','۽ فرعون وٽ جادوگر آيا چوڻ لڳا ته جيڪڏھن اسين غالب ٿيون ته اسان لاءِ ضرور (ڪو) انعام (ھئڻ) گھرجي.');
INSERT INTO "amroti" VALUES(7,114,'7:114','فرعون چيو ته ھائو (انعام به ڏبو) ۽ بيشڪ مرتبي وارن مان (به) ٿيندؤ.');
INSERT INTO "amroti" VALUES(7,115,'7:115','چيائون ته اي موسىٰ توکي (جادوءَ جي شيء) اڇلڻ گھرجي يا (پھرين) اسين اڇليندڙ ٿيون.');
INSERT INTO "amroti" VALUES(7,116,'7:116','(موسى) چيو ته (اوھين) اُڇليو، پوءِ جنھن مھل اُڇليائون (ته) ماڻھن جون اکيون جادوءَ سان بند ڪيائون ۽ انھن کي ڊيڄاريائون ۽ وڏا جادو آندائون.');
INSERT INTO "amroti" VALUES(7,117,'7:117','۽ مُوسىٰ ڏانھن وحي موڪليوسون ته پنھنجي لٺ اُڇل، پوءِ جيڪو جادو ڪيائون تنھنکي اُھا اُنھيءَ مھل ئي ڳھندا وئي.');
INSERT INTO "amroti" VALUES(7,118,'7:118','پوءِ سچ پڌرو ٿيو ۽ جيڪي ڪندا رھيا سو ڪوڙو ٿيو.');
INSERT INTO "amroti" VALUES(7,119,'7:119','پوءِ (فرعون وارا) اُن ھنڌ مغلوب ٿيا ۽ خوار ٿي موٽيا.');
INSERT INTO "amroti" VALUES(7,120,'7:120','۽ جادوگر سجدي ۾ ڪري پيا.');
INSERT INTO "amroti" VALUES(7,121,'7:121','چيائون ته جھانن جي پالڻھار تي ايمان آندوسون.');
INSERT INTO "amroti" VALUES(7,122,'7:122','(جو) مُوسىٰ ۽ ھارون جو پالڻھار (آھي).');
INSERT INTO "amroti" VALUES(7,123,'7:123','فرعون چيو ته اوھان منھنجي موڪل کان اڳي ئي اُن تي ايمان ڇو آندو آھي؟ ھيء (اوھان جو) فريب آھي (جو پاڻ ۾ گڏجي) ھن ڪري (ھن) شھر ۾ ڪيو اٿو ته سندس رھاڪن کي منجھانئس ڪڍي ڇڏيو، پوءِ سگھو کي ڄاڻندو.');
INSERT INTO "amroti" VALUES(7,124,'7:124','اوھان جا ھٿ ءِ اوھان جا پير اُبتڙ سبتڙ ضرور وڍيندس وري اوھان مڙني کي ضرور سوريءَ چاڙھيندس.');
INSERT INTO "amroti" VALUES(7,125,'7:125','چيائون ته بيشڪ اسان پنھنجي پالڻھار ڏانھن ضرور موٽندڙ آھيون.');
INSERT INTO "amroti" VALUES(7,126,'7:126','۽ تون رڳو ھن ڪري اسان سان وير ٿو رکين ته اسان جي پالڻھار جا حُڪم جڏھن اسان وٽ آيا (تڏھن) اسان مڃيا آھن، اي اسان جا پالڻھار اسان تي صبر پلٽ ۽ اسان کي مسلمان ڪري مار.');
INSERT INTO "amroti" VALUES(7,127,'7:127','۽ فرعون جي قوم جي سردارن (کيس) چيو ته موسىٰ ۽ سندس قوم کي ھن لاءِ ڇڏي ڏئين ٿو ڇا؟ ته مُلڪ ۾ فساد وجھن ۽ توکي ۽ تنھنجي معبودن (جي عبادت) کي ڇڏي ڏين؟ چيائين ته سندن پٽ سگھوئي ڪھنداسون ۽ سندين ڌيئرون ڇڏي ڏينداسون، ۽ اسين سندن مٿان بيشڪ زور وارا آھيون.');
INSERT INTO "amroti" VALUES(7,128,'7:128','مُوسىٰ پنھنجي قوم کي (دلگير ڏسي) چيو ته الله کان مدد گھرو ۽ صبر ڪريو، ڇوته ساري زمين الله جي آھي، پنھنجي ٻانھن مان جنھن کي گھرندو آھي تنھنکي اُن جو وارث بڻائيندو آھي، ۽ (چڱي) عاقبت پرھيزگارن لاءِ آھي.');
INSERT INTO "amroti" VALUES(7,129,'7:129','چيائون ته اسين تنھنجي اچڻ کان اڳ (به) ۽ تنھنجي اچڻ کان پوءِ (به) ايذايا ويا آھيون، (مُوسى) چين ته اُميد آھي ته اوھانجو پالڻھار اوھان جي دشمن کي ناس ڪري ڇڏيندو ۽ مُلڪ ۾ اوھان کي پيڙھيءَ تي وھاريندو پوءِ ڏسندؤ ته (اوھين) ڪھڙي طرح عمل ٿا ڪريو.');
INSERT INTO "amroti" VALUES(7,130,'7:130','۽ بيشڪ فرعون جي قوم کي ڏڪار ۽ ميون جي گھٽتائيءَ ۾ گرفتار ڪيوسون ته مانَ اُھي نصيحت وٺن.');
INSERT INTO "amroti" VALUES(7,131,'7:131','پوءِ جڏھن اُنھن کي سُک پھتو ٿي ته چيائون ٿي ته ھيء اسان جو (لائق) آھي، ۽ جيڪڏھن اُنھن کي ڪو ڏک پھچندو ھو ته موسىٰ ۽ سندس سنگت کي نڀاڳو ڀائيندا ھوا، خبردار الله وٽ سندين ئي نڀاڳائي آھي پر منجھائن گھڻا نه ٿا ڄاڻن.');
INSERT INTO "amroti" VALUES(7,132,'7:132','۽ چيائون ته (اي موسى) جڏھن ڪڏھن ڪابه نشاني اسان وٽ ھن لاءِ آڻيندين ته اُن سان اسان تي جادو ڪرين ته اسين توکي مڃڻ وارا نه آھيون.');
INSERT INTO "amroti" VALUES(7,133,'7:133','پوءِ مٿن طوفان ۽ ماڪڙ ۽ جونء ۽ ڏيڏر ۽ رت کليل مُعجزا موڪلياسون، پوءِ وڏائي ڪيائون ۽ (اُھا) ڏوھاري قوم ھئي.');
INSERT INTO "amroti" VALUES(7,134,'7:134','۽ جڏھن مٿن سزا پھتي ته (تڏھن) چيائون ٿي ته اي موسىٰ پنھنجي پالڻھار کان انھيءَ وعدي سان جو توسان ڪيو اٿس اسان لاءِ دُعا گھُر، ته جيڪڏھن اسان تان عذاب لاٿئي ته توتي ضرور ايمان آڻينداسون ۽ بني اسرائيلن کي (به) ضرور توسان ھلڻ جي موڪل ڏينداسون.');
INSERT INTO "amroti" VALUES(7,135,'7:135','پوءِ جنھن مھل عذاب جيڪو کين ٺھرايل مدت توڻي پھچڻو ھو سو کانئن لاٿوسون ٿي (ته) اُتي جو اُتي انھن اَنجام ڀڳو ٿي.');
INSERT INTO "amroti" VALUES(7,136,'7:136','پوءِ اسان کانئن پورو وير ورتو جو انھن کي سمنڊ ۾ انھيءَ ڪري ٻوڙيوسون جو اُنھن اسان جي آيتن کي ڪوڙ ڄاتو ۽ اُن کان بي خبر ھوا.');
INSERT INTO "amroti" VALUES(7,137,'7:137','۽ اُھا قوم جيڪا ھيڻي ڀانئبي ھئي تنھن کي اُنھيءَ اوڀر ۽ اولھ جي زمين (يعني شام ۽ مِصر) جو وارث ڪيوسون جنھن ۾ برڪت رکي ھئي سون، ۽ تنھنجي پالڻھار جو بني اسرائيلن سان چڱو انجام انھي سببان پورو ٿيو جو صَبر ڪيائون، ۽ فرعون ۽ سندس قوم جيڪي بڻايو ۽ جيڪا (اڏاوت) بلند ڪئي ھوائون سا (سڀ) ناس ڪري ڇڏي سون.');
INSERT INTO "amroti" VALUES(7,138,'7:138','۽ بني اسرائيل کي سمنڊ کان پار لنگھايوسون پوءِ اُھي ھڪڙي اھڙي قوم تي پُھتا جو پنھنجي بتن جا مجاور ھوا، (موسى) کي چيائون ته اي موسىٰ اسان لاءِ (به) ھڪڙو اھڙو خُدا بڻاءِ جھڙا ھنن لاءِ خُدا آھن، (موسى) چيو ته بيشڪ اوھين جاھل قوم آھيو.');
INSERT INTO "amroti" VALUES(7,139,'7:139','ھيء ماڻھو پاڻ جنھن (مذھب) ۾ پيل آھن سو رد آھي ۽ جيڪي ڪندا رھندا آھن سو غلط آھي.');
INSERT INTO "amroti" VALUES(7,140,'7:140','مُوسىٰ چيو ته الله کانسواءِ اوھان لاءِ ٻيو معبود ڇو گھران؟ جنھن اوھان کي جھانن جي ماڻھن کان ڀلو ڪيو آھي.');
INSERT INTO "amroti" VALUES(7,141,'7:141','۽ (ياد ڪر) جڏھن اوھان کي فرعون جي ماڻھن کان ڇڏايوسين جن اوھان کي بڇڙو عذاب چکايو ٿي، جو اوھان جا پٽ ڪُھندا ھوا ۽ اوھان جو ڌيئر جيئريون ڇڏيندا ھوا، ۽ اُن ۾ اوھان جي پالڻھار کان اوھان لاءِ وڏي پرک ھُئي.');
INSERT INTO "amroti" VALUES(7,142,'7:142','۽ مُوسىٰ کي ٽيھن راتين جو انجام ڏنوسون ۽ انھي کي (ٻـين) ڏھن سان پورو ڪيوسون پوءِ سندس پالڻھار جو انجام چاليھن راتين جو پورو ٿيو، ۽ مُوسىٰ پنھنجي ڀاءُ ھارون کي چيو ته منھنجي قوم ۽ منھنجي پوئتان خليفو ٿي ۽ سڌارو ڪج ۽ فسادين جي واٽ تي نه ھلج.');
INSERT INTO "amroti" VALUES(7,143,'7:143','۽ جنھن مھل مُوسىٰ اسان جي انجام واري ھنڌ آيو ۽ سندس پالڻھار ساڻس ڳالھايو (تنھن مھل) چيائين ته اي منھنجا پالڻھار مونکي پاڻ ڏيکار ته آءٌ توکي ڏسان، (الله) چيو ته مونکي ڪڏھن نه ڏسي سگھندين پر جبل ڏانھن نھار پوءِ جيڪڏھن پنھنجي جاءِ ٽڪاءُ ڪيو ته تون مونکي ڏسي سگھندين، پوءِ جنھن مھل سندس پالڻھار جبل ڏانھن تجلّو ڪيو (ته) اُن کي ٽڪر ٽڪر ڪري ڇڏيائين ۽ موسىٰ بيھوش ٿي ڪري پيو، پوءِ جنھن مھل ھوش ۾ آيو (ته) چوڻ لڳو ته توکي پاڪائي جڳائي تنھنجي درگاہ ۾ توبه ڪيم ۽ آءٌ (سڀ کان) پھريون مؤمن آھيان.');
INSERT INTO "amroti" VALUES(7,144,'7:144','(الله) فرمايو ته اي موسىٰ مون توکي پنھنجي پيغمبريءَ ۽ پنھنجي ڳالھائڻ سان ماڻھن کان ڀلارو ڪيو آھي، جيڪو (توريت) توکي ڏنو اٿم سو وٺ ۽ شڪر ڪندڙن مان ھُج.');
INSERT INTO "amroti" VALUES(7,145,'7:145','۽ اُن (مُوسى) لاءِ سڀڪنھن شيء جي نصيحت واسطي ۽ سڀڪنھن شيء جي ڌار ڌار بيان واسطي (توريت) تختن ۾ لکي ڏنوسون، (۽ چيو ويس ته) اُن کي پختائيءَ سان وٺ ۽ پنھنجي قوم کي حڪم ڪر ته اُن جون چڱيون ڳالھيون (عمل ۾) وٺندا رھن، اوھان کي بدڪارن جي جاءِ سگھو ڏيکاريندس.');
INSERT INTO "amroti" VALUES(7,146,'7:146','جيڪي زمين ۾ ناحق وڏائي ڪندا رھندا آھن تن کي سگھو پنھنجي آيتن (جي سمجھ) کان جھليندس، ۽ جيڪڏھن سڀ نشانيون ڏسندا ته (به) انھن تي ايمان نه آڻيندا، ۽ جيڪڏھن ھدايت جي واٽ ڏسندا ته ان کي واٽ نه بڻائيندا، ۽ جيڪڏھن گمراھي جي واٽ ڏسندا ته ان کي واٽ بڻائيندا، اِھو ھن سببان آھي جو اسان جي آيتن کي ڪوڙ ڀانيائون ۽ انھن کان غافل رھيا ھوا.');
INSERT INTO "amroti" VALUES(7,147,'7:147','۽ جن اسان جي آيتن کي ۽ آخرت جي ملڻ کي ڪوڙ ڄاتو تن جا اَعمال ناس ٿيا، جيڪي ڪمائيندا آھن تنھن کانسواءِ ٻيو ڪو بدلو نه ڏبن.');
INSERT INTO "amroti" VALUES(7,148,'7:148','۽ مُوسىٰ جي قوم کانئس پوءِ انھن (قبطين) جي زيورن مان گابي جو بوتو اھڙو ٺاھي ورتو جنھن کي رنڀ ھئي، نه ٿي ڏٺائون ڇا ته اُھو ساڻن نه ڳالھائيندو ھو ۽ نڪي انھن کي واٽ ڏيکاريندو ھو؟ اُن کي (خدا ڪري) ورتائون ۽ ظالم ھوا.');
INSERT INTO "amroti" VALUES(7,149,'7:149','۽ جڏھن پشيمان ٿيا ۽ سمجھيائون ته اُھي پڪ گمراہ ٿيا (ته) چيائون ته جيڪڏھن اسان جو پالڻھار اسان تي ٻاجھ نه ڪندو ۽ اسان کي نه بخشيندو ته ضرور ڇيئي وارن مان ٿينداسون.');
INSERT INTO "amroti" VALUES(7,150,'7:150','۽ جڏھن مُوسىٰ پنھنجي قوم ڏانھن ڏمريل افسوس ڪندڙ ٿي موٽيو (تڏھن) چيائين ته جيڪي منھنجي پرپٺ مون کانپوءِ ڪيو سو بڇڙو آھي، پنھنجي پالڻھار جي حُڪم کان (اڳ) ڇو تڪڙ ڪيوَ؟ ۽ ڦرھيون اُڇليائين ۽ پنھنجي ڀاءُ جي مٿي (جي وارن) کان وٺي کيس پاڻ ڏانھن ڇڪڻ لڳو، (ھارون) چيو ته اي مُنھنجا ڀاءُ بيشڪ (ھن) قوم مون کي ھيڻو ڀانيو ۽ مُنھنجي مارڻ تي سنبريا، تنھنڪري مون تي دشمن نه کلاءِ ۽ نڪي مون کي ظالم قوم سان گڏ.');
INSERT INTO "amroti" VALUES(7,151,'7:151','(موسى) چيو اي منھنجا پالڻھار مون کي ۽ مُنھنجي ڀاءُ کي بخش ۽ اسان کي پنھنجي ٻاجھ ۾ داخل ڪر، ۽ تون (سڀني) ٻاجھ وارن کان ٻاجھ وارو آھين.');
INSERT INTO "amroti" VALUES(7,152,'7:152','جن گابي کي (خُدا ڪري) ورتو تن کي سندن پالڻھار وٽان ڏمر ۽ دُنيا جي حياتيءَ ۾ خواري سگھو پھچندي، ۽ اھڙيءَ طرح ٺاھ ٺاھيندڙن کي سزا ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(7,153,'7:153','۽ (اي پيغمبر) جن بڇڙا ڪم ڪيا وري اُن کان پوءِ توبه ڪيائون ۽ ايمان آندائون، (تن کي) تنھنجو پالڻھار اُن (توبه) کان پوءِ بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(7,154,'7:154','۽ جڏھن مُوسىٰ کان ڏمر لٿو تڏھن (توريت جون) ڦرھيون ورتائين، ان جي لکيي ۾ اُنھن لاءِ ھدايت ۽ ٻاجھ ھُئي جيڪي پنھنجي پالڻھار کان ڊڄندا آھن.');
INSERT INTO "amroti" VALUES(7,155,'7:155','۽ مُوسىٰ پنھنجي قوم مان ستر ڄڻا اسان جي انجام جي وقت لاءِ چونڊيا، پوءِ جنھن مھل انھن کي زلزلي ورتو (ته موسى) چيو ته اي منھنجا پالڻھار جيڪڏھن (تون) گھرين ھا ته ھن کان اڳ انھن کي ۽ خاص مونکي ھلاڪ ڪري ڇڏين ھا، اسان منجھان بي عقلن جيڪي ڪيو تنھن سببان اسانکي ڇو ٿو ھلاڪ ڪرين؟ اِھا تنھنجي پرک (حڪمت) کانسواءِ نه آھي، جنھن کي گھرين تنھن کي ان سان گمراھ ڪندو آھين ۽ جنھن کي گھرندو آھين ھدايت ڪندو آھين، تون اسان جو سنڀاليندڙ آھين تنھنڪري اسانکي بخش ۽ اسان تي ٻاجھ ڪر ۽ تون (سڀ کان) چڱو بخشيندڙ آھين.');
INSERT INTO "amroti" VALUES(7,156,'7:156','۽ اسان لاءِ ھن دُنيا ۾ ۽ آخرت ۾ چڱائي لک ڇوته اسين توڏانھن رُجوع ٿيا آھيون، (الله) فرمايو ته آءٌ پنھنجو عذاب جنھن لاءِ وڻندو اٿم تنھنکي پھچائيندو آھيان، ۽ منھنجي ٻاجھ سڀڪنھن شيء کي شامل آھي، ۽ پوءِ آءٌ پنھنجي ٻاجھ انھن لاءِ لکندس جيڪي پرھيزگاري ڪندا آھن ۽ زڪواة ڏيندا آھن ۽ جيڪي منھنجي آيتن تي ايمان آڻيندا آھن.');
INSERT INTO "amroti" VALUES(7,157,'7:157','(۽ پڻ آخرت جي رحمت اُنھن لاءِ لکندس) جيڪي انھيء نبّي اُمّي پيغمبر جي تابعداري ڪندا آھن جنھن جون وصفون توريت ۽ انجيل ۾ پاڻ وٽ لکيلون لھندا آھن، (اُھو) اُنھن کي چڱن ڪمن جو حُڪم ڪندو آھي ۽ مندن ڪمن کان اُنھن کي جھليندو آھي ۽ سٺيون شيون اُنھن لاءِ حلال ڪندو آھي ۽ پليت شيون مٿن حرام ڪندو آھي ۽ سندن ڳرو بار ۽ جيڪي تڪليفون مٿن ھيون سي کانئن لاھيندو آھي، پوءِ جن اُن تي ايمان آندو ۽ سندس تعظيم ڪئي ۽ کيس مدد ڏنائون ۽ اُنھيءَ نُور (ڪلام الله) جي تابعداري ڪيائون جيڪو ساڻس لاٿو ويو سي ئي ڇٽل آھن.');
INSERT INTO "amroti" VALUES(7,158,'7:158','(اي پيغمبر) چؤ ته اي انسانؤ آءٌ اوھان مڙني ڏانھن اُن الله جو پيغمبر (ٿي آيو) آھيان جنھن جي آسمانن ۽ زمين ۾ بادشاھي آھي، اُن الله کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي اُھو جياريندو آھي ۽ ماريندو آھي، تنھنڪري الله ۽ سندس انھي اُمّي نبيء تي ايمان آڻيو جيڪو الله ۽ سندس حُڪمن کي مڃيندو آھي ۽ اُن جي تابعداري ڪريو ته مانَ اوھين سڌي واٽ لھو.');
INSERT INTO "amroti" VALUES(7,159,'7:159','۽ مُوسىٰ جي قوم مان ھڪڙي اھڙي ٽولي آھي جو (ماڻھن کي) سڌو رستو ڏيکاريندي آھي ۽ اُھي ان سان انصاف تي ھلندا آھن.');
INSERT INTO "amroti" VALUES(7,160,'7:160','۽ بني اسرائيلن مان ٻارھن پاڙا ٽوليون ڪري جدا ڪياسون، جڏھن موسىٰ کان سندس قوم پاڻي گھريو تڏھن ڏانھس وحي موڪليوسون ته پنھنجي لٺ پھڻ کي ھڻ، پوءِ (ھنيائين ته) اُن مان ٻارھن واھيون وھيون، بيشڪ سڀڪنھن ماڻھوءَ پنھنجي پاڻي ڀرڻ جي جاءِ ڄاتي ٿي، ۽ اسان مٿن ڪڪر جي ڇانو ڪئي ۽ مٿن مَن ۽ سَلۡوىٰ لاٿاسون، (۽ چيوسون ته) جيڪا اوھان جي سٺي روزي بڻائي اٿون تنھن مان کائو، ۽ اسان جو ڪُجھ نه وڃائون پر پاڻ کي نقصان ٿي لاٿائون.');
INSERT INTO "amroti" VALUES(7,161,'7:161','۽ جڏھن انھن کي چيو ٿي ويو ته ھن ڳوٺ ۾ وڃي ٽڪو ۽ منجھانئس جتان وڻيو تتان کائو ۽ چؤ ته بخش (ڪر) ۽ دروازي کان سجدو ڪندڙ ٿي لنگھو ته اوھان کي اوھان جون خطائون بخشيون، ڀلارن کي وڌيڪ (چڱائي) ڏينداسون.');
INSERT INTO "amroti" VALUES(7,162,'7:162','پوءِ انھن (بني اسرائيلن) مان جيڪي ظالم ھئا تن کي جيڪا ڳالھ چئي وئي سا انھن مٽائي ٻي ڳالھ چئي پوءِ مٿن آسمان کان انھيءَ سببان سزا موڪلي سون جو ظلم ٿي ڪيائون.');
INSERT INTO "amroti" VALUES(7,163,'7:163','۽ (اي پيغمبر) کائن اُن ڳوٺ (جي حال) بابت پڇا ڪر جيڪو درياءَ جي ڪناري تي ھو، جڏھن (اُھي منع ھوندي) ڇنڇر (جي حُڪم) ۾ حُڪم عدولي ڪرڻ لڳا جڏھن انھن جون مَڇيون سندن ڇنڇر ڏينھن (پاڻي جي مٿان) سندن سامھون پڌريون ٿي اينديون ھيون ۽ جنھن ڏينھن ڇنڇر نه ڪندا ھئا تنھن ڏينھن (ٻاھر نه) اينديون ھيون، اھڙي طرح اُنھن کي انھي سببان پرکيوسون جو نافرماني ٿي ڪيائون.');
INSERT INTO "amroti" VALUES(7,164,'7:164','۽ جڏھن منجھائن ھڪ ٽوليء (نصيحت ڪندڙن کي) چيو ته اھڙي قوم کي اوھين ڇو نصيحت ڪندا آھيو جن کي الله ھلاڪ ڪندڙ آھي يا کين سخت عذاب ڪندڙ آھي؟ (تڏھن) چيائون ته (اسان جي نصيحت) اوھانجي پالڻھار وٽ عذر آھي ۽ مانَ اُھي ڊڄن.');
INSERT INTO "amroti" VALUES(7,165,'7:165','پوءِ جيڪا (نصيحت) ڏني وئي ھُين سا جنھن مھل وساريائون اُنھي مھل اُنھن ماڻھن کي بچايوسون جيڪي مدائي کان جھليندا ھُين ۽ ظالمن کي سخت عذاب سان انھيء ڪري پڪڙيوسون جو ھو بدڪار ھوا.');
INSERT INTO "amroti" VALUES(7,166,'7:166','پوءِ جنھن کان جھليو ويو ھُين تنھن کان جڏھن حد کان لنگھي ويا ته چيوسون ته خوار ٿيل ڀولڙا ٿيو.');
INSERT INTO "amroti" VALUES(7,167,'7:167','۽ (ياد ڪر) جڏھن تنھنجي پالڻھار (يھودين کي) سُڌ ڏني ته (اُھو) مٿن قيامت تائين (اھڙو حاڪم) ضرور مقرر ڪندو جيڪو انھن کي بڇڙي سزا چکائيندو رھندو، ڇوته تنھنجو پالڻھار سَگھو سزا ڏيندڙ آھي، ۽ اُھو بخشڻھار مھربان (به) آھي.');
INSERT INTO "amroti" VALUES(7,168,'7:168','۽ اُنھن (بني اسرائيلن) کي ملڪ ۾ ٽوليون ٽوليون ڪري ڇڙو ڇڙ ڪري ڇڏيوسون، منجھائن ڪي سُڌريل ۽ ڪي اَڻ سڌريل آھن، ۽ اُنھن کي سکن ۽ ڏکن سان پرکيوسون ته مانَ اُھي موٽن.');
INSERT INTO "amroti" VALUES(7,169,'7:169','وري کائن پوءِ اھڙا بُڇڙا اُنھن جي جاڳھن تي ويٺا جو ڪتاب (توريت) جا (ڇڙا) وارث بڻيا ھن دنيا جو سامان اختيار ڪندا آھن ۽ چوندا آھن ته بخشبا سون، ۽ جيڪڏھن وٽن ڪو اھڙو سامان ايندو آھي ته (اُھي پشيمان نه ٿيندا آھن ۽) اُھو وٺندا رھندا آھن، کائن ڪتاب (توريت) ۾ ھي انجام نه ورتو ويو ھو ڇا ته سچ کانسواءِ الله تي ڪي به ڪين چون؟ ۽ جيڪي منجھس آھي سو پڙھيو به اٿن، ۽ آخرت جو مُلڪ اُنھن لاءِ ڀلو آھي جيڪي پرھيزگار آھن، پوءِ ڇونه سمجھندا آھيو؟');
INSERT INTO "amroti" VALUES(7,170,'7:170','۽ جيڪي ڪتاب کي مُحڪم وٺن ٿا ۽ نماز پڙھن ٿا، (اھڙن) نيڪن جو اجر اسين نه وڃائيندا آھيون.');
INSERT INTO "amroti" VALUES(7,171,'7:171','۽ (ياد ڪر) جڏھن انھن جي مٿان جبل کي ٽنگيوسون ڄڻ ته اُھو ڇٽي ھو ۽ ڀانيائون ته اُھو مٿن ڪرندو، (کين چيوسون ته) جيڪي اسان اوھان کي ڏنو آھي سو مُحڪم ڪري وٺو ۽ جيڪي منجھس آھي سو ياد ڪندا رھو ته مانَ اوھين (عذاب کان) بچو.');
INSERT INTO "amroti" VALUES(7,172,'7:172','۽ (اي پيغمبر ياد ڪر) جڏھن تنھنجي پالڻھار آدم جي اولاد مان (يعني) پٺين مان سندن اولاد کي پيدا ڪيو ۽ سندن وجود کي مٿن شاھد ڪيائين، (۽ پڇيائين) ته (آءٌ) اوھانجو پالڻھار نه آھيان ڇا؟ چيائون ته ھائو (آھين)، گواہ ٿيا آھيون، (اھا گواھي ھن لاءِ وٺڻي ھئي) ته متان قيامت جي ڏينھن چئو ته اسين ھن (ڳالھ) کان بي خبر ھئاسون.');
INSERT INTO "amroti" VALUES(7,173,'7:173','يا متان چئو ته اڳئي شرڪ اسان جي ابن ڏاڏن ڪيو ھو ۽ اسين کائن پوءِ اولاد ھئاسون، تنھنڪري جيڪي بيھودن ڪيو تنھن سببان اسان کي ڇو ٿو ھلاڪ ڪرين؟');
INSERT INTO "amroti" VALUES(7,174,'7:174','۽ اھڙي طرح اسين آيتون کليل طرح بيان ڪندا آھيون ته مانَ اُھي موٽن.');
INSERT INTO "amroti" VALUES(7,175,'7:175','۽ (اي پيغمبر) تون اُن (ماڻھو) جو قصو کين پڙھي ٻڌاءِ جنھن کي پنھنجين آيتن جو علم ڏنوھوسون (اُھو) اُن (آيتن جي علم) مان نڪتو پوءِ شيطان سندس پُٺيان لڳو پوءِ (اُھو) گمراھن مان ٿيو.');
INSERT INTO "amroti" VALUES(7,176,'7:176','۽ جيڪڏھن گھرون ھا ته کيس انھن (آيتن) سان ضرور مٿاھون ڪريون ھا پر ھُن (ھيٺ) زمين ڏانھن لاڙو ڪيو ۽ پنھنجن سڌن پٺيان لڳو، پوءِ سندس مثال ڪُتي جي مثال وانگر ٿيو، (جو) جيڪڏھن (تون) مٿس (ڪا شيء) اولارين ته سھڪي يا جي ڇڏي ڏئين ته (به) سھڪي، اھو انھي قوم جو مثال آھي جن اسان جي آيتن کي ڪُوڙ ڄاتو، پوءِ (اي پيغمبر انھن کي) اِھا نصيحت پڙھي ٻڌاءِ ته مانَ اُھي سوچ ڪن.');
INSERT INTO "amroti" VALUES(7,177,'7:177','جن ماڻھن اسان جي آيتن کي ڪوڙ ڄاتو تن جو حال بڇڙو آھي ۽ اُنھن پاڻ تي ئي ظلم ڪيو.');
INSERT INTO "amroti" VALUES(7,178,'7:178','جنھن کي الله ھدايت ڪئي سو ھدايت وارو آھي، ۽ جنھن کي ڀلايائين سي ئي ڇيئي وارا آھن.');
INSERT INTO "amroti" VALUES(7,179,'7:179','۽ بيشڪ دوزخ لاءِ جنن ۽ ماڻھن مان گھڻا پيدا ڪيا اٿون، انھن جو اھڙيون دليون آھن جو انھن سان (الله جو حُڪم) نه سمجھندا آھن، ۽ اُنھن جون اھڙيون اکيون آھن جو اُنھن سان (حقانيت) ڏسي نه سگھندا آھن، ۽ اُنھن جا اھڙا ڪن آھن جو انھن سان (حق کي) ٻڌي نه سگھندا آھن، اھي ڍورن وانگر آھن بلڪ اُنھن کان وڌيڪ گمراہ آھن، اُھي ئي غافل آھن.');
INSERT INTO "amroti" VALUES(7,180,'7:180','۽ الله جا چڱا نالا آھن انھن نالن سان کيس سڏيو، ۽ جيڪي سندس نالن ۾ ڏنگائي ھلندا آھن تن کي ڇڏي ڏيو، (۽) جيڪي ڪندا آھن تنھنجي سگھو سزا ڏبين.');
INSERT INTO "amroti" VALUES(7,181,'7:181','۽ جيڪي اسان پيدا ڪيا آھن تن مان ھڪ ٽولي آھي جا سچي (دين) جو رستو ڏيکاريندي آھي ۽ اُن (دين) سان انصاف ڪندا آھن.');
INSERT INTO "amroti" VALUES(7,182,'7:182','۽ جن اسان جي آيتن کي ڪوڙ ڄاتو تن کي سگھو وڌائي وڌائي (اُتاھون) سزا ڏينداسون جتان ڄاڻندا ئي نه.');
INSERT INTO "amroti" VALUES(7,183,'7:183','۽ اُنھن کي مُھلت ڏيندس، ڇوته اسان جي رٿ پڪي آھي.');
INSERT INTO "amroti" VALUES(7,184,'7:184','(اُھي) ڌيان نه ڪندا آھن ڇا ته سندن سنگتي (محمد صلى الله عليه وسلم) کي ڪا چريائي ڪانه آھي، اُھو ته رڳو پڌرو ڊيڄاريندڙ آھي.');
INSERT INTO "amroti" VALUES(7,185,'7:185','آسمانن ۽ زمين جي بادشاھيءَ (۾) ۽ الله ھر جنس مان جيڪي پيدا ڪيو آھي تنھن ۾ نه نھاريندا آھن ڇا؟ ۽ ته متان سندن (موت جي) ٺھرايل مُدّت پوري ٿيڻ تي ھجي، پوءِ قرآن پڄاڻا ڪھڙي ڳالھ تي ايمان آڻيندا.');
INSERT INTO "amroti" VALUES(7,186,'7:186','جنھن کي الله گمراہ ڪندو آھي تنھن کي ڪو ھدايت ڪرڻ وارو ڪونھي، ۽ الله اُنھن کي سندن گمراھيءَ ۾ حيران ٿيل ڇڏي ڏيندو آھي.');
INSERT INTO "amroti" VALUES(7,187,'7:187','(اي پيغمبر) توکان قيامت بابت پُڇن ٿا ته ڪھڙي مھل اچڻي آھي؟ چؤ ته انھيء جي خبر رڳو منھنجي پالڻھار وٽ آھي، الله کانسواءِ کيس سندس وقت تي ڪو پڌرو نه ڪندو، (اُنجي اچڻ جي مھل جي ڄاڻپ) آسمانن ۽ زمين ۾ ڳري ٿي آھي (يعني ڪو سمجھي نه ٿو سگھي)، اُھا ته اوھان وٽ اوچتو ئي ايندي، (اي پيغمبر) توکان (ائين) پڇن ٿا جو ڄڻڪ تون اُنھن جو کوٽيندڙ (يعني جاچي معلوم ڪندڙ) آھين، چؤ ته اُن جي خبر الله کانسواءِ ٻئي ڪنھن وٽ نه آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(7,188,'7:188','چؤ ته الله جي گھُر ڌاران آءٌ پاڻ کي نڪي فائدو ۽ نڪي نقصان پھچائي سگھان ٿو، ۽ جيڪڏھن ڳُجھ ڄاڻان ھا ته گھڻيون چڱايون گڏ ڪريان ھا، ۽ مون کي ڪو ڏک نه پھچي ھا، آءٌ ته قوم مؤمنن لاءِ رڳو ڊيڄاريندڙ ۽ خوشخبري ڏيندڙ (پيغمبر) آھيان.');
INSERT INTO "amroti" VALUES(7,189,'7:189','اُھو (الله) آھي جنھن اوھان کي ھڪ وجود مان بڻايو ۽ منجھانئس سندس زال ھن لاءِ بڻايائين ته ساڻس آرام وٺي، پوءِ جنھن مھل (اُھو) گڏ ٿيندو آھي ته اُھا ھلڪي بار سان ڳورھاري ٿيندي آھي ۽ اُن سان گھمندي رھندي آھي، وري جنھن مھل ڏاڍي ڳري ٿيندي آھي (تنھن مھل) ٻئي پنھنجي پالڻھار کي سڏيندا آھن (ته قسم آھي) ته جيڪڏھن اسان کي صحيح سالم ٻار ڏيندين ته ضرور شڪر ڪندڙن مان ٿينداسون.');
INSERT INTO "amroti" VALUES(7,190,'7:190','پوءِ جنھن مھل انھن کي صحيح سالم ٻار ڏيندو آھي (ته) جيڪي اُنھن کي ڏنائين تنھن ۾ الله سان شريڪ مقرر ڪندا آھن، ۽ جنھن شيء کي الله سان شريڪ بڻائيندا آھن تنھن کان الله جو شان مٿاھون آھي.');
INSERT INTO "amroti" VALUES(7,191,'7:191','(ھي مشرڪ) اھڙن کي ڇو ٿا شريڪ بڻائين جي ڪجھ بڻائي نه سگھندا آھن ۽ اُھي پاڻ خلقيل آھن.');
INSERT INTO "amroti" VALUES(7,192,'7:192','۽ نڪي انھن کي ڪا مدد ڏيئي سگھندا آھن ۽ نڪي پاڻ کي (ئي) مدد ڏيئي سگھندا آھن.');
INSERT INTO "amroti" VALUES(7,193,'7:193','۽ (اي پيغمبر) جيڪڏھن اُنھن کي ھدايت ڏانھن سڏيندؤ ته اوھان جي تابعداري نه ڪندا، اوھان تي ھڪ جھڙو آھي ته انھن کي سڏيو يا اوھين ماٺ ڪريو.');
INSERT INTO "amroti" VALUES(7,194,'7:194','الله کان سواءِ جن کي سڏيندا آھيو سي اوھان جھڙا ئي ٻانھا آھن تنھنڪري جيڪڏھن (اوھين) سچا آھيو ته انھن کي سڏيو ته ڀلي اوھان کي ورندي ڏين.');
INSERT INTO "amroti" VALUES(7,195,'7:195','اُنھن (بتن) کي ڪي پير آھن ڇا جن سان گھمندا آھن، يا انھن کي ڪي ھٿ آھن ڇا جن سان وٺندا آھن، يا انھن کي ڪي اکيون آھن ڇا جن سان ڏسندا آھن، يا اُنھن کي ڪي ڪن آھن ڇا جن سان ٻڌندا آھن؟ چؤ ته (اوھين) پنھنجن بتن کي سڏيو پوءِ مون لاءِ جيڪا رٿ اچيو سا رٿيو ۽ مون کي مُھلت نه ڏيو.');
INSERT INTO "amroti" VALUES(7,196,'7:196','بيشڪ منھنجو مددگار اُھو الله آھي جنھن ڪتاب نازل ڪيو، ۽ اُھو نيڪن کي مدد ڏيندو آھي.');
INSERT INTO "amroti" VALUES(7,197,'7:197','۽ ان (الله) کان سواءِ جن کي سڏيندا آھيو سي اوھان کي ڪا مدد ڏيئي نه سگھندا آھن ۽ نڪي پاڻ کي مدد ڏيئي سگھندا آھن.');
INSERT INTO "amroti" VALUES(7,198,'7:198','۽ جيڪڏھن اوھين انھن کي ھدايت ڏانھن سڏيو ته ڪين ٻڌندا، کين ڏسندو آھين (ته ڄڻ) ته توڏانھن تڪين ٿا مگر اُھي ڏسندا ئي نه آھن.');
INSERT INTO "amroti" VALUES(7,199,'7:199','(اي پيغمبر) معافي ڏيڻ جي عادت ڪر ۽ چڱي ڪم ڪرڻ جو حُڪم ڪر ۽ جاھلن کان منھن موڙ.');
INSERT INTO "amroti" VALUES(7,200,'7:200','۽ جيڪڏھن توکي شيطان کان ڪو وسوسو پوي ته الله کان پناھ گھر، بيشڪ اُھو ٻُڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(7,201,'7:201','جيڪي پرھيزگار آھن تن کي جڏھن شيطان کان ڪو وسوسو پھچندو آھي تڏھن الله کي ياد ڪندا آھن پوءِ اُھي اُنھيءَ ئي مھل ڏسندڙ ٿيندا آھن.');
INSERT INTO "amroti" VALUES(7,202,'7:202','۽ اُنھن (شيطانن) جا ڀائر کين گمراھيءَ ۾ ڇڪيندا آھن وري نه گھٽائيندا آھن.');
INSERT INTO "amroti" VALUES(7,203,'7:203','۽ (اي پيغمبر) جڏھن وٽن ڪا آيت نه آڻيندو آھين (ته) چوندا آھن ته (پاڻون) ڇونه ٺاھيندو آھين؟ چؤ ته جيڪي مون ڏانھن پنھنجي پالڻھار جو وحي ٿيندو آھي تنھن کانسواءِ (ڪنھن جي) تابعداري نه ڪندو آھيان، ھي (قرآن) اسانجي پالڻھار وٽان سوجھرو ۽ ھدايت ۽ ٻاجھ انھيءَ قوم لاءِ آھي جي مڃيندي آھي.');
INSERT INTO "amroti" VALUES(7,204,'7:204','۽ جڏھن قرآن پڙھيو وڃي (ته) اُھو ڪن ڏيئي ٻڌو ۽ چُپ ڪريو مانَ ٻاجھ ڪيا وڃو.');
INSERT INTO "amroti" VALUES(7,205,'7:205','۽ پنھنجي پالڻھار کي پنھنجي دل ۾ عاجزيءَ ۽ ڀو سان وڏي آواز ڌاران صبح ۽ سانجھيءَ جو ياد ڪر ۽ غافلن مان نه ھُج.');
INSERT INTO "amroti" VALUES(7,206,'7:206','تحقيق جيڪي تنھنجي پالڻھار وٽ (ويجھا) آھن (سي) سندس عبادت کان وڏائي نه ڪندا آھن ۽ ان جي پاڪائي بيان ڪندا آھن ۽ ان کي سجدو ڪندا آھن.');
INSERT INTO "amroti" VALUES(8,1,'8:1','(اي پيغمبر) غنيمت جي مال بابت توکان پڇن ٿا، چؤ ته غنيمت جو مال الله ۽ پيغمبر جو آھي، پوءِ الله کان ڊڄو ۽ اوھين پاڻ ۾ ٺھراءُ ڪريو، ۽ جيڪڏھن مؤمن آھيو ته الله ۽ سندس رَسُول جي فرمانبرداري ڪريو.');
INSERT INTO "amroti" VALUES(8,2,'8:2','مُؤمن اُھي ئي آھن جن جون دليون جڏھن الله ياد ڪيو ويندو آھي (ته) ڊڄنديون آھن ۽ جڏھن سندس آيتون انھن کي پڙھي ٻڌائبيون آھن تڏھن اُھي سندن ايمان کي وڌائينديون آھن ۽ پنھنجي پالڻھار تي ڀروسو ڪندا آھن.');
INSERT INTO "amroti" VALUES(8,3,'8:3','اُھي نماز پڙھندا آھن ۽ جيڪي کين رزق ڏنوسون تنھن مان خرچيندا آھن.');
INSERT INTO "amroti" VALUES(8,4,'8:4','اِھي ئي پڪا مؤمن آھن، انھن لاءِ سندن پالڻھار وٽ (وڏا) درجا ۽ بخشش ۽ چڱي روزي آھي.');
INSERT INTO "amroti" VALUES(8,5,'8:5','جئن تنھنجي رب توکي تنھنجي گھر (مدينه) مان سچي تدبير لاءِ ٻاھر آندو، ۽ بيشڪ مؤمنن منجھان ھڪ ٽولي ضرور ناراض ھئي.');
INSERT INTO "amroti" VALUES(8,6,'8:6','اُھي سچي ڳالھه بابت ان جي پڌري ٿيڻ کانپوءِ (به) توسان گفتگو ڪندا رھيا ڄڻ ته اُھي (اکين سان) ڏسندي موت ڏانھن ھڪاليا وڃن ٿا.');
INSERT INTO "amroti" VALUES(8,7,'8:7','۽ (ياد ڪريو) ته جڏھن الله اوھان سان انجام ڪيو ته (ڪافرن جي) ٻن ٽولين مان ھڪ اوھان کي ھٿ لڳندي ۽ اوھان گھريو ٿي ته بي ھٿيار (ٽولي) اوھان لاءِ ھُجي ۽ الله گھريو ٿي ته پنھنجي حُڪم سان حق کي ثابت ڪري ۽ ڪافرن جي پاڙ ھِنَ لاءِ وڍي.');
INSERT INTO "amroti" VALUES(8,8,'8:8','ته حق کي ثابت ڪري ۽ باطل کي باطل ڪري توڻيڪ گنھگار ناراض رھن.');
INSERT INTO "amroti" VALUES(8,9,'8:9','جڏھن جو پنھنجي پالڻھار کي فرياد ٿي ڪيوَ ته (اُھا) اوھانجي (فرياد) قبول ڪيائين (فرمايائين) ته آءٌ لڳولڳ ھڪ ٻئي پٺيان ايندڙن ھڪ ھزار ملائڪن سان اوھان جي مدد ڪندڙ آھيان.');
INSERT INTO "amroti" VALUES(8,10,'8:10','الله ان (مدد) کي رڳو خوشخبري ڪيو ھو ۽ ته اُن سان اوھان جون دليون تسلّي واريون ٿين، ۽ الله (جي پار کان) سواءِ (ٻئي ھنڌان) مدد ملڻي نه آھي، ڇوته الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(8,11,'8:11','(ياد ڪر) جنھن مھل پنھنجي طرف کان امن ڏيڻ لاءِ اوھان کي اوجھراڪي ڍڪايائين ۽ اوھان تي آسمان مان پاڻي ھِن لاءِ وسايائين ته اُن سان اوھان کي پاڪ ڪري ۽ اوھان کان شيطان جي پليتي نئي ۽ اوھان جي دلين کي مُحڪم رکي ۽ ان سان (اوھان جي) قدمن کي مُحڪم ڪري.');
INSERT INTO "amroti" VALUES(8,12,'8:12','(ياد ڪر) جڏھن تنھنجي پالڻھار ملائڪن ڏانھن وحي ڪيو ته آءٌ اوھان سان آھيان تنھنڪري مؤمنن کي ھمٿايو، ڪافرن کي دل ۾ سگھو دھشت وجھندس پوءِ (اي مؤمنؤ) گردنين تي ڌڪ ھڻونِ ۽ اُنھن جي آڱرين جا سنڌ ڀڃو.');
INSERT INTO "amroti" VALUES(8,13,'8:13','اِھو (حُڪم) ھن سببان آھي جو انھن الله ۽ سندس پيغمبر جي مخالفت ڪئي، ۽ جيڪو الله ۽ سندس پيغمبر جي مُخالفت ڪندو ته بيشڪ الله سخت سزا ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(8,14,'8:14','اھا (سزا اٿو) پوءِ ان کي چکو ۽ پڪ ڄاڻو ته ڪافرن لاءِ باھ جو عذاب آھي.');
INSERT INTO "amroti" VALUES(8,15,'8:15','اي مؤمنؤ جڏھن (ويڙھ ۾) ڪافرن جي مُچي سان آمھون سامھون ٿيو تڏھن اُنھن کي پٺي ڏئي نه ڦرو.');
INSERT INTO "amroti" VALUES(8,16,'8:16','۽ اُنھيءَ ڏينھن لڙائيءَ جي کيڏاريءَ يا (پنھنجي لشڪر جي) ڪنھن ٽوليءَ ڏانھن ڀر وٺندڙ کانسواءِ جيڪو انھن کي پٺي ڏئي ڦِرندو سو بيشڪ الله جي ڏمر ھيٺ آيو ۽ سندس جاءِ دوزخ آھي، ۽ اُھا بڇڙي جاءِ آھي.');
INSERT INTO "amroti" VALUES(8,17,'8:17','پوءِ کين اوھان نه ڪُٺو پر الله کين ڪٺو آھي، ۽ (اي پيغمبر) جڏھن تو (ڌوڙ جي مُٺ) اُڇلي ته (اُھا) تو نه اُڇلي پر الله اڇلي، ۽ (ھن لاءِ) ته پاڻ وٽان مؤمنن کي (نعمت ڏيڻ سان) چڱي طرح آزمائي، بيشڪ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(8,18,'8:18','اِھو (حال) آھي ۽ الله ڪافرن جي مڪر کي سُست ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(8,19,'8:19','(اي ڪافرؤ) جڏھن (بدر جي لڙائيءَ جي سنبرڻ مھل ڪُفر ۽ اسلام جي سچائي پرکڻ لاءِ) سوڀ گھريوَ ته بيشڪ اوھان جي آڏو (اسلام کي) سوڀ آئي، ۽ جيڪڏھن اوھين (اسلام جي سچائي سمجھي ڪفر کان) بس ڪندؤ ته اھو اوھان لاءِ چڱو آھي ۽ جيڪڏھن ٻيھر (ويڙھ لاءِ) ايندؤ ته وري (اسلام کي فتح) ڏينداسون، ۽ اوھانجو لشڪر جيتوڻيڪ گھڻو ھوندو ته به اوھان کان ڪُجھ ٽاري نه سگھندو ۽ بيشڪ الله مؤمنن سان (ساڻ آھي.');
INSERT INTO "amroti" VALUES(8,20,'8:20','اي ايمان وارؤ الله ۽ سندس پيغمبر جي فرمانبرداري ڪريو ۽ اُن کان منھن نه موڙيو حالانڪ اوھين (قُرآن) ٻُڌو ٿا.');
INSERT INTO "amroti" VALUES(8,21,'8:21','۽ انھن (ڪافرن ۽ منافقن) جھڙا نه ٿيو جن چيو ته ٻُڌوسون ۽ اُھي نه ٿا ٻڌن.');
INSERT INTO "amroti" VALUES(8,22,'8:22','الله وٽ (سڀني) جاندارن مان تمام بڇڙا ٻوڙا ۽ گونگا آھن جي (ڪُجھ به) نه سمجھندا آھن.');
INSERT INTO "amroti" VALUES(8,23,'8:23','۽ جيڪڏھن الله منجھس ڪا چڱائي ڄاڻي ھا ته ضرور کين ٻُڌائي ھا، ۽ جيڪڏھن کين ٻُڌائي ھا ته اُھي مُنھن موڙيندڙ ٿي ڦرن ھا.');
INSERT INTO "amroti" VALUES(8,24,'8:24','اي مسلمانؤ الله ۽ پيغمبر جو حُڪم قبول ڪريو جڏھن (پيغمبر) اوھان کي ھن لاءِ سڏي ٿو ته اوھانکي جيئرو ڪري، ۽ ڄاڻو ته الله آدمي جي ۽ سندس دل جي وچ ۾ اوٽ ھوندو آھي ۽ ڏانھس گڏ ڪبؤ.');
INSERT INTO "amroti" VALUES(8,25,'8:25','۽ اُنھيءَ فتني کان ڊڄو جو اوھان مان جن ظلم ڪيو تن کي خلاصو نه پھچندو (بلڪ عام ھوندو)، ۽ ڄاڻو ته الله سخت سزا ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(8,26,'8:26','۽ (اي مھاجرؤ سندس احسانن کي) ياد ڪريو جڏھن زمين (مُلڪ) ۾ اوھين ٿورڙا (۽) ھيڻا ھيؤ ڊنؤ ٿي ته متان ماڻھو اوھان کي تڪڙو کڻي وٺن، پوءِ اوھان کي جاءِ ڏنائين ۽ اوھان کي پنھنجي مدد سان سگھ ڏنائين ۽ اوھان کي سٺين شين مان روزي ڏنائين ته مانَ اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(8,27,'8:27','اي مؤمنؤ الله ۽ پيغمبر جي خيانت نه ڪريو ۽ اوھين ڄاڻي واڻي پاڻ ۾ (ھڪ ٻئي جي) امانت جي خيانت (به) نه ڪريو.');
INSERT INTO "amroti" VALUES(8,28,'8:28','۽ ڄاڻو ته اوھان جا مال ۽ اوھان جو اولاد رڳو پَرک آھن ۽ الله ئي وٽ وڏو اَجر آھي.');
INSERT INTO "amroti" VALUES(8,29,'8:29','اي ايمان وارؤ جيڪڏھن الله کان ڊڄندا رھندؤ ته اوھان لاءِ (اوھان جي دلين ۾ حق ۽ باطل کي) نکيڙيندڙ (نُور) پيدا ڪندو ۽ اوھانجا گناھ اوھان کان ٽاريندو ۽ اوھان کي بخشيندو، ۽ الله وڏي فضل وارو آھي.');
INSERT INTO "amroti" VALUES(8,30,'8:30','۽ (اي پيغمبر ياد ڪر) جنھن مھل تولاءِ ڪافرن رٿ ٿي ڪئي ته توکي بند ڪن يا توکي شھيد ڪن يا توکي ڪڍي ڇڏين، ۽ (بڇڙي) رٿ ڪيائون ٿي ۽ الله (به) رٿ ڪئي ٿي، ۽ الله چڱو رٿ ڪندڙن مان آھي.');
INSERT INTO "amroti" VALUES(8,31,'8:31','۽ جڏھن اسان جون آيتون کين پڙھي ٻڌائجن ٿيون (ته) چوندا آھن ته ٻڌوسون جيڪڏھن گھُرون ته اُنھي جھڙو (اسين به) چئون ته ھيءُ ته رڳو اڳين جون آکاڻيون آھن.');
INSERT INTO "amroti" VALUES(8,32,'8:32','۽ (ياد ڪريو) جڏھن (ڪافرن) چيو ته اي الله جيڪڏھن ھي (قرآن) تو وٽان حق آھي ته اسان تي آسمان کان پَھڻ وساءِ يا ڪو ڏکوئيندڙ عذاب اسان تي آڻ.');
INSERT INTO "amroti" VALUES(8,33,'8:33','۽ الله کي نه جڳائيندو آھي جو منجھن تنھنجي ھوندي کين عذاب ڪري، ۽ الله کي (ھي به) نه جڳائيندو آھي جو کين ھن ھوندي عذاب ڪري جو اُھي بخشش گھرندا ھجن.');
INSERT INTO "amroti" VALUES(8,34,'8:34','۽ کين ڇا (ٿيو) آھي جو الله کين (پيغمبر جي ھجرت کانپوءِ) عذاب نه ڪري ھن ھوندي جو اُھي تعظيم واريءَ مسجد کان (ماڻھن کي) جھلن ٿا ۽ اُن جا مُتولّي نه آھن، پرھيزگارن کانسواءِ ٻيو ڪو اُن جو متولّي نه آھي پر انھن مان گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(8,35,'8:35','۽ سندين نماز الله جي گھر وٽ سيٽين ۽ تاڙين ھڻڻ کانسواءِ (ڪُجھ) نه آھي، پوءِ انھيءَ سببان جو ڪفر ڪندا آھيو عذاب چکو.');
INSERT INTO "amroti" VALUES(8,36,'8:36','(۽) ڪافر پنھنجا مال ھن لاءِ خرچين ٿا ته (ماڻھن کي) الله جي واٽ کان جھلين، پوءِ اھو سگھو انھن (مالن) کي خرچ ڪندا وري (اھو خرچڻ) مٿن ارمان ٿيندو وري مغلوب ڪيا ويندا، ۽ ڪافر دوزخ ڏانھن گڏ ڪيا ويندا.');
INSERT INTO "amroti" VALUES(8,37,'8:37','ته الله پليت کي پاڪ کان ڌار ڪري ۽ پليت کي ھڪ ٻئي تي رکي پوءِ ھيڪاندو ڍير ڪري پوءِ اُن کي دوزخ ۾ وجھي، اِھي ئي ڇيئي وارا آھن.');
INSERT INTO "amroti" VALUES(8,38,'8:38','(اي پيغمبر) ڪافرن کي چؤ ته جيڪڏھن اوھين مُڙندؤ ته جو ڪُجھ گذريو سو کين بخشبو، ۽ جيڪڏھن ٻيھر ويڙھ ڪندا ته بيشڪ اڳين جي (اھا) عادت ٿي گذري آھي.');
INSERT INTO "amroti" VALUES(8,39,'8:39','۽ (اي مؤمنؤ) اُنھن سان (ايستائين) لڙائي ڪريو جيسين (ڪفر جو) غلبو نه رھي ۽ سموري حڪومت رڳو الله جي ٿئي (يعني قانونِ الاھي جو عمل ھلي)، پوءِ جيڪڏھن مڙي وڃن ته جيڪي ڪندا آھن سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(8,40,'8:40','۽ جيڪڏھن مُنھن موڙيائون (۽ باز نه آيا) ته ڄاڻو ته الله اوھان جو سنڀاليندڙ آھي، (۽ اُھو) چڱو سنڀاليندڙ ۽ چڱو مددگار آھي.');
INSERT INTO "amroti" VALUES(8,41,'8:41','۽ (ھيءَ ڳالھ) ڄاڻو ته جيڪو مال (ڪافرن تي فتح لھڻ کانپوءِ) اوھان جي ھٿ ۾ آيو تنھنجو پنجون حصّو الله ۽ پيغمبر ۽ (پيغمبر جي) عزيزن ۽ يتيمن ۽ مسڪينن ۽ مسافرن لاءِ آھي جيڪڏھن اوھان الله کي مڃيو آھي ۽ (پڻ) جيڪو پنھنجي ٻانھي (محمد) تي فيصلي جي ڏينھن جنھن ڏينھن ٻئي ٽوليون پاڻ ۾ مقابل ٿيون نازل ڪيوسون (تنھن تي يقين رکندا آھيو ته اُن حُڪم تي عمل ڪريو)، ۽ الله سڀڪنھن شيء تي وسوارو آھي.');
INSERT INTO "amroti" VALUES(8,42,'8:42','(اِھو اُھو وقت ھو) جنھن مھل اوھين اورين ڀر ۽ ھو پرينءَ ڀر ھوا ۽ قافلو اوھان کان ھيٺين ڀَر (ھليو ويو)، ۽ جيڪڏھن پاڻ ۾ انجام ڪريو ھا ته ضرور انجام ۾ برخلاف ٿيو ھا پر ھِن لاءِ ته جيڪو ڪم الله کي ڪرڻو ھو سو پورو ڪري (ته حق پڌرو ٿئي، ۽) ھن لاءِ ته جيڪو ھلاڪ (گمراہ) ٿئي سو چٽائي کانپوءِ مرته ھلاڪ ٿئي ۽ جيڪو جيئرو ٿئي (يعني حق کي پروڙي) سو چٽائي کانپوءِ مرته جيئرو رھي، ۽ بيشڪ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(8,43,'8:43','(ياد ڪر) جنھن مھل الله تنھنجي خواب ۾ توکي اُھي (ڪافر) ٿورا ڏيکاريا، ۽ جيڪڏھن توکي اُھي گھڻا ڏيکاري ھا ته ضرور بي ھمّت ٿيو ھا ۽ ضرور (اُن) ڪم ۾ ھڪٻئي سان تڪرار ڪريو ھا پر الله (اُن تڪرار کان اوھان کي) بچايو، بيشڪ اُھو سينن وارو (ڀيد) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(8,44,'8:44','۽ (ياد ڪريو) جڏھن اوھين ھڪٻئي جي سامھون ٿيؤ تڏھن (الله) اوھان جي اکين ۾ اُھي ٿورڙا ڪري ڏيکاريا ٿي ۽ انھن (ڪافرن) جي نگاھن ۾ اوھانکي ٿورڙا ڪري ڏيکاريائين ٿي ھن لاءِ ته الله اھو پورو ڪري جيڪو ڪرڻو ھو، ۽ الله ڏانھن (سڀ) ڪم موٽايا ويندا.');
INSERT INTO "amroti" VALUES(8,45,'8:45','اي ايمان وارؤ! جڏھن ڪنھن (ڪافر) ٽوليءَ سان (جنگ ۾) مقابلو ڪريو تڏھن ثابت قدم رھو ۽ الله کي گھڻو ياد ڪريو ته مانَ اوھين ڇُٽو.');
INSERT INTO "amroti" VALUES(8,46,'8:46','۽ الله ۽ سندس پيغمبر جي فرمانبرداري ڪريو ۽ پاڻ ۾ تڪرار نه ڪريو نه ته سست ٿيندؤ ۽ اوھان جي ((اتفاق جي) بوءِ ھلي ويندي ۽ صبر ڪريو، بيشڪ الله صابرن سان آھي.');
INSERT INTO "amroti" VALUES(8,47,'8:47','۽ اوھين اُنھن (بدر وارن ڪافرن) وانگر نه ٿيو جيڪي پنھنجي ديس مان تڪبر سان ۽ ماڻھن جي ڏيکاري لاءِ نڪتا ۽ (ماڻھن کي) الله جي واٽ کان جھلڻ لڳا، ۽ جيڪي ڪندا آھن تنھن کي الله گھيروڪندڙ آھي.');
INSERT INTO "amroti" VALUES(8,48,'8:48','۽ جڏھن شيطان سندن ڪرتوت اُنھن لاءِ سينگاريا ۽ (ڪافرن کي) کي چيائين ته اڄ ماڻھن مان ڪوبه اوھان تي غالب (ٿيڻو) نه آھي ۽ بيشڪ آءٌ اوھان جو واھرو آھيان، پوءِ جڏھن ٻئي لشڪر آمھون سامھون ٿيا ته (شيطان) پنھنجين کڙين (ڀرپوءِ) تي ڀڳو ۽ چيائين ته آءٌ اوھان کان بيزار آھيان تحقيق جيڪي مان ڏسان ٿو سو اوھين نٿا ڏسو تحقيق آءٌ الله کان ڊڄان ٿو، ۽ الله سخت سزا ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(8,49,'8:49','(اُھو وقت ياد ڪريو) جنھن مھل منافقن ۽ جن جي دلين ۾ بيماري ھئي تن چيو ته ھنن (مسلمانن) کي سندن دين ٺڳيو آھي، ۽ جيڪو الله تي ڀروسو ڪندو آھي ته بيشڪ الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(8,50,'8:50','(اي ڏسندڙ) جيڪڏھن (اُنھي مھل) ڏسين جنھن وقت ملائڪ ڪافرن جو ساہ ڪڍندا آھن (۽) سندن مُنھن ۽ پٺين کي سٽيندا ڪٽيندا آھن (ته عجب ڪرين) ۽ (چوندا اٿن ته ھاڻي) ساڙيندڙ عذاب (جو مزو) چکو.');
INSERT INTO "amroti" VALUES(8,51,'8:51','اِھو ھن سببان آھي جو جيڪي اوھان جي ھٿن اڳي موڪليو آھي ۽ بيشڪ الله ٻانھن تي ظلم ڪندڙ نه آھي.');
INSERT INTO "amroti" VALUES(8,52,'8:52','(انھن جو حال) فرعونين ۽ انھن جھڙو آھي جيڪي کانئن اڳ ھوا، (جو) الله جي آيتن کي نه مڃيائون پوءِ الله سندن گناھن سببان کين پڪڙيو، بيشڪ الله سگھارو سخت سزا ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(8,53,'8:53','اھو ھن سببان آھي جو الله جا نعمت ڪنھن قوم کي عطا ڪندو آھي تنھن کي (اُھو) ايستائين ڪڏھن نه مٽائيندو آھي جيسين جيڪي سندن دلين ۾ آھي سو (پاڻ نه) مٽائيندا آھن ۽ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(8,54,'8:54','(انھن جو حال) فرعونين ۽ پڻ جيڪي کانئن اڳ ھوا تن جھڙو آھي، (جو) پنھنجي پالڻھار جي آيتن کي ڪوڙ ڄاتائون تنھنڪري انھن کي سندن گناھن سببان ھلاڪ ڪيوسون ۽ فرعونين کي ٻوڙيوسون، مڙئي ظالم ھوا.');
INSERT INTO "amroti" VALUES(8,55,'8:55','جاندارن مان (سڀ کان) بڇڙا الله وٽ ڪافر آھن پوءِ اُھي ايمان نه ٿا آڻين.');
INSERT INTO "amroti" VALUES(8,56,'8:56','انھن مان جن سان (صلح جو) انجام ڪيو اٿيئي پوءِ وري ھر ڀيري پنھنجو انجام ڀڃيندا آھن ۽ اُھي نه ڊڄندا آھن.');
INSERT INTO "amroti" VALUES(8,57,'8:57','پوءِ جيڪڏھن لڙائيءَ ۾ کين لھين ته (انھن کي اھڙي سزا ڏي جنھن جي ڪري) جيڪي سندن پوئتان ھجن تن کي ڀڄاءِ ته مانَ اھي ياد ڪن.');
INSERT INTO "amroti" VALUES(8,58,'8:58','۽ جيڪڏھن ڪنھن قوم جي (انجام ڀڃڻ جي) خيانت کان ڊڄين ته اُنھن ڏانھن (سندن انجام) پوري ٿيڻ مھل بيزاريءَ جو اطلاع موڪل، ڇوته الله خيانت ڪندڙن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(8,59,'8:59','۽ ڪافر (ايئن) نه ڀائين ته (ڪو) اڳرائي ڪيائون، بيشڪ اُھي نه ٿڪائيندا.');
INSERT INTO "amroti" VALUES(8,60,'8:60','۽ انھن لاءِ (لڙائي جي ھٿيارن جي) سگھ مان ۽ گھوڙن جي تيار رکڻ مان جيڪي ٿي سگھيوَ سو الله جي ويرين ۽ پنھنجن ويرين کي دھشت ڏيڻ لاءِ تيار رکو ۽ انھي کانسواءِ ٻي قوم کي (به) کين نه ڄاڻندا آھيو، اُنھن کي الله ڄاڻندو آھي، ۽ جيڪي ڪنھن شيء مان الله جي واٽ ۾ خرچيو سو اوھان کي پورو ڏبو ۽ اوھان کي گھٽ نه ڏبو.');
INSERT INTO "amroti" VALUES(8,61,'8:61','۽ جيڪڏھن صلح لاءِ لاڙو ڪن ته (تون به) اُن ڏانھن لاڙو ڪر ۽ الله تي ڀروسو ڪر، بيشڪ اُھو ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(8,62,'8:62','۽ جيڪڏھن توکي دولاب ڏيڻ جو ارادو ڪن ٿا ته بيشڪ تو لاءِ الله ڪافي آھي، (۽) اُھوئي آھي جنھن توکي ۽ مؤمنن کي پنھنجي مدد سان سگھ ڏني.');
INSERT INTO "amroti" VALUES(8,63,'8:63','۽ اُنھن (مؤمنن) جي دلين ۾ ميلاپ ڏنائين، جيڪي زمين ۾ آھي سو مڙئي جيڪڏھن تون خرچين ھا ته سندين دلين ۾ ميلاپ ڏئي نه سگھين ھا پر الله سندن وچ ۾ ميلاپ ڏنون، بيشڪ اُھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(8,64,'8:64','اي پيغمبر توکي ۽ پڻ مؤمنن مان جن تنھنجي تابعداري ڪئي تن کي الله ڪافي آھي.');
INSERT INTO "amroti" VALUES(8,65,'8:65','اي پيغمبر مؤمنن کي لڙائيءَ تي ھمتاءِ، جيڪڏھن اوھان مان صبر وارا وِيھ آھن ته ٻن سَون تي غالب ٿيندا، ۽ جيڪڏھن اوھان مان سَو آھن ته ھڪ ھزار ڪافرن تي غالب ٿيندا ھن ڪري جو اُھا اھڙي قوم آھي جا نه سمجھندي آھي.');
INSERT INTO "amroti" VALUES(8,66,'8:66','ھاڻي الله اوھان تان بار ھلڪو ڪيو ۽ اوھان ۾ ھيڻائي معلوم ڪيائين، پوءِ جيڪڏھن اوھان مان صبر وارا سَو ھوندا ته ٻن سون تي غالب ٿيندا، ۽ جيڪڏھن اوھان مان ھزار ھوندا ته ٻن ھزارن تي الله جي حڪم سان غالب ٿيندا، ۽ الله صابرن ساڻ آھي.');
INSERT INTO "amroti" VALUES(8,67,'8:67','پيغمبر کي نه جڳائيندو آھي جو وٽس قيدي ھجن (بلڪ ڪُٺا وڃن) جيستائين مُلڪ ۾ (ڪافرن جي عام) خونريزي نه ڪري، (اوھين) دنيا جو سامان گھرندا آھيو، ۽ الله آخرت (جو ثواب ڏيڻ) گھرندو آھي، ۽ الله غالب حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(8,68,'8:68','جيڪڏھن خدا جي تقدير ۾ اڳي لکيل نه ھجي ھا ته اوھان جيڪي (فديو) ورتو تنھن ۾ اوھان کي ضرور وڏو عذاب پھچي ھا.');
INSERT INTO "amroti" VALUES(8,69,'8:69','پوءِ جيڪي اوھان مال غنيمت ورتو سو پاڪ حلال (ڀانيو ۽) کائو، ۽ الله کان ڊڄو، بيشڪ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(8,70,'8:70','اي پيغمبر، قيدين مان جيڪي اوھان جي ھٿن ۾ آھن تن کي چؤ ته جيڪڏھن الله اوھان جي دلين ۾ ڪا چڱائي ڄاتي ته جيڪي اوھان کان ورتو ويو تنھن کان چڱو اوھان کي ڏيندو ۽ اوھان کي بخشيندو، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(8,71,'8:71','۽ جيڪڏھن توسان خيانت ڪرڻ گھرندا ته بيشڪ الله سان (به) اڳ خيانت ڪئي ھوائون پوءِ (اوھان کي) انھن تي سگھارو ڪيائين، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(8,72,'8:72','جن ايمان آندو ۽ حجرت ڪئي ۽ پنھنجن مالن ۽ پنھنجن جندن سان الله جي واٽ ۾ جھاد ڪيو ۽ جن جاءِ ڏني ۽ مدد ڪئي اھي ھڪ ٻئي جا دوست آھن، ۽ جن ايمان آندو ۽ ھجرت نه ڪئي اوھان تي انھن جي دوستيءَ جو ڪو حق نه آھي جيسين ھجرت ڪن، ۽ جيڪڏھن دين (جي ڪم) ۾ اوھان کان مدد گھُرن ته اوھان کي انھيءَ قوم کانسواءِ ٻي قوم تي مدد ڏيڻ لازم آھي جن جو اوھانجي ۽ ھنن جي وچ ۾ انجام ٿيل ھجي، ۽ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(8,73,'8:73','۽ جيڪي ڪافر آھن تن مان ھڪڙا ٻين جا سنگتي آھن، پر (اي مسلمانؤ) جيڪڏھن اوھين (ائين) نه ڪندؤ ته مُلڪ ۾ فتنو ۽ وڏو فساد ٿيندو.');
INSERT INTO "amroti" VALUES(8,74,'8:74','۽ جن ايمان آندو ۽ ھجرت ڪئي ۽ الله جي واٽ ۾ جھاد ڪيو ۽ جن جاءِ ڏني ۽ مدد ڪئي اِھي ئي سچا مؤمن آھن، اُنھن لاءِ بخشش ۽ سڳوري روزي آھي.');
INSERT INTO "amroti" VALUES(8,75,'8:75','۽ جن (انھن کان) پوءِ ايمان آندو ۽ ھِجرت ڪئي ۽ اوھان سان گڏ جھاد ڪيو سي به اوھان مان آھن، ۽ مائٽيءَ وارا الله جي حُڪم موجب (پاڻ ۾) ھڪ ٻئي جا وڌيڪ حقدار آھن، ڇوته الله سڀڪنھن شيء کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(9,1,'9:1','جن مشرڪن سان اوھان انجام ڪيو تن ڏانھن الله ۽ سندس پيغمبر جي پاران (انجام کان) بيزاري آھي.');
INSERT INTO "amroti" VALUES(9,2,'9:2','(اي مشرڪؤ) مُلڪ ۾ چار مھينا سير ڪريو ۽ ڄاڻو ته اوھين الله کي ٿَڪائڻ وارا نه آھيو ۽ (ڄاڻو) ته الله ڪافرن کي خراب ڪرڻ وارو آھي.');
INSERT INTO "amroti" VALUES(9,3,'9:3','۽ وڏي حَج جي ڏينھن الله ۽ سندس پيغمبر جي طرفان ماڻھن ڏانھن پڙھو آھي ته الله ۽ سندس پيغمبر مشرڪن کان بيزار آھي، پوءِ (اي مشرڪؤ) جيڪڏھن (شرڪ کان) توبه ڪندؤ ته اُھا اوھان لاءِ ڀلي آھي، ۽ جيڪڏھن اوھين مُنھن ڦيرائيندؤ ته ڄاڻو ته اوھين الله کي ٿَڪائڻ وارا نه آھيو، ۽ (اي پيغمبر) ڪافرن کي ڏکوئيندڙ عذاب جي خبر ڏي.');
INSERT INTO "amroti" VALUES(9,4,'9:4','پر مشرڪن مان جن انجام ڪيو ھيوَ وري (انھن) اوھان سان (انجام ۾) ڪُجھ قُصور نه ڪيو ۽ نڪي اوھان جي خلاف ڪنھن کي مدد ڏنائون تن جو انجام سندين مدت تائين ساڻن پورو ڪريو، ڇوته الله پرھيزگارن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(9,5,'9:5','پوءِ (جن مھينن ۾ لڙائي) حرام (ڪئي ويئي آھي سي) مھينا جڏھن پورا ٿين ته (عرب جي) مشرڪن کي جتي لھو تتي انھن کي ماريو ۽ انھن کي پڪڙيو ۽ انھن کي بند رکو ۽ سڀني گھَٽن تي انھن لاءِ ويھو، پوءِ جيڪڏھن توبه ڪن ۽ نماز پڙھن ۽ زڪوٰة ڏين ته سندن واٽ بند نه ڪريو، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(9,6,'9:6','۽ جيڪڏھن مشرڪن مان ڪو ھڪڙو توکان امانَ گھُري ته کيس امانَ ڏي جيستائين (ھو) ڪلام الله ٻڌي پوءِ کيس سندس امن جي ھنڌ پھچاءِ، اِھو ھن ڪري آھي جو اُھا قوم اھڙي آھي جو نه ڄاڻندي آھي.');
INSERT INTO "amroti" VALUES(9,7,'9:7','الله وٽ ۽ سندس پيغمبر وٽ مشرڪن لاءِ اَنجام ڪئن ٿيندو سواءِ انھن جي جن سان تعظيم واريءَ مسجد وٽ انجام ڪيو ھُيوَ، پوءِ اُھي جيستائين اوھان جي انجام تي بيھڪ ڪن ته اوھين انھن لاءِ بيھڪ وٺو، بيشڪ الله پرھيزگارن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(9,8,'9:8','ڪئن (سندن انجام رھندو) ۽ جيڪڏھن اوھان تي غالب ٿين ته اوھان بابت نڪي مائٽي جو ۽ نڪي انجام جو خيال رکن، اوھان کي پنھنجين (مِٺين) زبانن سان راضي ڪندا آھن ۽ سندن دليون انڪاري آھن، ۽ منجھانئن گھڻا بي دين آھن.');
INSERT INTO "amroti" VALUES(9,9,'9:9','الله جي آيتن کي ٿوري مُلھ تي وڪيائون پوءِ (ماڻھن کي) سندس واٽ کان جھليائون، اُھي جيڪي ڪندا آھن سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(9,10,'9:10','ڪنھن مؤمن بابت نڪي مائٽي ۽ نڪي انجام جو خيال رکندا آھن، ۽ اھي ئي حد کان لنگھيل آھن.');
INSERT INTO "amroti" VALUES(9,11,'9:11','پوءِ جيڪڏھن (ڪفر کان) توبه ڪن ۽ نماز پڙھن ۽ زڪوٰة ڏين ته اوھان جا دين ۾ ڀائر آھن، ۽ جيڪي ماڻھو ڄاڻندا آھن تن لاءِ نشانيون کولي بيان ڪريون ٿا.');
INSERT INTO "amroti" VALUES(9,12,'9:12','۽ جيڪڏھن پنھنجن انجامن ٻَڌڻ کانپوءِ پنھنجا قسم ڀڃن ۽ اوھانجي دين ۾ طعنو ھڻن ته ڪفر جي سرڪردن سان جنگ ڪريو ڇو ته اُنھن جو قسم (اعتبار جوڳو) نه آھي مان اُھي جھلجن.');
INSERT INTO "amroti" VALUES(9,13,'9:13','اھڙي قوم سان ڇونه ٿا وڙھو جن پنھنجن قسمن کي ڀڳو ۽ جي پيغمبر جي ڪڍي ڇڏڻ لاءِ سنبريا ۽ منڍئون اوھان سان (دشمني) اوّل ۾ ڪرڻ لڳا؟ کائن ڇو ٿا ڊڄو؟ جيڪڏھن مؤمن آھيو ته الله (ھن لاءِ) وڌيڪ حقدار آھي ته کانئس ڊڄو.');
INSERT INTO "amroti" VALUES(9,14,'9:14','انھن سان وڙھو ته الله اوھان جي ھٿان کين سزا ڏيندو ۽ کين خوار ڪندو ۽ اوھانکي مٿن سوڀ ڏيندو ۽ مؤمنن جي قوم جي سينن کي شفا بخشيندو (ٺاريندو).');
INSERT INTO "amroti" VALUES(9,15,'9:15','۽ سندين دلين جي ڪاوڙ وڃائيندو، ۽ الله جنھن لاءِ گھرندو تنھن تي ٻاجھ سان موٽندو، ۽ الله ڄاڻندڙ حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(9,16,'9:16','ڀائيندا آھيو ڇا ته اوھان کي (جھاد جي تڪليف کان) ڇڏيو ويندو؟ حقيقت ڪري اوھان مان جن جھاد ڪيو ۽ نڪي الله ۽ نڪي پيغمبر ۽ نڪي مؤمنن کانسواءِ (ٻيو) ڪو ڳجھو دوست ورتو تن کي الله اڃا نه نکيڙيو آھي، ۽ جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(9,17,'9:17','پاڻ تي ڪفر جو اقراري ٿي مشرڪن کي الله جي مسجدين جو آباد ڪرڻ روا نه آھي، اِھي اُھي آھن جن جا عمل چٽ ٿي ويا، ۽ اُھي باھ ۾ سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(9,18,'9:18','الله جي مسجدين کي آباد ڪرڻ رڳو انھي کي جڳائيندو آھي جنھن الله ۽ قيامت جي ڏينھن کي مڃيو ۽ نماز پڙھي ۽ زڪوٰة ڏني ۽ الله کانسواءِ (ٻئي ڪنھن کان) نه ڊنو، پوءِ اُميد آھي ته اُھي ھدايت وارن مان آھن.');
INSERT INTO "amroti" VALUES(9,19,'9:19','اوھان حاجين کي پاڻي پيارڻ ۽ تعظيم واري مسجد کي آباد رکڻ اُن (جي عملن) جھڙو ڪيو آھي ڇا جنھن الله ۽ قيامت جي ڏينھن کي مڃيو ۽ الله جي واٽ ۾ جھاد ڪيو، الله وٽ (ھي ۽ اُھو) برابر نه آھن، الله ظالم قوم کي ھدايت نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(9,20,'9:20','جن مڃيو ۽ ھجرت ڪئي ۽ الله جي واٽ ۾ پنھنجين مالن ۽ پنھنجين جانين سان جھاد ڪيو سي الله وٽ مرتبي ۾ بلڪل وڏا آھن، ۽ اُھي مُراد ماڻڻ وارا آھن.');
INSERT INTO "amroti" VALUES(9,21,'9:21','انھن کي سندن پالڻھار پاڻ وٽان ٻاجھ ۽ راضپي ۽ انھن باغن جي خوشخبري ڏئي ٿو جن ۾ انھن لاءِ سدائين نعمتون آھن.');
INSERT INTO "amroti" VALUES(9,22,'9:22','منجھس سدائين رھڻ وارا آھن، ڇوته الله ئي وٽ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(9,23,'9:23','اي مؤمنؤ پنھنجن پئرن ۽ پنھنجن ڀائرن کي دوست نه وٺو جيڪڏھن (اُھي) ڪفر کي ايمان کان پيارو رکن (ته)، ۽ جيڪي اوھان مان انھن سان دوستي ڪندا سي ئي ظالم آھن.');
INSERT INTO "amroti" VALUES(9,24,'9:24','(اي پيغمبر) چؤ ته جيڪڏھن اوھان جا پيئر ۽ اوھان جو اولاد ۽ اوھان جا ڀائر ۽ اوھان جون زالون ۽ اوھان جا مائٽ ۽ اُھي مال جي اوھان ڪمايا آھن ۽ واپار جنھن جي بند ٿيڻ کان ڊڄو ٿا ۽ جايون جن کي پسند ڪريو ٿا سي اوھان کي الله ۽ سندس پيغمبر ۽ سندس واٽ ۾ جھاد ڪرڻ کان وڌيڪ پيارا آھن ته (ايسين) ترسو جيسين الله پنھنجو عذاب آڻي، ۽ الله بي دين قوم کي (سڌو) رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(9,25,'9:25','بيشڪ الله اوھان کي گھڻن ھنڌن ۾ سوڀ ڏني ۽ (پڻ جنگ) حُنين جي ڏينھن جو جڏھن اوھان جي گھڻائي اوھانکي عجب ۾ وڌو ۽ اوھان کان اوھان جي گھڻائي ڪجھ به ٽاري نه سگھي ۽ زمين پنھنجي ويڪرائي ھوندي (به) اوھان تي سوڙھي ٿي ويئي پوءِ اوھين پُٺيرا ٿي ڦريؤ.');
INSERT INTO "amroti" VALUES(9,26,'9:26','موٽي الله پنھنجي پار کان آرام پنھنجي پيغمبر تي ۽ مؤمنن تي لاٿو ۽ (ملائڪن جو) لشڪر لاٿائين جن کي نه ٿي ڏٺوَ، ۽ ڪافرن کي سزا ڏنائين، ۽ اِھا ڪافرن جي سزا آھي.');
INSERT INTO "amroti" VALUES(9,27,'9:27','موٽي اُن کانپوءِ الله جنھن لاءِ وڻيس تنھن تي ٻاجھ سان موٽندو، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(9,28,'9:28','اي مؤمنؤ مشرڪ رڳو پليت آھن تنھنڪري ھن ورہ پڄاڻا تعظيم واريءَ مسجد جي ويجھو نه وڃن، ۽ جيڪڏھن (اوھين) سڃائيءَ کان ڊڄندا آھيو ته جيڪڏھن الله گھريو ته پنھنجي فضل سان اوھانکي سگھوئي آسودو ڪندو، ڇوته الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(9,29,'9:29','جن کي ڪتاب ڏنو ويو تن مان جيڪي نڪي الله کي ۽ نڪي قيامت جي ڏينھن کي مڃيندا آھن ۽ نڪي الله ۽ سندس پيغمبر جي حرام ڪيل شيء کي حرام ڪري ڄاڻندا آھن ۽ نڪي سچو دين قبول ڪندا آھن تن سان ايستائين وڙھو جيسين (اُھي) رعيتي پنھنجي ھٿان جزيو ڏين.');
INSERT INTO "amroti" VALUES(9,30,'9:30','۽ يَھُودين چيو ته عُزير الله جو پُٽ آھي ۽ نصارن چيو ته عيسىٰ الله جو پُٽ آھي، اِھو سندن واتن سان آھي، اڳين ڪافرن جي چوڻ جھڙي رِيس ڪندا آھن، الله مٿن لعنت ڪري، ڪيڏي جو ڪيڏي ڀُلايا ويندا آھن.');
INSERT INTO "amroti" VALUES(9,31,'9:31','(۽) پنھنجن عالمن ۽ پنھنجن فقيرن ۽ عيسىٰ پُٽ مريم جي کي الله کانسواءِ پالڻھار ڪري ورتائون، ۽ ھڪ الله جي عبادت ڪرڻ کانسواءِ کين ٻيو حڪم نه ڪيو ويو آھي، اُن کانسواءِ ڪو عبادت جو لائق نه آھي، جيڪي ساڻس شريڪ ڪندا آھن تنھن کان ھو پاڪ آھي.');
INSERT INTO "amroti" VALUES(9,32,'9:32','(۽) پنھنجن واتن سان الله جي نُور کي وسائڻ گھرندا آھن ۽ الله پنھنجي نُور کي پوري ڪرڻ کانسواءِ نه رھندو جيتوڻيڪ ڪافر ارھا ٿين.');
INSERT INTO "amroti" VALUES(9,33,'9:33','اُھو (الله) اھي جنھن پنھنجي پيغمبر کي ھدايت ۽ سچي دين سان ھن لاءِ موڪليو ته ان کي سڀني دينن تي غالب ڪري جيتوڻيڪ مشرڪ رنج ٿين.');
INSERT INTO "amroti" VALUES(9,34,'9:34','اي ايمان وارؤ ڪتاب وارن عالمن ۽ فقيرن مان گھڻا ماڻھن جو مال ٺڳيءَ سان کائيندا آھن ۽ الله جي واٽ کان (ماڻھن کي) جھليندا آھن، ۽ جيڪي سون ۽ رپي جو خزانو گڏ ڪندا آھن ۽ اُھو الله جي واٽ ۾ نه خرچيندا آھن تن کي ڏکوئيندڙ عذاب جي خبر ڏي.');
INSERT INTO "amroti" VALUES(9,35,'9:35','جنھن ڏينھن (سندن مال) دوزخ جي باھ ۾ تپائبو (جڏھن تپندو ته) پوءِ اُن سان سندن پيشانيون ۽ سندن پاسريون ۽ سندن پٺيون ڏنڀبيون، (چئبن ته) ھيءُ اُھو (خزانو) آھي جيڪو پاڻ لاءِ گڏ ڪيو ھيوَ (ان جو) مزو چکو.');
INSERT INTO "amroti" VALUES(9,36,'9:36','بيشڪ مھينن جو ڳاڻيٽو يقيناً ڪتاب الاھي ۾ الله وٽ ٻارھن مھينا آھن جنھن ڏينھن کان آسمانن ۽ زمين کي بڻايائين منجھانئس چار (مھينا) تعظيم وارا آھن، اِھو پڪو دين آھي، تنھنڪري اوھين پاڻ کي منجھن نقصان نه لايو، ۽ سڀني مشرڪن سان وڙھو جھڙي طرح اوھان سان سڀيئي وڙھن ٿا، ۽ ڄاڻو ته الله پرھيزگارن سان گڏ آھي.');
INSERT INTO "amroti" VALUES(9,37,'9:37','تعظيم وارن مھينن جو اڳي پوءِ ڪرڻ رڳو ڪفر ۾ واڌارو آھي اُن سان ڪافر ڀُلايا ويندا آھن اُن (مھيني) کي ھڪ سال حلال ڳڻين ٿا ۽ ٻئي سال اُن کي حرام ڀائين ٿا ھن لاءِ ته الله جي حرام ڪيلن مھينن جي ڳاڻيٽي جي پورائي ڪن پوءِ جن کي الله حرام ڪيو آھي تن کي حلال ٿا ڪن، اُنھن لاءِ سندن بڇڙا ڪرتوت سينگاريا ويا، ۽ الله ڪافر ٽوليءَ کي ھدايت نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(9,38,'9:38','اي ايمان وارؤ اوھان کي ڇا (ٿيو) آھي جو جڏھن اوھان کي چئبو آھي ته الله جي واٽ ۾ (جھاد لاءِ) نڪرو ته زمين ڏانھن ڳرا ٿي ڪرندا آھيو، آخرت جي بدران دنيا جي حياتي سان راضي ٿيا آھيو، دنيا جي حياتي جو سامان آخرت (جي مقابلي) ۾ ٿورڙي کانسواءِ نه آھي.');
INSERT INTO "amroti" VALUES(9,39,'9:39','جيڪڏھن نه نڪرندؤ ته اوھان کي ڏکوئيندڙ عذاب جي سزا ڏيندو، ۽ اوھان کانسواءِ ٻي قوم مٽائي آڻيندو ۽ اوھين کيس ڪُجھ ضرر نه رسائيندؤ، ۽ الله سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(9,40,'9:40','جيڪڏھن پيغمبر کي مدد نه ڏيندؤ ته بيشڪ کيس الله مدد ڏني آھي جنھن مھل ڪافرن (مڪي مان) کيس ڪڍي ڇڏيو ٻن مان ھڪڙو پاڻ ھو جڏھن اُھي غار ۾ ھوا تڏھن پنھنجي سنگتيءَ کي چيائين ته اُلڪو نه ڪر تحقيق الله اسان سان (گڏ) آھي، پوءِ الله مٿس پنھنجو آرام لاٿو ۽ کيس اھڙي لشڪر سان مدد ڪيائين جو اُن کي اوھان نه ڏٺو ۽ ڪافرن جي ڳالھ کي ھيٺ ڪيائين، الله جي ڳالھ (ھميشه) مٿاھين آھي، ۽ الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(9,41,'9:41','(ھر حال جنگ جي) ٿوري سامان ۽ گھڻي سامان سوڌا (جھاد لاءِ) نڪرو ۽ پنھنجن مالن ۽ پنھنجين جِندن سان الله جي واٽ ۾ جھاد ڪريو، اھو اوھان لاءِ ڀلو آھي جيڪڏھن ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(9,42,'9:42','جيڪڏھن سامان سولو ۽ سفر ھلڪو ھجي ھا ته ضرور تنھنجي پٺيان لڳن ھا پر مٿن پنڌ پري ٿيو، ۽ الله جو قسم سگھوئي کڻندا (چوندا ته) جيڪڏھن اسين توفيق ساريون ھا ته اوھان سان گڏ نڪرون ھا، پاڻ کي (منافقيءَ سببان) ھلاڪ ڪن ٿا، ۽ الله ڄاڻي ٿو ته اُھي ڪوڙا آھن.');
INSERT INTO "amroti" VALUES(9,43,'9:43','الله توکي معاف ڪيو، کين تو ڇالاءِ موڪل ڏني جيسين توکي سچا پڌرا ٿين ھا ۽ (تون) ڪوڙن کي ڄاڻين ھا.');
INSERT INTO "amroti" VALUES(9,44,'9:44','جن الله ۽ قيامت جي ڏينھن کي مڃيو آھي سي پنھنجن مالن ۽ پنھنجين جِندن سان جھاد ڪرڻ ۾ توکان موڪل نٿا گھرن، ۽ الله پرھيزگارن کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(9,45,'9:45','انھن کان سواءِ ٻيو ڪو توکان موڪل نه گھرندو جيڪي الله ۽ قيامت جي ڏينھن کي نه مڃيندا آھن ۽ سندن دلين ۾ شڪ پيل آھن پوءِ اُھي پنھنجي شڪ ۾ حيران آھن.');
INSERT INTO "amroti" VALUES(9,46,'9:46','۽ جيڪڏھن ٻاھر نڪرڻ گھرن ھا ته ان لاءِ تياريءَ جو سامان تيار ڪن ھا پر الله سندن (جھاد تي) وڃڻ پسند نه ڪيو تنھنڪري کين توفيق نه ڏنائين ۽ چيو ويونِ ته وھندڙن سان گڏجي وِھو.');
INSERT INTO "amroti" VALUES(9,47,'9:47','جيڪڏھن اوھان ۾ (گڏجي) نڪرن ھا ته اوھان ۾ شرارت کانسواءِ ٻيو ڪُجھ نه وڌائين ھا ۽ ضرور اوھانجو ٻگيٿرو گھرندڙ ٿي اوھان جي وچ ۾ فساد وجھڻ لاءِ ڊوڙن ھا، ۽ اوھان ۾ اُنھن جا (ڪي) جاسوس آھن، ۽ الله ظالمن کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(9,48,'9:48','اُھي اڳ بيشڪ فساد جي فڪر ۾ ھوا ۽ تولاءِ بڇڙيون صلاحون ڪندا رھيا ايتري تائين جو مدد آئي ۽ الله جو حُڪم غالب ٿيو ۽ اُھي ناراض ھوا.');
INSERT INTO "amroti" VALUES(9,49,'9:49','۽ منجھائن ڪو آھي جو چوي ٿو ته مون کي موڪل ڏي ۽ مونکي فتني ۾ نه وجھ، خبردار (اُھي پاڻ) فتني ۾ پيل آھن، ۽ بيشڪ دوزخ ڪافرن کي گھيرو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(9,50,'9:50','(اي پيغمبر) جيڪڏھن توکي ڪو سھنج پھچندو آھي ته کين ڏکيو لڳندو آھي، ۽ جيڪڏھن ڪو توکي اھنج پھچندو آھي ته چوندا آھن ته بيشڪ پنھنجو ڪم اڳيئي سنڀالي ورتو اٿؤن ۽ اُھي سرھا ٿي ڦرندا آھن.');
INSERT INTO "amroti" VALUES(9,51,'9:51','(اي پيغمبر) چؤ ته جيڪي الله اسان لاءِ لکيو آھي تنھن کانسواءِ ٻيو ڪي اسانکي ڪڏھن نه پھچندو، اُھو اسانجو سنڀاليندڙ آھي، ۽ مؤمنن کي رڳو الله تي ڀروسو ڪرڻ جڳائي.');
INSERT INTO "amroti" VALUES(9,52,'9:52','(اي پيغمبر منافقن کي) چؤ ته اوھين اسان لاءِ ٻن چڱاين مان ھڪڙي کانسواءِ انتظار نه ڪڍندا آھيو، ۽ اسين (به) اوھان لاءِ انتظار ڪڍندا آھيون ته الله پاڻ وٽان يا اسان جي ھٿان اوھان کي سزا پھچائي، پوءِ اوھين انتظار ڪڍو اسين (به) اوھان سان گڏ منتظر آھيون.');
INSERT INTO "amroti" VALUES(9,53,'9:53','چؤ (اي منافقو) سَرھا ٿي توڻي ارھا ٿي خرچ ڪريو ته اوھان کان ڪڏھن قبول نه ڪبو، ڇوته اوھين بي دين ٽولي آھيو.');
INSERT INTO "amroti" VALUES(9,54,'9:54','۽ کانئن سندن خرچ جو قبول ڪرڻ ھن کانسواءِ نه جھليو ويو ته اُھي الله ۽ سندس پيغمبر جا مُنڪر آھن ۽ نڪي آرس ڪرڻ کان سواءِ نماز پڙھندا آھن ۽ نڪي (الله جي واٽ ۾) رنج کانسواءِ خرچيندا آھن.');
INSERT INTO "amroti" VALUES(9,55,'9:55','پوءِ توکي نڪي سندن مال ۽ نڪي سندن اولاد عجب ۾ وجھي، الله رڳو گھرندو آھي ته دنيا جي حياتي ۾ اُن سان کين عذاب ڪري ۽ سندن ساہ ڪُفر جي ئي حالت ۾ نڪرن.');
INSERT INTO "amroti" VALUES(9,56,'9:56','۽ الله جو قسم کڻندا آھن ته اُھي اوھان منجھان آھن، ۽ (حقيقت ڪري) اُھي اوھان منجھان نه آھن پر اُھا ڊڄڻي قوم آھي.');
INSERT INTO "amroti" VALUES(9,57,'9:57','جي ڪا پناھ جي جاءِ يا ڪي چُرون يا ڪو گھڙڻ جو ھنڌ لھن ھا ته اُن ڏانھن اُھي ڊوڙندا ئي ڦِرن ھا.');
INSERT INTO "amroti" VALUES(9,58,'9:58','۽ منجھائن ڪي اھڙا آھن جي صدقن (جي وراھڻ) ۾ طعنو ھڻندا آھن، پوءِ جيڪڏھن منجھانئس ڏجين ته سَرھا ٿيندا آھن ۽ جيڪڏھن منجھانئس نه ڏجين ته انھي ئي مھل ڪاوڙبا آھن.');
INSERT INTO "amroti" VALUES(9,59,'9:59','۽ الله ۽ سندس پيغمبر جيڪي کين ڏنو آھي تنھن تي جيڪڏھن راضي ٿين ھا ۽ چون ھا ته اسان کي الله ڪافي آھي اسان کي الله پنھنجي فضل سان ڏيندو ۽ سندس پيغمبر (به ڏيندو) اسين الله ڏانھن اميدوار آھيون (ته اُنھن لاءِ ڀلو ھجي ھا).');
INSERT INTO "amroti" VALUES(9,60,'9:60','صدقا رڳو فقيرن ۽ مسڪينن ۽ ان جي اُڳاڙي ڪندڙن ۽ جن جون دليون (اسلام ڏانھن) ميلاپ ڪرايل آھن (تن لاءِ) ۽ ٻانھن جي آجي ڪرڻ ۽ قرضين ۽ الله جي واٽ ۾ (جھاد ڪرڻ) ۽ مسافرن لاءِ آھن، (اِھو) الله (جي پار) کان حُڪم ٿيل آھي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(9,61,'9:61','۽ منجھائن ڪي اھڙا آھن جي پيغمبر کي ايذائيندا آھن ۽ چوندا آھن ته پر ٻڌڙيو آھي، چؤ ته اوھان لاءِ پر ٻڌڙيو ڀلو آھي الله کي مڃيندو آھي ۽ مؤمنن (جي صلاح) کي (به) مڃيندو آھي ۽ اوھان مان ايمان وارن لاءِ ٻاجھارو آھي، ۽ جيڪي الله جي پيغمبر کي ايذائيندا آھن تن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(9,62,'9:62','اوھان لاءِ الله جو قسم کڻندا آھن ته اوھان کي راضي ڪن، ۽ جيڪڏھن مؤمن آھن ته الله ۽ سندس پيغمبر وڌيڪ حقدار آھن جو اُن کي راضي ڪن.');
INSERT INTO "amroti" VALUES(9,63,'9:63','نه ڄاڻندا آھن ڇا ته جيڪي الله ۽ سندس پيغمبر جي مخالفت ڪندو تنھن لاءِ بيشڪ دوزخ جي باھ آھ؟ منجھس سدائين رھڻ وارو آھي، اِھا وڏي خواري آھي.');
INSERT INTO "amroti" VALUES(9,64,'9:64','منافق مسلمانن تي ڪنھن سورة جي لھڻ کان ڊڄن ٿا ته جيڪي سندن دلين ۾ آھي سو متان مسلمانن کي ڄاڻائي، (اي پيغمبر کين) چؤ ته اوھين پيا ٺـٺولي ڪريو، ڇوته جنھن کان ڊڄندا آھيو سو الله پڌرو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(9,65,'9:65','۽ (اي پيغمبر) جيڪڏھن کائن پڇين ته ضرور چوندا ته بيشڪ کِل ۽ راند ٿي ڪئي سون، (کين) چؤ ته الله ۽ سندس آيتن ۽ سندس پيغمبر سان مسخري ڪيوَ ٿي ڇا؟');
INSERT INTO "amroti" VALUES(9,66,'9:66','بھانو نه ڪريو بيشڪ ايمان کانپوءِ ڪُفر اٿوَ، جيڪڏھن اوھان مان ھڪ ٽوليءَ کان ٽرنداسون ته ٻيءَ ٽوليءَ کي ھن سببان سزا ڏينداسون جو اُھي ڏوھي ھوا.');
INSERT INTO "amroti" VALUES(9,67,'9:67','منافق ۽ منافقياڻيون ھڪ ٻئي جھڙا آھن، (اُھي) مَدن ڪمن جو حُڪم ڪندا آھن ۽ چڱن ڪمن کان جھليندا آھن ۽ پنھنجا ھٿ (خيرات ڏيڻ کان) بند ڪندا آھن، الله کي وساريائون تنھنڪري الله (به) کين وساريو، بيشڪ منافق بي دين آھن.');
INSERT INTO "amroti" VALUES(9,68,'9:68','الله منافقن ۽ منافقياڻين ۽ ڪافرن کي دوزخ جي باھ جو انجام ڏنو آھي منجھس سدائين رھڻ وارا آھن، اھا کين بس آھي، ۽ الله مٿن لعنت ڪئي آھي، ۽ انھيءَ لاءِ ھميشه جو عذاب آھي.');
INSERT INTO "amroti" VALUES(9,69,'9:69','اوھين (به) اھڙا ٿيا آھيو جيڪي اوھان کان اڳ ھوا (اُھي) اوھان کان سگھ ۽ مال ۽ اولاد ۾ گھڻو وڌيڪ ھوا، پوءِ پنھنجي نصيب آھر فائدو ورتائون پوءِ اوھان به پنھنجي نصيب آھر فائدو ورتو جھڙي طرح اوھان کان اڳين پنھنجي نصيب آھر فائدو ورتو ۽ (اوھين به) اُنھن وانگر اجاين بحثن ۾ پئجي ويا آھيو جئن اُھي اجاين بحثن ۾ پيا، انھن جي ڪمائي دنيا ۽ آخرت ۾ ناس ٿي، ۽ اُھي ئي ڇيئي وارا آھن.');
INSERT INTO "amroti" VALUES(9,70,'9:70','کانئن اڳين جي خبر نه پھتي اٿن ڇا جي نُوح ۽ عاد ۽ ثمود جي قوم، ۽ ابراھيم جي قوم ۽ مَدين وارا ۽ اونڌن ٿيلن ڳوٺن وارا ھوا، جو اُنھن ڏانھن سندن پيغمبر پڌرن مُعجزن سان آيا (ته نه مڃيائون)، پوءِ الله جو شان نه ھو جو مٿن ظلم ڪري پر پاڻ تي ظُلم ڪيائون ٿي.');
INSERT INTO "amroti" VALUES(9,71,'9:71','۽ مؤمن ۽ مؤمنياڻيون (پاڻ ۾) ھڪ ٻئي جا مددگار آھن، چڱن ڪمن جو حُڪم ڪندا آھن ۽ خراب ڪمن کان جھليندا آھن ۽ نماز کي قائم ڪندا آھن ۽ زڪوٰة ڏيندا آھن ۽ الله ۽ سندس پيغمبر جي فرمانبرداري ڪندا آھن، اُنھن تي الله سگھوئي ٻاجھ ڪندو، تحقيق الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(9,72,'9:72','الله مؤمنن ۽ مؤمنياڻين سان بھشت جو انجام ڪيو آھي جنھن جي ھيٺان واھيون آھن منجھس سدائين رھڻ وارا آھن ۽ (پڻ) ھميشه رھڻ وارن باغن ۾ چڱين جاين جو (انجام اٿن)، ۽ الله جو راضپو (سڀ کان) تمام وڏو آھي، اھا وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(9,73,'9:73','اي پيغمبر ڪافرن ۽ منافقن سان جھاد ڪر ۽ مٿن سختي ڪر، ۽ سندن جاءِ دوزخ آھي، ۽ اُھا جاءِ بُڇڙي آھي.');
INSERT INTO "amroti" VALUES(9,74,'9:74','الله جو قسم کڻندا آھن ته (پاڻ) نه چيو اٿن، ۽ بيشڪ ڪفر جو ڪلمو چيو اٿن ۽ پنھنجي اسلام (آڻڻ) کانپوءِ ڪُفر ڪيائون ۽ جنھن کي پھچي نه سگھيا تنھن لاءِ زور لاتائون، ۽ اِھو اُنھن رڳو ھن ڳالھ جو بدلو ڏنو جو الله پنھنجي فضل سان ۽ سندس رَسُول کين شاھوڪار ڪيو، ۽ پوءِ جيڪڏھن توبه ڪن ته انھن لاءِ ڀلي آھي، ۽ جيڪڏھن ڦرندا ته الله دُنيا ۽ آخرت ۾ کين ڏکوئيندڙ عذاب سان سزا ڏيندو، ۽ مُلڪ ۾ انھن لاءِ نڪو سنڀاليندڙ ۽ نڪو مددگار آھي.');
INSERT INTO "amroti" VALUES(9,75,'9:75','۽ منجھائن ڪي آھن جن الله سان انجام ڪيو آھي ته جيڪڏھن پنھنجي فضل سان اسان کي ڏنائين ته ضرور خيرات ڏينداسون ۽ ڀلارن مان ٿينداسون.');
INSERT INTO "amroti" VALUES(9,76,'9:76','پوءِ جنھن مھل پنھنجي فضل سان کين مال ڏنائين ته اُن جي ڪنجوسائي ڪيائون ۽ اُھي مُنھن موڙيندڙ ٿي ڦريا.');
INSERT INTO "amroti" VALUES(9,77,'9:77','پوءِ الله سندن دلين ۾ کين سندن ملڻ جي ڏينھن تائين انھي سببان منافقي لاڳو ڪري ڇڏي جو الله سان اُھو انجام نه پاريائون جيڪو ساڻس ڪيائون ۽ اُنھي سببان جو ڪوڙ چوندا رھيا.');
INSERT INTO "amroti" VALUES(9,78,'9:78','نه ڄاڻندا آھن ڇا ته الله سندن ڳجھا ۽ سندن پڌرا (سخن) ۽ سندن ڳجھيون صلاحون ڄاڻندو آھي ۽ تحقيق الله وڏو ڳُجھ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(9,79,'9:79','اِھي اھڙا آھن جو صدقي ڏيندڙن مؤمنن جي صدقي ڏئڻ ۾ طعنا ھڻندا آھن ۽ انھن کي (به طعنا ھڻندا آھن) جيڪي (مزدوري ڪرڻ جي) اوکائي کانسواءِ ڪي نه لھندا آھن ۽ انھن سان مسخريون ڪندا آھن، الله (به) سندن مسخرين جو کين بدلو ڏيندو ۽ انھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(9,80,'9:80','انھن لاءِ بخشش گھُرين يا انھن لاءِ بخشش نه گھُرين، جيڪڏھن اُنھن لاءِ ستر ڀيرا بخشش گھُرين ته به الله انھن کي ڪڏھن نه بخشيندو، اھو ھن سببان آھي جو اُنھن الله ۽ سندس پيغمبر کي نه مڃيو، ۽ الله بي دين قوم کي ھدايت نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(9,81,'9:81','الله جي پيغمبر جي (جھاد تي) وڃڻ کانپوءِ پٺ تي رھندڙ پنھنجي (رھجي) وھڻ تي خوش ٿيا ۽ پنھنجن مالن ۽ پنھنجن جِندن سان الله جي واٽ ۾ جِھاد ڪرڻ ناپسند ڪيائون ۽ (پاڻ ۾) چيائون ته گرميءَ ۾ (جھاد لاءِ) نه نڪرو، (اي پيغمبرکين) چؤ ته دوزخ جي باھ ڏاڍي گرم آھي، جيڪڏھن سمجھن ھا (ته ايئن نه ڪن ھا).');
INSERT INTO "amroti" VALUES(9,82,'9:82','پوءِ ٿورو کلڻ گھرجين ۽ گھڻو روئڻ گھرجين، اُنھن (اعمالن) جي بدلي ۾ جو جيڪي ڪندا آھن.');
INSERT INTO "amroti" VALUES(9,83,'9:83','پوءِ جيڪڏھن الله توکي منجھائن ڪنھن ٽوليءَ ڏانھن موٽائي نئي ته نڪرڻ لاءِ توکان موڪلائيندا پوءِ چؤ ته مون سان ڪڏھن نه نڪرندؤ ۽ مون سان گڏجي ڪنھن دشمن سان ڪڏھن نه وڙھندؤ، بيشڪ اوھين پھريون ڀيرو وھڻ سان راضي ٿيؤ تنھنڪري (ھاڻي به) پوئتي وھندڙن سان گڏجي ويھو.');
INSERT INTO "amroti" VALUES(9,84,'9:84','۽ منجھائن جيڪو مري تنھن تي بلڪل نماز نه پڙھ ۽ نڪي سندس قبر تي بيھ، بيشڪ انھن الله ۽ سندس پيغمبر کي نه مڃيو ۽ اُھي بي دين ٿي مُئا.');
INSERT INTO "amroti" VALUES(9,85,'9:85','۽ تون سندن مال ۽ سندن اولاد تي عجب نه ڪر، الله گھرندو آھي ته ان جي سببان دنيا ۾ ئي کين عذاب ڪري ۽ سندن ساھ ڪفر جي ئي حالت ۾ نِڪرن.');
INSERT INTO "amroti" VALUES(9,86,'9:86','۽ جيڪڏھن ڪا سُورة (ھِن لاءِ) نازل ڪئي ويندي آھي ته الله کي مڃيو ۽ سندس پيغمبر سان گڏجي جھاد ڪريو ته منجھائن آسودا توکان موڪلائيندا آھن ۽ چوندا آھن ته اسان کي ڇڏ ته ويٺلن سان گڏ رھون.');
INSERT INTO "amroti" VALUES(9,87,'9:87','ويٺلن زالن سان گڏ ھُجڻ ۾ راضي ٿيا ۽ سندين دلين تي مُھر ھنئي وئي پوءِ اُھي نه سمجھندا آھن.');
INSERT INTO "amroti" VALUES(9,88,'9:88','پر پيغمبر ۽ جن ساڻس ايمان آندو تن پنھنجن مالن ۽ پنھنجين جِندن سان جھاد ڪيو، ۽ اِھي اُھي آھن جن لاءِ چڱايون آھن، ۽ اُھي ئي ڇٽل آھن.');
INSERT INTO "amroti" VALUES(9,89,'9:89','انھن لاءِ الله اھڙا باغ تيار ڪيا آھن جن جي ھيٺان واھيون وھنديون آھن منجھس سدائين رھڻ وارا آھن، اھا وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(9,90,'9:90','۽ بَدوِين مان بھاني ڪرڻ وارا آيا ته کين موڪل ڏني وڃي ۽ جن الله ۽ سندس رسول کي ڪوڙو ڄاتو سي ويھي رھيا، منجھائن ڪافرن کي ڏکوئيندڙ عذاب سِگھو ئي پھچندو.');
INSERT INTO "amroti" VALUES(9,91,'9:91','نڪي عاجزن تي ۽ نڪي بيمارن تي ۽ نڪي اُھي جو جيڪي خرچين سو نه ٿا لھن تن تي ڪو گناھ آھي جڏھن ته الله ۽ سندس پيغمبر جا خيرخواھ ھجن، ڀلارن تي ڪو ڏوراپو نه آھي، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(9,92,'9:92','۽ نڪي اُنھن تي (ڪو گناھ آھي) جي جڏھن تو وٽ اچن ته کين (وھٽ تي) چاڙھين (۽ تون) چوين ته (ڪو وھٽ) نه ٿو لھان جنھن تي اوھان کي چاڙھيان، تڏھن اُھي موٽن ۽ ھن ڏُک کان سندن اکيون ڳوڙھا ھاريندڙ ھجن ته جيڪي (الله جي واٽ ۾) خرچين سو نه ٿا لھن،');
INSERT INTO "amroti" VALUES(9,93,'9:93','ڏوراپي جي واٽ انھن کانسواءِ ٻـين تي نه آھي جن توکان موڪلايو ۽ اُھي شاھوڪار ھوا، رھيلن زالن سان گڏ ھجڻ ۾ راضي ٿيا ۽ الله سندين دلين تي مُھر ھنئي پوءِ اُھي نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(9,94,'9:94','جڏھن (اوھين) اُنھن ڏانھن موٽندؤ (تڏھن) اوھان وٽ بھانو ڪندا، (انھن کي) چؤ ته بھانو نه ڪريو اوھان (جي ڳالھ) کي ڪڏھن نه مڃينداسون بيشڪ اوھان جي حال کان الله اسان کي خبر ڏني آھي، ۽ الله ۽ سندس پيغمبر اوھان جا عمل سِگھو ئي ڏسندو وري ڳجھ ۽ ظاھر جي ڄاڻندڙ (الله) ڏانھن موٽايا ويندؤ (۽) پوءِ جيڪي ٿي ڪيؤ تنھنجي اوھان کي سُڌ ڏيندو.');
INSERT INTO "amroti" VALUES(9,95,'9:95','جڏھن انھن ڏانھن موٽندؤ (ته) ستت ئي اوھان جي آڏو الله جو قسم ھِن لاءِ کڻندا ته (اوھين) کائن مڙي وڃو، پوءِ کائن مڙي وڃو، بيشڪ اُھي پليت آھن، ۽ جيڪي ٿي ڪمايائون تنھن جي عوض ۾ سندين جاءِ دوزخ آھي.');
INSERT INTO "amroti" VALUES(9,96,'9:96','اوھان وٽ ھن لاءِ قسم کڻندا ته (اوھين) کائن راضي ٿيو، پوءِ جيڪڏھن (اوھين) کائن راضي ٿيندؤ ته (به) الله بي دين ماڻھن کان راضي نه ٿيندو.');
INSERT INTO "amroti" VALUES(9,97,'9:97','بَدوي ڪُفر ۽ منافقيءَ ۾ تمام سخت آھن ۽ ھن (عادت) جوڳا آھن ته الله پنھنجي پيغمبر تي جيڪي حُڪم لاٿا تنھن جون (شرعي) حدون نه ڄاڻن، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(9,98,'9:98','۽ ڳوٺاڻن مان ڪي اھڙا آھن جو خرچ ڪن ٿا سو چَٽي ڄاڻندا آھن ۽ اوھان تي زماني جي ڦيرگھير (اچڻ) جا منتظر آھن، زماني جو بڇڙو ڦيرو مٿن ھجي (شال)، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(9,99,'9:99','۽ ٻھراڙي وارن مان ڪي اھڙا آھن جي الله ۽ قيامت جي ڏينھن کي مڃيندا آھن ۽ جيڪي خرچيندا آھن تنھن کي الله وٽ ويجھائي جو ۽ پيغمبر جي دُعا حاصل ڪرڻ جو وسيلو ڪري وٺندا آھن، بيشڪ اھا انھن لاءِ ويجھائيءَ (جو سبب) آھي الله کين پنھنجي رحمت ھيٺ سگھوئي داخل ڪندو، بيشڪ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(9,100,'9:100','۽ مُھاجرن ۽ انصارن مان (ايمان ۾) اڳرائي ڪرڻ وارا ۽ جن چڱائيءَ سان سندين تابعداري ڪئي اُنھن (سڀني) کان الله راضي آھي ۽ اُھي کانئس راضي آھن ۽ اُنھن لاءِ باغ تيار ڪيا اٿس جن جي ھيٺان واھيون وھنديون آھن منجھس سدائين رھڻ وارا آھن، اھا وڏي مُراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(9,101,'9:101','۽ اوھان جي آس پاس وارن بدوين مان (به) ڪي منافق آھن، ۽ مديني وارن مان (به)، ڪي منافقيءَ تي عادي ٿي ويا آھن، (تون) کين نه ڄاڻندو آھين، اسين انھن کي ڄاڻندا آھيون، سگھوئي اسين کين ٻه ڀيرا عذاب ڪنداسون وري وڏي عذاب ڏانھن موٽايا ويندا.');
INSERT INTO "amroti" VALUES(9,102,'9:102','۽ ٻيا (اھڙا) آھن جن پنھنجا گناھ باسيا آھن (انھن) چڱا ڪم ۽ ٻيا بڇڙا ڪم گڏي سڏي ڪيا آھن، اميد آھي ته الله مٿن ٻاجھ سان موٽندو، بيشڪ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(9,103,'9:103','سندن مالن مان زڪوٰة وصول ڪر ته اُن سان کين (ظاھر ۾ به) پاڪ ڪندو رھين ۽ کين (باطن ۾) سٺو ڪندو رھين ۽ مٿن رحمت جي دُعا گھر، ڇوته تنھنجي دُعا انھن لاءِ آرام آھي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(9,104,'9:104','نه ڄاڻندا آھن ڇا ته الله ئي پنھنجن ٻانھن جي توبه قبول ڪندو آھي ۽ (سندين) خيراتون قبول ڪندو آھي ۽ بيشڪ الله ئي توبه قبول ڪندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(9,105,'9:105','۽ چؤ ته عمل ڪريو پوءِ الله ۽ سندس پيغمبر ۽ مؤمن (سڀئي) اوھان جا عمل ڏسندا، ۽ ڳُجھ ۽ ظاھر جي ڄاڻندڙ (الله) ڏانھن سگھو موٽايا ويندؤ پوءِ جيڪي ڪندا آھيو تنھن جي اوھان کي سُڌ ڏيندو.');
INSERT INTO "amroti" VALUES(9,106,'9:106','۽ ٻيا الله جي حُڪم لاءِ رھايل آھن ته يا ته کين عذاب ڪري يا کين معاف ڪري، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(9,107,'9:107','۽ (انھن مان اھڙا به آھن) جن نقصان پُھچائڻ ۽ ڪفر ڪرڻ ۽ مسلمانن جي وچ ۾ ڏڦيڙ وجھڻ ۽ جن (ماڻھن) الله ۽ سندس پيغمبر سان اڳ ويڙھ ڪئي آھي تن واسطي گھٽ ٺاھڻ لاءِ (ھڪ) مسجد بڻائي آھي، ۽ (اُھي) ضرور قسم کڻندا ته چڱائيءَ کانسواءِ اسانجو (بڻائڻ ۾) ٻيو ڪو ارادو نه آھي، ۽ الله شاھدي ڏئي ٿو ته اُھي ڪوڙا آھن.');
INSERT INTO "amroti" VALUES(9,108,'9:108','منجھس اصلي نه بيھ، جا مسجد پھرين ڏينھن پرھيزگاريءَ تي اڏائي ويئي آھي تنھن ۾ توکي بيھڻ (يعني نماز پڙھڻ ۽ پڙھائڻ) وڌيڪ لائق آھي، جنھن ۾ اھڙا مڙس آھن جي پاڪ رھڻ پسند ڪندا آُھن، ۽ الله پاڪ رھڻ وارن کي پسند ڪندو آھي.');
INSERT INTO "amroti" VALUES(9,109,'9:109','ڀلا جنھن پنھنجي اڏاوت الله جي ڊڄ ۽ (سندس) رضامنديءَ تي اڏي سو ڀلو آھي يا اُھو جنھن پنھنجي اڏاوت نئن جي ڪرندڙ ڀر تي اَڏي؟ پوءِ اُھا اڏيندڙ سميت دوزخ جي باھ ۾ ڪري، ۽ الله ظالمن جي قوم کي ھدايت نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(9,110,'9:110','جو بُڻياد بڻايائون سو سدائين سندين دلين ۾ شڪ (جو سبب) رھندو سندن دلين جي وڍڻ کانسواءِ نه ويندو، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(9,111,'9:111','الله مؤمنن کان سندن سر ۽ مال ھِن ڪري ڳڌا جو پڪ بھشت انھن لاءِ آھي، (اِھي) اُن (انجام) تي الله جي واٽ ۾ وڙھندا آھن پوءِ ماريندا (به) آھن ۽ ماربا (به) آھن، جو انجام توريت ۽ انجيل ۽ قرآن ۾ پڪي طرح ٿيل آھي، ۽ الله کان وڌيڪ پنھنجي انجام کي پورو ڪندڙ ڪير آھي؟ پوءِ جنھن سودي جو اوھان واپار ڪيو آھي تنھن تي خوش ٿيو، ۽ اِھا ئي وڏي مُراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(9,112,'9:112','اِھي توبه ڪندڙ عبادت ڪندڙ ساراھ ڪندڙ (خُدا جي واٽ ۾) مُسافري ڪندڙ رُڪوع ڪندڙ سجدو ڪندڙ چڱائيءَ جو حُڪم ڪندڙ ۽ مدائيءَ کان جھليندڙ ۽ الله جي حدن کي نگاہ ۾ رکندڙ آھن، ۽ (اھڙن) مؤمنن کي خوشخبري ڏي.');
INSERT INTO "amroti" VALUES(9,113,'9:113','پيغمبر ۽ مؤمنن کي مشرڪن لاءِ کين (ھن ڳالھ جي) پڌري ٿيڻ کانپوءِ ته اُھي دوزخي آھن بخشش گھرڻ نه جڳائي توڻيڪ مائٽيءَ وارا ھجن.');
INSERT INTO "amroti" VALUES(9,114,'9:114','۽ ابراھيم جو پنھنجي پيءُ لاءِ بخشش گھرڻ انجام کانسواءِ نه ھو جو اُن ساڻس انجام ڪيو ھو، پوءِ جنھن دم کيس پڌرو ٿيو ته اُھو الله جو ويري آھي (ته ھڪدم) کانئس بيزار ٿيو، بيشڪ ابراھيم بلڪل ڪئنري دل وارو بُردبار ھو.');
INSERT INTO "amroti" VALUES(9,115,'9:115','۽ الله جو ڪم نه آھي جو ڪنھن قوم کي سڌي رستي ڏيکارڻ کانپوءِ گمراہ ڪري جيسين اُھي (ڳالھيون) اُنھن لاءِ پڌريون (نه) ڪري جن کان انھن کي بچڻ گھرجي بيشڪ الله ھر شيء ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(9,116,'9:116','بيشڪ آسمانن ۽ زمين جي بادشاھي الله جي ئي آھي، (اُھو) جياري ٿو ۽ ماري ٿو، ۽ الله کانسواءِ اوھان جو نڪو دوست ۽ نڪو مدد ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(9,117,'9:117','پيغمبر ۽ انھن مھاجرن ۽ انصارن تي بيشڪ الله ٻاجھ سان موٽيو جن سوڙھ جي وقت ۾ پيغمبر جي تابعداري ڪئي اُن کانپوءِ جو منجھائن ھڪ ٽوليءَ جون دليون لڏي وڃڻ تي ويجھيون ھيون وري (الله) مٿن ٻاجھ سان موٽيو، ڇوته اُھو مٿن شفقت ڪندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(9,118,'9:118','۽ انھن ٽن تي (به) جيڪي پوئتي رھايل، ايتري قدر جو زمين ايتري ويڪرائي ھوندي به مٿن سوڙھي ٿي وئي ھئي ۽ سندن ساہ مٿن منجھيا ھوا ۽ ڀانيائون ته (ھاڻي) الله کانسواءِ ڪا جاءِ پناھ جي نه آھي، وري مٿن ھِن لاءِ ٻاجھ سان موٽيو ته توبه ڪن، تحقيق الله توبه قبول ڪندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(9,119,'9:119','اي ايمان وارؤ الله کان ڊڄو ۽ سچن سان گڏ ھجو.');
INSERT INTO "amroti" VALUES(9,120,'9:120','مديني وارن ۽ سندس آس پاس وارن بدوين کي نڪي الله جي پيغمبر کان (جھاد ۾) پوءِ تي رھڻ ۽ نڪي سندس وجود کان پاڻ کي پيارو رکڻ جڳائي، اھو ھن ڪري جو کين نڪي اُڃ ۽ نڪي ڏُک ۽ نڪي بُک الله جي واٽ ۾ پھچندي آھي ۽ نڪي اھڙي ھنڌ ويندا آھن جتي ڪافر ڪاوڙجن ۽ نڪي دشمن کي (ڪو) اھنج پھچائيندا آھن پر انھن لاءِ ھر ھڪ (ڳالھ) بابت چڱو عمل لکيو ويندو آھي، تحقيق الله ڀلارن جو اَجر نه وڃائيندو آھي.');
INSERT INTO "amroti" VALUES(9,121,'9:121','۽ ڪوبه خرچ ٿورو يا گھڻو نه ڪندا آھن ۽ نڪو ڪو ميدان لنگھندا آھن پر انھن لاءِ (چڱو عمل) ھن لاءِ لکيو ويندو آھي ته جيڪو تمام چڱو ڪم ڪندا آھن تنھنجو الله کين بدلو ڏئي.');
INSERT INTO "amroti" VALUES(9,122,'9:122','۽ مؤمنن کي نه جڳائيندو آھي جو مڙيئي (گڏ) نڪرن، پوءِ ٽوليءَ مان ڪي ڄڻا منجھائن ھن لاءِ ڇونه نڪرندا آھن ته دين ۾ عالِم ٿين ۽ جڏھن پنھنجي قوم ڏانھن موٽي وڃن ته ان کي ڊيڄارين ته مانَ اُھي ڊڄن.');
INSERT INTO "amroti" VALUES(9,123,'9:123','اي مؤمنو ڪافرن مان جيڪي اوھان جي آس پاس آھن تن سان وڙھو ۽ ڀَلي ته (اُھي) اوھان ۾ ڪا ڏاڍائي ڏسن، ۽ ڄاڻو ته الله پرھيزگارن سان گڏ آھي.');
INSERT INTO "amroti" VALUES(9,124,'9:124','۽ جڏھن ڪابه سُورة لاھبي آھي ته منجھائن ڪو اھڙو آھي جو چوندو آھي ته ھن سورة اوھان مان ڪھڙي جو ايمان وڌايو آھي؟ پوءِ جن ايمان آندو آھي تن جي ايمان کي وڌايو اٿس ۽ اُھي خوش ٿيندا آھن.');
INSERT INTO "amroti" VALUES(9,125,'9:125','۽ جن جي دلين ۾ بيماري آھي تن کي پليتي تي پليتي وڌائي اٿس ۽ اُھي ڪافر ٿي مُئا.');
INSERT INTO "amroti" VALUES(9,126,'9:126','نه ڏسندا آھن ڇا ته ھر سال ھڪ ڀيرو يا ٻه ڀيرا سزا ڏني ويندي اٿن؟ وري نڪي توبه ڪندا آھن ۽ نڪي اُھي نصيحت وٺندا آھن.');
INSERT INTO "amroti" VALUES(9,127,'9:127','۽ جڏھن ڪا سورة (انھن بابت) نازل ڪبي آھي (ته) انھن مان ھڪڙا ٻـين ڏانھن (شرمندگيءَ کان) نھاريندا آھن، (۽ چوندا آھن ته) ڪنھن اوھانکي ڏٺو آھي ڇا؟ وري (گھرن ڏانھن) موٽندا آھن، الله سندن دليون ھن سببان ڦيرايون آھن جو اُھا بي سمجھ قوم آھي.');
INSERT INTO "amroti" VALUES(9,128,'9:128','(اي ماڻھو) بيشڪ اوھان جي جنس منجھان اوھان وٽ پيغمبر آيو آھي اوھان جو ڏُک مٿس اوکو آھي اوھان (جي ھدايت) تي حَرِيص آھي مسلمانن تي شفقت ڪندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(9,129,'9:129','پوءِ جيڪڏھن ڦرن ته چؤ ته مون کي الله ڪافي آھي، اُن کانسواءِ ڪو عبادت جو لائق نه آھي، مٿس ڀروسو ڪيم ۽ اُھو وڏي عرش جو مالڪ آھي.');
INSERT INTO "amroti" VALUES(10,1,'10:1','الٓرٰ، ھيءُ آيتون حِڪمت واري ڪتاب جون آھن.');
INSERT INTO "amroti" VALUES(10,2,'10:2','ماڻھن کي (ھيءَ ڳالھ) عجب ۾ وجھندي آھي ڇا ته منجھائن ھڪ مڙس ڏانھن وحي موڪليوسون ته ماڻھن کي ڊيڄار ۽ جن ايمان آندو تن کي خوشخبري ڏي ته انھن لاءِ سندن پالڻھار وٽ وڏومرتبو آھي، ڪافرن چيو ته ھي پڌرو جادوگر آھي.');
INSERT INTO "amroti" VALUES(10,3,'10:3','بيشڪ اوھان جو پالڻھار اُھو الله آھي جنھن آسمانن ۽ زمين کي ڇھن ڏينھن ۾ بڻايو (۽) وري عرش ڏانھن توجّه ڪيائين اھوئي ڪمن کي رِٿيندو آھي، ڪوئي سفارش ڪندڙ نه آھي پر سندس اجازت کانپوءِ، اھوئي اوھان جو پالڻھار الله آھي تنھنڪري سندس عبادت ڪريو، پوءِ ڇونه نصيحت وٺندا آھيو؟');
INSERT INTO "amroti" VALUES(10,4,'10:4','اوھان مڙني جو ڏانھس موٽڻ آھي، الله جو انجام سچو آھي، اھوئي پھريون (به) بڻائيندو آھي وري (به) کين ھن لاءِ موٽائي بڻائيندو ته جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي انصاف سان بدلو ڏئي، ۽ جن نه مڃيو تن لاءِ ٽھڪندڙ پاڻي ۽ ڏکوئيندڙ عذاب انھيءَ ڪري آھي جو ڪُفر ڪيو ھئائون.');
INSERT INTO "amroti" VALUES(10,5,'10:5','اُھو (الله) آھي جنھن سج کي چمڪندڙ ۽ چنڊ کي روشن ڪيو ۽ اُن (چنڊ) جون منزلون ھن لاءِ مَقرّر ڪيون ته اوھين ورھن جو ڳاڻيٽو ۽ حساب ڄاڻو، الله اِھو سچ کانسواءِ نه بڻايو آھي، ڄاڻندڙ قوم لاءِ کولي نشانيون بيان ڪندو آھي.');
INSERT INTO "amroti" VALUES(10,6,'10:6','بيشڪ رات ۽ ڏينھن جي ڦيرگھير (۾) ۽ الله جيڪي آسمانن ۽ زمين ۾ بڻايو تنھن ۾ پرھيزگار قوم لاءِ ضرور نشانيون آھن.');
INSERT INTO "amroti" VALUES(10,7,'10:7','بيشڪ جيڪي اسان جي ملڻ جو آسرو نه ٿا رکن ۽ دنيا جي حياتيءَ ساڻ ريجھي ويا ۽ اُنھيءَ سان آرامي ٿي رھيا ۽ اُھي جيڪي اسان جي آيتن کان بيخبر رھيا.');
INSERT INTO "amroti" VALUES(10,8,'10:8','اھي جيڪي ڪمائيندا رھيا تنھن سببان انھن جو مڪان باھ آھي.');
INSERT INTO "amroti" VALUES(10,9,'10:9','(۽) جن مڃيو ۽ چڱا ڪم ڪيا تن کي سندن پالڻھار سندن ايمان سببان (اھڙن بھشتن ڏانھن) سڌو رستو ڏيکاريندو، جن جي ھيٺان نعمتن وارن باغن ۾ واھيون وھنديون آھن.');
INSERT INTO "amroti" VALUES(10,10,'10:10','منجھن سندين دُعا (ھيءَ) آھي ته اي الله توکي پاڪائي جڳائي ۽ منجھن سندن آڌرڀاءُ سلام آھي، سندين پوئين دُعا ھيءَ آھي ته سڀ ساراھ جھانن جي پالڻھار الله کي جُڳائي.');
INSERT INTO "amroti" VALUES(10,11,'10:11','۽ جيڪڏھن الله ماڻھن لاءِ مدائي جلد موڪلي ھا جھڙو اُھي پنھنجي چڱائي جلد گھرندا آھن ته سندين (ٺھرايل) مدت انھن لاءِ پوري ڪئي وڃي ھا، پوءِ جيڪي اسان جي ملڻ جي اميد نه رکندا آھن تن کي سندن گمراھيءَ ۾ حيران ڇڏيندا آھيون.');
INSERT INTO "amroti" VALUES(10,12,'10:12','۽ جنھن مھل ماڻھوءَ کي ڪو ڏُک پھچندو آھي (ته) پنھنجي پاسي ڀر ويٺي يا بيٺي اسان کي سڏيندو آھي، پوءِ جنھن مھل کانئس سندس ڏک لاھيندا آھيون (ته اُن وقت اھڙي ويساري ۾ پئجي) ھليو ويندو آھي (جو) ڄڻڪ کيس جا اوکائي پُھتي ھئي تنھن (جي لاھڻ) لاءِ اسان کي سڏيو ئي نه ھوائين، اھڙي طرح حد کان لنگھندڙ جيڪي ڪندا آھن سو اُنھن لاءِ سينگاريو ويو آھي.');
INSERT INTO "amroti" VALUES(10,13,'10:13','۽ بيشڪ اوھان کان اڳ (گھڻن) جُڳن وارن جڏھن ظلم ڪيو ته (کين) ھلاڪ ڪيو اٿون ۽ وٽن سندن پيغمبر چِٽن معجزن سان آيا ۽ اھڙا نه ھوا جو ايمان آڻين، اھڙي طرح ڏوھارڻ قوم کي سزا ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(10,14,'10:14','وري کائن پوءِ مُلڪ ۾ اوھان کي سندين جاڳھن تي وھاريوسون ته اوھين ڪھڙي طرح ڪم ڪريو ٿا.');
INSERT INTO "amroti" VALUES(10,15,'10:15','۽ جڏھن اسان جون چٽيون آيتون کين پڙھي ٻڌائبيون آھن (ته) جيڪي اسان جي ملڻ جي اُميد نه رکندا آھن سي چوندا آھن ته ھن کانسواءِ ٻيو قرآن آڻ يا کيس مٽاءِ، چؤ ته پنھنجي طرفان ان جي مٽائڻ جو مون کي اختيار ڪونھي، جيڪي مونکي وحي ڪيو ويندو آھي تنھن کانسواءِ (ٻئي ڪنھن جي) تابعداري نه ڪندو آھيان، بيشڪ آءٌ جيڪڏھن پنھنجي پالڻھار جي نافرماني ڪريان ته وڏي ڏينھن جي عذاب کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(10,16,'10:16','چؤ ته جيڪڏھن الله گھري ھا ته آءٌ اُن (قرآن) کي اوھان تي نه پڙھان ھا ۽ نڪي (الله) اُن سان اوھان کي ڄاڻي ھا، بيشڪ آءٌ اوھان ۾ ھن کان اڳ (سڄي) ڄمار رھيس (ته نه پڙھيم)، پوءِ ڇونه ٿا سمجھو؟');
INSERT INTO "amroti" VALUES(10,17,'10:17','پوءِ ان کان وڌيڪ ظالم ڪير آھي جو الله تي ڪُوڙو ٺاھ ٺاھي يا سندس آيتن کي ڪوڙ ڄاڻي، سچ آھي ته ڏوھاري نه ڇُٽندا.');
INSERT INTO "amroti" VALUES(10,18,'10:18','۽ الله کانسواءِ جن کي پوڄيندا آھن سي نڪي کين ڏک پھچائيندا آھن ۽ نڪي کين سُک پھچائيندا آھن ۽ چوندا آھن ته ھي الله وٽ اسان جا شفاعت ڪندڙ آھن، چؤ ته اوھين الله کي اھڙيءَ شيءَ بابت چتائيندا آھيو ڇا جيڪا شيء (ھو) نڪي آسمانن ۾ ۽ نڪي زمين ۾ ڄاڻندو آھي، اُھو پاڪ آھي ۽ جنھن شيء کي ساڻس شريڪ ڪندا آھيو تنھن کان اُھو مٿاھون آھي.');
INSERT INTO "amroti" VALUES(10,19,'10:19','۽ (اصل) ماڻھو ھڪ ٽوليءَ کانسواءِ نه ھوا پوءِ اختلاف ڪيائون، ۽ جيڪڏھن تنھنجي پالڻھار جو حُڪم اڳ ٿيل نه ھجي ھا ته جنھن (ڳالھ) بابت اختلاف ڪن ٿا تنھنجو سندن وچ ۾ فيصلو پورو ڪيو وڃي ھا.');
INSERT INTO "amroti" VALUES(10,20,'10:20','۽ چوندا آھن ته سندن پالڻھار کان مٿس ڪو مُعجزو ڇونه لاٿو ويو آھي؟ چؤ ته ڳُجھ (جي خبر) الله کانسواءِ ٻئي ڪنھن کي نه آھي پوءِ انتظار ڪريو، آءٌ به اوھان سان گڏ انتظار ڪندڙ آھيان.');
INSERT INTO "amroti" VALUES(10,21,'10:21','۽ جڏھن ماڻھن کي اوکائي پھچڻ کانپوءِ (پنھنجي) ٻاجھ (سان سُک جو مزو) چکائيندا آھيون (ته) اُتي جو اُتي اُنھن کي اسانجي آيتن ۾ بدگمانيون ٿينديون آھن، چؤ ته الله رِٿ رٿڻ ۾ ڏاڍو تڪڙو آھي، بيشڪ جيڪي اوھين بڇڙيون رٿون ڪندا آھيو سي اسانجا ملائڪ لکندا آھن.');
INSERT INTO "amroti" VALUES(10,22,'10:22','اُھو (الله) آھي جو اوھان کي بر ۽ دريائن ۾ گھمائيندو آھي، ايتري تائين جو جڏھن ٻيڙين ۾ ھوندا آھيو، ۽ (اُھي) پاتڻين سميت سڻائن وائن سان ھلنديون آھن ۽ اُنھن سان سرھا ٿيندا آھن ته اوچتو کين اڻائو واءُ پھچندو آھي ۽ کين چوڌاري لھر ايندي آھي ۽ ڀائيندا آھن ته بيشڪ (اُھا) کين ويڙھي ويئي (ته) الله جي عبادت ۾ سچائيءَ وارا ٿي کيس سڏيندا آھن، (۽ چوندا آھن) ته جيڪڏھن ھن (اوکائي) کان اسان کي بچائين ته ضرور شڪراني ڪندڙن مان ٿينداسون.');
INSERT INTO "amroti" VALUES(10,23,'10:23','پوءِ جنھن مھل کين ڇڏايائين (ته) اُتي جو اُتي مُلڪ ۾ ناحق شرارت ڪندا آھن، اي ماڻھؤ اوھان جو شرارتون اوھان تي ئي آھن (ھيءُ) دُنيا جي حياتي جو فائدو (لڌو) وري اوھان جو موٽڻ اسان ڏانھن آھي پوءِ جيڪي ڪندا آھيو تنھن جي اوھان کي سُڌ ڏينداسون.');
INSERT INTO "amroti" VALUES(10,24,'10:24','دُنيا جي حياتي جو مثال رڳو انھي پاڻي وانگر آھي جنھن کي آسمان کان وسايوسون پوءِ ساڻس زمين جا (اُھي) اوڀڙ رليا مليا جن مان ماڻھو ۽ ڍور کائيندا آھن، ايتري تائين جو جنھن مھل زمين پنھنجي ساوڪ ورتي ۽ سينگاري ۽ سندس رھڻ وارن ڀانيو ته پاڻ مٿس وسوارا آھن (تنھن مھل) اوچتو اُن کي رات جو يا ڏينھن جو اسانجو حُڪم پھتو پوءِ اُن کي لُڻيل (ناس) ڪري ڇڏيوسون ڄڻڪ ڪالھ ئي نه ھُئي، اھڙي طرح سمجھندڙ قوم لاءِ کولي نشانيون بيان ڪندا آھيون.');
INSERT INTO "amroti" VALUES(10,25,'10:25','۽ الله سلامتيءَ جي گھر ڏانھن سڏيندو آھي، ۽ جنھن کي وڻيس تنھن کي سڌي واٽ ڏانھن ھدايت ڪندو آھي.');
INSERT INTO "amroti" VALUES(10,26,'10:26','جن چڱائي ڪئي تن لاءِ (اَھڙي) چڱائي ۽ بلڪ وڌيڪ آھي، ۽ سندن منھن کي نڪي ڪارنھن ۽ نڪي خواري ويڙھيندي، اھي بھشتي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(10,27,'10:27','۽ جن بڇڙائي ڪمائي تن لاءِ اُن جھڙي بڇڙائي ۽ کين خواري ڍڪيندي، اُنھن کي الله (جي عذاب) کان ڪو بچائيندڙ نه آھي، ڄڻڪ سندن مُنھن کي اونداھي رات مان ذرو ڍڪايو ويو آھي، اھي دوزخي آھن، منجھس سدائين رھن وارا آھن.');
INSERT INTO "amroti" VALUES(10,28,'10:28','۽ جنھن ڏينھن انھن مڙني کي گڏ ڪنداسون وري مشرڪن کي چونداسون ته اوھين ۽ اوھان جا (الله سان ٺھرايل) شريڪ پنھنجي ھنڌ بيھو، پوءِ سندن وچ ۾ جدائي وجھنداسون ۽ سندن شريڪ چوندا ته (اوھين) اسان کي نه پوڄيندا ھيؤ.');
INSERT INTO "amroti" VALUES(10,29,'10:29','پوءِ اسان جي ۽ اوھان جي وچ ۾ الله شاھد بس آھي ته اسين اوھان جي پوڄا کان ضرور بي خبر ھواسون.');
INSERT INTO "amroti" VALUES(10,30,'10:30','اُن ھنڌ سڀ ڪنھن ماڻھو جيڪي اڳي موڪليو ھوندو سو (اُھو) لھندو ۽ پنھنجي سچي ڌڻي الله ڏانھن موٽايا ويندا ۽ جيڪو ٺاھ ٺاھيائون ٿي سو کائن ڀلجي ويندو.');
INSERT INTO "amroti" VALUES(10,31,'10:31','چؤ ته اوھان کي آسمان ۽ زمين مان ڪير روزي ڏيندو آھي؟ ڪنن ۽ اکين جو ٻيو ڪير مالڪ آھي ڇا؟ ۽ ڪير جيئري کي مُئي مان ڪڍندو آھي؟ ۽ ڪير مُئي کي جيئري مان ڪڍندو آھي؟ ۽ ڪير ڪمن کي رٿيندو آھي، پوءِ سُتت ئي چوندا ته الله، پوءِ چؤ ته ڇونه ڊڄندا آھيو؟');
INSERT INTO "amroti" VALUES(10,32,'10:32','پوءِ اُھو ئي اوھان جو سچو پالڻھار الله آھي، پوءِ سچ کانپوءِ گمراھيءَ کانسواءِ (ٻيو) ڇا آھي، پوءِ ڪيڏي جو ڪيڏي ڦيرايا وڃو ٿا؟');
INSERT INTO "amroti" VALUES(10,33,'10:33','اھڙي طرح تنھنجي پالڻھار جو حُڪم انھن بدڪارن تي لازم ٿيو آھي جو اُھي ايمان نه آڻيندا.');
INSERT INTO "amroti" VALUES(10,34,'10:34','چؤ ته اوھان جي معبودن مان ڪو آھي ڇا جو خلق کي نئي سر بڻائي وري کين (ماري) موٽائي جياري؟ چؤ ته الله نئي سر خلق کي بڻائي ٿو وري کيس موٽائي جياريندو پوءِ اوھين ڪيڏي جو ڪيڏي ڦيرايا ويندا آھيو.');
INSERT INTO "amroti" VALUES(10,35,'10:35','چؤ ته اوھان جي معبودن مان ڪو آھي جو حق ڏانھن سِڌو رستو ڏيکاريندو آھي، چؤ ته الله سِڌو رستو ڏيکاريندو آھي، پوءِ جيڪو حق ڏانھن سِڌو رستو ڏيکاري سو تابعداري ڪرڻ جو وڌيڪ حقدار آھي يا اُھو جيڪو (پاڻ) رستي ڏيکارڻ کانسواءِ رستو نه لھندو ھُجي؟ پوءِ اوھان کي ڇا ٿيو، ڪھڙو نبيرو ڪريو ٿا؟');
INSERT INTO "amroti" VALUES(10,36,'10:36','۽ اُنھن مان گھڻا رڳو گُمان تي ھلندا آھن، بيشڪ گُمان سچ جي پورائي ڪجھ به نه ڪندو آھي، تحقيق جيڪي ڪندا آھن سو الله ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(10,37,'10:37','۽ ھي قُرآن الله کان سواءِ پاڻون ٺھيل نه آھي پر کانئس اڳ جيڪي ڪتاب آھن تن جو سچو ڪندڙ آھي ۽ حُڪمن کي کولي بيان ڪندڙ آھي منجھس ڪو شڪ نه آھي جھانن جي پالڻھار کان آھي.');
INSERT INTO "amroti" VALUES(10,38,'10:38','يا چوندا آھن ته (محمد صلى الله عليه وسلم) اُھو پاڻون ٺاھيو آھي، چؤ ته جيڪڏھن سچا آھيو ته جھڙيس ڪا سورة آڻيو ۽ الله کان سواءِ جنھن کي (سڏي) سگھو تنھن کي سڏيو.');
INSERT INTO "amroti" VALUES(10,39,'10:39','بلڪ جنھن علم کي پھچي نه سگھيا تنھن کي ڪوڙ ڀانيائون ۽ اڃا وٽن سندس وعدن جي سچائي نه آئي آھي، جيڪي کائن اڳ ھوا تن به اھڙي طرح ڪوڙ ڀانيو ھو پوءِ ڏس ته ظالمن جي پڇاڙي ڪھڙي طرح ٿي؟');
INSERT INTO "amroti" VALUES(10,40,'10:40','۽ منجھائن ڪو اُن (قرآن) تي ايمان آڻيندو آھي ۽ منجھائن ڪو اُن تي ايمان نه آڻيندو آھي، تنھنجو پالڻھار فساد ڪندڙن کي چڱو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(10,41,'10:41','۽ جيڪڏھن توکي ڪوڙو ڀائين ته چؤ مون لاءِ پنھنجو عمل ۽ اوھان لاءِ پنھنجو عمل آھي، جيڪي (آءٌ) ڪريان ٿو تنھن کان اوھين بيزار آھيو ۽ جيڪي (اوھين) ڪريو ٿا تنھن کان آءٌ بيزار آھيان.');
INSERT INTO "amroti" VALUES(10,42,'10:42','۽ منجھائن ڪي توڏانھن (ڪن ڏيئي قرآن کي) ٻڌندا آھن، پوءِ تون ٻوڙن کي ٻڌائيندين ڇا جيتوڻيڪ نه سمجھندا ھجن؟');
INSERT INTO "amroti" VALUES(10,43,'10:43','۽ منجھائن ڪوئي توڏانھن نھاريندو آھي، پوءِ انڌن کي سِڌو رستو ڏيکاريندين ڇا؟ جيتوڻيڪ ڏسندا ئي نه ھُجن.');
INSERT INTO "amroti" VALUES(10,44,'10:44','الله ماڻھن تي ڪُجھ ظلم نه ڪندو آھي پر ماڻھو پاڻ تي ظلم ڪندا آھن.');
INSERT INTO "amroti" VALUES(10,45,'10:45','۽ جنھن ڏينھن (الله) کين گڏ ڪندو تنھن ڏينھن ڄڻڪ (دنيا ۾) ڏينھن جي ھڪ گھڙيءَ کانسواءِ (وڌيڪ) ترسيائي نه ھوا پاڻ ۾ ھڪ ٻئي کي سڃاڻندا، بيشڪ جن الله جي ديدار کي ڪوڙ ڀانيو تن ڇيئو پاتو ۽ ھدايت وارا نه ھوا.');
INSERT INTO "amroti" VALUES(10,46,'10:46','۽ جيڪو انجام ساڻن ڪريون ٿا تنھن منجھان ڪجھ جي توکي ڏيکاريون ٿا يا توکي فوت ڪريون ته (ھر حال ۾) سندن موٽڻ اسان ڏانھن آھي وري جيڪي ڪندا آھن تنھن تي الله شاھد آھي.');
INSERT INTO "amroti" VALUES(10,47,'10:47','۽ سڀ ڪنھن اُمت لاءِ ھڪ پيغمبر آھي، پوءِ جڏھن سندن پيغمبر آيو (ته) سندن وچ ۾ اِنصاف سان نبيرو ڪيو ويو ۽ انھن تي ظلم نه ڪيو ويو.');
INSERT INTO "amroti" VALUES(10,48,'10:48','۽ چوندا آھن ته جيڪڏھن اوھين سچا آھيو ته اھو انجام ڪڏھن ٿيندو.');
INSERT INTO "amroti" VALUES(10,49,'10:49','چؤ ته (آءٌ) الله جي مرضيءَ کانسواءِ پاڻ کي نڪي ڏک ۽ نڪي سُک پھچائي سگھان ٿو، سڀ ڪنھن اُمّت لاءِ ٺھرايل مُدت آھي، جڏھن سندين ٺھرايل مُدّت پُھچي ٿي ته ھڪ گھڙي دير نه ڪندا ۽ نڪي اڳڀرو ويندا.');
INSERT INTO "amroti" VALUES(10,50,'10:50','چؤ ته مون کي خبر ڏيو ته جيڪڏھن سندس عذاب رات جو يا ڏينھن جو اوھان کي پُھچي ته (ڇا ڪري سگھندؤ) ڏوھاري ان کي ڪھڙو جلد گھُرندا آھن.');
INSERT INTO "amroti" VALUES(10,51,'10:51','وري جنھن مھل (اُھو) پھچندو (ته) ان تي ايمان آڻيندؤ ڇا، (۽ چيو ويندن ته) ھاڻي (مڃيؤ)؟ ۽ بيشڪ (اڳي) ان کي تڪڙو گھرندا ھُيؤ.');
INSERT INTO "amroti" VALUES(10,52,'10:52','وري ظالمن کي چيو ويندو ته ھميشگيءَ وارو عذاب چکو، جيڪي اوھين ڪمائيندا ھيؤ تنھن کانسواءِ اوھان کي ٻيو ڪو بدلو نه ڏبو.');
INSERT INTO "amroti" VALUES(10,53,'10:53','۽ توکان خبر پڇندا آھن ته اھو (وعدو) سچ آھي؟ چؤ ته ھائو! مون کي پنھنجي پالڻھار جو قسم آھي ته ضرور سچ آھي، ۽ اوھين ٿڪائڻ وارا نه آھيو.');
INSERT INTO "amroti" VALUES(10,54,'10:54','۽ جيڪڏھن سڀڪنھن ظلم ڪندڙ جيءَ کي جيڪي زمين ۾ آھي سو ھجي ھا (اُھو) ضرور پنھنجو عوض ڏئي ڇڏي ھا، ۽ جڏھن عذاب ڏسندا ته ڳجھو لڄي ٿيندا، ۽ سندن وچ ۾ انصاف سان نبيرو ڪيو ويندو ۽ انھن تي ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(10,55,'10:55','خبردار بيشڪ جيڪي آسمانن ۽ زمين ۾ آھي سو الله جو آھي، خبردار الله جو انجام سچو آھي پر اُنھن مان گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(10,56,'10:56','اُھو جياريندو آھي ۽ ماريندو آھي ۽ اوھين ڏانھس موٽايا ويندؤ.');
INSERT INTO "amroti" VALUES(10,57,'10:57','اي ماڻھو اوھان جي پالڻھار کان اوھان وٽ نصيحت ۽ جيڪا (بيماري) سينن ۾ آھي تنھن لاءِ شفا ۽ مؤمنن لاءِ ھدايت ۽ رحمت آئي آھي.');
INSERT INTO "amroti" VALUES(10,58,'10:58','چؤ ته الله جي فضل سان ۽ سندس ٻاجھ سان (نازل ٿيو آھي) پوءِ اُن سان خوش ٿيڻ گھرجين، جو گڏ ڪندا آھن تنھن کان اُھو ڀلو آھي.');
INSERT INTO "amroti" VALUES(10,59,'10:59','چؤ ته ڪي ڏسو ٿا ته الله روزيءَ مان جيڪي اوھان ڏانھن لاٿو آھي پوءِ منجھانئس اوھان (پاڻ) ڪُجھ حرام ۽ ڪُجھ حلال ڪيو آھي، چؤ ته الله اوھان کي موڪل ڏني آھي ڇا؟ يا الله تي ٺاھ ٺاھيندا آھيو؟');
INSERT INTO "amroti" VALUES(10,60,'10:60','۽ جيڪي الله تي ڪوڙ ٺاھيندا آھن تن کي قيامت جي ڏينھن ڪھڙو گمان آھي (ته کين) عذاب نه ڪندو ڇا؟ بيشڪ الله ماڻھن تي ضرور وڏي فضل ڪرڻ وارو آھي پر اُنھن مان گھڻا شڪرانو نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(10,61,'10:61','۽ ڪنھن حال ۾ نه ھوندو آھين ۽ الله جي طرف کان آيل قرآن مان جيڪي نه پڙھندو آھين ۽ ڪوبه ڪم اوھين نه ٿا ڪريو جڏھن منجھس لڳا رھندا آھيو ته اسين ئي اوھان وٽ حاضر آھيون، ۽ تنھنجي پالڻھار کان ذري جيترو نڪي زمين ۾ نڪي آسمان ۾ ڳجھو رھندو آھي ۽ نڪي اُن کان ٿورو ۽ نڪي گھڻو پر (سڀ) پڌري ڪتاب ۾ (لکيل) آھي.');
INSERT INTO "amroti" VALUES(10,62,'10:62','خبردار! الله جي پيارن کي نڪي ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(10,63,'10:63','جن ايمان آندو ۽ پرھيزگاري ڪئي اٿن.');
INSERT INTO "amroti" VALUES(10,64,'10:64','تن لاءِ دُنيا جي حياتي ۽ آخرت ۾ خوشخبري آھي، الله جي حُڪمن کي ڪا ڦير گھير ڪانھي، اھائي وڏي مُراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(10,65,'10:65','۽ سندن چوڻ توکي نه ڏکوئي، بيشڪ سڀ غلبو الله جو ئي آھي، اُھو ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(10,66,'10:66','خبردار بيشڪ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله جو آھي، ۽ جيڪي الله کانسواءِ ٻـين شريڪن جي عبادت ڪندا آھن سي (حقيقت ۾) گمان کانسواءِ ٻئي جي تابعداري نه ڪندا آھن ۽ اُھي رڳو اٽڪلون ڪندا آھن.');
INSERT INTO "amroti" VALUES(10,67,'10:67','اُھو (الله) آھي جنھن اوھان لاءِ رات کي ھن ڪري بڻايو ته منجھس آرام ڪريو ۽ ڏينھن کي روشن بڻايائين، بيشڪ اُن ۾ ٻڌندڙ قوم لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(10,68,'10:68','ڪافر چون ٿا ته الله (پنھنجي لاءِ) اولاد ورتو آھي اُھو پاڪ آھي، اُھو بي پرواہ آھي، جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو ان جو آھي، ھن (پٽ جي چوڻ) بابت اوھان وٽ ڪا سَند نه آھي، جيڪي نه ڄاڻندا آھيو سو ڇو الله تي چوندا آھيو.');
INSERT INTO "amroti" VALUES(10,69,'10:69','چؤ جيڪي الله تي ڪوڙو ٺاھ ٺاھيندا آھن سي نه ڇٽندا.');
INSERT INTO "amroti" VALUES(10,70,'10:70','دنيا ۾ ٿورو آسودو گذران ھوندن وري اسان وٽ سندن موٽڻ آھي پوءِ کين انھي سببان سخت عذاب چکائينداسين جو ڪُفر ڪندا آھن.');
INSERT INTO "amroti" VALUES(10,71,'10:71','۽ نُوح جو قصو کين پڙھي ٻُڌاءِ، جڏھن پنھنجي قوم کي چيائين ته اي منھنجي قوم جيڪڏھن منھنجو رھڻ ۽ الله جي آيتن سان منھنجي نصيحت ڏيڻ اوھان تي ڳوري ٿي آھي ته الله تي ڀروسو ڪيم پوءِ پنھنجي ڪم ۽ پنھنجن شريڪن جي مدد سان گڏجي صلاح ڪريو پوءِ اوھان جو ڪم اوھان کان ڳجھو رھجي نه وڃي ته پوءِ مون سان ڪري گذرو ۽ مون کي ڍر نه ڏيو.');
INSERT INTO "amroti" VALUES(10,72,'10:72','پوءِ جيڪڏھن (منھنجي چوڻ کان) ڦريؤ (ته ڇا ٿيو) مون اوھان کان ڪو اجورو نه گھريو ھو، منھنجو اُجورو الله کانسواءِ ٻئي تي نه آھي ۽ مون کي حُڪم ڪيو ويو آھي ته مُسلمانن مان ھجان.');
INSERT INTO "amroti" VALUES(10,73,'10:73','پوءِ کيس ڪوڙو ڀانيائون پوءِ کيس ۽ جيڪي ٻيڙي ۾ ساڻس ھوا تن کي بچايوسون ۽ کين انھن (ڪافرن) جو جانشين ڪيوسون ۽ جن اسان جي آيتن کي ڪوڙو ڀانيو تن کي ٻوڙيوسون، پوءِ ڏس ته جن کي ڊيڄاريو ويو تن جي پڇاڙي ڪھڙي طرح ٿي.');
INSERT INTO "amroti" VALUES(10,74,'10:74','پوءِ اُن (نوح) کانپوءِ (گھڻا) پيغمبر سندن قوم ڏانھن موڪلياسون پوءِ وٽن پڌرن مُعجزن سان آيا پوءِ اھڙا نه ھوا جو جنھن شيء کي پھريائين ڪوڙ ڀانيو ھئائون تنھن تي ايمان آڻين، اھڙي طرح حد کان لنگھندڙن جي دلين تي مھر ھڻندا آھيون.');
INSERT INTO "amroti" VALUES(10,75,'10:75','وري کائن پوءِ مُوسىٰ ۽ ھارون کي فرعون ۽ سندس سردارن ڏانھن پنھنجن مُعجزن سميت موڪليوسون پوءِ وڏائي ڪيائون ۽ ڏوھارڻ قوم ھُئي.');
INSERT INTO "amroti" VALUES(10,76,'10:76','پوءِ جنھن مھل وٽن اسان وٽان سچي ڳالھ آئي (تڏھن) چيائون ته بيشڪ ھي پڌرو جادو آڳي.');
INSERT INTO "amroti" VALUES(10,77,'10:77','مُوسىٰ چيو جڏھن اوھان وٽ حق آيو (ته) اُن لاءِ ڇو چوندا آھيو (ته جادو آھي)؟ ھي جادو آھي ڇا؟ ۽ جادوگر نه ڇٽندا.');
INSERT INTO "amroti" VALUES(10,78,'10:78','چيائون ته اسان وٽ ھن لاءِ آيو آھين ڇا ته جنھن (مذھب) تي اسان پنھنجا پيءُ ڏاڏا ڏٺا تنھن کان اسان کي روڪين ۽ مُلڪ ۾ اوھان (ٻنھي) لاءِ وڏائي ھجي؟ ۽ اسين اوھان کي مڃڻ وارا نه آھيون.');
INSERT INTO "amroti" VALUES(10,79,'10:79','۽ فرعون چيو ته سڀئي ھوشيار جادوگر مون وٽ آڻيو.');
INSERT INTO "amroti" VALUES(10,80,'10:80','پوءِ جنھن مھل جادوگر آيا (تنھن مھل) مُوسىٰ انھن کي چيو ته جيڪي اُڇلائڻو اٿو سو اوھين اڇليو.');
INSERT INTO "amroti" VALUES(10,81,'10:81','پوءِ جنھن مھل اُڇليائون (ته) موسىٰ چيو ته جيڪي آندو اٿَوَ سو جادو آھي، بيشڪ الله ان کي سگھوئي باطل ڪندو، بيشڪ الله فسادين جو ڪم نه ٺاھيندو آھي.');
INSERT INTO "amroti" VALUES(10,82,'10:82','۽ الله حق کي پنھنجي حُڪم سان ثابت ڪندو آھي جيتوڻيڪ ڏوھاري بڇان ڀانئين.');
INSERT INTO "amroti" VALUES(10,83,'10:83','پوءِ موسىٰ تي سندس قوم مان ھڪ ٽوليءَ کانسواءِ فرعون ۽ سندس سردارن کان ڊڄڻ جي ڪري ته متان کين ايذائين (ڪنھن) ايمان نه آندو، ۽ تحقيق فرعون (اُن) مُلڪ ۾ ھٺيلو ھو، ۽ اُھو ضرور حَد کان لنگھندڙن مان ھو.');
INSERT INTO "amroti" VALUES(10,84,'10:84','۽ موسىٰ چيو ته اي منھنجي قوم جيڪڏھن اوھان الله تي اِيمان آندو آھي ته جي مُسلمان آھيو ته مٿس ڀروسو ڪريو.');
INSERT INTO "amroti" VALUES(10,85,'10:85','پوءِ چيائون ته الله تي ڀروسو ڪيوسون، اي اسان جا پالڻھار اسان کي ظالمن جي قوم جي پرک نه ڪر.');
INSERT INTO "amroti" VALUES(10,86,'10:86','۽ ڪافرن جي قوم کان پنھنجي ٻاجھ سان اسان کي بچاءِ.');
INSERT INTO "amroti" VALUES(10,87,'10:87','۽ موسىٰ ۽ سندس ڀاءُ ڏانھن وحي ڪيوسون ته اوھين پنھنجي قوم لاءِ مِصر ۾ گھر بڻايو ۽ (اُنھن) پنھنجن گھرن کي قبلي جي سامھون ڪريو ۽ نماز پڙھو، ۽ مؤمنن کي خوشخبري ڏي.');
INSERT INTO "amroti" VALUES(10,88,'10:88','۽ مُوسىٰ چيو ته اي اسان جا پالڻھار تو فرعون ۽ سندس سردارن کي سينگار ۽ (گھڻو) مال دنيا جي حياتيءَ ۾ (ھن لاءِ) ڏنو آھي ته اي اسان جا پالڻھار تنھنجي واٽ کان ماڻھن کي ڀُلائين، اي اسان جا پالڻھار سندن مال کي ناس ڪر ۽ سندين دلين مُھر ھَڻ جو جيستائين ڏکوئيندڙ عذاب (نه) ڏسن تيسين ايمان نه آڻين.');
INSERT INTO "amroti" VALUES(10,89,'10:89','(الله) چيو ته بيشڪ اوھان (ٻنھي) جي دُعا قبول ڪئي وئي پوءِ ٻئي قائم رھو ۽ جيڪي نه ڄاڻندا آھن تن جي واٽ جي تابعداري نه ڪريو.');
INSERT INTO "amroti" VALUES(10,90,'10:90','۽ بني اسرائيلن کي سمنڊ کان (پار) لنگھايوسون پوءِ سندن پويان فرعون ۽ سندس لشڪر ظلم ۽ سرڪشي ڪرڻ لاءِ ايتري قدر سندن پُٺيان پيا جيسين ٻُڏا (فرعون) چيو ته ايمان آندم سچ آھي ته جنھن تي بني اسرائيلن ايمان آندو آھي تنھن کانسواءِ ڪوئي عبادت جو لائق نه آھي ۽ آءٌ مسلمانن منجھان آھيان.');
INSERT INTO "amroti" VALUES(10,91,'10:91','(چيو ويو ته) ھاڻي (ٿو ايمان آڻين)؟ ۽ بيشڪ توکان پوءِ اچن تن لاءِ نشاني ھجين، ۽ گھڻا ماڻھو اسان جي نشانين کان ضرور بيخبر آھن.');
INSERT INTO "amroti" VALUES(10,92,'10:92','پوءِ اڄ تنھنجي لاشي کي ھِن لاءِ بچائينداسون ته جيڪي توکان پوءِ اچن تن لاءِ نشاني ھجين، ۽ گھڻا ماڻھو اسان جي نشانين کان ضرور بيخبر آھن.');
INSERT INTO "amroti" VALUES(10,93,'10:93','۽ بيشڪ بني اسرائيلن کي چڱي ھنڌ رھڻ جي جاءِ ڏني سون ۽ کين سُٺين شين مان روزي ڏني سون، پوءِ (ايستائين) اختلاف نه ڪيائون جيسين وٽن علم (يعني قرآن نه) آيو، بيشڪ تنھنجو پالڻھار جنھن ڳالھ بابت جھڳڙو ڪندا آھن تنھن جو قيامت جي ڏينھن سندن وچ ۾ فيصلو ڪندو.');
INSERT INTO "amroti" VALUES(10,94,'10:94','پوءِ جيڪي توڏانھن نازل ڪيوسون تنھن کان جيڪڏھن شڪ ۾ آھين ته جيڪي توکان اڳ ڪتاب پڙھندا آھن تن کان پُڇ، بيشڪ تنھنجي پالڻھار کان توڏانھن حق آيو آھي پوءِ تون شڪ ڪندڙن مان نه ھُج.');
INSERT INTO "amroti" VALUES(10,95,'10:95','۽ نڪي تون انھن مان ھج جن الله جي آيتن کي ڪُوڙ ڄاتو ته تون ڇيئي وارن مان ٿيندين.');
INSERT INTO "amroti" VALUES(10,96,'10:96','بيشڪ جن تي پنھنجي پالڻھار (جي عذاب) جو حُڪم لازم ٿي چڪو آھي سي ايمان نه آڻيندا.');
INSERT INTO "amroti" VALUES(10,97,'10:97','جيتوڻيڪ وٽن سڀ دليل اچن جيستائين ته ڏکوئيندڙ عذاب ڏسن.');
INSERT INTO "amroti" VALUES(10,98,'10:98','يُونُس جي قوم کانسواءِ ڪن ڳوٺن (وارن عذاب نازل ٿيڻ وقت) ڇونه ايمان آندو ته سندن ايمان کين فائدو ڏئي ھا، جنھن مھل (يونس جي قوم) ايمان آندو (ته اُن وقت) خواريءَ وارو عذاب دُنيا جي حياتيءَ ۾ کانئن لاٿوسون ۽ کين ڪُجھ وقت آسُودو ڪيوسون.');
INSERT INTO "amroti" VALUES(10,99,'10:99','۽ جيڪڏھن تنھنجو پالڻھار گھري ھا ته جيڪي زمين ۾ آھن سي سڀ سمورا ضرور ايمان آڻين ھا، پوءِ تون ماڻھن کي زور ڪندين ڇا ته مؤمن ٿين؟');
INSERT INTO "amroti" VALUES(10,100,'10:100','۽ ڪنھن ماڻھوءَ کي الله جي گھُر کانسواءِ مؤمن ٿيڻ جو اختيار نه آھي، ۽ جيڪي نه ڄاڻندا آھن تن تي (ڪفر جي) پليتي رکندو آھي.');
INSERT INTO "amroti" VALUES(10,101,'10:101','چؤ ته نھاريو ته آسمانن ۽ زمين ۾ ڪھڙيون (سندن قدرتون) آُھن؟ ۽ نه مڃيندڙ قوم کي نشانيون ۽ ڊيڄارڻ فائدو نه ڏيندا آھن.');
INSERT INTO "amroti" VALUES(10,102,'10:102','پوءِ اُنھن جھڙين مُصيبتن کانسواءِ انتظار نه ڪندا آھن جيڪي کانئن اڳ گذري ويون، (کين) چؤ ته انتظار ڪريو آءٌ به اوھان سان گڏ انتظار ڪندڙن مان آھيان.');
INSERT INTO "amroti" VALUES(10,103,'10:103','وري اسين پيغمبرن کي ۽ جن ايمان آندو تن کي بچائيندا آھيون اھڙي طرح اسان تي حق آھي ته مؤمنن کي بچايون.');
INSERT INTO "amroti" VALUES(10,104,'10:104','چؤ ته اي ماڻھؤ جيڪڏھن منھنجي دين کان شڪ ۾ آھيو ته (ٻُڌو ته) الله کانسواءِ جن کي پوڄيندا آھيو تن کي نه پوڄيندس پر اُن الله کي پوڄيندس جيڪو اوھان کي ماريندو آھي، ۽ مُون کي حڪم ڪيو ويو آھي ته مؤمنن مان ھجان.');
INSERT INTO "amroti" VALUES(10,105,'10:105','۽ (ھيءَ) ته پنھنجي مھاڙ کي حنيف دين لاءِ بيھڪ وٺاءِ، ۽ مُشرڪن مان نه ھُج.');
INSERT INTO "amroti" VALUES(10,106,'10:106','۽ الله کانسواءِ انھي جي عبادت نه ڪر جيڪو توکي نڪي سُک پُھچائي ۽ نڪي توکي ڏُک پھچائي، پوءِ جيڪڏھن (ائين) ڪندين ته اُنھي مھل تون ظالمن مان ٿيندين.');
INSERT INTO "amroti" VALUES(10,107,'10:107','۽ جيڪڏھن الله توکي ڪو ڏک پھچائي ته اُن ڌاران ڪو اُن جي لاھڻ وارو ڪونھي، ۽ جيڪڏھن تولاءِ چڱائي گھري ته سندس فضل ڪو ھٽائڻ وارو نه آھي، پنھنجي ٻانھن مان جنھن کي وڻيس تنھن کي فضل پُھچائيندو آھي، ۽ اُھو بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(10,108,'10:108','چؤ ته اي ماڻھؤ بيشڪ اوھان وٽ اوھان جي پالڻھار کان حق آيو آھي، پوءِ جيڪو ھدايت وارو ٿيو سو پاڻ لاءِ ھدايت ٿو لھي، ۽ جيڪو ڀُلو تنھن تي گمراھيءَ ھجڻ کانسواءِ (ٻيو ڪي) نه آھي، ۽ آءٌ اوھان جو ذمي وار نه آھيان.');
INSERT INTO "amroti" VALUES(10,109,'10:109','۽ جيڪي توڏانھن وحي ڪيو وڃي ٿو تنھنجي تابعداري ڪر ۽ (ايستائين) صبر ڪر جيسين الله نبيرو ڪري، ۽ اُھو چڱو نبيرو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(11,1,'11:1','الرٰ، (ھي) ڪتاب آھي جنھن جون آيتون مُحڪم آھن وري حِڪمت واري داناء الله وٽان بيان ڪيل آھن.');
INSERT INTO "amroti" VALUES(11,2,'11:2','ته الله کانسواءِ ٻئي ڪنھن جي عبادت نه ڪريو، آءٌ سندس طرف کان اوھان کي ڊيڄاريندڙ ۽ خوشخبري ڏيندڙ آھيان.');
INSERT INTO "amroti" VALUES(11,3,'11:3','۽ ته پنھنجي پالڻھار کان بخشش گھرو وري ڏانھس (توبه ڪري) موٽو ته ٺھرايل مُدّت تائين اوھان کي چڱي نفعي سان فائدو ڏئي ۽ سڀڪنھن ڀلائيءَ واري کي سندس ڀلائي جو عوض ڏئي، ۽ جيڪڏھن اوھين ڦرندؤ ته بيشڪ آءٌ اوھان تي وڏي ڏينھن جي عذاب (پھچڻ) کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(11,4,'11:4','الله ڏانھن اوھان جو موٽڻ آھي، ۽ اُھو سڀڪنھن شي تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(11,5,'11:5','خبردار بيشڪ اُھي پنھنجن سينن کي ھن لاءِ وٽيندا آھن ته کانئس لِڪن، خبردار جنھن دم (اُھِي) پنھنجا ڪپڙا ڍڪيندا آھن (تنھن دم ۾) جيڪي ڳجھو ڪندا آھن ۽ جيڪي پڌرا ڪندا آھن سو (الله) ڄاڻندو آھي، بيشڪ اُھو سينن وارو (ڀيد) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(11,6,'11:6','۽ ڪو چُرندڙ زمين ۾ آھي ئي نه پر سندس روزي الله تي آھي ۽ سندس رھڻ جو ھنڌ ۽ سنديس سانڀڻ جي جاءِ (الله) ڄاڻندو آھي، سڀ پڌري ڪتابن ۾ لکيل آھي.');
INSERT INTO "amroti" VALUES(11,7,'11:7','۽ اُھو (الله) آھي جنھن آسمانن ۽ زمين کي ڇھن ڏينھن ۾ بڻايو ۽ سندس عرش پاڻي تي ھو (اھو بڻائڻ ھن ڪري آھي) ته اوھان کي پرکي ته اوھان مان عملن جي ڪري ڪير تمام چڱو آھي، ۽ جيڪڏھن انھن کي چوين ته موت کانپوءِ اوھين جياريا ويندؤ ته ڪافر ضرور چوندا ته ھي پڌرو جادو آھي.');
INSERT INTO "amroti" VALUES(11,8,'11:8','۽ جيڪڏھن ڪنھن مُقرّر مُدّت تائين کائن عذاب کڻي ھٽايون ته ضرور چوندا ته اُن کي ڪنھن جھليو آھي؟ خبردار جنھن ڏينھن (اُھو عذاب) وٽن ايندو (تنھن ڏينھن) کائن ٽرڻو نه آھي ۽ جنھن (شيء) سان ٺـٺوليون ڪندا ھوا سا کين وڪوڙي ويندي.');
INSERT INTO "amroti" VALUES(11,9,'11:9','۽ جيڪڏھن ماڻھوءَ کي پاڻ وٽان ڪا ٻاجھ چکايون ٿا (۽) وري اُھا کانئس کسيون ٿا، ته اُھو نااميد بي شڪر (بڻيو) آھي.');
INSERT INTO "amroti" VALUES(11,10,'11:10','۽ جيڪڏھن ان کي ڏک پھچڻ کانپوءِ سُک پُھچايون ته ضرور چوندو ته مون کان ڏک ويا، اھو پڪ سرھو وڏائي ڪندڙ ٿيندو آھي.');
INSERT INTO "amroti" VALUES(11,11,'11:11','پر جن صبر ڪيو ۽ چڱا ڪم ڪيا، تن لاءِ بخشش ۽ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(11,12,'11:12','پوءِ (اي پيغمبر) جيڪي توڏانھن وحي ڪجي ٿو تنھن مان متان ڪي ڇڏي ڏئين ۽ (پڻ) متان دل تنگ ڪرين ڪافرن جي ھن چوڻ تي ته مٿس خزانو ڇونه لاٿو ويو يا ملائڪ ساڻس ڇونه آيو، (نه ڇڏ ۽ نه دل تنگ ڪر) ڇوته تون ڊيڄاريندڙ ئي آھين ۽ الله ھر شيء جو سنڀاليندڙ آھي.');
INSERT INTO "amroti" VALUES(11,13,'11:13','بلڪ (ائين) به چون ٿا ته اُھو (قرآن) پاڻ ٺاھيو اٿس، چؤ ته جيڪڏھن سچا آھيو ته (اوھين به) اُن جھڙيون ڏہ سورتون پاڻون ٺاھيل آڻيو ۽ الله کانسواءِ (ٻـين) جن کي سڏي سگھو تن کي سڏيو.');
INSERT INTO "amroti" VALUES(11,14,'11:14','پوءِ جيڪڏھن اوھان جو (اھو چوڻ) قبول نه ڪن ته ڄاڻو ته (اُھو قرآن) رڳو الله جي علم سان لاٿو ويو آھي ۽ (ڄاڻو) ته ان کانسواءِ ٻيو ڪو عبادت جو لائق نه آھي، پوءِ اوھين اسلام تي (مُحڪم) ڇونه رھندؤ.');
INSERT INTO "amroti" VALUES(11,15,'11:15','جيڪو دنيا جي حياتي ۽ سندس سينگار گھرندو ھجي تن کي دنيا ۾ (ئي) سندن عملن جو اجورو پورو ڏينداسون ۽ انھن کي اُن ۾ گھٽ نه ڏبو.');
INSERT INTO "amroti" VALUES(11,16,'11:16','اِھي اُھي آھن جن لاءِ آخرت ۾ باہ کانسواءِ (ٻيو ڪي) نه آھي، ۽ جيڪي دنيا ۾ ڪيائون سو چٽ ٿيو ۽ جيڪي ڪمائيندا ھوا سو باطل ٿيو.');
INSERT INTO "amroti" VALUES(11,17,'11:17','(اُھو ماڻھو گمراھن جھڙو نه آھي) جيڪو پنھنجي پالڻھار (جي طرف) کان پڌري حجت تي ھجي ۽ الله کان اُن لاءِ شاھد (يعني قرآن پڙھندو) ھجي ۽ اُن (قرآن) کان اڳ موسىٰ جو ڪتاب (توريت) امام ۽ رحمت (به گواہ ھجي)، اِھي (ماڻھو) اُن (قرآن) تي ايمان آڻين ٿا، (پوءِ انھن جي سڀني) ٽولين مان جيڪو اُن (قرآن) جو انڪار ڪندو تنھن جي رھڻ جو ھنڌ باھ آھي، تنھنڪري (تون) کانئس شڪ ۾ نه ھُج، اُھو تنھنجي پالڻھار جي پار کان حق (لٿو) آھي پر گھڻا ماڻھو ايمان نه ٿا آڻين.');
INSERT INTO "amroti" VALUES(11,18,'11:18','۽ جيڪو الله تي ڪوڙو ٺاھ ٺاھي تنھن کان وڌيڪ ظالم ڪير آھي، اھي پنھنجي پالڻھار جي آڏو ڪيا ويندا ۽ شاھد چوندا ته اھي آھن جن پنھنجي پالڻھار تي ڪوڙ چيو، خبردار (انھن) ظالمن تي الله جي لعنت آھي.');
INSERT INTO "amroti" VALUES(11,19,'11:19','جيڪي الله جي واٽ کان جھليندا آھن ۽ ان جي ڏنگائي گھرندا آھن، ۽ اُھي ئي آخرت کي نه مڃيندا آھن.');
INSERT INTO "amroti" VALUES(11,20,'11:20','اُھي مُلڪ ۾ ڪڏھن عاجز ڪرڻ وارا نه آھن ۽ الله کانسواءِ (ٻيا ڪي) انھن جا مددگار نه آھن، انھن کي ٻيڻو عذاب ڪيو ويندو، جو ٻُڌي نه ٿي سگھيا ۽ نه ٿي ڏٺائون.');
INSERT INTO "amroti" VALUES(11,21,'11:21','اِھي اُھي آھن جن پاڻ کي ڇيئو لاتو ۽ جيڪو ٺاھ ٺاھيندا ھوا سو کانئن ڀلجي ويو.');
INSERT INTO "amroti" VALUES(11,22,'11:22','بيشڪ اِھي (ماڻھو) ضرور آخرت ۾ ڇيئي وارا آھن.');
INSERT INTO "amroti" VALUES(11,23,'11:23','بيشڪ جن ايمان آندو ۽ چڱا عمل ڪيا ۽ پنھنجي پالڻھار ڏانھن نويا سي بھشتي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(11,24,'11:24','انھن ٻن ٽولين جو مثال انڌي ۽ ٻوڙي ۽ ڏسندڙ ۽ ٻڌندڙ وانگر آھي، (اھي) مثال ۾ برابر آھن ڇا؟ پوءِ ڇونه ٿا نصيحت وٺو.');
INSERT INTO "amroti" VALUES(11,25,'11:25','۽ بيشڪ اسان نوح کي سندس قوم ڏانھن موڪليو (اُن چيو ته) آءٌ اوھان لاءِ پڌرو ڊيڄارندڙ آھيان.');
INSERT INTO "amroti" VALUES(11,26,'11:26','ته الله کانسواءِ (اوھين ٻئي جي) عبادت نه ڪريو، بيشڪ آءٌ اوھان کي ڏکوئيندڙ ڏينھن جي عذاب (پھچڻ) کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(11,27,'11:27','پوءِ سندس قوم مان ڪافر ٽوليءَ چيو ته توکي پاڻ جھڙي ماڻھو کانسواءِ (ٻيو) نٿا ڏسون ۽ اسان مان بي سوچ خسيسن کان سواءِ (ٻيو) تنھنجو تابعدار نه ٿا ڏسون، ۽ پاڻ کان اوھان جي ڪا زيادتي نه ٿا ڏسون بلڪ اوھان کي ڪوڙ ڀائيندا آھيون.');
INSERT INTO "amroti" VALUES(11,28,'11:28','(نوح) چيو ته اي منھنجي قوم (مون کي) ڏسيو ته سھين ته جيڪڏھن (آءٌ) پنھنجي پالڻھار جي پڪي حجت تي ھجان ۽ پاڻ وٽان مون کي رحمت ڏني ھجيس ۽ (جنھن جي حقيقت) اوھان کان لڪائي وئي ھجي ته اُن (جي مڃڻ) جو ھن ھوندي اوھان کي ڇو زور ڪريون جو اوھين اُن کي بڇان ڀائيندا ھجو؟');
INSERT INTO "amroti" VALUES(11,29,'11:29','۽ اي منھنجي قوم (آءٌ) اوھان کان اُن بابت ڪو مال نٿو گھران، منھنجو اجورو الله کانسواءِ ٻئي تي نه آھي ۽ آءٌ مؤمنن کي تڙڻ وارو نه آھيان، ڇوته اُھي پنھنجي پالڻھار کي ملڻ وارا آھن پر آءٌ اوھان کي جاھل قوم ڏسان ٿو.');
INSERT INTO "amroti" VALUES(11,30,'11:30','۽ اي منھنجي قوم جيڪڏھن کين تڙيندس ته مون کي الله کان ڪير ڇڏائيندو، پوءِ ڇونه سمجھندا آھيو؟');
INSERT INTO "amroti" VALUES(11,31,'11:31','۽ آءٌ اوھان کي نٿو چوان ته مون وٽ الله جا خزانا آھن ۽ نڪي (ڪو) ڳُجھ ڄاڻندو آھيان ۽ نڪي چوان ٿو ته آءٌ ملائڪ آھيان ۽ جن کي اوھان جون اکيون ھيڻو ڏسن ٿيون تن لاءِ نٿو چوان ته الله کين ڪا چڱائي نه ڏيندو، جيڪي سندين دلين ۾ آھي سو الله چڱو ڄاڻندڙ آھي، (جي چوندس ته) آءٌ انھيءَ مھل ظالمن مان ٿيندس.');
INSERT INTO "amroti" VALUES(11,32,'11:32','چيائون اي نوح بيشڪ تو اسان سان جھڳڙو ڪيو پوءِ اسان سان گھڻو جھيڙو ڪيئي جيڪڏھن سچن مان آھين ته اسان سان جيڪو انجام ڪرين ٿو سو اسان وٽ آڻ.');
INSERT INTO "amroti" VALUES(11,33,'11:33','(نوح) چيو ته اُھو اوھان وٽ رڳو الله آڻيندو جيڪڏھن اُن گھريو ته ۽ اوھين (ڀڄي) عاجزي ڪرڻ وارا نه آھيو.');
INSERT INTO "amroti" VALUES(11,34,'11:34','۽ جيڪڏھن آءٌ اوھان کي نصيحت ڏيڻ گھران ته جيڪڏھن الله اوھان جو ڀُلائڻ گھرندو ته منھنجي نصيحت اوھان کي ڪو نفعو نه ڏيندي، اُھو اوھان جو پالڻھار آھي، ۽ ڏانھس موٽايا ويندؤ.');
INSERT INTO "amroti" VALUES(11,35,'11:35','بلڪ (ھيئن به) چوندا آھن ته (ھن پيغمبر) اُھو پاڻون ٺاھيو آھي، چئو ته جيڪڏھن مون اُن کي پاڻ ٺاھيو آھي ته منھنجو ڏوھ مون تي آھي ۽ جيڪي اوھين ڏوھ ڪندا آھيو تنھن کان آءٌ بيزار آھيان.');
INSERT INTO "amroti" VALUES(11,36,'11:36','۽ نُوح ڏانھن وحي ڪيو ويو ته جنھن ايمان آندو آھي تنھن کانسواءِ تنھنجي قوم مان ٻيو ڪوبه ڪڏھن ايمان نه آڻيندو تنھنڪري جيڪي ڪندا آھن تنھن تي تون رنج نه ٿي.');
INSERT INTO "amroti" VALUES(11,37,'11:37','۽ اسان جي روبرو ۽ اسان جي حُڪم سان ٻيڙي جوڙ ۽ ظالمن بابت مون کي نه چئج، ڇوته اُھي ٻوڙيا ويندا.');
INSERT INTO "amroti" VALUES(11,38,'11:38','۽ ٻيڙي بڻائڻ لڳو، جڏھن ڪڏھن سندس قوم مان ڪا ٽولي وٽانئس لنگھي ٿي (ته) مٿس ٺـٺوليون ڪيائون، (نوح) ٿي چيو ته جيڪڏھن اوھين اسان تي ٺـٺوليون ڪريو ٿا ته اسين (به) اھڙيون ٺـٺوليون اوھان تي ڪنداسون جھڙيون ٺـٺوليون (اوھين) ڪريو ٿا.');
INSERT INTO "amroti" VALUES(11,39,'11:39','پوءِ جنھن کي عذاب پھچندو تنھن کي سگھوئي ڄاڻندؤ جو (اُھو) ان کي خوار ڪندو ۽ مٿس سدائين عذاب (پيو) لھندو.');
INSERT INTO "amroti" VALUES(11,40,'11:40','(انھيءَ ڪم ۾ ھوا) ايتري تائين جو اسان جو حُڪم آيو ۽ تنور اُڀاميو چيوسون ته سڀڪنھن جنس (جي جانورن) مان ٻه ٻه (نر ۽ مادي) ۽ جن بابت حڪم ٿي چڪو آھي (ته ھلاڪ ڪبا) تن ڌاران ٻيا پنھنجي گھر وارا ۽ جن ايمان آندو سي منجھس چاڙھ، ۽ ٿورن کانسواءِ مٿس ايمان نه آندو ھوائون.');
INSERT INTO "amroti" VALUES(11,41,'11:41','۽ (نوحؑ) چيو ته منجھس چڙھو الله جي نالي سان سندس ھلڻ ۽ سندس بيھڻ آھي، ڇوته منھنجو پالڻھار ضرور بخشيندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(11,42,'11:42','۽ اُھا جبلن جھڙين لھرين ۾ کين (کڻي) ھلڻ لڳي، ۽ نوحؓ پنھنجي پٽ کي سڏيو ۽ اُھو ڪناري تي ھو ته اي مُنھنجا پُٽ اسان سان چڙھ ۽ ڪافرن سان گڏ نه ھُج.');
INSERT INTO "amroti" VALUES(11,43,'11:43','چيائين ته (اھڙي) جبل تي پناھ وٺندس جو مون کي پاڻيءَ کان بچائيندو، (نوحؑ) چيو ته اڄ الله جي عذاب کان ڪو بچائيندڙ ڪونھي پر جنھن تي (پاڻ) رحم ڪري (سو بچي سگھندو)، ايتري ۾ ٻنھي جي وچ ۾ لھر اچي پئي ۽ (اُھو) ٻڏلن مان ٿيو.');
INSERT INTO "amroti" VALUES(11,44,'11:44','۽ فرمايو ويو ته اي زمين (تون) پنھنجو پاڻي چُھي وڃ ۽ اي آسمان (تون پنھنجو پاڻي) بند ڪر ۽ پاڻي سُڪايو ويو ۽ ڪم پورو ڪيو ويو ۽ (ٻيڙي جبل) جوديءَ تي بيٺي ۽ چيو ويو ته (شل) ظالمن جي قوم ناس ٿئي.');
INSERT INTO "amroti" VALUES(11,45,'11:45','۽ نوحؑ پنھنجي پالڻھار کي سڏيو ۽ چيائين اي منھنجا پالڻھار منھنجو پٽ (به) منھنجي گھر وارن منجھان آھي ۽ بيشڪ تنھنجو انجام بلڪل سچو آھي ۽ تو حاڪمن جو حاڪم آھين.');
INSERT INTO "amroti" VALUES(11,46,'11:46','(الله) فرمايو ته اي نوح اُھو تنھنجي گھر وارن مان نه آھي، ڇوته اَڻ سڌريل ڪمن وارو آھي، تنھنڪري جنھن جي توکي ڪا خبر نه آھي تنھن بابت مون کان نه پُڇ، آءٌ توکي نصيحت ڪريان ٿو ته متان جاھلن مان ٿئين.');
INSERT INTO "amroti" VALUES(11,47,'11:47','(نوحؓ) چيو ته اي منھنجا پالڻھار جنھن (ڳالھ جي حقيقت) جي مون کي خبر نه آھي تنھن بابت توکان پڇڻ جي آءٌ تو وٽان پناھ گھران ٿو، ۽ جي تون نه بخشيندين ۽ رحم نه ڪندين ته ڇيئي وارن مان ٿيندس.');
INSERT INTO "amroti" VALUES(11,48,'11:48','چيو ويو ته اي نوحؑ اسان جي طرف کان سلامتي ۽ برڪتن سان لھ جي توتي ھجن ۽ انھن ٽولين تي (به) جيڪي توساڻ آھن، ۽ (ٻيون ڪي) جماعتون ھونديون جن کي آسودو ڪنداسون وري انھن (مان ڪافرن) کي اسان وٽان ڏکوئيندڙ عذاب پھچندو.');
INSERT INTO "amroti" VALUES(11,49,'11:49','اِھي (قصا) ڳُجھين خبرن مان آھن جي تو ڏانھن وحي ڪريون ٿا، ھن کان اڳ نڪي (ان کي) تون ڄاڻندو ھئين ۽ نڪا تنھنجي قوم ڄاڻندي ھئي، پوءِ صبر ڪريو، بيشڪ پڇاڙي پرھيزگارن لاءِ آھي.');
INSERT INTO "amroti" VALUES(11,50,'11:50','۽ عادين ڏانھن سندن ڀاءُ ھود کي (موڪليوسون)، چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اُن (الله) کانسواءِ اوھان جو ڪو معبود نه آھي، اوھين رڳو ٺاھ ٺاھيندڙ آھيو.');
INSERT INTO "amroti" VALUES(11,51,'11:51','اي منھنجي قوم اوھان کان (ھن پيغام پھچائڻ تي) ڪو اجورو نٿو گھران، منھنجو اجورو رڳو انھي تي آھي جنھن مون کي پيدا ڪيو آھي، پوءِ اوھين ڇونه ٿا سمجھو.');
INSERT INTO "amroti" VALUES(11,52,'11:52','۽ اي منھنجي قوم پنھنجي پالڻھار کان بخشش گھرو وري سندس اڳيان توبه ڪريو ته اوھان تي لڳولڳ وسندڙ ڪڪر موڪليندو ۽ اوھان جي سگھ تي سگھ وڌائيندو ۽ اوھين ڏوھي ٿي منھن نه ڦيريو.');
INSERT INTO "amroti" VALUES(11,53,'11:53','چيائون ته اي ھُود اسان وٽ ڪوئي دليل نه آندو اٿئي ۽ اسين تنھنجي چوڻ تي پنھنجن معبودن کي ڇڏڻ وارا نه آھيون ۽ نڪي توکي مڃيندڙ آھيون.');
INSERT INTO "amroti" VALUES(11,54,'11:54','اسين توکي رڳو (ھي ڳالھ) چئون ٿا ته اسان جي ڪِن بُتن توکي ڪي ايذايو آھي، (ھود) چيو بيشڪ آءٌ الله کي شاھد ڪريان ٿو ۽ اوھين به شاھد ھُجو تحقيق آءٌ انھن کان بيزار آھيان جن کي (الله سان) شريڪ بڻائيندا آھيو.');
INSERT INTO "amroti" VALUES(11,55,'11:55','اُن (الله) ڌاران پوءِ مون سان سڀئي مدائي ڪريو وري مون کي مُھلت نه ڏيو.');
INSERT INTO "amroti" VALUES(11,56,'11:56','تحقيق مون پنھنجي پالڻھار ۽ اوھان جي پالڻھار الله تي ڀروسو ڪيو آھي، ڪو (جاندار) چرندڙ نه آھي جنھنجي پيشانيءَ (جي چڳه) اُن (الله) کانسواءِ ٻيو ڪو (ھٿ ۾) وٺندڙ ھجي بيشڪ منھنجو پالڻھار سڌيءَ واٽ تي آھي.');
INSERT INTO "amroti" VALUES(11,57,'11:57','پوءِ جيڪڏھن ڦرندؤ ته (مون کي ڇا ٿيندو) بيشڪ جنھن پيغام سان اوھان ڏانھن موڪليو ويو آھيان سو اوھان کي پھچايم، ۽ منھنجو پالڻھار اوھان کانسواءِ ٻيءَ قوم کي اوھان جي جاءِ تي وھاريندو، ۽ اوھين کيس ڪُجھ ذرو نقصان پھچائي نه سگھندؤ، بيشڪ منھنجو پالڻھار سڀني شين تي نگھبان آھي.');
INSERT INTO "amroti" VALUES(11,58,'11:58','۽ جنھن مھل اسان جو عذاب پھتو (تنھن مھل) ھود کي ۽ جن ساڻس ايمان آندو تن کي پنھنجي ٻاجھ سان بچايوسون، ۽ کين سخت عذاب کان ڇڏايوسون.');
INSERT INTO "amroti" VALUES(11,59,'11:59','۽ اھو حال عادين جو آھي جو پنھنجي پالڻھار جي نشانين جو انڪار ڪيائون ۽ پنھنجي پيغمبرن جي نافرماني ڪيائون ۽ سڀڪنھن ھٺيلي فساديءَ جي حُڪم جا تابعدار ٿيا.');
INSERT INTO "amroti" VALUES(11,60,'11:60','۽ ھن دُنيا ۾ (به) ۽ قيامت جي ڏينھن (به) پويان لعنت لاتي وين، خبردار تحقيق عادين پنھنجي پالڻھار جو انڪار ڪيو، خبردار عادين کي لعنت ھجي جو ھود جي قوم ھوا.');
INSERT INTO "amroti" VALUES(11,61,'11:61','۽ ثمود جي قوم ڏانھن سندن ڀاءُ صالحؑ کي موڪليوسون، چيائين ته اي منھنجي قوم الله جي عبادت ڪريو ان کانسواءِ (ڪو ٻيو) معبود نه آھي، اُنھيءَ اوھان کي زمين مان پيدا ڪيو ۽ منجھس اوھان کي آباد ڪيائين تنھنڪري کانئس بخشش گھرو وري سندس اڳيان توبه ڪريو، بيشڪ منھنجو پالڻھار ويجھو (۽) قبول ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(11,62,'11:62','چيائون ته اي صالح ھن کان اڳ بيشڪ اسين تو ۾ (ڪيئي) اُميدون رکندا ھواسون (جي ھاڻي بند ٿيون) اسان جي ابن ڏاڏن جن کي پوڄيو تن جي پوڄڻ کان اسان کي ڇو ٿو جھلين؟ ۽ جنھن ڏانھن اسان کي سڏين ٿو تنھن کان اسين بلڪل شڪ ۾ پيل آھيون.');
INSERT INTO "amroti" VALUES(11,63,'11:63','(صالح) چيو ته اي منھنجي قوم (مون کي اوھين) ڏسو ته سھين ته جيڪڏھن آءٌ پنھنجي پالڻھار کان چٽيءَ حُجت تي ھجان ۽ پنھنجي طرف کان مون تي ٻاجھ ڪئي ھجيس ته جيڪڏھن (آءٌ اوھان جو چيو مڃي) سندس نافرماني ڪريان ته الله کان مون کي ڪير ڇڏائيندو؟ پوءِ اوھان مون لاءِ رڳو نقصان وڌايو؟');
INSERT INTO "amroti" VALUES(11,64,'11:64','۽ اي منھنجي قوم ھيءَ الله جي ڏاچي اوھان لاءِ نشاني (مُعجزو) آھي پوءِ ان کي ڇڏيو ته الله جي زمين ۾ (جتي وڻيس اُتي) چَري ۽ ان کي ڪو ايذاءُ نه پھچايو نه ته پوءِ اوھان کي ستت ئي عذاب اچي وٺندو.');
INSERT INTO "amroti" VALUES(11,65,'11:65','پوءِ اُن جو کُچون ڪپي (ماري) وڌائون پوءِ (صالحؑ) چيو ته اوھين پنھنجن گھرن ۾ (ڇڙا) ٽي ڏينھن فائدو وٺو، اُھو فائدو ڪوڙو نه آھي.');
INSERT INTO "amroti" VALUES(11,66,'11:66','پوءِ جنھن مھل اسان جو عذاب آيو (تنھن مھل) صالح ۽ جن ساڻس ايمان آندو ھو تن کي پنھنجي ٻاجھ سان بچايوسون ۽ قيامت جي ڏينھن جي خواري کان (به) تحقيق تنھنجو پالڻھار طاقت وارو ۽ غالب آھي.');
INSERT INTO "amroti" VALUES(11,67,'11:67','۽ (انھن) ظالمن کي ھڪ خوفناڪ آواز ورتو پوءِ صبح جو پنھنجن حويلين ۾ ڪرونڊڙا ٿي مري ويا.');
INSERT INTO "amroti" VALUES(11,68,'11:68','ڄڻڪ منجھس رھيا ئي نه ھوا، خبردار ثمودي پنھنجي رب کان منڪر ٿيا، ٻڌو! ثمودين تي لعنت ھجي.');
INSERT INTO "amroti" VALUES(11,69,'11:69','۽ بيشڪ اسانجا قاصد ابراھيمؑ وٽ خوشخبري (ڏيڻ) لاءِ آيا (پوءِ) سلام ڏنائون، (ابراھيمؑ) سلام ورايو پوءِ ڀُڳل گابي (جي گوشت آڻڻ) ۾ ڪا دير نه ڪيائين.');
INSERT INTO "amroti" VALUES(11,70,'11:70','پوءِ جنھن مھل سندن ھٿن کي اُن (گوشت) ڏانھن نه پھچندو ڏنائين (تنھن مھل) کين اوپرو ڀانيائين ۽ کائن دل ۾ ڊنون چيائون ته نه ڊڄ اسان کي لُوط جي قوم ڏانھن موڪليو ويو آھي.');
INSERT INTO "amroti" VALUES(11,71,'11:71','۽ سنديس زال جا بيٺل ھُئي سا کِلي پوءِ ان کي اسحاق جي (ڄمڻ جي) ۽ اسحاق جي پٺيان يعقوب جي مُبارڪ ڏنيسون.');
INSERT INTO "amroti" VALUES(11,72,'11:72','چيائين ته مون لاءِ افسوس آھي آءٌ ھن حالت ۾ ڄڻينديس ڇا جو آءٌ پاڻ ڪراڙي جھور آھيان ۽ ھيءُ منھنجو مڙس ٻڍو آھي، ھي ته ھڪ عجيب ڳالھ آھي.');
INSERT INTO "amroti" VALUES(11,73,'11:73','چيائون ته الله جي حُڪم تي عجب ڇو ٿي ڪرين اي ھن گھر وارؤ اوھان تي الله جي ٻاجھ ۽ سندس برڪتون آھن، بيشڪ اھو (الله) ساراھيل وڏائيءَ وارو آھي.');
INSERT INTO "amroti" VALUES(11,74,'11:74','پوءِ جنھن مھل ابراھيمؑ کان ڀؤ لٿو ۽ خوشخبري پُھتس (ته اُن وقت) لُوط جي قوم بابت اسان سان تڪرار ڪرڻ لڳو.');
INSERT INTO "amroti" VALUES(11,75,'11:75','ڇوته اِبراھيم بُردبار نرم دل (الله ڏانھن) موٽندڙ ھو.');
INSERT INTO "amroti" VALUES(11,76,'11:76','(چيوسون ته) اي ابراھيمؑ اِن خيال کان مُڙي وڃ، تحقيق تنھنجي پالڻھار جو حُڪم اچي چڪو آھي، ۽ کين اَڻ ٽر عذاب اچڻ وارو آھي.');
INSERT INTO "amroti" VALUES(11,77,'11:77','۽ جڏھن اسان جا قاصد لُوط وٽ آيا (تڏھن) سندن (اچڻ) سببان خفي ٿيو ۽ انھن جي سببان تنگدل ٿيو ۽ چيائين ته ھي ڏينھن سخت آھي.');
INSERT INTO "amroti" VALUES(11,78,'11:78','۽ سندس قوم ڏانھس ڊوڙندي آئي، ۽ (اُھي) اڳي بُڇڙا ڪم ڪندا ھئا، چيائين ته اي منھنجي قوم ھيءُ منھنجون ڌيئرون اوھان جي (نڪاح) لاءِ ڏاڍيون سٺيون آھن پوءِ الله کان ڊڄو ۽ منھنجي مھمانن (سان بڇڙي ھلت ڪري) ۽ مونکي لڄي نه ڪريو، اوھان ۾ ڪو ڀلارو مڙس ڪونھي ڇا (جو اوھان کي جھلي)؟');
INSERT INTO "amroti" VALUES(11,79,'11:79','چيائون ته تون پڪي طرح ڄاڻي چڪو آھين ته اسان کي تنھنجي ڌيئرن ۾ ڪا احتياجي ڪانه آھي، ۽ توکي ته معلوم آھي جو اسين گھرون ٿا.');
INSERT INTO "amroti" VALUES(11,80,'11:80','(لُوط) چيو ته جيڪڏھن مون کي اوھان سان مقابلي جي سگھ ھجي ھا يا ڪنھن ڏاڍي اوٽ جي پناھ وٺان ھا (ته جيڪي ڪرڻو ھو سو ڪريان ھا).');
INSERT INTO "amroti" VALUES(11,81,'11:81','(ملائڪن) چيو ته اي لُوط اسين تنھنجي پالڻھار جا موڪليل آھيون (اُھي) تو وٽ ڪڏھن نه پھچي سگھندا پوءِ (تون) پنھنجي گھر وارن سوڌو ڪجھ رات ڏئي نِڪر ۽ اوھان کي جڳائي ته ڪو ھڪڙو پوءِ تي ڪافرن جي عذاب ڏانھن نه واجھائي پر تنھنجي زال (واجھائيندي)، ھن ڪري ته جو (عذاب) اُن (ڪافرن جي) قوم کي پھچندو سو اُن کي (به) پھچڻو آھي، سندن (عذاب پھچڻ جي) انجام جي مھل صبح آھي، صُبح ويجھو نه آھي ڇا؟');
INSERT INTO "amroti" VALUES(11,82,'11:82','پوءِ جنھن مھل اسان جو حُڪم پھتو ته اُن (ڳوٺ) کي ھيٺ مٿي ڪيوسون ۽ اُن تي پڪي مٽيءَ جا ڳوڙھا لڳولڳ وسايا سون.');
INSERT INTO "amroti" VALUES(11,83,'11:83','جو تنھنجي پالڻھار وٽان نشان ٿيل ھوا، ۽ اُھا (سزا) ظالمن کان پري نه ھئي.');
INSERT INTO "amroti" VALUES(11,84,'11:84','۽ مدين (جي قوم) ڏانھن سندن ڀاءُ شعيبؑ کي موڪليوسون، (شعيب) چيو ته اي منھنجي قوم الله جي عبادت ڪريو اُن کانسواءِ اوھان جو ڪو معبُود ڪونھي، ۽ ماڻ ۽ تور گھٽائي نه ڏيو آءٌ اوھان کي آسودو ڏسان ٿو آءٌ اوھان تي وڪوڙيندڙ ڏينھن جي عذاب (پھچڻ) کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(11,85,'11:85','۽ اي منھنجي قوم ماڻ ۽ تور انصاف سان پوري ڏيو وٺو ۽ ماڻھن کي سندين شيون گھٽائي نه ڏيو ۽ نڪي مُلڪ ۾ فسادي ٿي بگيڙو وجھو.');
INSERT INTO "amroti" VALUES(11,86,'11:86','جيڪڏھن اوھين مؤمن آھيو ته الله جي بچايل بچت اوھان لاءِ ڀلي آھي، ۽ آءٌ اوھان تي نگھبان نه آھيان.');
INSERT INTO "amroti" VALUES(11,87,'11:87','چيائون ته اي شعيب تنھنجي نماز توکي حڪم ڪيو آھي ڇا ته جنھن کي اسان جا ابا ڏاڏا پوڄيندا ھوا تنھن کي يا پنھنجن مالن ۾ جيڪي ڪرڻ گھرندا آھيون سو ڇڏي ڏيون، (چٿر ڪري چيائون) ته بيشڪ بردبار سنئين رستي وارو آھين.');
INSERT INTO "amroti" VALUES(11,88,'11:88','(شعيبؑ) چيو ته اي منھنجي قوم (اوھين ڏسو ته سھي ته آءٌ) جيڪڏھن پنھنجي پالڻھار کان چِٽي حُجّت تي ھجان ۽ پنھنجي پار کان چڱي روزي مون کي ڏني ھجيس (ته اوھان جو چيو ڪئن مڃيندس) ۽ جنھن کان اوھان کي جھليان ٿو تنھن کان اوھان (کي جھلڻ) جي ابتڙ ھلڻ نٿو گھران، جيڪي ٿي سگھيم سو سڌارڻ کانسواءِ نه گھرندس، ۽ الله (جي فضل) کانسواءِ مون کي ڪا سگھ نه آھي، مٿس ڀروسو ڪيم ۽ ڏانھس موٽان ٿو.');
INSERT INTO "amroti" VALUES(11,89,'11:89','۽ اي منھنجي قوم اوھان کي مون سان دشمني ڪرڻ اھڙي عذاب جو سبب نه بڻجي جھڙو عذاب نوح جي قوم يا ھود جي قوم يا صالح جي قوم کي پُھتو، ۽ لُوط جي قوم اوھان کان ڪا پري نه آھي.');
INSERT INTO "amroti" VALUES(11,90,'11:90','۽ پنھنجي پالڻھار کان بخشش گھرو وري اڳيانس توبه ڪريو، تحقيق منھنجو پالڻھار مھربان (سڀڪنھن جو) سڄڻ آھي.');
INSERT INTO "amroti" VALUES(11,91,'11:91','چيائون ته اي شعيب جيڪي تون چوين ٿو تنھن مان گھڻو نٿا سمجھون ۽ توکي پاڻ مان بلڪل ھيڻون ڏسون ٿا، ۽ جيڪڏھن تنھنجو ڪڙم نه ھجي ھا ته توکي پٿرن سان ماريون ھا، ۽ تون اسان تي ڪو سردار نه آھين.');
INSERT INTO "amroti" VALUES(11,92,'11:92','(شعيب) چيو ته اي منھنجي قوم اوھان وٽ منھنجو ڪڙم الله کان وڌيڪ پيارو آھي ڇا؟ ۽ اُن (الله جي حُڪم) کي پنھنجي پٺيءَ پويان اُڇلي ڇڏيو اٿوَ، تحقيق جيڪي ڪندا آھيو سو منھنجي پالڻھار جي قابو ۾ آھي.');
INSERT INTO "amroti" VALUES(11,93,'11:93','۽ اي منھنجي قوم اوھين پنھنجي پر تي عمل ڪريو آءٌ (به) عمل ڪندڙ آھيان، سگھوئي ڄاڻندؤ ته ڪنھن کي عذاب پھچي ٿو جو کيس خوار ڪندو ۽ جيڪو ڪوڙو آھي تنھن کي به، ۽ انتظار ڪريو آءٌ (به) اوھان سان گڏ انتظار ڪندڙ آھيان.');
INSERT INTO "amroti" VALUES(11,94,'11:94','۽ جنھن مھل اسان جو عذاب آيو تنھن مھل شعيبؑ کي ۽ جن ساڻس ايمان آندو ھو تن کي پنھنجي ٻاجھ سان بچايوسون، ۽ ظالمن کي سخت ڪڙڪي (اچي) پڪڙيو پوءِ پنھنجن گھرن ۾ گوڏن ڀر مري ويا.');
INSERT INTO "amroti" VALUES(11,95,'11:95','ڄڻڪ منجھن رھيا ئي نه ھوا، خبردار مدين وارن تي ڦِٽ ھجي جھڙي ثمود قوم تي لعنت ٿي.');
INSERT INTO "amroti" VALUES(11,96,'11:96','۽ بيشڪ موسىٰ کي پنھنجين نشانين ۽ پڌري حُجّت سان موڪليوسون.');
INSERT INTO "amroti" VALUES(11,97,'11:97','فرعون ۽ سندس سردارن ڏانھن پوءِ (سڀ ماڻھو) فرعون جي حُڪم جي پويان لڳا، ۽ فرعون جو حُڪم سڌو نه ھو.');
INSERT INTO "amroti" VALUES(11,98,'11:98','قيامت جي ڏينھن (فرعون) پنھنجي قوم جو اڳواڻ ٿيندو پوءِ کين باھ ۾ گھيڙيندو، ۽ جنھن گھٽ ۾ گھيڙيا ويندا سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(11,99,'11:99','۽ ھن (دنيا) ۾ (به) لعنت انھن کي پويان لڳائي ويئي ۽ قيامت جي ڏينھن (به) جيڪو (لعنت جو) انعام ملين سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(11,100,'11:100','(اي پيغمبر) اھي ڳوٺن جون خبرون آھن جي توتي بيان ڪريون ٿا انھن مان (ڪي) قائم (آھن) ۽ (ڪي) ناس ٿيل.');
INSERT INTO "amroti" VALUES(11,101,'11:101','۽ اسان انھن تي ظلم نه ڪيو پر ھنن پاڻ تي (پاڻھين) ظلم ڪيو پوءِ جنھن مھل تنھنجي پالڻھار جو حُڪم پھتو (ته) سندن معبودن جن کي الله کان سواءِ سڏيندا ھوا کائن ڪُجھ به نه ٽاريو، ۽ ھلاڪي کانسواءِ (ٻيو ڪي) کين نه وڌايائون.');
INSERT INTO "amroti" VALUES(11,102,'11:102','۽ ائين تنھنجي پالڻھار جو اُن مھل ڳوٺن (وارن) کي اُن حالت ۾ پڪڙڻ ٿيندو آھي جڏھن اُھي ظُلم ڪندڙ ٿيندا آھن، بيشڪ سندس پڪڙ سخت ڏکوئيندڙ آھي.');
INSERT INTO "amroti" VALUES(11,103,'11:103','بيشڪ ان (ڳالھ) ۾ انھيءَ لاءِ نشاني آھي جو آخرت جي عذاب کان ڊنو، اِھو اُھو ڏينھن آھي جنھن ۾ ماڻھن کي ميڙبو ۽ اِھو اُھو ڏينھن آھي جنھن ۾ (سڀ) حاضر ٿيندا.');
INSERT INTO "amroti" VALUES(11,104,'11:104','۽ مُقرّر مُدت کانسواءِ ان کي نه ٿا ڍر ڏيون.');
INSERT INTO "amroti" VALUES(11,105,'11:105','جنھن ڏينھن (اُھو) ايندو ته ڪو ماڻھو اُن (الله) جي موڪل کانسواءِ نه ڳالھائيندو، پوءِ اُنھن مان ڪي بدبخت ھوندا ۽ ڪي نيڪ بخت ھوندا.');
INSERT INTO "amroti" VALUES(11,106,'11:106','پوءِ جيڪي بدبخت ٿيا سي باہ ۾ (گھڙندا) انھن کي منجھس چيڪَ ۽ دانھَن ھوندي.');
INSERT INTO "amroti" VALUES(11,107,'11:107','مگر جيترو وقت تنھنجي پالڻھار گھريو ان کانسواءِ آسمانن ۽ زمين جي بقا ھجڻ تائين منجھس سدائين رھندا، بيشڪ تنھنجو پالڻھار جيڪي گھرندو آھي سو ڪندو آھي.');
INSERT INTO "amroti" VALUES(11,108,'11:108','۽ جيڪي نيڪ بخت ڪيا ويا سي بھشت ۾ (گھڙندا) مگر جيترو وقت تنھنجي پالڻھار گھريو (ان کانسواءِ) آسمانن ۽ زمين جي بقا ھجڻ تائين منجھس سدائين رھندا، (اِھا) بخشش اَڻ کٽ آھي.');
INSERT INTO "amroti" VALUES(11,109,'11:109','تنھنڪري اِھي جيڪا پوڄا ڪندا آھن تنھن کان تون شڪ ۾ نه رھ، جھڙو سندن پيءُ ڏاڏا اڳ پوڄيندا ھوا اِن کانسواءِ اِھي نه پوڄيندا آھن، ۽ اسين کين اَڻ کٽ (عذاب) جو سندن پورو حصّو ڏيندڙ آھيون.');
INSERT INTO "amroti" VALUES(11,110,'11:110','۽ بيشڪ موسىؑ کي ڪتاب ڏنوسون پوءِ منجھس اختلاف پيو، ۽ جيڪڏھن تنھنجي پالڻھار جو حڪم اڳي نه ٿي چڪي ھا ته سندن وچ ۾ فيصلو ڪيو وڃي ھا، ۽ اُھي کانئس وڏي شڪ ۾ پيل آھن.');
INSERT INTO "amroti" VALUES(11,111,'11:111','۽ جنھن مھل (اُٿاريا ويندا ته) سڀڪنھن کي تنھنجو پالڻھار سندن اعمالن جو پورو بدلو ڏيندو، ڇوته جيڪي ڪندا آھن تنھنجي اُھو خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(11,112,'11:112','پوءِ جئن توکي حُڪم ڪيو ويو آھي تئن تون مُحڪم رھ ۽ جن توسان توبه ڪئي (سي به محڪم رھن) ۽ حد کان نه لنگھو، بيشڪ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(11,113,'11:113','۽ ظالمن ڏانھن لاڙو نه ڪريو نه ته اوھان کي باھ لڳندي ۽ الله کان سواءِ ڪو اوھان جو دوست نه آھي وري مدد نه ڏبوَ.');
INSERT INTO "amroti" VALUES(11,114,'11:114','۽ ڏينھن جي اڳياڙيءَ ۽ پوياڙي جي وقتن ۾ ۽ ڪو وقت رات ۾ (به) نماز پڙھ، تحقيق چڱايون مداين کي نينديون آھن، نصيحت وٺندڙن لاءِ اِھا نصيحت آھي.');
INSERT INTO "amroti" VALUES(11,115,'11:115','۽ صبر ڪر بيشڪ الله ڀلارن جو اجر نه وڃائيندو آھي.');
INSERT INTO "amroti" VALUES(11,116,'11:116','پوءِ اوھان کان اڳ اڳين صدين وارن مان (گھڻا) سمجھ ڀريا ڇونه ٿيا جو (ماڻھن کي) ملڪ ۾ فساد (وجھڻ) کان جھلين ھا پر ڪي ٿورڙا ٿيا جن کي منجھائن بچايوسون، ۽ جيڪي ظالم ھوا سي جنھن (مال) ۾ آسودا ڪيا ويا تنھنجي پٺيان لڳا ۽ اُھي ڏوھاري ھوا.');
INSERT INTO "amroti" VALUES(11,117,'11:117','۽ (اي پيغمبر) تنھنجو پالڻھار اھڙو نه آھي جو ڳوٺن کي سندن سڌريل رھاڪُن ھوندي ظلم سان ناس ڪري ڇڏي.');
INSERT INTO "amroti" VALUES(11,118,'11:118','۽ جيڪڏھن تنھنجو پالڻھار گھري ھا ته (سڀني) ماڻھن کي ھڪ ئي مذھب (تي) ڪري ھا ۽ (اُھي) سدائين مختلف (مذھب تي) رھندا.');
INSERT INTO "amroti" VALUES(11,119,'11:119','پر جن تي تنھنجي پالڻھار رحم ڪيو (سي ھڪ ئي مذھب تي رھندا)، ۽ کين انھيءَ لاءِ (الله) پيدا ڪيو آھي، ۽ تنھنجي پالڻھار جو ٻولُ پورو ٿيو جو جنن ۽ ماڻھن مان گڏي دوزخ ضرور ڀريندس.');
INSERT INTO "amroti" VALUES(11,120,'11:120','۽ (اي پيغمبر) پيغمبرن جي خبرن مان تو وٽ ايترو بيان ڪريون ٿا جنھن سان تنھنجي دل کي مضبوط ڪريون، ۽ اُنھن (خبرن) ۾ حق ۽ مؤمنن لاءِ نصيحت ۽ سمجھاڻي آئي آھي.');
INSERT INTO "amroti" VALUES(11,121,'11:121','۽ (اي پيغمبر) جيڪي نه مڃيندا آھن تن کي چؤ ته اوھين پنھنجي جاءِ عمل ڪريو، اسين (به) عمل ڪندڙ آھيون.');
INSERT INTO "amroti" VALUES(11,122,'11:122','۽ انتظار ڪريو، اسين (به) انتطار ڪندڙ آھيون.');
INSERT INTO "amroti" VALUES(11,123,'11:123','۽ آسمانن ۽ زمين جو ڳُجھ رڳو الله کي (معلوم) آھي ۽ سڀ ڪم ڏانھس موٽايو ويندو آھي پوءِ سندس عبادت ڪر ۽ مٿس ڀروسو رک، ۽ جيڪي اوھين ڪندا آھيو تنھن کان تنھنجو پالڻھار بي خبر نه آھي.');
INSERT INTO "amroti" VALUES(12,1,'12:1','الرٰ، ھي پڌري ڪتاب جون آيتون آھن.');
INSERT INTO "amroti" VALUES(12,2,'12:2','بيشڪ اسان اُن کي قُرآن عربي (ٻوليءَ وارو) ڪري نازل ڪيو ته مانَ اوھين سمجھو.');
INSERT INTO "amroti" VALUES(12,3,'12:3','(اي پيغمبر) اسين توڏانھن ھن قرآن ۾ پنھنجي وحي موڪلڻ سان ڏاڍو چڱو قصّو توکي پڙھي ٻڌايون ٿا، ۽ تون ھن کان اڳ بيشڪ بي خبرن مان ھئين.');
INSERT INTO "amroti" VALUES(12,4,'12:4','(ياد ڪر) جڏھن يوسفؑ پنھنجي پيءُ کي چيو ته اي ابا مون يارھن تارا ۽ سج ۽ چنڊ ڏٺا انھن کي ڏٺم ته مون کي سجدو ٿا ڪن.');
INSERT INTO "amroti" VALUES(12,5,'12:5','(يعقوبؑ) چيو ته اي منھنجا پٽ پنھنجو خواب پنھنجن ڀائرن وٽ بيان نه ڪج نه ته تولاءِ ڪو ڦند ڦيريندا، بيشڪ شيطان انسان جو پڌرو ويري آھي.');
INSERT INTO "amroti" VALUES(12,6,'12:6','۽ اھري طرح تنھنجو پالڻھار توکي سڳورو ڪندو ۽ توکي خوابن جو تعبير سيکاريندو ۽ پنھنجي نعمت توتي ۽ يعقوبؑ جي اولاد تي اھڙي طرح پوري ڪندو جھڙي اڳ تنھنجي پيءُ ڏاڏي ابراھيمؑ ۽ اسحاقؑ تي پوري ڪيائين، بيشڪ تنھنجو پالڻھار ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(12,7,'12:7','بيشڪ يُوسفؑ ۽ سندس ڀائرن (جي قصي) ۾ پڇندڙن لاءِ (گھڻيون) نشانيون آھن.');
INSERT INTO "amroti" VALUES(12,8,'12:8','جڏھن (پاڻ ۾) چيائون ته بيشڪ يُوسفؑ ۽ سندس (سڳو) ڀاءُ (بن يامين) اسان جي پيءُ وٽ اسان کان ڏاڍا پيارا آھن جيتوڻيڪ اسين (گھڻا ڄڻا) ڏاڍي ٽولي آھيون، بيشڪ اسان جو پيءُ پڌري ڀُل ۾ آھي.');
INSERT INTO "amroti" VALUES(12,9,'12:9','(ھاڻي گھرجي ته) يُوسفؑ کي ڪُھو يا کيس ڪنھن ملڪ ۾ ڦٽي ڪري اچو ته اوھان جي پيءُ جي مھاڙ اوھان لاءِ نرالي ٿئي ۽ اُن (ڪم) کان پوءِ اوھين سڌريل ٿجو.');
INSERT INTO "amroti" VALUES(12,10,'12:10','منجھائن (ھڪ) چوندڙ چيو ته يُوسف کي نه ماريو ۽ جيڪڏھن (ڪي) ڪندڙ آھيو ته کوھ جي تري ۾ (کڻي) اُڇلايوس ته ڪو واٽھڙو کيس ڪڍي ويندو.');
INSERT INTO "amroti" VALUES(12,11,'12:11','(يعقوبؑ کي) چيائون ته اي بابا توکي ڇا ٿيو آھي يُوسفؑ جي نسبت ۾ توکي اسان تي ھن ھوندي به ڪو ڀروسو نٿو ٿئي جو اسين سندس خيرخواہ آھيون.');
INSERT INTO "amroti" VALUES(12,12,'12:12','سڀاڻي اسان سان کيس موڪل ته کائي ۽ کيڏي ۽ اسين سندس نگھبان آھيون.');
INSERT INTO "amroti" VALUES(12,13,'12:13','(يعقوبؑ) چيو ته اوھان جو کيس نئَڻ مون کي ضرور مونجھو ٿو ڪري ۽ ڊڄان ٿو ته (متان) کيس بگھڙ کائي ۽ کانئس اوھين بي خبر ھجو.');
INSERT INTO "amroti" VALUES(12,14,'12:14','چيائون ته اسان جي (گھڻن ڄڻن جي) ڏاڍي ٽولي ھوندي به جيڪڏھن کيس بگھڙ کاڌو ته اسين انھيءَ مھل ضرور ٽوٽي وارا ھونداسون.');
INSERT INTO "amroti" VALUES(12,15,'12:15','پوءِ جنھن مھل کيس نيائون (ته) اُن کي کوھ جي تري ۾ اڇلڻ جي پڪ ڪيائون، ۽ ڏانھس وحي ڪيوسون ته سندن ھن واقعي جي کين ضرور سڌ ڏيندين ۽ اُھي (توکي) نه ڄاڻندا.');
INSERT INTO "amroti" VALUES(12,16,'12:16','۽ پنھنجي پيءُ وٽ رات جو روئيندا آيا.');
INSERT INTO "amroti" VALUES(12,17,'12:17','چيائون ته اي بابا اسين ڊڪون پورائڻ وياسون ۽ يُوسف کي پنھنجي وسلن تي ڇڏيوسون پوءِ اُن کي بگھڙ کاڌو ۽ توڻي اسين سچا آھيون ته به تون اسان کي وسھندڙ نه ٿيندين.');
INSERT INTO "amroti" VALUES(12,18,'12:18','۽ سندس پھراڻ تي ڪوڙو رت لائي آندائون، (يعقوب) چيو ته (ائين نه آھي) بلڪ اوھان جي نفسن اوھان لاءِ (اُن) ٺاھ کي ٺاھيو آھي، تنھنڪري (مون کي) چڱو صبر ڪرڻ گھرجي، ۽ جيڪي اوھين بيان ڪندا آھيو تنھن تي الله کان مدد گھري وڃي ٿي.');
INSERT INTO "amroti" VALUES(12,19,'12:19','۽ (اُن کوھ تي) قافلو آيو پوءِ پنھنجي پاڻي ڀريندڙ کي موڪليائون جنھن پنھنجو ٻوڪو وڌو، چيائون وھ واھ! ھي نينگر آھي، ۽ کيس ھڪ (چڱي) وٿ سمجھي لڪايائون، ۽ جيڪي ڪيائون ٿي سو الله ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(12,20,'12:20','۽ کيس (ڀائرن) ھلڪيءَ بھا سان وڪيو جو ڪي ٿورا پئسا ھوا، سندس حق ۾ بيزاري ڪندڙ ھوا.');
INSERT INTO "amroti" VALUES(12,21,'12:21','۽ مصر وارن مان جنھن کيس ڳڌو تنھن پنھنجي زال (زليخا) کي چيو ته ھن جي چڱي تعظيم ڪج مانَ اسان کي ڪو فائدو ڏئي يا کيس پٽيلو ڪري رکون، ۽ ا ھڙي طرح يُوسفؑ کي مصر ۾ جاءِ ڏني سون، ۽ ھن لاءِ ته کيس خوابن جو تعبير سيکاريون، ۽ الله پنھنجي ڪم تي غالب آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(12,22,'12:22','۽ جنھن مھل (يُوسفؑ) پنھنجي جوانيءَ کي پھتو (ته) حِڪمت ۽ علم ڏنوسون، ۽ اھڙي طرح ڀلارن کي بدلو ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(12,23,'12:23','۽ جنھن زال جي گھر ۾ اُھو ھو تنھن سندس نفس کي راڻيو ٿي ۽ دروازا بند ڪيائين ۽ چيائين ته اوري آءُ، (يوسف) چيائين ته الله جي پناھ گھريم انھي منھنجي نپائيندڙ (يعني عزيز) مون کي چڱي ھنڌ رکيو آھي، ھيءَ (ڳالھ) سچي آھي ته ظالم نه ڇٽندا.');
INSERT INTO "amroti" VALUES(12,24,'12:24','۽ بيشڪ (اُھا) ان ڏانھن لڙي ۽ (يُوسفؑ) به اُن ڏانھن لڙيو، جيڪڏھن پنھنجي پالڻھار جو دليل نه ڏسي ھا (ته جيڪي ٿيڻو ھو سو ٿئي ھا)، اھڙي طرح (انھي ڪري ٿيو) ته بڇڙائي ۽ بي حيائي کانئس ٽاري سون، سچ آھي ته اھو اسان جي خالص ٻانھن مان ھو.');
INSERT INTO "amroti" VALUES(12,25,'12:25','۽ ٻئي دروازي ڏانھن ڊوڙيا ۽ زال ان (يُوسف) جو پھراڻ پٺيءَ کان ڦاڙيو ۽ سندس مڙس کي دروازي وٽ مليا، (زليخا) چيو ته جيڪو تنھنجي گھر واريءَ سان مدائي ڪرڻ جو ارادو ڪري تنھنجي سزا قيد ڪرڻ يا ڏکوئيندڙ عذاب کانسواءِ (ٻي ڪا) نه آھي.');
INSERT INTO "amroti" VALUES(12,26,'12:26','(يُوسفؑ) چيو ته ھن مون کي منھنجي نفس کان راڻيو ٿي ۽ سندس گھر جي ڀاتين مان ھڪ شاھد شاھدي ڏني، ته جيڪڏھن ھن جو پھراڻ اڳ کان ڦاڙيو ويو آھي ته (زليخا) سچي آھي ۽ اُھو ڪوڙن مان آھي.');
INSERT INTO "amroti" VALUES(12,27,'12:27','۽ جيڪڏھن سندس پھراڻ پٺيءَ کان ڦاڙيو ويو آھي ته (زليخا) ڪوڙي ۽ اھو سچن مان آھي.');
INSERT INTO "amroti" VALUES(12,28,'12:28','جنھن مھل (عزيز) پھراڻ پٺيءَ کان ڦاٽل ڏٺو (ته) چيائين ته (اي زالون) اھو (چوڻ) اوھان جي مڪرن مان آھي، ڇوته اوھان جو مڪر وڏو آھي.');
INSERT INTO "amroti" VALUES(12,29,'12:29','(اي) يوسف اِن (ڳالھ) کان منھن موڙ، ۽ (اي زليخا) تون (به) پنھنجي مدائيءَ جي بخشش گھر، ڇوته تون ئي خطا وارن مان آھين.');
INSERT INTO "amroti" VALUES(12,30,'12:30','۽ شھر ۾ ڪيترين زالن پچاريو ته عزيز جي زال پنھنجي جوان ٻانھي کي سندس نفس کان (بڇڙي ڪم لاءِ) راڻيندي آھي، بيشڪ (يُوسف جو) پيار سندس دل ۾ پيھي ويو آھي، اسين کيس پڌري گمراھيءَ ۾ ضرور ڏسنديون آھيون.');
INSERT INTO "amroti" VALUES(12,31,'12:31','پوءِ جنھن مھل (زليخا) سندن گلا ڪرڻ ٻڌي (ته) انھن ڏانھن (ماڻھو) موڪليائين ۽ انھن لاءِ مجلس تيار ڪيائين ۽ منجھائن ھر ھڪ کي ڪپ ڏنائين ۽ چيائين ته (اي يُوسفؑ) مٿن نِڪر، جنھن دم کيس ڏٺائون ته کيس وڌيڪ (سھڻو) ڄاتائون ۽ پنھنجا ھٿ وڍيائون، ۽ چيائون ته الله پاڪ وڏو آھي ھيءُ (نينگر) ماڻھو نه آھي، پر ھڪ سڳورو ملائڪ آھي.');
INSERT INTO "amroti" VALUES(12,32,'12:32','(زليخا) چيو اِھو اُھو آھي جنھن (جي محبت) ۾ اوھين مون کي ٽوڪينديون ھُيؤ، ۽ بيشڪ سندس نفس کان کيس راڻيم ٿي پوءِ پاڻ بچايائين، ۽ کيس جيڪي حُڪم ڪريان ٿي سو جيڪڏھن نه ڪندو ته ضرور قيد ڪيو ويندو ۽ ضرور بي فرمانن مان ٿيندو.');
INSERT INTO "amroti" VALUES(12,33,'12:33','(يُوسفؑ) چيو اي منھنجا پالڻھار جنھن (ڪم) ڏانھن مون کي سڏين ٿيون تنھن کان مون کي قيد پسند آھي، ۽ جيڪڏھن سندن مڪر مون کان نه ٽاريندين ته انھن ڏانھن لڙندس ۽ جاھلن مان ٿيندس.');
INSERT INTO "amroti" VALUES(12,34,'12:34','پوءِ سندس پالڻھار ان جي دُعا قبول ڪئي ۽ کانئس اُنھن جو مڪر ٽاريائين، ڇوته اُھو ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(12,35,'12:35','وري به نشانين جي ڏسڻ کان پوءِ کين ھيءَ (ڳالھ) نظر آئي ته ڪجھ وقت تائين کيس ضرور قيد وجھن.');
INSERT INTO "amroti" VALUES(12,36,'12:36','۽ ساڻس ٻه جوان قيد ۾ گھڙيا، انھن مان ھڪ چيو ته مون پاڻ کي (ننڊ ۾) ڏٺو ته شراب نپوڙيان ٿو، ۽ ٻئي چيو ته مون پاڻ کي مٿي تي ماني کڻندو ۽ منجھانئس پکين کي کائيندو ڏٺو، اسان کي اُن جي تعبير جي خبر ڏي، بيشڪ اسين توکي ڀلارن مان ڏسون ٿا.');
INSERT INTO "amroti" VALUES(12,37,'12:37','(يُوسفؑ) چيو ته جيڪو طعام اوھين کائيندا آھيو سو اوھان وٽ ايندو ئي نه پر اوھان وٽ ان جي اچڻ کان اڳيئي سندس تعبير آءٌ اوھان کي ڏسيندس، اِھو (تعبير ڪرڻ) انھن (ڳالھين) مان آھي جي منھنجي رب مونکي سيکاريون آھن، بيشڪ مون اُن قوم جي پر ڦٽي ڪئي آھي جيڪي الله کي نه مڃيندا آھن ۽ اُھي آخرت جا به مُنڪر آھن.');
INSERT INTO "amroti" VALUES(12,38,'12:38','۽ پنھنجي پيءُ ڏاڏن ابراھيمؑ ۽ اسحاقؑ يعقوبؑ جي دين جي پيروي ڪئي اٿم، ڪنھن شيءِ کي الله سان شريڪ ڪرڻ اسان کي نه جڳائيندو آھي، اھو اسان تي ۽ ٻـين ماڻھن تي الله جو فضل آھي پر گھڻا ماڻھو شڪرانو نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(12,39,'12:39','اي منھنجا سنگتي قيدي (ھيءَ ڳالھ سمجھندا آھيو ته) ڌارو ڌار گھڻا رب چڱا آھن يا اڪيلو غالب الله (چڱو آھي)؟');
INSERT INTO "amroti" VALUES(12,40,'12:40','الله کانسواءِ جن جي اوھين پوڄا ڪندا آھيو اُھي رڳو نالا آھن جي اوھان ۽ اوھان جي پيءُ ڏاڏن نالا رکيا آھن انھن جي بابت الله ڪا سندَ نازل نه ڪئي آھي، الله کانسواءِ ٻئي ڪنھن جي حڪومت نه آھي، حُڪم ڪيائين ته خاص اُن (الله) کانسواءِ ڪنھن جي عبادت نه ڪريو، اُھو دين قائم آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(12,41,'12:41','اي منھنجا قيدي سنگتي اوھان مان ھڪڙو پنھنجي بادشاھ کي شراب پياريندو، ۽ ٻيو سوريءَ چاڙھيو ويندو ۽ سندس مٿي مان پکي کائيندا، جنھن بابت پڇو ٿا سو ڪم پورو ٿي ويو.');
INSERT INTO "amroti" VALUES(12,42,'12:42','۽ انھن ٻنھي مان جنھن لاءِ گمان ھوس ته (نيٺ) ڇٽندو تنھن کي چيائين ته پنھنجي بادشاھ وٽ مون کي ياد ڪج، پوءِ شيطان سندس بادشاھ وٽ کانئس يادگيري وسارائي ڇڏي تنھنڪري (يُوسفؑ) ڪيترائي ورھيه قيد ۾ رھيو.');
INSERT INTO "amroti" VALUES(12,43,'12:43','۽ بادشاھ چيو ته مون (ننڊ) ۾ ست ٿُلھيون ڳـئون ڏٺيون آھن جن کي ستن ڏٻرين کاڌو ٿي ۽ (پڻ) ست ساوا سنگ ۽ ٻيا ست سُڪا (ڏٺا اٿم)، اي سردارؤ جيڪڏھن اوھين خواب جو تعبير ڪندا آھيو ته منھنجي خواب جو مون کي تعبير ٻڌايو.');
INSERT INTO "amroti" VALUES(12,44,'12:44','چيائون (اھي) پريشان خواب آھن، ۽ اسين ڪوڙن خوابن جو تعبير نه ڄاڻندا آھيون.');
INSERT INTO "amroti" VALUES(12,45,'12:45','۽ جيڪو ٻنھي مان ڇُٽو ھو ۽ گھڻي دير کانپوءِ ياد آيس تنھن چيو ته ان جي تعبير جي آءٌ توکي سُڌ ڏيندس پوءِ مونکي موڪليو.');
INSERT INTO "amroti" VALUES(12,46,'12:46','(پوءِ بندي خاني ڏانھن ويو چيائين) اي سچا يُوسفؑ ستن ٿلھين ڳئن بابت جن کي ستن ڏٻرين کاڌو ۽ ستن ساون سنگن ۽ ٻـين (ستن) سُڪن بابت تعبير ٻڌاءِ ته آءٌ ماڻھن ڏانھن موٽان ته مانَ اُھي ڄاڻن.');
INSERT INTO "amroti" VALUES(12,47,'12:47','(يوسف) چيو ته ست ورھيه ساندہ پوک ڪندا رھو، پوءِ جيڪو فصل لُڻو تنھن مان جيڪي کائو تنھن کانسواءِ ٻيو (سڀ) سندن سَنگن ۾ (رکي) ڇڏيو انھن ٿورن کانسواءِ جيڪي اوھين کائو.');
INSERT INTO "amroti" VALUES(12,48,'12:48','وري اُن کانپوءِ ست سال سختيءَ جا ايندا انھن لاءِ جيڪي (اَن) اڳي رکي ڇڏيو ھوندوَ سو جيڪي بچائي رکندؤ تنھن مان ٿورڙي کانسواءِ (ٻيو سڀ) کائي ويندا.');
INSERT INTO "amroti" VALUES(12,49,'12:49','وري اُن کانپوءِ اھڙو ورھ ايندو جنھن ۾ ماڻھن تي مينھن وسايو ويندو ۽ منجھس (ڊاک وغيرہ) نپوڙيندا.');
INSERT INTO "amroti" VALUES(12,50,'12:50','۽ بادشاھ چيو ته يُوسفؑ کي مون وٽ آڻيو، جنھن مھل وٽس قاصد آيو (ته ان وقت يُوسفؑ) چيو ته پنھنجي بادشاھ ڏانھن موٽ پوءِ کانئس پُڇ ته انھن زالن جو ڪھڙو حال ھو جن پنھنجا ھٿ وڍيا، ڇوته منھنجو پالڻھار سندن مِڪر کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(12,51,'12:51','(بادشاھ عورتن کان) پڇيو ته اوھان جو ڇا حال ھو جڏھن يُوسفؑ کي سندس نفس کان راڻيوَ ٿي، چيائون ته الله پاڪ آھي ڪا بُڇڙائي مٿس نه ڄاتي سون، عزيز جي زال چيو ته ھاڻي حق پڌرو ٿيو، مون کيس سندس نفس کان راڻڻ گھريو ھو ۽ اُھو بيشڪ سچن مان آھي.');
INSERT INTO "amroti" VALUES(12,52,'12:52','(يُوسفؑ چيو ته) اِھو ھن لاءِ ھو ته (عزيز) ڄاڻي ته مون پرپٺ سندس خيانت نه ڪئي ھئي ۽ الله خيانت ڪندڙن جو فريب پورو ٿيڻ نه ڏيندو آھي.');
INSERT INTO "amroti" VALUES(12,53,'12:53','۽ (آءٌ) پنھنجي نفس کي پاڪ نٿو ڀانيان، تحقيق نفس بڇڙائيءَ ڏانھن ڇڪيندو آھي پر جنھن تي مُنھنجو رب رحم ڪري، بيشڪ منھنجو پالڻھار بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(12,54,'12:54','۽ بادشاھ چيو ته کيس مون وٽ آڻيو ته (آءٌ) کيس پاڻ لاءِ مقرر ڪريان، پوءِ جنھن مھل ساڻس ڳالھايائين (تنھن مھل سندس سياڻپ سمجھي) چيائين ته تون اڄ اسان وٽ وڏي مرتبي وارو امانت وارو آھين.');
INSERT INTO "amroti" VALUES(12,55,'12:55','(يوسف) چيو ته مون کي ھن مُلڪ (مصر) جي خزانن تي مقرر ڪر، تحقيق آءٌ نگھبان (سڀني ڪمن جو) وڌيڪ ڄاڻندڙ آھيان.');
INSERT INTO "amroti" VALUES(12,56,'12:56','۽ اھڙي طرح يُوسف کي (انھي) ملڪ ۾ مرتبي وارو ڪيوسون، جتي وڻندو ھوس تِتي ٽڪندو ھو، جنھن لاءِ وڻندو اٿؤن تنھن تي ٻاجھ پھچائيندا آھيون ۽ نيڪَن جو اجر نه وڃائيندا آھيون.');
INSERT INTO "amroti" VALUES(12,57,'12:57','۽ جن ايمان آندو ۽ پرھيزگاري ڪندا رھيا تن لاءِ آخرت جو اجر ضرورڀلو آھي.');
INSERT INTO "amroti" VALUES(12,58,'12:58','۽ يُوسف جا ڀائر آيا پوءِ وٽس ويا ته کين سڃاتائين ۽ اُھي کيس نه سڃاڻندا ھوا.');
INSERT INTO "amroti" VALUES(12,59,'12:59','۽ جنھن مھل انھن لاءِ سندن سامان تيار ڪيائين (تنھن مھل کين) چيائين ته پنھنجي پيئيتي ڀاءُ کي مون وٽ آڻيو، اوھين نه ٿا ڏسو ڇا ته آءٌ پورو ماڻ ڏيان ٿو ۽ آءٌ چڱو مھماني ڪندڙ آھيان؟');
INSERT INTO "amroti" VALUES(12,60,'12:60','پوءِ جيڪڏھن کيس مون وٽ نه آڻيندؤ ته اوھان لاءِ مون وٽ ڪا مئپ ڪانھي ۽ نڪي مون کي ويجھا ٿجو.');
INSERT INTO "amroti" VALUES(12,61,'12:61','چيائون سگھو ان بابت (ڪنھن بھاني سان) سندس پيءُ کي راڻينداسون ۽ ضرور اسين (ايئن) ڪرڻ وارا ٿينداسون.');
INSERT INTO "amroti" VALUES(12,62,'12:62','۽ (يوسف) پنھنجن ماڻھن کي چيو ته سندين موڙي سندين مڏين ۾ وجھي ڇڏيو پوءِ جڏھن پنھنجن گھرن ڏانھن موٽي وڃن مانَ انھي کي سڃاڻن مانَ اُھي وري اچن.');
INSERT INTO "amroti" VALUES(12,63,'12:63','پوءِ جنھن مھل پنھنجي پيءُ وٽ (ڪنعان ۾) موٽي آيا (تنھن مھل) چيائون ته اي ابا (اَنَّ) مئي ڏيڻ اسان کان روڪيو ويو آھي تنھنڪري اسان مان اسان جي ڀاءُ (بنيامين) کي موڪل (ته) مئپ وٺون ۽ اسين سندس ضرور نگھبان آھيون.');
INSERT INTO "amroti" VALUES(12,64,'12:64','(يعقوب کين) چيو ته ھن جي نسبت اوھان تي اھڙو ئي اعتبار ٿو ڪريان جھڙو اڳ سندس ڀاءُ جي به نسبت اوھان تي اعتبار ڪيو ھوم، پوءِ الله چڱو نگھبان آھي، ۽ اُھو ٻاجھارن کان کان وڏو ٻاجھارو آھي.');
INSERT INTO "amroti" VALUES(12,65,'12:65','پوءِ جنھن مھل پنھنجو سامان کوليائون (ته اُن وقت) پنھنجي موڙي پاڻ وٽ موٽايل ڏٺائون، چيائون ته اي اسان جا ابا (ٻيو) اسان کي ڇا گھرجي، (جو) ھي اسان جي موڙي اسان ڏانھن وري موٽايل آھي (ته موٽي وڃون)، ۽ پنھنجن گھر وارن لاءِ اَن آڻيون ۽ پنھنجي ڀاءُ جي نگھباني ڪنداسون ۽ ھڪ اُٺ جو بار وڌيڪ به وٺنداسون، اِھو (جيڪي اَن آندو اٿئون سو) ماڻ ٿورو آھي.');
INSERT INTO "amroti" VALUES(12,66,'12:66','(يعقوب کين) چيو ته (آءٌ) اوھان سان ان کي (ايسين) ڪڏھن نه موڪليندس جيسين مون وٽ انجام ڪري الله جو سُنھن (نه) کڻندؤ ته اوھين ڪنھن اوکائي پوڻ کانسواءِ ان کي مون وٽ ضرور آڻيندؤ، پوءِ جنھن مھل کيس پنھنجو پڪو انجام ڏنائون (ته يعقوب) چيو ته جيڪي اسين چئون ٿا تنھن تي الله نگھبان آھي.');
INSERT INTO "amroti" VALUES(12,67,'12:67','۽ چيائين ته اي پٽؤ اوھين ھڪ دروازي کان نه گھِڙجو ۽ الڳ الڳ دروازن مان گھڙجو، ۽ آءٌ اوھان کان الله جي تقدير مان ڪابه شيء ٽاري نه ٿو سگھان، الله کانسواءِ (ٻئي) ڪنھن جو حُڪم نه آھي، مٿس ڀروسو ڪيم، ۽ سڀڪنھن ڀروسي ڪندڙ کي مٿس ڀروسو ڪرڻ گھرجي.');
INSERT INTO "amroti" VALUES(12,68,'12:68','۽ جھڙي طرح سندن پيءُ کين حُڪم فرمايو ھو تھڙي طرح جنھن مھل (مصر جي دروازن کان) گھڙيا، (تنھن مھل جيڪا سختي پھچين ھا) ته سندن پيءُ الله جي تقدير مان کائن ڪجھ به ٽاري نه ٿي سگھيو (اھو حُڪم ڪرڻ) يعقوب جي دل ۾ رڳو ھڪ خيال ھو جو اُن کي پورو ڪيائين، ۽ جيڪي کيس سيکاريو ھوسون تنھن سببان (اُھو) علم وارو ھو پر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(12,69,'12:69','۽ جنھن مھل يُوسف وٽ ويا تنھن مھل اُن پنھنجي (سڳي) ڀاءُ کي پاڻ وٽ جاءِ ڏني چيائين ته سچ پچ آءٌ تنھنجو ڀاءُ آھيان تنھنڪري جيڪي ڪيو اٿن تنھن تي ڏک نه ڪر.');
INSERT INTO "amroti" VALUES(12,70,'12:70','پوءِ جنھن مھل اُنھن لاءِ سندن سامان سنبرايائين (تنھن مھل) پنھنجي (سڳي) ڀاءُ جي مڏيءَ ۾ پيالو لڪايائين وري آواز ڪندڙ آواز ڪيو ته اي قافلي وارؤ اوھين ضرور چور اھيو.');
INSERT INTO "amroti" VALUES(12,71,'12:71','(قافلي وارن ٻڌي) انھن ڏانھن مُنھن ڪري چيو ته ڇا وڃايو اٿو؟');
INSERT INTO "amroti" VALUES(12,72,'12:72','چيائون ته بادشاھ جو پيالو وڃايو اٿون ۽ جنھن اُھو (لھي) آندو تنھن لاءِ اُٺ جو بار (انعام) آھي ۽ آءٌ (ان انجام) جو ذمي وار آھيان.');
INSERT INTO "amroti" VALUES(12,73,'12:73','چيائون ته الله جو قسم آھي ته اوھان کي خبر ھوندي ته اسين ھن لاءِ نه آيا آھيون ته مُلڪ ۾ ڏڦيڙ وجھون ۽ نڪي اسين چور آھيون.');
INSERT INTO "amroti" VALUES(12,74,'12:74','(وري نوڪرن) چيو ته جيڪڏھن ڪوڙا ھُجو ته اُن (چور) جي سزا ڪھڙي آھي؟');
INSERT INTO "amroti" VALUES(12,75,'12:75','چيائون ته اُن جي سزا (ھيء) آھي ته جنھنجي مڏيءَ مان اُھو لڌو سو (پاڻ) ان جو بدلو آھي، اھڙي طرح اسين ظالمن کي سزا ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(12,76,'12:76','پوءِ (يُوسف) پنھنجي ڀاءُ جي ٻوري (ڳولڻ) کان اڳ انھن (ٻـين) جون ٻوريون ڳولڻ شروع ڪيون وري اُھو (پيالو) پنھنجي ڀاءُ جي ٻوري مان ڪڍيائين، اھڙي طرح يُوسف کي تجويز سيکاري سون، جو بادشاہ جي قانون موجب پنھنجي ڀاءُ کي الله جي گھُر کانسواءِ وٺي نٿي سگھيو، جنھن کي گھرندا آھيون تنھن کي درجن ۾ مٿاھون ڪندا آھيون، ۽ ھر ڪنھن سياڻي جي مٿان سياڻو آھي.');
INSERT INTO "amroti" VALUES(12,77,'12:77','چيائون ته جيڪڏھن (ھن) چوري ڪئي آڳي ته اڳ سندس ڀاءُ (به) بيشڪ چوري ڪئي ھئي، پوءِ ان (ڳالھ) کي يوسف پنھنجي دل ۾ لڪايو ۽ کين اُھا پڌري نه ڪيائين، (دل ۾) چيائين ته اوھين بڇڙي مان وارا آھيو، ۽ جيڪي بيان ڪريو ٿا سو الله چڱو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(12,78,'12:78','چيائون ته اي عزيز ھن جو پيءُ پوڙھو وڏيءَ ڄمار وارو آھي اِن ڪري اسان مان ھڪڙو سندس بدران وٺ، اسين توکي نيڪن مان ڏسون ٿا.');
INSERT INTO "amroti" VALUES(12,79,'12:79','چيائين الله پناھ ۾ رکي جنھن وٽ پنھنجو سامان لڌوسون تنھن کانسواءِ ٻئي ڪنھن کي پڪڙيون (جيڪڏھن وٺون ته) انھي مھل اسين ظالم آھيون.');
INSERT INTO "amroti" VALUES(12,80,'12:80','جنھن مھل کانئس نا اميد ٿيا (تنھن مھل) نويڪلا ٿي صلاح ڪندڙ ٿيا، سندن وڏي (ڀاءُ) چيو ته نه ڄاڻندا آھيو ڇا؟ ته اوھان جي پيءُ اوھان کان الله جو پڪو انجام قسم تي ورتو آھي ۽ اڳ اوھان يوسف جي نسبت ۾ ڪجھ نه گھٽايو ھو، تنھنڪري (جيسين) منھنجو پيءُ مون کي موڪل (نه) ڏيندو يا الله مون لاءِ حُڪم نه ڪندو تيسين آءٌ ھن مُلڪ مان ڪڏھن نه نڪرندس، ۽ اُھو چڱو حُڪم ڏيڻ وارو آھي.');
INSERT INTO "amroti" VALUES(12,81,'12:81','اوھين پنھنجي پيءُ وٽ موٽي وڃو پوءِ چئو ته اي ابا تنھنجي پُٽ چوري ڪئي، ۽ جيڪو ڄاتوسون ان کانسواءِ ٻئي (ڳالھ) جي شاھدي نه ڏنيسون ۽ ڳُجھ (جي خبر) نه ڄاڻندا ھواسون.');
INSERT INTO "amroti" VALUES(12,82,'12:82','۽ جنھن ڳوٺ ۾ ھواسون انھي کان ۽ جنھن قافلي ۾ آياسون تنھن کان پُڇا ڪر، اسين ضرور سچا آھيون.');
INSERT INTO "amroti" VALUES(12,83,'12:83','(يعقوب) چيو ته (نه!) بلڪ اوھان جي نفس اوھان لاءِ (ڪا) ڳالھ ٺاھي آھي، تنھنڪري چڱو صبر ڪرڻ (منھنجو ڪم) آھي، اميد آھي ته الله انھن سڀني کي مون وٽ آڻيندو، ڇوته اُھو ڄاڻندڙ حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(12,84,'12:84','۽ کانئن مُنھن موڙيائين ۽ چيائين مون کي يُوسف لاءِ افسوس آھي ۽ سندس ٻئي اکيون ڏک کان اڇيون ٿيون پوءِ اُھو ڏک ڀريل ھو.');
INSERT INTO "amroti" VALUES(12,85,'12:85','(۽ سندس گھراڻي وارن) چيو ته الله جو قسم آھي ته تون سدائين يوسف کي ساريندو آھين جيسين بيمار ٿئين يا مري وڃين.');
INSERT INTO "amroti" VALUES(12,86,'12:86','چيائين ته آءٌ پنھنجو ڏک ۽ بيقراري الله جي جناب ۾ بيان ڪندو آھيان ۽ الله جي طرف کان اھو ڄاڻندو آھيان جو اوھين نه ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(12,87,'12:87','اي منھنجا پٽؤ وڃو ۽ يُوسف ۽ سندس ڀاءُ جي ڳولا ڪريو ۽ الله جي رحمت کان نا اميد نه ٿيو، ڇوته الله جي ٻاجھ کان ڪافرن جي قوم کانسواءِ (ٻيو) ڪو (به) نا اميد نه ٿيندو آھي.');
INSERT INTO "amroti" VALUES(12,88,'12:88','پوءِ جنھن مھل يوسف وٽ آيا (تنھن مھل) چيائون ته اي عزيز اسان کي ۽ اسان جي گھر وارن کي ڏک پھتو آھي ۽ نڪمو سامان کڻي آيا آھيون پوءِ اسان کي پوري مئپ ڏي ۽ اسان کي (ڪجھ) خيرات (به) ڏي، ڇوته الله خيرات ڪندڙن کي چڱو بدلو ڏيندو آھي.');
INSERT INTO "amroti" VALUES(12,89,'12:89','(يُوسف) کين چيو ته جڏھن اوھين بي سمجھ ھيؤ ته جيڪي اوھان يُوسف ۽ سندس ڀاءُ سان ڪيو سو ڄاتو اٿوَ؟');
INSERT INTO "amroti" VALUES(12,90,'12:90','چيائون ته تون ئي پڪ يُوسف آھين ڇا؟ چيائين (ھائو) آءٌ يُوسف آھيان ۽ ھيءُ منھنجو ڀاءُ آھي، بيشڪ الله اسان تي ٻاجھ ڪيو آھي، جيڪو پرھيز گاري ڪندو ۽ صبر ڪندو ته الله ڀلارن جو اجر نه وڃائيندو آھي.');
INSERT INTO "amroti" VALUES(12,91,'12:91','چيائون ته الله جو قسم آھي ته بيشڪ الله توکي اسان کان ڀلو ڪيو آھي ۽ بيشڪ اسين خطا ڪندڙ ھواسون.');
INSERT INTO "amroti" VALUES(12,92,'12:92','(يوسف) چيو ته اڄ اوھان تي ڪا ميار نه آھي، الله اوھان کي بخشيندو، ۽ اُھو (سڀني) ٻاجھارن کان بلڪل ٻاجھارو آھي.');
INSERT INTO "amroti" VALUES(12,93,'12:93','ھي منھنجو پھراڻ نيو پوءِ اُھو منھنجي پيءُ جي مُنھن تي گھمايو ته ڏسندڙ ٿيندو، ۽ مڙئي پنھنجي گھر جا آدمي مون وٽ آڻيو.');
INSERT INTO "amroti" VALUES(12,94,'12:94','۽ جنھن مھل قافلو (مصر مان) نڪتو (ته) سندن پيءُ چيو ته جيڪڏھن (اوھين) مون کي ڀورڙو نه ڄاڻو ته مون کي يُوسف جي بوءِ اچي ٿي.');
INSERT INTO "amroti" VALUES(12,95,'12:95','چيائون ته الله جو قسم ته تون اڳئين ويساري ۾ آھين.');
INSERT INTO "amroti" VALUES(12,96,'12:96','جنھن مھل خوشخبري ڏيندڙ آيو ته سندس مُنھن تي اھو (پھراڻ) گھمايائين ته موٽي ڏسندڙ ٿيو، چيائين ته اوھان کي نه چيو ھوم ڇا، ته آءٌ الله جي پار کان اھو ڄاڻندو آھيان جو اوھين نه ڄاڻندا آھيو؟');
INSERT INTO "amroti" VALUES(12,97,'12:97','چيائون ته اي اسان جا ابا اسان لاءِ اسان جي گناھن جي بخشش گھُر اسين بيشڪ گنھگار آھيون.');
INSERT INTO "amroti" VALUES(12,98,'12:98','(يعقوب) چيو ته آءٌ سِگھو اوھان لاءِ پنھنجي پالڻھار کان بخشش گھرندس، ڇوته اُھو ئي بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(12,99,'12:99','جنھن مھل يُوسف وٽ پُھتا (ته اُن وقت) پنھنجي ماءُ پيءُ کي پاڻ وٽ جاءِ ڏنائين ۽ چيائين جيڪڏھن الله گھريو ته مصر ۾ بي ڀوا ٿي گِھڙو.');
INSERT INTO "amroti" VALUES(12,100,'12:100','۽ پنھنجي ماءُ پيءُ کي تخت تي وھاريائين ۽ اُھي ڏانھس سجدو ڪندي ڪريا، ۽ (يُوسف) چيو ته اي منھنجا ابا ھيءُ منھنجي اڳين خوابن جو تعبير آھي، بيشڪ منھنجي پالڻھار ان کي سچو ڪيو، ۽ (الله) مون سان تڏھن بيشڪ احسان ڪيو جڏھن مون کي قيد مان ٻاھر ڪڍيائين ۽ منھنجي ۽ منھنجي ڀائرن جي وچ ۾ شيطان جھيڙو وڌو تنھن کانپوءِ اوھان کي ٻھراڙيءَ کان (شھر) ۾ آندائين، ڇوته منھنجو پالڻھار جنھن لاءِ وڻيس تنھن تي احسان ڪندڙ آھي، سچ آھي ته اُھو ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(12,101,'12:101','(يُوسف چيو ته) اي منھنجا پالڻھار بيشڪ مون کي بادشاھي ڏني اٿئي ۽ مون کي خوابن جو تعبير سيکاريو اٿئي، اي آسمانن ۽ زمين جا بڻائيندڙ، تون دُنيا ۽ آخرت ۾ منھنجو وارث آھين، مون کي مسلمان ڪري مار ۽ مون کي صالحن سان شامل ڪر.');
INSERT INTO "amroti" VALUES(12,102,'12:102','(اي پيغمبر) اھو (قصو) ڳُجھين خبرن مان آھي جو تو ڏانھن وحي ڪريون ٿا، انھن جڏھن فريب ڪندڙ ٿي پنھنجي ڪم جو پڪو ارادو ڪيائون تڏھن تون وٽن ڪونه ھئين.');
INSERT INTO "amroti" VALUES(12,103,'12:103','۽ (اي پيغمبر) جيتوڻيڪ حرص ڪرين ته به گھڻا ماڻھو ايمان آڻيندڙ نه آھن.');
INSERT INTO "amroti" VALUES(12,104,'12:104','۽ (تون) اُن (قرآن جي پُھچائڻ) تي کانئن ڪو اُجورو نه گھرندو آھين، اُھو (قرآن) جھانن جي نصيحت کانسواءِ (ٻيو ڪي) نه آھي.');
INSERT INTO "amroti" VALUES(12,105,'12:105','۽ ڪيتريون ئي نشانيون آسمانن ۽ زمين ۾ آھن جن تان لنگھندا آھن ۽ اُھي (ڏِسي ڪري) اُنھن (تي ڌيان ڏيڻ) کان مُنھن موڙيندا آھن.');
INSERT INTO "amroti" VALUES(12,106,'12:106','۽ منجھانئن گھڻا الله تي ايمان نه آڻيندا آھن پر اُھي (الله سان) شريڪ بڻائيندڙ آھن.');
INSERT INTO "amroti" VALUES(12,107,'12:107','(اُھي) الله جي عذاب مان پاڻ وٽ ڇِپ اچڻ کان يا قيامت جي اوچتي اچڻ کان بي ڀَوَا ٿيا آھن ڇا؟ جو اُھي نه ڄاڻندا ھُجن؟');
INSERT INTO "amroti" VALUES(12,108,'12:108','(اي پيغمبر) چؤ ته ھيءُ منھنجي واٽ آھي پڌري حُجّت سان الله ڏانھن سڏيان ٿو آءٌ ۽ منھنجا تابعدار به (سڏيندا آھن)، ۽ الله پاڪ آھي ۽ مان مشرڪن مان نه آھيان.');
INSERT INTO "amroti" VALUES(12,109,'12:109','۽ (اي پيغمبر) اسان توکان اڳ شھر وارن مان مڙسن کانسواءِ ٻيو ڪونه موڪليوسون جن ڏانھن وحي ڪيوسون، پوءِ ڇونه زمين ۾ گھمي ڏسندا آھن ته جيڪي کانئن اڳ ھوا تن جي پڇاڙي ڪھڙي طرح ٿي؟ ۽ آخرت جو گھر انھن لاءِ ڀلو آھي جيڪي ڊڄندا آھن، پوءِ ڇونه ڄاڻندا آھيو؟');
INSERT INTO "amroti" VALUES(12,110,'12:110','(ايستائين ڍر ڏنيسون) جيستائين پيغمبر (مدد پھچڻ کان) نا اميد ٿيڻ لڳا ۽ (سندين قوم) ڀانيو ته انھن سان ڪوڙو انجام ڪيو ويو آھي وٽن اسان جي مدد پھتي پوءِ جنھن کي گھريوسون تنھن کي ڇوٽڪارو ڏنو ويو، ۽ گنھگار قوم کان اسان جو عذاب نه ٽاريو ويندو آھي.');
INSERT INTO "amroti" VALUES(12,111,'12:111','بيشڪ ڌيان وارن لاءِ انھن (پيغمبرن) جي قِصّن ۾ عِبرت آھي، (ھي قرآن) بڻاوتي آکاڻي نه آھي پر جيڪي (ڪتاب) کانئس اڳ آھن تن کي سچو ڪندڙ آھي ۽ سڀڪنھن شيء لاءِ نکيڙ آھي ۽ ايمان واري قوم لاءِ ھدايت ۽ ٻاجھ آھي.');
INSERT INTO "amroti" VALUES(13,1,'13:1','المّرٰ، ھيءُ ڪتاب جون آيتون آھن، ۽ جيڪي توڏانھن تنھنجي پالڻھار وٽان نازل ڪيو ويو سو حق آھي پر گھڻا ماڻھو نه مڃيندا آھن.');
INSERT INTO "amroti" VALUES(13,2,'13:2','الله اُھو آھي جنھن آسمانن کي ريءَ ٿنڀن جي کڙو ڪيو آھي انھن کي ڏسو ٿا وري عرش ڏي متوجه ٿيو ۽ سج ۽ چنڊ کي فرمانبردار ڪيائين، جو سڀڪو ٺھرايل مُدّت تائين ھلندو آھي، (اُھو) سڀني ڪمن کي رٿيندو آھي نشانيون چٽائيءَ سان بيان ڪندو آھي ته مانَ اوھين پنھنجي پالڻھار جي مِلڻ جو يقين ڪريو.');
INSERT INTO "amroti" VALUES(13,3,'13:3','۽ اُھو (الله) آھي جنھن زمين کي پکيڙيو ۽ منجھس جبل ۽ درياء بڻايائين، ۽ منجھس سڀڪنھن ميوي مان ٻه ٻه قِسم بڻايائين ڏينھن کي رات سان ڍڪيندو آھي، بيشڪ اُن ۾ پروڙيندڙ قوم لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(13,4,'13:4','۽ زمين ۾ (پاڻون واڻا) ٽڪرا لڳولڳ آھن ۽ ڊاکن جا باغ آھن ۽ پوکون ۽ کجيون آھن (انھن مان ڪي) جُوھا ۽ (ڪي) ھڪ ٿُڙ واريون آھن اُنھن کي ھڪ (ئي) پاڻي پياريو ويندو آھي، ۽ اسين ميون ۾ ڪن (جي مزي) کي ڪِن کان وڌيڪ ڪندا آھيون، بيشڪ اُن ۾ سمجھندڙ قوم لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(13,5,'13:5','۽ (اي پيغمبر) جيڪڏھن توکي (سندن انڪار جو) عجب ٿيندو آھي ته سندن (ھن) چوڻ جو به عجب (ڪرڻو) آھي (جو چوندا آھن ته) جڏھن مٽي ٿينداسون ته اسين وري نئين بڻاوت ۾ بڻايا وينداسون ڇا؟ اِھي اُھي آھن جن پنھنجي پالڻھار جو انڪار ڪيو، ۽ اِھي اُھي آھن جن جي ڳچين ۾ ڳٽ ھوندا، ۽ اُھي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(13,6,'13:6','۽ (اي پيغمبر) چڱائي (جي پھچڻ) کان اڳي ئي توکان مدائي جلد گھرندا آھن ۽ بيشڪ کانئن اڳ (ماڻھن تي) عذاب ٿي چُڪا آھن، ۽ تنھنجو پالڻھار ماڻھن کي سندن ڏوھي ھجڻ ھوندي به بخشڻ وارو آھي، ۽ تنھنجو پالڻھار ڏاڍي عذاب ڪرڻ وارو (به) آھي.');
INSERT INTO "amroti" VALUES(13,7,'13:7','۽ ڪافر چوندا آھن ته ھن (پيغمبر) تي سندس پالڻھار کان ڪا (به) نشاني ڇونه نازل ڪئي وئي آھي؟ (اي پيغمبر) تون رڳو ڊيڄاريندڙ آھين ۽ سڀڪنھن قوم لاءِ ھدايت ڪرڻ وارو ھوندو آھي.');
INSERT INTO "amroti" VALUES(13,8,'13:8','سڀڪا مادي (پنھنجي پيٽ ۾) جيڪو (حمل) کڻندي آھي سو الله ڄاڻندو آھي ۽ ڳڀيرڻيون جيڪي گھٽائينديون آھن ۽ جيڪي وڌائينديون آھن (سو به ڄاڻندو آھي)، ۽ سڀڪا شيء وٽس اندازي سان آھي.');
INSERT INTO "amroti" VALUES(13,9,'13:9','ڳجھ ۽ ظاھر جو ڄاڻندڙ وڏو مٿاھون آھي.');
INSERT INTO "amroti" VALUES(13,10,'13:10','اوھان مان جيڪو ڳالھ ھوريان ڪري ۽ جيڪووڏي سڏ ڳالھائي ۽ جيڪو رات جو لِڪي ۽ ڏينھن جو پڌرو ٿي گھمي (سي سندس علم ۾) ھڪ جھڙا آھن.');
INSERT INTO "amroti" VALUES(13,11,'13:11','اُن (ماڻھوءَ) لاءِ (ملائڪ پھريدار) ھڪ ٻئي پٺيان ايندڙ سندس پويان (مقرر) آھن جو الله جي حُڪم سان اُن جي نگھباني ڪندا آھن، بيشڪ الله ڪنھن قوم جي حالت کي (ايستائين) نه ڦيرائيندو آھي جيسين اُھي جيڪي سندين دلين ۾ آھي سو (پاڻ) نه ڦيرائين، ۽ جڏھن الله ڪنھن قوم جي مدائيءَ جو ارادو ڪندو آھي ته اُن کي ڪو ٽَرڻ ڪونه آھي، ۽ نڪي انھن جو الله کانسواءِ ڪو مددگار آھي.');
INSERT INTO "amroti" VALUES(13,12,'13:12','اُھو (الله) آھي جيڪو اوھان کي ڀؤ ۽ اُميد لاءِ وِڄ ڏيکاريندو آھي ۽ ڳَرن ڪڪرن کي کڻندو آھي.');
INSERT INTO "amroti" VALUES(13,13,'13:13','۽ رعد ۽ (سڀ) ملائڪ سندس ڀؤ کان سندس ساراھ سان پاڪائي واکاڻيندا آھن، ۽ وِڄون موڪليندو آھي پوءِ اُھي جنھن لاءِ گھرندو آھي تنھن تي ڪيرائيندو آھي ۽ ڪافر الله بابت تڪرار ڪندا آھن، حالانڪ اُھو سخت عذاب ڪرڻ وارو آھي.');
INSERT INTO "amroti" VALUES(13,14,'13:14','کيس (ھر حال ۾) سڏڻ حق آھي، ۽ (ڪافر) الله کانسواءِ جن کي سڏيندا آھن سي (ٻي) ڪنھن (به) طرح ورندي ڏئي نه سگھندا (انھن سڏيندڙن جو مثال) رڳو انھيءَ (ماڻھوءَ) وانگر آھي جو پنھنجو ٻُڪ پاڻيءَ ڏانھن ھن لاءِ ڊگھيڙيندڙ ھجي ته پاڻي سندس وات ۾ پوي ۽ اُھو پاڻي سندس وات ۾ پوڻ وارو نه آھي، ۽ ڪافرن جو سڏڻ رڳو گمراھيءَ ۾ ھجڻ آھي.');
INSERT INTO "amroti" VALUES(13,15,'13:15','۽ جيڪي آسمانن ۽ زمين ۾ آھي سي سَرھا ٿي ۽ ارھا ٿي الله کي سجدو ڪندا آھن ۽ سندن پاڇا (به) صبح جو ۽ سانجھيءَ جو (سجدو ڪندا آھن).');
INSERT INTO "amroti" VALUES(13,16,'13:16','(اي پيغمبر کانئن) پُڇ ته آسمانن ۽ زمين جو پالڻھار ڪير آھي، (سندن پاران) چؤ ته الله، (وري کانئن) پڇا ڪر ته پوءِ الله کانسواءِ ٻيا اھڙا مددگار ڇو ورتا اَٿو جي پنھنجي جيئن کي نڪي سُک ۽ نڪي ڏک پھچائي سگھندا آھن؟ (۽ اي پيغمبر کين) چؤ ته انڌو ۽ سڄو ڪو برابر آھي ڇا؟ يا اونداھيون ۽ سوجھرو برابر آھن ڇا؟ الله لاءِ (اھڙا) شريڪ مُقرر ڪيا اٿن ڇا جو (انھن) الله جي مخلوقات جھڙي (خلق) خلقي ھجي جو (اُھا) خلق مٿن ھڪ جھڙي ٿي پئي آھي، (اي پيغمبر کين) چؤ ته سڀڪنھن جو خلقڻھار الله آھي ۽ اُھوئي اڪيلو غالب آھي.');
INSERT INTO "amroti" VALUES(13,17,'13:17','(جنھن) آسمان کان پاڻي وَسايو پوءِ نيون پنھنجي قدر سارو وھيون پوءِ ڇَر اُڀامندڙ گجيءَ کي کنيو، ۽ جيڪا شيء زيورن يا سامان جي طلب ڪرڻ لاءِ باھ ۾ ڳاريندا آھن اُن تي اُن جھڙي گجي (ٿيندي) آھي، اھڙي طرح الله کَري ۽ کوٽي جو مثال ڏيندو آھي، پر گجي ته گُم ٿي ويندي آھي، ۽ جا شيء ماڻھن کي نفعو ڏيندي آھي سا زمين ۾ رھندي آھي، اھڙي طرح الله مثال ڏيندو آھي.');
INSERT INTO "amroti" VALUES(13,18,'13:18','جن پنھنجي پالڻھار جو حُڪم قبول ڪيو تن لاءِ ڀلائي آھي، ۽ جيڪي سندس (حُڪم) قبول نه ٿا ڪن سي جيڪي زمين ۾ آھي سو سڀ ۽ پڻ اوترو ساڻس (ٻيو) انھن کي ھجي جيڪڏھن عوض ڏين (ته به قبول نه ڪبو)، انھن لاءِ حساب جي سختي آھي، ۽ سندين جاءِ دوزخ آھي، ۽ اُھا بڇڙي جاءِ آھي.');
INSERT INTO "amroti" VALUES(13,19,'13:19','پوءِ جيڪي توڏانھن تنھنجي پالڻھار کان نازل ڪيو ويو آھي تنھن کي جيڪو ڄاڻندو آھي ته سچ آھي سو ڪو اُن جھڙو آھي ڇا جيڪو انڌو ھجي؟ سمجھ وارن کانسواءِ ڪو نصيحت نه وٺندو آھي.');
INSERT INTO "amroti" VALUES(13,20,'13:20','جيڪي الله جو انجام پاڙيندا آھن ۽ انجام نه ڀڃندا آھن.');
INSERT INTO "amroti" VALUES(13,21,'13:21','۽ الله جنھن جي ڳنڍڻ جو حُڪم ڪيو تنھن کي جيڪي (ماڻھو) ڳنڍيندا آھن ۽ پنھنجي پالڻھار کان ڊڄندا آھن ۽ حساب جي سختيءَ جو ڀؤ ڪندا آھن.');
INSERT INTO "amroti" VALUES(13,22,'13:22','۽ جن پنھنجي پالڻھار جي رضامندي حاصل ڪرڻ لاءِ صبر ڪيو ۽ نماز پڙھي ۽ جيڪي کين روزي ڏني سون تنھن مان ڳجھو ۽ پڌرو خرچيائون ۽ چڱاين سان مدايون ٽاريندا آھن تن لاءِ آخرت جو گھر آھي.');
INSERT INTO "amroti" VALUES(13,23,'13:23','(جي) ھميشگيءَ وارا باغ آھن اُن ۾ گھڙندا ۽ سندن زالن ۽ سندن اولاد مان جيڪي سڌريل ھوندا سي (به گھڙندا) ۽ سڀڪنھن دروازي کان مٿن ملائڪ گھڙندا.');
INSERT INTO "amroti" VALUES(13,24,'13:24','(چوندا ته) انھي ڪري جو (ڏکن ۾) صبر ڪيو ھيؤ سلامتي آھي اوھان تي پوءِ آخرت جو گھر ڀلو آھي.');
INSERT INTO "amroti" VALUES(13,25,'13:25','۽ جيڪي الله جو انجام ان جي پڪي ڪرڻ کان پوءِ ڀڃندا آھن ۽ الله جن ڪمن جي ڳنڍڻ جو حُڪم ڪيو آھي سو ڇنندا آھن ۽ زمين ۾ فساد وجھندا آھن، انھن تي لعنت آھي ۽ انھن لاءِ بڇڙو گھر آھي.');
INSERT INTO "amroti" VALUES(13,26,'13:26','الله جنھن لاءِ گھرندو آھي تنھن لاءِ روزي ڪشادي ڪندو آھي ۽ گھٽ ڪندو آھي، ۽ (ڪافر) دنيا جي حياتي سان خوش ٿيندا آھن، ۽ دنيا جي حياتي آخرت ۾ ٿوريءَ وٿ کانسواءِ نه آھي.');
INSERT INTO "amroti" VALUES(13,27,'13:27','۽ ڪافر چوندا آھن ته ھن جي پالڻھار کان ھن تي معجزو ڇونه نازل ڪيو ويو آھي؟ (اي پيغمبر کين) چؤ ته الله جنھن لاءِ گھرندو آھي تنھن کي ڀلائيندو آھي ۽ جيڪي موٽيو تنھن کي پاڻ ڏانھن ھدايت ڪندو آھي.');
INSERT INTO "amroti" VALUES(13,28,'13:28','(اِھي اُھي آھن) جن ايمان آندو ۽ سندن دليون الله جي ذڪر سان آرامي ٿينديون آھن، خبردار الله جي ذڪر سان دليون آرام وٺنديون آھن.');
INSERT INTO "amroti" VALUES(13,29,'13:29','جن ايمان آندو ۽ چڱا عمل ڪيا تن لاءِ (آخرت ۾) خوشي ۽ چڱي جاءِ آھي.');
INSERT INTO "amroti" VALUES(13,30,'13:30','اھڙي طرح توکي ھڪ اھڙيءَ ٽوليءَ ڏانھن جو بيشڪ کانئن اڳ ڪيتريون ٽوليون گذري ويون آھن ھن لاءِ موڪليوسون ته جيڪي توڏانھن وحي ڪيوسون سو کين پڙھي ٻڌائين ۽ اُھي رحمان جا مُنڪر آھن، (اي پيغمبر کين) چؤ ته اُھو منھنجو پالڻھار آھي اُن کانسواءِ ٻيو ڪو مَعبُود نه آھي، ۽ مٿس ڀروسو ڪيم ۽ ڏانھس منھنجو موٽڻ آھي.');
INSERT INTO "amroti" VALUES(13,31,'13:31','۽ جيڪڏھن سچ پچ قرآن اھڙو ھجي ھا جو اُن سان جبل ھلائجن ھا يا اُن سان زمين ٽڪر ٽڪر ڪجي ھا يا اُن سان مئلن کي ڳالھرائجي ھا (ته به اُھي ايمان نه آڻين ھا) بلڪ سڀ ڪم الله جي وس ۾ آھن، اڃا مؤمنن نه ڄاتو آھي ڇا ته جيڪڏھن الله گھُري ھا ته سڀني ماڻھن کي ھدايت ڪري ھا، ۽ ڪافرن کي سندن ڪرتوتن سببان سدائين سزا پھچندي آھي يا ايستائين سندن گھرن جي آس پاس پئي پھچندي جيسين الله جو انجام ايندو، بيشڪ الله پنھنجي انجام کي نه ڦيرائيندو آھي.');
INSERT INTO "amroti" VALUES(13,32,'13:32','۽ (اي پيغمبر) بيشڪ توکان اڳ پيغمبرن تي ٺـٺوليون ڪيون ويون ھيون پوءِ ڪافرن کي ڍر ڏنم وري کين پڪڙيم، پوءِ منھنجو عذاب ڪئن ھو.');
INSERT INTO "amroti" VALUES(13,33,'13:33','سڀڪنھن ماڻھو جيڪي ڪمايو تنھن جي جيڪو خبر رکندڙ ھجي (سو بيخبر بتن جھڙو آھي) ڇا؟ ۽ (ڪافرن) الله جا شريڪ مقرّر ڪيا آھن، (اي پيغمبر کين) چؤ ته اُنھن (جي وصفن) جا نالا بيان ڪريو، يا کيس اُھو جتائيندا آھيو ڇا جيڪي (ھو) زمين ۾ نه ڄاڻندو آھي؟ يا (بنا حقيقت جي الله سان شريڪ ڪرڻ اِھي) ڇڙيون ڳالھيون آھن، بلڪ ڪافرن کي سندن بڇڙو خيال سھڻو ڪري ڏيکاريو ويو آھي ۽ اُھي سنئين واٽ کان روڪيا ويا آھن، ۽ جنھن کي الله گمراھ ڪري تنھن کي ڪو ھدايت ڪرڻ وارو ڪونه آھي.');
INSERT INTO "amroti" VALUES(13,34,'13:34','اُنھن لاءِ دنيا جي حياتيءَ ۾ عذاب آھي ۽ بيشڪ آخرت جي سزا ته بلڪل سخت آھي، ۽ کين الله (جي عذاب) کان ڪو بچائڻ وارو نه آھي.');
INSERT INTO "amroti" VALUES(13,35,'13:35','جنھن بھشت جو پرھيزگارن سان انجام ڪيو ويو آھي تنھن جو بيان (ھيءُ) آھي، ته ان جي ھيٺان واھيون وھنديون آھن، (۽) اُن جا ميوا ھميشہ آھن ۽ اُن جي ڇانوَ (سدائين رھندڙ آھي) پرھيزگارن جي اھا پڇاڙي آھي، ۽ ڪافرن جي پڇاڙي باھ آھي.');
INSERT INTO "amroti" VALUES(13,36,'13:36','۽ جن کي ڪتاب ڏنو اٿئون سي جيڪي توڏانھن نازل ڪيو ويو آھي تنھن سان سرھا ٿيندا آھن ۽ (انھن) جماعتن مان ڪو اھڙو آھي جو ان جي ڪن (ڳالھين) جو انڪار ڪندو آھي، (اي پيغمبر کين) چؤ مون کي انھيءَ کانسواءِ ٻيو حُڪم نه ڪيو ويو آھي ته الله جي عبادت ڪريان ۽ ساڻس ڪنھن کي شريڪ نه ڪريان، ڏانھس (ماڻھن کي) سڏيان ٿو ۽ ڏانھس منھنجو موٽڻ آھي.');
INSERT INTO "amroti" VALUES(13,37,'13:37','۽ اھڙي طرح اُن (قرآن) کي (ھڪ) حُڪم (ڪندڙ) عربي (ٻوليءَ) ۾ موڪليوسون، ۽ (اي پيغمبر) جيڪڏھن تو پاڻ وٽ علم جي اچڻ کان پوءِ سندن سَڌن جي تابعداري ڪندين ته الله وٽان نڪو سنڀاليندڙ ۽ نڪو بچائيندڙ لھندين.');
INSERT INTO "amroti" VALUES(13,38,'13:38','۽ بيشڪ توکان اڳ (ڪيترائي) پيغمبر موڪلياسون ۽ کين زالون ۽ اولاد ڏنوسون، ۽ ڪنھن پيغمبر کي الله جي حُڪم کان سواءِ ڪو مُعجزو آڻڻ نه جڳائيندو آھي، ھر ڪنھن مُدّت لاءِ لکيل حُڪم آھي.');
INSERT INTO "amroti" VALUES(13,39,'13:39','جيڪي الله کي وڻندو آھي سو ميٽيندو آھي ۽ قائم رکندو آھي، ۽ وٽس اصل ڪتاب آھي.');
INSERT INTO "amroti" VALUES(13,40,'13:40','۽ (اي پيغمبر) جيڪو انجام ساڻن ڪريان ٿو (تنھن مان) جيڪڏھن توکي ڪجھ ڏيکاريان يا توکي ماريان ته (بھرحال) توتي پيغام پھچائڻ ۽ اسان تي حساب وٺڻ کانسواءِ (ٻيو ڪي) نه آھي.');
INSERT INTO "amroti" VALUES(13,41,'13:41','اڃا نه ڏسندا آھن ڇا ته اسين زمين کي سندس چوڌاري گھٽائيندا آھيون، ۽ الله جو به حُڪم ڪندو آھي ته سندس حُڪم کي ڪو روڪڻ وارو نه آھي، ۽ اُھو (الله) سگھو حساب وٺندڙ آھي.');
INSERT INTO "amroti" VALUES(13,42,'13:42','۽ جيڪي کائن اڳ ھوا تن بيشڪ فريب ڪيو پوءِ سڀ بڇڙي سزا الله جي ھٿ ۾ آھي، سڀڪو جيءُ جيڪا ڪمائي ڪندو آھي (سا الله) ڄاڻندو آھي، ۽ ڪافر سگھو ڄاڻندا ته ڪنھن لاءِ آخرت جو گھر آھي.');
INSERT INTO "amroti" VALUES(13,43,'13:43','۽ ڪافر چوندا آھن ته تون پيغمبر نه آھين، (کين) چؤ ته منھنجي وچ ۾ ۽ اوھان جي وچ ۾ الله شاھد بس آھي ۽ اُھو (به) جنھن وٽ ڪتاب جو علم آھي.');
INSERT INTO "amroti" VALUES(14,1,'14:1','الٓرٰ، ھي ڪتاب آھي جو (اي پيغمبر) اُھو اسان توڏانھن ھن لاءِ نازل ڪيو آھي ته ماڻھن کي اونداھين مان سوجھري ڏانھن ڪڍين، (۽) سندس رب جي حُڪم سان انھي ساراھيل جي واٽ تي آڻين.');
INSERT INTO "amroti" VALUES(14,2,'14:2','جنھن جو اُھو (سڀئي) آھي جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي، ۽ سخت عذاب جي خرابي انھن ڪافرن لاءِ آھي.');
INSERT INTO "amroti" VALUES(14,3,'14:3','جيڪي دنيا جي حياتيءَ کي آخرت جي حياتيءَ کان وڌيڪ پيارو رکندا آھن ۽ (ماڻھن کي) الله جي واٽ کان جھليندا آھن ۽ ان جي ڏنگائي گھرندا آھن، اھي وڏيءَ گمراھي ۾ آھن.');
INSERT INTO "amroti" VALUES(14,4,'14:4','۽ ڪنھن پيغمبر کي سندس قوم جي ٻوليءَ کانسواءِ ٻيء (ٻوليءَ) سان نه موڪليوسون (ھن لاءِ) ته انھن کي (پڌرو) بيان ڪري، پوءِ الله جنھن کي گھري ٿو تنھن کي ڀُلائي ٿو ۽ جنھن لاءِ وڻيس تنھن کي ھدايت ٿو ڪري، ۽ اُھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(14,5,'14:5','۽ بيشڪ موسىٰ کي پنھنجن معجزن سان موڪليوسون (چيوسون) ته پنھنجي قوم کي اونداھين مان سوجھري ڏانھن ڪڍ، ۽ کين الله (جي ڏمر) وارا ڏينھن ياد ڏيار، بيشڪ اُن ۾ ھر ڪنھن صابر شاڪر لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(14,6,'14:6','۽ (ياد ڪر) جڏھن موسىٰ پنھنجي قوم کي چيو ته پاڻ تي الله جون نعمتون ياد ڪريو جڏھن ته فرعون جي (انھن) ماڻھن کان اوھان کي بچايائين جيڪي اوھان کي سخت عذاب چکائيندا ھوا، ۽ اوھان جي پُٽ ڪُھندا ھوا ۽ اوھان جون ڌيئرون جيئريون ڇڏيندا ھوا، ۽ ھن ۾ اوھان جي پالڻھار کان اوھان لاءِ وڏي آزمائش ھُئي.');
INSERT INTO "amroti" VALUES(14,7,'14:7','۽ جڏھن اوھان جي پالڻھار اوھان کي خبردار ڪيو ته جيڪڏھن اوھين (منھنجو) شڪرانو ڪندؤ ته اوھان کي ضرور وڌيڪ ڏيندس ۽ جيڪڏھن بي شُڪري ڪندؤ ته بيشڪ منھنجو عذاب سخت آھي.');
INSERT INTO "amroti" VALUES(14,8,'14:8','۽ موسىٰؑ چيو ته اوھان جو ۽ سڀني جھان وارن جو انڪار ڪرڻ (الله کي ڪجھ نقصان لائي نه سگھندو) ڇوته الله بي پرواھ ساراھيل آھي.');
INSERT INTO "amroti" VALUES(14,9,'14:9','اوھان کي انھن جي سُڌ نه پھتي آھي ڇا جيڪي اوھان کان اڳ نوح ۽ عاد جي قوم جا ھوا، ۽ جيڪي کانئن پوءِ ھوا، جن کي الله کان سواءِ ڪو ڪونه ڄاڻندو ھو، سندن پيغمبر وٽن پڌرن مُعجزن سان آيا پوءِ پنھنجا ھٿ واتن ۾ ورايائون ۽ چيائون ته جنھن شيء سان اوھان کي موڪليو ويو آھي تنھن جا اسين مُنڪر آھيون ۽ جنھن ڏانھن اوھين اسان کي سڏيندا آھيو تنھن کان اسين سخت شڪ ۾ پيل آھيون.');
INSERT INTO "amroti" VALUES(14,10,'14:10','سندن پيغمبر چيو ته (اوھان کي) آسمانن ۽ زمين جي بڻائيندڙ الله جي بابت ڪو شڪ آھي ڇا؟ (اُھو) اوھان کي ھن لاءِ سڏيندو آھي ته اوھان جا گناھ اوھان کي بخشي ۽ ٺھرايل مُدّت تائين اوھان کي مُھلت ڏئي، چيائون ته اوھين به رڳو اسان جھڙا ماڻھو آھيو (تنھنڪري اوھانجي چوڻ تي ڪئن لڳون)، جن کي اسان جا ابا ڏاڏا پوڄيندا ھوا تن کان اسان کي جھلڻ جو ارادو رکندا آھيو (جي سچا آھيو) ته اسان وٽ ڪو پڌرو دليل آڻيو.');
INSERT INTO "amroti" VALUES(14,11,'14:11','سندن پيغمبر کين چيو ته اسين اوھان جھڙا ئي ماڻھو آھيون پر الله پنھنجي ٻانھن مان جنھن لاءِ گھرندو آھي تنھن تي احسان ڪندو آھي، ۽ الله جي حُڪم کانسواءِ اوھان وٽ ڪنھن دليل جي آڻڻ جي اسان کي ڪا سگھ نه آھي، ۽ مؤمنن کي جڳائي ته الله تي ڀروسو ڪن.');
INSERT INTO "amroti" VALUES(14,12,'14:12','۽ اسان کي ڇا (ٿيو) آھي ته الله تي ڀروسو نه ڪريون ۽ بيشڪ اسان کي اسانجي سِڌن رستن جي ھدايت ڪيائين، ۽ جيڪي اسان کي ڏکوئيندا آھيو تنھن تي ضرور صبر ڪنداسون، ۽ ڀروسي ڪندڙن کي الله تي ڀروسو ڪرڻ گھرجي.');
INSERT INTO "amroti" VALUES(14,13,'14:13','۽ ڪافرن پنھنجن پيغمبرن کي چيو ته اوھان کي پنھنجي مُلڪ مان ڪڍي ڇڏينداسون يا اسان جي مذھب ڏانھن ضرور موٽي اچو، پوءِ سندن پالڻھار انھن ڏانھن وحي ڪيو ته ظالمن کي ضرور ناس ڪنداسون.');
INSERT INTO "amroti" VALUES(14,14,'14:14','۽ اُنھن کان پوءِ مُلڪ ۾ اوھان کي ضرور رھائينداسين، اِھو انجام انھي لاءِ آھي جيڪو منھنجي اڳيان بيھڻ کان ڊڄي ۽ منھنجي دڙڪي کان ڊڄي.');
INSERT INTO "amroti" VALUES(14,15,'14:15','۽ (پيغمبرن) سوڀ جون دُعائون گھريون ۽ سڀڪو ھٺيلو ضدي نامراد ٿيو.');
INSERT INTO "amroti" VALUES(14,16,'14:16','ان جي پويان دوزخ آھي ۽ اُن کي پُونءَ جو پاڻي پياريو ويندو.');
INSERT INTO "amroti" VALUES(14,17,'14:17','ان کي ڍُڪ ڍُڪ ڪري پيئندو ۽ اُن کي ڳيت ڏئي پي نه سگھندو ۽ سڀڪنھن پاسي کان موت ايندس ۽ ھو مُئل نه ھوندو، ۽ ان جي پويان سخت عذاب آھي.');
INSERT INTO "amroti" VALUES(14,18,'14:18','جن پنھنجي پالڻھار کان انڪار ڪيو تن جي ڪرتوتن جو مثال انھيءَ ڪيريءَ جھڙو آھي جنھن کي آنڌيءَ واري ڏينھن ۾ واءُ اڏائي، جيڪي ڪمايائون تنھن جي ذري تي پُھچي نه سگھندا، اھا ئي وڏي گمراھي آھي.');
INSERT INTO "amroti" VALUES(14,19,'14:19','(اي پيغمبر) نه ڏٺو اٿئي ڇا ته الله آسمانن ۽ زمين کي رِٿ سان بڻايو آھي، جيڪڏھن وڻيس ته اوھان کي نئي ۽ ٻي نئين خلق آڻي.');
INSERT INTO "amroti" VALUES(14,20,'14:20','۽ اِھو (ڪرڻ) الله تي اوکو نه آھي.');
INSERT INTO "amroti" VALUES(14,21,'14:21','۽ مڙيئي الله جي اڳيان حاضر ٿيندا پوءِ ھيڻا ھٺيلن کي چوندا ته اسين اوھان جي پٺيان لڳا ھئاسون پوءِ اوھين الله جي عذاب مان ڪجھ اسان کان ٽارڻ وارا آھيو ڇا؟ (ھٺيلا) چوندا ته الله اسان کي ھدايت ڪري ھا ته اسين به اوھان کي ھدايت ڪريون ھا، (ھاڻي) اسان جو رئڻ رڙڻ يا اسان جو صبر ڪرڻ اسان لاءِ ھڪ جھڙو آھي اسان کي ڪا واہ ڪانه آھي.');
INSERT INTO "amroti" VALUES(14,22,'14:22','۽ جڏھن ڪم فيصل ڪيو ويندو تڏھن شيطان چوندو الله اوھان سان سچو انجام ڪيو ھو ۽ مون اوھان سان (ڪوڙو) وعدو ڪيو ھو پوءِ اوھان سان نه پاڙيم، مون کي اوھان تي اوھان جي سڏڻ کانسواءِ (ٻيو ڪو) زور نه ھو پوءِ اوھان منھنجو چيو قبول ڪيو، تنھنڪري مون کي ڦٺ جٺ نه ڪريو ۽ (ھاڻي) پاڻ کي ڦٺ جٺ ڪريو، آءٌ اوھان جي دانھن ورنائڻ وارو نه آھيان ۽ نڪي اوھين منھنجي دانھن ٻڌڻ وارا آھيو، اوھان اڳي مون کي (الله) جو شريڪ بڻايو ھو تنھن کان آءٌ بيزار آھيان، ڇوته ظالمن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(14,23,'14:23','۽ جن ايمان آندو ۽ چڱا ڪم ڪيا سي (انھن) باغن ۾ داخل ڪيا ويندا جن جي ھيٺان واھيون وھنديون آھن پنھنجي پالڻھار جي موڪل سان منجھن سدائين رھندڙ آھن، منجھس سندين کيڪار سلام آھي.');
INSERT INTO "amroti" VALUES(14,24,'14:24','(اي پيغمبر) توکي معلوم نه آھي ڇا؟ ته الله ڪھڙو مثال بيان ڪيو آھي پاڪ ڪلمون پاڪ وڻ وانگر آھي سنديس پاڙ مُحڪم آھي جنھن جون ٽاريون آسمان آھن.');
INSERT INTO "amroti" VALUES(14,25,'14:25','پنھنجي پالڻھار جي حُڪم سان سڀ ڪنھن وقت پنھنجا ميوا پچائيندو آھي، ۽ الله ماڻھن لاءِ مثال بيان ڪندو آھي مانَ اُھي نصيحت وٺن.');
INSERT INTO "amroti" VALUES(14,26,'14:26','۽ بڇڙي ڳالھ جو مثال بڇڙي وڻ وانگر آھي جو زمين تان پٽيو ويو اُن کي ڪو ٽِڪاءُ نه آھي.');
INSERT INTO "amroti" VALUES(14,27,'14:27','جن ايمان آندو تن کي الله دُنيا جي حياتيءَ ۾ ثابت قول سام مُحڪم رکندو آھي ۽ آخرت ۾ به، ۽ الله ظالمن کي ڀُلائيندو آھي، ۽ الله جيڪي گھرندو آھي سو ڪندو آھي.');
INSERT INTO "amroti" VALUES(14,28,'14:28','(اي پيغمبر) توکي اُھي معلوم نه آھن ڇا؟ جن بي شُڪريءَ سببان الله جي نعمتن کي مٽايو ۽ پنھنجي قوم کي ھلاڪيءَ جي گھر ۾ وڌو.');
INSERT INTO "amroti" VALUES(14,29,'14:29','جو دوزخ آھي، اُھي منجھس گھڙندا، اُھو بڇڙو ٽڪاڻو آھي.');
INSERT INTO "amroti" VALUES(14,30,'14:30','۽ الله سان شريڪ مقرّر ڪيائون ته سندس واٽ کان ماڻھن کي ڀُلائين، (اي پيغمبرکين) چؤ ته اوھين آسودا رھو ڇوته اوھان جو (دوزخ جي) باہ ڏانھن موٽڻ آھي.');
INSERT INTO "amroti" VALUES(14,31,'14:31','(اي پيغمبر) منھنجن اُنھن ٻانھن کي چؤ جن ايمان آندو آھي ته نماز پڙھندا رھن ۽ جيڪي کين رزق ڏنو اٿون تنھن منجھان ڳجھو ۽ پڌرو اُن ڏينھن جي اچڻ کان اڳ (الله جي واٽ ۾) خرچ ڪندا رھن جنھن ۾ نڪو واپار ۽ نڪا يراني ھوندي.');
INSERT INTO "amroti" VALUES(14,32,'14:32','الله اُھو آھي جنھن آسمانن ۽ زمين کي بڻايو ۽ آسمان کان (مينھن جو) پاڻي وسايو پوءِ اُن سان سڀڪنھن جنس جي ميون مان اوھان جي روزي ڪڍيائين، ۽ اوھان کي ٻيڙيون ھن لاءِ نِوائي ڏنائين ته سندس حُڪم سان سمنڊ ۾ ترنديون رھن، ۽ اوھان کي نديون (به) نوائي ڏنائين.');
INSERT INTO "amroti" VALUES(14,33,'14:33','۽ اوھان لاءِ سدائين ھلندڙ سج ۽ چنڊ نوائي ڏنائين، ۽ اوھان لاءِ رات ۽ ڏينھن نوائي ڏنائين.');
INSERT INTO "amroti" VALUES(14,34,'14:34','۽ جيڪا شيءِ کانئس گھريوَ تنھن سڀ مان ڪي اوھان کي ڏنائين، ۽ جيڪڏھن الله جي نعمتن کي ڳڻيندؤ ته اُھي ڳڻي نه سگھندؤ، بيشڪ ماڻھو ضرور بي انصاف بي شڪر آھي.');
INSERT INTO "amroti" VALUES(14,35,'14:35','۽ (ياد ڪر) جڏھن ابراھيم چيو ته اي منھنجا پالڻھار ھن شھر (مڪي) کي امن وارو ڪر ۽ مون کي ۽ منھنجي اولاد کي بُتن جي پوڄڻ کان پاسي ڪر.');
INSERT INTO "amroti" VALUES(14,36,'14:36','اي منھنجا پالڻھار انھن (بتن) ماڻھن مان گھڻن کي ڀُلايو آھي، پوءِ جيڪو منھنجي پويان لڳو سو بيشڪ منھنجو آھي، ۽ جنھن منھنجي نافرماني ڪئي ته تون بيشڪ بخشڻھار مھربان آھين.');
INSERT INTO "amroti" VALUES(14,37,'14:37','اي اسانجا پالڻھار مون پنھنجي اولاد مان ڪن کي غير آباد ميدان ۾ تنھنجي تعظيم واري گھر وٽ ھِن لاءِ ٽڪايو آھي ته اي اسانجا پالڻھار اُھي نماز پڙھن پوءِ ڪن ماڻھن جون دليون انھن ڏانھن لڙندڙيون ڪر ۽ کين ميون جي روزي ڏي ته مانَ اُھي شُڪرانو ڪن.');
INSERT INTO "amroti" VALUES(14,38,'14:38','اي اسان جا پالڻھار جيڪي اسين ڳجھو ڪريون ٿا ۽ جيڪي پڌرو ڪريون ٿا سو تون ڄاڻندو آھين، ۽ ڪابه شيءِ زمين ۾ الله کان ڳُجھي نه آھي ۽ نڪي آسمان ۾.');
INSERT INTO "amroti" VALUES(14,39,'14:39','سڀ ساراھ انھي الله کي جڳائي جنھن مون کي ٻڍاپي ۾ اسماعيل ۽ اسحاق عطا ڪيو، ڇوته منھنجو پالڻھار ضرور دُعا ٻڌندڙ آھي.');
INSERT INTO "amroti" VALUES(14,40,'14:40','اي منھنجا پالڻھار مون کي ۽ منھنجي اولاد مان ڪن کي نماز پڙھندڙ ڪر، اي اسان جا پالڻھار منھنجي دُعا قبول ڪر.');
INSERT INTO "amroti" VALUES(14,41,'14:41','اي اسان جا پالڻھار مون کي ۽ منھنجي ماءُ پيءُ کي ۽ مؤمنن کي (انھي ڏينھن) بخش جنھن ڏينھن حساب کڙو ٿيندو.');
INSERT INTO "amroti" VALUES(14,42,'14:42','۽ جيڪي ظالم ڪندا آھن تنھن کان الله کي ئي بي خبرنه ڀانءِ، الله رڳو کين انھي ڏينھن تائين ڍر ڏني آھي جنھن ڏينھن اکيون ٽڙي وينديون.');
INSERT INTO "amroti" VALUES(14,43,'14:43','پنھنجا ڳاٽ کڻندڙ ڊوڙندڙ ھوندا پاڻ ڏانھن سندن نظر وري نه سگھندي، ۽ سندين دليون پيون دھڪنديون.');
INSERT INTO "amroti" VALUES(14,44,'14:44','۽ (اي پيغمبر) ماڻھن کي اُن ڏينھن کان ڊيڄار (جنھن ڏينھن) کين عذاب پھچندو پوءِ ظالم چوندا ته اي اسان جا پالڻھار اسان کي ويجھي مُدّت توڻي مُھلت ڏي ته تنھنجي سڏڻ کي قبول ڪريون ۽ پيغمبرن جي فرمانبرداري ڪريون، (چيو ويندن ته) ھن کان اڳ اوھين قسم نه کڻندا ھيؤ ڇا ته اوھان کي ڪو زوال نه ٿيندو؟');
INSERT INTO "amroti" VALUES(14,45,'14:45','۽ اوھين انھن جي جاين ۾ ٽڪيؤ جن پاڻ تي ظلم ڪيو ۽ اوھان کي پڌرو ٿيو ته انھن سان ڪئن ڪيوسون ۽ اوھان کي ڪيئي مثال بيان ڪياسون.');
INSERT INTO "amroti" VALUES(14,46,'14:46','۽ بيشڪ انھن پنھنجو وڏو مڪر ڪيو ۽ الله وت سندن مڪر (جي سزا) آھي، ۽ سندن مڪر ڪو اھڙو نه آھي جنھن سان جبل ٽري وڃن.');
INSERT INTO "amroti" VALUES(14,47,'14:47','پوءِ (اي پيغمبر) الله کي پنھنجن پيغمبرن سان پنھنجي وعدي جي خلافي ڪندڙ ھرگز نه ڀانءِ، ڇوته الله غالب (اُن ڏينھن) بدلي وٺڻ وارو آھي.');
INSERT INTO "amroti" VALUES(14,48,'14:48','جنھن ڏينھن ھيءَ زمين مٽائي ٻي زمين ڪئي ويندي ۽ آسمان (به) ۽ (سڀ ماڻھو) الله ھڪڙي زبردست جي آڏو ٿيندا.');
INSERT INTO "amroti" VALUES(14,49,'14:49','۽ اُن ڏينھن گنھگارن کي زنجيرن ۾ ٻَڌل ڏسندين.');
INSERT INTO "amroti" VALUES(14,50,'14:50','سندن ڪپڙا گندرف مان ھوندا ۽ سندن مُنھن کي باھ وڪوڙيندي.');
INSERT INTO "amroti" VALUES(14,51,'14:51','ھن لاءِ ته سڀڪنھن جيءَ جيڪي ڪمايو تنھن جو اُن کي الله بدلو ڏئي، بيشڪ الله جلد حساب ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(14,52,'14:52','اِھو (قرآن) ماڻھن کي پھچائڻو آھي ته اُن سان ڊيڄاريا وڃن ۽ ڄاڻن ته انھي ھڪ الله کانسواءِ ٻيو ڪو معبود نه آھي ۽ ڌِيان وارا نصيحت وٺن.');
INSERT INTO "amroti" VALUES(15,1,'15:1','الٓرٰ، ھي آيتون ڪتاب ۽ پڌري قرآن جون آھن.');
INSERT INTO "amroti" VALUES(15,2,'15:2','ڪافر گھڻو ئي (ھيءَ) خواھش ڪندا ته جيڪر مُسلمان ھُجون ھا.');
INSERT INTO "amroti" VALUES(15,3,'15:3','(اي پيغمبر) کين ڇڏي ڏي ته (دُنيا ۾) کائن ۽ (لذتن ۾) آسودا رھي گذارين ۽ کين آسرو رينجھائي ڇڏي ۽ سگھوئي ڄاڻندا.');
INSERT INTO "amroti" VALUES(15,4,'15:4','۽ ڪنھن ڳوٺ کي ويران نه ڪيوسون (سندن بڇڙن ڪمن جي ڪري) پر اُن لاءِ مُقرّر حڪم ڪيل ھو.');
INSERT INTO "amroti" VALUES(15,5,'15:5','ڪائي ٽولي پنھنجي ٺھرايل مُدّت کان نڪي تڪڙ ڪندي ۽ نڪي پوءِ تي رھي سگھندي.');
INSERT INTO "amroti" VALUES(15,6,'15:6','۽ (ڪافرن) چيو ته اي شخص جنھن تي قرآن لاٿو ويو آھي بيشڪ تون ضرور چريو آھين.');
INSERT INTO "amroti" VALUES(15,7,'15:7','جيڪڏھن تون سچن مان آھين ته اسان وٽ ملائڪ ڇو نه آڻيندو آھين؟');
INSERT INTO "amroti" VALUES(15,8,'15:8','(اي پيغمبر) کين چؤ ته ملائڪ پڪي رٿ کانسواءِ نازل نه ڪندا آھيون ۽ (جيڪڏھن نازل ڪريون ۽ نه مڃين ته) اُنھيءَ مھل اُنھن کي مُھلت نه ڏني ويندي.');
INSERT INTO "amroti" VALUES(15,9,'15:9','اسان نصيحت (يعني قرآن) نازل ڪيو ۽ اسين ضرور ان جي حفاظت ڪندڙ آھيون.');
INSERT INTO "amroti" VALUES(15,10,'15:10','۽ بيشڪ توکان اڳي پھرين ٽولين ۾ پيغمبر موڪلياسون.');
INSERT INTO "amroti" VALUES(15,11,'15:11','۽ ڪو پيغمبر وٽن نه ايندو ھو جو انھن سان ٺـٺوليون ڪرڻ کانسواءِ (ٻيو ڪي) نه ڪندا ھوا.');
INSERT INTO "amroti" VALUES(15,12,'15:12','۽ اھڙي طرح ڏوھارين جي دلين ۾ (اھڙو خيال) وجھندا آھيون.');
INSERT INTO "amroti" VALUES(15,13,'15:13','جو اُن کي نه مڃيندا ۽ بيشڪ اڳين جي ريت ھلي ايندي آھي.');
INSERT INTO "amroti" VALUES(15,14,'15:14','۽ جيڪڏھن آسمان مان ڪو دروازو مٿن کوليون ھا ته ان ۾ سدائين چڙھندا رھن.');
INSERT INTO "amroti" VALUES(15,15,'15:15','ضرور چون ھا ته رڳو اسان جون بند ڪيون ويون آھن بلڪ اسين جادو ڪيل قوم آھيون.');
INSERT INTO "amroti" VALUES(15,16,'15:16','۽ بيشڪ آسمان ۾ بُرج بڻاياسون ۽ اُن کي ڏسندڙن لاءِ سينگاريو سون.');
INSERT INTO "amroti" VALUES(15,17,'15:17','۽ اُن کي سڀڪنھن تڙيل شيطان کان بچايوسون.');
INSERT INTO "amroti" VALUES(15,18,'15:18','پر جيڪو چوريءَ ٻڌي تنھن جي پويان پڌرو اُلنبو پوندو آھي.');
INSERT INTO "amroti" VALUES(15,19,'15:19','۽ زمين کي پکيڙيوسون ۽ منجھس جبل کوڙيا سون ۽ منجھس ھر شيءِ اندازي سان ڄمائي سون.');
INSERT INTO "amroti" VALUES(15,20,'15:20','۽ منجھس اوھان لاءِ (قسم قسم جي) روزي (پيدا) ڪئي سون جن کي (اوھين) روزي ڏيندڙ نه آھيو.');
INSERT INTO "amroti" VALUES(15,21,'15:21','۽ اھڙي شيءِ آھي ئي ڪانه جنھن جو خزانون اسان وٽ نه آھي، انھن شين کي اسين ٺھرايل اندازي کانسواءِ نه لاھيندا آھيون.');
INSERT INTO "amroti" VALUES(15,22,'15:22','۽ وائِن ڪڪرن کڻندڙن کي موڪليندا آھيون پوءِ آسمان مان پاڻي وسائيندا آھيون پوءِ اُھو اوھان کي پياريندا آھيون، ۽ اُھو اوھين گڏ ڪرڻ وارا نه آھيو.');
INSERT INTO "amroti" VALUES(15,23,'15:23','۽ اسين ئي جياريندا آھيون ۽ ماريندا آھيون ۽ اسين سڀڪنھن جا وارث آھيون.');
INSERT INTO "amroti" VALUES(15,24,'15:24','۽ بيشڪ اوھان کان اڳ وارا ڄاڻندا آھيون ۽ بيشڪ (اوھان کان) پوءِ ايندڙ (به) ڄاڻندا آھيون.');
INSERT INTO "amroti" VALUES(15,25,'15:25','۽ بيشڪ تنھنجو پالڻھار کين کڙو ڪندو، ڇوته اُھو حڪمت وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(15,26,'15:26','۽ بيشڪ اسان آدم کي کڙڪندڙ مٽيءَ مان جا ڳوھيل گندي ٿيل ھئي بڻايو.');
INSERT INTO "amroti" VALUES(15,27,'15:27','۽ ھن کان اڳ جنن کي ڄَرَ واريءَ باھ مان بڻايوسون.');
INSERT INTO "amroti" VALUES(15,28,'15:28','۽ جڏھن تنھنجي پالڻھار ملائڪن کي چيو ته آءُ آدم کي کڙڪندڙ مٽيءَ مان جا ڳوھيل گندي ٿيل ھئي بڻائيندڙ آھيان.');
INSERT INTO "amroti" VALUES(15,29,'15:29','پوءِ جڏھن کيس پورو ڪري رھان ۽ منجھس پنھنجي روح مان ڦوڪيان ته کيس سجدو ڪندڙ ٿي ڪري پئجو.');
INSERT INTO "amroti" VALUES(15,30,'15:30','پوءِ اُنھن مڙني ملائڪن گڏجي سجدو ڪيو.');
INSERT INTO "amroti" VALUES(15,31,'15:31','سواءِ ابليس جي، سجدي ڪندڙن سان گڏ ھجڻ کان انڪار ڪيو.');
INSERT INTO "amroti" VALUES(15,32,'15:32','(الله) چيو ته اي ابليس توکي ڇا ٿيو جو سجدي ڪرڻ وارن مان نه ٿئين.');
INSERT INTO "amroti" VALUES(15,33,'15:33','(ھن) چيو ته مون کي نه جڳائيندو آھي ته آءٌ اھڙي ماڻھوءَ کي سجدو ڪريان جنھن کي کڙڪندڙ مٽيءَ مان جا ڳوھيل گندي ٿيل ھئي بڻايو اٿئي.');
INSERT INTO "amroti" VALUES(15,34,'15:34','(الله) فرمايو ته اُن (بھشت) مان نڪر ڇوته تون تڙيل آھين.');
INSERT INTO "amroti" VALUES(15,35,'15:35','۽ توتي قيامت تائين لعنت آھي.');
INSERT INTO "amroti" VALUES(15,36,'15:36','(ابليس) چيو ته اي منھنجا پالڻھار مون کي قيامت جي ڏينھن تائين مُھلت ڏي.');
INSERT INTO "amroti" VALUES(15,37,'15:37','(الله) فرمايو ته تون بيشڪ مُھلت ڏنلن مان آھين.');
INSERT INTO "amroti" VALUES(15,38,'15:38','ٺھرايل وقت واري ڏينھن تائين.');
INSERT INTO "amroti" VALUES(15,39,'15:39','(ابليس) چيو ته اي منھنجا پالڻھار تو جو مون کي گمراہ ڪيو آھي تنھنڪري آءٌ انھن لاءِ زمين ۾ (گناھن کي) ضرور سينگاريندس ۽ (ٻـين) سڀني کي گمراہ ڪندس.');
INSERT INTO "amroti" VALUES(15,40,'15:40','سواءِ اُنھن تنھنجن ٻانھن جي جي سچا آھن.');
INSERT INTO "amroti" VALUES(15,41,'15:41','(الله) فرمايو ته ھيءَ سڌي واٽ آھي مون تائين.');
INSERT INTO "amroti" VALUES(15,42,'15:42','گمراھن مان جن تنھنجي تابعداري تنھن کان سواءِ (ٻـين) منھنجن ٻانھن تي تنھنجو ڪو زور نه آھي.');
INSERT INTO "amroti" VALUES(15,43,'15:43','۽ اُنھن مڙني گمراھن جي انجام واري جاءِ دوزخ آھي.');
INSERT INTO "amroti" VALUES(15,44,'15:44','اُن کي ست دروازا آھن، ھر ڪنھن دروازي لاءِ انھن مان وراھيل ڀاڱو آھي.');
INSERT INTO "amroti" VALUES(15,45,'15:45','بيشڪ پرھيزگار باغن ۽ چشمن ۾ ھوندا.');
INSERT INTO "amroti" VALUES(15,46,'15:46','(کين چئبو ته) انھن ۾ بي فڪر ٿي سلامتيءَ سان گھڙو.');
INSERT INTO "amroti" VALUES(15,47,'15:47','۽ سندن سينن ۾ جيڪي ڪينو ھوندو سو انھن مان ڪڍنداسين (ھڪ ٻئي جا) ڀائر ٿي آمھون سامھون تختن تي ويٺا ھوندا.');
INSERT INTO "amroti" VALUES(15,48,'15:48','اُتي کين نڪو ڏک پھچندو ۽ نڪي اُتان ڪڍيا ويندا.');
INSERT INTO "amroti" VALUES(15,49,'15:49','(اي پيغمبر) منھنجن ٻانھن کي خبر ڏي ته آءٌ بخشڻھار مھربان آھيان.');
INSERT INTO "amroti" VALUES(15,50,'15:50','۽ منھنجو عذاب ئي ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(15,51,'15:51','۽ (اي پيغمبر) انھن کي ابراھيمؑ جي مھمانن جي خبر ڏي.');
INSERT INTO "amroti" VALUES(15,52,'15:52','جنھن مھل وٽس پھتا (تنھن مھل) چيائون سلام، ابراھيم چيو ته اسين اوھان کان ڊڄندڙ آھيون.');
INSERT INTO "amroti" VALUES(15,53,'15:53','چيائون ته نه ڊڄ اسين توکي علم واري پُٽ جي مبارڪ ڏيون ٿا.');
INSERT INTO "amroti" VALUES(15,54,'15:54','(ابراھيم) چيو ته مون کي ھن ھوندي به مبارڪ ڏيو ٿا جو مون کي ٻڍاپڻ پھتي آھي پوءِ ڪھڙي طرح مُبارڪ ڏيو ٿا؟');
INSERT INTO "amroti" VALUES(15,55,'15:55','چيائون ته توکي جائي مبارڪ ڏيون ٿا تنھنڪري نا اُميدن مان نه ٿيءُ.');
INSERT INTO "amroti" VALUES(15,56,'15:56','(ابراھيمؑ) چيو ته پنھنجي پالڻھار جي رحمت کان گُمراھن کانسواءِ (ٻيو) ڪير نا اُميد ٿيندو؟');
INSERT INTO "amroti" VALUES(15,57,'15:57','(وري ابراھيمؑ) چيو ته اي موڪليل (ملائڪو) اوھان جو ڪھڙو ڪم آھي.');
INSERT INTO "amroti" VALUES(15,58,'15:58','چيائين ته اسين ڏوھارين جي قوم ڏانھن موڪليل آھيون.');
INSERT INTO "amroti" VALUES(15,59,'15:59','مگر لُوط جي گھر وارا، جن مڙني کي اسين ڇوٽڪارو ڏيندڙ آھيون.');
INSERT INTO "amroti" VALUES(15,60,'15:60','سواءِ سندس زال جي جنھن لاءِ ٺھراء ڪيوسون ته اُھا رھيلن مان آھي.');
INSERT INTO "amroti" VALUES(15,61,'15:61','پوءِ جنھن مھل موڪليل (ملائڪ) لُوط جي گھراڻي وارن ڏانھن آيا.');
INSERT INTO "amroti" VALUES(15,62,'15:62','(تنھن مھل لُوط) چيو ته اوھين اوپري قوم آھيو.');
INSERT INTO "amroti" VALUES(15,63,'15:63','چيائون ته (نه) بلڪ تو وٽ اُھو (عذاب) وٺي آيا آھيون جنھن ۾ (ڪافر) شڪ ڪندا ھوا.');
INSERT INTO "amroti" VALUES(15,64,'15:64','۽ تو وٽ سچ سان آندو اٿون ۽ بيشڪ اسين ضرور سچا آھيون.');
INSERT INTO "amroti" VALUES(15,65,'15:65','پوءِ پنھنجي ڪٽنب سميت راتو واھ نڪر ۽ (تون) سندن پويان ھل ۽ اوھان مان ڪو ھڪڙو (به) پوئتي نه واجھائي ۽ جتي حُڪم ڪيو اَٿون اتي وڃو.');
INSERT INTO "amroti" VALUES(15,66,'15:66','۽ لُوط ڏانھن ھن حُڪم جو وحي ڪيوسون ته انھن جي پاڙ پرڀات مھل پٽي ويندي.');
INSERT INTO "amroti" VALUES(15,67,'15:67','۽ ڳوٺ وارا خوشيون ڪندا آيا.');
INSERT INTO "amroti" VALUES(15,68,'15:68','(لُوط) چيو ته ھي منھنجا مھمان آھن پوءِ مون لڄي نه ڪريو.');
INSERT INTO "amroti" VALUES(15,69,'15:69','۽ الله کان ڊڄو ۽ خوار نه ڪريوم.');
INSERT INTO "amroti" VALUES(15,70,'15:70','چيائون ته توکي جھان وارن (جي مھماني) کان نه جھليو ھوسون ڇا؟');
INSERT INTO "amroti" VALUES(15,71,'15:71','(لوط) چيو ته جيڪڏھن اوھين (منھنجو چيو) ڪندڙ ٿيندؤ ته ھي منھنجون ڌيون حاضر آھن.');
INSERT INTO "amroti" VALUES(15,72,'15:72','(اي پيغمبر) تنھنجي حياتيءَ جو قسم آھي ته اھي (ڪافر) گمراھيءَ ۾ حيران رھن ٿا.');
INSERT INTO "amroti" VALUES(15,73,'15:73','پوءِ سج اُڀرڻ مھل ڪَڙڪي ورتن.');
INSERT INTO "amroti" VALUES(15,74,'15:74','۽ اُن شھر جو مٿيون پاسو ان جي ھيٺ ڪيوسون ۽ مٿن پڪل مٽيءَ جا پھڻ وساياسون.');
INSERT INTO "amroti" VALUES(15,75,'15:75','بيشڪ ان ۾ عِبرت وٺندڙن لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(15,76,'15:76','۽ اِھو ڳوٺ ھميشه (جي اچ وڃ) واري واٽ تي ھو.');
INSERT INTO "amroti" VALUES(15,77,'15:77','بيشڪ اُن ۾ مؤمنن لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(15,78,'15:78','۽ ايڪه (جي ڳوٺن) وارا ظالم ھوا.');
INSERT INTO "amroti" VALUES(15,79,'15:79','پوءِ کائن بدلو ورتوسون، (لُوطين ۽ ايڪه وارن جا) ٻئي ڳوٺ پڌري رستي تي ھوا.');
INSERT INTO "amroti" VALUES(15,80,'15:80','۽ بيشڪ حِجر وارن پيغمبرن کي ڪوڙو ڀانيو.');
INSERT INTO "amroti" VALUES(15,81,'15:81','۽ کين پنھنجون نشانيون ڏنيون سون پوءِ اُنھن کان مُنھن موڙيندڙ ٿيا.');
INSERT INTO "amroti" VALUES(15,82,'15:82','۽ بي فڪر ٿي جبلن مان ٽُڪي گھر ٺاھيندا ھوا.');
INSERT INTO "amroti" VALUES(15,83,'15:83','پوءِ کين پرڀات مھل ڪڙڪي (اچي) ورتو.');
INSERT INTO "amroti" VALUES(15,84,'15:84','پوءِ سندين ڪمائي کائن ڪجھ به ٽاري نه سگھيو.');
INSERT INTO "amroti" VALUES(15,85,'15:85','۽ آسمانن ۽ زمين کي ۽ جيڪي منجھن آھي سو رِٿ کانسواءِ نه بڻايو اٿون، ۽ قيامت ضرور اچڻي آھي پوءِ (سندن بي ادبين کان) سھڻي طرح ٽارو ڪر.');
INSERT INTO "amroti" VALUES(15,86,'15:86','بيشڪ تنھنجو پالڻھار ڏاڍو پيدا ڪندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(15,87,'15:87','۽ بيشڪ توکي ٻٽيون ست آيتون (يعني سُورة فاتحة) ۽ وڏو قرآن ڏنوسون.');
INSERT INTO "amroti" VALUES(15,88,'15:88','اُنھن کي ھر جنس جي سامان مان جيڪي شيون ڏنيوسون تن ڏانھن پنھنجيون اکيون نه کول ۽ نڪي مٿن ڏک ڪر ۽ مؤمنن لاءِ پنھنجون ٻانھون جھڪيون ڪر.');
INSERT INTO "amroti" VALUES(15,89,'15:89','۽ چؤ ته بيشڪ آءٌ پڌرو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(15,90,'15:90','جھڙي طرح اُنھن وراھيندڙن تي (عذاب) نازل ڪيوسون.');
INSERT INTO "amroti" VALUES(15,91,'15:91','جن قرآن کي پرزا پرزا ڪيو.');
INSERT INTO "amroti" VALUES(15,92,'15:92','(تھڙي طرح) تنھنجي پالڻھار جو قسم آھي ته انھن مڙني کان ضرور پڇنداسون.');
INSERT INTO "amroti" VALUES(15,93,'15:93','جيڪي ڪندا ھوا تنھن بابت.');
INSERT INTO "amroti" VALUES(15,94,'15:94','جيڪي توکي حُڪم ڪجي ٿو سو کولي ٻُڌاءِ ۽ مُشرڪن کان مُنھن موڙ.');
INSERT INTO "amroti" VALUES(15,95,'15:95','اسان تنھنجي طرفان انھن چٿر ڪندڙن جي پورائي ڪئي.');
INSERT INTO "amroti" VALUES(15,96,'15:96','جي الله سان ٻيو معبود مُقرّر ڪندا آُھن، پوءِ سگھوئي ڄاڻندا.');
INSERT INTO "amroti" VALUES(15,97,'15:97','۽ بيشڪ ڄاڻندا آھيون ته جيڪي چوندا آھن تنھن سببان تنھنجو سينو تنگ ٿيندو آھي.');
INSERT INTO "amroti" VALUES(15,98,'15:98','۽ تون پاڪائيءَ سان پنھنجي پالڻھار جي ساراھ ڪر ۽ سجدي ڪندڙن مان ھُج.');
INSERT INTO "amroti" VALUES(15,99,'15:99','۽ ايستائين پنھنجي پالڻھار جي عبادت (پيو) ڪر جيسين توکي موت پھچي.');
INSERT INTO "amroti" VALUES(16,1,'16:1','الله جو حُڪم پُھتو تنھنڪري ان جي تڪڙ نه ڪريو، الله پاڪ آھي ۽ انھيءَ شيءِ کان مٿان آھي جيڪا ساڻس شريڪ مقرّر ڪندا آھن.');
INSERT INTO "amroti" VALUES(16,2,'16:2','پنھنجن ٻانھن مان جنھن لاءِ گھرندو آھي تنھن تي پنھنجي ارادي سان ملائڪن کي وحيءَ سان ھن لاءِ نازل ڪندو آھي ته (ماڻھن کي) ڊيڄاريو (ته خدا ٿو فرمائي) ته مون کانسواءِ ٻيو ڪو عبادت جو لائق نه آھي تنھنڪري مون کان ڊڄو.');
INSERT INTO "amroti" VALUES(16,3,'16:3','آسمانن ۽ زمين کي رِٿ سان بڻايائين، جيڪا شيءِ ساڻس شريڪ ڪندا آھن تنھن کان اُھو پاڪ آھي.');
INSERT INTO "amroti" VALUES(16,4,'16:4','آدميءَ کي منيءَ مان خلقيائين پوءِ اوچتوئي اُھو پڌرو جھڳڙالو ٿي پيو.');
INSERT INTO "amroti" VALUES(16,5,'16:5','۽ اوھان لاءِ ڍور خلقيائين انھن مان گرم پوشاڪون ۽ (ٻيا) فائدا آھن ۽ منجھائن ڪي کائيندا آھيو.');
INSERT INTO "amroti" VALUES(16,6,'16:6','جنھن مھل (اُھي ڍور) سھڙائيندا ايندا آھيو ۽ جنھن مھل ڇيڙيندا آھيو (تنھن مھل) اوھان لاءِ سونھن آھي.');
INSERT INTO "amroti" VALUES(16,7,'16:7','۽ جنھن شھر ڏانھن اوھين پنھنجي جيءُ جي اوکائيءَ کانسواءِ پھچي نه سگھندا آھيو تنھن ڏانھن اوھان جو بار کڻندا آھن، ڇوته اوھان جو پالڻھار وڏو ٻاجھارو مھربان آھي.');
INSERT INTO "amroti" VALUES(16,8,'16:8','۽ گھوڙا ۽ خچر ۽ گڏھ ھن لاءِ (بڻايائين) ته انھن تي اوھين چڙھو ۽ (انھن) کي زينت ڄاڻو، ۽ جيڪي شيون (اوھين) نه ڄاڻندا آھيو سي (به) پيدا ڪندو آھي.');
INSERT INTO "amroti" VALUES(16,9,'16:9','۽ الله ڏانھن سِڌو رستو پھچندو آھي ۽ اُن مان ڪي (رستا) مُڙيل آھن، ۽ جيڪڏھن (الله) گھري ھا ته اوھان مڙني کي سِڌو رستو ڏيکاري ھا.');
INSERT INTO "amroti" VALUES(16,10,'16:10','اُھو (الله) آھي جنھن آسمان مان اوھان لاءِ پاڻي وسايو منجھانئس ڪجھ پئڻ جو آھي ۽ منجھانئس جھنگ (پيدا ٿيندو) آھي جنھن ۾ مال چاريندا آھيو.');
INSERT INTO "amroti" VALUES(16,11,'16:11','اُن سان پوک ۽ زيتون ۽ کجيون ۽ ڊاکون ۽ ھر جنس جا ميوا اوھان لاءِ ڄمائيندو آھي، بيشڪ اُن ۾ پروڙيندڙ قوم لاءِ ضرور نشاني آھي.');
INSERT INTO "amroti" VALUES(16,12,'16:12','۽ اوھان لاءِ رات ۽ ڏينھن سج ۽ چنڊ تابع ڪيائين، ۽ تارا سندس حُڪم سان تابع ڪيل آھن، بيشڪ اُن ۾ ڄاڻندڙ قوم لاءِ ضرور نشانيون آھن.');
INSERT INTO "amroti" VALUES(16,13,'16:13','۽ قسمين قسمين رنگن جون (شيون ۽ جانور) جيڪي زمين ۾ پيدا ڪيائين سي به اوھان لاءِ آھن، بيشڪ اُن ۾ نصيحت وٺندڙ قوم لاءِ نشاني آھي.');
INSERT INTO "amroti" VALUES(16,14,'16:14','۽ اُھو (الله) آھي جنھن درياء کي ھن لاءِ تابع ڪيو ته اوھين منجھانئس تازو گوشت (يعني مڇي) کائو ۽ منجھانئس اُھي زيور (يعني موتي) ڪڍو جيڪي اوھين ڍڪيندا آھيو، ۽ منجھس ترندڙ ٻيڙيون ڏسندو آھين ۽ انھيءَ لاءِ ته اوھين سندس فضل ڳوليو ۽ ته مانَ اوھين شڪر ڪريو.');
INSERT INTO "amroti" VALUES(16,15,'16:15','۽ زمين ۾ مُحڪم جبل کوڙيائين ته متان اُھا اوھان کي ڌوڏو ڏئي ۽ نديون ۽ رستا بڻايائين ته مانَ اوھين سِڌو رستو لھو.');
INSERT INTO "amroti" VALUES(16,16,'16:16','۽ نشان (پيدا ڪيائين)، ۽ تارن سان اُھي سِڌو رستو لھندا آھن.');
INSERT INTO "amroti" VALUES(16,17,'16:17','جيڪو پيدا ڪري سو اُنھيءَ جھڙو آھي ڇا جو (ڪي به) پيدا نه ڪري؟ پوءِ ڇونه نصيحت وٺندا آھيو؟');
INSERT INTO "amroti" VALUES(16,18,'16:18','۽ جيڪڏھن اوھين الله جي نعمتن کي ڳڻيو ته انھن کي (ڪڏھن به) ڳڻي نه سگھندؤ، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(16,19,'16:19','۽ جيڪي لڪائيندا آھيو ۽ جيڪي پڌرو ڪندا آھيو سو الله ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(16,20,'16:20','۽ جن کي الله کانسواءِ سڏيندا آھن سي ڪا شيء خلقي نه سگھندا آھن ۽ اُھي (پاڻ) خلقيا ويا آھن.');
INSERT INTO "amroti" VALUES(16,21,'16:21','بي جان مُردا آھن، ۽ نه ڄاڻندا آھن ته ڪڏھن کڙا ڪيا ويندا.');
INSERT INTO "amroti" VALUES(16,22,'16:22','اوھان جو معبود ھڪڙو خُدا آھي، پوءِ جيڪي آخرت کي نه مڃيندا آھن تن جون دليون مڃڻ واريون نه آھن ۽ اُھي وڏائي ڪندڙ آھن.');
INSERT INTO "amroti" VALUES(16,23,'16:23','(ڪافر) جيڪي لڪائيندا آھن ۽ جيڪي (ھو) پڌرو ڪندا آھن سو بلاشڪ الله ڄاڻندو آھي، بيشڪ اُھو وڏائي ڪندڙن کي پيار نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(16,24,'16:24','۽ جڏھن اُنھن کي چئبو آھي ته اوھان جي پالڻھار ڇا نازل ڪيو آھي؟ (تڏھن) چوندا آھن ته اڳين جون اکاڻيون.');
INSERT INTO "amroti" VALUES(16,25,'16:25','(اھو سندن چوڻ) ھن لاءِ آھي جو قيامت جي ڏينھن پنھنجو پورو بار کڻندا ۽ جن کي اڻ ڄاڻائيءَ سان گمراہ ڪندا آھن تن جو (به) ڪجھ بار کڻندا، خبردار جيڪو (به) بار کڻندا سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(16,26,'16:26','جيڪي کائن اڳ ھوا تن بيشڪ فريب ڪيو پوءِ الله جو حُڪم سندن اڏاوتن کي پيڙھي کان پٽيو ۽ انھن تي مٿانئن ڇت اچي ڪري ۽ کين عذاب اُتان پُھتو جتان ڄاتائون ئي نه ٿي.');
INSERT INTO "amroti" VALUES(16,27,'16:27','وري قيامت جي ڏينھن کين خوار ڪندو ۽ چوندو ته (اوھان جا) مون سان ٺھرايل شريڪ جن جي بابت اوھين تڪرار ڪندا ھيؤ سي ڪٿي آھن؟ جن کي عِلم ڏنو ويو سي چوندا ته اَڄ انھن ڪافرن تي خواري ۽ بڇڙائي آھي.');
INSERT INTO "amroti" VALUES(16,28,'16:28','جن جو ملائڪ اھڙيءَ حالت ۾ ساہ ڪڍندا آھن جو پاڻ تي ظالم ھوا، اُھي صُلح پيش ڪندا (۽ چوندا ته) اسان ڪا بڇڙائي نه ڪئي ھئي، (ملائڪ چوندا ته) ھائو! جيڪي اوھين ڪندا ھيؤ سو الله ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(16,29,'16:29','پوءِ دوزخ جي دروازن مان گھڙو منجھس سدائين رھندڙ ھجو، پوءِ وڏائي ڪندڙن جي جاءِ بڇڙي آھي.');
INSERT INTO "amroti" VALUES(16,30,'16:30','۽ پرھيزگارن کي چيو ويندو ته ته اوھان جي پالڻھار ڇا نازل ڪيو آھي، چوندا ته چڱائي، جن ھن دنيا ۾ چڱائي ڪئي تن لاءِ چڱائي آھي، ۽ آخرت جو گھر ضرور ڀلو آھي، ۽ پرھيزگارن جو گھر ضرور ڀلو آھي.');
INSERT INTO "amroti" VALUES(16,31,'16:31','جو ھميشه جي رھڻ وارا باغ آھن اُن ۾ گھڙندا انھن جي ھيٺان واھيون وھنديون آھن جيڪي گھرندا سو انھن لاءِ منجھس آھي، اھڙي طرح الله پرھيزگارن کي بدلو ڏيندو.');
INSERT INTO "amroti" VALUES(16,32,'16:32','(اِھي اُھي آھن) جن کي ملائڪ پاڪ حالت ۾ ماريندا آھن (ملائڪ) چوندا آھن اَلسّلامُ عليڪم جيڪي ڪندا ھيؤ تنھن سببان بھشت ۾ گھِڙو.');
INSERT INTO "amroti" VALUES(16,33,'16:33','ڪافر (موت جي) ملائڪن جي پاڻ وٽ اچڻ يا تنھنجي پالڻھار (جي عذاب) جي حُڪم اچڻ کانسواءِ (ٻيو) ڪو انتظار نه ڪندا آھن، جيڪي کائن اڳ ھوا تن به اھڙي طرح ڪيو ھو، ۽ الله مٿن ظلم نه ڪيو پر اُھي پاڻ تي ظلم ڪندا ھوا.');
INSERT INTO "amroti" VALUES(16,34,'16:34','پوءِ کين سندن ڪرتوتن جي بڇڙائي پھتي ۽ جنھن لاءِ ٺـٺوليون ڪندا ھوا سا کين ويڙھي ويئي.');
INSERT INTO "amroti" VALUES(16,35,'16:35','۽ مُشرڪ چوندا آھن ته جيڪڏھن الله گھري ھا ته اُن ڌاران ڪا شيء نڪي اسين پوڄيون ھا ۽ نڪي اسان جا ابا ڏاڏا (پوڄين ھا) ۽ نڪي اُن جي حُڪم کانسواءِ ڪا شيء حرام ڪريون ھا، اھڙي طرح جيڪي کائن اڳ ھوا تن به ڪيو ھو، پوءِ پيغمبرن تي پڌري (پيغام) پھچائڻ کانسواءِ (ٻيو ڪي لازم) نه آھي.');
INSERT INTO "amroti" VALUES(16,36,'16:36','۽ بيشڪ سڀڪنھن ٽولي ۾ (ھڪ) پيغمبر موڪليوسون ته الله جي عبادت ڪريو ۽ بتن (جي پوڄا) کان پري ٿيو، پوءِ منجھائن ڪي اھڙا آھن جن کي الله ھدايت ڪئي ۽ منجھائن ڪي اھڙا آھن جن تي گمراھي ثابت ٿي، پوءِ مُلڪن ۾ گھُمو وري ڏسو ته ڪوڙن جي پڇاڙي ڪيئن ٿي؟');
INSERT INTO "amroti" VALUES(16,37,'16:37','(اي پيغمبر) جيڪڏھن ھنن کي ھدايت ڪرڻ لاءِ حرص ڪندين (ته منجھائن ڪوبه فائدو نه آھي) ڇوته الله جنھن کي گمراھ ڪندو آھي تنھن کي ھدايت نه ڪندو آھي ۽ انھن جو ڪوبه مددگار نه آھي.');
INSERT INTO "amroti" VALUES(16,38,'16:38','۽ الله جو پڪي طرح قسم کنيائون ته جيڪو مرندو تنھن کي الله وري نه جياريندو، ھائو! (وري جياريندو) اُن ڳالھ تي پڪ وعدو ٿي چڪو آھي مگر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(16,39,'16:39','ھن لاءِ (وري جياريندو) ته جنھن بابت تڪرار ڪندا آھن سو انھن کي پڌرو ڪري ٻڌائي ۽ ته ڪافر ڄاڻن ته اُھي (پاڻ) ڪُوڙا ھوا.');
INSERT INTO "amroti" VALUES(16,40,'16:40','ڪنھن به شيءِ لاءِ اسان جو چوڻ ھن (چوڻ) کانسواءِ نه آھي ته جڏھن اسين اُنکي پيدا ڪرڻ گھرندا آھيون ته اُن لاءِ چوندا آھيون ته ٿيءُ ته ٿي پوندي آھي.');
INSERT INTO "amroti" VALUES(16,41,'16:41','۽ جن پاڻ تي ظلم ٿيڻ کانپوءِ الله (جي واٽ) ۾ وطن ڇڏيو تن کي ضرور دنيا ۾ چڱي جاءِ ڏيندا آھيون، ۽ ضرور آخرت جو اجر تمام وڏو آھي، جيڪڏھن ڄاڻن ھا.');
INSERT INTO "amroti" VALUES(16,42,'16:42','(اُھي نعمتون انھن کي آھن) جن صبر ڪيو ۽ (جي) پنھنجي پالڻھار تي ڀروسو ڪن ٿا.');
INSERT INTO "amroti" VALUES(16,43,'16:43','۽ (اي پيغمبر) توکان اڳ (ڪو پيغمبر) مَردن کان کانسواءِ نه موڪليو اٿون انھن ڏانھن وحي ٿي ڪيوسون پوءِ (اي ماڻھو) جيڪڏھن اوھين نه ڄاڻندا آھيو ته ڪتاب وارن کان پڇو.');
INSERT INTO "amroti" VALUES(16,44,'16:44','دليلن ۽ ڪتابن سان (موڪلياسون)، ۽ اسان توڏانھن قرآن ھن لاءِ نازل ڪيو ته جيڪي ماڻھن ڏانھن نازل ڪيو ويو آھي سو اُنھن کي بيان ڪري ٻڌائين ۽ مانَ اُھي پروڙين.');
INSERT INTO "amroti" VALUES(16,45,'16:45','جن بڇڙي رٿ رٿي سي (ھن کان) بي ڀوا ٿيا آھن ڇا ته الله کين زمين ۾ ڳھائي ڇڏي يا جتان نه ڄاڻندا ھُجن اُتان مٿن عذاب اچي.');
INSERT INTO "amroti" VALUES(16,46,'16:46','يا اُنھن کي سندن گھمڻ ڦرڻ ۾ پڪڙي پوءِ اُھي عاجز ڪرڻ وارا نه آھن.');
INSERT INTO "amroti" VALUES(16,47,'16:47','يا ڀوائتيءَ حالت ۾ کين پڪڙي، پوءِ اوھان جو پالڻھار ٻاجھارو مھربان آھي.');
INSERT INTO "amroti" VALUES(16,48,'16:48','الله جيڪي شيون خلقيون تن ڏانھن ڇونه ڏسندا آھن جو سندن پاڇا سڄي ۽ کٻي (پاسي) الله کي سجدو ڪندڙ ٿي لڙندا آھن ۽ اُھي عاجزي ڪندڙ آھن.');
INSERT INTO "amroti" VALUES(16,49,'16:49','۽ جيڪي آسمانن ۾ آھن سي ۽ جيڪي جانور زمين ۾ آھن سي ۽ ملائڪ الله کي سجدو ڪندا آھن ۽ اُھي وڏائي نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(16,50,'16:50','(ملائڪ) پنھنجي پالڻھار کان ڊڄندا آھن جو سندن مٿان غالب آھي ۽ جيڪي حُڪم ڪبو اٿن سو ڪندا آھن.');
INSERT INTO "amroti" VALUES(16,51,'16:51','۽ الله فرمايو آھي ته اوھين ٻه خدا ڪري نه وٺو، اُن ھڪ الله کانسواءِ ٻيو ڪوبه عبادت جو لائق نه آھي، تنھنڪري اوھين خاص مون کان ڊڄندا رھو.');
INSERT INTO "amroti" VALUES(16,52,'16:52','۽ جيڪي آسمانن ۽ زمين ۾ آھي سو سندس آھي ۽ اُن جي عبادت سدائين (لازم) آھي، پوءِ الله کانسواءِ ٻـين کان ڇو ڊڄندا آھيو؟');
INSERT INTO "amroti" VALUES(16,53,'16:53','۽ جيڪا نعمت اوھان تي ٿيل آھي سا الله جي پار کان آھي پوءِ جڏھن اوھان تي سختي پھچي ٿي ته ڏانھس دانھون ڪندا آھيو.');
INSERT INTO "amroti" VALUES(16,54,'16:54','وري جڏھن اوھان کان ڏک لاھي (تڏھن) جھٽ پٽ اوھان مان ڪا ٽولي پنھنجي پالڻھار سان شريڪ مُقرّر ڪندي آھي.');
INSERT INTO "amroti" VALUES(16,55,'16:55','ھن لاءِ ته کين جيڪي ڏنوسون تنھن جي بي شڪري ڪن، پوءِ دنيا ۾ خوش گذاريو، پوءِ سِگھو ڄاڻندؤ.');
INSERT INTO "amroti" VALUES(16,56,'16:56','۽ جيڪي کين ڏنوسون تنھن مان (ڪافر) اُنھن (بُتن) لاءِ حصّو مُقرّر ڪندا آھن جي نه ڄاڻندا آھن، الله جو قسم آھي ته جيڪي ٺاھ ٺاھيندا آھيو تنھن بابت ضرور پڇيا ويندؤ.');
INSERT INTO "amroti" VALUES(16,57,'16:57','۽ الله لاءِ ڌيئرون مُقرّر ڪندا آھن اُھو پاڪ آھي ۽ پاڻ لاءِ جيڪي وڻندو اٿن (يعني پُٽ سو مُقرّر ڪندا آھن).');
INSERT INTO "amroti" VALUES(16,58,'16:58','۽ جڏھن منجھائن ڪنھن ھڪ کي ڌيءُ (ڄمڻ) جي خبر ڏبي آھي (تڏھن) سندس مُنھن ڪارو ٿي ويندو آھي ۽ اُھو ڏک ڀريل ھوندو آھي.');
INSERT INTO "amroti" VALUES(16,59,'16:59','جيڪا کيس بڇڙي خبر ڏني وئي تنھنجي ناراضپي سببان قوم کان پيو لڪندو آھي، (ڳڻتي ۾ پوندو آھي ته) اُن ڌيءُ کي خواريءَ ۾ رکي يا اُن کي مٽيءَ ۾ دٻي ڇڏي، خبردار اُھي جيڪي نبيرو ڪندا آھن سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(16,60,'16:60','جيڪي آخرت کي نه مڃيندا آھن تن جو مثال بڇڙو آھي، ۽ الله جو مثال مٿاھون آھي، ۽ اھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(16,61,'16:61','۽ جيڪڏھن الله ماڻھن کي سندن ظلم سببان پڪڙي ته ڪنھن چرندڙ کي زمين تي نه ڇڏي پر اُنھن کي ٺھرايل مدّت تائين مُھلت ڏني اٿس، پوءِ جنھن مھل سندن مھلت پھچندي (تنھن مھل) ھڪ گھڙي نڪي دير ڪندا ۽ نڪي اڳڀرو ويندا.');
INSERT INTO "amroti" VALUES(16,62,'16:62','۽ الله لاءِ اُھا شيء مقرر ڪندا آھن جيڪا (پاڻ) ناپسند ڪندا آھن ۽ سندن زبانون ڪوڙ بڪنديون آھن ته سندن لاءِ (ڇوٽڪاري جي) جي چڱائي آھي، بلاشڪ اُنھن لاءِ (دوزخ جي) باھ آھي ۽ اُھي حد کان لنگھي ويل آھن.');
INSERT INTO "amroti" VALUES(16,63,'16:63','الله جو قسم آھي ته بيشڪ توکان اڳ اُمّتن ڏانھن (پيغمبر) موڪلياسون پوءِ کين شيطان سندن عمل چڱا ڪري ڏيکاريا پوءِ اُھو اڄ سندن دوست آھي ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(16,64,'16:64','۽ (اي پيغمبر) توتي ڪتاب ھن کانسواءِ نازل نه ڪيوسون ته جن بابت تڪرار ڪيو تن کي تون بيان ڪرين ۽ (پڻ) مؤمن قوم لاءِ ھدايت ۽ ٻاجھ آھي.');
INSERT INTO "amroti" VALUES(16,65,'16:65','۽ الله آسمانن کان (مينھن جو) پاڻي وسايو پوءِ اُن سان زمين کي سندس ويرانيءَ کانپوءِ آباد ڪيائين، بيشڪ اُن ۾ ٻڌندڙ قوم لاءِ نشاني آھي.');
INSERT INTO "amroti" VALUES(16,66,'16:66','۽ بيشڪ ڍورن ۾ اوھان لاءِ عبرت آھي، جيڪي سندن پيٽن ۾ آھي تنھن مان ڇيڻن ۽ رت جي وچون نج کير اوھان کي پيارينديون آھن جو پيئندڙن کي وڻندڙ آھي.');
INSERT INTO "amroti" VALUES(16,67,'16:67','۽ کجين جي ۽ ڊاکن جي ڦرن مان ڪو (اھڙو قسم) آھي جو اُن مان شراب ۽ سٺي روزي بڻائيندا آھيو، بيشڪ اُن ۾ ڄاڻندڙ قوم لاءِ نشاني آھي.');
INSERT INTO "amroti" VALUES(16,68,'16:68','۽ (اي پيغمبر) تنھنجي پالڻھار ماکيءَ جي مَک ڏانھن الھام ڪيو ته ڪن جبلن ۾ ۽ وڻن ۾ ۽ جن شين مان (ماڻھو گھرن جون) ڇتيون جوڙيندا آھن تن ۾ گھر بڻاءِ.');
INSERT INTO "amroti" VALUES(16,69,'16:69','وري ھر ڪنھن جنس جي ميون مان کاءُ پوءِ پنھنجي پالڻھار جي واٽن تي عاجزيءَ سان ھل، سندس پيٽن مان رنگارنگ پئڻ جي شيء (يعني ماکي) نڪرندي آھي منجھس ماڻھن لاءِ شفا آھي، بيشڪ اُن ۾ سوچ ڪندڙ قوم لاءِ عبرت آھي.');
INSERT INTO "amroti" VALUES(16,70,'16:70','۽ الله اوھان کي خلقيو ۽ وري اوھان کي ماريندو ۽ اوھان مان نڪميءَ عُمر ڏانھن ھن لاءِ موٽايو ويندو آھي ته ڄاڻڻ کانپوءِ ڪُجھ نه ڄاڻي، ڇوته الله ڄاڻندڙ وس وارو آھي.');
INSERT INTO "amroti" VALUES(16,71,'16:71','۽ الله اوھان مان ھڪڙن کي ٻـين کان وڌيڪ روزي ڏني آھي، پوءِ اُھي وڌ ڏنل روزيءَ وارا پنھنجي روزيءَ کي پنھنجن ٻانھن تي موٽائڻ وارا نه آھن ته اُھي (سڀ) منجھس برابر ھُجن، پوءِ الله جي نعمتن جو ڇو انڪار ڪندا آھن؟');
INSERT INTO "amroti" VALUES(16,72,'16:72','۽ الله اوھان لاءِ اوھان جي جنس مان زالون پيدا ڪيون ۽ اوھان جي زالن مان پُٽ ۽ پوٽا پيدا ڪيائين ۽ اوھان کي سٺين شين مان روزي ڏنائين، پوءِ ڇو ڪوڙ کي مڃيندا آھن ۽ اُھي ڇو الله جي نعمتن جو انڪار ڪندا آھن.');
INSERT INTO "amroti" VALUES(16,73,'16:73','۽ الله ڌاران انھن جي عبادت ڪندا آھن جيڪي آسمانن ۽ زمين مان کين روزي ڏيڻ جو ڪجھ اختيار نه رکندا آھن ۽ نڪي (ڪجھ ڪري) سگھندا آھن.');
INSERT INTO "amroti" VALUES(16,74,'16:74','پوءِ الله لاءِ (بڇڙا) مثال بيان نه ڪريو، ڇوته الله ڄاڻندڙ آھي ۽ اوھين نه ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(16,75,'16:75','الله ھڪ خريد ٿيل ٻانھي جو مثال بيان ڪري ٿو جنھن جو ڪنھن شيءِ تي وس نه ھُجي ۽ (ٻيو) اُھو جنھن کي پاڻ وٽان چڱي روزي ڏنيسون پوءِ اُھو اُن سان (اسان جي واٽ ۾) ڳجھو ۽ پڌرو خرچ ڪري، (اھي ٻئي پاڻ ۾) برابر آھن ڇا؟ سڀ ساراھ الله کي جڳائي، پر انھن مان گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(16,76,'16:76','۽ الله ٻن مڙسن جو (ٻيو) مثال بيان ڪري ٿو جن ٻنھي مان ھڪڙو گنگو ھجي ڪنھن شيء تي وس ڪونه ھجيس ۽ اُھو پنھنجي مالڪ تي بار ھجي جيڏانھن موڪليس اُتان ڪا ڀَلائي نه آڻي اُھو اُنھيءَ جي برابر آھي ڇا جيڪو انصاف جو حُڪم ڏئي ۽ اُھو سڌيءِ واٽ تي ھُجي.');
INSERT INTO "amroti" VALUES(16,77,'16:77','۽ آسمانن ۽ زمين جو ڳُجھ الله کي (معلوم) آھي، ۽ قيامت (آڻڻ) جو ڪم (سندس اڳيان) رڳي اک ڇنڀ (جي دير) جھڙو آھي بلڪ اُھو انھيءَ کان به وڌيڪ نزديڪ آھي، ڇوته الله سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(16,78,'16:78','۽ الله اوھان کي اوھان جي مائن جي پيٽن مان ڪڍيو جو ڪُجھ نه ڄاڻندا ھيؤ ۽ اوھان جا ڪنّ ۽ اکيون ۽ دليون بڻايائين ته مانَ اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(16,79,'16:79','آسمانن جي ھوا ۾ تابع ڪيل پکين ڏانھن نه ڏسندا آھن ڇا؟ انھن کي الله کانسواءِ ڪونه جھليندو آھي، بيشڪ اُن ۾ ايمان واريءَ قوم لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(16,80,'16:80','۽ الله اوھان جي گھرن مان اوھان لاءِ آرام (جو ھنڌ) بڻايو ۽ ڍورن جي چمڙن مان اوھان لاءِ گھر بڻايائين جن کي اوھين پنھنجي مسافريءَ جي ڏينھن ۽ پنھنجي ھڪ ھنڌ رھڻ جي ڏينھن ۾ ھلڪو ڄاڻندا آھيو ۽ سندين اُنّن ۽ سندين ملسن ۽ سندين ڏاسن مان (گھر جو) اسباب ۽ فائدي جو سامان بڻايو ٿا (جوڳچ) مُدّت تائين (ڪم ڏئي ٿو).');
INSERT INTO "amroti" VALUES(16,81,'16:81','۽ الله پنھنجين خلقيل شين مان اوھان لاءِ ڇانئون بڻايون ۽ جبلن مان اوھان لاءِ چُرون بڻايائين ۽ اوھان لاءِ ڪپڙا بڻايائين جي اوھان کي گرميءَ (۽ سرديءَ) کان بچائيندا آھن، اھڙي طرح اوھان تي پنھنجي نعمت پوري ڪندو آھي ته مانَ اوھين فرمانبرداري ڪريو.');
INSERT INTO "amroti" VALUES(16,82,'16:82','پوءِ جيڪڏھن ڦرندا ته (اي پيغمبر) توتي پڌري پيغام پھچائڻ کانسواءِ (ٻيو ڪي) نه آھي.');
INSERT INTO "amroti" VALUES(16,83,'16:83','الله جي نعمت ڄاڻندا آھن وري اُن کي نه مڃيندا آھن ۽ اُنھن مان گھڻا ڪافر آھن.');
INSERT INTO "amroti" VALUES(16,84,'16:84','۽ جنھن ڏينھن ھر ڪنھن ٽوليءَ مان (ھڪ) شاھد کڙو ڪنداسون اُنھي ڏينھن ڪافرن کي (عُذر ڪرڻ لاءِ) موڪل نه ڏبي ۽ نڪي اُنھن جا عُذر قبول ڪيا ويندا.');
INSERT INTO "amroti" VALUES(16,85,'16:85','۽ جڏھن ظالم عذاب ڏسندا تڏھن کائن (عذاب) نڪي ھلڪو ڪبو ۽ نڪي اُنھن کي مُھلت ڏبي.');
INSERT INTO "amroti" VALUES(16,86,'16:86','۽ جڏھن مشرڪ پنھنجن شريڪن کي ڏسندا (تڏھن) چوندا ته اي اسان جا پالڻھار ھيء اسان جا اُھي شريڪ آھن جن کي توکانسواءِ سڏيندا ھواسين، پوءِ اُھي (بُت) انھن کي ورندي ڏيندا ته اوھين ڪُوڙا آھيو.');
INSERT INTO "amroti" VALUES(16,87,'16:87','۽ انھيءَ ڏينھن الله جي آڏو عاجزي ڪندا ۽ جيڪو ٺاھ ٺاھيندا ھوا سو کائن ڀُلجي ويندو.');
INSERT INTO "amroti" VALUES(16,88,'16:88','جن انڪار ڪيو ۽ الله جي واٽ کان (ماڻھن کي) جھليو تن کي عذاب جي مٿان عذاب انھيءَ سببان وڌائينداسون جو (ھو) فساد ڪندا ھوا.');
INSERT INTO "amroti" VALUES(16,89,'16:89','۽ اُن ڏينھن سڀڪنھن ٽوليءَ ۾ سندين جنس مان مٿن شاھد کڙا ڪنداسون ۽ (اي پيغمبر) توکي ھنن (يعني اُمّت) تي شاھد آڻينداسون، ۽ ھر شيء جي پڌرائيءَ لاءِ ۽ مُسلمانن جي ھدايت ۽ ٻاجھ ۽ خوشخبري ڏيڻ لاءِ توتي ڪتاب نازل ڪيوسون.');
INSERT INTO "amroti" VALUES(16,90,'16:90','بيشڪ الله انصاف ۽ ڀلائي (ڪرڻ) ۽ مائٽي وارن کي ڏيڻ جو حُڪم ڪري ٿو ۽ بي حيائي ۽ مدائي ۽ زبردستي کان جھلي ٿو، اوھان کي نصيحت ڏئي ٿو ته مانَ اوھين نصيحت وٺو.');
INSERT INTO "amroti" VALUES(16,91,'16:91','۽ جڏھن اوھين (الله جو نالو وٺي) پاڻ ۾ انجام ڪريو (تڏھن) الله جو انجام پاڙيو ۽ اوھين قَسمن کي سندن پڪي ڪرڻ کانپوءِ نه ڀڃو جو بيشڪ الله کي پاڻ تي نگھبان ڪيو اٿوَ، بيشڪ جيڪي ڪندا آھيو سو الله ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(16,92,'16:92','۽ اُن زال وانگر نه ٿيو جنھن پنھنجو سُٽ ڪتيل پڪي ڪرڻ کانپوءِ ڇني ٽُڪر ٽُڪر ڪري ڇڏيو، اوھين قسمن کي ھن لاءِ پاڻ ۾ حيلو بڻايو ٿا ته (ھڪ) ٽولي ٻي ٽوليءَ کان وڌيڪ ٿئي، الله ته اوھان کي رڳو اُن سان پرکيندو آھي، ۽ جنھن بابت اوھين تڪرار ڪندا آھيو سو قيامت جي ڏينھن اوھان جي لاءِ ضرور بيان ڪندو.');
INSERT INTO "amroti" VALUES(16,93,'16:93','۽ جيڪڏھن الله گھري ھا ته اوھان کي ھڪ ٽولي ڪري ھا پر جنھن لاءِ وڻندو اٿس تنھن کي گمراھ ڪندو آھي ۽ جنھن لاءِ وڻندو اٿس تنھن کي ھدايت ڪندو آھي، ۽ جيڪي ڪندا آھيو تنھن بابت اوھين ضرور پُڇبؤ.');
INSERT INTO "amroti" VALUES(16,94,'16:94','۽ اوھين پنھنجن قسمن کي پاڻ ۾ ٺڳي نه بڻايو نه ته اُنھن جي پڪي ڪرڻ کانپوءِ اوھان جا پيرَ ترڪندا ۽ (ماڻھن کي) الله جي واٽ کان جھلڻ سببان سزا چکندؤ، ۽ اوھان کي وڏو عذاب ٿيندو.');
INSERT INTO "amroti" VALUES(16,95,'16:95','۽ الله جي انجام کي ٿوري مُلھ سان نه وِڪڻو، ڇوته جيڪڏھن اوھين ڄاڻندا آھيو ته جيڪي الله وٽ آھي سو اوھان جي لاءِ چڱو آھي.');
INSERT INTO "amroti" VALUES(16,96,'16:96','جيڪي اوھان وٽ آھي سو کپي ويندو ۽ جيڪي الله وٽ آھي سو ھميشہ رھڻ وارو آھي، ۽ جن صبر ڪيو تن کي سندن عملن جو بلڪل چڱي طرح بدلو ڏينداسون.');
INSERT INTO "amroti" VALUES(16,97,'16:97','جنھن چڱو عمل ڪيو (پوءِ) اُھو مڙس ھُجي يا زال ۽ اُھو مؤمن ھوندو ته ضرور کيس چڱي حياتي ڏئي جيئرو ڪنداسون، ۽ جيڪي ڪندا ھوا تنھنجو بدلو کين تمام چڱيءَ طرح ڏينداسون.');
INSERT INTO "amroti" VALUES(16,98,'16:98','پوءِ (اي پيغمبر) جڏھن قرآن پڙھين تڏھن تڙيل شيطان (جي شر) کان الله جي سام پؤ.');
INSERT INTO "amroti" VALUES(16,99,'16:99','ان کي ايمان وارن تي ۽ جي پنھنجي پالڻھار تي ڀروسو ڪندا آھن تن تي ڪو غلبو نه آھي.');
INSERT INTO "amroti" VALUES(16,100,'16:100','سندس غلبو رڳو اُنھن تي آھي جيڪي کيس دوست رکندا آھن ۽ (انھن تي به) جيڪي الله سان شريڪ (مقرّر) ڪندا آھن.');
INSERT INTO "amroti" VALUES(16,101,'16:101','۽ جڏھن ھڪڙي آيت جي جاءِ تي ٻي آيت مٽائي آڻيندا آھيون ۽ الله جيڪي نازل ڪندو آھي سو چڱي طرح ڄاڻندڙ آھي (تڏھن) چوندا آھن ته تون رڳو پاڻون بڻائيندڙ آھين، (نه!) بلڪ اُنھن مان گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(16,102,'16:102','(اي پيغمبر) کين چؤ ته اُھو قرآن تنھنجي پالڻھار وٽان جبرئيل سچ سان ھن لاءِ نازل ڪيو آھي ته مؤمنن کي ثابت رکي ۽ مُسلمانن لاءِ ھدايت ۽ خوشخبري ھجي.');
INSERT INTO "amroti" VALUES(16,103,'16:103','۽ بيشڪ ڄاڻندا آھيون ته اُھي چوندا آھن ته اُن (پيغمبر) کي ڪو ماڻھو سيکاريندو آھي، جنھن ڏانھن اھا نسبت ڪندا آھن تنھن جي ٻولي عجمي آھي ۽ ھي (قرآن) پڌري عربي ٻوليءَ ۾ آھي.');
INSERT INTO "amroti" VALUES(16,104,'16:104','بيشڪ جيڪي الله جي آيتن کي نه مڃيندا آھن تن کي الله سڌو رستو نه ڏيکاريندو آھي ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(16,105,'16:105','ڪوڙو ٺاھ انھن کان سواءِ ڪونه ٺاھيندو آھي جيڪي الله جي آيتن کي نه مڃيندا آھن، ۽ اُھي (پاڻ) ڪُوڙا آھن.');
INSERT INTO "amroti" VALUES(16,106,'16:106','جنھن کي (ايمان کان ڦرڻ جو) زور ڪيو وڃي (اُھو زبان سان ڪي چوي) ۽ سندس دل ايمان سان آراميل ھجي اُن کانسواءِ جيڪو پنھنجي ايمان آڻڻ کانپوءِ الله جو انڪار ڪري بلڪ ڪُفر سان سينو ويڪرو ڪري (يعني راضي ٿئي) اھڙن تي الله جو ڏمر آھي، ۽ انھن تي وڏو عذاب آھي.');
INSERT INTO "amroti" VALUES(16,107,'16:107','اھو ھن ڪري آھي جو انھن دنيا جي حياتيءَ کي آخرت کان زيادہ پسند ڪيو آھي ۽ (انھيءَ ڪري) ته الله ڪافرن جي قوم کي ھدايت نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(16,108,'16:108','اِھي اُھي آھن جو سندين دلين ۽ سندن ڪَنن ۽ سندن اکين تي الله مُھر ھنئي، ۽ اُھي بي خبر آھن.');
INSERT INTO "amroti" VALUES(16,109,'16:109','بلاشڪ اُھي ئي آخرت ۾ ڇيئي وارا آھن.');
INSERT INTO "amroti" VALUES(16,110,'16:110','وري بيشڪ تنھنجو پالڻھار انھن کي (بخشي ٿو) جيڪي (ڪفر جي ڪلمي چوڻ لاءِ ڪافرن جي ھٿان) ايذايا ويا پوءِ وطن ڇڏيائون وري جھاد ڪيائون ۽ صبر ڪيائون بيشڪ تنھنجو پالڻھار ان کانپوءِ ضرور بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(16,111,'16:111','انھيءَ ڏينھن سڀڪو جيءُ پنھنجي طرفان جھيڙو ڪندو ايندو ۽ سڀڪنھن نفس جيڪي ڪمايو تنھنجو کيس پورو بدلو ڏبو ۽ انھن سان ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(16,112,'16:112','۽ الله ھڪ ڳوٺ جو (ھڪ) قِصو بيان ڪيو جو بي ڀئو آراميل ھو ان کي سندس روزي سڀڪنھن ھنڌان سوکائيءَ سان ايندي ھئي پوءِ الله جي نعمتن جي بي شڪرائي ڪيائون پوءِ جيڪي ڪندا ھوا تنھن سببان الله کين بُک ۽ ڀؤ جي پوشاڪ چکائي.');
INSERT INTO "amroti" VALUES(16,113,'16:113','۽ بيشڪ منجھائن (ھڪ) پيغمبر وٽن آيو پوءِ کيس ڪُوڙو ڀانيائون تنھنڪري کين عذاب پڪڙيو ۽ اُھي ظالم ھوا.');
INSERT INTO "amroti" VALUES(16,114,'16:114','پوءِ الله اوھان کي جيڪا حلال (۽) سُٺي روزي ڪري ڏني تنھن مان کائو، ۽ جيڪڏھن اُھين خاص الله جي عبادت ڪندا آھيو ته سندس نعمتن جو شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(16,115,'16:115','مئل ڍور ۽ رت ۽ سوئر جو ماس ۽ جنھن شيء کي الله جي نالي کانسواءِ ٻئي جي نالي سان سڏيو وڃي تنھن کانسواءِ ٻيو (ڪي به) اوھان تي حرام نه ڪيائين، پوءِ جيڪو نافرماني ڪندڙ نه ھجي ۽ نڪي حد کان لنگھندڙ ٿي لاچار ٿئي (۽ کائي) ته بيشڪ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(16,116,'16:116','۽ جن شين کي اوھان جون زبانون ڪوڙ سان بيان ڪنديون آھن تن لاءِ (ھنڪري) نه چئو ته ھي حلال آھي ۽ ھيءُ حرام آھي ته الله تي ڪُوڙو ٺاھ ٺاھيو، بيشڪ جيڪي الله تي ڪُوڙو ٺاھ ٺاھيندا آھن سي (ڪڏھن) نه ڇٽندا.');
INSERT INTO "amroti" VALUES(16,117,'16:117','(۽ ھيءُ حياتي) ٿوري وَٿ آھي، ۽ اُنھن لاءِ آخرت ۾ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(16,118,'16:118','۽ جيڪي توتي ھن کان اڳ بيان ڪيوسون سو يھودين تي حرام ڪيو ھوسون، ۽ اسان مٿن ظلم نه ڪيو پر (ھو) پاڻ تي ظلم ڪندا ھوا.');
INSERT INTO "amroti" VALUES(16,119,'16:119','وري جن بي سمجھائي سان بڇڙا ڪم ڪيا وري اُن کانپوءِ توبه ڪيائون ۽ پاڻ سڌاريائون تن لاءِ تنھنجو پالڻھار اُن (توبه) کانپوءِ بلڪل بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(16,120,'16:120','ابراھيمؑ ھڪ طرفو ٿيل الله جي عبادت ڪندڙ امام ھو، ۽ مُشرڪن مان نه ھو.');
INSERT INTO "amroti" VALUES(16,121,'16:121','الله جي نعمتن جو شُڪر ڪندڙ ھو، (الله) کيس سڳورو ڪيو ۽ سڌي واٽ ڏانھن ھدايت ڪيائينس.');
INSERT INTO "amroti" VALUES(16,122,'16:122','۽ دنيا ۾ کيس چڱائي ڏني سون، ۽ بيشڪ اُھو آخرت ۾ ڀلارن مان آھي.');
INSERT INTO "amroti" VALUES(16,123,'16:123','(اي پيغمبر اُن کان) پوءِ توڏانھن وحي ڪيوسون ته ھڪ مُھاڙي دين ابراھيم جي تابعداري ڪر، ۽ ابراھيم مشرڪن مان نه ھو.');
INSERT INTO "amroti" VALUES(16,124,'16:124','جن ڇنڇر بابت تڪرار ڪيو تن کانسواءِ ٻـين تي اُن (جي تعظيم) جو حُڪم لازم نه ڪيو ويو، ۽ بيشڪ تنھنجو پالڻھار قيامت جي ڏينھن سندن وچ ۾ ضرور نبيرو ڪندو جنھن ۾ تڪرار ڪندا آھن.');
INSERT INTO "amroti" VALUES(16,125,'16:125','(اي پيغمبر) حڪمت ۽ چڱي نصيحت سان پنھنجي پالڻھار جي واٽ ڏانھن (ماڻھن کي) سڏ ۽ چڱي طرح کين الزام ڏي، تحقيق جيڪو الله جي واٽ کان گمراھ ٿيو تنھنکي تنھنجو پالڻھار چڱو ڄاڻندڙ آھي ۽ اُھو ھدايت وارن کي (به) چڱو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(16,126,'16:126','۽ جيڪڏھن (ڪافرن کان) بدلو وٺو ته جيترو ايذايا ويا ھجو اوترو انھن کي ايذايو، ۽ جيڪڏھن اوھين صبر ڪريو ته اُھو صابرن لاءِ ڀلو آھي.');
INSERT INTO "amroti" VALUES(16,127,'16:127','۽ (اي پيغمبر) صبر ڪر ۽ تنھنجو صبر الله ئي جي توفيق سان آھي ۽ جيڪو فريب ڪندا آھن تنھن بابت نڪي مٿن ڏک ڪر ۽ نڪي خفي رہ.');
INSERT INTO "amroti" VALUES(16,128,'16:128','جيڪي پرھيزگاري ڪندڙ آھن ۽ جيڪي ڀلارا آھن تن سان الله گڏ آھي.');
INSERT INTO "amroti" VALUES(17,1,'17:1','اھو (الله) پاڪ آھي جنھن پنھنجي ٻانھي (محمد صلى الله عليه وسلم) کي ھڪڙي رات (جي اندر) تعظيم واري مسجد مان اُنھي مَسجد اَقصىٰ تائين جنھن جي چوڌاري برڪت رکي سون ھن لاءِ سير ڪرايو ته کيس پنھنجيون نشانيون ڏيکاريون، بيشڪ اُھو ئي ٻڌندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(17,2,'17:2','۽ مُوسىٰؑ کي ڪتاب (توريت) ڏنوسون ۽ ان کي بني اسرائيلن لاءِ ھدايت ڪرڻ وارو ڪيوسون (۽ چيوسون) ته مون کان سواءِ (ٻيو) ڪو ڀرجھلو نه وٺو.');
INSERT INTO "amroti" VALUES(17,3,'17:3','اي (ان قوم جو) اولاد جن کي نوح سان گڏ (ٻيڙي ۾) چاڙھيوسون، بيشڪ اھو شاڪر ٻانھو ھو.');
INSERT INTO "amroti" VALUES(17,4,'17:4','۽ بني اسرائيلن ڏانھن ڪتاب ۾ (ھيءُ حُڪم) موڪليوسون ته اوھين مُلڪ ۾ ٻه ڀيرا فساد ضرور ڪندؤ ۽ اوھين وڏي وڏائي سان ضرور ھٺيلائي ڪندؤ.');
INSERT INTO "amroti" VALUES(17,5,'17:5','پوءِ جڏھن ٻنھي مان پھريون انجام (فساد جو) ايندو (تڏھن) اوھان تي پنھنجا ڏاڍا سورھ ٻانھان کڙا ڪنداسون جي شھرن جي وچ ۾ ڪاھي پوندا، ۽ اھو انجام (پورو) ٿيڻو آھي.');
INSERT INTO "amroti" VALUES(17,6,'17:6','وري اوھان کي ھڪ ڀيرو مٿن غلبو ڏينداسون ۽ مال ۽ اولاد سان اوھان کي (لڳولڳ) وڌائينداسون ۽ اوھان کي لشڪر ڪري گھڻا ڪنداسون.');
INSERT INTO "amroti" VALUES(17,7,'17:7','جيڪڏھن اوھين چڱائي ڪندؤ ته (اُھا چڱائي) پاڻ لاءِ ڪندؤ، ۽ جيڪڏھن اوھين بڇڙائي ڪندؤ ته پاڻ لاءِ، پوءِ جڏھن ٻيو انجام (فساد جو) پھچندو ته ٻيا اوھان جا منھن ارھا ڪندا ۽ اھڙي طرح مسجد ۾ گھڙندا جھڙي طرح ان ۾ پھريون ڀيرو گھڙيا ھوا ۽ جنھن تي غالب ٿيندا تنھن کي چڱي طرح اجاڙيندا.');
INSERT INTO "amroti" VALUES(17,8,'17:8','(چيو ويو ته) اوھان جو پالڻھار ويجھو ئي اوھان تي ٻاجھ ڪندو، ۽ جيڪڏھن اوھين (وري) ڦرندؤ ته اسين به (سزا ڏيڻ ڏانھن) ڦرنداسون، ۽ ڪافرن لاءِ دوزخ کي بندي خانه مقرر ڪيوسون.');
INSERT INTO "amroti" VALUES(17,9,'17:9','ھيءُ قرآن انھي (واٽ) ڏانھن رستو ڏيکاريندو آھي جيڪا ڏاڍي سِڌي آھي ۽ انھن مؤمنن کي خوشخبري ڏيندو آھي جيڪي چڱا ڪم ڪندا آھن انھن لاءِ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(17,10,'17:10','۽ جيڪي آخرت کي نه مڃيندا آھن تن لاءِ ڏکوئيندڙ عذاب تيار ڪيو اٿون.');
INSERT INTO "amroti" VALUES(17,11,'17:11','۽ ماڻھو مدائيءَ کي (اھڙو) گھرندو آھي جھڙو چڱائيءَ کي سندس گھرڻ ھوندو آھي، ۽ ماڻھو اٻھرو آھي.');
INSERT INTO "amroti" VALUES(17,12,'17:12','۽ رات ۽ ڏينھن کي ٻه نشانيون ڪيون سون پوءِ رات جي نشاني کي ميٽايوسون ۽ ڏينھن جي نشاني کي ھن لاءِ روشن ڪيوسون ته پنھنجي پالڻھار جو فضل ڳوليو ۽ (پڻ ھن لاءِ) ته اوھين ورھن جو ڳاڻيٽو ۽ حساب ڄاڻو، ۽ سڀ ڪنھن شيء جو کولي بيان ڪيوسون.');
INSERT INTO "amroti" VALUES(17,13,'17:13','۽ سڀ ڪنھن ماڻھوءَ جي ڳچيءَ ۾ سندس عمل نامو ٻڌوسون، ۽ قيامت جي ڏينھن ڪتاب انھيءَ لاءِ ڪڍنداسون جو ان کي کليل ڏسندؤ.');
INSERT INTO "amroti" VALUES(17,14,'17:14','(چيو ويندو ته) پنھنجو ڪتاب پڙھ، اڄ تنھنجو جيءُ توتي حساب وٺندڙ بس آھي.');
INSERT INTO "amroti" VALUES(17,15,'17:15','جنھن ھدايت لڌي تنھن پاڻ لاءِ ئي ھدايت لڌي، ۽ جيڪو ڀُلو سو رڳو پاڻ لاءِ ڀُلندو، ۽ ڪو بار کڻندڙ ٻئي ڪنھن جو بار نه کڻندو، ۽ اسين ايستائين عذاب ڪندڙ نه آھيون جيستائين ڪنھن پيغمبر کي موڪليون.');
INSERT INTO "amroti" VALUES(17,16,'17:16','۽ اسين جڏھن ڪنھن ڳوٺ جو ناس ڪرڻ گھرندا آھيون (تڏھن) انھن مان آسودن کي (پيغمبر جي معرفت پنھنجي عبادت جو) حُڪم ڪندا آھيون پوءِ منجھس نافرماني ڪندا آھن تنھنڪري مٿن عذاب لازم ٿيندو آھي پوءِ پاڙ پٽڻ طرح ان جي پاڙ پٽيندا آھيون.');
INSERT INTO "amroti" VALUES(17,17,'17:17','۽ ڪيترائي جڳ نُوحؑ کان پوءِ ناس ڪياسون، ۽ تنھنجو پالڻھار پنھنجن ٻانھن جي گناھن جي خبر رکندڙ ڏسندڙ ڪافي آھي.');
INSERT INTO "amroti" VALUES(17,18,'17:18','جيڪو دنيا (جي آسودگي) گھرندو آھي تنھن کي ان ھنڌ اھائي جلد ڏيندا آھيون جيڪي گھرندا آھيون سو جنھن لاءِ گھرندا آھيون (تنھن کي ڏيندا آھيون) وري ان لاءِ دوزخ مقرر ڪيوسون، جنھن ۾ (ھو) خوار (۽) تڙيل ٿي گھڙندو.');
INSERT INTO "amroti" VALUES(17,19,'17:19','۽ جنھن آخرت گھري ۽ ان لاءِ چڱي ڪوشش ڪئي ۽ اُھو مؤمن (به) ھجي سي اُھي آھن جن جي ڪوشش قبول ٿيل آھي.');
INSERT INTO "amroti" VALUES(17,20,'17:20','ھن ٽوليءَ جي ۽ ھن ٽوليءَ جي ھر ھڪ کي تنھنجي پالڻھار جي بخشش مان مدد ڪريون ٿا، ۽ تنھنجي پالڻھار جي ڏات ڪا اٽڪيل نه آھي.');
INSERT INTO "amroti" VALUES(17,21,'17:21','ڏس ته انھن مان ھڪڙن کي ٻـين کان (دنيا ۾ رزق ۽ مرتبه ۾) ڪئن ڀلو ڪيو اٿون، ۽ آخرت ته درجن ڪري بلڪل وڏي ۽ مانَ ڏيڻ ڪري بلڪل وڌيڪ آھي.');
INSERT INTO "amroti" VALUES(17,22,'17:22','(اي ماڻھو تون) الله سان ٻيو ڪو معبود مقرّر نه ڪر نه ته گِلارو (۽) خوار ٿي وھندين.');
INSERT INTO "amroti" VALUES(17,23,'17:23','۽ تنھنجي پالڻھار حُڪم ڪيو آھي ته ان کانسواءِ ٻئي ڪنھن جي عبادت نه ڪريو ۽ ماءُ پيءُ سان نيڪي ڪريو، جيڪڏھن ٻنھين مان ڪو ھڪڙو تنھنجي آڏو ٻُڍائيءَ کي پھچي يا ٻَئي ته تون اُف (به) نه چئج ۽ نڪي کين جھڻڪج ۽ ساڻن چڱي ڳالھ (ادب واري) ڳالھائج.');
INSERT INTO "amroti" VALUES(17,24,'17:24','۽ انھن لاءِ عاجزيءَ جون ٻانھون ٻاجھ مان ھيٺاھيون ڪج ۽ چئج ته اي منھنجا پالڻھار اُنھن تي (اھڙي طرح) ٻاجھ ڪر جھڙي طرح مون کي ننڍپڻ ۾ پاليائون ٿي.');
INSERT INTO "amroti" VALUES(17,25,'17:25','جيڪي اوھان جي دلين ۾ آھي سو اوھان جو پالڻھار چڱي طرح ڄاڻندڙ آھي جيڪڏھن اوھين سڌريل ھوندؤ (ته بخشيندو) ڇوته الله توبه ڪندڙن کي بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(17,26,'17:26','۽ مائٽي وارا ۽ مسڪينن کي ۽ مُسافرن کي سندن حق ڏي ۽ اڍنگائيءَ سان اجايو نه وڃاءِ.');
INSERT INTO "amroti" VALUES(17,27,'17:27','ڇوته اجايو وڃائيندڙ شيطانن جا ڀائر آھن، ۽ شيطان پنھنجي پالڻھار جي نافرماني ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(17,28,'17:28','۽ جيڪڏھن تون (رزق جي اڻھوند ڪري) انھن کان منھن موڙين جنھن رزق جي ملڻ جي پنھنجي پالڻھار کان اميد رکين ٿو ته (کين جھڻڪي نه موٽاءِ ۽) ساڻن مٺي ڳالھ ڳالھاءِ.');
INSERT INTO "amroti" VALUES(17,29,'17:29','۽ نڪي پنھنجي ھٿ کي پنھنجي ڳچيءَ ۾ بند ڪر ۽ نڪي اھو سارو کولي ڇڏ نه ته ملامتي ۽ عاجز ٿي ويھي رھندين.');
INSERT INTO "amroti" VALUES(17,30,'17:30','تنھنجو پالڻھار جنھن لاءِ گھرندو آھي تنھنجي روزي ڪشادي ڪندو آھي ۽ گھٽ به ڪندو آھي، تحقيق اھو پنھنجن ٻانھن جي خبر رکندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(17,31,'17:31','۽ پنھنجن ٻارن کي سُڃائيءَ جي ڊپ کان نه ڪُھو، اسين کين ۽ (خود) اوھان کي روزي ڏيندا آھيون، بيشڪ انھن کي ڪُھڻ وڏو ڏوھ آھي.');
INSERT INTO "amroti" VALUES(17,32,'17:32','۽ زنا کي ويجھا نه وڃو ڇوته اھا بي حيائي آھي، ۽ اِھا واٽ بُڇڙي آھي.');
INSERT INTO "amroti" VALUES(17,33,'17:33','۽ اھڙي جيءَ کي نه ڪُھو جنھن جو (شرعي) حق کانسواءِ ڪُھڻ الله حرام ڪيو آھي، ۽ جيڪو ناحق ماريو وڃي ان جي وارث کي (قصاص جو) اختيار ڏنوسون پوءِ جڳائي ته مارڻ ۾ زيادتي نه ڪري، ڇوته اھو مدد ڏنل ٿيو آھي.');
INSERT INTO "amroti" VALUES(17,34,'17:34','۽ نڪي ڪنھن ڇوري ٻار جي مال کي چڱي نيت کانسواءِ ويجھا وڃو جيستائين ھو بلوغت کي پھچي، ۽ پنھنجو انجام پاڙيو، تحقيق انجام بابت بلڪل پڇا ٿيندي.');
INSERT INTO "amroti" VALUES(17,35,'17:35','۽ جڏھن ماڻ ڀريو تڏھن پورو ڀريو ۽ سنئين ترازيءَ سان توريو، اھا (پوري ماپ ۽ تور) ڀلي ۽ عاقبت جي ڪري بلڪل چڱي آھي.');
INSERT INTO "amroti" VALUES(17,36,'17:36','۽ (اي ٻانھا) جنھن شيء جي توکي ڪا سُڌ نه آھي اُن جي پويان نه لڳ، ڇوته ڪَن ۽ اک ۽ دل انھن سڀني (عضون) کان ان بابت بلڪل پڇا ٿيندي.');
INSERT INTO "amroti" VALUES(17,37,'17:37','۽ زمين ۾ تون آڪڙ ڪري نه گُھم، ڇوته تون نڪي زمين کي چيري سگھندين ۽ نڪي ڊيگھ ۾ جبل (جي چوٽي) کي پھچين سگھندين.');
INSERT INTO "amroti" VALUES(17,38,'17:38','اُنھن سڀني (عادتن) جي بڇڙائي تنھنجي پالڻھار وٽ ناپسند آھي.');
INSERT INTO "amroti" VALUES(17,39,'17:39','اھي حُڪم انھن مان آھن جيڪي تنھنجي پالڻھار توڏانھن حڪمت مان وحي ڪيا آھن، ۽ الله سان ٻيو ڪوبه معبُود مُقرّر نه ڪر نه ته تون ملامتي (۽) تڙيل ٿي دوزخ ۾ وڌو ويندين.');
INSERT INTO "amroti" VALUES(17,40,'17:40','(اي مشرڪؤ) اوھان جي پالڻھار اوھان کي پُٽن جي لاءِ چونڊيو ۽ (پاڻ) ملائڪن کي (پنھنجيون) ڌيئر ڪري ورتائين ڇا؟ بيشڪ اوھين ھيءَ وڏي ڳالھ ڪندا آھيو.');
INSERT INTO "amroti" VALUES(17,41,'17:41','۽ بيشڪ ھن قرآن ۾ ھن لاءِ وري وري سمجھايوسون ته نصيحت وٺن، ۽ اھو انھن بابت نفرت کانسواءِ (ٻيو ڪي) ڪين ٿو وڌائي.');
INSERT INTO "amroti" VALUES(17,42,'17:42','(اي پيغمبر) چؤ ته جيڪڏھن ساڻس ٻيا معبود جھڙي طرح ھوندا آھن (تھڙي طرح) ھجن ھا ته اھي انھي دم عرش جي مالڪ ڏانھن (جنگ جي) واٽ طلبين ھا.');
INSERT INTO "amroti" VALUES(17,43,'17:43','اھو پاڪ آھي ۽ جيڪي چوندا آھن تنھن کان وڏو (۽) مٿاھون آھي.');
INSERT INTO "amroti" VALUES(17,44,'17:44','ست آسمان ۽ زمين ۽ جيڪي منجھن آھي سي ان جي پاڪائي بيان ڪندا آھن، ۽ ڪا (به) شيء سندس ساراھ سان گڏ پاڪائي بيان ڪرڻ کانسواءِ نه آھي پر (اوھين) سندس تسبيح نه سمجھندا آھيو، بيشڪ اُھو بُردبار بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(17,45,'17:45','۽ جڏھن (تون) قرآن پڙھندو آھين (تڏھن) تنھنجي وچ ۾ ۽ جيڪي آخرت کي نه مڃيندا آھن تن جي وچ ۾ (اسين) لڪل پردو ڪندا آھيون.');
INSERT INTO "amroti" VALUES(17,46,'17:46','۽ ان جي سمجھڻ کان سندن دلين ۾ پردو ۽ سندن ڪَنن ۾ گھٻرائي وجھندا آھيون، ۽ جڏھن پنھنجي ھڪڙي پالڻھار کي قرآن ۾ ياد ڪرين ٿو تڏھن نفرت ڪري پنھنجين پُٺين (ڀرپوءِ) تي موٽندا آھن.');
INSERT INTO "amroti" VALUES(17,47,'17:47','(اي پيغمبر) جنھن مھل توڏانھن ڪن ڏيئي جنھن (ٺـٺولي ڪرڻ جي ارادي) سان (قرآن کي) ٻڌندا آھن ۽ جنھن مھل ڳجھيون صلاحون ڪندا آھن جڏھن ظالم چوندا آھن ته (اوھين) رڳو جادو ڪيل مڙس جي تابعداري ڪريو ٿا (تنھن مھل سندين انھن ڳالھين کي) چڱي طرح ڄاڻندا آھيون.');
INSERT INTO "amroti" VALUES(17,48,'17:48','ڏس ته تولاءِ ڪھڙا مٿال ڏيندا آھن پوءِ گمراھ ٿيا تنھنڪري سڌي واٽ لھي نه سگھندا آھن.');
INSERT INTO "amroti" VALUES(17,49,'17:49','۽ چوندا آھن ته جڏھن اسين (مري ڳري) ھڏا ۽ ذرا ذرا ٿينداسين (تڏھن) اسين وري نوان بڻجي اٿنداسين ڇا؟');
INSERT INTO "amroti" VALUES(17,50,'17:50','چؤ ته پھڻ ٿيو يا لوھ ٿيو.');
INSERT INTO "amroti" VALUES(17,51,'17:51','يا پنھنجين دلين ۾ جيڪا (ٻي) وڏي خلق ڀائيندا آھيو (سا ٿيو ته به اٿندؤ)، پوءِ سگھوئي چوندا ته ڪير اسان کي وري جياريندو؟ چؤ ته جنھن اوھان کي پھريون ڀيرو پيدا ڪيو (سو جياريندو)، پوءِ توڏانھن پنھنجو ڪنڌ لوڏيندا ۽ چوندا ته اھو ڪڏھن ٿيندو؟ چؤ ته اميد آھي ته سگھو ٿيڻو آھي.');
INSERT INTO "amroti" VALUES(17,52,'17:52','جنھن ڏينھن الله اوھان کي سڏيندو (تنھن ڏينھن) اوھين سندس ساراہ ڪندي ورندي ڏيندؤ ۽ ڀائيندؤ ته ٿوروئي وقت رھيا آھيون.');
INSERT INTO "amroti" VALUES(17,53,'17:53','۽ (اي پيغمبر) منھنجن ٻانھن کي چؤ ته (ڪافرن سان) بلڪل چڱي (ڳالھ) چوندا رھن، ڇوته شيطان سندن وچ ۾ جھيڙو وجھندو آھي، ڇوته شيطان ماڻھو جو پڌرو ويري آھي.');
INSERT INTO "amroti" VALUES(17,54,'17:54','اوھان جو پالڻھار اوھان کي چڱي طرح ڄاڻندو آھي، جيڪڏھن وڻيس ته اوھان تي ٻاجھ ڪري ۽ جيڪڏھن وڻيس ته اوھان کي عذاب ڪري، ۽ اسان توکي مٿن وڪيل ڪري نه موڪليو آھي.');
INSERT INTO "amroti" VALUES(17,55,'17:55','۽ جيڪي آسمانن ۽ زمين ۾ آھي سو تنھنجو پالڻھار چڱي طرح ڄاڻندو آھي، ۽ بيشڪ ھڪڙن نبين کي ٻـين کان وڌيڪ ڪيوسون ۽ داؤد کي زبور ڏنوسون.');
INSERT INTO "amroti" VALUES(17,56,'17:56','(اي پيغمبر) چؤ ته الله کانسواءِ ٻـين جن کي (الله جھڙو) ڀائيندا آھيو تن کي سڏيو پوءِ (اھي) اوھان کان نڪي ڪنھن اھنج لاھڻ جو ۽ نڪي ڦيرائڻ جو اختيار رکندا آھن.');
INSERT INTO "amroti" VALUES(17,57,'17:57','ھي (ڪافر خُدا کانسواءِ) جن کي سڏيندا آھن سي پاڻ پنھنجي پالڻھار ڏانھن وسيلو ڳوليندا آھن ته انھن مان ڪير ڏاڍو ويجھو آھي؟ ۽ سندس ٻاجھ جي اميد رکندا آھن ۽ سندس عذاب کان ڊڄندا آھن، ڇوته تنھنجي پالڻھار جو عذاب ڊڄڻ جھڙي شيء آھي.');
INSERT INTO "amroti" VALUES(17,58,'17:58','۽ ڪوبه ڳوٺ نه آھي مگر (جنھن جي رھڻ وارن) کي (سندن بڇڙن ڪرتوتن سبب) قيامت جي ڏينھن کان اڳ اسين ناس ڪرڻ وارا آھيون يا ان کي سخت عذاب ڏيندڙ آھيون، اِھو حُڪم ڪتاب ۾ لکيل آھي.');
INSERT INTO "amroti" VALUES(17,59,'17:59','۽ اسان کي مُعجزن موڪلڻ کان انھيءَ ڳالھ کانسواءِ ٻئي ڪنھن نه روڪيو جو اڳين (ماڻھن) انھن کي ڪوڙ ڄاتو ھو، ۽ ثمود (جي قوم) کي ڏاچي مُعجزہ لاءَ ڏني سون ته اُن کي نه مڃيائون، ۽ اسين رڳو ڊيڄارڻ لاءِ مُعجزا موڪليندا آھيون.');
INSERT INTO "amroti" VALUES(17,60,'17:60','۽ (اي پيغمبر ياد ڪر) جنھن مھل توکي چيوسون ته تنھنجي پالڻھار ماڻھن کي (پنھنجي قدرت سان) لپيٽيو آھي، ۽ اسان جيڪو توکي نظارو ڏيکاريوسون جو لعنت ڪيل وڻ قرآن ۾ بيان ڪيو ويو آھي سو ماڻھن جي پرک کانسواءِ نه آھي، ۽ اسين جو کين ڊيڄاريندا آھيون سو انھن بابت وڏي شرارت کانسواءِ (ٻيو ڪي) نه وڌائيندو آھي.');
INSERT INTO "amroti" VALUES(17,61,'17:61','۽ جڏھن ملائڪن کي چيوسون ته آدم کي سجدو ڪريو پوءِ ابليس کانسواءِ (ٻـين) سجدو ڪيو، (ابليس) چيو ته جنھن کي مٽيءَ مان پيدا ڪيئي تنھن کي ڇو سجدو ڪريان؟');
INSERT INTO "amroti" VALUES(17,62,'17:62','(وري ابليس) چيو ته ڏس ھي شخص جنھن کي تو مون تي شرف ڏنو، جيڪڏھن مون کي قيامت جي ڏينھن تائين مُھلت ڏيندين ته ٿورڙن کان سواءِ سندس (ٻئي) اولاد کي پاڙئون پٽيندس.');
INSERT INTO "amroti" VALUES(17,63,'17:63','(الله) چيو ته وڃ پوءِ منجھانئن جن تنھنجي تابعداري ڪئي تن اوھان سڀني جي سزا دوزخ آھي (۽ اھا) بلڪل پوري سزا آھي.');
INSERT INTO "amroti" VALUES(17,64,'17:64','۽ منجھانئن جنھن کي (ڀُلائي) سگھين تنھن کي پنھنجي سڏ سان ڀُلاءِ ۽ پنھنجن پيادن ۽ سوارن سان مٿن چڙھائي ڪر ۽ سندن مال ۽ اولاد ۾ ڀائيواري ڪر ۽ کين انجام ڏي، ۽ شيطان ٺڳيءَ کانسواءِ کين ڪو دلاسو نه ڏيندو آھي.');
INSERT INTO "amroti" VALUES(17,65,'17:65','بيشڪ منھنجن ٻانھن تي توکي ڪا سگھ نه آھي، ۽ تنھنجو پالڻھار نگھبان بس آھي.');
INSERT INTO "amroti" VALUES(17,66,'17:66','اوھان جو پالڻھار اھو آھي جيڪو دريائن ۾ ٻيڙيون ھن لاءِ ھلائيندو آھي ته سندس فضل ڳوليو، ڇوته اُھو اوھان تي ٻاجھارو آھي.');
INSERT INTO "amroti" VALUES(17,67,'17:67','۽ جڏھن اوھان کي درياءَ ۾ ڪا اوکائي پھچندي آھي تڏھن خاص ان (الله) کانسواءِ ٻـين جن کي اوھين سڏيندا آھيو سي وسري ويندا آھن، پوءِ جنھن مھل اوھان کي سُڪي ڏي بچائيندو آھي (تنھن مھل) مُنھن موڙيندا آھيو، ۽ ماڻھو ڏاڍو بي شڪر آھي.');
INSERT INTO "amroti" VALUES(17,68,'17:68','(اوھين) پاڻ کي سڪيءَ ۾ گھائڻ کان يا پاڻ تي (پھڻن جي) آنڌي اچڻ کان بي ڀوا ٿيا آھيو ڇا؟ وري اوھين پاڻ لاءِ ڪو واھرو نه لھندؤ.');
INSERT INTO "amroti" VALUES(17,69,'17:69','يا ٻئي ڀيري ان (درياء) ۾ موٽائڻ کان بي ڀَوَا ٿيا آھيو ڇا؟ پوءِ اوھان تي واءُ جو طوفان موڪلي پوءِ اوھان کي اوھان جي انڪار سببان ٻوڙي وري انھي (ٻوڙڻ جي) ڪري پاڻ لاءِ اسان تي ڪو پيڇي ڪرڻ وارو نه لھندؤ.');
INSERT INTO "amroti" VALUES(17,70,'17:70','۽ بيشڪ آدم جي اولاد کي شرف ڏنوسون ۽ کين پَٽن ۽ دريائن ۾ کنيوسون ۽ کين سٺين شين مان روزي ڏنيسون ۽ جيڪي خلقياسون تن مان گھڻن کان کين زيادہ مانَ وارو ڪيوسون.');
INSERT INTO "amroti" VALUES(17,71,'17:71','ھر ڪنھن ٽوليءَ کي سندن اڳواڻ سان سڏينداسون، پوءِ جنھن کي سندس اعمالنامون سندس ساڄي ھٿ ۾ ڏنو ويندو سي پنھنجو اعمالنامو پڙھندا ۽ تند جيترو به ظلم نه ڪبن.');
INSERT INTO "amroti" VALUES(17,72,'17:72','۽ جيڪو ھن دنيا ۾ انڌو ٿيو سو آخرت ۾ (به) انڌو (ھوندو) ۽ واٽ کان بِنھ ڀُليل رھندو.');
INSERT INTO "amroti" VALUES(17,73,'17:73','۽ جيڪي توڏانھن (پنھنجن حُڪمن جو) وحيُ ڪيوسون تنھن کانسواءِ توکي ھن لاءِ دوکي ۾ وجھڻ لڳا ھوا ته تون ان کانسواءِ ٻيو ڪو اسان تي ٺاھ ٺاھين، ۽ انھيءَ مھل توکي دوست بڻائين.');
INSERT INTO "amroti" VALUES(17,74,'17:74','۽ جيڪڏھن اسين توکي (پنھنجي ڳالھ تي) پختو نه رکون ھا ته ويجھو ھو جو بيشڪ اُنھن ڏانھن ڪي ٿوروئي لڙي پون ھا.');
INSERT INTO "amroti" VALUES(17,75,'17:75','۽ اتي جو اتي حياتي جو ٻيڻو عذاب ۽ موت جو (به) ٻيڻو عذاب توکي چکايون ھا وري پاڻ لاءِ اسان تي ڪو واھرو نه لھين ھا.');
INSERT INTO "amroti" VALUES(17,76,'17:76','۽ (اي پيغمبر مڪي جي) زمين ۾ سگھو ئي توکي ھن لاءِ بيقرار ڪندا ته ان مان توکي لڏائين ۽ انھيءَ مھل توکان پوءِ (پاڻ به) ٿورو وقت رھندا.');
INSERT INTO "amroti" VALUES(17,77,'17:77','توکان اڳ جيڪي پنھنجا پيغمبر موڪلياسون تن جو دستور موافق (جو سندن قوم کين لڏايو ته ناس ڪيو وين) ۽ اسان جي دستور کي ڪا ڦيرڦار نه لھندين.');
INSERT INTO "amroti" VALUES(17,78,'17:78','سج لڙئي کان وٺي رات جي اونداھيءَ تائين نماز پڙھ ۽ فجر جو قرآن پرھ، بيشڪ فجر جو قرآن پڙھڻ مھل ملائڪ حاضر ٿين ٿا.');
INSERT INTO "amroti" VALUES(17,79,'17:79','۽ ڪجھ رات جو (قرآن) سان تھجّد پرھ جو (اھا) تولاءِ زيادہ عبادت آھي، تنھنجو پالڻھار توکي سگھوئي ساراھيل جاءِ ۾ اُٿاريندو.');
INSERT INTO "amroti" VALUES(17,80,'17:80','۽ (اي پيغمبر) چؤ ته اي منھنجا پالڻھار مون کي (مديني ۾) سچائيءَ سان داخل ڪر ۽ (مڪي مان) مون کي سچائيءَ سان ٻاھر ڪڍ ۽ پاڻ وٽان مون لاءِ ڏاڍي سگھ مدد ڏيندڙ ڪر.');
INSERT INTO "amroti" VALUES(17,81,'17:81','۽ (اي پيغمبر) چؤ ته سچ آيو ۽ ڪوڙ ڀڳو، بيشڪ ڪوڙ ڀڄندڙ آھي.');
INSERT INTO "amroti" VALUES(17,82,'17:82','۽ اسين قرآن کي نازل ڪريون ٿا جو اھو سارو مؤمنن لاءِ شفا ۽ ٻاجھ آھي ۽ ظالمن بابت نقصان کانسواءِ (ٻيو ڪي) نه وڌائيندو آھي.');
INSERT INTO "amroti" VALUES(17,83,'17:83','۽ جڏھن انسان تي ڪا نعمت نازل ڪندا آھيون (تڏھن) مُنھن موڙيندو آھي ۽ کانئس پاسيرو ٿيندو آھي، ۽ جڏھن کيس مدائي پھچندي آھي ته نا اميد ٿيندو آھي.');
INSERT INTO "amroti" VALUES(17,84,'17:84','(اي پيغمبر) چؤ ته سڀڪو پنھنجي ڊول تي ڪم ڪندو آھي، پوءِ جيڪو ڏاڍيءَ سڌيءَ واٽ تي آھي تنھن کي تنھنجو پالڻھار چڱي طرح ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(17,85,'17:85','۽ (اي پيغمبر) توکان رُوح بابت پُڇن ٿا، چؤ ته رُوح منھنجي پالڻھار جي امر مان آھي ۽ اوھان کي ڪي ٿورڙي علم کانسواءِ نه ڏنو ويو آھي.');
INSERT INTO "amroti" VALUES(17,86,'17:86','۽ جيڪو (قرآن) توڌانھن وحي ڪيوسون سو جيڪڏھن گھرون ته ورائي وٺون ته پوءِ تون پاڻ لاءِ اسان تي ڪو ڀرجھلو نه لھندين.');
INSERT INTO "amroti" VALUES(17,87,'17:87','پر تنھنجي پالڻھار جي ٻاجھ (ان ورائي وٺڻ کي جھلي رکيو) آھي، ڇوته سندس فضل توتي وڏو آھي.');
INSERT INTO "amroti" VALUES(17,88,'17:88','(اي پيغمبر) چؤ ته جيڪڏھن ماڻھو ۽ جِنّ ھن قرآن جھڙي (ٻئي ڪنھن ڪتاب) آڻڻ لاءِ گڏ ٿين ته جھڙس ڪڏھن نه آڻي سگھندا جيتوڻيڪ ھڪ ٻئي کي مدد ڏين.');
INSERT INTO "amroti" VALUES(17,89,'17:89','۽ بيشڪ ھن قرآن ۾ سڀڪو مثال ماڻھن لاءِ بيان ڪيو اٿئون، پر گھڻا ماڻھو بي شڪر ٿي انڪار ڪندا آھن.');
INSERT INTO "amroti" VALUES(17,90,'17:90','۽ چوندا آھن ته اسين توکي (ايستائين) ھرگز نه مڃينداسون جيستائين زمين ۽ اسان لاءِ ڪو چشمو وھائين.');
INSERT INTO "amroti" VALUES(17,91,'17:91','يا کجين ۽ ڊاکن مان تولاءِ باغ ٿئي پوءِ سندس وچ ۾ تون وھندڙ واھيون وھائين.');
INSERT INTO "amroti" VALUES(17,92,'17:92','يا جھڙي طرح تو ڀانيو تھڙي طرح اسان تي آسمان ٽُڪر ٽُڪر ڪيرائين يا تون الله ۽ ملائڪن کي سامھون آڻين.');
INSERT INTO "amroti" VALUES(17,93,'17:93','يا توکي ڪو سونون گھر ھُجي يا آسمان تي چڙھين، ۽ تنھنجي چڙھڻ کي (ايستائين) ڪڏھن نه مڃينداسون جيستائين اسان تي (اھڙو لکيل) ڪتاب لاھين جنھن کي پڙھون، چؤ ته منھنجو پالڻھار پاڪ آھي آءٌ رڳو پيغام پھچائڻ وارو ماڻھو آھيان.');
INSERT INTO "amroti" VALUES(17,94,'17:94','۽ جڏھن ماڻھن وٽ ھدايت آئي (تڏھن) کين ايمان آڻڻ کان ھن ڏاران ڪنھن نه جھليو آھي جو چوندا آھن ته الله پيغمبرن کي ڇو ماڻھو ڪري موڪليو.');
INSERT INTO "amroti" VALUES(17,95,'17:95','(اي پيغمبر) چؤ ته جيڪڏھن ملائڪ (اوھان جان) زمين ۾ ٽڪندڙ ٿي گھمن ڦرن ھا ته اسين مٿن ضرور آسمان مان ملائڪ پيغمبر ڪري موڪليون ھا.');
INSERT INTO "amroti" VALUES(17,96,'17:96','(اي پيغمبر) چ‍ؤ ته الله اوھان ۽ اسان جي وچ ۾ شاھد بس آھي، بيشڪ اھو پنھنجي ٻانھن جي خبر رکندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(17,97,'17:97','۽ جنھن کي الله ھدايت ڪئي سو ھدايت وارو آھي، ۽ جيڪي گمراھ ٿيا تن لاءِ تون الله کانسواءِ ڪوئي دوست نه لھندين، ۽ کين قيامت جي ڏينھن سندن مُنھن ڀر انڌا ۽ گنگا ۽ ٻوڙا ڪري گڏ ڪنداسون، سندن جاءِ دوزخ آھي، جنھن مھل ڍرو ٿيندو تنھن مھل انھن لاءِ دوزخ کي وڌيڪ تپائينداسون.');
INSERT INTO "amroti" VALUES(17,98,'17:98','اھا سندن سزا ھن سببان آھي جو اسانجي نشانين جو انڪار ڪيائون ۽ چيائون ته جڏھن اسين ھڏا ۽ چُورو ٿينداسون (تڏھن) وري نئين سر پيدا ٿي اٿاريا وينداسون ڇا؟');
INSERT INTO "amroti" VALUES(17,99,'17:99','نه ڏسندا آھن ڇا ته جنھن الله آسمانن ۽ زمين کي بڻايو سو سندن اھڙي بڻائڻ تي وس وارو آھي ۽ انھن لاءِ اھا مُدّت مقرر ڪيائين جنھن ۾ ڪو شڪ نه آھي، پوءِ ظالم انڪار کانسواءِ ڪي قبول نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(17,100,'17:100','(اي پيغمبر) چؤ ته جيڪڏھن اوھين منھنجي پالڻھار جي خزانن جا مالڪ ھجو ھا ته انھيءَ مھل (سڀ) خرچ ٿي وڃڻ جي ڀَو کان اوھين ضرور بند رکو ھا، ۽ ماڻھو پڪو پيچ آھي.');
INSERT INTO "amroti" VALUES(17,101,'17:101','۽ بيشڪ مُوسىؑ کي نَوَ پڌريون نشانيون ڏنيوسون پوءِ بني اسرائيلن کان پُڇ ته جڏھن (اھو) وٽن آيو تڏھن کيس فرعون چيو ته اي مُوسىؑ بيشڪ آءٌ توکي يقيناً جادو ٿيل ڀائيندو آھيان.');
INSERT INTO "amroti" VALUES(17,102,'17:102','(مُوسىؑ) چيو ته بيشڪ تو ڄاتو آھي ته اھي (نشانيون) آسمانن ۽ زمين جي پالڻھار کانسواءِ (ٻئي ڪنھن) نه نازل ڪيون آھن (اوھان جي) سمجھائڻ لاءِ (ته ڏسي مون مڃيو)، ۽ اي فرعون آءٌ توکي (انھن جي انڪار سببان) ھلاڪ ٿيل ڀائيندو آھيان.');
INSERT INTO "amroti" VALUES(17,103,'17:103','پوءِ فرعون ملڪ مان سندن لوڌڻ جو ارادو ڪيو پوءِ فرعون ۽ سندس ساري سنگت کي ٻوڙيوسون.');
INSERT INTO "amroti" VALUES(17,104,'17:104','۽ کانئس پوءِ بني اسرائيلن کي چيوسون ته مُلڪ ۾ ٽڪو پوءِ جڏھن آخرت جو وعدو آيو (تڏھن) اوھان کي لپيٽي وٺنداسون.');
INSERT INTO "amroti" VALUES(17,105,'17:105','۽ ان (قرآن) کي اسان سچ سان نازل ڪيو آھي ۽ اھو سچ سان نازل ٿيو آھي، ۽ توکي خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ کانسواءِ نه موڪليوسون.');
INSERT INTO "amroti" VALUES(17,106,'17:106','۽ ان قرآن کي جُدا جُدا (سُورتون ۽ آيتون) ھن لاءِ ڪيوسون ته اھو ماڻھن کي آھسته آھسته پڙھي ٻڌائين ۽ ان کي آھسته آھسته ڪري لاٿوسون.');
INSERT INTO "amroti" VALUES(17,107,'17:107','(اي پيغمبر) چؤ ته اوھين ان قرآن کي مڃو توڙي نه مڃو، بيشڪ جن کي ھن کان اڳ علم ڏنو ويو آھي تن وٽ جڏھن (قرآن) پڙھبو آھي (تڏھن) سجدو ڪندڙ ٿي کاڏين ڀر ڪِرندا آھن.');
INSERT INTO "amroti" VALUES(17,108,'17:108','۽ چوندا آھن ته اسان جو پالڻھار پاڪ آھي بيشڪ اسان جي پالڻھار جو انجام ضرور (پورو) ٿيڻو آھي.');
INSERT INTO "amroti" VALUES(17,109,'17:109','۽ روئيندا کاڏين ڀر ڪِرندا آھن ۽ (قرآن) منجھن عاجزي وڌائيندو آھي.');
INSERT INTO "amroti" VALUES(17,110,'17:110','(اي پيغمبر) چؤ ته (الله کي) الله (جي نالي سان) سڏيو يا رحمان (جي نالي سان) سڏيو، جنھن نالي سان (اوھين) سڏيو انھي جا (سڀ) نالا چڱا آھن، ۽ پنھنجي نماز ۾ نڪي تمام ڏاڍيان پڙھ ۽ نڪي تمام ھوريان پڙھ ان جي وچ واري واٽ اختيار ڪر.');
INSERT INTO "amroti" VALUES(17,111,'17:111','۽ چؤ ته سڀ ساراھ انھي الله کي جڳائي جنھن (پاڻ لاءِ) ڪو اولاد نه ورتو آھي ۽ نڪي سندس مُلڪ ۾ ڪو ڀائيوار آھي ۽ نڪي ڪنھن ھيڻائي سببان ان جو ڪو مددگار آھي ۽ ھن جي گھڻي قدر وڏائي بيان ڪر.');
INSERT INTO "amroti" VALUES(18,1,'18:1','سڀ ساراھ انھي الله کي جڳائي جنھن پنھنجي ٻانھي (محمد صلى الله عليه وسلم) تي ڪتاب لاٿو ۽ ان ۾ ڪو ڏِنگ نه رکيائين.');
INSERT INTO "amroti" VALUES(18,2,'18:2','ھن لاءِ سڌو ڪيائين ته الله وٽان سخت مُصيبت کان ڪافرن کي ڊيڄاري ۽ اُنھن مؤمنن کي خوشخبري ڏي جيڪي چڱا ڪم ڪندا آھن ته اُنھن لاءِ چڱو اجر آھي.');
INSERT INTO "amroti" VALUES(18,3,'18:3','منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(18,4,'18:4','۽ انھن کي ڊيڄاري جيڪي چون ٿا ته الله (پاڻ لاءِ) پُٽ ورتو آھي.');
INSERT INTO "amroti" VALUES(18,5,'18:5','جنھن جو نڪي کين ۽ نڪي سندن پيءُ ڏاڏن کي ڪو علم آھي، وڏي (گناھ جي) ڳالھ آھي جا سندن واتان نڪري ٿي، اھي ڪوڙ کانسواءِ (ٻيو ڪي) نه چوندا آھن.');
INSERT INTO "amroti" VALUES(18,6,'18:6','جيڪڏھن اھي ھن ڳالھ کي نه مڃن ته متان تون ارمان کان سندن پويان پاڻ کي ھلاڪ ڪرين.');
INSERT INTO "amroti" VALUES(18,7,'18:7','جيڪي زمين تي آھي سو اسان ان جي سينگار لاءِ ھن ڪري ڪيو ته سندن آزمائش وٺون ته انھن مان عملن ۾ ڏاڍو چڱو ڪير آھي.');
INSERT INTO "amroti" VALUES(18,8,'18:8','۽ ان زمين تي جيڪا (ساول) آھي سا اسين چٽي پٽي ڪرڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(18,9,'18:9','پر ڀانيو اٿيئي ته غار ۽ رقيم وارا اسان جي نشانين مان عجيب آھن.');
INSERT INTO "amroti" VALUES(18,10,'18:10','جڏھن غار ۾ ڪيترن جوانن جاءِ ورتي تڏھن چيائون ته اي اسان جا پالڻھار پاڻ وٽان اسان تي ٻاجھ عطا ڪر ۽ اسان جي ڪم ۾ اسان لاءِ چڱائي ڪر.');
INSERT INTO "amroti" VALUES(18,11,'18:11','پوءِ ڪيترائي ورهيه کين غار ۾ ڪَنن ڀر سمھاريوسون.');
INSERT INTO "amroti" VALUES(18,12,'18:12','وري کين ھن لاءِ اٿاريوسون ته ڏيکاريون ته جا مدت رھيا سا ٻنھي ٽولين مان ڪھڙي کي ياد آھي.');
INSERT INTO "amroti" VALUES(18,13,'18:13','اسين توکي سندن سچي خبر بيان ڪريون ٿا، بيشڪ اھي ڪيترا جوان ھوا جو پنھنجي پالڻھار کي مڃيو ھوائون ۽ کين وڌيڪ ھدايت ڪئي ھئي سون.');
INSERT INTO "amroti" VALUES(18,14,'18:14','۽ سندن دلين تي (سچائي) ٻڌي سون جڏھن اٿيا تڏھن چيائون ته اسان جو پالڻھار آسمانن ۽ زمين جو پالڻھار آھي ان کانسواءِ ٻئي ڪنھن کي معبُود ڪري نه سڏينداسون (جي سڏيوسون ته) ضرور بيشڪ اُنھي مھل بيھودي ڳالھ چئي سون.');
INSERT INTO "amroti" VALUES(18,15,'18:15','اسان جي ھن قوم الله کانسواءِ ٻيا معبود ورتا آھن، مٿن ڇونه ڪو پڌرو دليل آڻيندا آھن؟ پوءِ جيڪو الله تي ڪوڙ ٺاھي ان کان وڌيڪ ظالم ڪير آھي؟');
INSERT INTO "amroti" VALUES(18,16,'18:16','۽ جڏھن انھن (مشرڪن) کان ۽ (اھي) الله کانسواءِ جن کي پوڄيندا آھن تن کان پاسي ٿيو تڏھن غار ڏانھن پناہ وٺو ته اوھان جو پالڻھار اوھان لاءِ ٻاجھ پکيڙيندو ۽ اوھان جي ڪم ۾ اوھان لاءِ سولائي موجود ڪندو.');
INSERT INTO "amroti" VALUES(18,17,'18:17','۽ (اي ڏسندڙ) جڏھن سج اڀري (تڏھن) ڏسندين ته (اُس) سندن غار کان سڄي پاسي لڙندي آھي ۽ جڏھن لھي تڏھن سندن کٻي پاسي مڙندي آھي ۽ اُھي ان (غار جي) ويڪرائي ۾ آھن، اِھا الله جي نشانين مان آھي، جنھن کي الله ھدايت ڪري سو ھدايت وارو آھي، ۽ جنھن کي ڀُلائي ان لاءِ ڪوبه دوست واٽ ڏيکارندڙ نه لھندين.');
INSERT INTO "amroti" VALUES(18,18,'18:18','۽ تون کين جاڳندڙ ڀائيندين پر اُھي (ننڊ ۾) سمھيل آھن، ۽ کين سڄي پاسي تي ۽ کٻي پاسي تي اٿلائيندا آھيون، ۽ سندن ڪتو چائٺ تي اڳيون ٽنگون ڊگھيريو پيو آھي، جيڪڏھن مٿن بيھي ڏسين ته کانئن ڀڄندڙ ٿي منھن ڦيرين ۽ کانئن دھشت کان ڀرجي وڃين.');
INSERT INTO "amroti" VALUES(18,19,'18:19','۽ اھڙي طرح کين ھن لاءِ کڙو ڪيوسون ته پاڻ ۾ پڇن، منجھانئن چوندڙ چيو ته (اوھين ھتي) ڪيترو ٽڪيؤ، چيائون ته ھڪ ڏينھن يا ڏينھن کان گھٽ رھياسون، (ڪن) چيو ته جيڪي اوھين رھيا آھيو سو اوھان جو پالڻھار چڱو ڄاڻندڙ آھي، پوءِ مان پاڻ مان ھڪ کي ھِن پنھنجي رپئي سان شھر ڏانھن موڪليو پوءِ ڀلي ڏسي ته اُن (ڳوٺ) جو ڪھڙو طعام سٺو آھي پوءِ ان مان ڪجھ تعام اوھان وٽ آڻي ۽ جڳائي ته نرمي ڪري ۽ اوھان جو حال ڪنھن ھڪڙي کي نه ڄاڻائي.');
INSERT INTO "amroti" VALUES(18,20,'18:20','ڇوته جيڪڏھن اھي اوھان تي غالب ٿيندا ته اوھان کي سنگسار ڪندا يا پنھنجي مذھب ۾ اوھان کي موٽائيندا ۽ اُن مھل اوھين ھرگز نه ڇٽندؤ.');
INSERT INTO "amroti" VALUES(18,21,'18:21','۽ اھڙي طرح (تڏھن) ماڻھن کي ھن لاءِ مٿن خبردار ڪيوسون ته ڄاڻن ته الله جو انجام سچو آھي ۽ بيشڪ قيامت (ٿيڻي) آھي ان ۾ ڪو شڪ نه آھي، تڏھن انھن جي ڳالھ بابت پاڻ ۾ جھڳڙو ڪرڻ لڳا پوءِ چيائون ته انھن جي (غار) تي ڪا اڏاوت اڏيو، سندن پالڻھار کين ڄاڻندڙ آھي، جيڪي پنھنجي ڳالھ ۾ غالب ٿيا تن چيو ته انھن (جي غار) تي مسجد ضرور بڻائينداسون.');
INSERT INTO "amroti" VALUES(18,22,'18:22','(ڪي) چوندا ته (اُھي) ٽي آھن چوٿون سندن ڪتو آھي، ۽ (ڪي) چوندا ته پنج آھن ڇھون سندن ڪتو آھي ڳُجھ بابت اٽڪل ھڻندا آھن، ۽ چوندا آھن ته ست آھن اٺون سندن ڪتو آھي، (اي پيغمبر) چؤ ته سندن ڳاڻيٽو منھنجو پالڻھار وڌيڪ ڄاڻندڙ آھي انھن کي رڳو ڪي ٿورا ڄاڻندا آھن، پوءِ انھن بابت سرسري جھڳڙي ڪرڻ کانسواءِ جھڳڙو نه ڪر، ۽ نڪي انھن بابت ڪافرن مان ڪنھن ھڪ کان پڇا ڪر.');
INSERT INTO "amroti" VALUES(18,23,'18:23','۽ ڪنھن ڪم لاءِ انشاءالله کانسواءِ نه چؤ ته آءٌ ھي سڀاڻي ڪندس.');
INSERT INTO "amroti" VALUES(18,24,'18:24','۽ جڏھن پنھنجي پالڻھار کي وسارين (تڏھن تون اُن کي) ياد ڪر ۽ چؤ ته سگھوئي منھنجو پالڻھار ھن کان به ڏاڍي ويجھي سڌي ھدايت ڪندو.');
INSERT INTO "amroti" VALUES(18,25,'18:25','۽ اُھي پنھنجي غار ۾ ٽن سون ورھن کان وڌيڪ نَو ورھ رھيا.');
INSERT INTO "amroti" VALUES(18,26,'18:26','(جيڪڏھن اُھي نه مڃين ته) چئو ته جيڪي رھيا سو الله وڌيڪ ڄاڻندڙ آھي، اُن کي آسمانن ۽ زمين جو ڳجھ (معلوم) آھي، ڪھڙو عجيب ڏسندڙ ۽ ڪھڙو عجيب ٻڌندڙ آھي، اُن کانسواءِ ڪو انھن جو مددگار نه آھي، ۽ پنھنجي حُڪم ۾ ڪنھن ھڪ کي ڀائيوار نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(18,27,'18:27','۽ تنھنجي پالڻھار جي ڪتاب مان جيڪي توڏانھن وحي ڪيو ويو سو پڙھ، سندس حُڪم کي ڪا ڦير ڦار نه آھي، ۽ اُن کانسواءِ تون ڪا واھ نه لھندين.');
INSERT INTO "amroti" VALUES(18,28,'18:28','۽ پاڻ کي انھن سان صبر ۾ رک جيڪي پنھنجي پالڻھار کي صُبح ۽ سانجھيءَ جو ياد ڪندا آھن (۽) سندس رضا مندي گھرندا آھن ۽ (گھرجي ته) تنھنجون اکيون دُنيا جي حياتيءَ جي سينگار جو ارادو رکي انھن (ياد ڪندڙن) کان (ٻئي پاسي) نه واجھائين، ۽ انھيءَ جي فرمانبرداري نه ڪر جنھن جي دل اسان پنھنجي ياد ڪرڻ کان غافل ڪئي ۽ پنھنجي خواھش جي پٺيان لڳو ۽ سندس ڪم حد کان وڌيل آھي.');
INSERT INTO "amroti" VALUES(18,29,'18:29','۽ چؤ ته (اھو سخن) اوھان جي پالڻھار وٽان (آيل) سچ آھي، پوءِ جنھن کي وڻي سو ايمان آڻي ۽ جنھن کي وڻي سو نه مڃي، اسان ظالمن لاءِ باھ تيار ڪئي آھي (۽) اُن جا پردا اُنھن کي ويڙھيندا، ۽ جيڪڏھن فرياد ڪندا ته انھن جي فرياد اھڙي پاڻيءَ سان ورائي ويندي جو پگھاريل ٽامي وانگر ھوندو سندن منھن کي ڀڃندو، اُھو بڇڙو پيڻ آھي، ۽ بڇڙو آرام جو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(18,30,'18:30','بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا ته جنھن چڱو ڪم ڪيو آھي تنھن جو اجر اسين نه وڃائينداسون.');
INSERT INTO "amroti" VALUES(18,31,'18:31','اِھي (اُھي) آھن جن لاءِ ھميشگيءَ جا بھشت (ھوندا) جن جي ھيٺان واھيون وھنديون آھن منجھس سونن ڪنگڻن جا زيور انھن کي پارايا ويندا ۽ سَنھي ۽ ٿُلھي پٽ جي سائي پوشاڪ ڍڪيندا (۽) اتي تختن تي تڪيو لائي وھندا، اِھو بدلو چڱو آھي، ۽ آرام جي جاءِ به چڱي آھي.');
INSERT INTO "amroti" VALUES(18,32,'18:32','۽ (اي پيغمبر) کين ٻن مڙسن جو قصو بيان ڪر ۽ انھن مان ھڪڙي لاءِ ڊاکن جا ٻه باغ پيدا ڪيا ھئاسون ۽ انھن جي چوڌاري کجيون پيدا ڪيو ھيوسون ۽ انھن جي وچ ۾ پوک پيدا ڪئي ھئي سون.');
INSERT INTO "amroti" VALUES(18,33,'18:33','ٻنھين باغن پنھنجا ميوا پچايا ۽ منجھانئن ڪجھ گھٽ نه ڪيائون ۽ انھن جي وچ ۾ واھيون وھايون سون.');
INSERT INTO "amroti" VALUES(18,34,'18:34','۽ ان کي (گھڻو) ڦر مليو، پوءِ پنھنجي سنگتيءَ سان ڳالھائيندي ان کي چيائين ته آءٌ توکان مال ۾ وڌيڪ آھيان ۽ ماڻھن جي ڪري (به) وڌيڪ عِزّت وارو آھيان.');
INSERT INTO "amroti" VALUES(18,35,'18:35','۽ پنھنجي باغ ۾ گھڙيو ۽ ھو پاڻ لاءِ ظالم ھو، چيائين ته مون کي ھن (باغ) جي ناس ٿيڻ جو ڪڏھن به گمان نه آھي.');
INSERT INTO "amroti" VALUES(18,36,'18:36','۽ نڪي قيامت جي قائم ٿيڻ جو مون کي ڪو گمان آھي ۽ جيڪڏھن پنھنجي پالھڻار ڏانھن موٽيس (ته) ھن کان (به) ڀلي جاءِ لھندس.');
INSERT INTO "amroti" VALUES(18,37,'18:37','سندس سنگتي ساڻس گفتگو ڪندي کيس چيو ته جنھن توکي مٽيءَ مان بڻايو وري نطفي سان وري سنئين لڱين مڙس ڪيائين تنھن جو تو ڇو انڪار ڪيو؟');
INSERT INTO "amroti" VALUES(18,38,'18:38','(آءٌ ھي ويساھ رکندو آھيان ته) منھنجو پالڻھار الله آھي ۽ آءٌ پنھنجي پالڻھار سان ڪوبه شريڪ نه ڪندس.');
INSERT INTO "amroti" VALUES(18,39,'18:39','۽ جڏھن تون پنھنجي باغ ۾ گھڙين تڏھن ڇونه چيئي ته جيڪي الله گھريو (سو ٿيڻو آھي) جو الله (جي مدد) کانسواءِ ڪا سگھ نه آھي، جيڪڏھن تون پاڻ کان مون کي مال ۽ اولاد ۾ گھٽ ڏسين ٿو.');
INSERT INTO "amroti" VALUES(18,40,'18:40','ته اُميد آھي جو منھنجو پالڻھار تنھنجي باغ کان چڱو مونکي ڏيندو ۽ اُن (تنھنجي باغ) تي آسمان کان ڪا آفت موڪلي پوءِ ڦري چِٽي پَٽي ٿي پَوي.');
INSERT INTO "amroti" VALUES(18,41,'18:41','يا سندس پاڻي سُڪي وڃي پوءِ ان کي ڪڏھن لھي نه سگھين.');
INSERT INTO "amroti" VALUES(18,42,'18:42','۽ سندس ڦر کي ڇٽ ڪيو ويو پوءِ اُن (جي بڻائڻ) ۾ جيڪي خرچيو ھوائين تنھن تي پنھنجا ھٿ مَليندو رھيو ۽ اُھو (باغ) پنھنجين (بڻايلن) ڇتين سميت ڪريل ھو ۽ چيائين ٿي ته افسوس آھي پنھنجي پالڻھار سان ڪنھن کي شريڪ مُقرر نه ڪريان ھا (ته چڱو ھو).');
INSERT INTO "amroti" VALUES(18,43,'18:43','۽ نڪي ان لاءِ ڪا ٽولي ھئي جو الله کانسواءِ کيس مدد ڏئي ۽ نڪي (پاڻ) بدلو وٺڻ وارو ھو.');
INSERT INTO "amroti" VALUES(18,44,'18:44','ھن ھنڌ الله جي سچي بادشاھي آھي، اُھو ثواب (ڏيڻ) ۾ ڀلو ۽ بدلي ڏيڻ ۾ (به) ڀلو آھي.');
INSERT INTO "amroti" VALUES(18,45,'18:45','۽ (اي پيغمبر) انھن کي دُنيا جي حياتيءَ جو مثال بيان ڪر جا انھي پاڻيءَ وانگر آھي جنھن کي آسمان کان وسايوسون پوءِ اُن سان زمين جا اوڀڙ گھاٽا ڄميا پوءِ ڦري ڀُريل ڪک ٿيا اُنھن کي وائن اُڏايو، ۽ الله سڀڪنھن شيءِ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(18,46,'18:46','مال ۽ اولاد دنيا جي حياتيءَ جو سينگار آھي، ۽ سدائين رھندڙ چڱايون تنھنجي پالڻھار وٽ ثواب ڪري ڀليون ۽ اُميد ڪري چڱيون آھن.');
INSERT INTO "amroti" VALUES(18,47,'18:47','۽ (ياد ڪر) جنھن ڏينھن جبلن کي ھلائينداسون ۽ زمين کي صفا پٽي ڏسندين ۽ ماڻھن کي گڏ ڪنداسون پوءِ منجھانئن ڪنھن ھڪ کي نه ڇڏينداسون.');
INSERT INTO "amroti" VALUES(18,48,'18:48','۽ (اُھي) تنھنجي پالڻھار وٽ صفون ڪري پيش ڪيا ويندا (چونداسون ته) جھڙي طرح اوھان کي پھريون ڀيرو پيدا ڪيوھوسون (تھڙي طرح) اسان وٽ آيا آھيو، بلڪ ڀائيندا ھيؤ ته اوھان جي لاءِ انجام جو ھنڌ نه ڪنداسون.');
INSERT INTO "amroti" VALUES(18,49,'18:49','۽ اعمالنامون رکيو ويندو پوءِ جيڪي منجھس ھوندو تنھن کان ڏوھارين کي ڊڄندڙ ڏسندين ۽ چوندا ته اسان لاءِ ارمان آھي ھن اعمالنامي جو ڇا حال آھي جو نڪي ننڍي (گناھ) ۽ نڪي وڏي کي ڳاڻيٽي کانسواءِ ڇڏيو اٿس، ۽ جيڪي ڪيائون سو حاضر لھندا، ۽ تنھنجو پالڻھار ڪنھن ھڪڙي تي به ظلم نه ڪندو.');
INSERT INTO "amroti" VALUES(18,50,'18:50','۽ جڏھن ملائڪن کي چيوسون ته آدم کي سجدو ڪريو تڏھن ابليس کانسواءِ ٻـين سجدو ڪيو، اھو جِنّن مان ھو پوءِ پنھنجي پالڻھار جي حُڪم جي نافرماني ڪيائين، پوءِ اوھين کيس ۽ سندس اولاد کي مون کانسواءِ ڇو دوست وٺندا آھيو حالانڪ اُھي اوھان جا ويري آھن، (شيطان) ظالمن لاءِ بڇڙو بدلو آھي.');
INSERT INTO "amroti" VALUES(18,51,'18:51','نڪي آسمانن ۽ زمين جي بڻائڻ مھل اُنھن (شيطانن) کي حاضر ڪيو ھوم ۽ نڪي سندن جيئن بڻائڻ مھل به، ۽ نڪي گمراھن کي مددگار وٺندڙ آھيان.');
INSERT INTO "amroti" VALUES(18,52,'18:52','۽ جنھن ڏينھن الله چوندو ته (اي مشرڪؤ!) اوھين انھن کي سڏيو جن کي منھنجا شريڪ ڄاڻندا ھيؤ ۽ پوءِ انھن کي سڏيندا ۽ اُھي کين ورندي نه ڏيندا ۽ سندن وچ ۾ ھلاڪت جو ھنڌ ڪنداسون.');
INSERT INTO "amroti" VALUES(18,53,'18:53','۽ ڏوھاري باھ کي ڏسندا پوءِ ڀائيندا ته اُھي اُن ۾ ڪرندا ۽ کانئس موٽڻ جي واھ نه لھندا.');
INSERT INTO "amroti" VALUES(18,54,'18:54','۽ بيشڪ ھن قرآن ۾ ھر طرح جا مثال ماڻھن لاءِ بيان ڪياسون، ۽ ماڻھو سڀ شيء کان گھڻو جھيڙاڪار آھي.');
INSERT INTO "amroti" VALUES(18,55,'18:55','۽ ماڻھن وٽ جڏھن ھدايت آئي تڏھن کين ايمان آڻڻ ۽ پنھنجي پالڻھار کان بخشش گھرڻ کان ھن ڌاران ڪنھن نه جھليو جو اڳين جي رسم (جھڙي پاڙ پَٽ سزا) وٽن اچي يا وٽن سامھون عذاب پُھچي.');
INSERT INTO "amroti" VALUES(18,56,'18:56','۽ پيغمبرن کي رڳو خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليون ٿا، ۽ ڪافر ڪوڙو جھڳڙو ھن لاءِ ڪندا آھن ته ان سان سچ کي ٿيڙين ۽ منھنجين نشانين کي ۽ جنھن سان ڊيڄاريا ويا تنھنکي ٺـٺولي ڪري ورتائون.');
INSERT INTO "amroti" VALUES(18,57,'18:57','۽ اُن کان وڌيڪ ظالم ڪير آھي جنھن کي سندس پالڻھار جي نشانين سان نصيحت ڏني وڃي ته اُن کان مُنھن موڙي ۽ جيڪي سندس ھٿن اڳي ڪري موڪليو سو وساري؟ اسان ان جي سمجھڻ کان سندين دلين تي پردا ڪيا ۽ سندن ڪنن ۾ گھٻرائي (وڌي سون) ۽ جيڪڏھن تون کين ھدايت ڏانھن سڏين ته اُن وقت سِڌو رستو ڪڏھن به نه لھندا.');
INSERT INTO "amroti" VALUES(18,58,'18:58','۽ تنھنجو پالڻھار بخشڻھار ٻاجھارو آھي، جيڪڏھن کين سندن ڪيتي سببان پڪڙي ھا ته انھن لاءِ عذاب تڪڙو موڪلي ھا، (نه!) بلڪ انھن لاءِ انجام ٿيل آھي جو اُن کانسواءِ ڪا واھ نه لھندا.');
INSERT INTO "amroti" VALUES(18,59,'18:59','۽ اھي ڳوٺ آھن جو انھن کي (تڏھن) ناس ڪيوسون جڏھن ظلم ڪيائون ۽ انھن جي ناس ڪرڻ لاءِ (ھڪ) انجام مقرر ڪيوسون.');
INSERT INTO "amroti" VALUES(18,60,'18:60','۽ (ياد ڪر) جڏھن موسىٰ پنھنجي سنگتيءَ کي چيو ته سدا پيو پنڌ ڪندس جيسين ٻن دريائن جي گڏ ٿيڻ جي ھنڌ پُھچندس يا ورھن تائين پيو ھلندس.');
INSERT INTO "amroti" VALUES(18,61,'18:61','پوءِ جنھن مھل ٻن دريائن جي گڏ ٿيڻ جي ھنڌ کي پُھتا (تنھن مھل) پنھنجي مڇي وساريائون جنھن پنھنجي واٽ درياءَ ۾ سرنگھ جان ورتي.');
INSERT INTO "amroti" VALUES(18,62,'18:62','پوءِ جڏھن اڳي ھليا (تڏھن موسىٰؑ) پنھنجي سنگتيءَ کي چيو ته اسان کي اسان جو منجھند جو طعام ڏي، بيشڪ اسان جي ھن مسافريءَ ۾ اسان کي اوکائي پُھتي آھي.');
INSERT INTO "amroti" VALUES(18,63,'18:63','چيائين ته (نه) ڏٺئي ڇا جڏھن پھڻ وٽ ترسيا ھواسون تڏھن مون (اتي) مڇي وساري، ۽ ان جي يادگيري مون کان شيطان کانسواءِ ٻئي ڪنھن نه وسارائي، ۽ پنھنجي واٽ درياء ۾ عجب طرح ورتائين.');
INSERT INTO "amroti" VALUES(18,64,'18:64','(موسىٰؑ) چيو ته اِھو ھنڌ اُھو آھي جنھن کي اسان ڳوليو ٿي، پوءِ ھو پنھنجي پوئين پيرين ڳوليندا موٽيا.');
INSERT INTO "amroti" VALUES(18,65,'18:65','پوءِ مُنھنجن ٻانھن مان اُھو ٻانھون لڌائون جنھن کي پاڻ وٽا ٻاجھ ڏني ھئي سون ۽ پاڻ وٽان کيس عِلم سيکاريو ھوسون.');
INSERT INTO "amroti" VALUES(18,66,'18:66','موسىٰؑ ان کي چيو ته آءٌ تنھنجي ڪڍ ھلان ھن شرط تي ته توکي جيڪي ھدايت مان سيکاريو ويو اُن مان مون کي به سيکارين.');
INSERT INTO "amroti" VALUES(18,67,'18:67','(خضرؑ) چيو ته تون مون سان ڪڏھن به صبر ڪري نه سگھندين.');
INSERT INTO "amroti" VALUES(18,68,'18:68','۽ جنھن جي توکي خبر ئي نه آھي تنھن تي تون ڪٿي صبر ڪندين.');
INSERT INTO "amroti" VALUES(18,69,'18:69','(موسىٰؑ) چيو ته جي الله گھريو ته مون کي صبر ڪندڙ لھندين ۽ آءٌ تنھنجي حُڪم جي نافرماني نه ڪندس.');
INSERT INTO "amroti" VALUES(18,70,'18:70','(خضرؑ) چيو ته جيڪڏھن منھنجي سنگت ڪرين ته (ايستائين) مون کان ڪنھن ڳالھ بابت نه پُڇج جيسين (آءٌ) پاڻ توسان ان جو بيان (نه) ڪريان.');
INSERT INTO "amroti" VALUES(18,71,'18:71','پوءِ ٻئي (اُٿي) ھليا، تان جو ٻيڙيءَ ۾ چڙھيا تڏھن (خضرؑ) اُن کي سوراخ ڪيو، (موسىٰؑ) چيو ته تو اُن کي انھيءَ لاءِ سوراخ ڪيو ڇا ته ٻيڙي وارن کي ٻوڙين؟ بيشڪ تو اڍنگو ڪم ڪيو.');
INSERT INTO "amroti" VALUES(18,72,'18:72','(خضرؑ) چيو ته توکي نه چيو ھوم ڇا ته تون مون سان صبر ڪري نه سگھندين.');
INSERT INTO "amroti" VALUES(18,73,'18:73','(موسىٰؑ) چيو ته جيڪي مون وساريو تنھن سببان مون کي نه پڪڙ ۽ منھنجي ڪم ۾ مون تي سختي نه وجھ.');
INSERT INTO "amroti" VALUES(18,74,'18:74','پوءِ ٻئي ھليا، تان جو جڏھن ھڪ ڇوڪر کي مليا تڏھن (خضرؑ) اُن کي ڪُٺو (موسىٰؑ) چيو ته قصاص ڌاران بيگناھ جِيءَ کي ڇو ڪُٺئي؟ بيشڪ تو خراب ڪم ڪيو.');
INSERT INTO "amroti" VALUES(18,75,'18:75','(خضرؑ) چيو ته توکي نه ھوم ڇا ته تون مون سان (گڏ رھي) ڪڏھن نه صبر ڪري سگھندين؟');
INSERT INTO "amroti" VALUES(18,76,'18:76','(موسىٰؑ) چيو ته جيڪڏھن ھِن کانپوءِ ڪنھن ڳالھ بابت توکان پڇان ته منھنجي سنگت نه ڪج، بيشڪ منھنجي پاران عذر (جي حد) کي پھتين.');
INSERT INTO "amroti" VALUES(18,77,'18:77','پوءِ (ٻئي) ھليا، تان جو جنھن مھل ھڪڙي ڳوٺ وارن وٽ آيا (تنھن مھل) ان جي رھاڪن کان طعام گھريائون ته (انھن) سندن مھماني ڪرڻ کان انڪار ڪيو پوءِ اُن (ڳوٺ) ۾ ھڪ ڊھڻ کي ويجھي ڀِت ڏٺائون پوءِ اُھا (خضرؑ) سڌي ڪئي، (موسىٰؑ) چيو ته جيڪڏھن گھرين ھا ته اُن (جي سڌي ڪرڻ) تي مزدوري ضرور وٺين ھا.');
INSERT INTO "amroti" VALUES(18,78,'18:78','(خضرؑ) چيو ته ھاڻي منھنجي ۽ تنھنجي وچ ۾ جُدائي پئي، ھاڻي توکي انھن ڳالھين جي حقيقت جي سُڌ ڏيان ٿو جن تي تون صبر نه ڪري سگھين.');
INSERT INTO "amroti" VALUES(18,79,'18:79','پر ٻيڙي (جا ڀڳي ويئي سا) مسڪينن جي ھئي (جنھن تي) ھو درياءَ ۾ پورھيو ڪندا (وتندا) ھوا پوءِ اُن کي عيب دار ڪرڻ گھريم جو سندن پوئتان اھڙو بادشاھ ھو جنھن سڀڪا ٻيڙي بيگر ۾ ٿي ورتي.');
INSERT INTO "amroti" VALUES(18,80,'18:80','۽ نينگر (جو ماريو ويو) تنھن جا ماءُ پيءُ مؤمن ھوا تنھنڪري ڊناسون ته نافرمانيءَ ۽ ڪفر ڪرڻ سان ٻنھي کي عاجز ڪندو.');
INSERT INTO "amroti" VALUES(18,81,'18:81','پوءِ گھُريوسون ته سندن پالڻھار کين اُن کان به چڱو بدلو سُٺائي جي ڪري ۽ ٻاجھ جي ڪري تمام ويجھو ڏئي.');
INSERT INTO "amroti" VALUES(18,82,'18:82','۽ جا ڀِت (ٺاھي وئي) سا شھر ۾ ٻن ڇورن ٻارن جي ھُئي ۽ ان جي ھيٺان سندن خزانون ھو ۽ سندن ماءُ پيءُ نيڪ ھوا، پوءِ تنھنجي پالڻھار گھريو ته ٻئي پنھنجي جوانيءَ کي پھچن ۽ پنھنجو خزانو تنھنجي پالڻھار جي ٻاجھ سان ڪڍن، ۽ اُھو پنھنجي راءِ سان نه ڪيم، اِھا اُن جي حقيقت آھي جنھن تي تون صبر نه ٿي ڪري سگھئين.');
INSERT INTO "amroti" VALUES(18,83,'18:83','۽ (اي پيغمبر) توکان ذُوالۡقَرۡنَيۡن بابت پڇن ٿا، چؤ ته اوھان کي ان جو (ڪُجھ) بيان ڪري ٻڌايان ٿو.');
INSERT INTO "amroti" VALUES(18,84,'18:84','بيشڪ اسان ان کي زمين ۾ ھٿ پھچ ڏني ۽ سڀڪنھن شيء جو سان کيس ڏنوسون.');
INSERT INTO "amroti" VALUES(18,85,'18:85','پوءِ سامان (جي مدد) سان ھليو.');
INSERT INTO "amroti" VALUES(18,86,'18:86','ايترو (سير ڪيائين) جو سچ لھڻ جي ھنڌ پھتو اُن کي گپڙاٺي تلاءُ ۾ لھندو ڏٺائين ۽ اُن (تلاءُ) وٽ ھڪ قوم کي ڏٺائين، چيوسون ته اي ذوالقرنين انھن کي سزا ڏئين يا منجھن ڪو چڱائيءَ جو سڌارو ڪرين (ته توکي اختيار آھي).');
INSERT INTO "amroti" VALUES(18,87,'18:87','(ذوالقرنين) چيو ته جيڪو (ماڻھو) ظلم ڪندو تنھن کي سزا ڏينداسون وري پنھنجي پالڻھار ڏانھن ورايو ويندو پوءِ اُھو کيس سخت عذاب جي سزا ڏيندو.');
INSERT INTO "amroti" VALUES(18,88,'18:88','۽ جيڪو ايمان آڻيندو ۽ چڱا ڪم ڪندو تنھن کي چڱو بدلو ملندو، ۽ پنھنجي معاملي مان اُن کي سولائي (جھڙو ڪم) چونداسون.');
INSERT INTO "amroti" VALUES(18,89,'18:89','وري سامان (جي مدد) سان ھليو.');
INSERT INTO "amroti" VALUES(18,90,'18:90','ايترو (سير ڪيائين) جو جڏھن سج اُڀرڻ جي ھنڌ پھتو ته اُھو ھڪ اھڙي قوم تي اُڀرندو ڏٺائين جنھن لاءِ سج کانسواءِ ڪا اوٽ نه بڻائي ھئي سون.');
INSERT INTO "amroti" VALUES(18,91,'18:91','اھڙي طرح (سندس قِصّوآھي)، ۽ بيشڪ اسان وٽ ان جي ساري خبر آھي.');
INSERT INTO "amroti" VALUES(18,92,'18:92','وري سامان (جي مدد) سان ھليو.');
INSERT INTO "amroti" VALUES(18,93,'18:93','ايترو جوجڏھن ٻن جبلن جي وچ ۾ پھتو ته انھن جي ھُن پاسي اھڙي قوم ڏٺائين جا ڪابه ڳالھ سمجھي نه ٿي سگھي.');
INSERT INTO "amroti" VALUES(18,94,'18:94','چيائون ته اي ذوالقرنين بيشڪ ياجوج ۽ ماجوج مُلڪ ۾ فساد وجھندڙ آھن تنھن ڪري تو لاءِ اسين ھن شرط تي ڏن مُقرّر ڪريون ته تون اسان جي ۽ سندن وچ ۾ اَڙ بڻاءِ.');
INSERT INTO "amroti" VALUES(18,95,'18:95','(ذوالقرنين) چيو ته منھنجي پالڻھار جيڪا مون کي ان بابت سگھ ڏني آھي سا ڀلي آھي پوءِ اوھين مون سان زور بار جي مدد ڪريو ته اوھان جي ۽ سندن وچ ۾ اَڙ بڻايان.');
INSERT INTO "amroti" VALUES(18,96,'18:96','لوھ جا ٽُڪر مون وٽ آڻيو، جنھن دم ٻنھي جبلن جي وچ ۾ (ڀري) پورائي ڪيائين (تنھن دم) چيائين ته ڌنئون، تان جو جڏھن ان کي (تپائي) باھ ڪيائين (تڏھن) چيائين ته مون وٽ آڻيو ته مٿس پگھاريل ٽامون پلٽيان.');
INSERT INTO "amroti" VALUES(18,97,'18:97','پوءِ (ياجوج ماجوج) نڪي ان تي چڙھي سگھيا ۽ نڪي اُن کي ڪا سرنگھ ھڻي سگھيا.');
INSERT INTO "amroti" VALUES(18,98,'18:98','چيائين ته ھي منھنجي پالڻھار جي (ھڪڙي) ٻاجھ آھي، جنھن مھل منھنجي پالڻھار جو انجام ايندو (تنھن مھل) ان کي سنئون ڪري ڇڏيندو ۽ منھنجي پالڻھار جو انجام سچو آھي.');
INSERT INTO "amroti" VALUES(18,99,'18:99','۽ اُن ڏينھن ڇڏينداسون ته ھڪ ٻئي ۾ ڳاھٽ ٿيندا (ايندا) ۽ صُور ۾ ڦوڪيو ويندو پوءِ انھن مِڙني کي گڏ ڪنداسون.');
INSERT INTO "amroti" VALUES(18,100,'18:100','۽ اُن ڏينھن اُنھن ڪافرن کي دوزخ آڏو ڪنداسون جئن آڏو ڪجي.');
INSERT INTO "amroti" VALUES(18,101,'18:101','جن جون اکيون منھنجي يادگيريءَ کان ڍَڪ ۾ ھيون ۽ (ساڙ کان ڪجھ) ٻُڌي نه سگھندا ھوا.');
INSERT INTO "amroti" VALUES(18,102,'18:102','ڪافرن (اڃا پاڻ کي سزا جوڳو) نه ڀانيو آھي ڇا جو مون کان سواءِ منھنجن ٻانھن کي دوست ورتو اٿن؟ اسان ڪافرن جي رھڻ لاءِ دوزخ تيار ڪيو آھي.');
INSERT INTO "amroti" VALUES(18,103,'18:103','(اي پيغمبر) چؤ ته ڪرتوتن جي ڪري ٽوٽي وارن جي اوھان کي سُڌ ڏيون ڇا؟');
INSERT INTO "amroti" VALUES(18,104,'18:104','جن پنھنجي ڪوشش دنيا جي حياتيءَ ۾ اَجائي وڃائي ۽ اُھي (پنھنجي لاءِ) ڀائيندا آھن ته اُھي (پاڻ) چڱو ڪم ڪندا آھن.');
INSERT INTO "amroti" VALUES(18,105,'18:105','اِھي اُھي آھن جن پنھنجي پالڻھار جي آيتن ۽ سندن ملڻ جو انڪار ڪيو پوءِ سندن ڪئي ڪمائي چٽ ٿي پوءِ اُنھن لاءِ قيامت جي ڏينھن ڪا تور کڙي نه ڪنداسون.');
INSERT INTO "amroti" VALUES(18,106,'18:106','اِھا سندين سزا دوزخ انھي سببان آھي جو ڪفر ڪيائون ۽ منھنجي آيتن ۽ منھنجي پيغمبرن کي ٺـٺولي ڪري ورتائون.');
INSERT INTO "amroti" VALUES(18,107,'18:107','جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ فردوس جا باغ مھماني (جي جاءِ) آھن.');
INSERT INTO "amroti" VALUES(18,108,'18:108','منجھس سدائين رھڻ وارا آھن کانئس وري اٿلڻ نه گھرندا.');
INSERT INTO "amroti" VALUES(18,109,'18:109','چؤ ته جيڪڏھن منھنجي پالڻھار جي سُخنن (لکڻ) لاءِ درياء مسُ ٿئي ته منھنجي پالڻھار جي سُخنن پوري ٿيڻ کان اڳ درياء کَپي ويندو توڻيڪ جھڙس (ٻيا) مدد لاءِ آڻيون.');
INSERT INTO "amroti" VALUES(18,110,'18:110','چؤ ته آءٌ اوھان جي جھڙوئي ماڻھو آھيان مون ڏانھن وحي موڪليو ويندو آھي ته اوھان جو معبود رڳو ھڪ الله آھي، پوءِ جيڪو پنھنجي پالڻھار جي ملڻ جي اميد رکي تنھنکي جڳائي ته چڱا ڪم ڪري ۽ پنھنجي پالڻھار جي عبادت ۾ ڪنھن ھڪڙي کي شريڪ نه ڪري.');
INSERT INTO "amroti" VALUES(19,1,'19:1','ڪٓھٰيٰعٓصٓ.');
INSERT INTO "amroti" VALUES(19,2,'19:2','تنھنجي پالڻھار جي ٻاجھ سندس ٻانھي زڪريا تي ٿيل جو (ھيءُ) ذڪر آھي.');
INSERT INTO "amroti" VALUES(19,3,'19:3','جڏھن پنھنجي پالڻھار کي ڳجھو ٻاڏائي سڏيائين.');
INSERT INTO "amroti" VALUES(19,4,'19:4','چيائين ته اي منھنجا پالڻھار مُنھنجا (لڱن جا) ھڏ سُست ٿيا آھن ۽ ٻُڍائيءَ کان منھنجو مٿو (اڇو) چمڪيو آھي ته اي منھنجا پالڻھار توکي سڏڻ کان نا اميد نه آھيان.');
INSERT INTO "amroti" VALUES(19,5,'19:5','۽ آءٌ پنھنجي پوئتان پنھنجن مائٽن کان ڊڄان ٿو ۽ منھنجي زال سنڍ آھي تنھنڪري پاڻ وٽان مون کي ڪو وارث بخش.');
INSERT INTO "amroti" VALUES(19,6,'19:6','جو منھنجو وارث ٿئي ۽ يعقوب جي اولاد جو به وارث ٿئي، ۽ اي منھنجا پالڻھار ان کي صالح ڪر.');
INSERT INTO "amroti" VALUES(19,7,'19:7','(چيوسون ته) اي زڪريا اسان توکي ھڪ نينگر جي مبارڪ ڏيون ٿا جنھنجو نالو يحيىٰ آھي اڳ اھڙي نالي وارو ڪونه ڪيوسون.');
INSERT INTO "amroti" VALUES(19,8,'19:8','چيائين اي منھنجا پالڻھار مون کي ھن حالت ۾ پٽ ڪئن ٿيندو جو منھنجي زال سنڍ آھي ۽ بيشڪ آءٌ پيريءَ کان جھورائيءَ کان پھتو آھيان.');
INSERT INTO "amroti" VALUES(19,9,'19:9','(جبرئيل) چيو ته اھڙي طرح (ٿيڻو) آھي، تنھنجي پالڻھار چيو آھي ته اِھو مون کي آسان آھي ۽ بيشڪ توکي ھن کان اڳ پيدا ڪيو اٿم ۽ تون (اصل) ڪجھ نه ھُئين.');
INSERT INTO "amroti" VALUES(19,10,'19:10','زڪريا چيو ته اي منھنجا پالڻھار مون لاءِ ڪا نشاني ٺھراءِ، فرمايائين ته تنھنجي نشاني (ھيءَ آھي) ته ماڻھن سان ساندہ ٽي ڏينھن راتيون ڳالھائي نه سگھندين.');
INSERT INTO "amroti" VALUES(19,11,'19:11','پوءِ محراب مان پنھنجي قوم ڏانھن نڪتو پوءِ انھن اشارو ڏنائين ته صُبح ۽ سانجھيءَ جو (الله جي) پاڪائي بيان ڪريو.');
INSERT INTO "amroti" VALUES(19,12,'19:12','(۽ يحيىٰ ڄمي ڏھن ورھن جو ٿيو ته) چيوسون ته اي يحيىٰ ڪتاب کي مُحڪم وٺ، ۽ ٻاراڻي ۾ کيس عِلم ڏنوسون.');
INSERT INTO "amroti" VALUES(19,13,'19:13','۽ پاڻ وٽان کيس شفقت ۽ سُٺائي ڏني سون، ۽ (اُھو) پرھيزگار ھو.');
INSERT INTO "amroti" VALUES(19,14,'19:14','۽ پنھنجي ماءُ پيءُ سان نيڪي ڪرڻ وارو ھو ۽ ھٺيلو نافرمان نه ھو.');
INSERT INTO "amroti" VALUES(19,15,'19:15','۽ جنھن ڏينھن ڄائو ۽ جنھن ڏينھن مرندو ۽ جنھن ڏينھن جيئرو ٿي اُٿندو (تنھن ڏينھن) مٿس سلام ھجي.');
INSERT INTO "amroti" VALUES(19,16,'19:16','۽ مريم جو قِصّو ڪتاب ۾ ياد ڪر، جڏھن پنھنجي گھر وارن کان اوڀرندي ھنڌ پاس ڀري ٿي.');
INSERT INTO "amroti" VALUES(19,17,'19:17','پوءِ کانئن ھڪ اوٽ ورتائين، پوءِ ڏانھنس پنھنجو ملائڪ موڪليوسون پوءِ ھو ڏسجڻ ۾ اُن لاءِ پورو ماڻھو بڻجي بيٺو.');
INSERT INTO "amroti" VALUES(19,18,'19:18','(مريم) چيو آءٌ توکان ٻاجھاري (الله) جي سام پوان ٿي جيڪڏھن پرھيزگار آھين (ته مون کان پري ٿيءُ).');
INSERT INTO "amroti" VALUES(19,19,'19:19','(ملائڪ) چيو ته آءٌ ته تنھنجي پالڻھار جو ھن لاءِ موڪليل آھيان ته توکي چڱو نينگر بخشيان.');
INSERT INTO "amroti" VALUES(19,20,'19:20','(مريم) چيو ته مون کي ٻار ڪيئن ٿيندو جو مون کي ڪنھن ماڻھوءَ نه ڇُھيو آھي ۽ نڪي آءٌ بدڪار آھيان.');
INSERT INTO "amroti" VALUES(19,21,'19:21','(ملائڪ) چيو ته اھڙي طرح (ٿيڻو) آھي، تنھنجي پالڻھار فرمايو ته اِھو مون کي سولو آھي، ۽ (ھن لاءِ) ته ان کي ماڻھن جي لاءِ پنھنجي طرف کان نشاني ۽ ٻاجھ (جو وسيلو) ڪريون، ۽ (اِھو) ڪم ٺھرايل آھي.');
INSERT INTO "amroti" VALUES(19,22,'19:22','پوءِ اُن سان پيٽ ٿيس پوءِ اُن سان پرڀري ھنڌ الڳ ٿي.');
INSERT INTO "amroti" VALUES(19,23,'19:23','پوءِ ويَم جي سورن کجيءَ جي ٿڙ ڏانھن نِيس، چيائين ته جيڪر ھن کان اڳ مران ھا ۽ اصل وسري ويل ھجان ھا (ته چڱو ھو).');
INSERT INTO "amroti" VALUES(19,24,'19:24','پوءِ کيس ملائڪ ھيٺين طرف کان سڏ ڪيو ته ڏک نه ڪر بيشڪ تنھنجي پالڻھار تنھنجي ھيٺان تلاءُ پيدا ڪيو آھي.');
INSERT INTO "amroti" VALUES(19,25,'19:25','۽ کجيءَ جي ٿڙ کي پاڻ ڏانھن ڌوڻ ته توتي تازيون کارڪون ڇاڻي.');
INSERT INTO "amroti" VALUES(19,26,'19:26','پوءِ کاءُ ۽ پيءُ ۽ نيڻ ٺار، پوءِ جيڪڏھن ڪنھن ماڻھوءَ کي ڏسين ته چؤ ته مون الله لاءِ روزو باسيو آھي تنھنڪري اڄ ڪنھن ماڻھوءَ سان پٽيءَ نه ڳالھائيندس.');
INSERT INTO "amroti" VALUES(19,27,'19:27','پوءِ اُن کي پنھنجي قوم وٽ کڻي آئي، چيائون ته اي مريم بيشڪ بڇڙي شيء آندي اٿيئي.');
INSERT INTO "amroti" VALUES(19,28,'19:28','اي ھارون جي ڀيڻ نڪي تنھنجو پيءُ بڇڙو مڙس ھو ۽ نڪي تنھنجي ماءُ ڪا بدڪار ھئي.');
INSERT INTO "amroti" VALUES(19,29,'19:29','پوءِ ڏانھنس اشارو ڏنائين، چيائون ته جيڪو پينگھي ۾ ڇوڪر آھي تنھن سان ڪيئن ڳالھايون.');
INSERT INTO "amroti" VALUES(19,30,'19:30','(عيسى) چيو ته آءٌ الله جو ٻانھو آھيان، مون کي ڪتاب ڏنو اٿس ۽ مون کي پيغمبر ڪيو اٿس.');
INSERT INTO "amroti" VALUES(19,31,'19:31','۽ جتي ھوندس اُتي مون کي برڪت وارو ڪيائين، ۽ جيسين جيئرو ھوندس تيسين مون کي نماز (پڙھڻ) ۽ زڪوٰة (ڏيڻ) جو حُڪم ڪيائين.');
INSERT INTO "amroti" VALUES(19,32,'19:32','۽ پنھنجي ماءُ سان چڱائي ڪندڙ (ڪيائين)، ۽ مون کي ھٺيلو ۽ بدبخت نه ڪيائين.');
INSERT INTO "amroti" VALUES(19,33,'19:33','۽ جنھن ڏينھن ڄائس ۽ جنھن ڏينھن مرندس ۽ جنھن ڏينھن وري جيئرو ٿي اٿندس تنھن ڏينھن مون تي سلام آھي.');
INSERT INTO "amroti" VALUES(19,34,'19:34','اِھو (قِصّو) عيسىٰ پٽ مريم جو آھي، سچي ڳالھ آھي جنھن ۾ (ماڻھو) شڪ ڪندا آھن.');
INSERT INTO "amroti" VALUES(19,35,'19:35','الله کي (پاڻ لاءِ) ڪو پٽ وٺڻ نه جڳائيندو آھي اُھو پاڪ آھي، جڏھن ڪو ڪم ڪندو آھي تڏھن اُن لاءِ رڳو چوندو آھي ته ٿيءُ ته ٿي پوندو آھي.');
INSERT INTO "amroti" VALUES(19,36,'19:36','۽ الله منھنجو پالڻھار ۽ اوھان جو پالڻھار آھي تنھنڪري سندس عبادت ڪريو، اِھا سڌي واٽ آھي.');
INSERT INTO "amroti" VALUES(19,37,'19:37','پوءِ ڪن ٽولين پاڻ ۾ اختلاف ڪيو، پوءِ ڪافرن لاءِ وڏي ڏينھن جي آڏي ٿيڻ کان خرابي آھي.');
INSERT INTO "amroti" VALUES(19,38,'19:38','جنھن ڏينھن اسان وٽ ايندا (تنھن ڏينھن) ڪھڙو نه چڱو ٻڌندا ۽ ڪھڙو نه چڱو ڏسندا پر ظالم اڄ پڌريءَ گمراھي ۾ آھن.');
INSERT INTO "amroti" VALUES(19,39,'19:39','۽ کين پشيماني جي جي ڏينھن کان ڊيڄار جنھن ڏينھن ڪم پورو ڪبو، ۽ اُھي غفلت ۾ آھن ۽ اُھي ايمان نه ٿا آڻين.');
INSERT INTO "amroti" VALUES(19,40,'19:40','بيشڪ اسين زمين جا ۽ جيڪي مٿس آھي تنھنجا وارث آھيون ۽ اسان ڏانھن موٽايا ويندا.');
INSERT INTO "amroti" VALUES(19,41,'19:41','۽ ابراھيم (جو قصو) ڪتاب ۾ ياد ڪر، بيشڪ اُھو سچو پيغمبر ھو.');
INSERT INTO "amroti" VALUES(19,42,'19:42','جڏھن پنھنجي پيءُ کي چيائين ته اي ابا جيڪو نڪي ڏسي ۽ نڪي توکان ڪجھ ٽاري تنھنجي پوڄا ڇو ڪندو آھين؟');
INSERT INTO "amroti" VALUES(19,43,'19:43','اي ابا بيشڪ (الله جي پار کان) مون وٽ سمجھ آئي آھي جا تو وٽ نه آئي آھي تنھنڪري منھنجي تابعداري ڪر ته توکي سڌو رستو ڏيکاريان.');
INSERT INTO "amroti" VALUES(19,44,'19:44','اي ابا شيطان کي نه پوڄ، ڇوته شيطان الله جو نافرمان آھي.');
INSERT INTO "amroti" VALUES(19,45,'19:45','اي ابا آءٌ توتي الله جي عذاب پھچڻ کان ڊڄان ٿو متان شيطان جي سنگتي ٿئين.');
INSERT INTO "amroti" VALUES(19,46,'19:46','(پڻس) چيو ته اي ابراھيم تون منھنجي معبودن کان ڦرڻ وارو آھين ڇا؟ جيڪڏھن نه روڪبين ته توکي ضرور پٿرن سان ماريندس ۽ گھڻي مدت مون کان پاسي ٿيءُ.');
INSERT INTO "amroti" VALUES(19,47,'19:47','(ابراھيم) چيو ته توتي سلام ھجي، آءٌ تولاءِ پنھنجي پالڻھار کان بخشش گھرندس ڇوته اُھو مون تي مھربان آھي.');
INSERT INTO "amroti" VALUES(19,48,'19:48','۽ الله کانسواءِ جن کي سڏيندا آھيو تن کان ۽ اوھان کان پاسو ڪريان ٿو ۽ پنھنجي پالڻھار جي عبادت ڪندس، اُميد آھي پنھنجي پالڻھار جي عبادت ڪرڻ کان بدبخت نه ٿيندس.');
INSERT INTO "amroti" VALUES(19,49,'19:49','پوءِ جڏھن کانئن ۽ الله کانسواءِ پوڄيندا ھوا تن کان پاسو ڪيائين (تڏھن) کيس اسحاق ۽ يعقوب عطا ڪيوسين، ۽ ھر ھڪ کي پيغمبر ڪيوسون.');
INSERT INTO "amroti" VALUES(19,50,'19:50','۽ کين پنھنجي ٻاجھ مان (گھڻيون شيون) عطا ڪيوسون ۽ انھن جي چڱي يادگيري بلند ڪئي سون.');
INSERT INTO "amroti" VALUES(19,51,'19:51','۽ ڪتاب ۾ موسىٰ (جو قصو) ياد ڪر، بيشڪ اُھو چونڊيل ھو ۽ پيغمبر نبي ھو.');
INSERT INTO "amroti" VALUES(19,52,'19:52','۽ طور (جبل) جي سڄي ڀر کان کيس سڏيوسون ۽ ھمراز ٿيڻ لاءِ کيس ويجھو ڪيوسون.');
INSERT INTO "amroti" VALUES(19,53,'19:53','۽ پنھنجي ٻاجھ سان سندس ڀاءُ ھوارون پيغمبر ڪري کيس عطا ڪيوسون.');
INSERT INTO "amroti" VALUES(19,54,'19:54','۽ ڪتاب ۾ اسماعيل (جو قصّو) ياد ڪر، بيشڪ اُھو انجام جو پورو ۽ پيغمبر نبي ھو.');
INSERT INTO "amroti" VALUES(19,55,'19:55','۽ پنھنجي گھر وارن کي نماز ۽ زڪوٰة جو حُڪم ڪندو ھو، ۽ پنھنجي پالڻھار وٽ پسند ھو.');
INSERT INTO "amroti" VALUES(19,56,'19:56','۽ ڪتاب ۾ ادريس (جو قصّو) ياد ڪر، بيشڪ اُھو سچو پيغمبر ھو.');
INSERT INTO "amroti" VALUES(19,57,'19:57','۽ وڏي مرتبي ڪري سندس مانُ مٿاھون ڪيوسون.');
INSERT INTO "amroti" VALUES(19,58,'19:58','جن تي الله فضل ڪيو سي آدم جي اولاد منجھان پيغمبرن مان آھن، ۽ جن کي نوح سان (ٻيڙي ۾) کنيوسون، ۽ ابراھيم ۽ يعقوب جي اولاد مان، ۽ جن کي ھدايت ڪئي سون ۽ چونڊيوسون تن مان آھن، جڏھن الله جون آيتون کين پڙھي ٻڌائبيون ھيون تڏھن سجدو ڪندڙ ۽ روئيندڙ ٿي ڪري پوندا ھوا.');
INSERT INTO "amroti" VALUES(19,59,'19:59','پوءِ سندن پوئتان ڪيترا (ندورا) پيڙھيءَ تي ويٺا (جو) نماز ڇڏي ڏنائون ۽ سڌن جي پٺيان لڳا پوءَ سگھو گمراھيءَ جي سزا لھندا.');
INSERT INTO "amroti" VALUES(19,60,'19:60','پر جن توبه ڪئي ۽ ايمان آندو ۽ چڱا ڪم ڪيا سي بھشت ۾ گھڙندا ۽ مٿن ڪجھ ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(19,61,'19:61','ھميشه جا باغ آھن جن لاءِ الله پنھنجي ٻانھن سان پرپٺ انجام ڪيو آھي، بيشڪ سندس انجام پھچڻو آھي.');
INSERT INTO "amroti" VALUES(19,62,'19:62','اُن ۾ سلامتي کانسواءِ (ٻي) ڪا بيھودي ڳالھه نه ٻڌندا، ۽ منجھس اُنھن لاءِ صُبح ۽ سانجھيءَ جو سندين روزي آھي.');
INSERT INTO "amroti" VALUES(19,63,'19:63','اِھو بھشت اُھو آھي جو پنھنجن ٻانھن مان جيڪو پرھيزگار بڻبو تنھن کي (اُن جو) وارث ڪنداسون.');
INSERT INTO "amroti" VALUES(19,64,'19:64','۽ (ملائڪن چيو ته) تنھنجي پالڻھار جي حُڪم کانسواءِ نه لھندا آھيون، جيڪو اسان جي آڏو آھي ۽ جيڪي اسان جي پويان آھي ۽ جيڪي انھن جي وچ ۾ آھي تنھنجي (سڀ خبر) اُن کي آھي، ۽ تنھنجو پالڻھار وسارڻ وارو نه آھي.');
INSERT INTO "amroti" VALUES(19,65,'19:65','آسمانن ۽ زمين ۾ جو ۽ جيڪي سندن وچ ۾ آھي تنھنجو پالڻھار آھي پوءِ سندس عبادت ڪر ۽ سندس عبادت تي صبر ڪر (يعني مُحڪم رھ)، تون اُن نالي ڪو ڄاڻندو آھين ڇا؟');
INSERT INTO "amroti" VALUES(19,66,'19:66','۽ ماڻھو چوندو آھي ته جڏھن مرندس (تڏھن) جيئرو ٿي ڪڍبس ڇا؟');
INSERT INTO "amroti" VALUES(19,67,'19:67','ماڻھو ياد نه ڪندو آھي ڇا ته کيس اڳ پيدا ڪيوسون ڪجھ به نه ھو؟');
INSERT INTO "amroti" VALUES(19,68,'19:68','پوءِ پنھنجي پالڻھار جو قسم آھي ته انھن کي ۽ شيطانن کي کڙو ڪنداسون وري دوزخ جي چوڌاري کين گوڏن ڀر حاضر ڪنداسون.');
INSERT INTO "amroti" VALUES(19,69,'19:69','وري سڀ ڪنھن ٽوليءَ مان اھڙن ماڻھن کي ڌار ڪنداسون جن انھن مان ٻاجھاري (الله) جي ڏاڍي نافرماني ڪئي.');
INSERT INTO "amroti" VALUES(19,70,'19:70','وري جيڪي انھن ۾ گھڙڻ جوڳا آھن تن کي اسين چڱو ڄاڻندا آھيون.');
INSERT INTO "amroti" VALUES(19,71,'19:71','۽ اوھان مان ڪو اھڙو ڪونھي جو اُن تي پھچڻ وارو نه آھي، تنھنجي پروردگار تي اھو انجام لازم مُقرّر آھي.');
INSERT INTO "amroti" VALUES(19,72,'19:72','وري پرھيزگارن کي بچائينداسون ۽ ظالمن کي منجھس گوڏن ڀر ڪرندڙ ڇڏينداسون.');
INSERT INTO "amroti" VALUES(19,73,'19:73','۽ جڏھن اسان جون پڌريون آيتون انھن کي پڙھي ٻڌائبيون آھن (تڏھن) ڪافر مؤمنن کي چوندا آھن ته ٻنھي ٽولين مان ڪھڙي (ٽولي) مانَ جي ڪري ڀلي ۽ صحبت جي ڪري ڏاڍي چڱي آھي.');
INSERT INTO "amroti" VALUES(19,74,'19:74','۽ کانئن اڳ ڪيترائي جُڳ ناس ڪياسون جو اُھي سامان ۽ ڏيک جي ڪري ڏاڍا چڱا ھئا.');
INSERT INTO "amroti" VALUES(19,75,'19:75','چؤ ته جيڪو گمراھيءَ ۾ آھي تنھن کي الله وڌيڪ وڌائيندو آھي، تان جو جيڪو انجام يا ته عذاب جو (ساڻن) ڪيو ويو سو جڏھن ڏسندا، تڏھن ھو ڄاڻندا ته بڇڙي مانَ وارو ۽ لشڪر جي ڪري ڏاڍو ھيڻو ڪير آھي.');
INSERT INTO "amroti" VALUES(19,76,'19:76','۽ الله ھدايت وارن کي ھدايت وڌيڪ ڪندو آھي، ۽ سدا رھندڙ چڱايون ثواب ڪري ڀليون ۽ موٽڻ ڪري چڱيون تنھنجي پالڻھار وٽ آھن.');
INSERT INTO "amroti" VALUES(19,77,'19:77','(اي پيغمبر) اُنھي کي نه ڏٺو اٿيئي ڇا جنھن اسان جي آيتن کي نه مڃيو ۽ چيائين ته مون کي مال ۽ اولاد ضرور ڏنو ويندو؟');
INSERT INTO "amroti" VALUES(19,78,'19:78','(اُھو) ڳجھ ڄاڻندو آھي ڇا يا الله وٽان ڪو انجام ورتو اٿس.');
INSERT INTO "amroti" VALUES(19,79,'19:79','ائين نه آھي، جيڪي چوندو آھي سو سگھوئي لکنداسون ۽ اُن لاءِ ڊگھو عذاب وڌائينداسون.');
INSERT INTO "amroti" VALUES(19,80,'19:80','۽ جيڪي چوي ٿو سو کانئس کسينداسون ۽ اسان وٽ ھيڪلو ايندو.');
INSERT INTO "amroti" VALUES(19,81,'19:81','۽ الله کانسواءِ ٻيا معبود ھن لاءِ ورتا اٿن ته اُنھن لاءِ مانَ جو سبب ٿين.');
INSERT INTO "amroti" VALUES(19,82,'19:82','ائين نه آھي، سندين پوڄا جو انڪار ڪندا ۽ اُنھن جا دشمن (۽ مخالف) ٿيندا.');
INSERT INTO "amroti" VALUES(19,83,'19:83','(اي پيغمبر) نه ڏٺو اٿيئي ڇا ته شيطانن کي ڪافرن تي موڪليوسون ته (اُھي) کين لوڏا ڏيئي ڌُوڻين؟');
INSERT INTO "amroti" VALUES(19,84,'19:84','پوءِ مٿن تڪڙ نه ڪر، انھن لاءِ پوري ڳڻ ڳڻيندا آھيون.');
INSERT INTO "amroti" VALUES(19,85,'19:85','اُن ڏينھن پرھيزگارن کي مھمانن وانگر ٻاجھاري (الله) وٽ کڙو ڪنداسون.');
INSERT INTO "amroti" VALUES(19,86,'19:86','۽ ڏوھين کي دوزخ ڏانھن اُڃو روانو ڪنداسون.');
INSERT INTO "amroti" VALUES(19,87,'19:87','جنھن الله وٽان انجام ورتو تنھن کانسواءِ (ٻيا ڪي) شفاعت ڪري نه سگھندا.');
INSERT INTO "amroti" VALUES(19,88,'19:88','۽ چوندا آھن ته الله پاڻ لاءِ اولاد ورتو آھي.');
INSERT INTO "amroti" VALUES(19,89,'19:89','(چئبن ته) بيشڪ اوھان بڇڙي شيء آندي.');
INSERT INTO "amroti" VALUES(19,90,'19:90','اِنھي (چوڻ) کان اِجھو آسمان ڦاٽندا ۽ زمين ڦاٽندي ۽ جبل ذرا ذرا ٿي ڪرندا.');
INSERT INTO "amroti" VALUES(19,91,'19:91','اُنھي سببان جو (انھن) الله لاءِ پُٽ (بڻائي) سڏيا.');
INSERT INTO "amroti" VALUES(19,92,'19:92','۽ الله کي اولاد وٺڻ نه جڳائيندو آھي.');
INSERT INTO "amroti" VALUES(19,93,'19:93','جيڪي آسمانن ۽ زمين ۾ آھي سي سڀ الله وٽ ٻانھا ٿي ايندا.');
INSERT INTO "amroti" VALUES(19,94,'19:94','بيشڪ الله کين ڳڻيو آھي ۽ سندن ڳاڻاٽو ڳڻيو اٿس.');
INSERT INTO "amroti" VALUES(19,95,'19:95','۽ قيامت جي ڏينھن انھن مان ھر ھڪ وٽس ھيڪلو ايندو.');
INSERT INTO "amroti" VALUES(19,96,'19:96','بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ سگھو الله محبّت پيدا ڪندو.');
INSERT INTO "amroti" VALUES(19,97,'19:97','پوءِ اُھو (قرآن) تنھنجي زبان تي رڳو ھن لاءِ آسان ڪيوسون ته (تون) ساڻس پرھيزگارن کي خوشخبري ڏئين ۽ ان سان جھيڙا گر قوم کي ڊيڄارين.');
INSERT INTO "amroti" VALUES(19,98,'19:98','۽ کانئن اڳ گھڻا جُڳ ناس ڪياسون، منجائن ڪنھن ھڪ جو پتو لھين ٿو ڇا يا سندس ڀڻڪ ٻڌين ٿو ڇا؟');
INSERT INTO "amroti" VALUES(20,1,'20:1','طٰہٰ.');
INSERT INTO "amroti" VALUES(20,2,'20:2','توتي قرآن ھن لاءِ نه لاٿوسون ته تون اؤکو ٿئين.');
INSERT INTO "amroti" VALUES(20,3,'20:3','پر جيڪو ڊڄي تنھن جي نصيحت ڏيڻ لاءِ (لاٿوسون).');
INSERT INTO "amroti" VALUES(20,4,'20:4','اُنھي (جي طرف) کان لھڻ وارو آھي جنھن زمين ۽ مٿاھن آسمانن کي بڻايو آھي.');
INSERT INTO "amroti" VALUES(20,5,'20:5','(اُھو) ٻاجھارو عرش تي قائم آھي.');
INSERT INTO "amroti" VALUES(20,6,'20:6','جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي ۽ جيڪي ٻنھي جي وچ ۾ آھي ۽ جيڪي زمين جي تري ھيٺان آھي سو اُن جو آھي.');
INSERT INTO "amroti" VALUES(20,7,'20:7','۽ جيڪڏھن (تون) ڏاڍيان ڳالھائين ته بيشڪ اُھو ڳُجھ ۽ پڌرو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(20,8,'20:8','الله (اُھو آھي جو) اُن کانسواءِ ٻيو ڪو معبود نه آھي، اُن جا چڱا نالا آھن.');
INSERT INTO "amroti" VALUES(20,9,'20:9','۽ (اي پيغمبر) توکي مُوسىٰ جي ڳالھ نه پُھتي آھي ڇا؟');
INSERT INTO "amroti" VALUES(20,10,'20:10','جڏھن باھ ڏٺائين تڏھن پنھنجي گھر جي ڀاتين کي چيائين ته ترسو جو مون باھ ڏٺي آھي اميد ته منجھانئس ڪو اڱر اوھان وٽ کڻي اچان يا باھ تي ڪو ڏَس ڏيڻ وارو لھان.');
INSERT INTO "amroti" VALUES(20,11,'20:11','پوءِ جنھن مھل باھ وٽ آيو (تنھن مھل) ان کي سڏيو ويو ته اي موسى.');
INSERT INTO "amroti" VALUES(20,12,'20:12','بيشڪ آءٌ تنھنجو پالڻھار آھيان تنھنڪري پنھنجي جُتي (پري) لاھ، ڇوته تون پاڪ ميدان طُوىٰ ۾ آھين.');
INSERT INTO "amroti" VALUES(20,13,'20:13','۽ مون توکي پسند ڪيو آھي تنھنڪري جيڪي وحي ڪجي سو ٻُڌ.');
INSERT INTO "amroti" VALUES(20,14,'20:14','سچ پچ آءٌ الله آھيان مون کان سواءِ (ٻيو ڪو) معبود نه آھي تنھنڪري منھنجي عبادت ڪر ۽ منھنجي ياد ڪرڻ لاءِ نماز قائم ڪر.');
INSERT INTO "amroti" VALUES(20,15,'20:15','بيشڪ قيامت اچڻي آھي اُن (جي وقت جي اچڻ) کي ھِن ڪري ڳجھو ڪريان ٿو ته سڀڪنھن جيءَ کي سندس ڪوشش جو بدلو ڏجي.');
INSERT INTO "amroti" VALUES(20,16,'20:16','جيڪو اُن کي نه مڃيندو آھي ۽ پنھنجن سَڌن جي پٺيان لڳو آھي سو توکي اُن (جي وسھڻ) کان متان ڦيرائي نه ته ھلاڪ ٿيندين.');
INSERT INTO "amroti" VALUES(20,17,'20:17','۽ اي مُوسىٰ اِھا تنھنجي سڄي ھٿ ۾ ڇا آھي؟');
INSERT INTO "amroti" VALUES(20,18,'20:18','چيائين ته ھيءَ منھنجي لٺ آھي، مٿس ٽيڪ ڏيندو آھيان ۽ اُن سان پنھنجين ٻڪرين تي (وڻ جا) پن ڇاڻيندو آھيان ۽ منجھس مون لاءِ ٻيا (به) فائدا آھن.');
INSERT INTO "amroti" VALUES(20,19,'20:19','(الله) فرمايو ته اي مُوسىٰ اُھا اُڇلي ڇڏ.');
INSERT INTO "amroti" VALUES(20,20,'20:20','پوءِ اُھا اُڇليائين ته اُھا انھي مھل ڊوڙندڙ واسينگ نانگ ٿي پئي.');
INSERT INTO "amroti" VALUES(20,21,'20:21','(الله) فرمايو ته اُنھيءَ کي وٺ ۽ نه ڊڄ، اُنھيءَ کي سندس پھرين شڪل ۾ موٽائينداسون.');
INSERT INTO "amroti" VALUES(20,22,'20:22','۽ پنھنجو ھٿ پنھنجي بغل تي رک ته بي عيبو اڇو ٿي نڪري اِھا ٻي نشاني (ھن لاءِ) آھي.');
INSERT INTO "amroti" VALUES(20,23,'20:23','ته توکي پنھنجين وڏين نشانين مان ڏيکاريون.');
INSERT INTO "amroti" VALUES(20,24,'20:24','فرعون ڏانھن وڃ جو اُھو بيشڪ نافرمان ٿيو آھي.');
INSERT INTO "amroti" VALUES(20,25,'20:25','(مُوسى) چيو ته اي منھنجا پالڻھار مون لاءِ منھنجو سينو کول.');
INSERT INTO "amroti" VALUES(20,26,'20:26','۽ منھنجو ڪم مون لاءِ سولو ڪر.');
INSERT INTO "amroti" VALUES(20,27,'20:27','۽ منھنجي زبان جي ھٻڪ لاھ.');
INSERT INTO "amroti" VALUES(20,28,'20:28','ته (اُھي) مُنھنجي ڳالھ سمجھن.');
INSERT INTO "amroti" VALUES(20,29,'20:29','۽ منھنجي گھر وارن منجھان مون لاءِ ڪو وزير مُقرّر ڪر.');
INSERT INTO "amroti" VALUES(20,30,'20:30','(يعني) منھنجي ڀاءُ ھارون کي.');
INSERT INTO "amroti" VALUES(20,31,'20:31','اُن سان منھنجي سگھ مُحڪم ڪر.');
INSERT INTO "amroti" VALUES(20,32,'20:32','۽ منھنجي ڪم ۾ اُن کي ڀائيوار ڪر.');
INSERT INTO "amroti" VALUES(20,33,'20:33','ته تنھنجي گھڻي پاڪائي بيان ڪريون.');
INSERT INTO "amroti" VALUES(20,34,'20:34','۽ توکي گھڻو ياد ڪريون.');
INSERT INTO "amroti" VALUES(20,35,'20:35','بيشڪ تون اسان جو حال ڏسندڙ آھين.');
INSERT INTO "amroti" VALUES(20,36,'20:36','(الله) چيو ته اي موسىٰ بيشڪ (تولاءِ) تنھنجو سوال قبول ڪيو ويو.');
INSERT INTO "amroti" VALUES(20,37,'20:37','۽ بيشڪ توتي ھڪ ڀيرو (ٻيو به) احسان ڪيوسون.');
INSERT INTO "amroti" VALUES(20,38,'20:38','جڏھن تنھنجي ماءُ ڏانھن اُھو الھام ڪيوسون جيڪو الھام ڪرڻو ھو.');
INSERT INTO "amroti" VALUES(20,39,'20:39','ته اُن نينگر کي صندوق ۾ وِجھ پوءِ اُن کي درياءَ ۾ اُڇل ته درياءُ کيس ڀر ڏانھن اُڇليندو (۽) منھنجو ويري ۽ سندس ويري کيس وٺندو، ۽ پاڻ وٽان توتي پيار وڌم، ۽ (گھريم) ته منھنجي اکين اڳيان پلجن.');
INSERT INTO "amroti" VALUES(20,40,'20:40','جڏھن تنھنجي ڀيڻ گھمي ٿي تڏھن چيائين ٿي جيڪو ان کي سنڀالي تنھنجو اوھان کي ڏس ڏيان، پوءِ توکي تنھنجي ماءُ ڏانھن موٽايوسون ته اُن جا نيڻ ٺرن ۽ غمگين نه رھي، ۽ ھڪڙو ماڻھو ماريئي پوءِ توکي ڏک کان ڇڏايوسين ۽ توکي پرکڻ طرح پرکيوسون، پوءِ مدين وارن ۾ ڪيترائي ورھ رھين، وري اي موسىٰ تون تقدير (الٰھي) موافق آئين.');
INSERT INTO "amroti" VALUES(20,41,'20:41','۽ (اي موسى) توکي خلاصو پاڻ لاءِ بڻايم.');
INSERT INTO "amroti" VALUES(20,42,'20:42','تون ۽ تنھنجو ڀاءُ منھنجين نشانين سميت (فرعون ڏانھن) وڃو ۽ منھنجي ياد ڪرڻ ۾ ڍرائي نه ڪجو.');
INSERT INTO "amroti" VALUES(20,43,'20:43','فرعون ڏانھن وڃو جو اُھو نافرمان ٿيو آھي.');
INSERT INTO "amroti" VALUES(20,44,'20:44','پوءِ اُن سان نرميءَ سان ڳالھائجو ته مانَ اُھو سوچ ڪري يا ڊڄي.');
INSERT INTO "amroti" VALUES(20,45,'20:45','چيائون ته اي اسان جا پالڻھار اسين ڊڄون ٿا ته متان اسان تي ڏاڍ ڪري يا زيادتي ڪري.');
INSERT INTO "amroti" VALUES(20,46,'20:46','(الله) چيو ته نه ڊڄو بيشڪ آءٌ اوھان سان گڏ آھيان ٻڌان ٿو ۽ ڏسان ٿو.');
INSERT INTO "amroti" VALUES(20,47,'20:47','پوءِ وٽس وڃو ۽ چئو ته اسين پنھنجي پالڻھار جا موڪليل آھيون پوءِ بني اسرائيلن کي اسان سان (ھلڻ) جي موڪل ڏي، ۽ کين نه ڏکوءِ بيشڪ تو وٽ تنھنجي پالڻھار (جي طرف) کان نشانيءَ سان آيا آھيون، ۽ جيڪو سڌي رستي تي ھلي تنھن تي سلامتي آھي.');
INSERT INTO "amroti" VALUES(20,48,'20:48','بيشڪ اسان ڏانھن وحي موڪليو ويو آھي ته جيڪو الله جي حُڪمن کي ڪُوڙ ڀانئي ۽ منھن موڙي تنھن تي عذاب (مقرر ٿيل) آھي.');
INSERT INTO "amroti" VALUES(20,49,'20:49','(فرعون) چيو ته پوءِ اي مُوسىٰ اوھان جو پالڻھار ڪير آھي؟');
INSERT INTO "amroti" VALUES(20,50,'20:50','(موسى) چيو ته اسان جو پالڻھاراُھو آھي جنھن ھر شيء کي (پاڻو داڻي) رُؤاني بخشي وري ھدايت ڪيائين.');
INSERT INTO "amroti" VALUES(20,51,'20:51','(فرعون) چيو ته پھرين ٽولين جو ڪھڙو حال آھي؟');
INSERT INTO "amroti" VALUES(20,52,'20:52','(موسى) چيو ته اُن جي خبر منھنجي پالڻھار وٽ ڪتاب ۾ آھي، منھنجو پالڻھار نڪي ڀُلندو آھي ۽ نڪي وساريندو آھي.');
INSERT INTO "amroti" VALUES(20,53,'20:53','جنھن اوھان لاءِ زمين کي وڇاڻو بڻايو ۽ توھان لاءِ منجھس رستا جاري ڪيا ۽ آسمان کان پاڻي وَسايو، ۽ پوءِ اُن سان ھر جنس جا اوڀڙ پاڻون داڻا ڄماياسون.');
INSERT INTO "amroti" VALUES(20,54,'20:54','(چيوسون ته) کائو ۽ پنھنجن ڍورن کي چاريو، بيشڪ اُن ۾ سمجھ وارن لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(20,55,'20:55','منجھانئس اوھان کي بڻايوسون ۽ منجھس اوھان کي ورائينداسون ۽ منجھانئس ٻيو ڀيرو اوھان کي ڪڍنداسون.');
INSERT INTO "amroti" VALUES(20,56,'20:56','۽ بيشڪ اُن (فرعون) کي سڀئي نشانيون ڏيکاريوسون پوءِ ڪوڙ ڀانيائين ۽ انڪار ڪيائين.');
INSERT INTO "amroti" VALUES(20,57,'20:57','(فرعون) چيو ته اي موسىٰ اسان وٽ ھِن لاءِ آيو آھين ڇا ته پنھنجي جادوءَ سان اسان جي مُلڪن مان اسان کي لوڌين؟');
INSERT INTO "amroti" VALUES(20,58,'20:58','پوءِ اسان به اھڙائي جادو تو وٽ ضرور آڻينداسون (جھڙا تون اسان کي ڏيکارين ٿو) تنھنڪري اسانجي ۽ پنھنجي وچ ۾ اھڙو انجام (جو وقت) مقرر ڪر ۽ نڪي اسين ابتڙ ٿيون ۽ (اُھو) ھڪ صاف پڌر ۾ ھُجي.');
INSERT INTO "amroti" VALUES(20,59,'20:59','چيائين ته اوھان جي انجام جو وقت جشن جو ڏينھن آھي ۽ ماڻھو ڏينھن چڙھي گڏ ٿين.');
INSERT INTO "amroti" VALUES(20,60,'20:60','پوءِ فرعون (گھر ڏانھن) موٽيو ۽ پنھنجا ڦند گڏ ڪيائين وري آيو.');
INSERT INTO "amroti" VALUES(20,61,'20:61','مُوسىٰ جادوگرن کي چيو ته اوھان تي خرابي ھجي الله تي ڪوڙو ٺاھ نه ٺاھيو نه ته اوھان کي ڪنھن آفت سان کپائيندو، ۽ جنھن ٺاھ ٺاھيو سو بيشڪ مراد کي نه پھتو.');
INSERT INTO "amroti" VALUES(20,62,'20:62','پوءِ پنھنجي ڪم ۾ پاڻ ۾ گفتگو ڪيائون ۽ ڳجھيون صلاحون ڪيائون.');
INSERT INTO "amroti" VALUES(20,63,'20:63','چيائون ته ھي ٻئي ضرور جادوگر آھن پنھنجي جادوءَ سان اوھان کي اوھان جي مُلڪ مان لوڌڻ ۽ اوھان جي بلڪل چڱي مذھب کي وڃائڻ گھرندا آھن.');
INSERT INTO "amroti" VALUES(20,64,'20:64','تنھنڪري پنھنجا جادوءَ (جا سامان) گڏ ڪريو وري صفون ٻڌي اچو، ۽ جيڪو اڄ ڏاڍو ٿيو سو بيشڪ ڇٽو.');
INSERT INTO "amroti" VALUES(20,65,'20:65','چيائون ته اي مُوسىٰ (تون) اُڇليندين يا پھرين اسين (پنھنجو جادو) اُڇليون.');
INSERT INTO "amroti" VALUES(20,66,'20:66','(مُوسى) چيو ته ڀلي (اوھين) اُڇليو، پوءِ اوچتوئي سندن رسا ۽ سندن لٺيون جادوءَ کان موسىٰ جي خيال ۾ آيو ته ڄڻڪ اھي ڊوڙن ٿيون.');
INSERT INTO "amroti" VALUES(20,67,'20:67','پوءِ مُوسىٰ پنھنجي دل ۾ ڊپ ڀانيو.');
INSERT INTO "amroti" VALUES(20,68,'20:68','چيوسون ته نه ڊڄ ڇوته تون ئي غالب آھين.');
INSERT INTO "amroti" VALUES(20,69,'20:69','۽ جيڪي تنھنجي سڄي ھٿ ۾ آھي سو اُڇل ته جيڪي بڻايائون سو ڳِھي وڃي، جيڪي بڻايائون سو جادوگر جي فريب کانسواءِ نه آھي، ۽ جادوگر جِتي ويندو اُتي نه ڇٽندو.');
INSERT INTO "amroti" VALUES(20,70,'20:70','پوءِ سڀ جادوگر سجدو ڪندڙ ٿي ڪري پيا چيائون ته مُوسىٰ ۽ ھارون جي پالڻھار تي آيمان آندوسون.');
INSERT INTO "amroti" VALUES(20,71,'20:71','(فرعون) چيو ته اوھان منھنجي موڪل ڏيڻ کان اڳ اُن تي ڇو ايمان آندو، بيشڪ اُھو اوھان جو ضرور وَڏو (يعني استاد) آھي جنھن اوھان کي جادو سيکاريو آھي، تنھنڪري اوھان جا ھٿ ۽ اوھان جا پير اُبتا سُبتا ضرور وڍيندس ۽ اوھان کي کجين جي ٿُڙن ۾ ضرور سُوريءَ چاڙھيندس، ۽ ضرور ڄاڻندؤ ته اسان مان ڪنھنجو عذاب بلڪل سخت ۽ بلڪل ھميشه رھڻ وارو آھي.');
INSERT INTO "amroti" VALUES(20,72,'20:72','چيائون ته چِٽن دليلن مان جيڪي اسان وٽ آيا تن تي ۽ جنھن اسان کي پيدا ڪيو تنھن تي توکي ڪڏھن زيادتي نه ڏينداسون تنھنڪري جيڪي تون ڪرڻ وارو آھين سو ڪر، (تون) رڳو دُنيا جي حياتيءَ ۾ ڪندين.');
INSERT INTO "amroti" VALUES(20,73,'20:73','بيشڪ اسان پنھنجي پالڻھار تي ھن لاءِ ايمان آندو آھي ته اسان جون خطائون ۽ جنھن جادو ڪرڻ تي تو اسان کي زور ڪيو سو اسان کي بخشي، ۽ الله ڀلو ۽ سدائين رھڻ وارو آھي.');
INSERT INTO "amroti" VALUES(20,74,'20:74','سچ آھي ته جيڪو منھنجي پالڻھار وٽ ڏوھاري ٿي ايندو تنھن لاءِ بيشڪ دوزخ آھي، اُھو منجھس نڪي مرندو ۽ نڪي جيئندو.');
INSERT INTO "amroti" VALUES(20,75,'20:75','۽ جيڪي مؤمن ٿي وٽس چڱا ڪم ڪري ايندا سي اُھي آھن جن لاءِ وڏا مرتبا آھن.');
INSERT INTO "amroti" VALUES(20,76,'20:76','سدائين رھڻ جا باغ جن جي ھيٺان واھيون وھنديون آھن منجھس سدائين رھڻ وارا آھن، ۽ جيڪو پاڪ ٿيو تنھنجو اِھو بدلو آھي.');
INSERT INTO "amroti" VALUES(20,77,'20:77','۽ بيشڪ موسىٰ ڏانھن وحي ڪيوسون ته راتو واھ منھنجي ٻانھن سميت نڪر پوءِ سمنڊ ۾ انھن لاءِ سُڪي واٽ بڻاءِ (ويرين کان) پڪڙجڻ جي ڀَو نه ڪر ۽ نڪي (ٻُڏڻ کان) ڊڄ.');
INSERT INTO "amroti" VALUES(20,78,'20:78','پوءِ فرعون پنھنجي لشڪر سميت سندن پوئتان پيو پوءِ سمنڊ مان (پاڻيءَ جو اُھو ڪُنَّ) کين وري ويو جيڪو کين ورڻو ھو.');
INSERT INTO "amroti" VALUES(20,79,'20:79','۽ فرعون پنھنجي قوم کي گمراھ ڪيو ۽ سڌي رستي نه لاتو.');
INSERT INTO "amroti" VALUES(20,80,'20:80','اي بني اسرائيلؤ بيشڪ اوھان کي اوھان جي ويريءَ کان بچايوسون ۽ طور (جبل) جي سڄي پاسي کان اوھان کي (توريت ڏئڻ جو) انجام ڪيوسون ۽ مَنّ ۽ سَلوىٰ اوھان تي لاٿاسون.');
INSERT INTO "amroti" VALUES(20,81,'20:81','(۽ چيوسون ته) سٺين (شين) مان جيڪا اوھان جي روزي ڪئي سون سا کائو ۽ منجھس نافرماني نه ڪريو نه ته اوھان تي منھنجو ڏمر نازل ٿيندو، ۽ جنھن تي منھنجو ڏمر نازل ٿيو سو بيشڪ اُجڙيو.');
INSERT INTO "amroti" VALUES(20,82,'20:82','۽ جنھن توبه ڪئي ۽ ايمان آندو ۽ چڱا ڪم ڪيا وري سڌو رستو لڌائين تنھن کي بيشڪ آءٌ بخشيندڙ آھيان.');
INSERT INTO "amroti" VALUES(20,83,'20:83','۽ اي موسىٰ ڪنھن توکي پنھنجي قوم کان (اڳ اچڻ جي) تڪڙ ڪرائي؟');
INSERT INTO "amroti" VALUES(20,84,'20:84','چيائين ته اُھي منھنجي پوئتان آھن ۽ اي منھنجا پالڻھار توڏانھن ھن لاءِ تڪڙ ڪيم ته راضي ٿئين.');
INSERT INTO "amroti" VALUES(20,85,'20:85','(الله) چيو ته اسان تنھنجي پويان تنھنجي قوم کي پرکيو ۽ کين سامريءَ گمراھ ڪيو.');
INSERT INTO "amroti" VALUES(20,86,'20:86','پوءِ مُوسىٰ پنھنجي قوم ڏانھن ڪاوڙجي ارمان ۾ موٽيو، چيائين ته اي منھنجي قوم اوھان جي پالڻھار اوھان کي چڱو انجام نه ڏنو ھو ڇا؟ پوءِ اوھان تي مُدو ڊگھو ٿي پيو ڇا يا پنھنجي پالڻھار جو ڏمر پاڻ تي نازل ٿيڻ گھريوَ؟ تنھنڪري منھنجي انجام جي ابتڙائي ڪيوَ.');
INSERT INTO "amroti" VALUES(20,87,'20:87','چيائون ته پنھنجي وس کان تنھنجي انجام جي برخلافي نه ڪئي سون پر (فرعون جي) قوم جي زيورن مان بار کڻايا وياسون پوءِ اُھي (باھ ۾) وڌاسون پوءِ اھڙي طرح سامري وڌا.');
INSERT INTO "amroti" VALUES(20,88,'20:88','پوءِ انھن لاءِ گابي جو بوتو ڪڍيائين جنھن کي رنڀ ھئي پوءِ چيائون ته ھيءُ اوھان جو معبود ۽ موسىٰ جو معبود آھي، ۽ (مُوسى) ڀلجي ويو.');
INSERT INTO "amroti" VALUES(20,89,'20:89','نه ٿي ڏٺائون ڇا ته (اُھو) اُنھن کي ورندي ورائي نٿو ڏئي، ۽ کين نڪي ڏُک ۽ نڪي سُک ڏئي سگھي ٿو؟');
INSERT INTO "amroti" VALUES(20,90,'20:90','۽ بيشڪ ھارُون پھريون ئي کين چيو ھو ته اي منھنجي قوم اوھين اُن (گابي) سان پرکيا ويا آھيو، بيشڪ اوھان جو پالڻھار ٻاجھارو (الله) آھي تنھنڪري منھنجي تابعداري ڪريو ۽ منھنجو حُڪم مڃيو.');
INSERT INTO "amroti" VALUES(20,91,'20:91','چيائون ته (اسين ايسين) مٿس مجاور رھنداسون جيسين مُوسىٰ اسان ڏانھن موٽي.');
INSERT INTO "amroti" VALUES(20,92,'20:92','(مُوسىٰ اچي) چيو ته اي ھارون توکي ڪنھن جھليو جڏھن کين گمراھ ٿيندو ڏٺئي.');
INSERT INTO "amroti" VALUES(20,93,'20:93','ته منھنجي پٺيان نه لڳين، منھنجي حُڪم جي نافرماني ڪيئي ڇا؟');
INSERT INTO "amroti" VALUES(20,94,'20:94','(ھارون) چيو ته اي ادا نڪي مون کي ڏاڙھيءَ کان وٺ ۽ نڪي منھنجي مٿي کان، آءٌ (ھن ڳالھ کان) ڊنس ته متان چوين ته يعقوب جي اولاد جي وچ ۾ جدائي وڌءِ ۽ منھنجي چوڻ جو انتظار نه ڪيئي.');
INSERT INTO "amroti" VALUES(20,95,'20:95','(موسى) چيو ته اي سامري تنھنجو ڇا حال آھي.');
INSERT INTO "amroti" VALUES(20,96,'20:96','چيائين ته اُھا (شيء) ڏٺم جا (ماڻھن) نه ڏٺي پوءِ اُن موڪيل جي پيرن جي (مٽيءَ جي) ھڪ مُٺ ڀريم پوءِ اُھا (گابي جي بوتي ۾) وڌم ۽ اھڙي طرح مُنھنجي نفس (اُھو) مون لاءِ سينگاريو.');
INSERT INTO "amroti" VALUES(20,97,'20:97','(موسى) چيو ته وڃ بيشڪ توکي حياتيءَ ۾ (ھيءَ سزا) آھي ته چوندو رھندين ته نه ڇُھو، ۽ تولاءِ بيشڪ (ٻيو به) انجام آھي جنھن جي توسان ابتڙائي نه ڪبي، ۽ پنھنجي معبود ڏانھن نھار جنھن تي تون مجاور ٿي ويٺو ھُئين، ته اُن کي ساڙينداسون وري اُن (جي ڪيريءَ) کي درياءَ ۾ کنڊائڻ وانگر کنڊائينداسين.');
INSERT INTO "amroti" VALUES(20,98,'20:98','اوھان جو معبود الله ئي آھي انھيءَ کانسواءِ ڪو عبادت جو لائق ڪونھي، سڀڪا شيء سندس علم ۾ سمائي آھي.');
INSERT INTO "amroti" VALUES(20,99,'20:99','(اي پيغمبر) اھڙي طرح جيڪي گذري ويا تن جي خبرن مان توتي بيان ڪريون ٿا، ۽ پاڻ وٽان توکي نصيحت (يعني قرآن) ڏنوسون.');
INSERT INTO "amroti" VALUES(20,100,'20:100','جيڪو کانئس مُنھن موڙيندو سو قيامت جي ڏينھن (گناھن جو) بار کڻندو.');
INSERT INTO "amroti" VALUES(20,101,'20:101','منجھس سدائين رھڻ وارا آھن، ۽ قيامت جي ڏينھن انھن کي بار کڻڻ بڇڙو آھي.');
INSERT INTO "amroti" VALUES(20,102,'20:102','جنھن ڏينھن صُور ۾ ڦوڪيو ويندو ۽ اُن ڏينھن ڏوھارين کي جھرو ڪري اُٿارينداسون.');
INSERT INTO "amroti" VALUES(20,103,'20:103','(تنھن ڏينھن) پاڻ ۾ سُ پُس ڪندا ته اوھين (دُنيا ۾) رڳو ڏھاڪو ڏينھن رھيؤ.');
INSERT INTO "amroti" VALUES(20,104,'20:104','جيڪي چوندا سو اسين چڱو ڄاڻندڙ آھيون اُن مھل منجھائن پوري رستي وارو چوندو ته ھڪ ڏينھن کانسواءِ نه رھيا آھيو.');
INSERT INTO "amroti" VALUES(20,105,'20:105','۽ (اي پيغمبر) جبلن بابت توکان پُڇن ٿا چؤ ته منھنجو پالڻھار اُنھن کي اُڏائي کنڊائيندو.');
INSERT INTO "amroti" VALUES(20,106,'20:106','پوءِ اُن ھنڌ کي صاف پٽ ڪري ڇڏيندو.');
INSERT INTO "amroti" VALUES(20,107,'20:107','منجھس نڪي ھيٺاھين ۽ نڪي مٿاھين ڏسندين.');
INSERT INTO "amroti" VALUES(20,108,'20:108','اُن ڏينھن سڏيندڙ جي پوئتان لڳندا جنھن کي ڪا ڏنگائي نه ھوندي، ۽ سڀ آواز الله لاءِ جھڪا ٿيندا پوءِ ڀُڻڪ کانسواءِ ڪُجھ نه ٻڌندين.');
INSERT INTO "amroti" VALUES(20,109,'20:109','جنھن کي الله موڪل ڏني ۽ اُن جي چوڻ کان راضي ٿيو تنھن کانسواءِ اُن ڏينھن ڪا سفارش فائدو نه ڏيندي.');
INSERT INTO "amroti" VALUES(20,110,'20:110','جيڪي سندن اڳيان ۽ جيڪي سندن پوئتان آھي سو (الله) ڄاڻندو آھي ۽ (ماڻھو) کيس علم ڪري نه ويڙھيندا.');
INSERT INTO "amroti" VALUES(20,111,'20:111','۽ سڀ مُنھن سدائين جيئري بيھڪ ڏيندڙ لاءِ عاجزي ڪندڙ آھن، ۽ جنھن ظلم (جو بار) کَنيو سو ڇيئي وارو ٿيو.');
INSERT INTO "amroti" VALUES(20,112,'20:112','۽ جيڪي چڱا ڪم ڪندو ۽ اُھو مؤمن (به) ھوندو سو نڪي ظلم کان ۽ نڪي نقصان کان ڊڄندو.');
INSERT INTO "amroti" VALUES(20,113,'20:113','۽ اھڙي طرح اِھو عربي قرآن لاٿوسون ۽ منجھس عذاب جو انجام طرحين طرحين بيان ڪيوسون ته مانَ اُھي ڊڄن يا (اُھو) اُنھن لاءِ نصيحت پيدا ڪري.');
INSERT INTO "amroti" VALUES(20,114,'20:114','پوءِ الله سچو بادشاھ مٿاھون آھي، ۽ قرآن (جي پڙھڻ) ۾ اُن کان اڳ جو توڏانھن ان جو لھڻ پورو ڪجي تڪڙ نه ڪر، ۽ چؤ ته اي منھنجا پالڻھار منھنجو علم وڌاءَ.');
INSERT INTO "amroti" VALUES(20,115,'20:115','۽ بيشڪ آدم کي اڳ حُڪم ڪيوھوسون پوءِ وساريائين ۽ سندس ڪا پڪي نيت نه لڌي سون.');
INSERT INTO "amroti" VALUES(20,116,'20:116','۽ (ياد ڪر) جڏھن ملائڪن کي چيوسون ته آدم کي سجدو ڪريو پوءِ ابليس کانسواءِ سڀني سجدو ڪيو، ۽ اُن انڪار ڪيو.');
INSERT INTO "amroti" VALUES(20,117,'20:117','پوءِ چيوسون ته اي آدم ھيءُ تنھنجو ۽ تنھنجي زال جو ويري آھي متان اوھان کي بھشت مان ڪڍي ته ڏکيا ٿيو.');
INSERT INTO "amroti" VALUES(20,118,'20:118','بيشڪ تولاءِ (ھي آھي ته) منجھس نڪي بُک لڳندءِ نڪي اُگھاڙو ٿيندين.');
INSERT INTO "amroti" VALUES(20,119,'20:119','۽ تون منجھس نڪي اُڃو ٿيندين ۽ نڪي اُس لڳندءِ.');
INSERT INTO "amroti" VALUES(20,120,'20:120','پوءِ کيس شيطان ڌوڪو ڏنو چيائين ته اي آدم سدائين رھڻ جي وڻ جو ۽ سدائين رھڻ واري بادشاھيءَ جو توکي ڏَس ڏيان؟');
INSERT INTO "amroti" VALUES(20,121,'20:121','پوءِ منجھانئس ٻنھي کاڌو تنھنڪري انھن کي سندين اوگھڙ پڌري ٿي ۽ بھشت (جي وڻ) جا پن پاڻ تي ويڙھڻ لڳا، ۽ آدم پنھنجي پالڻھار کان نافرمان ٿيو تنھنڪري ڀُلو.');
INSERT INTO "amroti" VALUES(20,122,'20:122','وري سندس پالڻھار کيس سڳورو ڪيو پوءِ مٿس ٻاجھ سان موٽيو ۽ (کيس) سڌي رستي لاتائين.');
INSERT INTO "amroti" VALUES(20,123,'20:123','چيائين ته منجھانئس ٻئي گڏ لھي وڃو اوھان مان ڪي ڪن جا ويري ٿيندا، پوءِ جڏھن اوھان وٽ منھنجي ھدايت اچي، تڏھن جنھن منھنجي ھدايت جي تابعداري ڪئي سو گمراھ نه ٿيندو ۽ نڪي ڏکيو ٿيندو.');
INSERT INTO "amroti" VALUES(20,124,'20:124','۽ جيڪي منھنجي ياد ڪرڻ کان منھن موڙيندو تنھن لاءِ بيشڪ حياتي اھنجي ٿيندي ۽ قيامت جي ڏينھن کيس انڌو ڪري اٿارينداسون.');
INSERT INTO "amroti" VALUES(20,125,'20:125','چوندو اي منھنجا پالڻھار مون کي انڌو ڪري ڇو اُٿاريئي ۽ بيشڪ (اڳي) سڄو ھوس.');
INSERT INTO "amroti" VALUES(20,126,'20:126','(الله) چوندو ته ائين آھي تو وٽ اسان جو آيتون آيون پوءِ کين وساريئي، ۽ اھڙي طرح اڄ وساربين.');
INSERT INTO "amroti" VALUES(20,127,'20:127','۽ اھڙي طرح انھي کي سزا ڏينداسون جيڪو حد کان لنگھيو ۽ پنھنجي پالڻھار جي نشانين کي نه مڃيائين، ۽ آخرت جو عذاب ڏاڍو سخت ۽ سدائين رھندڙ آھي.');
INSERT INTO "amroti" VALUES(20,128,'20:128','(ھن ڳالھ) کين سڌو رستو نه ڏيکاريو ڇا ته کانئن اڳ ڪيترائي جُڳ ناس ڪياسون جن جائن ۾ گھمندا آھن؟ بيشڪ اُن ۾ ڌيان وارن لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(20,129,'20:129','۽ (اي پيغمبر) جيڪڏھن تنھنجي پالڻھار جو حُڪم آڳاٽو نه ٿئي ھا ۽ (پڻ) مدت مقرر ٿيل نه ھجي ھا ته ضرور (عذاب) لازم ٿئي ھا.');
INSERT INTO "amroti" VALUES(20,130,'20:130','پوءِ جيڪي چوندا آھن تنھن تي صبر ڪر ۽ سج اُڀرڻ کان اڳ ۽ ان جي لھڻ کان اڳ پنھنجي پالڻھار جي پاڪائي ساراھ سان گڏ بيان ڪر، ۽ رات جي (پھرين) گھڙين ۾ ۽ ڪجھ گھڙيون ڏينھن جون پاڪائي بيان ڪر ته مانَ تون راضي ٿئين.');
INSERT INTO "amroti" VALUES(20,131,'20:131','۽ (اي پيغمبر) تون پنھنجي اکين کي انھن ڏانھن نه کول جنھن سان منجھائن (ڪافرن جي) ٽولين کي دنيا جي حياتي جي سينگار سان ھن لاءِ آسودو ڪيوسون ته منجھس کين پرکيون، ۽ تنھنجي پالڻھار جو رزق ڀلو ۽ سدائين رھڻ وارو آھي.');
INSERT INTO "amroti" VALUES(20,132,'20:132','۽ پنھنجي گھر وارن کي نمازجو حڪم ڪر ۽ مٿس صبر ڪر (يعني قائم رھ) توکان رزق نه گھرندا آھيون، اسين توکي رزق ڏيندا آھيون، ۽ چڱي عاقبت پرھيزگارن لاءِ آھي.');
INSERT INTO "amroti" VALUES(20,133,'20:133','۽ چون ٿا ته اسان وٽ پنھنجي پالڻھار جي ڪا نشاني ڇونه آڻيندو آھي؟ جيڪي پھرين صحيفن ۾ آھي تنھن جي نشاني وٽن نه آئي آھي ڇا؟');
INSERT INTO "amroti" VALUES(20,134,'20:134','۽ جيڪڏھن اسين کين ھن (پيغمبر موڪلڻ) کان اڳ عذاب سان ناس ڪريون ھا ته ضرور چون ھا ته اي اسانجا پالڻھار اسان ڏانھن پيغمبر ڇونه موڪليئي ته پنھنجي ذليل ٿيڻ ۽ خوار ھجڻ کان اڳ تنھنجي آيتن جي تابعداري ڪريون ھا.');
INSERT INTO "amroti" VALUES(20,135,'20:135','(اي پيغمبر) چؤ ته سڀڪو منتظر آھي پوءِ (اوھين به) منتظر رھو، پوءِ سگھوئي ڄاڻندو ته سڌي واٽ وارا ڪير آھن ۽ ھدايت وارو ڪير ٿيو؟');
INSERT INTO "amroti" VALUES(21,1,'21:1','ماڻھن لاءِ سندن حساب ويجھو ٿيو آھي ۽ اُھي غفلت ۾ (رھي کانئس) منھن موڙيندڙ آھن.');
INSERT INTO "amroti" VALUES(21,2,'21:2','سندن پالڻھار وٽان ڪا نئين نصيحت وٽن نه ايندي آھي پر اُن کي (ٺـٺوليءَ وانگر راند) کيڏندي ٻڌندا آھن.');
INSERT INTO "amroti" VALUES(21,3,'21:3','سندن دليون وائڙيون آھن، ۽ ظالم ڳجھيون صلاحون ڪندا آھن، ته ھي (پيغمبر) اوھان جھڙو ئي ماڻھو آھي، ۽ اوھين ڏسي وائسي جادوءَ کي ڇو ويجھا ويندا آھيو؟');
INSERT INTO "amroti" VALUES(21,4,'21:4','(پيغمبر) چيو ته منھنجو پالڻھار سڀ ڳالھ ڄاڻندڙ آھي (جا) آسمان ۽ زمين ۾ (جتي ھُجي)، ۽ اُھو ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(21,5,'21:5','بلڪ چيائون ته (اھو قرآن) اجايا خيالات آھن بلڪ اُن کي ٺاھ ٺاھيو اٿس بلڪ اُھو شاعر آھي، پوءِ جڳائي ته اسان وٽ ڪا نشاني آڻي جھڙي طرح پھريان (پيغمبر) موڪليا ويا ھوا.');
INSERT INTO "amroti" VALUES(21,6,'21:6','(ھائو) ڪوبه ڳوٺ جنھن کي اڳ ناس ڪيوسون تنھن (جي رھاڪن پنھنجن پيغمبرن تي) ايمان نه آندو ھو، پوءِ ھيءَ ايمان آڻيندا آھن ڇا؟');
INSERT INTO "amroti" VALUES(21,7,'21:7','۽ (اي پيغمبر) توکان اڳ رڳا مرد موڪلياسون جن ڏانھن وحي ڪيوھوسون (چ‍ؤ ته) جيڪڏھن نه ڄاڻندا آھيو ته ڪتاب وارن کان پڇو.');
INSERT INTO "amroti" VALUES(21,8,'21:8','۽ اُنھن (پيغمبرن) جو جُسو اھڙو نه بڻايوسون جو طعام نه کائين ۽ نه سدائين رھڻ وارا ھوا.');
INSERT INTO "amroti" VALUES(21,9,'21:9','وري ساڻن (پنھنجي) انجام کي سچو ڪيوسون پوءِ اُنھن کي ۽ جنھن لاءِ گھريوسون تنھن کي بچايوسون ۽ حد کان لنگھندڙن کي ناس ڪيوسون.');
INSERT INTO "amroti" VALUES(21,10,'21:10','بيشڪ اوھان ڏانھن ھڪ ڪتاب نازل ڪيوسون جنھن ۾ اوھان لاءِ نصيحت آھي، پوءِ ڇونه سمجھندا آھيو؟');
INSERT INTO "amroti" VALUES(21,11,'21:11','۽ ڪيترائي ڳوٺ جو اُھي ظالم ھوا ويران ڪياسون ۽ کانئن پوءِ ٻيو قومون پيدا ڪيون سون.');
INSERT INTO "amroti" VALUES(21,12,'21:12','پوءِ جنھن دم اسان جو عذاب سھي ڪيائون (تنھن دم) اُمالڪ اُتاھون اُٿي ڀڳا ٿي.');
INSERT INTO "amroti" VALUES(21,13,'21:13','(چيوسون ته) نه ڀڄو ۽ جن (نعمتن) ۾ آسودا ھُيؤ اُنھن ڏانھن ۽ پنھنجن گھرن ڏانھن موٽو ته مانَ اوھان کان پڇيو وڃي.');
INSERT INTO "amroti" VALUES(21,14,'21:14','چوندا رھيا ته اسان لاءِ خرابي آھي بيشڪ اسين ظالم ھئاسون.');
INSERT INTO "amroti" VALUES(21,15,'21:15','پوءِ سدائين سندن اھو پڪارڻ ھو تانجو کين لڻيل (۽) وسانيل ڪيوسون.');
INSERT INTO "amroti" VALUES(21,16,'21:16','۽ آسمان ۽ زمين کي ۽ جيڪي سندن وچ ۾ آھي سا ڪا اسان راند نه بڻائي آھي.');
INSERT INTO "amroti" VALUES(21,17,'21:17','جيڪڏھن اسان کي راند بڻائڻ (يعني ٻارن ٻچن) جو ڪو ارادو ھجي ھا ته ضرور اُنکي پاڻ وٽان وٺون ھا (يعني پاڻ جھڙا ڪريون ھا)، جيڪڏھن اسين ڪندڙ ھجون ھا (پر اسين ائين ڪندڙ نه آھيون).');
INSERT INTO "amroti" VALUES(21,18,'21:18','بلڪ سچ کي ڪُوڙ تي اُڇليندا آھيون پوءِ اُھو سندن مٿو ڀڃندو آھي پوءِ اُنھيءَ مھل اُھو (ڪوڙ) چٽ ٿيندڙ آھي ۽ جيڪي (الله جون) وصفون اوھين بيان ڪندا آھيو تنھن سببان اوھان لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(21,19,'21:19','۽ جيڪي آسمانن ۽ زمين ۾ آھي سو الله جو آھي، ۽ جيڪي وٽس آھن سي سندس عبادت کان وڏائي نه ڪندا آھن ۽ نڪي ٿڪبا آھن.');
INSERT INTO "amroti" VALUES(21,20,'21:20','(اُھي) رات جو ۽ ڏينھن جو پاڪائي بيان ڪندا آھن سُست نه ٿيندا آھن.');
INSERT INTO "amroti" VALUES(21,21,'21:21','زمين (جي شين) مان اھڙا معبود ورتا اٿن ڇا جو اُھي (مئي کانپوءِ کين) کڙو ڪندا؟');
INSERT INTO "amroti" VALUES(21,22,'21:22','جيڪڏھن آسمان ۽ زمين ۾ الله کانسواءِ (گھڻا) معبود ھجن ھا ته (آسمان ۽ زمين) ٻئي اُجڙن ھا، پوءِ ڪافر جيڪو بيان ڪندا آھن تنھن (ڳالھ) کان عرش جو پالڻھار پاڪ آھي.');
INSERT INTO "amroti" VALUES(21,23,'21:23','(الله) جيڪي ڪندو آھي تنھن بابت (اُھو) نه پڇبو ۽ اُنھن کان پڇا ڪبي.');
INSERT INTO "amroti" VALUES(21,24,'21:24','اُن کانسواءِ (ٻيا) معبود ڇا ورتائون؟ (اي پيغمبر کين) چؤ ته پنھنجو دليل آڻيو، ھي (قرآن) منھنجي سنگت جي نصيحت آھي ۽ (ھي) مون کان اڳ وارن جي نصيحت آھي (يعني توريت ۽ انجيل انھن مان دليل ڏيو) بلڪ انھن مان گھڻا حق کي نه ڄاڻندا آھن اُن ڪري اُھي مُنھن موڙيندڙ آھن.');
INSERT INTO "amroti" VALUES(21,25,'21:25','۽ توکان اڳ جيڪو (به) پيغمبر موڪليوسون تنھن ڏانھن ھي ئي وحي ڪيوسون ته مون کانسواءِ ڪو عبادت جو لائق نه آھي تنھنڪري منھنجي عبادت ڪريو.');
INSERT INTO "amroti" VALUES(21,26,'21:26','۽ چون ٿا ته الله (پنھنجي لاءِ) اولاد ورتو آھي اُھو پاڪ آھي، بلڪ (ملائڪ الله جا) سڳورا ٻانھا آھن.');
INSERT INTO "amroti" VALUES(21,27,'21:27','ڳالھائڻ ۾ اُن کان آڳاٽائي نه ڪندا آھن ۽ سندس حُڪم موجب ڪم ڪندا آھن.');
INSERT INTO "amroti" VALUES(21,28,'21:28','جيڪي سندن اڳتان ۽ جيڪي سندن پوئتان آھي سو (الله) ڄاڻندو آھي ۽ (الله) جنھن کان راضي ٿئي تنھن کان سواءِ ٻئي لاءِ سفارش نه ڪندا آھن ۽ اُھي سندس ڀَوَ کان ڊڄندڙ آھن.');
INSERT INTO "amroti" VALUES(21,29,'21:29','۽ منجھائن جيڪو چوي ته آءٌ الله کان سواءِ معبود آھيان تنھن کي دوزخ جي سزا ڏينداسون، ظالمن کي اھڙي ئي سزا ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(21,30,'21:30','ڪافرن نه ڏٺو آھي ڇا ته آسمان ۽ زمين بند ٿيل ھوا؟ پوءِ انھن کي کوليوسون، ۽ سڀڪنھن جيئري شيء کي پاڻيءَ مان پيدا ڪيوسون، پوءِ ڇونه ايمان آڻيندا آھن؟');
INSERT INTO "amroti" VALUES(21,31,'21:31','۽ زمين ۾ جبل پيدا ڪياسون ته متان کين ڌوڏي ۽ منجھس ويڪريون واٽون بڻايوسون ته مانَ اُھي واٽ لھن.');
INSERT INTO "amroti" VALUES(21,32,'21:32','۽ آسمان کي مُحڪم ڇت بڻائي سون، ۽ اُھي اُن جي نشانين کان مُنھن موڙيندڙ آھن.');
INSERT INTO "amroti" VALUES(21,33,'21:33','۽ (الله) اُھو آھي جنھن رات ۽ ڏينھن ۽ سج ۽ چنڊ بڻايو، سڀڪو (تارو پنھنجي) آسمان ۾ ترندو آھي.');
INSERT INTO "amroti" VALUES(21,34,'21:34','۽ توکان اڳ ڪنھن ماڻھوءَ کي سدائين رھڻ وارو نه ڪيوسون، جيڪڏھن تون مُئين ته اُھي سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(21,35,'21:35','سڀڪو جيءُ موت (جو مزو) چکڻ وارو آھي، ۽ اوھان کي مدائي ۽ چڱائي سان جاچڻ طرح پرکيندا آھيون، ۽ اسان ڏانھن موٽائبؤ.');
INSERT INTO "amroti" VALUES(21,36,'21:36','۽ جڏھن ڪافر توکي ڏسندا آھن تڏھن توکي رڳو ٺـٺولي ڪري وٺندا آھن، (۽ چوندا آھن) ته ھيءُ اُھو آھي ڇا جيڪو اوھان جي معبودن (جي عيبن) جو ذڪر ڪندو آھي، ۽ اُھي الله ياد ڪرڻ کان انڪار ڪندا آھن.');
INSERT INTO "amroti" VALUES(21,37,'21:37','ماڻھو اُٻھر مان بڻايو ويو آھي، سگھو پنھنجون نشانيون اوھان کي ڏيکاريندس پوءِ مون کان عذاب جلد نه گھرو.');
INSERT INTO "amroti" VALUES(21,38,'21:38','۽ چون ٿا ته جيڪڏھن اوھين سچا آھيو ته اُھو انجام ڪڏھن ٿيندو؟');
INSERT INTO "amroti" VALUES(21,39,'21:39','جيڪڏھن ڪافر اُن وقت کي ڄاڻن ھا جنھن مھل نڪي پنھنجن منھن ۽ نڪي پنھنجين پٺين کان باھ کي جھلي سگھندا ۽ نڪي کين مدد ڏبي (ته ائين نه چون ھا).');
INSERT INTO "amroti" VALUES(21,40,'21:40','بلڪ (اھا گھڙي) وٽن اوچتو ايندي پوءِ کين حيران ڪندي پوءِ نڪي اُھا ٽاري سگھندا ۽ نڪي کين مُھلت ڏبي.');
INSERT INTO "amroti" VALUES(21,41,'21:41','۽ بيشڪ توکان اڳ پيغمبرن سان ٺـٺوليون ڪيون ويون پوءِ منجھائن جن ٺـٺوليون ڪيون تن تي اُھو عذاب اُلٽيو جنھن سان ٺـٺوليون ڪندا ھوا.');
INSERT INTO "amroti" VALUES(21,42,'21:42','(اي پيغمبر کين) چؤ ته رات ۽ ڏينھن ۾ ٻاجھاري (الله جي عذاب) کان ڪير اوھان جي نگھباني ڪندو آھي، بلڪ اُھي، پنھنجي پالڻھار جي ياد ڪرڻ کان منھن موڙيندا آھن.');
INSERT INTO "amroti" VALUES(21,43,'21:43','اسان کانسواءِ (بچائڻ وارا) ڪي معبود آھن ڇا جو انھن کي بچائيندا؟ اُھي پاڻ کي به مدد ڏيئي نه سگھندا آھن ۽ نڪي اُھي (ھڪ ٻئي جي مدد سان) اسان (جي عذاب) کان بچندا.');
INSERT INTO "amroti" VALUES(21,44,'21:44','بلڪ کين ۽ پيءُ ڏاڏن کي آسودو ڪيوسون تان جو سندن ڄمار ڊگھي ٿي، پوءِ نه ٿا ڏسن ڇا ته (ڪافرن جي) مُلڪ کي اُن جي چوڌاريون (مسلمانن جي غلبي سان) گھٽائيندا آھيون؟ پوءِ اُھي غالب ٿيندا ڇا؟');
INSERT INTO "amroti" VALUES(21,45,'21:45','(اي پيغمبر کين) چؤ ته آءٌ اوھان کي وحي سان ئي ڊيڄاريندو آھيان، ۽ جڏھن (ماڻھو) ڊيڄاريا ويندا آھن تڏھن ٻوڙو (ڊيڄارڻ جو) سڏ نه ٻڌندو آھي.');
INSERT INTO "amroti" VALUES(21,46,'21:46','۽ جيڪڏھن تنھنجي پالڻھار جي عذاب مان ٿوروئي کين پھچي ته ضرور چون ته ھيء ارمان اسين ظالم ھواسون.');
INSERT INTO "amroti" VALUES(21,47,'21:47','۽ قيامت جي ڏينھن عدل جي ترازي رکندسون پوءِ ڪنھن جيءُ تي ڪجھ ظلم نه ڪبو، ۽ جيڪڏھن عمل اَھر جي داڻي جي تور ھوندو ته به اُن کي آڻينداسون، ۽ اسين حساب ڪندڙ بس آھيون.');
INSERT INTO "amroti" VALUES(21,48,'21:48','۽ بيشڪ مُوسىٰ ۽ ھارون کي نبيريندڙ ڪتاب ۽ انھن پرھيزگارن لاءِ سوجھرو ۽ نصيحت ڏني سون.');
INSERT INTO "amroti" VALUES(21,49,'21:49','جيڪي پنھنجي پالڻھار کان پرپٺ ڊڄندا آھن ۽ اُھي قيامت کان (پڻ) ڊڄندڙ آھن.');
INSERT INTO "amroti" VALUES(21,50,'21:50','۽ ھي (قرآن) ھڪ برڪت واري نصيحت آھي جنھن کي نازل ڪيوسون، پوءِ به اوھين ان جا منڪر ٿيندا آھيو ڇا؟');
INSERT INTO "amroti" VALUES(21,51,'21:51','۽ بيشڪ ابراھيم کي اڳي سندس ھدايت ڏني سون ۽ کيس ڄاڻندڙ ھواسين.');
INSERT INTO "amroti" VALUES(21,52,'21:52','جڏھن پنھنجي پيءُ ۽ پنھنجي قوم کي چيائين ته ھي ڪھڙيون مورتون آھن جن تي اوھين ويھي عبادت ٿا ڪريو.');
INSERT INTO "amroti" VALUES(21,53,'21:53','چيائين ته پنھنجا ابا ڏاڏا اُنھن جا پوڄيندڙ ڏٺاسون.');
INSERT INTO "amroti" VALUES(21,54,'21:54','(ابراھيم) چيو ته بيشڪ اوھين پاڻ ۽ اوھان جا ابا ڏاڏا پڌريءَ گمراھيءَ ۾ آھيو.');
INSERT INTO "amroti" VALUES(21,55,'21:55','چيائون اسان وٽ سچ آندو اٿئي يا تون راند ڪندڙن مان آھين؟');
INSERT INTO "amroti" VALUES(21,56,'21:56','(ابراھيم) چيو ته بلڪ اوھان جو پالڻھار اُھوئي آسمانن ۽ زمين جو پالڻھار آھي جنھن اُھي بڻايا آھن، ۽ آءٌ اُن (ڳالھ) تي شاھدي ڏيندڙن مان آھيان.');
INSERT INTO "amroti" VALUES(21,57,'21:57','۽ الله جو قسم آھي ته اوھان جي پٺيري ٿي ڦرڻ کانپوءِ اوھان جي بتن جو ضرور ڪو حيلو ڪندس.');
INSERT INTO "amroti" VALUES(21,58,'21:58','پوءِ اُنھن کي سندن وڏي کانسواءِ ٽُڪر ٽُڪر ڪيائين ته مانَ اھي ڏانھس موٽن.');
INSERT INTO "amroti" VALUES(21,59,'21:59','چيائون ته اسان جي بُتن سان ڪنھن ھھڙو حال ڪيو؟ بيشڪ اُھو ظالمن مان آھي.');
INSERT INTO "amroti" VALUES(21,60,'21:60','چيائون ته ھڪ جوان کان ٻڌوسون ته سندين گلا ٿي ڪيائين اُن کي ابراھيم سڏيندا آھن.');
INSERT INTO "amroti" VALUES(21,61,'21:61','چيائون ته کيس ماڻھن جي اکين اڳيان آڻيو ته مانَ اُھي گواھي ڏين.');
INSERT INTO "amroti" VALUES(21,62,'21:62','(جڏھن آندائون تڏھن) چيائون ته اي ابراھيم تو اسان جي بُتن جو ھي حال ڪيو آھي ڇا؟');
INSERT INTO "amroti" VALUES(21,63,'21:63','(ابراھيم) چيو ته (نه)! بلڪ اھو سندن ھن وڏي ڪيو آھي جيڪڏھن ڳالھائن ٿا ته کانئن پڇو.');
INSERT INTO "amroti" VALUES(21,64,'21:64','پوءِ پاڻ ڏانھن (سوچيندڙ ٿي) موٽيا پوءِ (ھڪ ٻئي کي) چيائون ته بيشڪ اوھين پاڻ بي انصاف آھيو.');
INSERT INTO "amroti" VALUES(21,65,'21:65','پوءِ پنھنجا ڪنڌ اُونڌا ڪيائون، (چيائون) ته بيشڪ ڄاڻندو آھين ته ھي نه ڳالھائيندا آھن.');
INSERT INTO "amroti" VALUES(21,66,'21:66','(ابراھيم) چيو ته الله کانسواءِ انھن کي پوڄيندا آھيو ڇا جيڪي اوھان کي نڪي نفعو پھچائيندا آھن ۽ نڪي اوھان کي نقصان پھچائيندا آھن.');
INSERT INTO "amroti" VALUES(21,67,'21:67','اوھان کي ۽ جنھن کي الله کانسواءِ پوڄيندا آھيو تنھن کي ڌِڪار ھُجي، (اوھين) نه ڄاڻندا آھيو ڇا؟');
INSERT INTO "amroti" VALUES(21,68,'21:68','چيائون ته اُن کي ساڙيو ۽ جيڪڏھن اوھين ڪي ڪرڻ گھرو ٿا ته پنھنجن بُتن جو ڀَلو وٺو.');
INSERT INTO "amroti" VALUES(21,69,'21:69','(پوءِ باھ ۾ وڌائونس) چيوسون ته اي باھ ابراھيم تي ٿڌي ۽ سلامتي واري ٿي پؤ.');
INSERT INTO "amroti" VALUES(21,70,'21:70','۽ کيس ايذائڻ گھريائون تنھنڪري کين بلڪل ڇيئي وارو ڪيوسون.');
INSERT INTO "amroti" VALUES(21,71,'21:71','۽ کيس ۽ لوط کي اھڙي زمين ڏانھن بچائي آندوسون جنھن ۾ جھان وارن لاءِ برڪت ڪئي سون.');
INSERT INTO "amroti" VALUES(21,72,'21:72','۽ ابراھيم کي اسحاق بخشيوسون، ۽ يعقوب (جو سندس پوٽو ھو) وڌيڪ (بخشيوسون)، ۽ سڀني کي صالح ڪيوسون.');
INSERT INTO "amroti" VALUES(21,73,'21:73','۽ اُھي اھڙا امام ڪياسون جو اسان جي حُڪم موجب سڌو رستو ڏيکاريندا ھوا ۽ انھن ڏانھن چڱن ڪمن ڪرڻ ۽ نماز پڙھڻ ۽ زڪوٰة ڏيڻ جو حُڪم ڪيوسون، ۽ اسان جي عبادت ڪندڙ ھوا.');
INSERT INTO "amroti" VALUES(21,74,'21:74','۽ لُوط کي حڪمت ۽ علم ڏنوسون ۽ کيس اھڙي ڳوٺ وارن کان بچايوسون جو مدا ڪم ڪندا ھوا، ڇوته اُھي بڇڙي نافرمان قوم ھوا.');
INSERT INTO "amroti" VALUES(21,75,'21:75','۽ کيس پنھنجي ٻاجھ ھيٺ داخل ڪيوسون، بيشڪ اُھو صالحن مان ھو.');
INSERT INTO "amroti" VALUES(21,76,'21:76','۽ (اي پيغمبر) نوح (جي قصي) کي (ياد ڪر) جڏھن (اُنھن پيغمبرن کان) اڳ دُعا گھريائين تڏھن سندس دُعا قبول ڪئي سون پوءِ کيس ۽ سندس گھر وارن کي وڏي اوکائيءَ کان بچايو سون.');
INSERT INTO "amroti" VALUES(21,77,'21:77','۽ کيس اھڙي قوم تي سوڀارو ڪيوسون جنھن اسان جي آيتن کي ڪوڙ ڀانيو، بيشڪ اُھي بڇڙي قوم ھوا تنھنڪري انھن مڙني کي ٻوڙيوسون.');
INSERT INTO "amroti" VALUES(21,78,'21:78','۽ دائود ۽ سليمان (جي قصي) کي (ياد ڪر) جڏھن پوک بابت فيصلو ڪيائون جو ھڪ قوم جي ڌڻ رات جو منجھس ڀيل ڪئي ھئي، ۽ سندن حُڪم ڪرڻ وقت حاضر ھواسون.');
INSERT INTO "amroti" VALUES(21,79,'21:79','تڏھن اُھو سليمان کي سمجھايوسون، ۽ ھر ھڪ کي حڪمت ۽ علم ڏنوسون، ۽ دائود کي جبل ۽ پکي تابع ڪري ڏناسون جو (ساڻس) تسبيح چوندا ھوا، ۽ (اسين ايئن) ڪرڻ وارا ھواسون.');
INSERT INTO "amroti" VALUES(21,80,'21:80','۽ دائود کي اوھان لاءِ زرھ بڻائڻ ھن لاءِ سيکاري سون ته اوھان کي اوھان جي لڙائي (جي نقصان) کان بچائي، پوءِ اوھين ڪي شڪر ڪندڙ آھيو ڇا؟');
INSERT INTO "amroti" VALUES(21,81,'21:81','۽ سُليمان کي تِکو واءُ (تابع ڪري ڏنوسون) جو سندس حُڪم سان اھڙي زمين ڏانھن ھلندو ھو جنھن ۾ برڪت رکي سون، ۽ سڀڪنھن شيء کي ڄاڻندڙ آھيون.');
INSERT INTO "amroti" VALUES(21,82,'21:82','۽ ديوَن مان ڪي اھڙا (تابع ڪري ڏناسون) جي اُن لاءِ ٽٻندا ھوا ۽ اُن کانسواءِ ٻيو ڪم (به) ڪندا ھوا، ۽ سندن نگھبان ھواسون.');
INSERT INTO "amroti" VALUES(21,83,'21:83','۽ (اي پيغمبر) ايوب (جي قصي) کي (ياد ڪر) جڏھن پنھنجي پالڻھار کي سڏيائين ته مون کي ڏُک پھتو آھي ۽ تون (سڀني) کان تمام ٻاجھارو آھين.');
INSERT INTO "amroti" VALUES(21,84,'21:84','پوءِ سندس دُعا قبول ڪئي سون پوءِ جيڪو کيس ڏک پھتو ھو سو لاٿوسون ۽ سندس گھر وارا کيس (ورائي) ڏناسون ۽ اُنھن جھڙا ساڻن گڏ پنھنجي ٻاجھ وٽان (ڏناسون) ۽ (ته) عبادت ڪندڙن لاءِ يادگيري ھجي.');
INSERT INTO "amroti" VALUES(21,85,'21:85','۽ اسماعيل ۽ ادريس ۽ ذوالڪفل کي (ياد ڪر)، ھر ھڪ صابرن مان ھو.');
INSERT INTO "amroti" VALUES(21,86,'21:86','۽ کين پنھنجي ٻاجھ ۾ داخل ڪيوسون، بيشڪ اُھي صالحن مان ھوا.');
INSERT INTO "amroti" VALUES(21,87,'21:87','۽ مڇي واري (جي قصي) کي (ياد ڪر) جڏھن ھو (پنھنجي قوم) تي ڪاوڙجي ويو پوءِ (ذوالنون ائين) ڀانيو ته اسان جي مٿس ڪڏھن پڪڙ نه ھلندي پوءِ اونداھين ۾ دانھون ڪيائين ته توکان سواءِ ڪو معبود نه آھي تون پاڪ آھين، آءٌ گنھگارن مان آھيان.');
INSERT INTO "amroti" VALUES(21,88,'21:88','پوءِ سندس دُعا قبول ڪئي سون ۽ کيس غم کان بچايوسون، ۽ اھڙي طرح مؤمنن کي بچائيندا آھيون.');
INSERT INTO "amroti" VALUES(21,89,'21:89','۽ زڪريا (جو قصّو ياد ڪر) جڏھن پنھنجي پالڻھار کي سڏيائين ته اي منھنجا پالڻھار مون کي ھيڪلو نه ڇڏ ۽ تون چڱو وارث آھين.');
INSERT INTO "amroti" VALUES(21,90,'21:90','پوءِ سندس دُعا قبول ڪئي سون ۽ کيس يحيىٰ بخشيوسون ۽ سندس زال کي سندس لاءِ چڱو ڪيوسون، اُھي چڱاين ۾ آڳاٽائي ڪندا ھوا ۽ اسان کي اُميد ۽ ڀؤ ڪري سڏيندا ھوا، ۽ اسان کان ڊڄندڙ ھوا.');
INSERT INTO "amroti" VALUES(21,91,'21:91','۽ (انھيءَ زال جوقصّو ياد ڪر) جنھن پنھنجي اُگھڙ بچائي پوءِ منجھس پنھنجو روح ڦوڪيوسون ۽ کيس ۽ سندس پٽ کي ساري جڳ لاءِ نشاني ڪئي سون.');
INSERT INTO "amroti" VALUES(21,92,'21:92','بيشڪ اِھو اوھان جو دين ھڪ دين آھي، ۽ آءٌ اوھان جو پالڻھار آھيان تنھنڪري منھنجي عبادت ڪريو.');
INSERT INTO "amroti" VALUES(21,93,'21:93','۽ پنھنجي (دين جي) ڪمن کي پاڻ ۾ ٽُڪر ٽُڪر ڪيائون، سڀئي مون ڏانھن موٽندڙ آھن.');
INSERT INTO "amroti" VALUES(21,94,'21:94','پوءِ جيڪو چڱا ڪم ڪندو ۽ اُھو مؤمن به ھوندو تنھنجي ڪوشش جي بي قدري نه ڪبي، ۽ اسين اُن لاءِ لکندڙ آھيون.');
INSERT INTO "amroti" VALUES(21,95,'21:95','۽ جنھن ڳوٺ کي ناس ڪيوسون تنھن تي حرام ٿيل آھي ته وري موٽي ايندا اُھي.');
INSERT INTO "amroti" VALUES(21,96,'21:96','تان جو جڏھن يَاجُوج ۽ مَاجُوج کي کولبو ۽ اُھي سڀڪنھن دڙي کان ڊوڙندا.');
INSERT INTO "amroti" VALUES(21,97,'21:97','۽ (جڏھن) سچو وعدو پھچندو تڏھن اوچتو ئي ڪافرن جو اکيون ٽڙي وينديون، (چوندا ته) ھَيءِ اَرمان بيشڪ ھن (حال) کان بي خبريءَ ۾ ھواسون بلڪ اسين ئي ظالم ھواسون.');
INSERT INTO "amroti" VALUES(21,98,'21:98','اوھين ۽ جن کي الله کانسواءِ پوڄيندا آھيو سي دوزخ جو ٻارڻ آھيو، اُن ۾ اوھين گھڙندڙ آھيو.');
INSERT INTO "amroti" VALUES(21,99,'21:99','جيڪڏھن اُھي (بُت) معبود ھجن ھا ته اُن (دوزخ) ۾ نه گھڙن ھا، ۽ مڙيئي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(21,100,'21:100','منجھس انھن کي رڙيون ھونديون ۽ اُھي منجھس (ڪي) نه ٻڌندا.');
INSERT INTO "amroti" VALUES(21,101,'21:101','جن لاءِ اڳي ئي اسان وٽ چڱائي (لکيل) آھي اُھي اُن (دوزخ) کان پري ڪبا.');
INSERT INTO "amroti" VALUES(21,102,'21:102','(اُھي) اُن جي ڀڻڪ نه ٻڌندا، ۽ جنھن (عيش) ۾ سندن جيءُ (رھڻ) گھرندو تنھن ۾ اُھي سدائين رھندا.');
INSERT INTO "amroti" VALUES(21,103,'21:103','کين تمام وڏو خوف ملول نه ڪندو ۽ کين مَلائڪ گڏبا، (۽ چوندن ته) ھيءُ اُھو ڏينھن آھي جنھن جو اوھان کي وعدو ڏنو ويندو ھو.');
INSERT INTO "amroti" VALUES(21,104,'21:104','جنھن ڏينھن آسمان کي ڪاغذ جي ويڙھيءَ وانگي ويڙھينداسون، جئن پھريون بڻايوسون (تئن) ان کي وري بڻائينداسون، اھو انجام اسان تي لازم آھي، اسين ڪرڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(21,105,'21:105','۽ بيشڪ توريت کان پوءِ زبور ۾ لکيوسون ته منھنجا صالح ٻانھان زمين جا وارث ٿيندا.');
INSERT INTO "amroti" VALUES(21,106,'21:106','ھن (قِصّي) ۾ عبادت ڪندڙ قوم لاءِ (مطلب جي) پورائي آھي.');
INSERT INTO "amroti" VALUES(21,107,'21:107','۽ (اي پيغمبر) توکي خاص جھانن جي رحمت لاءِ موڪليوسون.');
INSERT INTO "amroti" VALUES(21,108,'21:108','چ‍ؤ ته مون وٽ ھن حُڪم کانسواءِ وحي نه ٿيندو آھي ته ھڪ الله کانسواءِ اوھان جو ڪو معبود نه آھي، پوءِ اوھين ڇونه حُڪم مڃيندڙ آھيو؟');
INSERT INTO "amroti" VALUES(21,109,'21:109','پوءِ جيڪڏھن ڦرن ته چؤ ته اوھان سڀني کي (الله جي حڪم تي) ھڪ جھڙو خبردار ڪيم، ۽ جيڪو اوھان کي انجام ڏنو ويو سو نه ٿو ڄاڻان ته ويجھو آھي يا پري آھي.');
INSERT INTO "amroti" VALUES(21,110,'21:110','بيشڪ الله ظاھري ڳالھ (به) ڄاڻندو آھي ۽ جيڪي اوھين لڪائيندا آھيو سو (به) ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(21,111,'21:111','۽ آءٌ نه ڄاڻندو آھيان ته متان اُھا ٺھرايل (دير) اوھان لاءِ پرک ھجي ۽ ھڪ وقت تائين (اوھان لاءِ) نفعو ھُجي.');
INSERT INTO "amroti" VALUES(21,112,'21:112','(پيغمبر) چيو ته اي منھنجا پالڻھار سچو فيصلو ڪر، ۽ اسان جو پالڻھار ٻاجھارو آھي جيڪي اوھين بيان ڪندو آھي تنھن تي کانئس مدد گھري ويندي آھي.');
INSERT INTO "amroti" VALUES(22,1,'22:1','اي ماڻھؤ پنھنجي پالڻھار کان ڊڄو، ڇوته قيامت وارو ڌڪاءُ وڏي شيء آھي.');
INSERT INTO "amroti" VALUES(22,2,'22:2','جنھن ڏينھن اُن کي ڏسندؤ (تنھن ڏينھن) سڀڪا کير پياريندڙ جنھن کي کير پياريندي ھوندي تنھن کي (دھشت کان) وساريندي ۽ سڀڪا ڳڀ واري پنھنجي ڳڀ کي ڪيريندي ۽ ماڻھن کي بيھوش ڏسندين حالانڪ اُھي بيھوش نه ھوندا پر الله جو عذاب سخت آھي.');
INSERT INTO "amroti" VALUES(22,3,'22:3','۽ ماڻھن مان ڪو اھڙو آھي جو الله جي شان ۾ بي سمجھيءَ سان تڪرار ڪندو آھي ۽ سڀڪنھن شيطان تڙيل جي پٺيان لڳندو آھي.');
INSERT INTO "amroti" VALUES(22,4,'22:4','اُن (شيطان) تي لازم ڪيو ويو آھي ته جيڪو کيس دوست رکندو تنھن کي بيشڪ اُھو گمراھ ڪندو ۽ ان کي دوزخ جي عذاب ڏانھن سڌو رستو ڏيکاريندو.');
INSERT INTO "amroti" VALUES(22,5,'22:5','اي ماڻھؤ جيڪڏھن وري جيئري ٿئڻ کان اوھين شڪ ۾ پيل آھيو ته اسان اوھان کي مٽيءَ مان بڻايو وري نُطفي مان وري رت جي دڳ مان وري گوشت جي ٻوٽيءَ مان پورو بڻايل ۽ اڌ گابرو بڻايل ھن لاءِ ته اوھان کي کولي ٻڌايون، ۽ جنھن کي گھرندا آھيون تنھن کي مُقرر مُدّت تائين ڳڀيرڻين ۾ ٺھرائيندا آھيون وري ٻار بڻائي اوھان کي ٻاھر ڪڍندا آھيون وري نپائيندا آھيون ته اوھين پنھنجي جواني کي پھچو، ۽ اوھان مان ڪو اڳي مرندو آھي ۽ اوھان مان ڪنھن کي (جھورائي جي) نڪميءَ ڄمار ڏانھن ھن لاءِ ورايو ويندو آھي ته ڄاڻپ کانپوءِ ڪجھ نه ڄاڻي، ۽ زمين کي غير آباد ڏسندو آھين پوءِ جڏھن مٿس پاڻي وسائيندا آھيون تڏھن تازي ٿيندي ۽ اُڀامندي آھي ۽ سڀڪنھن جنس مان چڱا اوڀڙ ڄمائيندي آھي.');
INSERT INTO "amroti" VALUES(22,6,'22:6','اِھو (بيان) ھن لاءِ آھي جو الله (جو ئي ھجڻ) حق آھي جو اُھو مُئن کي جياريندو آھي ۽ اُھو سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(22,7,'22:7','۽ ته قيامت اچڻي آھي منجھس ڪو شڪ نه آھي ۽ ته جيڪي قبرن ۾ ھوندا تن کي الله اُٿاريندو.');
INSERT INTO "amroti" VALUES(22,8,'22:8','۽ ماڻھن مان ڪو اھڙو آھي جو الله جي شان ۽ ڄاڻپ ۽ ھدايت ۽ پڌري ڪتاب ڌاران تڪرار ڪندو آھي.');
INSERT INTO "amroti" VALUES(22,9,'22:9','(وڏائيءَ کان) ڪنڌ موڙيندڙ ٿي ھن لاءِ ته (ماڻھن کي) الله جي واٽ کان گمراھ ڪري، ان لاءِ دنيا ۾ خواري آھي ۽ قيامت جي ڏينھن کيس سڙڻ جو عذاب چکائينداسون.');
INSERT INTO "amroti" VALUES(22,10,'22:10','(چئبس ته) اھا سزا انھن ڪمن جي ڪري آھي جو تنھنجي ھٿن اڳي موڪليا آھن ۽ (انھيءَ ڪري) ته الله ٻانھن تي ظلم ڪندڙ نه آھي.');
INSERT INTO "amroti" VALUES(22,11,'22:11','۽ ماڻھن مان ڪو اھڙو آھي جو الله جي عبادت آسيري پاسيري ڪندو آھي، پوءِ جيڪڏھن کيس ڪو نفعو پھچندو آھي ته اُن (عبادت) سان آرامي ٿيندو آھي، ۽ جيڪڏھن کيس ڪو ڏک پھچندو آھي ته پنھنجي مُنھن ڀر ڦرندو آھي، دُنيا ۽ آخرت گنوايائين، اھوئي پڌرو ڇيئو آھي.');
INSERT INTO "amroti" VALUES(22,12,'22:12','(اُھو) الله کانسواءِ اُنھي کي سڏيندو آھي جيڪو کيس نڪي نقصان لائيندو آھي ۽ نڪي کيس نفعو ڏيندو آھي، اِھا ئي وڏي گمراھي آھي.');
INSERT INTO "amroti" VALUES(22,13,'22:13','انھيءَ کي سڏيندو آھي جنھن جو نقصان سندس نفعي کان وڌيڪ ويجھو آھي، بيشڪ (اھڙو) سائين بڇڙو آھي ۽ (اھڙو) ھمراھ (به) بڇڙو آھي.');
INSERT INTO "amroti" VALUES(22,14,'22:14','بيشڪ جن ايمان آندو ۽ چڱا عمل ڪيا تن کي الله اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن، بيشڪ الله جيڪي گھرندو آھي سو ڪندو آھي.');
INSERT INTO "amroti" VALUES(22,15,'22:15','جيڪو ڀائيندو آھي ته الله پيغمبر کي دنيا ۽ آخرت ۾ ڪڏھن مدد نه ڪندو تنھن کي گھرجي ته آسمان ڏانھن رسيءَ سان چڙھي وري گھرجيس ته ڇني پوءِ نھاري ته اُنھي جي حيلي اُن کي وڃايو (يا نه) جنھن اُن کي ڪاوڙايو.');
INSERT INTO "amroti" VALUES(22,16,'22:16','۽ اھڙي طرح اُن (قرآن) کي پڌريون آيتون ڪري نازل ڪيوسون ۽ الله جنھن کي گھرندو آھي تنھن کي ھدايت ڪندو آھي.');
INSERT INTO "amroti" VALUES(22,17,'22:17','بيشڪ جن ايمان آندو ۽ جيڪي يھودي ۽ صابي ۽ نصارىٰ ۽ مجوسي ۽ مشرڪ آھن تن (سڀني) جي وچ ۾ الله قيامت جي ڏينھن نبيرو ڪندو، ڇوته الله سڀڪنھن شيء تي حاضر آھي.');
INSERT INTO "amroti" VALUES(22,18,'22:18','(اي پيغمبر) نه ڏٺئي ڇا ته جيڪي آسمانن ۾ آھن ۽ جيڪي زمين ۾ آھن سي ۽ سج ۽ چنڊ ۽ تارا ۽ جبل ۽ وڻ ۽ ڍور ۽ ماڻھن مان گھڻا الله کي سجدو ڪندا آھن، ۽ گھڻا (ماڻھو) آھن جن تي عذاب لازم ٿيو آھي، ۽ جنھن کي الله خوار ڪري تنھن کي ڪو مانُ ڏيڻ وارو ڪونھي، ڇو ته الله جيڪي گھرندو آھي سو ڪندو آھي.');
INSERT INTO "amroti" VALUES(22,19,'22:19','اِھي ٻئي ٽوليون پاڻ ۾ ويري آھن پنھنجي پالڻھار بابت تڪرار ڪندڙ آھن، پوءِ جن ڪفر ڪيو تن لاءِ باھ جا ڪپڙا ورڇيا ويندا، (۽) سندن مٿن جي مٿان ٽھڪندڙ پاڻي ھاربو.');
INSERT INTO "amroti" VALUES(22,20,'22:20','جيڪي سندن پيٽن ۾ ھوندو سو ۽ کَلون اُن سان ڳاربيون.');
INSERT INTO "amroti" VALUES(22,21,'22:21','۽ اُنھن جي ڪُٽڻ لاءِ لوھ جا وڏاڻ ھوندا.');
INSERT INTO "amroti" VALUES(22,22,'22:22','جڏھن ڪڏھن منجھانئس ڏک جي ڪري نڪرڻ گھُرندا تڏھن منجھس ورائبا، ۽ (چيو ويندن ته) سڙڻ جو عذاب چکو.');
INSERT INTO "amroti" VALUES(22,23,'22:23','بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي الله اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون پيون وھنديون منجھن سون جا زيور ۽ موتي پارائبا، ۽ منجھن سندين پوشاڪ پَٽَ جي ھوندي.');
INSERT INTO "amroti" VALUES(22,24,'22:24','۽ (دُنيا ۾) کين چڱي ڳالھ ڏانھن رستو ڏيکاريو ويو ھو، ۽ الله ساراھيل جي واٽ ڏانھن رستي لاتا ويا ھوا.');
INSERT INTO "amroti" VALUES(22,25,'22:25','بيشڪ جن ڪفر ڪيو ۽ (ماڻھن کي) الله جي واٽ ۽ اُنھي تعظيم واريءَ مسجد کان جھليندا آھن جا ماڻھن لاءِ بڻائي سون جنھن ۾ شھري ۽ ٻھراڙيءَ وارا ھڪ جھڙا آھن، ۽ جيڪو منجھس ظلم سان زيادتيءَ جو ارادو ڪندو تنھن کي ڏکوئيندڙ عذاب چکائينداسون.');
INSERT INTO "amroti" VALUES(22,26,'22:26','۽ (ياد ڪر) جڏھن ابراھيم لاءِ بَيۡتُ الله جو ھنڌ مقرّر ڪيوسون، (۽ سمجھايوسون) ته مون سان ڪنھن کي شريڪ نه ڪر ۽ منھنجي گھر کي طواف ڪندڙن ۽ بيھندڙن ۽ رڪوع ڪندڙن (۽) سجدي ڪندڙن لاءِ پاڪ رک.');
INSERT INTO "amroti" VALUES(22,27,'22:27','۽ ماڻھن ۾ حج جو پڙھو ڏي ته توڏانھن پيادا ۽ ڏٻرن اُٺن تي چڙھي ايندا جو ڏورين واٽن کان (ھِن لاءِ) ايندا.');
INSERT INTO "amroti" VALUES(22,28,'22:28','ته پنھنجي فائدي جي ھنڌ تي پھچن ۽ ڍورن مان جيڪا روزي کين الله ڏني تنھن تي ڄاڻايلن ڏينھن ۾ (ذبح مھل) الله جو نالو ياد ڪن، پوءِ انھن منجھان (پاڻ به) کائو ۽ عاجز فقير کي به کارايو.');
INSERT INTO "amroti" VALUES(22,29,'22:29','وري ڀلي ته پنھنجي (جسي جي) مَرُّ لاھين ۽ پنھنجيون مڃتائون پوريون ڪن ۽ ھن قديم گھر جو طواف ڪن.');
INSERT INTO "amroti" VALUES(22,30,'22:30','اِھو (حُڪم) آھي، ۽ جيڪو الله جي عبادت جي رڪنن جو ادب ڪندو تنھن لاءِ اُھو سندن پالڻھار وٽ ڀلو آھي، ۽ جيڪي اوھان کي پڙھي ٻڌائبو تنھن کانسواءِ ٻيا ڍور اوھان تي حلال ڪيا ويا تنھنڪري بُتن جي پليتيءَ کان پاسو ڪريو ۽ ڪوڙي ڳالھ کان (به) پاسو ڪريو.');
INSERT INTO "amroti" VALUES(22,31,'22:31','الله ڏانھن ھڪ طرفا ٿي (۽) ساڻس شريڪ مُقرّر ڪندڙ نه ٿي (پاسو ڪريو)، ۽ جيڪو الله سان شريڪ مقرّر ڪندو سو آسمان کان ڪِريو جنھن کي (مردار خور) پکي کڻي ويا يا کيس واءُ (اُڏائي) پري ھنڌ اُڇليو.');
INSERT INTO "amroti" VALUES(22,32,'22:32','اِھو (حُڪم) آھي، ۽ جيڪو الله جي (عبادت جي) نشانين جو اَدب ڪري (۽ بُتن ۽ ڪوڙ کان پاڻ پَلي سو پرھيزگار آھي) ڇوته اِھو (ڪم) دلين جي پرھيزگاريءَ سان آھي.');
INSERT INTO "amroti" VALUES(22,33,'22:33','اوھان لاءِ انھن (ڍورن) ۾ گھڻا نفعا مقرّر مُدت تائين آھن وري قديم گھر ڏانھن (ڪسڻ لاءِ) پھچڻ جو ھنڌ اٿن.');
INSERT INTO "amroti" VALUES(22,34,'22:34','۽ سڀڪنھن ٽوليءَ لاءِ قرباني جي طرح ھن لاءِ مقرر ڪئي سون ته کين ڍورن مان جيڪي روزي الله ڏني تنھن تي الله جو نالو ياد ڪن، پوءِ اوھان جو معبود ھڪ خُدا آھي تنھنڪري سندس حُڪم مڃيو، ۽ انھن عاجزي ڪندڙن کي خوشخبري ڏي.');
INSERT INTO "amroti" VALUES(22,35,'22:35','جن جون دليون جڏھن الله کي ياد ڪبو آھي (تڏھن) ٿَڙڪنديون آھن ۽ (پڻ انھن ماڻھن کي جو) جيڪي مٿانِ آيو تنھن تي صبر ڪندڙ آھن ۽ نماز پڙھندڙ آھن ۽ کين جيڪا روزي ڏني سون تنھن مان خرچيندا آھن.');
INSERT INTO "amroti" VALUES(22,36,'22:36','۽ قربانيءَ وارن اُٺن کي اوھان لاءِ الله جي عبادت جي نشانين مان ڪيوسون منجھس اوھان لاءِ ڀلائي آھي، پوءِ قطاري مٿن الله جو نالو ياد ڪريو (يعني ذبح ڪريو)، پوءِ جڏھن اُھي پاسي ڀر ليٽن تڏھن منجھائن کائو ۽ (فقيرن) نه سوال ڪندڙ ۽ سوال ڪندڙ کي کارايو، اھڙي طرح اوھان کي اُھي تابع ڪري ڏناسون ته مانَ اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(22,37,'22:37','نڪي اُنھن (قربانين) جو گوشت ۽ نڪي اُنھن جو رتُ الله کي پھچندو آھي پر اوھان جي پرھيزگاري الله کي پھچندي آھي، اھڙي طرح ھو اوھان کي تابع ڪري ڏنائين ته اوھين الله جي وڏائي (ھن شڪراني سببان) بيان ڪريو جو اوھان کي ھدايت ڪيائين، ۽ ڀلارن کي خوشخبري ڏي.');
INSERT INTO "amroti" VALUES(22,38,'22:38','بيشڪ الله مؤمنن کان (سندن ويري) ٽاريندو آھي، ڇوته الله سڀڪنھن خيانت ڪندڙ بي شڪر کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(22,39,'22:39','جن سان (ڪافر) وڙھندا آھن تن کي (جھاد لاءِ) انھيءَ ڪري موڪل ڏني وئي جو انھن تي ظلم ڪيو ويو آھي، ۽ بيشڪ الله کين مدد ڏيڻ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(22,40,'22:40','جن کي پنھنجن ديسن مان ناحق لوڌيو ويو تن (ھن) چوڻ کانسواءِ (ٻيو) نه چيو ته اسانجو پالڻھار الله آھي، ۽ جيڪڏھن ڪن ماڻھن سان ڪن کي الله نه ٽاري ھا ته (جيڪر) خانقاھون ۽ گرجائون ۽ ڪليسا ۽ مسجدون جن ۾ الله جو نالو گھڻو ياد ڪبو آھي سي ضرور ڊاٺيون وڃن ھا، ۽ جيڪو الله (جي دين) جي مدد ڪندو آھي تنھنکي الله ضرور مدد ڏيندو آھي، ڇو ته الله ضرور ڏاڍو ۽ غالب آھي.');
INSERT INTO "amroti" VALUES(22,41,'22:41','انھن ماڻھن کي جو جيڪڏھن کين زمين ۾ غلبو ڏينداسون ته نماز پڙھندا ۽ زڪوٰة ڏيندا ۽ چڱن ڪمن جو حُڪم ڪندا ۽ مدن ڪمن کان منع ڪندا، ۽ سڀني ڪمن جي پڇاڙي الله لاءِ آھي.');
INSERT INTO "amroti" VALUES(22,42,'22:42','۽ (اي پيغمبر) جيڪڏھن توکي ڪوڙو ڀانئين ٿا ته بيشڪ کائن اڳ نوح جي قوم ۽ عادين ثمودين (پيغمبرن کي) ڪوڙ ڄاتو ھو.');
INSERT INTO "amroti" VALUES(22,43,'22:43','۽ ابراھيم جي قوم ۽ لُوط جي قوم (به).');
INSERT INTO "amroti" VALUES(22,44,'22:44','۽ مَدۡيَن وارن (به پنھنجي پيغمبرن کي ڪُوڙو ڄاتو)، ۽ مُوسىٰ کي (به) ڪوڙو ڄاتو ويو پوءِ انھن ڪافرن کي ڍر ڏنم وري کين پڪڙيم، پوءِ منھنجي سزا ڪھڙي (نه سخت) ھُئي.');
INSERT INTO "amroti" VALUES(22,45,'22:45','پوءِ ڪيترائي ڳوٺ آھن جن کي ناس ڪيوسون ۽ اُھي ظالم ھوا پوءِ اُھي پنھنجي ڇتن سميت ڊٺل آھن، ۽ گھڻا کُوھ اُجڙيل ۽ گھڻيون بلند ماڙيون سُڃيون پيون آھن.');
INSERT INTO "amroti" VALUES(22,46,'22:46','پوءِ مُلڪ ۾ نه گھميا آھن ڇا؟ ته کين (اھڙيون) دليون ھجن جن سان اُھي سمجھن يا کين (اھڙا) ڪن ھجن جن سان ٻُڌن، بيشڪ ھي (حالت) آھي ته سندن اکيون انڌيون نه آھن پر دليون انڌيون اٿن جيڪي سندن سينن ۾ آھن.');
INSERT INTO "amroti" VALUES(22,47,'22:47','۽ (اي پيغمبر) توکان عذاب جو جلد اچڻ گھرندا آھن ۽ الله پنھنجو انجام ڪڏھن اُبتڙ نه ڪندو، ۽ تنھنجي پالڻھار وٽ ھڪ ڏينھن اوھان جي ڳاڻيٽي جي ھزار ورھ جي برابر آھي.');
INSERT INTO "amroti" VALUES(22,48,'22:48','۽ گھڻا ڳوٺ آھن جن کي مُھلت ڏنم ۽ اُھي ظالم ھوا وري کين پڪڙيم، ۽ مون ڏانھن موٽڻ آھي.');
INSERT INTO "amroti" VALUES(22,49,'22:49','(اي پيغمبر) چؤ ته اي ماڻھؤ آءٌ اوھان کي رڳو پڌرو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(22,50,'22:50','پوءِ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ بخشش ۽ سڳوري روزي آھي.');
INSERT INTO "amroti" VALUES(22,51,'22:51','۽ جيڪي اسان جي آيتن جي جھڪي ڪرڻ ۾ ڊوڙيا سي دوزخي آھن.');
INSERT INTO "amroti" VALUES(22,52,'22:52','۽ (اي پيغمبر) توکان اڳ نه ڪوئي پيغمبر ۽ نه ڪوئي نبي موڪليوسون پر جڏھن ڪا (انساني) آرزو ڪيائين تڏھن شيطان سندن آرزوءِ ۾ (حرڪت) وجھڻ لڳو، پوءِ جيڪو شيطان وڌو سو الله مٽائيندو رھيو وري الله پنھنجين آيتن کي مُحڪم ڪري ٿو، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(22,53,'22:53','ھن لاءِ ته شيطان جيڪا حرڪت وڌي تنھن سان انھن کي ڌار ڪري پرکي جن جي دلين ۾ بيماري آھي ۽ سندين دليون سخت آھن، ۽ بيشڪ ظالم وڏي ڦيٽي ۾ آھن.');
INSERT INTO "amroti" VALUES(22,54,'22:54','۽ ته جن کي علم ڏنو ويو سي ڄاڻن ته اُھو تنھنجي پالڻھار وٽان سچ آھي پوءِ اُن تي ايمان آڻين ۽ سندن دليون الله لاءِ عاجزي ڪن، ۽ بيشڪ الله مؤمنن کي سڌي واٽ ڏانھن ضرور سڌو رستو ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(22,55,'22:55','۽ ڪافر اُنھيءَ (وحي) کان (ايسين) سدائين شڪ ۾ ھوندا جيسين اوچتو وٽن قيامت پھچي يا ندوري ڏينھن جو عذاب وٽن پھچي.');
INSERT INTO "amroti" VALUES(22,56,'22:56','اُنھيءَ ڏينھن الله جي بادشاھي آھي، (اُھو) سندن وچ ۾ نبيرو ڪندو، پوءِ جن ايمان آندو ۽ چڱا عمل ڪيا سي نعمتن وارن باغن ۾ ھوندا.');
INSERT INTO "amroti" VALUES(22,57,'22:57','۽ جن انڪار ڪيو ۽ اسان جي آيتن کي ڪوڙو ڀانيو سي اُھي آھن جن لاءِ خواريءَ وارو عذاب آھي.');
INSERT INTO "amroti" VALUES(22,58,'22:58','۽ جن الله جي واٽ ۾ ديس ڇڏيو وري ڪُسجي ويا يا مُئا تن کي الله چڱو رِزق ڏيندو، ۽ الله چڱو روزي ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(22,59,'22:59','جو ھنڌ ھنن کي وڻندو تنھن ۾ ضرور کين داخل ڪندو، ۽ بيشڪ الله ضرور ڄاڻندڙ بُردبار آھي.');
INSERT INTO "amroti" VALUES(22,60,'22:60','اِھو (حڪم) آھي، ۽ جيڪو جيترو ايذايو ويو اوترو ايذائي وري مٿس زيادتي ڪئي وڃي ته اُن (ايذايل) کي الله مدد ڏيندو، بيشڪ الله معافي ڏيندڙ بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(22,61,'22:61','اِھا (مدد) ھن سببان آھي ته الله رات کي ڏينھن ۾ وجھندو آھي ۽ ڏينھن کي رات ۾ وجھندو آھي ۽ (ھن ڪري) ته الله ٻڌندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(22,62,'22:62','اِھا (مدد) ھن ڪري آھي ته الله پاڻ برحق آھي ۽ الله کانسواءِ جنھن کي سڏيندا آھن سو ڪوڙو آھي ۽ (ھن ڪري) ته الله ئي مٿاھون وڏو آھي.');
INSERT INTO "amroti" VALUES(22,63,'22:63','(اي پيغمبر) نه ڏٺو اٿئي ڇا ته الله آسمانن مان (مينھن جو) پاڻي وسايو، پوءِ زمين ساول واري ٿيندي آھي، ڇوته الله لُطف ڪندڙ خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(22,64,'22:64','جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله جو آھي، ۽ الله ضروربي پرواھ ساراھيل آھي.');
INSERT INTO "amroti" VALUES(22,65,'22:65','(اي پيغمبر) نه ڏٺو اٿئي ڇا ته جيڪي زمين ۾ آھي سو الله اوھان لاءِ نوايو آھي ۽ ٻيڙيون درياءَ ۾ سندس حُڪم سان تَرنديون آھن، ۽ آسمان کي زمين تي ڪرڻ کان جھليندو آھي پر (جي ڪيرائيس ته) سندس اختيار آھي، بيشڪ الله ماڻھن تي بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(22,66,'22:66','۽ اُھوئي آھي جنھن اوھان کي جياريو، وري اوھان کي ماريندو وري اوھان کي جياريندو، بيشڪ ماڻھو بي شڪر آھي.');
INSERT INTO "amroti" VALUES(22,67,'22:67','سڀڪنھن ٽوليءَ لاءِ ھڪ شريعت مقرر ڪئي سون جنھن تي اُھي عمل ڪندڙ آھن پوءِ جڳائين ته ھِن ڪم ۾ جھڳڙو نه ڪن ۽ (تون) پنھنجي پالڻھار ڏانھن (ماڻھن کي) سڏيندو رھ، بيشڪ تون سڌيءَ واٽ تي آھين.');
INSERT INTO "amroti" VALUES(22,68,'22:68','۽ جيڪڏھن توسان جھڳڙو ڪن ته چؤ جيڪي ڪندا آھيو تنھنکي الله چڱو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(22,69,'22:69','جنھن بابت اوھين تڪرار ڪندا آھيو تنھن بابت قيامت جي ڏينھن الله اوھان جي وچ ۾ نبيرو ڪندو.');
INSERT INTO "amroti" VALUES(22,70,'22:70','(اي پيغمبر) معلوم نه اٿئي ڇا ته جيڪي آسمانن ۽ زمين ۾ آھي سو الله ڄاڻندو آھي؟ بيشڪ اِھا (ڳالھ) ڪتاب (لوح محفوظ) ۾ لکيل آھي، بيشڪ اُھو الله کي سولو آھي.');
INSERT INTO "amroti" VALUES(22,71,'22:71','۽ (ڪافر) الله کانسواءِ اُنھيءَ کي پوڄيندا آھن جنھن لاءِ ڪا سند نه لاٿي اٿس ۽ (پڻ) انھيءَ کي (پوڄيندا آھن) جنھن جي کين ڪا خبر نه آھي، ۽ ظالمن جو ڪو مددگار ڪونه آھي.');
INSERT INTO "amroti" VALUES(22,72,'22:72','۽ جڏھن اسان جيون چٽيون آيتون کين پڙھي ٻڌائبيون آھن تڏھن ڪافرن جي منھن ۾ ناراضپو معلوم ڪندو آھين، جيڪي مٿن اسان جون آيتون پڙھندا آھن تن تي ڪاھي پوڻ لاءِ ويجھا ٿيندا آھن، (اي پيغمبر کين) چؤ ته ھن کان وڌيڪ بڇڙي ڳالھ جي سُڌ اوھان کي ڏيان ڇا؟ (اُھا) باھ آھي جنھن جو الله ڪافرن کي انجام ڏنو آھي، اُھا جاءِ بُڇڙي آھي.');
INSERT INTO "amroti" VALUES(22,73,'22:73','اي ماڻھؤ ھڪ مثال بيان ڪجي ٿو اُھو اوھين ٻڌو، بيشڪ الله کانسواءِ جن کي سڏيندا آھيو سي ڪا مَک پيدا نه ڪندا آھن توڻيڪ اُن (جي بڻائڻ) لاءِ گھڻا گڏ ٿين، ۽ جيڪڏھن مَک کانئن ڪا شيء وٺي ته اُھا کانئس ڇڏائي نه سگھندا، طالب ۽ مطلوب (پوڄارا ۽ بت ٻئي) ھيڻا آھن.');
INSERT INTO "amroti" VALUES(22,74,'22:74','جھڙو الله جو قدر آھي (تھڙو) سندس قدر بجاءِ نه آندائون، بيشڪ الله ڏاڍو غالب آھي.');
INSERT INTO "amroti" VALUES(22,75,'22:75','الله ملائڪن ۽ ماڻھن مان پيغام پھچائڻ وارا چونڊيندو آھي، بيشڪ الله ٻڌندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(22,76,'22:76','جيڪي سندن اڳيان ۽ سندن پويان آھي سو الله ڄاڻندو آھي، ۽ الله ڏانھن سڀ ڪم موٽايا وڃن ٿا.');
INSERT INTO "amroti" VALUES(22,77,'22:77','اي ايمان وارؤ رڪوع ڪريو ۽ سجدو ڪريو ۽ پنھنجي پالڻھار جي عبادت ڪريو ۽ چڱا ڪم ڪريو ته مانَ اوھين ڇُٽو.');
INSERT INTO "amroti" VALUES(22,78,'22:78','۽ الله (جي دين) ۾ اھڙو جھاد ڪريو جھڙو اُن جي جھاد جو حق آھي، اُنھيءَ اوھان کي پسند ڪيو ۽ اوھان تي دين ۾ ڪا اوکائي نه ڪيائين، اوھان جي پيءُ ابراھيم جو دين (اوھان جو دين ڪيائين)، الله اوھان جو نالو ھن کان اڳ ۽ ھن (قرآن) ۾ مسلمان ھن لاءِ رکيو آھي ته پيغمبر (محمد رسُول صلى الله عليه وسلم) اوھان تي شاھد ھجي ۽ اوھين ماڻھن تي شاھد ھجو، پوءِ نماز پڙھو ۽ زڪوٰة ڏيو ۽ الله کي چنبڙي وٺو، (جو) اُھو اوھان جو سائين آھي، پوءِ چڱو سائين ۽ چڱو مددگار آھي.');
INSERT INTO "amroti" VALUES(23,1,'23:1','بيشڪ اُھي مؤمن ڇٽا.');
INSERT INTO "amroti" VALUES(23,2,'23:2','جيڪي پنھنجي نماز ۾ عاجزي ڪندڙ آھن.');
INSERT INTO "amroti" VALUES(23,3,'23:3','۽ جيڪي اجائي ڳالھ کان منھن موڙيندڙ آھن.');
INSERT INTO "amroti" VALUES(23,4,'23:4','۽ جي زڪوٰة ادا ڪندڙ آھن.');
INSERT INTO "amroti" VALUES(23,5,'23:5','۽ جي پنھنجين اُگھڙن کي (زنا کان) بچائيندڙ آھن.');
INSERT INTO "amroti" VALUES(23,6,'23:6','رڳو پنھنجين زالن يا پنھنجين ٻانھين سا (گڏ ٿيندا آھن) پوءِ بيشڪ اُھي ملامت ڪيل نه آھن.');
INSERT INTO "amroti" VALUES(23,7,'23:7','پوءِ جيڪي اُنھن (پنھنجين زالن يا پنھنجين ٻانھين) کانسواءِ طلبيندا سي ئي حد کان لنگھندڙ آھن.');
INSERT INTO "amroti" VALUES(23,8,'23:8','۽ جيڪي پنھنجين امانتن کي ۽ پنھنجي انجام کي سنڀاليندڙ آھن.');
INSERT INTO "amroti" VALUES(23,9,'23:9','۽ جيڪي پنھنجين نمازن جي نگاھ رکندا آھن.');
INSERT INTO "amroti" VALUES(23,10,'23:10','اِھي ئي اُھي وارث آھن.');
INSERT INTO "amroti" VALUES(23,11,'23:11','جيڪي نيٺ بھشت جا وارث ٿيندا، اُھي منجھس سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(23,12,'23:12','۽ بيشڪ ماڻھوءَ کي مٽيءَ جي ست مان بڻايوسون.');
INSERT INTO "amroti" VALUES(23,13,'23:13','وري اُن کي نطفو ڪري مُحڪم جاءِ ۾ رکيوسون.');
INSERT INTO "amroti" VALUES(23,14,'23:14','وري (اُن) نُطفي کي رت جو دڳ بڻايوسون وري (اُن) رت جي دڳ کي ٻوٽي بڻايوسون وري (اُن) ٻوٽي کي ھڏا بڻايوسون پوءِ (اُنھن) ھڏن کي گوشت پھرايوسون، وري اُن کي ٻي (نئين) بڻاوت بڻائي سون، پوءِ الله (وڏيءَ) برڪت وارو (سڀ کان) ڏاڍو چڱو خلقيندڙ آھي.');
INSERT INTO "amroti" VALUES(23,15,'23:15','وري بيشڪ اوھين ان کانپوءِ ضرور مرندؤ.');
INSERT INTO "amroti" VALUES(23,16,'23:16','وري بيشڪ اوھين قيامت جي ڏينھن اُٿاربؤ.');
INSERT INTO "amroti" VALUES(23,17,'23:17','۽ بيشڪ اوھان جي مٿان ست آسمان بڻاياسون، ۽ بڻائڻ کان بي خبر نه ھواسون.');
INSERT INTO "amroti" VALUES(23,18,'23:18','۽ آسمان کان پاڻي اندازي سان وسايوسون پوءِ اُن کي زمين ۾ رھايوسون، بيشڪ اسين اُن جي ورائي نيڻ تي وس وارا آھيون.');
INSERT INTO "amroti" VALUES(23,19,'23:19','پوءِ اُن سان کجين ۽ ڊاکن جا باغ اوھان لاءِ ڄماياسون، اوھان لاءِ انھن باغن ۾ گھڻا ميوا آھن ۽ انھن مان ڪي اوھان کائيندا آھيو.');
INSERT INTO "amroti" VALUES(23,20,'23:20','۽ اھُو (زيتون جو) وڻ (پيدا ڪيوسون) جو طُور سَينا مان نڪرندو آھي (اھڙي طرح) ڄمندو آھي جو تيل ۽ کائڻ وارن لاءِ ٻوڙ پيدا ٿيندو آھي.');
INSERT INTO "amroti" VALUES(23,21,'23:21','۽ بيشڪ اوھان لاءِ ڍورن ۾ (به) عبرت آھي، (جو) جيڪي سندن پيٽن (يعني اوھن) ۾ آھي تنھن مان اوھان کي (کير) پياريندا آھيون ۽ اوھان لاءِ منجھن (ٻيا به) گھڻا فائدا آھن ۽ منجھائن ڪي کائيندا (به) آھيو.');
INSERT INTO "amroti" VALUES(23,22,'23:22','۽ انھن تي ۽ ٻيڙين تي اوھان کي سوار ڪبو آھي.');
INSERT INTO "amroti" VALUES(23,23,'23:23','۽ بيشڪ نوح کي سندس قوم ڏانھن موڪليوسون پوءِ چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اُن کانسواءِ اوھان جو ڪو معبود نه آھي، پوءِ ڇونه ڊڄندا آھيو؟');
INSERT INTO "amroti" VALUES(23,24,'23:24','پوءِ سندس قوم مان جيڪي سردار ڪافر ھوا تن چيو ته ھي رڳو اوھان جھڙو ماڻھو آھي اوھان تي سرادري (حاصل) ڪرڻ گھرندو آھي، ۽ جيڪڏھن الله گھري ھا ته ملائڪن کي موڪلي ھا، اِھا (ڳالھ ته) پنھنجن ابن ڏاڏن ۾ نه ٻڌيسون.');
INSERT INTO "amroti" VALUES(23,25,'23:25','اِھو ته رڳو ھڪڙو ديوانو شخص آھي تنھنڪري اُن لاءِ ھڪ وقت تائين ترسو.');
INSERT INTO "amroti" VALUES(23,26,'23:26','(نوح) چيو اي منھنجا پالڻھار مون کي مدد ڏي انھيءَ ڪري جو مون کي ڪوڙو ڀانيائون.');
INSERT INTO "amroti" VALUES(23,27,'23:27','پوءِ ڏانھنس وحي ڪيوسون ته اسان جي اکين آڏو ۽ اسان جي حُڪم سان ٻيڙي ٺاھ پوءِ جڏھن اسان جو حُڪم اچي ۽ تنور اُڀامي تڏھن سڀڪنھن جوڙي مان ٻه ٻه (نر ۽ مادي) ۽ پنھنجي گھر وارا منجھس چاڙھ پر منجھائن جن بابت (ھلاڪ ٿيڻ جو) حُڪم ٿي چڪو (سي نه چاڙھ)، ۽ ظالمن بابت مون کي نه چؤ، ڇوته اُھي ٻُڏل آھن.');
INSERT INTO "amroti" VALUES(23,28,'23:28','پوءِ جڏھن ٻيڙيءَ تي تون چڙھي وِھين ۽ تنھنجا سنگتي (به وِھن) تڏھن چؤ ته سڀ ساراھ اُنھيءَ الله کي جڳائي جنھن ظالمن جي قوم کان اسان کي بچايو.');
INSERT INTO "amroti" VALUES(23,29,'23:29','۽ چؤ ته اي منھنجا پالڻھار مون کي برڪت واري ھنڌ لاھ ۽ تون (سڀ کان) چڱو لاھيندڙ آھين.');
INSERT INTO "amroti" VALUES(23,30,'23:30','بيشڪ ھن (قِصّي) ۾ نشانيون آھن ۽ بيشڪ اسين پرکڻ وارا ھواسون.');
INSERT INTO "amroti" VALUES(23,31,'23:31','وري کانئن پوءِ ٻيو جُڳ پيدا ڪنداسون.');
INSERT INTO "amroti" VALUES(23,32,'23:32','پوءِ منجھن ھڪ پيغمبر اُنھن (جي قبيلي) مان موڪليوسون (تنھن چين) ته الله جي عبادت ڪريو اُن کانسواءِ اوھانجو ڪو معبود نه آھي، پوءِ ڇونه ڊڄندا آھيو؟');
INSERT INTO "amroti" VALUES(23,33,'23:33','۽ سندس قوم مان جن سردارن ڪفر ڪيو ۽ آخرت جي ملڻ کي ڪوڙ ڀانيو ۽ کين دنيا جي حياتيءَ ۾ آسودو به ڪيو ھوسون تن چيو ته ھيءُ (پيغمبر) رڳو اوھان جھڙو ماڻھو آھي (جو) جنھن (قسم) منجھان (اوھين) کائيندا آھيو تنھن مان (اُھو به) کائيندو آھي ۽ جنھن (قسم) منجھان اوھين پيئندا آھيو تنھن مان (اُھو به) پيئندو آھي.');
INSERT INTO "amroti" VALUES(23,34,'23:34','۽ جيڪڏھن پاڻ جھڙي ماڻھوءَ جو چيو مڃيندؤ ته اوھين ان مھل ڇيئي وارا ٿيندؤ.');
INSERT INTO "amroti" VALUES(23,35,'23:35','اوھان سان انجام ڪندو آھي ڇا ته جڏھن (اوھين) مرندؤ ۽ مٽي ۽ ھڏا ٿيندؤ (تڏھن) اوھين ٻاھر ڪڍبؤ؟');
INSERT INTO "amroti" VALUES(23,36,'23:36','جنھن جو اوھان کي انجام ڏجي ٿو سو (ٻڌڻ ۽ چوڻ کان) پري (بلڪل) پري آھي.');
INSERT INTO "amroti" VALUES(23,37,'23:37','اسان جي رڳي ھيءَ دنيا جي حياتي آھي (جنھن ۾) مرندا آھيون ۽ جيئندا آھيون ۽ اسين (وري جيئرا ٿي) اُٿڻ وارا نه آھيون.');
INSERT INTO "amroti" VALUES(23,38,'23:38','ھيءُ رڳو ھڪ ماڻھو آھي جنھن الله تي ڪوڙ ٻڌو آھي ۽ اسين کيس (ڪڏھن) مڃڻ وارا نه آھيون.');
INSERT INTO "amroti" VALUES(23,39,'23:39','(پيغمبر) چيو ته منھنجا پالڻھار مون کي مدد ڏي ھن ڪري جو مون کي ڪوڙو ڀانيائون.');
INSERT INTO "amroti" VALUES(23,40,'23:40','(الله) فرمايو ته ٿوري مُدت پڄاڻا پشيمان ٿيندا.');
INSERT INTO "amroti" VALUES(23,41,'23:41','پوءِ کين سچي انجام جي ڪري ڌڪاءَ پڪڙيو پوءِ کين ڪُٽو ڪيوسون، پوءِ ظالمن جي قوم تي لعنت ھجي.');
INSERT INTO "amroti" VALUES(23,42,'23:42','وري کانئن پوءِ ٻيا جُڳ پيدا ڪياسون.');
INSERT INTO "amroti" VALUES(23,43,'23:43','ڪائي ٽولي پنھنجي مُدت کان اڳي نه ويندي آھي ۽ نڪي (اُھي) دير ڪندا آھن.');
INSERT INTO "amroti" VALUES(23,44,'23:44','وري لڳو لڳ پنھنجن پيغمبرن کي موڪليوسون، جڏھن ڪڏھن ڪنھن ٽوليءَ وٽ سندن پيغمبر آيو (تڏھن) کيس ڪوڙو ڄاتائون پوءِ انھن کي ھڪ ٻئي پٺيان (ناس) ڪيوسون ۽ کين (ٻـين لاءِ) آکاڻيون ڪيوسون، پوءِ نه مڃيندڙ قوم تي لعنت ھُجي.');
INSERT INTO "amroti" VALUES(23,45,'23:45','وري پنھنجين نشانين ۽ پڌريءَ حجت سان موسىٰ ۽ سندس ڀاءُ ھارون کي موڪليوسون.');
INSERT INTO "amroti" VALUES(23,46,'23:46','فرعون ۽ سندس سردارن ڏانھن پوءِ انھن وڏائي ڪئي ۽ (اُھي) ھٺيلي قوم ھوا.');
INSERT INTO "amroti" VALUES(23,47,'23:47','پوءِ چيائون ته پاڻ جھڙن ٻن ماڻھن تي ايمان آڻيون ڇا؟ ۽ سندن قوم (ته) اسان جو ٻيلپو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(23,48,'23:48','پوءِ انھن کي ڪوڙو ڄاتائون پوءِ ھلاڪ ڪيلن مان ٿيا.');
INSERT INTO "amroti" VALUES(23,49,'23:49','۽ بيشڪ موسىٰ کي ڪتاب ڏنوسون ته مانَ اُھي سِڌو رستو لھن.');
INSERT INTO "amroti" VALUES(23,50,'23:50','۽ مريم جي پٽ کي ۽ سندس ماءُ کي ھڪ نشاني ڪئي سون ۽ انھن کي سنئين ۽ وھندڙ پاڻي واري مٿاھين زمين تي (رھڻ جي) جاءِ ڏني سون.');
INSERT INTO "amroti" VALUES(23,51,'23:51','(چيوسون ته) اي پيغمبرؤ سٺين شين مان کائو ۽ چڱا ڪم ڪريو، بيشڪ جيڪي ڪندا آھيو سو آءٌ ڄاڻندڙ آھيان.');
INSERT INTO "amroti" VALUES(23,52,'23:52','۽ بيشڪ اوھان جو ھيءُ دين ھڪ دين آھي ۽ آءٌ اوھان جو پالڻھار آھيان تنھنڪري مون کان ڊڄو.');
INSERT INTO "amroti" VALUES(23,53,'23:53','پوءِ (اُنھن قومن) پنھنجي ڪم کي پاڻ ۾ ذرا ذرا ڪري ڇنون، سڀڪنھن ٽوليءَ وٽ جيڪي آھي تنھن سان اُھا سرھي (رھندي) آھي.');
INSERT INTO "amroti" VALUES(23,54,'23:54','پوءِ (اي پيغمبر) کين سندن غفلت ۾ ھڪ حد تائين ڇڏي ڏي.');
INSERT INTO "amroti" VALUES(23,55,'23:55','جنھن مال ۽ اولاد سان کين زيادتي ڏني سون تنھن کي ڀانئيندا آھن ڇا؟');
INSERT INTO "amroti" VALUES(23,56,'23:56','ته ڪا انھن لاءِ چڱاين ڏيڻ ۾ تڪر ٿا ڪريون، (نه!) بلڪ نه سمجھندا آھن.');
INSERT INTO "amroti" VALUES(23,57,'23:57','بيشڪ اُھي جيڪي پنھنجي پالڻھار جي ڀَوَ کان ڊڄندڙ آھن.');
INSERT INTO "amroti" VALUES(23,58,'23:58','۽ اُھي جيڪي پنھنجي پالڻھار جي نشانين تي ايمان آڻيندا آھن.');
INSERT INTO "amroti" VALUES(23,59,'23:59','۽ اُھي جيڪي پنھنجي پالڻھار سان شريڪ مُقرّر نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(23,60,'23:60','۽ اُھي جيڪي (اُھي) ڏيندا آھن جيڪي (سندس واٽ ۾) ڏيندا آھن ۽ سندن دليون انھي ڪري ڏڪنديون آھن ته اُھي پنھنجي پالڻھار ڏانھن موٽڻ وارا آھن.');
INSERT INTO "amroti" VALUES(23,61,'23:61','اھي (ماڻھو) چڱاين ۾ تڪڙائي ڪندا آھن ۽ اُھي اُن لاءِ اڳرائي ڪندڙ آھن.');
INSERT INTO "amroti" VALUES(23,62,'23:62','۽ (اسين) ڪنھن به جيءَ تي سندس طاقت کانسواءِ تڪليف نه ٿا رکون ۽ اسان وٽ ھڪ ڪتاب آھي جو سچ ڳالھائيندو آھي ۽ انھن تي ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(23,63,'23:63','بلڪ سندن دليون انھن (ڳالھين) کان غفلت ۾ آھن ۽ ھن کانسواءِ انھن جا ٻيا ڪرتوت به آھن جي اُھي ڪندا آھن.');
INSERT INTO "amroti" VALUES(23,64,'23:64','تان جو منجھائن آسودن (ماڻھن) کي جڏھن عذاب سان پڪڙيوسون تڏھن دانھون ڪندا رھيا.');
INSERT INTO "amroti" VALUES(23,65,'23:65','(چيوسون) ته اڄ دانھون نه ڪريو، ڇوته اسان کان اوھان کي مدد نه ملندي.');
INSERT INTO "amroti" VALUES(23,66,'23:66','بيشڪ اوھان کي منھنجيون آيتون پڙھي ٻڌائبيون ھيون تڏھن اوھين پنھنجين کڙين (ڀرپوءِ) تي ڀڄندا ھُيؤ.');
INSERT INTO "amroti" VALUES(23,67,'23:67','اُن (قرآن) کان وڏائي ڪندڙ (۽) آکاڻين ۾ مشغول ٿي (کيس) ڇڏيوَ ٿي.');
INSERT INTO "amroti" VALUES(23,68,'23:68','ھن ڳالھ (قرآن) کي نه سوچيائون ڇا يا وٽن اھڙي شيء آئي آھي جا سندن اڳين پيءُ ڏاڏن وٽ نه آئي ھُئي؟');
INSERT INTO "amroti" VALUES(23,69,'23:69','يا پنھنجي پيغمبر کي نه سڃاتائون ڇا تنھنڪري اُھي سندس مُنڪر (ٿيا) آھن.');
INSERT INTO "amroti" VALUES(23,70,'23:70','يا چوندا آھن ته کيس چريائي آھي، (نه!) بلڪ (پيغمبر) وٽن سچي ڳالھ آندي آھي ۽ منجھائن گھڻا سچ کي بُرو ڀائيندڙ آھن.');
INSERT INTO "amroti" VALUES(23,71,'23:71','۽ جيڪڏھن الله سَندن سَڌن تي ھلي ھا ته آسمان ۽ زمين ۾ جيڪي منجھن آھي سي ويران ٿين ھا، (نه!) بلڪ سندين نصيحت وٽن آندي سون ۽ اُھي پنھنجيءَ نصيحت (ڪتاب) کان مُنھن موڙيندڙ آھن.');
INSERT INTO "amroti" VALUES(23,72,'23:72','يا کائن ڪو اُجورو گھرندو آھين ڇا؟ پوءِ تنھنجي پالڻھار جو اُجورو (ڏئڻ) ڀلو آھي، ۽ اُھو چڱو روزي ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(23,73,'23:73','۽ بيشڪ تون سڌيءَ واٽ ڏانھن کين سڏيندو آھين.');
INSERT INTO "amroti" VALUES(23,74,'23:74','۽ بيشڪ جيڪي آخرت کي نه مڃيندا آھن سي سڌي واٽ کان پاسيرا ٿيندڙ آھن.');
INSERT INTO "amroti" VALUES(23,75,'23:75','۽ جيڪڏھن مٿن رحم ڪريون ھا ۽ جيڪو کين ڏک پُھتو سو لاھيون ھا ته (به) سدائين پنھنجيءَ گمراھيءَ ۾ حيران رھن ھا.');
INSERT INTO "amroti" VALUES(23,76,'23:76','۽ بيشڪ کين عذاب سان پڪڙيوسون پوءِ (به) پنھنجي پالڻھار جي اڳيان نه نِوڙيا ۽ نڪي عاجزي ڪيائون.');
INSERT INTO "amroti" VALUES(23,77,'23:77','(پنھنجي غفلت ۾ رھيا) تانجو جنھن مھل مٿن سخت عذاب وارو دروازو کوليوسون (تنھن مھل) اُتي جو اُتي اُنھي ۾ نا اُميد ٿيا.');
INSERT INTO "amroti" VALUES(23,78,'23:78','۽ (الله) اُھو آھي جنھن اوھان لاءِ ڪن ۽ اکيون ۽ دليون بڻايون، (پر اوھين تمام) ٿورو شڪر ڪندا آھيو.');
INSERT INTO "amroti" VALUES(23,79,'23:79','۽ (الله) اُھو آھي جنھن اوھان کي زمين ۾ پيدا ڪيو ۽ ڏانھس وري گڏ ڪيا ويندؤ.');
INSERT INTO "amroti" VALUES(23,80,'23:80','۽ (الله) اُھو آھي جيڪو جياري ٿو ۽ ماري ٿو ۽ رات ۽ ڏينھن جو ڦير ڦار ڪرڻ ان جو (ڪم) آھي، پوءِ نه سمجھندا آھيو ڇا؟');
INSERT INTO "amroti" VALUES(23,81,'23:81','بلڪ جئن اڳين چيو ته تئين چوندا آھن.');
INSERT INTO "amroti" VALUES(23,82,'23:82','چون ٿا ته ھان جڏھن مرنداسون ۽ مٽي ۽ ھڏا ٿينداسون (تڏھن) وري جيئل ٿي اٿنداسون ڇا؟');
INSERT INTO "amroti" VALUES(23,83,'23:83','بيشڪ اسان کي ۽ اسان جي ابن ڏاڏن کي ھن کان اڳ انھي (ڳالھ) جو انجام ڏنو ويو آھي اھي رڳو اڳين جا قصّا آھن.');
INSERT INTO "amroti" VALUES(23,84,'23:84','چؤ ته جيڪڏھن اوھين ڄاڻندا آھيو ته زمين ۽ جيڪي منجھس آھي سو ڪنھن جو آھي؟');
INSERT INTO "amroti" VALUES(23,85,'23:85','سگھوئي چوندا ته (اُھو سڀ) الله جو آھي، چؤ ته پوءِ ڇونه نصيحت وٺندا آھيو؟');
INSERT INTO "amroti" VALUES(23,86,'23:86','(اي پيغمبر کين) چؤ ته ستن آسمانن جو پالڻھار ۽ وڏي عرش جو پالڻھار ڪير آھي؟');
INSERT INTO "amroti" VALUES(23,87,'23:87','سگھوئي چوندا ته (اھو سڀ) الله جو آھي، چؤ ته پوءِ ڇونه ڊڄندا آھيو؟');
INSERT INTO "amroti" VALUES(23,88,'23:88','(کين) چؤ ته (اُھو) ڪير آھي جنھنجي ھٿ ۾ سڀڪنھن شيء جي حڪومت آھي ۽ اُھو پناھ ڏيندو آھي ۽ اُن (جي عذاب) کان ڪنھن کي پناھ نه ڏيئي سگھبي آھي جيڪڏھن ڄاڻندا آھيو (ته ڏسيو).');
INSERT INTO "amroti" VALUES(23,89,'23:89','سگھوئي چوندا ته (اھو سڀ) الله جي (ھٿ ۾) آھي، (کين) چؤ ته پوءِ اوھين ڪٿان ٺڳجو ٿا.');
INSERT INTO "amroti" VALUES(23,90,'23:90','بلڪ وٽن سچي (ڳالھ) آندي اٿئون ۽ بيشڪ اُھي ڪوڙا آھن.');
INSERT INTO "amroti" VALUES(23,91,'23:91','الله نڪي (پاڻ لاءِ) پٽ ڪري ورتو آھي ۽ نڪي ساڻس ڪو (ٻيو) معبود آھي (جيڪڏھن ائين ھجي ھا) ته ھر ڪو معبود پنھنجي بڻائي کي وٺيو وڃي ھا ۽ اُھي ھڪ ٻئي تي ڏاڍ ڪن ھا، (ڪافر) جيڪي بيان ڪندا آھن تنھن کان الله پاڪ آھي.');
INSERT INTO "amroti" VALUES(23,92,'23:92','ڳجھ ۽ ظاھر جو ڄاڻندڙ آھي جنھن کي (اُن سان) شريڪ بڻائيندا آھن تنھن کان مٿاھون آھي.');
INSERT INTO "amroti" VALUES(23,93,'23:93','(اي پيغمبر) چؤ ته اي منھنجا پالڻھار جيڪو انجام ڪرين ٿو سو جيڪر مون کي ڏيکارين.');
INSERT INTO "amroti" VALUES(23,94,'23:94','اي منھنجا پالڻھار مون کي ظالمن جي قوم ۾ (داخل) نه ڪر.');
INSERT INTO "amroti" VALUES(23,95,'23:95','۽ جيڪو کين انجام ڏيون ٿا تنھن جي توکي ڏيکارڻ تي بيشڪ اسين وسوارا آھيون.');
INSERT INTO "amroti" VALUES(23,96,'23:96','ڏاڍي چڱيءَ عادت سان مدائي کي ٽار، (ڪافر) جيڪي بيان ڪندا آھن سو اسين وڌيڪ ڄاڻندڙ آھيون.');
INSERT INTO "amroti" VALUES(23,97,'23:97','۽ (اي پيغمبر) چؤ ته اي منھنجا پالڻھار شيطانن جي وسوسن کان تنھنجي پناھ گھران ٿو.');
INSERT INTO "amroti" VALUES(23,98,'23:98','۽ اي منھنجا پالڻھار پاڻ وٽ انھن جي ويجھي ٿيڻ کان (به) تنھنجي پناھ وٺان ٿو.');
INSERT INTO "amroti" VALUES(23,99,'23:99','(ڪافر پنھنجي ويسلائي ۾ ھوندا) تانجو جنھن مھل منجھانئن ڪنھن کي موت ايندو (تنھن مھل) چوندو ته اي منھنجا پالڻھار مون کي دنيا ۾ (ورائي) موٽاءِ.');
INSERT INTO "amroti" VALUES(23,100,'23:100','ته جيڪر جيڪي (اُتي) ڇڏيم ته آءٌ چڱا عمل ڪريان ائين (ڪڏھن ٿيڻو) نه آھي، بيشڪ اِھا ڳالھ آھي جا ھو چوندڙ آھي، ۽ سندن اڳيان ھڪ پردو (عالَم قبر) انھيءَ ڏينھن تائين رھندو (جنھن ڏينھن قيامت ۾) اُٿاريا ويندا.');
INSERT INTO "amroti" VALUES(23,101,'23:101','پوءِ جنھن مھل صور ۾ ڦوڪبو (تنھن مھل) اُن ڏينھن سندن وچ ۾ نه ذاتيون رھنديون ۽ نڪي پاڻ ۾ پُڇا ڳاڇا ڪندا.');
INSERT INTO "amroti" VALUES(23,102,'23:102','پوءِ جنھن (جي نيڪيءَ) جو تراڙو ڳرو ٿيو سي ئي ڇُٽل آھن.');
INSERT INTO "amroti" VALUES(23,103,'23:103','۽ جنھن جو تراڙو ھلڪو ٿيو سي اُھي آھن جن پاڻ کي ڇيئو لاتو (اُھي) دوزخ ۾ سدائين رھندڙ آھن.');
INSERT INTO "amroti" VALUES(23,104,'23:104','سندن مُنھن کي باھ ساڙيندي ۽ اُھي منجھس بد شڪلا ٿيندا.');
INSERT INTO "amroti" VALUES(23,105,'23:105','(چيو ويندن ته) منھنجون آيتون اوھان کي نه پڙھي ٻڌائبيون ھيون ڇا؟ پوءِ اوھين انھن کي ڪوڙو ڀائيندا ھيؤ؟');
INSERT INTO "amroti" VALUES(23,106,'23:106','چوندا ته اي اسان جا پالڻھار اسان جي بدبختي اسان تي غالب ٿي وئي ۽ گمراھ قوم ھياسون.');
INSERT INTO "amroti" VALUES(23,107,'23:107','چوندا ته اي اسان جا پالڻھار ھتان اسان کي ٻاھر ڪڍ پوءِ جيڪڏھن موٽي (ڪفر) ڪريون ته بيشڪ اسين ظالم ٿيون.');
INSERT INTO "amroti" VALUES(23,108,'23:108','(الله) فرمائيندو ته منجھس خوار پيا رھو ۽ مون سان نه ڳالھايو.');
INSERT INTO "amroti" VALUES(23,109,'23:109','ڇوته منھنجن ٻانھن مان ھڪ ٽولي ھئي جا چوندي ھئي ته اي اسان جا پالڻھار اسان ايمان آندو تنھنڪري اسان کي بخش ۽ اسان تي رحم ڪر ۽ تون (سڀ کان) چڱو ٻاجھارو آھين.');
INSERT INTO "amroti" VALUES(23,110,'23:110','پوءِ ايسين کين ٺـٺولي ڪري ورتوَ جيسين (سندن دشمنيءَ) اوھان (جي دل) کان منھنجي يادگيري وسارائي ۽ اوھين مٿن کِليؤ ٿي.');
INSERT INTO "amroti" VALUES(23,111,'23:111','بيشڪ اڄ کين چڱو بدلو انھي ڪري ڏنم جو صبر ڪيو ھئائون جو اُھي مراد ماڻيندڙ آھن.');
INSERT INTO "amroti" VALUES(23,112,'23:112','(الله) فرمائيندو ته زمين ۾ (اوھين) ڪيترا ورھ رھيؤ؟');
INSERT INTO "amroti" VALUES(23,113,'23:113','چوندا ته ھڪ ڏينھن يا ڏينھن جو ڀاڱو رھياسون پوءِ ڳڻيندڙن کان پُڇا ڪر.');
INSERT INTO "amroti" VALUES(23,114,'23:114','(الله) فرمائيندو ته ٿوريءَ (دير) کانسواءِ نه رھيا ھيؤ جيڪر اوھين ڄاڻو ھا.');
INSERT INTO "amroti" VALUES(23,115,'23:115','اوھين ڀانيو ٿا ڇا ته اوھان کي اجايو بڻايو اٿئون ۽ اوھين اسان ڏانھن وري نه موٽايا ويندؤ؟');
INSERT INTO "amroti" VALUES(23,116,'23:116','پوءِ الله سچو بادشاھ تمام مٿاھون آھي، اُن کانسواءِ ڪو عبادت جو لائق نه آھي (اھو) سڳوري عرش جو پالڻھار آھي.');
INSERT INTO "amroti" VALUES(23,117,'23:117','۽ جيڪو الله سان گڏ ٻئي معبود کي سڏيندو جنھن جو کيس ڪو دليل ڪونھي تنھن جو حساب سندن پالڻھار وٽ ئي آھي، سچ آھي ته ڪافر نه ڇُٽندا.');
INSERT INTO "amroti" VALUES(23,118,'23:118','۽ چؤ ته اي منھنجا پالڻھار بخش ۽ رحم ڪر ۽ تون (سڀني) ٻاجھارن کان چڱو آھين.');
INSERT INTO "amroti" VALUES(24,1,'24:1','(ھيءَ ھڪ) سُورة آھي جا نازل ڪئي سون ۽ اُن (جي حڪمن) کي فرض ڪيوسون ۽ منجھس پڌريون آيتون نازل ڪيوسون ته مانَ نصيحت وٺو.');
INSERT INTO "amroti" VALUES(24,2,'24:2','زنا ڪندڙ زال ۽ زنا ڪندڙ مڙس سو ٻنھيءَ مان ھر ھڪ کي سؤ دُرا ھڻو، ۽ جيڪڏھن اوھين الله ۽ قيامت جي ڏينھن کي مڃيندا آھيو ته الله جي دين ۾ اوھان کي انھي ٻنھي تي نرمي ڪرڻ نه جڳائي، ۽ مؤمنن مان ھڪ ٽوليءَ کي انھن ٻنھي جي سزا تي حاضر ٿيڻ گھرجي.');
INSERT INTO "amroti" VALUES(24,3,'24:3','زنا ڪندڙ مرد زنا ڪندڙ زال يا مشرڪياڻيءَ کانسواءِ ٻي نه پرڻبو، ۽ زنا ڪندڙ زال کيس زنا ڪندڙ مڙس يا مشرڪ کانسواءِ ٻيو نه پرڻبو، ۽ اِھو (زنا ڪندڙ زال سان نڪاح ڪرڻ) مؤمنن تي حرام ڪيو ويو.');
INSERT INTO "amroti" VALUES(24,4,'24:4','۽ جيڪي پاڪدامن عورتن کي تھمت لائين وري چار شاھد نه آڻين ته کين اسي دُرا ھڻو ۽ انھن جي گواھي اصلي قبول نه ڪريو، ۽ اُھي بي دين آھن.');
INSERT INTO "amroti" VALUES(24,5,'24:5','پر ھن کان پوءِ جن توبه ڪئي ۽ پاڻ سُڌاريو، ته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(24,6,'24:6','۽ جيڪي پنھنجي زالن کي تُھمت لائين ۽ کين پاڻ کانسواءِ ڪي شاھد نه ھجن ته اھڙي ھر ھڪ جي (سچي) شاھدي چار ڀيرا (ھيئڻ ڏيڻ) آھي ته الله جو قسم ته بيشڪ پاڻ سچ چوندڙن مان آھي.');
INSERT INTO "amroti" VALUES(24,7,'24:7','۽ پنجون ڀيرو چوي ته جيڪڏھن ڪوڙن مان ھوندو ته مٿس الله جي لعنت ھجي.');
INSERT INTO "amroti" VALUES(24,8,'24:8','۽ اُن زال کان (ھيءَ ڳالھ) سزا ٽاريندي جو چار ڀيرا شاھدي ڏئي ته الله جو قسم ته بيشڪ اُھو مُڙس ڪُوڙ چوندڙن مان آھي.');
INSERT INTO "amroti" VALUES(24,9,'24:9','۽ پنجون ڀيرو (ھيءَ شاھدي ڏئي) ته جيڪڏھن اُھو مڙس سچن مان آھي ته اُن (زال) تي الله جو ڏمر ھُجي.');
INSERT INTO "amroti" VALUES(24,10,'24:10','۽ جيڪڏھن اوھان تي الله جو فضل ۽ سندس ٻاجھ نه ھجي ھا (ته عذاب ٿئيوَ ھا) ۽ بيشڪ الله توبه قبول ڪندڙ حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(24,11,'24:11','بيشڪ جن ڪُوڙو بُھتان آندو سي اوھان مان ھڪ ٽولو آھن، (اوھين) اُن (بُھتان) کي نه ڀانيو ته اوھان لاءِ (ڪو) مدو آھي، بلڪ اُھو اوھان لاءِ ڀلو آھي، منجھائن سڀڪنھن ماڻھوءَ گناھن مان جيڪي ڪمايو سو سندس لاءِ آھي، ۽ منجھائن جنھن بھتان جو وڏو لُڙ مچايو تنھن لاءِ وڏو عذاب آھي.');
INSERT INTO "amroti" VALUES(24,12,'24:12','جڏھن اُھو ٻُڌو تڏھن مؤمنن مڙسن ۽ مؤمنياڻن زالن پنھنجن (ماڻھن) ۾ چڱو گمان ڇونه ڪيو ۽ ڇونه چيائون ته ھيءُ پڌرو بُھتان آھي؟');
INSERT INTO "amroti" VALUES(24,13,'24:13','اِن (ڳالھ) تي چار شاھد ڇونه آندائون؟ پوءِ جڏھن شاھد نه آندائون تڏھن اُھي ئي الله وٽ ڪُوڙا آھن.');
INSERT INTO "amroti" VALUES(24,14,'24:14','۽ جيڪڏھن الله جو فضل ۽ سندس ٻاجھ دنيا ۽ آخرت ۾ اوھان تي نه ھجي ھا ته جنھن (ڳالھ) بابت چرچو مچايوَ (تنھن بابت) اوھان کي (تڏھن ئي) وڏو عذاب پھچي ھا.');
INSERT INTO "amroti" VALUES(24,15,'24:15','جڏھن ان کي پنھنجن زبانن سان ٻاڦيندا رھيؤ ۽ جنھن (ڳالھ جي حقيقت) جي اوھان کي ڪا خبر نه ھئي سا پنھنجن واتن سان چوندا رھيؤ ۽ اُن کي ھلڪو ٿي ڀانيوَ، ۽ (حقيقت ڪري) اُھو الله وٽ وڏو (گناہ) ھو.');
INSERT INTO "amroti" VALUES(24,16,'24:16','۽ جڏھن اُھو ٻڌوَ (تڏھن) ڇونه چيوَ ته اسان کي اھو چوڻ نه جڳائيندو آھي، (اي الله) تنھنجي پاڪائي بيان ڪريون ٿا ھي وڏ بُھتان آھي.');
INSERT INTO "amroti" VALUES(24,17,'24:17','الله اوھان کي نصيحت ٿو ڪري ته جيڪڏھن مؤمن آھيو ته (ٻيھر) وري اصلي اھڙو (ڪم) نه ڪجو.');
INSERT INTO "amroti" VALUES(24,18,'24:18','۽ الله اوھان لاءِ آيتون بيان ٿو ڪري، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(24,19,'24:19','بيشڪ جيڪي (ماڻھو) مؤمنن (جي نسبت) ۾ بدڪاريءَ (جي تھمت) جي پڌري ٿيڻ کي دوست رکندا آھن تن لاءِ دنيا ۽ آخرت ۾ ڏکوئيندڙ عذاب آھي، ۽ الله ڄاڻندو آھي ۽ اوھين نه ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(24,20,'24:20','۽ جيڪڏھن اوھان تي الله جو فضل ۽ سندس ٻاجھ نه ھجي ھا (ته ضرور خراب ٿيو ھا) ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(24,21,'24:21','اي ايمان وارؤ اوھين شيطان جي وِکن تي نه ھلو، ۽ جيڪو شيطان جي وِکن تي ھلندو ته شيطان ته بيحيائي (جون ڳالھيون) ۽ مندا ڪم ڏسيندو آھي، ۽ جيڪڏھن اوھان تي الله جو فضل ۽ سندس ٻاجھ نه ھجي ھا ته اوھان مان ڪو ھڪ ڪڏھن نه سُڌري ھا پر الله جنھن کي گھرندو آھي تنھن کي پاڪ ڪندو آھي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(24,22,'24:22','۽ اوھان مان فضل وارا ۽ آسودا مائٽي وارن ۽ مسڪينن ۽ الله جي واٽ ۾ وطن ڇڏيندڙن کي (ڪي به) نه ڏيڻ جو قسم نه کڻن، ۽ جڳائي ته معاف ڪن ۽ ٽارو ڪن، اوھين نه گھرندا آھيو ڇا ته الله اوھان کي بخشي؟ ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(24,23,'24:23','بيشڪ جيڪي (ماڻھو) پاڪدامن بي خبر مؤمنياڻن زالن کي تُھمت لائيندا آھن تن تي دنيا ۽ آخرت ۾ لعنت آھي، ۽ اُنھن لاءِ (اُنھيءَ ڏينھن) وڏو عذاب آھي.');
INSERT INTO "amroti" VALUES(24,24,'24:24','انھن جيڪي ڪمايو تنھن بابت سندين زبانون ۽ سندن ھٿ ۽ سندن پير جنھن ڏينھن مٿن شاھدي ڏيندا.');
INSERT INTO "amroti" VALUES(24,25,'24:25','اُن ڏينھن الله کين انصاف سان سندن بدلو پُورو ڏيندو ۽ (ھيءُ) ڄاڻندا ته الله ئي سچو پڌري ڪرڻ وارو آھي.');
INSERT INTO "amroti" VALUES(24,26,'24:26','پليت زالون پليت مردن لاءِ (لائق) آھن ۽ پليت مرد پليت زالن لاءِ (لائق) آھن، ۽ پاڪ زالون پاڪ مردن لاءِ (لائق) آھن ۽ پاڪ مرد پاڪ زالن لاءِ (لائق) آھن، (انھن جي به نسبت ماڻھو) جيڪي چوندا آھن تنھن (ڳالھ) کان اُھي پاڪ آھن، انھن لاءِ بخشش ۽ سڳوري روزي آھي.');
INSERT INTO "amroti" VALUES(24,27,'24:27','اي ايمان وارؤ پنھنجن گھرن کانسواءِ ٻـين (ماڻھن جي) گھرن ۾ (ايسين) نه گھڙو جيسين (نه) موڪلايو ۽ اُن جي رھڻ وارن تي سلام (نه) ڏيو، اِھو اوھان لاءِ چڱو آھي مانَ اوھين نصيحت وٺو.');
INSERT INTO "amroti" VALUES(24,28,'24:28','پوءِ جيڪڏھن منجھس ڪنھن کي نه ڏسو ته اُن ۾ (ايسين) نه گھڙو جيسين اوھان کي موڪل (نه) ڏجي، ۽ جيڪڏھن اوھان کي چئجي ته موٽي وڃو ته موٽي وڃو اِھو اوھان لاءِ تمام سٺو آھي، ۽ جيڪي اوھين ڪندا آھيو سو الله ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(24,29,'24:29','اھڙن گھرن ۾ گھڙڻ کان اوھان تي ڪو گناھ ڪونھي جنھن ۾ ڪوئي نه رھندو ھجي منجھس اوھان جو سامان (رکيل) ھجي، ۽ جيڪي پڌرو ڪندا آھيو ۽ جيڪي لڪائيندا آھيو سو الله ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(24,30,'24:30','مؤمن مڙسن کي چؤ ته پنھنجين اکين کي ھيٺ ڪن ۽ پنھنجن اُگھڙن کي بچائيندا رھن، اھو انھن لاءِ تمام سٺو آھي، جيڪي ڪندا آھن تنھنجي بيشڪ الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(24,31,'24:31','۽ مؤمن زالن کي چؤ ته پنھنجيون اکيون جھڪيون ڪن ۽ پنھنجن اوگھڙن کي بچائينديون رھن ۽ پنھنجي سينگار (يعني زيوتن جي جاين) کي پڌرو نه ڪن پر اُنھن مان جيڪي ظاھر (ضروري) آھن (جھڙا ھٿ پير) ۽ جڳائين ته پنھنجين چادرن کي پنھنجي گريبانن تي لاھي ڇڏين، ۽ پنھنجن مڙسن يا پنھنجن پين يا پنھنجن سُھرن يا پنھنجن پُٽن يا پنھنجن مڙسن جي پُٽن يا پنھنجن ڀائرن يا پنھنجن ڀائيٽن يا پنھنجن ڀاڻيجن يا پنھنجن زالن يا پنھنجن ٻانھن يا خدمت گار مڙسن مان (زالن ڏانھن) اڻ خواھش وارن يا جن ٻارن کي زالن جي اوگھڙ جو سماءُ نه پوي تن ڌاران ٻـين (سڀني) جي اڳيان پنھنجو سينگار پڌرو نه ڪن، ۽ پنھنجن پيرن سان (زيور) ھن لاءِ نه ڇمڪائين ته سندن سينگار مان جيڪي ڳجھو آھي سو ڄاتو وڃي، ۽ اي مسلمانؤ اوھين مڙيئي الله جي اڳيان توبه ڪريو ته مانَ اوھين ڇُٽو.');
INSERT INTO "amroti" VALUES(24,32,'24:32','۽ پنھنجيءَ (قوم) مان رنن زالن کي پرڻايو ۽ پنھنجن ٻانھن ۽ پنھنجين ٻانھين مان نيڪن کي (به پرڻايو)، جيڪڏھن (ھو) غريب ھوندا ته الله پنھنجي فضل سان کين آسُودو ڪندو، ۽ الله (مھربانيءَ ۾) ڪشادگيءَ وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(24,33,'24:33','۽ جيڪي پرڻجي نٿا سگھن تن کي ايسين پاڪدامن رھڻ گھرجي جيسين کين الله پنھنجي فضل سان آسودو ڪري، ۽ اوھان جي ٻانھن مان جيڪي (آزاديءَ لاءِ عوض ڏيڻ جو) لکيل انجام گھرن تن ۾ جيڪڏھن ڪا چڱائي ڄاڻو ته کين لکيل انجام ڏيو، ۽ جيڪو مال الله اوھان کي ڏنو آھي تنھن مان کين ڏيو، ۽ اوھان جون ٻانھيون جيڪڏھن پاڪدامن رھڻ جو ارادو ڪن ٿيون ته انھن کي اوھين دُنيا جي حياتيءَ جي سامان حاصل ڪرڻ لاءِ زنا تي زور نه ڪريو، ۽ جيڪو کين زنا تي زور ڪندو ته زور ڪرڻ کانپوءِ انھن (ٻانھين) کي الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(24,34,'24:34','۽ بيشڪ اوھان ڏانھن چٽيون نشانيون ۽ جيڪي اوھان کان اڳ گذري ويا تن جا مثال ۽ پرھيزگارن لاءِ نصيحت لاٿي سون.');
INSERT INTO "amroti" VALUES(24,35,'24:35','الله آسمانن ۽ زمين کي روشن کي ڪندڙ آھي، سندس نُور جو مثال انھيءَ جاري وانگر آھي جنھن ۾ ڏيو ھجي، ڏيو قنديل ۾ ھجي، اُھو قنديل ڄڻڪ چمڪندڙ ستارو آھي جو انھيءَ برڪت واري زيتون جي وڻ (جي تيل) مان ٻارجي جو نڪي اوڀر ۽ نڪي اولھ (ڄمندو) آھي سندس تيل جيتوڻيڪ باھ نه پھتي ھجيس ته به سوجھري ڪرڻ کي ويجھو آھي، نُور تي نُور آھي، الله جنھن کي گھرندو آھي تنھنکي پنھنجي نُور سان سِڌو رستو ڏيکاريندو آھي، ۽ الله ماڻھن لاءِ مثال بيان ڪندو آھي، ۽ الله سڀڪنھن شيء کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(24,36,'24:36','(اُنھي نُور جو ھنڌ) اُنھن گھرن ۾ (آھي) جن لاءِ الله حُڪم ڪيو آھي ته انھن جي تعظيم ڪجي ۽ انھن ۾ سندس نالو ياد ڪجي انھن ۾ صبح ۽ سانجھيءَ جو الله جي پاڪائي بيان ڪندا آھن.');
INSERT INTO "amroti" VALUES(24,37,'24:37','(يعني اھڙا) مڙس جو انھن کي واپار ۽ نڪي سودو الله جي ياد ڪرڻ ۽ نماز پڙھڻ ۽ زڪوٰة ڏيڻ کان جھليندو آھي، انھي ڏينھن کان ڊڄندا آھن جنھن ۾ دليون ۽ اکيون اُبتيون ٿينديون.');
INSERT INTO "amroti" VALUES(24,38,'24:38','ھن لاءِ ته جيڪي تمام چڱا عمل ڪيائون تن جوالله کين بدلو ڏئي ۽ کين پنھنجي فضل سان وڌيڪ به ڏئي، ۽ الله جنھن کي گھرندو آھي (تنھن کي) اَڻ ڳڻي روزي ڏيندو آھي.');
INSERT INTO "amroti" VALUES(24,39,'24:39','۽ جن ڪُفر ڪيو تن جا اعمال رڻ پَٽ جي رُڃ وانگر آھن اُڃو اُن کي پاڻي ڀانئي، تان جو جڏھن اُن وٽ پُھچي (تڏھن) اُن کي ڪا شيء نه لھي ۽ الله کي ئي پاڻ وٽ لھي پوءِ اُھو کيس سندس حساب پورو پُھچائي ڏئي، ۽ الله جلد حساب وٺندڙ آھي.');
INSERT INTO "amroti" VALUES(24,40,'24:40','يا (ڪافر جي عملن جو مثال) اونداھين وانگر آھي جو اُونھيءَ درياءَ ۾ لھر جي مٿان لھر ويڙھيس مٿانئس جھُڙ ھُجي، ھڪ ٻئي جي مٿان (اُھي) اونداھيون ھُجن، جڏھن پنھنجو ھٿ ڪڍي (تڏھن) اُن کي ڏسي نه سگھي، ۽ جنھن کي الله سوجھرو نه ڏنو تنھن کي ڪو سوجھرو ڪونھي.');
INSERT INTO "amroti" VALUES(24,41,'24:41','نه ڏٺو اٿئي ڇا ته جيڪي آسمانن ۽ زمين ۾ آھن سي ۽ کليل پرن وارا پکي الله جي پاڪائي بيان ڪندا آھن، بيشڪ سڀئي پنھنجي نماز ۽ پنھنجي تسبيح (پڙھڻ جي طرح) کان واقف آھن، ۽ جيڪي ڪندا آھن سو الله ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(24,42,'24:42','آسمانن ۽ زمين جي بادشاھت الله جي آھي، ۽ الله ڏانھن ورڻو آھي.');
INSERT INTO "amroti" VALUES(24,43,'24:43','(اي پيغمبر) نه ڏٺو اٿئي ڇا ته الله ڪڪرن کي ھلائيندو آھي وري انھن کي پاڻ ۾ گڏيندو آھي وري کين کين تھن تي تھ ڪندو آھي پوءِ ڏسندو آھين ته ڦڙي سندس وچ مان نڪرندي آھي، ۽ آسمان کان اُتي جي ڳڙن جي ٽڪرن مان ڳڙو وسائيندو آھي پوءِ جنھن کي وڻيس تنھن کي اُھو پُھچائيندو ۽ جنھن کان وڻيس تنھن کان اُھو ٽاريندو آھي، اُن (ڪڪر) جي وِڄ جو چمڪاٽ (ڄڻ ته) اجھو اکيون ٿو نئي.');
INSERT INTO "amroti" VALUES(24,44,'24:44','الله رات ۽ ڏينھن کي مٽائيندو آھي، بيشڪ اُن ۾ ڏسڻ وارن لاءِ عبرت آھي.');
INSERT INTO "amroti" VALUES(24,45,'24:45','۽ الله سڀڪو جاندار پاڻيءَ مان بڻايو آھي، پوءِ منجھائن ڪي پيٽ ڀر ھلندا آھن، ۽ منجھائن ڪي ٻن پيرن تي ھلندا آھن، ۽ منجھائن ڪي چئن (پيرن) تي ھلندا آھن، الله جيڪي گھرندو آھي سو پيدا ڪندو آھي، ڇوته الله سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(24,46,'24:46','بيشڪ پڌريون آيتون نازل ڪيوسون، ۽ الله جنھن کي گھري تنھن کي سڌو رستو ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(24,47,'24:47','۽ (منافق) چوندا آھن ته الله تي ۽ جنھن پيغمبر تي ايمان آندوسون ۽ فرمانبرداري ڪئي سون وري اُن کانپوءِ منجھانئن ھڪ ٽولي ڦري وڃي ٿي، ۽ اُھي اِيمان وارا نه آھن.');
INSERT INTO "amroti" VALUES(24,48,'24:48','۽ جڏھن الله ۽ سندس پيغمبر ڏانھن سڏبا آھن ته سندن وچ ۾ (پيغمبر) نبيرو ڪري (تڏھن) اُمالڪ منجھانئن ھڪ ٽولي منھن موڙيندڙ آھي.');
INSERT INTO "amroti" VALUES(24,49,'24:49','۽ جيڪڏھن (ڪنھن تي) سندن حق ھوندو آھي ته پيغمبر ڏانھن ڪنڌ نوائي ايندا آھن.');
INSERT INTO "amroti" VALUES(24,50,'24:50','سندين دلين ۾ بيماري آھي ڇا يا شڪ ۾ پيا آھن يا ڊڄندا آھن ته الله ۽ سندس پيغمبر مٿن ظلم ڪندو؟ (نه!) بلڪ اُھي پاڻ بي انصاف آھن.');
INSERT INTO "amroti" VALUES(24,51,'24:51','جڏھن مؤمنن کي الله ۽ سندس پيغمبر ڏانھن سڏيو آھي ته (پيغمبر) سندن وچ ۾ نبيرو ڪري تڏھن انھن جو چوڻ رڳو ھيءُ ھوندو آھي ته ٻڌوسون ۽ حُڪم مڃيوسون، ۽ اُھي ڇٽل آھن.');
INSERT INTO "amroti" VALUES(24,52,'24:52','۽ جيڪو الله ۽ سندس پيغمبر جو چيو مڃيندو ۽ الله کان ڊڄندو ۽ اُن جي نافرمانيءَ کان بچندو پوءِ اُھي مراد ماڻيندڙ آھن.');
INSERT INTO "amroti" VALUES(24,53,'24:53','۽ الله جا وڏا وڏا قسم پڪيءَ طرح کڻندا آھن ته جيڪڏھن کين حُڪم ڪرين ته ضرور (سڀ ڦٽي ڪري پنھنجي ديس مان) نڪرندا، (اي پيغمبر کين) چؤ ته قسم نه کڻو، دستوري فرمانبرداري (ڪريو)، ڇوته جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(24,54,'24:54','چؤ ته الله جي فرمانبرداري ڪريو ۽ پيغمبر جي فرمانبرداري ڪريو، پوءِ جيڪڏھن ڦرندؤ ته جيڪي پيغمبر تي (الله جي حُڪمن پُھچائڻ جو حق) رکيو ويو تنھن جو ذمو مٿس ئي آھي ۽ جيڪي اوھان تي رکيو ويو تنھن تي عمل ڪرڻ جو حق اوھان تي ئي آھي، ۽ جيڪڏھن سندس چيو مڃيندؤ ته ھدايت وارا ٿيندؤ، ۽ پيغمبر تي رڳو پڌرو پيغام پُھچائڻ لازم آھي.');
INSERT INTO "amroti" VALUES(24,55,'24:55','اوھان مان جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي الله انجام ڏنو آھي ته مُلڪ ۾ ضرور کين خليفو ڪندو جئن کانئن اڳين کي حاڪم ڪيائين، ۽ سندن انھيءَ دين کي جو انھن لاءِ پسند ڪيو اٿس انھن لاءِ ضرور پختو ڪندو ۽ سندس ڊڄ کان پوءِ کين مٽائي اَمن ڏيندو، (اُھي) منھنجي عبادت ڪندا رھندا، (۽) مون سان ڪنھن شيء کي شريڪ نه ڪندا، ۽ جيڪي ان کانپوءِ ناشڪري ڪندا سي ئي بي دين آھن.');
INSERT INTO "amroti" VALUES(24,56,'24:56','۽ نماز پڙھو ۽ زڪوٰة ڏيو ۽ پيغمبر جي فرمانبرداري ڪريو ته مانَ اوھان تي رحم ڪيو وڃي.');
INSERT INTO "amroti" VALUES(24,57,'24:57','ڪافرن کي نه ڀانءِ ته زمين ۾ ٿَڪائيندڙ آھن، ۽ سندين جاءِ دوزخ آھي، البت اُھو موٽڻ جو ھنڌ بڇڙو آھي.');
INSERT INTO "amroti" VALUES(24,58,'24:58','اي ايمان وارؤ اوھان جي ٻانھن ۽ جيڪي اوھان مان بلوغت کي نه پُھتا آھن تن کي ٽي وقت (گھرن ۾ اچڻ لاءِ) اوھان کان موڪلائڻ گھرجي، فجر جي نماز کان اڳ ۽ منجھند کي جو اوھين ڪپڙا لاھي رکندا آھيو ۽ سومھاڻي جي نماز کان پوءِ، (جو اھي) اوھان لاءِ ٽي اُگھڙ جا وقت آھن، انھن کان پوءِ ھڪ ٻئي وٽ اوھان جي اچڻ وڃڻ کان نڪي اوھان تي ۽ نڪي اُنھن تي گناھ آھي، اھڙي طرح الله اوھان لاءِ حُڪم بيان ڪري ٿو، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(24,59,'24:59','۽ جڏھن اوھان مان ٻار بلوغت کي پھچن تڏھن کين اھڙي طرح موڪل وٺڻ گھرجي جھڙي طرح انھن (ڇوڪرن) کان اڳيان (وڏا) موڪل وٺندا آھن، اھڙي طرح الله اوھان لاءِ پنھنجا حُڪم بيان فرمائي ٿو، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(24,60,'24:60','۽ جيڪي ٻڍيون زالون پرڻي جو ڪو آسرو نه رکنديون ھجن سي جيڪڏھن سينگارجي پڌري نه ڪرڻ جي ارادي سان پنھنجون چادرون نه ڍڪين ته مٿن گناھ ڪونھي، ۽ جيڪڏھن (اُن کان به) بچن ته اُنھن لاءِ ڀلو آھي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(24,61,'24:61','انڌي تي ڪا اوکائي نه آھي ۽ نڪي منڊي تي اوکائي ۽ نڪي بيمار تي ڪا اوکائي ۽ نڪي خود اوھان (ماڻھن) تي (ڪا اوکائي) آھي جي اوھين پنھنجن گھرن (مان) يا پنھنجن پيُن جي گھرن يا پنھنجن مائرن جي گھرن يا پنھنجي ڀائرن جي گھرن يا پنھنجن ڀينرن جي گھرن يا پنھنجن چاچن جي گھرن يا پنھنجن پڦين جي گھرن يا پنھنجن مامن جي گھرن يا پنھنجي ماسين جي گھرن يا جن جي گھرن جون ڪنجيون اوھان جي ھٿ آھن تن ۽ پنھنجن دوستن جي گھرن مان کائو، گڏجي (کائڻ) يا ڌار ڌار ٿي کائڻ ۾ اوھان تي ڪو گناھ ڪونھي، پوءِ جڏھن گھرن ۾ گھڙو تڏھن پنھنجن ماڻھن تي سلام ڏيو جو (اھا) الله وٽان برڪت واري سٺي دُعا آھي، اھڙي طرح الله اوھان لاءِ حُڪم بيان ڪندو آھي ته مانَ اوھين سمجھو.');
INSERT INTO "amroti" VALUES(24,62,'24:62','مؤمن رڳا اُھي آھن جن الله ۽ سندس پيغمبر تي ايمان آندو آھي ۽ جڏھن گڏ ڪَرڻ واري ڪنھن ڪم ۾ ساڻس ھوندا آھن (تڏھن) ايسين ڪيڏي نه ويندا آھن جيسين کانئس موڪل (نه) وٺندا آھن، بيشڪ (اي پيغمبر) جيڪي توکان موڪلائيندا آھن سي اُھي آھن جي الله ۽ سندس پيغمبر کي مڃيندا آھن، پوءِ جڏھن پنھنجن ڪن ڪمن لاءِ توکان موڪلائين تڏھن انھن مان جنھن لاءِ گھرين تنھن کي موڪل ڏي ۽ اُنھن لاءِ الله کان بخشش گُھر، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(24,63,'24:63','اوھين پاڻ ۾ پيغمبر کي سڏڻ اھڙي طرح نه ڀانيو جھڙيءَ طرح اوھان جو ھڪ ٻئي کي سڏڻ آھي بيشڪ الله انھن کي ڄاڻندو آھي جيڪي اوھان مان ٻي جي اوٽ ڪري کسڪي ويندا آھن، پوءِ جيڪي سندس حڪم جي ابتڙ ھلندا آھن تن کي پاڻ تي بلا پھچڻ کان يا پاڻ تي ڪنھن ڏکوئيندڙ عذاب پھچڻ کان ڊڄڻ گھرجي.');
INSERT INTO "amroti" VALUES(24,64,'24:64','خبردار ھجو جيڪي آسمانن ۽ زمين ۾ آھي سو بيشڪ الله جو آھي، جنھن (حال) تي اوھين آھيو تنھن کي بيشڪ الله ڄاڻندو آھي، ۽ جنھن ڏينھن ڏانھس موٽايا ويندا (تنھن ڏينھن) جيڪي ڪمايائون تنھنجي کين سُڌ ڏيندو، ۽ الله سڀڪنھن شيء کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(25,1,'25:1','اھو (الله) وڏو برڪت وارو آھي جنھن پنھنجي ٻانھي تي (حق ۽ باطل جو) فرق ڪندڙ (قرآن) ھن لاءِ نازل ڪيو ته جھان وارن لاءِ ڊيڄاريندڙ ھجي.');
INSERT INTO "amroti" VALUES(25,2,'25:2','(اُھو آھي) جنھن کي آسمانن ۽ زمين جي بادشاھي آھي ۽ (پاڻ لاءِ) نڪو اولاد ورتائين ۽ نڪي بادشاھيءَ ۾ ساڻس ڪو شريڪ آھي ۽ سڀڪنھن شيء کي بڻايائين پوءِ ان جو اندازو پوري طرح ٺھرايائين.');
INSERT INTO "amroti" VALUES(25,3,'25:3','۽ (ڪافرن) الله کانسواءِ اُھي معبود ورتا آھن جو ڪجھ بڻائي نه سگھندا آھن ۽ اُھي پاڻ پيدا ڪيل آھن ۽ پاڻ لاءِ نڪي ڏک ۽ نڪي سُک جو اختيار اٿن ۽ نڪي موت ۽ نڪي حياتي ۽ نه وري جيئري ٿيڻ جو اختيار اٿن.');
INSERT INTO "amroti" VALUES(25,4,'25:4','۽ ڪافر چوندا آھن ته ھيءُ (قرآن) رڳو ڪوڙو قصّو آھي جو اُھو پاڻون ٺاھيو اٿس ۽ اُن (جي ٺاھڻ) تي ٻي قوم به کيس مدد ڏني آھي، (اھڙي طرح چوڻ سان) بيشڪ اُنھن ظلم ۽ ڪوڙ (ڪم ۾) آندو آھي.');
INSERT INTO "amroti" VALUES(25,5,'25:5','۽ چيائون ته (ھي قرآن) اڳين جا قصّا آھن جو اُھي مٿس صُبح ۽ سانجھيءَ پڙھبا آھن.');
INSERT INTO "amroti" VALUES(25,6,'25:6','(اي پيغمبر کين) چؤ ته اُھو (قرآن) اُنھي (الله) نازل ڪيو آھي جيڪو آسمانن ۽ زمين جو ڳجھ ڄاڻندو آھي، بيشڪ اُھو بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(25,7,'25:7','۽ (ڪافر) چون ٿا ته ھن پيغمبر کي ڇا (ٿيو) آھي جو طعام کائيندو آھي ۽ بازارين ۾ گھمندو آھي، ڏانھس ملائڪ ڇو نه نازل ڪيو ويو آھي ته اُھو ساڻس (گڏ) ڊيڄاريندڙ ھجي.');
INSERT INTO "amroti" VALUES(25,8,'25:8','يا ڏانھس (آسمان کان) خزانو ڇو نه اُڇليو ويو يا اُن کي ڪو باغ ڇو نه آھي جو منجھانئس کائي، ۽ ظالم چوندا آھن ته اوھين رڳو جادو ڪيل ماڻھو جي تابعداري ڪندا آھيو.');
INSERT INTO "amroti" VALUES(25,9,'25:9','(اي پيغمبر) ڏس ته تو لاءِ ڪھڙي طرح جون ڳالھيون ڪندا آھن پوءِ (اُھي) گمراھ ٿيا ۽ (ھاڻي) ڪا واٽ لھي نٿا سگھن.');
INSERT INTO "amroti" VALUES(25,10,'25:10','اُھو (الله) وڏو برڪت وارو آھي جيڪڏھن گھري ته (دنيا ۾) تولاءِ ھن (چيل) کان ڀلا باغ جن جي ھيٺان واھيون پيون وھن ڏيئي ۽ توکي وڏيون ماڙيون (به) ڏئي.');
INSERT INTO "amroti" VALUES(25,11,'25:11','بلڪ قيامت کي ڪُوڙو ڀانيو اٿن ۽ جنھن قيامت کي ڪوڙو ڀانيو تنھن لاءِ دوزخ تيار ڪيو اٿئون.');
INSERT INTO "amroti" VALUES(25,12,'25:12','جڏھن (دوزخ) انھن کي ڏوراھين پنڌ کان ڏسندو (تڏھن) اُن جون ڪاوڙ جون ھڪلون ۽ گجڪار ٻڌندا.');
INSERT INTO "amroti" VALUES(25,13,'25:13','۽ جڏھن اُتان ھٿ پير جڪڙيل سوڙھي ھنڌ سٽيا ويندا (تڏھن) اُتي (پاڻ لاءِ) ھلاڪ ٿيڻ کي سڏيندا.');
INSERT INTO "amroti" VALUES(25,14,'25:14','(چيو ويندن ته) اڄ اوھين ھڪ ھلاڪ ٿيڻ کي نه پڪاريو پر گھڻن ھلاڪتن کي پُڪاريو.');
INSERT INTO "amroti" VALUES(25,15,'25:15','(اي پيغمبر کين) چؤ ته اُھو ڀلو آھي يا اُھو ھميشه وارو بھشت جنھن جو پرھيزگارن کي انجام ڏنو ويو آھي؟ جو اُنھن لاءِ بدلو ۽ موٽڻ جو ھنڌ ھوندو.');
INSERT INTO "amroti" VALUES(25,16,'25:16','جيڪي گھرندا سو انھن لاءِ منجھس سدائين (موجود) ھوندو، تنھنجي پالڻھار کان (اھو) وعدو گھريل آھي.');
INSERT INTO "amroti" VALUES(25,17,'25:17','۽ اُن ڏينھن کين ۽ جن کي الله کانسواءِ پوڄيندا آھن تن کي الله گڏ ڪندو پوءِ چوندو ته اوھان ھنن منھنجن ٻانھن کي گمراھ ڪيو ھو يا اُھي پاڻ واٽ کان گمراھ ٿيا ھوا؟');
INSERT INTO "amroti" VALUES(25,18,'25:18','چوندا ته تنھنجي پاڪائي بيان ٿا ڪريون تو کانسواءِ اسان کي ڪو دوست وٺڻ نه جڳائيندو ھو پر انھن کي ۽ سندن پين ڏاڏن کي آسودو ڪيئي تان جو نصيحت وساريائون، ۽ ھلاڪ ٿيل قوم ٿيا.');
INSERT INTO "amroti" VALUES(25,19,'25:19','پوءِ (چونداسون ته اي ڪافرؤ) بيشڪ جيڪي (ڳالھيون ھنن معبودن بابت) چَيوَ ٿي تن ۾ اُنھن اوھان کي ڪُوڙو ڪيو پوءِ (اوھين) نڪي عذاب ٽاري سگھو ٿا ۽ نڪي مدد وٺي سگھو ٿا، ۽ اوھان مان جنھن ظلم ڪيو ھوندو تنھن کي وڏو عذاب چکائينداسون.');
INSERT INTO "amroti" VALUES(25,20,'25:20','۽ توکان اڳ پيغمبرن مان جيترا به موڪلياسون سي (به تو ئي وانگر) طعام کائيندا ھوا ۽ بازارين ۾ گھمندا ھوا، ۽ اوھان کي (پاڻ ۾) ھڪ ٻئي لاءِ پرک ڪيو اٿئون، (ته ڏسون ته) صبر ڪريو ٿا (يا نه)؟ ۽ تنھنجو پالڻھار ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(25,21,'25:21','۽ جيڪي اسان جي ملڻ جي اُميد نه رکندا آھن سي چوندا آھن ته اسان تي ڇو نه ملائڪ نازل ڪيا ويا يا پنھنجي پالڻھار کي (ڇو نه ٿا) ڏسون؟ بيشڪ اُنھن پنھنجن جيئن ۾ وڏائي ڪئي ۽ تمام وڏو ھٺ ڪيائون.');
INSERT INTO "amroti" VALUES(25,22,'25:22','جنھن ڏينھن ملائڪن کي ڏسندا تنھن ڏينھن ڏوھارين لاءِ ڪا خوشخبري نه آھي ۽ چوندا ته (الله ڪري اوھين شال) گھڻو پري ھجو.');
INSERT INTO "amroti" VALUES(25,23,'25:23','۽ جيڪو ڪو عمل ڪيائون تنھن ڏانھن اسين مھاڙ ڪنداسون پوءِ اُن کي پڻي ڪري ڇڏينداسون.');
INSERT INTO "amroti" VALUES(25,24,'25:24','اُنھيءَ ڏينھن بھشتي رھڻ جي جاءِ ڪري ڀلا ۽ آرام ڪرڻ جي جاءِ ڪري تمام چڱا ھوندا.');
INSERT INTO "amroti" VALUES(25,25,'25:25','۽ جنھن ڏينھن آسمان ڪڪرن سان ڦاٽندو ۽ ملائڪ لڳو لڳ لاھبا.');
INSERT INTO "amroti" VALUES(25,26,'25:26','اُن ڏينھن سچي حڪومت الله جي آھي، ۽ اُھو ڏينھن ڪافرن تي سخت ٿيندو.');
INSERT INTO "amroti" VALUES(25,27,'25:27','۽ جنھن ڏينھن ظالم پنھنجا ٻئي ھٿ چٻيندو چوندو ته ھَيء اَرمان (جيڪر) پيغمبر سان گڏ واٽ وٺان ھا (ته چڱو ھو).');
INSERT INTO "amroti" VALUES(25,28,'25:28','ھَيء ھَيء ارمان جيڪر فلاڻي کي دوست نه وٺان ھا.');
INSERT INTO "amroti" VALUES(25,29,'25:29','بيشڪ جڏھن مون وٽ نصيحت آئي تنھن پڄاڻا ھن مون کي اُن کان گمراہ ڪيو، ۽ شيطان ماڻھوءَ کي (وقت تي) دغا ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(25,30,'25:30','۽ پيغمبر چوندو ته اي منھنجا پالڻھاربيشڪ منھنجي قوم ھن قرآن کي ڇڏي ڏنو ھو.');
INSERT INTO "amroti" VALUES(25,31,'25:31','۽ اھڙيءَ طرح سڀڪنھن پيغمبر لاءِ ڏوھارين مان ويري بڻاياسون، ۽ تنھنجو پالڻھار ھدايت ڪرڻ وارو ۽ مدد ڏيڻ وارو ڪافي آھي.');
INSERT INTO "amroti" VALUES(25,32,'25:32','۽ ڪافر چون ٿا ته پيغمبر تي سارو قرآن ھڪ ڀيري ڇو نه نازل ڪيو ويو؟ اھڙي طرح (ٿورو ٿورو ڪري لاٿوسون)، ته اُن سان تنھنجي دل کي مُحڪم ڪريون ۽ اُھو آھستو آھستو پڙھي ٻڌايوسون.');
INSERT INTO "amroti" VALUES(25,33,'25:33','۽ (ڪافر) تو وٽ اھڙي ڪا حُجّت نه ٿا آڻين جنھنجو اسين سچو (جواب) ۽ ڏاڍو چڱو بيان تو وٽ نه ٿا آڻيون.');
INSERT INTO "amroti" VALUES(25,34,'25:34','جيڪي پنھنجي منھن ڀر دوزخ ڏانھن گڏ ڪيا ويندا اُھي جاءِ جي ڪري تمام بُڇڙا ۽ واٽ کي بلڪل وڃائيندڙ آھن.');
INSERT INTO "amroti" VALUES(25,35,'25:35','۽ بيشڪ مُوسىٰ کي ڪتاب ڏنوسون ۽ ساڻس سندس ڀاءُ ھارون وزير ڪيوسون.');
INSERT INTO "amroti" VALUES(25,36,'25:36','پوءِ چيوسون ته (ٻئي) اُنھي قوم ڏانھن وڃو جن اسان جي آيتن کي ڪوڙ ڀانيو، پوءِ (آخر) کين پاڙئون پٽيوسون.');
INSERT INTO "amroti" VALUES(25,37,'25:37','۽ نوح جي قوم جڏھن پيغمبرن کي ڪُوڙو ڄاتو (تڏھن) کين ٻوڙيوسون ۽ کين ماڻھن لاءِ نشاني ڪئي سون، ۽ ظالمن لاءِ ڏکوئيندڙ عذاب تيار ڪيوسون.');
INSERT INTO "amroti" VALUES(25,38,'25:38','۽ عادين ۽ ثمودين ۽ رَس وارن ۽ اُنھن جي وچ ۾ گھڻن جُڳن کي (ناس ڪيوسون).');
INSERT INTO "amroti" VALUES(25,39,'25:39','۽ ھر ھڪ کي بيان ڪري ٻڌايوسون، ۽ سڀني کي ناس ڪيوسون (جئن) ناس ڪرڻ گھرجي.');
INSERT INTO "amroti" VALUES(25,40,'25:40','۽ بيشڪ (ڪافر) اُن ڳوٺ وٽان ٿي آيا آھن جنھن تي (پٿرن جو) بڇڙو مينھن وسايو ويو ھو، اُن کي نه ٿي ڏٺائون ڇا؟ بلڪ اُھي وري جيئري ٿيڻ جي اُميد نه رکندا ھوا.');
INSERT INTO "amroti" VALUES(25,41,'25:41','۽ جڏھن توکي ڏسن ٿا (تڏھن) توکي رڳو ٺـٺولي ڪري وٺن ٿا، (چون ٿا ته) ھيءُ اُھو (شخص) آھي ڇا جنھن کي الله پيغمبر ڪري موڪليو آھي.');
INSERT INTO "amroti" VALUES(25,42,'25:42','بيشڪ اسان کي پنھنجن بتن (جي پوڄا) کان تڏھن ئي ڀلائي ڇڏي ھا جيڪڏھن اسين مٿن مُحڪم نه رھون ھا، ۽ جڏھن عذاب ڏسندا (تڏھن) ڄاڻندا ته واٽ کي بلڪل وڃائيندڙ ڪير آھي.');
INSERT INTO "amroti" VALUES(25,43,'25:43','(اي پيغمبر) تو اُن کي ڏٺو جنھن پنھنجي سَڌ کي پنھنجو معبود ڪري ورتو؟ پوءِ تون مٿس ڪو نگھبان ٿيندين ڇا؟');
INSERT INTO "amroti" VALUES(25,44,'25:44','ڀانيو اٿئي ڇا ته منجھانئن گھڻا ٻڌن ٿا يا سمجھن ٿا؟ اِھي رڳو ڍورن وانگر آھن بلڪ اُھي واٽ کي بلڪل وڃائيندڙ آھن.');
INSERT INTO "amroti" VALUES(25,45,'25:45','(اي پيغمبر) پنھنجي پالڻھار ڏانھن نه ڏٺو اٿئي ڇا ته پاڇي کي ڪيئن ڊگھو ڪيائين؟ جيڪڏھن گھري ھا ته اُن کي ٺھرائي ڇڏي ھا، وري سج کي مٿس (پنھنجي قدرت جو) دليل ڪيوسون.');
INSERT INTO "amroti" VALUES(25,46,'25:46','وري اُن (پاڇي) کي پاڻ ڏانھن ھوريان ھوريان سوڙھو ڪيوسون (جئن) سوڙھو ڪرڻ گھرجي.');
INSERT INTO "amroti" VALUES(25,47,'25:47','۽ (الله) اُھو آھي جنھن اوھان لاءِ رات کي پردو ۽ ننڊ کي آرام ڪيو ۽ ڏينھن کي اُٿڻ جو وقت ڪيائين.');
INSERT INTO "amroti" VALUES(25,48,'25:48','۽ (الله) اُھو آھي جنھن پنھنجي ٻاجھ (يعني مينھن) کان اڳي وائُن کي خوشخبري ڏيندڙ ڪري موڪليو، ۽ آسمان مان پاڪ پاڻي (ھن لاءِ) وسايوسون');
INSERT INTO "amroti" VALUES(25,49,'25:49','ته اُن سان ويران شھر کي آباد ڪريون ۽ ته پنھنجن پيدا ڪيل ڍورن ۽ گھڻن ماڻھن کي اُھو پياريون.');
INSERT INTO "amroti" VALUES(25,50,'25:50','۽ بيشڪ اُن (مينھن) کي سندن وچ ۾ ھن ڪري ڦيرائيندڙ آھيون ته نصيحت وٺن، پوءِ گھڻا ماڻھو بي شڪريءَ کانسواءِ نٿا رھن.');
INSERT INTO "amroti" VALUES(25,51,'25:51','۽ جيڪڏھن گھرون ھا ته سڀڪنھن ڳوٺ ۾ ھڪ ڊيڄاريندڙ موڪليون ھا.');
INSERT INTO "amroti" VALUES(25,52,'25:52','پوءِ تون ڪافرن جو چيو نه مڃ ۽ اُن (قرآن) جي حُڪم سان (مقابلي طرح) ساڻن وڏو جھاد ڪر.');
INSERT INTO "amroti" VALUES(25,53,'25:53','۽ (الله) اُھو آھي جنھن ٻن دريائن کي پاڻ ۾ ملايو جو ھي (ھڪڙو) مِٺو اُڃ لاھيندڙ ۽ ھيءُ (ٻيو) لوڻاٺيو کارو آھي، ۽ سندن وچ ۾ اوٽ ۽ مُحڪم پردو بڻايائين.');
INSERT INTO "amroti" VALUES(25,54,'25:54','۽ (الله) اُھو آھي جنھن پاڻيءَ مان ماڻھوءَ کي بڻايو پوءِ کيس پيڙھي ۽ سيڻپيءَ جي مائٽي وارو ڪيائين، ۽ تنھنجو پالڻھار وس وارو آھي.');
INSERT INTO "amroti" VALUES(25,55,'25:55','۽ الله کان سواءِ اھڙن جي پوڄا ڪندا آھن جي کين نڪي سُک ڏين ۽ نڪي کين ڏک پھچائن، ۽ ڪافر پنھنجي پالڻھار کي پُٺي ڏيڻ وارو آھي.');
INSERT INTO "amroti" VALUES(25,56,'25:56','۽ (اي پيغمبر) اسان توکي رڳو خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو آھي.');
INSERT INTO "amroti" VALUES(25,57,'25:57','(کين) چؤ ته آءٌ اوھان کان اُن (قرآن جي پھچائڻ) تي ڪو اُجورو نٿو گھران پر جيڪو پنھنجي پالڻھار ڏانھن واٽ وٺڻ گھري (سو ڀلي ته وٺي).');
INSERT INTO "amroti" VALUES(25,58,'25:58','۽ (اي پيغمبر تون اُنھيءَ الله) جيئري تي ڀروسو ڪر جيڪو (ڪڏھن) نه مرندو ۽ سنديس ساراہ سان گڏ پاڪائي بيان ڪر، ۽ اُھو پنھنجن ٻانھن جي گناھن جي خبر رکندڙ ڪافي آھي.');
INSERT INTO "amroti" VALUES(25,59,'25:59','جنھن آسمانن ۽ زمين کي ۽ جيڪي سندن وچ ۾ آھي (سو) ڇھن ڏينھن ۾ بڻايو وري عرش تي قائم ٿيو، (اُھو) ٻاجھارو آھي پوءِ سندس (صفتون) ڪنھن خبر رکندڙ کان پُڇ.');
INSERT INTO "amroti" VALUES(25,60,'25:60','۽ جڏھن کين چئبو آھي ته رحمٰن کي سجدو ڪريو، (تڏھن) چوندا آھن ته رَحمٰن ڇا آھي جنھن لاءِ اسان کي حُڪم ٿو ڪرين؟ تنھن کي سجدو ڪريون ڇا؟ ۽ (اُنھي ڳالھ ھٿئون) سندن نفرت وڌائي.');
INSERT INTO "amroti" VALUES(25,61,'25:61','اُھو (الله) وڏي برڪت وارو آھي جنھن آسمان ۾ برج بڻايائين ۽ منجھس ڏيو (يعني سِج) ۽ چمڪندڙ چنڊ بڻايو.');
INSERT INTO "amroti" VALUES(25,62,'25:62','۽ (الله) اُھو آھي جنھن رات ۽ ڏينھن کي ھڪ ٻئي جي پوئتان ايندڙ اُنھيءَ (ماڻھو) لاءِ بڻايو جيڪو نصيحت وٺڻ گھري يا شڪرانو گھري.');
INSERT INTO "amroti" VALUES(25,63,'25:63','۽ ٻاجھاري (الله) جا ٻانھا اُھي آھن جيڪي زمين تي نِوَڙت سان گھمندا آھن ۽ جڏھن بي عقل ساڻن ڳالھائيندا آھن (تڏھن) چوندا آھن ته سلامت ھجو (شال).');
INSERT INTO "amroti" VALUES(25,64,'25:64','۽ اُھي جيڪي پنھنجي پالڻھار کي سجدو ڪندڙ ۽ قيام ڪندڙ ٿي راتيون گذاريندا آھن.');
INSERT INTO "amroti" VALUES(25,65,'25:65','۽ اُھي جيڪي چوندا آھن ته اي اسان جا پالڻھار اسان کان دوزخ جو عذاب ٽار، بيشڪ دوزخ جو عذاب (وڏي) چَٽي آھي.');
INSERT INTO "amroti" VALUES(25,66,'25:66','بيشڪ اُھو آرام ڪرڻ جو ھنڌ ۽ رھڻ جو ھنڌ بڇڙو آھي.');
INSERT INTO "amroti" VALUES(25,67,'25:67','۽ اُھي جڏھن خرچ ڪندا آھن (تڏھن) نڪي اجايو خرچيندا آھن ۽ نڪي تنگي ڪندا آھن ۽ انھن (ٻنھي) جي وچ ۾ پورا ھلندا آھن.');
INSERT INTO "amroti" VALUES(25,68,'25:68','۽ اُھي جيڪي الله ساڻ ڪنھن ٻئي معبود کي نه پوڄيندا آھن ۽ نڪي اھڙي جيءَ کي ماريندا آھن جنھنجو حق کانسواءِ مارڻ الله حرام ڪيو آھي ۽ نڪي زنا ڪندا آھن، ۽ جيڪو اُھو ڪم ڪندو سو سخت عذاب لھندو.');
INSERT INTO "amroti" VALUES(25,69,'25:69','قيامت جي ڏينھن اُن کي ٻيڻو عذاب ڪبو ۽ منجھس سدائين خوار رھندو.');
INSERT INTO "amroti" VALUES(25,70,'25:70','پر جن توبه ڪئي ۽ ايمان آندو ۽ چڱا ڪم ڪيا سي اُھي آھن جن جون مدايون الله ڦيرائي نيڪيون ڪندو، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(25,71,'25:71','۽ جيڪو توبه ڪري ۽ چڱا ڪم ڪري سو بيشڪ الله ڏانھن وري موٽي ٿو.');
INSERT INTO "amroti" VALUES(25,72,'25:72','۽ اُھي جيڪي ڪوڙي شاھدي نه ڏيندا آھن ۽ جڏھن بيھودي ڪم تي لنگھندا آھن (تڏھن چڱي ريت) ڀلارن وانگر لنگھندا آھن.');
INSERT INTO "amroti" VALUES(25,73,'25:73','۽ اُھي جو جڏھن کين سندن پالڻھار جي آيتن سان نصيحت ڏبي آھي (ته) اُن (جي ٻڌڻ) سان ٻوڙا ۽ انڌا ٿي نه ڪرندا آھن.');
INSERT INTO "amroti" VALUES(25,74,'25:74','۽ اُھي جيڪي چوندا آھن ته اي اسانجا پالڻھار اسان جي زالن ۽ اسان جي اولاد مان اسان کي اکين جو ٺار بخش ۽ اسان کي پرھيزگارن جو اِمام ڪر.');
INSERT INTO "amroti" VALUES(25,75,'25:75','اِھي (اُھي) آھن جن کي سندن صبر سببان وڏيون ماڙيون عوض ۾ ڏبيون ۽ اُن ۾ چڱي دُعا ۽ سلام پھچائبن.');
INSERT INTO "amroti" VALUES(25,76,'25:76','منجھس سدائين رھڻ وارا آھن، اُھو آرام ڪرڻ جو ھنڌ چڱو ۽ رھڻ جو ھنڌ چڱو آھي.');
INSERT INTO "amroti" VALUES(25,77,'25:77','(اي پيغمبر کين) چؤ ته جيڪڏھن (الله کي) اوھان جو سڏڻ نه ھجي ته منھنجي پالڻھار کي اوھان جي ڪا پرواھ نه آھي، بيشڪ اوھان (پيغمبر کي) ڪوڙو ڀانيو تنھنڪري سگھو اوھان لاءِ عذاب لازم ٿيندو.');
INSERT INTO "amroti" VALUES(26,1,'26:1','طٰسٓمّٓ.');
INSERT INTO "amroti" VALUES(26,2,'26:2','ھي پڌري ڪتاب جون آيتون آھن.');
INSERT INTO "amroti" VALUES(26,3,'26:3','متان تون پاڻ کي ھن سببان گھٽي ماريندڙ ٿئين ته (ڪافر) مسلمان نه ٿا ٿين.');
INSERT INTO "amroti" VALUES(26,4,'26:4','جيڪڏھن اسين گھرون ته آسمان کان مٿن ڪا (اھڙي) نشاني نازل ڪريون جنھن جي آڏو سندن گردنون جھڪيون ٿين.');
INSERT INTO "amroti" VALUES(26,5,'26:5','۽ ٻاجھاري (الله) کان ڪائي نئين نصيحت وٽن نه ايندي آھي جو کانئس منھن موڙيندڙ (نه) آھن.');
INSERT INTO "amroti" VALUES(26,6,'26:6','پوءِ بيشڪ ڪُوڙو ڀانئي چُڪا تنھنڪري سگھوئي وٽن اُھي خبرون اينديون جن سان اُھي ٺـٺوليون ڪندا ھوا.');
INSERT INTO "amroti" VALUES(26,7,'26:7','زمين ڏانھن نه ڏٺو اٿن ڇا ته منجھس ھر ڪنھن جنس جا ڪيترائي سُٺا اوڀڙ ڄماياسون؟');
INSERT INTO "amroti" VALUES(26,8,'26:8','بيشڪ اُن ۾ نشاني آھي، ۽ منجھانئن گھڻا مڃڻ وارا نه آھن.');
INSERT INTO "amroti" VALUES(26,9,'26:9','۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.');
INSERT INTO "amroti" VALUES(26,10,'26:10','۽ جڏھن تنھنجي پالڻھار موسىٰ کي سڏيو ته ظالمن جي قوم ڏانھن وڃ.');
INSERT INTO "amroti" VALUES(26,11,'26:11','جا فرعون جي قوم آھي، (کانئن پُڇ) ته پرھيزگاري نه ڪندا ڇا؟');
INSERT INTO "amroti" VALUES(26,12,'26:12','مُوسىٰ چيو ته اي منھنجا پالڻھار بيشڪ آءٌ ڊڄان ٿو ته متان مونکي ڪُوڙو ڀانئين.');
INSERT INTO "amroti" VALUES(26,13,'26:13','۽ منھنجو سينو تنگ ٿيندو آھي ۽ منھنجي زبان به پوري نه اُٿلندي آھي تنھنڪري ھارون ڏانھن (پيغمبريءَ جو حُڪم) موڪل.');
INSERT INTO "amroti" VALUES(26,14,'26:14','۽ انھن جو مون تي ڏوھ به آھي تنھنڪري ڊڄان ٿو ته متان مون کي ماري ڇڏين.');
INSERT INTO "amroti" VALUES(26,15,'26:15','(الله) فرمايو ته ايئن (ٿيڻو) نه آھي، پوءِ منھنجي نشانين سان ٻئي (ڄڻا) وڃو بيشڪ اسين اوھان سان گڏ ٻڌندڙ آھيون.');
INSERT INTO "amroti" VALUES(26,16,'26:16','تنھنڪري فرعون وٽ وڃو ۽ چئوس ته بيشڪ اسين جھانن جي پالڻھار جا (ھن پيغام سان) موڪليل آھيون.');
INSERT INTO "amroti" VALUES(26,17,'26:17','ته بني اسرائيل کي اسان سان موڪل.');
INSERT INTO "amroti" VALUES(26,18,'26:18','فرعون چيو ته توکي پنھنجن (ڀاتين) ۾ ننڍڙو ڪري نه پاليو ھوسون ڇا؟ ۽ پنھنجي عمر جا ڪيترائي ورھ اسان ۾ گذاريئي.');
INSERT INTO "amroti" VALUES(26,19,'26:19','۽ تو پنھنجو اُھو ڪم ڪيو جو تون ڪري چڪين ۽ تون بي شڪرن مان آھين.');
INSERT INTO "amroti" VALUES(26,20,'26:20','(موسى) چيو ته جنھن مھل اُھو ڪم ڪيو ھوم (تنھن مھل) آءٌ بي سمجھن مان ھوس.');
INSERT INTO "amroti" VALUES(26,21,'26:21','پوءِ جنھن مھل اوھان (جي ايذاءَ) کان ڊنس تنھن مھل اوھان کان ڀڳس پوءِ منھنجي پالڻھار مون کي سياڻپ بخشي ۽ مون کي پيغمبرن مان ڪيائين.');
INSERT INTO "amroti" VALUES(26,22,'26:22','۽ اِھو اُھو احسان آھي ڇا جنھنجو مون تي ٿورو رکين ٿو؟ ته بني اسرائيلن کي ٻانھون ڪري ورتو اٿئي.');
INSERT INTO "amroti" VALUES(26,23,'26:23','فرعون چيو ته جھانن جو پاليندڙ ڇا (کي چئبو) آھي؟');
INSERT INTO "amroti" VALUES(26,24,'26:24','(موسى) چيو ته آسمانن ۽ زمين جو ۽ جيڪي سندن وچ ۾ آھي تنھنجو پالڻھار آھي، جيڪڏھن اوھين ويساہ رکندا آھيو.');
INSERT INTO "amroti" VALUES(26,25,'26:25','(فرعون) پنھنجي آس پاس وارن کي چيو ته (اوھين) نه ٿا ٻُڌو ڇا؟');
INSERT INTO "amroti" VALUES(26,26,'26:26','(مُوسى) چيو ته (اُھو) اوھان جو پالڻھار ۽ اوھان جي ابن ڏاڏن جو پالڻھار آھي.');
INSERT INTO "amroti" VALUES(26,27,'26:27','(فرعون) چيو ته اوھان جو پيغمبر جيڪو اوھان ڏانھن موڪليو ويو آھي سو بيشڪ چريو آھي.');
INSERT INTO "amroti" VALUES(26,28,'26:28','(مُوسى) چيو ته اوڀر ۽ اولھ ۽ جيڪي انھن ٻنھي جي وچ ۾ آھي تنھنجو (به) پالڻھار آھي، جيڪڏھن اوھين سمجھ رکندا آھيو (ته اِھا سمجھاڻي بس آھي).');
INSERT INTO "amroti" VALUES(26,29,'26:29','(فرعون) چيو ته جيڪڏھن مون کانسواءِ ڪو ٻيو خدا ورتئي ته ضرور توکي قيدين مان ڪندس.');
INSERT INTO "amroti" VALUES(26,30,'26:30','(مُوسى) چيو ته جيتوڻيڪ تو وٽ پڌري شيء آڻيان ته به (قيد ڪندين) ڇا؟');
INSERT INTO "amroti" VALUES(26,31,'26:31','(فرعون) چيو ته جيڪڏھن تون سَچن مان آھين ته اُھا آڻ.');
INSERT INTO "amroti" VALUES(26,32,'26:32','پوءِ پنھنجي لَٺ اُڇليائين ته اُھا اُمالڪ پڌرو واسينگ (نانگ) ٿي پئي.');
INSERT INTO "amroti" VALUES(26,33,'26:33','۽ پنھنجو ھٿ ڪڍيائين ته اوچتو اُھو ڏسندڙن لاءِ چمڪندڙ ٿي پيو.');
INSERT INTO "amroti" VALUES(26,34,'26:34','(فرعون) پنھنجن آس پاس وارن سردارن کي چيو ته بيشڪ ھي شخص داناءُ جادوگر آھي.');
INSERT INTO "amroti" VALUES(26,35,'26:35','(ھو) اوھان کي اوھان جي ملڪ مان پنھنجي جادوءَ سان لوڌڻ گھري ٿو پوءِ مون کي (ھاڻي) ڇا ٿا حُڪم ڪريو؟');
INSERT INTO "amroti" VALUES(26,36,'26:36','چيائون ته کيس ۽ سندس ڀاءُ کي ترساءِ ۽ شھرن ۾ ڪوٺيندڙ کي موڪل.');
INSERT INTO "amroti" VALUES(26,37,'26:37','ته سڀڪنھن ڄاڻندڙ وڏي جادوگر کي کي تو وٽ آڻين.');
INSERT INTO "amroti" VALUES(26,38,'26:38','پوءِ ھڪ ڏينھن ٺھرايل جي انجام تي جادوگر گڏ ڪيا ويا.');
INSERT INTO "amroti" VALUES(26,39,'26:39','۽ ماڻھن کي چيو ويو ته اوھين (به) گڏ ٿيڻ وارا آھيو ڇا؟');
INSERT INTO "amroti" VALUES(26,40,'26:40','جيڪڏھن اُھي (جادوگر) غالب ٿيا ته مانَ اسين انھن جا تابعدار ٿيون.');
INSERT INTO "amroti" VALUES(26,41,'26:41','پوءِ جنھن مھل جادوگر آيا (تنھن مھل) فرعون کي چيائون ته جيڪڏھن اسين ڏاڍا ٿيون ته اسان لاءِ ڪو انعام آھي (يا نه!).');
INSERT INTO "amroti" VALUES(26,42,'26:42','(فرعون) چيو ته ھائو ۽ بيشڪ اوھين اُنھيءَ مھل (منھنجن) ويجھن مان ٿيندؤ.');
INSERT INTO "amroti" VALUES(26,43,'26:43','کين مُوسىٰ چيو ته جيڪي اوھين اڇلڻ وارا آھيو سو اُڇليو.');
INSERT INTO "amroti" VALUES(26,44,'26:44','پوءِ ھنن پنھنجا رسا ۽ پنھنجيون لٺيون اڇليون ۽ چيائون ته فرعون جي عزت جو قَسم آھي ته بيشڪ اسين ئي غالب ٿينداسون.');
INSERT INTO "amroti" VALUES(26,45,'26:45','پوءِ مُوسىٰ پنھنجي لَٺ اُڇلي ته اُھا اُتي جو اُتي جيڪو سانگ بڻائيندا ويا سو ڳھندي وئي.');
INSERT INTO "amroti" VALUES(26,46,'26:46','پوءِ جادوگر سجدو ڪندڙ ٿي نِويا.');
INSERT INTO "amroti" VALUES(26,47,'26:47','چيائون ته جھانن جي پالڻھار تي ايمان آندوسون.');
INSERT INTO "amroti" VALUES(26,48,'26:48','(جو) مُوسىٰ ۽ ھارون جو پالڻھار آھي.');
INSERT INTO "amroti" VALUES(26,49,'26:49','(فرعون) چيو ته اُن کان اڳ جو اوھان کي موڪل ڏيان اوھان موسىٰ تي ڇو ايمان آندو، بيشڪ اُھو اوھان جو وڏو آھي جنھن اوھان کي جادو سيکاريو، پوءِ سگھو ڄاڻندؤ، اوھان جا ھڪ پاسي جا ھٿ ۽ اوھان جا ٻئي پاسي جا پير ضرور وڍيندس ۽ اوھان مڙني کي (گڏ) ضرور سوريءَ تي چاڙھيندس.');
INSERT INTO "amroti" VALUES(26,50,'26:50','چيائون ته ڪا حرڪت نه آھي بيشڪ اسين پنھنجي پالڻھار ڏانھن موٽڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(26,51,'26:51','بيشڪ اسين اميد ٿا رکون ٿا ته اسان جو پالڻھار اسان جا ڏوھ اسان کي بخشيندو انھي ڪري جو (پھرين اسين) مُسلمان ٿيا آھيون.');
INSERT INTO "amroti" VALUES(26,52,'26:52','۽ موسىٰ ڏانھن وحي ڪيوسون ته منھنجن ٻانھن کي راتو واہ وٺي نِڪر ڇو ته (فرعون ۽ سندس لشڪر) اوھان جي پٺيان پوندا.');
INSERT INTO "amroti" VALUES(26,53,'26:53','پوءِ فرعون شھرن ۾ ڪوٺيندڙ موڪليا.');
INSERT INTO "amroti" VALUES(26,54,'26:54','(چيائين) ته بيشڪ ھيء (ماڻھو) ٿوري ٽولي آھن.');
INSERT INTO "amroti" VALUES(26,55,'26:55','۽ بيشڪ انھن اسان کي ڪاوڙايو آھي.');
INSERT INTO "amroti" VALUES(26,56,'26:56','۽ اسين ھٿيارن واري جماعت آھيون.');
INSERT INTO "amroti" VALUES(26,57,'26:57','(الله فرمائي ٿو ته) پوءِ انھن (فرعونين) کي باغن ۽ چشمن مان ٻاھر ڪڍيوسون.');
INSERT INTO "amroti" VALUES(26,58,'26:58','۽ خزانن ۽ چڱي جاءِ مان (به).');
INSERT INTO "amroti" VALUES(26,59,'26:59','اھڙي طرح (ڪيوسون)، ۽ انھن (شين) جو بني اسرائيلن کي وارث ڪيوسون.');
INSERT INTO "amroti" VALUES(26,60,'26:60','پوءِ سج اُڀرندي ئي اُنھن (بني اسرائيلن) جي پوئتان پيا.');
INSERT INTO "amroti" VALUES(26,61,'26:61','پوءِ جنھن مھل ٻنھي ٽولين ھڪ ٻئي ڏٺو (تنھن مھل) مُوسىٰ جي سنگتين چيو ته بيشڪ اسان کي پھچي ويا.');
INSERT INTO "amroti" VALUES(26,62,'26:62','(موسى) چيو ته ايئن نه آھي، بيشڪ منھنجو پالڻھار مون ساڻ آھي مون کي رستي لائيندو.');
INSERT INTO "amroti" VALUES(26,63,'26:63','پوءِ مُوسىٰ ڏانھن وحي ڪيوسون ته پنھنجي لٺ سمنڊ کي ھڻ، پوءِ اُھو چيرجي پيو پوءِ سڀڪو ڀاڱو وڏي پھاڙ جھڙو ٿيو.');
INSERT INTO "amroti" VALUES(26,64,'26:64','۽ انھن ٻـين (فرعونين) کي اُن ھنڌ ويجھو ڪيوسون.');
INSERT INTO "amroti" VALUES(26,65,'26:65','۽ مُوسىٰ ۽ سندس سنگتين مڙني کي بچايوسين.');
INSERT INTO "amroti" VALUES(26,66,'26:66','وري (ھنن) ٻـين (يعني فرعونين) کي ٻوڙيوسون.');
INSERT INTO "amroti" VALUES(26,67,'26:67','بيشڪ اُنھي (قصي) ۾ نشاني آھي، ۽ انھن مان گھڻا مڃڻ وارا نه ھوا.');
INSERT INTO "amroti" VALUES(26,68,'26:68','۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.');
INSERT INTO "amroti" VALUES(26,69,'26:69','۽ اُنھن کي ابراھيم جو قِصّو پڙھي ٻڌاءِ.');
INSERT INTO "amroti" VALUES(26,70,'26:70','جڏھن پنھنجي پيءُ ۽ پنھنجي قوم کي چيائين ته ڇا کي پوڄيندا آھيو؟');
INSERT INTO "amroti" VALUES(26,71,'26:71','چيائون ته (اسين) بُتن کي پوڄيندا آھيون پوءِ سارا ڏينھن انھن جا مجاور ٿي وھندا آھيون.');
INSERT INTO "amroti" VALUES(26,72,'26:72','(ابراھيم) چيو ته جڏھن (ھنن کي) سڏيو ٿا (تڏھن) اوھان جو (سڏ) ٻڌن ٿا ڇا؟');
INSERT INTO "amroti" VALUES(26,73,'26:73','يا اوھان کي سُک پھچائيندا آھن يا ڏک ڏيندا آھن؟');
INSERT INTO "amroti" VALUES(26,74,'26:74','چيائون (نه!) بلڪ پنھنجن ابن ڏاڏن کي ائين ڪندي ڏٺوسون.');
INSERT INTO "amroti" VALUES(26,75,'26:75','(ابراھيم) چيو ته (انھن کي) ڏٺو اٿوَ جن کي پوڄيندا رھيؤ؟');
INSERT INTO "amroti" VALUES(26,76,'26:76','اوھين ۽ اوھان جا اڳيان پيءُ ڏاڏا.');
INSERT INTO "amroti" VALUES(26,77,'26:77','بيشڪ اُھي جھانن جي پالڻھار کانسواءِ منھنجا ويري آھن.');
INSERT INTO "amroti" VALUES(26,78,'26:78','جنھن مون کي پيدا ڪيو آھي اُھو ئي مون کي سڌو رستو ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(26,79,'26:79','۽ اُھو آھي جو مون کي کارائيندو آھي ۽ مون کي پياريندو آھي.');
INSERT INTO "amroti" VALUES(26,80,'26:80','۽ جڏھن بيمار ٿيندو آھيان تڏھن مون کي شفا ڏيندو آھي.');
INSERT INTO "amroti" VALUES(26,81,'26:81','۽ اُھو آھي جو مون کي ماريندو ۽ وري مون کي جياريندو.');
INSERT INTO "amroti" VALUES(26,82,'26:82','۽ اُھو آھي جو آسرو اٿم ته قيامت جي ڏينھن منھنجون خطائون مون کي بخشيندو.');
INSERT INTO "amroti" VALUES(26,83,'26:83','اي منھنجا پالڻھار مُون کي دانائي بخش ۽ مون کي صالحن سان شامل ڪر.');
INSERT INTO "amroti" VALUES(26,84,'26:84','۽ پوين ۾ منھنجي چڱي يادگيري (قائم) ڪر.');
INSERT INTO "amroti" VALUES(26,85,'26:85','۽ مون کي نعمتن واري بھشت جي وارثن مان ڪر.');
INSERT INTO "amroti" VALUES(26,86,'26:86','۽ منھنجي پيءُ کي بخش جو اُھو گمراھن مان آھي.');
INSERT INTO "amroti" VALUES(26,87,'26:87','۽ جنھن ڏينھن (ماڻھو جيئرا ٿي) اُٿندا (تنھن ڏينھن) مون کي خوار نه ڪج.');
INSERT INTO "amroti" VALUES(26,88,'26:88','جنھن ڏينھن نڪي مال ۽ نڪي پٽ نفعو ڏيندا.');
INSERT INTO "amroti" VALUES(26,89,'26:89','پر اُھو (فائدي وارو ھوندو) جو الله وٽ سالم دل آڻيندو.');
INSERT INTO "amroti" VALUES(26,90,'26:90','۽ پرھيزگارن لاءِ بھشت ويجھو ڪيو ويندو.');
INSERT INTO "amroti" VALUES(26,91,'26:91','۽ گمراھن لاءِ دوزخ پڌرو ڪيو ويندو.');
INSERT INTO "amroti" VALUES(26,92,'26:92','۽ کين چيو ويندو ته اُھي ڪٿي آھن جي کي پوڄيندا ھيؤ.');
INSERT INTO "amroti" VALUES(26,93,'26:93','الله ڌاران؟ (ھاڻي) اُھي اوھان کي مدد ڏين ٿا ڇا يا (پاڻ) بدلو وٺي سگھن ٿا.');
INSERT INTO "amroti" VALUES(26,94,'26:94','پوءِ اُھي گمراہ (پوڄيندڙ) ۽ بُت منجھس اونڌا ڪري اُڇلايا ويندا.');
INSERT INTO "amroti" VALUES(26,95,'26:95','۽ مڙئي شيطاني لشڪر به (اڇلائبا).');
INSERT INTO "amroti" VALUES(26,96,'26:96','۽ (تڏھن) اُھي منجھس ھڪ ٻئي سان جھڳڙو ڪندي پيا چوندا.');
INSERT INTO "amroti" VALUES(26,97,'26:97','ته الله جو قسم آھي بيشڪ اسين ته پڌريءَ گمراھي ۾ ھواسون.');
INSERT INTO "amroti" VALUES(26,98,'26:98','جڏھن اوھان کي جھانن جي پالڻھار سان برابر ٿي ڪيوسون.');
INSERT INTO "amroti" VALUES(26,99,'26:99','۽ (ھنن) ڏوھارين کانسواءِ ٻئي ڪنھن اسان کي گمراہ نه ڪيو.');
INSERT INTO "amroti" VALUES(26,100,'26:100','پوءِ اسان جو (ھاڻي) نڪو ڪو سفارش ڪرڻ وارو آھي.');
INSERT INTO "amroti" VALUES(26,101,'26:101','۽ نڪو ڪو غم ٽار دوست آھي.');
INSERT INTO "amroti" VALUES(26,102,'26:102','پوءِ جيڪر ھڪ ڀيرو اسان کي (وري دنيا ۾ وڃڻ) ملي ته مؤمنن مان ٿيون.');
INSERT INTO "amroti" VALUES(26,103,'26:103','بيشڪ اِن ۾ ضرور نشاني آھي، ۽ انھن مان گھڻا مؤمن نه آھن.');
INSERT INTO "amroti" VALUES(26,104,'26:104','۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.');
INSERT INTO "amroti" VALUES(26,105,'26:105','نوح جي قوم پيغمبرن کي ڪوڙو ڀانيو.');
INSERT INTO "amroti" VALUES(26,106,'26:106','جڏھن کين سندن ڀاءُ نوح چيو ته (الله کان) ڇونه ٿا ڊڄو.');
INSERT INTO "amroti" VALUES(26,107,'26:107','بيشڪ آءٌ اوھان لاءِ امين پيغمبر آھيان.');
INSERT INTO "amroti" VALUES(26,108,'26:108','پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.');
INSERT INTO "amroti" VALUES(26,109,'26:109','۽ اوھان کان اِن (پيغام جي پھچائڻ) تي ڪو اُجورو نٿو گھران، منھنجو اجورو ته رڳو جھانن جي پالڻھار تي آھي.');
INSERT INTO "amroti" VALUES(26,110,'26:110','پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.');
INSERT INTO "amroti" VALUES(26,111,'26:111','چيائون ته توتي ايمان ڇو آڻيون حالانڪ نيچ ماڻھن (ڪنھن دنياوي لالچ لاءِ) تنھنجي تابعداري ڪئي آھي.');
INSERT INTO "amroti" VALUES(26,112,'26:112','(نوح) چيو ته مون کي (سندن اندر جي) ڪھڙي سُڌ ته (ھو) ڇا (نيت) ڪندا رھندا آھن.');
INSERT INTO "amroti" VALUES(26,113,'26:113','جيڪڏھن اوھين (سندين نيت) ڄاڻندا آھيو ته سندن حساب منھنجي پالڻھار کانسواءِ ٻئي ڪنھن تي نه آھي.');
INSERT INTO "amroti" VALUES(26,114,'26:114','۽ آءٌ مؤمنن کي تڙڻ وارو نه آھيان.');
INSERT INTO "amroti" VALUES(26,115,'26:115','آءٌ ته رڳو پڌرو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(26,116,'26:116','چيائون ته اي نوح جيڪڏھن تون نه رھندين ته ضرور سنگسار ٿيندين.');
INSERT INTO "amroti" VALUES(26,117,'26:117','(نوح) چيو ته اي منھنجا پالڻھار بيشڪ منھنجي قوم مون کي ڪوڙو ڀانيو.');
INSERT INTO "amroti" VALUES(26,118,'26:118','تنھنڪري منھنجي ۽ سندن وچ ۾ چڱي طرح فيصلو ڪر ۽ مونکي ۽ جيڪي مؤمن مون ساڻ آھن تن کي بچاءِ.');
INSERT INTO "amroti" VALUES(26,119,'26:119','پوءِ کيس ۽ سندس سنگتين کي ڀريل ٻيڙيءَ ۾ بچايوسون.');
INSERT INTO "amroti" VALUES(26,120,'26:120','وري کانئن پوءِ رھيلن کي ٻوڙيوسون.');
INSERT INTO "amroti" VALUES(26,121,'26:121','بيشڪ ان ۾ وڏي نشاني آھي، انھن مان گھڻا مڃيندڙ نه ھوا.');
INSERT INTO "amroti" VALUES(26,122,'26:122','۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.');
INSERT INTO "amroti" VALUES(26,123,'26:123','عاد (جي قوم) پيغمبرن کي ڪوڙو ڄاتو.');
INSERT INTO "amroti" VALUES(26,124,'26:124','جڏھن سندن ڀاءُ ھود کين چيو ته (الله کان) ڇونه ڊڄندا آھيو؟');
INSERT INTO "amroti" VALUES(26,125,'26:125','بيشڪ آءٌ اوھان جو امين پيغمبر آھيان.');
