INSERT INTO "amroti" VALUES(26,128,'26:128','سڀڪنھن دڙي تي اجايا مشغول ٿي نشان ڇو بڻائيندا آھيو؟');
INSERT INTO "amroti" VALUES(26,129,'26:129','۽ (ڇو) مُحڪم جايون بڻائيندا آھيو؟ ڄڻڪ اوھين سدائين رھندؤ.');
INSERT INTO "amroti" VALUES(26,130,'26:130','۽ جڏھن اوھين دست درازي ڪندا آھيو ته زبردست ٿي ڪندا آھيو.');
INSERT INTO "amroti" VALUES(26,131,'26:131','پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.');
INSERT INTO "amroti" VALUES(26,132,'26:132','۽ انھي (الله) کان ڊڄو جنھن اوھان کي انھي (شيء) جي مدد ڏني جا (اوھين) ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(26,133,'26:133','اوھان کي ڍورن ۽ پُٽن (ڏيڻ) سان مدد ڏني اٿس.');
INSERT INTO "amroti" VALUES(26,134,'26:134','۽ باغن ۽ پاڻي جي چشمن (ڏيڻ) سان به.');
INSERT INTO "amroti" VALUES(26,135,'26:135','بيشڪ آءٌ اوھان تي وڏي ڏينھن جي عذاب (پھچڻ) کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(26,136,'26:136','چيائون ته (جي) تون (اسان کي) نصيحت ڏئين يا نصيحت ڏيندڙن مان نه ھجين ته اسان لاءِ ھڪ جھڙو آھين.');
INSERT INTO "amroti" VALUES(26,137,'26:137','اِھا اڳين جي ئي عادت آھي.');
INSERT INTO "amroti" VALUES(26,138,'26:138','۽ اسين (ڪڏھن) عذاب ڪيل نه ٿينداسون.');
INSERT INTO "amroti" VALUES(26,139,'26:139','پوءِ کيس ڪوڙو ڀانيائون تنھنڪري کين ھلاڪ ڪيوسون، بيشڪ اِن (قصي) ۾ نشاني آھي، ۽ منجھانئن گھڻا مڃڻ وارا نه ھوا.');
INSERT INTO "amroti" VALUES(26,140,'26:140','۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.');
INSERT INTO "amroti" VALUES(26,141,'26:141','ثمود (جي قوم) پيغمبرن کي ڪوڙو ڀانيو.');
INSERT INTO "amroti" VALUES(26,142,'26:142','جڏھن کين سندن ڀاءُ صالح چيو ته (الله کان) ڇونه ڊڄندا آھيو؟');
