INSERT INTO "amroti" VALUES(26,146,'26:146','اُھو جيڪي ھتي (دنيا ۾) آھي تنھن ۾ اوھان کي بي ڀئو ڇڏي ڏبو ڇا (ته ھميشه آرام ۾ رھو)؟');
INSERT INTO "amroti" VALUES(26,147,'26:147','باغن ۽ چشمن ۾.');
INSERT INTO "amroti" VALUES(26,148,'26:148','۽ پوکن ۽ کجين ۾ جن جا گوشا ڪنئرا آھن.');
INSERT INTO "amroti" VALUES(26,149,'26:149','۽ ڪاريگر ٿي جبلن مان (تراشي) گھر بڻائيندا آھيو.');
