INSERT INTO "amroti" VALUES(26,151,'26:151','۽ انھن حد کان لنگھندڙن جو چيو نه مڃيو.');
INSERT INTO "amroti" VALUES(26,152,'26:152','جيڪي ملڪ ۾ بگيڙ وجھندا آھن ۽ سڌارا نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(26,153,'26:153','چيائون ته تون رڳو جادو ڪيلن مان آھين.');
INSERT INTO "amroti" VALUES(26,154,'26:154','تون رڳو اسان جھڙو ماڻھو آھين، جيڪڏھن تون سچن مان آھين ته ڪا نشاني آڻ.');
INSERT INTO "amroti" VALUES(26,155,'26:155','(صالح) چيو ته ھيءَ ڏاچي آھي اُن جي (پاڻي) پيڻ لاءِ ۽ اوھان جي (پاڻي) پيڻ لاءِ واري جو ڏينھن ٺھرايل آھي.');
INSERT INTO "amroti" VALUES(26,156,'26:156','۽ اوھين کيس ڪو ايذاءُ نه پھچائجو نه ته اوھان کي وڏي ڏينھن جو عذاب پڪڙيندو.');
INSERT INTO "amroti" VALUES(26,157,'26:157','پوءِ اُن جون کُچون ڪپيائون (ماري وڌائون) پوءِ پشيمان ٿيا.');
INSERT INTO "amroti" VALUES(26,158,'26:158','پوءِ کين عذاب پڪڙيو، بيشڪ اُن ۾ نشاني آھي، ۽ منجھانئن گھڻا مڃڻ وارا نه ھوا.');
