INSERT INTO "amroti" VALUES(26,165,'26:165','جھانن جي مڙسن ڏانھن (بڇڙائيءَ لاءِ) ڇو ويندا آھيو؟');
INSERT INTO "amroti" VALUES(26,166,'26:166','۽ جيڪي اوھان جي پالڻھار اوھان جي زالن مان بڻايو سو ڇڏيندا آھيو؟ بلڪ اوھين حد کان لنگھندڙ قوم آھيو.');
INSERT INTO "amroti" VALUES(26,167,'26:167','چيائون ته اي لُوط جيڪڏھن نه رھندين ته ضرور لوڌائبين.');
INSERT INTO "amroti" VALUES(26,168,'26:168','لُوط چيو ته بيشڪ آءٌ اوھان جي ڪم جو ويري آھيان.');
INSERT INTO "amroti" VALUES(26,169,'26:169','اي منھنجا پالڻھار جيڪي (ھو) ڪندا آھن تنھنجي شر کان مونکي ۽ منھنجي گھر جي ڀاتين کي بچاءِ.');
INSERT INTO "amroti" VALUES(26,170,'26:170','پوءِ کيس ۽ سندس مڙني ڀاتين کي بچايوسون.');
INSERT INTO "amroti" VALUES(26,171,'26:171','سواءِ (ھڪ) ٻڍڙي زال جي جا رھيلن مان ھئي.');
INSERT INTO "amroti" VALUES(26,172,'26:172','وري (انھن) ٻـين جي پاڙ پٽي سون.');
INSERT INTO "amroti" VALUES(26,173,'26:173','۽ مٿن (پھڻن جو) مينھن وسايوسون، پوءِ ڊيڄاريلن جو مينھن بڇڙو آھي.');
INSERT INTO "amroti" VALUES(26,174,'26:174','بيشڪ ھن ۾ (وڏي) نشاني آھي، ۽ منجھانئن گھڻا مڃڻ وارا نه ھوا.');
INSERT INTO "amroti" VALUES(26,175,'26:175','۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.');
INSERT INTO "amroti" VALUES(26,176,'26:176','(شھر) ايڪه وارن پيغمبرن کي ڪُوڙو ڀانيو.');
