INSERT INTO "amroti" VALUES(26,183,'26:183','۽ ماڻھن کي سندن شيون گھٽ نه ڏيو ۽ مُلڪ ۾ فسادي ٿي بگيڙ نه وجھو.');
INSERT INTO "amroti" VALUES(26,184,'26:184','۽ اُنھيءَ الله کان ڊڄو جنھن اوھان کي ۽ اڳين خلق کي خلقيو.');
INSERT INTO "amroti" VALUES(26,185,'26:185','چيائون ته تون رڳو جادو ڪيلن مان آھين.');
INSERT INTO "amroti" VALUES(26,186,'26:186','۽ تون رڳو اسان جھڙو ماڻھو آھين ۽ بيشڪ اسين توکي ڪوڙن مان ڀائيندا آھيون.');
INSERT INTO "amroti" VALUES(26,187,'26:187','جيڪڏھن تون سچن مان آھين ته اسان تي آسمان مان ڪا ڇِپ ڪيراءِ.');
INSERT INTO "amroti" VALUES(26,188,'26:188','(شعيب) چيو ته جيڪي اوھين ڪندا آھيو سو منھنجو پالڻھار چڱي طرح ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(26,189,'26:189','پوءِ اُن کي ڪوڙو ڀانيائون تنھنڪري ڇانوَ واري ڏينھن جي عذاب کين پڪڙيو، بيشڪ اُھو وڏي ڏينھن جو عذاب ھو.');
INSERT INTO "amroti" VALUES(26,190,'26:190','بيشڪ ھن (احوال) ۾ (وڏي) نشاني آھي، ۽ منجھانئن گھڻا مڃڻ وارا نه ھوا.');
INSERT INTO "amroti" VALUES(26,191,'26:191','۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.');
INSERT INTO "amroti" VALUES(26,192,'26:192','۽ بيشڪ اھو (قرآن) جھانن جي پالڻھار کان لاٿل آھي.');
INSERT INTO "amroti" VALUES(26,193,'26:193','اُن کي رُوح الامين (يعني جبرئيل) لاٿو.');
INSERT INTO "amroti" VALUES(26,194,'26:194','تنھنجي دل تي (ھن لاءِ) ته ڊيڄاريندڙن مان ٿئين.');
INSERT INTO "amroti" VALUES(26,195,'26:195','(جو) پڌريءَ عربي ٻوليءَ ۾ آھي.');
INSERT INTO "amroti" VALUES(26,196,'26:196','۽ بيشڪ سندس خبر اڳين صحيفن ۾ (لکيل) آھي.');
INSERT INTO "amroti" VALUES(26,197,'26:197','اُنھن لاءِ (اِھا) نشاني نه آھي ڇا ته بني اسرائيلن جا عالم اُن (جي حقانيت) کي ڄاڻن ھا.');
INSERT INTO "amroti" VALUES(26,198,'26:198','۽ جيڪڏھن قرآن کي (عربن کانسواءِ ٻئي) ڪنھن عجميءَ تي نازل ڪريون ھا.');
INSERT INTO "amroti" VALUES(26,199,'26:199','پوءِ اُھو اُن کي مٿن پڙھي ھا ته اُن کي (به) مڃڻ وارا نه ٿين ھوا.');
INSERT INTO "amroti" VALUES(26,200,'26:200','اھڙي طرح گنھگارن جي دلين ۾ اُھو (انڪار) وڌوسون.');
INSERT INTO "amroti" VALUES(26,201,'26:201','اُن تي (ايسين) ايمان نه آڻيندا جيسين ڏکوئيندڙ عذاب (نه) ڏسندا.');
INSERT INTO "amroti" VALUES(26,202,'26:202','پوءِ اوچتو وٽن عذاب ايندو ۽ اُھي بي خبر ھوندا.');
INSERT INTO "amroti" VALUES(26,203,'26:203','پوءِ چوندا ته اسين مُھلت ڏنل ٿينداسون ڇا؟');
INSERT INTO "amroti" VALUES(26,204,'26:204','پوءِ اسان جو عذاب تڪڙو گھرندا آھن ڇا؟');
INSERT INTO "amroti" VALUES(26,205,'26:205','ڀلا ڏٺو اٿئي ته جيڪڏھن کين ڪيئي ورھ آسودو ڪريون.');
INSERT INTO "amroti" VALUES(26,206,'26:206','وري وٽن اُھو اچي جنھن جو انجام ڏنو پئي وين.');
INSERT INTO "amroti" VALUES(26,207,'26:207','ته سندن آسودو ھجڻ کانئن ڇا (عذاب) ٽاريندو.');
INSERT INTO "amroti" VALUES(26,208,'26:208','۽ اھڙي ڪنھن ڳوٺ کي ناس نه ڪيوسون جنھن لاءِ ڊيڄاريندڙ (نه) ھوا.');
INSERT INTO "amroti" VALUES(26,209,'26:209','نصيحت ڏيڻ لاءِ، ۽ اسين ظالم نه ھواسون.');
INSERT INTO "amroti" VALUES(26,210,'26:210','۽ اُن (قرآن) کي شيطانن نازل نه ڪيو آھي.');
INSERT INTO "amroti" VALUES(26,211,'26:211','۽ نڪي کين جڳائيندو آھي ۽ نڪي ڪري سگھندا آھن.');
INSERT INTO "amroti" VALUES(26,212,'26:212','بيشڪ اُھي (ملائڪن جي ڪلام) ٻڌڻ (جي جاءِ) کان پري ڪيل آھن.');
INSERT INTO "amroti" VALUES(26,213,'26:213','تنھنڪري الله سان گڏ ڪنھن ٻئي معبُود کي نه سڏ ته عذاب وارن مان ٿيندين.');
INSERT INTO "amroti" VALUES(26,214,'26:214','۽ پنھنجن ويجھن مائٽن کي ڊيڄار.');
INSERT INTO "amroti" VALUES(26,215,'26:215','۽ مؤمنن مان جيڪو تنھنجي تابعداري ڪري تنھن لاءِ پنھنجو بازو جھڪو ڪر (يعني نئڙت ڪر).');
INSERT INTO "amroti" VALUES(26,216,'26:216','پوءِ جيڪڏھن تنھنجي نافرماني ڪن ته چؤ ته جيڪي ڪندا آھيو تنھن کان بيشڪ آءٌ بيزار آھيان.');
INSERT INTO "amroti" VALUES(26,217,'26:217','۽ اُنھيءَ غالب مھربان (الله) تي ڀروسو ڪر.');
INSERT INTO "amroti" VALUES(26,218,'26:218','جيڪو (تھجّد وقت) تنھنجي اُٿڻ مھل کي ڏسي ٿو.');
INSERT INTO "amroti" VALUES(26,219,'26:219','۽ سجدي ڪندڙن ۾ تنھنجو ڦرڻ به ڏسي ٿو.');
INSERT INTO "amroti" VALUES(26,220,'26:220','بيشڪ اُھوئي ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(26,221,'26:221','اوھان کي سُڌ (نه) ڏيان ڇا ته ڪنھن تي شيطان لھندا آھن؟');
INSERT INTO "amroti" VALUES(26,222,'26:222','سڀڪنھن ٻٽاڪيءَ گنھگار تي لھندا آھن.');
INSERT INTO "amroti" VALUES(26,223,'26:223','جو ٻڌل (ڳالھ اُن جي ڪنّ ۾ آڻي) وجھندا آھن ۽ منجھائن گھڻا ڪوڙا آھن.');
INSERT INTO "amroti" VALUES(26,224,'26:224','۽ گمراہ (ماڻھو) ئي شاعرن جي تابعداري ڪندا آھن.');
INSERT INTO "amroti" VALUES(26,225,'26:225','نه ڏٺو اٿئي ڇا ته اُھي سڀڪنھن ميدان ۾ حيران ٿيندا آھن.');
INSERT INTO "amroti" VALUES(26,226,'26:226','۽ بيشڪ اُھي اُھو چوندا آھن جيڪي نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(26,227,'26:227','پر جن ايمان آندو ۽ چڱا عمل ڪيا ۽ الله کي گھڻو ساريائون ۽ (پنھنجي) ڏکوئجڻ کانپوءِ بدلو ورتائون (اُھي گمراہ نه آھن)، ۽ ظالم سگھو ئي ڄاڻندا ته ڪھڙي موٽڻ جي ھنڌ وري موٽندا.');
INSERT INTO "amroti" VALUES(27,1,'27:1','طٰسٓ، ھي قرآن ۽ پڌري ڪتاب جون آيتون آھن.');
INSERT INTO "amroti" VALUES(27,2,'27:2','اُنھن مؤمنن لاءِ ھدايت ۽ خوشخبري آھي.');
INSERT INTO "amroti" VALUES(27,3,'27:3','جيڪي نماز پڙھندا آھن ۽ زڪوٰة ڏيندا آھن ۽ اُھي آخرت تي (به) يقين رکندا آھن.');
INSERT INTO "amroti" VALUES(27,4,'27:4','بيشڪ جيڪي آخرت کي نه مڃيندا آھن تن لاءِ سندن ڪرتوت سينگارياسون پوءِ اُھي حيران رھندا آھن.');
INSERT INTO "amroti" VALUES(27,5,'27:5','اِھي اُھي آھن جن لاءِ عذاب جي سختي آھي ۽ اُھي ئي آخرت ۾ ٽوٽي وارا آھن.');
INSERT INTO "amroti" VALUES(27,6,'27:6','۽ (اي پيغمبر) بيشڪ توکي حِڪمت واري ڄاڻندڙ (الله) وٽان قرآن ڏنو وڃي ٿو.');
INSERT INTO "amroti" VALUES(27,7,'27:7','(ياد ڪر) جڏھن موسىٰ پنھنجي گھر جي ڀاتين کي چيو ته بيشڪ مون باھ ڏٺي آھي، اجھو اوھان وٽ اُتان ڪا خبر آڻيندس يا اوھان وٽ (ڪو) ٻاريل ٽانڊو آڻيندس ته مانَ اوھين پاڻ سيڪيو.');
INSERT INTO "amroti" VALUES(27,8,'27:8','پوءِ جنھن مھل (باھ) وٽ آيو (تنھن مھل) سڏيو ويو ته جوئي باھ ۾ ۽ سندس چوگرد آھي سو برڪت وارو ڪيو ويو آھي، ۽ جھانن جي پالڻھار کي پاڪائي آھي.');
INSERT INTO "amroti" VALUES(27,9,'27:9','(چيو ويو ته) اي مُوسىٰ بيشڪ (ڳالھ ھيءَ آھي ته) آءٌ غالب حڪمت وارو الله آھيان.');
INSERT INTO "amroti" VALUES(27,10,'27:10','۽ پنھنجي لٺ کي اُڇل، پوءِ جنھن مھل اُن کي چرندو ڏٺائين ڄڻڪ اُھا نانگ آھي (تنھن مھل) پٺي ڏئي ڀڳو ۽ پوءِ تي نه موٽيو، (چيوسون ته) اي مُوسىٰ نه ڊڄ، بيشڪ مون وٽ پيغمبر نه ڊڄندا آھن.');
INSERT INTO "amroti" VALUES(27,11,'27:11','پر جنھن ظلم ڪيو وري مدائي کانپوءِ مٽائي چڱائي ڪيائين ته بيشڪ (اُنھيءَ لاءِ) بخشڻھار مھربان آھيان.');
INSERT INTO "amroti" VALUES(27,12,'27:12','۽ پنھنجو ھٿ پنھنجي گريبان ۾ وجھ ته بي عيب چمڪندڙ ٿي نڪري (انھن ٻن معجزن سان) جي نون معجزن ۾ داخل آھن فرعون ۽ سندس قوم ڏانھن (وڃ)، بيشڪ اُھي بدڪار قوم آھن.');
INSERT INTO "amroti" VALUES(27,13,'27:13','پوءِ جنھن مھل اسان جون چِٽيون آيتون وٽن پھتيون (تنھن مھل) چيائون ته ھي پڌرو جادو آھي.');
INSERT INTO "amroti" VALUES(27,14,'27:14','۽ بي انصافي ۽ وڏائيءَ کان اُنھن (نشانين) جو انڪار ڪيائون حالانڪ سندين دلين انھن کي پڪ ڄاتو ھو (ته الله جي طرف کان آيل آھن)، پوءِ ڏس ته فسادين جي پڇاڙي ڪھڙي طرح ٿي.');
INSERT INTO "amroti" VALUES(27,15,'27:15','۽ بيشڪ داؤد ۽ سليمان کي علم ڏنوسون، ۽ ھنن چيو ته سڀ ساراھ اُنھي الله کي جڳائي جنھن پنھنجن گھڻن مؤمن ٻانھن تي اسان کي ڀلائي ڏني آھي.');
INSERT INTO "amroti" VALUES(27,16,'27:16','۽ سليمان داؤد جو وارث ٿيو ۽ چيائين ته اي ماڻھؤ اسان کي پکين جي ٻولي سيکاري وئي آھي ۽ اسان کي سڀڪا نعمت ڏني وئي آھي، بيشڪ اھا پڌري ڀلائي آھي.');
INSERT INTO "amroti" VALUES(27,17,'27:17','۽ سليمان لاءِ جنن ۽ ماڻھن ۽ پکين مان سندس لشڪر گڏ ڪيا ويا پوءِ اُھي لڳولڳ صفون ٻڌائي بيھاريا ويندا ھوا.');
INSERT INTO "amroti" VALUES(27,18,'27:18','تان جو جڏھن ڪِوۡلين جي ميدان تي پھتا (تڏھن) ھڪ ڪِوۡل چيو ته اي ڪيوۡليون اوھين پنھنجي ٻرڙن ۾ گھڙو، ته سليمان ۽ سندس لشڪر پنھنجي بي خبريءَ ۾ اوھان کي نه لتاڙين.');
INSERT INTO "amroti" VALUES(27,19,'27:19','پوءِ (سليمان) اُن جي ڳالھائڻ کان مُرڪي کِليو ۽ چيائين ته اي منھنجا پالڻھار مون کي توفيق ڏي ته تنھنجي اُنھن نعمتن جو شڪر ڪريان جي مون تي ۽ منھنجي ماءُ پيءُ تي ڪيون اٿيئي ۽ (توفيق ڏيم) ته اھڙا چڱا ڪم ڪريان جن کان تون راضي ٿئين ۽ مون کي پنھنجي ٻاجھ سان پنھنجي صالح ٻانھن ۾ داخل ڪر.');
INSERT INTO "amroti" VALUES(27,20,'27:20','۽ پکين جو حال احوال معلوم ڪيائين پوءِ (ھُد ھُد کي نه ڏسي ڪري) چيائين ته مون کي ڇا (ٿيو) آھي جو ھُد ھُد نه ٿو ڏسان، يا ھو غير حاضر آھي؟');
INSERT INTO "amroti" VALUES(27,21,'27:21','ضرور سخت سزا ڏيندوسانس يا ضرور ڪُھندوسانس يا مون وٽ ڪو پڌرو دليل آڻي.');
INSERT INTO "amroti" VALUES(27,22,'27:22','پوءِ گھڻو نه ترسيو ته (اچي) چيائين ته اھڙي خبر آندي اٿم جنھنجي توکي ڪا سُڌ نه ھئي ۽ (شھر) سبا کان (ھيءَ) پڪي خبر تووٽ آندي اٿم.');
INSERT INTO "amroti" VALUES(27,23,'27:23','بيشڪ مون ھڪ زال ڏٺي جا اُنھن تي بادشاھي ٿي ڪري ۽ کيس سڀڪا نعمت ڏني ويئي آھي ۽ اُن کي ھڪ وڏو تخت (به) آھي.');
INSERT INTO "amroti" VALUES(27,24,'27:24','کيس ۽ سندس قوم کي الله کان سواءِ سج جي پوڄا ڪندو ڏٺم ۽ شيطان انھن لاءِ سندن ڪرتوت سينگاريا آھن ۽ کين (الله جي) واٽ کان جھليو اٿس تنھنڪري اُھي ھدايت نٿا لھن.');
INSERT INTO "amroti" VALUES(27,25,'27:25','(اُھي) اُنھيءَ الله کي سجدو ڇو نه ٿا ڪن جو آسمانن ۽ زمين جون ڳجھيون شيون پڌريون ڪندو آھي؟ ۽ جيڪي لڪائيندا آھيو ۽ جيڪي پڌرو ڪندا آھيو سو (به) ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(27,26,'27:26','الله (اُھو آھي جو) اُن کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي (اُھو) وڏي عرش جو پالڻھار آھي.');
INSERT INTO "amroti" VALUES(27,27,'27:27','(سليمان) چيو ته ڏسنداسين ته سچ چيو اٿيئي يا ڪوڙن مان آھين.');
INSERT INTO "amroti" VALUES(27,28,'27:28','ھيءُ منھنجو خط کڻي وڃ پوءِ اُھو اُنھن ڏانھن اُڇل وري کانئن منھن ڦيراءِ پوءِ ڏس ته ڇا ٿا ورندي ڏين؟');
INSERT INTO "amroti" VALUES(27,29,'27:29','(بلقيس خط ڏسي) چيو ته اي سردارؤ مون ڏانھن سڳورو خط اڇليو ويو آھي.');
INSERT INTO "amroti" VALUES(27,30,'27:30','بيشڪ اُھو (خط) سليمان وٽان (آيل) آھي ۽ بيشڪ اُھو الله ٻاجھاري مھربان جي نالي سان (شروع) آھي.');
INSERT INTO "amroti" VALUES(27,31,'27:31','(ھن مضمون سان لکيل آھي) ته منھنجي آڏو وڏائي نه ڪريو ۽ مون وٽ مسلمان ٿي اچو.');
INSERT INTO "amroti" VALUES(27,32,'27:32','(بلقيس وري) چيو ته اي سردارؤ منھنجي ڪم ۾ مونکي صلاح ڏيو، آءٌ ڪو ڪم ايسين نبيريندڙ نه آھيان جيسين اوھين حاضر (نه) ٿيندا آھيو.');
INSERT INTO "amroti" VALUES(27,33,'27:33','چيائون ته اسين زور وارا ۽ ڏاڍا جنگي جوان آھيون، ۽ (سڀڪو) ڪم توکي (وس ۾) آھي پوءِ نھار ته ڇا ٿي حُڪم ڪرين.');
INSERT INTO "amroti" VALUES(27,34,'27:34','چيائين ته بيشڪ بادشاھ جنھن مھل ڪنھن ڳوٺ ۾ گھڙندا آھن (تنھن مھل) اُن کي اُجاڙيندا آھن ۽ اُن جي مانَ وارن کي بي مانو ڪندا آھن، ۽ (سڀئي بادشاھ) ائين ئي ڪندا آھن.');
INSERT INTO "amroti" VALUES(27,35,'27:35','۽ بيشڪ آءٌ اُنھن ڏانھن ڪا سوکڙي موڪليان ٿي پوءِ ڏسان ته قاصد ڇا سان موٽن ٿا.');
INSERT INTO "amroti" VALUES(27,36,'27:36','پوءِ جنھن مھل سُليمان وٽ (قاصد) آيو (تنھن مھل سليمان) چيو ته اوھين مونکي مال سان مدد ڏيو ٿا ڇا؟ جو جيڪي مونکي الله ڏنو آھي سو انھيءَ کان ڀلو آھي جيڪي اوھان کي ڏنو اٿس، بلڪ اوھين پنھنجن سوغاتن سان خوش ٿيو ٿا.');
INSERT INTO "amroti" VALUES(27,37,'27:37','(سندن سوغاتن سميت) انھن ڏانھن موٽي وڃ (اسين) بيشڪ اھڙا ڪٽڪ وٽن آڻينداسون جن جي سامھون ٿيڻ جي انھن کي ڪا مجال نه ھوندي ۽ اُن ھنڌان کين بلڪل بي مانو ڪري ڪڍنداسين ۽ اُھي خوار ھوندا.');
INSERT INTO "amroti" VALUES(27,38,'27:38','(سليمان) چيو ته اي سردارؤ اوھان مان ڪير انھن جي مسلمان ٿي مون وٽ اچڻ کان اڳ مون وٽ سندس تخت آڻيندو؟');
INSERT INTO "amroti" VALUES(27,39,'27:39','جنَن مان ھڪڙي وڏي جن چيو ته تون پنھنجي جاءِ تان اُٿين تنھن کان اڳي آءٌ اھو تو وٽ آڻيندس، ۽ بيشڪ آءٌ مٿس وڏو سگھارو معتبر آھيان.');
INSERT INTO "amroti" VALUES(27,40,'27:40','(ٻيو) شخص جنھن وٽ ڪتاب مان علم ھو (تنھن) چيو ته تنھنجي نگاھ جي توڏانھن موٽڻ کان اڳي آءٌ اھو (تخت) تو وٽ آڻيندس، پوءِ جنھن مھل (سليمان) اُھو پاڻ وٽ رکيل ڏٺو (تنھن مھل) چيائين ته ھيءُ منھنجي پالڻھار جي فضل مان آھي، (ھن لاءِ) ته مونکي پرکي ته شڪر ڪريان ٿو يا بي شڪري ڪريان ٿو، ۽ جيڪو شڪر ڪندو سو رڳو پنھنجي (نفعي) لاءِ شڪر ڪندو، ۽ جنھن بي شڪري ڪئي ته منھنجو پالڻھار بي پرواہ ڪرم ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(27,41,'27:41','(سُليمان) چيو ته اُن جي تخت کي اُن (جي آزمائش) لاءِ مٽايو ته ڏسون ته سُڃاڻي ٿي يا اُنھن مان ٿئي ٿي جيڪي نه سڃاڻن؟');
INSERT INTO "amroti" VALUES(27,42,'27:42','پوءِ جنھن مھل (بلقيس) آئي (تنھن مھل) چيو ويو ته تنھنجو تخت ھھڙو آھي ڇا؟ چيائين ته ھيءُ ڄڻڪ اُھوئي آھي، ۽ ھن کان اڳ (سليمان جي سچائي بابت) اسان کي ڄاڻپ ڏني وئي ھئي ۽ اسين حڪم مڃيندڙ آھيون.');
INSERT INTO "amroti" VALUES(27,43,'27:43','۽ الله کانسواءِ جنھنجي پوڄا ڪندي ھئي تنھن کان کيس (سليمان) جھليو، بيشڪ اُھا ڪافرن جي قوم مان ھئي.');
INSERT INTO "amroti" VALUES(27,44,'27:44','اُن کي چيو ويو ته محلات ۾ گھِڙ پوءِ اُھا جنھن مھل ڏٺائين (تنھن مھل) اُن کي پاڻيءَ جو تلاءُ ڀانيائين ۽ پنھنجن پنين تان ڪپڙو لاٿائين، (سليمانؑ) چيو ته اھا محلات شيشن سان جڙاء ٿيل آھي، چيائين ته اي منھنجا پالڻھار مون پاڻ تي ظلم ڪيو ۽ (ھاڻي) جھانن جي پالڻھار الله کي سُليمان سميت مڃيم.');
INSERT INTO "amroti" VALUES(27,45,'27:45','۽ بيشڪ ثمود (قوم) ڏانھن سندن ڀاءُ صالح موڪليوسون ته الله جي عبادت ڪريو پوءِ اُھي اُن مھل ٻه ٽوليون ٿي جھڳڙو ڪنديون رھيون.');
INSERT INTO "amroti" VALUES(27,46,'27:46','(صالح) چيو ته اي منھنجي قوم اوھين چڱائيءَ کان اڳ تڪڙو عذاب ڇو گھرندا آھيو؟ الله کان بخشش ڇونه ٿا گھرو ته مانَ اوھان تي رحم ڪيو وڃي.');
INSERT INTO "amroti" VALUES(27,47,'27:47','چيائون ته توکي ۽ تنھنجي سنگت کي نڀاڳو سمجھيوسون، (صالح) چيو ته اوھان جو نڀاڳ الله وٽ آھي بلڪ اوھين قوم پرکبؤ.');
INSERT INTO "amroti" VALUES(27,48,'27:48','۽ (اُن) شھر ۾ نَو ڄڻا ھوا جي مُلڪ ۾ ڏڦير وجھندا ھوا ۽ سُڌارو نه ڪندا ھوا.');
INSERT INTO "amroti" VALUES(27,49,'27:49','اُنھن الله جو قسم کڻي پاڻ ۾ کڻي چيو ته صالح ۽ سندس گھر وارن کي راتاھو ھڻي ماريون وري سندس وارث کي ضرور چونداسون ته (اسين) سندس گھر وارن جي ھلاڪ ٿيڻ مھل حاضر نه ھواسون ۽ بيشڪ اسين سچا آھيون.');
INSERT INTO "amroti" VALUES(27,50,'27:50','۽ ھنن ھڪڙي قسم جو فريب ڪيو ۽ اسان (به) ھڪڙي قسم جي رٿ رٿي ۽ اُھي بي خبر ھوا.');
INSERT INTO "amroti" VALUES(27,51,'27:51','پوءِ (اي پيغمبر) نھار ته سندن فريب جي پڇاڙي ڪھڙي طرح ٿي جو اسان کين ۽ سندن ساري قوم کي ناس ڪيو.');
INSERT INTO "amroti" VALUES(27,52,'27:52','پوءِ اُنھيءَ سببان جو ظلم ڪيائون اُھي سندن گھر اُجڙيل آھن، ڄاڻندڙ قوم لاءِ بيشڪ ھن ۾ (وڏي) نشاني آھي.');
INSERT INTO "amroti" VALUES(27,53,'27:53','۽ جن ايمان آندو ۽ پرھيزگاري ڪئي تن کي بچايوسون.');
INSERT INTO "amroti" VALUES(27,54,'27:54','۽ لُوط کي (موڪليوسون) جڏھن پنھنجي قوم کي چيائين ته اوھين بي حيائي (جو ڪم) ڪندا آھيو ڇا؟ ۽ اوھين ڏسندا (به) آھيو.');
INSERT INTO "amroti" VALUES(27,55,'27:55','ڇا اوھين زالن کي ڇڏي مردن ڏانھن شھوت سان ويندا آھيو، بلڪ اوھين جاھل قوم آھيو.');
INSERT INTO "amroti" VALUES(27,56,'27:56','تڏھن سندن قوم جي ورندي رڳو ھيءَ ھئي ته چيائون ته پنھنجي ڳوٺ مان لُوط جي گھر وارن کي تڙيو، ڇو ته اُھي ماڻھو پاڪ ھجڻ گھرندا آھن.');
INSERT INTO "amroti" VALUES(27,57,'27:57','پوءِ کيس ۽ سندس گھر وارن کي بچايوسون سواءِ سندس زال جي، جنھن کي پوءِ تي رھيلن مان ٺھرايوسين.');
INSERT INTO "amroti" VALUES(27,58,'27:58','۽ مٿن مينھن وسايوسون، پوءِ ڊيڄايلن جو مينھن بڇڙو آھي.');
INSERT INTO "amroti" VALUES(27,59,'27:59','(اي پيغمبر) چؤ ته سڀ ساراہ الله کي جڳائي ۽ سندس انھن ٻانھن تي سلام ھجي جن کي پسند ڪيائين، الله ڀلو آھي يا اُھو جنھن کي (اُھي الله جو) شريڪ بڻائيندا آھن؟');
INSERT INTO "amroti" VALUES(27,60,'27:60','(نه بلڪ پڇون ٿا) اُھو ڪير آھي جنھن آسمانن ۽ زمين کي بڻايو ۽ اوھان لاءِ آسمان مان پاڻي وسايو؟ پوءِ ان سان (ھر طرح جا) رونق وارا باغ ڄماياسون، اُنھن (باغن) جي وڻن جي ڄمائڻ جي اوھان کي ڪا سگھ نه ھئي الله سان ڪو ٻيو معبود آھي ڇا؟ بلڪ اُھي (اھڙي قوم آھن جي سڌي رستي کان) ڏنگو ھلندا آھن.');
INSERT INTO "amroti" VALUES(27,61,'27:61','(نه بلڪ پڇون ٿا ٿه) زمين کي ٽڪاءَ وارو ڪنھن ڪيو ۽ اُن جي وچ ۾ نھرون (ڪنھن) پيدا ڪيون ۽ مٿس جبل (ڪنھن) پيدا ڪيا ۽ ٻن دريائن جي وچ ۾ (ڪنھن) اوٽ بڻائي؟ الله سان ڪو ٻيو معبود آھي ڇا؟ بلڪ اُنھن مان گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(27,62,'27:62','(نه بلڪ پُڇون ٿا ته) عاجز جي دُعا ڪير قبول ڪندو آھي جڏھن (ھو) الله کي سڏيندو آھي؟ ۽ (ڪير) ڏک لاھيندو آھي؟ ۽ اوھان کي زمين ۾ (ڪير اڳين جو) وارث ڪندو آھي، الله سان ڪو ٻيو معبود آھي ڇا؟ اوھين ٿوري نصيحت وٺندا آھيو.');
INSERT INTO "amroti" VALUES(27,63,'27:63','(نه بلڪ پڇون ٿا ته) اوھان کي بر ۽ بحر جي اونداھين ۾ ڪير (سڌو) رستو ڏيکاريندو آھي؟ ۽ پنھنجي ٻاجھ (مينھن) کان اڳ خوشخبري ڏيندڙ وائن کي ڪير گھلائيندو آھي؟ الله کانسواءِ ڪو ٻيو معبود آھي ڇا؟ (ڪافر) جيڪي شريڪ مقرر ڪندا آھن تن کان الله تمام مٿاھون آھي.');
INSERT INTO "amroti" VALUES(27,64,'27:64','ڀلا نئين سر مخلوق ڪير بڻائيندو آھي وري ٻيھر اُن کي بڻائيندو؟ ۽ اوھان کي آسمان ۽ زمين مان ڪير روزي ڏيندو آھي؟ الله سان ڪو ٻيو معبود آھي ڇا؟ (اي پيغمبر کين) چؤ ته جيڪڏھن اوھين سچا آھيو ته پنھنجي حُجت آڻيو.');
INSERT INTO "amroti" VALUES(27,65,'27:65','(اي پيغمبر کين) چؤ ته آسمانن ۽ زمين ۾ جيڪي به آھن سي الله کانسواءِ ڳجھ نه ڄاڻندا آھن، ۽ نڪي ڄاڻندا آھن ته ڪڏھن وري اُٿاربن.');
INSERT INTO "amroti" VALUES(27,66,'27:66','بلڪ سندن ڄاڻپ آخرت (جي نسبت) ۾ عاجز ٿي پئي آھي، بلڪ اُھي کانئس شڪ ۾ آھن، (نه!) بلڪ (ان جي سمجھ) کان انڌا آھن.');
INSERT INTO "amroti" VALUES(27,67,'27:67','۽ ڪافر چوندا آھن ته جڏھن اسين مٽي ٿينداسون ۽ اسان جا پيءُ ڏاڏا (به تڏھن به) اسين وري قبرن مان ڪڍباسون ڇا؟');
INSERT INTO "amroti" VALUES(27,68,'27:68','بيشڪ اسان کي ۽ اسان جي پيءُ ڏاڏن کي ھن کان اڳ انھيءَ ڳالھ جو اَنجام ڏنو ويو آھي ھيءُ رڳو اڳين جون آکاڻيون آھن.');
INSERT INTO "amroti" VALUES(27,69,'27:69','(اي پيغمبر کين) چؤ ته زمين ۾ گُھمو ۽ ڏسو ته گھنھگارن جي پڇاڙي ڪھڙي طرح ٿي ھئي.');
INSERT INTO "amroti" VALUES(27,70,'27:70','۽ (اي پيغمبر) مٿن ڏک نه ڪر ۽ سندن فريب ڪرڻ کان خفگيءَ ۾ نه رھ.');
INSERT INTO "amroti" VALUES(27,71,'27:71','۽ چوندا آھن ته جيڪڏھن اوھين سچا آھيو ته اھو انجام ڪڏھن ٿيندو.');
INSERT INTO "amroti" VALUES(27,72,'27:72','(کين) چؤ ته جيڪي تڪڙو گھرندا آھيو تنھن مان ڪجھ متان اوھان جي پُٺيءَ پويان (ويجھو) ھجي.');
INSERT INTO "amroti" VALUES(27,73,'27:73','۽ بيشڪ تنھنجو پالڻھار ماڻھن تي فضل ڪرڻ وارو آھي پر گھڻا ماڻھو شڪر نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(27,74,'27:74','۽ سندن سينا جيڪي لڪائيندا ۽ جيڪي پڌرو ڪندا آھن سو تنھنجو پالڻھار بيشڪ ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(27,75,'27:75','۽ آسمان ۽ زمين ۾ اھڙي ڪا (شيء) ڳجھي نه آھي جا پڌري ڪتاب ۾ (لکيل) نه آھي.');
INSERT INTO "amroti" VALUES(27,76,'27:76','بيشڪ ھي قرآن بني اسرائيلن کي گھڻيون ڳالھيون اُھي ڳالھيون بيان ڪندو آھي جن بابت اُھي تڪرار ڪندا ھوا.');
INSERT INTO "amroti" VALUES(27,77,'27:77','۽ بيشڪ اِھو مُؤمن لاءِ ھدايت ۽ ٻاجھ آھي.');
INSERT INTO "amroti" VALUES(27,78,'27:78','بيشڪ تنھنجو پالڻھار پنھنجي حُڪم سان سندن وچ ۾ نبيرو ڪندو، ۽ اُھو غالب ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(27,79,'27:79','پوءِ (اي پيغمبر تون) الله تي ڀروسو ڪر، بيشڪ تون پڌري سچي (دين) تي آھين.');
INSERT INTO "amroti" VALUES(27,80,'27:80','بيشڪ تون نڪي مُئن کي ٻُڌائي سگھندين ۽ نڪي ٻوڙن کي سڏ ٻڌائي سگھندين جڏھن (ھو) پٺي ڏيئي ڦرن.');
INSERT INTO "amroti" VALUES(27,81,'27:81','۽ نڪي تون انڌن کي سندن گمراھيءَ کان (موٽڻ لاءِ) سِڌو رستو ڏيکاريندڙ آھين، (تون) رُڳو انھيءَ کي ٻڌائيندين جيڪو اسان جي آيتن تي ايمان آڻيندو پوءِ اُھي فرمانبردار آھن.');
INSERT INTO "amroti" VALUES(27,82,'27:82','۽ جڏھن مٿن (عذاب جو) انجام لازم ٿيندو (تڏھن) انھن لاءِ ھڪ جانور زمين مان ڪڍنداسون جو ساڻن ڳالھائيندو انھيءَ ڪري جو ماڻھو اسان جي آيتن تي يقين نه رکندا ھوا.');
INSERT INTO "amroti" VALUES(27,83,'27:83','۽ اُنھي ڏينھن جو سڀڪنھن اُمّت مان اھڙيءَ ٽوليءَ کي کڙو ڪنداسون جنھن اسان جي آيتن کي ڪُوڙو ڄاتو ٿي پوءِ اُھي (ھڪٻئي جي پھچڻ تائين) بيھاريا ويندا.');
INSERT INTO "amroti" VALUES(27,84,'27:84','تان جو جڏھن (سڀ) ايندا (تڏھن الله) چوندو ته اوھان منھنجن آيتن کي ڇو ڪوڙ ڄاتو ھو ۽ انھن کي اوھان سمجھو به نه ھُيو يا (چؤ ته) اوھان ڪھڙو ڪم ڪيو ٿي؟');
INSERT INTO "amroti" VALUES(27,85,'27:85','۽ سندن ظلم ڪرڻ سببان مٿن (عذاب جو) انجام لازم ٿي چُڪو پوءِ اُھي (ماڻھو) ڳالھائي نه سگھندا.');
INSERT INTO "amroti" VALUES(27,86,'27:86','نه ڏٺو اٿن ڇا ته اسان رات کي (ھن لاءِ) پيدا ڪيو ته منجھس آرام وٺن ۽ ڏينھن کي روشن (بڻايوسون)، بيشڪ ھن ۾ مڃيندڙ قوم لاءِ (منھنجي قدرت جون) نشانيون آھن.');
INSERT INTO "amroti" VALUES(27,87,'27:87','۽ (ياد ڪريو) جنھن ڏينھن صُور ۾ ڦوڪبو (تنھن ڏينھن) جيڪي آسمانن ۾ آھن ۽ جيڪي زمين ۾ آھن سي سڀئي گھٻرائيندا پر جن لاءِ الله گھريو (سي نه گھٻرائيندا)، ۽ سڀڪو وٽن خوار ٿيل ايندو.');
INSERT INTO "amroti" VALUES(27,88,'27:88','۽ تون جبلن کي ڏسين ٿو انھن کي (زمين) ۾ مُحڪم ڀائين ٿو؟ حالانڪ اُھي ڪڪرن جي ھلڻ جان ھلندا، اِھا (اُنھي) الله جي جوڙ آھي جنھن سڀڪنھن شيء کي مُحڪم رکيو، بيشڪ جيڪي ڪندا آھيو تنھنجي اُھو خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(27,89,'27:89','جيڪو نيڪي آڻيندو تنھن لاءِ اُن کان ڀلو (بدلو) آھي، ۽ اُھي اُن ڏينھن گھٻرائڻ کان امن وارا ھوندا.');
INSERT INTO "amroti" VALUES(27,90,'27:90','۽ جيڪو مدائي آڻيندو پوءِ تن جا منھن باھ ۾ اونڌا ڪري وجھبا، (چيو ويندنِ) ته جيڪي ڪندا ھُيؤ تنھن کانسواءِ ڪو بدلو نه ٿو ڏجيوَ.');
INSERT INTO "amroti" VALUES(27,91,'27:91','(اي پيغمبر) چؤ ته مون کي ته (ھيءُ) حُڪم ڪيل آھي ته ھن شھر جي پالڻھار جي عبادت ڪريان جنھن اُن کي تعظيم وارو ڪيو ۽ سڀڪا شيء سندس آھي ۽ مونکي حُڪم ڪيل آھي ته مُسلمانن مان ھُجان.');
INSERT INTO "amroti" VALUES(27,92,'27:92','۽ (ھيءُ) قرآن پڙھان، پوءِ جيڪو ھدايت وارو ٿيو سو رڳو پاڻ لاءِ ھدايت وارو ٿئي ٿو، ۽ جيڪو گمراھ ٿيو ته چؤ ته آءٌ رڳو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(27,93,'27:93','۽ چؤ ته سڀ ساراھ الله کي جڳائي (اُھو) اوھان کي پنھنجون نشانيون سگھو ڏيکاريندو پوءِ جنکي اوھين سُڃاڻندؤ، ۽ جيڪي ڪندا آھيو تنھن کان اوھان جو پالڻھار بي خبر نه آھي.');
INSERT INTO "amroti" VALUES(28,1,'28:1','طٰسٓمّٓ.');
INSERT INTO "amroti" VALUES(28,2,'28:2','ھي پڌري ڪتاب جون آيتون آھن.');
INSERT INTO "amroti" VALUES(28,3,'28:3','(اي پيغمبر) مُوسىٰ ۽ فرعون جو ڪُجھ احوال مؤمن ماڻھن (جي ٻڌائڻ) لاءِ توکي سچيءَ طرح پڙھي ٻڌايون ٿا.');
INSERT INTO "amroti" VALUES(28,4,'28:4','بيشڪ فرعون مُلڪ ۾ وڏائي ڪئي ھئي ۽ ان جي رھڻ وارن کي ٽوليون ٽوليون ڪري ڇڏيو ھوائين منجھائن ھڪ ٽوليءَ کي ھيڻو ڪيو ھوائين جو سندن پُٽ ڪُھندو ھو ۽ سندن ڌيئرون جيئريون ڇڏيندو ھو، بيشڪ اُھو فسادين مان ھو.');
INSERT INTO "amroti" VALUES(28,5,'28:5','۽ گھُريوسون ته جن کي مُلڪ ۾ ھيڻو ڪيو ويو تن تي اِحسان ڪريون ۽ کين سردار بڻايون ۽ کين وارث ڪريون.');
INSERT INTO "amroti" VALUES(28,6,'28:6','۽ کين مُلڪ ۾ دٻدٻو ڏيون ۽ فرعون ۽ ھامان ۽ سندن لشڪرن کي اُنھن (ھيڻن) کان اُھا (ڳالھ) ڏيکاريون جنھن کان ڊڄندا ھوا.');
INSERT INTO "amroti" VALUES(28,7,'28:7','۽ مُوسىٰ جي ماءُ ڏانھن اِلھام ڪيوسون ته اُن کي کِير پيار، جڏھن ھن جي لاءِ ڊڄين تڏھن کيس درياءَ ۾ اُڇلج ۽ نڪي ڊڄج ۽ نڪي غمگين ٿِج، ڇوته اسين کيس توڏانھن ورائڻ وارا آھيون ۽ کيس پيغمبرن مان ڪرڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(28,8,'28:8','پوءِ فرعون جي گھراڻي وارن اُن کي جھٽيو ته (نيٺ) اُنھن لاءِ ويري ۽ ڏک جو سبب ٿئي، ڇوته فرعون ۽ ھامان ۽ سندن لشڪر خطا ڪندڙ ھوا.');
INSERT INTO "amroti" VALUES(28,9,'28:9','۽ فرعون جي زال چيو ته (ھيءُ ڇوڪر) منھنجي لاءِ ۽ تنھنجي لاءِ اکين جو ٺار آھي، اُن کي نه ڪُھو، مانَ اسان کي نفعو ڏئي يا کيس پُٽ ڪريون ۽ اُھي (اُن جي حال جي حقيقت) نه ڄاڻندا ھوا.');
INSERT INTO "amroti" VALUES(28,10,'28:10','۽ مُوسىٰ جي ماءُ جي دِل بي قرار ٿي، بيشڪ ان ڳالھ کي ستت ئي پڌرو ڪري ھا جيڪڏھن سندس دل تي اسين بندش نه ڪريون ھا ھِن لاءِ ته وسھندڙن مان ٿئي.');
INSERT INTO "amroti" VALUES(28,11,'28:11','۽ مُوسىٰ جي ڀيڻ کي چيائين ته سندس پيرو کڻ، پوءِ (اُھا) اُن کي پري کان ڏسندي رھي ۽ اُنھن نه ٿي ڄاتو.');
INSERT INTO "amroti" VALUES(28,12,'28:12','۽ (ڀيڻس جي پھچڻ کان) اڳ مٿس دائين جو کير حرام ڪيوسون پوءِ ڀيڻس چيو ته (آءٌ) اوھان کي اھڙو گھراڻو ڏسيان جو (اُھي) اوھان جي پاران اُن جي سنڀال ڪن؟ ۽ اُھي اُن جا گھڻ گھرا ھجن.');
INSERT INTO "amroti" VALUES(28,13,'28:13','پوءِ کيس سندس ماءُ ڏانھن وري پھچايوسين ته سندس اکيون ٺرن ۽ غمگين نه ٿئي ۽ (ھيء به) ڄاڻي ته بيشڪ الله جو انجام سچو آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(28,14,'28:14','۽ جنھن مھل پنھنجي جوانيءَ کي پھتو ۽ پختو ٿيو (تنھن مھل) کيس دانائي ۽ عِلم ڏنوسون، ۽ اھڙي طرح ڀلارن کي چڱو بدلو ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(28,15,'28:15','۽ (موسى) شھر ۾ اُتي جي رھاڪن جي غفلت جي وقت داخل ٿيو پوءِ اتي ٻه مڙس پاڻ ۾ وڙھندي ڏٺائين، ھي (ھڪڙو) سندس قوم مان ھو ۽ ھي (ٻيو) سندس ويريءَ (فرعون جي قوم) مان ھو، پوءِ جيڪو سندس قوم مان ھو تنھن اُنھيءَ تي ھمراھي گھري جيڪو سندس ويريء (جي قوم) مان ھو پوءِ مُوسىٰ ان کي مُڪ ھنئي ۽ اُن کي پورو ڪيائين، چيائين ته ھي شيطان جي ڪم مان آھي، بيشڪ اُھو پڌرو گمراھ ڪندڙ ويري آھي.');
INSERT INTO "amroti" VALUES(28,16,'28:16','چيائين ته اي منھنجا پالڻھار بيشڪ مون پاڻ تي ظلم ڪيو تنھنڪري مون کي بخش پوءِ کيس بخشيائين، بيشڪ اُھو بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(28,17,'28:17','چيائين ته اي منھنجا پالڻھار جيڪو مون تي فضل ڪيو اٿئي تنھن سببان ڏوھارين جو مددگار ڪڏھن نه ٿيندس.');
INSERT INTO "amroti" VALUES(28,18,'28:18','پوءِ صُبح تائين شھر ۾ ڊڄندڙ منتظر رھيو ته اوچتو جنھن کانئس مدد گھري ھئي تنھن کانئس (وري) مدد گھري، مُوسىٰ کيس چيو ته تون پڌرو بي راھو آھين.');
INSERT INTO "amroti" VALUES(28,19,'28:19','پوءِ جيڪو ٻنھي جو ويري ھو تنھن ڏانھن (موسى) جنھن مھل ھٿ ڊگھيرڻ جو ارادو ڪيو (تنھن مھل فرياد ڪندڙ مُوسىٰ کي پاڻ تي سنبريل ڏسي) چيو ته اي مُوسىٰ جئن ڪالھ ھڪڙي ماڻھوءَ کي ڪُٺئي تئن مون کي (به) ڪُھڻ جو ارادو ڪرين ٿو ڇا؟ مُلڪ ۾ زبردست ٿيڻ کانسواءِ تنھنجو ٻيو ڪو ارادو نه آھي تون سُڌاريندڙن مان ھُجڻ نه ٿو گھُرين.');
INSERT INTO "amroti" VALUES(28,20,'28:20','۽ ھڪ مڙس شھر جي پرين پاسي کان ڊوڙندو آيو، چيائين ته اي مُوسىٰ بيشڪ (ھتون جا) سردار تولاءِ رٿ ڪن ٿا ته توکي ڪُھن تنھنڪري نيڪري وڃ بيشڪ آءٌ تولاءِ خير گھرندڙن مان آھيان.');
INSERT INTO "amroti" VALUES(28,21,'28:21','پوءِ (مُوسى) ڊڄندو انتظار ڪڍندو اُن (شھر) مان نڪتو، چيائين ته اي منھنجا پالڻھار ظالمن جي قوم کان مونکي بچاءِ.');
INSERT INTO "amroti" VALUES(28,22,'28:22','۽ جنھن مھل مدين (شھر) جي طرف منھن ڪيائين (تنھن مھل) چيائين ته اُميد اٿم ته منھنجو پالڻھار مون کي سڌي واٽ ڏيکاريندو.');
INSERT INTO "amroti" VALUES(28,23,'28:23','۽ جنھن مھل مدين جي تڙ تي پھتو (تنھن مھل) اُن ماڻھن جي ھڪ ٽولي (جانورن کي) پاڻي پياريندي ڏٺائين، ۽ اُنھن کانسواءِ ٻه زالون (به پنھنجو ڌڻ پاڻي پيڻ کان) جھليندي ڏٺائين، (مُوسى) چيو ته اوھان جو ڪھڙو ڪم آھي؟ چيائون ته (اسين) ايسين (پنھنجي ڌڻ کي پاڻي) نه پيارينديون سين جيسين ڌنار (پنھنجا جانور پياري نه) موٽائين، ۽ اسان جو پيءُ وڏو ٻڍڙو آھي.');
INSERT INTO "amroti" VALUES(28,24,'28:24','پوءِ اُنھن (جي ڌڻ) کي پاڻي پياريائين وري (رھڻ لاءِ) پاڇي ڏانھن ڦِريو پوءِ چيائين ته اي منھنجا پالڻھار بيشڪ جيڪي مون ڏانھن چڱائيءَ مان موڪليءِ تنھن لاءِ آءٌ مُحتاج آھيان.');
INSERT INTO "amroti" VALUES(28,25,'28:25','پوءِ منجھائن ھڪڙي لڄ سان گھمندي وٽس آئي، چيائين ته منھنجو پيءُ توکي ھن لاءِ سڏي ٿو ته جيڪو اسان (جي ڌڻ) کي پاڻي پياريئي تنھنجي مزوري توکي ڏي، پوءِ جنھن مھل وٽس آيو ۽ سارو قصّو وٽس بيان ڪيائين (تنھن مھل شعيب) چيو ته نه ڊڄ، (ھاڻي) ظالم قوم کان ڇُٽين.');
INSERT INTO "amroti" VALUES(28,26,'28:26','منجھائن ھڪ چيو ته اي ابا ھن کي مزور ڪري رک، ڇوته ڀلو (نوڪر) جنھن کي مزور ڪري رکين اُھو آھي جو سگھارو معتبر ھجي.');
INSERT INTO "amroti" VALUES(28,27,'28:27','(شعيب) چيو ته منھنجو ارادو آھي ته ھنن پنھنجن ٻن ڌيئن مان ھڪ ھن شرط تي توکي پرڻايان ته اَٺ سال منھنجي مزوري ڪرين، ۽ جيڪڏھ ڏھ (ورھ) پورا ڪندين ته اُھو تنھنجي پار کان (احسان) آھي ۽ آءٌ توتي (ڪا) اوکائي ڪرڻ نه ٿو گھران، جيڪڏھن الله گھريو ته مون کي خير گھرن مان ڏسندين.');
INSERT INTO "amroti" VALUES(28,28,'28:28','(مُوسى) چيو ته منھنجي ۽ تنھنجي وچ ۾ اھو (انجام) آھي، انھن ٻنھي مدتن مان ڪا پورائي ڪريان ته مون تي ڪو زور نه ھوندو، ۽ جيڪي چئون ٿا تنھن تي الله شاھد (بس) آھي.');
INSERT INTO "amroti" VALUES(28,29,'28:29','پوءِ جنھن مھل مُوسىٰ (اھا) مُدّت پوري ڪئي ۽ پنھنجي گھر وارن کي وٺي ھليو (تنھن مھل وچ رستي ۾ ڪوہ) طور جي پاسي ڏانھن باھ ڏٺائين، پنھنجي گھر وارن کي چيائين ته ترسو بيشڪ مون باھ ڏٺي آھي اُميد اٿم ته اُتاھون ڪا خبر يا ڪو باھ جو ٽانڊو اوھان وٽ آڻيندس ته مانَ اوھين پاڻ سيڪيو.');
INSERT INTO "amroti" VALUES(28,30,'28:30','پوءِ جڏھن اُنھيءَ (باھ) وٽ آيو (تڏھن) ميدان جي سڄي ڀر کان مُبارڪ جاءِ ۽ وڻ مان سڏيو ويو ته اي مُوسىٰ بيشڪ آءٌ جھانن جو پالڻھار آھيان.');
INSERT INTO "amroti" VALUES(28,31,'28:31','۽ تون پنھنجي لٺ اُڇل، پوءِ جنھن مھل اُن کي چرندو ڏٺائين ڄڻڪ اُھا نانگ آھي (تنھن مھل) پٺيرو ٿي منھن موڙيائين ۽ پوئتي نه موٽيو، (چيوسون ته) اي موسىٰ اوري اچ ۽ نه ڊڄ، جو بيشڪ تون امن وارن مان آھين.');
INSERT INTO "amroti" VALUES(28,32,'28:32','پنھنجو ھٿ گريبان ۾ گھيڙ ته بي عيب چمڪندڙ ٿي نڪري، ۽ خوف کان (تسلّي ٿيڻ لاءِ) پنھنجون ٻانھون پاڻ ڏانھن گڏ پوءِ اُھي ٻئي حُجّتون فرعون ۽ سندس لشڪر ڏانھن تنھنجي پالڻھار (جي طرف) کان آھن، بيشڪ اُھي بدڪار ماڻھو ھوا.');
INSERT INTO "amroti" VALUES(28,33,'28:33','چيائين ته اي منھنجا پالڻھار بيشڪ منجھائن ھڪ شخص کي مون ماريو آھي تنھنڪري آءٌ پنھنجي مارجڻ کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(28,34,'28:34','۽ منھنجو ڀاءُ ھارون مون کان زبان جي ڪري وڌيڪ چٽو ڳالھائيندڙ آھي تنھنڪري اُن کي مون سان مدد ڏيندڙ ڪري موڪل ته منھنجي تصديق ڪري ڇوته آءٌ پنھنجي ڪوڙي ٿيڻ کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(28,35,'28:35','(الله) چيو ته سِگھو تنھنجا بازو تنھنجي ڀاءُ سان مُحڪم ڪنداسون ۽ اوھان کي غلبو ڏينداسون پوءِ (اُھي) اوھان ڏانھن (ايذاءَ لاءِ) پھچي نه سگھندا، ۽ اسانجي پڌرن معجزن سببان، اوھين ٻئي ۽ جيڪي اوھان جي پُٺيان لڳا سي غالب رھندا.');
INSERT INTO "amroti" VALUES(28,36,'28:36','پوءِ جنھن مھل مُوسىٰ اسان جي پڌرن معجزن سان وٽن آيو (تنھن مھل) چيائون ته ھيء رڳو ٺاھوڪو جادو آھي ۽ اِھا ڳالھ پنھنجن پھرين پيءُ ڏاڏن ۾ نه ٻُڌي سون.');
INSERT INTO "amroti" VALUES(28,37,'28:37','۽ مُوسىٰ چيو ته منھنجو پالڻھار اُنھيءَ کي وڌيڪ ڄاڻندو آھي جنھن وٽانئس ھدايت آندي آھي ۽ جنھن لاءِ (آخرت ۾) موچاري عاقبت جو گھر آھي، بيشڪ ظالم نه ڇُٽندا.');
INSERT INTO "amroti" VALUES(28,38,'28:38','۽ فِرعون چيو ته اي سردارؤ مون اوھان لاءِ پاڻ کانسواءِ ڪو ٻيو معبود نه ڄاتو آھي، پوءِ اي ھامان مون لاءِ سرن جو (کورو) ٻار پوءِ مون لاءِ ھڪ ماڙي بڻاءِ ته مان موسىٰ جي معبُود ڏانھن چڙھان ۽ آءٌ بيشڪ کيس ڪُوڙن مان ڀانيان ٿو.');
INSERT INTO "amroti" VALUES(28,39,'28:39','۽ اُن (فرعون) ۽ سندس لشڪر ناحق مُلڪ ۾ وڏائي ڪئي ۽ ڀانيائون ته اُھي اسان ڏانھن نه موٽائبا.');
INSERT INTO "amroti" VALUES(28,40,'28:40','پوءِ کيس ۽ سندس لشڪرن کي پڪڙيوسون ۽ پوءِ کيس سمنڊ ۾ اُڇليوسون، پوءِ ڏس ته ظالمن جي پڇاڙي ڪھڙي طرح ٿي.');
INSERT INTO "amroti" VALUES(28,41,'28:41','۽ اسان اُھي (دوزخين جا اڳواڻ) بڻايا جو (ماڻھن) کي باھ ڏانھن سڏيندا ھوا، ۽ قيامت جي ڏينھن مدد نه ڏبين.');
INSERT INTO "amroti" VALUES(28,42,'28:42','۽ ھن دنيا ۾ لعنت سندن پوئتان لاٿي سون، ۽ قيامت جي ڏينھن اُھي بُڇڙن مان ھوندا.');
INSERT INTO "amroti" VALUES(28,43,'28:43','۽ بيشڪ مُوسىٰ کي ڪتاب ڏنوسون اُن کان پوءِ جو پھرين جُڳن کي ھلاڪ ڪيوسون جو (اُھو) ماڻھن لاءِ چِٽو دليل ۽ ھدايت ڪندڙ ۽ ٻاجھ ھو ته مانَ اُھي نصيحت وٺن.');
INSERT INTO "amroti" VALUES(28,44,'28:44','۽ (اي پيغمبر تون تڏھن) اُلھندي پاسي کان نه ھئين جڏھن مُوسىٰ ڏانھن وَحي موڪليوسون ۽ نڪي (تون اُتي) حاضرن مان ھُئين.');
INSERT INTO "amroti" VALUES(28,45,'28:45','پر ڪيترائي جُڳ (مُوسىٰ کانپوءِ) پيدا ڪياسون پوءِ مٿن مُدت ڊگھي گذري، ۽ نڪي (تون) مدين وارن ۾ رھندڙ ھئين جو اسان جون کين آيتون پڙھي ٻڌايئي ٿي پر اسين (پيغمبرن) کي موڪليندا رھياسون.');
INSERT INTO "amroti" VALUES(28,46,'28:46','۽ نڪي (تون ڪوہ) طور جي پاسي ھُئين جڏھن موسىٰ کي سڏيوسون پر تنھنجي پالڻھار جي ٻاجھ توتي (ھن لاءِ ٿيل) آھي ته تون اُنھي قوم کي ڊيڄارين جن وٽ توکان اڳ ڪو ڊيڄاريندڙ نه آيو ته مانَ اُھي نصيحت وٺن.');
INSERT INTO "amroti" VALUES(28,47,'28:47','۽ جيڪڏھن ائين نه ھجي ھا ته سندن ھٿن جيڪي اڳي موڪليو آھي تنھنجي سبب اُنھن تي جڏھن مُصيبت پھچي تڏھن (متان) چون ته اي اسان جا پالڻھار اسان ڏانھن ڪو پيغمبر ڇونه موڪليئي ته تنھنجن آيتن جا تابعدار بڻجون ھا ۽ مؤمنن مان ھجون ھا (ته توکي نه موڪليون ھا).');
INSERT INTO "amroti" VALUES(28,48,'28:48','پوءِ جنھن مھل اسان وٽان اُنھن وٽ سچو (وحي) آيو (تنھن مھل) چوڻ لڳا ته جھڙو (معجزو) مُوسىٰ کي ڏنو ويو تھڙو ھن پيغمبر کي ڇونه ڏنو ويو آھي، جيڪي اڳي مُوسىٰ کي ڏنو ويو تنھن جو انڪار نه ڪيو اٿن ڇا؟ (ھاڻي) چون ٿا ته (توريت ۽ قرآن) ٻئي ھڪ جھڙا جادو آھن، ۽ چون ٿا ته اسين ھر ھڪ جا مُنڪر آھيون.');
INSERT INTO "amroti" VALUES(28,49,'28:49','(کين) چؤ ته جيڪڏھن سچا آھيو ته الله وٽان ڪو ڪتاب آڻيو جو انھن ٻنھي کان وڌيڪ سڌو رستو ڏيکاريندڙ ھجي ته اُن جي تابعداري ڪريان.');
INSERT INTO "amroti" VALUES(28,50,'28:50','پوءِ جيڪڏھن تنھنجي ڳالھ قبول نه ڪن ته ڄاڻ ته اُھي رڳو پنھنجن سَڌن تي ھلندا آھن، ۽ جيڪو الله جي رستي ڏيکارڻ کانسواءِ پنھنجي سَڌ تي ھلي تنھن کان وڌيڪ گمراھ ڪير آھي، بيشڪ الله ظالمن جي قوم کي سِڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(28,51,'28:51','۽ بيشڪ ھن قرآن کي انھن لاءِ لڳولڳ موڪليوسون ته مانَ اُھي نصيحت وٺن.');
INSERT INTO "amroti" VALUES(28,52,'28:52','جن کي ھن (قرآن) کان اڳ ڪتان ڏنو اٿون سي اُن (قرآن) کي مڃيندا آھن.');
INSERT INTO "amroti" VALUES(28,53,'28:53','۽ جڏھن کين (ھيءُ قرآن) پڙھي ٻڌائبو آھي (تڏھن) چوندا آھن ته اُن تي ايمان آندوسون بيشڪ اُھو اسان جي پالڻھار وٽان سچو آھي بيشڪ اسان اُن (جي نازل ٿيڻ) کان اڳيئي مُسلمان ھواسون.');
INSERT INTO "amroti" VALUES(28,54,'28:54','اِھي اُھي آھن جن کي سندن اَجر ٻه ڀيرا ڏبو انھي ڪري جو صبر ڪيائون ۽ مدائي کي چڱائي سان ٽاريندا آھن ۽ جيڪا کين روزي ڏني اٿون تنھن مان خرچيندا آھن.');
INSERT INTO "amroti" VALUES(28,55,'28:55','۽ جڏھن ڪا بيھودي ڳالھ ٻڌندا آھن (تڏھن) اُن کان مُنھن موڙيندا آھن ۽ چوندا آھن ته اسان جا ڪم اسان لاءِ آھن ۽ اوھان جا ڪم اوھان لاءِ آھن، اوھان تي سلام ھجي، اسين جاھلن (جي سنگت) کي نه ٿا گھرون.');
INSERT INTO "amroti" VALUES(28,56,'28:56','(اي پيغمبر) بيشڪ تون جنھن کي گھرين تنھن کي ھدايت ڪري نه ٿو سگھين پر الله جنھن کي گھرندو آھي تنھن کي ھدايت ڪندو آھي، ۽ اُھو ھدايت وارن کي چڱي طرح ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(28,57,'28:57','۽ (ڪي) چوندا آھن ته جيڪڏھن توساڻ ھدايت جي تابعداري ڪنداسون ته پنھنجي مُلڪ مان تڙجي وينداسون، کين حرم ۾ امن سان نه ٽڪاياسون ڇا؟ جو اُن ڏانھن سڀڪنھن جنس جو ميوو پاڻ وٽان روزيءَ طرح پھچائبو آھي پر اُنھن مان گھڻا (ماڻھو) نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(28,58,'28:58','۽ ڳوٺن مان ڪيترائي ويران ڪياسون جي پنھنجي گذران ۾ حد کان لنگھيا ھوا، پوءِ اُھي سندين رھڻ جون جايون آھن اُنھن کانپوءِ ڪن ٿورڙن (وقتن) کانسواءِ (اُتي) رھيوئي ڪونه آھي، ۽ آخر اسين (ئي) وارث ھواسون.');
INSERT INTO "amroti" VALUES(28,59,'28:59','۽ (اي پيغمبر) تنھنجو پالڻھار ڳوٺن کي (ايسين) ڪڏھن به ناس ڪرڻ وارو نه آھي جيسين اُن جي ڪنھن وڏي شھر ۾ ڪو پيغمبر (نه) موڪلي جو اسان جون آيتون کين پڙھي ٻڌائي، ۽ ڳوٺن کي ڪڏھن به ناس ڪرڻ وارا نه آھيون سواءِ ان حالت جي جو سندن رھڻ وارا ظالم ھجن.');
INSERT INTO "amroti" VALUES(28,60,'28:60','۽ ھر ھڪ جنس مان جيڪي اوھان کي ڏنو ويو آھي سو دُنيا جي حياتيءَ جو سامان ۽ ان جو سينگار آھي، ۽ جيڪي الله وٽ آھي سو ڀلو ۽ سدائين رھڻ وارو آھي، پوءِ ڇونه سمجھ رکندا آھيو؟');
INSERT INTO "amroti" VALUES(28,61,'28:61','ڀلا جنھن کي چڱو انعام ڏنوسون اُھو ھو لھڻ وارو آھي سو اُنھي جھڙو آھي ڇا جنھن کي دنيا جي سامان سان آسودو ڪيوسون وري اُھو قيامت جي ڏينھن (عذاب ۾) حاضر ڪيلن مان ھجي؟');
INSERT INTO "amroti" VALUES(28,62,'28:62','۽ جنھن ڏينھن (الله) کين سڏي چوندو ته مُنھنجا اُھي شريڪ ڪٿي آھن جن کي اوھين (مون جَھڙو) ڀائيندا ھيؤ.');
INSERT INTO "amroti" VALUES(28,63,'28:63','(ان ڏينھن) جن تي عذاب (جو حُڪم) لازم ٿي چڪو ھوندو سي چوندا ته اي اسانجا پالڻھار ھي اُھي آھن جن کي گمراھ ڪيوسون، جھڙو پاڻ گمراھ ٿياسون تھڙو کين (به) گمراھ ڪيوسون، (ھاڻي سڀني کان) تنھنجي آڏو بيزار ٿيون ٿا، اُھي اسان کي نه پوڄيندا ھوا.');
INSERT INTO "amroti" VALUES(28,64,'28:64','۽ چيو ويندو ته پنھنجن شريڪن کي سڏيو پوءِ کين سڏيندا ته اُھي کين ورندي نه ڏيندا ۽ عذاب ڏسندا، (تڏھن سَڌ ڪندا ته) جيڪر دنيا ۾ ھدايت وارا ھجون ھا.');
INSERT INTO "amroti" VALUES(28,65,'28:65','۽ جنھن ڏينھن (الله) کين سڏيندو پوءِ چوندو ته اوھان پيغمبرن کي ڪھڙي ورندي ڏني.');
INSERT INTO "amroti" VALUES(28,66,'28:66','پوءِ اُن ڏينھن (سڀ) ڳالھيون کين ڀُلجي وينديون پوءِ اُھي پاڻ ۾ (به) نه پڇندا.');
INSERT INTO "amroti" VALUES(28,67,'28:67','پوءِ جنھن توبه ڪئي ۽ ايمان آندو ۽ چڱا ڪم ڪيا سو اُميد آھي ته ڇٽلن مان ٿيندو.');
INSERT INTO "amroti" VALUES(28,68,'28:68','۽ (اي پيغمبر) تنھنجو پالڻھار جيڪي گھرندو آھي سو خلقيندو آھي (۽ جنھن کي گھرندو تنھن کي) پسند ڪندو آھي، انھن کي ڪو اختيار ڪونھي، الله کي پاڪائي آھي ۽ جنھن کي ساڻس شريڪ مَقرّر ڪندا آھن تنھن کان تمام مٿاھون آھي.');
INSERT INTO "amroti" VALUES(28,69,'28:69','۽ سندن سِينا جيڪي لڪائيندا آھن ۽ جيڪي پاڻ پڌرو ڪندا آھن سو تنھنجو پالڻھار ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(28,70,'28:70','۽ اُھو الله آھي اُن کانسواءِ ڪو عبادت جو لائق نه آھي، دُنيا ۽ آخرت ۾ سندس ساراھ آھي، ۽ سندس ئي حڪومت آھي ۽ ڏانھس اوھان کي موٽائبو.');
INSERT INTO "amroti" VALUES(28,71,'28:71','(کين) چؤ ته ڏسو ته سھين جيڪڏھن الله اوھان تي قيامت جي ڏينھن تائين ھميشہ رات (قائم) ڪري ته الله کانسواءِ (ٻيو) ڪھڙو معبود آھي جو اوھان وٽ سوجھرو آڻيندو، پوءِ نه ٻڌندا آھيو ڇا؟');
INSERT INTO "amroti" VALUES(28,72,'28:72','چؤ ته ڏسو ته سھين ته جيڪڏھن الله اوھان تي قيامت جي ڏينھن توڻي ھميشہ ڏينھن (قائم) ڪري ته الله کانسواءِ (ٻيو) ڪھڙو معبود آھي جو اوھان وٽ رات آڻيندو جنھن ۾ اوھين آرام وٺو، پوءِ اوھين نه ٿا ڏسو ڇا؟');
INSERT INTO "amroti" VALUES(28,73,'28:73','۽ پنھنجي ٻاجھ سان اوھان لاءِ رات ۽ ڏينھن بڻايائين ته اوھين منجھس آرام وٺو ۽ سندس فضل مان (روزي) طلب ڪريو ۽ ته مانَ اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(28,74,'28:74','۽ جنھن ڏينھن (الله) کين سڏي چوندو ته اُھي منھنجا شريڪ ڪٿي آھن جن کي اوھين (مون جھڙو) ڀائيندا ھيؤ؟');
INSERT INTO "amroti" VALUES(28,75,'28:75','۽ ھر ھڪ ٽوليءَ مان شاھد جُدا ڪنداسين ۽ چونداسين ته پنھنجو دليل پيش ڪريو پوءِ اُھي ڄاڻندا ته بيشڪ حق الله جي پاسي آھي ۽ جيڪو ٺاھ ٺاھيندا ھوا سو کانئن ڀُلي ويندو.');
INSERT INTO "amroti" VALUES(28,76,'28:76','بيشڪ قارون مُوسىٰ جي قوم مان ھو پوءِ مٿن شرارت ڪرڻ لڳو، ۽ کيس خزانن مان ايترو ڏنو ھوسون جو سندس ڪنجين سگھ واري ٽوليءَ کي ٿَڪايو ٿي، جڏھن سندس قوم کيس چيو ته (مال ڏسي) نه مَرۡڪُ ڇوته الله مَرۡڪَ ڪندڙن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(28,77,'28:77','۽ الله توکي جيڪي ڏنو آھي تنھن سان آخرت جي گھر جي ڀلائي طلب ڪر ۽ دنيا مان پنھنجو (نيڪ عمل ڪرڻ جو) حصّو نه وسار ۽ جھڙي طرح الله توسان چڱائي ڪئي آھي تھڙي طرح (تون سندس خلق سان) چڱائي ڪر ۽ زمين ۾ فساد وجھڻ نه گھُر، ڇوته الله فساد ڪرڻ وارن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(28,78,'28:78','(قارون) چيو ته اُھو (گھڻومال) ته رڳو ھڪ ھُنر سان جو مون وٽ آھي مون کي مليو آھي، نه ڄاتو ھوائين ڇا ته بيشڪ الله کانئس اڳ (اُھي) ڪيترائي جُڳ ناس ڪري ڇڏيا ھوا جيڪي کانئس ڏاڍي سگھ وارا ۽ گھڻي جمعيّت وارا ھوا، ۽ ڏوھارين کان سندن ڏوھن بابت (عذر) نه پڇبو.');
INSERT INTO "amroti" VALUES(28,79,'28:79','پوءِ (ھڪ ڏينھن) پنھنجي قوم جي سامھون پنھنجي سينگار سان نڪتو، جيڪي دنيا جي حياتي گھڙندا ھوا تن چيو ته ھيء ارمان اھڙو (مال) جيڪر اسان کي (به) ھجي ھا جھڙو قارون کي ڏنو ويو آھي بيشڪ اُھو وڏي نصيب وارو آھي.');
INSERT INTO "amroti" VALUES(28,80,'28:80','۽ جن کي علم ڏنو ويو تن چيو ته اوھان لاءِ خرابي ھُجي الله جو ثواب اُنھيءَ ماڻھوءَ لاءِ ڀَلو آھي جنھن ايمان آندو ۽ چڱا ڪم ڪيا، اِھا (ڳالھ) صابرن کانسواءِ (ٻئي) ڪنھنجي دل ۾ نه گِھڙندي آھي.');
INSERT INTO "amroti" VALUES(28,81,'28:81','پوءِ کيس ۽ سندس گھر کي زمين ۾ ڳھايوسون، پوءِ الله کانسواءِ ڪا ٽولي اُن لاءِ ڪانه ھئي جا کيس مدد ڏئي، ۽ نڪي پاڻ وير وٺڻ وارن مان ھو.');
INSERT INTO "amroti" VALUES(28,82,'28:82','۽ جن ڪالھ سندس مرتبي جي سَڌ ٿي ڪئي سي صبح جو چوڻ لڳا ته عجب آھي ته الله پنھنجن ٻانھن مان جنھن لاءِ گھرندو آھي (تنھنجي) روزي ڪشادي ڪندو آھي ۽ تنگ ڪندو آھي، جيڪڏھن الله اسان تي احسان نه ڪري ھا ته اسان کي (به زمين ۾) ڳھائي ڇڏي ھا، نه ڄاتو اٿيئي ته ڪافرن کي ڇوٽڪارو نه آھي.');
INSERT INTO "amroti" VALUES(28,83,'28:83','اِھو آخرت جو گھر آھي جنھن کي اسان انھن لاءِ مقرر ڪيو آھي جيڪي مُلڪ ۾ وڏائي نه ڪندا آھن ۽ نڪي فساد وجھڻ (گھرندا آھن)، ۽ (چڱي) عاقبت پرھيزگارن لاءِ آھي.');
INSERT INTO "amroti" VALUES(28,84,'28:84','جيڪو چڱائي آڻيندو تنھن لاءِ اُن کان ڀلو (بدلو) آھي، ۽ جيڪو مدائي آڻيندو ته مدائين ڪرڻ وارا رڳو اُھا سزا لھندا جيڪي ڪيائون ٿي.');
INSERT INTO "amroti" VALUES(28,85,'28:85','بيشڪ جنھن (الله) توتي قرآن نازل ڪيو سو موٽڻ جي جاءِ ڏانھن توکي ضرور موٽائڻ وارو آھي، چؤ ته جنھن ھدايت آندي تنھن کي منھنجو پالڻھار چڱو ڄاڻندڙ آھي ۽ اُنھيءَ کي به جيڪو پڌريءَ گمراھي ۾ آھي.');
INSERT INTO "amroti" VALUES(28,86,'28:86','۽ توکي ڪو آسرو نه ھو ته تو ڏانھن (ھيءُ) ڪتاب لاھبو پر تنھنجي پالڻھار جي ٻاجھ سان (لاٿو ويو) پوءِ ڪافرن جو مددگار ھرگز نه ٿج.');
INSERT INTO "amroti" VALUES(28,87,'28:87','۽ (اي پيغمبر) توکي الله جي آيتن (جي پھچائڻ) کان اُن پڃاڻا جو تو ڏانھن موڪليون ويون (ڪافر) نه جھلين ۽ پنھنجي پالڻھار ڏانھن (ماڻھن) کي سڏ، ۽ مشرڪن مان ڪڏھن نه ٿيءُ.');
INSERT INTO "amroti" VALUES(28,88,'28:88','۽ الله سان گڏ ٻئي ڪنھن کي معبود (سمجھي) نه سڏ، اُن کانسواءِ ڪو عبادت جو لائق نه آھي، سندس ذات کانسواءِ سڀڪا شيء ناس ٿيڻ واري آھي، سڀڪو حُڪم اُن جو ئي آھي ۽ ڏانھس موٽايا ويندؤ.');
INSERT INTO "amroti" VALUES(29,1,'29:1','الٓمّٓ.');
INSERT INTO "amroti" VALUES(29,2,'29:2','ماڻھن ڀانيو آھي ڇا ته سندن (رڳو ايتري) چوڻ تي ته ايمان آندوسون اُھي ڇڏي ڏبا ۽ اُھي نه آزمائبا؟');
INSERT INTO "amroti" VALUES(29,3,'29:3','۽ جيڪي کانئن اڳ ھوا تن کي بيشڪ پرکيوسون پوءِ جيڪي سچا آھن تن کي الله ضرور ڌار ڪندو ۽ ڪُوڙن کي (به) ضرور ڌار ڪندو.');
INSERT INTO "amroti" VALUES(29,4,'29:4','جيڪي بڇڙايون ڪن ٿا تن ڀانيو آھي ڇا ته اسان کون اڳتي نڪري ويندا؟ جيڪو نبيرو ڪن ٿا سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(29,5,'29:5','جيڪو الله جي ملڻ جي اميد رکندو آھي (سو سمجھي) ته بيشڪ الله جي ٺھرايل مُدت ضرور اچڻي آھي، ۽ اُھو ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(29,6,'29:6','۽ جيڪو (الله جي دين لاءِ) ڪوشش ڪري سو رڳو پنھنجي لاءِ ئي ڪوشش ڪري ٿو، ڇوته الله جھانن کان بي پرواھ آھي.');
INSERT INTO "amroti" VALUES(29,7,'29:7','۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کان سندن مدايون ضرور ميٽيندا سون ۽ جيڪي ڪندا ھوا تنھن جو کين ڏاڍو چڱو بدلو ڏينداسون.');
INSERT INTO "amroti" VALUES(29,8,'29:8','۽ ماڻھوءَ کي پنھنجي ماءُ پيءُ سان چڱائي ڪرڻ جو حُڪم ڪيوسون، ۽ (اي ماڻھو) جيڪڏھن (اُھي) مون سان ڪنھن کي شريڪ ڪرڻ جي توکي ڪوشش ڪن جنھن (جي حقيقت) جي توکي ڪا ڄاڻپ نه آھي ته اُنھن جو چيو نه مڃ، اوھان جو مون ڏانھن موٽڻ آھي تنھنڪري جيڪي ڪندا ھيؤ تنھنجي اوھان کي سُڌ ڏيندس.');
INSERT INTO "amroti" VALUES(29,9,'29:9','۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي صالحن (جي ٽوليءَ) ۾ ضرور داخل ڪنداسون.');
INSERT INTO "amroti" VALUES(29,10,'29:10','۽ ماڻھن مان ڪي (اھڙا) آھن جي چوندا آھن ته الله تي ايمان آندو اٿون پوءِ جڏھن الله (جي واٽ) ۾ اُن کي ڪو ايذاءُ پھچايو ويندو آھي (تڏھن) ماڻھن جي ايذائڻ کي الله جي عذاب وانگر سمجھندو آھي، ۽ جيڪڏھن تنھنجي پالڻھار وٽان ڪا مدد ايندي آھي ته چوندا آھن ته بيشڪ اسين اوھان ساڻ ھواسون، جيڪي جھان (وارن) جي دلين ۾ آھي سو الله چڱي طرح ڄاڻندڙ نه آھي ڇا؟');
INSERT INTO "amroti" VALUES(29,11,'29:11','۽ ته جن ايمان آندو آھي تن کي الله ڌار ڪندو آھي ۽ منافقن کي به ڌار ڪندو.');
INSERT INTO "amroti" VALUES(29,12,'29:12','۽ ڪافر مؤمنن کي چون ٿا (اوھين) اسان جي واٽ جي تابعداري ڪريو ۽ (اسين) اوھان جون مدايون کڻنداسين، حالانڪ اُھي سندين مداين مان ڪجھ به کڻڻ وارا نه آھن، بيشڪ اُھي ڪُوڙا آھن.');
INSERT INTO "amroti" VALUES(29,13,'29:13','۽ پنھنجن (گناھن جون) ڳٺڙيون ۽ (ٻيون ڪيئي) ڳٺڙيون پنھنجن گناھن سان ضرورکڻندا، ۽ جيڪو ٺاھ ٺاھيندا آھن تنھن بابت قيامت جي ڏينھن ضرور پڇيا ويندا.');
INSERT INTO "amroti" VALUES(29,14,'29:14','۽ بيشڪ نوح کي سندس قوم ڏانھن موڪليوسون ۽ پوءِ پنجاھ ورھ گھٽ (ھڪ) ھزار سال منجھن رھيو، پوءِ کين طوفان پڪڙيو ۽ اُھي ظالم ھوا.');
INSERT INTO "amroti" VALUES(29,15,'29:15','پوءِ کيس ۽ ٻيڙيءَ وارن کي (ٻڏڻ کان) بچايوسون ۽ اُن (ٻيڙيءَ) کي جھان (وارن) لاءِ نشاني ڪيوسون.');
INSERT INTO "amroti" VALUES(29,16,'29:16','۽ ابراھيم کي (موڪليوسون) جڏھن پنھنجي قوم کي چيائين ته الله جي عبادت ڪريو ۽ کانئس ڊڄو، اھو اوھان لاءِ ڀلو آھي جيڪڏھن اوھين ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(29,17,'29:17','اوھين ته الله کانسواءِ بُتن کي پوڄيندا آھيو ۽ اوھين (ڪوڙو) ٺاھ ٺاھيندا آھيو، بيشڪ الله کانسواءِ جن کي پوڄيندا آھيو سي ته اوھان کي روزي (ڏيڻ) جي (ڪي) مالڪ نه آھن تنھنڪري الله وٽان رزق طلب ڪريو ۽ سندس عبادت ڪريو ۽ سندس شڪرانو ڪريو، ڏانھس موٽايا ويندؤ.');
INSERT INTO "amroti" VALUES(29,18,'29:18','۽ جيڪڏھن ڪوڙو ڀانيو ٿا ته (ڇا ٿيو) بيشڪ اوھان کان اڳ (ڪيترين) اُمتن (پنھنجن پيغمبرن کي) ڪوڙو ڀانيو ھو، ۽ پيغمبر تي پڌري پيغام پھچائڻ کانسواءِ (ٻيو ڪي رکيل) نه آھي.');
INSERT INTO "amroti" VALUES(29,19,'29:19','نه ڏٺو اٿن ڇا ته الله خلق کي ڪھڙي طرح نئين سِر بنائيندو آھي؟ وري ٻيھر اُن کي بڻائيندو، بيشڪ اِھو (ڪم) الله تي آسان آھي.');
INSERT INTO "amroti" VALUES(29,20,'29:20','(کين) چؤ ته مُلڪ ۾ گھمو پوءِ نظر ڪريو ته الله ڪئن نئين سِر خلق کي پيدا ڪيو وري اُن پيدا ڪيل کي الله ٻيھر پيدا ڪندو، ڇوته الله سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(29,21,'29:21','جنھن کي وڻيس تنھن کي عذاب ڪري ۽ جنھن تي وڻيس رحم ڪري، ۽ (اوھين) ڏانھس موٽايا ويندؤ.');
INSERT INTO "amroti" VALUES(29,22,'29:22','۽ اوھين نڪي زمين ۾ ۽ نڪي آسمان ۾ عاجز ڪرڻ وارا آھيو، ۽ اوھان جو الله کانسواءِ نڪو سڄڻ ۽ نڪو مددگار آھي.');
INSERT INTO "amroti" VALUES(29,23,'29:23','۽ جن الله جي آيتن ۽ سندس ملڻ جو انڪار ڪيو سي منھنجي ٻاجھ کان نا اُميد ٿيا آھن ۽ اُنھن لاءِ ئي ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(29,24,'29:24','پوءِ ابراھيم جي قوم جي ورندي ھن چوڻ کانسواءِ (ٻي) ڪانه ھئي ته اُن کي ڪُھو يا اُن کي ساڙيو پوءِ الله کيس باھ کان بچايو، بيشڪ ھن (احوال) ۾ مڃيندڙ قوم لاءِ وڏيون نشانيون آھن.');
INSERT INTO "amroti" VALUES(29,25,'29:25','۽ (ابراھيم) چيو ته اوھان دنيا جي حياتيءَ ۾ رڳو پاڻ ۾ دوستيءَ جي ڪري الله کانسواءِ بتن کي اختيار ڪيو آھي، وري قيامت جي ڏينھن اوھين ھڪ ٻئي جو انڪار ڪندؤ ۽ اوھان مان ھڪڙا ٻـين تي لعنت ڪندا، ۽ اوھان جي جاءِ دوزخ آھي ۽ اوھان جو ڪوبه مددگار نه آھي.');
INSERT INTO "amroti" VALUES(29,26,'29:26','پوءِ ابراھيم تي لُوط ايمان آندو، ۽ (ابراھيم) چيو ته بيشڪ آءٌ پنھنجي پالڻھار ڏانھن وطن ڇڏڻ وارو آھيان، بيشڪ اُھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(29,27,'29:27','۽ اُن کي اسحاق ۽ يعقوب بخشيوسون ۽ سندس اولاد ۾ پيغمبري ۽ ڪتاب مقرر ڪيوسون ۽ کيس دنيا ۾ سندس اجر ڏنوسون، ۽ بيشڪ اُھو آخرت ۾ (به) صالحن مان آھي.');
INSERT INTO "amroti" VALUES(29,28,'29:28','۽ لُوط کي (پيغمبر ڪري موڪليوسون) جڏھن پنھنجي قوم کي چيائين ته اوھين اُھو بي حيائي جو ڪم ڪندا آھيو جو جھان وارن مان ڪنھن ھڪ (به) اھو اوھان کان اڳ نه ڪيو آھي.');
INSERT INTO "amroti" VALUES(29,29,'29:29','ڇا اوھين مڙسن ڏانھن (شھوت سان) ويندا آھيو ۽ واٽ تي ڦُر ڪندا آھيو، ۽ پنھنجن مجلسن ۾ بد ڪم ڪندا آھيو؟ پوءِ سندس قوم جي ورندي ھن چوڻ کان سواءِ نه ھئي ته جيڪڏھن سچن مان آھين ته الله جو عذاب اسان وٽ آڻ.');
INSERT INTO "amroti" VALUES(29,30,'29:30','(لُوط) چيو ته اي منھنجا پالڻھار مون کي فسادين جي قوم تي مدد ڏي.');
INSERT INTO "amroti" VALUES(29,31,'29:31','۽ جنھن مھل اسان جي قاصدن ابراھيم وٽ خوشخبري آندي (تنھن مھل) چيائون ته اسين ھن ڳوٺ وارن کي ناس ڪرڻ وارا آھيون، ڇوته اُتي جي رھڻ وارا ظالم (ٿيا) آھن.');
INSERT INTO "amroti" VALUES(29,32,'29:32','(ابراھيم) چيو ته بيشڪ اُن ۾ لُوط آھي، چيائون ته جيڪو به منجھس آھي تنھن کي اسين چڱو ڄاڻندڙ آھيون، کيس ۽ سندس گھر جي ڀاتين کي ضرور بچائينداسون سواءِ سنديس زال جي جا باقي رھيلن مان ٿيندي.');
INSERT INTO "amroti" VALUES(29,33,'29:33','۽ جنھن مھل اسان جا قاصد لُوط وٽ آيا تنھن مھل انھن جي (اچڻ) سببان ناراض ٿيو ۽ اُنھن جي (اچڻ) سببان دل تنگ ٿيو ۽ (اُنھن) چيو ته نڪي ڊڄ ۽ نڪي غم کاءُ، بيشڪ اسين توکي ۽ تنھنجي گھر جي ڀاتين کي بچائڻ وارا آھيون سواءِ تنھنجي زال جي جا باقي رھيلن مان ٿيندي.');
INSERT INTO "amroti" VALUES(29,34,'29:34','بيشڪ اسين ھن ڳوٺ وارن تي ھڪ عذاب انھي سببان آسمان کان لاھڻ وارا آھيون جو بدڪار ٿيا آھن.');
INSERT INTO "amroti" VALUES(29,35,'29:35','۽ بيشڪ اُن ڳوٺ جي پڌري نشاني سمجھندڙ قوم لاءِ ڇڏي سون.');
INSERT INTO "amroti" VALUES(29,36,'29:36','۽ مدين (وارن) ڏانھن سندن ڀاءُ شعيب کي موڪليوسون پوءِ چيائين ته اي منھنجي قوم الله جي عبادت ڪريو ۽ قيامت جي ڏينھن جي اُميد رکو ۽ فسادي ٿي مُلڪ ۾ نه ڦِرو.');
INSERT INTO "amroti" VALUES(29,37,'29:37','پوءِ اُن کي ڪوڙو ڀانيائون تنھنڪري کين زلزلي اچي ورتو پوءِ پنھنجن گھرن ۾ اونڌا ٿي (مري) پيا.');
INSERT INTO "amroti" VALUES(29,38,'29:38','۽ عاد ۽ ثمود کي (چٽ ڪيوسون) ۽ بيشڪ سندين ڪن جاين مان اوھان کي پڌرو ٿي چڪو آھي، ۽ شيطان اُنھن لاءِ سندن عمل سينگاريا ھوا پوءِ کين سِڌي واٽ کان جھليائين ۽ (اُھي) سمجھ وارا (به) ھوا.');
INSERT INTO "amroti" VALUES(29,39,'29:39','۽ قارون ۽ فرعون ۽ ھامان کي (به چٽ ڪيوسون)، ۽ بيشڪ مُوسىٰ پڌرن معجزن سان وٽن آيو پوءِ مُلڪ ۾ وڏائي ڪيائون ۽ (اڳتي) وڌي ويندڙ نه ھوا.');
INSERT INTO "amroti" VALUES(29,40,'29:40','پوءِ سڀڪنھن کي سندس گناھ سببان پڪڙيوسون، پوءِ منجھانئن ڪن تي سخت آنڌي موڪلي سون، ۽ منجھانئن ڪن کي ڪڙڪي (اچي) ورتو، ۽ منجھانئن ڪن کي زمين ۾ ڳھايوسون، ۽ منجھانئن ڪن کي ٻوڙيوسون، ۽ ڪڏھن ٿيڻو نه ھو جو الله مٿن ظلم ڪري پر ھنن پنھنجو پاڻ تي ظلم ٿي ڪيو.');
INSERT INTO "amroti" VALUES(29,41,'29:41','جن الله کانسواءِ ٻيا دوست ورتا آھن تن جو مثال ڪوريئڙي جي مثال وانگر آھي، جنھن گھر ٺاھيو، ۽ بيشڪ سڀني گھرن ۾ وڌيڪ ھيڻو ڪوريئڙي جو گھر آھي، جيڪڏھن (اھا ڳالھ) ڄاڻن ھا (ته ائين نه ڪن ھا).');
INSERT INTO "amroti" VALUES(29,42,'29:42','بيشڪ جنھن شيء کي الله کانسواءِ پوڄيندا آھن سا جيڪا به ھجي تنھن کي الله ڄاڻندو آھي، ۽ اُھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(29,43,'29:43','۽ اھي مثال آھن جن کي ماڻھن لاءِ بيان ڪندا آھيون، ۽ انھن کي ڄاڻندڙن کانسواءِ ڪونه سمجھندو آھي.');
INSERT INTO "amroti" VALUES(29,44,'29:44','الله آسمانن ۽ زمين کي (چڱيء) رِٿ سان خلقيو آھي، بيشڪ اِن (ڪم) ۾ مؤمنن لاءِ (وڏي) نشاني آھي.');
INSERT INTO "amroti" VALUES(29,45,'29:45','(اي پيغمبر) ڪتان مان جيڪي توڏانھن وحي ڪيو ويو سو پڙھ ۽ نماز پڙھ، بيشڪ نماز بي حيائي ۽ مَدي ڪم (ڪرڻ) کان جھليندي آھي، بيشڪ الله جو ياد ڪرڻ سڀ کان وڏو آھي، ۽ جيڪي ڪندا آھيو سو الله ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(29,46,'29:46','۽ ڪتاب وارن مان جن بي انصافي ڪئي تن کانسواءِ ٻـين سان ڏاڍي چڱي طريقي کانسواءِ تڪرار نه ڪريو ۽ چئو ته جيڪي اسان ڏانھن نازل ڪيو ويو آھي ۽ جيڪي اوھان ڏانھن نازل ڪيو ويو آھي تنھن تي اسان ايمان آندو آھي ۽ اسان جو معبود ۽ اوھان جو معبُود ھڪ آھي ۽ اسين سندس فرمانبردار آھيون.');
INSERT INTO "amroti" VALUES(29,47,'29:47','۽ (جھڙي طرح اڳيان ڪتاب نازل ڪياسون) اھڙي طرح اسان توڏانھن ڪتاب نازل ڪيو، پوءِ جن کي ڪتاب ڏنوسون سي اُن (قرآن) کي مڃيندا آھن، ۽ انھن (عربن) مان ڪو (اھڙو) آھي جو اُن تي ايمان آڻيندو آھي، ۽ ڪافرن کانسواءِ (ٻيو) ڪو اسان جي آيتن جو انڪار نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(29,48,'29:48','۽ اُنھيءَ (قرآن جي لھڻ) کان اڳ ڪو ڪتاب نڪي تون پڙھندو ھئين ۽ نڪي اُھو پنھنجي سڄي ھٿ سان لکندو ھئين (جيڪڏھن لکين پڙھين ھا) ته اُنھيءَ مھل اُھي ڪوڙا ضرور شڪ ۾ پون ھا.');
INSERT INTO "amroti" VALUES(29,49,'29:49','بلڪ اُھو (قرآن) پڌريون آيتون (آھن) انھن جي سينن ۾ (محفوظ) آھي جن کي عِلم ڏنو ويو آھي، ۽ ظالمن کان سواءِ (ٻيو) ڪو اسان جي آيتن جو انڪار نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(29,50,'29:50','۽ چون ٿا ته سندس پالڻھار کان مٿس مُعجزا ڇونه نازل ڪيا ويا آھن؟ (کين) چؤ مُعجزا (ته رڳو منھنجي) الله وٽ آھن، ۽ آءٌ ته رڳو پڌرو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(29,51,'29:51','اسان توتي ڪتاب نازل ڪيو جو کين پڙھي ٻڌائبو آھي سو کين ڪافي نه آھي ڇا، بيشڪ ھن ۾ مؤمنن جي قوم لاءِ ٻاجھ ۽ نصيحت آھي.');
INSERT INTO "amroti" VALUES(29,52,'29:52','(کين) چؤ ته الله منھنجي ۽ اوھان جي وچ ۾ شاھد بس آھي، جيڪي آسمانن ۽ زمين ۾ آھي سو اُھو ڄاڻندو آھي، ۽ جن ڪوڙ کي مڃيو ۽ الله جو انڪار ڪيو سي ئي ڇيئي وارا آھن.');
INSERT INTO "amroti" VALUES(29,53,'29:53','۽ توکان عذاب تڪڙو گھرندا آھن، ۽ جيڪڏھن (اُن جي) مُدّت مُقرر ٿيل نه ھجي ھا ته کين عذاب ضرور پھچي ھا، ۽ (اھو) مٿن پڪ اوچتو ايندو ۽ اُھي بي خبر ھوندا.');
INSERT INTO "amroti" VALUES(29,54,'29:54','توکان عذاب تڪڙو گھرندا آھن، بيشڪ دوزخ ڪافرن کي (اُنھيءَ ڏينھن) وڪوڙيندڙ آھي.');
INSERT INTO "amroti" VALUES(29,55,'29:55','جنھن ڏينھن کين عذاب سندن مٿن کان ۽ سندن پيرن جي ھيٺ کان ويڙھيندو ۽ (الله) چوندو ته جيڪي ڪندا ھيؤ تنھنجو (مزو) چکو.');
INSERT INTO "amroti" VALUES(29,56,'29:56','اي منھنجا مؤمن ٻانھؤ بيشڪ منھنجي زمين ويڪري آھي تنھنڪري خاص منھنجي عبادت ڪريو.');
INSERT INTO "amroti" VALUES(29,57,'29:57','سڀڪو جيءُ موت جو مزو چکڻ وارو آھي، وري اسان ڏي موٽائبؤ.');
INSERT INTO "amroti" VALUES(29,58,'29:58','۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي ضرور بھشت جي ماڙين ۾ ٽِڪائينداسون جن جي ھيٺان واھيون وھنديون آھن منجس سدائين رھڻ وارا آھن، (انھن چڱن) ڪمن ڪندڙن جي مزوري چڱي آھي.');
INSERT INTO "amroti" VALUES(29,59,'29:59','جن صبر ڪيو ۽ پنھنجي پالڻھار تي ڀروسو ڪندا آھن.');
INSERT INTO "amroti" VALUES(29,60,'29:60','۽ ڪيترائي جانور پنھنجي روزي ساڻ نه کڻندا آھن، الله تعالىٰ انھن کي ۽ اوھان کي روزي ڏيندو آھي، ۽ اُھو ئي ٻڌندڙ ۽ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(29,61,'29:61','۽ جيڪڏھن کائن پُڇين ته ڪنھن آسمانن ۽ زمين کي بڻايو ۽ (ڪنھن) سج ۽ چنڊ کي نِوايو آھي؟ ته ضرور چوندا ته الله، پوءِ ڪيڏانھن ڦيرايا ويندا آھن.');
INSERT INTO "amroti" VALUES(29,62,'29:62','الله پنھنجن ٻانھن مان جنھن لاءِ گھري تنھنجي روزي ڪشادي ڪندو آھي ۽ (جنھن لاءِ گھري ته تَنھنجي) گھٽ ڪندو آھي، ڇوته الله ھر شيءَ کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(29,63,'29:63','۽ جيڪڏھن کائن پڇين ته ڪنھن آسمان کان پاڻي وسايو پوءِ اُن سان زمين کي اُن جي مرڻ کانپوءِ جيئرو ڪيو ته ضرور چوندا ته الله، (اي پيغمبر کين) چؤ ته سڀ ساراھ الله کي جڳائي، بلڪ منجھائن گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(29,64,'29:64','۽ ھيءَ دنيا جي حياتي ته تماشي ۽ راند کانسواءِ (ٻيو) ڪي نه آھي، ۽ بيشڪ آخرت جي حويلي ئي سدائين حياتيءَ واري آھي، جيڪڏھن ڄاڻن ھا (ته آخرت کي دنيا کان وڌيڪ پسند ڪن ھا).');
INSERT INTO "amroti" VALUES(29,65,'29:65','پوءِ (اُھي) جڏھن ٻيڙين ۾ چڙھندا آھن (تڏھن) رڳو الله کي سندس اعتقاد رکي سڏيندا آھن، پوءِ جڏھن کين ڀر ڏانھن پھچائيندو (بچائي) آھي (تڏھن) اُتي جو اُتي (ھن ڪري) شرڪ ڪندا آھن.');
INSERT INTO "amroti" VALUES(29,66,'29:66','ته جيڪي کين ڏنوسون تنھنجي بي شُڪري ڪن، ۽ ته خوش گذارين، پوءِ سگھوئي (حال جي حقيقت) ڄاڻندا.');
INSERT INTO "amroti" VALUES(29,67,'29:67','نه ڏٺائون ڇا ته اسان (مڪي جي) حرم کي امن جي جاءِ ڪئي؟ حالانڪ سندس آس پاس کان ماڻھو اُمالڪ کڄي ويندا آھن، پوءِ ڇا ڪوڙ کي مڃيندا آھن ۽ الله جي نعمتن جو انڪار ڪندا آھن؟');
INSERT INTO "amroti" VALUES(29,68,'29:68','۽ اُن کان وڌيڪ ڪير ظالم آھي جيڪو الله تي ڪوڙو ٺاھ ٺاھي يا جڏھن وٽس حق آيو تڏھن اُن کي ڪوڙو ڄاڻي؟ ڪافرن جي جاءِ دوزخ ۾ نه آھي ڇا؟');
INSERT INTO "amroti" VALUES(29,69,'29:69','۽ جن اسان جي واٽ ۾ ڪوشش ڪئي تن کي ضرور پنھنجون واٽون ڏيکارينداسون، ۽ بيشڪ الله ڀلارن سان گڏ آھي.');
INSERT INTO "amroti" VALUES(30,1,'30:1','الٓمّٓ.');
INSERT INTO "amroti" VALUES(30,2,'30:2','رُومين شڪست کاڌي.');
INSERT INTO "amroti" VALUES(30,3,'30:3','ويجھي مُلڪ ۾ ۽ اُھي ئي پنھنجي شڪست کائڻ کانپوءِ سگھوئي غالب ٿيندا.');
INSERT INTO "amroti" VALUES(30,4,'30:4','ٿورن ورھن ۾، الله جي ئي (ھٿ ۾) حڪومت اڳي توڙي پوءِ آھي، ۽ اُنھيءَ ڏينھن مُؤمن خوش ٿيندا.');
INSERT INTO "amroti" VALUES(30,5,'30:5','الله جي مدد سان، (الله) جنھن کي گھري تنھن کي سوڀ ڏيندو آھي، ۽ اُھو ئي غالب مھربان آھي.');
INSERT INTO "amroti" VALUES(30,6,'30:6','الله انجام ڪيو آھي، الله پنھنجو انجام نه ڦيرائيندو پر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(30,7,'30:7','ظاھر دنيا جي حياتيءَ جون (رٿون) ڄاڻندا آھن، ۽ اُھي آخرت کان بي خبر آھن.');
INSERT INTO "amroti" VALUES(30,8,'30:8','پنھنجين دلين ۾ نه پروڙيائون ڇا؟ ته الله آسمانن ۽ زمين کي ۽ جيڪي انھن جي وچ ۾ آھي تنھنکي پوريءَ رِٿ ۽ مُقرّر مُدّت کانسواءِ نه بڻايو آھي، ۽ بيشڪ ماڻھن مان گھڻا پنھنجي پالڻھار جي مِلڻ کان مُنڪر آھن.');
INSERT INTO "amroti" VALUES(30,9,'30:9','مُلڪ ۾ نه گھميا آھن ڇا جو نھارين ته جيڪي کانئن اڳ ھوا تن جي پڇاڙي ڪھڙي طرح ٿي، جي کائن وڌيڪ سگھ وارا ھوا ۽ زمين کيڙيائون ۽ اُن کي آباد ڪيائون وڌيڪ تنھن کان جا ھنن آباد ڪئي ۽ سندن پيغمبر وٽن مُعجزن سان آيا، پوءِ الله جو شان اھڙو نه ھو جو مٿن ظلم ڪري پر پاڻ تي پاڻ ظلم ڪيائون ٿي.');
INSERT INTO "amroti" VALUES(30,10,'30:10','وري جن بڇڙائي ڪئي تن جي پڇاڙي بڇڙي ٿي ھِن ڪري جو الله جي آيتن کي ڪوڙ ڀانيائون ۽ مٿن ٺـٺوليون ڪندا ھوا.');
INSERT INTO "amroti" VALUES(30,11,'30:11','الله خلق کي نئين سر بڻائي ٿو وري ان کي ٻيھر بڻائيندو وري ڏانھس اوھين موٽائبؤ.');
INSERT INTO "amroti" VALUES(30,12,'30:12','۽ جنھن ڏينھن قيامت کڙي ٿيندي تنھن ڏينھن گنھگار نا اُميد ٿيندا.');
INSERT INTO "amroti" VALUES(30,13,'30:13','۽ (الله سان مُقرّر ڪيلن) سندن شريڪن مان ڪوبه انھن لاءِ سفارش ڪرڻ وارو نه ٿيندو ۽ پنھنجن (ٺھرايل) شريڪن جا منڪر ٿيندا.');
INSERT INTO "amroti" VALUES(30,14,'30:14','۽ جنھن ڏينھن قيامت قائم ٿيندي تنھن ڏينھن ماڻھو ڌاروڌار ٿيندا.');
INSERT INTO "amroti" VALUES(30,15,'30:15','پوءِ جن ايمان آندو ۽ چڱا ڪم ڪيا سي باغن ۾ خوش ڪبا.');
INSERT INTO "amroti" VALUES(30,16,'30:16','۽ جن ڪُفر ڪيو ۽ اسان جي آيتن کي ۽ قيامت جي ملڻ کي ڪوڙ ڄاتو سي عذاب ۾ حاضر ڪيل ھوندا.');
INSERT INTO "amroti" VALUES(30,17,'30:17','پوءِ جنھن مھل سانجھي ڪريو ۽ جنھن مھل صبح ڪريو تنھن مھل الله جي پاڪائي بيان ڪريو.');
INSERT INTO "amroti" VALUES(30,18,'30:18','۽ آسمانن ۽ زمين ۾ اُن جي ساراھ آھي ۽ پوئين پھر ۽ جنھن مھل منجھند ڪريو (تنھن مھل به پاڪائي بيان ڪريو).');
INSERT INTO "amroti" VALUES(30,19,'30:19','مُئي مان جيئرو ڪڍندو آھي ۽ جيئري مان مئو ڪڍندو آھي ۽ زمين کي ان جي مرڻ کانپوءِ جياريندو آھي، ۽ اھڙي طرح (اوھين قبرن مان) ڪڍبؤ.');
INSERT INTO "amroti" VALUES(30,20,'30:20','۽ سندس نشانين مان ھيءُ آھي جو اوھان کي مٽيءَ مان بڻايائين وري اُنھيءَ مھل اوھين ماڻھو ٿي گھمو ڦرو ٿا.');
INSERT INTO "amroti" VALUES(30,21,'30:21','۽ سندس نشانين مان ھيءُ (به) آھي جو اوھان جي جنس مان اوھان لاءِ زالون پيدا ڪيائين ته انھن سان آرام وٺو ۽ اوھان جي وچ ۾ پيار ۽ ٻاجھ پيدا ڪيائين، بيشڪ ھن ۾ اُنھيءَ قوم لاءِ نشانيون آھن جيڪي ڌيان رکندا آھن.');
INSERT INTO "amroti" VALUES(30,22,'30:22','آسمانن ۽ زمين جو بڻائڻ ۽ اوھان جي زبانن ۽ شڪلن جو قِسمين قِسمين ھجڻ سندس نشانين مان آھي، بيشڪ ان ۾ ڄاڻندڙن لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(30,23,'30:23','۽ اوھان جو رات ۽ ڏينھن ۾ سمھڻ ۽ اوھان جو سندس فضل مان روزي طلب ڪرڻ سندس نشانين مان آھي، بيشڪ ھن ۾ اُن قوم لاءِ نشانيون آھن جيڪي ٻڌندا آھن.');
INSERT INTO "amroti" VALUES(30,24,'30:24','۽ سندس نشانين مان (ھيءُ به) آھي جو اوھان کي ڊيڄارڻ ۽ اُميد رکڻ لاءِ وِڄ ڏيکاريندو آھي ۽ آسمان کان پاڻي وسائيندو آھي پوءِ اُن سان زمين کي اُن جي مرڻ کانپوءِ جياريندو آھي، بيشڪ ھِن ۾ اُنھيءَ قوم لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(30,25,'30:25','۽ سندس حُڪم سان آسمان ۽ زمين جو بيھڻ سندس نشانين مان آھي، وري جنھن مھل اوھان کي زمين مان ھڪ ئي ڀيرو سڏيندو (تنھن مھل) جھٽ پٽ اوھين نڪرندؤ.');
INSERT INTO "amroti" VALUES(30,26,'30:26','۽ جيڪي آسمانن ۽ زمين ۾ آھي سو سندس آھي، سڀئي سندس فرمانبردار آھن.');
INSERT INTO "amroti" VALUES(30,27,'30:27','۽ اُھوئي آھي جيڪو خلق کي نئين سِر بڻائيندو آھي وري اُن کي ورائي بڻائيندو ۽ اُھو کيس بلڪل سھنجو آھي، ۽ آسمانن ۽ زمين ۾ سندس ئي صفت تمام مٿاھين آھي، ۽ اھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(30,28,'30:28','(الله) اوھان لاءِ اوھان جي حال جو ھڪ مثال بيان ڪيو آھي، (ته) اوھان جا ھٿ جن (ٻانھن) جا مالڪ ٿيا آھن سي جيڪي (مال) اوھان کي ڏنوسين تنھن ۾ اوھان جا ڀائيوار آھن ڇا؟ جو اُھي ۽ اوھين اُن (مال) ۾ برابر (حصّي وارا) ھجو (۽ اوھين) انھن جي (دعوى) کان اھڙي طرح ڊڄندا آھيو جھڙي طرح اوھان کي پاڻ جھڙن (جي ڀائيواريءَ) کان ڀؤ آھي، اھڙي طرح انھيءَ قوم لاءِ نشانيون کولي بيان ڪندا آھيون جيڪي سمجھ رکندا آھن.');
INSERT INTO "amroti" VALUES(30,29,'30:29','بلڪ ظالم اَڻ ڄاڻائي سان پنھنجن سَڌن جا تابعدار ٿيا آھن، پوءِ جنھن کي الله گمراھ ڪيو تنھن کي ڪير ھدايت ڪندو، ۽ اُنھن لاءِ ڪو مددگار ڪونھي.');
INSERT INTO "amroti" VALUES(30,30,'30:30','پوءِ (اي پيغمبر) پنھنجي منھن کي دين لاءِ ھڪ طرفو ڪري بيھار، الله جي دين جي (پيروي ڪر) جنھن تي ماڻھن کي پيدا ڪيائين، الله جي خلقڻ کي ڪا مٽ سٽ نه آھي، اھو سِڌو دين آھي، پر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(30,31,'30:31','ڏانھس موٽندڙ ٿي (عبادت ڪريو) ۽ کانئس ڊڄو ۽ نماز پڙھندا رھو ۽ مشرڪن مان نه ٿيو.');
INSERT INTO "amroti" VALUES(30,32,'30:32','اُنھن مان (نه ٿيو) جن پنھنجي دين کي ڌارو ڌار ڪيو ۽ ٽوليون ٽوليون ٿي ويا، سڀڪا ٽولي اُنھيءَ تي خوش آھي جيڪي وٽس آھي.');
INSERT INTO "amroti" VALUES(30,33,'30:33','۽ جڏھن ماڻھن کي ڪا اوکائي پھچندي آھي (تڏھن) پنھنجي پالڻھار ڏي مُھاڙ ڪري کيس سڏيندا آھن وري جڏھن اُھو پاڻ وٽان کين ٻاجھ چکائيندو آھي (تڏھن) اُنھيءَ ئي مھل منجھائن ڪا ٽولي پنھنجي پالڻھار سان (ھن لاءِ) شريڪ بڻائيندي آھي.');
INSERT INTO "amroti" VALUES(30,34,'30:34','ته جيڪي کيس ڏنوسون تنھنجي بي شُڪري ڪن، پوءِ آسودا ٿيو، پوءِ سگھوئي ڄاڻندؤ.');
INSERT INTO "amroti" VALUES(30,35,'30:35','مٿن ڪا سَند لاٿي سون ڇا جو اُھا اُنھيءَ (شيء) جي (سچائي) بابت ڳالھائي ٿي جنھن شيءَ کي الله سان شريڪ ٺھرائيندا آھن.');
INSERT INTO "amroti" VALUES(30,36,'30:36','۽ جڏھن ماڻھن کي ٻاجھ چکائيندا آھيون (تڏھن) اُن سان سَرھا ٿيندا آھن، ۽ جيڪڏھن انھي سببان جو سندن ھٿن (مدايون) اڳي موڪليون، کين ڪو ڏک پھچندو آھي ته اُنھي ئي مھل نا اُميد ٿيندا آھن.');
INSERT INTO "amroti" VALUES(30,37,'30:37','نه ڏٺو اٿن ڇا؟ ته الله جنھن لاءِ گھرندو آھي تنھنجي روزي ڪشادي ڪندو آھي ۽ (جنھن لاءِ گھرندو آھي تنھنجي) گھٽ ڪندو آھي، بيشڪ ھن ۾ مڃيندڙ قوم لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(30,38,'30:38','تنھنڪري مائٽي واري ۽ مِسڪين ۽ مُسافر کي سندس حق ڏي، اھو انھن لاءِ ڀلو آھي جيڪي الله جو راضپو گھرندا آھن، ۽ اُھي ئي ڇوٽڪاري وارا آھن.');
INSERT INTO "amroti" VALUES(30,39,'30:39','۽ وياج مان جيڪي ڏنو ھجيوَ ته ماڻھن جي مال ۾ وَڌي سو الله وٽ نه وڌندو، ۽ الله جو راضپو گھرندڙ ٿي زڪوٰة مان جيڪي ڏنو ھجيوَ پوءِ اُھي ئي ٻيڻو ڪندڙ آھن.');
INSERT INTO "amroti" VALUES(30,40,'30:40','الله اُھو آھي جنھن اوھان کي بڻايو وري اوھان کي روزي ڏنائين وري اوھان کي ماريندو وري اوھان کي جياريندو، اوھان جي (ٺھرايل) شريڪن مان ڪو اھڙو آھي ڇا جو اھڙن ڪَمن مان ڪجھ ڪري (سگھي)؟ الله پاڪ آھي ۽ اُن کان مٿاھون آھي جنھن کي (ساڻس) شريڪ ٺھرائيندا آھن.');
INSERT INTO "amroti" VALUES(30,41,'30:41','ماڻھن جي ھٿن جي ڪئي سببان بَر ۽ بَحر ۾ فساد ھن لاءِ پڌرو ٿيو آھي ته جيڪي (مَدا ڪم) ڪيائون تنھن جي ڪُجھ (سزا) کين چکائي ته مانَ اُھي موٽن.');
INSERT INTO "amroti" VALUES(30,42,'30:42','چؤ ته مُلڪ ۾ گھُمو پوءِ ڏسو ته جيڪي اڳ ھوا تن جي پڇاڙي ڪئن ٿي، منجھائن گھڻا مشرڪ ھوا.');
INSERT INTO "amroti" VALUES(30,43,'30:43','پوءِ پنھنجي منھن کي اُن ڏينھن جي اچڻ کان اڳ سڌي دين لاءِ بيھار جو الله وٽان اُن (ڏينھن) کي رد ٿيڻو نه آھي اُن ڏينھن (ماڻھو) الڳ الڳ ٿيندا.');
INSERT INTO "amroti" VALUES(30,44,'30:44','جيڪو ڪافر ٿيو تنھن تي سندس ڪفر آھي، ۽ جن چڱا ڪم ڪيا سي پاڻ لاءِ ھنڌ وڇائين ٿا.');
INSERT INTO "amroti" VALUES(30,45,'30:45','ته جن ايمان آندو ۽ چڱا عمل ڪيا تن کي الله فضل سان بدلو ڏئي، بيشڪ اُھو ڪافرن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(30,46,'30:46','۽ سندس نشانين مان ھي آھي ته خوشخبري ڏيندڙ ھيرون گھُلائيندو آھي ۽ ته اوھان کي پنھنجي ٻاجھ مان ڪجھ چکائي ۽ ته سندس حُڪم سان ٻيڙيون تَرن ۽ ته سندس فضل مان روزي طلب ڪريو ۽ ته مانَ اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(30,47,'30:47','۽ بيشڪ توکان اڳ گھڻن پيغمبرن کي سندن قوم ڏانھن موڪليوسون پوءِ وٽن مُعجزن سان آيا پوءِ جن نافرماني ڪئي تن کان بدلو ورتوسون، ۽ مؤمنن کي مدد ڪرڻ اسان تي حق آھي.');
INSERT INTO "amroti" VALUES(30,48,'30:48','الله اُھو آھي جيڪو وائن کي موڪليندو آھي پوءِ (اُھي) ڪڪر کي کڻنديون آھن پوءِ جئن الله گھرندو آھي تئن آسمان ۾ اُن (ڪڪر) کي کنڊرائيندو آھي پوءِ اُن کي تَه تَه ڪندو آھي پوءِ (تون) ڦُڙين کي ڏسندو آھين جو اُن (ڪڪر) جي وچ مان نڪرنديون آھن، پوءِ جڏھن پنھنجن ٻانھن مان جنھن تي وڻندو اٿس تنھن تي اُھو (مينھن) پھچائيندو آھي (تڏھن) اُنھيءَ ئي مھل اُھي خوش ٿيندا آھن.');
INSERT INTO "amroti" VALUES(30,49,'30:49','۽ جيتوڻيڪ ان (مينھن) جي مٿن وَسائجڻ کان اڳ اڳيئي نااُميد ٿيندا ھوا.');
INSERT INTO "amroti" VALUES(30,50,'30:50','پوءِ الله جي ٻاجھ جي نشانين ڏانھن نھار ته ڪھڙي طرح زمين کي سندس مرڻ کان پوءِ جياري ٿو، بيشڪ اُھو (الله) مُئن کي ضرور جيارڻ وارو آھي، ۽ اُھو (الله) سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(30,51,'30:51','۽ جيڪڏھن ڪو واءُ (جو جھولو) گھُلايون پوءِ اُن (پوک) کي ھيڊو ٿيندو ڏسن ته اُن کانپوءِ بي شڪري ڪرڻ لڳندا آھن.');
INSERT INTO "amroti" VALUES(30,52,'30:52','پوءِ (اي پيغمبر) تون سڏُ نڪي مئن کي ٻڌائي سگھندين ۽ نڪي ٻَوڙن کي ٻڌائي سگھندين جڏھن ته اُھي پٺي ڏئي مُنھن ڦيرائين.');
INSERT INTO "amroti" VALUES(30,53,'30:53','۽ نڪي تون انڌن کي سندن گمراھيءَ کان (روڪي) سڌو رستو ڏيکاريندڙ آھين، تون ته رڳو اُنھيءَ کي ٻڌائي سگھندين جنھن اسان جي آيتن تي ايمان آندو پوءِ اُھي فرمانبردار به آھن.');
INSERT INTO "amroti" VALUES(30,54,'30:54','الله اُھو آھي جنھن اوھان کي (پھرين) ھيڻائي (واري شيء) مان خلقيو آھي وري ھيڻائيءَ کان پوءِ (اوھان ۾) سگھ پيدا ڪيائين وري سگھ کان پوءِ (اوھان کي) ھيڻائي ۽ ٻڍاپو ڏنائين، جئن گھرندو آھي تئن بڻائيندو آھي، ۽ اُھو ڄاڻندڙ وس وارو آھي.');
INSERT INTO "amroti" VALUES(30,55,'30:55','۽ جنھن ڏينھن قيامت قائم ٿيندي (تنھن ڏينھن) قسم کڻندا ته (دنيا ۾) ھڪ گھڙي کانسواءِ (وڌيڪ) نه رھيا ھواسون، اھڙي طرح (رستي کان) ڦيرايا ويندا ھوا.');
INSERT INTO "amroti" VALUES(30,56,'30:56','۽ جن کي عِلم ۽ ايمان ڏنو ويو آھي سي کين چوندا ته الله جي ڪتاب ۾ لکي موافق جيئري ٿي اُٿڻ جي ڏينھن توڻي (دنيا ۾) رھيا آھيو، پوءِ ھي جيئري ٿي اُٿڻ جو ڏينھن آھي پر اوھين نه ڄاڻندا ھيؤ.');
INSERT INTO "amroti" VALUES(30,57,'30:57','پوءِ اُن ڏينھن ظالمن کي سندن عذر ڪرڻ فائدو نه ڏيندو ۽ نڪي انھن کان توبه طلب ڪبي.');
INSERT INTO "amroti" VALUES(30,58,'30:58','۽ بيشڪ ماڻھن لاءِ ھن قرآن ۾ سڀڪنھن جنس جا مثال بيان ڪياسون، ۽ جيڪڏھن وٽن ڪا نشاني (تون) آڻين ته ڪافر ضرور چوندا ته اوھين رڳو ڪُوڙا آھيو.');
INSERT INTO "amroti" VALUES(30,59,'30:59','اھڙيءَ طرح جيڪي نه ڄاڻندا آھن تن جي دلين تي الله مُھر ھڻندو آھي.');
INSERT INTO "amroti" VALUES(30,60,'30:60','پوءِ (اي پيغمبر) صبر ڪر ڇوته الله جو واعدو سچ آھي ۽ جيڪي يقين نه رکندا آھن سي توکي ھلڪو نه ڪن.');
INSERT INTO "amroti" VALUES(31,1,'31:1','الٓمّٓ.');
INSERT INTO "amroti" VALUES(31,2,'31:2','ھي آيتون حِڪمت واري ڪتاب جون آھن.');
INSERT INTO "amroti" VALUES(31,3,'31:3','(جو) ھدايت ۽ ٻاجھ انھن ڀلارن لاءِ آھي.');
INSERT INTO "amroti" VALUES(31,4,'31:4','جيڪي نماز پڙھندا آھن ۽ زڪوٰة ڏيندا آھن ۽ اُھي آخرت جو ويساھ (به) رکندا آھن.');
INSERT INTO "amroti" VALUES(31,5,'31:5','اُھي پنھنجي پالڻھار جي سڌي رستي تي آھن ۽ اُھي ئي ڇوٽڪاري وارا آھن.');
INSERT INTO "amroti" VALUES(31,6,'31:6','۽ ماڻھن مان ڪو اھڙو آھي جو بيھوديءَ ڳالھ کي ڳنھندو آھي ته (ماڻھن کي) اڻ ڄاڻائيءَ سان الله جي واٽ کان ڀلائي، ۽ اُن (واٽ) کي ٺـٺولي ڪري وٺي، اُنھن لاءِ ئي خواريءَ وارو عذاب آھي.');
INSERT INTO "amroti" VALUES(31,7,'31:7','۽ جڏھن اسان جون آيتون کيس پڙھي ٻڌائبيون آھن (تڏھن) ھٺيلو ٿي منھن ڦيرائيندو آھي ڄڻڪ اُنھن کي ٻڌوئي نه ھوائين ڄڻڪ سندس ٻنھي ڪنن ۾ گھٻرائي آھي، تنھنڪري کيس ڏکوئيندڙ عذاب جي خبر ڏي.');
INSERT INTO "amroti" VALUES(31,8,'31:8','بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ نعمت وارا باغ آھن.');
INSERT INTO "amroti" VALUES(31,9,'31:9','منجھس سدائين رھڻ وارا آھن، (اِھو) الله جو پڪو انجام ڏنل آھي، ۽ اُھو غالب حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(31,10,'31:10','آسمانن کي ريءَ ٿنڀن جي بڻايائين جو کين ڏسو ٿا ۽ زمين ۾ جبل وڌائين ته متان اوھان کي ڌوڏي ۽ منجھس ھر جنس جا جانور پکيڙيائين، ۽ آسمان کان پاڻي وسايو سون پوءِ منجھس سٺا ھر جنس جا اوڀڙ ڄماياسون.');
INSERT INTO "amroti" VALUES(31,11,'31:11','اِھا الله جي بڻاوت آھي پوءِ مونکي ڏيکاريو ته جيڪي اُن (الله) کانسواءِ (ٻيا) آھن تن ڇا بڻايو آھي، بلڪ ظالم پڌريءَ ڀُل ۾ آھن.');
INSERT INTO "amroti" VALUES(31,12,'31:12','۽ بيشڪ لُقمان کي حِڪمت ڏني سون (چيوسون) ته الله جو شُڪر ڪر، ۽ جيڪو شُڪر ڪندو سو رڳو پنھنجي جيءَ (جي نفع) لاءِ شُڪر ڪندو، ۽ جيڪو بي شڪري ڪندو ته بيشڪ الله بي پرواھ ساراھيل آھي.');
INSERT INTO "amroti" VALUES(31,13,'31:13','۽ (ياد ڪر) جڏھن لُقمان پنھنجي پُٽ کي چيو ۽ اُنھيءَ کيس سمجھايو ٿي ته اي مُنھنجا پُٽڙا الله سان (ڪنھن کي) شريڪ نه ڪر، ڇوته شرڪ وڏي بي انصافي آھي.');
INSERT INTO "amroti" VALUES(31,14,'31:14','۽ ماڻھوءَ کي سندس ماءُ پيءُ بابت حڪم ڪيوسون، جو ماڻس تڪليف جي مٿان تڪليف سھي کيس (پنھنجي پيٽ ۾) کنيو ۽ ٻن ورھن ۾ سندس (بُبن جو) ڇڏائڻ آھي (حُڪم جو مطلب ھيءُ آھي) ته منھنجو ۽ پنھنجي ماءُ پيءُ جو شڪرانو ڪر (جو آخر) مون ڏانھن (ئي) موٽڻ آھي.');
INSERT INTO "amroti" VALUES(31,15,'31:15','۽ جيڪڏھن (اُھي) مون سان (ڪنھين کي) شريڪ ڪرڻ جي توکي ڪوشش ڪن جنھن جي توکي ڪا ڄاڻپ نه آھي ته سندن (انھي چوڻ جي) فرمانبرداري نه ڪر ۽ دُنيا ۾ ساڻن چڱي ريت سنگت ڪر، ۽ جنھن مون ڏانھن مُھاڙ ڪئي ھجي تنھنجي واٽ جي تابعداري ڪر، وري اوھان جو مون ڏانھن ورڻ آھي پوءِ جيڪي ڪندا ھيؤ تنھنجي اوھان کي سُڌ ڏيندس.');
INSERT INTO "amroti" VALUES(31,16,'31:16','(لُقمان ھيءَ به چيو ته) اي منھنجا پُٽڙا ڪابه (شيء) جيڪڏھن آھر جي داڻي جتري آھي پوءِ اُھا پھڻ ۾ ھجي يا آسمانن ۾ يا زمين ۾ (ھجي) ته الله اُن کي آڻي حاضر ڪندو، ڇوته الله باريڪ بين خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(31,17,'31:17','اي منھنجا پٽڙا نماز پڙھ ۽ چڱي ڪم جو حُڪم ڪر ۽ مدي (ڪم) کان منع ڪر ۽ توکي جيڪا اوکائي پھچي تنھن تي صبر ڪر، بيشڪ اھي (ڪم) ھمت وارن ڪمن مان آھن.');
INSERT INTO "amroti" VALUES(31,18,'31:18','۽ ماڻھن لاءِ پنھنجو ڳل (يعني منھن) چٻو نه ڪر ۽ زمين ۾ لاڏ سان نه گھُم، بيشڪ الله ھٺيلي وڏائي ڪندڙ کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(31,19,'31:19','۽ پنھنجي گھمڻ ۾ وچولي چال ھل ۽ پنھنجو آواز ڍرو ڪر، ڇوته آوازن مان بڇڙو (آواز) گڏھ جي ھينگ آھي.');
INSERT INTO "amroti" VALUES(31,20,'31:20','نه ڏٺو اٿو ڇا ته جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله اوھان کي تابع ڪري ڏنو آھي ۽ پنھنجون پڌريون ۽ ڳجھون نعمتون اوھان تي ڀرپور ڪيائين، ۽ ماڻھن مان ڪي (اھڙا) آھن جو اَڻ ڄاڻائي سان ۽ بنا ھدايت ۽ روشن ڪتاب ڌاران الله بابت جھڳڙو ڪندا آھن.');
INSERT INTO "amroti" VALUES(31,21,'31:21','۽ جڏھن کين چئبو آھي ته جيڪي الله نازل ڪيو تنھن جي تابعداري ڪريو (تڏھن) چوندا آھن (ته نه!) بلڪ اسان جنھن تي پنھنجا پيءُ ڏاڏا (ھلندا) ڏٺا تنھن جي تابعداري ڪنداسون، توڻيڪ شيطان دوزخ جي عذاب ڏانھن کين سڏي ٿو ته به (ان جي پيروي ڪندا ڇا)؟');
INSERT INTO "amroti" VALUES(31,22,'31:22','۽ جنھن پنھنجي مھاڙ الله ڏانھن ڪئي ۽ اُھو چڱن ڪمن ڪرڻ وارو به آھي تنھن بيشڪ مُحڪم رسيءَ کي پڪڙيو، ۽ (سڀني) ڪمن جي پڇاڙي الله ڏانھن آھي.');
INSERT INTO "amroti" VALUES(31,23,'31:23','۽ جيڪو ڪفر ڪري تنھن جو ڪفر توکي غمگين نه ڪري، کين اسان ڏانھن ورڻو آھي پوءِ جيڪي ڪيائون تنھن جي کين سُڌ ڏينداسون، ڇوته الله سينن وارو ڳجھ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(31,24,'31:24','(دنيا ۾) کين ٿورو (وقت) آسودو ڪريون ٿا وري کين سخت عذاب ڏانھن ڇڪي نينداسون.');
INSERT INTO "amroti" VALUES(31,25,'31:25','۽ جيڪڏھن کائن پڇندين ته آسمانن ۽ زمين کي ڪنھن بڻايو آھي ته ضرور چوندا ته الله، چؤ ته سڀ ساراھ الله کي جڳائي، بلڪ اُنھن مان گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(31,26,'31:26','جيڪي آسمانن ۽ زمين ۾ آھي سو الله جو ئي آھي، بيشڪ الله اُھوئي بي پرواھ (ڏاڍو) ساراھيل آھي.');
INSERT INTO "amroti" VALUES(31,27,'31:27','۽ جيڪي وڻ زمين ۾ آھن سي جيڪڏھن قلم ٿين ۽ سمنڊ (جو پاڻي) اُن جي مَسُ ٿئي اُن کانپوءِ ست سمنڊ (ٻيا به) ھجن ته الله (جي قدرت) جون ڳالھيون نه کُٽنديون، بيشڪ الله غالب حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(31,28,'31:28','اوھان (سڀني) جو خلقڻ ۽ اوھان جو وري جيارڻ رڳو ھڪڙيءَ ماڻھوءَ (جي خلقڻ ۽ وري جيارڻ) جھڙو آھي بيشڪ الله ٻڌندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(31,29,'31:29','نه ڏٺو اٿيئي ڇا ته الله رات کي ڏينھن ۾ داخل ڪندو آھي ۽ ڏينھن کي رات ۾ داخل ڪندو آھي ۽ سج ۽ چنڊ کي نِوايائين، سڀڪو مقرر مُدت توڻي ھلندو آھي ۽ (نه ڏٺو اٿيئي ڇا) ته جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(31,30,'31:30','اِھي (ڪم) ھن ڪري آھن ته الله ئي برحق آھي ۽ ته اُن کانسواءِ جنھن به ٻي کي سڏيندا آھن سو ڪوڙ آھي ۽ ته الله ئي تمام مٿاھون وڏو آھي.');
INSERT INTO "amroti" VALUES(31,31,'31:31','نه ڏٺو اٿيئي ڇا ته ٻيڙيون الله جي فضل سان سمنڊ ۾ ترنديون آھن (ھن لاءِ) ته (الله) اوھان کي پنھنجين نشانين مان ڏيکاري، بيشڪ ھن ۾ سڀڪنھن وڏي صابر شاڪر لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(31,32,'31:32','۽ جڏھن کين (درياءَ جي) لھر پاڇي وارن پھاڙن وانگر ويڙھيندي آھي (تڏھن) خاص الله کي نِلو ڏانھس مھاڙ ڪري سڏيندا آھن، پوءِ جنھن مھل کين ڀر ڏانھن بچارائي پھچائيندو آھي (تنھن مھل) منجھائن ڪي وچٿرا ھوندا آھن، سڀڪنھن انجام ڀڃندڙ بي شڪر کانسواءِ ٻيو ڪوبه اسان جي آيتن جو انڪار نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(31,33,'31:33','اي ماڻھؤ پنھنجي پالڻھار کان ڊڄو ۽ اُن ڏينھن کان (به) جنھن ۾ ڪو پيءُ پنھنجي پُٽ جي ڪم نه ايندو، ۽ نه پُٽ پنھنجي پيءُ جي ڪجھ ڪم ايندڙ ھوندو، بيشڪ الله جو وعدو سچو آھي تنھنڪري نڪي اوھان کي دنيا جي حياتي ٺڳي، ۽ نڪي اوھان کي الله بابت ٺڳيندڙ (شيطان) ٺڳي.');
INSERT INTO "amroti" VALUES(31,34,'31:34','بيشڪ قيامت جي خبر الله ئي وٽ آھي، ۽ اُھوئي مينھن وسائيندو آھي، ۽ جيڪي ڳڀيراڻين ۾ آھي سو (به) ڄاڻندو آھي، ۽ ڪوئي جيءُ نه ڄاڻندو آھي ته (پاڻ) سڀاڻي ڇا ڪندو، ۽ ڪوئي جيءُ نه ڄاڻندو آھي ته (پاڻ) ڪھڙي زمين تي مرندو، تحقيق الله ڄاڻندڙ خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(32,1,'32:1','الٓمّٓ.');
INSERT INTO "amroti" VALUES(32,2,'32:2','(ھن) ڪتاب جو لاھڻ جنھن ۾ ڪو شڪ ڪونھي جھانن جي پالڻھار وٽان آھي.');
INSERT INTO "amroti" VALUES(32,3,'32:3','چوندا آھن ڇا ته اُن (قرآن) کي پاڻون ٺاھيو اٿس، (نه!) بلڪ اُھو تنھنجي پالڻھار وٽان سچو (آيل) آھي (ھِن لاءِ) ته اُنھيءَ قوم کي ڊيڄارين جن وٽ توکان اڳ ڪو ڊيڄاريندڙ نه آيو آھي ته مانَ اُھي ھدايت لھن.');
INSERT INTO "amroti" VALUES(32,4,'32:4','الله اُھو آھي جنھن آسمانن ۽ زمين کي ۽ جيڪي سندن وچ ۾ آھي سوڇھن ڏينھن ۾ بڻايو وري عرش ڏانھن متوجه ٿيو، اُن کانسواءِ اوھان لاءِ نڪي ڪو سڄڻ ۽ نڪي ڪو پارت ڪندڙ آھي، پوءِ نصيحت نه ٿا وٺو ڇا؟');
INSERT INTO "amroti" VALUES(32,5,'32:5','آسمان کان زمين ڏانھن ڪم جو بندوبست ڪندو آھي، وري (اُھو بندوبست) اُن ڏينھن ۾ ڏانھس مٿاھون ويندو آھي جنھن ڏينھن جو اندازو ھزار ورھ جيترو انھيءَ ليکي مان آھي جو ڳڻيندا آھيو.');
INSERT INTO "amroti" VALUES(32,6,'32:6','اِھو آھي جو ڳجھ ۽ ظاھر جو ڄاڻندڙ غالب مھربان آھي.');
INSERT INTO "amroti" VALUES(32,7,'32:7','جنھي جيڪا شيء پيدا ڪئي سا سڀڪا چڱيءَ طرح بڻائي ۽ ماڻھوءَ جي پيدائش کي مٽيءَ مان نئون شروع ڪيائين.');
INSERT INTO "amroti" VALUES(32,8,'32:8','وري سندس اولاد بي قدر پاڻيءَ جي نچوڙي مان بڻايائين.');
INSERT INTO "amroti" VALUES(32,9,'32:9','وري کيس سئين لڱين ڪيائين ۽ منجھس پنھنجو روح ڦوڪيائين ۽ اوھان لاءِ ڪن ۽ اکيون ۽ دليون بڻايائين، اوھين ٿورو شڪر ڪندا آھيو.');
INSERT INTO "amroti" VALUES(32,10,'32:10','۽ چون ٿا ته جڏھن زمين ۾ رلي ملي وياسون (تڏھن به) اسين وري نئين سر بڻباسين ڇا؟ (نه!) بلڪ اُھي پنھنجي پالڻھار جي ملڻ کان منڪر آھن.');
INSERT INTO "amroti" VALUES(32,11,'32:11','(اي پيغمبر کين) چؤ ته جيڪو موت جو فرشتو اوھان تي مقرّر ڪيل آھي سو اوھان جو روح ڪڍندو وري پنھنجي پالڻھار ڏانھن موٽائبؤ.');
INSERT INTO "amroti" VALUES(32,12,'32:12','۽ (اُنھي مھل) جيڪڏھن ڏسين جنھن مھل گنھگار پنھنجي پالڻھار جي سامھون پنھنجا ڪنڌ اونڌا ڪندڙ ھوندا (ته عجب ڪرين)، (۽ چوندا ته) اي اسانجا پالڻھار ڏٺوسون ۽ ٻڌوسون ھاڻي اسان کي (دُنيا ۾) موٽاءِ ته چڱا ڪم ڪريون بيشڪ اسين يقين ڪندڙ آھيون.');
INSERT INTO "amroti" VALUES(32,13,'32:13','۽ جيڪڏھن گھُرون ھا ته سڀڪنھن شخص کي ھدايت ڪريون ھا پر مون وٽان (ھي) حُڪم ٺھرائجي چڪو آھي ته جِنّن ۽ ماڻھن (انھن ئي) مڙني مان دوزخ ضرور ڀريندس.');
INSERT INTO "amroti" VALUES(32,14,'32:14','پوءِ ھن ڏينھن جي ملڻ کي جو وساريو ھيؤ تنھن سببان عذاب چکو، بيشڪ اسان (به) اوھان کي وساريو ۽ جيڪي ڪندا ھيؤ تنھن سببان ھميشگيءَ جو عذاب چکو.');
INSERT INTO "amroti" VALUES(32,15,'32:15','اسان جي آيتن تي رڳو اُھي ايمان آڻيندا جن کي اُنھن (آيتن) سان جڏھن نصيحت ڏبي آھي تڏھن سجدو ڪندڙ ٿي ڪِرندا آھن ۽ پنھنجي پالڻھار جي ساراھ پاڪائي سان بيان ڪندا آھن ۽ اُھي وڏائي نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(32,16,'32:16','سندن پاسا سمھڻ جي ھنڌ کان الڳ ٿي پنھنجي پالڻھار کي خوف ۽ اُميد سان سڏيندا آھن، ۽ جيڪا کين روزي ڏني سون تنھن مان خرچيندا آھن.');
INSERT INTO "amroti" VALUES(32,17,'32:17','پوءِ جيڪو اکين جو ٺار انھن لاءِ لڪايو ويو آھي سو ڪو جيء نه ڄاڻندو آھي، اُھو بدلو ھنن جيڪي ڪمايو ھو تنھن سببان آھي.');
INSERT INTO "amroti" VALUES(32,18,'32:18','جيڪو مؤمن آھي سو بدڪار جي برابر آھي ڇا؟ (اُھي ٻئي ڪڏھن) برابر نه ٿيندا.');
INSERT INTO "amroti" VALUES(32,19,'32:19','جن ايمان آندو ۽ چڱا ڪم ڪيا تِن لاءِ بھشت (جا باغ) مھماني طور رھڻ جو ھنڌ آھي اُنھي سببان جو (چڱا) ڪم ڪندا رھيا.');
INSERT INTO "amroti" VALUES(32,20,'32:20','پر جن بڇڙائي ڪئي تن جي رھڻ جو ھنڌ دوزخ آھي، جنھن مھل منجھانئس نڪرڻ جو ارادو ڪندا (تنھن مھل) منجھس ورائي (پيا وجھبا) ۽ کين چئبو ته اُن باھ جو عذاب چکو جنھن کي اوھين ڪوڙ ڀائيندا ھئؤ.');
INSERT INTO "amroti" VALUES(32,21,'32:21','۽ کين وڏي عذاب کانسواءِ ھلڪي سزا (دنيا ۾ به) ضرور چکائينداسون ته مانَ اُھي (سڌي واٽ ڏانھن) ورن.');
INSERT INTO "amroti" VALUES(32,22,'32:22','۽ اُن کان وڌيڪ ظالم ڪير آھي جنھن کي سندس پالڻھار جي آيتن سان نصيحت ڏجي ته وري اُن کان مُنھن موڙي، بيشڪ اسين ڏوھارين کان بدلو وٺندڙ آھيون.');
INSERT INTO "amroti" VALUES(32,23,'32:23','۽ بيشڪ مُوسىٰ کي ڪتاب ڏنوسون پوءِ تون اُن (قرآن) جي ملڻ کان شڪ ۾ نه ٿج ۽ اُن موسىٰ کي بني اسرائيلن لاءِ ھدايت بڻايوسون.');
INSERT INTO "amroti" VALUES(32,24,'32:24','منجھانئن پيشوا ڪيا (ھوا) سون جي اسان جي حُڪم سان سڌو رستو ڏيکاريندا ھوا جڏھن جو (اُھي ڏکن تي) صبر ڪندا رھيا، ۽ اُھي اسان جي آيتن تي يقين رکندا ھوا.');
INSERT INTO "amroti" VALUES(32,25,'32:25','بيشڪ تنھنجو پالڻھار ئي قيامت جي ڏينھن سندن وچ ۾ انھيءَ (ڳالھ) جو نبيرو ڪندو جنھن بابت جھڳڙو ڪندا ھوا.');
INSERT INTO "amroti" VALUES(32,26,'32:26','کين پڌرو نه ٿيو آھي ڇا ته کانئن اڳ ڪيترا (ئي) جُڳ ناس ڪياسون جن جي گھرن ۾ (اُھي) گھُمندا آھن، بيشڪ ھن (ڳالھ) ۾ وڏيون نشانيون آھن، پوءِ نه ٻڌندا آھن پا؟');
INSERT INTO "amroti" VALUES(32,27,'32:27','نه ڏٺائون ڇا ته اسين اُٻڙٺ زمين ڏانھن پاڻي وھائيندا آھيون پوءِ اُن سان پوک ڄمائيندا آھيون جنھن مان سندن ڍور ۽ اُھي پاڻ کائيندا آھن، پوءِ نه ڏسندا آھن ڇا؟');
INSERT INTO "amroti" VALUES(32,28,'32:28','۽ چون ٿا ته جيڪڏھن اوھين سچا آھيو ته اھو فيصلو ڪڏھن ٿيندو؟');
INSERT INTO "amroti" VALUES(32,29,'32:29','(اي پيغمبر کين) چؤ ته فيصلي جي ڏينھن ڪافرن کي سندن ايمان آڻڻ فائدو نه ڏيندو ۽ نڪي کين مُھلت ڏبي.');
INSERT INTO "amroti" VALUES(32,30,'32:30','پوءِ کائن مُنھن موڙ ۽ انتظار ڪڍ بيشڪ اُھي (به) انتظار ڪڍندڙ آھن.');
INSERT INTO "amroti" VALUES(33,1,'33:1','اي پيغمبر الله کان ڊڄ ۽ ڪافرن ۽ منافقن جو چيو نه مڃ، بيشڪ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(33,2,'33:2','۽ جيڪي تنھنجي پالڻھار وٽان توڏانھن وحي ڪجي ٿو تنھنجي تابعداري ڪر، بيشڪ جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(33,3,'33:3','۽ الله تي ڀروسو ڪر، ۽ الله سنڀاليندڙ بس آھي.');
INSERT INTO "amroti" VALUES(33,4,'33:4','الله ڪنھن مڙس جي اندر ۾ ٻه دليون (پيدا) نه ڪيون آھن، ۽ نڪي اوھان جي انھن زالن کي جن کي اوھين ماءُ چئي ويٺا آھيو سي اوھان جون (حقيقي) مائر ڪيائين، ۽ نڪي اوھان جي پُٽيلن کي اوھان جا (حقيقي) پُٽ ڪيائين اھو اوھان جو چوڻ اوھان جي واتن جو آھي، ۽ الله سچ چوندو آھي ۽ اُھوئي (سڌو) رستو ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(33,5,'33:5','کين سندن پِين جا (پٽ ڪري) سڏيو اھو الله وٽ بلڪل انصاف آھي، پوءِ جيڪڏھن سندن پيئر نه ڄاڻندا ھجو ته اوھان جا دين ۾ ڀائر ۽ اوھان جا دوست آھن، ۽ جنھن ڳالھائڻ ۾ چوڪ ڪئي ھجيوَ تنھن ۾ اوھان تي ڪو گناھ نه آھي پر اوھان جي دلين جيڪي ڄاڻي واڻي ڪيو (تنھن ۾ گناھ آھي)، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(33,6,'33:6','پيغمبر مؤمنن تي سندن جندن کان وڌيڪ حق رکندو آھي ۽ سندس زالون سندين مائر آھن، ۽ مائٽي وارا ھڪ ٻئي جا پاڻ ۾ الله جي ڪتاب ۾ (ٻـين) مؤمنن ۽ ھجرت ڪندڙن کان وڌيڪ ويجھا آھن پر پنھنجن دوستن سان احسان ڪريو (ته جائز آھي)، اِھو (حُڪم) ڪتاب ۾ لکيل آھي.');
INSERT INTO "amroti" VALUES(33,7,'33:7','۽ (ياد ڪر) جڏھن پيغمبرن کان سندن انجام ورتوسون ۽ (پڻ) توکان ۽ نُوح ۽ ابراھيم ۽ مُوسىٰ ۽ عِيسىٰ پٽ مريم جي کان به، ۽ (ھن لاءِ) کانئن پڪو انجام ورتوسون.');
INSERT INTO "amroti" VALUES(33,8,'33:8','ته (الله) سچن کان سندين سچائي بابت پُڇي، ۽ ڪافرن لاءِ ڏکوئيندڙ عذاب تيار ڪيو اٿس.');
INSERT INTO "amroti" VALUES(33,9,'33:9','اي ايمان وارؤ پاڻ تي الله جو احسان ياد ڪريو جڏھن (ڪافرن جا) لشڪر اوھان تي (چڙھائي ڪري) آيا تڏھن مٿن واءُ کي موڪليوسون ۽ (اوھان جي مدد لاءِ ملائڪن جا) اھڙا لشڪر به جن کي اوھان نه ٿي ڏٺو (پوءِ ڪافرن کي ڀڄائي اوھان کي بچايوسون) ۽ جيڪي ڪريو ٿا سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(33,10,'33:10','جڏھن (اُھي) اوھان جي مٿان ۽ اوھان جي ھيٺان اوھان وٽ آيا ۽ اُنھيءَ مھل اکيون ٽِڙي ويون ۽ دليون (دھشت کان) نِڙ گھٽن کي پُھتيون ۽ الله جي به نسبت ڪيئي گمان ڀانيوَ ٿي.');
INSERT INTO "amroti" VALUES(33,11,'33:11','اُتي مُؤمنن کي پرکيو ويو ۽ کين تمام سخت لوڏيو ويو.');
INSERT INTO "amroti" VALUES(33,12,'33:12','۽ اُنھيءَ مھل منافقن ۽ جن جي دلين ۾ بيماري آھي تن چيو ٿي ته الله ۽ سندس پيغمبر اسان کي ٺڳيءَ کانسواءِ (ٻيو) ڪو انجام نه ڏنو آھي.');
INSERT INTO "amroti" VALUES(33,13,'33:13','۽ اُنھي مھل منجھانئن ھڪ ٽوليءَ چيو ته اي مديني وارؤ اوھان لاءِ (ھتي ترسڻ جي) ڪا جاءِ ڪانھي تنھنڪري موٽو، ۽ منجھانئن ھڪ ٽوليءَ پيغمبر کان موڪلايو ٿي ۽ چوڻ لڳا ته اسان جا گھر ھيڪلا آھن، حالانڪ اُھي ھيڪلا نه ھوا، ڀڄڻ کانسواءِ (ٻيو) ڪو ارادو نه ھون.');
INSERT INTO "amroti" VALUES(33,14,'33:14','۽ جيڪڏھن اُن (مديني) جي آس پاس کان مٿن ڪا (ڪافرن جي فوج) اچي ڪڙڪي ھا وري کانئن گھُرو جنگ گھُرڻ ۾ اچي ھا ته ضرور اُھا ڪن ھا ۽ اُن لاءِ ٿوري (دير) کان سواءِ ترسن ئي نه ھا.');
INSERT INTO "amroti" VALUES(33,15,'33:15','۽ (ھن کان) اڳ الله سان انجام ڪيو ھوائون ته (ڀڄڻ لاءِ) پٺيون نه ڦيريندا، ۽ الله جي انجام جي (ضرور) پُڇا ڪئي ويندي.');
INSERT INTO "amroti" VALUES(33,16,'33:16','(کين) چؤ ته جيڪڏھن اوھين مرڻ يا مارجڻ کان ڀڄندؤ ته ڀڄڻ اوھان کي فائدو نه ڏيندو ۽ اُنھيءَ مھل بلڪل ٿورو فائدو ڏنو ويندوَ.');
INSERT INTO "amroti" VALUES(33,17,'33:17','(اي پيغمبر کين) چؤ ته اوھان کي الله (جي عذاب) کان ڪير بچائيندو جيڪڏھن اُھو اوھان کي ڪا اوکائي پھچائڻ گھري يا اوھان کي ڪا ٻاجھ پھچائڻ گھري (ته اُھا ڪير روڪيندو؟)، ۽ الله کانسواءِ پاڻ لاءِ نڪو دوست ۽ نڪو مددگار لھندا.');
INSERT INTO "amroti" VALUES(33,18,'33:18','اوھان مان (جھاد کان) جھليندڙن ۽ پنھنجن ڀائرن کي (ھئن) چوندڙن کي ته اسان ڏانھن اچو بيشڪ الله ڄاڻندو آھي، ۽ ٿورن کانسواءِ (ٻيا) لڙائي ۾ نه ٿا اچن.');
INSERT INTO "amroti" VALUES(33,19,'33:19','(ھٿون) اوھان تي پبچائي ڪندڙ آھن، پوءِ جنھن مھل ڀَـوَ (جو وقت) اچي (تنھن مھل) کين ڏسين ته توڏانھن نھاريندا آھن جو سندن اکيون اُن (ماڻھوءَ) وانگر ڦِرنديون آھن جنھن تي موت (جي سڪرات) جي بيھوشي پھتي ھجي، پوءِ جنھن مھل (جنگ جو) ڀؤ لھندو آھي تنھن مھل (غنيمت جي) مال (وٺڻ) تي لالچي ٿي اوھان کي تکين زبانن سان ايذائيندا آھن، انھن ايمان نه آندو تنھنڪري الله سندن اعمال چَٽ ڪيا، ۽ اھو ڪم الله کي آسان آھي.');
INSERT INTO "amroti" VALUES(33,20,'33:20','(خوف جي ڪري) ڀائيندا آھن ته (ڪافرن جا) ڪٽڪ نه ويا آھن، ۽ جيڪڏھن (ڪافرن جا) ڪي ڪٽڪ اچن ھا ته (ھيءَ ڳالھ) گھُرن ھا ته اُھي جيڪر جھنگلن ۾ ويٺل ھجن ھا (۽) اوھان جون خبرون (ھرڪنھن کان) پڇندا رھن ھا، ۽ جيڪڏھن اوھان ۾ ھجن ته رڳو ٿورو (اچي) جنگ ۾ وڙھن.');
INSERT INTO "amroti" VALUES(33,21,'33:21','بيشڪ اوھان لاءِ الله جي پيغمبر جي طريقي ۾ عمدي پيروي اُنھيءَ لاءِ آھي جيڪو الله (جي رضا جي اُميد) ۽ قيامت جي ڏينھن (جي اچڻ) جي اميد رکندو ھجي ۽ الله کي گھڻو ياد ڪندو ھجي.');
INSERT INTO "amroti" VALUES(33,22,'33:22','۽ جنھن مھل مؤمنن (ڪافرن جي) لشڪرن کي ڏٺو (تنھن مھل) چيائون ته ھي اُھو انجام آھي جيڪو الله ۽ سندس پيغمبر اسان کي ڏنو ھو ۽ الله ۽ سندس پيغمبر سچ فرمايو ھو، ۽ اِنھيءَ (حالت) اُنھن کي ايمان ۽ فرمانبرداريءَ ۾ ھيڪارو وڌايو.');
INSERT INTO "amroti" VALUES(33,23,'33:23','مؤمنن مان ڪي اھڙا ماڻھو آھن جو جيڪو الله سان انجام ڪيو ھوائون تنھن کي سچو ڪيائون، پوءِ منجھانئن ڪن پنھنجو ذمون پورو ڪيو ۽ منجھانئن ڪو انتظار ڪندو آھي، ۽ (پنھنجي چوڻ کي) ڪنھن طرح نه مٽايائون.');
INSERT INTO "amroti" VALUES(33,24,'33:24','(اھو ھن ڪري آھي) ته الله سچن کي سندن سچائي سببان بدلو ڏئي ۽ جيڪڏھن گھري ته منافقن کي عذاب ڪري يا (توبه جي توفيق ڏئڻ سان) مٿن ٻاجھ سان موٽي، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(33,25,'33:25','۽ الله ڪافرن کي سندن ڏمر سميت موٽايو جو ڪا چڱائي نه لڌائون، ۽ الله مؤمنن جي پاران ويڙھ لاءِ ڪافي ٿيو، ۽ الله سگھارو غالب آھي.');
INSERT INTO "amroti" VALUES(33,26,'33:26','۽ ڪتاب وارن مان جن انھن (مشرڪن) جي مدد ڪئي ھئي تن کي (الله) سندن ڪوٽن مان لاٿو ۽ سندين دلين ۾ دھشت وڌائين (جو) ھڪ ٽوليءَ کي ڪُٺوَ ٿي ۽ ٻيءَ ٽوليءَ کي قيدي ڪيوَ ٿي.');
INSERT INTO "amroti" VALUES(33,27,'33:27','۽ (نيٺ) اوھان کي سندين زمين ۽ سندين گھرن ۽ سندن انھيءَ زمين جو وارث ڪيائين جنھن تي پير (به) نه گھُمايو ھيوَ، ۽ الله سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(33,28,'33:28','اي پيغمبر پنھنجي زالن کي چؤ ته جيڪڏھن اوھين دنيا جي حياتي ۽ اُن جو سينگار گھرنديون ھجو ته اچو ته اوھان کي جورو ڏيان ۽ چڱي طرح ڇڏيان.');
INSERT INTO "amroti" VALUES(33,29,'33:29','۽ جيڪڏھن اوھين الله ۽ سندس پيغمبر ۽ آخرت جي گھر (يعني بھشت) کي گھرو ٿيون ته بيشڪ اوھان مان نيڪ بختن لاءِ الله وڏو اجر تيار ڪيو آھي.');
INSERT INTO "amroti" VALUES(33,30,'33:30','اي پيغمبر جون زالون اوھان مان جيڪا پڌري بي حيائي ڪندي تنھن کي وڌائي ٻيڻو عذاب ڪيو ويندو، ۽ اِھو الله تي آسان آھي.');
INSERT INTO "amroti" VALUES(33,31,'33:31','۽ اوھان مان جيڪا الله ۽ سندس پيغمبر جي تابعداري ڪندي ۽ چڱا ڪم ڪندي تنھن کي سندس اجر ٻه ڀيرا ڏينداسين ۽ اُن لاءِ عزت واري روزي تيار ڪنداسين.');
INSERT INTO "amroti" VALUES(33,32,'33:32','اي پيغمبر جون زالون اوھين ڪن ٻـين زالن جھڙيون نه آھيو جيڪڏھن اوھين (الله کان) ڊڄنديون آھيو ته (ڪنھن ڌارئي ماڻھوءَ سان) ڳالھائڻ ۾ ھيٺاھين نه ڪريو ڇوته جنھن جي دل ۾ بيماري آھي سو (متان) طمع ڪري ۽ چڱو سُخن ڳالھايو.');
INSERT INTO "amroti" VALUES(33,33,'33:33','۽ پنھنجن گھرن ۾ رھو ۽ پھرينءَ جاھليت جي سينگار وانگر سينگار نه ڪريو ۽ نماز قائم رکو ۽ زڪوٰة ڏيو ۽ الله ۽ سندس پيغمبر جي فرمانبرداري ڪريو، اي پيغمبر جا گھر وارؤ الله رڳو ھي گھرندو آھي ته اوھان کان پليتي دور ڪري ۽ ته اوھان کي چڱي طرح پاڪ ڪري.');
INSERT INTO "amroti" VALUES(33,34,'33:34','۽ اوھان جي گھرن ۾ جيڪي الله جي آيتن ۽ دانائيءَ جي ڳالھين مان پڙھيو وڃي ٿو سو ياد ڪريو، بيشڪ الله لُطف ڪندڙ خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(33,35,'33:35','بيشڪ مُسلمان مرد ۽ مُسلمان زالون ۽ مؤمن ۽ مؤمنياڻيون ۽ فرمانبردار ۽ فرمانبردارڻيون ۽ سچا ۽ سچيون ۽ صبر وارا ۽ صبر واريون ۽ (خدا کان) ڊڄندڙ ۽ (خدا کان) ڊڄندڙيون ۽ خيرات ڪرڻ وارا ۽ خيرات ڪرڻ واريون ۽ روزي رکڻ وارا ۽ روزي رکڻ واريون ۽ پنھنجي اگھڙن کي نگاھ رکڻ وارا ۽ نگاھ رکڻ واريون ۽ الله کي گھڻو ياد ڪندڙ ۽ ياد ڪندڙيون انھن (سڀني) لاءِ الله بخشش ۽ وڏو اجر تيار ڪيو آھي.');
INSERT INTO "amroti" VALUES(33,36,'33:36','۽ جڏھن الله ۽ سندس پيغمبر ڪنھن ڪم بابت حُڪم ڪري (تڏھن) ڪنھن مؤمن ۽ ڪنھن مؤمنياڻيءَ کي انھي پنھنجي ڪم ۾ ڪو اختيار ھجڻ نه جڳائي، ۽ جيڪو الله ۽ سندس پيغمبر جي نافرماني ڪندو سو بيشڪ پڌريءَ ڀُل ۾ ڀُلو.');
INSERT INTO "amroti" VALUES(33,37,'33:37','۽ (اي پيغمبر ياد ڪر) جڏھن تو اُنھيءَ شخص کي چيو ٿي جنھن تي الله فضل ڪيو آھي ۽ تو (به) مٿس احسان ڪيو ته پنھنجيءَ زال کي جھل (۽ طلاق نه ڏي) ۽ الله کان ڊڄ ۽ تون پنھنجي دل ۾ اُھا ڳالھ لڪائين ٿو جنھن کي الله پڌرو ڪندو آھي ۽ ماڻھن کان ڊنين ٿي، حالانڪ الله (ھن ڳالھ جو) وڌيڪ حقدار آھي جو اُن کان ڊڄين، پوءِ جنھن مھل زيد اُن (عورت) کان پنھنجي حاجت پوري ڪئي (يعني طلاق ڏنائين تنھن مھل) اُن کي توسان پرڻايوسون (ھن لاءِ) ته مؤمنن تي پنھنجي پٽيلين جي زالن پرڻجڻ ۾ ڪا اوکائي نه رھي جڏھن (اُھي) اُنھن کان پنھنجي حاجت پوري ڪن (يعني طلاق ڏين)، ۽ الله جو (جيڪو) حُڪم (آھي سو) ٿيڻو آھي.');
INSERT INTO "amroti" VALUES(33,38,'33:38','پيغمبر تي اُنھيءَ ۾ ڪا اوکائي نه آھي جيڪي الله اُن (پيغمبر) لاءِ حلال ڪيو آھي، جيڪي اڳ گذري ويا تن ۾ (به) الله جو اھو دستور ھو، ۽ الله جو حُڪم ٺھرايل (اُنھن لاءِ) مُقرّر آھي.');
INSERT INTO "amroti" VALUES(33,39,'33:39','جيڪي الله جا پيغام پھچائيندا آھن ۽ کانئس ڊڄندا آھن ۽ الله کانسواءِ ٻئي ڪنھن کان نه ڊڄندا آھن، ۽ الله حساب ڪندڙ بس آھي.');
INSERT INTO "amroti" VALUES(33,40,'33:40','محمد (صلى الله عليه وسلم) اوھان جي مردن مان ڪنھن ھڪ جو پيءُ نه آھي پر الله جو پيغمبر ۽ سڀني پيغمبرن کان پڇاڙيءَ ۾ اچڻ وارو آھي، ۽ الله ھر شيء کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(33,41,'33:41','اي ايمان وارؤ الله کي تمام گھڻو ياد ڪريو.');
INSERT INTO "amroti" VALUES(33,42,'33:42','۽ صبح ۽ سانجھيءَ جو کيس پاڪائيءَ سان واکاڻيو.');
INSERT INTO "amroti" VALUES(33,43,'33:43','ھو اُھو آھي جو اوھان تي رحمت موڪلي ٿو ۽ سندس ملائڪ به اوھان لاءِ رحمت جي دُعا گھرندا آھن ته اوھان کي اونداھين مان سوجھري ڏانھن ڪڍي، ۽ الله مؤمنن تي مھربان آھي.');
INSERT INTO "amroti" VALUES(33,44,'33:44','جنھن ڏينھن الله کي ملندا (تنھن ڏينھن) سندن کِيڪر سلام آھي، ۽ اُنھن لاءِ عزت وارو اجر تيار ڪيو اٿس.');
INSERT INTO "amroti" VALUES(33,45,'33:45','اي پيغمبر بيشڪ اسان توکي شاھدي ڏيندڙ ۽ خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو.');
INSERT INTO "amroti" VALUES(33,46,'33:46','۽ الله ڏانھن سندس حُڪم سان سڏيندڙ ۽ روشن ڏيئو (ڪري موڪليوسون).');
INSERT INTO "amroti" VALUES(33,47,'33:47','۽ مؤمنن کي خوشخبري ڏي ته انھن لاءِ الله جي پار کان وڏو فضل آھي.');
INSERT INTO "amroti" VALUES(33,48,'33:48','۽ ڪافرن ۽ مُنافقن جو چيو نه مڃ ۽ سندن ايذائڻ (جي خيال) کي ڇڏ ۽ الله تي ڀروسو ڪر، ۽ الله ڪارساز بس آھي.');
INSERT INTO "amroti" VALUES(33,49,'33:49','اي ايمان وارؤ جڏھن مؤمنياڻين کي پرڻجو (۽) وري کين ھٿ لائڻ کان اڳ طلاق ڏيو تڏھن مٿن ڪا عِدّت اوھان لاءِ ڪانھي جنھن کي (اوھين) ڳڻيو، پوءِ کين (ڇڙو) جورو ڏيو ۽ چڱيءَ طرح سان کين ڇڏيو.');
INSERT INTO "amroti" VALUES(33,50,'33:50','اي پيغمبر بيشڪ اسان تولاءِ اُھي زالون حلال ڪيون جن کي تو سندن ڪابين ڏنو ۽ جيڪي ٻانھيون الله توتي عنايت ڪيون تن مان جن تي تنھنجو ھٿ مالڪ ٿيو سي ۽ تنھنجي چاچي جون ڌيئر ۽ تنھنجي پڦين جون ڌيئر ۽ ۽ تنھنجي مامي جون ڌيئر ۽ تنھنجي ماسين جون ڌيئر جن توسان ھجرت ڪئي حلال ڪيوسون، ۽ اُھا مؤمنياڻي زال به (حلال ڪئي سون) جيڪا پنھنجي جِند پيغمبر کي (سواءِ مھر وٺڻ جي) بخشي بشرطيڪ پيغمبر کيس پرڻجڻ جو ارادو ڪري، (اھو ڪم) مؤمنن کانسواءِ خلاصو تولاءِ آھي، انھن (مؤمنن) تي سندن زالن ۽ سندن ٻانھين بابت جن تي سندن ھٿ مالڪ ٿيا آھن جيڪي (ڪابين ۽ خرچ ڏيڻ) فرض ڪيو اٿون سو بيشڪ ڄاتو اٿون (اھو) ھن ڪري ته توتي ڪا اوکائي نه ھجي، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(33,51,'33:51','منجھانئن جنھن کي گھرين تنھن کي ڇڏي ڏئين ۽ جنھن کي گھرين تنھن کي پاڻ وٽ جاءِ ڏئين، ۽ جن کي تو پاسي ڪيو تن مان وري جنھن کي طلب ڪرين ته توتي ڪو گناھ نه آھي، اِھو (اختيار) ھن (ڳالھ) کي تمام ويجھو آھي ته سندن اکيون ٺرن ۽ (اُھي) غمگين نه ٿين ۽ جيڪي تون کين ڏئين تنھن کان اُھي سڀ خوش ٿين، ۽ جيڪي اوھان جي دلين ۾ آھي سو الله ڄاڻندو آھي، ۽ الله ڄاڻندڙ بُردبار آھي.');
INSERT INTO "amroti" VALUES(33,52,'33:52','(اي پيغمبر) انھن (ٻن قسمن) کانپوءِ توکي ٻيون زالون حلال نه آھن ۽ نڪي انھن جي بدلي (ٻيون) زالون نڪاح ڪرين جيتوڻيڪ سندن سونھن توکي پسند اچي پر تنھنجو ھٿ جنھن جو مالڪ ٿيو آھي (تنھن ۾ توکي اختيار آھي)، ۽ الله سڀڪنھن شيء تي نگھبان آھي.');
INSERT INTO "amroti" VALUES(33,53,'33:53','اي مؤمنو پيغمبر جي گھرن ۾ اُنھيءَ (وقت) کانسواءِ اندر نه وڃو (جنھن وقت) اوھان کي ڪنھن طعام (کائڻ) لاءِ اچڻ جي موڪل ڏني وڃي ۽ (اھڙي وقت وڃو جو) ان جي تيار ٿيڻ جو انتظار ڪندڙ نه ھجو پر جڏھن اوھان کي سڏيو وڃي تڏھن اندر وڃو پوءِ جڏھن کائي رھو تڏھن نڪري وڃو ۽ ڪنھن ڳالھ ۾ ريجھي ويھي نه رھو، ڇوته انھيءَ (ڪم) پيغمبر کي ايذايو ٿي پوءِ (ھن) اوھان کان لڄ ڪئي ٿي، ۽ الله حق (چوڻ) کان لڄ نه ڪندو آھي، ۽ جڏھن انھن (پيغمبر جي بيبين) کان ڪا شيء گھرو تڏھن پردي جي ٻاھران کانئن گھرو، اھو (ڪم) اوھان جي دلين ۽ انھن جي دلين لاءِ ڏاڍو پاڪ آھي، ۽ اوھان کي نه جڳائيندو آھي ته الله جي پيغمبر کي ايذايو ۽ نڪي (جڳائيندو آھي) ته اُن کانپوءِ سندس زالون اوھين ڪڏھن پرڻجو، ڇوته اُھو (ڪم) الله وٽ وڏو (گناھ) آھي.');
INSERT INTO "amroti" VALUES(33,54,'33:54','جيڪڏھن ڪنھن ڳالھ کي پڌرو ڪريو يا اُن کي لڪايو ته بيشڪ الله ھر شيء کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(33,55,'33:55','زالن تي نڪي پنھنجن پين ۽ نڪي پنھنجن پٽن ۽ نڪي پنھنجن ڀائرن ۽ نڪي پنھنجن ڀاٽين ۽ نڪي پنھنج ڀاڻيجن ۽ نڪي پاڻ جھڙين زالن ۽ نڪي جن تي سندن ھٿ مالڪ ٿيا آھن تن جي اڳيان (پردي نه ڪرڻ ۾) گناھ آھي، ۽ (اي زالون) الله کان ڊڄو ڇوته الله سڀ ڪنھن شيء تي حاضر آھي.');
INSERT INTO "amroti" VALUES(33,56,'33:56','بيشڪ الله ۽ سندس ملائڪ پيغمبر تي رحمت موڪليندا آھن، اي ايمان وارؤ اوھين به مٿس صلوات پڙھو ۽ پوري طرح سلام چئو.');
INSERT INTO "amroti" VALUES(33,57,'33:57','بيشڪ جيڪي الله ۽ سندس پيغمبر کي ايذائيندا آھن تن الله دنيا ۽ آخرت ۾ لعنت ڪئي آھي ۽ اُنھن لاءِ خواريءَ وارو عذاب تيار ڪيو اٿس.');
INSERT INTO "amroti" VALUES(33,58,'33:58','۽ جيڪي (ماڻھو) م‍ؤمنن ۽ مؤمنياڻين کي اھڙي ڪم (جي تھمت) سان ايذائيندا آھن جو اُنھن نه ڪيو ھجي تن بيشڪ ڪوڙي ٺاھ ۽ پڌري گناھ جو بار کنيو.');
INSERT INTO "amroti" VALUES(33,59,'33:59','اي پيغمبر پنھنجين زالن ۽ پنھنجين ڌيئرن ۽ مؤمنن جي زالن کي چؤ ته (جيڪڏھن ٻاھر نڪرن ته) پاڻ تي پنھنجون چادرون وجھن، اِھو (ھِن ڳالھ کي) تمام ويجھو آھي ته اُھي سڃاتيون وڃن پوءِ کين ايذاءُ نه ڏنو وڃي، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(33,60,'33:60','منافق ۽ جن جي دلين ۾ بيماري آھي سي ۽ مديني ۾ ڪوڙين ڳالھين پکيڙڻ وارا جيڪڏھن (پنھنجي عادت کان) نه جھلبا ته مٿن توکي بڇينداسون وري ٿورن (ڏينھن) کانسواءِ اُن (يعني مديني) ۾ تنھنجا پاڙيسري نه ٿيندا.');
INSERT INTO "amroti" VALUES(33,61,'33:61','لعنت ڪيل آھن، جتي لڀندا تِتي پڪڙبا ۽ چڱي طرح ماربا.');
INSERT INTO "amroti" VALUES(33,62,'33:62','جيڪي (ھن کان) گذري ويا تن ۾ (به) الله جو اھو دستور ھو، ۽ الله جي دستور کي ڪڏھن مت سٽ نه ڏسندين.');
INSERT INTO "amroti" VALUES(33,63,'33:63','ماڻھو توکان قيامت بابت پڇن ٿا، (کين) چؤ ته اُن جي خبر رڳو الله وٽ آھي، ۽ ڪھڙيءَ (ڳالھ) توکي ڄاڻايو متان قيامت ويجھڙو ئي ٿيندي ھجي.');
INSERT INTO "amroti" VALUES(33,64,'33:64','بيشڪ الله ڪافرن تي لعنت ڪئي آھي ۽ انھن لاءِ ساڙيندڙ باھ تيار ڪئي اٿس.');
INSERT INTO "amroti" VALUES(33,65,'33:65','منجھس سدائين رھڻ وارا آھن، نڪي ڪو سڄڻ ۽ نڪي ڪو مددگار لھندا.');
INSERT INTO "amroti" VALUES(33,66,'33:66','جنھن ڏينھن سندن منھن باھ ۾ اونڌا ڪبا (تنھن ڏينھن) چوندا ته اسان لاءِ ھيءَ ارمان (جيڪر) الله جي فرمانبرداري ڪريون ھا ۽ پيغمبر جي (به) فرمانبرداري ڪريون ھا (ته چڱو ھو).');
INSERT INTO "amroti" VALUES(33,67,'33:67','۽ چوندا ته اي اسان جا پالڻھار اسان پنھنجن سردارن ۽ پنھنجن وڏن جي فرمانبرداري ڪئي ته انھن اسان کي واٽ کان ڀُلايو.');
INSERT INTO "amroti" VALUES(33,68,'33:68','اي اسانجا پالڻھار کين ٻيڻو عذاب ڪر ۽ مٿن تمام وڏي لعنت ڪر.');
INSERT INTO "amroti" VALUES(33,69,'33:69','اي ايمان وارؤ (اوھين) اُنھن وانگر نه ٿيو جن مُوسىٰ کي ايذايو پوءِ جيڪي چيائون تنھن کان الله کيس پاڪ ڪيو، ۽ (اھو) الله وٽ مانَ وارن مان ھو.');
INSERT INTO "amroti" VALUES(33,70,'33:70','اي ايمان وارؤ الله کان ڊڄو ۽ سڌي ڳالھ چئو.');
INSERT INTO "amroti" VALUES(33,71,'33:71','(ته) اوھان جا عمل اوھان لاءِ سُڌاري ۽ اوھان جا گناھ اوھان کي بخشي، ۽ جيڪو الله ۽ سندس پيغمبر جي فرمانبرداري ڪندو تنھن بيشڪ وڏي مراد ماڻي.');
INSERT INTO "amroti" VALUES(33,72,'33:72','بيشڪ اسان آسمانن ۽ زمين ۽ جبلن کي امانت آڇي ته اُن جي کڻڻ کان انڪار ڪيائون ۽ کانئس ڊنا ۽ اُن کي انسان کنيو، بيشڪ اُھو تمام ظالم جاھل آھي.');
INSERT INTO "amroti" VALUES(33,73,'33:73','(ھيءَ آڇ ھن لاءِ آھي) ته الله منافقن ۽ منافقياڻين ۽ مشرڪن ۽ مشرڪياڻين کي عذاب ڪري ۽ الله مؤمنن ۽ مؤمنياڻين تي ٻاجھ سان موٽي، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(34,1,'34:1','سڀ ساراھ اُنھيءَ الله کي جڳائي جو جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) سندس آھي ۽ اُن جي ئي ساراھ آخرت ۾ آھي، ۽ اُھو ئي حڪمت وارو خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(34,2,'34:2','جيڪي زمين ۾ گھڙندو آھي ۽ جيڪي منجھانئس نڪرندو آھي ۽ جيڪي آسمان مان لھندو آھي ۽ جيڪي منجھس چڙھندو آھي سو (سڀ اُھو) ڄاڻندو آھي، ۽ اھوئي بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(34,3,'34:3','۽ ڪافر چوندا آھن ته اسان تي قيامت (ڪڏھن) نه ايندي، (کين) چؤ ته ھائو مون کي پنھنجي (اُنھيءَ) پالڻھار ڳُجھ ڄاڻيندڙ جو قسم آھي ته اوھان وٽ ضرور ايندي، جنھن کان ذري جيترو نڪي آسمانن ۽ نڪي زمين ۾ ڳُجھو رھي ٿو ۽ اُن کان تمام ننڍي (ڪا شيء اھڙي) ڪانھي ۽ نڪي تمام وڏي جا پڌري ڪتاب ۾ (لکيل) نه آھي.');
INSERT INTO "amroti" VALUES(34,4,'34:4','ھن لاءِ ته جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي بدلو ڏئي، انھن لاءِ ئي بخشش ۽ سڳوري روزي آھي.');
INSERT INTO "amroti" VALUES(34,5,'34:5','۽ جيڪي اسان جي آيتن ۾ مقابلو ڪندڙ ٿي ڊوڙيا انھن لاءِ ئي ڏکوئيندڙ عذاب جي سزا آھي.');
INSERT INTO "amroti" VALUES(34,6,'34:6','۽ جن کي عِلم ڏنو ويو سي سمجھندا آھن ته جيڪو (قرآن) تنھنجي پالڻھار وٽان توڏانھن نازل ڪيو ويو آھي سو برحق آھي ۽ غالب ساراھيل (الله) جي واٽ ڏانھن رستو ڏيکاريندڙ آھي.');
INSERT INTO "amroti" VALUES(34,7,'34:7','۽ ڪافر (ھڪ ٻئي) کي چوندا آھن ته اوھان کي اھڙو مڙس ڏسيون ڇا جو اوھان کي ٻڌائي ٿو ته جڏھن (اوھين) تمام ڳري ذرا ذرا ٿيندؤ (تڏھن) بيشڪ اوھين نئين سر بڻايا ويندؤ.');
INSERT INTO "amroti" VALUES(34,8,'34:8','(اُنھيءَ) الله تي ڪوڙو ٺاھ ٺاھيو آھي يا کيس ڪا چريائي آھي، (نه!) بلڪ جيڪي آخرت کي نه مڃيندا آھن سي عذاب ۾ ھُوندا ۽ (اُھي) وڏي ڀُل ۾ آھن.');
INSERT INTO "amroti" VALUES(34,9,'34:9','جيڪي سندن اڳيان ۽ جيڪي سندن پويان آسمان ۽ زمين مان (بڻايو ويو) آھي سو (غور ڪري) نه ڏٺائون ڇا؟ جيڪڏھن گھُرون ته کين زمين ۾ ڳھائي ڇڏيون يا آسمان کان مٿن ٽُڪرا ڪيرايون، بيشڪ ان ۾ سڀڪنھن (الله ڏانھن) ورندڙ ٻانھي لاءِ (وڏي) نشاني آھي.');
INSERT INTO "amroti" VALUES(34,10,'34:10','۽ بيشڪ داؤد کي پاڻ وٽان بزرگي ڏني سون، (۽ حُڪم ڪيوسون ته) اي جبلؤ ساڻس تسبيحون چئو ۽ پکين کي (اُن جو فرمانبردار ڪيوسون)، ۽ اُن لاءِ لوھ کي نرم ڪيوسون.');
INSERT INTO "amroti" VALUES(34,11,'34:11','(حڪم ڏنوسون) ته ويڪريون زرھون بڻاءِ ۽ اندازي سان ڪڙيون ٺاھ ۽ (اي داؤد جي گھراڻي وارؤ) چڱا ڪم ڪريو، ڇوته جيڪي اوھان ڪندا آھيو سو بيشڪ آءٌ ڏسندڙ آھيان.');
INSERT INTO "amroti" VALUES(34,12,'34:12','۽ واءُ کي سُليمان لاءِ (نوايوسون) اُن جي اڳياڙيءَ جي منزل مھيني جو پنڌ ۽ پوياڙيءَ جي منزل (به) مھيني جو پنڌ ھئي ۽ اُن لاءِ پگھريل ٽامي جو تلاءُ وَھايوسون، ۽ جنّن مان (به ڪن کي فرمانبردار ڪيوسون) جو سندس پالڻھار جي حڪم سان سندس اڳيان ڪمائيندا ھوا، ۽ منجھائن جيڪو اسان جي حُڪم کان مُنھن موڙيندو تنھن کي باھ جو عذاب چکائينداسون.');
INSERT INTO "amroti" VALUES(34,13,'34:13','جيڪي گھرندو ھو سو اُن لاءِ ڪي قلعا ۽ مُورتون ۽ حوض جيڏا پيالا ۽ وڏيون ديڳيون جي نه چُرنديون ھيون سي بڻائيندا ھوا، (چيوسون ته) اي داؤد جا اولاد شُڪرانو ڪريو، ۽ مُنھنجن ٻانھن مان ٿورا شُڪرانو ڪندڙ آھن.');
INSERT INTO "amroti" VALUES(34,14,'34:14','پوءِ جنھن مھل مٿس موت کي حُڪم ڪيوسون (تنھن مھل) سندس موت تي اُڏيھيءَ کانسواءِ (ٻئي ڪنھن) اُنھن (جِنّن) کي نه چتايو جو سندس لٺ کي ٿي کاڌائين، پوءِ جنھن مھل ڪريو (تنھن مھل) جِنّن معلوم ڪيو ته جيڪڏھن ڳجھ ڄاڻن ھا ته خواريءَ واري عذاب ۾ نه رھن ھا.');
INSERT INTO "amroti" VALUES(34,15,'34:15','بيشڪ سبا جي قوم لاءِ سندن ڳوٺ ۾ ھڪ نشاني ھُئي، (يعني) ٻه باغ سڄي ۽ کٻي پاسي کان ھوا، (چيو وين ته) پنھنجي پالڻھار جي رزق مان کائو ۽ سندس شڪرانو ڪريو، (اوھان لاءِ ھي) شھر سٺو آھي ۽ پالڻھار بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(34,16,'34:16','پوءِ (شڪر ڪرڻ کان) منھن موڙيائون تنھنڪري (بند ڀڃي) سخت ڇَـرَ کي مٿن وھايوسين ۽ سندن (اُنھن) ٻن باغن جي بدلي کين (ٻيا) ٻه باغ ڪسارن ميون ۽ لَئِن وارا ۽ ڪجھ ٿورين ٻيرين وارا ڏناسون.');
INSERT INTO "amroti" VALUES(34,17,'34:17','سندن بي شڪريءَ سببان کين اھا سزا ڏني سون، ۽ بي شڪر کانسواءِ (ٻئي) ڪنھن کي سزا نه ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(34,18,'34:18','۽ سندن وچ ۾ اُھي ڳوٺ جن ۾ برڪت رکي سون تن جي وچ ۾ پڌرا ڳوٺ (لڳولڳ) ڪياسون ۽ انھن ۾ اچ وڃ جو پورو بندوبست رکيوسون، (چيوسون ته) راتيان ۽ ڏينھان منجھن بي ڀؤا گھمندا رھو.');
INSERT INTO "amroti" VALUES(34,19,'34:19','پوءِ چيائون ته اي اسان جا پالڻھار اسان جي مسافرين ۾ ڏورائي ڪر ۽ پاڻ تي ظلم ڪيائون تنھن ڪري انھن کي (ٻـين جي لاءِ) آکاڻيون ڪيوسين ۽ کين تمام پرزا پرزا ڪري ڇني ڦاڙي ڇڏيوسون، بيشڪ ھن ۾ سڀڪنھن وڏي صابِر شاڪر لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(34,20,'34:20','۽ بيشڪ ابليس انھن به نسبت پنھنجي گمان کي سچو لڌو جو مؤمنن جي ھڪ ٽوليءَ کانسواءِ ٻـين اُن جي تابعداري ڪئي.');
INSERT INTO "amroti" VALUES(34,21,'34:21','۽ اُن جو مٿن ڪو زور نه ھو پر (اسين پرکون) ته جيڪو آخرت کي مڃيندو ھجي تنھن کي اُنھيءَ کان ڌار ڪريون جيڪو اُن کان شڪ ۾ (پيل) ھجي، ۽ تنھنجو پالڻھار سڀڪنھن شيء تي نگھبان آھي.');
INSERT INTO "amroti" VALUES(34,22,'34:22','(کين) چؤ ته الله کانسواءِ جن ۾ اوھين گمان ڪندا آھيو تن کي سڏيو، اُھي نڪي آسمانن ۾ ۽ نڪي زمين ۾ ذري جيترا (به) مالڪ آھن ۽ نڪي منجھن ڪا ڀائيواري آھي ۽ نڪي منجھائن ڪو الله جو مددگار آھي.');
INSERT INTO "amroti" VALUES(34,23,'34:23','۽ نڪي وٽس (ٻئي ڪنھن جي) سفارش فائدو ڏيندي مگر جنھن لاءِ رخصت ڏني ھوندائين (فرشتن جو ھي حال آھي ته جنھن مھل ڪو حُڪم خدا جو نازل ٿئي ٿو ته اُنھن جي طبيعت ۾ گھٻراھٽ پيدا ٿئي ٿي) تانجو جنھن وقت ھنن جي دلين تان گھٻراھٽ لھي ٿي (تنھن مھل) چون ٿا ته اوھان جي پالڻھار ڇا چيو آھي (مٿان ملائڪ ھيٺين کي) چوندا ته سچ فرمايو اٿس ۽ اُھو (سڀ کان) مٿاھون وڏو آھي.');
INSERT INTO "amroti" VALUES(34,24,'34:24','(کين) چؤ ته اوھان کي آسمانن ۽ زمين مان ڪير روزي ڏيندو آھي، چؤ ته الله (ڏيندو آھي) ۽ بيشڪ اسين يا اوھين (يا ته) ھدايت تي آھيون يا پڌريءَ گُمراھي ۾.');
INSERT INTO "amroti" VALUES(34,25,'34:25','چؤ ته جيڪي اسان ڏوھ ڪيا آھن تنھن بابت اوھين پڇيا نه ويندؤ ۽ جيڪي اوھين ڪندا آھيو تنھن بابت اسين پُڇيا نه وينداسون.');
INSERT INTO "amroti" VALUES(34,26,'34:26','چؤ ته اسان جو پالڻھار اسان (مڙني) کي گڏ ڪندو وري اسان ۽ (اوھان) جي وچ ۾ حق سان فيصلو ڪندو، ۽ اھوئي (سچو) فيصلو ڪندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(34,27,'34:27','چؤ ته جن کي اوھان ساڻس شريڪ بڻائي ملايو آھي سي مون کي ڏيکاريو (چؤ ته) ائين نه آھي، بلڪ اُھو الله غالب حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(34,28,'34:28','(اي پيغمبر) توکي ته مڙني ماڻھن لاءِ خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليوسون پر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(34,29,'34:29','۽ چوندا آھن ته جيڪڏھن اوھين سچا آھيو ته اھو (قيامت جو) انجام ڪڏھن ٿيندو.');
INSERT INTO "amroti" VALUES(34,30,'34:30','چؤ ته اوھان لاءِ اھڙي ڏينھن جو انجام آھي جنھن کان ھڪ گھڙي نڪي دير ڪندؤ ۽ نڪي اڳي ويندؤ.');
INSERT INTO "amroti" VALUES(34,31,'34:31','۽ ڪافر چون ٿا ته ھن قُرآن کي ڪڏھن نه مڃينداسون ۽ نڪي اُنھي کي جو کانئس اڳ آھي، ۽ (اي ڏسندڙ) جڏھن ظالم پنھنجي پالڻھار اڳيان بيھاربا (تڏھن) جيڪڏھن ڏسين ته (عجب ڪرين)، جو (پاڻ ۾) انھن مان ھڪڙا ٻـين ڏي ڳالھ ورائيندا، جيڪي ھيڻا آھن سي ھٺيلن کي چوندا ته جيڪڏھن اوھان نه ھجوھا ته اسين مؤمن ھجون ھا.');
INSERT INTO "amroti" VALUES(34,32,'34:32','ھٺيلا ھيڻن کي چوندا ته جڏھن (ھدايت) اوھان وٽ آئي تڏھن اسان اوھان کي ھدايت کان جھليو ھو ڇا؟ (نه!) بلڪ اوھين پاڻ گنھگار ھئؤ.');
INSERT INTO "amroti" VALUES(34,33,'34:33','۽ ھيڻا ھٺيلن کي چوندا ته (ائين نه آھي) بلڪ رات ۽ ڏينھن جي (اوھان جي) مڪر ڪرڻ (اسان کي جھليو ھو) جڏھن اسان کي حُڪم ڪندا ھيؤ ته الله سان ڪفر ڪيو ۽ ساڻس شريڪ ٺھرايون، ۽ جڏھن عذاب کي ڏسندا (تڏھن) پشيماني (دل ۾) لڪائيندا، ۽ ڪافرن جي ڳچين ۾ ڳٽ وجھنداسون، جيڪي ڪندا ھوا تنھن کانسواءِ ٻئي جي سزا نه ڏبين.');
INSERT INTO "amroti" VALUES(34,34,'34:34','۽ ڪنھن ڳوٺ ۾ ڪو ڊيڄاريندڙ نه موڪليوسون مگر اُن جي آسودن (ماڻھن) چيو ته بيشڪ جنھن شيء سان اوھين موڪليا ويا آھيو (تنھنکي) اسين نه مڃيندڙ آھيون.');
INSERT INTO "amroti" VALUES(34,35,'34:35','۽ چيائون ته اسين گھڻي مال ۽ اولاد وارا آھيون ۽ اسان کي عذاب ٿيڻو نه آھي.');
INSERT INTO "amroti" VALUES(34,36,'34:36','چؤ ته منھنجو پالڻھار جنھن لاءِ گھري تنھنجي روزي ڪشادي ڪندو آھي ۽ (جنھن لاءِ گھري تنھن جي) تنگ ڪندو آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(34,37,'34:37','۽ نڪي اوھان جا مال ۽ نڪي اوھان جا اولاد اھڙي شيء آھن جو اوھان کي اسان وٽ مرتبي جي ڪري ويجھو ڪن پر (ويجھا اُھي آھن) جن ايمان آندو ۽ چڱا ڪم ڪيا، تن جيڪي ڪمايو تنھن سببان اُنھن لاءِ ٻيڻو بدلو آھي ۽ اُھي ماڙين ۾ امن سان رھندا.');
INSERT INTO "amroti" VALUES(34,38,'34:38','۽ جيڪي اسان جي آيتن (جي رد ڪرڻ) ۾ مقابلو ڪندڙ ٿي ڪوشش ڪندا آھن اُھي عذاب ۾ حاضر ڪبا.');
INSERT INTO "amroti" VALUES(34,39,'34:39','چؤ ته بيشڪ منھنجو پالڻھار پنھنجن ٻانھن مان جنھن لاءِ گھرندو آھي تنھنجي روزي ڪشادي ڪندو آھي ۽ (جنھن لاءِ گھرندو آھي تنھنجي) تنگ ڪندو آھي، ۽ جيڪي اوھين ڪُجھ خرچيندؤ تنھن جو الله بدلو ڏيندو، ۽ اُھو چڱو روزي ڏيندڙ آھي.');
INSERT INTO "amroti" VALUES(34,40,'34:40','۽ جنھن ڏينھن اُنھن مڙني کي گڏ ڪندو وري ملائڪن کي چوندو ته (ھيءُ) اُھي آھن ڇا جيڪي اوھان کي پوڄيندا ھوا؟');
INSERT INTO "amroti" VALUES(34,41,'34:41','(ملائڪ) چوندا ته تون پاڪ ذات آھين تون اسان جو سائين آھين نه اُھي، بلڪ اُھي جنّن کي پوڄيندا ھوا، انھن مان گھڻا کين مڃيندڙ ھوا.');
INSERT INTO "amroti" VALUES(34,42,'34:42','پوءِ اڄ اوھان مان ڪوبه ھڪ ٻئي کي نڪي سُک پُھچائڻ جو نڪي ڏک پھچائڻ جو اختيار رکي ٿو، ۽ ظالمن کي چونداسون ته اُن باھ جي سزا جو (جو مزو) چکو جنھن کي اوھين ڪُوڙ ڀائيندا ھوا.');
INSERT INTO "amroti" VALUES(34,43,'34:43','۽ جڏھن اسان جون پڌريون آيتون کين پڙھي ٻڌائبيون آھن تڏھن (ھڪ ٻئي کي) چوندا آھن ته ھيءُ رڳو ھڪ ماڻھو آھي جو جنھن کي اوھان جا ابا ڏاڏا پوڄيندا ھوا تنھن کان اوھان کي جھلڻ گھرندو آھي، ۽ چوندا آھن ته ھيءُ (قرآن) رڳو ڪوڙ ٺھيل آھي، ۽ ڪافرن وٽ جڏھن سچي ڳالھ آئي (تڏھن) اُن لاءِ چوندا آھن ته ھي رڳو پڌرو جادو آھي.');
INSERT INTO "amroti" VALUES(34,44,'34:44','حالانڪ اُنھن (مشرڪن) کي نڪو ڪو ڪتاب ڏنوسون جنھن کي پڙھن ۽ نڪي توکان اڳ ڪو ڊيڄاريندڙ اُنھن ڏانھن موڪليوسون.');
INSERT INTO "amroti" VALUES(34,45,'34:45','۽ کائن اڳين به ڪوڙ ڄاتو ھو حالانڪ جيڪي انھنکي ڏنوھوسون تنھن جي ڏھين پتيءَ کي (به اُھي مشرڪ) نه پُھتا آھن پوءِ اُنھن اسان جي پيغمبرن کي ڪوڙو ڄاتو، پوءِ منھنجو عذاب ڪئن ھو.');
INSERT INTO "amroti" VALUES(34,46,'34:46','(کين) چؤ ته اوھان کي رڳو ھڪ ڳالھ جي نصيحت ڪريان ٿو، ته ٻه ٻه ۽ ھڪ ھڪ ٿي الله (جي ڪم) لاءِ کڙا ٿيو وري پروڙيو، ته اوھان جي سنگتيءَ کي ڪو سوداءُ نه آھي، (بلڪ) اُھو ته سخت عذاب (جي پھچڻ) کان اڳ اوھان کي ڊيڄاريندڙ آھي.');
INSERT INTO "amroti" VALUES(34,47,'34:47','(۽ کين) چؤ ته اوھان کان جيڪو ڪو اُجورو گھريو ھجيم سو اوھان کي ئي ھجي، منھنجو اُجورو الله کانسواءِ ٻئي ڪنھن تي نه آھي، ۽ اُھو ھر شيء تي شاھد آھي.');
INSERT INTO "amroti" VALUES(34,48,'34:48','(کين) چؤ ته بيشڪ منھنجو پالڻھار حق کي منھنجي دل ۾ وجھندو آھي، (اھو) ڳُجھن جو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(34,49,'34:49','(کين) چ‍ؤ ته حق آيو ۽ ڪوڙو معبود نڪي نئي سر پيدا ڪري ٿو نڪي بِھر پيدا ڪندو.');
INSERT INTO "amroti" VALUES(34,50,'34:50','(کين) چؤ ته جيڪڏھن گمراھ ٿيس ته رڳو پاڻ لاءِ گمراھ ٿيان ٿو، ۽ جيڪڏھن ھدايت وارو ٿيس ته (اُھا) اُنھيءَ سببان آھي جو منھنجو پالڻھار مون ڏانھن وحي موڪليندو آھي، بيشڪ اُھو ٻڌندڙ ويجھو آھي.');
INSERT INTO "amroti" VALUES(34,51,'34:51','۽ جنھن مھل پريشان ٿيندا ۽ (الله جي عذاب کان) ڇوٽڪارو نه لھندا ۽ ويجھي ھنڌان پڪڙبا (تنھن مھل) جيڪڏھن ڏسين (ته عجب ڪرين).');
INSERT INTO "amroti" VALUES(34,52,'34:52','۽ چوندا ته (ھاڻي) اُن (قرآن) تي ايمان آندوسون، ۽ (اُتي دُنيا جي) ڏورين ھنڌ کان کين (ايمان) ھٿ آڻڻ ڪٿان ٿيندو؟');
INSERT INTO "amroti" VALUES(34,53,'34:53','حالانڪ اڳي اُن قرآن کي نه مڃيندا ھوا، ۽ ڏورين ھنڌ کان اَڻ ڏٺو (گُمان جا تير) اُڇليندا رھيا.');
INSERT INTO "amroti" VALUES(34,54,'34:54','۽ اُنھن جي ۽ اُھي جيڪي سَڌون ڪندا رھيا تن جي وچ ۾ اَڙ وڌي وئي جھڙي طرح انھن جھڙن (ٻـين) کان اڳ ڪيو ويو، بيشڪ اُھي به وڏي شڪ ۾ پيل ھوا.');
INSERT INTO "amroti" VALUES(35,1,'35:1','سڀ ساراھ خاص الله کي جُڳائي جو آسمانن ۽ زمين جو بڻائيندڙ آھي ملائڪن کي قاصد ڪرڻ وارو آھي جو ٻن ٻن ۽ ٽن ٽن ۽ چَئن چَئن پَرن وارا آھن، جيڪي گھري سو خلقت ۾ وڌائي ٿو، بيشڪ الله ھر شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(35,2,'35:2','الله ماڻھن لاءِ (پنھنجيءَ) ٻاجھ مان جيڪي کوليندو تنھن کي ڪو بند ڪرڻ وارو نه آھي، ۽ جيڪي بند ڪندو تنھنکي اُن (جي بند ڪرڻ) کانپوءِ ڪو کولڻ وارو نه آھي، ۽ اُھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(35,3,'35:3','اي ماڻھؤ پاڻ تي الله جون نعمتون ياد ڪريو، الله کانسواءِ ڪو بڻائيندڙ آھي ڇا؟ جو آسمان ۽ زمين مان اوھان کي روزي ڏئي ٿو، اُن (الله) کانسواءِ ڪو عبادت جو لائق نه آھي پوءِ ڪيڏانھن ڦيرايا ويندا آھيو.');
INSERT INTO "amroti" VALUES(35,4,'35:4','۽ (اي پيغمبر) جيڪڏھن ڪوڙو ڀائين ته بيشڪ تو کان اڳ (ٻيا ڪيترائي) پيغمبر ڪوڙا ڀانيا ويا، ۽ سڀ ڪم الله ڏانھن موٽائجن ٿا.');
INSERT INTO "amroti" VALUES(35,5,'35:5','اي ماڻھؤ بيشڪ الله جو اَنجام سچّو آھي تنھنڪري نڪي اوھان کي دنيا جي حياتي ٺڳي، ۽ نڪي فريبيندڙ (شيطان) الله بابت اوھان کي فريبي.');
INSERT INTO "amroti" VALUES(35,6,'35:6','بيشڪ شيطان اوھان جو ويري آھي تنھنڪري اُن کي ويري (ڪري) وٺو، (ھو) پنھنجيءَ ٽوليءَ کي رڳو ھن لاءِ سڏيندو آھي ته دوزخين مان ٿين.');
INSERT INTO "amroti" VALUES(35,7,'35:7','جن ڪُفر ڪيو تن لاءِ سخت عذاب آھي، ۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ بخشش ۽ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(35,8,'35:8','جنھن لاءِ سندس بڇڙو ڪم سينگاريو ويو پوءِ اُن کي ھُن چڱو (ڪري) ڏٺو (سو چڱي ڪم ڪندڙ جھڙو ھوندو) ڇا؟ پوءِ بيشڪ الله جنھن کي گھرندو آھي تنھن کي گمراھ ڪندو آھي ۽ جنھن کي گھرندو آھي تنھن کي ھدايت ڪندو آھي، تنھنڪري مٿن ارمان ڪرڻ سببان متان تنھنجو ساھ نڪري، ڇوته جيڪي ڪندا آھن سو الله ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(35,9,'35:9','۽ الله اُھو آھي جنھن وائن کي موڪليو پوءِ اُنھن ڪڪر کي کنيو پوءِ اُن (ڪڪر) کي ويران ڳوٺ ڏانھن روانو ڪيوسون پوءِ اُن (جي پاڻيءَ) سان زمين کي سندس ويرانيءَ کانپوءِ جياريوسون، اھڙي طرح (قبرن مان) مُئن جو اُٿڻ آھي.');
INSERT INTO "amroti" VALUES(35,10,'35:10','جيڪو مانُ گھري (سو ياد رکي) ته سڀ مانُ الله ئي کي آھي (اُن وٽان عزت ملندي آھي)، ڏانھس پاڪ سُخن چڙھندو آھي ۽ اُن (پاڪ سُخن) کي چڱو عمل مٿي چاڙھيندو آھي، ۽ جيڪي بڇڙاين جي رٿ ڪندا آھن تن لاءِ سخت عذاب آھي، ۽ اُھا ٺڳي اُنھن جي ٽٽندي.');
INSERT INTO "amroti" VALUES(35,11,'35:11','۽ الله اوھان کي مٽيءَ مان پيدا ڪيو وري نطفي مان (بڻايائين) وري اوھان کي جوڙو جوڙو (مڙس ۽ زال) ڪيائين، ۽ مادين مان ڪابه سندس علم کانسواءِ نڪي ڍُڪي ٿيندي ۽ نڪي ڄڻيندي آھي، ۽ نڪي ڪنھن وڏي عمر واري کي ڄمار ڏبي آھي ۽ نڪي سندس ڄمار مان (ڪجھ) گھٽائبو آھي مگر (اُھو) ڪتاب ۾ لکيل آھي، بيشڪ اھو (ڪم) الله کي آسان آھي.');
INSERT INTO "amroti" VALUES(35,12,'35:12','۽ ٻه درياءَ ھڪ جھڙا نه آھن (جن مان) ھيءُ ھڪ مِٺو اُڃ لاھيندڙ آھي سندس پاڻي وڻندڙ آھي ۽ اُھو ٻيو کارو ڪوڙو آھي، ۽ ھر ھڪ مان (مڇيءَ جو) تازو گوشت کائيندا آھيو ۽ زيور (يعني موتي منجھائن) ڪڍندا آھيو جي پائيندا آھيو، ۽ منجھس (پاڻي کي) چيريندڙ ٻيڙيون ڏسندو آھين (ھن لاءِ) ته سندس فضل مان (روزي) طلب ڪريو ۽ ته مانَ اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(35,13,'35:13','رات کي ڏينھن ۾ گھيڙيندو آھي ۽ ڏينھن کي رات ۾ گھيڙيندو آھي ۽ سج ۽ چنڊ کي تابع ڪيائين، ھر ھڪ ٺھرايل مدت تي ھلندو آھي، اُھو الله اوھان جو پالڻھار آھي اُن جي بادشاھي آھي، ۽ جن کي الله کانسواءِ سڏيندا آھيو سي کارڪ جي ککڙيءَ جي ڇلُ (جيتري ذري) جا (به) مالڪ نه آھن.');
INSERT INTO "amroti" VALUES(35,14,'35:14','جيڪڏھن کين سڏيو ته اوھان جو سڏ نه ٻڌندا، ۽ جيڪڏھن (کڻي) ٻڌن (به) ته به اوھان کي ورندي نه ڏيندا، ۽ قيامت جي ڏينھن اوھان جي شريڪ ٺھرائڻ جا منڪر ٿيندا، ۽ توکي خبر رکندڙ جھڙي ڪوبه خبر نه ڏيندو.');
INSERT INTO "amroti" VALUES(35,15,'35:15','اي ماڻھو اوھين الله ڏانھن مُحتاج آھيو، ۽ الله اُھو بي پرواھ ساراھيل آھي.');
INSERT INTO "amroti" VALUES(35,16,'35:16','جيڪڏھن گھري ته اوھان کي ناس ڪري ۽ نئين خلق پيدا ڪري.');
INSERT INTO "amroti" VALUES(35,17,'35:17','۽ اِھو (ڪم) الله کي اَوکو نه آھي.');
INSERT INTO "amroti" VALUES(35,18,'35:18','۽ ڪوبه بار کڻندڙ ٻئي جو بار نه کڻندو، ۽ جيڪڏھن (گناھن جي) ڳوري بار وارو پنھنجي بار کڻڻ ڏانھن ڪنھن کي سڏيندو توڻيڪ مائٽي وارو ھوندو ته به منجھانئس ڪجھ به نه کنيو ويندو، تون ته انھن کي ڊيڄاري سگھندين جيڪي پنھنجي پالڻھار کان پرپُٺ ڊڄندا آھن ۽ نماز پڙھندا آھن، ۽ جيڪو پاڪ ٿيو سو رڳو پاڻ لاءِ پاڪ ٿئي ٿو، ۽ الله ڏانھن موٽڻ آھي.');
INSERT INTO "amroti" VALUES(35,19,'35:19','۽ انڌو ۽ سڄو برابر نه آھن.');
INSERT INTO "amroti" VALUES(35,20,'35:20','۽ نڪي اونداھيون ۽ نڪي سوجھرو.');
INSERT INTO "amroti" VALUES(35,21,'35:21','۽ نڪي ڇانوَ ۽ نڪي اُس (پاڻ ۾ ھڪ جھڙا آھن).');
INSERT INTO "amroti" VALUES(35,22,'35:22','۽ نڪي جيئرا ۽ نڪي مُئا برابر ٿين ٿا، بيشڪ الله جنھن کي گھري تنھن کي ٻڌائيندو آھي، ۽ تون ڪو قبر وارن کي ٻڌائيندڙ نه آھين.');
INSERT INTO "amroti" VALUES(35,23,'35:23','تون ته رڳو ڊيڄاريندڙ آھين.');
INSERT INTO "amroti" VALUES(35,24,'35:24','بيشڪ اسان توکي سچي دين سان خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو، ۽ اھڙي ڪا ٽولي آھي ئي ڪانه جنھن ۾ ڪو ڊيڄاريندڙ نه گذريو آھي.');
INSERT INTO "amroti" VALUES(35,25,'35:25','۽ جيڪڏھن توکي ڪوڙو ڀائين ٿا ته کائن اڳين به بيشڪ ڪوڙو ڀانيو ھو، جن وٽ سندن پيغمبر چِٽن معجزن ۽ صحيفن سان ۽ پڌرن ڪتابن سان آيا ھوا.');
INSERT INTO "amroti" VALUES(35,26,'35:26','وري ڪافرن کي پڪڙيم ۽ منھنجي سزا ڪيئن ھئي.');
INSERT INTO "amroti" VALUES(35,27,'35:27','نه ڏٺو اٿئي ڇا ته الله آسمانن کان پاڻي وسايو، پوءِ اُن سان ميوا پيدا ڪياسون جن جا رنگ قسمين قسمين آھن، ۽ جبلن مان ٽُڪر آھن ڪي اڇا ۽ ڪي ڳاڙھا انھن جا طرحين طرحين رنگ آھن ۽ ڪي نھايت ڪارا (به) آھن.');
INSERT INTO "amroti" VALUES(35,28,'35:28','۽ اھڙيءَ طرح ماڻھن ۽ چرندڙن ۽ ڍورن مان (به) جو انھن جا رنگ طرح طرح جا آھن (بڻاياسين)، الله کان سندس ٻانھن مان رڳو سمجھ وارا ڊڄندا آھن، بيشڪ الله غالب بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(35,29,'35:29','بيشڪ جيڪي (ماڻھو) الله جو ڪتاب پڙھندا آھن ۽ نماز (پوريءَ طرح) پڙھندا آھن ۽ جيڪي کين ڏنوسون تنھن مان ڳُجھو ۽ پڌرو خرچيندا آھن سي اھڙيءَ سوداگري جي اميد رکندا آھن جا ڪڏھن نه برباد ٿيندي.');
INSERT INTO "amroti" VALUES(35,30,'35:30','(ھن لاءِ) ته خُدا کين سندن اجر پورو ڏئي ۽ پنھنجي فضل سان کين وڌيڪ به ڏئي، ڇوته اھو بخشڻھار قدر ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(35,31,'35:31','۽ جيڪي توڏانھن ڪتاب مان وحي ڪيوسون سو سچ آھي جيڪي کانئس اڳ آھن تن کي به سچّو ڪندڙ آھي، بيشڪ الله پنھنجن ٻانھن جي خبر رکندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(35,32,'35:32','وري انھن کي ڪتابن جو وارث ڪيوسون جن کي پنھنجن ٻانھن مان چونڊيوسون، پوءِ منجھائن ڪي پاڻ لاءِ ظالم آھن، ۽ منجھائن ڪي وچٿرا آھن، ۽ منجھائن ڪي الله جي حُڪم سان چڱاين ۾ اڳرائي ڪندڙ آھن، اِھوئي وڏو فضل آھي.');
INSERT INTO "amroti" VALUES(35,33,'35:33','(انھن لاءِ ھميشه وارا باغ آھن) جن ۾ (اُھي) گھڙندا منجھن سون جا ڪنگڻ ۽ موتي پھرائبن، ۽ انھن ۾ سندن لباس پَٽ ھوندو.');
INSERT INTO "amroti" VALUES(35,34,'35:34','۽ چوندا ته سڀ ساراھ انھي الله کي جڳائي جنھن اسان کان (سڀ) ڏُک ٽاريا، بيشڪ اسان جو پالڻھار بخشڻھار قدر ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(35,35,'35:35','جنھن اسان کي پنھنجي فضل سان سدائين رھڻ واري ھنڌ لاٿو، نڪي اسان کي منجھس ڪو ڏک پھچندو ۽ نڪي اسان کي منجھس ڪو ٿَڪ پھچندو.');
INSERT INTO "amroti" VALUES(35,36,'35:36','۽ جن ڪفر ڪيو تن لاءِ دوزخ جي باھ آھي، نڪي مٿن حُڪم ڪبو ته مرن ۽ نڪي کائن اُن جي عذاب مان ڪي ھلڪو ڪبو، سڀڪنھن بي شُڪر کي اھڙي طرح سزا ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(35,37,'35:37','۽ اُھي (دوزخي) منجھس دانھون ڪندا، ته اي اسان جا پالڻھار اسان کي ڪڍ ته جيڪي ڪم ڪندا ھواسون تن ڌاران ٻيا چڱا ڪم ڪريون، (چيو ويندن ته) اوھان کي ايتري ڄمار نه ڏني ھئي سون ڇا ته جيڪو نصيحت وٺڻ گھري ھا سو منجھس نصيحت وٺي سگھي ھا؟ ۽ اوھان وٽ ڊيڄاريندڙ (به) آيو، تنھنڪري (پنھنجي ڪمائيءَ جو مزو) چکو ڇوته ظالمن جو ڪو مددگار نه آھي.');
INSERT INTO "amroti" VALUES(35,38,'35:38','بيشڪ الله آسمانن ۽ زمين جو ڳُجھ ڄاڻندڙ آھي، بيشڪ اُھو سِينن وارو (ڳُجھ) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(35,39,'35:39','(الله) اُھو آھي جنھن اوھان کي زمين ۾ (اڳين جو) پيڙھيءَ وارو ڪيو، پوءِ جيڪو ڪُفر ڪندو تنھن تي سندس ڪفر (جو نقصان) آھي، ۽ نڪي ڪافرن لاءِ سندن ڪُفر سندن پالڻھار وٽ ڏمر کانسواءِ (ٻيو ڪجھ) وڌائيندو، ۽ نڪي ڪافرن لاءِ سندن ڪفر ڇيئي کانسواءِ (ٻيو ڪجھ) وڌائيندو.');
INSERT INTO "amroti" VALUES(35,40,'35:40','(کين) چؤ ته الله کانسواءِ جن پنھنجن (ٺھرايلن) بُتن کي سڏيندا آھيو سي ڏٺا اٿوَ ڇا؟ مون کي ڏيکاريو ته زمين مان ڇا بڻايو اٿن يا آسمانن ۾ سندن ڪا ڀائيواري آھي، يا کين ڪو ڪتاب ڏنو اٿون ڇا جو اُھي اُن جي پڌري حُجّت تي آھن؟ (نه!) بلڪ ظالم ھڪ ٻئي کي رڳو ٺڳيءَ جو انجام ڏيندا آھن.');
INSERT INTO "amroti" VALUES(35,41,'35:41','بيشڪ الله آسمانن ۽ زمين کي ڊھڻ کان نگاھ رکندو آھي، ۽ جيڪڏھن ڊَھن ته خُدا کانسواءِ انھن کي ڪوبه ڪونه نگاھ رکندو، بيشڪ اُھو بُردبار بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(35,42,'35:42','۽ اُنھن پنھنجن پڪّن قسمن سان الله جو قسم کنيو ته جيڪڏھن وٽن ڪوبه ڊيڄاريندڙ آيو (تنھن مھل اُن جي اچڻ) کين رڳو نفرت وڌائي.');
INSERT INTO "amroti" VALUES(35,43,'35:43','مُلڪ ۾ وڏائي ڪرڻ ۽ بڇڙي رِٿ رِٿڻ سبب (نفرت ڪيائون)، ۽ بڇڙي رٿ رٿڻ (جو نتيجو) اُن جي ھلت ڪرڻ وارن کانسواءِ ٻئي تي نه پوندو آھي، تنھن ڪري رڳو اڳين جي (عذاب جي) دستور جا منتظر آھن، پوءِ (تون) ڪڏھن الله جي دستور کي ڪا مَٽ سَٽ نه ڏسندين، ۽ (تون) ڪڏھن الله جي دستور کي ڪا ڦيرگھير نه ڏسندين.');
INSERT INTO "amroti" VALUES(35,44,'35:44','زمين ۾ نه گھميا آھن ڇا جو ڏسن ته جيڪي کائن اڳ ھوا تن جي پڇاڙي ڪھڙي ٿي اُھي کائن سگھ ۾ به تمام ڏاڍا ھوا، ۽ الله اھڙو نه آھي جو کيس آسمانن ۾ ڪابه شيء ٿڪائي ۽ نڪي زمين ۾ (ٿڪائي)، بيشڪ اُھو ڄاڻندڙ وس وارو آھي.');
INSERT INTO "amroti" VALUES(35,45,'35:45','۽ جيڪڏھن الله ماڻھن کي سندن ڪرتوتن سببان پڪڙي ھا ته ڪو چُرندڙ زمين جي پُٺيءَ تي نه ڇڏي ھا پر ٺھرايل مُدّت توڻي کين مُھلت ڏئي ٿو، پوءِ جڏھن سندن مُدت آئي تڏھن بيشڪ الله پنھنجن ٻانھن کي ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(36,1,'36:1','اي پيغمبر.');
INSERT INTO "amroti" VALUES(36,2,'36:2','(ھن) حِڪمت واري قرآن جوقسم آھي.');
INSERT INTO "amroti" VALUES(36,3,'36:3','ته بيشڪ تون پيغمبرن مان آھين.');
INSERT INTO "amroti" VALUES(36,4,'36:4','(بيشڪ تون) سڌي واٽ تي آھين.');
INSERT INTO "amroti" VALUES(36,5,'36:5','(الله) غالب مھربان وٽان (قرآن جو) لاھڻ آھي.');
INSERT INTO "amroti" VALUES(36,6,'36:6','(ھن لاءِ) ته اھڙي قوم کي ڊيڄارين جو سندن پيءُ ڏاڏا نه ڊيڄاريا ويا آھن تنھنڪري اُھي بي خبر آھن.');
INSERT INTO "amroti" VALUES(36,7,'36:7','بيشڪ انھن مان گھڻن تي (عذاب جو) قول لازم ٿيو آھي تنھنڪري اُھي ايمان نه ٿا آڻين.');
INSERT INTO "amroti" VALUES(36,8,'36:8','بيشڪ اسان سندن ڳچين ۾ ڳَٽ وڌا پوءِ اُھي کاڏين تائين (پيل) اٿن تنھنڪري اُھي ڳاٽي ٽنگيل آھن.');
INSERT INTO "amroti" VALUES(36,9,'36:9','۽ سندن اڳيان اوٽ ۽ سندن پوئتان (به) اوٽ ڪئي سون پوءِ کين ڍڪيوسون تنھنڪري اُھي (ڪجھ) نه ڏسندا.');
INSERT INTO "amroti" VALUES(36,10,'36:10','۽ کين تون ڊيڄارين يا نه ڊيڄارين سندن لاءِ ھڪ جھڙو آھي تنھنڪري اُھي ايمان نه آڻيندا.');
INSERT INTO "amroti" VALUES(36,11,'36:11','تون رڳو اُن کي ڊيڄاريندين جيڪو نصيحت تي ھلي ۽ ٻاجھاري (الله) کان پرپُٺ ڊڄي، ته پوءِ اُن کي بخشش ۽ سڳوري اجر جي خوشخبري ڏي.');
INSERT INTO "amroti" VALUES(36,12,'36:12','بيشڪ اسين مُئن کي جياريندا آھيون ۽ جيڪي (مَدا توڻي چڱا ڪم ڪري) اڳي موڪليائون سي ۽ سندن پيرن جا نشان لکون ٿا، ۽ ھر شيء کي پڌري ڪتاب (لوح محفوظ) ۾ ڳڻيو اٿون.');
INSERT INTO "amroti" VALUES(36,13,'36:13','۽ اُنھن سان ڳوٺ (انطاڪيہ) وارن جو قصّو بيان ڪر، جڏھن وٽن پيغمبر آيا.');
INSERT INTO "amroti" VALUES(36,14,'36:14','(يعني) جڏھن اُنھن ڏانھن ٻن پيغمبرن کي موڪليوسون پوءِ ٻنھي کي ڪوڙو ڀانيائون تنھنڪري ٽئين سان غلبو ڏنوسون پوءِ (گڏجي) چيائون ته بيشڪ اسين اوھان ڏانھن موڪليل آھيون.');
INSERT INTO "amroti" VALUES(36,15,'36:15','چيائون ته اوھين ته رڳو اسان جھڙا ماڻھو آھيو ۽ ٻاجھاري (الله) ڪا شيءَ نه لاٿي آھي اوھين رڳو ڪُوڙا آھيو.');
INSERT INTO "amroti" VALUES(36,16,'36:16','چيائون ته اسان جو پالڻھار ڄاڻندو آھي ته بيشڪ اسين اوھان ڏانھن موڪليل آھيون.');
INSERT INTO "amroti" VALUES(36,17,'36:17','۽ اسان تي پڌري پيغام پھچائڻ کانسواءِ (ٻيو ڪجھ) لازم نه آھي.');
INSERT INTO "amroti" VALUES(36,18,'36:18','چيائون ته بيشڪ اسان اوھان جي (اچڻ) سببان بدبختي سمجھي آھي، جيڪڏھن اوھين نه جھلبؤ ته ضرور اوھان کي پھڻن سان مارينداسون ۽ ضرور اسان کان اوھان کي ڏکوئيندڙ عذاب پھچندو.');
INSERT INTO "amroti" VALUES(36,19,'36:19','(پيغمبرن) چيو ته اوھان جي بدبختي اوھان سان آھي، جيڪڏھن اوھان کي نصيحت ڏبي آھي ته (انھيءَ ڪري) ڇا؟ بلڪ اوھين حد کان لنگھيل قوم آھيو.');
INSERT INTO "amroti" VALUES(36,20,'36:20','۽ شھر جي پرين پاسي کان ھڪ مڙس ڊوڙندو آيو چيائين ته اي منھنجي قوم پيغمبرن جي تابعداري ڪريو.');
INSERT INTO "amroti" VALUES(36,21,'36:21','(اوھين) انھن جي تابعداري ڪريو جيڪي اوھان کان اُجورو نه گھرندا آھن ۽ اُھي سڌيءَ واٽ وارا آھن.');
INSERT INTO "amroti" VALUES(36,22,'36:22','۽ مون کي ڇا (ٿيو) آھي جو انھيءَ خُدا جي عبادت نه ڪريان جنھن مونکي پيدا ڪيو ۽ (اوھين به) ڏانھس موٽايا ويندؤ.');
INSERT INTO "amroti" VALUES(36,23,'36:23','اُن کانسواءِ ٻيا معبود وٺان ڇا؟ جيڪڏھن ٻاجھارو (الله) مونکي ڏُک پھچائڻ گھري ته اُنھن جي شفاعت مونکان ڪجھ (به ڏک) نه ٽاريندي ۽ نڪي (اُھي) مون کي ڇڏائيندا.');
INSERT INTO "amroti" VALUES(36,24,'36:24','(جي ائين ڪندس ته) بيشڪ آءٌ اُنھيءَ مھل پڌري گمراھيءَ ۾ ھوندس.');
INSERT INTO "amroti" VALUES(36,25,'36:25','بيشڪ مون اوھان جي پالڻھار تي ايمان آندو آھي، پوءِ مون کان ٻُڌو.');
INSERT INTO "amroti" VALUES(36,26,'36:26','(اِن چوڻ تي کيس شھيد ڪيائون) چيو ويس ته بھشت ۾ گھڙ، چيائين ھيء ارمان (جيڪر) منھنجي قوم ڄاڻي ھا.');
INSERT INTO "amroti" VALUES(36,27,'36:27','ته ڇاجي ڪري منھنجي پالڻھار مون کي بخشيو ۽ مون کي عزت وارن مان ڪيائين.');
INSERT INTO "amroti" VALUES(36,28,'36:28','۽ کانئس پوءِ سندس قوم تي آسمان کان ڪو لشڪر نه لاٿوسون ۽ نڪي (لشڪر) لاھيندڙ آھيون.');
INSERT INTO "amroti" VALUES(36,29,'36:29','(سندن سزا) رڳو ھڪ سخت رڙ ھئي پوءِ اُنھي (ئي) دم اُھي (باھ وانگر) وسامي (مري ويا).');
INSERT INTO "amroti" VALUES(36,30,'36:30','ٻانھن تي ارمان آھي، (جو) وٽن ڪو (به اھڙو) پيغمبر نه آيو جنھن سان ٺـٺوليون نه ٿي ڪيائون.');
INSERT INTO "amroti" VALUES(36,31,'36:31','نه ڏٺو اٿن ڇا ته کانئن اڳ ڪيترائي (ماڻھن جا) طبقا ناس ڪياسون (جو) اُھي اُنھن ڏانھن موٽي نه ٿا اچن.');
INSERT INTO "amroti" VALUES(36,32,'36:32','۽ جيڪي به آھن سي سڀ اسان وٽ حاضر ڪبا.');
INSERT INTO "amroti" VALUES(36,33,'36:33','۽ ھڪ نشاني اُنھن (جي جيارڻ) لاءِ مُئل زمين آھي، جنھن کي جياريوسون ۽ منجھانئس (اَن جا داڻا) ڪڍياسون جو اُن مان کائين ٿا.');
INSERT INTO "amroti" VALUES(36,34,'36:34','۽ منجھس کجورين ۽ ڊاکن جا باغ پيدا ڪياسون ۽ اُنھن (باغن) ۾ ڪي چشما وھاياسون.');
INSERT INTO "amroti" VALUES(36,35,'36:35','(ھن لاءِ ته) سندن ڦر مان کائيندا رھن ۽ اُنھن (ميون) کي سندن ھٿن نه بڻايو آھي، پوءِ ڇونه شُڪرانو ڪندا آھن؟');
INSERT INTO "amroti" VALUES(36,36,'36:36','اُھو (الله) پاڪ آھي جنھن اُھا شيء جيڪا زمين ڄمائيندي آھي سا ۽ سندن جيئن مان ۽ جيڪي (شيون) نه ڄاڻندا آھن تن مان سڀني جنس کي (جوڙو جوڙو ڪوڙو ۽ مِٺو ۽ نَر ۽ مادي) پيدا ڪيو.');
INSERT INTO "amroti" VALUES(36,37,'36:37','۽ اُنھن لاءِ ھڪ نشاني رات آھي، (جو) اُن مان ڏينھن کي ڇڪي ڪڍندا آھيون پوءِ اُنھي مھل اُھي اونداھي ۾ رھندا آھن.');
INSERT INTO "amroti" VALUES(36,38,'36:38','۽ سج پنھنجي ٽڪاءَ جي جاءِ تي پيو ھلندو آھي، اِھو غالب (الله) جو اندازو (ٺھرايل) آھي.');
INSERT INTO "amroti" VALUES(36,39,'36:39','۽ چنڊ لاءِ منزلون مقرر ڪيوسون تان جو کجيءَ جي پراڻي بھاري جي ڳنيءَ وانگر وري ڏنگو ٿيندو آھي.');
INSERT INTO "amroti" VALUES(36,40,'36:40','نڪي سج کي چنڊ جي پھچڻ جي (ڪا) مجال آھي ۽ نڪي رات (سج لھڻ کان اڳ) ڏينھن تي اڳرائي ڪندڙ آھي، ۽ (سج ۽ چنڊ ۽ تارن مان) سڀئي آسمان ۾ تَرن ٿا.');
INSERT INTO "amroti" VALUES(36,41,'36:41','۽ اُنھن لاءِ ھڪ نشاني (ھيءَ به آھي) جو اسان سندن اولاد کي ڀريل ٻيڙي ۾ کنيو.');
INSERT INTO "amroti" VALUES(36,42,'36:42','۽ اُنھن لاءِ اُن (ٻيڙيء) جھڙيون (ٻيون سواريون به) بڻايوسون جن تي سواري ڪندا آھن.');
INSERT INTO "amroti" VALUES(36,43,'36:43','۽ جيڪڏھن گھرون ته کين ٻوڙي ڇڏيون پوءِ انھن لاءِ ڪو دانھن ورنائڻ وارو نه ٿئي ۽ نڪي اُھي ڇڏائبا.');
INSERT INTO "amroti" VALUES(36,44,'36:44','پر پاڻ وٽان ٻاجھ ڪئي سون ۽ ھڪ وقت تائين (اُھي) آسودا ڪياسون.');
INSERT INTO "amroti" VALUES(36,45,'36:45','۽ جڏھن کين چئبو آھي ته اُنھي (عذاب) کان ڊڄو جيڪو اوھان جي اڳيان ۽ اوھان جي پٺيان (اچڻو) آھي ته مانَ اوھان تي ٻاجھ ڪجي.');
INSERT INTO "amroti" VALUES(36,46,'36:46','۽ سندن پالڻھار جي نشانين مان ڪائي (اھڙي) نشاني وٽن نه ايندي آھي جنھن کان (ھو) منھن موڙيندڙ نه ھوندا آھن.');
INSERT INTO "amroti" VALUES(36,47,'36:47','۽ جڏھن کين چئبو آھي ته جيڪي الله اوھان کي رزق ڏنو آھي تنھن مان خرچيو (تڏھن) ڪافر مؤمنن کي چوندا آھن ته اھڙي کي ڇو کارايون جو جيڪڏھن الله گھري ھا ته کيس کارائي ھا، اوھين اڳو پڌري گمراھيءَ ۾ آھيو.');
INSERT INTO "amroti" VALUES(36,48,'36:48','۽ چوندا آھن ته جيڪڏھن اوھين سچا آھيو ته اِھو انجام ڪڏھن (پورو) ٿيندو.');
INSERT INTO "amroti" VALUES(36,49,'36:49','اِھي رڳو ھڪ سخت آواز جو انتظار ڪندا آھن جو اُھي اُنھي حالت ۾ کين (اوچتو) پڪڙيندو جو اُھي جھڳڙو ڪندا ھوندا.');
INSERT INTO "amroti" VALUES(36,50,'36:50','پوءِ (اُھي) نڪي وصيت ڪري سگھندا ۽ نڪي پنھنجي گھر وارن ڏانھن موٽي سگھندا.');
INSERT INTO "amroti" VALUES(36,51,'36:51','۽ صُور ۾ ڦوڪبو پوءِ اُتي جو اُتي پنھنجي پالڻھار ڏانھن قبرن مان ڊوڙندا.');
INSERT INTO "amroti" VALUES(36,52,'36:52','چوندا ته اسان لاءِ ويل آھي اسان کي اسان جي سمھڻ جي جاءِ مان ڪنھن اُٿاريو؟ (ملائڪ چوندا ھائو) ھيءُ اُھو آھي جيڪو ٻاجھاري الله انجام ڏنو ھو ۽ پيغمبرن سچ چيو ھو.');
INSERT INTO "amroti" VALUES(36,53,'36:53','(اِھو واقعو) رڳو ھڪ سخت رڙ ھوندي پوءِ اُھي اُتي جو اُتي گڏ ڪري اسان وٽ حاضر ڪيا ويندا.');
INSERT INTO "amroti" VALUES(36,54,'36:54','پوءِ اڄ ڪنھن جيءَ تي ڪجھ به ظلم نه ڪبو ۽ جيڪي ڪندا ھئو رڳو اُن جو اوھان کي بدلو ڏبو.');
INSERT INTO "amroti" VALUES(36,55,'36:55','بيشڪ بھشتي اڄ ھڪ ڪم ۾ خوش ھوندا.');
INSERT INTO "amroti" VALUES(36,56,'36:56','اُھي ۽ سندين زالون ڇانوَن ۾ تختن تي ٽيڪ ڏئي وھندڙ ھوندا.');
INSERT INTO "amroti" VALUES(36,57,'36:57','انھن لاءِ بھشت ۾ ميوا آھن ۽ جيڪي گھرندا سو (پڻ) انھن لاءِ آھي.');
INSERT INTO "amroti" VALUES(36,58,'36:58','چيو ويندو ته پالڻھار مھربان (الله) کان (اوھان تي) سلام ھجي.');
INSERT INTO "amroti" VALUES(36,59,'36:59','۽ (چونداسون ته) اي گنھگارو اڄ (نيڪن کان) جدا ٿيو.');
INSERT INTO "amroti" VALUES(36,60,'36:60','اي آدم جا اولاد اوھان ڏانھن پيغام نه موڪليو ھوم ڇا ته شيطان کي نه پوڄجو، جو بيشڪ اوھان جو پڌرو ويري آھي.');
INSERT INTO "amroti" VALUES(36,61,'36:61','۽ ته منھنجي عبادت ڪجو، (جو) اِھا سڌي واٽ آھي.');
INSERT INTO "amroti" VALUES(36,62,'36:62','۽ بيشڪ (شيطان) اوھان مان گھڻيءَ خلق کي گمراھ ڪيو، پوءِ اوھين نه سمجھندا ھُئو ڇا؟');
INSERT INTO "amroti" VALUES(36,63,'36:63','ھيءُ اُھو دوزخ آھي (جنھن) جو اوھان کي وعدو ڏبو ھو.');
INSERT INTO "amroti" VALUES(36,64,'36:64','اُن ۾ اڄ انھي ڪري گِھڙو جو ڪفر ڪندا ھُئو.');
INSERT INTO "amroti" VALUES(36,65,'36:65','اڄ سندن واتن تي مُھر ھڻنداسون ۽ جيڪي ڪندا ھوا تنھن بابت سندن ھٿ اسان سان ڳالھائيندا ۽ سندن پير شاھدي ڏيندا.');
INSERT INTO "amroti" VALUES(36,66,'36:66','۽ جيڪڏھن گھرون ته سندين اکين کي ناس ڪريون پوءِ واٽ ڏانھن ڊوڙن ته پوءِ ڪٿان ڏسندا؟');
INSERT INTO "amroti" VALUES(36,67,'36:67','۽ جيڪڏھن گھرون ته سندين جاين تي (ئي) کين بد شڪل ڪري ڇڏيون ته پوءِ نڪي اڳتي ھلي سگھن ۽ نڪي پوءَ تي موٽن.');
INSERT INTO "amroti" VALUES(36,68,'36:68','۽ جنھن کي وڏي ڄمار وارو ڪندا آھيون تنھن کي بڻاوت ۾ اونڌو ڪندا آھيون، (ايتري ڳالھ به) نه سمجھندا آھن ڇا؟');
INSERT INTO "amroti" VALUES(36,69,'36:69','۽ اُن (پيغمبر) کي نڪي شعر سيکاريو اٿون ۽ نڪي (شعر) اُن جو لائق آھي، ھيءَ ته نصيحت ۽ پڌرو قرآن آھي.');
INSERT INTO "amroti" VALUES(36,70,'36:70','ته اُنھي کي ڊيڄاري جيڪو جيئرو (يعني سمجھ وارو) ھجي ۽ ڪافرن تي حجت ثابت ٿئي.');
INSERT INTO "amroti" VALUES(36,71,'36:71','نه ڏٺو اٿن ڇا ته اسان انھن لاءِ انھن شين مان ڍور پيدا ڪيا جن کي اسان جي ھٿن بڻايو؟ پوءِ اُھي اُنھن جا مالڪ آھن.');
INSERT INTO "amroti" VALUES(36,72,'36:72','۽ اُنھن (ڍورن) کي اُنھن جو تابعدار ڪيوسون پوءِ منجھانئن ڪي سندن چڙھيءَ جا آھن ۽ منجھانئن ڪي (اُھي) کائيندا آھن.');
INSERT INTO "amroti" VALUES(36,73,'36:73','۽ اُنھن لاءِ اُنھن (ڍورن) ۾ گھڻا فائدا آھن ۽ پيئڻ جون شيون (يعني کِير) آھن، پوءِ اُھي ڇونه شُڪر ڪندا آھن؟');
INSERT INTO "amroti" VALUES(36,74,'36:74','۽ الله کانسواءِ ٻيا معبود ڪري ورتا اٿن ته مانَ کين مدد ڏني وڃي.');
INSERT INTO "amroti" VALUES(36,75,'36:75','(اُھي) کين مدد ڏئي نه سگھندا ۽ اُھي (بُت) اُنھن جو لشڪر ٿي گڏ ڪيا ويندا.');
INSERT INTO "amroti" VALUES(36,76,'36:76','پوءَ (اي پيغمبر) توکي سندن چوڻ نه ڏکوئي، جيڪي ڳجھو ڪندا آھن ۽ جيڪي پڌرو ڪندا آھن سو بيشڪ اسين ڄاڻون ٿا.');
INSERT INTO "amroti" VALUES(36,77,'36:77','ماڻھوءَ نه ڏٺو آھي ڇا ته اسان کيس نُطفي مان بڻايو پوءِ تڏھن ته اُھو پڌرو جھيڙاڪ ٿيندو آھي.');
INSERT INTO "amroti" VALUES(36,78,'36:78','۽ اسان لاءِ ھڪ مثال بيان ڪندو آھي ۽ پنھنجي بڻاوت وساريندو آھي، ۽ چوندو آھي ته (ھنن) ھڏن کي ڪير جياريندو ۽ اُھو سڀڪنھن پيدائش کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(36,79,'36:79','(اي پيغمبر کين) چؤ ته جنھن اُنھن کي پھريون ڀيرو بڻايو سو اُنھن کي (وري) جياريندو ۽ اُھو سڀڪنھن پيدائش کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(36,80,'36:80','جنھن سائي وڻ مان اوھان لاءِ باھ پيدا ڪئي پوءِ اوچتو اوھين اُن مان باھ ٿا ٻاريو.');
INSERT INTO "amroti" VALUES(36,81,'36:81','جنھن آسمانن ۽ زمين کي بڻايو سو انھن جھڙن جي بڻائڻ تي وس وارو نه آھي ڇا؟ ھائو! (وس وارو آھي)، ۽ اُھو وڏو بڻائيندڙ (سڀ) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(36,82,'36:82','سندس حُڪم ھن کانسواءِ (ٻيو) ڪونه آھي ته جڏھن ڪا شيء بڻائڻ گھرندو آھي ته اُن لاءِ رڳو چوندو آھي ته ٿيءُ ته (اھا شي) ٿي پوندي آھي.');
INSERT INTO "amroti" VALUES(36,83,'36:83','پوءِ اُھو (الله) پاڪ آھي جنھن جي ھٿ ۾ ھر شيءَ جي حڪومت آھي ۽ ڏانھس اوھين موٽايا ويندؤ.');
INSERT INTO "amroti" VALUES(37,1,'37:1','(پنھنجي پالڻھار وٽ) قطار ڪري صفن ٻڌڻ وارن (ملائڪن) جو قسم آھي.');
INSERT INTO "amroti" VALUES(37,2,'37:2','پوءِ جھڻڪ ڏيئي (شيطانن) کي ڌمڪائڻ وارن (ملائڪن) جو قسم آھي.');
INSERT INTO "amroti" VALUES(37,3,'37:3','پوءِ (قرآن) پڙھڻ واري جماعت جو قسم آھي.');
INSERT INTO "amroti" VALUES(37,4,'37:4','ته اوھان جو معبُود ھڪڙو (ئي) آھي.');
INSERT INTO "amroti" VALUES(37,5,'37:5','(جو اُھو) آسمانن ۽ زمين جو ۽ جيڪي اُنھن ٻنھي جي وچ ۾ آھي تنھن جو پالڻھار آھي ۽ (اُھو) اُڀرڻ (۽ لَھڻ) وارن طرفن جو (به) پالڻھار آھي.');
INSERT INTO "amroti" VALUES(37,6,'37:6','بيشڪ اسان دُنيا جي آسمانن کي تارن جي سونھن سان سينگاريو.');
INSERT INTO "amroti" VALUES(37,7,'37:7','۽ سڀڪنھن سرڪش شيطان کان (انھيءَ) کي نگاھ رکيوسون.');
INSERT INTO "amroti" VALUES(37,8,'37:8','ته (شيطان) مَلَاءِ الۡاَعۡلىٰ ڏانھن ڪن ڏيئي (ڳالھيون) نه ٻڌن ۽ سڀڪنھن طرف کان (اُنھن ڏانھن) اُماڙيون اُڇلبيون آھن.');
INSERT INTO "amroti" VALUES(37,9,'37:9','(اُنھن جي) تڙڻ لاءِ ۽ اُنھن لاءِ ھميشه جو عذاب آھي.');
INSERT INTO "amroti" VALUES(37,10,'37:10','پر جنھن (چوريءَ ڪن ڏئي مَلَاءِاَعۡلىٰ جي ڳالھين مان ڪنھن ڳالھ کي) جھٽيو تنھن جي پوئتان ٻرندڙ اُماڙي پوندي آھي.');
INSERT INTO "amroti" VALUES(37,11,'37:11','پوءِ کانئن پُڇ ته اُھي پيدا ڪرڻ ۾ تمام مشڪل آھن ڇا يا (ذڪر ڪيل شيون) جن کي اسان بڻايو آھي؟ بيشڪ اسان کين چيڪيءَ مٽيءَ مان بڻايو.');
INSERT INTO "amroti" VALUES(37,12,'37:12','بلڪ (ڪافرن جي حال کان) عجب ڪيئي ۽ اُھي ٺـٺوليون ڪندا آھن.');
INSERT INTO "amroti" VALUES(37,13,'37:13','۽ جڏھن سمجھائبو اٿن (تڏھن) نه سمجھندا آھن.');
INSERT INTO "amroti" VALUES(37,14,'37:14','۽ جڏھن (ھو) ڪا نشاني ڏسن (تڏھن) ٺـٺوليون ڪندا آھن.');
INSERT INTO "amroti" VALUES(37,15,'37:15','۽ چوندا آھن ته ھيءُ (قرآن) رڳو پڌرو جادو آھي.');
INSERT INTO "amroti" VALUES(37,16,'37:16','ھان جڏھن مرنداسون ۽ مٽي ۽ ھڏا ٿينداسون (تڏھن) اسين وري (جيئرا ٿي) کڙا ٿينداسون ڇا؟');
INSERT INTO "amroti" VALUES(37,17,'37:17','اسان جا اڳيان پيءُ ڏاڏا به (اٿاربا) ڇا؟');
INSERT INTO "amroti" VALUES(37,18,'37:18','چؤ ته ھائو (اُٿاربا) حالانڪ اوھين خوار ھوندؤ.');
INSERT INTO "amroti" VALUES(37,19,'37:19','پوءِ اُھو (کڙو ٿيڻ) رڳو ھڪ سخت جھڻڪار ھوندي پوءِ اُھي اُتي جو اُتي ڏسڻ لڳندا.');
INSERT INTO "amroti" VALUES(37,20,'37:20','۽ چوندا ته ھيء ارمان اسان لاءِ ھي بدلي (وٺڻ) جو ڏينھن آھي.');
INSERT INTO "amroti" VALUES(37,21,'37:21','(چئبن ته) نبيري جو ڏينھن جنھن کي اوھين ڪوڙو ڀائيندا ھيؤ سو ھي آھي.');
INSERT INTO "amroti" VALUES(37,22,'37:22','(چئبو ته اي ملائڪو) ظالمن کي ۽ سندن سنگتين کي ۽ جن کي الله کانسواءِ پوڄيندا ھوا');
INSERT INTO "amroti" VALUES(37,23,'37:23','تن کي گڏ ڪريو پوءِ کين دوزخ جي واٽ ڏانھن ڏس ڏيو.');
INSERT INTO "amroti" VALUES(37,24,'37:24','۽ کين ترسايو جو بيشڪ کانئن پڇبو.');
INSERT INTO "amroti" VALUES(37,25,'37:25','(ته) اوھان کي ڇا (ٿيو) آھي جو ھڪ ٻئي جي مدد نٿا ڪريو؟');
INSERT INTO "amroti" VALUES(37,26,'37:26','بلڪ اُھي اڄ ڪنڌ نوائيندڙ آھن.');
INSERT INTO "amroti" VALUES(37,27,'37:27','۽ ڪي ڪن ڏانھن مھاڙ ڪري ھڪ ٻئي کان پڇڻ لڳندا.');
INSERT INTO "amroti" VALUES(37,28,'37:28','(ماڻھو پنھنجن سردارن کي) چوندا ته بيشڪ اوھين (گمراھ ڪرڻ لاءِ) سڄي (۽ کٻي) پاسي کان اسان وٽ ايندا ھُيؤ.');
INSERT INTO "amroti" VALUES(37,29,'37:29','(سردار) چوندا ته (نه!) بلڪ اوھين پاڻ مُسلمان نه ھُيؤ.');
INSERT INTO "amroti" VALUES(37,30,'37:30','۽ اوھان تي اسان جو ڪو زور نه (ھلندو) ھو، بلڪ (اوھين) پاڻ حد کان لنگھيل قوم ھُيؤ.');
INSERT INTO "amroti" VALUES(37,31,'37:31','پوءِ اسان تي اسان جي پالڻھار جو چوڻ ثابت ٿيو ته بيش اسين (عذاب) چکندڙ ھُجون.');
INSERT INTO "amroti" VALUES(37,32,'37:32','پوءِ اوھان کي گمراھ ڪيوسون جو بيشڪ اسين (پاڻ) به گمراھ ھواسون.');
INSERT INTO "amroti" VALUES(37,33,'37:33','پوءِ بيشڪ اُھي اُنھيءَ ڏينھن عذاب ۾ ھڪ ٻئي سا گڏ شريڪ ھوندا.');
INSERT INTO "amroti" VALUES(37,34,'37:34','بيشڪ اسين ڏوھارين سان اھڙي طرح ڪندا آھيون.');
INSERT INTO "amroti" VALUES(37,35,'37:35','بيشڪ اُھي (اھڙا) ھوا جو جڏھن کين چئبو ھو ته الله کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي (تڏھن) وڏائي ڪندا ھوا.');
INSERT INTO "amroti" VALUES(37,36,'37:36','۽ چوندا ھوا ته اسان ھڪ چريي شاعر جي (چوڻ) تي پنھنجن معبودن کي ڇڏينداسون ڇا؟');
INSERT INTO "amroti" VALUES(37,37,'37:37','بلڪ (پيغمبر محمد صلى الله عليه وسلم) سچ (يعني قرآن) آندو ۽ (سڀني) پيغمبرن کي سچو ڄاتائين.');
INSERT INTO "amroti" VALUES(37,38,'37:38','بيشڪ اوھين ڏکوئيندڙ عذاب (جو مزو) چکڻ وارا آھيو.');
INSERT INTO "amroti" VALUES(37,39,'37:39','۽ جيڪي ڪندا ھئو تنھن کانسواءِ (ٻيو) ڪو بدلو نه ڏبؤ.');
INSERT INTO "amroti" VALUES(37,40,'37:40','پر الله جا خاص ٻانھا (عذاب کان بچندا).');
INSERT INTO "amroti" VALUES(37,41,'37:41','اُنھن لاءِ روزي مُقرّر (ٿيل) آھي.');
INSERT INTO "amroti" VALUES(37,42,'37:42','(قسمين) ميوا، ۽ اُھي عزت وارا آھن.');
INSERT INTO "amroti" VALUES(37,43,'37:43','نعمت وارن باغن ۾.');
INSERT INTO "amroti" VALUES(37,44,'37:44','تختن تي ھڪ ٻئي جي آمھون سامھون ويٺل ھوندا.');
INSERT INTO "amroti" VALUES(37,45,'37:45','وھندڙ شراب جو پيالو مٿن پيو ڦيرائبو.');
INSERT INTO "amroti" VALUES(37,46,'37:46','جو (بلڪل) اڇو پيئندڙن لاءِ مزيدار ھوندو.');
INSERT INTO "amroti" VALUES(37,47,'37:47','نڪي منجھس مٿي جي ڦيري ھوندي ۽ نڪي اُھي کانئس نشي وارا ٿيندا.');
INSERT INTO "amroti" VALUES(37,48,'37:48','۽ وڏين اکين واريون ھيٺ نھاريندڙيون (زالون) وٽن ھونديون.');
INSERT INTO "amroti" VALUES(37,49,'37:49','ڄڻڪ اُھي (حورون) ڍَڪيل آنا آھن.');
INSERT INTO "amroti" VALUES(37,50,'37:50','پوءِ ڪي (ماڻھو) ڪن ڏانھن مھاڙ ڪري ڪري ھڪ ٻئي کان پڇڻ لڳندا.');
INSERT INTO "amroti" VALUES(37,51,'37:51','منجھانئن ڪو چوندڙ چوندو ته بيشڪ (دنيا ۾) منھنجو ھڪ سنگتي ھو.');
INSERT INTO "amroti" VALUES(37,52,'37:52','جو چوندو ھو ته تون (قيامت جو) مڃيندڙ ٿيو آھين ڇا؟');
INSERT INTO "amroti" VALUES(37,53,'37:53','ھا جڏھن مرنداسون ۽ مٽي ۽ ھڏا ٿينداسون (تڏھن) اسان کي بدلو ڏنو ويندو ڇا؟');
INSERT INTO "amroti" VALUES(37,54,'37:54','(وري اُھو) چوندو ته اوھين (به دوزخين کي) ڏسو ٿا ڇا؟');
INSERT INTO "amroti" VALUES(37,55,'37:55','پوءِ (پاڻ) جھاتي پائيندو ته اُھو (سنگتي) دوزخ جي وچ ۾ ڏسندو.');
INSERT INTO "amroti" VALUES(37,56,'37:56','چوندو ته الله جو قسم ته تو مونکي ذري گھٽ ھلاڪ ٿي ڪيو.');
INSERT INTO "amroti" VALUES(37,57,'37:57','۽ جيڪڏھن منھنجي پالڻھار (جو مون تي) فضل نه ٿئي ھا ته بيشڪ آءٌ (به عذاب ۾) حاضر ڪيلن مان ھجان ھا.');
INSERT INTO "amroti" VALUES(37,58,'37:58','بيشڪ اسين نڪي (ھاڻي وري) مرڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(37,59,'37:59','(اھو) رڳو اسان جو پھريون مرڻ ھو (جو ٿي چڪو) ۽ نڪي عذاب ڪيل آھيون.');
INSERT INTO "amroti" VALUES(37,60,'37:60','بيشڪ اِھا وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(37,61,'37:61','اھڙي (نعمت) لاءِ ڀلي ته عمل ڪندڙ عمل ڪن.');
INSERT INTO "amroti" VALUES(37,62,'37:62','ڀلا اِھا (بيان ڪيل نعمت) مھمانيءَ طور ڀلي آھي يا ٿوھر جو وڻ؟');
INSERT INTO "amroti" VALUES(37,63,'37:63','بيشڪ اسان اِن (وڻ) کي ظالمن جي لاءِ عذاب ٺھرايو آھي.');
INSERT INTO "amroti" VALUES(37,64,'37:64','بيشڪ اِھو ھڪ وڻ آھي جو دوزخ جي تري ۾ پيدا ٿيندو آھي.');
INSERT INTO "amroti" VALUES(37,65,'37:65','اُن جا گوشا ڄڻڪ شيطانن جون سِسيون آھن.');
INSERT INTO "amroti" VALUES(37,66,'37:66','پوءِ بيشڪ دوزخي اُن مان کائيندا پوءِ منجھانئس پيٽ ڀريندا.');
INSERT INTO "amroti" VALUES(37,67,'37:67','وري اُنھن لاءِ اُن (کاڄ) تي ٽھڪندڙ پاڻيءَ جي ملاوت ھوندي.');
INSERT INTO "amroti" VALUES(37,68,'37:68','وري بيشڪ کائڻ پيئڻ کانپوءِ سندن موٽڻ دوزخ ڏانھن ھوندو.');
INSERT INTO "amroti" VALUES(37,69,'37:69','بيشڪ اُنھن پنھنجن پيءُ ڏاڏن کي گمراھ ڏٺو.');
INSERT INTO "amroti" VALUES(37,70,'37:70','تنھنڪري اُھي (به) سندن پيرن تي ڊوڙن ٿا.');
INSERT INTO "amroti" VALUES(37,71,'37:71','۽ بيشڪ کانئن اڳ اڳين مان گھڻا گمراھ ٿيا ھوا.');
INSERT INTO "amroti" VALUES(37,72,'37:72','۽ بيشڪ منجھن ڊيڄاريندڙ موڪليا ھواسون.');
INSERT INTO "amroti" VALUES(37,73,'37:73','پوءِ نھار ته ڊيڄاريلن جي پڇاڙي ڪئن ٿي؟');
INSERT INTO "amroti" VALUES(37,74,'37:74','پر الله جا خاص ٻانھا (انھن جي پڇاڙي چڱي ٿي).');
INSERT INTO "amroti" VALUES(37,75,'37:75','۽ بيشڪ نوح اسان کي سڏيو پوءِ چڱا (دُعا کي) قبول ڪندڙ آھيون.');
INSERT INTO "amroti" VALUES(37,76,'37:76','۽ کيس ۽ سندس (تعلق وارن) ماڻھن کي وڏي مصيبت کان بچايوسون.');
INSERT INTO "amroti" VALUES(37,77,'37:77','۽ (فقط) سندس اولاد کي ئي باقي رھندڙ ڪيوسون.');
INSERT INTO "amroti" VALUES(37,78,'37:78','۽ پوين ۾ اُن لاءِ (چڱي يادگيري) ڇڏي سون.');
INSERT INTO "amroti" VALUES(37,79,'37:79','ساري جَڳ ۾ نوح تي شال سلام ھجي.');
INSERT INTO "amroti" VALUES(37,80,'37:80','بيشڪ اسين ڀلارن کي اھڙي طرح بدلو ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(37,81,'37:81','بيشڪ اُھو اسان جي مؤمنن ٻانھن مان آھي.');
INSERT INTO "amroti" VALUES(37,82,'37:82','وري ٻـين کي ٻوڙيوسون.');
INSERT INTO "amroti" VALUES(37,83,'37:83','۽ بيشڪ نوح جي پيروري ڪندڙن مان ابراھيم ھو.');
INSERT INTO "amroti" VALUES(37,84,'37:84','(ياد ڪر) جڏھن (ھو) پنھنجي پالڻھار وٽ سالم دِل سان آيو.');
INSERT INTO "amroti" VALUES(37,85,'37:85','(ياد ڪر) جڏھن پنھنجي پيءُ ۽ پنھنجي قوم کي چيائين ته اوھان ڇاکي پوڄيندا آھيو؟');
INSERT INTO "amroti" VALUES(37,86,'37:86','الله کانسواءِ ھٿرادو ٺھيل معبودن کي گھرو ٿا ڇا؟');
INSERT INTO "amroti" VALUES(37,87,'37:87','پوءِ جھانن جي پالڻھار بابت اوھان جو ڪھڙو گُمان آھي؟');
INSERT INTO "amroti" VALUES(37,88,'37:88','پوءِ تارن ۾ ھڪ ڀيرو چتائي ڏٺائين.');
INSERT INTO "amroti" VALUES(37,89,'37:89','پوءِ چيائين ته آءٌ بيمار ٿيندس.');
INSERT INTO "amroti" VALUES(37,90,'37:90','پوءِ کانئس پُٺي ڏيئي ڦِريا.');
INSERT INTO "amroti" VALUES(37,91,'37:91','پوءِ سندن معبودن ڏانھن منھن ڪيائين ته اوھين ڇونه ٿا کائو؟');
INSERT INTO "amroti" VALUES(37,92,'37:92','اوھان کي ڇا (ٿيو آھي) جو نه ٿا ڳالھايو؟');
INSERT INTO "amroti" VALUES(37,93,'37:93','پوءِ سڄي ھٿ (يعني زور) سان مٿن ڏاڍا ڌَڪ ھڻڻ لڳو.');
INSERT INTO "amroti" VALUES(37,94,'37:94','پوءِ (قوم وارا) ڏانھس ڊوڙندا سامھون آيا.');
INSERT INTO "amroti" VALUES(37,95,'37:95','(کين) چيائين ته جنھن کي اوھين (پاڻ) گھَڙيندا آھيو تنھن کي ڇو پوڄيندا آھيو؟');
INSERT INTO "amroti" VALUES(37,96,'37:96','ھن ھوندي جو الله اوھان کي ۽ جيڪي اوھين ڪريو ٿا تنھن کي پيدا ڪيو.');
INSERT INTO "amroti" VALUES(37,97,'37:97','(ھڪ ٻئي کي) چيائون ته ابراھيم لاءِ اڏاوت اڏيو پوءِ کيس (باھ جي) آڙاھ ۾ اُڇليو.');
INSERT INTO "amroti" VALUES(37,98,'37:98','پوءِ ساڻس فريب ڪرڻ جو ارادو ڪيائون پوءِ کين تمام زير ڪيوسون.');
INSERT INTO "amroti" VALUES(37,99,'37:99','۽ ابراھيم چيو ته بيشڪ آءٌ پنھنجي پالڻھار ڏانھن وڃڻ وارو آھيان (اھو) سگھوئي مون کي واٽ ڏيکاريندو (جتي سندس رضا ھوندي).');
INSERT INTO "amroti" VALUES(37,100,'37:100','اي منھنجا پالڻھار صالحن مان (ڪو پُٽ) مون کي عطا ڪر.');
INSERT INTO "amroti" VALUES(37,101,'37:101','پوءِ کيس ھڪ حليمت واري نينگر جي خوشخبري ڏني سون.');
INSERT INTO "amroti" VALUES(37,102,'37:102','پوءِ جڏھن ساڻس گڏ ڊوڙڻ (واريءَ عمر) کي پھتو (تڏھن ابراھيم) چيو ته اي منھنجا پُٽڙا بيشڪ آءٌ ننڊ ۾ ڏسان ٿو ته آءٌ توکي ڪُھان ٿو پوءِ تون نظر ڪر ته تنھنجي دل ۾ ڇا ٿو اچي؟ چيائين ته اي مُنھنجا ابا جنھن ڪم جو توکي حُڪم ڏنو وڃي ٿو سو ڪر جيڪڏھن الله گھريو ته مون کي صبر ڪرڻ وارن مان لھندين.');
INSERT INTO "amroti" VALUES(37,103,'37:103','پوءِ جڏھن ٻنھي حُڪم مڃيو ۽ ابراھيم (پنھنجي) پُٽ کي پيشانيءَ ڀر ليٽايو.');
INSERT INTO "amroti" VALUES(37,104,'37:104','۽ کيس سڏيوسون ته اي ابراھيم.');
INSERT INTO "amroti" VALUES(37,105,'37:105','بيشڪ تو خواب کي سچو ڪيو، بيشڪ اسين ڀلارن کي اھڙي طرح بدلو ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(37,106,'37:106','بيشڪ اِھا پڌري پرک آھي.');
INSERT INTO "amroti" VALUES(37,107,'37:107','۽ ذُبح جي وڏي جانور سان اُن جو عوض ڏنوسون.');
INSERT INTO "amroti" VALUES(37,108,'37:108','۽ پوين ۾ (چڱي ساراھ) اُن لاءِ ڇڏي سون.');
INSERT INTO "amroti" VALUES(37,109,'37:109','ابراھيم تي شال سلام ھجي.');
INSERT INTO "amroti" VALUES(37,110,'37:110','ڀلارن کي اھڙي طرح بدلو ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(37,111,'37:111','بيشڪ اُھو اسان جي مؤمنن ٻانھن مان آھي.');
INSERT INTO "amroti" VALUES(37,112,'37:112','۽ کيس اسحٰق جي خوشخبري ڏني سون جو صالحن مان ھڪڙو نبي آھي.');
INSERT INTO "amroti" VALUES(37,113,'37:113','۽ ابراھيم تي ۽ اسحٰق تي برڪت نازل ڪئي سون، ۽ ٻنھي جي اولاد مان ڪي چڱا آھن ۽ ڪي پاڻ تي پڌرو ظلم ڪندڙ آھن.');
INSERT INTO "amroti" VALUES(37,114,'37:114','۽ بيشڪ موسىٰ ۽ ھارون تي احسان ڪيوسون.');
INSERT INTO "amroti" VALUES(37,115,'37:115','۽ کين ۽ سندن قوم کي وڏي سختيءَ کان بچايوسون.');
INSERT INTO "amroti" VALUES(37,116,'37:116','۽ کين مدد ڏني سون پوءِ اُھي غالب رھيا.');
INSERT INTO "amroti" VALUES(37,117,'37:117','۽ کين پڌرو ڪتاب ڏنوسون.');
INSERT INTO "amroti" VALUES(37,118,'37:118','۽ کين سِڌي رستي لاتوسون.');
INSERT INTO "amroti" VALUES(37,119,'37:119','۽ پوين ۾ (چڱي ساراھ) انھن لاءِ ڇڏي سون.');
INSERT INTO "amroti" VALUES(37,120,'37:120','مُوسىٰ ۽ ھارون تي شال سلام ھجي.');
INSERT INTO "amroti" VALUES(37,121,'37:121','بيشڪ اسين ڀلارن کي اھڙيءَ طرح بدلو ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(37,122,'37:122','بيشڪ اُھي (ٻئي) اسان جي مؤمن ٻانھن مان آھن.');
INSERT INTO "amroti" VALUES(37,123,'37:123','۽ بيشڪ الياس پيغمبرن مان ھو.');
INSERT INTO "amroti" VALUES(37,124,'37:124','جڏھن پنھنجي قوم کي چيائين ته (الله کان) ڇونه ڊڄندا آھيو؟');
INSERT INTO "amroti" VALUES(37,125,'37:125','ڇو بَعل کي پوڄيندا آھيو ۽ تمام چڱي خلقيندڙ کي ڇڏيندا آھيو؟');
INSERT INTO "amroti" VALUES(37,126,'37:126','(جو) الله اوھان جو پالڻھار ۽ اوھان جي اڳين پِين ڏاڏن جو پالڻھار آھي.');
INSERT INTO "amroti" VALUES(37,127,'37:127','پوءِ کيس ڪوڙو ڀانيائون تنھنڪري اُھي بيشڪ (دوزخ ۾) حاضر ڪيل آھن.');
INSERT INTO "amroti" VALUES(37,128,'37:128','پر الله جا خاص ٻانھا (ڇٽل آھن).');
INSERT INTO "amroti" VALUES(37,129,'37:129','۽ پوين ۾ (چڱي ساراھ) اُن لاءِ ڇڏي سون.');
INSERT INTO "amroti" VALUES(37,130,'37:130','الياس تي شال سلام ھجي.');
INSERT INTO "amroti" VALUES(37,131,'37:131','بيشڪ اسين ڀلارن کي اھڙي طرح بدلو ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(37,132,'37:132','بيشڪ اُھو اسان جي مؤمن ٻانھن مان آھي.');
INSERT INTO "amroti" VALUES(37,133,'37:133','۽ بيشڪ لُوط پيغمبرن مان ھو.');
INSERT INTO "amroti" VALUES(37,134,'37:134','(ياد ڪر) جڏھن اُن کي ۽ سندس مڙني گھر وارن کي بچايوسون.');
INSERT INTO "amroti" VALUES(37,135,'37:135','سواءِ ھڪڙي ڪراڙي زال جي جا پُٺ تي رھندڙن مان ٿي.');
INSERT INTO "amroti" VALUES(37,136,'37:136','وري (ھنن) ٻـين کي ھلاڪ ڪيوسون.');
INSERT INTO "amroti" VALUES(37,137,'37:137','۽ بيشڪ اوھين انھن (جي ڳوٺن) وٽان صبح جو لنگھندا آھيو.');
INSERT INTO "amroti" VALUES(37,138,'37:138','۽ رات جو (به) پوءِ ڇونه سمجھندا آھيو؟');
INSERT INTO "amroti" VALUES(37,139,'37:139','۽ بيشڪ يُونس پيغمبرن مان ھو.');
INSERT INTO "amroti" VALUES(37,140,'37:140','جڏھن (ھو) ڀريل ٻيڙيءَ ڏانھن ڀڳو.');
INSERT INTO "amroti" VALUES(37,141,'37:141','پوءِ (ٻيڙي وارن سان) پُکو وڌائين ته (پاڻ) پُکي پيلن مان ٿيو.');
INSERT INTO "amroti" VALUES(37,142,'37:142','پوءِ کيس مڇي ڳھي وئي ۽ اُھو (اھڙو ڪم ڪندڙ ھو جو) ملامت جو لائق ھو.');
INSERT INTO "amroti" VALUES(37,143,'37:143','پوءِ اُھو جيڪڏھن تسبيح چوندڙن مان نه ھجي ھا.');
INSERT INTO "amroti" VALUES(37,144,'37:144','ته اُنھي ڏينھن تائين جنھن ۾ (ماڻھو) اُٿارجن اُن جي پيٽ ۾ رھي ھا.');
INSERT INTO "amroti" VALUES(37,145,'37:145','پوءِ کيس صاف پَٽ ۾ اُڇليوسون ۽ اُھو بيمار ھو.');
INSERT INTO "amroti" VALUES(37,146,'37:146','۽ مٿس وڻ ڪدوءَ (جي قِسم) مان ڄمايوسون.');
INSERT INTO "amroti" VALUES(37,147,'37:147','۽ کيس (ھڪ) لک يا (اُن کان) وڌيڪ (ماڻھن) ڏانھن موڪليوسون.');
INSERT INTO "amroti" VALUES(37,148,'37:148','پوءِ انھن ايمان آندو پوءِ ھڪ وقت تائين کين فائدو ڏنوسون.');
INSERT INTO "amroti" VALUES(37,149,'37:149','پوءِ (اي پيغمبر) اُنھن (يعني مشرڪن) کان پُڇ ته تنھنجي پالڻھار کي ڌيئر آھن ڇا يا کيس پُٽ آھنئ');
INSERT INTO "amroti" VALUES(37,150,'37:150','يا ملائڪن کي زالون بڻايوسون ۽ اُھي (اُن وقت) حاضر ھوا.');
INSERT INTO "amroti" VALUES(37,151,'37:151','خبردار ھُجو (ته) بيشڪ اُھي پنھنجي (ھٿرادو ٺھيل) ڪوڙ مان چوندا آھن.');
INSERT INTO "amroti" VALUES(37,152,'37:152','ته الله (اولاد کي) ڄڻيو آھي ۽ اُھي ڪوڙا آھن.');
INSERT INTO "amroti" VALUES(37,153,'37:153','پٽن کان ڌيئر پسند ڪيائين ڇا؟');
INSERT INTO "amroti" VALUES(37,154,'37:154','اوھان کي ڇا ٿيو آھي، ڪيئن نبيرو ڪندا آھيو؟');
INSERT INTO "amroti" VALUES(37,155,'37:155','(اوھين) ڇونه ڌيان ڪندا آھيو؟');
INSERT INTO "amroti" VALUES(37,156,'37:156','يا اوھان کي ڪا پڌري حُجّت آھي؟');
INSERT INTO "amroti" VALUES(37,157,'37:157','جيڪڏھن اوھين سچا آھيو ته پنھنجو ڪتاب آڻيو.');
INSERT INTO "amroti" VALUES(37,158,'37:158','۽ (ڪافرن) الله ۽ جِنن جي وچ ۾ مائٽي مُقرر ڪئي، ۽ بيشڪ جِنن ڄاتو آھي ته اُھي (قيامت ۾ حساب لاءِ) حاضر ڪيا ويندا.');
INSERT INTO "amroti" VALUES(37,159,'37:159','(ڪافر) جيڪي بيان ڪندا آھن تنھن کان الله پاڪ آھي.');
INSERT INTO "amroti" VALUES(37,160,'37:160','پر الله جا خاص ٻانھا (انھن جو چوڻ سچو آھي).');
INSERT INTO "amroti" VALUES(37,161,'37:161','پوءِ (اي ڪافرؤ) بيشڪ اوھين ۽ جن (بتن) کي پوڄيندا آھيو.');
INSERT INTO "amroti" VALUES(37,162,'37:162','اوھين (سڀئي) اُن (يعني ڪوڙن معبُودن جي عبادت) لاءِ گمراھ ڪرڻ وارا نه آھيو.');
INSERT INTO "amroti" VALUES(37,163,'37:163','پر اُنھن کي جيڪو (پاڻ) دوزخ ۾ گھڙڻ وارو آھي.');
INSERT INTO "amroti" VALUES(37,164,'37:164','۽ (ملائڪ چوندا آھن ته) اسان مان اھڙو آھي ئي ڪونه جنھن لاءِ (عبادت جي) ھڪ جاءِ ٺھرايل نه آھي.');
INSERT INTO "amroti" VALUES(37,165,'37:165','۽ بيشڪ اسين صِف ٻَڌڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(37,166,'37:166','۽ بيشڪ اسين تسبيح چوڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(37,167,'37:167','۽ بيشڪ (عرب جا ڪافر) چوندا ھوا.');
INSERT INTO "amroti" VALUES(37,168,'37:168','ته جيڪڏھن اسان وٽ پھرين جو احوال ھجي ھا.');
INSERT INTO "amroti" VALUES(37,169,'37:169','ته ضرور الله جا خاص ٻانھا ٿيون ھا.');
INSERT INTO "amroti" VALUES(37,170,'37:170','وري اُن (يعني قرآن) جا مُنڪر ٿيا پوءِ سِگھوئي (ڪفر جي پڄاڻي) ڄاڻندا.');
INSERT INTO "amroti" VALUES(37,171,'37:171','۽ بيشڪ پنھنجن ٻانھن پيغمبرن لاءِ اسان جو وعدو اڳي ئي ٿي چڪو..');
INSERT INTO "amroti" VALUES(37,172,'37:172','ته بيشڪ اُھي (اسان جا پيغمبر) ئي فتح مند آھن.');
INSERT INTO "amroti" VALUES(37,173,'37:173','۽ بيشڪ اسان جو لشڪر ئي غالب آھي.');
INSERT INTO "amroti" VALUES(37,174,'37:174','پوءِ کانئن ھڪ وقت تائين منھن موڙ.');
INSERT INTO "amroti" VALUES(37,175,'37:175','۽ کين ڏسندو رھ پوءِ اُھي به سگھوئي ڏسندا.');
INSERT INTO "amroti" VALUES(37,176,'37:176','(ھي ڪافر) اسان جو عذاب جلد گھرندا آھن ڇا؟');
INSERT INTO "amroti" VALUES(37,177,'37:177','پوءِ جڏھن سندن (گھرن جي) اڱڻن ۾ (عذاب) لھندو تڏھن ڊيڄاريلن جو صُبح بڇڙو ٿيندو.');
INSERT INTO "amroti" VALUES(37,178,'37:178','۽ کانئن ھڪ وقت تائين مُنھن موڙ.');
INSERT INTO "amroti" VALUES(37,179,'37:179','۽ ڏسندو رھ پوءِ اُھي به سگھوئي (نتيجو) ڏسندا.');
INSERT INTO "amroti" VALUES(37,180,'37:180','تنھنجو پالڻھار پاڪ آھي جيڪي (اِھي ڪافر) بيان ڪندا آھن تنھن کان (اُھو) عزّت وارو آھي.');
INSERT INTO "amroti" VALUES(37,181,'37:181','۽ (الله جي) پيغمبرن تي شال سَلام ھجي.');
INSERT INTO "amroti" VALUES(37,182,'37:182','۽ جھانن جي پالڻھار الله کي ئي سڀ ساراھ جڳائيندي آھي.');
INSERT INTO "amroti" VALUES(38,1,'38:1','صٓ (ھن) قرآن نصيحت (ڏيڻ) واري جو قسم آھي (ته جنھن دين ڏانھن سڏين ٿو سو سچ آھي).');
INSERT INTO "amroti" VALUES(38,2,'38:2','بلڪ ڪافر وڏائي ۽ مخالفت ۾ (پيل) آھن.');
INSERT INTO "amroti" VALUES(38,3,'38:3','کانئن اڳ گھڻائي جُڳ ھلاڪ ڪياسون پوءِ دانھون ڪرڻ لڳا ۽ اُھو ڇوٽڪاري جو وقت نه ھو.');
INSERT INTO "amroti" VALUES(38,4,'38:4','۽ منجھانئن ھڪ ڊيڄاريندڙ وٽن آيو ته عجب ڪرڻ لڳا، ۽ (اِھي) ڪافر چوڻ لڳا ته ھيءُ (شخص) جادوگر ڪوڙو آھي.');
INSERT INTO "amroti" VALUES(38,5,'38:5','ڀلا (ڏسو ته) سڀني معبودن کي ھڪ معبود ڪيائين؟ بيشڪ ھي ڏاڍي عجيب ڳالھ آھي.');
INSERT INTO "amroti" VALUES(38,6,'38:6','۽ منجھانئن (سردارن جي) ٽولي (ھڪ ٻئي کي چوندي) ھلي وئي ته ھلو ۽ پنھنجن معبودن (جي پوڄا) تي مُحڪم رھو، بيشڪ ھيءُ (دين) اھڙو آھي جو (اُن ۾ ڪو) غرض رکيو ويو آھي.');
INSERT INTO "amroti" VALUES(38,7,'38:7','اِھا (ڳالھ) پوئين دين ۾ (ڪڏھن) نه ٻڌي سون، ھيءُ ته رڳو ٺاھ آھي.');
INSERT INTO "amroti" VALUES(38,8,'38:8','اسان مڙني جي وچان مٿس قرآن نازل ڪيو ويو ڇا؟ بلڪ اِھي (ڪافر) منھنجي نصيحت کان شڪ ۾ (پيل) آھن، (نه!) بلڪ اڃا منھنجو عذاب نه چکيو اٿن.');
INSERT INTO "amroti" VALUES(38,9,'38:9','تنھنجي پالڻھار غالب بخشڻھار جي ٻاجھ جا (ڪي) خزانا وٽن آھن ڇا؟');
INSERT INTO "amroti" VALUES(38,10,'38:10','آسمانن ۽ زمين جي ۽ جيڪي اِنھن (ٻنھي) جي وچ ۾ آھي تنھنجي بادشاھي انھن جي آھي ڇا؟ (جي ايئن آھي) ته جڳائي ته رسين سان (لڙڪي) مٿي چڙھن.');
INSERT INTO "amroti" VALUES(38,11,'38:11','شڪست کاڌل ٽولين مان ھيءُ (ڪافر) ھتي ھڪ (گڏ ٿيل) لشڪر آھي.');
INSERT INTO "amroti" VALUES(38,12,'38:12','انھن کان اڳ نوح جي قوم ۽ عاد ۽ فرعون ميخن وارو ڪوڙ ڄاتو.');
INSERT INTO "amroti" VALUES(38,13,'38:13','۽ ثمود ۽ لُوط جي قوم ۽ (شھر) ايڪه وارن، انھن جماعتن به (ڪوڙ ڀانيو).');
INSERT INTO "amroti" VALUES(38,14,'38:14','اِھي ٽوليون (جيڪي به ھيون تن) سڀني پيغمبرن کي ڪوڙو ڀانيو تنھنڪري منھنجي سزا (مٿن) لازم ٿي.');
INSERT INTO "amroti" VALUES(38,15,'38:15','۽ ھيءُ (ھن وقت جا ڪافر به) رڳو ھڪ آواز جا منتظر رھندا آھن جنھن کي ڪا ساھي نه ھوندي.');
INSERT INTO "amroti" VALUES(38,16,'38:16','۽ (ٺـٺولي طرح) چوندا آھن ته اي اسان جا پالڻھار حساب جي ڏينھن کان اڳ اسان (جي عذاب) جي چٺي اسان کي جلد ڏي.');
INSERT INTO "amroti" VALUES(38,17,'38:17','(اي پيغمبر) جيڪي چوندا آھن تنھن تي صبر ڪر ۽ اسان جي سگھ واري ٻانھي داؤد کي ياد ڪر، بيشڪ اُھو رجوع ڪرڻ وارو ھو.');
INSERT INTO "amroti" VALUES(38,18,'38:18','بيشڪ اسان جبلن کي سندس تابع ڪيو (جو) سانجھي ۽ صبح جو (ساڻس) تسبيحون پڙھندا ھوا.');
INSERT INTO "amroti" VALUES(38,19,'38:19','۽ گڏ ڪيل پکين کي به (سندس تابع ڪيوسين)، ھر ھڪ (تسبيح گڏ چوڻ ۾) سندس فرمانبردار ھو.');
INSERT INTO "amroti" VALUES(38,20,'38:20','۽ سندس بادشاھيءَ کي مُحڪم ڪيوسين ۽ کيس دانائي ۽ نبير نڇيڙ ڏني سون.');
INSERT INTO "amroti" VALUES(38,21,'38:21','۽ (پاڻ ۾ ٻن) وڙھندڙ جي خبر تو وٽ نه پھتي آھي ڇا؟ جڏھن اُھي ڀِٽ ٽپي عبادت خانه ۾ آيا.');
INSERT INTO "amroti" VALUES(38,22,'38:22','جڏھن داؤد وٽ اندر پھتا تڏھن (ھو) کائن ڊڄي ويو چيائون ته نه ڊڄ، (اسين) ٻه جھڳڙو ڪندڙ آھيون اسان مون ھڪڙي ٻئي تي ڏاڍ ڪيو آھي تنھنڪري اسان جي وچ ۾ انصاف سان نبيرو ڪر ۽ (فيصلي ۾) بي انصافي نه ڪر ۽ اسان کي سڌيءَ واٽ ڏانھن رستي لاءِ.');
INSERT INTO "amroti" VALUES(38,23,'38:23','بيشڪ ھيءُ منھنجو ڀاءُ آھي، جنھن کي نوانوي رڍون آھن ۽ مون کي ھڪ رڍ آھي، پوءِ ھن چيو ته اِھا (ھڪ رڍ) به منھنجي حوالي ڪر ۽ مون کي ڳالھائڻ ۾ سخت ورتائين.');
INSERT INTO "amroti" VALUES(38,24,'38:24','(داؤد) چيو ته (ھن) تنھنجي (ھڪ) رڍ جي گھرڻ ڪري ته پنھنجن رڍن سان گڏي توتي بيشڪ ظلم ڪيو، ۽ بيشڪ ڀائيوارن مان گھڻا ھڪ ٻئي تي ظلم ڪندا آھن سواءِ اُنھن جي جن ايمان آندو ۽ چڱا ڪم ڪيا ۽ اُھي (ظلم نه ڪندڙ) ٿورا آھن، ۽ داؤد ڄاتو ته کيس پرکيو اٿون تنھنڪري پنھنجي پالڻھار کان بخشش گھريائين ۽ سجدو ڪندڙ ٿي ڪري پيو ۽ (الله ڏانھن) رجوع ڪيائين.');
INSERT INTO "amroti" VALUES(38,25,'38:25','پوءِ کيس اِھو (سندس گناھ) بخشيوسون، ۽ بيشڪ اُن کي اسان وٽ مرتبو آھي ۽ چڱو موٽڻ جو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(38,26,'38:26','(چيوسون ته) اي داؤد بيشڪ اسان توکي زمين ۾ نائب مُقرّر ڪيو تنھنڪري ماڻھن جي وچ ۾ انصاف سان فيصلو ڪر ۽ (نفس جي) سَڌ جي تابعداري نه ڪر جو (اھا) توکي الله جي واٽ کان ڀلائيندي، بيشڪ جيڪي الله جي واٽ کان ڀُلن ٿا تن لاءِ انھيءَ سببان سخت عذاب آھي جو حساب جو ڏينھن وساريائون.');
INSERT INTO "amroti" VALUES(38,27,'38:27','۽ آسمان ۽ زمين کي ۽ جيڪي انھن جي وچ ۾ آھي سو اجايو نه بڻايوسون، اھو ڪافرن جو گمان آھي، تنھنڪري ڪافرن لاءِ باھ (جي عذاب) جي خرابي آھي.');
INSERT INTO "amroti" VALUES(38,28,'38:28','جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي زمين ۾ فساد وجھڻ وارن جي برابر ڪنداسون ڇا؟ يا پرھيزگارن کي بڇڙن وانگر ڪنداسون ڇا؟');
INSERT INTO "amroti" VALUES(38,29,'38:29','ھي (قرآن) برڪت وارو ڪتاب آھي جو اُن کي توڏانھن نازل ڪيوسون ته (ماڻھو) اُن جي آيتن ۾ ڌيان ڪن ۽ عقل وارا نصيحت وٺن.');
INSERT INTO "amroti" VALUES(38,30,'38:30','۽ داؤد کي سُليمان (نالي پُٽ) عطا ڪيوسون، (سُليمان) چڱو ٻانھون ھو، بيشڪ اُھو (خُدا ڏانھن) ورڻ وارو ھو.');
INSERT INTO "amroti" VALUES(38,31,'38:31','(ياد ڪر) جڏھن پوياڙيءَ جو تکا گھوڙا وٽس پيش ڪيا ويا.');
INSERT INTO "amroti" VALUES(38,32,'38:32','پوءِ چيائين ته بيشڪ مون پنھنجي پالڻھار جي ياد ڪرڻ کان (ڀُلي ھنن گھوڙن جي) مال جي پيار کي (وڌيڪ) دوست رکيو، تان جو (سج) اوٽ جي پٺيان لڪو (۽ وچين نماز قضا ٿي).');
INSERT INTO "amroti" VALUES(38,33,'38:33','(چيائين ته ھنن) گھوڙن کي مون وٽ موٽائي آڻيو، پوءِ (گھوڙن جي) ڄنگھن ۽ ڪنڌن تي (ذبح جو) ھٿ لائڻ شروع ڪيائين.');
INSERT INTO "amroti" VALUES(38,34,'38:34','۽ بيشڪ سليمان کي پرکيوسون ۽ سندس تخت تي ھڪ ڌڙ اُڇليوسون وري (خُدا ڏانھن) موٽيو.');
INSERT INTO "amroti" VALUES(38,35,'38:35','چيائين ته اي منھنجا پالڻھار مون کي بخش ۽ مون کي اھڙي بادشاھي عطا ڪر جو مون کان پوءِ ڪنھن کي به نه جڳائي، بيشڪ تون ئي بخشڻھار آھين.');
INSERT INTO "amroti" VALUES(38,36,'38:36','پوءِ واءُ کي سندس تابع ڪيوسون جتي پھچڻ گھرندو ھو (تتي) آرام سان سندس حُڪم سان ھلندي ھئي.');
INSERT INTO "amroti" VALUES(38,37,'38:37','۽ ديون کي (سندس تابع ڪيوسون) جو ھر ھڪ عمارتون اڏيندڙ ۽ (درياءُ ٽٻندڙ ھو).');
INSERT INTO "amroti" VALUES(38,38,'38:38','۽ ٻيا (ديوَ) زنجيرن ۾ جڪڙيل (تابع ڪياسون).');
INSERT INTO "amroti" VALUES(38,39,'38:39','(چيوسون ته) ھيءُ اسان جو اِحسان آھي پوءِ احسان ڪر يار کي ڇڏ (جئن توکي وڻي) ته (توتي) حساب نه آھي.');
INSERT INTO "amroti" VALUES(38,40,'38:40','۽ بيشڪ اُن کي اسان وٽ مرتبو ۽ چڱو موٽڻ جو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(38,41,'38:41','۽ اسان جي ٻانھي ايّوب کي ياد ڪر، جڏھن پنھنجي پالڻھار کي ٻاڏايائين ته مون کي شيطان ايذاءُ ۽ ڏک پھچايو آھي.');
INSERT INTO "amroti" VALUES(38,42,'38:42','(تڏھن چيوسون ته) زمين تي پنھنجي لت ھڻ، جو ھي (چشمو) وھنجڻ جو ھنڌ ٿڌو ۽ پيئڻ لاءِ (مٺو) آھي.');
INSERT INTO "amroti" VALUES(38,43,'38:43','۽ کيس سندس گھر وارا ۽ ساڻن اوترا (ٻيا به) عطا ڪيوسون (ھيء) اسان جي طرف کان ٻاجھ ۽ ڌيان وارن لاءِ نصيحت آھي.');
INSERT INTO "amroti" VALUES(38,44,'38:44','۽ (چيوسون ته) پنھنجي ھٿ ۾ ٻھار وٺ پوءِ اُن سان (پنھنجي زال کي) ڌڪ ھڻ ۽ قسم نه ڀڃ، بيشڪ اسان کيس صابر لڌو، (ايّوب) چڱو ٻانھو ھو، اُھو (خُدا ڏانھن) ورڻ وارو ھو.');
INSERT INTO "amroti" VALUES(38,45,'38:45','۽ اسان جي ٻانھن ابراھيم ۽ اسحاق ۽ يعقوب کي ياد ڪر جي ھٿن ۽ اکين وارا (يعني علم ۽ عمل وارا) ھوا.');
INSERT INTO "amroti" VALUES(38,46,'38:46','بيشڪ اسان کين ھڪ پاڪ عادت لاءِ نرالو ڪيو جو آخرت جو ياد ڪرڻ آھي.');
INSERT INTO "amroti" VALUES(38,47,'38:47','۽ بيشڪ اُھي اسان وٽ سڳورن نيڪن مان ھوا.');
INSERT INTO "amroti" VALUES(38,48,'38:48','۽ اسماعيل ۽ اَلۡيَسَعَ ۽ ذُوالۡڪِفۡل کي ياد ڪر، ۽ ھر ھڪ نيڪن مان ھوا.');
INSERT INTO "amroti" VALUES(38,49,'38:49','ھيءُ (قرآن) نصيحت آھي، ۽ بيشڪ پرھيزگارن لاءِ چڱو موٽڻ جو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(38,50,'38:50','(يعني) ھميشه (رھڻ) جا باغ آھن (جن جا) دروازا انھن لاءِ کُليل آھن.');
INSERT INTO "amroti" VALUES(38,51,'38:51','اُتي ٽيڪ ڏيندڙ ھوندا اُتي گھڻا ميوا ۽ شراب پيا گھرائيندا.');
INSERT INTO "amroti" VALUES(38,52,'38:52','۽ وٽن ھيٺ نگاھ ڪرڻ واريون ۽ عمر ۾ ھڪ جيڏيون (زالون) ھونديون.');
INSERT INTO "amroti" VALUES(38,53,'38:53','ھي اُھو آھي جنھنجو حساب جي ڏينھن لاءِ انجام ڏنو وڃيوَ ٿو.');
INSERT INTO "amroti" VALUES(38,54,'38:54','بيشڪ اُھو اسان جو رزق آھي جنھن کي ڪا کوٽ نه ھوندي.');
INSERT INTO "amroti" VALUES(38,55,'38:55','اھو (بدلو ڀلارن لاءِ آھي)، ۽ بيشڪ حد کان لنگھندڙن لاءِ بڇڙو موٽڻ جو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(38,56,'38:56','(جو) دوزخ آھي، اُن ۾ گھڙندا، پوءِ اُھو بڇڙو وڇاڻو آھي.');
INSERT INTO "amroti" VALUES(38,57,'38:57','ھي (عذاب) ڪوسو پاڻي آھي ۽ پُونءِ آھي پوءِ ڀلي ته اُن کي چَکن.');
INSERT INTO "amroti" VALUES(38,58,'38:58','۽ اھڙو قسمين قسمين ٻيو (عذاب) به آھي.');
INSERT INTO "amroti" VALUES(38,59,'38:59','ھي ھڪ فوج آھي جا اوھان سان گڏ گھرڻ واري آھي، شال کين ڪا ويڪري جاءِ نه ملي، ڇوته اُھي باھ ۾ گھڙڻ وارا آھن.');
INSERT INTO "amroti" VALUES(38,60,'38:60','(تابع) چوندا ته (نه!) بلڪ اوھان کي شال ڪا ويڪري جاءِ نه ھجي، (جو) اوھان اسان لاءِ ڪُفر کي پراڻي رسم ڪري ڇڏيو، پوءِ دوزخ بڇڙي جاءِ آھي.');
INSERT INTO "amroti" VALUES(38,61,'38:61','چوندا ته اي اسان جا پالڻھار جنھن اسان لاءِ ھيءَ (ڪُفر جي) پراڻي رسم ٺاھي ھجي تنھنجي حق ۾ دوزخ ۾ ٻيڻو عذاب زيادہ ڪر.');
INSERT INTO "amroti" VALUES(38,62,'38:62','۽ دوزخي چوندا ته اسان کي ڇا (ٿيو) آھي جو مڙسن (مسلمانن مسڪينن) کي نٿا ڏسون جن کي بڇڙن مان ڳڻيندا ھواسون؟');
INSERT INTO "amroti" VALUES(38,63,'38:63','(ناحق) ساڻن ٺـٺوليون ڪندا ھواسون ڇا يا کانئن اسان جون اکيون ترڪيون آھن.');
INSERT INTO "amroti" VALUES(38,64,'38:64','بيشڪ دوزخين جو پاڻ ۾ جھيڙو ڪرڻ اِھا (ھڪ) سچي (ڳالھ) آھي.');
INSERT INTO "amroti" VALUES(38,65,'38:65','(کين) چؤ ته آءٌ رڳو ڊيڄاريندڙ آھيان، ۽ (رڳو) اڪيلي زبردست الله کانسواءِ (ٻيو) ڪوبه عبادت جو لائق نه آھي.');
INSERT INTO "amroti" VALUES(38,66,'38:66','آسمانن ۽ زمين جو ۽ جيڪي انھن (ٻنھي) جي وچ ۾ آھي تنھنجو پالڻھار غالب بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(38,67,'38:67','(کين) چؤ ته اِھو (قرآن) ھڪ وڏي خبر آھي.');
INSERT INTO "amroti" VALUES(38,68,'38:68','اوھين کانئس مُنھن موڙيندا آھيو.');
INSERT INTO "amroti" VALUES(38,69,'38:69','مون کي (اُنھيءَ جماعت) وڏي مرتبي وارن ملائڪن (جي حال) جي ڪا خبر نه آھي جڏھن (اُھي) پاڻ ۾ سوال جواب ڪندا آھن.');
INSERT INTO "amroti" VALUES(38,70,'38:70','مون ڏانھن ھيء ئي وحي ٿيندو آھي ته آءٌ رڳو پڌرو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(38,71,'38:71','(ياد ڪر) جڏھن تنھنجي پالڻھار ملائڪن کي چيو ته بيشڪ آءٌ مٽيءَ مان ھڪ ماڻھو بڻائڻ وارو آھيان.');
INSERT INTO "amroti" VALUES(38,72,'38:72','پوءِ جڏھن کيس پورو ٺاھي رھان ۽ منجھس پنھنجو رُوح ڦوڪيان تڏھن کيس سجدو ڪندڙ ٿي ڪري پئجو.');
INSERT INTO "amroti" VALUES(38,73,'38:73','پوءِ ملائڪن مڙني گڏ سجدو ڪيو.');
INSERT INTO "amroti" VALUES(38,74,'38:74','مگر ابليس (نه سجدو ڪيو)، ھن وڏائي ڪئي ۽ ڪافرن مان ٿيو.');
INSERT INTO "amroti" VALUES(38,75,'38:75','(الله) چيو ته اي ابليس جنھن کي پنھنجي ٻنھي ھٿن سان پيدا ڪيم تنھن کي سجدي ڪرڻ کان توکي ڪھڙيءَ ڳالھ جھليو، تو وڏائي ڪئي ڇا يا تون (حقيقت) ۾ وڏي مرتبي وارن مان آھين؟');
INSERT INTO "amroti" VALUES(38,76,'38:76','(ابليس) چيو ته آءٌ اُن کان ڀلو آھيان، (جو) مون کي باھ مان بڻايئي ۽ اُن کي مٽيءَ مان بڻايئي.');
INSERT INTO "amroti" VALUES(38,77,'38:77','(الله) چيو ته انھيءَ ڪري اُن مان نڪر جو بيشڪ تون تڙيل آھين.');
INSERT INTO "amroti" VALUES(38,78,'38:78','۽ بيشڪ توتي بدلي جي ڏينھن تائين منھنجي لعنت آھي.');
INSERT INTO "amroti" VALUES(38,79,'38:79','(اُن) چيو ته اي منھنجا پالڻھار تڏھن اُنھي ڏينھن تائين مونکي مُھلت ڏي (جنھن ڏينھن سڀ ماڻھو قبر مان) اُٿاريا وڃن.');
INSERT INTO "amroti" VALUES(38,80,'38:80','الله فرمايو ته (چڱو) بيشڪ تون مُھلت ڏنلن مان آھين.');
INSERT INTO "amroti" VALUES(38,81,'38:81','اُنھيءَ ٺھرايل وقت جي ڏينھن تائين.');
INSERT INTO "amroti" VALUES(38,82,'38:82','(ابليس) چيو ته تڏھن تنھنجي عزت جو قسم آھي ته ضرور اُنھن مڙني کي گمراھ ڪندس.');
INSERT INTO "amroti" VALUES(38,83,'38:83','منجھانئن تنھنجي خالص ٻانھن کانسواءِ (جو انھن تي وس نه ھلندو).');
INSERT INTO "amroti" VALUES(38,84,'38:84','الله فرمايو ته (اِھا ڳالھ) سچ آھي ۽ (آءٌ) سچ چوان ٿو.');
INSERT INTO "amroti" VALUES(38,85,'38:85','ته توسان ۽ منجھانئن جيڪي تنھنجي پيروي ڪندا تن مڙني سان دوزخ کي ضرور ڀريندس.');
INSERT INTO "amroti" VALUES(38,86,'38:86','(اي پيغمبر کين) چؤ ته آءٌ اوھان کان قرآن (پھچائڻ) بابت ڪو اُجورو نٿو گھران ۽ نڪي آءٌ (ڪوڙ) ٺاھڻ وارن مان آھيان.');
INSERT INTO "amroti" VALUES(38,87,'38:87','ھي (قرآن) ته جھانن لاءِ نصيحت آھي.');
INSERT INTO "amroti" VALUES(38,88,'38:88','۽ ڪنھن وقت کانپوءِ اُن جي سچائي جي خبر ضرور اوھين ڄاڻندؤ.');
INSERT INTO "amroti" VALUES(39,1,'39:1','(ھن) ڪتاب جو نازل ڪرڻ غالب حِڪمت واري الله (جي طرف) کان آھي.');
INSERT INTO "amroti" VALUES(39,2,'39:2','بيشڪ اسان توڏانھن سچ سان ڪتاب نازل ڪيو ته الله لاءِ (پنھنجي) عبادت کي خالص ڪري خاص اُن جي عبادت ڪر.');
INSERT INTO "amroti" VALUES(39,3,'39:3','خبردار ٿيءُ خالص عبادت الله ئي جي لاءِ آھي، ۽ جن الله کانسواءِ ٻيا دوست ڪري ورتا، (سي چوندا آھن ته) انھن کي رڳو ھن لاءِ پوڄيندا آھيون ته اسان کي الله ڏانھن مرتبي ۾ ويجھو ڪن، جنھن ڳالھ بابت اُھي جھڳڙو ڪندا آھن تنھن بابت بيشڪ انھن جي وچ ۾ الله فيصلو ڪندو، بيشڪ الله اُنھي کي سڌو رستو نه ڏيکاريندو جيڪو ڪوڙو (۽) بي شُڪر آھي.');
INSERT INTO "amroti" VALUES(39,4,'39:4','جيڪڏھن الله (پاڻ لاءِ) اولاد وٺڻ گھري ھا ته جيڪي (سڀئي) بڻايو اٿس تنھن مان جيڪو وڻيس ھا سو چونڊي ھا پر اُن جي ذات پاڪ آھي، اُھو ته الله اڪيلو (۽) زبردست آھي.');
INSERT INTO "amroti" VALUES(39,5,'39:5','آسمانن ۽ زمين کي (پوريءَ) رِٿ سان بڻايائين، رات کي ڏينھن تي لپيٽيندو آھي ۽ ڏينھن کي رات تي لپيٽيندو آھي ۽ سج ۽ چنڊ کي تابع ڪيائين، ھر ھڪ ٺھرايل مُدّت تي ھلندو آھي، خبردار اُھو (ئي) غالب بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(39,6,'39:6','اوھان کي ھڪ جيءَ مان بڻايائين وري منجھانئس سندس زال کي پيدا ڪيائين ۽ ڍورن مان اَٺ قسم اوھان لاءِ لاٿائين، اوھان کي اوھان کي مائن جي پيٽن ۾ ھڪ (قسم جي) بڻاوت کانپوءِ ٻئي (قسم جي) بڻاوت ٽن اونداھين ۾ پيدا ڪندو آھي، اُھو الله اوھان جو پالڻھار آھي اُن جي ئي (ساري) بادشاھي آھي، ان کانسواءِ ٻيو ڪو عبادت جو لائق نه آھي، پوءِ ڪيڏانھن ڦيرايا ويندا آھيو.');
INSERT INTO "amroti" VALUES(39,7,'39:7','جيڪڏھن بي شُڪري ڪندؤ ته بيشڪ الله اوھان کان بي پرواھ آھي، ۽ پنھنجن ٻانھن جي بي شُڪريءَ کي پسند نه ڪندو آھي، ۽ جيڪڏھن شڪر ڪندؤ ته بيشڪ الله اوھان لاءِ اُن کي پسند ڪندو، ۽ ڪو بار کڻندڙ ٻئي جو بار نه کڻندو، وري اوھان جو موٽڻ پنھنجي پالڻھار ڏانھن آھي پوءِ جيڪي ڪندا ھئو تنھنجي اوھان کي سُڌ ڏيندو، بيشڪ اُھو سِينن وارو (ڳجھ) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(39,8,'39:8','۽ جڏھن ماڻھوءَ کي ڪو ڏک پھچندو آھي (تڏھن) پنھنجي پالڻھار کي ڏانھس ورندڙ ٿي سڏيندو آھي وري جڏھن پاڻ وٽان کيس ڪا نعمت بخشيندو آھي (تڏھن) جنھن (مطلب) لاءِ اڳ سڏيندو ھو تنھنکي وساريندو آھي ۽ الله لاءِ شريڪ مقرّر ڪندو آھي ته سندس واٽ کان (ماڻھن کي) ڀُلائي، (اي پيغمبر) چؤ ته (اي ڪافر) پنھنجي ڪُفر (جي حالت) ۾ ٿورو (وقت) نفعو وٺ، بيشڪ تون دوزخين مان آھين.');
INSERT INTO "amroti" VALUES(39,9,'39:9','ڀلا جيڪو رات جي وقتن ۾ سجدو ڪندڙ ۽ بيھي عبادت ڪندڙ آھي آخرت (جي عذاب) کان ڊڄندو آھي ۽ پنھنجي پالڻھار جي رحمت جي اميد رکندو آھي (سو ڀَلو آھي يا اُھو بي شُڪر مُشرڪ؟)، (اي پيغمبر) چؤ ته سمجھ وارا ۽ بي سمجھ (پاڻ ۾) برابر ٿيندا ڇا؟ ڌيان وارا ئي نصيحت وٺندا آھن.');
INSERT INTO "amroti" VALUES(39,10,'39:10','چؤ ته اي منھنجا مُؤمن ٻانھؤ پنھنجي پالڻھار کان ڊڄو، جن ھن دُنيا ۾ چڱائي ڪئي تن ئي لاءِ (آخرت ۾) چڱائي آھي، ۽ الله جو مُلڪ ويڪرو آھي، رڳو صابرن کي سندن بدلو اَڻ کُٽ ڏبو.');
INSERT INTO "amroti" VALUES(39,11,'39:11','(اي پيغمبر کين) چؤ ته بيشڪ مون کي حُڪم ٿيو آھي ته الله لاءِ (پنھنجي) عبادت خالص ڪري رڳو اُن جي عبادت ڪريان.');
INSERT INTO "amroti" VALUES(39,12,'39:12','۽ مون کي حُڪم ٿيو آھي ته (سڀ کان) پھريون حُڪم مڃيندڙ (آءٌ) آھيان.');
INSERT INTO "amroti" VALUES(39,13,'39:13','چؤ ته جيڪڏھن آءٌ پنھنجي پالڻھار جي نافرماني ڪريان ته بيشڪ آءٌ وڏي ڏينھن جي عذاب کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(39,14,'39:14','چؤ ته پنھنجي عبادت الله لاءِ خالص ڪري فقط اُن جي عبادت ڪريان ٿو.');
INSERT INTO "amroti" VALUES(39,15,'39:15','پوءِ الله کانسواءِ جنھن کي (اوھين) گھرو تنھن کي پوڄيو، (کين) چؤ ته بيشڪ ڇيئي وارا اُھي آھن جن گمراھ ٿيڻ سبب پاڻ کي ۽ پنھنجي گھر وارن کي قيامت جي ڏينھن ڇيئي ۾ وڌو، خبردار اھوئي پڌرو ڇيئو آھي.');
INSERT INTO "amroti" VALUES(39,16,'39:16','سندن مٿان باھ جون ڇٽيون ۽ سندن ھيٺان به (اھڙا) وڇاڻا ھوندا، اِھو (عذاب) آھي جنھن کان الله پنھنجن ٻانھن کي ڊيڄاري ٿو، تنھنڪري اي منھنجا ٻانھؤ مون کان ڊڄو.');
INSERT INTO "amroti" VALUES(39,17,'39:17','۽ جن (ماڻھن) بتن جي پوڄا ڪرڻ کان پاسو ڪيو ۽ الله ڏانھن وريا تن لاءِ خوشخبري آھي.');
INSERT INTO "amroti" VALUES(39,18,'39:18','پوءِ (اي پيغمبر) منجھن اُنھن ٻانھن کي خوشخبري ڏي جيڪي ڳالھ ٻڌندا آھن. پوءِ اُن مان تمام چڱيءَ تي ھلندا آھن، اِھي (اُھي) آھن جن کي الله ھدايت ڪئي آھي ۽ اھي ئي ڌيان وارا آھن.');
INSERT INTO "amroti" VALUES(39,19,'39:19','ڀلا جنھن تي عذاب جو حُڪم ثابت ٿيو (سو بچي سگھندو ڇا؟)، تون باھ ۾ پيل کي ڇڏائي سگھندين ڇا؟');
INSERT INTO "amroti" VALUES(39,20,'39:20','پر جيڪي پنھنجي پالڻھار کان ڊنا تن لاءِ ماڙيون آھن اُنھن جي مٿان (ٻيون) ماڙيون اڏيليون آھن انھن جي ھيٺان واھيون وھنديون آھن، الله (اِھو) انجام ڪيو آھي، الله انجام نه ڦيرائيندو آھي.');
INSERT INTO "amroti" VALUES(39,21,'39:21','نه ڏٺو اٿيئي ڇا ته الله آسمان کان پاڻي وسايو پوءِ اُن کي زمين جي چشمن ۾ وھايائين وري اُن سان ڀاتين ڀاتين رنگ جي پوک ڄمائيندو آھي وري سُڪي ويندي آھي پوءِ اُن کي زرد ٿيل ڏسندو آھين وري اُن کي ذرا ذرا ڪندو آھي، بيشڪ اُن ۾ ڌيان وارن لاءِ نصيحت آھي.');
INSERT INTO "amroti" VALUES(39,22,'39:22','ڀلا جنھن جو سينو (دين) اسلام لاءِ الله کوليو آھي ۽ اُھو پنھنجي پالڻھار جي (نُور جي سوجھري) تي ھجي (سو سخت دل وارن ڪافرن جھڙو ٿيندو ڇا؟)، پوءِ اُنھن لاءِ ويل آھي جن جون دليون الله جي ياد ڪرڻ کان سخت (ٿيون) آھن، اِھي پڌريءَ گمراھي ۾ آھن.');
INSERT INTO "amroti" VALUES(39,23,'39:23','الله تمام چڱي ڳالھ (يعني) اھڙو ڪتاب نازل ڪيو جو ھڪ ٻئي جھڙا (سندس مضمون دُھرايل آھن اُن جي ٻڌڻ) کان اُنھن (ماڻھن) جون کَلون ڪانڊاربيون آھن جيڪي پنھنجي پالڻھار کان ڊڄندا آھن، وري سندين کَلون ۽ سندين دليون الله جي ياد ڪرڻ تي ڪنئريون ٿينديون آھن، اِھا الله جي ھدايت آھي جنھن کي گھري (تنھن کي) اُن سان واٽ ڏيکاري ٿو، ۽ جنھن کي الله گمراھ ڪري تنھن کي ڪو ھدايت ڪندڙ ڪونھي.');
INSERT INTO "amroti" VALUES(39,24,'39:24','ڀلا جيڪو قيامت جي ڏينھن پنھنجي مُنھن سان بڇڙي عذاب کي روڪي ٿو (سو ڇٽل جي برابر ٿيندو ڇا؟)، ۽ ظالمن کي چئبو ته جيڪي ڪندا ھيؤ (تنھنجي سزا) چڱو.');
INSERT INTO "amroti" VALUES(39,25,'39:25','جيڪي کانئن اڳ ھوا تن به ڪوڙ ڀانيو (ھو) پوءِ وٽن اُتان عذاب پھتو جتان ڄاڻندا ئي نه ھوا.');
INSERT INTO "amroti" VALUES(39,26,'39:26','پوءِ الله کين دنيا جي حياتيءَ ۾ خواري چکائي، ۽ بيشڪ آخرت جو عذاب (ته) تمام وڏو آھي، جيڪڏھن (اھا ڳالھ) ڄاڻن ھا (ته پاڻ کي عذاب کان بچائين ھا).');
INSERT INTO "amroti" VALUES(39,27,'39:27','۽ بيشڪ ماڻھن لاءِ ھن قرآن ۾ ھر قسم جا مثال بيان ڪياسون ته مانَ اُھي نصيحت وٺن.');
INSERT INTO "amroti" VALUES(39,28,'39:28','قرآن عربي (ٻوليءَ ۾) بي عيب (نازل ڪيوسون) ته مانَ اُھي ڊڄن.');
INSERT INTO "amroti" VALUES(39,29,'39:29','الله مثال بيان ٿو ڪري ھڪ ٻانھو جنھن ۾ (گھڻا) بد مزاج (مالڪ) ڀائيوار آھن ۽ (ٻيو) ٻانھو خلاصو ھڪڙي ماڻھوءَ جي ملڪيت آھي مثال ۾ اھي ٻئي (ٻانھا) برابر ٿيندا ڇا؟ سڀ ساراھ الله کي ئي جُڳائي، پر انھن مان گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(39,30,'39:30','(اي پيغمبر) بيشڪ تون (به) مرندين ۽ بيشڪ اُھي (به) مرندا.');
INSERT INTO "amroti" VALUES(39,31,'39:31','وري بيشڪ اوھين قيامت جي ڏينھن پنھنجي پالڻھار وٽ پاڻ ۾ جھڳڙو ڪندؤ.');
INSERT INTO "amroti" VALUES(39,32,'39:32','پوءِ اُن کان وڌيڪ ظالم ڪير آھي جنھن الله تي ڪوڙ ٺاھيو ۽ جڏھن سچو دين وٽس آيو (تڏھن) اُن کي ڪوڙ ڄاتائين، ڪافرن جي جاءِ دوزخ ۾ نه آھي ڇا؟');
INSERT INTO "amroti" VALUES(39,33,'39:33','۽ جنھن سچو دين آندو ۽ (جنھن) کيس سَچو ڄاتو اِھي ئي پرھيزگار آھن.');
INSERT INTO "amroti" VALUES(39,34,'39:34','جيڪي گھرندا سو انھن لاءِ سندن پالڻھار وٽ آھي، ڀلارن جو بدلو اِھو آھي.');
INSERT INTO "amroti" VALUES(39,35,'39:35','ته جيڪي بڇڙا ڪم ڪيائون سي الله کانئن ميٽي ۽ جيڪي چڱا ڪم ڪندا ھوا تن بابت سندن (چڱو) اُجورو کين بدلي ۾ ڏئي.');
INSERT INTO "amroti" VALUES(39,36,'39:36','الله پنھنجي ٻانھي (محمدؐ) کي ڪافي نه آھي ڇا؟ ۽ الله کانسواءِ جيڪي (ٻيا) آھن تن کان توکي ڊيڄاريندا آھن، ۽ جنھن کي الله ڀُلائي تنھن کي ڪو ھدايت ڪرڻ وارو نه آھي.');
INSERT INTO "amroti" VALUES(39,37,'39:37','۽ جنھن کي الله ھدايت ڪري تنھن کي ڪو ڀلائڻ وارو نه آھي، الله غالب بدلي وٺڻ وارو نه آھي ڇا؟');
INSERT INTO "amroti" VALUES(39,38,'39:38','(اي پيغمبر) جيڪڏھن کانئن پُڇين ته آسمانن ۽ زمين کي ڪنھن بڻايو آھي؟ ته ضرور چوندا ته الله، (پوءِ کين) چؤ ته الله کانسواءِ جن کي سڏيندا آھيو تن کي ڏسو (ته سھي) جيڪڏھن الله مون کي ڪا سختي (پھچائڻ) گھري ته اِھي اُن جي (پھچايل) سختيءَ جي لاھڻ وارا آھن ڇا؟ يا مون کي ڪو سُک (پھچائڻ) گھري ته اِھي (بُت) سندس (پھچايل) سُک جي بند ڪرڻ وارا آھن ڇا؟ (تون کين) چؤ ته مون کي الله ڪافي آھي، ڀروسي ڪندڙ مٿس (ئي) ڀروسو ڪندا آھن.');
INSERT INTO "amroti" VALUES(39,39,'39:39','(کين) چؤ ته اي منھنجي قوم اوھين پنھنجي جاءِ عمل ڪريو بيشڪ آءٌ (به پنھنجي جاءِ) عمل ڪندڙ آھيان، پوءِ سِگھوئي ڄاڻندؤ.');
INSERT INTO "amroti" VALUES(39,40,'39:40','ته ڪنھن تي (اُھو) عذاب اچي ٿو جو کيس خوار ڪندو ۽ مٿس ھميشه جو عذاب لھي ٿو.');
INSERT INTO "amroti" VALUES(39,41,'39:41','بيشڪ اسان توتي ڪتاب ماڻھن لاءِ سچ سان لاٿو، پوءِ جيڪو ھدايت وارو ٿيو سو خاص پنھنجي (فائدي) لاءِ، ۽ جيڪو گُمراھ ٿيو سو رڳو پنھنجي (نقصان) لاءِ گُمراھ ٿئي ٿو، ۽ تون مٿن ڪو ذمي وار نه آھين.');
INSERT INTO "amroti" VALUES(39,42,'39:42','الله ماڻھن جا رُوح سندن مرڻ جي مھل ڪڍندو آھي ۽ جيڪو نه مُئو آھي (تنھن جو روح) سندس ننڊ (جي حالت) ۾ (ڪڍندو آھي)، پوءِ جنھن تي موت جو حُڪم ڪيو اٿس تنھن کي جھليندو آھي ۽ اُن ٻئي کي ھڪ مُقرر مُدّت تائين ڇڏيندو آھي، بيشڪ ھن ۾ ماڻھن لاءِ نشانيون آھن جيڪي ڌيان ڪندا آھن.');
INSERT INTO "amroti" VALUES(39,43,'39:43','الله کانسواءِ ٻيا شفاعت ڪرڻ وارا ورتا اٿن ڇا؟ (کين) چؤ ته جيتوڻيڪ اُھي نڪي ڪجھ اختيار رکندا ھجن ۽ نڪي سمجھندا ھجن (ته به شفاعت ڪندا ڇا؟).');
INSERT INTO "amroti" VALUES(39,44,'39:44','(کين) چؤ ته سڀ شفاعت الله جي وس آھي، آسمانن ۽ زمين جي بادشاھي ان جي آھي، (اوھين) وري ڏانھس موٽائبؤ.');
INSERT INTO "amroti" VALUES(39,45,'39:45','۽ جڏھن اڪيلي الله (جي نالي) کي ياد ڪبو آھي (تڏھن) جيڪي آخرت کي نه مڃيندا آھن تن جون دليون ٽھنديون آھن، جڏھن الله کانسواءِ ٻـين کي ياد ڪبو آھي تڏھن اُنھيءَ ئي مھل اُھي سَرھا ٿيندا آھن.');
INSERT INTO "amroti" VALUES(39,46,'39:46','(اي پيغمبر) چؤ ته اي الله آسمانن ۽ زمين جا پيدا ڪندڙ ڳجھ ۽ ظاھر ڄاڻندڙ تون ئي پنھنجن ٻانھن جي وچ ۾ جنھن (ڳالھ) بابت (اُھي) جھڳڙو ڪندا رھيا (تنھن بابت) نبيرو ڪندين.');
INSERT INTO "amroti" VALUES(39,47,'39:47','۽ جيڪي زمين ۾ سڀئي مال آھي سو ۽ اُن جيترو ٻيو ساڻس جيڪڏھن ظالمن کي ھجي ته جيڪر اُھو قيامت جي ڏينھن بڇڙي عذاب کان (بچڻ لاءِ) بدلو ڏئي چڪن، ۽ الله جي طرف کان اھڙو (معاملو) انھن کي پڌرو ٿيندو جنھنجو گُمان (ئي) نه رکندا ھوا.');
INSERT INTO "amroti" VALUES(39,48,'39:48','۽ جيڪي بڇڙايون ڪمايون ھوائون سي انھن کي پڌريون ٿينديون ۽ جنھن (سزا) جون ٺـٺوليون ڪندا ھوا سا کين ويڙھي ويندي.');
INSERT INTO "amroti" VALUES(39,49,'39:49','پوءِ جڏھن ماڻھوءَ کي ڪا اوکائي پھچندي آھي (تڏھن) اسان کي سڏيندو آھي، وري جڏھن اُن کي پاڻ وٽان ڪا نعمت ڏيندا آھيون (تڏھن) چوندو آھي ته اُھا رڳو پنھنجي سمجھ سان (جا مون ۾ آھي) مون کي ڏني وئي آھي، (ايئن نه آھي) بلڪ اُھا (نعمت) پرک آھي پر اُنھن مان گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(39,50,'39:50','بيشڪ کانئن اڳين (به) اِھا (ڳالھ) چئي ھئي پوءِ جيڪي (ڪرتوت) ڪيائون ٿي تن کانئن ڪجھ به (مُصيبت) نه ٽاري.');
INSERT INTO "amroti" VALUES(39,51,'39:51','پوءِ جيڪي ڪمايائون تنھنجون بڇڙايون کين پھتيون، ۽ ھنن مان (به) جيڪي ظالم آھن تن جيڪي ڪمايائون تنھنجون بڇڙايون کين سِگھوئي پھچنديون ۽ اُھي عاجز ڪرڻ وارا نه آھن.');
INSERT INTO "amroti" VALUES(39,52,'39:52','نه ڄاتو اٿن ڇا ته الله جنھن لاءِ گھرندو آھي (تَنھنجو) رزق ڪُشادو ڪندو آھي ۽ (جنھن لاءِ گھرندو آھي تَنھنجو) گھٽ ڪندو آھي، بيشڪ ھن (ڪم) ۾ اُنھن ماڻھن لاءِ نشانيون آھن جيڪي ايمان آڻيندا آھن.');
INSERT INTO "amroti" VALUES(39,53,'39:53','(منھنجي طرفان اُنھن کي) چؤ ته اي منھنجا اُھي ٻانھؤ جن پاڻ تي ظلم ڪيو (اوھين) الله جي ٻاجھ کان نا اُميد نه ٿيو، ڇوته الله سڀ گناھ بخشيندو آھي، بيشڪ اُھوئي بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(39,54,'39:54','۽ پنھنجي پالڻھار ڏانھ ورو ۽ اُن کان اڳي سندس فرمانبردار ٿيو جو اوھان وٽ عذاب اچي (۽) وري مدد نه ڏنا ويندؤ.');
INSERT INTO "amroti" VALUES(39,55,'39:55','۽ اوھان جي پالڻھار کان اوھان ڏانھن جيڪا بلڪل چڱي شيء نازل ڪئي وئي آھي تنھن جي اُنھيءَ کان اڳ تابعداري ڪريو جو اوھان وٽ اوچتو عذاب اچي ۽ اوھين بي خبر ھجو.');
INSERT INTO "amroti" VALUES(39,56,'39:56','(انھيءَ ڪري خُداءِ ڏانھن مھاڙ ڪريو ۽ قرآن جي تابعداري ڪريو) ته متان پوءِ ڪوبه جيءُ چوي ته ھيءَ ارمان جو الله جي دين ۾ سُستي ڪيم ۽ بيشڪ (آءٌ ته) ٺـٺوليون ڪرڻ وارن مان ھوس.');
INSERT INTO "amroti" VALUES(39,57,'39:57','يا چوي ته جيڪڏھن الله مون کي ھدايت ڪري ھا ته ضرور پرھيزگارن مان ٿيان ھا.');
INSERT INTO "amroti" VALUES(39,58,'39:58','يا جڏھن عذاب ڏسي (تڏھن) چوي ته جي منھنجو ٻيو ڀيرو (دنيا ۾ وڃڻ) ٿئي ته جيڪر ڀلارن مان ٿيان.');
INSERT INTO "amroti" VALUES(39,59,'39:59','(چئبس ته) ھائو! بيشڪ تو وٽ منھنجون نشانيون آيون ھيون پوءِ اُنھن کي ڪُوڙو ڄاتئي ۽ وڏائي ڪيئي ۽ ڪافرن مان ٿيئن.');
INSERT INTO "amroti" VALUES(39,60,'39:60','۽ قيامت جي ڏينھن تون انھن کي ڏسندين جن الله تي ڪوڙ ٻَڌو ته سندن مُنھن ڪارا ٿيل ھوندا، وڏائي ڪندڙن جي جاءِ دوزخ ۾ نه آھي ڇا؟');
INSERT INTO "amroti" VALUES(39,61,'39:61','۽ جن پرھيزگاري ڪئي تن کي الله سندن ڇوٽڪاري جي ھنڌ ۾ (دوزخ کان) بچائي پھچائيندو، نڪي کين ڪا مدائي پھچندي ۽ نڪي اُھي غمگين رھندا.');
INSERT INTO "amroti" VALUES(39,62,'39:62','الله (ئي) سڀڪنھن شيء جو خلقيندڙ آھي، ۽ اُھو سڀڪنھن شي جي سنڀال ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(39,63,'39:63','آسمانن ۽ زمين جون ڪُنجيون انھي ئي وٽ آھن، ۽ جن الله جي نشانين جو انڪار ڪيو اُھي ئي ڇيئي وارا آھن.');
INSERT INTO "amroti" VALUES(39,64,'39:64','چؤ ته اي بي عقلؤ مون کي (ھي) ڏسيندا آھيو ڇا ته الله کانسواءِ ٻئي ڪنھن جي عبادت ڪريان.');
INSERT INTO "amroti" VALUES(39,65,'39:65','۽ (اي پيغمبر) بيشڪ توڏانھن وحي ڪيو ويو ۽ جيڪي توکان اڳ (ٻيا پيغمبر) ھوا تن (ھر ھڪ) ڏانھن (به) ته جيڪڏھن (الله سان ڪو) شريڪ مقرر ڪندين ته تنھنجا عمل ضرور ناس ٿيندا ۽ ضرور ڇيئي وارن مان ٿيندين.');
INSERT INTO "amroti" VALUES(39,66,'39:66','(نه!) بلڪ رڳو الله جي عبادت ڪر ۽ شُڪر ڪرڻ وارن مان ھُج.');
INSERT INTO "amroti" VALUES(39,67,'39:67','۽ (اُنھن) الله کي جئين سندس ڄاڻڻ جو حق آھي (تئين) نه سڃاتو، ۽ قيامت جي ڏينھن ساري زمين سندس مُٺ ۾ ھوندي ۽ آسمان سندس سڄي ھٿ ۾ ويڙھيل ھوندا، اُھو پاڪ آھي ۽ اُنھيءَ کان مٿاھون آھي جيڪي ساڻس شريڪ ٺھرائيندا آھن.');
INSERT INTO "amroti" VALUES(39,68,'39:68','۽ (پھريون ڀيرو) صُور ۾ ڦوڪيو ويندو پوءِ جيڪي آسمانن ۾ آھي ۽ جيڪو زمين ۾ آھي سو (سڀڪو) مرندو پر جنھن لاءِ الله گھريو (سو نه)، وري اُن ۾ ٻيو ڀيرو ڦوڪبو ته اُمالڪ کڙا ٿي (پيا) ڏسندا.');
INSERT INTO "amroti" VALUES(39,69,'39:69','۽ زمين پنھنجي پالڻھار جي نُور سان روشن ٿيندي ۽ (عملن جو) ڪتاب رکبو ۽ پيغمبرن ۽ شاھدن کي آڻبو ۽ سندن وچ ۾ حق سان نبيرو ڪبو ۽ اُنھن تي ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(39,70,'39:70','سڀڪنھن ماڻھوءَ جيڪي ڪمايو تنھنجو پورو بدلو کيس ڏبو ۽ جيڪي ڪندا آھن تنھن کي الله ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(39,71,'39:71','۽ ڪافرن کي ٽوليون ٽوليون ڪري دوزخ ڏانھن ھڪالبو، تان جو جڏھن اُن وٽ پھچندا (تڏھن) اُن جا دروازا کولبا ۽ اُن جا داروغا کين چوندا ته اوھان مان اوھان وٽ ڪي پيغمبر نه آيا ھوا ڇا؟ جو اوھان کي اوھان جي پالڻھار جون آيتون پڙھي ٻڌائين ۽ اوھان کي ھن ڏينھن جي پيش اچڻ کان ڊيڄارين، چوندا ته ھائو (آيا ھوا ڊيڄاريو به ھوائون) پر ڪافرن تي عذاب جو حُڪم ثابت ٿيو.');
INSERT INTO "amroti" VALUES(39,72,'39:72','(پوءِ انھن کي) چئبو ته دوزخ جي دروازن مان گھڙو منجھس سدائين رھندڙ آھيو، پوءِ وڏائي ڪندڙن جي جاءِ بڇڙي آھي.');
INSERT INTO "amroti" VALUES(39,73,'39:73','۽ جيڪي پنھنجي پالڻھار کان ڊنا سي ٽوليون ٽوليون بھشت ڏانھن روانا ڪبا، تانجو جڏھن اُن وقت ايندا ۽ اُن جا دروازا کولبا ۽ اُن جا دواروغا کين چوندا ته اوھان تي سلام ھجي اوھين (ھاڻي) خوش رھو پوءِ منجھس ھميشه رھڻ لاءِ گھڙو.');
INSERT INTO "amroti" VALUES(39,74,'39:74','۽ چوندا ته سڀ ساراھ انھيءَ الله کي جُڳائي جنھن پنھنجو انجام اسان سان سچو ڪيو ۽ اسان کي (ھن) زمين جو وارث ڪيائين بھشت مان جتي وڻندو اٿون اُتي جڳه وٺون ٿا، پوءِ عمل ڪندڙن جو اَجر (ڪھڙونه) چڱو آھي.');
INSERT INTO "amroti" VALUES(39,75,'39:75','۽ ملائڪن کي عرش جي چوڌاري وڪوڙيندڙ ڏسندين پنھنجي پالڻھار جي ساراھ سان گڏ پاڪائي بيان ڪندا آھن، ۽ اُنھن جي وچ ۾ انصاف جو حُڪم ڪبو ۽ (آخر ۾) چيو ويندو ته سڀ ساراھ جھانن جي پالڻھار الله کي جُڳائي.');
INSERT INTO "amroti" VALUES(40,1,'40:1','حٰمٓ.');
INSERT INTO "amroti" VALUES(40,2,'40:2','(ھن) ڪتاب جو ھيٺ لاھڻ الله غالب ڄاڻندڙ وٽان آھي.');
INSERT INTO "amroti" VALUES(40,3,'40:3','جو گناھ بخشيندڙ ۽ توبه قبول ڪندڙ سخت سزا ڏيندڙ سگھ وارو آھي، اُن کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي، ڏانھس ورڻو آھي.');
INSERT INTO "amroti" VALUES(40,4,'40:4','ڪافرن کانسواءِ الله جي آيتن بابت (ٻيو) ڪوبه تڪرار نه ڪندو آھي پوءِ شھرن ۾ (واپار لاءِ) سندن اچ وڃ توکي نه ٺڳي.');
INSERT INTO "amroti" VALUES(40,5,'40:5','کانئن اڳ نُوح جي قوم ۽ انھن پڄاڻا ٻـين (ڪيترين ئي) ٽولين ڪُوڙ ڄاتو، ۽ سڀڪنھن ٽوليءَ پنھنجن پيغمبر جو قصد ڪيو ته کيس پڪڙين ۽ اجاين (ڳالھين) سان جھيڙو ڪندا رھيا ته اُن سان سچيءَ ڳالھ کي ناس ڪن پوءِ کين ورتم، پوءِ منھنجي سزا (ڏيڻ) ڪيئن ھئي.');
INSERT INTO "amroti" VALUES(40,6,'40:6','۽ اھڙي طرح تنھنجي پالڻھار جو حُڪم ڪافرن تي ثابت ٿيو آھي ته اُھي دوزخي آھن.');
INSERT INTO "amroti" VALUES(40,7,'40:7','جيڪي (ملائڪ) عرش کي کڻندا آھن ۽ جيڪي اُن جي چوڌاري آھن سي پنھنجي پالڻھار جي پاڪائي ساراھ سان گڏ بيان ڪندا آھن ۽ مٿس ايمان آڻيندا آھن ۽ مؤمن لاءِ بخشش گھرندا آھن، (چوندا آھن ته) اي اسان جا پالڻھار سڀڪنھن شيء کي پنھنجي ٻاجھ ۽ علم ۾ وڪوڙيو اٿيئي تنھنڪري جن توبه ڪئي ۽ تنھنجي واٽ جي تابعداري ڪئي تن کي بخش ۽ کين دوزخ جي عذاب کان بچاءِ.');
INSERT INTO "amroti" VALUES(40,8,'40:8','اي اسان جا پالڻھار کين اُن ھميشه رھڻ وارن باغن ۾ داخل ڪر جن جو ساڻن وعدو ڪيو اٿيئي ۽ جيڪي سندن پين ڏاڏن ۽ سندن زالن ۽ سندن اولاد مان سڌريل ھجي تنھن کي به (اُنھن باغن ۾ داخل ڪر)، بيشڪ تون ئي غالب حڪمت وارو آھين.');
INSERT INTO "amroti" VALUES(40,9,'40:9','۽ کين سختين کان بچاءِ، ۽ جنھن کي اُن ڏينھن ۾ سختين کان بچايئي تنھن تي بيشڪ رحم ڪيئي، ۽ اِھو (بچائڻ) وڏي مُراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(40,10,'40:10','بيشڪ جن ڪُفر ڪيو تن کي سڏبو (۽ چئبو) ته الله جو (اوھان تي) ڏمرجڻ اوھان جي پاڻ تي (اڄ جي) ڏمرجڻ کان تمام وڏو آھي جڏھن جو اوھان کي (دنيا ۾) ايمان ڏانھن سڏبو ھو ۽ نه مڃيندا ھئو.');
INSERT INTO "amroti" VALUES(40,11,'40:11','چوندا ته اي اسانجا پالڻھار اسان کي ٻه ڀيرا ماريئي ۽ اسان کي ٻه ڀيرا جياريئي تنھنڪري (ھاڻي) پنھنجن گناھن کي (پاڻ) مڃيوسون پوءِ (ھاڻي) نڪرڻ جي ڪا واھ (ڪٿي) آھي؟');
INSERT INTO "amroti" VALUES(40,12,'40:12','(چئبن) اِھو (عذاب اوھان کي) انھيءَ ڪري آھي جو جڏھن (خاص) اڪيلي الله کي سڏبو ھو (تڏھن) اوھين انڪار ڪندا ھئو، ۽ جيڪڏھن ساڻس شريڪ ٺھرايو ويندو ھو ته (ان ڳالھ کي) مڃيندا ھئو، پوءِ (اڄ) تمام مٿاھين وڏي الله جو حڪم آھي.');
INSERT INTO "amroti" VALUES(40,13,'40:13','(الله) اُھو آھي جيڪو اوھان کي پنھنجون نشانيون ڏيکاريندو آھي ۽ آسمان مان اوھان لاءِ روزي نازل ڪندو آھي، ۽ نصيحت رڳو اُھو وٺندو جيڪو (خدا ڏانھن) موٽندو.');
INSERT INTO "amroti" VALUES(40,14,'40:14','پوءِ الله لاءِ پنھنجي عبادت کي خالص ڪري اُن کي سڏيو جيتوڻيڪ ڪافر بڇان ڀانئين.');
INSERT INTO "amroti" VALUES(40,15,'40:15','(اُھو) مرتبن کي وڏي ڪرڻ وارو ۽ عرش جو صاحب آھي، پنھنجن ٻانھن مان جنھن تي گھرندو آھي (تنھن تي) پنھنجي حڪم سان وحي موڪليندو آھي ته (اُھو ٻانھو ماڻھن کي الله جي) ملڻ جي ڏينھن کان ڊيڄاري.');
INSERT INTO "amroti" VALUES(40,16,'40:16','جنھن ڏينھن اُھي (قبرن مان) ٻاھر نڪرندا، انھن جي ڪابه شيء الله کان ڳُجھي نه رھندي، (الله فرمائيندو ته) اڄ ڪنھن جي بادشاھي آھي، (وري پاڻ چوندو) خاص اڪيلي غالب الله جي (بادشاھي) آھي.');
INSERT INTO "amroti" VALUES(40,17,'40:17','اڄ سڀڪنھن جِيءَ کي اُنھي جو بدلو ڏبو جيڪو ڪمايو اٿس، اڄ ڪو ظلم ڪونھي، بيشڪ الله جلد حساب وٺڻ وارو آھي.');
INSERT INTO "amroti" VALUES(40,18,'40:18','۽ (اي پيغمبر) کين قيامت جي ڏينھن (جي اُنھيءَ مھل) کان ڊيڄار جنھن مھل دليون نِڙ گھٽن کي ويجھو ٿينديون (اُھي) ڏک ڀريل ھوندا، (اُن ڏينھن) ظالمن جو ڪو دوست ۽ نه ڪو پارت ڪندڙ ھوندو جنھن جي ڳالھ مڃجي.');
INSERT INTO "amroti" VALUES(40,19,'40:19','اکين جي خيانت (واري نگاھ) ۽ جيڪي سينا لڪائيندا آھن سو (الله) ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(40,20,'40:20','۽ الله انصاف سان حُڪم ڪندو آھي، ۽ (منڪر) الله کانسواءِ جن کي سڏيندا آھن سي ڪجھ به حُڪم نه ڪندا آھن، بيشڪ الله ئي ٻڌندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(40,21,'40:21','مُلڪ ۾ نه گھميا آھن ڇا؟ ته (نھاري) ڏسن ته انھن جي پڇاڙي ڪھڙي طرح ٿي جيڪي کانئن اڳ ھوا، اُھي کائن سگھ ۾ زمين تي نشانين (يعني محلاتن جوڙڻ) ۾ تمام ڏاڍا ھوا پوءِ الله سندن گناھن سببان کين پڪڙيو، ۽ الله (جي عذاب) کان اُنھن کي ڪو بچائيندڙ نه ھو.');
INSERT INTO "amroti" VALUES(40,22,'40:22','اِھا (سزا) ھن ڪري ھين جو سندن پيغمبر (پڌرن) مُعجزن سان وٽن آيا ٿي پوءِ انڪار ڪيائون تنھنڪري الله کين پڪڙيو، بيشڪ اُھو سگھارو سخت سزا ڏيڻ وارو آھي.');
INSERT INTO "amroti" VALUES(40,23,'40:23','۽ بيشڪ موسىٰ کي پنھنجن مُعجزن ۽ پڌريءَ حُجّت سان موڪليوسون.');
INSERT INTO "amroti" VALUES(40,24,'40:24','فرعون ۽ ھامان ۽ قارون ڏانھن پوءِ چيائون ته ڪُوڙو جادوگر آھي.');
INSERT INTO "amroti" VALUES(40,25,'40:25','پوءِ جنھن مھل اسان وٽان ھنن وٽ سچي (پيغام) سان آيو (تنھن مھل) چيائون ته جن مٿس ايمان آندو آھي تن جا پُٽ ڪُھو ۽ سندن ڌيئرون جيئرون ڇڏيو، ۽ ڪافرن جو فريب ته رڳو غلطيءَ ۾ آھي.');
INSERT INTO "amroti" VALUES(40,26,'40:26','۽ فرعون (دربار وارن کي) چيو ته مون کي ڇڏيو ته موسىٰ کي ڪُھان ۽ ڀلي ته پنھنجي پالڻھار کي سڏي، بيشڪ آءٌ ڊڄان ٿو ته متان اوھان جو دين مٽائي يا مُلڪ ۾ فساد پيدا ڪري.');
INSERT INTO "amroti" VALUES(40,27,'40:27','۽ موسىٰ چيو ته بيشڪ مون سڀڪنھن ھٺيلي (جي لچائيءَ) کان جو حساب جي ڏينھن کي نه مڃيندو آھي پنھنجي پالڻھار ۽ اوھان جي پالڻھار جي پناھ ورتي.');
INSERT INTO "amroti" VALUES(40,28,'40:28','۽ فرعون جي مائٽن مان ھڪ مؤمن مڙس جو پنھنجو ايمان لڪائيندو ھو (تنھن) چيو ته اھڙي مڙس کي ڪھو ٿا ڇا جو چوي ٿو ته منھنجو پالڻھار الله آھي ۽ بيشڪ اوھان وٽ اوھان جي پالڻھار کان (چٽا) مُعجزا آندا اٿس، جيڪڏھن کڻي ڪوڙو ھجي ته سندس ڪوڙ (جو وبال) مٿس آھي، ۽ جيڪڏھن سچو ھوندو ته جيڪو وعدو اوھان سان ڪري ٿو تنھن مان ڪجھ اوھان کي ضرور پھچندو، ڇوته جيڪو حد کان لنگھندڙ ڪوڙو ھوندو تنھن کي الله سڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(40,29,'40:29','اي منھنجي قوم اڄ اوھان جي بادشاھي آھي مُلڪ ۾ (اوھين) غالب آھيو، جيڪڏھن (عذاب) اسان وٽ اچي ته الله جي عذاب کان (بچڻ لاءِ) اسان کي ڪير واھر ڏيندو؟ فرعون چيو ته جيڪا (چڱي مصلحت) ڏسان ٿو تنھن کانسواءِ (ٻي) اوھان کي نه ٿو ڏيکاريان ۽ اوھان کي سڌي رستي کانسواءِ (ٻيو رستو) نه ٿو ڏيکاريان.');
INSERT INTO "amroti" VALUES(40,30,'40:30','۽ جنھن ايمان آندو ھو تنھن چيو ته اي منھنجي قوم بيشڪ آءٌ اوھان تي اڳين ٽولين جھڙي (عذاب جي) ڏينھن (اچڻ) کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(40,31,'40:31','جھڙو حال نوح جي قوم ۽ عاد ۽ ثمود ۽ جيڪي کائن پوءِ ھوا تن جو (ٿيو ھو تھڙو متان ٿئي)، ۽ الله ٻانھن تي ظلم ڪرڻ نه گھرندو آھي.');
INSERT INTO "amroti" VALUES(40,32,'40:32','۽ اي منھنجي قوم بيشڪ آءٌ اوھان تي ھڪ ٻئي کي سڏڻ جي (انھيءَ) ڏينھن (اچڻ) کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(40,33,'40:33','جنھن ڏينھن اوھين پٺيرا ٿي ڀڄندؤ، الله کانسواءِ (ٻيو) اوھان کي ڪو بچائڻ وارو نه ھوندو، ۽ جنھن کي الله ڀُلائي ڇڏي تنھن کي ڪو سِڌو رستو ڏيکارڻ وارو ڪونھي.');
INSERT INTO "amroti" VALUES(40,34,'40:34','۽ بيشڪ ھن کان اڳ اوھان وٽ يوسف مُعجزن سان آيو ھو پوءِ جيڪي اوھان وٽ آندائين تنھن کان (اوھين) ھميشه شڪ ۾ ھُئؤ، تان جو جڏھن مُئو (تڏھن) چيوَ ته اُن کان پوءِ (ٻيو) ڪو پيغمبر الله نه موڪليندو، اھڙي طرح جيڪو حد کان لنگھندڙ شڪ ڪندڙ آھي تنھن کي الله ڀُلائيندو آھي.');
INSERT INTO "amroti" VALUES(40,35,'40:35','اُنھن کي (ڀُلائيندو آھي) جيڪي الله جي نشانين بابت اھڙيءَ حُجّت ڌاران جا وٽن آئي ھجي جھڳڙو ڪندا آھن، (اھو سندن جھڳڙو ڪرڻ) الله وٽ ۽ مُؤمنن وٽ وڏي غضب جھڙي ڳالھ آھي، اھڙي طرح الله سڀڪنھن وڏائي ڪندڙ ھٺيلي جي دل تي مُھر ھڻندو آھي.');
INSERT INTO "amroti" VALUES(40,36,'40:36','۽ فرعون چيو ته اي ھامان مون لاءِ ھڪ ماڙي بڻاءِ ته مانَ (انھن) واٽن سان پھچان.');
INSERT INTO "amroti" VALUES(40,37,'40:37','آسمانن جي واٽن کي ته مُوسىٰ جي معبُود جي سڌ لھان ۽ بيشڪ آءٌ کيس ڪوڙو ڀائيندو آھيان، ۽ اھڙيءَ طرح فرعون کي سندس بڇڙو ڪم چڱو ڪري ڏيکاريو ويو ۽ سڌيءَ واٽ کان جھليو ويو، ۽ فرعون جو مڪر رُڳو خرابيءَ ۾ ھو.');
INSERT INTO "amroti" VALUES(40,38,'40:38','۽ جنھن ايمان آندو ھو تنھن چيو ته اي منھنجي قوم منھنجي تابعداري ڪريو ته (آءٌ) اوھان کي سڌي رستي لايان.');
INSERT INTO "amroti" VALUES(40,39,'40:39','اي منھنجي قوم ھيءَ دنيا جي حياتي (ٿوري) وٿ کانسواءِ (ٻيو) ڪي نه آھي، ۽ بيشڪ آخرت ئي سدائين رھڻ جو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(40,40,'40:40','جنھن بد ڪم ڪيو تنھن کي رڳو اُھڙو بدلو ڏبو، ۽ جنھن چڱو ڪم ڪيو خواہ مڙس ھجي خواہ زال ۽ اُھو مؤمن به ھجي ته اُھي بھشت ۾ گھڙندا منجھس اڻ ڳڻي روزي ڏبين.');
INSERT INTO "amroti" VALUES(40,41,'40:41','اي منھنجي قوم مون کي ڇا (ٿيو) آھي جو (آءٌ) اوھان کي ڇوٽڪاري ڏانھن سڏيان ٿو ۽ (اوھين) مون کي باھ ڏانھن سڏيو ٿا.');
INSERT INTO "amroti" VALUES(40,42,'40:42','(اوھين) مون کي سڏيو ٿا ته الله جو انڪار ڪريان ۽ ساڻس اھڙي شيء شريڪ ٺھرايان جنھن (جي حقيقت) جي مون کي ڄاڻپ نه آھي، ۽ آءٌ اوھان کي غالب بخشڻھار ڏانھن سڏيان ٿو.');
INSERT INTO "amroti" VALUES(40,43,'40:43','بلاشڪ اوھين مون کي اُنھيءَ ڏانھن سڏيو ٿا جنھن کي دنيا ۽ آخرت ۾ سڏڻ جو ڪو حق نه آھي ۽ بيشڪ اسان جو موٽڻ الله ڏانھن آھي ۽ بيشڪ حد کان لنگھندڙ ئي دوزخي آھن.');
INSERT INTO "amroti" VALUES(40,44,'40:44','پوءِ جيڪي اوھان کي چوان ٿو سو سگھو ياد ڪندؤ، ۽ (آءٌ) پنھنجو ڪم الله کي سونپيان ٿو، ڇو ته الله ٻانھن (جي حال) کي ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(40,45,'40:45','پوءِ جيڪي ڦند سٽيائون تن جي بڇڙائيءَ کان الله کيس بچايو ۽ فرعون جي ماڻھن کي بڇڙي عذاب گھيريو.');
INSERT INTO "amroti" VALUES(40,46,'40:46','جا باھ آھي جنھن تي صبح ۽ سانجھيءَ جو کين حاضر ڪبو آھي، ۽ جنھن ڏينھن قيامت قائم ٿيندي، (تنھن ڏينھن حُڪم ڪبو ته) فرعونين کي سخت عذاب ۾ گھيڙيو.');
INSERT INTO "amroti" VALUES(40,47,'40:47','۽ (ياد ڪر) جڏھن باھ ۾ ھڪ ٻئي سان جھڳڙو ڪندا (تڏھن) ھيڻا ھٺيلن کي چوندا ته بيشڪ اسين اوھان جا تابعدار ھواسون تنھنڪري باھ (جي عذاب) مان ڪو حصّو اوھين اسان کان ٽارڻ وارا آھيو (يا نه)؟');
INSERT INTO "amroti" VALUES(40,48,'40:48','ھٺيلا چوندا ته بيشڪ اسين (پاڻ) سڀ منجھس (پيل) آھيون بيشڪ الله ٻانھن جي وچ ۾ نبيرو ڪري چڪو آھي.');
INSERT INTO "amroti" VALUES(40,49,'40:49','۽ دوزخي دوزخ جي داروغن کي چوندا ته اوھين پنھنجي پالڻھار کي عرض ڪريو ته اسان کان ھڪڙو ڏينھن ڪجھ عذاب ھلڪو ڪري.');
INSERT INTO "amroti" VALUES(40,50,'40:50','(داروغا) چوندا ته اوھان جا پيغمبر (پڌرن) معجزن سان اوھان وٽ نه ايندا ھوا ڇا؟ چوندا ته ھائو (ايندا ھوا)، (داروغا) چوندا ته پوءِ (اوھين) پاڻ پڪاريو، ۽ ڪافرن جو پڪارڻ رڳو گمراھي ۾ آھي.');
INSERT INTO "amroti" VALUES(40,51,'40:51','بيشڪ اسين پنھنجن پيغمبرن جي ۽ مؤمنن جي دنيا جي حياتيءَ ۾ ضرور مدد ڪنداسون ۽ جنھن ڏينھن شاھد کڙا ٿيندا (تنھن ڏينھن به مدد ڪنداسون).');
INSERT INTO "amroti" VALUES(40,52,'40:52','جنھن ڏينھن ظالمن کي سندن بھانو نفعو نه ڏيندو ۽ کين لعنت آھي ۽ انھن لاءِ (اُتي) بُڇڙي جاءِ آھي.');
INSERT INTO "amroti" VALUES(40,53,'40:53','۽ بيشڪ مُوسىٰ کي ھدايت ڏني سون ۽ بني اسرائيلن کي ڪتاب جو وارث ڪيوسون.');
INSERT INTO "amroti" VALUES(40,54,'40:54','جو ڌيان وارن جي سمجھائڻ ۽ نصيحت ڏيڻ لاءِ آھي.');
INSERT INTO "amroti" VALUES(40,55,'40:55','پوءِ (اي پيغمبر) صبر ڪر ڇوته الله جو وعدو سچّو آھي ۽ پنھنجي گناھ جي بخشش گھر ۽ صبح سانجھيءَ جو پنھنجي پالڻھار جي ساراھ سان گڏ پاڪائي بيان ڪر.');
INSERT INTO "amroti" VALUES(40,56,'40:56','بيشڪ جيڪي الله جي آيتن بابت (اُنھيءَ) حُجّت ڌاران جا وٽن آئي ھجي جھڳڙو ڪندا آھن تن جي سينن ۾ وڏائي (جي ارادي) کانسواءِ (ٻيو) ڪي نه آھي جنھن کي اُھي پُھچڻ وارا نه آھن، پوءِ الله جي پناھ گھر، بيشڪ اُھو ٻڌندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(40,57,'40:57','بيشڪ آسمانن ۽ زمين جي بڻاوت ماڻھن جي بڻاوت کان تمام وڏي آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(40,58,'40:58','۽ انڌو ۽ سڄو (پاڻ ۾) برابر نه آھي، ۽ (نڪي) جن ايمان آندو ۽ چڱا ڪم ڪيا (سي) نڪي مدائي ڪرڻ وارو (برابر آھن)، اوھين ٿوري نصيحت وٺندا آھيو.');
INSERT INTO "amroti" VALUES(40,59,'40:59','بيشڪ قيامت ضرور اچڻي آھي منجھس ڪو شڪ نه آھي، پر گھڻا ماڻھو ويساھ نه رکندا آھن.');
INSERT INTO "amroti" VALUES(40,60,'40:60','۽ اوھان جو پالڻھار فرمائي ٿو ته اوھين مون کان دُعا گھرو ته اوھان جي دُعا قبول ڪريان، بيشڪ جيڪي منھنجي عبادت کان وڏائي ڪندا آھن سي خوار ٿي دوزخ ۾ سگھوئي گھڙندا.');
INSERT INTO "amroti" VALUES(40,61,'40:61','الله اُھو آھي جنھن اوھان لاءِ رات کي پيدا ڪيو ته منجھس آرام وٺو ۽ ڏينھن کي اوھان جي (ھڪ ٻئي کي) ڏسڻ لاءِ (پيدا ڪيو) بيشڪ الله ماڻھن تي فضل (ڪرڻ) وارو آھي پر گھڻا ماڻھو شڪر نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(40,62,'40:62','اھو الله اوھان جو پالڻھار ھر شيء کي خلقيندڙ آھي، اُن کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي، پوءِ اوھين ڪيڏانھن ڦيرايا ويندا آھيو.');
INSERT INTO "amroti" VALUES(40,63,'40:63','اھڙي طرح اُھي (سئين واٽ کان) ڀُلائبا آھن جن الله جي آيتن جو انڪار ٿي ڪيو.');
INSERT INTO "amroti" VALUES(40,64,'40:64','الله اُھو آھي جنھن اوھان لاءِ زمين کي آرام جو ھنڌ ۽ آسمان کي ڇت بڻايو ۽ اوھان جي شڪل ٺاھيائين پوءِ اوھان جون شڪليون تمام چڱيون بڻايائين ۽ اوھان کي سٺين سٺين شين مان روزي ڏنائين، اھو الله اوھان جو پالڻھار آھي، پوءِ جھانن جو پالڻھار وڏي برڪت وارو آھي.');
INSERT INTO "amroti" VALUES(40,65,'40:65','اُھو (سدائين) جيئرو آھي اُن کانسواءِ ڪو عبادت جو لائق نه آھي تنھنڪري اُن لاءِ (پنھنجي) عبادت کي خالص ڪري کيس سڏيو، سڀ ساراھ جھانن جي پالڻھار الله کي جڳائي.');
INSERT INTO "amroti" VALUES(40,66,'40:66','(اي پيغمبر کين) چؤ ته الله کانسواءِ جن کي (اوھين) سڏيندا آھيو تن جي پوڄڻ کان جڏھن منھنجي پالڻھار وٽان مون وٽ (صاف) نشانيون آيون (تڏھن اُنھيءَ وقت) بيشڪ آءٌ جھليو ويو آھيان ۽ مون کي حڪم ڪيو ويو آھي ته جھانن جي پالڻھار جو فرمانبردار ھجان.');
INSERT INTO "amroti" VALUES(40,67,'40:67','(الله) اُھو آھي جنھن اوھان کي مٽيءَ مان بڻايو وري نُطفي مان وري رت جي دڳ مان وري اوھان کي ٻار ڪري ڪڍي ٿو وري (ڇڏي ٿو) ته (اوھين) پنھنجي پوريءَ سگھ کي پُھچو وري (ڇڏي ٿو) ته ٻُڍڙا ٿيو، ۽ اوھان مان ڪنھن کي (اِن کان) اڳ ماربو آھي ۽ (ڇڏي ٿو) ته مُقرّر مدت کي پھچو ۽ مانَ اوھين سمجھو.');
INSERT INTO "amroti" VALUES(40,68,'40:68','(الله) اُھو آھي جيڪو جياريندو آھي ۽ ماريندو آھي، پوءِ جڏھن ڪنھن ڪم جو حُڪم ڪندو آھي تڏھن اُن کي رڳو چوندو آھي ته ٿيءُ ته ٿي پوندو آھي.');
INSERT INTO "amroti" VALUES(40,69,'40:69','(اي پيغمبر) انھن ڏانھن نه ڏٺو اٿيئي ڇا جيڪي الله جي آيتن بابت جھڳڙو ڪندا آھن؟ (اُھي) ڪيڏانھن ڦيرايا ويندا آھن.');
INSERT INTO "amroti" VALUES(40,70,'40:70','جن ڪتاب کي ڪُوڙ ڄاتو ۽ جنھن (شي) سان پنھنجن پيغمبرن کي موڪليوسون تنھن کي (به) سگھوئي ڄاڻندا.');
INSERT INTO "amroti" VALUES(40,71,'40:71','جڏھن سندن ڳِچين ۾ ڳَٽ ۽ زنجير (ھوندا)، اُھي گِھلبا.');
INSERT INTO "amroti" VALUES(40,72,'40:72','ٽھڪندڙ پاڻيءَ ۾، وري باھ ۾ ٽچڪايا ويندا.');
INSERT INTO "amroti" VALUES(40,73,'40:73','وري کين چئبو ته جيڪي اوھين شريڪ ٺھرائيندا ھيؤ سي ڪِٿي آھن؟');
INSERT INTO "amroti" VALUES(40,74,'40:74','الله ڌاران (ٻيا) (اُھي) چوندا ته اسان (جي نظر) کان گُم ٿي ويا بلڪ ڪنھن شي کي اڳي نه پوڄيندا ھواسون، اھڙيءَ طرح الله ڪافرن کي گُمراھ ڪندو آھي.');
INSERT INTO "amroti" VALUES(40,75,'40:75','اِھا (سزا) اِنھيءَ سببان آھي جو اوھين زمين ۾ ناحق خوشيءَ (سبب آڪڙ) ڪندا ھئو ۽ ھن سببان (به) جو اوھين لاڏ ڪندا ھئو.');
INSERT INTO "amroti" VALUES(40,76,'40:76','(ھاڻي) دوزخ جي دروازن مان گھڙو منجھس سدائين رھندڙ (آھيو)، پوءِ (ڏسو ته دوزخ) ھٺيلن جي جاءِ بڇڙي آھي.');
INSERT INTO "amroti" VALUES(40,77,'40:77','پوءِ (اي پيغمبر تون) صبر ڪر بيشڪ الله جو وعدو سچو آھي، تنھنڪري جيڪڏھن کين جيڪو انجام ڏيون ٿا تنھن مان ڪجھ توکي (دُنيا ۾) ڏيکاريون يا (عذاب جي ظاھر ٿيڻ کان اڳ) توکي ماريون ته (نيٺ) اسان ڏانھن موٽائبا.');
INSERT INTO "amroti" VALUES(40,78,'40:78','۽ بيشڪ توکان اڳ ڪيترائي پيغمبر موڪلياسون منجھائن ڪنھن جو احوال توکي بيان ڪيوسون ۽ منجھائن ڪنھن جو احوال توکي بيان نه ڪيوسون، ۽ ڪنھن به پيغمبر کي الله جي حُڪم کانسواءِ ڪو (به) مُعجزو آڻڻو نه ھو، پوءِ جڏھن الله جو حُڪم آيو تڏھن انصاف سان نبيرو ڪيو ويندو ۽ اُتي ڪوُڙن ڇيئو پاتو.');
INSERT INTO "amroti" VALUES(40,79,'40:79','الله اُھو آھي جنھن اوھان لاءِ ڍور پيدا ڪيا ته اُنھن مان ڪن تي چڙھو ۽ منجھائن ڪي کائيندا آھيو.');
INSERT INTO "amroti" VALUES(40,80,'40:80','۽ منجھن اوھان لاءِ (گھڻا) فائدا آھن ۽ ته مٿن چڙھي اُنھي مطلب کي پھچو جو اوھان جي دلين ۾ آھي ۽ مٿن ۽ ٻيڙين تي اوھان کي چاڙھبو آھي.');
INSERT INTO "amroti" VALUES(40,81,'40:81','۽ (الله) اوھان کي پنھنجون نشانيون ڏيکاريندو آھي، پوءِ الله جي ڪھڙين (ڪھڙين) نشانين کان انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(40,82,'40:82','پوءِ زمين ۾ نه گھميا آھن ڇا؟ جو نھارين ته جيڪي کائن اڳ ھوا تن جي پڇاڙي ڪيئن ٿي، (جو) کائن گھڻا ھوا ۽ سگھ ۾ ۽ زمين تي نشانين ڪري (جي ڇڏي ويا) تمام ڏاڍا ھوا پوءِ جيڪي (ڪرتوت) ڪمايائون تن کائن ڪجھ به نه ٽاريو.');
INSERT INTO "amroti" VALUES(40,83,'40:83','پوءِ جنھن مھل سندن پيغمبر مُعجزن سان وٽن آيا (تنھن مھل) جيڪو (دنيا ھلائڻ جو) عِلم وٽن ھو تنھن سببان خوش ٿيا ۽ جنھن (ڳالھ) تي ٺـٺوليون ڪندا ھوا تنھن (جي بڇڙائي) کين ويڙھيو.');
INSERT INTO "amroti" VALUES(40,84,'40:84','پوءِ جنھن مھل اسان جو عذاب ڏٺائون (تنھن مھل) چيائون ته اڪيلي الله تي ايمان آندوسون ۽ جنھن شي کي (ساڻس) شريڪ مقرر ڪندا ھواسون تنھنجا منڪر ٿياسون.');
INSERT INTO "amroti" VALUES(40,85,'40:85','پوءِ جڏھن اسان جو عذاب ڏٺائون (تڏھن) سندن ايمان آڻڻ کين فائدو ڪڏھن نه ڏنو، (اھو) الله جو دستور آھي جو سندس ٻانھن ۾ ھليو ايندو آھي، اُن ھنڌ ڪافر ٽوٽي وارا ٿيا.');
INSERT INTO "amroti" VALUES(41,1,'41:1','حٰمٓ.');
INSERT INTO "amroti" VALUES(41,2,'41:2','(ھن ڪتاب جو الله) ٻاجھاري مھربان وٽان نازل ٿيڻ آھي.');
INSERT INTO "amroti" VALUES(41,3,'41:3','(ھيءُ اھڙو) ڪتاب جو سندس آيتون کولي کولي بيان ڪيون ويون آھن قُرآن عربيءَ ۾ سمجھ وارن ماڻھن لاءِ آھي.');
INSERT INTO "amroti" VALUES(41,4,'41:4','جو خوشخبري ڏيندڙ ڊيڄاريندڙ آھي، پوءِ گھڻن (ماڻھن) مُنھن موڙيو تنھنڪري اُھي (اُن کي) ٻڌندائي نه آھن.');
INSERT INTO "amroti" VALUES(41,5,'41:5','۽ چوندا آھن ته جنھن ڏانھن اسان کي سڏيندو آھين تنھن کان اسان جون دليون پردي ۾ آھن ۽ اسان جي ڪَنن ۾ گھٻرائي آھي ۽ اسان جي ۽ اوھان جي وچ ۾ اوٽ آھي تنھنڪري (تون پنھنجيءَ طرح) ڪم ڪر بيشڪ اسين (پنھنجيءَ طرح) ڪم ڪرڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(41,6,'41:6','(اي پيغمبر کين) چؤ ته آءٌ (به) اوھان ئي جھڙو ماڻھو آھيان مگر مون ڏانھن وحي ڪيو ويندو آھي ته اوھان جو معبُود ھڪ خدا آھي تنھنڪري ڏانھس سڌا متوجه ٿيو ۽ کانئس بخشش گھرو، ۽ (اُنھن) مشرڪن لاءِ ويل آھي.');
INSERT INTO "amroti" VALUES(41,7,'41:7','جيڪي زڪوٰة نه ڏيندا آھن ۽ اُھي آخرت کي (به) نه مڃيندا آھن.');
INSERT INTO "amroti" VALUES(41,8,'41:8','بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ اَڻ کُٽ اَجر آھي.');
INSERT INTO "amroti" VALUES(41,9,'41:9','(اي پيغمبر کين) چؤ ته اوھين اُن (الله) جو انڪار ڪندا آھيو ڇا جنھن زمين کي ٻن ڏينھن ۾ بڻايو؟ ۽ اُن لاءِ (ٻـين کي) شريڪ بڻائيندا آھيو، اِھو الله جھانن جو پالڻھار آھي.');
INSERT INTO "amroti" VALUES(41,10,'41:10','۽ اُن (زمين) ۾ مٿانئس ڳرا جبل بڻايائين ۽ منجھس برڪت رکيائين ۽ چئن ڏينھن ۾ منجھس اُن (جي رھاڪن) جا قُوۡت مُقرّر ڪيائين، پڇندڙن لاءِ برابر (کولي بيان ڪيو ويو) آھي.');
INSERT INTO "amroti" VALUES(41,11,'41:11','وري آسمان ڏانھن مُھڙ ڪيائين ۽ اُھو (اُن وقت) دُونھون ھو پوءِ اُن کي ۽ زمين کي چيائين ته سرھائيءَ سان خواھ اَرھائيءَ سان اچو، (ٻنھي) چيو ته خُوشيءَ سان آياسون.');
INSERT INTO "amroti" VALUES(41,12,'41:12','پوءِ (ٻـين) ٻن ڏينھن ۾ اُنھن کي سَت آسمان بڻايائين ۽ سڀڪنھن آسمان ۾ اُن (جي ڪم) جو حُڪم موڪليائين، ۽ دنيا جي آسمان کي (تارن جي) ڏينئن سينگاريوسون، ۽ (شيطانن کان اُنھيءَ جي) حفاظت ڪئي سون، اِھا (الله) غالب ڄاڻندڙ جي رِٿ آھي.');
INSERT INTO "amroti" VALUES(41,13,'41:13','پوءِ جيڪڏھن (اُھي) مُنھن موڙين ته چؤ ته اوھان کي اھڙي عذاب کان ڊيڄاريم جھڙو عاد ۽ ثَمُود جو عذاب (ھو).');
INSERT INTO "amroti" VALUES(41,14,'41:14','جڏھن پيغمبر سندن اڳيان ۽ سندن پويان انھن وٽ آيا (پيغمبرن چيو) ته الله کانسواءِ (ٻئي) ڪنھن جي عبادت نه ڪريو، چيائون ته جيڪڏھن اسان جو پالڻھار گھري ھا ته ضرور ملائڪن کي موڪلي ھا تنھنڪري (اوھين) جنھن سان موڪليا ويا آھيو تنھنکي اسين مڃڻ وارا نه آھيون.');
INSERT INTO "amroti" VALUES(41,15,'41:15','پوءِ عاد (وارن) زمين ۾ ناحق وڏائي ڪئي ۽ چيائون ته سگھ ۾ اسان کان وڌيڪ ڏاڍو ڪير آھي؟ نه ڏٺو اٿن ڇا ته اھو الله جنھن کين پيدا ڪيو سا کائن سگھ ۾ وڌيڪ ڏاڍو آھي، ۽ (اُھي) اسان جي نشانين جو انڪار ڪندا رھيا.');
INSERT INTO "amroti" VALUES(41,16,'41:16','تنھنڪري نحس (يعني مُصيبت جي) ڏينھن ۾ مٿن سخت واءُ موڪليوسون ته کين دُنيا جي حياتيءَ ۾ خواريءَ وارو عذاب چکايون، ۽ بيشڪ آخرت جو عذاب ته وڌيڪ خراب ڪندڙ آھي ۽ اُنھن کي (ڪا) مدد نه ڏبي.');
INSERT INTO "amroti" VALUES(41,17,'41:17','۽ ثمود (قوم) سو اُنھن کي پڌرو رستو ڏيکاريوسون پوءِ ھدايت کان انڌائي کي پسند ڪيائون پوءِ خوار ڪندڙ عذاب جي ڪڙڪي کين پڪڙيو انھي ڪري جو (بڇڙا ڪم) ڪمائيندا ھوا.');
INSERT INTO "amroti" VALUES(41,18,'41:18','۽ جن ايمان آندو ھو ۽ پرھيزگاري ڪندا ھوا تن کي بچايوسون.');
INSERT INTO "amroti" VALUES(41,19,'41:19','۽ جنھن ڏينھن الله جا ويري باھ ڏانھن گڏ ڪيا ويندا پوءِ (تنھن ڏينھن) اُھي (ھڪ ٻئي جي اچڻ تائين) ترسايا ويندا.');
INSERT INTO "amroti" VALUES(41,20,'41:20','تانجو جڏھن (اُھي مڙيئي) اُن (دوزخ) وٽ ايندا (تنھن مھل) سندن ڪَن ۽ سندن اکيون ۽ سندن کَلون جيڪي اُھي (دنيا ۾) ڪندا ھوا تنھنجي مٿان شاھدي ڏينديون.');
INSERT INTO "amroti" VALUES(41,21,'41:21','۽ پنھنجي کَلُن کي چوندا ته اسان تي شاھدي ڇو ڏنوَ؟ (سڀئي) چوندا ته اسان کي اُنھيءَ الله ڳالھرايو جنھن ھر شيء کي ڳالھرايو ۽ اُنھيءَ اوھان کي پھريون ڀيرو بڻايو ۽ (وري به) ڏانھس موٽائبؤ.');
INSERT INTO "amroti" VALUES(41,22,'41:22','۽ اوھين پنھنجن ڪَنّن ۽ پنھنجين اکين ۽ پنھنجين کَلُن جي پاڻ تي شاھدي ڏيڻ کان پردو نه ڪندا ھئؤ پر اوھان ئي ڀانيو ھو ته جيڪي ڪندا ھئو تنھن مان گھڻو (احوال) الله نه ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(41,23,'41:23','۽ اوھان جو اُھو گمان جيڪو پنھنجي پالڻھار جي حق ۾ (غلطيءَ سان) ڀانيو ھُئو تنھن (ھاڻي) اوھان کي ھلاڪ ڪيو تنھنڪري (اڄ) ڇيئي وارن مان ٿيؤ.');
INSERT INTO "amroti" VALUES(41,24,'41:24','پوءِ جيڪڏھن صبر ڪندا ته (به) اُنھن جي جاءِ باھ آھي، جيڪڏھن معافي گھرندا ته (به) اُنھن کي معافي نه ملندي.');
INSERT INTO "amroti" VALUES(41,25,'41:25','۽ انھن لاءِ (شيطان) سنگتي مُقرّر ڪياسون پوءِ جيڪي سندن اڳيان ۽ جيڪي سندن پويان آھي تنھنکي اُنھن لاءِ (سندن سنگتين) سينگاريو ۽ (انھيءَ ڪري اُنھن ٽولين ۾ گڏجي) مٿن سزا ثابت ٿي جيڪي کائن اڳ جِنّن ۽ ماڻھن مان گذري ويا آھن، بيشڪ اُھي (سڀ) ٽوٽي وارا ھوا.');
INSERT INTO "amroti" VALUES(41,26,'41:26','۽ ڪافرن (ھڪ ٻئي کي) چيو ته ھن قَرآن کي نه ٻُڌو ۽ اُن (جي پڙھڻ جي وچ ۾) لُڙ مچايو ته مانَ اوھين غالب ٿيو.');
INSERT INTO "amroti" VALUES(41,27,'41:27','پوءِ ڪافرن کي ضرور سخت سزا جو مزو چکائينداسون ۽ جيڪي تمام بڇڙو (ڪم) ڪندا ھوا تنھن جو بدلو ضرور کين ڏينداسون.');
INSERT INTO "amroti" VALUES(41,28,'41:28','الله جي ويرين جي سزا اِھا (ئي) باھ آھي، اُنھن لاءِ اُتي ھميشه جو گھر ھوندو، انھيءَ ڪري سزا (ھوندين) جو اسان جي آيتن جو انڪار ڪندا ھوا.');
INSERT INTO "amroti" VALUES(41,29,'41:29','۽ ڪافر چوندا ته اي اسان جا پالڻھار جِنّن ۽ ماڻھن مان جن اسان کي گُمراھ ڪيو سي اسان کي ڏيکار ته پنھنجن پيرن جي ھيٺان ڪري لتاڙيون ته ڀلي ته (سڀ کان) ھيٺاھان ھجن.');
INSERT INTO "amroti" VALUES(41,30,'41:30','بيشڪ جن چيو ته اسان جو پالڻھار الله آھي وري (ان ڳالھ تي) بيھڪ ورتائون تن تي (موت وقت رحمت جا) ملائڪ لھندا آھن (چوندا اَٿن) ته نه ڊڄو ۽ نڪي ڳڻتي ڪريو ۽ اُنھيءَ بھشت (جي خوشخبريءَ) سان خوش ٿيو جنھن جو اوھان کي وعدو ڏنو ويندو ھو.');
INSERT INTO "amroti" VALUES(41,31,'41:31','اسين دُنيا جي حياتيءَ ۾ اوھان جا دوست ھواسون ۽ آخرت ۾ به (اوھان جا دوست آھيون)، ۽ جيڪي اوھان جو جيءُ خواھش ڪندو سو اوھان لاءِ منجھس آھي ۽ جيڪي اوھين گھرندؤ سو (پڻ) اوھان لاءِ منجھس آھي.');
INSERT INTO "amroti" VALUES(41,32,'41:32','(اھا) مھماني بخشڻھار مھربان (خدا) وٽان آھي.');
INSERT INTO "amroti" VALUES(41,33,'41:33','۽ انھيءَ کان وڌيڪ چڱو ڳالھ ۾ ڪير آھي جنھن الله ڏانھن سڏيو ۽ (پاڻ ۾) چڱا ڪم ڪيائين ۽ چيو ته بيشڪ آءٌ مُسلمانن مان آھيان؟');
INSERT INTO "amroti" VALUES(41,34,'41:34','۽ نيڪي ۽ بدي پاڻ ۾ برابر نه آھن، (۽ نيڪي) جا ڏاڍي چڱي آھي تنھن سان (مدائي کي) ٽاريو، جو اُنھيءَ جي ۽ تنھنجي وچ ۾ دشمني آھي سو ڄڻڪ دوست مائٽي وارو ٿيندو.');
INSERT INTO "amroti" VALUES(41,35,'41:35','۽ اِھا (ڳالھ) صابرن کانسواءِ ٻئي کي حاصل نه ٿيندي آھي، ۽ نڪي وڏي نصيب واري کانسواءِ (ٻئي کي) اِھا حاصل ٿيندي آھي.');
INSERT INTO "amroti" VALUES(41,36,'41:36','۽ جيڪڏھن توکي شيطان کان (دل ۾) ڪا چوڪ اچي ته الله جي پناھ گھُر، ڇوته اُھو ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(41,37,'41:37','۽ سندس (قدرت جي) نشانين مان رات ۽ ڏينھن ۽ سج ۽ چنڊ آھي، نڪي سج کي ۽ نڪي چنڊ کي سجدو ڪريو ۽ انھيءَ الله کي سجدو ڪريو جنھن انھن کي خلقيو جيڪڏھن خاص سنديس عبادت ڪندا آھيو.');
INSERT INTO "amroti" VALUES(41,38,'41:38','پوءِ جيڪڏھن وڏائي ڪن ته (ڇا ٿيو) جيڪي تنھنجي پالڻھار وٽ آھن سي رات ۽ ڏينھن سندس تسبيحون چوندا آھن ۽ اُھي نه ٿڪبا آھن.');
INSERT INTO "amroti" VALUES(41,39,'41:39','۽ سندس (قدرت جي) نشانين مان ھيءُ به آھي جو زمين کي وساڻل ڏسندو آھين پوءِ جڏھن مٿس (مينھن جو) پاڻي وسايو سون (تڏھن) چري ۽ اُڀامي، بيشڪ جنھن اُنھي (زمين) کي جياريو آھي سو ضرور مُئن کي (به) جيارڻ وارو آھي، بيشڪ اُھو سڀڪنھن شيءَ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(41,40,'41:40','بيشڪ جيڪي اسان جي آيتن ۾ ڏنگائي ڪندا آھن سي اسان کان ڳُجھا نه آھن، ڀلا جيڪو باھ ۾ وجھبو سو ڀلو آھي يا جيڪو قيامت جي ڏينھن اَمن وارو ٿي ايندو؟ جيڪي وڻيوَ سو ڪريو بيشڪ جيڪي ڪندا آھيو سو خُدا ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(41,41,'41:41','بيشڪ جن قرآن جو (اُنھي وقت) انڪار ڪيو جڏھن وٽن آيو (سي اسان کان ڳُجھا نه آھن)، ۽ بيشڪ اُھو ڪتاب عزّت وارو آھي.');
INSERT INTO "amroti" VALUES(41,42,'41:42','جو نڪي اُن جي اڳيان ۽ نڪي اُن جي پويان ڪُوڙ داخل ٿيندو آھي، حڪمت واري ساراھيل (الله جي پار) کان لاٿل آھي.');
INSERT INTO "amroti" VALUES(41,43,'41:43','(اي پيغمبر) توکي اُنھيءَ کانسواءِ (ٻيو) ڪي نه چئبو آھي جيڪي توکان اڳ (ٻـين) پيغمبرن کي چيو ويو ھو، بيشڪ تنھنجو پالڻھار بخشش وارو (به) آھي ۽ ڏکوئيندڙ سزا ڏيڻ وارو (به) آھي.');
INSERT INTO "amroti" VALUES(41,44,'41:44','۽ جيڪڏھن اِن (ڪتاب) کي عجمي (يعني عربيءَ کان ڌارين ٻوليءَ ۾) قرآن ڪريون ھا ته ضرور (ڪافر) چون ھا ته سنديس آيتون ڇونه کولي بيان ڪيون ويون، ڀلا (قرآن) عجمي (ٻوليءَ ۾) ۽ (ٻڌڻ وارا) عربيءَ (ٻوليءَ) وارا آھن (سو ڇو)؟ (اي پيغمبر کين) چؤ ته اُھو مؤمنن لاءِ ھدايت ۽ شفا آھي، ۽ جيڪي نه مڃيندا آھن تن جي ڪَنّن ۾ گھٻرائي آھي ۽ اُھو (قرآن) مٿن انڌائي آھي، اِھي (اُنھن وانگر آھن جو) ڄڻڪ ڏورانھين ھنڌ کان سڏيا ويندا آھن.');
INSERT INTO "amroti" VALUES(41,45,'41:45','۽ بيشڪ مُوسىٰ کي ڪتاب ڏنوسون پوءِ منجھس اختلاف ڪيو ويو، ۽ جيڪڏھن تنھنجي پالڻھار کان حُڪم آڳاٽو نه ٿئي ھا ته سندن وچ ۾ ضرور نبيرو ڪيو وڃي ھا، ۽ بيشڪ اُھي کانئس وڏي شڪ ۾ (پيل) آھن.');
INSERT INTO "amroti" VALUES(41,46,'41:46','جيڪو چڱا ڪم ڪندو سو پاڻ لاءِ (ڪندو) تنھن تي (ئي اُن جو نقصان) آھي، ۽ تنھنجو پالڻھار ٻانھن تي ظلم ڪرڻ وارو آھي.');
INSERT INTO "amroti" VALUES(41,47,'41:47','قيامت (اچڻ) جي خبر الله (ئي) جي حوالي آھي، ۽ سندس ڄاڻپ ڌاران ڪي ميوا پنھنجن ڳڀين مان نه نڪرندا آھن ۽ (سندس ڄاڻپ ڌاران) ڪابه مادي نڪي ڍُڪي ٿيندي آھي ۽ نڪي ڄڻيندي آھي، ۽ اُن ڏينھن (الله) کين سڏيندو ته منھنجا شريڪ (جي اوھين مُقرّر ڪندا ھئو سي) ڪٿي آھن چوندا ته توکي خبر ڏني سون ته اسان سان ڪوبه (اُنھن جو) شاھد ڪونھي.');
INSERT INTO "amroti" VALUES(41,48,'41:48','۽ جن کي (ھن کان) اڳ (دنيا ۾) پوڄيندا ھوا سي کائن ڀُلجي ويندا ۽ ڀانئيندا ته کين ڪو ڇوٽڪارو ڪونھي.');
INSERT INTO "amroti" VALUES(41,49,'41:49','ماڻھو چڱائيءَ جي سڏڻ (دُعا گھرڻ) کان نه ٿڪبو آھي، ۽ جيڪڏھن کيس (ڪا) مدائي پھچندي آھي ته پوءِ آسرو ڇِني نا اُميد ٿيندو آھي.');
INSERT INTO "amroti" VALUES(41,50,'41:50','۽ جيڪڏھن کيس اوکائي پھچڻ کان پوءِ پاڻ وٽان ڪا ٻاجھ چکائيندا آھيون ته ضرور چوندو آھي ته ھيءَ مون (ئي) لاءِ آھي ۽ نه ٿو ڀانيان ته قيامت قائم ٿيڻي آھي ۽ جيڪڏھن (آءٌ) پنھنجي پالڻھار ڏانھن (کڻي) موٽائبس ته بيشڪ مون لاءِ وٽس (به) ضرور چڱي حالت ھوندي، پوءِ ڪافرن جيڪي ڪيو آھي تنھن بابت کين ضرور خبردار ڪنداسون، ۽ کين سخت عذاب ضرور چکائينداسون.');
INSERT INTO "amroti" VALUES(41,51,'41:51','۽ جڏھن ماڻھوءَ تي فضل ڪندا آھيون (تڏھن) مُنھن ڦيرائيندو آھي ۽ پنھنجو پاسو موڙيندو آھي، ۽ جڏھن کيس ڪا اوکائي پھچندي آھي تڏھن وڏيون دعائون گھُرندو آھي.');
INSERT INTO "amroti" VALUES(41,52,'41:52','(اي پيغمبر کين) چؤ ته اوھين ڏسو ته جيڪڏھن (قرآن) الله وٽان ھجي وري سندس انڪار ڪريو ته جيڪو وڏي ضِدّ ۾ پيل ھجي تنھن کان وڌيڪ گُمراھ ڪير آھي؟');
INSERT INTO "amroti" VALUES(41,53,'41:53','پنھنجيون نشانيون مُلڪن ۾ سندن وجودن ۾ کين سگھوئي ڏيکاريندا سون تان جو کين پڌرو ٿيندو بيشڪ اِھو (قرآن) سچو آھي، تنھنجو پالڻھار ڪافي نه آھي ڇا جيڪو سڀڪنھن شيء تي حاضر آھي؟');
INSERT INTO "amroti" VALUES(41,54,'41:54','خبردار بيشڪ اُھي پنھنجي پالڻھار جي ملڻ کان شڪ ۾ (پيل) آھن، خبردار اُھو ھر ڪنھن شيء کي وڪوڙيندڙ آھي.');
INSERT INTO "amroti" VALUES(42,1,'42:1','حٰمٓ.');
INSERT INTO "amroti" VALUES(42,2,'42:2','عٓسٓقٓ.');
INSERT INTO "amroti" VALUES(42,3,'42:3','غالب حڪمت وارو الله اھڙيءَ طرح توڏانھن وحي موڪلي ٿو ۽ اُنھن ڏانھن (به) جيڪي توکان اڳ ھوا (موڪليندو) رھيو.');
INSERT INTO "amroti" VALUES(42,4,'42:4','جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) سندس آھي، ۽ اُھو (سڀ کان) مٿاھون (۽) وڏو آھي.');
INSERT INTO "amroti" VALUES(42,5,'42:5','(خُدا جي دھشت کان) آسمان پنھنجي مٿان ستت ئي ڦاٽندا ۽ ملائڪ پنھنجي پالڻھار جي ساراھ سان گڏ پاڪائي بيان ڪندا آھن ۽ جيڪي زمين ۾ آھن تن لاءِ بخشش گھرندا آھن، خبردار بيشڪ الله ئي بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(42,6,'42:6','۽ جن اُن کانسواءِ ٻيا دوست ورتا آھن تن تي الله نگھبان آھي، ۽ تون مٿن ڪو ذمه وار نه آھين.');
INSERT INTO "amroti" VALUES(42,7,'42:7','۽ اھڙيءَ طرح توڏانھن عربيءَ (ٻوليءَ) ۾ قرآن وحي ڪيوسون ته مڪّي وارن ۽ سندس آس پاس وارن کي ڊيڄارين ۽ قيامت جو ڏينھن جنھن ۾ ڪو شڪ نه آھي تنھن کان (پڻ) ڊيڄارين، ھڪ ٽولي بھشت ۾ ۽ ٻي ٽولي دوزخ ۾ ھوندي.');
INSERT INTO "amroti" VALUES(42,8,'42:8','۽ جيڪڏھن الله گھُري ھا ته اُنھن سڀني کي ھڪ ٽولي ڪري ھا پر جنھن کي گھري تنھن کي پنھنجي ٻاجھ ۾ داخل ڪندو آھي، ۽ ظالمن جو نڪي ڪو دوست ۽ نڪي ڪو مددگار آھي.');
INSERT INTO "amroti" VALUES(42,9,'42:9','الله کانسواءِ ٻيا ڪارساز ڪري ورتا اٿن ڇا؟ ڇوته الله ئي ڪارساز آھي ۽ اُھو ئي مُئن کي جياريندو آھي، ۽ اُھو سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(42,10,'42:10','۽ جنھن (ڳالھ) بابت تڪرار ڪيو اٿوَ سو ڪجھ به ھجي ته اُنھيءَ جو نبيرو الله ڏانھن (حوالي ڪيل) آھي، منھنجو پالڻھار اھو الله آھي مٿي ڀروسو ڪيم، ۽ ڏانھس موٽان ٿو.');
INSERT INTO "amroti" VALUES(42,11,'42:11','آسمان ۽ زمين جو بڻائيندڙ آھي، اوھان جي جنس مان اوھان لاءِ زالون پيدا ڪيائين ۽ ڍورن (جي جنس) مان ڪيترائي ئي قِسم (بڻايائين)، انھي طرح سان اوھان کي پکيڙي ٿو، اُن جھڙي ڪا (به) شيءَ نه آھي، ۽ اُھو ٻڌندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(42,12,'42:12','آسمانن ۽ زمين جون ڪنجيون کيس آھن، جنھن لاءِ گُھري (تنھن لاءِ) روزي ڪشادي ڪندو آھي ۽ (جنھن لاءِ گھُري تنھن لاءِ) تنگ ڪندو آھي، بيشڪ اُھو ھر شيء کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(42,13,'42:13','اوھان لاءِ اھو دين مُقرّر ڪيائين جنھن (جي قائم ڪرڻ) جو نُوح کي حُڪم ڪيو ھوائين ۽ جيڪو توڏانھن به وحي ڪيوسين ۽ اُھو جنھن (جي قائم ڪرڻ) جو ابراھيم ۽ مُوسىٰ ۽ عيسىٰ کي حُڪم ڪيوسون ته دين کي قائم ڪريو ۽ منجھس ڌار ڌار نه ٿيو، مُشرڪن کي اُھا (ڳالھ) ڏکي لڳي جنھن ڏانھن کين سڏيندو آھين، الله جنھن کي گھُري (تنھن کي) پاڻ ڏانھن چونڊيندو آھي ۽ جيڪو (ڏانھس) ورندو آھي تنھن کي پاڻ ڏانھن ھدايت ڪندو آھي.');
INSERT INTO "amroti" VALUES(42,14,'42:14','۽ نه جُدا ٿيون (اُمتون) مگر بعد اُن جي جو آيو اُنھن وٽ علم از روءِ سرڪشي جي پاڻ ۾، ۽ جيڪڏھن تنھنجي پالڻھار کان مقرر مُدّت تائين (مھلت ڏيڻ جو) حڪم اڳي (جاري ٿيل) نه ھجي ھا ته ضرور سندن وچ ۾ (ھاڻي ئي) فيصلو ڪجي ھا، ۽ بيشڪ جي انھن (پيغمبرن) کان پوءِ ڪتاب جا وارث ڪيا ويا سي دين کان وڏي شڪ ۾ (پيل) آھن.');
INSERT INTO "amroti" VALUES(42,15,'42:15','پوءِ (تون) انھيءَ (دين) ڏانھن سڏ، ۽ جئن توکي حُڪم ڪيو ويو آھي (تيئن) بيھڪ وٺ، ۽ سندن سَڌن جي تابعداري نه ڪر، ۽ چؤ ته جيڪو به ڪتاب الله لاٿو تنھن کي مڃيم، ۽ مون کي حُڪم ٿيل آھي ته اوھان جي وچ ۾ انصاف ڪريان، الله اسان جو پالڻھار ۽ اوھان جو پالڻھار آھي، اسان لاءِ اسان جا عمل ۽ اوھان لاءِ اوھان جا عمل آھن، اسان جي ۽ اوھان جي وچ ۾ ڪو تڪرار ڪونھي، الله اسان سڀني کي (قيامت جي) ڏينھن گڏ ڪندو، ۽ ڏانھس موٽڻ آھي.');
INSERT INTO "amroti" VALUES(42,16,'42:16','۽ الله (جي دين) بابت سندس فرمان قبول ٿيڻ کانپوءِ جيڪو جھڳڙو ڪندا آھن تن جي حُجت سندن پالڻھار وٽ ڪوڙي (ٿيڻ واري) آھي ۽ مٿن (الله جو) ڏمر آھي ۽ اُنھن لاءِ سخت عذاب آھي.');
INSERT INTO "amroti" VALUES(42,17,'42:17','الله اُھو آھي جنھن سچو ڪتاب ۽ (انصاف جي) ترازي لاٿي، توکي ڪھڙيءَ ڳالھ ڄاڻايو متان قيامت ويجھي ھجي.');
INSERT INTO "amroti" VALUES(42,18,'42:18','جيڪي قيامت کي نه مڃيندا آھن سي اُن کي تڪڙو گھرندا آھن، ۽ جن ايمان آندو آھي سي اُن کان ڊڄندا آھن ۽ ڄاڻندا آھن ته بيشڪ اُھا سچ (اچڻي) آھي، خبردار بيشڪ جيڪي قيامت بابت جھڳڙو ڪندا آھن سي بيشڪ وڏيءَ گُمراھيءَ ۾ آھن.');
INSERT INTO "amroti" VALUES(42,19,'42:19','الله پنھنجن ٻانھن تي مھربان آھي جنھن کي گھري تنھن کي روزي ڏيندو آھي، ۽ اُھو سگھارو غالب آھي.');
INSERT INTO "amroti" VALUES(42,20,'42:20','جيڪو آخرت جي پوک گھُرندو آھي تنھن لاءِ سندس پوک ۾ واڌارو ڪندا آھيون، ۽ جيڪو دنيا جي پوک جو ارادو ڪندو آھي تنھنکي ڪجھ منجھانئس ڏيندا آھيون ۽ آخرت ۾ اُن جو ڪو حصّو نه آھي.');
INSERT INTO "amroti" VALUES(42,21,'42:21','اُنھن (يعني ڪافرن) جا ڪي اھڙا شريڪ آھن ڇا جن اُنھن لاءِ دين مان (اُھو) نئون رستو بڻايو آھي جنھن جو الله حُڪم نه ڏنو آھي، ۽ جيڪڏھن فيصلي ڪرڻ جو وعدو (ٿيل) نه ھجي ھا ته ضرور (ھاڻي ئي) سندن وچ ۾ نبيرو ڪجي ھا، ۽ بيشڪ ظالمن لاءِ ئي ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(42,22,'42:22','(اي پيغمبر) ظالمن جيڪي ڪمايو آھي تنھن (جي عذاب) سببان کين ڊڄندڙ ڏسندين ۽ اُھو (عذاب) کين پھچندڙ آھي، ۽ جن ايمان آندو ۽ چڱا ڪم ڪيا سي بھشتن جي باغن ۾ ھوندا، جيڪي گھرندا سو سندن پالڻھار وٽ اُنھن لاءِ (موجود) آھي، اِھوئي وڏو فضل آھي.');
INSERT INTO "amroti" VALUES(42,23,'42:23','اِھو (ثواب) اُھو آھي جنھن جي الله پنھنجن اُنھن ٻانھن کي خوشخبري ڏيندو آھي جن ايمان آندو آھي ۽ چڱا ڪم ڪيا آھن، (اي پيغمبر کين) چؤ ته آءٌ اوھان کان اُن (پيغام پھچائڻ) تي ڪو اُجورو نه ٿو گھُران پر (جڳائي ته) مائٽن (جي وچ) ۾ محبت (رکو)، ۽ جيڪو چڱائي ڪمائيندو تنھن لاءِ اُن ۾ چڱائي وڌائينداسون، ڇوته الله بخشڻھار قدر ڄاڻڻ وارو آھي.');
INSERT INTO "amroti" VALUES(42,24,'42:24','چوندا آھن ڇا ته (پيغمبر پاڻ) الله تي ڪوڙ ٺاھيو آھي، پوءِ جيڪڏھن الله گھري ته تنھنجيءَ دل تي مُھر ھڻي، ۽ الله ڪُوڙ کي ناس ڪندو آھي ۽ پنھنجن سُخنن سان سچي دين کي ثابت ڪندو آھي، بيشڪ اُھو سينن وارو (ڳجھ) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(42,25,'42:25','۽ اُھو آھي جو پنھنجن ٻانھن جي توبه قبول ڪندو آھي ۽ مداين کان ٽارو ڪندو آھي ۽ جيڪي ڪندا آھيو سو ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(42,26,'42:26','۽ جن ايمان آندو آھي ۽ چڱا ڪم ڪيا آھن تن جي دُعا قبول ڪندو آھي ۽ پنھنجي فضل سان کين وڌيڪ (به) ڏيندو آھي، ۽ ڪافرن لاءِ ئي سخت سزا آھي.');
INSERT INTO "amroti" VALUES(42,27,'42:27','۽ جيڪڏھن الله پنھنجن ٻانھن جي روزي ڪشادي ڪري ھا ته مُلڪ ۾ ضرور بگيڙو وجھن ھا پر جيترو (پاڻ) گھرندو آھي (اوترو) نازل ڪندو آھي، ڇوته اُھو پنھنجن ٻانھن کي ڄاڻندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(42,28,'42:28','۽ (الله) اُھو آھي جو (ماڻھن جي) نا اميد ٿيڻ کان پوءِ مينھن ھيٺ موڪليندو آھي ۽ پنھنجي رحمت پکيڙيندو آھي، اُھو (ئي) ڪارساز ساراھيل آھي.');
INSERT INTO "amroti" VALUES(42,29,'42:29','۽ آسمانن ۽ زمين جو بڻائڻ ۽ جيڪي سندن وچ ۾ چرندڙ پکيڙيائين تن جو (بڻائڻ) سنديس نشانين مان آھي، ۽ اُھو جڏھن گھرندو (تڏھن) سندن گڏ ڪرڻ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(42,30,'42:30','۽ جيڪا به اوکائي اوھان کي پھچندي آھي سا اوھان جي ھٿن جي ڪمائي سببان آھي ۽ گھڻن (گناھن) کان (ته خُدا) در گذر ڪندو آھي.');
INSERT INTO "amroti" VALUES(42,31,'42:31','۽ اوھين زمين ۾ (ڀڄي) عاجز ڪرڻ وارا نه آھيو، ۽ الله کانسواءِ اوھان جو نڪو ڪارساز ۽ نڪو مددگار آھي.');
INSERT INTO "amroti" VALUES(42,32,'42:32','۽ سنديس نشانين مان درياءَ ۾ جبلن وانگر جھاز (ھلندا) آھن.');
INSERT INTO "amroti" VALUES(42,33,'42:33','جيڪڏھن (خدا) گھري ته وائن کي جھلي پوءِ ٻيڙا درياءَ جي پٺي تي (ھلڻ کان) بيھي رھن، بيشڪ ان ۾ سڀڪنھن صبر ڪندڙ لاءِ نشانيون آھن.');
INSERT INTO "amroti" VALUES(42,34,'42:34','يا ھلاڪ ڪرين تنھن سببان جو ڪمايائون ۽ عفو ٿو ڪري گھڻن کان.');
INSERT INTO "amroti" VALUES(42,35,'42:35','۽ ته جيڪي اسان جي آيتن بابت جھڳڙو ڪندا آھن سي ڄاڻن ته انھن لاءِ ڇوٽڪارو نه آھي.');
INSERT INTO "amroti" VALUES(42,36,'42:36','پوءِ جيڪي اوھان کي ڏنو ويو آھي سو ڪجھ به ھجي ته اُھو دنيوي حياتيءَ جو (ٿورو) سامان آھي، ۽ جيڪي الله وٽ آھي سو ڀَلو ۽ ھميشه رھڻ وارو انھن لاءِ آھي جن ايمان آندو آھي ۽ پنھنجي رب تي ڀروسو ڪندا آھن.');
INSERT INTO "amroti" VALUES(42,37,'42:37','۽ (اُنھن لاءِ به) جيڪي وڏن گناھن ۽ بي حيائين کان پاسو ڪن ٿا ۽ جيڪڏھن ڪاوڙبا آھن (تڏھن) اُھي بخشيندا آھن.');
INSERT INTO "amroti" VALUES(42,38,'42:38','۽ جن پنھنجي پالڻھار جو حُڪم مڃيو ۽ نماز پڙھي، ۽ سندن ڪم پاڻ ۾ صلاح سان ھوندو آھي، ۽ جيڪي کين رزق ڏنوسون تنھن مان خرچيندا آھن.');
INSERT INTO "amroti" VALUES(42,39,'42:39','۽ اُھي (اھڙا آھن) جو جڏھن کين ڪو ظلم پھچي (تڏھن مظلوم جو مناسب) بدلو وٺندا آھن.');
INSERT INTO "amroti" VALUES(42,40,'42:40','۽ مدائيءَ جو بدلو اُھڙي مدائي آھي، پوءِ جيڪو معاف ڪري ۽ صُلح ڪري تنھن جو اجر الله تي آھي، بيشڪ الله ظالمن کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(42,41,'42:41','۽ بيشڪ جيڪي پنھنجي ايذائجڻ کانپوءِ وير وٺن تن تي ڪا (ڏوراپي جي) واٽ ڪانه آھي.');
INSERT INTO "amroti" VALUES(42,42,'42:42','(ڏوراپي جي) واٽ رڳو انھن تي آھي جيڪي ماڻھن کي ايذائيندا آھن ۽ مُلڪ ۾ ناحق فساد وجھندا آھن، اُنھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(42,43,'42:43','۽ البت جيڪو صبر ڪري ۽ بخشي ته بيشڪ اھو (عفو) ھمّت جي ڪمن مان آھي.');
INSERT INTO "amroti" VALUES(42,44,'42:44','۽ جنھن کي الله گُمراھ ڪري تنھن جو اُن کانپوءِ ڪو ڀرجھلو ڪونھي، ۽ (تون) ظالمن کي ڏسندين ته جڏھن (اُھي) سزا ڏسندا (تڏھن) چوندا ته (ھاڻي دنيا ۾) ورڻ جي ڪا واھ آھي ڇا؟');
INSERT INTO "amroti" VALUES(42,45,'42:45','۽ ڏسندين تون اُنھن کي جو پيش ڪيا ويندا باھ تي عاجزي ڪندڙ خواريءَ کان اک ڏٻي ڪيون پيا ڏسندا، ۽ مؤمن چوندا ته بيشڪ ڇيئي وارا اُھي آھن جن پاڻ کي ۽ پنھنجن گھر وارن کي قيامت جي ڏينھن ڇيئو لاتو، خبردار بيشڪ ظالم ھميشه جي عذاب ۾ ھوندا.');
INSERT INTO "amroti" VALUES(42,46,'42:46','۽ الله کانسواءِ اُنھن جا ڪي ڀر جھلا نه ھوندا جي کين مدد ڏين، ۽ جنھن کي الله گُمراھ ڪري تنھن لاءِ ڪا واٽ نه آھي.');
INSERT INTO "amroti" VALUES(42,47,'42:47','پنھنجي پالڻھار جو حُڪم اُن ڏينھن (جي اچڻ) کان اڳ قبول ڪريو جنھن کي الله (جي پار) کان ٽَرڻو نه آھي، اُن ڏينھن نڪا اوھان کي واھ آھي ۽ نه ھوندو اوھان لاءِ ڪو انڪار.');
INSERT INTO "amroti" VALUES(42,48,'42:48','پوءِ جيڪڏھن منھن موڙين ته توکي مٿن نگھبان ڪري نه موڪليو اٿون، توتي پيغام پھچائڻ کانسواءِ (ٻيو) ڪي لازم نه آھي، ۽ بيشڪ اسين جڏھن ماڻھوءَ کي پاڻ وٽان ٻاجھ (جو مزو) چکائيندا آھيون (تڏھن) اُن سان خُوش ٿيندو آھي، ۽ جيڪڏھن ڪا اوکائي سندن ھٿن جي اڳ ڪئي سببان کين پھچندي آھي ته بيشڪ ماڻھو بي شڪر (ٿيندو) آھي.');
INSERT INTO "amroti" VALUES(42,49,'42:49','آسمانن ۽ زمين جي بادشاھي خاص الله جي آھي، جيڪي گھري (سو) خلقيندو آھي، جنھن کي وڻيس (تنھن کي) ڌيئرون بخشيندو آھي ۽ جنھن کي وڻيس (تنھن کي) پُٽ بخشيندو آھي.');
INSERT INTO "amroti" VALUES(42,50,'42:50','يا کين پُٽ ۽ ڌيئرون گڏ ڏيندو آھي، ۽ جنھن کي وڻيس (تنھن کي) سَنڍ ڪندو آھي، بيشڪ اُھو ڄاڻندڙ وس وارو آھي.');
INSERT INTO "amroti" VALUES(42,51,'42:51','۽ ڪنھن ماڻھوءِ لاءِ ٿيڻو ئي نه آھي ته الله ساڻس ڳالھائي سواءِ ھن جي ته الھام سان يا پردي جي پويان يا ڪنھن قاصد موڪلڻ سان (ڳالھائي) پوءِ جيڪي الله گھريو سو سندس حُڪم سان پھچائي ڏي، بيشڪ اُھو (سڀ کان) مٿاھون حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(42,52,'42:52','۽ اھڙيءَ طرح اسان پنھنجي ڪلام سان توڏانھن قرآن وحي ڪيو، (اڳ) تون نه ڄاڻندو ھئين ته ڪتاب ڇا آھي ۽ نڪي (ڄاڻندو ھئين ته) ايمان (ڇا آھي) پر قُرآن کي نُور ڪيو اٿون پنھنجن ٻانھن مان جنھن کي گھرون (تنھن کي) اُن سان سڌو رستو ڏيکاريندا آھيون، ۽ بيشڪ تون سڌي رستي ڏانھن ھدايت ڪندو آھين.');
INSERT INTO "amroti" VALUES(42,53,'42:53','(يعني) اُنھي الله جو رستو جو جيڪي آسمانن ۽ زمين ۾ آھي سو سندس آھي، خبردار الله ڏانھن (ئي سڀ) ڪم وري ويندا آھن.');
INSERT INTO "amroti" VALUES(43,1,'43:1','حٰمٓ.');
INSERT INTO "amroti" VALUES(43,2,'43:2','پڌري ڪتاب جو قسم آھي.');
INSERT INTO "amroti" VALUES(43,3,'43:3','ته بيشڪ اسان ھن (ڪتاب) کي عربيءَ (ٻوليءَ ۾) قرآن ڪيو آھي ته مانَ اوھين سمجھو.');
INSERT INTO "amroti" VALUES(43,4,'43:4','۽ بيشڪ اُھو (ڪتاب) لَوح مَحۡفوظ ۾ اسان وٽ (لکيل) آھي بيشڪ (اُھو ڪتاب) وڏي مرتبي وارو حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(43,5,'43:5','اوھان کان نصيحت کي ھن ڪري مُنھن موڙي جھلينداسون ڇا ته اوھين حد کان لنگھيل قوم آھيو.');
INSERT INTO "amroti" VALUES(43,6,'43:6','۽ اڳين (قومن) ۾ گھڻائي پيغمبر موڪلياسون.');
INSERT INTO "amroti" VALUES(43,7,'43:7','۽ وٽن ڪوبه پيغمبر نه ٿي آيو مگر ساڻس ٺـٺوليون ٿي ڪيائون.');
INSERT INTO "amroti" VALUES(43,8,'43:8','پوءِ اُنھن (قريشن) کان سگھ ۾ تمام ڏاڍن (عاد ۽ ثمود) کي (جو کائن بلڪل سگھارا ھوا) ناس ڪياسون ۽ اڳين جي حالت (اڳين بيان ۾) گذري چڪي آھي.');
INSERT INTO "amroti" VALUES(43,9,'43:9','۽ جيڪڏھن کائن پُڇين ٿه آسمانن ۽ زمين کي ڪنھن بڻايو ته ضرور چوندا ته انھن کي غالب ڄاڻندڙ (الله) بڻايو.');
INSERT INTO "amroti" VALUES(43,10,'43:10','(اُنھيءَ الله) جنھن اوھان لاءِ زمين کي وڇاڻو بڻايو ۽ اوھان لاءِ منجھس رستا بڻايا ته مانَ اوھين سِڌو رستو لھو.');
INSERT INTO "amroti" VALUES(43,11,'43:11','۽ (اُنھيءَ الله) جنھن آسمان مان اندازي سان پاڻي وسايو، پوءِ اُن (پاڻيءَ) سان ويران ڳوٺ آباد ڪياسون، اھڙي طرح اوھان کي (قبرن مان) ڪڍيو ويندو.');
INSERT INTO "amroti" VALUES(43,12,'43:12','۽ (اُنھيءَ الله) جنھن ھر قسم جا جانور بڻايائين ۽ اوھان لاءِ ٻيڙيون ۽ اُھي جانور بڻايائين جن تي (اوھين) چڙھو.');
INSERT INTO "amroti" VALUES(43,13,'43:13','ته اُنھن جي پُٺين تي سِڌا ٿي ويھو وري جڏھن مٿن سنوان ٿي ويھو (تڏھن) پنھنجي پالڻھار جي نعمت ياد ڪريو ۽ چئو ته اُھو (الله) پاڪ آھي جنھن ھيءُ اسان کي نِوائي ڏنو ۽ (اسين) اُن جي نوائڻ جي (ڪڏھن) طاقت رکندڙ نه ھواسون.');
INSERT INTO "amroti" VALUES(43,14,'43:14','۽ بيشڪ اسين پنھنجي پالڻھار ڏانھن موٽڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(43,15,'43:15','۽ الله لاءِ سندس ٻانھن مان اولاد مقرر ڪيائون، بيشڪ ماڻھو پڌرو بي شُڪر آھي.');
INSERT INTO "amroti" VALUES(43,16,'43:16','(الله) پنھنجي مخلوق مان (پاڻ لاءِ) ڌيئرون اختيار ڪيون ڇا ۽ اوھان کي پٽن سان چونڊيو اٿس (ڇا)؟');
INSERT INTO "amroti" VALUES(43,17,'43:17','۽ جڏھن منجھائن ڪنھنکي اُنھيءَ شيءَ (يعني ڌيءُ جي ڄمڻ) جي خبر ڏجي جيڪا ٻاجھاري (الله) لاءِ مثال بيان ڪئي اٿس (تڏھن) سندن منھن ڪاراٽجي ويندو آھي ۽ اُھو ڏک ڀريو ھوندو آھي.');
INSERT INTO "amroti" VALUES(43,18,'43:18','جيڪو ڳھن ۾ پالجي ٿو ۽ اُھو جھڳڙي ۾ (پنھنجي ڳالھ کي) پڌرو ڪرڻ وارو نه آھي.');
INSERT INTO "amroti" VALUES(43,19,'43:19','۽ ملائڪن کي (الله جون) ڌيئرون مُقرّر ڪيون اٿن جي (پاڻ) الله جا ٻانھا آھن، (اھي) سندن پيدا ٿيڻ وقت حاضر ھوا ڇا؟ سندين (اھا) شاھدي سِگھو لکبي ۽ (ھُو قيامت ۾) پُڇيا ويندا.');
INSERT INTO "amroti" VALUES(43,20,'43:20','۽ چوندا آھن ته جيڪڏھن ٻاجھارو (الله) گھري ھا ته انھن (ملائڪن) جي عبادت نه ڪريون ھا کين ھن (ڳالھ) جي ڪا ڄاڻپ نه آھي، اُھي رڳو اٽڪل ھڻندا آھن.');
INSERT INTO "amroti" VALUES(43,21,'43:21','کين ھن (قرآن) کان اڳ ڪو ڪتاب ڏنو اٿون ڇا جو اُنھن اُن کي چنبڙي ورتو آھي.');
INSERT INTO "amroti" VALUES(43,22,'43:22','(نه!) بلڪ چوندا آھن ته بيشڪ اسان پنھنجن ابن ڏاڏن کي ھڪ مذھب تي ڏٺو ۽ بيشڪ اسين سندن پيرن تي رستي لھڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(43,23,'43:23','۽ اھڙي طرح توکان اڳ ڪنھن ڳوٺ ۾ ڪو ڊيڄاريندڙ نه موڪليوسون پر اُن جي آسودن (ماڻھن) چيو ته اسان پنھنجا پيءُ ڏاڏا ھڪ دين تي ڏٺا ۽ بيشڪ اسين انھن (ئي) جي پيرن تي پيروي ڪندڙ آھيون.');
INSERT INTO "amroti" VALUES(43,24,'43:24','(پيغمبر کين) چيو ته (پنھنجن ابن ڏاڏن جي پيروي ڪندؤ) ڇا؟ جيتوڻيڪ اوھان وٽ اُھو (دين) آندو اٿم جنھن (دين) تي اوھان پنھنجن پين ڏاڏن کي ڏٺو تنھن کان وڌيڪ سڌو رستو ڏيکارڻ وارو آھي، چيائون ته جنھن شيء سان اوھان کي موڪليو ويو آھي تنھنجا اسين مُنڪر آھيون.');
INSERT INTO "amroti" VALUES(43,25,'43:25','پوءِ کائن بدلو ورتوسون پوءِ ڏس ته ڪوڙن جي پڇاڙي ڪئن ٿي.');
INSERT INTO "amroti" VALUES(43,26,'43:26','۽ (ياد ڪر) جڏھن ابراھيم پنھنجي پيءُ ۽ پنھنجي قوم کي چيو ته جنھن شيء کي (اوھين) پوڄيندا آھيو تنھن کان بيشڪ آءٌ بيزار آھيان.');
INSERT INTO "amroti" VALUES(43,27,'43:27','پر جنھن مون کي پيدا ڪيو سو بيشڪ مون کي سگھوئي ھدايت ڪندو.');
INSERT INTO "amroti" VALUES(43,28,'43:28','۽ الله سندس اولاد ۾ اِھا (توحيد جي ڪلمي جي) ڳالھ ھميشه رھائي ته مانَ اُھي (ڪافر) موٽن.');
INSERT INTO "amroti" VALUES(43,29,'43:29','بلڪ مان انھن ۽ سندن ابن ڏاڏن کي آسودو ڪيو تان جو وٽن سچّو دين ۽ پڌرو پيغمبر (صلى الله عليه وسلم) آيو.');
INSERT INTO "amroti" VALUES(43,30,'43:30','۽ جنھن مھل سچو دين وٽن آيو (تنھن مھل) چيائون ته ھيءَ جادو آھي ۽ بيشڪ اسين اُن جا مُنڪر آھيون.');
INSERT INTO "amroti" VALUES(43,31,'43:31','۽ چيائون ته (ھنن) ٻن ڳوٺن وارن مان ڪنھن سردار مڙس تي ھيءُ قرآن ڇونه نازل ڪيو ويو.');
INSERT INTO "amroti" VALUES(43,32,'43:32','اِھي تنھنجي پالڻھار جي رحمت وراھيندا آھن ڇا؟ اسان سندن گذران سندن وچ ۾ دنيا جي حياتيءَ ۾ وراھيو آھي ۽ اُنھن مان ھڪڙن کي ٻـين کان وڏي مرتبي وارو ڪيوسون ته ھڪڙا ٻـين کي فرمانبردار ڪري وٺن، ۽ جيڪو (دنيا جو مال) گڏ ڪندا آھن تنھن کان تنھنجي پالڻھار جي رحمت ڀلي آھي.');
INSERT INTO "amroti" VALUES(43,33,'43:33','۽ جيڪڏھن ائين نه ھجي ھا ته (متان) ماڻھو ھڪ ٽولي (ڪفر جي) ٿي وڃن جيڪي ٻاجھاري (الله) جو انڪار ڪندا آھن تن لاءِ سندن گھرن جون ڇتيون ۽ ڏاڪڻيون جن تي چڙھن سي روپيون ڪريون ھا.');
INSERT INTO "amroti" VALUES(43,34,'43:34','۽ سندن گھرن جا دروازا ۽ تخت جن تي ٽيڪ ڏيئي ويھن (سي به روپا ڪريون ھا).');
INSERT INTO "amroti" VALUES(43,35,'43:35','۽ اِھو سڀ رڳو دنيا جي حياتيءَ جو (ٿورو) آسودو رھڻ آھي، ۽ تنھنجي پروردگار وٽ آخرت پرھيزگارن لاءِ آھي.');
INSERT INTO "amroti" VALUES(43,36,'43:36','۽ جيڪو ٻاجھاري (الله) جي ياد ڪرڻ کان غافل رھندو آھي تنھن لاءِ ھڪ شيطان مقرر ڪندا آھيون جو اُھو اُن جو سنگتي ھوندو آھي.');
INSERT INTO "amroti" VALUES(43,37,'43:37','۽ بيشڪ اُھي شيطان سڌي واٽ کان اُنھن کي جھليندا آھن ۽ (ماڻھو پاڻ کي) ڀانئيندا آھن ته اُھي ھدايت وارا آھن.');
INSERT INTO "amroti" VALUES(43,38,'43:38','تانجو جڏھن اسان وٽ ايندو (تڏھن پنھنجيءَ سنگتي شيطان کي) چوندو ته ھيء افسوس (جيڪر) منھنجي ۽ تنھنجي وچ ۾ اوڀر ۽ اولھ جيتري ڏورائي ھجي ھا پوءِ بڇڙو سنگتي آھين (تون).');
INSERT INTO "amroti" VALUES(43,39,'43:39','۽ ھرگز نه نفعو ڏيندي اوھان کي اڄ اھا سڌ جڏھن ظُلم ڪيو اوھان تحقيق اوھان عذاب ۾ شريڪ ٿيڻ وارا آھيو.');
INSERT INTO "amroti" VALUES(43,40,'43:40','(اي پيغمبر) تون ٻوڙن کي ٻڌائي ٿو سگھين ڇا يا انڌن کي ۽ اُھو جيڪو پڌريءَ گُمراھيءَ ۾ آھي تنھن کي ھدايت ڪري سگھين ٿو ڇا؟');
INSERT INTO "amroti" VALUES(43,41,'43:41','پوءِ جيڪڏھن توکي (ھن جھان مان) نيون ته بيشڪ اسين (اتي ئي) کائن بدلو وٺڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(43,42,'43:42','يا جيڪو کين انجام ڏنو اٿون سو توکي ڏيکاريون ته به بيشڪ اسين مٿن وسوارا آھيون.');
INSERT INTO "amroti" VALUES(43,43,'43:43','تنھن ڪري جيڪي توڏانھن وحي ڪيو ويو تنھن کي تون چنبڙي وٺ، بيشڪ تون سڌيءَ واٽ تي آھين.');
INSERT INTO "amroti" VALUES(43,44,'43:44','۽ بيشڪ اِھو (قرآن) تولاءِ ۽ تنھنجي قوم لاءِ نصيحت آھي، ۽ (اوھين) سگھوئي پڇيا ويندؤ.');
INSERT INTO "amroti" VALUES(43,45,'43:45','۽ پنھنجن پيغمبرن مان جيڪي توکان اڳ موڪليا ھواسون تن جو (احوال) پُڇ، ته ٻاجھاري (الله) کانسواءِ (ڪي) ٻيا معبود بڻايا ھواسون ڇا جن جي پوڄا ڪئي وڃي؟');
INSERT INTO "amroti" VALUES(43,46,'43:46','۽ بيشڪ مُوسىٰ کي پنھنجين نشانين سان فرعون ۽ سندس سردارن ڏانھن موڪليوسون پوءِ چيائين ته بيشڪ آءٌ جھانن جي پالڻھار جو موڪليل آھيان.');
INSERT INTO "amroti" VALUES(43,47,'43:47','پوءِ جنھن مھل وٽن اسان جي نشانين سان آيو (تنھن مھل) اوچتو اُھي اُنھن (معجزن) کان کلندا رھيا.');
INSERT INTO "amroti" VALUES(43,48,'43:48','۽ ڪابه نشاني کين نه ٿي ڏيکاري سون پر اِھا (نشاني) اُن جي اڳين کان تمام وڏي ھئي، ۽ کين عذاب سان پڪڙيوسون ته مانَ اُھي (ڪفر کان) موٽن.');
INSERT INTO "amroti" VALUES(43,49,'43:49','۽ چيائون ته اي جادوگر پنھنجي پالڻھار کان اسان لاءِ اُنھيءَ (وسيلي) سان دُعا گھر جنھن جو توسان انجام ڪيو اٿس، ته بيشڪ اسين ھدايت وارا ٿينداسون.');
INSERT INTO "amroti" VALUES(43,50,'43:50','پوءِ جنھن مھل کائن عذاب لاٿوسون اوچتو اُنھن عھد ڀڳو.');
INSERT INTO "amroti" VALUES(43,51,'43:51','۽ فرعون پنھنجي قوم ۾ پڙھو ڏنو چيائين ته اي منھنجي قوم مِصۡر جي بادشاھي منھنجي نه آھي ڇا؟ ۽ ھي نھرون منھنجي (ماڙين جي) ھيٺان (نه) وھنديون آھن (ڇا؟) اوھين نه ڏسندا آھيو ڇا؟');
INSERT INTO "amroti" VALUES(43,52,'43:52','(نه!) بلڪ آءٌ ھن (مُوسى) کان ڀَلو آھيان جو اُھو خوار آھي، ۽ چٽو ڳالھائي به نه ٿو سگھي.');
INSERT INTO "amroti" VALUES(43,53,'43:53','پوءِ مٿس سون جا ڪنگڻ ڇونه لاٿا ويا يا ساڻس گڏ ٿي ملائڪ ڇونه آيا؟');
INSERT INTO "amroti" VALUES(43,54,'43:54','پوءِ پنھنجي قوم کي بي عقل ڪيائين تنھنڪري سندس فرمانبرداري ڪيائون، بيشڪ اُھي بي دين قوم ھوا.');
INSERT INTO "amroti" VALUES(43,55,'43:55','پوءِ جڏھن اسان کي ڪاوڙايائون (تڏھن) کائن وير ورتوسون پوءِ اُنھن سڀني کي ٻوڙيوسون.');
INSERT INTO "amroti" VALUES(43,56,'43:56','پوءِ کين (ڪافرن جو) اڳواڻ ۽ پوين لاءِ آکاڻي ڪيوسون.');
INSERT INTO "amroti" VALUES(43,57,'43:57','۽ جڏھن مريم جي پُٽ جو قصّو بيان ڪيو ويو (تڏھن) اُمالڪ اُن (قِصّي) کان تنھنجي قوم لُڙ مچائيندي آھي.');
INSERT INTO "amroti" VALUES(43,58,'43:58','۽ (اُھي) چوندا آھن ته اسان جا معبُود ڀلا آھن يا عيسى؟ اِھو مثال توسان رڳو جھيڙي لاءِ بيان ڪندا آھن، بلڪ اُھي جھيڙاڪ قوم آھن.');
INSERT INTO "amroti" VALUES(43,59,'43:59','عيسىٰ رڳو ٻانھو آھي جنھن تي ٻاجھ ڪئي اٿون ۽ بني اسرائيلن لاءِ کيس (ھڪ) عبرت ڪئي سون.');
INSERT INTO "amroti" VALUES(43,60,'43:60','۽ جيڪڏھن گھرون ھا ته اوھان جي بدران زمين ۾ ملائڪن کي نائب ڪريون ھا.');
INSERT INTO "amroti" VALUES(43,61,'43:61','۽ بيشڪ عيسىٰ قيامت جي ھڪ نشاني آھي پوءِ اوھين قيامت ۾ شڪ نه ڪريو ۽ (اي پيغمبر چؤ ته) منھنجي تابعداري ڪريو (جو) اِھا سِڌي واٽ آھي.');
INSERT INTO "amroti" VALUES(43,62,'43:62','۽ اوھان کي شيطان نه جھلي، ڇوته اھو اوھان جو پڌرو ويري آھي.');
INSERT INTO "amroti" VALUES(43,63,'43:63','۽ جنھن مھل عيسىٰ چٽن معجزن سان آيو (تنھن مھل) چيائين ته اوھان وٽ حڪمت آندي اٿم ۽ (آيو آھيان) ته جن ڳالھين ۾ اختلاف ڪند آھيو تن مان ڪي اوھان لاءِ بيان ڪريان، پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.');
INSERT INTO "amroti" VALUES(43,64,'43:64','بيشڪ الله ئي منھنجو پالڻھار ۽ اوھان جو پالڻھار آھي تنھنڪري سنديس عبادت ڪريو، اِھا سِڌي واٽ آھي.');
INSERT INTO "amroti" VALUES(43,65,'43:65','پوءِ ٽولين پاڻ ۾ اختلاف ڪيو، تنھنڪري ڏکوئيندڙ ڏينھن جي عذاب کان ظالمن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(43,66,'43:66','(اُھي) رڳو قيامت (جي اچڻ) جو انتظار ڪندا آھن ته اوچتو وٽن اچي ۽ اُھي بي خبر ھجن.');
INSERT INTO "amroti" VALUES(43,67,'43:67','اُنھيءَ ڏينھن پرھيزگارن کانسواءِ (ٻيا) سڀئي دوست ھڪ ٻئي جا دشمن ھوندا.');
INSERT INTO "amroti" VALUES(43,68,'43:68','(چيو ويندو ته) اي منھنجا (اُھي) ٻانھا اڄ اوھان کي نڪي ڀؤ آھي نڪي اوھين غمگين ٿيو.');
INSERT INTO "amroti" VALUES(43,69,'43:69','جن اسان جي آيتن تي ايمان آندو ۽ مُسلمان ھوا.');
INSERT INTO "amroti" VALUES(43,70,'43:70','(چيو ويندو ته) اوھين ۽ اوھان جون زالون بھشت ۾ خُوش ٿي گھِڙو.');
INSERT INTO "amroti" VALUES(43,71,'43:71','مٿن سون جون رڪيبيون ۽ آبخورا ڦيرائبا، ۽ جيڪي دليون گھرنديون ۽ (جنھن جي ڏسڻ کان) اکيون ٺرنديون سو منجھس ھوندو، اوھين منجھس سدائين رھندؤ.');
INSERT INTO "amroti" VALUES(43,72,'43:72','۽ ھي اُھو بھشت آھي جنھن جو اوھان کي انھي سببان وارث ڪيو ويو جو اوھين (چڱا) ڪم ڪندا ھيؤ.');
INSERT INTO "amroti" VALUES(43,73,'43:73','منجھس اوھان لاءِ گھڻا ميوا آھن جن منجھان پيا ٿا کائو.');
INSERT INTO "amroti" VALUES(43,74,'43:74','بيشڪ ڏوھي دوزخ جي عذاب ۾ سدائين رھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(43,75,'43:75','کائن عذاب ھلڪو نه ڪبو ۽ اُھي منجھس نا اُميد (ٿي) رھندا.');
INSERT INTO "amroti" VALUES(43,76,'43:76','۽ مٿن ظلم نه ڪيوسون پر اُھي پاڻ ظالم ھوا.');
INSERT INTO "amroti" VALUES(43,77,'43:77','۽ (دوزخ جي دربان کي) سڏيندا ته اي مالڪ جڳائي ته تنھنجو پالڻھار اسان تي (موت جو) حڪم ڪري (ته عذاب کان ڇُٽون، مالڪ) چوندو ته بيشڪ اوھين ھميشه رھڻ وارا آھيو.');
INSERT INTO "amroti" VALUES(43,78,'43:78','بيشڪ اوھان وٽ سچو دين آندوسون پر اوھان مان گھڻا سچي ڳالھ کي ناپسند ڪرڻ وارا آھن.');
INSERT INTO "amroti" VALUES(43,79,'43:79','(ڪافرن به) ڪنھن ڪم جي رِٿ رٿي آھي ڇا؟ پوءِ بيشڪ اسين به پڪي رِٿ رٿيندا آھيون.');
INSERT INTO "amroti" VALUES(43,80,'43:80','ڀائيندا آھن ڇا ته اسين سندن ڳجھيون ڳالھيون ۽ سندن صلاحون نه ٻڌندا آھيون؟ ھائو (ٻڌندا آھيون) ۽ اسان جا موڪليل ملائڪ وٽن لکندا آھن.');
INSERT INTO "amroti" VALUES(43,81,'43:81','(تون) چؤ ته جيڪڏھن ٻاجھاري (الله) کي ڪو وياءُ ھجي، ته آءٌ (سڀ کان) پھرين (اُن جي) عبادت ڪندڙ ھجان.');
INSERT INTO "amroti" VALUES(43,82,'43:82','(اِھي) جيڪي بيان ڪندا آھن تنھن کان آسمانن ۽ زمين جو پالڻھار عرش جو مالڪ پاڪ آھي.');
INSERT INTO "amroti" VALUES(43,83,'43:83','پوءِ کين ڇڏ ته (ايسين ڪوڙين ڳالھين ۾) بڪ ڪن ۽ راند ڪن جيسين پنھنجي اُنھي ڏينھن کي ملن جنھنجو کين انجام ڏجي ٿو.');
INSERT INTO "amroti" VALUES(43,84,'43:84','۽ (الله) اُھو آھي جيڪو آسمانن ۾ معبود آھي ۽ زمين ۾ (به) معبود آھي، ۽ اُھو حڪمت وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(43,85,'43:85','۽ اُھو وڏي برڪت وارو آھي جنھن جي آسمانن ۽ زمين جي ۽ جيڪي اُنھن جي وچ ۾ آھي تنھن جي بادشاھت آھي، ۽ وٽس ئي قيامت (جي اچڻ) جي خبر آھي، ۽ ڏانھنس موٽائبؤ.');
INSERT INTO "amroti" VALUES(43,86,'43:86','۽ (ڪافر) جن (بُتن) کي الله کانسواءِ سڏيندا آھن سي شفاعت ڪري نه سگھندا آھن پر اُھي جن (دين جي) سچي شاھدي ڏني ھوندي ۽ اُھي (اُن جي حقيقت) ڄاڻندا آھن (اُھي شفاعت ڪندا).');
INSERT INTO "amroti" VALUES(43,87,'43:87','۽ جيڪڏھن کائن پڇين ته کين ڪنھن خلقيو ته ضرور چوندا ته الله (خلقيو آھي) پوءِ ڪيڏانھن ڦيرايا ويندا آھن.');
INSERT INTO "amroti" VALUES(43,88,'43:88','۽ (قسم آھي) پيغمبر جي انھي چوڻ تي ته اي منھنجا پالڻھار بيشڪ ھيءَ اھڙي قوم آھن جو (ڪنھن طرح) ايمان نه ٿا آڻين.');
INSERT INTO "amroti" VALUES(43,89,'43:89','پوءِ کائن مُنھن موڙ ۽ (موڪلائيءَ جو) سلام چؤ، پوءِ سگھوئي ڄاڻندا.');
INSERT INTO "amroti" VALUES(44,1,'44:1','حٰمٓ.');
INSERT INTO "amroti" VALUES(44,2,'44:2','(ھن) پڌريءَ ڪتاب جو قسم آھي.');
INSERT INTO "amroti" VALUES(44,3,'44:3','ته بيشڪ اسان ھن (ڪتاب) کي برڪت واريءَ رات ۾ لاٿو بيشڪ اسين ڊيڄاريندڙ ھواسون.');
INSERT INTO "amroti" VALUES(44,4,'44:4','اُن (برڪت واريءَ رات) ۾ سڀڪو ڪم حڪمت وارو فيصل ڪبو آھي.');
INSERT INTO "amroti" VALUES(44,5,'44:5','(اھو) پاڻ وٽان (پنھنجي) حڪم سان (لاٿوسون) بيشڪ اسين (ئي پيغمبر کي) موڪلڻ وارا ھواسون.');
INSERT INTO "amroti" VALUES(44,6,'44:6','تنھنجي پالڻھار جي ٻاجھ سبب (اُن کي لاٿوسون)، بيشڪ اُھوئي ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(44,7,'44:7','(جو) آسمانن ۽ زمين جو ۽ جيڪي انھن جي وچ ۾ آھي تنھنجو پالڻھار آھي، جيڪڏھن اوھين وسھندڙ آھيو (ته وسھو).');
INSERT INTO "amroti" VALUES(44,8,'44:8','اُن کانسواءِ (ٻيو) ڪوبه عبادت جو لائق نه آھي (جو) جياريندو آھي ۽ ماريندو آھي (اُھوئي) اوھان جو پالڻھار ۽ اوھان جي اڳين پين ڏاڏن جو پالڻھار آھي.');
INSERT INTO "amroti" VALUES(44,9,'44:9','بلڪ ڪافر (قيامت) کان شڪ ۾ آھن جو راند ڪندا آھن.');
INSERT INTO "amroti" VALUES(44,10,'44:10','پوءِ جنھن ڏينھن آسمان پڌرو دونھون آڻيندو (تنھن ڏينھن جو) تون انتظار ڪڍ.');
INSERT INTO "amroti" VALUES(44,11,'44:11','جو ماڻھن کي ويڙھي ويندو، اھو ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(44,12,'44:12','(پوءِ چوندا ته) اي اسان جا پالڻھار اسان کان اِھو عذاب ٽار (جو) بيشڪ اسين مُسلمان ٿيون ٿا.');
INSERT INTO "amroti" VALUES(44,13,'44:13','کين نصيحت وٺڻي ڪيئن ٿيندي حالانڪ بيشڪ وٽن پڌرو پيغمبر آيو ھو.');
INSERT INTO "amroti" VALUES(44,14,'44:14','وري کانئس مُنھن موڙيائون ۽ چيائون (ٿي) ته (ھيءُ) ماڻھو سيکاريل (۽) چريو آھي.');
INSERT INTO "amroti" VALUES(44,15,'44:15','بيشڪ اسين ٿورو (وقت) عذاب ٽارڻ وارا آھيون بيشڪ اوھين (وري ڪُفر ۾) ڦِرڻ وارا آھيو.');
INSERT INTO "amroti" VALUES(44,16,'44:16','جنھن ڏينھن تمام وڏي پڪڙ ڪنداسون (تنھن ڏينھن) بيشڪ اسين بدلو وٺڻ وارا آھيون.');
INSERT INTO "amroti" VALUES(44,17,'44:17','۽ بيشڪ کائن اڳ فرعون جي قوم جي آزمائش ڪئي سون ۽ وٽن (ھن ڳالھ سان) سڳورو پيغمبر آيو.');
INSERT INTO "amroti" VALUES(44,18,'44:18','ته الله جي ٻانھن کي منھنجي حوالي ڪريو، بيشڪ آءٌ اوھان وٽ امانت دار پيغمبر (آيو) آھيان.');
INSERT INTO "amroti" VALUES(44,19,'44:19','۽ (چوڻ آيو آھيان) ته الله جي آڏو ھٺ نه ڪريو، بيشڪ آءٌ اوھان وٽ پڌري حُجت آڻيندس.');
INSERT INTO "amroti" VALUES(44,20,'44:20','۽ بيشڪ مون پنھنجي پالڻھار ۽ اوھان جي پالڻھار جي پناھ ورتي ته (متان) مون کي پھڻن سان ماريو.');
INSERT INTO "amroti" VALUES(44,21,'44:21','۽ جيڪڏھن مون تي ايمان نه آڻيو ته مون کان پاسو ڪريو.');
INSERT INTO "amroti" VALUES(44,22,'44:22','پوءِ پنھنجي پالڻھار کي سڏيائين ته ھيءَ ڏوھاري قوم آھن.');
INSERT INTO "amroti" VALUES(44,23,'44:23','پوءِ (حُڪم ڪيوسون ته) منھنجن ٻانھن کي راتو رات وٺي نڪر جو اوھان جي پٺيان پوندا.');
INSERT INTO "amroti" VALUES(44,24,'44:24','۽ (تون) سمنڊ کي سڪل ڇڏ، بيشڪ اُھي ٻڏڻ وارا لشڪر آھن.');
INSERT INTO "amroti" VALUES(44,25,'44:25','باغ ۽ تلاءَ گھڻائي ڇڏيائون.');
INSERT INTO "amroti" VALUES(44,26,'44:26','۽ پوکون ۽ خاصيون جايون (به).');
INSERT INTO "amroti" VALUES(44,27,'44:27','۽ اُھي نعمتون (به) جنھن ۾ مزا ماڻيندا ھوا.');
INSERT INTO "amroti" VALUES(44,28,'44:28','اھڙي طرح (ٿيو)، ۽ ٻي قوم کي انھن (وسلن) جو وارث ڪيوسون.');
INSERT INTO "amroti" VALUES(44,29,'44:29','پوءِ مٿن آسمان ۽ زمين نه رُنو ۽ نڪي اُنھن کي مُلت ڏني ويئي.');
INSERT INTO "amroti" VALUES(44,30,'44:30','۽ بيشڪ بني اسرائيلن کي خوار ڪندڙ عذاب کان بچايوسون.');
INSERT INTO "amroti" VALUES(44,31,'44:31','جو فرعون کان (پھچندو) ھونِ، بيشڪ اُھو ھٺيلو حد کان لنگھندڙن مان ھو.');
INSERT INTO "amroti" VALUES(44,32,'44:32','۽ بيشڪ بني اسرائيلن کي ڄاڻي واڻي (ساري) جھان وارن کان پسند ڪيوسون.');
INSERT INTO "amroti" VALUES(44,33,'44:33','۽ کين اُھي مُعجزا ڏناسون جن ۾ پڌري آزمائش ھئي.');
INSERT INTO "amroti" VALUES(44,34,'44:34','بيشڪ اُھي (مُشرڪ) چوندا آھن.');
INSERT INTO "amroti" VALUES(44,35,'44:35','ته (اسان جي پڄاڻي) رڳو اسان جو (ھيءُ) پھريون موت آھي ۽ اسين وري نه اُٿارباسون.');
INSERT INTO "amroti" VALUES(44,36,'44:36','جيڪڏھن اوھين سچا آھيو ته اسان جي ابن ڏاڏن کي (جياري) آڻيو.');
INSERT INTO "amroti" VALUES(44,37,'44:37','اُھي ڀلا آھن يا تبع (حميري) جي قوم ۽ جيڪي کائن اڳ ھوا سي؟ اُنھن (سڀني) کي ناس ڪيوسون، بيشڪ اُھي ڏوھاري ھوا.');
INSERT INTO "amroti" VALUES(44,38,'44:38','۽ آسمان ۽ زمين کي ۽ جيڪي اُنھن جي وچ ۾ آھي سو راند ڪري نه بڻايوسون.');
INSERT INTO "amroti" VALUES(44,39,'44:39','انھن کي پوريءَ رِٿ کانسواءِ نه بڻايوسون پر انھن مان گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(44,40,'44:40','بيشڪ انھن مڙني جو انجام نبيري جو ڏينھن آھي.');
INSERT INTO "amroti" VALUES(44,41,'44:41','جنھن ڏينھن ڪوبه دوست ڪنھن دوست کان ڪجھ به (عذاب) نه ٽاريندو ۽ نڪي کين مدد ڏبي.');
INSERT INTO "amroti" VALUES(44,42,'44:42','سواءِ اُنھيءَ جي جنھن تي الله رحم ڪيو، بيشڪ الله ئي غالب مھربان آھي.');
INSERT INTO "amroti" VALUES(44,43,'44:43','بيشڪ ٿوھر جو وڻ.');
INSERT INTO "amroti" VALUES(44,44,'44:44','گنھگار جو کاڄ آھي.');
INSERT INTO "amroti" VALUES(44,45,'44:45','پگھريل ٽامي جان، پيٽن ۾ ٽچڪندو.');
INSERT INTO "amroti" VALUES(44,46,'44:46','تَتل پاڻي جي ٽچڪڻ وانگر.');
INSERT INTO "amroti" VALUES(44,47,'44:47','(ملائڪن کي چونداسون ته) ھن (ڏوھيءَ کي پڪڙيو) ۽ دوزخ جي وچ ڏانھن گھليوس.');
INSERT INTO "amroti" VALUES(44,48,'44:48','وري سندس مٿي تي تَتل پاڻي ھاريو جو (اِھو) عذاب (اٿس).');
INSERT INTO "amroti" VALUES(44,49,'44:49','(چئبس ته اِھا سزا) چَک ڇوته تون (پنھنجي گمان ۾) سگھورو مان وارو آھين.');
INSERT INTO "amroti" VALUES(44,50,'44:50','بيشڪ ھيءُ اُھو عذاب آھي جنھن ۾ اوھين شڪ ڪندا ھئو.');
INSERT INTO "amroti" VALUES(44,51,'44:51','بيشڪ پرھيزگار امن واريءَ جاءِ ۾ ھُوندا.');
INSERT INTO "amroti" VALUES(44,52,'44:52','باغن ۽ تلاون ۾.');
INSERT INTO "amroti" VALUES(44,53,'44:53','سنھي پَٽ جا (ڪپڙا) ۽ ٿُلھي پَٽ جا (ڪپڙا) ھڪ ٻئي جي آمھون سامھون ٿي ڍڪندا.');
INSERT INTO "amroti" VALUES(44,54,'44:54','(سندن حال) ائين ھوندو، ۽ کين وڏين (سھڻين) اکين واريون حورون پرڻائينداسون.');
INSERT INTO "amroti" VALUES(44,55,'44:55','منجھس سڀڪو ميوو گھرندا اھڙي حال ۾ جو امن وارا ھوندا.');
INSERT INTO "amroti" VALUES(44,56,'44:56','پھرين موت کانسواءِ (جو دنيا ۾ مري چڪا) اتي (ٻيو) موت نه چکندا، ۽ (الله) کين دوزخ جي عذاب کان بچايو.');
INSERT INTO "amroti" VALUES(44,57,'44:57','تنھنجي پالڻھار جي فضل سبب، اھائي وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(44,58,'44:58','قرآن کي تنھنجيءَ ٻوليءَ ۾ آسان ڪيوسون ته مانَ اُھي نصيحت وٺن.');
INSERT INTO "amroti" VALUES(44,59,'44:59','پوءِ (تون) انتظار ڪڍ بيشڪ اُھي به انتظار ڪڍڻ وارا آھن.');
INSERT INTO "amroti" VALUES(45,1,'45:1','حٰمٓ.');
INSERT INTO "amroti" VALUES(45,2,'45:2','ڪتاب جو لاھڻ الله غالب حڪمت واري کان آھي.');
INSERT INTO "amroti" VALUES(45,3,'45:3','بيشڪ آسمانن ۽ زمين ۾ ايمان وارن لاءِ پڪيون نشانيون آھن.');
INSERT INTO "amroti" VALUES(45,4,'45:4','۽ اوھان جي بڻائڻ ۾ ۽ جيڪي چرندڙن مان پکيڙي ٿو تنھن (جي بڻائڻ) ۾ اُنھن ماڻھن لاءِ نشانيون آھن جيڪي يقين رکندا آھن.');
INSERT INTO "amroti" VALUES(45,5,'45:5','۽ رات ۽ ڏينھن جي ڦيرگھير ۾ ۽ جيڪي الله آسمان کان روزي نازل ڪئي پوءِ اُن سببان زمين کي سندس غيرآباديءَ کانپوءِ آباد ڪيائين تنھن ۾ ۽ وائن جي ھلائڻ ۾ اُنھن ماڻھن لاءِ نشانيون آھن جيڪي سمجھ رکندا آھن.');
INSERT INTO "amroti" VALUES(45,6,'45:6','اھي الله جون آيتون آھن جي توکي سچ سان پڙھي ٻڌايون ٿا، پوءِ الله ۽ سندس آيتن پڃاڻا ڪھڙي ڳالھ تي ايمان آڻيندا.');
INSERT INTO "amroti" VALUES(45,7,'45:7','ھر ڪنھن ٻٽاڪيءَ گنھگار لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(45,8,'45:8','الله جون آيتون کيس پڙھي ٻڌائبيون آھن ته ٻڌندو آھي وري آڪڙجي ضد ڪندو آھي ڄڻڪ اُھي ٻڌيون ئي نه ھوائين، تنھنڪري کيس ڏکوئيندڙ عذاب جي خوشخبري سُڻاءِ.');
INSERT INTO "amroti" VALUES(45,9,'45:9','۽ جڏھن اسان جي آيتن مان ڪنھن ڳالھ تي واقف ٿيندو آھي (تڏھن) ان کي ٺـٺوليون ڪري وٺندو آھي، اُنھن ئي لاءِ خوار ڪندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(45,10,'45:10','سندن ٻئي طرف کان دوزخ آھي، ۽ جيڪي ڪمايائون نڪي اُھو کائن ڪجھ ٽاري سگھندو ۽ نڪي اُھي جن کي الله ڌاران سڄڻ ڪري ورتائون (کائن ٽاري سگھندا)، ۽ اُنھن لاءِ وڏو عذاب آھي.');
INSERT INTO "amroti" VALUES(45,11,'45:11','ھيءُ (قرآن) ھدايت آھي، ۽ جن پنھنجي پالڻھار جي آيتن جو انڪار ڪيو تن کي سخت سزا جو ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(45,12,'45:12','الله اُھو آھي جنھن درياءُ اوھان جي تابع ڪيو ته سندس حُڪم سان منجھس ٻيڙيون ھلن ۽ سندس فضل مان گذران طلب ڪريو ۽ مانَ اوھين شڪرانو ڪريو.');
INSERT INTO "amroti" VALUES(45,13,'45:13','۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو سڀئي پنھنجي پار کان اوھان جي ڪم ۾ لڳايائين، بيشڪ اُن ۾ اُنھن ماڻھن لاءِ نشانيون آھن جي پروڙيندا آھن.');
INSERT INTO "amroti" VALUES(45,14,'45:14','مؤمنن کي چؤ ته جيڪي الله جي واقعن جي اُميد نه رکندا آھن تن کان ھن ڪري ٽارو ڪن ته اُنھي سببان جو جيڪي ڪمايو ھوائون (انھن مان) ڪنھن قوم کي (الله) سزا ڏئي.');
INSERT INTO "amroti" VALUES(45,15,'45:15','جيڪو چڱا ڪم ڪندو سو پاڻ لاءِ، ۽ جيڪو مدائي ڪندو تنھن تي (اُن جو نقصان) آھي، وري پنھنجي پالڻھار ڏانھن موٽايا ويندؤ.');
INSERT INTO "amroti" VALUES(45,16,'45:16','۽ بيشڪ بني اسرائيلن کي ڪتاب ۽ دانائي ۽ پيغمبري ڏني سون ۽ سٺين شين مان کين روزي ڏني سون ۽ کين جھان وارن کان ڀلارو ڪيوسون.');
INSERT INTO "amroti" VALUES(45,17,'45:17','۽ دين بابت کين پڌرا دليل ڏناسون، پوءِ انھن (اڳي) اختلاف نه ڪيو پر وٽن ڄاڻپ آئي تنھن کانپوءِ پاڻ ۾ ساڙ (پيل) سببان (اختلاف ڪيائون)، بيشڪ جنھن ڳالھ بابت جھڳڙو ڪندا ھوا تنھن بابت قيامت جي ڏينھن تنھنجو پالڻھار سندن وچ ۾ نبيرو ڪندو.');
INSERT INTO "amroti" VALUES(45,18,'45:18','وري (اي پيغمبر) توکي دين جي پڌريءَ واٽ تي ڪيوسون تنھنڪري (تون) اُن دين جي تابعداري ڪر ۽ جيڪي نه ڄاڻندا آھن تن جي سَڌن پٺيان نه لڳ.');
INSERT INTO "amroti" VALUES(45,19,'45:19','ڇوته اِھي الله جي آڏو توکان ڪجھ به ٽاري نه سگھندا، ۽ بيشڪ ظالم ھڪ ٻئي جا دوست آھن، ۽ الله پرھيزگارن جو دوست آھي.');
INSERT INTO "amroti" VALUES(45,20,'45:20','ھيءُ (قرآن) ماڻھن لاءِ چٽا دليل آھن جيڪي ماڻھو يقين رکندا آھن تن لاءِ ھدايت ۽ ٻاجھ آھي.');
INSERT INTO "amroti" VALUES(45,21,'45:21','جن مدايون ڪيون سي ڀائيندا آھن ڇا ته کين انھن وانگر ڪنداسون جن ايمان آندو ۽ چڱا ڪم ڪيا؟ ته سندن جيئڻ ۽ مرڻ ھڪ جھڙو ھوندو، (اھو) جيڪو فيصلو ڪندا آھن سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(45,22,'45:22','۽ الله آسمانن ۽ زمين کي پوري رِٿ سان بڻايو ۽ ھن لاءِ ته ھر ڪنھن شخص جيڪي ڪمايو تنھن بابت کيس بدلو ڏجي ۽ مٿن ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(45,23,'45:23','(اي پيغمبر) اُنھيءَ کي نه ڏٺو اٿيئي ڇا؟ جنھن پنھنجي (نفس جي) سَڌ کي معبود ڪري ورتو ۽ ڄاڻڻ ھوندي الله کيس گمراھ ڪيو ۽ سندس ڪنّن تي ۽ سندين دل تي مُھر ھنيائين ۽ سنديس اکين تي پردو وڌائين، پوءِ الله ڌاران کيس ڪير ھدايت ڪندو، پوءِ ڇونه نصيحت وٺندا آھيو؟');
INSERT INTO "amroti" VALUES(45,24,'45:24','۽ چوندا آھن ته اسان جي رڳو ھي دنيا جي حياتي آھي جو مرندا آھيون ۽ جيئندا آھيون ۽ اسان کي رڳو زمانو ماريندو آھي، ۽ ان (ڳالھ) جي کين ڪا ڄاڻپ نه آھي، اُھي رڳو اٽڪل ھڻندا آھن.');
INSERT INTO "amroti" VALUES(45,25,'45:25','۽ جڏھن اسان جون پڌريون آيتون کين پڙھي ٻڌائبيون آھن (تڏھن) سندين حجت رڳو ھي چوڻ ھوندو آھي ته جيڪڏھن اوھين سچا آھيو ته اسان جي پيءُ ڏاڏن کي (جيئرو ڪري) آڻيو.');
INSERT INTO "amroti" VALUES(45,26,'45:26','(اي پيغمبر کين) چؤ ته الله اوھان کي جياريندو آھي وري اوھان کي ماريندو وري اوھان کي قيامت جي ڏينھن ۾ ڪو شڪ نه آھي گڏ ڪندو پر گھڻا ماڻھو (اِن ڳالھ کي) نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(45,27,'45:27','۽ آسمانن ۽ زمين جي بادشاھي الله جي آھي، ۽ جنھن ڏينھن قيامت ٿيندي تنھن ڏينھن ڪُوڙا ڇيئو پائيندا.');
INSERT INTO "amroti" VALUES(45,28,'45:28','۽ (اي پيغمبر) ھر ڪنھن ٽوليءَ کي گوڏن ڀر (ويٺل) ڏسندين، سڀڪا ٽولي پنھنجي اعمالنامي ڏانھن سڏبي، (چئبو) ته جيڪي (اوھين) ڪندا ھئو تنھن جو اڄ اوھان کي بدلو ڏبو.');
INSERT INTO "amroti" VALUES(45,29,'45:29','ھي اسان جو دفتر آھي جو اوھان تي پوري حقيقت پڌري ڪري ٿو، بيشڪ جيڪي اوھين ڪندا ھئو سو اسين لکندا ھواسون.');
INSERT INTO "amroti" VALUES(45,30,'45:30','پوءِ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي سندن پالڻھار پنھنجي ٻاجھ ھيٺ داخل ڪندو، اھا پڌري مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(45,31,'45:31','۽ جن ڪُفر ڪيو (تن کي چئبو) ته اوھان وٽ منھنجيون آيتون نه پڙھبيون ھيون ڇا؟ پوءِ اوھان وڏائي ڪئي ۽ ڏوھاري قوم ھئو.');
INSERT INTO "amroti" VALUES(45,32,'45:32','۽ جڏھن چئبو ھُيَوَ ته الله جو وعدو سچو آھي ۽ قيامت ۾ ڪو شڪ نه آھي (تڏھن) چوندا ھئو ته (اسين) نه ڄاڻندا آھيون ته قيامت ڇا آھي اسين رڳو ھڪ (ٿورو) گمان ڀائيندا آھيون ۽ اسين (ڪڏھن) يقين ڪرڻ وارا نه آھيون.');
INSERT INTO "amroti" VALUES(45,33,'45:33','۽ جيڪي ڪمايو ھوائون تنھن جون مدايون کين پڌريون ٿينديون ۽ جنھن شيءَ تي چٿرون ڪندا ھوا سا کين وڪوڙي ويندي.');
INSERT INTO "amroti" VALUES(45,34,'45:34','۽ چئبو ته اڄ اوھان کي (ائين) وسارينداسون جيئن اوھان پنھنجي ھن ڏينھن جي اچي پھچڻ کي وساريو ھو ۽ (ھاڻي) اوھان جي جاءِ دوزخ آھي ۽ اوھان جو ڪو مددگار نه آھي.');
INSERT INTO "amroti" VALUES(45,35,'45:35','اِھو (عذاب اوھان کي) ھن ڪري آھي جو اوھان الله جي آيتن کي ٺـٺوليون ڪري ورتو ھو ۽ دنيوي حياتيءَ اوھان کي ٺڳيو ھو، تنھنڪري اڄ منجھانئس نه ڪڍبن ۽ نڪي عذر طلب ڪبن.');
INSERT INTO "amroti" VALUES(45,36,'45:36','پوءِ سڀ ساراھ خاص الله کي جڳائي جو آسمانن جو پالڻھار ۽ زمين جو پالڻھار ۽ جھانن جو پالڻھار آھي.');
INSERT INTO "amroti" VALUES(45,37,'45:37','۽ آسمانن ۽ زمين ۾ سنديس وڏائي آھي، ۽ اُھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(46,1,'46:1','حٰمٓ.');
INSERT INTO "amroti" VALUES(46,2,'46:2','ڪتاب جو لاھڻ غالب حڪمت واري الله وٽان آھي.');
INSERT INTO "amroti" VALUES(46,3,'46:3','نه پيدا ڪيوسون آسمانن ۽ زمين کي ۽ جيڪي انھن جي وچ ۾ آھي مگر پوريءَ رِٿ سان ۽ ھڪ مقرر مدت تائين (رھڻ لاءِ)، ۽ ڪافر جنھن کان ڊيڄاريا ويا (تنھن کان) مُنھن موڙيندڙ آھن.');
INSERT INTO "amroti" VALUES(46,4,'46:4','(کين) چؤ ته ڏسيو ته سھي الله کانسواءِ جن کي سڏيندا آھيو تن ڪا بڻاوت بڻائي آھي ڇا مون کي ڏيکاريو ته زمين (جي شين) منجھان ڪھڙي شيء پيدا ڪئي اٿن يا آسمانن (جي بڻائڻ) ۾ انھن جي ڪا ڀائيواري (ثابت) آھي ڇا؟ جيڪڏھن سچا آھيو ته ھن کان اڳ (آيل) ڪوبه ڪتاب يا ڪا علمي سَند مون وٽ آڻيو.');
INSERT INTO "amroti" VALUES(46,5,'46:5','۽ اُنھيءَ کان وڌيڪ گمراھ ڪير آھي جيڪو الله ڌاران (ٻئي) اھڙي شيء کي سڏي جو قيامت تائين سندس سڏ کي نه ورنائي ۽ اُھي سندن سڏڻ کان (ئي) بي خبر آھن.');
INSERT INTO "amroti" VALUES(46,6,'46:6','۽ جڏھن (قيامت ۾) ماڻھو گڏ ڪبا (تڏھن) اھي سندن ويري ھوندا ۽ سندين پوڄا جا منڪر ھوندا.');
INSERT INTO "amroti" VALUES(46,7,'46:7','۽ جڏھن اسان جون پڌريون آيتون کين پڙھي ٻڌائبيون آھن (تڏھن اُھي) ڪافر سچي ڳالھ (يعني قرآن) به نسبت جنھن مھل وٽن آيو (تنھن مھل) چوندا آھن ته ھي پڌرو جادو آھي.');
INSERT INTO "amroti" VALUES(46,8,'46:8','بلڪ چوندا آھن ته اُن کي (پيغمبر) ٺاھيو آھي، (کين) چؤ ته جيڪڏھن اُن کي پاڻ ٺاھيو اٿم ته اوھين الله کان (ڇڏائڻ واسطي) مون لاءِ ڪجھ ڪري نٿا سگھو، جيڪي قرآن بابت گفتگو ڪندا آھيو (سو) الله چڱي طرح ڄاڻندڙ آھي، منھنجي ۽ اوھان جي وچ ۾ (خدا) حق کي ظاھر ڪندڙ بس آھي، ۽ اُھو بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(46,9,'46:9','چؤ ته آءٌ پيغمبرن مان ڪو نئون (آيل) نه آھيان ۽ نٿو ڄاڻان ته مون سان ۽ اوھان سان (دنيا ۾) ڇا ڪيو ويندو، جيڪي مون ڏانھن وحي ڪيو ويندو آھي تنھن کانسواءِ (ٻيءَ) ڪنھن (ڳالھ) جي تابعداري نه ڪندو آھي ۽ آءٌ رڳو پڌرو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(46,10,'46:10','(کين) چؤ ته ڏسو ته سھي جيڪڏھن (اھو قرآن) الله وٽان آيل آھي ۽ اوھين اُن کي نه مڃيو ۽ بني اسرائيلن مان ھڪ شاھد اھڙي (ڪتاب) تي شاھدي ڏني ھجي پوءِ اُنھيءَ ايمان آندو ۽ اوھان (مڃڻ کان) وڏائي ڪئي (ته ضرور ظالم ٿيندؤ) بيشڪ الله ظالمن جي قوم کي سِڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(46,11,'46:11','۽ ڪافرن مؤمنن به نسبت چيو ته جيڪڏھن (اھو دين) چڱو ھجي ھا ته اُھي اُن ڏانھن اسان کان اڳرائي نه ڪن ھا، ۽ جڏھن (ڪافرن) اُن سان واٽ نه لڌي تڏھن چوندا ته ھيءُ (قرآن) ھڪ پراڻو ڪوڙ آھي.');
INSERT INTO "amroti" VALUES(46,12,'46:12','۽ ھن (قرآن) کان اڳ مُوسىٰ جو ڪتاب اڳواڻ ۽ ٻاجھ ھو، ۽ ھي ڪتاب (ان جو) سچّو ڪندڙ عربيءَ ٻوليءَ ۾ آھي ھن لاءِ ته ظالمن کي ڊيڄاري، ۽ ڀلارن کي خوشخبري ڏئي.');
INSERT INTO "amroti" VALUES(46,13,'46:13','بيشڪ جن چيو ته اسان جو پالڻھار الله آھي پوءِ اِن (چوڻ) تي مُحڪم رھيا تن کي نڪو ڀَؤ آھي ۽ نڪي اُھي غمگين ھوندا.');
INSERT INTO "amroti" VALUES(46,14,'46:14','اِھي بھشتي آھن منجھس سدائين رھڻ وارا آھن، جيڪي عمل ڪندا ھوا تنھنجو بدلو آھي.');
INSERT INTO "amroti" VALUES(46,15,'46:15','۽ ماڻھوءَ کي پنھنجي ماءُ پيءُ سان چڱائي ڪرڻ جو حُڪم ڪيوسون، (جو) اوکائيءَ سان سنديس ماءُ کيس (پيٽ ۾) کنيو آھي ۽ اوکائيءَ سان کيس ڄڻيو آھي، ۽ اُنجو (پيٽ ۾) کڻڻ ۽ اُن جو (کير کان) ڇڏائڻ ٽيھ مھينا آھي، تان جو جڏھن پنھنجيءَ جواني کي پُھتو ۽ چاليھن ورھن کي پھتو (تڏھن) چوندو آھي ته اي منھنجا پالڻھار مون کي توفيق ڏي ته تنھنجي انھيءَ نعمت جو شڪر ڪريان جيڪا مون تي ۽ منھنجي ماءُ پيءُ تي انعام ڪئي اٿيئي ۽ ته (اھڙا) چڱا ڪم ڪريان جن کان راضي رھين ۽ منھنجي اولاد مون لاءِ سُڌار، بيشڪ آءٌ توڏانھن موٽيس ۽ بيشڪ آءٌ مسلمانن منجھان آھيان.');
INSERT INTO "amroti" VALUES(46,16,'46:16','اِھي اُھي آھن جن جا تمام چڱا ڪم جيڪي ڪيائون سي قبول ڪندا آھيون ۽ سندين مداين کان ٽرندا آھيون (اھي) بھشتين ۾ ھوندا، جيڪو ساڻن سچو وعدو ڪبو ھو تنھن جي ڪري.');
INSERT INTO "amroti" VALUES(46,17,'46:17','۽ جنھن پنھنجي ماءُ پيءُ کي چيو ته اوھان کان بيزار آھيان مون کي انجام ڏيو ٿا ڇا ته (قبرن مان) ڪڍيو ويندس ۽ بيشڪ مون کان اڳ (ماڻھن جا) ڪيترائي جُڳ گذري ويا آھن، ۽ اُھي ٻئي الله کي ٻاڏائيندا آھن ۽ (چوندا اٿس ته) پنھنجي ڪم بختي ڇڏي ايمان آڻ، بيشڪ الله جو انجام سچو آھي، پوءِ چوندو آھي ته اِھي رڳو اڳوڻن جو آکاڻيون آھن.');
INSERT INTO "amroti" VALUES(46,18,'46:18','اِھي اُھي آھن جن تي اھڙي سزا لازم ٿي (جھڙي اُنھن) ٽولين ۾ ٿي جي جِنن ۽ ماڻھن مان کائن اڳ گذري ويا، بيشڪ اُھي ڇيئي وارا ھوا.');
INSERT INTO "amroti" VALUES(46,19,'46:19','۽ سڀڪنھن لاءِ درجا انھيءَ آھر آھن جيڪي ڪم ڪيائون، ۽ (ھيُ ھِن لاءِ) ته (خُدا) سندن عملن جو کين پورو بدلو ڏئي ۽ مٿن ظلم نه ڪبو.');
INSERT INTO "amroti" VALUES(46,20,'46:20','۽ انھيءَ ڏينھن جو ڪافر باھ جي آڏو ڪبا، (چئبن ته) پنھنجون نعمتون پنھنجي دنيائي حياتيءَ ۾ وٺي چڪؤ ۽ اُن سان آسودا رھيؤ، تنھنڪري اڄ اوھان کي خواريءَ جي عذاب جو بدلو انھي ڪري ڏبو جو زمين ۾ ناحق وڏائي ڪندا ھئو ۽ انھيءَ ڪري جو بدڪاري ڪندا ھئو.');
INSERT INTO "amroti" VALUES(46,21,'46:21','۽ عاد جي ڀاءُ (ھود) کي ياد ڪر، جڏھن پنھنجي قوم کي (واديءَ) اَحقاف ۾ ڊيڄاريائين ۽ بيشڪ سندس اڳيان ۽ سندس پوئتان (ڪيترائي) ڊيڄاريندڙ گذريا ھوا ته الله کانسواءِ ڪنھين جي عبادت نه ڪريو، بيشڪ آءٌ اوھان تي وڏي ڏينھن جي عذاب کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(46,22,'46:22','چيائون ته اسان وٽ ھن لاءِ آيو آھين ڇا ته اسان کي پنھنجن معبودن کان ڦيرائن، پوءِ جيڪڏھن سچن مان آھين ته جيڪو وعدو اسان کي ڏئين ٿو سو اسان وٽ آڻ.');
INSERT INTO "amroti" VALUES(46,23,'46:23','چيائين ته اِھو علم رڳو الله کي آھي، ۽ جنھن (پيغام) سان موڪليو ويو آھيان سو اوھان کي پھچايان ٿو پر اوھان کي ھڪ جاھل قوم ڏسان ٿو.');
INSERT INTO "amroti" VALUES(46,24,'46:24','پوءِ جنھن مھل اُن کي ڪڪر (جھڙو) ڏٺائون جو سندن ميدانن ڏانھن سامھون ايندڙ ھو (تنھن مھل) چيائون ته ھيءُ ڪڪر آھي جو اسان تي وَسندو، (ھود چيو ته نه نه!) بلڪ اِھو اُھو آھي جنھن کي جلدي گھريؤ، (اھا) ھڪ آنڌي آھي جنھن ۾ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(46,25,'46:25','(جو) پنھنجي پالڻھار جي حُڪم سان سڀڪنھن شيءَ کي ناس ڪندي پوءِ (اھڙا ناس) ٿيا جو سندن گھرن کانسواءِ ڪجھ نه ٿي ڏٺو ويو، اھڙي طرح ڏوھاري قوم کي سزا ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(46,26,'46:26','۽ بيشڪ انھن کي اھڙي جاءِ ڏني ھئي سون جو اوھان کي به جاءِ نه ڏني سون ۽ انھن کي ڪنّ ۽ اکيون ۽ دليون ڏنيون ھيون سون، پوءِ جڏھن الله جي آيتن جو انڪار ڪيائون تڏھن نڪي سندن ڪَنن ۽ نڪي سندن اکين ۽ نڪي سندن دلين ڪجھ به (عذاب) کائن ٽاريو ۽ جنھن (عذاب) بابت ٺـٺوليون ڪندا ھوا سو مٿن ڪڙڪيو.');
INSERT INTO "amroti" VALUES(46,27,'46:27','۽ بيشڪ جيڪي ڳوٺ اوھان جي آس پاس آھن سي (سڀ) ناس ڪياسون ۽ طرحين طرحين نشانيون پڌريون ڪيون سون ته مانَ اُھي موٽن.');
INSERT INTO "amroti" VALUES(46,28,'46:28','پوءِ الله کانسواءِ جن کي ويجھائي لاءِ معبود ڪري ورتو ھوائون تن کين مدد ڇونه ڏني ھئي، بلڪ کائن گم ٿي ويا، ۽ اھو سندن ڪُوڙ آھي جيڪو ٺاھ ٺاھيندا ھوا.');
INSERT INTO "amroti" VALUES(46,29,'46:29','۽ (ياد ڪر) جڏھن جِنّن مان ھڪ ٽوليءَ کي توڏانھن لاڙيوسون جو قرآن ٻڌائون ٿي، پوءِ جنھن مھل پيغمبر وٽ پھتا چيائون ته ماٺ ڪريو، پوءِ جنھن مھل پورو ڪيو ويو ته پنھنجي قوم ڏانھن ڊيڄاريندڙ ٿي موٽيا.');
INSERT INTO "amroti" VALUES(46,30,'46:30','چيائون ته اي اسان جي قوم بيشڪ اسان اھڙو ڪتاب ٻڌو جو ُموسىٰ کانپوءِ لاٿو ويو آھي جو جيڪي اُن کان اڳ (نازل ٿيل) ھو تنھن کي سچو ڪندڙ آھي سچي دين ڏانھن ۽ سِڌي واٽ ڏانھن رستو ڏيکاري ٿو.');
INSERT INTO "amroti" VALUES(46,31,'46:31','اي اسان جي قوم الله جي (طرف) سڏيندڙ کي سڏ ڏيو ۽ اُن تي ايمان آڻيو ته (الله) اوھان جا ڪي ڏوھ اوھان کي بخشي ۽ ڏکوئيندڙ عذاب کان اوھان کي ڇڏائي.');
INSERT INTO "amroti" VALUES(46,32,'46:32','۽ جيڪو الله ڏانھن سڏيندڙ کي سڏ نه ڏيندو سو مُلڪ ۾ (ڀڄي) ٿَڪائڻ وارو نه آھي ۽ الله کانسواءِ اُن جا ڪي سڄڻ نه آھن، اُھي پڌريءَ گمراھي ۾ آھن.');
INSERT INTO "amroti" VALUES(46,33,'46:33','نه ڏٺو اٿن ڇا ته الله جنھن آسمانن ۽ زمين کي بڻايو آھي ۽ انھن جي بڻائڻ ۾ نه ٿڪو سو ھن (ڳالھ) تي وس وارو آھي ته مُئن کي جياري، ھائو! بيشڪ اِھو سڀڪنھن شيءَ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(46,34,'46:34','۽ جنھن ڏينھن ڪافر باھ جي سامھون آڻبا، (تنھن ڏينھن چئبن ته) ھي (وعدو) سچ نه آھي ڇا؟ چوندا ھائو! اسان جي پالڻھار جو قسم آھي، (الله) چوندو ته انھي ڪري عذاب چکو جو انڪار ڪندا ھئو.');
INSERT INTO "amroti" VALUES(46,35,'46:35','پوءِ (اي پيغمبر) جيئن وڏي ھمت وارن پيغمبرن صبر ڪيو ھو تئين صبر ڪر ۽ اُنھن لاءِ (عذاب) جلد نه گھُر، جنھن (قيامت) جو کين انجام ڏجي ٿو سو جنھن ڏينھن ڏسندا تنھن ڏينھن (ائين ڀائيندا ته دنيا ۾) ڄڻڪ اُھي ھڪ گھڙيءَ کانسواءِ رھيائي نه ھوا، (اھُو پيغام) پھچائڻو آھي، پوءِ بدڪارن جي قوم کانسواءِ ڪو ھلاڪ نه ڪبو.');
INSERT INTO "amroti" VALUES(47,1,'47:1','جن ڪُفر ڪيو ۽ الله جي واٽ کان جھليو تن جي عملن کي (الله) چَٽ ڪيو.');
INSERT INTO "amroti" VALUES(47,2,'47:2','۽ جن ايمان آندو ۽ چڱا ڪم ڪيا ۽ جيڪي مُحمّدؑ تي نازل ڪيو ويو ۽ اُھو سندن پالڻھار وٽان سچ آھي تنھن کي مڃيائون تن جون مدايون کائن ميٽيائين ۽ سندن حال کي سُڌاريائين.');
INSERT INTO "amroti" VALUES(47,3,'47:3','اِھو ھن ڪري آھي جو ڪافر ڪُوڙ جي پٺيان لڳا ۽ مؤمن پنھنجي پالڻھار جي سچي دين جي پيروي ڪئي، اھڙيءَ طرح ماڻھن لاءِ سندن احوال بيان ڪندو آھي.');
INSERT INTO "amroti" VALUES(47,4,'47:4','پوءِ جڏھن اوھين ڪافرن سان جنگ ڪريو تڏھن (انھن جون) گردنون ڪٽيو، تانجو جڏھن منجھن گھڻو رت ڇاڻ ڪيوَ تڏھن مضبوطيءَ سان قيد ٻڌو وري (قيد کان) پوءِ يا ته احسان ڪرڻ (سان ڇڏڻ) يا چَٽي وٺڻ (گھُرجي) تانجو جنگ پنھنجا ھٿيار رکي، اِھو (حُڪم) آھي، ۽ جيڪڏھن الله گھري ھا ته کائن بدلو وٺي ھا پر (گھرندو آھي) ته اوھان مان ڪن کي ڪن سان پرکي، ۽ جيڪي الله جي واٽ ۾ قتل ٿيا تن جا عمل ڪڏھن چَٽ نه ڪندو.');
INSERT INTO "amroti" VALUES(47,5,'47:5','کين سڌو رستو ڏيکاريندو ۽ سندن حال سڌاريندو.');
INSERT INTO "amroti" VALUES(47,6,'47:6','۽ کين اُنھيءَ بھشت ۾ داخل ڪندو جيڪو کين ڄاڻايو اٿس.');
INSERT INTO "amroti" VALUES(47,7,'47:7','اي ايمان وارؤ جيڪڏھن اوھين الله (جي دين) جي مدد ڪندؤ ته (الله) اوھان جي مدد ڪندو ۽ اوھان جا قدم مُحڪم ڪندو.');
INSERT INTO "amroti" VALUES(47,8,'47:8','۽ جن انڪار ڪيو تن کي شال ھلاڪي ھجي ۽ الله سندن عملن کي چَٽ ڪيو.');
INSERT INTO "amroti" VALUES(47,9,'47:9','اِھا (سزا) ھِن ڪري آھي جو جيڪي الله نازل ڪيو آھي تنھنکي انھن ناپسند ڪيو تنھن ڪري (الله) سندن عملن کي چٽ ڪيو.');
INSERT INTO "amroti" VALUES(47,10,'47:10','زمين ۾ نه گھميا آھن ڇا ته نھارين ته جيڪي کائن اڳ ھوا تن جي پڇاڙي ڪيئن ٿي، ۽ الله مٿن ھلاڪي نازل ڪئي، ۽ ڪافرن لاءِ اھڙيون (ئي سزائون) آھن.');
INSERT INTO "amroti" VALUES(47,11,'47:11','اِھو ھن ڪري آھي جو الله مؤمنن جو ڪارساز آھي ۽ (ھن ڪري) ته ڪافرن جو ڪو سڄڻ نه آھي.');
INSERT INTO "amroti" VALUES(47,12,'47:12','جن ايمان آندو آھي ۽ چڱا ڪم ڪيا آھن تن کي بيشڪ الله اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن، ۽ جن انڪار ڪيو (سي) دنيا جو نفعو وٺندا آھن ۽ ائين کائيندا آھن جئين ڍور کائيندا آھن ۽ سندين جاءِ باھ آھي.');
INSERT INTO "amroti" VALUES(47,13,'47:13','۽ (اي پيغمبر) تنھنجي ڳوٺ جنھن (جي ماڻھن) توکي ڪڍي ڇڏيو تنھن کان وڌيڪ سگھ وارا ڪيترائي ڳوٺ ھوا، جو اُنھن کي ناس ڪيوسون پوءِ انھن جو ڪو واھرو نه ھو.');
INSERT INTO "amroti" VALUES(47,14,'47:14','جيڪو پنھنجي پالڻھار کان پڌريء حُجت تي ھجي سو اُنھيءَ جھڙو آھي ڇا؟ جنھن کي سندس بڇڙا ڪم چڱا ڪري ڏيکاريا ويا ۽ پنھنجين سَڌن جي پٺيان لڳا.');
INSERT INTO "amroti" VALUES(47,15,'47:15','جنھن بھشت جو پرھيزگارن کي انجام ڏنو ويو آھي تنھنجي وصف ھي آھي جو اُن ۾ اھڙي پاڻي جون واھيون آھن جو (گھڻي رھڻ ڪري) نه سينواريو آھي، ۽ اھڙي کير جون واھيون آھن جنھن جو مزو نه مَٽيو آھي، ۽ پيئندڙن کي لذت ڏيندڙ شراب جون واھيون آھن، ۽ صفا ڪيل ماکيءَ جون به واھيون آھن، ۽ اُنھن لاءِ منجھس ھر جنس جا ميوا آھن ۽ سندن پالڻھار وٽان بخشش آھي، (اھي) انھن جھڙا آھن ڇا جيڪو باھ ۾ سدائين رھڻ وارو ھجي ۽ جن کي ڪوسو پاڻي پياريو وڃي جو سندن آنڊن کي ڇِني ڇڏي.');
INSERT INTO "amroti" VALUES(47,16,'47:16','۽ منجھائن ڪو اھڙو آھي جو توڏانھن ڪن ڏيندو آھي، تان جو جنھن مھل تو وٽان نڪرندا آھن (تنھن مھل) جن کي علم ڏنو ويو تن کي چوندا آھن ته ھاڻي (پيغمبر) ڇا چيو؟ اِھي اُھي آھن جن جي دلين تي الله مُھر ھنئي آھي ۽ جيڪي پنھنجي سَڌن تي ھليا.');
INSERT INTO "amroti" VALUES(47,17,'47:17','۽ جن سِڌو رستو ورتو تن کي خدا ھدايت ۾ وڌايو ۽ کين سندين پرھيزگاري ڏني.');
INSERT INTO "amroti" VALUES(47,18,'47:18','(ڪافر) پاڻ وٽ قيامت جي اوچتي اچڻ کانسواءِ (ٻي ڳالھ) منتظر نه آھن، پوءِ بيشڪ اُن جون نشانيون اچي چڪيون آھن، پوءِ جڏھن وٽن (قيامت) ايندي تڏھن کين نصيحت وٺڻ ڪٿان (نفعي واري ٿيندي).');
INSERT INTO "amroti" VALUES(47,19,'47:19','پوءِ ڄاڻ ته الله کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي ۽ پنھنجن گناھن جي ۽ مؤمنن ۽ مؤمنياڻين لاءِ بخشش گھُر، ۽ الله اوھان جي اچ وڃ جو ھنڌ ۽ اوھان جي رھڻ جي جڳھ ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(47,20,'47:20','۽ مؤمن چوندا آھن ته ڪا سُورة ڇونه نازل ڪئي وئي، پوءِ جڏھن پڌري معنىٰ واري سُورة نازل ڪئي وڃي ۽ اُن ۾ جھاد جو ذڪر ڪيو وڃي ته جن جي دلين ۾ بيماري آھي تن کي ڏسندين ته تو ڏانھن اُنھيءَ (ماڻھو) جي نھارڻ وانگر ڏسندا آھن جنھن کي موت (جي سڪرات) کان بيھوشي پُھتي ھجي، پوءِ اُنھن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(47,21,'47:21','(چڱي ڳالھ ته کين) چيو وٺڻ ۽ چڱي ڳالھ چوڻ آھي، پوءِ جڏھن (جھاد جو) ڪم پڪو ٿي چڪي، ته جيڪڏھن جيڪڏھن الله سان سچا رھن ھا اُنھن لاءِ ڀلو ھجي ھا.');
INSERT INTO "amroti" VALUES(47,22,'47:22','پوءِ (اي ضعيف ايمان وارؤ) جيڪڏھن اسلام کان ڦريئو ته (اوھين) ھن (ڳالھ) کي ويجھا آھيو ته زمين ۾ فساد ڪندؤ ۽ پنھنجيون مائٽيون ڇنندؤ.');
INSERT INTO "amroti" VALUES(47,23,'47:23','اِھي اُھي آھن جن کي الله لعنت ڪئي پوءِ کين ٻوڙو ڪيائين ۽ سندين اکيون انڌيون ڪيائين.');
INSERT INTO "amroti" VALUES(47,24,'47:24','ڇا قرآن ۾ ڌيان نه ڪندا آھن يا (سندين) دلين تي اُنھن جا قُفل لڳل آھن؟');
INSERT INTO "amroti" VALUES(47,25,'47:25','بيشڪ جيڪي (ماڻھو) اُن کانپوءِ جو کين ھدايت پڌري ٿي پنھنجين پٺين ڀر ڦريا شيطان (اھو ڪم) چڱو ڪري ڏيکاريو آھي، ۽ کين مھلت (ھجڻ) جو وعدو ڏنو اٿس.');
INSERT INTO "amroti" VALUES(47,26,'47:26','اھو ھن ڪري آھي ته جيڪي الله نازل ڪيو آھي تنھن کي جن (ماڻھن) ناپسند ڪيو تن کي اُنھن (منافقن) چيو ته ڪن ڪمن ۾ اوھان جو چيو مڃينداسون، ۽ الله سندين ڳجھين ڳالھين کي ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(47,27,'47:27','پوءِ (تڏھن سندن حال) ڪيئن ٿيندو جڏھن ملائڪ سندن رُوح ڪڍندا جنھن حال سندن مُنھن ۽ سندن پُٺيون ڪُٽيندا.');
INSERT INTO "amroti" VALUES(47,28,'47:28','اِھا (سزا) ھن ڪري آھي ته جنھن شيء الله کي ڏمرايو تنھنجي پٺيان لڳا ۽ سندس رضامندي کي ناپسند ڪيائون تنھنڪري (خدا) سندن عملن کي چٽ ڪيو.');
INSERT INTO "amroti" VALUES(47,29,'47:29','جن جي دلين ۾ بيماري آھي تن ڀانيو آھي ڇا ته الله سندن وير ڪڏھن پڌرا نه ڪندو.');
INSERT INTO "amroti" VALUES(47,30,'47:30','۽ جيڪڏھن گھرون ھا ته اُھي توکي ڏيکاريون ھا پوءِ سندن چھرن مان کين سڃاڻين ھا، ۽ ضرور کين ڳالھائڻ جي ڍنگ مان معلوم ڪندين، ۽ الله اوھان جا ڪرتوت ڄاڻندو آھي.');
INSERT INTO "amroti" VALUES(47,31,'47:31','۽ ضرور اوھان کي پرکينداسون ته اوھان مان مجاھدن ۽ صابرن کي معلوم ڪريون ۽ اوھان جا حال جاچيون.');
INSERT INTO "amroti" VALUES(47,32,'47:32','بيشڪ جن ڪفر ڪيو ۽ الله جي واٽ کان جھليو ۽ کين ھدايت پڌري ٿي تنھن کانپوءِ پيغمبر جي مخالفت ڪيائون سي الله جو ڪجھ نقصان نه ڪندا، ۽ (الله) سندن عمل سگھوئي چٽ ڪندو.');
INSERT INTO "amroti" VALUES(47,33,'47:33','اي ايمان وارؤ الله جو چيو مڃيو ۽ پيغمبر جو چيو مڃيو ۽ پنھنجا عمل نه وڃايو.');
INSERT INTO "amroti" VALUES(47,34,'47:34','بيشڪ جن ڪفر ڪيو ۽ الله جي واٽ کان جھليو وري ڪافر ٿي مُئا تن کي الله ڪڏھن نه بخشيندو.');
INSERT INTO "amroti" VALUES(47,35,'47:35','پوءِ سُست نه ٿيو ۽ صلح ڏانھن نه سڏيو، ۽ اوھين ئي غالب آھيو، ۽ الله اوھان ساڻ آھي ۽ اوھان جي عملن کي ڪڏھن ضايع نه ڪندو.');
INSERT INTO "amroti" VALUES(47,36,'47:36','دنيا جي حياتي ته رڳو راند ۽ تماشو آھي، ۽ جيڪڏھن ايمان آڻيندؤ ۽ پرھيزگاري ڪندؤ ته اوھان جو اجر اوھان کي ڏيندو ۽ اوھان کان اوھان جو (سڀ) مال نه گھرندو.');
INSERT INTO "amroti" VALUES(47,37,'47:37','جيڪڏھن اوھان کان (اوھان جو) مال گھري پوءِ (گھرڻ ۾) اوھان کي تنگ ڪري ته (انھيءَ وقت ضرور) بخيلي ڪندؤ ۽ اُھا (بخيلي) اوھان جون بدنيتون ظاھر ڪندي.');
INSERT INTO "amroti" VALUES(47,38,'47:38','خبردار ھجو اوھين اُھي (ماڻھو) آھيو جو اوھان کي سڏيو وڃي ٿو ته الله جي واٽ ۾ خرچيو، پوءِ اوھان مان ڪو (ته اھڙو آھي جو) بخيلي ٿو ڪري، ۽ جيڪو بخيلي ٿو ڪري سو رڳو پاڻ سان بخيلي ڪري ٿو، ۽ الله بي پرواھ آھي ۽ اوھين محتاج آھيو، ۽ جيڪڏھن منھن موڙيندؤ ته اوھان کانسواءِ (ٻي) ڪنھن قوم کي مٽائي آڻيندو پوءِ اُھي اوھان جھڙا نه ھوندا.');
INSERT INTO "amroti" VALUES(48,1,'48:1','(اي پيغمبر) بيشڪ اسان توکي پڌري فتح ڏني.');
INSERT INTO "amroti" VALUES(48,2,'48:2','ته تنھنجا قُصور جيڪي اڳي گذريا ۽ جيڪي پوءِ تي رھيا سي (سڀ) الله توکي معاف ڪري ۽ توتي پنھنجي نعمت پوري ڪري ۽ توکي سِڌي واٽ ڏيکاري.');
INSERT INTO "amroti" VALUES(48,3,'48:3','۽ الله غالب سوڀ سان توکي سوڀارو ڪري.');
INSERT INTO "amroti" VALUES(48,4,'48:4','(الله) اُھو آھي جنھن مؤمنن جي دلين ۾ آرام نازل ڪيو ته (انھن جو) ايمان (اڳين) ايمان سان وڌيڪ ٿئي، ۽ آسمانن ۽ زمين جا لشڪر الله جا (ئي) آھن، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(48,5,'48:5','ته مؤمنن ۽ مؤمنياڻين کي اھڙن باغن ۾ داخل ڪري جن جي ھيٺان واھيون پيون وھن اُتي سدائين رھندا ۽ سندن مدايون کائن ميٽيندو، ۽ اِھا الله وٽ وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(48,6,'48:6','۽ (ته) منافقن ۽ منافقياڻين کي ۽ مشرڪن ۽ مشرڪياڻين کي عذاب ڪري جي الله تي تمام بڇڙو گمان ڀانئيندڙ آھن، بڇڙي مصيبت مٿن ھجي شال، ۽ مٿن الله ڏمريو ۽ کين لعنت ڪيائين ۽ اُنھن لاءِ دوزخ تيار ڪيائين، ۽ اُھو بڇڙو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(48,7,'48:7','۽ آسمانن ۽ زمين جا لشڪر الله جا آھن، ۽ الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(48,8,'48:8','بيشڪ اسان توکي شاھدي ڏيندڙ ۽ خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو.');
INSERT INTO "amroti" VALUES(48,9,'48:9','ته اوھين الله ۽ سندس پيغمبر تي ايمان آڻيو ۽ سندس (دين جي) مدد ڪريو ۽ سندس عِزّت ڪريو، ۽ صبح ۽ سانجھيءَ جو الله کي پاڪائيءَ سان ساراھيو.');
INSERT INTO "amroti" VALUES(48,10,'48:10','بيشڪ جيڪي توسان بيعت ڪندا آھن سي الله ئي سان بيعت ڪندا آھن، سندن ھٿن مٿان الله جو ھٿ آھي، پوءِ جيڪو (انجام) ڀڃندو سو رڳو پنھنجي ڇيئي لاءِ ٿو ڀڃي، ۽ جنھن (ڳالھ) تي الله سان انجام ڪيو اٿس تنھنکي جيڪو پورو ڪندو پوءِ سگھو تنھن کي (الله) وڏو اجر ڏيندو.');
INSERT INTO "amroti" VALUES(48,11,'48:11','(اي پيغمبر) ٻھراڙيءَ وارن مان پوءِ تي رھيل توکي چوندا ته اسان کي پنھنجن مالن ۽ پنھنجي گھر جي ڀاتين رُجھايو تنھنڪري اسان لاءِ بخشش گھر، پنھنجين زبانن سان اھا (ڳالھ) چوندا آھن جا اُنھن جي دلين ۾ نه آھي، (کين) چؤ ته جيڪڏھن (الله) اوھان کي ڏک پھچائڻ جو ارادو ڪري يا اوھان کي سُک پھچائڻ جو ارادو ڪري ته الله جي سامھون اوھان لاءِ ڪير ڪجھ ڪري سگھي ٿو، بلڪ جيڪي ڪجھ ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(48,12,'48:12','بلڪ ڀانيوَ ته پيغمبر ۽ مؤمن پنھنجن گھرن ڏانھن اصلي ڪڏھن نه موٽندا ۽ اِھو (گمان) اوھان جي دلين ۾ چڱو (ڪري) ڏيکاريو ويو ۽ (تمام) بڇڙو گمان ڀانيوَ، ۽ (اوھين) ھلاڪ ٿيل قوم ٿيؤ.');
INSERT INTO "amroti" VALUES(48,13,'48:13','۽ جنھن الله ۽ سندس پيغمبر تي ايمان نه آندو ته بيشڪ اسان انھن ڪافرن لاءِ ٻرندڙ باھ تيار ڪئي آھي.');
INSERT INTO "amroti" VALUES(48,14,'48:14','۽ آسمانن ۽ زمين جي بادشاھي خاص الله جي آھي، جنھن کي وڻيس تنھن کي بخشي ۽ جنھن کي وڻيس تنھن کي عذاب ڪري، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(48,15,'48:15','جڏھن غنيمتن جي ھٿ ڪرڻ لاءِ ويندؤ تڏھن پوءِ تي رھيل چوندا اسان کي ڇڏيو ته اوھان سان ھلون، گھرندا آھن ته الله جي وعدي جي مخالفت ڪن، (کين) چؤ ته اسان سان ڪڏھن نه ھلندؤ اھڙي طرح الله اڳي ئي فرمائي چڪو آھي، پوءِ سگھو چوندا ته بلڪ اوھين اسان سان حسد ٿا ڪريو، بلڪ (ھميشه) ٿورڙي کانسواءِ (ڪجھ به) نه سمجھندا ھوا.');
INSERT INTO "amroti" VALUES(48,16,'48:16','(اي پيغمبر) پوءِ تي رھيلن ٻھراڙي وارن کي چؤ ته سخت ويڙھ واريءَ قوم (جي جنگ) ڏانھن (اوھين) سگھو سڏيا ويندؤ جو ساڻن ويڙھ ڪندؤ يا ته (اُھي) مُسلمان ٿيندا، پوءِ جيڪڏھن چيو مڃيندؤ ته الله اوھان کي چڱو اجر ڏيندو، ۽ جيڪڏھن (ائين) ڦرندؤ جيئن (اوھين) ھن کان اڳ ڦريا ھئو ته (الله) اوھان کي ڏکوئيندڙ عذاب جي سزا ڏيندو.');
INSERT INTO "amroti" VALUES(48,17,'48:17','(جھاد جي رھڻ کان) نڪي انڌي تي ڪو گناھ آھي، ۽ نڪي مندي تي ڪو گناھ آھي ۽ نڪي بيمار تي ڪو گناھ آھي، ۽ جيڪو الله ۽ سندس پيغمبر جو چيو مڃيندو تنھن کي (الله اھڙن) باغن ۾ داخل ڪندو جن جي ھيٺان واھيون پيون وھن، ۽ جيڪو ڦرندو تنھنکي ڏکوئيندڙ عذاب جي سزا ڏيندو.');
INSERT INTO "amroti" VALUES(48,18,'48:18','بيشڪ الله مؤمنن کان راضي ٿيو جنھن وقت وڻ ھيٺ توسان بيعت ٿي ڪيائون پوءِ جيڪي سندين دلين ۾ ھو سو ڄاتائين پوءِ مٿن (دل جو) آرام لاٿائين ۽ کين ھڪ ويجھي سوڀ انعام ڏنائين.');
INSERT INTO "amroti" VALUES(48,19,'48:19','۽ گھڻيون غنيمتون (به) جو اُنھن کي (اُھي) ھٿ آڻيندا، ۽ الله غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(48,20,'48:20','الله اوھان کي گھڻن غنيمتن جو وعدو ڏنو آھي جن کي ھٿ آڻيندؤ پوءِ ھيُ (خيبر جون غنيمتون) اوھان کي عطا ڪيائين ۽ اوھان کان ماڻھن جا ھٿ جھلي ڇڏيائين، ۽ ھن لاءِ ته مؤمنن لاءِ ھڪ نشاني ھجي ۽ اوھان کي (الله) سڌو رستو ڏيکاري.');
INSERT INTO "amroti" VALUES(48,21,'48:21','۽ ٻيون (به) جن تي سگھارانه ٿيا آھيو بيشڪ الله کي انھن جي خبر آھي، ۽ الله سڀڪنھن شيءَ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(48,22,'48:22','۽ جيڪڏھن ڪافر اوھان سان جنگ ڪن ھا ته ضرور پٺيون ڦيرائين ھا وري نڪو دوست ۽ نڪو مددگار لھن ھا.');
INSERT INTO "amroti" VALUES(48,23,'48:23','(اھوئي) الله جو قانون آھي جو اڳ ھليو ايندو آھي، ۽ الله جي قانون کي ڪڏھن ڪا ڦير ڦار نه ڏسندين.');
INSERT INTO "amroti" VALUES(48,24,'48:24','۽ (الله) اُھو آھي جنھن ڪافرن جا ھٿ اوھان کان ۽ اوھان جا ھٿ انھن کان جھليا مڪّي جي وچ ۾ اوھان کي مٿن سوڀاري ڪرڻ کان پوءِ، ۽ جيڪي ڪندا آھيو تنھن کي الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(48,25,'48:25','اِھي اُھي آھن جن انڪار ڪيو ۽ اوھان کي تعظيم واري مسجد کان جھليو ۽ قرباني کي به جو پنھنجي جاءِ تي پھچڻ کان روڪيل رھي، ۽ جيڪڏھن (اھڙا ڪي) مڙس مؤمن ۽ (ڪي) زالون مؤمنياڻيون (مڪي ۾) نه ھجن ھا جن کي اوھان نه ڄاڻندا آھيو ته (متان) کين لتاڙي ڇڏيو پوءِ بي خبريءَ ۾ انھن جو اوھان کي گناھ پھچي (ته ھاڻي ئي فتح ٿئي ھا)، جنھن کي الله گھري پنھنجي ٻاجھ ۾ داخل ڪري، جيڪڏھن (ٻئي ٽوليون) ڌار ھجن ھا ته ضرور مڪي وارن مان ڪافرن کي ڏکوئيندڙ عذاب ڪريون ھا.');
INSERT INTO "amroti" VALUES(48,26,'48:26','جڏھن ڪافرن پنھنجن دلين ۾ جاھليت جو جوش پيدا ڪيو تڏھن الله پنھنجي پيغمبر تي ۽ مؤمنن تي پنھنجو آرام نازل ڪيو ۽ کين پرھيزگاريءَ جي ڳالھ تي قائم رکيائين ۽ (اِھي) ان جا وڌيڪ حقدار ۽ اُن جا جوڳا ھوا، ۽ الله سڀڪنھن شيء کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(48,27,'48:27','بيشڪ الله پنھنجي پيغمبر کي سچو خواب ڏيکاريو، ته جيڪڏھن الله گھريو آھي ته پنھنجا مٿا ڪوڙائي ۽ وار ڪترائي تعظيم واريءَ مسجد ۾ بي ڀوا ٿي ضرور گھڙندؤ (۽) نه ڊڄندؤ، پوءِ جيڪي نه ڄاڻندا آھيو سو الله ڄاتو پوءِ اُن کان اڳ ھڪ ويجھي فتح ڏني.');
INSERT INTO "amroti" VALUES(48,28,'48:28','(الله) اُھو آھي جنھن پنھنجي پيغمبر کي ھدايت ۽ سچي دين سان موڪليو ته اُن کي سڀني دينن تي غالب ڪري، ۽ الله شاھد ڪافي آھي.');
INSERT INTO "amroti" VALUES(48,29,'48:29','مُحمّد الله جو موڪليل آھي، ۽ جيڪي سندس سنگتي آھن سي ڪافرن تي ڏاڍا سخت آھن ۽ پاڻ ۾ مھربان آھن کين رڪوع ڪندڙ سجدو ڪندڙ ڏسندو آھين الله جو فضل ۽ رضامندي طلبيندا آھن، انھن جي نشاني سندن منھن ۾ سجدي جي اثر کان (پڌري) آھي، اھا سندن وصف توريت ۾ آھي، ۽ سندس وصف اِنجيل ۾ آھي، (اھي) اُنھيءَ پوک وانگر آھن جنھن پنھنجو سَلو ڪڍيو پوءِ اُن کي مضبوط ڪيائين پوءِ ٿلھو ٿيو پوءِ پنھنجي سلي تي سڌي بيٺي جو ڪڙمين کي پئي وڻي (اسلامي غلبي جو به آخر اھڙو حال آھي) تانجو مؤمنن جي (ڏسڻ) ڪري ڪافرن کي ڪاوڙائي، جن ايمان آندو ۽ منجھائن چڱا ڪم ڪيا (تن کي) بخشش ۽ وڏي اجر جو الله انجام ڏنو آھي.');
INSERT INTO "amroti" VALUES(49,1,'49:1','اي ايمان وارؤ الله ۽ سندس پيغمبر جي آڏو اڳرائي نه ڪريو ۽ الله کان ڊڄو، بيشڪ الله ٻڌندڙ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(49,2,'49:2','اي ايمان وارؤ پنھنجا آواز پيغمبر جي آواز کان مٿي نه ڪريو ۽ جيئن اوھان جو ھڪ ٻئي سان وڏي سڏ ڳالھائڻ آھي (تئين) ساڻس ڳالھ ڏاڍيان نه ڳالھايو جو متان اوھان جا عمل چٽ ٿي وڃن ۽ اوھين نه پروڙيندا ھجو.');
INSERT INTO "amroti" VALUES(49,3,'49:3','بيشڪ جيڪي پنھنجن آوازن کي الله جي پيغمبر جي آڏو جھڪو ڪندا آھن اِھي اُھي آھن جن جي دلين کي الله پرھيزگاريءَ لاءِ آزمايو آھي، اُنھن لاءِ بخشش ۽ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(49,4,'49:4','بيشڪ جيڪي حُجرن جي ٻاھران توکي سڏيندا آھن تن مان گھڻا بي سمجھ آھن.');
INSERT INTO "amroti" VALUES(49,5,'49:5','جيڪڏھن اُھي تنھنجي ڏانھُن نڪرڻ تائين صبر ڪن ھا ته اُنھن لاءِ ڀَلو ھُجي ھا، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(49,6,'49:6','اي ايمان وارؤ جيڪڏھن ڪو بدڪار ڪا خبر اوھان وٽ آڻي ته جاچ ڪريو جو (متان) ڪنھن قوم کي بي خبريءَ سان نقصان پھچايو پوءِ جيڪي اوھان ڪري چڪؤ تنھن تي پشيمان ٿيو.');
INSERT INTO "amroti" VALUES(49,7,'49:7','۽ ڄاڻو ته اوھان ۾ الله جو پيغمبر آھي، جيڪڏھن گھڻن ڪمن ۾ ھو اوھان جو چيو مڃيندو ته اوھين ڏکيا ٿيندؤ پر الله اوھان وٽ ايمان کي پيارو ڪرايو ۽ اوھان جي دلين ۾ ان کي چڱو ڪري ڏيکاريائين ۽ اوھان وٽ ڪفر ۽ بي ديني ۽ گناھ کي ناپسند ڪرايائين، اھي سڌي رستي وارا آھن.');
INSERT INTO "amroti" VALUES(49,8,'49:8','الله جي فضل ۽ احسان سان، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(49,9,'49:9','۽ جيڪڏھن مؤمنن جون ٻه ٽوليون پاڻ ۾ وڙھن ته سندن وچ ۾ صُلح ڪريو، پوءِ جيڪڏھن انھن مان ھڪڙي ٻي تي زيادتي ڪري ته جيڪا زيادتي ٿي ڪري تنھن سان وڙھو جيسين تائين الله جي حُڪم ڏانھن موٽي اچي، پوءِ جيڪڏھن موٽي آئي ته سندن وچ ۾ انصاف سان صُلح ڪريو ۽ انصاف سان ھلو، ڇوته الله انصاف وارن کي پيارو رکندو آھي.');
INSERT INTO "amroti" VALUES(49,10,'49:10','مؤمن ته سڀ ڀائر آھن تنھنڪري پنھنجي ٻنھي ڀائرن جي وچ ۾ صلح ڪريو، ۽ الله کان ڊڄو ته مانَ اوھان تي ٻاجھ ڪئي وڃي.');
INSERT INTO "amroti" VALUES(49,11,'49:11','اي ايمان وارؤ ڪا ٽولي ڪنھن ٽولي تي ٺـٺولي نه ڪري جو متان اُھي اُنھن کان ڀلا ھجن ۽ نه زالون ٻـين زالن سان (ٺـٺوليون ڪن) جو متان اُھي اُنھن کان ڀَليون ھجن، ۽ نڪي پاڻ ۾ (ھڪ ٻئي کي) عيب لايو ۽ نڪي ھڪ ٻئي کي بڇڙن لقبن سان سڏيو، (جو) ايمان آڻڻ کانپوءِ بي دينيءَ جو نالو (پوڻ) خراب آھي، ۽ جن توبه نه ڪئي سي ئي ظالم آھن.');
INSERT INTO "amroti" VALUES(49,12,'49:12','اي ايمان وارؤ گھڻو ڪري گمان کان بچو، بيشڪ ڪي گمان گناھ آھن ۽ نڪي جاسوسي ڪريو ۽ نڪو ڪو اوھان مان ھڪ ٻئي جي گلا ڪري، ڀلا اوھان مان ڪو ھڪڙو پنھنجي مُئي ڀاءُ جو گوشت کائڻ پسند ڪندو آھي ڇا جنھن کان نفرت ڪندا آھيو، ۽ الله کان ڊڄو، بيشڪ الله توبه قبول ڪندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(49,13,'49:13','اي ماڻھؤ بيشڪ اسان اوھان کي ھڪ مڙس ۽ ھڪ زال مان پيدا ڪيو ۽ اوھان کي ذاتيون ۽ پاڙا ڪيوسون ته ھڪ ٻئي کي سڃاڻو، بيشڪ اوھان مان تمام عزت وارو الله وٽ اُھو آھي جيڪو اوھان مان ڏاڍو پرھيزگار آھي، بيشڪ الله ڄاڻندڙ خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(49,14,'49:14','جھنگلي چوندا آھن ته ايمان اندو اٿون، (اي پيغمبر کين) چؤ ته (حقيقت ڪري) ايمان نه آندو اٿو پر (ايئن) چؤ ته اسين مُسلمان ٿيا آھيون ۽ اڃا اوھان جي دلين ۾ ايمان گھڙيو (ئي) نه آھي، ۽ جيڪڏھن الله ۽ سندس پيغمبر جو چيو مڃيندؤ ته اوھان جي عملن مان اوھان کي ڪجھ (به) نه گھٽائيندو، بيشڪ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(49,15,'49:15','مؤمن رڳو اُھي آھن جن الله ۽ سندس پيغمبر تي ايمان آندو وري (ڪو) شڪ نه ڪيائون ۽ الله جي واٽ ۾ پنھنجن مالن ۽ پنھنجن جُسن سان جھاد ڪيائون، اھي ئي سچا آھن.');
INSERT INTO "amroti" VALUES(49,16,'49:16','(اي پيغمبر کين) چؤ ته پنھنجو دين الله کي ڄاڻائيندا آھيو ڇا؟ ۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو سڀ الله ڄاڻندو آھي، ۽ الله سڀڪنھن شيءَ کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(49,17,'49:17','(اي پيغمبر) اسلام آڻڻ جو توتي احسان رکندا آھن، چوينِ ته مون تي پنھنجي اسلام (آڻڻ) جو احسان نه رکو، بلڪ اوھان تي الله احسان رکندو آھي جو اوھان کي ايمان جي ھدايت ڪيائين جيڪڏھن سچا آھيو.');
INSERT INTO "amroti" VALUES(49,18,'49:18','بيشڪ الله آسمانن ۽ زمين جو ڳجھ ڄاڻندو آھي، ۽ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(50,1,'50:1','قٓ، سڳوري قرآن جو قسم آھي.');
INSERT INTO "amroti" VALUES(50,2,'50:2','بلڪ ھن ڳالھ ڪري عجب ۾ پيا ته اُنھن وٽ اُنھن مان (ئي) ھڪ ڊيڄاريندڙ آيو ته پوءِ ڪافرن چيو ته ھي عجب ڳالھ آھي.');
INSERT INTO "amroti" VALUES(50,3,'50:3','ڀلا جڏھن مرنداسون ۽ مٽي ٿينداسون، اھو وري جيئرو ٿيڻ (عقل کان) پري آھي.');
INSERT INTO "amroti" VALUES(50,4,'50:4','انھن مان جيڪي زمين گھٽائيندي آھي سو بيشڪ ڄاتو اٿون، ۽ اسان وٽ ھڪ حفاظت رکندڙ ڪتاب آھي.');
INSERT INTO "amroti" VALUES(50,5,'50:5','بلڪ سچو دين جڏھن وٽن آيو ته اُن کي ڪوڙ ڄاتائون پوءِ اُھي منجھيل ڪم ۾ آھن.');
INSERT INTO "amroti" VALUES(50,6,'50:6','پوءِ آسمان جو سندن مٿان آھي تنھن ڏانھن نه ڏٺو اٿن ڇا ته اُن کي ڪيئن بڻايو اٿون ۽ اُن کي (ڪيئن) سينگاريو اٿون ۽ اُن ۾ ڪا ڦوٽ نه آھي.');
INSERT INTO "amroti" VALUES(50,7,'50:7','۽ زمين کي پکيڙيوسون ۽ منجھس جبل وڌاسون ۽ منجھس سڀڪنھن وڻندڙين جنسن مان سَلّا ڄماياسون.');
INSERT INTO "amroti" VALUES(50,8,'50:8','سڀڪنھن ورندڙ ٻانھي جي واٽ ڏيکارڻ ۽ نصيحت ڏيڻ لاءِ.');
INSERT INTO "amroti" VALUES(50,9,'50:9','۽ آسمان کان برڪت وارو پاڻي لاٿوسون پوءِ اُن سان باغ ۽ لڻڻ واري پوک جو اَن ڄمايوسون.');
INSERT INTO "amroti" VALUES(50,10,'50:10','۽ ڊگھيون کجيون (به) جن جا گوشا تہ ٿيل آھن.');
INSERT INTO "amroti" VALUES(50,11,'50:11','ٻانھن جي روزي (ڏيڻ) لاءِ ۽ اُن سان ويران شھر کي آباد ڪيوسون، ۽ اھڙي طرح (قبرن مان) نڪرڻ آھي.');
INSERT INTO "amroti" VALUES(50,12,'50:12','کائن اڳ نوح جي قوم ۽ رس (جي شھر) وارن ۽ ثمود (قوم) ڪوڙو ڀانيو.');
INSERT INTO "amroti" VALUES(50,13,'50:13','۽ عاد ۽ فرعون ۽ لُوط جي ڀائرن.');
INSERT INTO "amroti" VALUES(50,14,'50:14','۽ ايڪه وارن ۽ تُبّع جي قوم، ھر ھڪ پيغمبرن کي ڪُوڙو ڄاتو پوءِ (مٿن) منھنجي عذاب جو انجام لازم ٿيو.');
INSERT INTO "amroti" VALUES(50,15,'50:15','پھرين ڀيري (جي) خلقڻ ۾ (ڪو) ٿڪا آھيو ڇا؟ (نه!) بلڪ اُھي نئين سر پيدا ڪرڻ کان شڪ ۾ آھن.');
INSERT INTO "amroti" VALUES(50,16,'50:16','۽ بيشڪ ماڻھوءَ کي پيدا ڪيوسون ۽ سندس نفس کيس جيڪو وسوسو وجھندو آھي سو ڄاڻندا آھيون، ۽ اسين ڏانھس (سندس) ساہ جي رڳ کان وڌيڪ ويجھا آھيون.');
INSERT INTO "amroti" VALUES(50,17,'50:17','جڏھن جو ٻه لکندڙ (ھڪ) سڄي پاسي کان ۽ (ٻيو) کٻي پاسي کان وھندڙ لکندا آھن.');
INSERT INTO "amroti" VALUES(50,18,'50:18','ڪا ڳالھ نه ڳالھائيندو آھي جنھن جي لاءِ وٽس ھڪ نگھبان موجود نه آھي.');
INSERT INTO "amroti" VALUES(50,19,'50:19','۽ موت جي سختي سچ پچ ايندي، (چئبو ته) اِھا اُھا شيءِ آھي جنھن کان پاسو ڪندو ھُئين.');
INSERT INTO "amroti" VALUES(50,20,'50:20','۽ صُور ۾ ڦوڪيو ويندو، اھو عذاب جو ڏينھن آھي.');
INSERT INTO "amroti" VALUES(50,21,'50:21','۽ سڀڪو جيءَ ايندو جو ساڻس ھڪ ھڪليندڙ ۽ ھڪ شاھِد ھوندو.');
INSERT INTO "amroti" VALUES(50,22,'50:22','(چئبو ته) بيشڪ ھن (ڏينھن) کان بي خبريءَ ۾ ھُئين ھاڻي توکان تنھنجو پردو کوليوسون تنھنڪري اڄ تنھنجي نگاھ تِکي آھي.');
INSERT INTO "amroti" VALUES(50,23,'50:23','۽ سندس سنگتي (ملائڪ) چوندو ته جيڪي مون وٽ (لکيل) موجود ھو سو ھي آھي.');
INSERT INTO "amroti" VALUES(50,24,'50:24','(چئبو ته) اي ٻئي (ملائڪؤ) سڀڪنھن ناشڪر ھٺيلي کي دوزخ ۾ اُڇليو.');
INSERT INTO "amroti" VALUES(50,25,'50:25','(جو) خير کان جھليندڙ حد کان لنگھندڙ شڪ ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(50,26,'50:26','جنھن الله سان ٻيو معبود ٺھرايو اھڙي (ھر ھڪ) کي سخت عذاب ۾ اُڇليو.');
INSERT INTO "amroti" VALUES(50,27,'50:27','سندس سنگتي (شيطان) چوندو ته اي منھنجا پالڻھار مون اُن کي گمراھ نه ڪيو ھو پر اُھو (پاڻ) وڏي گمراھيءَ ۾ (پيل) ھو.');
INSERT INTO "amroti" VALUES(50,28,'50:28','(الله) چوندو ته مون وٽ جھڳڙو نه ڪريو ھن ھوندي جو بيشڪ اوھان ڏانھن عذاب جو انجام (ھن کان) اڳي موڪليو ھوم.');
INSERT INTO "amroti" VALUES(50,29,'50:29','نڪي مون وٽ انجام ڦيرائبو آھي ۽ نڪي آءٌ ٻانھن تي ظلم ڪرڻ وارو آھيان.');
INSERT INTO "amroti" VALUES(50,30,'50:30','جنھن ڏينھن دوزخ کي چونداسون ته ڀريو آھين (يا نه!) ۽ (دوزخ) چوندو ته (ٻيو به) ڪجھ (اڃا) ھن کان وڌيڪ آھي ڇا؟');
INSERT INTO "amroti" VALUES(50,31,'50:31','۽ بھشت پرھيزگارن لاءِ ويجھو ڪبو نه پري.');
INSERT INTO "amroti" VALUES(50,32,'50:32','(چئبو ته) جنھن جو اوھان کي سڀڪنھن (الله ڏانھن) رجوع ڪرڻ واري ۽ ادب نگاھ رکڻ واري لاءِ انجام ڏبو ھو سو ھي آھي.');
INSERT INTO "amroti" VALUES(50,33,'50:33','جيڪو پرپٺ ٻاجھاري (الله) کان ڊنو ۽ (الله ڏانھن) رجوع ٿيل دل سان آيو.');
INSERT INTO "amroti" VALUES(50,34,'50:34','(چئبو ته) سلامتيءَ سان بھشت ۾ گھڙو، اھو سدائين رھڻ جو ڏينھن آھي.');
INSERT INTO "amroti" VALUES(50,35,'50:35','جيڪي گھرندا سو منجھس انھن لاءِ (موجود) آھي ۽ اسان وٽ (اُن کان) وڌيڪ به آھي.');
INSERT INTO "amroti" VALUES(50,36,'50:36','۽ انھن کان اڳ ڪيترائي جُڳ ھلاڪ ڪياسون جي پڪڙ ۾ اُنھن کان به تمام ڏاڍا ھوا پوءِ شھرن ۾ ڳولا ڪرڻ لڳا، ته ڪا (بچڻ جي) واھ آھي؟');
INSERT INTO "amroti" VALUES(50,37,'50:37','بيشڪ انھيءَ ڳالھ ۾ اُنھي لاءِ نصيحت آھي جنھن کي دل ھجي يا جو دل لائي ڪن ڏئي.');
INSERT INTO "amroti" VALUES(50,38,'50:38','۽ بيشڪ آسمانن ۽ زمين کي ۽ جيڪي ٻنھي جي وچ ۾ آھي تنھن کي ڇھن ڏينھن ۾ بڻايوسون، ۽ اسان کي ڪو ٿَڪ نه ٿيو.');
INSERT INTO "amroti" VALUES(50,39,'50:39','پوءِ (اي پيغمبر) جيڪي (ڪافر) چوندا آھن تنھن تي صبر ڪر ۽ سج اُڀرڻ کان اڳ ۽ لھڻ کان اڳ پنھنجي پالڻھار جي پاڪائي ساراھ سان بيان ڪر.');
INSERT INTO "amroti" VALUES(50,40,'50:40','۽ ڪجھ (مھل) رات جو الله کي پاڪائيءَ سان ساراھ ۽ نماز کان پوءِ به.');
INSERT INTO "amroti" VALUES(50,41,'50:41','۽ (ھي ڳالھ) ٻُڌ ته جنھن ڏينھن ويجھي ھنڌ کان سڏيندڙ سڏيندو.');
INSERT INTO "amroti" VALUES(50,42,'50:42','جنھن ڏينھن سچ پچ سخت ھَڪل ٻُڌندا، اُھو (قبرن مان) نڪرڻ جو ڏينھن آھي.');
INSERT INTO "amroti" VALUES(50,43,'50:43','بيشڪ اسين جياريندا آھيون ۽ ماريندا آھيون ۽ اسان ڏانھن موٽڻ آھي.');
INSERT INTO "amroti" VALUES(50,44,'50:44','جنھن ڏينھن زمين انھن کان ڦاٽندي (تنھن ڏينھن) تڪڙا وڃڻ وارا ھوندا، اِھو گڏ ڪرڻ اسان کي آسان آھي.');
INSERT INTO "amroti" VALUES(50,45,'50:45','جيڪي (ڪافر) چوندا آھن سو اسين چڱي طرح ڄاڻندا آھيون ۽ تون مٿن (ڪو) ڏاڍ ڪرڻ وارو نه آھين، پوءِ جيڪو منھنجي عذاب جي ڌمڪيءَ کان ڊڄي تنھن کي قرآن سان سمجھاءِ.');
INSERT INTO "amroti" VALUES(51,1,'51:1','قسم آھي اُڏائڻ وارن (وائن) جو (خاڪ کي) اُڏائڻ.');
INSERT INTO "amroti" VALUES(51,2,'51:2','پوءِ (پاڻي جي) بار کڻڻ وارن (ڪڪرن) جو قسم آھي.');
INSERT INTO "amroti" VALUES(51,3,'51:3','پوءِ آسانيءَ سان ھلندڙين (ٻيڙين) جو قسم آھي.');
INSERT INTO "amroti" VALUES(51,4,'51:4','پوءِ ڪم وراھڻ وارن (ملائڪن) جو قسم آھي.');
INSERT INTO "amroti" VALUES(51,5,'51:5','ته بيشڪ جيڪو اوھان سان انجام ڪجي ٿو سو سچ آھي.');
INSERT INTO "amroti" VALUES(51,6,'51:6','۽ بيشڪ (عملن جو) بدلو ضرور ٿيڻو آھي.');
INSERT INTO "amroti" VALUES(51,7,'51:7','واٽن واري آسمان جو قسم آھي.');
INSERT INTO "amroti" VALUES(51,8,'51:8','ته (اوھين) پاڻ ۾ تڪرار واريء ھڪ ڳالھ ۾ (پيل) آھيو.');
INSERT INTO "amroti" VALUES(51,9,'51:9','قرآن کان اُھو ڦيرايو ويندو آھي جيڪو (ازل) ۾ ڦيرايل آھي.');
INSERT INTO "amroti" VALUES(51,10,'51:10','اُنھن ڪوڙ ھڻندڙن کي لعنت ڪئي ويئي.');
INSERT INTO "amroti" VALUES(51,11,'51:11','جيڪي اَڻ ڄاڻائي ۾ وسارڻ وارا آھن.');
INSERT INTO "amroti" VALUES(51,12,'51:12','پُڇندا آھن ته قيامت جو ڏينھن ڪڏھن ٿيندو؟');
INSERT INTO "amroti" VALUES(51,13,'51:13','(ھائو) اُنھي ڏينھن جو اُنھن کي باھ ۾ عذاب ڪبو.');
INSERT INTO "amroti" VALUES(51,14,'51:14','(چئبو ته ھيُ) پنھنجو عذاب چکو، ھيُ اُھو آھي جنھن کي اوھين جلد گھرندا ھئو.');
INSERT INTO "amroti" VALUES(51,15,'51:15','بيشڪ پرھيزگار باغن ۽ چشمن ۾ ھوندا.');
INSERT INTO "amroti" VALUES(51,16,'51:16','جيڪي سندن پالڻھار کين ڏنو سو وٺڻ وارا ھوندا، بيشڪ اُھي ھن کان اڳ چڱائي ڪندڙ ھوا.');
INSERT INTO "amroti" VALUES(51,17,'51:17','رات جو ٿورو سمھندا ھوا.');
INSERT INTO "amroti" VALUES(51,18,'51:18','۽ اُھي اَسرن جو بخشش گھرندا ھوا.');
INSERT INTO "amroti" VALUES(51,19,'51:19','۽ سندن مالن ۾ سوال ڪندڙ ۽ نه سوال ڪندڙ مُحتاج جو حصّو ھو.');
INSERT INTO "amroti" VALUES(51,20,'51:20','۽ يقين ڪندڙن لاءِ زمين ۾ نشانيون آھن.');
INSERT INTO "amroti" VALUES(51,21,'51:21','۽ اوھان جي جندن ۾ به، پوءِ نٿا ڏسو ڇا؟');
INSERT INTO "amroti" VALUES(51,22,'51:22','۽ اوھان جي روزي آسمان ۾ آھي ۽ اُھو (به) جنھنجو اوھان کي انجام ڏجي ٿو.');
INSERT INTO "amroti" VALUES(51,23,'51:23','پوءِ آسمان ۽ زمين جي پالڻھار جو قسم آھي ته اھا خبر سچي آھي جھڙي طرح اوھين ڳالھائيندا آھيو.');
INSERT INTO "amroti" VALUES(51,24,'51:24','اِبراھيم جي سڳورن مھمانن جي خبر تو وٽ (نه) آئي آھي ڇا؟');
INSERT INTO "amroti" VALUES(51,25,'51:25','جڏھن اُھي وٽس آيا تڏھن سلام چيائين، ھن (به) سلام چيو، (دل ۾ ڄاتائين ته ھي) اوپرا ماڻھو آھن.');
INSERT INTO "amroti" VALUES(51,26,'51:26','پوءِ پنھنجي گھر وارن ڏانھن تکو ھليو ۽ ٿلھو گابو (پچائي) آندائين.');
INSERT INTO "amroti" VALUES(51,27,'51:27','پوءِ اھو انھن کي ويجھو ڪيائين چيائين ته ڇونه ٿا کائو؟');
INSERT INTO "amroti" VALUES(51,28,'51:28','پوءِ کائن دل ۾ ڊپ رکيائين، چيائون ته نه ڊڄ، ۽ اُن کي ھڪ داناءَ نينگر جي مبارڪ ڏنائون.');
INSERT INTO "amroti" VALUES(51,29,'51:29','پوءِ سنديس زال دانھن ڪندي سامھون آئي پوءِ پنھنجو منھن ڪٽيائين ۽ چيائين ته ٻڍڙي سنڍ (ڄڻيندي ڇا؟).');
INSERT INTO "amroti" VALUES(51,30,'51:30','چيائون ته ائين ئي تنھنجي پالڻھار فرمايو آھي، ڇوته اُھو حِڪمت وارو ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(51,31,'51:31','(ابراھيم) چيو ته اي قاصد‍ؤ اوھان جو ڪھڙو غرض آھي.');
INSERT INTO "amroti" VALUES(51,32,'51:32','چيائون ته بيشڪ اسين ھڪڙي ڏوھاري قوم ڏانھن موڪليا ويا آھيون.');
INSERT INTO "amroti" VALUES(51,33,'51:33','ته مٿن پڪي مٽيءَ جون گليليون وسايون.');
INSERT INTO "amroti" VALUES(51,34,'51:34','جي حد کان لنگھندڙن لاءِ تنھنجي پالڻھار وٽ نشان ڪيل آھن.');
INSERT INTO "amroti" VALUES(51,35,'51:35','پوءِ مؤمنن مان اتي جيڪو ھو تنھن کي ٻاھر ڪڍيوسون.');
INSERT INTO "amroti" VALUES(51,36,'51:36','پوءِ اُتي مسلمانن جي ھڪ گھر کانسواءِ (ٻيو ڪو گھر) نه ڏٺوسون.');
INSERT INTO "amroti" VALUES(51,37,'51:37','۽ جيڪي ڏکوئيندڙ عذاب کان ڊڄندا آھن تن لاءِ اُتي (عبرت لاءِ) نشاني ڇڏي سون.');
INSERT INTO "amroti" VALUES(51,38,'51:38','۽ مُوسىٰ (جي قصّي) ۾ (به عِبرت آھي) جڏھن کيس پڌري دليل سان فرعون ڏانھن موڪليوسون.');
INSERT INTO "amroti" VALUES(51,39,'51:39','پوءِ ھن پنھنجي لشڪر سميت منھن موڙيو چيائين ته (ھي) جادوگر يا چريو آھي.');
INSERT INTO "amroti" VALUES(51,40,'51:40','تنھنڪري اُن کي ۽ سندس لشڪر کي پڪڙيوسون پوءِ اُن کي ملامت وارو ڪري سمنڊ ۾ اُڇليوسون.');
INSERT INTO "amroti" VALUES(51,41,'51:41','۽ عاد جي (قصي) ۾ (به عبرت آھي) جڏھن اھڙو بي برڪتو واءُ موڪليوسون.');
INSERT INTO "amroti" VALUES(51,42,'51:42','جو جنھن شيء تي اچي پھچي تنھن کي ڳريل ھڏي وانگر ڪرڻ کانسواءِ نه ڇڏي.');
INSERT INTO "amroti" VALUES(51,43,'51:43','۽ ثمود (جي قصّي) ۾ (به عبرت آھي) جڏھن کين چيو ويو ته ھڪ مُدت تائين مزا ماڻيو.');
INSERT INTO "amroti" VALUES(51,44,'51:44','پوءِ پنھنجي پالڻھار جي حُڪم (مڃڻ) کان ھٺ ڪيائون تنھنڪري سندن ڏسندي کين (ھڪ) سخت ڪڙڪي ورتو.');
INSERT INTO "amroti" VALUES(51,45,'51:45','پوءِ نڪي اُٿي سگھيا ۽ نڪي وير وٺڻ وارا ھوا.');
INSERT INTO "amroti" VALUES(51,46,'51:46','۽ (ھن کان) اڳ نوح جي قوم (کي ھلاڪ ڪيوسون) بيشڪ اُھي بدڪار ماڻھو ھوا.');
INSERT INTO "amroti" VALUES(51,47,'51:47','۽ آسمان کي (پنھنجيءَ) قوت سان بڻايوسون ۽ بيشڪ اسين سگھارا آھيون.');
INSERT INTO "amroti" VALUES(51,48,'51:48','۽ زمين کي پکيڙيوسون جو چڱا پکيڙيندڙ آھيون.');
INSERT INTO "amroti" VALUES(51,49,'51:49','۽ سڀڪنھن شيءَ مان جوڙو جوڙو بڻايوسون ته مانَ اوھين نصيحت وٺو.');
INSERT INTO "amroti" VALUES(51,50,'51:50','پوءِ الله ڏانھن ڀڄو، بيشڪ آءٌ سندس طرف کان اوھان لاءِ پڌرو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(51,51,'51:51','۽ الله سان (ٻيو) ڪو معبُود نه ٺھرايو، بيشڪ آءٌ سندس پار کان اوھان لاءِ پڌرو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(51,52,'51:52','اھڙي طرح جيڪي انھن کان اڳ ھوا تن وٽ ڪوبه پيغمبر نه آيو پر چيائون ته جادوگر يا چريو آھي.');
INSERT INTO "amroti" VALUES(51,53,'51:53','ان (چوڻ) جي ھڪ ٻئي کي وصيت ڪندا آيا آھن ڇا؟ (نه!) بلڪ اُھي نافرمان قوم آھن.');
INSERT INTO "amroti" VALUES(51,54,'51:54','تنھنڪري (اي پيغمبر) کائن منھن موڙ ھاڻي تون ميار وارو نه آھين.');
INSERT INTO "amroti" VALUES(51,55,'51:55','۽ سمجھاڻي ڏيندو رھ ڇوته سمجھائڻ مؤمنن کي نفعو ڏيندو آھي.');
INSERT INTO "amroti" VALUES(51,56,'51:56','۽ جِنّن ۽ ماڻھن کي رڳو پنھنجي عبادت لاءِ پيدا ڪيوسون.');
INSERT INTO "amroti" VALUES(51,57,'51:57','نڪي کائن ڪا روزي ٿو گھران ۽ نڪي گھران ٿو ته مون کي کارائين.');
INSERT INTO "amroti" VALUES(51,58,'51:58','بيشڪ الله ئي روزي ڏيندڙ آھي جو تمام سگھارو زبردست آھي.');
INSERT INTO "amroti" VALUES(51,59,'51:59','پوءِ بيشڪ جن ظلم ڪيو تن لاءِ سندن يارن جي سزا جھڙو عذاب (جو ڀاڱو) آھي پوءِ مون کان جلد (عذاب) نه گُھرن.');
INSERT INTO "amroti" VALUES(51,60,'51:60','پوءِ ڪافرن لاءِ اُن سندن ڏينھن کان خرابي آھي جنھن جو کين وعدو ڏنو وڃي ٿو.');
INSERT INTO "amroti" VALUES(52,1,'52:1','(جبل) طُور جو قسم آھي.');
INSERT INTO "amroti" VALUES(52,2,'52:2','۽ لکيل ڪتاب جو (قسم) آھي.');
INSERT INTO "amroti" VALUES(52,3,'52:3','جو کُليل ورقن ۾ (لکيل) آھي.');
INSERT INTO "amroti" VALUES(52,4,'52:4','۽ آباد گھر جو (قسم) آھي.');
INSERT INTO "amroti" VALUES(52,5,'52:5','۽ مٿاھينءَ ڇت جو (قسم) آھي.');
INSERT INTO "amroti" VALUES(52,6,'52:6','۽ اُڀامندڙ درياءَ جو (قسم) آھي.');
INSERT INTO "amroti" VALUES(52,7,'52:7','ته بيشڪ تنھنجي پالڻھار جو عذاب ضرور ٿيڻو آھي.');
INSERT INTO "amroti" VALUES(52,8,'52:8','اُن کي ڪوبه ٽارڻ وارو ڪونھي.');
INSERT INTO "amroti" VALUES(52,9,'52:9','جنھن ڏينھن آسمان موج ھڻي چُرندو.');
INSERT INTO "amroti" VALUES(52,10,'52:10','۽ جبل چڱيءَ طرح ھلندا.');
INSERT INTO "amroti" VALUES(52,11,'52:11','تنھن ڏينھن اُنھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(52,12,'52:12','جيڪي اَجاين ڳالھين ۾ راند ڪندا آھن.');
INSERT INTO "amroti" VALUES(52,13,'52:13','جنھن ڏينھن دوزخ جي باھ ڏانھن ڌڪا ڏيئي روانا ڪبا.');
INSERT INTO "amroti" VALUES(52,14,'52:14','(تنھن ڏينھن چئبن ته) ھي اُھا باھ آھي جنھن کي ڪُوڙ ڀائندا ھئو.');
INSERT INTO "amroti" VALUES(52,15,'52:15','ھيءُ جادو آھي ڇا يا اوھين نه ٿا ڏسو؟');
INSERT INTO "amroti" VALUES(52,16,'52:16','ان ۾ گھڙو پوءِ صبر ڪريو يا صبر نه ڪريو، (ته) اوھان لاءِ ھڪ جھڙي (ڳالھ) آھي، جيڪي اوھين ڪندا ھئو رڳو تنھنجو بدلو اوھان کي ڏنو ويندو.');
INSERT INTO "amroti" VALUES(52,17,'52:17','بيشڪ پرھيزگار باغن ۽ نعمتن ۾ ھوندا.');
INSERT INTO "amroti" VALUES(52,18,'52:18','جيڪا (نعمت) کين سندن پالڻھار ڏني تنھن سان خوش ھوندا، ۽ (اُنھي ڪري به خوش ھوندا جو) سندن پالڻھار کين دوزخ جي عذاب کان بچايو.');
INSERT INTO "amroti" VALUES(52,19,'52:19','(چئبن ته) انھي ڪري جو (چڱا) ڪم ڪندا ھئو مزي سان کائو ۽ پيئو.');
INSERT INTO "amroti" VALUES(52,20,'52:20','ھڪ ٻئي جي برابر وڇايلن تختن تي ٽيڪ ڏيئي ويٺل ھوندا، ۽ کين وڏين اکين وارين حورن سان پرڻائينداسون.');
INSERT INTO "amroti" VALUES(52,21,'52:21','۽ جن ايمان آندو ۽ سندن اولاد ايمان ۾ سندن رستو ھليا تن سان سندن اولاد کي ملائينداسون ۽ کين سندن عملن مان ڪجھ به نه گھٽائينداسون، سڀڪنھن مڙس جيڪي ڪمايو تنھن ۾ (اُھو پاڻ) ڳه ٿيل آھي.');
INSERT INTO "amroti" VALUES(52,22,'52:22','۽ انھن کي ميوو ۽ گوشت جنھن (قسم) مان گھرندا (تنھن مان) پيا ڏينداسون.');
INSERT INTO "amroti" VALUES(52,23,'52:23','اُتي شراب جا پيالا ھڪ ٻئي کان پيا وٺندا جنھن ۾ نڪي بڪ بڪ ۽ نڪو گناھ ۾ پوڻ ھوندو.');
INSERT INTO "amroti" VALUES(52,24,'52:24','۽ وٽن اھڙا نينگر سندن آس پاس (خدمت لاءِ) پيا ايندا ويندا جو ڄڻڪ اُھي ڍڪيل موتي آھن.');
INSERT INTO "amroti" VALUES(52,25,'52:25','۽ اُنھن مان ھڪڙا ٻـين ڏانھن پاڻ ۾ پڇا ڪرڻ ڪرڻ لاءِ مُنھن ڪندا.');
INSERT INTO "amroti" VALUES(52,26,'52:26','چوندا ته بيشڪ اسين ھن کان اڳ پنھنجي گھر وارن ۾ ڊڄندڙ ھئاسون.');
INSERT INTO "amroti" VALUES(52,27,'52:27','پوءِ الله اسان تي احسان ڪيو ۽ اسان کي دوزخ جي عذاب کان بچايائين.');
INSERT INTO "amroti" VALUES(52,28,'52:28','بيشڪ اسين ھن کان اڳ کيس سڏيندا ھواسون، بيشڪ اُھوئي اِحسان ڪندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(52,29,'52:29','پوءِ (اي پيغمبر کين) نصيحت ڪر جو تون پنھنجي پالڻھار جي فضل سان نڪي ڀُوپُو آھين ۽ نڪي چريو آھين.');
INSERT INTO "amroti" VALUES(52,30,'52:30','بلڪ چوندا آھن ڇا ته شاعر آھي (۽ اسين) سندس حق ۾ زماني جي ڦير گھير اچڻ جا منتظر آھيون.');
INSERT INTO "amroti" VALUES(52,31,'52:31','(انھن ڪافرن کي) چؤ ته اوھين به انتظار ڪڍو جو بيشڪ آءٌ به اوھان سان انتظار ڪڍندڙ آھيان.');
INSERT INTO "amroti" VALUES(52,32,'52:32','کين سندن عقل اھو ڏس ڏيندا آھن ڇا يا اُھي شرارتي قوم آھي.');
INSERT INTO "amroti" VALUES(52,33,'52:33','چوندا آھن ڇا ته ھِن قرآن کي پاڻون بڻايو اٿس، بلڪ ايمان نه ٿا آڻين.');
INSERT INTO "amroti" VALUES(52,34,'52:34','پوءِ (کين) جڳائي ته جيڪڏھن سچا آھن ته اُن جھڙو ڪو سخن (بڻائي) آڻين.');
INSERT INTO "amroti" VALUES(52,35,'52:35','اھي بي سبب (پاڻھي) پيدا ٿي پيا آھن ڇا يا اُھي (پاڻ) پيدا ڪندڙ آھن؟');
INSERT INTO "amroti" VALUES(52,36,'52:36','آسمانن ۽ زمين کي بڻايو اٿن ڇا، بلڪ اُھي يقين نه ٿا آڻين.');
INSERT INTO "amroti" VALUES(52,37,'52:37','تنھنجي پالڻھار جا خزانا انھن وٽ آھن ڇا يا اُھي داروغا آھن؟');
INSERT INTO "amroti" VALUES(52,38,'52:38','کين ڪا ڏاڪڻ آھي ڇا جنھن تي (چڙھي) ڪن ڏئي ٻڌندا آھن؟ پوءِ جڳائي ته اُنھن مان ٻُڌندڙ ڪو پڌرو دليل آڻي.');
INSERT INTO "amroti" VALUES(52,39,'52:39','الله کي ڌيئر آھن ڇا ۽ اوھان کي پُٽ؟');
INSERT INTO "amroti" VALUES(52,40,'52:40','اُنھن کان مزوري گھرين ٿو ڇا؟ جو اُھي (اُنھيءَ) چٽيءَ کان ڳوري بار (ھيٺ) ٿيا آھن.');
INSERT INTO "amroti" VALUES(52,41,'52:41','اُنھن وٽ ڳُجھ جو علم آھي ڇا جو اُھي لکندا آھن.');
INSERT INTO "amroti" VALUES(52,42,'52:42','ڪا بڇڙي رٿ ڪرڻ گھرندا آھن ڇا؟ پوءِ جن ڪُفر ڪيو سي ئي فريب ۾ پيل آھن.');
INSERT INTO "amroti" VALUES(52,43,'52:43','انھن لاءِ الله کانسواءِ (ٻيو) ڪو عبادت جو لائق آھي ڇا؟ (ساڻس) جيڪو شريڪ مقرر ڪندا آھن تنھن کان الله پاڪ آھي.');
INSERT INTO "amroti" VALUES(52,44,'52:44','۽ جيڪڏھن آسمان مان ڪو ٽڪرو ڪرندڙ ڏسندا ته چوندا ته گھاٽو ڪَڪر آھي.');
INSERT INTO "amroti" VALUES(52,45,'52:45','پوءِ (اي پيغمبر) اُنھن کي ايستائين ڇڏي ڏي جيستائين پنھنجو اُھو ڏينھن ڏسن جنھن ۾ بيھوش ڪيو ويندن.');
INSERT INTO "amroti" VALUES(52,46,'52:46','جنھن سندن فريب ڪجھ به نه ٽاريندو ۽ نڪي کين مدد ڏبي.');
INSERT INTO "amroti" VALUES(52,47,'52:47','۽ بيشڪ ظالمن لاءِ ھن کانسواءِ ٻيو عذاب (به) آھي پر اُنھن مان گھڻا نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(52,48,'52:48','۽ (اي پيغمبر) تون پنھنجي پالڻھار جي حُڪم (اچڻ) تائين صبر ڪر ڇوته تون اسان جي نظر ھيٺ آھين ۽ جنھن مھل تون اُٿين (تنھن مھل) پنھنجي پالڻھار جي ساراھ سان پاڪائي بيان ڪر');
INSERT INTO "amroti" VALUES(52,49,'52:49','۽ ڪجھ رات جو (به) سندس پاڪائي بيان ڪر ۽ ستارن جي لھڻ کانپوءِ (به).');
INSERT INTO "amroti" VALUES(53,1,'53:1','ستاري جو قسم آھي جڏھن لھي.');
INSERT INTO "amroti" VALUES(53,2,'53:2','ته اوھان جو (ھي) سنگتي (محمّد) نڪي ڀُلو ۽ نڪي بي راھو ھليو.');
INSERT INTO "amroti" VALUES(53,3,'53:3','۽ نڪي (نفس جي) سَڌ کان ڳالھائيندو آھي.');
INSERT INTO "amroti" VALUES(53,4,'53:4','ھيُ قرآن رڳو وحي آھي جو موڪليو ويندو آھي.');
INSERT INTO "amroti" VALUES(53,5,'53:5','اُن کي ڏاڍي سگھ واري سيکاريو آھي.');
INSERT INTO "amroti" VALUES(53,6,'53:6','جو زور وارو آھي، پوءِ اُھو سِڌو بيٺو.');
INSERT INTO "amroti" VALUES(53,7,'53:7','۽ اُھو (آسمان جي) مٿاھين ڪناري تي ھو.');
INSERT INTO "amroti" VALUES(53,8,'53:8','وري ويجھو ٿيو پوءِ ھيٺ لٿو.');
INSERT INTO "amroti" VALUES(53,9,'53:9','پوءِ ٻن ڪمانن جيتري وِٿي ھئي يا (اُن کان به) تمام ويجھو ھو.');
INSERT INTO "amroti" VALUES(53,10,'53:10','پوءِ الله جي ٻانھي ڏانھن پيغام پھچايائين جيڪي پھچايائين.');
INSERT INTO "amroti" VALUES(53,11,'53:11','جيڪي (پيغمبر جي) دل ڏٺو سو اُن ڪوڙ نه ڄاتو.');
INSERT INTO "amroti" VALUES(53,12,'53:12','جيڪي (پيغمبر) ڏسندو آھي تنھن ۾ اُن سان جھڳڙو ڇو ڪندا آھيو؟');
INSERT INTO "amroti" VALUES(53,13,'53:13','۽ بيشڪ اُنھيءَ (ملائڪ) کي (انھيءَ مھل) ٻيو ڀيرو (سندس اصلي صورت ۾) ڏٺو ھوائين.');
INSERT INTO "amroti" VALUES(53,14,'53:14','سِدۡرَةُ الۡمُنۡتَھىٰ وٽ.');
INSERT INTO "amroti" VALUES(53,15,'53:15','اُن وٽ رھڻ جو بھشت آھي.');
INSERT INTO "amroti" VALUES(53,16,'53:16','جنھن مھل سِدرة کي انھيءَ ٿي ڍڪيو جنھن ڍڪيو ٿي.');
INSERT INTO "amroti" VALUES(53,17,'53:17','(پيغمبر جي اک) نڪي لڙي ۽ نڪي حد کان لنگھي.');
INSERT INTO "amroti" VALUES(53,18,'53:18','بيشڪ ڪي وڏيون نشانيون پنھنجي پالڻھار جون ڏٺائين.');
INSERT INTO "amroti" VALUES(53,19,'53:19','لات ۽ عُزّىٰ کي ڏٺو اَٿوَ ڇا؟');
INSERT INTO "amroti" VALUES(53,20,'53:20','۽ پوئين ٽين منات کي به؟');
INSERT INTO "amroti" VALUES(53,21,'53:21','ڇا اوھان لاءِ پٽ آھن ۽ الله لاءِ ڌيئرون.');
INSERT INTO "amroti" VALUES(53,22,'53:22','اِھو ورھاڱو اُنھيءَ مھل بي انصافيءَ وارو آھي.');
INSERT INTO "amroti" VALUES(53,23,'53:23','اھي (سڀ) رڳا نالا آھن جي اوھان پاڻ ۽ اوھان جي پِين ڏاڏن مُقرّر ڪيا آھن الله اُن (جي ثابتيءَ) جو ڪو دليل نازل نه ڪيو آھي، (ھيُ) رڳو گمان تي ھلندا آھن ۽ جيڪي (سندن) جيءَ سَڌ ڪندا آھن (تنھن تي به)، ۽ بيشڪ سندن پالڻھار کان وٽن ھدايت آئي آھي.');
INSERT INTO "amroti" VALUES(53,24,'53:24','ماڻھو جيڪا سڌ ڪندو آھي سا کيس ملندي ڇا؟');
INSERT INTO "amroti" VALUES(53,25,'53:25','پوءِ ھو جَڳ ۽ ھيُ جَڳ الله جو آھي.');
INSERT INTO "amroti" VALUES(53,26,'53:26','۽ آسمانن ۾ ڪيترائي ملائڪ آھن جن جي پارت ذري جيترو به نفعو نه ڪندي پر الله جي موڪل ڏيڻ کانپوءِ جنھن لاءِ گھري ۽ راضي ٿئي.');
INSERT INTO "amroti" VALUES(53,27,'53:27','بيشڪ جيڪي آخرت کي نه مڃيندا آھن سي ملائڪن تي ڌيئرن جا نالا رکندا آھن.');
INSERT INTO "amroti" VALUES(53,28,'53:28','۽ کين ان (ڳالھ) جي ڪا خبر نه آھي، رُڳو گمان تي ھلندا آھن، ۽ بيشڪ گمان حقيقت جي سڃاڻڻ ۾ ڪجھ به نفعو نه ڏيندو آھي.');
INSERT INTO "amroti" VALUES(53,29,'53:29','پوءِ (اي پيغمبر) انھيءَ کان منھن موڙ جنھن اسان جي ياد ڪرڻ کان مُنھن ڦيرايو ۽ دنيا جي حياتيءَ کانسواءِ (ٻيو) ڪي نه گھريائين.');
INSERT INTO "amroti" VALUES(53,30,'53:30','اھا سندن علم جي پڄاڻي آھي، بيشڪ تنھنجو پالڻھار اُنھيءَ کي چڱيءَ طرح ڄاڻندڙ آھي جيڪو سندس واٽ کان ڀُلو ۽ جيڪو سڌي رستي ھليو تنھنکي (به) اُھو چڱي طرح ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(53,31,'53:31','۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله جو آھي ته جن بڇڙا ڪم ڪيا تن کي انھن ڪمن جي ڪري نيٺ سزا ڏئي ۽ جن چڱايون ڪيون تن کي ڀلائيءَ جو بدلو ڏئي.');
INSERT INTO "amroti" VALUES(53,32,'53:32','جيڪي ننڍن گناھن کانسواءِ وڏن گناھن ۽ بي حيائيءَ کان پاڻ کي بچائيندا آھن، بيشڪ تنھنجو پالڻھار (اُنھن لاءِ) وڏي بخشش وارو آھي، اُھو اوھان کي وڌيڪ ڄاڻندڙ آھي جڏھن اوھان کي زمين مان پيدا ڪيائين ۽ جڏھن اوھين پنھنجي مائن جي پيٽن ۾ ٻار ھئؤ، تنھنڪري اوھين پاڻ کي نه ساراھيو، الله انھيءَ کي چڱي طرح ڄاڻندڙ آھي جيڪو پرھيزگار ٿيو.');
INSERT INTO "amroti" VALUES(53,33,'53:33','(اي پيغمبر) ڇا پوءِ ڏٺو اٿئي انھي کي جيڪو ڦريو.');
INSERT INTO "amroti" VALUES(53,34,'53:34','۽ ٿورو (مال) ڏنائين ۽ سخت دل ٿيو.');
INSERT INTO "amroti" VALUES(53,35,'53:35','وٽس ڳجھ جي خبر آھي ڇا جو ڄڻڪ اُھو (سڀ ڪجھ) اکين سان ڏسندو آھي.');
INSERT INTO "amroti" VALUES(53,36,'53:36','اُنھيءَ جي خبر کيس نه ڏني وئي ڇا جيڪي موسىٰ جي صحيفن ۾ (لکيل) آھي.');
INSERT INTO "amroti" VALUES(53,37,'53:37','۽ (جيڪي) ابراھيم (جي صحيفن ۾ آھي) جنھن (الله جي حَقن کي) پورو ڪيو.');
INSERT INTO "amroti" VALUES(53,38,'53:38','(ھيُ حُڪم) ته ڪو بار کڻندڙ ڪنھن ٻئي (جي گناھ) جو بار نه کڻندو.');
INSERT INTO "amroti" VALUES(53,39,'53:39','۽ ته ماڻھوءَ کي رڳو اُھائي (ڪمائي) ملندي جيڪا ڪيائين.');
INSERT INTO "amroti" VALUES(53,40,'53:40','۽ ته سندس ڪمائي جلد ڏٺي ويندي.');
INSERT INTO "amroti" VALUES(53,41,'53:41','وري کيس تمام پورو بدلو (انھي ڪمائي موجب) ڏنو ويندو.');
INSERT INTO "amroti" VALUES(53,42,'53:42','۽ ته تنھنجي پالڻھار ڏانھن موٽڻ آھي.');
INSERT INTO "amroti" VALUES(53,43,'53:43','۽ ته اُن کلايو ۽ رئاريو.');
INSERT INTO "amroti" VALUES(53,44,'53:44','۽ ته اُن ماريو ۽ جياريو.');
INSERT INTO "amroti" VALUES(53,45,'53:45','۽ ته اُن (الله) نر ۽ مادي جوڙو جوڙو بڻايو.');
INSERT INTO "amroti" VALUES(53,46,'53:46','منيءَ جي ھڪ ڦڙي مان جڏھن (ڳڀيرڻ ۾) ھاريو وڃي.');
INSERT INTO "amroti" VALUES(53,47,'53:47','۽ ته اُن تي اُھو ٻيو (ڀيرو) پيدا ڪرڻ لازم آھي.');
INSERT INTO "amroti" VALUES(53,48,'53:48','۽ ته اُن آسودو ڪيو ۽ موڙي ڏنائين.');
INSERT INTO "amroti" VALUES(53,49,'53:49','۽ ته اُھو ئي شِعۡرىٰ تاري جو پالڻھار آھي.');
INSERT INTO "amroti" VALUES(53,50,'53:50','۽ ته اُن پھرين عاد کي ناس ڪيو.');
INSERT INTO "amroti" VALUES(53,51,'53:51','۽ ثمود کي (به) پوءِ انھن مان (ڪنھين کي) باقي نه ڇڏيائين.');
INSERT INTO "amroti" VALUES(53,52,'53:52','۽ اُن کان اڳ نُوح جي قوم کي به ھلاڪ ڪيائين، ڇوته اُھي ڏاڍا ظالم ۽ تمام حد کان لنگھيل ھوا.');
INSERT INTO "amroti" VALUES(53,53,'53:53','۽ (شھر) اونڌي ڪيل کي ھيٺ اُڇليائين.');
INSERT INTO "amroti" VALUES(53,54,'53:54','پوءِ اُن کي ڍڪيو جنھن ڍڪيو.');
INSERT INTO "amroti" VALUES(53,55,'53:55','پوءِ اي ماڻھو پنھنجي پالڻھار جي نعمتن مان ڪھڙيءَ ۾ شڪ ٿو ڪرين.');
INSERT INTO "amroti" VALUES(53,56,'53:56','ھي (پيغمبر) اڳين ڊيڄاريندڙن (جي جنس) مان (ھڪ) ڊيڄاريندڙ آھي.');
INSERT INTO "amroti" VALUES(53,57,'53:57','ويجھي اچڻ واري (قيامت) ويجھي آھي.');
INSERT INTO "amroti" VALUES(53,58,'53:58','اُن کي الله کانسواءِ ڪو ظاھر ڪرڻ وارو ڪونھي.');
INSERT INTO "amroti" VALUES(53,59,'53:59','ھن ڳالھ کان تعجب ڪندا آھيو ڇا؟');
INSERT INTO "amroti" VALUES(53,60,'53:60','۽ کلندا آھيو ۽ نه رئندا آھيو.');
INSERT INTO "amroti" VALUES(53,61,'53:61','۽ اوھين راند ڪندڙ آھيو.');
INSERT INTO "amroti" VALUES(53,62,'53:62','پوءِ الله کي سجدو ڪريو ۽ عبادت ڪريو.');
INSERT INTO "amroti" VALUES(54,1,'54:1','قيامت ويجھي آئي ۽ چنڊ چيرجي پيو.');
INSERT INTO "amroti" VALUES(54,2,'54:2','۽ جيڪڏھن (ڪافر) ڪا نشاني ڏسندا ته مُنھن موڙيندا ۽ چوندا ته ھيء قديمي جادو آھي.');
INSERT INTO "amroti" VALUES(54,3,'54:3','۽ ڪوڙ ڄاتائون ۽ پنھنجن سَڌن تي ھليا ۽ سڀڪو ڪم پنھنجي وقت تي ٺھرايل آھي.');
INSERT INTO "amroti" VALUES(54,4,'54:4','۽ بيشڪ وٽن اُھي خبرون آيون آھن جن ۾ جھڻڪ آھي.');
INSERT INTO "amroti" VALUES(54,5,'54:5','پوري ڏاھپ (به) آھي پوءِ عِبرت جون ڳالھيون کين ڪو فائدو نه ٿيون ڏين.');
INSERT INTO "amroti" VALUES(54,6,'54:6','تنھنڪري کانئن مُنھن موڙ، جنھن ڏينھن سڏيندڙ اَڻ وڻندڙ شيءَ ڏانھن سڏيندو.');
INSERT INTO "amroti" VALUES(54,7,'54:7','(تنھن ڏينھن) پنھنجن جھڪن اکين سان قبرن مان ائين نڪرندا جو ڄڻڪ اُھي پکڙيل مڪڙ آھن.');
INSERT INTO "amroti" VALUES(54,8,'54:8','(اُن) سڏيندڙ ڏانھن ڊوڙندا ويندا، ڪافر ته ھيُ سخت ڏينھن آھي.');
INSERT INTO "amroti" VALUES(54,9,'54:9','انھن کان اڳ نوح جي قوم ڪوڙ ڀانيو پوءِ اسان جي ٻانھي کي ڪوڙو ڄاتائون ۽ چيائون ته چريو آھي ۽ ساڻس سخت ڳالھايو ويو.');
INSERT INTO "amroti" VALUES(54,10,'54:10','پوءِ ھن پنھنجي پالڻھار کي عرض ڪيو (۽ چيائين) ته بيشڪ آءٌ ھيڻو آھيان تنھنڪري تون کائن وير وٺ.');
INSERT INTO "amroti" VALUES(54,11,'54:11','پوءِ آسمان جا دروازا گھڻي وھندڙ پاڻيءَ سان کولياسون.');
INSERT INTO "amroti" VALUES(54,12,'54:12','۽ زمين مان چشمان جاري ڪياسون پوءِ (چوڌاري) پاڻي گڏ ٿيو جنھن ڪم لاءِ (اُھو) ٺھرايو ويو ھو.');
INSERT INTO "amroti" VALUES(54,13,'54:13','۽ کيس تختن ۽ ميخن واريءَ (ٻيڙي) تي چاڙھيوسون.');
INSERT INTO "amroti" VALUES(54,14,'54:14','جا اسان جي اکين آڏو ٿي ھلي، انھيءَ شخص جي بدلي وٺڻ لاءِ جنھن کي نه مڃيو ھوائون.');
INSERT INTO "amroti" VALUES(54,15,'54:15','۽ بيشڪ اُنھي (سزا) کي (عبرت لاءِ) ھڪ نشاني ڪري ڇڏيوسون پوءِ آھي ڪو نصيحت وٺڻ وارو؟');
INSERT INTO "amroti" VALUES(54,16,'54:16','پوءِ منھنجو عذاب ۽ منھنجا دڙڪا ڪھڙي طرح ھوا.');
INSERT INTO "amroti" VALUES(54,17,'54:17','۽ بيشڪ قرآن کي نصيحت وٺڻ لاءِ آسان ڪيو اٿون پوءِ آھي ڪو نصيحت وٺڻ وارو؟');
INSERT INTO "amroti" VALUES(54,18,'54:18','عاد (قوم) ڪوڙ ڀانيو پوءِ منھنجو عذاب ۽ منھنجا دڙڪا ڪھڙيءَ طرح ھوا؟');
INSERT INTO "amroti" VALUES(54,19,'54:19','بيشڪ اسان سخت طوفان کي تمام نڀاڳي ڏينھن ۾ مٿن موڪليو.');
INSERT INTO "amroti" VALUES(54,20,'54:20','جو ماڻھن کي پٽيائين ٿي ڄڻڪ اُھي پاڙون پٽيل کجيءَ جا تڙ آھن.');
INSERT INTO "amroti" VALUES(54,21,'54:21','پوءِ منھنجو عذاب ۽ منھنجا دڙڪا ڪھڙي طرح ھوا؟');
INSERT INTO "amroti" VALUES(54,22,'54:22','۽ بيشڪ قرآن کي نصيحت وٺڻ لاءِ آسان ڪيو اٿون پوءِ آھي ڪو نصيحت وٺڻ وارو؟');
INSERT INTO "amroti" VALUES(54,23,'54:23','ثمود جي قوم ڊيڄاريندڙن کي ڪوڙو ڀانيو.');
INSERT INTO "amroti" VALUES(54,24,'54:24','پوءِ چيائون ته پنھنجي قوم مان ھڪ اھڙي ماڻھوءَ جي تابعداري ڪريون ڇا؟ (جي ائين ڪريون ته) بيشڪ اسين انھي مھل ضرور گمراھي ۽ چريائي ۾ ھونداسون.');
INSERT INTO "amroti" VALUES(54,25,'54:25','اسان جي وچان اُنھيءَ تي (ئي) وحيُ نازل ڪيو ويو ڇا؟ بلڪ اُھو وڏو ڪوڙو پاڻ پڏائيندڙ آھي.');
INSERT INTO "amroti" VALUES(54,26,'54:26','سڀاڻي ڄاڻندا ته پاڻ پڏائيندڙ ڏاڍو ڪوڙو ڪير آھي.');
INSERT INTO "amroti" VALUES(54,27,'54:27','بيشڪ اسين سندن پرک لاءِ (ھڪ) ڏاچي موڪلينداسون پوءِ (اي صالح) انھن جو منتظر رھ ۽ صبر ڪر.');
INSERT INTO "amroti" VALUES(54,28,'54:28','۽ کين خبردار ڪر ته سندن وچ ۾ پاڻي وراھيل آھي، ھر ڪو (پنھنجي) واري تي حاضر ٿئي.');
INSERT INTO "amroti" VALUES(54,29,'54:29','پوءِ پنھنجي سنگتيءَ کي سڏيائون پوءِ وڌيڪ ھلت ڪيائين ۽ منڊو ڪيائين.');
INSERT INTO "amroti" VALUES(54,30,'54:30','پوءِ منھنجو عذاب ۽ منھنجا دڙڪا ڪھڙي طرح ھوا؟');
INSERT INTO "amroti" VALUES(54,31,'54:31','بيشڪ اسان ھڪ ڌڪاءُ مٿن موڪليو پوءِ ڀتي پيھر وانگر (ڍير) ٿي پيا.');
INSERT INTO "amroti" VALUES(54,32,'54:32','۽ بيشڪ قرآن کي نصيحت وٺڻ لاءِ آسان ڪيو اَٿون پوءِ آھي ڪو نصيحت وٺڻ وارو؟');
INSERT INTO "amroti" VALUES(54,33,'54:33','لُوط جي قوم ڊيڄاريندڙن کي ڪوڙو ڄاتو.');
INSERT INTO "amroti" VALUES(54,34,'54:34','بيشڪ اسان مٿن ڪنڪريٽ وسائيندڙ واءُ کي موڪليو سواءِ لُوط جي گھر وارن جي، جو انھن کي اَسر مھل بچايوسون.');
INSERT INTO "amroti" VALUES(54,35,'54:35','پاڻ وٽان فضل سان، جنھن شڪرانو ڪيو تنھن کي ائين ئي بدلو ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(54,36,'54:36','۽ بيشڪ (لُوط) اسان جي پڪڙڻ کان کين ڊيڄاريو ھو پوءِ ھٿون انھن ڊيڄارڻ جي ڪري تڪرار ڪيائون.');
INSERT INTO "amroti" VALUES(54,37,'54:37','۽ بيشڪ لُوط کي سندس مھمانن بَنسبت ڇڪتاڻ ڪيائون پوءِ سندن اکين (جي ديد) کي وڃايوسون (پوءِ چيوسون) ته منھنجو عذاب ۽ منھنجا دڙڪا چکو.');
INSERT INTO "amroti" VALUES(54,38,'54:38','۽ بيشڪ ھميشه وارو عذاب انھن تي صُبح جو پھتو.');
INSERT INTO "amroti" VALUES(54,39,'54:39','پوءِ (چيو وين) ته منھنجو عذاب ۽ منھنجا دڙڪا چکو.');
INSERT INTO "amroti" VALUES(54,40,'54:40','۽ بيشڪ قرآن کي نصيحت وٺڻ لاءِ آسان ڪيو اَٿون پوءِ آھي ڪو نصيحت وٺڻ وارو.');
INSERT INTO "amroti" VALUES(54,41,'54:41','۽ بيشڪ فرعون جي قوم ڏانھن ڊيڄاريندڙ آيا.');
INSERT INTO "amroti" VALUES(54,42,'54:42','ھنن اسان جي مڙني نشانين کي ڪوڙو ڄاتو تنھنڪري کين غالب پڪڙڻ واري وانگر پڪڙيوسون.');
INSERT INTO "amroti" VALUES(54,43,'54:43','(اي قريشؤ) اوھان جا ڪافر انھن ٽولين کان وڌيڪ ڀلا آھن ڇا يا اوھان لاءِ اڳين ڪتابن ۾ ڪا ڇوٽڪي جي چٺي آھي ڇا؟');
INSERT INTO "amroti" VALUES(54,44,'54:44','چوندا آھن ڇا ته اسين وير وٺڻ واري جماعت آھيون.');
INSERT INTO "amroti" VALUES(54,45,'54:45','انھي جماعت کي جلد شڪست ڏبي ۽ پُٺي ڦيرائيندا.');
INSERT INTO "amroti" VALUES(54,46,'54:46','بلڪ سندن انجام جي جاءِ قيامت آھي ۽ قيامت ڏاڍي سخت ۽ تمام ڪوڙي آھي.');
INSERT INTO "amroti" VALUES(54,47,'54:47','بيشڪ ڏوھاري گمراھي ۽ چريائي ۾ آھن.');
INSERT INTO "amroti" VALUES(54,48,'54:48','(ياد ڪر) جنھن ڏينھن انھن کي سندن مُنھن ڀر (دوزخ جي) باھ ۾ گھلبو، چئبو ته ھاڻي دوزخ جو مزو چکو.');
INSERT INTO "amroti" VALUES(54,49,'54:49','بيشڪ اسان سڀڪنھن شيء کي ٺھرايل انداز سان بڻايو.');
INSERT INTO "amroti" VALUES(54,50,'54:50','۽ اسان جو ڪم رڳو اک ڇنڀ ۾ ٿيڻ جھڙو ھڪ سُخن آھي.');
INSERT INTO "amroti" VALUES(54,51,'54:51','۽ بيشڪ اوھان جھڙيون (ڪيتريون ئي) ٽوليون ھلاڪ ڪيون سون پوءِ آھي ڪو نصيحت وٺڻ وارو.');
INSERT INTO "amroti" VALUES(54,52,'54:52','۽ جيڪي ڪيو اٿن سو (سڀ سندن) اعمالنامن ۾ (لکيل) آھي.');
INSERT INTO "amroti" VALUES(54,53,'54:53','۽ سڀڪي ننڍو وڏو لکيل آھي.');
INSERT INTO "amroti" VALUES(54,54,'54:54','بيشڪ پرھيزگار باغن ۽ نھرين ۾ ھوندا.');
INSERT INTO "amroti" VALUES(54,55,'54:55','اُھي سچي مجلس ۾ سگھ واري بادشاھ وٽ رھندا.');
INSERT INTO "amroti" VALUES(55,1,'55:1','ٻاجھاري (الله).');
INSERT INTO "amroti" VALUES(55,2,'55:2','قرآن سيکاريو.');
INSERT INTO "amroti" VALUES(55,3,'55:3','ماڻھو کي پيدا ڪيائين.');
INSERT INTO "amroti" VALUES(55,4,'55:4','ان کي پڌرو ڳالھائڻ سيکاريائين.');
INSERT INTO "amroti" VALUES(55,5,'55:5','سج ۽ چنڊ پوري حساب تي ھلندا آھن.');
INSERT INTO "amroti" VALUES(55,6,'55:6','۽ وليون ۽ وڻ (الله کي) سجدو ڪندا آھن.');
INSERT INTO "amroti" VALUES(55,7,'55:7','۽ آسمان کي بلند ڪيائين ۽ انصاف جي ترازي رکيائين.');
INSERT INTO "amroti" VALUES(55,8,'55:8','ھن لاءِ ته تور ۾ حد کان نه لنگھو.');
INSERT INTO "amroti" VALUES(55,9,'55:9','۽ انصاف سان پورو توريندا رھو ۽ تور ۾ کوٽ نه ڪريو.');
INSERT INTO "amroti" VALUES(55,10,'55:10','ماڻھن لاءِ زمين کي پکيڙيائين.');
INSERT INTO "amroti" VALUES(55,11,'55:11','منجھس (ھر جنس) جو ميوو ۽ چپڙين واريون کجيون آھن.');
INSERT INTO "amroti" VALUES(55,12,'55:12','۽ بُھ وارو اَنُّ ۽ سرھاڻيون (به اُن ۾) آھن.');
INSERT INTO "amroti" VALUES(55,13,'55:13','پوءِ (اي جنّؤ ۽ ماڻھو) پنھنجي رب جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,14,'55:14','ماڻھوءَ کي ٽِڪر جھڙي وڄندڙ مٽيءَ مان پيدا ڪيائين.');
INSERT INTO "amroti" VALUES(55,15,'55:15','۽ جنّ کي باھ جي الانبي مان بڻايائين.');
INSERT INTO "amroti" VALUES(55,16,'55:16','پوءِ (اي جنّؤ ۽ ماڻھو) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,17,'55:17','جو ٻنھي اوڀرن جو پالڻھار آھي ۽ ٻنھي اولھن جو پالڻھار آھي.');
INSERT INTO "amroti" VALUES(55,18,'55:18','پوءِ (اي جنّؤ ۽ ماڻھو) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,19,'55:19','ٻن دريائن (کاري ۽ مٺيءَ) کي وھايائين جو پاڻ ۾ گڏجي ٿا وھن.');
INSERT INTO "amroti" VALUES(55,20,'55:20','انھن ٻنھي جي وچ ۾ اوٽ آھي جو ھڪ ٻئي تي زيادتي نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(55,21,'55:21','پوءِ (اي جنّؤ ۽ ماڻھو) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,22,'55:22','انھن ٻنھي مان موتي ۽ مرجان نڪرندا آھن.');
INSERT INTO "amroti" VALUES(55,23,'55:23','پوءِ (اي جنّو ۽ ماڻھو) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,24,'55:24','۽ سمنڊ ۾ ھلندڙ جھاز (سڙھ کنيل) جبلن وانگر اُچا الله جا آھن.');
INSERT INTO "amroti" VALUES(55,25,'55:25','پوءِ (اي جنّو ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,26,'55:26','جيڪو به زمين تي آھي سو سڀ ناس ٿيندو.');
INSERT INTO "amroti" VALUES(55,27,'55:27','۽ (اي پيغمبر) تنھنجي پالڻھار جي ذات باقي رھندي جو عزت وارو ۽ نعمتن وارو آھي.');
INSERT INTO "amroti" VALUES(55,28,'55:28','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,29,'55:29','جيڪو به آسمانن ۽ زمين ۾ آھي سو سڀڪو الله کان گھرندو آھي، سڀڪنھن ڏينھن اُھو ڪم ۾ آھي.');
INSERT INTO "amroti" VALUES(55,30,'55:30','پوءِ (اي جنّو ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,31,'55:31','اي جنّو ۽ ماڻھؤ (اسين واندا ٿي) اوھان ڏانھن جلد متوجه ٿينداسون.');
INSERT INTO "amroti" VALUES(55,32,'55:32','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,33,'55:33','اي جِنّن ۽ ماڻھن جي ٽولي جيڪڏھن اوھين آسمانن ۽ زمين جي پاسن کان ٻاھر نڪري سگھندا آھيو ته نڪرو، وڏي ھمت کانسواءِ ٻي طرح نڪري نه سگھندؤ.');
INSERT INTO "amroti" VALUES(55,34,'55:34','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,35,'55:35','اوھان تي باھ جو اُلنبو ۽ دُونھون موڪلبو جو مقابلو ڪري نه سگھندؤ.');
INSERT INTO "amroti" VALUES(55,36,'55:36','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,37,'55:37','پوءِ جڏھن آسمان ڦاٽندو تڏھن اڌوڙ وانگر گلابي ٿيندو.');
INSERT INTO "amroti" VALUES(55,38,'55:38','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,39,'55:39','پوءِ اُن ڏينھن نڪي ماڻھو ۽ نڪي جن پنھنجي گناھ بابت پُڇبا.');
INSERT INTO "amroti" VALUES(55,40,'55:40','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙن نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,41,'55:41','ڏوھاري پنھنجي شڪلين مان پيا سڃاتا ويندا ۽ پوءِ ڄُنڊن ۽ پيرن مان پڪڙبا.');
INSERT INTO "amroti" VALUES(55,42,'55:42','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,43,'55:43','(چئبو ته) ھي اُھو دوزخ آھي جنھن کي گنھگارن ڪوڙ ڀانيو ھو.');
INSERT INTO "amroti" VALUES(55,44,'55:44','انھيءَ (دوزخ) ۾ ۽ تَتي ٽھڪندڙ پاڻيءَ ۾ پيا ايندا ويندا.');
INSERT INTO "amroti" VALUES(55,45,'55:45','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,46,'55:46','۽ جيڪو پنھنجي پالڻھار جي آڏو بيھڻ کان ڊنو تنھن لاءِ ٻه باغ آھن.');
INSERT INTO "amroti" VALUES(55,47,'55:47','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,48,'55:48','ٻئي باغ گھاٽا آھن.');
INSERT INTO "amroti" VALUES(55,49,'55:49','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,50,'55:50','انھن (ٻن باغن) ۾ ٻه چشما وھن ٿا.');
INSERT INTO "amroti" VALUES(55,51,'55:51','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,52,'55:52','انھن ٻنھي (باغن) ۾ سڀڪنھن ميوي مان ٻه ٻه جنسون آھن.');
INSERT INTO "amroti" VALUES(55,53,'55:53','پوءِ (اي جنّو ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,54,'55:54','اھڙن وڇاڻن تي ٽيڪ ڏئي ويٺا ھوندا جنھن جو استر اِسۡتَبۡرق پٽ مان ھوندو، ۽ انھن ٻنھي باغن جو ميوو ويجھو ھوندو.');
INSERT INTO "amroti" VALUES(55,55,'55:55','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,56,'55:56','اُنھن (باغن جي ماڙين) ۾ (شرم ڪري) ھيٺ نگاھ ڪرڻ واريون (حُورون) ھونديون جن کي اُنھن کان اڳ نڪي ڪنھن ماڻھو ۽ جنّ ڇھيو آھي.');
INSERT INTO "amroti" VALUES(55,57,'55:57','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,58,'55:58','اُھي (حُورون) ڄڻڪ ياقُوت ۽ مَرجان آھن.');
INSERT INTO "amroti" VALUES(55,59,'55:59','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,60,'55:60','چڱائيءَ جو بدلو چڱائيءَ کانسواءِ ٻيو نه آھي.');
INSERT INTO "amroti" VALUES(55,61,'55:61','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھارجي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,62,'55:62','۽ اُنھن ٻن (باغن) کانسواءِ ٻيا ٻه باغ به آھن.');
INSERT INTO "amroti" VALUES(55,63,'55:63','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,64,'55:64','ٻئي ڏاڍا ساوا چھچ آھن.');
INSERT INTO "amroti" VALUES(55,65,'55:65','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,66,'55:66','اُنھن ٻنھي (باغن) ۾ ڦوھاري وانگر جوش سان پاڻي نڪرڻ وارا ٻه چشما ھوندا.');
INSERT INTO "amroti" VALUES(55,67,'55:67','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,68,'55:68','انھن ٻنھي باغن ۾ (ھر جنس جو) ميوو ۽ کجيون ۽ ڏاڙھون ھوندا.');
INSERT INTO "amroti" VALUES(55,69,'55:69','پوءِ (ي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,70,'55:70','انھن (باغن) ۾ سڳوريون سھڻيون زالون ھونديون.');
INSERT INTO "amroti" VALUES(55,71,'55:71','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,72,'55:72','حُورون تنبن منجھ پردہ نشين ھونديون.');
INSERT INTO "amroti" VALUES(55,73,'55:73','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,74,'55:74','انھن کي انھن (بھشتين) کان اڳ نڪي ماڻھو ۽ نڪي ڪنھن جنّ ڇھيو آھي.');
INSERT INTO "amroti" VALUES(55,75,'55:75','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,76,'55:76','ساون وھاڻن ۽ سھڻن پٿراڻين تي ٽيڪ ڏيئي ويٺل ھوندا.');
INSERT INTO "amroti" VALUES(55,77,'55:77','پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.');
INSERT INTO "amroti" VALUES(55,78,'55:78','تنھنجي پالڻھار عزت واري جو نالو وڏي برڪت وارو آھي.');
INSERT INTO "amroti" VALUES(56,1,'56:1','جڏھن قيامت ايندي.');
INSERT INTO "amroti" VALUES(56,2,'56:2','جنھنجي اچڻ ۾ ڪو ڪوڙ نه آھي.');
INSERT INTO "amroti" VALUES(56,3,'56:3','ڪن کي جھڪي ڪرڻ واري ۽ ڪن کي مٿاھين ڪرڻ واري.');
INSERT INTO "amroti" VALUES(56,4,'56:4','جڏھن زمين سخت ڌوڏبي.');
INSERT INTO "amroti" VALUES(56,5,'56:5','۽ جبل پوري طرح ڀڃي پڻي ڪيا ويندا.');
INSERT INTO "amroti" VALUES(56,6,'56:6','پوءِ اُڏاريل دُز وانگر ٿيندا.');
INSERT INTO "amroti" VALUES(56,7,'56:7','۽ اوھين تڏھن ٽي ٽوليون ٿيندؤ.');
INSERT INTO "amroti" VALUES(56,8,'56:8','پوءِ سڄي پاسي وارا، سڄي پاسي وارا ڪھڙا ته چڱا ھوندا.');
INSERT INTO "amroti" VALUES(56,9,'56:9','۽ کٻي پاسي وارا، کٻي پاسي وارا ڪھڙا (ته) بدحال ھوندا؟');
INSERT INTO "amroti" VALUES(56,10,'56:10','۽ (چڱاين ۾) اڳرائي ڪرڻ وارا اڳي ٿيڻ وارا.');
INSERT INTO "amroti" VALUES(56,11,'56:11','اھي (ئي) مقرب آھن.');
INSERT INTO "amroti" VALUES(56,12,'56:12','نعمتن وارن باغن ۾ ھوندا.');
INSERT INTO "amroti" VALUES(56,13,'56:13','گھڻا پھرين مان.');
INSERT INTO "amroti" VALUES(56,14,'56:14','۽ ٿورا پوين مان.');
INSERT INTO "amroti" VALUES(56,15,'56:15','(سون سان) اُڻيل تخت تي.');
INSERT INTO "amroti" VALUES(56,16,'56:16','ھڪ ٻئي جي آمھون سامھون ٿي مٿن ٽيڪ ڏئي ويٺل ھوندا.');
INSERT INTO "amroti" VALUES(56,17,'56:17','ھميشه رھڻ وارا نينگر وٽن پيا ايندا ويندا.');
INSERT INTO "amroti" VALUES(56,18,'56:18','آبخورن ۽ ڪوزن سان ۽ وھندڙ شراب مان (ڀريل) پيالا (آڻيندن).');
INSERT INTO "amroti" VALUES(56,19,'56:19','(اھڙو صاف) جنھن کان نڪي مٿي ۾ سور پوندن ۽ نڪي بيھوش ھوندا.');
INSERT INTO "amroti" VALUES(56,20,'56:20','۽ (پڻ ھر جنس جو) ميوو جتان وڻندن.');
INSERT INTO "amroti" VALUES(56,21,'56:21','۽ پکين جو گوشت جنھن قسم مان (پيو) گھرين.');
INSERT INTO "amroti" VALUES(56,22,'56:22','۽ وڏين اکين وارون حورون.');
INSERT INTO "amroti" VALUES(56,23,'56:23','(ڄڻڪ) ڍڪيل موتين جھڙيون.');
INSERT INTO "amroti" VALUES(56,24,'56:24','جيڪي ڪمايو ھوائون تنھنجي بدلي ۾.');
INSERT INTO "amroti" VALUES(56,25,'56:25','اتي نڪا اجائي بَڪ نڪا گناھ جھڙي ڳالھ ٻڌندا.');
INSERT INTO "amroti" VALUES(56,26,'56:26','پر ھر ڪو سلام سلام پيو چوندو.');
INSERT INTO "amroti" VALUES(56,27,'56:27','۽ سڄي پاسي وارا، سڄي پاسي وارا ڪھڙي حال ھوندا.');
INSERT INTO "amroti" VALUES(56,28,'56:28','لاسڙين ٻيرين ۾.');
INSERT INTO "amroti" VALUES(56,29,'56:29','۽ ڀريل ڦردار ڪيوڙن ۾.');
INSERT INTO "amroti" VALUES(56,30,'56:30','۽ ڊگھن پاڇن ۾.');
INSERT INTO "amroti" VALUES(56,31,'56:31','۽ (ھيٺ) ڪرندڙ پاڻي ۾.');
INSERT INTO "amroti" VALUES(56,32,'56:32','۽ گھڻن ميون ۾.');
INSERT INTO "amroti" VALUES(56,33,'56:33','نڪي کُٽندا ۽ نڪي (کائن) جھلبا.');
INSERT INTO "amroti" VALUES(56,34,'56:34','۽ اُچا وڇاڻا.');
INSERT INTO "amroti" VALUES(56,35,'56:35','بيشڪ اسان حورن کي ھڪ نموني تي پيدا ڪيو آھي.');
INSERT INTO "amroti" VALUES(56,36,'56:36','پوءِ کين ڪنواريون بڻايوسون.');
INSERT INTO "amroti" VALUES(56,37,'56:37','مڙسن کي وڻندڙ ھڪ جيڏيون.');
INSERT INTO "amroti" VALUES(56,38,'56:38','سڄي پاسي وارن لاءِ.');
INSERT INTO "amroti" VALUES(56,39,'56:39','گھڻي جماعت پھرين مان آھي.');
INSERT INTO "amroti" VALUES(56,40,'56:40','۽ گھڻي جماعت پوين مان آھي.');
INSERT INTO "amroti" VALUES(56,41,'56:41','۽ کٻي پاسي وارا، کٻي پاسي وارا ڪھڙا بدحال ھوندا.');
INSERT INTO "amroti" VALUES(56,42,'56:42','ڪوسي جھولي ۽ تَتي پاڻيءَ ۾ ھوندا.');
INSERT INTO "amroti" VALUES(56,43,'56:43','۽ ڪاري دونھين جي پاڇي ۾ ھوندا.');
INSERT INTO "amroti" VALUES(56,44,'56:44','جو نڪي ٿڌو ھوندو ۽ نڪي سھائيندڙ.');
INSERT INTO "amroti" VALUES(56,45,'56:45','بيشڪ اُھي ھن کان اڳ (دُنيا ۾) سُک ۾ پيل ھوا.');
INSERT INTO "amroti" VALUES(56,46,'56:46','۽ وڏي گناھ (شرڪ) تي ضد ڪندا ھوا.');
INSERT INTO "amroti" VALUES(56,47,'56:47','۽ چوندا ھوا ته، جڏھن مرنداسون ۽ مٽي ۽ ھڏا ٿينداسون (تڏھن) اسين وري (قبرن مان) اُٿاريا وينداسون ڇا؟');
INSERT INTO "amroti" VALUES(56,48,'56:48','يا اسان جا پھريان پيءُ ڏاڏا.');
INSERT INTO "amroti" VALUES(56,49,'56:49','(اي پيغمبر) چؤ ته بيشڪ اڳيان ۽ پويان.');
INSERT INTO "amroti" VALUES(56,50,'56:50','ھڪ مقرّر ڪيل ڏينھن جي وقت تي ضرور گڏ ڪيا ويندا.');
INSERT INTO "amroti" VALUES(56,51,'56:51','وري بيشڪ اوھين اي گمراھو ڪوڙ ڀائيندڙؤ.');
INSERT INTO "amroti" VALUES(56,52,'56:52','ضرور اوھين ٿوھر جي وڻ مان کائيندؤ.');
INSERT INTO "amroti" VALUES(56,53,'56:53','پوءِ منجھانئس پيٽن کي ڀريندؤ.');
INSERT INTO "amroti" VALUES(56,54,'56:54','پوءِ مٿانئس تَتل پاڻيءَ مان پيئندؤ.');
INSERT INTO "amroti" VALUES(56,55,'56:55','پوءِ اُڃايل اُٺن جي پيئڻ وانگر پيئندؤ.');
INSERT INTO "amroti" VALUES(56,56,'56:56','قيامت جي ڏينھن اِھا سندن مھماني آھي.');
INSERT INTO "amroti" VALUES(56,57,'56:57','اسان اوھان کي پيدا ڪيو پوءِ (وري جيارڻ کي) ڇونه سچ ڀائيندا آھيو.');
INSERT INTO "amroti" VALUES(56,58,'56:58','ڀلا ڏسو ته سھي ته جيڪا مني وجھندا آھيو.');
INSERT INTO "amroti" VALUES(56,59,'56:59','تنھن مان اوھين خلقيو ٿا ڇا؟ يا اسين خلقڻ وارا آھيون؟');
INSERT INTO "amroti" VALUES(56,60,'56:60','اسان اوھان ۾ موت مقرّر ڪيو آھي ۽ اسين عاجز نه آھيون.');
INSERT INTO "amroti" VALUES(56,61,'56:61','ته اوھان جھڙن کي مٽائي پيدا ڪريون ۽ اوھان کي اھڙي (شڪل) ۾ پيدا ڪريون جنھن کي ڄاڻندا ئي ئي نه ھجو.');
INSERT INTO "amroti" VALUES(56,62,'56:62','۽ بيشڪ پھرين پيدائش ڄاڻي چڪؤ پوءِ ڇونه ٿا نصيحت وٺو؟');
INSERT INTO "amroti" VALUES(56,63,'56:63','ڏسو ته سھي جيڪي اوھين پوکيندا آھيو.');
INSERT INTO "amroti" VALUES(56,64,'56:64','سو اوھين ڄمائيندا آھيو ڇا يا اسين ڄمائيندا آھيون؟');
INSERT INTO "amroti" VALUES(56,65,'56:65','جيڪڏھن گھُرون ته اُن کي ناس ڪري ڇڏيون پوءِ اوھين حيرت ۾ رھجي وڃو.');
INSERT INTO "amroti" VALUES(56,66,'56:66','(۽ پيا چئو) ته بيشڪ اسين ٽوٽي ۾ پئجي وياسون.');
INSERT INTO "amroti" VALUES(56,67,'56:67','بلڪ اسين بي نصيب آھيون.');
INSERT INTO "amroti" VALUES(56,68,'56:68','ڀلا ڏسو ته سھي جيڪو پاڻي پيئندا آھيو.');
INSERT INTO "amroti" VALUES(56,69,'56:69','اُھو اوھين ڪڪرن مان ھيٺ لاھيندا آھيو ڇا يا اسين ھيٺ لاھيندا آھيون؟');
INSERT INTO "amroti" VALUES(56,70,'56:70','جيڪڏھن گھُرون ته اُن کي کارو ڪري ڇڏيون پوءِ ڇونه شڪرانو ڪندا آھيو؟');
INSERT INTO "amroti" VALUES(56,71,'56:71','ڀلا ڏسو ته سھي ته جا باھ ٻاريندا آھيو.');
INSERT INTO "amroti" VALUES(56,72,'56:72','تنھن جي وڻ کي اوھان پيدا ڪيو آھيو آھي ڇا؟ يا اسين پيدا ڪندڙ آھيون.');
INSERT INTO "amroti" VALUES(56,73,'56:73','اسان اُن (وڻ) کي نصيحت وٺڻ لاءِ ۽ مسافرن جي فائدي لاءِ پيدا ڪيو آھي.');
INSERT INTO "amroti" VALUES(56,74,'56:74','تنھنڪري پنھنجي وڏي پالڻھار جي نالي کي پاڪائي سان ياد ڪر.');
INSERT INTO "amroti" VALUES(56,75,'56:75','پوءِ تارن جي ڪِرڻ جي جاءِ جو قسم کڻان ٿو.');
INSERT INTO "amroti" VALUES(56,76,'56:76','۽ جيڪڏھن ڄاڻو ته بيشڪ اُھو قسم وڏو آھي.');
INSERT INTO "amroti" VALUES(56,77,'56:77','بيشڪ اھو سڳورو قرآن آھي.');
INSERT INTO "amroti" VALUES(56,78,'56:78','لڪل ڪتاب ۾ لکيل آھي.');
INSERT INTO "amroti" VALUES(56,79,'56:79','پاڪائيءَ وارن کانسواءِ ٻيو ڪو اُن کي ھٿ نه لائيندو آھي.');
INSERT INTO "amroti" VALUES(56,80,'56:80','جھانن جي پالڻھار وٽان نازل ڪيل آھي.');
INSERT INTO "amroti" VALUES(56,81,'56:81','پوءِ اوھين ھن ڳالھ (جي مڃڻ) ۾ سُستي ڪرڻ وارا آھيو ڇا.');
INSERT INTO "amroti" VALUES(56,82,'56:82','۽ اُن کي ڪوڙ ڀانئين پنھنجو حصّو ڪريو ٿا.');
INSERT INTO "amroti" VALUES(56,83,'56:83','پوءِ جنھن مھل (ڪنھنجو) ساھ نر گھٽ وٽ پھچي.');
INSERT INTO "amroti" VALUES(56,84,'56:84','۽ اوھين انھيءَ مھل پيا ٿا ڏسو.');
INSERT INTO "amroti" VALUES(56,85,'56:85','۽ اسين اوھان جي به نسبت اُن ڏانھن تمام ويجھا آھيون پر اوھين نه ڏسندا آھيو.');
INSERT INTO "amroti" VALUES(56,86,'56:86','پوءِ جيڪڏھن (اوھين خدا جي حُڪم ۾) لاچار ٿيل نه آھيو.');
INSERT INTO "amroti" VALUES(56,87,'56:87','ته جيڪڏھن سچا آھيو ته روح کي ڇو نه موٽايو.');
INSERT INTO "amroti" VALUES(56,88,'56:88','پوءِ جيڪڏھن (مئل الله جي) ويجھن ٻانھن مان ھوندو.');
INSERT INTO "amroti" VALUES(56,89,'56:89','ته اُن لاءِ خوشي ۽ سُرھاڻ ۽ نعمت وارو باغ آھي.');
INSERT INTO "amroti" VALUES(56,90,'56:90','۽ جيڪڏھن سڄي پاسي وارن مان ھوندو.');
INSERT INTO "amroti" VALUES(56,91,'56:91','ته سڄي پاسي وارن مان توکي سلامتي آھي.');
INSERT INTO "amroti" VALUES(56,92,'56:92','پر جيڪڏھن اُھو ڪُوڙ ڀانئيندڙن گمراھن مان ھوندو.');
INSERT INTO "amroti" VALUES(56,93,'56:93','ته (اُن لاءِ) تَتل پاڻي مھماني.');
INSERT INTO "amroti" VALUES(56,94,'56:94','۽ دوزخ ۾ گھيڙڻ آھي.');
INSERT INTO "amroti" VALUES(56,95,'56:95','بيشڪ ھي خبر پوري يقيني آھي.');
INSERT INTO "amroti" VALUES(56,96,'56:96','تنھنڪري پنھنجي پالڻھار وڏي جي نالي کي پاڪائيءَ سان ياد ڪر.');
INSERT INTO "amroti" VALUES(57,1,'57:1','جيڪي آسمانن ۽ زمين ۾ آھي سو سڀ الله جي پاڪائي بيان ڪندو آھي، ۽ اُھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(57,2,'57:2','آسمانن ۽ زمين جي بادشاھي اُن جي آھي، (اُھو) جياريندو آھي ۽ ماريندو آھي، ۽ اُھو سڀڪنھن شيءِ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(57,3,'57:3','اُھو (سڀ کان) پھريون ۽ (سڀ کان) پويون ۽ پڌرو ۽ ڳجھو آھي، ۽ اُھو سڀڪنھن شيءِ کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(57,4,'57:4','(الله) اُھو آھي جنھن آسمانن ۽ زمين کي ڇھن ڏينھن ۾ بڻايو وري عرش تي متوجه ٿيو، جيڪي زمين ۾ گھڙندو آھي ۽ جيڪي نڪرندو آھي ۽ جيڪي آسمان مان لھندو آھي ۽ جيڪي منجھس چڙھي ويندو آھي سو (سڀ) اُھو ڄاڻندو آھي، ۽ جتي اوھين ھجو اُتي اھو اوھان سان گڏ آھي، ۽ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(57,5,'57:5','آسمانن ۽ زمين جي بادشاھي اُن جي آھي، ۽ الله ڏانھن سڀ ڪم ورايا وڃن ٿا.');
INSERT INTO "amroti" VALUES(57,6,'57:6','رات کي ڏينھن ۾ گھيڙيندو آھي ۽ ڏينھن کي رات ۾ گھيڙيندو آھي، ۽ اُھوسِينن وارو (ڳجھ) ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(57,7,'57:7','الله ۽ سندس پيغمبر تي ايمان آڻيو ۽ اُنھيءَ مال مان خرچيو جنھن ۾ اوھان کي ٻـين جو وارث ڪيو اٿس، ۽ اوھان مان جن ايمان آندو ۽ (الله جي واٽ ۾) خرچ ڪيو تن لاءِ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(57,8,'57:8','۽ اوھان کي ڇا (ٿيو) آھي جو الله تي ايمان نه ٿا آڻيو؟ حالانڪ پيغمبر اوھان کي سڏي ٿو ته اوھين پنھنجي پالڻھار تي ايمان آڻيو ۽ بيشڪ الله اوھان کان انجام ورتو آھي جيڪڏھن مڃڻ وارا آھيو.');
INSERT INTO "amroti" VALUES(57,9,'57:9','(الله) اُھو آھي جيڪو پنھنجي ٻانھي (محمّد) تي پڌريون آيتون نازل ڪندو آھي ته (ڪفر جي) اونداھين مان (اسلام جي) سوجھري ڏانھن اوھان کي ڪڍي، ۽ بيشڪ الله اوھان تي شفقت ڪندڙ مھربان آھي.');
INSERT INTO "amroti" VALUES(57,10,'57:10','۽ اوھان کي ڇا (ٿيو) آھي جو الله جي واٽ ۾ نه خرچيندا آھيو حالانڪ آسمانن ۽ زمين جي ميراث خاص الله جي آھي، جنھن (مڪي جي) فتح کان اڳ خرچ ڪيو ۽ (الله جي دين لاءِ ڪافرن سان) وڙھيو تنھنجي برابر اوھان مان ڪوبه ڪونھي، اھي انھن کان تمام وڏي مرتبي وارا آھن جن (فتح کان) پوءِ خرچ ڪيو ۽ (دين لاءِ) وڙھيا، ۽ ھر ھڪ کي الله چڱو انجام ڏنو آھي، ۽ جيڪي ڪندا آھيو تنھن جي الله (پوري) خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(57,11,'57:11','اُھو ڪير آھي جو الله کي چڱو قرض ڏئي پوءِ (الله) اُھو (قرض) اُن کي ٻيڻو ڪري ڏيندو ۽ اُن لاءِ سڳورو اجر آھي.');
INSERT INTO "amroti" VALUES(57,12,'57:12','(ياد ڪر) جنھن ڏينھن مؤمنن ۽ مؤمنياڻين کي (ھن حالت ۾) ڏسندين جو سندن نُور اُنھن جي اڳيان ۽ انھن جي ساڄن پاسن کان پيو ڊوڙندو (چئبن ته) اوھان کي خوشخبري ھجي جو اڄ اوھان لاءِ اھڙا باغ آھن جن جي ھيٺان واھيون پيون وَھن اُتي سدائين رھندؤ، اھا وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(57,13,'57:13','جنھن ڏينھن منافق ۽ منافقياڻيون (گڏجي) مؤمنن کي چوندا ته ترسو (ته سھي) ته اوھان جي (ايماني) نُور مان (ڪجھ) روشني وٺون چيو ويندو ته اوھين پنھنجي پٺ تي (دنيا ڏانھن) موٽو پوءِ ڪو سوجھرو ڳوليو، پوءِ سندن وچ ۾ ھڪ اھڙي ڀت ھنئي ويندي جنھن کي دروازو ھوندو، اُن ڀت جي اندارن رحمت ھوندي ۽ ان جي ٻاھرين پاسي کان عذاب.');
INSERT INTO "amroti" VALUES(57,14,'57:14','منافق مُسلمانن کي سڏيندا ۽ چوندا ته اسين اوھان سان (دنيا ۾) گڏ نه ھواسون ڇا؟ (مُسلمان) چوندا ته ھائو پر پنھنجو پاڻ کي جوکي ۾ وڌوَ ۽ (ايمان آڻڻ ۾) دير ڪندا رھيؤ ۽ (نبوت ۾) شڪ آندو ۽ اوھان کي سَڌن ٺڳي ڇڏيو تان جو الله جو حُڪم آيو ۽ اوھان کي الله (جي حُڪم مڃڻ) کان (شيطان) ٺڳيندڙ ٺڳي ڇڏيو.');
INSERT INTO "amroti" VALUES(57,15,'57:15','تنھن ڪري (اي منافقؤ) اڄ نڪي اوھان کان ۽ نڪي ڪافرن کان ڪو بدلو وٺبو، اوھان جي جاءِ (دوزخ جي) باھ آھي، اھا اوھان جي لائق آھي، ۽ اھا موٽڻ جي جاءِ بڇڙي آھي.');
INSERT INTO "amroti" VALUES(57,16,'57:16','اڃا مؤمنن لاءِ اُھو وقت نه آيو آھي ڇا؟ جو الله جي ياد ڪرڻ وقت ۽ وحي الٰھيءَ مان جيڪو نازل ٿيو تنھن (جي ياد) آڻڻ وقت سندن دليون آزي ڪن ۽ انھن وانگر نه ٿين جن کي ھن کان اڳ ڪتاب ڏنو ويو پوءِ مٿن ڊگھي مدت گذري تنھنڪري سندن دليون سخت ٿي ويون، ۽ منجھائن گھڻا بي دين آھن.');
INSERT INTO "amroti" VALUES(57,17,'57:17','ڄاڻو ته الله زمين کي سندس ويرانيءَ کانپوءِ آباد ڪندو آھي، بيشڪ اسان اوھان لاءِ چٽيون نشانيون بيان ڪيون آھن ته مانَ اوھين پرجھو.');
INSERT INTO "amroti" VALUES(57,18,'57:18','بيشڪ خيرات ڏيندڙن ۽ خيرات ڏيندڙن کي، ۽ جن الله کي سھڻو قرض ڏنو آھي تن کي ٻيڻو ڪري ڏبو ۽ اُنھن لاءِ سڳورو اجر آھي.');
INSERT INTO "amroti" VALUES(57,19,'57:19','۽ جن الله ۽ سندس پيغمبرن تي ايمان آندو آھي اُھي ئي پنھنجي پالڻھار وٽ بلڪل سچا ۽ شھيد آھن، اُنھن لاءِ سندن اجر ۽ سندن نُور آھي، ۽ جن ڪفر ڪيو ۽ اسان جي آيتن کي ڪُوڙ ڀانيو اُھي دوزخي آھن.');
INSERT INTO "amroti" VALUES(57,20,'57:20','ڄاڻو ته دُنيا جي حياتي رڳو راند ۽ بي فائدِي ۽ سينگار ۽ پاڻ ۾ فخر ڪرڻ ۽ مالن ۽ اولادن ۾ (ھڪ ٻئي کان) گھڻائي طلبڻ آھي، (انھيءَ جو مثال) انھيءَ مينھن وانگر (آھي) جنھن جو سلو (ڄمائڻ) ڪڙمين کي عجب ۾ وجھي وري سُڪي وڃي پوءِ اُن کي پيلو ٿيل ڏسين وري ڀُري وڃي، ۽ آخرت ۾ (الله جي دشمنن لاءِ) سخت عذاب آھي ۽ (الله جي دوستن لاءِ) الله کان بخشش ۽ رضامندي آھي، ۽ دنيا جي حياتي رڳو ٺڳيءَ جو سامان آھي.');
INSERT INTO "amroti" VALUES(57,21,'57:21','پنھنجي پالڻھار جي بخشش ۽ انھي بھشت ڏانھن اڳرائي ڪريو جنھن جي ويڪرائي آسمان ۽ زمين جي ويڪرائي وانگر آھي انھن لاءِ تيار ڪيو ويو آھي جن الله ۽ سندس پيغمبرن تي ايمان آندو، اھو الله جو فضل آھي جنھن کي وڻيس تنھن کي ڏيندو آھي، ۽ الله وڏي فضل وارو آھي.');
INSERT INTO "amroti" VALUES(57,22,'57:22','ڪابه مُصيبت نڪي زمين ۾ ۽ نڪي اوھان جي جندن ۾ پھچندي آھي پر اُنھي کان اڳ اُن کي پيدا ڪريون ھڪ ڪتاب ۾ لکيل آھي، بيشڪ اھو (ڪم) الله کي آسان آھي.');
INSERT INTO "amroti" VALUES(57,23,'57:23','ھن ڪري (اھا خبر ڏني اٿون) ته جيڪي اوھان جي ھٿن مان ويو تنھن تي ارمان نه ڪريو ۽ جيڪي اوھان کي عطا ڪيائين تنھن تي خوشي نه ڪريو، ۽ الله سڀڪنھن ھٺيلي پاڻ سڏائيندڙ کي دوست نه رکندو آھي.');
INSERT INTO "amroti" VALUES(57,24,'57:24','جيڪي پيچائي ڪندا آھن ۽ ماڻھن کي به پيچائي ڏسيندا آھن، ۽ جيڪو منھن موڙيندو ته بيشڪ الله ئي بي پرواھ ساراھيل آھي.');
INSERT INTO "amroti" VALUES(57,25,'57:25','بيشڪ پنھنجن پيغمبرن کي پڌرين نشانين سان موڪليوسون ۽ ساڻن ڪتاب ۽ تارازي ھن لاءِ موڪليوسون ته ماڻھو انصاف سان بيھڪ وٺن، ۽ لوھ موڪليوسون جنھن جي ڪري سخت ويڙھ آھي ۽ ماڻھن لاءِ (ٻيا) فائدا (به) آھن ۽ ته جيڪو الله ۽ سندس پيغمبرن کي پرپُٺ مدد ڏئي تنھن کي الله معلوم ڪري، بيشڪ الله سگھارو غالب آھي.');
INSERT INTO "amroti" VALUES(57,26,'57:26','۽ بيشڪ نُوح ۽ ابراھيم کي موڪليوسون ۽ سندن اولاد ۾ پيغمبري ۽ ڪتاب (امانت) رکيوسون پوءِ منجھائن ڪي ھدايت وارا آھن، ۽ منجھائن گھڻا بي دين آھن.');
INSERT INTO "amroti" VALUES(57,27,'57:27','وري پٺيانئن پنھنجا (ٻيا) پيغمبر موڪلياسون ۽ پوئتان عيسىٰ پُٽ مريم جي کي موڪليوسون ۽ کيس انجيل ڏنوسون، ۽ جن سندس تابعداري ڪئي تن جي دلين ۾ نرمي ۽ ٻاجھ رکي سون، ۽ اُھا گوشه نشيني جا پاڻ ٺاھي ھوائون سا اسان مٿن فرض نه ڪئي ھُئي پر الله جي رضامندي جي طلب لاءِ پاڻ ٺاھي ھوائون پوءِ اُن جو پورو حق نظر نه رکيائون، پوءِ منجھائن جن ايمان آندو تن کي سندن اجر ڏنوسون، ۽ منجھائن گھڻا بي دين آھن.');
INSERT INTO "amroti" VALUES(57,28,'57:28','اي ايمان وارؤ الله کان ڊڄو ۽ سندس پيغمبر تي ايمان آڻيو ته اوھان کي پنھنجي ٻاجھ مان به ڀاڱا ڏئي ۽ اوھان کي اھڙو نُور ڏئي جنھن سان (رستو) ھلو ۽ اوھان کي بخشي، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(57,29,'57:29','ھن ڪري (الله ھي خبر ڏني) ته اھل ڪتاب جا ڄاڻن ته اُھي الله جي فضل مان ڪنھن شيءَ تي سگھ رکي نه سگھندا آھن ۽ ته فضل الله جي ھٿ ۾ آھي جنھن کي وڻيس تنھن کي ڏيندو آھي، ۽ الله وڏي فضل وارو آھي.');
INSERT INTO "amroti" VALUES(58,1,'58:1','بيشڪ الله انھيءَ (زال) جو ڳالھائڻ ٻڌو جنھن مڙس بابت توسان جھڳڙو ٿي ڪيو ۽ الله جي آڏو (پنھنجو) ڏک جو احوال (بيان) ٿي ڪيو، ۽ الله اوھان ٻنھي جو پاڻ ۾ ڳالھائڻ ٻڌو ٿي، بيشڪ الله ٻڌندڙ ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(58,2,'58:2','اوھان مان جيڪي پنھنجين زالن کي ماءُ ڪوٺي وھندا آھن اُھي (زالون ڪي) اُنھن جون مائر نه آھن، سندن مائر (ته) اُھي آھن جن کين ڄڻيو آھي، بيشڪ اُھي (ائين چوندڙ) ھڪ اڻ جڳائيندڙ ڳالھ ۽ ڪوڙ چوندا آھن، ۽ بيشڪ الله معافي ڏيندڙ بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(58,3,'58:3','۽ جيڪي پنھنجي زالن ئي ماءُ ڪوٺي وھندا آھن سي وري جيڪي چيائون تنھن کان (پوئتي) موٽندا آھن ته ٻنھي (زال مڙس) جي پاڻ ۾ ھٿ لائڻ کان اڳ (ھڪ) ٻانھون آجو ڪرڻ واجب آھي، انھي (حُڪم) سان اوھان کي نصيحت ڏجي ٿي، ۽ جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(58,4,'58:4','پوءِ جيڪو (ٻانھي کي) نه لھي (تنھن تي) ٻنھي جي پاڻ ۾ ھٿ لائڻ کان اڳ ٻه مھينا لڳولڳ روزا رکڻ (واجب) آھن، پوءِ جيڪي (روزا رکي) نه سگھي تنھن تي سٺ مسڪينن جو کارائڻ (واجب) آھي، اِھو (حُڪم) ھن لاءِ آھي ته (اوھين) الله ۽ سندس پيغمبر (جي حڪم) کي مڃيو، ۽ اھي الله جون (مقرر ڪيل) حدون آھن، ۽ مُنڪرن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(58,5,'58:5','بيشڪ جيڪي الله ۽ سندس پيغمبر جي مُخالفت ڪندا آھن تن کي (ائين) خوار ڪيو ويو جيئن اُنھن کي خوار ڪيو ويو جيڪي کائن اڳ ھوا ۽ بيشڪ پڌريون آيتون نازل ڪيون سون، ۽ مُنڪرن لاءِ خوار ڪندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(58,6,'58:6','جنھن ڏينھن انھن مڙني کي الله اُٿاريندو (تنھن ڏينھن) کين (انھيءَ جي) سُڌ ڏيندو جيڪي ڪمايو ھوائون، الله اُن کي ڳڻي رکيو آھي ۽ (ھنن) اُھو وساري ڇڏيو، ۽ الله ھر شيءِ تي خبر رکڻ وارو آھي.');
INSERT INTO "amroti" VALUES(58,7,'58:7','نه ڏٺو اٿئي ڇا ته جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله ڄاڻندو آھي، (ڪٿي به پاڻ ۾) ٽن ڄڻن جي ڳُجھي صلاح ڪرڻ نه ھوندي آھي پر اُنھن مان چوٿون الله آھي ۽ نڪي پنجن (ڄڻن) جي پر انھن ۾ ڇھون الله آھي ۽ نڪي ان (ڳاڻاٽي) کان تمام گھٽ ۽ تمام وڌ آھن پر الله ساڻن آھي جتي به ھجن، وري جيڪي ڪيو اٿن (تنھنجي) قيامت جي ڏينھن کين سُڌ ڏيندو، بيشڪ الله سڀڪنھن شيءِ کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(58,8,'58:8','(اي پيغمبر) انھن ڏانھن نه ڏٺو اٿئي ڇا جن کي ڳُجھين صلاحن ڪرڻ کان جھليو ويو وري جنھن کان جھليا ويا سوئي ڪندا آھن ۽ گناھ ۽ حد کان لنگھڻ ۽ پيغمبر جي نافرماني ڪرڻ بابت پاڻ ۾ ڳجھيون صلاحون ڪندا آھن، ۽ جڏھن تو وٽ ايندا آھن (تڏھن) توکي انھي (لفظ) سان سلام ڪندا آھن جنھن سان توکي الله سلام نه ڪيو آھي ۽ پنھنجين دلين ۾ چوندا آھن ته جيڪي چوندا آھيون تنھن جي ڪري الله اسان کي ڇونه ٿو عذاب ڪري؟ کين دوزخ بس آھي، ان ۾ پوندا، پوءِ (دوزخ) بڇڙي جاءِ آھي.');
INSERT INTO "amroti" VALUES(58,9,'58:9','اي ايمان وارؤ جڏھن (اوھين) پاڻ ۾ (ڪا) ڳُجھي صلاح ڪريو ته گناھ ۽ حد کان لنگھڻ ۽ پيغمبر جي نافرمانيءَ بابت (ڪابه) پاڻ ۾ صلاح نه ڪريو ۽ پاڻ ۾ چڱائي ۽ پرھيزگاريءَ بابت صلاح ڪريو، ۽ انھيءَ الله کان ڊڄو جنھن ڏانھن گڏ ڪيا ويندؤ.');
INSERT INTO "amroti" VALUES(58,10,'58:10','بڇڙي صلاح ڪرڻ ته شيطان جو ڪم آھي ھن لاءِ ته مؤمنن کي دلگير ڪري ۽ ھو الله جي حُڪم کانسواءِ کين ڪجھ به نُقصان پُھچائڻ وارو نه آھي، ۽ جڳائي ته مؤمن الله تي ڀروسو ڪن.');
INSERT INTO "amroti" VALUES(58,11,'58:11','اي ايمان وارؤ جڏھن اوھان کي چئجي ته (پنھنجين) مجلسن ۾ جاءِ ڪشادي ڪريو پوءِ (جاءِ) ڪشادي ڪريو ته الله به اوھان جي لاءِ ڪشادگي ڪري، ۽ جڏھن چئجي ته اُٿو تڏھن اُٿو ته اوھان مان جن ايمان آندو آھي ۽ جن کي علم ڏنو ويو تن جا مرتبا الله وڏا ڪري، ۽ جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(58,12,'58:12','اي ايمان وارؤ جڏھن اوھين (گھرو ته) پيغمبر سان ڪا ڳجھي ڳالھ ڪريو تڏھن پنھنجي ڳجھي ڳالھ ڪرڻ کان اڳ پھريائين خيرات ڏيو، اھو (ڪم) اوھان لاءِ ڀَلو ۽ تمام سٺو آھي، پوءِ جيڪڏھن ڪُجھ نه لھو ته بيشڪ الله بخشڻھار مھربا آھي.');
INSERT INTO "amroti" VALUES(58,13,'58:13','اوھين (انھي ڳالھ کان) ڊڄي ويؤ ڇا ته پنھنجي ڳالھ چوڻ کان اڳي خيرات ڏيو، پوءِ جڏھن اوھان نه ڪيو ۽ الله اوھان کان ٽارو ڪيو تڏھن نماز پڙھندا رھو ۽ زڪوٰة ڏيندا رھو ۽ الله ۽ سندس پيغمبر جو چيو مڃيو، ۽ جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(58,14,'58:14','(اي پيغمبر) انھن ڏانھن نه ڏٺو اٿئي ڇا جن ھڪ اھڙي قوم سان دوستي رکي جن تي الله ڏمريو آھي، نڪي اُھي (يعني منافق) اوھان مان آھن ۽ نڪي اُنھن (يھودين) مان آھن، ۽ اُھي ڄاڻي ٻجھي ڪوڙ تي قسم کڻندا آھن.');
INSERT INTO "amroti" VALUES(58,15,'58:15','اُنھن لاءِ الله سخت عذاب تيار ڪيو آھي، بيشڪ اُھي جيڪي ڪندا آھن سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(58,16,'58:16','پنھنجن قَسمن کي ڍال ڪري ورتو اٿن ۽ الله جي واٽ کان (ماڻھن کي) جھليائون تنھنڪري انھن لاءِ خواري ڏيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(58,17,'58:17','الله (جي عذاب) کان نڪي سندن مال ۽ نڪي سندن اولاد کانئن ڪجھ به ٽاريندو، اُھي دوزخي آھن، اِھي اُن ۾ سدائين رھندا.');
INSERT INTO "amroti" VALUES(58,18,'58:18','جنھن ڏينھن انھن مڙني کي الله اٿاريندو تنھن ڏينھن سندس آڏو (ائين) قسم کڻندا جئن اوھان جي آڏو قَسم کڻندا آھن ۽ ڀائيندا آھن ته اُھي ڪنھن چڱي ڳالھ تي آھن، خبردار ٿيو بيشڪ اُھي اصل ڪوڙا آھن.');
INSERT INTO "amroti" VALUES(58,19,'58:19','مٿن شيطان غالب ٿي ويو آھي پوءِ اُنھن (جي دل) کان الله جي يادگيري وساريائين، اِھي (ماڻھو) شيطان جي جماعت آھن، خبردار (ٿيو ته) بيشڪ شيطان جي جماعت ئي ٽوٽي واري آھي.');
INSERT INTO "amroti" VALUES(58,20,'58:20','بيشڪ جيڪي الله ۽ سندس پيغمبر (جي حُڪم) کان مخالفت ڪندا آھن سي تمام خواريءَ وارن (ماڻھن) ۾ آھن.');
INSERT INTO "amroti" VALUES(58,21,'58:21','الله لکي ڇڏيو آھي ته آءٌ ضرور غالب رھندس ۽ منھنجا پيغمبر به، بيشڪ الله سگھارو غالب آھي.');
INSERT INTO "amroti" VALUES(58,22,'58:22','(اي پيغمبر) اھڙي قوم جيڪا الله ۽ قيامت جي ڏينھن کي مڃيندي ھجي تنھن کي تون (ھن وصف سان) نه ڏسندين ته جنھن الله ۽ سندس پيغمبر جي مخالفت ڪئي آھي تنھن سان دوستي رکندا ھجن توڙيڪ اُھي سندن پيئر يا سندن پُٽ يا سندن ڀائر يا سندن مائٽ ھجن، اِھي (مؤمن اُھي) آھن جن جي دلين ۾ الله ايمان لکيو آھي ۽ جن کي پنھنجي فيض سان سگھ ڏني اٿس، ۽ انھن کي اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن ھو اُنھيءَ ۾ سدائين رھندا، الله کانئن راضي ٿيو ۽ اُھي کانئس راضي ٿيا، اُھي الله جي جماعت آھن، خبردار (ٿيو ته) بيشڪ الله جي جماعت ئي ڇُٽڻ واري آھي.');
INSERT INTO "amroti" VALUES(59,1,'59:1','جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو سڀ الله جي پاڪائي بيان ڪندو آھي، ۽ اُھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(59,2,'59:2','(الله) اُھو آھي جنھن ڪتاب وارن مان ڪافرن کي سندن گھرن مان پھرين ئي ويڙھ ۾ ٻاھر ڪڍي ڇڏيو، (اي مسلمانؤ) انھن جي نڪرڻ جو اوھان ڪو گمان نه ڪندا ھئو ۽ اُھي ڀائيندا ھوا ته الله (جي عذاب) کان سندن قلعا کين بچائڻ وارا ھوندا پوءِ (اُتان) وٽن الله (جو عذاب) پھتو جتان ڄاڻندا ئي نه ھوا ۽ سندن دلين ۾ اھڙي دھشت وڌائين جو پنھنجن ھٿن سان ۽ مُسلمانن جي ھٿن سان پنھنجن گھرن کي (پاڻ) ڊاھڻ لڳا، تنھنڪري اي اکين وارؤ عبرت وٺو.');
INSERT INTO "amroti" VALUES(59,3,'59:3','۽ جيڪڏھن الله مٿن ديس نيڪالي نه لکي ھا ته کين (ٻيءَ طرح) دنيا ۾ عذاب ڪري ھا، ۽ انھن کي آخرت ۾ (به) باھ جو عذاب آھي.');
INSERT INTO "amroti" VALUES(59,4,'59:4','اِھو عذاب (ڪرڻ) ھن ڪري آھي جو انھن الله ۽ سندس پيغمبر جي مخالفت ڪئي، ۽ جيڪو الله جي مخالفت ڪندو ته بيشڪ الله سخت عذاب (ڪرڻ) وارو آھي.');
INSERT INTO "amroti" VALUES(59,5,'59:5','(اي مؤمنؤ) جيڪي کجين جا وڻ وڍيوَ يا انھن کي پنھنجي پاڙن تي بيٺل ڇڏيوَ سو الله جي حُڪم سان ۽ ھِن لاءِ ته ھو ڪافرن کي خوار ڪري.');
INSERT INTO "amroti" VALUES(59,6,'59:6','۽ انھن (يعني بني نضير جي مالن) مان جيڪو (مال) الله پنھنجي پيغمبر کي ھٿ ۾ ڏنو تنھن تي اوھان نڪي گھوڙا ۽ نڪي ڪي اُٺ ڊوڙايا ھوا پر الله پنھنجن پيغمبرن کي جنھن تي وڻيس (تنھن تي) غالب ڪندو آھي، ۽ الله سڀڪنھن شيءَ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(59,7,'59:7','ڳوٺاڻن (جي مالن) مان جيڪي الله پنھنجي پيغمبر جي ھٿ ۾ ڏنو سو خاص الله لاءِ پيغمبر لاءِ ۽ (پيغمبر جي) مائٽن لاءِ ۽ ڇورن ٻارن ۽ مسڪينن ۽ مسافرن لاءِ آھي (اِھو ھن لاءِ آھي) ته اُھو اوھان مان دنيا وارن جي وچ ۾ ڏيڻ وٺڻ ۾ نه اچي، ۽ جيڪي پيغمبر اوھان کي ڏئي سو وٺو، ۽ جنھن کان اوھان کي جھلي تنھن کي ڇڏي ڏيو، ۽ الله کان ڊڄو، بيشڪ الله سخت عذاب (ڪرڻ) وارو آھي.');
INSERT INTO "amroti" VALUES(59,8,'59:8','(۽ اُھو فَيءَ جو مال) انھن ھجرت ڪرڻ وارن مسڪينن لاءِ (به) آھي جن کي سندن گھرن مان ۽ مالن مان ٻاھر ڪڍيو ويو جيڪي (پنھنجي) الله کان فضل ۽ سندس رضامندي طلبيندا آھن ۽ الله کي ۽ سندس پيغمبر کي مدد ڏيندا آھن، اُھي ئي سچا آھن.');
INSERT INTO "amroti" VALUES(59,9,'59:9','۽ (انھن لاءِ به آھي) جن دارالاسلام (يعني مديني) ۾ ۽ ايمان ۾ مُھاجرن کان اڳ گھر بڻايو جيڪو وٽن لڏي اچي تنھن کي دوست رکندا آھن ۽ جيڪي مھاجرن کي ڏنو ويو تنھن بابت پنھنجين دلين ۾ ڪا پريشاني نه لھندا آھن ۽ (ٻـين کي) پاڻ کان پسند ڪندا آھن توڻيڪ کين ڪو احتياج ھوندو آھي، ۽ جن کي پنھنجي نفس جي حرص کان بچايو ويو سي ئي ڇٽڻ وارا آھن.');
INSERT INTO "amroti" VALUES(59,10,'59:10','۽ (اھو مال اُنھن لاءِ به آھي) جيڪي انھن (مھاجرن ۽ انصارن) کانپوءِ آيا چوندا آھن ته اي اسان جا پالڻھار اسان کي بخش ۽ اسان جي (انھن) ڀائرن کي (به) جن ايمان آڻڻ ۾ اسان کان اڳرائي ڪئي ۽ مؤمنن به نسبت اسان جي دلين ۾ ڪو ڪينو نه وجھ اي اسان جا پالڻھار بيشڪ تون ڏاڍو شفقت ڪندڙ مھربان آھين.');
INSERT INTO "amroti" VALUES(59,11,'59:11','اُنھن ڏانھن نه ڏٺو اٿئي ڇا جيڪي منافق ٿيا جي پنھنجن انھن ڀائرن کي چوندا آھن جيڪي ڪتاب وارن مان مُنڪر آھن ته جيڪڏھن اوھان کي (وطن مان) تڙايو ويندو ته (به اسين) اوھان سان گڏ نڪرنداسون ۽ اوھان جي حق ۾ ڪڏھن به ڪنھن جو چيو نه مڃينداسون ۽ جيڪڏھن اوھان سان ويڙھ ڪئي ويندي ته اوھان جي ضرور مدد ڪنداسون، ۽ الله شاھدي ٿو ڏيئي ته بيشڪ اُھي ڪوڙا آھن.');
INSERT INTO "amroti" VALUES(59,12,'59:12','جيڪڏھن تڙبُن ته (اُھي منافق) ساڻن (وطن مان) نه نڪرندا، ۽ جيڪڏھن اُنھن سان جنگ ڪبي ته کين مدد نه ڏيندا، پر جيڪڏھن (کڻي) مدد ڏيندا ته ضرور پٺيون ڦيرائيندا، وري کين مدد نه ملندي.');
INSERT INTO "amroti" VALUES(59,13,'59:13','(اي مُسلمانؤ) بيشڪ اوھين انھن جي دلين ۾ الله کان (به) وڌيڪ دھشت وارا آھيو، اِھو ھِن ڪري آھي جو اُھي بي سمجھ قوم آھن.');
INSERT INTO "amroti" VALUES(59,14,'59:14','(سڀ) ھڪ ھنڌ تي اوھان سان ويڙھ نه ڪندا پر قلعي واري (محڪم) ڳوٺن ۾ يا ڀتين جي آڙ ۾، پاڻ ۾ انھن جي ويڙھ تمام سخت پيل آھي، کين گڏ ٿيندو ڀانئيندو آھي پر اُنھن جون دليون ڌار ڌار آھن، اھو ھن ڪري آھي جو اھي بي عقل قوم آھن.');
INSERT INTO "amroti" VALUES(59,15,'59:15','(سندن مثال) انھن جي مثال جھڙو آھي جيڪي کانئن اڳ ويجھو (گذريل) ھوا (جن) پنھنجي ڪئي جي سزا چکي، ۽ انھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(59,16,'59:16','(انھن جو مثال) شيطان جي مثال وانگر آھي جڏھن ماڻھوءَ کي چوندو آھي ته ڪافر ٿيءُ، پوءِ جنھن مھل ڪافر ٿيو (تنھن مھل ھو) چوندو آھي ته بيشڪ آءٌ توکان بيزار آھيان آءٌ پنھنجي پالڻھار الله کان ڊڄان ٿو.');
INSERT INTO "amroti" VALUES(59,17,'59:17','پوءِ ٻنھي جي پڇاڙي ھيئن ٿي ته ٻئي باھ ۾ ھميشه ھوندا، ۽ اھا ظالمن جي سزا آھي.');
INSERT INTO "amroti" VALUES(59,18,'59:18','اي ايمان وارؤ الله کان ڊڄو ۽ جڳائي ته سڀڪو شخص سوچي ته سڀاڻي (قيامت) لاءِ ڇا اڳي موڪليو اٿس، ۽ الله کان ڊڄندا رھو، ڇوته جيڪي ڪندا آھيو تنھن بابت الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(59,19,'59:19','۽ انھن جھڙا نه ٿيو جن الله کي وساريو پوءِ الله انھن کان سندن جيئن (جي سڌاري) کي وسارايو، اھي بي دين آھن.');
INSERT INTO "amroti" VALUES(59,20,'59:20','دوزخي ۽ بھشتي برابر نه آھن، بھشتي ئي مُراد ماڻيندڙ آھن.');
INSERT INTO "amroti" VALUES(59,21,'59:21','جيڪڏھن ھن قرآن کي ڪنھن جبل تي نازل ڪريون ھا ته ضرور ان کي الله جي خوف کان عاجز پرزا پرزا ٿيل ڏسين ھا، ۽ اھي مثال ماڻھن لاءِ بيان ڪريون ٿا ته مانَ اُھي ڌيان ڪن.');
INSERT INTO "amroti" VALUES(59,22,'59:22','الله اُھو آھي جنھن کانسواءِ (ٻيو) ڪو عبادت جو لائق ڪونھي، (جو) ڳجھ ۽ ظاھر جو ڄاڻندڙ آھي، اُھو ٻاجھارو مھربان آھي.');
INSERT INTO "amroti" VALUES(59,23,'59:23','الله اُھو آھي جنھن کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي، (جو) بادشاھ تمام پاڪ ذات (سڀني عيبن کان) سلامت امن ڏيڻ وارو نگھبان غالب زبردست وڏائي جو سائين آھين، انھن جي شريڪ مقرر ڪرڻ کان الله پاڪ آھي.');
INSERT INTO "amroti" VALUES(59,24,'59:24','اُھو الله خلقڻھار نئون بڻائيندڙ شڪليون ٺاھيندڙ آھي اُن جا (سڀ) چڱا نالا آھن، جيڪي (به) آسمانن ۽ زمين ۾ آھي سي (سڀ) سندس پاڪائي بيان ڪندا آھن، ۽ اُھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(60,1,'60:1','اي ايمان وارؤ منھنجي دشمنن ۽ پنھنجي دشمنن کي دوست (ڪري) نه وٺو اُنھن ڏانھن دوستي جي ڪري پيغام موڪليو ٿا حالانڪ جيڪو اوھان وٽ سچو دين آيو آھي تنھن جا اُھي مُنڪر ٿي چڪا آھن پيغمبر کي ۽ اوھان کي (پنھنجي وطن مان) ھن ڪري ڪڍين ٿا پنھنجي پالڻھار الله تي ايمان آندو اَٿو جيڪڏھن (خاص) منھنجيءَ واٽ ۾ جھاد ڪرڻ لاءِ ۽ منھنجي رضامندي حاصل ڪرڻ لاءِ نڪتا آھيو انھن ڏانھن دوستي جي ڪري ڳُجھو پيغام ٿا موڪليو، حالانڪ جيڪي ڳجھو ڪريو ٿا ۽ جيڪي پڌرو ڪريو ٿا سو آءٌ چڱي طرح ڄاڻندو آھيان، ۽ اوھان مان جيڪو ائين ڪندو سو بيشڪ سِڌو رستو ڀُلو.');
INSERT INTO "amroti" VALUES(60,2,'60:2','جيڪڏھن (ڪافر) اوھان کي لھندا ته اوھان جا پڪا ويري ھوندا ۽ اوھان ڏانھن پنھنجا ھٿ ۽ پنھنجيون زبانون ايذاءَ لاءِ ڊگھيون ڪندا ۽ گھُرندا ته جيڪر (ڪنھن طرح اوھين به) ڪافر ٿيو.');
INSERT INTO "amroti" VALUES(60,3,'60:3','ڪڏھن نه اوھان کي اوھان جا مائٽ ۽ نڪي اوھان جو اولاد قيامت جي ڏينھن ڪو فائدو ڏيندو، ۽ اوھان جي وچ ۾ (الله) فيصلو ڪندو، ۽ جيڪي (اوھين) ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(60,4,'60:4','بيشڪ ابراھيم ۽ جيڪي ساڻس ھوا تن جي طريقي ۾ اوھان لاءِ چڱي پيروي آھي، جڏھن پنھنجي قوم کي چيائون ته بيشڪ اسين اوھان کان ۽ جن کي الله کانسواءِ پوڄيندا آھيو تن کان بيزار آھيون، اوھان جا مُنڪر ٿي چڪاسون ۽ اسان جي ۽ اوھان جي وچ ۾ ھميشه دشمني ۽ وير پڌرو ٿي چڪو جيستائين ھڪ الله تي ايمان (نه) آڻيندؤ (ابراھيم جي پيروي چڱي آھي) سواءِ ھن (ڳالھ) جي ته ابراھيم جو پنھنجي پيءُ کي چوڻ ته تولاءِ ضرور بخشش گھرندس ۽ الله کان تولاءِ ڪجھ (ڪري) نه ٿو سگھان، (ابراھيم ۽ سندس ساٿين چيو ته) اي اسان جا پالڻھار توتي ڀروسو ڪيوسون ۽ توڏانھن موٽياسون ۽ تو ڏانھن (ئي) موٽڻ آھي.');
INSERT INTO "amroti" VALUES(60,5,'60:5','اي اسان جا پالڻھار اسان کي ڪافرن جو زيردست نه ڪر ۽ اي اسان جا پالڻھار اسان کي بخش، ڇوته تون ئي غالب حڪمت وارو آھين.');
INSERT INTO "amroti" VALUES(60,6,'60:6','بيشڪ انھيءَ (مٿينءَ) جماعت ۾ چڱي پيروي اوھان مان انھيءَ لاءِ آھي جيڪو الله (جي لقاءَ) ۽ قيامت جي ڏينھن (جي اچڻ) جي اميد رکندو ھجي، ۽ جيڪو منھن موڙيندو ته بيشڪ الله سڀ کان بي پرواھ ساراھيل آھي.');
INSERT INTO "amroti" VALUES(60,7,'60:7','ويجھو آھي ته الله اوھان جي ۽ انھن جي وچ ۾ دوستي پيدا ڪري جن سان انھن منجھان دشمني رکي اٿوَ، ۽ الله سگھارو آھي، ۽ الله بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(60,8,'60:8','الله اوھان کي انھن (سان ڳنڍڻ) کان نٿو جھلي جن اوھان سان دين (جي ڳالھ) ۾ نه جنگ ڪئي آھي ۽ نڪي اوھان جي گھرن مان اوھان کي (ٻاھر) ڪڍيو آھي (۽ ھن ڳالھ کان به نٿو جھلي) ته ساڻن احسان ڪريو ۽ سندن حق ۾ انصاف ڪريو، بيشڪ الله انصاف ڪرڻ وارن کي دوست رکندو آھي.');
INSERT INTO "amroti" VALUES(60,9,'60:9','الله اوھان کي رڳو انھن (سان ڳنڍڻ) کان جھلي ٿو جن اوھان سان دين بابت ويڙھ ڪئي ۽ اوھان کي اوھان جي گھرن مان ڪڍيو آھي ۽ اوھان جي ڪڍڻ ۾ (ٻـين جي) مدد ڪئي آھي (ھن کان منع ڪري ٿو) ته ساڻن دوستي رکو، ۽ جيڪي ساڻن دوستي رکندا سي ئي ظالم آھن.');
INSERT INTO "amroti" VALUES(60,10,'60:10','اي ايمان وارؤ جڏھن اوھان وٽ مؤمنياڻيون وطن ڇڏي اچن تڏھن کين جانچيو، الله انھن جي ايمان کي چڱو ڄاڻندڙ آھي، پوءِ جيڪڏھن کين مؤمنياڻيون ڄاڻو ته انھن کي ڪافرن ڏانھن موٽائي نه موڪليو، نڪي اُھي (مُسلمان زالون) اُنھن (جي مڙسن ڪافرن) کي حلال آھن ۽ نڪي اُھي (ڪافر مڙس) انھن (مسلمان زالن) کي حلال آھن، ۽ جيڪي (ڪابين بابت) خرچ ڪيائون سو اُنھن مڙسن کي ڏيو، ۽ جڏھن کين سندن ڪابين ڏيو ته انھن جي نڪاح ڪرڻ ۾ اوھان تي (ڪو) گناھ نه آھي، ۽ پاڻ وٽ نه جھليو ڪافرياڻين جي لاڳاپن کي (به) ۽ جيڪي اوھان (پنھنجين زالن تي ڪابين) خرچ ڪيو سو جڳائي ته انھن کان گھرو ۽ جيڪي انھن (ڪافرن) پنھنجين زالن تي خرچ ڪيو سو ڀلي ته اُھي (اوھان کان) گھرن، اھو الله جو حُڪم آھي، جو اوھان جي وچ ۾ فيصلو ٿو ڪري، ۽ الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(60,11,'60:11','۽ جيڪڏھن اوھان جي زالن مان ڪا ڪافرن ڏانھن (مُرتد ٿي) وئي ھجي پوءِ (ڪافرن کي) ايذايو ته جن جون زالون ويئون ھجن تن کي (اُن مال مان) اوترو ڏيو جيترو انھن (ڪابين ۾) خرچ ڪيو ھو، ۽ انھيءَ الله کان ڊڄو جو جنھن تي اوھين ويساھ رکندڙ آھيو.');
INSERT INTO "amroti" VALUES(60,12,'60:12','اي پيغمبر جڏھن تو وٽ مؤمن زالون اچن ھن (شرط) تي ته توسان بيعت ڪن ته ڪنھن کي الله سان شريڪ مُقرّر نه ڪنديون ۽ نڪي چوري ڪنديون ۽ نڪي زنا ڪنديون ۽ نڪي پنھنجي اولاد کي ڪُھنديون ۽ نڪي اھڙي ڪوڙي تھمت آڻينديون جنھن کي پنھنجن ھٿن ۽ پنھنجن پيرن سان ٺاھيو ھجين ۽ نڪي ڪنھن چڱي ڪم ۾ تنھنجي نافرماني ڪنديون تڏھن سندن بيعت قبول ڪر ۽ انھن لاءِ الله کان بخشش گھر، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(60,13,'60:13','اي ايمان وارؤ اوھين انھيءَ قوم سان دوستي نه رکو جنھن تي الله ڏمريو آھي بيشڪ اُھي آخرت (جي ثواب) کان (اھڙو) نا اُميد ٿيا آھن جھڙوڪ ڪافر قبرن وارن کان نا اُميد ٿيا آھن.');
INSERT INTO "amroti" VALUES(61,1,'61:1','جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) الله جي پاڪائي بيان ڪندو آھي، ۽ اُھو غالب حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(61,2,'61:2','اي ايمان وارؤ جيڪي نه ڪندا آھيو سو ڇو چوندا آھيو.');
INSERT INTO "amroti" VALUES(61,3,'61:3','الله وٽ اھا (ڳالھ) ڏاڍي ناپسند آھي جو اُھا (ڳالھ) چئو جا نٿا ڪريو.');
INSERT INTO "amroti" VALUES(61,4,'61:4','بيشڪ الله انھن کي دوست رکندو آھي جي سندس واٽ ۾ (اھڙي) صف ٻڌي وڙھندا آھن (جو) ڄڻڪ اُھي ھڪڙي سِيھي پلٽيل ڀِت آھن.');
INSERT INTO "amroti" VALUES(61,5,'61:5','۽ (ياد ڪر) جڏھن مُوسىٰ پنھنجي قوم کي چيو ته اي منھنجي قوم مون کي ڇو ايذائيندا آھيو ۽ حالانڪ (اوھين) ڄاڻندا آھيو ته آءٌ اوھان ڏانھن الله جو موڪيل آھيان، پوءِ جنھن مھل ڏنگائي ڪيائون ته الله سندن دلين کي ڏنگو ڪيو، ۽ الله بدڪار قوم کي سڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(61,6,'61:6','۽ (اُھو وقت ياد ڪر) جڏھن عيسىٰ پُٽ مريم جي چيو ته اي بني اسرائيلو بيشڪ آءٌ اوھان ڏانھن الله جو موڪليل آھيان جيڪو توريت منھنجي اڳيان آھي تنھنجو سچو ڪندڙ آھيان ۽ ھڪ اھڙي پيغمبر جي خوشخبري ڏيندڙ آھيان جو مُون کانپوءِ ايندو جنھن جو نالو احمد ھوندو، پوءِ جنھن مھل اُھو چِٽن مُعجزن سان وٽن آيو چيائون ته ھي پڌرو جادو آھي.');
INSERT INTO "amroti" VALUES(61,7,'61:7','۽ اُن کان وڌيڪ ظالم ڪير آھي جنھن الله تي ڪوڙ ٻَڌو حالانڪ ان کي اِسلام ڏانھن سڏيو ويندو آھي، ۽ الله ظالمن جي قوم کي سِڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(61,8,'61:8','(اُھي ڪافر) گھرندا آھن ته الله جي نُور کي پنھنجي واتن سان وِسائين، حالانڪ الله پنھنجي نُور کي پوري ڪرڻ وارو آھي توڻيڪ ڪافر مٺيان ڀانئين.');
INSERT INTO "amroti" VALUES(61,9,'61:9','(الله) اُھو آھي جنھن پنھنجي پيغمبر کي ھدايت ۽ سچي دين سان موڪليو ته اُن کي سڀني دينن تي غالب ڪري توڻيڪ مشرڪ بڇان ڀانئين.');
INSERT INTO "amroti" VALUES(61,10,'61:10','اي ايمان وارؤ اوھان کي انھيءَ سوداگريءَ جو ڏس نه ڏيان ڇا جو اوھان کي ڏکوئيندڙ عذاب کان بچائي؟');
INSERT INTO "amroti" VALUES(61,11,'61:11','(ته) الله ۽ سندس پيغمبر تي ايمان آڻيو ۽ پنھنجن مالن ۽ پنھنجن جندن سان الله جي واٽ ۾ لڙائي ڪريو، جيڪڏھن ڄاڻندا آھيو ته اُھو اوھان لاءِ ڀلو آھي.');
INSERT INTO "amroti" VALUES(61,12,'61:12','اوھان جا گناھ اوھان کي بخشيندو ۽ اوھان کي (اھڙن) باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن ۽ سُٺين جاين ھميشه (رھڻ) وارن بھشتن ۾، اھا وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(61,13,'61:13','۽ ٻي (نعمت به ڏيندو) جنھن کي دوست رکندا آھيو، الله جي طرف کان مدد ۽ فتح ويجھي، ۽ مؤمنن کي خوشخبري ڏي.');
INSERT INTO "amroti" VALUES(61,14,'61:14','اي ايمان وارؤ الله جي دين جا مددگار ھجو جھري طرح عيسىٰ پٽ مريم جي پنھنجي خاص يارن کي چيو ته الله جي دين ۾ منھنجا مددگار ڪير آھن؟ انھن خاص يارن چيو ته اسين الله (جي دين) جا مددگار آھيون پوءِ بني اسرائيلن مان ھڪ ٽولي ايمان آندو ۽ ٻئي ٽوليءَ انڪار ڪيو، پوءِ ايمان وارن کي سندن دشمنن تي غلبو ڏنوسون پوءِ غالب ٿيا.');
INSERT INTO "amroti" VALUES(62,1,'62:1','جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) الله جي پاڪائي بيان ڪندو آھي (جو) بادشاھ تمام پاڪ (ذات) غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(62,2,'62:2','(الله) اُھو آھي جنھن اڻ پڙھيلن ۾ انھن (جي قوم) مان ھڪ پيغمبر پيدا ڪيو جو وٽن سندس آيتون پڙھندو آھي ۽ کين پاڪ ڪندو آھي ۽ کين ڪتاب ۽ دانائي سيکاريندو آھي، ۽ بيشڪ اِھي (ھن کان) اڳ پڌري گمراھي ۾ ھوا.');
INSERT INTO "amroti" VALUES(62,3,'62:3','۽ انھن مان ٻـين لاءِ به جي اڃا انھن (مسلمانن) سان نه مليا آھن، ۽ اُھو غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(62,4,'62:4','اِھو الله جو فضل آھي انھن کي ڏيندو آھي جنھن کي وڻندو اٿس، ۽ الله وڏي فضل وارو آھي.');
INSERT INTO "amroti" VALUES(62,5,'62:5','جن (ماڻھن) کي توريت کڻايو ويو پوءِ اُھو نه کنيائون تن جو مثال (انھيءَ) گڏھ جي مثال وانگر آھي جو (گھڻا) ڪتاب کڻي، جن الله جي آيتن کي ڪوڙ ٿي ڀانيو تنھن قوم جو مثال بڇڙو آھي، ۽ الله ظالم ماڻھن کي سڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(62,6,'62:6','(اي پيغمبر) چؤ ته اي يھوديو جيڪڏھن ڀائيندا آھيو ته ٻـين (سڀني) ماڻھن کانسواءِ رڳو اوھين الله جا دوست آھيو ته جيڪڏھن (انھي ڳالھ ۾) سچا آھيو ته موت گھرو.');
INSERT INTO "amroti" VALUES(62,7,'62:7','۽ جيڪي سندن ھٿن اڳي موڪليو آھي تنھنجي ڪري اُھو ڪڏھن نه گھرندا، ۽ الله ظالمن کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(62,8,'62:8','(اي پيغمبر) چؤ ته بيشڪ اُھو موت جنھن کان ڀڄندا آھيو سو ته ضرور اوھان کي پھچڻ وارو آھي وري (اوھين) ڳجھ ڄاڻندڙ ڏانھن موٽايا ويندؤ پوءِ جيڪي (اوھين دُنيا ۾) ڪندا ھئو تنھن جي اوھان کي سُڌ ڏيندو.');
INSERT INTO "amroti" VALUES(62,9,'62:9','اي ايمان وارؤ جڏھن جُمعي جي ڏينھن نماز لاءِ بانگ ڏني وڃي تڏھن الله جي ياد ڪرڻ ڏانھن ڊُڙو ۽ واپار ڇڏيو، جيڪڏھن ڄاڻندا آھيو ته اھو اوھان لاءِ ڀلو آھي.');
INSERT INTO "amroti" VALUES(62,10,'62:10','پوءِ جڏھن نماز پوري ڪئي وڃي ته زمين تي کنڊري وڃو ۽ الله جي فضل جي طلب ڪريو ۽ الله کي گھڻو ياد ڪريو ته مانَ اوھين ڇٽو.');
INSERT INTO "amroti" VALUES(62,11,'62:11','۽ (اُھي مسلمان) جڏھن ڪو سودو (ھلندو) يا ڪو تماشو ڏسن ٿا (ته) اُن ڏانھن کنڊري وڃن ٿا ۽ توکي (خطبي ۾) بيٺل ڇڏين ٿا، (انھن کي) چؤ ته جيڪي الله وٽ آھي سو تماشي ۽ واپار کان (وڌيڪ) ڀَلو آھي، ۽ الله (سڀني) روزي ڏيندڙن کان ڀَلو آھي.');
INSERT INTO "amroti" VALUES(63,1,'63:1','جڏھن منافق تو وٽ ايندا آھن (ته) چوندا آھن ته شاھدي ڏيون ٿا ته بيشڪ تون الله جو پيغمبر آھين، ۽ الله ڄاڻندو آھي ته بيشڪ تون سندس پيغمبر آھين، ۽ الله شاھدي ٿو ڏئي ته بيشڪ (اُھي) منافق ڪوڙا آھن.');
INSERT INTO "amroti" VALUES(63,2,'63:2','پنھنجن قَسمن کي ڍال (ڪري) ورتو اٿن پوءِ الله جي واٽ کان جھلن ٿا، بيشڪ اُھي جيڪي ڪندا آھن سو بڇڙو آھي.');
INSERT INTO "amroti" VALUES(63,3,'63:3','اھو ھن ڪري آھي جو اُنھن ايمان آندو وري انڪار ڪيائون پوءِ انھن جي دلين تي مُھر ھنئي وئي تنھنڪري اُھي نه سمجھندا آھن.');
INSERT INTO "amroti" VALUES(63,4,'63:4','۽ (اي پيغمبر تون) جڏھن کين ڏسندو آھين (تڏھن) سندن جُسا توکي وڻندا آھن، ۽ جيڪڏھن ڳالھائيندا آھن ته اُنھن جي ڳالھ ڪَن ڏئي ٻڌندو آھين، ڄڻڪ اُھي اُڀيون ڀِت سان ڪاٺيون رکيل آھن، سڀڪنھن آواز کي پاڻ تي ڀو ڀائيندا آھن، اُھي ويري آھن تنھنڪري کائن حِذ رک، الله کين ھلاڪ ڪري، ڪيڏانھن ڦيرايا ٿا وڃن.');
INSERT INTO "amroti" VALUES(63,5,'63:5','۽ جڏھن کين چئبو آھي ته اچو ته الله جو پيغمبر اوھان لاءِ بخشش گھري تڏھن پنھنجا مٿا موڙيندا آھن ۽ انھن کي ڏسندو آھين ته وڏائي ڪندڙ ٿي جھلجن ٿا.');
INSERT INTO "amroti" VALUES(63,6,'63:6','تون انھن لاءِ بخشش گھرين يا انھن لاءِ بخشش نه گھرين ته انھن لاءِ ھڪ جھڙي (ڳالھ) آھي، انھن کي الله ڪڏھن نه بخشيندو، بيشڪ الله بي دينن جي قوم کي سِڌو رستو نه ڏيکاريندو آھي.');
INSERT INTO "amroti" VALUES(63,7,'63:7','اھي اھڙا آھن جو (ھڪ ٻئي کي) چوندا آھن ته جيڪي (مھاجر) الله جي پيغمبر وٽ آھن تن تي خرچ نه ڪريو تان جو ڇڏي وڃن، ۽ (ھيءَ نه ڄاڻندا آھن ته) آسمانن ۽ زمين جا خزانا خاص الله جا آھن پر (ان ڳالھ کي) مُنافق نه سمجھندا آھن.');
INSERT INTO "amroti" VALUES(63,8,'63:8','چوندا آھن ته جيڪڏھن (اسين) مديني ۾ موٽي پُتاسون ته وڌيڪ عِزّت وارو تمام بي عزتي کي اُتان ضرور ڪڍي ڇڏيندو، ۽ عِزّت الله کي ۽ سندس پيغمبر کي ۽ مؤمنن کي آھي پر (اھا ڳالھ) منافق نه ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(63,9,'63:9','اي ايمان وارؤ اوھان کي نڪي اوھان جا مال ۽ نڪي اوھان جا اولاد الله جي ياد ڪرڻ کان ويسلو ڪري، ۽ جيڪي ائين ڪندا سي ئي ڇيئي وارا آھن.');
INSERT INTO "amroti" VALUES(63,10,'63:10','۽ جيڪي اوھان کي روزي ڏني اٿون تنھن منجھان اُنھي کان اڳ خرچ ڪريو جو اوھان مان ڪنھن ھڪڙي کي موت پھچي پوءِ چوڻ لڳي ته اي منھنجا پالڻھار جيڪر مون کي ھڪ ٿوري مُدت تائيم مُھلت ڏين ھا ته خيرات ڪريان ھا ۽ صالحن مان ٿيان ھا.');
INSERT INTO "amroti" VALUES(63,11,'63:11','۽ جڏھن ڪنھن جيءَ جو اجل ايندو آھي (تڏھن) اُن کي الله ڪڏھن مُھلت نه ڏيندو آھي، ۽ جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(64,1,'64:1','جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) الله جي پاڪائي بيان ڪندو آھي، سندس ئي بادشاھي آھي ۽ سنديس ئي ساراھ آھي، ۽ اُھو سڀڪنھن شيءِ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(64,2,'64:2','(الله) اھو آھي جنھن اوھان کي پيدا ڪيو پوءِ اوھان مان ڪو ڪافر ۽ اوھان مان ڪو مؤمن آھي، ۽ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(64,3,'64:3','آسمانن ۽ زمين کي پوريءَ رِٿ سان بڻايائين ۽ اوھانکي صُورت ڏنائين پوءِ اوھان جون صُورتون تمام چڱيون بڻايائين ۽ ڏانھس موٽڻ آھي.');
INSERT INTO "amroti" VALUES(64,4,'64:4','جيڪي آسمانن ۽ زمين ۾ آھي سو (اُھو) ڄاڻندو آھي ۽ جيڪي لڪائيندا آھيو ۽ جيڪي پڌرو ڪندا آھيو سو (به) ڄاڻندو آھي، ۽ الله سينن جو ڳجھ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(64,5,'64:5','(ھن کان) اڳ جيڪي ڪافر ھئا تن جي اوھان وٽ خبر نه آئي آھي ڇا؟ پوءِ پنھنجي ڪئي جي سزا چکيائون ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(64,6,'64:6','اِھو (عذاب) ھِن ڪري آھي جو سندن پيغمبر (چِٽن) مُعجزن سان وٽن ايندا ھئا ته چوندا ھوا ته اسان کي (اسان جھڙا) ماڻھو (سڌو) رستو ڏيکاريندا ڇا، پوءِ انڪار ڪيائون ۽ منھن ڦيرايائون ۽ الله (به) بي پرواھي ڪئي، ۽ الله بي پرواھ ساراھيل آھي.');
INSERT INTO "amroti" VALUES(64,7,'64:7','ڪافر ڀائيندا آھن ته (قبرن مان) کين ڪڏھن نه اُٿاربو، (کين) چؤ ته ھائو! منھنجي پالڻھار جو قسم ته (اوھين) ضرور اُٿاربؤ وري جيڪي ڪندا ھئو تنھن جي اوھان کي ضرور سُڌ ڏبي، ۽ اھو (ڪم) الله کي آسان آھي.');
INSERT INTO "amroti" VALUES(64,8,'64:8','تنھنڪري الله ۽ سندس پيغمبر تي ايمان آڻيو ۽ اُنھيءَ نُور (قرآن) تي به جيڪو نازل ڪيوسون، ۽ جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(64,9,'64:9','جنھن مھل اوھان کي قيامت جي ڏينھن ۾ گڏ ڪندو اُھو ڏينھن ھارجڻ ۽ کٽجڻ جو آھي، ۽ جيڪو الله تي ايمان آڻيندو ۽ چڱا ڪم ڪندو تنھن کان سندس مدايون الله ميٽيندو ۽ اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن اُتي سدائين رھندا، اُھا وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(64,10,'64:10','۽ جن ڪُفر ڪيو ۽ اسان جي آيتن کي ڪوڙو ڄاتو اھي دوزخي آھن اُن ۾ سدائين رھندا، ۽ (اُھو) ھنڌ بڇڙو آھي.');
INSERT INTO "amroti" VALUES(64,11,'64:11','الله جي حُڪم کانسواءِ ڪابه مصيبت نه پھچندي آھي، ۽ جيڪو الله تي ايمان آڻيندو تنھن جي دل کي (اُھو) ھدايت ڪندو، ۽ الله ھر شيءَ کي ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(64,12,'64:12','۽ الله جو چيو مڃيو ۽ پيغمبر جو چيو مڃيو، پوءِ جيڪڏھن مُنھن موڙيندؤ ته اسان جي پيغمبر تي رڳو پڌرو پيغام پھچائڻ آھي.');
INSERT INTO "amroti" VALUES(64,13,'64:13','الله (اُھو آھي جو) اُن کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي، ۽ مؤمنن کي جڳائي ته الله تي (ئي) ڀروسو ڪن.');
INSERT INTO "amroti" VALUES(64,14,'64:14','اي ايمان وارؤ بيشڪ ڪي اوھان جون زالون ۽ ڪي اوھان جو اولاد اوھان جو دشمن آھي تنھنڪري انھن کان حذر ڪريو، ۽ جيڪڏھن معافي ڪريو ۽ ٽارو ڪريو ۽ بخشيو ته بيشڪ الله (به) بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(64,15,'64:15','اوھان جا مال ۽ اوھان جو اولاد رڳو پرک آھن، ۽ الله ئي وٽ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(64,16,'64:16','تنھنڪري جيترو ٿي سگھيوَ اوترو الله کان ڊڄو ۽ (الله جو حُڪم) ٻڌو ۽ چيو مڃيو ۽ خرچ ڪريو ته اوھان جي جِندن لاءِ چڱو آھي، ۽ جيڪي پنھنجي نفس جي حرص کان بچايا ويا سي ئي ڇُٽڻ وارا آھن.');
INSERT INTO "amroti" VALUES(64,17,'64:17','جيڪڏھن الله کي چڱيءَ طرح قرض ڏيندؤ ته (الله) اوھان کي اُھو (قرض) ٻيڻون (ورائي ڏيندو) ۽ اوھان کي بخشيندو، ۽ الله قدردان بردبار آھي.');
INSERT INTO "amroti" VALUES(64,18,'64:18','ڳجھ ۽ ظاھر جو ڄاڻندڙ غالب حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(65,1,'65:1','اي پيغمبر (پنھنجي اُمت کي چؤ ته اوھين) جڏھن زالن کي طلاق ڏيڻ جو ارادو ڪريو تڏھن کين عِدت (جي شروع) ۾ طلاق ڏيو ۽ عِدَّت ڳڻيو، ۽ پنھنجي پالڻھار الله کان ڊڄو، کين سندن گھرن مان (عِدّت جي مُدّت ۾) پڌري بي حيائي جي ڪرڻ کانسواءِ نه ڪڍو ۽ (جڳائي ته) اُھي (پاڻ به) نه نڪرن، اھي الله جون (مُقرّر ڪيل) حدون آھن، ۽ جيڪو الله جون حدون (لتاڙي) لنگھندو تنھن بيشڪ پنھنجي جيءُ تي ظلم ڪيو، ڪوبه نه ڄاڻندو آھي ته اُن (طلاق ڏيڻ) کانپوءِ (متان) الله ڪو ٻيو رستو پيدا ڪري.');
INSERT INTO "amroti" VALUES(65,2,'65:2','پوءِ جڏھن (طلاق واريون) پنھنجي مُدّت کي پھچن تڏھن چڱي طرح سان کين رکو يا چڱي طرح سان کين ڇڏي ڏيو ۽ پاڻ مان ٻه معتبر شاھد ڪريو ۽ الله لڳ شاھدي پوري ڏيو، انھيءَ (حُڪم) سان انھيءَ کي نصيحت ڏجي ٿي جيڪو الله ۽ قيامت جي ڏينھن کي مڃيندو ھجي ۽ جيڪو الله کان ڊڄندو تنھن جي نڪرڻ جي (الله) ڪا واھ ڪندو.');
INSERT INTO "amroti" VALUES(65,3,'65:3','۽ کيس اُتاھون روزي ڏيندو جتان گمان ئي نه ھوندس، ۽ جيڪو الله تي ڀروسو ڪندو تنھن کي الله ڪافي آھي، بيشڪ الله پنھنجي ڪم کي پورو ڪندڙ آھي، بيشڪ الله سڀڪنھن شيءِ جو اندازو ڪيو آھي.');
INSERT INTO "amroti" VALUES(65,4,'65:4','۽ اوھان جي (طلاق ڏنل) زالن مان جيڪي حيض کان نااُميد ٿيون ھجن (تن جي عِدّت بابت) جيڪڏھن شڪ ۾ پيا آھيو ته انھن جي عِدّت ٽي مھينا آھي ۽ جيڪي اڃا حيض واريون نه ٿيون آھن (تن جي به)، ۽ پيٽ وارين زالن جي عِدّت اِھا آھي جو ٻار ڄڻين، ۽ جيڪو الله کان ڊڄندو تنھن لاءِ الله سندس ڪم ۾ آساني ڪندو.');
INSERT INTO "amroti" VALUES(65,5,'65:5','اھو الله جو حُڪم آھي جو اوھان ڏانھن ڏانھن نازل ڪيائين، ۽ جيڪو الله کان ڊڄندو تنھن جون مدايون الله کانئن ميٽيندو ۽ ان کي اجر وڌيڪ ڏيندو.');
INSERT INTO "amroti" VALUES(65,6,'65:6','جتي پنھنجي وس آھر (اوھين) ٽڪندا آھيو تتي انھن کي (عدت جا ڏينھن) ٽڪايو ۽ کين ھن لاءِ نه ايذايو ته انھن کي تنگ ڪريو، ۽ جيڪڏھن پيٽ سان ھجن ته جيسين پنھنجو ٻار ڄڻين (تيسين) کين خرچ ڏيو، پوءِ جيڪڏھن اوھان جي حُڪم موجب (اُھي ٻار کي) کير پيارين ته کين سندن مزوري ڏيو، ۽ ھلي چليءَ موجب پاڻ ۾ صلاح ڪريو، ۽ جيڪڏھن پاڻ ۾ سختي ڪريو ته اُن کي ٻي (ڪا) کير پياري.');
INSERT INTO "amroti" VALUES(65,7,'65:7','جڳائي ته وس وارو پنھنجي وس آھي خرچ ڪري، ۽ جنھن تي روزي تنگ ڪئي ويئي آھي تنھن کي جڳائي ته جيڪي الله کيس ڏنو آھي تنھن مان خرچ ڪري، ڪنھن کي الله تڪليف نه ڏيندو آھي پر (اوتري قدر) جيتري قدر کيس الله ڏنو آھي، سگھوئي الله ڏکيائيءَ کانپوءِ سکيائي ظاھر ڪندو.');
INSERT INTO "amroti" VALUES(65,8,'65:8','۽ (اھڙا) گھڻائي ڳوٺ (ھوا) جن پنھنجي پالڻھار جي حُڪم ۽ سندس پيغمبرن (جي حڪم) کان نافرماني ڪئي پوءِ سخت عذاب سان انھن کي پڪڙيوسون ۽ سخت عذاب سان کين عذاب ڪيوسون.');
INSERT INTO "amroti" VALUES(65,9,'65:9','پوءِ انھن (ڳوٺ وارن) پنھنجي ڪم جي سزا چکي ۽ انھن جي ڪم جي پڇاڙي خراب ٿي.');
INSERT INTO "amroti" VALUES(65,10,'65:10','انھن لاءِ الله سخت عذاب تيار ڪيو آھي تنھنڪري اي عقل وارا مؤمنؤ الله کان ڊڄو، بيشڪ الله اوھان ڏانھن ھڪ نصيحت موڪلي آھي.');
INSERT INTO "amroti" VALUES(65,11,'65:11','جو رسول آھي جو اوھان تي الله جون پڌريون آيتون (ھن لاءِ) پڙھندو آھي ته جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي اونداھين کان سوجھري ڏانھن ڪڍي، ۽ جيڪو الله تي ايمان آڻيندو ۽ چڱا ڪم ڪندو تنھن کي اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن اُتي ھميشه رھندا، بيشڪ اُن جي روزي چڱي طرح ٺاھي آھي.');
INSERT INTO "amroti" VALUES(65,12,'65:12','الله اُھو آھي جنھن ستن آسمانن کي بڻايو ۽ اوتريون زمينون به، اُنھن جي وچ ۾ اُن جو حُڪم نازل ٿيندو آھي ته ڄاڻو ته الله سڀڪنھن شيءَ تي وس وارو آھي ۽ ته الله عِلم سان سڀڪنھن شيءِ کي وڪوڙيو آھي.');
INSERT INTO "amroti" VALUES(66,1,'66:1','اي پيغمبر جنھن شيءِ کي الله تولاءِ حلال ڪيو آھي سا ڇو ٿو حرام ڪرين؟ پنھنجين زالن جو راضپو گھرين ٿو، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(66,2,'66:2','بيشڪ الله اوھان لاءِ اوھان جي قسمن جو کولڻ درست ڪيو آھي، ۽ الله اوھان جو سائين آھي، ۽ اُھو ڄاڻندڙ حِڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(66,3,'66:3','۽ (ياد ڪر) جڏھن پيغمبر پنھنجين زالن مان ڪنھن ھڪڙي کي ڪا ڳالھ ڳجھي چئي، پوءِ جڏھن اُن (ڳالھ) کي اُن (بي بيءَ) پڌرو ڪيو ۽ الله پيغمبر کي اُن (ڳجھي ڳالھ جي پڌري ڪرڻ) تي واقف ڪيو (تڏھن) پيغمبر ڪُجھ اُھا (ڳالھ بي بيءَ کي) ڄاڻائي ڪجھ چوڻ کان ٽارو ڪيائين، پوءِ جڏھن اُن (بي بيءَ) کي ڳجھيءَ ڳالھ (جي پڌري ڪرڻ) کان واقف ڪيائين (تڏھن) اُن (بي بي) چيو ته اھا توکي ڪنھن ڄاڻائي، (پيغمبر) فرمايو ته مون کي (الله) ڄاڻندڙ خبر رکندڙ خبر ڏني.');
INSERT INTO "amroti" VALUES(66,4,'66:4','(اي پيغمبر جون زالون) جيڪڏھن اوھين ٻئي الله وٽ توبه ڪنديون ته بيشڪ اوھان جي دلين لاڙو ڪيو آھي، ۽ جيڪڏھن پيغمبر (جي ايذائڻ تي ٻئي گڏجي ڏاڍ ڪنديون ته بيشڪ الله سندس ڀرجھلو آھي ۽ جبرئيل ۽ سُڌريل مؤمن (به)، ۽ ھن کانپوءِ ملائڪ (به) مددگار آھن.');
INSERT INTO "amroti" VALUES(66,5,'66:5','جيڪڏھن پيغمبر اوھان کي طلاق ڏيندو ته اميد آھي ته سندس پالڻھار اوھان کان چڱيون زالون فرمانبردار مؤمنياڻيون نماز پڙھڻ واريون توبه ڪرڻ واريون عبادت ڪرڻ واريون رنيلان ۽ ڪنواريون (ٻيون) ان کي عوض ۾ ڏيندو.');
INSERT INTO "amroti" VALUES(66,6,'66:6','اي ايمان وارؤ پاڻ کي پنھنجي گھر جي ڀاتين کي انھيءَ باھ کان بچايو جنھن جو ٻارڻ ماڻھو ۽ پھڻ ھوندا جنھن (باھ) تي سخت طبع زبردست ملائڪ (مُقرر) آھن جيڪي (الله) کين فرمايو آھي تنھن ۾ الله جي نافرماني نه ڪندا آھن ۽ جيڪي کين حُڪم ڪبو آھي سو ڪندا آھن.');
INSERT INTO "amroti" VALUES(66,7,'66:7','(ان ڏينھن چئبو ته) اي ڪافرؤ اڄ بھانو (پيش) نه ڪريو، جيڪي ڪندا ھئو رڳو تَنھنجي اوھان کي سزا ڏجي ٿي.');
INSERT INTO "amroti" VALUES(66,8,'66:8','اي ايمان وارؤ الله وٽ توبه ڪريو سچي توبه، اوھان جي پالڻھار ۾ اميد آھي ته اوھان جون مدايون اوھان کان ميٽيندو ۽ اوھان کي (اھڙن) باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن جنھن ڏينھن جو الله پيغمبر کي ۽ جن ساڻس ايمان آندو آھي تن کي خوار نه ڪندو، سندن (ايماني) نُور سندن اڳيان ۽ سندن سڄن پاسن ۾ ڊوڙندو چوندا (ته) اي اسان جا پالڻھار اسان لاءِ اسان جو نُور ڀرپور ڪر ۽ اسان کي بخش، بيشڪ تون سڀڪنھن شيءَ تي وس وارو آھين.');
INSERT INTO "amroti" VALUES(66,9,'66:9','اي پيغمبر ڪافرن ۽ منافقن سان لڙائي ڪر ۽ مٿن سختي ڪر، ۽ سندن جاءِ دوزخ آھي، ۽ اُھو ھنڌ بڇڙو آھي.');
INSERT INTO "amroti" VALUES(66,10,'66:10','الله نُوح جي زال ۽ لُوط جي زال جو ھڪ مثال ڪافرن لاءِ بيان ڪري ٿو، (اُھي) اسان جي ٻن صالحن ٻانھن (جي نڪاح) ھيٺ ھيون پوءِ اُنھن زالن ٻنھي سان خيانت ڪئي ۽ پوءِ اُھي ٻئي (پنھنجين) زالن کان الله جو عذاب ڪجھ به ٽاري نه سگھيا (انھن کي) چئبو ته باھ ۾ گھڙندڙن سان گڏ گھڙو.');
INSERT INTO "amroti" VALUES(66,11,'66:11','۽ الله مؤمنن لاءِ فرعون جي زال جو ھڪ مثال بيان ڪري ٿو، جڏھن چيائين ته اي منھنجا پالڻھار پاڻ وٽ بھشت ۾ مون لاءِ ڪو گھر ٺاھ ۽ فرعون ۽ سندس ڪم کان مون کي بچاءِ ۽ ظالمن جي ٽوليءَ کان (پڻ) مون کي بچاءِ.');
INSERT INTO "amroti" VALUES(66,12,'66:12','۽ مَرۡيم ڌِيءُ عِمۡران جي جنھن پنھنجي اُگھڙ کي بچايو پوءِ اُن (جي رحم) ۾ پنھنجو رُوح ڦوڪيوسون ۽ پنھنجي پالڻھار جي حُڪمن ۽ سندس ڪتابن کي سچ ڄاتو ھوائين ۽ فرمانبردارن مان ھُئي.');
INSERT INTO "amroti" VALUES(67,1,'67:1','اُھو (الله) وڏي برڪت وارو آھي جنھن جي ھٿ ۾ (ساري مُلڪ جي) بادشاھي آھي، ۽ اُھو سڀڪنھن شيءِ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(67,2,'67:2','(الله اُھو آھي) جنھن موت ۽ حياتي پيدا ڪئي (انھيءَ لاءِ) ته اوھان کي پرکي ته عمل جي ڪري تمام چڱو اوھان مان ڪير آھي؟ ۽ اُھو غالب بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(67,3,'67:3','(الله اُھو آھي) جنھن ستن آسمانن کي طبقا طبقا بڻايو، (اي ڏسندڙ تون الله) ٻاجھاري جي بڻاوت ۾ ڪو فرق نه ڏسندين. پوءِ ورائي (نظر ڪري) ڏس ته (ڪٿي) ڪا ڦوٽ ڏسين ٿو؟');
INSERT INTO "amroti" VALUES(67,4,'67:4','پوءِ وري به ٻه ڀيرا ڏس ته (ضرور تنھنجي) نظر تو ڏانھن جھڪي ٿي ٿڪجي موٽندي.');
INSERT INTO "amroti" VALUES(67,5,'67:5','۽ بيشڪ دنيا جي آسمان کي (ستارن جي) ڏيئن سان سينگاريو اٿون ۽ اُنھن کي شيطانن جي چُٽڻ لاءَ (به) بڻايوسون ۽ شيطانن لاءِ دوزخ جو عذاب تيار ڪيو اٿون.');
INSERT INTO "amroti" VALUES(67,6,'67:6','۽ جن پنھنجي پالڻھار جو انڪار ڪيو تن لاءِ دوزخ جي سزا آھي، ۽ اُھا جاءِ بڇڙي آھي.');
INSERT INTO "amroti" VALUES(67,7,'67:7','جڏھن کين دوزخ ۾ اڇليو ويندو (تڏھن) اُن جون (گڏھ جھڙيون) ھينگون ٻڌندا ۽ اُھو پيو اُڀامندو.');
INSERT INTO "amroti" VALUES(67,8,'67:8','جو ڪاوڙ کان (ڄڻ ته) اجھو ٿو ڦاٽي. جنھن مھل ڪنھن ٽوليءَ کي اُن ۾ پيو وجھبو (تنھن مھل) دوزخ جا داروغا انھن کان پڇندا ته اوھان وٽ ڪو ڊيڄاريندڙ (پيغمبر) نه آيو ھو ڇا؟');
INSERT INTO "amroti" VALUES(67,9,'67:9','چوندا ھائو! بيشڪ اسان وٽ ڊيڄاريندڙ آيو ھو، پر (اُن کي) ڪوڙو ڄاتوسون ۽ چيوسون ته الله ڪا (به) شيءِ نه لاٿي آھي، اوھين ته رڳو وڏيءَ گمراھيءَ ۾ (پيل) آھيو.');
INSERT INTO "amroti" VALUES(67,10,'67:10','۽ چوندا ته جيڪڏھن اسين ٻُڌون ھا يا سمجھون ھا ته دوزخين (جي ٽوليءَ) ۾ (ڪڏھن داخل) نه ٿيون ھا.');
INSERT INTO "amroti" VALUES(67,11,'67:11','پوءِ پنھنجو ڏوھ (پاڻ) مڃيندا، پوءِ دوزخين تي لعنت ھجي شال.');
INSERT INTO "amroti" VALUES(67,12,'67:12','بيشڪ جيڪي پنھنجي پالڻھار کان پرپٺ ڊڄندا آھن تن لاءِ بخشش ۽ وڏو اجر آھي.');
INSERT INTO "amroti" VALUES(67,13,'67:13','۽ پنھنجي ڳالھ ڳجھي ڳالھايو يا اُھا پڌري چئو، ته بيشڪ الله سينن جو ڳُجھ ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(67,14,'67:14','جنھن پيدا ڪيو سو ڪيئن نه ڄاڻندو؟ ۽ اُھو اونھي نظر وارو خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(67,15,'67:15','(الله) اُھو آھي جنھن زمين کي اوھان جي لاءِ تابع ڪيو ته ان جي رستن ۾ گھمو ۽ الله جي رزق مان کائو، ۽ (جيئرو ٿي) ڏانھس کڙو ٿيڻو آھي.');
INSERT INTO "amroti" VALUES(67,16,'67:16','(اوھين) اُنھيءَ کان بي ڀَوا ٿيا آھيو ڇا ته جيڪو آسمان ۾ آھي سو متان اوھان کي زمين ۾ ڳِھائي ڇڏي پوءِ اُتي جو اُتي زمين ڌڏي؟');
INSERT INTO "amroti" VALUES(67,17,'67:17','(اوھين) انھيءَ کان بي ڀَوا ٿيا آھيو ڇا ته جيڪو آسمان ۾ آھي سو متان اوھان تي پھڻ وسائيندڙ واءُ موڪلي، پوءِ سگھو ڄاڻندؤ ته منھنجو ڊيڄارڻ ڪيئن آھي؟');
INSERT INTO "amroti" VALUES(67,18,'67:18','۽ بيشڪ جيڪي انھن کان اڳ ھوا تن (به) ڪوڙ ڀانيو پوءِ منھنجي سزا ڪھڙي طرح ھئي.');
INSERT INTO "amroti" VALUES(67,19,'67:19','پنھنجي مٿاھون پکين ڏانھن نه ڏٺو اٿن ڇا؟ جي کنڀڙاٽيون ٽيڙي ھلندا آھن ۽ (ڪڏھن ڪڏھن) بند ڪندا آھن، انھن کي ٻاجھاري (الله) کانسواءِ (ٻيو ڪو) به جھلي نه رکندو آھي، بيشڪ اُھو سڀڪنھن شيءِ کي ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(67,20,'67:20','اُھو اوھان جو لشڪر ڪھڙو آھي جو اوھان کي ٻاجھاري (الله) کانسواءِ مدد ڏئي؟ ڪافر رڳو ڀولي ۾ (پيل) آھن.');
INSERT INTO "amroti" VALUES(67,21,'67:21','(ڀلا) اُھو ڪير آھي جو جيڪڏھن الله پنھنجو رزق جھلي ته اھو اوھان کي روزي ڏئي؟ بلڪ (ڪافر) سرڪشي ۽ (حق کان) نفرت ۾ اڙجي پيا آھن.');
INSERT INTO "amroti" VALUES(67,22,'67:22','ڀلا جيڪو پنھنجي مُنھن ڀر اونڌو ڪريل ھلندو ھجي سو وڌيڪ سڌي رستي وارو آھي يا اُھو جيڪو سڌو سنئون سڌي واٽ تي ھلندو ھجي؟');
INSERT INTO "amroti" VALUES(67,23,'67:23','چئو ته (الله) اُھو آھي جنھن اوھان کي پيدا ڪيو ۽ اوھان جا ڪن ۽ اکيون ۽ دليون بڻايائين، (اوھين) ٿورڙو احسان مڃيندا آھيو.');
INSERT INTO "amroti" VALUES(67,24,'67:24','چئونَ ته (الله) اُھو آھي جنھن اوھان کي زمين ۾ پکيڙيو ۽ (اوھين) ڏانھس گڏ ڪيا ويندؤ.');
INSERT INTO "amroti" VALUES(67,25,'67:25','۽ (ڪافر) چوندا آھن ته اھو انجام ڪڏھن (پورو) ٿيندو جيڪڏھن سچا آھيو؟');
INSERT INTO "amroti" VALUES(67,26,'67:26','(اي پيغمبر کين) چؤ ته (اھا) خبر رڳو الله وٽ آھي، ۽ آءٌ رڳو پڌرو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(67,27,'67:27','۽ جنھن مھل ان (انجام) کي ويجھو ٿيل ڏسندا ته ڪافرن جا مُنھن ڪاراٽجي ويندا، ۽ چئبن ته جنھن کي طلبيندا ھئو سو ھيءُ آھي.');
INSERT INTO "amroti" VALUES(67,28,'67:28','(اي پيغمبر کين) چؤ ته ڏٺو اٿوَ ڇا ته جيڪڏھن مون کي ۽ جيڪي مون ساڻ آھن تن کي الله ھلاڪ ڪري يا الله اسان تي ٻاجھ ڪري ته (هر صورت ۾) ڪافرن کي ڏکوئيندڙ عذاب کان ڪير ڇڏائيندو؟');
INSERT INTO "amroti" VALUES(67,29,'67:29','(اي پيغمبر کين) چؤ ته اُھو ٻاجھارو آھي اُن تي ايمان آندوسون ۽ مٿس ڀروسو ڪيوسون، پوءِ سگھو ئي ڄاڻندؤ ته پڌريءَ گمراھي ۾ ڪير آھي.');
INSERT INTO "amroti" VALUES(67,30,'67:30','(اي پيغمبر کين) چؤ ته ڏٺو اٿو ڇا ته جيڪڏھن اوھان جو پاڻي ھيٺ پيھي وڃي ته اوھان وٽ وھندڙ پاڻي ڪير آڻيندو؟');
INSERT INTO "amroti" VALUES(68,1,'68:1','نٓ قَلَم جو قسم آھي ۽ اُن جو به جيڪي لکندا آھن.');
INSERT INTO "amroti" VALUES(68,2,'68:2','ته تون پنھنجي پالڻھار جي فضل سان ڪو چريو نه آھين.');
INSERT INTO "amroti" VALUES(68,3,'68:3','۽ بيشڪ تولاءِ اڻ کٽ ثواب آھي.');
INSERT INTO "amroti" VALUES(68,4,'68:4','۽ بيشڪ تون وڏي خُلق تي آھين.');
INSERT INTO "amroti" VALUES(68,5,'68:5','پوءِ سگھوئي ڏسندين ۽ اُھي (به) ڏسندا.');
INSERT INTO "amroti" VALUES(68,6,'68:6','ته اوھان مان ڪير چريو آھي.');
INSERT INTO "amroti" VALUES(68,7,'68:7','بيشڪ تنھنجو پالڻھار ئي انھيءَ کي تمام چڱي طرح ڄاڻندڙ آھي جيڪو سندس واٽ کان گمراھ ٿيو، ۽ اھو سنئين واٽ ويندڙن کي به تمام چڱي طرح ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(68,8,'68:8','تنھنڪري ڪوڙ ڀائيندڙن جو چيو نه مڃ.');
INSERT INTO "amroti" VALUES(68,9,'68:9','اُھي گھرندا آھن ته جيڪڏھن نرمي ڪرين ته اُھي به نرمي ڪن.');
INSERT INTO "amroti" VALUES(68,10,'68:10','۽ تون سڀڪنھن وڏي قسميءَ خوار جو چيو نه مڃ.');
INSERT INTO "amroti" VALUES(68,11,'68:11','(جو) گھڻو عيب لائيندڙ چغلي ھڻندڙ گھمندڙ.');
INSERT INTO "amroti" VALUES(68,12,'68:12','خير کان جھليندڙ حد کان لنگھيل گنھگار.');
INSERT INTO "amroti" VALUES(68,13,'68:13','بدعادتو اِن سڀنيءَ کانپوءِ (حرامي (به) ھجي.');
INSERT INTO "amroti" VALUES(68,14,'68:14','انھي ڪري جو مال ۽ اولاد وارو آھي.');
INSERT INTO "amroti" VALUES(68,15,'68:15','جڏھن وٽس اسان جون آيتون پڙھجن (تڏھن) چوي ته اڳين جون آکاڻيون آھن.');
INSERT INTO "amroti" VALUES(68,16,'68:16','سگھوئي کيس سونڍ تي داغ ڏينداسون.');
INSERT INTO "amroti" VALUES(68,17,'68:17','بيشڪ اسان ھنن کي (اھڙي طرح) آزمايو جھڙي طرح باغ وارن کي آزمايو ھوسون، جڏھن قسم کنيائون ته سويرو (ئي) اُن پوک کي ضرور لُڻندا (سين).');
INSERT INTO "amroti" VALUES(68,18,'68:18','۽ اِنشاءَالله نه چيائون.');
INSERT INTO "amroti" VALUES(68,19,'68:19','پوءِ تنھنجي پالڻھار (جي پار) کان اُن (باغ) تي ھڪ (اھڙي) بلا پلٽي ۽ اُھي (اڃا) سمھيل ئي ھوا.');
INSERT INTO "amroti" VALUES(68,20,'68:20','جو (اُھو) ناڙ وانگر ٿي پيو.');
INSERT INTO "amroti" VALUES(68,21,'68:21','پوءِ سويرو ھڪ ٻئي کي سڏڻ لڳا.');
INSERT INTO "amroti" VALUES(68,22,'68:22','جيڪڏھن اوھين لاباري ڪرڻ وارا آھيو ته پوءِ پنھنجي پوک تي سويرو ھلو.');
INSERT INTO "amroti" VALUES(68,23,'68:23','پوءِ اُھي ھليا ۽ ھڪ ٻئي کي ھوريان چوڻ لڳا.');
INSERT INTO "amroti" VALUES(68,24,'68:24','ته (جئن) ھن (پوک) ۾ اڄ اوھان وٽ ڪو مسڪين اندر اچي نه سگھي.');
INSERT INTO "amroti" VALUES(68,25,'68:25','۽ (پنھنجي گمان ۾) بخل (جي نيت) تي سگھارا ٿي سويرو پھتا.');
INSERT INTO "amroti" VALUES(68,26,'68:26','پوءِ جنھن مھل اُن (پوک) کي ڏٺائون (ته) چيائون ته بيشڪ اسين رستو ڀليل آھيون.');
INSERT INTO "amroti" VALUES(68,27,'68:27','(نه نه!) بلڪ اسين بي نصيب (ٿيل) آھيون.');
INSERT INTO "amroti" VALUES(68,28,'68:28','سندن چڱي (مڙس) چيو ته اوھان کي نه چيو ھومِ ڇا ته الله جي پاڪائي ڇونه ٿا بيان ڪريو؟');
INSERT INTO "amroti" VALUES(68,29,'68:29','چيائون ته (اسين) پنھنجي پالڻھار جي پاڪائي بيان ڪريون ٿا ۽ بيشڪ (انھي نيت ڪري) ظالم ھواسون.');
INSERT INTO "amroti" VALUES(68,30,'68:30','پوءِ ھڪ ٻئي کي ملامت ڪندڙ ٿي ھڪڙن ٻـين ڏانھن مھاڙ ڪئي.');
INSERT INTO "amroti" VALUES(68,31,'68:31','چوڻ لڳا ته ويل اسان لاءِ بيشڪ اسين حد کان لنگھندڙ ھواسون.');
INSERT INTO "amroti" VALUES(68,32,'68:32','مانَ اسان جو پالڻھار اسان کي ھن کان ڀلو (ٻيو باغ) بدلو ڏئي بيشڪ اسين پنھنجي پالڻھار ڏانھن اُميد رکندڙ آھيون.');
INSERT INTO "amroti" VALUES(68,33,'68:33','ائين ئي آفت (پوڻي) آھي، ۽ بيشڪ آخرت جو عذاب تمام وڏو آھي، ارمان! جيڪڏھن سمجھن ھا.');
INSERT INTO "amroti" VALUES(68,34,'68:34','بيشڪ پرھيزگارن لاءِ سندن پالڻھار وٽ نعمت جا باغ آھن.');
INSERT INTO "amroti" VALUES(68,35,'68:35','مسلمانن کي گنھگارن جھڙو ڪنداسون ڇا؟');
INSERT INTO "amroti" VALUES(68,36,'68:36','اوھان کي ڇا ٿيو آھي؟ ڪھڙو ٺھراءُ ٿا ڪيو؟');
INSERT INTO "amroti" VALUES(68,37,'68:37','اوھان وٽ ڪو ڪتاب آھي ڇا جنھن ۾ (ھيءَ ڳالھ) پڙھندا آھيو.');
INSERT INTO "amroti" VALUES(68,38,'68:38','ته بيشڪ جيڪي اوھين پسند ڪندا آھيو سو آخرت ۾ اوھان لاءِ ھوندو.');
INSERT INTO "amroti" VALUES(68,39,'68:39','اوھان جا اسان جي ذمي (ھن ڳالھ جا) ڪي پڪا انجام ٿيل آھن ڇا جو اُنھن جو قيامت تائين حُڪم ھلندو ته بيشڪ جيڪو ٺھراءُ ڪريو ٿا سو اوھان لاءِ آھي.');
INSERT INTO "amroti" VALUES(68,40,'68:40','(اي پيغمبر) مشرڪن کان پڇ ته انھن مان ڪير ھن (ڳالھ) جو ذمي وار آھي.');
INSERT INTO "amroti" VALUES(68,41,'68:41','انھن (مشرڪن) کي (ھن ڳالھ ۾) ڪي شريڪ آھن ڇا؟ جيڪڏھن سچا آھن ته جڳائين ته پنھنجي شريڪن کي (مدد لاءِ) آڻين.');
INSERT INTO "amroti" VALUES(68,42,'68:42','جنھن ڏينھن پنّي جي تجلّي ٿيندي ۽ اُنھن کي سجدي ڏانھن سڏبو پوءِ نه ڪري سگھندا.');
INSERT INTO "amroti" VALUES(68,43,'68:43','انھن جون اکيون جھڪيون ھونديون. (۽) کين خواري وٺي ويندي، ۽ بيشڪ اُنھن کي سجدي ڏانھن سڏبو ھو حالانڪ اُھي چڱا ڀلا (به) ھوا.');
INSERT INTO "amroti" VALUES(68,44,'68:44','پوءِ (اي پيغمبر) مون کي ۽ انھيءَ کي جو ھن (قرآن جي) ڳالھ کي ڪوڙ ڀائيندو آھي ڇڏي ڏي، (اسين) انھن کي درجي بدرجي (اُتان دوزخ ۾) ڇڪينداسون جتان اُھي نه ڄاڻندا.');
INSERT INTO "amroti" VALUES(68,45,'68:45','انھن کي مُھلت ڏيان ٿو، بيشڪ منھنجي رِٿ پڪي آھي.');
INSERT INTO "amroti" VALUES(68,46,'68:46','انھن کان ڪو اجورو گھرندو آھين ڇا جنھنڪري (ان) چٽيءَ کان ڳوري بار آھن.');
INSERT INTO "amroti" VALUES(68,47,'68:47','(يا) انھن وٽ ڪو ڳجھ (جو علم) آھي ڇا جو اھي لکندا آھن.');
INSERT INTO "amroti" VALUES(68,48,'68:48','پوءِ (تون) پنھنجي پالڻھار جي حُڪم (اچڻ) تائين صبر ڪر ۽ مڇيءَ واري (يونس) وانگر نه ھُج، جڏھن دانھن ڪيائين ۽ اُھو ڏک ۾ ڀرجي ويو ھو.');
INSERT INTO "amroti" VALUES(68,49,'68:49','جيڪڏھن کيس سندس پالڻھار جي طرفان رحمت نه پھچي ھا ته ضرور سُڃ ۾ اڇليو وڃي ھا ۽ اُھو بڇڙي حال ھجي ھا.');
INSERT INTO "amroti" VALUES(68,50,'68:50','پوءِ سندس پالڻھار کيس نوازيو ۽ صالحن مان ڪيائينس.');
INSERT INTO "amroti" VALUES(68,51,'68:51','۽ بيشڪ ڪافر جڏھن قرآن ٻڌندا آھن (تڏھن) توکي پنھنجن (تکين) اکين سان ٿيڙڻ تي آھن ۽ چوندا آھن ته بيشڪ ھي (پيغمبر) چريو آھي.');
INSERT INTO "amroti" VALUES(68,52,'68:52','۽ (حقيقت ۾) ھي (قرآن) ته جھانن لاءِ نصيحت آھي.');
INSERT INTO "amroti" VALUES(69,1,'69:1','قيامت.');
INSERT INTO "amroti" VALUES(69,2,'69:2','ڇا آھي (اھا) قيامت.');
INSERT INTO "amroti" VALUES(69,3,'69:3','۽ ڪھڙي شيءِ توکي ڄاڻايو ته قيامت ڇا آھي؟');
INSERT INTO "amroti" VALUES(69,4,'69:4','ثمود ۽ عاد (قوم) قيامت کي ڪُوڙ ڄاتو.');
INSERT INTO "amroti" VALUES(69,5,'69:5','پوءِ ثمود (قوم) سي ته ھڪڙي رڙ سان ناس ڪيا ويا.');
INSERT INTO "amroti" VALUES(69,6,'69:6','۽ عاد سي ھڪ بيحد سخت واءُ سان ناس ڪيا ويا.');
INSERT INTO "amroti" VALUES(69,7,'69:7','(الله) اُن (واءُ) کي ست راتيون ۽ اٺ ڏينھن لڳولڳ مٿن کڙو ڪيو پوءِ قوم کي اُن (زمين) ۾ ڪريل ڏسين ھا ڄڻ ته اُھي ڀتي کجيءَ جا ٿُڙ آھن.');
INSERT INTO "amroti" VALUES(69,8,'69:8','پوءِ انھن کي ڪجھ بچيل ڏسين ٿو ڇا؟');
INSERT INTO "amroti" VALUES(69,9,'69:9','۽ فرعون ۽ جيڪي اُن کان اڳ ھوا ۽ ناس ڪيل شھرن وارا (به) گناھ (جا ڪم) ڪري آيا.');
INSERT INTO "amroti" VALUES(69,10,'69:10','۽ پنھنجي پالڻھار جي رسول جي نافرماني ڪيائون تنھنڪري (الله) وڏي پڪڙ سان کين پڪڙيو.');
INSERT INTO "amroti" VALUES(69,11,'69:11','بيشڪ جڏھن پاڻي حد کان لنگھي ويو (تڏھن) اسان اوھان کي ھلندڙ ٻيڙيءَ ۾ چاڙهيو.');
INSERT INTO "amroti" VALUES(69,12,'69:12','ھن ڪري ته اُن (ڳالھ) کي اوھان لاءِ يادگيري ڪريون ۽ ان کي ڪو ياد رکڻ وارو ڪَنُّ ياد رکي.');
INSERT INTO "amroti" VALUES(69,13,'69:13','پوءِ جڏھن صُور ۾ ھڪڙي ڦوڪ ڦوڪبي.');
INSERT INTO "amroti" VALUES(69,14,'69:14','۽ زمين ۽ جبل کنيا ويندا پوءِ ھڪ ڀيرو انھن کي ڪٽيو ويندو.');
INSERT INTO "amroti" VALUES(69,15,'69:15','پوءِ اُن ڏينھن قيامت قائم ٿيندي.');
INSERT INTO "amroti" VALUES(69,16,'69:16','۽ آسمان ڦاٽندو پوءِ اُھو اُن ڏينھن ڍلو ٿيل ھوندو.');
INSERT INTO "amroti" VALUES(69,17,'69:17','۽ ملائڪ آسمان جي ڪنارن تي ھوندا، ۽ تنھنجي پالڻھار جو تخت اُن ڏينھن اٺ ملائڪ پنھنجي مٿان کڻندا.');
INSERT INTO "amroti" VALUES(69,18,'69:18','اُن ڏينھن اوھان کي (الله جي) آڏو ڪيو ويندو اوھان جي ڪا ڳالھ ڳجھي لڪي نه رھندي.');
INSERT INTO "amroti" VALUES(69,19,'69:19','پوءِ جنھن کي سندس اعمالنامون سندس سڄي ھٿ ۾ ڏنو ويو سو چوندو ته وٺو منھنجو اعمالنامو پڙھو.');
INSERT INTO "amroti" VALUES(69,20,'69:20','بيشڪ مون يقين رکيو ھو ته آءٌ پنھنجي حساب کي پھچندس.');
INSERT INTO "amroti" VALUES(69,21,'69:21','اُھو چڱي گذران ۾ ھوندو.');
INSERT INTO "amroti" VALUES(69,22,'69:22','مٿاھين بھشت ۾.');
INSERT INTO "amroti" VALUES(69,23,'69:23','جنھن جا ميوا ويجھا ھوندا.');
INSERT INTO "amroti" VALUES(69,24,'69:24','(چئبن ته) وڻنديءَ تي کائو ۽ پيئو انھيءَ ڪري جو گذريل ڏينھن ۾ اڳي موڪليو ھيؤ.');
INSERT INTO "amroti" VALUES(69,25,'69:25','۽ جنھن کي سندس اعمالنامون سندس کٻي ھٿ ۾ ڏنو ويو، سو چوندو ھاءِ ارمان جيڪر منھنجو اعمالنامون مون کي نه ملي ھا.');
INSERT INTO "amroti" VALUES(69,26,'69:26','نه ڄاڻان ھا ته منھنجو حساب ڇا آھي؟');
INSERT INTO "amroti" VALUES(69,27,'69:27','ھاءِ ارمان جيڪر موت نبيريندڙ ھجي ھا.');
INSERT INTO "amroti" VALUES(69,28,'69:28','منھنجي مال مون کي (ڪو) فائدو نه ڏنو.');
INSERT INTO "amroti" VALUES(69,29,'69:29','مون کان منھنجي بادشاھت چٽ ٿي.');
INSERT INTO "amroti" VALUES(69,30,'69:30','(ملائڪن کي چئبو ته) پڪڙيوس پوءِ ڳچيءَ ۾ ڳٽ ٻڌوس.');
INSERT INTO "amroti" VALUES(69,31,'69:31','وري دوزخ ۾ وجھوس.');
INSERT INTO "amroti" VALUES(69,32,'69:32','وري اھڙي زنجير ۾ قابو ڪريوس جنھن جي ڊيگھ ستر گز ھجي.');
INSERT INTO "amroti" VALUES(69,33,'69:33','ڇو ته اُھو (ماڻھو) الله وڏي کي نه مڃيندو ھو.');
INSERT INTO "amroti" VALUES(69,34,'69:34','۽ نڪي (ٻـين کي) مسڪينن جي کارائڻ تي تاڪيد ڪندو ھو.');
INSERT INTO "amroti" VALUES(69,35,'69:35','تنھنڪري اُن جو اڄ ھت ڪو دوست ڪونھي.');
INSERT INTO "amroti" VALUES(69,36,'69:36','۽ نڪي اُن جو ڦٽن جي روڳ کانسواءِ ٻيو ڪو کاڌو آھي.');
INSERT INTO "amroti" VALUES(69,37,'69:37','(جو) ڏوھارين کانسواءِ (ٻيو) ڪو ان کي نه کارائيندو.');
INSERT INTO "amroti" VALUES(69,38,'69:38','پوءِ جيڪي ڏسو ٿا اُن جو قسم کڻان ٿو.');
INSERT INTO "amroti" VALUES(69,39,'69:39','۽ جيڪي نه ٿا ڏسو تنھن جو (به).');
INSERT INTO "amroti" VALUES(69,40,'69:40','ته بيشڪ ھي (قرآن) ھڪ سڳوري قاصد جو سنيھو آھي.');
INSERT INTO "amroti" VALUES(69,41,'69:41','۽ اُھو ڪنھن شاعر جو ڪلام نه آھي، اوھين ٿورڙو يقين رکو ٿا.');
INSERT INTO "amroti" VALUES(69,42,'69:42','۽ نڪي ڪنھن ڳجھ بڪندڙ جو ڪلام آھي، ٿوري نصيحت وٺو ٿا.');
INSERT INTO "amroti" VALUES(69,43,'69:43','(ھيءُ) جھانن جي پالڻھار کان نازل ٿيل آھي.');
INSERT INTO "amroti" VALUES(69,44,'69:44','۽ جيڪڏھن (پيغمبر) اسان تي ڪي ڳالھيون (پاڻون) ٺاھي ھا.');
INSERT INTO "amroti" VALUES(69,45,'69:45','ته ضرور سڄي ھٿ کان کيس پڪڙيون ھا.');
INSERT INTO "amroti" VALUES(69,46,'69:46','وري سندس دل جي رڳ وڍي ڇڏيون ھا.');
INSERT INTO "amroti" VALUES(69,47,'69:47','پوءِ اوھان مان ڪو ھڪڙو (اسان جي عذاب کي) کانئس جھلڻ وارو نه آھي.');
INSERT INTO "amroti" VALUES(69,48,'69:48','۽ بيشڪ ھي (قرآن) پرھيزگارن لاءِ نصيحت آھي.');
INSERT INTO "amroti" VALUES(69,49,'69:49','۽ بيشڪ اسين ڄاڻون ٿا ته ڪي اوھان مان ڪوڙ ڀانئيندڙ آھن.');
INSERT INTO "amroti" VALUES(69,50,'69:50','۽ بيشڪ ھي (قرآن) ڪافرن لاءِ ارمان (جو سبب) آھي.');
INSERT INTO "amroti" VALUES(69,51,'69:51','۽ بيشڪ ھي (قرآن) پورو سَچو آھي.');
INSERT INTO "amroti" VALUES(69,52,'69:52','تنھنڪري تون پنھنجي وڏي پالڻھار جي نالي کي پاڪائيءَ سان ياد ڪر.');
INSERT INTO "amroti" VALUES(70,1,'70:1','ھڪ سوال ڪندڙ ڪافرن تي پوندڙ عذاب جو سوال ڪيو.');
INSERT INTO "amroti" VALUES(70,2,'70:2','جنھن کي ڪو ھٽائڻ وارو نه آھي.');
INSERT INTO "amroti" VALUES(70,3,'70:3','جو وڏن مرتبن واري الله جي طرف کان (پوندڙ) آھي.');
INSERT INTO "amroti" VALUES(70,4,'70:4','جنھن ڏينھن جي ڊيگھ پنجاھ ھزار ورھيه آھي تنھن ۾ ملائڪ ۽ جبرئيل الله ڏانھن چڙھندا.');
INSERT INTO "amroti" VALUES(70,5,'70:5','تنھنڪري تون چڱو صبر ڪر.');
INSERT INTO "amroti" VALUES(70,6,'70:6','ڇوته ڪافر انھيءَ (ڏينھن) کي پري ڏسن ٿا.');
INSERT INTO "amroti" VALUES(70,7,'70:7','۽ اسين اُن کي ويجھو ٿا ڏسون.');
INSERT INTO "amroti" VALUES(70,8,'70:8','جنھن ڏينھن آسمان پگھريل ٽامي وانگر ٿيندو.');
INSERT INTO "amroti" VALUES(70,9,'70:9','۽ جبل رڱيل اُن جان ٿيندا.');
INSERT INTO "amroti" VALUES(70,10,'70:10','۽ ڪو مائٽ ڪنھن ٻئي مائٽ کان نه پڇندو.');
INSERT INTO "amroti" VALUES(70,11,'70:11','(توڙي جو) اُھي کين ڏسڻ ۾ ايندا، گنھگار گھرندو ته جيڪر اُن ڏينھن جي عذاب جي بدران پنھنجا پُٽ بدلو ڏئي.');
INSERT INTO "amroti" VALUES(70,12,'70:12','۽ پنھنجي زال ۽ پنھنجو ڀاءُ (به).');
INSERT INTO "amroti" VALUES(70,13,'70:13','۽ پنھنجو اُھو سارو قبيلو (به) جنھن کيس ٽڪايو ٿي.');
INSERT INTO "amroti" VALUES(70,14,'70:14','۽ اُھو (به) جيڪي سڀئي زمين ۾ آھي پوءِ سندس اھو (بدلو ڏيڻ) ڇڏائيس.');
INSERT INTO "amroti" VALUES(70,15,'70:15','ائين نه آھي، بيشڪ اِھو (دوزخ) الانبن واري باھ آھي.');
INSERT INTO "amroti" VALUES(70,16,'70:16','مَٿي جي کَل کي ڇڪيندڙ آھي.');
INSERT INTO "amroti" VALUES(70,17,'70:17','جيڪو (خدا کان) پٺيرو ٿيو ۽ منھن موڙيائين تنھنکي سڏي ٿي.');
INSERT INTO "amroti" VALUES(70,18,'70:18','۽ (مال) گڏ ڪيائين پوءِ سانڀيائين.');
INSERT INTO "amroti" VALUES(70,19,'70:19','بيشڪ ماڻھو حرص وارو پيدا ٿيل آھي.');
INSERT INTO "amroti" VALUES(70,20,'70:20','جڏھن (ڪو) ڏُک پھچندو اٿس (ته) دانھون ڪندو آھي.');
INSERT INTO "amroti" VALUES(70,21,'70:21','۽ جڏھن ڪو سُک پھچندو اٿس (ته) ڪنجوس ٿيندو آھي.');
INSERT INTO "amroti" VALUES(70,22,'70:22','پر (اُھي) نمازي (نه).');
INSERT INTO "amroti" VALUES(70,23,'70:23','جيڪي پنھنجي نماز تي ھميشه پورا آھن.');
INSERT INTO "amroti" VALUES(70,24,'70:24','۽ اُھي (به) جن جي مالن ۾ (خداءَ جو) حصّو مقرر ٿيل آھي.');
INSERT INTO "amroti" VALUES(70,25,'70:25','سوال ڪندڙ ۽ نه سوال ڪندڙن لاءِ.');
INSERT INTO "amroti" VALUES(70,26,'70:26','۽ اُھي جيڪي قيامت جي ڏينھن جو يقين رکندا آھن.');
INSERT INTO "amroti" VALUES(70,27,'70:27','۽ اُھي جيڪي پنھنجي پالڻھار جي عذاب کان ڊڄڻ وارا آھن.');
INSERT INTO "amroti" VALUES(70,28,'70:28','بيشڪ سندس پالڻھار جو عذاب (اھڙو آھي) جنھن کان بچي نه سگھبو.');
INSERT INTO "amroti" VALUES(70,29,'70:29','۽ اُھي جيڪي پنھنجين اگھڙن کي نگاھ رکڻ وارا آھن.');
INSERT INTO "amroti" VALUES(70,30,'70:30','پنھنجين زالن يا پنھنجين ٻانھين کانسواءِ پوءِ بيشڪ اُھي ملامت وارا نه آھن.');
INSERT INTO "amroti" VALUES(70,31,'70:31','پوءِ جيڪي اُن کانسواءِ (ٻي ڳالھ) طلبيندا سي ئي حد کان لنگھڻ وارا آھن.');
INSERT INTO "amroti" VALUES(70,32,'70:32','۽ اُھي جيڪي پنھنجين امانتن کي ۽ پنھنجن انجامن کي پوري ڪرڻ وارا آھن.');
INSERT INTO "amroti" VALUES(70,33,'70:33','۽ اُھي جيڪي پنھنجين شاھدين تي قائم آھن.');
INSERT INTO "amroti" VALUES(70,34,'70:34','۽ اُھي جيڪي پنھنجي نماز جي سنڀال ڪندڙ آھن.');
INSERT INTO "amroti" VALUES(70,35,'70:35','اُھي (بھشت جي) باغن ۾ عزت سان رھندا.');
INSERT INTO "amroti" VALUES(70,36,'70:36','پوءِ ڪافرن کي ڇا (ٿيو) آھي جو توڏانھن ڪنڌ کڻي ڊوڙندا آھن.');
INSERT INTO "amroti" VALUES(70,37,'70:37','سڄي پاسي کان ۽ کٻي پاسي کان ٽوليون ٽوليون ٿي.');
INSERT INTO "amroti" VALUES(70,38,'70:38','منجھائن سڀڪو شخص نعمت واري بھشت جي گھڙڻ جي طمع ڪري ٿو ڇا؟');
INSERT INTO "amroti" VALUES(70,39,'70:39','ائين نه آھي، بيشڪ اسان اُنھن کي اُنھيءَ شيءِ مان پيدا ڪيو آھي جنھنجي خبر اٿن.');
INSERT INTO "amroti" VALUES(70,40,'70:40','پوءِ اُڀرندن ۽ اُلھندن جي پالڻھار جو قسم کڻان ٿو ته بيشڪ اسين وسوارا آھيون.');
INSERT INTO "amroti" VALUES(70,41,'70:41','ھن (ڳالھ) تي جو اُنھن کان چڱا (ٻيا) مٽائي آڻيون ۽ اسين ڪي عاجز ٿيڻ وارا نه آھيون.');
INSERT INTO "amroti" VALUES(70,42,'70:42','پوءِ (اي پيغمبر) انھن کي (ايسين) ٺاھ ٺاھيندو ۽ راند ڪندو ڇڏي ڏي جيسين اُھي پنھنجو اُھو ڏينھن ڏسن جنھن جو کين انجام ڏجي ٿو.');
INSERT INTO "amroti" VALUES(70,43,'70:43','جنھن ڏينھن قبرن مان ڊوڙندا نڪرندا ڄڻ ته اُھي ڪنھن جھنڊي ڏانھن ڊڪن ٿا.');
INSERT INTO "amroti" VALUES(70,44,'70:44','ان وقت سندن اکيون جھڪيون ھونديون انھن کي خواري وٺي ويندي، اِھو اُھو ڏينھن آھي جنھن جو کين انجام ڏنو ٿي ويو.');
INSERT INTO "amroti" VALUES(71,1,'71:1','بيشڪ اسان نوح کي سندس قوم ڏانھن موڪليو ته پنھنجي قوم کي انھن وٽ ڏکوئيندڙ عذاب جي پھچڻ کان اڳ ۾ ڊيڄار.');
INSERT INTO "amroti" VALUES(71,2,'71:2','چيائين ته اي منھنجي قوم بيشڪ آءٌ اوھان لاءِ پڌرو ڊيڄاريندڙ آھيان.');
INSERT INTO "amroti" VALUES(71,3,'71:3','ته الله جي عبادت ڪريو ۽ کانئس ڊڄو ۽ منھنجو چيو مڃيو.');
INSERT INTO "amroti" VALUES(71,4,'71:4','ته اوھان جا گناھ اوھان کي بخشي ۽ اوھان کي ٺھرايل وقت تائين ڍر ڏئي، بيشڪ الله جو ٺھرايل وقت ايندو (تڏھن) ان کي ڍر نه ڏبي، جيڪڏھن ڄاڻندا آھيو.');
INSERT INTO "amroti" VALUES(71,5,'71:5','(نوح) چيو ته اي منھنجا پالڻھار بيشڪ آءٌ پنھنجي قوم کي رات ۽ ڏينھن سڏيندو رھيس.');
INSERT INTO "amroti" VALUES(71,6,'71:6','پوءِ منھنجي سڏڻ سندن حق ۾ ڀڄڻ کانسواءِ (ٻيو) ڪي نه وڌايو.');
INSERT INTO "amroti" VALUES(71,7,'71:7','۽ بيشڪ مون جڏھن به انھن کي سڏيو ته تون انھن کي بخشين (تڏھن) پنھنجيون آڱريون پنھنجن ڪَنن ۾ وڌائون ۽ پنھنجا ڪپڙا پاڻ تي ويڙھيائون ۽ (ڪُفر تي) قائم رھيا ۽ تمام گھڻو ھٺ ڪيائون.');
INSERT INTO "amroti" VALUES(71,8,'71:8','وري بيشڪ مون اُنھن کي ڏاڍيان سڏيو.');
INSERT INTO "amroti" VALUES(71,9,'71:9','وري بيشڪ مون انھن کي ظاھر ظھور چيو ۽ انھن کي ڳجھي طرح (به) چيم.');
INSERT INTO "amroti" VALUES(71,10,'71:10','پوءِ چيم ته پنھنجي پالڻھار کان بخشش گھرو، ڇوته اُھو بخشڻھار آھي.');
INSERT INTO "amroti" VALUES(71,11,'71:11','ته گھڻو وسندڙ مينھن اوھان تي لڳولڳ وسائي.');
INSERT INTO "amroti" VALUES(71,12,'71:12','۽ اوھان کي مال ۽ پٽ گھڻا ڏئي ۽ اوھان کي باغ ڏئي ۽ اوھان کي نَھرون بڻائي ڏي.');
INSERT INTO "amroti" VALUES(71,13,'71:13','اوھان کي ڇا (ٿيو) آھي جو الله جي وڏائي جو ويساھ نه ٿا رکو.');
INSERT INTO "amroti" VALUES(71,14,'71:14','حالانڪ اوھان کي قسمن قسمن ۾ بڻايائين.');
INSERT INTO "amroti" VALUES(71,15,'71:15','نه ڏسندا آھيو ڇا ته الله ڪيئن ستن آسمانن کي طبقا طبقا بڻايو.');
INSERT INTO "amroti" VALUES(71,16,'71:16','۽ انھن ۾ چنڊ کي روشن ڪيائين ۽ سج کي (چمڪندڙ) ڏيئو ڪيائين.');
INSERT INTO "amroti" VALUES(71,17,'71:17','۽ الله اوھان کي زمين منجھان ھڪڙي ڄمائڻ طرح ڄمايو.');
INSERT INTO "amroti" VALUES(71,18,'71:18','وري اوھان کي زمين ۾ موٽائي نيندو ۽ وري اوھان کي پوري طرح ٻاھر ڪڍندو.');
INSERT INTO "amroti" VALUES(71,19,'71:19','۽ الله اوھان لاءِ زمين کي وڇاڻو بڻايو.');
INSERT INTO "amroti" VALUES(71,20,'71:20','ته اُن ۾ ويڪرين واٽن سان ھلو.');
INSERT INTO "amroti" VALUES(71,21,'71:21','نوح چيو ته اي منھنجا پالڻھار بيشڪ انھن منھنجو چيو نه مڃيو ۽ اُنھن جي پويان لڳا جن جي حق ۾ سندن مال ۽ سندن اولاد ڇيئي کانسواءِ (ٻيو) ڪي نه وڌايو.');
INSERT INTO "amroti" VALUES(71,22,'71:22','۽ انھن تمام وڏو فريب پکيڙيو.');
INSERT INTO "amroti" VALUES(71,23,'71:23','۽ چيائون ته پنھنجن بُتن کي ڪڏھن نه ڇڏيو ۽ نڪي وَد ۽ نڪي سُوَاع ۽ نڪي يَغُوث ۽ يَعُوق ۽ نَسۡر کي ڇڏيو.');
INSERT INTO "amroti" VALUES(71,24,'71:24','۽ بيشڪ گھڻن کي گمراھ ڪيائون، ۽ (اي الله) ظالمن کي گمراھيءَ کانسواءِ (ٻيو) ڪي وڌيڪ نه ڏي.');
INSERT INTO "amroti" VALUES(71,25,'71:25','انھن کي سندن گناھن جي سبب ڪري ٻوڙيو ويو وري انھن کي باھ ۾ داخل ڪيو ويو، پوءِ الله کانسواءِ (ٻيو) ڪو پاڻ لاءِ مددگار نه لڌائون.');
INSERT INTO "amroti" VALUES(71,26,'71:26','۽ نُوح چيو ته اي منھنجا پالڻھار ڪافرن مان ڪو زمين تي رھندڙ نه ڇڏ.');
INSERT INTO "amroti" VALUES(71,27,'71:27','بيشڪ تون جيڪڏھن انھن کي ڇڏيندين ته تنھنجن ٻانھن کي گمراھ ڪندا ۽ اُھي بدڪار مُنڪر کانسواءِ (ٻيو) نه ڄڻيندا.');
INSERT INTO "amroti" VALUES(71,28,'71:28','اي منھنجا پالڻھار مون کي ۽ منھنجي ماءُ پيءُ کي بخش ۽ جيڪو منھنجي گھر ۾ مؤمن ٿي اچي تنھن کي ۽ سڀني مؤمنن ۽ مؤمنياڻين کي (بخش)، ۽ ظالمن کي ھلاڪيءَ کانسواءِ (ٻيو) ڪي وڌيڪ نه ڏي.');
INSERT INTO "amroti" VALUES(72,1,'72:1','(اي پيغمبر) چؤ ته مون ڏانھن (ھن ڳالھ جو) وحي موڪليو ويو ته جِنّن مان ھڪ ٽوليءَ (قرآن) ٻڌو پوءِ چيائون ته بيشڪ اسان ھڪ عجيب قرآن ٻڌو.');
INSERT INTO "amroti" VALUES(72,2,'72:2','جو سڌي رستي ڏانھن ڏس ڏئي ٿو تنھنڪري ان تي ايمان آندوسون، ۽ پنھنجي پالڻھار سان ڪنھن ھڪڙي کي شريڪ ھرگز نه ڪنداسون.');
INSERT INTO "amroti" VALUES(72,3,'72:3','۽ (بيان ڪرڻ لڳا) ته اسان جي پالڻھار جو شان تمام وڏو آھي نڪي ڪو زال ۽ اولاد رکيو اٿس.');
INSERT INTO "amroti" VALUES(72,4,'72:4','۽ ھي ته الله جي نسبت ۾ اسان مان بيوقوف ڪوڙو ٺاھ ٺاھيو ٿي.');
INSERT INTO "amroti" VALUES(72,5,'72:5','۽ ھي ته اسان ڀانيو ھو ته اسان ۽ جنّ ڪڏھن الله تي ڪوڙ نه چوندا.');
INSERT INTO "amroti" VALUES(72,6,'72:6','۽ ھي ته ماڻھن مان ڪيترا مڙس جنّن جي مڙسن جي سام پوندا ھوا پوءِ ماڻھن جنّن (جي حق) ۾ (ھٿئون) وڏائي وڌائي.');
INSERT INTO "amroti" VALUES(72,7,'72:7','۽ ھي ته (اي جنّو) ماڻھن اھڙو گمان ڪيو ھو جھڙو اوھان گمان ڪيو ھو ته الله ھرگز ڪنھن کي پيغمبر ڪري نه موڪليندو.');
INSERT INTO "amroti" VALUES(72,8,'72:8','۽ ھي ته اسان آسمان کي جاچيو پوءِ ڏٺوسونس ته سخت چوڪيدارن ۽ اُلانبن سان ڀرجي ويو آھي.');
INSERT INTO "amroti" VALUES(72,9,'72:9','۽ ھي ته اسان (اڳي) آسمان جي (گھڻن) رستن تي (ملائڪن جي گفتگو) ٻڌڻ لاءِ وھندا ھواسين، پوءِ جيڪو ھاڻي ڪن ڏيندو آھي سو پاڻ لاءِ تيار ٿيل اُلانبي وارو تارو ڏسندو آھي.');
INSERT INTO "amroti" VALUES(72,10,'72:10','۽ ھي ته اسين نه ٿا ڄاڻون ته جيڪي زمين ۾ آھن تن لاءِ ڪنھن مدائي جو ارادو ڪيو ويو آھي يا انھن لاءِ سندن پالڻھار ڪو چڱائي جو ارادو ڪيو آھي.');
INSERT INTO "amroti" VALUES(72,11,'72:11','۽ ھي ته ڪي اسان مان سڌريل آھن ۽ اسان مان ڪي اُن کانسواءِ (به) آھن، (اسين) مختلف ٽوليون ٽوليون ھواسون.');
INSERT INTO "amroti" VALUES(72,12,'72:12','۽ ھي ته اسان ڄاتو ته زمين ۾ الله کي ڪڏھن به ٿَڪائي نه سگھنداسون ۽ نڪي ڀڄي ڪڏھن ٿَڪائي سگھنداسونس.');
INSERT INTO "amroti" VALUES(72,13,'72:13','۽ ھي ته جڏھن اسان ھدايت (جي ڳالھ) ٻڌي (تڏھن) اُن تي ايمان آندوسون، پوءِ جيڪو پنھنجي پالڻھار تي ايمان آڻيندو سو نڪي ڪنھن نقصان کان ۽ نڪي ڪنھن ظلم کان ڊڄندو.');
INSERT INTO "amroti" VALUES(72,14,'72:14','۽ ھي ته اسان مان ڪي مسلمان آھن ۽ اسان مان ڪي گنھگار آھن، پوءِ جيڪي مُسلمان ٿيا تن سِڌي واٽ جي نيت ڪئي.');
INSERT INTO "amroti" VALUES(72,15,'72:15','۽ گنھگار سي دوزخ جي ڪاٺيون ھوندا.');
INSERT INTO "amroti" VALUES(72,16,'72:16','۽ (اي پيغمبر) ھيءَ (به چؤ ته مون ڏانھن موڪليو ويو آھي) ته جيڪڏھن ماڻھو سڌيءَ واٽ تي بيھڪ وٺن ھا ته ضرور انھن (جي پوکن) کي گھڻو پاڻي پياريون ھا.');
INSERT INTO "amroti" VALUES(72,17,'72:17','ته اُن ۾ کين پرکيون ۽ جيڪو پنھنجي پالڻھار جي ياد ڪرڻ کان مُنھن موڙيندو تنھن کي (الله) سخت عذاب ۾ ڦاسائيندو.');
INSERT INTO "amroti" VALUES(72,18,'72:18','۽ ھي ته سڀ مسجدون (خاص) الله جون آھن تنھنڪري الله سان گڏ ڪنھن جي عبادت نه ڪريو.');
INSERT INTO "amroti" VALUES(72,19,'72:19','۽ ھي ته جنھن مھل الله جو ٻانھو (محمد صلى الله عليه وسلم) بيٺو ته سندس عبادت ڪري تنھن مھل جنّ مٿس ڳاھٽ ڪرڻ تي ھوا.');
INSERT INTO "amroti" VALUES(72,20,'72:20','چؤ ته آءٌ رڳو پنھنجي پالڻھار جي عبادت ڪريان ٿو ۽ ڪنھن ھڪڙي کي ساڻس شريڪ نه ٿو ڪريان.');
INSERT INTO "amroti" VALUES(72,21,'72:21','چؤ ته بيشڪ آءٌ اوھان کي ڏک پھچائڻ ۽ سنئي رستي تي آڻڻ جو وس نٿو رکان.');
INSERT INTO "amroti" VALUES(72,22,'72:22','چؤ ته بيشڪ مون کي الله (جي عذاب) کان ڪوبه نه بچائيندو، ۽ اُن کانسواءِ ڪا پناھ ڪڏھن نه لھندس.');
INSERT INTO "amroti" VALUES(72,23,'72:23','مگر الله جي پار کان خبر پھچائڻي آھي ۽ سندس پيغام پھچائڻا آھن ۽ جيڪو الله ۽ سندس پيغمبر جي نافرماني ڪندو تنھن لاءِ بيشڪ دوزخ جي باھ آھي جنھن ۾ سدائين رھندا.');
INSERT INTO "amroti" VALUES(72,24,'72:24','(ايستائين غفلت ۾ رھندا) جيسين ڪه جيڪو اُنھن کي انجام ڏجي ٿو سو ڏسن پوءِ ڄاڻندا ته مددگار جي ڪري وڌيڪ ھيڻو ۽ ڳاڻيٽي جي ڪري تمام ٿورو ڪير آھي.');
INSERT INTO "amroti" VALUES(72,25,'72:25','(کين) چؤ ته آءٌ نه ٿو ڄاڻان ته جنھنجو اوھان کي انجام ڏجي ٿو سو (الائي) ويجھو آھي يا منھنجو پالڻھار اُن جي ڪا (ٻي) مُدت مُقرر ڪندو.');
INSERT INTO "amroti" VALUES(72,26,'72:26','(اُھو) ڳُجھ جو ڄاڻندڙ آھي پوءِ پنھنجي ڳجھ تي ڪنھن ھڪڙي کي واقف نه ڪندو آھي.');
INSERT INTO "amroti" VALUES(72,27,'72:27','سواءِ انھيءَ جي جو جنھن پيغمبر کي پسند ڪيائين بيشڪ الله سندس اڳتان ۽ سندس پوئتان نگھبان (ملائڪ) موڪليندو آھي.');
INSERT INTO "amroti" VALUES(72,28,'72:28','(ھن لاءِ) ته ڄاڻي ته پنھنجي پالڻھار جا حُڪم سچ پچ پھچايا اٿن ۽ جيڪي وٽن آھي تنھنکي الله ھر طرح پنھنجي وس رکيو آھي ۽ سڀڪنھن شيءَ جو ڳاڻيٽو ڳڻي ڇڏيو اٿس.');
INSERT INTO "amroti" VALUES(73,1,'73:1','اي (پاڻ تي) ڪپڙي ويڙھڻ ورا مڙس.');
INSERT INTO "amroti" VALUES(73,2,'73:2','رات جو کڙو ٿيءُ پر ٿورو.');
INSERT INTO "amroti" VALUES(73,3,'73:3','رات جو اڌ يا اڌ رات کان ٿورو گھٽاءِ.');
INSERT INTO "amroti" VALUES(73,4,'73:4','يا اڌ رات تي وڌاءِ ۽ قرآن ٺاھي ٺاھي پڙھ.');
INSERT INTO "amroti" VALUES(73,5,'73:5','بيشڪ اسين توتي ھڪ ڳرو حُڪم نازل ڪنداسين.');
INSERT INTO "amroti" VALUES(73,6,'73:6','بيشڪ رات جو اُٿڻ (نفس جي) جھڪي ڪرڻ ۾ تمام سخت آھي ۽ لفظن (چوڻ) ۾ تمام پڪو آھي.');
INSERT INTO "amroti" VALUES(73,7,'73:7','بيشڪ توکي ڏينھن ۾ گھڻو ڌنڌو آھي.');
INSERT INTO "amroti" VALUES(73,8,'73:8','۽ پنھنجي پالڻھار جو نالو ياد ڪر ۽ سڀني پاسن کان ھڪ طرفو ٿي چڱي طرح ڏانھس متوجه ٿيءُ.');
INSERT INTO "amroti" VALUES(73,9,'73:9','جو اوڀر ۽ اولھ جو پالڻھار آھي ان کانسواءِ ٻيو ڪو عبادت جو لائق نه آھي تنھنڪري تون کيس ڀرجھلو ڪري وٺ.');
INSERT INTO "amroti" VALUES(73,10,'73:10','۽ (ڪافر) جيڪي چوندا آھن تنھن تي صبر ڪر ۽ چڱي طرح انھن کي ڇڏي ڏي.');
INSERT INTO "amroti" VALUES(73,11,'73:11','۽ مون کي ۽ جيڪي ڪوڙ ڀائيندڙ آسودا آھن تن کي ڇڏي ڏي ۽ اُنھن کي ٿوري مُھلت ڏي.');
INSERT INTO "amroti" VALUES(73,12,'73:12','بيشڪ اسان وٽ (ڳرا) نيل ۽ ٻاريل باھ آھي.');
INSERT INTO "amroti" VALUES(73,13,'73:13','۽ نڙي گھٽيندڙ طعام ۽ ڏکوئيندڙ عذاب آھي.');
INSERT INTO "amroti" VALUES(73,14,'73:14','جنھن ڏينھن زمين ۽ جبل ڌٻندا ۽ جبل واريءَ جا ڍڳ ھاريل ٿيندا.');
INSERT INTO "amroti" VALUES(73,15,'73:15','(اي ماڻھو) بيشڪ اسان اوھان ڏانھن ھڪ پيغمبر اوھان تي شاھدي ڏيندڙ موڪليو جھڙي طرح فرعون ڏانھن (موسى) پيغمبر (ڪري) موڪليوسون.');
INSERT INTO "amroti" VALUES(73,16,'73:16','پوءِ فرعون اُن پيغمبر جي نافرماني ڪئي تنھنڪري اُن کي سخت پڪڙ ۾ پڪڙيوسون.');
INSERT INTO "amroti" VALUES(73,17,'73:17','پوءِ جيڪڏھن (اوھين) ڪافر رھندؤ ته اھڙي ڏينھن کان ڪيئن بچندو جيڪو ٻارن کي ٻڍو ڪندو.');
INSERT INTO "amroti" VALUES(73,18,'73:18','اُن ڏينھن ۾ آسمان ڦاٽندو، الله جو انجام (ضرور پورو) ٿيڻو آھي.');
INSERT INTO "amroti" VALUES(73,19,'73:19','بيشڪ ھيءَ نصيحت آھي، پوءِ جيڪو گھري سو پنھنجي پالڻھار ڏانھن واٽ وٺي.');
INSERT INTO "amroti" VALUES(73,20,'73:20','بيشڪ تنھنجو پالڻھار ڄاڻندو آھي ته تون ٻن ٽھائن رات جي ويجھو اُٿندڙ آھين ۽ اڌ رات جو ۽ ٽھائي رات جي ۽ جيڪي تو ساڻ آھن تن مان به ھڪ ٽولي (اٿندي آھي)، ۽ الله رات ۽ ڏينھن جو اندازو ڪندو آھي، الله ڄاتو ته اوھين رات جو اُٿڻ پورو ڪري نه ٿا سگھو تنھنڪري اوھان تي (ٻاجھ سان) موٽيو پوءِ جيترو آسان ٿي سگھي سو قرآن مان پڙھو، الله ڄاڻي چڪو ته اوھان مان ڪي بيمار ٿيندا ۽ ٻيا جي زمين ۾ مسافري ڪن ٿا الله جي فضل مان (روزيءَ جي) طلب ڪن ٿا ۽ ٻيا الله جي واٽ ۾ (ڪافرن سان) وڙھن ٿا، تنھنڪري جيترو قرآن مان آسان ٿي سگھي سو پڙھو ۽ نماز کي قائم رکو ۽ زڪوٰة ڏيو ۽ الله کي چڱيءَ طرح قرض ڏيو، ۽ جيڪي پاڻ لاءِ چڱائي مان اڳي موڪليندؤ تنھن کي الله وٽ وڌيڪ چڱو ۽ اجر ڪري تمام وڏو لھندؤ، ۽ الله کان بخشش گھرو، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO "amroti" VALUES(74,1,'74:1','اي (پاڻتي) ڪپڙي ويڙھڻ وارا مڙس.');
INSERT INTO "amroti" VALUES(74,2,'74:2','کڙو ٿي پوءِ ڊيڄار.');
INSERT INTO "amroti" VALUES(74,3,'74:3','۽ پنھنجي پالڻھار جي وڏائي بيان ڪر.');
INSERT INTO "amroti" VALUES(74,4,'74:4','۽ پنھنجا ڪپڙا پاڪ رک.');
INSERT INTO "amroti" VALUES(74,5,'74:5','۽ پليتي کان پري رھ.');
INSERT INTO "amroti" VALUES(74,6,'74:6','۽ (اھڙو) احسان نه ڪر جو زيادہ عوض گھرين.');
INSERT INTO "amroti" VALUES(74,7,'74:7','۽ پنھنجي پالڻھار (جي حُڪم) لاءِ صبر اختيار ڪر.');
INSERT INTO "amroti" VALUES(74,8,'74:8','پوءِ جڏھن صُور ۾ ڦوڪبو.');
INSERT INTO "amroti" VALUES(74,9,'74:9','تڏھن اُن ڏينھن اوکو وقت ٿيندو.');
INSERT INTO "amroti" VALUES(74,10,'74:10','ڪافرن تي اڻ سکيو (وقت) ھوندو.');
INSERT INTO "amroti" VALUES(74,11,'74:11','مون کي ۽ جنھن کي اڪيلو پيدا ڪيم تنھن کي ڇڏي ڏي.');
INSERT INTO "amroti" VALUES(74,12,'74:12','۽ اُن کي گھڻو مال ڏنم.');
INSERT INTO "amroti" VALUES(74,13,'74:13','۽ (مجلسن ۾) حاضر ٿيڻ وارا پُٽ (به ڏنم).');
INSERT INTO "amroti" VALUES(74,14,'74:14','۽ ان کي پوري طرح وڌايم.');
INSERT INTO "amroti" VALUES(74,15,'74:15','وري (به) لالچ رکندو آھي ته (کيس) وڌيڪ ڏيان.');
INSERT INTO "amroti" VALUES(74,16,'74:16','ائين نه آھي، بيشڪ اُھو اسان جي آيتن سان جھڳڙو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(74,17,'74:17','جلد اُن کي وڏي سختي سان تڪليف ڏيندس.');
INSERT INTO "amroti" VALUES(74,18,'74:18','بيشڪ اُن دل ۾ سوچ ڪئي ۽ ٺھراءُ ڪيو.');
INSERT INTO "amroti" VALUES(74,19,'74:19','پوءِ لعنت ھجيس شال ڪيئن ٺھراء ڪيائين.');
INSERT INTO "amroti" VALUES(74,20,'74:20','وري به لعنت ھجيس شال ڪيئن ٺھراءُ ڪيائين.');
INSERT INTO "amroti" VALUES(74,21,'74:21','وري (غور ڪري) ڏٺائين.');
INSERT INTO "amroti" VALUES(74,22,'74:22','وري منھن ۾ شور وڌائين ۽ گھنڊ پاتائين.');
INSERT INTO "amroti" VALUES(74,23,'74:23','وري پٺيرو ٿيو ۽ وڏائي ڪيائين.');
INSERT INTO "amroti" VALUES(74,24,'74:24','پوءِ چيائين ته ھي (قرآن) رڳو جادو آھي جو (جادوگرن کان) ھليو اچي ٿو.');
INSERT INTO "amroti" VALUES(74,25,'74:25','ھي (قرآن) رڳو ماڻھوءَ جو ڪلام آھي.');
INSERT INTO "amroti" VALUES(74,26,'74:26','جلد اُن کي دوزخ ۾ وجھندس.');
INSERT INTO "amroti" VALUES(74,27,'74:27','۽ توکي ڪھڙي شيءَ ڄاڻايو ته دوزخ ڇا آھي.');
INSERT INTO "amroti" VALUES(74,28,'74:28','اُھو نڪي ڪجھ (گوشت) رھائي ٿو ۽ نڪي (ھڏا) ڇڏي ٿو.');
INSERT INTO "amroti" VALUES(74,29,'74:29','ماڻھن کي (ساڙي) ڪارو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(74,30,'74:30','اُن تي اُڻويہ ڄڻا دربان آھن.');
INSERT INTO "amroti" VALUES(74,31,'74:31','۽ دوزخ جا دربان رڳو ملائڪ ڪيا اٿون، ۽ سندن ڳاڻيٽو رڳو ڪافرن لاءِ پرک ڪئي اٿون ته ڪتاب وارا يقين ڪن ۽ ايمان وارا پنھنجي ايمان ۾ وڌن ۽ ڪتاب وارا ۽ مؤمن شڪ ۾ نه پون ۽ ته جن جي دلين ۾ (منافقيءَ جي) بيماري آھي سي ۽ ڪافر چون ته ھن مثال ڏيڻ ۾ الله جو ڇا مطلب آھي، اھڙيءَ طرح الله جنھن کي گھري (تنھن کي) ڀلائيندو آھي ۽ جنھن کي گھري (تنھن کي) ھدايت ڪندو آھي، ۽ تنھنجي پالڻھار جا لشڪر اُن کانسواءِ (ٻيو) ڪونه ڄاڻندو آھي، ۽ ھيءَ (دوزخ) سڀ ڪنھن ماڻھوءَ لاءِ رڳو نصيحت آھي.');
INSERT INTO "amroti" VALUES(74,32,'74:32','سچ چوان ٿو چنڊ جو قسم آھي.');
INSERT INTO "amroti" VALUES(74,33,'74:33','۽ رات جو جڏھن پٺ ڦيري.');
INSERT INTO "amroti" VALUES(74,34,'74:34','۽ صبح جو (قسم آھي) جڏھن روشن ٿئي.');
INSERT INTO "amroti" VALUES(74,35,'74:35','ته بيشڪ دوزخ ھڪ وڏين شين مان آھي.');
INSERT INTO "amroti" VALUES(74,36,'74:36','(سڀڪنھن) ماڻھو کي ڊيڄاريندڙ آھي.');
INSERT INTO "amroti" VALUES(74,37,'74:37','اوھان مان انھي لاءِ جيڪو گھري ته (بھشت) لاءِ اڳي وڌي يا (دوزخ ۾) پٺ تي رھي.');
INSERT INTO "amroti" VALUES(74,38,'74:38','سڀڪو جيءُ پنھنجي ڪئي ۾ ڳه پيل آھي.');
INSERT INTO "amroti" VALUES(74,39,'74:39','پر سڄي پاسي وارا (ڇٽل آھن).');
INSERT INTO "amroti" VALUES(74,40,'74:40','(بھشت جي) باغن ۾ ھوندا، گڏجي پڇندا.');
INSERT INTO "amroti" VALUES(74,41,'74:41','ڏوھارين کان.');
INSERT INTO "amroti" VALUES(74,42,'74:42','ته اوھان کي دوزخ ۾ ڪھڙي ڳالھ وڌو.');
INSERT INTO "amroti" VALUES(74,43,'74:43','چوندا ته نڪي نمازين مان ھواسون.');
INSERT INTO "amroti" VALUES(74,44,'74:44','۽ نڪي مسڪينن کي کارائيندا ھواسون.');
INSERT INTO "amroti" VALUES(74,45,'74:45','۽ بڪواتن سان گڏ بڪوات ڪندا ڪندا ھواسون.');
INSERT INTO "amroti" VALUES(74,46,'74:46','۽ بدلي جي ڏينھن کي ڪوڙ ڀانئيندا ھواسون.');
INSERT INTO "amroti" VALUES(74,47,'74:47','تان جو اسان کي موت پھتو.');
INSERT INTO "amroti" VALUES(74,48,'74:48','پوءِ کين پارت ڪندڙن جي پارت نفعو نه ڏيندي.');
INSERT INTO "amroti" VALUES(74,49,'74:49','پوءِ انھن کي ڇا (ٿيو) آھي جو نصيحت کان منھن موڙيندڙ (ٿيا) آھن.');
INSERT INTO "amroti" VALUES(74,50,'74:50','ڄڻ ته اُھي ٽھندڙ گڏھ آھن.');
INSERT INTO "amroti" VALUES(74,51,'74:51','جي شينھن کان ڀڳا ھجن.');
INSERT INTO "amroti" VALUES(74,52,'74:52','بلڪ منجھائن ھر ھڪ مڙس گھرندو آھي ته کيس (آسمان) مان کليل ڪتاب ڏجن.');
INSERT INTO "amroti" VALUES(74,53,'74:53','ائين نه آھي، بلڪ آخرت کان نه ٿا ڊڄن.');
INSERT INTO "amroti" VALUES(74,54,'74:54','سچ آھي ته بيشڪ قرآن ھڪ نصيحت آھي.');
INSERT INTO "amroti" VALUES(74,55,'74:55','پوءِ جيڪو گُھري سو اُن کي پڙھي.');
INSERT INTO "amroti" VALUES(74,56,'74:56','۽ الله جي گھر کانسواءِ (ان کي) ياد ڪري نه سگھندا آھن، (الله) اُھو آھي جنھن کان ڊڄڻ گھرجي ۽ اھو بخشش جو به مالڪ آھي.');
INSERT INTO "amroti" VALUES(75,1,'75:1','قيامت جي ڏينھن جو قسم کڻان ٿو.');
INSERT INTO "amroti" VALUES(75,2,'75:2','۽ ملامت ڪرڻ واري نفس جو قسم ٿو کڻان (ته قيامت ۾ سڀ اُٿندا).');
INSERT INTO "amroti" VALUES(75,3,'75:3','ماڻھو ڀائيندو آھي ڇا ته سندس ھڏا ڪڏھن گڏ نه ڪنداسون.');
INSERT INTO "amroti" VALUES(75,4,'75:4','ھائو! سنديس آڱرين جي ڏوڏين جي سڌي ڪرڻ تي وسوارا آھيون.');
INSERT INTO "amroti" VALUES(75,5,'75:5','بلڪ ماڻھو ھي گھرندو آھي ته پنھنجي اڳ (جي وقت) لاءِ گناھ ڪري.');
INSERT INTO "amroti" VALUES(75,6,'75:6','پڇي ٿو ته قيامت جو ڏينھن ڪڏھن ٿيندو؟');
INSERT INTO "amroti" VALUES(75,7,'75:7','پوءِ جڏھن اکيون ڦاٽي وينديون.');
INSERT INTO "amroti" VALUES(75,8,'75:8','۽ چنڊ گرھبو.');
INSERT INTO "amroti" VALUES(75,9,'75:9','۽ سج ۽ چنڊ (ھڪ جاءِ) گڏ ڪبا.');
INSERT INTO "amroti" VALUES(75,10,'75:10','(تڏھن) چوندو ماڻھو ته ڀڄڻ جي واھ ڪٿي آھي؟');
INSERT INTO "amroti" VALUES(75,11,'75:11','نه نه! ڪا واھ ڪانھي.');
INSERT INTO "amroti" VALUES(75,12,'75:12','تنھنجي پالڻھار وٽ اُن ڏينھن ٽڪاءُ جو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(75,13,'75:13','اُن ڏينھن ماڻھوءَ کي (انھيءَ بابت) خبردار ڪبو جيڪي اڳي موڪليو ھوائين ۽ پوئتي رھايائين.');
INSERT INTO "amroti" VALUES(75,14,'75:14','بلڪ ماڻھو پنھنجي لاءِ (پاڻ) ھڪ حُجت آھي.');
INSERT INTO "amroti" VALUES(75,15,'75:15','توڻيڪ پنھنجا بھانا پيش پيو ڪري.');
INSERT INTO "amroti" VALUES(75,16,'75:16','(اي پيغمبر وحي جي) قرآن پڙھڻ مھل پنھنجي زبان کي (ھن لاءِ) نه چور ته اُھو جلدي (ياد ڪري) وٺين.');
INSERT INTO "amroti" VALUES(75,17,'75:17','ڇوته (تنھنجي سيني ۾) قرآن جو گڏ ڪرڻ ۽ اُن جو پڙھڻ (آسان ڪرڻ) اسان جي ذمي آھي.');
INSERT INTO "amroti" VALUES(75,18,'75:18','پوءِ جڏھن قرآن (ملائڪ کان) پڙھايون تڏھن (پنھنجي دل) اُنجي پڙھڻ جي پٺيان لڳاءِ.');
INSERT INTO "amroti" VALUES(75,19,'75:19','وري بيشڪ اُنجو پڌرو ڪرڻ اسان جي ذمي آھي.');
INSERT INTO "amroti" VALUES(75,20,'75:20','نه نه! بلڪ (اي ڪافرؤ) دنيا کي دوست رکندا آھيو.');
INSERT INTO "amroti" VALUES(75,21,'75:21','۽ آخرت کي ڇڏيندا آھيو.');
INSERT INTO "amroti" VALUES(75,22,'75:22','ڪي مُنھن اُن ڏينھن سرھا ھوندا.');
INSERT INTO "amroti" VALUES(75,23,'75:23','پنھنجي پالڻھار ڏانھن ڏسندڙ ھوندا.');
INSERT INTO "amroti" VALUES(75,24,'75:24','۽ ڪي مُنھن اُن ڏينھن ڪارا ٿيل ھوندا.');
INSERT INTO "amroti" VALUES(75,25,'75:25','ڀانئيندا ته ساڻن پٺيءَ توڙ معاملو ڪيو ويندؤ.');
INSERT INTO "amroti" VALUES(75,26,'75:26','نه نه! جڏھن (ساھ) نِڙ گھٽ تي پھچندو.');
INSERT INTO "amroti" VALUES(75,27,'75:27','۽ چيو ويندو ته ڪو، ڦيڻو رکڻ وارو آھي.');
INSERT INTO "amroti" VALUES(75,28,'75:28','۽ ڀانئيندو ته (ھاڻي روح جي) جُدا ٿيڻ (جو) وقت اچي ويو.');
INSERT INTO "amroti" VALUES(75,29,'75:29','۽ ھڪ پِني ٻئي پِنيءَ سان وچڙندي.');
INSERT INTO "amroti" VALUES(75,30,'75:30','اُن ڏينھن تنھنجي پالڻھار وٽ ھلڻو آھي.');
INSERT INTO "amroti" VALUES(75,31,'75:31','پوءِ نڪي (الله جي ڪلام کي) سچو ڪيائين ۽ نڪي نماز پڙھيائين.');
INSERT INTO "amroti" VALUES(75,32,'75:32','پر ڪوڙ ڀانيائين ۽ منھن موڙيائين.');
INSERT INTO "amroti" VALUES(75,33,'75:33','وري پنھنجي گھر وارن ڏانھن آڪڙجي ھليو.');
INSERT INTO "amroti" VALUES(75,34,'75:34','توتي خرابي ھجي پوءِ توتي خرابي ھجي.');
INSERT INTO "amroti" VALUES(75,35,'75:35','وري (ٻيھر چوان ٿو ته) توتي خرابي ھجي.');
INSERT INTO "amroti" VALUES(75,36,'75:36','ماڻھو (ايئن) ڀانئي ٿو ڇا ته (کيس) بيڪار ڇڏي ڏبو.');
INSERT INTO "amroti" VALUES(75,37,'75:37','(اھو) منيءَ جو ٽيپو نه ھو ڇا جو (ماءُ جي ڳڀيرڻ ۾) اڇليو ٿي ويو.');
INSERT INTO "amroti" VALUES(75,38,'75:38','وري (رت ۾) دڳ ھو پوءِ الله (اُن کي) بڻايو ۽ اُن کي سئين لڱين ڪيائين.');
INSERT INTO "amroti" VALUES(75,39,'75:39','پوءِ منيءَ مان ٻه جنسون نَر ۽ مادي بڻايائين.');
INSERT INTO "amroti" VALUES(75,40,'75:40','اھو الله ھن (ڳالھ) تي وس وارو نه آھي ڇا جو مُئن کي جياري؟');
INSERT INTO "amroti" VALUES(76,1,'76:1','ڪڏھن ماڻھوءَ تي زماني مان ھڪ (اھڙو) وقت گذري چڪو آھي جو (ھو اھڙي) ڪا شيءَ نه ھو جا يادگيريءَ ۾ اچي.');
INSERT INTO "amroti" VALUES(76,2,'76:2','بيشڪ اسان ماڻھوءَ کي ھڪڙي (پاڻ ۾) گڏيل منيءَ مان پيدا ڪيو، ته اُن کي پرکيون پوءِ کيس ٻڌندڙ ڏسندڙ ڪيوسون.');
INSERT INTO "amroti" VALUES(76,3,'76:3','بيشڪ اسان کيس سڌو دڳ ڏسيو (ھاڻي ھو) شُڪر ڪندڙ ٿئي يا بي شُڪر.');
INSERT INTO "amroti" VALUES(76,4,'76:4','بيشڪ اسان ڪافرن لاءِ زنجير يا ڳٽ ۽ ٻرندڙ باھ تيار ڪئي آھي.');
INSERT INTO "amroti" VALUES(76,5,'76:5','بيشڪ نيڪ بخت ٻانھا (انھي شراب جي) پيالي مان پيئندا جنھن جي ملاوت ڪافور (جي چشمي جي پاڻي) جي ھوندي.');
INSERT INTO "amroti" VALUES(76,6,'76:6','اھو ھڪ چشمو آھي جنھن مان الله جا (پيارا) ٻانھا پيئندا جنھن کي چڱي طرح وھائيندا.');
INSERT INTO "amroti" VALUES(76,7,'76:7','جي (الله جي) باس پوري ڪندا آھن ۽ اُن ڏينھن کان ڊڄندا آھن جنھن جي بڇڙائي (ھر طرف) پڌري ھوندي.');
INSERT INTO "amroti" VALUES(76,8,'76:8','۽ طعام جي احتياج ھوندي مُحتاج ۽ يتيم ۽ قيدي کي کارائيندا آھن.');
INSERT INTO "amroti" VALUES(76,9,'76:9','(۽ چوندا آھن ته) اسين ته اوھان کي الله جي واسطي کارائيندا آھيون اوھان کان نڪو بدلو ۽ نڪا شڪر گذاري گھرندا آھيون.');
INSERT INTO "amroti" VALUES(76,10,'76:10','بيشڪ اسين پنھنجي پالڻھار کان ھڪ ڏکئي ڏينھن کان ڊڄندا آھيون جو تمام سخت آھي.');
INSERT INTO "amroti" VALUES(76,11,'76:11','پوءِ الله اُن ڏينھن جي مدائي کان کين بچايو ۽ اُنھن کي تازگي ۽ خوشي پھچائي.');
INSERT INTO "amroti" VALUES(76,12,'76:12','۽ سندن صبر ڪرڻ سببان باغ ۽ پَٽ (جو ويس) کين بدلو ڏنائين.');
INSERT INTO "amroti" VALUES(76,13,'76:13','اُن (باغ) ۾ تختن تي ٽيڪ ڏئي وھندا، اتي نڪي (سج جي) اُتت ۽ نڪي ڪا سخت سردي ڏسندا.');
INSERT INTO "amroti" VALUES(76,14,'76:14','۽ اُن (باغ) جون ڇانئون مٿن ويجھيون ٿيل ھونديون ۽ اُن جا ميوا (پٽڻ ۾) چڱي طرح ويجھا ڪبا.');
INSERT INTO "amroti" VALUES(76,15,'76:15','۽ روپن ٿانون ۽ پيالن سان وٽن اچ وڃ ٿيندي جي شيشن وانگر ھوندا.');
INSERT INTO "amroti" VALUES(76,16,'76:16','(پر) شيشا (به) چاندي جا جن کي انھن (پياريندڙن) پوري انداز سان ٺھرايو آھي.');
INSERT INTO "amroti" VALUES(76,17,'76:17','۽ اُتي کين شراب جا پيالا پياربا جنھن (جي پاڻي) جي ملاوت سُنڍ (جي چشمي) جي ھوندي.');
INSERT INTO "amroti" VALUES(76,18,'76:18','اُتي اھو ھڪ چشمو آھي جنھن جو نالو سَلۡسَبِيۡل رکيو وڃي ٿو.');
INSERT INTO "amroti" VALUES(76,19,'76:19','۽ وٽن ھميشه رھندڙ نينگر پيا گھمندا ڦرندا، (اي ڏسندڙ) جڏھن انھن کي ڏسندين (ته) انھن کي کنڊريل موتي پيو ڀائيندين.');
INSERT INTO "amroti" VALUES(76,20,'76:20','۽ جڏھن اُتي نھاريندين (ته) اڻ کٽ نعمت ۽ وڏي بادشاھي پيو ڏسندين.');
INSERT INTO "amroti" VALUES(76,21,'76:21','مٿانئن سنھي سائي پَٽ ۽ ٿُلھي پَٽ جا ڪپڙا ھوندا، ۽ چاندِيءَ جا ڪنگڻ کين پارايا ويندا، ۽ سندن پالڻھار کين تمام پاڪ شراب پياريندو.');
INSERT INTO "amroti" VALUES(76,22,'76:22','بيشڪ اھو اوھان (جي عملن) جو بدلو آھي ۽ اوھان جو سعيو قبول ڪيل آھي.');
INSERT INTO "amroti" VALUES(76,23,'76:23','بيشڪ اسان توتي قرآن درجي بدرجي لاٿو.');
INSERT INTO "amroti" VALUES(76,24,'76:24','پوءِ پنھنجي پالڻھار جي حڪم (اچڻ) تائين صبر ڪر ۽ انھن (ماڻھن) مان ڪنھن گنھگار يا بي شڪر جو چيو نه مڃ.');
INSERT INTO "amroti" VALUES(76,25,'76:25','۽ پنھنجي پالڻھار جو نالو صبح ۽ سانجھيءَ ياد ڪر.');
INSERT INTO "amroti" VALUES(76,26,'76:26','۽ ڪجھ (وقت) رات ۾ اُن لاءِ نماز پڙھ ۽ گھڻي رات سندس پاڪائي ساراھ.');
INSERT INTO "amroti" VALUES(76,27,'76:27','بيشڪ اُھي (ڪافر) دنيا کي پيارو رکندا آھن ۽ ڳوري ڏينھن کي پنھنجي پٺيان ڦٽو ڪندا آھن.');
INSERT INTO "amroti" VALUES(76,28,'76:28','اسان کين پيدا ڪيو ۽ سندن سَنڌ پُختا ڪياسون، ۽ جڏھن گھرون (ته) اھڙا ٻيا عوض ۾ مٽائي آڻيون.');
INSERT INTO "amroti" VALUES(76,29,'76:29','بيشڪ اھا نصيحت آھي، پوءِ جيڪو گھري سو پنھنجي پالڻھار ڏانھن واٽ وٺي.');
INSERT INTO "amroti" VALUES(76,30,'76:30','۽ اوھين نه گھرندؤ مگر اُھو جيڪي الله گھري، ڇوته الله ڄاڻندڙ حڪمت وارو آھي.');
INSERT INTO "amroti" VALUES(76,31,'76:31','جنھن کي گھري (تنھن کي) پنھنجي رحمت ۾ داخل ٿو ڪري، ۽ ظالمن لاءِ ڏکوئيندڙ عذاب تيار ڪيو اٿس.');
INSERT INTO "amroti" VALUES(77,1,'77:1','چڱين گُھلندڙن ھيرُن جو قسم آھي.');
INSERT INTO "amroti" VALUES(77,2,'77:2','پوءِ زور سان لڳڻ وارن وائن جو قسم آھي.');
INSERT INTO "amroti" VALUES(77,3,'77:3','۽ (ڪڪرن کي) کڻندڙن پکيڙيندڙن وائن جو قسم آھي.');
INSERT INTO "amroti" VALUES(77,4,'77:4','پوءِ (ڪڪرن کي) ھڪ ٻئي کان جدا ڪندڙ وائن جو قسم آھي.');
INSERT INTO "amroti" VALUES(77,5,'77:5','پوءِ وحي آڻيندڙن ملائڪن جو قسم آھي.');
INSERT INTO "amroti" VALUES(77,6,'77:6','(جي) عذر وڃائڻ لاءِ يا ڊيڄارڻ لاءِ (وحي) آڻيندا آھن.');
INSERT INTO "amroti" VALUES(77,7,'77:7','ته بيشڪ جنھن (ڳالھ) جو اوھان کي انجام ڏجي ٿو سا ضرور ٿيڻي آھي.');
INSERT INTO "amroti" VALUES(77,8,'77:8','پوءِ جڏھن تارا ميٽي ڇڏبا.');
INSERT INTO "amroti" VALUES(77,9,'77:9','۽ جڏھن آسمان چيربو.');
INSERT INTO "amroti" VALUES(77,10,'77:10','۽ جڏھن جبل اڏائبا.');
INSERT INTO "amroti" VALUES(77,11,'77:11','۽ جڏھن پيغمبر (ٺھرايل وقت تي) گڏ ڪبا.');
INSERT INTO "amroti" VALUES(77,12,'77:12','(تڏھن ثابت ٿيندو ته پيغمبرن کي) ڪھڙي ڏينھن لاءِ ترسايو ويو.');
INSERT INTO "amroti" VALUES(77,13,'77:13','فيصلي جي ڏينھن لاءِ.');
INSERT INTO "amroti" VALUES(77,14,'77:14','۽ توکي ڪھڙي شيءَ ڄاڻايو ته فيصلو جو ڏينھن ڇا آھي.');
INSERT INTO "amroti" VALUES(77,15,'77:15','اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(77,16,'77:16','پھرين کي ھلاڪ نه ڪيو اٿون ڇا؟');
INSERT INTO "amroti" VALUES(77,17,'77:17','وري (اُن کانپوءِ) پوين کي انھن جي پٺيان آڻيندا آھيون.');
INSERT INTO "amroti" VALUES(77,18,'77:18','ائين ڏوھارين سان ڪندا آھيون.');
INSERT INTO "amroti" VALUES(77,19,'77:19','اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(77,20,'77:20','اوھان کي خسيس پاڻي مان نه بڻايو اٿون ڇا؟');
INSERT INTO "amroti" VALUES(77,21,'77:21','پوءِ اُن کي ھڪ پختي ھنڌ ۾ رکيوسون.');
INSERT INTO "amroti" VALUES(77,22,'77:22','ھڪ مُقرّر وقت تائين.');
INSERT INTO "amroti" VALUES(77,23,'77:23','پوءِ (بڻاوت ۾) پورو ڪيوسون جو (اسين) چڱي سگھ وارا آھيون.');
INSERT INTO "amroti" VALUES(77,24,'77:24','اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(77,25,'77:25','زمين کي ماپائڻ واري نه ڪئي اٿون ڇا؟');
INSERT INTO "amroti" VALUES(77,26,'77:26','(جو) جيئرن ۽ مُئن کي (ماپائي).');
INSERT INTO "amroti" VALUES(77,27,'77:27','۽ زمين ۾ اُچا جبل ڪياسون ۽ اوھان کي مٺو پاڻي پياريوسون.');
INSERT INTO "amroti" VALUES(77,28,'77:28','اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(77,29,'77:29','(چيو ويندو ته ھاڻي) جنھن (دوزخ) کي اوھين ڪوڙ ڀائيندا ھوا تنھن ڏانھن وڃو.');
INSERT INTO "amroti" VALUES(77,30,'77:30','(چئبن ته) ٽن شاخن واريءَ ڇانوَ ڏانھن وڃو.');
INSERT INTO "amroti" VALUES(77,31,'77:31','نه ٿڌي ڇانوَ ھوندي ۽ نه (باھ جي) توَ کان توکي بچائيندي.');
INSERT INTO "amroti" VALUES(77,32,'77:32','بيشڪ اُھا (باھ) ماڙيءَ جيڏيون چڻنگون اڇليندي آھي.');
INSERT INTO "amroti" VALUES(77,33,'77:33','ڄڻ ته اُھي ھيڊا اُٺ آھن.');
INSERT INTO "amroti" VALUES(77,34,'77:34','اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(77,35,'77:35','اُھو ڏينھن اُھو آھي جنھن ۾ نه ڳالھائيندا.');
INSERT INTO "amroti" VALUES(77,36,'77:36','۽ نڪي کين موڪل ڏبي ته (ڪو) عذر (بيان) ڪن.');
INSERT INTO "amroti" VALUES(77,37,'77:37','اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(77,38,'77:38','ھيءُ فيصلي جو ڏينھن آھي، اوھان کي ۽ اڳين کي گڏ ڪيوسون.');
INSERT INTO "amroti" VALUES(77,39,'77:39','پوءِ جيڪڏھن اوھان کي بڇڙي رِٿ ڪرڻي آھي ته منھنجي حق ۾ رٿيو.');
INSERT INTO "amroti" VALUES(77,40,'77:40','اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(77,41,'77:41','بيشڪ پرھيزگار ڇانوَن ۽ چشمن ۾ ھوندا.');
INSERT INTO "amroti" VALUES(77,42,'77:42','۽ جنھن جنس مان گھرندا (تنھن جنس جي) ميون ۾ ھوندا.');
INSERT INTO "amroti" VALUES(77,43,'77:43','(چئبن ته) چڱا ڪم ڪندا ھئو تنھنڪري خوش ٿي کائو ۽ پيئو.');
INSERT INTO "amroti" VALUES(77,44,'77:44','بيشڪ اسين نيڪن کي ائين بدلو ڏيندا آھيون.');
INSERT INTO "amroti" VALUES(77,45,'77:45','اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(77,46,'77:46','(اي ڪوڙ ڀائيندڙؤ دنيا ۾) ٿوري دير کائي وٺو ۽ آسودا رھو بيشڪ اوھين ڏوھاري آھيو.');
INSERT INTO "amroti" VALUES(77,47,'77:47','اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(77,48,'77:48','۽ جڏھن کين چئجي ته نماز پڙھو (ته) نماز نه پڙھندا آھن.');
INSERT INTO "amroti" VALUES(77,49,'77:49','اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.');
INSERT INTO "amroti" VALUES(77,50,'77:50','پوءِ اُن (قرآن) کانپوءِ ڪھڙي ڳالھ تي ويساھ رکندا.');
INSERT INTO "amroti" VALUES(78,1,'78:1','ڪھڙيءَ ڳالھ بابت ھڪ ٻئي کان پڇن ٿا.');
INSERT INTO "amroti" VALUES(78,2,'78:2','(ھائو!) ھڪڙي اھڙي وڏي واقعي بابت.');
INSERT INTO "amroti" VALUES(78,3,'78:3','جنھن (جي ٿيڻ) ۾ اُھي (پاڻ ۾) مختلف آھن.');
INSERT INTO "amroti" VALUES(78,4,'78:4','ائين (سندن خيالن وانگر) نه آھي سگھوئي ڄاڻندا.');
INSERT INTO "amroti" VALUES(78,5,'78:5','وري (به چئون ٿا) ته ائين نه آھي سگھوئي ڄاڻندا.');
INSERT INTO "amroti" VALUES(78,6,'78:6','زمين کي وڇاڻو نه ڪيو اٿون ڇا؟');
INSERT INTO "amroti" VALUES(78,7,'78:7','۽ جبلن کي ميخون؟');
INSERT INTO "amroti" VALUES(78,8,'78:8','۽ اوھان کي جوڙو جوڙو (نر ۽ مادي) بڻايوسون.');
INSERT INTO "amroti" VALUES(78,9,'78:9','۽ اوھان جي ننڊ کي فرحت بڻايوسون.');
INSERT INTO "amroti" VALUES(78,10,'78:10','۽ رات کي ڍَڪ ڪيوسون.');
INSERT INTO "amroti" VALUES(78,11,'78:11','۽ ڏينھن کي گذران (جو وقت) ڪيوسون.');
INSERT INTO "amroti" VALUES(78,12,'78:12','۽ اوھان جي مٿون ست مُحڪم (آسمان) بڻاياسون.');
INSERT INTO "amroti" VALUES(78,13,'78:13','۽ ھڪ چمڪندڙ ڏيئو (سج) بڻايوسون.');
INSERT INTO "amroti" VALUES(78,14,'78:14','۽ ڪڪرن مان پلٽندڙ پاڻي وسايوسون.');
INSERT INTO "amroti" VALUES(78,15,'78:15','ته اُن سان اَن ۽ سَلا ڄمايون.');
INSERT INTO "amroti" VALUES(78,16,'78:16','۽ گھاٽاباغ (به).');
INSERT INTO "amroti" VALUES(78,17,'78:17','بيشڪ فيصلي جو ڏينھن ھڪ مقرر مھل آھي.');
INSERT INTO "amroti" VALUES(78,18,'78:18','جنھن ڏينھن صُور ۾ ڦوڪبو ته اوھين ٽوليون ٽوليون ٿي ايندؤ.');
INSERT INTO "amroti" VALUES(78,19,'78:19','۽ آسمان کولبو پوءِ دروازا ٿي پوندو.');
INSERT INTO "amroti" VALUES(78,20,'78:20','۽ جبل ھلائبا پوءِ رُڃ وانگر ٿيندا.');
INSERT INTO "amroti" VALUES(78,21,'78:21','بيشڪ دوزخ انتظار ڪڍڻ وارو آھي.');
INSERT INTO "amroti" VALUES(78,22,'78:22','جو ھٺيلن جو رھڻ جو ھنڌ آھي.');
INSERT INTO "amroti" VALUES(78,23,'78:23','منجھس ڪيئي ڊگھيون مُدتون رھندا.');
INSERT INTO "amroti" VALUES(78,24,'78:24','اُن ۾ نڪا ٿڌائي ۽ نڪا پيڻ جي شيءِ چکندا.');
INSERT INTO "amroti" VALUES(78,25,'78:25','تتي پاڻي ۽ روڳ کانسواءِ.');
INSERT INTO "amroti" VALUES(78,26,'78:26','(اھو سندن) پورو پورو بدلو آھي.');
INSERT INTO "amroti" VALUES(78,27,'78:27','ڇوته اُھي حساب ڏيڻ جي اُميد نه رکندا ھوا.');
INSERT INTO "amroti" VALUES(78,28,'78:28','۽ اسان جي حُڪمن کي بلڪل ڪوڙ ڀائيندا ھوا.');
INSERT INTO "amroti" VALUES(78,29,'78:29','۽ سڀ شيءِ کي لکي ڳڻي ڇڏيو اٿون.');
INSERT INTO "amroti" VALUES(78,30,'78:30','پوءِ (چئبن ته مزو) چکو جو عذاب کانسواءِ اوھان تي (ٻيو) ڪي (به) نه وڌائينداسون.');
INSERT INTO "amroti" VALUES(78,31,'78:31','بيشڪ پرھيزگارن لاءِ ڪاميابي آھي.');
INSERT INTO "amroti" VALUES(78,32,'78:32','جو باغ ۽ ڊاکون.');
INSERT INTO "amroti" VALUES(78,33,'78:33','۽ ھم عمر نوجوان زالون.');
INSERT INTO "amroti" VALUES(78,34,'78:34','۽ شراب جا ڇلڪندڙ پيالا (به).');
INSERT INTO "amroti" VALUES(78,35,'78:35','اتي نڪا اجائي بڪ ۽ نڪو ڪوڙ ٻڌندا.');
INSERT INTO "amroti" VALUES(78,36,'78:36','(اھو پورو) بدلو تنھنجي پالڻھار کان (انعام) عطا ڪيل حساب روءِ آھي.');
INSERT INTO "amroti" VALUES(78,37,'78:37','جو آسمانن ۽ زمين جو ۽ جيڪي ٻنھي جي وچ ۾ آھي تن جو پالڻھار ٻاجھارو آھي اُن (جي ھيبت) کان ڪوبه ورندي ڏئي نه سگھندو.');
INSERT INTO "amroti" VALUES(78,38,'78:38','جنھن ڏينھن روح ۽ (سڀئي) ملائڪ صفون ٻڌي بيھندا (تنھن ڏينھن) جنھن کي ٻاجھاري (الله) موڪل ڏني ھوندي ۽ پوري ڳالھ ڳالھائي ھوندائين تنھن کانسواءِ (ٻيو ڪي به) نه ڳالھائيندا.');
INSERT INTO "amroti" VALUES(78,39,'78:39','اِھو ڏينھن سچ پچ (ٿيڻو) آھي، تنھنڪري جيڪو گھري سو پنھنجي پالڻھار ڏانھن (پنھنجي) رھڻ جو ھنڌ ٺاھي.');
INSERT INTO "amroti" VALUES(78,40,'78:40','بيشڪ اسان اوھان کي ويجھي عذاب (اچڻ) کان ڊيڄاري چڪاسون، جنھن ڏينھن آدمي اُھو ڏسندو جيڪي سندس ٻنھي ھٿن اڳي ڪري موڪليو ۽ ڪافر چوندو ته ھاءِ ارمان جيڪر آءٌ مٽي ھجان ھا.');
INSERT INTO "amroti" VALUES(79,1,'79:1','اندر گھڙي ڇڪي آئيندڙن جو قسم آھي.');
INSERT INTO "amroti" VALUES(79,2,'79:2','۽ کولي آسانيءَ سان ڳنڍ ڇوڙيندڙن جو قسم آھي.');
INSERT INTO "amroti" VALUES(79,3,'79:3','۽ چڱي طرح سان ترندڙن جو قسم آھي.');
INSERT INTO "amroti" VALUES(79,4,'79:4','پوءِ ڊوڙي اڳرائي ڪندڙن جو قسم آھي.');
INSERT INTO "amroti" VALUES(79,5,'79:5','پوءِ (سڀ) ڪم جي رٿيندڙن ٽولين جو قسم آھي.');
INSERT INTO "amroti" VALUES(79,6,'79:6','ته جنھن ڏينھن ڌٻڻ واري ڌٻندي.');
INSERT INTO "amroti" VALUES(79,7,'79:7','ان کي پٺيان لڳندڙ (ٻيو ڌوڏو) پٺيان لڳندو.');
INSERT INTO "amroti" VALUES(79,8,'79:8','تنھن ڏينھن ڪيتريون ئي دليون ڏڪي وڃڻ واريون ھونديون.');
INSERT INTO "amroti" VALUES(79,9,'79:9','انھن جون اکيون (خواريءَ سبب) جھڪيون ٿينديون.');
INSERT INTO "amroti" VALUES(79,10,'79:10','چون ٿا ته اسين پوئين پيرين موٽائباسون ڇا؟');
INSERT INTO "amroti" VALUES(79,11,'79:11','ھان جڏھن ڀُتا ھڏا ٿينداسون؟');
INSERT INTO "amroti" VALUES(79,12,'79:12','چوندا آھن ته انھي مھل اھو ٻيھر موٽڻ (وڏو) نقصان وارو آھي.');
INSERT INTO "amroti" VALUES(79,13,'79:13','پوءِ اھو (واقعو) ھڪڙي سخت ھڪل آھي.');
INSERT INTO "amroti" VALUES(79,14,'79:14','پوءِ اتي جو اُتي ھڪ پڌري ميدان ۾ حاضر ٿيل ھوندا.');
INSERT INTO "amroti" VALUES(79,15,'79:15','(اي پيغمبر) تو وٽ مُوسىٰ جي خبر پُھتي آھي ڇا؟');
INSERT INTO "amroti" VALUES(79,16,'79:16','جڏھن سندس پالڻھار کيس پاڪ ميدان طُوىٰ ۾ سڏيو.');
INSERT INTO "amroti" VALUES(79,17,'79:17','ته فرعون ڏانھن وڃ ڇوته ھو حد کان لنگھيو آھي.');
INSERT INTO "amroti" VALUES(79,18,'79:18','پوءِ (ان کي) چؤ ته ھن ڏانھن خيال اٿئي ته تون سڌرجين؟');
INSERT INTO "amroti" VALUES(79,19,'79:19','۽ تنھنجي پالڻھار ڏانھن توکي (سڌو) رستو ڏيکاريان ته (تون اُن کان) ڊڄين.');
INSERT INTO "amroti" VALUES(79,20,'79:20','پوءِ فرعون کي وڏي نشاني ڏيکاريائين.');
INSERT INTO "amroti" VALUES(79,21,'79:21','پوءِ (فرعون موسى) کي ڪوڙو ڀانيو ۽ نافرماني ڪيائين.');
INSERT INTO "amroti" VALUES(79,22,'79:22','وري (اُتان) پٺيرو ٿي تکو ھلڻ لڳو.');
INSERT INTO "amroti" VALUES(79,23,'79:23','پوءِ (پنھنجي قوم کي) گڏ ڪيائين پوءِ سڏيائين.');
INSERT INTO "amroti" VALUES(79,24,'79:24','پوءِ چيائين ته آءٌ اوھان جو تمام مٿاھون پالڻھار آھيان.');
INSERT INTO "amroti" VALUES(79,25,'79:25','پوءِ الله ان کي آخرت ۽ دُنيا جي عذاب ۾ پڪڙيو.');
INSERT INTO "amroti" VALUES(79,26,'79:26','بيشڪ ھن (قصّي) ۾ انھي لاءِ نصيحت آھي جيڪو ڊڄي.');
INSERT INTO "amroti" VALUES(79,27,'79:27','ڀلا بڻاوت ۾ اوھين ڏکيا آھيو يا آسمان جنھن کي الله بڻايو؟');
INSERT INTO "amroti" VALUES(79,28,'79:28','اُن جي مٿاھين بلند ڪيائين پوءِ اُن کي برابر بيھاريائين.');
INSERT INTO "amroti" VALUES(79,29,'79:29','۽ اُن جي رات کي اوندھ ڪيائين ۽ اُن جو سوجھرو (ڏينھن جو) پڌرو ڪيائين.');
INSERT INTO "amroti" VALUES(79,30,'79:30','۽ اُن کان پوءِ زمين کي وڇايائين.');
INSERT INTO "amroti" VALUES(79,31,'79:31','اُن مان سندس پاڻي ۽ سندس گاھ ڪڍيائين.');
INSERT INTO "amroti" VALUES(79,32,'79:32','۽ جبلن کي مُحڪم کوڙيائين.');
INSERT INTO "amroti" VALUES(79,33,'79:33','اوھان جي نفعي لاءِ ۽ اوھان جي ڍورن (جي نفعي) لاءِ.');
INSERT INTO "amroti" VALUES(79,34,'79:34','پوءِ جڏھن وڏو واقعو (قيامت جو) ايندو.');
INSERT INTO "amroti" VALUES(79,35,'79:35','اُن ڏينھن ماڻھو اھو ياد ڪندو جيڪي (دنيا ۾) ڪمايو ھوائين.');
INSERT INTO "amroti" VALUES(79,36,'79:36','۽ دوزخ اُنھيءَ لاءِ پڌرو ڪبو جيڪو ڏسڻ گھرندو.');
INSERT INTO "amroti" VALUES(79,37,'79:37','پوءِ جيڪو حد کان لنگھيو ھوندو.');
INSERT INTO "amroti" VALUES(79,38,'79:38','۽ ھن دنيا جي حياتيءَ کي ڀَلو سمجھيائين.');
INSERT INTO "amroti" VALUES(79,39,'79:39','تنھن جي جاءِ بيشڪ دوزخ آھي.');
INSERT INTO "amroti" VALUES(79,40,'79:40','۽ جيڪو پنھنجي پالڻھار جي (حضور ۾) روبرو بيھڻ کان ڊنو ۽ نفس کي سَڌ کان جھليائين.');
INSERT INTO "amroti" VALUES(79,41,'79:41','پوءِ تنھنجي جاءِ بيشڪ بھشت آھي.');
INSERT INTO "amroti" VALUES(79,42,'79:42','(اي پيغمبر) توکان قيامت بابت پُڇن ٿا ته اُن جو (واقعو) ٿيڻ ڪڏھن آھي؟');
INSERT INTO "amroti" VALUES(79,43,'79:43','(اي پڇندڙ) اُن جي خبر بابت تون ڪھڙي خيال ۾ آھين؟');
INSERT INTO "amroti" VALUES(79,44,'79:44','اُن جي پڄاڻي (جي خبر) تنھنجي پالڻھار وٽ آھي.');
INSERT INTO "amroti" VALUES(79,45,'79:45','جيڪو قيامت کان ڊڄي رڳو تنھن لاءِ تون ڊيڄاريندڙ آھين.');
INSERT INTO "amroti" VALUES(79,46,'79:46','جنھن ڏينھن اُن کي ڏسندا (تنھن ڏينھن) ائين ڀانئيندا ته ڄڻڪ ھڪڙي وقت سانجھي يا ان جي صبح کانسواءِ (دنيا ۾) رھيا ئي نه ھوا.');
INSERT INTO "amroti" VALUES(80,1,'80:1','مُنھن ۾ گُھنڊ وڌائين ۽ مُنھن موڙيائين.');
INSERT INTO "amroti" VALUES(80,2,'80:2','ھن ڪري جو وٽس نابين آيو.');
INSERT INTO "amroti" VALUES(80,3,'80:3','۽ ڪنھن سمجھايئي جيڪر اُھو سڌرجي ھا.');
INSERT INTO "amroti" VALUES(80,4,'80:4','يا نصيحت ٻڌي ھا پوءِ اُھا نصيحت ٻڌڻ نفعو ڏئيس ھا.');
INSERT INTO "amroti" VALUES(80,5,'80:5','پر جنھن بي پرواھي ڪئي.');
INSERT INTO "amroti" VALUES(80,6,'80:6','پوءِ تنھن (جي سڌاري) لاءِ تون پٺيان پيو آھين.');
INSERT INTO "amroti" VALUES(80,7,'80:7','ھن ۾ توتي (ڪا ميار) ڪانھي ته اُھو نه سُڌرجي.');
INSERT INTO "amroti" VALUES(80,8,'80:8','۽ جيڪو تو وٽ ڊوڙندو آيو.');
INSERT INTO "amroti" VALUES(80,9,'80:9','۽ اُھو (پنھنجي الله کان) ڊڄي ٿو.');
INSERT INTO "amroti" VALUES(80,10,'80:10','پوءِ تنھن کان تون ويسلو ٿئين ٿو.');
INSERT INTO "amroti" VALUES(80,11,'80:11','ائين نه آھي بيشڪ اھي (قرآن جون آيتون) ھڪ نصيحت آھي.');
INSERT INTO "amroti" VALUES(80,12,'80:12','پوءِ جيڪو گھُري سو قرآن ياد رکي.');
INSERT INTO "amroti" VALUES(80,13,'80:13','(بيشڪ قرآن جون آيتون) تعظيم وارن ورقن ۾ لکيل آھن.');
INSERT INTO "amroti" VALUES(80,14,'80:14','جي مٿاھان ۽ پاڪ آھن.');
INSERT INTO "amroti" VALUES(80,15,'80:15','لکندڙن جي ھٿن ۾.');
INSERT INTO "amroti" VALUES(80,16,'80:16','جي سڳورا ڀلارا آھن.');
INSERT INTO "amroti" VALUES(80,17,'80:17','ماڻھو ماريو وڃي جو ڪھڙو نه بي شُڪر آھي.');
INSERT INTO "amroti" VALUES(80,18,'80:18','(ڌيان ڪري ته الله) ڪھڙي شيءَ مان پيدا ڪيس.');
INSERT INTO "amroti" VALUES(80,19,'80:19','مني جي ڦڙي مان، خلقيائينس پوءِ اندازو مقرر ڪيائينس.');
INSERT INTO "amroti" VALUES(80,20,'80:20','وري (نڪرڻ جي) واٽ سنھنجي ڪيائينس.');
INSERT INTO "amroti" VALUES(80,21,'80:21','وري ماريائينس پوءِ قبر ۾ رکايائينس.');
INSERT INTO "amroti" VALUES(80,22,'80:22','وري جڏھن گھرندو تڏھن اُن کي جيئرو ڪندو.');
INSERT INTO "amroti" VALUES(80,23,'80:23','ائين نه آھي جيڪي فرمايائينس سو پورو نه ڪيائين.');
INSERT INTO "amroti" VALUES(80,24,'80:24','پوءِ جڳائي ته ماڻھو پنھنجي کاڌي ڏانھن نھاري ڏسي.');
INSERT INTO "amroti" VALUES(80,25,'80:25','ته اسان پاڻي کي چڱيءَ طرح آسمان کان پلٽيو.');
INSERT INTO "amroti" VALUES(80,26,'80:26','وري زمين کي پوري طرح چيريوسون.');
INSERT INTO "amroti" VALUES(80,27,'80:27','پوءِ منجھس اَن ڄمايوسون.');
INSERT INTO "amroti" VALUES(80,28,'80:28','۽ ڊاکون ۽ ترڪاريون.');
INSERT INTO "amroti" VALUES(80,29,'80:29','۽ زيتون ۽ کجيون.');
INSERT INTO "amroti" VALUES(80,30,'80:30','۽ گھاٽا باغ.');
INSERT INTO "amroti" VALUES(80,31,'80:31','۽ ميوو ۽ گاھ (به).');
INSERT INTO "amroti" VALUES(80,32,'80:32','اوھان جي نفعي لاءِ ۽ اوھان جي ڍورن لاءِ.');
INSERT INTO "amroti" VALUES(80,33,'80:33','پوءِ جڏھن ڪَن ٻوڙا ڪندڙ (دھشت واري) ھڪل ايندي.');
INSERT INTO "amroti" VALUES(80,34,'80:34','انھي ڏينھن آدمي پنھنجي ڀاءُ کان ڀڄندو.');
INSERT INTO "amroti" VALUES(80,35,'80:35','۽ (پڻ) پنھنجي ماءُ کان ۽ پنھنجي پيءُ کان.');
INSERT INTO "amroti" VALUES(80,36,'80:36','۽ پنھنجي گھر واريءَ کان ۽ پنھنجي پٽن کان ڀڄندو.');
INSERT INTO "amroti" VALUES(80,37,'80:37','انھن مان سڀڪنھن مڙس لاءِ اُن ڏينھن اھڙي حالت ھوندي جو کيس بس ھوندي.');
INSERT INTO "amroti" VALUES(80,38,'80:38','ڪيترائي منھن اُن ڏينھن روشن ھوندا.');
INSERT INTO "amroti" VALUES(80,39,'80:39','کِلندڙ ۽ (تمام) سرھا ھوندا.');
INSERT INTO "amroti" VALUES(80,40,'80:40','۽ ڪيترائي منھن (ھوندا) جن تي اُن ڏينھن رئي پيل ھوندي.');
INSERT INTO "amroti" VALUES(80,41,'80:41','جن کي ڪارنھن ويڙھي ويندي.');
INSERT INTO "amroti" VALUES(80,42,'80:42','اھي ئي مُنڪر بدڪار آھن.');
INSERT INTO "amroti" VALUES(81,1,'81:1','جڏھن سج ويڙھبو.');
INSERT INTO "amroti" VALUES(81,2,'81:2','۽ جڏھن تارا ميرا ٿيندا.');
INSERT INTO "amroti" VALUES(81,3,'81:3','۽ جڏھن جبل ھلايا ويندا.');
INSERT INTO "amroti" VALUES(81,4,'81:4','۽ جڏھن ڍُڪيون ويامڻ تي ڏاچيون بيڪار ڇڏبيون.');
INSERT INTO "amroti" VALUES(81,5,'81:5','۽ جڏھن جھنگلي جانور گڏ ڪبا.');
INSERT INTO "amroti" VALUES(81,6,'81:6','۽ جڏھن درياءَ (باھ وانگر) تپائبا.');
INSERT INTO "amroti" VALUES(81,7,'81:7','۽ جڏھن جيءَ پاڻ ۾ ملائبا.');
INSERT INTO "amroti" VALUES(81,8,'81:8','۽ جڏھن جيئري پوريل نياڻي پڇبي ته.');
INSERT INTO "amroti" VALUES(81,9,'81:9','ڪھڙي ڏوھ ڪري ماري وئي.');
INSERT INTO "amroti" VALUES(81,10,'81:10','۽ جڏھن اعمالناما کولبا.');
INSERT INTO "amroti" VALUES(81,11,'81:11','۽ جڏھن آسمان ڇلبو.');
INSERT INTO "amroti" VALUES(81,12,'81:12','۽ جڏھن دوزخ ٻاربو.');
INSERT INTO "amroti" VALUES(81,13,'81:13','۽ جڏھن بھشت ويجھو ڪبو.');
INSERT INTO "amroti" VALUES(81,14,'81:14','(تڏھن) ھر ھڪ جيءُ جيڪي حاضر آندو سو ڄاڻندو.');
INSERT INTO "amroti" VALUES(81,15,'81:15','پوئتي ھٽندڙ تارن جو قَسم کڻان ٿو.');
INSERT INTO "amroti" VALUES(81,16,'81:16','جي (ڪڏھن) سڌا ھلندڙ آھن (ڪڏھن) لڪي ويندڙ آھن.');
INSERT INTO "amroti" VALUES(81,17,'81:17','۽ قسم آھي رات جو جڏھن پوئتي موٽندي آھي.');
INSERT INTO "amroti" VALUES(81,18,'81:18','۽ قسم آھي صُبح جو جڏھن روشن ٿئي.');
INSERT INTO "amroti" VALUES(81,19,'81:19','ته بيشڪ اھو (قرآن) ھڪ موڪليل سڳوري (مَلَڪ) جو پيغام ڏيڻ آھي.');
INSERT INTO "amroti" VALUES(81,20,'81:20','جو سگھ واري عرش جي مالڪ (الله) وٽ وڏي مرتبي وارو آھي.');
INSERT INTO "amroti" VALUES(81,21,'81:21','جو مڃوتيءَ وارو اُتي (مَلڪوت ۾) امين آھي.');
INSERT INTO "amroti" VALUES(81,22,'81:22','۽ (ھي) اوھان جو سنگتي (محمد صلى الله عليه وسلم ڪو) چريو نه آھي.');
INSERT INTO "amroti" VALUES(81,23,'81:23','۽ بيشڪ (اوھان جي سنگتي) اُن (مَلَڪ) کي آسمان جي پڌري ڪناري تي (چتائي) ڏٺو آھي.');
INSERT INTO "amroti" VALUES(81,24,'81:24','۽ اُھو (اوھان جو سنگتي) ڳجھ (جي ڳالھ ڏسڻ) تي بخل ڪندڙ نه آھي.');
INSERT INTO "amroti" VALUES(81,25,'81:25','۽ قرآن تڙيل شيطان جو ڪلام نه آھي.');
INSERT INTO "amroti" VALUES(81,26,'81:26','پوءِ ڪيڏانھن وڃو ٿا؟');
INSERT INTO "amroti" VALUES(81,27,'81:27','ھيءُ (قرآن) جھان وارن جي نصيحت کانسواءِ (ٻيو) ڪي نه آھي.');
INSERT INTO "amroti" VALUES(81,28,'81:28','اوھان مان جيڪو سڌو ھلڻ گھري تنھن لاءِ آھي.');
INSERT INTO "amroti" VALUES(81,29,'81:29','۽ جھانن جي پالڻھار الله جي گھُر کانسواءِ (ٻيو) ڪي اوھين گھري نه سگھندؤ.');
INSERT INTO "amroti" VALUES(82,1,'82:1','جڏھن آسمان ڦاٽندو.');
INSERT INTO "amroti" VALUES(82,2,'82:2','۽ جڏھن تارا ڇڻي پوندا.');
INSERT INTO "amroti" VALUES(82,3,'82:3','۽ جڏھن سمنڊ تمام جوش سان وھائبا.');
INSERT INTO "amroti" VALUES(82,4,'82:4','۽ جڏھن قبرون پٽبيون.');
INSERT INTO "amroti" VALUES(82,5,'82:5','(تڏھن) سڀڪو شخص اُھو ڄاڻندو جيڪي اڳي موڪليو ھوائين ۽ جيڪي پوئتي ڇڏيو ھوائين.');
INSERT INTO "amroti" VALUES(82,6,'82:6','اي ماڻھو توکي تنھنجي (انھي) سڳوري پالڻھار کان ڪھڙي شيءَ ٺڳيو؟');
INSERT INTO "amroti" VALUES(82,7,'82:7','جنھن توکي پيدا ڪيو پوءِ توکي سنئين لڱين ڪيائين پوءِ توکي برابر ڪيائين.');
INSERT INTO "amroti" VALUES(82,8,'82:8','جھڙي صورت ۾ گھريائين (تھڙيءَ ۾) تنھنجو جوڙ جوڙيائين.');
INSERT INTO "amroti" VALUES(82,9,'82:9','ائين نه آھي بلڪ اوھين عملن جي جزا ڏيڻ کي ڪوڙ ڀائيندا آھيو.');
INSERT INTO "amroti" VALUES(82,10,'82:10','۽ بيشڪ اوھان تي نگھبان (مقرر ٿيل) آھن.');
INSERT INTO "amroti" VALUES(82,11,'82:11','سڳورا لکڻ وارا.');
INSERT INTO "amroti" VALUES(82,12,'82:12','جيڪي اوھين ڪندا آھيو سو ڄاڻندا آھن.');
INSERT INTO "amroti" VALUES(82,13,'82:13','بيشڪ ڀلارا (بھشت جي) نعمت ۾ ھوندا.');
INSERT INTO "amroti" VALUES(82,14,'82:14','۽ بيشڪ گنھگار دوزخ ۾ ھوندا.');
INSERT INTO "amroti" VALUES(82,15,'82:15','عملن جي بدلي جي ڏينھن دوزخ ۾ داخل ٿيندا.');
INSERT INTO "amroti" VALUES(82,16,'82:16','۽ اُن کان اُھي لڪل نه ھوندا.');
INSERT INTO "amroti" VALUES(82,17,'82:17','۽ ڪنھن سمجھايئي ته عملن جي بدلي جو ڏينھن ڇا آھي؟');
INSERT INTO "amroti" VALUES(82,18,'82:18','وري (به چئون ٿا ته) ڪنھن سمجھايئي ته عملن جي بدلي جو ڏينھن ڇا آھي؟');
INSERT INTO "amroti" VALUES(82,19,'82:19','اُھو ھڪ ڏينھن آھي جو ڪو جيءُ ڪنھن جيءَ کي ڪجھ فائدو پھچائي نه سگھندو، ۽ اُن ڏينھن (ساري) حڪومت خاص الله جي ھوندي.');
INSERT INTO "amroti" VALUES(83,1,'83:1','(تور ۽ ماپ ۾) انھن گھٽ ڪري ڏيندڙن لاءِ ويل آھي.');
INSERT INTO "amroti" VALUES(83,2,'83:2','جي جڏھن (پاڻ لاءِ) ماڻھن کان مَئي وٺندا آھن ته بلڪل پورو ڪري وٺندا آھن.');
INSERT INTO "amroti" VALUES(83,3,'83:3','۽ جڏھن اُنھن کي مَئي ڏيندا آھن يا کين توري ڏيندا آھن (ته) گھٽائي ڏيندا آھن.');
INSERT INTO "amroti" VALUES(83,4,'83:4','اھي گمان نه ٿا ڪن ڇا ته کين (ضرور) اُٿاربو.');
INSERT INTO "amroti" VALUES(83,5,'83:5','اُنھيءَ وڏي ڏينھن لاءِ.');
INSERT INTO "amroti" VALUES(83,6,'83:6','جنھن ڏينھن ماڻھو جھانن جي پالڻھار اڳيان ٿي بيھندا.');
INSERT INTO "amroti" VALUES(83,7,'83:7','سچ آھي ته بيشڪ بدڪارن جو اعمالنامو سجين ۾ آھي.');
INSERT INTO "amroti" VALUES(83,8,'83:8','۽ ڪنھن سمجھايئي ته سجين ڇا آھي؟');
INSERT INTO "amroti" VALUES(83,9,'83:9','ھڪ لکيل دفتر آھي.');
INSERT INTO "amroti" VALUES(83,10,'83:10','اُن ڏينھن (انھن) ڪوڙ ڀانئيندڙن لاءِ ويل آھي.');
INSERT INTO "amroti" VALUES(83,11,'83:11','جيڪي عملن جي بدلي جي ڏينھن کي ڪوڙ ڀانئيندا آھن.');
INSERT INTO "amroti" VALUES(83,12,'83:12','۽ اُن کي سڀڪنھن حد کان لنگھندڙ گنھگار کانسواءِ (ٻيو) ڪو ڪوڙ نه ڀانئيندو.');
INSERT INTO "amroti" VALUES(83,13,'83:13','جڏھن اسان جون آيتون کيس پڙھي ٻڌائبيون آھن ته چوندو آھي ته (اھي) اڳين جون آکاڻيون آھن.');
INSERT INTO "amroti" VALUES(83,14,'83:14','ائين بلڪل نه آھي! بلڪ، جيڪي ڪمائيندا ھوا تنھن جي ڪَٽ سندين دلين تي ڄمي ويئي آھي.');
INSERT INTO "amroti" VALUES(83,15,'83:15','ائين نه آھي پنھنجي پالڻھار (جي سامھون اچڻ) کان انھي ڏينھن روڪيل ھوندا.');
INSERT INTO "amroti" VALUES(83,16,'83:16','وري بيشڪ اُھي دوزخ ۾ گھڙڻ وارا ھوندا.');
INSERT INTO "amroti" VALUES(83,17,'83:17','وري چئبن ته ھي اُھو دوزخ آھي جنھن کي اوھين ڪوڙ ڀانئيندا ھُيؤ.');
INSERT INTO "amroti" VALUES(83,18,'83:18','سچ آھي بيشڪ ڀلارن جو اعمالنامو عِلّيِّيۡن ۾ آھي.');
INSERT INTO "amroti" VALUES(83,19,'83:19','۽ ڪنھن سمجھايئي ته عِلِّيّين ڇا آھي؟');
INSERT INTO "amroti" VALUES(83,20,'83:20','ھڪ لکيل دفتر آھي.');
INSERT INTO "amroti" VALUES(83,21,'83:21','اُن وٽ (الله جا) مقرب حاضر رھندا آھن.');
INSERT INTO "amroti" VALUES(83,22,'83:22','بيشڪ ڀلارا (بھشت) جي نعمتن ۾ ھوندا.');
INSERT INTO "amroti" VALUES(83,23,'83:23','تختن تي ويھي نظارو پيا ڪندا.');
INSERT INTO "amroti" VALUES(83,24,'83:24','(سڀني پاسن کان) سندن منھن مان ئي نعمت جي تازگي پيو معلوم ڪندين.');
INSERT INTO "amroti" VALUES(83,25,'83:25','مھر لڳل صاف شراب مان پيو پياربن.');
INSERT INTO "amroti" VALUES(83,26,'83:26','جنھن جي مُھر مشڪ (جي) ھوندي، ۽ شوقينن کي جڳائي ته اُنھيءَ ۾ شوق رکن.');
INSERT INTO "amroti" VALUES(83,27,'83:27','اُن جي ملاوت تَسۡنِيم (جي پاڻيءَ) مان ھوندي.');
INSERT INTO "amroti" VALUES(83,28,'83:28','جو ھڪ چشمو آھي جنھن مان الله جا ويجھا (ٻانھا) پيئندا.');
INSERT INTO "amroti" VALUES(83,29,'83:29','بيشڪ ڏوھاري (دنيا ۾) مؤمنن تي کلندا ھوا.');
INSERT INTO "amroti" VALUES(83,30,'83:30','۽ جڏھن وٽائن لنگھندا ھوا ته مؤمنن تي حقارت ڪري پاڻ ۾ ميڇون ڏيندا ھوا.');
INSERT INTO "amroti" VALUES(83,31,'83:31','۽ جڏھن پنھنجن گھر وارن ڏانھن موٽندا ھوا (ته) ڏاڍيون خوشيون ڪندا موٽندا ھوا.');
INSERT INTO "amroti" VALUES(83,32,'83:32','۽ جڏھن مؤمنن کي ڏسندا ھوا ته چوندا ھوا ته بيشڪ اُھي گمراھ آھن.');
INSERT INTO "amroti" VALUES(83,33,'83:33','۽ (حقيقت ڪري) اُھي مؤمنن تي نگھبان ڪري نه موڪليا ويا ھوا.');
INSERT INTO "amroti" VALUES(83,34,'83:34','تنھنڪري اڄ مؤمن ڪافرن تي کِلن ٿا.');
INSERT INTO "amroti" VALUES(83,35,'83:35','تختن تي (ويھي) نظارو ڪن ٿا.');
INSERT INTO "amroti" VALUES(83,36,'83:36','(۽ چون ٿا) ته ڪافرن کي انھيءَ جو بدلو ڏنو ويو آھي ڇا جيڪي اُھي ڪندا ھوا.');
INSERT INTO "amroti" VALUES(84,1,'84:1','جڏھن آسمان ڦاٽندو.');
INSERT INTO "amroti" VALUES(84,2,'84:2','۽ پنھنجي پالڻھار جو حُڪم ڪن ڏئي ٻڌندو ۽ (ٻڌڻ) حق اٿس.');
INSERT INTO "amroti" VALUES(84,3,'84:3','۽ جڏھن زمين پکيڙبي.');
INSERT INTO "amroti" VALUES(84,4,'84:4','۽ جيڪي اُن ۾ ھوندو سو (سڀ) ڪڍي اُڇليندي ۽ خالي ٿيندي.');
INSERT INTO "amroti" VALUES(84,5,'84:5','۽ پنھنجي پالڻھار جو حُڪم ڪن ڏئي ٻڌندي ۽ (ٻڌڻ) حق اٿس.');
INSERT INTO "amroti" VALUES(84,6,'84:6','اي ماڻھو تون پنھنجي پالڻھار ڏانھن (پھچڻ ۾) تمام گھڻي تڪليف وٺڻ وارو آھين جو اُن کي (وڃي) ملندين.');
INSERT INTO "amroti" VALUES(84,7,'84:7','پوءِ اُھو (ماڻھو) جنھن کي سندس سڄي ھٿ ۾ سندس اعمالنامو ڏبو.');
INSERT INTO "amroti" VALUES(84,8,'84:8','تنھن سان آسانيءَ طرح سگھوئي حساب ڪبو.');
INSERT INTO "amroti" VALUES(84,9,'84:9','۽ پنھنجن گھر وارن ڏانھن خوش ٿي موٽندو.');
INSERT INTO "amroti" VALUES(84,10,'84:10','۽ اُھو ماڻھو جنھن کي سندس اعمالنامو سندس پٺي پويان ڏبو.');
INSERT INTO "amroti" VALUES(84,11,'84:11','سو سگھوئي موت کي سڏيندو.');
INSERT INTO "amroti" VALUES(84,12,'84:12','۽ دوزخ ۾ گھڙندو.');
INSERT INTO "amroti" VALUES(84,13,'84:13','ڇوته اُھو (دنيا ۾) پنھنجن گھر وارن ۾ خوش (حال) ھو.');
INSERT INTO "amroti" VALUES(84,14,'84:14','بيشڪ انھيءَ ڀانيو ھو ته (الله ڏانھن) ڪڏھن نه موٽندو.');
INSERT INTO "amroti" VALUES(84,15,'84:15','ھائو! (موٽندو) ڇوته سندس پالڻھار اُن کي ڏسندڙ آھي.');
INSERT INTO "amroti" VALUES(84,16,'84:16','پوءِ شفق جو قسم کڻان ٿو.');
INSERT INTO "amroti" VALUES(84,17,'84:17','۽ رات جو (قسم) ۽ جنھن کي گڏ ڪيو اٿس.');
INSERT INTO "amroti" VALUES(84,18,'84:18','۽ چنڊ جو (قسم) جڏھن پورو روشن ٿئي.');
INSERT INTO "amroti" VALUES(84,19,'84:19','ته ھڪ حال کان ٻئي حال تي ضرور چڙھندؤ.');
INSERT INTO "amroti" VALUES(84,20,'84:20','پوءِ ھنن (ڪافرن) کي ڇا (ٿيو) آھي جو ايمان نه آڻيندا آھن.');
INSERT INTO "amroti" VALUES(84,21,'84:21','۽ جڏھن وٽن قرآن پڙھبو آھي (ته) سجدو نه ڪندا آھن.');
INSERT INTO "amroti" VALUES(84,22,'84:22','بلڪ (اھي) ڪافر ڪوڙ ڀانئيندا آھن.');
INSERT INTO "amroti" VALUES(84,23,'84:23','۽ جيڪي (پنھنجين دلين ۾) سانڀيندا آھن سو الله چڱي طرح ڄاڻندڙ آھي.');
INSERT INTO "amroti" VALUES(84,24,'84:24','پوءِ کين ڏکوئيندڙ عذاب جي خبر ڏي.');
INSERT INTO "amroti" VALUES(84,25,'84:25','پر جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ اَڻ کُٽ اَجر آھي.');
INSERT INTO "amroti" VALUES(85,1,'85:1','بُرجن واري آسمان جو قسم آھي.');
INSERT INTO "amroti" VALUES(85,2,'85:2','۽ وعدي ڪيل ڏينھن جو قسم آھي.');
INSERT INTO "amroti" VALUES(85,3,'85:3','۽ حاضر ٿيڻ واري ۽ جنھن وٽ حاضر ڪيو وڃي تنھن جو قسم آھي.');
INSERT INTO "amroti" VALUES(85,4,'85:4','ته کاھين وارا ماريا وڃن.');
INSERT INTO "amroti" VALUES(85,5,'85:5','جا باھ گھڻي ٻَل واري ھئي.');
INSERT INTO "amroti" VALUES(85,6,'85:6','جڏھن اُھي اُنھن باھ جي (کاھين) تي ويٺل ھوا.');
INSERT INTO "amroti" VALUES(85,7,'85:7','۽ جيڪي اُنھن مسلمانن سان (ظلم) ٿي ڪيو تنھن کي روبرو ڏسڻ وارا ھوا.');
INSERT INTO "amroti" VALUES(85,8,'85:8','۽ انھن ھن کانسواءِ (ٻيءَ) ڪنھن ڳالھ جو وير نه ٿي ورتو ته انھن اُنھيءَ غالب ساراھيل الله تي ايمان آندو ھو.');
INSERT INTO "amroti" VALUES(85,9,'85:9','جنھن کي آسمانن ۽ زمين جي بادشاھي آھي، ۽ الله سڀ شيءَ جي خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(85,10,'85:10','بيشڪ جن (ماڻھن) مؤمنن ۽ مؤمنياڻين کي ايذايو وري توبه نه ڪيائون تن لاءِ دوزخ جو عذاب آھي ۽ اُنھن لاءِ سڙڻ جو عذاب (به) آھي.');
INSERT INTO "amroti" VALUES(85,11,'85:11','بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ اھڙا باغ آھن جن جي ھيٺان واھيون پيون وھن، اھا وڏي مراد ماڻڻ آھي.');
INSERT INTO "amroti" VALUES(85,12,'85:12','بيشڪ تنھنجي پالڻھار جي پڪڙ بلڪل سخت آھي.');
INSERT INTO "amroti" VALUES(85,13,'85:13','بيشڪ اُھو پھريون دفعو بڻائي ٿو ۽ وري ٻيھر موٽائيندو.');
INSERT INTO "amroti" VALUES(85,14,'85:14','۽ اُھو بخشڻھار پيار رکندڙ آھي.');
INSERT INTO "amroti" VALUES(85,15,'85:15','(جو) عرش جو مالڪ تمام وڏي شان وارو آھي.');
INSERT INTO "amroti" VALUES(85,16,'85:16','جيڪي گھري سو ڪرڻ وارو ئي آھي.');
INSERT INTO "amroti" VALUES(85,17,'85:17','تو وٽ لشڪرن جي خبر (نه) آئي آھي ڇا؟');
INSERT INTO "amroti" VALUES(85,18,'85:18','(يعني) فرعون ۽ ثمود جي.');
INSERT INTO "amroti" VALUES(85,19,'85:19','بلڪ ڪافر ڪوڙ ڀانئڻ ۾ (لڳا پيا) آھن.');
INSERT INTO "amroti" VALUES(85,20,'85:20','۽ الله سندن چؤڦير گھيرو ڪندڙ آھي.');
INSERT INTO "amroti" VALUES(85,21,'85:21','بلڪ ھي سڳورو قرآن آھي.');
INSERT INTO "amroti" VALUES(85,22,'85:22','حفاظت ڪيل تختي ۾ لکيل آھي.');
INSERT INTO "amroti" VALUES(86,1,'86:1','آسمان جو ۽ رات جي وقت ۾ ايندڙ جو قسم آھي.');
INSERT INTO "amroti" VALUES(86,2,'86:2','۽ ڪنھن سمجھايئي ته رات جو ايندڙ ڇا آھي؟');
INSERT INTO "amroti" VALUES(86,3,'86:3','(اُھو) چمڪندڙ تارو آھي.');
INSERT INTO "amroti" VALUES(86,4,'86:4','ته ڪو جيءُ اھڙو ڪونھي جنھن تي ھڪ نگھبان مقرر ٿيل (نه) آھي.');
INSERT INTO "amroti" VALUES(86,5,'86:5','تنھنڪري ماڻھوءَ کي گھرجي ته نھاري ڏسي ته کيس ڇا مان بڻايو ويو آھي.');
INSERT INTO "amroti" VALUES(86,6,'86:6','ٽپي سان نڪرندڙ پاڻي مان بڻايو ويس.');
INSERT INTO "amroti" VALUES(86,7,'86:7','جو پُٺيءَ ۽ اُرھ جي وچ مان نڪرندو آھي.');
INSERT INTO "amroti" VALUES(86,8,'86:8','بيشڪ الله سندس وري موٽائڻ تي وس وارو آھي.');
INSERT INTO "amroti" VALUES(86,9,'86:9','جنھن ڏينھن (ماڻھن جي دلين جا) سڀ ڳُجھ جاچيا ويندا.');
INSERT INTO "amroti" VALUES(86,10,'86:10','پوءِ تنھن ڏينھن ڪانه اُن کي سگھ رھندي ۽ نڪو واھرو (ھوندس).');
INSERT INTO "amroti" VALUES(86,11,'86:11','۽ مينھن واري آسمان جو قسم آھي.');
INSERT INTO "amroti" VALUES(86,12,'86:12','۽ ڦاٽن واريءَ زمين جو (به) قسم آھي.');
INSERT INTO "amroti" VALUES(86,13,'86:13','ته بيشڪ قرآن ھڪ فيصلو ڪندڙ ڳالھ آھي.');
INSERT INTO "amroti" VALUES(86,14,'86:14','۽ ھيُ قرآن ڪا ٺـٺولي (جي ڳالھ) نه آھي.');
INSERT INTO "amroti" VALUES(86,15,'86:15','بيشڪ ڪافر ھڪ (طرح جي) ڳجھي رٿ رٿين ٿا.');
INSERT INTO "amroti" VALUES(86,16,'86:16','۽ (آءٌ به) ھڪ (طرح جي) رٿ رٿيان ٿو.');
INSERT INTO "amroti" VALUES(86,17,'86:17','پوءِ ڪافرن کي ڍر ڏي ۽ ٿوري مُدت کين ڇڏي ڏي.');
INSERT INTO "amroti" VALUES(87,1,'87:1','(اي پيغمبر) اُنھي پنھنجي تمام مٿاھين پالڻھار جي نالي جي پاڪائي بيان ڪر.');
INSERT INTO "amroti" VALUES(87,2,'87:2','جنھن (ساري مخلوق کي) خلقيو پوءِ سنئين لڱين ڪيائين.');
INSERT INTO "amroti" VALUES(87,3,'87:3','۽ جنھن اندازو ڪيو پوءِ رستو ڏيکاريائين.');
INSERT INTO "amroti" VALUES(87,4,'87:4','۽ جنھن تازو گاھ ڄمايو.');
INSERT INTO "amroti" VALUES(87,5,'87:5','پوءِ اُن کي سُڪو سڙيو ڪيائين.');
INSERT INTO "amroti" VALUES(87,6,'87:6','اسين توکي (قرآن) پاڙھينداسون پوءِ نه وساريندين.');
INSERT INTO "amroti" VALUES(87,7,'87:7','مگر جيڪي الله گھريو آھي، بيشڪ اُھو ظاھر کي ڄاڻندو آھي ۽ جيڪي ڳُجھو آھي تنھن کي (به).');
INSERT INTO "amroti" VALUES(87,8,'87:8','۽ آسان رستو توکي سنھنجو ڪري ڏينداسين.');
INSERT INTO "amroti" VALUES(87,9,'87:9','پوءِ جيڪڏھن نصيحت ڏئڻ فائدو ڪري ته نصيحت ڪر.');
INSERT INTO "amroti" VALUES(87,10,'87:10','جيڪو ڊڄندو سو سُتت سمجھ وٺندو.');
INSERT INTO "amroti" VALUES(87,11,'87:11','۽ اُن (نصيحت وٺڻ) کان (اُھو) تمام نڀاڳو پاسو ڪندو.');
INSERT INTO "amroti" VALUES(87,12,'87:12','جو وڏيءَ باھ ۾ پوندو.');
INSERT INTO "amroti" VALUES(87,13,'87:13','وري نڪي اُتي مرندو ۽ نڪي جيئرو رھندو.');
INSERT INTO "amroti" VALUES(87,14,'87:14','بيشڪ اُھو ڇٽو جيڪو سڌريو.');
INSERT INTO "amroti" VALUES(87,15,'87:15','۽ پڻ پنھنجي پالڻھار جو نالو ياد ڪري نماز پڙھندو رھيو.');
INSERT INTO "amroti" VALUES(87,16,'87:16','بلڪ دُنيا جي حياتيءَ کي (آخرت کان) پسند ڪندا آھيو.');
INSERT INTO "amroti" VALUES(87,17,'87:17','۽ (حقيقت ڪري) آخرت ڀَلي آھي ۽ ھميشه رھڻ واري آھي.');
INSERT INTO "amroti" VALUES(87,18,'87:18','بيشڪ ھيءَ (بيان) پھرين صحيفن ۾ (به لکيل) آھي.');
INSERT INTO "amroti" VALUES(87,19,'87:19','جي صحيفا ابراھيم ۽ مُوسىٰ جا آھن.');
INSERT INTO "amroti" VALUES(88,1,'88:1','(اي پيغمبر) تو وٽ ڍڪيندڙ (قيامت) جي خبر پھتي آھي ڇا؟');
INSERT INTO "amroti" VALUES(88,2,'88:2','اُن ڏينھن ڪيترائي مُنھن خوار ھوندا.');
INSERT INTO "amroti" VALUES(88,3,'88:3','ڪمائيندڙ ڏک ڀوڳيندا ھوندا.');
INSERT INTO "amroti" VALUES(88,4,'88:4','ٻرندڙ باھ ۾ پوندا.');
INSERT INTO "amroti" VALUES(88,5,'88:5','بلڪل تتي چشمي مان پاڻي پياربن.');
INSERT INTO "amroti" VALUES(88,6,'88:6','ڪنڊن واري گاھ کانسواءِ (ٻيو) ڪو کاڄ انھن لاءِ نه ھوندو.');
INSERT INTO "amroti" VALUES(88,7,'88:7','(جو اُھو) نڪي (کين) ڀُلھو ڪندو ۽ نڪي بُک لاھيندو.');
INSERT INTO "amroti" VALUES(88,8,'88:8','ڪيئي مُنھن اُن ڏينھن تازا ھوندا.');
INSERT INTO "amroti" VALUES(88,9,'88:9','پنھنجي ڪمائيءَ کان راضي ھوندا.');
INSERT INTO "amroti" VALUES(88,10,'88:10','مٿاھين بھشت ۾ ھوندا.');
INSERT INTO "amroti" VALUES(88,11,'88:11','اتي ڪا اجائي ڳالھ نه ٻڌندين.');
INSERT INTO "amroti" VALUES(88,12,'88:12','اُتي چشما وھندڙ آھن.');
INSERT INTO "amroti" VALUES(88,13,'88:13','اُتي وڏا تخت مٿي وڇايل.');
INSERT INTO "amroti" VALUES(88,14,'88:14','۽ آبخورا رکيل.');
INSERT INTO "amroti" VALUES(88,15,'88:15','۽ وھاڻا قطار ڪري رکيل.');
INSERT INTO "amroti" VALUES(88,16,'88:16','۽ غاليچا وڇايل آھن.');
INSERT INTO "amroti" VALUES(88,17,'88:17','(ماڻھو) اُٺن ڏانھن نه ڏسندا آھن ڇا؟ ته ڪيئن خلقيا ويا.');
INSERT INTO "amroti" VALUES(88,18,'88:18','۽ آسمان ڏانھن ته ڪيئن مٿي ڪيو ويو.');
INSERT INTO "amroti" VALUES(88,19,'88:19','۽ جبلن ڏانھن ته ڪيئن کڙا ڪيا ويا.');
INSERT INTO "amroti" VALUES(88,20,'88:20','۽ زمين ڏانھن ته ڪيئن وڇائي وئي.');
INSERT INTO "amroti" VALUES(88,21,'88:21','پوءِ (اي پيغمبر) تون، رڳو نصيحت ڪرڻ وارو آھين.');
INSERT INTO "amroti" VALUES(88,22,'88:22','تون مٿن ڪو داروغو نه آھين.');
INSERT INTO "amroti" VALUES(88,23,'88:23','پر جنھن مُنھن موڙيو ۽ ڪُفر ڪيو.');
INSERT INTO "amroti" VALUES(88,24,'88:24','تنھن کي الله وڏي عذاب جي سزا ڏيندو.');
INSERT INTO "amroti" VALUES(88,25,'88:25','بيشڪ اسان ڏانھن سندن موٽڻ آھي.');
INSERT INTO "amroti" VALUES(88,26,'88:26','وري بيشڪ سندن حساب وٺڻ اسان جي ذمي آھي.');
INSERT INTO "amroti" VALUES(89,1,'89:1','پرھ جو قسم آھي.');
INSERT INTO "amroti" VALUES(89,2,'89:2','۽ ڏھن راتين جو قسم آھي.');
INSERT INTO "amroti" VALUES(89,3,'89:3','۽ ٻڌيءَ ۽ اِڪئيءَ جو قسم آھي.');
INSERT INTO "amroti" VALUES(89,4,'89:4','۽ رات جو قسم آھي جڏھن ھلي.');
INSERT INTO "amroti" VALUES(89,5,'89:5','ھان ڌيان واري لاءِ ھن (قسمن) ۾ بيشڪ پورو قسم آھي.');
INSERT INTO "amroti" VALUES(89,6,'89:6','نه ڏٺئي ڇا ته (قوم) عاد سان تنھنجي پالڻھار ڪيئن ڪيو.');
INSERT INTO "amroti" VALUES(89,7,'89:7','(جي) ارم وارا وڏن ٿنڀن (جي عمارتن) وارا ھوا.');
INSERT INTO "amroti" VALUES(89,8,'89:8','جن جھڙو شھرن ۾ ڪونه بڻايو ويو آھي.');
INSERT INTO "amroti" VALUES(89,9,'89:9','۽ (قوم) ثمُود سان (ڪيئن ڪيائين) جن ميدان (قرٰا) ۾ پھڻن جون ڇپون گھڙيون ھيون.');
INSERT INTO "amroti" VALUES(89,10,'89:10','۽ ميخن واري فرعون سان (ڪئن ڪيائين).');
INSERT INTO "amroti" VALUES(89,11,'89:11','انھن (مڙني ٽولين) جن شھرن ۾ سرڪشي ڪئي.');
INSERT INTO "amroti" VALUES(89,12,'89:12','پوءِ منجھن گھڻو فساد ڪيائون.');
INSERT INTO "amroti" VALUES(89,13,'89:13','تنھنڪري تنھنجي پالڻھار مٿن عذاب جو چھبڪ وھايو.');
INSERT INTO "amroti" VALUES(89,14,'89:14','بيشڪ تنھنجو پالڻھار گھَٽَ (واري ھنڌ) ۾ (عملن کي ڏسندڙ) آھي.');
INSERT INTO "amroti" VALUES(89,15,'89:15','پوءِ ماڻھو کي جڏھن سندس پالڻھار (ھن طرح) پرکيندو آھي جو اُن کي مانوارو ڪندو آھي ۽ نعمت ڏيندو اٿس، ته چوندو آھي ته منھنجي پالڻھار مون کي نوازيو.');
INSERT INTO "amroti" VALUES(89,16,'89:16','۽ جڏھن کيس (ٻئي طرح ھيئن) پرکيندو آھي جو مٿس روزي تنگ ڪندو آھي ته چوندو آھي ته منھنجي پالڻھار مون کي بي مانو ڪيو.');
INSERT INTO "amroti" VALUES(89,17,'89:17','(ھرگز) ائين نه آھي بلڪ ڇوري ٻار کي تعظيم نه ڏيندا آھيو.');
INSERT INTO "amroti" VALUES(89,18,'89:18','۽ نڪي مسڪينن جي کارائڻ تي ھڪ ٻئي کي رغبت ڏياريندا آھيو.');
INSERT INTO "amroti" VALUES(89,19,'89:19','۽ مُئن جو مال سموروئي ڳھي ويندا آھيو.');
INSERT INTO "amroti" VALUES(89,20,'89:20','۽ گھڻي پيار سان مال کي پيارو رکندا آھيو.');
INSERT INTO "amroti" VALUES(89,21,'89:21','ائين نه ڪجي جڏھن زمين (جي اونچاڻ) ڪٽي ڪٽي سنئين ڪئي ويندي.');
INSERT INTO "amroti" VALUES(89,22,'89:22','۽ تنھنجو پالڻھار ايندو ۽ ملائڪ صِف صِف ٿي (ايندا).');
INSERT INTO "amroti" VALUES(89,23,'89:23','۽ اُن ڏينھن دوزخ کي آڻبو، اُن ڏينھن ماڻھو نصيحت وٺندو ۽ اُنھيءَ مھل (اھا نصيحت وٺڻ) اُن کي ڪٿي فائدي واري آھي.');
INSERT INTO "amroti" VALUES(89,24,'89:24','چوندو ته ھاءِ ارمان جيڪر پنھنجي (ھن ھميشه جي) حياتيءَ لاءَ (ڪجھ) اڳي موڪليان ھا.');
INSERT INTO "amroti" VALUES(89,25,'89:25','پوءِ انھيءَ ڏينھن الله جي عذاب جھڙو ڪوبه ڪونه عذاب ڪندو.');
INSERT INTO "amroti" VALUES(89,26,'89:26','۽ نڪو ڪو الله جي قيد ڪرڻ جھڙو قيد ڪندو.');
INSERT INTO "amroti" VALUES(89,27,'89:27','(مؤمنن کي چئبو ته) اي آرام وٺڻ وارا جيءُ.');
INSERT INTO "amroti" VALUES(89,28,'89:28','تون پنھنجي پالڻھار ڏانھن موٽِ ھل (تون اُن کان) راضي ٿيل آھين (ھو توکان) راضي ٿيل آھي.');
INSERT INTO "amroti" VALUES(89,29,'89:29','تنھنڪري (تون) منھنجي ٻانھن (جي ٽوليءَ) ۾ داخل ٿيءُ.');
INSERT INTO "amroti" VALUES(89,30,'89:30','۽ منھنجي بھشت ۾ گِھڙ.');
INSERT INTO "amroti" VALUES(90,1,'90:1','ھن شھر (مڪي) جو قسم کڻان ٿو.');
INSERT INTO "amroti" VALUES(90,2,'90:2','۽ تون ھن شھر ۾ (ته) رھندڙ آھين.');
INSERT INTO "amroti" VALUES(90,3,'90:3','۽ ڄڻيندڙ جو (قسم کڻان ٿو) ۽ ان جو جيڪو ڄايو.');
INSERT INTO "amroti" VALUES(90,4,'90:4','ته بيشڪ ماڻھو کي ڏک ۾ (پوڻ لاءِ) خلقيوسون.');
INSERT INTO "amroti" VALUES(90,5,'90:5','(ماڻھو) ڀانئيندو آھي ڇا ته مٿس ڪوبه غالب نه ٿيندو؟');
INSERT INTO "amroti" VALUES(90,6,'90:6','چوي ٿو ته مون تمام گھڻو مال کپايو.');
INSERT INTO "amroti" VALUES(90,7,'90:7','ڀانئيندو آھي ڇا ته ڪنھن ڪونه ڏٺو اٿس؟');
INSERT INTO "amroti" VALUES(90,8,'90:8','کيس ٻه اکيون نه ڏنيون اٿون ڇا؟');
INSERT INTO "amroti" VALUES(90,9,'90:9','۽ ڄڀ ۽ ٻه چَپ به؟');
INSERT INTO "amroti" VALUES(90,10,'90:10','۽ ٻه رستا ڏيکارياسونس.');
INSERT INTO "amroti" VALUES(90,11,'90:11','پوءِ ڪوبه سخت لَڪ نه لنگھيو.');
INSERT INTO "amroti" VALUES(90,12,'90:12','۽ ڪنھن سمجھايئي ته سخت لَڪّ ڇا آھي؟');
INSERT INTO "amroti" VALUES(90,13,'90:13','ٻانھو آجو ڪرڻ آھي.');
INSERT INTO "amroti" VALUES(90,14,'90:14','يا بُک جي ڏينھن ۾ کاڄ کارائڻ.');
INSERT INTO "amroti" VALUES(90,15,'90:15','مائٽي واري ڇوري ٻار کي.');
INSERT INTO "amroti" VALUES(90,16,'90:16','يا مٽيءَ ۾ رُلندڙ محتاج کي.');
INSERT INTO "amroti" VALUES(90,17,'90:17','ان کانپوءِ (اُھو) مؤمنن مان ھجي ۽ ھڪ ٻئي کي صبر جي وصيت ڪيائون ۽ (پڻ) ھڪ ٻئي کي (خلق تي) ٻاجھ ڪرڻ جي وصيت ڪيائون.');
INSERT INTO "amroti" VALUES(90,18,'90:18','اُھي نيڪ بخت سڄي پاسي وارا آھن.');
INSERT INTO "amroti" VALUES(90,19,'90:19','۽ جن اسان جي آيتن جو انڪار ڪيو سي بدبخت کٻي وارا آھن.');
INSERT INTO "amroti" VALUES(90,20,'90:20','مٿن دميل باھ چوڌاري ويڙھيل (موڪلبي) آھي.');
INSERT INTO "amroti" VALUES(91,1,'91:1','سج ۽ اُن جي سوجھري جو قسم آھي.');
INSERT INTO "amroti" VALUES(91,2,'91:2','۽ چنڊ جوقسم آھي جڏھن ان جي پوئتان اچي.');
INSERT INTO "amroti" VALUES(91,3,'91:3','۽ ڏينھن جو (قسم آھي) جڏھن سج کي روشن ڪري.');
INSERT INTO "amroti" VALUES(91,4,'91:4','۽ رات جو (قسم آھي) جڏھن سج کي ڍڪي.');
INSERT INTO "amroti" VALUES(91,5,'91:5','۽ آسمان جو ۽ اُن جو (قسم آھي) جنھن اُن کي بڻايو.');
INSERT INTO "amroti" VALUES(91,6,'91:6','۽ زمين جو ۽ اُنجو (قسم آھي) جنھن اُن کي پکيڙيو.');
INSERT INTO "amroti" VALUES(91,7,'91:7','۽ (ماڻھوءَ جي) جيءُ جو ۽ اُنجو (قسم آھي) جنھن اُن کي جوڙي برابر ڪيو.');
INSERT INTO "amroti" VALUES(91,8,'91:8','پوءِ سندس بدڪاري ۽ سندس پرھيزگاريءَ (جي سڃاڻ) اندر ۾ وڌائينس.');
INSERT INTO "amroti" VALUES(91,9,'91:9','جنھن اُن (نفس) کي (بدڪاريءَ کان) پاڪ ڪيو سو بيشڪ ڇٽو.');
INSERT INTO "amroti" VALUES(91,10,'91:10','۽ جنھن اُن کي (گناھن ۾) پوري ڇڏيو سو بيشڪ ٽوٽي وارو ٿيو.');
INSERT INTO "amroti" VALUES(91,11,'91:11','(قوم) ثمود پنھنجي سرڪشيءَ سببان (پيغمبر کي) ڪوڙو ڀانيو.');
INSERT INTO "amroti" VALUES(91,12,'91:12','جڏھن منجھانئن بلڪل نڀاڳو اُٿيو.');
INSERT INTO "amroti" VALUES(91,13,'91:13','پوءِ الله جي پيغمبر کين چيو ته الله جي ڏاچي ۽ اُن جي پاڻي پيڻ کي آڏو نه ٿيو.');
INSERT INTO "amroti" VALUES(91,14,'91:14','پوءِ پيغمبر کي ڪوڙو ڀانيائون پوءِ ڏاچيءَ جو کُچون ڪپيائون، پوءِ سندن گناھن سببان سندن پالڻھار مٿن عذاب پلٽيو پوءِ اُن (سزا) کي (سڀني) تي ھڪ جھڙو ڪري ڇڏيائين.');
INSERT INTO "amroti" VALUES(91,15,'91:15','۽ (الله) اُن (سزا) جي پڇاڙيءَ کان نه ٿو ڊڄي.');
INSERT INTO "amroti" VALUES(92,1,'92:1','رات جو قسم آھي جڏھن ڍَڪي ٿي.');
INSERT INTO "amroti" VALUES(92,2,'92:2','۽ ڏينھن جو (قسم آھي) جڏھن روشن ٿئي ٿو.');
INSERT INTO "amroti" VALUES(92,3,'92:3','۽ اُن جو (قسم آھي) جنھن نر ۽ مادي خلقي.');
INSERT INTO "amroti" VALUES(92,4,'92:4','ته بيشڪ اوھان جي ڪوشش قسمين قسمين آھي.');
INSERT INTO "amroti" VALUES(92,5,'92:5','پوءِ جنھن (الله لڳ) ڏنو ۽ پرھيزگاري ڪئي.');
INSERT INTO "amroti" VALUES(92,6,'92:6','۽ چڱائي کي سچ ڄاتائين.');
INSERT INTO "amroti" VALUES(92,7,'92:7','تنھنکي (نجات جو) آسان رستو سھنجو ڪري ڏينداسين.');
INSERT INTO "amroti" VALUES(92,8,'92:8','۽ جنھن بخل ڪيو ۽ (پاڻ کي) بي پرواھ ڄاتائين.');
INSERT INTO "amroti" VALUES(92,9,'92:9','۽ چڱائي کي ڪوڙ ڀانيائين.');
INSERT INTO "amroti" VALUES(92,10,'92:10','تنھن کي اوکائيءَ (۾ پوڻ) لاءِ (رستو) آسان ڪري ڏينداسين.');
INSERT INTO "amroti" VALUES(92,11,'92:11','۽ جڏھن اونڌو ڪرندو ته سندس مال کانئس (ڪجھ) نه ٽاريندو.');
INSERT INTO "amroti" VALUES(92,12,'92:12','بيشڪ سڌو رستو ڏيکارڻ اسان تي (لازم) آھي.');
INSERT INTO "amroti" VALUES(92,13,'92:13','۽ بيشڪ آخرت ۽ دُنيا (ٻئي) اسان جي وس ۾ آھن.');
INSERT INTO "amroti" VALUES(92,14,'92:14','پوءِ اوھان کي باھ کان ڊيڄاريم جا ڄڀي ڪري ٿي.');
INSERT INTO "amroti" VALUES(92,15,'92:15','(اھڙي) وڏي نڀاڳي کانسواءِ (ٻيو) ڪو اُن ۾ ڪونه گھڙندو.');
INSERT INTO "amroti" VALUES(92,16,'92:16','جنھن (اسلام کي) ڪوڙ ڄاتو ۽ پٺيرو ٿيو.');
INSERT INTO "amroti" VALUES(92,17,'92:17','۽ اُن (باھ) کان انھي وڏي پرھيزگار کي پاسي ڪبو.');
INSERT INTO "amroti" VALUES(92,18,'92:18','جيڪو پنھنجو مال (الله جي واٽ ۾) ڏئي ٿو ته پاڪ ٿئي.');
INSERT INTO "amroti" VALUES(92,19,'92:19','۽ مٿس اھڙي ڪنھن جو ڪو احسان ٿيل نه آھي جو بدلو ڏجي.');
INSERT INTO "amroti" VALUES(92,20,'92:20','پر پنھنجي تمام مٿاھين پالڻھار جي رضامندي طلبڻ لاءِ (ڏئي ٿو).');
INSERT INTO "amroti" VALUES(92,21,'92:21','۽ ضرور جلد خوش ٿيندو.');
INSERT INTO "amroti" VALUES(93,1,'93:1','مٿي آيل ڏينھن جو قسم آھي.');
INSERT INTO "amroti" VALUES(93,2,'93:2','۽ رات جو (قسم) آھي، جڏھن ڍَڪي ٿي.');
INSERT INTO "amroti" VALUES(93,3,'93:3','ته (اي پيغمبر) تنھنجي پالڻھار توکي نه ڇڏيو ۽ نڪي ناراض ٿيو.');
INSERT INTO "amroti" VALUES(93,4,'93:4','۽ بيشڪ آخرت تولاءِ دنيا کان گھڻو چڱي آھي.');
INSERT INTO "amroti" VALUES(93,5,'93:5','۽ تنھنجو پالڻھار سگھوئي توکي (ايترو) ڏيندو جو (تون) خوش ٿيندين.');
INSERT INTO "amroti" VALUES(93,6,'93:6','توکي يتيم نه ڏٺائين ڇا پوءِ (توکي) جاءِ ڏنائين.');
INSERT INTO "amroti" VALUES(93,7,'93:7','۽ توکي ناواقف ڏٺائين پوءِ (شريعت جي) رستي لاتائين.');
INSERT INTO "amroti" VALUES(93,8,'93:8','۽ توکي مسڪين ڏٺائين پوءِ آسودو ڪيائين.');
INSERT INTO "amroti" VALUES(93,9,'93:9','تنھنڪري يتيم تي تون ڏاڍ نه ڪر.');
INSERT INTO "amroti" VALUES(93,10,'93:10','۽ سُوالي کي نه جھڻڪ.');
INSERT INTO "amroti" VALUES(93,11,'93:11','۽ پنھنجي پالڻھار جي نعمت جو بيان ڪندو رھ.');
INSERT INTO "amroti" VALUES(94,1,'94:1','(اي پيغمبر) تنھنجو سِينو تولاءِ ڪشادو نه ڪيو اٿون ڇا؟');
INSERT INTO "amroti" VALUES(94,2,'94:2','۽ تنھنجو (اُھو) بار توکان لاٿوسون.');
INSERT INTO "amroti" VALUES(94,3,'94:3','جنھن تنھنجي پٺي کي ڳرو ڪيو ھو.');
INSERT INTO "amroti" VALUES(94,4,'94:4','۽ تنھنجي ساراھ تولاءِ مٿاھين ڪئي سون.');
INSERT INTO "amroti" VALUES(94,5,'94:5','ڇوته بيشڪ اھنج سان گڏ سھنج آھي.');
INSERT INTO "amroti" VALUES(94,6,'94:6','بيشڪ اھنج سان گڏ سھنج آھي.');
INSERT INTO "amroti" VALUES(94,7,'94:7','تنھنڪري جڏھن واندو ٿئين ته (عبادت لاءِ) محنت ڪر.');
INSERT INTO "amroti" VALUES(94,8,'94:8','۽ خاص پنھنجي پالڻھار ڏانھن دل لڳاءِ.');
INSERT INTO "amroti" VALUES(95,1,'95:1','انجير ۽ زيتون جو قسم آھي.');
INSERT INTO "amroti" VALUES(95,2,'95:2','۽ طُور سينا جو.');
INSERT INTO "amroti" VALUES(95,3,'95:3','۽ ھِن اَمن واري شھر (مڪي) جو.');
INSERT INTO "amroti" VALUES(95,4,'95:4','ته بيشڪ ماڻھوءَ کي تمام سھڻي نموني پيدا ڪيوسون.');
INSERT INTO "amroti" VALUES(95,5,'95:5','وري سڀني ھيٺاھين کان ھيٺ موٽايوسونس.');
INSERT INTO "amroti" VALUES(95,6,'95:6','پر جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ اَڻ کُٽ اجر آھي.');
INSERT INTO "amroti" VALUES(95,7,'95:7','پوءِ (اي ماڻھو) ھن (نصيحت) پڄاڻا ڪھڙي شيءِ توکي عملن جي بدلي ملڻ بابت منڪر بڻائي ٿي؟');
INSERT INTO "amroti" VALUES(95,8,'95:8','حاڪمن جو وڏو حاڪم الله نه آھي ڇا؟');
INSERT INTO "amroti" VALUES(96,1,'96:1','(اي پيغمبر) پنھنجي پالڻھار جي نالي سان پڙھ جنھن (سارو جڳ) خلقيو.');
INSERT INTO "amroti" VALUES(96,2,'96:2','ماڻھوءَ کي رت جي دِڳ مان بڻايائين.');
INSERT INTO "amroti" VALUES(96,3,'96:3','پڙھ ۽ تنھنجو پالڻھار ڏاڍو سڳورو آھي.');
INSERT INTO "amroti" VALUES(96,4,'96:4','جنھن (ماڻھوءَ کي علم) قلم سان سيکاريو.');
INSERT INTO "amroti" VALUES(96,5,'96:5','ماڻھوءَ کي اُھو سيکاريائين جو نه ڄاڻندو ھو.');
INSERT INTO "amroti" VALUES(96,6,'96:6','سچ آھي ته بيشڪ ماڻھو سرڪشي ڪندو آھي.');
INSERT INTO "amroti" VALUES(96,7,'96:7','جڏھن اُھو پاڻ کي بي پرواھ ڏسندو آھي.');
INSERT INTO "amroti" VALUES(96,8,'96:8','بيشڪ تنھنجي پالڻھار ڏانھن موٽي وڃڻو آھي.');
INSERT INTO "amroti" VALUES(96,9,'96:9','اُھو ڏٺو اٿئي ڇا جيڪو جھليندو آھي.');
INSERT INTO "amroti" VALUES(96,10,'96:10','(منھنجي) ٻانھي کي جڏھن اھو نماز پڙھندو آھي.');
INSERT INTO "amroti" VALUES(96,11,'96:11','ڏٺو اٿئي ڇا ته جيڪڏھن (اُھو ماڻھو) سڌي رستي تي ھجي ھا.');
INSERT INTO "amroti" VALUES(96,12,'96:12','يا پرھيزگاريءَ جو حڪم ڪري ھا.');
INSERT INTO "amroti" VALUES(96,13,'96:13','ڏٺو اٿئي ڇا ته جيڪڏھن ڪوڙ ڀانيائين ۽ پٺيرو ٿيو.');
INSERT INTO "amroti" VALUES(96,14,'96:14','ته (ھيُ) نه ڄاتائين ڇا ته الله ڏسي ٿو.');
INSERT INTO "amroti" VALUES(96,15,'96:15','ائين نه آھي! قسم آھي ته جيڪڏھن پاڻ نه پليندو، ته ضرور پيشانيءَ جي وارن کان وٺي گھلينداسونس.');
INSERT INTO "amroti" VALUES(96,16,'96:16','اُھا پيشاني جا ڪوڙي گنھگار آھي.');
INSERT INTO "amroti" VALUES(96,17,'96:17','پوءِ ڀلي ته پنھنجي ڪچھريءَ وارن کي سڏي.');
INSERT INTO "amroti" VALUES(96,18,'96:18','اسين به دوزخ جي داروغن کي سڏينداسون.');
INSERT INTO "amroti" VALUES(96,19,'96:19','ائين نه آھي، تون چيو نه مڃيس ۽ سجدو ڪر ۽ (الله کي) ويجھو ٿيءُ.');
INSERT INTO "amroti" VALUES(97,1,'97:1','بيشڪ اسان قرآن شب قدر ۾ لاٿو.');
INSERT INTO "amroti" VALUES(97,2,'97:2','۽ ڪنھن سمجھايئي ته شب قدر ڇا آھي.');
INSERT INTO "amroti" VALUES(97,3,'97:3','شب قدر، ھزار مھينن کان ڀلي آھي.');
INSERT INTO "amroti" VALUES(97,4,'97:4','منجھس ملائڪ ۽ رُوح پنھنجي پالڻھار جي حڪم سان سڀڪنھن ڪم (جي پورائي) لاءِ لھندا آھن.');
INSERT INTO "amroti" VALUES(97,5,'97:5','اُنھيءَ رات پرھ ڦُٽڻ تائين سلامتي آھي.');
INSERT INTO "amroti" VALUES(98,1,'98:1','ڪتاب وارن ۽ مشرڪن مان جن انڪار ڪيو سي (ايسين تائين پنھنجي رسم کان) الڳ ٿيڻ وارا نه ھوا جيسين تائين وٽن پڌري حُجّت (نه) اچي.');
INSERT INTO "amroti" VALUES(98,2,'98:2','(يعني) الله جو پيغمبر جو پاڪ صحيفا پڙھي.');
INSERT INTO "amroti" VALUES(98,3,'98:3','جن ۾ سڌا سنوان لکيل حُڪم آھن.');
INSERT INTO "amroti" VALUES(98,4,'98:4','۽ جن کي ڪتاب ڏنو ويو سي پاڻ وٽ پڌري حُجّت جي اچڻ کانپوءِ مُختلف ٿيا.');
INSERT INTO "amroti" VALUES(98,5,'98:5','۽ ھن کانسواءِ (ٻيو) ڪو حڪم نه ڪيو وين ته باطل کان مُنھن موڙي الله جي عبادت ڪن ۽ نماز پڙھندا رھن ۽ زڪوٰة ڏيندا رھن ۽ اھو آھي سڌو دين.');
INSERT INTO "amroti" VALUES(98,6,'98:6','بيشڪ ڪتاب وارن ۽ مشرڪن مان جن ڪفر ڪيو سي دوزخ جي باھ ۾ پوندا منجھس سدائين رھندڙ ھوندا، اھي ئي (ساريءَ) خلق کان (تمام) بڇڙا آھن.');
INSERT INTO "amroti" VALUES(98,7,'98:7','بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا اھي ئي (ساريءَ) خَلق کان ڀَلا آھن.');
INSERT INTO "amroti" VALUES(98,8,'98:8','سندن بدلو سندن پالڻھار وٽ ھميشه جي رھڻ وارا باغ آھن جن جي ھيٺان واھيون پيون وھن انھن ۾ ھميشه سدائين رھندا، الله کائن راضي ٿيو ۽ اُھي کانئس راضي ٿيا، اھو (وعدو) انھيءَ لاءِ جيڪو پنھنجي پالڻھار کان ڊنو.');
INSERT INTO "amroti" VALUES(99,1,'99:1','جڏھن زمين پنھنجي سخت ڌوڏجڻ سان ڌوڏبي.');
INSERT INTO "amroti" VALUES(99,2,'99:2','۽ زمين پنھنجا بار ٻاھر ڪڍندي.');
INSERT INTO "amroti" VALUES(99,3,'99:3','۽ ماڻھو چوندو ته زمين کي ڇا ٿيو آھي.');
INSERT INTO "amroti" VALUES(99,4,'99:4','اُن ڏينھن (اُھا) پنھنجيون سڀ خبرون بيان ڪندي.');
INSERT INTO "amroti" VALUES(99,5,'99:5','ھن ڪري جو تنھنجي پالڻھار ڏانھس حُڪم ڪيو.');
INSERT INTO "amroti" VALUES(99,6,'99:6','اُن ڏينھن ماڻھو ڌار ڌار ٿي ورندا، ته سندس ڪم کين ڏيکارجن.');
INSERT INTO "amroti" VALUES(99,7,'99:7','پوءِ جنھن ذري جيترو چڱو ڪم ڪيو ھوندو سو اُن کي ڏسندو.');
INSERT INTO "amroti" VALUES(99,8,'99:8','۽ جنھن ذري جيترو بد ڪم ڪيو ھوندو سو اُن کي ڏسندو.');
INSERT INTO "amroti" VALUES(100,1,'100:1','ھانبارو ھڻي ڊوڙندڙن گھوڙن جو قَسم آھي.');
INSERT INTO "amroti" VALUES(100,2,'100:2','پوءِ پھڻن تي نعلن ھڻڻ سان چڻنگن ڪڍندڙن (گھوڙن) جوقسم آھي.');
INSERT INTO "amroti" VALUES(100,3,'100:3','پوءِ صبح جي مھل لُٽ ڪندڙ (گھوڙن) جو قسم آھي');
INSERT INTO "amroti" VALUES(100,4,'100:4','جو اُن مھل رئي اُٿارين.');
INSERT INTO "amroti" VALUES(100,5,'100:5','پوءِ اُن گھڙي (دشمنن جي) ٽوليءَ جي وچ ۾ پون.');
INSERT INTO "amroti" VALUES(100,6,'100:6','ته بيشڪ ماڻھو پنھنجي پالڻھار جو وڏو بي شُڪر آھي.');
INSERT INTO "amroti" VALUES(100,7,'100:7','۽ بيشڪ اُھو پاڻ انھن تي شاھد آھي.');
INSERT INTO "amroti" VALUES(100,8,'100:8','۽ بيشڪ اھو مال جي محبت ۾ ڏاڍو آھي.');
INSERT INTO "amroti" VALUES(100,9,'100:9','(اُنھيءَ وقت کي) نه ڄاڻندو آھي ڇا جنھن وقت انھن کي اُٿاربو جيڪي قبرن ۾ نه آھن.');
INSERT INTO "amroti" VALUES(100,10,'100:10','۽ جيڪي سينن ۾ آھي سو پڌرو ڪبو.');
INSERT INTO "amroti" VALUES(100,11,'100:11','بيشڪ اُن ڏينھن سندن پالڻھار سندن (حال جي) خبر رکندڙ آھي.');
INSERT INTO "amroti" VALUES(101,1,'101:1','سخت ٺوڪيندڙ.');
INSERT INTO "amroti" VALUES(101,2,'101:2','(اُھا) سخت ٺوڪيندڙ ڇا آھي؟');
INSERT INTO "amroti" VALUES(101,3,'101:3','۽ ڪنھن سمجھايئي ته (اُھا) سخت ٺوڪيندڙ ڇا آھي؟');
INSERT INTO "amroti" VALUES(101,4,'101:4','جنھن ڏينھن ماڻھو پکيڙيلن پتنگن جان ھوندا.');
INSERT INTO "amroti" VALUES(101,5,'101:5','۽ جبل تنبيل رڱيل اُن جان ھوندا.');
INSERT INTO "amroti" VALUES(101,6,'101:6','پوءِ جنھن (جي عملن) جا ترارا ڳرا ٿيا.');
INSERT INTO "amroti" VALUES(101,7,'101:7','سو دلپسند گذران ۾ ھوندو.');
INSERT INTO "amroti" VALUES(101,8,'101:8','۽ جنھن (جي عملن) جا ترارا ھلڪا ٿيا.');
INSERT INTO "amroti" VALUES(101,9,'101:9','تنھن (جي رھڻ) جي جاءِ ھاوِيَه آھي.');
INSERT INTO "amroti" VALUES(101,10,'101:10','۽ ڪنھن سمجھايئي ته ھاويه ڇا آھي؟');
INSERT INTO "amroti" VALUES(101,11,'101:11','ھڪ ٻرندڙ باھ آھي.');
INSERT INTO "amroti" VALUES(102,1,'102:1','(اي ماڻھو) پاڻ ۾ گھڻائي (تي مرڪڻ) اوھان کي ويسلو ڪيو.');
INSERT INTO "amroti" VALUES(102,2,'102:2','تان جو قبرن کي ڏٺوَ.');
INSERT INTO "amroti" VALUES(102,3,'102:3','ائين نه آھي سگھوئي ڄاڻندؤ.');
INSERT INTO "amroti" VALUES(102,4,'102:4','وري (چئون ٿا ته) سگھوئي ڄاڻندؤ.');
INSERT INTO "amroti" VALUES(102,5,'102:5','نه نه! جيڪڏھن پڪي ويساھ سان ڄاڻو ھا (ته ويسلو نه ويھو ھا).');
INSERT INTO "amroti" VALUES(102,6,'102:6','قسم آھي ته ضرور دوزخ کي ڏسندؤ.');
INSERT INTO "amroti" VALUES(102,7,'102:7','وري به ان کي يقين واريءَ اک سان ضرور ڏسندؤ.');
INSERT INTO "amroti" VALUES(102,8,'102:8','وري اُن ڏينھن نعمتن بابت اوھان کان ضرور پڇبو.');
INSERT INTO "amroti" VALUES(103,1,'103:1','پوياڙيءَ جو قسم آھي.');
INSERT INTO "amroti" VALUES(103,2,'103:2','ته بيشڪ ماڻھو گھاٽي ۾ آھي.');
INSERT INTO "amroti" VALUES(103,3,'103:3','سواءِ انھن جي جن ايمان آندو ۽ چڱا ڪم ڪيا ۽ ھڪ ٻئي کي سچ جي وصيّت ڪيائون، ۽ ھڪ ٻئي کي صبر جي وصّيت ڪيائون.');
INSERT INTO "amroti" VALUES(104,1,'104:1','(انھيءَ) سڀڪنھن گلا ڪندڙ عيب ڳوليندڙ لاءِ ويل آھي.');
INSERT INTO "amroti" VALUES(104,2,'104:2','جنھن مال گڏ ڪيو ۽ اُھو ڳڻي ڳڻي رکيائين.');
INSERT INTO "amroti" VALUES(104,3,'104:3','ڀانئيندو آھي ته سندس مال سدائين رھندو.');
INSERT INTO "amroti" VALUES(104,4,'104:4','ھرگز ائين نه آھي ضرور حُطمه ۾ اُڇلائبس.');
INSERT INTO "amroti" VALUES(104,5,'104:5','۽ ڪنھن سمجھايئي ته حُطَمَه ڇا آھي؟');
INSERT INTO "amroti" VALUES(104,6,'104:6','الله جي ٻاريل باھ آھي.');
INSERT INTO "amroti" VALUES(104,7,'104:7','جا دلين تي پھچندي.');
INSERT INTO "amroti" VALUES(104,8,'104:8','بيشڪ اُھا مٿن در بند ڪيل آھي.');
INSERT INTO "amroti" VALUES(104,9,'104:9','(جنھن حالت ۾ اُھي) ڊگھن ٿنڀن ۾ (ٻڌل ھوندا).');
INSERT INTO "amroti" VALUES(105,1,'105:1','(اي پيغمبر) نه ڏٺئي ڇا؟ ته تنھنجي پالڻھار ھاٿي وارن سان ڪئن ڪيو؟');
INSERT INTO "amroti" VALUES(105,2,'105:2','سندن بڇڙي رٿ کي ناس نه ڪيائين ڇا؟');
INSERT INTO "amroti" VALUES(105,3,'105:3','۽ وَلر وَلر پکي مٿن موڪليائين.');
INSERT INTO "amroti" VALUES(105,4,'105:4','جن ڪنڪريٽ جون پھڻيون کين ھنيون ٿي.');
INSERT INTO "amroti" VALUES(105,5,'105:5','پوءِ اولڙ گاھ جان کين ڪيائين.');
INSERT INTO "amroti" VALUES(106,1,'106:1','قريشين کي اُلفت ڏيارڻ لاءِ.');
INSERT INTO "amroti" VALUES(106,2,'106:2','جو سياري ۽ اونھاري جي سفر سببان کين اُلفت ڏيارڻ آھي.');
INSERT INTO "amroti" VALUES(106,3,'106:3','تنھنڪري گُھرجين ته ھِن گھر جي (انھيءَ) پالڻھار جي عبادت ڪن.');
INSERT INTO "amroti" VALUES(106,4,'106:4','جنھن کين بُک ۾ کاڄ کاريايو، ۽ کين ڀؤ کان امن ڏنو.');
INSERT INTO "amroti" VALUES(107,1,'107:1','(اي پيغمبر) انھي کي ڏٺيءِ ڇا جيڪو عملن جي بدلي ملڻ کي ڪوڙ ڀانئيندو آھي.');
INSERT INTO "amroti" VALUES(107,2,'107:2','پوءِ اُھو آھي جو ڇوري ٻار کي ڌِڪيندو آھي.');
INSERT INTO "amroti" VALUES(107,3,'107:3','۽ (ڪنھن کي) مسڪين جي کارائڻ تي رغبت نه ڏياريندو آھي.');
INSERT INTO "amroti" VALUES(107,4,'107:4','پوءِ انھن نمازين لاءِ وَيل آھي.');
INSERT INTO "amroti" VALUES(107,5,'107:5','جي پنھنجين نمازن کان ويسلا رھندا آھن.');
INSERT INTO "amroti" VALUES(107,6,'107:6','اُھي جيڪي رياءُ ڪندا آھن.');
INSERT INTO "amroti" VALUES(107,7,'107:7','۽ اُڌاريون شيون جھليندا آھن.');
INSERT INTO "amroti" VALUES(108,1,'108:1','(اي پيغمبر) بيشڪ اسان توکي ڪوثر عطا ڪيو.');
INSERT INTO "amroti" VALUES(108,2,'108:2','تنھنڪري پنھنجي پالڻھار جي لاءِ نماز پڙھ ۽ قرباني ڪر.');
INSERT INTO "amroti" VALUES(108,3,'108:3','بيشڪ تنھنجو ويري ئي بي نسل آھي.');
INSERT INTO "amroti" VALUES(109,1,'109:1','(اي پيغمبر ڪافرن کي) چؤ ته اي ڪافرؤ.');
INSERT INTO "amroti" VALUES(109,2,'109:2','نڪي آءٌ ان جي عبادت ڪندس جنھنجي اوھين عبادت ڪندا آھيو.');
INSERT INTO "amroti" VALUES(109,3,'109:3','۽ نڪي اوھين ان جي عبادت ڪندؤ جنھن جي آءٌ عبادت ڪندو آھيان.');
INSERT INTO "amroti" VALUES(109,4,'109:4','۽ نڪي آءٌ ان جي عبادت ڪندس جنھنجي اوھان عبادت ڪئي.');
INSERT INTO "amroti" VALUES(109,5,'109:5','۽ نڪي اوھين ان جي عبادت ڪندؤ جنھنجي آءٌ عبادت ڪندو آھيان.');
INSERT INTO "amroti" VALUES(109,6,'109:6','اوھان لاءِ اوھان جو دين ۽ مون لاءِ منھنجو دين آھي.');
INSERT INTO "amroti" VALUES(110,1,'110:1','جڏھن الله جي مدد ۽ سوڀ اچي،');
INSERT INTO "amroti" VALUES(110,2,'110:2','۽ ماڻھن کي الله جي دين ۾ ٽوليون ٽوليون ٿي گھڙندو ڏسين،');
INSERT INTO "amroti" VALUES(110,3,'110:3','تڏھن (تون) پنھنجي پالڻھار جي ساراھ سان گڏ پاڪائي بيان ڪر ۽ کانئس بخشش گھر. بيشڪ اُھو ٻاجھ سان موٽندو آھي.');
INSERT INTO "amroti" VALUES(111,1,'111:1','ابولھب جا ھٿ ڀڳا ۽ (پاڻ به) ھلاڪ ٿيو.');
INSERT INTO "amroti" VALUES(111,2,'111:2','سندس مال ۽ جيڪي ڪمايائين تنھن کانئس (الله جو عذاب) نه ٽاريو.');
INSERT INTO "amroti" VALUES(111,3,'111:3','ڇيئي واري باھ ۾ سگھو پوندو.');
INSERT INTO "amroti" VALUES(111,4,'111:4','۽ سندس زال به، جا ڪاٺين (جي) لَڏ کڻندڙ آھي.');
INSERT INTO "amroti" VALUES(111,5,'111:5','جنھن جي ڳچيءَ ۾ کاٻر جو نوڙ پيل آھي.');
INSERT INTO "amroti" VALUES(112,1,'112:1','(اي پيغمبر) چؤ ته الله اڪيلو آھي.');
INSERT INTO "amroti" VALUES(112,2,'112:2','الله بي احتياج آھي.');
INSERT INTO "amroti" VALUES(112,3,'112:3','نڪي ڄڻيائين، ۽ نڪي ڄڻيو ويو.');
INSERT INTO "amroti" VALUES(112,4,'112:4','۽ سندس برابر ڪوبه ڪونھي.');
INSERT INTO "amroti" VALUES(113,1,'113:1','(اي پيغمبر) چؤ ته صبح جي پالڻھار جي پناھ گھران ٿو.');
INSERT INTO "amroti" VALUES(113,2,'113:2','جيڪي پيدا ڪيو اٿس تنھنجي بڇڙائي کان.');
INSERT INTO "amroti" VALUES(113,3,'113:3','۽ اونداھي ڪندڙ (رات) جي بڇڙائي کان جڏھن اُھا پکڙجي.');
INSERT INTO "amroti" VALUES(113,4,'113:4','۽ ڳنڍين ۾ ڦوڪيندڙين (عورتن) جي بڇڙائي کان.');
INSERT INTO "amroti" VALUES(113,5,'113:5','۽ مَسَھُوء جي بڇڙائيءَ کان جڏھن (اُھو) مَسَھائي ڪري.');
INSERT INTO "amroti" VALUES(114,1,'114:1','(اي پيغمبر) چؤ ته ماڻھن جي پالڻھار جي پناه گھران ٿو.');
INSERT INTO "amroti" VALUES(114,2,'114:2','ماڻھن جي بادشاھ جي.');
INSERT INTO "amroti" VALUES(114,3,'114:3','ماڻھن جي (سچي) معبود جي.');
INSERT INTO "amroti" VALUES(114,4,'114:4','پٺ تي ھٽي ويندڙ (شيطان) جي وَسَوَسن جي بڇڙائي کان.');
INSERT INTO "amroti" VALUES(114,5,'114:5','جيڪي ماڻھن جي سينن ۾ وَسَوَسو وجھي ٿو.');
INSERT INTO "amroti" VALUES(114,6,'114:6','جو جنن (جي جنس) مان ھجي يا ماڻھن (جي جنس) مان.');
