INSERT INTO "apaguna" VALUES(1,1,'1:1','Maqaa Rabbii Akkaan Mararfataa Akkaan Rahmata Godhaa Ta’eetiin');
INSERT INTO "apaguna" VALUES(1,2,'1:2','Faaruun hundi kan Rabbii Gooftaa aalamaati');
INSERT INTO "apaguna" VALUES(1,3,'1:3','Akkaan mararfataa akkaan rahmata godhaa.');
INSERT INTO "apaguna" VALUES(1,4,'1:4','Abbaa (mootii) guyyaa murtii');
INSERT INTO "apaguna" VALUES(1,5,'1:5','Si qofa gabbarra; Si qofa gargaarsifannas');
INSERT INTO "apaguna" VALUES(1,6,'1:6','Daandii sirrii nu qajeelchi.');
INSERT INTO "apaguna" VALUES(1,7,'1:7','Karaa warra isaan irratti tola ooltee; kan isaan irratti hin dallanaminiifi hin jallatinii (nu qajeelchi)');
INSERT INTO "apaguna" VALUES(2,1,'2:1','Alif, Laam, Miim');
INSERT INTO "apaguna" VALUES(2,2,'2:2','Kun kitaaba [Rabbiin biraa ta’uu isaa ilaalchisee] mamiin isa keessa hin jirreedha;warra Rabbiin sodaataniif qajeelfama');
INSERT INTO "apaguna" VALUES(2,3,'2:3','Isaan "Ghaybitti" (wantoota miiraan hin dhaqqabamnetti) amanan, kan salaata sirnaan salaataniifi waan Nuti isaaniif arjoomne irraa kennaniidha');
INSERT INTO "apaguna" VALUES(2,4,'2:4','Ammas isaan warra waan gara keetti buufameefi waan si dura buufametti amananiifi isaan Aakhiraa dhugoomsaniidha.');
INSERT INTO "apaguna" VALUES(2,5,'2:5','Warri sun qajeelfama Rabbii isaanii biraa ta’e irra jiruAmmas warri sun isaantu milkaa’oodha');
INSERT INTO "apaguna" VALUES(2,6,'2:6','Dhugumatti, isaan kafaran isaan akeekkachiiftes isaan akeekkachiisuu dhiiftes [lamaanuu] isaan irratti wal qixa; hin amananu.');
INSERT INTO "apaguna" VALUES(2,7,'2:7','Rabbiin onnee isaaniitiifi dhageettii isaanii irratti cufe; agartuu isaanii irras haguuggiitu jiraIsaaniif adabbii guddaatu jira.');
INSERT INTO "apaguna" VALUES(2,8,'2:8','Namoota irraa nama odoo hin amanin "Rabbiifi Guyyaa Aakhiraatti amanne" jedhutu jira.');
INSERT INTO "apaguna" VALUES(2,9,'2:9','Rabbiifi warra amanan gowwoomsan [isaanitti fakkaata]; hin beekan malee lubbuu isaanii malee homaa hin gowwoomsanu.');
INSERT INTO "apaguna" VALUES(2,10,'2:10','Onnee isaanii keessa dhukkuba [shakkiifi nifaaqaatu] jiraRabbiinis dhukkuba isaaniif dabaleWaan kijibaa turaniif jechas adabbii laalessaa ta’etu isaaniif jira');
INSERT INTO "apaguna" VALUES(2,11,'2:11','Yeroo "Dachii keessatti badii hin raawwatinaa" isaaniin jedhames "nuti warra tolchu qofa" jedhu.');
INSERT INTO "apaguna" VALUES(2,12,'2:12','Dhagayaa! Dhugumatti isaan isaanumatu warra badiitiGaruu [haala gadhee isaanii kana] hin beekanu.');
INSERT INTO "apaguna" VALUES(2,13,'2:13','Yeroo "akka namoonni amananitti amanaa" isaaniin jedhames, "sila nuti akka warri gowwaan amananitti amannaa?" jedhuDhagayaa! dhugumatti isaan isaanumatu gowwootaGaruu isaan hin beekan.');
INSERT INTO "apaguna" VALUES(2,14,'2:14','Yeroo warra amanan qunnaman "nuti amanneerra" jedhuYeroo sheyxaanota (hoggantoota) isaanii waliin kophaa ba’an immoo "dhugumatti nuti isin waliin jirra; nuti isaanitti qishnuma (qoosaa) godhuuf [akkas goone]" jedhu.');
INSERT INTO "apaguna" VALUES(2,15,'2:15','Rabbiin [mindaa qishnaa isaaniitiif] isaanitti qishnaa godha; jallina isaanii keessatti odoo isaan dhama’anuu isaan dhiisa.');
INSERT INTO "apaguna" VALUES(2,16,'2:16','Isaan warra jallina qajeelumaan bitataniidhaDaldalli isaaniis bu’aa dhabeIsaanis warra qajeelan hin turre.');
INSERT INTO "apaguna" VALUES(2,17,'2:17','Fakkeenyi isaanii akka fakkii isa ibidda qabsiifatee, yeroma isheen waan naannoo isaa ibsitu Rabbiin ifaa isaanii adeemsisee dukkana keessatti haala hin argineen isaan dhiiseeti');
INSERT INTO "apaguna" VALUES(2,18,'2:18','[Isaan] duudaa, arraba qabamaafi jaamaadhaKanaafuu [gara karaa qajeelaa] hin deebi’anu.');
INSERT INTO "apaguna" VALUES(2,19,'2:19','Yookiin akka bokkaa cimaa samii irraa ta’ee kan isa keessa dukkanni, kakawwee (mandiisuu)fi calanqeen (hangaasuun) jiru, du’a bakakkaa irraa ta’e sodaaf quba gurra isaanii keessa kaa’atuRabbiinis kaafirootatti marsaadha.');
INSERT INTO "apaguna" VALUES(2,20,'2:20','Calanqichi ija isaanii butuutti dhiyaataDhawaata isaaniif ifuun isa keessa deemuYeroo isaan irratti dukkanaa’e immoo ni dhaabatuRabbiin odoo fedhee dhageettiifi agartuu isaanii ni deemsisa tureDhugumatti, Rabbiin waan hunda irratti danda’aadha');
INSERT INTO "apaguna" VALUES(2,21,'2:21','Yaa namootaa! Akka Rabbiin sodaattaniif jecha gooftaa keessan isa isiniifi warra isiniin duraa uume qofa gabbaraa!');
INSERT INTO "apaguna" VALUES(2,22,'2:22','[Rabbiin] Isa dachii afata, samii immoo ijaarsa (gubbaa) isiniif taasisee, kan samii irraa bishaan buusee ergasii isaan fuduraa irraa soorata isiniif baaseedhaKanaafuu odoo [kan gabbaramuun malu Rabbiin qofa ta’uu] beektanuu Rabbiif shariikota hin godhinaa.');
INSERT INTO "apaguna" VALUES(2,23,'2:23','Yoo waan Nuti gabricha keenya irratti buufne (Qur’aana) irraa mamii keessa taataniif, yoo kan dhugaa dubbattan taataniif, suuraa akka isaa [tokko] fidaatii ragoota (gargaartotaafi deeggartoota) keessan kan Rabbiin ala jiranis waamadhaa.');
INSERT INTO "apaguna" VALUES(2,24,'2:24','Yoo [amma dura] hin dalagin; fuuldurattis hin dalagdanu; kanaafuu ibidda qabsiisaan ishee namaafi dhagaa ta’e kan kaafirootaaf qopheeffamte sodaadhaa.');
INSERT INTO "apaguna" VALUES(2,25,'2:25','Isaan amananii, gaggaarii hojjatan, jannatoonni laggeen jala ishee yaatu isaaniif jira jechuun [isaan] gammachiisiDhawaatuma firii ishee irraa sooramaniin "kun soorata kana dura sooramneedha" jedhuWal fakkaataa ta''ee isaaniif kennameIsaaniif ishee keessa haadholeen manaa qulqulleeffamanis ni jiruIsaanis ishee keessatti hafoodha');
INSERT INTO "apaguna" VALUES(2,26,'2:26','Dhugumatti Rabbiin bookees taatee waan ishee irra guddaa (yookiin xiqqaa) fakkeenya taasisuu hin saalfatuWarri amanan akka inni dhugaa Gooftaa isaanii irraa ta’e ni beekuIsaan kafaran immoo "Rabbiin fakkeenya kanatti maal fedhe?" jedhu[Rabbiin] kanaan (namoota) baay’ee jallisee (namoota) baay’ees qajeelchaFinciltoota malees (kan biraa) ittiin hin jallisu');
INSERT INTO "apaguna" VALUES(2,27,'2:27','[Isaan] warra waadaa Rabbii erga kakuun jabeessanii booda diiganii, waan (firummaa) Rabbiin fufamuu isaatti ajaje kutanii, dachii keessatti badii raawwataniidhaWarri sun hoonga’oon isaanuma.');
INSERT INTO "apaguna" VALUES(2,28,'2:28','Sila odoo Inni haala isin du’aa turtaniin isin jiraachisee ergasii isin ajjeesee ergasii isin jiraachisuun ergasii gara Isaatti kan deebifamtanu taatanii jirtanuu akkamitti Rabbitti kafartu?');
INSERT INTO "apaguna" VALUES(2,29,'2:29','Inni (Rabbiin) Isa waan dachii keessa jiru hunda isiniif uumee ergasii gara samii ol ta’ee samii torba isaan taasiseedhaInni waan hunda akkaan beekaadha.');
INSERT INTO "apaguna" VALUES(2,30,'2:30','Yeroo Gooftaan kee malaa’ikootaan "dhugumatti, Ani (ilmaan namaa) dachii keessatti bakka bu’aa (khaliifaa) taasisuufan jira" jedhes [yaadadhu]Isaanis ni jedhan: "Sila odoo nuti galata keetiin si faarsinuufi si qulqulleessinuu namoota ishee (dachii) keessatti badii dalagan, kan dhiiga (walii) dhangalaasan ishee keessatti taasiftaa?" [Innis] ni jedhe: "Dhugumatti, Ani akkaan beekaa waan isin hin beekneti."');
INSERT INTO "apaguna" VALUES(2,31,'2:31','Aadamiinis maqoota [waan] hundaa isa barsiiseErgasii malaa’ikoota irra isaan fideetiin "yoo kan dhugaa dubbattan taataniif maqoota wantoota kanneenii naaf odeessaa” jedhe');
INSERT INTO "apaguna" VALUES(2,32,'2:32','[Malaa’ikoonnis] ni jedhan: "Ati qulqulloofteNu waan Ati nu barsiifte malee beekumsa hin qabnu; dhugumatti, Ati Simatu akkaan beekaa ogeessa."');
INSERT INTO "apaguna" VALUES(2,33,'2:33','[Rabbiin] ni jedhe: "Yaa Aadam! maqoota isaanii isaanitti himi." Yeroma inni maqoota isaanii isaanitti himu [Rabbiin] ni jedhe: "Sila Ani akkaan beekaa wantoota fagoo kan samiifi dachiiti; ammas Ani akkaan beekaa waan isin ifa taasiftaniifi waan isin dhoksaa turtaniiti’ isiniin hin jennee?"');
INSERT INTO "apaguna" VALUES(2,34,'2:34','Yeroo Nuti malaa’ikootaan "Aadamiif sujuuda[] godhaa" jennes [yaadadhu]Ibliisa malee Isaan sujuuda godhan; [Inni] ni dide; ni boonesKaafiroota irraayis ta’e.');
INSERT INTO "apaguna" VALUES(2,35,'2:35','Ni jennes: "Yaa Aadam! Atiifi haati manaa kee jannata keessa jiraadhaaIshee irraa bal''oo haala taateen iddoo feetanii nyaadhaaMukattii kanatti immoo hin dhiyaatinaaYoos miidhaa hojjattoota irraa taatu."');
INSERT INTO "apaguna" VALUES(2,36,'2:36','Shayxaannis ishee (jannata) irraa isaan mucucaachiseWaan isaan isa keessa turan irraas isaan baaseNutis ni jenne: "gariin keessan gariif diina ta’aa gadi bu’aaIsiniifis dachii keessa iddoo jireenyaatiifi waan itti fayyadamtan kan yeroo murtaa’eetu jira."');
INSERT INTO "apaguna" VALUES(2,37,'2:37','Aadamis Gooftaa isaa irraa jechoota[] fudhatee sana booda [Rabbiin] dhiifama isaaf godheDhugumatti, Inni (Rabbiin) Isatu akkaan tawbaa qeebalaa, akkaan rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,38,'2:38','Nuti ni jenne: "Hundi keessanuu ishee (jannata) irraa gadi bu’aaErgasii yoo Ana irraa qajeelfamni isiniif dhufe, namni qajeelfama kiyya hordofe, sodaan isaan irra hin jiru; isaan hin gaddanus."');
INSERT INTO "apaguna" VALUES(2,39,'2:39','Warri kafaranii keeyyattoota keenyas kijibsiisan, isaan sun warra ibiddaatiIsaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(2,40,'2:40','Yaa Ilmaan Israa’iil! Ni’imaa kiyya kan Ani ittiin isin qanansiise yaadadhaaWaadaa kiyyas[] guutaa; Anis waadaa keessan nan guutaAna qofa sodaadhaas!');
INSERT INTO "apaguna" VALUES(2,41,'2:41','Waan Ani buuse kan mirkaneessaa waan isin bira jiruu (Tawraatiifi Injiil) ta’ettis amanaaJalqaba warra isatti kafaruu hin ta’inaaKeeyyattoota kiyyaanis gatii xiqqaa hin bitatinaaAna qofa sodaadhaa.');
INSERT INTO "apaguna" VALUES(2,42,'2:42','Odoo beektanii dhugaa sobaan [walitti] hin makinaa; haqa hin dhoksinaas.');
INSERT INTO "apaguna" VALUES(2,43,'2:43','Salaata seeraan salaataa; zakaas kennaa; warra rukuu’a godhan wajjinis rukuu’a godhaa.');
INSERT INTO "apaguna" VALUES(2,44,'2:44','Sila odoo kitaaba dubbiftanuu gaariitti nama ajajaa lubbuu keessan dagattuu! Sila hin xiinxaltanuu!');
INSERT INTO "apaguna" VALUES(2,45,'2:45','Obsaafi salaataan gargaarsa barbaadaaDhugumatti, isheen warra [Rabbiin] sodaatan irratti malee ulfaattuudha.');
INSERT INTO "apaguna" VALUES(2,46,'2:46','[Isaan] warra Gooftaa isaanii wajjiin wal qunnamuufi gara Isaatti deebi’uu mirkaneeffataniidha.');
INSERT INTO "apaguna" VALUES(2,47,'2:47','Yaa Ilmaan Israa’iil! Ni’imaa kiyya kan Ani isinii ooleefi kan Ani aalama hunda irra isin caalchise [yaadadhaa].');
INSERT INTO "apaguna" VALUES(2,48,'2:48','Guyyaa lubbuun [tokko] lubbuu (biraa) homaa hin fayyadne, kan jaarsummaan (araarsi) ishee irraa hin fudhatamneefi gumaanis ishee irraa hin fuudhamne, kan isaanis hin tumsamne san sodaadhaa.');
INSERT INTO "apaguna" VALUES(2,49,'2:49','Ammas yeroo Nuti ummata Fir’awn kan hamtuu adabbii isin dhandhamsiisaa turan, ilmaan dhiiraa keessan gorra’aniifi dubartoota keessan dhiisan irraa bilisa isin baafne san [yaadadhaa]Sana keessa balaa guddaa Gooftaa keessan irraa ta’etu jira.');
INSERT INTO "apaguna" VALUES(2,50,'2:50','Yeroo bahra isiniif addaan foonee, ergasii bilisa isin baafnee ummata Fir’awn odoo isin argitanuu bishaaniin dhidhimsine sanis [yaadadhaa].');
INSERT INTO "apaguna" VALUES(2,51,'2:51','Yeroo Muusaaf halkan afurtama beellamnes [yaadadhaa]Ergasii isin bakka inni hin jirretti warra of miidhe taatanii mirgoo (jibicha) [gabbaramaa] taasifattan.');
INSERT INTO "apaguna" VALUES(2,52,'2:52','Ergasii sana booda akka isin galateeffataniif jecha dhiifama isiniif goone.');
INSERT INTO "apaguna" VALUES(2,53,'2:53','|Yeroo Muusaaf kitaaba (Tawraatii)fi addaan fooyaa dhugaafi kijibaa akka isin qajeeltaniif jecha kennines [yaadadhaa].');
INSERT INTO "apaguna" VALUES(2,54,'2:54','Yeroo Muusaan ummata isaatiin "yaa ummata kiyya! isin mirgoo [gabbaramaa] taasifachuun lubbuu keessan miitanii jirtanKanaaf gara [Rabbii] uumaa keessan ta’eetti deebi’aaLubbuu keessanis ajjeesaaSanatu Uumaa keessan biratti isiniif caala" jedhes [yaadadhaa]Ergasii [Rabbiin] dhiifama isinii godhe (tawbaa keessan qeebale)Dhugumatti, Inni akkaan tawbaa qeebalaa akkaan rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,55,'2:55','Yeroo isin "yaa Muusaa! nuti hanga Rabbiin ifatti arginutti siif hin amannu" jettanii odoo isin argitanuu duuti isin qabattes [yaadadhaa].');
INSERT INTO "apaguna" VALUES(2,56,'2:56','Ergasii akka isin galateeffattaniif jecha booda du’a keessaniitii isin kaafne.');
INSERT INTO "apaguna" VALUES(2,57,'2:57','Duumessa isin irratti gaaddiseessineManniifi Salwiis[] isin irratti buufne"Wantoota gaggaarii Nuti isiniif kennine irraa nyaadhaa" (isiniin jenne)Isaanis homaa nu hin miine; garuu lubbuu isaanii kan miidhan turan');
INSERT INTO "apaguna" VALUES(2,58,'2:58','Yeroo Nuti "magaalattii[] kana seenaa; ishee irraas iddoo feetanii [nyaata] bal’aa nyaadhaaBalbalichas sujuudaa seenaa"[Kadhaan keenyas badiin] nurraa harca''uudha" jedhaa, badii keessan isiniif araaramnaWarra toltuu hojjataniif [mindaa] dabaluuf jirra" jennes [yaadadhaa].');
INSERT INTO "apaguna" VALUES(2,59,'2:59','|Warri miidhaa hojjatan [jecha isaaniin jedhame] jecha isa isaaniin hin jedhaminitti jijjiiranWaan isaan fincilaa turaniif jecha warra miidhaa hojjatan irratti adabbii samii irraa ta''e buufne.');
INSERT INTO "apaguna" VALUES(2,60,'2:60','Yeroo Muusaan ummata isaatiif dhugaatii (bishaan) barbaadee achumaan Nutis "ulee keetiin dhagicha rukuti" jennaan burqituun kudha lama isa irraa maddite [yaadadhaa]Namoonni hundi iddoo irraa dhugan beekanii jiru"Soorata Rabbii irraa nyaadhaa, dhugaa; Dachii keessas badii hin babal’isinaa" [jenneen].');
INSERT INTO "apaguna" VALUES(2,61,'2:61','Yeroo "Yaa Muusaa! nuti nyaata tokko irratti hin obsinuu, akka waan dachiin biqilchitu irraa, kudraa ishee irraa, dabaaqula ishee irraas, qamadii ishee irraas, misira ishee irraas, shunkurtii ishee irraas nuuf baasu Gooftaa kee nuuf kadhu" jettanis [yaadadhaa](Muusaanis) ni jedhe: "Sila isin isa gadi aanaa ta’etti isa caalaa ta’e jijjiirrattuu? Gara magaalaa gadi bu’aa; wanti gaafattan isiniif jira." Salphinaafi hiyyummaan isaan irratti haguugamtee, dallansuu Rabbi irraa ta’een deebi’anKun waan isaan keeyyattoota Rabbiitti kafaraa, nabiyyootas haqaan ala ajjeesaa turaniifiKun waan isaan fincilaa, daangaas dabraa turaniif');
INSERT INTO "apaguna" VALUES(2,62,'2:62','Dhugumatti warri amanan, isaan yahuudoman, kiristaanonniifi saabi’oonni,[] [isaan irraa] namni Rabbiifi Guyyaa Aakhiraatti amanee toltuus hojjate, isaaniif mindaan Gooftaa isaanii biratti qophaa’ee jiraSodaanis isaan irra hin jiru; Isaanis hin gaddanu.');
INSERT INTO "apaguna" VALUES(2,63,'2:63','[Yaa ilmaan Israa’iil!] Yeroo Nuti waadaa keessan qabanneefi [gaara] Xuuris isin gubbaatti ol kaafnee "waan Nuti isiniif kennine cimsaa qabadhaaAkka Rabbiin sodaattaniif waan isa keessa jirus yaadadhaa’ isiniin jenne [yaadadhaa]."[]');
INSERT INTO "apaguna" VALUES(2,64,'2:64','Ergasii sana booda gara galtanOdoo tolli Rabbiitiifi rahmanni Isaa isin irra jiraachuu baatee, silaa isin warra hoongaye irraa taatu.');
INSERT INTO "apaguna" VALUES(2,65,'2:65','Dhugumatti isin warra isin irraa [dhimma] Sanbataa keessatti[] daangaa dabranii, "isin jaldeeyyii salphattoota ta’aa" isaaniin jenne beektanii jirtu.');
INSERT INTO "apaguna" VALUES(2,66,'2:66','Kanaafuu Nuti ishee (adabbii sana) warra isaaniin duraatiifi isaaniin boodaatiif jiilchaa, warra Rabbiin sodaataniif immoo gorsa taasifne.');
INSERT INTO "apaguna" VALUES(2,67,'2:67','Yeroo Muusaan ummata isaatiin "dhugumatti, Rabbiin sa’a qaluutti isin ajaja" jedhe [yaadadhaa][Ummanni isaas] "sila ati qishnaa nu godhattaa?" jedhan[Muusaanis] "ani warra wallaaloo [namatti qoosan] ta’uu irraa Rabbittin maganfadha" jedhe');
INSERT INTO "apaguna" VALUES(2,68,'2:68','[Ummanni isaas] "isheen maal akka taate ifa nuuf haa godhuu Gooftaa kee nuuf gaafadhu" jedhaniin[Muusaanis] ni jedhe: "dhugumatti, Inni (Rabbiin) ‘isheen sa’a hin dulloomin, kan jabbiis hin ta’in giddu gala taate’ jedhaWaanuma ajajamtan dalagaa."');
INSERT INTO "apaguna" VALUES(2,69,'2:69','[Ummanni isaas] "bifa ishee ifa nuuf haa godhuu Gooftaa kee nuuf gaafadhu" jedhan[Muusaanis] "dhugumatti, Inni (Rabbiin) ‘isheen sa’a daalattii, bifti ishee akkaan daalattii daaw’attoota gammachiiftuudha'' jedha" jedheen.');
INSERT INTO "apaguna" VALUES(2,70,'2:70','[Ummanni isaas] “Dhugumatti, sa’oonni nurratti wal fakkaataniiru; maalummaa ishee ifa nuuf haa godhuu Gooftaa kee nuuf gaafadhuNuti yoo Rabbiin fedhe qajeeluuf jirra" jedhan.');
INSERT INTO "apaguna" VALUES(2,71,'2:71','[Muusaanis] ni jedhe: "Inni (Rabbiin) ‘dhugumatti isheen sa’a dachii qotuufi oorruu (bishaan) obaasuu irratti hin leenjifamin, kan [hirdhuu qaamaa irraa] nagaa taateefi bifti makaan ishee keessa hin jirreedha’ jedha." [Ummanni isaas] "amma dhugaa fiddee jirta" jedhanKan hojjachuuttis hin dhihaanne ta’anii ishee qalan.');
INSERT INTO "apaguna" VALUES(2,72,'2:72','Yeroo lubbuu ajjeeftanii [waa’ee] ishee keessatti wal falmitan [yaadadhaa]Rabbiin waan isin dhoksaa turtan ifa baasaadha.');
INSERT INTO "apaguna" VALUES(2,73,'2:73','"Garii [qaama] isheetiin isa (reeffa isaa) rukutaa" jenneAkka isin xiinxaltaniif jecha akka kanatti Rabbiin du’aa jiraachisee mallattoolee Isaas isinitti agarsiisa.');
INSERT INTO "apaguna" VALUES(2,74,'2:74','Ergasii booda onneen keessan ni gogdeIsheen [gogiinsa keessatti] akkuma dhagaati; yookiin goginaan ni caaltiDhugumatti dhagoota irraa kan laggeen isarraa maddantu jiruIsaan(dhagoota) irraa kan dhodhooyee bishaan isa irraa ba’us ni jiraAmmas isaan irraa kan sodaa Rabbii irraa ka''ee gadi kufutu jiraRabbiin waan isin dalagdan irraa dagataa miti.');
INSERT INTO "apaguna" VALUES(2,75,'2:75','Sila! odoo gareen isaan (Yahuudaa) irraa ta’e haasa’a Rabbii kan dhaga’an ta’anii, eega hubatanii booda odoo beekanuu jijjiiran jiranuu, nuuf amanu jechuu kajeeltuu?');
INSERT INTO "apaguna" VALUES(2,76,'2:76','Yeroo warra amanan qunnaman "nuti amannee jirra" jedhanYeroo gariin isaanii garii waliin kophaa ba''an, "sila akka Rabbii keessan biratti ittiin isin falmanuuf waan Rabbiin isin beeksise isaanitti odeessituu! Sila hin xiinxaltanuu!" jedhu.');
INSERT INTO "apaguna" VALUES(2,77,'2:77','Sila isaan akka Rabbiin waan isaan dhoksaniifi waan isaan ifa godhan beeku hin beekanuu?');
INSERT INTO "apaguna" VALUES(2,78,'2:78','Isaan irraas wallaaltota hawwii sobaa malee kitaaba hin beeknetu jiraIsaan kan shakkan malee homaa miti.');
INSERT INTO "apaguna" VALUES(2,79,'2:79','Warra kitaaba harka isaaniitiin katabanii, ergasii isaan gatii muraasa bitachuuf jecha "kun Rabbiin biraa bu’e" jedhaniif badiin mirkanaa''e! Waan harki isaanii barreessiteef jecha badiin isaaniif haa ta’u! Ammas waan isaan carraaqqataniif jecha badiin isaaniif haa ta’u.');
INSERT INTO "apaguna" VALUES(2,80,'2:80','"Ibiddi guyyaa lakkaa’amtuu malee nu hin tuqxu" jedhan"Sila isin Rabbiin bira waadaa kaayattanii? [Yoo akkas ta’eef] Rabbiin waadaa isaa hin diigu; [miti] dhugaatti, waan hin beekne Rabbi irratti dubbattu" jedhi.');
INSERT INTO "apaguna" VALUES(2,81,'2:81','[Dubbiin akka isin jettan] miti! Namni hamtuu hojjatee hamtuun isaa isa marsite, isaan warra ibiddaatiIsaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(2,82,'2:82','Warri amananii, gaggaarii hojjatan immoo isaan warra jannataa kan ishee keessatti hafanuudha.');
INSERT INTO "apaguna" VALUES(2,83,'2:83','Ammas yeroo Nuti Ilmaan Israa’iil irraa "Rabbiin malee [waan biraa] hin gabbarinaa; abbaafi haadhaaf, firootaafis, yatiimotaafis, hiyyeeyyiifis tola oolaa; namootattis toltuu haasa’aa; salaatas gadi dhaabaa; zakaas kennaa" (jechuun) waadaa qabanne [yaadadhaa]Ergasii nama muraasa isin irraa ta’e malee, duuba deebi’aa taatanii irraa gara galtani.');
INSERT INTO "apaguna" VALUES(2,84,'2:84','Yeroo Nuti "dhiiga keessan hin dhangalaasinaa; namoota keessanis manneen isaanii irraa hin baasinaa" (jechuun) waadaa keessan qabannee ergasii isin ragaa taatanii mirkaneessitan [yaadadhaa].');
INSERT INTO "apaguna" VALUES(2,85,'2:85','Ergasii isinuma kanneentu lubbuu walii ajjeesaOdoo isaan baasuun dhorgaa isin irratti ta’ee jiruu garee isin irraa ta’e manneen isaanii irraa baaftu; isaan irrattis badiifi diinummaan wal gargaartuYoo isaan booji’amtoota ta’anii isinitti dhufanii immoo isaan bilisoomsituSila garii kitaabaatti amantanii gariitti kafartuu? Mindaan nama isin irraa kana hojjatuu jireenya addunyaa kanaa keessatti salphina malee hin jiruGuyyaa Qiyaamaas gara adabbii cimaa deebifamuRabbiin waan isin dalagdan kan dagatus hin taane.');
INSERT INTO "apaguna" VALUES(2,86,'2:86','Isaan warra jireenya addunyaatiin Aakhiraa jijjiirrataniidhaKanaafuu adabbiin isaan irraa hin salphifamuIsaan hin gargaaramanus.');
INSERT INTO "apaguna" VALUES(2,87,'2:87','Dhugumatti Nuti Muusaaf kitaaba kenniineerraIsa boodas ergamtoota walitti aansuun hordofsiifnee jirraIisaa ilma Maryamiifis mallattoolee ifa ta’an kenninee jirra"Ruuhul Qudsiin" (Jibriiliinis) isa jabeessinee jirraSila dhawaata ergamaan waan lubbuun keessan hin feeneen isinitti dhufuun boontaniiti garii kijibsiiftanii garii ajjeeftuu?');
INSERT INTO "apaguna" VALUES(2,88,'2:88','[Isaanis] "Onneen keenya cufamaadha" jedhanDhugumatti sababa kufrii isaanii Rabbiin waan isaan abaareef xiqqoo malee hin amananu.');
INSERT INTO "apaguna" VALUES(2,89,'2:89','Yeroo kitaabni Rabbiin biraa ta’e kan waan isaanii wajjin jiru (Tawraatifi injiilin) dhugoomsu isaanitti dhufu, kan san dura akka warra kafaran irratti injifannoo argataniif [dhufaatii Nabi Muhammadiin SAW Rabbiin] kadhachaa turanii yeroo wanti isaan beekan isaanitti dhufu isatti kafaranKanaafuu abaarsi Rabbii kaafiroota irra haa jiraatu.');
INSERT INTO "apaguna" VALUES(2,90,'2:90','Isaan Rabbiin tola Isaa irraa gabroota Isaa gidduu nama fedhe irratti buusuu waanyuun (jibbuun) waan Rabbiin buusetti kafaruun, wanti isaan lubbuu isaanii ittiin jijjiirratan waa fokkateKanaafuu dallansuu irratti dallansuudhaan deebi’anKaafirootaaf adabbii xiqqeessaatu jira.');
INSERT INTO "apaguna" VALUES(2,91,'2:91','Yeroo waan Rabbiin buusetti amanaa isaaniin jedhame "nuti waan nurratti buufametti amanna" jedhuWaan isaan ala jiru odoo inni dhugaa dhugoomsaa waan (kitaaba) isaanii wajjin jiruu ta’ee jiruu itti kafaran(Yaa Muhammad! SAW) jedhi: "Sila yoo dhugaan mu’uminoota taataniif maaliif sana dura nabiyyoota Rabbii ajjeeftu?"');
INSERT INTO "apaguna" VALUES(2,92,'2:92','Dhugumatti Muusaanis ragaawwan ifa ta’aniin isinitti dhufee jiraErgasii isin lubbuu keessan miidhoo taatanii isa booda (bakka inni hin jirretti) mirgoo [gabbaramaa] godhattan.');
INSERT INTO "apaguna" VALUES(2,93,'2:93','Yeroo Nuti waadaa keessan qabannee Gaara Xuurii gubbaa keessan irratti ol fuunee "Waan Nuti isiniif kennine ciminaan qabadhaa[Jecha keenyas] dhaga''aa" jenne [yaadadhaa]Ni jedhan: "Dhageenyee faallessineerra." Onnee isaanii keessa sababaa kufrii isaanitiin [jaalala] mirgoo [gabbaruu] obaafamanii jiru"Yoo mu’uminoota taataniif wanti iimaanni keessan itti isin ajaju waa fokkate" jedhi!');
INSERT INTO "apaguna" VALUES(2,94,'2:94','Jedhi: "Yoo manni Aakhiraa Rabbiin biratti isin qofaaf namoota biraatiin ala kophatti ta’e, yoo dhugaa kan dubbattan taataniif mee du’a hawwaa."');
INSERT INTO "apaguna" VALUES(2,95,'2:95','Sababa waan harki isaanii dabarfatteef jecha gonkumaa isa hin hawwanuRabbiin warra miidhaa dalaganu akkaan beekaadha.');
INSERT INTO "apaguna" VALUES(2,96,'2:96','Nama hunda irra jireenyaaf bololaa (saassataa) ta’uu isaanii argitaMushrikoota irrallee [jireenyaaf bololu]Tokkoon isaanii haala jiraachifamuun isaa kan isa hin fayyanne ta’ee jiruun, odoo waggaa kuma jiraachifamee jaalataRabbiin waan isaan dalagan akkaan argaadha.');
INSERT INTO "apaguna" VALUES(2,97,'2:97','"Namni sababa Jibriil isa (Qur’aana) dhugoomsaa waan isa duraatiifi mu’uminootaaf qajeelfamaafi gammachuu ta’e fedha Rabbiitiin onnee kee irratti waan buuseef Jibriiliif diina ta’e, [dallansuu isaatiin haa du’u]” jedhi.');
INSERT INTO "apaguna" VALUES(2,98,'2:98','Namni Rabbiif, malaa’ikootaaf, ergamtootaaf, Jibriiliifi Miikaa’iiliif diina ta’e [kaafira]Dhugumatti Rabbiin kaafirootaaf diina.');
INSERT INTO "apaguna" VALUES(2,99,'2:99','Nuti dhugumatti keeyyattoota ifa ta’an gara keetti buufnee jirraIsaanitti kan kafaru finciltoota malee hin jiru.');
INSERT INTO "apaguna" VALUES(2,100,'2:100','Sila isaan dhawaata waadaa wahii seenaniin, gareen isaan irraa ta’e isa gataa (waadaa diigaa)? Dhugaatti irra hedduun isaanii hin amanan.');
INSERT INTO "apaguna" VALUES(2,101,'2:101','| Yeroo ergamaan Rabbiin biraa dhugoomsaa waan isaan biraa ta’ee isaanitti dhufes, gareen warra kitaabni kennameef irraa ta’e akka waan hin beekiniitti kitaaba Rabbii dugda duuba isaaniitti of irraa darbu.');
INSERT INTO "apaguna" VALUES(2,102,'2:102','| Waan shayxaanonni bara mootummaa Sulaymaan keessa qara’aa turan hordofanSulaymaanis hin kafarreGaruu shayxaanonni sihriifi waan malaa’ikoota lamaan Haaruutiifi Maaruut irratti Baabil keessatti buufame nama kan barsiisan ta''anii kafaranIsaan (malaa’ikoonni) lamaan nama tokkos odoo "Nuti mokkoroof dhufnee hin kafarinaa" hin jedhin (sihrii) hin barsiisanu(Namoonnis malaa’ikoota) lamaan irraa waan ittiin dhirsaafi niitii addaan baasan baratuIsaanis nama tokko hayyama Rabbiitin ala kan miidhan hin taaneWaan isaan miidhuu malee isaan hin fayyadne baratanDhugumatti, isaan (yahuudonni) akka warri isa (sihrii) jijjiirrate Aakhiratti qooda hin qabaanne sirritti beekaniiruOdoo kan beekan ta’anii, wanti isaan lubbuu isaanii itti gurguran waa fokkate!');
INSERT INTO "apaguna" VALUES(2,103,'2:103','Odoo isaan amananii Rabbiin sodaatanii, silaa mindaan isaan Rabbiin biraa argatan akkaan caalaa ture; odoo kan beekan ta’anii (silaa kan biraa hin filatanu).');
INSERT INTO "apaguna" VALUES(2,104,'2:104','Yaa warra amantan! (Ergamaa Rabbiitiin) "Raa’inaa" hin jedhinaatii "Unzurnaa" jedhaa; dhageeffadhaasKaafirootaaf adabbii laalessaa ta’etu jira');
INSERT INTO "apaguna" VALUES(2,105,'2:105','Abbootii kitaabaas ta’ee mushrikoota irraa warri kafaran Gooftaa keessan irraa toltuun isin irratti buufamuu hin jaalatanuRabbiin rahmata Isaatiin nama fedhe filataRabbiinis abbaa tola guddaati.');
INSERT INTO "apaguna" VALUES(2,106,'2:106','Wanta keeyyata irraa jijjiirre yookiin si irraanfachiifne caaltuu ishee yookiin fakkaattuu ishee finnaSila ati Rabbiin waa hunda irratti danda’aa ta’uu hin beektuu?');
INSERT INTO "apaguna" VALUES(2,107,'2:107','Sila ati mootummaan samiifi dachii kan Rabbii ta’uu hin beektuu? Isiniifis Rabbiin ala jaalalleefi gargaaraan hin jiru.');
INSERT INTO "apaguna" VALUES(2,108,'2:108','Dhugaatti isin akka Muusaan kana dura gaafatame ergamaa keessan gaafachuu feetuNamni kufrii iimaanaan jijjiirrate, dhugumatti karaa sirrii irraa jallateera.');
INSERT INTO "apaguna" VALUES(2,109,'2:109','Hedduun warra kitaabaa irraa ta’an erga dhugaan ifa isaanii galee booda, waanyoo lubbuu isaanii irraa kan ka''e erga amantanii booda kaafiroota taatanii isin deebisuu jaallatuHanga Rabbiin ajaja Isaa fidutti dhiifama godhaa; irras dabraaDhugumatti, Rabbiin waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(2,110,'2:110','Salaata gadi dhaabaa; zakaas kennaaToltuu irraa waan lubbuu keessaniif dabarfattan (kamillee) Rabbiin biratti isa argattuDhugumatti, Rabbiin waan isin hojjattan akkaan argaadha.');
INSERT INTO "apaguna" VALUES(2,111,'2:111','"Nama yahuudaa yookiin kiristaana ta’e malee jannata hin seenu” jedhanKun hawwii isaaniiti“Yoo kan dhugaa dubbattan taataniif ragaa keessan fidaa" jedhi.');
INSERT INTO "apaguna" VALUES(2,112,'2:112','(Dubbiin akka isin jettanii) miti; namni gaarii dalagaa fuula isaa Rabbiif kenne mindaan isaa Rabbii isaa bira isaaf jira; sodaan isaan irra hin jiru; isaan hin gaddanus.');
INSERT INTO "apaguna" VALUES(2,113,'2:113','Oduma kitaaba qara’anuu yahuudonni “kiristaanonni homaa irra (amantii sirrii ta’e hordofaa) hin jiranu” jedhu; Kiristaanonnis “yahuudonni homaa irra hin jiranu” jedhuAkkuma san warri hin beekne fakkaataa jecha isaanii dubbatanRabbiin Guyyaa Qiyaamaa gidduu isaaniitti waan isaan isa keessatti wal dhabaa turan irratti murteessa.');
INSERT INTO "apaguna" VALUES(2,114,'2:114','Nama masjiidota Rabbii akka isaan keessatti maqaan Rabbii hin faarfamne dhorgeefi onsuu isaaniitiif carraaqe caalaa namni miidhaa hojjate hin jiruWarri sun haala sodaan guutamaniin malee isaan (masjiidota Rabbii) seenuun isaaniif hin ta’uIsaaniif addunyaa keessatti salphinatu jiraAmmas isaaniif Aakhiratti adabbii guddaa ta’etu jira.');
INSERT INTO "apaguna" VALUES(2,115,'2:115','Bahaafi Dhiitis kanuma RabbiitiKanaafuu gara feetan yoo gara galtan, Fuulli Rabbii achuma jiraDhugumatti, Rabbiin (rahmanni Isaa) bal’aadha; hunda beekaadha.');
INSERT INTO "apaguna" VALUES(2,116,'2:116','"Raabbiin ilma godhate" jedhanInni qulqullina isaaf (malu ququllaa''e)Dhugaatti wanti samiifi dachii keessa jiru (hundi) kan IsaatiHunduu isaaf ajajamoodha.');
INSERT INTO "apaguna" VALUES(2,117,'2:117','(Inni) argamsiisaa samiifi dachiitiYeroo dhimma wahii murteesses, wanti Inni isaan jedhu argamii qofa; yeroma san argama.');
INSERT INTO "apaguna" VALUES(2,118,'2:118','Warri (homaa) hin beekne: "maaliif Rabbiin nuun hin dubbanne yookiin maaliif mallattoon wahii nuuf hin dhufne? jedhu; akkasuma warri isaaniin duraa fakkaataa jecha isaanii dubbatanOnneen isaanii wal fakkaatteDhugumatti, Nuti namoota (amantii Rabbii) mirkaneeffataniif mallattoolee (keenya) ifa taasifnee jirra');
INSERT INTO "apaguna" VALUES(2,119,'2:119','Nuti dhugaan gammachiisaafi akeekkachiisaa taasifnee si ergineAti warra ibiddaa irraa hin gaafatamtu.');
INSERT INTO "apaguna" VALUES(2,120,'2:120','Yahuudotas ta’anii kiristaanonni hanga ati amantii isaanii hordoftutti sirraa hin jaalatanu(Yaa Muhammad! SAW) "Dhugumatti qajeelfamni Rabbii isuma qofatu qajeelfama (sirriidha)" jedhiErga beekumsa irraa sitti dhufee yoo fedha lubbuu isaanii hordofte, Rabbi irraa jaalallees ta’ee tumsaan siif hin jiru.');
INSERT INTO "apaguna" VALUES(2,121,'2:121','Isaannan Nuti kitaaba (Tawraatiin yookiin Qur’aana) isaaniif kennine qaraatii (hordoffii) malu isa qara’uIsaan isatti amanuNamni isatti kafares, isaan warra hoonga’oodha.');
INSERT INTO "apaguna" VALUES(2,122,'2:122','Yaa ilmaan Israa’iil! Ni’imaa kiyya kan Ani isin irratti oole san yaadadhaa; Ani Aalama hunda irra isin caalchisuu kiyyaas [yaadadhaa].');
INSERT INTO "apaguna" VALUES(2,123,'2:123','Guyyaa lubbuun tokko lubbuu biraa hin fayyanne, kan gumaanis ishee irraa hin fuudhamne, araarri homaatuu ishee hin fayyanneefi isaanis tumsa hin arganne sodaadhaa.');
INSERT INTO "apaguna" VALUES(2,124,'2:124','Yeroo Ibraahiimiin Gooftaan isaa jechootaan isa mokkoree (Ibraahiimis) isa guute [yaadadhaa](Rabbiinis) "Dhugu-matti, Ani namootaaf imaama (dursaan) si taasisa" jedhe(Ibraahiim) ni jedhe:"Sanyii kiyya irraas (taasisi)." (Rabbiinis) ni jedhe: "Waadaan (nabiyyummaan) kiyya warra miidhaa hojjatan hin argatu."');
INSERT INTO "apaguna" VALUES(2,125,'2:125','Yeroo Manicha (Ka’abaa) namootaaf iddoo deddeebiifi iddoo nagaa taasifnes [yaadadhaa]Maqaama Ibraahiim irraas iddoo salaataa taasifadhaa(Nutis) Ibraahiimiifi Ismaa’iiliin mana Kiyya warra isa irra naanna’aniif, warra keessa taa’aniifi warra (isa keessatti) rukuu’aafi sujuuda godhaniif qulqulleessuu isaan ajajnee jirra.');
INSERT INTO "apaguna" VALUES(2,126,'2:126','Yeroo Ibraahiim "Gooftaa kiyya! Magaalaa kana (Makkaan) iddoo tasgabbii taasisiWarra (jiraattota) isaas, namoota isaan irraa Rabbiifi guyyaa Aakhiraatti amaneef fuduraalee irraa soorata isaaniif kenni" jedhe [yaadadhaa](Rabbiinis) "Nama kafares (niin nyaachisa); Ani xiqqoon isa qanansiisee ergasii gara adabbii ibiddaattin isa geessaIddoo deebii kan akkaan fokkateedha!" jedhe.');
INSERT INTO "apaguna" VALUES(2,127,'2:127','Yeroo Ibraahiimiifi Ismaa’iil "Yaa Rabbi Ati dhagahaa beekaa dhaa! nurraa qeebali" jedhaa; bu’uura manaa (Ka’abaa) ol fuudhan (ummataaf himi).');
INSERT INTO "apaguna" VALUES(2,128,'2:128','"Gooftaa keenya! Nu lamaanuu Siif ajajamtoota nu taasisi; sanyii keenya irraas ummata Siif ajajamtoota (taasisi)‘Manaasika’ keenyas (sirnoota hajjiifi umraah irratti raawwachuu qabnus) nutti agarsiisiTawbaa keenyas nurraa qeebaliDhugumatti, Ati Situ akkaan tawbaa qeebalaa, akkaan rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,129,'2:129','Gooftaa keenya! Ergamaa isaan irraa ta’e kan keeyyattoota kee isaan irratti qara’u, kan kitaabaafi ogummaa isaan barsiisee isaanis qulqulleessu isaan keessatti ergiDhugumatti, Ati Situ akkaan injifataa, ogeessa."');
INSERT INTO "apaguna" VALUES(2,130,'2:130','Sila nama lubbuu isaa gowwoomse malee namni amantii Ibraahiim irraa gara galu ni jiraa? addunyaa keessattis isa filannee jirra; dhugumatti, inni Aakhiraa keessattis warra gaggaarii irraayyi.');
INSERT INTO "apaguna" VALUES(2,131,'2:131','Yeroo Gooftaan isaa "(Rabbiin jala) butami" isaan jennaan, [Inni] "Gooftaa aalamaa jala butameera" jedhe [yaadadhu].');
INSERT INTO "apaguna" VALUES(2,132,'2:132','Ibraahiim ilmaan isaatiif ishee (Islaamummaa) dhaamee ture; Ya’aquubiis (sanuma dhaame); "Yaa ilmaan kiyya! Dhugumatti, Rabbiin amantii isiniif filee jiraa Muslimoota taatanii malee hin du’inaa" (jedhe).');
INSERT INTO "apaguna" VALUES(2,133,'2:133','Sila yeroo Ya’aquubitti duuti dhufnaan ilmaan isaatiin "ana booda maal gabbartu?" jedhu isin bira jirtumoo? Isaanis ni jedhan: "Nuti Gooftaa kee, Gooftaa abboota kee Ibraahiim, Ismaa’iiliifi Isihaaq gabbaramaa tokkicha kan ta’e gabbarraNuti Isaaf ajajamtoota (Muslimoota)."');
INSERT INTO "apaguna" VALUES(2,134,'2:134','Dhugumatti isheen sun ummata dabarteIsheedhaaf mindaa waan oomishteetu jira; Isiniifis mindaa waan oomishtaniitu jiraIsinis waan isaan hojjataa turan irraa hin gaafatamtanu.');
INSERT INTO "apaguna" VALUES(2,135,'2:135','"Ni qajeeltuu Yahuudaa yookiin Kiristaanota ta’aa" jedhan"Dhugaatti karaa Ibraahiim isa qajeelaa ta’e (hordofna); Innis mushrikoota irraa hin taane" jedhi.');
INSERT INTO "apaguna" VALUES(2,136,'2:136','(Mu’uminoonni) jedhaa: “Nuti Rabbitti amannee jirra; waan gara keenyatti buufamettis; waan gara Ibraahiim, Ismaa’iil, Isihaaq, Ya’aquubii[]fi Asbaaxitti buufametti (amannee jirra)Waan Muusaafi Iisaan kennamanittisWaan nabiyyootaaf Gooftaa isaanii irraa kennamettis (amannee jirra)Isaan irraa gidduu nama tokkoos addaan hin baafnuNutis warra Isaaf butameedha.”');
INSERT INTO "apaguna" VALUES(2,137,'2:137','Yoo fakkaataa waan isin itti amantaniititti amananiif dhugumatti qajeelaniiruYoo isa irraa gara galan immoo isaan wal faallessuu qofa keessa jiruRabbiinis isaan siif ga’a (sirraa dhorga)Innis akkaan dhagayaa, akkaan beekaadha.');
INSERT INTO "apaguna" VALUES(2,138,'2:138','Halluu Rabbii isa uumamaa (amantii Isaa) (qabadhaa)Namni Rabbirra halluu dibuu tolchu eenyu? Nuti isuma gabbarra');
INSERT INTO "apaguna" VALUES(2,139,'2:139','(Yaa Muhammad! Yahuudotaafi Kiristaanotaan) jedhi: “odoo Inni Gooftaa keenyaafi Gooftaa keessan ta’ee jiruu, nuufis dalagaan keenya, isiniifis dalagaan keessan jiruu, waa’ee Rabbii keessatti nuun falmituu? Nuti (amantii) Isaaf qulqulleessoodha.');
INSERT INTO "apaguna" VALUES(2,140,'2:140','Sila Ibraahiim, Ismaa’iil, Isihaaq, Ya’aquubiifi Asbaax yahuudota yookiin kiristaanota turan jettuu? Sila isintu irra beekamoo Rabbi?” jedhiinSila nama ragaa Rabbirraa bu’e kan isa bira jiru dhokse caalaa namni miidhaa hojjate eenyu? Rabbiinis waan isin dalagdan dagataa miti."');
INSERT INTO "apaguna" VALUES(2,141,'2:141','Dhugumatti isheen sun ummata dabarteIsheefis mindaa waan oomishteetu jira; Isiniifis mindaa waan oomishtaniitu jiraIsinis waan isaan hojjataa turan irraa hin gaafatamtanu');
INSERT INTO "apaguna" VALUES(2,142,'2:142','Namoota irraa warri gowwaan "Sila qiblaa irra turan (Beytal-Maqdis)irraa maaltu isaan garagalche?" jedhu"Kallattiin Bahaafi Dhihaas kanuma RabbiitiInni nama fedhe gara karaa sirrii qajeelcha" jedhi.');
INSERT INTO "apaguna" VALUES(2,143,'2:143','Akkasuma (akkuma isin qajeelchinetti) namoota irratti ragaa akka taatanuufi ergamichis ragaa isin irratti ta’uuf jecha filatamtuu ummataa isin taasifneNuti qiblaa ati irra turtes nama ergamaa hordofuufi nama (amantii irraa) duubatti deebi’u eenyu akka ta’e beekuuf malee hin taasifneDhugumatti sun warra Rabbiin qajeelche irratti malee ulfaattuu turteRabbiin iimaana (salaata) keessan (kan gara Beytal-Maqdis gara galtanii sagadaa turtan) kan balleessu hin taaneDhugumatti, Rabbiin namootaaf mararfataa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,144,'2:144','Dhugumatti, gara samii deddeebi’uu fuula keetii arginee jirraGara qiblaa ati jaallattuu si deebisuuf jirra(Ammuma) fuula kee gara Masjidal-Haraam (Ka’abaatti) gara galchiEessallee yoo taatan fuula keessan gara isaa gara galchaaDhugumatti, warri kitaabni kennameef akka inni dhugaa Gooftaa isaanii irraa ta''e ni beekuRabbiinis waan isaan hojjatan irraa dagataa miti.');
INSERT INTO "apaguna" VALUES(2,145,'2:145','|Odoo warra kitaabni isaaniif kennametti mallattoo hundaan itti dhufteeyyuu qiblaa kee hin hordofanuAtis qiblaa isaanii kan hordoftu hin taaneGariin isaaniis qiblaa garii isaanii kan hordofanu hin taaneDhugumatti, erga beekumsa irraa waa sitti dhufee booda odoo fedha lubbuu isaanii hordoftee, ati yoos warra akkaan miidhaa hojjatanu irraa taata turte.');
INSERT INTO "apaguna" VALUES(2,146,'2:146','Warri Nuti kitaaba isaaniif kennine akkuma ilmaan ofii beekanitti isa (Ergamicha) beekuDhugumatti gareen isaan irraa ta’e odoo beekanuu dhugaa dhoksu.');
INSERT INTO "apaguna" VALUES(2,147,'2:147','Dhugaan Gooftaa kee irraayiKanaafuu warra shakkan irraa hin ta’in.');
INSERT INTO "apaguna" VALUES(2,148,'2:148','Hawaasa hundaafuu kallattii isaan itti gara galantu jiraToltuu irratti wal dorgomaaEessallee yoo taatan Rabbiin (Guyyaa Qiyaamaa) hunda keessanuu ni fidaDhugumatti, Rabbiin waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(2,149,'2:149','Iddoma (imalaaf) baate kamirraayyuu fuula kee gara Masjidal Haraam gara galchiDhugumatti, inni dhugaa Gooftaa kee irraa ta’eRabbiinis waan isin dalagdan dagataa hin taane.');
INSERT INTO "apaguna" VALUES(2,150,'2:150','Iddoma (imalaaf) baate kamirraayyuu fuula kee gara Masjiidal Haraam gara galchiEessallee yoo taatan akka namootaaf ragaan isin irratti hin jiraanneef, akka Anis qananii kiyya guutee isiniif kennuufi isinis (gara haqaa) qajeeltaniif jecha fuula keessan gara isaa gara galchaaGaruu isaan irraa warra miidhaa hojjatan hin sodaatinaatii Anuma qofa sodaadhaa.');
INSERT INTO "apaguna" VALUES(2,151,'2:151','Akkuma ergamaa isin irraa ta’e kan keeyyattoota keenya isin irratti qara’u, isin qulqulleessu, kitaabaafi hikmaa (hadiisa) isin barsiisuufi waan isin hin beeknes isin barsiisu isin keessatti ergine (isin qanansiifne).');
INSERT INTO "apaguna" VALUES(2,152,'2:152','Ana yaadadhaa; Anis isiniin yaadadhaaGalatas naaf galchaa; Nattis hin kafarinaa.');
INSERT INTO "apaguna" VALUES(2,153,'2:153','Yaa warra amantan! Obsaafi salaataan gargaarsa barbaadaaDhugumatti, Rabbiin warra obsan waliin jira.');
INSERT INTO "apaguna" VALUES(2,154,'2:154','Nama karaa Rabbii keessatti ajjeefamuun du’aadha hin jedhinaaDhugumatti isaan fayyaadha, garuu isin hin beektanu.');
INSERT INTO "apaguna" VALUES(2,155,'2:155','Isaan warra yeroo balaan isaan tuqe "dhugumatti nuti kan Rabbiiti, nuti gara Isaattis deebi’oodha" jedhaniidha.');
INSERT INTO "apaguna" VALUES(2,156,'2:156','Isaan warra faaruufi tolli Gooftaa isaanii irraa ta’e isaan irra jiruudhaWarri sun isaanumatu qajeelfamoodha.');
INSERT INTO "apaguna" VALUES(2,157,'2:157','Isaan warra faaruufi tolli Gooftaa isaanii irraa ta’e isaan irra jiruudhaWarri sun isaanumatu qajeelfamoodha.');
INSERT INTO "apaguna" VALUES(2,158,'2:158','Dhugumatti, Safaafi Marwaan mallattoolee Rabbii irraayyiNamni Manichatti hajja’e yookiin umraa godhe isaan lamaan jidduu naanna’uun badiin isarra hin jiruNamni fedhii isaatiin toltuu hojjates (ni mindeeffama)Rabbiin galateeffataa beekaadha');
INSERT INTO "apaguna" VALUES(2,159,'2:159','Dhugumatti, isaannan waan Nuti buufne kan ragaawwan ifa ta’aniifi qajeelfama irraa ta’e erga kitaabicha (keenya) keessatti namootaaf addeessinee booda dhoksan, warra san Rabbiin isaan abaara; warri abaaranus isaan abaaru.');
INSERT INTO "apaguna" VALUES(2,160,'2:160','Garuu warri tawbatanii, hojii gaarii hojjatanii, (dhugaa dhoksan san) ifatti mul’isan warra san tawbaa isaanii isaan irraa nan qeebalaAni akkaan tawba-qeebalaa, akkaan rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,161,'2:161','Dhugumatti, warri kafaranii kaafirtoota ta’anii du’an, sunniin abaarsi Rabbii, malaa’ikootaafi kan namoota hundaatuu isaan irra jiraata.');
INSERT INTO "apaguna" VALUES(2,162,'2:162','Isii keessa yeroo hunda jiraatuadabbiin isaan irraa hin salphifamuBaallamnis isaaniif hin kennamu.');
INSERT INTO "apaguna" VALUES(2,163,'2:163','Gabbaramaan keessan gabbaramaa tokkichaIsa malee dhugaan gabbaramaan hin jiru(Inni) akkaan mararfataa rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,164,'2:164','Dhugumatti uumama samiifi dachii, wal irraa bakka bu’uu halkaniifi guyyaa, doonii waan namoota fayyaduun galaana keessa yaatu, bishaan irraa waan Rabbiin samii irraa buusee ittiin dachii erga isiin duutee booda jiraachiseefi lubbu-qabeeyyii hunda irraa isii keessa facaase, bubbisa qilleensaafi duumessa samiifi dachii giddutti (uumamootaaf) mijeeffamanii keessa namoota xiinxalaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(2,165,'2:165','Namoota irraas nama Rabbiin ala gabbaramoota (biraa) kan akka Rabbiin jaallatanutti isaan jaallatan godhatantu jiruIsaan amanan irra caalaa Rabbiin jaallatuWarri miidhaa hojjatanu yeroo adabbii argan akka humni hundi kan Rabbii ta’eefi Rabbiinis (abbaa) adabbii cimaa akka ta’e odoo arguu kan danda’an ta’anii (silaa gabbaramaa biraa hin godhatanu).');
INSERT INTO "apaguna" VALUES(2,166,'2:166','Yeroo warri hordofaman warra hordofan irraa qulqullaayanii (akka isaan hin jallisin ibsanii)fi (yeroo) adabbii argan, hariiroon isaanii hundi yeroo isaan jalaa citu (odoo beekanii gabbaramaa biraa hin godhatanu ture).');
INSERT INTO "apaguna" VALUES(2,167,'2:167','Isaan hordofanis ni jedhu: "odoo carraan (gara addunyaa) deebi’uu nuuf jiraatee, silaa akkuma isaan nurraa qulqullaayan nutis isaan irraa qulqulloofna turre." Akka kanatti Rabbiin hojiiwwan isaanii gaabbii (sheenaa) isaan irratti godhee isaanitti mul’isaIsaan ibidda irraas bahoo miti.');
INSERT INTO "apaguna" VALUES(2,168,'2:168','Yaa namootaa! Waan dachii keessa jiru irraa halaala gaarii ta’e nyaadhaaTarkaanfiilee sheyxaanaas hin hordofinaaDhugumatti, inni isiniif diina ifa bahaadha.');
INSERT INTO "apaguna" VALUES(2,169,'2:169','Kan inni itti isin ajaju hamtuu, waan fokkataafi Rabbi irratti waan hin beekne dubbachuudha.');
INSERT INTO "apaguna" VALUES(2,170,'2:170','Yeroo "waan Rabbiin buusetti amanaa" isaanin jedhames, "Lakkisaa; nuti waan abbootiin keenya hordofaa turan hordofna" jedhuSila odoo abbootiin isaanii kan homaa hin beekneefi karaa qajeelaa irra hin jirre ta’anilleemoo?');
INSERT INTO "apaguna" VALUES(2,171,'2:171','Fakkeenyi warra kafaranii, akka fakkeenya isa waan waamichaafi iyyansa malee hin dhageenyetti lallabuuti(Isaan) duudaa, arraba qabamaafi jaamaadha; isaan hin xiinxalanu.');
INSERT INTO "apaguna" VALUES(2,172,'2:172','Yaa warra amantan! Gaggaarii waan nuti isiniif kenninee irraa nyaadhaa; yoo kan Isa gabbartan taatan, Rabbiifis galata galchaa.');
INSERT INTO "apaguna" VALUES(2,173,'2:173','Kan Inni isin irratti dhorgamaa taasise baktii, dhiiga, foon booyyeetiifi waan (maqaan) Rabbiin alaa isa irratti ol fuudhame qofaNamni odoo halaalli mijaa’uu haraamatti darbuufi nyaata isaa keessatti daangaa darbuun maleetti rakkifame badiin isarra hin jiruDhugumatti, Rabbiin akkaan mararfataa rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,174,'2:174','Dhugumatti, isaan kitaaba irraa waan Rabbiin buuse dhoksanii gatii xiqqaa isaan bitatan, isaan sun garaa isaanii keessatti ibidda malee hin nyaatanuRabbiinis Guyyaa Qiyaamaa isaan hin dubbisu; isaan hin qulqulleessus; isaaniifis adabbii laalessaa ta’etu jira.');
INSERT INTO "apaguna" VALUES(2,175,'2:175','Isaan warra qajeelumaan jal’ina, araaramaan immoo adabbii bitataniidhaSila maaltu ibidda irratti akkaan isaan obsisiise!');
INSERT INTO "apaguna" VALUES(2,176,'2:176','Sun sababa Rabbiin kitaaba dhugaan buufnaan, warri kitaaba keessatti wal dhaban wal diddaa fagoo keessa ta’aniifi.');
INSERT INTO "apaguna" VALUES(2,177,'2:177','Dalagaan gaariin fuula keessan gara bahaafi dhihaa naannessuu miti; haa ta’u malee dalagaan gaariin nama Rabbitti, Guyyaa Aakhiraatti, malaa’ikootatti, kitaabattiifi nabiyyootatti amanee, odoo jaallatuu qabeenya firaaf, yatiimaaf, hiyyeeyyiif, kara deemaaf, kadhattootaafi gabra bilisoomsuuf kennee, salaatas gadi dhaabee, zakaa kenneefi warra yeroo waadaa seenan waadaa isaanii guutanii, warra rakkina, dhukkuba hamaafi yeroo lolan obsaniidhaIsaan warra dhugaa qabataniIsaanis warra Rabbiin sodaataniidha.');
INSERT INTO "apaguna" VALUES(2,178,'2:178','Yaa warra amantan! Ajjeechaa keessatti "qisaasni" (seerri adabbii wal qixxeessuu) isin irratti barreeffameeraBilisni bilisaan, gabrichi gabrichaan, dubartiin dubartiidhaanNamni obboleessa isaa irraa wanti tokko isaaf dhiifame, toltuun hordofuufi kaffaltiin gumaa dhiigaas haala gaariin raawwatamuutu irra jiraKun laaffisuufi rahmata Gooftaa keessan irraa ta’eedhaNamni sana booda daangaa dabre, isaaf adabbii laalessaa ta’etu jira.');
INSERT INTO "apaguna" VALUES(2,179,'2:179','Yaa warra sammuu qabdan! "Qisaasa" keessa lubbuu (du’a) irraa baraaruutu isiniif jiraAkka isin ajjeechaa irraa eeggattaniif (kana goone) .');
INSERT INTO "apaguna" VALUES(2,180,'2:180','Yeroo duuti tokkoon keessanitti dhufe, yoo kan qabeenya of duubatti dhiise ta’e: abbaa, haadhaafi firootaaf haala gaariin dhaamuun isin irratti murteeffameera');
INSERT INTO "apaguna" VALUES(2,181,'2:181','Namni erga isa (dhaamsa) dhagayee booda isa jijjiire, badiin isaa warra isa jijjiiran irratti ta’aDhugumatti, Rabbiin dhagayaa, beekaadha.');
INSERT INTO "apaguna" VALUES(2,182,'2:182','Namni isa dhaamu san irraa jal’ina yookiin badii sodaatee gidduu isaaniitti nagaa buuse, badiin isa irra hin jiruDhugumatti, Rabbiin akkaan mararfataa rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,183,'2:183','Yaa warra amantan! Akka isin Rabbiin sodaattaniif Soomni akkuma warra isin duraa irratti barreefametti (dirqama ta’etti) isin irrattis barreeffameera.');
INSERT INTO "apaguna" VALUES(2,184,'2:184','Guyyoota lakkaa’amtuu (qofatu murteeffame)Isin irraa namni dhukkubsataa yookiin kara deemaa ta’e, guyyaa biraa irraa lakkaawwachuutu (isa irra jira)Warra isa (Soomuu) danda’an irratti kaffaltii (fidyaa) hiyyeessa nyaachisuutu jiraNamni fedha isaatiin toltuu dabalatee hojjate, sun isaaf caalaadhaYoo kan beektan taataniif soomuutu isiniif caala');
INSERT INTO "apaguna" VALUES(2,185,'2:185','Jiini Ramadaanaa isa Qur’aanni namootaaf qajeelfamaafi ragaa ifa bahaa qajeelfamichaafi addaan baasaa (haqaafi kijibaa) ta’e keessa buufameedhaNamni isin irraa ji’a kana dhaqqabe isa haa soomuNamni dhukkubsataa yookiin kara deemaa ta’e, guyyaa biraa irraa lakkaawwachuutu (isa irra jira)Rabbiin isiniif laaffisuu fedha malee isin irratti jabeessuu hin fedhuAkka lakkoofsa (guyyaa isin irratti barreeffamee) guuttaniifi waan isin qajeelcheef akka Rabbiin guddiftaniif, akkasumas akka (Rabbiin) galateeffattaniif jecha (kana goone).');
INSERT INTO "apaguna" VALUES(2,186,'2:186','Yeroo gabroonni kiyya Ana irraa si gaafatanis “dhugumatti, Ani isaanitti dhihoodha” (jedhiin)Kadhaa kadhataatiif yeroo inni na kadhate nan owwaadhaAkka qajeelaniif jecha naaf haa owwaatanu; nattis haa amananu.');
INSERT INTO "apaguna" VALUES(2,187,'2:187','Halkan soomaa gara dubartoota (niitiwwan) keessanii dhaquun (qunnamtii saalaa gochuun) isiniif hayyamameeraIsaan isiniif uffata; isiniis isaaniif uffataRabbiin akka isin lubbuu keessan ganaa turtan beekee tawbaa keessan isin irraa qeebalee dhiifamas isiniif godheAmma isaan waliin qunnamtii saalaa godhaa; (ilmaan irraa) waan Rabbiin isiniif katabe barbaaddadhaaHanga barraaqa irraa jibriin adiin (bariisaa) jibrii gurraacha (dukkana halkanii) irraa adda fooyamutti nyaadhaatii dhugaaErgasii hanga halkaniitti sooma keessan guutaaOdoo masjiidoota keessa i’itikaafa taa’aa jirtanuu isaan waliin qunnamtii saalaa hin raawwatinaaSun daangaawwan Rabbiitii isheetti hin dhihaatinaaAkka Isa sodaataniif jecha akka kanatti Rabbiin keeyyattoota Isaa namootaaf ibsa.');
INSERT INTO "apaguna" VALUES(2,188,'2:188','Qabeenya keessanis gidduu keessanitti sobaan hin nyaatinaaQabeenya namootaa irraa garii isaa odoo beektan cubbuun nyaachuuf jecha abbootii seeraatiifis malaan-maltummaan hin dhihaatinaa.');
INSERT INTO "apaguna" VALUES(2,189,'2:189','(Yaa Muhammad!) Waa’ee baatii reefuu baatu irraa si gaafatu“Isheen namootaaf kan yeroo ibsituufi hajji (beeksiftuu)dha” jedhiDalagaan gaariin manneen boroo isaaniitiin seenuu keessanii mitiGaruu dalagaan gaariin (iimaana) nama Rabbiin sodaatuutiManneen balbaluma isheetiin seenaa; akka milkooftaniif jecha Rabbiinis sodaadhaa.');
INSERT INTO "apaguna" VALUES(2,190,'2:190','Karaa Rabbii keessattis warra isiniin lolaniin lolaaDaangaas hin dabrinaaRabbiin warra daangaa dabran hin jaalatu.');
INSERT INTO "apaguna" VALUES(2,191,'2:191','Warra isiniin lolan) bakkuma isaan argitanitti isaan ajjeesaaIddoo isaan isin baasan irraas isaan baasaaShirkiitu ajjeechaa caalaa hamaadhaMasjidal-Haraam biratti, yoo isaan isa keessatti isin waraanan malee isin isaan hin waraaninaaYoo isaan isin waraanan, isinis isaan ajjeesaa; galanni kaafirootaa akkuma kana.');
INSERT INTO "apaguna" VALUES(2,192,'2:192','Yoo isaan (waraana) dhaabanii amantii seenan Rabbiin akkaan mararfataa rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,193,'2:193','Hanga shirkiin hafee amantiin Rabbiif qofa ta’utti isaaniin lolaaYoo isaan (lola) dhiisan, warra miidhaa hojjatu irratti malee diinomuun hin jiru.');
INSERT INTO "apaguna" VALUES(2,194,'2:194','Baatiin kabajamaan baatuma sana keessa (galata galfama); kabajaan gamaa gamanaayi (yoo isaan baaticha san kabajan isiniis kabajju)Nama isin irratti daangaa dabre isinis fakkaatuma waan inni isin irratti daangaa dabreetiin isa irratti daangaa dabraaRabbiin sodaadhaa; Rabbiin warra Isa sodaatuu wajjiin ta’uus beekaa.');
INSERT INTO "apaguna" VALUES(2,195,'2:195','Karaa Rabbii keessattis arjoomaaHarka keessaniin gara badiittis of hin darbinaaToltuus hojjadhaa; Dhugumatti, Rabbiin warra toltuu hojjatan ni jaalata.');
INSERT INTO "apaguna" VALUES(2,196,'2:196','Hajjiifi umraa Rabbiif jecha guutaaYoo dhorgamtan, horii wareegaa (hadyii) irraa kan isiniif laafe (qalaa)Hanga hadyiin iddoo (qalma) isaa ga’uttis mataa keessan hin haaddatinaaNamni isin irraa dhukkubsate yookiin mataa isaa irra rakkoon jiraate, soomuun yookiin sadaqachuun yookiin qaluun ''fidyaa'' haa baasuYeroo nagaa taatan, namni umraadhaan hanga hajjiitti qanani''e hadyii irraa waan isaaf laafetu irra jiraNamni hin argatin, guyyaa sadi hajjii keessatti, (guyyaa) torba immoo yeroo deebitan (soomuu qabdu)Sun (guyyaa) kudhan guutuudha(Hadyiin yookiin soomuun) sun nama maatiin isaa masjiidal-Haraam bira (jiraataa Makkaa) hin ta’iniif qofaRabbiin sodaadhaa; adabbiin Rabbii cimaa ta’uus beekaa');
INSERT INTO "apaguna" VALUES(2,197,'2:197','Hajjiin ji’oota beekamoodhaNamni isaan keessa hajjii ofitti murteesse (ihraama godhate), hajjii keessatti qunnamtii saalaa gochuu, finciluufi falmii gaggeessuu hin qabuToltuu irraa waan isin hojjattan Rabbiin akkaan ni beekaGalaa guduunfadhaa; dhugumatti caalaan galaa sodaa RabbiitiYaa warra sammuu qabdan Anuma sodaadhaa!');
INSERT INTO "apaguna" VALUES(2,198,'2:198','(Yeroo hajjii keessa) tola Rabbii keessanii irraa yoo barbaaddattan badiin isin irra hin jiruYeroo Arafaa irraa dachaatanis Mash’aral Haraam biratti Rabbiin zakkaraaAkkuma Inni isin qajeelchetti isa zakkaraa; sanaan dura warra jallatoo irraa turtani.');
INSERT INTO "apaguna" VALUES(2,199,'2:199','(Yaa Qurayshotaa!) ergasii bakka namoonni yaa''aniin yaa''aaRabbiin araarama gaafadhaaDhugumatti, Rabbiin mararfataa rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,200,'2:200','Yeroo hojii hajjii raawwattan, akkuma abbootii keessan faarsitanitti yookiin faaruu sana caalu Rabbiin zakkaraaNamoota irraa nama odoo Aakhiraatti carraa hin qabne “yaa Rabbii keenya addunyaa irratti (gaarii) nuuf kenni” jedhutu jira.');
INSERT INTO "apaguna" VALUES(2,201,'2:201','Isaan irraas nama "Gooftaa keenya! Duuniyaatti toltuu, Aakhirattis toltuu nuuf kenni; adabbii ibiddaa irraa nu baraari" jedhantu jiru.');
INSERT INTO "apaguna" VALUES(2,202,'2:202','Warra san isaaniif waan hojjatan irraa qoodatu jiraRabbiin saffisaa qormaataati.');
INSERT INTO "apaguna" VALUES(2,203,'2:203','Guyyoota lakkaa’amtuu keessa Rabbiin yaadadhaaNamni guyyaa lama keessatti ariifate badiin isarra hin jiruNamni booda aanes badiin isa irra hin jiru(Kun) nama (Rabbiin) sodaateefiRabbiin sodaadhaa; gara Isaatti oofamuu keessanis beekaa.');
INSERT INTO "apaguna" VALUES(2,204,'2:204','Namoota irraa nama haala falmiidhaan jabaa ta''een jechi isaa addunyaa irratti si dinqisiisuufi kan waan qalbii isaa keessa jiru irratti Rabbiin ragaa godhutu jira');
INSERT INTO "apaguna" VALUES(2,205,'2:205','Yeroo gara gales, ishee keessatti badii raawwachuuf, sanyiifi horii balleessuuf dachii keessa fiigaRabbiin badii hin jaalatu.');
INSERT INTO "apaguna" VALUES(2,206,'2:206','Yeroo "Rabbiin sodaadhu" isaan jedhames, of tuulummaan badiitti isa kakaasaGa’aan Isaa jahannamiAfanni (jahannam ta’uun) waa fokkate!');
INSERT INTO "apaguna" VALUES(2,207,'2:207','Nama irraas nama jaalala Rabbii barbaachaaf lubbuu ofii gurgurutu jiraRabbiinis gabroota akkaan mararfataadha.');
INSERT INTO "apaguna" VALUES(2,208,'2:208','Yaa warra amantan! Islaamummaa keessa guutuu guutuutti seenaa; tarkaanfilee shayxaanaas hin hordofinaaDhugumatti, inni (shayxaanni) isiniif diina ifa bahaadha.');
INSERT INTO "apaguna" VALUES(2,209,'2:209','Erga ragaawwan ifa ta’an isiniif dhufanii booda (daandii Islaamaa irraa) yoo mucucaattan, Rabbiin injifataa, ogeessa ta’uu beekaa.');
INSERT INTO "apaguna" VALUES(2,210,'2:210','Sila Rabbiifi malaa’ikoonni, gaaddisa duumessa irraa ta’e keessaan isaanitti dhufuufi murteen raawwatamuu malee waan biraa eeguu? Wantoonni hunduu gamuma Rabbii deebifamti.');
INSERT INTO "apaguna" VALUES(2,211,'2:211','Mallattoolee ifa ta’an meeqaatama akka isaaniif kennine Ilmaan Israa’iil gaafadhuNamni erga isatti dhufee booda ni’imaa Rabbii jijjiire (adaba cimaatu isa mudata)Dhugumatti Rabbiin irra cimaa adabaati.');
INSERT INTO "apaguna" VALUES(2,212,'2:212','Isaan kafaraniif jireenyi addunyaa miidhagfameera; warra amananittis ni qoosuWarri Rabbiin sodaatan immoo Guyyaa Qiyaamaa isaanii ol ta’uRabbiinis nama fedheef (tola Isaa irraa) lakkoofsa malee soorata kenna.');
INSERT INTO "apaguna" VALUES(2,213,'2:213','Namoonni amantii tokkicha irra turan; ergasii Rabbiin nabiyyoota gammachiiftotaafi akeekkachiiftota taasisee ergeIsaan waliinis kitaaba akka inni waan isaan isa keessatti wal dhaban gidduu namootaatti murteessuuf dhugaan buuseWarra inni kennameef qofatu erga ragaawwan ifa ta’an isaaniif dhufanii booda wal jibbaa gidduu isaanii jiru irraa kan ka’e wal dhabeRabbiin fedha Isaatiin warra amanan gara dhugaa waan isaan irratti wal dhabanii qajeelcheRabbiin nama fedhe gara karaa sirraawaa qajeelcha.');
INSERT INTO "apaguna" VALUES(2,214,'2:214','Sila odoo (mokkoroon) fakkaataa warra isin dura dabranii isinitti hin dhufin jannata seenuu yaadduu? Rakkinniifi dhukkubni hamtuun isaan tuqxee, hanga ergamaaniifi warri isa waliin amanan "sila tumsi Rabbii yoom (dhufa)?" jedhan tokkotti shororkeeffamanEeyyeen! Dhugumatti, tumsi Rabbii dhihoodha.');
INSERT INTO "apaguna" VALUES(2,215,'2:215','(Yaa Muhammad!) maal akka sadaqatan si gaafatu“Qabeenya irraa wanti kennattan abbaa haadha, firoota, yatiimota, hiyyeeyyiifi kara deemaaf ta’uu qabaToltuu irraa waan isin hojjattan dhugumatti Rabbiin akkaan isa beekaadha” jedhi.');
INSERT INTO "apaguna" VALUES(2,216,'2:216','Odoma inni isin biratti jibbamaa ta’uu lolli dirqama isin irratti godhameOdoo inni isiniif gaarii ta''uu waan tokko jibbuun keessan mirkanaa’eOdoo inni isiniif hamtuu ta’uu waan tokko jaalachuun keessanis mirkanaa’eRabbumatu beeka; isin hin beektanu');
INSERT INTO "apaguna" VALUES(2,217,'2:217','Ji’oota kabajamoo keessa loluu irraa si gaafatuJedhi: "Isa keessa loluun (yakka) guddaadhaKaraa Rabbii irraa (namoota) deebisuun, Isatti kafaruun, namoota Masjidal Haraam irraa dhorguuniifi jiraattota isaa achi irraa baasuutu Rabbiin biratti irra guddaadhaShirkiitu ajjeechaa irra (badiin isaa) guddaadhaYoo danda’an hanga diinii keessan irraa isin deebisanitti isiniin loluu irraa hin deemanNamni isin irraa diinii isaa irraa garagalee kaafira ta’ee du’e, warri sun addunyaafi Aakhiraattis dalagaaleen isaanii baddee jirtiAmmas warri sun warra ibiddaati; isaan achi keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(2,218,'2:218','Dhugumatti, warri amananiifi warri godaananii, karaa Rabbii keessattis qabsaa’an, isaan sun rahmata Rabbii ni kajeeluRabbiinis araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,219,'2:219','Dhugaatii macheessaniifi qumaara irraa si gaafatuJedhi: "Dilii guddaafi faayidaawwantu namootaaf isaan keessa jiraDiliin isaan lamaanii faayidaa isaan lamaanii irra guddaadha." Ammas maal akka arjoomanu si gaafatu“Isa dhimma keessan irraa hafe” jedhiinAkka xiinxaltaniif jecha Rabbiin akka kanatti keeyyattoota isaa isiniif ibsa.');
INSERT INTO "apaguna" VALUES(2,220,'2:220','Duuniyaafi Aakhiraa keessatti (haa xiinxaltaniifi)Waa’ee yatiimotaa irraas si gaafatu“Isaaniif tolchuutu caalaadha” jedhiYoo isaanitti makamtanis isaan obboleeyyan keessaniRabbiinis nama balleessu isa tolchu irraa adda baasee beekaRabbiin odoo fedhee, isin rakkisuu danda’aDhugumatti, Rabbiin akkaan injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(2,221,'2:221','Hanga isaan amananutti (dubartoota) mushrikaa hin fuudhinaaOdoma isheen isin gammachiifteeyyuu gabrittii mu’uminaatu mushrika caaltiYoo amanan malee dhiira mushrikootaattis hin heerumsiisinaaOdoma (inni) isin gammachiiseeyyuu gabricha mu’uminaatu mushrika irra caalaWarri sun gara ibiddaatti waamu; Rabbiin immoo heyyama isaatiin gara jannataafi araaramaatti waamaHaa xiinxalaniif jecha aayatoota isaa namootaaf ibsa.');
INSERT INTO "apaguna" VALUES(2,222,'2:222','Dhiiga laguu (haydii) irraas si gaafatu “Inni (iddoon isaa) faalamaadha; kanaafuu yeroo dhiiga xurii keessa dubartoota irraa fagaadhaa” jedhiHanga isaan qulqullaa’anittis isaanitti hin dhihaatinaaYeroo isaan qulqulleeffatan, gara Rabbiin isin ajajeen isaan bira dhaqaaDhugumatti, Rabbiin warra gara Isaa tawbataniifi qulqulleeffatan ni jaalata.');
INSERT INTO "apaguna" VALUES(2,223,'2:223','Niitiiwwan keessan isiniif oyruudhaOyruu keessan kallattii feetaniin itti dhaqaaLubbuu keessaniif gaarii dabarfadhaaRabbiinis sodaadhaa; Isa qunnamuu keessanis beekaaMu’uminootas gammachiisi.');
INSERT INTO "apaguna" VALUES(2,224,'2:224','Akka toltuu hin hojjanne, Rabbiin hin sodaanneefi gidduu namootaatti araara hin buufne Rabbiin kakachuu keessan sababaa hin taasifatinaaRabbiinis dhagayaa, beekaadha.');
INSERT INTO "apaguna" VALUES(2,225,'2:225','Rabbiin kakuu keessan keessatti isa itti hin yaadaminiif isin hin qabuGaruu waan onneen hojjataniif isin qabaRabbiinis araaramaa, obsaadha.');
INSERT INTO "apaguna" VALUES(2,226,'2:226','Warra haadha manaa isaanii irraa kakataniif ji’oota afur eeguutu isaan irra jiraYoo deebi’an dhugumatti Rabbiin akkaan araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(2,227,'2:227','Yoo hiikkaa irratti muratan, dhugumatti Rabbiin dhagayaa, beekaadha.');
INSERT INTO "apaguna" VALUES(2,228,'2:228','Dubartoonni hiikaman xurii laguu sadi ofii isaaniitiif eeguu qabuYoo Rabbiifi Guyyaa Aakhiraatti kan amanan ta’an waan Rabbiin gadaamessa isaanii keessatti uume dhoksuun isaaniif halaala mitiYeroo san keessatti yoo tolchuu fedhan isaan deebifachuu keessatti irra caalaa mirga kan qaban dhirsota isaaniitiIsaaniifis (mirga) fakkaataa akka (dirqama) isaan irra jiruutu haala gaariin isaaniif jiraDhiironni isaan irra sadarkaa qabuRabbiinis hunda injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(2,229,'2:229','Hiikkaan (eega isaatii deebifatamu) yeroo lama.[] Sana booda haala gaariin qabachuu yookiin bifa gaariin gadi lakkisuudhaIsin (dhiironni) waan isaaniif kennitan (mahrii) irraa isaan lamaanuu daangawwan Rabbii sirreessuu dhabuu yoo sodaatan malee homaa fudhachuun isiniif hin hayyamamuYoo isaan lamaan daangawwan Rabbii hin sirreessanu jechuu sodaattan, waan isheen ittiin of baafte fudhachuu keessatti isaan lamaan irra badiin hin jiruKun daangawwan Rabbiitii isaan hin dabrinaa; namni daangaa Rabbii dabre, isaan sun isaanumatu miidhaa hojjatoodha.');
INSERT INTO "apaguna" VALUES(2,230,'2:230','Yoo (hiikkaa sadaffaa) ishee hiike, sana booda hanga dhirsa biraatti heerumtutti isaaf hin heyyamamtuYoo inni (dhirsi boodaa) ishee hiike, daangaa Rabbii eeguu isaanii beeknaan isaan lamaan yoo walitti deebi’an badiin isaan irra hin jiruKun daangawwan Rabbiiti; ummata beekaniif ishee ibsa.');
INSERT INTO "apaguna" VALUES(2,231,'2:231','Yeroo dubartoota hiiktanii beellamni isaaniis ga’e; bifa gaariin isaan qabadhaa yookiin bifa gaariin isaan gaggeessaaRakkisuudhaan akka daangaa isaanitti dabartaniif isaan hin qabatinaaNamni sana hojjate, dhugumatti lubbuu ofii miidheeraKeeyyattoota Rabbiis qishinaa hin taasifatinaaNi’imaa Rabbii kan isin irra jirtuufi kitaaba (Qur’aana)fi hikmaa (Sunnaa) irraas waan Inni isin irratti buuse kan ittiin isin gorsu yaadadhaaRabbiin sodaadhaasAkka Rabbiin waan hunda beekaa ta’es beekaa.');
INSERT INTO "apaguna" VALUES(2,232,'2:232','Yeroo dubartoota hiiktanii beellama isaanii guuttatan, yoo isaan lamaan waan gaarii irratti walii galan dhirsoota isaanii (kan jalqabaatti) heerumuu irraa isaan hin dhorginaaSana isin irraa nama Rabbiifi Guyyaa Aakhiraatti amanutu ittiin gorfamaSun isiniif irra gaariifi irra qulqulluudhaRabbitu beekaa; isin hin beektanu.');
INSERT INTO "apaguna" VALUES(2,233,'2:233','Nama yeroo hoosisaa guutuu fedheef haadholiin ilmaan isaanii waggaa lama guutuu hoosisuSoorataafi uffata isaanii (haadholeef) akka dandeettii isaatiin kennuun nama ilmi isaaf dhalate (abbaa) irra jiraLubbuun tokkos dandeettii isheetii ol hin dirqisiifamtuHaati sababaa ilma isheetiin hin rakkifamtu; namni ilmi isaaf dhalates (abbaanis) sababaa ilma isaatiin (hin rakkifamu)Nama (abbaa) dhaalu irras fakkaataa sanaatu jiraYoo isaan lamaan fedha isaaniitiifi wal mariin guusisuu fedhan, badiin isaan irra hin jiruYoo ilmaan keessaniif hoosiftuu gootanii hoosisuu feetanis, yoo waan kennitan akkaataa gaariin kennitan, badiin isin irra hin jiruRabbiin sodaadhaa, Rabbiin waan isin dalagdan argaa ta’uu beekaa.');
INSERT INTO "apaguna" VALUES(2,234,'2:234','Isin irraas isaan du’anii niitii of duubatti dhiisan, isaan (niitiiwwan kun) ji’oota afuriifi guyyaa kudhan turuu qabuYeroo beellama isaanii guuttatan, waan isaan karaa gaariin (heerumuuf) of qopheessan keessa badiin isin irra hin jiruRabbiin waan isin hojjattan beekaadha.');
INSERT INTO "apaguna" VALUES(2,235,'2:235','Waan dubartoota (dhirsi irraa du’e iddaan isaanii xumuramuu dura) mallattoon isaan dubbatachuu mul’iftan yookiin (waan) onnee keessan keessatti dhoksitan keessatti badiin isin irra hin jiruAkka isin isaan dubbatuuf jirtan Rabbiin beekeeraGaruu yoo jecha beekamaa jechuu ta’e malee, iccitiidhaan waadaa waliif hin seeninaaHanga beellamni daangaa ga’utti nikaah hidhuu hin murteessinaaRabbiin waan lubbuu keessan keessa jiru beekaa ta''uus beekaa; Isa sodaadhaaRabbiin araaramaa, obsaa ta’us beekaa.');
INSERT INTO "apaguna" VALUES(2,236,'2:236','Dubartoota odoo isaan hin tuqin yookiin mahrii isaaniis isaaniif hin murteessin yoo hiiktan badiin isin irra hin jiru; kennaa itti fayyadaman isaaniif kennaaNamni qabu akkaatuma dandeettii isaatiin, kan hin qabnes akkaatuma dandeettii isaatiin bifa gaariin kennaa isaan itti fayyadamanu (kennuu qaba)Kuni warra toltuu hojjatan irratti dirqama.');
INSERT INTO "apaguna" VALUES(2,237,'2:237','Odoo mahrii isaaniif murteessitanii jirtanuu isaan tuquun dura yoo isaan hiiktan, walakkaa waan murteessitaniitu (isheef jira), garuu yoo isaan (duubartoonni) dhiisan yookiin inni hidhaan nikaa harka isaa jiru (dhirsi) dhiise (walakkaan dirqamaa miti)Isin dhiisuutu sodaa (Rabbiitti) irra dhiyoodhaTola giddu keessanii hin irraanfatinaa; Rabbiin waan isin hojjattan argaadha.');
INSERT INTO "apaguna" VALUES(2,238,'2:238','Salaatowwan irratti, keessattuu salaatattii giddu galeessaa irratti tikfadhaaHaala Rabbiif ajajamoo taataniinis dhaabbadhaa.');
INSERT INTO "apaguna" VALUES(2,239,'2:239','Yoo (diina) sodaattanis, miilaan deemaa yookiin yaabbatoo ta''aa sagadaaYoo (lola keessaa) gara nagaa baatan, Rabbiin, akkuma waan (san dura) isin hin beekne Inni isin barsiisetti yaadadhaa.');
INSERT INTO "apaguna" VALUES(2,240,'2:240','Isin irraa isaan du’anii, niitiiwwan of duubatti dhiisan, niitiiwwan isaaniitiif (qabeenya dhiisanii du’an irraa) waan odoo mana isaanii irraa hin baafamne hanga waggaa tokkoo isaan itti fayyadaman isaaniif haa dhaamanYoo isaan (niitiiwwan fedha isaanitiin) bahan, waan shari’aa keessatti beekamaa irraa isaan ofii isaaniitii hojjataniif badiin isin irra hin jiruRabbiinis injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(2,241,'2:241','Dubartoota hiikamaniifis wanti isaan ittiin fayyadaman haala sababa qabeessa ta’een kennamuufii qaba(Kun) warraRabbiin sodaatan irratti haqa.');
INSERT INTO "apaguna" VALUES(2,242,'2:242','Akka isin xiinxaltaniif akkanatti Rabbiin keeyyattoota Isaa isiniif addeessa.');
INSERT INTO "apaguna" VALUES(2,243,'2:243','Sila warra manneen isaanii irraa du’a sodaatanii kumaatama ta’anii ba’anii (qalbiin) hin ilaallee? Rabbiin “du’aa!” isaaniin jedhee, sana booda isaan jiraachiseDhugumatti, Rabbiin namoota irratti abbaa tola (guddaa)tiGaruu irra hedduun namaa hin galateeffatan.');
INSERT INTO "apaguna" VALUES(2,244,'2:244','Karaa Rabbii keessatti lolaaRabbiin dhagayaa, beekaa ta’uus beekaa.');
INSERT INTO "apaguna" VALUES(2,245,'2:245','Namni liqii gaarii Rabbiif liqeessee Inni dachaa baay’ee isaaf baay’isu eenyuma! Rabbiin nama fedhe (rizqii) irraa qabata; kan fedhe immoo itti baldhisaIsinis gara Isaatti deebifamtu.');
INSERT INTO "apaguna" VALUES(2,246,'2:246','Sila gara qondaaltota Ilmaan Israa’iil irraa ta’an kan booda Muusaa, yeroo isaan nabiyyii isaaniitiin "hogganaa nuuf godhi karaa Rabbii keessatti lollaa" jedhan hin ilaallee? [Inni] "Sila yoo lolli isin irratti murteeffame loluu dhabuu keessan hin sodaattamuu?" jedhe"Odoo manneeniifi ilmaan keenya irraa baafamnee jirruu maaltu nuuf jiraayyi karaa Rabbii keessatti hin lolle?" jedhanYeroma lolli isaan irratti murteeffamus namoota muraasa isaan irraa ta’an malee irraa gara galanRabbiin miidhaa raawwattoota ni beeka.');
INSERT INTO "apaguna" VALUES(2,247,'2:247','Nabiyyiin isaaniis isaaniin jedhe: "Dhugumatti, Rabbiin Xaaluutiin mootii godhee isiniif ramadeera." [Isaan] "Sila odoo nuti mootummaaf isarra abboomnuu, inni qabeenya ga’aas argatee hin beekne akkamiin nurratti mootummaan isaaf ta’a?" jedhanNi jedhe: "Dhugumatti, Rabbiin isin irra isa filatee beekumsaafi qaama keessattis bal''ina isaaf dabaleRabbiin mootummaa Isaa nama fedheef kennaRabbiin (arjummaan isaa) bal’aa, beekaadha."');
INSERT INTO "apaguna" VALUES(2,248,'2:248','Nabbiyyiin isaaniis isaaniin ni jedhe: "Dhugumatti, mallattoon mootummaa isaa sanduuqni malaa’ikoonni isa baadhatan kan isa keessa tasgabbiin Gooftaa keessan biraa ta’eefi hanbaan waan maatiin Muusaafi Haaruun dhiisan irraa ta’e isa keessa jiru isinitti dhufuudhaDhugumatti, yoo mu’uminoota taataniif, kana keessa mallattootu isiniif jira."');
INSERT INTO "apaguna" VALUES(2,249,'2:249','Xaaluutis yeroma waraana (isaa) qabatee ba’u ni jedhe: "Dhugumatti, Rabbiin laga wahiitiin isin mokkoraadhaNama harka isaatiin hammaarrii tokkittii hammaarrate malee namni isa irraa dhuge anarraayii mitiNamni isa hin dhandhamin immoo anarraayyiMuraasa isaan irraa ta''e malee isa irraa dhuganiYeroma inniifi warri isaa wajjin amanan isa qaxxaamuran, (warri dhugan) ni jedhan: "Har''a Jaaluutiifi waraana isaa irratti dandeettiin nuuf hin jiru." Warri Rabbiin wal qunnamoo ta''uu isaanii dhugoomsan ni jedhan: "Garee muraasa heddutu hayyama Rabbiitiin garee baay’ee injifate." Rabbiinis warra obsan wajjin jira.');
INSERT INTO "apaguna" VALUES(2,250,'2:250','Yeroma Jaaluutiifi waraana isaatiif dirreetti bahan ni jedhan: “Gooftaa keenya! Obsa nurratti dhangalaasiFaana keenyas gadi dhaabi; ummata kaafirootaa irrattis nu tumsi."');
INSERT INTO "apaguna" VALUES(2,251,'2:251','Hayyama Rabbiitiin isaan injifataniDaawuudis Jaaluutiin ajjeesee Rabbiin mootummaafi ogummaa isaaf kennee waan fedhe irraas isa barsiiseOdoo nama garii gariin deebisuun Rabbii jiraachuu baatee, silaa dachiin baddeGaruu Rabbiin aalama hunda irratti abbaa tola guddaati.');
INSERT INTO "apaguna" VALUES(2,252,'2:252','Kun keeyyattoota RabbiitiDhugaadhaan ishee sirratti dubbifnaAtis dhugumatti ergamtoota irraayyi.');
INSERT INTO "apaguna" VALUES(2,253,'2:253','Ergamoota kana garii isaanii garii irra caalchifneIsaan irraa nama Rabbiin ittiin dubbatetu jiraGarii isaaniis sadarkaa heddu ol kaaseIisaa ilma Maryamiifis ragaawwan ifa ta’an kenninee jirraRuuhii qulqulluunis (Jibriiliiniis) isa jajjabeessineOdoo Rabbiin fedhee, warri booda isaanii erga ragaawwan ifa taate isaanitti dhuftee booda wal hin lolan turanGaruu isaan wal dhabaniIsaan irraa namni amane ni jira; isaan irraas kan kafare ni jiraOdoo Rabbiin fedhee, wal hin lolanGaruu Rabbiin waan fedhe hojjata.');
INSERT INTO "apaguna" VALUES(2,254,'2:254','Yaa warra amantan! Odoo guyyaan bittaa gurgurtaan, jaalalleefi manguddummaan isa keessa hin jirre hin dhufin dura waan Nuti isiniif kennine irraa kennadhaaKaafiroonni isaanumatu miidhaa raawwattoota.');
INSERT INTO "apaguna" VALUES(2,255,'2:255','Rabbiin Isa malee haqaan gabbaramaan hin jiru; (Inni) jiraataadha; waan hundaan dhaabbataadhaMugaatiis ta’ee hirribni Isa hin qabatuWanti samii keessa jiruufi wanti dachii keessa jiru (hundi) kan IsaatiInni hayyama Isaatiin ala Isa biratti araarsu eenyuma? Waan fuuldura isaaniitiifi waan duuba isaaniis ni beekaBeekumsa Isaa irraas waan Inni fedhetti malee hin marsanuKursiin Isaa samiifi dachii irra bal’ateeraIsaan lamaan tiksuun Isa hin dadhabsiisuInnis ol ta’aa, guddaadha.');
INSERT INTO "apaguna" VALUES(2,256,'2:256','Amantii keessa dirqisiisuun hin jiruDhugumatti, qajeelumni jallina irraa ifa ta''eeraNamni xaaghuutatti kafaree, Rabbitti amane, dhugumatti haada jabaa hin cinne qabateeraRabbiin dhagayaa, beekaadha.');
INSERT INTO "apaguna" VALUES(2,257,'2:257','Rabbiin gargaaraa warra amananiitiDukkana heddu irraa gara ifaatti isaan baasaWarri kafaran gargaartonni isaanii xaaghuuta; Ifa irraas gara dukkana hedduutti isaan baasuIsaan sun warra ibiddaati; isaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(2,258,'2:258','Sila gara isa Rabbiin mootummaa isaaf kenninaan (waa’ee) Gooftaa isaa keessatti Ibraahiimiin mormee hin ilaallee? Yeroo Ibraahiim "Gooftaan kiyya Isa jiraachisuufi ajjeesu" jedhu "Anis nan jiraachisa; nan ajjeesas" jedheIbraahiim "Dhugumatti, Rabbiin aduu Baha irraa fida; ati immoo Dhiha irraa fidi" jedheAchumaan inni kafare dhama''eRabbiin miidhaa hojjattoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(2,259,'2:259','Yookiin gara namicha haala (manneen ishee) mataa ishee irratti kukkuftee jirtuun ganda irra dhufee "akkamumatti Rabbiin erga du’a isheetii ishee kana jiraachisa!" jedhee (hin ilaallee?) Rabbiiniis waggaa dhibba isa ajjeesee ergasii isa kaasee "hangam turte?" jedheen"Guyyaa tokko, yookiingarii guyyaan ture" jedhe(Rabbiiniis) ni jedhe: "Ati waggaa dhibba turteMee gara nyaataafi dhugaatii keetii ilaali; hin jijjiiramne! Gara harree keetiis ilaali! Namootaaf mallattoo si taasisuuf (kana goone)Akkamitti akka walitti qabnuufi ergasii foonis itti uffifnu gara lafees ilaali." Yeroma ifa isaaf ta’u "Rabbiin waan hunda irratti danda’aa ta’uu nan beeka" jedhe.');
INSERT INTO "apaguna" VALUES(2,260,'2:260','Yeroo Ibraahiim "Gooftaa kiyya! Akkamitti du’aa akka kaaftu natti agarsiisi" jedhes [yaadadhaa](Rabbiiniis) "Sila hin amannee?" jedhe(Ibraahiimis): "Lakkii (amaneera), Garuu akka onneen kiyya tasgabbaa’uufi" jedhe"Simbira irraa afur gara keetti fuudhii isaan kukkutiSana booda isaan irraa qooda wahii gaara hunda irra kaayiErgasii isaan waami; fiigaa sitti dhufu.” Rabbiin injifataa, ogeessa ta’uus beeki.');
INSERT INTO "apaguna" VALUES(2,261,'2:261','Fakkeenyi warra qabeenya isaanii karaa Rabbii keessatti arjoomanii, akka fakkeenya firii sootalloo (mataa) torba baasee sootalloo hunda keessa firiin dhibbi jiruutiRabbiin nama fedheef dachaa taasisaRabbiin (kenni isaa) bal’aa, beekaadha.');
INSERT INTO "apaguna" VALUES(2,262,'2:262','Warri qabeenya isaanii karaa Rabbii keessatti kennatanii, ergasii waan arjooman dhaaddannaafi rakkisuu hin hordofsiisin, isaaniif mindaan Gooftaa isaanii bira jiraSodaanis isaan irra hin jiru; isaan hin gaddanus.');
INSERT INTO "apaguna" VALUES(2,263,'2:263','Jecha gaariifi dhiifamatu sadaqaa rakkisuun isa hordofu irra caalaRabbiinis dureessa, obsaadha.');
INSERT INTO "apaguna" VALUES(2,264,'2:264','Yaa warra amantan! Sadaqaa keessan akka isa qabeenya isaa namoonni isaaf haa arganiif arjoomuu fi, Rabbiifi guyyaa Aakhiraattis hin amannetti dhaaddannaafi (nama) rakkisuudhaan hin balleessinaaFakkeenyi isaa akka fakkeenya dhagaa diriiraa biyyeen isa irra jiruu kan bokkaan cimaan isa tuqee (biyyee irraa dhiquun) duwwaa isa hanbiseetiIsaan waan horatan irraa homaa irratti dandeettii hin qabanRabbiinis ummata kaafirootaa hin qajeelchu.');
INSERT INTO "apaguna" VALUES(2,265,'2:265','Fakkeenyi warra qabeenya isaanii jaalala Rabbii barbaaduufi lubbuu isaanii raggaasisuuf arjoomanii, akka fakkeenya ashaakiltii lafa ol fuudhamaa irra jiruu bokkaan cimaan itti roobee oomisha ishee dachaan kennitee yoo bokkaan guddaan ishee hin argatinis tiifuun (of geessuuti)Rabbiin waan isin dalagdan argaadha.');
INSERT INTO "apaguna" VALUES(2,266,'2:266','Sila tokkoon keessan ashaakiltiin Nakhliifi Inaba irraa ta’e, kan laggeen jala ishee yaatu, kan isa keessa fuduraaleen hundi isaaf jiru, odoo ilmaan dadhabduu qabuu kan dullumni isa qabde, bubbeen ibidda qabu ishee tuqee gubatte isaaf ta''uu ni jaalataa? Akka isin xiinxaltaniif jecha akka kanatti Rabbiin keeyyattoota Isaa isiniif addeessa');
INSERT INTO "apaguna" VALUES(2,267,'2:267','Yaa warra amantan! gaggaarii waan carraaqqattaniifi waan Nuti dachii irraa isiniif baafne irraa kennadhaa(Odoo isiniif kennamee) Isa keessatti ija dunuunfattanii malee kan hin fudhanne taatanii, isa irraa kennachuudhaan Waan fokkataa hin yaadinaaRabbiin dhugumatti dureessa, faarfamaa ta’uu beekaa.');
INSERT INTO "apaguna" VALUES(2,268,'2:268','Shayxaanni hiyyummaan isin sodaachisa, wantoota fokkataattis isin ajajaRabbiin garuu araaramaafi tola Isa biraa ta’e waadaa isiniif galaRabbiin (kenni isaa) bal’aa, beekaadha.');
INSERT INTO "apaguna" VALUES(2,269,'2:269','Inni hikmaa[] nama fedheef kennaNamni hikmaa kenname, dhugumatti toltuun baay’een isaaf kennameeraWarra sammuu qaban malee eenyullee hin gorfamu.');
INSERT INTO "apaguna" VALUES(2,270,'2:270','Kenna irraa waan isin kennattan yookiin qodhaa (nazrii) irraa waan nazartanis, Rabbiin isa beekaMiidhaa hojjattootaafis tumsitoonni hin jiranu.');
INSERT INTO "apaguna" VALUES(2,271,'2:271','Sadaqaa yoo mul’iftanii (kennattan) isheen waa tolte; yoo ishee dhoksitanii hiyyeeyyiif kennitanis isatu isiniif caalaHamtuuwwan keessanis isin irraa harcaasaRabbiin waan isin dalagdan keessa beekaadha.');
INSERT INTO "apaguna" VALUES(2,272,'2:272','Qajeelchuun isaanii (dirqama) sirratti hin taaneGaruu Rabbiin nama fedhe ni qajeelchaQabeenya irraas wanti isin kennattan lubbuma keessaniifiFuula Rabbii barbaachaaf malee hin kennattanuQabeenya irraa wanti isin kennattan haala isin kan hin miidhamne taataniin guutamee isiniif deebifama.');
INSERT INTO "apaguna" VALUES(2,273,'2:273','(Sadaqaan) hiyyeeyyii warra dachii keessa deemuu hin dandeenye, kan kadhaa irraa of qabuu isaanii irraa wallaalaan dureeyyii isaan se’u, mallattoolee isaaniitiin kan isaan beektu, namoota kan hin kadhanne, ifachiisuus (kan hin ifachiifne) ta’anii karaa Rabbii keessatti hidhamaniif (kennamti)Qabeenya irraa waan isin kennattan dhugumatti Rabbiin isa beeka.');
INSERT INTO "apaguna" VALUES(2,274,'2:274','Warra qabeenya isaanii halkaniifi guyyaa, dhoksaafi ifatti mul’isanii arjooman, isaaniif mindaan isaanii Gooftaa isaanii bira jiraSodaanis isaan irra hin jiru; isaanis hin gaddanu.');
INSERT INTO "apaguna" VALUES(2,275,'2:275','Warri dhala nyaatan (Guyyaa Qiyaama) akka isa shayxaanni maraatummaa irraa isa kukuffisu dhaabbatuutti malee hin dhaabbatanuKunis waan isaan "bittaa gurgurtaan akkuma dhalaati" jedhaniifiRabbiin immoo bittaa gurgurtaa hayyamee dhala dhorgeNamni gorsi Gooftaa isaa irraa itti dhufee (dhala nyaachuu irraa) dhorgame, wanti (dhorgaan dura) darbe kanuma isaatiDhimmi isaas (waan dhufu keessatti) gara RabbiitiNamni (itti) deebi’e immoo isaan warra ibiddaati; isaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(2,276,'2:276','Rabbiin dhala (keessaa barakaa) ni haqa; sadaqaawwan immoo ni guddisaRabbiin warra akkaan kafaraa, dilaawaa hunda hin jaalatu.');
INSERT INTO "apaguna" VALUES(2,277,'2:277','Dhugumatti, isaan amananii gaggaariis hojjatanii, salaatas gadi dhaabanii, zakaas kennan, warri sun mindaan isaanii gooftaa isaanii bira isaaniif jiraSodaanis isaan irra hin jiru; isaanis hin gaddanu.');
INSERT INTO "apaguna" VALUES(2,278,'2:278','Yaa warra amantan! Rabbiin sodaadhaa; yoo mu’uminoota taataniif dhala irraa waan (nama irratti) hafe dhiisaa');
INSERT INTO "apaguna" VALUES(2,279,'2:279','Yoo (kana) hin godhin, lola Rabbiifi ergamaa Isaa irraa ta’e beekkadhaa! Yoo tawbattan mataan qabeenya keessanii isinumaaf (haqa)Hin miitanus; hin miidhamtanus.');
INSERT INTO "apaguna" VALUES(2,280,'2:280','Yoo rakkataan argameef hanga argatutti eeggachuutu (isin irra jira)(Isaanitti) saddaqachuutu irra isiniif caalaYoo kan beektan taataniif (sadaqadhaa).');
INSERT INTO "apaguna" VALUES(2,281,'2:281','Guyyaa isa keessa gara Rabbii deebitan, ergasii lubbuun hundi kan hin miidhamne taatee waan hojjatteen galata galfamtu sodaadhaa.');
INSERT INTO "apaguna" VALUES(2,282,'2:282','Yaa warra amantan! Hanga beellama beekamaatti liqii wahii yoo walii liqeessitan isa barrreessaaBarreessaan wahiis gidduu keessanitti haqaan haa barreessuBarreessaanis akka Rabbiin isa barsiisetti barreessuu hin didinHaa barreessu; Inni liqiin isa irra jirus haa dubbisuAllaah gooftaa isaa ta’es haa sodaatuIsa irraas homaa hin hir’isinYoo inni liqiin isa irra jiru gowwaa yookiin dadhabaa yookiin inni kan dubbisuu hin dandeenye ta’e firri isaa haa dubbisuRagaa lama dhiirota keessan irraa ragsiifadhaaYoo dhiirri lama hin argamin, dhiira tokkoofi dubartii lama, dagachuu ishee tokkoofi yaadachiisuu ishee birootiif jecha, ragoolee namoota isin jaallattan irraa haala ta’aniin ragaa godhaaRagoonnis yeroo waamaman hin didinXiqqaas ta’ee guddaa, hanga yeroo isaatti, isa barreessuu hin hifatinaaKanatu Rabbiin biratti irra haqa; ragummaafis irra mirkanaa’aadha; akka isin hin shakkineefis irra dhihoodhaGaruu yoo daldala bakkuma sanitti gidduu keessanitti raawwattan taate ishee barreessuu dhabuudhaan badiin isin irra hin jiruYeroo bittanii walitti gurgurtanis ragaa godhaaNamni barreessuufi kan ragaa bahus miidhamuu hin qabuYoo raawwattan, dhugumatti inni fincila (ajaja Rabbii diduu) keessaniRabbiin sodaadhaa; Rabbiin isin barsiisaRabbiin waan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(2,283,'2:283','Yoo imala irra jiraattanii barreessaa hin argatin, qabdii qabamtuu taatetu(bakka bu’a)Yoo gariin keessan garii amane immoo, inni amaname amaanaa isaa haa dhaqqabsiisu; Rabbii Gooftaa isaa ta’es haa sodaatuRagaas hin dhoksinaaNamni ishee dhokse, dhugumatti inni onneen isaa dilaawaadhaRabbiinis waan isin hojjattan beekaadha.');
INSERT INTO "apaguna" VALUES(2,284,'2:284','Wantoonni samiifi dachii keessa jiran kan RabbiitiWaan lubbuu keessan keessa jiru yoo ifatti mul’iftan yookiin yoo isa dhoksitanis Rabbiin ittiin isin qorata; nama fedheef ni araarama; nama fedhe immoo ni adabaRabbiinis waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(2,285,'2:285','Ergamaan (kiyya) waan Gooftaa isaa irraa gara isaatti buufametti amaneera; mu’uminoonnisHundumtuu Rabbitti, malaa’ikota Isaa, kitaabota Isaafi ergamtoota Isaatti amananiiru.[] "Nuti ergamtoota Isaa irraa tokkos adda hin baafnu" (jedhan)"Nuti dhageenye; ajajamnesGooftaa keenya! Araarama kee (barbaanna)Deebiinis garuma keeti" jedhan.');
INSERT INTO "apaguna" VALUES(2,286,'2:286','Rabbiin lubbuu tokkos dandeettii isheetiin ala hin dirqisiisu(Toltuu irraa) wanti isheen carraaqqate isheedhumaafi; (hamtuu irraa) wamti isheen carraaqqattes ishee irratti (ta’a)"Gooftaa keenya! Yoo daganne yookiin dogoggorre nu hin qabinGooftaa keenya! Ba’aa guddaa akka warra nuun duraa irratti baachifte nurratti hin baachisinGooftaa keenya! Waan isatti dandeettiin nuuf hin jirre nu hin baachisinIrras nuuf dabri; nuuf araaramisRahmatas nuuf godhiAti gargaaraa keenyaUmmata kaafirootaa irrattis nuuf tumsi.');
INSERT INTO "apaguna" VALUES(3,1,'3:1','Alif Laam Miim');
INSERT INTO "apaguna" VALUES(3,2,'3:2','Rabbiin Isa malee dhugaan gabbaramaan hin jiru[Inni] jiraataa, waan hundaan dhaabbataadha.');
INSERT INTO "apaguna" VALUES(3,3,'3:3','Waan isaan duraa kan dhugoomsu haala ta''een kitaaba dhugaadhaan sirratti buuseTawraatiifi Injiiliinis ni buuse.');
INSERT INTO "apaguna" VALUES(3,4,'3:4','[Qur’aana] dura namootaaf qajeelfama godhee [isaan buuse]Dhugaafi soba adda baasaas ni buuseDhugumatti, warri keeyyattoota Rabbiitti kafaran isaaniif adabbii cimaatu jiraRabbiin injifataa, abbaa haaloo bayannaati.');
INSERT INTO "apaguna" VALUES(3,5,'3:5','Dhugumatti, Rabbiin dachii keessattis ta’ee samii keessatti homtuu isa jalaa hin dhokatu.');
INSERT INTO "apaguna" VALUES(3,6,'3:6','Inni Isa gadaamessa keessatti akka fedhetti isin bocuHaqaan gabbaramaan isa malee hin jiru[Inni] injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(3,7,'3:7','Inni isa sirratti kitaaba buuseIsa irraa keeyyata ifaatu jiraIsaan bu’uura kitaabaatiKan biraa immoo wal fafakkaattuudhaWarri onnee isaanii keessa jal’inni jiru fitnaa barbaachaaf yookiin hiika isa dhokataa barbaachaaf jecha isa wal fafakkaataa hordofuHiika isaa isa dhokataa Rabbiin malee homtuu hin beekuWarri beekumsa keessa fagaatan immoo “hundinuu Rabbii keenya biraayyii itti amanne” jedhuWarra sammuu qabu malee namni biroo hin gorfamu.');
INSERT INTO "apaguna" VALUES(3,8,'3:8','[Isaanis ni jedhu]: “Gooftaa keenya! Erga nu qajeelchitee booda onnee keenya [dhugaa irraa] hin jal''isinRahmata Si biraa ta’e nuuf kenniDhugumatti Ati akkaan kennaadha.');
INSERT INTO "apaguna" VALUES(3,9,'3:9','Gooftaa keenya! Ati guyyaa isa keessa mamiin hin jirreef nama walitti qabuuf jirtaDhugumatti Rabbiin beellama kan diiguu miti" [jedhu].');
INSERT INTO "apaguna" VALUES(3,10,'3:10','Dhugumatti isaan kafaran qabeenyiifi ilmaan isaanii Rabbirraa (adaba Isaa irraa) homaa isaan hin duroomsituAmmas warri sun qoraan ibiddaati');
INSERT INTO "apaguna" VALUES(3,11,'3:11','| [Haalli isaanii] akka naamusa namoota Fir’awniifi warra isaan dura turaniitiKeeyyattoota keenya kijibsiisanii Rabbiin badii isaanitiin isaan qabeRabbiin abbaa adabbii cimaati.');
INSERT INTO "apaguna" VALUES(3,12,'3:12','Isaan kafaraniin jedhi: “fuulduratti ni injifatamtu; gara jahannamittis ni oofamtuIddoon qubannichaas fokkateera.”');
INSERT INTO "apaguna" VALUES(3,13,'3:13','Dhugumatti, gareewwan waraanaa lamaan [guyyaa Badrii] wal qunnaman keessa mallattootu isiniif jiraGareen tokko karaa Rabbii keessatti loltiIsheen biraa immoo kaafiraIsaan (kaafiroonni) ilaalcha ijaatiin (lakkoofsa Muslimootaa) dachaa isaanii ta’anii isaan arguRabbiin nama fedhe tumsa Isaatiin isa deeggaraDhugumatti warra sammuu qabaniif kana keessa gorsatu jira.');
INSERT INTO "apaguna" VALUES(3,14,'3:14','Namootaaf jaalalli wantoota lubbuun isaanii feetuu kan dubartootaafi ilmaan irraa, warqeefi meeta kuufamaa irraa, faradoo malateeffamtee miidhagfamte, beelladaafi dachii qonnaaf mijooftu irraa ta’e miidhagfamee jiraKun meeshaa jireenya addunyaatiRabbiin immoo Isa bira deebii bareedaatu jira.');
INSERT INTO "apaguna" VALUES(3,15,'3:15','Jedhi: “Sila waan sana caalu isiniif odeessuu? Isaan Rabbiin sodaataniif Gooftaa isaanii bira jannata laggeen ishee jala yaatu kan yeroo hunda ishee keessa jiraatantu isaaniif jiraAmmas niitolii qulqulleeffamaniifi jaalala Rabbiin biraa ta’etu [isaaniif jira]Rabbiin gabroota argaadha."');
INSERT INTO "apaguna" VALUES(3,16,'3:16','Isaan warra “Gooftaa keenya! Dhugumatti, nuti amanneerra; kanaaf badii keenya nuuf araarami; adabbii ibiddaas nu baraari” jedhan.');
INSERT INTO "apaguna" VALUES(3,17,'3:17','Obsitoota, dhugaa dubbattoota, [Rabbiif] ajajamtoota, kennattootaafi warra obboroo keessa dhiifama kadhatanin [faarsa].');
INSERT INTO "apaguna" VALUES(3,18,'3:18','Rabbiin haqaan dhaabbataa ta’ee akka Isa malee dhugaan gabbaramaan hin jirre ragaa ba’e; maleeykonniifi warri beekumsaas [ragaa ba’aniiru]Isa hunda injifataa, ogeessa malee haqaan gabbaramaan hin jiru.');
INSERT INTO "apaguna" VALUES(3,19,'3:19','Dhugumatti amantiin Rabbiin biratti [fudhatama qabu] Islaama qofaIsaan kitaabni kennameef erga beekumsi isaaniif dhufee booda jibbaa gidduu isaanii jiruuf jecha malee [san dura] wal hin dhabneNamni keeyyattoota Rabbiitti kafare dhugumatti Rabbiin ariifataa qormaataati.');
INSERT INTO "apaguna" VALUES(3,20,'3:20','Yoo isaan siin falmanis: “Ani fedha Rabbii jala butameera; namni ana hordofes [akkasuma]” jedhiWarra kitaabni isaaniif kennameefi wallaaltotaanis “sila islaamoftaniittuu?” jedhiYoo islaama''aniif dhugumatti qajeelaniiruYoo irraa garagalan wanti sirra jiru ergaa geessisuu qofaRabbiin gabroota argaadha.');
INSERT INTO "apaguna" VALUES(3,21,'3:21','Dhugumatti isaan keeyyattoota Rabbiitti kafaranu, karaan ala nabiyyoota ajjeesanuufi namoota irraas warra haqatti ajajanis ajjeesanu, adabbii laalessaan isaan gammachiisi.');
INSERT INTO "apaguna" VALUES(3,22,'3:22','Isaan warra dalagaaleen isaanii addunyaafi Aakhirattis jalaa baddeedhaIsaaniif tumsitoonni hin jiran.');
INSERT INTO "apaguna" VALUES(3,23,'3:23','Sila gara isaan kitaaba irraa qoodni wahii isaaniif kennamee hin ilaallee? Akka jidduu isaaniitti murteessuuf gara kitaaba Rabbii waamamanii ergasii gareen isaan irraa ta’e didaa ta’ee gara gala.');
INSERT INTO "apaguna" VALUES(3,24,'3:24','Kun waan isaan “ibiddi guyyoota lakkaa’amtuu malee nu hin tuqxu” jedhaniifiKijibni isaan [of biraa] uumaa turan amantii isaanii keessatti isaan gowwoomse.');
INSERT INTO "apaguna" VALUES(3,25,'3:25','Sila yeroo Nuti guyyaa mamii hin qabneef walitti isaan qabnee lubbuun hundi waan hojjatte guutuutti argatte akkam ta’u? Garuu isaan hin miidhamanu.');
INSERT INTO "apaguna" VALUES(3,26,'3:26','[Yaa Muhammad!] jedhi: "yaa Rabbii abbaa mootummaa taate! Mootummaa nama feeteef kennita; nama feete irraa immoo mootummaa mulqitaNama feetes ni jabeessitaNama feetes ni xiqqeessitaToltuun hundi harka kee jiraDhugumatti Ati waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(3,27,'3:27','Halkan guyyaa keessa seensiftaGuyyaas halkan keessa seensiftaDu’aa irraas jiraa baaftaJiraa irraas du’aa baaftaNama feetes herrega malee soorta" [jedhi]');
INSERT INTO "apaguna" VALUES(3,28,'3:28','Mu’uminoonni mu’uminootaan alatti kaafiroota jaalallee hin godhatinYoo eeggannaa wahii isaan irraa eeggachuuf ta’e malee namni kana dalage, [diinii] Rabbii irraa homaa keessa hin jiruRabbiin nafsii Isaa (adabbii Isaa) isin sodaachisaDeebiinis garuma Rabbiiti.');
INSERT INTO "apaguna" VALUES(3,29,'3:29','“Yoo waan qoma keessan keessa jiru dhoksitan yookiin isa mul’iftanis Rabbiin isa beeka[Inni] waan samii keessa jiruufi waan dachii keessa jiru ni beekaRabbiin waan hunda danda’aadha” jedhi!');
INSERT INTO "apaguna" VALUES(3,30,'3:30','Guyyaa lubbuun hundi toltuu irraa waan hojjatte dhiyaataa ta''ee argitu [yaadadhu]Hamtuu irraa waan hojjatte immoo odoo jidduu isheetiifi jidduu isaa fageenyi dheeraan jiraatee jaal’attiRabbiin nafsii Isaa isin sodaachisaRabbiin gabroota akkaan mararfataadha.');
INSERT INTO "apaguna" VALUES(3,31,'3:31','”Yoo kan Rabbiin jaallattan taataniif ana hordofaa Rabbiin isin jaallata; badii keessanis isiniif araaramaRabbiin araaramaa, rahmata godhaadha” jedhiin.');
INSERT INTO "apaguna" VALUES(3,32,'3:32','”Rabbiifi ergamaan bulaa” jedhiYoo irraa garagalaniif [kafaraniiru]Dhugumatti Rabbiin kaafiroota hin jaalatu.');
INSERT INTO "apaguna" VALUES(3,33,'3:33','Dhugumatti Rabbiin Aadam, Nuuh, maatii Ibraahimiifi maatii Imraan aalama hunda irra filateera.');
INSERT INTO "apaguna" VALUES(3,34,'3:34','Sanyii gariin ishee garii irraa taate san [filate]Rabbiin dhagayaa, beekaadha.');
INSERT INTO "apaguna" VALUES(3,35,'3:35','Yeroo niitiin Imraan “Gooftaa kiyya! ani waan garaa kiyya keessa jiru bilisa godhee siif nazareeraa narraa qeebali” jette [yaadadhu]Dhugumatti Ati simatu dhagayaa, beekaadha.”');
INSERT INTO "apaguna" VALUES(3,36,'3:36','Yeroma ishee deesses “Gooftaa kiyya! ani dubran daye" jetteRabbiin irra beekaa waan isheen deesseeti“Dhiirri akka durbaa hin taaneAnis Maryamitti ishee moggaaseeraAni sheyxaana abaaramaa irraa isheefi sanyii ishees siinan tiksa” [jette].');
INSERT INTO "apaguna" VALUES(3,37,'3:37','Gooftaan ishee qeebaltii gaarii ta’een ishee qeebaleGuddina gaariis ishee guddiseZakariyyaan itti gaafatama ishee fudhateZakariyyaan dhawaatuma ishee irratti mihraaba seenuun soorata ishee biratti arga“Yaa maryam kun eessaa siif dhufa?” jedhe“Inni Rabbiin biraayyi” jetteDhugumatti Rabbiin nama fedhe herrega malee soora.');
INSERT INTO "apaguna" VALUES(3,38,'3:38','Bakka sanatti Zakariyyaan “Gooftaa kiyya! Ati Suma biraa ilmaan gaarii naaf kenniDhugumatti Ati dhagayaa kadhaati" jedhe.');
INSERT INTO "apaguna" VALUES(3,39,'3:39','Malaa’ikoonnis isaa mihraaba keessa sagadaaf dhaabbataa jiruu “Dhugumatti Rabbiin Yahyaa, dhugoomsaa jecha Rabbi irraa ta''e, caalaa, qunnamtii saalaa irraa eegamaafi nabiyyii gaggaarii irraa ta’een si gammachiisa" [jettee] isatti lallabde.');
INSERT INTO "apaguna" VALUES(3,40,'3:40','[Innis] “yaa Gooftaa kiyya! Sila ani odoo dhugaatti dullumni na qabee jiruufi niitiin kiyyas maseena taatee jirtuu akkamittin ilma argadha?" jedhe[Rabbiinis] “Akka kanatti Rabbiin waan fedhe hojjata” jedheen.');
INSERT INTO "apaguna" VALUES(3,41,'3:41','[Innis] “yaa Gooftaa kiyya! mallattoo naaf taasisi” jedhe(Rabbiinis) “Guyyoota sadii mallattoon malee namootatti dubbachuu dhabuu keeti.[] Gooftaa keetis baay’inaan faarsiGalgalaafi ganamas Isa qulqulleessi” jedheen.');
INSERT INTO "apaguna" VALUES(3,42,'3:42','Yeroo Malaa’ikoonni “Yaa Maryam! Dhugumatti Rabbiin si filateeraSi qulqulleesseerasDubartoota aalama hundaa irras si filateera” jedhan [yaadadhu].');
INSERT INTO "apaguna" VALUES(3,43,'3:43','”Yaa Maryam! Gooftaa keetiif akkaan ajajamiSujuudas godhiWarra gadi jedhan waliinis gadi jedhi (rukuu’a godhi)."');
INSERT INTO "apaguna" VALUES(3,44,'3:44','Kun oduuwwan fagoo kan Nuti gara keetti beeksifnuudhaYeroo ‘eenyu Maryamiin akka guddisu’ carraa isaanii darban ati isaan bira hin turreYeroo isaan wal falmanis ati isaan bira hin turre.');
INSERT INTO "apaguna" VALUES(3,45,'3:45','Yeroo malaa’ikoonni “yaa Maryam! dhugumatti Rabbiin jecha Isa biraa ta’e, kan maqaan isaa Al-Masiih Iisaa ilma Maryam jedhamu, kan addunyaafi Aakhiraattis kabajamaafi warra Rabbitti dhiheeffaman irraa ta’een si gammachiisa” jette [yaadadhu]');
INSERT INTO "apaguna" VALUES(3,46,'3:46','Daa’imummaafi erga guddateeyis namaan dubbataWarra gaggaarii irraayis ta’a.');
INSERT INTO "apaguna" VALUES(3,47,'3:47','[Maryamis] “yaa Gooftaa kiyya! Odoo namni tokkollee ana hin tuqne akkamitti ilmi naaf argama?” jette[Rabbiinis] “Akka kanatti Rabbiin waan fedhe uumaInni yeroo waan tokko murteesse ‘ta’i!’ qofa isaan jedha; yeroma san ta’a” jedhe.');
INSERT INTO "apaguna" VALUES(3,48,'3:48','(Rabbiin) Barreeffama, ogummaa, Tawraatiifi Injiiliinis isa barsiisa.”');
INSERT INTO "apaguna" VALUES(3,49,'3:49','Gara ilmaan Israa’iilittis ergamaa [isa godha]“Ani dhugumatti Gooftaa keessan irraa raajiidhaanan isinitti dhufeAni dhoqqee irraa waan akka allaattiin isiniif uuma; isa keessattis nan afuufa; eeyyama Rabbiitiin allaattiis ni ta’aEeyyama Rabbiitiin jaamaafi lamxii nan fayyisaEeyyama Rabbiitiin du’aas nan jiraachisaWaan isin nyaattaniifi waan isin manneen keessan keessa ol kaayattanis isinittin himaDhugumatti kana keessa yoo kan amantan taataniif mallattoo gaariitu isiniif jira.');
INSERT INTO "apaguna" VALUES(3,50,'3:50','Waan na dura ture Tawraatiin kanan dhugoomsu ta''ee garii wantoota dhorgaa isin irratti taasifamanii halaala isiniif taasisuuf jechan ragaa Gooftaa keessan irraa ta’een isinitti dhufeRabbiin sodaadhaatii anaaf ajajamaa.');
INSERT INTO "apaguna" VALUES(3,51,'3:51','Dhugumatti Rabbiin Gooftaa kiyyaafi Gooftaa keessan; kanaafuu Isa gabbaraaKun karaa qajeelaadha.”');
INSERT INTO "apaguna" VALUES(3,52,'3:52','Iisaan yeroma kufrii isaan irraa hubatu “namni [waamicha ani] gara Rabbiitti godhu keessatti na tumsu eenyu?” jedheHawaariyyoonnis “nutu tumsitoota Rabbiiti.[] Rabbitti amanneerraAkka nuti Muslimoota taanes ragaa nuuf ba’i” jedhan.');
INSERT INTO "apaguna" VALUES(3,53,'3:53','“Yaa Gooftaa keenya! Nuti waan Ati buuftetti amanneerraErgamicha (Iisaas) hordofne; kanaafuu warra [Rabbiin malee Gooftaan biraa akka hin jirre] ragaa ba’an wajjin nu barreessi” [jedhan].');
INSERT INTO "apaguna" VALUES(3,54,'3:54','Isaanis tooftaa baasani; Rabbiinis tooftaa baaseRabbiin immoo irra caalaa warra tooftaa baasuuti.');
INSERT INTO "apaguna" VALUES(3,55,'3:55','Yeroo Rabbiin jedhes [yaadadhu]:[] “Yaa Iisaa! Ani si raffisaadhaGara kiyyattis ol si fuudhaadhaIsaan kafaran irraas si qulqulleessaadhaIsaan si hordofanis hanga Guyyaa Qiyaamatti warra kafaran irratti ol taasisaadhaErgasii deebiin keessan gara kiyya; waan isin isa keessatti wal dhabaa turtan keessattis gidduu keessanitti nan murteessa.');
INSERT INTO "apaguna" VALUES(3,56,'3:56','Isaan kafaran addunyaafi Aakhiratti adabbii cimaan isaan adaba; tumsaanis isaaniif hin jiru.');
INSERT INTO "apaguna" VALUES(3,57,'3:57','Warra amananii gaggaarii dalagan immoo mindaa isaanii ni guutaafiRabbiin miidhaa raawwattoota hin jaal’atu.');
INSERT INTO "apaguna" VALUES(3,58,'3:58','Kana keeyyattootaafi gorsa ogummaa irraa ta’e sirratti dubbifna.');
INSERT INTO "apaguna" VALUES(3,59,'3:59','Dhugumatti, fakkeenyi Iisaa Rabbiin biratti akka fakkeenya AadamiBiyyee irraa isa uumeErgasii ‘ta’i’ isaan jedheInnis yeroma san ta’e');
INSERT INTO "apaguna" VALUES(3,60,'3:60','Dhugaan Gooftaa kee biraayyi; shakkitoota irraa hin ta’in.');
INSERT INTO "apaguna" VALUES(3,61,'3:61','Nama erga beekumsi sitti dhufee booda isa keessatti siin falmeen “Kottaa; ilmaan keenyaafi ilmaan keessan, dubartoota keenyaafi dubartoota keessan, lubbuu keenyaafi lubbuu keessan haa waamnuErgasii du''aa''iitti jabaannee abaarsa Rabbii kijibdoota irra haa goonu” jedhiin.');
INSERT INTO "apaguna" VALUES(3,62,'3:62','Dhugumatti kun isumatu seenaa dhugaatiRabbiin malee dhugaan gabbaramaan hin jiruDhugumatti Rabbiin Isatu injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(3,63,'3:63','Yoo [dhugaa] irraa gara galaniif, Rabbiin beekaa balleessitootaati.');
INSERT INTO "apaguna" VALUES(3,64,'3:64','Jedhi: “yaa warra kitaabaa! Gara jecha nuufi isin gidduutti wal qixa taatee kottaa[Isheenis] akka Rabbiin malee waan biraa hin gabbarre; Isattis waan biraa akka hin qindeessine, gariin keenyas garii Rabbiin alatti gabbaramaa akka hin taasifanneedhaYoo isaan [kana] irraa gara galan “akka nuti Muslimoota taane ragaa ba’aa” jedhaa.');
INSERT INTO "apaguna" VALUES(3,65,'3:65','Yaa warra kitaabaa! Maaliif waa’ee Ibraahiim keessatti wal falmitu? Tawraatiifi Injiil isa booda malee hin buune; sila hin xiinxaltanuu!');
INSERT INTO "apaguna" VALUES(3,66,'3:66','Dhaga’aa! isin warri kun waan beekumsi isaa isiniif jiru keessatti wal falmitaniittu! Maaliif waan beekumsi isaa isiniif hin jirre keessatti wal falmitu? Rabbiin ni beekaa isin hin beektanu.');
INSERT INTO "apaguna" VALUES(3,67,'3:67','Ibraahiim yahuudas kiristaanas hin taaneGaruu inni Muslima qajeelaa tureInni mushrikoota irraas hin taane.');
INSERT INTO "apaguna" VALUES(3,68,'3:68','Namoonni Ibraahiimitti irra aanan: isaan isa hordofan, nabiyyii kanaafi warra amananiidhaRabbiin gargaaraa mu’uminootaati.');
INSERT INTO "apaguna" VALUES(3,69,'3:69','Gareen warra kitaabaa irraa taate tokko isin jallisuu jaalatteLubbuu isaanii malee homaa hin jallisanu; garuu isaan hin beekanu');
INSERT INTO "apaguna" VALUES(3,70,'3:70','Yaa warra kitaabaa! odoo beektanuu maaliif keyyattoota Rabbiitti kafartu?');
INSERT INTO "apaguna" VALUES(3,71,'3:71','Yaa warra kitaabaa! maaliif dhugaa sobaan walitti maktanii odoo beektanuu dhugaa dhoksitu?');
INSERT INTO "apaguna" VALUES(3,72,'3:72','Gareen warra kitaabaa irraa taate tokkos ni jette: “jalqaba guyyaa isa warra amanan irratti bu’etti amanaaAkka isaan [Islaama] irraa deebi’aniif jecha dhuma guyyaa immoo itti kafaraa.');
INSERT INTO "apaguna" VALUES(3,73,'3:73','Tokkoon namaa fakkii waan isin fudhattanii fudhachuu yookiin kan Rabbii keessan biratti ittiin isin falman ta’uu warra amantii keessan hordofeef malee hin amaninaa (ifa hin baasinaa)(Yaa Muhammad SAW) “qajeelfamichis qajeelfama Rabbiiti" jedhi“Tolli (Islaamummaa) harka Rabbiiti; abbaa fedheef isa kenna” jedhiRabbiin (kenni isaa) bal’aa, beekaadha.');
INSERT INTO "apaguna" VALUES(3,74,'3:74','Inni nama fedhe rahmata Isaatiif (nabiyyummaadhaaf) filataRabbiin abbaa tola guddaati.');
INSERT INTO "apaguna" VALUES(3,75,'3:75','Warra kitaabaa irraas nama yoo ati qabeenya guddaan isa amante isa siif deebisutu jiraIsaan irraas nama yoo maallaqa warqeetiin isa amante hanga yeroo dheeraa isaaf eegdutti isa siif hin deebifnetu jiraKun waan isaan “wallaaltota (qabeenya isaanii nyaachuu) keessatti badiin nurra hin jiru” jedhaniifi odoo beekanuu Rabbi irratti kijiba odeessaniifi.');
INSERT INTO "apaguna" VALUES(3,76,'3:76','Eeyyeen! (badiin isaan irra ni jira)namni waadaa isaa guuteefi (Rabbiin) sodaate, dhugumatti Rabbiin warra (Isa) sodaatanu ni jaalata.');
INSERT INTO "apaguna" VALUES(3,77,'3:77','Dhugumatti, isaan waadaa Rabbiifi kakuu isaanii gatii xiqqaan jijjiirratan, isaan Aakhiratti qoodni homaatuu isaaniif hin jiruRabbiinis Guyyaa Qiyaamaa isaan hin dubbisu; gara isaaniis hin ilaalu; isaan hin qulqulleessusadabbiin laalessaanis isaaniif jira.');
INSERT INTO "apaguna" VALUES(3,78,'3:78','Isaan irraayis, odoo inni kitaaba irraa hin ta’in akka isin kitaaba irraayi jettanii isa yaaddaniif jecha garee arraba isaanii kitaabaan micciirantu jiraOdoo inni Rabbiin biraa hin ta’inis Rabbiin biraayi jedhuOdoma beekanuu Rabbirratti kijiba dubbatu.');
INSERT INTO "apaguna" VALUES(3,79,'3:79','Ilma namaa tokkoof Rabbiin kitaaba, hukmii (beekumsaafi hubannoo seera amantii)fi nabiyyummaa isaaf kennee, ergasii namootaan “Rabbii gaditti gabroota naaf ta’aa” jechuun (isaaf) hin maluGaruu (kan inni isaaniin jedhu) waan kitaaba barsiisaa turtaniifi waan qu''ataa turtaniif jecha beektota beekumsa isaaniitiin hojjatan ta’aa jechuudha.');
INSERT INTO "apaguna" VALUES(3,80,'3:80','Maleykotaafi nabiyyoota gabbaramaa taasifachuutti isin ajajuun isaaf hin maluSila erga isin Muslimoota taatanii booda kufritti isin ajajaa?');
INSERT INTO "apaguna" VALUES(3,81,'3:81','Yeroo Rabbiin nabiyyoota irraa “waan ani kitaabaafi ogummaa irraa isiniif kennee, ergasii ergamaan waan isinii wajjin jiru dhugoomsu isinitti dhufe, akka isatti amantaniifi isa tumsitanu” (jechuun) waadaa qabate [yaadadhu](Rabbiinis) "mirkaneessitanii? Sana irrattis waadaa kiyya qabdanii?" jedhe[Isaan] "mirkaneessine" jedhan; (Rabbiinis) “Ragaa ta’aa; anis warra isinii wajjin ragaa ta’u irraayi” jedhe.');
INSERT INTO "apaguna" VALUES(3,82,'3:82','Namni sana booda garagale, warri sun isaantu finciltoota.');
INSERT INTO "apaguna" VALUES(3,83,'3:83','Sila odoo wantoonni samiifi dachii keessaa hunduu fedhaan yookiin dirqamaan Rabbiif ajajamaniifi gara isaatti deebi’anii, amantii kan Rabbiin alaa barabaaduu?');
INSERT INTO "apaguna" VALUES(3,84,'3:84','(Yaa Muhammad SAW) “Rabbitti, Waan nurratti bu’etti, Waan Ibraahiim, Ismaa’iil, Isihaaq, Ya’aquub, ilmaan Ya’aquub irratti buufametti, waan Muusaa, Iisaafi nabiyyoonni Gooftaa isaanii irraa kennamanittis amannee jirraIsaan irraa gidduu nama tokkoos addaan hin foonuNutis isaaf ajajamtoota” jedhi.');
INSERT INTO "apaguna" VALUES(3,85,'3:85','Namni Islaamaan alatti amantii barbaade isarraa hin qeebalamuAakhiraattis inni warra hoonga’an irraayi.');
INSERT INTO "apaguna" VALUES(3,86,'3:86','Sila ummata amanantii isaanii booda, erga ergamaan Rabbii dhugaa ta’uu ragaa ba’aniifi ragoonni ifa ta’an isaanitti dhufanii (booda) kafaran Rabbiin akkamitti qajeelcha? Rabbiinis miidhaa hojjattoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(3,87,'3:87','Warri sun mindaan isaanii abaarsi Rabbii, kan maleeykotaafi namoota hundaa isaan irra jiraachuudha.');
INSERT INTO "apaguna" VALUES(3,88,'3:88','Ishee (ibidda) keessatti hafoodhaAdabichis isaan irraa hin salphifamuYeroonis isaaniif hin kennamu.');
INSERT INTO "apaguna" VALUES(3,89,'3:89','Garuu isaan sana booda tawbatanii toltuu hojjatan (isaaniif ni dhiifama)Dhugumatti Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(3,90,'3:90','Warra iimaana isaanii booda kafaranii ergasiis kufrii dabalatan tawbaan isaanii hin qeebalamtuIsaan, isaanumatu jallattoota.');
INSERT INTO "apaguna" VALUES(3,91,'3:91','Dhugumatti isaan kafaranii, kaafiroota ta’anii du’an, warqee dachii guutuun, odoma ittiin of bituu fedheeyyuu tokkoon isaanii irraa hin qeebalamuSunniin isaaniif adabbii laalessaa ta’etu jiraIsaaniif tumsitoonnis hin jiran.');
INSERT INTO "apaguna" VALUES(3,92,'3:92','Hanga waan jaalattan irraa kennattanitti khayrii hin argattanWanta isin qabeenya irraa kennattan Rabbiin isa beeka.');
INSERT INTO "apaguna" VALUES(3,93,'3:93','Waan Israa’iil odoo Tawraat hin bu’in dura lubbuu isaa irratti haraama taasise malee nyaanni hunduu Ilmaan Israa’iiliif halaala ture(Yaa Muhammad) "yoo kan dhugaa dubbattan taataniif Tawraatiin fidaa dubbisaa” jedhi.');
INSERT INTO "apaguna" VALUES(3,94,'3:94','Namni sana booda Rabbi irratti kijiba uume, warri sun isaantu miidhaa hojjattoota.');
INSERT INTO "apaguna" VALUES(3,95,'3:95','“Rabbiin dhugaa dubbateera; karaa amantii Ibraahiim qajeelaa ta’e hordofaaInnis mushrikoota irraa hin taane" jedhi.');
INSERT INTO "apaguna" VALUES(3,96,'3:96','Dhugumatti manni jalqaba ilma namaatiif haala barakeeffamaafi aalama hundaaf qajeelfama ta’een kaayame isa Bakkaan (Makkaan) jiruudha.');
INSERT INTO "apaguna" VALUES(3,97,'3:97','Isa keessa mallattoolee ifa ta’an Maqaama Ibraahiimtu jiraNamni isa seenes nagaa ta’aRabbiif nama irratti, kan gara isaa deemuu danda''e irratti, manicha hajja''uutu dirqamni jiraNamni kafares, dhugumatti Rabbiin aalama irraa duroomaadha.');
INSERT INTO "apaguna" VALUES(3,98,'3:98','“Yaa warra kitaabaa! Sila odoo Rabbiin waan isin dalagdanu beekaa ta’uu maaliif keeyyattoota Rabbiitti kafartu?” jedhi.');
INSERT INTO "apaguna" VALUES(3,99,'3:99','“Yaa warra kitaabaa! Sila maaliif odoo beektanuu jallina ishee barbaachaaf karaa Rabbii irraa nama debiftu? Rabbiinis waan isin dalagdanu irraa dagataa miti" jedhi.');
INSERT INTO "apaguna" VALUES(3,100,'3:100','Yaa warra amantan! Yoo garee warra kitaabni kennameef irraa ta’een ajajamtan, erga amantanii booda gara kufriitti isin deebisu.');
INSERT INTO "apaguna" VALUES(3,101,'3:101','Sila odoo keeyyattoonni Rabbii isin irratti qara’amaniifi ergamaan Isaa isin keessa jiruu akkamitti kafartu? Namni Rabbiin of tikse dhugumatti karaa sirraawaa qajeelfameera.');
INSERT INTO "apaguna" VALUES(3,102,'3:102','Yaa warra amantan! Dhugaa sodaa isaa Rabbiin sodaadhaa; Muslimoota taatan malees hin du’inaa.');
INSERT INTO "apaguna" VALUES(3,103,'3:103','Haada Rabbiis (Qur’aana) hunda ta''aa qabadhaa; adda addas hin ba’inaaNi’imaa Rabbii kan yeroo isin diina walii turtan Inni onnee keessan wal simsiisuun ni’imaa Isaatiin obboleeyyan taatan san, isin irra jiruufi (yeroo) isin qarqara boolla ibiddaa irra turtanii ishee irraa isin baraarses yaadadhaaAkka isin sirrooftaniif jecha Rabbiin akka kanatti keeyyattoota Isaa isiniif addeessa.');
INSERT INTO "apaguna" VALUES(3,104,'3:104','Isin irraa ummanni gara khayrii waamtuufi kan gaariitti ajajju badii irraa dhorgitus haa jiraattuWarri sun isaantu milkaa’oodha.');
INSERT INTO "apaguna" VALUES(3,105,'3:105','Akka warra erga ragoota ifa ta’an isaanitti dhufee booda addaan faca’aniifi wal dhabanii hin ta’inaaWarra sanis isaaniif adabbii laalessaatu jira.');
INSERT INTO "apaguna" VALUES(3,106,'3:106','Guyyaa fuulli gariin addaataniifi fuulli gariin gurraacha’an (adabamu)Warri fuulli isaanii gurraachofte immoo “sila erga amantan booda kafartanii? Waan kafaraa turtaniif adabbii dhandhamaa” (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(3,107,'3:107','Warri fuulli isaanii addaatte immoo rahmata Rabbii keessa ta’u; Isaan ishee keessatti hafoodha');
INSERT INTO "apaguna" VALUES(3,108,'3:108','Isheen sun, kan Nuti dhugaan ishee sirratti qaraanu taatee, keeyyattoota RabbiitiRabbiinis aalama hundaafuu miidhaa kan fedhu hin taane.');
INSERT INTO "apaguna" VALUES(3,109,'3:109','Wantoonni samiifi wantoonni dachii keessa jiran (hundi) kan RabbiitiWantoonni hunduu gara Rabbiitti deebi’u');
INSERT INTO "apaguna" VALUES(3,110,'3:110','Isin caaltuu ummata namaaf mul''ifamtee taataniittuToltutti ajajju; hamtuu irraas ni dhorgitu; Rabbittis ni amantuOdoo abbootiin kitaabaa amananii, silaa gaarii isaaniif ta’a tureIsaan irraa mu''uminoonnis ni jiru; garuu irra hedduun isaanii finciltoota.');
INSERT INTO "apaguna" VALUES(3,111,'3:111',' Rakkisuu malee isin hin miidhanuOdoo isiniin lolaniiyyuu duuba isin irraa deebi’uErgasii tumsa hin argatan.');
INSERT INTO "apaguna" VALUES(3,112,'3:112','Yoo waadaa Rabbirraa ta’eefi waadaa nama irraa ta’een (eegaman) malee bakkuma isaan jiran hundatti salphinni isaan irra kaayamteettiDallansuu Rabbirraa ta’eenis deebi’an; hiyyummaanis isaan irra kaayamteKun waan isaan keeyyattoota Rabbiitti kafaraa turaniifi karaan ala nabiyyoota ajjeesaa turaniifiKun waan isaan ajaja Rabbii didaniifi daangaa dabraa turaniifi.');
INSERT INTO "apaguna" VALUES(3,113,'3:113','Isaan wal qixa mitiAbbootii kitaabaa irraa namoota dhugaaf dhaabbatan, kan sujuudaa sa’aatii halkanii keeyyattoota Rabbii qara’antu jira.');
INSERT INTO "apaguna" VALUES(3,114,'3:114','Rabbittiifi guyyaa Aakhiraatti amanu; toltuuttis ni ajaju; hamtuu irraas ni dhorgu; gaggaarii keessattis ni arifatuIsaan sun warra gaggaarii irraayi.');
INSERT INTO "apaguna" VALUES(3,115,'3:115','Toltuu irraa wanta hojjatan kamillee hin dhorgatamanuRabbiin warra (Isa) sodaatan beekaadha.');
INSERT INTO "apaguna" VALUES(3,116,'3:116','Dhugumatti isaannan kafaran, qabeenyiifi ilmaan isaanii (adabbii) Rabbii irraa homaa isaan hin deebiftuSunniin warra ibiddaatiIsaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(3,117,'3:117','Fakkeenyi waan isaan jiruu addunyaa tana keessatti sadaqatanii akka fakkeenya bubbee qorri hamaan isa keessa jiru, kan ooyruu warra lubbuu isaanii miidhanii tuqxee, isa balleessiteetiRabbiin isaan hin miine; garuu isaantu lubbuu isaanii miidha.');
INSERT INTO "apaguna" VALUES(3,118,'3:118','Yaa warra amantan! namoota isin irraa hin ta’in jaalalloo iccitii hin godhatinaaIsin balleessuu irraa duubatti hin deebi’anuRakkina keessan hawwaniDhugumatti, jibbaan afaan isaanii irraa mul’atteWanti qomni isaanii dhoksitu immoo irra guddaadhaYoo kan xiinxaltan taataniif raajiwwan ifa isiniif taasifnee jirra.');
INSERT INTO "apaguna" VALUES(3,119,'3:119','Dhaga''aa! isin warri sun isaan jaallattu; isaan immo isin hin jaalatanuIsin kitaabota hundatti ni amantuYeroo isin qunnaman “nuti amannee jirra” jedhuYeroo kophaa ba’an immoo dallansuu irraa kan ka''e quba isaanii isin irratti ciniinu“Dallansuu keessaniin du’aa" jedhiDhugumatti Rabbiin beekaa waan qoma keessa jiruuti.');
INSERT INTO "apaguna" VALUES(3,120,'3:120','Yoo toltuun wahii isin tuqe, isaan gaddisiisaYoo hamtuun isin tuqe immoo isheetti gammaduYoo isin obsitanii (Rabbiin) sodaattaniif malli isaanii homaa isin hin miidhuDhugumatti Rabbiin waan isaan dalagan (beekumsa Isaatiin) marsaadha.');
INSERT INTO "apaguna" VALUES(3,121,'3:121','Yeroo ati mu’uminoota iddoowwan lolaa kan qubsiiftu taatee maatii kee irraa ganamaan baatu [yaadadhu]Rabbiinis dhagayaa, beekaadha');
INSERT INTO "apaguna" VALUES(3,122,'3:122','Yeroo gareen lamaan isin irraa ta''an odoo Rabbiin gargaaraa isaan lamaanii ta’uu lunnoomuu yaadan [yaadadhaa]Mu’uminoonnis Rabbirratti qofa haa irkatanu.');
INSERT INTO "apaguna" VALUES(3,123,'3:123','Dugumatti haala isin xiqqoo taataniin, dirree Badriitti Rabbiin isin tumseeraAkka galateeffattaniif jecha Rabbiin sodaadhaa.');
INSERT INTO "apaguna" VALUES(3,124,'3:124','Yeroo ati mu’uminootaan “sila Gooftaan keessan maleeykota kuma sadii (samii irraa) buufamoota ta''aniin isin gargaaruun isin hin ga''uu?” jettu [yaadadhaa].');
INSERT INTO "apaguna" VALUES(3,125,'3:125','Eeyyeen; yoo obsitaniifi Rabbiin sodaattan, (mushrikoonnis) battalumatti isinitti dhufan; Gooftaan keessan maleeykota mallatteeffamoo ta’an kuma shaniin isin gargaara');
INSERT INTO "apaguna" VALUES(3,126,'3:126','Isin gammachiisuufi onneen keessan ittiin haa tasgabbooftuuf malee Rabbiin (waan biraatiif) isa hin taasifneTumsis Rabbii injifataa, ogeessa ta’e irraa malee hin jiru.');
INSERT INTO "apaguna" VALUES(3,127,'3:127','Akka xiqqaatanii deebi’aniif Isaan kafaran irraa garii balleessuuf yookiin isaan salphisuuf (kana godhe).');
INSERT INTO "apaguna" VALUES(3,128,'3:128','Hanga Rabbiin toobaa isaanii qeebalutti yookiin waan isaan miidhaa raawwattoota ta''aniif isaan adabutti siif murtiin homaayyuu hin jiru.');
INSERT INTO "apaguna" VALUES(3,129,'3:129','Wantoonni samii keessa jiraniifi wantoonni dachii keessa jiran (hundi) kan Rabbiiti(Inni) nama fedheef ni araarama; nama fedhe immoo ni adabaRabbiinis araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(3,130,'3:130','Yaa warra amantan! dhala dachaalee dachaafamaa ta''e hin nyaatinaaAkka milkooftaniif jechas Rabbiin sodaadhaa.');
INSERT INTO "apaguna" VALUES(3,131,'3:131','Ibidda kaafirootaaf qopheeffamte san sodaadhaa.');
INSERT INTO "apaguna" VALUES(3,132,'3:132','Akka mararfatamtaniif jecha Rabbiifi ergamichaaf ajajamaa.');
INSERT INTO "apaguna" VALUES(3,133,'3:133','Gara araarama Gooftaa keessan irraa ta’eefi gara jannata warra Rabbiin sodaataniif qophooftee kan bal’inni ishee akka samiifi dachii taateetti ariifadhaa.');
INSERT INTO "apaguna" VALUES(3,134,'3:134','(Isaan) warra yeroo gammachuufi gaddaa arjooman; kan dallansuu liqimsaniifi namootaaf dhiifama godhaniidhaRabbiinis toltuu hojjattoota ni jaalata.');
INSERT INTO "apaguna" VALUES(3,135,'3:135','(Isaan) warra yeroo waan fokkataa hojjatan yookiin lubbuu ofii miidhan Rabbiin yaadatanii badii isaaniitiif araarama gaafataniifi haala (badii ta’uu isaa) beekaniin waan hojjatan irra hin turreedhaRabbiin malee namni badii araaramu hin jiru');
INSERT INTO "apaguna" VALUES(3,136,'3:136','Isaan sun mindaan isaanii araarama Gooftaa isaanii biraa ta’eefi jannata laggeen ishee jala yaatuudhaIsaan ishee keessatti hafoodhaMindaan hojjattootaa (jannata ta''uun) waa tole!');
INSERT INTO "apaguna" VALUES(3,137,'3:137','Dhugumatti, isiniin dura karaaleen[] darbiteettiDachii keessa deemaatii booddeen (xumurri) kijibsiiftotaa akkam akka ta’e ilaalaa.');
INSERT INTO "apaguna" VALUES(3,138,'3:138','Kun namootaaf ibsa; warra (Rabbiin) sodaataniif immoo qajeelfamaafi gorsa.');
INSERT INTO "apaguna" VALUES(3,139,'3:139','Yoo mu’uminoota taataniif isintu irra aanaa hin laafinaa; hin gaddinaas.');
INSERT INTO "apaguna" VALUES(3,140,'3:140','Yoo madaan isin tuqes, dhugumatti ummatas (diinotas) madaan fakkaataan isaan tuqeeraGuyyoota sana gidduu namaatti nanaannessinaRabbiin isaan amanan mul’isuufi isin irraas wareegamtoota taasisuuf (kana godhe)Rabbiin miidhaa hojjattoota hin jaalatu.');
INSERT INTO "apaguna" VALUES(3,141,'3:141','Rabbiin warra amanan qulqulleessuufi kaafiroota immoo balleessuf (kana godhe).');
INSERT INTO "apaguna" VALUES(3,142,'3:142','Sila odoo Rabbiin isin irraa warra qabsaa’aniifi warra obsan hin mul’ifne jannata seenuu yaadduu?');
INSERT INTO "apaguna" VALUES(3,143,'3:143','Dhugumatti isin isa qunnamuu keessan dura du’a hawwaa turtanDhugaatti (amma) kan laaltan taatanii isa argitaniittu.');
INSERT INTO "apaguna" VALUES(3,144,'3:144','Muhammad ergamaa dhugumatti ergamoonni dura isaa dabran malee homaa mitiSila yoo inni du’e yookiin ajjeefame of duuba deebituu? Namni faana isaa irra of duuba deebi’e Rabbiin homaa hin miidhuRabbiin galateeffattootaaf fuulduratti mindaa ni galcha.');
INSERT INTO "apaguna" VALUES(3,145,'3:145','Lubbuu kamiifuu eeyyama Rabbiitiin ala du''uun isheef hin taaneMurtee baallamamaa (Rabbiin murteesseedha)Nama mindaa addunyaa fedhes ishee irraa isaaf kenninaNama mindaa Aakhiraa fedhes, ishee irraa isaaf kenninaGalateeffattoota fuulduratti mindaa galchuuf jirra.');
INSERT INTO "apaguna" VALUES(3,146,'3:146','Nabiyyoota irraa kan beektonni heddu isaa wajjin lolan baay''eedhaWaan karaa Rabbii keessatti isaan mudateefis hin laafne; hin dadhabnes; (diinaafis) hin jilbiiffanneRabbiin obsitoota ni jaallata.');
INSERT INTO "apaguna" VALUES(3,147,'3:147','Jechi isaaniis “Gooftaa keenya! badii keenyaafi dhimmoota keenya keessatti daangaa dabriinsa keenya nuuf araarami; faana keenyas gadi dhaabi; ummata kaafirootaa irrattis nu tumsi” jechuu malee kan biraa hin taane.');
INSERT INTO "apaguna" VALUES(3,148,'3:148','Rabbiin mindaa addunyaatiifi gaarii mindaa Aakhiraa isaaniif kenneRabbiinis toltuu hojjattoota ni jaalata.');
INSERT INTO "apaguna" VALUES(3,149,'3:149','Yaa warra amantan! Yoo warra kafaraniif ajajamtan faana keessan irra (of duuba) isin deebifnaan hoonga’oota taatanii duubatti deebitu.');
INSERT INTO "apaguna" VALUES(3,150,'3:150','Dhugaatti Rabbiin gargaaraa keessaniInni irra caalaa tumsitootati.');
INSERT INTO "apaguna" VALUES(3,151,'3:151','Waan isaan wantoota inni ragaa qabatamaa ittiin hin buusin Rabbitti qindeessaniif jecha, onnee warra kafaranii keessa fuulduratti sodaa darbinaTeessoon isaanii ibiddaTeessoon miidhaa raawwattootaa waa fokkate!');
INSERT INTO "apaguna" VALUES(3,152,'3:152','Yeroo isin eeyyama Isaatiin isaan ajjeeftan Rabbiin waadaa Isaa isiniif dhugoomseeraErga (Rabbiin) waan jaalattan isin agarsiisee, yeroma isin lunnoomtanii ajaja keessattis wal dhabdanii, faallessitan (Rabbiinis isinii tumsuu dhiise)Isin irraa nama addunyaa fedhutu jiraIsin irraas nama Aakhiraa fedhutu jiraErgasii isin qormaatuuf jecha isaan irraa isin deebiseDhugumatti Rabbiin irra isiniif dabreeraRabbiin mu’uminoota irratti abbaa tolaati.');
INSERT INTO "apaguna" VALUES(3,153,'3:153','Odoo ergamaan duuba keessanii isin waamuu namtokkollee kan hin millanne taatanii yeroo baqattanu [yaadadhaa]Akka isin waan isin jala dabreefi waan isin tuqe irratti hin gaddineef jecha gadda irraan gadda isinitti buuseRabbiinis waan isin dalagdan hunda akkaan beekaadha');
INSERT INTO "apaguna" VALUES(3,154,'3:154','Ergasii cinqaa booda tasgabbii, hirriba garee isin irraa ta’e haguugu, isin irratti buuseGaree biraa immoo dhugumatti lubbuun isaanii isaan yaachifteettiWaan dhugaa hin ta’in, yaada jaahiliyyaa, Rabbi irratti yaadu“Sila nuti dhimmicha[] irraa qooda wahii qabnaa?” jedhu(Yaa Muhammad!) jedhi: “Dhugumatti dhimmichi hundi isaatuu kan Rabbiiti." Waan siif hin mul’ifne lubbuu isaanii keessatti dhoksu“Odoo dhimmicha irraa waan tokko qabaannee silaa asitti hin ajjeefamnu turre” jedhuJedhi “odoma manneen keessan keessa taataniiyyuu, warri duuti isaan irratti barreeffame gara iddoo itti du’anii ni bahu turan." Rabbiin waan qoma keessan keessa jiru qormaatuufi waan (badii) onnee keessan keessa jiran mul''isuuf (kana godhe)Rabbiinis waan qoma keessaa beekaadha.');
INSERT INTO "apaguna" VALUES(3,155,'3:155','Dhugumatti warri isin irraa guyyaa gareewwan lamaan wal qunnaman duubatti deebi’an, sababaa garii wantoota isaan hojjataniitiif kan isaan mucuceesse sheyxaanumaDhugumatti Rabbiin dhiifama isaaniif godheeraRabbiin araaramaa, obsaadha.');
INSERT INTO "apaguna" VALUES(3,156,'3:156','Yaa warra amantan! akka warra kafaranii obboleeyyan isaaniitiin, yeroo isaan dachii keessa deeman yookiin loltoota ta''an “odoo nu bira ta’anii silaa hin du’anu; hin ajjeefamanus” jedhanii hin ta’inaaRabbiin kana onnee isaanii keessatti gaabbii gochuuf (kana jedhan)Rabbiin ni jiraachisa; ni ajjeesasRabbiin waan isin dalagdanis argaadha.');
INSERT INTO "apaguna" VALUES(3,157,'3:157','Yoo karaa Rabbii keessatti ajjeefamtan yookiin duutan, araaramaafi rahmata Rabbi irraa ta’etu waan isaan walitti qaban irra caala.');
INSERT INTO "apaguna" VALUES(3,158,'3:158','Yoo duutan yookiin ajjeefamtanis, dhugumatti isin garuma Rabbiitti walitti qabamtu.');
INSERT INTO "apaguna" VALUES(3,159,'3:159',')Yaa Muhammad!) rahmata Rabbi irraa ta’een isaaniif laafteOdoo ati nama haala gadheefi qalbii gogaa taatee silaa si biraa faca’uIrra isaaniif darbi; isaaniifis araarama kadhu; dhimmoota (adda addaa) irrattis isaan mari’achiisiYeroo murattes Rabbirratti hirkadhuDhugumatti, Rabbiin warra (Isa) irratti hirkatan ni jaalata.');
INSERT INTO "apaguna" VALUES(3,160,'3:160','Yoo Rabbiin isin gargaare, wanti isin injifatu hin jiruYoo Inni isin xiqqeesse immoo, inni booda isaatii isin gargaaru sun eenyu? Mu’uminoonni Rabbuma irratti haa hirkatanu.');
INSERT INTO "apaguna" VALUES(3,161,'3:161','Nabiyyii kamiifuu (boojuu) dhokfachuun hin taaneNamni boojuu irraa miliqfate, Guyyaa Qiyaamaa waan miliqfate qabatee dhufaErgasii lubbuun hundi mindaa waan hojjattee guutuutti argattiIsaanis hin miidhamanu');
INSERT INTO "apaguna" VALUES(3,162,'3:162','Sila namni jaalala Rabbii hordofe akka nama dallansuu Rabbi irraa ta’een deebi’ee manni isaa jahannam ta''eetii? Deebiin isaa waa fokkate!');
INSERT INTO "apaguna" VALUES(3,163,'3:163','Isaan Rabbiin biratti sadarkaa adda addaa qabuRabbiinis waan isaan dalagan argaadha.');
INSERT INTO "apaguna" VALUES(3,164,'3:164','Dhugumatti Rabbiin yeroo ergamaa isaan irraa ta’e, kan keeyyattoota Isaa isaan irratti qara’u, (badii) irraas isaan qulqulleessu, kitaabaafi sunnaa (hikmaa) isaan barsiisu isaan keessatti ergu mu’uminoota irratti tola ooleeraDhugaatti sana dura isaan jallina ifa bahaa keessa turan.');
INSERT INTO "apaguna" VALUES(3,165,'3:165','Sila yeroma rakkoon kan isin fakkaataa ishee lama (injifannoo) argattan isin mudattu “kun eessaa nutti dhufe” jettuu? “Inni isinuma biraayyi" jedhiDhugumatti, Rabbiin waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(3,166,'3:166','Wanti guyyaa gareewwan lamaan wal qunnaman isin tuqe hayyamuma Rabbiitiini(Rabbiin) mu’uminootas (ittiin) ifa baasuuf (akkas godhe).');
INSERT INTO "apaguna" VALUES(3,167,'3:167','Isaan munaafiqoomanii “kottaa; karaa Rabbii keessatti lolaa; yookiin ofirraa deebisaa” isaaniin jedhamnaan “nuti odoo lola beeknee silaa isin hordofna turre” jedhanis akka ifa baasuufIsaan guyyaa san amantii caalaa kufriitti irra dhihoodhaWaan onnee isaanii keessa hin jirre afaan isaaniitiin jedhuRabbiinis irra beekaa waan isaan dhoksaniiti.');
INSERT INTO "apaguna" VALUES(3,168,'3:168','(Isaan) warra taa’anii obboleeyyan isaaniitiin “odoo nuuf ajajamanii silaa hin ajjeefamanu turan” jedhaniidha“Yoo kan dhugaa dubbattan taataniif du’a lubbuu keessan irraa deebisaa” jedhi');
INSERT INTO "apaguna" VALUES(3,169,'3:169','Warra karaa Rabbii keessatti du’an du’aatti hin yaadinDhugumatti isaan Gooftaa isaanii biratti jiraatoodha; ni sooramu.');
INSERT INTO "apaguna" VALUES(3,170,'3:170','Isaan waan Rabbiin tola Isaa irraa isaaniif kenneen gammadoo ta''anii (sooramu)Warra isaan boodaa kan isaan hin dhaqqabiniinis waan sodaan isaan irra hin jirreefi isaanis hin gaddineef jecha ni gammadu.');
INSERT INTO "apaguna" VALUES(3,171,'3:171','Ni’imaafi tola Rabbirraa ta’eefi Rabbiin mindaa mu’uminootaa balleessuu dhabuun ni gammadu.');
INSERT INTO "apaguna" VALUES(3,172,'3:172',')Isaan) warra erga madaan isaan tuqee booda Rabbiifi ergamaaf awwaataniidhaIsaan irraa warra toltuu hojjataniifi Rabbiin sodaataniif mindaa guddaatu jira.');
INSERT INTO "apaguna" VALUES(3,173,'3:173',')Isaan) warra namoonni isaaniin “Namoonni isiniif (waraana) walitti qabanii jiruu sodaadhaa” jennaan iimaana isaaniif dabalee “ga’aan keenya Rabbii, isarratti hirkachuun toleera” jedhaniidha.');
INSERT INTO "apaguna" VALUES(3,174,'3:174','Odoo hamtuun isaan hin tuqin jaalala Rabbiis hordofanii ni’imaa Rabbi irraa ta’eefi tola isaatiin deebi’anRabbiin abbaa tola guddaati.');
INSERT INTO "apaguna" VALUES(3,175,'3:175','Inni sun sheyxaana; jaalalloo isaa (isin) sodaachisaa, yoo mu’umina taataniif isaan hin sodaatinaatii anuma sodaadhaa.');
INSERT INTO "apaguna" VALUES(3,176,'3:176','Isaan kufrii keessatti ariifatan si hin gaddisiisinIsaan Rabbiin homaa hin miidhanuRabbiin akka isaaniif Aakhiratti qooda homaatuu hin taasifne fedhaIsaaniifis adabbii guddaatu jira.');
INSERT INTO "apaguna" VALUES(3,177,'3:177','Dhugumatti isaan iimaanaan kufriijijjiirratan, Rabbiin homaa hin miidhanuAmmas isaaniif adabbii laalessaatu jira.');
INSERT INTO "apaguna" VALUES(3,178,'3:178','Warri kafaran (adabbii) isaaniif tursiisuun gaarii isaaniif ta''uu hin yaadinKan Nuti isaaniif tursiifnuuf akka badii dabalataniif qofaAmmas isaaniif adabbii xiqqeessaatu jira.');
INSERT INTO "apaguna" VALUES(3,179,'3:179','Hanga fokkataa gaarii irraa adda fooyutti Rabbiin waan isin irra jirtan irratti kan mu’uminoota dhiisu hin taaneAmmas Rabbiin fagoo irratti kan isin mullisu hin taaneGaruu Rabbiin ergamtoota Isaa irraa nama fedhe filataRabbiifi ergamaa Isaatti amanaaYoo amantanii (Rabbiin) sodaattan, mindaa guddaatu isiniif jira.');
INSERT INTO "apaguna" VALUES(3,180,'3:180','Warri waan Rabbiin tola Isaa irraa isaaniif kenneen don’oomanu inni isaaniif gaarii ta''uu hin yaadinDhugumatti inni isaaniif badaadhaFuulduratti wanti isaan ittiin don’ooman Guyyaa Qiyaamaa (bofa ta’ee) isaanitti fannifamaDhaalli samiifi dachii kanuma RabbiitiRabbiinis waan isin dalagdan beekaadha.');
INSERT INTO "apaguna" VALUES(3,181,'3:181','Dhugumatti Rabbiin jecha warra “Rabbiin hiyyeessa; nuti dureeyyiidha” jedhanii dhagayeeraWaan isaan jedhaniifi ajjeechaa isaan nabiyyoota irratti haqaan ala raawwatanis ni barreessina“Adaba (ibidda) gubaas dhandhamaa” isaaniin jenna.');
INSERT INTO "apaguna" VALUES(3,182,'3:182','Sun sababaa waan harki keessan dabarfatteefi Rabbiinis gabroota kan miidhu ta''uu dhabuu isaatiini.');
INSERT INTO "apaguna" VALUES(3,183,'3:183',')Isaan) warra “hanga inni kennaa (qurbaana) ibiddi nyaatuun nutti dhufutti akka ergamaa kamittiyyuu hin amanne Rabbiin nu ajajeera” jedhaniidha“Dhugumatti ana duras ergamtoonni ragoota ifa ta’aniifi isa isin jettaniinis isinitti dhufanii jiruYoo kan dhugaa jettan taataniif maaliif isaan ajjeeftan?” jedhi.');
INSERT INTO "apaguna" VALUES(3,184,'3:184','Yoo si kijibsiisaniif, ergamtoonni si duraa kan ragoota ifa ta’an, xalayaafi kitaaba ibsaa ta''een dhufanis kijibsiifamanii jiru.');
INSERT INTO "apaguna" VALUES(3,185,'3:185','Lubbuun hundi du’a dhandhamtuudhaMindaawwan keessanis guutummaatti kan argattan Guyyaa QiyaamatiNamni ibidda irraa fageeffamee jannata seensifame, dhugumatti milkaayeeraJireenyi addunyaas meeshaa gowwoomsaa qofa.');
INSERT INTO "apaguna" VALUES(3,186,'3:186','Isin dhugumatti qabeenya keessaniifi lubbuu keessan keessatti ni qormaatamtuWarra isin dura kitaabni kennameef irraafi warra (Rabbitti) qindeessan irraas wantoota isin rakkisan baay’ee dhageessuYoo obsitanii, (Rabbiin) sodaattan, dhugumatti kun gocha cimaadha.');
INSERT INTO "apaguna" VALUES(3,187,'3:187','Yeroo Rabbiin warra kitaaba kennamaniin “namootaaf isa ibsaa; akka isa hin dhoksine” (jedheenii) irraa waadaa kakuun jabeeffame fuudhe [yaadadhu]Isaan garuu dugda duuba isaaniitti isa darbanii gatii xiqqaattis isa gurguratanWanti isaan bitatan waa fokkate!');
INSERT INTO "apaguna" VALUES(3,188,'3:188','Warra waan hojjatanitti gammadanii, waan hin dalagin irratti galateeffatamuu jaalatan hin yaadin; adabbii irraa baraaramuu isaaniis hin yaadinIsaaniifis adabbii laalessaa ta’etu jira.');
INSERT INTO "apaguna" VALUES(3,189,'3:189','Mootummaan samiifi dachii kan RabbiitiRabbiinis waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(3,190,'3:190','Uumuu samiifi dachii; wal bakka bu’uu halkaniifi guyyaa keessa warra sammuu qabaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(3,191,'3:191',')Isaan) warra dhaabatanii, taa’anii, cinaacha isaanii irrattis Rabbiin faarsanii, “yaa Gooftaa keenya! Ati kana taphaaf hin uumne, qulqulloofte, adabbii ibiddaa irraas nu baraarsi" jedhaa uumama samiifi dachii keessattis xiinxalaniidha');
INSERT INTO "apaguna" VALUES(3,192,'3:192',')Ammas) "Gooftaa keenya! Ati nama ibidda seensifte, dhugumatti isa salphiftee jirtaMiidhaa hojjattootaaf gargaaraan hin jiru.');
INSERT INTO "apaguna" VALUES(3,193,'3:193','Gooftaa keenya! dhugumatti nuti lallabaa ‘Gooftaa keessanitti amanaa’ jechuun iimaanaaf lallabu dhageenyee amannee jirraGooftaa keenya! badii keenya nuuf araarami; hamtuuwwan keenyas nurraa harcaasi; warra qulqulluu wajjinis nu ajjeesi.');
INSERT INTO "apaguna" VALUES(3,194,'3:194','Gooftaa keenya! Waan ergamtoota kee irratti waadaa nuuf seente nuuf kenniGuyyaa Qiyaamaas nu hin salphisinDhugumatti Ati waadaa hin faallessitu" (jedhaa xiinxalanii dha).');
INSERT INTO "apaguna" VALUES(3,195,'3:195','Gooftaan isaaniis “Ani dalagaa nama isin irraa dalagee kan dhiiraas ta’ee dhalaa hin balleessuGariin keessan garii irraayyi" jechuun isaaniif awwaateIsaannan godaananii; manneen isaanii irraa baafamanii, kan karaa kiyya keessatti rakkifamanii,fi lolaniifi ajjeefaman, dhugumatti hamtuuwwan isaanii isaan irraa harcaaseen ashaakiltii laggeen jala ishee yaatu isaan seensisaMindaa Rabbiin biraa ta’e (mindeffaman)Rabbiin isa bira mindaa gaariitu jira.');
INSERT INTO "apaguna" VALUES(3,196,'3:196','Dachii keessa garagaggaluun warra kafaranii si hin gowwoomsin.');
INSERT INTO "apaguna" VALUES(3,197,'3:197','(Kun) qananii muraasaErgasii teesson isaanii jahannamiAfatichi waa fokkate!');
INSERT INTO "apaguna" VALUES(3,198,'3:198','Garuu isaan Gooftaa isaanii sodaatan ishee keessatti hafoo ta''anii isaaniif ashaakiltiilee laggeen ishee jala yaatutu jiraAffeerraa Rabbiin biraa ta’e (affeeraman)Waan Rabbin biraatu qulqullootaaf caalaadha.');
INSERT INTO "apaguna" VALUES(3,199,'3:199','Warra kitaabaa irraa kan Rabbiif of gadi qaban, Keeyyattoota Rabbii maallaqa xiqqaan hin jijjiirranne ta''anii warra Rabbiitti, waan gara keessanitti buufameefi waan gara isaaniitti buufamettis amanantu jiruWarri sun isaaniif mindaan isaanii Gooftaa isaanii bira jiraDhugumatti, Rabbiin qormaanni isaa ariifataadha.');
INSERT INTO "apaguna" VALUES(3,200,'3:200','Yaa warra amantan! Obsaa; obsaan (diinota keessan) injifadhaa; karaa Rabbii keessattis of hidhaaAkka milkooftaniif jecha Rabbiin sodaadhaa');
INSERT INTO "apaguna" VALUES(4,1,'4:1','Yaa namootaa! Gooftaa keessan Isa lubbuu tokko (Aadam) irraa isin uumee, ishee irraa cimdii ishee (Hawwaa) uumee, isaan lamaan irraas dhiirotaafi dubartoota baay’ee tamsaase sodaadhaaRabbii ittiin wal kadhattaniifi firummaas (muruu) sodaadhaaDhugumatti, Rabbiin isin to’ataadha.');
INSERT INTO "apaguna" VALUES(4,2,'4:2','Yatiimotaafis qabeenya isaanii kennaaGaggaarii gadheedhaan hin jijjiiratinaaQabeenya isaaniis gara qabeenya keessaniitti (makuun) hin nyaatinaaDhugumatti inni badii guddaa ta''eera.');
INSERT INTO "apaguna" VALUES(4,3,'4:3','Yoo yatiimota (fuuchuu) keessatti wal hin qixxeessinuu sodaattan, dubartoota irraa isaan isinitti bareedan lama, sadi yookiin afur fuudhaaYoo wal hin qixxeessinuu sodaattan immoo tokkittii (fuudhaa); yookiin waan (gabrittii) harki keessan horatte (fuudhaa)Haqa dabsuu dhabuuf Kanatu irra dhihoodha.');
INSERT INTO "apaguna" VALUES(4,4,'4:4','Dubartootaaf mahriiwwan isaanii kenna jaalalaan ta''e kennaafiiYoo isaan jaalala lubbuu isaaniitiin isa irraa isiniif kennan, halaala gaarii haala ta''een isa nyaadhaa.');
INSERT INTO "apaguna" VALUES(4,5,'4:5','Qabeenya keessan kan Rabbiin dhaabbata (jireenyaa) isiniif taasise gowwootatti hin kenninaaIshee irraas soorata isaaniif kennaa; isaan uffisaas, jechoota beekamaas isaaniin jedhaa.');
INSERT INTO "apaguna" VALUES(4,6,'4:6','Yatiimota hanga isaan umrii gaa''ilaa ga’anitti qoradhaaYoo isaan irraa qajeeluma argitan, qabeenya isaanii gara isaaniitti deebisaa(Sodaa) guddachuu isaanii ariifatoo taatanii balleessuuf jecha ishee hin nyaatinaaNamni dureessa ta’e, (irraa) of haa qabuNamni hiyyeessa ta’e immoo beekamaadhaan haa nyaatuYeroo qabeenya isaanii gara isaaniitti deebiftan isaan irratti ragaa godhaaRagaadhaaf Rabbiinuu gahaadha.');
INSERT INTO "apaguna" VALUES(4,7,'4:7','Dhiirotaaf Waan haadhaa abbaafi firoonni dhiisan irraa qoodatu jiraDubartootaafis waan haadhaa abbaafi firoonni dhiisan irraa qoodatu jiraWaan isa irraa xiqqaateefi guddate irraas; Qooda murteeffamaa (Rabbiin isa qoodeera).');
INSERT INTO "apaguna" VALUES(4,8,'4:8','Yeroo firoonni, yatiimonniifi hiyyeeyyiin (bakka) qoodaa dhufan isa irraa isaaniif kennaa; jecha gaariis isaaniin jedhaa.');
INSERT INTO "apaguna" VALUES(4,9,'4:9','Warri odoo duuba isaaniitti ilmaan dadhaboo dhiisaanii (du''anii) isaan irratti sodaatanu, (yatiima miidhuu) haa sodaatanuRabbiin sodaatanii jecha haqaa haa jedhanu');
INSERT INTO "apaguna" VALUES(4,10,'4:10','Dhugumatti isaan qabeenya yatiimotaa miidhuudhaan nyaatan, wanti isaan garaa isaanii keessatti nyaatanu ibiddumaFuulduratti ibidda boba’aa seenu.');
INSERT INTO "apaguna" VALUES(4,11,'4:11','Rabbiin ilmaan keessan keessatti isiniif dhaamaDhiiraaf fakkaataa qooda durba lamaatu jiraYoo dubartii lamaafi sanaa ol ta’an, lama sadaffaa waan inni dhiiseetu isaaniif jiraYoo tokko qofa taate walakkaatu isheef jiraYoo isaaf (nama du’eef) ilmaan jiraate, abbaafi haadha isaatiif waan inni dhiise irraa tokko tokkoon isaaniitiif tokko jahaffaatu jiraYoo ilmi isaaf hin jiraatin, abbaafi haati isaa isa dhaalan, haadha isaatiif tokko sadaffaatu jiraYoo obboleeyyan isaaf jiraatan immoo haadha isaatiif tokko jahaffaatu jira(Kun hundinuu kan qoodamu) booda dhaamsa inni dhaammate yookiin dayniitiiyyiAbbootiin keessaniifi ilmaan keessan eenyuu isaaniitu bu''aa isiniif buusuutti dhihoodhaa hin beektanuMurtii Rabbi irraa ta''e (Rabbiin murteesseera)Rabbiin beekaa, ogeessa ta''e.');
INSERT INTO "apaguna" VALUES(4,12,'4:12','Yoo ilmoon isaaniif hin jiraatin walakkaa waan haati manaa keessanii dhiisaniitu isiniif jiraYoo ilmoon isaaniif jiraate immoo tokko arfaffaa waan isaan dhiisaniitu isiniif jira(Kun) booda dhaamsa isaan dhaammatanii yookiin dayniitiiyyiYoo ilmoon isiniif hin jiraatin tokko arfaffaa waan isin dhiiftaniitu isaaniifis jiraYoo ilmoon isiniif jiraate immoo tokko saddeetaffaa waan isin dhiiftaniitu isaaniif jira(Kun) booda dhaamsa isin dhaammatanii yookiin dayniitiiyyiHaala obboleessa yookiin obboleettii qabuun yoo dhiirri kan abbaa haadhaafi ilmaan hin qabne ta''ee dhaalamu argame yookiin dubartiinis tokko tokkoon isaaniitiif tokko jahaffaatu jiraYoo sanaa ol ta''an immoo isaan tokko sadaffaa keessatti hirmaattota(Kun) haala miidhamuun hin argaminiin booda dhaamsa dhaamamtee yookiin dayniitiiyyiDhaamsa Rabbi irraa ta’e (isiniif dhaame)Rabbiin beekaa, obsaadha.');
INSERT INTO "apaguna" VALUES(4,13,'4:13','Isheen kun daangawwan RabbiitiNamni Rabbiifi ergamaa Isaatiif ajajame jannattoota jala ishee laggeen yaatu keessatti hafoo ta''anii isaan seensisaKun milkii guddaadha.');
INSERT INTO "apaguna" VALUES(4,14,'4:14','Namni Rabbiifi ergamaa Isaa faallessee, daangaawwan Isaas dabre kan ishee keessatti hafu ta''ee (Rabbiin) ibidda isa seensisaIsaafis adabbii xiqqeessaa ta’etu jira.');
INSERT INTO "apaguna" VALUES(4,15,'4:15','Dubartoota keessan irraas isaan hojii fokkataa (qunnamtii seeraan alaa) raawwatan, namoota afur kan isin irraa ta’an isaan irratti ragaa baasaaYoo isaan ragaa ba’an hanga duuti isaanitti dhufutti yookiin (hanga) Rabbiin karaa (biraa) isaanii godhutti manneen keessatti isaan qabaa.');
INSERT INTO "apaguna" VALUES(4,16,'4:16',' Isin irraa namoonni lamaan (dhiiraafi dubartii) sagaagalummaa hojjatan isaan lamaanuu akkaan rakkisaaYoo isaan tawbatanii hojii gaarii hojjatan, isaan (rakkisuu) irraa gara galaaDhugumatti, Rabbiin akkaan tawbaa qeebalaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(4,17,'4:17',' Tawbaan qeebaltiin ishee Rabbi irra jiru warra wallaalumaan hamtuu hojjatanii ergasii dhihootti tawbataniifiWarra san Rabbiin tawbaa isaanii irraa qeebalaRabbiinis beekaa, ogeessa ta''eera.');
INSERT INTO "apaguna" VALUES(4,18,'4:18',' Tawbaan warra hanga tokkoon isaaniitti duuti dhufutti badii hojjatanii "ani amma tawbadhe" jedhaniifi isaannan kaafiroota ta’anii du’aniifis (qeebalamtuu) hin taaneWarra san Nuti adabbii laalessaa isaaniif qopheessinee jirra.');
INSERT INTO "apaguna" VALUES(4,19,'4:19',' Yaa warra amantan! dubartii dirqamaan dhaaluun isiniif hin eeyyamamuYoo hojii fokkataa raawwachuun isaanii ifatti beekame malee; garii waan (mahrii) isaaniif kennitanii fudhachuuf (heeruma) isaan hin dhorginaaAkkaataa gaariin isaan waliin jiraadhaaYoo isaan jibbitanis (obsaa); waan tokko isin jibbitanii Rabbiin isa keessa khayrii baay’eegodhuun ni kajeelama.');
INSERT INTO "apaguna" VALUES(4,20,'4:20',' Haala tokkoo isaaniitiif qabeenya guddaa kennitanii jirtanuun yoo niitii tokko bakka ishee kan biraa jijjiirrachuu barbaaddan, isa irraa homaa hin fudhatinaaSila haala miidhaafi dilii ifaa ta’ee jiruun fudhattuu?');
INSERT INTO "apaguna" VALUES(4,21,'4:21',' Sila bifa gariin keessan gara garii ga''ee jiruufi isaanis isin irraa waadaa jabaafi ulfaataa fudhatanii jiranuun akkamitti isa (mahrii isheef kennitan) fudhattu?');
INSERT INTO "apaguna" VALUES(4,22,'4:22',' Dubartoota irraa isaan abbootiin keessan fuudhan hin fuudhinaaGaruu wanti dhugumatti dabre (isiniif dhiifamaa dha)Dhugumatti inni fokkataa, jibbamaafi karaan isaa hamaa ta''ee jira.');
INSERT INTO "apaguna" VALUES(4,23,'4:23',' Haadholeen keessan, shamarran keessan, obboleettiwwan keessan, adaadowwan keessan, haboowwan (obboleettii haadhaa) keessan, shamarran obboleessaas, shamarran obboleettiis, haadholeen keessan kan isin hoosisanis, obboleettiiwwan keessan kanneen hoosisa irraa ta’anis, haadholee niitiiwwan keessaniis, shamarran isin guddiftanii gola keessan keessa jiran kan niitiiwwan keessan itti seentan irraa dhalatan (fuuchuun) isin irratti haraama godhamteetti; yoo isaan irra kan hin seenne taatan immoo badiin isin irra hin jiruNiitiiwwan ilmaan keessan kanneen dugda keessan irraa ta''anis, obboleettiiwwan lamaan walitti qabuunis (akkasuma haraama)Garuu kan duraan dabre (isiniif dhiifamaa dha)Dhugumatti Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(4,24,'4:24',' Dubartoota irraayis isaan (dhiiraan) tikfaman kan harki keessan dhuunfatte malee (dhorgamaa dha)Heera Rabbiin isin irratti murteesse (qabadhaa)Warra kanneeniin ala jiran of tiksaa sagaagalummaa irraa fagaataa taatanii qabeenya keessaniin barbaaduun isiniif eeyyamameeraIsaan irraa (warra qunnamtii saalaa waliin raawwachuun) ittiin qananiitan mahriiwwan isaanii haala dirqama ta''een isaanii kennaaWaan erga mahriin murtaa’ee booda ittiin wal jaalattan keessa badiin isin irra hin jiruDhugumatti Rabbiin beekaa, ogeessa ta’eera.');
INSERT INTO "apaguna" VALUES(4,25,'4:25',' Isin irraa namni (dubartoota) tikfamoo mu’umina ta’an fuuchuu qabeenyaan hin danda’in, shamarran mu’umina ta’an kan harki keessan dhuunfatte irraa (haa fuudhanu)Rabbiinis irra beekaa iimaana keessaniitiGariin keessan garii irraayyiEeyyama warra isaaniitiin isaan fuudhaaMahrii isaaniis haala gaarii ta’een isaaniif kennaaTikfamtuu ta''anii, sagaagalummaa kan hin raawwanneefi kan hiriyaa dhiiraa hin qabne ta''anii (fuudhaa)Yoo yeroo heeruman hojii fokkataa raawwatan, adabbii irraa walakkaa kan isaan walaba ta’an irra jiruutu isaan irra jiraKun isin irraa nama zinaa sodaateefi; obsuutu immoo isiniif caalaRabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(4,26,'4:26',' Rabbiin (waan eeyyamamaafi dhorgamaa ta’an) isiniif ibsuu, karaa warra isin dura turanii isin qajeelchuufi tawbaa keessan qeebaluu fedhaRabbiin beekaa, ogeessasi.');
INSERT INTO "apaguna" VALUES(4,27,'4:27',' Rabbiin tawbaa keessan qeebaluu fedhaWarri fedha lubbuu isaanii hordofan immoo isin jallina guddaa jallachuu fedhu.');
INSERT INTO "apaguna" VALUES(4,28,'4:28',' Rabbiin isin irraa salphisuu fedhaIlmi namaa dadhabaa ta’ee uumame.');
INSERT INTO "apaguna" VALUES(4,29,'4:29',' Yaa warra amantan! Yoo daldala jaalala keessan irraa ta''een malee qabeenya keessan gidduu keessanitti kijibaan hin nyaatinaaLubbuu keessanis hin ajjeesinaaDhugumatti Rabbiin isin mararfataadha.');
INSERT INTO "apaguna" VALUES(4,30,'4:30',' Namni daangaa dabruufi miidhaaf kana dalage, fuulduratti ibidda isa seensifnaKunis Rabbirratti salphaadha.');
INSERT INTO "apaguna" VALUES(4,31,'4:31',' Yoo badii gugurdoo irraa dhorgamtan irraa fagaattan, badii (xixiqqaa) keessan isin irraa haqnee, seensa kabajaa qabus isin seensifna.');
INSERT INTO "apaguna" VALUES(4,32,'4:32',' Wantoota Rabbiin garii keessan garii irra ittiin caalchises hin hawwinaaDhiirotaafis qooda waan isaan hojjatan irraa ta’etu jiraDubartootaafis qooda waan isaan hojjatan irraa ta’etu jiraRabbiin tola Isaa irraa kadhaaDhugumatti Rabbiin waan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(4,33,'4:33',' Hunda isaaniitiifiis (dhiiraafi dubartiif) waan abbaa haatiniifi firoonni dhiisan irraa dhaala taasifnee jirraIsaan kakuun keessan (firummaan) hiites qooda isaanii isaaniif kennaaDhugumatti Rabbiin waan hunda irratti ragaadha.');
INSERT INTO "apaguna" VALUES(4,34,'4:34',' Dhiironni dhimma dubartootatiin dhaabbatoodha; (kunis) waan Rabbiin garii isaanii garii irra caalchiseefi qabeenya isaanii irraas waan kennaniifiDubartoonni gaggaariin (Rabbiifi dhirsa isaaniitiif) ajajamoodha; fagoottis waan Rabbiin isaan eegsise tiksitootaIsaan finciluu isaanii sodaattan immoo isaan gorsaa; (itti aansun) ciisa keessatti isaaniin oodaa; (dhuma irratti yoo sirraa''uu didan) isaan rukutaaYoo isaan isiniin ajajaman isaan irratti karaa hin barbaadinaaDhugumatti Rabbiin ol ta’aa, guddaadha.');
INSERT INTO "apaguna" VALUES(4,35,'4:35',' Yoo gidduu isaan lamaaniitti waldhabbii beektan araarsaa tokko warra isaa irraa, araarsaa tokko immoo warra ishee irraa ergaaYoo isaan lamaan araaramuu (walii galuu) fedhan, Rabbiin gidduu isaan lamaaniitti walii galtee uumaDhugumatti Rabbiin beekaa, keessa beekaadha');
INSERT INTO "apaguna" VALUES(4,36,'4:36',' Rabbiin gabbaraa; Isatti homaa hin qindeessinaasAbbaa haadhaaf, firootaaf, yatiimotaaf, hiyyeeyyiif, ollaa firooma qabuuf, ollaa firooma hin qabneef, hiriyaa maddii keessan jiruufis, kara deemaafis, (gabroota) mirgi keessan dhuunfateefis tola (oolaa)Dhugumatti Rabbiin nama boonaa dhaaddataa ta’e hin jaalatu.');
INSERT INTO "apaguna" VALUES(4,37,'4:37',' Isaannan donnoomanii, namootas donnummatti ajajaniifi waan Rabbiin tola Isaa irraa isaaniif kenne dhoksan (hin jaalatu)Kaafirootaaf adabbii salphisaa qopheessinee jirra.');
INSERT INTO "apaguna" VALUES(4,38,'4:38',' Isaannan qabeenya isaanii akka namoonni isaan arganuuf arjooman, kan Rabbiifi Guyyaa Aakhiraattis hin amanne (isaan hiriyoota sheyxaanati)Namni sheyxaanni hiriyaa isaaf ta’e, hiriyummaan isaa waa fokkate!');
INSERT INTO "apaguna" VALUES(4,39,'4:39',' Sila odoo Rabbiifi Guyyaa Aakhiraatti amananiifi waan Rabbiin isaaniif kenne irraa kennatanii maaltu isaan irra jira? Rabbiinis isaan beekaadha.');
INSERT INTO "apaguna" VALUES(4,40,'4:40',' Dhugumatti Rabbiin hanga madaala atomii tokkoo hin miidhuYoo isheen dalagaa gaarii taate dachaa ishee taasisa; mindaa guddaa Isa biraa ta’es (dabalaan) ni kenna.');
INSERT INTO "apaguna" VALUES(4,41,'4:41',' Sila yeroo Nuti ummata hunda irraa ragaa fidneefi sillee warra kana irratti ragaa goonee si fidne (haalli isaanii) akkam ta’a?');
INSERT INTO "apaguna" VALUES(4,42,'4:42',' Guyyaa san warri kafaraniifi ergamicha faallessan odoo dachiin isaan irratti wal qixxaattee jaalatuIsaanis haasawa tokko Rabbiin dhoksuu hin danda’an.');
INSERT INTO "apaguna" VALUES(4,43,'4:43',' Yaa warra amantan! Haala machaahaa taataniin hanga waan dubbattanu beektanitti, karaa dabraaf malee yoo janaabaa taatanis hanga dhiqattanutti salaatatti hin dhihaatinaaYoo dhukkubsataa taatan, imala irra jiraattan, yookiin tokkoon keessan mana fincaaniitii (iddoo itti udaanuu) dhufe yookiin dubartootaan wal tuqxanii bishaan dhabdan biyyoo qulqulluu tayammumadhaa; fuula keessaniifi harka keessan haxaa’aaRabbiin irra dabraa araaramaadha.');
INSERT INTO "apaguna" VALUES(4,44,'4:44',' Sila gara warra kitaaba irraa qoodni isaaniif kennamee hin ilaallee? (Isaan) jallina bitu; isin karaa jallachuus ni barbaadu.');
INSERT INTO "apaguna" VALUES(4,45,'4:45',' Rabbiin diinota keessan irra beekaadhaEegumsaaf Rabbiin qofti ga’aa dha! Tumsaafis Rabbiin qofti ga’aadha');
INSERT INTO "apaguna" VALUES(4,46,'4:46',' Isaan yahuudoman irraa (warra) jechoota iddoo isaanii (kan sirrii) irraa jijjiirantu jiraNi jedhus: “dhageenye; faallessinesDhagayamuun maletti dhaga''i." Arraba isaanii micciiruufi amantii arrabsuuf jecha ''raa’inaa'' jedhuOdoo isaan “nuti dhageenye; toles jenne; atis dhagayi; nu mil’adhus (unzurnaa)” jedhanii, silaa santu isaaniif caalaafi irra sirrii tureGaruu Rabbiin sababaa kufrii isaanitiin isaan abaareKanaaf isaan xiqqoo malee hin amananu.');
INSERT INTO "apaguna" VALUES(4,47,'4:47','Yaa warra kitaaba fudhattan! odoo nuti fuula haqnee duuba ishee hin deebisin yookiin akka warra sanbataa abaarretti hin abaarin dura waan nuti buufne kan isa isin biraa dhugoomsutti amanaaAjajni Rabbii raawwatamaa ta’eera.');
INSERT INTO "apaguna" VALUES(4,48,'4:48','Dhugumatti Rabbiin Isatti waa qindeeffamuu hin araaramuWaan sanaa gadii nama fedheef ni araaramaNamni Rabbitti qindeesse dhugumatti cubbuu guddaa uumeera.');
INSERT INTO "apaguna" VALUES(4,49,'4:49','Sila gara isaan lubbuu ofii qulqulleessanii hin ilaallee? Lakki; Rabbiin nama ofii fedhe (qofa) qulqulleessaHanga kirrii qola Timiraa tokkoyyuu hin miidhamanu.');
INSERT INTO "apaguna" VALUES(4,50,'4:50','Akkamitti akka isaan Rabbirratti kijiba odeessan ilaaliCubbuu ifa bahaa ta’uuf inninuu ga’aadha.');
INSERT INTO "apaguna" VALUES(4,51,'4:51','Sila gara warra kitaaba irraa qoodni isaaniif kennamee hin ilaallee? Isaan sihriifi xaaghuutatti amanuIsaan kafaraniinis ni jedhu: “Warra kanatu isaan amanan (Muslimoota) caalaa karaa qajeelaadha."');
INSERT INTO "apaguna" VALUES(4,52,'4:52','Isaan Warra isaan Rabbiin abaareNama Rabbiin abaare, gargaaraa isaaf hin argitu.');
INSERT INTO "apaguna" VALUES(4,53,'4:53','Sila mootummaa irraa qoodni isaaniif jira moo? Yeroo sana hanga tuqaa ija (killee) timiraa irra jiru namaaf hin kennanu ture.');
INSERT INTO "apaguna" VALUES(4,54,'4:54','Moo sila waan Rabbiin tola Isaa irraa namootaaf kenne irratti isaan waanyu? Dhugumatti Nuti maatii Ibraahiimiif kitaabaafi ogummaa kenninee jirraMootummaa guddaas isaaniif kenninee jirra.');
INSERT INTO "apaguna" VALUES(4,55,'4:55','Isaan irraas nama isatti (Muhammaditti) amanetu jiraIsaan irraas nama isa irraa garagaletu jiraGubaatii (isaaniitiif) jahannam ga’aadha.');
INSERT INTO "apaguna" VALUES(4,56,'4:56','Dhugumatti warra keeyyattoota keenyatti kafaran, Nuti ibidda isaan seensifnaDhawaata gogaan isaanii gubatuun, akka isaan adabbii dhandhamaniif jecha gogaa isheen alaa isaaniif jijjiirraDhugumatti Rabbiin injifataa'' ogeessa ta''e.');
INSERT INTO "apaguna" VALUES(4,57,'4:57','Isaannan amananii gaggaarii hojjatan, Nuti jannata laggeen ishee jala yaatu isaan seensifnaIshee keessatti yeroo hunda hafoodhaAchi keessa isaaniif niitiiwwan qulqulleeffamtuutu jiruGaaddisa gaaddiddeessaas isaan seensifna.');
INSERT INTO "apaguna" VALUES(4,58,'4:58','Dhugumatti, Rabbiin amaanawwan gara abbootii isaanii geessuufi yeroo nama gidduutti murteessitanis haqaan murteessuu isin ajajaDhugumatti, wanti Rabbiin ittiin isin gorsu (barsiisu) akkam tolaadha! Rabbiin akkaan dhagayaa, akkaan argaa ta''e.');
INSERT INTO "apaguna" VALUES(4,59,'4:59','Yaa warra amantan! Rabbiif ajajamaa; ergamichaafi warra isin irraa abbootii taaytaa ta’aniifis ajajamaaYoo waan tokko keessatti wal mormitan, gara Rabbiifi ergamaa (Isaa) deebisaa; yoo Rabbiifi Guyyaa Aakhiraatti kan amantan taatan (gara san deebisaa)Kun caalaafi hiikaafis bareedaadha.');
INSERT INTO "apaguna" VALUES(4,60,'4:60','Sila gara warra waan gara keetti buufameefi waan si dura buufametti amanne jedhanii hin ilaallee? Odoo isatti kafaruu ajajamanii jiranuu gara xaaghuutaatti murteeffachuu barbaaduSheyxaannis jallina fagoo ta’e isaan jallisuu fedha.');
INSERT INTO "apaguna" VALUES(4,61,'4:61','Yeroo “gara waan Rabbiin buuseefi gara Ergamaa kottaa” isaaniin jedhame, kan munaafiqoonni gara galiinsa sirraa gara galan argita.');
INSERT INTO "apaguna" VALUES(4,62,'4:62','Yeroo balaan guddaan sababaa waan harki isaanii durfatteen isaan tuqee, ergasii Rabbitti kakataa “nuti toltuufi wal ta’iinsa malee waan biraa hin feene” (jedhaa) sitti dhufan akkam ta’u laata?');
INSERT INTO "apaguna" VALUES(4,63,'4:63','Isaan warra waan onnee isaanii keessa jiru Rabbiin beekuudhaIsaan irraayis gara galiIsaanis gorsiJecha gahaa lubbuu isaanii keessa seenus isaaniin jedhi.');
INSERT INTO "apaguna" VALUES(4,64,'4:64','Nuti ergamaa irraa akka eeyyama Rabbiitiin ajajni isaa fudhatamuuf malee hin ergineOdoo isaan (munaafiqoonni) yeroo lubbuu isaanii miidhan, sitti dhufanii Rabbiin araara kadhataniifi Ergamaanis araara isaaniif kadhatee, silaa dhugumatti akkaan araaramaa, akkaan rahmata godhaa ta’eetin Rabbiin argatu turan.');
INSERT INTO "apaguna" VALUES(4,65,'4:65','Lakki; Rabbii keetiinin kakadhaa! hanga waan gidduu isaaniitti irratti wal dhaban keessatti si murteessisanii ergasii waan ati murteessite irratti odoo lubbuu isaanii keessatti diddaa homaatuu hin mul’isiniifi fudhannaa (garaa guutuu ta’een) fudhatanitti isaan hin amananu.');
INSERT INTO "apaguna" VALUES(4,66,'4:66','Odoo Nuti isaan irratti “Lubbuu keessan ajjeesaa yookiin manneen keessan irraa ba’aa” (jennee) katabnee silaa warra muraasa isaan irraa ta’an malee (baay’een isaanii) isa hin dalaganuOdoo isaan waan itti ajajaman kana dalaganii silaa sanatu isaaniif caalaadhaAkkaan (amantii isaanii) raggaasisas ture');
INSERT INTO "apaguna" VALUES(4,67,'4:67','Silaa yeroo san dhugumatti nu biraa mindaa guddaa isaaniif kennina turre.');
INSERT INTO "apaguna" VALUES(4,68,'4:68','Silaa dhugumatti karaa sirrii ta’es isaan qajeelchina turre.');
INSERT INTO "apaguna" VALUES(4,69,'4:69','Namni Rabbiifi Ergamaadhaaf ajajames isaan sun nabiyyoota, dhugoomsitoota, shahiidotaafi warren gaggaarii irraa warra Rabbiin qanansiise waliin ta’uWarra kanaan hiriyoomuun waa tole!');
INSERT INTO "apaguna" VALUES(4,70,'4:70','Tolli kun Rabbi irraayyiBeekumsaanis Rabbiin qofti ga’aadha.');
INSERT INTO "apaguna" VALUES(4,71,'4:71','Yaa warra amantan! Of eeggannoo keessan qabadhaaGareenis duulaa yookiin dimshaashanis duulaa.');
INSERT INTO "apaguna" VALUES(4,72,'4:72','Dhugumatti, isin irraas nama (duula irraa) duubatti jedhutu jiraKanaaf yoo balaan wahii isin tuqe “Dhugumatti, Rabbiin yeroo akka ani isaan waliin (duula irratti) argamu hin taasisin qananii (Isaa) narratti dhangalaasee jira" jedha.');
INSERT INTO "apaguna" VALUES(4,73,'4:73','Yoo tolli Rabbirraa ta''e wahii isin tuqe immoo silaa dhugumatti inni akka waan gidduu keessaniifi gidduu isaa jaalalli wahii hin turinii “yaa hawwii kiyya! Odoo isaan waliin ta’ee, silaa milkii guddaa milkaaya ture” jedha.');
INSERT INTO "apaguna" VALUES(4,74,'4:74','(Mu’uminoonni) isaan jireenya addunyaa isa Aakhiraatti gurguratan karaa Rabbii keessatti haa lolanNamni karaa Rabbii keessatti lolee ergasii ajjeefame yookiin injifate, Nuti mindaa guddaa isaaf kennina.');
INSERT INTO "apaguna" VALUES(4,75,'4:75','Sila maaltu isiniif jiraatee karaa Rabbii keessatti, warra dadhaboo dhiirota, dubartoota, ijoollee irraa ta’an, isaan “Gooftaa keenya! Magaalattii jiraattonni ishee miidhaa raawwattoota ta''an kana irraa nu baasi; gargaaraa Si biraa ta’es nuuf taasisi; tumsaa Si biraa ta’es nuuf taasisi” jedhaniif jettanii hin lolle?');
INSERT INTO "apaguna" VALUES(4,76,'4:76','Warri amanan karaa Rabbii keessatti loluIsaan kafaran immoo karaa xaaghuutaa keessatti loluGargaaraa sheyxaanatiin lolaaDhugumatti tooftaan sheyxaanaa dadhabaa ta''e.');
INSERT INTO "apaguna" VALUES(4,77,'4:77','Sila gara isaan harka keessan (lola irraa) qabadhaa; salaatas gadi dhaabaa; zakaas kennaa jedhamanii hin ilaallee? Yeroma lolli dirqama isaan irratti ta’u yoma san gareen isaan irraa ta’e sodaa akka Rabbiin sodaatanii yookiin sodaa san caalu namoota sodaatan“Yaa Gooftaa keenya! Maaliif lola dirqama nurratti taasifte? Odoo yeroo gabaabaa nu tursiiftee (maal qaba)?” jedhan“Qananiin addunyaa xiqqaadha; Aakhiraan nama (Rabbiin) sodaatuuf caalaadhaHanga qajjiisaa (qola killee) Timiraa hin miidhamtan" jedhi.');
INSERT INTO "apaguna" VALUES(4,78,'4:78','“Eessallee yoo taatan duuti isin argataOdoo gamoowwan ciccimoo keessa taatanillee (isin irraa hin hafu)Yoo toltuun wahii isaan tuqu immoo “kun Rabbiin biraayi” jedhuYoo hamtuun wahii isaan tuqu immoo “(yaa Muhammad!) kun si biraayi" jedhu“Hunduu Rabbiin biraayyi" jedhiUmmanni kun maaltu isaaniif jiraayyi dubbii homaayyuu hubachuutti hin dhihaanne?');
INSERT INTO "apaguna" VALUES(4,79,'4:79','Toltuu irraa wanti si tuqe kamillee RabbirraayyiHamtuu irraa wanti si tuqe kamillee lubbuu kee irraayiNamootaaf ergamaa taasifneetiin si ergineRagummaaf Rabbiin qofti ga’adha.');
INSERT INTO "apaguna" VALUES(4,80,'4:80','Namni ergamichaan ajajame dhugaatti Rabbiin ajajameeraNama gara gale immoo isaan irratti eegaa taasifnee si hin ergine');
INSERT INTO "apaguna" VALUES(4,81,'4:81','(Yaanni keenya siif) ajajamuudha” jedhuYeroo si biraa bahan, gareen isaan irraa ta’e tokko faallaa waan ati jettee bulfataRabbiin waan isaan bulfatan ni barreessaIsaan irraa gara gali; Rabbi irrattis irkadhuIrkoo ta’uu irratti Rabbiin ga’aadha.');
INSERT INTO "apaguna" VALUES(4,82,'4:82','Sila isaan Qur’aana hin xiinxalanuu? Odoo waan Rabbiin alaa biraa ta’ee, silaa isa keessatti wal dhabbii baay’ee argu turan.');
INSERT INTO "apaguna" VALUES(4,83,'4:83','Yeroo tasgabbii yookiin sodaa irraa dhimmi wahii isaaniitti dhufe, (namoota keessatti) isa facaasuOdoo gara Ergamaatiifi gara nama isaan irraa abbootii dhimmaa (taaytaa) ta’anii isa deebisanii, silaa isaan irraa warri sirriitti qoratan (xiinxalan) isa beeku turanOdoo ni’imaan Rabbiitiifi rahmanni Isaa isin irra jiraachuu baatee, silaa isin muraasa (keessan) malee sheyxaana hordoftu turtan.');
INSERT INTO "apaguna" VALUES(4,84,'4:84','Karaa Rabbii keessattis loli; lubbuu kee malee hin dirqisiifamtuMu’uminootas kakaasiTasa Rabbiin rakkoo warra kafaranii (sirraa) qabuun ni kajeelamaGama lolaatiin Rabbitu irra cimaadha; jiilchaanis irra cimaadha.');
INSERT INTO "apaguna" VALUES(4,85,'4:85','Namni araarama gaarii araarse, ishee irraa qoodni (mindaan) isaaf ta’aNamni araarama gadhee araarses, ishee irraa qoodni (hamaan) isaaf ta’aRabbiin waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(4,86,'4:86','Yeroo kabajaa wahiitiin kabajamtan, ishee irra gaariidhaan kabajaa; yookiin isuma deebisaaDhugumatti, Rabbiin waan hunda irratti qorataadha');
INSERT INTO "apaguna" VALUES(4,87,'4:87','Rabbiin dhugaan gabbaramaan isa malee hin jiruDhugumatti, Guyyaa Qiyaamaa keessa walitti isin qaba; isa keessa mamiin hin jiruEenyutu Rabbiin caalaa haasa’a keessatti irra dhugaa dubbata?');
INSERT INTO "apaguna" VALUES(4,88,'4:88','Sila odoo Rabbiin sababaa waan isaan hojjataniitiin duuba isaan deebisee jiruu maaltu isiniif jiraannaan dhimma munaafiqootaa keessatti garee lamatti qoodamtan? Sila isin nama Rabbiin jallise qajeelchuu barbaadduu? nama Rabbiin jallise karaa (ittiin qajeelu) isaaf hin argitu');
INSERT INTO "apaguna" VALUES(4,89,'4:89','Odoo isin akkuma isaan kafaran kafartanii isaan waliin wal qixa taatanii jaalatuHanga karaa Rabbii keessatti godaananitti isaan irraa jaalallee hin qabatinaaYoo isaan gara galan, isaan qabaatii bakka argitanitti isaan ajjeesaaIsaan irraas jaalalleefi gargaaraa hin godhatinaa.');
INSERT INTO "apaguna" VALUES(4,90,'4:90','Warra gidduu keessaniifi gidduu isaaniitti walii galteen (nagaa) jiru wajjin hidhaa qaban yookiin kanneen qomni isaanii isiniin loluu irraa dhiphattee isinitti dhufan maleeRabbiin odoo fedhee isaan isin irratti ol taasisee isiniin lolu turanYoo isaan isin irraa fagaatanii isin hin loliniifi nagaa isiniif kennan Rabbiin (akka loltan) isaan irratti karaa isiniif hin goone.');
INSERT INTO "apaguna" VALUES(4,91,'4:91','Warra biraas kan tasgabbii isin irraafi tasgabbii ummata isaanii irraa barbaadan fuuldura ni argituDhawaata gara shirkiitti deebifamaniin ishee keessa cuuphamuYoo isaan isin irraa hin fagaatin, nagaa gara keessan hin dhaqqabsiisiniifi harka isaanii hin qabin, isaan qabaatii bakka argitanitti isaan ajjeesaaWarra kana, Nuti isaan irratti (lola akka deemsiftan) ragaa ifa ta’e isiniif kenninee jirra.');
INSERT INTO "apaguna" VALUES(4,92,'4:92','Yoo dogoggoraan ta’e malee Mu’umina ajjeesuun Mu’uminaaf hin ta''uNamni mu’umina dogoggoraan ajjeese, yoo isaan (maatiin nama ajjeefamee) dhiifama godhaniif malee gabra mu’uminaa bilisoomsuufi ''diyaa'' (gumaa dhiigaa) gara maatii isaa (nama du’ee) kennamutu (baasuun isarra jira)Yoo inni (namni ajjeefame) haala mu’umina ta’een ummata diinota keessan irraa ta’ee gabra mu’uminaa bilisoomsuutu irra jiraYoo inni ummata gidduu keessaniifi gidduu isaanii walii galteen (nagaa) jiru irraa ta’e, gumaa dhiigaa gara maatii isaa kennamuufi gabra mu’uminaa bilisoomsuutu (isarra jira)Namni (waan gabra ittiin bilisoomsu) hin argatin ji’a lama walitti aansee soomuutu (irra jira)Tawbaa Rabbi irraa ta''e (haa toobatu)Rabbiinis beekaa, ogeessa ta''eera.');
INSERT INTO "apaguna" VALUES(4,93,'4:93','Namni odoo beekuu mu’umina ajjeese, mindaan isaa, achi keessatti hafaa ta''ee, jahannamiRabbiinis isa irratti dallanee isas abaaree jiraadabbii guddaas isaaf qopheesse.');
INSERT INTO "apaguna" VALUES(4,94,'4:94','Yaa warra amantan! Yeroo karaa keessa deemtan (dubbii) adda baafadhaaBoojuun baay’een Rabbi bira jirtii meeshaa addunyaa barbaaduuf nama salaamtaa isiniin jedheen “ati Mu’uminaa miti" hin jedhinaaDuraan isinis akkasuma turtanii Rabbiin isin irratti tola oolee dubbii adda baafadhaaRabbiin waan isin hojjattan beekaa ta''eera.');
INSERT INTO "apaguna" VALUES(4,95,'4:95','Mu’uminoota irraa warri (jihaada irraa) taa’an, warra miidhama qaban malee, warra qabeenyaafi lubbuu isaaniitiin karaa Rabbii keessatti qabsaa’aniin wal qixa hin ta’anuRabbiin warra qabeenyaafi lubbuu isaaniitiin qabsaa’an isaan taa’an irra sadarkaa caalchisee jiraRabbiin hundaafuu jannata waadaa seeneefii jiraRabbiin warra qabsaa’an isaan taa’an irra mindaa guddaan caalchisee jira.');
INSERT INTO "apaguna" VALUES(4,96,'4:96','Sadarkaawwan Isa irraa ta’an, araaramaafi rahmataan (isaan caalchise)Raabbiin araaramaa, rahmata godhaa ta''eera.');
INSERT INTO "apaguna" VALUES(4,97,'4:97','Warri haala lubbuu isaanii miidhoo ta''aniin malaykaan isaan ajjeefte (malaykonni) “maaluma keessa turtan?” jedhan“Dachii keessatti hacuucamoo turre” jedhu“Sila dachiin Rabbii akka ishee keessa godaantaniif bal’aa hin taanee?” jedhanJarri sun qubsumni isaanii jahannamiDeebiin waa hammaatte!');
INSERT INTO "apaguna" VALUES(4,98,'4:98','Isaan dadhaboo dhiiraa, dubartootaafi ijoollee irraa ta’an kan karoora baafachuu hin dandeenyeefi karaa hin qajeelle malee.');
INSERT INTO "apaguna" VALUES(4,99,'4:99','Warra kana Rabbiin irra isaaniif dabruun dhugaadhaRabbiin akkaan irra dabraa, akkaan araaramaa ta''eera.');
INSERT INTO "apaguna" VALUES(4,100,'4:100','Namni karaa Rabbii keessatti godaane, dachii keessatti iddoo godaansaa hedduufi bal''ina argataNamni mana isaa irraa gara Rabbiitiifi ergamaa Isaa godaanuuf ba’ee ergasii duuti isa dhaqqabe, dhugumatti mindaan isaa Rabbiratti isaa mirkanaa’eRabbiin araaramaa, rahmata godhaa ta''eera.');
INSERT INTO "apaguna" VALUES(4,101,'4:101','Yeroo dachii keessa deemtanis, yoo warri kafaran isin rakkisuu sodaattan, salaata irraa gabaabsuu keessaniin badiin homaatuu isin irra hin jiruDhugumatti, kaafiroonni isiniif diinota ifaa ta''aniiru.');
INSERT INTO "apaguna" VALUES(4,102,'4:102','Yeroo isaan keessa jiraattee salaata isaaniin dhaabde, gareen isaan irraa ta’e tokko si waliin haa dhaabbatanu; meeshaa isaaniis haa qabatanuYeroo sujuudanii ka''an, duuba keessan irraan haa ta''anuGareen biraa isaan hin salaatin dhufanii sii waliin haa salaatanuOf eeggannoo isaaniifi meeshaa isaaniis haa qabatanuWarri kafaran odoo isin meeshaa keessaniifi galaa keessan irraa dagattanii dabiinsa tokkicha isin irratti dabanii jaalatuYoo rakkoon bokkaa irraa ta''e isin mudate yookiin dhukkubsataa taatan meeshaa keessan lafa kaayachuun badiin isin irra hin jiruOf eeggannoo keessan immoo qabadhaaDhugumatti, Rabbiin kaafirootaaf adabbii salphisaa qopheessee jira.');
INSERT INTO "apaguna" VALUES(4,103,'4:103','Yeroo salaata raawwattan, dhaabbataa, taa''aafi cinaacha keessan irra haala taataniin Rabbiin faarsaaYeroo tasgabbooftan salaata dhaabaaDhugumatti, salaanni mu’uminoota irratti dirqama yeroon isaa murtaa’e ta’eera.');
INSERT INTO "apaguna" VALUES(4,104,'4:104','Ummata (diinaa) barbaacha keessatti hin laafinaa; Yoo kan laalattan taatan isaanis akkuma isin laalattanitti laalatuRabbi irraas waan isaan hin kajeelle kajeeltuRabbiinis beekaa, ogeessa ta''eera.');
INSERT INTO "apaguna" VALUES(4,105,'4:105','Dhugumatti, akka waan Rabbiin sitti agarsiiseen gidduu namootaatti murteessituuf jecha kitaabicha haqaan gara keetti buufnee jirraGantootaaf falmaa hin ta''in.');
INSERT INTO "apaguna" VALUES(4,106,'4:106','Rabbiin araarama kadhuDhugumatti Rabbiin akkaan araaramaa, akkaan rahmata godhaa ta''eera.');
INSERT INTO "apaguna" VALUES(4,107,'4:107','Isaan lubbuu isaanii ganan irraas hin falminDhugumatti Rabbiin nama akkaan ganaa dilaawaa ta’e hin jaalatu.');
INSERT INTO "apaguna" VALUES(4,108,'4:108','Nama irraa ni dhokatu; haala inni yeroo isaan jecha irraa waan inni hin jaalanne bulfatan isaanii wajjin jiruun Rabbi irraa hin dhokatanuRabbiin waan isaan dalagan (beekumsa Isaatiin) marsaa ta''eera.');
INSERT INTO "apaguna" VALUES(4,109,'4:109','Dhaga''aa! Isin warri kun jireenya addunyaa keessatti isaan irraa falmitanii, Guyyaa Qiyaamaa immoo eenyutu isaan irraa falma? Yookiin eenyutu isaan irratti tiksaa ta''a?');
INSERT INTO "apaguna" VALUES(4,110,'4:110','Namni hamtuu hojjatee yookiin lubbuu isaa miidhee ergasii Rabbiin araarama kadhate, Rabbiin araaramaa, rahmata godhaa ta’uu arga');
INSERT INTO "apaguna" VALUES(4,111,'4:111','Namni badii oomishes, kan inni isa oomishu lubbuma isaa irrattiRabbiin beekaa, ogeessa ta''eera.');
INSERT INTO "apaguna" VALUES(4,112,'4:112','Namni dogoggora yookiin badii oomishee ergasii nama qulqulluu irratti isa dabre, inni dhugumatti cubbuufi badii ifa ta’e baateera.');
INSERT INTO "apaguna" VALUES(4,113,'4:113','Odoo tolli Rabbiitiifi rahmanni Isaa sirra jiraachuu baatee, silaa gareen isaan irraa taate tokko si jallisuuf yaaddeettiLubbuu isaanii malees homaa sin jallisanuHomaa si hin miidhanusRabbiin Qur’aanaafi hadiisa sirratti buuse; waan ati hin beeknes si barsiise, Tolli Rabbiin sirratti oolu guddaa ta’eera.');
INSERT INTO "apaguna" VALUES(4,114,'4:114','Nama sadaqaatti yookiin gaggaarii yookiin gidduu namaatti araarama uumuutti ajaje malee irra hedduu iccitii isaanii keessa toltuun hin jiruNama jaalala Rabbii barbaaduuf kana hojjate, fuulduratti mindaa guddaa isaaf kennina.');
INSERT INTO "apaguna" VALUES(4,115,'4:115','Namni erga karaan sirriin ifa isaaf ta’ee booda ergamicha faallesseefi karaa mu’uminootaatiin ala hordofe, waan inni moo''e (jallina) irratti isa moosifna; jahannamiinis isa seensifnaDeebittiin waa fokkatee!');
INSERT INTO "apaguna" VALUES(4,116,'4:116','Dhugumatti Rabbiin Isatti qindeeffamuu hin araaramuWaan sanaa gadii nama fedheef ni araaramaNamni Rabbitti qindeesse dhugumatti jallina fagoo ta’e jallateera.');
INSERT INTO "apaguna" VALUES(4,117,'4:117','Isaa gaditti dhaltuuwwan malee homaa hin gabbaranu; shayxaana darbamaa malees homaa hin gabbaranu.');
INSERT INTO "apaguna" VALUES(4,118,'4:118','Rabbiin isa abaareeraInnis ni jedhe: “Ani gabroota kee irraa qooda murtaa’e nan qabadha.');
INSERT INTO "apaguna" VALUES(4,119,'4:119','Isaanis nan jallisa; isaan nan hawwisiisas; isaanis nan ajajaa gurra beeyladaa ni tarsaasu; isaanis nan ajajaa uumama Rabbii ni jijjiiru." Namni Rabbiin ala sheyxaana jaalallee godhate, inni dhugumatti hoongoo ifa ta’e hoonga’eera.');
INSERT INTO "apaguna" VALUES(4,120,'4:120','Isaaniif waadaa seena; isaanis ni hawwisiisaShayxaanni gowwoomsaa malee isaaniif waadaa hin galu.');
INSERT INTO "apaguna" VALUES(4,121,'4:121','Warri sun qubsumni isaanii jahannami; ishee irraa gara itti dabanis hin argatanu.');
INSERT INTO "apaguna" VALUES(4,122,'4:122','Isaannan amananii, gaggaarii hojjatan, fuulduratti jannata laggeen ishee jala yaatu isaan seensifnaYeroo hunda achi keessatti hafuRabbiin waadaa seenee dhugaa isa godheNamni dubbiidhaan Rabbirra dhugaa eenyu? (Hin jiru).');
INSERT INTO "apaguna" VALUES(4,123,'4:123','Hawwii keessaniifi hawwii abbootii kitaabaatiin hin taane; namni hamtuu hojjate isaan mindeeffama; Rabbii gaditti jaalalleefi gargaaraas isaaf hin argu.');
INSERT INTO "apaguna" VALUES(4,124,'4:124','Dhiiraafi dhalaa irraa namni mu’umina ta’ee hojii gaggaarii hojjate, isaan sun jannata seenu; hanga tuqaa temiraas hin miidhamanu.');
INSERT INTO "apaguna" VALUES(4,125,'4:125','Amantii keessattis nama haala bareechaa ta''een fuula isaa Rabbiif bulchee tolcheefi amantii Ibraahiim qajeelaa haala ta''een hordofe caalaa namni tolche eenyu? Rabbiin Ibraahiimiin jaalallee godhateera.');
INSERT INTO "apaguna" VALUES(4,126,'4:126','Wantoonni samiifi wantoonni dachii keessa jiran (hundi) kan RabbiitiRabbiin waan hundatti (beekumsaan) marsaa ta''e.');
INSERT INTO "apaguna" VALUES(4,127,'4:127','Waa''ee dubartootaa keessatti si gaafatu"Waa''ee isaanii keessatti Rabbiin isiniif ibsaWanti kitaaba keessattis waa''ee dubartoota yatiimaa kan isin waan isaaniif barreeffame isaaniif hin kenninefi isaan fuuchuu jibbitaniifi dadhabdoota ijoollee irraa ta''aniifi yatiimotaafis haqaan dhaabbachuu isin irratti dubbifamutu (isinitti hima)” jedhiToltuu irraa waan isin hojjattan kamillee dhugumatti Rabbiin isa beekaa ta’eera.');
INSERT INTO "apaguna" VALUES(4,128,'4:128','Yoo dubartiin tokko dhirsa ishee irraa fagaachuu yookiin irraa gara galiinsa sodaatte, gidduu isaaniitti walii galtee uumuun badiin isaan irra hin jiruWalii galteen gaariidhaLubbuun don’ummaa kennamteYoo tolchitanii (badii irraa) eeggattan, dhugumatti Rabbiin waan isin hojjattan (hunda) keessa beekaadha.');
INSERT INTO "apaguna" VALUES(4,129,'4:129','Odoma bololtaniillee gidduu dubartootaa wal qixxeessuu hin dandeessanu; (Gara ishee tokkoo) dabiinsa hunda dabdanii (ishee kaan) akka rarraafamtuutti hin dhiisinaaYoo tolchitanii (Rabbiin) sodaattan, dhugumatti Rabbiin araaramaa, rahmata godhaa ta’eera.');
INSERT INTO "apaguna" VALUES(4,130,'4:130','Yoo isaan adda ba’an, Rabbiin hundaayyuu tola Isaa irraa ni duroomsaRabbiin (kenni isaa) bal''aa, ogeessa ta''eera.');
INSERT INTO "apaguna" VALUES(4,131,'4:131','Wantoonni samii keessaafi wantoonni dachii keessaa kan RabbiitiDhugumatti warra isin dura kitaaba kennamaniifi isiniifis Rabbiin sodaadhaa jechuu dhaamnee jirraYoo kafartanis Wantoonni samii keessaafi wantoonni dachii keessaa kan RabbiitiRabbiin dureessa, faarfamaa ta’eera.');
INSERT INTO "apaguna" VALUES(4,132,'4:132','Wantoonni samiifi dachii keessaa hundi kan RabbiitiHirkoodhaaf Rabbumatu ga’aadha.');
INSERT INTO "apaguna" VALUES(4,133,'4:133','Yaa namootaa! (Rabbiin) yoo fedhe isin deemsisee warra biraa fidaRabbiin kana irratti danda’aa ta''eera.');
INSERT INTO "apaguna" VALUES(4,134,'4:134','Namni mindaa addunyaa kan fedhu ta''e, mindaan addunyaafi Aakhiraa Rabbiin bira qofa jiraRabbiin dhaga’aa, argaa ta’eera.');
INSERT INTO "apaguna" VALUES(4,135,'4:135','Yaa warra amantan! odoma lubbuu keessan yookiin abbaa haadha keessaniifi firoota keessan irrattis ta’eeyyuu haqaan dhaabbataa Rabbiif ragaa bahaa ta’aaYoo dureessa yookiin hiyyeessa ta’anis Rabbitu irra isaanitti aanaHaqa irraa dabuuf fedha lubbuu keessanii hin hordofinaaYoo (ragaa) dabsitan yookiin (ragaa bahuu irraa) gara galtan, dhugumatti Rabbiin waan isin dalagdan hunda beekaa ta''eera.');
INSERT INTO "apaguna" VALUES(4,136,'4:136','Yaa warra amantan! Rabbitti, ergamaa Isaatti, kitaaba ergamaa Isaa irratti buufamettiifi kitaaba Inni sana dura buusetti amanaaNamni Rabbitti, maleeykota Isaatti, kitaabota Isaatti, ergamtoota Isaattiifi Guyyaa Aakhiraatti kafare, inni dhugumatti jallina fagoo jallateera.');
INSERT INTO "apaguna" VALUES(4,137,'4:137','Dhugumatti isaan amananii ergasii kafaran, ergasii amananii ergasii kafaran, ergasiis kufrii daddabalatan, Rabbiin kan isaaniif araaramuufi karaa isaan qajeelchu hin taane.');
INSERT INTO "apaguna" VALUES(4,138,'4:138','Adabni laalessaan isaaniif jira jechuudhaan munaafiqoota gammachiisi.');
INSERT INTO "apaguna" VALUES(4,139,'4:139',')Isaan) warra mu’uminootaa gaditti kaafiroota jaalallee godhataniidhaSila jabeenya isaan bira barbaaduu? Dhugumatti, jabeenyi hundi kan Rabbiiti.');
INSERT INTO "apaguna" VALUES(4,140,'4:140','Dhugumatti, kitaaba keessatti “yeroo keeyyattoota Rabbii kan isaanitti kafaramuufi isaanitti qoofamu dhageessan, hanga isaan haasa’a biraa keessa seenanitti isaan waliin hin taa’inaa” jechuun (Rabbiin) isin irratti buuseeraYoos isinis fakkaattota isaaniitiDhugumatti, Rabbiin munaafiqootaafi kaafiroota jahannam keessatti walitti qabaadha.');
INSERT INTO "apaguna" VALUES(4,141,'4:141',')Isaan) warra hamtuu isinitti eeganuudhaYoo injifannoon Rabbi irraa isiniif ta’e “nuti isinii wajjiin hin turree?” jedhuYoo carraan kaafirootaf ta’e immoo “sila nuti isin irratti akkaan hin hoggannee? Mu’uminoota isin irraa hin dhorginee?” jedhuRabbiin Guyyaa Qiyaamaa gidduu keessanitti ni murteessaRabbiin kaafirootaaf mu’uminoota irratti karaa hin godhu.');
INSERT INTO "apaguna" VALUES(4,142,'4:142','Dhugumatti munaafiqoonni Rabbiin ganuInnis (mindaa dalagaa isaaniitiif) ganaa isaaniitiYeroo gara salaataa ka''anis, taatiyaahaa, nama argisiifataa dhaabbatuXiqqoo malee Rabbiin hin faarsanu.');
INSERT INTO "apaguna" VALUES(4,143,'4:143','Gidduu kanatti dhama''oo ta''anii gara isaan kanaas miti; gara isaan sanaas mitiNama Rabbiin jallise, karaa (qajeelumaa) isaaf hin argitu.');
INSERT INTO "apaguna" VALUES(4,144,'4:144','Yaa warra amantan! Mu’uminootaa gaditti kaafiroota jaalallee hin taasifatinaaSila isin irratti Rabbiif ragaa ifa ta’e taasisuu feetuu?');
INSERT INTO "apaguna" VALUES(4,145,'4:145','Dhugumatti, munaafiqoonni ibidda irraa sadarkaa gadii keessa ta''uTumsaas isaaniif hin argitu.');
INSERT INTO "apaguna" VALUES(4,146,'4:146','Isaan tawbatanii, tolchanii, Rabbiin qabataniifi amantii isaanii Rabbiif qulqulleessan maleeWarri kun mu’uminoota waliin ta’uRabbiinis mu’uminootaaf mindaa guddaa ni kenna');
INSERT INTO "apaguna" VALUES(4,147,'4:147','Yoo isin (Isa) galateeffattanii (Isatti) amantan Rabbiin adabbii keessan irraa maal godha? Rabbiin galateeffataa, beekaa ta’eera.');
INSERT INTO "apaguna" VALUES(4,148,'4:148','Yoo nama miidhame ta’e malee Rabbiin jecha irraa hamaa ifa baasuu hin jaalatuRabbiinis dhagayaa, beekaa ta''eera.');
INSERT INTO "apaguna" VALUES(4,149,'4:149','Yoo toltuu ifa gootan yookiin isa dhoksitan yookiin hamtuu irraa dhiifama gootan, dhugumatti Rabbiin akkaan dhiifama godhaa, danda’aa ta''eera.');
INSERT INTO "apaguna" VALUES(4,150,'4:150','Dhugumatti, isaan Rabbiifi ergamtoota Isaatti kafaran, kan gidduu Rabbiitiifi ergamtoota Isaa adda baasuu fedhan, “garii Isaatti ni amanna; garii Isaatti ni kafarra” jedhanii, gidduu sanitti karaa godhachuu fedhan,');
INSERT INTO "apaguna" VALUES(4,151,'4:151','Isaan warra dhugaan kafaraniidhaNutis kaafirootaaf adabbii xiqqeessaa qopheessinee jirra.');
INSERT INTO "apaguna" VALUES(4,152,'4:152','Isaan Rabbiifi ergamtoota Isaattis amanan, gidduu tokkoon isaan irraa ta''ees adda hin baasin, isaan san (Rabbiin) mindaa isaanii isaaniif kennaRabbiin araaramaa, rahmata godhaa ta''eera.');
INSERT INTO "apaguna" VALUES(4,153,'4:153','Abbootiin kitaabaa samii irraa kitaaba isaan irratti buusuu si gaafatuMuusaas waan kana irra guddaa gaafataniiru“Rabbiin ifatti nu agarsiisi” jedhaniSababaa miidhaa isaaniitiin gaggabbiin (duuti) isaan qabdeSana booda erga mallattoowwan isaanitti dhufanii jibicha (gabbaramaa) godhatanSanas irra isaaniif dabrineMuusaaf ragaa ifa ta’e kennine.');
INSERT INTO "apaguna" VALUES(4,154,'4:154','Waadaa isaan galchuuf gaara isaan irratti ol kaafnee “sujuuda godhaa balbalicha seenaa” isaaniin jenne“Sanbata keessattis daangaa hin tarinaa" isaaniin jenneWaadaa ulfaataas isaan irraa fuune.');
INSERT INTO "apaguna" VALUES(4,155,'4:155','Sababa isaan waadaa isaanii diiguutiin, keeyyattoota Rabbiitti kafaruutiin, nabiyyootas karaan ala ajjeesuu isaaniitiifi “onneen keenya haguugamtee jirti” jechuu isaaniitiin (isaan abaarre)Dhugumatti Rabbiin sababaa kufrii isaaniitiif ishee (onnee isaanii) irratti haguuge; muraasa malee hin amananu.');
INSERT INTO "apaguna" VALUES(4,156,'4:156','Sababaa kufrii isaaniitiifi Maryam irratti kijiba guddaa odeessuu isaanitiif (isaan abaarre).');
INSERT INTO "apaguna" VALUES(4,157,'4:157','Ammas waan isaan “nuti Masiih Iisaa ilma Maryam, ergamaa Rabbii, ajjeefnee jirra” jedhaniif (isaan abaarre)Isaaniitti fakkeeffame malee isa hin ajjeefne; hin fannisnesDhugumatti, isaan isa keessatti wal dhaban isa irraa shakkii keessa jiruYaada hordofuu malee isa irratti beekumsa homaatuu hin qabanQabatamaatti isaan isa hin ajjeefne.');
INSERT INTO "apaguna" VALUES(4,158,'4:158','Dhugumatti Rabbiin gara isaatti ol isa fuudheRabbiin injifataa, ogeessa ta''e.');
INSERT INTO "apaguna" VALUES(4,159,'4:159','Abbootiin kitaabaa kamillee odoo hin du''in dura isatti amanu malee hin hafuGuyyaa Qiyaamaas isaan irratti ragaa ta’a.');
INSERT INTO "apaguna" VALUES(4,160,'4:160','Sababaa miidhaa warra yahuudomaniifi sababaa isaan (namoota) baay’ee karaa Rabbii irraa deebisaniif gaggaarii halaala isaaniif ta’ee ture haraama isaan irratti taasifne.');
INSERT INTO "apaguna" VALUES(4,161,'4:161','Dhalas odoo dhugumaan irraa dhorgamanii jiranuu fudhachuu isaaniitiin, horii namaas karaa malee waan nyaataniif (isaan abaarre)Kaafiroota isaan irraa ta''aniif adabbii laalessaa qopheessineerra.');
INSERT INTO "apaguna" VALUES(4,162,'4:162','Garuu isaan irraa warri beekumsa gadi fagoo qabaniifi mu’uminoonni waan gara keetti bu’eefi waan si dura bu’etti ni amanu; warra salaata dhaabanin (faarsa)Warri zakaa kennaniifi, Rabbiifi Guyyaa Aakhiraattis amanan, isaan sana mindaa guddaa isaaniif kennina.');
INSERT INTO "apaguna" VALUES(4,163,'4:163','Dhugumatti, Nuti akkuma gara Nuuhiifi nabiyyoota isa booda turaniitti beeksisa goone, gara keetti beeksisa goonee jirra; Gara Ibraahiim, Ismaa’iil, Isihaaq, Ya’aquub, ilmaan Ya’aquub, Iisaa, Ayyuub, Yuunus, Haaruuniifi Suleymaanittis beeksisa goonee jirraDaawuudiifis ‘Zabuur’ kennineerra.');
INSERT INTO "apaguna" VALUES(4,164,'4:164','Ergamtoota san dura seenaa isaanii siif himneefi kan seenaa isaanii siif hin himiniis (erginee jirra)Rabbiin haasofsiisuudhuma Muusaa haasofsiise.');
INSERT INTO "apaguna" VALUES(4,165,'4:165','Akka Ergamtoota booda Rabbi irratti namaaf ragaan hin argamneef Gammachiisoofi dinniinoo taasifnee ergamtoota (ergine.) Rabbiinis injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(4,166,'4:166','Garuu Rabbiin waan gara keetti buuse irratti ragaa ba’aBeekumsa Isaatiin isa buuseMalaa’ikoonnis ragaa ba’uRagaadhaaf Rabbiin qofti ga’aadha.');
INSERT INTO "apaguna" VALUES(4,167,'4:167','Dhugumatti, warra kafaraniifi karaa Rabbii irraa (namoota) deebisan, dhugumatti isaan jallina fagoo ta’e jallataniiru.');
INSERT INTO "apaguna" VALUES(4,168,'4:168','Dhugumatti, isaan kafaraniifi miidhaa hojjatan, Rabbiin kan isaaniif araaramuufi karaa isaan qajeelchu hin taane.');
INSERT INTO "apaguna" VALUES(4,169,'4:169','Garuu ishee keessatti yeroo hunda hafoo ta''anii karaa jahannam (isaan qajeelcha)Kun Rabbi irratti laafaa ta’eera.');
INSERT INTO "apaguna" VALUES(4,170,'4:170','Yaa namootaa! Dhugumatti, ergamaan dhugaa Gooftaa keessan irraa ta’een isinitti dhufeeraa; isatti amanaa caalaa isiniif (ta''aa)Yoo kafartanis, wantoonni samiifi dachii keessaa kan RabbiitiRabbiinis beekaa, ogeessa ta’eera.');
INSERT INTO "apaguna" VALUES(4,171,'4:171','Yaa abbootii kitaabaa! amantii keessan keessatti daangaa hin dabrinaa; Rabbi irrattis dhugaa malee hin haasa’inaaIlmi Maryam, Masiih, Iisaan ergamaa Rabbiiti; jecha Isaa (ta''i jedhu) kan Inni gara Maryam darbeedhaRuuhii (uumaa) isaa irraa ta''eedhaRabbiifi ergamtoota Isaattis amanaa“Rabbiin sadihi” hin jedhinasIsarraa dhorgamaa; isiniif caalaa (ta''aa)Rabbiin dhugaan gabbaramaa tokkicha qofaInni ilmi Isaaf ta’uu irraa qulqullaa’eWantoonni samii keessaafi wantoonni dachii keessaa kan IsaatiHirkoodhaafis Rabbiin qofti ga’aadha.');
INSERT INTO "apaguna" VALUES(4,172,'4:172','Masiih Rabbiif gabricha ta’uu irraa hin nuffu; Malaa’ikoonni dhihoonis (hin nuffanu)Namni Isa gabbaruu irraa nuffeefi boone, Inni hunda isaanii gara Isaatti walitti isaan qaba.');
INSERT INTO "apaguna" VALUES(4,173,'4:173','Isaannan amananiifi gaggaarii hojjatan, mindaa isaanii isaaniif guutaTola Isaa irraas isaaniif dabalaIsaannan nuffaniifi boonan adabbii laalessaa isaan adabaIsaaniifis Rabbii gaditti jaalallees ta’ee tumsaa hin argatanu.');
INSERT INTO "apaguna" VALUES(4,174,'4:174','Yaa namootaa! Dhugumatti ragaan Gooftaa keessan irraa ta''e isinitti dhufeeraIfa mul’ataa taasifnees gara keessanitti buufnee jirra.');
INSERT INTO "apaguna" VALUES(4,175,'4:175','Isaannan Rabbitti amananii, Isa qabatan immoo, fuulduratti rahmata Isa irraa ta’eefi tola keessa isaan seensisaGara isaatti karaa qajeelaa isaan qajeelcha.');
INSERT INTO "apaguna" VALUES(4,176,'4:176','Si gaafatuJedhi: “Rabbiin waa''ee ‘kalaalah’ (nama jalaafi gubbaan kan isa dhaalu hin qabne) keessatti isiniif ibsaYoo namni ilma hin qabne tokko obboleettii odoo qabuu du’e isheedhaaf walakkaa waan inni dhiisetu jiraInnis yoo isheen ilma hin qabaatin ishee dhaalaYoo isaan (obboleettiiwwan) lama ta’an, isaaniif dhaala inni dhiise irraa lama sadaffaatu jiraYoo obbolaan dhiirotaafi dubartoota ta''an immoo dhiiraaf fakkii qooda dubartii lamaatu jiraRabbiin akka isin hin jallanneef isiniif ibsaRabbiin waan hunda beekaadha."');
INSERT INTO "apaguna" VALUES(5,1,'5:1','Yaa warra amantan! Waadaa guutaa; (Hajjiifi umraa) kan hidhattan taatanii adamsuu halaala haala hin godhatiniin wanti isin irratti dubbifamu qofti odoo hafuu beelladoonni isiniif halaala godhamteettiRabbiin waan fedhe murteessa.');
INSERT INTO "apaguna" VALUES(5,2,'5:2','Yaa warra amantan! Mallattoolee Rabbii, ji''a kabajamo, horii wareegaaf dhufan, horii (wareega hajjiitiif) morma isaanii irratti mallattoon fannifame,fi warra tolaafi jaalala Gooftaa isaanii barbaacha gara mana kabajamaa fedhanuus halaala hin godhatinaaYeroo ihraama (hajjii yookiin umraa) raawwattan adamsadhaaWaan masjida kabajamaa irraa isin deebisaniif jibbaan ummata (Makkaa) daangaa dabruutti isin hin kakaasinToltuufi sodaa Rabbii irratti wal gargaaraaBadiifi daangaa dabruu irratti wal hin gargaarinaaRabbiin sodaadhaaDhugumatti Rabbiin adabbiin isaa akkaan cimaadha.');
INSERT INTO "apaguna" VALUES(5,3,'5:3','(Isinirratti haraama godhamte) Baktiin, dhiigni, foon booyyee, waan (maqaan) Rabbiin alaa isa irratti ol fuudhame, hudhamtee kan duutes, rukutamtee kan duutes, konkolaattee kan duutes, kan gaafaan waraanamtee duutes, wanti bineensi nyaates yoo (odoo inni hin du’in dura) gorraatan malee, wanti taabota irratti gorra’ames, xiyyaan carraa ilaallachuunis isin irratti haraama godhamteettiKuni fincilaHar''a warri kafaran amantii keessan irraa abdii muratanii jiruIsaan hin sodaatinaa; Anuma (qofa) sodaadhaaAmantii keessan har''a isiniif guuteera, ni’imaawwan kiyyas isin irratti guuteeraAmantiis Islaama isiniif jaaladheeraNamni beela keessatti dhiphifame, kan gara badii dabuu hin yaadin (isaaf dhiifama)Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(5,4,'5:4','Maaltu akka isaaniif hayyamame si gaafatu"Gaggaariin hunduu, waan Rabbiin isin barsiise irraa kan barsiiftan taatanii wanti isin leenjiftoota taatanii carraaqxota irraas barsiiftan isiniif hayyamameeraWaan isaan isiniif qaban irraa nyaadhaaMaqaa Rabbiis isa irratti dubbadhaaRabbiin sodaadhaaDhugumatti, Rabbiin qormaanni (Isaa) saffisaadha.');
INSERT INTO "apaguna" VALUES(5,5,'5:5','Har''a gaggaariin hundi isiniif eeyyamameeraNyaanni abbootii kitaabaas isiniif eeyyamamaadhaNyaanni keessanis isaaniif hayyamamaadhaTikfattoonni dubartoota mu’uminoota irraa ta’aniifi tikfattoonni dubartoota warra isiniin dura kitaaba kennaman irraa ta''anii fuudhoo, kan sagaagala hin dalagneefi sanyoo (jaalallee dhoksaa) hin qabanne taatanii (isiniif eeyyamama)Namni iimaana irraa gara gale, dhugumatti dalagaan isaa badeInnis Aakhiraatti warra hoonga’an irraa ta’a');
INSERT INTO "apaguna" VALUES(5,6,'5:6','Yaa warra amantan! Yeroo salaataaf kaatan, fuula keessaniifi harka keessan hanga ciqileetti dhiqadhaaMataa keessaniis haxaa''aa; Luka keessanis hanga koronyootti (dhiqaa)Yoo janaabaa taatan (bishaan saalaa isin irraa dhangala’e) dhiqadhaaYoo dhukkubsatoo taatan yookiin imala irra jiraattan yookiin tokkoon keessan mana fincaanii irraa dhufe yookiin yoo dubartiin wal tuqxanii (qunnamtii saalaa gootanii), ergasii bishaan hin argatin dachii (biyyee) qulqulluu barbaaddadhaa (tayammuma godhadhaa)Isa irraas fuula keessaniifi harka keessan haxaa''aaRabbiin rakkina tokkollee isin irra fiduu hin fedhuGaruu akka isin galateeffattaniif jecha isin qulquleessuufi ni’imaa Isaa isin iraatti guutuu fedha.');
INSERT INTO "apaguna" VALUES(5,7,'5:7','Ni’imaa Rabbii kan isin irra jiruufi waadaa Isaa kan yeroo isin "dhageenye; awwaannes" jettan Inni isiniif gales yaadadhaaRabbiin sodaadhaaDhugumatti, Rabbiin beekaa waan qoma keessaati.');
INSERT INTO "apaguna" VALUES(5,8,'5:8','Yaa warra amantan! Rabbiif gadi dhaabbataa ta’aa; haqaan ragaa ba’aa ta''aaJibbaan ummataa haqa dalaguu dhiisuu irratti isin hin kakaasinHaqa hojjadhaa; isatu sodaa Rabbiitti irra dhihoodhaRabbiin sodaadhaasDhugumatti, Rabbiin waan isin hojjattan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(5,9,'5:9','Rabbiin isaan amananii gaggaariis hojjatan, isaaniif araaramaafi mindaa guddaatu jira (jechuun) waadaa seeneera.');
INSERT INTO "apaguna" VALUES(5,10,'5:10',' Isaan kafaranii keeyyattoota keenyas sobsiisan, isaan warra ibiddaati.');
INSERT INTO "apaguna" VALUES(5,11,'5:11',' Yaa warra amantan! Ni’imaa Rabbii kan yeroo ‘ummanni harka isaanii gara keessan diriirsuu yaadanii, (Rabbiin) harka isaanii isin irraa qabe’ isin irra jiru yaadadhaaRabbiin sodaadhaaMu’uminoonnis Rabbi irratti haa irkatan.');
INSERT INTO "apaguna" VALUES(5,12,'5:12',' Dhugumatti, Rabbiin waadaa Ilmaan Israa’iil qabatee jiraIsaan irraas qondaalota kudha lama goonee jirraRabbiinis "Ani isin waliinin jiraYoo isin salaata dhaabdanii zakaas kennitanii, ergamoota kiyyattis amantanii, isaan gargaartanii, Rabbiif liqaa bareedaa liqeessitan, dhugumatti Ani hamtuuwwan keessan isin irraa harcaaseeti, Jannata laggeen ishee jala yaatu isin seensisaIsin irraa namni san booda kafare, inni dhugumatti karaa qajeelaa irraa jallateera" jedhe.');
INSERT INTO "apaguna" VALUES(5,13,'5:13',' Sababaa isaan waadaa isaanii diiganiif, isaan abaarree onnee isaaniis gogaa taasifneIddoowwan isaanii (kan sirrii) irraa jechoota jijjiiruWaan ittiin gorfaman irraas qooda wahii dagatanIsaan irraa nama muraasa malee gantuu isaan irraa taate arguurraa hin deemtuDhiifama isaaniif godhi; irras dabriDhugumatti, Rabbiin tolchitoota ni jaalata.');
INSERT INTO "apaguna" VALUES(5,14,'5:14',' Isaan nuti Kiristaanota jedhan irraallee waadaa isaanii fuunee, waan ittiin gorfaman irraa qooda wahii dagatanHanga Guyyaa Qiyaamatti gidduu isaanitti diinummaafi jibbaa buufneRabbiinis waan isaan dalagaa turan fuula dura isaanitti hima.');
INSERT INTO "apaguna" VALUES(5,15,'5:15',' Yaa warra kitaabaa! Ergamaan keenya kan irra hedduu kitaabaa irraa waan isin dhoksaa turtan isiniif ibsuufi baay’ee isaa immoo dhiifamaan irra isiniif dabru isinitti dhufeeraDhugumatti, iftiifi kitaabni ifaa ta’e Rabbi irraa isiniif dhufeera.');
INSERT INTO "apaguna" VALUES(5,16,'5:16',' Rabbiin nama jaalala Isaa karaa nagaa hordofe ittiin (kitaabichaan) qajeelchaDukkana irraa gara ifaatti eeyyama Isaatiin isaan baasaGara karaa sirriis isaan qajeelcha.');
INSERT INTO "apaguna" VALUES(5,17,'5:17',' Warri “Rabbiin isumatu Masiih ilma Maryami” jedhan dhugumatti kafaraniiruJedhi: "sila yoo Inni Masiih ilma Maryam, haadha isaatiifi namoota dachii irra jiran hunda balleesuu fedhe eenyutu Rabbi irraa isin tiksa?" Mootummaan samii, dachiifi wantoota gidduu isaan lamaanii jiran hundaatuu kan RabbiitiWaan fedhe uumaRabbiin waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(5,18,'5:18',' Yahuudonniifi Kiristaanonni "nuti ilmaan Rabbiitiifi jaalallee Isaati" jedhanAti jedhi: "maaliif badii keessaniin isin adaba? Dhugumatti isin nama Inni uume irraayi(Inni) nama fedheef ni araarama; nama fedhe immoo ni adabaMootummaan samii, dachiifi wantoota gidduu isaan lamaanii kan RabbiitiDeebiin garuma Isaatti" (jedhi).');
INSERT INTO "apaguna" VALUES(5,19,'5:19',' Yaa abbootii kitaabaa! Akka isin gammachiisaafi dinniinaanis nutti hin dhufne hin jenneef, ergamaan keenya kan (waan hunda) ifa isiniif taasisu, eega wal irraa citiinsi ergamootaa argamee booda isinitti dhufeeraDhugumatti gammachiisaafi akeekkachiisaan isinitti dhufeeraRabbiin waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(5,20,'5:20',' Yeroo Muusaan ummata isaatiin jedhes [yaadadhaa]: "yaa ummata kiyya! Ni’imaa Rabbiin yeroo nabiyyota isin keessa taasisee, moototas isin godheefi waan aalama irraa eenyuufillee hin kennin isiniif kennee isin irratti oole yaadadhaa.');
INSERT INTO "apaguna" VALUES(5,21,'5:21',' Yaa ummata kiyya! Dachii qulqulleeffamtuu Rabbiin isiniif barreesse (Filisxeeniin) seenaaDugda duubatti hin deebi’inaas(Yoo akkas gootan) hongaa’oo taatanii deebitu."');
INSERT INTO "apaguna" VALUES(5,22,'5:22',' "Yaa Muusaa! Dhugumatti, ishee keessa ummata of tuultotatu (gara jabeeyyiitu) jiraNuti hanga isaan ishee keessaa ba’anitti gara ishee hin seennuYoo isaan ishee irraa ba’an, nuti ni seenna" jedhan.');
INSERT INTO "apaguna" VALUES(5,23,'5:23',' Namoota (Rabbiin) sodaatan, kan Rabbiin ni’imaa Isaatiin isaan qanansiise irraa dhiirotiin lama ni jedhan: "balbala isaan irratti seenaaYeroo isa seentan isin injifattootaYoo mu’uminoota taataniif Rabbirratti hirkadhaa."');
INSERT INTO "apaguna" VALUES(5,24,'5:24',' Ni jedhan: "Yaa Muusaa! Hanga isaan ishee keessa jiranutti, nuti gonkumaa ishee hin seennuAtiifi Gooftaan kee deemaa lolaa, nuti asuma teenya."');
INSERT INTO "apaguna" VALUES(5,25,'5:25',' )Muusaan) ni jedhe: "Yaa Gooftaa kiyya! Ani ofiifi obboleessa kiyya irratti malee taaytaa hin qabuNuufi finciltoota gidduu addaan baasi."');
INSERT INTO "apaguna" VALUES(5,26,'5:26',' )Rabbiiniis) ni jedhe: "isheen (dachittii seenuun) waggoota afurtamaaf isaan irratti haraamfamtuudha; Dachii keessa dhama’uUmmata finciltoota irratti hin gaddin."');
INSERT INTO "apaguna" VALUES(5,27,'5:27',' Oduu ilmaan Aadam lamaanii kan yeroo isaan waan Rabbitti isaan dhiheessu (qurbaana) dhiyeessanii, isa tokko irraa qeebalamee isa kan biraa irraa qeebalamuu dide dhugaan isaaniif dubbisi(Inni lamuu isa duraatiin) ni jedhe: "Ani dhugumatti sin ajjeesa." (Inni duraas) ni jedhe: "Rabbiin kan qeebalu warra Isa sodaatan irraayyi.');
INSERT INTO "apaguna" VALUES(5,28,'5:28',' Yoo ati na ajjeesuuf harka kee gara kiyyatti diriirsites, ani si ajjeesuuf harka kiyya gara keetti hin diriirsuAni Rabbii Gooftaa aalamaan sodaadha.');
INSERT INTO "apaguna" VALUES(5,29,'5:29',' Ani ati badii kiyyaafi badii keetiin deebitee warra ibiddaa irraa ta''uun fedhaKunis mindaa miidhaa hojjattootaati."');
INSERT INTO "apaguna" VALUES(5,30,'5:30',' Lubbuun isaas isaaf miidhagsinaan obboleessa isaa ajjeeseWarra hoonga’an irraas ta’e.');
INSERT INTO "apaguna" VALUES(5,31,'5:31',' Akkamitti akka reeffa obboleessa isaa dhoksu isa argisiisuuf Rabbiin harraagessa dachii haadhu erge"Sila ani Reeffa obboleessa kiyyaa dhoksuuf akka harraagessa kanaa ta’uun dadhabee?" jedhee gaabbitoota irraa ta’e.');
INSERT INTO "apaguna" VALUES(5,32,'5:32',' Sababa kanaan Ilmaan Israa’iil irratti "namni lubbuu tokko lubbuu biraatiin yookiin dachii keessa balleessuun alatti ajjeese akka waan nama hunda ajjeeseti; namni ishee jiraachises akka waan nama hunda jiraachiseeti" jechuu murteessineDhugumatti, ergamoonni keenya ragaawwan ifa ta’aniin isaanitti dhuftee jirtiErgasii irra hedduun isaanii sana booda dachii keessatti daangaa dabroodha.');
INSERT INTO "apaguna" VALUES(5,33,'5:33',' Dhugumatti mindaan warra Rabbiifi ergamaa Isaatiin lolanii, dachii keessa balleessuuf deemanii, ajjeefamuu yookiin fannifamuu yookiin harkaafi miilli isaanii faallaan irraa ciramuu yookiin biyya irraa ari’amuudhaKun addunyaatti isaaniif salphinaAakhiraatti immoo isaaniif adabbii guddaatu jira.');
INSERT INTO "apaguna" VALUES(5,34,'5:34',' Isaannan odoo isin isaan irratti hin danda''in dura toobatan malee; Rabbiinis araaramaa mararfataa ta''uu isaa beekaa');
INSERT INTO "apaguna" VALUES(5,35,'5:35',' Yaa warra amantan! Rabbiin sodaadhaaWaan ittiin gara Isaatti dhihaattanus barbaadaaAkka milkooftaniif jecha karaa Isaa keessattis qabsaa’aa.');
INSERT INTO "apaguna" VALUES(5,36,'5:36',' Dhugumatti, isaan kafaran odoo wanti dachii keessa jiru hundiifi fakkaatan isaas isa waliin isaaniif ta’ee isaan adabbii Guyyaa Qiyaamaa irraa of bituu fedhanii isaan irraa hin qeebalamuIsaaniif adabbii laalessaa ta’etu jira.');
INSERT INTO "apaguna" VALUES(5,37,'5:37',' Ibidda irraa ba’uu fedhuIsaanis ishee irraa kan ba’anii mitiIsaaniif adabbii itti fufaatu jira.');
INSERT INTO "apaguna" VALUES(5,38,'5:38',' Dhiira hattuufi dubartii hattuus galata waan isaan dalaganiif jiilchaa Rabbi irraa ta''eef jecha harka isaanii lamaan kutaaRabbiin injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(5,39,'5:39',' Namni miidhaa (raawwachuu) isaa booda toobatee tolches, dhugumatti Rabbiin tawbaa isaa irraa qeebalaRabbiin akkaan araaramaa, akkaan rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(5,40,'5:40',' Sila Rabbiin mootummaan samiifi dachii kan Isaa ta’uu hin beeknee! Nama fedhe ni adaba; nama fedheef ni araaramaRabbiin waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(5,41,'5:41',' Yaa ergamichaa! Isaan afaaniin amanne jedhanii onneen isaanii immoo hin amanin irraa warri kufrii keessatti ariifatan si hin gaddisiisinIsaan yahuudoman irraas kan kijibaaf dhaggeeffatanu, ummata biraa kan gara kee hin dhufiniif dhaggeeffatantu jiruJechootas bakka isaa irraa jijjiiru"Yoo kana kennamtaniif isa qabadhaa; yoo isa hin kennamin immoo eeggadhaa" jedhuNama Rabbiin mokkoroo isaa fedhe Rabbi irraa waan tokko isaaf hin deebiftuSunniin warra Rabbiin onnee isaanii qulqulleessuu hin fedhiniAddunyaa kanatti salphinatu isaaniif jira; Aakhiraatti immoo adabbii guddaa ta’etu isaaniif jira.');
INSERT INTO "apaguna" VALUES(5,42,'5:42',' Kijiba dhaggeeffattoota, haraama nyaatoodhaYoo sitti dhufan, gidduu isaaniitti murteessi; yookiin isaan irraa gara galiYoo ati isaan irraa gara galtes homaa si hin miidhanuYoo murteessiteef, haqaan gidduu isaaniitti murteessiRabbiin warra haqaan murteessan ni jaalata.');
INSERT INTO "apaguna" VALUES(5,43,'5:43',' Sila odoo Tawraat kan murteen Rabbii ishee keessa jiru taatee isaan bira jirtuu akkamitti si murteessisiifatu? Ergasii san booda gara galuJarri sun mu’uminootaa miti.');
INSERT INTO "apaguna" VALUES(5,44,'5:44',' Dhugumatti, Nuti Tawraatiin kan ishee keessa qajeelfamaafi ifaan jiru taasifnee buufnee jirraNabiyyoonni warri fedha Rabbiitiif bulan warra yahuudomaniif isheedhaan murteessuQeesonniifi beektonnis waan kitaaba Rabbii irraa eegsifamaniniifi ishee irratti ragaa ta''aniin (murteessu)Namoota hin sodaatinaa; Ana sodaadhaaKeeyyattoota kiyyas gatii xiqqaan hin gurguratinaaNamni waan Rabbiin buuseen hin murteessin, isaan sun kaafiroota.');
INSERT INTO "apaguna" VALUES(5,45,'5:45',' Ishee keessatti "Lubbuun lubbuudhaan, ijji ijaan, funyaan funyaaniin, gurri gurraan, ilkaan ilkaaniin, madaanis gumaadha" jechuun isaan irratti barreessinee jirraNamni isa sadaqate (gumaa dhiise) inni isaaf (badii) harcaasaadhaNamni waan Rabbiin buuseen hin murteessin, isaan sun miidhaa raawwattoota.');
INSERT INTO "apaguna" VALUES(5,46,'5:46',' Faana isaanii irratti Iisaa ilma Maryam haala dhugeessaa waan isaan duraa Tawraat irraa ta''eetiin hordofsiifneInjiiliinis kan isa keessa qajeelfamniifi ifti jiru, dhugoomsaa waan isaan duraa Tawraat irraa ta''eefi (Rabbi) sodaattotaaf qajeelfamaafi gorsa haala ta''een isaaf kennine');
INSERT INTO "apaguna" VALUES(5,47,'5:47','Warri Injiil waan Rabbiin isa keessatti buuseen haa murteessanu; Namni waan Rabbiin buuseen hin murteessin, isaan sun finciltoota');
INSERT INTO "apaguna" VALUES(5,48,'5:48',' Nuti Kitaaba irraa waan isa duraatiif dhugoomsaafi isa irrattis tiksaa ta''ee Qur’aana gara keetti buufneerraWaan Rabbiin buuseen gidduu isaaniitti murteessiDhugaa waan sitti dhufe irraa dabuun fedha lubbuu isaanii hin hordofinHundumaafuu seera shari’aatiifi karaa ifa ta’e taasifnee jirraRabbiin odoo fedhee ummata tokko isin taasisa tureGaruu waan isiniif kenne keessatti isin qormaatuuf (addaan isin baase)Hojii gaarii keessatti wal dorgomaaHunda taatanii deebiin keessan gara RabbiitiWaan isin isa keessatti wal dhabaa turtanis isinitti hima.');
INSERT INTO "apaguna" VALUES(5,49,'5:49',' Ammas "gidduu isaaniitti waan Rabbiin buuseen murteessiFedhii lubbuu isaaniis hin hordofinGarii waan Rabbiin gara keetti buusee irraas akka si hin deebifne, isaan eeggadhu" (jechuudhaan buufne)Yoo isaan (sirraa) gara galan, Rabbiin garii badii isaaniitiin isaan tuquu (adabuu) barbaada jechuu beekiDhugumatti, hedduun namoota irraa ta''e finciltoota.');
INSERT INTO "apaguna" VALUES(5,50,'5:50',' Sila murtii ''jaahiliyyaa''[] barbaaduu? Ummata hubannoo amantii qabaniif wanti Rabbi irra murtiin isaa miidhagaa hin jiru.');
INSERT INTO "apaguna" VALUES(5,51,'5:51',' Yaa warra amantan! Yahuudotaafi Kiristaanotas jaalallee keessoo hin godhatinaaGariin isaanii jaalallee gariitiIsin irraa namni jaalallee isaan godhate, inni isaanuma irraayyiDhugumatti, Rabbiin ummata miidhaa hojjattoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(5,52,'5:52',' Isaan onnee isaanii keessa dhukkubni (munaafiqummaa) jiru, "naannoon rakkinaa tokko nu tuquu sodaanna" jedhaa, isaan (gargaaruu) keessatti kan ariifatan agartaRabbiin injifannoo yookiin murtii isa biraa ta’e fidee, waan lubbuu isaanii keessatti gadi qabatan irratti gaabbitoota ta''uun isaanii mirkanaa''eera.');
INSERT INTO "apaguna" VALUES(5,53,'5:53',' Isaan amanan ni jedhu: "isaan nuti isinii wajjin jirra jedhanii kakuu cimaa Rabbiin kakatan isaan kanaa mitii?" (jedhu)Dalagaan isaanii badeHongaa''oos ta''an.');
INSERT INTO "apaguna" VALUES(5,54,'5:54',' Yaa warra amantan! Isin irraa namni amantii isaa (Islaama) irraa gara gale, Rabbiin namoota isaan jaalatuufi isaanis Isa jaalatan, kan mu’uminootaaf gadi of qabanii kaafiroota irratti ciman, kan karaa Rabbii keessatti qabsaa’aniifi komii warra isaan komatuu kan hin sodaanne fuulduratti ni fidaKun tola RabbiitiNama fedheef isa kennaRabbiin (kenni isaa) bal''aa, beekaadha.');
INSERT INTO "apaguna" VALUES(5,55,'5:55',' Dhugumatti, jaalalleen keessan Rabbiifi ergamaa IsaatiAmmas isaan amanan, kanneen salaata dhaabaniifi kan rukuu''a godhan ta''aa zakaa kennaniidha.');
INSERT INTO "apaguna" VALUES(5,56,'5:56',' Namoonni Rabbiin, ergamaa Isaatiifi warra amanan jaalallee taasifatan (isaan garee Rabbiiti)Dhugumatti gareen Rabbii isaanumatu injifattoota.');
INSERT INTO "apaguna" VALUES(5,57,'5:57',' Yaa warra amantan! Warra amantii keessan qishinaafi tapha taasisan kan isaan isin dura kitaaba kennamaniifi kaafiroota irraa ta’an jaalallee hin taasifatinaaYoo mu’uminoota taataniif Rabbiin sodaadhaa.');
INSERT INTO "apaguna" VALUES(5,58,'5:58',' Yeroo gara salaataa lallabdan (azaana gootan), qishinaafi tapha ishee godhatuKun waan isaan ummata hin xiinxalle ta’aniifi.');
INSERT INTO "apaguna" VALUES(5,59,'5:59',' "Yaa warra kitaabaa! Sila nuti Rabbitti, waan gara keenya buufamettiifi waan kana dura buufametti amanuu keenyaafi irra hedduun keessan finciltoota ta''uu malee homaa nurratti jibbituu?" jedhi.');
INSERT INTO "apaguna" VALUES(5,60,'5:60',' Jedhi: "Sila kan adabbiin isaa Rabbiin biratti san caalaa badaa ta’e isiniif himuu? Namni Rabbiin isa abaareefi isa irratti dallane, kan isaan irraa gara jaldeessaafi booyyetti jijjiire, isaan xaaghuuta (gabbaramtoota Rabbiin alaa) gabbaran, isaan sun warra (Aakhiratti) iddoon isaanii badaa ta''eefi irra jallatoowwan karaati."');
INSERT INTO "apaguna" VALUES(5,61,'5:61',' Isaan odoma dhugumatti kufrii wajjin seenaniifi isaan dhugumatti isumaa wajjin ba’anii jiranuu yeroo isinitti dhufan "nuti amanne" jedhuRabbiinis irra beekaa waan isaan dhoksaa turaniiti.');
INSERT INTO "apaguna" VALUES(5,62,'5:62',' Baay’ee isaan irraa ta''es kan badii, daangaa dabruufi wantoota dhorgaman nyaachuu keessatti ariifatan argitaWantoonni isaan hojjataa turan waa fokkate!');
INSERT INTO "apaguna" VALUES(5,63,'5:63',' Sila maaliif qeesonniifi beektonni jechoota isaanii kan badiitiifi wantoota dhorgaman nyaachuu isaanii irraa isaan hin dhorgine? Wanti isaan dalagaa turan waa fokkate!');
INSERT INTO "apaguna" VALUES(5,64,'5:64',' Yahuudonnis "harki Rabbii hidhamtuu dha" jetteHarki isaanii hidhamtee waan jedhaniinis abaaramaniiruLakki! Harki Isaa lamaanuu diriiroodhaAkka fedhetti kennaDhugumatti, irra hedduu isaaniitiif wanti Gooftaa kee irraa gara keetti buufame daangaa dabruufi kufrii dabalaDiinummaafi wal jibbaa hanga Guyyaa Qiyaamatti turus gidduu isaaniitti darbineDhawaata isaan ibidda lolaaf qabsiisaniin Rabbiin ishee dhaamsaIsaanis badii dachii keessa facaasuuf fiiguRabbiinis badii hojjattoota hin jaalatu.');
INSERT INTO "apaguna" VALUES(5,65,'5:65',' Odoo warri kitaabaa amananii Rabbiinis sodaatanii, silaa hamtuu isaanii isaan irraa haqnee jannata qananii isaan seensifna turre.');
INSERT INTO "apaguna" VALUES(5,66,'5:66',' Odoo isaan Tawraatiin, Injiiliifi waan Gooftaa isaanii irraa gara isaanitti buufametti hojjatanii, silaa gubbaa isaanitiifi miila isaanii jalaa nyaatu turanIsaan irraa ummata giddu galeessa ta''antu jiraHedduun isaan irraa ta’e immoo hojiin isaanii waa fokkate.');
INSERT INTO "apaguna" VALUES(5,67,'5:67',' Yaa Ergamichaa! Waan Gooftaa kee irraa sirratti buufame dhaqqabsiisiYoo hin dalagin, ati ergaa Isaa hin geessifneRabbiinis namoota irraa si eegaDhugumatti, Rabbiin ummata kaafiraa hin qajeelchu.');
INSERT INTO "apaguna" VALUES(5,68,'5:68',' Jedhi: "Yaa warra kitaabaa! Isin hanga Tawraatiin, Injiiliifi waan Gooftaa keessan irraa gara keessanitti buufametti hojjattanitti homaa irra hin jirtan." Dhugumatti, wanti Gooftaa kee irraa gara keetti buufame daangaa dabruufi kufrii irra hedduu isaaniitiif dabalaUmmata kaafiraa irratti hin gaddin.');
INSERT INTO "apaguna" VALUES(5,69,'5:69',' Isaannan amanan, isaan yahuudoman, saabi’oonnii[]fi kiristaanonnis, namni Rabbiifi guyyaa Aakhiraatti amanee, wantoota gaggaariis dalage, isaan irra sodaan hin jiru; isaan hin gaddanis.');
INSERT INTO "apaguna" VALUES(5,70,'5:70',' Dhugumatti, Nuti waadaa Ilmaan Israa’iil irraa fuunee ergamootas gara isaanitti erginee jirraDhawaata ergamaan waan lubbuun isaanii hin feeneen isaanitti dhufuun garii isaanii ni kijibsiisu; garii isaanii immoo ni ajjeesu.');
INSERT INTO "apaguna" VALUES(5,71,'5:71',' Adabni hin jiru jechuu yaadanKanaafuu jaamanii duudanErgasii Rabbiin tawbaa isaanii irraa qeebaleErgasiis irra hedduun isaanii jaamanii duudanRabbiin waan isaan dalagan argaadha.');
INSERT INTO "apaguna" VALUES(5,72,'5:72',' Isaan "Rabbiin Masiih Ilma Maryami" jedhan dhugumatti kafaranii jiruMasiihis ni jedhe: "Yaa Ilmaan Israa''iil! Rabbii Gooftaa kiyyaafi Gooftaa keessan ta’e gabbaraaDhugumatti, dubbiin namni Rabbitti waa qindeesse, Rabbiin isa irratti jannata haraama taasiseeraTeessoon isaas ibiddaMiidhaa raawwattootaafis tumsitoonni hin jiranu."');
INSERT INTO "apaguna" VALUES(5,73,'5:73',' Isaan "Rabbiin sadaffaa sadeeniiti" jedhanis dhugumatti kafaraniiruGabbaramaa tokkicha malee haqaan gabbaramaan biraa hin jiruYoo waan jedhan (kana) irraa hin dhorgamin, dhugumatti warra isaan irraa kafaran adabbii laalessaa ta’etu tuqa.');
INSERT INTO "apaguna" VALUES(5,74,'5:74',' Sila gara Rabbiitti tawbatanii araarama Isa hin kadhatanuu? Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(5,75,'5:75',' Masiih Ilma Maryam ergamaa isa dura ergamoonni dabran malee homaa mitiHaati isaas akkaan dhugoomsituudhaLamaanuu nyaata kan nyaatan turanAkkamitti akka keeyyattoota ifa isaaniif taasifnu ilaaliErgasiis akkamitti akka isaan (dhugaa irraa) garagalan ilaali.');
INSERT INTO "apaguna" VALUES(5,76,'5:76',' Jedhi: "Sila isin Rabbiin alatti waan miidhaafi faayidaa isiniif hin qabne gabbartuu? Rabbitu Isa hunda dhagayaa, hunda beekaadha."');
INSERT INTO "apaguna" VALUES(5,77,'5:77',' Jedhi: "Yaa warra kitaabaa! amantii keessan keessatti dhugaan ala (waan biraatti amanuun) daangaa hin dabrinaaFedhii ummata duraan jallatanii dabranii, (namoota) heddus jallisaniifi karaa qajeelaa irraa jallatanii hin hordofinaa."');
INSERT INTO "apaguna" VALUES(5,78,'5:78',' Isaan Ilmaan Israa’iil irraa kafaran arraba Daawuudiifi Iisaa Ilma Maryam irratti abaaramaniiruKun waan isaan (Rabbiin) faallessaniifi kan daangaa dabran turaniifi.');
INSERT INTO "apaguna" VALUES(5,79,'5:79',' Hamtuu hojjatan irraa kan wal hin dhorgine turanWantoonni isaan hojjataa turan waa fokkate.');
INSERT INTO "apaguna" VALUES(5,80,'5:80',' Hedduu isaan irraa ta’e kan warra kafaran jaalallee godhatan argitaRabbiin isaan irratti waan dallaneef wanti lubbuun isaanii isaaniif dabarsite waa fokkate! Isaan adabbii keessattis hafoodha.');
INSERT INTO "apaguna" VALUES(5,81,'5:81',' Odoo kan Rabbitti, nabiyyittiifi waan gara isaatti bu’etti amanan ta’anii, silaa isaan jaalallee hin godhatanu turanGaruu isaan irraa (namoonni) hedduun finciltoota.');
INSERT INTO "apaguna" VALUES(5,82,'5:82','Dhugumatti namoota diinummaan warra amananiif akkaan cimaa ta''an yahuudotaafi mushrikoota ta’uu argitaDhugumatti, namoota jaalalaan isaan amananitti irra dhihoo ta’an warra "nuti kiristaanota" jedhan ta’aniitiin agartaKun waan isaan irraa qeesonniifi monokseewwan jiraniifi isaanis hin boonneefi.');
INSERT INTO "apaguna" VALUES(5,83,'5:83',' )Kiristaanonni) yeroo waan gara ergamichaa buufame dhagayan, sababaa dhugaa beekaniif jecha kan ijji isaanii imimmaan dhangalaatu argitaNi jedhu: "(Yaa) Gooftaa keenya! Nuti amannee ragoota wajjin nu barreessi.');
INSERT INTO "apaguna" VALUES(5,84,'5:84',' Sila maaltu nuuf jiraayyi Rabbiifi dhugaa irraa waan nutti bu’etti hin amanne? Gooftaan keenya ummata gaggaarii wajjin nu seensisuus ni hawwina" (jedhu).');
INSERT INTO "apaguna" VALUES(5,85,'5:85',' Sababaa waan isaan jedhaniitiin Rabbiin jannatoota laggeen ishee jala yaatu, kan ishee keessatti hafan ta''anii isaan mindeesseKunis mindaa warra toltuu hojjatanuuti.');
INSERT INTO "apaguna" VALUES(5,86,'5:86',' Isaan kafaranii keeyyattoota keenyas kijibsiisan, sunniin warra ibiddaati.');
INSERT INTO "apaguna" VALUES(5,87,'5:87',' Yaa warra amantan! gaggaarii waan Rabbiin isiniif hayyamee haraama hin taasisinaaDaangaas hin dabrinaa; Dhugumatti, Rabbiin warra daangaa dabranu hin jaalatu.');
INSERT INTO "apaguna" VALUES(5,88,'5:88',' Halaalaafi gaarii haala ta’aniin Waan Rabbiin isiniif kenne irraas nyaadhaaRabbii Isa isin itti amantan sodaadhaa.');
INSERT INTO "apaguna" VALUES(5,89,'5:89',' Rabbiin kakuu keessan keessatti kan osoo itti hin yaadin kakattaniin isin hin qabuGaruu waan kakuuf murteeffattanii (kakattaniin) isin qabaWanti isa harcaasus hiyyeeyyii kudhan giddu gala waan maatii keessan nyaachiftan irraa nyaachisuu yookiin isaan uffisuu yookiin gabra wahii bilisoomsuudhaNamni hin argatin, guyyaa sadi soomuu qabaKun (karaa badii) kakuu keessanii yeroo kakattan ittiin harcaaftaniidhaKakuu keessan tiksaaAkka isin galateeffattaniif jecha Rabbiin akka kanatti keeyyattoota Isaa isiniif addeessa');
INSERT INTO "apaguna" VALUES(5,90,'5:90',' Yaa warra amantan! akka milkooftaniif jecha dhugumatti farsoon (kamriin), qumaarri, taaghuunni, xiyyaan hixaa (carraa) ilaallachuun (raaggachuun) najasa dalagaa sheyxaanaa irraayyii isa irraa fagaadhaa.');
INSERT INTO "apaguna" VALUES(5,91,'5:91',' Wanti sheyxaanni fedhu dhugaatii macheessaniifi qumaaraan gidduu keessanitti diinummaafi wal jibbaa buusuun Rabbiin yaadachuufi salaata irraa isin dhorguudhaSila isin hin dhorgamtanuu?');
INSERT INTO "apaguna" VALUES(5,92,'5:92',' Rabbiif ajajamaaErgamaafis ajajamaa(Wantoota dhorgaman kanneen irraa) eeggadhaaYoo irraa gara galtan immoo dhugumatti ergamaa keenya irra karaa ifa ta’een (ergaa keenya) geessuu malee wanti biraa akka hin jirre beekaa.');
INSERT INTO "apaguna" VALUES(5,93,'5:93',' Waan (dur) nyaatan keessatti isaan amananii gaggaariis hojjatan yeroo (Rabbiin) sodaatanii (Isatti) amananii gaggaariis hojjatan, ergasii (Rabbiin) sodaatanii (Isatti) amanan; ammas (Rabbiin) sodaataniifi (dalagaa) tolchan irra badiin hin jiruRabbiin toltuu hojjattoota ni jaalata.');
INSERT INTO "apaguna" VALUES(5,94,'5:94',' Yaa warra amantan! Rabbiin nama ghaybiitti Isa sodaatu mul’isuuf jecha waan adamsoo irraa harki keessaniifi waraanni keessan argatteen isin mokkoraNamni kana booda daangaa dabre, isaaf adabbii laalessaatu jira.');
INSERT INTO "apaguna" VALUES(5,95,'5:95',' Yaa warra amantan! odoo hajjii irra jirtanuu bineensa bosonaa hin ajjeesinaaNamni isin irraa odoo beekuu ajjeese, adabbiin isaa waan isin irraa namni haqaa lama murteesse beeylada irraa fakkii waan ajjeesee haala wareega taateen kan ka’abaa geeffamtu yookiin hiyyeeyyii nyaachisuu yookiin fakkiidhuma kanaa soomuudha(Kunis) akka adabbii hojii isaa dhandhamuufiWaan (dhorgamuun dura) dabre Rabbiin irra isiniif dabreeraNama (gara badichaa) deebi’es Rabbiin haaloo irraa ba’ataRabbiin injifataa, abbaa haaloo ba’annaati.');
INSERT INTO "apaguna" VALUES(5,96,'5:96',' Adamoon galaanaafi nyaanni isaa itti fayyadama keessaniifi daldaltootaatiif jecha isiniif eeyyamameeraWaan hidhannaa Hajjii keessa jirtanuun adamoon lafa irraa isin irratti haraama godhameRabbii isa gara Isaatti oofamtanu sodaadhaa.');
INSERT INTO "apaguna" VALUES(5,97,'5:97',' Rabbiin Ka’abaa mana kabajamaa, ji’a kabajamaa, horii wareegaas, horii wareegaaf mallattoon morma irratti godhames namootaaf gadi dhaabbii (jireenya) taasiseeraKun akka Rabbiin wantoota samii keessa jiraniifi wantoota dachii keessa jiran hunda kan beekuufi ammas Rabbiin waan hunda beekaa ta’uu beektaniifi.');
INSERT INTO "apaguna" VALUES(5,98,'5:98',' Beekaa! Dhugumatti Rabbiin (abbaa) adabbii cimaatiDhugumatti Rabbiinis araaramaa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(5,99,'5:99',' Ergamicha irra dhaqqabsiisuu malee homaa hin jiruRabbiinis waan isin mul’iftaniifi waan isin dhoksitan ni beeka.');
INSERT INTO "apaguna" VALUES(5,100,'5:100',' Jedhi: "Odoma baay’inni wantoota fokkataa si dinqisiiseeyyuu fokkataaniifi gaariin wal hin qixxaatanu." Yaa warra sammuu qabdan akka milkooftaniif jecha Rabbiin sodaadhaa.');
INSERT INTO "apaguna" VALUES(5,101,'5:101',' Yaa warra amantan! waan yoo ifa isiniif taasifamte isin rakkiftu irraa hin gaafatinaaYoo yeroo Qur’aanni bu’u ishee irraa gaafattan, ifa isiniif taasifamtiRabbiin ishee irra dabreeraRabbiin araaramaa, obsaadha.');
INSERT INTO "apaguna" VALUES(5,102,'5:102',' Ummanni isiniin duraa dhugumatti ishee gaafatanii, ergasii sababaa isheetiin kafaranii jiru.');
INSERT INTO "apaguna" VALUES(5,103,'5:103',' Rabbiin "Bahiiraah", "Saa’ibaa", "Wasiilaa"fi "Haam"[] karaa hin gooneGaruu warri kafaran Rabbirratti soba uumuIrra hedduun isaaniis hin xiixalanu.');
INSERT INTO "apaguna" VALUES(5,104,'5:104',' Yeroo "gara waan Rabbiin buuseefi gara ergamichaa kottaa" isaaniin jedhame "wanti nuti abbootii keenya irratti argine nuuf ga’aadha" jedhuSila odoo abbootiin isaanii homaa kan hin beekneefi karaa hin qajeelle ta’aniis (isaanuma hordofuu)?');
INSERT INTO "apaguna" VALUES(5,105,'5:105',' Yaa warra amantan! Lubbuu keessan qabaaYeroo isin qajeeltan namni jal’ate isin hin miidhuDeebiin keessan hundaatuu gara RabbiitiWaan isin dalagaa turtan hundaa isinitti hima.');
INSERT INTO "apaguna" VALUES(5,106,'5:106',' Yaa warra amantan! Yeroo tokkoo keessanitti duuti dhufe ogga dhaamsaa, nama haqaa lama kan isin irraa ta’e yookiin yoo isin dachii keessa deemtanii rakkoon du’aa isin mudate nama lama kan isiniin alaa irraa jidduu keessanitti ragaan (isin irratti dirqama)Yoo shakkitan booda salaataatii isaan lamaan dhaabdanii "odoma fira dhihoo ta’eeyyuu isaan (ragaa keenyaan) maallaqa hin jijjiirrannu; ragaa Rabbiis hin dhoksinu; nuti yeroo sana warra dilaawaa irraayyi" jedhanii Rabbiin kakatu.');
INSERT INTO "apaguna" VALUES(5,107,'5:107',' Yoo isaan lamaan badii haqa godhachuun beekame, fira warra (dhaala) haqa godhate irraa namni lama bakka isaan lamaanii dhaabatanii "ragaan keenya ragaa isaan lamaanii irra dhugaadha; nuti daangaas hin dabrine; yeroo san nuti miidhaa raawwattoota irraayyi" (jedhanii) Rabbiin kakatu.');
INSERT INTO "apaguna" VALUES(5,108,'5:108',' Ragaan bifa sirriin dhufuufi eega kakuu isaaniitii kakuun deebifamuu sodaachuuf kanatu irra dhihoodhaRabbiin sodaadhaatii (ajaja isaas) dhaga''aaRabbiin finciltoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(5,109,'5:109',' Guyyaa Rabbiin ergamoota (Isaa) walitti qabee "deebii maaliitu isiniif deebi’e" jedhu [yaadadhu][Isaan] "nuti beekumsa hin qabnu; Ati situ akkaan beekaa fagooti" jedhan.');
INSERT INTO "apaguna" VALUES(5,110,'5:110',' “Yaa Iisaa ilma Maryam! haala ati daa’imummaafi ga’eessummaan nama dubbiftuun yeroo Ani ruuhii qulqulluudhaan si jabeesse, ni’imaa Ani siifi haadha kee irratti oole yaadadhu" yeroo Rabbiin jedhe (dubbadhu)Ammas (yeroo) Ani kitaaba, ogummaa, Tawraatiifi Injiiliin si barsiisuufi yeroo heyyama kiyyaan dhoqqee irraa waan akka allaattii uumtee afuura keessatti afuuftee heyyama kiyyaan allaattii ta’u; yeroo heyyama kiyyaan jaamaafi shoolii (barasa) fayyiftu, yeroo heyyama kiyyaan du’aa kaaftu, yeroo ati ragaa ifa ba’aan isaanitti dhufnaan isaan irraa warri kafaran “kuni sihrii ifa galaadha” jedhan, yeroo (si ajjeesuu barbaadnaan) banii Israa’iil sirraa deebise" [yaadadhu].');
INSERT INTO "apaguna" VALUES(5,111,'5:111',' Yeroo ani gara hawaariyyootaa "Anaafi ergamaa kiyyatti amanaa" jechuu beeksisee isaanis "Nuti amanne; akka nuti Muslimoota taane ragaa ba’i" jedhan [yaadadhu].');
INSERT INTO "apaguna" VALUES(5,112,'5:112',' Yeroo hawaariyyoonni "Yaa Iisaa Ilma Maryam! Sila Gooftaan kee maaddii (qorii) samii irraa nuuf buusuu danda’aa?" jedhanu [yaadadhu](Iisaanis) "Yoo mu’uminoota taataniif Rabbiin sodaadhaa" jedhe.');
INSERT INTO "apaguna" VALUES(5,113,'5:113',' )Isaanis) "Nuti ishee irraa nyaachuu, onneen keenya tasgabbaa’uu, akka wanti ati jettu dhugaa ta’e mirkaneeffachuufi nutis san irratti warra ragaa ba’an irraa ta’uu feena" jedhan.');
INSERT INTO "apaguna" VALUES(5,114,'5:114',' Iisaa Ilma Maryam ni jedhe: "Yaa Allaah! Gooftaa keenya! akka dhaloota keenya kan jalqabaafi boodaatiif ayyaana (guyyaa gammachuu)fi mallattoo sirraa (buute) taatuuf samii irraa maaddii nurratti buusinu soorisAti caalaa warra rizqii kennuuti."');
INSERT INTO "apaguna" VALUES(5,115,'5:115',' Rabbiin ni jedhe: "Ani ishee isinirratti nan buusaIsin irraa namni san booda kafare, Ani adabbii aalama irraa eenyuunillee hin adabnen isa adaba."');
INSERT INTO "apaguna" VALUES(5,116,'5:116',' Yeroo Rabbiin "Yaa Iisaa Ilma Maryam! sila situ namootaan anaafi haadha kiyya Rabbii gaditti gabbaramaa lama taasifadhaa jedhee?" jedhu [yaadadhu][Inni] ni jedhe: "Ati qulqulloofte! Ani waan dhugaa naaf hin taane jechuun anaaf hin ta''uYoo ani isa jedheera ta''eef, ati dhugumatti isa barteettaWaan lubbuu kiyya keessaa ni beektaAni waan nafsii kee keessaa hin beekuDhugumatti, ati akkaan beekaa waan fagooti.');
INSERT INTO "apaguna" VALUES(5,117,'5:117',' Waan ati itti na ajajje ‘Rabbiin, Gooftaa kiyyaafi Gooftaa keessan gabbaraa’ jechuu malee waan biraa isaaniin hin janneHangan isaan keessa turetti isaan irratti ragaan tureErga ol nafuutee booda ati isaan irratti tiksaa turteAti waan hunda irratti ragaadha.');
INSERT INTO "apaguna" VALUES(5,118,'5:118',' Yoo isaan adabdes, isaan gabrootuma keetiYoo isaaniif araaramtes, ati simatu injifataa, ogeessa."');
INSERT INTO "apaguna" VALUES(5,119,'5:119',' Rabbiin ni jedhe: "Kun guyyaa warra dhugaa dubbatan dhugaan isaanii fayyaduudhaIsaaniif ishee keessatti yeroo hunda hafaa ta''anii jannatoota jala ishee laggeen yaatutu jiraRabbiin isaan irraa jaalateeraIsaanis Isarraa jaalataniiruKun milkii guddaadha."');
INSERT INTO "apaguna" VALUES(5,120,'5:120',' Mootummaan samii, dachiifi wantoota isaan keessaa kan RabbiitiInnis waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(6,1,'6:1','Faaruun hundi kan Rabbii isa samiifi dachii uumee, dukkanaafi ifa argamsiiseetiErgasii isaan kafaran Rabbii isaaniitti qindeessu.');
INSERT INTO "apaguna" VALUES(6,2,'6:2','Inni isa dhoqqee irraa isin uumee ergasii beellama (du''aa) murteesseedhaBeellamni beekamaan (Qiyaamaan) immoo Isa bira jiraErgasii isin ni shakkitu.');
INSERT INTO "apaguna" VALUES(6,3,'6:3','Inni samii keessattiifi dachii keessattis gabbaramaadhaIccitii keessaniifi ifa bahaa keessanis ni beekaWaan isin hojjattanis ni beeka.');
INSERT INTO "apaguna" VALUES(6,4,'6:4','Keeyyanni keeyyattoota Gooftaa isaanii irraa ta''e isaanitti dhufee yoo kan isaan ishee irraa gara galan ta''e malee hin hafu.');
INSERT INTO "apaguna" VALUES(6,5,'6:5','Dhugumatti, isaan yeroma dhugaan isaanitti dhufu sobsiisaniiruOduun waan (adabbii) isaan itti qoosaa turanii fuulduratti isaanitti dhufa.');
INSERT INTO "apaguna" VALUES(6,6,'6:6','Sila isaaniin dura jaarraa (ummata) irraa kan dachii keessatti hanga isiniif hin mijeessin isaaniif mijeessine heddu balleessuu keenya hin ilaalanuu? Bokkaas wal hordofaa ta''ee isaan irratti buufneLaggeenis jala isaanii akka yaatu gooneBadii isaaniitiin isaan balleessinee booda isaaniitii jaarraa biraa uumne.');
INSERT INTO "apaguna" VALUES(6,7,'6:7','Odoo kitaaba waraqaa irratti barreeffame sirratti buufnee silaa harka isaanitiin isa tuqanii warri kafaran "kun sihrii ifa ta’e malee homaa miti" jedhu.');
INSERT INTO "apaguna" VALUES(6,8,'6:8','Ni jedhu: "Maaliif Maleykaan isa irratti hin buufamin?" Odoo Maleykaa buufnee silaa dhimmichi ni murteeffama; Ergasii yeroon isaaniif hin kennamu ture.');
INSERT INTO "apaguna" VALUES(6,9,'6:9','Odoma maleykaa isa gooneeyyuu dhugumatti silaa dhiira isa taasifneeti waan isaan walitti fakkeessan isaan irratti wal fakkeesina turre.');
INSERT INTO "apaguna" VALUES(6,10,'6:10','Dhugumatti, ergamoota si dura turanittis qoosamee tureIsaan irraa warra qoosanitti wanti isaan itti qoosaa turan isaanitti marse.');
INSERT INTO "apaguna" VALUES(6,11,'6:11','Jedhi: "dachii keessa deemaa; ergasii booddeen warra dhugaa kijibsiisee akkam akka ta’e ilaalaa."');
INSERT INTO "apaguna" VALUES(6,12,'6:12','"Wantoonni samiifi dachii keessa jiran hundi kan eenyuuti?" jedhi“Kan Rabbiiti” jedhiLubbuu Isaa irratti rahmata barreessee jiraDhugumatti, Guyyaa Qiyaamaa mamiin isa keessa hin jirreef walitti isin qabaIsaan lubbuu isaanii hoongessan, hin amananu.');
INSERT INTO "apaguna" VALUES(6,13,'6:13','Wanti halkaniifi guyyaa keessa callises kan RabbiitiInni dhagayaa, beekaadhas.');
INSERT INTO "apaguna" VALUES(6,14,'6:14','"Sila Rabbii uumaa samiifi dachii, inni kan nyaachisu hin nyaanne ta''een alan gabbaramaa tolfadhaa?" jedhi"Ani jalqaba nama Rabbiif bulee ta''uuttin ajajameMushrikoota irraas hin ta''in (jedhameera)" jedhi');
INSERT INTO "apaguna" VALUES(6,15,'6:15','"Ani yoon Gooftaa kiyya faallesseef adabbii guyyaa guddaan sodaadha" jedhi.');
INSERT INTO "apaguna" VALUES(6,16,'6:16','Namni guyyaa san isa (adabicha) irraa deebifame, dhugumatti (Rabbiin) isa mararfateeraKun milkii guddaadha.');
INSERT INTO "apaguna" VALUES(6,17,'6:17','Yoo Rabbiin miidhaan si tuqe, Isa malee deemsisaan isaaf hin jiruYoo toltuudhaan isin tuqes, Inni waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(6,18,'6:18','Inni gabroota Isaa irratti mo’ataadhaInni ogeessa, keessa beekaadhas.');
INSERT INTO "apaguna" VALUES(6,19,'6:19','"Sila ragaadhaaf waan hunda irra maaltu guddaadha?" jedhi"RabbumaGidduu kiyyaafi gidduu keessanitti ragaadhaQur’aanni kunis akkan ittiin isiniifi nama inni bira ga’u dinniinuuf gara kiyyatti beeksifameSila isin dhugumatti Rabbii wajjin gabbaramaan biraa ni jira jettanii ragaa baatuu?" jedhiin"Ani ragaa (akkasii) hin ba’u" jedhi"Dhugumatti, Inni dhugaan gabbaramaa tokkichaAnis waan isin gabbartan irraa qulqulluudha" jedhi.');
INSERT INTO "apaguna" VALUES(6,20,'6:20','Warri kitaaba isaaniif kennine akka ilmaan isaanii beekanitti isa beekuWarri lubbuu isaanii hoongessan, isaan hin amananu.');
INSERT INTO "apaguna" VALUES(6,21,'6:21','Nama Rabbirratti kijiba uume yookiin keeyyattoota Isaa kijibsiise caalaa namni miidhaa hojjate eenyu? Dhugumatti, miidhaa hojjattoonni hin milkaayanu.');
INSERT INTO "apaguna" VALUES(6,22,'6:22','Guyyaa hunda isaanii walitti qabnee, ergasii isaan qindeessaniin "sila warri isin Rabbii wajjin gabbaramoodha jettanii mormaa turtan eessa jiru?" jennuun san [yaadadhu].');
INSERT INTO "apaguna" VALUES(6,23,'6:23','Ergasii deebiin isaanii "Rabbii keenyaan kakannee! nuti mushrikoota hin taane" jechuu malee hin jiru.');
INSERT INTO "apaguna" VALUES(6,24,'6:24','Akka itti isaan lubbuu isaanii kijibsiisaniifi wanti isaan uumanii odeessaa turan akka itti isaan irraa bade mee ilaali!');
INSERT INTO "apaguna" VALUES(6,25,'6:25','Isaan irraas nama gara kee dhageeffatutu jiraAkka isaan isa hin hubanne onnee isaanii irratti haguuggaa, gurra isaanii irratti immoo duudina taasifnee jirraOdoma mallattoowwan hundaa arganiiyyuu, isheetti hin amananuWarri kafare yeroo kan sitti falman ta''anii sibira dhufan, (Qur’aanni) kun ogbarruu warra durii malee waan biraatii miti" jedhu.');
INSERT INTO "apaguna" VALUES(6,26,'6:26','Isaan isa (Muhammadiin) irraa dhorgu; Isa irraas fagaatanLubbuu isaanii malee homaa hin balleessan; garuu hin beekanu');
INSERT INTO "apaguna" VALUES(6,27,'6:27','Yeroo isaan ibidda irra dhaabamanii "yaa hawwii keenya! odoo (addunyaatti) deebifamnee aayatoota Rabbii keenyaas kijibsiisuu dhiifnee mu''umina irraas taanee" jedhan argitee (waan rifachiisaa argita ture)');
INSERT INTO "apaguna" VALUES(6,28,'6:28','Dhugumatti wanti isaan kana dura dhoksaa turan isaaniif mul’ateOdoo deebifamaniis waan irraa dhorgamanitti deebi’uIsaan kijibdoota.');
INSERT INTO "apaguna" VALUES(6,29,'6:29','"Isheen jireenya addunyaati malee kan biraa miti, nutis (du’a booda) hin kaafamnu" jedhan.');
INSERT INTO "apaguna" VALUES(6,30,'6:30','Yeroo isaan Gooftaa isaanii fuulduratti dhaabaman odoo argitee (waan rifachiisaa argita ture)"Sila inni kun dhugaa mitii?" jedhe"Eeyyeen; Gooftaa keenyaan kakannee! dhugaa dha" jedhan"Waan kafaraa turtaniif (amma) adabicha dhandhamaa!" jedhe.');
INSERT INTO "apaguna" VALUES(6,31,'6:31','Isaan wal qunnamtii Rabbii kijibsiisan dhugumatti hoonga’aniiruYeroo Qiyaamattiin tasa isaanitti dhufte badii isaanii dugda isaanii irratti baataa "Waan ishee (addunyaa) keessatti hanqifneef yaa gaabbii keenya!" jedhanDhagayaa! Ba’aan isaanii waa fokkate!');
INSERT INTO "apaguna" VALUES(6,32,'6:32','Jireenyi addunyaa taphaafi qoosaa malee waan biraa mitiDhugumatti warra (Rabbiin) sodaataniif ganda Aakhiraatu caalaSila isin hin xiinxaltanuu?');
INSERT INTO "apaguna" VALUES(6,33,'6:33','Dhugumatti wanti isaan jedhan si gaddisiisuu ni beeknaIsaan (onnee isaatiin) si hin kijibsiisanuGaruu miidhaa raawwattoonni keeyyattoota Rabbii kijibsiisu');
INSERT INTO "apaguna" VALUES(6,34,'6:34','Dhugumatti, ergamoonni si duraas kijibsiifamaniiruWaan kijibsiifaman irrattis ni obsanHanga tumsi keenya isaaniif dhufuttis ni rakkifamanJechoota Rabbiitiif jijjiiraan hin jiruDhugumatti, oduu ergamootaa irraa (hangi tokko) sitti dhufeera.');
INSERT INTO "apaguna" VALUES(6,35,'6:35','Yoo (sirraa) garagaluun isaanii sitti ulfaate, mallattoon isaanitti dhufuuf yoo qaawwa dachii keessa barbaaduu yookiin kortoo samii keessaa dandeesse (raawwadhu)Rabbiin odoo fedhee, (hunda isaanii) qajeeluma irratti walitti isaan qabaWallaalaa irraas hin ta’in.');
INSERT INTO "apaguna" VALUES(6,36,'6:36','Kan owwaatu warruma dhaga’u qofaDu''aawwan immoo Rabbiin isaan kaasaErgasii gara Isaatti deebifamu.');
INSERT INTO "apaguna" VALUES(6,37,'6:37','"Maaliif mallattoon Gooftaa isaa irraa isa irratti hin buufamne?" jedhu"Dhugumatti, Rabbiin mallattoo buusuu irratti danda’aa dha" jedhiGaruu irra hedduun isaanii hin beekanu');
INSERT INTO "apaguna" VALUES(6,38,'6:38','Deddeemtuu dachii keessaas ta''ee simbirroon koola isaa lamaaniin bararus uumamuma akka keessanii malee hin taaneKitaaba keessatti Nuti homaa hin dhiifneErgasii gara Gooftaa isaanii oofamu.');
INSERT INTO "apaguna" VALUES(6,39,'6:39','Isaan keeyyattoota keenya kijibsiisan duudaafi arraba qabamaa dukkana keessa jiraniidhaRabbiin nama fedhe isa jallisa; nama fedhe immoo karaa qajeelaa irra isa godha.');
INSERT INTO "apaguna" VALUES(6,40,'6:40','Jedhi: "Sila yoo adabbiin Rabbii isinitti dhufe yookiin Qiyaamaan isinitti dhufte, yoo kan dhugaa dubbattan taataniif, waan Rabbiin alaa ni kadhattuu? mee naaf himaa!"');
INSERT INTO "apaguna" VALUES(6,41,'6:41','Dhugumatti Isa qofa kadhattu, yoo fedhe waan isin gara isaatti kadhattan san isin irraa kaasaWaan qindeessitanis ni irraanfattu.');
INSERT INTO "apaguna" VALUES(6,42,'6:42','Dhugumatti, gara ummattoota siin duraatti erginee jirraAkka isaan (Rabbitti) watwaataniif hiyyummaafi dhukkubaan isaan qabnee jirra.');
INSERT INTO "apaguna" VALUES(6,43,'6:43','Maaliif yeroo adabbiin keenya isaanitti dhufe (Rabbitti) hin watwaatinGaruu onneen isaanii ni gogdeSheyxaannis waan isaan dalagaa turan isaaniif miidhagse.');
INSERT INTO "apaguna" VALUES(6,44,'6:44','Yeroma isaan waan ittiin gorfaman dagatan, balbala waan hundaa isaanitti banne; yeroo isaan waan kennamaniin gammadan odoo hin beekne (adabaan) isaan qabneIsaan yoosuu abdii murattoota.');
INSERT INTO "apaguna" VALUES(6,45,'6:45','Booddeen ummata isaan miidhaa hojjatanii ni murameFaaruun kan Rabbii Gooftaa aalamaati.');
INSERT INTO "apaguna" VALUES(6,46,'6:46','Jedhi: “Mee naaf himaa! Yoo Rabbiin dhageettiifi argaa keessan fudhatee, onnee keessan irrattis cufe, gabbaramaan Rabbiin alaa isa isiniif fidu eenyu?” Akkamitti keeyyattoota akka ibsinuufi ergasii akkamitti akka isaan gara galan ilaali.');
INSERT INTO "apaguna" VALUES(6,47,'6:47','Jedhi: "Mee naaf himaa! Yoo adabbiin Rabbii tasa yookiin ifatti isinitti dhufe, sila ummata miidhaa hojjattoota malee ni balleeffama?"');
INSERT INTO "apaguna" VALUES(6,48,'6:48','Nuti ergamoota gammachiisoofi dinniinoo taasifnee malee hin erginuWarri amananiifi toltuu hojjatan sodaan isaan irra hin jiru; isaan hin gaddanus.');
INSERT INTO "apaguna" VALUES(6,49,'6:49','Isaan keeyyattoota keenya kijibsiisan, waan isaan fincilaa turaniif adabbiin isaan tuqa.');
INSERT INTO "apaguna" VALUES(6,50,'6:50','Jedhi: "Kuusaaleen Rabbii ana bira jira isiniin hin jedhu; fagoos hin beeku; ani Maleykaa dhas isiniin hin jedhuWaan gara kiyya buufame malee hin hordofuSila jaamaafi argaan wal qixxaataa? Sila hin xiinxaltanii?" (jedhi).');
INSERT INTO "apaguna" VALUES(6,51,'6:51','Isaan isa (Rabbiin) malee jaalalleefi manguddoo kan hin qabne ta''anii gara Gooftaa isaaniitti walitti qabamuu sodaatan akka isaan (Rabbiin) sodaataniif isaan (Qur’aanaan) dinniini.');
INSERT INTO "apaguna" VALUES(6,52,'6:52','Warra fuula Isaa barbaaduuf ganamaafi galgala Gooftaa isaanii kadhatan hin ari’inIsaan ariitee miidhaa raawwattoota irraa akka taatuuf qormaata isaanii irraa waa tokko sirra hin jiru; qormaata kee irraas waa tokko isaan irra hin jiru');
INSERT INTO "apaguna" VALUES(6,53,'6:53','Akkasuma "isaan kana Rabbiin nu gidduudhaa tola itti oolee?" akka isaan jedhaniif garii isaanii gariin mokkoreSila Rabbiin galateeffattoota akkaan beekaa hin taanee?');
INSERT INTO "apaguna" VALUES(6,54,'6:54','Yeroo warri keeyyattoota keenyatti amanan sitti dhufan "Nageenyi isin irra haa jiraatuGooftaan keessan lubbuu Isaa irratti rahmata barreesseera: ‘namni isin irraa wallaalumaan hamtuu hojjatee ergasii isa booda gara Rabbii deebi’ee toltuu hojjate, dhugumatti Inni (isaaf) araaramaa, (isaaf) rahmata godhaa dha" (jedhi).');
INSERT INTO "apaguna" VALUES(6,55,'6:55','Akkasumas akka (dhugaan mirkanaa''uu fi) karaan yakkamtootaas ifa ta’uuf jecha keeyyattoota addeessina');
INSERT INTO "apaguna" VALUES(6,56,'6:56','"Ani warra isin Rabbiin ala gabbartan gabbaruu irraa dhorgameera" jedhi"Ani fedha lubbuu keessanii hin hordofu, yoos dhugumatti ani jalladhe; warra qajeelan irraahii miti" jedhi.');
INSERT INTO "apaguna" VALUES(6,57,'6:57','Jedhi: "Ani ragaa ifa ta’e kan Gooftaa kiyya irraa dhufe irran jiraIsin isa kijibsiiftaniWanti isin arifachiifattan ana bira hin jiruMurtiin Rabbiif malee hin taaneInni dhugaa murteessaInni irra caalaa warra murteessaniiti."');
INSERT INTO "apaguna" VALUES(6,58,'6:58','Jedhi: "Odoo wanti isin arifachiifattan ana bira jiraatee, silaa dhimmichi gidduu kiyyaafi gidduu keessanitti ni murtaa’a tureRabbiin miidhaa hojjattoota akkaan beekaadha."');
INSERT INTO "apaguna" VALUES(6,59,'6:59','Furtuuwwan fagoo (ghaybii) Isa bira jiraIsa malee homtuu ishee hin beekuInni waan dachii keessa jiruufi waan bahra keessa jirus ni beekaBaala irraas tokkollee inni beeku malee hin kufuFiriin dukkana dachii keessaa, jiidhaafi gogaanis kitaaba ifa ta’e keessa jiraatu malee hin hafu.');
INSERT INTO "apaguna" VALUES(6,60,'6:60','Inni Isa halkan isin ajjeesuudha; Waan isin guyyaa carraaqxanis ni beekaErgasii akka beellamni murtaa''aan dhumuuf isa (guyyaa) keessa isin kaasaErgasiis deebiin keessan gara IsaatiErgasiis waan isin hojjachaa turtan isinitti hima.');
INSERT INTO "apaguna" VALUES(6,61,'6:61','Inni gabroota Isaa gubbaa ta''ee, hundaa injifataadhaIsin irrattis tiksitoota ergaYeroo duuti tokkoon keessanitti dhufe ergamoonni keenya isaan kan (ajaja) hin hanqifne ta''anii isa ajjeesu');
INSERT INTO "apaguna" VALUES(6,62,'6:62','Ergasiis gara Gooftaa isaanii dhugaa ta''e, gara Rabbii deebifamuDhagayaa! murtiin kan Isaa qofaInnis irra arifataa qorattootaati.');
INSERT INTO "apaguna" VALUES(6,63,'6:63','Jedhi: "Sila dukkana dachiifi bahraa irraa kan isin mul''iftaniifi iccitiin isa waammattan taatanii ‘yoo kana irraa nu baraarseef, nuti galateeffattoota irraa taana’ (jettanu) eenyutu isin baraarsa?"');
INSERT INTO "apaguna" VALUES(6,64,'6:64','Jedhi: "Rabbumatu isheefi rakkoo hunda irraa isin baraarsaErgasii isin ni qindeessitu."');
INSERT INTO "apaguna" VALUES(6,65,'6:65','Jedhi: "Inni adabbii gubbaa keessan irraa yookiin miila keessan jalaa isin irratti erguu, yookiin garee haala taataniin isin walitti makee garii keessan lola garii dhandhamsiisuu irratti danda’aadha." Akka isaan hubataniif jecha akkamitti keeyyattoota akka ibsinu ilaali.');
INSERT INTO "apaguna" VALUES(6,66,'6:66','Odoo inni dhugaa ta''ee jiruu ummanni kee isa kijibsiisan"Ani isin irratti tiksaa miti" jedhiin.');
INSERT INTO "apaguna" VALUES(6,67,'6:67','Oduu hundaaf yeroo itti mirkanaa’utu jiraFuula dura beektu.');
INSERT INTO "apaguna" VALUES(6,68,'6:68','Yeroo warra keeyyattoota keenya keessa seenan (itti qoosan) argite, hanga isaan dubbii sanaan alaa keessa seenanitti isaan irraa garagaliYoo sheyxaanni si dagachiises, yaadannoo booda ummata miidhaa raawwattoota wajjin hin taa’in.');
INSERT INTO "apaguna" VALUES(6,69,'6:69','Warra (Rabbiin) sodaatan irra dilii isaanii irraa homaayyuu hin jiruGaruu akka isaan eeggataniif jecha gorsatu (isaaniif jira).');
INSERT INTO "apaguna" VALUES(6,70,'6:70','Warra amantii isaanii taphaafi qoosaa godhatanii jireenyi addunyaa isaan gowwoomsite isaan dhiisiAkka lubbuun waan hojjatteen hin banne isaan (Qur’aanaan) gorsiRabbii gaditti jaalalleefi manguddoonni isaaniif hin taaneLubbuun hundi odoo kafaltii hunda kafalteeyyuu ishee irraa hin fuudhamuIsaan sun warra waan hojjataniin balleeffamaniIsaaniif waan kafaraa turaniif jecha dhugaatii bishaan danfaa irraa ta’eefi adabbii laalessaa ta’etu jira.');
INSERT INTO "apaguna" VALUES(6,71,'6:71','Jedhi: "Sila nuti Rabbii gaditti waan nu hin fayyanneefi nu hin miine gabbarraa? Erga Rabbiin nu qajeelchee booda akka isa dhama''aa ta''ee sheyxaanni dachii keessa isa fuudhee, kan isaaf hiriyoonni ''gara keenya kottu'' (jechuun) gara qajeelumaa isa waaman jiranu (kan isaan hin hordofnee san) of duuba deebinaa?" Dhugumatti, qajeelfamni Rabbii isatu qajeelfama (sirrii)tiNutis Gooftaa aalamatiif akka bullu ajajamnee jirra" jedhi.');
INSERT INTO "apaguna" VALUES(6,72,'6:72','Salaata dhaabuufi Isa sodaachuutti (ajajamne)Innis Isa gara Isaatti walitti qabamtaniidha.');
INSERT INTO "apaguna" VALUES(6,73,'6:73','Inni Isa samiifi dachii dhugaan uumeGuyyaa ‘ta’i’ jennaan achumaan ta’u san [yaadadhu]Jechi Isaa dhugaadhaGuyyaa xurumbaan afuufamu mootummaan kan IsaatiInni beekaa fagoo (gheybii)fi dhihootiInni ogeessa, keessa beekaadha.');
INSERT INTO "apaguna" VALUES(6,74,'6:74','Yeroo Ibraahiim abbaa isaa Azaariin "sila ati taabota gabbaramoota godhattaa? Dhugumatti, ani siifi ummata kee jallina ifaa keessattiin argaara" jedhes [yaadadhu].');
INSERT INTO "apaguna" VALUES(6,75,'6:75','Akka (beekee) mirkaneeffattoota irraas ta’uuf jecha, akka kanatti Ibraahiimiin mootummaa samiifi dachii agarsiifne.');
INSERT INTO "apaguna" VALUES(6,76,'6:76','Yeroo halkan isa irratti dukkanaa’es, urjii argeetiin "kun Gooftaa kiyya" jedheYeroo inni seenus "ani seentota hin jaaladhu" jedhe.');
INSERT INTO "apaguna" VALUES(6,77,'6:77','Yeroma ji’as ba’aa ta''ee argu "kuni Gooftaa kiyya" jedheYeroo inni seenus "odoo Gooftaan kiyya na qajeelchuu baatee silaa ani ummata jallattoota irraan ta’a" jedhe.');
INSERT INTO "apaguna" VALUES(6,78,'6:78','Yeroma aduu kan baate taatee argu "kun Gooftaa kiyya; kanatu irra guddaa dha" jedheYeroo isheen seentu "yaa ummata kiyya! ani waan isin qindeessitan irraa qulqulluu dha" jedhe.');
INSERT INTO "apaguna" VALUES(6,79,'6:79','Dhugumatti, ani fuula kiyya qajeelaa haalan ta’een gara Isa samiifi dachii uumee deebiseen jira; Ani mushrikoota irraas miti.');
INSERT INTO "apaguna" VALUES(6,80,'6:80','Ummanni isaas isaan falme[Inni] ni jedhe: "Sila dhugumatti odoo Inni na qajeelchee jiruu isin (waa’ee) Rabbii keessatti na falmituu? Waan isin Isatti qindeessitanis hin sodaadhuYoo Gooftaan kiyya fedhe malee (homtuu narra hin ga’u)Gooftaan kiyya beekumsa Isaatiin waan hunda waliin ga’eeraSila hin gorfamtanuu?');
INSERT INTO "apaguna" VALUES(6,81,'6:81','Odoo isin waan Inni ragaa isaa isin irratti hin buusin kan gabbartan hin sodaanne taatanuu, ani akkamittin waan isin qindeessitan sodaadha? Yoo kan beektan taataniif garee lamaan keessaa isa kamtu tasgabbii mirga godhata?" (jedhi).');
INSERT INTO "apaguna" VALUES(6,82,'6:82','Isaannan amananii, iimaana isaanii shirkiidhaan hin makin isaaniif tasgabbiitu jiraIsaanis qajeelfamoodha.');
INSERT INTO "apaguna" VALUES(6,83,'6:83','Isheen kun ragaa keenyaIbraahiimiif ummata isaa irratti kenninee jirraNama feenes sadarkaawwan keessatti ol fuunaDhugumatti, Gooftaan kee ogeessa, beekaadha.');
INSERT INTO "apaguna" VALUES(6,84,'6:84','Isihaaqiifi Ya’aquubiin isaaf kennineHundaahuu ni qajeelchineSana duras Nuuhiin qajeelchinee jirraSanyii isaa irraas Daawuud, Sulaymaan, Ayyuub, Yuusuf, Muusaafi Haaruunin (qajeelchinee jirra)Akka kanatti toltuu hojjattoota mindeessina.');
INSERT INTO "apaguna" VALUES(6,85,'6:85','Zakariyyaa, Yahyaa, Iisaafi Ilyaasiinis (ni qajeelchine)Hundinuu gaggaarii irraayyi.');
INSERT INTO "apaguna" VALUES(6,86,'6:86','Ismaa’il, Yasa’i, Yuunusiifi Luuxiinis (qajeelchine)Hundaayyuu aalama irra caalchifne.');
INSERT INTO "apaguna" VALUES(6,87,'6:87','Abbootii isaanii, ilmaan isaaniifi obboleeyyan isaanii irraas (qajeelchine)Isaan fillee, gara karaa qajeelaa isaan qajeelchinee jirra.');
INSERT INTO "apaguna" VALUES(6,88,'6:88','Kun qajeelloo RabbiitiGabroota isaa irraa nama fedhe isaan qajeelchaOdoo isaan qindeessanii silaa wanti isaan dalagaa turan isaan jalaa bada.');
INSERT INTO "apaguna" VALUES(6,89,'6:89','Isaan warra nuti kitaaba, ogummaafi nabiyyummaa isaaniif kennineedhaYoo isaan kun (warri Makkaa) isheetti kafaran, dhugumatti Nuti namoota isheetti hin kafarre isheef qopheessineerra.');
INSERT INTO "apaguna" VALUES(6,90,'6:90','Isaan warra Rabbiin qajeelcheedhaQajeelfama isaanii hordofi"Ani isa irratti mindaa isin hin gaafadhuInni aalamaaf yaadannoo malee homaa miti" jedhi.');
INSERT INTO "apaguna" VALUES(6,91,'6:91','Yeroo "Rabbiin nama irratti homaa hin buufne" jedhan guddina Isaaf malu Rabbiin hin guddifne"Ifaafi namootaaf qajeelfama haala ta''een kan isin waraqaalee heddu isa gootan kitaaba Muusaan ittiin dhufe eenyutu buuse? (Kan feetan) ishee mul''iftuHeddu immoo ni dhoksituWaan isiniifi abbootiin keessan hin beekne beeksifamtanii jirtan" jedhi"(Kan isa buuse) Rabbuma" jedhiErgasii haala isaan dhara isaanii keessa taphataniin isaan dhiisi.');
INSERT INTO "apaguna" VALUES(6,92,'6:92','Kunis kitaaba barakaa qabu kan (kitaabota) isa duraa dhugoomsu, (jiraattota) haadha magaalotaa (Makkaa)fi namoota naannoo ishee jiran akka ittiin dinniinuuf Nuti buufneedhaWarri Aakhiratti amanan kan salaata isaanii irratti tikfatan ta''anii isatti ni amanu.');
INSERT INTO "apaguna" VALUES(6,93,'6:93','Nama Rabbi irratti kijiba odeesse yookiin odoo wahyiin homaatuu isatti hin beeksifamin "wahyiin gara kiyyatti bu’eera" jedheefi nama "anis fakkaataa waan Rabbiin buuseetiin buusa" jedhe caalaa namni miidhaa hojjate eenyu? Odoo yeroo miidhaa hojjattoonni haguuggaa du’aa keessa ta’anii malaykonnis harka isaanii diriirsanii "lubbuu keessan baasaa; har''a isin waan Rabbi irratti dhugaan alaa jedhaa turtaniifi keeyyattoota Isaa irraa boonaa turtaniif, adabbii salphisaa galata galfamtu" (jedhanu) argitee (dubbii rifachiisaa argita ture).');
INSERT INTO "apaguna" VALUES(6,94,'6:94','Dhugumatti isin kophaa taatanii akkuma yeroo jalqabaa isin uumnetti Wantoota Nuti isiniif kennine hunda dugda duuba keessanitti dhiiftanii gara keenya dhuftaniittuNuti manguddoota (taabota) keessan kan isin “kun warra Rabbiin waliin gabbarru” jedhaa turtan isin wajjiin hin arginu(Hariiroon) gidduu keessanii dhugumatti citeeraWanti isin mormaa turtanis isin jalaa badeera.');
INSERT INTO "apaguna" VALUES(6,95,'6:95','Rabbiin baqaqsaa fireetiifi ija tamriiti(Inni) du’aa irraa fayyaa baasaFayyaa irraas du’aa baasaadhaKun Rabbii keessaniIsin akkamumatti gara galturee?');
INSERT INTO "apaguna" VALUES(6,96,'6:96',')Inni) baqaqsaa bariitiHalkan (yeroo) boqonnaa, aduufi ji’as lakkoofsa (yeroo ittiin beekuuf) godheKun qideessuu (Rabbii) injifataa, beekaati.');
INSERT INTO "apaguna" VALUES(6,97,'6:97','Inni (Rabbiin) Isa akka dukkana dachiifi bahraa keessa isaaniin qajeeltaniif urjiiwwan isiniif godhe saniDhugumatti, Nuti keeyyattoota ummata beekaniif addeessinee jirra.');
INSERT INTO "apaguna" VALUES(6,98,'6:98','Inni (Rabbiin) Isa lubbuu tokko irraa isin uumeedhaBakka ragga''iinsaa (gad-aamessaa)fi bakka qubsumaa (dugdatu isiniif jira)Dhugumatti, Nuti namoota hubataniif keeyyattoota addeessineerra.');
INSERT INTO "apaguna" VALUES(6,99,'6:99','Inni Isa samii irraa bishaan buuseIsaanis biqilaa waan hundaa baafnee, isa irraahis magariisa baafneIsa irraa firii wal koraa, muka Timiraa irraa, daraaraa ishee irraa yudoo dhihoo taate, ashaakiltii inaba irraa ta''e, zaytuuna, rummaana wal fakkaataafi wal fakkaataa hin taane baafnaMee gara oomisha isaa yeroo inni fireesseefi (gara) bilchina isaas ilaalaaDhugumatti, kana keessa ummata amananiif mallattoowwantu jira.');
INSERT INTO "apaguna" VALUES(6,100,'6:100','Odoo inni isaanis uumee jiruu jinnoota Rabbiif shariika taasisan; isaan beekumsaan maleetti ilmaan dhiiraafi dhalaa Isaaf (Rabbiif) uumanInni wantoota isaan kijibaan Isatti maxxansan irraa qulqulluufi ol ta’aadha.');
INSERT INTO "apaguna" VALUES(6,101,'6:101','Jalqabaa uumama samiifi dachiitiWaan hundaa uumee, Niitii kan hin qabne ta''ee akkamitti ilmoon isaaf ta’a? Inni waan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(6,102,'6:102','Kan akkana hojjate kun Rabbii keessaniIsa malee dhugaan gabbaramaan hin jiru(Inni) uumaa waan hundaatii, Isa (qofa) gabbaraaInnis waan hunda tiksaadha.');
INSERT INTO "apaguna" VALUES(6,103,'6:103',')Addunyaatti) agartuun Isa hin agartuInni immoo agartuu hunda ni argaInni mararfataa, beekaadha.');
INSERT INTO "apaguna" VALUES(6,104,'6:104','Dhugumatti, Gooftaa keessan irraa ragaawwan ifa ta’an isinitti dhufeeraNamni (ragaawwan kanniin) arge lubbuma isaatiif (itti fayyadama)Namni jaames isheedhuma irratti (miidhaa geessisa)Aniis isin irratti tiksaa miti.');
INSERT INTO "apaguna" VALUES(6,105,'6:105','Akkasumas (akka isaan xiinxalaniifi kitaaba durii) qu''ate jedhaniifi ummata beekaniif immoo ibsuuf keeyyattoota addeessina');
INSERT INTO "apaguna" VALUES(6,106,'6:106','Waan Gooftaa kee irraa sitti beeksifame hordofiIsa malee dhugaan gabbaramaan hin jiru; mushrikoota irraas garagali.');
INSERT INTO "apaguna" VALUES(6,107,'6:107','Odoo Rabbiin fedhee, silaa (isatti) hin qindeessanu tureIsaan irrattis tiksaa si hin gooneAti isaan irrattis moosifamaa miti.');
INSERT INTO "apaguna" VALUES(6,108,'6:108','Beekumsa maleetti jibbaadhaan Rabbiin arrabsuu warra isaan Rabbii gaditti gabbaran hin arrabsinaaAkka kanatti ummata hundaaf dalagaa isaanii miidhagsineErgasii deebiin isaanii gara Gooftaa isaaniiti; waan isaan dalagaa turanis isaanitti hima.');
INSERT INTO "apaguna" VALUES(6,109,'6:109','"Odoo mallattoon wahii isaanitti dhufee, silaa isatti amanna" jedhanii kakuu isaanii cimaa Rabbitti kakatani"Mallattoowwan Rabbuma biraayi" jedhiYeroo isheen (raajiwwan) dhufte isaan hin amananuu maaltu isin beeksise? (Rabbitu beeka malee isin hin beektanu).');
INSERT INTO "apaguna" VALUES(6,110,'6:110','Akkuma isaan yeroo jalqabaa isatti hin amanin onnee isaaniitiifi agartuu isaanii gara gaggalchinaKan dhama''an ta''anii jallina isaanii keessatti isaan dhiifna');
INSERT INTO "apaguna" VALUES(6,111,'6:111','Odoo Nuti malaykota gara isaaniitti buufnee, du’aawwanis isaaniin haasa''anii, waan hundayyuu isaan duratti walitti qabnellee, isaan yoo Rabbiin fedhe malee kan amananii mitiGaruu irra hedduun isaanii ni wallaalu.');
INSERT INTO "apaguna" VALUES(6,112,'6:112','Akka kanatti nabiyyii hundaafuu diina shayxaana namaafi jinnii irraa ta’an, kan gowwoomsuuf jecha gariin isaanii gara gariitti, jecha miidhagaa beeksisan godheeraOdoo Gooftaan kee fedhee silaa hin raawwatanu tureIsaaniifi kijiba isaan uumanis dhiisi.');
INSERT INTO "apaguna" VALUES(6,113,'6:113','Akka onneen warra Aakhiratti hin amaninii gara isaa daban, akka isas jaalataniifi (adaba irraa) waan raawwachaa jiranis akka raawwataniif.');
INSERT INTO "apaguna" VALUES(6,114,'6:114','"Odoo innni kitaaba addeeffamaa gara keessanitti buusee jiruun Rabbiin alatti murteessaa barbaaddadhaa?" (jedhi).Warri kitaaba isaaniif kennines, akka inni dhugaan Gooftaa kee irraa buufame ni beekuWarra maman irraas hin ta’in');
INSERT INTO "apaguna" VALUES(6,115,'6:115','Jechi Gooftaa keetii dhugaafi haqa haala ta''een xumurameeraJechoota Isaatiif jijjiiraan hin jiruInni dhagayaa, beekaadha.');
INSERT INTO "apaguna" VALUES(6,116,'6:116','Yoo irra hedduu namoota dachii keessaatiin ajajamte, karaa Rabbii irraa si jallisuIsaan shakkii malee homaa hin hordofanIsaan kijiboo malee homaa hin taane.');
INSERT INTO "apaguna" VALUES(6,117,'6:117','Dhugumatti, Gooftaan kee Isumatu nama karaa Isaa irraa jallatu irra beeka; warra qajeelanis Isumatu beeka.');
INSERT INTO "apaguna" VALUES(6,118,'6:118','Yoo keeyyattoota Isaatti kan amantan taataniif, waan maqaan Rabbii irratti dubbatame irraa qofa nyaadhaa.');
INSERT INTO "apaguna" VALUES(6,119,'6:119','Sila maaltu isiniif jiraayyi waan maqaan Rabbii irratti dubbatame nyaachuu dhiiftan? Wanti gara isaatti rakkifamtan odoo hafuu dhugumatti, Inni wantoota isin irratti dhorgamaa godhe isiniif addeesseeraDhugumatti, (namoonni) baay’een beekumsaan malee fedha lubbuu isaaniitiin jallatuDhugumatti, Gooftaan kee Isaatu irra beekaa warra daangaa d');
INSERT INTO "apaguna" VALUES(6,120,'6:120','Badii ifa bahaafi dhokataa isaas dhiisaaDhugumatti, isaannan cubbuu hojjatan waan kijiba uumaniif fuuldura galata galfamu.');
INSERT INTO "apaguna" VALUES(6,121,'6:121','Waan maqaan Rabbii isarratti hin dubbatamin hin nyaatinaaDhugumatti, inni fincilaDhugumatti, shayxaanonni akka isiniin falmanuuf gara jaalallee isaanii hasaasuIsinis yoo isaaniif ajajamtan dhugumatti isin mushrikoota.');
INSERT INTO "apaguna" VALUES(6,122,'6:122','Sila namni du’aa turee ergasii isa jiraachifnee ifa isaaf goone, akka fakkii nama dukkana keessa jiru, kan ishee irraa hin baanee ta''aa? Akka kanatti kaafirootaaf wanti isaan dalagaa turan miidhagfame.');
INSERT INTO "apaguna" VALUES(6,123,'6:123','Akkasumas magaalota hunda keessatti gugurdoo jallattoota ishee akka ishee keessatti dhara karoorsaniif gooneHin beekanu malee, lubbuu isaanii irratti malee dhara hin karoorsanu.');
INSERT INTO "apaguna" VALUES(6,124,'6:124','Yeroo mallattoon wahii isaanitti dhuftes "hanga fakkaataan waan ergamoonni Rabbii kennamanii nuuf kennamutti hin amannu" jedhuRabbiin ergaa Isaa eessa akka kaayu akkaan beekaWarra badii hojjatan waan isaan dhara karoorsaa turaniif Rabbiin biratti xiqqeenyaafi adabbii cimaa ta’etu (isaan) tuqa.');
INSERT INTO "apaguna" VALUES(6,125,'6:125','Namni Rabbiin isa qajeelchuu fedhe, qoma Isaa Islaamaaf bal’isaNama isa jallisuu fedhe immoo qoma isaa akkaan dhiphaa, akka nama samii keessa ol ba’uu taasisaAkka kanatti Rabbiin adabbii warra hin amanne irratti godha.');
INSERT INTO "apaguna" VALUES(6,126,'6:126','Kun qajeelaa haala ta’een karaa Gooftaa keetiitiUmmata gorfamaniif mallattoo dhugaadhaan adda baafneerra.');
INSERT INTO "apaguna" VALUES(6,127,'6:127','Isaaniif Gooftaa isaanii bira ganda nagayaatu jiraInnis waan isaan dalagaa turaniif jecha gargaaraa isaaniiti.');
INSERT INTO "apaguna" VALUES(6,128,'6:128','Guyyaa hunda isaanii walitti qabee "Yaa garee jinnootaa! Namoota daneeffattanii jirtu" (jedhuun yaadadhu)Jaalalleen isaanii kan namoota irraa ta’anis "Gooftaa keenya! gariin keenya gariitti fayyadamne; beellama keenya Ati nuuf beellamtes geenyee jirra" jedhu"Hangi Rabbiin fedhe qofti odoo hafuu, Ibiddi keessatti hafoo taatanii qubannaa keessani" jedhaDhugumatti Gooftaan kee ogeessa, beekaadha.');
INSERT INTO "apaguna" VALUES(6,129,'6:129','Waan isaan hojjataa turaniif jecha, akka kanatti garii miidhaa hojjattoota garii irratti moosifna.');
INSERT INTO "apaguna" VALUES(6,130,'6:130','"Yaa garee jinniifi namaa! Sila ergam-oonni isin irraa ta’an kan keeyyattoota kiyya isin irratti qara’aniifi qunnamtii guyyaa keessanii kana kan isin dinniinanu isinitti hin dhufnee?" (jedhamu)"Nuti lubbuu keenya irratti ragaa baanee jirra" jedhanJireenyi addunyaas isaan gowwoomsiteDhugumatti, isaan kaafiroota akka turan lubbuu isaanii irratti ragaa ba’an.');
INSERT INTO "apaguna" VALUES(6,131,'6:131','Kun waan Gooftaan kee ganda tokko haala warri ishee dagataa ta''een, badii (isheetiif jecha) kan balleessu hin ta''iniifi.');
INSERT INTO "apaguna" VALUES(6,132,'6:132','Hundaafuu waan hojjattan irraa sadarkaaleetu jiraGooftaan keetis waan isaan dalagan irraa dagataa miti.');
INSERT INTO "apaguna" VALUES(6,133,'6:133','Gooftaan kees dureessa, abbaa rahmataatiAkkuma isin sanyii ummata biraa irraa argamsiise, yoo fedhe, isin adeemsiseetiin (dhabamsiisetiin) isin booda waan fedhe bakka buusa.');
INSERT INTO "apaguna" VALUES(6,134,'6:134','Dhugumatti, wanti waadaa isiniif galame dhufaadhaIsinis kan (adabbii irraa) maqsitanii miti.');
INSERT INTO "apaguna" VALUES(6,135,'6:135','"Yaa ummata kiyya! Akkaataa adeemsa keessanii irratti dalagaa; Anis dalagaadhaNamni manni booddee isaaf ta’u eenyuun akka ta’e fuulduratti ni beektuDhugumatti, miidhaa hojjattoonni hin milkaa’anu" jedhi.');
INSERT INTO "apaguna" VALUES(6,136,'6:136','Oyruufi beyladaa inni uumerraa qooda Rabbiif godhaniiti yaaduma isaaniitiin "kun kan Rabbiiti, kun immoo kan gooftolee keenyaati" jedhanWanti gabbaramtoota isaaniitiif ta’e gara Rabbiitti hin ga’u; wanni Rabbiif ta’e immoo gara gabbaramtoota isaaniitti ni ga’aWanti isaan murteessan waa fokkate!');
INSERT INTO "apaguna" VALUES(6,137,'6:137','Akka kanatti shariikonni isaanii hedduu mushrikoota irraa ta''aniif isaan balleessuufi amantii isaanii isaan irratti walitti makuuf jecha ajjeechaa ilmaan isaanii miidhagseefOdoo Rabbiin fedhee hin raawwatanu tureKijiba isaanii wajjin isaan dhiisi');
INSERT INTO "apaguna" VALUES(6,138,'6:138','Yaada isaaniitiin "kun horiifi oyruu dhorgamtuudha; Nama nuti feene malee ishee hin nyaatu(kun) horii dugdi ishee (yaabbii irraa) dhorgamteedha; (kun immoo) horii maqaa Rabbii irratti hin dubbannee dha" jedhanRabbi irratti kijiba uumuuf (kana hojjatan)Rabbiin waan isaan kijibaa turaniin fuulduratti isaan mindeessa.');
INSERT INTO "apaguna" VALUES(6,139,'6:139','Ni jedhan: "Wanti garaa horii kanneen keessaa dhiirota keenya qofaaf hayyamamtuudha; dubartoota keenya irratti immoo dhorgamoodhaYoo du’aa ta’eef, isaan hunduu isa keessatti shariika." Ibsa isaaniitiin (kijiba Rabbi irra kaa''aniin) isaan mindeessaDhugumatti Inni ogeessa, beekaadha');
INSERT INTO "apaguna" VALUES(6,140,'6:140','Warri wallaalaan beekumsaan ala ilmaan isaanii ajjeesanii, Rabbiin irratti kijiba maxxansuun waan Rabbiin isaaniif kenne haraama taasisan, dhugumatti hoonga’aniiruIsaan jallataniiru; qajeeloo hin taane.');
INSERT INTO "apaguna" VALUES(6,141,'6:141','Rabbiin isa ashaakiltii sireen godhamuufiifi sireen hin godhamneef uumeedhaMuka timiraa, sanyii dhandhamni wal dhabaa ta''e, Zaytuunaafi Rummaana wal fakkaataniifi wal hin fakkaannes (kan uumee dha)Firee isaa irraa yeroo inni fireesse nyaadhaa; guyyaa haamama isaas haqa isaa (zakaa) kennaa(Kennaan) daangaa hin dabrinaaDhugumatti, Inni warra daangaa dabran hin jaalatu.');
INSERT INTO "apaguna" VALUES(6,142,'6:142','Horii irraas isaan fe’isaaf oolaniifi fe’isaaf hin oolle (uume)Waan Rabbiin isiniif kenne irraa nyaadhaa; tarkaanfiilee shayxaanaas hin hordofinaaDhugumatti, inni isiniif diina ifa bahaadha.');
INSERT INTO "apaguna" VALUES(6,143,'6:143','Qindiiwwan saddeeti (uumne): hoolaa irraa lama (kormaafi dhalaa), Re’ee irraas lama (kormaafi dhalaa)"Sila (Rabbiin) korma lamaan haraam godhe moo dhaltuu lamaani? Moo waan gadaamessi dhaltuu lamaanii of keessaa qabdu? Yoo kan dhugaa dubbattan taataniif, beekumsaan naaf himaa" jedhiin.');
INSERT INTO "apaguna" VALUES(6,144,'6:144','Gaala irraas lama, Loon irraas lama (kormaafi dhalaa uume)"Sila (Rabbiin) korma lamaan dhorge moo dhaltuu lamaani? Moo waan gadaamessi dhaltuu walitti qabatte? Moo yeroo Rabbiin kanatti isin ajaju hirmaataa turtani? Nama beekumsa malee namoota jallisuuf jecha Rabbiin irratti kijiba odeesse caalaa namni miidhaa hojjate hin jiruDhugumatti, Rabbiin ummata miidhaa hojjattoota hin qajeelchu."');
INSERT INTO "apaguna" VALUES(6,145,'6:145','Jedhi: "Ani waan gara kiyyatti wahyii godhame keessatti yoo du’aa (baktii) yookiin dhiiga dhangala’aa yookiin foon Booyyee, inni najisa, yookiin fincila waan Rabbiin alaatiif gorra’ame malee nama isa nyaatu irratti dhorgamaa hin argu." Namni halaalaa jiruu haraamatti darbuufi nyaata keessatti daangaa dabruun maletti rakkifame dhugumatti, Gooftaan kee araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(6,146,'6:146','Warra yahuudoman irratti abbaa qeensaa hunda haraama taasifnee jirraSa''aa, busaa''ee (re’eefi hoolaa) irraas cooma isaan lamaanii, waan dugdi isaaniitiifi marrimaan isaanii baatte yookiin waan lafeetti makame malee (isa biraa) isaan irratti haraama taasifnee jirraKana daangaa dabruu isaaniitiif isaan mindeessineNuti dhugaa dubbatoodha.');
INSERT INTO "apaguna" VALUES(6,147,'6:147','Yoo isaan si kijibsiisan "Gooftaan keessan abbaa rahmata bal’aatiadabbiin Isaas ummata yakkamaa irraa hin deebifamu" jedhiin.');
INSERT INTO "apaguna" VALUES(6,148,'6:148','Isaannan qindeessan fuulduratti ni jedhu "odoo Rabbiin fedhee, silaa nutis ta’ee abbootiin keenya hin qindeessinu turreWanta tokkos haraama hin taasifnu turreAkkasuma warri isaan dura turan hanga adabbii keenya dhandhamanitti kijibsiisan"Sila beekumsa irraa inni isin nuuf baasuu (fiduu) dandeessan isin bira ni jiraa? Shakkii malee homaa hordofaa hin jirtanuIsinis kijiboo malee hin taane" jedhi.');
INSERT INTO "apaguna" VALUES(6,149,'6:149','"Ragaan guutuun kan RabbiitiInni odoo fedhee, silaa hunda keessan ni qajeelcha ture" jedhi.');
INSERT INTO "apaguna" VALUES(6,150,'6:150','Jedhi: "Mee ragoota keessan kan akka Rabbiin waan kana haraama taasise ragaa ba’an fidadhaaYoo isaan ragaa ba’an, ati isaan waliin ragaa hin ba’inFedha lubbuu warra keeyyattoota keenya kijibsiisanii, warra Aakhiraatti hin amanneefi isaan (qaamota biraa) Gooftaa isaanii wajjin wal qixxeessan hin hordofin."');
INSERT INTO "apaguna" VALUES(6,151,'6:151','Jedhi: "Kottaa! Waan Gooftaan keessan isin irratti haraama godhen isin irratti qara’a: Isatti waa qindeessuu dhiisuu, abbaafi haadhatti tola ooluu, ilmaan keessan beelaaf jecha hin ajjeesinaa, Nutu isiniifi isaanis soora; wantoota fokkatoo ta’an kan mul’ates ta’ee waan dhokate itti hin dhihaatinaa; lubbuu Rabbiin (ajjeesuu) haraama godhes haqaan maleetti hin ajjeesinaaIsa san akka xiinxaltaniif ishee isiniif dhaame.');
INSERT INTO "apaguna" VALUES(6,152,'6:152','Qabeenya yatiimaas yoo toltuudhaan ta’e malee hanga inni umrii cimina isaa ga’utti itti hin dhihaatinaa; safaraafi madaalas haqaan guutaaNuti lubbuu tokkos waan isheen dandeessutti malee hin dirqisiifnuOdoma fira dhihoo ta’ellee yeroo jecha (ragaa) kennitanis, haqa dalagaaWaadaa Rabbiis guutaaKana akka isin yaadattaniif isinii dhaama.”');
INSERT INTO "apaguna" VALUES(6,153,'6:153','Dhugumatti, kun karaa kiyya Isa qajeelaa dhaa, isa hordofaaKaraa Rabbii irraa isin baasanii karaa heddu hin hordofinaaKana akka isin sodaattaniif isa isiniif dhaama.');
INSERT INTO "apaguna" VALUES(6,154,'6:154','Isaan toltuu hojjatan irratti (ni’imaa keenya) guutuuf, waan hunda addeessuuf, qajeelfamaafi rahmata gochuuf, akka isaan qunnamtii Gooftaa isaanii waliin godhanitti amananiif muusaaf kitaaba kennine.');
INSERT INTO "apaguna" VALUES(6,155,'6:155','Kun kitaaba Nuti buufne kan barakeeffameedhaKanaafuu isa hordofaaAkka rahmanni isiniif godhamu (Rabbiin) sodaadhaa.');
INSERT INTO "apaguna" VALUES(6,156,'6:156','Akka isin (Arabni amantii hin qabne) "kitaabichi kan buufame garee lamaan nu duraatiifi, nutis qo’annoo isaanii irraa homaa hin beeknu" hin jenneefi.');
INSERT INTO "apaguna" VALUES(6,157,'6:157','Yookiin akka isin "nuti odoo kitaabni nurratti buufamee dhugumatti silaa isaan (yahuudotaafi kiristaanota) caalaa karaa qajeelaa qabanna turre" hin janneefiDhugumatti, ragaan ifa ta’e (Qur’aanni), qajeelfamaafi rahmanni Gooftaa keessan irraa isiniif dhufeeraNama keeyyattoota Rabbii kijibsiiseefi ishee irraa gara gale caalaa namni miidhaa hojjate eenyu? Nuti warra keeyyattoota keenya irraa gara galan sababaa isaan gara galaa turaniif adabbii hamaa isaan mindeessina.');
INSERT INTO "apaguna" VALUES(6,158,'6:158','Sila malaykonni isaanitti dhufuu, yookiin Gooftaan kee dhufuu, yookiin gariin mallattoolee Gooftaa keetii dhufuu malee homaa ni eegaaruuGuyyaa gariin mallattoo Gooftaa keetii dhufu lubbuu san dura hin amanin, yookiin iimaana ishee keessatti toltuu hin hojjatin tokko iimaanni ishee homaa ishee hin fayyadu"Isin eegaa; Nuti eegoo dha" jedhiin.');
INSERT INTO "apaguna" VALUES(6,159,'6:159','Dhugumatti, warri amantii isaanii qoqqoodaniifi garee garee ta''an, ati waan tokko keessattillee isaan irraayii mitiDhimmi isaanii gara Rabbiiti, ergasii Inni waan isaan dalagaa turan (hunda) isaanitti hima.');
INSERT INTO "apaguna" VALUES(6,160,'6:160','Namni toltuun dhufe, isaaf (toltuu) fakkaattuu ishee kudhantu jiraNamni hamtuun dhufe, inni fakkaattuu ishee malee hin mindeeffamuIsaanis hin miidhamanu.');
INSERT INTO "apaguna" VALUES(6,161,'6:161','"Dhugumatti, ani Gooftaan kiyya gara karaa qajeelaa, amantii sirrii kan karaa Ibraahiim, ‘haniifa’ (Rabbii tokkicha gabbaruu) ta’etti na qajeelchee jiraInni mushrikoota irraa hin turre" jedhi.');
INSERT INTO "apaguna" VALUES(6,162,'6:162','Jedhi: "Dhugumatti salaanni kiyya, ibaadaan kiyya, jireenyi kiyyaafi duuti kiyyas Rabbii Gooftaa aalama qofaafi.');
INSERT INTO "apaguna" VALUES(6,163,'6:163','Shariikni isaaf hin jiru; isuma kanattin ajajame; ani jalqaba Muslimootati."');
INSERT INTO "apaguna" VALUES(6,164,'6:164','Jedhi: "Sila ani Rabbiin ala odoo Inni Gooftaa waan hundaa ta’ee jiru Gooftaa biraa barbaadaa? Lubbuun hundi isheedhuma irratti malee (badii) hin hojjattu(Lubbuun) ba’aa (badii) baadhattu tokko ba’aa kan biraa hin baadhattuErgasii deebiin keessan gara Gooftaa keessaniitiWantoota isin isa keessattis wal dhabaa turtan (hunda) isinitti hima."');
INSERT INTO "apaguna" VALUES(6,165,'6:165','Rabbiin Isa dachii keessatti bakka bu’oo isin godhe; waan isinii kenne keessatti isin qormaatuuf jecha, sadarkaa keessatti garii keessan garii irra ol fuudheDhugumatti, Gooftaan kee adabbiin isaa saffisaadha; ammas dhugumatti Inni araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(7,1,'7:1','Alif Laam Miim Saad');
INSERT INTO "apaguna" VALUES(7,2,'7:2','(Kun) kitaaba gara keetti buufameIsa (geessuu) irraa qoma kee keessa dhiphinni hin jiraatinAkka isaan akeekkachiiftuufi mu’uminootaaf gorsa ta’uuf (buufne)');
INSERT INTO "apaguna" VALUES(7,3,'7:3','Waan Gooftaa keessan irraa gara keessanitti buufame hordofaaJaalallee (biraa) isaa gaditti hin hordofinaaXiqqoo qofa gorfamtu');
INSERT INTO "apaguna" VALUES(7,4,'7:4','Magaalota irraa kan Nuti balleessinee adabbiin keenya halkan yookiin guyyaa isaanii rafuutti jiruu isheetti dhufe hedduudha.');
INSERT INTO "apaguna" VALUES(7,5,'7:5','Yeroo adabbiin keenya isaanitti dhufu jechi isaanii "nuti miidhaa raawwattoota turre" jechuu malee waan biraa hin taane.');
INSERT INTO "apaguna" VALUES(7,6,'7:6','Dhugumatti, isaan (ergamaan) gara isaaniitti ergame ni gaafanna; ergamootas ni gaafanna.');
INSERT INTO "apaguna" VALUES(7,7,'7:7','Dhugumatti, (guutuu seenaa isaanii) beekumsaan isaan irratti odeessinaNuti (kana irraa) fagoo hin taane.');
INSERT INTO "apaguna" VALUES(7,8,'7:8','Madaalli guyyaa san dhugaadhaNamni madaalli (hojii gaarii) isaa ulfaatte, isaan sun isaanumatu milkaayoodha.');
INSERT INTO "apaguna" VALUES(7,9,'7:9','Namni madaalli (hojii gaarii) isaa salphatte immoo, isaan sun warra waan keeyyattoota keenya miidhaa turaniif lubbuu isaanii hoongessaniidha.');
INSERT INTO "apaguna" VALUES(7,10,'7:10',' Dhugumatti, dachii keessa isinii mijeessinee ishee keessas waan ittiin jireenyaa isiniif gooneerra; Galateeffannaan keessan xiqqoodha.');
INSERT INTO "apaguna" VALUES(7,11,'7:11',' Dhugumatti, isin uumneerraErgasiis isin bocneErgasiis maleykotaan "Aadamiif sujuudaa" jenneAchumaan sujuudani; Ibliis maleeInni warra sujuudan irraa hin taane.');
INSERT INTO "apaguna" VALUES(7,12,'7:12',' (Rabbis) "Yeroo ani si ajaju, sujuuduu irraa maaltu si dhorge?" jedhe(Ibliis) "Anatu isa caala; Ibidda irraa na uumteetiin isa dhoqqee (biyyee) irraa uumte" jedhe.');
INSERT INTO "apaguna" VALUES(7,13,'7:13',' (Rabbiinis) "ishee irraa bu’i; Ishee keessatti boonuun siif hin taane; ba’i ati warra salphattoota irraayi" jedheen');
INSERT INTO "apaguna" VALUES(7,14,'7:14',' (Ibliis) "Hanga guyyaa isaan kaafamaniitti na tursiisi" jedhe.');
INSERT INTO "apaguna" VALUES(7,15,'7:15',' (Rabbiiniis) "Ati warra tursiifaman irraayi" jedheen.');
INSERT INTO "apaguna" VALUES(7,16,'7:16',' (Ibliisis) ni jedhe: "Waan ati na jallifteef, Dhugumatti, karaa kee qajeeloo irran isaaniif taa’a');
INSERT INTO "apaguna" VALUES(7,17,'7:17',' Ergasii fuuldura isaanii, duuba isaanii, mirga isaaniitiifi bitaa isaanii irraan isaanitti dhufaIrra hedduu isaaniis galateeffattoota ta''anii hin argitu."');
INSERT INTO "apaguna" VALUES(7,18,'7:18',' (Rabbiinis) ni jedhe: "Ishee irraa arrabsamaa, fageeffamaa haala taateen ba’iIsaan irraa namoota si hordofe, isin hunda irraan Jahannama guuta.');
INSERT INTO "apaguna" VALUES(7,19,'7:19',' Yaa Aadam! Atiifi niitiin kee jannata keessa taa''aa; iddoo feetan irraas nyaadhaaMiidhaa raawwattoota irraa taatu mukattii kanatti immoo hin dhihaatinaa."');
INSERT INTO "apaguna" VALUES(7,20,'7:20',' Shayxaanni qaama saalaa isaanii kan dhokatee ture ifatti mul’isuuf isaan lamaanitti hasaase; "Rabbiin keessan mukattii kana irraa akka isin lamaan malaykota hin taaneef yookiin akka isin lamaan hafoo hin taaneefi malee waan biraatiif isin hin dhorgine" jedheen.');
INSERT INTO "apaguna" VALUES(7,21,'7:21',' "Dhugumatti, ani isiniif warra toltuu yaadan irraayi" (jechuun) isaan lamaaniif kakate.');
INSERT INTO "apaguna" VALUES(7,22,'7:22',' Gowwoomsuun isaan jalliseYeroma isaan mukattii dhandhaman, qaamni saalaa isaanii ni mul''atte; isaanis qaama isaanii baala jannataatiin haguuguu jalqabanGooftaan isaaniis "Sila Ani mukattii tana irraa isin hin dhorginee; shayxaanni isin lamaanif diina ifa ta’e isiniin hin jennee?" jechuun isaanitti lallabe.');
INSERT INTO "apaguna" VALUES(7,23,'7:23',' "Gooftaa keenya! lubbuu keenya miinee jirraYoo ati nuuf hin araaramneefi rahmata nuuf hin godhin, nuti Dhugumatti, warra hoonga’an irraa taana" jedhan.');
INSERT INTO "apaguna" VALUES(7,24,'7:24',' (Rabbiin) ni jedhe: "Gariin keessan gariif diina haala taataniin bu’aaIsiniif dachii irra qubsumaafi qananii hanga yeroo murtaa’ee ta''eetu jira."');
INSERT INTO "apaguna" VALUES(7,25,'7:25',' "Isheedhuma keessa jiraattu; ishee keessattis duutu; ishee irraas baafamtu" jedheen');
INSERT INTO "apaguna" VALUES(7,26,'7:26',' Yaa ilmaan Aadam! Nuti uffata qaama saalaa keessan haguuguufi faaya isin irratti buufnee jirraUffata sodaa Rabbiitu, saniitu caalaKun mallattoowwan Rabbii irraayi; akka isaan yaadataniif (isaaniif himne).');
INSERT INTO "apaguna" VALUES(7,27,'7:27',' Yaa ilmaan Aadam! Akka abbaafi haadha keessan qaama saalaa isaanii isaanitti mul’isuuf uffata isaanii isaan irraa mulquun jannata irraa baasetti akka shayxaanni isin hin mokkorreDhugumatti, inniifi gosti isaa iddoo isin isaan hin agarre irraa isin arguNuti isaan hin amanneef shayxaanota jaalallee goonee jirra.');
INSERT INTO "apaguna" VALUES(7,28,'7:28',' Yeroo waan fokkattuu hojjatanis "abbootii keenya ishee irratti agarre; Rabbiinis isheetti nu ajaje" jedhu"Rabbiin fokkataatti hin ajajuSila isin waan hin beekne Rabbi irratti jettuu?" jedhiin.');
INSERT INTO "apaguna" VALUES(7,29,'7:29',' "Gooftaan kiyya haqatti ajajeMasjida hunda biratti fuula keessan (ibaadaaf gara isaa) sirreessaaAmantii keessan isaaf qulqulleessaa haala taataniin isa gabbaraaAkkuma isin jalqabetti deebitu" jedhi');
INSERT INTO "apaguna" VALUES(7,30,'7:30',' Garee tokko ni qajeelche; gareen biraa immoo jallinni isaan irratti mirkanaa’eDhugumatti, isaan Rabbii gaditti shayxaanota jaalallee taasifatani‘Isaan qajeeloo dha’ jechuus yaadu.');
INSERT INTO "apaguna" VALUES(7,31,'7:31',' Yaa ilmaan Aadam! Masjida hunda birattuu uffata keessan qabadhaaNyaadhaa, dhugaa, daangaa immoo hin dabrinaaDhugumatti, Inni daangaa dabritoota hin jaalatu.');
INSERT INTO "apaguna" VALUES(7,32,'7:32',' "Namni uffata ishee Rabbiin gabroota Isaatiif baaseefi soorata irraas gaggaarii dhorge eenyu?" jedhiIsheen Guyyaa Qiyaamaa warra amananiif dhuunfaa haala taateen jiruu addunyaa keessattis kan isaaniiti" jedhiAkka kanatti keeyyattoota ummata beekaniif addeessina.');
INSERT INTO "apaguna" VALUES(7,33,'7:33',' Jedhi: "wanti Gooftaan kiyya haraama godhe gochaalee fokkatoo, isaan irraa waan ifa ta’ees ta’ee kan dhokate, dilii, karaan ala daangaa dabruu, waan inni ragaa isaaf hin buusin Rabbitti qindeessuufi Rabbi irratti waan hin beekne jechuudha."');
INSERT INTO "apaguna" VALUES(7,34,'7:34',' Ummata hundaafuu yeroo murtaa’etu jiraYeroo beellamni isaanii dhufe sa’aatii tokko booda hin aanfamanu; duras hin dabranu.');
INSERT INTO "apaguna" VALUES(7,35,'7:35',' Yaa ilmaan Aadam! Yoo ergamoonni isin irraa ta’an kan keeyyattoota kiyya isin irratti qara’an isinitti dhufan, namni (Rabbiin) sodaatee toltuu hojjate, sodaan isaan irra hin jiru; isaan hin gaddanus.');
INSERT INTO "apaguna" VALUES(7,36,'7:36',' Warri keeyyattoota keenya kijibsiisanii ishee irraa boonan, sunniin warra ibiddaati; isaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(7,37,'7:37',' Nama Rabbiin irratti kijiba uume yookiin keeyyattoota Isaa kijibsiise caalaa namni miidhaa hojjate eenyu? Sunniin qoodni isaanii kitaaba irraa haala ta''een isaan dhaqqaba; Ergamoonni keenya lubbuu isaanii kan fuudhan ta''anii yeroo isaanitti dhufan "Warri Rabbii gaditti gabbaraa turtan eessa jiru?" jedhu"Nurraa badaniiru" jedhu"Isaan kaafiroota" jechuu lubbuu isaanii irratti ragaa ba’an.');
INSERT INTO "apaguna" VALUES(7,38,'7:38',' (Rabbiin) ni jedha: "Isin ummatoota isin dura dabran kan jinnootaafi namoota irraa ta’an keessa ta’aatii ibidda seenaa." Ummanni wahii dhawaata seentuun obboleettii ishee (kan ishee jallifte) abaartiYeroo hundi isaanii ishee keessatti walitti qabamanitti isheen boodaa ishee duraatiin "Gooftaa keenya! Isaan kun nu jallisanii jiruKanaaf adabbii dachaa ta’e ibidda irraa isaaniif kenni" jedhu(Rabbiinis) "Hundaafuu dachaatu jiraGaruu isin hin beektanu" jedhe.');
INSERT INTO "apaguna" VALUES(7,39,'7:39',' Warri jalqabaas warra boodaatiin "isiniif nurra caaliinsi wahii hin jiru; Waan hojjachaa turtaniif jecha adabbii dhandhamaa" jedhaniin.');
INSERT INTO "apaguna" VALUES(7,40,'7:40',' Dhugumatti, isaan keeyyattoota keenya kijibsiisaniifi isaan irraa boonan, balballan samii isaaniif hin banamuHanga gaalli qaawwa lilmoo keessa seenutti jannatas hin seenanuAkka kanatti yakkamtoota mindeessina.');
INSERT INTO "apaguna" VALUES(7,41,'7:41',' Isaaniif Jahannam irraa firaashii, gubbaa isaaniitii immoo haguuggiitu jiraAkka kanatti miidhaa raawwattoota mindeessina.');
INSERT INTO "apaguna" VALUES(7,42,'7:42',' Isaannan amananii, gaggaarii hojjatan, Nuti lubbuu tokkos dandeettii ishee malee hin dirqisiifnu; isaan sun warra jannataatiIsaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(7,43,'7:43',' Jibbaa qoma isaanii keessa jirus ni baafneLaggeen jala (gamoo) isaanii yaati"Faaruun kan Rabbii kanaaf nu qajeelcheetiOdoo Rabbiin nu qajeelchuu baatee hin qajeellu turre; ergamoonni Rabbii keenyaa dhugaadhaan dhufteetti" jedhan"Jannanni kun sababa waan hojjataa turtaniitiin dhaalchifamtanii dha" jechuun (isaanitti) lallabamu.');
INSERT INTO "apaguna" VALUES(7,44,'7:44',' Warri jannataa warra ibiddaatti "Nuti Dhugumatti, waan Gooftaan keenya waadaa nuuf gale dhugaa haala ta’een arganneerraIsin waan Gooftaan keessan waadaa isiniif gale haala dhugaa ta’een argattanii jirtuu?" jechuun itti lallabu"Eeyyeen" jedhanLallabaan gidduu isaaniitti ta’e "Abaarsi Rabbii miidhaa hojjattoota irra haa jiraatu" jechuun lallabe.');
INSERT INTO "apaguna" VALUES(7,45,'7:45',' (Isaan) warra karaa Rabbii irraa deebisaniifi akka isheen jallattu fedhaniidhaIsaanis Aakhiratti kafaroodha.');
INSERT INTO "apaguna" VALUES(7,46,'7:46',' Gidduu isaanii haguuggaatu jira''A’araaf'' irras dhiirota hundayyuu mallattoo isaanitiin beekantu jiruWarra jannataatiinis "nageenyi isin irratti haa jiraatu" jechuun itti lallabuIsaanis (warri a’araaf) haala kan ishee kajeelan ta''aniin ishee hin seenne.');
INSERT INTO "apaguna" VALUES(7,47,'7:47',' Yeroo ijji isaanii gara warra ibiddaa deebifamte "Gooftaa keenya! Ummata miidhaa hojjattoota wajjiin nu hin taasisin" jedhu.');
INSERT INTO "apaguna" VALUES(7,48,'7:48',' Warri a’araaf dhiirota mallattoo isaanitiin beekanitti lallaban"(Qabeenya) walitti qabuun keessaniifi wanti isin boonaa turtan waa isin hin fayyanne" jedhu.');
INSERT INTO "apaguna" VALUES(7,49,'7:49',' Sila isaan kun warra Rabbiin rahmataan isaaniif hin godhu jettanii kakattaniidhaa? Jannata seenaa, sodaan isin irra hin jiru; isinis kan gadditanu hin taane (jedhamu)');
INSERT INTO "apaguna" VALUES(7,50,'7:50',' Warri ibiddaa "Bishaan irraa yookiin waan Rabbiin isin soore irraa nurratti dhangalaasaa" jechuun warra jannataatti lallabu"Dhugumatti, Rabbiin isaan lamaan kaafiroota irratti haraama taasiseera" jedhaniin.');
INSERT INTO "apaguna" VALUES(7,51,'7:51',' (Isaan) warra amantii isaanii qoosaafi tapha godhatanii jireenyi addunyaa isaan gowwoomsiteedhaAkkuma isaan walqunnamtii guyyaa isaanii kana irraanfataniifi keeyyattoota keenya mormaa turan guyyaa har''aa isaan irraanfanna.');
INSERT INTO "apaguna" VALUES(7,52,'7:52',' Dhugumatti, Nuti kitaaba beekumsaa wajjiin isa addeessineen haala qajeelumaafi rahmata ummata amananiif ta''een isaanitti dhufnee jirra.');
INSERT INTO "apaguna" VALUES(7,53,'7:53',' Sila isaan xumura isaa malee maal eegaaru? Guyyaa xumurri isaa dhufu warri san dura isa dagatan "Dhugumatti, ergamoonni Gooftaa keenyaa dhugaan dhufteettiSila araarsonni nuuf araarsan nuuf jiraa? Yookiin dalagaa isa hojjataa turreen alaa hojjachuuf (gara addunyaatti) deebifamnaa?" jedhuDhugumatti, lubbuu isaanii hoongessanii jiruWanti isaan kijibaan uumaa turanis isaan jalaa badeera.');
INSERT INTO "apaguna" VALUES(7,54,'7:54',' Dhugumatti, Gooftaan keessan Rabbii Isa samiifi dachii guyyoota jaha keessatti uumee ergasii Arshii irratti (ol ta’iinsa isaaf malu) ol ta’eedhaHaala ariitiidhaan isa barbaada ta''een halkaniin guyyaa haguugaAduu, ji’aafi urjiiwwanis kan ajaja Isaatiin laaffifaman ta''anii (isaan uume)Dhagayaa! Uumuuniifi ajajnis kan IsaatiRabbiin Gooftaa aalamaa ta’e toltuun isaa baay’ate.');
INSERT INTO "apaguna" VALUES(7,55,'7:55',' Gooftaa keessan gadi of qabuufi dhoksaadhaan kadhaaDhugumatti, Inni daangaa dabritoota hin jaalatu.');
INSERT INTO "apaguna" VALUES(7,56,'7:56',' Dachii irrattis erga isheen toltee booda hin balleessinaaSodaafi kajeellaadhaan Isa kadhaaDhugumatti, rahmanni Rabbii warra toltuu hojjatanutti dhihoodha.');
INSERT INTO "apaguna" VALUES(7,57,'7:57',' Inni Isa bubbeewwan gammachiisaa taasisee rahmata Isaa dura erguudhaYeroo isheen duumessa ulfaataa baadhatte gara biyya duuteetti oofna; bishaan isatti buusuun fuduraalee hunda baafneAkka isin gorfamtaniif jecha akka kanatti du’aa (awwaala irraa) kaafna.');
INSERT INTO "apaguna" VALUES(7,58,'7:58',' Biyyi gaariin biqilaan isaa hayyama Gooftaa isaatiin bahaInni bade immoo (biqilaan isaa) dadhabbiidhaan malee hin bahuAkka kanatti keeyyattoota namoota galateeffataniif addeessina.');
INSERT INTO "apaguna" VALUES(7,59,'7:59',' Dhugumatti, Nuti Nuuhiin gara ummata isaatti erginee "Yaa ummata kiyya! Rabbiin qofa gabbaraaIsa malee gabbaramaan biraa isiniif hin jiruDhugumatti, ani adabbii guyyaa guddaan isin irratti sodaadha" jedheen.');
INSERT INTO "apaguna" VALUES(7,60,'7:60',' Qondaalonni ummata isaa irraa ta’an "Dhugumatti, nuti jallina ifa galaa keessatti si argina" jedhan.');
INSERT INTO "apaguna" VALUES(7,61,'7:61',' (Nuuhis) ni jedhe: "Yaa ummata kiyya! Ani jallinni homaatuu natti hin jiruGaruu ani ergamaa Gooftaa aalamaa irraa ta’eedha.');
INSERT INTO "apaguna" VALUES(7,62,'7:62',' Ergaa Gooftaa kiyyaan isin dhaqqabsiisa; toltuus isiniifin yaadaRabbi irraahis waan isin hin beeknen beeka.');
INSERT INTO "apaguna" VALUES(7,63,'7:63',' Akka Rabbiin sodaattaniifi akka mararfatamtaniif nama isin irraa ta’e tokko irratti akka isin akeekkachiisuuf gorsi Rabbii keessan irraa ta’e isinitti dhufuu dinqisiifattanii?" (isaaniin jedhe).');
INSERT INTO "apaguna" VALUES(7,64,'7:64',' Isa kijibsiisanIsaafi warra doonii keessa isa waliin ta’an ni baraarsineIsaan keeyyattoota keenya kijibsiisan immoo bishaaniin dhidhimsineDhugumatti, isaan ummata jaamaa turan.');
INSERT INTO "apaguna" VALUES(7,65,'7:65',' Gara Aadis obboleessa isaanii Huud (erginee) "Yaa ummata kiyya! Rabbiin qofa gabbaraa; Isa malee gabbaramaan biraa isiniif hin jiruSila hin sodaattanuu?" jedheen.');
INSERT INTO "apaguna" VALUES(7,66,'7:66',' Ummata isaa irraas qondaalonni warra kafaranii "Dhugumatti, nuti gowwummaa keessatti si arginaDhugumatti, kijibdootaa irraa ta''uu kees yaanna" jedhaniin.');
INSERT INTO "apaguna" VALUES(7,67,'7:67',' (Huudis) ni jedhe: "Yaa ummata kiyya! Ani gowwummaan homaatuu natti hin jiruGaruu ani ergamaa Gooftaa aalama hundaa irraa ta''eedha.');
INSERT INTO "apaguna" VALUES(7,68,'7:68',' Ergaalee Gooftaa kiyyaan isin dhaqqabsiisaAnis isiniif tola yaadaa, amanamaadha.');
INSERT INTO "apaguna" VALUES(7,69,'7:69',' Akka isin akeekkachiisuuf jecha gorsi Rabbii keessan irraa nama isin irraa ta''e irratti (bu''ee) isinitti dhufuu dinqisiifattanii? Yeroo Inni ummata Nuuh booda bakka bu’oota isin taasiseefi uumama keessattis bal''ina isiniif dabale yaadadhaaAkka milkooftaniif jecha ni’imaa Rabbii yaadadhaa."');
INSERT INTO "apaguna" VALUES(7,70,'7:70',' "Akka nuti Rabbiin tokkicha Isaa gabbarruufi wantoota abbootiin keenya gabbaraa turan dhiifnuuf nutti dhuftee? Yoo warra dhugaa dubbatu irraa taateef waan ittiin nu sodaachiftu san nutti fidi" jedhan.');
INSERT INTO "apaguna" VALUES(7,71,'7:71',' "Dhugumatti, adabniifi dallansuun Gooftaa keessan irraa isin irratti bu’eeraSila isin maqaalee isiniifi abbootiin keessan ishee moggaaftan kan Rabbiin isheedhaan ragaa hin buusin keessatti na mormituu? Eegaa; ani isin wajjin warra eegan irraayii" jedhe.');
INSERT INTO "apaguna" VALUES(7,72,'7:72',' Isaafi warra isa waliin turan rahmata nurraa ta’een nagaa baafneWarra keeyyattoota keenya kijibsiisaniifi isaan mu’uminootas hin ta’in hundee isaanii buqqifne.');
INSERT INTO "apaguna" VALUES(7,73,'7:73',' Gara Samuudis obboleessa isaanii Saalihiin (erginaan) ni jedhe: "Yaa ummata kiyya! Rabbiin qofa gabbaraaIsa malee gabbaramaan biraa isiniif hin jiruDhugumatti, Gooftaa keessan irraa mallattoon ifaa taate isinitti dhuftee jirtiKun haala mallattoo isiniif taateen gaala Rabbiitii, ishee dhiisaa dachii Rabbii keessa haa nyaattuHamtuunis ishee hin tuqinaa adabbii laalessaatu isin qabata');
INSERT INTO "apaguna" VALUES(7,74,'7:74',' Yeroo Inni Aadiin booda bakka bu’oota isin taasiseefi dachii keessa isin jiraachise yaadadhaaDirree ishee irraa gamoowwan ijaarrattu; gaarreen irraas qottaniitin manneen taasiftuKanaafuu, ni’imaa Rabbii yaadadhaa; badii raawwataa taatanii dachii keessa hin balleessinaa."');
INSERT INTO "apaguna" VALUES(7,75,'7:75',' Ummata isaa irraa qondaalonni warri boonan warra hacuucaman irraa warra amananiin "sila Saalih Rabbii isaa biraa ergamuu ni beektuu?" jedhaniin"(Eeyyeen) Nuti waan inni ittiin ergametti amanoodha" jedhan.');
INSERT INTO "apaguna" VALUES(7,76,'7:76',' Isaannan boonan "Dhugumatti, nuti isa isin itti amantan sanatti kafarreerra" jedhan.');
INSERT INTO "apaguna" VALUES(7,77,'7:77',' Yeroma san gaalattii waraanan; ajaja Gooftaa isaanii irraas bahanii "Yaa Saalih! Yoo ergamtoota irraa taateef waan ittiin nu sodaachiftu nutti fidi" jedhan.');
INSERT INTO "apaguna" VALUES(7,78,'7:78',' Yeroma san sochiin (kirkirri) dachii isaan qabatte; manneen isaanii keessatti jilbeenfatanii (dhumanii) bariifatan.');
INSERT INTO "apaguna" VALUES(7,79,'7:79',' (Saalihis) isaan irraa gara galee "Yaa ummata kiyya! Dhugumatti, ani ergaa Gooftaa kiyyaa isin dhaqqabsiiseera; tolas isiniif yaadeeraGaruu isin nama isiniif yaadu hin jaalattanu" jedhe.');
INSERT INTO "apaguna" VALUES(7,80,'7:80',' Luuxiinis, yeroo inni ummata isaatiin jedhe [yaadadhaa]: "Sila isin badii fokkataa aalama irraa (namni) tokkoyyuu isheedhaan isin hin dursin raawwattuu?');
INSERT INTO "apaguna" VALUES(7,81,'7:81',' Dhugumatti, isin fedhii saalaatiif jecha dubartii dhiiftanii dhiiratti dhuftuu! Dhugumatti, isin ummata daangaa dabroodha."');
INSERT INTO "apaguna" VALUES(7,82,'7:82',' Deebiin ummata isaas "magaalaa keessan irraa isaan baasaa; isaan Dhugumatti, ummata qulqulleeffatanuu dha" jechuu malee homaa hin turre.');
INSERT INTO "apaguna" VALUES(7,83,'7:83',' Isaafi maatii isaa ni baraarsineNiitiin isaa garuu warra (adabbii keessatti) hafan irraa taate.');
INSERT INTO "apaguna" VALUES(7,84,'7:84',' Rooba (adabaas) isaan irratti roobsifneBooddeen yakkamtootaa akkam akka ta’e ilaali.');
INSERT INTO "apaguna" VALUES(7,85,'7:85',' Gara Madyanis obboleessa isaanii Shu’aybiin (erginee) ni jedhe: "Yaa ummata kiyya! Rabbiin qofa gabbaraaIsa malee gabbaramaan biraa isiniif hin jiruDhugumatti, ragaan ifa taate isiniif dhuftee jirtiSafaraafi madaalas guutaaNamootas waan isaanii jalaa hin hir’isinaaDachii keessattis erga toluu isheetii hin balleessinaaYoo mu’uminoota taatan sanatu isiniif caala.');
INSERT INTO "apaguna" VALUES(7,86,'7:86',' Namoota sodaachisuufi karaa Rabbii irraas namoota isatti amanan deebisuun akka isheen jallattu kan barbaaddan taatanii xiqqoo turtanii isin baay’esses yaadadhaa.karaa hunda irraa hin taa’inaaYeroo isin Booddeen warra badii raawwatanii akkam akka ta’es ilaalaa');
INSERT INTO "apaguna" VALUES(7,87,'7:87',' Yoo gareen isin irraa ta’e tokko waan ani ittiin ergametti amananii gareen biraa immoo hin amanin hanga Rabbiin gidduu keenyatti murteessutti obsaaInnis irra caalaa warra murteessaniiti" (isaaniin jedhe).');
INSERT INTO "apaguna" VALUES(7,88,'7:88',' Ummata isaa irraa qondaalonni warra boonanii "Yaa Shu’aybi! nuti dhugumatti, siifi isaan si waliin amananis magaalaa keenya irraa ni baafna; yookiin isin (hunduu) gara amantii keenyaa deebi’uu qabdu" jedhan"Sila odoo kan jibbinus taanee moo!" jedhe.');
INSERT INTO "apaguna" VALUES(7,89,'7:89',' "Nuti yoo erga Rabbiin sana irraa nu baraarsee booda gara amantii keessanii deebine, Dhugumatti, Rabbiin irratti kijiba uumnee jirraYoo Rabbiin, Gooftaan keenya fedhe malee ishee keessatti deebi’uun nuuf hin taaneGooftaan keenya beekumsaan waan hunda marsee jiraRabbi irratti hirkannee jirra(Yaa) Gooftaa keenya! Gidduu keenyaafi gidduu namoota keenyaatti dhugaan murteessi; Ati irra caalaa warra murteessaniiti" (jedhe).');
INSERT INTO "apaguna" VALUES(7,90,'7:90',' Ummata isaa irraa qondaalonni warra kafaranii "isin yoo Shu’aybiin hordoftan, Dhugumatti, isin yeroo san hoonga’oo dha" jedhan.');
INSERT INTO "apaguna" VALUES(7,91,'7:91',' Yeroma san sochiin (kirkirri) dachii isaan qabatte; manneen isaanii keessatti jilbeenfatoo ta''anii (du''anii) bariisifatan.');
INSERT INTO "apaguna" VALUES(7,92,'7:92',' Warri Shu’aybiin kijibsiisan akka waan ishee keessa hin turree ta’anWarri Shu’aybiin kijibsiisan isaan hoonga’oo ta’an.');
INSERT INTO "apaguna" VALUES(7,93,'7:93',' (Shu’ayb) isaan irraa gara galee "yaa ummata kiyya! Ani ergaa Gooftaa kiyyaa isin dhaqqabsiiseeraToltuus isiniif dhaameeraKanaafuu ani ummata kafaran irratti akkamittiin gadda!" jedhe.');
INSERT INTO "apaguna" VALUES(7,94,'7:94',' Nuti gara magaalaa tokkoo nabiyyii kamuu erginee (yoo ummanni sun ajaja isaa fudhachuu didan), jiraattota ishee akka isaan gadi of qabaniif jecha, hiyyummaafi dhibeen isaan qabnee malee hin hafne.');
INSERT INTO "apaguna" VALUES(7,95,'7:95',' Ergasii hanga isaan baay’atanii "Dhugumatti, abbootii keenyas rakkinniifi toltuun tuqxee jirti (kun waanuma yeroon fiduu dha)" jedhanutti bakka hamtuu toltuutti jijjiirreAchumaan odoo isaan hin beekne tasa isaan qabne.');
INSERT INTO "apaguna" VALUES(7,96,'7:96',' Odoo warri magaalattii amananii (Rabbiinis) sodaatanii, silaa Dhugumatti, Nuti samiifi dachii irraa barakaawwan isaaniif banna turreGaruu isaan ni sobsiisanKanaafuu waan isaan hojjataniif jecha isaan qabne.');
INSERT INTO "apaguna" VALUES(7,97,'7:97',' Sila jiraattonni magaalattii isaanii rafaa jiranuu adabbiin keenya halkan isaanitti dhufuu (irraa) of amanuu?');
INSERT INTO "apaguna" VALUES(7,98,'7:98',' Yookiin jiraattonni magaalattii isaanii taphatanuu adabbiin keenya ganama isaanitti dhufuu of amanuu?');
INSERT INTO "apaguna" VALUES(7,99,'7:99',' Sila tooftaa Rabbii amanuu? Tooftaa Rabbii ummata hoonga’aa malee eenyullee hin amanu.');
INSERT INTO "apaguna" VALUES(7,100,'7:100',' Sila isaannan dachii (dhumiinsa) warra ishee boodaan dhaalanuuf nuti odoo feenee dilii isaaniitiin kan isaan adabnu ta’uun keenya isaaniif ifa hin taanee? Onnee isaanii irrattis cufaa goonaa isaan hin dhaga’anu.');
INSERT INTO "apaguna" VALUES(7,101,'7:101',' Magaalota kana (kan Nuuh irraa hanga ummata Shu’aybitti jiru) seenaalee isaanii irraa siif odeessinaDhugumatti, ergamoonni isaanii ragaawwan ifa ta’aniin isaanitti dhufaniiruWaan san dura kijibsiisanitti kan amanan hin taaneAkka kanatti Rabbiin onnee kaafirootaa irratti cufa.');
INSERT INTO "apaguna" VALUES(7,102,'7:102',' Irra hedduu isaaniis waadaa isaanii irra kan gadi dhaabbatan hin argine; irra hedduu isaanii finciltoota ta’uu argine.');
INSERT INTO "apaguna" VALUES(7,103,'7:103',' Ergasii isaan booda mallattoowwan keenyaan Muusaa gara Fir’awniifi qondaalota isaatti erginee jirraIsaanis fudhachuu didanBooddeen (itti galli) balleessa hojjattootaa akkam akka ta’e ilaali.');
INSERT INTO "apaguna" VALUES(7,104,'7:104',' Muusaanis ni jedhe: "Yaa Fir’awna! Dhugumatti, ani ergamaa Gooftaa aalama hundaa irraa ta’eedha.');
INSERT INTO "apaguna" VALUES(7,105,'7:105',' Rabbi irratti dhugaa malee dubbachuu dhiisuun koo dhugaadha; dhugumatti, ani ragaa ifa galaa Gooftaa keessan irraa ta’een isinitti dhufeeraKanaafuu Ilmaan Israa’iil na wajjiin gadi dhiisi."');
INSERT INTO "apaguna" VALUES(7,106,'7:106',' (Fir’awnis) "Yoo mallattoon dhufteef, yoo warra dhugaa dubbatan irraa taateef ishee fidi" jedheen.');
INSERT INTO "apaguna" VALUES(7,107,'7:107',' Ulee isaa darbate; isheen yeroma san bofa ifa galaa taate');
INSERT INTO "apaguna" VALUES(7,108,'7:108',' Harka isaas ni baase; isheenis yoosuu warra ilaalaniif adii (calaqqisaa) taate.');
INSERT INTO "apaguna" VALUES(7,109,'7:109',' Qondaalonni ummata Fir’awni irraa ta''an ni jedhan: "Inni kun Dhugumatti, saahiree (nama falfalaa) beekaadha.');
INSERT INTO "apaguna" VALUES(7,110,'7:110',' Dachii keessan irraa isin baasuu fedha; maal ajajjuree?" (jedheen).');
INSERT INTO "apaguna" VALUES(7,111,'7:111',' [Isaan] ni jedhan: "isaafi obboleessa isaa tursiisiMagaalota keessas walitti qabdoota ergi.');
INSERT INTO "apaguna" VALUES(7,112,'7:112',' Saahiree beekaa hundaan sitti dhufu."');
INSERT INTO "apaguna" VALUES(7,113,'7:113',' Saahiroonni gara Fir’awni dhufanii "yoo nuti kan injifannu taane mindaan nuuf jiraa?" jedhan.');
INSERT INTO "apaguna" VALUES(7,114,'7:114',' [Inni] "EeyyeenIsin warra (natti) dhiyeeffamu irraayi" jedheen.');
INSERT INTO "apaguna" VALUES(7,115,'7:115',' "Yaa Muusaa! Situ (ulee kee dura) darbatamoo nutu warra darbatu taana" jedhan.');
INSERT INTO "apaguna" VALUES(7,116,'7:116',' "Darbadhaa" jedheYeroma (funyoo isaanii) darbatan ija namootaa falfalanii isaan sodaachisanSihrii guddaanis dhufan.');
INSERT INTO "apaguna" VALUES(7,117,'7:117',' Gara Muusaattis "ulee kee darbi" (jechuun) beeksisa gooneAchumaan isheen waan isaan kijiban ni liqimsiti.');
INSERT INTO "apaguna" VALUES(7,118,'7:118',' Dhugaan ni mirkanaa’e; wanti isaan hojjataa turanis ni bade.');
INSERT INTO "apaguna" VALUES(7,119,'7:119',' Achitti ni injifataman; salphattoota ta’anii deebi’an.');
INSERT INTO "apaguna" VALUES(7,120,'7:120',' Saahiroonni (Rabbiif) kan sujuudan ta''anii kufan.');
INSERT INTO "apaguna" VALUES(7,121,'7:121',' Ni jedhan: "Gooftaa aalamaatti amanneerra.');
INSERT INTO "apaguna" VALUES(7,122,'7:122',' Gooftaa Muusaafi Haaruunitti."');
INSERT INTO "apaguna" VALUES(7,123,'7:123',' Fir’awnis ni jedhe: "odoo ani isiniif hin hayyamin isatti amantuu? Dhugumatti, kun tooftaa isin magaalattii keessatti jiraattota ishee isii irraa baasuuf jecha mari’attaniidhaFuulduratti ni beektu');
INSERT INTO "apaguna" VALUES(7,124,'7:124',' Dhugumatti, harka keessaniifi miila keessan wal faallessuunan kukkutaErgasii hunda keessaniiyyuu nan fannisa."');
INSERT INTO "apaguna" VALUES(7,125,'7:125',' Isaanis "Nuti Dhugumatti, gara Gooftaa keenyaa deebi’oo dha" jedhan.');
INSERT INTO "apaguna" VALUES(7,126,'7:126',' "Mallattooleen Gooftaa keenyaa yeroma nutti dhuftu itti amanuu (keenya) malee wanti nurraa haaloo bahattu hin jiruGooftaa keenya! obsa nurratti dhangalaasiMuslimoota godhii nu ajjeesi" (jedhan)');
INSERT INTO "apaguna" VALUES(7,127,'7:127',' Ummata Fir’awni irraas qondaalonni "sila ati Muusaafi ummata isaa akka dachii keessa balleessanii (Muusaanis) sihiifi gabbaramtoota keetis akka dhiisuuf (isaan) dhiiftaa?" jedhan[Inni] ni jedhe: "Nuti ilmaan dhiiraa isaanii ni ajjeefnaDubartoota isaanii immoo ni jiraachifnaDhugumatti, nuti isaan irratti moo’attoota."');
INSERT INTO "apaguna" VALUES(7,128,'7:128',' Muusaan ummata isaatiin "Rabbiin gargaarsifadhaa; obsaasDhugumatti, dachiin kan RabbiitiGabroota Isaa irraa nama fedhe ishee dhaalchisaBooddeenis kan warra Rabbiin sodaataniiti" jedhe.');
INSERT INTO "apaguna" VALUES(7,129,'7:129',' "Nuti odoo ati nutti hin dhufin duraafi erga nutti dhuftee boodas rakkifamnee jirra" jedhan[Inni] "Gooftaan keessan diinota keessan balleessee dachii keessatti bakka isin buusee ergasii akkam akka dalagdan isin ilaaluutu mala" jedhe.');
INSERT INTO "apaguna" VALUES(7,130,'7:130',' Dhugumatti, Nuti ummata Fir’awni akka isaan gorfamaniif jecha gogiinsa waggootaaf tureefi hir’ina oomishaatiin isaan qabne.');
INSERT INTO "apaguna" VALUES(7,131,'7:131',' Yeroo toltuun wahii isaanitti dhufte "kun nuuf malti" jedhuYoo hamtuun isaan tuqxe immoo milkii hamaa Muusaafi namoota isa waliinii irratti ilaallatuDhagayaa! Dhugumatti, gaddiin hamaan isaanii Rabbiin bira jiraGaruu irra hedduun isaanii hin beekan.');
INSERT INTO "apaguna" VALUES(7,132,'7:132',' Isaanis (Muusaan) ni jedhan: "Yoo ati raajii irraa waan feeteen isheedhaan sihrii nutti godhuuf nutti dhufte, nuti siif kan amannu hin taane."');
INSERT INTO "apaguna" VALUES(7,133,'7:133',' Achumaan nuti lolaa dachii walga’e, awaannisa, injiraan, raachaafi dhiiga mallattoowwan addeeffamaa haala ta''aniin isaan irratti erginee jirraIsaan garuu ni boonan(Isaaniis) ummata yakkamtoota turan.');
INSERT INTO "apaguna" VALUES(7,134,'7:134',' Yeroo adabichi isaan irratti kufus ni jedhan: "yaa Muusaa! waan Inni si biraa waadaa qabuun Gooftaa kee nuuf kadhu; yoo ati adabicha nurraa deebifte, Dhugumatti, nuti siif amanna; Ilmaan Israa’ilis si waliin ergina."');
INSERT INTO "apaguna" VALUES(7,135,'7:135',' Hanga beellama isaan dhaqqabanuutti yeroma nuti isaan irraa adabicha deebifnu achumaan isaan waadaa isaanii diigu.');
INSERT INTO "apaguna" VALUES(7,136,'7:136',' Sababa isaan keeyyattoota keenya sobsiisanii ishee irraayis kan dagatan ta’aniif galaana keessatti isaan dhidhimsinee haaloo isaan irraa ba’anne.');
INSERT INTO "apaguna" VALUES(7,137,'7:137',' Nuti namoota hacuucamaa turan Bahaafi Dhiha dachii ishee barakaa keessa goonee dhaalchifneSababa isaan obsaniif jechi Rabbii keetii gaariin Banii Israa’iil irratti guutamteWaan Fir’awniifi ummanni isaa dalagaa turaniifi waan isaan ijaaraa turanis ni barbadeessine.');
INSERT INTO "apaguna" VALUES(7,138,'7:138',' Ilmaan Israa’iilis galaana ceesifneetiin ummata taabota isaanii (gabbaruu) irra taa’aa jirutti dhufanii "yaa Muusaa akkuma isaan waan gabbaramu qaban nuufis waan gabbaramu godhi" jedhan[Inni] ni jedhe: "Isin ummata wallaaloodha.');
INSERT INTO "apaguna" VALUES(7,139,'7:139',' Isaan kun wanti isaan keessa jiran mancaafamaadhaWanti isaan dalagaa turanis kufaadha."');
INSERT INTO "apaguna" VALUES(7,140,'7:140',' "Sila odoo Inni aalama hunda irra isin caalchisee jiruun ani Rabbiin ala gabbaramaa biraa isiniif barbaadaa?" jedhe.');
INSERT INTO "apaguna" VALUES(7,141,'7:141',' Yeroo Nuti namoota Fir’awni kan hamtuu adabaa isin dhandhamsiisaniifi ilmaan keessan ajjeesanii dubartoota keessan jiraachisan irraa nagaa isin baafnes [yaadadhaa]Kana keessas balaa guddaa Gooftaa keessan irraa ta’etu jira.');
INSERT INTO "apaguna" VALUES(7,142,'7:142',' Muusaafis halkan soddoma waadaa seenneKudhaniinis ishee guunneBeellamni Gooftaa isaas halkan afurtama guuteMuusaanis obboleessa isaa Haaruunin ni jedhe: "Namoota kiyya keessatti bakka na bu’iGaaromsisKaraa warra balleessaa hojjatanii hin hordofin."');
INSERT INTO "apaguna" VALUES(7,143,'7:143',' Yeroo Muusaan beellama keenyaaf dhufee Rabbiin isaa isaan dubbatu "Yaa gooftaa kiyya (of) na argisiisi sin ilaalaa!" jedhe(Rabbiinis) "Nan argitu; garuu gara gaaraa ilaali; yoo bakka isaatti ragga''eef Na argitaa" jedheYeroo Rabbiin isaa gaaraaf mul’atu daakamaa lafaan qixxaa''aa isa godheMuusaanis of wallaalee kufeYeroma of barus "ati qulqulloofte; ani gara keetti deebi’e anis jalqaba mu’uminootaati" jedhe.');
INSERT INTO "apaguna" VALUES(7,144,'7:144',' (Rabbiin) ni jedhe: "Yaa Muusaa! Ani ergaa kiyyaafi haasa’a kiyyaan namoota irra si filadheeraWaan Ani siif kenne qabadhuGalateeffattoota irraas ta’i."');
INSERT INTO "apaguna" VALUES(7,145,'7:145',' Loohiwwan keessattis waan hunda irraa gorsaafi ibsa waan hundaa isaaf barreessine"Ishee ciminaan qabadhuNamoota keetis akka toltuu ishee qabatan ajajiAni biyya finciltootaa fuuldurattin isinitti agarsiisa" (jedheen).');
INSERT INTO "apaguna" VALUES(7,146,'7:146',' Ani warra haqaan ala dachii keessatti boonan keeyyattoota kiyya irraa garan galchaYoo mallattoolee hunda arganis isatti hin amananuYoo karaa qajeelaa argan isa karaa hin godhatanuYoo karaa jallinaa argan isa karaa godhatuKun waan isaan keeyyattoota keenya kijibsiisaniifi ishee irraas dagatoo turaniifi.');
INSERT INTO "apaguna" VALUES(7,147,'7:147',' Warri keeyyattoota keenyaafi qunnamtii Aakhiraa kijibsiisan, dalagaaleen isaanii baddeSila waan hojjataa turan malee mindeeffamuu?');
INSERT INTO "apaguna" VALUES(7,148,'7:148',' Ummanni Muusaa (deemsa) isaa booda faaya isaanii irraa jibicha qaama sagalee (akka koqiinsaa) qabu tolfatanSila akka inni isaanitti hin dubbanneefi gara karaa (sirriis) isaan hin qajeelchine hin arganuu? (Gabbaramaa) isa godhatan; miidhaa hojjattootas ta’an.');
INSERT INTO "apaguna" VALUES(7,149,'7:149',' Yeroo gaabbanii jallachuu isaaniis argan, (tawbataniitin) jedhan: "odoo Gooftaan keenya rahmata nuuf godhuu baateefi nuuf hin araaramnee, dhugumatti silaa nuti hoonga’oo irraa taana ture."');
INSERT INTO "apaguna" VALUES(7,150,'7:150',' Muusaan yeroma dallanaa gaddaa gara ummata isaa deebi’u "wanti isin ana booda na bakka buuftan fokkate; sila ajaja Gooftaa keessanii ariifachiifattani moo?" jedheLoohiiwwanis darbee mataa obboleessa isaa kan harkisu ta''ee ofitti qabe(Obboleessis) "Yaa ilma haadha kiyyaa! Ummanni na hacuucanii anas ajjeesuuf dhihaataniKanaafuu diina natti hin gammachiisinUmmata miidhaa raawwattoota wajjinis na hin godhin" jedhe.');
INSERT INTO "apaguna" VALUES(7,151,'7:151',' (Muusaanis) "Gooftaa kiyya! Anaafi obboleessa kiyyaaf araaramiRahmata kee keessas nu seensisiAti akkaan rahmata godhaa warra rahmata godhaniiti" jedhe.');
INSERT INTO "apaguna" VALUES(7,152,'7:152',' Dhugumatti, isaannan mirgoo (gabbaramaa) godhatan, Gooftaa isaanii irraa dallansuufi salphinni jireenya addunyaa kanaa keessatti isaan argataAkka kanatti kijiba uumtota mindeessina.');
INSERT INTO "apaguna" VALUES(7,153,'7:153',' Isaannan dilii heddu hojjatanii sana booda tawbataniifi amanan, Dhugumatti, Gooftaan kee sana booda araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(7,154,'7:154',' Yeroma dallansuun Muusaa irraa qabbanaa’u loohiwwan fuudheBarreefama ishee keessas qajeelfamaafi rahmatatu isaan Gooftaa isaanii sodaataniif jira.');
INSERT INTO "apaguna" VALUES(7,155,'7:155',' Muusaanis ummata isaa irraa namoota torbaatama beellama keenyaaf filateYeroo sochiin (kirkirri) lafaa isaan qabattu, (Muusaan) ni jedhe: "Gooftaa kiyya! Odoo feetee kana dura isaaniifi anas ni balleessita tureSila waan gowwoonni nurraa ta’an dalaganiin nu balleessitaa? Isheen (fitnattiin) mokkoroodhuma keeti malee homaa mitiIsheedhaan nama feete jalliftee nama feete immoo qajeelchitaAti gargaaraa keenyaNuufis araaramiRahmatas nuuf godhiAti caalaa warra araaramaniiti.');
INSERT INTO "apaguna" VALUES(7,156,'7:156',' Jireenya addunyaa kana keessattiifi kan Aakhiraa keessattis toltuu nuuf katabiNuti gara kee deebineerra." (Rabbiin) ni jedhe: "(kun) adabbii kiyya; naman fedhen isaan tuqaRahmanni kiyyas waan hundaaf ballatteIshee fuulduratti warra Rabbiin sodaatan, zakaa kennaniifi warra keeyyattoota keenyatti amananiifin barreessa."');
INSERT INTO "apaguna" VALUES(7,157,'7:157','(Isaan) warra ergamaa, nabiyyii hin barreessineefi hin dubbifne, isa isaan biratti Tawraatiifi Injiiliin keessatti barreeffamee isa argan, kan toltutti isaan ajajuufi hamtuu irraa isaan dhorgu, kan gaggaarii halaala isaaniif taasisu, fokkataa immoo haraama isaan irratti taasisu, kan ba’aa isaaniitiifi hidhaa isaan irra ture isaan irraa lafa kaayu hordofaniidhaIsaannan isatti amananii isa kabajan, kan isa gargaaraniifi ifa isa waliin bu’e hordofan, isaan isaanumatu warra milkaa’oodha.');
INSERT INTO "apaguna" VALUES(7,158,'7:158',' Jedhi: "Yaa namootaa! Dhugumatti, ani ergamaa Rabbii Isa mootummaan samiifi dachii kan Isaa ta’e irraa gara hunda keessaniitti (ergamee dha)Isa malee dhugaan gabbaramaan hin jiruNi jiraachisa; ni ajjeesasRabbiifi ergamaa Isaa, kan nabiyyii hin dubbifneefi hin barreessine ta’ee Rabbiifi jechoota Isaatti amanutti, amanaaAkka qajeeltaniif jecha isa hordofaa."');
INSERT INTO "apaguna" VALUES(7,159,'7:159',' Ummata Muusaa irraas ummata dhugaan qajeelaniifi ittiin haqa murteessantu jira.');
INSERT INTO "apaguna" VALUES(7,160,'7:160',' Ummata gosa kudha lama goonee isaan qoqqoodneGara Muusattis, yeroo namoonni isaa dhugaatii isa gaafatan "Ulee keetiin dhagaa rukuti" jechuu beeksifneIsa irraas burqituu kudha lamatu madditeNamoonni hunduu iddoo irraa dhugan ni beekanDuumessa isaan irratti gaaddiseessine''Mannii''fi ''salwiis'' isaan irratti buufne: "Gaggaarii waan nuti isiniif kenninee irraa nyaadhaa" (jenneen)Isaan Nu hin miine; garuu isaan lubbuu isaanii miidhaa turan.');
INSERT INTO "apaguna" VALUES(7,161,'7:161',' Yeroo "magaalattii kana keessa jiraadhaa; ishee irraa bakka feetanii nyaadhaa''(kan nu feenu Badiin nurraa) harca''uudhas'' jedhaaBalbalas sujuuda godhaa seenaaBadiiwwan keessan isiniif dhiifna(Mindaa) warra toltuu hojjataniis ni daballa" jedhames [yaadadhaa].');
INSERT INTO "apaguna" VALUES(7,162,'7:162',' Isaan irraa warri miidhaa hojjatan jecha isaanin jedhameen ala isa biraa jijjiiranWaan miidhaa hojjataa turaniif jecha adabbii samii irraa isaan irratti ergine.');
INSERT INTO "apaguna" VALUES(7,163,'7:163',' Waa’ee magaalattii galaanicha cinaa jirtuu yeroo isaan sanbata keessatti daangaa dabran, yeroo qurxummiin isaanii guyyaa sanbataa mul’attuu taatee isaanitti dhuftu irraa isaan gaafadhu; guyyaa isaan sanbata hin guddifne (guyyaa biroo) isaanitti hin dhuftuWaan isaan fincilaa turaniif akka kanatti isaan qormaanna.');
INSERT INTO "apaguna" VALUES(7,164,'7:164',' Yeroo ummanni isaan irraa ta’e "Namoota Rabbiin isaan balleessuuf yookiin adabbii cimaa isaan adabuuf jiru maaliif gorsitu?" jedhan yaadadhu(Gorsitoonnis) "Gooftaa keessan biratti sababa nuuf haa ta''uufi akka isaanis Rabbiin sodaataniifi" jedhan.');
INSERT INTO "apaguna" VALUES(7,165,'7:165',' Yeroma isaan waan ittiin gorfaman dagatan, isaan hamtuu irraa (namoota) dhorgan nagaa baafnee isaan miidhaa hojjatan immoo waan isaan fincilaa turaniif jecha adabbii hamaan isaan qabne.');
INSERT INTO "apaguna" VALUES(7,166,'7:166',' Yeroma isaan daangaa wantoota irraa dhorgamanii darban "jaldeessa salphattoota ta’aa" isaanin jenne.');
INSERT INTO "apaguna" VALUES(7,167,'7:167',' Yeroo Gooftaan kee hanga Guyyaa Qiyaamatti isaan irratti nama adabbii laalessaa isaan dhandhamsiisu akka isaan irratti ergu beeksise (yaadadhu.) Dhugumatti, Gooftaan kee adabbiin isaa arifataadhaInnis akkaan araaramaa, akkaan rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(7,168,'7:168',' Nuti (Ilmaan Isaraa’iil) dachii keessatti ummatoota adda addaa taasifnee isaan kukkunnee jirraIsaan irraas gaggaariitu jiraIsaan irraas sanaan alatu jiraAkka isaan deebi’aniif jecha toltuuwwaniifi hamtuuwwaniin isaan qormaannee jirra.');
INSERT INTO "apaguna" VALUES(7,169,'7:169',' Booda isaaniitiis bakka bu’oonni badaan kitaaba dhaalan bakka bu’anMeeshaa dhihoo (hoosa) kana fudhataa "fuulduratti nuuf dhiifama" jedhuYoo meeshaan san fakkaatu isaanitti dhufes ni fudhatuSila waadaan kitaabaa kan akka isaan Rabbiin irratti dhugaa malee hin dubbanne isaan irraa hin fuudhamnee? Waan isa keessa jirus hin dubbifnee? Manni Aakhiraas isaan Rabbiin sodaataniif caalaadhaSila isin hin xiinxaltanuu?');
INSERT INTO "apaguna" VALUES(7,170,'7:170',' Isaannan kitaabicha qabatanii, salaatas sirnaan dhaaban, Dhugumatti, Nuti mindaa warra toltuu hojjatanuu hin balleessinu.');
INSERT INTO "apaguna" VALUES(7,171,'7:171',' Yeroo Nuti gaara xuur gubbaa isaanii irratti akka gaaddisaa ta’ee ol fuunee, isaanis nurratti kufa jechuu dhugoomsanii "Waan nuti isiniif kennine ciminaan qabadhaaAkka (Rabbiin) sodaattaniif jecha waan isa keessa jiru qu’adhaa" (jenne yaadadhu)');
INSERT INTO "apaguna" VALUES(7,172,'7:172',' Yeroo Gooftaan kee ilmaan Aadam irraa, dugda isaanii irraa sanyii isaanii baaseefi "Ani Gooftaa keessanii mitii?" jechuun lubbuu isaanii irratti ragaa isaan godhe [yaadadhu]Isaanis "Eeyyeen; ragaa baanee jirra" jedhanKunis akka isin Guyyaa Qiyaamaa "Dhugumatti, nuti kana irraa daguu keessa turre" hin jenneefi.');
INSERT INTO "apaguna" VALUES(7,173,'7:173',' “Yookiin abbootii keenyatu duraan (Rabbitti) qindeessani; nutis sanyii isaaniin boodaa turreSila waan balleessitoonni hojjataniin nu balleessitaa?” akka isin hin jenneef (ragaa isin goone).');
INSERT INTO "apaguna" VALUES(7,174,'7:174',' Akka isaan (yaadanii)fi deebi’aniif jecha akka kanatti keeyyattoota keenya addeessina.');
INSERT INTO "apaguna" VALUES(7,175,'7:175',' Oduu isa keeyyattoota keenya isaaf kenninaan ishee irraa bahee, sheyxaana isa hordofsiiseefi jallattoota irraa ta’ee isaan irratti dubbisi.');
INSERT INTO "apaguna" VALUES(7,176,'7:176',' Odoo feenee, isheedhaan (keeyyattoota keenyaan) ol isa fuuna tureGaruu inni gara dachiitti dabe; fedhii lubbuu isaas ni hordofeFakkeenyi isaa akka fakkii saree yoo ariite arraba baasuu, yoo dhiiftes arraba baasuutiKuni fakkeenya ummata keeyyattoota keenya kijibsiisaniitiAkka isaan xiinxalaniif jecha seenaalee odeessi.');
INSERT INTO "apaguna" VALUES(7,177,'7:177',' Fakkeenyi ummata keeyyattoota keenya kijibsiisaniifi Lubbuu isaanii kan miidhan ta’anii waa fokkate!');
INSERT INTO "apaguna" VALUES(7,178,'7:178',' Namni Rabbiin qajeelche, isatu qajeelaadhaNamni Inni jallise immoo, isaan warra hoonga’oodha.');
INSERT INTO "apaguna" VALUES(7,179,'7:179',' Dhugumatti, Nuti jahannamiif jinnootaafi namoota irraa heddu uumnee jirraIsaan onnee ittiin hin hubanne qabuIsaan ijas kan ittiin hin argine qabuAmmas isaan gurra ittiin hin dhageenye qabuWarri sun akka beyladaatiInumaa jarri irra jallatoodhaIsaan isaanumatu dagattoota.');
INSERT INTO "apaguna" VALUES(7,180,'7:180',' Rabbiif maqaalee gaariitu jiruIsheedhaan Isa kadhaaWarra maqaalee Isaa jallisanis dhiisaaWaan dalagaa turan fuulduratti mindeffamu.');
INSERT INTO "apaguna" VALUES(7,181,'7:181',' Namoota Nuti uumne irraa, ummata dhugaan qajeelaniifi isaanis haqa murteessantu jiru.');
INSERT INTO "apaguna" VALUES(7,182,'7:182',' Isaannan keeyyattoota keenya kijibsiisan, Nuti karaa isaan hin beekne irraan sadarkaa sadarkaadhaan isaan qabna.');
INSERT INTO "apaguna" VALUES(7,183,'7:183',' Yeroos isaaniifin kenna; Dhugumatti, tooftaan kiyya cimaadha.');
INSERT INTO "apaguna" VALUES(7,184,'7:184',' Sila isaan Nabiyyii isaaniitti maraa-tummaan dhibamuu hin xiinxalanuu? Inni akeekkachiisaa ifa galaa malee homaa miti.');
INSERT INTO "apaguna" VALUES(7,185,'7:185',' Sila mootummaa samii, dachiifi waan Rabbiin wantoota hunda irraa uume, akkasumas beellamni (yeroon du’a) isaanii Dhugumatti kan dhihaate ta’uu hin ilaalanuu? Sila booda isaatii (Qur’aanaatii) haasa’a kamitti amanu?');
INSERT INTO "apaguna" VALUES(7,186,'7:186',' Nama Rabbiin jallise, qajeelchaan isaaf hin jiruJallina isaanii keessattis dhama''aa ta''anii isaan dhiisa.');
INSERT INTO "apaguna" VALUES(7,187,'7:187',' Waa’ee Qiyaamaa irraa yoom akka dhaabbattu (dhuftu) si gaafatu"Beekumsi ishee Gooftaa kiyya bira qofaIsa malee homtuu yeroo isheetiif ishee hin ibsuSamiifi dachii keessatti ulfaatteIsheen tasa (daguutti) malee isinitti hin dhuftu" jedhiAkka waan ati ishee sirritti beektuutti si gaafatu"Beekumsi ishee Rabbiin bira qofa jiraGaruu irra hedduun namootaa hin beekan" jedhi.');
INSERT INTO "apaguna" VALUES(7,188,'7:188',' "Ani waan Rabbiin fedhe malee lubbuu kiyyaaf faayidaas ta’ee miidhaa fiduu hin danda''uOdoon kanin fagoo (ghaybii) beeku ta''ee, silaa toltuu irraan baay''ifadha; hamtuunis na hin tuqu tureAni namoota amananiif akeekkachiisaafi gammachiisaa malee homaa miti" jedhi.');
INSERT INTO "apaguna" VALUES(7,189,'7:189',' Inni Isa lubbuu tokko irraa isin uumee isa irraas akka isheetti booharuuf (mukuu bahuuf) niitii isaaf taasiseedhaYeroo inni ishee qunnamus ulfa salphaa ulfoofte, (salphina isaa irraa bakka feete) ittiin deemteYeroma isheen ulfaattu "yoo Ati ilma gaarii nuuf kennite, dhugumatti, nuti warra Si galateeffatan irraa taana" jechuun Rabbiin Gooftaa isaanii kadhatan.');
INSERT INTO "apaguna" VALUES(7,190,'7:190',' Yeroo Inni ilma gaarii isaaniif kennu waan Inni isaaniif kenne (maqaa) keessatti Isaaf shariika taasisanRabbiin waan isaan Isa waliin gabbaran irraa ol ta’eera.');
INSERT INTO "apaguna" VALUES(7,191,'7:191',' Sila warra ofiifuu uumamoo ta’anii, waa tokko uumuu hin dandeenye Rabbitti qindeessuu?');
INSERT INTO "apaguna" VALUES(7,192,'7:192',' Isaan gargaaruus ta’ee lubbuu isaanii gargaaruu hin danda’an.');
INSERT INTO "apaguna" VALUES(7,193,'7:193',' Yoo isin gara qajeelumaa isaan waamtanis isin hin hordofanIsaan waamtanus yookiin cal’iftoota taatanus isin irratti walqixa.');
INSERT INTO "apaguna" VALUES(7,194,'7:194',' Dhugumatti, isaan isin Rabbii gaditti gabbartan gabrootuma fakkii keessaniitiYoo kan dhugaa dubbattan taataniif mee isaan kadhaa isiniif haa awwaatanuu.');
INSERT INTO "apaguna" VALUES(7,195,'7:195',' Sila isaan miila ittiin deeman qabuu? Yookiin harka ittiin qabatan qabuu? Yookiin ija ittiin argan qabuu? Yookiin gurra ittiin dhagayan qabuu? "Mee warra Rabbii waliin gabbartan waamadhaaErgasii tooftaa kessan natti baasaaYeroos naaf hin kenninaa (isaan hin sodaadhu).');
INSERT INTO "apaguna" VALUES(7,196,'7:196',' Dhugumatti, jaalalleen kiyya Rabbii Isa kitaaba buuseedhaInni warra toltuu hojjatan ni gargaara" (jedhiin).');
INSERT INTO "apaguna" VALUES(7,197,'7:197',' Warri isin Isaa gaditti gabbartan isin gargaaruus ta’ee lubbuu isaanii gargaaruu hin danda’anu.');
INSERT INTO "apaguna" VALUES(7,198,'7:198',' Yoo gara qajeelumaa isaan waamtanis hin dhaga''anuKan isaan homaa hin argine ta''anii gara kee ilaalanu argita.');
INSERT INTO "apaguna" VALUES(7,199,'7:199',' Haala (namootaa irraa isa) laafaa qabadhu; toltuuttis ajaji; wallaalaa irraahis gara gali.');
INSERT INTO "apaguna" VALUES(7,200,'7:200',' Yoo garagalchaan shayxaana irraa ta’e tokko (ajaja Rabbii irraa) si bute Rabbitti maganfadhuDhugumatti, Inni dhagayaa, argaadha.');
INSERT INTO "apaguna" VALUES(7,201,'7:201',' Dhugumatti, isaan Rabbiin sodaatan yeroo naanna''aan shayxaana irraa ta’e tokko isaan tuqe, (Rabbiin) yaadatuAchumaan argaa ta''u.');
INSERT INTO "apaguna" VALUES(7,202,'7:202',' Obboleeyyan isaaniis jallina isaaniif dabaluErgasiis (jallisuu irraa) hin gabaabbatanu.');
INSERT INTO "apaguna" VALUES(7,203,'7:203',' Yeroo ati raajii wahiitiin isaanitti hin dhufin "Maaliif ishee hin finne (hin uumne)?" jedhu"Wanti ani hordofu waan Gooftaa kiyya biraa gara kiyyatti beeksifame qofa(Qur’aanni) kun ragaa ifa bahaa Gooftaa keessan irraa ta''e kan ummata amananiif qajeelfamaafi rahmata ta’ee dha" jedhi.');
INSERT INTO "apaguna" VALUES(7,204,'7:204',' Yeroo Qur’aanni qara’amus akka rahmanni isiniif godhamuuf gara isaa dhageeffadhaa; cal’isaas.');
INSERT INTO "apaguna" VALUES(7,205,'7:205',' Lubbuu kee keessatti gadi of qabaafi sodaataa, sagalee ol hin fuudhaminiin ganamaafi galgala Gooftaa kee faarsiDagattoota irraas hin ta’in.');
INSERT INTO "apaguna" VALUES(7,206,'7:206',' Dhugumatti, isaannan Gooftaa kee bira jiran Isa gabbaruu irraa hin boonanu; Isa qulqulleessu; Isaafis sujuuda godhu.');
INSERT INTO "apaguna" VALUES(8,1,'8:1','Boojii irraa si gaafatu“Boojiin kan Rabbiifi ergamaa IsaatiKanaafuu Rabbiin sodaadhaaHariiroo gidduu keessan jirus tolchaaYoo mu’uminoota taatan Rabbiifi ergamaa Isaatiif ajajamaa” jedhi');
INSERT INTO "apaguna" VALUES(8,2,'8:2','Mu’uminoonni (dhugaa) warra yeroo maqaan Rabbii dubbatamu onneen isaanii rifattu, kan yeroo keeyyatoonni Isaa isaan irratti dubbifamtu Iimaana isaaniif dabaltuufi Gooftaa isaanii irratti irkataniidha.');
INSERT INTO "apaguna" VALUES(8,3,'8:3','Isaannan salaata seera ishee guutanii dhaabaniifi waan isaaniif kennine irraas kennitaniidha.');
INSERT INTO "apaguna" VALUES(8,4,'8:4','Isaannan kun isaan qofatu dhugumatti mu’uminootaGooftaa isaanii biratti sadarkaa hedduu, araaramaafi soorata gaarii ta’etu (jannatatu) isaaniif jiraaf');
INSERT INTO "apaguna" VALUES(8,5,'8:5',')Qoodiinsa boojii kana gariin namaa jibbuun) akkuma gariin mu’uminootaa kan jibban ta’anii dhugaa irratti taatee Rabbiin kee mana kee irraa si baaseeti.');
INSERT INTO "apaguna" VALUES(8,6,'8:6','Dhugaa irratti, erga inni ifa ta’ee booda, akka waan odoo argan gara du’aa oofamaaraniitti siin falmu.');
INSERT INTO "apaguna" VALUES(8,7,'8:7','Yeroo tokkoon gareewwan lamaanii isiniif ta’uu Rabbiin waadaa isiniif galee, isinis isheen abbaa lolaa hin ta’Islaamni (gareen daldaalaa) isiniif ta’uu jaallattan, Rabbiin immoo jechoota (waadaa) Isaatiin dhugaa mirkaneessuufi hidda kaafirootaa kutuu yeroo fedhe san (waan ta’e yaadadhu).');
INSERT INTO "apaguna" VALUES(8,8,'8:8','(Wanti kana hojjateef) odoo kaafiroonni jibban akka dhugaa mirkaneessee soba barbadeessuufi.');
INSERT INTO "apaguna" VALUES(8,9,'8:9','Yeroo isin Rabbii keessan irraa birmannaa barbaaddanii “Ani maleykoota kuma tokko wal hordofoo ta’an isiniif gargaarsa godhaadha” jechuun isiniif owwaate [yaadadhu].');
INSERT INTO "apaguna" VALUES(8,10,'8:10','Rabbiin gammachuu (keessanii)fi akka onneen keessan ittiin tasgabbooftuuf malee waan biraatiif isa hin gooneInjifannoon Rabbiin biraa malee hin jiruDhugumatti Rabbiin injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(8,11,'8:11','Yeroo Inni tasgabbii Isa biraa ta’ee (adda waraanaatti) hirribaan isin haguugeefi ittiin isin qulqulleessuf, waswaasa sheyxaanaas isin irraa deemsisuuf, onnee keessanis (obsaan) jabeessuuf, faanawwan (keessanis cirracha irratti) ittiin gadi dhaabuuf jecha samii irraa yeroo bishaan isin irratti buuse [yaadadhaa].');
INSERT INTO "apaguna" VALUES(8,12,'8:12','Yeroo Gooftaan kee gara maleykotaatti “dhugumatti, Ani (gargaarsaan) isin waliin jiraa, isaan amanan (miila isaanii) gadi dhaabaaAni onnee isaan kafaranii keessa sodaan buusa; Morma (isaanii) gubbaa rukutaaIsaan irraas quba hunda rukutaa” (jedhee) beeksisa godhe [yaadadhu].');
INSERT INTO "apaguna" VALUES(8,13,'8:13','Kun waan isaan Rabbiifi ergamaa Isaa faallessaniifiNamni Rabbiifi ergamaa Isaa faallesse, (Rabbiin isa adaba)Dhugumatti Rabbiin adabbiin isaa cimaadha.');
INSERT INTO "apaguna" VALUES(8,14,'8:14','Kun (adaba keessanii), isa dhandhamaaDhugumatti kaafirootaaf adabbii ibiddaatu jira.');
INSERT INTO "apaguna" VALUES(8,15,'8:15','Yaa warra amantan! (waraanaaf) kan walitti qabaman ta’anii yeroo warra kafaran qunnamtan, duuba keessan isaanitti gara hin galchinaa (hin baqatinaa).');
INSERT INTO "apaguna" VALUES(8,16,'8:16','Namni guyyaa san duuba isaa isaaniitti garagalche, yoo mijjeeffannaa lolaatiif yookiin gara hoomaa (isaa) siquuf ta’e malee, inni dhugumatti dallansuu Rabbi irraa ta’een deebi’ee jiraTeessoon isaas jahannamiDeebiin (isaanii jahannam) waa fokkate.');
INSERT INTO "apaguna" VALUES(8,17,'8:17','Isin isaan hin ajjeefneGaruu Rabbitu isaan ajjeeseYeroo darbattus ati hin darbanneGaruu Rabbiitu darbateMu’uminootaafis tola gaarii isa biraa ta’e akka kennuuf (kana hojjate)Dhugumatti, Rabbiin dhagahaa, beekaadha.');
INSERT INTO "apaguna" VALUES(8,18,'8:18','Kun (dhugaadha)Dhugumatti, Rabbiinis dadhabsiisaa tooftaa kaafirootati.');
INSERT INTO "apaguna" VALUES(8,19,'8:19',')Yaa warra kafartan!) Yoo murtii barbaaddan, dhugumatti murtiin isiniif dhufee jiraYoo (badii irraa) dhorgamtan inni isiniif caalaadhaAmmas yoo isin (waraanatti) deebitan, nutis (tumsa keenyaan) ni deebinaTuunni keessan odoo baay’atanillee homaa isin hin fayyadanuDhugumatti Rabbiin (gargaarsaan) mu’uminoota waliin jira.');
INSERT INTO "apaguna" VALUES(8,20,'8:20','Yaa warra amantan! Rabbiifi ergamaa Isaatiif ajajajamaaOdoo dhageessanuu Isa irraas gara hin galinaa.');
INSERT INTO "apaguna" VALUES(8,21,'8:21','Akka isaan haala hin dhageenyeen “dhageenyee jirra” jedhanii hin ta’inaa.');
INSERT INTO "apaguna" VALUES(8,22,'8:22','Dhugumatti, irra badaan lubbu qabeeyyii Rabbiin biratti duudaa, arraba qabamaa, isaan hubannoo hin qabneedha.');
INSERT INTO "apaguna" VALUES(8,23,'8:23','Rabbiin odoo isaan keessatti toltuu beekee, silaa isaan dhageessisa tureOdoo isaan dhageessisees, silaa isaan kan garagalan ta’anii irraa baqatu.');
INSERT INTO "apaguna" VALUES(8,24,'8:24','Yaa warra amantan! Rabbiif awwaadhaaErgamaa Isaatiifis; yeroo inni waan isin jiraachisutti isin waame (awwaadhaa)Dhugumatti Rabbiin gidduu namichaatiifi onnee isaa akka golguufi isinis gara Isaatti kan walitti qabamtan ta’uu beekaa.');
INSERT INTO "apaguna" VALUES(8,25,'8:25','Rakkoo (Fitnaa) isin irraa warra miidhaa hojjatan qofa kophaatti hin mudannes sodaadhaaDhugumatti, Rabbiin adabbiin isaa cimaa akka ta’es beekaa');
INSERT INTO "apaguna" VALUES(8,26,'8:26','Yeroo isin muraasa, dachii keessattis hacuucamoo, namoonnis (saamichaafi ajjeechaan) isin bubbutuu sodaachaa, (Rabbiin) isin qubsiisee, tumsa Isaatiinis isin gargaaree, akka isin galateeffattaniif wantoota gaggaarii irraas isin soore yaadadhaa.');
INSERT INTO "apaguna" VALUES(8,27,'8:27','Yaa warra amantan! Rabbiifi ergamaa Isaa hin ganinaaOdoo beektanii amaanaa keessanis hin ganinaa.');
INSERT INTO "apaguna" VALUES(8,28,'8:28','Dhugumatti, qabeenyi keessaniifi ilmaan keessanis mokkoroo (fitnaa) akka ta’an beekaaDhugumatti Rabbiin mindaan guddaan Isa bira jira');
INSERT INTO "apaguna" VALUES(8,29,'8:29','Yaa warra amantan! Yoo Rabbiin sodaattaniif, addaan baasaa (dhugaafi dharaa) isiniif godhaBadii keessanis isin irraa harcaasaIsiniif araaramasRabbiin abbaa tola guddaati.');
INSERT INTO "apaguna" VALUES(8,30,'8:30',' Yeroo isaan kafaran si hidhuuf yookiin si ajjeesuf yookiin (biyya irraa) si baasuuf tooftaa sirratti baasan [yaadadhu]Isaan tooftaa baasu; Rabbiinis tooftaa baasa (tooftaa isaanii isaan irratti deebisa)Rabbiin irra caalaa warra tooftaa baasuuti.');
INSERT INTO "apaguna" VALUES(8,31,'8:31',' Yeroo keeyyatoonni keenya isaan irratti qara’amtu “Nuti (ishee) dhageenyee jirra, odoo feenee fakkaataa kanaa ni jenna; kuni ogbarruu warra duriiti malee waan biraatii miti” jedhu.');
INSERT INTO "apaguna" VALUES(8,32,'8:32',' Yeroo isaan “Yaa Allaah! Yoo kuni dhugaa si biraa (bu’e) ta’e, samii irraa dhagaa nurratti roobsi; yookiin adabbii laalessaa nutti fidi” jedhan [yaadadhu].');
INSERT INTO "apaguna" VALUES(8,33,'8:33',' Rabbiinis odoo ati isaan keessa jirtuu kan isaan adabu hin taaneAmmas Rabbiin odoo isaan araarama kadhatanuu kan isaan adabu hin taane.');
INSERT INTO "apaguna" VALUES(8,34,'8:34',' Haala isaan masjiida kabajamaa irraa (namoota) kan dhorganiifi itti gaafatamaa isaas hin ta’aniin, Rabbiin maaliif isaan hin adabne? Warra Rabbiin sodaatan malee itti gaafatamaan isaa kan biraa hin taane; garuu irra hedduun isaanii hin beekanu.');
INSERT INTO "apaguna" VALUES(8,35,'8:35',' Salaanni isaanii mana (Ka’abaa) biratti fiiysuu (siiksuu)fi harka dhahuu (shubbisuu) malee waan biraa hin taaneSababa kufrii keessaniif adabbii dhandhamaa.');
INSERT INTO "apaguna" VALUES(8,36,'8:36',' Dhugumatti, isaan kafaran (namoota) karaa Rabbii irraa dhorguuf jecha qabeenya isaanii kennuFuula duras ishee kennuSana booda isaan irratti gaabbii taatiErgasiis ni injifatamu; isaan kafaran gara jahannamitti walitti qabamu.');
INSERT INTO "apaguna" VALUES(8,37,'8:37',' Rabbiin fokkataa gaarii irraa adda baasee, fokkataas garii isaa garii gubbaatti taasisee, hunda isaanii walitti tuulee, ergasii jahannam keessatti isaan taasisuuf (walitti qabamu)Warri sun isaanumatu hoonga’oodha.');
INSERT INTO "apaguna" VALUES(8,38,'8:38',' Isaan kafaraniin, yoo (kufrii irraa) dhorgamtan wanti dabre isaaniif araaramamaYoo (itti) deebi’an immoo dhugumatti, karaan (adabbiin) warra duraanii dabritee jirti.');
INSERT INTO "apaguna" VALUES(8,39,'8:39',' Hanga “fitnaan” (shirkiin) hin argamneefi amantiin hundi isaa Rabbiif ta’utti isaaniin lolaa; (kufrii irraa) yoo dhorgaman, dhugumatti, Rabbiin waan isaan dalagan argaadha.');
INSERT INTO "apaguna" VALUES(8,40,'8:40',' .Yoo isaan irraa gara galanis, dhugumatti Rabbiin “gargaaraa keessan ta’uu beekaaTiksaan (Isa ta’uun) waa tole; tumsaan (Isa ta’uunis) waa tole.');
INSERT INTO "apaguna" VALUES(8,41,'8:41',' Wanti isin boojitan kumiyyuu, tokko shanaffaan isaa Rabbiif, ergamaa Isaa, firoota dhihoo, yatiima, hiyyeeyyiifi kara deemaaf ta’uu isaa beekaa; yoo kan Rabbiifi waan Nuti gabricha keenya irratti guyyaa addaba’iinsaa (dhugaafi soba addaan baafne), guyyaa (Badrii kan) tuutti lamaan wal qunnaman buufnetti kan amantan taatan (sana beekaa)Rabbiinis waan hunda irratti danda’aadha');
INSERT INTO "apaguna" VALUES(8,42,'8:42',' Yeroo isin moggaa (Madiinaatti) dhihoo taatanii, isaanis moggaa fagootti ta’an, daldaltoonni immoo jala keessan ta’an (waan ta’e yaadadhaa)Odoo waliif beellamtanii, silaa beellama keessatti wal dhabdu turtanGaruu Rabbiin dhimma (beekumsa isaa keessatti) raawwatamaa ta’e raawwachuuf, namni bades ragaa ifa ta’e booda akka baduuf, namni jiraatus ragaa ifa ta’e booda akka jiraatuuf (beellama malee walitti isin qabe)Dhugumatti Rabbiin dhaga’aa, beekaadha.');
INSERT INTO "apaguna" VALUES(8,43,'8:43',' Yeroo Rabbiin abjuu kee keessatti xiqqoo ta’uu isaanii sitti argisiises [yaadadhu]Odoo baay’ee ta’anii isaan sitti agarsiisee, silaa luynoomtanii, dhimmicha keessattis wal mormitu turtanGaruu Rabbiin nagaya (isin) godheDhugumatti, Inni beekaa waan qoma keessa jiruuti.');
INSERT INTO "apaguna" VALUES(8,44,'8:44',' Yeroo walitti dhuftanii Rabbiin dhimma (beekumsa Isaa keessatti) raawwatamaa ta’e galmaan ga’uuf jecha, isin ija isaanii keessatti kan xiqqeessu ta’ee, yeroo ilaalcha keessan keessatti xiqqoo taasisee isaan isin agarsiise [yaadadhu]Wantoonni hundi gara Rabbii deebifamti');
INSERT INTO "apaguna" VALUES(8,45,'8:45',' Yaa warra amantan! Yeroo garee (diinaa) wahii qunnamtan, gadi dhaabbadhaa; akka milkooftaniifis (faaruu) baay’ee Rabbiin faarsaa');
INSERT INTO "apaguna" VALUES(8,46,'8:46',' Rabbiifi ergamaa Isaatiifis ajajamaa; walis hin morminaa; luynoomtanii humni keessanis (isin irraa) ni deemtii; obsaaDhugumatti, Rabbiin obsitoota wajjin jira');
INSERT INTO "apaguna" VALUES(8,47,'8:47',' Akka isaan boonaa, namootatti of argisiisiisan ta’anii, karaa Rabbii irraas (namoota) dhorgaa, manneen isaanii irraa bahanii hin ta’inaaRabbiin waan isaan dalaganitti marsaadha');
INSERT INTO "apaguna" VALUES(8,48,'8:48',' Yeroo sheyxaanni dalagaa isaanii (gadhee) miidhagsee, “har’a nama irraa injifataan isiniif hin jiruAnis gargaaraa keessan” jedheen san [yaadadhu]Yeroma gareen lamaan wal argan, of duuba deebi’ee “Dhugumatti, ani isin irraa qulqulluudha; ani waan isin hin agarren argaa jiraAni Rabbiinin sodaadhaRabbiin adabbiin Isaa cimaadha” jedhe');
INSERT INTO "apaguna" VALUES(8,49,'8:49',' Yeroo munaafiqoonniifi warri onnee isaanii keessa dhukkubni (kufrii) jiru “warra kana amantii isaaniitu isaan gowwoomse” jedhan [yaadadhu]Namni Rabbi irratti hirkate (ni moo’ata)Dhugumatti Rabbiin injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(8,50,'8:50',' Yeroo maleeykonni lubbuu warra kafaranii fuula isaaniitiifi duuba isaanii rukutaa “adabbii gubu dhandhamaa” jedhaa fuudhan odoo gartee (dubbii cimaa argita).');
INSERT INTO "apaguna" VALUES(8,51,'8:51',' ”Kun waan harki keessan dabarfatteefi (waan) Rabbiinis gabroota miidhaa hin ta’iniifi” (isaaniin jedhama)');
INSERT INTO "apaguna" VALUES(8,52,'8:52',' )Haalli ummata kanaa) Akkuma haala warra Fir’awniifi warra isaan dura turan kan keeyyattoota Rabbiitti kafaranii Rabbiin badiiwwan isaaniitiin isaan qabeetiDhugumatti Rabbiin jabaa, adabbiin Isaa cimaati.');
INSERT INTO "apaguna" VALUES(8,53,'8:53',' Kun waan Rabbiin tola ummata irratti oole tokko hanga isaan waan lubbuu isaaniitti jiru jijjiiranitti kan hin jijjiirre ta’eefi Rabbiinis dhagayaa beekaa waan ta’eefi.');
INSERT INTO "apaguna" VALUES(8,54,'8:54',' Akka haala namoota Fir’awniifi warra isaan duraa, kan keeyyattoota Gooftaa isaaniitti kafaranii, ergasii badiiwwan isaaniitiin isaan balleessineeti; ummata Fir’awnis bishaaniin dhidhimsine; hundumtuu miidhaa hojjattoota turan.');
INSERT INTO "apaguna" VALUES(8,55,'8:55',' Dhugumatti, irra badaan lubbu qabeeyyii Rabbiin biratti warra kafarani; isaan hin amananu.');
INSERT INTO "apaguna" VALUES(8,56,'8:56',' (Isaan) warra ati waadaa isaan irraa fuutee, yeroo (waadaa seenan) hunda kan Rabbiin hin sodaanne ta’anii waadaa isaanii diiganiidha.');
INSERT INTO "apaguna" VALUES(8,57,'8:57',' Yoo lola irratti isaan argite akka isaan gorfamaniif jecha namoota isaan boodaa isaaniin addaan facaasi.');
INSERT INTO "apaguna" VALUES(8,58,'8:58',' Garuu yoo namoota irraa ganiinsa sodaatte ifaan ifatti (waadaa isaanii) gara isaanitti darbiDhugumatti, Rabbiin warra (waadaa) ganan hin jaalatu.');
INSERT INTO "apaguna" VALUES(8,59,'8:59',' Isaan kafaran (adaba Rabbii jalaa) miliquu hin yaadinDhugumatti, isaan Nu hin dadhabsiisanu.');
INSERT INTO "apaguna" VALUES(8,60,'8:60',' Humnaafi faradoo hidhaman irraas ta’ee waan dandeessaniin, kan ittiin diina Rabbii, diina keessaniifi ummattoota biraa kan isaan hin tahin, kan Rabbiin isaan beeku, isin isaan hin beekne ittiin akka sodaachiftaniif isaaniif qopheessaaWanti karaa Rabbii keessatti (isin) arjoomtan kamillee (mindaan isaa) isiniif kennamaIsin hin miidhamtanu.');
INSERT INTO "apaguna" VALUES(8,61,'8:61',' Yoo gara araaraatti daban, atis gara isheetti dabi; Rabbi irratti irkadhuDhugumatti, Inni dhaga’aa, argaadha.');
INSERT INTO "apaguna" VALUES(8,62,'8:62',' Yoo isaan si ganuu fedhanis, dhugumatti ga’aan kee RabbumaInni Isa tumsa Isaatiifi mu’uminootaan si jabeesseedha.');
INSERT INTO "apaguna" VALUES(8,63,'8:63',' Gidduu onnee isaaniis kan wal taasiseedhaGaruu Rabbitu wal taasise malee odoo (wal taasisuuf) waan dachii keessaa hunda arjoomteeyyuu, gidduu onnee isaanii wal taasisuu hin dandeessuDhugumatti Inni injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(8,64,'8:64',' Yaa nabiyyii (kiyya)! Siifi mu’uminoota irraa namoota si hordofaniifis Rabbiin gahaadha.');
INSERT INTO "apaguna" VALUES(8,65,'8:65',' Yaa nabiyyii (kiyya)! Mu’uminoota lola irratti kakaasiIsin irraa obsitoonni digdamni yoo argame, dhibba lama haa injifatanuIsin irraa dhibbi tokko yoo argame, waan isaan ummata hin hubanne ta’aniif isaan kafaran irraa kuma haa injifatanu.');
INSERT INTO "apaguna" VALUES(8,66,'8:66',' Amma Rabbiin isin irraa laaffiseeraIsin keessa dadhabbiin akka jirus beekeeraIsin irraa obsitoonni dhibbi tokko yoo argame, dhibba lama haa injifatuIsin irraa kumni tokko yoo argame, hayyama Rabbiitiin kuma lama haa injifatuRabbiinis obsitootaa wajjin jira.');
INSERT INTO "apaguna" VALUES(8,67,'8:67',' Nabiyyii kamiifuu hanga lafa irratti injifatutti (ajjeechaa isaanii bakkaan gahutti), booji’amtoonni isaaf ta’uun isaaf hin maluIsin meeshaa (faa’idaa) addunyaa feetu; Rabbiin immoo Aakhiraa (isiniif) fedhaRabbiin injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(8,68,'8:68',' Odoo murtiin Rabbi irraa dabre ta’uu baatee, silaa waan isin fudhattaniif adabbiin guddaan isin mudata ture.');
INSERT INTO "apaguna" VALUES(8,69,'8:69',' Haala hayyamamaafi gaarii ta’een waan boojitan irraa nyaadhaaRabbiinis sodaadhaaDhugumatti, Rabbiin araaramaa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(8,70,'8:70',' Yaa nabiyyii (kiyya)! Booji’amtoota irraa namoota harka kee keessa jiraniin jedhi: “Odoo Rabbiin onnee keessan keessatti toltuu wahii beekee, silaa waan isin irraa fudhatame waan caalu isiniif kenna ture; isiniifis ni araarama tureRabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(8,71,'8:71',' Yoo isaan si ganuu fedhan, dhugumatti kana duras Rabbiin gananii jiru; isaan irraas (ajjeesuufi booji’uun) mijjeeffatee jiraRabbiin beekaa, ogeessa.');
INSERT INTO "apaguna" VALUES(8,72,'8:72',' Dhugumatti, isaan amananii godaananii, karaa Rabbii keessattis qabeenyaafi lubbuu isaanitiin qabsaa’aniifi isaan (isin) qubsiisanii, gargaarsas (isiniif) godhan, warri sun gariin isaanii jaalallee gariitiIsaan amananii hin godaanin immoo hanga godaananitti jaalala isaanii irraa homtuu isiniif hin taaneYoo amantii keessatti gargaarsa isin gaafatan, namoota gidduu keessaniifi gidduu isaanii waadaan jiru irratti (yoo ta’e) malee isaan gargaaruun isin irratti dirqamaRabbiin waan isin hojjattan argaadha.');
INSERT INTO "apaguna" VALUES(8,73,'8:73',' Isaan kafaranis gariin isaanii jaalallee gariitiYoo isin isa (mu’uminoota jaallattanii kaafiroota jibbuu) raawwachuu baattan dachii keessatti balaa (shirkii)fi badii guddaatu argama.');
INSERT INTO "apaguna" VALUES(8,74,'8:74',' Isaan amananii, godaananii, karaa Rabbii keessatti qabsaa’aniifi isaan (isin) qubsiisanii, gargaarsa (isiniif) godhan, warri sun isaanumatu mu’uminoota dhugaatiIsaaniif araaramaafi soorata gaariitu jira.');
INSERT INTO "apaguna" VALUES(8,75,'8:75',' Warri sana booda amananii, godaananiifi isin waliin qabsaa’an, isaan isin irraayyi(Dhaala ilaalchisee) kitaaba Rabbii keessatti warri firaa gariin isaanii garii (dhaaluu)tti irra caalaadhaDhugumatti, Rabbiin waan hunda beekaadha');
INSERT INTO "apaguna" VALUES(9,1,'9:1','(Isheen kun) Qulqullina Rabbiifi ergmaa Isaa irraa taate kan gara warra mushrikoota irraa (lola dhiisuuf) waadaa walii galtaniitti (qaqqabduudha)');
INSERT INTO "apaguna" VALUES(9,2,'9:2','Ji’oota afuriif dachii keessa deemaaIsin Rabbiin dadhabsiisoo akka hin ta’iniifi Rabbiinis dhugumatti salphisaa kaafirootaa ta’uu beekaa.');
INSERT INTO "apaguna" VALUES(9,3,'9:3','(Kuni) labsii Rabbiifi ergamaa Isaa irraa guyyaa hajji guddaa,[] “Rabbiin qindeessitoota irraa qulqulluudha; Ergamaan Isaas (akkasuma)Yoo tawbattaniif, inni isiniif caalaadhaYoo garagaltan immoo isin Rabbiin dadhabsiisoo akka hin ta’in beekaa” jechuudhaan gara namaatti (qaqqabduu dha)Warra kafaranis adabbii laalessaadhaan gammachiisi.');
INSERT INTO "apaguna" VALUES(9,4,'9:4','Garuu qindeessitoota irraa Warri waadaa walii seentanii sana booda homaa isin hin hirdhisiniifi (nama) tokkollee isin irratti hin gargaarin, hanga beellama isaaniitti waadaa isaanii isaaniif guutaaDhugumatti Rabbiin sodaattota (isaa) ni jaalata.');
INSERT INTO "apaguna" VALUES(9,5,'9:5','Yeroo ji’oonni kabajamoon[] dabran, mushrikoota bakka argitanitti ajjeesaa; isaan booji’aas; isaan marsaas; karaa hunda irras isaaniif taa’aaYoo tawbatanii, salaata gadi dhaabanii, zakaas kennan karaa isaaniif gadi dhiisaaDhugumatti Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(9,6,'9:6','Yoo tokkoon qindeessitoota irraa ta’e nagaya gochuu sirra barbaade, hanga inni haasaa Rabbii dhagahutti eegumsa isaaf godhiSana booda iddoo eegumsa isaatiin isa ga’iKun waan isaan ummata hin beekne ta’aniifi.');
INSERT INTO "apaguna" VALUES(9,7,'9:7','Qindeessitootaaf waadaan Rabbiin biraafi ergamaa Isaa bira akkamitti jiraata? Garuu isaan masjiida kabajamaa biratti waadaa walii seentan, hanga isaan (waadaadhaan) isiniif dhaabbatanitti isinis isaaniif dhaabbadhaaDhugumatti Rabbiin warra Isa sodaatan ni jaalata.');
INSERT INTO "apaguna" VALUES(9,8,'9:8','Odoo isin irratti injifannoo argatanii, firummaafi waadaas isin keessatti kan hin eegganne ta’anii, akkamitti (waadaan isaaniif ta’a)? Afaan isaaniitiin isin jaallachiisu; Onneen isaanii immoo ni diddiIrra hedduun isaaniis finciltoota');
INSERT INTO "apaguna" VALUES(9,9,'9:9','Keeyyattoota Rabbiitiin bu’aa xiqqaa bitatanii karaa Isaa irraas (namoota) dhorganDhugumatti isaan wanti isaan dalagaa turan waa fokkate.');
INSERT INTO "apaguna" VALUES(9,10,'9:10','Mu’umina keessatti, firummaas ta’ee waadaa hin eeggatanuWarri sun isaanumatu daangaa dabritoota.');
INSERT INTO "apaguna" VALUES(9,11,'9:11','Yoo tawbatanii, salaatas gadi dhaabanii, zakaa kennan (isaan) amantii keessatti obboleeyyan keessaniUmmata xiinxalaniif keeyyattoota keenya ni addeessina.');
INSERT INTO "apaguna" VALUES(9,12,'9:12','Yoo erga waadaa seenanii booda waadaa isaanii diiganii amantii keessan xureessan, isaan waadaan isaaniif hin jiruu, akka dhorgamaniif hoggantoota kufriitin lolaa.');
INSERT INTO "apaguna" VALUES(9,13,'9:13','Sila ummata waadaa isaanii diiganii, ergamaa (Rabbii) biyya irraa baasuuf barbaadan, isaan dursanii kan isin (miidhuu) jalqaban odoo ta’anii jiranuu maaliif isaaniin hin lolle? Sila isaan sodaattuu? Yoo mu’uminoota taatan Rabbitu Isa sodaachuu irra haqa godhata.');
INSERT INTO "apaguna" VALUES(9,14,'9:14','Isaaniin lolaaRabbiin harka keessaniin isaan adaba; isaan salphisas; isaan irratti isin tumsas; qalbii warra mu’uminootaas ni fayyisa.');
INSERT INTO "apaguna" VALUES(9,15,'9:15','Dallansuu onnee isaaniis ni deemsisa; Rabbiin nama fedhe irra toowbaa qeebalaRabbiin beekaa, ogeessa.');
INSERT INTO "apaguna" VALUES(9,16,'9:16','Sila odoo Rabbiin isin irraa namoota qabsaa’aniifi kan Rabbiin, ergamaa isaatiifi mu’uminootaan ala jaalalloo hin godhatin adda hin baafne dhiifamtu seetuu? Rabbiin waan isin dalagdan keessa beekaadha');
INSERT INTO "apaguna" VALUES(9,17,'9:17','Odoo lubbuu isaanii irratti kufrii ragaa ba’anii masjiidota Rabbii jiraachisuun mushrikootaan hin maluIsaan dalagaan isaanii baddeIsaan ibidda keessa yeroo hunda jiraatu');
INSERT INTO "apaguna" VALUES(9,18,'9:18','Masjiidota Rabbii kan jiraachisu nama Rabbiifi Guyyaa Qiyaamaatti amane, kan salaata gadi dhaabe, zakaa kenneefi Rabbiin malee homaa hin sodaanne qofaWarri kun isaan karaa sirrii qabatan irraa ta’uun mirkana’eera.');
INSERT INTO "apaguna" VALUES(9,19,'9:19','Sila warra ka’abaa hajja’u obaasuufi masjiida kabajamaa jiraachisuu akka (imaana) nama Rabbiifi Guyyaa Qiyaamaatti amanee, karaa Rabbii keessatti qabsaa’ee gootuu? Rabbiin biratti wal hin qixxaa’anRabbiinis ummata midhaa hojjatoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(9,20,'9:20','Isaan amananii, godaananiifi karaa Rabbii keessatti qabeenyaafi lubbuu isaaniitiin qabsaa’an; Rabbiin biratti sadarkaadhaan ol aanoodhaIsaan sun isaanumatu milkaa’oodha.');
INSERT INTO "apaguna" VALUES(9,21,'9:21','Gooftaan isaanii rahmataafi jaalala Isa irraa ta’eefi jannata ishee keessa qananiin yeroo hundaa isaaniif jirtuun isaan gammachiisa.');
INSERT INTO "apaguna" VALUES(9,22,'9:22','Yeroo hunda ishee keessa kan jiraatan ta’anii (isaan gammachiisa); Dhugumatti, Rabbiin Isa bira mindaa guddaatu jira');
INSERT INTO "apaguna" VALUES(9,23,'9:23','Yaa warra amantan! Abbootii keessaniifi obboleeyyan keessan yoo isaan iimaana irra kufrii jaallatan, jaalallee hin taasifatinaaIsin irraa namni jaalallee isaan taasifate, warri sun isaanumatu midhaa raawwatoota.');
INSERT INTO "apaguna" VALUES(9,24,'9:24','“Yoo abbootin keessan, ilmaan keessan, obboleeyyan keessan, niitiwwan keessan, firoonni keessan, qabeenyi isin horattan, daldalli isin hir’achuu ishee sodaattaniifi manneen isin jaallattan Rabbi, ergamaa Isaatiifi karaa Isaa keessatti qabsaa’uu irra gara keessanitti jaalatamaa ta’e, hanga Rabbiin murtii Isaatiin dhufutti eegaa” jedhiRabbiinis ummata finciltoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(9,25,'9:25','Dhugumatti, Rabbiin iddoo baay’eetti isin tumseeraGuyyaa (lola) Hunayni, yeroo baay’inni keessan isin ajaa’ibsiise, kan homaa isin irraa hin deebisin, dachiin bal’ina ishee wajjin isinitti dhiphattee, sana booda yeroo baqa duubatti garagaltan (waan ta’e yaadadhu).');
INSERT INTO "apaguna" VALUES(9,26,'9:26','Ergasii Rabbiin ergamaa Isaatiifi mu’uminoota irratti tasgabbii Isaa buuseWaraana isin hin argines buusee Isaan kafaran adabeKun mindaa kaafirootaati.');
INSERT INTO "apaguna" VALUES(9,27,'9:27','Rabbiin sana booda nama fedhe irraa tawbaa isaa qeebalaRabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(9,28,'9:28','Yaa warra amantan! Mushrikoonni najisa qofa; waggaa isaanii kana booda masjiidicha kabajamaa biratti akka hin dhiyaanneYoo beela sodaattanis Rabbiin yoo fedhe tola Isa irraa ta’een isin duroomsaDhugumatti, Rabbiin beekaa, ogeessa.');
INSERT INTO "apaguna" VALUES(9,29,'9:29','Isaan Rabbiifi Guyyaa Dhumaatti hin amanin, kan waan Rabbiifi ergamaan Isaa haraam godhe haraam hin godhin, kan amantii haqaa hin fudhanne, warra kitaabni kennameef irraa kan ta’an hanga isaan salpatoo ta’anii gibira harkatti kennanitti loolaani.');
INSERT INTO "apaguna" VALUES(9,30,'9:30','Yahuudonni “Uzayr ilma Rabbiiti” jedhanKiristaanonnis “Masiih ilma Rabbiiti” jedhanKun jecha isaanii kanuma afaan isaaniitiJecha isaan duraan kafaraniittis ni fakkeessuAbaarsi Rabbii isaan irratti haa jiraatuu, akkamitti (dhugaa irraa) garagalu!');
INSERT INTO "apaguna" VALUES(9,31,'9:31','Gooftaa tokkicha gabbarutti malee kan hin ajajamin tahanii, hayyoota isaaniitiifi moloksee isaanii Rabbiin alatti gabaramtoota godhatan; Masiih ilma Maryamis (gabbaramaa godhatan)Isa malee gabbaramaan haqaa hin jiruInni waan isaan itti qindessan irraa qulqullaa’e.');
INSERT INTO "apaguna" VALUES(9,32,'9:32','Isaan (kaafiroonni) ifaa Rabbii afaanuma isaaniitiin dhaamsuu fedhuOduma kaafirtoonni jibbanuu Rabbiin ifaa Isaa guutuu malee (fedhii isaanii) ni dida.');
INSERT INTO "apaguna" VALUES(9,33,'9:33','Inni (Rabbiin) Isa odoma mushrikoonni jibbanuu, amantii hundarra ol taasisuuf jecha ergamaa Isaa qajeelumaafi amantii haqaatiin ergeedha');
INSERT INTO "apaguna" VALUES(9,34,'9:34','Yaa warra amantan! Dhugumatti irra hedduun hayyootaafi moloksee qabeenya namootaa haqaan malee nyaatan; karaa Rabbii irraahis (namoota) deebisuIsaan meetaafi warqee kuusatan, kan karaa Rabbii keessatti ishee hin sadaqanne adabbii laalessaan isaan gammachiisi.');
INSERT INTO "apaguna" VALUES(9,35,'9:35','Guyyaa ibidda jahannam keessatti ishee irratti ho’ifamu, kan addi isaanii, cinaachi isaaniitiifi dugdi isaanii isheen gubamu “Kun waan isin lubbuu keessaniif kuusaa turtaniidha; waan kuusaa turtaniis dhandhamaa” (jedhamu)');
INSERT INTO "apaguna" VALUES(9,36,'9:36','Lakkoofsi ji’ootaa Rabbi biratti gaafuma samiifi dachii uume kitaaba Rabbii keessatti ji’a kudha lamaIsaan keessaa afur kabajamoodhaSanatu amantii sirriitiIsaan keessatti lubbuu keessan hin miidhinaaMushrikootas, akkuma isaan tokko ta’anii isiniin lolan; tokko ta’aatii lolaaDhugumatti Rabbiin warra isa sodaatan wajjin ta’uu beekaa.');
INSERT INTO "apaguna" VALUES(9,37,'9:37','Kabajaa ji’a tokkoo gara ji’a birootti siqsuun kufrii irratti (kufrii) dabaluudhaWarri kafaran isaan jallifamuWaggaa tokko (keessa) hayyamamaa isa godhu; waggaa biraa immoo dhorgamaa isa godhuAkka lakkoofsa wanta Rabbiin dhorgamaa godhee qunnamaniif jecha waan Rabbiin dhorgamaa godhe hayyamamaa godhuFokkataan hojii isaaniis isaaniif miidhagfameRabbiinis ummata kafaran hin qajeelchu.');
INSERT INTO "apaguna" VALUES(9,38,'9:38','Yaa warra amantan! Maaltu isiniif jiraayyi yeroo karaa Rabbii keessatti (lolaaf) bahaa isiniin jedhame lafatti rinciccu? Sila jireenya Addunyaa Aakhiraa irra jaalatanii? Qananiin jireenya Adunyaa Aakhiraa biratti xiqqo malee homaa miti.');
INSERT INTO "apaguna" VALUES(9,39,'9:39','Yoo isin (jihaadaaf) hin bahin, (Rabbiin) adabbii laalessaa isin adaba; Ummata isiniin alaas bakka keessan buusa; isin homaa Isa hin miitanRabbiin waan hunda irratti danda’aadha');
INSERT INTO "apaguna" VALUES(9,40,'9:40','Yoo isin isaaf tumsuu baattaniis, dhugumatti yeroo warri kafaran lammaffaa nama lamaa ta’ee isa (Makkaa) baasan, yeroo isaan lamaanuu goda keessa ta’an, yeroo inni saahiba isaatiin “dhugumatti Rabbiin nu waliin jiraa; hin gaddin” jedhu san Rabbiin isaaf tumseeraRabbiin tasgabbii Isa irraa ta’e isa irratti buuse; waraana isin ishee hin arginiinis isa jabeesseJecha warra kafaranii gad aantuu taasiseJechi Rabbii isheedhumatu ol aantuudhaRabbiin injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(9,41,'9:41','Salphoo taatanii yookiin ulfaatoo[] taataniis duulaaf bahaa; Qabeenyaafi lubbuu keessaniinis karaa Rabbii keessatti lolaaYoo kan beektan taatan kanatu isiniif caalaadha.');
INSERT INTO "apaguna" VALUES(9,42,'9:42','(Wanti ati itti isaan waamtu) odoo bu’aa dhihoofi imala giddu galeessa ta’ee silaa si hordofu turanGaruu imalatu isaanitti fagaate“Odoo dandeenyee silaa isin wajjiin baana turre” (jedhanii) Rabbiin kakatuLubbuu isaanii balleessuRabbiin kijibdoota ta’uu isaanii ni beeka.');
INSERT INTO "apaguna" VALUES(9,43,'9:43','(Yaa Muhammad!) Rabbiin dhiifama siif godhee, hanga isaan dhugaa dubbatan ifa siif ta’anii warra soban beektutti (lola irraa hafuu) maalif isaaniif hayyamte?');
INSERT INTO "apaguna" VALUES(9,44,'9:44','Isaan Rabbiifi Guyyaa Aakhiraatti amanan qabeenyaafi lubbuu isaaniitiin qabsaa’uu irraa (hafuuf) hayyama si hin gaafatanuRabbiin warra Isa sodaatu beekaadha');
INSERT INTO "apaguna" VALUES(9,45,'9:45','(Lola irraa hafuuf) hayyama kan si gaafatu isaan Rabbiifi guyyaa aakhiraatti hin amanin kan onneen isaanii shakkiteedha; Isaanis shakkii isaanii keessa deddeebi’u.');
INSERT INTO "apaguna" VALUES(9,46,'9:46','Odoo (lolaaf) bahuu fedhanii silaa qophii isaaf qophaa’u turanGaruu Rabbiin bahuu isaanii jibbee duubatti isaan hanbise; “warra taa’uu[] wajjiin taa’aa” jedhame.');
INSERT INTO "apaguna" VALUES(9,47,'9:47','Odoma isinii waliin ba’aniiyyuu badii malee homaa isiniif hin dabalanuRakkoo kan isinitti barbaadan ta’anii, jeequmsaan gidduu keessan fiigu turanIsin keessas namoota isaaniif dhaggeeffatantu jiruRabbiin miidhaa hojjattoota ni beeka.');
INSERT INTO "apaguna" VALUES(9,48,'9:48','Dhugumatti, odoma isaan jibbanuu hanga dhugaan dhufee murtiin Rabbii ifa ba’utti isaan duraanis rakkoo barbaadanii dhimmoota siif gara gaggalchanii turan.');
INSERT INTO "apaguna" VALUES(9,49,'9:49','Isaan irraa nama “(hafuuf) naaf hayyami; nas hin qormaatin” jedhuutu jiraDhagayaa! (isaan) qormaata keessatti kufaniiruDhugumatti Jahannam kaafirootatti marsituudha.');
INSERT INTO "apaguna" VALUES(9,50,'9:50','Yoo toltuun wahii si mudatte, isaan gaddisiiftiYoo balaan wahii si mudatte immoo “nuti dursinee of eeggannoo keenya qabannee jirra” jedhan; kan gammadan ta’anii duuba deebi’u.');
INSERT INTO "apaguna" VALUES(9,51,'9:51','“Waan Rabbiin nuuf barreesse malee homaa nu hin mudatuInni tiksaa keenya” jedhiMu’uminoonni Rabbuma (qofa) irratti haa hirkatan.');
INSERT INTO "apaguna" VALUES(9,52,'9:52','“Sila tokkoo toltuu lamaanii malee waan biraa nutti eegduu? Nutis Rabbiin adabbii Isa biraa ta’een yookiin harka keenyaan isin tuquu eegnaIsinis eegaa; nutis isin wajjiin eegoodha” jedhi.');
INSERT INTO "apaguna" VALUES(9,53,'9:53','“Kan jaallattanis ta’aatii yookiin kan jibbitan ta’aa arjoomaa; isin irraa hin qeebalamuDhugumatti, isin ummata finciltoota turtan” jedhi.');
INSERT INTO "apaguna" VALUES(9,54,'9:54','Rabbiifi ergamaa isaatti kafaruu, salaatas hifatoo ta’anii malee kan hin dhufneefi kan jibban ta’anii malee arjoomuu dhabuu isaanii malee wanti sadaqaan isaanii isaan irraa qeebalamuu dhorge hin jiru.');
INSERT INTO "apaguna" VALUES(9,55,'9:55','Qabeenyi isaaniitiifi ilmaan isaanii si hin ajaa’ibsiisinDhugumatti, wanti Rabbiin fedhu jireenya Addunyaa keessatti isheedhaan isaan adabuufi kaafiroota ta’anii lubbuun isaanii bahuu fedha');
INSERT INTO "apaguna" VALUES(9,56,'9:56','Isaan dhugumatti isin irraayyi jechuun Rabbiin kakatu; isaan isin irraayii mitiGaruu isaan ummata sodaatanuudha.');
INSERT INTO "apaguna" VALUES(9,57,'9:57','Odoo iddoo itti baqatan yookiin goda wahii yookiin iddoo seenan (qaawwa wahii) argatanii silaa kan fiigan ta’anii gara isaatti deebi’u turan.');
INSERT INTO "apaguna" VALUES(9,58,'9:58','Isaan irraas nama (qooda) sadaqaa keessatti maqaa si xureessutu jiraYoo ishee irraa kennaman ni jaallatu; yoo ishee irraa hin kennamne immoo yoosan isaan ni jibbu.');
INSERT INTO "apaguna" VALUES(9,59,'9:59','Odoo isaan waan Rabbiifi ergamaan Isaa isaaniif kenne jaalatanii “Rabbiin nuuf ga’aadhaRabbiin fuula dura tola Isaa irraa nuuf kennaErgamaan Isaas (akkasuma)Dhugumatti, nuti gara Rabbii kajeeloodha” jedhanii (silaa isaaniif caalaa ture).');
INSERT INTO "apaguna" VALUES(9,60,'9:60','Zakaawwan (kan qoodamtu) dhabeeyyiif, hiyyeeyyiif, warra ishee irratti hojjataniif, warra onneen isaanii (Islaamummaa) barsiifamtuuf, gabra bilisoomsuuf, warra idaan irra jiruuf, karaa Rabbii keessattiifi kara deemaadhaaf, (qofa)Dirqama Rabbi irraa ta’e (qabadhaa)Rabbiinis beekaa, ogeessa');
INSERT INTO "apaguna" VALUES(9,61,'9:61','Isaan irraas warra Nabiyyii rakkisantu jiru; “Inni gurra” jedhuJedhiin: “(Eeyyeen inni) gurra isiniif gaarii ta’eedha; Rabbittis ni amana, Mu’uminootas ni dhugoomsaIsin irraa warra amananiifis rahmata.” Isaan ergamaa Rabbii rakkisan, isaaniif adabbii laalessaatu jira');
INSERT INTO "apaguna" VALUES(9,62,'9:62','Isin gammachiisuf Rabbiin isiniif kakatu; yoo mu’uminoota ta’an Rabbiifi ergamaa Isaatu isaan isa gammachiisuu irra haqa godhata');
INSERT INTO "apaguna" VALUES(9,63,'9:63','Sila nama Rabbiifi ergamaa Isaa faallesse, dhugumatti ibidda jahannamtu ishee keessatti kan hafu ta’ee isaaf jira jechuu hin beeknee? Kun salphina guddaadha.');
INSERT INTO "apaguna" VALUES(9,64,'9:64','Munaafiqoonni suuraan waan onnee isaanii keessa jiru isaanitti odeessitu isaan irratti bu’uu sodaatuJedhiin: “qishnaa godhaaDhugumatti, Rabbiin waan isin sodaattan (ifa) baasaadha.”');
INSERT INTO "apaguna" VALUES(9,65,'9:65','Dhugumatti odoo isaan gaafattee “Nuti haasawatti kan seennuufi kan taphannu qofa turre” jedhu“Sila Rabbitti, keeyyattoota Isaatiifi ergamaa Isaatti qishnaa gootuu? jedhiin.');
INSERT INTO "apaguna" VALUES(9,66,'9:66','Rakkoo hin himatinaa! Dhugumatti isin erga amantanii booda kafartanii jirtuYoo garee isin irraa ta’eef irra dabarres, garee biraa immoo waan isaan yakkamtoota ta’aniif ni adabna.');
INSERT INTO "apaguna" VALUES(9,67,'9:67','Munaafiqoonni dhiiraatiifi munaafiqoonni dubartii gariin isaanii garii irraayiWaan jibbamaatti ni ajaju; waan gaarii irraa immoo ni dhorgu; harka isaaniis (kenna irraa) ni deeffatu; Rabbiin dagatan; Innis isaan dhiiseDhugumatti munaafiqoonni isaanumatu finciltoota');
INSERT INTO "apaguna" VALUES(9,68,'9:68','Rabbiin munaafiqoota dhiiraa, munaafiqoota dubartiifi kaafirootaafis ibidda jahannam ishee keessatti kan hafan ta’anii beellamee jira; isheen isaaniif geessuudhaRabbiinis isaan abaareeraIsaaniif adabbii itti fufaatu jira');
INSERT INTO "apaguna" VALUES(9,69,'9:69','(Isin) akkuma warra isin dura dabraniiti; Isaan humnaan isin irra jajjaboo, qabeenyaafi ilmaaniinis isin irra baay’ee turanIsaan qooda isaaniitiin ni qanani’an, akkuma isaan isin dura (dabran) qooda isaanitti qanani’an isiniis qooda keessanitti qananiitanAkkuma isaan (tapha) keessa seenan isinis (tapha) keessa seentanWarri sun Addunyaafi Aakhiraattis dalagaaleen isaanii baddee jirtiWarri sun isaanumatu hoonga’oodha.');
INSERT INTO "apaguna" VALUES(9,70,'9:70','Sila oduun warra isaan duraa isaanitti hin dhufnee? (Oduun) ummata Nuuh, Aad, Samuud, ummata Ibraahiim, jiraattota Madyaniifi (gandattii) garagalfamtuu (isaanitti hin dhufnee)? Ergamtoonni isaanii ragaa ifa ta’een isaanitti dhufanii jiruRabbiin kan isaan miidhu hin taaneGaruu isaan lubbuu isaanii kan miidhan turan');
INSERT INTO "apaguna" VALUES(9,71,'9:71','Mu’uminoonni dhiiratiifi Mu’uminoonni dubaraa gariin isaanii jaalallee gariitiToltutti ajaju; hamtuu irraas ni dhorgu, salaatas gadi dhaabu; Zakaas ni kennu; Rabbiifi ergamaa Isaatiifis ni ajajamuWarri sun Rabbiin fuula dura rahmata isaaniif godhaDhugumatti Rabbiin injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(9,72,'9:72','Rabbiin mu’uminoota dhiiraatiifi dubartootaatiifis jannata laggeen ishee jala yaatu, ishee keessatti kan hafan ta’anii manneen gaggaarii jannata qubsumaa keessa taates waadaa galee jiraJaalalli Rabbiin irraa ta’e (hunda) irra guddaadhaKun milkii guddaadha.');
INSERT INTO "apaguna" VALUES(9,73,'9:73','Yaa Nabiyyichaa! kaafirootaafi munaafiqoota irratti qabsaa’i; isaan irrattis jabeessiTeesson isaanii jahannamiDeebiin isaaniis (jahannam) waa fokkatee!');
INSERT INTO "apaguna" VALUES(9,74,'9:74','Homaa hin jenne jedhanii Rabbiin kakatuDhugumatti isaan jecha kufrii jedhaniiru; Erga Islaamummaa isaaniitiis kafaraniiru; Waan itti hin geenyes yaadaniiru; Rabbiifi ergamaan isaa tola Isa irraa ta’een isaan duroomsuu malee waan biraa hin jibbineYoo tawbatan caalaa isaaniif ta’a; yoo (irraa) garagalan immoo Rabbiin Addunyaafi Aakhiraattis adabbii laalessaan isaan adabaDachii keessattis jaalalleefi tumsaan isaaniif hin jiru.');
INSERT INTO "apaguna" VALUES(9,75,'9:75','Isaan irraas namoota “Yoo (Rabbiin) tola Isaa irraa nuuf kenne, silaa dhugumatti ni arjoomna; Gaggaarii irraas ni taana turre” (jechuun) Rabbiif waadaa seenantu jiru.');
INSERT INTO "apaguna" VALUES(9,76,'9:76','Yeroma (Rabbiin) tola Isaa irraa isaaniif kenne, ittiin doynoomani; kan garagalan ta’anii dugda duuba deebi’an.');
INSERT INTO "apaguna" VALUES(9,77,'9:77','Waan Rabbiin waadaa isaa faallessaniifi waan sobaa turaniif hanga guyyaa Isaan wal qunnamaniitti munaafiqummaa onnee isaanii keessatti isaan dhaalchise.');
INSERT INTO "apaguna" VALUES(9,78,'9:78','Sila Rabbiin iccitii isaaniitiifi maqoo isaaniis kan beeku ta’uufi Rabbiin wantoota fagoo hundas akkaan beekaa ta’uu hin beeknee?');
INSERT INTO "apaguna" VALUES(9,79,'9:79','(Isaan) warra mu’uminoota irraa warra arjoomuu keessatti tola oolaniifi isaan waan dandeettii isaanii malee hin arganne maqaa xureessaniidha; Isaan isaanitti qishinuRabbiinis isaanitti qishina (isaan adaba)Isaaniif adabbii laalessaatu jira.');
INSERT INTO "apaguna" VALUES(9,80,'9:80','Araarama isaaniif kadhadhus yookiin araarama isaaniif kadhachuu dhiisis; yoo yeroo torbaatama araarama isaaniif kadhattes, Rabbiin isaaniif hin araaramuKun waan isaan Rabbiifi ergamaa Isaattis kafaraniifiRabbiin ummata finciltoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(9,81,'9:81','Isaan (duula irraa) hafan, booda ergamaa Rabbiitii taa’uu isaaniitti gammadan; qabeenyaafi lubbuu isaaniitiin karaa Rabbii keessatti qabsaa’us ni jibbani; “ho’a keessa duulaaf hin bahinaa” jedhan“Odoo beekanii ibidda jahannamtu ho’atti irra cimaadha” jedhiin.');
INSERT INTO "apaguna" VALUES(9,82,'9:82','Galata waan hojjataniitiif xiqqoo kolfanii baay’ee haa boo’an.');
INSERT INTO "apaguna" VALUES(9,83,'9:83','Yoo Rabbiin gara garee isaan irraa ta’eetti si deebisee, (duulaaf) bahuuf hayyama si gaafatan, “Ana wajjiin gonkumaa hin baatanu; Ana wajjinis diinaan hin loltanuIsin dhugumatti yeroo jalqabaa taa’umsa jaalattanii jirtuKanaafuu warra (mana) taa’an wajjin taa’aa” jedhiin.');
INSERT INTO "apaguna" VALUES(9,84,'9:84','Isaan irraa nama du’e tokko irrattis hin sagadinQabrii isaa irras hin dhaabbatinDhugumatti isaan Rabbiifi ergamaa Isaattis kafaranii finciltoota ta’anii du’aniiru.');
INSERT INTO "apaguna" VALUES(9,85,'9:85','Qabenyi isaaniitiifi ilmaan isaanii si hin ajaa’ibsiisinRabbiin isheedhaan addunyaa keessatti isaan adabee, isaan kaafiroota ta’anii lubbuun isaanii bahuu fedha.');
INSERT INTO "apaguna" VALUES(9,86,'9:86','Yeroo suuraan Rabbitti amanaa ergamaa Isaa wajjiinis qabsaa’aa isaaniin jettu tokko buufamte, isaan irraa warri qabeenya qaban si hayyamsiifatu“Nu dhiisi; nuti warra taa’an wajjin taanaa” jedhan');
INSERT INTO "apaguna" VALUES(9,87,'9:87','Warra (duula irraa) hafan wajjiin ta’uu jaalatanOnnee isaanii irrattis cufameera, isaanis hin hubatanu.');
INSERT INTO "apaguna" VALUES(9,88,'9:88','Garuu ergamichiifi warri isaa wajjin amanan, qabeenyaafi lubbuu isaaniitiin qabsaa’anii jiruWarri sun toltuu baay’eetu isaaniif jiraWarri sun isaanumatu milkaa’oodha.');
INSERT INTO "apaguna" VALUES(9,89,'9:89','Rabbiin jannata laggeen ishee jala yaatu, Isaan ishee keessatti kan hafan ta’anii, isaaniif qopheessee jiraKuni milkii guddaadha.');
INSERT INTO "apaguna" VALUES(9,90,'9:90','Tikfattoota baadiyyaa irraa warri rakkoo himatan akka hayyamni isaaniif godhamuuf jecha dhufanIsaan Rabbiifi ergamaa Isaa kijibanis ni taa’anIsaan irraa warra kafaran fuula dura adabbii laalessaatu isaan mudata.()');
INSERT INTO "apaguna" VALUES(9,91,'9:91','Dadhaboota, dhukkubsattootaafi isaan waan kennatan hin arganne irratti yeroo Rabbiifi ergamaa Isaatiif toltuu yaadan (duuluu baatanis) diliin hin jiruToltuu raawattoota irra karaan (komii) hin jiruRabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(9,92,'9:92','Isaan akka waan yaabbatan isaaniif kennitu yeroo sitti dhufan, ati “waan yaabbattan isiniif arguu hin danda’u” jetteen, waan sadaqatan dhabuu irraa gaddaan ijji isaanii imimmaan yaasaa deebi’an irras (komiin) hin jiru');
INSERT INTO "apaguna" VALUES(9,93,'9:93','Karaan (Komii) kan jiru warra dureeyyii ta’anii hayyama si gaafatan qofa irraWarra hafan waliin ta’uu jaalatan; Rabbiinis onnee isaanii irratti cufe; kanaafuu, isaan homaa hin beekan.');
INSERT INTO "apaguna" VALUES(9,94,'9:94','Yeroo gara isaanii deebitan rakkina kara keessan dhiheeffatu“Sababaa hin dhiheessinaa; Nuti isiniif hin amannuDhugumatti, Rabbiin oduu keessan irraa nuuf himee jiraRabbiifi ergamaan Isaas hojii keessan arguuf jiruSana booda gara isa waan mul’atuufi hin mul’anne beekuutti deebifamtuWaan isin dalagaa turtanis isinitti hima” jedhiin.');
INSERT INTO "apaguna" VALUES(9,95,'9:95','Yeroo isin gara isaanii deebitan akka isin isaan irraa gara galtaniif Rabbiin isiniif kakatu; isaan irraa gara galaa; dhugumatti, isaan najisaMindaa waan dalagaa turaniitiif teesson isaanii jahannama.');
INSERT INTO "apaguna" VALUES(9,96,'9:96','Akka isin isaan irraa jaalattaniif isiniif kakatuYoo isin isaan irraa jaalattanis, Rabbiin dhugumatti ummata finciltoota irraa hin jaalatu');
INSERT INTO "apaguna" VALUES(9,97,'9:97','Horsiisee bultoonni kufriifi munaafiqummaatti irra cimoodha; daangaawwan waan Rabbiin ergamaa Isaa irratti buusee beekuu dhabuuttis irra dhihoodhaRabbiin hunda beekaa, ogeessa');
INSERT INTO "apaguna" VALUES(9,98,'9:98','Horsiisee bultoota irraas namoota waan (karaa Rabbii keessatti) arjoomu akka idaatti qabatee, naannoowwan (rakkinaa) isinitti eegutu jiraNaannoon hamtuu isaan irratti haa ta’uRabbiin dhaga’aa, beekaadha.');
INSERT INTO "apaguna" VALUES(9,99,'9:99','Ammas tikfattoota baadiyyaa irraa nama Rabbiifi Guyyaa Aakhiraatti amanee waan (karaa Rabbii keessatti) arjoomus Rabbiin biratti dhiheenyaafi kadhaa ergamichaa godhatutu jiraDhagayaa! dhugumatti isheen isaaniif dhiheenyaRabbiinis fuula dura rahmata Isaa keessa isaan seensisaDhugumatti Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(9,100,'9:100','Muhaajirootaafi Ansaarota irraas dursitoonni jalqabdoota ta’aniifi warri toltuudhaan isaan hordofanis, Rabbiin isaan irraa jaalateeraIsaanis Isa irraa jaalatanii jiruJannata ishee jala laggeen yaatus kan ishee keessatti hafan ta’anii isaaniif qopheessee jiraKun milkii guddaadha.');
INSERT INTO "apaguna" VALUES(9,101,'9:101','Tikfattoota baadiyyaa irraa gariin warra naannoo keessan jiruu munaafiqootaWarra Madiinaa irraas (namoota) munaafiqummaa irratti ragga’an, kan ati isaan hin beeknetu jiruNuti isaan ni beeknaFuula dura yeroo lama[] isaan adabnaErgasii isaan gara adabbii guddaa deebifamu.');
INSERT INTO "apaguna" VALUES(9,102,'9:102','Warri biroo immoo kan badii isaanii fudhatan, dalagaa gaariifi fokkataa biraa walitti makantu jiru; Rabbiin tawbaa isaanii irraa qeebaluun mirkanaa’eDhugumatti Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(9,103,'9:103','Qabeenya isaanii irraa sadaqaa isheedhaan isaan qulqulleessituufi isaaniif dabaltuun fuudhiRabbiinis isaaniif kadhu; kadhaan kee isaaniif tasgabbiidhaRabbiin dhagayaa, beekadha.');
INSERT INTO "apaguna" VALUES(9,104,'9:104','Sila isaan ‘Rabbiin Isatu gabroota Isaa irraa tawbaa qeebalaafi sadaqaas fudha’ jechuu hin beeknee? Ammas dhugumatti ‘Rabbiin isatu tawbaa qeebalaafi rahmata godhaa dha’ jechuus (hin beeknee?).');
INSERT INTO "apaguna" VALUES(9,105,'9:105','“Hojjadhaa; Rabbiin, ergamaan Isaatiifi mu’uminoonnis fuula dura dalagaa keessan ni arguGara beekaa wantoota hin mul’anneefi wantoota mul’ataniis fuula dura ni deebituWaan isin dalagaa turtanis isinitti odeessa” jedhi.');
INSERT INTO "apaguna" VALUES(9,106,'9:106','Warri biroo immoo kan murtii Rabbii (eeguuf) booda aanfamantu jiruYookiin isaan adabbii yookiin immoo tawbaa isaan irraa qeebalaRabbiin beekaa, ogeessa.');
INSERT INTO "apaguna" VALUES(9,107,'9:107','Warri (mu’uminoota) rakkisuuf, kufrii (babal’isuuf), gidduu mu’uminootaa addaan baasuufi nama duraan Rabbiifi ergamaa Isaatiin loleef eegumsa godhuuf jecha masjiida tolfatanis (isaanuma irraayyi)Dhugumatti “Nuti toltuu malee homaa hin feene” jedhanii kakatuRabbiin dhugumatti isaan sobdoota jechuu ragaa baha.');
INSERT INTO "apaguna" VALUES(9,108,'9:108','Gonkumaa isa (masjiidicha) keessa hin dhaabbatinDhugumatti, masjiida guyyaa jalqabaa irraa sodaa (Rabbii) irratti ijaarametu ati isa keessa dhaabbachuu haqa godhataIsa keessa namoota qulqulleeffachuu jaalatantu jiruRabbiinis qulqulleeffattoota ni jaalata.');
INSERT INTO "apaguna" VALUES(9,109,'9:109','Sila nama jaarmaya isaa sodaa Rabbi irraa ta’eefi jaalala (Isaa) irratti bu’uurse moo, nama jaarmaya isaa afaan boollaa kan cituutti dhiyaate irratti bu’uuressee ibidda jahannam keessatti isaan kufetu caala? Rabbiin ummata miidhaa hojjattoota hin qajeelchu');
INSERT INTO "apaguna" VALUES(9,110,'9:110','Ijaarsi isaanii inni isaan ijaaran yoo onneen isaanii ciccitte malee onnee isaanii keessatti munaafiqummaafi shakkii ta’uu irraa hin deemuRabbiin beekaa, ogeessa');
INSERT INTO "apaguna" VALUES(9,111,'9:111','Dhugumatti, Rabbiin mu’uminoota irraa jannanni isaaniif ta’uudhaan lubbuufi qabeenya isaanii biteeraKaraa Rabbii keessatti lolu; ni ajjeesu; ni ajjeefamusKun Tawraat, Injiiliifi Qur’aana keessatti waadaa Isa irratti mirkanaa’eedhaNamni Rabbi irra waadaa isaa guutu eenyu? Kanaafuu gurgurtaa keessan isa gurgurttaniin gammadaaSunis isatu milkii guddaadha.');
INSERT INTO "apaguna" VALUES(9,112,'9:112','(Isaan) warra tawbaa deebi’an, gabbartoota, faarsitoota, soomtota, warra rukuu’a godhan, warra sujuuda godhan, warra toltuutti ajajan, warra hamtuu irraa dhorganiifi warra daangaa Rabbiis eeganiidhaMu’uminootas (jannataan) gammachiisi.');
INSERT INTO "apaguna" VALUES(9,113,'9:113','Nabiyyichaafi warra amananiif, mushrikootaaf odoo firoota dhihoo ta’anillee erga warra jahannam ta’uun ifa isaaniif ta’ee booda, (Rabbiin) araarama kadhachuun kan malu hin taane.');
INSERT INTO "apaguna" VALUES(9,114,'9:114','Araarama abbaa isaatiif kadhachuun Ibraahiimiis waadattii ishee isaaf seene sana (guutuu) irraayyi malee waan biraa hin turreYeroma inni diina Rabbii ta’uun ifa isaaf ta’u isa irraa qulqullaa’eDhugumatti, Ibraahiim akkaan gara laafaa, obsaadha.');
INSERT INTO "apaguna" VALUES(9,115,'9:115','Rabbiin ummata tokko erga isaan qajeelchee booda hanga waan isaan eeggachuu qaban isaaniif ibsutti kan (isaan) jallisu (yakkamaa godhu) hin taaneDhugumatti, Rabbiin waan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(9,116,'9:116','Dhugumatti, Rabbiin mootummaan samiifi dachii kan IsaatiNi jiraachisa; ni ajjeesasRabbiin alatti tiksaafi tumsaantokkollee isiniif hin jiru.');
INSERT INTO "apaguna" VALUES(9,117,'9:117','Dhugumatti Rabbiin nabiyyicha, muhaajirootaafi ansaarota warra erga onneen garee isaan irraa ta’anii jallachuutti dhihaatee booda, yerottii rakkoo keessa isa hordofan irraas tawbaa qeebaleeraSana booda Rabbiin tawbaa isaanii qeebaleeraDhugumatti inni isaaniif mararfataa rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(9,118,'9:118','Warra sadeen hanga dachiin bal’ina ishee wajjin isaan irratti dhiphattee, lubbuun isaaniis isaan irratti dhiphattee, Rabbi irraas gara Isaatti malee iddoon itti baqatan hin jiruu hanga mirkaneeffatanitti booda aanfaman irraas (Rabbiin tawbaa qeebaleera)Sana booda akka isaan tawbataniif jecha tawbaa isaan irraa qeebaleDhugumatti, Rabbiin Isatu araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(9,119,'9:119','Yaa warra amantan! Rabbiin sodaadhaa; warra dhugaa dubbatan wajjiiniis ta’aa');
INSERT INTO "apaguna" VALUES(9,120,'9:120','Warra Madiinaatiifi tikfattoota baadiyyaa irraa warra moggaa isaaniitti argamaniif ergamaa Rabbii irraa duubatti hafuufi lubbuu isaaniis lubbuu isaa irra jaalachuun isaaniif hin malu tureSunis sababa hojiin gaariin ittiin isaaniif barreeffamu malee karaa Rabbii keessatti dheebuun, dadhabbiifi beellis kan isaan hin mudanne ta’ee akkasumas iddoo ejjannaa kaafiroota dallansiisu tokkoos hin ejjanneefi diina irraas (ajjeesuufi booji’uun) qaqqabiinsa tokkos kan hin qaqqabne (waan) ta’eefiDhugumatti, Rabbiin mindaa toltuu raawwattootaa hin balleessu.');
INSERT INTO "apaguna" VALUES(9,121,'9:121','Rabbiin irra gaarii waan isaan dalagaa turaniitiin isaan mindeessuuf jecha, yoo isaaniif barreeffame malee kennaa xiqqoos ta’ee guddaa hin kennan; laga tokkos hin qaxxaamuran');
INSERT INTO "apaguna" VALUES(9,122,'9:122','Mu’uminoonni (nabiyyii wajjin yoo ta’e malee) dimshaashatti duuluun isaaniin hin maluSila maaliif garee hunda irraa muraasni tokko hin lolle? (Warri biroo immoo) akka amantii keessatti hubannoo godhatanii yeroo gara maatii isaaniitti deebi’an, akka isaan dinniinaniif (gorsaniif) jecha (maaliif hin hafne).');
INSERT INTO "apaguna" VALUES(9,123,'9:123','Yaa warra amantan! Kuffaara irraa warra isinitti aananiin lolaaIsin keessattis jabeenya haa arganuRabbiinis warra Isa sodaatan wajjin jiraachuu beekaa.');
INSERT INTO "apaguna" VALUES(9,124,'9:124','Yeroo suuraan tokka buutes isaan irraa nama “eenyuu keessaniif isheen kun iimaana dabalte?” jedhutu jiraEgaa isaan amananiif kan gammadanu ta’anii iimaana isaaniif dabalte.');
INSERT INTO "apaguna" VALUES(9,125,'9:125','Isaan onnee isaanii keessa dhukkubni jiru immoo najisa isaanii irratti najisa isaaniif dabalteIsaanis kaafiroota ta’anii du’an.');
INSERT INTO "apaguna" VALUES(9,126,'9:126','Sila isaan waggaa hunda keessa yeroo tokko yookiin yeroo lama mokkoramuu isaanii hin arganuu? Ergasiis hin tawbatanii? Isaanis hin gorfamanuu?');
INSERT INTO "apaguna" VALUES(9,127,'9:127','Yeroo suuraan (isaan saaxiltu) tokko buufamte “namni tokko sila isin argaa jiraa?” jedhaa gariin isaanii gara garii ilaalaSana booda garagalanii deemuWaan isaan ummata hubannoo hin qabne ta’aniif Rabbiin onnee isaanii gara galche.');
INSERT INTO "apaguna" VALUES(9,128,'9:128','Dhugumatti, ergamaan isin irraa ta’e, kan rakkinni keessan isa irratti jabaatu, kan isiniif (akka amantanuuf) bololu, mu’uminootaaf immoo mararfataa, rahmata godhaa ta’e isinitti dhufeera.');
INSERT INTO "apaguna" VALUES(9,129,'9:129','Yoo isaan (sirraa) garagalan, “Rabbiin naaf gahaadhaIsa malee haqaan Gabbaramaan hin jiruIsa qofa irrattin irkadhe; Innis Gooftaa arshii guddaati” jedhi.');
INSERT INTO "apaguna" VALUES(10,1,'10:1','Alif Laam RaaSunniin keeyyattoota kitaabicha ogummaan guutameeti');
INSERT INTO "apaguna" VALUES(10,2,'10:2','Sila gara nama isaan irraa ta’ee “nama akeekkachiisi; warra amanes toltuun dabre Rabbii isaanii bira isaaniif jiraachuudhaan gammachiisi” jechuun wahyii godhuun keenya namootatti dinqii ta’eeraa? Kaafirtoonni: “Dhugumatti, inni kun falfalaa ifa bahaadha” jedhan');
INSERT INTO "apaguna" VALUES(10,3,'10:3','Dhugumatti, Gooftaan keessan Allaah Isa samiifi dachii guyyoota jaha keessatti uumee, ergasii Arshii irratti (ol ta’iinsa isaaf malu) ol ta’eedhaWaan hundaa garagaggalchaAraarsaan homaatuu hayyama Isaatiin booda malee hin jiruKun Rabbii Gooftaa keessan ta’eedhaKanaafuu Isa qofa gabbaraaSila isin hin gorfamtanuu?');
INSERT INTO "apaguna" VALUES(10,4,'10:4','Deebiin hunda keessanii gara IsaatiWaadaa Rabbiin (beellamee dha)Dhugaa gochuus (isa godhe)Dhugumatti, Isatu uumuu jalqabee, ergasiis isa deebisaIsaan amananii, toltuu dalaganiif haqaan mindaa isaaniif kennuuf (akkas godhe)Isaan kafaran waan kafaraniif jecha isaanif dhugaatii bishaan danfaa irraa ta’eefi adabbii laalessaatu jira.');
INSERT INTO "apaguna" VALUES(10,5,'10:5','Inni Isa aduu madda ifaa, baatiis ifa taasisee, akka lakkoofsa waggootaafi herregaa beektaniif jecha bakka isaaf (Ji’aaf) godheedhaRabbiin kana dhugaan malee hin uumne(Rabbiin) keeyyattoota namoota beekaniif addeessa.');
INSERT INTO "apaguna" VALUES(10,6,'10:6','Dhugumatti, bakka wal bu’uu halkaniifi guyyaa keessa, waan Rabbiin samiifi dachii keessatti uume keessaas namoota (Rabbiin) sodaataniif mallattooleetu jira');
INSERT INTO "apaguna" VALUES(10,7,'10:7','Dhugumatti, isaan qunnamtii keenya hin sodaanne, kan jireenya addunyaa qofa fedhanii, isheetti gammadaniifi isaan keeyyattoota keenya irraa dagatoo ta’an,');
INSERT INTO "apaguna" VALUES(10,8,'10:8','Isaan sun waan hojjachaa turaniif jecha teessoon isaanii ibidda');
INSERT INTO "apaguna" VALUES(10,9,'10:9','Dhugumatti, isaan amananii, gaggaarii hojjatan Gooftaan isaanii iimaana isaanitiin isaan qajeelchaJannatoota qananii keessa laggeen isaan jala yaati.');
INSERT INTO "apaguna" VALUES(10,10,'10:10','Kadhaan isaanii ishee keessatti (yeroo waan tokko fedhan)[] “yaa Allaah! Qulqulliini keeti” jechuudhaKabajaan isaaniis ishee keessatti “nagayaa” jechuudhaKadhaan isaanii kan boodaas[] “faaruun hundi kan Rabbii gooftaa aalamaati” jechuudha.');
INSERT INTO "apaguna" VALUES(10,11,'10:11','Odoo Rabbiin namaaf akka isaan gaarii (argachuuf) jerjerfatanitti hamtuu isaaniif jerjersee, silaa beellamni (adabbii) isaanii, isaaniif raawwatama tureIsaan wal ga’ii keenya hin sodaanne, jallina isaanii keessa dhama’oo taasifnee isaan dhiifna');
INSERT INTO "apaguna" VALUES(10,12,'10:12','Ilmi namaa yeroo rakkinni wahii isa tuqe, cinaacha isaa irrattis yookiin taa’eetis yookiin dhaabbateetis Nu kadhataYeroo Nuti rakkina isaa isa irraa fuune akka waan rakkina isa tuqeef Nu hin kadhatiniitti (kufrii isaa) itti fufaAkka kanatti daangaa dabartootaaf wanti isaan hojjachaa turan miidhagfame');
INSERT INTO "apaguna" VALUES(10,13,'10:13','Dhugumatti, dhaloota isin duraa irraa, yeroo isaan lubbuu isaanii miidhanii, ergamtoonni isaanii raajii dirree taa’uun isaanitti dhufanii hin amanin balleessinee jirraAkka kanatti ummata badii raawwattoota ta’aniif galata galchina.');
INSERT INTO "apaguna" VALUES(10,14,'10:14','Ergasii isaan booda akkam akka dalagdan ilaaluuf jecha dachii keessatti bakka bu’oota isin taasifne');
INSERT INTO "apaguna" VALUES(10,15,'10:15','Yeroo keeyyattoonni keenya ifa ta’anii isaan irratti dubbifamanis, isaan qunnamtii keenya hin sodaanne “Qur’aana kana hin ta’iniin nutti kottu; yookiin isa jijjiiri” jedhu“Ani of biraa isa jijjiiruun naaf hin ta’uWaan gara kiyyatti wahyii godhamu malee hin hordofuDhugumatti, ani yoon ajaja Gooftaa kiyyaa dide, adabbii guyyaa guddaan sodaadha” jedhi.');
INSERT INTO "apaguna" VALUES(10,16,'10:16','“Odoo Rabbiin fedhee, silaa ani isa isin irratti hin dubbisuInnis (Rabbis) isa isin hin beeksisuDhugumatti, ani isa duras yeroo dheeraa isin keessa jiraadheeraSila isin sammuu hin qabdanii?” jedhiin.');
INSERT INTO "apaguna" VALUES(10,17,'10:17','Nama Rabbi irratti kijiba uume yookiin kan keeyyattoota Isaa sobsiise caalaa namni miidhaa hojjate eenyu? Dhugumatti, badii raawwattoonni hin milkaa’anu');
INSERT INTO "apaguna" VALUES(10,18,'10:18','Rabbiin ala waan isaan hin miineefi hin fayyadne gabbaranii “Isaan kun Rabbiin biratti manguddoota keenya” jedhu“Sila isin waan Inni samiifi dachii keessatti hin beekne Rabbitti himtuu? Inni (Rabbiin) waan isaan Isa waliin gabbaran irraa qulqullaa’e; olis ta’ee jira” jedhi.');
INSERT INTO "apaguna" VALUES(10,19,'10:19','Ilmi namaa ummata tokko malee hin turreBooda irra wal dhabanOdoo jechi Gooftaa kee irraa ta’e dursee dabruu baatee, silaa waan isaan irratti wal dhaban keessatti gidduu isaaniitti ni murteeffama ture.');
INSERT INTO "apaguna" VALUES(10,20,'10:20','“Mallattoon Gooftaa isaa irraa maaliif isa irratti hin buufamin?” jedhu“Wanti fagoo hundi RabbumaafiIsinis eegaa; Anis isin wajjin warra eegan irraayi” jedhiin.');
INSERT INTO "apaguna" VALUES(10,21,'10:21','Yeroo namoota erga rakkoon isaan tuqee booda rahmata isaan dhandhamsiifne, yeroma san keeyyattoota keenya keessatti tooftaa baasuun isaaniif ta’aadha“Rabbitu tooftaatti isaan irra akkaan jarjaraadha” jedhiDhugumatti, ergamtoonni keenya (maleykonni) tooftaa isin baaftan ni barreessu.');
INSERT INTO "apaguna" VALUES(10,22,'10:22','Inni Isa dachiifi bishaan keessas isin deemsisuYeroo dooniwwan keessatti taatanii qilleensa gaarii keessa isaaniin deemanii, isheetti gammadan, qilleensi bubbisaan isaanitti dhufa; danbaliiniis bakka hunda irraa isaanitti dhufa; marfamnees yaadu“Yoo Ati (balaa) kana irraa nagaa nu baafte, nuti dhugumatti, warra (si) galateeffatan irraa taana” (jechaa) ibaadaa Isaaf qulqulleessanii Rabbiin qofa kadhatu.');
INSERT INTO "apaguna" VALUES(10,23,'10:23','Yeroma Inni nagaa isaan baase, isaan yoosuu haqaan ala dachii keessatti daangaa dabruYaa namootaa! Daangaa dabriinsi keessan isinuma irrattiQananii jireenya addunyaa (xiqqoo qananiitu)Sana booda deebiin keessan gara keenyattii, Nuti waan isin dalagaa turtan isinitti himna.');
INSERT INTO "apaguna" VALUES(10,24,'10:24','Dhugumatti, fakkeenyi jireenya addunyaa, akka bishaan Nuti samii irraa isa buufnee, ergasii biqiltuuwwan dachii kan waan ilmi namaatiifi horiin nyaatan irraa ta’an isaan wal makamanii, yeroo dachiin faaya ishee qabattee, miidhagdee, abbootiin isheetis ishee irratti danda’oo ta’uu yaadan, adabbiin keenya halkan yookiin guyyaa isheetti dhufee, akka waan kaleessa achitti hin ta’iniitti (hin jirreetti) haamamaa ishee gooneetiAkka kanatti mallattoolee ummata xiinxalaniif adda baafna.');
INSERT INTO "apaguna" VALUES(10,25,'10:25','Rabbiin gara mana nagayaatti waamaNama fedhes gara karaa qajeelaati qajeelcha.');
INSERT INTO "apaguna" VALUES(10,26,'10:26','Isaan toltuu hojjataniif (mindaa) gaariifi dabalataatu jiraGurraachinniifi salphinni homaatuu fuula isaanii hin haguuguIsaan sun warra jannataatiIsaan ishee keessatti hafoodha');
INSERT INTO "apaguna" VALUES(10,27,'10:27','Isaan hamtuu hojjatanis mindaan hamtuu fakkaatuma isheetiin ta’aSalphinnis isaan haguugdiRabbi irraa wanti isaan tiksus isaaniif hin jiruFuulli isaanii akka waan kutaa halkan dukkaneessaa ta’e irraa ta’een hagoogamanii fakkaattiIsaan sun warra ibiddaatiIsaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(10,28,'10:28','Guyyaa isaan hunda walitti qabnus, (dubbadhu)Ergasii isaan Nutti qindeessaniin ni jenna: “Isiniifi warri isin Nu waliin gabbaraa turtanis iddoo jirtan dhaabadhaa!” Sana booda gidduu isaanii addaan baafnaWarri isaan Nu waliin gabbaraa turaniis: “Isin nu gabbaraa hin turre” jedhu');
INSERT INTO "apaguna" VALUES(10,29,'10:29','“Gidduu keenyaafi gidduu keessanitti ragaa ta’uuf Rabbumatu ga’aadhaDhugumatti, nuti isin nu gabbaruu irraa dagatoo taanee turre.”');
INSERT INTO "apaguna" VALUES(10,30,'10:30','Achitti lubbuun hundi waan dabarsite ni beekkattiGara Rabbii Gooftaa isaanii kan dhugaa deebifamuSobni isaan uummachaa turan isaan irraa bada.');
INSERT INTO "apaguna" VALUES(10,31,'10:31','“Eenyutu samiifi dachii irraa isin soora? Yookiin dhageettiifi argaa eenyutu dhuunfata? Ammas eenyutu jiraataa du’aa irraa, du’aas jiraataa irraa baasa? Eenyutu waan hunda gara gaggalchas?” jedhi‘Rabbuma’ jechuuf jiruu“Sila Rabbiin hin sodaattanii?” jedhiin.');
INSERT INTO "apaguna" VALUES(10,32,'10:32','Sun Allaah, Gooftaa keessan, kan dhugaatiDhugaa booda jallina malee maaltu jira? Akkamumatti irraa gara galfamtu?');
INSERT INTO "apaguna" VALUES(10,33,'10:33','Akka kanatti jechi Gooftaa keetii, warra fincilan irratti amanuu dhabuun mirkanoofte.');
INSERT INTO "apaguna" VALUES(10,34,'10:34','“Sila warra isin Rabbii wajjiin gabbartan irraa warri uumama jalqabee, ergasii deebisu jiraa?” jedhi“Rabbiin uumama jalqabeeti, ergasiis deebisaAkkamumatti gara galfamtu?” jedhi.');
INSERT INTO "apaguna" VALUES(10,35,'10:35','“Sila warra isin Rabbii wajjin gabbartan irraa warri gara haqaatti qajeelchu jiraa?” jedhi“Rabbiin gara haqaatti qajeelchaSila Isa gara haqaatti qajeelchutu hordofamuu mirga godhatamoo isa yoo qajeelfame malee, qajeeluu hin dandeenyee dha? Sila isin maaltu isinii jiraaf akkuma feetan murteessitu?” jedhi.');
INSERT INTO "apaguna" VALUES(10,36,'10:36','Irra hedduun isaaniis shakkii malee hin hordofanDhugumatti, shakkiin dhugaa irraa homaa hin duroomsuDhugumatti, Rabbiin beekaa waan isaan hojjataniiti.');
INSERT INTO "apaguna" VALUES(10,37,'10:37','Qur’aanni kun Rabbii gaditti sobaan kan uumamu hin taaneGaruu dhugoomsaa (kitaabota) isa dura turaniitiifi addeessaa (seeraati)Gooftaa aalama hundaa irraa ta’uu isaa mamiin tokkos isa keessa hin jiru.');
INSERT INTO "apaguna" VALUES(10,38,'10:38','Moo sila isaan “isatu (Muhammadtu) of irraa isa uume” jedhu? “Oggas suuraa tokko kan isa fakkaattu fidaaYoo kan dhugaa dubbattan taatan, Rabbii gaditti nama dandeessan (gargaarsaaf) waamadhaa” jedhi.');
INSERT INTO "apaguna" VALUES(10,39,'10:39','Garuu isaan waan beekumsa isaatiin hin marsiniifi waan hiikkaan isaa amma dura itti hin dhufiniyyi kijibsiisuWarri isaan duraas akkuma kanatti kijibsiisanBooddeen miidhaa raawwattootaa akkam akka ta’e ilaali.');
INSERT INTO "apaguna" VALUES(10,40,'10:40','Isaan irraas namoota isatti amanantu jiraIsaan irraas namoota isatti hin amannetu jiraGooftaan keetis badii hojjattoota akkaan beekaadha.');
INSERT INTO "apaguna" VALUES(10,41,'10:41','Yoo si kijibsiisaniis “Anaaf dalagaa kiyyatu jira; isiniifis dalagaa keessantu jiraIsin waan ani dalagu irraa qulqullaa’oodha; anis waan isin dalagdan irraa qulqulluudha” jedhi.');
INSERT INTO "apaguna" VALUES(10,42,'10:42','Isaan irraas namoota gara kee dhageeffatantu jiraSila ati namoota gurri duude, odoo isaan sammuu hin qabne dhageessiftaa?');
INSERT INTO "apaguna" VALUES(10,43,'10:43','Isaan irraas namoota gara kee ilaalantu jiraSila ati jaamaa, odoo isaan hin argine isaan qajeelchitaa?');
INSERT INTO "apaguna" VALUES(10,44,'10:44','Dhugumatti, Rabbiin namoota homaa hin miidhuGaruu namoonni ofiif of miidhu.');
INSERT INTO "apaguna" VALUES(10,45,'10:45','Haala akka waan yeroo guyyaa irraa taate tokko malee hin turin itti fakkaatuun, odoo gidduu isaaniitti wal beekanuu guyyaa (Rabbiin) walitti isaan qabu [yaadadhu]Dhugumatti, warri qunnamtii Rabbii sobsiisan hoonga’aniiru; qajeeloos hin taane.');
INSERT INTO "apaguna" VALUES(10,46,'10:46','Garii waan waadaa isaaniif gallee yoo sitti argisiifne (toleera); yookiin yoo si ajjeefnes deebiin isaanii garuma keenya ta’aErgasii Rabbiin waan isaan dalagan irratti ragaadha.');
INSERT INTO "apaguna" VALUES(10,47,'10:47','Ummata hundaafis ergamaan (ergamee) jiraYeroo ergamaan isaanii dhufe odoo hin miidhamne jidduu isaaniitti haqaan murteeffama.');
INSERT INTO "apaguna" VALUES(10,48,'10:48','“Yoo kan dhugaa dubbattan taataniif waadaan kun yoom ta’a?” jedhu.');
INSERT INTO "apaguna" VALUES(10,49,'10:49','“Waan Rabbiin fedhe malee ani lubbuu kiyyaaf miidhaas ta’ee bu’aas fiduu hin danda’uUmmata hundaaf beellamatu jiraYeroo beellamni isaanii dhufe, sa’aatii tokkos booda hin aanan; duras hin dabran.”');
INSERT INTO "apaguna" VALUES(10,50,'10:50','“Mee naaf himaa! yoo adabbiin Isaa halkan yookiin guyyaa isinitti dhufe, badii raawwattoonni isa irraa maal jarjarfatu?” jedhi');
INSERT INTO "apaguna" VALUES(10,51,'10:51','“Sila ogga inni argamee booda isatti amantuu? Sila odoo duraan jarjarfataa turtanii amma amantuu?” (jedhamaan).');
INSERT INTO "apaguna" VALUES(10,52,'10:52','Ergasii isaan miidhaa hojjataniin ni jedhama: “Adaba yeroo hundaa dhandhamaa! Sila waan hojjataa turtaniin malee ni mindeeffamtuu?”');
INSERT INTO "apaguna" VALUES(10,53,'10:53','“Sila inni (Guyyaan Qiyaamaa) dhugaa dhaa?” (jechuun) si odeeffatu“Eeyyeen! Gooftaa kiyyatti kakadhee! Inni dhugaadhaIsinis (isa jalaa) miliquu hin dandeessan” jedhi.');
INSERT INTO "apaguna" VALUES(10,54,'10:54','Odoo lubbuu miidhaa raawwatte hundaaf wanti dachii keessa jiru (hundi) ta’eefii isaan of baaftiYeroo adabicha arganis sheenaa ni mul’ifatuGidduu isaaniittis odoo hin miidhamne haqaan murteeffama.');
INSERT INTO "apaguna" VALUES(10,55,'10:55','Dhagayaa! Dhugumatti, wanti samiifi dachii keessa jiru kan RabbiitiDhagayaa! Dhugumatti, waadaan Rabbii dhugaadhaGaruu irra baay’een namootaa hin beekan');
INSERT INTO "apaguna" VALUES(10,56,'10:56','Inni ni jiraachisa; ni ajjeesas; gara Isaatittis deebifamtu.');
INSERT INTO "apaguna" VALUES(10,57,'10:57','Yaa namootaa! Dhugumatti, gorsi Gooftaa keessan irraa taate, qorichi waan qoma keessaas, qajeelfamaafi rahmanni mu’uminootaaf taate isinitti dhufte');
INSERT INTO "apaguna" VALUES(10,58,'10:58','“Tola Rabbiitiifi rahmata Isaatiin (dhufte)Waan isaan walitti qaban irra isatu caalaa, isa sanitti haa gammadanu” jedhi.');
INSERT INTO "apaguna" VALUES(10,59,'10:59','“Soorata irraa waan Rabbiin isiniif buuse, isin ergasii isa irraa dhorgamaafi hayyamamaa taasiftan san mee naaf himaa?” jedhi“Sila Rabbitu isiniif hayyamemoo, Rabbi irratti kijiba uumtanii odeessitu?” jedhi.');
INSERT INTO "apaguna" VALUES(10,60,'10:60','Sila isaan Rabbi irratti kijiba odeessan Guyyaa Qiyaamaa maal yaadu? Dhugumatti, Rabbiin namoota irratti abbaa tola (guddaa)tiGaruu irra hedduun isaanii hin galateeffatanu.');
INSERT INTO "apaguna" VALUES(10,61,'10:61','(Yaa Muhammad!) Hojii kamillee keessatti hin taatu, Qur’aana irraas isa hin dubbiftu, dalagaa irraa waan tokkos hin hojjattan, yeroo isin isatti seentan Nuti isin irratti ragaa taanu malee hin hafuWanti hanga atomii dachii keessattis ta’ee, samii keessattis Rabbii kee irraa hin fagaatuWanti san irra xiqqaas ta’ee, guuddaan kitaaba addeessaa ta’e keessatti jiraatu malee hin hafu');
INSERT INTO "apaguna" VALUES(10,62,'10:62','Dhagayaa! Dhugumatti, jaalalleen Rabbii sodaan isaan irra hin jiraatu; isaan hin gaddanis.');
INSERT INTO "apaguna" VALUES(10,63,'10:63','Isaan warra amananii, (Rabbiin) sodaachaa turaniidha.');
INSERT INTO "apaguna" VALUES(10,64,'10:64','Isaaniif jireenya addunyaa keessattis ta’ee kan Aakhiraa keessatti gammachuutu jiraJechoota Rabbii jijjiiraan hin jiruSun isumatu milkii guddaadha');
INSERT INTO "apaguna" VALUES(10,65,'10:65','Jechi isaanii si hin gaddisiisinDhugumatti, jabeenyi hunduu kan RabbiitiInni dhaga’aa, beekaadha.');
INSERT INTO "apaguna" VALUES(10,66,'10:66','Dhagayaa! Dhugumatti, wanti samii keessa jiruufi wanti dachii keessa jiru kan RabbiitiIsaan Rabbiin ala wantoota biraa gabbaranis, dhugaatti wantoota Rabbiin ala gabbaran hin hordofanuWanti isaan hordofan mamii qofa; isaan kijiba qofa uumanii odeessu.');
INSERT INTO "apaguna" VALUES(10,67,'10:67','Inni (Rabbiin) Isa halkan akka isa keessa boqattan, guyyaa immoo kan ibsu isiniif taasiseedhaDhugumatti, kana keessa namoota dhagayaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(10,68,'10:68','“Rabbiin ilma godhate” jedhuInni (kana) irraa qulqullaa’ee jiraInni durreessaWanti samii keessa jiruufi wanti dachii keessa jiru kan IsaatiKanatti ragaa homaatuu hin qabdanSila waan hin beekne Rabbi irratti jettuu?');
INSERT INTO "apaguna" VALUES(10,69,'10:69','“Dhugumatti, isaan Rabbi irratti kijiba uumanu hin milkaa’anu” jedhi.');
INSERT INTO "apaguna" VALUES(10,70,'10:70','(Kun) qananii addunyaa keessatti taateedhaErgasii deebiin isaanii gara keenyatti qofa ta’aErgasii waan isaan kafaraa turaniif adabbii cimaa isaan dhandhamsiifna.');
INSERT INTO "apaguna" VALUES(10,71,'10:71','Oduu Nuuh isaan irratti dubbisiYeroo inni ummata isaatiin “yaa ummata kiyya! Yoo (isin waliin) turuun kiyyaafi keeyyattoota Rabbiitiin (isin) yaadachiisuun kiyya isinitti guddate, ani Rabbi irratti hirkadhee, tooftaa keessaniifi isaan Rabbii waliin gabbartani walitti qabadhaa; ergasii dharti keessan isin irraa dhokataa hin ta’in; ergasii yeroo naaf hin kenninaatii narratti raawwadhaa”jedhe.');
INSERT INTO "apaguna" VALUES(10,72,'10:72','“Yoo gara galtan, mindaa homaatuu isin hin gaafadhuMindaan kiyya Rabbi irratti malee hin taaneAni Muslimtoota irraa ta’uuttiin ajajame.”');
INSERT INTO "apaguna" VALUES(10,73,'10:73','Isaan isa kijibsiisani; ergasii Nuti isaafi namoota isaa waliin doonii keessa jiranis ni baraarsineBakka bu’oo isaan gooneeti warra keeyyattoota keenya sobsiisan bishaaniin dhidhimsineBooddeen dinniinamtootaa akkam akka ta’e ilaali.');
INSERT INTO "apaguna" VALUES(10,74,'10:74','Ergasii isa booda ergamtoota gara ummata isaanii ni erginee, mallattoowwan ifa ta’aniin isaanitti dhufanIsaan waan san dura kijibsiisan sanatti kan amanan hin taaneAkka kanatti onnee daangaa dabritootaa irratti cufna.');
INSERT INTO "apaguna" VALUES(10,75,'10:75','Ergasii isaan booda Muusaafi Haaruuniin gara Fir’awniifi qondaalota isaa keeyyattoota keenyaan erginee jirraIsaan boonaniiti, badii raawwattoota irraas ta’an.');
INSERT INTO "apaguna" VALUES(10,76,'10:76','Yeroo dhugaan Nu biraa isaanitti dhufu “dhugumatti, kun falfala ifa ba’aadha” jedhan.');
INSERT INTO "apaguna" VALUES(10,77,'10:77','Muusaanis ni jedhe “sila dhugaan isinitti dhufnaan (kana) jettuu? Sila inni kun falfalaa? Falfaltoonni hin milkaa’an.”');
INSERT INTO "apaguna" VALUES(10,78,'10:78','Isaan ni jedhan: “Sila ati waan abbootii keenya isa irratti argine irraa nu garagalchitee ol aantummaanis dachii keessatti akka isin lamaaniif taatuuf nutti dhuftee? Nuti isin lamaaniif hin amannu.');
INSERT INTO "apaguna" VALUES(10,79,'10:79','Fir’awnis ni jedhe: “Cufa falfalaa beekaatiin natti koottaa.”');
INSERT INTO "apaguna" VALUES(10,80,'10:80','Yeroo falfaltoonni dhufan, Muusaan isaaniin ni jedhe: “Waan darbuu feetan darbaa.”');
INSERT INTO "apaguna" VALUES(10,81,'10:81','Yeroo isaan darban Muusaan ni jedhe: “Wanti isin isaan dhuftan falfalaa, dhugumatti, Rabbiin isa balleessuuf jiraDhugumatti, Rabbiin dalagaa badii hojjattootaa hin tolchu.');
INSERT INTO "apaguna" VALUES(10,82,'10:82','Odoo badii raawwattoonni jibbanuu, Rabbiin dhugaa jechoota Isaatiin mirkaneessa.”');
INSERT INTO "apaguna" VALUES(10,83,'10:83','Fir’awniifi qondaaltonni isaa isaan jallisuu sodaachuu irraa kan ka''e, dhaloota ummata Muusaa irraa ta’e malee Muusaaf hin amanneDhugumatti, Fir’awn dachii keessatti of tuulaadhaAmmas inni daanga dabritoota irraayi.');
INSERT INTO "apaguna" VALUES(10,84,'10:84','Muusaanis ni jedhe: “Yaa ummata kiyya! Yoo kan Rabbitti amantan taatan, Isuma irratti hirkadhaa; yoo Muslimoota taatan.”');
INSERT INTO "apaguna" VALUES(10,85,'10:85','Isaanis ni jedhan: “Nuti Rabbi irratti hirkannee jirraYaa Gooftaa keenya! Ummata miidhaa raawwattootaaf sababaa jallinnaa nu hin taasisin');
INSERT INTO "apaguna" VALUES(10,86,'10:86','Namoota kaafirootaa jalaas Rahmata keetiin nagaa nu baasi.”');
INSERT INTO "apaguna" VALUES(10,87,'10:87','Muusaafi obboleessa isaattis: “Isin lachanuu ummata keessaniif Misratti manneen qopheessaa; manneen keessanis iddoo ibaadaa taasisaa; salaatas dhaabaa; mu’uminootas gammachiisi” jechuu wahyii goone.');
INSERT INTO "apaguna" VALUES(10,88,'10:88','Muusaanis ni jedhe: “Gooftaa keenya! Ati dhugumatti Fir’awniifi qondaalota isaatiif jireenya addunyaa keessatti, (yaa) Gooftaa keenya! Karaa kee irraa akka (namoota) jallisaniif jecha miidhaginaafi qabeenya kennitee jirtaGooftaa keenya! Qabeenya isaanii mancaasi; onnee isaanii irrattis jabeesiIsaan hanga adabbii laalessaa arganitti hin amanan.”');
INSERT INTO "apaguna" VALUES(10,89,'10:89','(Rabbiin) ni jedhe: “Dhugumatti, kadhaan keessan lamaanii fudhatama argattee jirtiKaraa qajeelaa irratti gadi dhaabbadhaa; karaa warra hin beeknees hin hordofinaa.”');
INSERT INTO "apaguna" VALUES(10,90,'10:90','Ilmaan Israa’iil galaana ceesifneeti, Fir’awniifi waraanni isaas jibbaafi diinummaaf isaan hordofan; yeroo bishaaniin nyaatamuun isa dhaqqabu “dhugumatti, Isa Ilmaan Israa’iil itti amanan malee haqaan gabbaramaan biraa hin jiruu amaneeraAnis Muslimtoota irraayi” jedhe.');
INSERT INTO "apaguna" VALUES(10,91,'10:91','Sila odoo duraan diddee, balleessitoota irraa taatee jirtuu amma (amantaa)!');
INSERT INTO "apaguna" VALUES(10,92,'10:92','Har’a qaama keetiin (reeffa kee) akka ati namoota si boodaatiif mallattoo taatuuf si baafnaDhugumatti, hedduun namoota irraa ta’an mallattoolee keenya irraa dagatoodha.');
INSERT INTO "apaguna" VALUES(10,93,'10:93','Nuti dhugumatti, Ilmaan Israa’iil iddoo kabajamaa (Shaamiifi Misraan keessa) isaan qubachiifnee jirraWantoota gaggaarii irraas soorata isaanif kenninee jirraHanga beekumsi isaanitti dhufutti wal hin dhabneDhugumatti, Gooftaan kee guyyaa Qiyaamaa waan isaan keessatti wal dhabaa turan keessatti gidduu isaaniitti ni murteessa.');
INSERT INTO "apaguna" VALUES(10,94,'10:94','Yoo waan Nuti gara keetti buufne irraa shakkii keessa jiraatte, isaan si dura kitaaba dubbisaa turan gaafadhuDhugumatti, haqni Gooftaa kee irraa sitti dhufee jiraa, shakkitoota irraa hin ta’in.');
INSERT INTO "apaguna" VALUES(10,95,'10:95','Isaan keeyyattoota Rabbii kijibsiisan irraas hin ta’in; hoongoftoota irraa taataa.');
INSERT INTO "apaguna" VALUES(10,96,'10:96','Dhugumatti, warri jechi Gooftaa keetii isaan irratti mirkanoofte hin amanan.');
INSERT INTO "apaguna" VALUES(10,97,'10:97','Odoo mallattoon hundi isaanitti dhufanillee hanga adabbii laalessaa arganitti (hin amanan).');
INSERT INTO "apaguna" VALUES(10,98,'10:98','Sila magaalaan (erga adabbii Rabbii argitee) amantee ergasii iimaanni ishee ishee fayyade ummata Yuunus malee maaliif hin jiraatini? (Hin jiru jechuu dha)Isaan amannaan jireenya addunyaa keessatti adabbii salphisaa ta’e isaan irraa fuunee, hanga yeroo murtaa’eettis isaan qanansiifne.');
INSERT INTO "apaguna" VALUES(10,99,'10:99','Gooftaan kee odoo fedhee namni dachii keessa jiru hundi ni amana tureSila ati hanga isaan mu’uminoota ta’anitti namoota dirqisiiftaa?');
INSERT INTO "apaguna" VALUES(10,100,'10:100','Lubbuu kamiifuu hayyama Rabbiitiin ala amanuun hin taaneIsaan sammuu hin qabne irrattiis adabbii taasisa.');
INSERT INTO "apaguna" VALUES(10,101,'10:101','“Mee wantoota samiifi dachii keessa jiran (hunda) ilaalaa” jedhiMallattooleefi akeekkachiiftonnis namoota hin amanne irraa homaa hin deebisanu.');
INSERT INTO "apaguna" VALUES(10,102,'10:102','Sila fakkaataa guyyoota warra isaan dura dabranii malee maal eegu? “Egaa, anis isin waliin eegdota irraayi” jedhi');
INSERT INTO "apaguna" VALUES(10,103,'10:103','Ergasii ergamtoota keenyaafi isaan amananis nagaa baafna! Akka kanatti mu’uminoota nagaa baasuun Nu irratti haqa.');
INSERT INTO "apaguna" VALUES(10,104,'10:104','“Yaa namootaa! Yoo amantii kiyya irraa shakkii keessa taatan, isaan isin Rabbii gaditti gabbartan san hin gabbaru; garuu Rabbii isa isin ajjeesun gabbaraAni mu’uminoota irraa ta’uuttin ajajame” jedhi.');
INSERT INTO "apaguna" VALUES(10,105,'10:105','“Fuula kee qajeelaa taasisii guutumatti amantiitti gara galchi; mushrikoota irraas hin ta’in” (jechuuttiin ajajame).');
INSERT INTO "apaguna" VALUES(10,106,'10:106','Rabbii gaditti waan si hin fayyadneefi si hin miine hin gabbarinYoo (akkas) hojjatte, dhugumatti ati yoos miidhaa hojjattoota irraa taata.”');
INSERT INTO "apaguna" VALUES(10,107,'10:107','Yoo Rabbiin rakkinaan si tuqes, Isa malee wanti isa fuudhu hin jiru; yoo toltuun si fedhees, tola Isaa wanti deebisu hin jiruIsaan (tola saniin) gabroota Isaa irraa nama fedhe tuqaInnis araaramaa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(10,108,'10:108','“Yaa namootaa! Dhugumatti, Gooftaa keessan irraa dhugaan isinitti dhufee jiraKanaafuu namni qajeele, kan inni qajeelu lubbuma ofiitiifiNamni jallates, kan inni jallatu isuma irrattiAnis isin irratti bakka bu’aa miti” jedhi.');
INSERT INTO "apaguna" VALUES(10,109,'10:109','Waan gara keetti wahyii godhames hordofiHanga Rabbiin murtii kennuttis obsiInnis irra caalaa murteessitootaati.');
INSERT INTO "apaguna" VALUES(11,1,'11:1','Alif Laam Raa(Qur’aanni kun) kitaaba keeyyatoonni isaa sirriitti qindeeffamtee, sana booda (Rabbii) ogeessa, keessa beekaa ta’e biraa addaan baafamteedha');
INSERT INTO "apaguna" VALUES(11,2,'11:2','“Rabbiin malee waan biraa hin gabbarinaa” jechuun (addaan baafamte)Dhugumatti, ani isiniif Isa irraa akeekkachiisaafi gammachiisaadha');
INSERT INTO "apaguna" VALUES(11,3,'11:3','“Gooftaa keessanis araarama kadhadhaa; ergasiis gara Isaatti tawbaa deebi’aaInni hanga beellama beekamaatti qananii bareedaa isin qanansiisaAbbootii tolaa hundaafis tola (mindaa) isaa ni kenna” jechuun (addaan baafamte)Yoo (irraa) gara galan, ani adabbii guyyaa guddaan isin irratti sodaadha.');
INSERT INTO "apaguna" VALUES(11,4,'11:4','Deebiin keessan gara RabbiittiInnis waan hunda irratti danda’aadha');
INSERT INTO "apaguna" VALUES(11,5,'11:5','Dhagayaa! Dhugumatti, isaan Isa jalaa dhokachuuf qoma isaanii maruDhagayaa! yeroo isaan uffata isaanii haguuggatan, (Rabbiin) waan isaan dhoksaniifi waan isaan ifa godhanis ni beekaDhugumatti, Inni beekaa waan qoma keessa jiruuti');
INSERT INTO "apaguna" VALUES(11,6,'11:6','Lubbu qabeeyyiin tokkollee sooranni ishee Rabbi irratti ta’u malee dachii keessatti hin taaneIddoo jireenya isheetiifi iddoo du’a ishees ni beekaHundumtuu kitaaba ifa ta’e keessa jira');
INSERT INTO "apaguna" VALUES(11,7,'11:7','Inni (Rabbiin) Isa samiifi dachii guyyoota jaha keessatti uumeedhaArshiin Isaas bishaan gubbaa tureEenyuu keessantu dalagaan isaa irra gaarii ta’uu isin qormaatuuf (isaan uume)Yoo ati “Dhugumatti isin du’a booda kaafamoodha” jetteen, isaan kafaran “kun sihrii ifa ta’e malee waan biraatii miti” jedhu');
INSERT INTO "apaguna" VALUES(11,8,'11:8','Adabichas yoo hanga beellama murtaa’eetti isaan irraa tursiifne, “maaltu isa qabe (tursiise)?” jedhuDhagayaa! guyyaa inni isaanitti dhufu, isaan irraa deebifamaa mitiWanti isaan ittiin qishinaa turanis isaanitti marsee jira.');
INSERT INTO "apaguna" VALUES(11,9,'11:9','Yoo Nuti rahmata Nu biraa ta’e Nama dhandhamsiifnee ergasii isa irraa mulqine, (abdii muratee itti kafara)Dhugumatti inni hedduu abdii murataa, hedduu kafaraadha');
INSERT INTO "apaguna" VALUES(11,10,'11:10','Yoo Nuti eega rakkoon isa mudatee booda qananiiwwan isa dhandhamsiifne, dhugumatti “hamtuuwwantu narraa deeme” jedhaDhugumatti, inni akkaan gammadaa, akkaan dhaaddataadha.');
INSERT INTO "apaguna" VALUES(11,11,'11:11','Garuu isaannan obsaniifi toltuuwwan dalagan warri sun araaramaafi mindaa guddaatu isaaniif jira.');
INSERT INTO "apaguna" VALUES(11,12,'11:12','“Maaliif qabeenyi guddaan isa irratti hin buufamne, yookiin maleykaan isa waliin hin dhufne” jechuu isaanii sodaachuuf jecha garii waan gara keetti buufamee dhiisuun, isattis onneen kee dhiphachuun, sirratti sodaatamaAti akeekkachiisaa qofaRabbiin wanta hunda irratti tiksaadha');
INSERT INTO "apaguna" VALUES(11,13,'11:13','“Inumaayyuu isatu (Qur’aana) of biraa uume” jedhuu? “yoo kan dhugaa dubbattan taatan Rabbiin ala waan dandeessan waamadhaatii, suuraa kudhan kan (isin biraa) uumamtuu taate kan isa fakkaattu fidaa” jedhiin.');
INSERT INTO "apaguna" VALUES(11,14,'11:14','Yoo isaan isiniif hin awwaatin, inni (Qur’aanni kun) kan buufame beekumsa Rabbiitiin akka ta’eefi Isa malees gabbaramaan haqaa akka hin jirre beekiSila isin Muslima hin taatanuu? (Muslima ta’aa jedhiin).');
INSERT INTO "apaguna" VALUES(11,15,'11:15','Namni jireenya Addunyaatiifi miidhagina ishee fedhe, Nuti dalagaa isaanii ishee keessatti guunnee isaaniif kenninaIsaanis ishee keessatti hin hir’ifamanu.');
INSERT INTO "apaguna" VALUES(11,16,'11:16','Isaan sun warra Aakiraatti ibidda malee homtuu isaaniif hin jirreedhaWanti isaan ishee keessatti dalaganis ni badeWanti isaan dalagaa turanis badaadha');
INSERT INTO "apaguna" VALUES(11,17,'11:17','Sila namni ragaa mul’ataa Gooftaa isaa biraa ta’e irra jiru, kan ragaan Isa (Allaah) irraa ta’e isa hordofuufi isa duras kitaabni Muusaa kan hogganaafi rahmata ta’ee (ragaa isaaf ba’e) (akka nama jiruu addunyaa fedhuu ni ta’aa)? Warri sun isatti (Qur’aanatti) ni amanuAhzaabota irraas warra isatti kafare ibiddi itti gala isaatiIsa irraa shakkii keessatti hin ta’inDhugumatti, inni dhugaa Gooftaa kee irraa ta’eedhaGaruu irra hedduun namootaa hin amananu');
INSERT INTO "apaguna" VALUES(11,18,'11:18','Isa Rabbi irratti kijiba uume irra namni of miidhe eenyu? Warri sun Gooftaa isaanii irra fidamuRagoonnis “isaan kun warra Gooftaa isaanii irratti sobaniidha” jedhuDhagayaa! Abaarsi Rabbii miidhaa hojjattoota irra haa jiraatu.');
INSERT INTO "apaguna" VALUES(11,19,'11:19','Isaanis warra karaa Rabbii irraa (namoota) deebisaniifi dabiinsa ishees fedhaniidhaIsaan isaanumatu Aakiraattis kaafiroota');
INSERT INTO "apaguna" VALUES(11,20,'11:20','Warri sun dachii keessatti (Rabbiin) dadhabsiisoo hin turreRabbii alattis gargaaraan tokkolleen isaaniif hin turreAdabichis dachaa isaaniif godhama(Haqa) dhagahuus kan danda’an hin turre; kan arganis hin turre.');
INSERT INTO "apaguna" VALUES(11,21,'11:21','Warri sun isaan lubbuu isaanii hoonga’anii wanti isaan ofirraa uumanii (gabbaraa) turanis jalaa badeedha.');
INSERT INTO "apaguna" VALUES(11,22,'11:22','Dhugumatti, isaan isaanumatu Aakiraatti hoonga’oota, jechuutti Shakkiin hin jiru.');
INSERT INTO "apaguna" VALUES(11,23,'11:23','Dhugumatti, isaan amananii gaggaarii hojjatanii, Rabbii isaaniitiif gadi jedhan isaan sun warra jannataatiIsaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(11,24,'11:24','Fakkeenyi gareewwan lamaanii akka jaamaa, duudaafi argaa, dhagayaatiSila fakkeenyatti isaan lamaan wal qixxaa’uu? Sila isin hin gorfamtanii?');
INSERT INTO "apaguna" VALUES(11,25,'11:25','Dhugumatti, Nuti Nuuhiin gara ummata isaa erginee jirra(Innis ni jedhe): “Ani isiniif akeekkachiisaa ifa bahaadha.');
INSERT INTO "apaguna" VALUES(11,26,'11:26','Rabbiin malee (waan biraa) hin gabbarinaa; Dhugumatti, ani adabbii guyyaa laalessaan isin irratti sodaadha.”');
INSERT INTO "apaguna" VALUES(11,27,'11:27','Ummata isaa iraa qondaaltonni isaannan kafaranii “nama fakkii keenya taatee malee si hin agarruGadi aantota keenya warra yaada gadhee (yartuu) malee kan si hordofes hin agarreIsiniifis nurratti caalmaya tokkollee hin agarreInumaayyuu sobdoota ta’uu keessan shakkina” jedhan.');
INSERT INTO "apaguna" VALUES(11,28,'11:28','Inni ni jedhe: “Yaa ummata kiyya! Mee naaf himaa; yoo ani ragaa ifa bahaa Gooftaa kiyya irraa ta’e irra jiraadhee, Innis rahmata Isa biraa ta’e (nabiyyummaa) naaf kennee, isin jalaa dhokate, sila nuti odoo isin isheedhaaf jibbitoota taatan dirqamaan isin fudhachiifnaa?');
INSERT INTO "apaguna" VALUES(11,29,'11:29','Yaa ummata kiyya! Ani isa (ergaa qaqqabsiisuu) irratti mindaa isin hin gaafadhuMindaan kiyya Rabbiin qofa irra jiraAnis ari’aa warra amananiitii mitiDhugumatti, isaan Gooftaa isaanii wajjin wal qunnamoodhaGaruu ani ummata wallaalaa ta’uun isin arga');
INSERT INTO "apaguna" VALUES(11,30,'11:30','Yaa ummata kiyya! Yoo ani isaan ari’e, (adabbii) Rabbii jalaa maaltu na baraarsa? Sila homaa hin gorfamtanuu?');
INSERT INTO "apaguna" VALUES(11,31,'11:31','Ani kuusaan qabeenya Rabbii ana bira jira isiniin hin jedhuGaybii (wanta fagoos) hin beekuAni maleeykaa dhas isiniin hin jedhuIsaannan ijji keessan tuffattu (xinneessituu)nis Rabbiin toltuu isaaniif hin kennu hin jedhuRabbiin waan lubbuu isaanii keessa jiru irra beekaadhaYoo san, ani dhugumatti warra miidhaa hojjatan irraayyi.”');
INSERT INTO "apaguna" VALUES(11,32,'11:32','[Isaan] “yaa Nuuh! Ati dhugumatti nu mormitee jirtaMormii keenyas baay’essitee jirtaYoo warra dhugaa dubbatan irraa taate, waan nuun sodaachiftu sana nutti fidi” jedhan.');
INSERT INTO "apaguna" VALUES(11,33,'11:33','Isa kana kan isinitti fidu Rabbuma qofaYoo fedhes (ni ariifachiisa)Isinis kan dadhabsiiftan hin taane” jedhe.');
INSERT INTO "apaguna" VALUES(11,34,'11:34','“Yoo an isin gorsuu fedhes, yoo Rabbiin isin jallisuu fedha ta’e gorsi koo isin hin fayyaduInni Gooftaa keessaniGaruma isaa qofa deebitus” jedhe.');
INSERT INTO "apaguna" VALUES(11,35,'11:35','Sila “(Muhammad) isa (Qur’aana) of irraa uume” jedhuu? “Yoo ani of irraa isa uume, badiin kiyya anuma irra jiraAnis badii isin hojjattan irraa qulquulluudha” jedhiin.');
INSERT INTO "apaguna" VALUES(11,36,'11:36','Gara Nuuhittis akkana jechuun beeksisa goone: “Dhugumatti, warra kana dura amanan malee ummata kee irraa kana booda homtuu hin amanu;Kanaaf waan isaan hojjatanitti hin gaddin');
INSERT INTO "apaguna" VALUES(11,37,'11:37','Doonis ilaalcha (tika) keenyaafi ajaja keenyaan hojjadhuWarra miidhaa dalagan keessatti natti hin dubbatinDhugumatti, isaan bishaanin dhidhimfamoodha.”');
INSERT INTO "apaguna" VALUES(11,38,'11:38','Innis (Nuuhis) doonii hojjata (ture)Dhawaatuma qondaalonni ummata isaa irraa ta’an isa irra qaxxaamuraniin, isatti qishinuInnis ni jedhe: “Yoo isin nutti qishintanis nutis akkuma isin qishintan isinitti qishinuuf jirra.');
INSERT INTO "apaguna" VALUES(11,39,'11:39','Namni adabbiin isa salphisu itti dhufuufi adabbiin itti fufaanis isa irra qubatu eenyu akka ta’e beekuuf jirtu.”');
INSERT INTO "apaguna" VALUES(11,40,'11:40','Yeroma ajajni keenya dhufee, badaa ibiddaa (keessaa bishaan) burqe “Wantoota hunda irraa (saala) lama lama (kormaafi dhalaa), maatii keetis nama jechi (adabaa) isa irratti dabre malee, namoota amananis ishee keessatti fe’i” isaan jenneIsa waliinis namoota muraasa malee (baay’een) hin amanne.');
INSERT INTO "apaguna" VALUES(11,41,'11:41','“Haala deemsi isheetiifi dhaabbatiinsi ishees maqaa Rabbiitiin taa’een, ishee yaabbadhaaDhumatti Rabbiin kiyya araaramaa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(11,42,'11:42','Isheenis danbalii akka gaaraa keessa isaanin adeemtiNuuhis ilma isaa(doonittii irraa) fagoo tureen “Yaa ilma kiyya! Nu waliin yaabbadhuWarra kafaran wajjinis hin ta’in” jechuun lallabe.');
INSERT INTO "apaguna" VALUES(11,43,'11:43','(Ilmi isaas) ni jedhe: “Ani fuula dura gara gaara bishaan jalaa na baraarsuun qubadha.” (Nuuhis) ni jedhe: “Har’a wanti ajaja Rabbii irraa nama baraarsu hin jiru; nama Rabbiin rahmata isaaf godhe malee.” Danbaliinis gidduu isaanii haguugeInni warra bishaaniin nyaataman irraa ta’e.');
INSERT INTO "apaguna" VALUES(11,44,'11:44','Ni jedhames: “Yaa dachii! Bishaan kee liqimsiYaa samii! (Bokkaa kee) qabadhu.” Bishaanis xuuxamee hir’ateMurtiin Rabbiis (adabichis) ni raawwatameIsheenis tabba juudiyyi jedhamu irra dhaabbatteNi jedhames: “(rahmata Rabbii irraa) Fageenyi ummata miidhaa hojjattoota irra haa jiraatu!”');
INSERT INTO "apaguna" VALUES(11,45,'11:45','Nuuhis Gooftaa isaatti lallabee, “Gooftaa kiyya! dhugumatti, ilmi kiyya maatii kiyya irraayiWaadaan keetis dhugaadhaAtis irra haqa murteessaa abbootii murtiiti” jedhe.');
INSERT INTO "apaguna" VALUES(11,46,'11:46','(Rabbiinis) “Yaa Nuuh! dhugumatti, inni maatii kee irraa mitiInni hojii gaarii hin ta’iniidhaKanaafuu waan beekumsa itti hin qabne na hin gaafatinAni akka ati wallaaltota irraa hin taanen si gorsa” jedhe.');
INSERT INTO "apaguna" VALUES(11,47,'11:47','(Nuuhis) “Gooftaa kiyya! Ani waan beekumsa itti hin qabne si gaafachuu irraa sittin maganfadhaYoo Ati naaf araaramtee, rahmatas naaf hin godhin, warra hoonga’an irraan ta’a” jedhe.');
INSERT INTO "apaguna" VALUES(11,48,'11:48','“Yaa Nuuh nageenyaa Nu biraa ta’eefi barakaa siifi garii ummata si wajjin jiran irratti ta’een, (doonittii irraa) bu’aa(Sanyii isaanii irraas) ummata fuula dura yeroof isaan qanansiifnee san booda Nu biraa adabbiin dhukkubsaan isaan tuqutu jira” jedhame.');
INSERT INTO "apaguna" VALUES(11,49,'11:49','Isheen kun oduu fagoo kan gara keetti beeksisa goonu irraayiSis ta’ee ummanni keetis kana dura kan ishee beektan hin turreObsiDhugumatti, booddeen (gaariin) kan warra (Rabbiin) sodataniiti');
INSERT INTO "apaguna" VALUES(11,50,'11:50','Gara Aadittis obboleessa isaanii Huudiin (erginee jirra)Innis ni jedhe: “Yaa ummata kiyya! Rabbiin gabbaraaGabbaramaan (haqaa) Isa malee isiniif hin jiruIsin kijiba uumoo malee waa biraa hin taane.');
INSERT INTO "apaguna" VALUES(11,51,'11:51','Yaa ummata kiyya! Ani isa (ergaa qaqqabsiisuu) irratti mindaa isin hin gaafadhuMindaan kiyyas Isa na uume irratti malee hin taaneSila isin hin xiinxaltanii?');
INSERT INTO "apaguna" VALUES(11,52,'11:52','Yaa ummata kiyya! Gooftaa keessan araarama kadhaa, ergasii gara Isaatti tawbaa deebi’aaBokkaa walitti dhaabbataa godhee isiniif ergaaHumna keessan irrattis humna isiniif dabalaaYakkamtoota taatanii irraa gara hin galinaa.”');
INSERT INTO "apaguna" VALUES(11,53,'11:53','“Yaa Huud! Ati ragaan nutti hin dhufneNutis jecha keetiif jennee gabbaramtoota keenya kan dhiifnu hin taaneNutis siif amanoo miti” jedhan.');
INSERT INTO "apaguna" VALUES(11,54,'11:54','Garii gabbaramtoota keenyaatu hamtuun si tuqee malee homaa hin jennu(Huudis) ni jedhe: “Ani Rabbiinin ragaa godha; isinis ‘ani waan isin qindeessitan irraa qulqulluu ta’uu’ ragaa ba’aa.');
INSERT INTO "apaguna" VALUES(11,55,'11:55','Isaa gaditti (waan gabbartan irraa ani qulqulluudhaYoo dandeessan) Tooftaa natti baasaa haala huda taataniin; Sana booda yeroo naaf hin kenninaa.');
INSERT INTO "apaguna" VALUES(11,56,'11:56','Ani Rabbii Gooftaa kiyyaafi Gooftaa keessan ta’e irratti irkadheeraUumama lubbuu qabu tokkos kan Inni odoo sammuu isaa hin qabin hafe hin jiruDhugumatti, Gooftaan kiyya karaa qajeelaa irra jira.');
INSERT INTO "apaguna" VALUES(11,57,'11:57','Yoo gara galtanis, dhugumatti ani waanin ittiin gara keessanitti ergame dhaqqabsiiseeraGooftaan kiyyas ummata isin hin ta’in bakka buusaHomaas Isa hin miitanDhugumatti, Gooftaan kiyya waan hunda irratti eegaadha.”');
INSERT INTO "apaguna" VALUES(11,58,'11:58','Yeroo ajajni keenya dhufu, Huudiifi warra isa waliin amanan rahmata Nurraa ta’een nagaa baafneadabbii cimaa ta’e irraas nagaa isaan baafne');
INSERT INTO "apaguna" VALUES(11,59,'11:59','Isheen sun (ummata) AadiMallattoowwan Gooftaa isaanii mormanii, ergamtoota Isaas didan; ajaja of tuulaa, haqa mormaa ta’e hundaa hordofan');
INSERT INTO "apaguna" VALUES(11,60,'11:60','Addunyaa kana keessatti abaarsaan hordofsiifamanii jiru; Guyyaa Qiyaamaas (akkasuma)Dhagayaa! Dhugumatti, Aad Gooftaa isaaniitti kafaraniiruDhagayaa! Ummata Huud kan ta’an Aadiif (rahmata irraa) fageenyi haa ta’u.');
INSERT INTO "apaguna" VALUES(11,61,'11:61','Gara Samuudis obboleessa isaanii Saalihiin (erginee jirra)Ni jedhe: “Yaa ummata kiyya! Rabbiin gabbaraaIsa malee dhugaan gabbaramaan biraa isiniif hin jiruInni dachii irraa isin uumeeti, ishee keessa isin jiraachiseKanaaf araarama Isa (qofa) kadhaa; ergasii gara Isaatti tawbaa deebi’aaDhugumatti, Gooftaan kiyya (beekumsa Isaatiin) dhihoo, awwaataa dhas.”');
INSERT INTO "apaguna" VALUES(11,62,'11:62','Ni jedhan: “Yaa Saalih! Ati kana dura nu keessatti nama kajeelamaa turteSila ati waan abbootiin keenya gabbaraa turan gabbaruu irraa nu dhorgitaa? Nuti waan ati itti nu waamtu irraa shakkii guddaa qabna.”');
INSERT INTO "apaguna" VALUES(11,63,'11:63','(Saalih) ni jedhe: “Yaa ummata kiyya mee naaf himaa! Yoo ani ragaa ifa ba’aa Gooftaa kiyya irraa ta’e irra jiraadhee Innis rahmata Isa biraa ta’e (nabiyyummaa) naaf kennee, yoo ani Isa dide Rabbiin jalaa eenyutu na baraarsa? Isin hoongeessuu malee homaa naaf hin dabaltanu.');
INSERT INTO "apaguna" VALUES(11,64,'11:64','Yaa ummata kiyya! Isheen kun haala mallattoo isiniif taateen gaala RabbiitiDachii Rabbii keessa haa nyaattuu ishee dhiisaa, Hamtuudhaan ishee hin tuqinaa(Yoo akkas gootan) adabbiin dhihoo ta’e isin tuqa.”');
INSERT INTO "apaguna" VALUES(11,65,'11:65','Ishee ni waraananInnis “Manneen keessan keessa guyyoota sadihiif qanani’aaKun waadaa (adabbii) hin kijibsiifamneedha” jedhe');
INSERT INTO "apaguna" VALUES(11,66,'11:66','Yeroma ajajni keenya dhufu, Saalihiifi warra isa wajjiin amanan rahmata Nu biraa ta’een nagaa (isaan) baafne; xiqqeenya guyyaa sanii irraas (isaan baraarsine)Dhugumatti, Gooftaan kee isatu jabaa, injifataadha.');
INSERT INTO "apaguna" VALUES(11,67,'11:67','Iyyansi hamaan miidhaa hojjattoota qabattee, manneen isaanii keessatti kan jilbeenfatan ta’anii (du’anii) bariisifatan.');
INSERT INTO "apaguna" VALUES(11,68,'11:68','Akka waan ishee keessa hin jiraatinii ta’anDhagayaa! Dhugumatti Samuud Gooftaa isaaniitti kafaranii jiruDhagayaa! fageenyi Samuudiif haa ta’u!');
INSERT INTO "apaguna" VALUES(11,69,'11:69','Dhugumatti, ergamtoonni keenya Ibraahiimitti oduu gammachiisaan dhufanii “nagaa (sirratti buufna)” jedhanInnis “nagaan (isin irra haa jiraatu)” jedheOdoo hin turre jibicha waadamaan dhufe.');
INSERT INTO "apaguna" VALUES(11,70,'11:70','Yeroma harki isaanii gara isaa (nyaatichaa) kan hin geenye ta’uu argu, isaan morme; isaan irraas sodaa dhokfate“Hin sodaatin; nuti gara ummata Luux ergamnee” jedhan.');
INSERT INTO "apaguna" VALUES(11,71,'11:71','Niitiin isaa dhaabattuu turtee achumaan kolfiteNuti Isihaaqiin, Isihaaq boodas Ya’aquubiin ishee gammachiifne.');
INSERT INTO "apaguna" VALUES(11,72,'11:72','Ni jette: “Yaa badii kiyya! Sila odoo ani dullattii, dhirsi kiyya kunis dullacha ta’ee jiruun da’aa? Dhugumatti, kun waan dinqisiisaa dha!”');
INSERT INTO "apaguna" VALUES(11,73,'11:73','Ni jedhan: “Sila murtii Rabbii irraa dinqisiifattaa? Yaa warra manichaa! Rahmanni Rabbiitiifi barakaan Isaa isin irra haa jiraatu; dhugumatti, Inni faarfamaa, guddaadha.”');
INSERT INTO "apaguna" VALUES(11,74,'11:74','Yeroma Ibraahiim irraa sodaan deemee gammachuun isatti dhuftu, inni ummata Luux keessatti Nuun falmuu eegale');
INSERT INTO "apaguna" VALUES(11,75,'11:75','Dhugumatti, Ibraahiim obsaa, mararfataa, deebi’aadha.');
INSERT INTO "apaguna" VALUES(11,76,'11:76','Yaa Ibraahiim! (Falmii) kana irraa gara galiDhugumatti, ajajni Gooftaa keetii dhufee jiraIsaanis adabbiin hin deebifamne isaanitti dhufa');
INSERT INTO "apaguna" VALUES(11,77,'11:77','Yeroma ergamtoonni keenya Luuxitti dhufan, inni isaanitti gadde; qomti isaas isaaniif dhiphate“Kun guyyaa cimaadha” jedhe.');
INSERT INTO "apaguna" VALUES(11,78,'11:78','Ummanni isaas gara isaatti kan ariifatan ta’aa isatti dhufanSana duras gocha fokkataa kan raawwatanu turan(Luux) ni jedhe: “Yaa ummata kiyya! Isaan kun shamarran kiyyaIsaan (yoo seeraan fuutan) isiniif qulqulluudhaRabbiin sodaadhaa; keessummaa kiyya keessattis na hin xiqqeessinaaSila isin irraa namni qajeelaan tokkolleen hin jiruu?”');
INSERT INTO "apaguna" VALUES(11,79,'11:79','Isaan ni jedhan: “Dhugumatti, ati akka nuti ijoollee dubaraa kee irraa fedha hin qabne beektee jirta, dhugumatti ati waan nuti barbaannu sirritti beekta.”');
INSERT INTO "apaguna" VALUES(11,80,'11:80','Ni jedhe: “Ani odoon humna isin irratti qabaadhee yookiin gara gosa jabduu hirkadhee (isinin adabbii ture).”');
INSERT INTO "apaguna" VALUES(11,81,'11:81','(Isaan) ni jedhan: “Yaa Luux! Dhugumatti, nuti ergamtoota Gooftaa keetiiti; Isaan gara keetti hin ga’anuMaatii kee qabadhuu kutaa halkan irraa taate keessa deemiNiitii kee malee Isin irraa eenyullee of duuba akka hin mil’anneDhugumatti, wanti isaan tuqu ishee tuqaadhaBeellamni isaaniis bariidhaSila bariin dhihoo mitii?”');
INSERT INTO "apaguna" VALUES(11,82,'11:82','Yeroo ajajni keenya dhufu, gubbaa ishee (magaalattii) jala ishee taasifnee, Ishee irrattis dhagaa dhoqqee gubamaa wal hordofaa irraa ta’e ishee irratti roobsifne.');
INSERT INTO "apaguna" VALUES(11,83,'11:83','(Dhagattiin) Gooftaa kee biratti kan mallatteeffamte taatee (ishee roobsine)Isheen miidhaa hojjattoota irraa fagoo miti.');
INSERT INTO "apaguna" VALUES(11,84,'11:84','Gara Madyanis obboleessa isaanii Shu’aybiin (erginee jirra)“Yaa ummata kiyya! Rabbiin gabbaraaIsa malee dhugaan gabbaramaan biraa isiniif hin jiruSafaraafi madaala hin hir’isinaaAni qabeenya keessattin isin argaaraDhugumatti ani adabbii guyyaa marsaa ta’een isin irratti sodaadha');
INSERT INTO "apaguna" VALUES(11,85,'11:85','Yaa ummata kiyya! Safaraafi madaalas haqaan guutaaNamoota jalaas wantoota isaanii hin hirdhisinaa; dachii keessattis balleessitoota taatanii badii hin facaasinaa');
INSERT INTO "apaguna" VALUES(11,86,'11:86','Yoo mu’uminoota taataniif waan Rabbi isiniif hambisetu isiniif caala; Anis isin irratti eegaa miti.”');
INSERT INTO "apaguna" VALUES(11,87,'11:87','Ni jedhan: “Yaa Shu’ayb! Sila salaata keetu waan abbootiin keenya gabbaraa turan akka dhiifnu yookiin akka nuti qabeenya keenya irratti akka feene hin dalagne si ajajjii? Dhugumatti, ati simatu obsaa, qajeelaa dha!”');
INSERT INTO "apaguna" VALUES(11,88,'11:88','Ni jedhe: “Yaa ummata kiyya! Mee naaf himaa; yoon ragaa ifa ba’aa Gooftaa kiyya irraa ta’e irra jiraadhee, innis kennaa gaarii Isa biraa ta’e naaf kenne, (sila qabeenya karaan alaatiin walitti makuun qabaa)Ani waanin irraa isin dhorge (hojjachuun) isin faallessuu hin fedhuAni hangan danda’een (isin) tolchuu malee hin fedhuHaqa qunnamuun kiyyas Rabbi irraa malee hin taaneIsa irratti hirkadhe; garuma isaan deebi’a.');
INSERT INTO "apaguna" VALUES(11,89,'11:89','Yaa ummata kiyya! ana faallessuun akka fakkaataa waan ummata Nuuh yookiin ummata Huud yookiin ummata Saalih tuqee isin tuquu irratti isin hin kaasneUmmanni Luuxis isin irraa fagoo miti.');
INSERT INTO "apaguna" VALUES(11,90,'11:90','Gooftaa keessan araarama kadhaaErgasii gara Isaa deebi’aaDhugumatti, Gooftaan kiyya rahmata godhaa, jaallataadha.”');
INSERT INTO "apaguna" VALUES(11,91,'11:91','“Yaa Shu’ayb! Irra hedduu waan ati jettuu nuti hin hubannuNu keessatti dadhabaa taateetiin si arginaOdoo gosti kee jiraachuu baatee, silaa dhagaan tumnee si ajjeefna turreAti nurratti jabaa miti” jedhan.');
INSERT INTO "apaguna" VALUES(11,92,'11:92','Ni jedhe: “Yaa ummata kiyya! Sila gosa kiyyatu Rabbi irra isinitti jabaataa? Duuba keessan irraan gara dugdaa Isa gootanDhugumatti, Gooftaan kiyya waan isin dalagdanutti marsaadha.');
INSERT INTO "apaguna" VALUES(11,93,'11:93','Yaa ummata kiyya! Isin sadarkaa keessaniin hojjadhaa; Ani hojjataadhaNama adabbiin isa xiqqeessu isatti dhufuufi nama isa kijibaa ta’e fuula dura beektuIsinis eegaa! Ani isin waliin eegaadha.”');
INSERT INTO "apaguna" VALUES(11,94,'11:94','Yeroma ajajni keenya dhufu, Shu’aybiifi warra isa waliin amanan rahmata Nu biraa ta’een nagaa baafnee, warra miidhaa hojjatanis iyyansi cimaan ni qabdeManneen isaanii keessatti jilbeenfatoo ta’anii (du’anii) bariisifatan');
INSERT INTO "apaguna" VALUES(11,95,'11:95','Akka waan ishee keessa hin jiraatinii ta’anDhagayaa! akkuma Samuud fagaatte! Madyaniif fageenyi haa ta’u');
INSERT INTO "apaguna" VALUES(11,96,'11:96','Dhugumatti, Muusaa keeyyattoota keenyaafi ragaa ifa ta’een erginee jirra');
INSERT INTO "apaguna" VALUES(11,97,'11:97','Gara Fir’awniifi qondaalota isaa (ergine)Ajaja Fir’awni hordofanAjajni Fir’awnis sirrii miti.');
INSERT INTO "apaguna" VALUES(11,98,'11:98','Guyyaa Qiyaamaa ummata isaa dursee, ibidda isaan seensisa(Inni) seensa seenamaa (ta’uun isaa) waa fokkate!');
INSERT INTO "apaguna" VALUES(11,99,'11:99','Addunyaa kana keessatti abaarsaan hordofsiifamanGuyyaa Qiyaamaas (akkasuma)Badhaasni badhaafaman waa fokkate!');
INSERT INTO "apaguna" VALUES(11,100,'11:100','Kun oduu magaalotaa irraayi; isa sirratti odeessinaIshee irraa dhaabbataafi haamamaatu jiru.');
INSERT INTO "apaguna" VALUES(11,101,'11:101','Nuti isaan hin mine; Garuu isaan lubbuu isaanii miidhanYeroma ajajni Gooftaa keetii dhufu gabbaramtoonni isaanii kan isaan Rabbii gaditti gabbaraa turan homaa isaan hin fayyadneHoongessuu malees homaa isaaniif hin daballe.');
INSERT INTO "apaguna" VALUES(11,102,'11:102','Qabaan Gooftaa keetiis yeroo magaalota haala miidhaa hojjattuu taateen qabe akkuma kanaDhugumatti, qabaan Isaa laalessaa, jabaadha');
INSERT INTO "apaguna" VALUES(11,103,'11:103','Dhugumatti, kana keessa namoota adabbii Aakiraa sodaataniif mallattootu jiraSun guyyaa namoonni isaaf walitti qabamaniidha; ammas sun guyyaa dhufamaadha.');
INSERT INTO "apaguna" VALUES(11,104,'11:104','Beellama lakkaa’amaaf malee isa hin tursiifnu.');
INSERT INTO "apaguna" VALUES(11,105,'11:105','Guyyaa inni dhufu lubbuun kamillee hayyama Isaatiin ala hin dubbattuIsaan irraas warra hoonga’aafi milkaa’aatu jira.');
INSERT INTO "apaguna" VALUES(11,106,'11:106','Isaan hoonga’an ibidda keessa ta’uIshee keessatti iyyansaafi himimsaatu isaaniif jira');
INSERT INTO "apaguna" VALUES(11,107,'11:107','Waan Gooftaan kee fedhe malee, Hanga samiifi dachiin turan ishee keessa kan turan ta’anii (ibidda keessa jiraatu)Dhugumatti, Gooftaan kee waan fedhe hojjataadha');
INSERT INTO "apaguna" VALUES(11,108,'11:108','Isaan milkeeffaman immoo Jannata keessa jiraatuWaan Gooftaan kee fedhe malee, hanga samiifi dachiin turtu ishee keessatti kan hafan ta’anii (jannata keessa jiraatu)Kennaa addaan hin cinne (kennaman).');
INSERT INTO "apaguna" VALUES(11,109,'11:109','(Yaa Muhammad!) waan warri sun gabbaran irraa shakkii keessatti hin tahin(Isaan) akkuma abbootiin isaanii duraan gabbaran malee hin gabbaranuDhugumatti, Nutis qooda isaanii odoo hin hir''ifamne guunnee isaaniif kennina.');
INSERT INTO "apaguna" VALUES(11,110,'11:110','Dhugumatti, Muusaaf kitaaba kennineerra; isa keessattis wal dhabameOdoo jechi Gooftaa kee biraa dabruu baatee silaa gidduu isaaniitti murteeffama tureDhugumatti, isaan isa irraa shakkii cimaa keessa jiru.');
INSERT INTO "apaguna" VALUES(11,111,'11:111','Dhugumatti hunda isaaniitiif Gooftaan kee dalagaa isaanii isaaniif guutaDhugumatti, Inni waan isaan dalagan beekaadha');
INSERT INTO "apaguna" VALUES(11,112,'11:112','Akka ajajamtetti gadi dhaabbadhuIsaan si waliin tawbatanis (gadi haa dhaabbatanii)Daangaas hin dabrinaaDhugumatti, Inni waan isin dalagdan argaadha.');
INSERT INTO "apaguna" VALUES(11,113,'11:113','Gara warra miidhaa hojjatanii hin dabinaa(Yoo akkas gootan) ibiddi isin tuqaRabbiin malees jaalallee biraa hin qabdanErgasii hin tumsamtanu.');
INSERT INTO "apaguna" VALUES(11,114,'11:114','Qaccee lamaan guyyaatiifi yeroo halkan irraa ta’e salaata dhaabaaToltuuwwan hamtuu ni deemsisuKun warra gorfamaniif gorsa');
INSERT INTO "apaguna" VALUES(11,115,'11:115','Obsi! Dhugumatti, Rabbiin mindaa toltuu hojjattootaa hin balleessu.');
INSERT INTO "apaguna" VALUES(11,116,'11:116','Maaliif jaarraawwan isiniin duraa irraa warri gaariin kan dachii keessatti badii irraa dhorgan hin jiraatin? Garuu xiqqoo warra nuti nagaya baafnee irraa (argamaniiru)Isaan miidhaa hojjatan immoo waan isa keessatti qanansiifaman hordofee yakkamtoota irraa ta’an.');
INSERT INTO "apaguna" VALUES(11,117,'11:117','Gooftaan kee magaalaa kamillee, odoo jiraattonni ishee warra toltuu hojjatan ta’anii jiranuu, miidhaan ishee hin balleessu');
INSERT INTO "apaguna" VALUES(11,118,'11:118','Gooftaan kee odoo fedhee, silaa nama hunda ummata tokko taasisa tureWal dhaboo ta’uu irraa hin deemanu');
INSERT INTO "apaguna" VALUES(11,119,'11:119','Nama Gooftaan kee mararfate malee (waldhabuu irraa hin deman)Kanumaafis isaan uumeJechi Gooftaa keetii “dhugumatti jahannamiin walii gala jinnootaafi namootaa irraa ta’aniin nan guuta” jechuun guutamteetti');
INSERT INTO "apaguna" VALUES(11,120,'11:120','Oduu ergamtootaa irraa waan ittiin onnee kee raggaasifnu hunda sirratti odeessinaIshee kana keessas dhugaa, gorsaafi yaadachiifni mu’uminootaaf ta’e siif dhufeera');
INSERT INTO "apaguna" VALUES(11,121,'11:121','Isaan hin amanneenis jedhi: “sadarkaa keessaniin dalagaaNuti dalagoodha.');
INSERT INTO "apaguna" VALUES(11,122,'11:122','Isinis eegaa! Nutis eegoodha.”');
INSERT INTO "apaguna" VALUES(11,123,'11:123','Iccitiin samiifi dachii keessaa kanuma RabbiitiDhimmi hundi gara Isaatti deebifamaIsa gabbariIsa irratti hirkadhuGooftaan kee waan isin hojjattan irraa dagataa miti');
INSERT INTO "apaguna" VALUES(12,1,'12:1','Alif Laam RaaKun keeyyattoota kitaaba ibsaa ta’eeti');
INSERT INTO "apaguna" VALUES(12,2,'12:2','Nuti akka isin xiinxaltaniif jecha haala Qur’aana Arabiffaa ta’een isa buufne.');
INSERT INTO "apaguna" VALUES(12,3,'12:3','Nuti Qur’aana kana gara keetti beeksisuudhaan seenaa gaggaarii sirratti odeessinaAtis isa dura dagattoota irraa turte.');
INSERT INTO "apaguna" VALUES(12,4,'12:4','Yeroo Yuusuf abbaa isaatin “Yaa abbaa kiyya! Ani (abjuudhaan) urjiiwwan kudha tokko, aduufi ji’a argeera; kan anaaf sujuuda godhan ta’anii isaan argeen jira” jedhe [yaadadhu]');
INSERT INTO "apaguna" VALUES(12,5,'12:5','(Abbaan isaas) ni jedhe: “Yaa ilma kiyya! Tooftaa (dhara) sirratti baasuu abjuu kee obboleeyyan keetti hin himinDhugumatti, sheyxaanni namaaf diina ifa bahaadha.');
INSERT INTO "apaguna" VALUES(12,6,'12:6','Akka kanatti Gooftaan kee si filata; hiikkaa manaamaas si barsiisa; ni’imaa Isaas akkuma kana dura abbootii kee Ibraahiimiifi Isihaaq irratti guute sirrattiifi maatii Ya’aquub irrattis ni guutaDhugumatti, Gooftaan kee beekaa, ogeessa.”');
INSERT INTO "apaguna" VALUES(12,7,'12:7','Dhugumatti, (seenaa) Yuusufiifi obboleeyyan isaa keessa warreen gaafataniif mallattootu jira.');
INSERT INTO "apaguna" VALUES(12,8,'12:8','Yeroo (obboleeyyan Yuusuf) jedhan: “Dhugumatti, odoo nuti garee taanee jirru Yuusufiifi obboleessi isaa nurra gara abbaa keenyaatti jaalatamoodhaDhugumatti, abbaan keenya dogoggora ifa bahaa keessa jira.');
INSERT INTO "apaguna" VALUES(12,9,'12:9','Yuusufiin ajjeesaa; yookiin dachii (fagootti) isa gataa; fuulli abbaa keessanii isiniif dhuunfaa ta’aSana booda ummata gaggaarii taatu” (waliin jedhan)');
INSERT INTO "apaguna" VALUES(12,10,'12:10','Isaan irraa jedhaan tokko ni jedhe: “Yuusufiin hin ajjeesinaatii yoo kan dalagdan taataniif akka gariin daldaltoota karaa dheeraa deemu isa fudhatu hundee boollaa (eela) keessatti isa gataa.”');
INSERT INTO "apaguna" VALUES(12,11,'12:11','Ni jedhan: “Yaa abbaa keenya! Sila ati maaliif Yuusuf irratti nu hin amanne? Nutis isaaf tola yaadoodha');
INSERT INTO "apaguna" VALUES(12,12,'12:12','Haa bashannanuu, haa taphatuu boru nu waliin isa ergi; dhugumatti, nutis isaaf tiksitoota.”');
INSERT INTO "apaguna" VALUES(12,13,'12:13','(Ya’aquub) ni jedhe: “Isa fuutanii deemuun keessan na gaddisiisaIsinis isa irraa dagatoo taatanii yeyyiin isa nyaachuun sodaadha.”');
INSERT INTO "apaguna" VALUES(12,14,'12:14','[Isaan] ni jedhan: “Odoo nuti garee taanee jirruu yoo yeyyiin isa nyaatteef, dhugumatti nuti yeroo san hoongoftoota.”');
INSERT INTO "apaguna" VALUES(12,15,'12:15','Yeroma deemanii boolla gadi fageenya qabu keessatti isa gatuuf walii galan (isa darban)Nutis “dhugumatti, ati dhimma isaanii kana odoo isaan (si) hin beekin isaanitti himta” jechuun gara isaatti beeksifne.');
INSERT INTO "apaguna" VALUES(12,16,'12:16','Isaanis galgala booyaatiin abbaa isaanii bira dhufan');
INSERT INTO "apaguna" VALUES(12,17,'12:17','“Yaa abbaa keenya! Nuti wal dorgomuuf Yuusufiin meeshaa keenya biratti dhiifnee deemneYomasan yeyyiin isa nyaateOdoma nuti warra dhugaa dubbatan taaneeyyuu ati nuuf kan amantuu miti” jedhan');
INSERT INTO "apaguna" VALUES(12,18,'12:18','Qamisa isaa irrattis dhiiga sobaatiin dhufan(Yaaquub) ni jedhe: “Lakkisaa; Lubbuu keessantu dubbii (sobaa) isiniif miidhagsiteObsi kiyya obsa gaariidhaWaan isin jettan kana irrattis gargaarsifatamaan Rabbi qofa.”');
INSERT INTO "apaguna" VALUES(12,19,'12:19','Kara deemtonnis (daldaaltoonnis) dhuftee nama isaaniif bishaan waraabu erganii baaldii isaa (boolla keessa) gadi buuse“Yaa gammachuu! Kuni mucaa dargaggeessa” jedheMeeshaa godhanii isa dhoksanRabbiin waan isaan hojjatan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(12,20,'12:20','Gatii xiqqaa, maallaqa lakkaa’amtuutti, isa gurguranIsa keessattis warra dhimma isaa hin qabne irraa ta’an.');
INSERT INTO "apaguna" VALUES(12,21,'12:21','Inni Misra irraa isa bites niitii isaatiin ni jedhe: “nu fayyaduun yookiin ilma isa taasifachuun keenya ni kejeelamaa (iddoo) qubsuma isaa kabaji.” (Akka bulchaa isa goonuu)fi hiikoo manaamaas isa barsiifnuuf jecha akka kanatti Yuusufiif dachii keessa mijjeessineRabbiinis dhimma Isaa irratti injifataadhaGaruu irra hedduun namaa hin beekanu');
INSERT INTO "apaguna" VALUES(12,22,'12:22','Yeroo inni jabummaa (qaama) isaa ga’u, ogummaafi beekumsa isaaf kennineAkka kanattis toltuu hojjattoota mindeessina.');
INSERT INTO "apaguna" VALUES(12,23,'12:23','Isheen inni mana ishee keessa jirus (qunnamtii saalaatiif) lubbuu isaa irraa isa barbaaddeHulaalees ni cuccufte“Ani siif qophaa’eeraa kottu” jette“Ani Rabbitti maganfadha! Inni (dhiirsi kee) gooftaa kiyya; jireenya kootis naaf tolchee jiraDhugumatti, miidhaa hojjattoonni hin milkaayanu” jedheen');
INSERT INTO "apaguna" VALUES(12,24,'12:24','Dhugumatti, isheen isatti yaaddee jirtiOdoo ragaa Gooftaa isaa arguu baatee innis isheetti yaadee tureAkka kanatti hamtuufi hojii fokkataa isa irraa deebisuuf (ragaa keenya isa agarsiifne)Dhugumatti, inni gabroota keenya qulqulleeffamoo irraayyi.');
INSERT INTO "apaguna" VALUES(12,25,'12:25','(Isaan lamaan) gara balbalaa wal dorgomanIsheenis qamiisa isaa gara duubatiin kutteLamaanuu balbala biratti gooftaa (dhirsa) ishee arganIsheen ni jette: “Mindaan nama warra (niitii) keetti hamtuu yaadee hidhamuu yookiin adabbii laalessaa malee homaa hin taane.”');
INSERT INTO "apaguna" VALUES(12,26,'12:26','Inni (Yuusuf) ni jedhe: “Isheetu (qunnamtii saalaatiif) lubbuu kiyya irra na barbaaddeRagaa ba’aan maatii ishee irraa ta’e tokkos (akkana jechuun) ragaa ba’e: “Yoo qamiisni isaa gara duraatiin baqaqfame, isheen dhugaa jettee inni sobdoota irraayi');
INSERT INTO "apaguna" VALUES(12,27,'12:27','Yoo qamiisni isaa gara duubaatiin baqaqfame, isheen ni sobdee inni dhugaa dubbattoota irraayi.”');
INSERT INTO "apaguna" VALUES(12,28,'12:28','Yeroma qamiisni isaa gara duubatiin kan baqaqfame ta’uu argu, (dhirsi ishee) ni jedhe: “Dhugumatti, inni kun tooftaa keessan (dubartootaa) irraayyiDhugumatti, tooftaan keessan guddaadha');
INSERT INTO "apaguna" VALUES(12,29,'12:29','Yuusuf kana irraa garagaliAti (dubartittiin) immoo badii keetiif araarama kadhuDhugumatti, ati warreen dogoggoran irraa taatee jirta.”');
INSERT INTO "apaguna" VALUES(12,30,'12:30','Dubartoonni magaalattii keessa jiranis ni jedhan: “Niitiin mootichaa gabra ishee (qunnamtii saalaatiif) lubbuu isaa irraa isa feeteJaalalli isaas onnee ishee gubee jiraDhugumatti, nuti jallina ifa ta’e keessatti ishee agarra.”');
INSERT INTO "apaguna" VALUES(12,31,'12:31','Yeroma hamii isaanii dhageessu, gara isaaniitti ergiteNyaatas isaaniif qopheessiteTokko tokkoon isaaniitiifis hablee (billaa) ni kenitte“Isaan irratti gadi bahi” jetteYeroma isa argan isa dinqisiifatanHarka isaaniis kukkutan“Rabbiin qulqulaa’ee inni kun namaa mitiInni kun maleykaa gaariidha malee waan biraatii miti” jedhan');
INSERT INTO "apaguna" VALUES(12,32,'12:32','(Isheenis) ni jette: “kun isa isin isa irratti na komattaniidhaAnumatu (qunnamtii saalaatiif) lubbuu isaa irraa isa feenaan dideYoo inni waan ani isa ajaju dalaguu dide Dhugumatti ni hidhama; Salphattoota irraas ni ta’a” jette');
INSERT INTO "apaguna" VALUES(12,33,'12:33','[Inni] “Gooftaa kiyya! Hidhaatu waan isaan itti na waaman irra ana biratti jaalatamaadhaYoo Ati tooftaa isaanii narraa deebifte malee, ani gara isaaniin daba; wallaaltota irraas nan ta’a” jedhe');
INSERT INTO "apaguna" VALUES(12,34,'12:34','Kanaaf Gooftaan isaa isaaf awwaatee tooftaa isaaniis isarraa deebiseDhugumatti, Inni (Rabbiin) Isatu dhaga’aa, beekaadha');
INSERT INTO "apaguna" VALUES(12,35,'12:35','Sana booda erga mallattoowwan (qulqullummaa isaa mul’isu) arganii, yeroo murtaa’eef isa hiina jechuutu isaaniif mul’ate');
INSERT INTO "apaguna" VALUES(12,36,'12:36','Isa waliin dargaggeessi lamas mana hidhaa seenaniTokkoon isaanii ni jedhe: “ani (abjuudhaan) kanin farsoo cuunfun of arge.” Inni biraa immoo ni jedhe: “ani mataa kiyya gubbaatti daabboo baadhee kan allaattiin isa irraa nyaattun arge.” “Gaarii hojjattoota irraa ta’uu kee arginaa hiika isaa nutti himi.”');
INSERT INTO "apaguna" VALUES(12,37,'12:37','(Yuusuf) ni jedhe: “nyaanni isin isa nyaattan odoo inni isinitti hin dhufin dura hiika isaa isin lamaanitti himu malee isinitti hin dhufu; Kun wantoota Gooftaan kiyya na barsiise irraayyiDhugumatti, ani amantii (karaa) ummata Rabbitti hin amannee isaanis Aakiraatti kaafiroota ta’anii dhiiseera.');
INSERT INTO "apaguna" VALUES(12,38,'12:38','Amantii abboota kiyya Ibraahiim, Isihaaqiifi Ya’aquubis hordofeeraWaan tokkos Rabbitti qindeessuun nuuf hin taaneKuni tola Rabbii kan nuufi namoota (hunda) irra jiruudhaGaruu irra hedduun namootaa (Rabbiin) hin galateeffatanu.');
INSERT INTO "apaguna" VALUES(12,39,'12:39','Yaa hiriyoota kiyya lamaan kan mana hidhaa! Sila gooftota adda addaa baay’ee moo Rabbii tokkicha hunda mo’ataa ta’etu caala?');
INSERT INTO "apaguna" VALUES(12,40,'12:40','Maqoota isiniifi abbootiin keessan moggaaftan malee Rabbii gaditti waan biraa gabbaraa hin jirtanuRabbiin ishee (gabbaruu)tti ragaa tokkos hin buufne; murtiin Rabbiif malee eenyuufuu hintaaneIsa malees akka hin gabbarre ajajeeraKun amantii gadi dhaabbataadhaGaruu irra hedduun namaa hin beekanu');
INSERT INTO "apaguna" VALUES(12,41,'12:41','Yaa hiriyoota kiyya kan mana hidhaa! Egaa isin lamaan keessaa tokko gooftaa (mootii) isaa farshoo obaasaInni biraa immoo fannifameeti allaattiin mataa isaa irraa nyaattiDhimmi inni isin isa keessatti hiika gaafattan murtaa’ee jira.”');
INSERT INTO "apaguna" VALUES(12,42,'12:42','Issaan lamaan keessaa isa (nagaa) baha jedhee yaadeen “Gooftaa kee biratti na dubbadhu” jedheenGooftaa isaatti dubbachuu sheyxaanni isa dagachiiseWaggaa muraasa mana hidhaa keessa ture');
INSERT INTO "apaguna" VALUES(12,43,'12:43','Mootichis ni jedhe: “Dhugumatti, ani sa’oota gabbataa ta’an torba kan (sa’oonni) huqqatoo ta’an torba nyaatan, akkasumas sootalloo (mataa midhaanii) magariisa torbaafi kan biraas gogaa (torba) abjuu keessatti argeeraYaa qondaaltota (kiyya)! yoo kan manaama hiiktanu taataniif manaama kiyya kana naaf hiikaa.”');
INSERT INTO "apaguna" VALUES(12,44,'12:44','“(Kuni) bursusa (yaacoo) manaamaatiNutis hiika bursusaa kan beeknu hin taane” jedhani.');
INSERT INTO "apaguna" VALUES(12,45,'12:45','Inni isaan lamaan (mana hidhaa keessa turan) irraa nagaa ba’eefi yeroo dheeraa booda (Yuusufiin) yaadatee ni jedhe: “Anatu hiika isaa isinitti himaa na ergaa.” (Gara Yuusufis ni deeme)');
INSERT INTO "apaguna" VALUES(12,46,'12:46','(Innis ni jedhe): “Yaa Yuusufii dhugaa dubbataa taate! Sa’oota gabbatoo torba kan (sa’oonni) huqqatoon torba isaan nyaataniifi sootalloo (mataa midhaanii) magariisaa torbaafi kan biraas gogaa (torba) hiika (abjuu kanaa) akka beekanuuf gara namaa deebi’ee isaanitti himuun koo ni kajeelamaa nuuf himi');
INSERT INTO "apaguna" VALUES(12,47,'12:47','Ni jedhe: “Waggaa walitti aanan torbaaf akkuma barametti facaafattuXiqqoo waan irraa nyaattan malee waan oomishtan sootalloo isaa keessatti isa dhiisaa');
INSERT INTO "apaguna" VALUES(12,48,'12:48','Ergasii san booda, xiqqoo waan kuuftanii malee waan isin isaaniif keessan hunda kan nyaatanu waggoota rakkinaa torbatu dhufa');
INSERT INTO "apaguna" VALUES(12,49,'12:49','Ergasii sana booda waggaa isa keessa namoonni rooba argataniifi isa keessas (inaba) cuunfatantu dhufa.”');
INSERT INTO "apaguna" VALUES(12,50,'12:50','Mootichis “Isa natti fidaa jedhe.” Yeroma ergamaan (mootichaa) isatti dhufu, (Yuusuf) ni jedhe: “Gara gooftaa keetii deebi’ii ‘waa’een dubartoota harka isaanii muranii maali’ (jedhii) isa gaafadhuDhugumatti Gooftaan kiyya tooftaa isaanii beekaadha.”');
INSERT INTO "apaguna" VALUES(12,51,'12:51','(Mootichis) “Yeroo Yuusufiin (qunn-amtii saalaatiif) lubbuu isaa irra isa barbaaddan haalli keessan maal ture” jedheen(Dubartoonnis) ni jedhan: “Rabbiin qulqullaa’ee hamtuu tokkollee isa irratti hin beeknu.” Niitiin mootichaas “Amma dhugaan ifa ta’eeraAnatu (qunnamtii saalaatiif) isa fedheDhugumatti, inni warreen dhugaa dubbatan irraayyi.”');
INSERT INTO "apaguna" VALUES(12,52,'12:52','(Yuusufis) “kun (gooftaan kiyya) fagootti (bakka inni hin jirretti) akka an isa hin ganiniifi Rabbiin dhugumatti tooftaa gantootaa hin sirreessu jechuu akka beekuufi” (jedhe)');
INSERT INTO "apaguna" VALUES(12,53,'12:53','“Lubbuu kiyyas hin qulqulleessuDhugumatti, nama Gooftaan kiyya rahmata godheef malee lubbuun hamtuutti ajajjuudhaDhugumatti, Gooftaan kiyya araaramaa, rahmata godhaadha.”');
INSERT INTO "apaguna" VALUES(12,54,'12:54','Mootichis ni jedhe: “lubbuu kootiifan isa qulqulleessaa isaan natti kottaaYeroma isa (Yuusufiin) hasofsiisu, “Dhugumatti, ati har’a nu biratti mijjeeffataa amanamaadha” jedheen.');
INSERT INTO "apaguna" VALUES(12,55,'12:55','(Yuusufis) “kuusaa dachii irratti (itti gaafatamaa) na taasisi; ani isa irratti eegaa beekaadha” jedhe.');
INSERT INTO "apaguna" VALUES(12,56,'12:56','Akka kanatti ishee irraa bakka fedhe kan qubatu ta’ee Yuusufiif dachii keessatti mijjeessineRahmata keenyaan nama feene tuqnaMindaa warreen toltuu hojjataniis hin balleessinu.');
INSERT INTO "apaguna" VALUES(12,57,'12:57','Dhugumatti, isaannan amananii (Rabbiin) sodaattota ta’aniif mindaa Aakiraatu caala.');
INSERT INTO "apaguna" VALUES(12,58,'12:58','Obboleeyyan Yuusufis gara isaa dhufanii isa irratti seenanOdoo isaan isa wallaalanii jiranii inni isaan beeke.');
INSERT INTO "apaguna" VALUES(12,59,'12:59','Yeroma waan geggeessaa isaanii isaaniif qopheesse ni jedhe: “Obboleessa keessan kan karaa abbaatiin ta’een natti kottaaSila caalaa keessummmessitootaa ta’ee safara guutuu koo hin argitanuu?');
INSERT INTO "apaguna" VALUES(12,60,'12:60','Yoo isaan natti dhufuu baattan safarri na bira isiniif hin jiruNattis hin dhiyaatinaa.”');
INSERT INTO "apaguna" VALUES(12,61,'12:61','[Isaan] “Nuti waa’ee isaa abbaa isaa ni gaafannaDhugumatti, nuti (dhimma kana) raawwatoodha.”');
INSERT INTO "apaguna" VALUES(12,62,'12:62','Gabroota isaatiin “akka yeroo gara maatii isaanii deebi’an ishee beekanii deebi’aniif maallaqa isaanii fe’isa isaanii keessa kaa’aa”jedhe.');
INSERT INTO "apaguna" VALUES(12,63,'12:63','Yeroma gara abbaa isaanii deebi’an ni jedhan: “Yaa abbaa keenya! safarri (midhaanii) nurraa dhorgameeraObboleessa Kenya nuu wajjiin ergiNi safarannaNuti dhugumatti isaaf tiksoodha.”');
INSERT INTO "apaguna" VALUES(12,64,'12:64','(Ya’aquub) ni jedhe: “Sila akkan duraan obboleessa isaa irratti isin amane malee isa irratti isin amanaa? Rabbiin eegaa ta’uutti caalaadhaInni irra rahmata godhaa warreen rahmata godhaniiti.”');
INSERT INTO "apaguna" VALUES(12,65,'12:65','Yeroma meeshaa isaanii bananii maallaqa isaanii kan isaaniif deebi’e ta’uu arganis ni jedhan: “Yaa abbaa keenya! (kana caalaa) maal barbaanna? Isheen kuni maallaqa keenya; gara keenya deebifamteMaatii keenyaafis midhaan ni binnaObboleessa keenyas ni tiksinaFe’iinsa (ba’aa) gaalaa tokkoos ni dabalannaKun (mooticha biratti) safara xiqqoodha.”');
INSERT INTO "apaguna" VALUES(12,66,'12:66','(Ya’aquub) ni jedhe: “Yoo (diinaan) marfamtan malee hanga akka isa deebiftanii naaf fiddanutti kakuu Rabbi irraa ta’e naaf kennitanitti isa isin wajjiin hin ergu.” Yeroma isaan kakuu isaanii isaaf kennan “Rabbiin waan nuti jennu irratti ragaadha” jedhe.');
INSERT INTO "apaguna" VALUES(12,67,'12:67','Ni jedhe: “Yaa ilmaan kiyya! balbala tokkoon hin seeninaaBalbaloota adda addaatiin seenaaAnis (murtii) Rabbii irraa homaa isin irraa hin duroomsuDhugumatti, murtiin Rabbiif malee hin maluIsa (qofa) irratti hirkadheHirkattoonnis isuma (qofa) irratti haa hirkatan.”');
INSERT INTO "apaguna" VALUES(12,68,'12:68','Yeroma isaan akkaataa abbaan isaanii isaan ajajeen seenanis, (murtii) Rabbii irraa homaa kan isaan duroomsu hin taaneGaruu dhimma lubbuu Ya’aquub keessa jiru ishee raawwateeraDhugumatti inni waan isa barsiifne irratti abbaa beekumsaatiGaruu irra hedduun namaa hin beekanu');
INSERT INTO "apaguna" VALUES(12,69,'12:69','Yeroma isaan Yuusuf irra seenan obboleessa isaa (Ben-yaamiiniin) ofitti dhiheessee “Dhugumatti, ani anatu obboleessa keetii, waan isaan hojjachaa turanittis hin gaddin” jedhe.');
INSERT INTO "apaguna" VALUES(12,70,'12:70','Yeroma meeshaa isaanii isaaniif qopheessu, safartuttii meeshaa obboleessa isaa keessa kaa’eSana booda “isin yaa daldaaltoota dhugumatti, hattoota” (jechuun) lallabaan lallabe.');
INSERT INTO "apaguna" VALUES(12,71,'12:71','Isaanis, gara isaanii gara galuun, “Maal dhabdan?” jedheniin.');
INSERT INTO "apaguna" VALUES(12,72,'12:72','Ni jedhan: “Nuti safartuu mootii dhabnee; nama isa fideefis (badhaasa) fe’aa gaalaa tokkotu isaaf jiraAni kana irratti waadaa galaadha.”');
INSERT INTO "apaguna" VALUES(12,73,'12:73','Isaanis ni jedhan: “Rabbitti kakannee! Akka nuti dachii keessatti badii raawwachuuf hin dhufin isinis beektaniittuNuti hattootas hin taane.”');
INSERT INTO "apaguna" VALUES(12,74,'12:74','“Yoo sobdoota taataniif adabbiin isaa maal ta’a?” jedhaniin.');
INSERT INTO "apaguna" VALUES(12,75,'12:75','Ni jedhan: “Adabni isaa namni inni meeshaa isaa keessatti argame isumatu (fudhamuutu) adabbii isaatiNuti akka kanatti isaan miidhaa (hanna) raawwatanu adabna.”');
INSERT INTO "apaguna" VALUES(12,76,'12:76','Sakkatta’uus qalqalloo obboleessa isaa dura qalqalloo isaaniitiin jalqabeSana booda meeshaa obboleessa isaa keessaa ishee baaseAkka kanatti Yuusufiif tooftaa baafneOdoo Rabbiin Fedhuu baatee obboleessa isaa seera mootichaa keessatti kan fudhatu hin turreNama feene sadarkaa hedduu ol fuunaAbbootii beekumsaa hundaa olittuu beekaatu jira.');
INSERT INTO "apaguna" VALUES(12,77,'12:77','Isaanis (obboleeyyan Yuusufis) ni jedhan: “Yoo hates, obboleessi isaas (Yuusufis) kana dura hatee ture.” Yuusuf ishee of keessa dhokseIsaaniifis ishee hin ibsine(Of keessattis) ni jedhe: “Isinumatu sadarkaan keessan gadheedhaRabbiinis waan isin jettan irra beekaadha.”');
INSERT INTO "apaguna" VALUES(12,78,'12:78','Isaanis ni jedhan: “Yaa mootichaa! Dhugumatti, isaaf abbaa jaarsa manguddoo ta’etu jiraKanaaf bakka isaa tokkoon keenya fudhadhuNuti gaggaarii irraa ta’uu kee si argina.”');
INSERT INTO "apaguna" VALUES(12,79,'12:79','Inni ni jedhe: “Nuti nama qabeenya keenya isa biratti argine malee fudhachuu irraa Rabbitti maganfannaDhugumatti, nuti yeroo san miidhaa hojjattoota.”');
INSERT INTO "apaguna" VALUES(12,80,'12:80','Yeroma isa irraa abdii muratan, kan maryatan ta’anii kophaa ba’anGuddaan isaanii ni jedhe: “Sila dhugumatti abbaan keessan kakuu Rabbi irraa ta’e isin irratti qabachuufi kana duras Yuusuf irratti waan daangaa dabartan hin beektanuu? Kanaaf ani hanga abbaan kiyya naaf hayyamutti yookiin (hanga) Rabbiin dhimma kana naaf murteessutti dachii kana gadi hin dhiisuInnis irra caalaa warreen murtii kennaniiti.');
INSERT INTO "apaguna" VALUES(12,81,'12:81','Gara abbaa keessanii deebi’aatii jedhaanii: “Yaa abbaa keenya! dhugumatti, ilmi kee ni hateNutis waan beekneen malee ragaa hin baaneNuti waan fagoo tiksaa hin taane');
INSERT INTO "apaguna" VALUES(12,82,'12:82','(Namoota) magaalaa nuti ishee keessa turreefi daldaaltota nuti isaan wajjin dhufnes gaafadhuNuti dhugaa dubbatoodha.”');
INSERT INTO "apaguna" VALUES(12,83,'12:83','(Ya’aquub) ni jedhe: “Lakkisaa; lubbuu keessantu waan tokko isiniif midhaagsite; (Obsi koo) obsa bareedaadhaRabbiin hunda isaanii naaf deebisuun ni kajeelamaDhugumatti, Inni Isumatu beekaa, ogeessa.”');
INSERT INTO "apaguna" VALUES(12,84,'12:84','Isaan irraas gargalee “Yaa gadda kiyya! Yuusuf irratti” jedheYaaddoo irraa ijji isaa lamaanis addaatte; inni yaadaan guutamaadha.');
INSERT INTO "apaguna" VALUES(12,85,'12:85','“Rabbitti kakannee! ati hanga manguddoo dadhabaa taatutti yookiin warra du’an irraa taatutti Yuusufiin dubbachuu irraa hin deemtu!” jedhan.');
INSERT INTO "apaguna" VALUES(12,86,'12:86','(Ya’aquub) ni jedhe: “Ani yaaddoofi gadda kiyyas Rabbuma qofattin himadhaRabbi irraa waan isin hin beeknen beeka.');
INSERT INTO "apaguna" VALUES(12,87,'12:87','Yaa ilmaan kiyya! deemaa Yuusufiifi obboleessa isaa irraa odeeffadhaaRahmata Rabbii irraas abdii hin muratinaaDhugumatti, ummata kaafiroota malee rahmata Rabbii irraa eenyullee abdii hin kutu.”');
INSERT INTO "apaguna" VALUES(12,88,'12:88','Yeroma isa (Yuusuf) irra seenan “Yaa mootichaa! nuufi maatii keenyas rakkoon mudateeraMallaqa tuffatamtuun dhufnee safara nuuf guutii nurrattiis sadaqadhu” isaan jedhanDhugumatti, Rabbiin warreen sadaqatan ni mindeessa.');
INSERT INTO "apaguna" VALUES(12,89,'12:89','(Yuusufis) “Sila waan Yuusufiifi obboleessa isaa irratti yeroo wallaaloo turtan hojjattan beektuu?” jedheen.');
INSERT INTO "apaguna" VALUES(12,90,'12:90','[Isaan] “Sila dhugamatti ati Yuusufii?” jedhan[Inni] “Ani Yuusufi; inni kunis obboleessa kiyyaDhugumatti Rabbiin tola nutti ooleeraDhugumatti, namni (Rabbiin) sodaateefi obse, Rabbiin mindaa toltuu hojjattootaa hin balleessu” jedhe.');
INSERT INTO "apaguna" VALUES(12,91,'12:91','[Isaan]: “Rabbitti kakannee! Dhugumatti, Rabbiin nurra si filatee jiraDhugumatti nuti dogoggortoota turre” jedhan.');
INSERT INTO "apaguna" VALUES(12,92,'12:92','(Yuusufis) ni jedhe: “Har’a komiin isin irra hin jiru; Rabbiin isiniif araaramaInni irra rahmata godhaa warra rahmata godhaniiti');
INSERT INTO "apaguna" VALUES(12,93,'12:93','Qamisii kiyya kanaan deemaatii fuula abbaa kiyyaa irratti darbaa[Inni] argaa ta’ee dhufaMaatii keessan hundaanis natti kottaa.”');
INSERT INTO "apaguna" VALUES(12,94,'12:94','Yeroma daldaltoonni (Misraan) gadi dhiiftu, abbaan isaanii “Odoo isin otontoraatti (totorra’aatti) na yaaduu dhabdanii ani foolii yuusufin argaa jira” jedhe');
INSERT INTO "apaguna" VALUES(12,95,'12:95','[Isaan] “Rabbitti kakannee! Dhugumatti, ati jallinuma kee kan durii keessa jirta” jedhan.');
INSERT INTO "apaguna" VALUES(12,96,'12:96','Gammachiisaan Yeroma dhufu fuula isaa (Ya’aquub) irratti isa darbeInnis kan argu ta’e“Ani Rabbi irraa waan isin hin beeknen beeka isiniin hin jennee?” jedhe');
INSERT INTO "apaguna" VALUES(12,97,'12:97','[Isaan] “Yaa abbaa keenya! Dhugumatti, nuti dogoggortoota taanee jirraa badii keenyaaf (Rabbiin) araarama nuuf kadhu” jedhan');
INSERT INTO "apaguna" VALUES(12,98,'12:98','(Ya’aquubis) “Ani fuul-duratti Gooftaa kiyya araarama isiniifin kadhaDhugumatti, Inni Isatu araaramaa, rahmata godhaadha” jedhe.');
INSERT INTO "apaguna" VALUES(12,99,'12:99','Ergasii yeroo Yuusufiin bira seenan, haadhaafi abbaa isaa gara ofiitti qubsiise“Yoo Rabbiin fedhe nagayaan Misraan seenaa” jedhe');
INSERT INTO "apaguna" VALUES(12,100,'12:100','Abbaafi haadha isaas siree irratti ol fuudheIsaanis sujuuda isaaf bu’an[Inni] ni jedhe: “Yaa abbaa kiyya! kun hiika manaama kiyyaa kan duraaniitiGooftaan kiyya dhugaa ishee taasiseeraErga shayxaanni anaafi obboleeyyan kiyya gidduutti diinummaa uumee booda yeroo ana mana hidhaa irraa baasee isinis baadiyaa irraa fidu dhugumatti, Gooftaan kiyya tola natti oolee jiraDhugumatti, Gooftaan kiyya nama fedheef mararfataadhaDhugumatti, Inni Isa qofatu beekaa, ogeessa.');
INSERT INTO "apaguna" VALUES(12,101,'12:101','Gooftaa kiyya! dhugumatti mootummaa irraa naaf kennitee jirtaHiika manaamotaa irraas na barsiiftee jirta(Yaa) Uumaa samiifi dachii! Ati Duuniyaafi Aakhiraattis gargaaraa kiyyaMuslima taasisii na ajjeesii Warra gaggaarittis na dhaqqabsiisi.”');
INSERT INTO "apaguna" VALUES(12,102,'12:102','Kun oduu fagoo irraayyiGara keetti isa beeksifnaYeroo isaan kan (Yuusufitti) tooftaa baasan ta’anii dhimma isaanii waliin qindeessan ati isaan bira hin turre.');
INSERT INTO "apaguna" VALUES(12,103,'12:103','Irra hedduun namootaas (amanuu isaaniitiif) odoo bololtees kan amananii miti.');
INSERT INTO "apaguna" VALUES(12,104,'12:104','Ati isa irratti mindaa wahii isaan hin gaafattuInni (Qur’aanni) aalamaaf gorsa malee homaa hin taane.');
INSERT INTO "apaguna" VALUES(12,105,'12:105','Samiifi dachii keessa mallattoo kan isaan ishee irraa gara galan ta’anii ishee irra taran heddutu jira.');
INSERT INTO "apaguna" VALUES(12,106,'12:106','Irra hedduun isaaniis qindeessitoota ta’anii malee Rabbitti hin amananu.');
INSERT INTO "apaguna" VALUES(12,107,'12:107','Sila tasa haala isaan hin beekneen Rabbi irraa haguugduun adabbii Rabbii irraa taate isaanitti dhufuu yookiin Qiyaamaan isaanitti dhufuu of amanuu?');
INSERT INTO "apaguna" VALUES(12,108,'12:108','“Kun karaa kiyyaAniifi namni na hordofes beekumsa irratti haala ta’een gara Rabbiitti waamaRabbiinis qulqullaa’eAnis Mushrikoota irraayyii miti” jedhi.');
INSERT INTO "apaguna" VALUES(12,109,'12:109','Si duras dhiirota warra gandaa irraa wahyii gara isaaniitti buufne malee hin ergineSila dachii keessa deemanii booddeen warra isaaniin dura turanii akkam akka ta’e hin ilaalanuu? Dhugumatti, isaan (Rabbiin) sodaataniif mana Aakhiraatu caalaSila hin xiinxaltanuu?');
INSERT INTO "apaguna" VALUES(12,110,'12:110','Yeroma Ergamoonni abdii murataniifi dhugumatti kijibsiifamuu beekanitti tumsi keenya isaaniif dhufee namni Nuti feenes baraarameadabbiin keenyas yakkamtoota irraa hin deebifamu.');
INSERT INTO "apaguna" VALUES(12,111,'12:111','Dhugumatti, seenaalee isaanii keessa warra sammuu qabaniif gorsatu jiraInni (Qur’aanni) oduu lafaa kaafamee odeeffame hin taaneGaruu dhugoomsaa (kitaabban) isa duraatiAddeessaa wantoota hundaati; ummata amananiifiis qajeelfamaafi rahmata');
INSERT INTO "apaguna" VALUES(13,1,'13:1','Alif Laam Miim RaaKun keeyyattoota kitaabaatiInni Gooftaa kee biraa gara keetti buufame dhugaadhaGaruu irra hedduun namaa hin amananu.');
INSERT INTO "apaguna" VALUES(13,2,'13:2','Rabbiin Isa samii (torban) utubaa malee uumeedha; isinuu ishee ni agartusana booda Arshii irratti (akka Isaaf maluun) ol ta’eAduufi baatiis ni laaffiseHundinuu hanga beellama murtaa’eetti yaa’uWantoota hunda gara gaggalchaAkka isin qunnamuu Rabbii keessanii mirkaneeffattaniif jecha mallattoolee addaan baasa');
INSERT INTO "apaguna" VALUES(13,3,'13:3','Inni Isa dachiis diriirsee ishee keessatti gaarreeniifi laggeeniis godheedhaFuduraalee hunda irraas gosoota lama ishee keessatti godheeraHalkan guyyaa haguugsisaDhugumatti, kana keessa namoota xiinxalaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(13,4,'13:4','Dachii keessas kutaalee walitti ollooman, ashaakiltii Inaba irraa ta’an, sanyiiwwan faca’aniifi muka Timiraa damee qabaniifi damee hin qabne, kan bishaan tokkoon obaafamantu jiraDhandhama keessatti garii isaanii garii irra caalchifneerraDhugumatti, kana keessa ummata xiinxalaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(13,5,'13:5','Yoo kan raajeffattu taateef jecha isaanii kan “sila yeroo biyyee taane nuti uumama haaraa keessatti (taanaa)?” (jedhutu) raajiidhaIsaan warra Gooftaa isaaniitti kafaraniidhaIsaan sun sakaallaan morma isaanii keessa jiraIsaan warra ibiddaatiIsaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(13,6,'13:6','Odoo isaan dura adabbiin hedduun dabree jiruu, hamtuudhaan toltuu dura si ariifachiifatuDhugumatti, Gooftaan kee namaaf miidhaa hojjachuu isaaniitii wajjiin abbaa araaraatiGooftaan kee abbaa adabbii cimaati');
INSERT INTO "apaguna" VALUES(13,7,'13:7','Isaannan kafaranis “Maaliif mallattoon wahii Gooftaa isaa biraa isa irratti hin buufamne?” jedhuAti dinniinuma qofaUmmata hundaaf qajeelchaan jira.');
INSERT INTO "apaguna" VALUES(13,8,'13:8','Rabbiin waan dhaltuun hundi baattu, waan gadaamessi hir’iftuufi waan dabaltus ni beekaWanti hundinuu Isa biratti hanga murtaa’aadhaani');
INSERT INTO "apaguna" VALUES(13,9,'13:9','(Rabbiin) Beekaa waan fagoofi dhihoo, guddaa, ol ta’aadha.');
INSERT INTO "apaguna" VALUES(13,10,'13:10','Isin irraa namni jechaan (sagalee) gadi qabeefi ol fuudhes, ammas namni halkan dhokateefi guyyaa ifatti ba’es waluma qixa');
INSERT INTO "apaguna" VALUES(13,11,'13:11','Isaaf (tokko tokkoo namaatiif) maleeykota wal tareeffatoo ta’an fuuldura isaatiifi duuba isaa irraa ajaja Rabbiitiin isa eegantu jiruDhugumatti, Rabbiin waan ummata bira jiru hanga isaan waan isaan bira jiru jijjiiranitti isaan irraa hin jijjiiruYeroo Rabbiin namoota wahiitti hamtuu fedhe, deebisaan isaaf hin jiru; Isaaniifiis Isaa gaditti gargaaran hin jiru.');
INSERT INTO "apaguna" VALUES(13,12,'13:12','Inni Isa kan sodaattaniifi kan kajeeltan taatanii hangaasuu isin agarsiisuudhaDuumessa ulfaataas ni uuma.');
INSERT INTO "apaguna" VALUES(13,13,'13:13','Mandiisuun (kakawweennis) faaruu Isaatiin (Isa) qulqulleessaMaleeykonnis sodaa Isaa irraa (Isa qulqulleessu)Bakakkaas ergee, haala isaan (kaafiroonni) Rabbiin keessatti wal falmanuun, isheedhaan nama fedhe rukutaInni humni isaa cimaadha.');
INSERT INTO "apaguna" VALUES(13,14,'13:14','Waamichi dhugaa kan IsaatiWarri isaan Isa (Rabbii) gaditti waammatan, Akka (owwaannaa) nama inni afaan isaatti haa ga’uuf haala Inni isatti hin geenyeen ganaa isaa lamaan gara bishaanii hiixatuu (diriirsuu) malee isaaniif hin awwaatanKadhaan kaafirootaa hoongoo keessa malee hin taane.');
INSERT INTO "apaguna" VALUES(13,15,'13:15','Namoonni samiifi dachii keessa jiran kan jaalataniifi kan jibban ta’anii, Gaaddidduuwwan isaaniis ganamaafi galgala Rabbiif sujuuda godhu.');
INSERT INTO "apaguna" VALUES(13,16,'13:16','“Gooftaan samiifi dachii eenyu?” jedhiin“Allaahdha” Jedhi“Sila Isaa gaditti jaalallee lubbuu isaaniitiif bu’aas ta’ee miidhaa hin argamsiifne taasifattanii?” jedhiin“Sila jaamaafi namni argu wal qixa ta’uu? dukkanaafi ifnis wal qixa ta’aa? jedhiinSila isaan Rabbiif shariikowwan uumama akka Isaa uumanii uumamni isaan irratti wal fakkaate godhanii? “Rabbiin uumaa waan hundaati; Inni tokkicha injifataadha” jedhi');
INSERT INTO "apaguna" VALUES(13,17,'13:17','Samii irraa bishaan (rooba) buusee laggeen hanga isheetiin ni yaate; lolaanis hoomacha ol fuudhamaa baadhate; waan isaan faayaafi meeshaa barbaaduuf jecha ibidda irratti qabsiisan irraas hoomacha (xurii) akkasiitu jiraAkka kanatti Rabbiin dhugaafi soba fakkeenya godha; hoomachi goggogaa ta’ee deemaWanti nama fayyadu immoo dachii keessa taa’aAkka kanatti Rabbiin fakkeenya godha');
INSERT INTO "apaguna" VALUES(13,18,'13:18','Isaan Gooftaa isaaniitii awwaataniif jannatatu jiraIsaan Isaaf hin awwaatin immoo, odoo wanti dachii keessa jiru hundiifi fakkaataan isaas (isaaniif argamee) silaa isaan of bituWarri sun isaaniif qormaata hamaatu jiraTeessoon isaaniis jahannami(Inni) afata ta’uun waa fokkataa dha!');
INSERT INTO "apaguna" VALUES(13,19,'13:19','Sila namni wanti Gooftaa kee irraa gara keetti bu’e dhugaa ta’uu isaa beeku akka nama jaamaatii? warra sammuu qabu qofatu gorfama.');
INSERT INTO "apaguna" VALUES(13,20,'13:20','(Isaan) warra waadaa Rabbii guutaniifi waadichas hin diigneedha.');
INSERT INTO "apaguna" VALUES(13,21,'13:21','Ammas warra waan Rabbiin akka inni fufamu ajaje fufanii Gooftaa isaaniis sodaataniifi hamaa qormaataa sodaataniidha.');
INSERT INTO "apaguna" VALUES(13,22,'13:22','Ammas isaan fuula Gooftaa isaanii barbaacha obsan, kan salaatas dhaabanii, waan Nuti isaaniif kennine irraa dhoksaniifi mul’isanii arjooman, kan waan gadhee waan gaariin deebisaniidhaWarri sun gandi boodaa isaaniif ta’aadha');
INSERT INTO "apaguna" VALUES(13,23,'13:23','(Isheen) Jannata qubsumaa kan isaan ishee seenanuudha; Abbootii isaanii, niitiwwan isaanii, ilmaan isaanii irraas namni gaarii hojjate (ishee seena)Maleeykonnis balbala hundaan isaanitti seenu.');
INSERT INTO "apaguna" VALUES(13,24,'13:24','“Waan obsitaniif nageenyi isin irratti haa jiraatu; gandi boodaa waa tole!” (jedhu).');
INSERT INTO "apaguna" VALUES(13,25,'13:25','Isaan waadaa Rabbii erga jabaachuu isaatii booda diiganiifi waan Rabbiin akka fufamu ajaje kutan, kan dachii keessatti badii raawwatan warri sun abaarsi isaaniif jira; isaaniif ganda hamaa ta’e (jahannamtu) jira.');
INSERT INTO "apaguna" VALUES(13,26,'13:26','Rabbiin nama fedheef soorata bal’isa; (nama fedhe) itti dhiphisaIsaan jireenya addunyaa kanaatti gammadaniJireenyi addunyaa jireenya Aakhiraa biratti qananii yeroo gabaabaa malee hin taane.');
INSERT INTO "apaguna" VALUES(13,27,'13:27','Isaan kafaranis “Sila maaliif Gooftaa isaa biraa mallattoon wahii itti hin bu’in?” jedhu“Dhugumatti, Rabbiin nama fedhe ni jallisa; nama deebi’e immoo gara Isaatti qajeelcha” jedhi.');
INSERT INTO "apaguna" VALUES(13,28,'13:28','(Isaan) warra amananii, onneen isaanii zikrii Rabbiitiin tasgabbooftuudhaDhaga’aa! zikrii (faaruu) Rabbiitiin onneen ni tasgabboofti.');
INSERT INTO "apaguna" VALUES(13,29,'13:29','Isaan amananii, gaggaarii hojjatan, isaaniif jiruu gaariifi deebii miidhagaa ta’etu jira');
INSERT INTO "apaguna" VALUES(13,30,'13:30','Akka kanatti gara ummata ishee dura ummatoonni biraa dabrite keessatti odoma isaan ‘Rahmaanitti’ kafaranuu akka isa gara keetti buufne isaan irratti dubbiftuuf si ergine(Yaa Muhammad!) “Inni Gooftaa kiyya, Isa malee gabbaramaan haqaa hin jiruIsa irratti hirkadheeraGaruma Isaattiin tawbaas deebi’a” jedhi.');
INSERT INTO "apaguna" VALUES(13,31,'13:31','Odoo Qur’aanni gaarreen (iddoo isaanii irraa) ittiin deemanii yookiin dachiin isaan ciccittee yookiin du’aan isaan dubbifameeyyuu (hin amananu)Amriin hunduu RabbumaafiSila isaan amanan akka Rabbiin odoo fedhee namoota hunda qajeelchu hin beekanuu? Isaan kafaran waan isaan dalaganiin qalbi dhooftuun isaan tuqxu ta’uu irraa yookiin hanga waadaan Rabbii dhufutti manneen isaaniitti dhihoo qubachuu irraa hin deemtuDhugumatti, Rabbiin waadaa hin diigu.');
INSERT INTO "apaguna" VALUES(13,32,'13:32','Ergamoota si duraattis qishnaan itti godhameera; isaan kafaraniif beellama godheen sana booda isaan qabeadabbiin kiyya akkam ta’e ree!');
INSERT INTO "apaguna" VALUES(13,33,'13:33','Sila Inni (Rabbiin) lubbuu hunda irratti waan isheen hojjatteen (tiksuun) dhaabbataa ta’e (akka gabbaramaa akkas hin ta’iniitii?) Isaan Rabbiifis hiriyoota (shariika) taasisan“Isaan maqaa dhahaa” jedhiSila waan Inni dachii keessatti hin beekne Isatti himtumoo jecha mul’ataa (kan sobaa dubbattu)?” Dhugumatti isaan kafaraniif tooftaan isaanii isaaniif miidhagfamee, karaa qajeelaa irraas deebifamaniiruNama Rabbiin isa jallises qajeelchaan isaaf hin jiru');
INSERT INTO "apaguna" VALUES(13,34,'13:34','Isaaniif jireenya addunyaa keessatti adabbiitu jiraadabbiin Aakhiraa immoo akkaan jabaadhaAmmas isaaniif (adabbii) Rabbii irraa ittisaan hin jiru.');
INSERT INTO "apaguna" VALUES(13,35,'13:35','Fakkeenyi jannata warri (Rabbiin) sodaatan waadaa galamanii, jala ishee laggeentu yaa’a; nyaanni ishee walitti dhaabbataadha; gaaddisni ishees (akkasuma)Kun booddee warra Rabbiin sodaataniitiBooddeen kaafirootaa immoo ibidda.');
INSERT INTO "apaguna" VALUES(13,36,'13:36','Warri nuti kitaaba isaaniif kennine, waan gara keetti buufametti ni gammaduAhzaaba irraas nama garii isaa mormutu jira“Kan ani ajajames Rabbiin qofa gabbaruufi Isatti homaa qindeessuu dhabuudha” jedhiGara Isaa qofattin waama; deebiin kiyyas gara Isaati.');
INSERT INTO "apaguna" VALUES(13,37,'13:37','Akka kanatti murtii Arabiffaa ta’ee isa (Qur’aana) buufneAtis, erga beekumsa irraa wanti siif kenname sitti dhufee booda, odoo fedha lubbuu isaanii hordoftee, silaa (adabbii) Rabbii irraa tumsaafi tiksaan siif hin jiru.');
INSERT INTO "apaguna" VALUES(13,38,'13:38','Dhugumatti, Nuti si duras ergamtoota erginee jirraNiitiiwwaniifi ilmaan isaaniif taasifnee jirraErgamaan tokkos yoo hayyama Rabbiitin ta’e malee mallattoo (raajii) tokkos fiduu hin danda’ubeellama hundaafuu galmeetu jira.');
INSERT INTO "apaguna" VALUES(13,39,'13:39','Rabbiin waan fedhe ni haqa; (waan fedhe immoo) ni mirkaneessaIsa biras haadha kitaabaa (Lawhal Mahfuuztu) jira');
INSERT INTO "apaguna" VALUES(13,40,'13:40','Yoo garii waan ittiin isaan sodaachifnee si argisiifne (tolte)Yookiin yoo si ajjeefnes (komiin sirra hin jiru)Wanti sirra jiru geessuu qofa; qormaanni immoo Nurra jira.');
INSERT INTO "apaguna" VALUES(13,41,'13:41','Sila akka Nuti dachii daangaa ishee irraa hir’isaa dhufnu hin ilaalanii? Rabbiin ni murteessa; murtii isaas wanti duubatti deebisu hin jiruInni qormaanni isaa saffisaadha.');
INSERT INTO "apaguna" VALUES(13,42,'13:42','Dhugumatti, warri isaaniin duraas mala baasanii jiruMalli hundi kan RabbiitiWaan lubbuun hundi hojjattu ni beekaKaafiroonnis dhumti gaariin eenyuuf akka ta’e beekuuf jiru.');
INSERT INTO "apaguna" VALUES(13,43,'13:43','Isaan kafaran “ati ergamaa miti” Siin jedhu“Gidduu kiyyaafi gidduu keessanitti ragummaan Rabbiifi namoonni beekumsi kitaabaa isaan bira jiru ga’aadha” jedhi.');
INSERT INTO "apaguna" VALUES(14,1,'14:1','Alif Laam Raa(Inni) kitaaba akka ati nama hayyama Gooftaa isaanitiin dukkana (kufrii) irraa gara ifaa (Islaamaa), gara karaa (Rabbii) injifataa, faarfamaa ta’ee baaftuuf gara keetti buufneedha');
INSERT INTO "apaguna" VALUES(14,2,'14:2','Rabbiin Isa wanti samii keessa jiruufi wanti dachii keessa jiru hundi kan Isaa ta’eedhaBadiin adabbii cimaa irraa ta’e kaafirootaaf qophaa’eera.');
INSERT INTO "apaguna" VALUES(14,3,'14:3','Isaan warra jireenya addunyaa kanaa isa Aakhiraa irra jaalatanii, karaa Rabbii irraa (namoota) deebisaniifi jallina ta’uu ishee barbaadaniidhaIsaan sun jallina fagoo keessa jiru');
INSERT INTO "apaguna" VALUES(14,4,'14:4','Nuti ergamaa irraa tokkos akka inni (ergaa keenya) isaaniif addeessuf jecha afaan ummata isaatiin malee hin ergineRabbiin nama fedhe ni jallisa; nama fedhe immoo ni qajeelchaInnis injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(14,5,'14:5','Nuti dhugumatti Muusaa raajiwwan keenyaan “Ummata kee dukkana irraa gara ifaatti baasi, Guyyoota Rabbiitiin isaan yaadachiisi” jechuun erginee jirraDhugumatti, kana keessa warra akkaan obsanii, cimsanii galateeffatan hundaaf mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(14,6,'14:6','Yeroo Muusaan ummata isaatiin jedhes [yaadadhu]:“Ni’imaa Rabbii kan yeroo adabbii laalessaa isin dhandhamsiisaa; ilmaan keessan qaqqalaa; dubartoota keessan lubbuun akka jiraatan kan dhiisan ta’anii ummata Fir’awni irraa isin baraarse of irratti yaadadhaaKana keessa mokkoroo guddaa Gooftaa keessan irraa ta’etu jira');
INSERT INTO "apaguna" VALUES(14,7,'14:7','Ammas yeroo Gooftaan keessan lallabee: “Yoo na galateeffattan, (ni’imaa kiyya irraa) isiniifin dabalayoo natti kafartan, dhugumatti adabbiin kiyya akkaan hamaadha” (jedhe yaadadhaa).');
INSERT INTO "apaguna" VALUES(14,8,'14:8','Muusaanis “Yoo isiniifi namoonni dachii irra jiran walumaa galatti kafartan (Rabbiin homaa hin miitan)” jedhedhugumatti Rabbiin dureessa, faarfamaadha.');
INSERT INTO "apaguna" VALUES(14,9,'14:9','Sila oduun warra isin dura turanii isin hin geenyee? (Oduun) Kan ummata Nuuh, Aad, Samuudiifi warra isaan boodaa kan Rabbiin malee homtuu isaan hin beekne (isin hin geenyee)? Ergamoonni isaanii ragaawwan ifa ta’aniin itti dhufanii harka isaanii afaan isaanii keessatti deebisani“Dhugumatti, nuti isa isin ittiin ergamtanitti kafarree jirraAmmas nuti isa isin itti nu waamtan irraa mamii dhamaasaa keessa jirra” jedhan.');
INSERT INTO "apaguna" VALUES(14,10,'14:10','Ergamoonni isaaniis ni jedhan: “Sila Rabbii uumaa samiifi dachii ta’e, kan badii keessan irraa isiniif araaramuufi hanga yeroo murtaa’ee isin tursiisuuf isin waamu (jiraachuu isaa) keessa shakkiin jiraa?” [Isaan] “Isin namuma akka keenyaa kan waan abbootiin keenya gabbaraa turan irraa nu dhorguu barbaadu malee homaa hin taane; mee ragaa ifa ta’een nutti kottaa” jedhan.');
INSERT INTO "apaguna" VALUES(14,11,'14:11','Ergamoonni isaanii isaanin ni jedhan: “Nuti nama akka keessanii malee homaa hin taaneGaruu Rabbiin gabroota Isaa irraa nama fedhe irratti tola oolaNuti hayyama Rabbiitiin ala taaytaa (ragaa) tokkoonis isinitti dhufuun nuuf hin taaneMu’uminoonni Rabbi irratti (qofa) haa hirkatanu.');
INSERT INTO "apaguna" VALUES(14,12,'14:12','Sila dhugumatti odoo Inni karaa keenya nu qajeelchee jiruu maaltu nuuf jiraayyi Rabbi irratti hin hirkanne! Dhugumatti rakkoo isin nurrraan geessan irratti ni obsinaHirkattoonni Rabbi irratti haa hirkatu.”');
INSERT INTO "apaguna" VALUES(14,13,'14:13','Warri kafaran ergamtoota isaaniitiin “dhugumatti, nuti dachii keenya irraa isin baafna yookiin gara amantii keenyaa akka deebitan” jedhanRabbiin isaaniis “Nuti dhugumatti miidhaa hojjattoota ni balleessina” jechuu beeksisa gara isaanitti godhe.');
INSERT INTO "apaguna" VALUES(14,14,'14:14','“Dhugumatti isaan booda dachittii irra isin qubsiifnaKun nama Na dura dhaabbachuu sodaateefi dinniina kiyyas sodaateefi.”');
INSERT INTO "apaguna" VALUES(14,15,'14:15','(Ergamoonnis Rabbi irraa) deeggarsa barbaadanOf tuulaan, mormaan hundi ni hoonga’e.');
INSERT INTO "apaguna" VALUES(14,16,'14:16','Fuuldura isaas jahannamtu jiraBishaan malaa irraas obaafama.');
INSERT INTO "apaguna" VALUES(14,17,'14:17','Fedha isaatiin ala liqimsuutti dirqama; gadi isa liqimsuuttis hin dhiyaatu (hin danda’u)Duuti kallattii hunda irraa isatti dhufaInnis kan du’uu mitiFuuldura isaas adabbii guddaa ta’etu jira');
INSERT INTO "apaguna" VALUES(14,18,'14:18','Fakkeenyi warra Rabbii isaaniitti kafaranii, dalagaaleen isaanii akka daaraa guyyaa bubbee cimaa qilleensi itti hammaatteetiWaan hojjatan irraa homaattuu (fayyadamuu) hin danda’anKun isatu jallina fagoodha');
INSERT INTO "apaguna" VALUES(14,19,'14:19','Sila dhugumatti Rabbiin samiifi dachii dhugaan uumuu hin argituu? (Rabbiin) yoo fedhe isin adeemsiseetiin uumama haaraa fida.');
INSERT INTO "apaguna" VALUES(14,20,'14:20','Kunis Rabbi irratti jabaa hin taane.');
INSERT INTO "apaguna" VALUES(14,21,'14:21','Hunda ta’anii Rabbiif mul’atandadhaboonnis isaan boonaniin “dhugumatti, nuti hordoftoota keessan turree, sila isin adabbii Rabbii irraa homa tokko nurraa deebisoo dhaa?” jedhan(Isaan boonanis) “Odoo Rabbiin nu qajeelchee, silaa nutis isin qajeelchina turre(Har’a) obsinus obsuu baannus nurratti waluma qixaIddoon baqannaa tokkos nuuf hin jiru” jedhu.');
INSERT INTO "apaguna" VALUES(14,22,'14:22','yeroma dhimmichi murteeffamu sheyxaanni ni jedhe: “Dhugumatti, Rabbiin waadaa dhugaa isiniif galeera; anis waadaa isiniif galeen isin ganeIsin waamnaanuma naaf owwaattan malee anaaf isin irratti aangoon tokkollee hin taaneofuma komadhaa malee ana hin komatinaaAni isiniif birmataa miti; isiniis naaf birmatoo mitiAni waan isin duraan (Rabbitti) na qindeessitan mormeera.” Dhugumatti, miidhaa raawwattoonni adabbii laalessaatu isaaniif jira.');
INSERT INTO "apaguna" VALUES(14,23,'14:23','Isaan amananii gaggaarii hojjatanis hayyama Gooftaa isaaniitiin ishee keessatti kan hafan ta’anii jannata laggeen ishee keessa yaatu seensifamanWal kabajaan isaanii ishee keessatti ‘salaam’ (jechuu dha).');
INSERT INTO "apaguna" VALUES(14,24,'14:24','Sila akka Rabbiin jecha gaarii akka muka gaarii hundeen ishee gadi dhaabbataa ta’ee dameen ishees samii keessatti ta’eetti fakkeenya godhe hin arginee?');
INSERT INTO "apaguna" VALUES(14,25,'14:25','Yeroo hundas hayyama Gooftaa isheetiin firii ishee ni kennitiAkka gorfamanuuf jecha Rabbiin fakkeenya namootaaf godha.');
INSERT INTO "apaguna" VALUES(14,26,'14:26','Fakkeenyi jecha fokkataa akka muka fokkataa gubbaa dachii irraa buqqifamtee isheedhaaf gadi dhaabannaan hin taaneeti.');
INSERT INTO "apaguna" VALUES(14,27,'14:27','Warra amanan jireenya addunyaafi Aakhiraa keessatti jecha gadi dhaabbataadhaan Rabbiin gadi (isaan) dhaabaRabbiin miidhaa hojjattoota ni jallisaRabbiin waan fedhe hojjata.');
INSERT INTO "apaguna" VALUES(14,28,'14:28','Sila gara isaan ni’imaa Rabbii kufriin jijjiiranii, ummata isaanii ganda badii qubsiisanii hin ilaallee?');
INSERT INTO "apaguna" VALUES(14,29,'14:29','Ishee kan seenan ta’anii Jahannamiin (seensisfaman)Iddoon qubannaa waa fokkate!');
INSERT INTO "apaguna" VALUES(14,30,'14:30','Karaa Isaa irraa (namoota) jallisuuf Rabbiif hiriyoota godhan“Dhugumatti deebiin keessan gara ibiddaatii (yeroo xiqqoof) qanani’aa!” jedhiin');
INSERT INTO "apaguna" VALUES(14,31,'14:31','(Yaa Muhammad!) gabroota kiyya warreen amananiin “odoo guyyaan bittaa gurgurtaafi jaalalleen isa keessa hin jirre hin dhufin dura salaata ni dhaaban, waan Nuti isaaniif kennine irraas kan dhoksaniifi mul’isan ta’anii ni arjoomu (sagadaa, arjoomaa)” jedhiin.');
INSERT INTO "apaguna" VALUES(14,32,'14:32','Rabbiin Isa samiifi dachii uumee samii irraas bishaan (rooba) buusee ittiin fuduraa irraa soorata isiniif baaseedhaDooniis akka ajaja Isaatiin galaana keessa deemtu isiniif laaffise; laggeenis isiniif laaffise.');
INSERT INTO "apaguna" VALUES(14,33,'14:33','Aduufi ji’as walitti fufuun kan deeman ta’anii isiniif laaffisee halkaniifi guyyaas isiniif laaffise.');
INSERT INTO "apaguna" VALUES(14,34,'14:34','Waan isin Isa kadhattan hunda irraa isiniif kenneTola Rabbii odoo lakkooftaniis ishee fixuu hin dandeessanuDhugumatti, namoonni daran miidhaa hojjataa daran kafaraadha.');
INSERT INTO "apaguna" VALUES(14,35,'14:35','Yeroo Ibraahiim jedhu [yaadadhu]:“Gooftaa kiyya! Biyya kana (Makkaan) tasgabbaa’aa taasisiAnaafi ilmaan kiyyaas taabota gabbaruu irraa nu fageessi.');
INSERT INTO "apaguna" VALUES(14,36,'14:36','Gooftaa kiyya! Dhugumatti, isaan hedduu nama irraa ta’e jallisanii jiru; Namni na hordofe, inni anarraayyiNama na dide immoo dhugumatti Ati araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(14,37,'14:37','Gooftaa keenya! Ani garii maatii kiyya irraa ta’e bowwaa (laga) qonna hin qabne, mana kee kabajamaa biratti dhiiseen jiraGooftaa keenya! Akka salaata gadi dhaabaniif jecha nama irraas onnee (isaanii) kan gara isaanii hawwitu taasisi; Akka isaan Si galateeffataniif fuduraalee irraas isaan soori.');
INSERT INTO "apaguna" VALUES(14,38,'14:38','Gooftaa keenya! Ati waan nuti dhoksinuufi waan nuti ifa goonu ni beektaRabbi irratti dachii keessattis ta’ee samii keessatti homtuu hin dhokatu');
INSERT INTO "apaguna" VALUES(14,39,'14:39','Faaruun (hundi) kan Rabbii manguddummaa irratti Ismaa’iiliifi Isihaaqiin naaf kenneetiDhugumatti, Gooftaan kiyya kadhaa dhaga’aadha');
INSERT INTO "apaguna" VALUES(14,40,'14:40','Gooftaa kiyya! Kan salaata dhaabu na godhiIlmaan kiyya irraas (akkasuma)Gooftaa keenya! kadhaa kiyyas narraa qeebali.');
INSERT INTO "apaguna" VALUES(14,41,'14:41','Gooftaa keenya! Anaaf, haadhaafi abbaa kiyyaafi mu’uminoota hundaafis guyyaa qormaanni godhamu san araarami.”');
INSERT INTO "apaguna" VALUES(14,42,'14:42','Rabbiin Waan miidhaa hojjattoonni raawwatan irraa dagataa ta’uu hin yaadinKan Rabbiin isaan tursiisuuf guyyaa isa keessa ijji banamtuufi ol fuudhamtuufi.');
INSERT INTO "apaguna" VALUES(14,43,'14:43','(Gara waamichaatti) mataa isaanii kan ol kaasan ta’anii (ijji isaanii ol kaati)Ijji isaaniis gara isaaniitti hin deebituQalbiin (sammuun) isaaniis duwwaadha.');
INSERT INTO "apaguna" VALUES(14,44,'14:44','Guyyaa adabichi isaanitti dhufee, warri miidhaa hojjatan: “Gooftaa keenya! hanga beellama muraasaatti nu tursiisi; waamicha Kee awwaannee ergamoota keetis hordofna” jedhan sana namoota akeekkachiisi“Sila isin kanaan dura (addunyaa irraa) deemsi nuuf hin jiru jechuun kakachaa hin turree?');
INSERT INTO "apaguna" VALUES(14,45,'14:45','Manneen warra lubbuu ofii miidhanii keessas hin jiraannee? Akkam akka isaanitti hojjannes ifa isiniif ta’eeraFakkeenya hedduus isiniif gooneerra” (jedhamaan).');
INSERT INTO "apaguna" VALUES(14,46,'14:46','(Isaan) dhugumatti mala isaanii malaniiruMalli isaanii (galanni isaa) Rabbiin bira jiraTooftaan isaaniis kan gaarri isa irraa bakka deemtuu miti.');
INSERT INTO "apaguna" VALUES(14,47,'14:47','Rabbiin waadaa Isaa kan ergamtoota Isaatiif gale diigaa ta’uu hin yaadinDhugumatti, Rabbiin injifataa, abbaa haaloo bahannaati.');
INSERT INTO "apaguna" VALUES(14,48,'14:48','Guyyaa dachiin dachii biraatti jijjiiramtee samiinis (akkasuma taatee), Rabbii tokkicha, injifataa ta’eef (uumamni hundi) walitti qabamanii mul’atan [yaadadhu].');
INSERT INTO "apaguna" VALUES(14,49,'14:49','Guyyaa san badii hojjattoota takaallaa keessatti kan walitti qindaa’an ta’anii argita.');
INSERT INTO "apaguna" VALUES(14,50,'14:50','Qamiisni isaaniis “qanxiraana” (reenjii) irraayyiIbiddis fuula isaanii ni haguugdi');
INSERT INTO "apaguna" VALUES(14,51,'14:51','Rabbiin lubbuu hunda waan isheen dalagdeen mindaa galchuuf (kana godhe)Dhugumatti, Rabbiin herregni isaa saffisaadha.');
INSERT INTO "apaguna" VALUES(14,52,'14:52','(Qur’aanni) kun akka ittiin akeekkachiifamanu, Inni (Rabbiin) gabbaramaa tokkicha ta’uu akka beekaniifi warri sammuu qaban akka ittiin gorfamaniif namaaf qaqqabsiifamaadha (gahaa dha).');
INSERT INTO "apaguna" VALUES(15,1,'15:1','Alif Laam RaaIsheen kun keewwattoota kitaabichaatiifi Qur’aana (waan hunda) ifa baasaa ta’eeti');
INSERT INTO "apaguna" VALUES(15,2,'15:2','Isaan kafaran (Guyyaa Qiyaamaa) odoo Muslimoota ta’anii baay’ee hawwu!');
INSERT INTO "apaguna" VALUES(15,3,'15:3','Haa nyaatanii; Haa qanani’anii; hawwiin (sobaa) isaan haa daguu isaan dhiisi(Itti gala isaaniis) fuula duratti ni beeku.');
INSERT INTO "apaguna" VALUES(15,4,'15:4','Beellamni beekamaan isheef jiraatu malee (warra) magaalaa tokkollee hin balleessine.');
INSERT INTO "apaguna" VALUES(15,5,'15:5','Ummanni tokkollee beellama ishee hin dursitu, boodas hin aansamanu.');
INSERT INTO "apaguna" VALUES(15,6,'15:6','“Yaa isa zikriin (Qur’aanni) isa irratti buufame! dhugumatti ati maraataadha” jedhan.');
INSERT INTO "apaguna" VALUES(15,7,'15:7','Yoo warra dhugaa dubbattoota irraa taate, maaliif maleeykotaan nutti hin dhufne?” (jedhan).');
INSERT INTO "apaguna" VALUES(15,8,'15:8','Nuti maleeykota dhugaan (adabaan) malee hin buufnuYoosan yeroon isaaniif kan kennamu hin taane.');
INSERT INTO "apaguna" VALUES(15,9,'15:9','Dhugumatti, Nuti Zikrii Numatu buuseNumatu isaaf tiksoodha.');
INSERT INTO "apaguna" VALUES(15,10,'15:10','Dhugumatti, Nuti siin duratti ummattoota duraanii keessatti (ergamtoota) erginee jirra.');
INSERT INTO "apaguna" VALUES(15,11,'15:11','Ergamaan tokkos isatti kan qishinan ta’anii malee isaanitti hin dhufne');
INSERT INTO "apaguna" VALUES(15,12,'15:12','Akka kanatti onnee yakkamtootaa keessa isa (sobsiisuu) seensifna.');
INSERT INTO "apaguna" VALUES(15,13,'15:13','Isatti hin amananuDhugumatti, gochi (adabbiin) warra duraanii dabartee jirti (wanti isaan tuqe isaan kanas ni tuqa)');
INSERT INTO "apaguna" VALUES(15,14,'15:14','Odoo balbala samii irraa ta’e isaan irratti bannee, ishee keessa kan ol ba’an ta’anii;');
INSERT INTO "apaguna" VALUES(15,15,'15:15','Silaa “ijuma keenyatu cufameInumaa nuti ummata sihriin itti godhameedha” jedhu.');
INSERT INTO "apaguna" VALUES(15,16,'15:16','Nuti dhugumatti samii keessattis urjiilee gugurdaa taasifnee, namoota ilaalaniif ishee miidhagsinee jirra.');
INSERT INTO "apaguna" VALUES(15,17,'15:17','Sheyxaana darbatamaa hunda irraas ishee tiiksine.');
INSERT INTO "apaguna" VALUES(15,18,'15:18','Isa dhageettii hatee qaanqeen ifabahaa ta''e isa hordofe malee,');
INSERT INTO "apaguna" VALUES(15,19,'15:19','Dachiis Nuti ishee diriirsinee jirraGaarreenis ishee keessati goonee jirraWanta madaalamaa ta’e hunda irraas ishee keessatti magarsinee jirra.');
INSERT INTO "apaguna" VALUES(15,20,'15:20','Ishee keessatti waan ittiin jiraattaniifi waan isin isaan hin soorre (beyladoota) isiniif goone.');
INSERT INTO "apaguna" VALUES(15,21,'15:21','Kuusaaleen isaa Nu biratti ta’u male Wanti tokkos hin taane; Hanga beekaamaadhaan malee isa hin buufnu.');
INSERT INTO "apaguna" VALUES(15,22,'15:22','Baadhattuuwwan (duumessaa) taasifnee bubbeewwan erginee jirraSamii irraas bishaan buufnee isa isin obaafnee jirra(Nutu isiniif kuusa malee) isin isa (kuusuu irratti) danda’oo hin taane.');
INSERT INTO "apaguna" VALUES(15,23,'15:23','Nuti dhugumatti lubbuu Numatu jiraachisee ajjeesasNumatu dhaaltotas.');
INSERT INTO "apaguna" VALUES(15,24,'15:24','Dhugumatti Nuti isin irraa warren darban beekneerraWarren boodaas dhugumatti beeknee jirra');
INSERT INTO "apaguna" VALUES(15,25,'15:25','Dhugumatti Gooftaan kee Isumatu walitti isaan qaba; Inni ogeessa, beekaadha');
INSERT INTO "apaguna" VALUES(15,26,'15:26','Dhugumatti Nuti nama (Aadamiin) suphee dhoqqee jijjiiramaa ta’e irraa uumne');
INSERT INTO "apaguna" VALUES(15,27,'15:27','Jinnii (Ibliisiin) immoo duraan ibidda boba’aa irraa isa uumne.');
INSERT INTO "apaguna" VALUES(15,28,'15:28','Yeroo Gooftaan kee maleeykotaan jedhe [yaadadhu]:“Ani nama suphee (faara) dhoqqee jijjiiramarraa ta’e irraa uumaadha.');
INSERT INTO "apaguna" VALUES(15,29,'15:29','Yeroo Ani isa sirreessee ruuhii (uumaa) kiyya irraa ta’e isa keessatti afuufe, kan sujuuddan ta’aatii isaaf kufaa');
INSERT INTO "apaguna" VALUES(15,30,'15:30','Maleeykonni hundi walumaa galatti sujuuda bu’an.');
INSERT INTO "apaguna" VALUES(15,31,'15:31','Garuu Ibliis warra sujuuda bu’an irraa ta’uu ni dide.');
INSERT INTO "apaguna" VALUES(15,32,'15:32','(Rabbiin) “Yaa Ibliis! Maaliif warra sujuuda bu’an irraa ta’uu didde?” jedhee');
INSERT INTO "apaguna" VALUES(15,33,'15:33','[Inni] “Ani nama Ati suphee (faara) dhoqqee jijjiiramaa ta’e irraa uumteef sujuuda hin bu’u” jedhe');
INSERT INTO "apaguna" VALUES(15,34,'15:34','(Rabbiinis) “ishee irraa ba’i; Ati dhugumatti darbamaadha.');
INSERT INTO "apaguna" VALUES(15,35,'15:35','Dhugumatti hanga Guyyaa Murtiitti abaarsi sirra jira.”');
INSERT INTO "apaguna" VALUES(15,36,'15:36','“Gooftaa kiyya! Hanga guyyaa kaafamaatti na tursiisi” jedhe.');
INSERT INTO "apaguna" VALUES(15,37,'15:37','(Rabbiinis) “Dhugumatti, ati tursiifamtoota irraayi” jedhe.');
INSERT INTO "apaguna" VALUES(15,38,'15:38','“Hanga guyyaa yeroo beekaamaatti.”');
INSERT INTO "apaguna" VALUES(15,39,'15:39','(Ibliis) ni jedhe: “Gooftaa kiyya! na jallisuu keetiin kakadhee, dhugumatti dachii keessatti (karaa jallinaa) isaaniif miidhagseen walumaa galatti isaan jallisa');
INSERT INTO "apaguna" VALUES(15,40,'15:40','Gabroota kee irraa qulqulleeffamoota isaan ta’an malee.');
INSERT INTO "apaguna" VALUES(15,41,'15:41','(Rabbiinis) ni jedhe: “Kun karaa qajeelaa (isa eeguun) narratti ta’eedha');
INSERT INTO "apaguna" VALUES(15,42,'15:42','Jallattoota irraa warra si hordofan malee dhugumatti, gabroota kiyya irratti dandeettiin tokkoyyuu siif hin jiru.');
INSERT INTO "apaguna" VALUES(15,43,'15:43','Dhugumatti jahannam (isaafi hordoftoota isaatiif) walumaagalatti beellama isaaniiti.');
INSERT INTO "apaguna" VALUES(15,44,'15:44','Isheedhaafis balbala torbatu jiraTokko tokkoon balbalaatiif isaan irraa qooda ramadamaatu jira.”');
INSERT INTO "apaguna" VALUES(15,45,'15:45','Dhugumatti, warri (Rabbiin) sodaatan ashaakiltiiwwaniifi burqaawwan keessa jiru.');
INSERT INTO "apaguna" VALUES(15,46,'15:46','“Nagaan tasgabbaa’aa ishee seenaa” (jedhamaan).');
INSERT INTO "apaguna" VALUES(15,47,'15:47','Obboleeyyan siree irratti walitti garagaggalan ta’anii jibbaa irraa waan qoma isaanii keessa jiru baafne');
INSERT INTO "apaguna" VALUES(15,48,'15:48','Ishee keessatti dadhabbiin isaan hin tuquIsaanis ishee irraa hin baafamanu.');
INSERT INTO "apaguna" VALUES(15,49,'15:49','“Ani dhugumatti Anatu araaramaa, rahmata godhaadha” jechuu gabroota kiyyatti odeessi.');
INSERT INTO "apaguna" VALUES(15,50,'15:50','“Adabni kiyyas isatu adabbii laalessaadha” (jeechuus itti himi.)');
INSERT INTO "apaguna" VALUES(15,51,'15:51','Keessummaa Ibraahiim irraas isaanitti odeessi');
INSERT INTO "apaguna" VALUES(15,52,'15:52','Yeroo isa irratti seenanii, salaam jedhan (nagaa gaafatan) [Inni] “Dhugumatti, nuti isin irraa sodaatoodha” jedheen');
INSERT INTO "apaguna" VALUES(15,53,'15:53','“Hin sodaatinNuti ilma beekaa ta’een si gammachiifnaa” jedhan.');
INSERT INTO "apaguna" VALUES(15,54,'15:54','“Sila dullumni na tuquu wajjin na gammachiiftuu? Maaliin na gammachiiftu?” jedheen.');
INSERT INTO "apaguna" VALUES(15,55,'15:55','“(Nuti) dhugamaan si gammachiifne; Abdii kutattoota irraa hin ta’in” jedhaniin');
INSERT INTO "apaguna" VALUES(15,56,'15:56','“Jallattoota malee Rahmata Gooftaa isaa irraa eenyutu abdii kutata?” jedhe.');
INSERT INTO "apaguna" VALUES(15,57,'15:57','“Isin yaa warra ergamtootaa! Dhimmi (haalli) keessan maali?” jedheen.');
INSERT INTO "apaguna" VALUES(15,58,'15:58','“Nuti dhugumatti, gara ummata yakkamtootaatti ergamne” jedhan.');
INSERT INTO "apaguna" VALUES(15,59,'15:59','“Garuu maatii Luux dhugumatti, nuti hunduma ta’anii isaan baraarsoodha.”');
INSERT INTO "apaguna" VALUES(15,60,'15:60','“Niitii isaa malee; Nuti (adaba keessatti) haftoota irraa ta’uu ishee murteessine.”');
INSERT INTO "apaguna" VALUES(15,61,'15:61','Yeroma ergamoonni maatii Luuxitti dhufanu');
INSERT INTO "apaguna" VALUES(15,62,'15:62','“Dhugumatti, isin ummata wallaalamoodha” jedhe.');
INSERT INTO "apaguna" VALUES(15,63,'15:63','“Dhugumatti nuti waan isaan isa keessatti shakkaa turaniin sitti dhufne” jedhan.');
INSERT INTO "apaguna" VALUES(15,64,'15:64','“Dhugaadhaan sitti dhufneDhugumatti nuti dhugaa dubbattoota.');
INSERT INTO "apaguna" VALUES(15,65,'15:65','Cittuu halkan irraa taateen maatii keetiin deemi; Duuba isaanii hordofiIsin irraa namni tokkos of duuba hin mil’atin; gara iddoo ajajamtaniis deemaa.”');
INSERT INTO "apaguna" VALUES(15,66,'15:66','“Ajaja sanas xumurri warra sanii bariifattoota ta’anii kutamaadha” jechuun gara isaatti murteessine');
INSERT INTO "apaguna" VALUES(15,67,'15:67','Jiraattonni magaalaas kan wal gammachiisan ta’anii dhufan.');
INSERT INTO "apaguna" VALUES(15,68,'15:68','(Luuxis ni jedhe): “Dhugumatti, isaan kun keessumoota kiyyaa, nan saaxilinaa” jedhe');
INSERT INTO "apaguna" VALUES(15,69,'15:69','“Rabbiin sodaadhaa; anas hin xiqqeessinaa.”');
INSERT INTO "apaguna" VALUES(15,70,'15:70','(Namoonni magaalattiis) “Sila nuti aalama (keessummeessuu) irraa si hin dhorginee?” jedhan.');
INSERT INTO "apaguna" VALUES(15,71,'15:71','(Luuxis) ni jedhe: “kun shamarran kiyya, Yoo kan raawwattan taataniif (isaan fuudhaa).”');
INSERT INTO "apaguna" VALUES(15,72,'15:72','(Yaa Muhammad!) Jireenya keetti kakadhee! Dhugumatti, isaan machii isaanii kessa dhama’u.');
INSERT INTO "apaguna" VALUES(15,73,'15:73','Biiftuun isaanitti kan baatu ta’anii iyyattiin isaan qabatte');
INSERT INTO "apaguna" VALUES(15,74,'15:74','Gubbaa ishee jala ishee taasifnee, dhagaa xuubii (dhoqqee gubame) irraa ta’e isaanitti roobsine');
INSERT INTO "apaguna" VALUES(15,75,'15:75','Dhugumatti, kana keessa xiinxaltootaaf mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(15,76,'15:76','Dhugumatti, isheen (magaalattiin) daandii qajeelaa irra jirti.');
INSERT INTO "apaguna" VALUES(15,77,'15:77','Dhugumatti, kana keessa mu’uminootaaf mallattootu jirti.');
INSERT INTO "apaguna" VALUES(15,78,'15:78','Dubbiif, warri bosonaa dhugumatti miidhaa raawwattoota ta’an.');
INSERT INTO "apaguna" VALUES(15,79,'15:79','Haaloo isaan irraa ba’anneDhugumatti, isaan lamaanuu daandii ifa ta’e irra jiru.');
INSERT INTO "apaguna" VALUES(15,80,'15:80','Dhugumatti jiraattonni Hijris ergamtoota kijibsiisaniiru.');
INSERT INTO "apaguna" VALUES(15,81,'15:81','Mallattoolee keenyas isaaniif kenninee, isaan irraa garagaloo ta’an.');
INSERT INTO "apaguna" VALUES(15,82,'15:82','Of amanoo ta’anii gaarreen irraas manneen isaanii uraa turaniiru');
INSERT INTO "apaguna" VALUES(15,83,'15:83','Bariifatoo ta’anii iyyansi isaan qabatte');
INSERT INTO "apaguna" VALUES(15,84,'15:84','Wanti isaan hojjataa turan isaan irraa homaa hin deebifne.');
INSERT INTO "apaguna" VALUES(15,85,'15:85','Samii, dachiifi wantoota gidduu isaan lamaanii dhugaadhaan malee hin uumneDhugumatti Qiyaamaanis dhuftuudhaIrraa gara galuu gaarii ta’e gara gali.');
INSERT INTO "apaguna" VALUES(15,86,'15:86','Rabbiin kees isatu akkaan uumaa, beekaadha');
INSERT INTO "apaguna" VALUES(15,87,'15:87','Dhugumatti Nuti deddeebi’aa irraa torbaafi Qur’aana guddaa siif kenninee jirra');
INSERT INTO "apaguna" VALUES(15,88,'15:88','Gara waan nuti garee isaan (kaafiroota) irraa ta’an ittiin qanansiifnee ija kee lamaan hin diriirsin; Isaan irrattis hin gaddin; kooluu ija keetiis Mu’uminootaaf gadi qabi');
INSERT INTO "apaguna" VALUES(15,89,'15:89','“Dhugumatti ani, anatu sodaachisaa ifa galaadha” jedhi.');
INSERT INTO "apaguna" VALUES(15,90,'15:90','Akkuma qoqqooddattoota irratti buufne');
INSERT INTO "apaguna" VALUES(15,91,'15:91','Isaan Qur’aana garee taasisan (gariitti amananii garitti kafaran).');
INSERT INTO "apaguna" VALUES(15,92,'15:92','Gooftaa keetti kakannee! dhugumatti hunda isaanii ni gaafanna.');
INSERT INTO "apaguna" VALUES(15,93,'15:93','Waan isaan dalagaa turan irraa');
INSERT INTO "apaguna" VALUES(15,94,'15:94','Waan itti ajajamte ifatti labsi; Mushrikoota irraas garagali.');
INSERT INTO "apaguna" VALUES(15,95,'15:95','Dhugumatti, warra sitti qishnan siif geenyeerra.');
INSERT INTO "apaguna" VALUES(15,96,'15:96','(Isaan) warra Rabbii wajjiin gabbaramaa biraa godhan; fuulduratti beeku.');
INSERT INTO "apaguna" VALUES(15,97,'15:97','Dhugumatti waan isaan jedhanuun qomni kee rakkachuu ni beekna.');
INSERT INTO "apaguna" VALUES(15,98,'15:98','Gooftaa kee faarsuu wajjin Isa qulqulleessiWarra sujuudan irraas ta’i');
INSERT INTO "apaguna" VALUES(15,99,'15:99','Hanga dhugaan (duuti) sitti dhufutti Gooftaa kee gabbari.');
INSERT INTO "apaguna" VALUES(16,1,'16:1','Ajajni Rabbii (Guyyaan Qiyaamaa) dhufee, kanaafuu isa hin ariifachiisinaa(Rabbiin) waan isaan Isatti qindeessan irraa qulqullaa’ee ol ta’eera');
INSERT INTO "apaguna" VALUES(16,2,'16:2','Maleeykota wahyiidhaan eeyyama Isaatiin gabroota Isaa irraa nama fedhe irratti buusa“(kaafiroota adabaan) akeekkachiisaaDubbiin, Gooftaan dhugaan gabbaramu Ana malee hin jiru; Anuma sodaadhaa” jechuun (malaykaa buusa).');
INSERT INTO "apaguna" VALUES(16,3,'16:3','Samiifi dachii dhugaadhaan uumeeraInni waan isaan (Isatti) qindeessan irraa ol ta’eera.');
INSERT INTO "apaguna" VALUES(16,4,'16:4','Bishaan xiqqoo irraa nama uumeYoma san inni (Aakiraa sobsiisuun) mormaa ifa bahaadha.');
INSERT INTO "apaguna" VALUES(16,5,'16:5','Beyladas isiniif ishee uumeIsaan keessatti isiniif ho’ifannaafi bu’aalee birootu jiraIshee irraas ni nyaattu.');
INSERT INTO "apaguna" VALUES(16,6,'16:6','Yeroo (gara mooraa) galchitaniifi yeroo bobbaaftan (gadi yaaftan) ishee keessa bareedumatu isiniif jira.');
INSERT INTO "apaguna" VALUES(16,7,'16:7','Ba’aa keessan ulfaataas gara biyya isin ifaajee (rakkina) lubbuutiin malee isa gahoo hin taaneetti baadhattiDhugumatti, Gooftaan keessan mararfataa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(16,8,'16:8','Farda, gaangewwaniifi harrootas akka yaabbattaniifi miidhaginaaf jecha (isiniif uume)Wantoota isin hin beeknes ni uuma');
INSERT INTO "apaguna" VALUES(16,9,'16:9','(Tola isaatiin) karaa qajeelaa ibsuun Rabbuma irra jiraIshee irraas jallaatu jira(Rabbiin) odoo fedhee hunda keessan isin qajeelcha ture.');
INSERT INTO "apaguna" VALUES(16,10,'16:10','Inni Isa samii irraa bishaan isiniif buuseIsiniif isa irraa dhugaatiitu jiraIsa irraas muka isin ishee keessatti (beyladoota) tikfattantu (marga.)');
INSERT INTO "apaguna" VALUES(16,11,'16:11','Isaan sanyii, Zeytuuna, muka Timiraa, Inabaafi fuduraalee hunda irraa isiniif magarsaDhugumatti, kana keessa namoota xiinxalaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(16,12,'16:12','Halkan, guyyaa, aduufi ji’as isiniif laaffiseUrjiiwwanis ajaja Isaatiin laaffifamoodhaDhugumatti, kana keessa namoota hubannoo qabaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(16,13,'16:13','Bifti isaanii adda adda ta’ee wantoota dachii keessatti isiniif uumes (isiniif laaffise)Dhugumatti, kana keessa namoota gorfamaniif mallattootu jira');
INSERT INTO "apaguna" VALUES(16,14,'16:14','Inni Isa akka isa irraa foon haarawa nyaattaniifi faaya ishee uffattanis isa irraa baafattaniif jecha galaana isiniif laaffiseedhaDooniwwanis isa (galaana) keessa baqaqsituu taatee (kan deemtu) argitaAkka tola isaa irraa barbaaddaniifi akka galateeffattaniif jecha (kana godhe)');
INSERT INTO "apaguna" VALUES(16,15,'16:15','Gaarreenis akka isiniin hin sochooneef dachii keessatti godheLaggeeniifi karaalees akka qajeeltaniif (godhe).');
INSERT INTO "apaguna" VALUES(16,16,'16:16','Mallattoolees (isiniif godhe)Urjiidhaanis isaan (karaa) qajeelu.');
INSERT INTO "apaguna" VALUES(16,17,'16:17','Sila Inni (waan hunda) uumu akka isa (waan tokkos) hin uumneetii? Sila isin hin gorfamtanuu?');
INSERT INTO "apaguna" VALUES(16,18,'16:18','Tola Rabbii yoo lakkooftan waliin gahuu hin dandeessanuDhugumatti, Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(16,19,'16:19','Rabbiin waan isin gadi qabattaniifi waan ol qabattanis ni beeka.');
INSERT INTO "apaguna" VALUES(16,20,'16:20','Warri isaan Rabbii gaditti gabbaranu homaa hin uumanuIsaanumtuu ni uumamu.');
INSERT INTO "apaguna" VALUES(16,21,'16:21','(Isaan) du’aawwan fayyaa hin taaneedhaYoom akka kaafamaniis hin beekanu.');
INSERT INTO "apaguna" VALUES(16,22,'16:22','Dhugumatti gabbaramaan keessan gabbaramaa tokkichaIsaan Aakhiraatti hin amanne, onneen isaanii (tokkichummaa Rabbii) mormituudha; isaanis boontota');
INSERT INTO "apaguna" VALUES(16,23,'16:23','Rabbiin waan isaan dhoksaniifi waan isaan mul’isanis beekaa ta’uutti shakkiin hin jiruInni boontota hin jaallatu.');
INSERT INTO "apaguna" VALUES(16,24,'16:24','Yeroo “Gooftaan keessan (Muhammad irratti) maal buuse” isaaniin jedhame, “(inni) afoola (ogbarruu) warra duriiti” jedhu.');
INSERT INTO "apaguna" VALUES(16,25,'16:25','Akka Guyyaa Qiyaamaa guutuu taatee badii isaaniitiifi badii warra beekumsa malee jallisanii irraa baadhatanuuf (kana jedhu)Dhagahaa! Wanti isaan baadhatan waa fokkate!');
INSERT INTO "apaguna" VALUES(16,26,'16:26','Dhugumatti, warri isaan dura turan tooftaa baasaniiru(adabbiin) Rabbii bu’uura irraan manneen isaaniitti dhufee (ishee diige)Ijoon (gubbaan manaa) gubbaa isaaniitiin isaan irratti kufeAdabnis karaa isaan hin beekneen isaanitti dhufe');
INSERT INTO "apaguna" VALUES(16,27,'16:27','Sana booda Guyyaa Qiyaamaa isaan salphisa“Hiriyoonni kiyya kan (dhimma) isaanii keessatti (mu’uminoota) faallessaa turtan eessa jiru?” jedhaIsaan beekumsa kennaman “dhugumatti har’a salphinaafi hamtuun kaafiroota irratti ta’eera” jedhu.');
INSERT INTO "apaguna" VALUES(16,28,'16:28','(Isaan) warra lubbuu isaanii miidhoo ta’anii maleeykonni isaan ajjeesaniidhaHamtuu tokkollee kan dalagnu hin turre jechuun ajajamoo ta’uu isaanii (sobaan) ibsu“Lakkisaa dhugumatti, Rabbiin waan isin hojjachaa turtan beekaadha');
INSERT INTO "apaguna" VALUES(16,29,'16:29','Ishee keessatti kan haftan ta’aatii balbala Jahannam seenaa.” (isaaniin jedhama)Dhugumatti, teessoon boontotaa waa fokkate.');
INSERT INTO "apaguna" VALUES(16,30,'16:30','Isaan (Rabbiin) sodaataniin “Gooftaan keessan (Muhammad irratti) maal buuse?” jedhame“Gaarii (buuse)” jedhanWarra toltuu hojjataniif addunyaa kana keessatti (jiruu) gaariitu jiraManni Aakhiraa immoo irra caalaadhaGandi warra (Rabbiin) sodaatanii waa tole!');
INSERT INTO "apaguna" VALUES(16,31,'16:31','(Isheen) Jannata qubsumaa jala ishee laggeen kan yaatu taatee, Ishee keessa wanti isaan fedhan isaaniif ta’uun kan ishee seenaniidhaAkka kanatti Rabbiin sodaattota (Isaa) mindeessa');
INSERT INTO "apaguna" VALUES(16,32,'16:32','(Isaan) warra gaggaarii ta’anii maleykonni “nageenyi isin irratti haa jiraatu; waan hojjachaa turtaniinis jannata seenaa” jedhaa isaan ajjeefteedha');
INSERT INTO "apaguna" VALUES(16,33,'16:33','Sila (kaafiroonni) maleeykonni isaanitti dhufuu yookiin ajajni Gooftaa keetii isaanitti dhufuu malee (waan biraa) ni eeguu? Warri isaaniin duraas akkuma kana dalaganiiruRabbiin isaan hin miineGaruu isaan lubbuu isaanii kan miidhan turan.');
INSERT INTO "apaguna" VALUES(16,34,'16:34','(Adabni) hamtuuwwan isaan hojjatanii isaan argatteWanti isaan itti qishinaa turanis isaanitti marse.');
INSERT INTO "apaguna" VALUES(16,35,'16:35','Isaan (Rabbitti waan biraa) qindeessan ni jedhan: “odoo Rabbiin fedhee, nutis ta’ee abbootiin keenya Isaa gaditti waan biraa hin gabbarru turre(Ajaja) Isaatiin alas homaa haraama hin goonu turre.” Warri isaaniin duraas akkuma kana dalaganSila ergamtoota irra (ergaa) ifatti qaqqabsiisuu malee wanti biraa jiraa?');
INSERT INTO "apaguna" VALUES(16,36,'16:36','Dhugumatti, Nuti “Rabbiin gabbaraa; taabota (gabbaruu) irraas fagaadhaa” (jechuun) ummata hunda keessatti ergamaa erginee jirraIsaan irraa nama Rabbiin isa qajeelchetu jiraAmmas isaan irraa nama jallinni isa irratti mirkanaa’etu jiraDachii keessa deemaatii booddeen sobsiiftotaa akkam akka ta’e ilaalaa');
INSERT INTO "apaguna" VALUES(16,37,'16:37','Yoo qajeeluma isaanii irratti bololtes (qajeelchuu hin dandeessu)Dhugumatti Rabbiin namoota jallisu hin qajeelchu; tumsitoonnis isaaniif hin jiran.');
INSERT INTO "apaguna" VALUES(16,38,'16:38','Kakaa isaanii kan daangaa gahe “Rabbiin nama du’e hin kaasu” jechuun Rabbiin kakatanLakkisaa (isin kaasaa); Waadaa isa irratti dhugaa ta’e (Rabbiin seeneera)Garuu irra hedduun namaa hin beekanu.');
INSERT INTO "apaguna" VALUES(16,39,'16:39','(Sababni du’a irraa isaan kaasuuf) akka waan isaan isa keessatti wal dhaban isaaniif ibsuufi akka warri kafaran dhugumatti kijiboota ta’uu isaanii beekaniifi.');
INSERT INTO "apaguna" VALUES(16,40,'16:40','Wanta kamillee (ta’uu) isaa yoo feene, jechi keenya isaan ‘ta’i’ (jechuu) qofaAchumaan ta’a');
INSERT INTO "apaguna" VALUES(16,41,'16:41','Isaan erga miidhamanii booda (karaa) Rabbii keessatti godaanan, dhugumatti addunyaa keessatti (qubannaa) gaarii ta’e isaan qubsiifnaOdoo kan beekan ta’anii mindaa Aakhiraatu irra guddaadha.');
INSERT INTO "apaguna" VALUES(16,42,'16:42','(Isaan) warra obsanii, Gooftaa isaanii (qofa) irratti hirkataniidha.');
INSERT INTO "apaguna" VALUES(16,43,'16:43','Nuti si dura dhiirota wahyii gara isaaniitti goone malee (waan biraa) hin ergineYoo kan hin beekne taatan, abbootii beekumsaa gaafadhaa.');
INSERT INTO "apaguna" VALUES(16,44,'16:44','Mallattoolee ifa ta’aniifi kitaabotaan (isaan ergine)Akka namootaaf waan gara isaaniitti buufame ibsituufi isaanis xiinxalaniif Qur’aana gara keetti buufne.');
INSERT INTO "apaguna" VALUES(16,45,'16:45','Sila isaan tooftaa badaa baasan ‘Rabbiin dachii isaaniin gadi kutuu yookiin iddoo isaan hin beekne irraa adabbiin isaanitti dhufuu’ hin sodaatanuu?');
INSERT INTO "apaguna" VALUES(16,46,'16:46','Yookiin deddeebi’uu isaanii keessatti (Rabbiin) isaan qabuu (hin sodaatanuu)? Isaanis dadhabsiisoo hin taane.');
INSERT INTO "apaguna" VALUES(16,47,'16:47','Yookiin sodaa (isaanii) irratti (Rabbiin) isaan qabuu (hin sodaatanuu)? Dhugumatti Gooftaan keessan mararfataa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(16,48,'16:48','Sila wantoota irraa gara waan Rabbiin uumee, kan gaaddisni isaanii xiqqaatoo ta’anii Rabbiif sujuuduudhaan gara mirgaafi bitaatti daban hin ilaalanii?');
INSERT INTO "apaguna" VALUES(16,49,'16:49','Lubbu qabeeyyii irraa Wanti samii keessa jiraniifi Wanti dachii keessa jiran, maleeykonnis Rabbiif sujuuda godhuIsaanis hin boonanu.');
INSERT INTO "apaguna" VALUES(16,50,'16:50','Gubbaa isaanii irratti Gooftaa isaanii sodaatu; waan itti ajajamaniis ni dalagu.');
INSERT INTO "apaguna" VALUES(16,51,'16:51','Rabbiinis “gabbaramoota lama hin godhatinaaDhugumatti, Inni Gooftaa tokko qofaAnuma qofa sodaadhaa” jedhe.');
INSERT INTO "apaguna" VALUES(16,52,'16:52','Wantoonni samiifi dachii keessa jiran hundi kan IsaatiAmantiinis walitti fufaa ta’ee kan IsaatiSila waan Rabbiin ala jiru sodaattuu?');
INSERT INTO "apaguna" VALUES(16,53,'16:53','Ni’imaa irraa wanti isin bira jirus Rabbuma irraayyiErgasii yeroo miidhaan isin tuqe, garuma Isaatti waywaattu.');
INSERT INTO "apaguna" VALUES(16,54,'16:54','Ergasii yeroo Inni miidhaa san isin irraa kaasu, yeroma san gareen isin irraa ta’e Gooftaa isaaniitti (wanta biraa) qindeessu.');
INSERT INTO "apaguna" VALUES(16,55,'16:55','Waan Nuti isaaniif kenninetti kafaruuf (Isatti qindeessu)Qanani’aa; fuula dura (waan isin mudatu ) ni beektuu');
INSERT INTO "apaguna" VALUES(16,56,'16:56','Waan Nuti isaaniif kennine irraa waan hin beekneef qooda taasisuRabbitti kakadhee! waan (sobaan) odeessaa turtan irraa ni gaafatamtu');
INSERT INTO "apaguna" VALUES(16,57,'16:57','(Maleykaa irraa) Rabbiif shamarran taasisuQulqullina isaa (qulqullaa’e)Isaaniif immoo waan fedhan (dhiirota) taasisu');
INSERT INTO "apaguna" VALUES(16,58,'16:58','Tokkoon isaanii yeroo dubaraan gammachiifame, innis gaddaan kan guutame ta’ee fuulli isaa gurraacha ta’a.');
INSERT INTO "apaguna" VALUES(16,59,'16:59','Fokkina waan ittiin gammachiifamee irraas “Sila salphina irratti ishee qabata moo? biyyee keessatti ishee awwaala?” (jechuun kan dhama’e ta’ee) namoota irraa dhokataDhagahaa! Wanti isaan murteessan waa fokkate!');
INSERT INTO "apaguna" VALUES(16,60,'16:60','Isaan Aakhiraatti hin amanneef amala gadheetu jiraRabbiif immoo sifaata ol ta’aatu jiraInni injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(16,61,'16:61','Odoo Rabbiin namoota miidhaa isaaniitiin qabee, silaa dugda ishee (dachii) irratti lubbuu qabeeyyii tokkoyyuu hin dhiisu tureGaruu hanga beellama murtaa’eetti isaan tursiisaYeroo beellamni isaanii ga’e, sa’aatii tokkollee hin tursiifamanu; duras hin bu’anu.');
INSERT INTO "apaguna" VALUES(16,62,'16:62','Waan ofii jibbanis Rabbiif taasisu“Toltuun (Jannanni) numaafi” (jechuun) Arrabni isaaniis kijiba dubbattiIbiddi isaaniif ta’ee isaan (gara ishee) jalqaba oofamuutti shakkiin hin jiru.');
INSERT INTO "apaguna" VALUES(16,63,'16:63','Rabbitti kakadhee! Dhugumatti, Nuti gara ummatoota si duraa (ergamtoota) erginee jirraSheyxaannis dalagaa isaanii isaaniif miidhagsee jiraInni (sheyxaanni) har’a jaalallee (gargaaraa) isaaniitiIsaaniifis adabbii laalessaa ta’etu jira.');
INSERT INTO "apaguna" VALUES(16,64,'16:64','Mu’uminootaaf qajeelumaafi rahmata ta’ee akka waan isaan isa keessatti wal dhaban isaaniif addeessituuf malee (waa biraatiif) Nuti kitaaba sirratti hin buufne.');
INSERT INTO "apaguna" VALUES(16,65,'16:65','Rabbiin samii irraa bishaan buusee dachiis erga isheen duute booda ittiin jiraachiseDhugumatti kana keessa ummata dhaga’aniif mallattootu jira.');
INSERT INTO "apaguna" VALUES(16,66,'16:66','Dhugumatti beellada keessas xiinxalatu isiniif jiraWaan garaa isaanii keessa jiru irraa cumaafi dhiiga gidduu aannan qulqulluu dhugdootaaf gadi liqimfamaa ta’e isin obaafna.');
INSERT INTO "apaguna" VALUES(16,67,'16:67','Fuduraalee mukkeen Tamiriitiifi Inabaa irraas dhugaatii macheessuufi soorata gaggaarii godhattuDhugumatti kana keessa namoota hubannoo qabaniif mallattootu jira.');
INSERT INTO "apaguna" VALUES(16,68,'16:68','Gooftaan keetis “gaarreen, mukaafi waan isaan ijaaran irraas mana godha dhu” (jechuun) gara kanniisaatti beeksisa godhe.');
INSERT INTO "apaguna" VALUES(16,69,'16:69','“Ergasii fuduraa hunda irraa nyaadhuu laafaa haala ta’een karaalee Gooftaa keetii seeni.” Garaa ishee irraa dhugaatii bifti isaa gara gara ta’e, kan isa keessa namaaf dawaan jirutu bahaDhugumatti kana keessa namoota xiinxalaniif mallatootu jira.');
INSERT INTO "apaguna" VALUES(16,70,'16:70','Rabbiin isin uumeErgasii isin ajjeesaBeekumsa booda akka homaa hin beekneef, Isin irraas nama gara umrii gadi aanaa (dullumaa) deebifamutu jiraDhugumatti, Rabbiin beekaa, danda’aadha.');
INSERT INTO "apaguna" VALUES(16,71,'16:71','Rabbiin soorata keessatti garii keessan garii irra caalchiseeraIsaan caalchifaman qabeenya isaanii gara waan (gabroota) harki isaanii dhuunfattee deebisanii isaanis isa keessati kan wal qixxaatanu hin taaneSila tola Rabbii mormuu?');
INSERT INTO "apaguna" VALUES(16,72,'16:72','Rabbiin lubbuu keessan irraas haadhotii manaa isiniif godheHaadhotii manaa keessan irraas ilmaaniifi ilmaan ilmaaniis isiniif godheWantoota gaggaarii irraas isin sooreSila isaan sobatti amananii tola Rabbiitti kafaruu?');
INSERT INTO "apaguna" VALUES(16,73,'16:73','Rabbiin ala wantoota samiifi dachii irraas soorata isaaniif hin kennineefi (waan tokko) kan hin dandeenye gabbaru.');
INSERT INTO "apaguna" VALUES(16,74,'16:74','Rabbiif fakkeenya hin godhinaaDhugumatti, Rabbiin ni beeka; isin hin beektanu');
INSERT INTO "apaguna" VALUES(16,75,'16:75','Rabbiin gabricha horatamaa, kan homaa irratti dandeettii hin qabneefi nama Nuti of biraa soorata gaarii isaaf kenninee, inni isa irraa dhoksaafi ifattis kan arjoomu (isaafi wantoota isaan alaatiif) fakkeenya taasiseeraSila isaan wal qixa ta’uu? Faaruun hundi kan RabbiitiDhugumatti irra hedduun isaanii hin beekanu.');
INSERT INTO "apaguna" VALUES(16,76,'16:76','Rabbiin namoota lama (mu’uminootaafi kaafirootaaf) fakkeenya taasiseera: tokkoon isaan lamaanii arraba hidhamaadha; homaa irrattis dandeettii hin qabu; inni maatii isaa irratti ba’aadha; bakkuma inni isa erge hunda irraayyuu waan gaariin hin dhufuSila inniifi namni inni karaa qajeelaa irra jiraatee haqatti ajaju, wal qixa ta’aa?');
INSERT INTO "apaguna" VALUES(16,77,'16:77','Iccitiin fagoo samiifi dachii (hunduu) kan RabbiitiWaa’een Qiyaamaas akka libsuu ijaa malee hin taane; yookiin inni isa irra dhihoodhaDhugumatti, Rabbiin waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(16,78,'16:78','Rabbiin kan homaa hin beekne taatanii garaa haadha keessanii irraa isin baaseAkka galateeffattanuuf dhageettii, argaafi onnees isiniif godhe.');
INSERT INTO "apaguna" VALUES(16,79,'16:79','Sila gara allaattii hawaa samii keessa (kufuu irraa) Rabbiin malee wanti biraa kan ishee hin qabne taatee (bararuuf) laaffifamtuu taatee hin ilaallee? Dhugumatti, kana keessa namoota amananiif mallattooleetu jira');
INSERT INTO "apaguna" VALUES(16,80,'16:80','Rabbiin manneen keessan irraa iddoo teessumaa isiniif godheGogaa beyladaa irraas manneen guyyaa imala deemtaniifi guyyaa mana jireenya keessaniis salphifattan isiniif taasiseeraAkkasumas suufa hoolaa, rifeensa gaalaafi rifeensa re’ee irraas hanga yerootiif meeshaafi faa’idaalee (isiniif godheera).');
INSERT INTO "apaguna" VALUES(16,81,'16:81','Rabbiin wantoota uume irraa gaaddisawwan isiniif godheGaarreen irraas iddoo dhokannaa (isinii godhe)Uffatawwan ho’aa (fi qorra) isin irraa dhorgitus isiniif godhe; uffatawwan balaa waraanaa irraa isin tiksitusAkka isin (isaaf) ajajamtaniif akka kanatti (Rabbiin) tola Isaa isin irratti guuta');
INSERT INTO "apaguna" VALUES(16,82,'16:82','Yoo (haqa irraa) garagalaniif, dhugumatti wanti sirra jiru (ergaa) qaqqabsiisuu ifa ta’e qofa.');
INSERT INTO "apaguna" VALUES(16,83,'16:83','Tola Rabbii ni beekuErgasii ishee mormuIra hedduun isaaniis kaafiroota');
INSERT INTO "apaguna" VALUES(16,84,'16:84','Guyyaa ummata hunda irraa ragaa kaafnus [yaadadhaa]Sana booda isaan kafaraniif (akka haasa’an) hayyamni hin kennamu(Komii dhiifama qabus) hin komatamanu.');
INSERT INTO "apaguna" VALUES(16,85,'16:85','Isaan miidhaa dalagan yeroo adabbii argan, isaan irraa hin salphifamu; Yeroonis isaaniif hin kennamu.');
INSERT INTO "apaguna" VALUES(16,86,'16:86','Isaan (Rabbitti waan biraa) qindeessan yeroo qindoolee isaanii argan “Gooftaa keenya! Isaan kun qidoolee keenya warra nuti siin ala kadhachaa turreedha” jedhu(Gabbaramtoonnis) “dhugumatti isin kijiboodha” (jedhuun) jecha gara isaanii darbatu.');
INSERT INTO "apaguna" VALUES(16,87,'16:87','Isaan guyyaa san ajajamoo ta’uu gara Rabbiitti dhiyeessuWanti isaan sobaan uumaa turanis isaan irraa bade.');
INSERT INTO "apaguna" VALUES(16,88,'16:88','Isaan kafaranii karaa Rabbii irraas (namoota) deebisan, waan isaan balleessaa turaniif jecha, adabbii irratti adabbii isaaniif daballa.');
INSERT INTO "apaguna" VALUES(16,89,'16:89','Guyyaa Nuti ummata hunda keessatti lubbuma isaanii irraa ragaa isaan irratti baafnuufi sis (yaa Muhammad SAW!) isaan kana irratti ragaa goonee si fidnu [yaadadhu]Waan hunda addeessuuf jecha, Muslimootaaf qajeeluma, rahmataafi gammachuu goonee Kitaaba (Qur’aana) sirratti buufne');
INSERT INTO "apaguna" VALUES(16,90,'16:90','Dhugumatti, Rabbiin haqatti, toltuu hojjachuufi firootaaf (gargaarsa) kennuutti ajajaWantoota fokkatoo, waan jibbamaadhaafi miidhaa irraas ni dhorgaAkka gorfamtaniif jechas isin gorsa');
INSERT INTO "apaguna" VALUES(16,91,'16:91','Yeroo waadaa seentan waadaa Rabbii guutaaKakuus dhugumatti Rabbiin kan ragaa of irratti gootan taatanii eega ishee jabeessitanii booda hin diiginaaDhugumatti Rabbiin waan isin hojjattan ni beekaa.');
INSERT INTO "apaguna" VALUES(16,92,'16:92','Ummanni tokko ummata biraa irra sababa baay’achuutiin kakuu keessan gidduu keessanitti (wal ganuuf) seensa godhattanii akka ishee jibrii ishee erga footee jabeessitee booda diiginsa diigdee hin ta’inaaRabbiin kanaan isin qormaatuuf (kana godhe)Guyyaa Qiyaamaas waan isa keessatti wal dhabaa turtan dhugumatti isiniif addeessa');
INSERT INTO "apaguna" VALUES(16,93,'16:93','Rabbiin odoo fedhee ummata tokko isin taasisa tureGaruu nama fedhe ni jallisa; nama fedhes ni qajeelchaWaan dalagaa turtan irraas dhugumatti ni gaafatamtu.');
INSERT INTO "apaguna" VALUES(16,94,'16:94','Faanti erga lafa qabattee booda akka hin mucucaanneefi sababaa karaa Rabbii irraa (namoota) deebiftaniif hamtuu akka hin dhandhamneef kakuu keessan gidduu keessanitti (wal ganuuf) seensa hin godhatinaa(Oggas) isiniif adabbii guddaatu jira');
INSERT INTO "apaguna" VALUES(16,95,'16:95','Waadaa Rabbiis maallaqa xiqqaatti hin gurguratinaaYoo kan beektan taatan dhugumatti, wanti Rabbiin bira jiru, isatu isiniif caalaadha.');
INSERT INTO "apaguna" VALUES(16,96,'16:96','Wanti isin biraa ni dhumaWanti Rabbiin biraa immoo hafaadhaIsaan obsaniif mindaa isaanii irra gaarii waan isaan dalagaa turaniitiin isaan mindeessina.');
INSERT INTO "apaguna" VALUES(16,97,'16:97','Dhiiras ta’ee dubartii irraa nama mu’umina ta’ee dalagaa gaarii hojjate, dhugumatti jireenya gaarii isa jiraachifna; mindaa isaaniis irra gaarii waan isaan dalagaa turaniitiin isaan mindeessina.');
INSERT INTO "apaguna" VALUES(16,98,'16:98','Yeroo Qur’aana qaraatu sheyxaana darbatamaa irraa Rabbitti maganfadhu.');
INSERT INTO "apaguna" VALUES(16,99,'16:99','Dhugumatti, inni warra amananii Gooftaa isaanii irratti hirkatan irratti aangoon isaaf hin jiru');
INSERT INTO "apaguna" VALUES(16,100,'16:100','Aangoon isaa warruma jaalalloo isa godhataniifi warra Isatti qindeessoo ta’an (qofa) irratti');
INSERT INTO "apaguna" VALUES(16,101,'16:101','Yeroo keeyyata tokko bakka keeyyata biraa jijjiirre, Rabbiin waan buusu akkan beekaadha; “Ati (yaa Muhammad!) soba of biraa uumaa malee kan biraa miti” jedhuDhugumatti irra baay’een isaanii hin beekanu');
INSERT INTO "apaguna" VALUES(16,102,'16:102','“Akka warra amanan (haqa irra) gadi dhaabuuf, Muslimootaaf qajeelumaafi gammachuu ta’ee, ruuhiin qulqulluun (Jibriil) Gooftaa kee irraa isa buuse” jedhi.');
INSERT INTO "apaguna" VALUES(16,103,'16:103','Nuti dhugumatti “kan (Qur’aana) isa barsiisu nama malee waan biraa miti” jechuu isaanii ni beeknaQooqni inni isaan gara isaatti jallisanii ‘Ajamii’dha(Qur’aanni) kun immoo qooqa Arabiffaa ifa bahaadha.');
INSERT INTO "apaguna" VALUES(16,104,'16:104','Dhugumatti isaan keeyyattoota Rabbiitti hin amanne, Rabbiin isaan hin qajeelchu; isaaniifis adabbii laalessaatu jira');
INSERT INTO "apaguna" VALUES(16,105,'16:105','Dhugumatti kan kijiba uumu isaan keeyyattoota Rabbiitti hin amanne qofaWarri sun isaanumatu kijibdoota');
INSERT INTO "apaguna" VALUES(16,106,'16:106','Nama odoo qalbiin isaa Iimaana irratti ragga’ee jiruu (kufriitti) dirqisiifame malee, namni eega amanuu isaatii kafare (Adaba laalessaatu isaaf jira)Garuu namni qoma isaa kufriif bal’ise dallansuu Rabbi irraa ta’etu isaan irra jira; Isaaniifis adabbii guddaatu jira');
INSERT INTO "apaguna" VALUES(16,107,'16:107','Kun waan isaan jireenya addunyaa Aakhiraa caalaa jaalataniifi Rabbiinis ummata kaafirootaa waan hin jaallanneefi');
INSERT INTO "apaguna" VALUES(16,108,'16:108','Isaan warra Rabbiin onnee isaanii, dhageettii isaaniifi agartuu isaanii irratti cufeedhaIsaan sun isaanumatu dagattoota');
INSERT INTO "apaguna" VALUES(16,109,'16:109','Dhugumatti isaan Aakhiraatti isaanumatu hoonga’oo ta’uutti shakkiin hin jiru');
INSERT INTO "apaguna" VALUES(16,110,'16:110','Ergasii Gooftaan kee isaan erga rakkifamanii booda godaananii, ergasii qabsaa’anii, obsaniif (araaramaa, rahmata godhaa dha); dhugumatti Gooftaan kee booda isheetii (rakkifamuutii) araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(16,111,'16:111','Guyyaa lubbuun hundi kan ofirraa mormitu taatee dhuftuufi Isaan kan hin miidhamne ta’anii lubbuun hundi waan dalagdeen mindeeffamtu [yaadadhu]');
INSERT INTO "apaguna" VALUES(16,112,'16:112','Rabbiin magaalattii nagaa tasgabbooftuu turte, kan sooranni ishee iddoo hunda irraa baay’inaan isheetti dhufu, kan tola Rabbiitti kafartee Rabbiin waan isaan hojjachaa turaniin uffata beelaafi sodaa ishee dhandhamsiise (nama tola isaatti kafareef) fakkeenya taasise');
INSERT INTO "apaguna" VALUES(16,113,'16:113','Dhugumatti ergamaan isaan irraa ta’e isaanitti dhufee, isas sobsiisani; odoo isaan miidhaa hojjatoota ta’anii jiranuu adabbiin isaan qabate');
INSERT INTO "apaguna" VALUES(16,114,'16:114','Hayyamamaa, gaarii haala ta’een waan Rabbiin isiniif kenne irraa nyaadhaaYoo kan Isa (qofa) gabbartan taataniif tola Rabbii galateeffadhaa.');
INSERT INTO "apaguna" VALUES(16,115,'16:115','Wanti (Rabbiin) isin irratti haraama (dhorgamaa) godhe baktii, dhiiga, foon booyyeetiifi waan wantoota (gabbaramtoota) Rabbiin alaatiif sagaleen irratti olfuudhameedhaNamni odoo halaalli mijaa’uu haraamatti darbuufi nyaata isaa keessatti daangaa darbuun maleetti rakkifame (badiin isarra hin jiru)Dhugumatti Rabbiin araaramaa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(16,116,'16:116','Rabbirratti kijiba uumuuf jettanii waan arrabni keessan kijiba dubbatuun “kun hayyamamaadha; kun immoo dhorgamaadha” hin jedhinaaDhugumatti isaan Rabbirratti soba odeessan hin milkaa’anu');
INSERT INTO "apaguna" VALUES(16,117,'16:117','(Isaaniif) qananii xiqqootu jira(Aakhiraatti immoo) isaaniif adabbii laalessaatu jira.');
INSERT INTO "apaguna" VALUES(16,118,'16:118','Isaan yahuudota ta’an irrattis waan kana dura siif himne dhorgaa taasifnee jirraNuti waa isaan hin miinee, garuu isaan lubbuu isaanii kan miidhan turan');
INSERT INTO "apaguna" VALUES(16,119,'16:119','San booda Gooftaan kee isaan wallaalumaan hamtuu dalaganii sana booda tawbatanii (hojii isaanii) tolchan, dhugumatti Gooftaan kee ishee booda araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(16,120,'16:120','Dhugumatti Ibraahiim ogganaa Rabbiif ajajamaa, qajeelaa tureMushrikoota irraa hin taane');
INSERT INTO "apaguna" VALUES(16,121,'16:121','Tolawwan Isaatiif galatoomfataa ture(Rabbiin) isa filatee gara karaa sirriis isa qajeelche.');
INSERT INTO "apaguna" VALUES(16,122,'16:122','Addunyaa keessattis toltuu isaaf kenninee jirraDhugumatti inni Aakhiraa keessattis gaggaarii irraayyi.');
INSERT INTO "apaguna" VALUES(16,123,'16:123','Ergasii “karaa Ibraahiim qajeelaa ta’e hordofaa” (jechuun) beeksisa gara keetti gooneInni (Ibraahiim) mushrikoota irraa hin taane.');
INSERT INTO "apaguna" VALUES(16,124,'16:124','Sanbatni (dhorgaa) kan godhame warruma isa keessatti wal dhaban qofa irrattiDhugumatti Gooftaan keetis guyyaa qiyaamaa waan isaan isa keessatti wal dhabaa turan gidduu isaanitti murtii kenna.');
INSERT INTO "apaguna" VALUES(16,125,'16:125','Gara karaa Gooftaa keetii ogummaafi gorsa bareedaan waamiIshee (falmii) irra gaarii taateen isaan falmiDhugumatti Gooftaan kee nama karaa Isaa irraa jallate ni beekaaInni qajeeltotas beekaadha');
INSERT INTO "apaguna" VALUES(16,126,'16:126','Yoo haaloo bahattan fakkaatuma waan miidhamtaniitiin bahadhaaYoo obsitan immoo obsitootaaf isatu caalaadha.');
INSERT INTO "apaguna" VALUES(16,127,'16:127','Obsi; obsuun kee (si dandeessisuu) Rabbiitiin malee hin taaneIsaan irrattis hin gaddinTooftaa isaan baasan irraa dhiphina keessatti hin ta’in.');
INSERT INTO "apaguna" VALUES(16,128,'16:128','Dhugumatti, Rabbiin warra Isa sodaataniifi warra toltuu hojjattan wajjin jira.');
INSERT INTO "apaguna" VALUES(17,1,'17:1','Inni gabricha Isaa halkan masjida kabajamaa (Makkaa) irraa gara masjida fagoo, kan moggaa (naannoo) isaa barakaa goonee, mallattoowwan keenya akka itti argisiifnuuf deemsise qulqullaa’eDhugumatti, Inni Isumatu dhaga’aa, argaadha');
INSERT INTO "apaguna" VALUES(17,2,'17:2','Muusaafis kitaaba kenninee Ilmaan Israa’iilif “anaa gaditti hirkoo hin godhatinaa” (jechuun) qajeelfama isa goone.');
INSERT INTO "apaguna" VALUES(17,3,'17:3',')Yaa) sanyii namoota Nuuh wajjin (doonii) yaabbachiifne! Dhugumatti, inni gabricha galatoomfataa ture.');
INSERT INTO "apaguna" VALUES(17,4,'17:4','Gara Ilmaan Israa’iil kitaaba keessatti “dhugumatti isin dachii keessatti yeroo lama badii dalagdu; of tuulummaa guddaa ta’es of tuuluuf jirtu” (jechuun) beeksisa goonee jirra');
INSERT INTO "apaguna" VALUES(17,5,'17:5','Isaan lamaan irraa waadaan jalqabaa yeroo dhufe, gabroota Nuuf ta’an kan lolatti cimaa isin irratti ergineIsaanis gola manneen keessanii keessa ni deddeebi’ani(Waadichis) waadaa raawwatamaa ta’eera');
INSERT INTO "apaguna" VALUES(17,6,'17:6','Ergasii isaan irratti dabaree injifannoo isiniif deebifnee qabeenyaafi ilmaanis isiniif daballeGama gosaatiinis baay’ee isin taasifne.');
INSERT INTO "apaguna" VALUES(17,7,'17:7','Yoo toltuu hojjattaniif lubbuma keessaniif toltuu hojjattuYoo hamtuu hojjattanis isuma irrattiYeroo waadaan lamaffaa dhufe, akka (diinotiin keessan) fuula keessan hammeessan, akka masjiidichas (Aqsaas) akkuma yeroo jalqabaa isa seenanitti seenaniifi akka waan irratti injifatan barbadeessuu barbadeessan (isin irratti ergina)');
INSERT INTO "apaguna" VALUES(17,8,'17:8','Gooftaan keessan rahmata isiniif gochuun ni kajeelamaYoo isin (badiitti) deebitan, Nutis (isin adabuutti) ni deebinaJahannamiinis kaafirootaaf mana hidhaa taasifnee jirra.');
INSERT INTO "apaguna" VALUES(17,9,'17:9','Dhugumatti, Qur’aanni kun (karaa) ishee irra haqaafi qajeeltuu taatetti ni qajeelchaMu’uminoota warra gaggaarii hojjatan dhugumatti mindaan guddaan isaaniif jiraachuudhaan gammachiisa.');
INSERT INTO "apaguna" VALUES(17,10,'17:10','Isaan aakhiraatti hin ammanne immoo adabbii laalessaa ta’e isaaniif qopheessinee jirra (jechuun gammachiisa).');
INSERT INTO "apaguna" VALUES(17,11,'17:11','Ilmi namaa (yeroo aarii) hamtuu akkuma toltuu kadhatutti (Rabbiin) kadhataIlmi namaa jarjaraa ta’eera.');
INSERT INTO "apaguna" VALUES(17,12,'17:12','Halkaniifi guyyaas mallattoo lama taasifnee jirraMallattoo halkanii dukkanaan haguugnee, mallattoo guyyaa immoo ifa gooneAkka tola Gooftaa keessan irraa ta’e barbaaddaniifi akka lakkoofsa waggootaafi herregas beektaniif (kana goone)Waan hunda addaan baafnee isa addeessinee jirra.');
INSERT INTO "apaguna" VALUES(17,13,'17:13','Nama hundayyuu carraa isaa morma isaatti rarraafnee jirraGuyyaa Qiyaamaa kitaaba banamaa (inni) isa argatu taasifneeti isaaf baafna.');
INSERT INTO "apaguna" VALUES(17,14,'17:14','Kitaaba kee dubbisiHardha ati mataan kee of irratti herregaa ta’uuf ga’aadha.');
INSERT INTO "apaguna" VALUES(17,15,'17:15','Namni qajeele, kan inni qajeelu matuma ofiitiif qofaNamni jallates, kan inni jallatu ofuma irrattiLubbuun ba’aa baadhattu kamilleen ba’aa lubbuu biraa hin baadhattuHanga ergamaa erginutti kan adabnus hin taane.');
INSERT INTO "apaguna" VALUES(17,16,'17:16','Yeroo magaalaa tokko balleessuu feene, namoota qanani’oo ishee (akka Rabbiin sodaatan) ajajnaIsaan ishee keessatti ajaja Rabbii didu; kanaaf jechi (adabaa) isaan irratti mirkanaa’aIshee guutumatti barbadeessine.');
INSERT INTO "apaguna" VALUES(17,17,'17:17','Nuuhiin boodas magaalota irraa meeqaatama balleessine! Badii gabroota Isaa beekaa argaa ta’uun Gooftaan kee ga’aadha');
INSERT INTO "apaguna" VALUES(17,18,'17:18','Nama ishee (addunyaa) jerjertuu taate kana fedhu, ishee keessatti nama feeneef waan feene jerjersinaErgasii Ibidda jahannam arrabsamaa, darbamaa ta’ee kan seenu isaaf goona');
INSERT INTO "apaguna" VALUES(17,19,'17:19','Nama Aakhiraa fedhee haala dhugoomsee jiruun hojii ishee isiif hojjate, warra san hojiin isaanii galateeffatamaa ta’eera');
INSERT INTO "apaguna" VALUES(17,20,'17:20','Hundaayyuu, isaan kanaafi isaan sanas, kennaa Gooftaa keetii irraa (isaaniif) kenninaKennaan Gooftaa keetii dhorgatamaa miti.');
INSERT INTO "apaguna" VALUES(17,21,'17:21','Akkamitti garii isaanii garii irra akka caalchifne ilaaliHaa ta’u malee, Aakhiraatu sadarkaafi caalchisuuttis irra guddoodha.');
INSERT INTO "apaguna" VALUES(17,22,'17:22','Rabbii wajjin gabbaramaa biraa hin taasisin; (yoo waliin gabbarte) balaaleffatamaa, salphataa taatee teessa.');
INSERT INTO "apaguna" VALUES(17,23,'17:23','Gooftaan keetis akka isin Isa malee homaa hin gabbarreefi haadhaa abbaa keessanitti tola ooltan murteessee jiraYoo odoo ati lubbuun jirtuu tokkoon isaanii yookiin lamaanuu sadarkaa dullummaa irra ga’an, jecha hifannaa (tuffii) isaaniin hin jedhinIsaaniinis hin lolinJecha kabajaa isaaniin jedhi.');
INSERT INTO "apaguna" VALUES(17,24,'17:24','Isaaniif rahmata godhuuf jecha koola (qoochoo) xiqqeenyaa isaaniif gadi qabi“Gooftaa kiyya! Akkuma isaan anaa xiqqaa ta’ee na guddisan Atis rahmata isaaniif godhi” jedhi.');
INSERT INTO "apaguna" VALUES(17,25,'17:25','Gooftaan keessan waan lubbuu keessan keessa jiru akkaan beekaYoo namoota gaggaarii taataniif Inni warra akkaan gara Isaatti deebi’aniif araaramaa ta’eera.');
INSERT INTO "apaguna" VALUES(17,26,'17:26','Firaaf, harka qalleeyyiifi kara deemaafis haqa isaa kenni, qisaasuu (bazzaruu) hin qisaasin.');
INSERT INTO "apaguna" VALUES(17,27,'17:27','Dhugumatti, warri qisaasan obboleeyyan sheyxaanaa ta’aniiruSheyxaannis Gooftaa isaatti akkaan kafaraa ta’eera.');
INSERT INTO "apaguna" VALUES(17,28,'17:28','Yoo kennaa Gooftaa kee irraa ta’e kan isii kajeeltu barbaaduuf jecha isaan irraa gara galte, jecha laafaa isaaniin jedhi.');
INSERT INTO "apaguna" VALUES(17,29,'17:29','Harka keeti gara morma keetiitti sakaalamaa hin godhinDiriirinsa hundas isa hin diriirsinKomatamaa, sheena’aa taatee teessa.');
INSERT INTO "apaguna" VALUES(17,30,'17:30','Dhugumatti Gooftaan kee kennaa nama fedheef ni bal’isa; (nama fedhe irratti) ni dhiphisasDhugumatti, Inni gabroota Isaa beekaa, argaa ta’eera.');
INSERT INTO "apaguna" VALUES(17,31,'17:31','Ilmaan keessanis sodaa hiyyummaatiif jecha hin ajjeesinaaIsaaniifi isinis Nutu sooraDhugumatti, isaan ajjeesuun badii guddaa ta’eera.');
INSERT INTO "apaguna" VALUES(17,32,'17:32','Sagaagalummaattis hin dhihaatinaaDhugumatti, inni (hojii) fokkataafi karaa hamaa ta’ee jira.');
INSERT INTO "apaguna" VALUES(17,33,'17:33','Lubbuu Rabbiin (ajjeessuu) haraama godhe haqaan ala hin ajjeesinaaNama miidhamee ajjeefames nama isa dhaaluuf aangoo (dhiiga gaafachuu isaaf) kenninee jirraAjjeechaa keessatti daangaa hin dabrinDhugumatti, inni tumsamaa ta’eera.');
INSERT INTO "apaguna" VALUES(17,34,'17:34','Qabeenya yatiimaas, kaayyoo gaariif malee hanga inni cimina isaa ga’utti itti hin dhihaatinaaWaadaas guutaaDhugumatti, waadaan gaafatamaa ta’ee jira.');
INSERT INTO "apaguna" VALUES(17,35,'17:35','Yeroo safartanis safara guutaaMadaala qajeelaa ta’eenis madaalaaKun waan gaariidha; booddeen isaas bareedaadha.');
INSERT INTO "apaguna" VALUES(17,36,'17:36','Waan beekumsa isaa hin qabne hin hordofinDhugumatti, dhageettiin argaaniifi onneenis, kun hundinuu isa irraa gaafatamaa ta’eera.');
INSERT INTO "apaguna" VALUES(17,37,'17:37','Dachii keessas boonaa hin deeminDhugumatti, ati dachii hin urtu; dheerinnaanis gaara hin geessu.');
INSERT INTO "apaguna" VALUES(17,38,'17:38','Sun hundi yakki isaa Gooftaa kee biratti jibbamaa ta’eera');
INSERT INTO "apaguna" VALUES(17,39,'17:39','Kuni beekumsa irraa waan Gooftaan kee gara keetti wahyii godheedhaRabbii wajjinis gabbaramaa biraa hin taasisin(Yoos) komatamaa, fageeffamaa taatee jahannam keessatti darbamta.');
INSERT INTO "apaguna" VALUES(17,40,'17:40','Sila Gooftaan keessan isiniif ilmaan dhiiraa fileeti ofiif maleeykota irraa ilmaan dubaraa godhatee? Dhugumatti, isin jecha guddaa jettu.');
INSERT INTO "apaguna" VALUES(17,41,'17:41','Dhugumatti, Nuti Qur’aana kana keessatti akka (isaan) gorfamaniif deddeebinee ifa goonee jirraBaqa malee homaa isaaniif hin dabalu.');
INSERT INTO "apaguna" VALUES(17,42,'17:42','Jedhi: “Odoo akka isaan jedhan Isa waliin gabbaramtoonni biraa jiraatanii, silaa gara abbaa Arshiitti karaa barbaaddatu turan.”');
INSERT INTO "apaguna" VALUES(17,43,'17:43','Inni (Rabbiin) waan isaan jedhan irraa qulqulluufi ol ta’iinsa guddaas ol ta''eera');
INSERT INTO "apaguna" VALUES(17,44,'17:44','Samiin torbaniifi Dachiin, akkasumas wanni isaan keessa jiran hundi Isa faarsuWanti tokkos kan odoo faaruu Isaatiin Isa hin qulqulleessin hafu hin jiruGaruu isin faaruu isaanii hin beektanuDhugumatti, Inni obsaa, araaramaa ta’eera.');
INSERT INTO "apaguna" VALUES(17,45,'17:45','Yeroo Qur’aana dubbiftus gidduu keetiifi gidduu warra Aakhiraatti hin amannee haguuggii hin mul’anne taasifna.');
INSERT INTO "apaguna" VALUES(17,46,'17:46','Akka isaan isa (Qur’aana) hin hubannes onnee isaanii irratti haguuggii, gurra isaanii keessatti immoo duuchaa goonee jirraYeroo Qur’aana keessatti maqaa Gooftaa keetii kophaa Isaa dubbatte, baqataa dugda galu');
INSERT INTO "apaguna" VALUES(17,47,'17:47','Nuti yeroo isaan gara kee dhaggeeffatan, yeroo isaan maqoo (iccitii) hasaasan, yeroo miidhaa raawwattoonni “namticha sihriin tuqame malee waan biraa hin hordoftanu” (waliin) jedhanis waan isaan isa dhageeffataniif ni beekna.');
INSERT INTO "apaguna" VALUES(17,48,'17:48','Fakkeenya akkamii akka siif taasisan ilaaliIsaan jallatanii jiru; karaa (sirrii argachuus) hin danda’anu');
INSERT INTO "apaguna" VALUES(17,49,'17:49','Ni jedhus: “sila nuti erga (duunee) lafeefi bututaa (tortoraa) taanee booda uumama haarawa taanee kaafamoo dhaa?”');
INSERT INTO "apaguna" VALUES(17,50,'17:50','(Yaa Muhammad!) jedhiin: “Dhagaa yookiin sibiila ta’aa.');
INSERT INTO "apaguna" VALUES(17,51,'17:51','Yookiin uumama qoma keessan keessatti (kaafamuun) guddatus ta’aa (kaafamuun keessan hin hafu).” Ergasiis “Eenyutu nu deebisa” jedhu“Isa yeroo jalqabaa isin uumeedha” jedhiMataa isaanii sitti raasuuf jiruNi jedhus: “Inni sun yoomi ree?” “Dhihoo ta’uun mirkanaa’aadha” jedhiin.');
INSERT INTO "apaguna" VALUES(17,52,'17:52','Guyyaa Inni isin waamnaan, isin Isa faarsaa owwaattanii, yeroo gabaabaa malee turuu dhabuu yaaddan (ta’uun mirkanaa’e).');
INSERT INTO "apaguna" VALUES(17,53,'17:53','Gabroota kiyyaan “jecha gaggaarii dubbadhaaDhugumatti, sheyxaanni gidduu isaanii balleessaDhugumatti, sheyxaanni ilma namaatiif diina ifa bahaadha” jedhiin.');
INSERT INTO "apaguna" VALUES(17,54,'17:54','Gooftaan keessan akkaan isin beekaadhaYoo fedhes rahmata isiniif godha; yookiin yoo fedhe isin adabaNuti isaan irratti itti gaafatamaa goonee si hin ergine.');
INSERT INTO "apaguna" VALUES(17,55,'17:55','Gooftaan keenama samiifi dachii keessa jiru akkaan beekaadhaNuti dhugumatti nabiyyoota garii garii irra caalchifnee jirraDaawudiif zabuur kennineerra');
INSERT INTO "apaguna" VALUES(17,56,'17:56','“Warra Isa (Rabbii) gaditti (gabbaramaadha) jettan san waammadhaa! (Isaan) miidhaa isin irraa fuudhuus ta’ee naannessuu hin danda’an” jedhiin.');
INSERT INTO "apaguna" VALUES(17,57,'17:57','Warri isaan yaammatan sun eenyuu isaaniitu gara Rabbiitti irra dhihaataadhaan dhiheenya barbaadu.[] Rahmata isaas ni kajeeluadabbii isaas ni sodaatuDhugumatti, adabbiin Rabbii keetii sodaatamaa ta’ee jira.');
INSERT INTO "apaguna" VALUES(17,58,'17:58','Magaalota kamiyyuu Guyyaa Qiyaamaatiin dura ishee balleessinu yookiin adabbii cimaa ishee adabnu malee hin haftuSun kitaabicha keessatti barreeffamaa ta’eera.');
INSERT INTO "apaguna" VALUES(17,59,'17:59','Mallattoolee keenya erguu wanti Nu dhorge warri duraa ishee kijibsiisuu malee hin jiruSamuudiifis gaalattii dhaltuu akka mallattoo ifa galaatti kenninee jirraIsheettis kafaranii (halaakamani)Nuti sodaachisaaf malee mallattoo hin erginu.');
INSERT INTO "apaguna" VALUES(17,60,'17:60','Yeroo “dhugumatti, Gooftaan kee namootaan marseera” siin jennes [yaadadhu]Arginsa kee ishee (halkan Israa’ii) si argisiifne san, namoota ittiin mokkoruuf qofa malee waan biraatiif hin gooneMukattii Qur’aana keessatti abaaramtes (mokkoroof malee hin goone)Nuti isaan sodaachifna; inni daangaa dabriinsa guddaa malee waan biraa isaaniif hin dabalu.');
INSERT INTO "apaguna" VALUES(17,61,'17:61','Yeroo maleeykotaan “Aadamiif sujuuda godhaa” jennes [yaadadhu]Ibliis malee isaan sujuuda godhanInni (Ibliis) “sila ani nama Ati dhoqqee (biyyee) irraa uumteefin sujuuda godhaa?” jedhe.');
INSERT INTO "apaguna" VALUES(17,62,'17:62',')Ibliis) ni jedhe: “Mee naaf himi! Isa Ati ana caalaa kabajje kana, yoo hanga Guyyaa Qiyaamaatti na tursiifte, sanyii isaa muraasa malee garan fedhen harkisa.”');
INSERT INTO "apaguna" VALUES(17,63,'17:63',')Rabbiin) ni jedhe: “Deemi! Namni isaan irraa si hordofes dhugumatti, jahannam galata keessan galata guutamaa ta’eedha.');
INSERT INTO "apaguna" VALUES(17,64,'17:64','Isaan irraa nama dandeesses sagalee keetiin kakaasiYaabbattoota keetiifi lafoo keetiinis isaan irratti gulufsiisiQabeenyaafi ilmaan keessattis isaanitti qindaa’ii waadaas isaaniif gali.” Sheyxaanni gowwoomsuu malee homaa waadaa isaaniif hin galu.');
INSERT INTO "apaguna" VALUES(17,65,'17:65','Dhugumatti, gabroota kiyya, isaan irratti aangoon siif hin jiruHirkoo ta’uuf Rabbiin kee ga’aadha.');
INSERT INTO "apaguna" VALUES(17,66,'17:66','Gooftaan keessan Isa akka tola Isaa irraa barbaaddattaniif, doonii galaana keessa isiniif deemsisuudhaDhugumatti, Inni isiniif rahmata godhaa ta’eera.');
INSERT INTO "apaguna" VALUES(17,67,'17:67','Yeroo rakkoon wahii galaana keessatti isin tuqes, Isa malee namoonni isin kadhachaa turtan (hundi) ni baduYeroma Inni gara dachiitti isin baase (Isa irraa) gara galtanNamnis akkaan kafaraa ta’eera.');
INSERT INTO "apaguna" VALUES(17,68,'17:68','Sila (Rabbiin) cinaa dachii isiniin kutuu yookiin bubbee cirrachaa isin irratti erguu, ergasii hirkoo argachuu dhabuu keessan hin sodaattanuu?');
INSERT INTO "apaguna" VALUES(17,69,'17:69','Yookiin immoo yeroo biraa isa (galaana) keessatti isin deebisee yeroma san hobonboleettii isinitti ergee, sababa isin kafartaniif isin balleessee ergasii isaan (adaba sanaan) isiniif Nu irratti hordofaa argachuu dhabuu keessan hin sodaattanuu?');
INSERT INTO "apaguna" VALUES(17,70,'17:70','Dhugumatti, Nuti ilmaan Aadam kabajneerraDachiifi galaana keessattis isaan baadhanneerraWantoota gaggaarii (halaala) irraas soorata isaaniif kennine jirraIrra hedduu wanta uumnee irras caalchisuu isaan caalchifnee jirra.');
INSERT INTO "apaguna" VALUES(17,71,'17:71','Guyyaa nama hunda imaamota isaaniitiin waamnu [yaadadhaa]Namni galmeen (kitaabni) isaa mirga isaatiin kennameef, warri sun kitaaba isaanii ni dubbisu; hanga qajjiisa baqaqaa firii timiraa keessaallee hin miidhamanu.');
INSERT INTO "apaguna" VALUES(17,72,'17:72','Namni addunyaa keessatti jaamaa (qalbii) ta’e, aakhiraa keessattis jaamaadha; karaadhaanis irra jallataadha.');
INSERT INTO "apaguna" VALUES(17,73,'17:73','Dhugumatti, isaan waan Nuti gara keetti wahyii goone irraa, akka ati soba isaan ala ta’e Nu irratti uumtuuf jecha si jallisuutti dhiyaatanii turanOggas jaalallee si taasifatu ture.');
INSERT INTO "apaguna" VALUES(17,74,'17:74','Odoo Nuti gadi si dhaabuu baannee, silaa ati waan xiqqoo gara isaanii dabuutti dhihaattee turte');
INSERT INTO "apaguna" VALUES(17,75,'17:75','Oggas Nuti (adabbii) dachaa kan jireenyaatiifi (adabbii) dachaa du’aa si dhandhamsiifna tureErgasii ati Nurraa tumsaa hin argattu.');
INSERT INTO "apaguna" VALUES(17,76,'17:76','Dhugumatti, isaan akka ishee irraa si baasaniif dachii keessatti si shororkeessuutti dhihaatanii jiruYoos, si booda xiqqoo malee hin turan ture.');
INSERT INTO "apaguna" VALUES(17,77,'17:77','Karaa ergamtoota keenya irraa nama si dura erginee (karaa siif goone)Karaa keenyaaf jijjiirama hin argitu.');
INSERT INTO "apaguna" VALUES(17,78,'17:78','Aduun dabuu irraa hanga dukkana halkaniitti salaata ulaagaa ishee guutii sagadiSalaata fajriis (sagadi)Salaanni fajrii (maleykaadhaan) hirmaatamaa ta’eera');
INSERT INTO "apaguna" VALUES(17,79,'17:79','Halkan irraas isaan (Qur’aanaan) haala (salaata) dabalataa siif taateen sagadiGooftaan kee iddoo faarfamaa ta’e si dhaabuun mirkanaa’aadha.');
INSERT INTO "apaguna" VALUES(17,80,'17:80','Jedhis: “(yaa) Gooftaa kiyya! Seensa gaarii naseensisiBa’iinsa gaariis na baasiSi biraas ragaa na gargaaru naaf taasisi.');
INSERT INTO "apaguna" VALUES(17,81,'17:81','“Dhugaan dhufe; sobni dhabameDhugumatti, sobni dhabamaa ta’eera” jedhi.');
INSERT INTO "apaguna" VALUES(17,82,'17:82','Nuti Qur’aana irraa waan qorichaafi rahmata mu’uminootaaf ta’e kan miidhaa raawwattootaaf hoongoo malee homaa hin daballe buufna.');
INSERT INTO "apaguna" VALUES(17,83,'17:83','Yeroo ilma namaa ni’imaa keenyaan qanansiifnes, garagaleeti cinaa isaatiin (dhugaa) irraa fagaataYeroo hamtuun isa tuqes, abdii murataa ta’a.');
INSERT INTO "apaguna" VALUES(17,84,'17:84','Jedhi: “Hunduu karaa isatti fakkaateen dalagaGooftaan keetis nama karaan isaa qajeelaa ta’e akkaan beekaadha.”');
INSERT INTO "apaguna" VALUES(17,85,'17:85','Ruuhii irraa si gaafatu“Ruuhiin dhimma (amrii) gooftaa kiyyaatiBeekumsa irraa waan xiqqoo malee isiniif hin kennamne” jedhiin.');
INSERT INTO "apaguna" VALUES(17,86,'17:86','Odoo feenee isa gara keetti wahyii goone ni deemsifna turreErgasii siif Nurraa isa (deebisutti) itti gaafatamaa hin argattu.');
INSERT INTO "apaguna" VALUES(17,87,'17:87','Garuu rahmata Gooftaa kee irraa ta’eef (isa eegne)Dhugumatti, tolli Isaa si irratti guddaa ta’eera.');
INSERT INTO "apaguna" VALUES(17,88,'17:88','Jedhi: “odoo ilmi namaatiifi jinnoonnis fakkaataa Qur’aana kanaatiin dhufuu irratti walitti qabamanii, odoo gariin isaanii gariif gargaaraa ta’aniis fakkaataa isaatiin hin dhufan.”');
INSERT INTO "apaguna" VALUES(17,89,'17:89','Dhugumatti, Nuti Qur’aana kana keessatti fakkeenyota hunda irraa namootaaf addeessinee jirraIrra hedduun namootaa kafaruu malee didanii jiru.');
INSERT INTO "apaguna" VALUES(17,90,'17:90','Ni jedhanis: “Hanga burqituu dachii keessaa nuuf maddisiiftutti siif hin amannu.');
INSERT INTO "apaguna" VALUES(17,91,'17:91','Yookiin (hanga) oyruu muka timiraatiifi Inabaa irraa siif ta’ee, ergasii isa keessa laggeen yaasiinsa yaaftutti (hin amannu).');
INSERT INTO "apaguna" VALUES(17,92,'17:92','Yookiin (hanga) akka jettetti kutaa samii irraa taate nu irratti kuffiftutti yookiin (hanga) Rabbiifi maleeykota fuuldura keenya fiddutti (hin amannu).');
INSERT INTO "apaguna" VALUES(17,93,'17:93','Yookiin (hanga) manni faaya warqee irraa ta’e siif ta’utti yookiin (hanga) samii keessa ol baatutti; ol ba’iinsa keettis hanga kitaaba dubbifnu nuuf buuftutti hin amannu” (jedhu)“Gooftaan kiyya qulqullaa’ee jiraSila ani nama ergamaa (Rabbii) ta’ee ergame malee waan biraa ta’eeraa?” jedhi');
INSERT INTO "apaguna" VALUES(17,94,'17:94','Namootas yeroo qajeelfamni (Rabbii) isaanitti dhufe “sila Rabbiin nama ergamaa taasisee ergaa?” jechuu malee homtuu amanuu isaan hin dhorgine.');
INSERT INTO "apaguna" VALUES(17,95,'17:95','“Odoo dachii keessa maleykonni tasgabbaa’anii deeman kan jiraatan ta’ee, silaa maleykaa samii irraa ergamaa taasifnee isaan irratti buufna turre” jedhi.');
INSERT INTO "apaguna" VALUES(17,96,'17:96','“Gidduu kiyyaafi gidduu keessanitti ragaaf Rabbiin ga’aadhaDhugumatti, Inni gabroota Isaa irratti beekaa, argaa ta’eera” jedhiin');
INSERT INTO "apaguna" VALUES(17,97,'17:97','Namni Rabbiin qajeelche, isatu qajeelaadhaNama Inni jallises, isaaniif Isaa gaditti gargaartota hin argituufGuyyaa Qiyaamaas fuula isaanii irratti, jaamaa, arrabaa qabamaafi gurraa duudaa ta’anii isaan kaafnaTeessoon isaaniis jahannamiDhawaata isiin gadi laaftuun gubaa ibiddaa isaaniif daballa.');
INSERT INTO "apaguna" VALUES(17,98,'17:98','Waan isaan keeyyattoota keenyatti kafaranii “sila yeroo lafeefi biyyee taane, nuti dhugamatti uumama haara’a taanee kaafamoodhaa?” jedhaniif kun mindaa isaaniiti.');
INSERT INTO "apaguna" VALUES(17,99,'17:99','Sila akka Rabbiin samiifi dachii uume fakkaataa isaanii uumuu irratti danda’aa ta’eefi beellama isa keessa shakkiin hin jirre isaaniif taasise ta’uu hin ilaallee? Miidhaa dalagdoonni (Rabbitti) kafaruu malee ni didan');
INSERT INTO "apaguna" VALUES(17,100,'17:100','Jedhi: “odoo isin dilbii rahmata Gooftaa kiyyaa dhuunfattanii, silaa dhumuu sodaaf (of biratti) qabattu turtan.” Ilmi namaa akkaan don’a ta’eera.');
INSERT INTO "apaguna" VALUES(17,101,'17:101','Dhugumatti, Muusaaf mallattoolee ifa ta’an sagal kennineefii jirraIlmaan Israa’iil, yeroo inni isaanitti dhufnaan Fir’awnis “yaa Muusaa! Ani dhugumatti sihriin qabamaa ta’uun si yaada” isaan jedhe gaafadhu.');
INSERT INTO "apaguna" VALUES(17,102,'17:102','(Muusaanis) ni jedhe: “Ati dhugumatti isaan kana haala ragaa ifa ba’aa ta’aniin Gooftaa samiifi dachii malee homtuu hin buusin sirritti beektee jirtaAnis yaa Fir’awn! Dhugumatti, ati halaakamaa ta’uun si yaada.”');
INSERT INTO "apaguna" VALUES(17,103,'17:103','Dachii (Misraa) irraas isaan baasuu fedheIsaafi namoota isa waliin turan hundas bishaan isaan nyaachifne.');
INSERT INTO "apaguna" VALUES(17,104,'17:104','Nutis isa booda ilmaan Israa’iiliin ni jenne: “dachii keessa jiraadhaaYeroo beellamni boodaa (Qiyaamaan) dhufe, hunda keessaniin dhufna.”');
INSERT INTO "apaguna" VALUES(17,105,'17:105','Nutis dhugaan isa (Qur’aana) buufne; innis dhugaan bu’eGammachiisaafi sodaachisaa goonee malee Nuti si hin ergine.');
INSERT INTO "apaguna" VALUES(17,106,'17:106','Qur’aana akka ati suuta suutaan namoota irratti isa dubbiftuuf isa qoqqoodne; buusiinsas isa buufne.');
INSERT INTO "apaguna" VALUES(17,107,'17:107','Jedhi: “Isatti amanaa yookiinis hin amaninaa; dhugumatti, isaan isa dura beekumsa kennaman, yeroo inni isaan irratti dubbifame fuula isaaniitiin sujuuda bu’u.”');
INSERT INTO "apaguna" VALUES(17,108,'17:108','Ni jedhus: “Gooftaan keenya qulqullaa’eeraDhugumatti, waadaan Gooftaa keenyaa raawwatamaadha.”');
INSERT INTO "apaguna" VALUES(17,109,'17:109','Fuula isaanii irratti booyaa sujuuda bu’uInnis sodaa isaaniif dabala.');
INSERT INTO "apaguna" VALUES(17,110,'17:110','Jedhi: “Allaah kadhaa yookiin Rahmaaniin kadhaa; kamiinuu yoo kadhattan Isaaf maqoota babbareedaa ta’antu jiraSalaata kee keessatti (baay’ee) sagalee kee ol hin fuudhin; (baay’ee) gadis hin qabatinGiddu (lamaan) sanitti karaa godhadhu');
INSERT INTO "apaguna" VALUES(17,111,'17:111','”Faaruun hundi kan Rabbii ilma hin godhatiniiti, kan mootummaa keessatti hiriyyaa hin qabneefi kan sababa salphinaa gargaaraan isaaf hin jirreeti” jedhiGuddisuu Isa guddisi');
INSERT INTO "apaguna" VALUES(18,1,'18:1','Faaruun hundi kan Rabbii gabricha Isaa irratti kitaaba buusee isa keessattis daba hin godhiniiti.');
INSERT INTO "apaguna" VALUES(18,2,'18:2','Qajeelaa (godhee) akka adabbii cimaa Isa biraa ta’e ittiin sodaachisee, mu’uminoota gaggaarii hojjatan ‘mindaan bareedaan isaaniif jiraachuudhaan’ gammachiisuuf (buuse).');
INSERT INTO "apaguna" VALUES(18,3,'18:3','Isa keessa yeroo hunda jiraatoodha');
INSERT INTO "apaguna" VALUES(18,4,'18:4','Isaan “Rabbiin ilma qaba” jedhanis sodaachisuuf (isa buuse).');
INSERT INTO "apaguna" VALUES(18,5,'18:5','Isaaniifi abbootii isaaniitiifis isa irratti beekumsi[] hin jiruJechi afaan isaanii irraa baatu waa guddatte! Soba malee homaa hin haasa’anu.');
INSERT INTO "apaguna" VALUES(18,6,'18:6','Yoo isaan haasa’a kanatti amanuu baatan gaddaan faana isaanii irratti lubbuu kee balleessuutu siif sodaatama.');
INSERT INTO "apaguna" VALUES(18,7,'18:7','Dhugumatti, Nuti isaan irraa eenyu akka irra tolchee hojjatu mul’isuuf waan dachii irra jiru (hunda) isheef miidhagina taasifne');
INSERT INTO "apaguna" VALUES(18,8,'18:8','Dhugumatti, Nuti wantoota ishee (dachii) irra jiran hunda dirree duwwaa biqilaa hin qabne taasisoodha.');
INSERT INTO "apaguna" VALUES(18,9,'18:9','Sila (seenaan) warra holqaafi gabatee mallattoolee keenya irraa dinqisiisaa ta’uu yaaddee?');
INSERT INTO "apaguna" VALUES(18,10,'18:10','Yeroo dargaggoonni gara holqaa galanii “(yaa) Gooftaa keenya! Si biraa rahmata nuuf kenni; dhimma keenya irraas karaa qajeelaa nuuf mijeessi” jedhan (waan ta’e yaadadhu)');
INSERT INTO "apaguna" VALUES(18,11,'18:11','Holqa keessattis waggoota lakkaa’amaniif dhageettii isaanii irratti (hirriba) darbine.');
INSERT INTO "apaguna" VALUES(18,12,'18:12','Ergasii Nuti ‘kamii gareewwan lamaaniitu dheerina yeroo turanii irra hubataa beekuuf jecha’ isaan dammaqsine.');
INSERT INTO "apaguna" VALUES(18,13,'18:13','Nuti oduu isaanii dhugaan siif odeessinaDhugumatti, isaan dargaggoota Gooftaa isaaniitti amananii, qajeeluma isaaniif daballeedha.');
INSERT INTO "apaguna" VALUES(18,14,'18:14','Ammas Nuti yeroo isaan dhaabbatanii “Gooftaan keenya Gooftaa samiifi dachiitiNuti Isa malee gabbaramaa biraa hin waammannuDhugumatti, (yoo kana raawwanne), haasa’a (kufrii keessa) daangaa dabre dubbanne jirra” jedhan onnee isaanii irratti (iimaana) jabeessinee jirra.');
INSERT INTO "apaguna" VALUES(18,15,'18:15','“Ummanni keenya kun Isaa gaditti gabbaramoota biraa taasifataniiruMaaliif ragaa ifa ta’e isaan irratti hin fidatanu? Nama Rabbi irratti soba uume caalaa namni miidhaa hojjate eenyu?');
INSERT INTO "apaguna" VALUES(18,16,'18:16',')Dargaggoonnis ni jedhan): “Yeroo isaaniifi wantoota isaan Rabbiin ala gabbaran irraa fagaattan, gara holqaa galaaGooftaan keessan rahmata Isaa irraa isiniif bal’iseeti dhimma keessan irraas laafina isiiniif qopheessa.”');
INSERT INTO "apaguna" VALUES(18,17,'18:17','Aduus yeroo baatu holqa isaanii irraa kan gara mirgaatti dabdu argitaYeroo dhiitus, haala isaan bal’ina isa (goda) irraa ta’e keessa jiraniin gara bitaatti isaan dhiiftiKun mallattoolee Rabbii irraayiNamni Rabbiin (isa) qajeelche, isatu qajeelaadhaNamni Inni jallise immoo gargaaraa isa qajeelchu isaaf hin argitu.');
INSERT INTO "apaguna" VALUES(18,18,'18:18','Kanuma isaan rafaa jiranu dammaqoo isaan seetaMirgaafi bitaatti isaan garagaggalchinaSareen isaaniis dhundhumma isaa lamaan moggaa (holqaatti) diriirsaadhaOdoo isaan argitee dhugumatti, silaa baqannaan isaan irraa garagalta; isaan irraas sodaa guutamta turte.');
INSERT INTO "apaguna" VALUES(18,19,'18:19','Akkuma san, akka gidduu isaanitti wal gaafataniif jecha isaan dammaqsineJedhaan isaan irraa ta’e tokko “Yeroo hangam (as) turtan?” jedhe“Guyyaa tokko yookiin garii guyyaa turre” jedhanNi jedhan: “Yeroo hangam akka (as) turtan Gooftaa keessan (qofatu) akkaan beekaTokkoo keessan maallaqa keessan kanaan gara magaalaa ergaaNyaanni gaariin kam akka ta’e ilaalee, isa irraa soorata isiniif haa fiduHaa dhokatus; waa’ee keessanis nama tokkos akka hin beeksifne.');
INSERT INTO "apaguna" VALUES(18,20,'18:20','Isaan yoo isin argan, dhagaan tumanii isin ajjeesu yookiin amantii isaaniitti isin deebisuYoo san ammoo gonkumaa hin milkooftan.”');
INSERT INTO "apaguna" VALUES(18,21,'18:21','Akka kanatti akka isaan beellamni Rabbii dhugaa ta’uu beekaniifi Qiyaamaas shakkiin ishee keessa jiraachuu dhabuu akka beekaniif, yeroo isaan gidduu isaaniitti waa’ee warra holqaa keessatti wal dhaban, isaan irratti mul’ifneNi jedhan: “Gooftaa isaaniitu akkaan isaan beekaa, ijaarsa isaan irratti ijaaraa.” Warri dhimma isaanii irratti injifatan immoo “Nuti dhugumatti, masjida isaan irratti godhanna” jedhan.');
INSERT INTO "apaguna" VALUES(18,22,'18:22','(Waan hin argin shaallaguudhaan) “Isaan yoo sadi ta’an, afraffaan isaanii saree isaaniiti” jechuuf jiru“Isaan shan yoo ta’an jahaffaan isaanii saree isaaniiti” jedhus“Isaan torba yoo ta’an saddeettaffaan isaaniis saree isaaniti” jedhu(Yaa Muhammad!): “Lakkoofsa isaanii Gooftaa kiyyatu akkaan beeka, namoota muraasa malee isaan hin beeku” jedhiKanaafuu mormii ifa ta’e malee, waa’ee isaanii keessatti hin falminWaa’ee isaanii keessattis isaan irraa nama tokkos hin gaafatin.');
INSERT INTO "apaguna" VALUES(18,23,'18:23','Waan tokkos “Ani waan san boru hojjataadha” hin jedhin');
INSERT INTO "apaguna" VALUES(18,24,'18:24','“Yoo Rabbiin fedhe” (jettu) maleeyeroo dagattes Gooftaa kee yaadadhu“Gooftaan kiyya gara karaa isa kana irra qajeelumatti dhihoo ta’ee na qajeelchuun ni kajeelama” jedhi.');
INSERT INTO "apaguna" VALUES(18,25,'18:25','Isaanis holqa isaanii keessa waggoota dhibba sadi turanii sagalis dabalatan.');
INSERT INTO "apaguna" VALUES(18,26,'18:26','“Hanga isaan turan Rabbumatu akkaan beekaa(Beekumsi) fagoo kan samiifi dachii Isumaafi” jedhiAkkam arga! Akkam dhaga’as! Isaaniif Isa malee gargaaraan hin jiruMurtii Isaa keessattis tokkollee hin hirmaachisu');
INSERT INTO "apaguna" VALUES(18,27,'18:27','Waan kitaaba Gooftaa keetii (Qur’aana) irraa gara keetti beeksisa godhame dubbisiJechoota Isaa jijjiiraan hin jiruIsa malee iddoo itti dabdus hin argattu.');
INSERT INTO "apaguna" VALUES(18,28,'18:28','Lubbuu keetis isaan Fuula Isaa fedhanii Gooftaa isaanii ganamaafi galgala waammatanii wajjiin hidhiMiidhagina jireenya addunyaa feetee ija kee lamaan isaan irra hin qaarsinNama onnee isaa yaadannoo keenya irraa cufne, kan fedha lubbuu isaa hordofuufi dhimmi isaa daangaa darbaa ta’eef hin ajajamin.');
INSERT INTO "apaguna" VALUES(18,29,'18:29','Jedhis: “Dhugaan Gooftaa keessan irraayiNamni fedhe haa amanu; namni fedhes haa kafaruDhugumatti, Nuti miidhaa hojjattootaaf ibidda dallaan ishee isaanitti marse qopheessinee jirraYoo birmachiifatanis bishaan akka rajaa (siicoo) danfeetti fuula quncisutu isaaniif kennamaDhugaatichis akkaan fokkate! Hirkannaanis akkaan fokkate!');
INSERT INTO "apaguna" VALUES(18,30,'18:30','Dhugumatti isaan amananii, gaggaarii hojjatan, Nuti mindaa nama dalagaa tolchee hin balleessinu.');
INSERT INTO "apaguna" VALUES(18,31,'18:31','Warra san isaaniif jannata qubsumaa kan laggeen isaan jala yaatuutu jiraAchi keessatti isaan gumeewwan warqee irraa ta’een miidhagfamuUffata magariisa hariira haphiifi furdaa irraa hojjatamanis uffatuIshee keessatti sireewwan irratti hirkatanii taa’uMindaan waa tole! Hirkannaanis waa bareedde!');
INSERT INTO "apaguna" VALUES(18,32,'18:32','Dhiira lama kan tokkoo isaaniittiif oyruu lama kan inaba irraa ta’an taasifnee, isaan lamaanuu muka timiraatiin marsinee, gidduu isaaniittis midhaan (facaafamu) taasifne fakkeenya isaaniif godhi.');
INSERT INTO "apaguna" VALUES(18,33,'18:33','Oyruun lamaanuu firii isaanii odoo homaa hin hirdhisin kennite; gidduu isaaniitii laga maddisiifne (san fakkeenya godhiif)');
INSERT INTO "apaguna" VALUES(18,34,'18:34','Isaaf midhaan godhatee hiriyaa isaatiin odoo itti haasa’uu “Anatu qabeenyaan si caala; gosaanis sirra jabaadha” jedheen.');
INSERT INTO "apaguna" VALUES(18,35,'18:35','Haala lubbuu isaa miidhaa ta’een oyruu isaa seenee ni jedhe: “Isiin kun gonkumaa ni baddii hin yaadu.');
INSERT INTO "apaguna" VALUES(18,36,'18:36','Qiyaamaanis ni dhaabbattii hin yaaduDhugumatti, yoon gara Gooftaa kiyyaa deebifameef, iddoo deebii kan isa caaluun argadha.”');
INSERT INTO "apaguna" VALUES(18,37,'18:37','Hiriyyaan isaas kan isaan haasa’u ta’ee ni jedhe: “Sila ati Isa biyyee irraa, ergasii bishaan saalaa irraa si uumee, ergasii nama guutuu si godhetti kafartee?');
INSERT INTO "apaguna" VALUES(18,38,'18:38','Ani garuu Inni, Allaah’n Gooftaa kiyya; ani Gooftaa kiyyatti tokkollee hin dabalu');
INSERT INTO "apaguna" VALUES(18,39,'18:39','Yeroo oyruu kee seentu “waan Rabbiin fedhetu (ta’a); Rabbiin malee humni (naaf) hin jiru” maaliif jechuu dhiifte! Yoo ana horiifi ilmaaniiniis sirra xiqqaa ta’uu na agartes;');
INSERT INTO "apaguna" VALUES(18,40,'18:40','Gooftaan kiyya waan oyruu kee irra caalu naaf kennee, ishee (kee) irratti immoo samii irraa adabbii buusee, dirree duwwaa ta’uun ishee ni mala');
INSERT INTO "apaguna" VALUES(18,41,'18:41','Yookiin bishaan ishee gadi lixaa taanaan ati isa barbaaduu dadhabuun (ni mala).”');
INSERT INTO "apaguna" VALUES(18,42,'18:42','Midhaan isaattis ni marfame (ni bade)Innis odoo isheen mogolee (jirma) ishee irratti kukkuftee jirtuu, ganaa (shanacha) isaa lamaan gara gaggalchaa “yaa gaabbii koo! Odoon Gooftaa kiyyatti waa tokko dabaluu baadhee” jechuutti seene');
INSERT INTO "apaguna" VALUES(18,43,'18:43','Gareen Rabbii gaditti isa tumsitus isaaf hin jirtuInnis tumsamaa hin taane.');
INSERT INTO "apaguna" VALUES(18,44,'18:44','Achittiis (Qiyaamaas) mootummaan kan Rabbii haqa ta’eetiInni mindeessuutti Isumatu caala; boodattis Isumatu caalaadha.');
INSERT INTO "apaguna" VALUES(18,45,'18:45','Fakkeenya jiruu addunyaa akka bishaan samii irraa buufnee, biqiltuuwwan dachii isatti makamanii, goggogaa caccabaa bubbeen isa faffacaaftu ta’een godhiifiRabbiin waan hunda irratti danda’aa ta’eera');
INSERT INTO "apaguna" VALUES(18,46,'18:46','Qabeenyiifi ilmaan faaya jirreenya addunyaatiGaggaarii haftuu taatetu Gooftaa kee biratti mindaadhaan irra caala; gama abdiittis irra caalaadha.');
INSERT INTO "apaguna" VALUES(18,47,'18:47','Guyyaa Nuti gaarreen deemsifnee dachiis dirree ta’uu argitee, isaanis walitti qabnu [yaadadhu]Isaan irraas tokkollee hin hanbifnu');
INSERT INTO "apaguna" VALUES(18,48,'18:48','Hiriiroo godhamanii Gooftaa kee irra fidaman; “dhugumatti, akka yeroo jalqabaa isin uumnetti nutti dhuftanNuti waadaa isiniif godhuu dhabuu yaaddu turtan” (jedhaman).');
INSERT INTO "apaguna" VALUES(18,49,'18:49','Galmeenis kaa’amee, yoma san badii raawwattoota waan isa keessa jiru irraa sodaatoo ta’anii isaan agartaNi jedhus: “Yaa badii keenya! Kitaabni kun kan xiqqoofi guddoos ishee walitti qabu malee hin dhiifne maaluma!” Waan hojjatan dhiyaataa ta’ee arguGooftaan kee tokkollee hin miidhu.');
INSERT INTO "apaguna" VALUES(18,50,'18:50','Yeroo maleeykotaan “Aadamiif sujuuda godhaa” jennes [yaadadhu]Ibliis jinnii irraa ta’ee ajaja Gooftaa isaa irraa fincile malee sujuuda godhanSila isaan isiniif diinoota odoo ta’anii jiranuu, Ana dhiiftanii isaafi sanyii isaa jaalallee godhattuu? (Ibliis) miidhaa raawwattootaaf jijjiirraa ta’uun akkaan fokkate!');
INSERT INTO "apaguna" VALUES(18,51,'18:51','Ani uumaminsa samii, dachiifi uumaminsa lubbuu isaanii irrattis isaan hin hirmaachifneAni jallistootas gargaartota godhataa hin taane.');
INSERT INTO "apaguna" VALUES(18,52,'18:52','Guyyaa Inni “(warra) hiriyyoota Kiyya akka ta’an dubbattan san waamaa” jedhus [yaadadhu]Isaanis ni waamanii, garuu isaaniif hin owwaatanNutis gidduu isaaniitti hallayyaa (jahannam) taasifna.');
INSERT INTO "apaguna" VALUES(18,53,'18:53','Yakkamtoonni ibidda ni arganIshee keessa bu’oo ta’uu isaanii yaadaniKaraa ishee jalaa ittiin ba’anis hin argatan.');
INSERT INTO "apaguna" VALUES(18,54,'18:54','Nuti dhugumatti Qur’aana kana keessatti fakkeenyota hunda irra deddeebinee (namootaaf) ibsinee jirraNamnis waan hunda irra falmii baay’isaa ta’eera.');
INSERT INTO "apaguna" VALUES(18,55,'18:55','Namoota yeroo qajeelumni isaanitti dhufe amanuufi Gooftaa isaanii araara kadhachuu irraa wanti isaan dhorge karaan (dhumiinsi) warra durii isaanitti dhufuu yookiin adabbiin fuulduraa isaanitti dhufuu (eeguu) malee homaa hin jiru');
INSERT INTO "apaguna" VALUES(18,56,'18:56','Nuti ergamtootas gammachiiftotaafi dinniintoota goonee malee hin erginuWarri kafaran sobaan haqa deemsisuuf isaan falmu; keeyyattoota kiyyaafi waan dinniinamanis qishnaa godhatan.');
INSERT INTO "apaguna" VALUES(18,57,'18:57','Nama keeyyattoota Gooftaa isaatiin yaadachiifamee ergasii ishee irraa garagaluufi waan harki isaa lamaan dabarfatte dagate caalaa namni miidhaa hojjate eenyu? Nuti dhugumatti akka isaan isa (Qur’aana) hin hubanne onnee isaanii irratti qadaaddaafi gurra isaanii keessattis duudiinsa taasifnee jirraYoo gara qajeellumaa isaan waamtes, isaan yoo san gonkumaa hin qajeelan.');
INSERT INTO "apaguna" VALUES(18,58,'18:58','Gooftaan keetis akkaan araaramaa, abbaa rahmataatiOdoo waan isaan hojjataniin isaan qabee, silaa dhugumatti, adabbii isaaniif jerjersa tureGaruu isaaniif waadaa isa malee garri itti deebi’an hin jirre qabu.');
INSERT INTO "apaguna" VALUES(18,59,'18:59',')Warra) magaalotaa sanis sababaa isaan miidhaa hojjatan isaan balleessineYeroo isaan itti balleeffamanuufis beellama murtaa’aa taasifneerra.');
INSERT INTO "apaguna" VALUES(18,60,'18:60','Yeroo Muusaan dargaggoo isaatiin “hangan iddoo galaanni lamaan itti wal qunnaman ga’utti yookiin deemsa irratti waggoota (hedduu) fixutti (deemuu) hin dhiisu” jedhe [yaadadhu]');
INSERT INTO "apaguna" VALUES(18,61,'18:61','Yeroma isaan lamaan iddoo wal ga’iinsa galaana lamaanii ga’an, qurxummii isaanii ni dagatanInnis (qurxummiin) karaa isaa galaana keessa baqaqaa godhate.');
INSERT INTO "apaguna" VALUES(18,62,'18:62','Yeroma isaan lamaan (iddoo san) qaxxaamuran, (Muusaan) dargaggicha isaatiin ni jedhe: “Laaqana keenya nuuf fidiDhugumatti, nuti imala keenya kana irraa dadhabbii guddaa qunnamnee jirra.”');
INSERT INTO "apaguna" VALUES(18,63,'18:63',')Innis) ni jedhe: “Argitee? Sila yeroo nuti gara dhagaa hirkanne, dhugumatti, ani qurxummii irraanfadheeraWanti akka isa hin yaadanne na irraanfachiises sheyxaana malee hin jiruInnis karaa isaa galaana keessa dinqisiisaa godhate.”');
INSERT INTO "apaguna" VALUES(18,64,'18:64',')Muusaan) ni jedhe: “Sun waan nuti barbaadaa turreedha.” Faana isaanii irras hordafaa gara duubaa deebi’an.');
INSERT INTO "apaguna" VALUES(18,65,'18:65','Gabricha gabroota keenya irraa ta’e, kan Nuti of biraa rahmata isaaf kennineefi beekumsa Nurraa ta’e isa barsiifne wahii argan.');
INSERT INTO "apaguna" VALUES(18,66,'18:66','Muusaanis isaan “sila waan barsiifamte kan qajeelaa ta’e irraa akka na barsiiftuuf si hordofuu danda’aa?” jedhe');
INSERT INTO "apaguna" VALUES(18,67,'18:67',')Namichis) ni jedhe: “Dhugumatti, ati ana wajjiin obsuudhaaf hin dandeessu');
INSERT INTO "apaguna" VALUES(18,68,'18:68','Sila waan beekumsa isaa hin qabnee irratti akkamitti obsita?”');
INSERT INTO "apaguna" VALUES(18,69,'18:69',')Muusaanis) “Yoo Rabbiin fedhe obsaa ta’uu na argitaAjaja keetis hin didu” jedhe');
INSERT INTO "apaguna" VALUES(18,70,'18:70',')Namichis) yoo na hordofte, waan tokkoyyuu hanga ani waa’ee isaa siif himutti homaa irraa na hin gaafatin” jedheen');
INSERT INTO "apaguna" VALUES(18,71,'18:71','Isaan lamaanuu hanga doonii tokko keessa yaabbatanitti ni deemanInnis (namichis) ishee ure(Muusaanis) ni jedhe: “Sila namoota ishee keessa jiran bishaan nyaachisuuf ishee urtee? Dhugumatti, ati waan guddaa fidde!”');
INSERT INTO "apaguna" VALUES(18,72,'18:72',')Namichis) ni jedhe: “Sila ani dhugumatti, ati ana wajjiin obsuudhaaf hin dandeessu siin hin jennee?”');
INSERT INTO "apaguna" VALUES(18,73,'18:73','(Muusaanis) ni jedhe: “Waanan irraanfadheef na hin qabinDhimma kiyya irraas rakkoo na hin baachisin.”');
INSERT INTO "apaguna" VALUES(18,74,'18:74','Hanga mucaa tokkotti dhufanitti deemanii achumaan isa (mucicha) ajjeese(Muusaanis) “Sila ati lubbuu qulqulluu lubbuu (ajjeesuun) ala ajjeeftaa? Dhugumatti, ati waan fokkataa hojjatteetta” jedhe.');
INSERT INTO "apaguna" VALUES(18,75,'18:75','(Namichis) ni jedhe: “Sila ani dhugumatti, ati ana wajjiin obsuudhaaf hin dandeessu siin hin jennee?”');
INSERT INTO "apaguna" VALUES(18,76,'18:76',')Muusaanis) ni jedhe: “Yoon ishee kana booda waan tokko irraas si gaafadhe, natti hin miiltomiin (natti hin shariikin)Dhugumatti, ana biraa dhiifama dhumaa irra geessee jirta.”');
INSERT INTO "apaguna" VALUES(18,77,'18:77','Ergasii isaan lamaan hanga warra magaalattii tokkoo bira dhufanii, jiraattota ishee nyaata gaafannaan, isaan keessummeessuu didanitti deemanErgasii dhaaba mana jiguu fedhuu tokko arganii, (namichi) ol kaasee isa dhaabe(Muusaanis): “Odoo feetee isa irratti mindaa fudhachuu ni dandeessa” jedhe.');
INSERT INTO "apaguna" VALUES(18,78,'18:78',')Namichis) ni jedhe: “Kun addaan ba’iinsa gidduu kiyyaafi gidduu keetiitiAni hiikkaa wantoota ati irratti obsuu dadhabdee sittiin hima.');
INSERT INTO "apaguna" VALUES(18,79,'18:79','Doonittiin qabeenya namoota hiyyeeyyii galaana keessa dalaganii turteWaan isaan fuula duratti mootiin dooniwwan (fayyaa) hunda saamee fudhatu tokko jiruufin ishee irratti hirdhina uumuu fedhe.');
INSERT INTO "apaguna" VALUES(18,80,'18:80','Mucichi immoo, haadhaafi abbaan isaa mu’uminootaNutis (yoo guddate) jallinaafi kufriidhaan isaan haguuguu sodaanne.');
INSERT INTO "apaguna" VALUES(18,81,'18:81','Gooftaan isaanii (ilma) qulqullinaan isa irra caaluufi rahmatattis irra dhihoo ta’e bakka isaaniif buusu feene.');
INSERT INTO "apaguna" VALUES(18,82,'18:82','Dhaabni manichaa immoo kan ijoollee yatiimota ta’an lama magaalattii keessa jiraataniiti tureIsa jala immoo qabeenya awwaalame kan isaan lamaaniitu jiraAbbaan isaanii nama gaarii tureGooftaan keetis (ijoollen lamaan) umrii ciminaa irra ga’anii qabeenya isaanii kan lafa jalatti awwaalame rahmata Gooftaa kee irraa ta’een akka baafatan fedheAnis kana (hunda) fedha kiyya irraa ka’ee hin hojjanneKun hiikkaa wantoota ati irratti obsuu dadhabdeeti.”');
INSERT INTO "apaguna" VALUES(18,83,'18:83','Zulqarnayni irraas si gaafatu“Ani isa irraa oduun isin irratti dubbisuuf jira” jedhi.');
INSERT INTO "apaguna" VALUES(18,84,'18:84','Dhugumatti, Nuti dachii keessatti isaaf mijeessinee jirraWaan hunda irraas tooftaa isaaf kennine.');
INSERT INTO "apaguna" VALUES(18,85,'18:85','Innis karaa deeme');
INSERT INTO "apaguna" VALUES(18,86,'18:86','Yeroma iddoo lixa aduu ga’u, isheedhaa galaana dhoqqee gurraacha qabdu keessaan lixxuu argeIshee birattis ummata wahii argeNuti ni jenneeni: “Yaa Zulqarnayn! Ati isaan adabuu yookiin isaan keessatti toltuu hojjachuu (filannoo qabda).”');
INSERT INTO "apaguna" VALUES(18,87,'18:87','Inni ni jedhe: “Nama miidhaa hojjate nuti fuulduratti isa adabnaErgasii gara Gooftaa isaa deebifamee Innis adabbii hamaa ta’e isa adabuuf jira.');
INSERT INTO "apaguna" VALUES(18,88,'18:88','Nama amanee, gaariis hojjate immoo, isaaf mindaa gaarii ta’etu jiraNutis ajaja keenya irraa laafaatti isa ajajuuf jirra.”');
INSERT INTO "apaguna" VALUES(18,89,'18:89','Sana booda karaa hordofe.');
INSERT INTO "apaguna" VALUES(18,90,'18:90','Yeroma iddoo baha biiftuu ga’e, isheedhaa ummata ishee irraa golgaa Nuti isaaniif hin taasisin irratti baatu arge.');
INSERT INTO "apaguna" VALUES(18,91,'18:91',')Dubbichi) akka kanaWantoota isa bira jiran dhugumatti, beekumsaan marsineerra.');
INSERT INTO "apaguna" VALUES(18,92,'18:92','Ergasii karaa (kaabaa) hordofe.');
INSERT INTO "apaguna" VALUES(18,93,'18:93','Yeroma gidduu gaara lamaanii ga’e, isaan lamaanii asitti ummata haasa’a beekuutti hin dhihaanne arge.');
INSERT INTO "apaguna" VALUES(18,94,'18:94',')Isaanis) “Yaa Zalqarnayn! Dhugumatti, Ya’ajuujiifi Ma’ajuuj dachii keessatti badii hojjatoodhaNuufi isaan giddutti cufaa godhuu irratti baasii siif goonuu?” jedhan');
INSERT INTO "apaguna" VALUES(18,95,'18:95',')Innis) ni jedhe: “Waan Gooftaan kiyya isa keessatti naaf mijeessetu caalaGidduu keessaniifi isaaniitti cufaa nan godhaa, humnaan na gargaaraa.');
INSERT INTO "apaguna" VALUES(18,96,'18:96','Muraa Sibiilaa naaf kennaa.” Yeroo iddoo gaara lamaan gidduu jiru guutu “afuufaa” jedheYeroo ibidda (diimaa) isa godhus “sibiila baqe isa irrattin dhangalaasa naa fidaa” jedhe');
INSERT INTO "apaguna" VALUES(18,97,'18:97',')Ya’ajuujifi Ma’ajuuj) isa irra koruu hin dandeenye; jalaanis isa uruu hin dandeenye.');
INSERT INTO "apaguna" VALUES(18,98,'18:98',')Zulqarnayn) ni jedhe: “Kun rahmata Gooftaa kiyya irraa ta’eYeroo waadaan Gooftaa kiyyaa dhufe, Inni bittinnaa’aa isa godhaWaadaan Gooftaa kiyyaa dhugaa ta’eera.”');
INSERT INTO "apaguna" VALUES(18,99,'18:99','Guyyaa (Ya’ajuujifi Ma’ajuuj dhufan) san garii isaanii garii keessa kan dhangala’u taasifnee dhiifnaGarriinis (xurumbaanis) ni afuufamaa (uumama hunda) guutumatti walitti isaan qabna.');
INSERT INTO "apaguna" VALUES(18,100,'18:100','Guyyaa san jahannamiin kaafirootaaf dhiyeessuu dhiheessina.');
INSERT INTO "apaguna" VALUES(18,101,'18:101','(Isaan) warra ijji isaanii yaadannoo kiyya (Qur’aana) irraa haguuggaa keessa turanii, (isa) dhaga’uu hin dandeenyeedha.');
INSERT INTO "apaguna" VALUES(18,102,'18:102','Sila warri kafaran gabroota kiyya Anaa gaditti gabbaramtoota godhachuu yaaduu? Dhugumatti, Nuti jahannamiin kaafirootaf iddoo simannaatiif qopheessineerra.');
INSERT INTO "apaguna" VALUES(18,103,'18:103','”Sila warra hojiin isaanii akkaan honga’oo ta’an isiniif himnuu?” jedhi.');
INSERT INTO "apaguna" VALUES(18,104,'18:104','Isaan warra odoo dalagaa tolchinee hojjannaa yaadanuu adunyaa keessatti hojiin isaanii duraa badeedha.');
INSERT INTO "apaguna" VALUES(18,105,'18:105','Isaan sun warra keeyyattoota Gooftaa isaanitiifi wal qunnamtii Isaatti kafaranii dalagaan isaanii baddeedhaKanaafuu Guyyaa Qiyaamaa madaala isaaniif hin dhaabnu.');
INSERT INTO "apaguna" VALUES(18,106,'18:106','Isaan sun waan kafaranii keeyyattoota kiyyaafi ergamtoota kiyyas qishnaa godhataniif galanni isaanii jahannami.');
INSERT INTO "apaguna" VALUES(18,107,'18:107','Dhugumatti, isaan amananii, gaggaarii hojjatan, Jannanni Firdaws iddoo simannaa isaaniif taatee jirti.');
INSERT INTO "apaguna" VALUES(18,108,'18:108','Ishee keessa yeroo hunda jiraatuIshee irraa jijjiiramuu hin barbaadan.');
INSERT INTO "apaguna" VALUES(18,109,'18:109','Jedhi: “Odoo galaanni qalama jechoonni Gooftaa kiyyaa (ittiin barraa’u) ta’ee, silaa odoo jechoonni Gooftaa kiyyaa hin dhumne galaanni dhuma ture; odoma (galaanni biraa) kan isa fakkaatu gargaarsaan finneeyyuu (galaanatu dhuma ture).”');
INSERT INTO "apaguna" VALUES(18,110,'18:110','Jedhi: “Ani namuma akka keessanii kan haqaan gabbaramaan keessan gabaramaa tokkicha qofa ta’uu wahyiin gara kiyyatti godhamuudhaKanaaf namni Gooftaa isaatiin wal ga’uu sodaatu (kajeelu) dalagaa gaarii haa hojjatu; Gooftaa isaa gabbaruu keessattis eenyunillee hin qindeessin.”');
INSERT INTO "apaguna" VALUES(19,1,'19:1','Kaaf Haa Yaa Ayn Saade');
INSERT INTO "apaguna" VALUES(19,2,'19:2','(Kun) rahmata gooftaan kee Gabricha Isaa Zakariyyaaf (godhe) dubbachuudha.');
INSERT INTO "apaguna" VALUES(19,3,'19:3','Yeroo inni Rabbii Isaa waamicha dhoksaa waame (dubbadhu)');
INSERT INTO "apaguna" VALUES(19,4,'19:4','“Rabbii kiyya! Ani lafeen kiyya laafte; mataan kiyyas Arrii makatee; Rabbii kiyya ani kadhaa keetiin hoonga’aa hin taane” jedhe.');
INSERT INTO "apaguna" VALUES(19,5,'19:5','“Ani booda kootii firoota kiyya sodaadheen jiraNiitiin kiyyas maseentuudhaSuma biraa ilma naaf kenni.');
INSERT INTO "apaguna" VALUES(19,6,'19:6','“Kan ana (amantii kiyya) dhaaluufi kan sanyii Ya’aquub irraas dhaaluYaa Gooftaa kiyya! Jaallatamaas isa taasisi.”');
INSERT INTO "apaguna" VALUES(19,7,'19:7','(Rabbiin ni jedhe): “Yaa Zakariyyaa! Dhugumatti, Nuti ilma maqaan isaa Yahyaa ta’e, kan kana dura maqaa isaa eenyuufillee hin taasisiniin si gammachiifna.”');
INSERT INTO "apaguna" VALUES(19,8,'19:8','(Zakariyyaan) ni jedhe: “(Yaa) Rabbii kiyya! Odoo haati manaa koo maseena taatee jirtuu, anis dulluma irraa hoonga ga’ee jiruu akkamitti ilmi naaf ta’uu danda?”');
INSERT INTO "apaguna" VALUES(19,9,'19:9','Ni jedhe: “Dhimmichi akkuma kanaRabbiin kee ‘Kun anaaf laafaadhaDhugumatti, ani kana dura si’ii homaa hin turin si uumeen jira’ jedhe.”');
INSERT INTO "apaguna" VALUES(19,10,'19:10','(Zakariyyaanis) ni jedhe: “(Yaa) Rabbii kiyya! Mallattoo wahii naaf godhi.” (Rabbiinis) ni jedhe: “Mallattoon kee halkan sadihiif odoo fayyaa qabduu (Zikrii malee) nama dubbisuu dhabuudha.”');
INSERT INTO "apaguna" VALUES(19,11,'19:11','Ergasii iddoo sagadaa keessaa ummata isaa irratti gadi ba’ee, ganamaafi galgala Rabbiin qulqulleessuu mallattoon itti beeksise');
INSERT INTO "apaguna" VALUES(19,12,'19:12','“Yaa Yahyaa! Kitaabicha ciminaan qabadhu” (jenneen)Beekumsas ijoollummaan isaaf kennine.');
INSERT INTO "apaguna" VALUES(19,13,'19:13','Mararfannaa Nu biraa ta’eefi qulqullinas (isaaf kenninee jirra)Innis (Rabbiin) sodaataa ta’eera.');
INSERT INTO "apaguna" VALUES(19,14,'19:14','Haadhaafi abbaa isaatiifis tola oolaa ta’eeraOf tuulaa, didaas hin taane');
INSERT INTO "apaguna" VALUES(19,15,'19:15','Nageenyis guyyaa inni dhalate, guyyaa inni du’uufi guyyaa inni lubbuun kaafamus isa irra haa jiraatu.');
INSERT INTO "apaguna" VALUES(19,16,'19:16','Kitaabicha (Qur’aana) keessatti Maryamiinis yeroo maatii ishee irraa iddoo gara bahaatti achi baate dubbadhu');
INSERT INTO "apaguna" VALUES(19,17,'19:17','Isaan irraa haguuggaa godhattee, ergamaa keenya (Jibriiliin) gara isiitti eregineInnis namuma (uumaan isaa) guutuu isiitti fakkaate.');
INSERT INTO "apaguna" VALUES(19,18,'19:18','Ni jette: “Yoo kan (Rabbiin) sodaattu taateef dhugumatti, ani Rahmaaniinin si irraa tikfama.”');
INSERT INTO "apaguna" VALUES(19,19,'19:19','(Jibriilis): “Ani ergamaa Rabbii keetiiti; akkan ilma qulqulluu siif kennuuf (Rabbiin na erge)” jedheen');
INSERT INTO "apaguna" VALUES(19,20,'19:20','Ni jette: “Odoo namni tokko ana hin tuqin; sagaagaltuus odoon hin ta’in akkamitti ilmi naaf ta’uu danda’a?”');
INSERT INTO "apaguna" VALUES(19,21,'19:21','Innis ni jedhe: “Dhimmichi akkuma kanaGooftaan keetis ‘kuni natti laafaadha; Namootaafis raajiifi rahmata Nu irraa ta’es akka isa taasifnuufi” jedhe(Kun) dhimma dura murtaa’e ta’eera');
INSERT INTO "apaguna" VALUES(19,22,'19:22','Yoosuu isa ulfoofte; kophaa ishee gara bakka fagoo isaan deemte.');
INSERT INTO "apaguna" VALUES(19,23,'19:23','Ciniinsuun gara gufuu timiraa ishee maqseNi jette: “Yaa badii kiyya! Odoo kana dura du’ee, wan dagatamaa ta’ee maal qaba!”');
INSERT INTO "apaguna" VALUES(19,24,'19:24','“Hin gaddin; dhugumatti, Gooftaan kee jala keetti laga xiqqoo taasiseera” jechuun ishee jalaa isheetti lallabe.');
INSERT INTO "apaguna" VALUES(19,25,'19:25','“Gufuu timiraa gara keetti hurgufi; asheeta timira bilchaataa ta’e sirratti harcaafti');
INSERT INTO "apaguna" VALUES(19,26,'19:26','Nyaadhu, dhugii; ijaanis itti gammadiYoo nama tokko argites ‘Dhugumatti, ani Rahmaaniif eenyullee dubbisuu dhabuu qodhaa (nazrii) galeen jiraKanaaf hardha nama tokkos hin dubbisu” jedhi.');
INSERT INTO "apaguna" VALUES(19,27,'19:27','Ergasii isa baadhattee gara ummata ishee dhufte[Isaan] ni jedhan: “Yaa Maryam! Dhugumatti ati waan akkaan jabaatu fidde.');
INSERT INTO "apaguna" VALUES(19,28,'19:28','Yaa obboleettii Haaruun! Abbaan kee nama badaa hin turre; haati keetis sagaagaltuu hin turre.”');
INSERT INTO "apaguna" VALUES(19,29,'19:29','(Isiinis) gara isaatti akeekte[Isaan]: “Akkamitti daa’ima siree daa’imummaa keessa jiru dubbifna?” jedhan.');
INSERT INTO "apaguna" VALUES(19,30,'19:30','Innis (daa’imichis) ni jedhe: “Dhugumatti, ani gabricha RabbiitiInni kitaaba naaf kenneera; nabiyyiis na taasisee jira.');
INSERT INTO "apaguna" VALUES(19,31,'19:31','Iddoo ani jiru hundattis barakeeffamaa na taasisee jira; salaataafi zakaas hangan lubbuun jiru naaf dhaamee jira.');
INSERT INTO "apaguna" VALUES(19,32,'19:32','Haadha kiyyaafis tola oolaa (na godhe); of tuulaa hoonga’aas, ana hin taasifne.');
INSERT INTO "apaguna" VALUES(19,33,'19:33','Guyyaa ani dhaladhe, guyyaa ani du’uufi guyyaa ani kaafamuus nageenyi ana irra jira.”');
INSERT INTO "apaguna" VALUES(19,34,'19:34','Inni sun Iisaa ilma Maryami; inni jecha dhugaa kan isaan isa keessatti shakkan saniidha.');
INSERT INTO "apaguna" VALUES(19,35,'19:35','Rabbiif ilma taasifachuun hin maluInni qulqullaa’eYeroo dhimma tokko murteesse, wanti Inni jedhu “ta’i” jechuu qofa; innis yoosuu ta’a');
INSERT INTO "apaguna" VALUES(19,36,'19:36','(Iisaan) “Dhugumatti, Rabbiin Gooftaa kiyyaafi Gooftaa keessaniKanaaf Isa qofa gabbaraaSantu karaa qajeelaadha” jedhe.');
INSERT INTO "apaguna" VALUES(19,37,'19:37','Gareewwan (yahuudaafi nasaaraa) gidduu isaaniitti (waa’ee Iisaa irratti) wal dhaban; wal gayii guyyaa guddaa (Qiyaamaa) san keessatti warra kafaraniif adabbiin mirkanaa’eera!');
INSERT INTO "apaguna" VALUES(19,38,'19:38','(Kaafiroonni) guyyaa isaan Nutti dhufan, maaltu akkaan dhaga’oo argoo isaan taasise! Garuu hardha miidhaa raawwattoonni jallina ifaa ta’e keessa jiru.');
INSERT INTO "apaguna" VALUES(19,39,'19:39','Haala isaan daguu keessatti ta’aniin odoo isaan hin amanin yeroo dhimmichi murtaa’u, Guyyaa Gaabbii san isaan sodaachisi');
INSERT INTO "apaguna" VALUES(19,40,'19:40','Dhugumatti, Nuti Numatu dachiifi waan ishee irra jiru dhaala; garuma keenya deebifamus.');
INSERT INTO "apaguna" VALUES(19,41,'19:41','Kitaaba keessatti Ibraahiimis dubbadhuDhugumatti, inni nama akkaan dhugaa dubbataa, nabiyyii ture');
INSERT INTO "apaguna" VALUES(19,42,'19:42','Yeroo inni abbaa isaatiin “Yaa abbaa kiyya! Maaliif waan hin dhageenye, hin argineefi homaa sirraa deebisuu hin dandeenye gabbarta?” jedhe (dubbadhu)');
INSERT INTO "apaguna" VALUES(19,43,'19:43','“Yaa abbaa koo! Dhugumatti, ani beekumsa irraa wanti sitti hin dhufin natti dhufee jiraKanaafuu na hordofi; ani karaa sirraawaan si qajeelcha');
INSERT INTO "apaguna" VALUES(19,44,'19:44','Yaa abbaa koo! Sheyxaana hin gabbarin; dhugumatti sheyxaanni Rahmaan didaa ta’eera');
INSERT INTO "apaguna" VALUES(19,45,'19:45','Yaa abbaa koo! Dhugumatti, ani Rahmaan irraa adabbiin si tuqee, atis sheyxaanaaf jaalallee ta’uu keen sodaadha.”');
INSERT INTO "apaguna" VALUES(19,46,'19:46','(Abbaan isaas) “Yaa Ibraahiim! Ati gabbaramtoota kiyya jibbitaa? Yoo hin dhorgamne, dhugumatti ani dhagaaniin si tuma (si cafaqa)Yeroo dheeraaf ana irraa fagaadhu” jedhe');
INSERT INTO "apaguna" VALUES(19,47,'19:47','(Ibraahiimis) “Nageenyi sirra haa jiraatu! Ani Rabbii kiyyan araarama siif kadhaDhugumatti, Inni akkaan rahmata naaf godhaa ta’eera” jedhe');
INSERT INTO "apaguna" VALUES(19,48,'19:48','“Isiniifi wantoota isin Rabbiin alatti gabbartan irraan fagaadhaGooftaa kiyya qofan gabbarasGooftaa kiyya gabbaruu kootiin hoonga’aa ta’uu dhabuu koon abdadha.”');
INSERT INTO "apaguna" VALUES(19,49,'19:49','Yeroma inni isaaniifi waan isaan Rabbii gaditti gabbaran irraa fagaatu, Isihaaqiifi Ya’aquubiin isaaf kennineHunda isaaniis nabiyyii taasifne');
INSERT INTO "apaguna" VALUES(19,50,'19:50','Rahmata keenya irraas isaaniif kennineGaariin dubbatamuu (faaruu) ol fuudhamaas isaaniif goone.');
INSERT INTO "apaguna" VALUES(19,51,'19:51','Kitaaba (Qur’aana) keessatti (waa’ee) Muusaa dubbadhuDhugumatti, inni filatamaa ta’ee jira; ergamaa, nabiyyis ta’eera');
INSERT INTO "apaguna" VALUES(19,52,'19:52','Nutis moggaa mirga (gaara) Xuur irraa isatti lallabnee, maqoo itti haasa’uunis (of itti) isa dhiheessine!');
INSERT INTO "apaguna" VALUES(19,53,'19:53','Tola keenya irraas obboleessa isaa Haaruuniin nabiyyii (taasifnee) isaaf kennine.');
INSERT INTO "apaguna" VALUES(19,54,'19:54','Kitaaba (Qur’aana) keessatti (waa’ee) Ismaa’iilis dubbadhuDhugumatti, inni dhugoomsaa waadaa ta’ee jiraErgamaa, nabiyyiis ta’ee jira');
INSERT INTO "apaguna" VALUES(19,55,'19:55','Maatii (ummata) isaas salaatafi zakaatti kan ajaju tureGooftaa isaa birattis jaallatamaa ture.');
INSERT INTO "apaguna" VALUES(19,56,'19:56','Kitaaba keessatti Idriisiinis dubbadhuDhugumatti, inni dhugaa dubbataa, nabiyyii ture.');
INSERT INTO "apaguna" VALUES(19,57,'19:57','Iddoo ol aanaattis ol isa fuunee jirra.');
INSERT INTO "apaguna" VALUES(19,58,'19:58','Isaan sun warra Rabbiin isaan irratti tola oole; nabiyyoota ilmaan Aadam irraa ta’anii, warra Nuuhii wajjiin (doonii irratti) baadhanne irraas ta’anii, ilmaan Ibraahiimiifi Israa’iil irraas ta’anii, ammas warra Nuti qajeelchinee filanne irraas kan ta’aniidhaKan yeroo keeyyattoonni Rahmaan isaan irratti dubbifamu sujuudaafi booyaa kufaniidha.');
INSERT INTO "apaguna" VALUES(19,59,'19:59','Isaan boodaas bakka bu’oota (gadhee) salaata miidhaniifi fedha lubbuu isaanii hordofantu hafeHallayyaa jahannam quunnamuuf jiru.');
INSERT INTO "apaguna" VALUES(19,60,'19:60','Garuu Namni tawbatee, amanee, gaarii hojjate, Warri sun jannata seenu; homaas hin miidhamanu.');
INSERT INTO "apaguna" VALUES(19,61,'19:61','Jannata qubsumaa ishee Rahmaan gabroota Isaatiif fagootti waadaa gale (seenu)Dhugumatti, Inni waadaan Isaa dhufamaa ta’eera');
INSERT INTO "apaguna" VALUES(19,62,'19:62','Nagaa (wal gaafachuu) malee ishee keessatti haasa’a bu’a dhabeessa hin dhagayanIshee keessas isaaniif sooranni isaanii ganamaafi galgala jira.');
INSERT INTO "apaguna" VALUES(19,63,'19:63','Jannatni sun ishee gabroota keenya irraa namoota Rabbiin sodaatan dhaalchifnuudha.');
INSERT INTO "apaguna" VALUES(19,64,'19:64','Nuti (maleykonni) ajaja Gooftaa keetiitiin ala hin buunuWanti fuuldura keenyaa, wanti duuba keenyaafi wanti gidduu sana jirus kan IsaatiGooftaan keetis dagataa hin taane');
INSERT INTO "apaguna" VALUES(19,65,'19:65','(Inni) Gooftaa samii, dachiifi wantoota gidduu isaan lamaan jiran hundaatiKanaafuu Isa (qofa) gabbari; Isa gabbaruu irrattis obsa qabaadhuSila Isaaf moggoo (fakkaataa) wahii ni beektaa?');
INSERT INTO "apaguna" VALUES(19,66,'19:66','Namni “Sila ani ergan du’ee lubbuun jiraadhee (qabrii) baafamaa?” jedha.');
INSERT INTO "apaguna" VALUES(19,67,'19:67','Sila namni akka Nuti kanaan dura dhabama irraa isa uumne hin yaadatuu?');
INSERT INTO "apaguna" VALUES(19,68,'19:68','Gooftaa keetiin kakannee! Dhugumatti, isaaniifi sheyxaanota walitti qabneeti, ergasii moggaa jahannamitti jilbeenfatoo taasifnee isaan fidna');
INSERT INTO "apaguna" VALUES(19,69,'19:69','Sana booda garee hunda irraa isaan Rahmaan irratti jallinatti irra cimoo ta’an ni baafna.');
INSERT INTO "apaguna" VALUES(19,70,'19:70','Ergasii, dhugumatti, Nuti (isaan irraa) warra ishee (Ibidda) seenuu qaban akkaan beekna.');
INSERT INTO "apaguna" VALUES(19,71,'19:71','Isin irraa eenyulleen ishee seenu malee hin hafuDirqama Rabbii keetiin murteeffameedha.');
INSERT INTO "apaguna" VALUES(19,72,'19:72','Sana booda warra Rabbiin sodaatan nagaa baafna; warra miidhaa hojjatan immoo ishee keessatti jilbiiffatoo taasifnee (isaan) dhiifna.');
INSERT INTO "apaguna" VALUES(19,73,'19:73','Yeroo keeyyattoonni keenya ifa ta’anii isaan irratti dubbifamanis, kaafiroonni mu’uminootaan “Garee lamaan keessaa isa kamtu caalmaa sadarkaatiifi miidhagina iddoo waltajjii qaba?” jedhu.');
INSERT INTO "apaguna" VALUES(19,74,'19:74','Isaaniin duras dhaloota heddu kan meeshaa mana keessaatiifi argaa ijaattis isaan caalaa babbareedoo ta’anis balleessinee jirra?');
INSERT INTO "apaguna" VALUES(19,75,'19:75','Jedhi (Yaa Muhammad!-SAW): “Nama jallina keessa jiru Rahmaan (jallina inni filate) akkaan isaaf haa dabalu; yeroo waan waadaa isaaniif galame, adabbii yookiin Qiyaamaa argan, nama iddoon isaa irra badaafi raayyaa waraanaatti irra dadhabaa ta’e fuulduratti ni beeku.');
INSERT INTO "apaguna" VALUES(19,76,'19:76','Rabbiinis warra qajeelaniif qajeeluma dabalaHojiiwwan gaggaarii yeroo dheeraaf turantu Rabbii kee biratti mindaa ta’uun caalaadha; deebiidhaanis isatu (irra) caalaadha');
INSERT INTO "apaguna" VALUES(19,77,'19:77','Sila isa keeyyattoota keenyatti kafaree “Ani dhugumatti, qabeenyaafi ilmaan kennamuufin jira” jedhe naaf himi?');
INSERT INTO "apaguna" VALUES(19,78,'19:78','Sila (iccitii) fagoo irratti mul’atemoo Rahmaan biratti waadaa wahii godhate?');
INSERT INTO "apaguna" VALUES(19,79,'19:79','Lakki; (kun hin ta’u) Nuti waan inni jedhus ni galmeessina; adabbii irraas dabaltii (isaaf malu) isaaf daballa.');
INSERT INTO "apaguna" VALUES(19,80,'19:80','Waan inni (qaba) jedhu (hunda) irraa dhaalla; innis kophaa isaa Nutti dhufa');
INSERT INTO "apaguna" VALUES(19,81,'19:81','Akka humnaafi kabajaa isaaniif ta’aniif jecha Rabbiin alatti gabbaramoo godhatan');
INSERT INTO "apaguna" VALUES(19,82,'19:82','Lakkisaa; (kun waan ta’uu miti) isaan fuulduratti gabbaruu isaanii ni mormu; isaan irrattis faallaa ta’anii dhiaatu.');
INSERT INTO "apaguna" VALUES(19,83,'19:83','Sila akka Nuti kaafiroota irratti sheyxaanota erginee (badii irratti) kakaasanii dhiibaa turan hin arginee?');
INSERT INTO "apaguna" VALUES(19,84,'19:84','Isaan (adabamuu) irratti hin jerjerin; Nuti lakkoofsa (guyyootaa) isaaniif lakkaayaa jirra.');
INSERT INTO "apaguna" VALUES(19,85,'19:85','Guyyaa warra Rabbiin sodaatan gara Rahmaanitti garee kabajamoo taasifnee isaan walitti qabnu [yaadadhu].');
INSERT INTO "apaguna" VALUES(19,86,'19:86','Badii raawwattoota immoo dheebotoo taasifnee gara Jahannam oofna');
INSERT INTO "apaguna" VALUES(19,87,'19:87','Nama Rahmaan biraa waadaa qabu malee araarsaa argachuu hin danda’anu');
INSERT INTO "apaguna" VALUES(19,88,'19:88','“Rahmaan ilma godhate” jedhu.');
INSERT INTO "apaguna" VALUES(19,89,'19:89','Dhugumatti, isin waan fokkataa fiddaniirtu.');
INSERT INTO "apaguna" VALUES(19,90,'19:90','(Kana) irraa samiin babbaqaquu, dachiinis dhodhooyuufi gaarreenis caccabanii kufuuf dhiaatu (fiddan).');
INSERT INTO "apaguna" VALUES(19,91,'19:91','Rahmaaniif ilma moggaasuu irraa (babbaqaqutti dhiaatu)');
INSERT INTO "apaguna" VALUES(19,92,'19:92','Rahmaan ilma godhachuun isaaf hin malu');
INSERT INTO "apaguna" VALUES(19,93,'19:93','Wanti samiifi dachii keessa jiran hundi gabra (Isaa) qofa ta’anii gara Rahmaan dhufanu malee hin hafan');
INSERT INTO "apaguna" VALUES(19,94,'19:94','Dhugumatti, Inni tokko tokkoon isaan beeka; lakkoofsa guutuu ta’es isaan lakkaa’eera');
INSERT INTO "apaguna" VALUES(19,95,'19:95','Hundi isaaniis Guyyaa Qiyaamaa kophaa isaanii gara Isaatti (Rahmaanitti) dhufu.');
INSERT INTO "apaguna" VALUES(19,96,'19:96','Dhugumatti, isaan amananii, gaggaarii hojjatan, Rahmaan fuulduratti jaalala isaaniif taasisa');
INSERT INTO "apaguna" VALUES(19,97,'19:97','Wanti Nuti arraba kee irratti isa (Qur’aana kana) sii laaffifneef akka warra Rabbiin sodaatan ittiin gammachiiftee, ummata akkaan mormitoota ta’an immoo ittiin sodaachiftuufi');
INSERT INTO "apaguna" VALUES(19,98,'19:98','Isaan duras dhaloota irraa meeqaatama balleessinee jirra! Sila isaan irraa tokkos argitaa? Yookiin hasaasa (qilixii) isaaniif ni dhageessaa?');
INSERT INTO "apaguna" VALUES(20,1,'20:1','Xaa haa.');
INSERT INTO "apaguna" VALUES(20,2,'20:2','(Yaa Muhammad!) Nuti Qur’aana akka ati rakkattuuf sirratti hin buufne');
INSERT INTO "apaguna" VALUES(20,3,'20:3','Nama Rabbiin sodaatu yaadachiisuuf malee');
INSERT INTO "apaguna" VALUES(20,4,'20:4','Isa dachiifi samii ol fuudhamoo uume irraa buufamaadha.');
INSERT INTO "apaguna" VALUES(20,5,'20:5','Rahmaan Arshii irratti (ol ta’iinsa Isaaf malu) ol ta’eera.');
INSERT INTO "apaguna" VALUES(20,6,'20:6','Wanti samii, dachiifi gidduu isaan lamaanii jiruufi wanti biyyee jala jiru hundi kan Isaati');
INSERT INTO "apaguna" VALUES(20,7,'20:7','Jecha yoo ol fuutes, dhugumatti Inni (Rabbiin) iccitiifi waan isa caalaa dhokates ni beeka.');
INSERT INTO "apaguna" VALUES(20,8,'20:8','Rabbiin dhugaan gabbaramaan isa malee hin jiruMaqoonni babbareedoon kan Isaa (qofa)');
INSERT INTO "apaguna" VALUES(20,9,'20:9','Sila oduun Muusaa sitti dhufeeraa?');
INSERT INTO "apaguna" VALUES(20,10,'20:10','Yeroo Ibidda argu maatii isaatin “as taa’aa ani ibiddaan argee, waan irraa qabsiifatanin isinii fida yookiin ibidda biraa nama (karaa irratti) na qajeelchu argachuun kajeelaa” jedhe');
INSERT INTO "apaguna" VALUES(20,11,'20:11','Yeroma gara ishee dhufu “Yaa Muusaa!” (jechuun) itti lallabamee');
INSERT INTO "apaguna" VALUES(20,12,'20:12','“Dhugumatti, Ani Gooftaa keeti; kophee kee baafadhu, ati laga qulqulleeffamaa, Xuwaa keessa jirtaa.');
INSERT INTO "apaguna" VALUES(20,13,'20:13','Ani si filadheeraa waan buufamu dhageeffadhu.');
INSERT INTO "apaguna" VALUES(20,14,'20:14','Dhugumatti, Ani Allaahdha; Ana malee dhugaan gabbaramaan hin jiru! Ana gabbariYaadannoo kiyyaaf salaatas seeraan salaati.');
INSERT INTO "apaguna" VALUES(20,15,'20:15','Dhugumatti, Qiyaamaan dhufaa jirtii akka lubbuun hundi waan hojjatte mindeeffamtuuf jecha (of irraa) ishee dhoksuuttin dhihaadha.');
INSERT INTO "apaguna" VALUES(20,16,'20:16','Namni isheetti hin amanin, kan fedhii isaa hordofe ishee irraa gara si hin galchin! (Yoo isa hordofte) ni badda.”');
INSERT INTO "apaguna" VALUES(20,17,'20:17','Yaa Muusaa! Wanti harka mirgaa kee keessa jiru sun maali?”');
INSERT INTO "apaguna" VALUES(20,18,'20:18','(Muusaan) ni jedhe: “Isheen ulee kiyya; ishee irrattin hirkadha; Re’ee kiyyaafis isheenin (baala mukaa) harcaasaIshee keessa anaaf dhimmi biraas ni jira.”');
INSERT INTO "apaguna" VALUES(20,19,'20:19','(Rabbiin) ni jedhe: “yaa Muusaa! Ishee darbi.”');
INSERT INTO "apaguna" VALUES(20,20,'20:20','Ishee ni darbeYeroma san bofa fiigu taate.');
INSERT INTO "apaguna" VALUES(20,21,'20:21','(Rabbiin) ni jedhe: “Ishee qabi; hin sodaatinis; haala ishee kan jalqabaatti ishee deebifnaa.');
INSERT INTO "apaguna" VALUES(20,22,'20:22','Harka kee gara cinaacha keetiitti maxxansiMallattoo biraa ta’uun dhibee (tokko) malee adii taatee baati.”');
INSERT INTO "apaguna" VALUES(20,23,'20:23','Mallattoolee keenya isaan gugurdoo irraa akka si agarsiifnuuf.');
INSERT INTO "apaguna" VALUES(20,24,'20:24','Gara Fir’awnaa deemiDhugumatti, inni daangaa dabree jira.”');
INSERT INTO "apaguna" VALUES(20,25,'20:25','(Muusaan) ni jedhe: “Yaa Gooftaa kiyya! Qoma kiyya naaf bal’isi.');
INSERT INTO "apaguna" VALUES(20,26,'20:26','(Hojii) kiyyaas naaf laaffisi.');
INSERT INTO "apaguna" VALUES(20,27,'20:27','Hidhaas (Cabaqas) arraba kiyya irraa hiiki.');
INSERT INTO "apaguna" VALUES(20,28,'20:28','Jecha kiyya haa hubatanii.');
INSERT INTO "apaguna" VALUES(20,29,'20:29','Maatii kiyya irraas gargaaraa naaf godhi.');
INSERT INTO "apaguna" VALUES(20,30,'20:30','Haaruunuma obboleessa kiyya.');
INSERT INTO "apaguna" VALUES(20,31,'20:31','Isaan irree kiyya cimsi.');
INSERT INTO "apaguna" VALUES(20,32,'20:32','Dhimma kiyya keessattis isa hirmaachisi.');
INSERT INTO "apaguna" VALUES(20,33,'20:33','Akka baay’ee si faarsinuuf.');
INSERT INTO "apaguna" VALUES(20,34,'20:34','Akka baay’inaanis si yaadannuuf.');
INSERT INTO "apaguna" VALUES(20,35,'20:35','Dhugumatti, Ati nu argaa taateetta.”');
INSERT INTO "apaguna" VALUES(20,36,'20:36','(Rabbiinis) ni jedhe: “Yaa Muusaa! Dhugumatti, kadhaa kee kennamtee jirta.');
INSERT INTO "apaguna" VALUES(20,37,'20:37','Dhugumatti, yeroo biraas si qanansiifnee turre.');
INSERT INTO "apaguna" VALUES(20,38,'20:38','Yeroo waan beeksifamu gara harmee keetii (wahyii) buufne.');
INSERT INTO "apaguna" VALUES(20,39,'20:39','Sanduuqa keessa isa (daa’ima) kaayi; laga keessatti isa darbi; Lagichi moggaatti isa darbeeti diinni kiyyaafi diinni isaas isa fudhataJaalala Ana biraa ta’es si irratti darbeera; tika kiyya irratti akka guddattuuf.');
INSERT INTO "apaguna" VALUES(20,40,'20:40','(Yaa Muusaa!) Yeroo obboleettin kee deemtee ‘Nama isa guddisu irratti isin qajeelchuu?’ jetteen [yaadadhu]Akka ijji ishee tasgabbooftuufi isiin hin gaddineef jecha gara haadha keetiitti si deebifneLubbuus ajjeeftee turtee yaaddoo guddaa jalaa si baraarree mokkora cimaas si mokkorree jirraErgasii waggaa baay’ee warra Madyan keessa turtee jirtaSana booda yeroo Ani siif murteessetti (bakka kana) dhuftee jirta; yaa Muusaa!');
INSERT INTO "apaguna" VALUES(20,41,'20:41','Anis ofiif si filadheera.');
INSERT INTO "apaguna" VALUES(20,42,'20:42','Atiifi obboleessi keetis mallattoolee kiyyaan deemaa; zikrii kiyya keessattis isin lachanuu hin laafinaa.');
INSERT INTO "apaguna" VALUES(20,43,'20:43','Isin lamaanuu gara Fir’awni deemaa; dhugumatti, inni daangaa dabree jira.');
INSERT INTO "apaguna" VALUES(20,44,'20:44','Akka gorfamuuf yookiin akka sodaachuu sodaatuuf jecha, jecha laafaa (gaarii) isaan jedhaa.”');
INSERT INTO "apaguna" VALUES(20,45,'20:45','Isaan lamaan ni jedhan: “Gooftaa keenya! Dhugumatti, nuti adabbii cimaa nu irratti raawwata; yookiin (nurrattis) daangaa darbuu sodaanna.”');
INSERT INTO "apaguna" VALUES(20,46,'20:46','(Rabbiin) ni jedhe: “Hin sodaatinaaDhugumatti, Ani isin lamaan wajjinin jira; nan dhaga’a; nan argas.');
INSERT INTO "apaguna" VALUES(20,47,'20:47','Isin lachanuu isa bira dhaqaa: ‘Dhugumatti, nuti ergamtoota Gooftaa keetiiti; ilmaan Israa’iil nu waliin ergi; isaan hin adabinis; dhugumatti, nuti mallattoon Gooftaa kee biraa sitti dhufneNageenyis nama qajeelfama (Rabbii) hordofe irratti’ jedhaanii.');
INSERT INTO "apaguna" VALUES(20,48,'20:48','“Dhugumatti, adabbiin nama sobsiisee garagale irratti ta’uun gara keenyatti wahyiin godhameera.”');
INSERT INTO "apaguna" VALUES(20,49,'20:49','(Fir’awn): “Yaa Muusaa! Gooftaan keessan eenyu?” jedhe');
INSERT INTO "apaguna" VALUES(20,50,'20:50','(Muusaan) ni jedhe: “Gooftaan keenya Isa (waan hunda) akkaataa uumama isaa isaaf kennee sana booda isa qajeelcheedha.”');
INSERT INTO "apaguna" VALUES(20,51,'20:51','(Fir’awn) ni jedhe: “Haalli ummata darbanii hoo akkam ta’a?”');
INSERT INTO "apaguna" VALUES(20,52,'20:52','(Muusaanis) ni jedhe: “Beekumsi ishee Gooftaa kiyya bira galmee keessa jiraGooftaan kiyya hin dogoggoru; hin irranfatus.”');
INSERT INTO "apaguna" VALUES(20,53,'20:53','(Inni) Isa dachii afata isiniif godhee, ishee keessatti karaalee isiniif laaffisee, samii irraa bishaan buuse saniIsaan (bokkaan) akaakuu biqiltuulee adda addaa irraa taate ni baafne.');
INSERT INTO "apaguna" VALUES(20,54,'20:54','(Isa irraa) nyaadhaa; beyladoota keessanis dheechisaaDhugumatti, kana keessa namoota sammuu qabaniif mallattotu jira.');
INSERT INTO "apaguna" VALUES(20,55,'20:55','Ishee (dachii) irraa isin uumne; ishee keessatti isin deebifna; yeroo biraas ishee irraa isin baafna.');
INSERT INTO "apaguna" VALUES(20,56,'20:56','Dhugumatti, mallattoolee keenya hunda ishee isatti (Fir’awnitti) argisiifneerra; innis ni sobsiisee; ni dides.');
INSERT INTO "apaguna" VALUES(20,57,'20:57','(Fir’awn) ni jedhe: “Yaa Muusaa! Sila ati dachii keenya irraa sihrii keetiin nu baasuuf dhuftee?');
INSERT INTO "apaguna" VALUES(20,58,'20:58','Nutis sihrii fakkaataa isaatiin siif dhufnaKanaaf gidduu keenyaafi gidduu keetti beellama nutiifi atis irraa hin hafne iddoo giddu galeessa ta’etti godhi.”');
INSERT INTO "apaguna" VALUES(20,59,'20:59','(Muusaanis) ni jedhe: “Beellamni keessan guyyaa ayyaanaati; namoonnis waaree dura walitti qabamuudha.”');
INSERT INTO "apaguna" VALUES(20,60,'20:60','Ergasii Fir’awni gara galee, deemee dhara isaa walitti qabee sana booda dhufe.');
INSERT INTO "apaguna" VALUES(20,61,'20:61','Muusaan isaaniin “Yaa badii keessan! Rabbi irratti soba hin odeessinaa; adabbiidhaan isin balleessaDhugumatti, namni (Rabbi irratti) soba odeesse hoonga’ee jira” jedhe');
INSERT INTO "apaguna" VALUES(20,62,'20:62','Dubbii isaanii irratti gidduu isaaniitti wal falman, maqoo isaaniis dhokfatan.');
INSERT INTO "apaguna" VALUES(20,63,'20:63','Ni jedhan: “Jarri lamaan kun warra falfalaati (sihriitii), falfala isaanitiin dachii keessan irraa isin baasanii karaa (shari’a) keessan ishee caaltuu taate dhabamsiisuu fedhu.');
INSERT INTO "apaguna" VALUES(20,64,'20:64','Kanaafuu tooftaa keessan walitti qabadhaa; sana booda hiriiraan koottaaDhugumatti, namni hardha irra aane (mo’ate) milkaa’eera.');
INSERT INTO "apaguna" VALUES(20,65,'20:65','Yaa Muusaa! Ati darbachuu yookiin jalqaba nama darbuu nuti ta’uu (filadhu)” jedhaniin');
INSERT INTO "apaguna" VALUES(20,66,'20:66','(Muusaanis) “Lakkisaa; isinummaan darbaa!” jedheBattalumatti haadni isaaniifi uleen isaaniis falfala isaanii irraa kan ka''e waan fiigdu itti fakkeeffama.');
INSERT INTO "apaguna" VALUES(20,67,'20:67','Muusaan lubbuu isaa keessatti sodaan itti dhaga’ame.');
INSERT INTO "apaguna" VALUES(20,68,'20:68','“Hin sodaatin! Dhugumatti, ati sumatu mooha” jenneen.');
INSERT INTO "apaguna" VALUES(20,69,'20:69','“Waan mirga kee keessa jiru darbi! Waan isaan dalagan ni liqimsitiWanti isaan dalagan (hundi) toftaa nama falfala hojjatuutiNamni falfala hojjatu bakkuma dhufettuu hin milkaa’u.”');
INSERT INTO "apaguna" VALUES(20,70,'20:70','Ergasii warri falfala hojjatan sujuuda bu’anii “Gooftaa Haaruniifi Muusaatti amanne” jedhan');
INSERT INTO "apaguna" VALUES(20,71,'20:71','(Fir’awn) ni jedhe: “Odoo ani isiniif hin hayyamin isatti amantuu? Dhugumatti, inni guddaa keessan kan falfala isin barsiiseedhaDhugumatti, harkaafi miila keessan wal dhabsiiseen kukkutaAmmas jirma muka timiraa irrattin isin fannisaIsinis (yoos) eenyu keenya (anamoo Gooftaa Muusaatu) adabbiin isaa irra cimaafi turaa akka ta’e beekuuf jirtu.”');
INSERT INTO "apaguna" VALUES(20,72,'20:72','Ni jedhan: “Waan nutti dhufe kan ragaawwan ifa bahan irraa ta’eefi Isa nu uume irra si hin filannuKanaafuu waan murteessitu murteessi! Wanti ati murteessitus jiruma addunyaa kana qofa.');
INSERT INTO "apaguna" VALUES(20,73,'20:73','“Akka Inni dogoggora keenyaafi falfala irraas waan ati isatti nu dirqisiifte nuuf araaramuuf, dhugumatti, nuti Gooftaa keenyatti amannee jirraRabbitu caalaafi hafaadha');
INSERT INTO "apaguna" VALUES(20,74,'20:74','Dhugumatti, namni badii hojjataa ta’ee gara Rabbii isaa dhufe, isaaf dhugumatti, jahannamtu jira; ishee keessatti hin du’u, hin jiraatus');
INSERT INTO "apaguna" VALUES(20,75,'20:75','Namni Mu’umina ta’ee, dhugumaan hojii gaarii hojjataa gara Isaa dhufes isaan sun isaaniif sadarkaalee ol ta’aatu jira.');
INSERT INTO "apaguna" VALUES(20,76,'20:76','Jannata qubsumaa kan jala ishee laggeen yaatutu (isaanif jira); ishee keessa yeroo hunda jiraatuKun immoo mindaa nama qulqulleeffateeti.');
INSERT INTO "apaguna" VALUES(20,77,'20:77','Dhugumatti, gara Muusaa “Gabroota kiyya qabadhuu halkaniin deemi! Isaaniifis galaana keessatti karaa gogaa taasisi; (Fir’awn) si dhaqqabuufi (bishaaniin nyaatamuu isaa) hin sodaattu” jechuun wahyii goonee jirra:');
INSERT INTO "apaguna" VALUES(20,78,'20:78','Fir’awnis hoomaa isaa waliin isaan hordofe; galaanicha irraas wanti isaan haguuge guutumatti isaan haguuge.');
INSERT INTO "apaguna" VALUES(20,79,'20:79','Fir’awni ummata isaa ni jallise malee (isaan) hin qajeelchine.');
INSERT INTO "apaguna" VALUES(20,80,'20:80','Yaa ilmaan Israa’iil! Dhugumatti Nuti diinota keessan jalaa isin baraarsinee moggaa mirga gaara Xuur irrattis waadaa isinii goonee, waan mi’aawaa damma fakkaatuufi foon allaattiis isin irratti buufnee jirra.');
INSERT INTO "apaguna" VALUES(20,81,'20:81','Wantoota gaggaarii Nuti isiniif kennine irraa nyaadhaa, isa keessattis daangaa hin dabrinaa; (yoo dabartan) dallansuu kiyyatu isin irratti bu’aNamni dallansuun kiyya isa irratti bu’e, dhugumatti, bade.');
INSERT INTO "apaguna" VALUES(20,82,'20:82','Dhugumatti, nama tawbatee, amanee, gaggaarii hojjatee, sana booda (hanga du’utti) karaa qajeelaa qabateef inni akkaan araaramaadha');
INSERT INTO "apaguna" VALUES(20,83,'20:83','“Ummata kee biraa maaltu si jarjarse? Yaa Muusaa!” (jedheen).');
INSERT INTO "apaguna" VALUES(20,84,'20:84','(Muusaan) ni jedhe: “Kuunnoo isaan faana kiyya irra jiruYaa Gooftaa kiyya! Akka Ati (na irraa) jaallattuufiin gara kee jarjare.”');
INSERT INTO "apaguna" VALUES(20,85,'20:85','(Rabbiinis) ni jedhe: “Nuti dhugumatti, ummata kee bakka ati hin jirretti mokkorree jirra; Saamiriyyichis isaan jallise.”');
INSERT INTO "apaguna" VALUES(20,86,'20:86','Muusaan gara ummata isaa, dallanaa, gaddaa deebi’ee “Yaa ummata kiyya! Sila Rabbiin keessan waadaa gaarii isiniif hin seennee? (Yeroo) beellamichaatu isinitti dheeratemoo? Dallansuun gooftaa keessan irraa isinitti bu’uu feetaniiti waadaa kiyya diigdanii?”');
INSERT INTO "apaguna" VALUES(20,87,'20:87','[Isaan] ni jedhan: “Waadaa siif seenne fedha keenyaan hin diigneGaruu nuti ba’aa (faaya) bareedumaa kan warra (Fir’awn) baachifamneeti, (ibidda keessatti) ishee darbineAkkuma nuti darbinetti, Saamiriyyichis darbe.”');
INSERT INTO "apaguna" VALUES(20,88,'20:88','Sana booda qaama, Jibicha (mirgoo), sagaleen barooduun isaaf jiru isaaniif baasee “Kanatu gabbaramaa keessaniifi gabbaramaa Muusaati; (Muusaan isa) irraanfateeti (barbaaduuf deeme)” jedhan.');
INSERT INTO "apaguna" VALUES(20,89,'20:89','Sila kan inni jecha tokkos isaaniif deebisuu dhabuufi isaan miidhuus ta’ee fayyaduu hin dandeenye ta’uu hin arganii?');
INSERT INTO "apaguna" VALUES(20,90,'20:90','Dhugumatti, sana duras Haaruun “Yaa ummata kiyya! Inni (kun) waan isin ittiin mokkoramtaniidhaGooftaan keessan immoo Rahmaani; ana hordofaa; ajaja kiyyas tole jedhaa” isaaniin jedhee ture.');
INSERT INTO "apaguna" VALUES(20,91,'20:91','[Isaan] “Hanga Muusaan gara keenya deebi’utti Nuti isa (Jibicha) gabbaruu irraa hin deemnu” jedhan.');
INSERT INTO "apaguna" VALUES(20,92,'20:92','(Muusaanis) ni jedhe: “Yaa Haaruun! Yeroo jallachuu isaanii argite maaltu si dhorge?');
INSERT INTO "apaguna" VALUES(20,93,'20:93','Ana hordofuu (maaltu si dhorge?) Moo ajaja kiyya fudhachuu didde?”');
INSERT INTO "apaguna" VALUES(20,94,'20:94','(Haaruunis) “Yaa ilma harmee kiyyaa! Areeda kiyyas ta’ee mataa kiyya hin qabinDhugumatti, ‘Ati gidduu ilmaan Israa’iil addaan baafte; jecha kiyyas hin eegdu (naan) jettaan sodaadhe” jedhe');
INSERT INTO "apaguna" VALUES(20,95,'20:95','(Muusaanis) ni jedhe: “Ati hoo yaa Saamiriyyii! Maal taate?”');
INSERT INTO "apaguna" VALUES(20,96,'20:96','(Saamiriyyiinis) “Ani waan isaan hin beekin beekeetin, faana kottee (farda) ergamaa (Jibriil) irraa (biyyee) hammaarrii tokko fudheen ishee darbeLubbuun kiyyas gocha kana naaf miidhagsite” jedhe.');
INSERT INTO "apaguna" VALUES(20,97,'20:97','(Muusaanis) ni jedhe: “DeemiDhugumatti, jireenya keessatti ‘Nan tuqinaa jechaa jiraachuun siif tahaadha.’ ammas dhugumatti, siif beellama ati hin hanbifamnetu siif jiraGara gabbaramaa kee isa gabbaraa turtee ilaaliNuti dhugumatti, isa gubna; sana booda (caccabaa isaa) galaana keessatti facaasinsa facaafna.”');
INSERT INTO "apaguna" VALUES(20,98,'20:98','Dhugaan gabbaramaan keesan Allaah Isa Isa malee dhugaan gabbaramaan biraa hin jirre qofaInni waan hunda beekumsaan wal ga’eera.');
INSERT INTO "apaguna" VALUES(20,99,'20:99','Akkuma oduu (warra Fir’awn) kanatti waan dur dabre irraa sitti odeessinaNuti dhugumatti, of biraa yaadannoo (Qur’aana) siif kenninee jirra.');
INSERT INTO "apaguna" VALUES(20,100,'20:100','Namni isa (Qur’aana) irraa garagale, dhugumatti, inni Guyyaa Qiyaamaa ba’aa (dilii) baadhata.');
INSERT INTO "apaguna" VALUES(20,101,'20:101','Yeroo hunda isa (adaba ba’aa kanaa) keessa jiraataGuyyaa Qiyaamaa ba’aa isaanii ta’uun waa fokkate!');
INSERT INTO "apaguna" VALUES(20,102,'20:102','Guyyaa garrii (xurumbaa) keessa afuufamu (dubbadhu)Guyyaa san badii raawwattoota (ija) cuquliisa (jaamaa) taasifnee walitti qabna.');
INSERT INTO "apaguna" VALUES(20,103,'20:103','Gidduu isaaniitti “Isin (guyyoota) kudhan malee (addunyaa irra) hin turre” jechuun walitti hasaasu');
INSERT INTO "apaguna" VALUES(20,104,'20:104','Nuti yeroo mala irra beekaan isaanii “isin guyyaa tokko malee hin teenye” jedhu waan isaan jedhan akkaan beekna.');
INSERT INTO "apaguna" VALUES(20,105,'20:105','Gaarreen irraas si gaafatuAtis “Gooftaan kiyya faffacaasuu ishee faffacaasa” jedhiin.');
INSERT INTO "apaguna" VALUES(20,106,'20:106','“Lafa wal qixxaa’aa biqilaa hin qabne taasisee ishee dhiisa.');
INSERT INTO "apaguna" VALUES(20,107,'20:107','Ishee keessatti bu’aa bahiis hin argitu.”');
INSERT INTO "apaguna" VALUES(20,108,'20:108','Guyyaa san lallabaa hordofu; asiifi achi irraa dabuun isaaniif hin jiraatuSagaleen hunduu Rahmaaniif gadi jettiShokoksaa malee homaa hin dhageessu.');
INSERT INTO "apaguna" VALUES(20,109,'20:109','Guyyaa san manguddummaan homaa hin fayyaddu; nama Rahmaan isaaf hayyameefi jecha isaas irraa jaallateef malee.');
INSERT INTO "apaguna" VALUES(20,110,'20:110','Inni waan fuuldura isaaniitiifi waan duuba isaaniitis beekaIsaan immoo beekumsaan isa hin marsine');
INSERT INTO "apaguna" VALUES(20,111,'20:111','Fuulli (Rabbii) jiraataa, waan hundaan dhaabatuuf gadi jetteDhugumatti, namni miidhaa baadhate hoonga’e.');
INSERT INTO "apaguna" VALUES(20,112,'20:112','Namni mu’umina ta’ee gaggaarii irraa hojjate, (hamtuu irratti) dabaluus ta’ee hirdhina (mindaa isaa) hin sodaatu.');
INSERT INTO "apaguna" VALUES(20,113,'20:113','Akkanatti dubbifamaa Afaan Arabaa goonee isa (Qur’aana) buufne; isa keessattis akeekkachiisa irraa akka isaan sodaataniif yookiin akka gorsa isaaniif haaromsuuf addeessine.');
INSERT INTO "apaguna" VALUES(20,114,'20:114','Egaa Allaahn, mootiin dhugaa, ol ta’eeraOdoo wahyiin isaa gara kee bu’ee hin xumuramin dura, Qur’aana (dubbisuutti) hin jarjarin“(Yaa) Rabbii kiyya! Beekumsa naaf dabali” jedhi');
INSERT INTO "apaguna" VALUES(20,115,'20:115','Dhugumatti, sana duras Aadamitti beeksisa goonee, dagatee tureNuti kutannoo isaaf hin argine.');
INSERT INTO "apaguna" VALUES(20,116,'20:116','Yeroo maleykotaan “Aadamiif sujuuda bu’aa” jennes [yaadadhu]Ibliisa malee yoosuu sujuuda bu’anInni ni dide');
INSERT INTO "apaguna" VALUES(20,117,'20:117','Ergasii ni jenne: “Yaa Aadam! Dhugumatti, kun diina keetiifi (diina) niitii teetiitiKanaafuu jannata keessaa akka isin lamaanuu hin baafne; (yoo jannataa baate) ni rakkatta.');
INSERT INTO "apaguna" VALUES(20,118,'20:118','Dhugumatti, ati ishee (jannata) keessatti beela’uu dhabuufi daaruu dhabuunis siif jira.');
INSERT INTO "apaguna" VALUES(20,119,'20:119','Ammas ati ishee keessatti dheebochuu dhabuufi aduun gubachuu dhabuunis siif jira.”');
INSERT INTO "apaguna" VALUES(20,120,'20:120','Ergasii sheyxaanni gara isaatti hasaasee: “Yaa Aadam! Gara muka yeroo hundaatiifi mootummaa hin dhabamne irratti si qajeelchuu?” jedheen');
INSERT INTO "apaguna" VALUES(20,121,'20:121','Yoosuu isaan lamaan ishee irraa nyaatani; qaamni saalaa isaanii isaaniif mul’atte; baala jannataa irraa of irratti maxxansuu eegalanAadamis ajaja Gooftaa isaa didee dogoggore.');
INSERT INTO "apaguna" VALUES(20,122,'20:122','Sana booda Rabbiin isaa isa filatee, isaaf tawbaa qeebalee isa qajeelche');
INSERT INTO "apaguna" VALUES(20,123,'20:123','(Rabbiin) ni jedhe: “Gariin keessan gariif diina ta’aatii, isin lamaanuu waliin ishee (jannata) irraa (gara dachii) gadi bu’aaErgasii yoo Ana irraa qajeelfamni isinitti dhufe, namni qajeelfama kiyya hordofe hin jallatu; hin rakkatus.');
INSERT INTO "apaguna" VALUES(20,124,'20:124','Namni yaadannoo (Qur’aana) kiyya irraa garagale, dhugumatti, isaaf jireenya dhiphoo ta’etu jira; Guyyaa Qiyaamaas jaamaa gooneeti isa kaafna.”');
INSERT INTO "apaguna" VALUES(20,125,'20:125','“Gooftaa kiyya! Odoon (addunyaa irratti) nama argu ta’ee jiruu (har’a) maaliif jaamaa gootee na kaafte?” jedha.');
INSERT INTO "apaguna" VALUES(20,126,'20:126','(Rabbiin) ni jedha: “Akka kanatti keeyyattoonni keenya sitti dhuftee, ishee dagatteAtis hardha akkuma sanitti dagatamta.”');
INSERT INTO "apaguna" VALUES(20,127,'20:127','Egaa akkuma kanatti nama daangaa dabree, keeyyattoota Gooftaa isaatti hin amanin mindaa isaaf kenninaadabbii Aakhiraatu irra cimaafi irra hafaadha.');
INSERT INTO "apaguna" VALUES(20,128,'20:128','Sila dhaloota isaaniin duraa irraa (heddu) kan isaan manneen isaanii keessa deemaa jiran meeqaatama balleessuun keenya isaaniif adda hin baanee? Dhugumatti, kana keessa abbootii sammuutiif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(20,129,'20:129','Odoo jechattiin Gooftaa kee irraa dabarteefi beellamni murtaa’e jiraachu baatee, silaa (adabni aaddunyaatti) isaan qabaa ta’a ture.');
INSERT INTO "apaguna" VALUES(20,130,'20:130','Waan isaan jedhan irratti obsiGooftaa keetis baha biiftuu duraafi lixa ishee dura faaruun Isa qulqulleessiYeroo halkaniitiifi qarqara guyyaa irraas (Isa) qulqulleessi(Kunis mindaa inni siif kennu) akka jaallattuufi.');
INSERT INTO "apaguna" VALUES(20,131,'20:131','Ija kee lamaanis gara waan Nuti gareewwan adda addaa isaan (kaafiroota) irraa ta’an akka isa keessatti isaan mokkoruuf jecha ittiin qanansiifne kan miidhagina jireenya addunyaa ta’ee hin diriirsinKennaa Rabbii keetiitu caalaafi irra hafaadha.');
INSERT INTO "apaguna" VALUES(20,132,'20:132','Maatii keetis salaatatti ajaji; ishee irrattis obsiNuti soorata si hin gaafannuNutu si sooraBooddeen (gaariin) warra Rabbi sodaatuufi.');
INSERT INTO "apaguna" VALUES(20,133,'20:133','Ni jedhanis: “Maaliif Gooftaa isaa irraa mallattoo wahiitin nutti hin dhufne?” Sila ragaan ifa taate kan kitaabota durii keessa jirtu isaanitti hin dhufnee?');
INSERT INTO "apaguna" VALUES(20,134,'20:134','Odoo Nuti isa (Rasuula -SAW) dura adabbiin isaan balleessinee, silaa ni jedhu turan: “Gooftaa keenya! Odoo ergamaa gara keenyatti ergitee, silaa odoo salphinaafi xiqqeenyi nu hin mudatin dura keeyyattoota kee hordofna turre.”');
INSERT INTO "apaguna" VALUES(20,135,'20:135','(Yaa Muhammad!): “Hunduu eegaadhaIsinis eegaaNamni warra karaa sirrii ta’eefi namni qajeele eenyuun akka ta’e beekuuf jirtu” jedhi');
INSERT INTO "apaguna" VALUES(21,1,'21:1','Namootaaf, odoo isaan gara galanii daguu keessa jiranuu qormaanni isaanii isaaniif dhihaatee jira');
INSERT INTO "apaguna" VALUES(21,2,'21:2','Gorsi haaraan Rabbii isaanii irraa kan isaan taphachaatuma isa dhaggeeffatan ta’an malee isaanitti hin dhufu.');
INSERT INTO "apaguna" VALUES(21,3,'21:3','Onneen isaanii dagattuu taatee malee (hin dhaggeeffattu)Miidhaa hojjattoonnis iccitiin marii taasisanii “Sila kun namuma akka keessaniitii mitii? Sila isin odoo argitan falfalatti dhaqxuu?" (waliin jedhu).');
INSERT INTO "apaguna" VALUES(21,4,'21:4','(Muhammad-SAW) ni jedhe: “Gooftaan kiyya jecha samiifi dachii keessatti (dubbatamu hunda) ni beekaInnis dhaga’aa, beekaadha.');
INSERT INTO "apaguna" VALUES(21,5,'21:5','Dhugumatti, isaan ni jedhan: “(Qur’aanni) kun abjuu laaqamaadhaLakkii! Soba inni of irraa uumeeLakkii! Inni weellisaadha(Yoo dhugaa ta’e) raajii akka warri durii ittiin ergameen nutti haa dhufu.”');
INSERT INTO "apaguna" VALUES(21,6,'21:6','Gandi Nuti ishee balleessine tokkolleen hin amanne; sila isaan ni amanuu?');
INSERT INTO "apaguna" VALUES(21,7,'21:7','Si duras dhiirota wahyii gara isaanii goonu malee hin ergineYoo kan hin beekne taatan warra beekumsa qaban gaafadhaa');
INSERT INTO "apaguna" VALUES(21,8,'21:8','Nuti isaan (ergamoota Rabbii) qaamota nyaata hin nyaanne hin taasifnes; kan (addunyaa keessa) yeroo hunda jiraatanis hin taane');
INSERT INTO "apaguna" VALUES(21,9,'21:9','Sana booda waadicha isaaniif dhugaa goonee isaaniifi nama feenes ni baraarsinee; daanga dabartoota immoo ni balleessine.');
INSERT INTO "apaguna" VALUES(21,10,'21:10','Dhugumatti, Nuti kitaaba yaadannoon (gorsi) keessan isa keessa jiru gara keessanitti buufnee jirraSila hin xiinxaltanuu?');
INSERT INTO "apaguna" VALUES(21,11,'21:11','(Warra) gandaa miidhaa hojjattu taatee baay''ee balleessine; erga isheetii ummata biraa uumne');
INSERT INTO "apaguna" VALUES(21,12,'21:12','Yeroo adabbii keenya argan, yeroma san isaan ishee irraa gulufu.');
INSERT INTO "apaguna" VALUES(21,13,'21:13','Hin gulufinaa; akka gaafatamtaniif jecha gara waan isa keessatti qanansiifamtaniifi (gara) manneen keessanii deebi’aa');
INSERT INTO "apaguna" VALUES(21,14,'21:14','(Isaan) ni jedhan: “Yaa badii keenya! Dhugumatti, nuti miidhaa hojjattoota turre.');
INSERT INTO "apaguna" VALUES(21,15,'21:15','Hanga haamamoo, dhaamoo isaan taasifnutti jechi (nuti miidhaa raawwattoota taane) jedhu watwaata isaanii ta’uu irraa hin deemne.');
INSERT INTO "apaguna" VALUES(21,16,'21:16','Nuti samii, dachiifi wantoota isaan lamaan gidduu jiran taphattoota taanee hin uumne.');
INSERT INTO "apaguna" VALUES(21,17,'21:17','Odoo tapha godhachuu feenee silaa of biraa isa taasifanna; kana hojjatoo hin taane.');
INSERT INTO "apaguna" VALUES(21,18,'21:18','Lakkii; dhugaa soba irratti darbina (ol taasifna)Sammuu isa dhoosee (isa balleessa); yoosuu inni (sobni) dhabamaadhaIsiniifis waan (Rabbi) irratti uumtaniif badiitu (mirkanaa’ee) jira.');
INSERT INTO "apaguna" VALUES(21,19,'21:19','Wantoonni samiifi dachii keessa jiran hundi kan IsaatiIsaan Isa (Allaah) bira jiranis (maleykonnis) Isa gabbaruu irraa hin boonan; hin hifatanis.');
INSERT INTO "apaguna" VALUES(21,20,'21:20','Halkaniifi guyyaa (Rabbiin) qulqulleessu; hin boqatanis.');
INSERT INTO "apaguna" VALUES(21,21,'21:21','Sila gabbaramtoota (gabbaran), kanneen (namoota) du’aa kaasan, dachii irraa godhatuu?');
INSERT INTO "apaguna" VALUES(21,22,'21:22','Odoo isaan lamaan (samiifi dachii) keessa Rabbiin malee gabbaramtoonni biraa jiraatanii lamaan isaaniituu ni badu tureRabbiin Gooftaa Arshii ta’e waan kaafiroonni (Isaan) jedhan irraa qulqullaa’e!');
INSERT INTO "apaguna" VALUES(21,23,'21:23','Inni waan dalagu irraa hin gaafatamu; isaan immoo ni gaafatamu.');
INSERT INTO "apaguna" VALUES(21,24,'21:24','Sila isaan Rabbiin ala gabbaramtoota taasifatuu? “Ragaa keessan fidadhaa” jedhiinKun gorsa namoota ana waliin jiraniifi gorsa namoota ana dura turaniitiGaruu irra hedduun isaanii dhugaa hin beekanuKanaafuu (isa) irraa garagalu.');
INSERT INTO "apaguna" VALUES(21,25,'21:25','(Yaa Muhammad!) Si dura ergamaa irraa tokkoyyuu “Ana malee dhugaan gabbaramaan hin jiru; kanaafuu Ana (qofa) gabbaraa” jechuun wahyii gara isaatti buufnu malee hin ergine');
INSERT INTO "apaguna" VALUES(21,26,'21:26','Isaanis “Rahmaan ilma godhate” jedhuInni qulqullaa’eeraLakkii isaan (maleykoonni) gabroota (Rabbii) kabajamoodha');
INSERT INTO "apaguna" VALUES(21,27,'21:27','Jechaan Isa hin dursanu; isaan ajaja Isaatiin hojjatu.');
INSERT INTO "apaguna" VALUES(21,28,'21:28','Inni waan isaaniin duraatiifi waan isaaniin duubaas ni beekaNama Inni jaalateef malee isaan hin manguddoomanuIsaanis (ofiifuu) sodaa Isaa (Allaah) irraa na’u');
INSERT INTO "apaguna" VALUES(21,29,'21:29','Isaan irraa nama “Isaan alatti anatu gabbaramaadha” jedhe, isa sana jahannam isaaf galata galchinaAkkasitti miidhaa hojjattoota galata galchina');
INSERT INTO "apaguna" VALUES(21,30,'21:30','Sila isaan kafaran akka samiifi dachiin walitti maxxantuu (qaama tokko) turanii, ergasii addaan isaan baafne hin beekanuu? Bishaan irraas lubbu qabeeyyii hunda ni uumneSila hin amananuu?');
INSERT INTO "apaguna" VALUES(21,31,'21:31','Dachii keessatti akka isheen isaaniin hin sochoone gaarreen lafa qabataa taasifneerraIshee keessattis daandiwwan gugurdaa akka ittiin qajeelaniif isaaniif taasifneerra.');
INSERT INTO "apaguna" VALUES(21,32,'21:32','Samiis jaarmaya (kan gubbaa) eegamaa isa taasifnee jirraGaruu isaan mallattoolee ishee irraa garagalu');
INSERT INTO "apaguna" VALUES(21,33,'21:33','Inni Isa halkaniifi guyyaa, aduufi baatii uumeedha; hunduu orbitii (isaanii) keessa daaku (deemu)');
INSERT INTO "apaguna" VALUES(21,34,'21:34','(Yaa Muhammad!) Si duras ilma namaatiif jireenya waaru (yeroo hundaa) hin taasifneSila yoo ati duute, isaan ni waaruu?');
INSERT INTO "apaguna" VALUES(21,35,'21:35','Lubbuun hundi du’a dhandhamtuudhaIsin qormaatuuf hamtuufi gaariin isin mokkorraGaruma keenyatti deebifamtu.');
INSERT INTO "apaguna" VALUES(21,36,'21:36','Isaan kafaran yeroo si argan odoo Rahmaaniin dubbachuutti kafaranii jiranuu “sila kun isa gabbaramtoota keessan (hamtuun) maqaa dhahu sani mitii?” (jechuun) qishnaa qofa si taasifatu');
INSERT INTO "apaguna" VALUES(21,37,'21:37','Namni hunduu jarjara irraa uumameAni mallattoolee kiyya isinittin agarsiisaa Ana hin jerjersiifatinaa.');
INSERT INTO "apaguna" VALUES(21,38,'21:38','“Yoo kan dhugaa dubbattan taataniif, waadaan kun yoom (raawwatama)?” jedhu');
INSERT INTO "apaguna" VALUES(21,39,'21:39','Isaan kafaran odoo yeroo ibidda fuula isaanii irraafi dugda isaanii irraa qabuu hin dandeenyeefi tumsas hin arganne san beekanii (hin jerjerfatanu ture).');
INSERT INTO "apaguna" VALUES(21,40,'21:40','Dhugumaatti, isheen (Qiyaaman) tasuma isaanitti dhuftee, isaan dhamaaftiIshee (of irraa) deebisuu hin danda’anu; yeroonis isaaniif hin kennamu.');
INSERT INTO "apaguna" VALUES(21,41,'21:41','Dhugumatti, si duras ergamtootatti qishnamee (baacamee) isaan irraa warri baacan wanti isaan itti qishnaa turan isaan marse.');
INSERT INTO "apaguna" VALUES(21,42,'21:42','Jedhi: “Eenyutu halkaniifi guyyaa (adabbii) Rahmaan irraa isin eega?” Dhugumatti, isaan yaadannoo Rabbii isaanii irraa garagaloodha.');
INSERT INTO "apaguna" VALUES(21,43,'21:43','Moo isaan gabbaramtoota Nu irraa isaan tiksan qabu? Lubbuu isaaniituu tumsuu hin danda’anuIsaanis Nu irraa tikfamuu hin danda’an.');
INSERT INTO "apaguna" VALUES(21,44,'21:44','Dhugumatti, Nuti isaan kanaafi abbootii isaaniis hanga yeroon isaan irratti dheeratutti qanansiifneSila Nuti dachii moggaa ishee irraa suuta suutaan hirdhisaa akka deemnu hin arganuu? Moo isaantu injifatoodhaa?');
INSERT INTO "apaguna" VALUES(21,45,'21:45','(Yaa Muhammad!) Jedhi: “Kan ani isin sodaachisuun wahyii qofaaniNamoonni gurri isaanii (haqa irraa) duude yeroo dinniinni isaaniif godhamu hin dhagayanu.”');
INSERT INTO "apaguna" VALUES(21,46,'21:46','Rabbiin kakadhee! gosti adabbii Gooftaa kee irraa ta’e yoo isaan tuqe ni jedhu: “Yaa badii keenya! Dhugumatti nuti miidhaa raawwattoota taaneerra.”');
INSERT INTO "apaguna" VALUES(21,47,'21:47','Nuti Guyyaa Qiyaamatiif madaala haqaa lafa keenyaLubbuun takkas homaa hin miidhamtuYoo waan ulfina hanga firii (ija) raafuu qabu ta’es Nuti ishee ni fidnaQormaataafis Nuti ga’oodha');
INSERT INTO "apaguna" VALUES(21,48,'21:48','Dhugumatti, Nuti Muusaafi Haaruuniif (kitaaba) adda baasaa, ifnanaafi yaadannoo warra Rabbiin sodaataniif ta’e kenninee jirra.');
INSERT INTO "apaguna" VALUES(21,49,'21:49','(Isaan) warra Gooftaa isaanii fageenyatti sodaataniifi isaan Qiyaamaa irraa sodaataniidha.');
INSERT INTO "apaguna" VALUES(21,50,'21:50','Kun (Qur’aanni) gorsa barakeeffamaa Nuti buufneedhaSila isin isa mormituu?');
INSERT INTO "apaguna" VALUES(21,51,'21:51','Dhugumatti, Nuti kana duras qajeelfama Ibraahiimiif kenninee jirraNuti isa beekoo taanee jirra');
INSERT INTO "apaguna" VALUES(21,52,'21:52','Yeroo inni abbaafi ummata isaatiin “Siidaawwan isin (Ibaadaaf) ishee irra teessan kun maali?” jedhe (kennineef)');
INSERT INTO "apaguna" VALUES(21,53,'21:53','[Isaan] ni jedhan: “Abbootii keenya kan ishee gabbaran agarre.”');
INSERT INTO "apaguna" VALUES(21,54,'21:54','[Inni] ni jedhe: “Dhugumatti, isiniifi abbootiin keessanis jallina ifa bahaa keessa jirtan.”');
INSERT INTO "apaguna" VALUES(21,55,'21:55','[Isaan] ni jedhaniin: “Sila dhugaan nutti dhufte moo ati warra taphatan irraayi?”');
INSERT INTO "apaguna" VALUES(21,56,'21:56','Ni jedhe: “Baraa, Rabbiin keessan Gooftaa samiifi dachii, kan isaan uume saniAnis kana irratti warra ragaa ba’an irraa tokko.”');
INSERT INTO "apaguna" VALUES(21,57,'21:57','“Allaah’n kakadhee! Ani erga isin dugda gara galtanii deemtan booda dhugumatti, tooftaan ittiin taabota keessan (mancaasun) qopheeffadha” (of keessatti jedhe).');
INSERT INTO "apaguna" VALUES(21,58,'21:58','Akka isaan gara isaa (taabota guddichaa) deebi''aniif jecha guddaa isaaniif ta’e tokko qofa (dhiisee) caccabaa isaan godhe.');
INSERT INTO "apaguna" VALUES(21,59,'21:59','(Isaan) ni jedhan: “Namni gabbaramtoota keenyatti kana hojjate dhugumatti, inni miidhaa raawwattoota irraayi.”');
INSERT INTO "apaguna" VALUES(21,60,'21:60','“Dargaggeessa Ibraahiim isaan jedhamu tokko kan maqaa isaanii (hamtuun) dubbatu dhageenyeerra” jedhan.');
INSERT INTO "apaguna" VALUES(21,61,'21:61','Ni jedhan: “Akka isaan ragaa irratti bahaniif jecha ija namootaa dura fidaa.”');
INSERT INTO "apaguna" VALUES(21,62,'21:62','“Sila situ gabbaramtoota keenya irratti (yakka) kana raawwatee Yaa Ibraahiim?” jedhan.');
INSERT INTO "apaguna" VALUES(21,63,'21:63','(Ibraahiimis) “Lakkii, guddaa isaaniitu kana hojjateYoo kan dubbatan ta’aniif isaan gaafadhaa” jedhe.');
INSERT INTO "apaguna" VALUES(21,64,'21:64','Gara lubbuu isaanii deebi’anii “isin isinumatu miidhaa raawwattoota” jedhan');
INSERT INTO "apaguna" VALUES(21,65,'21:65','Sana booda mataa isaanii lafatti gadi deebisanii “ati dhugumatti, isaan kun hin dubbatanuu beektee jirta (maaliif kana jetta?)” jedhan.');
INSERT INTO "apaguna" VALUES(21,66,'21:66','(Ibraahiim) ni jedhe: “Sila Rabbiin ala waan homaa isin hin fayyadneefi isin hin miine gabbartuu?');
INSERT INTO "apaguna" VALUES(21,67,'21:67','Isiniifi wantoota isin Allaah’n ala gabbartaniinis ‘uffi’ (jedheera)! Sila hin xiinxaltanuu?”');
INSERT INTO "apaguna" VALUES(21,68,'21:68','Ni jedhan: “Yoo kan hojjattan taataniif isa gubaa; gabbaramtoota keessanis gargaaraa.”');
INSERT INTO "apaguna" VALUES(21,69,'21:69','Nutis “Yaa ibiddaa! Ibraahiim irratti qabbanaafi nagaa ta’i" jenne');
INSERT INTO "apaguna" VALUES(21,70,'21:70','Isaanis hamtuu itti yaadanNuti isaanuma irra hoonga’oo goone.');
INSERT INTO "apaguna" VALUES(21,71,'21:71','Isaafi Luuxiin gara dachii ishee keessa aalama hundaaf barakaa gooneetti bilisa baafne.');
INSERT INTO "apaguna" VALUES(21,72,'21:72','Isihaaqiin isaaf kenninee, Ya’aquubis dabalataan (isaaf kennine)Hundaayyuu gaggaarii goone.');
INSERT INTO "apaguna" VALUES(21,73,'21:73','Imaamota ajaja keenyaan qajeelchan isaan taasifnee jirraWantoota gaggaarii hojjachuu, salaata dhaabuufi zakaa baafachuus gara isaanitti wahyii gooneWarra Numa (qofa) gabbaran turan');
INSERT INTO "apaguna" VALUES(21,74,'21:74','Luuxiinis murtii haqaafi beekumsa (amantii) isaaf kenninee jirra(Ummata) ganda badii fokkatoo ta’an hojjatan san irraas isa baraarsinee jirraDhugumatti, isaan ummata badoo, finciltoota turan.');
INSERT INTO "apaguna" VALUES(21,75,'21:75','Rahmata keenya keessas isa seensifneDhugumatti, inni warra gaggaarii irraayi.');
INSERT INTO "apaguna" VALUES(21,76,'21:76','Nuuhiinis ‘yeroo inni sana dura (Nutti) lallabee isaaf owwaannee, isaafi maatii isaas cinqaa guddaa irraa baraarsine’ [yaadadhu]');
INSERT INTO "apaguna" VALUES(21,77,'21:77','Namoota keeyyattoota keenya sobsiisan irraas nagaya isa baafneDhugumatti, isaan warra hamtuu turanKanaaf hunda isaanii bishaaniin dhidhimsine.');
INSERT INTO "apaguna" VALUES(21,78,'21:78','Daawuudiifi Suleeymaaniinis ‘yeroo isaan lamaan lafa qonnaa (oyruu) re’oonni ummataa (halkan) dheedan keessatti murtii kennan” [yaadadhu]Nutis murtii isaanii ni beekna turre.');
INSERT INTO "apaguna" VALUES(21,79,'21:79','Ishee (murtittii) Suleeymaaniin hubachiifneHundaafuu beekumsa murtii haqaafi beekumsa (amantiis) kennineerraGaarreeniifi allaattiinis Daawuudii waliin akka Ana qulqulleessan laaffifneNutis hojjatoo taane');
INSERT INTO "apaguna" VALUES(21,80,'21:80','Uffata sibiilaa kan yeroo wal loltan waraana irraa isin ittisan hojjachuus isa barsiifnee jirraSila isin (Nu) hin galatoomfattanuu?');
INSERT INTO "apaguna" VALUES(21,81,'21:81','Suleeymaanifis qilleensa bubbisaa, kan ajaja isaatiin gara dachii ishee keessatti barakaa goonee yaatu (laaffifnee jirra)Nutis waan hunda beekoo taaneerra.');
INSERT INTO "apaguna" VALUES(21,82,'21:82','Sheyxaanota irraas isaan (faaya baasuuf garba keessa) isaaf lixaniifi sana malees hojii biraa hojjatantu jiraNutis tiksoo isaanii taaneerra.');
INSERT INTO "apaguna" VALUES(21,83,'21:83','Ayyuubiinis ‘yeroo inni Gooftaa isaatti lallabee “ani dhugumatti, rakkoon na tuqeera; Ati irra rahmata godhaa warra rahmata godhaniiti” (jedhu yaadadhu)');
INSERT INTO "apaguna" VALUES(21,84,'21:84','Isaaf owwaannee cinqaa irraa waan isarra jiru irraa furreRahmata Nu biraa ta''eefi warra Rabbiin gabbaran yaadachisuuf jecha maatii isaatiifi fakkaataa isaaniis isaanii wajjin isaaf kennine');
INSERT INTO "apaguna" VALUES(21,85,'21:85','Ismaa’iil, Idriisiifi Zalkifliis (yaadadhu); hunduu obsitoota irraayi.');
INSERT INTO "apaguna" VALUES(21,86,'21:86','Rahmata keenya keessas isaan seensifneDhugumatti, isaan warra gaggaarii irraayi.');
INSERT INTO "apaguna" VALUES(21,87,'21:87','Zannuuniinis (Yuunusiinis yaadadhu); yeroo inni dallanaa deemee, Nuti hamtuu isa irratti dhiphisuu dhabuu yaadeDukkana keessaa “Si malee dhugaan gabbaramaan hin jiruAti qulqulloofteDhugumatti, ani miidhaa hojjataa irraa ta’een jira” (jechuun) lallabe');
INSERT INTO "apaguna" VALUES(21,88,'21:88','Isaaf owwaanne; cinqaa irraas isa baraarsineAkka kanatti mu’uminoota nagaa baafna');
INSERT INTO "apaguna" VALUES(21,89,'21:89','Zakariyyaas (yaadadhu); yeroo inni Gooftaa isaatti lallabee “Gooftaa kiyya! Kophaa (ilma malee) ana hin dhiisin; Ati irra caalaa dhaaltotaati” (jedhe).');
INSERT INTO "apaguna" VALUES(21,90,'21:90','Kanaafuu isaaf owwaannee Yahyaas isaaf kennineNiitii isaas (akka deessu) isaaf tolchineDhugumatti, isaan (nabiyy-oonni) toltuu keessatti kan daddafan ta''anii kajeellaafi sodaan (guutamanii) kan Nu kadhataniifi Nu sodaatan ta''an.');
INSERT INTO "apaguna" VALUES(21,91,'21:91','Ishee qaama saalaa ishee tikfattes (Maryamiinis yaadadhu)Ruuhii (uumama) keenya irraa ta’e ishee keessatti afuufneIsheefi ilma ishees aalama hundaaf mallattoo taasifne.');
INSERT INTO "apaguna" VALUES(21,92,'21:92','Dhugumatti, isheen kun amantii keessan amantii tokkittiidhaAnis Gooftaa keessanii Ana (qofa) gabbaraa.');
INSERT INTO "apaguna" VALUES(21,93,'21:93','Amantii isaanii keessatti adda qoodamanHunduu gara keenya deebi’oodha');
INSERT INTO "apaguna" VALUES(21,94,'21:94','Namni mu’umina ta’ee gaggaarii irraa hojjate, hojii isaa hoonga’uun hin jiruNuti isaaf galmeessaadha');
INSERT INTO "apaguna" VALUES(21,95,'21:95','Ganda ishee balleessine irratti (duniyatti deebi''uun) dhorgaadha; isaan hin deebi''an.');
INSERT INTO "apaguna" VALUES(21,96,'21:96','Hanga yeroo (hidhaan) Ya’ajuujiifi Ma’ajuuj banamee isaanis tabba hunda irraan dhangala’anuutti (hin deebi''anu).');
INSERT INTO "apaguna" VALUES(21,97,'21:97','Waadaan dhugaas dhihaatee jiraYeroo san ijji warra kafaree akkuma banamtetti haftuudha“Yaa badii keenya! Dhugumatti, nuti kana irraa daguu keessa turre(Yoom kana qofa) inumaa miidhaa raawwattoota irraa taane" jedhu');
INSERT INTO "apaguna" VALUES(21,98,'21:98','Dhugumatti, isiniifi wantoonni isin Rabbiin ala gabbartanis qoraan (ibidda) jahannami; isin ishee (Jahannam) seenoodha');
INSERT INTO "apaguna" VALUES(21,99,'21:99','Isaan (taabotoonni) kunniin odoo gabbaramtoota ta’anii silaa ishee hin seenan tureHundi isaaniis ishee keessatti waaru (hafu).');
INSERT INTO "apaguna" VALUES(21,100,'21:100','Isaaniif ishee keessatti halaaka (iyyaansa) harreetu jira; akkasumas isaan ishee keessatti hin dhaga’an.');
INSERT INTO "apaguna" VALUES(21,101,'21:101','Dhugumatti, warra Nu irraa milkiin isaaniif dabarte, isaan sun ishee irraa fageeffamoodha');
INSERT INTO "apaguna" VALUES(21,102,'21:102','Sagalee ishee hin dhaga’anuIsaanis waan lubbuun isaanii jaallattu keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(21,103,'21:103','(Guyyaa Qiyaamaa) naasuun guddaan isaan hin gaddisiisuMaleykonnis isaan qunnamuun “Kun guyyaa keessan kan waadaa isiniif galamaa tureedha” (jedhuun)');
INSERT INTO "apaguna" VALUES(21,104,'21:104','Guyyaa samii akka barreessan xalayaa (galmee) marutti marru san [yaadadhu]Akkuma uumaa duraa jalqabnetti isa deebifna(Kun) waadaa Nu irra jiruudhaDhugumatti, Nuti raawwattoota taanee jirra.');
INSERT INTO "apaguna" VALUES(21,105,'21:105','Dhugumatti, Nuti erga ‘Lawhal Mahfuuz’ keessatti barreessinee booda kitaabota keessatti “dachii gabroota kiyya gaggaariitu dhaalaa” barreessineerra.');
INSERT INTO "apaguna" VALUES(21,106,'21:106','Dhugumatti, (Qur’aana) kana keessa ergaa ga''aa ta’etu namoota (Rabbiin) gabbaraniif jira.');
INSERT INTO "apaguna" VALUES(21,107,'21:107','Aalama hundaaf rahmata taasifnee malee (waan biraatiif) si hin ergine.');
INSERT INTO "apaguna" VALUES(21,108,'21:108','(Yaa Muhammad!) Jedhi: “Ani wanti gara kiyyatti wahyii godhamu ‘dhugaan gabbaramaan keessan gabbaramaa tokkicha’ (kan jedhuu dha)Sila isin Muslimoota hin taatanuu ree?”');
INSERT INTO "apaguna" VALUES(21,109,'21:109','Yoo gara galan “Ani ifaan ifatti isin beeksiseAni wanti waadaa isiniif galame dhihoo moo, fagoo ta’aa isaa hin beeku” jedhi.');
INSERT INTO "apaguna" VALUES(21,110,'21:110','Dhugumatti, Inni jecha ol fuudhamaa ni beekaWaan isin dhoksitanis ni beeka.');
INSERT INTO "apaguna" VALUES(21,111,'21:111','Ani inni (an isinitti himu kun) mokkoroo isiniif ta’uufi qananii yeroo gabaabaa (isiniif) ta’aa hin beeku.');
INSERT INTO "apaguna" VALUES(21,112,'21:112','(Muhammad) ni jedhe: “(Yaa) Rabbi! (Anaafi warra ana sobsiisan giddutti) dhugaan murtii godhiGooftaan keenyas Rahmaani, waan isin jettan irrattis gargaarsifatamaadha');
INSERT INTO "apaguna" VALUES(22,1,'22:1','Yaa namootaa! Gooftaa keessan sodaadhaaDhugumatti, sochiin Qiyaamaa waan guddaadha');
INSERT INTO "apaguna" VALUES(22,2,'22:2','Guyyaa ishee argitan, dubartiin hoosiftu hundi waan hoosiftu irraa ni dagatti(Dubartiin) ulfa qabdu hundi ulfa ishee garaa darbitiNamootas (sodaa irraa) machaa’aa agartaIsaan waa machaa''oo mitiGaruu adabbii Rabbiitu garmalee cimaadha.');
INSERT INTO "apaguna" VALUES(22,3,'22:3','Namoota irraas nama waa''ee Rabbii keessatti beekumsa malee falmee, sheyxaana didaa ta’e hunda hordofutu jira.');
INSERT INTO "apaguna" VALUES(22,4,'22:4','Isa (sheyxaana) irratti ‘inni nama isa jaalallee godhate kamillee ni jallisa; gara ibidda qabsiifamaas isa qajeelcha’ jechuun barreeffamee jira.');
INSERT INTO "apaguna" VALUES(22,5,'22:5','Yaa namootaa! Yoo kaafamuu irraa shakkii keessa jiraattan, dhugumatti Nuti biyyee irraa isin uumnee; ergasii copha bishaan saalaa irraa, dhiiga ititaa irraa, ergasii foon alalfatamu kan uumaa guuttateefi hin guuttatin irraa isin uumne; (humnaafi dandeettii keenya) isiniif ibsuuf (kana goone)Nuti waan (abbaa) feene hanga yeroo murtaa’eetti gadaamessa keessa teessifnee, ergasii daa’imman goonee isin baafnaErgasii akka umrii jabinaa keessan irra geessaniif (isin jiraachifna)Isin irraas nama (umrii ijoollummaatiin) du’utu jiraErga duraan waa beekee akka homaa hin beekneef, isin irraas nama gara umrii gadi aanaa deebi''utu jiraDachiis gogduu taatee agarta; yeroo Nuti bishaan (bokkaa) ishee irratti buufne ni sochooti; ni iitoftis, akaakuu marga bareedaa irraas ni magarsiti(Ashaakiltii) bifa hundaa irraas ni biqilchiti.');
INSERT INTO "apaguna" VALUES(22,6,'22:6','Sun waan Rabbiin, Isatu dhugaa ta’eefi Isatu du’aa kan kaasuu, ammas Inni waan hunda irratti danda’aa ta’eefi.');
INSERT INTO "apaguna" VALUES(22,7,'22:7','Dhugumatti, Qiyaamaan dhuftuudha; ishee keessa mamiin hin jiruAmmas dhugumatti Rabbiin namoota awwaala keessa jiran (hunda) ni kaasa');
INSERT INTO "apaguna" VALUES(22,8,'22:8','Namoota irraa nama beekumsa, qajeelfamaafi kitaaba (ergaa Rabbii) ibsu malee (waa''ee) Rabbii keessatti falmutu jira');
INSERT INTO "apaguna" VALUES(22,9,'22:9','Morma isaa maree (namoota) karaa Rabbii irraa jallisuuf (nama falmutu jira)Isaaf addunyaa kana keessatti salphinatu jiraGuyyaa Qiyaamaas adabbii gubaa ta’e isa dhandhamsiifna');
INSERT INTO "apaguna" VALUES(22,10,'22:10','“Kun waan harki kee lamaan hojjataniif” (isaan jedhama)Dhugumatti, Allaahnis gabroota (Isaa) kan miidhu hin taane.');
INSERT INTO "apaguna" VALUES(22,11,'22:11','Namoota irraas nama amantii irraa moggaatti ta’ee (shakkiin) Rabbi gabbarutu jiraYoo toltuun isa tuqe, isatti raga’aYoo rakkoon isa tuqe ammoo, fuula isaa irratti garagalaInni addunyaafi Aakhiraa hoonga’ee jiraSun isatu hoongoo ifa bahaadha.');
INSERT INTO "apaguna" VALUES(22,12,'22:12','Rabbiin ala waan isa hin miineefi isa hin fayyadne kadhataSun isatu jallina fagoodha.');
INSERT INTO "apaguna" VALUES(22,13,'22:13','Nama miidhaan isaa bu’aa isaa irra dhihoo ta’e kadhataJaalallee ta’uun (isaa) waa fokkate! Ammas hiriyaa ta’uun (isaa) waa fokkate!');
INSERT INTO "apaguna" VALUES(22,14,'22:14','Dhugumatti, Rabbiin isaan amananii, gaggaarii dalagan Jannata laggeen ishee jala yaatu isaan seensisaDhugumatti, Rabbiin waan fedhu hojjata.');
INSERT INTO "apaguna" VALUES(22,15,'22:15','Namni duuniyaafi Aakhiraa keessatti Rabbiin isa (Muhammadiin-SAW) hin tumsu jechuu yaade, gara samii haada hidhatee of haa fannisuErgasii tooftaan isaa waan inni jibbu dhabamsiisuu haa ilaalu');
INSERT INTO "apaguna" VALUES(22,16,'22:16','Akka kanatti keeyyattoota ifa bahaa goonee isa buufneDhugumatti, Rabbiin nama fedhe ni qajeelcha.');
INSERT INTO "apaguna" VALUES(22,17,'22:17','Dhugumatti, isaan amanan, isaan yahuudoman, saabi’oonni, kiristaanonni, majuusonniifi isaan mushrikoota ta’anis; Rabbiin dhugumatti, Guyyaa Qiyaamaa gidduu isaaniitti murteessaDhugumatti, Allaah’n waan hunda irratti ragaadha');
INSERT INTO "apaguna" VALUES(22,18,'22:18','Sila akka Rabbiin wanti samii keessa jiruufi wanti dachii keessa jiruus, aduun, jiini, urjiiwwan, gaarreen, mukkeen, lubbu qabeeyyiifi namoonni baay’enis sujuuda Isaaf godhan hin arginee? (Namoota) baay’ee adabbiin isa irratti mirkanaa’ee jiraNama Rabbiin isa salphise immoo namni isa kabaju hin jiruDhugumatti, Rabbiin waan fedhe hojjata.');
INSERT INTO "apaguna" VALUES(22,19,'22:19','Gareen kun lamaan warra Rabbii isaanii keessatti wal falmaniidhaIsaan kafaran uffata ibidda irraa ta’etu isaaniif kutamee, mataa isaanii gubbaas bishaan danfaatu itti naqama.');
INSERT INTO "apaguna" VALUES(22,20,'22:20','Isa sanaan wanti garaa isaanii keessa jiruufi gogaan (isaanii) baqfama.');
INSERT INTO "apaguna" VALUES(22,21,'22:21','Ammas jirma sibiila irraa ta’etu isaaniif jira');
INSERT INTO "apaguna" VALUES(22,22,'22:22','Dhawaata isaan yaaddoodhaan ishee irraa ba’uu fedhaniin ishee keessatti deebifaman“Adaba gubaa dhandhamaa” (jedhamani).');
INSERT INTO "apaguna" VALUES(22,23,'22:23','Dhugumatti, Rabbiin isaan amananii, gaggaarii hojjatan Jannata laggeen jala ishee yaatu seensisaIshee keessattis gumeewwan (kilkillee) warqeefi faaya irraa ta’an uffifamuUffanni isaaniis ishee keessatti hariira');
INSERT INTO "apaguna" VALUES(22,24,'22:24','(Addunyaa kana keessatti) gara jecha gaarii qajeelfamanAmmas gara karaa (Rabbii) faarfamaa ta’eetti qajeelfaman');
INSERT INTO "apaguna" VALUES(22,25,'22:25','Dhugumatti, isaan kafaranii, karaa Rabbiitiifi masjiida kabajamaa isa namoota ishee (Makkaan) keessa jiraataniifi biyyoota biraa irraa dhufaniif wal qixa goone irraa (namoota) dhorgan, (hoonga’an)Nama ishee keessatti sababa miidhaatiin jallachuu fedhes adabbii laalessaa irraa isa dhandhamsiifna');
INSERT INTO "apaguna" VALUES(22,26,'22:26','Yeroo Ibraahiimitti iddoo manaa (Ka''abaa) mul’ifnee “homaa Natti hin qindeessin; Mana Kiyyas namoota naannawaniif, isaan (salaataaf) dhaabbataniif, isaan rukuu’a godhaniifi sujuuda bu’aniif qulqulleessi” (jennes dubbadhu.)');
INSERT INTO "apaguna" VALUES(22,27,'22:27','(Sirna) hajjii (akka raawwatan) namoota keessatti lallabi(Namoonnis sirnicha raawwachuuf) miilaafi geejjiba huqqifaman hunda irra ta’anii, daandiiwwan fagoo irraa sitti dhufu.');
INSERT INTO "apaguna" VALUES(22,28,'22:28','Bu’aalee isaanii irratti akka hirmaataniif, guyyoota beekamoo ta''an keessattis beylada irraa waan Rabbiin isaanii kenne irratti akka maqaa Rabbii dubbataniif (beeksisi)Ishee irraa nyaadhaa; hiyyeessa dhabaas nyaachisaa');
INSERT INTO "apaguna" VALUES(22,29,'22:29','Sana booda xuriiwwan isaanii haa dhabamsiisan; qodhaawwan isaaniis haa guutanMana turaa (Ka’abaan) haa naanna’anii.');
INSERT INTO "apaguna" VALUES(22,30,'22:30','(Dubbiin) sanuma! Namni wantoota Rabbiin kabaje guddise, Gooftaa isaa biratti sanatu isaaf caalaIsaan (of duratti) isiniif ibsamuuf (jiran) malee beyladoonni (hundi) isiniif eeyyamamteXurii taabotaa irraa fagaadhaaJecha cubbuu irraas fagaadhaa.');
INSERT INTO "apaguna" VALUES(22,31,'22:31','Rabbiif qajeeloo haala taataniin odoo homaa Isatti hin qindeessin (fagaadhaa)Namni Rabbitti waa dabale, akka nama samii irraa kufee allaattiin isa buttee yookiin bubbeen iddoo fagootti isa gatteeti.');
INSERT INTO "apaguna" VALUES(22,32,'22:32','(Dubbiin) sanuma! Namni mallattoolee amantii Rabbii kabajes, dhugumatti sun onneen sodaa (Rabbii) qabaachuu irraayi');
INSERT INTO "apaguna" VALUES(22,33,'22:33','Isiniif isaan (beyladoota wareegaa) keessa hanga beellama murtaa’eetti bu’aawwantu jiraSana booda iddoon isaanii gara Mana turaa (daangaa harama Makkaa)ti.');
INSERT INTO "apaguna" VALUES(22,34,'22:34','Ummata hundaafuu akka horii Inni isaaniif kenne irratti maqaa Isaa faarsaniif jecha bakka qalmaa (isaaniif) taasifnee jirraDhugaan gabbaramaan keessanis Gooftaa tokkichaIsaaf harka kennaaWarra Isaaf gad of qaban gammachiisi.');
INSERT INTO "apaguna" VALUES(22,35,'22:35','Isaan warra yeroo Rabbiin dubbatame onneen isaanii sodaattu, kan waan isaan tuqe irratti obsan , kan salaata dhaabaniifi waan isaaniif kennine irraa kennaniidha.');
INSERT INTO "apaguna" VALUES(22,36,'22:36','Beeyladoota wareega Ka’abaa mallattoolee Rabbii irraa isiniif gooneIsaan keessa gaggaariitu isiniif jiraOdoo sakaalamanii jiranuu maqaa Rabbii isaan irratti faarsaaYeroo cinaachi ishee kufte ishee irraa nyaadhaa; hiyyeessa nama hin kadhanneefi isa kadhatu nyaachisaaAkka isin galata galchitaniif Nuti akka kanatti ishee (beeyladoota) isiniif laaffifne.');
INSERT INTO "apaguna" VALUES(22,37,'22:37','Foon isaaniitiifi dhiigni isaaniis Rabbiin bira hin ga’uGaruu sodaa Rabbii kan isin biraa ta’etu Isa bira ga’aAkka kanatti waan isin qajeelcheef akka Rabbiin guddiftaniif, isiniif laaffiseGaarii hojjattoota gammachiisi');
INSERT INTO "apaguna" VALUES(22,38,'22:38','Dhugumatti, Rabbiin isaan amanan irraa ni ittisaDhugumatti nama akkaan gantuu, kafaraa ta''e Rabbiin hin jaallatu');
INSERT INTO "apaguna" VALUES(22,39,'22:39','Warra lolaman sababa miidhamuu isaaniitiif hayyamni godhameefRabbiinis isaan tumsuu irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(22,40,'22:40','Isaan warra manneen isaanii irraa haqaan ala waan “Rabbiin keenya Allaahdha” jedhaniif qofa baafamaniidhaOdoo Rabbiin namoota garii isaanii gariin deebisuun jiraachuu baatee, silaa iddoon moloksee, bataskaanoleen kiristaanootaa, iddoon ibaadaa Yahuudotaafi masjiidonni maqaan Rabbii hedduu keessatti faarfamu ni diigamu turanDhugumatti, Rabbiin nama (amantii) Isaa tumsu, tumsuuf jiraDhugumatti, Rabbiin irra jabaa, injifataadha');
INSERT INTO "apaguna" VALUES(22,41,'22:41','Isaan warra yoo Nuti dachii keessatti isaaniif mijeessine salaata dhaaban, zakaa kennan, toltuuttis (namoota) ajajaniifi hamtuu irraa (namoota) dhorganiidhaBooddeen waan hundaa Rabbumaafi');
INSERT INTO "apaguna" VALUES(22,42,'22:42','Yoo si sobsiisan, isaaniin duras ummanni Nuuh, Aadiifi Samuud sobsiisanii jiru');
INSERT INTO "apaguna" VALUES(22,43,'22:43','Ummanni Ibraahiimiifi ummanni Luuxis (akkasuma).');
INSERT INTO "apaguna" VALUES(22,44,'22:44','Jiraattonni Madyanis (akkasuma)Muusaanis sobsiifamee jiraAni kaafirootaaf yeroo gabaabaa kenneetan sana booda isaan qabeadabbiin kiyya akkam (hamaa) dha!.');
INSERT INTO "apaguna" VALUES(22,45,'22:45','Magaalota irraa meeqaatama odoo isheen miidhaa hojjattuu taatee jirtuu ishee balleessine! Isheen gubbaa ishee irratti kukkuftuudhaEela onteefi Gamoowwan dhedheeraa (meeqa duwwaa dhiifne).');
INSERT INTO "apaguna" VALUES(22,46,'22:46','Sila isaan (Mushrikoonni Makkaa) dachii keessa hin deemnee? Onneen ittiin waa hubatan yookiin gurri ittiin waa dhagayan isaaniif ta''aDhugumatti, kan jaamtu ija (gubbaa kanaa) mitiGaruu kan jaamtu onnee laphee keessa jiruudha.');
INSERT INTO "apaguna" VALUES(22,47,'22:47','Adabatti si jarjarfatuRabbiin waadaa Isaa hin diiguDhugumatti, guyyaan tokko Rabbii kee biratti waan isin lakkooftan irraa akka waggaa kumaati');
INSERT INTO "apaguna" VALUES(22,48,'22:48','Magaalota irraa haala isaan miidhaa hojjatoo ta’anii jiraniin Ani yeroo isheef kennee, ergasii (adabaan) ishee qabee kan deebiin ishee gara kiyyatti ta’uuf taa’u meeqaatama');
INSERT INTO "apaguna" VALUES(22,49,'22:49','(Yaa Muhammad!) Jedhi: “Yaa namootaa! Ani isiniif sodaachisaa ifa bahaa malee hin taane.”');
INSERT INTO "apaguna" VALUES(22,50,'22:50','Isaan amananii, gaggaarii hojjatan, isaaniif araaramaafi kennaa gaariitu jira.');
INSERT INTO "apaguna" VALUES(22,51,'22:51','Isaan Nu dadhabsiisuun miliquu yaadanii aayatoota keenya balleessuuf deeman, isaan sun warra jahannami.');
INSERT INTO "apaguna" VALUES(22,52,'22:52','Nuti si dura ergamaas ta’ee nabiyyii tokko erginee ‘yeroo inni dubbise sheyxaanni dubbisa isaa keessa (gurra ummataa irratti soba) darbu malee’ kan hafu hin taaneRabbiin waan sheyxaanni itti darbu san ni balleessaSana booda aayatoota isaa ni raggaasisaRabbiin beekaa, ogeessa');
INSERT INTO "apaguna" VALUES(22,53,'22:53','Waan sheyxaanni darbu san isaan onnee isaanii keessa dhukkubni jiruufi warra onneen isaanii gogeef qormaata gochuuf (akkas taasise)Dhugumatti, miidhaa hojjattoonni wal diddaa fagoo ta’e keessa jiru.');
INSERT INTO "apaguna" VALUES(22,54,'22:54','Ammas akka isaan beekumsa kennaman dhugaa Rabbii kee irraa dhufe ta’uu isaa beekanii isatti amananii, onneen isaanii isatti tasgabbii argattuuf (kana taasise)Dhugumatti, Rabbiin isaan amanan gara karaa sirrii qajeelchaadha');
INSERT INTO "apaguna" VALUES(22,55,'22:55','Hanga Qiyaamaan tasa isaanitti dhufutti yookiin (hanga) adabbiin guyyaa maseenaa (isa booda halkan hin jirree) isaanitti dhufutti isaan kafaran waa’ee isaa (Qur’aanaa) irraa shakkuu irraa hin deeman.');
INSERT INTO "apaguna" VALUES(22,56,'22:56','Mootummaan guyyaa san kan AllaahtiGidduu isaaniitti ni murteessaIsaan amananii, gaggaarii hojjatan Jannata qananii keessa ta’u.');
INSERT INTO "apaguna" VALUES(22,57,'22:57','Isaan kafaranii, keeyyattoota keenya sobsiisan, warri sun adabbii (jahannam kan) salphisaa ta’etu isaaniif jira.');
INSERT INTO "apaguna" VALUES(22,58,'22:58','Isaan karaa Rabbii keessatti godaananii, ergasii ajjeefaman yookiin du’an, dhugumatti, Rabbiin kennaa gaarii isaanii kennuuf jiraDhugumatti, Rabbiin Isatu irra caalaa kennitootaati.');
INSERT INTO "apaguna" VALUES(22,59,'22:59','Dhugumatti, Inni isaan seensa isa jaallatan seensisaDhugumatti, Rabbiin beekaa, obsaadha.');
INSERT INTO "apaguna" VALUES(22,60,'22:60','(Dubbiin) akkanaNamni fakkaataa waan ittiin miidhameetiin haaloo bahatee, ergasii isa irratti itti roorrifame, dhugumatti, Rabbiin isa tumsaDhugumatti, Rabbiin irra namaaf dabraa, araaramaadha');
INSERT INTO "apaguna" VALUES(22,61,'22:61','Sun waan Rabbiin halkan guyyaa keessa seensisuufi guyyaas halkan keessa seensisa ta''eefiDhugumatti, Rabbiin dhaga’aa, argaadha.');
INSERT INTO "apaguna" VALUES(22,62,'22:62','Kun waan Rabbiin, Inni (gabbaramaa) dhugaa ta’ee, wantoonni isaan Isaa gaditti gabbaran immoo isa sobaa ta’ee, ammas Rabbiin Isatu ol ta’aa, guddaa ta''eefi.');
INSERT INTO "apaguna" VALUES(22,63,'22:63','Sila akka Rabbiin samii irraa bishaan buusee, dachiin (isaan) magariisa taate hin arginee? Dhugumatti, Rabbiin rahmata godhaa, keessa beekaadha.');
INSERT INTO "apaguna" VALUES(22,64,'22:64','Wantoonni samii keessa jiraniifi wantoonni dachii keessa jiran hundi kan IsaatiDhugumatti, Rabbiin Isatu dureessa, faarfamaadha');
INSERT INTO "apaguna" VALUES(22,65,'22:65','Sila akka Allaahn wantoota dachii keessa jiraniifi dooniwwan galaana keessa daakan ajaja Isaatiin isiniif mijeesse hin arginee? Eeyyama Isaatiin yoo ta''e malee akka samiin dachii irratti hin kufne qabaRabbiin namootaaf akkaan mararfataa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(22,66,'22:66','Inni Isa isin jiraachisee, sana booda isin ajjeesee, Sana booda isin jiraachisuudhaDhugumatti, namni akkaan (Rabbitti) kafaraadha.');
INSERT INTO "apaguna" VALUES(22,67,'22:67','Ummata hundaaf seera isaan amantii ittiin geggeeffatan isaaniif taasifnee jirraKanaafuu (mushrikoonni Makkaa) dhimma kana irratti akka siin hin falmineGara Gooftaa keetiittis waamiDhugumatti, ati karaa qajeelaa irra jirta.');
INSERT INTO "apaguna" VALUES(22,68,'22:68','Yoo siin falmanis “Rabbiin akkaan beekaa waan isin hojjattaniiti” jedhiin.');
INSERT INTO "apaguna" VALUES(22,69,'22:69','Rabbiin Guyyaa Qiyaamaa waan isin isa keessatti wal dhabaa turtan gidduu keessanitti murteessa.');
INSERT INTO "apaguna" VALUES(22,70,'22:70','Sila Rabbiin wantoota samiifi dachii keessa jiran hunda akka beeku hin beektuu? Dhugumatti, kun kitaaba (Lawhal Mahfuuz) keessa jiraDhugumatti, kun Rabbi irratti laafaadha.');
INSERT INTO "apaguna" VALUES(22,71,'22:71','Rabbiin alatti waan Inni ragaa homaatuu isaaf hin buusiniifi waan isaanis isa ilaalchisee beekumsa homaatuu hin qabne gabbaruMiidhaa hojjattootaaf gargaaraan tokkos hin jiru');
INSERT INTO "apaguna" VALUES(22,72,'22:72','Yeroo keeyyattoonni keenya kan ifa ta’an isaan irratti dubbifamanis, fuula warra kafaranii irraa (ajaja Rabbii) diduu hubattaWarra keeyyattoota keenya isaan irratti dubbisanitti utaaluutti dhihaatu(Yaa Muhammad!) “Sila waan sana caalaa hamaa ta’e isiniif himuu? Ibidda Rabbiin isaan kafaraniif waadaa galeedhaDeebichi waa fokkate!” jedhi.');
INSERT INTO "apaguna" VALUES(22,73,'22:73','Yaa namootaa! Fakkeenyi taasifamee jiraa isa dhaggeeffadhaaDhugumatti, isaan isin Rabbiin alatti kadhattan (gabbartan) odoo isaaf walitti qabamaniillee tiisisa tokko hin uumanuYoo tiisisni waan tokko isaan harkaa butes, (waan) san isa irraa buusuu hin danda’anuGabbaraafi gabbaramaan dadhabeera.');
INSERT INTO "apaguna" VALUES(22,74,'22:74','Dhugaa guddina Isaa Rabbiin hin guddifneDhugumatti, Rabbiin jabaa, injifataadha');
INSERT INTO "apaguna" VALUES(22,75,'22:75','Rabbiin maleykota irraa ergamoota filata; namoota irraas (ni filata)Dhugumatti, Rabbiin dhaga’aa, argaadha.');
INSERT INTO "apaguna" VALUES(22,76,'22:76','Inni wantoota fuuldura isaanii jiraniifi wantoota duuba isaanii jiran hunda ni beekaWantoonni hundi gara Rabbiitti deebifamti.');
INSERT INTO "apaguna" VALUES(22,77,'22:77','Yaa warra amantan! Rukuu’a bu’aa; sujuudas bu’aa; Gooftaa keessanis gabbaraa; akka milkooftaniif toltuus hojjadhaa.');
INSERT INTO "apaguna" VALUES(22,78,'22:78','Qabsoo Isaaf malu (Karaa) Rabbii keessatti qabsaa’aa; Inni isin filateera; amantii keessatti dhiphina tokkollee isin irratti hin gooneKaraa abbaa keessan Ibraahiim (deemaa)Akka ergamaan Rabbii isin irratti ragaa ba’uufi isinis namoota irratti ragaa baataniif Isatu (Rabbitu) kana duraafi (Qur’aana) kana keessattis Muslimoota isin moggaaseKanaaf salaata dhaabaa; zakaa kennaa; Rabbiin ciminaan qabadhaaInni jaalalloo keessaniJaalallee ta''uuf waa tolee! Gargaaraa ta''uufis waa tole!');
INSERT INTO "apaguna" VALUES(23,1,'23:1','Dhugumatti, mu’uminoonni milkaa’an.');
INSERT INTO "apaguna" VALUES(23,2,'23:2','Isaan warra salaata isaanii keessatti khushuu’a qabaniidha');
INSERT INTO "apaguna" VALUES(23,3,'23:3','Isaan warra wanta bu’aa hin qabne irraa garagalaniidha.');
INSERT INTO "apaguna" VALUES(23,4,'23:4','Isaan warra zakaas raawwataniidha.');
INSERT INTO "apaguna" VALUES(23,5,'23:5','Isaan warra qaama saalaa isaanii tikfatoo ta’aniidha.');
INSERT INTO "apaguna" VALUES(23,6,'23:6','Haadha manaa isaanii yookiin waan mirgi isaanii dhuunfatte irratti maleeDhugumatti, isaan komatamoo miti.');
INSERT INTO "apaguna" VALUES(23,7,'23:7','Namni sanaan ala (waan biraa) barbaade, isaan sun daangaa dabroodha.');
INSERT INTO "apaguna" VALUES(23,8,'23:8','Isaan warra amaanaa isaaniitiifi waadaa isaaniis tikfatoo ta’aniidha.');
INSERT INTO "apaguna" VALUES(23,9,'23:9','Isaan warra salaata isaanii irratti tikfataniidha');
INSERT INTO "apaguna" VALUES(23,10,'23:10','Isaan sun isaanuma qofatu dhaaltota (jannataati).');
INSERT INTO "apaguna" VALUES(23,11,'23:11','Isaan ishee keessatti hafoo ta''anii Firdawsiin dhaalu.');
INSERT INTO "apaguna" VALUES(23,12,'23:12','Dhugumatti, Nuti ilma namaa qulqulluu (cunfaa) dhoqqee irraa ta''e irraa uumne.');
INSERT INTO "apaguna" VALUES(23,13,'23:13','Sana booda coba bishaan saalaa kan iddoo tasgabbaa’aa, mijaa''aa (gadaamessa) keessatti isa taasifne.');
INSERT INTO "apaguna" VALUES(23,14,'23:14','Sana booda bishaan saalaa dhiiga ititaa taasifne; ergasii dhiiga ititaa muraa foonii taasifne; ergasii foonittii (sana) lafee taasifne; ergasii lafees foon itti uffifne; sana booda uumama biraa (daa’ima) isa gooneRabbiin irra tolchaa uumtotaa ta’e qulqullaa’e!');
INSERT INTO "apaguna" VALUES(23,15,'23:15','Isin sana booda du’oodha.');
INSERT INTO "apaguna" VALUES(23,16,'23:16','Sana booda dhugumatti, isin Guyyaa Qiyaamaa ni kaafamtu');
INSERT INTO "apaguna" VALUES(23,17,'23:17','Ammas dhugumatti, Nuti gubbaa keessanitti samii torba uumnee jirraNuti uumuu irraa kan dagannu hin taane');
INSERT INTO "apaguna" VALUES(23,18,'23:18','Samii irraas bishaan (rooba) hanga barbaachisaa ta’een buufnee ergasii dachii keessa isa kuufneDhugumatti, Nuti isa deemsisuu irrattis danda’oodha.');
INSERT INTO "apaguna" VALUES(23,19,'23:19','Isa saniin iddoowwan ashaakiltii kan mukkeen timiraafi inabaa kan isaan keessa isiniif fuduraaleen heddu jiraniifi isinis ishee irraa nyaattan isiniif magarsine');
INSERT INTO "apaguna" VALUES(23,20,'23:20','Muka gaara Xuuru siinaa irraa baatu (margitu), kan dhadhaa dibatamu biqilchituufi warra nyaataniifis ittoo taatus (isiniif uumne).');
INSERT INTO "apaguna" VALUES(23,21,'23:21','Dhugumatti, isiniif beyladoota keessa waan xiinxaltantu jiraWaan garaa isaanii keessa jiru irraas (aannan) isin obaafnaAmmas isiniif isaan keessa bu’aalee (biraa) baay’eetu jiraIsaan irraas ni nyaattu');
INSERT INTO "apaguna" VALUES(23,22,'23:22','Isheefi dooniiwwan irrattis baadhatamtu.');
INSERT INTO "apaguna" VALUES(23,23,'23:23','Dhugumatti, Nuti Nuuhiin gara ummata isaa erginee jirraInnis ni jedhe: “Yaa ummata kiyya! Allaah gabbaraaIsa malee gabbaramaan biraa isiniif hin jiruSila isin (Allaah) hin sodaattanii?”');
INSERT INTO "apaguna" VALUES(23,24,'23:24','Qondaaltonni ummata isaa irraa kafaran ni jedhan: “Kun namuma akka keessaniiti malee waan biraa hin taaneIsin irra of caalchisuu fedhaRabbiin odoo fedhee silaa maleykota buusa tureWaan kana nuti abbootii keenya durii keessattis hin dhageenye.');
INSERT INTO "apaguna" VALUES(23,25,'23:25','Inni nama maraatummaan itti jiru malee waan biraa mitiKanaafuu hanga yeroo murtaa’ee isa eegaa.”');
INSERT INTO "apaguna" VALUES(23,26,'23:26','(Nuuhis) ni jedhe: “Gooftaa kiyya! Waan ana sobsiisaniif isaan irratti na gargaari.”');
INSERT INTO "apaguna" VALUES(23,27,'23:27','Nuti (akkana jechuun): gara isaatti wahyii goone: “Ilaalchaafi ajaja keenyaan doonii hojjadhuYeroo ajajni keenya dhufee, badaa ibiddaa (jalaan bishaan) madde, waan hunda irraa gosoota lama lama (kormaafi dhalaa) ishee (doonii san) keessa seensisiMaatii keetis (seensisi) nama isaan irraa jechi (adabbiin) keenya duraan irratti dabre maleeWaa’ee warra miidhaa hojjatanii keessatti homaa ana hin gaafatinDhugumatti, isaan bishaaniin nyaatamoodha.');
INSERT INTO "apaguna" VALUES(23,28,'23:28','Yeroo atiifi namoonni si waliin jiran doonii gubbaa baatan “Faaruun kan Rabbii Isa namoota miidhaa hojjatan irraa nu baraarseeti” jedhi');
INSERT INTO "apaguna" VALUES(23,29,'23:29','Ammas jedhi: “Gooftaa kiyya! Qubsuma barakaa qabu na qubsiisi; Atis irra caalaa qubsiiftotaati.”');
INSERT INTO "apaguna" VALUES(23,30,'23:30','Dhugumatti, (adaba ummata Nuuh) kana keessa mallattooleetu jira; Nuti dhugumatti (ummata Nuuhii) qormaatoo taanee jirra.');
INSERT INTO "apaguna" VALUES(23,31,'23:31','Sana boodas, isaan duubaan dhaloota biroo uumne.');
INSERT INTO "apaguna" VALUES(23,32,'23:32','Isaan keessatti ergamaa isaan irraa ta’e erginee “Rabbiin gabbaraa; Isa malee Gooftaan biraa isiniif hin jiruSila isin (Allaah) hin sodaattanuu?” (jenne).');
INSERT INTO "apaguna" VALUES(23,33,'23:33','Ummata isaa kanneen kafaran, kan qunnamtii Aakhiraa sobsiisaniifi jireenya addunyaa keessatti Nuti isaan garmalee duroomsine irraa qondaalonni ni jedhan: “Kun namuma akka keessaniiti malee waan biraatii mitiWaan isin nyaattan irraa nyaata; waan isin dhugdan irraas dhuga');
INSERT INTO "apaguna" VALUES(23,34,'23:34','Yoo nama akka keessaniitiif ajajamtan, dhugumatti isin yoos hoonga’oodha.');
INSERT INTO "apaguna" VALUES(23,35,'23:35','Sila isin erga duutanii biyyeefi lafee taatanii booda deebitanii (qabrii keessaa) baafamtu (jedhee) isin baallamaa?');
INSERT INTO "apaguna" VALUES(23,36,'23:36','Wanti isin ittiin dinniinamtan (qabrii irraa kaafamuun) fagaate! fagaate!');
INSERT INTO "apaguna" VALUES(23,37,'23:37','Isheen jireenya keenya addunyaa malee kan biraatii mitiNuti ni duuna; ni jiraannasNuti kaafamoo miti.');
INSERT INTO "apaguna" VALUES(23,38,'23:38','Inni namicha Rabbi irratti soba odeessu malee waan biraa mitiNutis isaaf amanoo hin taane.”');
INSERT INTO "apaguna" VALUES(23,39,'23:39','[Inni] “Gooftaa kiyya! Waan na sobsiisaniif na tumsi” jedhe');
INSERT INTO "apaguna" VALUES(23,40,'23:40','(Rabbiinis) “Yeroo muraasa booda, isaan dhugumatti gaabbitoota ta’uuf jiru” jedhe');
INSERT INTO "apaguna" VALUES(23,41,'23:41','Iyyi cimaan dhugaadhaan isaan qabatteYoosuu hoomacha isaan gooneUmmatoota miidhaa hojjattootaaf (rahmata Rabbii irraa) fageenyi haa ta’u.');
INSERT INTO "apaguna" VALUES(23,42,'23:42','Ergasii, isaan booda dhaloota biroo uumne.');
INSERT INTO "apaguna" VALUES(23,43,'23:43','Ummanni kamillee beellama ishee hin dabartu; boodas hin aantu.');
INSERT INTO "apaguna" VALUES(23,44,'23:44','Sana booda ergamtoota keenya walduraa duubaan ergineDhawaatuma ummata tokkotti ergamaan ishee dhufuun isa sobsiisuKanaafuu (badii keessatti) garii isaanii garii hordofsiifneOduu odeeffamu isaan taasifneNamoota hin amanneef fageenyi haa ta’u!');
INSERT INTO "apaguna" VALUES(23,45,'23:45','Sana booda Muusaafi obboleessa isaa Haaruuniin raajiiwwan keenyaafi ragaa ifa galaan ergine.');
INSERT INTO "apaguna" VALUES(23,46,'23:46','Gara Fir’awniifi qondaaltota isaa (ergine)Isaan garuu ni boonan; namoota ol aanoo of taasisan');
INSERT INTO "apaguna" VALUES(23,47,'23:47','Ni jedhan: “Sila namoota lama kan nu fakkaataniifi ummanni isaanii nuuf gabbartoota ta''an isaaniif amannaa?"');
INSERT INTO "apaguna" VALUES(23,48,'23:48','Kanaafuu isaan lamaan ni sobsiisanWarra balleeffaman irraas ta’an');
INSERT INTO "apaguna" VALUES(23,49,'23:49','Akka isaan (haqatti) qajeelaniif jecha dhugumatti, Muusaaf kitaaba kenninee jirra');
INSERT INTO "apaguna" VALUES(23,50,'23:50','Ilma Maryamiifi haadha isaatis mallattoo taasifneIsaan lamaanuu gara iddoo ol fuudhamaa; tasgabbiifi burqituu isaan qubsiifnee jirra.');
INSERT INTO "apaguna" VALUES(23,51,'23:51','Yaa ergamtoota (kiyya)! Wantoota gaggaarii irraa nyaadhaaDalagaalee gaggaariis hojjadhaaDhugumatti, Ani waan isin hojjattan akkaan beekaadha.');
INSERT INTO "apaguna" VALUES(23,52,'23:52','Isheen kun (Rabbii tokkicha gabbaruun) karaa tokko taatee amantii keessaniAnis Gooftaa keessanii ana sodaadhaa');
INSERT INTO "apaguna" VALUES(23,53,'23:53','Amantii isaaniis gidduu isaanitti addaan cicciratanGareen hundinuu waan isaan bira jirutti gammadu.');
INSERT INTO "apaguna" VALUES(23,54,'23:54','Hanga yeroo murtaa''eetti jallina isaanii keessatti isaan dhiisi.');
INSERT INTO "apaguna" VALUES(23,55,'23:55','Sila sababni Nuti qabeenyaafi ilmaan irraa isaaniif daballu (isaaniif gaarii)se’uu?');
INSERT INTO "apaguna" VALUES(23,56,'23:56','Toltuuwwan isaaniif ariifachiifna (se''uu?) Lakkii; (Nuti kanaaf hin goone)Garuu isaan hin hubatan.');
INSERT INTO "apaguna" VALUES(23,57,'23:57','Dhugumatti, warri sodaa Gooftaa isaanii irraa naasuun jiraatan,');
INSERT INTO "apaguna" VALUES(23,58,'23:58','Warri keeyyattoota Gooftaa isaaniittis amanan,');
INSERT INTO "apaguna" VALUES(23,59,'23:59','Warri Gooftaa isaaniittis homaa hin qindeessine,');
INSERT INTO "apaguna" VALUES(23,60,'23:60','Ammas warri waan kennan onneen isaanii sodaan guutamteefi dhugumaan (qormaataaf) gara Gooftaa isaanii akka deebi’an amanaa kennan,');
INSERT INTO "apaguna" VALUES(23,61,'23:61','Isaan sun hojiiwwan gaggaarii keessatti akkaan ariifatoodha; isaan isheedhaaf dorgomoodha.');
INSERT INTO "apaguna" VALUES(23,62,'23:62','Nuti nama tokkos dandeettii isaatiin ala isa hin dirqisiifnuNu bira kitaaba dhugaa dubbatutu jiraIsaanis hin miidhamanu.');
INSERT INTO "apaguna" VALUES(23,63,'23:63','Dhugumatti onneen isaanii (Qur’aana) kana irraa haguuggaa keessa jirtiIsaaniif kana malees dalagaalee (gadhee) biraa kan isaan hojjatantu jira.');
INSERT INTO "apaguna" VALUES(23,64,'23:64','Yeroma Nuti isaan irraa warra jireenya qananii daangaa dabre keessa jiraachaa turan adabaan qabnu, yeroma san isaan (deeggarsa kadhaaf) sagalee ol fuudhanii iyyu.');
INSERT INTO "apaguna" VALUES(23,65,'23:65','Har’a hin iyyinaaDhugumatti, isin Nu irraa tumsa hin argattanu');
INSERT INTO "apaguna" VALUES(23,66,'23:66','Dhugumatti, keeyyattoonni kiyya isin irratti dubbifamaa turaniiti, isin duubatti irraa deebi’aa turtan.');
INSERT INTO "apaguna" VALUES(23,67,'23:67','Isaan (Harama makkaatiin) warra boonan taatanii halkanis ka''abaa biratti (Qur''aana) arrabsitu.');
INSERT INTO "apaguna" VALUES(23,68,'23:68','Sila jecha (Rabbii) hin xiinxalanuu? Moo waan abbootii isaanii kan jalqabaatti hin dhufintu isaanitti dhufe?');
INSERT INTO "apaguna" VALUES(23,69,'23:69','Ergamaa isaanii hin beekinii, isa sobsiisan moo?');
INSERT INTO "apaguna" VALUES(23,70,'23:70','“Moo maraatummaatu isatti jira” jedhu? Lakkii; odoo irra baay’een isaanii dhugaa jibbanuu inni dhugaan isaanitti dhufe.');
INSERT INTO "apaguna" VALUES(23,71,'23:71','Odoo dhugaan fedha lubbuu isaanii hordofee, silaa samii, dachiifi wantoonni isaan keessa jiranis ni badu turanInumaayyuu; nuti Qur''aana kabajaan isaanii keessa jiru isaaniif kennineeti, isaan kabajaa isaanii irraa garagalu.');
INSERT INTO "apaguna" VALUES(23,72,'23:72','Moo gatii akka siif kafalan isaan gaafatta? Gatii Gooftaa keetiitu caalaInnis irra caalaa warra kennaniiti.');
INSERT INTO "apaguna" VALUES(23,73,'23:73','Dhugumatti, ati gara karaa qajeelaatti isaan waamta.');
INSERT INTO "apaguna" VALUES(23,74,'23:74','Dhugumatti Isaan Aakhiraatti hin amanne karaa (qajeelaa) irraa akkaan jallatoodha.');
INSERT INTO "apaguna" VALUES(23,75,'23:75','Odoma rahmata isaaniif goonee, rakkoo isaan irra jirus irraa kaafnee, dhugumatti isaan dhama''anii jallina isaanii keessa jiraatu.');
INSERT INTO "apaguna" VALUES(23,76,'23:76','Dhugumatti, Nuti adabaan isaan qabnee jirraIsaan Gooftaa isaaniitiif gadi of hin qabne; Isattis hin watwaanne');
INSERT INTO "apaguna" VALUES(23,77,'23:77','Yeroo balbala adabbii cimaa qabu isaan irratti banne, yeroma san isaan abdii muratoodha.');
INSERT INTO "apaguna" VALUES(23,78,'23:78','Innis Isa dhageettii, argaafi onnee isiniif uumeedhaWanti isin galateeffattan xiqqoo qofa');
INSERT INTO "apaguna" VALUES(23,79,'23:79','Innis Isa dachii keessa isin facaaseedhaGara Isaattis walitti qabamtu.');
INSERT INTO "apaguna" VALUES(23,80,'23:80','Innis Isa jiraachisuufi ajjeesuudhaJijjiiramni halkaniifi guyyaas kan IsaatiSila hin hubattanii?');
INSERT INTO "apaguna" VALUES(23,81,'23:81','Fakkaatuma waan warri duraanii jedhanii jedhan.');
INSERT INTO "apaguna" VALUES(23,82,'23:82','Ni jedhan: “Sila erga duunee biyyeefi lafee taane booda, dhugumatti nuti (deebinee) kaafamnaa?');
INSERT INTO "apaguna" VALUES(23,83,'23:83','Dhugumatti, nuufi abbootiin keenyas kana dura kana waadaa galamnee jirraKun ogbarruu warra durii malee waan biraatii miti.”');
INSERT INTO "apaguna" VALUES(23,84,'23:84','Jedhi: “Yoo kan beektan taatan dachiiniifi isaan ishee keessa jiran kan eenyuti?”');
INSERT INTO "apaguna" VALUES(23,85,'23:85','“Kan Rabbiiti” jedhu“Sila isin hin gorfamtanuu?" jedhi');
INSERT INTO "apaguna" VALUES(23,86,'23:86','“Gooftaan samii torbaniifi Gooftaan Arshii guddaa eenyu?” jedhi.');
INSERT INTO "apaguna" VALUES(23,87,'23:87','“Kan Rabbiiti” jechuuf jiru“Sila isin Allaah hin sodaattanuu?” jedhiin.');
INSERT INTO "apaguna" VALUES(23,88,'23:88','“Inni mootummaan waan hundaa harka Isaa jiru innis kan tiksu; isa irratti kan hin tikfamne, yoo kan bektan taatan eenyu?” jedhiin.');
INSERT INTO "apaguna" VALUES(23,89,'23:89','“Kan Allaahti” jedhu“Akkam (dhugaa) irraa garagalfamtu ree?” jedhiin.');
INSERT INTO "apaguna" VALUES(23,90,'23:90','Inumaayyu, Nuti dhugaan isaanitti dhufnee jirraDhugumatti, isaanis sobdoota.');
INSERT INTO "apaguna" VALUES(23,91,'23:91','Rabbiin ilma hin godhanneIsa waliinis gabbaramaan tokkoyyuu hin jiru(Odoo jiraatanii) silaa gabbaramaan hundi waan uumeen deemaGariin isaanii garii irratti ol ta’u turanRabbiin waan isaan itti fakkeessan (hunda) irraa qulqullaa’e!');
INSERT INTO "apaguna" VALUES(23,92,'23:92','Beekaan waan fagoofi dhihoo sun (qulqullaa’e)Waan isaan itti dabalan irraas ol ta’e!');
INSERT INTO "apaguna" VALUES(23,93,'23:93','Jedhi: “Gooftaa kiyya! Yoo (adabbii) isaaniif waadaa galame natti agarsiifte,');
INSERT INTO "apaguna" VALUES(23,94,'23:94','Gooftaa kiyya! (adaba irraa na baraarsi); ummata miidhaa hojjattoota keessas na hin taasisin.”');
INSERT INTO "apaguna" VALUES(23,95,'23:95','Dhugumatti, Nutis waan isaaniif waadaa galle si argisiisuu irratti danda’oodha');
INSERT INTO "apaguna" VALUES(23,96,'23:96','Hamtuu kamuu ishee irra gaariidhaan deebisiNuti waan isaan jedhan akkaan beekoodha');
INSERT INTO "apaguna" VALUES(23,97,'23:97','Jedhi: “Gooftaa kiyya! Ani hasaasa sheyxaanotaa irraa Sittin maganfadha!');
INSERT INTO "apaguna" VALUES(23,98,'23:98','Gooftaa kiyya! Isaan natti dhufuu irraa Sittin maganfadha.”');
INSERT INTO "apaguna" VALUES(23,99,'23:99','Yeroo tokkoon isaaniitti duuti dhufu ni jedha: “Gooftaa kiyya! Na deebisi');
INSERT INTO "apaguna" VALUES(23,100,'23:100','Ani waaniin dhiise keessatti gaarii hojjachuun kajeela.” Lakkii! Isheen kun jecha inni ishee jedhu (qofa)Hanga guyyaa kaafamaatti, isaan duuba cufaatu jira.');
INSERT INTO "apaguna" VALUES(23,101,'23:101','Yeroo garriin (xurumbaan) afuufame, guyyaa san firummaan gidduu isaanii hin jiru; walis hin gaafatan.');
INSERT INTO "apaguna" VALUES(23,102,'23:102','Namni madaalli (hojii gaarii) isaa ulfaatte, isaan sun warra milkaa’oodha');
INSERT INTO "apaguna" VALUES(23,103,'23:103','Namni madaalli (hojii gaarii) isaa salphattes, isaan sun warra lubbuu isaanii hoongessanJahannam keessas yeroo hunda jiraatu');
INSERT INTO "apaguna" VALUES(23,104,'23:104','Ibiddi fuula isaanii ni gubdiIsaanis achi keessatti fuulaa fokkatoota.');
INSERT INTO "apaguna" VALUES(23,105,'23:105','Sila keeyyattoonni kiyya isin irratti dubbifamaa turtee, isinis isaan sobsiisaa hin turree?');
INSERT INTO "apaguna" VALUES(23,106,'23:106','Isaanis ni jedhan: “(Yaa) Gooftaa keenya! Gogiinsa keenyatu nu injifate; nus ummata jallatoo taane.');
INSERT INTO "apaguna" VALUES(23,107,'23:107','Gooftaa keenya! Ishee irraa nu baasiErgasii yoo itti deebine, dhugumatti nutu miidhaa hojjattoota.”');
INSERT INTO "apaguna" VALUES(23,108,'23:108','(Rabbiinis) "Salphadhaatii ishee (jahannam) keessa fagaadhaaAnas hin dubbisinaa" jedha');
INSERT INTO "apaguna" VALUES(23,109,'23:109','Dhugumatti, gareen gabroota kiyya irraa ta’e tokko “Gooftaa keenya! Amannee jirraKanaafuu nuuf araaramii, rahmataas nuuf godhiAti irra caalaa warra rahmata godhaniiti” jedhaa turaniiti,');
INSERT INTO "apaguna" VALUES(23,110,'23:110','Isinis hanga yaadannoo (faaruu) kiyya isin dagachiisanitti isaanitti qishnaa (baacaa) turtanIsaanittis kolfaa turtan');
INSERT INTO "apaguna" VALUES(23,111,'23:111','Dhugumatti, Ani har’a waan isaan obsaniif isaan milkaa’oo ta’uudhaan isaaniif galata galche');
INSERT INTO "apaguna" VALUES(23,112,'23:112','(Rabbiin) ni jedhe: “Sila dachii keessa lakkoofsa waggaa hangamii jiraattan?”');
INSERT INTO "apaguna" VALUES(23,113,'23:113','[Isaan] ni jedhan: “Nuti guyyaa tokko yookiin garii guyyaa jiraanneWarra lakkaa’an gaafadhu.”');
INSERT INTO "apaguna" VALUES(23,114,'23:114','(Rabbiin) ni jedha: “Odoo kan beektan taatanii isin (yeroo) xiqqoo malee hin jiraanne.');
INSERT INTO "apaguna" VALUES(23,115,'23:115','Sila Nuti taphumaaf isin uumnee yaadduu? Ammas isin gara keenyatti deebifamuu dhabuu yaadduu”?');
INSERT INTO "apaguna" VALUES(23,116,'23:116','Rabbiin mootii dhugaa ta’e, ol ta’eIsa malee Gooftaan biraa hin jiruInni Gooftaa Arshii kabajamaati.');
INSERT INTO "apaguna" VALUES(23,117,'23:117','Namni Rabbii wajjin odoo ragaa isa irratti hin qabne, gabbaramaa biraa kadhatu (gabbaru), qormaanni isaa Gooftaa isaa bira jiraDhugumatti, warri kaafaran hin milkaa’anu.');
INSERT INTO "apaguna" VALUES(23,118,'23:118','“Gooftaa kiyya! Naaf araarami, rahmatas (naaf) godhiAti irra caalaa warra rahmata godhaniiti” jedhi.');
INSERT INTO "apaguna" VALUES(24,1,'24:1',')Isheen kun) suuraa Nuti ishee buufnee, dirqama ishee taasifnee, akka gorfamtanuuf jecha ishee keessattis keeyyattoota ifa ta’an buufneedha.');
INSERT INTO "apaguna" VALUES(24,2,'24:2','Dubartii (gaa’ilaan dura) sagaagaltuufi dhiira (odoo hin fuudhin) sagaagalus tokko tokkoon isaanii rukuttaa dhibba isaan rukutaaYoo Rabbiifi Guyyaa Aakhiraatti kan amantan taataniif, amantii (adaba yookiin haddii) Rabbii keessatti isaan lamaaniif garaa laafuun isin hin qabatinadabbii isaan lamaaniis gareen mu’uminoota irraa ta’e itti haa hirmaatu.');
INSERT INTO "apaguna" VALUES(24,3,'24:3','Dhiirri sagaagalaan sagaagaltuu yookiin dubartii mushrika taate malee hin fuudhuDubartiin sagaagaltuunis dhiira sagaagalaa yookiin dhiira mushrika ta’e malee namni biraa ishee hin fuudhuSun mu’uminoota irratti dhorgamee jira');
INSERT INTO "apaguna" VALUES(24,4,'24:4','Isaan dubartoota kabajaa ofii eegan sagaagalummaan maqaa isaanii xureessanis, ergasii ragaa afur fiduu hin dandeenye, rukuttaa saddeettama isaan rukutaaRagummaa isaaniis gonkumaa irraa hin fuudhinaaIsaan sun isaanumatu finciltoota.');
INSERT INTO "apaguna" VALUES(24,5,'24:5','Isaan sana booda tawbatanii, tolchan malee(Isaan sanaaf) dhugumatti, Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(24,6,'24:6','Isaan odoo lubbuu isaanii malee ragaan isaaniif hin jirre niitiwwan isaanii sagaagalummaan maqaa xureessanis, kakuun tokkoo isaanii ‘inni dhugaa dubbattoota irraayi’ jechuun yeroo afur Rabbiin kakachuudha.');
INSERT INTO "apaguna" VALUES(24,7,'24:7',')Ragaan) shaneessituunis yoo inni namoota sobaan maqaa ishee xureessan irraa ta’e, ‘abaarsi Rabbii isa irra haa jiraatu’ jechuun kakachuudha.');
INSERT INTO "apaguna" VALUES(24,8,'24:8','“Inni sobdoota irraayyi” jettee yeroo afur Rabbiin kakachuutu adabbii (addunyaa) ishee irraa deebisa.');
INSERT INTO "apaguna" VALUES(24,9,'24:9',')Ragaan) shanessituus ‘yoo inni (dhiirsi ishee) namoota dhugaa jedhan irraa ta’e abaarsi Rabbii ishee irra haa jiraatu’ jechuudha');
INSERT INTO "apaguna" VALUES(24,10,'24:10','Odoo tolli Rabbiitiifi rahmanni Isaa isin irra jiraachuu baatee, (silaa Rabbiin isin adabbii ture)Dhugumatti, Rabbiin akkaan tawbaa qeebalaa, ogeessa');
INSERT INTO "apaguna" VALUES(24,11,'24:11','Dhugumatti, isaan (Aa’ishaa iratti) soba fidan garee isin irraa ta’aniidhaIsa isiniif hamtuu hin se''inaaInumaa inni isiniif caalaadhaNama isaan irraa ta’e hundaafuu, hamtuu irraa wantuma inni carraaqqatetu isaaf jiraIsa isaan irraa (soba) guddaa baadhate immoo adabbii guddaa ta''etu isaaf jira.');
INSERT INTO "apaguna" VALUES(24,12,'24:12','Maaliif yeroo isa (soba kana) dhageessan, mu’uminoonni dhiiraas ta’ee kan dubaraa, ofii isaaniitiif toltuu yaadanii “Kun soba ifa bahaadha” jechuu dhaban?');
INSERT INTO "apaguna" VALUES(24,13,'24:13','Maaliif isa irratti ragaalee afur fiduu dhiisan? Eega ragootaan hin dhufin, isaan sun Allaah biratti sobdoonni isaanuma.');
INSERT INTO "apaguna" VALUES(24,14,'24:14','Odoo tolaafi rahmanni Allaah addunyaa kanaafi Aakhiraa isin irra jiraachuu baatee, silaa waan isin waa’ee isaa odeessitaniif adabbii guddaatu isin tuqa ture.');
INSERT INTO "apaguna" VALUES(24,15,'24:15','Yeroo isin arraba keessaniin olola isaa ooftanii, afaan keessaniinis waan sirritti hin beekne jettanii, odoo inni Rabbiin biratti guddaa ta’ee jiruu isin waan laafaa isa seetan san (adabni guddaan isin tuqa ture).');
INSERT INTO "apaguna" VALUES(24,16,'24:16','Sila yeroo isa (soba kana) dhageessan “waa’ee kanaa dubbachuun nuuf hin malu(Yaa Rabbi!) Ati qulqulloofte! Kun soba guddaadha” maaliif hin jenne?');
INSERT INTO "apaguna" VALUES(24,17,'24:17','Yoo mu''imintoota taataniif akka isin (gocha) kana fakkaatutti gonkumaa hin deebine Rabbiin isin gorsa.');
INSERT INTO "apaguna" VALUES(24,18,'24:18','Allaah’n keeyyattoota Isaa isiniif ibsaRabbiinis beekaa, ogeessa.');
INSERT INTO "apaguna" VALUES(24,19,'24:19','Dhugumatti, isaan gochi fokkataan isaan amanan keessatti tamsa’uu jaallatan, isaaniif addunyaafi Aakhiraa keessatti adabbii laalessaa ta’etu (isaaniif) jiraRabbumatu beeka; isin hin beektanu');
INSERT INTO "apaguna" VALUES(24,20,'24:20','Odoo tolaafi rahmanni Allaah isin irra jiraachuu baatee, ammaas odoo Rabbiin mararfataa rahmata godhaa ta’uu baatee, (silaa isin adabbii ture).');
INSERT INTO "apaguna" VALUES(24,21,'24:21','Yaa warra amantan! Tarkaanfiiwwan shayxaanaa hin hordofinaaNamni tarkaanfiilee shayxaanaa hordofe, inni (sheyxaanni) gocha fokkataafi gocha jibbamaa ta’anitti ajajaOdoo tolli Rabbiitiifi rahmanni Isaa isin irra jiraachuu baatee, silaa tokkoon keessanis gonkumaa badii irraa hin qulqullooftanu tureGaruu Rabbiin nama fedhe ni qulqulleessaRabbiin dhaga’aa, beekaadha');
INSERT INTO "apaguna" VALUES(24,22,'24:22','Isin irraa namoonni tolaafi qabeenyaan badhaadhan firoota isaanii, hiyyeeyyiifi namoota karaa Rabbii keessatti godaananiif kennuu diduu irratti hin kakatinDhiifama haa godhanu; namaafis irra haa dabranuSila Rabbiin isiniif araaramuu hin jaallattanuu? Allaahnis araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(24,23,'24:23','Dhugumatti, isaan dubartoota tikfamoo, sagaagalummaa irraa dagatoofi mu’uminoota ta’an maqaa xureessan, addunyaafi Aakhiraattis abaaramaniiruIsaaniif adabbii guddaatu jira');
INSERT INTO "apaguna" VALUES(24,24,'24:24','Guyyaa Arrabni isaanii, harki isaaniitiifi miilli isaanii waan isaan dalagaa turan ragaa isaan irratti ba’an (adabamu).');
INSERT INTO "apaguna" VALUES(24,25,'24:25','Guyyaa san Rabbiin mindaa (hojii) isaanii kan dhugaa guutee isaaniif kennaIsaanis Rabbiin dhugaa, ifa baasaa ta’uu ni beeku.');
INSERT INTO "apaguna" VALUES(24,26,'24:26','Dubartoonni badoon dhiirota badoofi, dhiironni badoonis dubartoota badoof ta’uDubartoonni gaariin dhiirota gaariifi, dhiironni gaggaariinis dubartoota gaariifiIsaan sun waan isaan jedhan irraa qulqulleeffamoodhaIsaaniif araaramaafi kennaa gaarii ta’etu (Jannatatu) jira.');
INSERT INTO "apaguna" VALUES(24,27,'24:27','Yaa warra amantan! Mana keessan hin ta’in hanga hayyamsiifattaniifi abbootii isaanii nagaa gaafattanitti hin seeninaaAkka gorfamtaniif jecha santu isiniif caala.');
INSERT INTO "apaguna" VALUES(24,28,'24:28','Yoo ishee keessatti nama tokkos arguu baattan, hanga isiniif hayyamamutti ishee hin seeninaaYoo deebi’aa isiniin jedhames deebi’aaSantu isiniif irra qulqulluudhaRabbiinis waan isin dalagdan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(24,29,'24:29','Mana namni keessa hin jiraanne kan ishee keessa meeshaan keessan jiru (hayyama malee) yoo seentan badiin isin irra hin jiruRabbiin waan isin mul’iftaniifi waan isin dhoksitanis ni beeka.');
INSERT INTO "apaguna" VALUES(24,30,'24:30','Mu’uminootaan “(ija keessan gadi qabadhaa)” jedhi, ija isaanii gadi qabatu; qaama saalaa isaaniis ni tikfatuSantu isaaniif irra qulqulluudhaDhugumatti, Rabbiin keessa beekaa waan isaan dalaganiiti.');
INSERT INTO "apaguna" VALUES(24,31,'24:31','Dubartoota mu’uminootaatiinis “(ija keessan gadi qabadhaa”) jedhi, ija isaanii gadi qabatu; qaama saalaa isaaniis ni tikfatu“Faaya isaaniis isa isaan irraa ofiin mul’ate malee hin mul’isinaa; haguuggaa isaanii qoma isaaniitti haa uffisanu; miidhagina qaama isaanii dhiirsa isaanii, yookiin abbootii isaanii, yookiin abbootii dhiirsa isaanii, yookiin ilmaan isaanii, yookiin ilmaan dhiirsa isaanii, yookiin obboleeyyan isaanii, yookiin ilmaan obboleeyyan isaanii, yookiin ilmaan obboleettiiwwan isaanii, yookiin dubartoota isaanii (mu’uminoota ta’an), yookiin gabroota too’annaa isaanii jala jiran, yookiin tajaajiltoota dhiira irraa ta’an kan fedhii hin qabne ta’aniifi yookiin ijoollee qaama saala dubartootaa irratti hin mul’atinitti (hin beekinitti) malee miidhagina isaanii hin mul’isinMiidhagina isaanii irraa wanti isaan dhoksan akka beekamuuf jecha miila isaaniitiin dachii hin dhiitinYaa mu’uminootaa! Akka milkooftaniif jecha hundi keessanuu gara Rabbiitti tawbaa deebi’aa.” jedhiin.');
INSERT INTO "apaguna" VALUES(24,32,'24:32','Isin irraas konfoleysa (dhiiraas ta’ee dubaratii hin fuudhin) fuusisaaGabroota keessan kan dhiiraatiifi dubartootaa irraas ta’an isaan gaggaarii (fuusisaa)Yoo hiyyeeyyii ta’anis Rabbiin tola Isaa irraa isaan duroomsaRabbiin dureessa, beekaadha.');
INSERT INTO "apaguna" VALUES(24,33,'24:33','Isaan waan fuudhaaf isaan barbaachisu hin argatin hanga Rabbiin tola Isaa irraa isaan duroomsutti, kabajaa ofii haa eeggatanGabroota too’annaa keessan jala jiran gidduus yoo isaan keessatti toltuu beektan, isaan barreeffama (beellamaan bilisoomuu) barbaadaniif, barreessaaQabeenya Rabbii kan Inni isiniif kenne irraas isaaniif kennaaShamarran gabrootaa keessan yoo isaan kabajaa ofii eeguu fedhan isin bu’aa jireenya addunyaatiif jecha, sagaagalummaa irratti isaan hin dirqinaaNamni isaan dirqe, dhugumatti Rabbiin erga isaan dirquutii (isaanif) araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(24,34,'24:34','Dhugumatti, Nuti keeyyattoota ifa ta’aniifi fakkeenya warra isin dura dabranii irraas, akka warra Rabbiin sodaataniifis gorsa ta’aniif jecha gara keessanitti buufnee jirra.');
INSERT INTO "apaguna" VALUES(24,35,'24:35','Rabbiin ifaa samiifi dachiitiFakkeenyi ifa Isaa akka foddaa ibsaan ishee keessa jiruutiIbsaan sun immoo birillee keessa jiraBirillichis akka urjii calaqqisaa muka barakeeffamtuu, zeytuunaa irraa taate, kan kallattii bahaas ta’ee, dhihattiis hin ta’in, kan zeytiin ishee odoo ibiddi isa tuquu baattellee ifuutti dhihaatu kan fakkaattuudha(Kana jechuun) ifaa ifaa irraati ta’ee dha! Rabbiin nama fedhe gara ifaa Isaatti qajeelchaAmmas Rabbiin namootaaf fakkeenyota taasisaRabbiin waan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(24,36,'24:36','Manneen (masjiidoota) kabajamuufi maqaan Allaah ishee keessatti dubbatamuutti (faarfamuutti) Rabbiin ajaje keessatti, ishee keessatti ganamaafi galgalas Isa qulqulleessu.');
INSERT INTO "apaguna" VALUES(24,37,'24:37','Dhiironni daldalas ta’ee gurgurtaan yaadannoo Rabbii irraa, salaata dhaabuufi zakaa kennuu irraa isaan hin dagnetu guyyaa isa keessa onneefi ijji gara gaggaltu kan sodaatan ta’anii (ishee keessatti Rabbiin qulqulleessu).');
INSERT INTO "apaguna" VALUES(24,38,'24:38','Akka Rabbiin irra gaarii waan isaan dalaganii isaaniif galata galchee, tola Isaa irraas isaaniif dabaluuf (Isa sodaatu)Rabbiin nama fedhe herrega malee soora.');
INSERT INTO "apaguna" VALUES(24,39,'24:39','Warri kafaran dalagaaleen isaanii akka siribii dirree keessa jirtuu, kan dheebotaan bishaan isa se’uutiYeroo isa bira ga’e homaa ta’uu isaa dhabe; Allaah of biratti argee (Rabbiin) galata isaa guutee isaaf galcheetiRabbiin saffisaa herregaati.');
INSERT INTO "apaguna" VALUES(24,40,'24:40','Yookiin akka dukkana galaana gadi fagoo ta’e keessa jiru, kan danbaliin gubbaa isaa danbaliin (biraa) jiru kan isa gubbaa duumessi jiru haguuguuti(Isheen kun) dukkana gariin ishee garii gubbaa jirtuudha; yeroo harka isaa baase argutti hin dhihaatuNamni Allaahn ifaa isaaf hin taasisin, ifaan homaatuu isaaf hin jiru.');
INSERT INTO "apaguna" VALUES(24,41,'24:41','Sila dhugumatti Allaah wanti samiifi dachii keessa jiraniifi allaattiinis koola (qoochoo) isaanii diriirsanii akka Isa qulqulleessan (faarsan) hin arginee? Hunduu dhugumatti, Isaaf sagaduufi Isa qulqulleessuus beekaniiruRabbiin waan isaan dalagan beekaadha.');
INSERT INTO "apaguna" VALUES(24,42,'24:42','Mootummaan samiifi dachii kan AllaahtiDeebiinis garuma Allaahti');
INSERT INTO "apaguna" VALUES(24,43,'24:43','Sila Allaahn duumessa oofee, ergasii gidduu isaa walitti qabee, ergasii tuulamaa isa taasisuu hin arginee? Sana booda bokkaa gidduu isaatii kan ba’u argitaAkkasumas samii irraa gaara ishee keessatti ta’e kan cabbii irraa ta’e irraa (cabbii) buuseeti, nama fedheen tuqee, nama fedhe irraas isa deebisaIfti hangaasuu isaa ija balleessutti dhihaata.');
INSERT INTO "apaguna" VALUES(24,44,'24:44','Rabbiin halkaniifi guyyaa gara gaggalchaDhugumatti, kana keessa namoota hubannoo qabaniif gorsatu jira.');
INSERT INTO "apaguna" VALUES(24,45,'24:45','Allaahn wantoota lubbuu qaban hunda bishaan irraa uumeIsaan irraa warra garaa isaaniitiin deeman (looyan)tu jiraIsaan irraas warra miila lamaan deemantu jiraIsaan irraas warra (miila) afuriin deemantu jiraRabbiinis waan fedhe uumaDhugumatti, Rabbiin waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(24,46,'24:46','Nuti dhugumatti keeyyattoota ifa ta’an buufnee jirraRabbiinis nama fedhe gara karaa sirrii qajeelcha.');
INSERT INTO "apaguna" VALUES(24,47,'24:47',')Munaafiqoonni) “Allaahfi ergamaatti amanne; ajajamnes” jedhaniiti, ergasii gareen isaan irraa ta’e sana booda garagalaIsaan sun, mu’uminoota miti.');
INSERT INTO "apaguna" VALUES(24,48,'24:48','Yeroo akka gidduu isaaniitti murteessuuf gara Allaahfi ergamaa Isaatti waamamanis, gareen isaan irraa ta’e yoosuu garagaloodha.');
INSERT INTO "apaguna" VALUES(24,49,'24:49','Yoo dhugaan isaaniif jiraate immoo ariifatoo ta’anii gara isaa (ergamaa) dhufu.');
INSERT INTO "apaguna" VALUES(24,50,'24:50','Sila onnee isaanii keessa dhukkubni jiramoo, Rabbiifi ergamaa Isaatu dabaan (haqa malee) isaan irratti murteessaa sodaatu? Lakkii; isaan isaanumatu miidha raawwattoota.');
INSERT INTO "apaguna" VALUES(24,51,'24:51','Jechi Mu’uminootaa yeroo akka gidduu isaaniitti murteessuuf gara Rabbiifi ergamaa isaatti waamaman “dhageenye; ajajamnes” jechuu qofaIsaan sun milkaa’oon isaanuma.');
INSERT INTO "apaguna" VALUES(24,52,'24:52','Namni Allaahfi ergamaa Isaa tole jedhee, Allaaha sodaatee, Isas sodaate, isaan sun isaanumatu milkaa’oodha.');
INSERT INTO "apaguna" VALUES(24,53,'24:53','Yoo (duulaaf) bahuutti isaan ajajje, dhugumatti, akka ba’an kakuu isaanii cimaa Rabbiin kakatu“Hin kakatinaa, ajajamuu beekamaatu (isiniif caalaa dha)” jedhiinDhugumatti, Rabbiin waan isin dalagdan keessa beekaadha');
INSERT INTO "apaguna" VALUES(24,54,'24:54','Jedhi: “Rabbiif ajajamaa; akkasuma ergamaafis ajajamaaYoo duubatti deebitan, kan isa irra jiru dirqama isa irra kaa’ame qofaIsin irras waanuma baachifamtan qofatu jiraYoo isaaf ajajamtan ni qajeeltuErgamaa irra kan jiru (itti kenname) ifaan ifatti geessuu qofa.');
INSERT INTO "apaguna" VALUES(24,55,'24:55','Rabbiin isin irraa warra amananii, gaggaarii hojjatan akkuma warra isaan duraa (dachii irratti) bakka buusee ture, dachii irratti bakka isaan buusuuf jira‘Amantii isaaniif jaalate isaaniif mijeessee, erga sodaa isaaniitii nagayatti isaaniif jijjiiraa’ waadaa galeeraOdoo humaa natti hin qindeessin Ana qofa gabbaru (waadaa gale)Sana booda warri kafare, isaanumatu finciltoota');
INSERT INTO "apaguna" VALUES(24,56,'24:56','Akka rahmanni isiniif godhamuuf jecha, salaata (sirnaan) sagadaa; zakaas kennaa; ergamaafis ajajamaa.');
INSERT INTO "apaguna" VALUES(24,57,'24:57','Isaan kafaran dachii keessatti waan miliqan hin se’inTeessoon isaaniis ibidda ta’aDeebichi waa fokkate!');
INSERT INTO "apaguna" VALUES(24,58,'24:58','Yaa warra amantan! Isaan (gabroonni) mirgi keessan dhuunfatteefi isin irraa isaan umrii saalfii (gaa’ilaa) hin ga’in yeroo sadi hayyama isin haa gaafatanu: salaata fajrii dura, yeroo walakkaa guyyaa ho’aaf uffata keessan baafattaniifi salaata ishaa’ii boodaYeroo sadi kan isin qullaa baataniidhaIsaan kanaan ala gariin keessan garii irra yoo naanna’an, isin irras ta’ee isaan irra diliin hin jiraatuAkka kanatti Rabbiin keeyyattoota (Isaa) isiniif addeessaAllaahn beekaa, ogeessa.');
INSERT INTO "apaguna" VALUES(24,59,'24:59','Ijoolleen isin irraa ta’an yeroo umrii gaa’ilaa ga’an, akkuma warri isaaniin duraa hayyamsiifataa turanitti haa hayyamsiifatanuAkka kanatti Rabbiin keeyyattoota Isaa isiniif ibsaRabbiin beekaa, ogeessa');
INSERT INTO "apaguna" VALUES(24,60,'24:60','Dubartoonni manguddooman kan heeruma hin kajeelle odoo miidhagina isaanii hin saaxilin uffata isaanii (kan gubbaa) lafa kaa’uu keessatti badiin isaan irra hin jiruGaruu tikfamuutu isaaniif irra caalaAllaahn dhaga’aa, beekaadha.');
INSERT INTO "apaguna" VALUES(24,61,'24:61',')Namaa wajjin nyaachuu keessatti) Jaamaa irra rakkinni hin jiruNaafa (hokkolaa) irrattis rakkinni hin jiruDhukkubsataa irraas rakkinni hin jiruIsin irrattiis mana keessan irraa, yookiin manneen abbootii keessanii irraa, yookiin manneen haadholii keessanii irraa, yookiin manneen obboleeyyan keessan dhiiraa irraa, yookiin manneen obboleettiwwan keessanii irraa, yookiin manneen wasiila (abbeeraawwan) keessanii irraa, yookiin manneen adaadota keessanii irraas, yookiin manneen eessumman keessanii irraas yookiin manneen haboowwan (aaddee) keessanii irraa, yookiin waan furtuun isaa harka keessan jiru irraa, yookiin (mana) hiriyyaa keessanii irraa nyaachuu keessatti rakkinni isin irra hin jiruWalii wajjinis ta’ee adda addaan nyaachuu keessatti badiin isin irra hin jiraatuYeroo manneen seentan of irratti nagaa buusaaKabajaa Rabbi biraa taate, barakeeffamtuu, qulqulluu taate (of irratti buusaa)Akka kanatti akka xiinxaltaniif Rabbiin keeyyattoota (Isaa) isiniif ibsa.');
INSERT INTO "apaguna" VALUES(24,62,'24:62','Mu’uminoonni dhugaa, isaan Rabbiifi ergamaa Isaatti amananii, yeroo isaa wajjin dhimma walitti qabaa ta’e irratti ta’an, hanga isa hayyamsiifatanitti achii hin deemneedhaDhugumatti, isaan si hayyamsiifatan, isaan sun warra Rabbiifi ergamaa Isaatti amananiidhaKanaafuu yeroo garii dhimma isaanitiif si hayyamsiifatan, isaan irraa nama feetef hayyami; Rabbi irraas araarama isaaniif kadhuDhugumatti, Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(24,63,'24:63','Waamicha ergamaa (Rabbii) gidduu keessanitti akka gariin keessan garii waamu hin taasisinaaDhugumatti, Rabbiin isaan isin irraa dhokatanii luqqa’an (ba’an) ni beekaKanaafuu isaan ajaja isaa (nabiyyii) faallessan rakkoon wahii isaan tuquu, yookiin adabbiin laalessaan isaan tuquu of irraa haa eeganu.');
INSERT INTO "apaguna" VALUES(24,64,'24:64','Dhagayaa! Dhugumatti, wanti samiifi dachii keessa jiran kan RabbiitiDhugumatti, Inni waan isin irra jirtan ni beekaGuyyaa gara Isaa deebi’anis waan isaan dalagan itti odeessaRabbiin waan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(25,1,'25:1','Inni furqaaniin (Qur’aana) akka aalama hundaaf dinniinaa (sodaachisaa) ta’uuf jecha gabricha Isaa irratti buuse toltuun isaa baay’atte.');
INSERT INTO "apaguna" VALUES(25,2,'25:2','[Inni] Isa mootummaan samiifi dachii Isaaf ta’e; ilmas kan hin godhatin; kan mootummaa [isaa] keessattis hiriyaan Isaaf hin jirreefi waan hundaa uumee akka isaaniin maluun sirreesse');
INSERT INTO "apaguna" VALUES(25,3,'25:3','Isaa gaditti gooftolee homaa hin uumne kan isaanuu uumamanu, kan of irraa miidhaa deebisuu ta’ee bu’aa fiduu hin dandeenye, ajjeesuu, jiraachisuufi du’a irraa kaasuus hin dandeenye [gabbaramoo] godhatan.');
INSERT INTO "apaguna" VALUES(25,4,'25:4','Warri kafaran “kun soba inni (Muhammad) of irraa uumeefi namoonni biraa irratti isa gargaaran malee waan biraatii miti” jedhanDhugumatti isaan miidhaafi cubbuu rawwatanii jiru');
INSERT INTO "apaguna" VALUES(25,5,'25:5','“(Kun) hogbarruu warra durii ishee barreessisee isheenis ganamaafi galgala isa irratti dubbifamtuudha” jedhan');
INSERT INTO "apaguna" VALUES(25,6,'25:6','”Isa samiifi dachii keessatti iccitii [hunda] beekutu isa buuse” jedhiDhugumatti, Inni akkaan araaramaa, akkaan rahmata godhaa ta’eera');
INSERT INTO "apaguna" VALUES(25,7,'25:7','Isaanis “kan nyaata nyaatuufi magaalota keessa adeemu kun ergamaa akkamiiti? Sila maaliif maleykaan gara isaa buufamanii isaa wajjin dinniinaa hin taane” jedhan.');
INSERT INTO "apaguna" VALUES(25,8,'25:8','”Yookiin immoo maaliif qabeenyi guddaan wahii gara isaatti hin darbamne? Yookiin ashaakiltii ishee irraa nyaatu maaliif isaaf hin taane?” (jedhu)Miidhaa hojjatoonnis “isin nama falfalli itti hojjatame malee [nama fayyaa] hordafaa hin jirtan” jedhan.');
INSERT INTO "apaguna" VALUES(25,9,'25:9','Akka isaan fakkeenya siif godhanii jallatanii karaa sirrii [argachuu] hin dandeenye ilaali (xiinxali).');
INSERT INTO "apaguna" VALUES(25,10,'25:10','Inni (Rabbiin) yoo fedhe waan san irra caalu, Jannata jala ishee laggeen yaatu siif godhee gamoowwanis siif godhu toltuun isaa baay’ate.');
INSERT INTO "apaguna" VALUES(25,11,'25:11','Dhugumatti isaan Qiyaamaa ni sobsiisanNama Qiyaamaa sobsiiseefis ibidda boba’aa qopheessinee jirra.');
INSERT INTO "apaguna" VALUES(25,12,'25:12','Yeroo iddoo fagoo irraa isaan argite dallansuufi halaaka (iyya akka harree) isheef ni dhagahu.');
INSERT INTO "apaguna" VALUES(25,13,'25:13','Yeroo takaalamanii (harki isaanii morma isaaniitti hidhamee) ishee irraa iddoo dhiphootti darbamanis achitti badii waammatan.');
INSERT INTO "apaguna" VALUES(25,14,'25:14','“Har’a badii tokko qofa hin waaminaatii badii hedduu waamaa” [hin argattanu isaaniin jedhama].');
INSERT INTO "apaguna" VALUES(25,15,'25:15','”Sila kanatu caalamoo Jannata yeroo hundaa ishee warri Rabbiin sodaatan baallamamantu caala?” jedhiinIsheen isaaniif mindaafi itti gala taateetti');
INSERT INTO "apaguna" VALUES(25,16,'25:16','Kan keessatti hafan ta’anii ishee keessatti wanta fedhan hundatu isaaniif jira; [Kunis] gooftaa kee irratti waadaa kadhatamaa (kan mu’uminoonni duraan Rabbiin kadhatan) ta’eera.');
INSERT INTO "apaguna" VALUES(25,17,'25:17','Guyyaa Rabbiin isaaniifi wantoota isaan Rabbii gaditti gabbaran walitti qabee “sila gabroota kiyya kanneen isintu jallisemoo isaanumatu karaa bade?” jedhu san [yaadadhu].');
INSERT INTO "apaguna" VALUES(25,18,'25:18','[Isaanis] “[yaa Rabbi!] Ati qulqulloofte! Sii gaditti gargaartota (jaalalleewwan) godhachuun kan nuuf malu hin taane; Garuu hanga gorsa [kee] dagatanii ummata bade ta’anitti Ati isaaniifi abbootii isaaniis qanansiifte” jedhan.');
INSERT INTO "apaguna" VALUES(25,19,'25:19','Dhugumatti, waan isin jettan keessatti isin sobsiisaniiruKanaafuu [adabbii Rabbii] deebisuus ta’ee [warra adabbiin tuquuf] tumsuu hin dandeessanNama isin irraa miidhaa hojjates adabbii guddaa isa dhandhamsiifna.');
INSERT INTO "apaguna" VALUES(25,20,'25:20','Siin durattis ergamoota irraa kan nyaata nyaataniifi magaalota keessa deeman taasifnee malee hin ergine; garii keessan gariidhaaf mokkoroo taasifne; ni obsituu? Gooftaan kee [waan hunda] argaadha');
INSERT INTO "apaguna" VALUES(25,21,'25:21','Warri qunnamtii keenya hin sodaanne “sila maaliif Maleykonni nutti hin buufamne? yookiin maaliif Gooftaa keenya hin argine?” jedhanDhugumatti, isaan lubbuu isaanii keessatti boonaniiru; jallina guddaas jallataniiru');
INSERT INTO "apaguna" VALUES(25,22,'25:22','Guyyaa isaan Maleykota argan, guyyaa san jallattootaaf gammachuun hin jiru[Maleykonnis] “(gammachuun kamillee isin irratti) dhorgamiinsa dhorgamaadha[]” jedhu.');
INSERT INTO "apaguna" VALUES(25,23,'25:23','Nutis gara dalagaa isaan dalaganii feenee (dhufnee) dhukkee faca’aa isa goone.');
INSERT INTO "apaguna" VALUES(25,24,'25:24','Warri Jannataa guyyaa san iddoo jireenyaa keessatti akkaan caalaa; iddoo boqonnaattis akkaan miidhagaadha');
INSERT INTO "apaguna" VALUES(25,25,'25:25','Guyyaa samiin duumessaan dhodhootuufi Maleykonni[] bu’iinsa buufamanis [yaadadhaa]!');
INSERT INTO "apaguna" VALUES(25,26,'25:26','Mootummaan dhugaa guyyaa san kan RahmaaniInni guyyaa kaafiroota irratti cimaa ta’eedha.');
INSERT INTO "apaguna" VALUES(25,27,'25:27','Guyyaa miidhaa hojjattoonni “yaa badii koo! odoon Ergamichaa wajjiin karaa sirrii qabadhee” jedhee [gaabbiidhaan] harka isaa lamaan ciniinu [yaadadhaa].');
INSERT INTO "apaguna" VALUES(25,28,'25:28','”Yaa badii kiyya! Odoon ebaluun jaalallee onnee taasifachuu baadhee maal qaba!');
INSERT INTO "apaguna" VALUES(25,29,'25:29','Dhugumatti, gorsa irraa erga inni natti dhufee booda na jalliseera.” Sheyxaanni namaaf salphisaa ta’eera.');
INSERT INTO "apaguna" VALUES(25,30,'25:30','Ergamichis “Yaa Gooftaa kiyya! Dhugumatti, ummanni kiyya Qur’aana kana dhiifamaa (oodamaa) taasifatan” jedhe.');
INSERT INTO "apaguna" VALUES(25,31,'25:31','Akkuma kana nabiyyii hundaafuu jallattoota irraa diina isaaniif gooneKaraa irratti qajeelchuufi [Mu’uminaaf] tumsuu irratti Rabbiin kee ga’aadha.');
INSERT INTO "apaguna" VALUES(25,32,'25:32','Isaan kafaran ni jedhan: “Maaliif Qur’aanni hundi al tokko isarratti hin buufamin?” Akka kanatti onnee kee isaan gadi dhaabuuf jecha (adda addaan buufne)Suuta suutaan isa ibsine');
INSERT INTO "apaguna" VALUES(25,33,'25:33','Yoo [deebii] dhugaafi kan hiikti isaa gaarii ta’e siif kennine malee gaaffiidhaan sitti dhufanii hin hafan');
INSERT INTO "apaguna" VALUES(25,34,'25:34','[Isaan] warra fuula isaanii irratti gara jahannam oofamaniidhaIsaan warra iddoo hamaa qubataniifi karaas jallataniidha.');
INSERT INTO "apaguna" VALUES(25,35,'25:35','Dhugumatti, Nuti Muusaaf kitaaba (Tawraatiin) kenninee Obboleessa isaa Haaruuniinis isa waliin deeggaraa taasifnee jirra.');
INSERT INTO "apaguna" VALUES(25,36,'25:36','”Gara warra keeyyattoota keenya sobsiisanii deemaa” isaaniin jenneAchumaan [ergamaan lamaan sobsiifnaan] balleessuu isaan balleessine.');
INSERT INTO "apaguna" VALUES(25,37,'25:37','Ummata Nuuhis yeroma isaan ergamoota sobsiisan isaan dhidhimsinee namootaafis mallattoo isaan taasifneMiidhaa raawwattootaafis adabbii laalessaa qopheessineerra.');
INSERT INTO "apaguna" VALUES(25,38,'25:38','[Ummata] Aad, Samuud, warra Eelaa[]fi jaarrota baay’ee jidduu sana jiranis (balleesinee jira)');
INSERT INTO "apaguna" VALUES(25,39,'25:39','Hunda isaanitiif fakkeenyota taasifnee jirraHunda isaaniis barbadeessuu barbadeessinee jirra');
INSERT INTO "apaguna" VALUES(25,40,'25:40','Dhugumatti, isaan Magaalattii Roobni hamaan itti roobe irra qaxxaamuraa turaniiruSila isaan kan ishee argan hin taanee [maaliif hin gorfamne]? Dhugaatti kaafama [Qiyaamaa] kan sodaatan hin taane.');
INSERT INTO "apaguna" VALUES(25,41,'25:41','Yeroo si arganis “Isa kanaa inni Rabbiin ergamaa taasisee erge” jechuun qishnaa malee waan biraa si hin taasifatanu');
INSERT INTO "apaguna" VALUES(25,42,'25:42','”Odoo nuti isaan [gabbaruu] irratti obsuu baannee, silaa (Muhammad) gabbaramtoota keenya irraa nu jallisuutti dhihaatee ture” (jedhu)Fuulduratti yeroo adabbii argan immoo namni karaa bade eenyuun akka ta’e beekuuf jiru');
INSERT INTO "apaguna" VALUES(25,43,'25:43','Sila nama gabbaramaa isaa fedhuma lubbuu isaa taasifate hin arginee? Sila ati isa irratti tiksaa (eegaa) taataa?');
INSERT INTO "apaguna" VALUES(25,44,'25:44','Sila ati irra baay’een isaanii ni dhaga’u yookiin (waan itti himame) ni hubatu jettee yaaddaa? Isaan akka beyladaa malee waan biraa hin taaneInumaa isaan (horii) irra karaa jallatoodha');
INSERT INTO "apaguna" VALUES(25,45,'25:45','Sila gara Gooftaa keetii akkamitti Gaaddisa akka diriirse hin ilaallee?[] Odoo fedhees turaa isa taasisa tureErgasii Aduu (jiraachuu) isaa (gaaddisaa) irratti ragaa taasifne');
INSERT INTO "apaguna" VALUES(25,46,'25:46','Ergasiis qabiinsa suuta suutaa gara keenyatti isa (gaaddisa) qabne (hir’isne)');
INSERT INTO "apaguna" VALUES(25,47,'25:47','Inni (Rabbiin) Isa halkan uffata, hirriba immoo boqonnaa isiniif taasiseefi Guyyaas yeroo olii gadi sochootan taasiseedha');
INSERT INTO "apaguna" VALUES(25,48,'25:48','Ammas Inni Isa rahmata (bokkaa) isaa dura qilleensa gammachiisaa godhee ergeedhaSamii irraas bishaan qulqulluu buusne');
INSERT INTO "apaguna" VALUES(25,49,'25:49','Akka dachii duute isaan jiraachisnuufi waan uumne irraa horiifi namoota baay’ee isa obaasnuuf [Samii irraa rooba buufne]');
INSERT INTO "apaguna" VALUES(25,50,'25:50','Dhugumatti, akka gorfamaniif jecha gidduu isaaniitti isa raabsinee jirraIrra hedduun namaas kafaruu malee [amanuu] ni didan.');
INSERT INTO "apaguna" VALUES(25,51,'25:51','Odoo feenee magaalota hunda keessatti akeekkachiisaa ni ergina ture');
INSERT INTO "apaguna" VALUES(25,52,'25:52','Kaafirootaaf hin ajajaminii isaan (Qur’aana kanaan) qabsoo guddaa isaan irratti qabsaa’i.');
INSERT INTO "apaguna" VALUES(25,53,'25:53','Inni Isa galaana lama, tokko mi’aayaa dhugaatiif mijaa’uu, kaan immoo soogiddoo kuraawaa ta’e, wal maddii qabee gadi dhiisee jidduu isaan lamaanittis dawoofi daangaa daangeffamaa taasiseedha.');
INSERT INTO "apaguna" VALUES(25,54,'25:54','Ammas Inni Isa bishaan irraa nama uumee ergasii fira (sanyii)fi soddaa tasiseedhaGooftaan kee danda’aa ta’e');
INSERT INTO "apaguna" VALUES(25,55,'25:55','Rabbii gaditti waan isaan hin fayyadneefi hin miine gabbaruKaafirri Gooftaa isaa irratti deeggaraa [sheyxanaa] ta’eera');
INSERT INTO "apaguna" VALUES(25,56,'25:56','Nutis gammachiisaafi akeekkachiisaa goonee malee si hin ergine');
INSERT INTO "apaguna" VALUES(25,57,'25:57','Jedhi: “Isa irratti mindaa isin hin gaafadhu; garuu namni karaa gooftaa isaa qabachuu fedhe [haa saddaqatu].”');
INSERT INTO "apaguna" VALUES(25,58,'25:58','Jiraataa Isa hin duune irratti hirkadhuFaarsuu Isaa waliin Isa qulqulleessiBadii gabroota Isaa iccitii beekuu keessatti Isumatu ga’aadha.');
INSERT INTO "apaguna" VALUES(25,59,'25:59','Inni Samiifi Dachii, akkasumas waan isaan lamaan gidduu jiran (hunda) guyyoota jaha keessatti uumee ergasii Arshii irratti (haala isaaf maluun) ol ta’e; (Inni) RahmaaniKanaaf haala keessa beekaa ta’een isa gaafadhu.');
INSERT INTO "apaguna" VALUES(25,60,'25:60','Yeroo Rahmaaniif sujuuda godhaa jedhaman “Rahmaan immoo maali? Waanuma isin nu ajajjan hundaaf sujuuda goonaa?” jedhuKunis Rabbirraa] fagaachuu isaaniif dabale.');
INSERT INTO "apaguna" VALUES(25,61,'25:61','Inni Samii keessatti buruuja[] taasisee ishee keessattis (biiftuu) ifaafi ji’a ibsaa taasise khayriin Isaa baay’ate.');
INSERT INTO "apaguna" VALUES(25,62,'25:62','Inni Isa halkaniifi guyyaa namoota gorfamuu fedhaniif yookiin galata galchuu fedhaniif wal irraa bakka bu’aa taasiseedha');
INSERT INTO "apaguna" VALUES(25,63,'25:63','Gabroonni Rahmaan isaan suutaan Dachii irra deeman; yeroo wallaaltonni isaan dubbisanis “Salaaman” (nagaa) jedhanuudha');
INSERT INTO "apaguna" VALUES(25,64,'25:64','Isaan Gooftaa isaaniitiif sujuuda godhaa; dhaabbataa bulaniidha');
INSERT INTO "apaguna" VALUES(25,65,'25:65','Ammas warra jedhani: “(yaa) Gooftaa keenya! adabbii jahannam nurraa deebisi; dhugumatti adabbiin ishee kan nama irraa hin deemneedha.');
INSERT INTO "apaguna" VALUES(25,66,'25:66','Dhugumatti, isheen iddoo qubannaafi jireenyaa ta’uun fokkattee jirti.”');
INSERT INTO "apaguna" VALUES(25,67,'25:67','Ammas (gabroonni Rabbii) isaan yeroo arjooman karaa malee hin balleessineefi hin donnoomne kan gidduu kanatti (kennaan isaanii) sirrii ta’eedha.');
INSERT INTO "apaguna" VALUES(25,68,'25:68','Ammas isaan Rabbii wajjin waan biraa hin gabbarre (hin kadhanne), kan lubbuu Rabbiin ajjeesuu haraama godhe haqaan alatti hin ajjeefneefi hin sagaagalleedhaNamni (hojii gadhee) kana raawwate, adabbii qunnama');
INSERT INTO "apaguna" VALUES(25,69,'25:69','Guyyaa Qiyaamaa adabbiin (jahannam) dachaa isaaf taasifamaIsa keessa salphifamaa ta’ee yeroo hunda jiraatas.');
INSERT INTO "apaguna" VALUES(25,70,'25:70','Nama tawbateefi amanee dalagaa gaarii hojjate maleeIsaan sun Rabbiin hamtuu isaanii gaariitti jijjiiraafRabbiin araaramaa, rahmata godhaa ta’eera');
INSERT INTO "apaguna" VALUES(25,71,'25:71','Namni tawbateefi gaarii hojjate, inni dhugumatti tawbaa gaarii gara Rabbiitti deebi’ee jira.');
INSERT INTO "apaguna" VALUES(25,72,'25:72','Ammas (gabrooni Rabbii) isaan ragaa cubbuu hin baaneefi yeroo tapha bira qaxxaamuranis kabajaadhaan qaxxaamuraniidha.');
INSERT INTO "apaguna" VALUES(25,73,'25:73','Ammas isaan yeroo keeyyattoota Gooftaa isaanitiin yaadachiifaman [kan hubataniifi fudhatan ta’anii malee] duudaafi jaamaa ta’anii ishee irratti hin kufneedha.');
INSERT INTO "apaguna" VALUES(25,74,'25:74','Ammas warra “Gooftaa keenya! Niitolii keenyaafi sanyii keenyas gammachuu ijaa kan ta’an nuuf kenniWarra Rabbiin sodaataniifis imaama (dursaa) nu taasisi” jedhanuudha.');
INSERT INTO "apaguna" VALUES(25,75,'25:75','Isaan sun waan obsaniif jecha (Jannata keessatti) iddoo ol aanaa ta’e (gamoo) mindeeffamuIshee keessattis kabajaafi nageenya qunnamu');
INSERT INTO "apaguna" VALUES(25,76,'25:76','Ishee keessa yeroo hunda jiraatuIsheen iddoo qubannaafi jireenyaa kan akkaan miidhagdeedha');
INSERT INTO "apaguna" VALUES(25,77,'25:77','Jedhi: “Odoo kadhaan (Iimaanni) keessan jiraachuu baatee, silaa Gooftaan kiyya homattuu isin hin lakkaa’u tureDhugumatti isin sobsiiftanii jirtuKanaaf adabbiin (isiniif qophaa’e) kan yeroo hunda isin waliin jiraatu ta’a.”');
INSERT INTO "apaguna" VALUES(26,1,'26:1','Xaa Siin Miim.');
INSERT INTO "apaguna" VALUES(26,2,'26:2','Kun keeyyattoota kitaaba (dhugaa soba irraa) adda baasaa ta’eeti');
INSERT INTO "apaguna" VALUES(26,3,'26:3','(Yaa Muhammad!) Ati Mu’uminoota ta’uu dhabuu isaaniitiif [gaddaan] lubbuu kee baasuun siif sodaatama');
INSERT INTO "apaguna" VALUES(26,4,'26:4','Odoo feenee samii irraa mallattoo isaan irratti buufnee (isheedhaaf ajajamuun) mormi isaanii isheedhaaf gadi jedhaa taati ture');
INSERT INTO "apaguna" VALUES(26,5,'26:5','Isaan kan sobsiisan ta’anii malee gorsi (kitaabni) haaraan Rahmaan irraa isaaniif kan dhufu hin taane.');
INSERT INTO "apaguna" VALUES(26,6,'26:6','Dhugumatti isaan [Qur’aana kana] sobsiisaniiruOduun waan isaan itti qishnaa (qoosaa) turanii isaanitti dhufuuf jira');
INSERT INTO "apaguna" VALUES(26,7,'26:7','Sila gara Dachii akka nuti gosoota gaggaarii hunda irraa meeqaatama ishee keessatti biqilchine isaan hin ilaallee?');
INSERT INTO "apaguna" VALUES(26,8,'26:8','Dhugumatti, sana keessa mallattootu jiraIrra hedduun isaaniis kan amanan hin taane');
INSERT INTO "apaguna" VALUES(26,9,'26:9','Dhugumatti Gooftaan kee Isatu injifataa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(26,10,'26:10','Yeroo Gooftaan kee “Gara ummata miidhaa dalagdootaa deemi” [jedhee] Muusaa waame [yaadadhu]');
INSERT INTO "apaguna" VALUES(26,11,'26:11','“Gara ummata Fir’awn [dhaqi]Sila Rabbiin hin sodaatanii? [jedhiin].”');
INSERT INTO "apaguna" VALUES(26,12,'26:12','[Muusaanis] ni jedhe: “Gooftaa kiyya! Dhugumatti, ani na sobsiisuun sodaadha.');
INSERT INTO "apaguna" VALUES(26,13,'26:13','[Yoo na sobsiisan] qomti kiyyas ni dhiphataArrabni kiyyas hin diriiruKanaafuu gara Haaruunitti ergi');
INSERT INTO "apaguna" VALUES(26,14,'26:14','Isaaniifis yakka [dhiigaatu] narra jira; kanaafuu na ajjeesuun sodaadha.”');
INSERT INTO "apaguna" VALUES(26,15,'26:15','[Rabbiinis] ni jedhe: “lakki! (si hin ajjeesanu)Isin lamaan mallattoolee keenyaan deemaaDhugumatti, Nuti isinii waliin dhageeffatoota.”');
INSERT INTO "apaguna" VALUES(26,16,'26:16','“Fir’awnitti dhaqaatii ‘Nuti ergamaa Gooftaa aalama hundaati’ jedhaa.');
INSERT INTO "apaguna" VALUES(26,17,'26:17','“Ilmaan Israa’iil nu waliin gadi dhiisi” jechuudhaan [ergamne jedhaa]');
INSERT INTO "apaguna" VALUES(26,18,'26:18','[Fir’awnis] ni jedhe: “sila nuti mucaa xiqqaa taatee of biratti si hin guddifnee? Umrii kee irraas waggoota baay’ee nu keessa hin turree?');
INSERT INTO "apaguna" VALUES(26,19,'26:19','Dalagaa kee ishee dalagde (ajjeechaa raawwatte) sanas hin hojjannee? Ati gantoota irraahi” [jedhe].');
INSERT INTO "apaguna" VALUES(26,20,'26:20','[Muusaanis] ni jedhe: “Yeroo san wallaaltota irraa ta’een ishee hojjadhe.');
INSERT INTO "apaguna" VALUES(26,21,'26:21','Yeroman isin sodaadhun isin irraa dheesse; Gooftaan kiyyas beekumsa naaf kennee ergamtoota irraa na godhe.');
INSERT INTO "apaguna" VALUES(26,22,'26:22','Sunis Ilmaan Israa’iil gabra godhachuu keetiin tola ati narratti himattuudha.”');
INSERT INTO "apaguna" VALUES(26,23,'26:23','Fir’awn “gooftaan aalama hundaa maali?” jedhe.');
INSERT INTO "apaguna" VALUES(26,24,'26:24','[Muusaanis]: “gooftaa Samii, Dachiifi waan gidduu isaan lamaaniiti; yoo kan dhugoomsitan taatan [itti amanaa]” jedheen.');
INSERT INTO "apaguna" VALUES(26,25,'26:25','[Fir’awnis] namoota maddii isaa jiraniin “Sila [waan inni jedhu] hin dhageessanuu?” jedhe.');
INSERT INTO "apaguna" VALUES(26,26,'26:26','[Muusaan] “Gooftaa keessaniifi Gooftaa abbootii keessan kan jalqabaati” jedhe.');
INSERT INTO "apaguna" VALUES(26,27,'26:27','[Fir’awn] “ergamaan keessan inni gara keessanitti ergame dhugumatti maraatuudha” jedhe.');
INSERT INTO "apaguna" VALUES(26,28,'26:28','[Muusaan] “Gooftaa Bahaa, Dhihaafi waan gidduu isaan lamaanii jiruuti; yoo kan hubattan taataniif [itti amanaa]” jedhe.');
INSERT INTO "apaguna" VALUES(26,29,'26:29','[Fir’awn] “dhugumatti, yoo ati ana malee gabbaramaa biraa godhatte, hidhamtoota irraan si taasisa” jedhe.');
INSERT INTO "apaguna" VALUES(26,30,'26:30','[Muusaan] “sila odoo waan (ragaa) ifa ta’een sitti dhufeellee moo?” jedhe.');
INSERT INTO "apaguna" VALUES(26,31,'26:31','[Fir’awn] “yoo warra dhugaa dubbatan irraa taate isa fidi” jedhe.');
INSERT INTO "apaguna" VALUES(26,32,'26:32','[Muusaan] ulee isaa ni darbateYeroma san isheen jawwee ifa galaa taate.');
INSERT INTO "apaguna" VALUES(26,33,'26:33','Harka isaas [bobaa isaa jalaa] ni baase; Yeroma san isheen warra ilaalaniif adii taate.');
INSERT INTO "apaguna" VALUES(26,34,'26:34','[Fir’awn] qondaaltota naanna’a isaa jiraniin “dhugumatti, kun saahiree (falfalaa) akkaan beekaadha” jedhe');
INSERT INTO "apaguna" VALUES(26,35,'26:35','“Sihrii isaatiin dachii keessan irraa isin baasuu fedhaa, isin immoo maalitti na ajajju” (jedhe).');
INSERT INTO "apaguna" VALUES(26,36,'26:36','[Isaanis] ni jedhan: “Isaafi obboleessa isaas tursiisiMagaalota keessattis walitti qabdoota ergi;');
INSERT INTO "apaguna" VALUES(26,37,'26:37','Saahiroota (falfaltoota) beekumsa qaban hundaan sitti haa dhufanu.”');
INSERT INTO "apaguna" VALUES(26,38,'26:38','Saahiroonnis beellama guyyaa beekamaatiif walitti qabaman.');
INSERT INTO "apaguna" VALUES(26,39,'26:39','Namootaanis ni jedhame: “Isin sila walitti qabamtanii? (Dafaa walitti qabamaa);');
INSERT INTO "apaguna" VALUES(26,40,'26:40','Yoo isaan kan injifatan ta’an, Saahiroota (falfaltoota) hordofuun keenya ni kajeelamaa.”');
INSERT INTO "apaguna" VALUES(26,41,'26:41','Falfaltoonni yeroma dhufan, Fir’awniin “Sila dhugumatti yoo nuti mo’attoota taaneef mindaan nuuf jiraa? ” jedhan.');
INSERT INTO "apaguna" VALUES(26,42,'26:42','[Fir’awnis] “Eeyyeen; isin yoosan [natti] dhihaattota irraa taatu” jedhe');
INSERT INTO "apaguna" VALUES(26,43,'26:43','Muusaanis “Isin waan darbuu feetan darbaa” isaanin jedhe.');
INSERT INTO "apaguna" VALUES(26,44,'26:44','Haadaafi uleewwan isaanii ni darban; “Jabeenya Fir’awniin! Nuti dhugumatti nutu injifattoota” jedhan.');
INSERT INTO "apaguna" VALUES(26,45,'26:45','Muusaanis ulee isaa ni darbateYeroma san isheen waan isaan sobaan fakkeessan hunda liqimsite.');
INSERT INTO "apaguna" VALUES(26,46,'26:46','Falfaltoonnis [yeroma kana argan] sujuuda bu’aa ta’anii gadi kufan');
INSERT INTO "apaguna" VALUES(26,47,'26:47','Ni jedhanis: “Gooftaa aalama hundaatti amanne;');
INSERT INTO "apaguna" VALUES(26,48,'26:48','Gooftaa Muusaafi Haaruunitti.”');
INSERT INTO "apaguna" VALUES(26,49,'26:49','[Fir’awnis] ni jedhe: “Odoo ani isiniif hin eeyyamin isatti amantanii? Dhugumatti, inni hayyuu keessan kan sihrii isin barsiiseedhaFuuldurattis [waan ta’u] beekuuf jirtuDhugumatti, ani Harka keessaniifi Luka keessan wal-faallessuudhaan isin irraan mummuraDhugumatti hunduma keessan isinin fannisa.”');
INSERT INTO "apaguna" VALUES(26,50,'26:50','Ni jedhan: “Miidhaan [nurra] hin jiru! Dhugumatti, nuti gara Gooftaa keenyaa deebi’oodha.');
INSERT INTO "apaguna" VALUES(26,51,'26:51','Dhugumatti, nuti warra jalqaba amanan ta’uudhaan Rabbiin keenya badii keenya nuuf araaramuu ni kajeella .”');
INSERT INTO "apaguna" VALUES(26,52,'26:52','Gara Muusaattis “halkaniin gabroota kiyyaan deemi (baqadhu) dhugumatti isin [diinota keessaniin] hordofamoodha” jechuun wahyii goone.');
INSERT INTO "apaguna" VALUES(26,53,'26:53','Fir’awnis magaalota keessatti walitti qabdoota ergee,');
INSERT INTO "apaguna" VALUES(26,54,'26:54','“Dhugumatti warri kun gareewwan xiqqoodha.');
INSERT INTO "apaguna" VALUES(26,55,'26:55','Isaanis dhugumatti nu dallansiisoodha.');
INSERT INTO "apaguna" VALUES(26,56,'26:56','Nutis baay’ee of eegdota” [jedhe].');
INSERT INTO "apaguna" VALUES(26,57,'26:57','Ashaakiltiileefi burqaawwan irraas isaan baafne.');
INSERT INTO "apaguna" VALUES(26,58,'26:58','Kuusaa qabeenyaafi iddoowwan kabajamaa irraas.');
INSERT INTO "apaguna" VALUES(26,59,'26:59','Akka kanatti Ilmaan Israa’iil ishee dhaalchifne.');
INSERT INTO "apaguna" VALUES(26,60,'26:60','Bariifatoo ta’anii isaan hordofan.');
INSERT INTO "apaguna" VALUES(26,61,'26:61','Yeroma gareen lamaan wal argan, gareen Muusaa “nuti dhugumatti [garee Fir’awniin] dhaqqabamoodha” jedhan.');
INSERT INTO "apaguna" VALUES(26,62,'26:62','[Muusaan] ni jedhe: “lakkisaa! Dhugumatti, Gooftaan kiyya na wajjin jiraInni [furmaatatti] na qajeelcha.”');
INSERT INTO "apaguna" VALUES(26,63,'26:63','Gara Muusaatti “ulee keetiin galaana rukuti” jechuun wahyii goone[Yeroma inni dhahu] qoqqoodamee Kutaan qoodama isaa hunduu akka gaara guddaa ta’e.');
INSERT INTO "apaguna" VALUES(26,64,'26:64','Bakka sanattis warra biraa dhiheessine.');
INSERT INTO "apaguna" VALUES(26,65,'26:65','Muusaafi warra isa waliin turan hundaa ni baraarsine');
INSERT INTO "apaguna" VALUES(26,66,'26:66','Sana booda warra biroo bishaaniin dhidhimsine.');
INSERT INTO "apaguna" VALUES(26,67,'26:67','Dhugumatti, kana keessa mallattootu jiraIrra hedduun isaanii Mu’uminoota hin taane.');
INSERT INTO "apaguna" VALUES(26,68,'26:68','Dhugumatti Rabbiin kee, Isumatu injifataa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(26,69,'26:69','Seenaa Ibraahimis isaan irratti dubbisi.');
INSERT INTO "apaguna" VALUES(26,70,'26:70','Yeroo inni abbaafi ummata isaatiin “maal gabbartu?” jedhe.');
INSERT INTO "apaguna" VALUES(26,71,'26:71','“Taabotawwan gabbarra; ishee [gabbaruu] irras warra turu taane” jedhan.');
INSERT INTO "apaguna" VALUES(26,72,'26:72','[Ibraahiim] ni jedhe: “sila yeroo isaan waammattan isin dhagahanii?');
INSERT INTO "apaguna" VALUES(26,73,'26:73','Yookiinis isin fayyaduu? Yookiinis isin miidhuu?”');
INSERT INTO "apaguna" VALUES(26,74,'26:74','[Isaanis] “Lakkisaa; abbootii keenya akkas kan dalagan agarre” jedhan.');
INSERT INTO "apaguna" VALUES(26,75,'26:75','[Ibraahiim] ni jedhe: “Sila waan gabbaraa turtan hubattanii jirtuu?');
INSERT INTO "apaguna" VALUES(26,76,'26:76','Isiniifi abbootiin keessan kan duraaniis?');
INSERT INTO "apaguna" VALUES(26,77,'26:77','Dhugumatti, isaan anaaf diinota; garuu Gooftaa aalama hundaa malee (inni jaalalloo kooti).');
INSERT INTO "apaguna" VALUES(26,78,'26:78','[Inni] isa na uume, isatu na qajeelcha.”');
INSERT INTO "apaguna" VALUES(26,79,'26:79','Inni Isa na nyaachisuufi na obaasuudha.');
INSERT INTO "apaguna" VALUES(26,80,'26:80','Yeroo an dhukkubsadhes, Isatu na fayyisa.');
INSERT INTO "apaguna" VALUES(26,81,'26:81','Isa na ajjeesuufi Sana booda na jiraachisuudha.');
INSERT INTO "apaguna" VALUES(26,82,'26:82','Isa guyyaa murtiis badii kiyya akka naaf araaramu kajeeluudha.');
INSERT INTO "apaguna" VALUES(26,83,'26:83','Gooftaa kiyya! beekumsa naaf kenniNamoota gaggaariittis na dhaqqabsiisi.');
INSERT INTO "apaguna" VALUES(26,84,'26:84','Dhaloota boodaa keessattis faaruu gaarii naaf taasisi');
INSERT INTO "apaguna" VALUES(26,85,'26:85','Dhaaltota Jannata qananii dhaalan irraas na taasisi.');
INSERT INTO "apaguna" VALUES(26,86,'26:86','Abbaa kiyyaafis araaramiDhugumatti, inni jallattoota irraa ta’eera.');
INSERT INTO "apaguna" VALUES(26,87,'26:87','Guyyaa isaan (gabroonni) kaafamanis na hin xinneessin.”');
INSERT INTO "apaguna" VALUES(26,88,'26:88','Guyyaa qabeenyiifi ilmaanis homaa hin fayyadne.');
INSERT INTO "apaguna" VALUES(26,89,'26:89','Nama onnee qulqulluudhaan Rabbitti dhufe malee.');
INSERT INTO "apaguna" VALUES(26,90,'26:90','[Guyyaa] Jannannis warra Rabbiin sodaataniif dhiheeffamte;');
INSERT INTO "apaguna" VALUES(26,91,'26:91','[Guyyaa] Ibiddi jahannamis jallattootaaf muldhifamte;');
INSERT INTO "apaguna" VALUES(26,92,'26:92','[Guyyaa] “wanti isin gabbaraa turtan eessa jiru?” isaaniin jedhame');
INSERT INTO "apaguna" VALUES(26,93,'26:93','“Rabbii gaditti (ta’anii) Sila isin tumsuu? Yookiin of ni tumsuu?”');
INSERT INTO "apaguna" VALUES(26,94,'26:94','Isaaniifi jallattoonni ishee keessatti darbaman');
INSERT INTO "apaguna" VALUES(26,95,'26:95','Waraanni Ibliis hunduu (ni darbaman).');
INSERT INTO "apaguna" VALUES(26,96,'26:96','Isaan ishee keessatti wal falmaa ni jedhu:');
INSERT INTO "apaguna" VALUES(26,97,'26:97','Allaahn kakannee! Nuti dhugumatti jallina ifa ta’e keessa turre.');
INSERT INTO "apaguna" VALUES(26,98,'26:98','Yeroo Gooftaa aalama hundaatti isin qixxeessinu san.');
INSERT INTO "apaguna" VALUES(26,99,'26:99','Ficiltoota malee homtuu nu hin jallisne.');
INSERT INTO "apaguna" VALUES(26,100,'26:100','Araarsitoonni tokkollee nuuf hin jiru.');
INSERT INTO "apaguna" VALUES(26,101,'26:101','Hiriyaan garaa laafus (hin jiru)');
INSERT INTO "apaguna" VALUES(26,102,'26:102','Odoo (addunyaatti) deebiin tokkollee nuuf jiraatee silaa Mu’uminoota irraa taana turre.');
INSERT INTO "apaguna" VALUES(26,103,'26:103','Dhugumatti, kana keessa mallattootu jiraIrra hedduun isaanii Mu’uminoota hin taane.');
INSERT INTO "apaguna" VALUES(26,104,'26:104','Dhugumatti Gooftaan kee isumatu injifataa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(26,105,'26:105','Ummanni Nuuh ergamtoota sobsiiftee jirti.');
INSERT INTO "apaguna" VALUES(26,106,'26:106','Yeroo obboleessi isaanii Nuuh jedheen: “Sila Rabbiin hin sodaattanii');
INSERT INTO "apaguna" VALUES(26,107,'26:107','Ani isiniif ergamaa amanamaadha.');
INSERT INTO "apaguna" VALUES(26,108,'26:108','Rabbiin sodaadhaa; ajaja kiyyas fudhaa.');
INSERT INTO "apaguna" VALUES(26,109,'26:109','Ani isa (waamicha kana) irratti mindaa tokkollee isin hin gaafadhu; Mindaan kiyya Gooftaa aalama hundaa irratti malee hin taane.');
INSERT INTO "apaguna" VALUES(26,110,'26:110','Rabbiin sodaadhaaAjaja kiyyas fudhaa.”');
INSERT INTO "apaguna" VALUES(26,111,'26:111','[Ummanni isaas] “sila nuti odoo warri gadi aanoon si hordofanii jiranuu siif amannaa?” jedhan.');
INSERT INTO "apaguna" VALUES(26,112,'26:112','[Innis] ni jedhe: “Waan isaan dalagaa turan maalittin beeka?');
INSERT INTO "apaguna" VALUES(26,113,'26:113','Odoo beektanii qormaanni isaanii Gooftaa kiyya qofa irra malee hin taane.');
INSERT INTO "apaguna" VALUES(26,114,'26:114','Ani ari’aa (fageesaa) Mu’uminootaa hin taane');
INSERT INTO "apaguna" VALUES(26,115,'26:115','Ani akeekkachiisaa ifa bahaa ta’e malee hin taane.”');
INSERT INTO "apaguna" VALUES(26,116,'26:116','[Isaan] ni jedhan: “Yaa Nuuh! [waan jettu irraa] yoo dhorgamuu baatte, warra dhakaan cafaqaman irraa taata.”');
INSERT INTO "apaguna" VALUES(26,117,'26:117','[Nuuhis] ni jedhe: “Yaa gooftaa kiyya! Dhugumatti, ummanni koo na sobsiisaniiru.');
INSERT INTO "apaguna" VALUES(26,118,'26:118','Ati gidduu kiyyaafi gidduu isaaniitti murtii [haqaa] murteessiAnaafi Mu’uminoota irraa nama na waliin ta’anis baraarsi.”');
INSERT INTO "apaguna" VALUES(26,119,'26:119','Nutis isaafi namoota isa waliin turan doonii guutamte keessatti isaan baraarsine');
INSERT INTO "apaguna" VALUES(26,120,'26:120','Ergasii booda [bilisa isaan baasuutii] warra hafan ni dhidhimsine.');
INSERT INTO "apaguna" VALUES(26,121,'26:121','Dhugumatti, kana keessa mallattootu jiraGaruu irra hedduun isaanii Mu’uminoota hin taane.');
INSERT INTO "apaguna" VALUES(26,122,'26:122','Dhugumatti Gooftaan kee Isatu injifataa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(26,123,'26:123','Aad (ummanni huud) ergamtoota sobsiifte.');
INSERT INTO "apaguna" VALUES(26,124,'26:124','Yeroo obboleessi isaanii Huud isaaniin jedhe: “Sila (Rabbiin) hin sodaattanii?');
INSERT INTO "apaguna" VALUES(26,125,'26:125','Ani isiniif ergamaa amanamaadha.');
INSERT INTO "apaguna" VALUES(26,126,'26:126','Rabbiin sodaadhaa; ajaja kiyyas fudhaa.');
INSERT INTO "apaguna" VALUES(26,127,'26:127','Ani isa (waamicha kana) irratti mindaa tokkollee isin hin gaafadhu; mindaan kiyya Gooftaa aalama hundaa irratti malee hin taane.');
INSERT INTO "apaguna" VALUES(26,128,'26:128','Sila isin kan [nama tarutti] qishintanu taatanii iddoo ol fuudhamaa hunda irratti [gamoo] mallattoo ijaartuu?');
INSERT INTO "apaguna" VALUES(26,129,'26:129','Kuusaa bishaaniitiifi gamoowwanis akka waan keessatti haftanuutti tolfattuu?');
INSERT INTO "apaguna" VALUES(26,130,'26:130','Yeroo [namoota adabbiif] qabdanis gara jabeeyyii taatanii qabdan.');
INSERT INTO "apaguna" VALUES(26,131,'26:131','Rabbiin sodaadhaa; ajaja kiyyas fudhaa.');
INSERT INTO "apaguna" VALUES(26,132,'26:132','Isa waan [gaggaarii] isin beektan hundaan isin badhaases sodaadhaa.');
INSERT INTO "apaguna" VALUES(26,133,'26:133','Beyladootaafi ilmaaniinis isin badhaase.');
INSERT INTO "apaguna" VALUES(26,134,'26:134','Ashaakiltiiwwaniifi burqaawwaniinis [isin qananiise].');
INSERT INTO "apaguna" VALUES(26,135,'26:135','Ani adabbii Guyyaa guddaan isin irratti sodaadha.”');
INSERT INTO "apaguna" VALUES(26,136,'26:136','[Ummanni Huud] ni jedhan: “Yoo nu gorsites yoo warra gorsan irraa ta’uu baattes nurratti waluma qixa');
INSERT INTO "apaguna" VALUES(26,137,'26:137','Kun amaluma warra duriiti malee waan biraa miti.');
INSERT INTO "apaguna" VALUES(26,138,'26:138','Nuti kan adabamnuu miti” [jedhan].');
INSERT INTO "apaguna" VALUES(26,139,'26:139','Isa sobsiisanii Nutis isaan balleessineDhugumatti, kana keessa mallattootu jiraGaruu irra hedduun isaanii mu’uminoota hin taane.');
INSERT INTO "apaguna" VALUES(26,140,'26:140','Dhugumatti, Rabbiin kee Isatu injifataa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(26,141,'26:141','Samuud (ummanni Saalih) ergamtoota sobsiifte.');
INSERT INTO "apaguna" VALUES(26,142,'26:142','Yeroo obboleessi isaanii Saalih isaaniin jedhe: “Sila Rabbiin hin sodaattanii?');
INSERT INTO "apaguna" VALUES(26,143,'26:143','Ani isiniif ergamaa amanamaadha.');
INSERT INTO "apaguna" VALUES(26,144,'26:144','Rabbiin sodaadhaa; ajaja kiyyas fudhaa.');
INSERT INTO "apaguna" VALUES(26,145,'26:145','Ani isa (waamicha kana) irratti mindaa tokkollee isin hin gaafadhu; Mindaan kiyya Gooftaa aalama hundaa irra malee hin taane.');
INSERT INTO "apaguna" VALUES(26,146,'26:146','Sila waan as jiru (qabeenya kana) keessatti nageenyaan dhiifamtuu?');
INSERT INTO "apaguna" VALUES(26,147,'26:147','Ashaakiltiiwwaniifi burqaawwan keessatti,');
INSERT INTO "apaguna" VALUES(26,148,'26:148','Oorruuwwaniifi muka tamirii ijji ishee Bilchaataa taate (keessattis ni dhiifamtuu?)');
INSERT INTO "apaguna" VALUES(26,149,'26:149','Cocollee taatanii gaarreen irraas manoota bocattuu (soqattuu?)');
INSERT INTO "apaguna" VALUES(26,150,'26:150','Rabbiin sodaadhaa; ajaja kiyyas fudhaa.');
INSERT INTO "apaguna" VALUES(26,151,'26:151','Ajaja warra daangaa dabranii hin fudhatinaa.');
INSERT INTO "apaguna" VALUES(26,152,'26:152','Isaan Dachii keessatti badii raawwataniifi toltuu hin raawwannee” [jedhe].');
INSERT INTO "apaguna" VALUES(26,153,'26:153','[Isaan] ni jedhan: “Ati namoota sihriin (falfalli) baay’een itti godhame irraa isa tokkoodha.');
INSERT INTO "apaguna" VALUES(26,154,'26:154','Ati namuma akka keenyaati malee waan biraatii mitiWarra dhugaa dubbatan irraa yoo taate mallattoo fidi.”');
INSERT INTO "apaguna" VALUES(26,155,'26:155','[Saalihis] ni jedhe: “Isheen kun Gaala; isheef carraa dhugaatiitu jira; Isiniifis carraa dhugaatii guyyaa beekamaa ta’eetu jira.');
INSERT INTO "apaguna" VALUES(26,156,'26:156','Hamtuun ishee hin tuqinaa[Yoo akkas gootan] adabbii guyyaa guddaatu isin mudata.”');
INSERT INTO "apaguna" VALUES(26,157,'26:157','Ishee waraananii yeroma san sheena’oota ta’an');
INSERT INTO "apaguna" VALUES(26,158,'26:158','Adabbichis isaan qabateDhugumatti, kana keessa mallattootu jiraIrra hedduun isaanii Mu’uminoota hin taane');
INSERT INTO "apaguna" VALUES(26,159,'26:159','Dhugumatti Gooftaan kee Isatu injifataa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(26,160,'26:160','Ummanni Luuxis ergamoota sobsiifte');
INSERT INTO "apaguna" VALUES(26,161,'26:161','Yeroo obboleessi isaanii Luux isaanin jedhu: “Sila Rabbiin hin sodaattanii?');
INSERT INTO "apaguna" VALUES(26,162,'26:162','Dhugumatti, ani isiniif ergamaa amanamaadha.');
INSERT INTO "apaguna" VALUES(26,163,'26:163','Rabbiin sodaadhaa; ergaa kiyyas fudhaa.');
INSERT INTO "apaguna" VALUES(26,164,'26:164','Ani isa (waamicha kana) irratti mindaa tokkollee isin hin gaafadhuMindaan kiyya Gooftaa aalama hundaa irra malee hin taane.');
INSERT INTO "apaguna" VALUES(26,165,'26:165','Sila isin (qunnamtii saalaatiif) aalama hunda irraa dhiirotatti dhuftuu?');
INSERT INTO "apaguna" VALUES(26,166,'26:166','Waan Gooftaan keessan haadhotii manaa keessan irraa isiniif uumes ni dhiiftuu? Dhugumatti isin ummata daangaa dabritoota” [jedhe].');
INSERT INTO "apaguna" VALUES(26,167,'26:167','[Isaan] ni jedhan: “Yaa Luux! Yoo [waan jettu irraa] hin dhorgamin, dhugumatti warra [biyyarraa] baafaman irraa taata.”');
INSERT INTO "apaguna" VALUES(26,168,'26:168','[Luuxis] ni jedhe: “Dhugumatti, ani namoota dalagaa keessan jibban irraayyi.');
INSERT INTO "apaguna" VALUES(26,169,'26:169','Yaa Gooftaa kiyya! Anaafi maatii kiyya waan isaan dalagan irraa nu baraari.”');
INSERT INTO "apaguna" VALUES(26,170,'26:170','Isaafi maatii isaas walumaa galatti ni baraarsine.');
INSERT INTO "apaguna" VALUES(26,171,'26:171','Jaartii tokko kan warra [adaba keessatti] hafan irraa taate malee');
INSERT INTO "apaguna" VALUES(26,172,'26:172','Ergasii warra biroo ni balleessine.');
INSERT INTO "apaguna" VALUES(26,173,'26:173','Bokkaa [adabbii] isaan irratti roobsineRoobni warra akeekkachiifamanii waa fokkate.');
INSERT INTO "apaguna" VALUES(26,174,'26:174','Dhugumatti, kana keessa mallattootu jiraIrra hedduun isaaniis Mu’uminoota hin taane.');
INSERT INTO "apaguna" VALUES(26,175,'26:175','Dhugumatti Gooftaan kee Isatu injifataa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(26,176,'26:176','.Jiraattonni Aykah (bosonaa) ergamoota sobsiifte.');
INSERT INTO "apaguna" VALUES(26,177,'26:177','Yeroo Shu’ayb isaaniin jedhu: “sila Rabbiin hin sodaattanuu?');
INSERT INTO "apaguna" VALUES(26,178,'26:178','Ani isiniif ergamaa amanamaadha.');
INSERT INTO "apaguna" VALUES(26,179,'26:179','Rabbiin sodaadhaa; ergaa kiyyas fudhadhaa.');
INSERT INTO "apaguna" VALUES(26,180,'26:180','Ani isa (waamicha kana) irratti mindaa tokkollee isin hin gaafadhuMindaan kiyya Gooftaa aalama hundaa irra malee hin taane.');
INSERT INTO "apaguna" VALUES(26,181,'26:181','Safara guutaa; [haqa namaa] hir’iftoota irraa hin ta’inaa.');
INSERT INTO "apaguna" VALUES(26,182,'26:182','Madaala sirrii ta’een madaalaa.');
INSERT INTO "apaguna" VALUES(26,183,'26:183','Namoota wantoota isaanii irraa hin hir’isinaa; badii hojjattoota taataniis dechii keessa badiin hin deeminaa.');
INSERT INTO "apaguna" VALUES(26,184,'26:184','Isa isiniifi dhaloota duraanii uume sodaadhaa.”');
INSERT INTO "apaguna" VALUES(26,185,'26:185','[Isaan] ni jedhan: “Ati namoota sihriin (falfalli) baay’een itti godhame irraa isa tokkoodha.');
INSERT INTO "apaguna" VALUES(26,186,'26:186','Ati nama fakkii keenyaa malee (uumaa biroo) hin taane; nutis sobdoota irraa ta’uu si yaadna.');
INSERT INTO "apaguna" VALUES(26,187,'26:187','Yoo warra dhugaa dubbatan irraa taate samii irraa cittuu wahii nurratti kuffisi” [jedhaniin].');
INSERT INTO "apaguna" VALUES(26,188,'26:188','[Inni] ni jedhe: “Gooftaan kiyya waan isin dalagdan beekaadha.”');
INSERT INTO "apaguna" VALUES(26,189,'26:189','Isa sobsiisanAdabbiin guyyaa gaaddisaa (duumessa gurraachaa) isaan qabateDhugumatti, inni adabbii guyyaa guddaa ta’eera.');
INSERT INTO "apaguna" VALUES(26,190,'26:190','Dhugumatti, kana keessa mallattootu jiraGaruu irra hedduun isaanii mu’uminoota hin taane.');
INSERT INTO "apaguna" VALUES(26,191,'26:191','Dhugumatti, Gooftaan kee Isatu injifataa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(26,192,'26:192','Dhugumatti, inni (Qur’aanni) buufamaa Gooftaa aalama hundaati');
INSERT INTO "apaguna" VALUES(26,193,'26:193','Ruuhii amanamaa ta’e (Jibriiltu) isaan bu’e');
INSERT INTO "apaguna" VALUES(26,194,'26:194','Akka ati akeekkachiiftota irraa taatuuf jecha onnee kee irratti [buuse].');
INSERT INTO "apaguna" VALUES(26,195,'26:195','Qooqa Arabiffaa ifaa ta’een.');
INSERT INTO "apaguna" VALUES(26,196,'26:196','Dhugumatti, inni (waa’een Qur’aanaa) kitaabota durii keessa jira.');
INSERT INTO "apaguna" VALUES(26,197,'26:197','Sila beektonni Ilmaan Israa’iil isa beekuun mallattoo isaaniif (warra Makkaatiif) hin ta’uu?');
INSERT INTO "apaguna" VALUES(26,198,'26:198','Odoo garii nama Araba hin ta’inii irratti isa (Qur’aana) buufnee,');
INSERT INTO "apaguna" VALUES(26,199,'26:199','Innis isaan irratti dubbisee, isaan kan isatti amanan hin taane.');
INSERT INTO "apaguna" VALUES(26,200,'26:200','Akka kanatti isa (diddaa Qur’aanaa) onnee badi raawwattootaa keessa seensifne');
INSERT INTO "apaguna" VALUES(26,201,'26:201','Hanga adabbii laalessaa ta’e arganitti isatti hin amananu');
INSERT INTO "apaguna" VALUES(26,202,'26:202','Inni (adabbichi) odoo isaan hin beekin tasa isaanitti dhufa');
INSERT INTO "apaguna" VALUES(26,203,'26:203','[Akka amannuuf] “yeroon nuuf kennamuu danda’aa?” jedhu');
INSERT INTO "apaguna" VALUES(26,204,'26:204','Sila adabbii keenya ariifachiifatuu?');
INSERT INTO "apaguna" VALUES(26,205,'26:205','Mee naaf himi! Odoo waggoota hedduuf isaan qanansiifnee,');
INSERT INTO "apaguna" VALUES(26,206,'26:206','Sana booda wanti isaan ittiin akeekkachiifamaa turan isaanitti dhufee,');
INSERT INTO "apaguna" VALUES(26,207,'26:207','Wanti isaan ittiin qanani’aa turan [adaba Rabbii irraa] maal isaan irraa deebisa?');
INSERT INTO "apaguna" VALUES(26,208,'26:208','Magaalaa tokkollee akeekkachiisaan isaaniif jiraatu malee isaan hin balleessine');
INSERT INTO "apaguna" VALUES(26,209,'26:209','[Kun] yaadachiisa; Nuti miidhaa hojjattoota hin taane.');
INSERT INTO "apaguna" VALUES(26,210,'26:210','Sheyxaanonnis isaan [Qur’aanaan] hin buune.');
INSERT INTO "apaguna" VALUES(26,211,'26:211','Isaaniifis hin malu[Isa fiduu] hin danda’anus');
INSERT INTO "apaguna" VALUES(26,212,'26:212','Dhugumatti, isaan isa dhagahuu irraa fageeffamoodha');
INSERT INTO "apaguna" VALUES(26,213,'26:213','Rabbii waliin gooftaa biraa hin kadhatin[Yoo akkas goote] warra adabaman irraa taata.');
INSERT INTO "apaguna" VALUES(26,214,'26:214','Firoota kee dhihoo ta’an akeekkachiisi.');
INSERT INTO "apaguna" VALUES(26,215,'26:215','Mu’uminoota irraa nama si hordofeef nyaara (kooluu) kee gadi qabi.');
INSERT INTO "apaguna" VALUES(26,216,'26:216','Yoo ajaja kee fudhachuu didanis “Ani waan isin hojjattan irraa qulqulluudha” jedhiin.');
INSERT INTO "apaguna" VALUES(26,217,'26:217','[Rabbii] injifataa, rahmata godhaa ta’e irratti hirkadhu.');
INSERT INTO "apaguna" VALUES(26,218,'26:218','Isa yeroo ati dhaabbattu si argu [irratti hirkadhu]');
INSERT INTO "apaguna" VALUES(26,219,'26:219','[Isa] sochiiwwan ati warra sagadan keessatti gootu [argu irratti hirkadhu]');
INSERT INTO "apaguna" VALUES(26,220,'26:220','Dhugumatti, Inni Isatu argaa, beekaadha.');
INSERT INTO "apaguna" VALUES(26,221,'26:221','Sheyxaanonni eenyu irratti akka bu’an isinitti himuu?');
INSERT INTO "apaguna" VALUES(26,222,'26:222','Kijibaa, badii hojjataa hunda irratti buuti');
INSERT INTO "apaguna" VALUES(26,223,'26:223','Wanta dhagahanis [waa himtuu irratti] gadi buusuIrra baay’een isaanii immo kijibdoota');
INSERT INTO "apaguna" VALUES(26,224,'26:224','Walaleessitoota jallattootatu isaan hordofa.');
INSERT INTO "apaguna" VALUES(26,225,'26:225','Isaan dhugumatti sulula (bowwaa) hunda keessa akka seenan hin agartuu?');
INSERT INTO "apaguna" VALUES(26,226,'26:226','Dhugumatti isaan waan hin dalagne kan dubbatan ta’uu [hin agartuu]?');
INSERT INTO "apaguna" VALUES(26,227,'26:227','Warra amananiifi gaggaarii hojjatan, kan baay’inaan Rabbiin faarsan, erga miidhamanii booda [walaloodhaan] of irraa deebisan [harka kaffalatan] malee[Isaan hin komatanu]Isaan miidhaa dalaganis [eega du’anii booda] deebifama akkamii akka deebifaman beekuuf jiru.');
INSERT INTO "apaguna" VALUES(27,1,'27:1','Xaa SiinKun keeyyattoota Qur’aanaafi kitaaba ibsaa ta’eeti');
INSERT INTO "apaguna" VALUES(27,2,'27:2','Mu’uminootaaf qajeelfamaafi gammachiisaadha.');
INSERT INTO "apaguna" VALUES(27,3,'27:3','(Isaan) warra Salaata dhaaban, zakaa kennaniifi isaan Aakhiraatti amananiidha.');
INSERT INTO "apaguna" VALUES(27,4,'27:4','Dhugumatti, isaan Aakhiraatti hin amanin, [gadhee] dalagaa isaanii isaaniif miidhagsineIsaanis ni dhama’u.');
INSERT INTO "apaguna" VALUES(27,5,'27:5','Isaan sun warra [addunyaa keessatti] adabbiin hamaan isaaniif jiruudhaIsaan Aakhiraatti isaanumatu hoonga’oodha.');
INSERT INTO "apaguna" VALUES(27,6,'27:6','Dhugumatti, ati Qur''aana [Rabbii] ogeessa, beekaa ta’e irraa qunnamta');
INSERT INTO "apaguna" VALUES(27,7,'27:7','Yeroo Muusaan maatii isaatiin “ani ibidda argeen jira; [deemeetin] ishee irraa odeeffannoo [waa’ee karaa] isiniif fidaYookiin immoo akka isin ho’ifattaniif xombooraa qabsiifamaa wahii isiniif fida” jedhe [yaadadhu].');
INSERT INTO "apaguna" VALUES(27,8,'27:8','Yeroma inni ishee bira dhaqu akkana jechuun isatti lallabame: “Inni ibidda keessa jiruufi Inni cinaa ishee jiru khayriin isaa baay’ate; Rabbiin gooftaa aalamaa ta’es qulqullaahe');
INSERT INTO "apaguna" VALUES(27,9,'27:9','Yaa Muusaa! Dubbiin Ani Allaah injifataa, ogeessa ta’eedha');
INSERT INTO "apaguna" VALUES(27,10,'27:10','Ulee kee darbi.” [Ni darbes] yeroma akka bofaatti kan isheen sochootu argu of duuba deebi’ee dheesseDuuba hin ilaalles“Yaa Muusaa! hin sodaatin; dhugumatti ani ergamtoonni na biratti hin sodaatanu');
INSERT INTO "apaguna" VALUES(27,11,'27:11','Garuu Namni miidhaa dalagee ergasii hamtuu boodaan toltuu bakka buuse dhugumatti Ani [isaaf] araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(27,12,'27:12','Harka kee bobaa kee keessa seensisi; isheen fokkachuudhaan maleetti adii taatee baatiMallattoowwan saglaniin gara Fir’awniifi ummata isaa (deemi)Dhugumatti isaan ummata finciltoota ta’anii jiru.”');
INSERT INTO "apaguna" VALUES(27,13,'27:13','Yeroma mallattoowwan keenya ifa taatee isaanitti dhuftu “kun Sihrii (falfala) ifa bahaadha” jedhan.');
INSERT INTO "apaguna" VALUES(27,14,'27:14','Odoo lubbuun isaanii mirkaneessituu, miidhaafi boonaaf jecha ishee morman; booddeen badii raawwattootaa akkam akka ture ilaali.');
INSERT INTO "apaguna" VALUES(27,15,'27:15','Dhugumatti, Nuti Daawuudiifi Suleeymaaniif beekumsa kennineerraIsaan lamaanis “faaruun hundi kan Rabbii Isa gabroota Isaa mu’uminootaa baay’ee irra nu caalchiseeti” jedhan.');
INSERT INTO "apaguna" VALUES(27,16,'27:16','Suleeymaan Daawuudiin ni dhaaleNi jedhes: “Yaa namootaa! Qooqa simbiraa barsiifamneerraWanti hundi nuuf kennamee jiraDhugumatti, kun tola ifa bahaadha.”');
INSERT INTO "apaguna" VALUES(27,17,'27:17','Suleeymaaniif waraanni isaa jinnoota, namootaafi allaattii irraa walitti qabame; isaanis walitti ittifamoodha');
INSERT INTO "apaguna" VALUES(27,18,'27:18','Yeroma holqa mixii (Dagandaa) irra dhufan, mixiin tokko “Yaa tuuta mixii! akka Suleeymaaniifi waraanni isaa odoo hin beekin isin hin caccabsineef manneen keessan seenaa” jette');
INSERT INTO "apaguna" VALUES(27,19,'27:19','Innis jecha ishee irraa kolfuun ni seeqateNi jedhes: “Yaa Gooftaa kiyya! Akkan ni’imaa kee ishee anaafi abbaa haadha kiyya qanansiifte galateeffadhuufi hojii gaarii ati jaalattu hojjadhu na barsiisiRahmata keetiinis gabroota kee gaggaarii keessa na seensisi.”');
INSERT INTO "apaguna" VALUES(27,20,'27:20','Simbirroos to’atee ni jedhe: “maaliifan aduddee (Hud-hud) hin argine? Moo sila isumatu fagaattota irraa ta’e?');
INSERT INTO "apaguna" VALUES(27,21,'27:21','Dhugumatti, adabbii cimaan isa adabaYookiin isan gorra’aYookiin immoo ragaa ifaa ta’een natti haa dhufu.”');
INSERT INTO "apaguna" VALUES(27,22,'27:22','[Yeroo hanga] fagoo hin taane turee ni jedhe: “waan ati hin barin bareen jiraSaba’i irraas oduu dhugaa ta’een sitti dhufeera.');
INSERT INTO "apaguna" VALUES(27,23,'27:23','Ani Dubartii isaan bulchitu kan Waan hunda kennamte, isheedhaafis sireen guddaan jirun arge.');
INSERT INTO "apaguna" VALUES(27,24,'27:24','Isheefi ummata ishee kan Rabbii gaditti Aduuf sagadanin argeSheyxaann dalagaa isaanii isaaniif miidhagsee karaa [qajeelaa] irraa isaan deebiseIsaan hin qajeelanu.');
INSERT INTO "apaguna" VALUES(27,25,'27:25','Akka isaan Rabbii waan dhokataa Samiifi Dachii ifatti baasuufi waan isin dhoksitaniifi mul’iftan beekuuf sujuuda hin goone [isaan dhorgee jira]');
INSERT INTO "apaguna" VALUES(27,26,'27:26','Allahn Isa malee dhugaan gabbaramaan hin jiruInni Gooftaa arshii guddaati.”');
INSERT INTO "apaguna" VALUES(27,27,'27:27','[Suleymaan] ni jedhe: “Dhugaa jetteefi warra sobdoota irraa taatee kee fuuldura ilaalla');
INSERT INTO "apaguna" VALUES(27,28,'27:28','Xalayaa kiyya kanaan deemii gara isaaniitti darbadhuSana booda isaan irraa gara galii waan isaan deebisan ilaali.”');
INSERT INTO "apaguna" VALUES(27,29,'27:29','[Mootittiinis] ni jette: “Yaa qondaaltota [kiyya]! Dhugumatti, ani xalayaan kabajamaan natti darbameera.');
INSERT INTO "apaguna" VALUES(27,30,'27:30','Dhugumatti, inni Suleymaan irraayyiInnis [qabiyyeen isaa] ‘maqaa Rabbii akkaan mararfataa akkaan rahmata godhaa ta’eetiin’.');
INSERT INTO "apaguna" VALUES(27,31,'27:31','Narratti hin booninaa; Muslimtoota ta’aatii natti kottaa [jedha].”');
INSERT INTO "apaguna" VALUES(27,32,'27:32','Ni jette: “Yaa qondaaltota [kiyya]! Dhimma kiyya irratti furmaata na akeekaa; Ani hangan isin hirmaattanitti dhimma kamillee murteessaa hin taane.”');
INSERT INTO "apaguna" VALUES(27,33,'27:33','[Qondaalonnis] “Nuti abbootii humnaafi abbootii lola cimaatiAjajni gara keetiMaal akka ajajju ilaali” jedhan');
INSERT INTO "apaguna" VALUES(27,34,'27:34','Ni jette: “Mootonni yeroo magaalaa tokko seenan ishee balleessuKabajamtoota ishees ni xiqqeessuIsaanis akkasuma dalagu.');
INSERT INTO "apaguna" VALUES(27,35,'27:35','Dhugumatti ani kennaa wahii gara isaaniitti ergituudhaWaan ergamtoonni ittiin deebi’anus ilaaltuudha.”');
INSERT INTO "apaguna" VALUES(27,36,'27:36','Yeroma Suleymaanitti dhufu ni jedhe: “Sila isin qabeenyaan na gargaartuu? Wanti Rabbiin naaf kenne waan isiniif kenne ni caalaDhugumatti isin kennaa keessaniin gammaddu.');
INSERT INTO "apaguna" VALUES(27,37,'27:37','Gara isaanii deebi’iNuti waraana isaan dura dhaabbachuu hin dandeenyeen isaanitti dhufnaIshee irraa xiqqaatoo ta’anii salphinaan isaan baafna.”');
INSERT INTO "apaguna" VALUES(27,38,'27:38','Ni jedhe: “Yaa qondaaltota [kiyya]! Odoo isaan Muslimtoota ta’anii natti hin dhufin eenyutu Isin irraa siree ishee natti fida?”');
INSERT INTO "apaguna" VALUES(27,39,'27:39','Jabaan Jinnoota irraa ta’e tokko ni jedhe: “Ani odoo ati iddoo murtee keetii irraa hin ka''in duran isaan sitti dhufaDhugumatti, ani kana irratti akkaan cimaa amanamaadha.”');
INSERT INTO "apaguna" VALUES(27,40,'27:40','Inni beekumsi kitaaba irraa ta’e isa bira jiru tokko immoo ni jedhe: “Ani [libsattee] odoo ijji kee sitti hin deebinen siif fida.” [Suleymaan] yeroma isa biratti ragga’ee argu ni jedhe: “kun tola Gooftaa kootii irraayyiAkka ani Isa galateeffadhuufi Isatti kafaru na mokkoruuf [kana godhe]Namni Isa galateeffate, kan inni galateeffatu lubbuma isaatiifNamni kafares [isuma irratti]Dhugumatti Gooftaan kiyya dureessa, arjaadha” [jedhe]');
INSERT INTO "apaguna" VALUES(27,41,'27:41','Ni jedhe: “siree ishee halluu isaa isheef jijjiiraa[Siree ishee beekuutti] ni qajeeltimoo warra hin qajeelle irraa taatii ilaalla.”');
INSERT INTO "apaguna" VALUES(27,42,'27:42','Yeroma isheen dhuftus sireen kee akka kanaa jedhameIsheenis “inni akkuma waan isaati” jette[Suleymaanis] “dura ishee beekumsa kennamnee Muslimtoota taaneerra” jedhe.');
INSERT INTO "apaguna" VALUES(27,43,'27:43','Wanti isheen Rabbii gaditti gabbaraa turte [Islaama irraa] ishee deebiseDhugumatti, isheen ummata kaafirootaa irraa turte');
INSERT INTO "apaguna" VALUES(27,44,'27:44','“Gamoo seeni” isheedhaan jedhameYeroma isa argitu haroo isa seetee mogolee ishee lamaan irraa saaqxe[Suleymaan] ni jedhe: “Dhugumatti, inni kun masaraa mucuceeffamaa daawwitii irraa ta’ee [bishaan keessaan mul’atuudha].” [Isheenis] “Gooftaa kiyya! Ani lubbuu kiyya miidheeraSuleymaanii wajjin Allaah Rabbii aalama hundaa ta’etti harka kenne (Islaamawe)” jette.');
INSERT INTO "apaguna" VALUES(27,45,'27:45','Dhugumatti, gara Samuudittis obboleessa isaanii Saalih “Allaah qofa gabbaraa” jechuun erginee jirra; Yerama san isaan garee lama ta’anii wal falman.”');
INSERT INTO "apaguna" VALUES(27,46,'27:46','[Saalihis] ni jedhe: “Yaa ummata kiyya! Maaliif toltuu dura hamtuu (adabbii) jarjarfattu? Akka rahmanni isinii godhamuuf maaliif Rabbiin araarama hin kadhanne?”');
INSERT INTO "apaguna" VALUES(27,47,'27:47','[Isaan] “Nuti siifi namoota si waliin jiraniin hooddane (milkii laallanne)” jedhan[Saalihis] “hooddannaan keessan Rabbiin biraGaruu isin ummata mokkoramoodha” jedhe.');
INSERT INTO "apaguna" VALUES(27,48,'27:48','Magalattii keessa namoota sagal kan gaarii hin hojjanne lafa balleesantu ture');
INSERT INTO "apaguna" VALUES(27,49,'27:49','Ni jedhan: “Dhugumatti isaafi maatii isaa halkan keessa akka balleessinee sana booda fira isaatiin ‘nuti ajjeechaa maatii isaa bira hin jirru; nuti dhugaa dubbattoota’ jechuuf Rabbiin waliif kakadhaa.”');
INSERT INTO "apaguna" VALUES(27,50,'27:50','Isaan tooftaa baasanNutis odoo isaan hin beekne tooftaa itti baasne.');
INSERT INTO "apaguna" VALUES(27,51,'27:51','Booddeen tooftaa isaanii akkam akka ta’e ilaali! Dhugumatti, Nuti isaaniifi ummata isaanii guutumatti balleessine.');
INSERT INTO "apaguna" VALUES(27,52,'27:52','Kun sababa isaan miidhaa hojjataniin ontuu haala taateen manneen isaaniitiDhugumatti, kana keessa namoota beekaniif mallattootu jira');
INSERT INTO "apaguna" VALUES(27,53,'27:53','Isaan amananii Rabbiin sodaataa turan bilisa baafne.');
INSERT INTO "apaguna" VALUES(27,54,'27:54','Luuxiinis yeroo inni ummata isaatiin “sila odoo argitanii gocha fokkataa hojjattuu?” jedhe [yaadadhu].');
INSERT INTO "apaguna" VALUES(27,55,'27:55','“Sila isin fedhii qunnamtii saalaatiif dubartii dhiiftanii dhiiratti dhuftuu? Dhugumatti isin ummata wallaaltota.”');
INSERT INTO "apaguna" VALUES(27,56,'27:56','Deebiin ummata isaas “Maatii Luux magaalaa keessan irraa baasaaDhugumatti, isaan namoota qulqulleeffatanii” jechuu malee hin taane.');
INSERT INTO "apaguna" VALUES(27,57,'27:57','Isaafi maatii isaa ni baraarsineGaruu niitii isaa warra [adabbii keessatti] hafan irraa ta’uu ishee murteessine');
INSERT INTO "apaguna" VALUES(27,58,'27:58','Rooba [dhagaa] isaan irratti roobsineRoobni warra dinniinamanii fokkateera.');
INSERT INTO "apaguna" VALUES(27,59,'27:59','[Yaa Muhammad] jedhi: “Faaruun hundi kan Rabbiiti; gabroota Isaa kan [Inni] filate irras nageenyi haa jiraatuSila Rabbi moo wantoota isaan [Isatti] dabalantu caala?”');
INSERT INTO "apaguna" VALUES(27,60,'27:60','Sila Inni samiifi dachii uumee, samii irraa bishaan isiniif buusee ergasii ashaakiltiiwwan miidhagoo ta’an isaan magarsine [akka isa homaa hin uumneetii]? Mukkeen ishee biqilchuun dandeettii keessanii mitiSila Allaah wajjin gooftaan biraa ni jiraa? Isaan ummata Rabbitti qixxeessaniidha');
INSERT INTO "apaguna" VALUES(27,61,'27:61','Sila Isa dachii tasgabbooftuu taasise, kan ishee keessattis laggeen taasise, kan isheef gaarreen gadi lixan taasise, kan gidduu galaanota lamaatti girdoo taasise moo [warra isaan isatti dabalantu caala]? Sila Rabbii wajjin gabbaramaan biraa ni jiraa? Dhugumatti irra hedduun isaanii hin beekanu');
INSERT INTO "apaguna" VALUES(27,62,'27:62','Sila Isa yeroo namni rakkataan (muddamaan) Isa kadhate jalaa owwaatee hamtuu saaquufi bakka bu’oota dachii isin taasisu [moo waan isaan Rabbitti dabalantu caala?] Sila Rabbii wajjin gabbaramaan biraa ni jiraa? Hangi isin gorfamtan xiqqaadha.');
INSERT INTO "apaguna" VALUES(27,63,'27:63','Sila eenyutu dukkana dachiifi galaanaa keessatti isin qajeelcha; ammas eenyutu qilleensa rahmata Isaa dura gammachiisaa godhee erga? Sila Rabbii wajjiin gabbaramaan biraa ni jiraa? Allaahn waan isaan [Isatti] qindeessan irraa ol ta’e.');
INSERT INTO "apaguna" VALUES(27,64,'27:64','Sila inni uumuu jalqabee ergasii isa deebisu eenyu? Ammas eenyutu dechiifi samii irraa soorata isiniif kenna? Sila gabbaramaan biraa Allaah wajjin ni jiraa? Yoo kan dhugaa dubbattan taataniif ragaa keessan fidaa” jedhi.');
INSERT INTO "apaguna" VALUES(27,65,'27:65','“Wanti samiifi dachii keessaa hunduu Rabbiin malee wanta fagoo (ghaybii) hin beekuYoom akka kaafamanis hin beekanu” jedhi.');
INSERT INTO "apaguna" VALUES(27,66,'27:66','Sila beekumsa isaaniitu waa’ee Aakhiraatti dhaqqabee? Lakkisaa isaan ishee irraa shakkii keessa jiru; dhugumatti isaan ishee irraa jaamoodha.');
INSERT INTO "apaguna" VALUES(27,67,'27:67','Isaan kafaran ni jedhan: “Sila yeroo [duunee] biyyee taane, nuufi abbootiin keenya dhugumatti ni kaafamnaa?');
INSERT INTO "apaguna" VALUES(27,68,'27:68','Dhugumatti, nuufi abbootiin keenya nuun duratti kana waadaa galamnee turreGaruu kunis ogbarruu warra durii malee hin taane” [jedhan].');
INSERT INTO "apaguna" VALUES(27,69,'27:69','“Dachii keessa deemaatii booddeen badii raawwattootaa akkam akka ta’e ilaalaa” jedhi.');
INSERT INTO "apaguna" VALUES(27,70,'27:70','Isaan irratti hin gaddinTooftaa isaan baasanuufis dhiphina keessatti hin ta’in');
INSERT INTO "apaguna" VALUES(27,71,'27:71','“Yoo dhugaa dubbattoota taatan waadaan kun yoomi?” jedhu');
INSERT INTO "apaguna" VALUES(27,72,'27:72','“Gariin wanta isin ariifachiifattanii kan isinitti dhihaate ta’uun dhugaadha” jedhi.');
INSERT INTO "apaguna" VALUES(27,73,'27:73','Dhugumatti, Rabbiin kee namoota irratti abbaa tola guddaatiGaruu irra hedduun isaanii hin galateeffatan');
INSERT INTO "apaguna" VALUES(27,74,'27:74','Dhugumatti, Rabbiin kee waan qomti isaanii dhoksiteefi waan isaan ifatti mul’isan ni beeka');
INSERT INTO "apaguna" VALUES(27,75,'27:75','Wanti fagoon samiifi dechii keessaa kamiyyuu, kitaaba ifa galaa keessa jiraatu malee hin hafu.');
INSERT INTO "apaguna" VALUES(27,76,'27:76','Dhugumatti, Qur’aanni kun Ilmaan Israa’iil irratti irra hedduu waan isaan isa keessatti wal dhabanii isaaniif odeessa.');
INSERT INTO "apaguna" VALUES(27,77,'27:77','Dhugumatti inni mu’uminootaaf qajeelfamaafi rahmata.');
INSERT INTO "apaguna" VALUES(27,78,'27:78','Dhugumatti, Rabbiin kee murtii Isaatiin gidduu isaanitti murteessaInni injifataa, beekaadha');
INSERT INTO "apaguna" VALUES(27,79,'27:79','Allaah irratti hirkadhuDhugumatti, ati dhugaa ifa ta’e irra jirta.');
INSERT INTO "apaguna" VALUES(27,80,'27:80','Dhugumatti, ati namoota du’an hin dhageessiftuNamoota gurri duudes yeroo isaan dugda sitti deebisuun garagalan hin dhageessiftu.');
INSERT INTO "apaguna" VALUES(27,81,'27:81','Namoota jaamaa jallina isaanii irraa kan isaan qajeelchitu hin taaneNamoota Muslimtoota ta’anii keeyyattoota keenyatti amanan malee kan dhageessiftu hin taane.');
INSERT INTO "apaguna" VALUES(27,82,'27:82','Yeroo jechi (adabbiin) isaan irratti murtaa’e bineensa “namoonni keeyyattoota keenya kan hin dhugoomsine ta’aniiru” jettee isaanitti dubbattu dachii irraa isaaniif baafna');
INSERT INTO "apaguna" VALUES(27,83,'27:83','Guyyaa ummata hunda irraa garee warra keeyyattoota keenya sobsiisan irraa ta’an walitti qabnee isaan walitti ittifaman [yaadadhu].');
INSERT INTO "apaguna" VALUES(27,84,'27:84','Yeroma isaan dhufanu “sila isin odoo beekumsaan ishee hin marsin keeyyattoota kiyya sobsiiftanii? Moo maaluma hojjataa turtan?” jedhaan.');
INSERT INTO "apaguna" VALUES(27,85,'27:85','Waan isaan miidhaa dalaganiif jechi (adabbiin) isaan irratti argameKanaafuu isaan sababa hin dhiheeffatanu.');
INSERT INTO "apaguna" VALUES(27,86,'27:86','Sila Akka nuti halkan yeroo isa keessa boqataniifi guyyaa immoo kan keessa arganii (hojjatan) isaaniif goone hin ilaallee? Dhugumatti, kana keessa namoota amananiif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(27,87,'27:87','Guyyaa garrii (xurumbaa) keessa afuufamu wantoonni samii keessa jiranuufi dechii keessa jiran hunduu wantoota Rabbiin fedhe malee yeroo isaan rifatan [wanta ta’u yaadadhu]Hundumtuu salphatoo ta’anii gara Isaa (Rabbii) dhufan.');
INSERT INTO "apaguna" VALUES(27,88,'27:88','Gaarreenis odoo isheen akka duumessaatti deemtuu kan dhaabbattu ta’uu ishee yaadaa argitaHojii Rabbii wanta hunda jabeessee [ilaali]Dhugumatti Inni waan isin hojjattan keessa beekaadha.');
INSERT INTO "apaguna" VALUES(27,89,'27:89','Nama gaarii dalage mindaa gaarii sana caalutu isaaf jira; isaan guyyaa san rifannaa irraa nagaa bahoodha');
INSERT INTO "apaguna" VALUES(27,90,'27:90','Namni hamtuu dalage fuulli isaanii ibidda keessatti gonbifamtee “sila waan dalagaa turtan malee mindeeffamtuu?” [isaaniin jedhama].');
INSERT INTO "apaguna" VALUES(27,91,'27:91','Kan ani ajajame Gooftaa biyyattii kanaa kan ishee kabaje gabbaruu qofaWanti hunduu kan isaatiMuslimoota irraa ta’uuttis ajajameera.');
INSERT INTO "apaguna" VALUES(27,92,'27:92','Qur’aana dubbisuuttis (ajajameera)Namni qajeele, kan inni qajeelu lubbuma isaatiifiNama jallates “Ani akeekkachiiftota irraayi malee kan biraa hin taane” jedhiin.');
INSERT INTO "apaguna" VALUES(27,93,'27:93','“Faaruun hundi kan AllaahtiInnis fuulduratti mallattoolee Isaa isinitti agarsiisaIsinis ishee beekuuf jirtu” jedhiinRabbiin kee waan isin dalagdan irraa dagataa miti.');
INSERT INTO "apaguna" VALUES(28,1,'28:1','Xaa Siin Miim.');
INSERT INTO "apaguna" VALUES(28,2,'28:2','Kun keeyyattoota kitaaba ifa ba’aati');
INSERT INTO "apaguna" VALUES(28,3,'28:3','Oduu Muusaafi Fir’awn irraa ummata amananiif dhugaadhaan sirratti dubbifna.');
INSERT INTO "apaguna" VALUES(28,4,'28:4','Dhugumatti, Fir’awn Dachii keessatti of tuulee jiraNamoota ishees garee garee taasiseeraGaree isaan irraa ta’e ni hacuucaIlmaan isaanii kan dhiiraa ni ajjeesa; kan dubaraa immoo ni jiraachisaDhugumatti inni mancaaftota irraa ture');
INSERT INTO "apaguna" VALUES(28,5,'28:5','Nuti isaan lafa irratti cunqurfaman (hacuucaman) irratti tola oollee, matootii isaan goonee dhaaltotas isaan taasisuu feena');
INSERT INTO "apaguna" VALUES(28,6,'28:6','Dachii keessas isaaniif mijeessuu [feena]Fir’awniin, Haamaaniifi waraana isaan lamaanii, isaan irraa waan sodaachaa turan agarsiisuu [feena]');
INSERT INTO "apaguna" VALUES(28,7,'28:7','Gara haadha Muusaas “isa hoosisi; yeroo isa irratti sodaatte, galaana keessatti isa darbiHin sodaatin; hin gaddinisDhugumatti, nuti isa gara keetti deebisoofi ergamoota irraa isa taasisoodha” jechuun beeksisa goone.');
INSERT INTO "apaguna" VALUES(28,8,'28:8','Maatiin Fir’awn akka diinaafi gadda isaanitti ta’uuf isa fudhatanDhugumatti Fir’awn, Haamaanifi waraanni isaan lamaanii dogongortoota turan');
INSERT INTO "apaguna" VALUES(28,9,'28:9','Haati manaa Fir’awn ni jette: “anaafi siif gammachuu ijaatii isa hin ajjeesinaa; nu fayyaduun yookiin ilma isa taasifachuun keenya ni kajeelama.” Isaanis haala hin beekneen [isheen bulan].');
INSERT INTO "apaguna" VALUES(28,10,'28:10','Onneen haadha Muusaa duwwaa ta’eAkka isheen mu’uminoota irraa taatuuf jecha odoo onnee ishee jabeessuu baannee, [Ilma ishee ta’uu] isaa ifa gochuutti dhihaattee turte');
INSERT INTO "apaguna" VALUES(28,11,'28:11','Obboleettii isaatiinis “isa hordofi” jetteen; isheenis fagoo irraa odoo isaan hin beekne isa argite.');
INSERT INTO "apaguna" VALUES(28,12,'28:12','[Gara haadhaatti deebi’uun] dura hoosiftoota biraa irraa isa dhorginee turre[Obboleettin isaas] “Maatii isa isiniif guddisuufi gaarii isaaf yaadu irratti isin qajeelchuu?” jetteen.');
INSERT INTO "apaguna" VALUES(28,13,'28:13','Akka Ijji ishee gammadduufi hin yaaddofneef, akkasumas akka waadaan Rabbii dhugaa ta’uu beektuuf jecha gara haadha isaatti isa deebifneGaruu irra hedduun isaanii hin beekan.');
INSERT INTO "apaguna" VALUES(28,14,'28:14','Yeroma inni jabina ga’ee umrii dargaggummaa guutu ogummaafi beekumsa isaaf kennineAkkuma kanatti toltuu hojjattoota mindeessina.');
INSERT INTO "apaguna" VALUES(28,15,'28:15','Yeroo ummanni ishee daguu keessa jiranitti magaalattii seenee, namoota lama kan wal lolan argeKuni (inni tokko) lammii isaa irraa yoo ta’u, inni kun immoo diina isaa irraa tureInni lammii isaa irraa ta’e isa diina isaa irraa ta’e irratti tumsa isa gaafannaan Muusaan abootteen rukutee isa ajjeese[Muusaan] ni jedhe: “kun dalagaa Sheyxaanaa irraayyiDhugumatti, inni diina jallisaa ifa bahaadha.”');
INSERT INTO "apaguna" VALUES(28,16,'28:16','“Yaa Gooftaa kiyya! Ani lubbuu kiyya miidheeraa Ati naaf araarami” jedheInnis isaaf araarameDhugumatti, Inni Isumatu araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(28,17,'28:17','“Yaa Gooftaa kiyya! Waan narratti tola oolteen gargaaraa yakkamtootaa hin ta’u” jedhe.');
INSERT INTO "apaguna" VALUES(28,18,'28:18','Magaalattii keessatti sodaataa of eeggataa ta’ee bariisifateYoma san inni kaleessa gargaarsa isa gaafate isatti lallabnaan Muusaan “dhugumatti ati jallataa ifa bahaadha” isaan jedhe.');
INSERT INTO "apaguna" VALUES(28,19,'28:19','Yeroma inni isa isaan lamaaniifuu diina ta’e qabuu fedhe, innis “Yaa Muusaa! Sila akkuma kaleessa lubbuu ajjeefte anas ajjeesuu feetaa? Ati dachii keessatti abbaa irree ta’uu malee hin feetu; toltuu hojjattoota irraas ta’uu hin feetu” jedheen');
INSERT INTO "apaguna" VALUES(28,20,'28:20','Gurbaan tokko magaalattii irraa fiigaa dhufee “Yaa Muusaa! Dhugumatti, qondaalonni [Fir’awn] si ajjeesuuf sirratti mari’achaa jiru; ani dhugumatti namoota toltuu siif yaadan irraayii [magaalattii irraa] bahi” jedhe');
INSERT INTO "apaguna" VALUES(28,21,'28:21','Sodaataafi of eeggataa ta’ee ishee irraa bahe“Yaa Rabbii kiyya! Ummata miidhaa hojjattoota ta''an jalaa nagaya na baasi” jedhe.');
INSERT INTO "apaguna" VALUES(28,22,'28:22','Yeroma gara Madyan gara galu “gooftaan kiyya karaa sirrii akka na qajeelchun kajeela” jedhe.');
INSERT INTO "apaguna" VALUES(28,23,'28:23','Yeroma bishaan Madyan bira ga’u garee namoota irraa ta’e kan [beyladoota isaanii] bishaan obaasan argee, isaan boodas dubartoota lama kan beyladoota isaanii ittisan argee “maali haalli keessan?” isaaniin jedheIsaanis “Nuti hanga tiksiteen obaasanii geeffatanitti hin obaafnuAbbaan keenya jaarsa guddaadha” jedhan.');
INSERT INTO "apaguna" VALUES(28,24,'28:24','Isaan lamaaniif obaasee sana booda gara gaaddisaa deebi’ee “yaa Gooftaa kiyya! Dhugumatti, ani toltuu irraa waan Ati naaf murteessitetti haajomaadha” jedhe.');
INSERT INTO "apaguna" VALUES(28,25,'28:25','Ergasii tokkoon isaan lamaanii saalfachaa deemuun isatti dhuftee “dhugumatti, abbaan kiyya mindaa waan nuuf obaaftee siif kafaluuf si waama” jetteYeroma inni itti dhufee seenaa isatti himu “hin sodaatin; ummata miidhaa hojjattoota jalaa baateetta” jedheen.');
INSERT INTO "apaguna" VALUES(28,26,'28:26','Tokkoon Isaan lamaaniis “Yaa abbaa kiyya! Isa nuuf ramadiDhugumatti, irra caalaan namoota ati ramadduu isa jabaa, amanamaadha” jette');
INSERT INTO "apaguna" VALUES(28,27,'28:27','[Inni] ni jedhe: “Ani tokkoo shamarree koo kana lamaanii woggaa saddeet na tajaajiluu kee irratti sitti heerumsiisuun fedhaKudhan yoo guuttes [sun tola] si biraa ta’e; sirratti jabeessuu hin fedhuYoo Rabbiin fedhe gaggaarii irraa ta’uu na arguuf jirta.”');
INSERT INTO "apaguna" VALUES(28,28,'28:28','[Muusaanis] ni jedhe: “kun [walii galtee] gidduu kiyyaafi keetiitiBeellama lamaan irraa kamiyyuu yoon guute, daangaa dabruun narra hin jiru.[] Rabbii waan nuti jennu irratti ragaadha.”');
INSERT INTO "apaguna" VALUES(28,29,'28:29','Yoroma Muusaan beellama guutee maatii isaatiin deemu, cinaa [gaara] xuuritti ibidda argee maatii isaatiin “asuma turaa; ani ibiddan argee, ishee biraa oduu wahii yookiin xonbooraa ibidda irraa ta’een akka ho’ifattanuuf isinitti dhufuun koo ni kajeelamaa” jedhe');
INSERT INTO "apaguna" VALUES(28,30,'28:30','Yeroma ishee bira ga’u moggaa lagaa gara mirgaa irraa, dachii barakeeffamtuu keessatti, mukattii irraa “yaa Muusaa! Dhugumatti, Ani Allaah Rabbii aalama hundaati” jechuun itti lallabame.');
INSERT INTO "apaguna" VALUES(28,31,'28:31','“Ulee kee darbi” [jechuunis itti lallabame]Yeroma akka waan bofa taateetti kan sochootu taatee ishee argu, of duuba deebi’ee dheesse; duubas hin mil’anne“Yaa Muusaa! As garagali; hin sodaatinDhugumatti, ati warra nagaa bahan irraayi” [jedhameen]');
INSERT INTO "apaguna" VALUES(28,32,'28:32','“Harka kee bobaa kee keessa seensisiFokkina tokko malee adii taatee baatiSodaa irraa [bilisa ta’uuf] harka kee gara cinaacha keetiitti maxxansi.[] Isheen kun lamaan mallattoo lama kan Rabbii kee irraa gara Fir’awniifi qondaaltota isaatti ergamaniidhaDhugumatti, isaan namoota finciltoota turan.”');
INSERT INTO "apaguna" VALUES(28,33,'28:33','Innis: “Yaa Gooftaa kiyya! Dhugumatti, ani isaan irraa nama tokko ajjeeseen jiraNa ajjeesuun sodaadha” jedhe.');
INSERT INTO "apaguna" VALUES(28,34,'28:34','“Obboleessi kiyya Haaruun, inni arrabni isaa ana caalaatti [haasawa] ifa godhaKanaafuu gargaaraa na dhugoomsu godhii ana waliin isa ergiAni na sobsiisuu isaaniin sodaadha.”');
INSERT INTO "apaguna" VALUES(28,35,'28:35','[Rabbiinis] ni jedhe: “Nuti fuulduratti irree kee obolleessa keetiin jabeessinee isin lamaaniifis aangoo goonaKanaafuu (hamtuun) gara keessan hin ga’anuMallattoolee keenyaan isin lamaaniifi namoonni isin hordofan injifatoodha.”');
INSERT INTO "apaguna" VALUES(28,36,'28:36','Yeroma Muusaan mallattoolee keenya ifa taateen isaanitti dhufu “kun sihrii uumame malee waan biraatii mitiWaa’ee kanaa abbootii keenya kan durii keessatti dhageenyee hin beeknu” jedhan.');
INSERT INTO "apaguna" VALUES(28,37,'28:37','Muusaanis ni jedhe: “Rabbiin kiyya nama Isa biraa qajeelumaan dhufeefi nama booddeen ganda Aakiraa isaaf taatus caalaatti beekaDhugumatti, miidhaa raawwattoonni hin milkaa’anu.”');
INSERT INTO "apaguna" VALUES(28,38,'28:38','Fir’awnis “yaa qondaaltota kiyya! Ana malee gabbaramaa biraa isiniif hin beekuYaa Haamaan! Dhoqqee irratti ibidda naaf bobeessii gamoo dheeraa naaf godhi; gara Rabbii Muusaa ilaaluun koo ni kajeelamaDhugumatti ani sobdoota irraa ta’uun isa yaada” jedhe.');
INSERT INTO "apaguna" VALUES(28,39,'28:39','Inniniifi waraanni isaa dachii keessatti haqaan malee boonanii, dhugumatti gara keenyatti deebifamuu dhabuu yaadan.');
INSERT INTO "apaguna" VALUES(28,40,'28:40','Isaafi waraana isaa qabneeti galaana keessatti isaan darbineBooddeen miidhaa hojjattootaa akkam akka ta’e ilaali.');
INSERT INTO "apaguna" VALUES(28,41,'28:41','Oggantoota gara Ibiddaatti nama waaman isaan taasifneGuyyaa Qiyaamaas hin tumsamanu.');
INSERT INTO "apaguna" VALUES(28,42,'28:42','Addunyaa kana keessattis abaarsa isaan hordofsiifneGuyyaa Qiyaamaas isaan warra fokkisiifamoo irraayyi.');
INSERT INTO "apaguna" VALUES(28,43,'28:43','Erga dhaloota duraanii balleessinee booda beekumsa, qajeelumaafi rahmata namaaf ta’ee akka isaan ittiin gorfamanuuf, dhugumatti Muusaaf kitaaba kenninee jirra');
INSERT INTO "apaguna" VALUES(28,44,'28:44','[Yaa Muhammad] yeroo Nuti ergaa keenya gara Muusaatti dabarsine ati moggaa tabbaa kan gara dhihaa irra hin turre; hirmaattota irraas hin taane.');
INSERT INTO "apaguna" VALUES(28,45,'28:45','Haa ta’u malee Nuti [sana booda] dhaloota baay’ee uumnee waggoonni isaan irratti dheerannaan [si ergine]Ati jiraattota Madyan gidduus jiraataa taatee, keeyyattoota keenya isaan irratti kan dubbiftu hin taaneHaa ta’u malee Nuti [ergaa kanaan si] ergoo taanee jirra.');
INSERT INTO "apaguna" VALUES(28,46,'28:46','Yeroo Nuti [Muusaatti] lallabne ati gaara Xuur maddii hin turreHaa ta’u malee ummata siin duratti akeekkachiisaan itti hin dhufin haa gorfamaniif akka akeekkachiiftuuf jecha rahmata Rabbii kee irraa ta’een [si ergine].');
INSERT INTO "apaguna" VALUES(28,47,'28:47','Odoo waan harki isaanii dabarsiteef balaan isaan tuqee “yaa Rabbii keenya! keeyyattoota kee hordofnee mu’uminoota irraas akka taanu, maaliif ergamaa gara keenyatti hin ergine” jechuu baatanii [silaa si hin erginu turre].');
INSERT INTO "apaguna" VALUES(28,48,'28:48','Yeroma dhugaan Nu biraa isaanitti dhufu “Inni maaliif fakkaataan waan Muusaan kennamee isaaf hin kennamne?” jedhanSila isaan duraan waan Muusaaf kennametti kafaranii hin turree? “[Tawraatiifi Qur’aanni] Sihrii lama kan wal gargaaraniidha” jedhan“Dhugumatti, nuti hundattuu kafaroodha” jedhan');
INSERT INTO "apaguna" VALUES(28,49,'28:49','[Yaa Muhammad] “yoo dhugaa kan dubbattan taataniif, mee kitaaba Rabbiin biraa ta’e isaan lamaan irra qajeelaa ta’e qabadhaa kottaa[Yoo san] anis isa nan hordofaa” jedhiin.');
INSERT INTO "apaguna" VALUES(28,50,'28:50','Yoo siif hin owwaatin, fedhii isaanii qofa hordofuu isaanii beekiNama qajeeluma Rabbi irraa ta’een alatti fedhii ofii hordofe caalaa namni jallate tokkoyyuu hin jiruRabbiin ummata miidhaa hojjattoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(28,51,'28:51','Dhugumatti, Nuti akka isaan gorfamanuuf jecha [Qur’aana] isaaniif ifa goonee jirra.');
INSERT INTO "apaguna" VALUES(28,52,'28:52','Warri isa (Qur’aana) dura kitaaba isaaniif kennine, isaan isatti ni amanu');
INSERT INTO "apaguna" VALUES(28,53,'28:53','Yeroo inni isaan irratti qara’amus “Isatti amanne; dhugumatti, inni haqa Rabbii keenya irraa ta’eedhaNuti isa duras Muslimtoota turre” jedhan.');
INSERT INTO "apaguna" VALUES(28,54,'28:54','Isaan sun waan obsanii, hamtuus toltuudhaan deebisanii, waan Nuti isaaniif kennine irraas kennataniif jecha mindaa isaanii yeroo lama kennamu.');
INSERT INTO "apaguna" VALUES(28,55,'28:55','Yeroo dhara dhaga’anis, irraa garagalaniiti “nuuf dalagaa keenyatu jira; isiniifis dalagaa keessantu jiraNageenyi isin irra haa jiraatuNuti wallaaltota hin barbaannu” jedhan');
INSERT INTO "apaguna" VALUES(28,56,'28:56','Dhugumatti, ati nama jaallatte hin qajeelchituGaruu Allaahtu nama fedhe qajeelchaInnis warra qajeelan beekaadha');
INSERT INTO "apaguna" VALUES(28,57,'28:57','[Isaan] “yoo si waliin karaa qajeelumaa hordofne dachii keenya irraa butamna (guuramna)” jedhanSila nuti [iddoo] kabajamaa, nagayaa, soorata Nu biraa ta’eef fuduraaleen wanta hundaa gara isheetti kan guuramu isaaniif hin mijeessinee? Haa ta’u malee irra hedduun isaanii hin beekanu.');
INSERT INTO "apaguna" VALUES(28,58,'28:58','[Warra] gandaa irraas kan jireenya ishee keessatti boontee ishee balleessine hedduudhaIsheen sun gandoota isaanii kan isaaniin booda xiqqoo malee keessa hin jiraataminiidha.[] Nutis dhaaltota taaneerra.');
INSERT INTO "apaguna" VALUES(28,59,'28:59','Rabbiin kee [warra] magaalotaa hanga irra guddoo ishee keessatti ergamaa keeyyattoota keenya isaan irratti dubbisu ergutti kan balleessu hin taaneNuti warra magaalotaa qubattoonni ishee miidhaa hojjattoota ta’anii malee kan balleessinu hin taane.');
INSERT INTO "apaguna" VALUES(28,60,'28:60','Wantoota irraa kan isiniif kenname hundi mi’a (meeshaa) jireenya addunyaatiifi miidhagina ishee qofaWanta Allaah bira jirutu akkaan caalaafi hafaadhaSila hin xiinxaltanuu?');
INSERT INTO "apaguna" VALUES(28,61,'28:61','Sila namni Nuti waadaa gaarii (jannata) waadaa isaaf gallee inni kan isa qunnamu ta’e, akka nama qananii jireenya addunyaa isa qanansiifnee sana booda inni Guyyaa Qiyaamaa warra [jahannam irra] fidamoo irraa ta’uutii?');
INSERT INTO "apaguna" VALUES(28,62,'28:62','Guyyaa [Rabbiin] “Hiriyoonni kiyya kan isin dubbataa turtan sun eessa jiru?” jechuun isaanitti lallabu san [yaadadhu]');
INSERT INTO "apaguna" VALUES(28,63,'28:63','Warri jechi (adabbiin) isaan irratti mirkanaa’e “Yaa Rabbii keenya! Isaan kun warra nuti jallifneedhaAkkuma ofii jallannetti isaan jallifneNuti gara keetti quulqulloofnee, isaan kun nu gabbaraa hin turre” jedhan.');
INSERT INTO "apaguna" VALUES(28,64,'28:64','“Shariikota keessan waammadhaa” jedhameeniIsaan ni waammatu; jarris isaaniif hin owwaatanu, adabbii [ibiddaas] ni arganOdoo qajeeluun isaanii argameera ta’ee [adabbii hin argan turan].');
INSERT INTO "apaguna" VALUES(28,65,'28:65','Guyyaa [Rabbiin] isaanitti lallabee “Ergamtootaaf maal deebiftan?” jedhus [yaadadhu].');
INSERT INTO "apaguna" VALUES(28,66,'28:66','Guyyaa san oduun isaan jalaa dhokattiIsaan walis hin gaafatanu.');
INSERT INTO "apaguna" VALUES(28,67,'28:67','Namni tawbatee, [Rabbitti] amanee, gaarii hojjate milkaa’ota irraa ta’uun isaa mirkanaa’aadha.');
INSERT INTO "apaguna" VALUES(28,68,'28:68','Rabbiin kee waan fedhe uuma; ni filatasIsaaniif garuu filannoon hin jiruAllaahn waan isaan itti dabalan irraa qulqullaa’ee olis ta’eera');
INSERT INTO "apaguna" VALUES(28,69,'28:69','Rabbiin kee waan qomti isaanii dhoksituufi waan isaan mul’isanis ni beeka.');
INSERT INTO "apaguna" VALUES(28,70,'28:70','Inni AllaahdhaIsa malee dhugaan gabbaramaan hin jiruFaaruun hundi addunyaafi Aakhiraa keessatti kan IsaatiMurtiinis kan IsaatiGaruma Isaa qofa deebifamtu.');
INSERT INTO "apaguna" VALUES(28,71,'28:71','“Mee naaf odeessaa! Yoo Rabbiin halkan walitti aansee hanga Guyyaa Qiyaamaatti tursiise, sila Rabbiin malee gabbaramaan ifaan isiniif dhufu eenyu? Sila isin hin dhageessanuu?” jedhiin.');
INSERT INTO "apaguna" VALUES(28,72,'28:72','Ammas “mee naaf odeessaa! yoo Rabbiin guyyaa walitti aansee hanga Guyyaa Qiyaamatti tursiise, sila Rabbiin malee gabbaramaan halkan isa keessa boqottan isiniif fidu eenyu? Sila isin hin argitanuu?” jedhi.');
INSERT INTO "apaguna" VALUES(28,73,'28:73','Rahmata Isaa irraayyi halkaniifi guyyaa akka isa (halkan) keessa boqottanii [guyyaa] tola Isaa irraa barbaaddattanii fi, akka Isa galatoomfattaniif isinii taasise.');
INSERT INTO "apaguna" VALUES(28,74,'28:74','Guyyaa [Rabbiin] “hiriyoonni kiyya kan isin dubbataa turtan sun eessa jiru?” jechuun isaanitti lallabu san [yaadadhu]');
INSERT INTO "apaguna" VALUES(28,75,'28:75','Ummata hunda irraa ragaa fuuneeti “ragaa keessan fidaa” jennaanYeroo san dhugaan kan Rabbii qofa ta’uu beekuWanti isaan uummatanis isaan irraa bada.');
INSERT INTO "apaguna" VALUES(28,76,'28:76','Dhugumatti, Qaaruun ummata Muusaa irraa tureIsaan irrattis daangaa dabreKuusaa qabeenyaa irraas waan furtuuleen isaa garee jajjaboo ta’anitti ulfaattu isaaf kennineYeroo ummanni isaa “[qabeenyaan] hin boonin; dhugumatti, Allaahn warra boonan hin jaalatu” isaan jedhan san [yaadadhu].');
INSERT INTO "apaguna" VALUES(28,77,'28:77','“Waan Rabbiin siif kenne keessattis mana Aakhiraa ittiin barbaadiAddunyaa irraas qooda kee hin dagatinAkkuma Allaahn tola gara keetti oole atis tola ooliDachii keessattis badii [facaasuu] hin barbaadinDhugumatti Allaahn balleessitoota hin jaalatu.”');
INSERT INTO "apaguna" VALUES(28,78,'28:78','[Qaaruunis] “wanti an isa kennameef beekumsa na bira jiruuf jecha” jedheDhaloota baay’ee irraa nama inni humnaanis isa (Qaaruun) irra cimaa ta’e, kan qabeenya kuufachuuttiis isa irra baay’ee ta’e dhugumatti isa dura Rabbiin balleessuu hin beeknee? Yakka hojjattoonni badii isaanii irraa hin gaafatamanu');
INSERT INTO "apaguna" VALUES(28,79,'28:79','Faaya isaa uffatee ummata isaa irratti baheIsaan jireenya addunyaa fedhan “fakkaataan waan Qaaruuniif kennamee nuuf ta’uu hawwina; dhugumatti inni abbaa qooda guddaati” jedhan');
INSERT INTO "apaguna" VALUES(28,80,'28:80','Isaan beekumsa kennamanis ni jedhan: “yaa badii keessan! Nama amanee, gaarii hojjateef mindaa Rabbiitu irra caalaKanas obsitoota malee eenyunuu hin kennamu.”');
INSERT INTO "apaguna" VALUES(28,81,'28:81','Isaafi ganda isaatis ni dhidhimsine (dachii ittiin kunne)Gareen Rabbiin alaa kan isa tumsan isaaf hin taaneInnis warra of baraarsan irraa hin taane.');
INSERT INTO "apaguna" VALUES(28,82,'28:82','Isaan kaleessa iddoo isaa hawwan “edaa Rabbiin dhugumatti gabroota Isaa irraa nama fedheef soorata bal’isee [nama fedhetti] immoo dhiphisa! Odoo Rabbiin ni’imaa Isaa nurratti ooluu baatee, silaa nuun kuta (dhidhimsa) tureEdaa dhugumatti kaafiroonni hin milkaa’anu!” jedhaa bulan.');
INSERT INTO "apaguna" VALUES(28,83,'28:83','Ganda Aakhiraa san warra dachii keessatti ol aantummaafi badii [raawwachuu] hin feeneef ishee goonaBooddeen (gaariin) warra Rabbiin sodaataniifi');
INSERT INTO "apaguna" VALUES(28,84,'28:84','Namni gaarii dalage waan san irra caalutu isaaf jiraNamni hamtuun dhufes, [fakkii ishee argata]Warri hojii gadhee hojjatan waan hojjataa turan malee hin mindeeffamanu.');
INSERT INTO "apaguna" VALUES(28,85,'28:85','Dhugumatti, Inni Qur’aana si irratti buuse, gara iddoo deebiitti (Makkaatti) si deebisa.[] “Rabbiin kiyya nama qajeelumaan dhufeefi nama jallina ifa bahaa ta’e keessa jiru akkaan beekaadha” jedhi.');
INSERT INTO "apaguna" VALUES(28,86,'28:86','Rahmata Rabbii kee irraa ta’een [sirratti buufne malee] ati kitaabni gara keetti buufamuu kajeelaa hin turreKaafirootaaf gargaaraa hin tahin.');
INSERT INTO "apaguna" VALUES(28,87,'28:87','Erga isheen gara keetti buufamtee booda keeyyattoota Rabbii irraa isaan si hin deebisinGara Rabbii keetiitti waamiMushrikoota irraas hin ta’in.');
INSERT INTO "apaguna" VALUES(28,88,'28:88','Rabbii wajjiin gabbaramaa biraa hin kadhatin (hin gabbarin)Dhugaan gabbaramaan Isa malee hin jiruFuula Isaa malee wanti hundi ni badaMurtiinis kan IsaatiGaruma Isaatti deebifamtu');
INSERT INTO "apaguna" VALUES(29,1,'29:1','Alif Laam Miim');
INSERT INTO "apaguna" VALUES(29,2,'29:2','Sila namoonni “amanne” jechuudhaan odoo hin mokkoramin dhiifamuu yaaduu?');
INSERT INTO "apaguna" VALUES(29,3,'29:3','Dhugumatti, Nuti warra isaaniin duraas mokkorree jirraDhugumatti, Rabbiin isaan dhugaan [amanne] jedhan beekuuf jira, sobdootas beekuuf jira .');
INSERT INTO "apaguna" VALUES(29,4,'29:4','Sila isaan hojii gadhee dalagan Nu jalaa bahuu yaaduu? Wanti isaan murteessan waa fokkate!');
INSERT INTO "apaguna" VALUES(29,5,'29:5','Namni qunnamtii Allaah sodaate, dhugumatti beellamni Rabbii dhufaadhaInni dhagayaa, beekaadhas.');
INSERT INTO "apaguna" VALUES(29,6,'29:6','Namni qabsaa’e kan inni qabsaa’u lubbuma isaatiifiRabbiin Dhugumatti, aalama hunda irraa dureessa.');
INSERT INTO "apaguna" VALUES(29,7,'29:7','Isaan amananii gaggaarii hojjatan badii isaanii isaan irraa haqnaDhugumatti, gaarii waan isaan dalagaa turanii isaan mindeessina');
INSERT INTO "apaguna" VALUES(29,8,'29:8','Namaaf haadhaafi abbaa isaatti tola ooluu dhaamneYoo waan ati beekumsa isaa hin qabne akka Natti qindeessituuf sirratti qabsaayan, isaan lamaaniin hin bulinDeebiin keessanis garuma kiyyaWaan hojjachaa turtanis isinittin odeessa.');
INSERT INTO "apaguna" VALUES(29,9,'29:9','Isaan amananii gaggaarii hojjatan, namoota gaggaarii keessa isaan galchina.');
INSERT INTO "apaguna" VALUES(29,10,'29:10','Namoota irraa nama “Rabbitti amanneerra” jedhaa yeroo karaa Rabbii keessatti rakkifame, rakkina namni isa irratti raawwatu akka adabbii Rabbii taasisutu jiraYoo injifannoon Rabbii kee irraa dhufes dhugumatti “nuti isinii wajjin turre” jedhuSila Allaahn waan qoma aalamaa keessa jiru akkaan beekaa hin taanee?');
INSERT INTO "apaguna" VALUES(29,11,'29:11','Dugumatti Allaahn warra amanan ni beekaMunaafiqootas ni beeka.');
INSERT INTO "apaguna" VALUES(29,12,'29:12','Isaan kafaran warra amananiin “karaa keenya hordofaa; nuti cubbuu keessan isin irraa baadhannaa” jedhanIsaan badii isaanii irraa homaa hin baadhatanuDhugumatti, isaan sobdoota.');
INSERT INTO "apaguna" VALUES(29,13,'29:13','Dhugumatti, isaan ba’aa isaaniitiifi ba’aa biraas kan isaanii wajjin ni baadhatuDhugumatti, isaan waan soba uuman irraa Guyyaa Qiyaamaa ni gaafatamu');
INSERT INTO "apaguna" VALUES(29,14,'29:14','Dhugumatti, nabi Nuuhiin gara ummata isaatti ergineerra[Inni] waggaa kuma tokkoof, waggaan shantamni odoo hafuu isaan keessa tureOdoo isaan miidhaa hojjattoota ta’anii jiranii bishaaniin dhidhimfamuun isaan qabe.');
INSERT INTO "apaguna" VALUES(29,15,'29:15','Isaafi warra doonii (keessa turan) ni baraarsine; aalama hundaaf mallattoo ishee goone.');
INSERT INTO "apaguna" VALUES(29,16,'29:16','Ibraahiimiinis yeroo inni ummata isaatiin “Allaah gabbaraaIsa sodaadhaaYoo kan beektan taataniif santu irra isiniif caala” jedhe [yaadadhu].');
INSERT INTO "apaguna" VALUES(29,17,'29:17','“Wanti isin Rabbii gaditti gabbartan taabotuma qofa; cubbuus ni uumtuDhugumatti, isaan isin Rabbiin alatti gabbartan soorata isiniif kennuutti dandeettii hin qabanuSoorata Rabbuma biraa barbaadaaIsa gabbaraasGalatas Isaaf galchaaGaruma Isaa deebifamtu.');
INSERT INTO "apaguna" VALUES(29,18,'29:18','Yoo sobsiiftanis, dhugumatti ummanni isin duraas sobsiisanii jiruErgamaa irras geessuu ifa ta’e malee homaa hin jiru');
INSERT INTO "apaguna" VALUES(29,19,'29:19','Sila isaan akka Rabbiin uumuu eegalee ergasii isa deebisu hin ilaalanuu? Sun Allaah irratti laafaadha.');
INSERT INTO "apaguna" VALUES(29,20,'29:20','[Yaa Muhammad!] “dachii keessa deemaatii akka Rabbiin uumuu itti jalqabe ilaalaa! Sana booda Allaahn kaasiinsa dhumaa ni kaasaDhugumatti, Rabbiin waan hunda irratti danda’aadha” jedhiin.');
INSERT INTO "apaguna" VALUES(29,21,'29:21','Nama fedhe ni adaba; nama fedhes rahmata isaaf godhaGara Isaa qofatti deebifamtu.');
INSERT INTO "apaguna" VALUES(29,22,'29:22','Isin dachiifi samii keessatti kan dadhabsiiftan hin taaneRabbiin ala jaalalleefi tumsaan isiniif hin jiru');
INSERT INTO "apaguna" VALUES(29,23,'29:23','Isaan keeyyattoota Rabbiitiifi qunnamtii Isaatti kafaran, isaan sun rahmata kiyya irraa abdii kutatanii jiruIsaan sun adabbii laaleessaa ta’etu isaaniif jira.');
INSERT INTO "apaguna" VALUES(29,24,'29:24','Deebiin ummata isaas “Isa ajjeesaa yookiin isa gubaa” jechuu malee waan biraa hin taaneAllaahnis Ibidda irraa isa baraarsee jiraDhugumatti, kana keessa namoota amananiif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(29,25,'29:25','[Ibraahiimis] “Isin Rabbii gaditti jiruu addunyaa keessatti sanama wanti tolfattaniif jaalala jidduu keessaniitiif qofaSana booda Guyyaa Qiyaamaa gariin keessan garitti kafaraGariin keessan garii abaarasManni jireenya keessaniis ibidda ta’a; tumsitoonni homaatuu isiniif hin jiru” jedhe.');
INSERT INTO "apaguna" VALUES(29,26,'29:26','Luuxis isaaf (Ibraahiimiif) ni amane(Ibraahiimis) “ani gara Rabbii kiyyaa godaanaadhaDhugumatti, Inni injifataa, ogeessa” jedhe.');
INSERT INTO "apaguna" VALUES(29,27,'29:27','Is-haaqiifi Ya’iquubiinis isaaf kennineSanyii isaa keessa nabiyyummaafi Kitaaba ni taasifneMindaa isaa addunyaa keessattis isaaf kennineDhugumatti, inni Aakhiratti namoota gaggaarii irraayi.');
INSERT INTO "apaguna" VALUES(29,28,'29:28','Luuxiinis yeroo inni ummata isaatiin “dhugumatti, isin gocha fokkataa kan aalama hunda irraa ummanni tokkollee isin dura hin hojjatin raawwattu” jedheen (erginee jirra).');
INSERT INTO "apaguna" VALUES(29,29,'29:29','“Sila isin (qunnamtii saalatiif) dhiiratti dhuftuu?! (Saamichaanis) karaa kuttuu? Iddoo Yaa’ii keessanii keessattis gochoota jibbamoo ta’an raawwattuu? Deebiin ummata isaa “yoo warra dhugaa dubbatan irraa taateef, adabbii Rabbiitiin nutti kottu” jechuu malee homaa hin taane.');
INSERT INTO "apaguna" VALUES(29,30,'29:30','(Luuxis) “Gooftaa kiyya! Ummata balleessitoota kanneen irratti na tumsi” jedhe.');
INSERT INTO "apaguna" VALUES(29,31,'29:31','Yeroma ergamtoonni keenya oduu gammachiisaan Ibraahiimitti dhufan “nuti dhugumatti, jiraattota magaalaa kanaa waan warri ishee miidhaa hojjattoota ta’aniif isaan balleessoodha” jedhan.');
INSERT INTO "apaguna" VALUES(29,32,'29:32','(Ibraahiimis) “Dhugumatti Luux ishee keessa jira” jedhe(Maleykonnis) “nuti nama ishee keessa jiru akkaan beekna; dhugumatti, haadha manaa isaa kan warra hafan keessatti taatu malee isaafi maatii isaa ni baraarsina” jedhan.');
INSERT INTO "apaguna" VALUES(29,33,'29:33','Yeroma ergamtoonni keenya Luuxitti dhufan isaaniif yaadda’ee qomti isaas isaaniif dhiphatteIsaanis “hin sodaatin; hin yaadda’inisDhugumatti, nuti siifi maatii kee nagaa baafnaGaruu niitiin kee warra (adaba keessatti) hafan irraa taati” jedhan.');
INSERT INTO "apaguna" VALUES(29,34,'29:34','“Nuti jiraattota magaalaa kanaa irratti waan isaan finciltoota ta’aniif samii irraa adabbii cimaa buusoodha” jedhan');
INSERT INTO "apaguna" VALUES(29,35,'29:35','Dhugumatti, Nuti ishee irraa namoota sammuu qabaniif mallattoo ifa taate hanbifnee jirra');
INSERT INTO "apaguna" VALUES(29,36,'29:36','Gara (ummata) Madyanis obboleessa isaanii Shu’aybiin (erginee jirra)Innis “Yaa ummata kiyya! Allaah qofa gabbaraaGuyyaa Aakhiraas sodaadhaaBalleessitoota taataniis badii dachii keessa hin tamsaasinaa” jedheen.');
INSERT INTO "apaguna" VALUES(29,37,'29:37','Isa kijibsiisanKirkirri (sochiin) lafaas isaan qabatteYoosuu ganda isaanii keessatti jilbeenfatanii dhumanii bulan.');
INSERT INTO "apaguna" VALUES(29,38,'29:38','Aadiifi Samuudiinis ni balleessine; (Isaan balleessuun keenyas) gandoota isaanii irraa ifa isiniif ta’ee jiraOdoo kan argan ta’anii jiranuu shayxaanni hojii isaanii isaaniif miidhagsee, karaa haqaa irraa isaan deebise');
INSERT INTO "apaguna" VALUES(29,39,'29:39','Qaaruuniin, Fir’awniifi Haamaaniinis (balleessineerra)Dhugumatti, Muusaan ragaa ifa ta’een isaanitti dhufeeti, dachii keessatti boonanii (isaan qabne)Kan Nu jalaa miliqanis hin taane');
INSERT INTO "apaguna" VALUES(29,40,'29:40','Hundumaa badii isaatiif isa qabneIsaan irraa namoota bubbee cirrachaa itti erginetu jiraIsaan irraas namoota iyyansi isaan qabetu jiraIsaan irraas namoota dachii ittiin kunnetu jiraIsaan irraas namoota bishaan nyaachifnetu jiraRabbiin kan isaan miidhu hin taaneGaruu isaan lubbuu ofii kan miidhan ta’aniiru.');
INSERT INTO "apaguna" VALUES(29,41,'29:41','Fakkeenyi isaan Rabbii gaditti jaalallee godhatanii (gabbaranii) akka birbirooftuu (sharariitii) mana ijaarratteetiDhugumatti odoo kan beekan ta’anii irra laafaan manaa mana birbirooftuuti.');
INSERT INTO "apaguna" VALUES(29,42,'29:42','Dhugumatti, Rabbiin waan hunda irraa waan isaan Isaa gaditti kadhatan ni beekaInni injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(29,43,'29:43','Fakkeenyota san namootaaf ishee ni goona; beektota malees ishee hin hubatu.');
INSERT INTO "apaguna" VALUES(29,44,'29:44','Rabbiin samiifi dachii dhugaan uumeDhugumatti, sana keessa mu’uminootaaf mallattootu jira.');
INSERT INTO "apaguna" VALUES(29,45,'29:45','(Yaa Muhammad!) kitaaba irraa waan gara kee buufame dubbisiSalaatas haqa isaa guutii sagadiDhugumatti, salaanni wantoota fokkatoofi jibbamoo ta’an irraa dhorgitiZikrii (faaruu) Rabbiitu (waan hunda irra) guddaadhaRabbiin waan isin hojjattan ni beeka.');
INSERT INTO "apaguna" VALUES(29,46,'29:46','Warra kitaabaatiin falmii gaariin malee hin falminaa; isaan irraa warri miidhaa dalagan odoo hafanuuNuti “isa gara keenyatti bufameefi gara keessanittis buufametti amanneerraGabbaramaan keenyaafi gabbaramaan keessanis tokkoNuti isaaf ajajamoodha” jedhaa.');
INSERT INTO "apaguna" VALUES(29,47,'29:47','Akkuma (warra si duraa irratti buufne) kitaaba gara keetti buufneIsaan kitaaba kennineef isatti ni amanuIsaan kana irraas (warra makkaa irraas) namoota isatti amanutu jiraKaafiroota malee keeyyattoota keenya namni mormu hin jiru.');
INSERT INTO "apaguna" VALUES(29,48,'29:48','Ati (yaa Muhammad!) Isa dura kitaaba tokkollee kan dubbiftuufi harka keetiinis barreessitu hin turreOdoo akkas taatee warri badii ni shakku turan.');
INSERT INTO "apaguna" VALUES(29,49,'29:49','Garuu inni keeyyattoota ifa ta’an kan qoma warra beekumsa kennamee keessa jiruudhaMiidhaa hojjattoota malee keeyyattoota keenya namni mormu hin jiru');
INSERT INTO "apaguna" VALUES(29,50,'29:50','Isaanis ni jedhan: “Maaliif raajiiwwan Gooftaa Isaa biraa isa irratti hin buufamin? (Atis) “raajiwwan kan jiran Allaah bira qofaAni immoo akeekkachiisaa ifa ta’e qofa” jedhiin');
INSERT INTO "apaguna" VALUES(29,51,'29:51','Sila kitaaba (Qur’aana) isaan irratti qara’amu si irratti buusuun keenya qofti isaaniif hin ga’uu? Dhugumatti, kana keessa namoota amananiif rahmataafi yaadannootu jira.');
INSERT INTO "apaguna" VALUES(29,52,'29:52','Jedhi: “jidduu kiyyaafi keessanitti ragaa ta’uuf Rabbiin gahaadhaWaan samiifi dachii keessa jiru hundaa ni beekaIsaan sobatti amananiifi Rabbitti kafaran, warri sun isaanumatu hoonga’oodha.');
INSERT INTO "apaguna" VALUES(29,53,'29:53','Adabatti si ariifachiisuOdoo beellamni beekamaan jiraachuu baatee silaa adabbiin (addunyaattis) isaanitti dhufa tureDhugumatti akka tasaa odoo isaan hin beekin isaanitti dhufa');
INSERT INTO "apaguna" VALUES(29,54,'29:54','Adabbiitti si ariifachiisuDhugumatti, jahannam kaafirootatti marsituudha.');
INSERT INTO "apaguna" VALUES(29,55,'29:55','Guyyaa adabbiin (Ibiddi) gubbaa isaanii irraafi miila isaanii jalaan isaan haguugu, (Maleykan ibiddaas) “Waan dalagaa turtan dhandhamaa” jedha.');
INSERT INTO "apaguna" VALUES(29,56,'29:56','“Yaa gabroota kiyya warra amantan! Dachiin kiyya bal’oodhaa, Anuma qofa gabbaraa.”');
INSERT INTO "apaguna" VALUES(29,57,'29:57','Lubbuun hunduu du’a ni dhandhamtiSana booda gara keenyatti deebifamtu.');
INSERT INTO "apaguna" VALUES(29,58,'29:58','Isaan amananii gaggaarii hojjatan, dhugumatti jannata irraa manneen jireenyaa kan laggeen ishee jala yaatu, kan yeroo hunda ishee keessa jiraatan ta’anii isaan qubsiifnaMindaan hojjattootaa waa tole!');
INSERT INTO "apaguna" VALUES(29,59,'29:59','[Isaan] warra obsaniifi Gooftaa isaanii irratti hirkataniidha.');
INSERT INTO "apaguna" VALUES(29,60,'29:60','Lubbu qabeeyyii irraa kan soorata isaanii hin baadhanne kan Rabbiin isiniifi isaanis sooru hedduudha');
INSERT INTO "apaguna" VALUES(29,61,'29:61','Dhugumatti “eenyutu samiifi dachii uumee, aduufi baatiis laaffise?” jettee yoo isaan gaafatte “Allaahdha” siin jedhuAkkamitti (tawhiida irraa) garagalfamu ree?');
INSERT INTO "apaguna" VALUES(29,62,'29:62','Rabbiin gabroota Isaa irraa nama fedheef soorata ni bal’isa(Nama fedhe immoo) isatti dhiphisaDhugumatti, Rabbiin waan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(29,63,'29:63','Dhugumatti, yoo “eenyutu samii irraa bishaan buusee, dachii erga isheen duutee booda ittiin jiraachise?” jettee isaan gaafatte “Allaahdha” jedhu(Yaa Muhammad!) “Faaruun hunduu kan Allaahti” jedhiGaruu irra hedduun isaanii hin hubatan.');
INSERT INTO "apaguna" VALUES(29,64,'29:64','Jireenyi addunyaa kun qoosaafi tapha malee waan biraa hin taaneDhugumatti, manni Aakhiraa odoo beektanii isheetu jireenya dhugaati.');
INSERT INTO "apaguna" VALUES(29,65,'29:65','Yeroo doonii yaabbatan, amantii Isaaf qulqulleessuun Allaah qofa kadhatuYeroo Inni gara dachiitti nagaan isaan baase immoo yeroma san (Rabbitti) qindeessu');
INSERT INTO "apaguna" VALUES(29,66,'29:66','Waan nuti isaaniif kenninetti kafaruufi qanani’anuuf (Rabbitti qindeessu)Garuu (waan booddeen isaanii ta’u) fuula duratti ni beeku');
INSERT INTO "apaguna" VALUES(29,67,'29:67','Sila isaan namoonni moggaa isaaniitii (ajjeechaafi saamichaan) kan bubbutaman osoo ta’anuu akka nuti (Makkaa) iddoo kabajamaa, tasgabbaayaa taasifne hin ilaalanuu? Sila sobatti amananii ni’imaa Rabbiitti kafaruu?');
INSERT INTO "apaguna" VALUES(29,68,'29:68','Nama Rabbirratti soba uumee odeesse yookiin dhugaan itti dhufnaan sobsiise caalaa namni miidhaa hojjate (tokkolleen) hin jiruSila teessoon kaafirootaa jahannam hin taanee?');
INSERT INTO "apaguna" VALUES(29,69,'29:69','Warra Nu (karaa keenya) keessatti qabsaa’an karaa keenya isaan qajeelchinaDhugumatti, Rabbiin toltuu hojjattoota waliin jira');
INSERT INTO "apaguna" VALUES(30,1,'30:1','Alif Laam Miim.');
INSERT INTO "apaguna" VALUES(30,2,'30:2','Roomaan injifatamte');
INSERT INTO "apaguna" VALUES(30,3,'30:3','Irra dhihoo dachii keessatti (injifataman)Isaanis mo’atamuu isaanii booda fuulduratti ni mo’atu');
INSERT INTO "apaguna" VALUES(30,4,'30:4','Waggoota muraasa keessatti (moo’atu)Murtiin dhimma duraas ta’ee boodaa kan RabbiitiGuyyaa san mu’uminoonni ni gammadu');
INSERT INTO "apaguna" VALUES(30,5,'30:5','Tumsa Allaahtiin (gammadu)(Rabbiin) nama fedhe ni tumsaInni injifataa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(30,6,'30:6','Waadaa Rabbii (Rabbiin beellame)Rabbiin waadaa isaa hin diiguGaruu irra hedduun namootaa hin beekanu.');
INSERT INTO "apaguna" VALUES(30,7,'30:7','Isaan jireenya addunyaa irraa gubbuma beekuIsaan Aakhiraa irraa immoo daguu keessa jiru');
INSERT INTO "apaguna" VALUES(30,8,'30:8','Sila lubbuma isaanii keessatti Rabbiin samiifi dachii, akkasumas wantoota isaan lamaan gidduu jiranu, haqaafi beellama beekamaadhaan malee hin uumne jechuu gadi fageenyaan hin xiinxallee? Dhugumatti, irra hedduun namootaa qunnamtii Gooftaa isaaniitti kafaroodha.');
INSERT INTO "apaguna" VALUES(30,9,'30:9','Sila dachii keessa deemanii booddeen warra isaaniin duraa akkam akka ta’e hin ilaallee? Humnaan isaan irra cimoo turanDachiis qotanii waan isaan (warri Makkaa) misoomsan irra hedduu misoomsaniiruErgamtoonni isaaniis ragaawwan ifa ta’aniin isaanitti dhufanDhugumatti, Rabbiin kan isaan miidhu hin taaneGaruu isaan lubbuu isaanii kan miidhan ta’an.');
INSERT INTO "apaguna" VALUES(30,10,'30:10','Sana booda booddeen isaan hamtuu dalaganii, sababa waan isaan keeyyattoota Rabbii sobsiisaniifi isheettis kan qishnaa godhan ta’aniif (adabbii) fokkataa ta’e.');
INSERT INTO "apaguna" VALUES(30,11,'30:11','Rabbiin uuminsa ni jalqabaSana booda isa ni deebisaSana booda gara Isaa deebifamtu.');
INSERT INTO "apaguna" VALUES(30,12,'30:12','Guyyaa Qiyaamaan dhaabbattus, badii raawwattoonni abdii kutatu');
INSERT INTO "apaguna" VALUES(30,13,'30:13','Warra isaan Rabbitti dabalan irraa jaarsi araaraa isaaniif hin argamuIsaanis waan isaan Rabbitti qindeessaa turanitti kafaroota (mormitoota) ta’u.');
INSERT INTO "apaguna" VALUES(30,14,'30:14','Guyyaa Qiyaamaan dhaabbattus, guyyaa san (mu’uminootaafi kaafiroonni) addaan bahu');
INSERT INTO "apaguna" VALUES(30,15,'30:15','Garuu isaan amananii gaggaarii hojjatan, isaan caffee (jannata) keessatti gammachiifamu.');
INSERT INTO "apaguna" VALUES(30,16,'30:16','Warri kafaranii keeyyattoota keenyaafi qunnamtii Aakhiraa sobsiisan immoo, isaan sun adabbii keessa fidamoodha');
INSERT INTO "apaguna" VALUES(30,17,'30:17','Yeroo dhiisifattaniifi yeroo bariisifattanis, qulqullina isaan malu Allaah (qulqulleessaa).');
INSERT INTO "apaguna" VALUES(30,18,'30:18','Samiifi dachii keessattis faaruun hundi kan Isaati; waaree boodaafi yeroo waareetti seentanis isa faarsaa.');
INSERT INTO "apaguna" VALUES(30,19,'30:19','(Inni) jiraa du’aa irraa baasa; du’aas jiraa irraa baasaDachiis erga isheen duutee booda jiraachisaAkkuma kanatti kaafamtu.');
INSERT INTO "apaguna" VALUES(30,20,'30:20','(Rabbiin) biyyee irraa isin (abbaa keessan) uumuufi san booda isin yeroma san namoota dachii keessa faffaca’u ta’uun mallattoolee isaa irraayyi');
INSERT INTO "apaguna" VALUES(30,21,'30:21','Lubbuma keessan irraa akka gara isaaniitti boqottaniif haadholee manaa isiniif uumuufi gidduu keessanittis jaalalaafi mararfannaa gochuun mallattoolee Isaa irraayyiDhugumatti, kana keessa namoota xiinxalaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(30,22,'30:22','Samiifi dachii uumuu, arrabni (lugaan) keessaniifi bifti keessan waldhabuunis mallattoo Isaa irraayyiDhugumatti, kana keessa aalama hundaaf mallattooleetu jiru.');
INSERT INTO "apaguna" VALUES(30,23,'30:23','Hirribni keessan kan halkaniifi guyyaa, tola isaa irraa barbaaddachuun keessanis mallattoolee (dandeettii) Isaa irraayyiDhugumatti, kana keessa namoota dhagahaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(30,24,'30:24','Akka (bakakkaa) sodaattaniifi (roobas) kajeeltaniif hangaasuu isin agarsiisuu, bishaan samii irraa buusuufi booda isheen duutee dachii ittiin jiraachisuun, mallattoolee dandeettii isaa irraayyiDhugumatti, kana keessa namoota sammuu qabaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(30,25,'30:25','Samiifi dachiin ajaja Isaatiin dhaabbachuun, sana booda yeroo Inni dachii irraa waamicha tokko isin waame, yeroma san isin (awwaala keessaa) bahuun mallattoolee isaa irraayyi');
INSERT INTO "apaguna" VALUES(30,26,'30:26','Wanti samiifi dachii keessa jiru hundi kan IsaatiHunduu Isaaf ajajamu.');
INSERT INTO "apaguna" VALUES(30,27,'30:27','Inni (Rabbiin) Isa uumiinsa jalqabu; sana booda kan isa deebisuudhaInni isa irratti akkaan laafaadhaDachiifi samii keessa sifaata ol aanaatu Isaaf jiraInni injifataa ogeessa');
INSERT INTO "apaguna" VALUES(30,28,'30:28','(Rabbiin) lubbuma keessan irraa fakkeenya isiniif taasiseSila waan harki mirgaa keessan horatte (Gabroota keessan) irraa soorata isiniif kennine keessatti shariikni isin isa (sooraticha) keessatti walqixa taatanii akka lubbuu keessan (nama gita keessanii) sodaattanitti isa sodaattan, isiniif ni jiraa? Akka kanatti namoota sammuu qabaniif keeyyattoota addeessina.');
INSERT INTO "apaguna" VALUES(30,29,'30:29','Dhugumatti, isaan miidhaa hojjatan beekumsaan ala fedha lubbuu isaanii hordofanNama Rabbiin jallise eenyutu isa qajeelcha? Gargaartonni isaaniif hin jiranu.');
INSERT INTO "apaguna" VALUES(30,30,'30:30','(Yaa Muhammad!) karaa haqaatti dabuun fuula kee diiniif sirreessiUumaa Rabbii ishee inni irratti nama uumes (qabadhaa)Uumaa Rabbii jijjiiruun hin jiruSantu amantii qajeelaadhaGaruu irra hedduun namootaa hin beekanu.');
INSERT INTO "apaguna" VALUES(30,31,'30:31','Gara Isaatti deebi’oo ta’uun (diinii Isaa qabadhaa); Isa sodaadhaaSalaatas (haqaan) sagadaaMushrikoota irraa hin ta’inaa');
INSERT INTO "apaguna" VALUES(30,32,'30:32','Isaan amantii isaanii gargar qoqqoodaniifi garee garee ta’an irraas (hin ta’inaa)Gareen hunduu waan isaan bira jirutti gammadu.');
INSERT INTO "apaguna" VALUES(30,33,'30:33','Namoonni yeroo rakkoon isaan tuqu gara Isaatti tawbaa deebi’uun Gooftaa isaanii kadhatuSana booda yeroo inni rahmata Isa biraa ta’e isaan dhandhamsiise immoo, yeroma san gareen isaan irraa ta’e Rabbii isaaniitti (wanta biraa) qindeessu .');
INSERT INTO "apaguna" VALUES(30,34,'30:34','Waan isaaniif kenninetti kafaruuf (kana godhu)(Dhugaa) fuuladuratti ni beektuu (addunyaa irratti) qanani’aa');
INSERT INTO "apaguna" VALUES(30,35,'30:35','Sila nuti ragaa kan waan isaan (Rabbitti) qindeessan dubbatu isaan irratti hin buufnee?');
INSERT INTO "apaguna" VALUES(30,36,'30:36','Yeroo namoota rahmata dhandhamsiifne isheetti gammaduyeroo sababa waan harki isaanii dabarsitee (hojjattee)tiin hamtuun wahii isaan tuqxe immoo yeroma san abdii muratu.');
INSERT INTO "apaguna" VALUES(30,37,'30:37','Sila akka Rabbiin nama fedheef soorata bal’isuufi (nama fedhetti immoo) dhiphisu hin beekanuu? Dhugumatti, kana keessa namoota amananiif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(30,38,'30:38','Nama firaa, hiyyeessaafi kara deemaafis haqa isaa kenniIsaan fuula Rabbii kajeelaniif kanatu caalaIsaan sun isaanumatu milkaa’oodha.');
INSERT INTO "apaguna" VALUES(30,39,'30:39','Wanti dhala irraa qabeenya namaa keessatti haa dhaluuf isin kennitan Rabbiin biratti homaa hin dhaluGaruu zakaa irraa wanti isin fuula Rabbii feetanii kennitan, (isatu isiniif dhala)Jarri sun (kennattoonni) isaantu (mindaa) dachaa argamsiisoodha.');
INSERT INTO "apaguna" VALUES(30,40,'30:40','Allaahn Isa isin uumeSan booda kan soorata isiniif kenneSana booda isin ajjeesaSana booda isin jiraachisaSila warra isin Rabbitti (qindeessitan) irraa kan wantoota kanneen irraa waan tokko dalagan ni jiruu? (Rabbiin) wantoota isaan isatti qindeessan hunda irraa qulqullaa’ee ol ta’e.');
INSERT INTO "apaguna" VALUES(30,41,'30:41','Garii waan isaan dalaganii akka isaan dhandhamsiisnuuf jecha, sababa waan harki namaa hojjatteetiin dachiifi galaana keessattis badiin mul’ateDeebi’uun isaaniis ni kajeelama.');
INSERT INTO "apaguna" VALUES(30,42,'30:42','(Yaa Muhammad!) “dachii keessa deemaa; booddeen warra isiniin duraas akkam akka ta’e ilaalaa; irra hedduun isaaniis mushrikoota turan” jedhiin');
INSERT INTO "apaguna" VALUES(30,43,'30:43','Guyyaan deebi’uun isaaf hin jirre odoo hin dhufne dura fuula kee amantii sirrii ta’eef sirreessiGuyyaa san (gara jannataafi ibiddaatti) qoodamu');
INSERT INTO "apaguna" VALUES(30,44,'30:44','Nama kafare kufriin isaa isuma irratti ta’aNamoonni dalagaa gaarii hojjatanis, lubbuma isaaniitiif qopheeffatu');
INSERT INTO "apaguna" VALUES(30,45,'30:45','Isaan amananii hojiiwwan gaggaarii dalagan tola Isaa irraa akka mindeessuuf (kana hojjate)Dhugumatti, Inni kaafiroota hin jaallatu.');
INSERT INTO "apaguna" VALUES(30,46,'30:46','Rahmata Isaa irraa isin dhandhamsiisuf, dooniinis ajaja Isaatiin akka deemtuuf, tola Isaa irraas akka barbaaddattaniifi akka Isa galateeffattaniif, gammachiiftuu taasisee qilleensa erguun Isaa mallattoolee Isaa irraayyi.');
INSERT INTO "apaguna" VALUES(30,47,'30:47','Dhugumatti, si duras ergamtoota gara ummatoota isaaniitti erginee jirraIsaanis ragaalee ifa ta’aniin isaanitti dhufanii jiruIsaan daangaa dabran irraas haaloo bahanneMu’uminoota gargaaruun immoo haqa Nurratti ta’eera.');
INSERT INTO "apaguna" VALUES(30,48,'30:48','Rabbiin Isa qilleensa erginaan duumessa sochooftuudha; akka fedhetti samii keessa ni bittinneessa; addaan kukkutamaas isa taasisaRoobas kan gidduu isaatii bahu argitaYeroo ittiin (rooba sanaan) gabroota Isaa irraa nama fedhe tuqe, yeroma san isaan ni gammadu.');
INSERT INTO "apaguna" VALUES(30,49,'30:49','Odoo (roobni) isaan irratti hin bu’in dura immoo, sana dura dhugumatti abdii murattoota turan');
INSERT INTO "apaguna" VALUES(30,50,'30:50','Gara faana rahmata Rabbii, akka (inni) erga isheen duutee booda dachii jiraachisu ilaaliDhugumatti [Rabbiin] Sun du’aawwan ni jiraachisaInni waan hunda irratti danda’aadha');
INSERT INTO "apaguna" VALUES(30,51,'30:51','Dhugumatti, odoo bubbee (oyruu isaaniitti) erginee daalacha ta’ee isa arganii, silaa booda isaatii (tola kiyyatti) kan kafaran ta’u.');
INSERT INTO "apaguna" VALUES(30,52,'30:52','(Yaa Muhammad!) ati du’aa hin dhageessiftuNama duudaawwanis yeroo isaan of duuba deebi’anii garagalan waamicha hin dhageessiftu.');
INSERT INTO "apaguna" VALUES(30,53,'30:53','(Yaa Muhammad!) ati jaamaawwan jallina isaa irraa kan isaan qajeelchitu hin taane(Ati) namoota Rabbiif ajajamoo ta’anii keeyyattoota keenyatti amanan malee (warra biraa) hin dhageessiftu.');
INSERT INTO "apaguna" VALUES(30,54,'30:54','Rabbiin isa (bishaan) laafaa irraa isin uumee, sana booda laafina boodaan cimina taasisee; sana booda cimina boodaan laafinaafi arrii taasiseedhaWaan barbaades ni uumaInnis beekaa danda’aadha.');
INSERT INTO "apaguna" VALUES(30,55,'30:55','Guyyaa Qiyaamaan dhaabbattu, yakkamtoonni sa’aatii tokko malee (addunyaa irra) hin turre (jedhanii) kakatuAkkuma kanatti (haqa irraa) kan gara galfaman turan.');
INSERT INTO "apaguna" VALUES(30,56,'30:56','Isaan beekumsaafi iimaana kennaman immoo “dhugumatti, isin akkuma kitaaba Rabbii keessatti (murtaa’etti,) hanga guyyaa kaafamaatti turtaniittuKunis guyyaa kaafamaatiGaruu isin (dhufuu isaa) hin beektanu turtan.”');
INSERT INTO "apaguna" VALUES(30,57,'30:57','Guyyaa san warra miidhaa hojjatan rakkoo himachuun isaanii (isaan) hin fayyaduIsaan ceephoos hin buufachiifamanu');
INSERT INTO "apaguna" VALUES(30,58,'30:58','Dhugumatti, Nuti Qur’aana kana keessatti namootaaf fakkeenya hunda irraa taasifnee jirraGaruu (Yaa Muhammad!) yoo ati mallattoo wahiitiin isaan itti dhufte warri kafaran “isin kijibdoota malee waan biraa hin taane” jedhu');
INSERT INTO "apaguna" VALUES(30,59,'30:59','Akka kanatti Allaahn onnee warra haqa hin beeknee irratti cufaa godha');
INSERT INTO "apaguna" VALUES(30,60,'30:60','(Yaa Muhammad!) ObsiDhugumatti, waadaan Rabbii dhugaadhaIsaan (Qiyaamaa) hin dhugoomsine akka si hin xiqqeessine');
INSERT INTO "apaguna" VALUES(31,1,'31:1','Alif Laam Miim.');
INSERT INTO "apaguna" VALUES(31,2,'31:2','Kun keeyyattoota kitaaba ogummaan guutameeti.');
INSERT INTO "apaguna" VALUES(31,3,'31:3','Isaan toltuu hojjataniif qajeelumaafi rahmata haala ta’een (buufame)');
INSERT INTO "apaguna" VALUES(31,4,'31:4','Isaan warra salaata (haqaan) sagadaniifi zakaas kennataniidhaIsaan isaanumatu Aakhiraa dhugoomsa.');
INSERT INTO "apaguna" VALUES(31,5,'31:5','Isaan sun qajeeluma Gooftaa isaanii irraa ta’e irra jiruIsaan sun isaanumatu milkaa’oodha.');
INSERT INTO "apaguna" VALUES(31,6,'31:6','Namoota irraa nama beekumsaan ala karaa Rabbii irraa ittiin jallisuufi meeshaa taphaa taasifachuuf jecha haasa’a taphaa bitutu jiraIsaan sana adabbii xiqqeessaatu isaaniif jira.');
INSERT INTO "apaguna" VALUES(31,7,'31:7','Yeroo keeyyattoonni keenya isa irratti dubbifamte, akka waan hin dhaga’iniitti akka waan gurra isaa lamaan keessa duudummaan jiruu, boonaa ta’ee irraa garagalaAdabbii laalessaan isa gammachiisi.');
INSERT INTO "apaguna" VALUES(31,8,'31:8','Dhugumatti, isaan amananii gaggaarii hojjatan ashaakiltiilee qananiitu isaaniif jira');
INSERT INTO "apaguna" VALUES(31,9,'31:9','Ishee keessatti hafoo ta’anii (ishee seenu)Rabbiin waadaa dhugaa isaaniif galeInni injifataa, ogeessa');
INSERT INTO "apaguna" VALUES(31,10,'31:10','Samii utubaalee malee uumne; (isin) ishee ni agartuGaarreen gadi dhaabbatoo ta’an akka dachiin isiniin hin sochooneef keessa kaa’eLubbu qabeeyyii hunda irraas ishee keessatti facaaseBishaanis Samii irraa buufnee, gosoota gaggaarii hunda ishee keessatti magarsine');
INSERT INTO "apaguna" VALUES(31,11,'31:11','Kun uumaa RabbiitiMee waan isaan Isaa gadii uuman na agarsiisaaDhugumatti miidhaa hojjattoonni jal’ina ifa galaa keessa jiru');
INSERT INTO "apaguna" VALUES(31,12,'31:12','“Rabbiif galata galchiNamni (Rabbiin) galateeffate kan inni galateeffatu matuma isaatiifi; Namni kafares (matuma isaa irratti)Dhugumatti Rabbiin dureessa, faarfamaadha” (jechuun) dhugumatti Luqmaaniif ogummaa kenninee jirra.');
INSERT INTO "apaguna" VALUES(31,13,'31:13','Yeroo Luqmaan ilma isaatiin odoo isa gorsuu “yaa ilma kiyya! Allaahiitti waa hin qindeessin; dhugumatti shirkiin miidhaa guddaadha” jedhe [yaadadhu].');
INSERT INTO "apaguna" VALUES(31,14,'31:14','Namaafis haadhaafi abbaa isaatti (tola ooluu) dhaamnee jirra; haati isaas dadhabbii dadhabbii irratti ta’een isa baadhatteHarma irraa gu’uun isaas waggaa lama keessatti“Anaafi haadhaa abbaa keetiif galata galchiDeebiinis gara kiyyatti” jechuun isa ajajne.');
INSERT INTO "apaguna" VALUES(31,15,'31:15','Yoo isaan lamaan waan beekumsa isaa hin qabne akka Natti qindeessituuf sitti qabsaawan, tole isaaniif hin jedhinAddunyaa keessatti haala gaariin isaan waliin jiraadhuKaraa nama gara kiyya deebi’ee hordofiSana booda deebiin keessan gara kiyya; waan isin dalagaa turtan isinittin odeessa.');
INSERT INTO "apaguna" VALUES(31,16,'31:16','(Luqmaanis) “yaa ilma kiyya! isheen (badiin) yoo hanga madaala ija raafuu taatee, yoo dhagaa yookiin samii yookiin dachii keessa taates, Rabbiin ishee fidaRabbiin maalummaa ishee beekaa iddoo ishees beekaadha.');
INSERT INTO "apaguna" VALUES(31,17,'31:17','Yaa ilma kiyya! Salaata sirritti sagadiToltuutti nama ajajiHamtuu irraas nama dhorgiWaan si mudate irrattis obsiDhugumatti, sun wantoota jajjaboo (murannoo gaafatan) irraayyi.');
INSERT INTO "apaguna" VALUES(31,18,'31:18','[Boonaan] maddii (boqoo) kee namoota irraa gara hin galchin; dachii keessas of tuulaa hin deeminDhugumatti, Rabbiin of tuulaa, of dhaadaa hunda hin jaallatu.');
INSERT INTO "apaguna" VALUES(31,19,'31:19','Deemsa kee keessatti giddu galeessa ta’ii sagalee keetis gadi qabiDhugumatti irra jibbamaan sagalee sagalee harreeti.”');
INSERT INTO "apaguna" VALUES(31,20,'31:20','Sila akka Rabbiin wantoota samii keessaatiifi wantoota dachii keessaas isaaniif laaffisee, tola isaa mul’attuufi dhokattuus isin irratti dhangalaase hin argitanuu? Namoota irraas nama beekumsa, ragaafi kitaaba ibsaa ta’een maletti (waa’ee) Rabbii keessatti falmutu jira');
INSERT INTO "apaguna" VALUES(31,21,'31:21','Yeroo “Waan Rabbiin buuse hordofaa” isaaniin jedhame “Lakkisaa; nuti waan abbootii keenya isarratti argine hordofna” jedhuOduma Sheyxaanni gara adabbii boba’aatti kan isaan waamullee ta’ee moo?');
INSERT INTO "apaguna" VALUES(31,22,'31:22','Namni toltuu hojjataa fuula isaa gara Rabbiitti kenne (bute) dhugumatti funyoo cimaa qabateera; booddeen wantoota hundaatuu gara Rabbiiti.');
INSERT INTO "apaguna" VALUES(31,23,'31:23','Namni kafare kufriin isaa si hin yaachisinDeebiin isaaniis gara keenya; waan isaan dalaganis isaanitti ni odeessinaDhugumatti Allaahn beekaa waan qoma keessaati.');
INSERT INTO "apaguna" VALUES(31,24,'31:24','Yeroo xiqqoof isaan ni qanansiifnaSana booda gara adabbii guddaatti isaan dirqisiifna.');
INSERT INTO "apaguna" VALUES(31,25,'31:25','“Samiifi dachii eenyutu uume?” (jettee) yoo isaan gaafatte “Rabbi” jedhu(Yaa Muhammad!) “Faaruun hundi kan Rabbiiti” (jedhi)Garuu irra hedduun isaanii hin beekanu.');
INSERT INTO "apaguna" VALUES(31,26,'31:26','Wantoonni samiifi dachii keessa jiran (hundi) kan AllaahtiDhugumatti, Rabbiin Isatu dureessa, faarfamaadha.');
INSERT INTO "apaguna" VALUES(31,27,'31:27','Odoo wanti dachii keessa jiru muka irraa qalamii ta’ee, galaannis booda isaatii kan galaanni torba jiru itti ida’amuudhaan (gargaaree ittiin barreeffameeyyuu) jechoonni Rabbii hin dhumuRabbiin injifataa beekaadha.');
INSERT INTO "apaguna" VALUES(31,28,'31:28','Isin uumuufi isin kaasuunis akkuma lubbuu tokkittii malee hin taaneDhugumatti, Rabbiin dhaga’aa, argaadha.');
INSERT INTO "apaguna" VALUES(31,29,'31:29','Sila Rabbiin halkan guyyaa keessa seensisee guyyaas halkan keessa seensisuu, aduufi ji’as haala hundumtuu hanga beellama beekamaatti deemanuun kan isaan laaffise ta’uufi Rabbiin wanta isin hojjattan hunda keessa beekaa ta’uu hin arginee?');
INSERT INTO "apaguna" VALUES(31,30,'31:30','Kun waan Rabbiin Isatu dhugaa ta’eefi wantoonni Isaa gaditti isaan waammatan soba ta’eefiAmmas dhugumatti waan Allaahn Isatu ol ta’aa, guddaa ta’eefi.');
INSERT INTO "apaguna" VALUES(31,31,'31:31','Sila (Rabbiin) mallattoollee Isaa irraa isinitti agarsiisuuf akka dooniin tola Rabbiitiin galaana keessa deemtu hin arginee? Dhugumatti, kana keessa akkaan obsitoota, galateeffattootaaf mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(31,32,'31:32','Yeroo danbaliin akka gaaraa isaan haguuge amantaa (kadhaa) Isaaf qulqulleessanii Allaah qofa kadhatuYeroma inni gara dachiitti nagaan isaan baase immoo, isaan irraa nama (kadhaa isaa iirraa) of qusatutu jiraMallattoolee keenya hundaayyuu waadaa diigaa, kafaraa malee hin mormu');
INSERT INTO "apaguna" VALUES(31,33,'31:33','Yaa namootaa! Gooftaa keessan sodaadhaaGuyyaa abbaan ilma isaa homaa hin fayyadne, kan ilmis abbaa isaa homaa hin fayyadne sanis sodaadhaaDhugumatti waadaan Rabbii dhugaadhaJiruun addunyaa isin hin gowwoomsin(Sheyxaanni) gowwomsaan (obsa) Rabbiitiin isin hin gowwoomsin');
INSERT INTO "apaguna" VALUES(31,34,'31:34','Dhugumatti, Rabbiin beekumsi Qiyaamaa Isuma bira jiraRoobas ni buusa; waan gadaamessa keessa jirus ni beekaLubbuun tokko waan boru carraaqqattu hin beektuLubbuun tokko dachii kamitti akka duutus hin beektuDhugumatti, Rabbiin beekaa, keessa hubataadha.');
INSERT INTO "apaguna" VALUES(32,1,'32:1','Alif Laam Miim.');
INSERT INTO "apaguna" VALUES(32,2,'32:2','Buufamni kitaabichaa Rabbii aalamaa irraa ta''uun shakkiin isa keessa hinjiru.');
INSERT INTO "apaguna" VALUES(32,3,'32:3','Sila “(Muhammadtu) of irraa isa uume” jedhuu? Akkasii mitiDhugumatti, inni dhugaa Rabbii kee irraa ta’ee, ummata siin duratti dinniinaan itti hin dhufin akka dinniintuuf jecha (siif buufame)');
INSERT INTO "apaguna" VALUES(32,4,'32:4','Allaahn isa samii, dachiifi waan gidduu isaan lamaanii jiran hunda guyyoota jaha keessatti uumee sana booda Arshii irratti (ol ta’iinsa isaan malu) ol ta’eedhaIsaa gaditti gargaaraafi araarsaan tokkollee isiniif hin jiruSila hin gorfamtanuu?');
INSERT INTO "apaguna" VALUES(32,5,'32:5','Inni wantoota hunda samii irraa gara dachiitti gara gaggalcha (qindeessa)Sana booda guyyaa dheerinni isaa waan isin lakkooftan irraa waggaa kuma ta’e keessatti gara isaa ol koru.');
INSERT INTO "apaguna" VALUES(32,6,'32:6','Inni sana hojjate beekaa fagoofi waan mul’atuu, kan injifataa, rahmata godhaa ta’eeti.');
INSERT INTO "apaguna" VALUES(32,7,'32:7','(Inni) isa waan uume hunda miidhagseefi uumama namaa dhoqqee irraa jalqabeedha.');
INSERT INTO "apaguna" VALUES(32,8,'32:8','Sana booda sanyii isaa dhiiga ititaa bishaan laafaa irraa ta’e irraa godhe');
INSERT INTO "apaguna" VALUES(32,9,'32:9','Sana booda isa walqixxeessee ruuhii [uumaa] isaa irraa ta’e isa keessatti afuufeDhageettii, argaafi Onnees isiniif taasiseXiqquma galateeffattu');
INSERT INTO "apaguna" VALUES(32,10,'32:10','“Sila yeroo (duunee) dachii keessa fagaanne nuti uumama haaraa keessatti taanaa?” jedhanDhugumatti, isaan qunnamtii Rabbii isaaniitti kafaroodha.');
INSERT INTO "apaguna" VALUES(32,11,'32:11','“Maleykaa du’aa isa isin irratti ramadametu isin ajjeesaSana booda gara gooftaa keessanii deebifamtu” jedhiin.');
INSERT INTO "apaguna" VALUES(32,12,'32:12','Yeroo yakkamtoonni Rabbii isaanii biratti mataa isaanii gadi buusanii “yaa Gooftaa keenya! Arginee, dhageenyee, nuti dhugoomsoodhaa, hojii gaarii hojjannaa (addunyaatti) nu deebisi” jedhan san odoo argitee [dubbii cimaa argita turte].');
INSERT INTO "apaguna" VALUES(32,13,'32:13','Odoo feenee lubbuu hundaa qajeeluma ishee isheedhaaf kennina turreGaruu jecha “jinnootaafi namoota walitti qabaman irraa jahannam nan guuta” jedhutu Ana irraa haqa ta’ee jira.');
INSERT INTO "apaguna" VALUES(32,14,'32:14','“Waan qunnamtii guyyaa keessan kanaa dagattaniif (adabbii) dhandhamaaNutis (adabbii keessatti) isin dhiifna; waan dalagaa turtaniifis adabbii yeroo hundaa dhandhamaa” (jedhamu).');
INSERT INTO "apaguna" VALUES(32,15,'32:15','Keeyyattoota keenyatti warra yeroo isheedhaan gorfaman kan hin boonne ta’anii sujuuda bu’anii, Rabbii isaanii faarsan malee kan amanu hin jiru');
INSERT INTO "apaguna" VALUES(32,16,'32:16','Cinaachi isaaniis ciisicha irraa ni fagaattiSodaataafi kajeelaa Goftaa isaanii ni kadhatuWaan isaaniif kennine irraas ni sadaqatu');
INSERT INTO "apaguna" VALUES(32,17,'32:17','Mindaa waan isaan hojjataa turaniif jecha, gammachuu ijaa irraa waan isaaniif dhokfame lubbuun tokkoyyuu hin beektu.');
INSERT INTO "apaguna" VALUES(32,18,'32:18','Sila namni mu’umina ta’e akka nama fincilaatii? Wal hin qixxaa’an.');
INSERT INTO "apaguna" VALUES(32,19,'32:19','Warri amananii gaggaarii hojjatan waan isaan hojjataa turaniif jecha, qophii ta’ee jannata qobsumaatu isaaniif jira');
INSERT INTO "apaguna" VALUES(32,20,'32:20','Isaan fincilan immoo teessoon isaanii ibiddaDhawaatuma ishee irraa ba’uu fedhaniin ishee keessatti deebifamu“Adaba ibiddaa kan isa sobsiisaa turtan dhandhamaa” isaaniin jedhama');
INSERT INTO "apaguna" VALUES(32,21,'32:21','Dhugumatti, Nuti akka isaan (gara iimaanaatti) debi’aniif jecha adabbii guddaa dura, adabbii xiqqaa (kan addunyaa) isaan dhamdhamsiifna');
INSERT INTO "apaguna" VALUES(32,22,'32:22','Nama keeyyattoota Gooftaa isaatiin gorfamee sana booda ishee irraa duubatti deebi’e caalaa namni miidhaa dalage hin jiruDhugumatti, Nuti yakkamtoota irraa haaloo bahatoodha');
INSERT INTO "apaguna" VALUES(32,23,'32:23','Dhugumatti, Muusaaf kitaaba kennineerraIsaan wal qunnamuu irraa shakkii keessa hin ta’inIlmaan Israa’iiliifis qajeeluma isa taasifne');
INSERT INTO "apaguna" VALUES(32,24,'32:24','Yeroma isaan obsaniifi keeyyattoota keenya dhugoomsan, isaan irraa imaamota ajaja keenyaan qajeelchan taasifne.');
INSERT INTO "apaguna" VALUES(32,25,'32:25','Dhugumatti, Gooftaan kee isumatu Guyyaa Qiyaamaa wantoota isaan isa keessatti wal dhabaa turan keessatti gidduu isaaniitti murteessa.');
INSERT INTO "apaguna" VALUES(32,26,'32:26','Sila akka Nuti isaaniin duratti dhaloota baay’ee irraa balleessine gandoota isaanii keessa kan deeman ta’anii ifa isaaniif hin taanee? Dhugumatti, sana keessa mallattooleetu jiraSila hin dhagahanuu?');
INSERT INTO "apaguna" VALUES(32,27,'32:27','Sila akka nuti bishaan gara dachii gogaa oofnee biqiloota horiin isaaniitiifi ofii isaaniitii irraa nyaatan ittiin biqilchinu hin ilaallee? Sila hin arganuu?');
INSERT INTO "apaguna" VALUES(32,28,'32:28','“Yoo kan dhugaa dubbattan taataniif murtiin kun yoom ta’a?” jedhu');
INSERT INTO "apaguna" VALUES(32,29,'32:29','Guyyaa murtii san warra kafaran iimaanni isaanii isaan hin fayyaduYeroonis isaaniif hin kennamu.');
INSERT INTO "apaguna" VALUES(32,30,'32:30','Isaan irraa gara gali[Adabbii isaanitti bu’u] eegiDhugumatti, isaanis (baduu kee) eegoodha');
INSERT INTO "apaguna" VALUES(33,1,'33:1','Yaa nabiyyichaa! Allaah sodaadhuKaafirootaafi munaafiqootaafis hin ajajaminDhugumatti, Rabbiin beekaa, ogeessa ta’eera.');
INSERT INTO "apaguna" VALUES(33,2,'33:2','Waan Gooftaa kee irraa gara keetti beeksisa godhames hordofiDhugumatti Rabbiin waan isin dalagdan keessa beekaa ta’eera');
INSERT INTO "apaguna" VALUES(33,3,'33:3','Allaah irratti hirkadhu; hirkoo ta’uufis Rabbumatu ga’a.');
INSERT INTO "apaguna" VALUES(33,4,'33:4','Rabbiin nama tokkoof keessa isaatti onnee lama hin taasifneNiitolii keessan kan isaan irraa dugda (haadhaatti) fakkeessitan san haadholii keessan hin goone; guddifachaa keessanis ilmaan keessan hin gooneSun jecha keessan kan isin afaan keessaniin haasoftaniidhaRabbiin dhuguma jedhaIsatu karaa sirriitti qajeelcha.');
INSERT INTO "apaguna" VALUES(33,5,'33:5','Abboota isaaniitiin isaan waamaaIsatu Rabbiin biratti irra sirriidhaYoo abbootii isaanii beekuu baattan, obboleewwan keessan kan amantiitiifi jaalalleewwan keessaniWaan isa dogoggortan keessatti badiin isin irra hin jiruGaruu waan onneen keessan hamilteef (isin qaba)Rabbiin araaramaa, rahmata godhaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,6,'33:6','Nabiyyiin Mu’uminootatti lubbuu isaanii irra itti aanaadhaHaadholiin manaa isaatis haadholee isaaniitiKitaaba Rabbii keessatti, warra firooma qabantu mu’iminootaafi godaantota caalaa gariin isaanii garii (dhaaluutti) haqaGaruu gara jaalalleewwan keessaniitti [dhaamsa dhaamuudhaan] gaarii yoo hojjattan [ni eeyyamama]Kun kitaaba (Lawhal mahfuuz) keessatti barreeffamaa ta’eera');
INSERT INTO "apaguna" VALUES(33,7,'33:7','Yeroo Nuti nabiyyoota irraa waadaa isaanii fuune: sirraas, Nuuh, Ibraahiim, Muusaafi Iisaa ilma Maryam irraas qabne [yaadadhu]Isaan irraa waadaa cimaa qabne.');
INSERT INTO "apaguna" VALUES(33,8,'33:8','(Rabbiin) warra dhugaa dubbatan dhugaa dubbatuu isaanii irraa akka isaan gaafatuuf (kana godhe)Kaafirootaaf immoo adabbii laalessaa ta’e qopheessee jira.');
INSERT INTO "apaguna" VALUES(33,9,'33:9','Yaa warra amantan! Ni’imaa Rabbii kan yeroo hoomaan (waraanaa) isinitti dhufanii, ergasii bubbeefi hoomaa isin hin agarre isaan irratti ergine, isin irra jiru yaadadhaaRabbiin waan isin dalagdan argaa ta’eera');
INSERT INTO "apaguna" VALUES(33,10,'33:10','Yeroo gubbaa keessaniifi garjalee keessan irraa isinitti dhufan, yeroo ijjis dabdee (hiramtee) onneenis kokkee geessee Rabbitti yaada fokkataa yaaddan san [yaadadhaa]');
INSERT INTO "apaguna" VALUES(33,11,'33:11','Bakka sanitti mu’uminoonni mokkoramaniiti, shorarkaa cimaa shoorarkeeffaman.');
INSERT INTO "apaguna" VALUES(33,12,'33:12','Yeroo munaafiqootaafi isaan onnee isaanii keessa dhukkubni jiru “Rabbiifi ergamaan Isaas gowwoomsaa malee wanta biraa waadaa nuuf hin goone” jedhan [yaadadhaa].');
INSERT INTO "apaguna" VALUES(33,13,'33:13','Yeroo gareen isaan irraa taate “Yaa warra madiinaa teessumti isiniif hin jiruu deebi’aa” jattes [yaadadhaa]Gareen isaan irraa ta’e “manneen keenya hadiyyoo (duwwaa)dha” jedhanii odoo isheen hadiyyoo hin ta’iin hayyama gaafatuBaqa malee isaan waan biraa hin fedhanu');
INSERT INTO "apaguna" VALUES(33,14,'33:14','Odoo (magaalattiin) gama hundaan isaan irratti seenamtee, ergasii shirkii (diin irraa gara galuu) gaafatamanii, dhugumatti ni raawwatu turanIshee (shirkii simachuu) irraa waa xiqqoo malee hin turanu.');
INSERT INTO "apaguna" VALUES(33,15,'33:15','Dhugumatti, kana dura hin baqannu jechuun Rabbiif waadaa galanii turanWaadaan Rabbii gaafatamaa ta’eera');
INSERT INTO "apaguna" VALUES(33,16,'33:16','“Yoo du’a yookiin ajjeechaa irraa baqattan ta’e baqachuun isin hin fayyaduYeroo san xiqqoo malee hin qanansiifamtanu” jedhiin');
INSERT INTO "apaguna" VALUES(33,17,'33:17','Jedhi: “Yoo Inni hamtuu yaade yookiin rahmata isiniif fedhe, namni Rabbi irraa isin baraaru eenyu? Rabbiin ala jaalalleefi tumsaas hin argatanu.');
INSERT INTO "apaguna" VALUES(33,18,'33:18','Dhugumatti Rabbiin warra duubatti (namoota) deebasaniifi obboleeyyan isaaniitiin “gara keenya kottaa” jedhanii yeroo xiqqoo malee lola hin dhaqne ni beeka');
INSERT INTO "apaguna" VALUES(33,19,'33:19','(Boojuu argachuu keessan) isin irratti doynoomoo ta’anii (malee lola hin dhufanu); yeroo sodaan isaanitti dhufe, akka isa du’aaf gaggabeetti kan ijji isaanii naannoftu ta’anii gara kee ilaalan isaan agartaYeroo sodaan deeme immoo qabeenya irratti bololoo ta’anii arraba qaraan isin waraanuJarri sun hin amanne; Rabbiinis dalagaalee isaanii jalaa balleesseerakunis Rabbi irratti laafaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,20,'33:20','(Munaafiqoonni) Tuutni kaafiraa hin deemne jedhanii yaaduYoo gamtaan diinaa dhufe immoo, baadiyyaatti bahanii tikfattoota keessatti (ta’anii) oduu keessan irraa gaggaafachuu jaallatuOdoo isin keessa ta’aniillee xiqqoo malee hin lolan ture');
INSERT INTO "apaguna" VALUES(33,21,'33:21','Dhugumatti, ergamaa Rabbii keessatti nama Rabbiifi Guyyaa Aakhiraa sodaatee Rabbiin baay’inaan yaadatuuf hidhannaa gaariitu jira');
INSERT INTO "apaguna" VALUES(33,22,'33:22','Mu’uminoonni yeroma gamtaa diinaa argan “kun waan Rabbiifi ergamaan Isaa waadaa nuuf galaniRabbiifi ergamaan Isaas dhugaa dubbataniiru” jedhuIimaanaafi (Rabbitti) harka kennuu malee wanta biraa isaaniif hin daballe');
INSERT INTO "apaguna" VALUES(33,23,'33:23','Mu’uminoota irraa namoota waan irratti waadaa Rabbiif galan dhugoomsantu jiru; isaan irraas nama dirqama isaa raawwatetu jira; isaan irraas nama eeggataa jirutu jiraJijjiirraa kamillee hin jijjiirre');
INSERT INTO "apaguna" VALUES(33,24,'33:24','Akka Rabbiin warra dhugaa dubbatan dhugaa isaaniitiin mindeessuufi munaafiqootas yoo fedhe akka adabuuf yookiin tawbaa isaanii akka irraa qeebaluuf (kana godhan)Dhugumatti, Rabbiin araaramaa, rahmata godhaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,25,'33:25','Rabbiin isaan kafaran dallansuu isaaniitiin odoo toltuu tokkollee hin argatin isaan deebiseMu’uminootas Rabbiin lola (irraa) isaaniif ga’eeraAllaahn jabaa, injifataa ta’eera.');
INSERT INTO "apaguna" VALUES(33,26,'33:26','(Rabbiin) abbootii kitaabaa irraa warra isaan (kaafiroota) gargaaran mooraa isaanii irraa isaan buusee, onnee isaanii keessattis sodaa darbeGarii isaanii ni ajjeeftu; garii isaanii immoo ni boojitu.');
INSERT INTO "apaguna" VALUES(33,27,'33:27','Dachii isaanii, manneen isaanii, qabeenya isaaniitiifi dachii isin gubbaa hin ejjatiniis isin dhaalchiseRabbiin waan hunda irratti danda’aa ta’eera.');
INSERT INTO "apaguna" VALUES(33,28,'33:28','Yaa nabiyyichaa! Niitolii keetiin “jireenya addunyaatiifi bareeduma ishee kan feetan yoo taataniif, kottaa waan ittiin qananiitan isiniif kenneen, gadi dhiisuu bareedaa ta’e isin gadi dhiisa” jedhiin.');
INSERT INTO "apaguna" VALUES(33,29,'33:29','Rabbiin, ergamaa Isaatiifi mana Aakhiraa kan filattan yoo taatan immoo, isin irraa warra toltuu hojjataniif Rabbiin mindaa guddaa qopheessee jira');
INSERT INTO "apaguna" VALUES(33,30,'33:30','Yaa dubartoota nabiyyichaa! Isin irraa namni badii ifa galtuu taateen dhufe (raawwate), adabbiin dachaa lama isheetti godhamaKunis Rabbi irratti laafaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,31,'33:31','Isin irraa isheen Rabbiifi ergamaa Isaatiif ajajamtee toltuus hojjatte, mindaa ishee yeroo lama isheef kenninee, soorata gaariis (jennatas) isheedhaaf qopheessine');
INSERT INTO "apaguna" VALUES(33,32,'33:32','Yaa dubartoota nabiyyichaa! Isin akka tokkoo dubartoota biraatii mitiAkka inni onnee isaa keessa dhukkubni jiru hin kajeelleef, yoo Rabbiin sodaattan jecha keessan hin laaffisinaa; jecha bareedaas dubbadhaa');
INSERT INTO "apaguna" VALUES(33,33,'33:33','Manneen keessan keessa taa’aa; akka mul’ifannaa bara wallaalaa duriittis miidhagina keessan hin mul’isinaa; salaatas sirnaan dhaabaa; zakaas kennaa; Rabbiifi ergamaa Isaatiifis ajajamaaYaa maatii nabiyyichaa! Wanti Rabbiin fedhu xurii isin irraa deemsisee qulqulleessiinsa isin qulqulleessuu fedha.');
INSERT INTO "apaguna" VALUES(33,34,'33:34','Keeyyattoota (Qur’aana) Rabbiifi hadiisa irraa waan manneen keessan keessatti dubbifamu hubadhaa (ittiin gorfamaa)Dhugumatti, Rabbiin keessa beekaa, hubataa ta’eera');
INSERT INTO "apaguna" VALUES(33,35,'33:35','Dhugumatti, dhiironni Muslimaatiifi dubartoonni Muslimaas, dhiironni mu’uminaatiifi dubartoonni mu’uminaas, dhiironni Rabbiif ajajamaniifi dubartoonni Rabbiif ajajamanis, dhiironni dhugaa dubbataniifi dubartoonni dhugaa dubbatanis, dhiironni obsaniifi dubartoonni obsanis, dhiironni (Rabbiin) sodaataniifi dubartoonni (Rabbiin) sodaatanis, dhiironni sadaqataniifi dubartoonni sadaqatanis, dhiironni soomaniifi dubartoonni soomanis, dhiironni qaama saalaa isaanii (haraama irraa) tikfataniifi dubartoonni tikfatanis, dhiironni hedduu Rabbiin faarsaniifi dubartoonni hedduu faarsanis, Rabbiin araaramaafi mindaa guddaa isaaniif qopheesseera');
INSERT INTO "apaguna" VALUES(33,36,'33:36','Mu’uminaafi mu’umintittiif yeroo Rabbiifi ergamaan Isaa dhimma tokko murteessan ajaja isaanii irraa filannoon biraa isaaniif jiraachuun hin taaneNamni Rabbiifi ergamaa Isaa dide, dhugumatti inni jallina ifa ta’e jallateera');
INSERT INTO "apaguna" VALUES(33,37,'33:37','Yeroo ati isa Rabbiin tola irratti oolee atis tola irratti oolteen “Niitii kee qabadhu, Rabbiinis sodaadhu” jatteen [yaadadhu]Ammas (yeroo) waan Rabbiin isa ifa baasuuf taa’u, ati of keessatti dhoksitesOdoo Rabbiin sodaachuun irra haqa ta’uu yeroo nama sodaattes [yaadadhu]Akka mu’uminoota irratti niitii ilmaan guddisaa (guddifachaa) isaanii yeroo isaan dhimma irraa fixatan (fuuchuu) keessatti rakkoon hin taaneef jecha, Yeroma Zeydiin ishee irraa dhimma fixate (ishee hiike), ishee sitti heerumsiifneAjajni Rabbii raawwatamaa ta’eera');
INSERT INTO "apaguna" VALUES(33,38,'33:38','Waan Rabbiin isaaf murteesse keessatti nabiyyicha irra badiin tokkoyyuu hin jiruSun karaa Rabbii kan warra dur dabran keessatti (raawwatamaa) tureedhaAjajni Rabbiis murtee murteeffamaa ta’eedha');
INSERT INTO "apaguna" VALUES(33,39,'33:39','(Isaan) warra ergaawwan Rabbii geessaniifi Isa malee waan tokkollee kan hin sodaanne Isa sodaataniidhaTiksaa ta’uuf Rabbiinuu ga’aadha.');
INSERT INTO "apaguna" VALUES(33,40,'33:40','Muhammad dhiirota keessan irraa abbaa nama tokkoos hin taaneGaruu inni ergamaa Rabbiitiifi xumura nabiyyootaa ta’eeraRabbiinis waan hunda beekaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,41,'33:41','Yaa warra amantan! Faaruu baay’ee Allaah faarsaa.');
INSERT INTO "apaguna" VALUES(33,42,'33:42','Ganamaafi galgalas faaruudhaan Isa qulqulleessaa.');
INSERT INTO "apaguna" VALUES(33,43,'33:43','Inni (Rabbiin) Isa rahmata isin irratti buusuudhaMaleykonni Isaas (akkasuma rahmata isiniif kadhatu); dukkana keessaa gara ifaatti isin baasuuf (isin mararfata); mu’uminootaafis akkaan rahmata godhaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,44,'33:44','Guyyaa Isa qunnaman wanti isaan ittiin wal kabajan ‘nagaan (isin irra haa jiraatuu)’dha(Allaahnis) mindaa gaarii ta’e isaaniif qopheesseera');
INSERT INTO "apaguna" VALUES(33,45,'33:45','Yaa nabiyyichaa! Nuti ragaa, gammachiisaafi akeekkachiisaa goonee si erginee jirra');
INSERT INTO "apaguna" VALUES(33,46,'33:46','Hayyama Isaatiin gara Rabbiitti waamaafi ifaa (haqa) ibsaa ta’e goonee (si ergine).');
INSERT INTO "apaguna" VALUES(33,47,'33:47','Rabbi irraa tolli guddaan isaaniif jiraachuun mu’uminoota gammachiisi.');
INSERT INTO "apaguna" VALUES(33,48,'33:48','Ajaja kaafirootaafi munaafiqootaas tole hin jedhinRakkoo isaaniis (Rabbumatti) dhiisiRabbi irrattiis hirkadhuIrratti hirkachuuf Rabbumatu ga’a.');
INSERT INTO "apaguna" VALUES(33,49,'33:49','Yaa warra amantan! Yeroo dubartoota mu’uminaa fuutanii, Sana booda odoo qunnamtii saalaa hin raawwatin isaan hiiktan, “Iddaan” isin isaan irratti lakkaawwattan isiniif hin jiruIsaan qanansiisaa, gadi dhiisuu bareedaas isaan gadi dhiisaa.');
INSERT INTO "apaguna" VALUES(33,50,'33:50','Yaa nabiyyichaa! Nuti niitiwwan kee isaan ‘Mahrii’ isaaniif kennite, (boojuu irraa) waan Rabbiin sitti deebise irraa warra harki kee horatte, shamarran abbeeraa (wasiila) keetii, shamarran adaadota keetii, shamarran eessumman keetiifi shamarran haboo (obboleettii haadha) keetiis isaan si waliin godaanan, dubartii mu’uminaas yoo isheen nabiyyichaaf of kennitee yoo nabiyyiinis ishee fuuchuu fedhe, mu’uminoota biraatiif odoo hin ta’in si qofaaf qulqulluu taatee (isaan fuuchuu) halaal siif goonee jirraIsaan (mu’uminoota) irratti niitii isaaniitiifi waan harki isaanii horatte keessatti wanta isaan irratti dirqama goone dhugumatti beeknee jirraAkka rakkoon si irratti hin taaneef (kana goone)Rabbiinis araaramaa, rahmata godhaa ta’eera');
INSERT INTO "apaguna" VALUES(33,51,'33:51','Isaan irraa nama feete dhiiftee, nama feete immoo gara keetti maxxanfattaIsaan yeroof dhiifte irraa nama feete (yoo fuutes), badiin sirra hin jiruIjji isaanii gammaduu, gadduu dhabuufi hundi isaanii waan ati isaaniif kennite jaallachutti kanatu dhihoodhaRabbiin waan onnee keessan keessa jiru ni beekaRabbiin beekaa, obsaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,52,'33:52','Kana booda dubartoonni (biraa) siif halaal hin ta’anu; odoma bareedinni isaanii si dinqisiisellee bakka isaanii warra biraa jijjiiruunis, waan mirgi kee dhuunfatte malee (halaala siif hin ta’u)Rabbiinis waan hunda irratti too’ataa ta’eera.');
INSERT INTO "apaguna" VALUES(33,53,'33:53','Yaa warra amantan! Haala bilchina isaa hin eegneen, yoo gara nyaataatti hayyamni isiniif godhame malee manneen nabiyyii hin seeninaaGaruu yeroo waamamtan seenaa; yeroo nyaattanii raawwattan haasa’atti mukuu bahuun (boharuun) maleetti bittinnaa’aaDhugumatti, (gochi) sun nabiyyii rakkisa; isin irraa ni saalfata ta’aRabbiin garuu dhugaa irraa hin saalfatuYeroo meeshaa isaan (niitolii nabiyyii) gaafattanis haguuggii (girdoo) duubaan isaan gaafadhaaAkkas gochuu keessantu onnee keessaniifi onnee isaaniitiifis irra qulqulluudhaErgamaa Rabbii rakkisuufi niitiwwan isaaniis booda (du’a) isaatii gonkumaa fuuchuun isiniif hin maluDhugumatti, (gochi) sun Rabbiin biratti (badii) guddaa ta’eera');
INSERT INTO "apaguna" VALUES(33,54,'33:54','Waan tokko yoo ifa gootan, yookiin yoo isa dhoksitanis dhugumatti Rabbiin waan hunda beekaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,55,'33:55','Isaan (dubartoota nabiyyii SAW) irratti abboota isaanii, ilmaan isaanii, obboleeyyan isaanii, ilmaan obboleeyyan isaanii, ilmaan obbleettiwwan isaanii, dubartoota isaaniitii (Muslimtootaatii)fi waan mirgi isaanii horatte (gabroota isaanii) biratti (miidhaagina isaanii mul’isuu) keessatti badiin isaan irra hin jiruAllaahs sodaadhaaDhugumatti, Rabbiin waan hunda irratti ragaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,56,'33:56','Dhugumatti, Rabbiifi maleykonni Isaas nabiyyii irratti rahmata buusuYaa warra amantan! (isinis) rahmata isa irratti buusaa; nageenyas buusinsa buusaa');
INSERT INTO "apaguna" VALUES(33,57,'33:57','Dhugumatti, isaan Rabbiifi ergamaa Isaa rakkisan, Rabbiin addunyaafi Aakhiraa keessattis isaan abaareeraadabbii xiqqeessaa ta’es isaaniif qopheesseera.');
INSERT INTO "apaguna" VALUES(33,58,'33:58','Isaan mu’uminoota dhiiraafi mu’uminoota dubartiis waan isaan hin hojjatatiniin rakkisan, dhugumatti isaan cubbuufi badii ifa ba’aa baadhataniiru.');
INSERT INTO "apaguna" VALUES(33,59,'33:59','Yaa nabiyyichaa! “Niitolii keetiin, shamarran keetiifi dubartoota mu’uminootatiinis, jilbaaba (hijaaba) isaanii (qaama) isaanii irratti gadi buusaa” jedhiinAkka beekamanii hin rakkifamneefis kanatu irra dhihoodhaRabbiin araaramaa, rahmata godhaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,60,'33:60','Munaafiqoonni warri onnee isaanii keessa dhukkubni jiruufi isaan Madiina keessatti shororkeessitoota ta’aniis yoo hin dhorgamin, Nuti dhugumatti isaan irratti si moosifna; sana booda yeroo gabaabaaf malee ishee (Madiina) keessa sitti olloomanii hin jiraatan.');
INSERT INTO "apaguna" VALUES(33,61,'33:61','Abaaramtoota ta’anii malee (Madiinaa keessatti sitti hin ollooman); Iddoma argamanitti qabamanii ajjeefamiinsa (hamaa) ajjeefaman.');
INSERT INTO "apaguna" VALUES(33,62,'33:62','Karaa Rabbii kan warra duraan dabran keessatti (Inni karaa godhe)Karaa Rabbiitiifis jijjiirama hin argitu.');
INSERT INTO "apaguna" VALUES(33,63,'33:63','Namoonni Qiyaamaa irraa si gaafatu“Dhugumatti, beekumsi ishee Rabbuma bira jira” jedhiinMaaltu si beeksise? Tasa Qiyaamaan dhihoo ta’uun ni kajeelama.');
INSERT INTO "apaguna" VALUES(33,64,'33:64','Dhugumatti, Rabbiin kaafiroota abaaree ibidda boba’aas isaaniif qopheessee jira.');
INSERT INTO "apaguna" VALUES(33,65,'33:65','Yeroo hundas ishee keessa kan jiraatan ta’anii, jaalallees ta’ee gargaaraa (isaan baraaru) hin argatan.');
INSERT INTO "apaguna" VALUES(33,66,'33:66','Guyyaa fuulli isaanii ibidda keessatti gara gaggalfamtu, “yaa hawwii keenya! Odoo ajaja Rabbiitiifi ajaja ergamaa (Isaa) fudhannee” jedhan.');
INSERT INTO "apaguna" VALUES(33,67,'33:67','“Yaa Gooftaa keenya! Nuti ajaja matootii keenyaafi gurguddoota keenyaa fudhanneeti isaanis karaa nu jallisan” jedhan.');
INSERT INTO "apaguna" VALUES(33,68,'33:68','“Yaa Gooftaa keenya! adabbii irraa dachaa lama isaaniif kennii abaarsa guddaa ta’es isaan abaari.”');
INSERT INTO "apaguna" VALUES(33,69,'33:69','Yaa warra amantan! Akka warra Muusaa rakkifnaan Rabbiinis waan isaan jedhan irraa isa (Muusaa) qulqulleessee san hin ta’inaaInnis Rabbiin biratti kabajamaa ta’eera.');
INSERT INTO "apaguna" VALUES(33,70,'33:70','Yaa warra amantan! Rabbiin sodaadhaa; jecha sirrii dubbadhaa.');
INSERT INTO "apaguna" VALUES(33,71,'33:71','Dalagaalee keessanis isiniif tolcha; badiiwwan keessaniis isiniif araaramaNamni ajaja Rabbiifi ergamaa Isaa fudhate dhugumatti milkii guddaa milkaa’eera.');
INSERT INTO "apaguna" VALUES(33,72,'33:72','Nuti amaanaa samii, dachiifi gaarreen irra (bifa filannootiin) fidnaan ishee baadhachuu didanii; ishee irraas sodaatanNamni immoo ishee baadhateDugumatti, inni akkaan of miidhaa, wallaalaa ta’ee jira.');
INSERT INTO "apaguna" VALUES(33,73,'33:73','Rabbiin munaafiqoota dhiiraafi dubartii, mushrikoota dhiiraafi dubartiis adabuufi akkasumas Rabbiin mu’uminoota dhiiraafi dubartii irraa tawbaa qeebaluuf jecha (kana godhe)Rabbiin araaramaa, rahmata godhaa ta’eera');
INSERT INTO "apaguna" VALUES(34,1,'34:1','Faaruun hundi kan Rabbii Isa wanti samii keessaafi wanti dachii keessaa hundi kan Isaa ta’eetiAakhirattis faaruun hundi kanuma IsaatiInni ogeessa, beekaadha.');
INSERT INTO "apaguna" VALUES(34,2,'34:2','Inni waan dachii keessa seenu, waan ishee irraa bahu, waan samii irraa bu’uufi waan ishee keessa ol ba’us ni beekaInni mararfataa, araaramaadha.');
INSERT INTO "apaguna" VALUES(34,3,'34:3','Warri kafaran “Qiyaamaan nutti hin dhuftu” jedhan“Akka isin jettanii mitiRabbii kiyya beekaa waan fagootiin kakadhee! Isheen dhugumatti, isinitti dhufti” jedhiinWanti hanga madaala atamii, wanti sana irra xiqqaafi kan sana irra guddaas samii keessaafi dachii keessattis, isa irraa kan hin fagaanne ta’ee, kitaaba ifa baasaa ta’e keessa jiraatu malee (hin hafu).');
INSERT INTO "apaguna" VALUES(34,4,'34:4','Isaan amananii toltuu hojjatan mindeessuuf jecha (Qiyaamaan dhufti)Warri sun araaramaafi soorata gaarii ta’etu isaaniif jira.');
INSERT INTO "apaguna" VALUES(34,5,'34:5','Isaan keeyyattoota keenya dadhabsiisoo ta’anii socho’an immoo, adabbii (gosa) adabbii laalessaa irraa ta’etu isaaniif jira.');
INSERT INTO "apaguna" VALUES(34,6,'34:6','Isaan beekumsi kennameef, inni Rabbii kee irraa gara keetti buufame inni dhugaa ta’uufi gara karaa (Rabbii) injifataa, faarfamaa ta’eetti kan qajeelchu ta’uu ni beeku.');
INSERT INTO "apaguna" VALUES(34,7,'34:7','Isaan kafaranis “(duutanii) cicciramiinsa guutuu yeroo cicciramtan isin uumaa haarawa keessatti taatu jedhee namicha isinitti odeessutti isin qajeelchinuu?” jedhan');
INSERT INTO "apaguna" VALUES(34,8,'34:8','“Rabbi irratti soba oddeessa moo maraatummaatu isatti jira?” (jedhu)Akkasii mitii; isaan Aakhiratti hin amanne (Aakhiraatti) adabaafi (addunyaatti) jallina fagoo ta’e keessa jiru');
INSERT INTO "apaguna" VALUES(34,9,'34:9','Sila isaan samiifi dachii irraa gara waan dura isaaniifi waan duuba isaanii jiru hin ilaallee? Yoo feenes dachii isaaniin kunna; yookiin cittuu samii irraa taate isaan irratti kuffifnaDhugumatti, sana keessa gabricha gara Rabbiitti tawbaa deebi’u hundaaf mallattootu jira.');
INSERT INTO "apaguna" VALUES(34,10,'34:10','Dhugumatti, Nuti Daawudiif tola Nu biraa ta’e kennineerra“Yaa gaarreenii! sinbira wajjin ta’uun isa (Daawudii) wajjin (Rabbiin) faarsaa” (jenne)Sibiilas isaaf laaffifnee jirra.');
INSERT INTO "apaguna" VALUES(34,11,'34:11','“Qoloo (uffata lolaa) babal’aas hojjadhu; dhawaa isaa keessattis hanga shallagiToltuuwwanis dalagaa; ani wanta isin hojjattan hunda argaadha.');
INSERT INTO "apaguna" VALUES(34,12,'34:12','Suleeymaaniifis waaree dura (karaa) ji’a tokkoo kan deemtuufi waaree boodas (karaa) ji’a tokkoo kan balaliituu taatee bubbee (isaaf laaffifne)Madda sibiilaas (nahaasaas) isaaf yaafnee jirraJinnoota irraas warra hayyama Gooftaa Isaaniitiin fuuldura isaatti hojjatantu jiraIsaan irraa kan ajaja keenya irraa dabe, adabbii ibidda boba’aa irraa ta’e isa dhandhamsiifna.');
INSERT INTO "apaguna" VALUES(34,13,'34:13','Iddoolee ibaadaa, fakkiiwwan, gabateewwan guddinni isaa akka naannigaa ta’eefi okkoteewwan nahaasaa kan iddoo qabattee teessu irraa wanta inni barbaade isaaf hojjatu“Yaa maatii Daawuud (ajaja isaa guutuun) galata (Rabbii) hojjadhaaGabroota kiyya irraas namni Ana galatoomfataan xiqqaadha” (jenneen).');
INSERT INTO "apaguna" VALUES(34,14,'34:14','Yeroma Nuti du’a isa (Suleeymaan) irratti murteessine, rirma dachii kan ulee isaa nyaattu tokko malee wanti du’uu isaa irratti isaan qajelche hin jiruYeroma inni kufu, jinnoonni odoo iccitii (ghaybii) kan beekan ta’anii, silaa adabbii salphisaa ta’e keessa hin turru jechuu hubatan.');
INSERT INTO "apaguna" VALUES(34,15,'34:15','Dhugumatti, (warra) ‘Saba’iif’ iddoo jireenya isaanii keessatti mallattoon ta’eera(Isheenis) oyruu lama gama mirgaafi bitaatiin kan taateedha“Soorata Gooftaa keessanii irraa nyaadhaa; galatas Isaaf galchaa.” (Biyyattiin keessanis) biyya gaariidha(Rabbiin keessanis) Gooftaa araaramaadha” (isaaniin jedhame).');
INSERT INTO "apaguna" VALUES(34,16,'34:16','(Ajaja Rabbii irraas) gara galaniiti lolaa hidhaa irraa isaanitti ergineAshaakiltii isaan lamaaniis oyruu lama kan abbaa dhandhama hadhaa’aa, birbirsaafi qurquraa irraas waan xiqqoo (kan qabdutti) isaaniif jijjiirre');
INSERT INTO "apaguna" VALUES(34,17,'34:17','Waan isaan kafaraniif sana mindaa isaaniif kennine(Adaba akka kanaa) akkaan kafaraa malee ni mindeessinaa?');
INSERT INTO "apaguna" VALUES(34,18,'34:18','Gidduu isaaniitiifi gidduu gandattii barakaa ishee keessatti gooneetti gandoota walitti dhaabbatan goonee hanga adeemsaas ishee keessatti ni safarre“Isaan keessas halkan hedduufi guyyootas nagayaan deemaa.”');
INSERT INTO "apaguna" VALUES(34,19,'34:19','Isaanis “Yaa gooftaa keenya! Gidduu deemsa (karaa) keenyaa addaan fageessi” jedhanLubbuu isaaniis miidhanii, oduuwwan isaan taasifnee, bittinneessuu guutuu isaan bittinneessineDhugumatti, sana keessa namoota akkaan obsaniifi akkaan Rabbiin galatoomfatan hundaaf mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(34,20,'34:20','Dhugumatti, Ibliis yaada isaa isaan irratti dhugoomseeraGaree xiqqaa mu’uminoota irraa ta’e malee isas hordofaniiru.');
INSERT INTO "apaguna" VALUES(34,21,'34:21','Isa Aakhiraa dhugoomsu, isa ishee irraa shakkii keessa jiru irraa akka adda beeknuuf malee isaaf taaytaan tokkollee isaan irratti hin taaneRabbiin keetis wantoota hunda irratti tiksaadha');
INSERT INTO "apaguna" VALUES(34,22,'34:22','(Yaa Muhammad!) “mee isaan Rabbiin ala (gooftoleedha) jettan sana waammadhaaSamiifi dachii keessatti waan hanga atomiillee hin qabanIsaan lamaan (samiifi dachii) keessattis qoodni isaaniif hin jiruIsaaf (Rabbiif) gargaaraan isaan irraa ta’e tokkollee hin jiru.');
INSERT INTO "apaguna" VALUES(34,23,'34:23','Manguddummaan Isa (Rabbiin) biratti nama Inni isaaf hayyame malee hin fayyadduYeroo onnee isaanii (manguddootaa) irraa sodaan deeme “Rabbiin keessan maal jedhe?” jedhanii gaafatu“Dhugaa (jedhe); Innis ol ta’aa, guddaadha” jedhu');
INSERT INTO "apaguna" VALUES(34,24,'34:24','(Yaa Muhammad!) “Eenyutu samiifi dachii irraa isin soora?” jedhi“Rabbuma” jedhiinDhugumatti nuti yookiin isin qajeeluma irra yookiin jallina ifa ta’e keessa jirra” jedhiin.');
INSERT INTO "apaguna" VALUES(34,25,'34:25','“Isin badii nuti hojjanne irraa hin gaafatamtanu; nutis waan isin hojjattan irraa hin gaafatamnu” jedhiin');
INSERT INTO "apaguna" VALUES(34,26,'34:26','“Gooftaan keenya walitti nu qaba; ergasiis dhugaadhaan gidduu keenyatti murteessaInni haqaan murteessaa, beekaadha” jedhiin.');
INSERT INTO "apaguna" VALUES(34,27,'34:27','(Yaa Muhammad! Warra mushrikaatiin) “Warra shariikota gootanii Isatti dhaqqabsiiftan san mee natti argisiisaa? Dhorgamaa! (Inni shariika hin qabu)Dhugumatti, Inni Allaah injifataa, ogeessa” jedhiin.');
INSERT INTO "apaguna" VALUES(34,28,'34:28','Nuti nama hundaaf walumaagalatti gammachiisaafi akeekkachiisaa goonee malee si hin ergineGaruu irra hedduun namootaa hin beekanu.');
INSERT INTO "apaguna" VALUES(34,29,'34:29','“Yoo kan dhugaa dubbattan taatan waadaan (Qiyaamaan) kun yoomi?” jedhu.');
INSERT INTO "apaguna" VALUES(34,30,'34:30','“Beellama guyyaa tokkoo kan isin ishee irraa duubatti hin hafneefi duras hin darbinetu isiniif jira” jedhiin');
INSERT INTO "apaguna" VALUES(34,31,'34:31','Isaan kafaran “Qur’aana kanaafi (kitaaba) isa dura bu’ettis hin amannu” jedhanMiidhaa raawwattoonni yeroo isaan Gooftaa isaanii bira dhaabbatanii jecha gariin isaanii garii irratti deebisu odoo argitee! (Silaa wanta cimaa agarta turte)Isaan dadhaboo turan isaan boonaa turaniin “odoo isin jiraachuu baattanii silaa nuti ni amanna turre” jedhu.');
INSERT INTO "apaguna" VALUES(34,32,'34:32','Isaan boonaa turanis isaan dadhaboo turaniin “sila nutu karaa qajeelaa irraa erga inni isinitti dhufee booda isin deebisee? Lakkisaa! Isinumatu yakkamtoota taatan” jedhuun');
INSERT INTO "apaguna" VALUES(34,33,'34:33','Isaan dadhaboo turanis isaan boonaa turaniin “Lakkisaa; mala keessan kan halkaniifi guyyaatu; yeroo akka Rabbitti kafarruufi hiriyootaas akka Isaaf taasifnu nu ajajjan santu (badii keessa nu buuse).” jedhanHundi isaaniituu yeroo adabicha argan gaabbii (sheenaa) isaanii ni dhokfatanMorma warra kafaranii keessa dirii (sansalata) goonaSila waan dalagaa turan malee ni mindeeffamuu?');
INSERT INTO "apaguna" VALUES(34,34,'34:34','Magaalaa kamillee keessatti akeekkachiisaa tokkoyuu erginee badhaadhoon ishee “nuti waan isin ittiin ergamatan kanatti kafaroodha” jedhan malee hin hafanu.');
INSERT INTO "apaguna" VALUES(34,35,'34:35','“Nuti qabeenyaafi ilmaanis isin caalaa baay’ee qabnaNutis kan adabamnu hin taane” jedhan.');
INSERT INTO "apaguna" VALUES(34,36,'34:36','“Dhugumatti, Gooftaan kiyya soorata nama fedheef bal’isa; (kan fedhe irratti immoo) ni dhiphisaGaruu irra hedduun namaa (kana) hin beekanu” jedhiin.');
INSERT INTO "apaguna" VALUES(34,37,'34:37','Qabeenyi keessaniifi ilmaan keessan kan dhiheenya gara keenyatti isin dhiheessu hin taaneHaa ta’u malee namoonni amananii, gaggaarii dalagan isaan sun sababa waan isaan dalaganiitiin mindaa dachaa ta’etu isaaniif jiraIsaanis gamoowwan ol fuudhamoo keessa nageenyaan jiraatu.');
INSERT INTO "apaguna" VALUES(34,38,'34:38','Warri dadhabsiisoo ta’anii keeyyattoota keenya keessatti sochii godhan, jarri sun adabbii (ibiddaa) keessa fidamoodha');
INSERT INTO "apaguna" VALUES(34,39,'34:39','“Dhugumatti, Rabbiin kiyya gabroota Isaa irraa nama fedheef soorata bal’isa; (nama fedhes) isatti dhiphisa.Wanta kam irraayyuu waan isin kennattan Inni bakka isa buusaInnis irra caalaa warra soorata kennaniiti.');
INSERT INTO "apaguna" VALUES(34,40,'34:40','(Yaa Muhammad!) guyyaa (Rabbiin) hunda isaanii (gabbaraafi gabbaramaas) kaasee, ergasii maleykotaan “sila isaan kun isin gabbaraa turanii?” jedhu [yaadadhu]');
INSERT INTO "apaguna" VALUES(34,41,'34:41','(Maleykonnis) “(yaa gooftaa keenya!) qulqullina siif malu (si qulqulleessinee); isaan odoo hin taane gargaaraan keenya Suma qofa(Akka isaan jedhan) mitii isaan jinni gabbaraa turanIrra hedduun isaaniis isaanitti amanoodha” jedhan');
INSERT INTO "apaguna" VALUES(34,42,'34:42','Har’a (Guyyaa Qiyaamaa), gariin keessan garii fayyaduus ta’ee miidhuu hin danda’uIsaan lubbuu ofii miidhaniinis “adaba Ibiddaa ishee sobsiisaa turtan san dhandhamaa” jenna.');
INSERT INTO "apaguna" VALUES(34,43,'34:43','Yeroo keeyyatoonni keenya ifa taatee isaan irratti dubbifamtus “kun namicha waan abbootiin keessan gabbaraa turan irraa isin dhorguu fedhu malee waan biraatii miti” jedhu“Kun soba uumamaadha malee waan biraa hin taane” jedhuIsaan kafaran yeroma dhugaan isaanitti dhufe “kuni sihrii ifa bahaa malee waan biraa hin taane” jedhan.');
INSERT INTO "apaguna" VALUES(34,44,'34:44','Kitaabota isaan baratan tokkollee isaaniif hin kennineSiin durattis akeekkachiisaa (ergamaa) tokkollee gara isaaniitti hin ergine.');
INSERT INTO "apaguna" VALUES(34,45,'34:45','Warri isaaniin duraas sobsiisanii jiruIsaan kun (humnaafi qabeenyaanis) tokko kurnaffaa waan isaaniif kenninee hin geenyeErgamtoota kiyya sobsiisanii (Nuti isaan balleessine)adabbiin kiyyas akkam ta’e? (Mee xiinxali!)');
INSERT INTO "apaguna" VALUES(34,46,'34:46','(Yaa Muhammad!) jedhi: “kan ani isin gorsu waan tokko qofattiInnis akka cimdiifi kophaanis Allaahf dhaabattanii, ergasii saahiba keessan (Muhammaditti) maraatummaan tokkollee hin jiru jechuu xiinxaltaniifiInni akeekkachiisaa adabbii cimaa dura isiniif (ergame) malee waan biraa hin taane.');
INSERT INTO "apaguna" VALUES(34,47,'34:47','Jedhi: “mindaa irraa wanti ani isin gaafadhe (yoo jiraates) inni isinumaafiMindaan kiyyas Rabbi irratti malee hin taaneInnis waan hunda irratti hirmaataa argaadha.”');
INSERT INTO "apaguna" VALUES(34,48,'34:48','“Dhugumatti, Rabbiin kiyya dhugaa ni buusa(Inni) beekaa iccitiiwwan hundaati” jedhiin');
INSERT INTO "apaguna" VALUES(34,49,'34:49','“Dhugaan dhufe; sobni (taaytaa isaa) hin jalqabu; hin deebi’us” jedhiin');
INSERT INTO "apaguna" VALUES(34,50,'34:50','“Yoon jalladhes, kan ani jalladhu lubbuu kiyya irrattiYoon karaa qajeelaa qabadhes, waan Rabbiin kiyya gara kiyyatti erguuniInni dhugumatti dhagayaa, dhihaataadha” jedhi.');
INSERT INTO "apaguna" VALUES(34,51,'34:51','(Yaa Mohammad SAW!) odoo yeroo isaan (ibidda arganii) rifatan agartee, (silaa waan guddaa agarta)Iddoon itti bahanis (isaaniif) hin jiruIddoo dhihoo irraa qabamanii jiru.');
INSERT INTO "apaguna" VALUES(34,52,'34:52','(Guyyaa Qiyaamaa) “Isatti amannee jirra” jedhuIddoo fagoo (biyya addunyaa) irraa (iimaana) fudhachuun akkamitti isaaniif mijjata?');
INSERT INTO "apaguna" VALUES(34,53,'34:53','Dhugumatti, kana dura isatti kafaranii turanIddoo fagoo ta’e irraa (tilmaamaan) haasawa darbatu');
INSERT INTO "apaguna" VALUES(34,54,'34:54','Gidduu isaaniitiifi gidduu waan isaan fedhaniitti akkuma san dura fakkaattota isaanii irratti godhametti haguuggiin godhamee jira; Dhugumatti, isaan shakkii cimaa keessatti ta’anii jiru.');
INSERT INTO "apaguna" VALUES(35,1,'35:1','Faaruun hundi kan Rabbii uumaa samiifi dachii ta’e, kan maleykotas ergamtoota abbootii kooluuwwan lama lamaa, sadi sadiifi afur afurii taasiseef haa ta’uUumama keessattis waan fedhe ni dabalaDhugumatti, Rabbiin waan hunda irratti danda’aadha');
INSERT INTO "apaguna" VALUES(35,2,'35:2','Rahmata irraa wanta Rabbiin namaaf bane wanti ishee dhorgu tokkoyyuu hin jiruWanta Inni dhorges Isa boodaan wanti ishee gadi dhiisu (ergu) hin jiruInnis injifataa, ogeessa');
INSERT INTO "apaguna" VALUES(35,3,'35:3','Yaa namootaa! Ni’imaa Allaah of irratti yaadadhaaSila Rabbiin ala uumaan biraa kan samiifi dachii irraa soorata isiniif kennu ni jiraa? Isa malee gabbaramaan haqaa hin jiruKanaafuu akkamitti isa (irraa) garagaltu?');
INSERT INTO "apaguna" VALUES(35,4,'35:4','(Yaa Muhammad!) Yoo si sobsiisan, ergamtoonni si duraas (akkuma kee) sobsiifamanii jiruDhimmoonni hundis gara Rabbiitti deebifamu.');
INSERT INTO "apaguna" VALUES(35,5,'35:5','Yaa namootaa! Dhugumatti, waadaan Allaah dhugaadhaKanaafu jireenyi addunyaa akka isin hin gowwoomsineGowwoomsaan (Sheyxaanni) (obsa) Rabbiitiin akka isin hin gowwoomsine');
INSERT INTO "apaguna" VALUES(35,6,'35:6','Dhugumatti, Sheyxaanni isiniif diinaa, isinis diina isa godhaaGareewwan isaas akka warra ibidda boba’aa (seenu) ta’aniif isaan waama.');
INSERT INTO "apaguna" VALUES(35,7,'35:7','Isaan kafaran adabbii cimaatu isaaniif jiraIsaan amananii, gaggaarii hojjatan immoo araaramaafi mindaa guddaatu isaaniif jira');
INSERT INTO "apaguna" VALUES(35,8,'35:8','Sila namni fokkataan hojii isaa isaaf bareechifame, kan gaarii taasisee ishee ilaalu (isa karaa qajeelaa irra jiruu wajjin wal qixa ta’aa)? Dhugumatti, Allaahn nama fedhe ni jallisa; nama fedhe immoo ni qajeelchaKanaafuu isaan irratti lubbuun kee sheenaadhaan akka hin baaneDhugumatti, Allaahn waan isaan hojjatan beekaadha.');
INSERT INTO "apaguna" VALUES(35,9,'35:9','Allaahnis Isa qilleensa ergee, isheenis duumessa ol kaaftee gara lafa duuteetti isa oofnee dachii erga isheen duutee booda ittiin jiraachifneKaafamni Qiyaamaas akkuma kana.');
INSERT INTO "apaguna" VALUES(35,10,'35:10','Namni jabeenya fedhe (Rabbiin bira haa barbaadu)Jabeenyi hunduu kan RabbiitiHaasofni gaariin gara isaa ol bahaDalagaa gaariitu isa ol fuudhaIsaan hojii gadheef tooftaa baasan adabbii cimaatu isaaniif jiraTooftaan isaanii inuma bada');
INSERT INTO "apaguna" VALUES(35,11,'35:11','Rabbiin biyyee irraa, ergasii bishaan irraa isin uumee, ergasii cimdii isin taasiseBeekumsa Isaatiin ala dubartiin kamillee hin ulfooftu; hin deessusNamni umrii dheereffame tokkollee, kitaaba keessa jiraatu malee umriin isaa hin dheereffamuUmrii isaa irraa hin hir’ifamus; dhugumatti, sun Allaah irratti laafaadha.');
INSERT INTO "apaguna" VALUES(35,12,'35:12','Galaanni lamaan wal qixa hin ta’anu; inni tokko mi’aawaa, dheebu kutaa, dhugaatiin isaas mijaa’aadha; inni kun immoo ashaboo akkaan kuraawaadhaHunduma irraayyuu foon haarawa ni nyaattu; faaya ishee uffattanus ni baafattuAkka tola isaa irraa barbaaddattanuufi akka isa galatoomfattanuuf dooniiwwan isa (galaana) keessa baqaqsituu taatee (kan deemtu) agarta');
INSERT INTO "apaguna" VALUES(35,13,'35:13','Inni halkan guyyaa keessa seensisa; guyyaas halkan keessa seensisaAduufi ji’as ni laaffise; hunduu hanga beellama beekamaa ta’eetti (orbitii isaanii irra) yaa’uInni (kana hojjate) kun Rabbii keessan AllaahdhaIsaan isin Rabbii gaditti kadhattan wanta hanga qola haphii ija timiraallee hin qabanu');
INSERT INTO "apaguna" VALUES(35,14,'35:14','Yoo isaan kadhattanis, kadhaa keessan hin dhagahan; odoo dhagahaniis isiniif hin owwaatanGuyyaa Qiyaamaas shirkii keessan ni mormu(Yaa Muhammad!) Akka Rabbii keessa beekaatti homtuu siif hin odeessu.');
INSERT INTO "apaguna" VALUES(35,15,'35:15','Yaa namootaa! Isinumatu gara Allaahtti haajamoodha; Allaahn Isumatu dureessa, faarfamaadha.');
INSERT INTO "apaguna" VALUES(35,16,'35:16','Yoo fedhe isin (dachii irraa) deemsiseeti uumama haaraa fida.');
INSERT INTO "apaguna" VALUES(35,17,'35:17','Sunis Allaah irratti ulfaataa miti.');
INSERT INTO "apaguna" VALUES(35,18,'35:18','(Lubbuun) badii hojjattu (tokkos) badii lubbuu biraa hin baadhattu(Lubbuun) ba’aan (badiin) itti ulfaate tokkos akka ba’aa ishee namni biraa irraa baadhatu yoo gaafattes, odoma fira ishee ta’eellee ishee irraa homaayyuu hin baadhatamuKan ati akeekkachiiftu isaan Rabbii isaanii fagootti sodaatanii, Salaatas gadi dhaabaniidhaNamni (badii irraa) of qulqulleesse, kan inni of qulqulleessu matuma isaatiifiDeebi’iinsi garuma Rabbiiti.');
INSERT INTO "apaguna" VALUES(35,19,'35:19','Jaamaafi namni Ija qabus wal hin qixxaatu.');
INSERT INTO "apaguna" VALUES(35,20,'35:20','Dukkanniifi iftis (akkasuma).');
INSERT INTO "apaguna" VALUES(35,21,'35:21','Gaaddisniifi ho’i cimaanis');
INSERT INTO "apaguna" VALUES(35,22,'35:22','Namoonni lubbuun jiraniifi isaan du’anis wal qixaa mitiDhugumatti, Allaahn nama fedhe ni dhageessisaAti garuu nama awwaala keessa jiru dhageessisaa miti.');
INSERT INTO "apaguna" VALUES(35,23,'35:23','Ati akeekkachiisaa malee waan biraa hin taane.');
INSERT INTO "apaguna" VALUES(35,24,'35:24','Nuti gammachiisaafi akeekkachiisaa taasifnee dhugaadhaan si erginee jirraUmmanni tokkollee kan akeekkachiisaan isaan keessatti hin ergamin hin jiru.');
INSERT INTO "apaguna" VALUES(35,25,'35:25','Yoo si sobsiisanis, warri isaaniin duraas dhugumatti (ergamoota) sobsiisanii jiruErgamtoonni isaanii ragaalee ifa ta’aniin, kitaabota wanta hunda of keessatti hammataniifi kitaaba ibsaa ta’een, isaanitti dhufanii turan.');
INSERT INTO "apaguna" VALUES(35,26,'35:26','Ergasiis isaan kafaran qabeen jiraadabbiin kiyyaas akkam ta’e!');
INSERT INTO "apaguna" VALUES(35,27,'35:27','Rabbiin bishaan samii irraa buusee, ittiin fuduraalee bifti isaa adda adda ta’e akka biqilchine hin arginee? Gaarreen irraas karaalee adaadiifi diddiimaa, bifti isaanii gara gara ta’eefi akkaan gurraacha ta’anis (akka uumne hin arginee?)');
INSERT INTO "apaguna" VALUES(35,28,'35:28','Namoota, lubbu qabeeyyiifi beyladoota irraahis warra bifti isaanii akkasuma gara gara ta’es ni uumneGabroota Isaa irraas kan Allaah sodaatan beektota qofaDhugumatti, Rabbiin injifataa, araaramaadha');
INSERT INTO "apaguna" VALUES(35,29,'35:29','Dhugumatti, isaan kitaaba (Qur’aana) Rabbii dubbisanii, salaatas sirnaan dhaabaniifi kan waan Nuti isaaniif kennine irraa dhoksaafi ifattis kennan, daldala kasaaraa hin qabne kajeelu.');
INSERT INTO "apaguna" VALUES(35,30,'35:30','Akka Inni (Rabbiin) mindaa isaanii guutee isaaniif kennuufi tola isaa irraas isaanii dabaluuf (kajeelu)Dhugumatti, Inni araaramaa, galateeffataadha.');
INSERT INTO "apaguna" VALUES(35,31,'35:31','Kitaaba (Qur’aana) irraa waan isa duraa kan dhugoomsu ta’ee kan Nuti gara keetti buufne isatu dhugaadhaDhugumatti Rabbiin gabroota isaa akkaan beekaa argaadha.');
INSERT INTO "apaguna" VALUES(35,32,'35:32','Ergasii kitaabicha (Qur’aana kana) isaan gabroota keenya keessaa filanne dhaalchifnee jirraIsaan irraas nama lubbuu ofii miidhutu jira; isaan irraas nama karaa giddu galeessaa filatutu jiraIsaan irraas warra hayyama Rabbiitiin hojiiwwan gaggaarii irratti dursantu jiraSun isumatu tola guddaadha.');
INSERT INTO "apaguna" VALUES(35,33,'35:33','Jannata qubannaa kan ishee seenan, ishee keessattis gumeewwan warqee irraa ta’aniifi faaya uffifamantu (isaaniif jira)Uffanni isaaniis ishee keessatti hariira');
INSERT INTO "apaguna" VALUES(35,34,'35:34','Ni jedhus: “faaruun kan Rabbii gadda nurraa deemsiseetiDhugumatti, Gooftaan keenya araaramaa, galateeffataadha.');
INSERT INTO "apaguna" VALUES(35,35,'35:35','(Inni) Isa tola Isa irraa ta’een mana jireenyaa kan gonkumaa keessaa hin baane, kan ishee keessatti rakkoon nu hin tuqneefi kan ishee keessattis dadhabbiin nu hin tuqne keessa nu qubachiiseedha.');
INSERT INTO "apaguna" VALUES(35,36,'35:36','Isaan kafaran ibidda jahannamtu isaaniif jiraIsaanitti murteeffamtees hin du’anuadabbii ishee irraas homtuu isaan irraa hin salphifamuAkkaan kafaraa hunda akka kanatti mindeessina.');
INSERT INTO "apaguna" VALUES(35,37,'35:37','Isaanis ishee keessatti “(Yaa) Gooftaa keenya! Isa duraan hojjachaa turreen ala toltuu hojjennaa nu baasi” jechuun ishee keessaa waywaatu“Sila umrii dheeraa namni gorfamuu fedhe isa keessatti gorfamuu danda’u isin hin jiraachifnee? akeekkachiisaanis isinitti hin dhufnee? (Adaba kiyya) dhandhamaa; miidhaa hojjattootaaf tumsaan hin jiru.”');
INSERT INTO "apaguna" VALUES(35,38,'35:38','Dhugumatti, Rabbiin beekaa fagoo samiifi dachiitiInni beekaa wantoota qoma keessa jiraniiti.');
INSERT INTO "apaguna" VALUES(35,39,'35:39','Inni Isa dachii keessatti bakka bu’oota isin godheNamni kafare kufriin isaa isuma irrattiKaafiroota kufriin isaanii Rabbii isaanii biratti jibbaa malee homaa isaaniif hin dabaluKaafiroota kufriin isaanii hoongoo malee homaa isaaniif hin dabalu.');
INSERT INTO "apaguna" VALUES(35,40,'35:40','“Sila hiriyoota (shariikoota) keessan kan Rabbiin ala gabbartan mee naaf himaa? Dachii irraa waan isaan uuman mee na argisiisaaSila samii (uumuu) keessatti hirmaannaan isaaniif ni jiraa? Moo kitaaba isaan ragaa qabatamaa isa irraa ta’e irratti jiran isaaniif kenninee? Lakkisaa; miidhaa hojjattoonni gariin isaanii gariif wal gowwoomsuu malee homaa waadaa waliif hin galanu.');
INSERT INTO "apaguna" VALUES(35,41,'35:41','Dhugumatti, Rabbiin samiifi dachiin akka hin deemne qabaOdoo bakkaa deemaniis Isa malee wanti tokkoyyuu isaan lamaan hin qabuInni obsaa, araaramaa ta’eera');
INSERT INTO "apaguna" VALUES(35,42,'35:42','Yoo akeekkachiisaan isaanitti dhufe ummatoota kamillee caalaa qajeelaa taana jechuun kakuu isaanii cimaa Rabbitti kakatanYeroma akeekkachiisan isaanitti dhufu (dhugaa irraa) dheefa malee homaa isaaniif hin daballe');
INSERT INTO "apaguna" VALUES(35,43,'35:43','Dachee keessatti boonaafi tooftaa dharaa (malee isaaniif hin daballe)Tooftaan hamaan abbaa isaatti malee hin marsuSila karaa warra durii malee maal eegan? Karaa Rabbiitiif jijjiirama hin argitu; karaa Rabbiitiif (nama biraatti) naannessuu hin argitu');
INSERT INTO "apaguna" VALUES(35,44,'35:44','Sila isaan dachii keessa deemanii booddeen warra isaaniin duraa oduma humnaanis isaan irra cimoo ta’anii akkam akka ture hin ilaalanuu? Rabbiin samii keessattiifi dachii keessattis wanti isa dadhabsiisu hin jiruInni beekaa, danda’aa ta’eera');
INSERT INTO "apaguna" VALUES(35,45,'35:45','Rabbiin odoo namoota waan isaan hojjataniif qabee dugda ishee (dachii) irratti lubbu qabeeyyii tokkoyyuu hin dhiisu tureGaruu hanga yeroo beekamaatti isaan tursiisaYeroon isaanii hoggaa dhufe (mindeessa)Rabbiin gabroota isaa argaa ta’eera');
INSERT INTO "apaguna" VALUES(36,1,'36:1','Yaa Siin.');
INSERT INTO "apaguna" VALUES(36,2,'36:2','Qur’aana ogummaan guutameenin kakadha.');
INSERT INTO "apaguna" VALUES(36,3,'36:3','Dhugumatti, ati ergamtoota irraayyi');
INSERT INTO "apaguna" VALUES(36,4,'36:4','Karaa qajeelaa irra jirta.');
INSERT INTO "apaguna" VALUES(36,5,'36:5','Buusiinsa Rabbii injifataa, rahmata godhaa ta’e irraa (buufame).');
INSERT INTO "apaguna" VALUES(36,6,'36:6','Ummata abbootiin isaanii hin akeekkachiifamin, kan dagatoo turan akka ittiin akeekkachiiftuuf (buufame)');
INSERT INTO "apaguna" VALUES(36,7,'36:7','Dhugumatti, irra hedduu isaanii jechi (adabbiin) irratti mirkanaa’era; kanaafuu isaan hin amananu');
INSERT INTO "apaguna" VALUES(36,8,'36:8','Nuti morma isaanii irratti takaallaa goone; isheen hanga lafee areedaatti (geessi)Kanaafuu isaan (mataa) ol fudhamoodha');
INSERT INTO "apaguna" VALUES(36,9,'36:9','Dura isaaniitti cufaa, duuba isaanittis cufaa goonee isaan haguugneKanaafuu isaan hin argan');
INSERT INTO "apaguna" VALUES(36,10,'36:10','Isaan akeekkachiiftus, isaan akeekkachiisuu baattus isaan irratti wal qixa; hin amananu.');
INSERT INTO "apaguna" VALUES(36,11,'36:11','Kan ati akeekkachiiftu nama gorsa hordofee Rahmaaniinis fagootti sodaate qofaAraaramaafi mindaa gaariin isa gammachiisi');
INSERT INTO "apaguna" VALUES(36,12,'36:12','Nuti numatu du’aa jiraachifnaWaan isaan hojjatanii dabarsaniifi hambaalee isaanii ni barreessinaWaan hundayyuu kitaaba ifa bahaa keessatti galmeessinee jirra');
INSERT INTO "apaguna" VALUES(36,13,'36:13','Isaaniif fakkeenya warra gandaa kan yeroo ergamtoonni isheetti dhufee godhi.');
INSERT INTO "apaguna" VALUES(36,14,'36:14','Yeroo Nuti (ergamoota) lama gara isaaniitti erginee, isaan lamaanuu sobsiisanii, ergasii sadaffaadhaan (isaan) jajjabeessinee, “nuti gara keessanitti ergamoodha” jedhan (fakkeenya godhiif)');
INSERT INTO "apaguna" VALUES(36,15,'36:15','Isaanis “isin namuma akka keenyaati malee waan biraa mitiRabbiinis homa tokkoyyuu hin buufne; isinis kan sobdan malee waan biraa hin taane” jedhan');
INSERT INTO "apaguna" VALUES(36,16,'36:16','(Ergamtoonnis) “nuti gara keessanitti ergamoo ta’uu Rabbiin keenya ni beeka” jedhan.');
INSERT INTO "apaguna" VALUES(36,17,'36:17','“(Ergaa Rabbii) kan ifa ta’e geessuu malee homtuu nurra hin jiru.”');
INSERT INTO "apaguna" VALUES(36,18,'36:18','(Ummannis) “Nuti isiniin hooddanne (milkii ilaallanne) yoo (waamicha keessan irraa) hin dhorgamin, dhagaan isin tumnaAdabbiin laalessaa ta’es nurraa isin tuqa” jedhan.');
INSERT INTO "apaguna" VALUES(36,19,'36:19','“Hooddannaan (milkii ilaallachuun) keessan isinumaa wajjin” jedhaniSila yoo gorfamtanis (ni hooddattuu)? Akkasii mitiIsinumatu ummata daangaa dabritoota” jedhan');
INSERT INTO "apaguna" VALUES(36,20,'36:20','Kutaa fagoo magaalattii irraa namichi tokko fiigaa dhufee ni jedhe: “yaa ummata kiyya! Ergamtoota hordofaa.');
INSERT INTO "apaguna" VALUES(36,21,'36:21','Namoota mindaa (baasii) isin hin gaafanneefi ofii isaanitiifis qajeelfamoo ta’an hordofaa.');
INSERT INTO "apaguna" VALUES(36,22,'36:22','Ani maaliifan Isa na uume, kan isiniis gara Isaa deebifamtanu hin gabbarre?');
INSERT INTO "apaguna" VALUES(36,23,'36:23','Sila ani Isaa gadittiin gabbaramtoota biraa kan Yoo Rahmaan miidhaa wahiitiin na fedhe jaarsummaan isaanii homaa ana hin fayyadneefi isaaniis ana hin baraarsinen taasifadhaa?');
INSERT INTO "apaguna" VALUES(36,24,'36:24','Ani yeroo san jallina ifa galaa keessa ta’eera');
INSERT INTO "apaguna" VALUES(36,25,'36:25','Ani Rabbii keessanitti amanee na dhagayaa” [jedhe](Achumaan dhagaan tumanii isa ajjeesan).');
INSERT INTO "apaguna" VALUES(36,26,'36:26','“Jannata seeni” jedhameenInnis ni jedhe: “ummanni kiyya odoo beekaniin hawwa!');
INSERT INTO "apaguna" VALUES(36,27,'36:27','Waan Rabbiin kiyya naaf araarameefi kabajamtoota irraa na godhe (odoo beekaniin hawwa).”');
INSERT INTO "apaguna" VALUES(36,28,'36:28','Booda isaatii ummata isaa irratti hoomaa waraanaa samii irraa hin buufne! Kan buufnus hin turre.');
INSERT INTO "apaguna" VALUES(36,29,'36:29','Isheen iyya tokko malee waan biraa hin taaneYeroma san isaan dhaamoo (dhabamoo)dha.');
INSERT INTO "apaguna" VALUES(36,30,'36:30','Yaa gaabbii gabroota kiyyaa! Ergamaan kamillee isaanitti dhufee isatti qishinan (baacan) malee hin hafanu.');
INSERT INTO "apaguna" VALUES(36,31,'36:31','Sila isaan dura ummata irraa meeqaatama akka balleessine hin arginee? Isaan gara isaaniitti hin deebi’anuu (hin arginee)?');
INSERT INTO "apaguna" VALUES(36,32,'36:32','Dhugumatti, hundi isaaniituu (murtiif) fuuldura keenyatti dhihaatu.');
INSERT INTO "apaguna" VALUES(36,33,'36:33','Dachii duutuun Nuti ishee jiraachifneefi firii (adda addaa) ishee irraa baafnee isa irraa nyaatan isaaniif mallattoodha.');
INSERT INTO "apaguna" VALUES(36,34,'36:34','Ishee keessatti oyruu muka timirootaafi inabootaa irraa ta’e taasifneerraIshee keessaa burqaalee irraas maddisiifnee jirra.');
INSERT INTO "apaguna" VALUES(36,35,'36:35','Fuduraa isaatiifi waan harki isaanii hojjatte irraa akka nyaataniif (kana goone)Sila hin galateeffatanuu?');
INSERT INTO "apaguna" VALUES(36,36,'36:36','Inni waan dachiin biqilchitu irraa, lubbuu isaanii irraafi wantoota isaan hin beekne irraa cimdii hunda uume qulqullaa’eera');
INSERT INTO "apaguna" VALUES(36,37,'36:37','Halkanis isaaniif mallattoodhaIsarraa guyyaa baafnaYeroma san isaan dukkana keessatti ta’u.');
INSERT INTO "apaguna" VALUES(36,38,'36:38','Aduunis hanga daangaa murtaa’eetti deemtiSun murtee (Rabbii) injifataa, beekaati');
INSERT INTO "apaguna" VALUES(36,39,'36:39','Baatiis hanga inni akka damee meexxiitti deebi’utti buufata isaa goonee jirra.');
INSERT INTO "apaguna" VALUES(36,40,'36:40','Aduun baatii dhaqqabuun isheef hin ta’u; halkanis guyyaa hin dursuHunduu sarara keessa deemu.');
INSERT INTO "apaguna" VALUES(36,41,'36:41','Nuti sanyii isaanii doonii guutame keessatti fe’uun keenya isaaniif mallattoodha.');
INSERT INTO "apaguna" VALUES(36,42,'36:42','Fakkaataa isaa (doonii Nuuh) irraas waan isaan yaabbatan isaaniif gooneerra');
INSERT INTO "apaguna" VALUES(36,43,'36:43','Odoo feenee bishaaniin isaan dhidhimsina turreBirmataan tokkos isaaniif hin jiruIsaan hin baraarfamanus');
INSERT INTO "apaguna" VALUES(36,44,'36:44','Rahmata nurraa ta’eef hanga yeroo (muraasaa) akka qanani’aniif (dhiifne) malee (ni dhidhimsina turre).');
INSERT INTO "apaguna" VALUES(36,45,'36:45','Yeroo “akka rahmanni isiniif godhamuuf waan fuuldura keessaniifi duuba keessan jiru sodaadhaa” isaanin jadhame (irraa gara galu)');
INSERT INTO "apaguna" VALUES(36,46,'36:46','Mallattoolee Rabbii irraa mallattoon tokko isaanitti dhuftee ishee irraa gara galan malee hin hafan.');
INSERT INTO "apaguna" VALUES(36,47,'36:47','Yeroo “waan Rabbiin isiniif kenne irraa kennadhaa” isaaniin jadhame, warri kafaran warra amananiin “sila nama Rabbiin odoo fedhee isa nyaachisu nyaachifnaa? Isin jallina ifa ta’e keessa malee hin jirtanu” jedhu.');
INSERT INTO "apaguna" VALUES(36,48,'36:48','Ni jedhu: “Yoo warra dhugaa dubbatu taatan waadaan kun yoomi?”');
INSERT INTO "apaguna" VALUES(36,49,'36:49','Iyyansa tokkittii kan haala isaan wal falmaa jiranuun isaan qabdu malee homaa hin eeganu.');
INSERT INTO "apaguna" VALUES(36,50,'36:50','Yeroo san dhaamsa (walii dabarsuu) hin danda’an; gara maatii isaaniis hin deebi’anu.');
INSERT INTO "apaguna" VALUES(36,51,'36:51','Garriin (xurunbaan) ni afuufama; yoosuu isaan awwaala keessaa gara Gooftaa isaanii bahu.');
INSERT INTO "apaguna" VALUES(36,52,'36:52','“Yaa badii keenya! Hirriiba keenya irraa eenyutu nu dammaqse? Kun waan Rahmaan waadaa nuuf gale; ergamtoonnis dhugaa dubbatan” jedhan.');
INSERT INTO "apaguna" VALUES(36,53,'36:53','Isheen afuuffii tokko malee waan biraatii mitiYeroo san isaan hundi nu bira fidaman.');
INSERT INTO "apaguna" VALUES(36,54,'36:54','“Har’a lubbuun kamillee homaa hin miidhamtuWaan dalagaa turtan malee hin mindeeffamtanu” isaaniin jedhama.');
INSERT INTO "apaguna" VALUES(36,55,'36:55','Dhugumatti, warri jannataa har’a dalagaan qabamanii gammachuun guutamu.');
INSERT INTO "apaguna" VALUES(36,56,'36:56','Isaaniifi niitiiwwan isaaniis gaaddisa keessa, sireewwan irratti hirkatu.');
INSERT INTO "apaguna" VALUES(36,57,'36:57','Isaaniif ishee keessatti fuduraaleetu jiraWanti isaan kadhatan hunduu isaaniif jira.');
INSERT INTO "apaguna" VALUES(36,58,'36:58','Salaamtaan Rabbii abbaa rahmataa irraa isaaniin jedhama.');
INSERT INTO "apaguna" VALUES(36,59,'36:59','“Isin yaa yakkamtootaa! Har’a (Mu’uminootaan) adda ba’aa” (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(36,60,'36:60','“Yaa ilmaan Aadam! Sheyxaana hin gabbarinaa; inni isiniif diina ifa ba’aadha” jechuun waadaa gara keessanitti hin dabarsinee?');
INSERT INTO "apaguna" VALUES(36,61,'36:61','“Ana gabbaraa; kanatu karaa qajeelaadha” (isiniin hin jennee)?');
INSERT INTO "apaguna" VALUES(36,62,'36:62','Dhugumatti (sheyxaanni) isin irraa uumama baay’ee jalliseeraSila isin sammuu hin qabdanuu?');
INSERT INTO "apaguna" VALUES(36,63,'36:63','“Isheen kun jahannama ishee waadaa isiniif galamaa turteedha.');
INSERT INTO "apaguna" VALUES(36,64,'36:64','Waan kafaraa turtaniif har’a ishee seenaa” (jedhamaani).');
INSERT INTO "apaguna" VALUES(36,65,'36:65','Har’a afaan isaanii ni cufna; harki isaanii Nutti dubbatti; miilli isaaniis waan isaan hojjataa turan ragaa baati.');
INSERT INTO "apaguna" VALUES(36,66,'36:66','Odoo feenee ija isaanii haqna turreGama karaatti wal dorgomu; akkamitti argu!');
INSERT INTO "apaguna" VALUES(36,67,'36:67','Odoo feenee iddoowwan isaanii irratti gara uumama biraatti isaan jijjiirras turreIsaan duratti deemuus ta’ee duubatti deebi’uu hin danda’anu.');
INSERT INTO "apaguna" VALUES(36,68,'36:68','Nama umrii dheeressine uumaa kessatti isa gadi dabsina (cabsina)Sila hin yaadanuu?');
INSERT INTO "apaguna" VALUES(36,69,'36:69','Nuti isa (Nabiyyii) walaloo hin barsiifne; isaafis hin maluInni yaadannoofi Qur’aana ifa galaa malee waan biraa miti');
INSERT INTO "apaguna" VALUES(36,70,'36:70','Akka inni nama jiraa ta’e akeekkachiisuufi kaafiroota irratti jechi (adabbii) mirkanaa’uuf (isa buufne).');
INSERT INTO "apaguna" VALUES(36,71,'36:71','Sila waan harki keenya dalagde irraa beyladoota isaaniif uumnee isaan ishee horatoo ta’an hin ilaallee?');
INSERT INTO "apaguna" VALUES(36,72,'36:72','Ishee isaaniif laaffifneGariin yaabbii isaaniitiif oolu; garii isaanii immoo ni nyaatu');
INSERT INTO "apaguna" VALUES(36,73,'36:73','Isaaniif ishee keessa faayidaafi dhugaatiiwwantu jiraSila hin galateeffattanuu?');
INSERT INTO "apaguna" VALUES(36,74,'36:74','Akka tumsamaniif jecha Rabbiin ala gabbaramtoota godhatan.');
INSERT INTO "apaguna" VALUES(36,75,'36:75','Isaan tumsuu hin danda’anIsaan (kaafiroonni) isaaniif (sanamaaf) garee khidmaaf dhufanuudha');
INSERT INTO "apaguna" VALUES(36,76,'36:76','Jechi isaanii si hin yaaddessinNuti waan isaan dhoksaniifi waan isaan ifa godhanis ni beekna.');
INSERT INTO "apaguna" VALUES(36,77,'36:77','Sila namni akka Nuti bishaan saalaa irraa isa uumne hin arginee? Yeroo san inni mormaa ifa ba’aa ta’ee argama.');
INSERT INTO "apaguna" VALUES(36,78,'36:78','Fakkeenya Nuuf godheeti, uumama isaa dagatee “erga isheen bututtee lafee eenyutu jiraachisa?” jedhe');
INSERT INTO "apaguna" VALUES(36,79,'36:79','“Isa yeroo jalqabaa ishee uumetu jiraachisa” jedhiInni uumama hunda beekaadha.');
INSERT INTO "apaguna" VALUES(36,80,'36:80','(Inni) Isa muka magariisa irraa ibidda isiniif godheedhaYeroo san isin isa irraa qabsiifattu.');
INSERT INTO "apaguna" VALUES(36,81,'36:81','Sila Inni samiifi dachii uume fakkaataa isaanii uumuu irratti danda’aa mitii? Eeyyeen (danda’aa dha!)Inni akkaan uumaa, beekaadha.');
INSERT INTO "apaguna" VALUES(36,82,'36:82','Ajajni Isaa, yoo waan tokko fedhe “tahi” jechuma qofa; yoosuu taha.');
INSERT INTO "apaguna" VALUES(36,83,'36:83','Inni mootummaan waan hundaa harka Isaa jiru qulqullaa’e! Garuma Isaatti deebifamtu.');
INSERT INTO "apaguna" VALUES(37,1,'37:1','(Maleykota) hiriiruu hiriiraniin (kakadha.)');
INSERT INTO "apaguna" VALUES(37,2,'37:2','(Maleeykota) oofiinsa oofaniinis');
INSERT INTO "apaguna" VALUES(37,3,'37:3','(Isaan Qur’aana) dubbisuu dubbisaniinis.');
INSERT INTO "apaguna" VALUES(37,4,'37:4','Dhugaan gabbaramaan keessan tokkicha');
INSERT INTO "apaguna" VALUES(37,5,'37:5','(Inni) Gooftaa samii, dachiifi wantoota jidduu isaan lamaanii jiraniitiGooftaa baha adda addaatis.');
INSERT INTO "apaguna" VALUES(37,6,'37:6','Dhugumatti, Nuti samii addunyaa faaya urjiitiin miidhagsineerra.');
INSERT INTO "apaguna" VALUES(37,7,'37:7','Sheyxaana fincilaa ta’e hunda irraas tiksuu (tiksineerra)');
INSERT INTO "apaguna" VALUES(37,8,'37:8','Gara garee ol aanaa hin dhaggeeffachiifamanuGama hunda irraa (urjiin) darbataman');
INSERT INTO "apaguna" VALUES(37,9,'37:9','Ari’amuuf jecha (darbatamu)Isaaniif adabbii addaan hin cinnetu jira');
INSERT INTO "apaguna" VALUES(37,10,'37:10','Warra (wahyii) butiinsa butee, qaanqeen uraa ta’e isa hordofe malee');
INSERT INTO "apaguna" VALUES(37,11,'37:11','Mee Isaan gaafadhuUumuu keessatti kan irra cimu isaani moo warra uumne kan biraati? Nuti dhoqqee walitti qabattuu irraa isaan uumne');
INSERT INTO "apaguna" VALUES(37,12,'37:12','Dhugumatti, (diduu isaanii) dinqisiifattee jirta; isaan immoo ni qoosu.');
INSERT INTO "apaguna" VALUES(37,13,'37:13','Yeroo gorfamanis hin gorfamanu.');
INSERT INTO "apaguna" VALUES(37,14,'37:14','Yeroo mallattoo arganis ni baacu.');
INSERT INTO "apaguna" VALUES(37,15,'37:15','“Kun Sihrii ifa galaadha malee waan biraatii miti” jadhan.');
INSERT INTO "apaguna" VALUES(37,16,'37:16','“Sila erga duunee, biyyeefi lafee taanee booda, nuti kaafamnaa?');
INSERT INTO "apaguna" VALUES(37,17,'37:17','Abbootiin keenya kan jalqabaatis moo?');
INSERT INTO "apaguna" VALUES(37,18,'37:18','(Yaa Muhammad!) “eeyyeen; xiqqaatoo taatanii (kaafamtu)” jedhiin.');
INSERT INTO "apaguna" VALUES(37,19,'37:19','Isheen iyyansa tokko qofa; yeroo san isaan ni ilaalu.');
INSERT INTO "apaguna" VALUES(37,20,'37:20','“Yaa badii keenya! Kun guyyaa murtiiti” jedhan');
INSERT INTO "apaguna" VALUES(37,21,'37:21','“Kun Guyyaa murtii kan isin sobsiisaa turtaniidha” (jedhama).');
INSERT INTO "apaguna" VALUES(37,22,'37:22','“Isaan miidhaa dalagan, hiriyoota (fakkaatoo) isaaniitiifi waan isaan gabbaraa turanis walitti qabaa” (malaykotaan jedhama.)');
INSERT INTO "apaguna" VALUES(37,23,'37:23','“Rabbiin ala (warra gabbaraa turan walitti qabaa); gara karaa ibidda jahiim isaan qajeelchaa.');
INSERT INTO "apaguna" VALUES(37,24,'37:24','Isaan dhaabaa; dhugumatti, isaan ni gaafatamu.');
INSERT INTO "apaguna" VALUES(37,25,'37:25','Sila maal taataniiti wal hin tumsine?”');
INSERT INTO "apaguna" VALUES(37,26,'37:26','Dhugumatti isaan har’a harka kennoodha');
INSERT INTO "apaguna" VALUES(37,27,'37:27','Gariin isaanii garii irratti gara galanii, wal gaafatu.');
INSERT INTO "apaguna" VALUES(37,28,'37:28','Ni jedhu: “dhugumatti, isin mirga irraan [gaarii fakkeessuun] nutti dhufaa turtan.”');
INSERT INTO "apaguna" VALUES(37,29,'37:29','Ni jedhuun: “isinumatu mu’uminoota hin ta’ini.');
INSERT INTO "apaguna" VALUES(37,30,'37:30','Nuti isin irratti aangoo hin qabnu turreGaruu isinumatu ummata daangaa dabroo taatan.”');
INSERT INTO "apaguna" VALUES(37,31,'37:31','Jechi Rabbii keenyaa nurratti mirkanaa’eera; dhugumatti nuti (adabbii) dhandhamoodha');
INSERT INTO "apaguna" VALUES(37,32,'37:32','Ofii keenyaa jallanneeti isin jallifne” [jedhu].');
INSERT INTO "apaguna" VALUES(37,33,'37:33','Isaan guyyaa san adabbii keessatti waliin hirmaatoodha.');
INSERT INTO "apaguna" VALUES(37,34,'37:34','Nuti warra badii hojjatan irratti akka kana dalagna.');
INSERT INTO "apaguna" VALUES(37,35,'37:35','Dhugumatti, isaan yeroo “Rabbiin malee gabbaramaan dhugaa hin jiru” isaanin jedhamu ni boonu.');
INSERT INTO "apaguna" VALUES(37,36,'37:36','“Sila nuti weellisaa maraataa (kanaaf) gabbaramtoota keenya dhiifnaa?” jedhu');
INSERT INTO "apaguna" VALUES(37,37,'37:37','Lakki! Inni dhugaan dhufeeti ergamtootas dhugoomse');
INSERT INTO "apaguna" VALUES(37,38,'37:38','Dhugumatti, isin adabbii laalessaa dhandhamuuf jirtu.');
INSERT INTO "apaguna" VALUES(37,39,'37:39','Waan dalagaa turtan malee hin mindeeffamtan;');
INSERT INTO "apaguna" VALUES(37,40,'37:40','Garuu gabroonni Rabbii qulqulluun');
INSERT INTO "apaguna" VALUES(37,41,'37:41','Warri sun kennaa beekamaa ta’etu isaaniif jira.');
INSERT INTO "apaguna" VALUES(37,42,'37:42','Oduma kabajamoo ta’anuu fuduraaleen isaaniif taatuudha');
INSERT INTO "apaguna" VALUES(37,43,'37:43','Jannata qananii keessatti (kabajamoodha).');
INSERT INTO "apaguna" VALUES(37,44,'37:44','Haala walitti gara galoo ta’aniin siree irratti ta’u.');
INSERT INTO "apaguna" VALUES(37,45,'37:45','Kubbaayyaa farsoo qulqulluu irraa ta’een isaan irra naanna’ama;');
INSERT INTO "apaguna" VALUES(37,46,'37:46','Adii, warra dhuganiif mi’aayaa kan taate.');
INSERT INTO "apaguna" VALUES(37,47,'37:47','Ishee keessa dhukkubni hin jiru; isaan ishee irraa hin machaa’anis.');
INSERT INTO "apaguna" VALUES(37,48,'37:48','Isaan bira dubartoota ija isaanii gabaabsan, kan ijji babaldhaa ta’etu jira');
INSERT INTO "apaguna" VALUES(37,49,'37:49','Isaan (qulqullina keessatti) akka hanqaaquu eegamaati.');
INSERT INTO "apaguna" VALUES(37,50,'37:50','Gariin isaanii garii irratti, gara galaniiti wal gaafatu.');
INSERT INTO "apaguna" VALUES(37,51,'37:51','Jedhaan isaan irraa ta’e ni jedha: “ani (addunyaa irratti) hiriyaan qaba ture.');
INSERT INTO "apaguna" VALUES(37,52,'37:52','Kan “sila ati warra (du’a booda kaafamni akka jiru) amanan irraayii?” jedhu');
INSERT INTO "apaguna" VALUES(37,53,'37:53','“Sila erga duunee biyyeefi lafee (nyaatame) taanee booda kaafamnee mindeeffamnaa?”');
INSERT INTO "apaguna" VALUES(37,54,'37:54','Ni jedhe: “Sila isin (ana waliin deemtanii ) gadi hin ilaaltanii?”');
INSERT INTO "apaguna" VALUES(37,55,'37:55','Gadi millatee walakkaa jahiimitti isa arge');
INSERT INTO "apaguna" VALUES(37,56,'37:56','Ni jedhe: “Rabbiin kakadhee! Na balleessitee oolte!');
INSERT INTO "apaguna" VALUES(37,57,'37:57','Odoo tolli Gooftaa kiyyaa na tiksuu baatee, silaa anis warra (ibidda) seenan irraa ta’een ture.”');
INSERT INTO "apaguna" VALUES(37,58,'37:58','(Warri jannataa ni jedhu): “sila nuti lamuu wahii duunaa?');
INSERT INTO "apaguna" VALUES(37,59,'37:59','Du’a keenya kan jalqabaa malee; nuti adabamoos miti.”');
INSERT INTO "apaguna" VALUES(37,60,'37:60','Kun isumatu milkii guddaadha');
INSERT INTO "apaguna" VALUES(37,61,'37:61','Fakkaataa kanaatiif hojjattoonni haa hojjatanu.');
INSERT INTO "apaguna" VALUES(37,62,'37:62','Qubannaaf sanatu caala moo, (muka hadhaawaa) zaqquumtu caala?');
INSERT INTO "apaguna" VALUES(37,63,'37:63','Dhugumatti Nuti miidhaa raawwattootaaf mokkoroo ishee taasifne.');
INSERT INTO "apaguna" VALUES(37,64,'37:64','Isheen muka hundee ibidda jahannam keessaan baatuudha.');
INSERT INTO "apaguna" VALUES(37,65,'37:65','Firiin ishee akka mataa sheyxaanotaati');
INSERT INTO "apaguna" VALUES(37,66,'37:66','Isaan ishee irraa nyaatanii garaa isaanii ishee irraa guuttatu.');
INSERT INTO "apaguna" VALUES(37,67,'37:67','Sana booda isaaniif (dhugaatii) bishaan danfaa irraa makametu jira.');
INSERT INTO "apaguna" VALUES(37,68,'37:68','Sana booda deebiin isaanii gara ibidda jahannam ta’a.');
INSERT INTO "apaguna" VALUES(37,69,'37:69','Dhugumatti, isaan abbootii isaanii jallattoota arganiiti,');
INSERT INTO "apaguna" VALUES(37,70,'37:70','Isaan faana isaanii irra deemu.');
INSERT INTO "apaguna" VALUES(37,71,'37:71','Isaan duras irra baay’een ummattoota durii jallatanii jiru.');
INSERT INTO "apaguna" VALUES(37,72,'37:72','Dhugumatti isaan keessatti dinniintota erginee jirra');
INSERT INTO "apaguna" VALUES(37,73,'37:73','Booddeen warra dinniinamee akkam akka ta’e ilaali.');
INSERT INTO "apaguna" VALUES(37,74,'37:74','Gabroota Rabbii qulqulleeffamoo malee');
INSERT INTO "apaguna" VALUES(37,75,'37:75','Dhugumatti Nuuh nutti lallabee ture; Nuti warri waamicha owwaate waa tolle!');
INSERT INTO "apaguna" VALUES(37,76,'37:76','Badii guddaa (bishaanin liqimfamuu) irraa isaafi maatii isaa baraarsine');
INSERT INTO "apaguna" VALUES(37,77,'37:77','Hortee isaa isaanuma (warra badii jalaa) hafan taasifne.');
INSERT INTO "apaguna" VALUES(37,78,'37:78','Isaaf dhaloota boodaa keessatti (yaadannoo gaarii) dhiifne.');
INSERT INTO "apaguna" VALUES(37,79,'37:79','Nageenyi aalama keessatti Nuuh irra haa jiraatu.');
INSERT INTO "apaguna" VALUES(37,80,'37:80','Nuti akka kanatti toltuu hojjattoota mindeessina.');
INSERT INTO "apaguna" VALUES(37,81,'37:81','Inni gabroota keenya mu’uminootaa irraayi.');
INSERT INTO "apaguna" VALUES(37,82,'37:82','Sana booda warra kaan ni dhidhimsine');
INSERT INTO "apaguna" VALUES(37,83,'37:83','Ibraahiimis hordoftoota (karaa) isaa ( Nuuh) irraayi');
INSERT INTO "apaguna" VALUES(37,84,'37:84','Yeroo Rabbii isaa bira onnee qulqulluun dhufe (dubbadhu).');
INSERT INTO "apaguna" VALUES(37,85,'37:85','Yeroo abbaa isaatiifi ummata isaatiin “maal gabbartu?” jedhe.');
INSERT INTO "apaguna" VALUES(37,86,'37:86','“Sila kijibaani, gooftoota Allaahn ala jiran godhachuu feetanii?');
INSERT INTO "apaguna" VALUES(37,87,'37:87','Rabbii aalama hundaa irratti yaanni keessan maali?”');
INSERT INTO "apaguna" VALUES(37,88,'37:88','Achumaan urjiiwwan keessa ilaalcha tokko ilaale.');
INSERT INTO "apaguna" VALUES(37,89,'37:89','Ergasii “ani dhukkubsataadha” jedhe.');
INSERT INTO "apaguna" VALUES(37,90,'37:90','Isa irraa gara galanii deeman.');
INSERT INTO "apaguna" VALUES(37,91,'37:91','Ergasii inni gara gabbaramtoota isaanii maqee “sila hin nyaattanuu?” jedheen.');
INSERT INTO "apaguna" VALUES(37,92,'37:92','Maal taatanii hin dubbanne?');
INSERT INTO "apaguna" VALUES(37,93,'37:93','Ergasii isaan irratti mirgaan rukuttaa irratti raawwate.');
INSERT INTO "apaguna" VALUES(37,94,'37:94','(Mushrikoonni) saffisaan gara isaa gara galan.');
INSERT INTO "apaguna" VALUES(37,95,'37:95','(Ibraahiim) ni jedhe: “sila waanuma (ofii keessanii) boccan gabbartuu?');
INSERT INTO "apaguna" VALUES(37,96,'37:96','Odoo Allaahn isiniifi waan isin dalagdanis uume jiruu.”');
INSERT INTO "apaguna" VALUES(37,97,'37:97','(Mushrikoonni) ni jedhan: “jaarmaya isaaf ijaaraa; ergasii ibidda boba’aa keessatti isa darbaa.”');
INSERT INTO "apaguna" VALUES(37,98,'37:98','Isaan tooftaa isatti baasuu fedhanii Nuti immoo warra jalaa (moo’amoo) isaan taasifne.');
INSERT INTO "apaguna" VALUES(37,99,'37:99','Innis ni jedhe: “Ani gara Rabbii kiyyaa deemaadha; Inni na qajeelcha.');
INSERT INTO "apaguna" VALUES(37,100,'37:100','(Yaa) Rabbii (kiyya)! (Ilmaan) gaggaarii naaf kenni.”');
INSERT INTO "apaguna" VALUES(37,101,'37:101','Ilma obsaadhaan isa gammachiifne');
INSERT INTO "apaguna" VALUES(37,102,'37:102','Yeroma isa waliin deemuu irra ga’u ni jedhe: “yaa ilma kiyya! Ani abjuu keessatti kan si gorra’un argeWaan siif mul’atu ilaali.” (Ilmis) ni jedhe: “Yaa abbaa kiyya! Waan ajajamte dalagiYoo Rabbiin fedhe obsitoota irraa ta’uu na argita.”');
INSERT INTO "apaguna" VALUES(37,103,'37:103','Yeroo isaan lamaanuu (fedha Rabbiitiif) harka kennan, (abbaan ilma isaa kana) adda isaa irratti ciibse.');
INSERT INTO "apaguna" VALUES(37,104,'37:104','Nutis “Yaa Ibraahiim!” jechuun itti lallabne');
INSERT INTO "apaguna" VALUES(37,105,'37:105','“Abjuu (kee) dhugoomsiteettaNuti akka kanatti toltuu hojjattoota mindeessina.”');
INSERT INTO "apaguna" VALUES(37,106,'37:106','Dhugumatti, kun mokkoroo ifa galaadha.');
INSERT INTO "apaguna" VALUES(37,107,'37:107','Gorra’amaa guddaadhaan isa furre.');
INSERT INTO "apaguna" VALUES(37,108,'37:108','Isaaf (yaadannoo gaarii) warra boodaa keessatti dhiifne.');
INSERT INTO "apaguna" VALUES(37,109,'37:109','Nageenyi Ibraahiim irra haa jiraatu');
INSERT INTO "apaguna" VALUES(37,110,'37:110','Akka kanatti toltuu hojjattoota mindaa isaaniif kennina');
INSERT INTO "apaguna" VALUES(37,111,'37:111','Inni gabroota keenya mu’uminootaa irraayi');
INSERT INTO "apaguna" VALUES(37,112,'37:112','Is-haaqiin, nabiyyii warra gaggaarii irraa ta’een isa gammachiifne.');
INSERT INTO "apaguna" VALUES(37,113,'37:113','Isa irrattiifi Is-haaq irrattis barakaa gooneSanyii isaan lamaanii irraa nama toltuu hojjatuufi kan lubbuu isaa miidhaa ifa ta’etu jira.');
INSERT INTO "apaguna" VALUES(37,114,'37:114','Muusaafi Haaruun irrattis dhugumatti tola oollee jirra');
INSERT INTO "apaguna" VALUES(37,115,'37:115','Isaan lamaaniifi ummata isaanis rakkina guddaa irraa baraarsinee jirra.');
INSERT INTO "apaguna" VALUES(37,116,'37:116','Isaan tumsinee, isaanumatu injifattoota ta’ani');
INSERT INTO "apaguna" VALUES(37,117,'37:117','Kitaaba ifa ta’e isaaniif kennine.');
INSERT INTO "apaguna" VALUES(37,118,'37:118','Karaa qajeelaa isaan qajeelchine.');
INSERT INTO "apaguna" VALUES(37,119,'37:119','Isaan lamaaniif dhaloota boodaa keessatti (yaadannoo gaarii) dhiifnee jirra.');
INSERT INTO "apaguna" VALUES(37,120,'37:120','Nageenyi Muusaafi Haaruun irra haa jiraatu.');
INSERT INTO "apaguna" VALUES(37,121,'37:121','Nuti akka kanatti toltuu hojjattoota mindeessina.');
INSERT INTO "apaguna" VALUES(37,122,'37:122','Isaan lamaanuu gabroota keenya mu’uminoota irraayi.');
INSERT INTO "apaguna" VALUES(37,123,'37:123','Dhugumatti, Ilyaasis ergamtoota irraayi');
INSERT INTO "apaguna" VALUES(37,124,'37:124','Yeroo ummata isaatiin jedhu [yaadadhu]:“Rabbiin hin sadaattanuu?');
INSERT INTO "apaguna" VALUES(37,125,'37:125','Sila ‘Ba’al’[] gabbartanii (Gooftaa) warra waa uumu hunda irra caalaa ta’e dhiiftuu?');
INSERT INTO "apaguna" VALUES(37,126,'37:126','Allaah Goftaa keessaniifi Gooftaa abbootii keessan isaan jalqabaa ta’e.”');
INSERT INTO "apaguna" VALUES(37,127,'37:127','Isa sobsiisanKanaafuu isaan dhugumatti (adabaaf) dhiheeffamu.');
INSERT INTO "apaguna" VALUES(37,128,'37:128','Gabroota Rabbii qulqulleeffamoo malee.');
INSERT INTO "apaguna" VALUES(37,129,'37:129','Isaaf dhaloota boodaa keessatti (yaadannoo gaarii) dhiifnee jirra.');
INSERT INTO "apaguna" VALUES(37,130,'37:130','Nageenyi Ilyaas irra haa jiraatu.');
INSERT INTO "apaguna" VALUES(37,131,'37:131','Nuti akka kanatti toltuu hojjattoota mindeessina.');
INSERT INTO "apaguna" VALUES(37,132,'37:132','Inni gabroota keenya mu’uminoota irraayi.');
INSERT INTO "apaguna" VALUES(37,133,'37:133','Luuxiis dhugumatti ergamtoota irraayi.');
INSERT INTO "apaguna" VALUES(37,134,'37:134','Yeroo isaafi maatii isaa hunda (adaba jalaa) baraarsine [yaadadhu].');
INSERT INTO "apaguna" VALUES(37,135,'37:135','Jaartii (niitii isaa) kan warra (adabbiif) hafan keessa taate malee.');
INSERT INTO "apaguna" VALUES(37,136,'37:136','Sana booda kan biraa hunda ni balleessine.');
INSERT INTO "apaguna" VALUES(37,137,'37:137','Isin ganamfatoo taatanii isaan irra dabartu.');
INSERT INTO "apaguna" VALUES(37,138,'37:138','Halkanis (irra qaxxaamurtu); sila sammuu hin qabdanii?');
INSERT INTO "apaguna" VALUES(37,139,'37:139','Dhugumatti, (nabi) Yuunusis ergamtoota irraayi.');
INSERT INTO "apaguna" VALUES(37,140,'37:140','Yeroo gara doonii (fe’isaan) guutamee sokke [yaadadhu].');
INSERT INTO "apaguna" VALUES(37,141,'37:141','(Namoota doonii waliin) carraa kaasee, warra carraan itti ba’e irraa ta’e.');
INSERT INTO "apaguna" VALUES(37,142,'37:142','Haala komatamaa ta’een qurxummiin isa liqimse.');
INSERT INTO "apaguna" VALUES(37,143,'37:143','Odoo inni warra Rabbiin qulqulleessan irraa ta’uu baatee;');
INSERT INTO "apaguna" VALUES(37,144,'37:144','Silaa hanga guyyaa kaafamaniitti garaa isaa (qurxumichaa) keessa jiraata ture.');
INSERT INTO "apaguna" VALUES(37,145,'37:145','Isaa dhukkubsataa lafa dirreetti isa darbine.');
INSERT INTO "apaguna" VALUES(37,146,'37:146','Muka buqqee irraa ta’e isa irratti biqilchine.');
INSERT INTO "apaguna" VALUES(37,147,'37:147','Gara (namoota) kuma dhibbaa yookiin sanarra dabalaniitti isa ergine.');
INSERT INTO "apaguna" VALUES(37,148,'37:148','Achumaan amannaan hanga yeroo murtaa’eetti isaan qanansiifne');
INSERT INTO "apaguna" VALUES(37,149,'37:149','(Yaa Muhammad!) isaan gaafadhu: “Sila Gooftaa keetiif ilmaan dubaraa ta’aniiti, isaaniif immoo ilmaan dhiiraa ta’anii?”');
INSERT INTO "apaguna" VALUES(37,150,'37:150','Moo isaanii argani malaykota dubara uumnee?');
INSERT INTO "apaguna" VALUES(37,151,'37:151','Dhagaayaa! Isaan soba isaanii irraa ni jedhu.');
INSERT INTO "apaguna" VALUES(37,152,'37:152','“Rabbiin ni dhale” (jedhu) Dhugumatti, isaan kijiboodha.');
INSERT INTO "apaguna" VALUES(37,153,'37:153','Sila Inni ilmaan dubaraati ilmaan dhiiraa irra filatee?');
INSERT INTO "apaguna" VALUES(37,154,'37:154','Maal taatan? Akkamitti murteessitan?');
INSERT INTO "apaguna" VALUES(37,155,'37:155','Sila hin gorfamtanuu?');
INSERT INTO "apaguna" VALUES(37,156,'37:156','Moo ragaa ifa ta’etu isiniif jira?');
INSERT INTO "apaguna" VALUES(37,157,'37:157','Yoo kan dhugaa jettan taataniif kitaaba keessaniin koottaa');
INSERT INTO "apaguna" VALUES(37,158,'37:158','Gidduu Isaatiifi gidduu malaykotaatti firummaa godhanMalaykoonni dhugumatti akka isaan (kun) dhiaatuuf jiran sirritti beekanii jiru');
INSERT INTO "apaguna" VALUES(37,159,'37:159','Waan isaan (kaafirtoonni), isaan jedhan irraa Rabbiin qulqullaa’e.');
INSERT INTO "apaguna" VALUES(37,160,'37:160','Gabroota Rabbii isaan qulqulleeffamoo malee (ni dhihaatu).');
INSERT INTO "apaguna" VALUES(37,161,'37:161','Isiniifi wantoonni isin gabbartan.');
INSERT INTO "apaguna" VALUES(37,162,'37:162','Isin isa irratti (nama amane kamillee) jallistootaa miti.');
INSERT INTO "apaguna" VALUES(37,163,'37:163','Namoota ibidda jahannam seenun (itti murtaa’e) malee');
INSERT INTO "apaguna" VALUES(37,164,'37:164','Nurraa (maleykaan) tokkollee iddoon beekamaan isaaf ta’u malee hin hafu');
INSERT INTO "apaguna" VALUES(37,165,'37:165','.Nuti (maleykonni), dhugumatti, hiriira galoodha.');
INSERT INTO "apaguna" VALUES(37,166,'37:166','Nuti dhugumatti Rabbiin qulqullessoodha.');
INSERT INTO "apaguna" VALUES(37,167,'37:167','Isaan (mushrikoonni Makkaa) ni jedhu:');
INSERT INTO "apaguna" VALUES(37,168,'37:168','“Odoo yaadannoo warra durii keessaa tokko nu bira jiraatee,');
INSERT INTO "apaguna" VALUES(37,169,'37:169','Silaa dhugumatti gabroota Rabbii qulqulleeffamoo taana turre.”');
INSERT INTO "apaguna" VALUES(37,170,'37:170','(Qur’aanni itti dhufnaan) isatti kafaranFuula dura beekuuf jiraatu.');
INSERT INTO "apaguna" VALUES(37,171,'37:171','Dhugumatti, jechi keenya gabroota keenya ergamtootaaf dabreera.');
INSERT INTO "apaguna" VALUES(37,172,'37:172','Isaan isaanumatu tumsamoodha.');
INSERT INTO "apaguna" VALUES(37,173,'37:173','Waraanni keenyas injifattoonni isaanuma (jechuunis dabarte)');
INSERT INTO "apaguna" VALUES(37,174,'37:174','(Yaa Muhammad!) Yeroo murtaa’eef isaan dhiisii irraa garagali.');
INSERT INTO "apaguna" VALUES(37,175,'37:175','Ati itti eegi, fuula dura (waan isaaniif qophaa’e) ni argu.');
INSERT INTO "apaguna" VALUES(37,176,'37:176','Sila adabbii keenya jarjarfatanii?');
INSERT INTO "apaguna" VALUES(37,177,'37:177','Yeroo (adabbiin) qe’ee isaaniitti bu’e, bariisaan warra dinniinamanii fokkateera.');
INSERT INTO "apaguna" VALUES(37,178,'37:178','(Yaa Muhammad!) hanga yeroo murtaa’eetti isaan irraa garagali.');
INSERT INTO "apaguna" VALUES(37,179,'37:179','Itti eegi, fuula dura (waan isaaniif qophaa’e) ni argu.');
INSERT INTO "apaguna" VALUES(37,180,'37:180','Goftaan kee, Gooftaan injifannaa, waan isaan jedhaniin irraa qulqullaa’e.');
INSERT INTO "apaguna" VALUES(37,181,'37:181','Nageenyis ergamtoota irra haa jiraatu');
INSERT INTO "apaguna" VALUES(37,182,'37:182','Faaruun hundi kan Allaah, Gooftaa aalama hundaati.');
INSERT INTO "apaguna" VALUES(38,1,'38:1','SaadQur’aana abbaa yaadannoo ta’eenin (kakadha)!');
INSERT INTO "apaguna" VALUES(38,2,'38:2','Dhugumatti isaan kafaran of tuulummaafi mormii keessa jiru.');
INSERT INTO "apaguna" VALUES(38,3,'38:3','Isaan dura (dhaloota) jaarraa meeqaatama balleessinee jirraYeroo baqaaf hin taane walitti lallaban');
INSERT INTO "apaguna" VALUES(38,4,'38:4','Sodaachisaan isaan irraa ta’e isaanitti dhufuun isaan dinqisiisee, ni jedhan: “kun falfalaa, sobaadha.');
INSERT INTO "apaguna" VALUES(38,5,'38:5','Sila gabbaramtoota (hunda) gabbaramaa tokko qofa taasisee? Dhugumatti, kun waan dinqisiisaadha.”');
INSERT INTO "apaguna" VALUES(38,6,'38:6','Gurguddoon isaan (mushrikoota) irraa ta’an deemaniiti “gooftolee keessan (gabbaruu) irratti obsaa; dhugumatti, inni kun wanta nurraa barbaadamuudha.');
INSERT INTO "apaguna" VALUES(38,7,'38:7','Amantii gara dhumaa (kiristaanummaa) keessatti kana hin dhageenyeKun waanuma haara’a uumame malee homaa miti.');
INSERT INTO "apaguna" VALUES(38,8,'38:8','Sila yaadannoon (Qur’aanni) kun nu hunda gidduu isa qofarratti buufamee?” Dhugumatti, isaan yaadannoo kiyya irraa mamii keessa jiruGaruu hanga ammaatti adabbii (kiyya) hin dhandhamnee (kanaaf kafaran).');
INSERT INTO "apaguna" VALUES(38,9,'38:9','Furtuuwwan rahmata Rabbii keetii kan injifataa, arjoomaa ta’ee isaan bira jiraa?');
INSERT INTO "apaguna" VALUES(38,10,'38:10','Moo mootummaa samii, dachiifi wantoota gidduu isaanii jirantu kan isaaniitii? Oggas, karaa keessaan (gara samii) ol haa ba’anii.');
INSERT INTO "apaguna" VALUES(38,11,'38:11','Isaan bakka sana (dirree badrii) keessa jiran humna waraana xiqqoo(dha)Akkuma warri gamtaa durii injifataman saniiti (injifatamuuf jiru)');
INSERT INTO "apaguna" VALUES(38,12,'38:12','Isaaniin dura ummanni Nuuh, Aadiifi Fir’awni abbaa shikaalaa kan ta’an (ergamaa isaanii) sobsiisaniiru.');
INSERT INTO "apaguna" VALUES(38,13,'38:13','Samuud, ummanni Luuxiifi warri bosonaas (sobsiisanii jiru)Isaan hunduu gareewwan (dhugaa irratti duulanii dha)');
INSERT INTO "apaguna" VALUES(38,14,'38:14','Hundi isaanii ergamtoota sobsiisan malee hin hafneKanaafuu adabbiin (kiyya) isaan irratti mirkanaa’e.');
INSERT INTO "apaguna" VALUES(38,15,'38:15','Isaan kun iyyansa tokko kan deebi’uun isheef hin jirre malee homaa hin eegan.');
INSERT INTO "apaguna" VALUES(38,16,'38:16','Ni jedhan: “Yaa Rabbii keenya! (Adaba irraa) qooda keenya guyyaa qormaataa dura nuuf arifachiisi.”');
INSERT INTO "apaguna" VALUES(38,17,'38:17','Waan isaan jedhan irratti obsiGabricha keenya Daawuudiin abbaa jabeenyaa kan ta’e yaadadhuDhugumatti, inni akkaan tawbataa ture.');
INSERT INTO "apaguna" VALUES(38,18,'38:18','Nuti gaarreen isa waliin galgalaafi ganama kan Rabbiin faarsan ta’anii laaffifnee jirra.');
INSERT INTO "apaguna" VALUES(38,19,'38:19','Allaattiinis walitti qabamtuu taatee (isa waliin akka faarsan mijeessinee jirra)Hunduu gara Isaa deebi’oodha.');
INSERT INTO "apaguna" VALUES(38,20,'38:20','Mootummaa isaa jabeessinee jirraHikmaa (nabiyyummaa)fi haasa’a adda baasuus isaaf kennineerra.');
INSERT INTO "apaguna" VALUES(38,21,'38:21','Sila oduun wal falmitootaa kan yeroo dadaa koranii gamoo (isaa) seenan si dhaqqabeeraa?');
INSERT INTO "apaguna" VALUES(38,22,'38:22','Yeroo isaan isa irra seenanu, isaan irraa rifate“Hin sodaatin; (nuti) wal falmitoota lama kan gariin keenya garii irratti daangaa dabreedhaKanaaf gidduu keenyatti dhugaan murteessiHin jallisinii, gara karaa sirrii nu qajeelchi” jedhan.');
INSERT INTO "apaguna" VALUES(38,23,'38:23','Dhugumatti, kun obboleessa kiyyaIsaaf hoolota dhaltuu sagaltamii sagaltu jiraAnaaf immoo hoolaa dhaltuu tokko qofatu jira“Ishee naaf kenni” jedheeti, kadhaa keessatti natti jabeesse.');
INSERT INTO "apaguna" VALUES(38,24,'38:24','(Daawuud) ni jedhe: “dhugumatti, hoolaa kee gara hoolota isaatti dabalachuuf si gaafachuudhaan miidhaa sirratti dalagee jiraDhugumatti, irra hedduun warra wajjin hojjatanii gariin isaanii garii irratti miidhaa dalagu; isaan amananii, toltuu dalagan maleeIsaanis baay’ee xiqqoodha.” Daawuud akka isa qoranne beekeeti, Rabbii isaa araarama kadhatee, rukuu’as bu’ee, (gara Gooftaa isaatti) deebi’e.');
INSERT INTO "apaguna" VALUES(38,25,'38:25','(Nutis) sana isaaf araaramneDhugumatti, isaaf Nu biratti dhihaannaafi iddoo deebii (jannata) gaarii ta’etu jira.');
INSERT INTO "apaguna" VALUES(38,26,'38:26','Yaa Daawuud! Nuti dachii keessatti bakka bu’aa si taasifnee jirraKanaaf gidduu namootaatti dhugaan murteessiKaraa Rabbii irraa si jallisaa fedha lubbuu hin hordofinDhugumatti, isaan karaa Rabbii irraa jallatan, waan guyyaa herregaa dagataniif isaaniif adabbii cimaa ta’etu jira');
INSERT INTO "apaguna" VALUES(38,27,'38:27','Samii, dachiifi wantoota gidduu lamaan isaanii jiran hunda, taphaaf hin uumneSun hubannoo isaan kafaraniitiIsaan kafaraniif ibidda irraa laga hamaa ta’etu isaaniif jira.');
INSERT INTO "apaguna" VALUES(38,28,'38:28','Sila isaan amananii, toltuu dalagan akka warra dachii keessatti balleessa dalaganii ni goonaa? Warra Allaah sodaatanis akka warra yakkamoo ni taasifnaa?');
INSERT INTO "apaguna" VALUES(38,29,'38:29','(Kun) kitaaba barakaa, akka isaan keeyyattoota isaa xiinxalaniifi akka warri sammuu qaban ittiin gorfamuuf jecha gara keetti isa buufneedha.');
INSERT INTO "apaguna" VALUES(38,30,'38:30','Daawuudiifis Suleeymaaniin kennineGabricha akkam tole! Dhugumatti, inni akkaan tawbataadha.');
INSERT INTO "apaguna" VALUES(38,31,'38:31','Waaree booda faradoon leenjifamoon, fiigichatti cimoo ta’an yeroo isa irra fidaman (dubbadhu).');
INSERT INTO "apaguna" VALUES(38,32,'38:32','Ni jedhe: “ani hanga aduun (dukkana halkaniitiin) dhokattutti jaalala qabeenyaa (fardaa) yaadannoo Gooftaa kiyyaa caalaa filadhe.');
INSERT INTO "apaguna" VALUES(38,33,'38:33','Isaan kana gara kiyya deebisaa” (jedhee), battalumatti lukaafi morma isaanii mummuruutti seene.');
INSERT INTO "apaguna" VALUES(38,34,'38:34','Dhugumatti Suleeymaaniin mokkorree turreSiree isaa irratti qaama laafaa darbineSana booda ( tawbaa) deebi’e.');
INSERT INTO "apaguna" VALUES(38,35,'38:35','Ni jedhe: “Gooftaa kiyya! Naaf araarami; aangoo ana booda eenyuufillee hin malles naaf kenniDhugumatti, Ati situ akkaan kennaadha.”');
INSERT INTO "apaguna" VALUES(38,36,'38:36','Qilleensi isaaf laaffifnee, ajaja isaatiin laaftuu taatee gara inni fedhetti deemti.');
INSERT INTO "apaguna" VALUES(38,37,'38:37','Shayxaanotas isaan akkaan ijaaraniifi bishaan keessa akkaan lixan hunda (isaaf laaffifne).');
INSERT INTO "apaguna" VALUES(38,38,'38:38','Warri biroos kan diriin walitti hidhaman (isaaf laaffifne).');
INSERT INTO "apaguna" VALUES(38,39,'38:39','“Kun kennaa keenyaa, kenni! yookiin dhorgadhu! herregni homaatuu sirra hin jiru” (janneen).');
INSERT INTO "apaguna" VALUES(38,40,'38:40','Dhugumatti, isaafis Nu bira dhihaannaafi deebii gaarii ta’etu jira.');
INSERT INTO "apaguna" VALUES(38,41,'38:41','Gabricha keenya Ayyuubiinis yeroo inni Gooftaa isaatti lallabee “shayxaanni rakkinaafi adabaan na tuqee jira” (jedhe) dubbadhu');
INSERT INTO "apaguna" VALUES(38,42,'38:42','(Rabbiin): “Dachii miila keetiin rukutiKun (bishaan) qabbanaa’aa dhiqataafi dhugaatiif oolu” (jedheen).');
INSERT INTO "apaguna" VALUES(38,43,'38:43','Rahmata Nu biraa ta’eefi gorsa warra sammuu qabaniif jecha maatii isaa (deebifnee) isaaf kenninee, fakkaataa isaaniis (isaaf daballe).');
INSERT INTO "apaguna" VALUES(38,44,'38:44','“Harka keetiinis harcummee qabii (niitii kee) isaan rukuti; Waadaa (kee) hin cabsin” (jenneen)Dhugumatti, (isa mokkorreeti) obsaa ta’uu isa agarreGabricha akkam gaarii ta’e! Dhugumatti, inni akkaan tawbaa deebi’aadha.');
INSERT INTO "apaguna" VALUES(38,45,'38:45','Gabroota keenya Ibraahiim, Is-haaqiifi Ya’aquubiin kanneen (Ibaadaa irratti) cimoofi ija xiinxaltu qaban san dubbadhu.');
INSERT INTO "apaguna" VALUES(38,46,'38:46','Dhugumatti, Nuti haala gaariidhaan isaan qulqulleessine(Isheenis) ganda (Aakhiraa) yaadachuudha.');
INSERT INTO "apaguna" VALUES(38,47,'38:47','Dhugumatti isaan Nu biratti filatamoo gaggaarii irraayi.');
INSERT INTO "apaguna" VALUES(38,48,'38:48','Ismaa’il, Yasa’iifi Zulkifliinis dubbadhu; (isaan) hunduu filatamtoota irraayi.');
INSERT INTO "apaguna" VALUES(38,49,'38:49','Kuni (isaaniif) faaruudhaDhugumatti, isaan Rabbiin sodaataniif deebii gaariitu jira');
INSERT INTO "apaguna" VALUES(38,50,'38:50','Jannata qubsumaa kan balballi isaaniif banaa ta’antu jira.');
INSERT INTO "apaguna" VALUES(38,51,'38:51','Ishee keessa (siree irratti) hirkatanii jiraatuIshee keessatti fuduraalee baay’eefi dhugaatiis ni waammatu.');
INSERT INTO "apaguna" VALUES(38,52,'38:52','Dubartoota nama biraa ilaaluu irraa ija gabaabsan, kan umriin isaanii wal qixa ta’etu isaan bira jira.');
INSERT INTO "apaguna" VALUES(38,53,'38:53','Kun waan guyyaa herregaatiif beellama godhamtaniidha.');
INSERT INTO "apaguna" VALUES(38,54,'38:54','Dhugumatti, kun kennaa keenyaa, dhumti isaaf hin jiru.');
INSERT INTO "apaguna" VALUES(38,55,'38:55','Kun (kan mu’uminootaati)Daangaa dabartootaaf immoo deebii hamaa ta’etu jira.');
INSERT INTO "apaguna" VALUES(38,56,'38:56','Jahannama ishee seenantu jiraAfatichi waa fokkate!');
INSERT INTO "apaguna" VALUES(38,57,'38:57','Kuni bishaan danfaafi malaadhaa, haa dhandhamanu.');
INSERT INTO "apaguna" VALUES(38,58,'38:58','[Adabbiin] biraa fakkaataa isaa [kan duraa] ta’e gosa baay’eetu jira.');
INSERT INTO "apaguna" VALUES(38,59,'38:59','Kuni garee isinii waliin ibidda seenaniidha” (maleykaan jettiin)“Simannaan gaariin isaaniif hin ta’in, isaanis (numaa waliin) ibidda seenoodha” (matootiin jedhan).');
INSERT INTO "apaguna" VALUES(38,60,'38:60','(Hordoftoonnis) ni jedhu: “Isinis simannaan gaariin isiniif hin godhaminIsintu karaa kan nuuf godhee (isin hordofne).” Iddoon teessumaa waa fokkatee!');
INSERT INTO "apaguna" VALUES(38,61,'38:61','Ni jedhu: “Gooftaa keenya! nama kana karaa nuuf godhe ibidda keessatti adabbii dachaa ta’e isaaf dabali.”');
INSERT INTO "apaguna" VALUES(38,62,'38:62','(Matootiin warra ibiddaa) Ni jedhu: “Maaliif namoota (duniyatti) warra baay’ee badii hojjatan irraa lakkaa’aa turre (asitti) hin argine?');
INSERT INTO "apaguna" VALUES(38,63,'38:63','(Nutu) qishnaa isaan godhate moo, ijatu isaan irraa nu dabe?”');
INSERT INTO "apaguna" VALUES(38,64,'38:64','Dhugumatti, sun dhugaadha; (inni) wal mormii warra ibiddaati.');
INSERT INTO "apaguna" VALUES(38,65,'38:65','“Ani akeekkachiisaadha; Rabbii tokkicha injifataa ta’e malee dhugaan gabbaramaan hin jiru” jedhi.');
INSERT INTO "apaguna" VALUES(38,66,'38:66','Gooftaa samii, dachiifi waan gidduu isaan lamaanii jiruu, injifataa, araaramaa ta’e malee.');
INSERT INTO "apaguna" VALUES(38,67,'38:67','Jedhi: “inni (Qur’aanni ) oduu guddaadha.');
INSERT INTO "apaguna" VALUES(38,68,'38:68','Isin isa irraa garagaltani.');
INSERT INTO "apaguna" VALUES(38,69,'38:69','Yeroo isaan (waa’ee Aadam keessatti) falman ani waa’ee jiraattoota olii (maleykotaa) irraa beekumsa hin qabu.');
INSERT INTO "apaguna" VALUES(38,70,'38:70','Ani sodaachisaa ifa galaa ta’uu malee beeksifni (biraa) gara kiyya hin godhamu.”');
INSERT INTO "apaguna" VALUES(38,71,'38:71','Yeroo Gooftaan kee malaykotaan “Ani nama dhoqqee irraa uumuufan jira” jedhe [yaadadhu].');
INSERT INTO "apaguna" VALUES(38,72,'38:72','“Ergan isa sirreessee, ruuhii kiyya (kan Ani uume) irraa isa keessatti afuufee booda isaaf sujuuda bu’aa” (jedheen).');
INSERT INTO "apaguna" VALUES(38,73,'38:73','Malaykonni hundi isaanii sujuuda bu’an.');
INSERT INTO "apaguna" VALUES(38,74,'38:74','Garuu Ibliis boonee, kaafiroota irraa ta’e');
INSERT INTO "apaguna" VALUES(38,75,'38:75','(Rabbiin) ni jedhe: “Yaa Ibliis! Waan Ani harka kiyyaan uumeef sujuuduu maaltu si dhorge? Ni boonte moo, of tuultota irraa taate?”');
INSERT INTO "apaguna" VALUES(38,76,'38:76','(Ibliisis) ni jedhe: “Anatu isarra caala; ana ibidda irraa na uumte, isa immoo suphee (horofa) irraa isa uumte.”');
INSERT INTO "apaguna" VALUES(38,77,'38:77','(Rabbiin) ni jedhe: “ishee irraa ba’i; ati dhugumaan (rahmata irraa) fageeffamaadha.');
INSERT INTO "apaguna" VALUES(38,78,'38:78','Hanga guyyaa murtiitti abaarsi kiyya sirra haa jiraatu.”');
INSERT INTO "apaguna" VALUES(38,79,'38:79','(Ibliisis) ni jedhe: “hanga guyyaa (du’a irraa) kaafamaniitti na tursiisi.”');
INSERT INTO "apaguna" VALUES(38,80,'38:80','(Rabbiinis) “dhugumatti, ati warra tursiifaman irraayi” jedheen.');
INSERT INTO "apaguna" VALUES(38,81,'38:81','“Hanga guyyaa yeroo beekamaatti.”');
INSERT INTO "apaguna" VALUES(38,82,'38:82','(Ibliisis) ni jedhe: “jabeenya keetiin kakadhee! Isaan hunda nan jallisa.');
INSERT INTO "apaguna" VALUES(38,83,'38:83','Gabroota kee irraa isaan qulqulleefamoo malee.”');
INSERT INTO "apaguna" VALUES(38,84,'38:84','(Rabbiin) ni jedhe: “Egaa dhugaan (Ana irraayi), Ani dhugumaan jedhas.');
INSERT INTO "apaguna" VALUES(38,85,'38:85','Jahannamiin siifi isaan irraa namoota si hordofan hundaaniin guuta.”');
INSERT INTO "apaguna" VALUES(38,86,'38:86','(Yaa Muhammad!) jedhi: “(Qur’aana) kana irratti mindaa isin hin gaafadhuAni warra of dirqan irraayis miti.');
INSERT INTO "apaguna" VALUES(38,87,'38:87','Inni gorsa aalama hundaati malee homaa miti.');
INSERT INTO "apaguna" VALUES(38,88,'38:88','Dhugumatti, oduu isaa yeroo booda beekuuf jirtu.”');
INSERT INTO "apaguna" VALUES(39,1,'39:1','Buufamuun kitaabichaa Rabbii injifataa, ogessa ta’e irraayi.');
INSERT INTO "apaguna" VALUES(39,2,'39:2','Nuti dhugaan kitaaba gara keetti buufneerraKanaaf amantii Isaaf qulqulleessaa ta’ii Rabbiin gabbari');
INSERT INTO "apaguna" VALUES(39,3,'39:3','Dhaga’aa! Amantiin qulqulluun kan Allaah qofaWarri Isa malee gargaartota godhatan “gara Rabbiitti akka sirritti nu dhiheessaniif malee isaan hin gabbarru” (jedhu)Dhugumatti, Rabbiin waan isaan isa keessatti wal dhabaa turan gidduu isaaniitti ni murteessaDhugumatti, Rabbiin nama kijibaa, akkaan kafaraa ta’e hin qajeelchu.');
INSERT INTO "apaguna" VALUES(39,4,'39:4','Rabbiin odoo ilma godhachuu fedhee, silaa waan ofii uumu gidduu kan fedhe filata tureQulqullina isaaf malu (sana irraa qulqullaa’e)Inni Allaah tokkicha hunda mo’ataadha.');
INSERT INTO "apaguna" VALUES(39,5,'39:5','Samiifi dachii dhugaan uumeHalkan guyyaa irratti mara; guyyaas halkan irratti maraAduufi baatiis ni laaffiseHunduu yeroo murtaa’eef deemuDhaga’aa! Inni injifataa, araaramaadha.');
INSERT INTO "apaguna" VALUES(39,6,'39:6','Lubbuu tokko irraa isin uumee, ergasii ishee irraa cimdii ishee taasisee; beeyladoota irraas cimdiiwwan saddeet isiniif buuse; Garaa haadha keessanii keessatti uumama boodaan uumama godhee dukkana sadi keessatti isin uumaKun Rabbii gabbaramaa keessan ta’eedhaMootummaan kan IsaatiiIsa malee dhugaan gabbaramaan hin jiruAkkamumatti (Isarraa) gara galfamturee?');
INSERT INTO "apaguna" VALUES(39,7,'39:7','Yoo kafartanis, dhugumatti Rabbiin isin irraa dureessaGaruu gabroota Isaatiif kufrii hin jaallatuYoo (Isa) galateeffattan isa isiniif ni jaallata(Lubbuun) dilii hojjattu tokko dilii (nama) biraa hin baadhattuErgasii deebiin keessan gara Gooftaa keessaniitiInnis waan isin dalagaa turtan isinitti odeessaDhugumatti, Inni beekaa waan qoma keessaati.');
INSERT INTO "apaguna" VALUES(39,8,'39:8','Ilmi namaa yoo rakkoon wahii isa tuqe, gara Isaa deebi’uun Gooftaa isaa kadhataErgasii yeroo (Rabbiin) ni’imaa Isa biraa ta’e isaaf kenne, Isa (Rabbii) dura kadhachaa ture irraanfateeti, karaa Isaa irraa (namoota) jallisuuf jecha Rabbiif hiriyaa taasisa“Kufrii keetti xiqqo bashannani; dhugumatti ati warra ibiddaa irraayi” jedhiin.');
INSERT INTO "apaguna" VALUES(39,9,'39:9','Sila namni Rabbiif jecha yeroo halkanii sujuudaafi (salaataaf) dhaabbataa, Aakhiraa sodaataa, rahmata Rabbii isaa kajeelaa (Rabbiif) ajajamu (isa hin amanneen wal qixxaataa?) Jedhi: “sila isaan beekaniifi isaan hin beekne wal qixxaatuu?” Dhugumatti, isaan gorfaman warra sammuu qaban qofa.');
INSERT INTO "apaguna" VALUES(39,10,'39:10','Jedhi: “Yaa gabroota kiyya kan amantan! Gooftaa keessan sodaadhaaIsaan addunyaa kana keessatti toltuu hojjataniif (Aakhiratti) toltuun (qophaa’ee) jiraDachiin Rabbiis bal’oodhaWarri obsan mindaan isaanii kan isaaniif kennamu lakkoofsa maleedha.”');
INSERT INTO "apaguna" VALUES(39,11,'39:11','“Ani amantii Isaaf qulqulleessee, Rabbi gabbaruuttin ajajame” jedhi.');
INSERT INTO "apaguna" VALUES(39,12,'39:12','“Jalqaba Muslimtootaa ta’uttis nin ajajame.”');
INSERT INTO "apaguna" VALUES(39,13,'39:13','“Ani yoon ajaja Gooftaa kiyyaa dide adabbii guyyaa guddaan sodaadha” jedhi.');
INSERT INTO "apaguna" VALUES(39,14,'39:14','Jedhi: “Ani Rabbuma qofaan, amantii kiyya Isaaf qulqulleessaa ta’ee gabbara.”');
INSERT INTO "apaguna" VALUES(39,15,'39:15','Isaan ala waan feetan gabbaraa“Dhugumatti, warri hoonga’an isaan Guyyaa Qiyaamaa ofiifi maatii isaaniis hoonga’aniidhaDhugumatti, kun isumatu hoongoo ifa galaadha.”');
INSERT INTO "apaguna" VALUES(39,16,'39:16','Isaaniif gubbaa isaanii irraa haguuggaa ibidda irraa ta’eetu jira; jala isaanii irrallee haguuggaa (ibiddaatu jira)Sana Rabbiin gabroota Isaa ittiin sodaachisa“Yaa gabroota kiyya! Anuma qofa sodaadhaa.”');
INSERT INTO "apaguna" VALUES(39,17,'39:17','Isaan gabbaramoota sobaa gabbaruu irraa fagaatanii, gara Rabbii deebi’an, isaaniif gammachuutu jiraGabroota kiyya gammachiisi.');
INSERT INTO "apaguna" VALUES(39,18,'39:18','(Isaan) warra jecha dhageeffatanii, ergasii irra bareedaa isaa hordofaniidhaIsaan sun warra Rabbiin isaan qajeelcheIsaan sun isaanumatu warra sammuuti.');
INSERT INTO "apaguna" VALUES(39,19,'39:19','Sila namni jechi adabbii isa irratti mirkanaa’e (nama hamtuu irraa fagaate waliin wal qixaa)? Sila ati nama ibidda keessa jiru ni baraartaa?');
INSERT INTO "apaguna" VALUES(39,20,'39:20','Garuu warra Rabbii isaanii sodaatan, isaaniif kutaalee isaan gubbaa kutaaleen (biraa) ijaaramtuun jirtu, kan isaan jala laggeen dhangalaatutu jiraWaadaa Rabbii (Inni wareege)Rabbiin waadaa Isaa hin diigu.');
INSERT INTO "apaguna" VALUES(39,21,'39:21','Sila Rabbiin bishaan (rooba) samii irraa buusee, ergasii burqaa godhee dachii keessa gadi isa liccisiise hin agarree? Ergasii isaan (bishaaniin) midhaan bifti isaa gara garaa biqilchee, ergasii goggogee daalacha ta’u argitaErgasii caccabaa godhaDhugumatti, kana keessa warra sammuu qabaniif gorsatu jira.');
INSERT INTO "apaguna" VALUES(39,22,'39:22','Sila namni Rabbiin qoma isaa Islaamaaf bal’isee, beekumsa Gooftaa isaa irraa ta’erra jiru (akka nama Islaama’uu dideetii)? Yaa badii namoota onneen isaanii zikrii Rabbii irraa gogdee! Isaan sun jallina ifa ta’e keessa jiru');
INSERT INTO "apaguna" VALUES(39,23,'39:23','Rabbiin haasawa gaarii, kitaaba (miidhagina keessatti) wal fakkaatu, kan oduun isaa lama lamaan deemtu, kan isa irraa gogaan warra Gooftaa isaanii sodaatanii suukannooftu, ergasii gogaan isaaniitiifi onneen isaanii gara yaadannoo Rabbiitti laafu buusee jiraKun qajeelfama Rabbiiti; isaan nama fedhe qajeelchaNama Rabbiin jallise qajeelchaan isaaf hin jiru.');
INSERT INTO "apaguna" VALUES(39,24,'39:24','Sila namni Guyyaa Qiyaamaa fuula isaatiin adabbii hamaa san (qaama) isaa irraa eegu (akka nama jannata seenuutii?) Miidhaa dalagtootaan ni jedhama: “Waan oomishaa turtan dhandhamaa.”');
INSERT INTO "apaguna" VALUES(39,25,'39:25','Warri isaaniin duraa sobsiisaniiti, adabbiin gara isaan hin beekneen isaanitti dhufe.');
INSERT INTO "apaguna" VALUES(39,26,'39:26','Rabbiin jiruu addunyaa keessatti salphina isaan dhandhamsiiseOdoo beekanii adabbiin kan Aakhiraatu irra guddaadha.');
INSERT INTO "apaguna" VALUES(39,27,'39:27','Dhugumatti, Nuti namaaf Qur’aana kana keessatti akka isaan gorfamaniif fakkeenya hunda irraa goone.');
INSERT INTO "apaguna" VALUES(39,28,'39:28','Dubbifamaa afaan Arabaa haala ta’een, kan jallinna tokko hin qabne ta’ee akka Rabbiin sodaataniif jecha (isaanif buufne)');
INSERT INTO "apaguna" VALUES(39,29,'39:29','Rabbiin ‘nama tokko kan shariikkan (isa irratti) wal hin taane waliin qabaniifi kan namni tokko qofti qabu fakkeenya godheIsaan kun lamaan fakkeenyaaf walqixaa? Faaruun hundi kan RabbiitiGaruu irra hedduun isaanii hin beekanu.');
INSERT INTO "apaguna" VALUES(39,30,'39:30','Dhugumatti, (Yaa Muhammad) ati ni duuta; isaanis ni du’u!');
INSERT INTO "apaguna" VALUES(39,31,'39:31','Ergasii isin (hunduu) Guyyaa Qiyaamaa Gooftaa keessan biratti wal mormitu.');
INSERT INTO "apaguna" VALUES(39,32,'39:32','Sila nama Rabbi irratti sobe, kan yeroo dhugaan isatti dhufu sobsiise irra namni miidhaa hojjate ni jiraa? Sila teessoon kaafirootaa jahannam keessa hin taanee?');
INSERT INTO "apaguna" VALUES(39,33,'39:33','Inni dhugaan dhufeefi kan isa dhugoomses, isaan sun isaanuma warra Rabbiin sodaatu.');
INSERT INTO "apaguna" VALUES(39,34,'39:34','Isaaniif waan isaan fedhantu Rabbii isaanii bira jiraSun galata warra toltuu hojjataniiti.');
INSERT INTO "apaguna" VALUES(39,35,'39:35','Akka Rabbiin hamtuu waan dalaganii isaan irraa haqee mindaa isaanii gaarii waan isaan hojjatanii isaanii galata galchuuf (isaaniif laaffise).');
INSERT INTO "apaguna" VALUES(39,36,'39:36','Sila Rabbiin gabricha Isaatiif ga’aa mitii? Warra Rabbii gaditti gabbaraniin si sodaachisuNama Rabbiin jallise qajeelchaan isaaf hin jiru.');
INSERT INTO "apaguna" VALUES(39,37,'39:37','Nama Rabbiin qajeelches wanti isa jallisu hin jiruSila Rabbiin injifataa, haaloo baafataa hin taanee?');
INSERT INTO "apaguna" VALUES(39,38,'39:38','“Samiifi dachii eenyutu uume?” (jettee) odoo isaan gaafattee “Rabbuma” jedhu“Mee naaf odeessaa? Wanti isin Rabbiin ala waammattan (gabbartan) odoo Rabbiin hamtuu naan fedhee, sila isaan (na) rakkisuu isaa san ni deebisuu? Yookiin odoo rahmataan na fedhee, isaan rahmata Isaa qabuu ni danda’uu?” jedhiin: “Rabbumatu naaf ga’a; warri hirkatan Isa irratti hirkatu.”');
INSERT INTO "apaguna" VALUES(39,39,'39:39','“Yaa ummata kiyya! Karaa irra jirtan irratti dalagaa; anis dalagaadhaFuulduratti beekuuf jirtu” jedhiin.');
INSERT INTO "apaguna" VALUES(39,40,'39:40','“Nama adabbiin isa xiqqeessu itti dhufuufi kan adabbiin dhaabbataan isa irratti bu’u eenyu akka ta’e (beekuuf jirtu).”');
INSERT INTO "apaguna" VALUES(39,41,'39:41','Nuti namootaaf kitaaba (Qur’aana) dhugaan sirratti buufneNamni qajeele lubbuma isaatiifiNamni jallates isheedhuma irratti jallataAti isaan irratti dirqama (qajeelchuu) hin qabdu');
INSERT INTO "apaguna" VALUES(39,42,'39:42','Rabbiin lubbuu yeroo du’a isheetti ni ajjeesa; ishee duuti hin ga’inis hirriiba ishee keessatti (ajjeesa)Ergasii ishee du’a itti murteesse ni qabaIshee biraa (kan du’a itti hin murteessin) hanga beellama beekamaatti gadi dhiisaDhugumatti, kana keessa warra xiinxalaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(39,43,'39:43','Sila Rabbii gadittii manguddoota godhatanii? “Odoma isaan homaa hin qabneefi sammuus hin qabne (godhatanii?)” jedhi.');
INSERT INTO "apaguna" VALUES(39,44,'39:44','“Araarri hunduu kanuma RabbiitiMootummaan samiifi dachii kan IsaatiErgasii gara Isaatti deebifamtu” jedhiin');
INSERT INTO "apaguna" VALUES(39,45,'39:45','Yeroo Rabbiin kophaa Isaa faarfamu onneen warra Aakhiratti hin amannee ni sunturrooftiYeroo warri Isaan (Rabbiin) alaa faarfaman yeruma san gammadu.');
INSERT INTO "apaguna" VALUES(39,46,'39:46','Jedhi: “Yaa Rabbii uumaa samiifi dachii! Beekaa fagoofi dhihoo! gidduu gabroota keetiitti waan isaan irratti wal dhabaa turan keessatti sumatu murteessa.”');
INSERT INTO "apaguna" VALUES(39,47,'39:47','Odoo wanti dachii keessa jiru hundi isaan miidhaa dalaganiif ta’ee fakkaataan isaas isaa waliin (isaanif ta’e), adabbii Guyyaa Qiyaamaa hamaa san irraa isaan of baasu turanWaan isaan hin yaadintu Rabbi irraa isaaniif mul’ate.');
INSERT INTO "apaguna" VALUES(39,48,'39:48','Hamtuun waan isaan hojjatanii isaaniif mul’ateWanti isaan itti qishnaa turanis isaanitti marse.');
INSERT INTO "apaguna" VALUES(39,49,'39:49','Namni yeroo rakkoon isa tuqe nu kadhataErgasii yeroo Nuti ni’imaa Nu biraa ta’e isaaf kennine, “beekumsuma kiyyaafiin kenname” jedhaLakkisaa! isheen sun mokkoraGaruu irra hedduun isaanii hin beekanu.');
INSERT INTO "apaguna" VALUES(39,50,'39:50','Warri isaaniin duraas isuma jedhanii jiruWanti isaan hojjachaa turan homaa isaan irraa hin deebifne');
INSERT INTO "apaguna" VALUES(39,51,'39:51','(Mindaa) hamtuu isaan hojjataniitu isaan tuqeKanneen irraa warri miidhaa dalaganis hamtuuwwan waan isaan dalaganii isaan tuquuf jiraIsaan Rabbi duraa hin bahan.');
INSERT INTO "apaguna" VALUES(39,52,'39:52','Sila akka Rabbiin nama fedheef kenna bal’isee, (kan fedhetti immoo) dhiphisu hin beekanuu? Dhugumatti, kana keessa ummata amananiif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(39,53,'39:53','(Yaa Muhammad!) jedhi: “Yaa gabroota kiyya kan lubbuu isaanii irratti daangaa dabartan! Rahmata Rabbii irraa abdii hin muratinaaDhugumatti Rabbiin balleessa hundaa ni araaramaInni araaramaa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(39,54,'39:54','Odoo adabbiin isinitti hin dhufin gara Rabbii keessanii deebi’aa, Isaafis ajajamaaSana booda hin tumsamtanu.');
INSERT INTO "apaguna" VALUES(39,55,'39:55','Adabni tasa odoo isin hin beekin isinitti dhufuu dura irra gaarii waan Goftaa keessan irraa gara keessanitti bu’ee hordofaa');
INSERT INTO "apaguna" VALUES(39,56,'39:56','Lubbuun “Yaa sheenaa kiyya kanin karaa Rabbii keessatti daangaa dabree, warra qishinan irraa ta’ee!” akka hin jenneef (hordofaa).');
INSERT INTO "apaguna" VALUES(39,57,'39:57','Yookiin “odoo Rabbiin na qajeelchee sila warra Rabbiin sodaatan irraan ta’a ture” akka hin jenneef.');
INSERT INTO "apaguna" VALUES(39,58,'39:58','Yookaan yeroo adabbii (Rabbii) argite, “carraan (addunyaatti deebi’uu) tokko odoo naaf ta’ee, toltuu dalagdoota irraan ta’a ture” akka hin jenneef.');
INSERT INTO "apaguna" VALUES(39,59,'39:59','Lakkii; dhugumatti mallattooleen kiyya sitti dhufaniiti, ati isaan sobsiiftee, boontee, kaafiroota irraa taate.');
INSERT INTO "apaguna" VALUES(39,60,'39:60','Guyyaa Qiyaamaa warra Rabbi irratti soban kan fuulli isaanii gurracha ta’e argitaSila warra boonaniif teessoon jahannam keessa hin taanee?');
INSERT INTO "apaguna" VALUES(39,61,'39:61','Rabbiin warra Isa sodaatan milkii isaaniitiin (jahannam irraa) isaan baraara; hamtuun isaan hin tuqu; isaan hin gaddanus.');
INSERT INTO "apaguna" VALUES(39,62,'39:62','Rabbiin uumaa waan hundaatiInni waan hunda irrattis tiksaadha.');
INSERT INTO "apaguna" VALUES(39,63,'39:63','Furtuuwwan samiifi dachii kan IsaatiIsaan keeyyattoota Rabbiitti kafaran, isaan sun hoonga’oon isaanuma.');
INSERT INTO "apaguna" VALUES(39,64,'39:64','(Yaa Muhammad!) “Yaa wallaaltotaa! Sila Rabbiin ala (waan biraa) gabbaruutti na ajajjuu?” jedhi');
INSERT INTO "apaguna" VALUES(39,65,'39:65','Dhugamatti, gara keetiifi gara warra si dura turaniittis “yoo Rabbii wajjiin waan biraa gabbarte dalagaan kee ni bada; warra hoonga’an irraas taata” jechuun wahyiin buufameera.');
INSERT INTO "apaguna" VALUES(39,66,'39:66','Rabbiin qofa gabbari; galateeffattoota irraas ta’i.');
INSERT INTO "apaguna" VALUES(39,67,'39:67','Isaan Rabbiin guddina isaaf malu hin guddifneGuyyaa Qiyaamaa dachiin guutuun qabaa Isaa keessa taati; samiinis mirga Isaatiin maramtuudhaInni qulqullaa’e; waan isaan Isa waliin gabbaran irraas ol ta’e.');
INSERT INTO "apaguna" VALUES(39,68,'39:68','Garrii (Xurumbaa) keessatti ni afuufamaWarri samii keessaatiifi warri dachii keessaa hunduu, nama Allaahn fedhe malee, ni dhumuErgasii yeroo biraas isa keessatti afuufamaYeroo san isaan dhaabbatanii ilaaloo ta’u.');
INSERT INTO "apaguna" VALUES(39,69,'39:69','Dachiinis ifa Gooftaa isiitiin iftiGalmeenis ni kaayamaNabiyyootaafi ragooleenis ni fidamuGidduu isaaniitti odoo isaan hin miidhamne haqaan murteeffama.');
INSERT INTO "apaguna" VALUES(39,70,'39:70','Lubbuun hundi waanuma hojjate galata galfamtiInni waan isaan hojjatan akkaan beekaadha');
INSERT INTO "apaguna" VALUES(39,71,'39:71','Isaan kafaran gara jahannam gareen ta’anii oofamu; yeroo ishee bira ga’an balballi ishee banamteeti, waardiyoonni ishee “sila ergamtoonni isin irraa ta’an kan keeyyattoota Gooftaa keessanii isin irratti dubbisaniifi wal ga’ii guyyaa keessanii kana isin sodaachisan isinitti hin dhufnee?” isaaniin jedhan[Isaan] “eeyyee (dhufaniiru), garuu jecha adabaatu kaafiroota irratti mirkanaa’e” jedhan.');
INSERT INTO "apaguna" VALUES(39,72,'39:72','“Balbala jahannam ishee keessatti hafoo ta’aatii seenaa” jedhamaTeessoon warra boonanii akkam fokkataa dha!');
INSERT INTO "apaguna" VALUES(39,73,'39:73','Warri Rabbii isaanii sodaatan gareedhaan gara jannataatti oofamuYeroma isaan isheetti dhufanii balballi ishees banamtee, waardiyoonni ishees “nageenyi isin irra haa jiraatu; toltaniittu; keessatti hafoo ta’aatii ishee seenaa” isaanin jettu (ishee seenan).');
INSERT INTO "apaguna" VALUES(39,74,'39:74','Ni jedhanis: “faaruun hundi kan Rabbii Isa waadaa Isaa dhugaa nuuf godhee, dachii (jannataas) nu dhaalchiseetiJannata keessaa iddoo barbaanne qubannaMindaan hojjattootaa waa tole!”');
INSERT INTO "apaguna" VALUES(39,75,'39:75','Maleykotas, kan naannoo Arshii Gooftaa isaanii marsanii, faaruu Gooftaa isaanii waliin Isa qulqulleessan argitaGidduu isaaniitti (uumamaatti) dhugaan murteeffameeti “faaruun hundi kan Rabbii Gooftaa aalamaati” jedhame.');
INSERT INTO "apaguna" VALUES(40,1,'40:1','Haa Miim.');
INSERT INTO "apaguna" VALUES(40,2,'40:2','Buufamni kitaabichaa Rabbii injifataa, beekaa ta’e irraayi.');
INSERT INTO "apaguna" VALUES(40,3,'40:3','Araaramaa dilii, qeebalaa tawbaa, (abbaa) adabbii cimaa, abbaa arjummaa(irraayyi)Isa malee dhugaan gabbaramaan hin jiru; deebiinis gara Isaati.');
INSERT INTO "apaguna" VALUES(40,4,'40:4','Keeyyattoota Rabbii keessatti warra kafaran malee eenyulleen hin falmuDachii keessa gara gaggaluun isaanii si hin gowwoomsin.');
INSERT INTO "apaguna" VALUES(40,5,'40:5','Isaan dura ummanni Nuuhiifi gareewwan isaan boodaas sobsiisanii jiruUmmanni hundi ergamaa isaanii qabuuf yaadaniiruSobaan falmanii dhugaa balleessufis yaalaniitin isaan qabeadabbiin kiyya akkam ta’e laata!');
INSERT INTO "apaguna" VALUES(40,6,'40:6','Akkuma kanatti jechi Rabbii keetii isaan kafaran irratti ‘dhugumatti isaan warra ibiddaa ta’uun’ mirkanoofte.');
INSERT INTO "apaguna" VALUES(40,7,'40:7','Isaan (malaykonni) Arshii baadhataniifi isaan maddii isaa jiranis Gooftaa isaanii faarsuun qulqulleessu; Isattis ni amanu; isaan amananiifis (akkana jechuun) araarama kadhatu: “Rabbii keenya! Ati rahmataafi beekumsaan waan hunda wal geessee jirta; kanaafuu isaan tawbataniifi karaa kee hordofaniif araaramiadabbii jahiim irraas isaan tiksi.');
INSERT INTO "apaguna" VALUES(40,8,'40:8','(Yaa) Rabbii keenya! Jannata qubannaa kan waadaa isaaniif galte isaan seensisi; akkasumas namoota toltuu hojjatan, kan abbootii isaanii, warra isaaniitiifi ilmaan isaanii irraa ta’anis (seensisi)Dhugumatti, Ati sumatu injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(40,9,'40:9','Hamtuu irraas isaan eegiNama Ati hamtuu irraa eegde, dhugumatti guyyaa san rahmata isaaf gootee jirtaSuni isumatu milkii guddaadha.”');
INSERT INTO "apaguna" VALUES(40,10,'40:10','Dhugumatti, warri kafaran “jibbaa Rabbii, kan yeroo gara iimaanaa waamamtanii isin kafartanii (Inni isin jibbutu) jibbaa mataa keessanii (kan har’a of jibbitan) irra guddaadha” jechuun itti lallabama.');
INSERT INTO "apaguna" VALUES(40,11,'40:11','Ni jedhan: “yaa Gooftaa keenya! Yeroo lama nu ajjeefteeti yeroo lama nu jiraachifteAmma balleessa keenya fudhanneerraa, karaan (rakkoo hamaa kana irraa) ittiin baanu wahii jiraa?”');
INSERT INTO "apaguna" VALUES(40,12,'40:12','Kun waan isin yeroo Rabbiin kophaa Isaa waamamu kafartanii, yeroo Isatti qindeeffame immoo amantaniifiMurtiin kan Rabbii ol ta’aa, guddaati.');
INSERT INTO "apaguna" VALUES(40,13,'40:13','Inni Isa mallattoolee Isaa isinitti argisiisuufi samii irraa rooba isiniif buusuudhaNama gara Isaa deebi’u malee eenyunuu hin gorfamu.');
INSERT INTO "apaguna" VALUES(40,14,'40:14','Oduma kaafirtoonni jibbanuu amantii isaaf qulqulleessaa, Rabbiin gabbaraa.');
INSERT INTO "apaguna" VALUES(40,15,'40:15','(Rabbiin Isa) sadarkaa ol fuudhu; dhuunfataa ‘Arshiiti.’ Akka guyyaa wal qunnamtii akeekkachiisuuf wahyii ajaja Isaa irraa ta’e gabroota Isaa gidduu nama fedhe irratti buusa.');
INSERT INTO "apaguna" VALUES(40,16,'40:16','Guyyaa isaan hundi ifatti bahan; (dhimma) isaan (ilaallatu) irraa homaatuu Rabbi irraa hin dhokatu“Har’a mootummaan kan eenyuuti? Kan Rabbii tokkicha, hunda injifataa ta’eeti.” (Rabbiin jedha).');
INSERT INTO "apaguna" VALUES(40,17,'40:17','Har’a (Guyyaa Qiyaamaa) lubbuun hundi waan hojjatte mindeeffamtiHar’a miidhaan hin jiruDhugumatti, Rabbiin saffisaa herregaati.');
INSERT INTO "apaguna" VALUES(40,18,'40:18','Guyyaa dhihaattuu (Qiyaamaa), kan yeroo onneen kokkee bira geessu yaada guutaman san isaan akeekkachiisi(Guyyaa san) miidhaa raawwattootaaf firri yookiin manguddoon fudhatama qabu hin jiru.');
INSERT INTO "apaguna" VALUES(40,19,'40:19','(Rabbiin) gantuu ijaatiifi waan qomti dhoksitu hunda ni beeka.');
INSERT INTO "apaguna" VALUES(40,20,'40:20','Rabbiin dhugaan murteessaWarri Isaa gaditti isaan gabbaran homaa hin murteessanuDhugumatti, Rabbiin Isumatu dhagayaa, argaadha');
INSERT INTO "apaguna" VALUES(40,21,'40:21','Sila dachii keessa deemanii booddeen warra isaan dura turanii akkam akka ta’e hin ilaalanuu? Humnaafi dachii keessatti faana dhiisuun isaan irra ciccimoo turaniiti, Rabbiin badii isaaniitif isaan qabe(adabbii) Rabbii irraa wanti isaan tiksu tokkollee isaaniif hin jiru');
INSERT INTO "apaguna" VALUES(40,22,'40:22','Kun waan isaan, ergamtoonni isaanii ragaawwan ifa ta’aniin isaanitti dhufnaan, isaan (itti) kafaraniifiRabbiin isaan ni qabeDhugumatti, Inni jabaa, adabbii cimaati.');
INSERT INTO "apaguna" VALUES(40,23,'40:23','Dhugumatti, Muusaa keeyyattoota keenyaafi ragaa ifa ta’een ergineerra');
INSERT INTO "apaguna" VALUES(40,24,'40:24','Gara Fir’awn, Haamaaniifi Qaaruunitti (erginee) “falfalaa, akkaan sobaadha” jedhan.');
INSERT INTO "apaguna" VALUES(40,25,'40:25','Yeroo inni dhugaa Nu biraa ta’een itti dhufe, ni jedhan: “ilmaan dhiiraa warra isaa waliin amananii ajjeesaa; dubartoota isaanii dhiisaa.” Tooftaan kaafirootaa dhabama malee hin taane.');
INSERT INTO "apaguna" VALUES(40,26,'40:26','Fir’awnis “Muusaa nan ajjeesaa na dhiisaa, Gooftaa isaa haa waammatuu.” “Ani amantii keessan jijjiruu yookiin dachii keessatti badii mul’isuu isaan sodaadha” jedhe.');
INSERT INTO "apaguna" VALUES(40,27,'40:27','Muusaanis ni jedhe: “ani nama boonaa guyyaa herregaatti hin amanne hunda irraa Rabbii kiyyaafi Rabbii keessanittiin maganfadhe.”');
INSERT INTO "apaguna" VALUES(40,28,'40:28','Namichi mu’uminni iimaana isaa dhoksu, kan maatii Fir’awni irraa ta’e ni jedhe: “sila isin namicha, waan Gooftaan kiyya Rabbuma jedheef, odoo Gooftaa keessan irraa ragaawwan ifa ta’aniin isinitti dhufee jiruu ajjeeftuu? Yoo sobaa ta’e sobni isaa isuma irratti; yoo nama dhugaa ta’e gariin waan inni waadaa isiniif galee isin tuqaDhugumatti, Rabbiin nama daangaa dabraa, akkaan sobaa ta’e kamillee hin qajeelchu.');
INSERT INTO "apaguna" VALUES(40,29,'40:29','Yaa ummata kiyya! Har’a dachii keessatti ol aantummaa qabaachuun, mootummaan keessan; yoo adabbiin Rabbii nutti dhufe namni nu tumsu eenyu?” (jedheen)Fir’awni “ani waanuman ofii kiyyaa amanu malee isinin hin akeeku; karaa sirraawaa malees isin hin qajeelchu” jedhe.');
INSERT INTO "apaguna" VALUES(40,30,'40:30','Inni amane ni jedhe: “Yaa ummata kiyya! Ani fakkaataa guyyaa ummata duriin isin irratti sodaadha.');
INSERT INTO "apaguna" VALUES(40,31,'40:31','Fakkaataa aadaa ummata Nuuh, Aad, Samuudiifi warra isaan boodaan (isin irratti sodaadha)Rabbiin gabrootaaf miidhaa hin fedhu.');
INSERT INTO "apaguna" VALUES(40,32,'40:32','Yaa ummata kiyya! Ani (adabbii) guyyaa wal waamiinsaan isin irratti sodaadha.');
INSERT INTO "apaguna" VALUES(40,33,'40:33','(adabbii) guyyaa, kan Rabbi irraa tiksaan isiniif hin jirre taatanii of duuba deebitanuun (isin irratti sodaadha)Nama Rabbiin jallise qajeelchaan isaaf hin jiru.”');
INSERT INTO "apaguna" VALUES(40,34,'40:34','Yuusufis kana dura ragaawwan ifa ta’aniin isinitti dhufee tureIsin waan inni isinittiin dhufe irraa shakkii keessa ta’uu irraa hin deemneYeroma inni du’e “Rabbiin isa booda ergamaa hin ergu” jettanAkka kanatti Rabbiin nama daangaa dabraa, mamaa ta’e jallisa.');
INSERT INTO "apaguna" VALUES(40,35,'40:35','(Isaan) warra keeyyattoota Rabbii keessatti, odoo ragaan isaanitti hin dhufin falmaniidha; jibbaan (isaanii) Rabbi birattiifi isaan amanan birattis guddate! Akka kanatti Rabbiin (onnee) warra boonaa, of tuulaa hunda irratti cufa.');
INSERT INTO "apaguna" VALUES(40,36,'40:36','Fir’awni ni jedhe: “yaa Haamaan! Akkan karaa ol ga’u gamoo naaf ijaari.');
INSERT INTO "apaguna" VALUES(40,37,'40:37','Karaawwan samii (ol ba’eetan), gara Gooftaa Muusaa ilaalaAni sobaa ta’uu isaan yaada.” Akka kanatti Fir’awniif hamtuun dalagaa isaa miidhagfamee, karaa (Rabbii) irraas dhorgameTooftaan Fir’awni hoongoo malee hin taane.');
INSERT INTO "apaguna" VALUES(40,38,'40:38','Inni amane ni jedhe: “Yaa ummata kiyya! Karaa qajeelaan isin qajeelchaa, na hordofaa.');
INSERT INTO "apaguna" VALUES(40,39,'40:39','Yaa ummata kiyya! Jireenyi addunyaa kanaa meeshaadhaDhugumatti, Aakhiraan isheetu ganda qubannaati.');
INSERT INTO "apaguna" VALUES(40,40,'40:40','Namni hamtuu hojjate, fakkaattuu isiitiin malee galata hin galfamuNamni odoo amanee jiruu toltuu dalage, dhiiras ta’ee dubartii irraas, isaan sun jannata seenaniiti ishee keessatti herrega malee kennamu.');
INSERT INTO "apaguna" VALUES(40,41,'40:41','Ammas yaa ummata kiyya! Maaluma taatani! Ani gara nageenyaattin isin waamaa, isin immoo gara ibiddaatti na waamtuu?');
INSERT INTO "apaguna" VALUES(40,42,'40:42','Akka ani Rabbitti kafaruufi waan waa’ee isaa beekumsa hin qabne isatti qindeessuuf na waamtuAni immoo gara (Rabbii) injifataa, araaramaa ta’eettin isin waama.');
INSERT INTO "apaguna" VALUES(40,43,'40:43','Dhugumatti, wanti isin itti na waamtan sun addunyaa keessattis ta’ee Aakhiraa keessatti waamichi homaatuu akka isaaf hin jirreefi deebiin keenya gara Rabbii qofa ta’uufi daangaa dabroonnis isaan warra ibiddaa ta’uun shakkiin hin jiru.');
INSERT INTO "apaguna" VALUES(40,44,'40:44','Waanin ani isiniin jedhu kana yaadachuuf jirtuAnis dhimma kiyya gara Rabbiittin hirkifadhaDhugumatti, Rabbiin waa’ee gabroota Isaa argaadha.”');
INSERT INTO "apaguna" VALUES(40,45,'40:45','Rabbiinis hamtuuwwan waan isaan itti wal mari’atanii jalaa isa baraarseMaatii Fir’awnitti adabbii hamaatu marse.');
INSERT INTO "apaguna" VALUES(40,46,'40:46','Ibidda (jahannam) kan ganamaafi galgala ishee irra fidamantu (isaan marse)Guyyaa Qiyaamaan dhaabattu “maatii Fir’awni adabbii akkaan cimaa seensisaa.”');
INSERT INTO "apaguna" VALUES(40,47,'40:47','Yeroo ibidda keessatti wal mormanii, isaan dadhaboon isaan boonaniin “nuti isin hordofaa turree, maaf ibidda irraa garii nurraa hin qoodanne?” jedhan san (yaadadhu.)');
INSERT INTO "apaguna" VALUES(40,48,'40:48','Isaan boonan “nuti hunduu isheedhuma keessa jirra; dhugumatti, Rabbiin gidduu gabrootaatti murtii kenneera” jedhu.');
INSERT INTO "apaguna" VALUES(40,49,'40:49','Isaan ibidda keessa jiran waardiyyoota jahannamiin “Gooftaan keessan (hanguma) guyyaa tokko (kan addunyaa) adabbii akka nurraa salphisu (nuuf) kadhaa” jedhan.');
INSERT INTO "apaguna" VALUES(40,50,'40:50','(Waardiyyoonni) “sila ergamtoonni keessan ragaa ifa ta’een isinitti hin dhufnee?” jedhan(Warri ibiddaatis) “eeyyeen; (nutti dhufaniiru)” jedhan(Waardiyyoonni) “Ergasuu kadhaa! Kadhaan kaafirootaa dhabama malee hin taane” jedhan.');
INSERT INTO "apaguna" VALUES(40,51,'40:51','Dhugumatti, Nuti ergamtoota keenyaafi isaan amananiifis jiruu addunyaa keessattis ta’ee guyyaa ragoonni dhaabbatan ni tumsina');
INSERT INTO "apaguna" VALUES(40,52,'40:52','Guyyaa miidhaa raawwattoota, sababni isaan dhiheeffatan homaa fayyaduu dhabee, abaarsi isaaniif jiru san; kan ammaas manni fokkotaan isaaniif jiru san (ni tumsinaafi)');
INSERT INTO "apaguna" VALUES(40,53,'40:53','Dhugumatti, Muusaaf qajeelfama kenninee, Ilmaan Israa’iilis kitaaba dhaalchifneerra.');
INSERT INTO "apaguna" VALUES(40,54,'40:54','Warra qalbii qabaniif qajeelfamaafi gorsa');
INSERT INTO "apaguna" VALUES(40,55,'40:55','ObsiDhugumatti, waadaan Rabbii dhugaadhaDilii keetiifis araarama kadhu; Gooftaa keetiis ganamaafi galgala faaruun qulqulleessi');
INSERT INTO "apaguna" VALUES(40,56,'40:56','Dhugumatti, isaan keeyyattoota Rabbii keessatti, odoo ragaan tokkollee isaanitti hin dhufin morman, qoma isaanii keessa boona, itti ga’uu hin dandeenye malee homaatuu hin jiruRabbitti maganfadhuDhugumatti, Inni Isumatu dhagayaa, argaadha');
INSERT INTO "apaguna" VALUES(40,57,'40:57','Samiifi dachii uumuutu namaa uumuu irra guddaadhaGaruu irra hedduun namootaa hin beekanu');
INSERT INTO "apaguna" VALUES(40,58,'40:58','Jaamaafi (inni) argaan wal qixaa miti; isaan amananii, toltuu dalaganiifi inni hamtuu dalagu (wal qixaa miti); hangi isin gorfamtan xiqqoodha');
INSERT INTO "apaguna" VALUES(40,59,'40:59','Qiyaamaan dhuftuudha; ishee keessa mamiin hin jiruGaruu irra hedduun namootaa hin amananu.');
INSERT INTO "apaguna" VALUES(40,60,'40:60','Gooftaan keessan ni jedhe: “Na kadhaa; isiniifiin awwaadhaaDhugumatti, isaan ana gabbaruu irraa boonan, salphattoota ta’anii, jahannam seenuuf jiru.”');
INSERT INTO "apaguna" VALUES(40,61,'40:61','Rabbiin Isa halkan akka ishee keessa boqattaniif, guyyaa immoo ibsaa kan isiniif godheDhugumatti, Rabbiin namoota irratti abbaa tolaatiGaruu irra hedduun namootaa hin galatoomfatanu.');
INSERT INTO "apaguna" VALUES(40,62,'40:62','(Inni kana isiniif godhe) kun Allaah, Gooftaa keessani; Uumaa waan hundaatiIsa malee haqaan gabbaramaan hin jiruAkkamitti irraa gara galfamtu?');
INSERT INTO "apaguna" VALUES(40,63,'40:63','Akkanatti warri keeyyattoota Rabbii morman irraa gara galfamu.');
INSERT INTO "apaguna" VALUES(40,64,'40:64','Rabbiin Isa dachii jireenyaaf mijooftuu, samii immoo ijaarsa (gubbaa) isiniif godhee, isin uumee, suuraa keessan miidhagsee, wantoota gaggaarii irraa isiniif kenne(Inni kana godhe) kun Allaah, Gooftaa keessaniAllaahn Gooftaa aalama hundaa ta’e tolli Isaa guddate.');
INSERT INTO "apaguna" VALUES(40,65,'40:65','Inni jiraataadhaIsa malee dhugaan gabbaramaan hin jiruKanaafuu amantii Isaaf qulqulleessaa, Isa kadhaaFaaruun kan Rabbii, Gooftaa aalama hundaa ta’eeti.');
INSERT INTO "apaguna" VALUES(40,66,'40:66','“Ani erga ragaawwan ifa ta’an Gooftaa kiyya irraa natti dhufanii booda wantoota isin Rabbii gaditti waamtan (gabbartan) gabbaruu iraa dhorgameeraGooftaa aalama hundaatiif harka kennuttiis ajajameera” jedhi.');
INSERT INTO "apaguna" VALUES(40,67,'40:67','Inni Isa biyyee irraa, ergasii bishaan saalaa irraa, ergasii dhiiga ititaa irraa, isin uumee ergasii daa’ima godhee isin baasaErgasii akka jabina keessan geessan (isin jiraachisa.) Ergasii akka jaarsa taatan isin godhaIsin irraa nama san dura du’uutu jiraAkka beellama beekamaa geessaniifi akka sammuu horattaniif (isin jiraachisa).');
INSERT INTO "apaguna" VALUES(40,68,'40:68','Inni Isa jiraachisuufi ajjeesuYeroo dhimma tokko murteesse “argami” isaan jechuu qofa; yoosuu argama.');
INSERT INTO "apaguna" VALUES(40,69,'40:69','Sila isaan keeyyattoota Rabbii keessatti morman akka itti irraa deebifaman hin arginee?');
INSERT INTO "apaguna" VALUES(40,70,'40:70','Isaan kitaabaafi waan Nuti ergamtoota keenya ittiin ergine sobsiisan, (hin arginee?) (fuul duratti) beekuuf jiru;');
INSERT INTO "apaguna" VALUES(40,71,'40:71','Yeroo sakaallaan sibiilaa morma isaanii irratti maramee, Sansalannis (itti diramee) harkifaman.');
INSERT INTO "apaguna" VALUES(40,72,'40:72','Bishaan danfaa keessa (harkifamanii) ergasii ibidda keessatti gubamu;');
INSERT INTO "apaguna" VALUES(40,73,'40:73','Ergasii “Isaan Rabbitti qindeessaa turtan sun eessa jiru?” isaaniin jedhama');
INSERT INTO "apaguna" VALUES(40,74,'40:74','Rabbiin ala (gabbaraa kan turtan)“Nu jalaa badaniiruLakkisaa nuti asiin dura homaa gabbaraa hin turre!” Akka kanatti Rabbiin kaafiroota jallisa.');
INSERT INTO "apaguna" VALUES(40,75,'40:75','“Kun waan dachii irratti haqaan ala gammadaa turtaniifi waan daangaa darbitaniifi”');
INSERT INTO "apaguna" VALUES(40,76,'40:76','“Balbala jahannam ishee keessatti hafoo ta’aa seenaa; (isheen) teessoo warra boontotaa ta’uun waa fokkate!” (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(40,77,'40:77','ObsiDhugumatti, waadaan Rabbii dhugaadhaYoo garii waan waadaa isaaniif gallee si agarsiifne (waa tole); yookiin yoo si ajjeefnes, garuma keenya deebifamu');
INSERT INTO "apaguna" VALUES(40,78,'40:78','Dhugumatti, si dura ergamtoota erginee jirraIsaan irraa warra seenaa isaanii siif himnetu jiraIsaan irraas warra seenaa isaanii siif hin himintu jiruErgamaan tokko hayyama Rabbiitiin ala keeyyata tokko fiduu hin danda’uYeroo ajajni Rabbii dhufe, (dhimmichi) dhugaan murteeffamaKijibdoonni achitti ni hoonga’u.');
INSERT INTO "apaguna" VALUES(40,79,'40:79','Rabbiin Isa beyladoota, (garii ishee) yaabbattanii, (garii immoo) nyaattaniif isinii uumeedha.');
INSERT INTO "apaguna" VALUES(40,80,'40:80','Isiniif isaan keessa bu’aa adda addaatu jiraAkka dhimmoota qoma keessan keessatti yaaddan ittiin bakkaan ga’attaniif (isinii uume)Akkasumas isaan irrattiifi dooniiwwan irratti baadhatamtanii (dachiifi bishaan keesa adeemtu).');
INSERT INTO "apaguna" VALUES(40,81,'40:81','Inni mallattoolee Isaa isin agarsiisaMallattoowwan Rabbii irraa kamiin mormitu?');
INSERT INTO "apaguna" VALUES(40,82,'40:82','Sila dachii keessa deemanii, booddeen warra isaan duraa akkam akka ta’e hin ilaallee? Isaan irra bay’ee, humnaanis irra ciccimoo, dachii keessatti faana dhiisuunis isaan caalu turanus wanti isaan dachii keessatti hojjachaa turan (adabbii) irraa isaan hin duroomsine');
INSERT INTO "apaguna" VALUES(40,83,'40:83','Yeroo ergamtoonni isaanii ragaalee ifa ta’aniin isaanitti dhufan, beekumsa irraa waan isaan bira jirutti gammadanii (boonan)Wanti isaan itti qishnaa turan isaan marse.');
INSERT INTO "apaguna" VALUES(40,84,'40:84','Yeroo adabbii keenya argan ni jedhan: “Rabbitti amanne; waan Isatti qindeessaa turrettis kafarre.”');
INSERT INTO "apaguna" VALUES(40,85,'40:85','Erga adabbii keenya arganii booda amanuun isaanii kan isaan fayyadu hin taane(Sun) karaa Rabbii kan gabroota Isaa keessatti dabreedhaKaafiroonni achitti hoonga’e');
INSERT INTO "apaguna" VALUES(41,1,'41:1','Haa Miim.');
INSERT INTO "apaguna" VALUES(41,2,'41:2','(Qur’aanni kun) Rabbii mararfataa, rahmata godhaa ta’e irraa buufamaadha');
INSERT INTO "apaguna" VALUES(41,3,'41:3','Kitaaba keeyyattoonni isaa namoota beekaniif, dubbifamaa Afaan Arabaa ta’ee, adda baafamteedha.');
INSERT INTO "apaguna" VALUES(41,4,'41:4','Gammachiisaafi sodaachisaa ta’ee (buufamee dha); irra hedduun isaanii irraa gara galanSaniifuu isaan hin dhagayan.');
INSERT INTO "apaguna" VALUES(41,5,'41:5','Ni jedhan: “onneen keenya waan isin itti nu waamtan irraa haguuggaa keessa jiraGurra keenya keessas duudummaatu jiraGidduu keenyaafi gidduu keetis waan haguugutu jiraKanaaf ati (waan feete) dalagi; nutis (waan feene) dalagna.');
INSERT INTO "apaguna" VALUES(41,6,'41:6','Jedhi: “ani namuma akka keessaniiti; ‘dhugaan gabbaramaan keessan gabbaramaa tokkicha qofa; gara Isaa qajeelaa, araaramas Isa gaafadhaa, warra (Isatti) qindeessaniif adabatu jira’ jechuutu gara kiyyatti beeksisa godhama.”');
INSERT INTO "apaguna" VALUES(41,7,'41:7','Isaan warra zakaa hin kennine, kan Aakhirattis kafaraniidha.');
INSERT INTO "apaguna" VALUES(41,8,'41:8','Dhugumatti, isaan amananii, toltuu hojjatan isaaniif mindaa adda hin cinnetu jira.');
INSERT INTO "apaguna" VALUES(41,9,'41:9','Jedhi: “Sila isin Isa dachii guyyaa lama keessatti uumetti kafartanii, hiriyyaa Isaaf gootuu? Sun Gooftaa aalama hundaati.');
INSERT INTO "apaguna" VALUES(41,10,'41:10','Ishee (dachii) keessatti gaarreen lafa qabatoo ta’an; gubbaa isiitti godhee, ishee keessattis barakaa godheIshee keessatti wantoota ittiin jiraatan guyyuma afur guutuu keessatti warra itti haajamaniif murteesse.');
INSERT INTO "apaguna" VALUES(41,11,'41:11','Ergasii haala isheen aara taatee jirtuun samii ol ta’e, isheefi dachiinis “jaalattanii yookiin jibbitanii kottaa” jedheIsaanis “jaalannee dhufne” jedhan.');
INSERT INTO "apaguna" VALUES(41,12,'41:12','Guyyaa lama keessatti samii torba godhee xumuree, samii hunda keessatti, dhimma ishee ilaalchisee beeksisa itti godheSamii addunyaas ifaadhaan (urjiin) miidhagsinee, tika cimaas (tiksine)Kun murtii (Rabbii) injifataa, beekaati.');
INSERT INTO "apaguna" VALUES(41,13,'41:13','Yoo isaan gara galan “ani iyyansa hamaa, akka iyyansa ummata Aadiifi Samuudin isin sodaachiseera” jedhiin.');
INSERT INTO "apaguna" VALUES(41,14,'41:14','(adabbii) yeroo ergamtoonni fuuldura isaaniitiifi duuba isaaniitiin itti dhufanii “Rabbiin malee waan biraa hin gabbarinaa” (isaaniin jennaan), “Gooftaan keenya odoo fedhee malaykota buusa ture; kanaaf nuti waan isin itti amantanitti kafarree jirra” jedhanii san (isin sodaachise).');
INSERT INTO "apaguna" VALUES(41,15,'41:15','Aad immoo dachii keessatti haqaan ala boonanii “humnaan namni nurra cimaa eenyu?” jedhanSila Rabbiin, Inni isaan uume sun dhugumatti, humnaan isaan irra cimaa ta’uu hin arginee? Keeyyattoota keenya mormitoota turan.');
INSERT INTO "apaguna" VALUES(41,16,'41:16','Akka jiruu addunyaa keessatti adabbii salphinaa isaan dhandhamsiifnuuf jecha guyyoota (isaaniif) hamaa keessatti bubbee cimaa isaan irratti ergine(Haa ta’uu malee) adabbii Aakhiraatu irra salphisaadhaIsaan hin tumsamanu.');
INSERT INTO "apaguna" VALUES(41,17,'41:17','Samuudiinis isaan qajeelchineeti, qajeelloo irra jallina filatani; waan isaan hojjataa turaniif duuti adabbii salphisaa isaan qabatte');
INSERT INTO "apaguna" VALUES(41,18,'41:18','Isaan amananii, Rabbiin kan sodaatan turan nagaya baafne.');
INSERT INTO "apaguna" VALUES(41,19,'41:19','Guyyaa diinotiin Rabbii gara ibiddaatti walitti qabamanii ittifaman san [yaadadhu]');
INSERT INTO "apaguna" VALUES(41,20,'41:20','Yeroo ishee bira dhufan, dhageettiin isaanii, agartuun isaanitiifi gogaan isaanii waan isaan dalagaa turan ragaa isaan irratti ba’u.');
INSERT INTO "apaguna" VALUES(41,21,'41:21','Gogaa isaanitiin “Maalif ragaa nurratti baatu?” jedhan[Isaan] ni jedhu: “Rabbii Isa waan hunda dubbachiise, kan yeroo jalqabaa isin uume, kan gara Isaatti deebifamtantu nu dubbachiise.”');
INSERT INTO "apaguna" VALUES(41,22,'41:22','Isinis dhageettiin keessan, agartuun keessaniifi gogaan keessan ragaa isin irratti ba’uu kan of dhoksitanu hin taaneHaa ta’u malee, Rabbiin hedduu waan isin dalagdanii beekuu dhabuu yaaddan.');
INSERT INTO "apaguna" VALUES(41,23,'41:23','Sun yaada keessan, kan isin Gooftaa keessanitti yaaddaniidhaIsin balleessee, warra hoonga’an irraa taatan.');
INSERT INTO "apaguna" VALUES(41,24,'41:24','Yoo obsanis, ibiddi teessoo isaaniiti, yoo ceephoo buufachuu barbaadanis, isaan warra ceephefamu irraayii miti');
INSERT INTO "apaguna" VALUES(41,25,'41:25','Nuti hiriyoota (Sheyxaanaa) isaaniif ramadneeti, wantoota fuuldura isaaniitiifi wantoota booda isaanii isaaniif bareechanJechi (adabbiin) akkuma ummatoota isaaniin duraa kan jinniifi nama irratti mirkanaa’e, isaan irrattis mirkanaa’eIsaan dhugumatti hoonga’oo ta’aniiru');
INSERT INTO "apaguna" VALUES(41,26,'41:26','Isaan kafaran “Qur’aana kana hin dhaga’inaa; akka injifattaniif (yeroo dubbifamu) isa keessatti wacaa” jedhan');
INSERT INTO "apaguna" VALUES(41,27,'41:27','Isaan kafaran dhugumaan adabbii cimaa isaan dhandhamsiifnaDhugumatti, hamtuu isaan dalagaa turaniifis isaan mindeessina.');
INSERT INTO "apaguna" VALUES(41,28,'41:28','Sun waan mindaan diinotii Rabbii ibidda ta’eefiIsaaniif ishee keessa mana yeroo hundaatu jiraWaan keeyyattoota keenya mormaniif isaaniif mindaa ta’a.');
INSERT INTO "apaguna" VALUES(41,29,'41:29','Isaan kafaran “Gooftaa keenya! Isaan jinnootaafi namoota irraa nu jallisan lamaan nutti agarsiisi; akka warra jalaa keessa ta’aniif faana keenya jala isaan goona” jedhan');
INSERT INTO "apaguna" VALUES(41,30,'41:30','Dhugumatti, isaan “gabbaramaan keenya Allaahdha” jedhanii, ergasii achirratti gadi dhaabbatan, (yeroo isaan du’an) malaykonni isaan irratti bubu’aniiti, “hin sodaatinaa; hin gaddinaas; jannata waadaa isiniif galameen gammadaa.');
INSERT INTO "apaguna" VALUES(41,31,'41:31','Nuti jiruu addunyaa keessatti jaalalloo keessan turre; Aakhirattis akkasumaIsiniif ishee (jannata) keessa waan lubbuun keessan feetutu jiraIsiniif ishee keessa waan barbaaddantu jira.');
INSERT INTO "apaguna" VALUES(41,32,'41:32','Afeerraa (Rabbii) araaramaa, rahmata godhaa irraa ta’eedha” (isaaniin jedhu).');
INSERT INTO "apaguna" VALUES(41,33,'41:33','Nama gara Rabbiitti waameefi hojii gaggaarii hojjatee “ani Muslimoota irraayi” jedhe caalaa namni jecha bareedu eenyu?');
INSERT INTO "apaguna" VALUES(41,34,'41:34','(Hojiin) gaariiniifi hamtuun wal hin qixxaa’anIshee toltuu taateen deebisiYoos inni gidduu keetiifi gidduu isaa diinummaan jiru akka jaalalloo firaa ta’uu (agarta).');
INSERT INTO "apaguna" VALUES(41,35,'41:35','Warra obsan malee eenyulleen ishee hin argatuAbbaa qooda guddaa qabu malee eenyulleen ishee hin argatu');
INSERT INTO "apaguna" VALUES(41,36,'41:36','Yoo waswaasaan shayxaana irraa ta’e si jallise, Rabbitti maganfadhuDhugumatti, Inni Isumatu dhaga’aa, beekaadha.');
INSERT INTO "apaguna" VALUES(41,37,'41:37','Halkan, Guyyaan, Aduufi Baatiinis mallattoolee Isaa irraayyiYoo kan (dhugaan) Isa gabbartan taatan Aduufis ta’ee baatiif hin salaatinaaRabbii isaan uume qofaaf sagadaa');
INSERT INTO "apaguna" VALUES(41,38,'41:38','Yoo boonanis, isaan (malaaykoonni) Rabbii kee bira jiran halkaniifi guyyaa Isa qulqullessu; isaan hin hifatanu.');
INSERT INTO "apaguna" VALUES(41,39,'41:39','Ati dachii gogduu taate arguun mallattoolee Isaa irraayiErgasii yeroo Nuti ishee irratti bishaan (rooba) buufne ni sochooti, ni tullooftisDhugumatti, Inni ishee jiraachise, warra du’e jiraachisaadhaInni waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(41,40,'41:40','Dhugumatti, isaan keeyyattoota keenya bakka irraa dabsan nu jalaa dhokachuu hin danda’anuSila Guyyaa Qiyaamaa isa ibidda keessatti darbamu moo, isa nagaa ta’ee dhufutu caala? Waan feetan dalagaa; Inni waan isin dalagdan hunda argaadha');
INSERT INTO "apaguna" VALUES(41,41,'41:41','Dhugumatti, isaan erga inni itti dhufee booda yaadannotti (Qur’aanatti) kafaran (mindaa ni argatu)Dhugumatti, inni kitaaba injifataadha');
INSERT INTO "apaguna" VALUES(41,42,'41:42','Sobni fuuldura isaatiis ta’ee duuba isaatiin itti hin dhufu(Rabbii) ogeessa faarfamaa ta’e irraa buufame.');
INSERT INTO "apaguna" VALUES(41,43,'41:43','Waan ergamtoota si duraatiin jedhameen ala wanti siin jedhamu hin jiruDhugumatti, Gooftaan kee abbaa araaramaatiifi abbaa adabbii laaleessaati');
INSERT INTO "apaguna" VALUES(41,44,'41:44','Odoo Qur’aana afaan ajamii (Arabiffaan ala) goonee, sila “maaliif keeyyattoonni isaa sirritti hin ibsamin; ergamaan Arabaa, akkamitti afaan Ajamiin bu’e?” jedhu turan(Yaa Muhammad!) jedhiin: “Inni warra amananiif qajeelfamaafi qorichaIsaan hin amanin gurra isaanii keessa duudummaatu jiraInnis isaan irratti jaamummaadhaIsaan sun (akka nama) iddoo fagoo irraa waamamuuti.”');
INSERT INTO "apaguna" VALUES(41,45,'41:45','Dhugumatti, Muusaaf kitaaba kennineeti, isa keessatti wal dhabameOdoo jechi Gooftaa kee biraa dabarte jiraachuu baattee gidduu isaaniitti murtiin ni kennama tureIsaan isa irraa shakkii mamsiisaa keessa jiru.');
INSERT INTO "apaguna" VALUES(41,46,'41:46','Namni toltuu hojjate ofumaafiNamni hamtuu hojjates isheedhuma irrattiGooftaan kee gabroota kan miidhuu miti.');
INSERT INTO "apaguna" VALUES(41,47,'41:47','Beekumsi Qiyaamaa garuma Isaatti deebifamaBeekumsa Rabbiitiin ala firiin kamillee qola ishee keessaa hin baatu, dubartiin kamuu hin ulfooftu, hin deessusGuyyaa “shariikonni kiyya eessa jiru?” (jedhee) Inni itti lallabu ni jedhu: “(Yaa Rabbi!) nurraa kan (Ati shariika qabaachuu) ragaa ba’u dhabamuu si beeksifneerra.”');
INSERT INTO "apaguna" VALUES(41,48,'41:48','Wanti isaan duraan (addunyaa irratti) kadhachaa (gabbaraa) turanis isaan irraa badeKarri itti dheessan isaaniif hin jiruu ni mirkaneeffatu.');
INSERT INTO "apaguna" VALUES(41,49,'41:49','Namni qabeenya kadhachuu irraa hin hifatuYeroo hiyyummaan isa tuqe akkaan abdii murataa, rahmata Rabbii irraa garaa kutataa ta’a.');
INSERT INTO "apaguna" VALUES(41,50,'41:50','Odoo erga rakkoon isa tuqee booda rahmata Nu irraa ta’e isa dhandhamsiifnee silaa “kun anaaf mala; ani Qiyaaman ni dhaabbatti jedhee hin yaadu; yoon gara Gooftaa kiyyaa deebifames, Isa bira (carraa) gaariitu naaf jira” jedhaDhugumatti, warra kafaran waan isaan hojjachaa turan isaanitti odeessinee, adabbii ulfaataas isaan dhandamsiisuuf jirra');
INSERT INTO "apaguna" VALUES(41,51,'41:51','Yeroo Nuti nama irratti tola oolle, inni gara galeeti (iimaana) irraa fagaataYeroo hamtuun isa tuqes yeruma san abbaa kadhaa hedduu ta’a');
INSERT INTO "apaguna" VALUES(41,52,'41:52','Jedhi: “Mee naaf himaa! Yoo (Qur’aanni) Rabbi biraa ta’ee, ergasii isin isatti kafartan? Nama isa mormii fagoo ta’e keessa jiru irra jallaan ni jiraa?');
INSERT INTO "apaguna" VALUES(41,53,'41:53','Dhugaa ta’uun isaa (Qur’aanaa) hanga isaaniif ifa ta’utti Nuti mallattoowwan keenya moggaa (samii)fi lubbuu isaanii keessatti isaan agarsiifnaInni waan hunda irratti to’ataa ta’uun Gooftaa keetiif hin ga’uu?');
INSERT INTO "apaguna" VALUES(41,54,'41:54','Mee dhagayaa! Dhugumatti, isaan (kun) Gooftaa isaanii qunnamuu irraa mamii keessa jiruDhaga’aa! Dhugumatti, Inni waan hundaan marsee jira.');
INSERT INTO "apaguna" VALUES(42,1,'42:1','Haa Miim.');
INSERT INTO "apaguna" VALUES(42,2,'42:2','Ayn Siin Qaaf.');
INSERT INTO "apaguna" VALUES(42,3,'42:3','Akka kanaan Rabbiin injifataa ogeessa ta’e gara keetiifi gara isaan siin duraa wahyii godha.');
INSERT INTO "apaguna" VALUES(42,4,'42:4','Wanti samiifi wanti dachii keessaa hundi kan IsaatiInni ol ta’aa, guddaadha.');
INSERT INTO "apaguna" VALUES(42,5,'42:5','Samiin (torban) wal gubbaa baqaquutti dhihaattiMaleeykonni faaruu Gooftaa isaaniitiin Isa qulqulleessuNama dachii keessa jiruufis araarama kadhatuDhaga’aa! Dhugumatti, Rabbiin Isumatu araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(42,6,'42:6','Warri Isaan alatti jaalallee godhatan, Rabbiin isaan to’ataAti isaan irratti itti gaafatamaa miti.');
INSERT INTO "apaguna" VALUES(42,7,'42:7','Akka (warra) haadhoo magaalaa (Makkaa)fi waarra moggaa ishee sodaachiftee, guyyaa wal ga’ii kan shakkii hin qabnes akka dinniintuuf Qur’aana Afaan Arabaa gara keetti beeksisa gooneGariin jannata seenu; gariin immoo ibidda keessa.');
INSERT INTO "apaguna" VALUES(42,8,'42:8','Rabbiin odoo fedhee ummata tokko isaan taasisa; garuu nama fedhe rahmata Isaa keessa galchaMiidhaa raawwattoota jaalallees ta’ee tumsaan isaaniif hin jiru.');
INSERT INTO "apaguna" VALUES(42,9,'42:9','Moo Isaan ala jaalallee taasifatu? Rabbiin Isumatu jaalalleedhaIsumatu du’aas kaasaInni waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(42,10,'42:10','Wanti isin irratti wal dhabdan hunduu murtiin isaa garuma RabbiitiInni sun Allaahdha; Gooftaa kiyya; Isa irratti hirkadhe; garuma Isaattin deebi’as.');
INSERT INTO "apaguna" VALUES(42,11,'42:11','(Inni) uumaa samiifi dachiitiLubbuu keessan irraa qindiiwwan (haadholee manaa) isiniif godhe; beyladoota irraas cimdii (kormaafi dhalaa isiniif taasise)Bifa kanaan isin baay’isaFakkaataan Isaa (Allaah) hin jiruInni dhaga’aa, argaadha.');
INSERT INTO "apaguna" VALUES(42,12,'42:12','Furtuun samiifi dachii kan IsaatiNama fedheef kennaa bal’isa; (kan fedhe immoo itti) dhiphisaDhugumatti Inni waan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(42,13,'42:13','Amantii irraa waan Nuuhiif dhaameefi isa gara kee wahyii goone, waan Ibraahiim, Muusaafi Iisaaf dhaamne karaa isiniif godhe[Inni] amantii sirriitti hojjadhaa, isa keessattiis addaan hin babahinaa jechuudhaWanti ati itti waamtu mushrikoota irratti guddateera! Rabbiin nama fedhe gara isaatti filata; nama tawbabates gara Isaatti qajeelcha.');
INSERT INTO "apaguna" VALUES(42,14,'42:14','Erga beekumsi isaanitti dhufee booda sababaa jibbaa gidduu isaanii jiruuf malee adda hin babaaneOdoo jechi Gooftaa kee biraa hanga yeroo beekamaatti dabre jiraachuu baatee, silaa gidduu isaaniitti murtaa’a tureWarri isaan booda kitaaba dhaalchifaman isa irraa shakkii cimaa keessa jiru.');
INSERT INTO "apaguna" VALUES(42,15,'42:15','Kanaafuu (tokkummaa) sanatti waamiAkkuma ajajamtetti (haqa irratti) gadi dhaabbadhuFedhii isaanii hin hordofin“Kitaaba irraa waan Rabbiin buusetti amaneera; gidduu keessanitti haqa murteessuus ajajameeraAllaahn Gooftaa keenyaafi Gooftaa keessaniNuuf hojii keenyatu jira; Isiniifis hojii keessantu jiraGidduu keenyaafi keessanitti mormiin hin jiruRabbiin (Guyyaa Qiyaamaa) gidduu keenya walitti qabaDeebiinis garuma Isaatti” jedhi.');
INSERT INTO "apaguna" VALUES(42,16,'42:16','Warri erga Isaaf (Rabbiif) owwaatamee booda, (diin) Rabbii keessatti falman ragaan isaanii Gooftaa isaanii biratti kuftuudhaDallansuun Rabbii isaan irra jira; adabbiin cimaanis isaaniif jira');
INSERT INTO "apaguna" VALUES(42,17,'42:17','Rabbiin Isa kitaabaafi madaala haqaan buuseedhaQiyaaman dhihoo ta’uun ni kajeelamaa maaltu si beeksise?!');
INSERT INTO "apaguna" VALUES(42,18,'42:18','Isaan isheetti hin amanin ishee jarjarfatuIsaan amanan (garuu) ishee irraa sodaatu; haqa ta’uu ishees ni beekuDhagayaa! Isaan Qiyaamaa keessatti falmanu jallina fagoo keessa jiru.');
INSERT INTO "apaguna" VALUES(42,19,'42:19','Rabbiin gabroota Isaatiif akkaan rahmata godhaNama fedhes ni sooraInni jabaa injifataadha.');
INSERT INTO "apaguna" VALUES(42,20,'42:20','Nama mindaa Aakhiraa fedha ta’e mindaa isaa keessatti isaaf daballaNama mindaa addunyaa fedha ta’es ishee irraa isaaf kenninaAakhiraatti immoo qoodni isaaf hin jiru.');
INSERT INTO "apaguna" VALUES(42,21,'42:21','Sila shariikota amantii irraa waan Rabbiin isa hin hayyamin karaa isaaniif godhantu jiraa? Odoo jechi murtiin (Guyyaa Qiyaamaati jedhu) jiraachuu baatee gidduu isaaniitti ni murteeffama tureMiidhaa hojjattoonni, isaaniif adabbii laalessaatu jira');
INSERT INTO "apaguna" VALUES(42,22,'42:22','Miidhaa raawwattoota waan hojjatan irraa odoma isaanitti bu’uu taa’uu kan sodaatan ta’uu argitaIsaan amananii, toltuu hojjatan immoo caffeewwan jannataa keessa jiraatu; isaaniif Gooftaa isaanii bira waan fedhantu jiraKun isumatu tola guddaadha');
INSERT INTO "apaguna" VALUES(42,23,'42:23','Kun isa Rabbiin gabroota Isaa isaan amananii, toltuu hojjatan ittiin gammachiisuudha“Isa (Qur’aana) irratti jaalala firummaa malee mindaa isin hin gaafadhuNama toltuu hojjate ishee (toltuu) keessatti gaarii isaaf daballaRabbiin araaramaa, galateeffataadha');
INSERT INTO "apaguna" VALUES(42,24,'42:24','Rabbi irratti soba uume jedhuu? Rabbiin immoo yoo fedhe onnee kee irratti cufeeti dhara haqee haqa jecha Isaatiin mirkaneessaInni waan qoma keessa jiru beekaadha.');
INSERT INTO "apaguna" VALUES(42,25,'42:25','Inni Isa gabroota Isaa irraa tawbaa qeebalu; Kan hamtuus irra dabruufi waan isin dalagdan beekuudha.');
INSERT INTO "apaguna" VALUES(42,26,'42:26','Isaan amananii toltuu dalaganiif ni owwaataToltuu Isaa irraas isaaniif dabalaKaafiroonni isaaniif adabbii cimaatu jira');
INSERT INTO "apaguna" VALUES(42,27,'42:27','Rabbiin gabroota Isaatiif kennaa odoo bal’isee, silaa dachii keessatti daangaa dabru turanGaruu waan fedhe qixa sirrii ta’een buusaInni gabroota Isaa keessa beekaa, argaadha.');
INSERT INTO "apaguna" VALUES(42,28,'42:28','Inni Isa erga abdii kutatanii, bokkaa buusuufi rahmata Isaa babal’isuudhaInni gargaaraa, faarfamaadha');
INSERT INTO "apaguna" VALUES(42,29,'42:29','Samii torban, dechiifi lubbu qabeeyyii isaan lamaan keessa facaase uumuun mallattoolee Isaa irraayyiInni yeroo fedhetti walitti isaan qabuu irrattis danda’aadha.');
INSERT INTO "apaguna" VALUES(42,30,'42:30','Hamtuu irraa wanti isin tuqe waanuma harki keessan hojjatteefi; baay’ees dhiifama godha.');
INSERT INTO "apaguna" VALUES(42,31,'42:31','Isinis dachii keessatti Rabbi duraa bahoo mitiRabbiin ala jaalalleefi tumsaanis isiniif hin jiru.');
INSERT INTO "apaguna" VALUES(42,32,'42:32','Dooniiwwan akka tabbaa kan galaana keessa yaa’anis mallattoolee Isaa irraayi;');
INSERT INTO "apaguna" VALUES(42,33,'42:33','Yoo fedhe qilleensa cal’isiiseeti dugda isaa irra tattaa’oo ta’u turanKana keessa warra akkaan obsanii, galatoomfatan hundaaf mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(42,34,'42:34','Yookiin waan isaan hojjataniin isaan balleessa; baay’ees dhiifama godha');
INSERT INTO "apaguna" VALUES(42,35,'42:35','Isaan keeyyattoota keenya keessatti falman bakka itti daban dhabuu isaanii akka beekaniif (isaan balleesse).');
INSERT INTO "apaguna" VALUES(42,36,'42:36','(Qabeenya) irraa wanti isin kennamtan kamiyyuu meeshaa jireenya addunyaatiWaan Rabbiin bira jiruutu isaan amananii, Gooftaa isaanii irratti hirkataniif caalaafi hafaadha.');
INSERT INTO "apaguna" VALUES(42,37,'42:37','Isaan warra dilii gurguddaafi gochaalee fokkatoos irraa fagaatanii, yeroo dallananis dhiifama godhaniidha.');
INSERT INTO "apaguna" VALUES(42,38,'42:38','Isaan warra Gooftaa isaaniitiif owwaatanii, salaataas sirnaan sagadanii, kan dhimmi isaanii jidduu isaaniitti wal marii ta’ee, waan nuti isaaniif kennine irraas arjoomaniidha.');
INSERT INTO "apaguna" VALUES(42,39,'42:39','Isaan warra yeroo miidhaan isaan qunname (haqaan) haaloo bahataniidha.');
INSERT INTO "apaguna" VALUES(42,40,'42:40','Galanni hamtuu hamtuma akka isheetiNamni dhiifama godhee, (hariiroos) tolche galanni isaa Rabbi irra jiraInni (Allaahn) miidhaa hojjattoota hin jaallatu.');
INSERT INTO "apaguna" VALUES(42,41,'42:41','Namni erga miidhamee booda haaloo bahates, isaan sun diliin isaan irra hin jiru');
INSERT INTO "apaguna" VALUES(42,42,'42:42','Komiin kan jiru isaan namoota miidhanii, haqaan ala dachii keessatti daangaa dabran irrattiIsaan sun adabbii laalessaatu isaaniif jira.');
INSERT INTO "apaguna" VALUES(42,43,'42:43','Namni obsee irra dabre, dhugumatti sun dhimmoota gurguddoo irraayi.');
INSERT INTO "apaguna" VALUES(42,44,'42:44','Nama Rabbiin jallise, Isa booda qajeelchaan isaaf hin jiruMiidhaa raawwattoota, yeroo adabbii argan gaabbanii kan “karaan (gara dachii) ittiin deebi’amu jiraa?” jedhan argita.');
INSERT INTO "apaguna" VALUES(42,45,'42:45','Salphina irraa sodaan guutamanii ija laafaan ilaalaa ishee (jahannam) irra kan fidaman isaan argitaIsaan amanan immoo “hoonga’oonni isaan Guyyaa Qiyaamaa lubbuu isaaniitiifi maatii isaanii hoonga’aniidha” jedhuDhagayaa! Miidhaa hojjattoonni adabbii yeroo hundaa keessa jiraatu.');
INSERT INTO "apaguna" VALUES(42,46,'42:46','Gargaartonni kamiyyuu kan Rabbiin ala isaaniif tumsan isaaniif hin jiruNama Rabbiin isa jallise karaan (ittiin qajeelu) isaaf hin jiru.');
INSERT INTO "apaguna" VALUES(42,47,'42:47','Guyyaan namni isa deebisuu danda’u hin jirre sun Rabbi irraa odoo isinitti hin dhufin dura Rabbii keessaniif owwaadhaaGuyyaa san hirkoon homaatuu isiniif hin jiruMormiinis isiniif hin jiru.');
INSERT INTO "apaguna" VALUES(42,48,'42:48','Yoo (dhugaa irraa) garagalanis, tiksaa goonee isaan irratti si hin ergineKan sirra jiru (ergaa) geessuu qofaNuti yeroo nama rahmata Nu biraa ta’e dhandhamsiifne, isheetti gammadaYoo hamtuun waan harki isaanii hojjatteef isaan tuqxe, dhugumatti, namni kufrii baay’isa.');
INSERT INTO "apaguna" VALUES(42,49,'42:49','Mootummaan samiifi dachii kan RabbiitiWaan fedhe uumaNama fedheef dubara kenna; nama fedheef immoo dhiira kenna.');
INSERT INTO "apaguna" VALUES(42,50,'42:50','Yookiin dhiiraafi dubara makaa kennaNama fedhe immoo maseena godhaInni beekaa, danda’aadha.');
INSERT INTO "apaguna" VALUES(42,51,'42:51','Namaaf Rabbiin isa dubbisuun, wahyiin yookiin dahoo duubaan, yookiin ergamaa ergee hayyama Isaatiin waan fedhe itti buusuun malee hin taaneInni ol aanaa, ogeessa.');
INSERT INTO "apaguna" VALUES(42,52,'42:52','Akkasumas Qur’aana ruuhii keenya irraa ta’e gara keetti wahyii gooneKitaabaafi iimaanni maal akka ta’an kan beektu hin turreGaruu ifaa gabroota keenya irraa nama feene ittiin qajeelchinu isa gooneAtis gara karaa qajeelaatti ni qajeelchita.');
INSERT INTO "apaguna" VALUES(42,53,'42:53','Karaa Rabbii wanti samii keessa jiruufi wanti dachii keessa jiru hundi kan Isaa ta’ee (ni qajeelchita)Dhagayaa! Dhimmi hundi garuma Rabbiitti deebi’a.');
INSERT INTO "apaguna" VALUES(43,1,'43:1','Haa Miim.');
INSERT INTO "apaguna" VALUES(43,2,'43:2','Kitaaba addeessaa ta’eenin kakadha.');
INSERT INTO "apaguna" VALUES(43,3,'43:3','Akka isin hubattaniif Nuti Qur’aana Arabiffaa isa taasifne.');
INSERT INTO "apaguna" VALUES(43,4,'43:4','Inni haadha kitaabaa ol aanaa ogummaan guutamee Nu bira jiru keessatti argama.');
INSERT INTO "apaguna" VALUES(43,5,'43:5','Ummata daangaa dabritoota waan taataniif gorsa isin irraa dabsinaa?');
INSERT INTO "apaguna" VALUES(43,6,'43:6','Warra duraa keessattis nabiyyoota meeqaatama erginee jirra.');
INSERT INTO "apaguna" VALUES(43,7,'43:7','Nabiyyiin tokkollee isaanitti dhufee yoo itti qishinan (baacan) malee hin hafne.');
INSERT INTO "apaguna" VALUES(43,8,'43:8','Humnaan isaan irra cimoo warra ta’an balleessinee jirraFakkeenyi warra jalqabaa dabreera.');
INSERT INTO "apaguna" VALUES(43,9,'43:9','Odoo samiifi dachii eenyutu uume jettee isaan gaafattee “Isa injifataa beekaa ta’etu isaan uume” jedhu.');
INSERT INTO "apaguna" VALUES(43,10,'43:10','(Rabbiin) Isa dachii afaa isiniif godhee, ishee keessattis akka qajeeltaniif karaalee isiniif godhe.');
INSERT INTO "apaguna" VALUES(43,11,'43:11','Ammas (Rabbiin) Isa samii irraa bishaan madaalaan buusee, ergasii ittiin dachii duutuu jiraachifneAkkuma kana baafamtu.');
INSERT INTO "apaguna" VALUES(43,12,'43:12','Isa cimdii hunda uumee, dooniifi beyladoota irraas waan yaabbattan isiniif godhe.');
INSERT INTO "apaguna" VALUES(43,13,'43:13','Akka dugda isaanii irratti baatanii, ergasii ni’imaa Gooftaa keessanii yeroo ol baatan yaadattanii “Inni kana nuuf laaffise qulqullaa’e; nuti isa danda’oo hin turre” jettaniif.');
INSERT INTO "apaguna" VALUES(43,14,'43:14','Nuti gara Gooftaa keenyaa deebi’oodha.');
INSERT INTO "apaguna" VALUES(43,15,'43:15','Gabroota Isaa irraa qooda Isaaf taasisaniDhugumatti, namni kafraa ifa bahaadha.');
INSERT INTO "apaguna" VALUES(43,16,'43:16','Sila waan uumu irraa ilmaan dubaraa (ofiif) taasifatee, isin immoo ilmaan dhiiraatiin isin filee?');
INSERT INTO "apaguna" VALUES(43,17,'43:17','Yeroo tokkoon isaanii waan “Rahmaaniif” fakkeenya taasiseen gammachiifame, dallansuun guutameeti fuulli isaa gurraacha ta’a.');
INSERT INTO "apaguna" VALUES(43,18,'43:18','Nama faaya miidhaginaa keessatti guddifamu, kan falmii keessatti sirriitti ibsuus hin dandeenye (godhatee)?');
INSERT INTO "apaguna" VALUES(43,19,'43:19','Maleeykota isaan gabroota Rahmaan ta’an dubara taasisanYeroo isaan uumaman bira jiran moo? Ragaan isaanii fuuldura barreeffameeti gaafatamus.');
INSERT INTO "apaguna" VALUES(43,20,'43:20','“Odoo Rahmaan fedhee isaan hin gabbarru turre” jedhanIsaan ni kijibu malee sana irratti beekumsa homaatuu hin qaban.');
INSERT INTO "apaguna" VALUES(43,21,'43:21','Moo (Qur’aana) kana dura kitaaba isaan sirritti qabatan isaaniif kennine?');
INSERT INTO "apaguna" VALUES(43,22,'43:22','Lakkii! Isaan “nuti abboota keenya amantii wahii irratti argine, nutis faana isaanii irra qajeeloodha” jedhan.');
INSERT INTO "apaguna" VALUES(43,23,'43:23','Akkasumas (Yaa Muhammad!) siin dura magaalaa kam keessattuu sodaachisaa erginee, qanani’oon ishee “Nuti abbootii keenya amantii wahii irratti argine; nuti faanuma isaanii irra hordoftoota” jedhan malee hin hafne.');
INSERT INTO "apaguna" VALUES(43,24,'43:24','(Ergamichis) “odoo ani waan abbootii keessan irratti argitan irra qajeelaatiin isinitti dhufee jiruu (hordoftuu)?” jedhe(Mushrikoonnis) “nuti waan isin ittiin ergamtanitti kafaroodha” jedhan.');
INSERT INTO "apaguna" VALUES(43,25,'43:25','Kanaafuu isaan irraa haaloo ba’anneBooddeen sobsiiftotaa akkam akka ta’e ilaali.');
INSERT INTO "apaguna" VALUES(43,26,'43:26','Yeroo Ibraahiim abbaa isaatiifi ummata isaatiin jedhes [yaadadhu]:“Dhugumatti ani waan isin gabbartan irraa qulqulluudha.');
INSERT INTO "apaguna" VALUES(43,27,'43:27','Isa na uume maleeInni na qajeelcha.”');
INSERT INTO "apaguna" VALUES(43,28,'43:28','Akka isaan deebi’aniif jechattii isa booda haftuu ishee godhe.');
INSERT INTO "apaguna" VALUES(43,29,'43:29','Dhugumatti, isaan kanaafi abbootii isaaniis hanga dhugaafi ergamaan ifa baasaan isaanitti dhufuttin qanansiise.');
INSERT INTO "apaguna" VALUES(43,30,'43:30','Dhugaan isaanitti dhufnaan immoo “kuni falfala; nuti isatti kafaroodha” jedhan.');
INSERT INTO "apaguna" VALUES(43,31,'43:31','Ni jedhan: “Qur’aanni kun nama guddaa ganda lamaan irraa ta’etti maaliif buufamuu dhabe?”');
INSERT INTO "apaguna" VALUES(43,32,'43:32','Sila Rahmata Gooftaa keetii isaantu qoodaa? Nutu jiruu addunyaa keessatti waan ittiin jiraatan gidduu isaaniitti qoodeAkka gariin isaanii garii tajaajilaa godhatuuf jecha garii isaanii garii irra sadarkaan ol fuuneRahmata Gooftaa keetiitu waan isaan walitti qaban irra caala');
INSERT INTO "apaguna" VALUES(43,33,'43:33','Odoo namni (gara addunyaa jallachuun) ummata tokko ta’uu baatee, silaa namoota Rahmaanitti kafaraniif ijoo manneen isaaniitiifi sadarkaawwan ittiin ol ba’an meeta irraa taasifna turre.');
INSERT INTO "apaguna" VALUES(43,34,'43:34','Manneen isaaniitiifis hulaaleefi sireewwan irratti hirkatanis (meeta irraa isaaniif goona turre).');
INSERT INTO "apaguna" VALUES(43,35,'43:35','Faayaas (ni goonaaf)Kun hunduu qananii jiruu addunyaa qofaAakhiraan Gooftaa kee biratti warra Rabbiin sodaataniifi.');
INSERT INTO "apaguna" VALUES(43,36,'43:36','Nama gorsa (Qur’aana) Rahmaan irraa gara gale, sheeyxaana isaaf ramadna; inni isaaf hiriyaadha.');
INSERT INTO "apaguna" VALUES(43,37,'43:37','Isaanis karaa (Rabbii) irraa isaan dhorgu; isaan immoo karaa qajeelaa irra waan jiran of se’u.');
INSERT INTO "apaguna" VALUES(43,38,'43:38','Yeroo Nutti dhufe “yaa gaabbii kiyya! Odoo gidduu kiyyaafi gidduu kee fageenyi baha lamaanii (bahaafi dhihaa) jiraatee” jedhaHiriyichi waa fokkate!');
INSERT INTO "apaguna" VALUES(43,39,'43:39','Har’a ibidda keessatti waliin jiraachuun keessan waan lubbuu keessan miitaniif homaa isin hin fayyadu.');
INSERT INTO "apaguna" VALUES(43,40,'43:40','Sila ati duudaa dhageessiftaa yookiin jaamaafi nama jallina ifa ta’e keessa jiru ni qajeelchitaa?');
INSERT INTO "apaguna" VALUES(43,41,'43:41','(Yaa Muhammad!) yoo si deemsifnellee, Nuti isaan irraa haaloo ba’achuuf jirra.');
INSERT INTO "apaguna" VALUES(43,42,'43:42','Yookiin isa waadaa isaaniif galle yoo sitti agarsiifnes, Nuti isaan irratti danda’oodha.');
INSERT INTO "apaguna" VALUES(43,43,'43:43','Isa gara keetti wahyii godhame san qabadhuAti karaa qajeelaa irra jirta.');
INSERT INTO "apaguna" VALUES(43,44,'43:44','Inni (Qur’aanni) siifi ummata keetiifis gorsaFuula dura ni gaafatamtu.');
INSERT INTO "apaguna" VALUES(43,45,'43:45','Ergamtoota keenya irraa nama si dura ergine “Sila Rahmaan malee gooftolii gabbaraman taasifnee jirraa?”gaafadhu');
INSERT INTO "apaguna" VALUES(43,46,'43:46','Dhugumatti, Muusaa raajiiwwan keenyaan gara Fir’awniifi qoondaaltota isaatti erginee, “ani ergamaa Gooftaa aalama hundaati” jedhe.');
INSERT INTO "apaguna" VALUES(43,47,'43:47','Yeroo raajiiwwan keenyaan isaanitti dhufu, isaan yeroo san isheetti kolfu.');
INSERT INTO "apaguna" VALUES(43,48,'43:48','Raajii irraa ishee obboleettii ishee irra caaltu malee isaan hin agarsiifnuAkka isaan (gara keenya) deebi’aniif adabaanis isaan qabne.');
INSERT INTO "apaguna" VALUES(43,49,'43:49','“Ati yaa kan sihrii hojjattu! Gooftaa kee waan waadaa siif galeen nuuf kadhuDhugumatti, nuti ni qajeella” jedhan.');
INSERT INTO "apaguna" VALUES(43,50,'43:50','Yeroma adabbii isaan irraa deebifnu isaan waadaa diigan.');
INSERT INTO "apaguna" VALUES(43,51,'43:51','Fir’awnis ummata isaa keessatti lallabee, “yaa ummata kiyya! sila mootummaan Misraa anaaf hin turree? Laggeen kunis ana jala hin yaa’anii? Sila hin agartanuu?” jedhe.');
INSERT INTO "apaguna" VALUES(43,52,'43:52','“Isa laafaa (tuffatamaa), dubbii adda baasuu hin dandeenye kana irra anatu caala');
INSERT INTO "apaguna" VALUES(43,53,'43:53','Yoo akkas ta’e maaliif gumeewwan warqee (dahaba) irraa hojjataman isa irratti hin buufamne? Yookiin maleykonni isa wajjin ta’anii maaliif hin dhufne?” (jedhe).');
INSERT INTO "apaguna" VALUES(43,54,'43:54','Akkanaan ummata isaa kakaasee ajaja isaa fudhatanDhugumatti, isaan ummata fincilaa turan.');
INSERT INTO "apaguna" VALUES(43,55,'43:55','Yeroo isaan Nu dallansiisan, haaloo isaanitti baanee, hunda isaaniituu dhidhimsine.');
INSERT INTO "apaguna" VALUES(43,56,'43:56','Dhaloota isaan boodaatiif hangafaafi fakkeenya isaan taasifne.');
INSERT INTO "apaguna" VALUES(43,57,'43:57','Yeroo ilmi Maryam (Iisaan) fakkeenya godhamu ummanni kee isa irraa kolfu (gammadu).');
INSERT INTO "apaguna" VALUES(43,58,'43:58','“Gooftolee keenya moo isatu caala?” jedhanWaan kana falmiif malee siif hin godhanuDhugumatti, isaan ummata mormitoota.');
INSERT INTO "apaguna" VALUES(43,59,'43:59','Dhugumatti, Inni (Iisaan) gabricha Nuti isa irratti tola oollee, Ilmaan Israa’iliif fakkeenya isa taasifne malee waan biraa miti.');
INSERT INTO "apaguna" VALUES(43,60,'43:60','Odoo feenee maleeykota isin irraa bakka bu’an dachii keessa goona turre.');
INSERT INTO "apaguna" VALUES(43,61,'43:61','Inni Qiyaamaaf mallattoo dhaa, ishee hin shakkinaa; ana hordofaaKanatu karaa qajeelaadha.');
INSERT INTO "apaguna" VALUES(43,62,'43:62','Sheyxaanni (dhugaa hordofuu irraa) akka isin hin dhorgineInni isiniif diina ifa ba’aadha.');
INSERT INTO "apaguna" VALUES(43,63,'43:63','Iisaan yeroo ragaalee ifa ta’aniin dhufe, ni jedhe: “Dhugumatti, ani beekumsaanin isinitti dhufe; ammaas akkan garii waan isin keessatti wal dhabdanii isiniif ibsuufin (dhufe.) Rabbiin sodaa dhaa; anaafis ajajamaa.');
INSERT INTO "apaguna" VALUES(43,64,'43:64','Allaahn Isumatu Gooftaa kiyyaafi Gooftaa keessaniIsa (qofa) gabbaraa; kanatu karaa qajeelaadha.');
INSERT INTO "apaguna" VALUES(43,65,'43:65','Gareewwan gidduu isaaniitti (dhimma kana irratti) wal dhabaniiruIsaan miidhaa dalaganiif adabbii guyyaa laalessaatu jira.');
INSERT INTO "apaguna" VALUES(43,66,'43:66','Sila Qiyaamaan daguudhaan odoo isaan hin beekin isaanitti dhufuu malee maal eegu?');
INSERT INTO "apaguna" VALUES(43,67,'43:67','Jaalalleewwan guyyaa san isaan Rabbiin sodaatan malee, gariin isaanii gariif diina.');
INSERT INTO "apaguna" VALUES(43,68,'43:68','Yaa gabroota kiyya! Har’a sodaan isin irra hin jiru; isin hin gadditanus.');
INSERT INTO "apaguna" VALUES(43,69,'43:69','Isaan keeyyattoota keenyatti amananii, Muslimoota ta’an;');
INSERT INTO "apaguna" VALUES(43,70,'43:70','Isiniifi haadholiin manaa keessanis gammadaa jannata seenaa” (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(43,71,'43:71','Qoriiwwan warqee irraa ta’aniifi kubbaayyaan isaan irra naanna’amaIshee keessas waan lubbuun feetuufi ijji itti gammaddutu jiraIsin ishee (jannata) keessatti hafoodha” (jedhamaani).');
INSERT INTO "apaguna" VALUES(43,72,'43:72','Jannata ishee dhalchifamtan kana waan hojjachaa turtaniini.');
INSERT INTO "apaguna" VALUES(43,73,'43:73','Isiniif ishee keessa fuduraalee baay’ee isaan irraa nyaattantu jira.');
INSERT INTO "apaguna" VALUES(43,74,'43:74','Yakkamtoonni adabbii jahannam keessatti hafu.');
INSERT INTO "apaguna" VALUES(43,75,'43:75','(Adabichi) Isaan irraa hin salphifamu; isaanis ishee keessatti abdii muratoodha.');
INSERT INTO "apaguna" VALUES(43,76,'43:76','Nuti isaan hin miine; garuu isaan (ofumaaf) miidhaa hojjattoota ta’an.');
INSERT INTO "apaguna" VALUES(43,77,'43:77','(Waardiyaa ibiddaatiin) “yaa Maalik! Gooftaan kee akka du’a nutti murteessu (nuuf gaafadhuu)” lallabu(Maalikis) “isin ishee keessa turoodha” jedhe.');
INSERT INTO "apaguna" VALUES(43,78,'43:78','hugumatti, Nuti haqaan isinitti dhufnee turreGaruu irra baay’een keessan haqa jibbitoota turtan.');
INSERT INTO "apaguna" VALUES(43,79,'43:79','Tooftaa baasanii turani, nutis tooftaa itti baafne.');
INSERT INTO "apaguna" VALUES(43,80,'43:80','Nuti iccitii isaaniitiifi maqoo isaanii hin dhageenyuu yaadanii? Lakkisaa! (ni dhageenya); ergamtoonni keenyas isaan biraa barreessaa jiru.');
INSERT INTO "apaguna" VALUES(43,81,'43:81','(Yaa Muhammad!) jedhi: “odoo Rahmaaniif ilmi jiraatee, silaa anatu jalqaba warra (isa) gabbaraniiti.”');
INSERT INTO "apaguna" VALUES(43,82,'43:82','Rabbiin samiifi dachii kan Gooftaa arshii ta’e waan isaan ittiin (Isa) wassafan (ibsan) irraa qulqullaa’eera.');
INSERT INTO "apaguna" VALUES(43,83,'43:83','Hanga guyyaa isaanii, isa waadaa galaman qunnamanitti (dhara keessa) seenanii haa taphatanii isaan dhiisi.');
INSERT INTO "apaguna" VALUES(43,84,'43:84','Inni Isa samii keessatti gabbaramaa, dachii keessattis gabbaramaatiInni ogeessa, beekaadha.');
INSERT INTO "apaguna" VALUES(43,85,'43:85','Inni mootummaan samii, dachiifi waan isaan lamaan gidduu kan Isaa ta’e, kan beekumsi Qiyaamaas Isa bira jiru, kan gara Isaatti deebifamtan sun toltuun isaa baay’ate.');
INSERT INTO "apaguna" VALUES(43,86,'43:86','Warri Rabbii gaditti waammatan, namoota dhugaa beekanii ragaa bahaniif malee, manguddummaa seenuu hin danda’anu.');
INSERT INTO "apaguna" VALUES(43,87,'43:87','Eenyu akka isaan uume odoo isaan gaafattee “Allaahdha” jedhuAkkamumatti irraa garagalfamuree?');
INSERT INTO "apaguna" VALUES(43,88,'43:88','Jechi inni (nabi Muhammad SAW!) “yaa Gooftaa kiyya! Dhugumatti, isaan kun ummata hin amanne” jedhe (sunis Allaah bira jira).');
INSERT INTO "apaguna" VALUES(43,89,'43:89','(Yaa Muhammad!) isaan irraa gara galii “nagaan (isin irra haa jiraatu)” jedhiin; (waan isaan mudatu) fuula dura beekuuf jiru.');
INSERT INTO "apaguna" VALUES(44,1,'44:1','Haa Miim.');
INSERT INTO "apaguna" VALUES(44,2,'44:2','Kitaaba addeessaa ta’eenin (kakadha).');
INSERT INTO "apaguna" VALUES(44,3,'44:3','Nuti halkan barakeeffamtuu keessa isa buufneNuti dinniinoo taaneerra.');
INSERT INTO "apaguna" VALUES(44,4,'44:4','Ishee (halkanittii) keessa dhimmi murteeffame hundi adda baafama,');
INSERT INTO "apaguna" VALUES(44,5,'44:5','Adda baafaminsa nu biraa ta’e (ibsina)Nuti ergitoota taaneerra');
INSERT INTO "apaguna" VALUES(44,6,'44:6','Rahmata Gooftaa kee irraa ta’eef (buufne)Inni Isumatu dhaga’aa, beekaadha.');
INSERT INTO "apaguna" VALUES(44,7,'44:7','(Rahmata) Gooftaa samii, dachiifi waan gidduu isaan lamaanii irraa ta’eetiYoo kan dhugoomsitan taataniif (kana hubadhaa).');
INSERT INTO "apaguna" VALUES(44,8,'44:8','Isa malee dhugaan gabbaramaan hin jiruNi jiraachisa; ni ajjeessas(Inni) Gooftaa keessaniifi Gooftaa abbootii keessan kan jalqabaati.');
INSERT INTO "apaguna" VALUES(44,9,'44:9','Dhugumatti isaan shakkii keessa taphatu.');
INSERT INTO "apaguna" VALUES(44,10,'44:10','Kanaafuu, guyyaa samiin aara ifa ba’aan dhuftu (waan isaan mudatu) eegi.');
INSERT INTO "apaguna" VALUES(44,11,'44:11','(Aarri kun) namoota haguugaKun adabbii laalessaadha.');
INSERT INTO "apaguna" VALUES(44,12,'44:12','(Kaafiroonni yeroo adabicha argan) “Gooftaa keenya! Adabicha nurraa kaasi nuti ni amannaa” (jedhu).');
INSERT INTO "apaguna" VALUES(44,13,'44:13','Odoo ergamaan ifa baasaan isaanitti dhufee jiruu, (erga adabaatii) gorfamuun akkamitti isaaniif jiraata?');
INSERT INTO "apaguna" VALUES(44,14,'44:14','Ergasii isa irraa gara galanii “(Inni) barsiifamaa maraatuudha” jedhan.');
INSERT INTO "apaguna" VALUES(44,15,'44:15','Nuti yeroo gabaabaaf adabbii saaqoodha; Isin (kufriitti) deebi’oodha.');
INSERT INTO "apaguna" VALUES(44,16,'44:16','Guyyaa qabaa guddaa qabnu Nuti haaloo bahatoodha.');
INSERT INTO "apaguna" VALUES(44,17,'44:17','Dhugumatti, Nuti isaan dura ummata Fir’awni mokkorree jirraErgamaan kabajamaanis isaanitti dhufe.');
INSERT INTO "apaguna" VALUES(44,18,'44:18','“Ani isiniif ergamaa amanamaadhaa, gabroota Rabbii gara kiyya kennaa!” jechuun (isaanitti dhufe).');
INSERT INTO "apaguna" VALUES(44,19,'44:19','“Rabbi irratti hin booninaa; ani ragaa ifa ta’een isinitti dhufaadha” jachuunis.');
INSERT INTO "apaguna" VALUES(44,20,'44:20','“Ani akka dhagaan tumtanii nan ajjeefne Gooftaa kiyyaafi Gooftaa keessanittin maganfadhe.');
INSERT INTO "apaguna" VALUES(44,21,'44:21','Yoo naaf hin amanne narraa fagaadhaa.”');
INSERT INTO "apaguna" VALUES(44,22,'44:22','Gooftaa isaa ni kadhate: “Isaan kun ummata yakkamtoota” (jedhe).');
INSERT INTO "apaguna" VALUES(44,23,'44:23','(Rabbiin ni jedhe): “Gabroota kiyyaan halkaniin deemiDhugumatti isin hordofamtoota.');
INSERT INTO "apaguna" VALUES(44,24,'44:24','Galaana akkuma banametti dhiisiDhugumatti, isaan garee bishaaniin dhidhimfamoodha.”');
INSERT INTO "apaguna" VALUES(44,25,'44:25','Ashaakiltiifi burqituuwwan meeqaatama dhiisan.');
INSERT INTO "apaguna" VALUES(44,26,'44:26','Oyruufi iddoo jireenyaa gaggaariis.');
INSERT INTO "apaguna" VALUES(44,27,'44:27','Qananii ishee keessatti gammadaa turanis.');
INSERT INTO "apaguna" VALUES(44,28,'44:28','Akka kanatti ummata biraa ishee dhaalchifne.');
INSERT INTO "apaguna" VALUES(44,29,'44:29','Samiifi dachiin isaan irratti hin boonye; kan tursiifanis hin turre.');
INSERT INTO "apaguna" VALUES(44,30,'44:30','Dhugumatti, Ilmaan Israa’iil adabbii salphisaa irraa nagaya baafne.');
INSERT INTO "apaguna" VALUES(44,31,'44:31','Fir’awni irraa (ni baraarsine)Inni boonaa, daangaa dabraa irraa ta’eera.');
INSERT INTO "apaguna" VALUES(44,32,'44:32','Nuti beekumsaan aalama hunda irra isaan filanne.');
INSERT INTO "apaguna" VALUES(44,33,'44:33','Raajiiwwan irraas waan mokkorriin ifa ta’e isa keessa jiru isaaniif kennine.');
INSERT INTO "apaguna" VALUES(44,34,'44:34','Dhugumatti, isaan kun (Qureeyshonni) ni jedhu:');
INSERT INTO "apaguna" VALUES(44,35,'44:35','“Du’a keenya kan jalqabaa malee isheen (biraa) hin jirtuNuti (kana booda) hin kaafamnu.');
INSERT INTO "apaguna" VALUES(44,36,'44:36','Yoo warra dhugaa dubbatu taataniif, abbootii keenya fidaa.”');
INSERT INTO "apaguna" VALUES(44,37,'44:37','Sila isaantu caala moo ummata Tubba’iifi warra isaan duraati? Isaan yakkamtoota taanaan isaan balleessine.');
INSERT INTO "apaguna" VALUES(44,38,'44:38','Samii, dachiifi waan isaan lamaan gidduu jirus taphatoo taanee hin uumne.');
INSERT INTO "apaguna" VALUES(44,39,'44:39','Dhugaan malee isaan lachan hin uumneGaruu irra hedduun isaanii hin beekanu.');
INSERT INTO "apaguna" VALUES(44,40,'44:40','Dhugumatti, Guyyaan murtii beellama isaan hundaati.');
INSERT INTO "apaguna" VALUES(44,41,'44:41','Guyyaa jaalalleen jaalallee irraa homaa hin deebifne, kan isaanis (kaafiroonnis) hin tumsamneedha.');
INSERT INTO "apaguna" VALUES(44,42,'44:42','Nama Rabbiin rahmata godheef maleeInni Isumatu injifataa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(44,43,'44:43','Dhugumatti, mukni ‘Zaqquum’');
INSERT INTO "apaguna" VALUES(44,44,'44:44','Nyaata dilaawaati.');
INSERT INTO "apaguna" VALUES(44,45,'44:45','Akka cumaa (siicoo) zaytiitti garaa keessatti danfa.');
INSERT INTO "apaguna" VALUES(44,46,'44:46','Akka danfuu bishaan ho’aatti.');
INSERT INTO "apaguna" VALUES(44,47,'44:47','(Ni jedhama): “Isa qabaatii gara walakkaa ibidda boba’aa harkisaa.');
INSERT INTO "apaguna" VALUES(44,48,'44:48','Ergasii mataa isaa gubbaatti adabbii bishaan danfaa irraa ta’e dhangalaasaa.');
INSERT INTO "apaguna" VALUES(44,49,'44:49','Dhandhami; dhugumatti, ati simatu cimaa, arjaa (of fakkeessuu) turtee!');
INSERT INTO "apaguna" VALUES(44,50,'44:50','Inni kun waan isa mamaa turtan sani” (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(44,51,'44:51','Warri Rabbiin sodaatan iddoo nagayaa keessa.');
INSERT INTO "apaguna" VALUES(44,52,'44:52','Ashaakiltiifi burqituuwwan keessa.');
INSERT INTO "apaguna" VALUES(44,53,'44:53','Walitti gara galoo ta’anii (uffata) hariira haphiifi hariira furdaa irraa ta’e uffatu');
INSERT INTO "apaguna" VALUES(44,54,'44:54','Akkuma kana dubra adaadii ija babala’aa isaan fuusifne');
INSERT INTO "apaguna" VALUES(44,55,'44:55','Achi keessattis nagaa ta’anii fuduraalee hunda waammatu.');
INSERT INTO "apaguna" VALUES(44,56,'44:56','Ishee keessatti du’a jalqabaa malee du’a hin dhandhamanRabbiin adabbii ibidda boba’aa irraa isaan tikse.');
INSERT INTO "apaguna" VALUES(44,57,'44:57','Tola Gooftaa kee irraa ta’eefSun isumatu milkii guddaadha');
INSERT INTO "apaguna" VALUES(44,58,'44:58','Akka gorfamaniif jacha isa (Qur’aana) arraba keetti laaffifne.');
INSERT INTO "apaguna" VALUES(44,59,'44:59','(Waan isaan mudatu) eegi; isaanis eegoodha.');
INSERT INTO "apaguna" VALUES(45,1,'45:1','Haa Miim.');
INSERT INTO "apaguna" VALUES(45,2,'45:2','Bu’iinsi Qur’aanaa Rabbii Injifataa, ogeessa ta’e irraayi');
INSERT INTO "apaguna" VALUES(45,3,'45:3','Dhugumatti, samiifi dachii keessa mu’uminootaaf mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(45,4,'45:4','Uumama keessaniifi lubbu qabeeyyii irraa waan Inni (Rabbiin dachii keessa) facaasu keessas namoota dhugaan amananiif raajiiwwantu jiru.');
INSERT INTO "apaguna" VALUES(45,5,'45:5','Wal dhabuu halkanii, guyyaatiifi rooba irraa waan Rabbiin duumeessa irraa buusee, dachii eega goggogina isheetii ittiin magarseefi qilleensa gara gaggalchuu keessa warra xinxalaniif mallattooleen taatuudha.');
INSERT INTO "apaguna" VALUES(45,6,'45:6','(Yaa Muhammad!) Kunniin keeyyattoota Rabbiitii, dhugaan sirratti dubbifnaSila erga Rabbiifi keeyyattoota Isaatii oduu akkamii amanu?');
INSERT INTO "apaguna" VALUES(45,7,'45:7','Cufa nama akkaan kijibaa, dilaawaa ta’eetiif laga ibiddaatu jira.');
INSERT INTO "apaguna" VALUES(45,8,'45:8','Keeyyattoota Rabbii kan isa irratti dubbifaman dhaga’aa; ergasii akka waan ishee hin dhaga’iniitti boona isaa itti fufaadabbii laalessaan isa gammachiisi.');
INSERT INTO "apaguna" VALUES(45,9,'45:9','Yeroo keeyyattoota keenya irraa waa beeke qishina (qoosaa) ishee godhataWarra san adabbii xiqqeessaa ta’etu isaaniif jira.');
INSERT INTO "apaguna" VALUES(45,10,'45:10','Fuuldura isaanii jahannamtu jiraWanti isaan horatanis ta’ee wanti isaan Rabbiin ala jaalallee godhatanis homaa isaan irraa hin deebisanuIsaaniif adabbii guddaatu jira.');
INSERT INTO "apaguna" VALUES(45,11,'45:11','(Qur’aanni) kun qajeelfamaIsaan keeyyattoota Gooftaa isaaniitti kafaran, qooda adabbii laalessaa irraa ta’etu isaaniif jira.');
INSERT INTO "apaguna" VALUES(45,12,'45:12','Rabbiin Isa galaana, akka dooniwwan ajaja Isaatiin isa keessa deeman, akka tola Isaa irraa kajeeltaniifi akka Isa galateeffattaniif jecha isiniif laaffise sani,');
INSERT INTO "apaguna" VALUES(45,13,'45:13','Waan samii keessa jiruufi waan dachii keessa jiru hunda (laaffisuu) Isa irraa ta’e isiniif laaffiseDhugumatti, kana keessa ummattoota xiinxalaniif mallattooleetu jira.');
INSERT INTO "apaguna" VALUES(45,14,'45:14','(Yaa Muhammad!) Warra amananiin, “warra guyyoota Rabbii hin sodaanneef dhiifama godhaa” jedhiAkka Inni namootaaf waan isaan dalaganiin mindaa kennuuf (dhiifama godhaa).');
INSERT INTO "apaguna" VALUES(45,15,'45:15','Namni toltuu hojjate lubbuma isaatiifiNamni badii dalages isheedhuma irrattiErgasii gara Gooftaa keessanii deebifamtu');
INSERT INTO "apaguna" VALUES(45,16,'45:16','Dhugumatti, ilmaan Israa’iliif kitaaba, hubannoo seera isaatiifi nabiyyummaas kennineerraWantoota gaggaarii irraas isaaniif kenninee, aalama (yeroo isaanii) hunda irras isaan caalchifne.');
INSERT INTO "apaguna" VALUES(45,17,'45:17','Shari’aa irraas waan ifa ta’e isaaniif kannineErga beekumsi isaanitti dhufee booda, jibbansa gidduu isaanii jiruuf malee (waan birootiif) wal hin dhabneDhugumatti, Gooftaan kee waan isaan isa keessatti wal dhabaa turan keessatti Guyyaa Qiyaamaa gidduu isaanitti murteessa.');
INSERT INTO "apaguna" VALUES(45,18,'45:18','Ergasii karaa ajaja (keenya) irraa ta’e irra si keenyee, ishee hordofiFedha lubbuu warra homaa hin beeknee hin hordofin.');
INSERT INTO "apaguna" VALUES(45,19,'45:19','Isaan homaa (adabbii) Rabbii irraa si hin duroomsanMiidhaa hojjattoonni gariin isaanii jaalallee gariitiRabbiin immoo jaalallee warra Isa sodaataniiti.');
INSERT INTO "apaguna" VALUES(45,20,'45:20','(Qur’aanni) kun namootaaf beekumsa ifa ba’aadhaWarra dhugoomsaniif qajeelfamaafi rahmata.');
INSERT INTO "apaguna" VALUES(45,21,'45:21','Sila warri hamtuu hojjatan, jiraa isaaniitiifi du’aa isaaniitis akka warra amananii toltuu hojjatanii gochuu keenya yaadanii? Wanti isaan murteessan waa fokkate!');
INSERT INTO "apaguna" VALUES(45,22,'45:22','Odoo hin miidhamne akka lubbuun hundi mindeeffamtuuf, Rabbiin samiifi dachii haqaan uume.');
INSERT INTO "apaguna" VALUES(45,23,'45:23','Sila nama gabbaramaa isaa fedhii isaa godhate, kan Rabbiin beekumsaan isa jallisee, dhageettii isaatiifi onnee isaa irratti cufee, ija isaa irrattis haguuggaa godhe mee naaf himi? Erga Rabbiin (jallisee) booda eenyutu isa qajeelcha? Sila hin gorfamtanuu?');
INSERT INTO "apaguna" VALUES(45,24,'45:24','“Jireenya keenya kan addunyaa malee isheen (jiruun biraa) hin jirtu; ni duuna; (gariin keenyas) ni jiraanna; yeroo malee wanti nu balleessu hin jiru” jedhanWaan kanatti beekumsa homaatuu hin qabanuIsaan se’oo malee hin taane.');
INSERT INTO "apaguna" VALUES(45,25,'45:25','Yeroo keeyyattoonni keenya ifa ba’anii isaan irratti dubbifamanis, ragaan isaanii “yoo kan dhugaa dubbattan taataniif abbootii keenya fidaa” jechuu malee hin taane.');
INSERT INTO "apaguna" VALUES(45,26,'45:26','(Yaa Muhammad!) Jedhi: “Rabbumatu isin jiraachisee, ergasii isin ajjeesa; ergasii Guyyaa Qiyaamaa keessatti walitti isin qabaIsa keessa mamiin hin jiruGaruu irra hedduun namaa hin beekanu.”');
INSERT INTO "apaguna" VALUES(45,27,'45:27','Mootummaan samiifi dachii kan RabbiitiGuyyaa Qiyaaman dhaabbattus, guyyaa san warri dharaa ni hoonga’u');
INSERT INTO "apaguna" VALUES(45,28,'45:28','(Guyyaa san) ummata hunda jilbiiffatanii argitaUmmanni hunduu gara kitaaba isheetti waamamti“Har’a waan dalagaa turtan mindeeffamtu” (isaaniin jedhama)');
INSERT INTO "apaguna" VALUES(45,29,'45:29','Kun kitaaba keenya kan isin irratti dhugaan dubbatuudhaDhugumatti, Nuti waan isin dalagaa turtan galmeessuu turre.');
INSERT INTO "apaguna" VALUES(45,30,'45:30','Isaan amananii, toltuu dalagan immoo Gooftaan isaanii rahmata Isaa keessa isaan seensisaKun isumatu milkii ifa galaadha.');
INSERT INTO "apaguna" VALUES(45,31,'45:31','Isaan kafaran immoo “sila keeyyatoonni Kiyya isin irratti dubbifamaa turtee, boontanii, ummata badoo hin turree?” (jedhamaan).');
INSERT INTO "apaguna" VALUES(45,32,'45:32','Yeroo “waadaan Rabbii dhugaadha; Qiyaamaan akka dhuftu mamiin ishee keessa hin jiru” jedhame, “Qiyaamaan maal akka taate hin beeknu; mamii malee yaada (ishee) hin qabnuNuti dhugoomsoo hin taane” jettan.');
INSERT INTO "apaguna" VALUES(45,33,'45:33','Hamtuun waan dalaganii ifa isaaniif ba’a; wanti isaan itti qishnaa (qoosaa) turanis isaan marsa.');
INSERT INTO "apaguna" VALUES(45,34,'45:34','Ni jedhamaani: “Har’a akkuma isin qunnamtii guyyaa keessan kanaa dagattan (Nutis) isin daganna (isin dhiifna); teessoon keessanis ibidda; tumsitoonnis isiniif hin jiranu.”');
INSERT INTO "apaguna" VALUES(45,35,'45:35','Kun waan isin keeyyattoota Rabbii qishnaa (qoosaa) taasiiftaniifi waan jiruun addunyaa isin gowwoomsiteefiHar’a ishee (ibidda) irraa hin baafamanu; isaan ceephoos hin buufachiifamanu.');
INSERT INTO "apaguna" VALUES(45,36,'45:36','Faaruun kan Rabbii Gooftaa samiifi Gooftaa dachii, Gooftaa aalama hundaati.');
INSERT INTO "apaguna" VALUES(45,37,'45:37','Samiifi dachii keessatti boonni IsumaafiInnis injifataa, ogeessa.');
INSERT INTO "apaguna" VALUES(46,1,'46:1','Haa Miim.');
INSERT INTO "apaguna" VALUES(46,2,'46:2','Bu’iinsi Qur’aanaa Rabbii injifataa, ogeeysa ta’e irraayi.');
INSERT INTO "apaguna" VALUES(46,3,'46:3','Samii, dachiifi waan isaan lamaan gidduu jiranis dhugaafi yeroo beekamaa ta’eef malee hin uumneIsaan kafaran waan ittiin sodaachifaman irraa garagaloodha.');
INSERT INTO "apaguna" VALUES(46,4,'46:4','(Yaa Muhammad!) jedhi: “wantoota Rabbiin ala gabbartan naaf himaa? Dachii irraa waan isaan uuman natti agarsiisaaMoo samii (uumuu) keessatti shariikummaan isaaniif jira? Yoo dhugaa dubbattoota taataniif mee kitaaba (Qur’aana) kana dura (bu’e) yookiin hambaa beekumsa durii wahii naaf dhiyeessaa.”');
INSERT INTO "apaguna" VALUES(46,5,'46:5','Nama Rabbiin ala waan hanga Guyyaa Qiyaamaatti homaa isaaf hin owwaanneefi isaan kadhaa isaanii irraa dagatoo ta’an gabbaru caalaa jallataan eenyu?');
INSERT INTO "apaguna" VALUES(46,6,'46:6','Yeroo ilmi namaa walitti qabamu diinota isaaniif ta’uIbaadaa isaaniittis kafaroo ta’u.');
INSERT INTO "apaguna" VALUES(46,7,'46:7','Yeroo keeyyatoonni keenya ifa ta’anii isaan irratti dubbifaman, isaan kafaran dhugaan isaanitti dhufnaan “kun Sihrii ifa galaadha” jedhan.');
INSERT INTO "apaguna" VALUES(46,8,'46:8','Yookiin “(Muhammadtu SAW) sobaan isa uume” jedhu(Yaa Muhammad!) “yoo ani sobaan isa uumeef (adabbii) Rabbii irraa naaf tumsuu hin dandeessanuInni (Rabbiin) waan isin keessa galtan akkaan beekaadhaAnaafi isin giddutti ragaaf Inni ga’aadhaInni araaramaa, rahmata godhaadha” jedhi.');
INSERT INTO "apaguna" VALUES(46,9,'46:9','(Yaa Muhammad!) jedhi: “Ani ergamtoota irraa isa jalaqabaatii mitiWaan anaafi isin irratti dalagamuuf jirus hin beekuWaan gara kiyyatti wahyii godhamu malee (wanta biraa) hin hordofuAni sodaachisaa ifa bahaadha malee waan biraatii miti.”');
INSERT INTO "apaguna" VALUES(46,10,'46:10','“Mee naaf himaa! Yoo (Qur’aanni kun) odoma isin itti kafartanuu Rabbi biraa ta’e, raga bahaan Ilmaan Israa’ilii irraa ta’e fakkaataa isaa irratti ragaa bahee yoo amanee isin immoo boontan, (isin miidhaa hin hojjannee)? Rabbiin miidhaa raawwattoota hin qajeelchu” jedhi.');
INSERT INTO "apaguna" VALUES(46,11,'46:11','Isaan kafaran isaan amananiin ni jedhu: “Odoo inni (Qur’aanni) waan gaarii ta’ee silaa gara isaatti nu hin dursanu ture.” Isaan eega hin qajeelinii “kun dhara duriiti” jechuuf jiru.');
INSERT INTO "apaguna" VALUES(46,12,'46:12','Isa (Qur’aana) duras kitaaba Muusaa kan qajeelfamaafi rahmata ta’etu tureKunis kitaaba dhugoomsaa, akka warra miidhaa hojjatan sodaachisee, toltuu hojjattoota immoo gammachiisuuf Afaan Arabaatiin (bu’ee)dha.');
INSERT INTO "apaguna" VALUES(46,13,'46:13','Isaan “Gooftaan keenya Allaahdha” jedhanii ergasii (sana irratti) gadi dhaabbatan, sodaan isaan irra hin jiru; isaan hin gaddanis.');
INSERT INTO "apaguna" VALUES(46,14,'46:14','Isaan sun warra jannataati; ishee keessa yeroo hunda jiraatu; mindaa waan dalagaa turaniif (isaanif kenname).');
INSERT INTO "apaguna" VALUES(46,15,'46:15','Namootaaf abbaafi haadha isaatti tola ooluu dhaamneHaati isaa rakkoon isa baadhattee, rakkoon isa deesseUlfaafi harma gu’uun isaa ji’oota soddoma; yeroo jabina isaa ga’ee, waggaa afurtama ga’u “Gooftaa kiyya! Ani ni’imaa kee kan Ati ana, haadhaafi abbaa kiyya irratti oolte galateeffachuufi dalagaa gaarii Ati jaallattu hojjachuu na qabsiisiSanyii (hortee) kiyyas gaarii taasisiAni gara kee tawbaa deebi’eeraAni Muslimtoota irraayi” jedhe.');
INSERT INTO "apaguna" VALUES(46,16,'46:16','Isaan sun warra irra gaggaarii wantoota isaan dalaganii nuti irraa qeeballee hamtuu isaanii dhiifama goonufi(Fuula duras) namoota Jannataa keessatti ta’u(Kun) waadaa dhugaa kan isaaniif galamaa ture.');
INSERT INTO "apaguna" VALUES(46,17,'46:17','Inni abbaafi haadha isaatiin “isin lachaniifuu ish (uffi)! Kanuma dhaloonni baay’een dabrani jiranu, akka ani kaafamu natti himtuu?” jedheHaala isaan lamaan birmachuu Rabbii gaafachaa, “yaa badii kee! Beellamni Rabbii dhugaadhaa amani” jedhanii gorsanuu “kun ogbarruu warra duriiti malee waa biraa miti” jedha;');
INSERT INTO "apaguna" VALUES(46,18,'46:18','Isaan sun warra ummattoota isaan dura dabran kan jinnootaafi namoota irraa ta’an keessatti jechi (adabbii) isaan irratti mirkanaa’eedhaIsaan hoonga’oota ta’aniiru.');
INSERT INTO "apaguna" VALUES(46,19,'46:19','Hundaafuu waan dalagan irraa sadarkaatu jiraOdoo isaan hin miidhamne, hojii isaanii isaanii guutuuf (mindeessa).');
INSERT INTO "apaguna" VALUES(46,20,'46:20','Guyyaa isaan kafaran ibidda irra fidaman: “(wantoota) gaggaarii keessan jireenya keessan kan addunyaa keessatti dabarfattanii itti fayyadamtanii turtanHar’a immoo waan dachii keessatti dhugaan ala boonaa turtaniifi waan fincilaa turtaniif adabbii salphisaa mindeeffamtu (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(46,21,'46:21','Yeroo obboleessi Aad (Huud) Ahqaafitti ummata isaa dinniine yaadadhuIsa duraafi isa boodas sodaachiftoonni; “Allaah malee waan biraa hin gabbarinaa; ani adabbii guyyaa guddaan isin irratti sodaadha” (jechuun) dabartee turte.');
INSERT INTO "apaguna" VALUES(46,22,'46:22','(Ummanni isaas) ni jedhan: “Sila ati gooftolii keenya irraa akka gara nu galchituuf nutti dhuftee? Yoo warra dhugaa dubbatan irraa taateef waan nuun sodaachiftuun nutti koottu.”');
INSERT INTO "apaguna" VALUES(46,23,'46:23','(Huud) ni jedhe: “Beekumsi (adabbittii) Allaah bira jiraAni waanuman ittiin ergame qofan isinitti geessaGaruu ani ummata wollaaloo ta’uun isin arga.”');
INSERT INTO "apaguna" VALUES(46,24,'46:24','Yeroo isa (adabicha) (duumessa fakkaatee) gara laggeen isaanii garagalee dhufaa ta’uu argan: “kun duumessa rooba nuuf kennuu dhufe” jedhan“Lakkisaa! Inni waan jarjarfachaa turtan sanii; bubbee adabbiin laalessaan isa keessa jiruudha” (jedhaman).');
INSERT INTO "apaguna" VALUES(46,25,'46:25','Waan hunda ajaja Rabbii isheetiin balleessitiManneen isaanii malee wanti tokkos kan hin mul’anne ta’anAkka kanatti namoota badii hojjatan mindeessina.');
INSERT INTO "apaguna" VALUES(46,26,'46:26','(Yaa Qureyshotaa!) dhugumatti, Nuti waan isiniif hin mijeessin isaaniif mijeessinee turreDhageettii, agartuufi qalbiis isaaniif gooneeDhageettiin, agartuufi qalbiin isaanii, waan aayatoota keenya mormaa turaniif homaa isaan irraa hin deebifneWanti isaan qishnaa itti godhaa turan isaanitti marse.');
INSERT INTO "apaguna" VALUES(46,27,'46:27','Dhugumatti, magaalota (ummatoota) moggaa keessan jiran gidduu (heddu) balleessinee jirraMallattoolee (keenyas) karaa adda addaatiin isaan argisiifne.');
INSERT INTO "apaguna" VALUES(46,28,'46:28','Maaliif wantoonni isaan akka Rabbiitti isaan dhiheessituuf gabbaramoo godhatan isaan hin tumsin? Lakkisaa! (yeroo san) gonkumaa isaan irraa dheessanKunis kijiba isaanitiifi waan isaan sobaan uumanii haasa’aa turaniidha.');
INSERT INTO "apaguna" VALUES(46,29,'46:29','(Yaa Muhammad!) Yeroo garee jinnoota irraa ta’e kan Qur’aana dhageeffatan ta’anii gara keetti garagalchine [yaadadhu]Yeroo isa bira dhufan “cal’isaa dhageeffadhaa” (waliin) jedhanYeroo inni xumuramu immoo dinniintota ta’anii gara ummattoota isaanii deebi’an.');
INSERT INTO "apaguna" VALUES(46,30,'46:30','Ni jedhan: “Yaa ummata keenya! Nuti kitaaba Muusaa booda bu’e, kan waan isa duraa dhugoomsaa ta’e, kan gara dhugaafi gara karaa sirrii qajeelchu dhageenyee jirra.');
INSERT INTO "apaguna" VALUES(46,31,'46:31','Yaa ummata keenya! Waamaa Rabbii (nabi Muhammadiin) tole jedhaa; isattis amanaaInni (Rabbiin) badii keessan irraa isiniif araarama; adabbii laalessaa irraas isin baraara.');
INSERT INTO "apaguna" VALUES(46,32,'46:32','Namni waamaa Rabbii tole hin jenne, dachii keessatti miliqee kan ba’uu miti; isaan (Allaahn) alas gargaaraan biraa isaaf hin jiruWarri sun jallina ifa ta’e keessa jiru.”');
INSERT INTO "apaguna" VALUES(46,33,'46:33','Sila Rabbiin, Isa samiifi dachii uumee, isaan uumuu keessatti dadhabbiin itti hin dhaga’amin, du’aa kaasuu irratti danda’aa akka ta’e hin arginee? Eeyyeen; Inni waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(46,34,'46:34','Guyyaa isaan kafaran ibidda irratti fidaman “sila kun dhugaa mitii?” (isaanin jedhama)Isaanis “Eeyyeen; Gooftaa keenyatti kakannee! (Dhugaa dha)” jedhuInnis “Waan (Rabbitti) kafaraa turtaniif adabbii kana dhandhamaa” (isaaniin) jedha.');
INSERT INTO "apaguna" VALUES(46,35,'46:35','(Yaa Muhammad!) Akkuma ergamtoota irraa warri murannoo qabu obsetti, obsiIsaanitti hin ariifatinGuyyaa waan waadaa isaaniif galame argan akka waan guyyaa irraa sa’aatii tokko qofa malee hin jiraatiniitti of lakkaa’u(Qur’aanni) ga’aadhaSila finciltoota malee namni biraa ni badaa?');
INSERT INTO "apaguna" VALUES(47,1,'47:1','Warra kafaranii, karaa Rabbii irraa [namoota] deebisan [Rabbiin] hojiiwwan isaanii balleesseera.');
INSERT INTO "apaguna" VALUES(47,2,'47:2','Warra amananii, dalagaa gaggaarii hojjatanii, waan Muhammad irratti buufametti ‘inni dhugaa Gooftaa isaanii irraa ta’e’ jechuu amanan, (Rabbiin) dilii isaanii isaan irraa haqeeti, haala isaaniis tolche.');
INSERT INTO "apaguna" VALUES(47,3,'47:3','Kun waan warri kafaran dhara bu’ura hin qabne hordofaniifi warri amanan immoo dhugaa Gooftaa isaanii biraa ta’e hordofaniifiAkka kanatti Rabbiin fakkeenya isaanii ilma namaatiif taasisa.');
INSERT INTO "apaguna" VALUES(47,4,'47:4','Yeroo warra kafare qunnamtan morma dhawaaYeroo isaan baay’ee ajjeeftan, hidhaa jabeessaaSana booda isaan irratti tola oolaa yookiin waa irraa fudhaa, hanga lolli qara (ho’a) ishee dhiiftutti (kana godhaa)Dubbiin kanaRabbiin odoo fedhee isaan irraa haaloo bahataGaruu (isaaniin loluutti wanni isin ajajeef) garii keessan gariin mokkoruuf jechaWarra karaa Rabbii keessatti ajjeefaman hojii isaanii hin balleessu');
INSERT INTO "apaguna" VALUES(47,5,'47:5','Fuulduratti isaan ni qajeelcha; haala isaaniis ni tolcha.');
INSERT INTO "apaguna" VALUES(47,6,'47:6','Jannata isaan seensisa; ishee isaanitti beeksisa.');
INSERT INTO "apaguna" VALUES(47,7,'47:7','Yaa warra amantan! Yoo (diin) Rabbii tumsitan, Inni isin tumsaFaana keessanis gadi dhaaba.');
INSERT INTO "apaguna" VALUES(47,8,'47:8','Warri kafaran badiin isaaniif ta’aadha(Rabbiin) dalagaa (hojii) isaaniis ni balleessa.');
INSERT INTO "apaguna" VALUES(47,9,'47:9','Kun waan isaan (Qur’aana) Rabbiin buuse jibbaniifiKanaafuu (Rabbiin) dalagaa isaanii duraa balleesse.');
INSERT INTO "apaguna" VALUES(47,10,'47:10','Sila dachii keessa deemanii booddeen warra isaan duraa maal akka ta’e hin ilaallee? Rabbiin guutumatti isaan barbadeesseKaafirootaaf fakkaattuun ishee taatuudha.');
INSERT INTO "apaguna" VALUES(47,11,'47:11','Kun waan Allaahn gargaaraa warra amananii ta’eefi kaafirootaaf immoo gargaaraan waan hin jirreefi.');
INSERT INTO "apaguna" VALUES(47,12,'47:12','Rabbiin warra amananii, hojiiwwan gaggaarii hojjatan jannata laggeen isaan keessa yaa’u seensisaWarri kafaran immoo (addunyaa irratti) qanani’aniiti, akkuma beelladoonni nyaatanitti nyaachaa jiraatanIbiddi bakka jireenya isaaniiti.');
INSERT INTO "apaguna" VALUES(47,13,'47:13','Baay’ee magaalotaa, kan magaalaa kee ishee (of irraa) si baafte (Makkaan) irra ciccimoo ta’an balleessinee jirraHomaa tumsaan isaaniif hin jiru.');
INSERT INTO "apaguna" VALUES(47,14,'47:14','Sila inni ragaa ifa ba’aa Gooftaa isaa irraa ta’e irra jiru akka nama dalagaan hamtuun isaa isaaf miidhagfamee, fedha lubbuu isaanii hordofanii ni ta’aa?');
INSERT INTO "apaguna" VALUES(47,15,'47:15','Fakkeenyi jannata warra Rabbiin sodaataniif waadaa galamee (akka kana): ishee keessa laggeen bishaan hin xiraahin irraa ta’e, laggeen aannan dhandhamni isaa hin jijjiiramin irraa ta’e, laggeen daadhii namoota dhuganiif mi’aayaa ta’e irraa ta’eefi laggeen damma qulqulleeffame (nadhii) irraa ta’etu jiraAmmas isaaniif ishee (jannata) keessa fuduraalee hundaafi araarama Gooftaa isaanii irraa ta’etu jira(Sila isaan kun) akka namoota ibidda keessa yeroo hunda jiraataniifi bishaan danfaa obaafamanii, inni marrimaan isaanii cicciruutii?');
INSERT INTO "apaguna" VALUES(47,16,'47:16','Isaan irraa namoota si dhaggeeffatee yeroo si biraa gadi bahe, warra beekkumsa kennamaniin “reefu maal jedhe?” jedhantu jiraIsaan sun warra qalbii isaanii irratti Rabbiin cufee, fedhii isaanii hordofaniidha');
INSERT INTO "apaguna" VALUES(47,17,'47:17','Isaan karaa qajeelaa hordofan, (Rabbiin) qajeeluma isaaniif dabalee, sodaa isaanii isaaniif kenne.');
INSERT INTO "apaguna" VALUES(47,18,'47:18','Sila (Qiyaaman) tasa isaanitti dhufuu malee maal eegu? Mallattooleen ishee dhufaniiruYeroo isheen isaanitti dhufte gorfamuun isaanii akkamitti isaaniif ta’a?');
INSERT INTO "apaguna" VALUES(47,19,'47:19','Rabbiin malee dhugaan gabbaramaan biraa akka hin jirre beeki; dilii keetiifis araarama (Rabbii) kadhu; mu’uminoota dhiiraafi dubartootaatiifis (kadhu)Rabbiin sochii keessaniifi iddoo isin qubattanis ni beeka.');
INSERT INTO "apaguna" VALUES(47,20,'47:20','Warri amanan “odoo suuraan (jihaada ajajju) buufamtee maal qaba?” jedhuYeroo Suuraan heera tumtu buufamtee ishee keessatti lolli dubbatame, warra onnee isaanii keessa dhukkubni jiru ilaalcha nama machii du’aa keessa jiruu kan gara kee ilaalu argitaBadiin isaaniif haa ta’u.');
INSERT INTO "apaguna" VALUES(47,21,'47:21','Ajaja fudhachuufi jecha gaariitu (isaaniif caala)Yeroo dhimmichi murtaa’e odoo Rabbitti dhugaa haasa’anii irra isaaniif caala ture.');
INSERT INTO "apaguna" VALUES(47,22,'47:22','Sila yoo duubatti deebitan dachii keessatti badii hojjachuufi firooma keessan muruu kajeeltanii?');
INSERT INTO "apaguna" VALUES(47,23,'47:23','Isaan sun warra Rabbiin isaan abaaree gurra isaaniis duuchee ija isaaniis jaamseedha.');
INSERT INTO "apaguna" VALUES(47,24,'47:24','Sila Qur’aana hin xiinxalanii? Moo onnee isaanii irra cufaa isheetu jira?');
INSERT INTO "apaguna" VALUES(47,25,'47:25','Isaan erga karaan qajeelaan ifa isaaniif ta’ee booda duubatti jedhan, sheyxaanatu (hojii gadhee) isaanitti bareechee hamilee isaanitti godhe.');
INSERT INTO "apaguna" VALUES(47,26,'47:26','Kun waan isaan warra waan Rabbiin buuse jibbaniin “dhimma garii keessatti ajaja keessan fudhanna” jedhaniifRabbiin iccitii isaanii ni beeka.');
INSERT INTO "apaguna" VALUES(47,27,'47:27','Yeroo maleeykonni fuula isaaniitiifi dugda isaanii rukutuun isaan ajjeesan akkam tahuu taa’an?');
INSERT INTO "apaguna" VALUES(47,28,'47:28','Kun waan isaan wanta Rabbiin dallansiisu hordofanii jaalala Isaa (Rabbii) jibbaniifiKanaafuu (Rabbiin) dalagaa isaanii balleesse.');
INSERT INTO "apaguna" VALUES(47,29,'47:29','Sila warri onnee isaanii keessa dhukkubni jiru Rabbiin jibbansa garaa isaanii keessa jiru hin baasuu yaadanii?');
INSERT INTO "apaguna" VALUES(47,30,'47:30','Odoo feenee isaan sitti argisiifneeti, mallattoolee isaanitiin isaan beekta turteErgaa jechootaatiinis isaan ni beektaRabbiin dalagaa keessan ni beeka.');
INSERT INTO "apaguna" VALUES(47,31,'47:31','Hanga qabsaa’otaafi obsitoota isin irraa adda baafnee, oduu keessanis hanga mul’ifnutti isin mokkorra.');
INSERT INTO "apaguna" VALUES(47,32,'47:32','Isaan kafaranii karaa Rabbii irraa dhorgan, erga karaan qajeelaan isaaniif ifa ta’e booda ergamaa (Rabbii) faallessan, homaa Rabbiin hin miidhanu(Rabbiin) dalagaa isaanii balleessuuf jira.');
INSERT INTO "apaguna" VALUES(47,33,'47:33','Yaa warra amantan! Rabbiif ajajamaa; ergamaa (Isaatifis) ajajamaa; dalagaawwan keessanis hin balleessinaa.');
INSERT INTO "apaguna" VALUES(47,34,'47:34','Isaan kafaranii (namoota) karaa Rabbii irraa dhorganii ergasii kafaroo ta’anii du’an, Rabbiin isaaniif hin araaramu');
INSERT INTO "apaguna" VALUES(47,35,'47:35','Hin dadhabinaa; haala isinumti gubbaa aantanii jirtanuun gara araaramaatti hin waaminaaRabbiin isin waliin jira; (mindaa) dalagaa keessaniis hin hir’isu.');
INSERT INTO "apaguna" VALUES(47,36,'47:36','Jireenyi addunyaa taphaafi yeroo dabarfannaadhaYoo amantanii Rabbiin sodaattan mindaa keessan isiniif kenna; qabeenya keessan isin hin gaafatu.');
INSERT INTO "apaguna" VALUES(47,37,'47:37','Yoo ishee isin gaafatee (gaafii) isinitti bay’ise isin ni dhorgattu turtan; Innis (Rabbiinis) iccitii keessan ni baasa.');
INSERT INTO "apaguna" VALUES(47,38,'47:38','Isin warri kun dhaga’aa! Karaa Rabbii keessatti arjoomuuf waamamtuIsin irraa nama doynoomuutu jiraNamni doynoome kan inni dooynoomu lubbuma isaa irrattiRabbiin dureessa; isintu hiyyeeyyiidhaYoo duubatti deebitanis, (Rabbiin) namoota biraa kan isin hin ta’in bakka keessan buusaIsaan ergasii akka keessan hin ta’an');
INSERT INTO "apaguna" VALUES(48,1,'48:1','(Yaa Muhammad!) Nuti injifannoo ifa ta’e si gonfachiifneerra.');
INSERT INTO "apaguna" VALUES(48,2,'48:2','Akka Rabbiin dilii kee irraa waan dabreefi waan dhufus siif araaramuuf, akka ni’imaa Isaa sirratti guutufi akka karaa qajeelaa si qajeelchuf jecha (injifannoo ifa ta’e siif kenne).');
INSERT INTO "apaguna" VALUES(48,3,'48:3','Rabbiin tumsa cimaa sii tumsuuf (si injifachiise).');
INSERT INTO "apaguna" VALUES(48,4,'48:4','Inni Isa tasgabbii onnee mu’uminootaa keessatti buuse(Kunis) akka isaan iimaana isaanii wajjiin iimaana dabalataniifiWaraanni samiifi dachii kanuma RabbiitiRabbiin beekaa ogeessa ta’eera.');
INSERT INTO "apaguna" VALUES(48,5,'48:5','Mu’uminoota dhiiraafi dubartootaa jannata isa jala laggeen yaa’an kan yeroo hunda ishee keessa jiraatan akka seensisuufi ammas hamtuu isaanii isaan irraa haquufiSun Rabbiin biratti milkii guddaa ta’eera.');
INSERT INTO "apaguna" VALUES(48,6,'48:6','Munaafiqoota dhiiraafi dubartootaa, mushrikoota dhiiraafi dhalaas, isaan Rabbi irratti hamtuu yaadan, isaan irratti marsaan hamtuun taatuudhaDallansuun Rabbii isaan irratti haa jiraatuRabbiin rahmata irraa isaan haa fageessuJahannamiinis isaaniif qopheesseera(Isheen) deebii akkaan fokkattuu dha!');
INSERT INTO "apaguna" VALUES(48,7,'48:7','Waraanni samiifi dachii (hundi) kan AllaahtiRabbiin injifataa, ogeessa ta’eera.');
INSERT INTO "apaguna" VALUES(48,8,'48:8','(Yaa Muhammad!) Nuti ragaa, gammachiisaafi sodaachisaa goonee si ergine.');
INSERT INTO "apaguna" VALUES(48,9,'48:9','(Yaa Namootaa!) Akka isin Allaahfi ergamaa isaatti amantan, isa guddiftan, kabajjaniifi akkasumas ganamaafi galgala Isa faarsitaniif (isiniif ergine).');
INSERT INTO "apaguna" VALUES(48,10,'48:10','Isaan waadaa siif galan, kan isaan waadaa galan RabbiifiHarki Rabbii harka isaanii gubbaadhaNamni waadaa diige, kan inni waadaa diige lubbuma isaa irrattiNama waan irratti Rabbiif waadaa gale guute, Inni mindaa guddaa isaa kennuuf jira.');
INSERT INTO "apaguna" VALUES(48,11,'48:11','Shanaanota irraa namoonni duubatti hafan “qabeenya keenyaafi maatii keenyatu nu qabe; kanaaf Rabbiin araarama nuuf kadhu” siin jechuuf jiruWaan onnee isaanii keessa hin jirre arraba isaanitiin jedhu(Yaa Muhammad) “yoo Inni miidhaa isin irra geessuu yookiin bu’aa isiniif kennuu fedhe, eenyutu waan Allaahn barbaade gochuu irraa Isa dhorguu danda’a? Garuu Rabbiin waan isin hojjattan beekaa ta’eera” jedhi.');
INSERT INTO "apaguna" VALUES(48,12,'48:12','Dhugumatti, Ergamaan Rabbiitiifi mu’uminoonni gara maatii isaanii gonkumaa deebi’uu dhabuu yaaddanii turtanSun onnee keessan keessatti miidhagfameeti, yaada hamtuu yaaddanii ummata mancaafamoo taatan.');
INSERT INTO "apaguna" VALUES(48,13,'48:13','Namni Rabbiifi ergamaa Isaatti hin amanin (kaafira)Nuti kaafirootaaf ibidda boba’aa qopheessinee jirra.');
INSERT INTO "apaguna" VALUES(48,14,'48:14','Mootummaan samiifi dachii kan Rabbii qofaNama fedheef ni araarama; nama fedhes ni adabaRabbiin araaramaa, mararfataa ta’eera.');
INSERT INTO "apaguna" VALUES(48,15,'48:15','Isaan duubatti hafan “yeroo gara boojuu, ishee fudhachuuf deemtan isin hordofnaa nu dhiisaa” jedhuJecha Rabbii jijjiiruu fedhu“Nu hin hordoftan! Rabbiin duraanis akkasuma jedhe” jedhiin“Isin nu waanyitu (hassaddu)” jechuuf jiruWaa xiqqoo malee hin hubatanu.');
INSERT INTO "apaguna" VALUES(48,16,'48:16','Shanaanota irraa isaan duubatti hafaniin jedhi: “gara namoota lolatti cimoo ta’aniitti waamamuuf jirtu; isaaniin loltu yookiin harka kennuKana yoo fudhattan, Rabbiin mindaa gaarii isiniif kennaYoo akkuma duraan garagaltanitti ammas garagaltan, Inni adabbii laalessaa isin adaba.');
INSERT INTO "apaguna" VALUES(48,17,'48:17','(Lola irraa hafuudhaan) jaamaa irra badiin hin jiruHokkolaa irras badiin hin jiruDhukkubsataa irras diliin hin jiruNama Rabbiifi ergamaa isaatif ajajame, (Rabbiin) jannata laggeen ishee jala yaatu isa seensisaNama irraa garagale immoo adabbii laalessaa isa adaba.');
INSERT INTO "apaguna" VALUES(48,18,'48:18','(Yaa Muhammad!) Rabbiin mu’uminoota yeroo isaan muka jalatti waadaa siif galan irraa jaallateeraWaan onnee isaanii keessa jirus beekeeraTasgabbii isaan irratti buusee injifannoo dhihoo ta’e (sulhii hudeebiyaa) isaaniif galata galche.');
INSERT INTO "apaguna" VALUES(48,19,'48:19','Boojii baay’ee ta’e kan isaan fudhatanis (galata galche)Rabbiin injifataa, ogeessa ta’eera.');
INSERT INTO "apaguna" VALUES(48,20,'48:20','Rabbiin boojii baay’ee kan ishee fudhattan waadaa isiniif galeeraIshee kana immoo isiniif jarjarseHarka namootaas isin irraa qabe(Kanas kan godhe) akka isheen mu’uminootaaf mallattoo taatee, karaa qajeelaas isin qajeelchuuf.');
INSERT INTO "apaguna" VALUES(48,21,'48:21','(Injifannoofi boojii) biraa kan to’annaa keessan jala hin jirre kan Rabbiin dhugumatti ishee marsetu jiraRabbiin waan hunda irratti danda’aa ta’eera.');
INSERT INTO "apaguna" VALUES(48,22,'48:22','Isaan kafaran odoo isinin lolaniiyyuu, dugda garagalanii eegasii tiksaafi tumsaa hin argatanu ture.');
INSERT INTO "apaguna" VALUES(48,23,'48:23','Karaa Rabbii kan dhugumatti duraan dabarte (karaa godhe)Karaa Rabbiitiif jijjiirama hin argitu.');
INSERT INTO "apaguna" VALUES(48,24,'48:24','Innis Isa Laga Makkaa keessatti erga isaan irratti isin moosisee booda harka isaanii isin irraa, harka keessanis isaan irraa qabeedhaRabbiin waan isin hojjattan argaa ta’eera.');
INSERT INTO "apaguna" VALUES(48,25,'48:25','Isaan warra kafaranii, masjiida kabajamaa (seenuu) irraa isin dhorganii, horii wareegaas akka iddoo qalma isaanii hin geenye dhorgamaa godhaniidhaOdoo dhiirotiin mu’uminoota ta’aniifi dubartoonni mu’uminoota ta’an kan isin hin beekne jiraachuu baatanii; isin isaan irra ejjachuun (ajjeesuun) jiraachuu baatee; odoo hin beekin isaan irraa rakkinni (diliin) isin tuquun jiraachuu baatee (Rabbiin isiniif hayyama ture)Rabbiin nama fedhe rahmata Isaa keessa seensisuuf (hin hayyamini)Odoo (mu’uminootaafi kaafiroonni) addaan bahanii silaa isaan irraa warra kafaran adabbii laalessaa ta’een adabna turre.');
INSERT INTO "apaguna" VALUES(48,26,'48:26','Yeroo warri kafare qalbii isaanii keessa haaloo, hinaaffaa wallaaltotaa godhee, Rabbiin tasgabbii isaa ergamaa isaatiifi mu’uminoota irratti buusee, jecha sodaa (Isaa) isaan qabachiise [yaadadhu]Isaanumatu ishee haqa godhatee, warra ishee ta’eRabbiin waan hunda beekaa ta’eera.');
INSERT INTO "apaguna" VALUES(48,27,'48:27','Rabbiin ergamaa Isaatiif abjuu haqaan dhugoomsee jiraYoo Rabbiin fedhe nagayaan mataa keessan haaddachaa, (gariinis) gabaabsachaa, odoo hin sodaanne masjiida kabajamaa seenuuf jirtuInni (Rabbiin) waan isin hin beekin beekeeti sanaa asitti injifannoo dhihoo (araara hudeeybiyaa) godhe.');
INSERT INTO "apaguna" VALUES(48,28,'48:28','Inni Isa ergamaa Isaa qajeelfamaafi amantii dhugaatiin, amantii hunda irratti ol isa taasisuuf jecha ergeedhaRagaaf Rabbiin qofti ga’aadha.');
INSERT INTO "apaguna" VALUES(48,29,'48:29','Muhammad ergamaa RabbiitiWarri isaa wajjin jiranis kuffaara irratti ciccimoo, gidduu isaanitti immoo wal mararfatoodhaTolaafi jaalala Rabbii barbaachaaf rukuu’aafi sujuuda kan baay’isan argitaMallattoon isaanii faanaa sujuudaa irraa ta’ee fuula isaanii keessa jiraSun fakkeenya isaanii kan Tawraat keessaatiFakkeenyi isaanii kan Injiil keessa jiru immoo akka midhaan lakkuu isaa baasee isa jabeessee, furdatee, jirma isaa irratti dhaabbatee, facaafattoota jaalachiisuuti(Wanni akkanatti isaan jabeessef) akka isaaniin kaafirtoota dallansiisuufiRabbiin isaan irraa warra amananii gaggaarii hojjatan araaramaafi mindaa guddaa waadaa isaaniif galee jira');
INSERT INTO "apaguna" VALUES(49,1,'49:1','Yaa warra amantan! Rabbiifi ergamaa Isaa dura hin dabrinaaRabbiin sodaadhaaDhugumatti, Rabbiin dhagayaa, beekaadha');
INSERT INTO "apaguna" VALUES(49,2,'49:2','Yaa warra amantan! Sagaleewwan keessan sagalee Nabiyyichaa irra ol hin kaasinaaAkka dalagaawwan keessan odoo isin hin beekne hin banneef jecha, haasa’a akka gariin keessan garii irratti ol fuudhutti isa irratti ol hin fuudhinaa');
INSERT INTO "apaguna" VALUES(49,3,'49:3','Isaan sagaleewwan isaanii ergamaa Rabbii biratti gadi qabatan, isaan sun warra Rabbiin onnee isaanii sodaa Rabbiitif qopheesseIsaaniif araaramaafi mindaa guddaatu jira.');
INSERT INTO "apaguna" VALUES(49,4,'49:4','Isaan duuba manneenii irraa si waaman, hedduun isaanii hin hubatanu.');
INSERT INTO "apaguna" VALUES(49,5,'49:5','Odoo hanga ati isaanitti gadi baatutti obsanii silaa (sanatu) isaaniif caala tureRabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(49,6,'49:6','Yaa warra amantan! Yoo fincilaan tokko oduun isinitti dhufe, akka wallaalumaan namoota miitanii, ergasii waan hojjattan irratti hin gaabbineef dubbii adda baafadhaa.');
INSERT INTO "apaguna" VALUES(49,7,'49:7','Isin keessa ergamaan Rabbii jiraachuu beekaaDhimmoota baay’ee keessatti odoo isin jala deemee rakkinni isin irra ga’a tureGaruu Rabbiin iimaana gara keessanitti jaallachiiseeti, onnee keessan keessattis isa miidhagsee, kufrii, fincilaafi dilii gara keessanitti jibbamaa godheIsaan sun isaanumatu qajeeloodha.');
INSERT INTO "apaguna" VALUES(49,8,'49:8','Tolaafi ni’imaa Rabbi irraa ta’een (kana godhe)Rabbiinis beekaa, ogeessa');
INSERT INTO "apaguna" VALUES(49,9,'49:9','Yoo gareen mu’uminoota irraa ta’an lama wal waraanan, gidduu isaaniitti araara buusaaYoo lamaan keessaa isheen tokko ishee lammataa irratti daangaa darbite, ishee daangaa darbite san hanga isheen gara ajaja Rabbii deebitutti waraanaaYoo isheen (ajaja Rabbiitif) bulte gidduu isaanitti haqaan tolchaa; wal qixxeessasDhugumatti, Rabbiin warra wal qixxeessan jaallata.');
INSERT INTO "apaguna" VALUES(49,10,'49:10','Mu’uminoonni obboleeyyani; kanaafuu gidduu obboleeyyan keessanii tolchaaAkka mararfatamtanuuf jecha Rabbiin sodaadhaa.');
INSERT INTO "apaguna" VALUES(49,11,'49:11','Dhiironni dhiirota biraa hin tuffatinIsaan irra caaluun isaanii (tuffatamtootaa) ni malaDubartoonnis dubartoota biraa (hin tuffatin)Isaan irra caaluun isaanii (tuffatamtootaa) ni malaTokkoon keessan isa biraa maqaa hin xureessin; maqaa qoosaatiin wal hin arrabsinaaErga amanuutii maqaan faasiquummaa (amantiin alaa) akkam fokkataa dha! Namni hin tawbatin, isaanumatu miidhaa raawwattoota.');
INSERT INTO "apaguna" VALUES(49,12,'49:12','Yaa warra amantan! Hedduu seehaa (mamii) irraa fagaadhaa; gariin seehaa diliidhaWalis hin basaasinaaGariin keessanis garii hin hamatinTokkoon keessan foon obboleessa isaa odoo du’ee jiruu nyaachuu ni fedhaa? (Yeroo) san ni jibbitanii (isa jiraa hoo maalif nyaachuu feetan?) Rabbiin sodaadhaaRabbiin akkaan tawbaa qeebalaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(49,13,'49:13','Yaa namootaa! Nuti dhiiraafi dubartii irraa isin uumneeti, akka wal beektaniif jecha sabootaafi gosoota isin taasifneRabbiin biratti irra kabajamaan keessan, irra Rabbiin sodaataa keessaniRabbiin beekaa keessa hubataadha');
INSERT INTO "apaguna" VALUES(49,14,'49:14','Shanaanoon “nuti amanneerra” jedhu“Waan ammallee iimaanni onnee keessan keessa hin seeniniif jecha, isin hin amanne; garuu ‘islaamofne’ jedhaa.” Garuu yoo ajaja Rabbiifi ergamaa Isaa fudhattan, Inni hojiiwwan keessan irraa homaa hin hir’isuRabbiin araaramaa, mararfataadha');
INSERT INTO "apaguna" VALUES(49,15,'49:15','Mu’uminoonni isaan Rabbiifi ergamaa Isaatti amananii, ergasii shakkii hin qabaatiniifi qabeenya isaanitiifi lubbuu isaaniitiin karaa Rabbii keessatti qabsaa’aniidhaIsaan isaanumatu warra dhugaati.');
INSERT INTO "apaguna" VALUES(49,16,'49:16','Jedhi: “Waa’ee amantii keessanii Rabbiin barsiisuu feetuu? Rabbiin waan samii keessa jiruufi waan dachii keessa jiru ni beekaRabbiin waan hunda beekaadha.”');
INSERT INTO "apaguna" VALUES(49,17,'49:17','Islaamawuu isaaniitiin sirratti dhaaddatu“Islaamummaa keessaniin ana irratti hin dhaaddatinaaYoo warra dhugaa dubbatu taatan, Rabbiin waan imaanatti isin qajeelcheef isin irratti dhaaddata” jedhiin.');
INSERT INTO "apaguna" VALUES(49,18,'49:18','Rabbiin fagoo samiifi dachii keessaa ni beekaRabbiin waan isin dalagdan (hunda) argaadha.”');
INSERT INTO "apaguna" VALUES(50,1,'50:1','QaafQur’aana guddaanin (kakadha).');
INSERT INTO "apaguna" VALUES(50,2,'50:2','Dhugumaatti, sodaachisaan isaan irraa ta’e isaanitti dhufuu dinqisiifatanKaafiroonni “kun waan dinqisiisaadha” jedhan.');
INSERT INTO "apaguna" VALUES(50,3,'50:3','“Sila yeroo duunee biyyee taane (ni kaafamnaa)? Sun deebii fagoodha.”(jedhu).');
INSERT INTO "apaguna" VALUES(50,4,'50:4','Dhugumatti, waan dachiin isaan irraa hir’iftu beeknee jirraGalmee tiksaatu nu bira jira.');
INSERT INTO "apaguna" VALUES(50,5,'50:5','Dhugumatti isaan dhugaa, yeroo inni isaanitti dhufu sobsiisanii jiruIsaan dubbii wal makataa keessa jiru.');
INSERT INTO "apaguna" VALUES(50,6,'50:6','Sila gara samii gubbaa isaanii jirtuu, kan uraa homaatuu hin qabne, akkamitti akka ishee ijaarreefi miidhagsine hin ilaallee?');
INSERT INTO "apaguna" VALUES(50,7,'50:7','Dachii diriirsinee gaarreen dhaabbatoo ishee keessatti gadi suuqnee akaakuu (biqiltootaa) babbareedoo hunda irraa ishee keessatti magarsine.');
INSERT INTO "apaguna" VALUES(50,8,'50:8','Gabricha tawbatuuf hubannaafi gorsa akka ta’uuf (kana goone).');
INSERT INTO "apaguna" VALUES(50,9,'50:9','Samii irraas bishaan barakaa qabu buufnee ergasii isaan ashaakiltiiwwaniifi firiiwwan haamamanis biqilchine.');
INSERT INTO "apaguna" VALUES(50,10,'50:10','Hundee muka timiraa dhedheeroo, kan daraaraa kuufamaa qabuus (ni magarsine).');
INSERT INTO "apaguna" VALUES(50,11,'50:11','Gabroota nyaachisuuf jecha (buufne)Isaan (bishaan saniin) dachii duutuu jiraachifneBaafamuunis akkasuma.');
INSERT INTO "apaguna" VALUES(50,12,'50:12','Isaaniin dura ummanni Nuuh, warri eelaa (biirii)fi Samuudis ni sobsiifte.');
INSERT INTO "apaguna" VALUES(50,13,'50:13','Aad, Fir’awniifi obboleeyyan Luuxis.');
INSERT INTO "apaguna" VALUES(50,14,'50:14','Warri bosonaafi ummanni Tubba’isHunduu ergamtoota sobsiisanii, adabbiin kiyya isaanitti mirkanaa’e.');
INSERT INTO "apaguna" VALUES(50,15,'50:15','Sila Nuti uumaa duraatiin ni dadhabnee? Lakkii! Isaan uumaa haarawa irraa shakkii wal makate keessa jiru.');
INSERT INTO "apaguna" VALUES(50,16,'50:16','Nuti dhugumatti, nama uumneerraWaan lubbuun isaa itti hasaastus ni beeknaNuti (beekumsa keenyan) hidda dhiigaa (hamaa) irra gara isaatti dhihoodha.');
INSERT INTO "apaguna" VALUES(50,17,'50:17','Yeroo (maleeykonni) jecha fuudhan lamaan mirgaafi bitaa isaa irra taa’anii jecha fuudhan (nuti gara isaatti dhihoo dha).');
INSERT INTO "apaguna" VALUES(50,18,'50:18','Tiksaan itti dhihaataan isa bira jiraatu malee jecha tokkoyyuu hin dubbatu.');
INSERT INTO "apaguna" VALUES(50,19,'50:19','Machiin du’aa dhugaan dhuftee jirti“Kun waan ati irraa dheessaa turteedha” (jedhamaan).');
INSERT INTO "apaguna" VALUES(50,20,'50:20','Xurumbaa ni afuufama; sun guyyaa waadaati.');
INSERT INTO "apaguna" VALUES(50,21,'50:21','Lubbuun hundi haala ishee wajjin oofaafi ragaa bahaan jiranuun dhufte.');
INSERT INTO "apaguna" VALUES(50,22,'50:22','“Ati kana irraa daguu keessa turteNuti haguuggaa kee sirraa kaafneKanaafuu ijji kee har’a qara” (jedhamaan).');
INSERT INTO "apaguna" VALUES(50,23,'50:23','Qindiin (maleeyakaan) isaa ni jedha: “kun waan (galmaa’ee) na bira jiruudha.”');
INSERT INTO "apaguna" VALUES(50,24,'50:24','Isin lamaanuu akkaan kafaraa haqa didaa hunda jahannamitti darbaa.');
INSERT INTO "apaguna" VALUES(50,25,'50:25','(Kaafira) toltuu dhorgaa, daangaa dabraafi shakkaa ta’e (jahannamitti darbaa).');
INSERT INTO "apaguna" VALUES(50,26,'50:26','Isa Rabbii wajjin gabbaramaa biraa taasise adabbii cimaa keessa isa buusaa.');
INSERT INTO "apaguna" VALUES(50,27,'50:27','Hiriyaan isaa (kan sheyxaanaa) ni jedha: “Gooftaa keenya! Ani daangaa isa hin dabarsineGaruu isumatu jallina fagoo ta’e keessa ture.”');
INSERT INTO "apaguna" VALUES(50,28,'50:28','Rabbiin ni jedha: “Ana biratti wal hin falminaa; Ani dursee sodaachisa kiyya gara keessanitti ergeera.');
INSERT INTO "apaguna" VALUES(50,29,'50:29','Ana biratti jechi hin jijjiiramu; Anis gabroota kiyya irratti miidhaa raawataa miti.”');
INSERT INTO "apaguna" VALUES(50,30,'50:30','Guyyaa Nuti jahannamiin “sila guutamteettaa?” jennuun, isheen immoo “dabalanni jiraa?” jettu (hin jijjiiramu.)');
INSERT INTO "apaguna" VALUES(50,31,'50:31','Jannannis namoota Rabbi sodaataniif haala hin fagaatiniin dhiheeffamte.');
INSERT INTO "apaguna" VALUES(50,32,'50:32','Cufa nama akkaan gara Rabbii deebi’aa tikfataa ta’eetiin “kun waan waadaa galamtaniidha” (jedhama).');
INSERT INTO "apaguna" VALUES(50,33,'50:33','“Nama fagotti Rabbi sodaatee, onnee gara Rabbii deebituun dhufeefi.');
INSERT INTO "apaguna" VALUES(50,34,'50:34','Nagayaan ishee seenaa; sun guyyaa hafiinsaati” (jedhama).');
INSERT INTO "apaguna" VALUES(50,35,'50:35','Isaaniif waan fedhantu ishee keessa jiraNu bira daballis jira.');
INSERT INTO "apaguna" VALUES(50,36,'50:36','Isaan dura dhaloota humnaan isaan irra ciccimoo turan baay’ee balleessinee jirraBiyya keessa “iddoo baqaa argannaa?” jechaa fiigaa turan.');
INSERT INTO "apaguna" VALUES(50,37,'50:37','Dhugumatti, nama onnee qabuuf yookiin isa yeroo (gorsa) irratti hirmaatu gurra itti kennuuf, sana keessa yaadannootu jira.');
INSERT INTO "apaguna" VALUES(50,38,'50:38','Dhugumatti, Nuti samiifi dachii, akkasumas wantoota gidduu isaanii jiran guyyoota jaha keessatti uumne; dadhabbiin tokkoyyuu Nu hin tuqne.');
INSERT INTO "apaguna" VALUES(50,39,'50:39','Waan isaan jedhanu irratti obsiFaaruu Rabbii keetiitiinis baha biiftuu duraafi lixa (ishee) dura salaati.');
INSERT INTO "apaguna" VALUES(50,40,'50:40','Halkan irraas Isa qulqulleessi; salaata boodas (sunnaalee sagadi).');
INSERT INTO "apaguna" VALUES(50,41,'50:41','Guyyaa lallabaan iddoo dhihoo irraa lallabus dhaggeeffadhu.');
INSERT INTO "apaguna" VALUES(50,42,'50:42','Guyyaa dhugaan iyyansa dhagayan, sun guyyaa (qabrii irraa) ba’iinsaati.');
INSERT INTO "apaguna" VALUES(50,43,'50:43','Nuti, Numatu jiraachisa; Numatu ajjeesas; deebiin (dhumaas) garumma keenya');
INSERT INTO "apaguna" VALUES(50,44,'50:44','Guyyaa dachiin isaan irraa ariitiidhaan babbaqaqxu (deebiin garuma keenya)Sun walitti qabuu Nurratti laafaa ta’eedha.');
INSERT INTO "apaguna" VALUES(50,45,'50:45','Nuti akkaan beekoo waan isaan jedhaniitiAtis isaan irratti moosifamaa hin taaneNama adabbii kiyya sodaatu Qur’aanaan gorsi.');
INSERT INTO "apaguna" VALUES(51,1,'51:1','(Qilleensa) bubbisuu bubbiftuunin kakadha.');
INSERT INTO "apaguna" VALUES(51,2,'51:2','(Duumessa bishaan) ulfaataa baadhattuunis.');
INSERT INTO "apaguna" VALUES(51,3,'51:3','(Dooniwwan) haala salphaan (bishaan gubbaa) yaa’aniinis.');
INSERT INTO "apaguna" VALUES(51,4,'51:4','(Maleykota) ajaja (Rabbii) raabsaniinis.');
INSERT INTO "apaguna" VALUES(51,5,'51:5',' Wanti isin beellamamtan dhugaadha.');
INSERT INTO "apaguna" VALUES(51,6,'51:6','(Guyyaan) murtii dhugumatti argamaadha.');
INSERT INTO "apaguna" VALUES(51,7,'51:7','Ammas samii abbaa miidhaginaatiin kakadhe.');
INSERT INTO "apaguna" VALUES(51,8,'51:8','Dhugumatti, isin yaada wal dhadhabaa keessa jirtu.');
INSERT INTO "apaguna" VALUES(51,9,'51:9',' Kan namni haqa irraa garagalfamu ittiin garagalfamuudha.');
INSERT INTO "apaguna" VALUES(51,10,'51:10','Sobdoonni abaaramaniiru.');
INSERT INTO "apaguna" VALUES(51,11,'51:11','Isaan sun wallaaluma keessatti kan of dagataniidha.');
INSERT INTO "apaguna" VALUES(51,12,'51:12','“Guyyaan mindaa yoomi?” (jechuun) gaafatu.');
INSERT INTO "apaguna" VALUES(51,13,'51:13','(Inni) Guyyaa isaan ibidda irra fidamaniidha.');
INSERT INTO "apaguna" VALUES(51,14,'51:14','“Adaba keessan kan ariifataa turtan dhandhamaa” (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(51,15,'51:15','Isaan Rabbiin sodaatan, jannataafi burqituuwwan keessa jiraatu.');
INSERT INTO "apaguna" VALUES(51,16,'51:16','Waan Gooftaan isaanii isaaniif kenne fudhachaa (jiraatu)Isaan sana dura toltuu hojjattoota turan.');
INSERT INTO "apaguna" VALUES(51,17,'51:17','Halkan irraa xiqqaa qofa kan rafan turan.');
INSERT INTO "apaguna" VALUES(51,18,'51:18','Yeroo hiraabaa (obboroo) isaan araarama kadhataniidha.');
INSERT INTO "apaguna" VALUES(51,19,'51:19','Kan qabeenya isaanii keessa kadhataafi kan hin kadhanneefis haqni jiruudha.');
INSERT INTO "apaguna" VALUES(51,20,'51:20','Dachii keessas isaan dhugaan amananiif mallattooleetu jiru.');
INSERT INTO "apaguna" VALUES(51,21,'51:21','Lubbuu keessan keessas (akkasuma)Sila hin argitanuu?');
INSERT INTO "apaguna" VALUES(51,22,'51:22','Sooranni keessaaniifi wanti isin waadaa galamtanis samii keessa jira.');
INSERT INTO "apaguna" VALUES(51,23,'51:23','Gooftaa samiifi dachiitiin kakadhee! Inni dhugaa fakkiidhuma waan isinuu dubbattanuuti.');
INSERT INTO "apaguna" VALUES(51,24,'51:24','Seenaan keessummaa Ibraahiim kabajamoo ta’anii sitti dhufeeraa?');
INSERT INTO "apaguna" VALUES(51,25,'51:25','Yeroo isa irratti seenanii “Nagaa (si irratti buufna)” jedhan[Inni] “nageenyi (isin irras haa jiraatu); isin namoota ani hin beekneedha” jedheen.');
INSERT INTO "apaguna" VALUES(51,26,'51:26','Ergasii gara maatii isaa deebi’eeti mirgoo (jibicha) gabbataan dhufe.');
INSERT INTO "apaguna" VALUES(51,27,'51:27','Isaanitti dhiyeesseeti “sila hin nyaattanuu?” jedhe.');
INSERT INTO "apaguna" VALUES(51,28,'51:28','Ergasii (yeroo nyaachuu dhabuu isaanii argu) isaan irraa sodaan isa qabeIsaan “hin sodaatin” jedhaniinIlma beekumsa qabuunis isa gammachiisan.');
INSERT INTO "apaguna" VALUES(51,29,'51:29','Sana booda niitin isaa sagalee ol fuudhamaan haasa’aa dhufteFuula ishee dhadhayaa “Ani jaartii manguddoo maseena hoo (akkamittin daha)!” jette');
INSERT INTO "apaguna" VALUES(51,30,'51:30','(Ibraahiim) ni jedhe: “Yaa ergamootaa! Dhimmi keessan maali?”');
INSERT INTO "apaguna" VALUES(51,31,'51:31','(Ibraahiim) ni jedhe: “Yaa ergamootaa! Dhimmi keessan maali?”');
INSERT INTO "apaguna" VALUES(51,32,'51:32','[Isaan] ni jedhan: “Nuti gara ummata yakkamtootaa ergamne.');
INSERT INTO "apaguna" VALUES(51,33,'51:33','Dhagaa dhoqqee gubataa irraa ta’e isaan irratti erguuf (dhufne).”');
INSERT INTO "apaguna" VALUES(51,34,'51:34','Rabbii kee biratti daanga dabartootaaf kan mallateeffamte haala taateen.');
INSERT INTO "apaguna" VALUES(51,35,'51:35','Mu’uminoota irraa namoota ishee keessa jiran ni baafne.');
INSERT INTO "apaguna" VALUES(51,36,'51:36','Mana Muslimtootaa irraa kan ta’e malee ishee keessatti homaa hin argine.');
INSERT INTO "apaguna" VALUES(51,37,'51:37','Warra adabbii laalessaa sodaataniif ishee keessatti mallattoo hambifnee jirra.');
INSERT INTO "apaguna" VALUES(51,38,'51:38','(Seenaa) Muusaa keessas yeroo ragaa ifa ba’aan gara Fir’awnitti isa ergine (mallattoo goonee jirra).');
INSERT INTO "apaguna" VALUES(51,39,'51:39','Innis (Fir’awnis) humna waraanaa isaa waliin (dhugaa irraa) gara galee “(Muusaan) falfalaadha; yookiin maraataadha” jedhe.');
INSERT INTO "apaguna" VALUES(51,40,'51:40','Isaafi waraana isaas haala komatamaa ta’een qabnee galaana keessatti isaan darbine');
INSERT INTO "apaguna" VALUES(51,41,'51:41','(Seenaa) Aad keessas, yeroo qilleensa maseenaa isaan irratti ergine (mallattootu jira).');
INSERT INTO "apaguna" VALUES(51,42,'51:42','Waan isa irra dhufte tokkoyyuu akka lafee dhumee isa gootu malee hin dhiiftu.');
INSERT INTO "apaguna" VALUES(51,43,'51:43','(Seenaa) Samuud keessas, yeroo hanga murtaa’e tokko qanani’aa isaaniin jedhame (mallattootu jira).');
INSERT INTO "apaguna" VALUES(51,44,'51:44','Ajaja Gooftaa isaanii irraa ni boonanOdoo isaan ilaalanuu iyyansi (dhumti) isaan qabatte.');
INSERT INTO "apaguna" VALUES(51,45,'51:45','Ergasii dhaabbachuu hin dandeenye; tumsamoos hin taane.');
INSERT INTO "apaguna" VALUES(51,46,'51:46','Ummata Nuuhis san dura (ballessinee jirra)Dhugumatti, isaan ummata finciltoota turan.');
INSERT INTO "apaguna" VALUES(51,47,'51:47','Samiis humnaan ishee ijaarreDhugumatti, Nuti (ishee) bal’isoodha.');
INSERT INTO "apaguna" VALUES(51,48,'51:48','Dachiis diriirsinee jirra; (Nuti) diriirsitoonni waa tolle!');
INSERT INTO "apaguna" VALUES(51,49,'51:49','Akka isin gorfamtaniif waan hunda irraa qindiiwwan uumne.');
INSERT INTO "apaguna" VALUES(51,50,'51:50','Gara Rabbii baqadhaaAni Isa irraa isiniif sodaachisaa ifa bahaadha.');
INSERT INTO "apaguna" VALUES(51,51,'51:51','Allaah wajjinis gabbaramaa biraa hin taasisinaaAni Isa irraa isiniif sodaachisaa ifa bahaadha.');
INSERT INTO "apaguna" VALUES(51,52,'51:52','Akkasuma, ergamaan tokkos warra isaan dura turanitti hin dhufne, yoo isaan “falfalaadha; yookiin maraataadha” jedhaniin malee');
INSERT INTO "apaguna" VALUES(51,53,'51:53','Sila (sobsiisuu kana) waliif dhaamanii? Lakkisaa, isaanumatu ummata daangaa dabroodha.');
INSERT INTO "apaguna" VALUES(51,54,'51:54','Isaan irraa garagali; ati komatamaa miti.');
INSERT INTO "apaguna" VALUES(51,55,'51:55','Yaadachiisi; yaadachiifni mu’uminoota ni fayyaddii.');
INSERT INTO "apaguna" VALUES(51,56,'51:56','Jinniifi ilma namaa akka Ana qofa gabbaraniif malee hin uumne.');
INSERT INTO "apaguna" VALUES(51,57,'51:57','Soorata homaatuu isaan irraa hin fedhuAkka Ana nyaachisanis hin fedhu.');
INSERT INTO "apaguna" VALUES(51,58,'51:58','Rabbiin Isatu akkaan soorata kennaa, abbaa humna cimaati.');
INSERT INTO "apaguna" VALUES(51,59,'51:59','Dhugumatti, miidhaa raawwattootaaf qooda adabaa kan akka adabbii fakkaattota isaaniitu jiraafii akka Nan jarjarfanne.');
INSERT INTO "apaguna" VALUES(51,60,'51:60','Warra kafaraniif guyyaa isaanii kan beellama godhaman keessatti badiin isaaniif mirkanaa’eera.');
INSERT INTO "apaguna" VALUES(52,1,'52:1','(Gaara) Xuuriin kakadhe.');
INSERT INTO "apaguna" VALUES(52,2,'52:2','Kitaaba barreeffamaanis.');
INSERT INTO "apaguna" VALUES(52,3,'52:3','Xalaayaa bal’ifamaa keessatti.');
INSERT INTO "apaguna" VALUES(52,4,'52:4','Mana jiraachifamaanis.');
INSERT INTO "apaguna" VALUES(52,5,'52:5','Ijoo (gombisaa) ol fuudhamaanis.');
INSERT INTO "apaguna" VALUES(52,6,'52:6','Galaana qabsiifamaaniis.');
INSERT INTO "apaguna" VALUES(52,7,'52:7','Dhugumatti, adabbiin Gooftaa keetii argamaadha.');
INSERT INTO "apaguna" VALUES(52,8,'52:8','Wanti isa deebisu tokkolleen hin jiru.');
INSERT INTO "apaguna" VALUES(52,9,'52:9','Guyyaa samiin socho’iinsa (hamaa) sochootu.');
INSERT INTO "apaguna" VALUES(52,10,'52:10','(Guyyaa) gaarreenis deemicha deemtu.');
INSERT INTO "apaguna" VALUES(52,11,'52:11','Warra guyyaa san kijibsiisaniif badii ibiddaatu jira.');
INSERT INTO "apaguna" VALUES(52,12,'52:12','(Isaan) warra soba keessa taphataniidha.');
INSERT INTO "apaguna" VALUES(52,13,'52:13','Guyyaa humnaan gara ibidda jahannam oofamiinsa oofaman');
INSERT INTO "apaguna" VALUES(52,14,'52:14','(Isaaniin jedhama): “Isheen kun ibidda kan ishee sobsiisaa turtaniidha.');
INSERT INTO "apaguna" VALUES(52,15,'52:15','Sila kun falfala moo isinumatu hin argitanu?!');
INSERT INTO "apaguna" VALUES(52,16,'52:16','Ishee seenaa (dhandhamaa); obsitanus yookiin obsuu baattanus isiniif waluma qixaWanti isin mindeeffamtan waanuma hojjachaa turtan qofa” (jedhaman).');
INSERT INTO "apaguna" VALUES(52,17,'52:17','Dhugumatti, isaan Rabbiin sodaatan jannataafi qananii keessa jiraatu.');
INSERT INTO "apaguna" VALUES(52,18,'52:18','Waan Gooftaan isaanii isaaniif kennetti gammadoo ta’anii (keessa jiraatu); Gooftaan isaaniis adabbii gubaa jalaa isaan baraarse.');
INSERT INTO "apaguna" VALUES(52,19,'52:19','“Waan hojjataa turtaniif jecha gaarii haala ta’een nyaadhaa; dhugaa” (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(52,20,'52:20','Sireewwan hiriira galfaman irra hirkatoo ta’anii (jiraatu)Dubartoota adaadii ija babal’aa isaan fuusifna.');
INSERT INTO "apaguna" VALUES(52,21,'52:21','Isaan amananii ilmaan isaaniis iimaanaan isaan hordofan, maatii isaanii isaanitti dhaqqabsiifna(Galata) dalagaa isaanii irraas homaayyuu hin hir’ifnuNamni hunduu waan hojjateen qabamaadha.');
INSERT INTO "apaguna" VALUES(52,22,'52:22','Mudraaleefi foon waan isaan fedhan irraa ta’e isaaniif daballa.');
INSERT INTO "apaguna" VALUES(52,23,'52:23','Ishee keessatti geeba (xoofoo farsoo jannataa) kan ishee keessa sobaafi diliin hin jirre walitti daddabarsu (waliif kennu).');
INSERT INTO "apaguna" VALUES(52,24,'52:24','Ijoollee dhiiraa isaanii warra faaya eegamaa fakkaatantu isaan irra naanna’u.');
INSERT INTO "apaguna" VALUES(52,25,'52:25','Gariin isaaniis garii irratti gara galaniiti wal gaggaafatu.');
INSERT INTO "apaguna" VALUES(52,26,'52:26','Ni jedhu: “Nuti kana dura maatii keenya keessatti (Rabbi irraa) sodaatoo turre.');
INSERT INTO "apaguna" VALUES(52,27,'52:27','Rabbiin nu qanansiisee, adabbii summaa’aa jalaas nu baraarse.');
INSERT INTO "apaguna" VALUES(52,28,'52:28','Nuti dhugumatti, kana dura Isa kadhataa turreInni dhugumatti, Isumatu tola oolaa, mararfataadha.”');
INSERT INTO "apaguna" VALUES(52,29,'52:29','Yaadachiisi! Ati ni’imaa Gooftaa keetiitiin, raagaafi maraataa miti.');
INSERT INTO "apaguna" VALUES(52,30,'52:30','“Weellisaadha; dhufuu du’aa isa irratti eegna” jedhan mitii?');
INSERT INTO "apaguna" VALUES(52,31,'52:31','“Eegaa! Anis isin waliin warra eegu irrayii” jedhiin.');
INSERT INTO "apaguna" VALUES(52,32,'52:32','Sila sammuu isaaniitu kanatti isaan ajaja moo? Lakkii, isaanumatu ummata daangaa darboodha.');
INSERT INTO "apaguna" VALUES(52,33,'52:33','Sila isatu isa (Qur’aana) uume jedhu moo?” lakkii, isaanumatu hin amanne.');
INSERT INTO "apaguna" VALUES(52,34,'52:34','Yoo kan dhugaa dubbatan ta’aniif haasaa fakkaataa isaatiin haa dhufanu.');
INSERT INTO "apaguna" VALUES(52,35,'52:35','Sila isaan odoo wanti isaan uume hin jirre uumaman moo isaanumatu uumoo dha?');
INSERT INTO "apaguna" VALUES(52,36,'52:36','Moo samiifi dachii uuman? Lakki, isaan hin dhugoomsanu.');
INSERT INTO "apaguna" VALUES(52,37,'52:37','Kuusaa Gooftaa keetiitu isaan bira jira moo isaantu injifattoota?');
INSERT INTO "apaguna" VALUES(52,38,'52:38','Moo kortoo (samii geessu) kan isa keessaan (maleykota) dhagayantu isaaniif jira! Dhaggeeffataan isaanii ragaa ifa ta’een haa dhufu.');
INSERT INTO "apaguna" VALUES(52,39,'52:39','Moo Isaaf ilmaan dubaraa jiraataniiti isiniif immoo ilmaan dhiiraa jiru?');
INSERT INTO "apaguna" VALUES(52,40,'52:40','Moo mindaa isaan gaafatteeti kaffaltiin isaa isaanitti ulfaate?');
INSERT INTO "apaguna" VALUES(52,41,'52:41','Moo isaan bira iccitiin fagoo jiraateeti isaan barreeffatan?');
INSERT INTO "apaguna" VALUES(52,42,'52:42','Lakkii! Tooftaa baasuu fedhaniWarri kafare isaanuma kan tooftaan itti baafamu.');
INSERT INTO "apaguna" VALUES(52,43,'52:43','Moo Rabbiin malee gabbaramaa biraa qabu? Rabbiin waan isaan itti qixxeessan irraa qulqullaa’e.');
INSERT INTO "apaguna" VALUES(52,44,'52:44','Yoo samii irraa cittuu wahii kan gadi kuftu arganis “duumessa kuufamaadha” jedhu');
INSERT INTO "apaguna" VALUES(52,45,'52:45','Hanga guyyaa isaanii kan isa keessa dhuman qunnamanitti isaan dhiisi.');
INSERT INTO "apaguna" VALUES(52,46,'52:46','Guyyaa tooftaan isaanii homaa isaan hin fayyadneefi isaanis hin tumsamne (hanga qunnamanitti).');
INSERT INTO "apaguna" VALUES(52,47,'52:47','Dhugumatti, isaan miidhaa raawwataniif sana dura adabbiitu jiraGaruu irra hedduun isaanii hin beekanu.');
INSERT INTO "apaguna" VALUES(52,48,'52:48','Murtii Gooftaa keetiitiif obsiDhugumatti, ati tika keenya jala jirtaYeroo kaatu Faaruu Gooftaa keetiitiin Isa qulqulleessi.');
INSERT INTO "apaguna" VALUES(52,49,'52:49','Halkanis Isa faarsi; yeroo urjiiwwan seenanis (faarsi)');
INSERT INTO "apaguna" VALUES(53,1,'53:1','Urjiin kakadhe; yeroo inni bu’u (seenu).');
INSERT INTO "apaguna" VALUES(53,2,'53:2','Hiriyaan keessan (Muhammad SAW) hin jallanne; hin dogoggorres.');
INSERT INTO "apaguna" VALUES(53,3,'53:3','Fedha lubbuu irraas hin haasa’u');
INSERT INTO "apaguna" VALUES(53,4,'53:4','Inni (wanti inni dubbatu) wahyii buufamu qofa.');
INSERT INTO "apaguna" VALUES(53,5,'53:5','(Jibriil) kan humni isaa cimaatu isa barsiise.');
INSERT INTO "apaguna" VALUES(53,6,'53:6','Abbaa humnaa ta’ee (bifa Rabbiin irratti isa uumeen) ol ta’e.');
INSERT INTO "apaguna" VALUES(53,7,'53:7','Inni moggaa (samii) isa ol aanaa irratti (ol ta’e)');
INSERT INTO "apaguna" VALUES(53,8,'53:8','Ergasii dhihaatee gadis bu’e.');
INSERT INTO "apaguna" VALUES(53,9,'53:9','Hanga fageenya qabannaa gooboo lamaanii yookiin sana irra dhihoo ta’e.');
INSERT INTO "apaguna" VALUES(53,10,'53:10','Gara gabricha Isaatti (Rabbiitti) waan wahyii godhe wahyii godhe.');
INSERT INTO "apaguna" VALUES(53,11,'53:11','Onneen (Nabiyyii-SAW) waan arge keessatti homaa hin sobne.');
INSERT INTO "apaguna" VALUES(53,12,'53:12','Sila ergasii waan inni (yeroo mi’iraajaa) arge irratti isa mormituu?');
INSERT INTO "apaguna" VALUES(53,13,'53:13','Dhugumatti yeroo biraas isa argeera.');
INSERT INTO "apaguna" VALUES(53,14,'53:14','Qurquraa daangaa (samii torbaffaa) biratti arge.');
INSERT INTO "apaguna" VALUES(53,15,'53:15','Ishee bira jannata qubsumaatu jira');
INSERT INTO "apaguna" VALUES(53,16,'53:16','Yeroo wanti haguugu mukattii qurquraa haguugu (arge).');
INSERT INTO "apaguna" VALUES(53,17,'53:17','Ijji gamaa gamana hin jallanne, irras hin dabarre.');
INSERT INTO "apaguna" VALUES(53,18,'53:18','Dhugumatti, inni mallattoolee Gooftaa isaa gurguddoo irraa argeera.');
INSERT INTO "apaguna" VALUES(53,19,'53:19','Mee (taabota) Laatiifi Uzzaa (jedhaman) naaf himaa?');
INSERT INTO "apaguna" VALUES(53,20,'53:20','Manaatiin kan sadaffaa duubatti haftuu taates (naaf himaa)?');
INSERT INTO "apaguna" VALUES(53,21,'53:21','Sila isiniif dhiirri ta''eeti dubartiin isaaf taatee?');
INSERT INTO "apaguna" VALUES(53,22,'53:22','Isheen kun oggas qooda jalloodha.');
INSERT INTO "apaguna" VALUES(53,23,'53:23','Isheen maqoota isiniifi abbootiin keessan odoo Rabbiin ragaa isheef hin buusin moggaaftani malee homaa mitiSe’aafi waan lubbuun isaanii feetu qofa hordofuDhugumaan qajeelfamni Rabbii isaanii irraa isaanitti dhufee jira.');
INSERT INTO "apaguna" VALUES(53,24,'53:24','Moo namaaf waanuma hawwetu jira?');
INSERT INTO "apaguna" VALUES(53,25,'53:25','Aakiraafi addunyaanis kan Rabbiiti.');
INSERT INTO "apaguna" VALUES(53,26,'53:26','Maleeykota irraa, warra erga Rabbiin nama fedhuuf hayyamee, jaallate malee manguddummaan isaanii homaa hin fayyadne baay’eetu samii keessa jiru.');
INSERT INTO "apaguna" VALUES(53,27,'53:27','Isaan Aakhiratti hin amanne moggaasa dubartootaa maleeykota moggaasu.');
INSERT INTO "apaguna" VALUES(53,28,'53:28','Isa irratti beekumsa homaatuu hin qabanShakkii malee homaa hin hordofanuShakkiin immoo dhugaa irraa homaa hin duroomsu.');
INSERT INTO "apaguna" VALUES(53,29,'53:29','Nama yaadannoo keenya irraa garagalee, jiruu addunyaa malee hin fedhin irraa garagali');
INSERT INTO "apaguna" VALUES(53,30,'53:30','Kun beekumsa irraa daangaa isaaniitiDhugumatti, Rabbiin kee Isumatu nama karaa Isaa irraa jallate akkaan beekaadhaInni nama qajeeles akkaan beekaadha.');
INSERT INTO "apaguna" VALUES(53,31,'53:31','Wanti samii keessa jiruufi wanti dachii keessa jirus kan RabbiitiIsaan hamtuu dalagan waan isaan dalaganiin isaaniif mindeessuufi isaan toltuu hojjatanis ishee caaltuu taate isaan mindeessuuf (uume).');
INSERT INTO "apaguna" VALUES(53,32,'53:32','Isaan sun warra dogoggora xixiqqoo malee dilii gugurdoofi sagaagalummaa irraa fagaataniidhaDhugumatti, Gooftaan kee araaramni Isaa bal’aadhaInni yeroo dachii irraa isin uumeefi yeroo isin garaa haadha keessanii keessatti ulfa taatanii jirtan akkaan isin beekaLubbuu keessan hin qulqulleessinaaInni nama (Isa) sodaatu akkaan beekaadha.');
INSERT INTO "apaguna" VALUES(53,33,'53:33','Mee isa gara gale naaf himi?');
INSERT INTO "apaguna" VALUES(53,34,'53:34','Kan xiqqoo kennee, ergasii dhoorgate.');
INSERT INTO "apaguna" VALUES(53,35,'53:35','Sila beekumsi fagoo isa bira jiraannaan inni (isa) beekee?');
INSERT INTO "apaguna" VALUES(53,36,'53:36','Moo wanta barreeffamoota Muusaa keessa jirutu itti hin himaminii?');
INSERT INTO "apaguna" VALUES(53,37,'53:37','Kan Ibraahiim isa (ajaja Rabbii) guutees');
INSERT INTO "apaguna" VALUES(53,38,'53:38','(Isheen sun) lubbuun dilii baadhattu ba’aa lubbuu biraa hin baadhattuudha.');
INSERT INTO "apaguna" VALUES(53,39,'53:39','Namaafis waan hojjate malee hintaaneedha.');
INSERT INTO "apaguna" VALUES(53,40,'53:40','Hojiin isaas of duratti mul’achuuf jiraadha');
INSERT INTO "apaguna" VALUES(53,41,'53:41','Achii booda mindaa guutuutu isaaf kennamaadha.');
INSERT INTO "apaguna" VALUES(53,42,'53:42','Xumurri (waan hundaas) gara Rabbiitti ta’uudha.');
INSERT INTO "apaguna" VALUES(53,43,'53:43','Inni Isumatu kofalchiise; boochises.');
INSERT INTO "apaguna" VALUES(53,44,'53:44','Inni Isumatu ajjeese; jiraachises.');
INSERT INTO "apaguna" VALUES(53,45,'53:45','Inni cimdii lama, kormaafi dhalaa, uume.');
INSERT INTO "apaguna" VALUES(53,46,'53:46','Bishaan hormaataa irraa (uume), yeroo buufamtu');
INSERT INTO "apaguna" VALUES(53,47,'53:47','Uumuun boodaas Rabbuma irra jira.');
INSERT INTO "apaguna" VALUES(53,48,'53:48','Inni Isa duroomseefi hiyyoomseedha.');
INSERT INTO "apaguna" VALUES(53,49,'53:49','Inni Isumatu Gooftaa Shi’iraati');
INSERT INTO "apaguna" VALUES(53,50,'53:50','Isumatu Aadii duraa balleesse.');
INSERT INTO "apaguna" VALUES(53,51,'53:51','(Ummata) Samuudis hin hambifne.');
INSERT INTO "apaguna" VALUES(53,52,'53:52','Ummata Nuuhis sana dura (balleesse)Isaan isaanumatu akkaan miidhaa raawwattootaafi daangaa darbitoota turan.');
INSERT INTO "apaguna" VALUES(53,53,'53:53','Ganda gombifamtuus gadi kuffise.');
INSERT INTO "apaguna" VALUES(53,54,'53:54','Wanti haguuge ishee haguuge.');
INSERT INTO "apaguna" VALUES(53,55,'53:55','Ni’imaa Rabbii keetii irraa kamiin shakkita?');
INSERT INTO "apaguna" VALUES(53,56,'53:56','Kun (Muhammad SAW) sodaachisaa sodaachistoota durii irraa ta’eedha.');
INSERT INTO "apaguna" VALUES(53,57,'53:57','Dhihaattuun (Qiyaamaa) dhihaatteetti.');
INSERT INTO "apaguna" VALUES(53,58,'53:58','Rabbiin ala isheef mul’iftuun hin jirtu.');
INSERT INTO "apaguna" VALUES(53,59,'53:59','Sila haasa’a kana (Qur’aana) irraa dinqisiifattanii?');
INSERT INTO "apaguna" VALUES(53,60,'53:60','Ni kolfituu! Hin boochanuu?');
INSERT INTO "apaguna" VALUES(53,61,'53:61','Haala sirbitanuun (irraa garagaltuu)?');
INSERT INTO "apaguna" VALUES(53,62,'53:62','Allaah’f sujuudaa; (Isuma qofa) gabbaraa');
INSERT INTO "apaguna" VALUES(54,1,'54:1','Qiyaamaan kaloofte; jiinis lamatti addaan dhoohe.');
INSERT INTO "apaguna" VALUES(54,2,'54:2','Yeroo mallattoo wahii argan gara galaniiti “(kun) sihrii cimaadha” jedhu.');
INSERT INTO "apaguna" VALUES(54,3,'54:3','(Qur’aana) sobsiisanii fedha lubbuu isaanii hordofuWanti hunduu galma ga’aadha.');
INSERT INTO "apaguna" VALUES(54,4,'54:4','Dhugumatti, oduu irraa wanti isa keessa dhorgaan jiru isaanitti dhufeera.');
INSERT INTO "apaguna" VALUES(54,5,'54:5','Beekumsi ga’aan (isaanitti dhufeera)Akeekkachiistonni homaa isaan hin fayyadu.');
INSERT INTO "apaguna" VALUES(54,6,'54:6','Isaan irraa garagaliGuyyaa waamaan gara waan rifachiisaa ta’eetti (isaan) waamu;');
INSERT INTO "apaguna" VALUES(54,7,'54:7','(Guyyaa san) ijji isaanii sodaan guutamteeti, awwaala (isaanii) irraa ba’anii akka awwaannisa faca’ee fakkaatu');
INSERT INTO "apaguna" VALUES(54,8,'54:8','Gara waamaatti ori’uKaafiroonni “Kun guyyaa (rakkisaa) cimaadha.” jedhu.');
INSERT INTO "apaguna" VALUES(54,9,'54:9','Isaaniin dura ummanni Nuuh sobsiifteeraGabricha keenya (Nuuh) sobsiisanii, “inni maraataa dha, dhorgameeras.” jedhan');
INSERT INTO "apaguna" VALUES(54,10,'54:10','“Ani injifatamaa dhaa naaf tumsi” (jedhee) Gooftaa isaa kadhate.');
INSERT INTO "apaguna" VALUES(54,11,'54:11','Bishaan dhangala’aa ta’een hulaalee samii itti banne.');
INSERT INTO "apaguna" VALUES(54,12,'54:12','Burqituuwwan dachii burqisiifnee bishaan (samiifi dachii) murtii dhugumaan murtaa’e irratti walitti dhufan.');
INSERT INTO "apaguna" VALUES(54,13,'54:13','Doonii abbaa xawulaafi mismaaraa irratti isa baadhanne (feene)');
INSERT INTO "apaguna" VALUES(54,14,'54:14','Galata isa itti kafaramee taatee tika keenyaan deemti.');
INSERT INTO "apaguna" VALUES(54,15,'54:15','Dhugumatti, Nuti mallattoo akka taatuuf ishee dhiifneNamni ittiin gorfamu jiraa laata?');
INSERT INTO "apaguna" VALUES(54,16,'54:16','(Yeroo san) adabbiin kiyyaafi sodaachisni kiyyas akkam ture laata?');
INSERT INTO "apaguna" VALUES(54,17,'54:17','Dhugumatti, Qur’aanas hubachuudhaaf laaffifneerraNamni ittiin gorfamu jiraa?');
INSERT INTO "apaguna" VALUES(54,18,'54:18','(Ummanni) Aad (Huudiin) sobsiifteadabbiin kiyyaafi akeekkachiifni kiyyaas akkam ta’e ree?');
INSERT INTO "apaguna" VALUES(54,19,'54:19','Qilleensa qorraa guyyaa hamaa itti fufaa ta’e keessa isaanitti ergine.');
INSERT INTO "apaguna" VALUES(54,20,'54:20','Nama ni butti(Yeroo san) jirma teemiraa kan dachii irraa buqqa’e fakkaatu.');
INSERT INTO "apaguna" VALUES(54,21,'54:21','(Yeroo san) adabbiin kiyyaafi akeekkachiisni kiyyas akkam ture laata?');
INSERT INTO "apaguna" VALUES(54,22,'54:22','Dhugumatti, Qur’aana hubachuuf laaffifnee jirraNamni gorfamu jiraa?');
INSERT INTO "apaguna" VALUES(54,23,'54:23','(Ummanni) Samuudis akeekkachiisa (kiyya) sobsiisaniiru.');
INSERT INTO "apaguna" VALUES(54,24,'54:24','Ni jedhan: “nama nurraa ta’e tokko hordofnaa? Nuti oggas jallinaafi maraatuu keessa jirra.');
INSERT INTO "apaguna" VALUES(54,25,'54:25','Sila yaadachiifni kun nu hunda gidduu isa (Nabi Saalih) qofa irratti buufamee? Lakkisaa! Inni akkaan sobaa, boonaadha.');
INSERT INTO "apaguna" VALUES(54,26,'54:26','Sobaan boonaan eenyuun akka ta’e boru beekuuf jiru.');
INSERT INTO "apaguna" VALUES(54,27,'54:27','Dhugumatti, Nuti gaala dhaltuu isaaniin mokkoruuf ergoodhaKanaaf (Yaa Saalih!) Isaan eegi; obsis.');
INSERT INTO "apaguna" VALUES(54,28,'54:28','Bishaan (gaalattii fi) isaan gidduutti qoodamus isaanitti himiDhugaatiin (garee) hundaayyuu dhufamaadha.');
INSERT INTO "apaguna" VALUES(54,29,'54:29','Hiriyaa isaaniitti lallabaniiti, (waraana isaa) fudhatee waraane');
INSERT INTO "apaguna" VALUES(54,30,'54:30','(Yeroo san) adabbiin kiyyaafi akeekkachiisni kiyyaa akkam ture laata?');
INSERT INTO "apaguna" VALUES(54,31,'54:31','Dhugumatti, Nuti iyya tokkittii isaan irratti ergineErgasii akka caccabaa mooraa horiin irra deemee tahan.');
INSERT INTO "apaguna" VALUES(54,32,'54:32','Dhugumatti, Qur’aana hubachuuf laaffifneerraEgaa namni gorfamu jiraa?');
INSERT INTO "apaguna" VALUES(54,33,'54:33','Ummanni “Luux” dinniina sobsiisaniiru.');
INSERT INTO "apaguna" VALUES(54,34,'54:34','Dhugumatti, Nuti isaan irratti bubbee cirrachaa erginee jirraMaatii Luux malee, (yeroo) bariisaa isaan baraarsine.');
INSERT INTO "apaguna" VALUES(54,35,'54:35','Qananii Nu biraa ta’eef (baraarsine)Akka kanatti nama galata Nuuf galche mindeessina.');
INSERT INTO "apaguna" VALUES(54,36,'54:36','Dhugumatti, inni (Luux) qabaa keenya isaan akeekkachiisee jiraIsaan garuu dinniinicha mormaniiru.');
INSERT INTO "apaguna" VALUES(54,37,'54:37','Dhugumatti, keessummaa isaa irraa (badii itti hojjachuuf) isa gaafatanKanaaf ija isaanii jaamsinee “adaba kiyyaafi dinniina (kiyyas) dhandhamaa” (jenneen).');
INSERT INTO "apaguna" VALUES(54,38,'54:38','Dhugumatti, adabbiin cimaan bariidhaan isaanitti ganamfate.');
INSERT INTO "apaguna" VALUES(54,39,'54:39','Adaba kiyyaafi dinniina (kiyyas) dhandhamaa” (isaaniin jenne).');
INSERT INTO "apaguna" VALUES(54,40,'54:40','Dhugumatti, Qur’aana hubachuuf laaffifneerraEgaa namni gorfamu jiraa?');
INSERT INTO "apaguna" VALUES(54,41,'54:41','Dhugumatti, maatii Fir’awnitti akeekkachiifni dhufeera.');
INSERT INTO "apaguna" VALUES(54,42,'54:42','Mallattoolee keenya hunda sobsiisaniiti, qabaa Isa injifataa, danda’aa ta’ee isaan qabne.');
INSERT INTO "apaguna" VALUES(54,43,'54:43','(Yaa Qureeyshotaa!) Sila kaafiroota keessantu isaan kanneen irra caala moo kitaabota keessa (adabbii irraa) baraaramuutu isiniif jira?');
INSERT INTO "apaguna" VALUES(54,44,'54:44','Hinumaayyuu “Nuti tuuta injifattoota” jedhu.');
INSERT INTO "apaguna" VALUES(54,45,'54:45','Tuutni isaanii cabsameeti dugda gara galuuf jiru.');
INSERT INTO "apaguna" VALUES(54,46,'54:46','Dhugumatti, Qiyaamaan beellama isaaniitiQiyaamaan irra ulfaattuufi akkaan hadhooftuudha.');
INSERT INTO "apaguna" VALUES(54,47,'54:47','Dhugumatti, yakkamtoonni jallinaafi ibidda qabsiifamaa keessatti ta’u.');
INSERT INTO "apaguna" VALUES(54,48,'54:48','Guyyaa fuula isaanii irratti ibidda keessa harkifaman, “tuqiinsa Saqar dhandhamaa” (jedhamaan).');
INSERT INTO "apaguna" VALUES(54,49,'54:49','Nuti waan hunda hamma murtaa’een isa uumne.');
INSERT INTO "apaguna" VALUES(54,50,'54:50','Ajajni keenya akkuma liphsuu ijaa yeroo tokko qofa.');
INSERT INTO "apaguna" VALUES(54,51,'54:51','Dhugumatti, Nuti fakkaattota keessan balleessinee jirraNamni gorfamu jiraa?');
INSERT INTO "apaguna" VALUES(54,52,'54:52','Wanti isaan hojjatan hunduu galmeewwan keessa jira.');
INSERT INTO "apaguna" VALUES(54,53,'54:53','Wanti xiqqaafi guddaan hundi barreeffamaadha.');
INSERT INTO "apaguna" VALUES(54,54,'54:54','Dhugumatti, warri Rabbiin sodaatan jannataafi laggeen keessa jiraatu.');
INSERT INTO "apaguna" VALUES(54,55,'54:55','Teessoo dhugaa (jannata) keessa Mootii danda’aa bira jiraatu.');
INSERT INTO "apaguna" VALUES(55,1,'55:1','Ar-Rahmaan.');
INSERT INTO "apaguna" VALUES(55,2,'55:2','Qur’aana barsiise.');
INSERT INTO "apaguna" VALUES(55,3,'55:3','Nama ni uume.');
INSERT INTO "apaguna" VALUES(55,4,'55:4','Haasa’a isa barsiise.');
INSERT INTO "apaguna" VALUES(55,5,'55:5','Aduufi jiini qixa murtaa’een deemu.');
INSERT INTO "apaguna" VALUES(55,6,'55:6','Biqilaafi mukkeenis (Allaahf) sujuudu.');
INSERT INTO "apaguna" VALUES(55,7,'55:7','Samii ol ishee fuudhe; madaalas ni kaaye.');
INSERT INTO "apaguna" VALUES(55,8,'55:8','Akka isin madaala keessatti daangaa hin dabrineef jecha.');
INSERT INTO "apaguna" VALUES(55,9,'55:9','Madaala sirriitti qajeelchaa; madaala hin hir’isinaa.');
INSERT INTO "apaguna" VALUES(55,10,'55:10','Dachiis uumama hundaaf ishee kaaye.');
INSERT INTO "apaguna" VALUES(55,11,'55:11','Ishee keessatti fuduraaleefi mukkeen temiraa kan qolola (qola) habaaboo qabantu jira.');
INSERT INTO "apaguna" VALUES(55,12,'55:12','Midhaan baalaafi urgaa qabantu jiru.');
INSERT INTO "apaguna" VALUES(55,13,'55:13','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,14,'55:14','(Rabbiin) ilma namaa dhoqqee akka okkoteetti iyyu (suphee) irraa uume.');
INSERT INTO "apaguna" VALUES(55,15,'55:15','Sheyxaana immoo boba’aa ibiddaa kan aara hin qabne irraa uume.');
INSERT INTO "apaguna" VALUES(55,16,'55:16','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,17,'55:17','(Rabbiin) Gooftaa baha lamaaniifi Gooftaa dhiha lamaaniiti.');
INSERT INTO "apaguna" VALUES(55,18,'55:18','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,19,'55:19','Galaanota lamaan kan wal qunnaman taasisee gadi lakkise.');
INSERT INTO "apaguna" VALUES(55,20,'55:20','Gidduu isaanii dahootu jira; walitti hin makamanu.');
INSERT INTO "apaguna" VALUES(55,21,'55:21','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,22,'55:22','Isaan lamaan irraayyuu faayaafi burjaanaatu baha.');
INSERT INTO "apaguna" VALUES(55,23,'55:23','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,24,'55:24','Dooniiwwan tolfaman kan akka gaarreenii galaana keessa yaa’an kan Isaati.');
INSERT INTO "apaguna" VALUES(55,25,'55:25','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,26,'55:26','Wanti ishee (dachii) gubbaa jiru hundi ni dhabama.');
INSERT INTO "apaguna" VALUES(55,27,'55:27','Fuula Gooftaa keetii, kan guddinaafi ulfinaatu hafa.');
INSERT INTO "apaguna" VALUES(55,28,'55:28','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,29,'55:29','Wanti samiifi dachii keessa jiru hundi Isa kadhataGuyyaa hunda Inni hojiitti jira.');
INSERT INTO "apaguna" VALUES(55,30,'55:30','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,31,'55:31',' Yaa ulfaattota lamaanii (jinniifi namaa) fuulduratti isiniif raawwanna.');
INSERT INTO "apaguna" VALUES(55,32,'55:32','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,33,'55:33','Yaa garee jinniifi namaa! Yoo moggaawwan samiifi dachii qaxxaamuruu dandeessan, qaxxaamuraaDandeettiin malee hin qaxxaamurtan.');
INSERT INTO "apaguna" VALUES(55,34,'55:34','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,35,'55:35','Isin lamaan irratti boba’aa Ibidda irraa ta’eefi sibiila baqetu ergamaErgasii of irraa dhorguu hin dandeessanu.');
INSERT INTO "apaguna" VALUES(55,36,'55:36','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,37,'55:37','Yeroo samiin dhodhootee diimtuu akka dhadhaa taate.');
INSERT INTO "apaguna" VALUES(55,38,'55:38','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,39,'55:39','Ilmi namaas ta’ee jinniin guyyaa san dilii isaa irraa hin gaafatamu.');
INSERT INTO "apaguna" VALUES(55,40,'55:40','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,41,'55:41','Yakkamtoonni mallattoo isaaniitiin beekamuSammuufi miila ni qabamu');
INSERT INTO "apaguna" VALUES(55,42,'55:42','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,43,'55:43','“Kun jahannam ishee yakkamtoonni sobsiisaa turaniidha” (jedhama).');
INSERT INTO "apaguna" VALUES(55,44,'55:44','Gidduu isheetiifi gidduu bishaan akkaan danfaa ta’ee naanna’u.');
INSERT INTO "apaguna" VALUES(55,45,'55:45','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,46,'55:46','Nama fuulduree Gooftaa isaa dhaabbachuu sodaateef jannata lamatu jira.');
INSERT INTO "apaguna" VALUES(55,47,'55:47','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,48,'55:48','Kan dameewwan dhedheeroo qaban');
INSERT INTO "apaguna" VALUES(55,49,'55:49','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,50,'55:50','Isaan lamaan keessa madda yaa’an lamatu jira.');
INSERT INTO "apaguna" VALUES(55,51,'55:51','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,52,'55:52','Isaan lamaan keessa fuduraalee hunda irraa cimdiitu jira.');
INSERT INTO "apaguna" VALUES(55,53,'55:53','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,54,'55:54','Afata hariira furdaan guutame irratti hirkatanii taa’uFuduraaleen jannata lamaanii dhihoodha.');
INSERT INTO "apaguna" VALUES(55,55,'55:55','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,56,'55:56','Isaan keessa (dubartoota bareeda) ija isaanii dhirsa isaanii irratti gabaabsan, kan san dura namniifi jinniin hin tuqintu jira.');
INSERT INTO "apaguna" VALUES(55,57,'55:57','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,58,'55:58','Faayaafi burjaana fakkaatu.');
INSERT INTO "apaguna" VALUES(55,59,'55:59','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,60,'55:60','Sila mindaan toltuu toltuu malee ni jiraa?');
INSERT INTO "apaguna" VALUES(55,61,'55:61','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,62,'55:62','Isaan lamaan malees jannata lamaatu jira.');
INSERT INTO "apaguna" VALUES(55,63,'55:63','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,64,'55:64','Magariisa gurraacha’aadha');
INSERT INTO "apaguna" VALUES(55,65,'55:65','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,66,'55:66','Isaan lamaan keessa burqituuwwan dhangala’an lamatu jiru.');
INSERT INTO "apaguna" VALUES(55,67,'55:67','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,68,'55:68','Isaan lamaan keessa fuduraalee, timiraafi rummaanatu jira.');
INSERT INTO "apaguna" VALUES(55,69,'55:69','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,70,'55:70','Isaan keessa gaggaarii, babbareedoo ta’antu jira.');
INSERT INTO "apaguna" VALUES(55,71,'55:71','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,72,'55:72','Dubartoota manneen keessatti (dhirsa irratti) gabaabfamantu jiru.');
INSERT INTO "apaguna" VALUES(55,73,'55:73','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,74,'55:74','Isaan dura namaafi jinniinis hin tuqne');
INSERT INTO "apaguna" VALUES(55,75,'55:75','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,76,'55:76','Afata magariisaafi boraatiiwwan babbareedoo irra hirkatanii taa’u.');
INSERT INTO "apaguna" VALUES(55,77,'55:77','Tola Rabbii keesanii keessaa kamiin kijibsiiftu?');
INSERT INTO "apaguna" VALUES(55,78,'55:78','Maqaan Gooftaa keetii, abbaa guddinnaafi ulfinaa guddate');
INSERT INTO "apaguna" VALUES(56,1,'56:1','Yeroo argamtuun (Qiyaamaan) argamte,');
INSERT INTO "apaguna" VALUES(56,2,'56:2','Argama isheetiif kijibni hin jirtu.');
INSERT INTO "apaguna" VALUES(56,3,'56:3','Gadi qabduu, ol fuutuudha.');
INSERT INTO "apaguna" VALUES(56,4,'56:4','Yeroo dachiin raafama cimaa raafamte,');
INSERT INTO "apaguna" VALUES(56,5,'56:5','(Yeroo) gaarreenis facaafamiinsa facaafamte,');
INSERT INTO "apaguna" VALUES(56,6,'56:6','Dhukkee facaafamtuus taate,');
INSERT INTO "apaguna" VALUES(56,7,'56:7','(Yeroo) Isinis garee sadi taatan,');
INSERT INTO "apaguna" VALUES(56,8,'56:8','(Gareen tokko) warra mirgaati; warri mirgaa akkam (milkaa’oo dha)!');
INSERT INTO "apaguna" VALUES(56,9,'56:9','(Gareen lammaffaan) warra bitaati; warri bitaa akkam (hoonga’oo dha)!');
INSERT INTO "apaguna" VALUES(56,10,'56:10','(Gareen sadaffaa) Isaan gara toltuutti dursitoota dursoodha.');
INSERT INTO "apaguna" VALUES(56,11,'56:11','Isaan (Rabbitti) dhiheeffamoota.');
INSERT INTO "apaguna" VALUES(56,12,'56:12','Jannata qananii keessatti ta’u.');
INSERT INTO "apaguna" VALUES(56,13,'56:13','Warra duraa irraa baay’eedha.');
INSERT INTO "apaguna" VALUES(56,14,'56:14','Warra boodaa irraa immoo xiqqoodha.');
INSERT INTO "apaguna" VALUES(56,15,'56:15','Sireewwan dhawamte irratti ta’u.');
INSERT INTO "apaguna" VALUES(56,16,'56:16','Fuulaan walitti garagalanii ishee irratti hirkatu.');
INSERT INTO "apaguna" VALUES(56,17,'56:17','Ijoollee (ijoollummaan) yeroo hunda jiraatantu isaan irra naanna’a');
INSERT INTO "apaguna" VALUES(56,18,'56:18','Kubbaayyaa, quluufi geeba (xoofoo) farsoo irraa ta’een.');
INSERT INTO "apaguna" VALUES(56,19,'56:19','Isii irraa hin bowwaafatan; hin machaa’anis.');
INSERT INTO "apaguna" VALUES(56,20,'56:20','Fuduraalee irraa waan isaan filataniin (irra naanna’u).');
INSERT INTO "apaguna" VALUES(56,21,'56:21','Foon allaattii irraas waan isaan fedhaniin');
INSERT INTO "apaguna" VALUES(56,22,'56:22','Dubra adaadii ija babal’ootu (isiniif jira.)');
INSERT INTO "apaguna" VALUES(56,23,'56:23','Akka faaya eegamaa kan fafakkaatan.');
INSERT INTO "apaguna" VALUES(56,24,'56:24','Mindaa waan hojjataa turaniif jecha (kana isaaniif goone).');
INSERT INTO "apaguna" VALUES(56,25,'56:25','Isii (jannata) keessatti wacaafi wal arrabsuus hin dhaga’an.');
INSERT INTO "apaguna" VALUES(56,26,'56:26','“Nagayaa nagayaa waliin jechuu malee” (hin dhaga’an).');
INSERT INTO "apaguna" VALUES(56,27,'56:27','Warri mirgaa; warra mirgaa waa’een isaanii akkam tola!');
INSERT INTO "apaguna" VALUES(56,28,'56:28','(Isaan) mukkeen qurquraa kan qoree hin qabne keessa jiraatu.');
INSERT INTO "apaguna" VALUES(56,29,'56:29','Muuza firiidhaan guutame (keessa jiraatu).');
INSERT INTO "apaguna" VALUES(56,30,'56:30','Gaaddisa diriirfamaa (keessa).');
INSERT INTO "apaguna" VALUES(56,31,'56:31','Bishaan dhangala’u (keessa).');
INSERT INTO "apaguna" VALUES(56,32,'56:32','Fuduraalee baay’ee (keessa).');
INSERT INTO "apaguna" VALUES(56,33,'56:33','Kan maddi isaanii hin cinneefi kan irraa hin dhorgamne (keessa).');
INSERT INTO "apaguna" VALUES(56,34,'56:34','Firaasholee ol fuudhamtuu (keessa jiraatu.)');
INSERT INTO "apaguna" VALUES(56,35,'56:35','Dhugumatti, Nuti (dubartoota jannataa) uuminsa isaan uumne.');
INSERT INTO "apaguna" VALUES(56,36,'56:36','Dubroota isaan goone.');
INSERT INTO "apaguna" VALUES(56,37,'56:37','Jaallattoota (abbaa manaa), kan hiriyoota ta’an (isaan goone).');
INSERT INTO "apaguna" VALUES(56,38,'56:38','Warra mirgaatiif (qophaa’an).');
INSERT INTO "apaguna" VALUES(56,39,'56:39','Hedduun (isaanii) warra duraa irraayi.');
INSERT INTO "apaguna" VALUES(56,40,'56:40','Warra boodaa irraahis baay’eedha.');
INSERT INTO "apaguna" VALUES(56,41,'56:41','Warri bitaa; warri bitaa akkam (fokkatu)!');
INSERT INTO "apaguna" VALUES(56,42,'56:42','Ibidda laalessaafi bishaan danfaa keessa jiraatu.');
INSERT INTO "apaguna" VALUES(56,43,'56:43','Gaaddisa aara gurraacha irraa ta’e keessa.');
INSERT INTO "apaguna" VALUES(56,44,'56:44','(Gaaddiisa) qabbanaa’aa hin ta’iniifi gaariis hin ta’in.');
INSERT INTO "apaguna" VALUES(56,45,'56:45','Dhugumatti, isaan sana dura qanansiifamoo turan.');
INSERT INTO "apaguna" VALUES(56,46,'56:46','Dilii guddaa itti fufu turan.');
INSERT INTO "apaguna" VALUES(56,47,'56:47','“Sila yeroo duunee biyyeefi lafee taane, dhugumatti nuti kaafamoodhaa?” jedhaa turan');
INSERT INTO "apaguna" VALUES(56,48,'56:48','“Abbootiin keenya kan jalqabaas moo?” (jechuu turan).');
INSERT INTO "apaguna" VALUES(56,49,'56:49','“(Yaa Muhammad!) dhugumatti, isaan jalqabaas ta’ee isaan boodaas (ni kaafamu)” jedhiin');
INSERT INTO "apaguna" VALUES(56,50,'56:50','Beellama guyyaa beekamaa keessatti walitti qabamuuf jiru.');
INSERT INTO "apaguna" VALUES(56,51,'56:51','Ergasii isin yaa jallattoota sobsiiftotaa!');
INSERT INTO "apaguna" VALUES(56,52,'56:52','Muka Zaqquum irraa nyaatoodha.');
INSERT INTO "apaguna" VALUES(56,53,'56:53','Ishee irraas garaa keessan guuttatoodha.');
INSERT INTO "apaguna" VALUES(56,54,'56:54','Isa irrattis bishaan danfaa dhugoodha.');
INSERT INTO "apaguna" VALUES(56,55,'56:55','Dhugaatii akka gaala dheebottee dhugdu.');
INSERT INTO "apaguna" VALUES(56,56,'56:56','Kun simannaa isaanii kan guyyaa murtiiti.');
INSERT INTO "apaguna" VALUES(56,57,'56:57','Nuti isin uumnee; maaliif hin dhugoomsine?');
INSERT INTO "apaguna" VALUES(56,58,'56:58','Waan (maniyyii) dhangalaaftan san mee naaf himaa?');
INSERT INTO "apaguna" VALUES(56,59,'56:59','Sila isintu isa uumamoo? Nutu uumoo dha?');
INSERT INTO "apaguna" VALUES(56,60,'56:60','Nutu du’a gidduu keessanitti murteesseNuti dadhabsiifamoo miti.');
INSERT INTO "apaguna" VALUES(56,61,'56:61','Fakkaataa keessan jijjiiree, waan isin hin beekne keessatti isin uumuu irratti (dadhaboo miti).');
INSERT INTO "apaguna" VALUES(56,62,'56:62','Dhugumatti, akkaataa uumama jalqabaa beektanii jirtu; maaliif hin gorfamne?');
INSERT INTO "apaguna" VALUES(56,63,'56:63','Mee waa’ee waan facaaftanii naaf himaa!');
INSERT INTO "apaguna" VALUES(56,64,'56:64','Isintu isa biqilcha moo Nutu biqilchoo dha?');
INSERT INTO "apaguna" VALUES(56,65,'56:65','Odoo feenee caccabaa gooneeti kan dinqisiifattan taatan.');
INSERT INTO "apaguna" VALUES(56,66,'56:66','“Nuti dhugumatti hoonga’oodha”(jettu).');
INSERT INTO "apaguna" VALUES(56,67,'56:67','“Lakkisaa; nuti dhorgamoodha” (waliin jettu).');
INSERT INTO "apaguna" VALUES(56,68,'56:68','Mee waa’ee bishaan dhugdanii san Naaf himaa?');
INSERT INTO "apaguna" VALUES(56,69,'56:69','Sila duumessa irraa isintu isa buusee? Moo Nutu (isa) buusoo dha?');
INSERT INTO "apaguna" VALUES(56,70,'56:70','Odoo feenee soogiddoo (kuraawaa) isa taasifnaSila maaliif (Rabbiin) hin galatoomfanne?');
INSERT INTO "apaguna" VALUES(56,71,'56:71','Mee waa’ee ibidda qabsiifattanii san naaf himaa?');
INSERT INTO "apaguna" VALUES(56,72,'56:72','Sila muka ishee isintu biqilchee? Moo Nutu biqilchoo dha?');
INSERT INTO "apaguna" VALUES(56,73,'56:73','Nuti ibidda kana gorsaafi qananii imaltoonni itti fayyadaman ishee goone.');
INSERT INTO "apaguna" VALUES(56,74,'56:74','Maqaa Gooftaa kee Isa guddaa faarsi.');
INSERT INTO "apaguna" VALUES(56,75,'56:75','Seensa urjiiwwaniitiniin kakadha');
INSERT INTO "apaguna" VALUES(56,76,'56:76','Inni dhugumatti, odoo kan beektan ta’ee kakuu guddaadha.');
INSERT INTO "apaguna" VALUES(56,77,'56:77','Dhugumatti, inni Qur’aana kabajamaadha.');
INSERT INTO "apaguna" VALUES(56,78,'56:78','Kitaaba akkaan eegamaa (Lawhal Mahfuuz) keessa jira.');
INSERT INTO "apaguna" VALUES(56,79,'56:79','Warra qulqulluu malee (eenyulleen) isa hin tuqu.');
INSERT INTO "apaguna" VALUES(56,80,'56:80','Gooftaa aalama hundaa irraa buufamaadha.');
INSERT INTO "apaguna" VALUES(56,81,'56:81','Sila haasa’a kanaa kan isin laaffiftan?');
INSERT INTO "apaguna" VALUES(56,82,'56:82','(Galata) rizqii (rooba) keessanii kijibsiisuu gootanii?');
INSERT INTO "apaguna" VALUES(56,83,'56:83','Yeroo (lubbuun) kokkee geesse, (maaliif hin deebiftanuu)?');
INSERT INTO "apaguna" VALUES(56,84,'56:84','Odoo yeroo san isin ilaaltanuu,');
INSERT INTO "apaguna" VALUES(56,85,'56:85','Isin hin argitan malee Nuti isin caalaa gara isaatti dhihoo jirra.');
INSERT INTO "apaguna" VALUES(56,86,'56:86','Yoo (kaafamtanii) kan galata hin galfamne taatan (lubbuu san deebisaa).');
INSERT INTO "apaguna" VALUES(56,87,'56:87','Yoo kan dhugaa dubbattan taatan maaliif hin deebiftanu?');
INSERT INTO "apaguna" VALUES(56,88,'56:88','Yoo inni (namni du’e sun) namoota Rabbitti dhiyeeffaman irraa ta’e,');
INSERT INTO "apaguna" VALUES(56,89,'56:89','(Isaaf) haara galfii, gammachuufi jannata qananiitu jira.');
INSERT INTO "apaguna" VALUES(56,90,'56:90','Yoo inni warra mirgaa irraa ta’e immoo,');
INSERT INTO "apaguna" VALUES(56,91,'56:91','Warra mirgaa irraa waan taateef nagaan siif haa jiraatu” (isaan jedhama)');
INSERT INTO "apaguna" VALUES(56,92,'56:92','Yoo sobsiiftota jallattoota irraa ta’e immoo,');
INSERT INTO "apaguna" VALUES(56,93,'56:93','Isaaf afeerraa bishaan danfaa irraa ta’etu jira.');
INSERT INTO "apaguna" VALUES(56,94,'56:94','Jahiim seenuutu isaaf jira.');
INSERT INTO "apaguna" VALUES(56,95,'56:95','Dhugumatti, inni (wanti suuraan ibsite) kun haqa mirkanaa’aadha.');
INSERT INTO "apaguna" VALUES(56,96,'56:96','Maqaa Gooftaa kee guddaa ta’ee faarsi (qulqulleessi).');
INSERT INTO "apaguna" VALUES(57,1,'57:1','Wanti samiifi dachii keessa jiru hundi Rabbiin qulqulleesseInni injifataa, Ogeessa.');
INSERT INTO "apaguna" VALUES(57,2,'57:2','Mootummaan samiifi dachii kan IsaatiInni ni jiraachisa; ni ajjeesasInni waan hunda irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(57,3,'57:3','Inni duruma jira; kan boodaatis; mul’ataadha; dhokataas (argaa dha); Inni waan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(57,4,'57:4','Inni Isa samiifi dachii guyyoota jaha keessatti uumee; ergasii ‘Arshii’ irratti (ol ta’iinsa isaafmalu) ol ta’eedhaWaan dachii keessa gadi seenuufi waan ishee irraa ba’u, waan samii irraa bu’uufi waan ishee keessa ol ba’us ni beekaInni eessallee yoo jiraattan isin waliin jiraRabbiin waan isin dalagdan hunda ni arga');
INSERT INTO "apaguna" VALUES(57,5,'57:5','Mootummaan samiifi dachii kan IsaatiDhimmi hunduu gara Rabbiitti deebifamti.');
INSERT INTO "apaguna" VALUES(57,6,'57:6','Halkan guyyaa keessa galcha; guyyaas halkan keessa galchaInni beekaa waan qoma keessa jiruuti.');
INSERT INTO "apaguna" VALUES(57,7,'57:7','Rabbiifi ergamaa Isaatti amanaa; waan Inni isa keessatti bakka bu’aa isin taasise irraas kennadhaaIsin irraa isaan amananii, arjooman, isaaniif mindaa guddaatu jira');
INSERT INTO "apaguna" VALUES(57,8,'57:8','Odoo ergamaan akka gooftaa keessanitti amantaniif isin waamuu, odoo waadaa keessan isin irraa fuudhee jiruu, yoo mu’uminoota taataniif, sila maaltu isinii jiraaf Rabbitti amanuu diddan?');
INSERT INTO "apaguna" VALUES(57,9,'57:9','Inni Isa gabricha Isaa irratti keeyyattoota ifa ba’aa ta’an dukkana irraa gara ifaatti isin baasuuf jecha buuseedhaAllaah’n isin mararfataa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(57,10,'57:10','Karaa Rabbii keessatti kan hin arjoomne maal taataniiti? Dhaalli samiifi dachiis kan Allaah qofaIsin irraa namni injifannoo (Makkaa) dura arjoomeefi qabsaa’e (isaan sana booda arjoomaniifi qabsaa’an wajjiin) wal hin qixxaa’anIsaan sun warra sana booda arjoomaniifi qabsaa’an irra sadarkaa guddaa qabuHundaa Rabbiin (mindaa) gaarii waadaa galeefii jiraRabbiin iccitii waan isin dalagdanii beekaadha.');
INSERT INTO "apaguna" VALUES(57,11,'57:11','Namni Rabbiif liqii gaarii liqeessee, Inni (Rabbiin) dachaa isaaf taasisuun mindaan gaariin isaaf ta’u eenyu laata?');
INSERT INTO "apaguna" VALUES(57,12,'57:12','Guyyaa mu’uminoota dhiiraafi dubartootaa kan ifaan isaanii fuuldura isaanitiifi mirga isaanii irra adeemu argitu “gammachuun keessan har’a, ishee keessatti hafaa taatanii oyruu jala ishee laggeen yaatuudha; Sanatu milkii guddaadha” (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(57,13,'57:13','Guyyaa munaafiqoonni dhiirotaafi dubartootaa isaan amananiin “nu eegaa! Ifaa keessan irraa qabsiifannaa!” jedhan, “Gara booda keessaniitti deebi’aa! Ifaa achii barbaadaa” jedhamaGidduu isaaniitti jogola (dahoo) balbala qabu kan keessa isaa rahmanni, ala isaa immoo adabbiin jirutu taasifama.');
INSERT INTO "apaguna" VALUES(57,14,'57:14','(Munaafiqoonni) isaan (mu’uminoota) waamaniiti “Nuti isin waliin hin turree?” (jedhuun)“Eeyyeen! Garuu isin lubbuu keessan jalliftanii, badii keenya eegaa turtanii, amantiis shakkitanii, hanga ajajni Rabbii dhufutti hawwiin sobaa isin gowwoomsitee, gowwoomsaan (sheyxaanni) (obsa) Rabbiitiin isin gowwoomse.');
INSERT INTO "apaguna" VALUES(57,15,'57:15','Har’a isiniifi isaan kafaran irraas kafaltiin hin fuudhamuTeessoon keessan ibidda ta’aIsheen jaallalloo keessaniDeebiin waa fokkate!');
INSERT INTO "apaguna" VALUES(57,16,'57:16','Warra amananiif, yeroon qalbiin isaanii zikrii Rabbiitiifi dhugaa irraa waan bu’eef laaftu hin dhihaannee? Akka warra isaaniin dura kitaaba kennamanii, yeroon itti dheerattee, qalbiin isaanii goggogdee, baay’een isaanii shari’aa irraa bahanii tahuu dhabutti (hin dhihaannee?)');
INSERT INTO "apaguna" VALUES(57,17,'57:17','Beekaa! Dhugumatti, Rabbiin dachii erga isheen duutee booda jiraachisaDhugumatti, akka isin hubattaniif jecha keeyyattoota (keenya) isiniif addeessinee jirra.');
INSERT INTO "apaguna" VALUES(57,18,'57:18','Dhiiroliin sadaqaa kennatan, dubartoonni sadaqaa kennatte, kanneen liqaa gaarii Rabbiif liqeessan, (mindaan isaanii) dachaa isaaniif ta’aMindaan gaarii ta’ees isaaniif jira');
INSERT INTO "apaguna" VALUES(57,19,'57:19','Isaan Rabbiifi ergamoota Isaatti amanani, isaantu warra dhugaan amananiidhaWareegamtoonni Gooftaa isaanii biratti mindaa isaanitiifi ibsaa isaaniitu isaaniif jiraIsaan kafaranii keeyyattoota keenya sobsiisan immoo isaan sun warra ibiddaati.');
INSERT INTO "apaguna" VALUES(57,20,'57:20','Jireenyi addunyaa tapha, gammachuu yeroo gabaabaa, miidhagina, dhaaddannoo gidduu keessaniitiifi qabeenyaafi ilmaan baay’ifatuu qofa akka ta’e beekaaAkka fakkeenya bokkaa, margi isaa qotee bulaa dinqisiisee, kan ergasii gogee daalachaa’aa ta’uu argitee, ergasii caccabaa ta’eetiAakhiratti adabbii cimaa, araaramaafi jaalala Rabbi irraa ta’etu jiraJireenyi addunyaa meeshaa daguuti malee waan biraatii miti.');
INSERT INTO "apaguna" VALUES(57,21,'57:21','Gara araarama Gooftaa keessan irraa ta’eefi jannata bal’inni ishee akka bal’ina samiifi dachii ta’eetti wal dorgomaaIsaan Rabbiifi ergamtoota Isaatti amananiif qophoofteKun tola Rabbii kan Inni nama fedheef kennuudhaRabbiin abbaa tola guddaati.');
INSERT INTO "apaguna" VALUES(57,22,'57:22','Rakkoo irraa wanti dachiifi lubbuu keessan keessatti isin tuqe kamillee, odoo ishee hin uumin dura kitaaba keessatti galmeessinu malee hin hafuDhugumatti, kun Rabbi irratti laafaadha.');
INSERT INTO "apaguna" VALUES(57,23,'57:23','Akka waan isin jalaa dabre irratti hin yaaddofneefi waan (Rabbiin) isiniif kennettis (garmalee) hin gammadneefi (kana godhe)Rabbiin nama boonaa, dhaaddataa hunda hin jaallatu.');
INSERT INTO "apaguna" VALUES(57,24,'57:24','Isaan warra doynoomanii, namallee doynummatti ajajaniidhaNamni (amantii irraa) garagales Rabbiin dureessa faarfamaadha.');
INSERT INTO "apaguna" VALUES(57,25,'57:25','Dhugumatti, ergamtoota keenya ragaawwan ifa ta’aniin erginee, kitaabaafi madaalas akka namoonni haqaan dhaabbataniif jecha isaan waliin buufnee jirraSibiilas kan isa keessa humna cimaafi namootaaf bu’aawwan jiru goonee buufneerraRabbiin nama Isaafi ergamtoota Isaa fagootti (ghaybiitti) tumsanu mul’isuuf (kana godhe)Dhugumatti, Rabbiin jabaa, injifataadha.');
INSERT INTO "apaguna" VALUES(57,26,'57:26','Dhugumatti, Nuuhiifi Ibraahiimiin erginee jirraSanyii isaan lamaanii keessatti nabiyyummaafi kitaaba goonee jirraIsaan irraa namoota qajeelantu jira; baay’een isaan irraa ta’an immoo finciltoota');
INSERT INTO "apaguna" VALUES(57,27,'57:27','Ergasii faana isaanii irra ergamtoota keenya hordofsiifneIisaa ilma Maryamis hordofsiifnee, Injiiliin isaaf kenninee jirraOnnee isaan isa (Iisaa) hordofanii keessattis namaaf rifatuufi gara laafummaa gooneMoloksummaas ni eegalanJaalala Rabbii barbaaduuf (eegalan) malee Nuti isaan irratti hin barreessine(Garuu ) dhugaa eeggachuu ishee hin eegganneIsaan irraa warra amananiif mindaa kenninaBaay’een isaanii immoo finciltoota.');
INSERT INTO "apaguna" VALUES(57,28,'57:28','Yaa warra (Muusaafi Iisaatti) amantan! Rabbiin sodaadhaaErgamaa Isaattis (Muhammadittis SAW) amanaa! (Rabbiin) rahmata Isaa irraa dachaa isiniif kenna; ifaa ittiin deemtanis isiniif taasisa; isiniifis ni araaramaRabbiin araaramaa, mararfataadha.');
INSERT INTO "apaguna" VALUES(57,29,'57:29','Abbootiin kitaabaa (Yahuudonniifi Kiristaanonni) qananii Rabbii irratti homaa murteessuu akka hin dandeenye, qananiin harka Rabbii akka jiruufi Inni nama fedheef akka kennu haa beekanuuf (kana ibse)Rabbiin abbaa tola guddaati.');
INSERT INTO "apaguna" VALUES(58,1,'58:1','Dhugumatti Rabbiin jecha ishee waa''ee abbaa manaa ishee keessatti siin wal –falmituufi gara Rabbiitti himannaa dhiyeeffattuu dhaga''eeraRabbiinis falmii keessan lamaan ni dhaga''aDhugumatti Rabbiin dhagayaa, argaadha.');
INSERT INTO "apaguna" VALUES(58,2,'58:2','Isaan isin keessaa dubartoota (haadhotii manaa) isaanii irraa ‘Zihaara’ godhan (‘ati anaaf akka dugda haadha kiyyaati’ jechuun kakatan), isaan (haadhotiin manaa) haadholii isaanii mitiHaadholiin isaanii isaanuma isaan da''an sana qofaDhugumatti, isaan jecha irraa isa fokkataafi soba ta''e dubbatuDhugumatti Rabbiinis dhiifama godhaa araaramaadha.');
INSERT INTO "apaguna" VALUES(58,3,'58:3','Isaan dubartoota isaanii irraa "Zihaara" godhanii ergasii waan jedhan keessa deebi''anus, odoo wal hin tuqin dura morma (gabraa) bilisa baasuutu isaan irra jiraKana akka isin ittiin gorfamtaniif (Rabbiin tume)Rabbiinis waan isin hojjattan keessa beekaadha.');
INSERT INTO "apaguna" VALUES(58,4,'58:4','Namni hin argatinis, odoo wal hin tuqin dura ji''oota walitti aanan lama soomuutu isaan irra jiraNamni (kanas) hin danda''in, miskiinoota (hiyyeeyyii) jaahaatama nyaachisuu qabaKun akka Rabbiifi ergamaa Isaatti amantaniifiAkkasumas, isheen kun daangaa RabbiitiKaafirootafis adabbii laaleessaa ta''etu jira.');
INSERT INTO "apaguna" VALUES(58,5,'58:5','Odoo Nuti dhugumaan ragaa ifaa buufnee jirruu warren Rabbiifi ergamaa Isaa faallessan akkuma warri isaaniin duraa salphatan salphataniiruKaafirootaafis adabbii xiqqeessaatu jira.');
INSERT INTO "apaguna" VALUES(58,6,'58:6','Guyyaa Rabbiin hunda isaanii kaasee waan isaan hojjatan isaanitti himu [yaadadhu](Isaan) kan dagatan ta’anii Rabbiin beekee galmeesseraRabbiinis waan hunda irratti ragaadha.');
INSERT INTO "apaguna" VALUES(58,7,'58:7','Sila Rabbiin waan samii keessa jiruufi waan dachii keessa jiru kan beeku ta''uu hin argituu? Maqoon (iccitiin) gidduu namoota sadiitti, Inni afraffaa isaanii ta''u malee hin argamu, giddu namoota shaniittis Inni jahaffaa isaanii ta''u malee, giddu namoota sanaa gadiis ta''ee kan sanaa olii iddoma isaan ta’anitti isaan waliin ta’u malee (hin argamu)Ergasii, Guyyaa Qiyaamaa waan isaan dalagan (hunda) isaanitti odeessaDhugumatti, Rabbiin waan hunda irratti beekaadha.');
INSERT INTO "apaguna" VALUES(58,8,'58:8','Sila gara warra iccitii (badaa) irraa dhorgamanii, ergasii gara waan irraa dhorgamanii deebi''anii, akkasumas badii, miidhaafi ergamicha faallessuu irratti wal mari''atanuu hin ilaallee? Yeroo sitti dhufanis waan Rabbiin ittiin nagaa si hin gaafatiniin nagaa si gaafatuLubbuu isaanii keessatti (Gidduu isaaniitti) "dhuguma Muhammad yoo nabiyyii ta’eef) Rabbiin maaliif waan nuti jennuun nu hin adabne?" jedhuJahannam isaaniif ga''aadha; ishee seenuDeebichi (isaanii) waa fokkate!');
INSERT INTO "apaguna" VALUES(58,9,'58:9','Yaa warra (Rabbitti) amantan! Yeroo iccitii dubbattan badii, miidhaafi faallaa ajaja ergamaa Rabbii irratti hin mari''atinaatii toltuufi sodaa Rabbii irratti mari''adhaaRabbii gara Isaatti oofamtanis sodaadhaa.');
INSERT INTO "apaguna" VALUES(58,10,'58:10','Dhugumatti, iccitiin (badaan) sheyxaana qofa irraayi(Sheyxaanni) warra amanan ittiin gaddisiisuf (kana hojjata)Eeyyama Rabbiitiin alas waan tokkollee isaan hin miidhuMu’uminoonnis Rabbiin qofa irratti haa hirkatan.');
INSERT INTO "apaguna" VALUES(58,11,'58:11','Yaa warra (Rabbitti) amantan! Yeroo teessumma keessa waliif babal''isaa isiniin jedhame waliif babal''isaa; Rabbiin isiniif bal''isaaYeroo ka''aa jedhames ka''aaRabbiin isin irraa warra amananiifi isaan beekumsi kennameef sadarkaa baay’ee isaan ol kaasaRabbiinis waan isin hojjattan keessa Beekaadha.');
INSERT INTO "apaguna" VALUES(58,12,'58:12','.Yaa warra (Rabbitti) amantan! Yeroo ergamaa (Rabbii) wajjiin maqoo gochuu feetan, maqoo keessan dura sadaqaa heddu dursaa (kennaa)Kun isiniif irra caalaafi (dilii keessaniifis) irra qulquleessaadhaYoo (waan sadaqaa kennitan) hin argatin immoo, dhugumatti Rabbiin Araaramaa Rahmata Godhaadha.');
INSERT INTO "apaguna" VALUES(58,13,'58:13','Sila maqoo keessan (kan Nabiyyii SAW wajjiin gootan) dura sadaqaa dursuu sodaattanii? Yoo raawwachuu baattanis Rabbiin dhiifama isiniif godheeraSalaata dhaabaa; zakaas kennaa; Rabbiifi ergamaa Isaatiif ajajamaaRabbiin waan isin hojjattan keessa beekaadha.');
INSERT INTO "apaguna" VALUES(58,14,'58:14','Sila gara Isaan namoota Rabbiin itti dallane (Yahuudota) jaalallee godhatanii hin agarree? Isaan isin irraayis miti; isaan irraayis miti; odoo beekanuu kijiba irratti kakatu');
INSERT INTO "apaguna" VALUES(58,15,'58:15','Rabbiin adabbii cimaa isaanif qopheessee jiraDhugumatti, isaan wanti isaan hojjachaa turan waa fokkate!');
INSERT INTO "apaguna" VALUES(58,16,'58:16','Kakuu isaanii gaachena taasifatanii karaa Rabbii irraa (namoota) deebisuKanaafuu, isaaniif adabbii xiqqeessaatu jira.');
INSERT INTO "apaguna" VALUES(58,17,'58:17','Qabeenyi isaaniifi ijoolleen isaaniis (adabbii) Rabbii irraa wama tokko isaan hin duroomsanuIsaan sun warra Ibiddaati; isaan ishee keessatti hafoodha.');
INSERT INTO "apaguna" VALUES(58,18,'58:18','Guyyaa Rabbiin hunda isaanii (murtiif) kaasu sana akkuma isiniif kakataa turan Isaafis (Rabbiifis) kakatuWaan (haqa) wahii irra jiran of se''uDhagayaa! dhugumatti, isaanumatu sobdoota.');
INSERT INTO "apaguna" VALUES(58,19,'58:19','Sheyxaanni isaan irratti moo''ee faaruu Rabbii isaan dagachiiseeraIsaan garee sheyxaanatiDhaga''aa! Dhugumatti, gareen sheyxaanaa isaanumatu hoonga’oodha.');
INSERT INTO "apaguna" VALUES(58,20,'58:20','Isaan Rabbiifi ergamaa isaa faallessan, isaan sun warra gadi aanoo keessa ta’u..');
INSERT INTO "apaguna" VALUES(58,21,'58:21','Rabbiin "dhugumatti, Anaafi Ergamtoonni Kiyya ni mo''anna" (jechuun) murteesseeraDhugumatti, Rabbiin Jabaa, Injifataadha.');
INSERT INTO "apaguna" VALUES(58,22,'58:22','Ummata Rabbiifi Guyyaa Aakhiraatti amananu, namoota Rabbiifi ergamaa Isaa morman odoma abbootii isaanii, yookiin ilmaan isaanii, yookiin obboleeyyan isaanii, yookiin firoota isaanii ta''anillee, kan (isaan keessaan) jaallatanu hin argituIsaan sana (Rabbiin) onnee isaanii keessatti iimaana barreessee jira; tumsa Isa irraa ta''eenis isaan jabeesseeraJannatoota laggeen ishee jala yaatus achi keessaatti hafoo haala ta''aniin isaan seensisaRabbiin isaan irraa jaallateera; isaanis Isa irraa jaalataniiruIsaan garee RabbiitiDhagayaa! Dhugumatti, cifraan (gareen) Rabbii isaantu milkaawoodha.');
INSERT INTO "apaguna" VALUES(59,1,'59:1','Wantoonni samii keessaafi wantoonni dachii keessaa (hundi) Rabbiin qulqulleesseInnis Injifataa Ogeessa.');
INSERT INTO "apaguna" VALUES(59,2,'59:2','Inni Isa abbootii kitaabaa irraa isaan kafaran godaansa jalqabaatiif gandoota isaanii irraa baaseedhaBahuu isaanii isin hin yaadne turtanIsaanis da''annoowwan isaanii Rabbi irraa nu dhowwa jechuu yaadaniRabbiinis bakka isaan hin yaadin irraan isaanitti dhufee, onnee isaanii keessatti sodaa darbeManneen isaanii harka isaaniifi harka mu’uminootaatiin onsanYaa warra sammuu qabdan mee xiinxalaa!!');
INSERT INTO "apaguna" VALUES(59,3,'59:3','Odoo Rabbiin (Madiinaa keessaa) godaansa isaan irratti barreessuu baatee, silaa addunyaa keessatti isaan adabbii tureIsaaniifis Aakhiraa keessatti adabbii ibiddaatu jira.');
INSERT INTO "apaguna" VALUES(59,4,'59:4','Kun sababa isaan (ajaja) Rabbiifi ergamaa Isaa didaniifiNama (ajaja) Rabbii dide (Rabbiin isa adaba)Dhugumatti, Rabbiin adabbiin isaa cimaadha.');
INSERT INTO "apaguna" VALUES(59,5,'59:5','Muka timiraa irraa wanti isin murtan yookiin akkuma hundee ishee irra dhaabattetti dhiiftan eeyyama Rabbiitiini(wanti Rabbiin kana godheef) finciltoota salphisuufi.');
INSERT INTO "apaguna" VALUES(59,6,'59:6','Waan Rabbiin isaan (qabeenya isaanii) irraa ergamaa Isaa irratti deebise isin isa irratti fardaafi gaalas hin gulufsiifneGaruu Rabbiin ergamtoota Isaa abbaa fedhe irratti moosisaRabbiinis waan hunda irratti danda''aadha.');
INSERT INTO "apaguna" VALUES(59,7,'59:7','Wanti Rabbiin (qabeenya) warra magaalaa irraa ergamaa Isaa irratti deebise akka jidduu dureeyyii isin irraa ta''aniitti kan naanna''u hin taaneef jecha Rabbi, ergamaa, fira dhihoo (ergamaa), yatiimotaa, hiyyeeyyiifi nama imala irra jiruuf (haqa godhame)Waan Ergamichi (Kiyya) isiniif kennes qabadhaaWaan inni irraa isin dhowwes dhowwamaaRabbiinis sodaadhaaDhugumatti, Rabbiin adabbiin Isaa cimaadha.');
INSERT INTO "apaguna" VALUES(59,8,'59:8','Hiyyeeyyii godaantotaa warra tola Rabbiifi jaalala isaa barbaacha, akkasumas Rabbiifi ergamaa Isaa tumsuuf jecha manaafi qabeenya isaanii irraa baafamaniifis (ni kennama)Isaan sun isaanumatu warra dhugaati.');
INSERT INTO "apaguna" VALUES(59,9,'59:9','Warri isaaniin dura biyyattii qubsuma godhataniifi Iimaana qabatan, namoota gara isaanii godaanan ni jaallatuWaan (Muhaajirootaaf) kenname irrattis qoma isaanii keessatti jibbaa tokkollee hin arganuOdoma rakkoo cimaa qabaataniillee lubbuu isaanii irra (muhaajiroota) filatuNamoonni doynummaa lubbuu isaanii tiksanis, isaan warra milkaa’oodha.');
INSERT INTO "apaguna" VALUES(59,10,'59:10','Warri booda isaaniitii dhufanis "Yaa Gooftaa keenya! Nuufi obboleeyyan keenya warra iimaanaan nu dursaniif araarami; akkasumas, onnee Keenya keessa jibbaa warra amananiif hin taasisinGooftaa keenya! Ati mararfataa rahmata oolaadha" jedhu.');
INSERT INTO "apaguna" VALUES(59,11,'59:11','Sila gara warra munaafiqa ta''anii hin ilaallee? Obboleeyyan isaanii kan warra kitaabaa irraa ta''anii kafaraniin "Yoo isin baafamtaniif isinii wajjiin baana; waa''ee keessan keessattis yoomuu, eenyuuniyyuu hin ajajamnu; yoo isin lolamtanis dhugaatti isin gargaarra" jedhuRabbiinis akka isaan kijibdoota ta''an ragaa ba''a.');
INSERT INTO "apaguna" VALUES(59,12,'59:12','Odoo isaan baafamaniis isaanii wajjiin hin ba''anu; odoo isaan lolamaniiyyuu isaan hin tumsanu; odoma isaan tumsaniiyyuu dugda duuba gara galu (ni dheessu); ergasiihis hin tumsamanu.');
INSERT INTO "apaguna" VALUES(59,13,'59:13','Dhugumatti, isintu gama sodaatamuutiin onnee isaanii keessatti Rabbi irra cimaadhaKun waan isaan ummata hin hubanne ta''aniifi.');
INSERT INTO "apaguna" VALUES(59,14,'59:14','Yoo magaalota da''annaan marfamte keessa ta''an yookiin dallaa duubaan ta''e malee waliin ta''anii isin hin waraananuDiinummaan isaanii gidduu isaaniitti cimaadhaHaala onneen isaanii adda qoqqoodamee jiruun tokkummaa qaban isaan seetaKun waan isaan namoota hin xiinxalle ta''aniifi.');
INSERT INTO "apaguna" VALUES(59,15,'59:15','(Fakkeenyi isaanii) akka warra isaaniin dura yeroo dhihootti adabbii dalagaa isaanii dhandhamaniitiIsaaniifis adabbii laalessaatu jira.');
INSERT INTO "apaguna" VALUES(59,16,'59:16','Akka fakkii sheyxaanaa ilma namaatiin "(Rabbitti) kafari" jedhee erga inni kafaree booda immoo "ani sirraa qulqulluudha; ani Rabbii Gooftaa aalamaan sodaadha" jedheeti.');
INSERT INTO "apaguna" VALUES(59,17,'59:17','Xumurri isaan lamaaniituu keessatti hafoo ta''anii ibidda keessa ta''uudhaKun immoo mindaa miidhaa raawwattootaati.');
INSERT INTO "apaguna" VALUES(59,18,'59:18','Yaa warra amantan! Rabbiin sodaadhaaLubbuun kamuu waan boruuf dabarfatte haa ilaaltuAmmas Rabbiin sodaadhaaDhugumatti, Rabbiin waan isin hojjattan hunda keessa beekaadha.');
INSERT INTO "apaguna" VALUES(59,19,'59:19','Akka warra Rabbiin dagannaan lubbuu isaanii isaan dagachiisee hin ta''inaaIsaan isaanumatu finciltoota.');
INSERT INTO "apaguna" VALUES(59,20,'59:20','Warri ibiddaafi warri Jannataa wal hin qixxaatanuWarri Jannataa isaanumatu milkaa''oodha.');
INSERT INTO "apaguna" VALUES(59,21,'59:21','Odoo Qur''aana kana gaara irratti buufnee, silaa sodaa Rabbii irraa ka''ee kan inni xinnaatee dhodhooyu argita tureFakkeenyota kana akka isaan xiinxalaniif jecha namootaaf taasifna.');
INSERT INTO "apaguna" VALUES(59,22,'59:22','Inni Allaahn Isa gabbaramaan dhugaa isa malee hin jirre(Inni) Beekaa fagoofi dhihootiInni akkaan rahmataa godhaa akkaan mararfataadha.');
INSERT INTO "apaguna" VALUES(59,23,'59:23','Inni Allaahii Isa gabbaramaan dhugaa isa malee hin jirre, mootii, hir''ina irraa qulqulluu, abbaa nagayaa, nageenya arjoomaa, Qeexaa, injifataa, guddaa (ol ta''aa), boonaadhaWaan isaan qindeessan hunda irraa Rabbiin qulqullaa''e.');
INSERT INTO "apaguna" VALUES(59,24,'59:24','Inni Rabbii Uumaa (waan hundaa), (waan hin jirre) Argamsiisaa, bocaa ta''eedhaIsaaf maqoolee gaggaarii ta''antu jiraWantoonni samiifi dachii keessaa Isa qulqulleessu (faarsu)Inni Injifataa ogeessa.');
INSERT INTO "apaguna" VALUES(60,1,'60:1','Yaa warra amantan! Diina kiyyaafi diina keessan jaalallee hin godhatinaaOdoo isaan dhugaa irraa waan isinitti dhufetti kafaranii, sababa isin Rabbii Gooftaa keessan ta''etti amantaniif qofa Ergamichaafi isinis baasanii jiranuu isin gara isaaniitti jaalala dhaqqabsiiftuYoo karaa Kiyya keessatti qabsaa''uufi jaalala Kiyya barbaacha kan baatan taataniif (jaalalloo hin godhatinaa)Odoo ani waan isin dhoksitaniifi waan isin ifa gootan beekuu gara isaaniitti iccitiin jaalala dhaqqabsiiftuIsin irraa namni kana hojjate dhugumatti karaa qajeelaa irraa jallateera.');
INSERT INTO "apaguna" VALUES(60,2,'60:2','Yoo isin arganiif diinota isiniif ta''uHarkaafi arraba isaaniis hamtuun gara keessanitti diriirsuKafaruu keessanis ni jaalatu');
INSERT INTO "apaguna" VALUES(60,3,'60:3','Firoonni keessaniifi ilmaan keessanis homaa isin hin fayyadanu; Guyyaa Qiyaamaa (Rabbiin) addaan isin baasaRabbiinis waan isin hojjattan argaadha.');
INSERT INTO "apaguna" VALUES(60,4,'60:4','Dhugumatti, Ibraahimiifi warra isa wajjiin turan keessatti hidhannoon gaariin isiniif ta''eeraYeroo (isaan) ummata isaaniitiin "nuti isiniifi waan isin Rabbiin alatti gabbartan irraa qulqulluudha; isinitti kafarre; hanga Rabbii tokkichatti amantanutti gidduu keenyaafi gidduu keessanitti diinummaafi jibbansi abadii ifa ba''eera" jedhan (hidhannoon gaariin isiniif jira)Garuu jechi Ibraahiim abbaa isaatiin "araaraman siif kadhaa (adabbii) Rabbii irraa immoo waan tokko siif hin fayyadu" jedhe qoftii odoo hafuu"Gooftaa keenya! Sirratti hirkanne; gara Keettis deebine; deebiinis garuma kee qofa" (jedhan san hordofaa)');
INSERT INTO "apaguna" VALUES(60,5,'60:5','"Gooftaa keenya! warra kafaraniif mokkoroo nu hin godhinGooftaa keenya! Nuuf araarami; Ati injifataa ogeessa');
INSERT INTO "apaguna" VALUES(60,6,'60:6','Dhugumatti, nama Rabbiifi Guyyaa Aakhiraa kajeeluuf isaan keessa hidhannoo gaariitu isiniif jiraNamni (kanarraa) garagale (ofuma miidha)Dhugumatti Rabbiinis dureessa faarfamaadha.');
INSERT INTO "apaguna" VALUES(60,7,'60:7','Rabbiin gidduu keessaniifi gidduu warra isin isaan irraa diinomfattanii (nyaapha godhattanii) jaalala taasisuun ni kajeelamaRabbiinis Danda''aadhaAmmas Rabbiin araaramaa rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(60,8,'60:8','Rabbiin isaan sababaa amantiitiin isin hin waraaniniifi manneen keessan irraa isin hin baasiniif tola ooluufi haqaan waliin jiraachuu isin hin dhorguDhugumatti, Rabbiin warra haqa dalagan ni jaalata.');
INSERT INTO "apaguna" VALUES(60,9,'60:9','Kan Rabbiin isin dhorgu warra sababaa amantiitiin isin waraananiifi manneen keessan irraa isin baasan, akkasumas isin baasuu irratti gargaaran jaalalloo godhachuudhaNamni isaan jaalalloo godhate, isaan warra of miidhoodha.');
INSERT INTO "apaguna" VALUES(60,10,'60:10','Yaa warra amantan! Yeroo dubartoonni mu’uminaa godaantuu ta''anii isinitti dhufan (amantiif godaanuu isaanii baruuf) isaan qoradhaaIimaana isaanii Rabbitu akkaan beekaYoo mu’uminoota ta''uu isaanii bartan gara warra kafaraniitti isaan hin deebisnaaIsaan (dubartoonni) isaaniif eeyyamamoo miti; isaanis (dhiironni) isaaniif eeyyamamoo mitiIsaaniifis (dhirsota isaanii kan kaafiraatiifis) waan isaan baasan kennaaYeroo mahrii isaanii isaaniif kennitan isaan fuuchuun diliin isin irra hin jiruWaadaa fuudhaa kan niitiwwan keessan kaafiraa hin qabatinaa (nikaan diigameera)Isinis waan isaan (niitolii keessan kan kufrii irra jiran) irratti baaftan gaafadhaa; isaanis (dhirsotiin kaafiraa kan niitolii isaanii amanan) waan baasan haa gaafatanKun murtii Rabbiiti(Isatu) gidduu keessanitti murteessaRabbiin beekaa ogeessa.');
INSERT INTO "apaguna" VALUES(60,11,'60:11','Yoo tokkoon niitolee keessan irraa taate gara kaafirootaa isin jalaa baatee ergasii loltanii boojitan isaan niitoleen isaanii deemaniif fakkaataa waan isaan baasanii kennaafiiRabbii isa itti amantanis sodaadhaa.');
INSERT INTO "apaguna" VALUES(60,12,'60:12','Yaa Nabiyyichaa! Yeroo dubartoonni mu’uminaa Rabbitti homaa qindeessuu dhiisuu, hatuu dhiisuu, Zinaa godhuu dhabuu, ijoollee isaaniis ajjeesuu dhiisuu, cubbuu harka isaaniitiifi miila isaanii gidduutti uumanii odeessuu dhabuufi akka waan gaarii keessatti ajaja kee hin faallessine waadaa sii galuuf gara kee dhufan, waadaa isaan irraa fuudhiRabbiinis araarama isaaniif kadhuDhugumatti, Rabbiin araaramaa rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(60,13,'60:13','Yaa warra amantan! Ummata Rabbiin irratti dallane jaalallee hin godhatinaaAkkuma kaafiroonni warra qabrii irraa abdii muratan isaanis Aakhiraa irraa dhugumatti abdii murataniiru.');
INSERT INTO "apaguna" VALUES(61,1,'61:1',' Wantoonni samii keessaafi wantoonni dachii keessaa (hundi) Rabbiin qulqulleesseInnis injifataa ogeessa');
INSERT INTO "apaguna" VALUES(61,2,'61:2',' Yaa warra amantan! Maaliif waan hin hojjanne haasoftu?');
INSERT INTO "apaguna" VALUES(61,3,'61:3',' Waan hin hojjanne haasa''uun keessan jibbaansi isaa Rabbiin biratti guddate.');
INSERT INTO "apaguna" VALUES(61,4,'61:4','Rabbiin warra karaa Isaa keessatti akka ijaarsaa wal qabataa ta''anii hiriiraan lolan ni jaalata.');
INSERT INTO "apaguna" VALUES(61,5,'61:5','Yeroo Muusaan ummata isaatiin "yaa ummata (kiyya)! Odoo akka ani Ergamaa Rabbii kan gara keessanitti (ergame ta''uu) sirriitti beektanuu maaliif na rakkiftan?" jedhe [yaadadhaa]Yeroma isaan jallatanu Rabbiin onnee isaanii jalliseRabbiin ummata finciltoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(61,6,'61:6','Yeroo Iisaa Ibni Maryam "yaa Banii Israa''iil! Ani Ergamaa Rabbii kan waan Tawraatiin keessatti na dura bu''e dhugoomsuufi ergamaa na booda dhufu kan maqaan isaa Ahmad ta''een (isin) gammachiisu ta''ee gara keessan ergame" jedhe [yaadadhaa]Yeroma inni ragaawwan ifaan isaanitti dhufes "kun ''sihrii'' (falfala) ifa galaa dha" jedhan.');
INSERT INTO "apaguna" VALUES(61,7,'61:7','Nama odoo gara Islaamaa waamamuu Rabbi irratti kijiba uumee odeesse caalaa namni miidhaa hojjate eenyu? Rabbiinis ummata miidhaa raawwattoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(61,8,'61:8','Ifa Rabbii afaan isaaniitiin balleessuu fedhuOdoma kaafiroonni jibbaniiyyuu Rabbiin ifa Isaa ni guuta');
INSERT INTO "apaguna" VALUES(61,9,'61:9','Inni Isa odoo mushrikoonni jibbanuu, amantiiwwan biraa hunda irratti ol taasisuuf jecha ergamaa Isaa qajeelfamaafi amantii haqaatiin ergeedha');
INSERT INTO "apaguna" VALUES(61,10,'61:10','Yaa warra amantan! Mee daldala adabbii laalessaa irraa isin baraarsu irratti isin qajeelchuu?');
INSERT INTO "apaguna" VALUES(61,11,'61:11','Rabbiifi ergamaa Isaatti amantu, akkasumas karaa Rabbii keessatti qabeenyaafi lubbuu keessaniin qabsooftuYoo kan beektan taataniif kanatu (waan hunda) isiniif caala.');
INSERT INTO "apaguna" VALUES(61,12,'61:12','(Yoo kana dalagdaniif Rabbiin) badii keessan isiniif araaramaJannatoota laggeen jala ishee yaatus isin seensisaManneen babbareedaa Jannata qubannaa keessattis (isiniif qopheessee jira)Kun milkii guddaadha.');
INSERT INTO "apaguna" VALUES(61,13,'61:13','Waan biraas kan isin jaalattan (isiniif kenna)(Isheenis) tumsa Rabbi irraa ta''eefi banamuu (Makkaa) dhihoo ta''eedhaMu’uminootas gammachiisi.');
INSERT INTO "apaguna" VALUES(61,14,'61:14','Yaa warra amantan! Akkuma Iisaa Ibnu Maryam hawaariyyootaan "(waamicha ani) gara Rabbiitti (godhu irratti) namni na tumsu eenyu?" jennaan hawaariyoonnis "nutu tumsitoota Rabbiiti" jedhan san isinis tumsitoota Rabbii ta''aaIlmaan Israa''iil irraayis gartokkeen amananii gartokkeen kafaranIsaan amanan san diinota isaanii irratti jabeessinaan injifatoo ta''an');
INSERT INTO "apaguna" VALUES(62,1,'62:1','Wanti samii keessaafi wanti dachii keessaa Rabbii mootii, qulqulluu, injifataa, ogeessa ta''e qulqulleessa.');
INSERT INTO "apaguna" VALUES(62,2,'62:2','Inni Isa ummata wallaalaa keessatti ergamaa isaan irraa ta''e, kan keeyyattoota Isaa isaan irratti qara''u, (shirkii irraayis) isaan qulqulleessuufi kitaabaafi ogummaa isaan barsiisu ergeedhaIsaanis (ergamuu isaatiin) dura jallinna ifa ta''e keessa turan.');
INSERT INTO "apaguna" VALUES(62,3,'62:3','(Ummata) biroos kan isaan irraa ta''anii isaanitti hin dhaqqabiniifis (isa erge)Inni injifataa ogeessa');
INSERT INTO "apaguna" VALUES(62,4,'62:4','Kun tola RabbiitiIsa abbaa fedheef kennaRabbiinis abbaa tola guddaati.');
INSERT INTO "apaguna" VALUES(62,5,'62:5','Fakkeenyi warra Tawraatiin baachifamanii ergasii isa hin baadhatinii (ittiin hin hojjatinii) akka fakkeenya harree kitaabota gurguddaa baadhattuutiFakkeenyi ummata keeyyattoota Rabbii sobsiisanii akkaan fokkataa dha! Rabbiin ummata miidhaa raawwattoota hin qajeelchu.');
INSERT INTO "apaguna" VALUES(62,6,'62:6','"Yaa warra yahuudomtan! Namni biraa odoo hafuu isin qofatu jaalalloo Rabbiiti kan jettan yoo taataniif, yoo nama dhugaa dubbatu taataniif du''a hawwaa" jedhiin');
INSERT INTO "apaguna" VALUES(62,7,'62:7','Sababa waan harki isaanii dabarsiteef jecha yoomuu isa hin hawwanuRabbiin miidhaa raawwattoota beekaadha.');
INSERT INTO "apaguna" VALUES(62,8,'62:8','(Yaa Muhammad! ammas yahuudotaan): "dhugumatti, duuti inni isin isa irraa baqattan sun isinuma argataErgasii gara beekaa fagoofi dhihoo ta''eetti deebifamtanii waan isin dalagaa turtan isinitti hima" jedhiin');
INSERT INTO "apaguna" VALUES(62,9,'62:9','Yaa warra amantan! Yeroo guyyaa Jimaataa salaataaf lallabame gara zikrii Rabbii deemaa; daldalas dhiisaaYoo kan beektan taataniif kanatu isiniif caala.');
INSERT INTO "apaguna" VALUES(62,10,'62:10','Yeroo salaatattiin xumuramte immoo dachii keessa faca''aa; tola Rabbii irraayis barbaaddadhaaAkka milkooftaniif jechas baay''inaan Rabbiin faarsaa.');
INSERT INTO "apaguna" VALUES(62,11,'62:11','Yeroo daldala yookiin tapha wahii argan dhaabiitti si dhiisanii gara isaaniitti faffaca''u; "wanta Rabbiin bira jirutu taphaafi daldala irra caalaRabbiinis Irra caalaa arjootaati" jedhiin');
INSERT INTO "apaguna" VALUES(63,1,'63:1','Munaafiqoonni yeroo sitti dhufan "ati Ergamaa Rabbii ta''uu kee ragaa baana" jedhuDhugumatti Rabbiin ati Ergamaa isaa ta''uu ni beekaMunaafiqoonni akkaan sobdoota ta''uu Rabbiin ragaa ba''a');
INSERT INTO "apaguna" VALUES(63,2,'63:2','Kakuu isaanii gaachana godhatanii karaa Rabbii irraa (namoota) deebisuIsaan dhugumatti wanti isaan dalagaa turan fokkate');
INSERT INTO "apaguna" VALUES(63,3,'63:3','Kunis sababa isaan amananii ergasii kafaraniifiWaan onnee isaanii irratti cufameef isaan hin hubatanu.');
INSERT INTO "apaguna" VALUES(63,4,'63:4','Yeroo isaan argites qaamoleen isaanii si dinqisiiftiYoo isaan haasa''anis dubbii isaanii ni dhageeffataIsaan akka mukkeen hirkifameetiIyya hundaa isaan irratti (iyyame) jechuu yaaduIsaanumatu diinota; kanaafuu isaan eeggadhuRabbiin isaan haa abaaruu akkamitti (amantii irraa) deebi''u!');
INSERT INTO "apaguna" VALUES(63,5,'63:5','Yeroo "kottaa Ergamaan Rabbii araarama isiniif haa barbaadu" isaaniin jedhame mataa isaanii naannessuKan isaan boonaa gara galanus ni argita');
INSERT INTO "apaguna" VALUES(63,6,'63:6','Araarama isaanii barbaaddus isaanii barbaaduu dhiiftus isaan irratti wal qixa; Rabbiin isaaniif hin araaramuRabbiin ummata finciltoota hin qajeelchu');
INSERT INTO "apaguna" VALUES(63,7,'63:7','Isaan warra "namoota Ergamaa Rabbii bira jiranu hanga isaan faffaca''anutti isaan irratti hin arjoominaa" jedhanuudhaKuusaaleen samiifi dachii kan Rabbiiti; garuu munaafiqoonni hin hubatanu.');
INSERT INTO "apaguna" VALUES(63,8,'63:8','"Yoo gara Madiinaa deebineef, dhugumatti, (nuti) injifattoonni ishee irraa salphattoota baasuuf jirra" jedhuInjifannoon kan Rabbii, kan Ergamaa Isaatiifi kan mu’uminootaati; garuu munaafiqoonni hin beekanu');
INSERT INTO "apaguna" VALUES(63,9,'63:9','Yaa warra amantan! Qabeenyi keessaniifi ilmaan keessan zikrii Rabbii irraa isin hin daginNamoonni kana dalagan isaanumatu hoonga''oota.');
INSERT INTO "apaguna" VALUES(63,10,'63:10','Odoo tokkoon keessanitti duuti dhufnaan "Gooftaa kiyya! Odoo hanga beellama gabaabaatti na tursiiftee, silaa dhugumatti, sadaqadheen warra gaggaarii irraayis ta''a" hin jedhin dura waan nuti isiniif kennine irraa kennadhaa');
INSERT INTO "apaguna" VALUES(63,11,'63:11','Rabbiin immoo lubbuu tokko yeroo beellamni ishee dhufe hin tursiisuRabbiin keessa Beekaa waan isin dalagdaniiti');
INSERT INTO "apaguna" VALUES(64,1,'64:1','Wanti samii keessaafi wanti dachii keessaa hundi Rabbiin qulqulleessaMootummaan kan Isaa ti; faarunis kan IsaatiInni waan hunda irratti danda''aadha.');
INSERT INTO "apaguna" VALUES(64,2,'64:2','Inni Isa isin uumeIsin irraa Kaafirris ni jira; isin irraa Mu''uminnis ni jiraRabbiin waan isin dalagdan argaadha.');
INSERT INTO "apaguna" VALUES(64,3,'64:3','Samiifi dachii dhugaan uumeIsinis bocee boca keessan gaaromseDeebiinis garuma isaati');
INSERT INTO "apaguna" VALUES(64,4,'64:4','Wantoota samiifi dachii keessa jiru hunda ni beeka; akkasumas waan isin dhoksitaniifi waan isin ifa gootan ni beekaRabbiin waan qoma keessaa beekaadha');
INSERT INTO "apaguna" VALUES(64,5,'64:5','Sila oduun warra duraan kafaranii adabbii kufrii isaanii dhandhamanii isinitti hin dhufnee! Isaaniifis ibidda laalessaatu jira.');
INSERT INTO "apaguna" VALUES(64,6,'64:6','Kunis sababa ergamooleen isaanii ragaa iftuudhaan isaanitti dhufnaan "sila namatu nu qajeelchaa?" jedhanii kafaranii irraayis garagalaniifiRabbiinis of ga''eRabbiin dureessa faarfamaadha');
INSERT INTO "apaguna" VALUES(64,7,'64:7','Isaan kafaran (erga duunee) hin kaafamnu jechuu dubbatan"Eeyyeen! Gooftaa kiyyatti kakadhee! Dhugumatti, isin ni kaafamtu; ergasii wanti dalagdan hunduu isinitti himamaSunis Rabbirratti laafaa dha" jedhiin.');
INSERT INTO "apaguna" VALUES(64,8,'64:8','Rabbiifi Ergamaa Isaatti, akkasumas ifa isa Nuti buufnetti amanaaRabbiin waan isin hojjattan hunda keessa beekaadha.');
INSERT INTO "apaguna" VALUES(64,9,'64:9','Guyyaa wal gahichaatiif guyyaa (Rabbiin) walitti isin qabu san [yaadadhaa]Sun guyyaa wal gowwoomsuutiNamni Rabbitti amanee toltuu dalage (Rabbiin) dilii isaa isarraa harcaasee Jannata jala ishee laggeen yaatu keessa abaditti hafoo ta''anii isa seensisaSun milkii guddaadha.');
INSERT INTO "apaguna" VALUES(64,10,'64:10','Isaan kafaranii keeyyattoota keenya sobsiisan, ishee keessatti hafoo ta''anii isaan warra ibiddaati(Isheen) deebii ta''uun waa fokkatte!');
INSERT INTO "apaguna" VALUES(64,11,'64:11','Hamtuu irraa wanti tokkollee eeyyama Rabbiitiin ala nama hin tuquNama Rabbitti amanes qalbii isaa ni qajeelchaRabbiin waan hunda beekaadha.');
INSERT INTO "apaguna" VALUES(64,12,'64:12','Rabbiif ajajamaa; Ergamichaafis ajajamaaYoo gara galtanis wanti Ergamaa Keenya irra jiru (ergaa itti kenname) dhaqqabsiisuu qofa.');
INSERT INTO "apaguna" VALUES(64,13,'64:13','Rabbiin, dhugaan gabbaramaan isa malee hin jiruMu''uminoonnis Rabbi irratti qofa haa hirkatanu');
INSERT INTO "apaguna" VALUES(64,14,'64:14',' Yaa warra amantan! Dhugumatti, niitolii keessaniifi ilmaan keessan irraa warra isiniif diinaatu jira; kanaafuu, isaan eeggadhaaYoo dhiifama gootanii irra dabartaniifii araaramtan (sanatu isiniif caala)Rabbiinis araaramaa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(64,15,'64:15','Dhugumatti, qabeenyi keessaniifi ilmaan keessan mokkoroodhaRabbiin Isa bira mindaa guddaatu jira.');
INSERT INTO "apaguna" VALUES(64,16,'64:16','Hanga dandeessaniin Rabbiin sodaadhaa; dhagayaa; (Isaafis) ajajamaa; arjoomaas; sanatu lubbuu keessaniif caalaa ta''aNamni don''ummaa lubbuu isaa (irraa) tikfame, isaan isaanumatu milkaa''oodha.');
INSERT INTO "apaguna" VALUES(64,17,'64:17','Yoo liqii gaarii Rabbiif liqeessitan, dachaa isiniif godha; isiniif araaramasRabbiin galateeffataa obsaadha.');
INSERT INTO "apaguna" VALUES(64,18,'64:18','(Rabbiin) beekaa fagoofi dhihoo injifataa, ogeessa');
INSERT INTO "apaguna" VALUES(65,1,'65:1','Yaa Nabiyyichaa! Yeroo dubartoota hiiktan, ''Iddaa'' isaaniitiin isaan hiikaa''Iddaass'' lakkaa''aaRabbii Gooftaa keessan ta''es sodaadhaaYoo gocha fokkataa ifa ta''een dhufan malee manneen isaanii irraa isaan hin baasinaa; isaanis hin ba''inSun daangaawwan RabbiitiNamni daangaawwan Rabbii tare, dhugumatti inni lubbuu ofii miidheeraYoo tasa Rabbiin san booda dhimma haara''a (walitti deebi''uu gidduu keessanitti) uume hin beektu.');
INSERT INTO "apaguna" VALUES(65,2,'65:2','Yeroo beellama isaanii ga''an haala gaariin isaan qabadhaa yookiin haala gaariin isaaniin adda ba''aaIsin irraas warra haqaa (nama) lama ragaa godhaa(Warri ragaa baatanus) Rabbiif jedhaatii ragaa sirreessaaNamni Rabbiifi Guyyaa Aakhiraatti kan amanu ta''e kanaan gorfamaNamni Rabbiin sodaatu (Rabbis) karaa bahiinsaa isaaf godha');
INSERT INTO "apaguna" VALUES(65,3,'65:3','Bakka inni hin yaadin irraayis isa sooraNama Rabbi irratti hirkatuuf Inni isaaf ga''aadhaDhugumatti, Rabbiin ajaja isaa bakkaan ga''aadhaDhugumatti Rabbiin waan hundaafuu hanga sirraawaa taasiseera.');
INSERT INTO "apaguna" VALUES(65,4,'65:4','Dubartoota keessan irraa isaan xurii laguu (haydii) irraa abdii kutan yoo shakkitaniif iddaan isaanii ji''a sadi qofaIsaan haydii arguu hin jalqabinis (akkasuma)Warri ulfa ta''an immoo baallamni isaanii ulfa isaanii da''uudhaNama Rabbiin sodaatu (Rabbiin) dhimma isaa irraa laafina isaaf godha');
INSERT INTO "apaguna" VALUES(65,5,'65:5','Kun ajaja Rabbii kan gara keessanitti isa buuseedhaNama Rabbiin sodaatu (Rabbiin) badii isaa isa irraa harcaasee mindaas isaaf guddisa');
INSERT INTO "apaguna" VALUES(65,6,'65:6','Dandeettii keessan irraa bakka qubattanu isaan qubsiisaaIsaan irratti dhiphisuuf jechas isaan hin rakkisinaaYoo warra ulfaa ta''aniif hanga ulfa isaanii da''anitti isaan qallabaaYoo isiniif hoosisan immoo mindaa isaanii isaaniif kennaaJidduu keessanitti haala gaariin mari''adhaaYoo wal rakkiftaniif kan biraa isaaf haa hoosisu.');
INSERT INTO "apaguna" VALUES(65,7,'65:7','Abbaan dandeettii qabu dandeettii isaa irraa haa qallabuNamni sooranni isaa isa irratti dhiphifame, waan Rabbiin isaaf kenne irraa haa qallabuRabbiin lubbuu kamiyyuu waan isheedhaaf kenne malee hin dirqisiisuRabbiin dhiphina booda bal''ina godhuuf jira');
INSERT INTO "apaguna" VALUES(65,8,'65:8','Magaalota irraa heddutu ajaja Rabbii isheefi ergamtoota isaa faallessitee qormaata cimaa ishee qormaannee adabbii fokkataas ishee adabne');
INSERT INTO "apaguna" VALUES(65,9,'65:9','Sababa kanaan adabbii dalagaa ishee dhamdhamteBooddeen dubbii ishees hongoo ta''e.');
INSERT INTO "apaguna" VALUES(65,10,'65:10','Rabbiin adabbii cimaa isaaniif qopheesseYaa warri sammuu qabdan, warri amantan Rabbiin sodaadhaaDhugumatti Rabbiin gara keessanitti gorsa buuseera');
INSERT INTO "apaguna" VALUES(65,11,'65:11','Warra amananii gaggaarii hojjatan dukkanoota keessaa gara ifaatti baasuuf jecha ergamaa keeyyattoota Rabbii ibsituu taate isin irratti dubbisu (ergine)Nama Rabbitti amanee gaariis dalage jannatoota jala ishee laggeen yaatu keessatti yeroo hundaaf hafoo taasisee isa seensisaDhugaatti Rabbiin isaaf soorata bareeche');
INSERT INTO "apaguna" VALUES(65,12,'65:12','Rabbiin Isa samii torbaniifi dachii irraayis fakkiidhuma isaanii uumeedhaAjajni jidduu isaaniitti bu''a(Wanti inni kana uumeefis) akka isin Rabbiin waan hunda irratti danda''aafi Rabbiin beekumsaan wantoota cufa marsuu isaa beektanuufi');
INSERT INTO "apaguna" VALUES(66,1,'66:1','Yaa Nabiyyichaa! Maaliif haadholee manaa kee jaalachiisuu barbaaddee waan Rabbiin halaala siif godhe haraama goota? Rabbiin araaramaa, rahmata godhaadha.');
INSERT INTO "apaguna" VALUES(66,2,'66:2','Dhugumatti, Rabbiin karaa kakuu keessan ittiin hiiktan isiniif murteesseeraRabbiin gargaaraa keessaniInni beekaa ogeessa');
INSERT INTO "apaguna" VALUES(66,3,'66:3','Yeroo Nabiyyichi gara garii niitolee isaatti iccitii wahii himu [yaadadhaa]Yeroo isheen isa odeessitee Rabbiinis isa isatti beeksisu, garii isaa (isheetti) beeksisee, garii irraa gara galeYeroo inni isheetti himus “eenyutu kana sitti hime” jette"Beekaa, keessa hubataatu natti hime" jedheen.');
INSERT INTO "apaguna" VALUES(66,4,'66:4','Yoo tawbaa gara Rabbii deebitaniif (isiniif araarama)Onneen lamaan keessanii dhugumaan dabdeettiYoo isa irratti wal gargaartanis Rabbiin isatu gargaaraa isaatiJibriiliifi gaggaariin Mu''uminaas, Maleykonnis booda sanaatii gargaartota isaati');
INSERT INTO "apaguna" VALUES(66,5,'66:5','Yoo inni isin hiike, Gooftaan isaa haadholee manaa kan isin irra caalan, Muslimoota, mu’uminoota, ajajamtoota, toobbattoota, (Rabbiin) gabbartoota, kan soomanu, kan gursummeeyyiifi durba ta''an bakka isaaf buusuun ni kajeelama.');
INSERT INTO "apaguna" VALUES(66,6,'66:6','Yaa warra amantan! Lubbuu keessaniifi maatiiwwan keessan ibidda qoraan isii namootaafi dhagaa heddu ta''e, irraa eegaaIsheerra maleeykota gara jabeeyyii, ciccimoo ta''antu jiruWaan Rabbiin isaan ajaje hin faallessanu; waan ajajamanis ni hojjatu');
INSERT INTO "apaguna" VALUES(66,7,'66:7','"Yaa warra kafartan! Guyyaa har’aa (Qiyaamaa) kana rakkina hin himatinaa; wanti isin mindeeffamtan waanuma dalagaa turtan qofa" (jedhamu).');
INSERT INTO "apaguna" VALUES(66,8,'66:8','Yaa warra amantan! Tawbaa qulqulluu gara Rabbiitti deebi''aaGuyyaa Rabbiin Nabiyyiifi warra isaa wajjin amanan hin hoongessine san Gooftaan keessan diliiwwan keessan isin irraa haxaawee Jannatoota jala ishee laggeen yaatu isin seensisuun ni kajeelamaHaala isaan "Yaa Gooftaa keenya! Ifaa keenya nuuf guuti; nuufis araarami; Ati waan hunda irratti danda''aa dha" jedhanuun ifti isaanii fuuldura isaaniitiifi mirga isaanii deema.');
INSERT INTO "apaguna" VALUES(66,9,'66:9','Yaa Nabiyyichaa! Kaafirootaafi munaafiqoota irratti qabsaa''ii isaan irrattis cimiTeessoon isaaniis ibidda JahannamiDeebichi akkaan fokkate!');
INSERT INTO "apaguna" VALUES(66,10,'66:10','Rabbiin isaan kafaraniif niitii Nuuhiifi niitii Luux fakkeenya godheGabroota keenya gaggaarii gidduu gabroota lama jala turanii isaan gananIsaan lamaanis (Nuuhifi Luux) adabbii Rabbii irraa isaan hin duroomsine"Isin lamaan ibidda warra seenan wajjin seenaa" jedhameen.');
INSERT INTO "apaguna" VALUES(66,11,'66:11','Ammas Rabbiin isaan amananiif niitii Fir''awni fakkeenya godheYeroo isheen "yaa Gooftaa kiyya! Si biratti Jannata keessatti mana naaf ijaari; Fir''awniifi dalagaa isaa irraa na baraarsi; ummata miidhaa hojjatoota irraas na baraarsi" jette.');
INSERT INTO "apaguna" VALUES(66,12,'66:12','Maryam Intala Imraan, ishee qaama saalaa ishee tiksites (fakkeenya godheera)Ruuhii (uumaa) keenya irraa ta''es isa keessatti afuufneJechoota Gooftaa isheetiifi kitaabota Isaas ni dhugoomsiteWarra Rabbiif ajajamu irraas taate.');
INSERT INTO "apaguna" VALUES(67,1,'67:1','(Rabbiin) Inni mootummaan waan hundaa harka Isaa jiru tolli isaa baay''ateInni waan hunda irratti danda''aadha.');
INSERT INTO "apaguna" VALUES(67,2,'67:2','(Rabbiin) Isa eenyuu keessantu irra toltuu akka hojjatu isin qoruuf jecha du''aafi jireenya uumeedhaInnis injifataa, araaramaadha.');
INSERT INTO "apaguna" VALUES(67,3,'67:3','(Rabbiin) Isa samii torba wal jalaa gubbaan uumeedha; uumaa Rahmaan keessatti wal dhabbii irraa homaa hin argituMee agartuu kee (gara samii) deebisii dhoohaa irraa waan ta''e ni argitaa?');
INSERT INTO "apaguna" VALUES(67,4,'67:4','Ergasii yeroo baay''ee ija deddeebisi; ijji (kee) hoonga''aa dadhabaa ta''ee gara kee deebi''a.');
INSERT INTO "apaguna" VALUES(67,5,'67:5','Dhugumatti, samii addunyaa ibsaawwaniin (urjiiwwaniin) miidhagsineerraSheyxaanotaafis furguuggee ishee gooneIsaaniifis adabbii ibidda boba''aa qopheessineerra.');
INSERT INTO "apaguna" VALUES(67,6,'67:6','Isaan Gooftaa isaaniitti kafaraniifis adabbii jahannamtu jiraDeebi’ichis waa fokkate!');
INSERT INTO "apaguna" VALUES(67,7,'67:7','Yeroo ishee keessatti darbamanis haala isheen danfuutti jirtuun isheedhaaf (sagalee akka) halaaka harree dhaga''u.');
INSERT INTO "apaguna" VALUES(67,8,'67:8','Dallansuu irraa kan ka''e ciccituutti dhihaatti; dhawaata gareen wahii ishee keessatti darbamuun waardiyyaan ishee "sila akeekkachiisaan (nabiyyiin) isinitti hin dhufnee?" (jechuun) isaan gaafata.');
INSERT INTO "apaguna" VALUES(67,9,'67:9','"Eeyyeen; dhugumatti akeekkachiisaan nutti dhufeera; sobsiifneeti ''Rabbiin homaa hin buufne; isin jallina guddaa keessa jirtu'' jenneen" jedhu');
INSERT INTO "apaguna" VALUES(67,10,'67:10','"Odoo kan dhageenyu yookiin kan xiinxallu taanee, silaa warra ibidda boba''aa keessaa hin taanu turre" jedhan');
INSERT INTO "apaguna" VALUES(67,11,'67:11','Dilii isaaniis ni amananWarra ibidda boba''aatiifis (rahmata Rabbii irraa) fagaachuun haa jiraatu!');
INSERT INTO "apaguna" VALUES(67,12,'67:12','Dhugumatti, warra Gooftaa isaanii fagootti sodaataniif araaramaafi mindaa guddaatu isaaniif jira.');
INSERT INTO "apaguna" VALUES(67,13,'67:13','Haasa''a keessan gadi qabaa yookiin immoo ol qabaa; Inni beekaa waan qoma keessaati.');
INSERT INTO "apaguna" VALUES(67,14,'67:14','Sila Inni (iccitii) uumee keessa argaafi beekaa ta''e, (iccitii isaanii) hin beekuu!');
INSERT INTO "apaguna" VALUES(67,15,'67:15','Inni Isa dachii laaffifamtuu isiniif godheedha; naannoo ishee keessa deemaatii soorata Isaa irraas nyaadhaaDeebiinis garuma Isaatti');
INSERT INTO "apaguna" VALUES(67,16,'67:16','Sila Inni samii olii, dachii isiniin kunnaan, achumaan isheen socho’uu of ni amantuu?');
INSERT INTO "apaguna" VALUES(67,17,'67:17','Yookiin immoo Inni samii olii bubbee cirracha qabu isin irratti erguu ni amantuu? Akeekkachiisni Kiyya akkam akka ta''e beekuuf jirtu.');
INSERT INTO "apaguna" VALUES(67,18,'67:18','Dhugumatti, warri isaaniin duraas sobsiisaniiru; (adabbiin) Ana diduu akkam tureree!');
INSERT INTO "apaguna" VALUES(67,19,'67:19','Sila gara simbirroota gubbaa isaaniitti koola (qoochoo) isaanii babal''isanii walitti dachaasanii hin ilaalanuu? Rahmaaniin malee homtuu isaan hin qabuDhugumatti, Inni waan hunda argaadha.');
INSERT INTO "apaguna" VALUES(67,20,'67:20','Sila inni Rahmaaniin alatti humna waraanaa keessan ta''ee isin tumsuu danda''u eenyuma? Kaafiroonni gowwummaa keessa malee hin jiranu.');
INSERT INTO "apaguna" VALUES(67,21,'67:21','Odoo Inni soorata Isaa qabee, inni isin sooru eenyuma? Dhugumatti, isaan diddaafi (haqa) irraa baqachuu itti fufaniiru.');
INSERT INTO "apaguna" VALUES(67,22,'67:22','Sila nama fuula isaa irratti gonbifamee deemu moo isa dhaabbatee karaa qajeelaa irra deemutu caalaa qajeelaa dha?');
INSERT INTO "apaguna" VALUES(67,23,'67:23','Jedhi: "Inni Isa isin uumee dhageettii, agartuufi onnee isiniif godheedha; hangi isin galatoomfattan waa xiqqaate!"');
INSERT INTO "apaguna" VALUES(67,24,'67:24','Jedhi: "Inni Isa dachii keessa isin facaaseedha; garuma Isaatti oofamtus."');
INSERT INTO "apaguna" VALUES(67,25,'67:25','"Yoo kan dhugaa dubbattan taataniif, beellamni kun yoomi?" jedhu.');
INSERT INTO "apaguna" VALUES(67,26,'67:26','Jedhi: "Dhugumatti, beekumsi isaa Rabbiin bira qofa jiraAni immoo akeekkachiisaa ifa galaa qofa."');
INSERT INTO "apaguna" VALUES(67,27,'67:27','Yeroma dhihoo ta''ee isa (beellamicha) argan fuulli warra kafaranii fokkatte"Inni kun isa waammataa turtanii dha" jedhame');
INSERT INTO "apaguna" VALUES(67,28,'67:28','"Mee naaf himaa! yoo Rabbiin anaafi nama ana wajjin jiru balleesse yookiin rahmata nuuf godhe, namni kaafiroota adabbii laalessaa irraa baraaru eenyu?" jedhi.');
INSERT INTO "apaguna" VALUES(67,29,'67:29','"Inni RahmaaniIsatti amanne; Isa irrattis hirkanneNamni jallina ifa ta''e keessa jiru eenyu akka ta''e beekuuf jirtu" jedhiin.');
INSERT INTO "apaguna" VALUES(67,30,'67:30','Jedhi: "Mee naaf himaa! odoo bishaan keessan dachii keessa gadi lixaa ta''ee namni bishaan yaa''u isiniif fidu eenyu?"');
INSERT INTO "apaguna" VALUES(68,1,'68:1','NuunQalamaafi waan (malaykonni) barreessaniinin kakadha.');
INSERT INTO "apaguna" VALUES(68,2,'68:2','Ati ni''imaa Gooftaa keetiitiin maraataa hin taane.');
INSERT INTO "apaguna" VALUES(68,3,'68:3','Dhugumatti, siif mindaa hin cinnetu jira.');
INSERT INTO "apaguna" VALUES(68,4,'68:4','Dhugumatti, ati amala (naamusa) guddaa irra jirta.');
INSERT INTO "apaguna" VALUES(68,5,'68:5','Fuulduratti arguuf jirta; isaanis arguuf jiru.');
INSERT INTO "apaguna" VALUES(68,6,'68:6','Eenyuu keessantu Maraataa akka ta’e (arguuf jirtu).');
INSERT INTO "apaguna" VALUES(68,7,'68:7','Dhugumatti, Gooftaan kee Isatu nama karaa Isaa irraa jallate akkaan beeka; namoota qajeeloos Isatu akkaan beeka.');
INSERT INTO "apaguna" VALUES(68,8,'68:8','Kijibsiiftotaaf hin ajajamin.');
INSERT INTO "apaguna" VALUES(68,9,'68:9','Odoo ati (ejjannoo kee) laaffiftee [Isaan] laaffisanii jaalatu.');
INSERT INTO "apaguna" VALUES(68,10,'68:10','Akkaan kakataa salphataa hundaaf hin ajajamin.');
INSERT INTO "apaguna" VALUES(68,11,'68:11','Akkaan hamataa, walitti naqaadhaan akkaan deemaa kan ta''eef (hin ajajamin).');
INSERT INTO "apaguna" VALUES(68,12,'68:12','Toltuu kan akkaan dhorgu, daangaa dabraa, dilaawaafis.');
INSERT INTO "apaguna" VALUES(68,13,'68:13','Gara jabeessa, sana booda immoo galtuu kan ta''een (hin ajajamin).');
INSERT INTO "apaguna" VALUES(68,14,'68:14','Waan abbaa qabeenyaafi ilmaanii ta''eef (boonee kafare).');
INSERT INTO "apaguna" VALUES(68,15,'68:15','Yeroo keeyyattoonni Keenya isa irratti dubbifamtu, "(kun) ogbarruu warra duriiti" jedhe.');
INSERT INTO "apaguna" VALUES(68,16,'68:16','Funyaan irratti isa mallatteessina.');
INSERT INTO "apaguna" VALUES(68,17,'68:17','Dhugumatti, Nuti akkuma abbootii ashaakiltii yeroo isaan bariidhaan ishee funaannachuuf kakatan qormaannetti, isaanis (mushrikoota Makkaas) qormaanneerra');
INSERT INTO "apaguna" VALUES(68,18,'68:18','Siinoo hin baasanu (yoo Rabbiin fedhe hin jedhan).');
INSERT INTO "apaguna" VALUES(68,19,'68:19','Odoo isaan rafanuu, naanna''aan (adabbiin) Rabbii kee biraa ta''e ishee irra naanna''e');
INSERT INTO "apaguna" VALUES(68,20,'68:20','Akka halkan dukkanaa (cilee) taatee bulte');
INSERT INTO "apaguna" VALUES(68,21,'68:21','Bariidhaan walitti lallaban.');
INSERT INTO "apaguna" VALUES(68,22,'68:22','"Yoo kan funaannattan taataniif oyruu keessan irratti bariifadhaa" (waliin jedhan).');
INSERT INTO "apaguna" VALUES(68,23,'68:23','Walitti hasaasaa (gara oyruu) deeman.');
INSERT INTO "apaguna" VALUES(68,24,'68:24','"Har’a hiyyeessi tokkoyyuu isinirratti akka ishee hin seenne" (jechuun hasaasan).');
INSERT INTO "apaguna" VALUES(68,25,'68:25','(Hiyyeeyyii) dhowwuu irratti danda''oo ta''anii bariisifatan.');
INSERT INTO "apaguna" VALUES(68,26,'68:26','Yeroo ishee arganu ni jedhan: "nuti karaa badoodha.');
INSERT INTO "apaguna" VALUES(68,27,'68:27','Inumaayyuu nuti hoongeffamoodha."');
INSERT INTO "apaguna" VALUES(68,28,'68:28','Irra filatamaan isaanii "sila maaliif siinoo hin baafne (yoo Rabbiin fedhe hin jenne) isiniin jedhee hin turree?" jedhe.');
INSERT INTO "apaguna" VALUES(68,29,'68:29','"Gooftaan keenya qulqullaa''e; dhugumatti, nuti miidhaa hojjattoota taaneerra" jedhan.');
INSERT INTO "apaguna" VALUES(68,30,'68:30','Kan wal komatan ta’anii gariin isaanii garii irratti garagale.');
INSERT INTO "apaguna" VALUES(68,31,'68:31','Ni jedhan: "yaa badii keenya! Nuti dhugumatti, daangaa dabritoota.');
INSERT INTO "apaguna" VALUES(68,32,'68:32','Gooftaan keenya kan ishee caaltu bakka nuuf buusuu kajeellaNuti dhugumatti, gara Gooftaa keenyaa kajeeloodha” (jedhan).');
INSERT INTO "apaguna" VALUES(68,33,'68:33','Adabni (addunyaa) akkanuma; adabbii Aakhiraatu irra guddaadhaOdoo beekanii (irraa of tiksu turan).');
INSERT INTO "apaguna" VALUES(68,34,'68:34','Dhugumatti, warra Rabbiin sodaataniif Gooftaa isaanii bira Jannata qananiitu jira.');
INSERT INTO "apaguna" VALUES(68,35,'68:35','Sila Nuti muslimoota akka yakkamtootaa ni goonaa?');
INSERT INTO "apaguna" VALUES(68,36,'68:36','Maalumatu isiniif jira? Akkamitti murteessitu!');
INSERT INTO "apaguna" VALUES(68,37,'68:37','Dhugumatti, kitaaba (samii bu''e) kan isa keessaa dubbiftantu isiniif jiraa?');
INSERT INTO "apaguna" VALUES(68,38,'68:38','Kan dhugumatti, isa keessa wanti isin filattan jiru (qabduu)?');
INSERT INTO "apaguna" VALUES(68,39,'68:39','Sila ‘wanti isin murteessitan isiniif jira’ jechuun kakuu Guyyaa Qiyaamaa keessatti bakka ga''aa ta''etu isiniif jira moo?');
INSERT INTO "apaguna" VALUES(68,40,'68:40','Eenyuu isaaniitu sanaaf wabiidhaa isaan gaafadhu.');
INSERT INTO "apaguna" VALUES(68,41,'68:41','Sila shariikota (kan murtee isaanii dhugoomsu) qabu moo? Yoo kan dhugaa dubbatan ta''aniif shariikota isaaniitiin haa dhufanu.');
INSERT INTO "apaguna" VALUES(68,42,'68:42','Guyyaa mogolee irraa saaqamee (munaafiqoonni) gara sujuudaa waamamanii hin dandeenye san [yaadadhu].');
INSERT INTO "apaguna" VALUES(68,43,'68:43','Ijji isaanii sodaattuu taatee xiqqeenyis isaan haguugaDhugumatti, isaan odoo fayyaa qabanuu (addunyaa irratti) sujuudatti waamamaa turan.');
INSERT INTO "apaguna" VALUES(68,44,'68:44','Anaafi nama haasa''a (Qur''aana) kana sobsiisu walitti nu dhiisi; Nuti suutaan karaa isaan hin beekne irraa isaan qabna.');
INSERT INTO "apaguna" VALUES(68,45,'68:45','Yeroon isaaniif kennaDhugumatti, tooftaan Kiyya cimaadha.');
INSERT INTO "apaguna" VALUES(68,46,'68:46','Sila mindaa isaan gaafatteeti kaffaluuf itti ulfaatemoo (maali)?');
INSERT INTO "apaguna" VALUES(68,47,'68:47','Sila (iccitii) fagoo kan isaan barreessantu isaan bira jira moo?');
INSERT INTO "apaguna" VALUES(68,48,'68:48','Murtii Gooftaa keetiitiif obsiAkka saahiba qurxummii kan yeroo dallansuun guutame san (Rabbitti) lallabee hin ta''in.');
INSERT INTO "apaguna" VALUES(68,49,'68:49','Odoo ni''imaan Gooftaa isaa irraa taate isa dhaqqabuu baattee, silaa komatamaa ta''eeti dirree biqiltuu hin qabne irratti darbama ture.');
INSERT INTO "apaguna" VALUES(68,50,'68:50','Gooftaan isaa isa filateeti gaggaarii irraa isa godhe.');
INSERT INTO "apaguna" VALUES(68,51,'68:51','Isaan kafaran yeroo yaadachiisa dhaga''an ija isaaniitiin lafaan si dhahuufi "dhugumatti, inni maraataa dha" (siin) jechuutti dhihaatu.');
INSERT INTO "apaguna" VALUES(68,52,'68:52','Innis aalama hundaaf yaadachiisa malee hin taane.');
INSERT INTO "apaguna" VALUES(69,1,'69:1','Mirkaneessituun,');
INSERT INTO "apaguna" VALUES(69,2,'69:2','Mirkaneessituttiin maali?');
INSERT INTO "apaguna" VALUES(69,3,'69:3','Mirkaneessituttiin maal akka taate maaltu si beeksise?!');
INSERT INTO "apaguna" VALUES(69,4,'69:4','(Ummanni) Samuudiifi Aad rukuttuu qalbii (Qiyaamaa) sobsiisaniiru.');
INSERT INTO "apaguna" VALUES(69,5,'69:5','Samuudis iyya daangaa darbaadhaan balleeffaman');
INSERT INTO "apaguna" VALUES(69,6,'69:6','Aad immoo qilleensa sagalee cimaa akkaan bubbisuun balleeffaman.');
INSERT INTO "apaguna" VALUES(69,7,'69:7','Halkan torbaafi guyyaa saddeetiif walitti aansee isaan irratti ergeIshee keessatti ummata kan jirma timiraa hundeen buqqifame fakkaatanii kukkufan argita');
INSERT INTO "apaguna" VALUES(69,8,'69:8','Sila isaan irraa haftee wahii ni argitaa?');
INSERT INTO "apaguna" VALUES(69,9,'69:9','Fir''awn, warri isa duraatiifi (gandoonni) gombifamtoonnis diliidhaan dhufan.');
INSERT INTO "apaguna" VALUES(69,10,'69:10','Ergamaa Gooftaa isaanii faallessinaan qabaa cimtuu isaan qabe.');
INSERT INTO "apaguna" VALUES(69,11,'69:11','Nuti yeroo bishaan daangaa dabru doonii keessatti isin baanne.');
INSERT INTO "apaguna" VALUES(69,12,'69:12','Akka ishee isiniif gorsa goonuufi gurri waa hubattus akka ishee hubattuuf (kana goone)');
INSERT INTO "apaguna" VALUES(69,13,'69:13','Yeroo Garrii ykn Faaggaa (xurumbaa) keessa afuuffaan tokkittiin afuufamte,');
INSERT INTO "apaguna" VALUES(69,14,'69:14','Dacheeniifi gaarreenis ol fuudhamanii caccabiinsa tokkittii caccaban,');
INSERT INTO "apaguna" VALUES(69,15,'69:15','Guyyaa san argamtuun (Qiyaamaan) ni argamti.');
INSERT INTO "apaguna" VALUES(69,16,'69:16','Samiinis ni baqaqxi; kanaafuu isheen guyyaa san laaftuudha.');
INSERT INTO "apaguna" VALUES(69,17,'69:17','Maleeykonnis daangawwan ishee irratti ta''uArshii Gooftaa keetiis guyyaa san (maleeykota) saddeettu gubbaa isaaniitti baadhatu.');
INSERT INTO "apaguna" VALUES(69,18,'69:18','Guyyaa san (murtiidhaaf) ni dhiyeeffamtuu, (iccitiin) dhokattuun isin irraa taate hin dhokattu.');
INSERT INTO "apaguna" VALUES(69,19,'69:19','Namni kitaaba isaa mirga isaatiin kenname ni jedha: “hoodhaa kitaaba kiyya dubbisaa.');
INSERT INTO "apaguna" VALUES(69,20,'69:20','Ani qormaata koo qunnamuu dhugoomseen ture.”');
INSERT INTO "apaguna" VALUES(69,21,'69:21','Innis jireenya jaalatamtuu keessa ta''a.');
INSERT INTO "apaguna" VALUES(69,22,'69:22','Jannata ol fuudhamtuu keessa (ta''a).');
INSERT INTO "apaguna" VALUES(69,23,'69:23','Kan firiiwwan ishee dhihoo ta''an;');
INSERT INTO "apaguna" VALUES(69,24,'69:24','"Waan guyyoota darban keessa dabarfattaniif jecha gammadoo ta’aatii nyaadhaa; dhugaa" (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(69,25,'69:25','Namni kitaaba isaa bitaa isaatiin kennames ni jedha: "yaa badii kiyya! Odoon kitaaba kiyya kennamuu hafee.');
INSERT INTO "apaguna" VALUES(69,26,'69:26','Qormaanni kiyya maal akka ta''es (odoon) beekuu hafee.');
INSERT INTO "apaguna" VALUES(69,27,'69:27','Odoo isheen xumura (jiruu) kiyyaa taateen hawwa!');
INSERT INTO "apaguna" VALUES(69,28,'69:28','Qabeenyi kiyya homaa nan duroomsine (fayyadne).');
INSERT INTO "apaguna" VALUES(69,29,'69:29','Taaytaan kiyyas narraa bade” (jedha).');
INSERT INTO "apaguna" VALUES(69,30,'69:30','Isa qabaatii isa hidhaa (sakaalaa).');
INSERT INTO "apaguna" VALUES(69,31,'69:31','Ergasii ibidda isa seensisaa');
INSERT INTO "apaguna" VALUES(69,32,'69:32','Ergasii sansalata dheerinni isaa dhundhuma torbaatama ta''e keessa isa seensisaa.');
INSERT INTO "apaguna" VALUES(69,33,'69:33','Inni Rabbii Guddaatti hin amanu ture.');
INSERT INTO "apaguna" VALUES(69,34,'69:34','Hiyyeeyyii nyaachisuu irrattis (namoota) hin kakaasu ture.');
INSERT INTO "apaguna" VALUES(69,35,'69:35','Har’a asitti firri isaaf hin jiru.');
INSERT INTO "apaguna" VALUES(69,36,'69:36','Nyaannis malaa (warra ibiddaa) irraa malee hin jiru.');
INSERT INTO "apaguna" VALUES(69,37,'69:37','Warra dilaawoo malee isa hin nyaatu.');
INSERT INTO "apaguna" VALUES(69,38,'69:38','Waan isin argitaniinis niin kakadha.');
INSERT INTO "apaguna" VALUES(69,39,'69:39','Waan isin hin argineenis (niin kakadha).');
INSERT INTO "apaguna" VALUES(69,40,'69:40','Dhugumatti, inni (Qur''aanni) jecha ergamaa kabajamaati.');
INSERT INTO "apaguna" VALUES(69,41,'69:41','Inni jecha weellisaatii miti; xiqquma qofa amantu.');
INSERT INTO "apaguna" VALUES(69,42,'69:42','Jecha raagaatiis miti; xiqqoo malee hin gorfamtanu.');
INSERT INTO "apaguna" VALUES(69,43,'69:43','Gooftaa aalama hundaa irraa buufamaadha.');
INSERT INTO "apaguna" VALUES(69,44,'69:44','Odoo garii jechootaa (kan Nuti hin jedhin) Nurratti dubbatee,');
INSERT INTO "apaguna" VALUES(69,45,'69:45','Silaa Nuti mirgaan isa qabna turre.');
INSERT INTO "apaguna" VALUES(69,46,'69:46','Ergasii hidda onnee isarraa kunna turre.');
INSERT INTO "apaguna" VALUES(69,47,'69:47','Isin irraa namni isarraa dhorgu tokkollee hin jiru.');
INSERT INTO "apaguna" VALUES(69,48,'69:48','Innis (Qur''aanni) warra Rabbiin sodaataniif gorsa.');
INSERT INTO "apaguna" VALUES(69,49,'69:49','Dhugumatti isin irraa namoonni (Qur''aana) sobsiisan akka jiran Nuti ni beekna.');
INSERT INTO "apaguna" VALUES(69,50,'69:50','Inni (Qur''aanni) kaafiroota irratti gaabbiidha.');
INSERT INTO "apaguna" VALUES(69,51,'69:51','Dhugumatti, inni (Qur''aanni) dhugaa mirkanaa’aadha.');
INSERT INTO "apaguna" VALUES(69,52,'69:52','Maqaa Gooftaa kee guddaa ta’ee faarsi.');
INSERT INTO "apaguna" VALUES(70,1,'70:1','Adaba (kaafiroota irratti) argamaa ta''e kadhataan wahii kadhate.');
INSERT INTO "apaguna" VALUES(70,2,'70:2','Kan kaafirootatti (argamu kadhate); wanti isa (adabicha) deebisu hin jiru.');
INSERT INTO "apaguna" VALUES(70,3,'70:3','Rabbii Abbaa kortoowwanii ta''e irraa (argama)');
INSERT INTO "apaguna" VALUES(70,4,'70:4','Malaykonniifi Jibiriil guyyaa shallagni isaa waggoota kuma shantama ta''e keessatti gara Isaa ol koru.');
INSERT INTO "apaguna" VALUES(70,5,'70:5','Obsa gaarii obsi.');
INSERT INTO "apaguna" VALUES(70,6,'70:6','Isaan (adabicha) fagoo taasisanii isa ilaalu.');
INSERT INTO "apaguna" VALUES(70,7,'70:7','Nuti immoo dhihootti isa argina.');
INSERT INTO "apaguna" VALUES(70,8,'70:8','Guyyaa Samiin akka meeta baqee taatu.');
INSERT INTO "apaguna" VALUES(70,9,'70:9','(Guyyaa) Gaarreenis akka jibrii hiddamee taatu.');
INSERT INTO "apaguna" VALUES(70,10,'70:10','(Guyyaa) firri fira hin gaafanne.');
INSERT INTO "apaguna" VALUES(70,11,'70:11','Isaan agarsiifamuNamni dilaawaan odoo ilmaan isaa kennee adabbii guyyaa sanii jalaa bilisa ba''ee jaalata.');
INSERT INTO "apaguna" VALUES(70,12,'70:12','Niitii isaatiifi obboleessa isaatiinis (odoo bilisa ba’ee jaalata);');
INSERT INTO "apaguna" VALUES(70,13,'70:13','Firoota isaa kan ofitti isa maxxanfattuunis;');
INSERT INTO "apaguna" VALUES(70,14,'70:14','Nama dachii keessaa hunda (kennee) ergasii (fidaan sun) nagaa isa baasuu (jaalata).');
INSERT INTO "apaguna" VALUES(70,15,'70:15','Dhorgamaa! Dhugummatti, isheen balala ibiddaati.');
INSERT INTO "apaguna" VALUES(70,16,'70:16','Buqqiftuu gogaa sammuuti.');
INSERT INTO "apaguna" VALUES(70,17,'70:17','Nama (amantii irraa) duubatti deebi''ee, gara gale ni waamti.');
INSERT INTO "apaguna" VALUES(70,18,'70:18','(Qabeenya) walitti qabee (zakaa odoo hin kennin) lakkaa’u (hunda waamti)');
INSERT INTO "apaguna" VALUES(70,19,'70:19','Dhugumatti, namni obsa dhabeessa ta''ee uumame.');
INSERT INTO "apaguna" VALUES(70,20,'70:20','Yeroo hamtuun isa tuqe dallanaadha (gammachuu dhaba).');
INSERT INTO "apaguna" VALUES(70,21,'70:21','Yeroo wanti gaariin isa tuqe immoo dhorgataadha.');
INSERT INTO "apaguna" VALUES(70,22,'70:22','Warra sagadan malee;');
INSERT INTO "apaguna" VALUES(70,23,'70:23','Isaan salaata isaanii irratti gadi dhaabbatan (dawwaman malee);');
INSERT INTO "apaguna" VALUES(70,24,'70:24','Isaan qabeenya isaanii keessa mirgi beekamaan jiru,');
INSERT INTO "apaguna" VALUES(70,25,'70:25','Kadhataafi hiyyeessa hin kadhanneef,');
INSERT INTO "apaguna" VALUES(70,26,'70:26','Isaan Guyyaa Murtii dhugoomsan,');
INSERT INTO "apaguna" VALUES(70,27,'70:27','Isaan adabbii Gooftaa isaanii sodaatan(malee kan biroo obsa dhabaa dha).');
INSERT INTO "apaguna" VALUES(70,28,'70:28','Dhugumatti, adabbiin Gooftaa isaanii amanamaa miti.');
INSERT INTO "apaguna" VALUES(70,29,'70:29','Isaan qaama saalaa isaanii tiiksan,');
INSERT INTO "apaguna" VALUES(70,30,'70:30','Niitolii isaanii yookiin waan mirgi isaanii horatan irratti malee; dhugumatti, isaan komatamoo miti.');
INSERT INTO "apaguna" VALUES(70,31,'70:31','Namni sanaan ala barbaade immoo, isaan warra daangaa dabroodha.');
INSERT INTO "apaguna" VALUES(70,32,'70:32','Ammas isaan amaanaa isaaniitiifi waadaa isaanii eegan,');
INSERT INTO "apaguna" VALUES(70,33,'70:33','Isaan ragaa isaaniitiin dhaabbatan,');
INSERT INTO "apaguna" VALUES(70,34,'70:34','Isaan salaata isaanii irratti tiikfatan.');
INSERT INTO "apaguna" VALUES(70,35,'70:35','Warri sun jannata keessatti kabajamoodha.');
INSERT INTO "apaguna" VALUES(70,36,'70:36','Warra kafareef maaltu jiraayi gara kee ariifatu?');
INSERT INTO "apaguna" VALUES(70,37,'70:37','Garee gareen mirgaafi bitaa irra guutanii.');
INSERT INTO "apaguna" VALUES(70,38,'70:38','Sila namni isaan irraa ta''e hundi jannata qananii seensifamuu kajeelaa?');
INSERT INTO "apaguna" VALUES(70,39,'70:39','Haa dhorgamanu! Nuti waanuma isaan beekan irraa isaan uumne.');
INSERT INTO "apaguna" VALUES(70,40,'70:40','Gooftaa Bahaafi Dhiha hedduutiinin kakadhaa! Nuti dhugumatti danda’oodha.');
INSERT INTO "apaguna" VALUES(70,41,'70:41','Namoota isaan irra caalan bakka buusuu irratti (danda''oo dha)Nuti dadhabsiifamoo hin taane.');
INSERT INTO "apaguna" VALUES(70,42,'70:42','Hanga Guyyaa isaaniif beellamame san qunnamanitti, (cubbuu keessa) seenanii haa taphatanii isaan dhiisi.');
INSERT INTO "apaguna" VALUES(70,43,'70:43','Guyyaa qabrii irraa arifataa ba''an isaan waan gara taabotaa dhangala''an fakkaatu.');
INSERT INTO "apaguna" VALUES(70,44,'70:44','Ijji isaanii xiqqaattee salphinni isaan haguugdiSun guyyaa isaan isa sodaachifamaa turaniidha.');
INSERT INTO "apaguna" VALUES(71,1,'71:1','Nuti "odoo adabbiin laalessaan isaanitti hin dhufin dura ummata kee sodaachisi" jechuun Nuuhiin gara ummata isaatti ergineerra');
INSERT INTO "apaguna" VALUES(71,2,'71:2','(Nuuhis) ni jedhe: "yaa ummata kiyya! Ani isiniif akeekkachiisaa ifa bahaadha.');
INSERT INTO "apaguna" VALUES(71,3,'71:3','Rabbiin gabbaraa; Isa sodaadhaa; anaafis ajajamaa" jechuun.');
INSERT INTO "apaguna" VALUES(71,4,'71:4','"Dilii keessan irraa isiniif araarama; hanga yeroo murtaa''eettis isin tursiisaBeellamni Rabbii yeroo dhufe booda hin aanfamuOdoo kan beektan taatanii (silaa isa malee hin gabbartanu).');
INSERT INTO "apaguna" VALUES(71,5,'71:5','(Nuuhiin) ni jedhe: "Gooftaa kiyya! Ani ummata kiyya halkaniifi guyyaan waame.');
INSERT INTO "apaguna" VALUES(71,6,'71:6','Waamichi kiyya (dhugaa irraa) baqa malee homaa isaaniif hin daballe.');
INSERT INTO "apaguna" VALUES(71,7,'71:7','Akka Ati isaaniif araaramtuuf dhawaata ani isaan waamuun qubbeen isaanii gurra isaanii keessa kaa’anii uffata isaanii haguuggatanii (badii) itti fufan; booniinsa boonan.');
INSERT INTO "apaguna" VALUES(71,8,'71:8','Ergasii lallabeen isaan waame.');
INSERT INTO "apaguna" VALUES(71,9,'71:9','Achi boodas ani ifan isaaniif godhe; hasaasuus isaanittin hasaase.');
INSERT INTO "apaguna" VALUES(71,10,'71:10','‘Gooftaa keessan araarama kadhaa; dhugumatti Inni Akkaan Araaramaadhan’ jedhe.');
INSERT INTO "apaguna" VALUES(71,11,'71:11','Rooba baay''ee deddeebi''aa isin irratti erga.');
INSERT INTO "apaguna" VALUES(71,12,'71:12','Qabeenyaafi ilmaanis isiniif dabalaAshaakiltii isiniif godheeti laggeenis isiniif taasisa.');
INSERT INTO "apaguna" VALUES(71,13,'71:13','Maaltu isiniif jiraayi guddina Rabbii hin sodaanne?');
INSERT INTO "apaguna" VALUES(71,14,'71:14','Dhugumatti, sadarkaa sadarkaadhaan isin uumeera.');
INSERT INTO "apaguna" VALUES(71,15,'71:15','Sila akka Rabbiin samii torban wal jalaa gubbaatti uume hin arginee?');
INSERT INTO "apaguna" VALUES(71,16,'71:16','Isaan keessattis ji''a ibsaa taasisee; aduus madda ifaafi ho''aa (siraaja) taasise.');
INSERT INTO "apaguna" VALUES(71,17,'71:17','Rabbiinis biqilchuu (uumuu) dachii irraa isin biqilche.');
INSERT INTO "apaguna" VALUES(71,18,'71:18','Ergasii ishee keessatti isin deebiseeti, baasuus isin baasa.');
INSERT INTO "apaguna" VALUES(71,19,'71:19','Rabbiinis dachii afata isiniif godhe.');
INSERT INTO "apaguna" VALUES(71,20,'71:20','Akka isin ishee irraa karaalee babal''aa seentaniif.');
INSERT INTO "apaguna" VALUES(71,21,'71:21','Nuuhiin ni jedhe: "Gooftaa kiyya! Isaan ajaja kiyya didanii, nama qabeenyi isaatiifi ilmaan isaa hoonga’iinsa malee homaa isaaf hin daballe hordofan.');
INSERT INTO "apaguna" VALUES(71,22,'71:22','Tooftaa gurguddaa baasan');
INSERT INTO "apaguna" VALUES(71,23,'71:23','‘Gabbaramtoota keessaniis hin dhiisinaa; waddi, suwaa''i, yaghuus, ya''uuqiifi nasriinis hin dhiisinaa'' (waliin) jedhan.');
INSERT INTO "apaguna" VALUES(71,24,'71:24','Dhugumatti, (namoota) baay''ee jallisaniiruMiidhaa raawwattootaaf jallinna malee hin dabalin.”');
INSERT INTO "apaguna" VALUES(71,25,'71:25','Diliiwwan isaanii irraa kan ka''e, bishaaniin nyaachifamanii ibidda seensifamanRabbiin alatti tumsitoota isaaniif hin arganne.');
INSERT INTO "apaguna" VALUES(71,26,'71:26','Nuuhiin ni jedhe: "Gooftaa kiyya! Kaafiroota irraa nama tokkollee dachii irratti hin dhiisin.');
INSERT INTO "apaguna" VALUES(71,27,'71:27','Yoo Ati isaan hambifte gabroota Kee jallisu; dilaawoo akkaan kaafira ta''e malees hin dhalan (hin horan).');
INSERT INTO "apaguna" VALUES(71,28,'71:28','Gooftaa kiyya! Anaaf, abbaafi haadha kiyyaaf, nama mu’umina ta''ee mana kiyya seeneef, mu''uminootaafi mu''umintittiifis araaramiKaafirootaafis badii malee hin dabalin.”');
INSERT INTO "apaguna" VALUES(72,1,'72:1','(Yaa Muhammad SAW!) Gareen jinnii irraa ta''an (Qur''aana) dhageeffatanii (akkana) jedhaniin wahyiin natti godhameera jedhi: “nuti dhugumatti, Qur''aana dinqisiisaa dhageenye');
INSERT INTO "apaguna" VALUES(72,2,'72:2','[Inni] gara karaa sirriitti qajeelcha; kanaafuu, isatti amanneerraGooftaa keenyattis waan tokkollee hin qindeessinu.');
INSERT INTO "apaguna" VALUES(72,3,'72:3','Inumaa kabajaan Gooftaa keenyaa dhugumatti guddateera; niitiis ta''ee ilmoo hin godhanne.');
INSERT INTO "apaguna" VALUES(72,4,'72:4','Gowwoonni keenya Rabbi irratti soba dubbatu turan.');
INSERT INTO "apaguna" VALUES(72,5,'72:5','Nuti immoo namoonniifi jinnoonni Rabbi irratti soba hin dubbatanu jechuu yaanne.');
INSERT INTO "apaguna" VALUES(72,6,'72:6','Ammas dhiironni nama irraa ta''an dhiirota jinnii irraa ta''anitti maganfatanii boona qofa isaaniif (jinnootaaf) dabalaa turan.');
INSERT INTO "apaguna" VALUES(72,7,'72:7','Akkuma isin yaaddan isaanis Rabbiin nama tokkollee (erga du''ee) hin kaasu jechuu yaadaa turan.');
INSERT INTO "apaguna" VALUES(72,8,'72:8','Nutis samiitti ga''uu (feeneti) kan isheen eegdota jajjaboofi qaanqeen (urjii furguuggamtuu) guutamte ishee argine.');
INSERT INTO "apaguna" VALUES(72,9,'72:9','Nuti ishee irraa teessuma (haasa''a) dhageeffachuuf teenyu qabna turreNamni amma dhageeffate immoo qaanqee (urjii) isa eeggatu isaaf arga.');
INSERT INTO "apaguna" VALUES(72,10,'72:10','Nuti nama dachii keessa jirutti hamtuun yaadamuu yookiin Gooftaan isaanii qajeeluma isaaniin fedhuu hin beeknu!');
INSERT INTO "apaguna" VALUES(72,11,'72:11','Nutis nurraa gaggaariin ni jiranAmmas nurraa kan sanaan ala ta''antu jiruNuti warra karaa adda addaati turre');
INSERT INTO "apaguna" VALUES(72,12,'72:12','Nuti dachii keessatti Rabbiin jalaa hin baanu; dheessuudhaanis Isa hin dadhabsiifnuu beeknee jirra.');
INSERT INTO "apaguna" VALUES(72,13,'72:13','Nuti yeroma Qajeelfama dhageenye isatti amanneNamni Gooftaa isaatti amane hir’achuu (mindaa) ta''ee dabaltii (adabaa) hin sodaatu.');
INSERT INTO "apaguna" VALUES(72,14,'72:14','Nuti, nurraa muslimoonni ni jiran; jallattoonnis nurraa ni jiru; Namni Islaamayes warri sun karaa qajeelaa barbaadanii jiru.');
INSERT INTO "apaguna" VALUES(72,15,'72:15','Jallattoonni immoo Jahannamiif qoraan ta''an.”');
INSERT INTO "apaguna" VALUES(72,16,'72:16','Odoo isaan karaa qajeelaa irra gadi dhaabbatanii, bishaan baay''ee (samii irraa) isaan obaafna turre.');
INSERT INTO "apaguna" VALUES(72,17,'72:17','Isa keessatti isaan qormaatuuf jecha (obaafna ture)Namni yaadannoo Gooftaa isaa (Qur''aana) irraa garagale, (Rabbiin) adabbii jabaa isa seensisa.');
INSERT INTO "apaguna" VALUES(72,18,'72:18','Masjiidonni kan Rabbiiti; Rabbii wajjiin homaa hin waammatinaa.');
INSERT INTO "apaguna" VALUES(72,19,'72:19','Yeroo gabrichi Rabbii (Muhammad SAW!) Isa waammachuuf dhaabbatu (jinnoonni) isa irratti tuuta ta''utti dhihaatu.');
INSERT INTO "apaguna" VALUES(72,20,'72:20','(Yaa Muhammad SAW!) jedhi: "wanti ani gabbaru Gooftaa kiyya qofa; Isattiis waan tokkollee hin qindeessu."');
INSERT INTO "apaguna" VALUES(72,21,'72:21','Jedhi: "Ani dhugumatti, miidhaas ta''ee qajelluma isiniif (fiduu) hin danda''u."');
INSERT INTO "apaguna" VALUES(72,22,'72:22','Jedhi: "Ani (adabbii) Rabbii irraa wanti tokkollee ana hin baraarsuIsa malees hirkoo hin argadhu.');
INSERT INTO "apaguna" VALUES(72,23,'72:23','Rabbi irraa (wahyii)fi ergaawwan Isaa geessuu malee (hin danda''u)Namni Rabbiifi Ergamaa Isaa faallesse, dhugumatti ibidda jahannam kan ishee keessatti hafutu isaaf jira.');
INSERT INTO "apaguna" VALUES(72,24,'72:24','Yeroo waan ittiin sodaachifaman argan namni tumsaadhaan akkaan dadhabaafi lakkoofsaan daran xiqqaa eenyuun akka ta''e beekuuf jiru.');
INSERT INTO "apaguna" VALUES(72,25,'72:25','“Wanti isin sodaachifamtan dhihoo ta''uu yookiin Rabbiin kiyya beellama yeroo (dheeraa) isaaf godhaa hin beeku” jedhiin.');
INSERT INTO "apaguna" VALUES(72,26,'72:26','(Rabbiin) Beekaa fagooti; fagoo Isaa irratti nama tokkollee hin mul''isu.');
INSERT INTO "apaguna" VALUES(72,27,'72:27','Ergamaa irraa nama jaalate malee (kan biraatti hin mul''isu)Inni fuuldura isaatiifi duuba isaatiin eegdota (malaykota) seensisa.');
INSERT INTO "apaguna" VALUES(72,28,'72:28','Haaluma waan isaan bira jiru hundatti marseefi waan hundaayyuu lakkoofsaan walitti qabee jiruun ‘ergaawwan Gooftaa isaanii dhugumatti bakkaan ga''uu isaanii’ akka ifa baasuuf (eegdota kaa''e)');
INSERT INTO "apaguna" VALUES(73,1,'73:1','Yaa mammarataa!');
INSERT INTO "apaguna" VALUES(73,2,'73:2','Xiqqo malee halkan guutuu dhaabbadhu');
INSERT INTO "apaguna" VALUES(73,3,'73:3','Walakkaa isaa (dhaabbadhu) yookiin xiqqoo isa irraa hir’isi.');
INSERT INTO "apaguna" VALUES(73,4,'73:4','Yookiin isa irratti dabaliQur''aanas suutaan dubbisi.');
INSERT INTO "apaguna" VALUES(73,5,'73:5','Nuti jecha ulfaataa sirratti buusuuf jirra.');
INSERT INTO "apaguna" VALUES(73,6,'73:6','Dhugumatti, ka''umsi halkanii (arrabaafi qalbii) wal simachiisuutti akkaan jabaafi dubbisuufis qajeelaadha.');
INSERT INTO "apaguna" VALUES(73,7,'73:7','Guyyaa keessa (dhimma jireenyaatiif) sochii dheeraatu siif jira.');
INSERT INTO "apaguna" VALUES(73,8,'73:8','Maqaa Gooftaa keetiis faarsi; gara isaattis citiinsa citi.');
INSERT INTO "apaguna" VALUES(73,9,'73:9','(Inni) Gooftaa bahaafi dhihaatiIsa malee gabbaramaan dhugaa hin jiru; hirkoo isa taasifadhu.');
INSERT INTO "apaguna" VALUES(73,10,'73:10','Waan isaan jedhan irratti obsi; fageenya gaarii isaan irraa fagaadhu.');
INSERT INTO "apaguna" VALUES(73,11,'73:11','Anaafi sobsiiftota warra qananii walitti Na dhiisi; beellama xiqqoos isaaniif qabi');
INSERT INTO "apaguna" VALUES(73,12,'73:12','Dhugumatti, Nu bira sakaallaa ulfaataafi ibiddatu jira');
INSERT INTO "apaguna" VALUES(73,13,'73:13','Akkasumas nyaata nama hudhuufi adabbii laalessaa ta''etu (jira)');
INSERT INTO "apaguna" VALUES(73,14,'73:14','Guyyaa dachiifi gaarreen hurgufamaniifi gaarreen tuulaa cirracha yaa''uu ta''u keessa (sakkaalamu)');
INSERT INTO "apaguna" VALUES(73,15,'73:15','Nuti akkuma gara Fir''awn Ergamaa erginee turre gara keessanittis Ergamaa ragaa isin irratti ba''u erginee jirra.');
INSERT INTO "apaguna" VALUES(73,16,'73:16','Fir''awnnis Ergamicha faallessee (didee) qabaa cimaa isa qabne');
INSERT INTO "apaguna" VALUES(73,17,'73:17','Yoo kafartaniif, (adabbii) Guyyaa ijoollee arrii taasisuu akkamitti eeggattu?');
INSERT INTO "apaguna" VALUES(73,18,'73:18','Samiin isaan (guyyaa sanaan) dhodhootuudhaBaallamni Isaa raawwatamaa ta''e.');
INSERT INTO "apaguna" VALUES(73,19,'73:19','Dhugumatti, isheen kun gorsaNama fedhe gara Gooftaa isaatti karaa godhata');
INSERT INTO "apaguna" VALUES(73,20,'73:20','(Yaa Muhammad SAW!) Dhugumatti akka atiifi warri sii wajjin ta''an irraa gareen wahii, lama sadaffaa halkanii, walakkaa isaatiifi tokko sadaffaa isaatii gadis dhaabbattan Gooftaan kee ni beekaRabbiin halkaniifi guyyaa ni shallagaAkka isin isa safartanii waliin hin geenye beekee tawbaa keessan isin irraa qeebaleQur''aana irraas (salaata keessatti) waan isiniif laafe qara''aaIsin irraa dhukkubsataan akka jiraatu, warri biraa immoo tola Rabbii barbaacha dachii keessa akka deeman, warri biraas karaa Rabbii keessatti akka lolan beekeeti (isin irrraa salphise)Kanaafuu, isa (Qur''aana) irraa waan isiniif laafe qara''aatii salaata dhaabaa; zakaas kennaaLiqii bareedaas Rabbiif liqeessaaToltuu irraa waan lubbuu keessaniif dabarfattan Rabbiin biratti inni caalaafi kan mindaa guddaa qabu ta''eeti isa argituRabbiin araarama kadhaa; dhugumatti, Rabbiin araaramaa, rahmata godhaadha');
INSERT INTO "apaguna" VALUES(74,1,'74:1','Yaa mammarataa!');
INSERT INTO "apaguna" VALUES(74,2,'74:2','Ka''ii akeekkachiisi.');
INSERT INTO "apaguna" VALUES(74,3,'74:3','Gooftaa keetis guddisi');
INSERT INTO "apaguna" VALUES(74,4,'74:4','Uffata keetis qulqulleessi');
INSERT INTO "apaguna" VALUES(74,5,'74:5','Taabotaanis oodi.');
INSERT INTO "apaguna" VALUES(74,6,'74:6','Baay''ifannaa barbaachaaf hin arjoomin.');
INSERT INTO "apaguna" VALUES(74,7,'74:7','Gooftaa keetiif obsi.');
INSERT INTO "apaguna" VALUES(74,8,'74:8','Yeroo xurumbaa keessa afuufame;');
INSERT INTO "apaguna" VALUES(74,9,'74:9','Sun, guyyaan gaafasii guyyaa rakkisaadha');
INSERT INTO "apaguna" VALUES(74,10,'74:10','Kaafiroota irratti (guyyaa) laafaa miti');
INSERT INTO "apaguna" VALUES(74,11,'74:11','Nama ani kophaa isa uumee wajjin (walitti) Na dhiisi;');
INSERT INTO "apaguna" VALUES(74,12,'74:12','Kan Ani qabeenya bal''aas isaaf godhe;');
INSERT INTO "apaguna" VALUES(74,13,'74:13','Ilmaan dhiiraa kan isa biratti argamanis;');
INSERT INTO "apaguna" VALUES(74,14,'74:14','Bal''isuus kan Ani isaaf bal''ise;');
INSERT INTO "apaguna" VALUES(74,15,'74:15','Ergasii akka Ani isaaf dabalus kan kajeelu;');
INSERT INTO "apaguna" VALUES(74,16,'74:16','Haa dhorgamu! Inni keeyyattoota Keenya mormaa ture.');
INSERT INTO "apaguna" VALUES(74,17,'74:17','Dhugumatti, rakkoo guddaattin isa dirqa.');
INSERT INTO "apaguna" VALUES(74,18,'74:18','Inni ni xiinxale; ni tilmaames.');
INSERT INTO "apaguna" VALUES(74,19,'74:19','Abaarame! Akkamumatti tilmaame?');
INSERT INTO "apaguna" VALUES(74,20,'74:20','Ergasiis abaarame! Akkamumatti tilmaame?');
INSERT INTO "apaguna" VALUES(74,21,'74:21','Ergasii ni ilaale');
INSERT INTO "apaguna" VALUES(74,22,'74:22','Ergasii fuula guure; akkaan jijjiirame');
INSERT INTO "apaguna" VALUES(74,23,'74:23','Ergasii gara gale; ni boone');
INSERT INTO "apaguna" VALUES(74,24,'74:24','Ni jedhes: "(Qur''aanni) kun falfala (nama biraa) irraa waraabame qofa.');
INSERT INTO "apaguna" VALUES(74,25,'74:25','Kun jecha namaa malee hin taane."');
INSERT INTO "apaguna" VALUES(74,26,'74:26','Saqar isa seensisuufin jira');
INSERT INTO "apaguna" VALUES(74,27,'74:27','Saqar maal akka ta''e maaltu si barsiise?');
INSERT INTO "apaguna" VALUES(74,28,'74:28','(Foon) hin hambiftu (lafees) hin dhiiftu');
INSERT INTO "apaguna" VALUES(74,29,'74:29','Gogaas gubduudha');
INSERT INTO "apaguna" VALUES(74,30,'74:30','Ishee irra (waardiyyaa) kudha sagaltu jira.');
INSERT INTO "apaguna" VALUES(74,31,'74:31','Eegdota Ibiddaas malaykota malee kan biraa hin gooneLakkoofsa isaaniis isaan kafaraniif qormaata, warri kitaaba kennaman akka mirkaneeffatan, warri amanan iimaana akka dabalatanuuf, warri kitaaba kennamaniifi mu''uminoonnis akka hin shakkinee,fi akka warri onnee isaanii keessa dhukkubni jiruufi kaafiroonni ‘Rabbiin (lakkoofsa) kanaan fakkeenya maalii fedhe’ jedhaniif malee kana hin gooneAkka kanatti Rabbiin nama fedhe jallisee nama fedhe immoo qajeelcha(Baay''ina) waraana Gooftaa keetiis Isa malee homtuu hin beekuIsheenis namaaf gorsa malee waan biraatii miti.');
INSERT INTO "apaguna" VALUES(74,32,'74:32','Haa dhorgamu! Ji''attin kakadha!');
INSERT INTO "apaguna" VALUES(74,33,'74:33','Halkaniinis; yeroo inni deeme.');
INSERT INTO "apaguna" VALUES(74,34,'74:34','Bariidhaanis yeroo inni ife.');
INSERT INTO "apaguna" VALUES(74,35,'74:35','Dhugumatti, isheen (saqar) tokkoo (rakkoolee) gurgurddooti.');
INSERT INTO "apaguna" VALUES(74,36,'74:36','Namaaf akeekkachiiftuudha.');
INSERT INTO "apaguna" VALUES(74,37,'74:37','Nama isin irraa (hojii gaariidhaan) fuulduratti deemuu, yookiin duubatti hafuu fedheef (akeekkachiistuu dha).');
INSERT INTO "apaguna" VALUES(74,38,'74:38','Lubbuun hundi waan hojjatteen qabamtuudha');
INSERT INTO "apaguna" VALUES(74,39,'74:39','Warra mirgaa malee.');
INSERT INTO "apaguna" VALUES(74,40,'74:40','(Isaan) jannata keessatti wal gaggaafatu.');
INSERT INTO "apaguna" VALUES(74,41,'74:41','Yakkamtoota irraa (wal gaafatu).');
INSERT INTO "apaguna" VALUES(74,42,'74:42','"Maaltu Saqariin keessa isin seensise?" (isaanin jedhu)');
INSERT INTO "apaguna" VALUES(74,43,'74:43','Ni jedhan: "nuti warra salaatan irraa hin turre.');
INSERT INTO "apaguna" VALUES(74,44,'74:44','Hiyyeeyyii kan nyaachifnus hin turre;');
INSERT INTO "apaguna" VALUES(74,45,'74:45','Warra (hamtuu) keessa seenuu wajjin seenaa turre;');
INSERT INTO "apaguna" VALUES(74,46,'74:46','Guyyaa mindaas kan sobsiifnu turre;');
INSERT INTO "apaguna" VALUES(74,47,'74:47','Hanga (duuti) mirkanaayaan nutti dhufutti (haala kana irra turre)" (jedhu)');
INSERT INTO "apaguna" VALUES(74,48,'74:48','Jaarsummaan (araarri) warra araarsanii isaan hin fayyadu.');
INSERT INTO "apaguna" VALUES(74,49,'74:49','Maaltu isaaniif jiraayi gorsa (Qur''aanaa) irraa gara galoo ta''u?');
INSERT INTO "apaguna" VALUES(74,50,'74:50','(Yeroo akeekkachiisa Rabbii irraa dheessan) akka harree diidoo dammaqanii (gugatanii) fakkaatu');
INSERT INTO "apaguna" VALUES(74,51,'74:51','Leenca irraa kan baqatan.');
INSERT INTO "apaguna" VALUES(74,52,'74:52','Inumaa namni isaan irraa ta''e hundi kitaabota banamoo kennamuu fedha.');
INSERT INTO "apaguna" VALUES(74,53,'74:53','Dhugumatti, (hin kennamanu) isaan Aakhirumaayyuu hin sodaatanu');
INSERT INTO "apaguna" VALUES(74,54,'74:54','Dhugumatti, inni (Qur''aanni) gorsa');
INSERT INTO "apaguna" VALUES(74,55,'74:55','Nama fedheetu isaan gorfama.');
INSERT INTO "apaguna" VALUES(74,56,'74:56','Yoo Rabbiin fedhe malee hin gorfamanuInni (Rabbiin) kan sodaatamuu haqa godhatuufi Abbaa Araaramaati.');
INSERT INTO "apaguna" VALUES(75,1,'75:1','Lakki! Guyyaa Qiyaamaatiinin kakadha.');
INSERT INTO "apaguna" VALUES(75,2,'75:2','Lubbuu of komattuu taateenis nan kakadha; (mindaa galfamuuf jirtu)');
INSERT INTO "apaguna" VALUES(75,3,'75:3','Sila namni lafee isaa walitti hin qabnuu yaadaa?');
INSERT INTO "apaguna" VALUES(75,4,'75:4','Eeyyeen (walitti ni qabna)(Nuti) qubbeen isaatuu akka turanitti deebifnee sirreessuu irratti danda''oodha');
INSERT INTO "apaguna" VALUES(75,5,'75:5','Dhugumatti namni fuuldura isaa (Qiyaamaa) sobsiisuu fedha.');
INSERT INTO "apaguna" VALUES(75,6,'75:6','"Guyyaan Qiyaamaa yoomii?"gaafata.');
INSERT INTO "apaguna" VALUES(75,7,'75:7','(Guyyaan sun) yeroo ijji dhamaatu.');
INSERT INTO "apaguna" VALUES(75,8,'75:8','(Yeroo) jiini ifa dhabu (gaddiddaa’u)');
INSERT INTO "apaguna" VALUES(75,9,'75:9','Aduufi jiinis walitti qabamu.');
INSERT INTO "apaguna" VALUES(75,10,'75:10','Guyyaa san namni "bakki baqaa eessa?" jedha');
INSERT INTO "apaguna" VALUES(75,11,'75:11','Dhorgamaa! (Guyyaa san) iddoon baqaa hin jiru.');
INSERT INTO "apaguna" VALUES(75,12,'75:12','Guyyaa san bakki qubannaa gara Gooftaa keetiiti.');
INSERT INTO "apaguna" VALUES(75,13,'75:13','Namni guyyaa san wanti inni hojjateefi dhiise itti himama');
INSERT INTO "apaguna" VALUES(75,14,'75:14','Dhugumatti, namni of irratti ragaadha');
INSERT INTO "apaguna" VALUES(75,15,'75:15','Odoma rakkoo adda addaa dhiyeeffateeyyuu');
INSERT INTO "apaguna" VALUES(75,16,'75:16','Isa (qabachuu)tti ariifachuuf jecha arraba kee isaan hin sochoosin');
INSERT INTO "apaguna" VALUES(75,17,'75:17','Dhugumatti, isa (onnee kee keessatti) walitti qabuufi isa dubbisuun kee nurra jira.');
INSERT INTO "apaguna" VALUES(75,18,'75:18','.Yeroo Nuti isa qaraane qiraatii isaa hordofi');
INSERT INTO "apaguna" VALUES(75,19,'75:19','Ergasii isa ibsuun Nurra jira.');
INSERT INTO "apaguna" VALUES(75,20,'75:20','Dhugumatti, isin ariifattuu (duuniyaa) jaalattu.');
INSERT INTO "apaguna" VALUES(75,21,'75:21','Aakhiraa ni dhiiftu');
INSERT INTO "apaguna" VALUES(75,22,'75:22','Fuulli gariin Guyyaa san iftuudha');
INSERT INTO "apaguna" VALUES(75,23,'75:23','Gara Gooftaa ishees ilaaltuudha');
INSERT INTO "apaguna" VALUES(75,24,'75:24','Fuulli gariinis Guyyaa san akkaan suntuurtuudha.');
INSERT INTO "apaguna" VALUES(75,25,'75:25','Balaan guddaan ishee irratti raawwatamuu yaaddi.');
INSERT INTO "apaguna" VALUES(75,26,'75:26','Dhaga’aa! Yeroo (lubbuun ba''uuf) kokkee bira geesse,');
INSERT INTO "apaguna" VALUES(75,27,'75:27','(Yeroo) "eenyutu isa yaala?" jedhame,');
INSERT INTO "apaguna" VALUES(75,28,'75:28','(Namni du''aa jiru sun) ‘dhugumatti, inni (addunyaa kana irraa) adda ba''uu dha’ jechuu yeroo mirkaneeffate;');
INSERT INTO "apaguna" VALUES(75,29,'75:29','(Yeroo) mogoleen mogoleetti maramte.');
INSERT INTO "apaguna" VALUES(75,30,'75:30','Guyyaa san oofamiinsi gara Gooftaa keetiiti.');
INSERT INTO "apaguna" VALUES(75,31,'75:31','Hin amanne; hin salaanne.');
INSERT INTO "apaguna" VALUES(75,32,'75:32','Garuu kijibsiisee irraa gara gale.');
INSERT INTO "apaguna" VALUES(75,33,'75:33','Ergasii boonaa gara maatii isaa deeme');
INSERT INTO "apaguna" VALUES(75,34,'75:34','Badiin siif haa ta’u! Ammas badiin!');
INSERT INTO "apaguna" VALUES(75,35,'75:35','Sana booda badiin siif haa ta’u! Ammas badiin!');
INSERT INTO "apaguna" VALUES(75,36,'75:36','Sila namni akkanumaan dhiifamuu yaadaa?');
INSERT INTO "apaguna" VALUES(75,37,'75:37','Sila inni bishaan saalaa kan dhangalaafamu irraa ta''ee hin turree?');
INSERT INTO "apaguna" VALUES(75,38,'75:38','Ergasii dhiiga ititaa ta''eeti (nama godhee) uumee guute.');
INSERT INTO "apaguna" VALUES(75,39,'75:39','Isa irraas cimdii lama, dhiiraafi dhalaa, taasise');
INSERT INTO "apaguna" VALUES(75,40,'75:40','Sila (Rabbiin akkas godhe) sun du''aa jiraachisuu irratti danda''aa mitii?');
INSERT INTO "apaguna" VALUES(76,1,'76:1','Sila barri hangi ta''e nama irra kan inni waan yaadatamu hin ta''in hin dhufnee?');
INSERT INTO "apaguna" VALUES(76,2,'76:2','Dhugumatti Nuti nama, isa qormaatuuf jecha bishaan saalaa walitti makamaa ta''e irraa uumnee; Dhaga''aafi argaas isa goone');
INSERT INTO "apaguna" VALUES(76,3,'76:3','Nuti haala (inni) galateeffataa yookiin mormaa ta''een karaa isa qajeelchine.');
INSERT INTO "apaguna" VALUES(76,4,'76:4','Nuti kaafirootaaf sansalata, sakaallaawwaniifi ibidda boba''aa qopheessinee jirra.');
INSERT INTO "apaguna" VALUES(76,5,'76:5','Warri gaggaariin khamrii makaan ishee kaafuuriin ta''e irraa dhugu.');
INSERT INTO "apaguna" VALUES(76,6,'76:6','(Kun) burqaa gabroonni Rabbii ishee dhuganiidha(Karaa fedhanitti) dhangalaafachuu ishee dhangalaafatu');
INSERT INTO "apaguna" VALUES(76,7,'76:7','Qodhaa (nazrii) isaanii ni guutu; guyyaa hamtuun isaa babal''atus ni sodaatu');
INSERT INTO "apaguna" VALUES(76,8,'76:8','Nyaatas ofii jaallachuu wajjin hiyyeessa, yatiimaafi booji''amaas nyaachisu.');
INSERT INTO "apaguna" VALUES(76,9,'76:9','(Ni jedhu): "dhugumatti, wanti nuti isin nyaachifnuuf Fuula Rabbii qofaafi; isin irraa mindaas ta''ee galata wahii hin barbaannu.');
INSERT INTO "apaguna" VALUES(76,10,'76:10','Dhugumatti, nuti Gooftaa keenya irraa Guyyaa fuula guuraa hammaataa ta''e sodaanna.”');
INSERT INTO "apaguna" VALUES(76,11,'76:11','Rabbiinis hamtuu guyyaa gaafasii irraa isaan tikseeti (fuula isaaniis) ifaafi gammachuu qunnamsiise.');
INSERT INTO "apaguna" VALUES(76,12,'76:12','Sababa isaan obsaniifis jannataafi hariira galata isaaniif galche');
INSERT INTO "apaguna" VALUES(76,13,'76:13','Ishee keessatti sireewwan irratti hirkatanii, odoo ishee keessatti aduufi qorra hamaa hin argine (jiraatu)');
INSERT INTO "apaguna" VALUES(76,14,'76:14','Haala gaaddisni ishee isaan irratti gadi dhihoo, firiiwwan ishees isaaniif laaffifamuu laaffifamteen (jannata seenu).');
INSERT INTO "apaguna" VALUES(76,15,'76:15','Meeshota meeta irraa ta''aniifi kubbaayyawwan qaruuraa irraa ta''een isaan irra naanna''ama.');
INSERT INTO "apaguna" VALUES(76,16,'76:16','Qaruuraalee meeta irraa ta''een hanga isaan ga''u ishee (irraa) qoodu.');
INSERT INTO "apaguna" VALUES(76,17,'76:17','Ishee keessattis khamrii makaan ishee zinjibila ta''e obaafamu.');
INSERT INTO "apaguna" VALUES(76,18,'76:18','Burqaa ishee (jannata) keessa jirtu Salsabiil jedhamtee kan waamamtu (irraa obaafamu).');
INSERT INTO "apaguna" VALUES(76,19,'76:19','Ijoollee ishee keessatti hafoo ta’antu isaan irra naanna''a; yeroo isaan argitu faaya bibittinneeffame sitti fakkaatu');
INSERT INTO "apaguna" VALUES(76,20,'76:20','Yeroo achi keessa ilaaltes qananiifi mootummaa guddaa argita.');
INSERT INTO "apaguna" VALUES(76,21,'76:21','Gubbaa isaanii irra uffata magariisa hariira qal''aafi hariira furdaatu jira; gumeelee meeta irraa ta''es ni faayamuGooftaan isaaniis dhugaatii qulqulleessaa isaan obaasa');
INSERT INTO "apaguna" VALUES(76,22,'76:22','"Dhugumatti, kun mindaa isiniif ta''eeraDalagaan keessanis galateeffatamaa dha" (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(76,23,'76:23','Dhugumatti, Nuti Qur''aana buusiinsa sirratti buusnee jirra');
INSERT INTO "apaguna" VALUES(76,24,'76:24','Murtii Gooftaa keetiitiifis obsi; isaan irraa nama dilaawaa yookiin kafaraa ta''eef hin ajajamin.');
INSERT INTO "apaguna" VALUES(76,25,'76:25','Ganamaafi galgalas maqaa Gooftaa keetii faarsi.');
INSERT INTO "apaguna" VALUES(76,26,'76:26','Halkan irraallee Isaaf salaati; halkan dheeraas Isaaf (salaata laylii) salaati');
INSERT INTO "apaguna" VALUES(76,27,'76:27','Dhugumatti, warri kun (addunyaa) arifattuu jaalatu; guyyaa ulfaataas fuulduratti dhiisu (dagatu).');
INSERT INTO "apaguna" VALUES(76,28,'76:28','Nuti isaan uumnee qaama isaaniis jabeessine; yeroo feenes (isaan balleessinee) fakkaatuma isaanii bakka buusinsa bakka buufna.');
INSERT INTO "apaguna" VALUES(76,29,'76:29','Dhugumatti, kuni gorsa; namni fedhe gara Gooftaa isaatti karaa godhata.');
INSERT INTO "apaguna" VALUES(76,30,'76:30','Yoo Rabbiin fedhe malee isin homaa hin feetanRabbiin beekaa ogeessa ta’eera.');
INSERT INTO "apaguna" VALUES(76,31,'76:31','Nama fedhe rahmata Isaa keessa seensisaMiidhaa raawwattoota immoo isaaniif adabbii laalessaa qopheesse.');
INSERT INTO "apaguna" VALUES(77,1,'77:1',' (Maleykota) wal hordoftuu ta''anii ergamaniinin kakadha.');
INSERT INTO "apaguna" VALUES(77,2,'77:2',' (Bubbeewwan) humnaan bubbisuu bubbisaniinis;');
INSERT INTO "apaguna" VALUES(77,3,'77:3',' (Bubbeewwan rooba) faffacaasuu facaasaniinis;');
INSERT INTO "apaguna" VALUES(77,4,'77:4',' (Keeyyatoota haqaafi dhara) adda baasuu adda baasanittis;');
INSERT INTO "apaguna" VALUES(77,5,'77:5',' (Malaykota) ergaa buuftotattis;');
INSERT INTO "apaguna" VALUES(77,6,'77:6',' Sababaa dhabamsiisuuf yookiin akeekkachiisuuf jecha (kan buusanittin kakadha).');
INSERT INTO "apaguna" VALUES(77,7,'77:7',' Wanti (Qiyaamaan) isin sodaachifamtan argamaadha.');
INSERT INTO "apaguna" VALUES(77,8,'77:8','Yeroo urjiiwwan ifti ishee haqamte,');
INSERT INTO "apaguna" VALUES(77,9,'77:9',' Yeroo samiinis babbanamte;');
INSERT INTO "apaguna" VALUES(77,10,'77:10',' Yeroo gaarreenis (bakka irraa) bubuqqifamte,');
INSERT INTO "apaguna" VALUES(77,11,'77:11',' Yeroo Ergamoonnis beellamaan walitii qabamte;');
INSERT INTO "apaguna" VALUES(77,12,'77:12',' Guyyaa kamiif beellamaman?');
INSERT INTO "apaguna" VALUES(77,13,'77:13',' Guyyaa addaan bahiinsaatiif (beellamaman)');
INSERT INTO "apaguna" VALUES(77,14,'77:14',' Guyyaan Addaan bahiinsaa maal akka ta''e maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(77,15,'77:15',' Kijibsiiftotaaf guyyaa san badiin mirkanaa''e');
INSERT INTO "apaguna" VALUES(77,16,'77:16',' Sila Nuti warra duraanii hin balleessinee?');
INSERT INTO "apaguna" VALUES(77,17,'77:17',' Ergasiis warra boodaa isaan hordofsiifna.');
INSERT INTO "apaguna" VALUES(77,18,'77:18',' Yakkamtootas akkanuma goona.');
INSERT INTO "apaguna" VALUES(77,19,'77:19',' Kijibsiiftotaaf guyyaa san badiin mirkanaa''e.');
INSERT INTO "apaguna" VALUES(77,20,'77:20',' Sila Nuti bishaan dadhabaa (maniyyii) irraa isin hin uumnee?');
INSERT INTO "apaguna" VALUES(77,21,'77:21',' Bakka mijaa''aa keessas isa goone.');
INSERT INTO "apaguna" VALUES(77,22,'77:22',' Hanga yeroo beekamaatti');
INSERT INTO "apaguna" VALUES(77,23,'77:23',' Ni shallagnes; nuti shallagdoota akkaan tole');
INSERT INTO "apaguna" VALUES(77,24,'77:24',' Kijibsiiftotaaf guyyaa san badiin mirkanaa''e.');
INSERT INTO "apaguna" VALUES(77,25,'77:25',' Sila Nuti dachii kan hunda walitti qabdu hin taasifnee?');
INSERT INTO "apaguna" VALUES(77,26,'77:26',' Jiraafi du''aas (walitti qabdi)?');
INSERT INTO "apaguna" VALUES(77,27,'77:27',' Gaarreen ragga''oo dhedheeraa ishee keessatti gooneBishaan mi''aawaas isin obaafne.');
INSERT INTO "apaguna" VALUES(77,28,'77:28',' Kijibsiiftotaaf guyyaa san badiin mirkanaa''e.');
INSERT INTO "apaguna" VALUES(77,29,'77:29',' (Isaaniin ni jedhama): "gara waan sobsiisaa turtanii deemaa.');
INSERT INTO "apaguna" VALUES(77,30,'77:30',' Gara gaaddisa abbaa damee sadihii deemaa."');
INSERT INTO "apaguna" VALUES(77,31,'77:31',' Gaaddiseessaa miti; boba''aa ibiddaa irraas hin duroomsu');
INSERT INTO "apaguna" VALUES(77,32,'77:32',' Isheen qaanqeewwan akka gamoo darbatti.');
INSERT INTO "apaguna" VALUES(77,33,'77:33',' Inni (qaanqechi) gaalota bobboora fakkaata.');
INSERT INTO "apaguna" VALUES(77,34,'77:34',' Kijibsiiftotaaf guyyaa san badiin mirkanaa''e.');
INSERT INTO "apaguna" VALUES(77,35,'77:35',' Kun Guyyaa isaan hin dubbanneedha.');
INSERT INTO "apaguna" VALUES(77,36,'77:36',' Rakkoo haa himataniif isaaniif hin eeyyamamu.');
INSERT INTO "apaguna" VALUES(77,37,'77:37',' Kijibsiiftotaaf guyyaa san badiin mirkanaa''e.');
INSERT INTO "apaguna" VALUES(77,38,'77:38',' Kun guyyaa adda foo''amiinsaati; Isiniifi warra duraas walitti qabne.');
INSERT INTO "apaguna" VALUES(77,39,'77:39',' Yoo tooftaa wahii qabaattan tooftaa Natti baafadhaa.');
INSERT INTO "apaguna" VALUES(77,40,'77:40',' Kijibsiiftotaaf guyyaa san badiin mirkanaa''e.');
INSERT INTO "apaguna" VALUES(77,41,'77:41',' Dhugumatti, warri Rabbiin sodaatan gaaddisootaafi burqituuwwan keessa jiraatu.');
INSERT INTO "apaguna" VALUES(77,42,'77:42',' Asheeta irraa waan fedhan (keessa taa''u).');
INSERT INTO "apaguna" VALUES(77,43,'77:43','"Sababaa waan dalagaa turtaniitiif gammachuun nyaadhaa; dhugaa" (jedhamu).');
INSERT INTO "apaguna" VALUES(77,44,'77:44',' Nuti akka kanatti warra toltuu hojjatan mindeessina.');
INSERT INTO "apaguna" VALUES(77,45,'77:45',' Kijibsiiftotaaf guyyaa san badiin mirkanaa''e.');
INSERT INTO "apaguna" VALUES(77,46,'77:46',' (Addunyaa irratti) "nyaadhaa; xiqqoo qanani''aa; dhugumatti isin badii hojjattoota” (isaaniin jedhama).');
INSERT INTO "apaguna" VALUES(77,47,'77:47',' Kijibsiiftotaaf guyyaa san badiin mirkanaa''e.');
INSERT INTO "apaguna" VALUES(77,48,'77:48',' Yeroo ‘salaataa’ isaaniin jedhames hin salaatanu.');
INSERT INTO "apaguna" VALUES(77,49,'77:49',' Kijibsiiftotaaf guyyaa san badiin mirkanaa''e.');
INSERT INTO "apaguna" VALUES(77,50,'77:50',' Isaan ala haasawa kamitti amanu ree?');
INSERT INTO "apaguna" VALUES(78,1,'78:1','[Warri hin amanin] maal irraa wal gaafatuu?');
INSERT INTO "apaguna" VALUES(78,2,'78:2','Oduu guddaa irraa [wal gaafatu');
INSERT INTO "apaguna" VALUES(78,3,'78:3','Isa isaan isa keessatti wal dhaban [irraayi].');
INSERT INTO "apaguna" VALUES(78,4,'78:4','Haa dhowwamanu! fuula dura beekuuf jiru');
INSERT INTO "apaguna" VALUES(78,5,'78:5','Ammas haa dhowwamanu! fuula dura beekuuf jiru.');
INSERT INTO "apaguna" VALUES(78,6,'78:6','Sila Nuti dachii firaasha hin goonee?');
INSERT INTO "apaguna" VALUES(78,7,'78:7','Gaarreenis shikaala [hin goonee]?');
INSERT INTO "apaguna" VALUES(78,8,'78:8','Cimdiis goonee isin uumne.');
INSERT INTO "apaguna" VALUES(78,9,'78:9','Hirriba keessan boqonnaa goone.');
INSERT INTO "apaguna" VALUES(78,10,'78:10','Halkan uffata goone.');
INSERT INTO "apaguna" VALUES(78,11,'78:11','Guyyaas yeroo jireenyaa (keessa hojjatan) goone.');
INSERT INTO "apaguna" VALUES(78,12,'78:12','Gubbaa keessanittis [samii] jajjaboo torba ijaarre.');
INSERT INTO "apaguna" VALUES(78,13,'78:13','Ifaa boba''aas uumne.');
INSERT INTO "apaguna" VALUES(78,14,'78:14','Duumessa irraas bishaan dhangala''aa baay''ee buufne.');
INSERT INTO "apaguna" VALUES(78,15,'78:15','Isaan fuduraafi marga baasuuf jecha [buufne]');
INSERT INTO "apaguna" VALUES(78,16,'78:16','Ashaakiltii damee wal keessa seensifattes.');
INSERT INTO "apaguna" VALUES(78,17,'78:17','Dhugumatti, guyyaan fooyamaa yeroo beellamamaa ta''e.');
INSERT INTO "apaguna" VALUES(78,18,'78:18','[Guyyaan sun] guyyaa Garriin (xurumbaan) afuufamee isinis garee gareen dhuftanuudha.');
INSERT INTO "apaguna" VALUES(78,19,'78:19','[Guyyaa] samiinillee banamtee hulaalee taatuudha.');
INSERT INTO "apaguna" VALUES(78,20,'78:20','Gaarreenis deemsifamtee siribii (siribduu) taate.');
INSERT INTO "apaguna" VALUES(78,21,'78:21','Dhugumatti jahannam waldaa taateetti');
INSERT INTO "apaguna" VALUES(78,22,'78:22','Warra daangaa dabraniif iddoo deebiiti.');
INSERT INTO "apaguna" VALUES(78,23,'78:23','Yeroo dheeraaf ishee keessa turu.');
INSERT INTO "apaguna" VALUES(78,24,'78:24','Ishee keessatti qabbanas ta''ee dhugaatii hin dhandhamanu.');
INSERT INTO "apaguna" VALUES(78,25,'78:25','Bishaan akkaan danfaafi malaa malee [dhugaatii biraa hin argatanu]');
INSERT INTO "apaguna" VALUES(78,26,'78:26','Mindaa wal madaalu [mindeeffaman].');
INSERT INTO "apaguna" VALUES(78,27,'78:27','Isaan qormaata hin sodaatanu turan.');
INSERT INTO "apaguna" VALUES(78,28,'78:28','Keeyyattoota keenyas kijibsiisuu kijibsiisan.');
INSERT INTO "apaguna" VALUES(78,29,'78:29','Wanta cufaayyuu haala barreeffameen walitti qabne.');
INSERT INTO "apaguna" VALUES(78,30,'78:30','“Dhandhamaa; azaaba malees isiniif hin daballu” [isaaniin jedhama].');
INSERT INTO "apaguna" VALUES(78,31,'78:31','Dhugumatti, warra Rabbiin sodaataniif milkiitu jira.');
INSERT INTO "apaguna" VALUES(78,32,'78:32','Ashaakiltiifi inabatu [jira].');
INSERT INTO "apaguna" VALUES(78,33,'78:33','Dubartoota harma guntuttuu hiriyaa ta''antu [jiru].');
INSERT INTO "apaguna" VALUES(78,34,'78:34','Geeba (xoofoo) guutamtuu.');
INSERT INTO "apaguna" VALUES(78,35,'78:35','Achi keessatti dubbii badaafi wal kijibsiisuu hin dhaga''anu.');
INSERT INTO "apaguna" VALUES(78,36,'78:36','Mindaa Gooftaa kee irraa ta’e kennaa ga’aa [mindeeffaman].');
INSERT INTO "apaguna" VALUES(78,37,'78:37','Gooftaa samii, dachiifi waan gidduu isaanii jiru, mararfataa kan ta''e [irraa mindeeffaman]Isaa wajjin dubbachuu hin danda''anu.');
INSERT INTO "apaguna" VALUES(78,38,'78:38','Guyyaa Jibriiliifi Maleykonni haala hiriiraniin dhaabbatan nama Rabbiin eeyyameefi dhugaa dubbate malee hin haasawanu.');
INSERT INTO "apaguna" VALUES(78,39,'78:39','Sun guyyaa [dhufaatiin isaa] haqaatiNamni fedhe [hojii gaarii hojjachuun] gara Gooftaa isaatti deebii tolfata.');
INSERT INTO "apaguna" VALUES(78,40,'78:40','Nuti adabbii dhihoo kan guyyaa namni waan harki lamaan isaa dabarfatte arguufi kaafirris "yaa hawwii kiyya! odoon biyyee ta’ee maal qaba" jedhu san isin sodaachifneerra.');
INSERT INTO "apaguna" VALUES(79,1,'79:1','[Maleykaa lubbuu kaafiraa] humnaan buttuunin (kakadha).');
INSERT INTO "apaguna" VALUES(79,2,'79:2','[Kan lubbuu mu’uminaa] luqqisuu suuta luqqiftuunis.');
INSERT INTO "apaguna" VALUES(79,3,'79:3','[Kan ajaja Rabbii geessuuf] adeemuu demaniinis.');
INSERT INTO "apaguna" VALUES(79,4,'79:4','Maleykota] dorgommii wal dorgomaniinis');
INSERT INTO "apaguna" VALUES(79,5,'79:5','(Kan) ajaja Rabbii gara dachii geessaniininis (nan kakadha');
INSERT INTO "apaguna" VALUES(79,6,'79:6','Guyyaa sochootuun [afuuffiin xurumbaa nama] sochooftu;');
INSERT INTO "apaguna" VALUES(79,7,'79:7','Hordoftuutu (afuuffii biraatu) ishee hordofa.');
INSERT INTO "apaguna" VALUES(79,8,'79:8','Onneen gariin guyyaa san rifattuudha.');
INSERT INTO "apaguna" VALUES(79,9,'79:9','Ijji ishee salphattuudha.');
INSERT INTO "apaguna" VALUES(79,10,'79:10','Ni jedhu: "sila nuti dhugumatti haala jireenya duriitti ni deebifamnaa?');
INSERT INTO "apaguna" VALUES(79,11,'79:11','Sila erga lafee dhumte taanee moo?');
INSERT INTO "apaguna" VALUES(79,12,'79:12','Yoo akkas ta''e sun deebii hoongooti" jedhan.');
INSERT INTO "apaguna" VALUES(79,13,'79:13','Isheen iyya (afuuffii) tokko qofa.');
INSERT INTO "apaguna" VALUES(79,14,'79:14','Yeroma san isaan gubbaa dachii irratti ta''u');
INSERT INTO "apaguna" VALUES(79,15,'79:15','Sila haasofni waa’ee Muusaa sitti dhufee?');
INSERT INTO "apaguna" VALUES(79,16,'79:16','Yeroo Gooftaan isaa laga qulqulleffamaa "Xuwaa" jedhamu keessatti isatti lallabe.');
INSERT INTO "apaguna" VALUES(79,17,'79:17','Gara Fir''awn deemi; inni daangaa darbee jira.');
INSERT INTO "apaguna" VALUES(79,18,'79:18','Jedhiin: "sila gara (shirkii irraa) qulqulleffachuutti fedha qabdaa?');
INSERT INTO "apaguna" VALUES(79,19,'79:19','Akka sodaattuuf gara Gooftaa keetii si qajeelchutti (fedha qabdaa)."');
INSERT INTO "apaguna" VALUES(79,20,'79:20','[Muusan] mallattoo guddaa isa (Fir''awniin) agarsiise.');
INSERT INTO "apaguna" VALUES(79,21,'79:21','Inni (Fir’awn) sobsiisee faallesse.');
INSERT INTO "apaguna" VALUES(79,22,'79:22','Ergasii ariifataa gara gale.');
INSERT INTO "apaguna" VALUES(79,23,'79:23','Sana booda walitti qabee lallabe:');
INSERT INTO "apaguna" VALUES(79,24,'79:24','“Ani gooftaa keessan isa ol aanaadha” jedhe.');
INSERT INTO "apaguna" VALUES(79,25,'79:25','Sana booda Rabbiin adabbii [badii] boodaatiifi jalqabaatiifis isa qabe.');
INSERT INTO "apaguna" VALUES(79,26,'79:26','Dugumatti, kana keessa nama Rabbiin sodaatuuf gorsatu jira.');
INSERT INTO "apaguna" VALUES(79,27,'79:27','Sila uumaa keessantu irra jabaata moo samii dha? Inni (Rabbiin) ishee ni ijaare.');
INSERT INTO "apaguna" VALUES(79,28,'79:28','Ijaarsa ishee ol fuudhee, ishee wal qixxeesse.');
INSERT INTO "apaguna" VALUES(79,29,'79:29','Halkan ishee dukkaneessee guyyaa ishee ibse.');
INSERT INTO "apaguna" VALUES(79,30,'79:30','Dachii sana booda sirreesse.');
INSERT INTO "apaguna" VALUES(79,31,'79:31','Ishee irraa bishaan isheetiifi biqiltuu ishee ni baase.');
INSERT INTO "apaguna" VALUES(79,32,'79:32','Gaarreenis dachii irra ni dhaabe.');
INSERT INTO "apaguna" VALUES(79,33,'79:33','Isiniifi beelladoota keessan qanansiisuuf [kana godhe].');
INSERT INTO "apaguna" VALUES(79,34,'79:34','Yeroo balaan guddoon (Qiyaamaan) dhufte,');
INSERT INTO "apaguna" VALUES(79,35,'79:35','Guyyaa namni waan hojjate hunda yaadatu,');
INSERT INTO "apaguna" VALUES(79,36,'79:36','(Guyyaa) ibiddi jahannam namuma arguuf mul''atte,');
INSERT INTO "apaguna" VALUES(79,37,'79:37','Namni daangaa dabree,');
INSERT INTO "apaguna" VALUES(79,38,'79:38','Jireenya addunyaa kanaa filate,');
INSERT INTO "apaguna" VALUES(79,39,'79:39','Dhugumatti, teesson isaa jahannami.');
INSERT INTO "apaguna" VALUES(79,40,'79:40','Namni Gooftaa isaa dura dhaabbachuu sodaatee, lubbuu isaa waan isheen hawwitu irraa dhorge,');
INSERT INTO "apaguna" VALUES(79,41,'79:41','Dhugumatti, jannanni bakka qubannaa isaati.');
INSERT INTO "apaguna" VALUES(79,42,'79:42','[Yaa Muhammad SAW!] Qiyaamaa irraa, yoom argamtii ishee si gaafatu.');
INSERT INTO "apaguna" VALUES(79,43,'79:43','Ati beekumsa ishee irraa maal keessa jirta?');
INSERT INTO "apaguna" VALUES(79,44,'79:44','Beekumsi ishee kan dhumaa garuma Gooftaa keetiiti.');
INSERT INTO "apaguna" VALUES(79,45,'79:45','Dhugumatti, ati akeekkachiisaa nama ishee sodaatuuti.');
INSERT INTO "apaguna" VALUES(79,46,'79:46','Isaan guyyaa ishee argan waan galgala tokko yookiin ganama tokko malee (addunyaa irra) hin turin itti fakkaata');
INSERT INTO "apaguna" VALUES(80,1,'80:1','Fuula guuree gara gale');
INSERT INTO "apaguna" VALUES(80,2,'80:2','Sababa jaamaan isatti dhufeef.');
INSERT INTO "apaguna" VALUES(80,3,'80:3','Maaltu si beeksise? Qulqullaa’uun isaa ni kajeelama');
INSERT INTO "apaguna" VALUES(80,4,'80:4','Yookiin gorfameeti gorsi isa fayyaddi.');
INSERT INTO "apaguna" VALUES(80,5,'80:5','Nama duroome immoo,');
INSERT INTO "apaguna" VALUES(80,6,'80:6','Ati [akka amananuuf jecha] gara isaa gara galta.');
INSERT INTO "apaguna" VALUES(80,7,'80:7','Haala qulqullaa''uu dhiisuun isaa homaa sirra hin jirreen.');
INSERT INTO "apaguna" VALUES(80,8,'80:8','Nama fiigaa sitti dhufe immoo,');
INSERT INTO "apaguna" VALUES(80,9,'80:9','Kan Rabbiin sodaatu ta’ee,');
INSERT INTO "apaguna" VALUES(80,10,'80:10','Ati isa irraa dagatta.');
INSERT INTO "apaguna" VALUES(80,11,'80:11','Dhorgami! Isheen gorsa.');
INSERT INTO "apaguna" VALUES(80,12,'80:12','Nama fedhetu isa (Qur''aana) yaadata.');
INSERT INTO "apaguna" VALUES(80,13,'80:13','Galmeewwan kabajamtuu keessa jira.');
INSERT INTO "apaguna" VALUES(80,14,'80:14','Ol fuudhamtuu, qulqulleeffamtuu (keessa jira).');
INSERT INTO "apaguna" VALUES(80,15,'80:15','Harka barressitootaa (maleykotaa) jira.');
INSERT INTO "apaguna" VALUES(80,16,'80:16','Kabajamoo, ajajamtoota (harka jira).');
INSERT INTO "apaguna" VALUES(80,17,'80:17','Namni abaarame! Sila maaltu isa kafarsiise?');
INSERT INTO "apaguna" VALUES(80,18,'80:18','(Rabbiin) waan akkamii irraa isa uume? (hin xiinxaluu?)');
INSERT INTO "apaguna" VALUES(80,19,'80:19','Bishaan saalaa irraa isa uumeeti, ergasii isa sirreesse.');
INSERT INTO "apaguna" VALUES(80,20,'80:20','Ergasii karaa (inni ittiin dhalatu) isaaf laaffise.');
INSERT INTO "apaguna" VALUES(80,21,'80:21','Ergasii isa ajjeeseti isa awwaalchise.');
INSERT INTO "apaguna" VALUES(80,22,'80:22','Ergasii yeroo fedhe isa kaasa.');
INSERT INTO "apaguna" VALUES(80,23,'80:23','Dhugumatti, (namni) waan Rabbiin isa ajaje ammallee hin raawwanne.');
INSERT INTO "apaguna" VALUES(80,24,'80:24','Namni gara nyaata isaa haa ilaalu.');
INSERT INTO "apaguna" VALUES(80,25,'80:25','Nuti bishaan buusiinsa baay’inaan buufne.');
INSERT INTO "apaguna" VALUES(80,26,'80:26','Ergasii dachii dhodhoosiinsa dhodhoofne.');
INSERT INTO "apaguna" VALUES(80,27,'80:27',' Achumaan Ishee keessatti firii magarsine.');
INSERT INTO "apaguna" VALUES(80,28,'80:28','Inabaafi margas,');
INSERT INTO "apaguna" VALUES(80,29,'80:29','Zeytunaafi muka timiraas,');
INSERT INTO "apaguna" VALUES(80,30,'80:30','Ashaakiltiilee muka baay’atanis,');
INSERT INTO "apaguna" VALUES(80,31,'80:31','Fuduraafi kaloo [akka nuti uumne haa ilaalu].');
INSERT INTO "apaguna" VALUES(80,32,'80:32','Isiniifi beylada keessanis qanansiisuuf.');
INSERT INTO "apaguna" VALUES(80,33,'80:33','Yeroo gurra duuchituun (Qiyaamaan)dhufte,');
INSERT INTO "apaguna" VALUES(80,34,'80:34','Guyyaa namni obboleessa isaa irraa dheessu,');
INSERT INTO "apaguna" VALUES(80,35,'80:35','Haadha isaatiifi abbaa isaa irraas,');
INSERT INTO "apaguna" VALUES(80,36,'80:36','Niitii isaatiifi ijoollee isaa irraas (baqatu san dhufti).');
INSERT INTO "apaguna" VALUES(80,37,'80:37','Nama Isaan irraa ta’e hundaaf guyyaa sana dhimma isa duroomsutu jira.');
INSERT INTO "apaguna" VALUES(80,38,'80:38','Fuulli gariin guyyaa san ni ifti.');
INSERT INTO "apaguna" VALUES(80,39,'80:39','Kolfituu, gammadduudha.');
INSERT INTO "apaguna" VALUES(80,40,'80:40','Fuulli gariin immoo guyyaa san dhukkeetu (quttootu) irra jira.');
INSERT INTO "apaguna" VALUES(80,41,'80:41','Gurraachinni ishee haguuga.');
INSERT INTO "apaguna" VALUES(80,42,'80:42','Isaan sun warra Rabbitti kafaranii, balleessa hojjataniidha.');
INSERT INTO "apaguna" VALUES(81,1,'81:1','Yeroo aduun maramte,');
INSERT INTO "apaguna" VALUES(81,2,'81:2','Yeroo urjiin harcaate,');
INSERT INTO "apaguna" VALUES(81,3,'81:3','Yeroo gaarreen bakkaa oofamte,');
INSERT INTO "apaguna" VALUES(81,4,'81:4','Yeroo gaalli rimaan baatii kudhanii dhiifamte (tiksitee malee hafte),');
INSERT INTO "apaguna" VALUES(81,5,'81:5','Yeroo bineensonni bosonaa walitti qabamte,');
INSERT INTO "apaguna" VALUES(81,6,'81:6','Yeroo galaanni qabsiifamte (akka ibiddaatti boba''an),');
INSERT INTO "apaguna" VALUES(81,7,'81:7','Yeroo lubbuun hundi qindeeffamte');
INSERT INTO "apaguna" VALUES(81,8,'81:8','Yeroo durbi fayyaan awwaalamte gaafatamte,');
INSERT INTO "apaguna" VALUES(81,9,'81:9','Balleessa kamiif akka ajjeefamte;');
INSERT INTO "apaguna" VALUES(81,10,'81:10','Yeroo galmeen (hojii) raabsamte,');
INSERT INTO "apaguna" VALUES(81,11,'81:11','Yeroo samiin buqqifamtee maramte,');
INSERT INTO "apaguna" VALUES(81,12,'81:12','Yeroo ibiddi qabsiifamte,');
INSERT INTO "apaguna" VALUES(81,13,'81:13','Yeroo jannanni dhiheeffamte,');
INSERT INTO "apaguna" VALUES(81,14,'81:14','Lubbuun waan dalagatte beekteetti.');
INSERT INTO "apaguna" VALUES(81,15,'81:15','(Urjiiwwan) guyyaa badanii halkan mul''ataniinin kakadha.');
INSERT INTO "apaguna" VALUES(81,16,'81:16','(Urjiiwwan) yaa’aa seenaa ta’aniinin (kakadha).');
INSERT INTO "apaguna" VALUES(81,17,'81:17','Halkaniinis yeroo inni dukkanaa’en (kakadha),');
INSERT INTO "apaguna" VALUES(81,18,'81:18','Bariittillee yeroo inni diriiren (kakadha),');
INSERT INTO "apaguna" VALUES(81,19,'81:19','Dhugumatti, inni (Qur''aanni) kun jecha Ergamaa kabajamaati');
INSERT INTO "apaguna" VALUES(81,20,'81:20','(Ergamaa) abbaa humnaa ta''e, kan Gooftaa Arshii biratti iddoo jabaa qabu.');
INSERT INTO "apaguna" VALUES(81,21,'81:21','Bakka sanatti ajajni isaa fudhatamaafi amanamaa kan ta''e.');
INSERT INTO "apaguna" VALUES(81,22,'81:22','Saahibni keessan (Muhammad SAW!) maraataa miti.');
INSERT INTO "apaguna" VALUES(81,23,'81:23','Dhugumatti, inni moggaa ol aanaa (samii) ifa galaa (gara Bahaa) keessatti isa argee jira.');
INSERT INTO "apaguna" VALUES(81,24,'81:24','Inni (Nabiyyiin) beekumsa fagoo irratti don''aa miti (hin dhoksu).');
INSERT INTO "apaguna" VALUES(81,25,'81:25','Innis (Qur''aanni) jecha sheeyxaana darbamaa miti.');
INSERT INTO "apaguna" VALUES(81,26,'81:26','Mee eessuma deemtu?');
INSERT INTO "apaguna" VALUES(81,27,'81:27','Inni aalama hundaaf gorsa malee homaa miti.');
INSERT INTO "apaguna" VALUES(81,28,'81:28','Nama isin irraa qajeeluma fedheef (gorsa ta''a).');
INSERT INTO "apaguna" VALUES(81,29,'81:29','Yoo Rabbiin aalama hundaa fedhe malee isin homaa hin feetanu');
INSERT INTO "apaguna" VALUES(82,1,'82:1','Yeroo samiin dhodhoote,');
INSERT INTO "apaguna" VALUES(82,2,'82:2','Yeroo urjiiwwan harca''an,');
INSERT INTO "apaguna" VALUES(82,3,'82:3','Yeroo galaanonni walitti makaman,');
INSERT INTO "apaguna" VALUES(82,4,'82:4','Yeroo qabroonni gara gaggalfamtee du’aa ishee keessa jiru ol baafte,');
INSERT INTO "apaguna" VALUES(82,5,'82:5','(Yeroo san) lubbuun hundi waan dabarfatte (hojjattee)fi dhiifte ni beekti');
INSERT INTO "apaguna" VALUES(82,6,'82:6','Yaa namootaa! Gooftaa kee kan Arjaa ta''etti (amanuu irraa) maaltu si gowwoomse?');
INSERT INTO "apaguna" VALUES(82,7,'82:7','Isa si uumee, ergasii si guutee, si sirreesse,');
INSERT INTO "apaguna" VALUES(82,8,'82:8','Boca fedhe keessatti walitti si qabe (sanarraa maaltu si gowwoomse)?');
INSERT INTO "apaguna" VALUES(82,9,'82:9','Dhorgamaa! Dhugumatti, guyyaa murtii ni sobsiiftu.');
INSERT INTO "apaguna" VALUES(82,10,'82:10','Dhugumatti, isin irra to’attootatu jiru.');
INSERT INTO "apaguna" VALUES(82,11,'82:11','Kabajamoo, barreessitoota kan ta’an.');
INSERT INTO "apaguna" VALUES(82,12,'82:12','Waan isin dalagdan ni beeku.');
INSERT INTO "apaguna" VALUES(82,13,'82:13','Dhugumatti, warri toltuu hojjatan qananii keessa jiraatu');
INSERT INTO "apaguna" VALUES(82,14,'82:14','Warri hamtuu hojjatan immoo ibidda keessa jiraatu.');
INSERT INTO "apaguna" VALUES(82,15,'82:15','Guyyaa murtii ishee seenu.');
INSERT INTO "apaguna" VALUES(82,16,'82:16','Isaan ishee irraa hin fagaatanu.');
INSERT INTO "apaguna" VALUES(82,17,'82:17','Sila guyyaan murtii maal akka ta''e maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(82,18,'82:18','Ergasii guyyaan murtii maal akka ta''e maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(82,19,'82:19','(Inni) guyyaa lubbuun tokko lubbuu biraatiif homaa gochuu hin dandeenyeGuyyaa san ajajni (hundi) kan Rabbiiti.');
INSERT INTO "apaguna" VALUES(83,1,'83:1','Warra (safara) hir’isuuf badiin haa ta’u!');
INSERT INTO "apaguna" VALUES(83,2,'83:2','Isaan yeroo namoota irraa safaratan guuchisiifatan,');
INSERT INTO "apaguna" VALUES(83,3,'83:3','Yeroo namootaaf safaran yookiin madaalan immoo hir’isan.');
INSERT INTO "apaguna" VALUES(83,4,'83:4','Sila isaan sun kaafamuu isaanii hin yaadanii?');
INSERT INTO "apaguna" VALUES(83,5,'83:5','Guyyaa guddaadhaaf.');
INSERT INTO "apaguna" VALUES(83,6,'83:6','Guyyaa namni Gooftaa aalamaatiif dhaabbatu.');
INSERT INTO "apaguna" VALUES(83,7,'83:7','Dhugumatti, galmeen badii hojjattootaa Sijjiin keessa ta''a.');
INSERT INTO "apaguna" VALUES(83,8,'83:8','"Sijjiin" maal akka ta''e maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(83,9,'83:9','Kitaaba barreeffamaadha (dalagaa yakkamtootaatu irratti barreeffama).');
INSERT INTO "apaguna" VALUES(83,10,'83:10','Kijibsiiftotaaf Guyyaa san badiin mirkanaa’e.');
INSERT INTO "apaguna" VALUES(83,11,'83:11','Isaan guyyaa murtii sobsiisaniif.');
INSERT INTO "apaguna" VALUES(83,12,'83:12','Daangaa darbitoota dilaawoo cufa ta’e malee namni isa kijibsiisu hin jiru.');
INSERT INTO "apaguna" VALUES(83,13,'83:13','Yeroo keeyyattoonni Keenya isaan irratti qara''amte"ogbarruu warra duriiti" jedha.');
INSERT INTO "apaguna" VALUES(83,14,'83:14','Haa dhorgamanu! Dhugumatti wanti isaan hojjataa turan onnee isaanii haguuge.');
INSERT INTO "apaguna" VALUES(83,15,'83:15','Dhagayaa! Isaan guyyaa san Gooftaa isaanii irraa haguugamoodha.');
INSERT INTO "apaguna" VALUES(83,16,'83:16','Ergasii isaan ibidda galtoota.');
INSERT INTO "apaguna" VALUES(83,17,'83:17','Ergasii "inni kun isa isin sobsiisaa turtan" jedhama.');
INSERT INTO "apaguna" VALUES(83,18,'83:18','Dhugumatti, galmeen toltuu hojjattootaa Illiyyiin keessa ta''a.');
INSERT INTO "apaguna" VALUES(83,19,'83:19','"Illiyyiin" maal akka ta''e maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(83,20,'83:20','Kitaaba barreeffamaadha (hojii warra Rabbitti amaneetu irratti barreeffame).');
INSERT INTO "apaguna" VALUES(83,21,'83:21','Warra Rabbitti dhiheeffamantu itti hirmaata.');
INSERT INTO "apaguna" VALUES(83,22,'83:22','Dhugumatti, toltuu hojjattoonni qananii keessa jiraatu.');
INSERT INTO "apaguna" VALUES(83,23,'83:23','Sireewwan irra (taa''anii) ilaalu.');
INSERT INTO "apaguna" VALUES(83,24,'83:24','Fuula isaanii keessatti ifina qananii ni beekta.');
INSERT INTO "apaguna" VALUES(83,25,'83:25','Farsoo qadaadamaa irraa obaafamu.');
INSERT INTO "apaguna" VALUES(83,26,'83:26','Dhumti isaa miskiidha; sana keessatti warri wal dorgoman wal haa dorgoman.');
INSERT INTO "apaguna" VALUES(83,27,'83:27','Makaan isaas ‘Tasniim’ irraayi.');
INSERT INTO "apaguna" VALUES(83,28,'83:28','(Tasniim) laga warri Rabbitti dhiheeffaman dhuganiidha.');
INSERT INTO "apaguna" VALUES(83,29,'83:29','Dhugumatti, isaan dilaawan isaan amananitti kolfaa turan.');
INSERT INTO "apaguna" VALUES(83,30,'83:30','Yeroo isaan bira dabran wal akeeku.');
INSERT INTO "apaguna" VALUES(83,31,'83:31','Yeroo gara maatii isaanii deebi''anis gammadaa deebi’u.');
INSERT INTO "apaguna" VALUES(83,32,'83:32','Yeroo isaan arganis "dhugumatti, isaan kun jallattoota" jedhu.');
INSERT INTO "apaguna" VALUES(83,33,'83:33','Haala to''attoota ta''anii isaan irratti hin ergaminiin.');
INSERT INTO "apaguna" VALUES(83,34,'83:34','Har’a (guyyaa kaafamiinsaa) isaan amanantu kaafirootatti kolfa.');
INSERT INTO "apaguna" VALUES(83,35,'83:35','Sireewwan irra (ta''anii) ilaalu.');
INSERT INTO "apaguna" VALUES(83,36,'83:36','"Sila kaafiroonni waan dalagaa turan mindeffamaniiruu?” [jedhu]');
INSERT INTO "apaguna" VALUES(84,1,'84:1','Yeroo samiin dhodhoote');
INSERT INTO "apaguna" VALUES(84,2,'84:2','(Yeroo isheen ajaja) Gooftaa ishee dhaggeeffatte; (wanti kana godhu waan itti argameef) dhugaa qabdi.');
INSERT INTO "apaguna" VALUES(84,3,'84:3','Yeroo dachiinis diriirfamte,');
INSERT INTO "apaguna" VALUES(84,4,'84:4','Waan ishee keessa jiru darbitee, duwwaas taate,');
INSERT INTO "apaguna" VALUES(84,5,'84:5','(Yeroo isheen ajaja) Gooftaa ishee dhaggeeffatte; (wanti kana godhu waan itti argameef) dhugaa qabdi.');
INSERT INTO "apaguna" VALUES(84,6,'84:6','Yaa namootaa! Dhugumatti, ati gara Rabbii keetii (dhaqqabuuf) hojiin qabsaa’aadhaa, isa (hojii kee) qunnamaadhas');
INSERT INTO "apaguna" VALUES(84,7,'84:7','Namni galmee isaa mirga isaatiin kenname;');
INSERT INTO "apaguna" VALUES(84,8,'84:8','Qormaata salphaa qoramuuf jira.');
INSERT INTO "apaguna" VALUES(84,9,'84:9','Gammadaa gara maatii isaa deebi’a.');
INSERT INTO "apaguna" VALUES(84,10,'84:10','Namni galmee isaa dugda duubaan kenname immoo;');
INSERT INTO "apaguna" VALUES(84,11,'84:11','Badii kadhachuuf jira.');
INSERT INTO "apaguna" VALUES(84,12,'84:12','Ibidda boba’aa seena.');
INSERT INTO "apaguna" VALUES(84,13,'84:13','Dhugumatti, inni maatii isaa keessatti gammadaa ture.');
INSERT INTO "apaguna" VALUES(84,14,'84:14','Dhugamatti, inni (gama Rabbii) deebi’uu dhabuu yaade.');
INSERT INTO "apaguna" VALUES(84,15,'84:15','Lakki! (ni deebi’a)Gooftaan isaa isa argaadha.');
INSERT INTO "apaguna" VALUES(84,16,'84:16','Haa dhorgamu! Diimina galgalaattin kakadha.');
INSERT INTO "apaguna" VALUES(84,17,'84:17','Halkaniifi waan inni walitti qabeenis (nan kakadha).');
INSERT INTO "apaguna" VALUES(84,18,'84:18','Ji''attis yeroo inni guuteenin (kakadha).');
INSERT INTO "apaguna" VALUES(84,19,'84:19','Dhugumatti, isin sadarkaa tokko irraa gara sadarkaa biraatti ceetu.');
INSERT INTO "apaguna" VALUES(84,20,'84:20','Maaltu isaaniif jiraayi hin amanne?');
INSERT INTO "apaguna" VALUES(84,21,'84:21','Yeroo Qur''aanni isaan irratti qara''amus sujuuda hin bu''an.');
INSERT INTO "apaguna" VALUES(84,22,'84:22','Dhugumaatti, warri kafaran ni sobsiisu.');
INSERT INTO "apaguna" VALUES(84,23,'84:23','Rabbiin irra beekaa waan isaan (onnee isaanii keessatti) walitti qabaniiti.');
INSERT INTO "apaguna" VALUES(84,24,'84:24','Adaba laalessaadhaan isaan gammachiisi.');
INSERT INTO "apaguna" VALUES(84,25,'84:25','Garuu isaan amananii gaggaarii hojjatan isaaniif mindaa hin cinnetu jira');
INSERT INTO "apaguna" VALUES(85,1,'85:1','Samii abbaa orbitii urjiitiin (nan kakadha).');
INSERT INTO "apaguna" VALUES(85,2,'85:2','Guyyaa beellamamaanis,');
INSERT INTO "apaguna" VALUES(85,3,'85:3','Ragaa ba''aafi warra ragaan itti ba''amuunis (nan kakadhaa),');
INSERT INTO "apaguna" VALUES(85,4,'85:4','Warri boollaa abaaraman');
INSERT INTO "apaguna" VALUES(85,5,'85:5','(Warri) ibidda boba’aa.');
INSERT INTO "apaguna" VALUES(85,6,'85:6','Yeroo isaan ishee irra taa''an,');
INSERT INTO "apaguna" VALUES(85,7,'85:7','Isaan waan mu''imintootatti hojjatan irratti hirmaattota.');
INSERT INTO "apaguna" VALUES(85,8,'85:8','Rabbii injifataa, faarfamaatti amanuu malee homaa isaan irraa haaloo hin ba’anne.');
INSERT INTO "apaguna" VALUES(85,9,'85:9','(Rabbiin) Isa mootummaan samiifi dachii Isaaf ta''eedhaRabbiin waan hunda irratti ragaadha.');
INSERT INTO "apaguna" VALUES(85,10,'85:10','Dhugumatti, isaan mu''imintoota dhiiraafi dubartootaa rakkisanii ergasii gara Rabbii isaanii kan hin deebi''Islaamni (hin tawbatin) isaaniif adabbii jahannamtu jira; ammas isaaniif adabbii gubaatu jira.');
INSERT INTO "apaguna" VALUES(85,11,'85:11','Dhugumatti, isaan amananii gaggaarii hojjatan, isaaniif ashaakiltiiwwan laggeen jala ishee yaatutu jiraSun milkii guddaadha.');
INSERT INTO "apaguna" VALUES(85,12,'85:12','Dhugumatti, qabaan Gooftaa keetii daran cimaadha.');
INSERT INTO "apaguna" VALUES(85,13,'85:13','Inni Isumatu [uumama] jalqaba; (du''a boodas) ni deebisa.');
INSERT INTO "apaguna" VALUES(85,14,'85:14','Inni akkaan araaramaa, akkaan jaalataadha');
INSERT INTO "apaguna" VALUES(85,15,'85:15','(Inni) abbaa Arshiiti; guddaadha.');
INSERT INTO "apaguna" VALUES(85,16,'85:16','Waan fedhe akkaan hojjataadha.');
INSERT INTO "apaguna" VALUES(85,17,'85:17','Sila oduun humna waraanaa si ga''eeraa?');
INSERT INTO "apaguna" VALUES(85,18,'85:18','(Waraana) Fir''awniifi Samuud.');
INSERT INTO "apaguna" VALUES(85,19,'85:19','Dhugumatti, isaan kafaran kijibsiisuu keessa jiru.');
INSERT INTO "apaguna" VALUES(85,20,'85:20','Rabbiin immoo duuba isaanii irraan marsaadha.');
INSERT INTO "apaguna" VALUES(85,21,'85:21','Dhugumatti, inni Qur''aana guddaadha.');
INSERT INTO "apaguna" VALUES(85,22,'85:22','Luuhii tiikfamaa keessa jira.');
INSERT INTO "apaguna" VALUES(86,1,'86:1','Samiifi (urjii) halkan dhufaa ta’eenin (kakadha).');
INSERT INTO "apaguna" VALUES(86,2,'86:2','(Urjii) halkan dhufaa ta’e maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(86,3,'86:3','(Inni) urjii ifti isaa dukkana uraa ta’eedha.');
INSERT INTO "apaguna" VALUES(86,4,'86:4','Lubbuun hundi tiksaan ishee irra jiraatu malee hin hafu.');
INSERT INTO "apaguna" VALUES(86,5,'86:5','Namni maal irraa akka uumame haa ilaalu.');
INSERT INTO "apaguna" VALUES(86,6,'86:6','Bishaan utaalaa irraa uumame.');
INSERT INTO "apaguna" VALUES(86,7,'86:7','Jidduu dugdaatiifi qomaatii kan ba’u.');
INSERT INTO "apaguna" VALUES(86,8,'86:8','Inni isa deebisuu irratti danda’aadha.');
INSERT INTO "apaguna" VALUES(86,9,'86:9','Guyyaa iccitiiwwan ifa ba''an.');
INSERT INTO "apaguna" VALUES(86,10,'86:10','(Gaafas) humnas ta’ee tumsaan isaaf hin jiru.');
INSERT INTO "apaguna" VALUES(86,11,'86:11','Samii abbaa deddeebiitiinin (kakadha).');
INSERT INTO "apaguna" VALUES(86,12,'86:12','Dachii dhodhootuunin (kakadha).');
INSERT INTO "apaguna" VALUES(86,13,'86:13','Dhugumatti, inni (Qur''aanni) jecha adda baasaadha.');
INSERT INTO "apaguna" VALUES(86,14,'86:14','Inni qoosaa hin taane.');
INSERT INTO "apaguna" VALUES(86,15,'86:15','Dhugumatti, isaan tooftaa baasu.');
INSERT INTO "apaguna" VALUES(86,16,'86:16','Anis tooftaan baasa.');
INSERT INTO "apaguna" VALUES(86,17,'86:17','Kaafirootaaf yeroo kenniAmmas xinnoo yeroo kenniif');
INSERT INTO "apaguna" VALUES(87,1,'87:1','Maqaa Gooftaa kee olaanaa ta''ee qulqulleessi.');
INSERT INTO "apaguna" VALUES(87,2,'87:2','Isa (waan hunda) uumee guute.');
INSERT INTO "apaguna" VALUES(87,3,'87:3','Isa murteessee qajeelche.');
INSERT INTO "apaguna" VALUES(87,4,'87:4','Isa marga baase,');
INSERT INTO "apaguna" VALUES(87,5,'87:5','Goggogaa gurraacha isa taasise.');
INSERT INTO "apaguna" VALUES(87,6,'87:6','Si qaraasisuuf jirraa hin dagattu');
INSERT INTO "apaguna" VALUES(87,7,'87:7','Waan Rabbiin fedhe maleeInni mul’ataafi dhokataas ni beeka.');
INSERT INTO "apaguna" VALUES(87,8,'87:8','Nuti karaa laaftuu taateef si laaffifna');
INSERT INTO "apaguna" VALUES(87,9,'87:9','Yoo yaadachiisni kan fayyadu ta’e yaadachiisi.');
INSERT INTO "apaguna" VALUES(87,10,'87:10','Namni Rabbiin sodaatu gorfamuuf jira.');
INSERT INTO "apaguna" VALUES(87,11,'87:11','Hoonga''aan ishee irraa fagaata.');
INSERT INTO "apaguna" VALUES(87,12,'87:12','Inni ibidda guddoo seenu.');
INSERT INTO "apaguna" VALUES(87,13,'87:13','Ergasii ishee keessatti hin du''u; hin jiraatus.');
INSERT INTO "apaguna" VALUES(87,14,'87:14','Dhugumatti, namni of qulqulleeffate milkaa’e.');
INSERT INTO "apaguna" VALUES(87,15,'87:15','Maqaa Gooftaa isaa dubbatee kan salaate.');
INSERT INTO "apaguna" VALUES(87,16,'87:16','Inumaa isin jiruu addunyaa filattu.');
INSERT INTO "apaguna" VALUES(87,17,'87:17','Odoo Aakhiraan caaltuufi haftuu taatee jirtuu [addunyaa filattu].');
INSERT INTO "apaguna" VALUES(87,18,'87:18','Dhugumatti, kun barreeffamoota duraanii keessa jira.');
INSERT INTO "apaguna" VALUES(87,19,'87:19','Barreeffamoota Ibraahiimifi Muusaa [keessa jira]');
INSERT INTO "apaguna" VALUES(88,1,'88:1','Sila oduun haguugduu[] (Qiyaamaa) sitti dhufeeraa?');
INSERT INTO "apaguna" VALUES(88,2,'88:2','Fuulli gariin guyyaa san xiqqaattuudha.');
INSERT INTO "apaguna" VALUES(88,3,'88:3','Hojjattuu ifaajjuudha');
INSERT INTO "apaguna" VALUES(88,4,'88:4','Ibidda gubduu seenti.');
INSERT INTO "apaguna" VALUES(88,5,'88:5','Burqituu danfituu irraa obaafamti.');
INSERT INTO "apaguna" VALUES(88,6,'88:6','Adaamii malee nyaanni isaaniif hin jiru.');
INSERT INTO "apaguna" VALUES(88,7,'88:7','Hin gabbisu; beela irraas hin duroomsu.');
INSERT INTO "apaguna" VALUES(88,8,'88:8','Fuulli gariin guyyaa san qananiituudha.');
INSERT INTO "apaguna" VALUES(88,9,'88:9','Dalagaa ishee jaalattuudha');
INSERT INTO "apaguna" VALUES(88,10,'88:10','Jannata ol fuudhamtuu keessa jiraatti.');
INSERT INTO "apaguna" VALUES(88,11,'88:11','Ishee keessatti waca hin dhaga''anu.');
INSERT INTO "apaguna" VALUES(88,12,'88:12','Ishee keessa burqituu yaatutu jirti.');
INSERT INTO "apaguna" VALUES(88,13,'88:13','Ishee keessa sireewwan ol fuudhamtuutu jirti.');
INSERT INTO "apaguna" VALUES(88,14,'88:14','Kubbaayyaawwan kaa’amtuutu jirti.');
INSERT INTO "apaguna" VALUES(88,15,'88:15','Boraatiiwwan tarree galfamantu (isaaniif jira).');
INSERT INTO "apaguna" VALUES(88,16,'88:16','Afaawwan diriirfamanis (isaaniif jirti).');
INSERT INTO "apaguna" VALUES(88,17,'88:17','Sila gara gaalaa akkamitti akka uumamte hin ilaalanuu?');
INSERT INTO "apaguna" VALUES(88,18,'88:18','Gara samii akkamitti akka ol fuudhamte (hin ilaalanuu)?');
INSERT INTO "apaguna" VALUES(88,19,'88:19','Gara gaarreenis akkamitti akka dhaabamte (hin ilaalanuu)?');
INSERT INTO "apaguna" VALUES(88,20,'88:20','Gara dachiis akkamitti akka diriirfamte (hin ilaalanuu)?');
INSERT INTO "apaguna" VALUES(88,21,'88:21','Yaadachiisi; ati yaadachiisaa qofa.');
INSERT INTO "apaguna" VALUES(88,22,'88:22','Isaan irratti dirqisiisaa hin taane.');
INSERT INTO "apaguna" VALUES(88,23,'88:23','Garuu nama gara galee kafare,');
INSERT INTO "apaguna" VALUES(88,24,'88:24','Rabbiin adabbii guddaa isa adaba');
INSERT INTO "apaguna" VALUES(88,25,'88:25','Deebiin isaanii garuma Keenya.');
INSERT INTO "apaguna" VALUES(88,26,'88:26','Ergasii qormaanni isaanii Nurra jira');
INSERT INTO "apaguna" VALUES(89,1,'89:1','Bariinin[] (kakadha).');
INSERT INTO "apaguna" VALUES(89,2,'89:2','Halkan kurnaniini,');
INSERT INTO "apaguna" VALUES(89,3,'89:3','Cimdiifi qarattis;');
INSERT INTO "apaguna" VALUES(89,4,'89:4','Halkanittis; yeroo inni deeme.');
INSERT INTO "apaguna" VALUES(89,5,'89:5','Sila sana keessa abbootii sammuutiif kakuun quubsaan hin jiruu? (ni jira)!');
INSERT INTO "apaguna" VALUES(89,6,'89:6','Sila akkam akka Gooftaan kee Aaditti hojjate hin arginee?');
INSERT INTO "apaguna" VALUES(89,7,'89:7','Iramiin warra dhedheerootti,');
INSERT INTO "apaguna" VALUES(89,8,'89:8','Ishee fakkiin ishee biyyoota keessatti hin uumamin san.');
INSERT INTO "apaguna" VALUES(89,9,'89:9','Samuudittis warra laga (Hijri) keessatti dhagaa uran (mana irraa tolfatan).');
INSERT INTO "apaguna" VALUES(89,10,'89:10','Fir''awniinis abbaa shikaalaatti;');
INSERT INTO "apaguna" VALUES(89,11,'89:11','Isaan biyyoota keessatti daangaa dabran.');
INSERT INTO "apaguna" VALUES(89,12,'89:12','Kan ishee keessatti badii baay''isan.');
INSERT INTO "apaguna" VALUES(89,13,'89:13','Gooftaan kee akaakuu adabbii isaan irratti buuse.');
INSERT INTO "apaguna" VALUES(89,14,'89:14','Dhugumatti, Gooftaan kee qeexaadha (beekaadha).');
INSERT INTO "apaguna" VALUES(89,15,'89:15','Namni yeroo gooftaan isaa isa kabajuun qanansiisee isa qorate “Gooftaan kiyya na kabaje” jedha.');
INSERT INTO "apaguna" VALUES(89,16,'89:16','Yeroo inni hiree isaa itti xiqqeessee isa qorate immoo “Gooftaan kiyya na xiqqeesse” jedha.');
INSERT INTO "apaguna" VALUES(89,17,'89:17','Haa dhorgamu! Dhugumatti, isin yatiima hin kabajjan.');
INSERT INTO "apaguna" VALUES(89,18,'89:18','Hiyyeessa nyaachisuu irrattis hin kakaaftan.');
INSERT INTO "apaguna" VALUES(89,19,'89:19','Qabeenya dhaalaa, nyaata [halaalaafi haraama] walitti qabate nyaattu.');
INSERT INTO "apaguna" VALUES(89,20,'89:20','Jaalala baay’ee qabeenya jaalattu.');
INSERT INTO "apaguna" VALUES(89,21,'89:21','Dhorgamaa! Yeroo dachiin waliin dhawamiinsa jabaa waliin dhawamte.');
INSERT INTO "apaguna" VALUES(89,22,'89:22','(Yeroo) Gooftaan kee dhufee maleykonnis hiriira hiriiran [dhufan].');
INSERT INTO "apaguna" VALUES(89,23,'89:23','Guyyaa san jahannamis ni fidamaGuyyaa san namni ni yaadataYaadannoon (guyyaa sanii) maal isaaf godha ree?');
INSERT INTO "apaguna" VALUES(89,24,'89:24','Ni jedha: "odoon jiruu kiyyaaf dabarfadheen hawwa."');
INSERT INTO "apaguna" VALUES(89,25,'89:25','Guyyaa san adabbii Isaa eenyunuu hin adabu.');
INSERT INTO "apaguna" VALUES(89,26,'89:26','Hidhaa Isaas eenyunuu hin hidhu.');
INSERT INTO "apaguna" VALUES(89,27,'89:27','“Yaa lubbuu tasgabbooftuu![]');
INSERT INTO "apaguna" VALUES(89,28,'89:28','Jaallataa, jaallatamaa gara Gooftaa keetii deebi''i.');
INSERT INTO "apaguna" VALUES(89,29,'89:29','Gabroota kiyya keessas seeni.');
INSERT INTO "apaguna" VALUES(89,30,'89:30','Jannata kiyyas seeni” (jedhamti)');
INSERT INTO "apaguna" VALUES(90,1,'90:1','Biyya (Makkaa) kanaanin kakadha.');
INSERT INTO "apaguna" VALUES(90,2,'90:2','Ati biyyi kun (lolaan banuun) siif eeyyamamaadha.');
INSERT INTO "apaguna" VALUES(90,3,'90:3','Abbaafi waan inni dhaleenis (nan kakadha).');
INSERT INTO "apaguna" VALUES(90,4,'90:4','Dhugumatti, Nuti nama cinqii[] keessatti uumne.');
INSERT INTO "apaguna" VALUES(90,5,'90:5','Sila inni tokkolleen[] isa irratti hin danda''uu yaadaa?');
INSERT INTO "apaguna" VALUES(90,6,'90:6','“Qabeenya guddaan balleesse" jedha.');
INSERT INTO "apaguna" VALUES(90,7,'90:7','Sila inni tokkolleen isa hin arguu yaadaa?');
INSERT INTO "apaguna" VALUES(90,8,'90:8','Sila Nuti ija lama isaaf hin taasifnee?');
INSERT INTO "apaguna" VALUES(90,9,'90:9','Arrabaafi hidhii lamas;');
INSERT INTO "apaguna" VALUES(90,10,'90:10','Karaa lama (gaariifi badaa) isaaf ibsine.');
INSERT INTO "apaguna" VALUES(90,11,'90:11','Sila maaliif tulluu hin dabrine?[]');
INSERT INTO "apaguna" VALUES(90,12,'90:12','Tulluttiin maal akka taate maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(90,13,'90:13','(Inni) Gabra bilisoomsuudha');
INSERT INTO "apaguna" VALUES(90,14,'90:14','Yookiin guyyaa beelaa keessa nyaachisuudha.');
INSERT INTO "apaguna" VALUES(90,15,'90:15','Yatiima fira ta’an;');
INSERT INTO "apaguna" VALUES(90,16,'90:16','Yookiin hiyyeessa harkaan lafa qabate (deege).');
INSERT INTO "apaguna" VALUES(90,17,'90:17','Ergasii warra amananii, obsa waliif dhaamaniifi rahmata waliif dhaaman irraa maaliif hin taane?');
INSERT INTO "apaguna" VALUES(90,18,'90:18','Isaan sun warra mirgaati.');
INSERT INTO "apaguna" VALUES(90,19,'90:19','Isaan keeyyattoota keenyatti kafaran immoo isaan warra bitaati.');
INSERT INTO "apaguna" VALUES(90,20,'90:20','Isaan irratti ibiddi cufamtuudha.[]');
INSERT INTO "apaguna" VALUES(91,1,'91:1','Aduufi ifa isheetiinin (kakadha).');
INSERT INTO "apaguna" VALUES(91,2,'91:2','Ji’aanis; yeroo inni ishee hordofe.');
INSERT INTO "apaguna" VALUES(91,3,'91:3','Guyyaanis yeroo inni ishee (aduu) mul’ise;');
INSERT INTO "apaguna" VALUES(91,4,'91:4','Halkaniinis yeroo inni ishee haguuge;');
INSERT INTO "apaguna" VALUES(91,5,'91:5','Samiifi kan ishee ijaareenis;');
INSERT INTO "apaguna" VALUES(91,6,'91:6','Dachiifi kan ishee diriirseenis;');
INSERT INTO "apaguna" VALUES(91,7,'91:7','Lubbuufi kan ishee uumeenis;');
INSERT INTO "apaguna" VALUES(91,8,'91:8','Kan jal’ina isheefi qajeeluma ishee ishee barsiise.');
INSERT INTO "apaguna" VALUES(91,9,'91:9','Namni ishee qulqulleesse dhugumatti milkaa’e.');
INSERT INTO "apaguna" VALUES(91,10,'91:10','Namni ishee xureesses dhugaatti hoonga’e.');
INSERT INTO "apaguna" VALUES(91,11,'91:11','Samuud daangaa dabruu isheetiin kijibsiifte.');
INSERT INTO "apaguna" VALUES(91,12,'91:12','Yeroo irra jallaan ishee ka’u (kijibsiifte).');
INSERT INTO "apaguna" VALUES(91,13,'91:13','Ergamaan Rabbiis isaaniin “gaala Rabbiitiifi dhugaatii ishee (eeggadhaa)” jedhe.');
INSERT INTO "apaguna" VALUES(91,14,'91:14','Isa kijibsiisanii ishee waraananSababa badii isaanii isaan balleessee wal qixxeesse.');
INSERT INTO "apaguna" VALUES(91,15,'91:15','Haaloo ishee hin sodaatu');
INSERT INTO "apaguna" VALUES(92,1,'92:1','Halkaniin (nan kakadha); yeroo (inni) haguuge.');
INSERT INTO "apaguna" VALUES(92,2,'92:2','Guyyaanis yeroo inni mul''ate;');
INSERT INTO "apaguna" VALUES(92,3,'92:3','Isa kormaafi dhalaa uumeenis;');
INSERT INTO "apaguna" VALUES(92,4,'92:4','Dhugumatti, dalagaan keessan adda adda (wal dhaba).');
INSERT INTO "apaguna" VALUES(92,5,'92:5','Namni kennatee Rabbiin sodaate,');
INSERT INTO "apaguna" VALUES(92,6,'92:6','Islaamummaas dhugoomse,');
INSERT INTO "apaguna" VALUES(92,7,'92:7','(Karaa) laaftuuf isa laaffisuuf jirra.');
INSERT INTO "apaguna" VALUES(92,8,'92:8','Nama don’oomee of ga’e,');
INSERT INTO "apaguna" VALUES(92,9,'92:9','Islaamummaa sobsiise immoo,');
INSERT INTO "apaguna" VALUES(92,10,'92:10','(Karaa) jabduuf isa laaffisuuf jirra.');
INSERT INTO "apaguna" VALUES(92,11,'92:11','Yeroo inni (ibiddatti) kufu qabeenyi isaa isa hin fayyadu.');
INSERT INTO "apaguna" VALUES(92,12,'92:12','Dhugumatti, karaa qajeelaa ifa galchuun (mul''isuun) Nurra jira.');
INSERT INTO "apaguna" VALUES(92,13,'92:13','Dhugumatti, Aakhiraafi addunyaan keenya.');
INSERT INTO "apaguna" VALUES(92,14,'92:14','Ibidda qabsiifamaa ta''e isin sodaachise.');
INSERT INTO "apaguna" VALUES(92,15,'92:15','Hoonga’aa malee ishee hin seenu.');
INSERT INTO "apaguna" VALUES(92,16,'92:16','Kan sobsiisee gara gale (malee).');
INSERT INTO "apaguna" VALUES(92,17,'92:17','Namni Rabbiin sodaatu immoo ishee irraa fageeffamuuf jira.');
INSERT INTO "apaguna" VALUES(92,18,'92:18','Kan qabeenya isaa qulqulleeffachuuf kennu');
INSERT INTO "apaguna" VALUES(92,19,'92:19','Haala isa bira nama tokkoof galanni galfamu hin jirreen (kan kennatu).');
INSERT INTO "apaguna" VALUES(92,20,'92:20','Fuula Gooftaa isaa ol aanaa ta''e barbaachaaf malee (hin kennu).');
INSERT INTO "apaguna" VALUES(92,21,'92:21','Dhugumatti, inni fuulduratti jaalachuuf jira');
INSERT INTO "apaguna" VALUES(93,1,'93:1','Waareenin (kakadha).');
INSERT INTO "apaguna" VALUES(93,2,'93:2','Halkaniinis yeroo inni callise.');
INSERT INTO "apaguna" VALUES(93,3,'93:3','Gooftaan kee si hin dhiifne; si hin jibbines');
INSERT INTO "apaguna" VALUES(93,4,'93:4','Dhugumatti, Aakhiraatu addunyaa irra siif caala.');
INSERT INTO "apaguna" VALUES(93,5,'93:5','Dhugumatti, Rabbiin kee fuula dura (kenna guddaa) siif kenna; ni jaallattas');
INSERT INTO "apaguna" VALUES(93,6,'93:6','Sila yatiima ta’uu si argee si hin hirkifnee?');
INSERT INTO "apaguna" VALUES(93,7,'93:7','Dogoggoraas ta’uu si argee si qajeelche.');
INSERT INTO "apaguna" VALUES(93,8,'93:8','Hiyyeessas taatee si argee si duroomse.');
INSERT INTO "apaguna" VALUES(93,9,'93:9','Yatiima hin hacuucin');
INSERT INTO "apaguna" VALUES(93,10,'93:10','Kadhataas hin ifatin');
INSERT INTO "apaguna" VALUES(93,11,'93:11','Ni''imaa (qananii) Gooftaa keetiis odeessi.');
INSERT INTO "apaguna" VALUES(94,1,'94:1','Sila Nuti qoma kee siif hin bal''ifnee?');
INSERT INTO "apaguna" VALUES(94,2,'94:2','Ba’aa (badii) kee sirraa buufne.');
INSERT INTO "apaguna" VALUES(94,3,'94:3','Isa dugda keetti gar malee ulfaate (sirraa buufne).');
INSERT INTO "apaguna" VALUES(94,4,'94:4','Maqaa keetis siif ol kaafne.');
INSERT INTO "apaguna" VALUES(94,5,'94:5','Rakkinaa wajjin laafinatu jira.');
INSERT INTO "apaguna" VALUES(94,6,'94:6','Dhugumatti, rakkina wajjin laafinatu jira.');
INSERT INTO "apaguna" VALUES(94,7,'94:7','Yeroo (dalagaa wahii) raawwattes jabaadhu');
INSERT INTO "apaguna" VALUES(94,8,'94:8','Gara Gooftaa keetii qofa kajeeli');
INSERT INTO "apaguna" VALUES(95,1,'95:1','Tiiniifi Zaytuuniiniin (kakadha).');
INSERT INTO "apaguna" VALUES(95,2,'95:2','Gaara (Tulluu) Siiniinaatiinis.');
INSERT INTO "apaguna" VALUES(95,3,'95:3','Biyya nagaa kanaanis (nin kakadha).');
INSERT INTO "apaguna" VALUES(95,4,'95:4','Dhugumatti, Nuti nama dhaabbii irra gaarii ta''e keessatti isa uumne.');
INSERT INTO "apaguna" VALUES(95,5,'95:5','Ergasii bakka warra jalaa hundarra gadi aanaa ta''etti isa deebifne.[]');
INSERT INTO "apaguna" VALUES(95,6,'95:6','Garuu isaan amananii gaggaarii dalagan isaaniif mindaa addaan hin cinnetu jira.');
INSERT INTO "apaguna" VALUES(95,7,'95:7','Kana booda maaltu mindaa [guyyaa Qiyaamaa] si kijibsiisa (kijibsiisutti si kaase)?');
INSERT INTO "apaguna" VALUES(95,8,'95:8','Sila Rabbiin irra murteessaa warra murteessanuu hin taanee?');
INSERT INTO "apaguna" VALUES(96,1,'96:1','Maqaa Gooftaa keetii Isa (waan hunda) uumeetiin dubbisi.');
INSERT INTO "apaguna" VALUES(96,2,'96:2','(Isa) nama dhiiga ititaa irraa uume.');
INSERT INTO "apaguna" VALUES(96,3,'96:3','Haala Gooftaan kee hunda irra arjaa ta’een qara''i.');
INSERT INTO "apaguna" VALUES(96,4,'96:4','Isa qalamaan (barreessuu) barsiise sani.');
INSERT INTO "apaguna" VALUES(96,5,'96:5','Nama, waan inni hin beekne barsiise.');
INSERT INTO "apaguna" VALUES(96,6,'96:6','Dhugumatti, namni daangaa dabra');
INSERT INTO "apaguna" VALUES(96,7,'96:7','Sababa duroomuu of arguutiin.');
INSERT INTO "apaguna" VALUES(96,8,'96:8','Deebiin garuma Gooftaa keetiiti.');
INSERT INTO "apaguna" VALUES(96,9,'96:9','Sila isa dhorgu san argitee?');
INSERT INTO "apaguna" VALUES(96,10,'96:10','Gabricha yeroo inni salaatu (kan dhorgu argitee)?');
INSERT INTO "apaguna" VALUES(96,11,'96:11','Yoo inni qajeeluma irra jiraate hoo mee naaf himi!');
INSERT INTO "apaguna" VALUES(96,12,'96:12','Yookiin immoo sodaa Rabbiitti yoo ajaje hoo?');
INSERT INTO "apaguna" VALUES(96,13,'96:13','Yoo inni (Abuu Jahal) kijibsiisee irraa gara gale hoo mee naaf himi?');
INSERT INTO "apaguna" VALUES(96,14,'96:14','Sila inni Rabbiin arguu hin beekuu?');
INSERT INTO "apaguna" VALUES(96,15,'96:15','Haa dhorgamu; yoo inni (Abuu Jahal) dhorgamuu baate, sammuu qabna.');
INSERT INTO "apaguna" VALUES(96,16,'96:16','Sammuu sobduu, dogoggortuu taate (qabna).');
INSERT INTO "apaguna" VALUES(96,17,'96:17','Garee isaa haa waammatu.');
INSERT INTO "apaguna" VALUES(96,18,'96:18','Nutis waardiyyaa keenya waammachuuf jirra.');
INSERT INTO "apaguna" VALUES(96,19,'96:19','Dhorgami! Isaan hin ajajaminSujuudis gara Rabbiittis dhihaadhu.');
INSERT INTO "apaguna" VALUES(97,1,'97:1','Dhugumatti, Nuti halkan sadarkaa qabdu keessa isa (Qur''aana) buufne');
INSERT INTO "apaguna" VALUES(97,2,'97:2','Halkan sadarkaa qabdu maal akka taate maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(97,3,'97:3','Halkan sadarkaa qabdu ji''a kumarra caalti.');
INSERT INTO "apaguna" VALUES(97,4,'97:4',' Maleykonniifi Jibriil ishee keessa eeyyama Gooftaa isaaniitiin ajaja hundaan bu''u.');
INSERT INTO "apaguna" VALUES(97,5,'97:5','Isheen hanga baha fajriitti nagaadha');
INSERT INTO "apaguna" VALUES(98,1,'98:1','Warri abbootii kitaabaafi mushrikoota irraa kafaran odoo ragaan isaanitti hin dhufin kan dhiifamanu[] hin taane.');
INSERT INTO "apaguna" VALUES(98,2,'98:2','(Ragichis) ergamaa Rabbi irraa ta’e kan kitaabota qulqulleeffame dubbisu.');
INSERT INTO "apaguna" VALUES(98,3,'98:3','Ishee keessa barreeffamoota sirriitu jira.[]');
INSERT INTO "apaguna" VALUES(98,4,'98:4','Isaan kitaaba kennamanis hanga ragaan ifaan isaanitti dhufutti adda hin facaane.');
INSERT INTO "apaguna" VALUES(98,5,'98:5','Rabbiin qofa gabbaruu, qajeelanii amantii Isa qofaaf qulqulleessuu, salaata dhaabuufi zakaa kennuutti malee homaa hin ajajamneKun immoo amantii qajeelaadha.');
INSERT INTO "apaguna" VALUES(98,6,'98:6','Warri abbootii kitaabaafi mushrikoota irraa kafaran ibidda jahannam keessatti hafoo ta’anii jiraatuWarri sun isaanumatu irra hamtuu uumamaati.');
INSERT INTO "apaguna" VALUES(98,7,'98:7','Dhugumatti, isaan amananiifi gaggaarii hojjatan, isaan sun isaanumatu irra caalaa uumamaati.');
INSERT INTO "apaguna" VALUES(98,8,'98:8','Mindaan isaanii Gooftaa isaanii biratti jannata qubsumaa kan jala ishee laggeen yaatu yeroo hunda ishee keessa jiraatuRabbiin isaan irraa jaalateera; isaanis Isa irraa jaalataniiruKun nama Gooftaa isaa sodaatuuf qofa ta’a');
INSERT INTO "apaguna" VALUES(99,1,'99:1','Yeroo dachiin sochii ishee sosochoote,');
INSERT INTO "apaguna" VALUES(99,2,'99:2','Akkasumas, (yeroo) dachiin ba''aa ishee baafte,');
INSERT INTO "apaguna" VALUES(99,3,'99:3','Namni "sila isheen maal taate?" jedhe,');
INSERT INTO "apaguna" VALUES(99,4,'99:4','Guyyaa san (dachiin) oduu ishee ni odeessiti.');
INSERT INTO "apaguna" VALUES(99,5,'99:5','[Kunis] Waan Gooftaan kee isheetti beeksisa godheef.');
INSERT INTO "apaguna" VALUES(99,6,'99:6','Guyyaa san namoonni hojii isaanii agarsiifamuuf jecha garee gareen (bakka dhaabbii irraa) deebi’u.');
INSERT INTO "apaguna" VALUES(99,7,'99:7','Namni (hanga) madaala atomii toltuu dalage isuma arga.');
INSERT INTO "apaguna" VALUES(99,8,'99:8','Namni (hanga) madaala atomii hamtuu dalages isuma arga');
INSERT INTO "apaguna" VALUES(100,1,'100:1','(Faradoo jihaadaa) harganaa guluftuunin (kakadha),');
INSERT INTO "apaguna" VALUES(100,2,'100:2','(Isaan yeroo gulufan kottee isaaniitiin) ibidda qabsiisaniin,');
INSERT INTO "apaguna" VALUES(100,3,'100:3','(Isaan) bariidhaan (diina irratti) weerara adeemsisanitti;');
INSERT INTO "apaguna" VALUES(100,4,'100:4','Isaan dhukkee ol kaasanitti;');
INSERT INTO "apaguna" VALUES(100,5,'100:5','Isaan (weeraraan) gamtaa diinaa walakkeessaniin [kakadha].');
INSERT INTO "apaguna" VALUES(100,6,'100:6','Dhugumatti, namni (hedduun) Gooftaa isaa mormaadha.');
INSERT INTO "apaguna" VALUES(100,7,'100:7','Inni kana irratti ragaadha');
INSERT INTO "apaguna" VALUES(100,8,'100:8','Inni (namni) qabeenya jaalachuutti cimaadha.');
INSERT INTO "apaguna" VALUES(100,9,'100:9','Sila inni yeroo wanti qabroota keessa jiran hundi baafaman sana hin beekuu?');
INSERT INTO "apaguna" VALUES(100,10,'100:10','Yeroo wanti qoma keessa jiran (hundi) ifa baafaman (hin beekuu?)');
INSERT INTO "apaguna" VALUES(100,11,'100:11','Dhugumatti, Gooftan isaanii guyyaa san isaan beekaadha.');
INSERT INTO "apaguna" VALUES(101,1,'101:1','Qalbii rukuttuun (Qiyaamaan).');
INSERT INTO "apaguna" VALUES(101,2,'101:2','Qalbii rukuttuun maali?');
INSERT INTO "apaguna" VALUES(101,3,'101:3','Qalbii rukuttuun maal akka taate maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(101,4,'101:4','Guyyaa namni akka billaacha faffacaafamee ta’u.');
INSERT INTO "apaguna" VALUES(101,5,'101:5','(Guyyaa) gaarreen akka jirbii iddamee (qaasasame) ta''u.');
INSERT INTO "apaguna" VALUES(101,6,'101:6','Namni madaalli (hojii) isaa ulfaatte,');
INSERT INTO "apaguna" VALUES(101,7,'101:7','Inni jireenya jaalatamtuu keessa ta''a.');
INSERT INTO "apaguna" VALUES(101,8,'101:8','Namni madaalli (hojii) isaa salphatte immoo,');
INSERT INTO "apaguna" VALUES(101,9,'101:9','Haati isaa “Haawiyaa[]”dha.');
INSERT INTO "apaguna" VALUES(101,10,'101:10','Isheen (Haawiyaan) maal akka taate maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(101,11,'101:11','(Isheen) ibidda gubduudha.');
INSERT INTO "apaguna" VALUES(102,1,'102:1','Baay’ifachuun (ibaadaa irraa) isin jaataneesse (shaagale).');
INSERT INTO "apaguna" VALUES(102,2,'102:2','Hanga awwaala dhaqxanitti (duutanitti).');
INSERT INTO "apaguna" VALUES(102,3,'102:3','Dhorgamaa! Fuula dura beekuuf jirtu.');
INSERT INTO "apaguna" VALUES(102,4,'102:4','Ergasiis dhorgamaa! Fuula dura beekuuf jirtu');
INSERT INTO "apaguna" VALUES(102,5,'102:5','Dhugumatti, odoo beekumsa mirkanaa’aa beektanii (hin shaagalamtanu ture).');
INSERT INTO "apaguna" VALUES(102,6,'102:6','Dhugumatti ibidda ni argitu.');
INSERT INTO "apaguna" VALUES(102,7,'102:7','Ergasii argaa mirkanaa’aa arguuf jirtu');
INSERT INTO "apaguna" VALUES(102,8,'102:8','Ergasii dhugumatti guyyaa san qananii irraa gaafatamuuf jirtu');
INSERT INTO "apaguna" VALUES(103,1,'103:1','Yeroonin kakadha.');
INSERT INTO "apaguna" VALUES(103,2,'103:2','Dhugumatti, namni hunduu hoongoo keessa jira.');
INSERT INTO "apaguna" VALUES(103,3,'103:3','Isaan amanan, gaggaarii dalagan, haqa walii dhaamaniifi obsa walii dhaaman malee (warri hafan hoongoo guddaa keessa jiru).');
INSERT INTO "apaguna" VALUES(104,1,'104:1','Hamattuufi arrabsituu hundaaf badiin mirkanaa’e.');
INSERT INTO "apaguna" VALUES(104,2,'104:2','Isa qabeenya walitti qabee lakkaa''e.');
INSERT INTO "apaguna" VALUES(104,3,'104:3','Qabeenyi isaa isa hambisuu kan yaadu.');
INSERT INTO "apaguna" VALUES(104,4,'104:4','Haa dhorgamu! [Ibidda] caccabsituu keessatti darbamuuf jira.');
INSERT INTO "apaguna" VALUES(104,5,'104:5','Caccabsituun maal akka taate maaltu si beeksise?');
INSERT INTO "apaguna" VALUES(104,6,'104:6','(Isheen) ibidda Rabbii kan qabsiifamtuudha.');
INSERT INTO "apaguna" VALUES(104,7,'104:7','Ishee onnee irratti (laalaan ishee) mul’attuudha.');
INSERT INTO "apaguna" VALUES(104,8,'104:8','Dhugumatti, isheen isaan irratti cufamtuudha.');
INSERT INTO "apaguna" VALUES(104,9,'104:9','Utubaalee diriirfamaa keessatti (itti cufama).');
INSERT INTO "apaguna" VALUES(105,1,'105:1','Sila Gooftan kee Abbootii Arbaa irratti akkam akka dalage hin arginee?');
INSERT INTO "apaguna" VALUES(105,2,'105:2','Sila tooftaa isaanii dhabama hin goonee? (godheera).');
INSERT INTO "apaguna" VALUES(105,3,'105:3','Simbira garee gareedhaan isaanitti erge.');
INSERT INTO "apaguna" VALUES(105,4,'105:4','Dhagaalee xuubii (suphee gubamte) irraa taate isaanitti darbatti.');
INSERT INTO "apaguna" VALUES(105,5,'105:5','Akka alalaa (okaa) nyaatamee isaan taasise.');
INSERT INTO "apaguna" VALUES(106,1,'106:1','Qurayshota barsiisuuf,');
INSERT INTO "apaguna" VALUES(106,2,'106:2','Fe’annaa gannaafi bonaa isaan barsiisuuf jecha (warra arbaa balleesse).');
INSERT INTO "apaguna" VALUES(106,3,'106:3','Kanaafuu Gooftaa mana kanaa haa gabbaranu.');
INSERT INTO "apaguna" VALUES(106,4,'106:4','Isa beela irraa isaan nyachiseefi sodaa irraa isaan tasgabbeesse.');
INSERT INTO "apaguna" VALUES(107,1,'107:1','Isa qormaata sobsiisu argitee?');
INSERT INTO "apaguna" VALUES(107,2,'107:2','Sun isa yatiima humnaan deebisu.');
INSERT INTO "apaguna" VALUES(107,3,'107:3','Hiyyeessa nyaachisuu irrattis (namoota) hin kakaafne.');
INSERT INTO "apaguna" VALUES(107,4,'107:4','Warra salaataniif badiin mirkanaa’e.');
INSERT INTO "apaguna" VALUES(107,5,'107:5','Isaan sun warra salaata isaanii irraa dagatan.');
INSERT INTO "apaguna" VALUES(107,6,'107:6','Isaan warra (dalagaa namatti) agarsiifataniif.');
INSERT INTO "apaguna" VALUES(107,7,'107:7','Isaan meeshaa ergisaa dhorgataniif (badiin mirkanaa’e)');
INSERT INTO "apaguna" VALUES(108,1,'108:1','Nuti dhugumatti, tola guddaa siif kennineerra.');
INSERT INTO "apaguna" VALUES(108,2,'108:2','Gooftaa keetiif sagadii isaaf qali.');
INSERT INTO "apaguna" VALUES(108,3,'108:3','Dhugumatti, kan si jibbu isuma kan qaccee cite (kheyrii hunda irraa cite).');
INSERT INTO "apaguna" VALUES(109,1,'109:1','Jedhi: "yaa warra Rabbitti kafartan!');
INSERT INTO "apaguna" VALUES(109,2,'109:2','Ani waan isin gabbartan hin gabbaru.');
INSERT INTO "apaguna" VALUES(109,3,'109:3','Isinis waan ani gabbaru kan gabbartan hin taane.');
INSERT INTO "apaguna" VALUES(109,4,'109:4','Anis waan isin gabbartan (fuula duras) kan gabbaruu miti.');
INSERT INTO "apaguna" VALUES(109,5,'109:5','Isinis waan ani gabbaru (fuula duras) kan gabbartanii miti.');
INSERT INTO "apaguna" VALUES(109,6,'109:6','Isiniif amantiin keessan jira; anaafis amantiin kiyya jira."');
INSERT INTO "apaguna" VALUES(110,1,'110:1','Yeroo tumsi Rabbiitiifi banamiinsi (Makkaa) dhufe,');
INSERT INTO "apaguna" VALUES(110,2,'110:2','Namootas garee gareedhaan diinii Rabbii keessa kan seenan argite,');
INSERT INTO "apaguna" VALUES(110,3,'110:3','Gooftaa kee faarsuu wajjin qulqulleessi; araaramas Isa irra barbaadInni akkaan tawbaa qeebalaadha.');
INSERT INTO "apaguna" VALUES(111,1,'111:1','Harki Abuu Lahab lamaan badde[Innis]bade.');
INSERT INTO "apaguna" VALUES(111,2,'111:2','Qabeenyi isaatiifi wanti inni hores isa hin fayyanne.');
INSERT INTO "apaguna" VALUES(111,3,'111:3','Ibidda abbaa bobayaa (belbelaa) taate seenuuf jira.');
INSERT INTO "apaguna" VALUES(111,4,'111:4','Niitiin isaas baadhattuu qoraanii taatee (seenti).');
INSERT INTO "apaguna" VALUES(111,5,'111:5','Morma ishee keessa haada quncee temiraa irraa ta''etu jira');
INSERT INTO "apaguna" VALUES(112,1,'112:1','Jedhi: "Inni Allaah (gabbaramaa dhugaa) tokkicha.');
INSERT INTO "apaguna" VALUES(112,2,'112:2','Allaahn hirkoo waa hundaati.');
INSERT INTO "apaguna" VALUES(112,3,'112:3','Hin dhalle hin dhalannes.');
INSERT INTO "apaguna" VALUES(112,4,'112:4','Hiriyaan tokkoos isaaf hin taane.”');
INSERT INTO "apaguna" VALUES(113,1,'113:1','Jedhi: "Gooftaa bariittin maganfadha.');
INSERT INTO "apaguna" VALUES(113,2,'113:2','Hamtuu waan Inni uumee irraa,');
INSERT INTO "apaguna" VALUES(113,3,'113:3','Hamtuu dukkanaa yeroo dukkanaa’e irraas,');
INSERT INTO "apaguna" VALUES(113,4,'113:4','Hamtuu guduunfaa keessatti tuftu irraas,');
INSERT INTO "apaguna" VALUES(113,5,'113:5','Hamtuu haasidii yeroo inni hassadu (waanyu) irraas (Rabbittin maganfadha).”');
INSERT INTO "apaguna" VALUES(114,1,'114:1','Jedhi: "Gooftaa namaattin maganfadha.');
INSERT INTO "apaguna" VALUES(114,2,'114:2','Mootii namaatti,');
INSERT INTO "apaguna" VALUES(114,3,'114:3','Gabbaramaa namaatti,');
INSERT INTO "apaguna" VALUES(114,4,'114:4','Hamtuu waswaasii dhokatuufi mul’atuu irraa,');
INSERT INTO "apaguna" VALUES(114,5,'114:5','Isa qoma namaa keessa waswaasu irraa,');
INSERT INTO "apaguna" VALUES(114,6,'114:6','Jinniifi nama irraa kan ta’e (irraan maganfadha).”');
