INSERT INTO "aykyuni" VALUES(1,1,'1:1','Ní orúkọ Allāhu, Àjọkẹ́-ayé, Àṣàkẹ́-ọ̀run.1 ');
INSERT INTO "aykyuni" VALUES(1,2,'1:2','Gbogbo ẹyìnń jẹ́ ti Allāhu, Olúwa2 gbogbo ẹ̀dá, ');
INSERT INTO "aykyuni" VALUES(1,3,'1:3','Àjọkẹ́-ayé, Àṣàkẹ́-ọ̀run,');
INSERT INTO "aykyuni" VALUES(1,4,'1:4','Olùkápá-ọjọ́-ẹ̀san.');
INSERT INTO "aykyuni" VALUES(1,5,'1:5','Ìwọ nìkan ni à ń jọ́sìn fún. Ọ̀dọ̀ Rẹ nìkan sì ni à ń wá oore sí.1 ');
INSERT INTO "aykyuni" VALUES(1,6,'1:6','Fi ẹsẹ̀ wa rinlẹ̀ l’ójú ọ̀nà tààrà (’Islām),1 ');
INSERT INTO "aykyuni" VALUES(1,7,'1:7','ọ̀nà àwọn tí O ṣe ìdẹ̀ra fún,yàtọ̀ sí (ọ̀nà) àwọn ẹni-ìbínú (ìyẹn, àwọn yẹhudi) àti (ọ̀nà) àwọn olùṣìnà (ìyẹn, àwọn nasọ̄rọ̄). ');
INSERT INTO "aykyuni" VALUES(2,1,'2:1','’Alif lām mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(2,2,'2:2','Tírà yìí, kò sí iyèméjì nínú rẹ̀. Ìmọ̀nà ni fún àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(2,3,'2:3','(Àwọn olùbẹ̀rù Allāhu ni:) àwọn tó gbàgbọ́ ní òdodo nínú ìkọ̀kọ̀àti àwọn tó ń kírun àti àwọn tó ń ná nínú ohun tí A pèsè fún wọn, ');
INSERT INTO "aykyuni" VALUES(2,4,'2:4','àti àwọn tó gbàgbọ́ ní òdodo nínú ohun tí A sọ̀kalẹ̀ fún ọ àti ohun tí A sọ̀kalẹ̀ ṣíwájú rẹàti àwọn tó ní àmọ̀dájú nípa Ọjọ́ Ìkẹ́yìn.2 ');
INSERT INTO "aykyuni" VALUES(2,5,'2:5','Àwọn wọ̀nyẹn wà lórí ìmọ̀nà láti ọ̀dọ̀ Olúwa wọn. Àwọn wọ̀nyẹn gan-an ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(2,6,'2:6','Dájúdájú àwọn tó ṣàì gbàgbọ́, bákan náà ni fún wọn, yálà o kìlọ̀ fún wọn tàbí o kò kìlọ̀ fún wọn, wọn kò níí gbàgbọ́.1 ');
INSERT INTO "aykyuni" VALUES(2,7,'2:7','Allāhu fi èdídí dí ọkàn wọn àti ìgbọ́rọ̀ wọn. Èbìbò sì bo ìríran wọn. Ìyà ńlá sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(2,8,'2:8','Ó ń bẹ nínú àwọn ènìyàn, ẹni tó ń wí pé: “A gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn.” Wọn kì í sì ṣe onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(2,9,'2:9','Wọ́n ń tan Allāhu àti àwọn tó gbàgbọ́ ní òdodo. Wọn kò sì tan ẹnì kan bí kò ṣe ẹ̀mí ara wọn; wọn kò sì fura.');
INSERT INTO "aykyuni" VALUES(2,10,'2:10','Àrùn kan wà nínú ọkàn wọn nítorí náà Allāhu ṣe àlékún àrùn fún wọn.2 Ìyà ẹlẹ́ta-eléro sì wà fún wọn nítorí pé wọ́n ń parọ́. ');
INSERT INTO "aykyuni" VALUES(2,11,'2:11','Nígbà tí wọ́n bá sọ fún wọn pé: “Ẹ má ṣèbàjẹ́ lórí ilẹ̀.” Wọ́n á wí pé: “Àwa kúkú ni alátùn-únṣe.”');
INSERT INTO "aykyuni" VALUES(2,12,'2:12','Ẹ kíyè sí i! Dájúdájú àwọn gan-an ni òbìlẹ̀jẹ́, ṣùgbọ́n wọn kò fura.');
INSERT INTO "aykyuni" VALUES(2,13,'2:13','Nígbà tí wọ́n bá sọ fún wọn pé: “Ẹ gbàgbọ́ ní òdodo gẹ́gẹ́ bí àwọn ènìyàn ṣe gbàgbọ́.” Wọ́n á wí pé: “Ṣé kí á gbàgbọ́ gẹ́gẹ́ bí àwọn òmùgọ̀ ṣe gbàgbọ́ ni?” Ẹ kíyè sí i! Dájúdájú àwọn gan-an ni òmùgọ̀, ṣùgbọ́n wọn kò mọ̀.');
INSERT INTO "aykyuni" VALUES(2,14,'2:14','Nígbà tí wọ́n bá pàdé àwọn tó gbàgbọ́ ní òdodo, wọ́n á wí pé: “A gbàgbọ́.” Nígbà tí ó bá sì kù wọ́n ku àwọn (ẹni) èṣù wọn, wọ́n á wí pé: “Dájúdájú àwa ń bẹ pẹ̀lú yín, àwa kàn ń ṣe yẹ̀yẹ́ ni.”');
INSERT INTO "aykyuni" VALUES(2,15,'2:15','Allāhu máa fi wọ́n ṣe yẹ̀yẹ́. Ó sì máa mú wọn lékún sí i nínú àgbéré wọn, tí wọn yóò máa pa rìdàrìdà.1 ');
INSERT INTO "aykyuni" VALUES(2,16,'2:16','Àwọn wọ̀nyẹn ni àwọn tí wọ́n fi ìmọ̀nà ra ìṣìnà.Nítorí náà, òkòwò wọn kò lérè, wọn kò sì jẹ́ olùmọ̀nà. ');
INSERT INTO "aykyuni" VALUES(2,17,'2:17','Àpèjúwe wọn dà bí àpèjúwe ẹni tí ó tan iná, ṣùgbọ́n nígbà tí ìmọ́lẹ̀ tàn sí àyíká rẹ̀ tán, Allāhu mú ìmọ́lẹ̀ wọn lọ, Ó sì fi wọ́n sílẹ̀ sínú àwọn òkùnkùn; wọn kò sì ríran mọ́.');
INSERT INTO "aykyuni" VALUES(2,18,'2:18','Adití, ayaya, afọ́jú ni wọ́n; nítorí náà wọn kò níí ṣẹ́rí padà.');
INSERT INTO "aykyuni" VALUES(2,19,'2:19','Tàbí (àpèjúwe wọn) dà bí òjò ńlá tí ń rọ̀ láti sánmọ̀. Ó mú àwọn òkùnkùn, àrá sísán àti mọ̀nàmọ́ná lọ́wọ́. Wọ́n ń fi ìka wọn sínú etí wọn nítorí àwọn iná láti ojú sánmọ̀ fún ìbẹ̀rù ikú. Allāhu sì yí àwọn aláìgbàgbọ́ ká.');
INSERT INTO "aykyuni" VALUES(2,20,'2:20','Mọ̀nàmọ́ná náà fẹ́ẹ̀ mú ìríran wọn lọ. Ìgbàkígbà tí ó bá tan ìmọ́lẹ̀ sí wọn, wọ́n á rìn lọ nínú rẹ̀. Nígbà tí ó bá sì ṣóòòkùn mọ́ wọn, wọ́n á dúró si. Àti pé tí ó bá jẹ́ pé Allāhu bá fẹ́, dájúdájú ìbá gba ìgbọ́rọ̀ wọn àti ìríran wọn. Dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(2,21,'2:21','Ẹ̀yin ènìyàn, ẹ jọ́sìn fún Olúwa yín, Ẹni tí Ó dá ẹ̀yin àti àwọn tó ṣíwájú yín, nítorí kí ẹ lè ṣọ́ra (fún ìyà Iná).1 ');
INSERT INTO "aykyuni" VALUES(2,22,'2:22','(Ẹ jọ́sìn fún) Ẹni tí Ó ṣe ilẹ̀ fún yín ní ìtẹ́, (Ó ṣe) sánmọ̀ ní àjà (le yín lórí), Ó sọ omi kalẹ̀ láti sánmọ̀ (fún yín), Ó sì fi mú àwọn èso jáde ní ìjẹ-ìmu fún yín. Nítorí náà, ẹ má ṣe bá Allāhu wá akẹgbẹ́, ẹ sì mọ̀ (pé kò ní akẹgbẹ́).');
INSERT INTO "aykyuni" VALUES(2,23,'2:23','Tí ẹ bá wà nínú iyèméjì nípa ohun tí A sọ̀kalẹ̀ fún ẹrúsìn Wa, nítorí náà, ẹ mú sūrah kan wá bí irú rẹ̀, kí ẹ sì pe àwọn ẹlẹ́rìí yín, yàtọ̀ sí Allāhu, tí ẹ bá jẹ́ olódodo.');
INSERT INTO "aykyuni" VALUES(2,24,'2:24','Tí ẹ kò bá sì ṣe bẹ́ẹ̀, ẹ kò wulẹ̀ lè ṣe bẹ́ẹ̀, nítorí náà, ẹ ṣọ́ra fún Iná, èyí tí ìkoná rẹ̀ jẹ́ àwọn ènìyàn àti òkúta tí wọ́n pa lésè sílẹ̀ de àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(2,25,'2:25','Fún àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere ní ìró ìdùnnú pé, dájúdájú àwọn Ọgbà Ìdẹ̀ra kan ń bẹ fún wọn, èyí tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Ìgbàkígbà tí A bá p’èsè jíjẹ-mímu kan fún wọn nínú èso rẹ̀, wọn yóò sọ pé: “Èyí ni wọ́n ti pèsè fún wa tẹ́lẹ̀tẹ́lẹ̀.” - Wọ́n mú un wá fún wọn ní ìrísí kan náà ni (àmọ́ pẹ̀lú adùn ọ̀tọ̀ọ̀tọ̀). - Àwọn ìyàwó mímọ́ sì ń bẹ fún wọn nínú Ọgbà Ìdẹ̀ra. Olùṣegbére sì ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(2,26,'2:26','Dájúdájú Allāhu kò níí tijú láti fí ohun kan (tí ó mọ) bí ẹ̀fọn tàbí ohun tí ó jù ú lọ ṣàkàwé ọ̀rọ̀. Ní ti àwọn tó gbàgbọ́ ní òdodo, wọn yóò mọ̀ pé dájúdájú òdodo ni láti ọ̀dọ̀ Olúwa wọn. Ní ti àwọn tó ṣàì gbàgbọ́, wọn yóò wí pé: “Kí ni ohun tí Allāhu gbàlérò pẹ̀lú àkàwé yìí?” Allāhu ń fi ṣi ọ̀pọ̀lọ́pọ̀ lọ́nà. Ó sì ń fi tọ́ ọ̀pọ̀lọ́pọ̀ sọ́nà. Kò sì níí fi ṣi ẹnikẹ́ni lọ́nà àyàfi àwọn arúfin.1 ');
INSERT INTO "aykyuni" VALUES(2,27,'2:27','(Àwọn arúfin náà ni) àwọn tó ń yẹ májẹ̀mu Allāhu lẹ́yìn tí májẹ̀mu náà ti fìdí múlẹ̀, wọ́n tún ń já ohun tí Allāhu pa láṣẹ pé kí wọ́n dàpọ̀, wọ́n sì ń ṣe ìbàjẹ́ lórí ilẹ̀, àwọn wọ̀nyẹn gan-an ni ẹni òfò.');
INSERT INTO "aykyuni" VALUES(2,28,'2:28','Báwo ni ẹ ṣe ń ṣàì gbàgbọ́ nínú Allāhu ná! Bẹ́ẹ̀ sì ni òkú ni yín (tẹ́lẹ̀), Ó sì sọ yín di alààyè. Lẹ́yìn náà, Ó máa sọ yín di òkú. Lẹ́yìn náà, Ó máa sọ yín di alààyè. Lẹ́yìn náà, ọ̀dọ̀ Rẹ̀ ni wọn yóò da yín padà sí.1 ');
INSERT INTO "aykyuni" VALUES(2,29,'2:29','Òun ni Ẹni tí Ó dá ohunkóhun tó wà lórí ilẹ̀ fún yín pátápátá. Lẹ́yìn náà, Ó wà l’ókè sánmọ̀, Ó sì ṣe wọ́n tógún régé sí sánmọ̀ méje. Òun sì ni Onímọ̀ nípa gbogbo n̄ǹkan.1 ');
INSERT INTO "aykyuni" VALUES(2,30,'2:30','(Rántí) nígbà tí Olúwa rẹ sọ fún àwọn mọlāika pé: “Dájúdájú Èmi yóò fi àrólé kan sórí ilẹ̀.”Wọ́n sọ pé: “Ṣé Ìwọ yóò fi ẹni tí ó máa ṣèbàjẹ́ síbẹ̀, tí ó sì máa ta ẹ̀jẹ̀ sílẹ̀? Àwa sì ń ṣe àfọ̀mọ́ àti ẹyìn fún Ọ. A óò máa ṣe àfọ̀mọ́ fún Ọ sẹ́!” Ó sọ pé: “Dájúdájú Èmi mọ ohun tí ẹ̀yin kò mọ̀.” ');
INSERT INTO "aykyuni" VALUES(2,31,'2:31','Allāhu fi gbogbo àwọn orúkọ náà mọ Ādam. Lẹ́yìn náà, Ó kó wọn síwájú àwọn mọlāika, Ó sì sọ pé: “Ẹ sọ àwọn orúkọ ìwọ̀nyí fún Mi, tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(2,32,'2:32','Wọ́n sọ pé: “Mímọ́ ni fún Ọ, kò sí ìmọ̀ kan fún wa àyàfi ohun tí O fi mọ̀ wá. Dájúdájú Ìwọ, Ìwọ ni Onímọ̀, Ọlọ́gbọ́n.”');
INSERT INTO "aykyuni" VALUES(2,33,'2:33','Ó sọ pé: “Ādam, sọ orúkọ wọn fún wọn.” Nígbà tí ó sọ orúkọ wọn fún wọn tán, Ó sọ pé: “Ṣé Èmi kò sọ fún yín pé dájúdájú Èmi nímọ̀ ìkọ̀kọ̀ àwọn sánmọ̀ àti ilẹ̀, Mo sì nímọ̀ ohun tí ẹ̀ ń ṣe àfihàn rẹ̀ àti ohun tí ẹ̀ ń fi pamọ́?”');
INSERT INTO "aykyuni" VALUES(2,34,'2:34','(Rántí) nígbà tí A sọ fún àwọn mọlāika pé: “Ẹ forí kanlẹ̀ kí (Ànábì) Ādam.”Wọ́n sì forí kanlẹ̀ kí i àyàfi ’Iblīs.2 Ó kọ̀, ó sì ṣe ìgbéraga. Ó sì wà nínú àwọn aláìgbàgbọ́. ');
INSERT INTO "aykyuni" VALUES(2,35,'2:35','A sì sọ pé: “Ādam, ìwọ àti ìyàwó rẹ, ẹ máa gbé nínú Ọgbà Ìdẹ̀ra. Kí ẹ máa jẹ nínú rẹ̀ ní gbẹdẹmukẹ ní ibikíbi tí ẹ bá fẹ́. Kí ẹ sì má ṣe súnmọ́ igi yìí, kí ẹ má baà wà nínú àwọn alábòsí.”1 ');
INSERT INTO "aykyuni" VALUES(2,36,'2:36','Lẹ́yìn náà, Èṣù yẹ àwọn méjèèjì lẹ́sẹ̀ kúrò nínú Ọgbà Ìdẹ̀ra, ó sì mú wọn jáde kúrò nínú ibi tí wọ́n wà. A sì sọ pé: “Ẹ sọ̀kalẹ̀, ọ̀tá ní apá kan yín jẹ́ fún apá kan. Ibùgbé àti n̄ǹkan ìgbádùn sì ń bẹ fún yín lórí ilẹ̀ títí di ìgbà kan.”');
INSERT INTO "aykyuni" VALUES(2,37,'2:37','Lẹ́yìn náà, (Ànábì) Ādam rí àwọn ọ̀rọ̀ kan gbà láti ọ̀dọ̀ Olúwa rẹ̀. Ó sì gba ìronúpìwàdà rẹ̀. Dájúdájú Allāhu, Òun ni Olùgba-ìronúpìwàdà, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,38,'2:38','A sọ pé “Gbogbo yín, ẹ sọ̀kalẹ̀ kúrò nínú rẹ̀. Nígbà tí ìmọ̀nà bá dé ba yín láti ọ̀dọ̀ Mi, ẹni tí ó bá tẹ̀lé ìmọ̀nà Mi, ìpáyà kò níí sí fún wọn. Wọn kò sì níí banújẹ́. ');
INSERT INTO "aykyuni" VALUES(2,39,'2:39','Àwọn tó bá sì ṣàì gbàgbọ́, tí wọ́n pe àwọn āyah Wa ní irọ́, àwọn wọ̀nyẹn ni èrò inú Iná. Olùṣegbére ni wọ́n nínú rẹ̀.”');
INSERT INTO "aykyuni" VALUES(2,40,'2:40','Ẹ̀yin ọmọ ’Isrọ̄’īl,ẹ rántí ìdẹ̀ra Mi, èyí tí Mo ṣe fún yín. Ẹ mú májẹ̀mu Mi ṣẹ, Mo máa mú (ẹ̀san) májẹ̀mu yín ṣẹ. Èmi nìkan ni kí ẹ sì páyà. ');
INSERT INTO "aykyuni" VALUES(2,41,'2:41','Ẹ gbàgbọ́ nínú ohun tí Mo sọ̀kalẹ̀, tí ó ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó wà pẹ̀lú yín. Ẹ má ṣe jẹ́ ẹni àkọ́kọ́ tí ó máa ṣàì gbàgbọ́ nínú rẹ̀. Ẹ má ṣe ta àwọn āyah Mi ní owó pọ́ọ́kú.Èmi nìkan ṣoṣo ni kí ẹ sì bẹ̀rù. ');
INSERT INTO "aykyuni" VALUES(2,42,'2:42','Ẹ má da irọ́ pọ̀ mọ́ òdodo, ẹ sì má fi òdodo pamọ́ nígbà tí ẹ̀yin mọ (òdodo).');
INSERT INTO "aykyuni" VALUES(2,43,'2:43','Ẹ kírun, ẹ yọ zakāh, kí ẹ sì dáwọ́tẹ orúnkún pẹ̀lú àwọn olùdáwọ́tẹ-orúnkún (lórí ìrun).');
INSERT INTO "aykyuni" VALUES(2,44,'2:44','Ṣé ẹ̀yin yóò máa pa àwọn ènìyàn l’áṣẹ ohun rere, ẹ sì ń gbàgbé ẹ̀mí ara yín, ẹ̀yin sì ń ké Tírà, ṣé ẹ kò ṣe làákàyè ni!');
INSERT INTO "aykyuni" VALUES(2,45,'2:45','Ẹ wá oore (Allāhu) pẹ̀lú sùúrùàti ìrun kíkí. Dájúdájú ó lágbára (láti ṣe bẹ́ẹ̀) àyàfi fún àwọn olùpáyà (Allāhu), ');
INSERT INTO "aykyuni" VALUES(2,46,'2:46','(Àwọn olùpáyà Allāhu ni) àwọn tó mọ̀ dájúdájú pé, àwọn yóò pàdé Olúwa wọn, àti pé dájúdájú àwọn yóò padà sí ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(2,47,'2:47','Ẹ̀yin ọmọ ’Isrọ̄’īl, ẹ rántí ìdẹ̀ra Mi, èyí tí Mo fi ṣèdẹ̀ra fún yín. Dájúdájú Èmi tún ṣoore àjùlọ fún yín lórí àwọn ẹ̀dá (àsìkò yín).');
INSERT INTO "aykyuni" VALUES(2,48,'2:48','Ẹ bẹ̀rù ọjọ́ kan tí ẹ̀mí kan kò níí rọrọ̀ kiní kan fún ẹ̀mí kan. Wọn kò níí gba ìṣìpẹ̀ l’ọ́wọ́ rẹ̀. Wọn kò níí gba ààrọ̀ l’ọ́wọ́ rẹ̀.Wọn kò sì níí ràn wọ́n lọ́wọ́.2 ');
INSERT INTO "aykyuni" VALUES(2,49,'2:49','(Ẹ rántí) nígbà tí A gbà yín là lọ́wọ́ àwọn ènìyàn Fir‘aon, tí wọ́n ń fi ìyà burúkú jẹ yín, tí wọ́n ń dúńbú àwọn ọmọkùnrin yín, tí wọ́n sì ń jẹ́ kí àwọn obìnrin yín ṣẹ̀mí. Àdánwò ńlá wà nínú ìyẹn fún yín láti ọ̀dọ̀ Olúwa yín.');
INSERT INTO "aykyuni" VALUES(2,50,'2:50','(Ẹ rántí) nígbà tí A pín agbami odò sí (ọ̀nà méjìlá ọ̀tọ̀ọ̀tọ̀) fún yín, A sì gbà yín là. A tẹ àwọn ènìyàn Fir‘aon rì. Ẹ̀yin náà sì ń wò (wọ́n nínú agbami odò).');
INSERT INTO "aykyuni" VALUES(2,51,'2:51','(Ẹ rántí) nígbà tí A ṣe àdéhùn ogójì òru fún (Ànábì) Mūsā. Lẹ́yìn náà, ẹ tún bọ ọ̀bọrọgidi ọmọ màálù lẹ́yìn rẹ̀. Alábòsí sì ni yín.');
INSERT INTO "aykyuni" VALUES(2,52,'2:52','Lẹ́yìn náà, A mójú kúrò fún yín lẹ́yìn ìyẹn nítorí kí ẹ lè dúpẹ́ (fún Allāhu).');
INSERT INTO "aykyuni" VALUES(2,53,'2:53','(Ẹ rántí) nígbà tí A fún (Ànábì) Mūsā ní Tírà àti ọ̀rọ̀-ìpínyà nítorí kí ẹ lè mọ̀nà.1 ');
INSERT INTO "aykyuni" VALUES(2,54,'2:54','(Ẹ rántí) nígbà tí (Ànábì) Mūsā sọ fún ìjọ rẹ̀ pé: “Ẹ̀yin ìjọ mi, dájúdájú ẹ̀yin ṣàbòsí sí ẹ̀mí ara yín nípa sísọ ọ̀bọrọgidi ọmọ màálù di òrìṣà. Nítorí náà, ẹ ronú pìwàdà sí ọ̀dọ̀ Ẹlẹ́dàá yín, kí àwọn tí kò bọ màálù pa àwọn tó bọ ọ́ láààrin yín. Ìyẹn l’óore jùlọ fún yín ní ọ̀dọ̀ Ẹlẹ́dàá yín. Ó sì máa gba ìronúpìwàdà yín. Dájúdájú Allāhu, Òun ni Olùgba-ìronúpìwàdà, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,55,'2:55','(Ẹ rántí) nígbà tí ẹ wí pé: “Mūsā, a ò níí gbàgbọ́ nínú rẹ àfi kí á rí Allāhu ní ojúkojú.” Nítorí náà, iná láti ojú sánmọ̀ gba yín mú, ẹ sì ń wò bọ̀ọ̀.');
INSERT INTO "aykyuni" VALUES(2,56,'2:56','Lẹ́yìn náà, A ji yín dìde lẹ́yìn ikú yín, nítorí kí ẹ lè dúpẹ́ (fún Allāhu).1');
INSERT INTO "aykyuni" VALUES(2,57,'2:57','A tún fi ẹ̀ṣújò ṣe ibòji fún yín. A sì tún sọ (ohun mímu) mọnnuàti (ohun jíjẹ) salwā2 kalẹ̀ fún yín. Ẹ jẹ nínú àwọn n̄ǹkan dáadáa tí A pèsè fún yín. Wọn kò sì ṣàbòsí sí Wa, ṣùgbọ́n ẹ̀mí ara wọn ni wọ́n ń ṣàbòsí sí. ');
INSERT INTO "aykyuni" VALUES(2,58,'2:58','(Ẹ rántí) nígbà tí A sọ pé: “Ẹ wọ inú ìlú yìí.Ẹ jẹ nínú ìlú náà níbikíbi tí ẹ bá fẹ́ ní gbẹdẹmukẹ. Ẹ gba ẹnu-ọ̀nà (ìlú) náà wọlé ní ìtẹríba.2 Kí ẹ sì wí pé: “Ha ẹ̀ṣẹ̀ wa dànù.” A óò forí àwọn ẹ̀ṣẹ̀ yín jìn yín. A ó sì ṣe àlékún (ẹ̀san rere) fún àwọn olùṣe-rere. ');
INSERT INTO "aykyuni" VALUES(2,59,'2:59','Àwọn tó ṣàbòsí yí ọ̀rọ̀ náà padà (sí ọ̀rọ̀ mìíràn) yàtọ̀ sí èyí tí A sọ fún wọn. Nítorí náà, A sọ ìyà kalẹ̀ láti sánmọ̀ lé àwọn tó ṣàbòsí lórí nítorí pé, wọ́n ń rú òfin.');
INSERT INTO "aykyuni" VALUES(2,60,'2:60','(Ẹ rántí) nígbà tí (Ànábì) Mūsā tọrọ omi fún ìjọ rẹ̀. A sì sọ pé: “Fi ọ̀pá rẹ na òkúta.” Orísun omi méjìlá sì ṣàn jáde láti inú rẹ̀. Àwọn ènìyàn kọ̀ọ̀kan sì ti mọ ibùmu wọn. Ẹ jẹ, kí ẹ sì mu nínú arísìkí Allāhu. Kí ẹ sì má ṣèbàjẹ́ sórí ilẹ̀.');
INSERT INTO "aykyuni" VALUES(2,61,'2:61','(Ẹ rántí) nígbà tí ẹ wí pé: “Mūsā, a ò níí ṣe ìfaradà lórí oúnjẹ ẹyọ kan. Nítorí náà, pe Olúwa rẹ fún wa. Kí Ó mú jáde fún wa nínú ohun tí ilẹ̀ ń hù jáde ní ewébẹ̀ rẹ̀ àti kùkúḿbà rẹ̀ àti fūmu rẹ̀ (wíìtì àti àlùbọ́sà áyù) àti ọkàbàbà rẹ̀ àti àlùbọ́sà rẹ̀.” (Ànábì Mūsā) sọ pé: “Ṣé ẹ̀yin yóò fi èyí tó yẹpẹrẹ pààrọ̀ èyí tí ó dára jùlọ ni? Ẹ sọ̀kalẹ̀ sínú ìlú (mìíràn). Dájúdájú ohun tí ẹ̀ ń bèèrè fún ń bẹ (níbẹ̀) fún yín.” A sì mú ìyẹpẹrẹ àti òṣì bá wọn. Wọ́n sì padà wálé pẹ̀lú ìbínú láti ọ̀dọ̀ Allāhu. Ìyẹn nítorí pé, dájúdájú wọ́n ń ṣàì gbàgbọ́ nínú àwọn āyah Allāhu, wọ́n sì ń pa àwọn Ànábì láì lẹ́tọ̀ọ́. Ìyẹn nítorí pé, wọ́n yapa (àṣẹ Allāhu), wọ́n sì ń tayọ ẹnu-ààlà.');
INSERT INTO "aykyuni" VALUES(2,62,'2:62','Dájúdájú àwọn tó gbàgbọ́ ní òdodo àti àwọn yẹhudi àti àwọn nasọ̄rọ̄ àti àwọn sọ̄bi’u; ẹnikẹ́ni tí ó bá ní ìgbàgbọ́ òdodo nínú Allāhu àti Ọjọ́ Ìkẹ́yìn, tí ó sì ṣe iṣẹ́ rere, ẹ̀san wọn ń bẹ fún wọn ní ọ̀dọ̀ Olúwa wọn. Kò sí ìbẹ̀rù fún wọn. Wọn kò sì níí banújẹ́.1 ');
INSERT INTO "aykyuni" VALUES(2,63,'2:63','Ẹ rántí nígbà tí A gba àdéhùn yín, A sì gbé àpáta wá sókè orí yín, (A sì sọ pé): “Ẹ gbá ohun tí A fún yín mú dáradára, kí ẹ sì rántí ohun tó wà nínú rẹ̀, nítorí kí ẹ lè bẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(2,64,'2:64','Lẹ́yìn náà, ẹ pẹ̀yìndà lẹ́yìn ìyẹn. Tí kì í bá ṣe oore àjùlọ Allāhu àti àánú Rẹ̀ lórí yín, ẹ̀yin ìbá wà nínú ẹni òfò.');
INSERT INTO "aykyuni" VALUES(2,65,'2:65','Dájúdájú ẹ mọ àwọn tó kọjá ẹnu-ààlà nínú yín nípa ọjọ́ Sabt. A sì sọ fún wọn pé: “Ẹ di ọ̀bọ, ẹni-ìgbéjìnnà sí ìkẹ́ (ẹni àbùkù ẹni yẹpẹrẹ).”');
INSERT INTO "aykyuni" VALUES(2,66,'2:66','A sì ṣe é ní ìyà (àríkọ́gbọ́n) fún ẹni tó ṣojú rẹ̀ àti ẹni tó ń bọ̀ lẹ́yìn rẹ̀. (Ó tún jẹ́) wáàsí (ìṣítí) fún àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(2,67,'2:67','(Ẹ rántí) nígbà tí (Ànábì) Mūsā sọ fún ìjọ rẹ̀ pé: “Dájúdájú Allāhu ń pa yín ní àṣẹ pé kí ẹ pa abo màálù kan.” Wọ́n wí pé: “Ṣé ò ń fi wá ṣe yẹ̀yẹ́ ni!” Ó sọ pé: “Mò ń sádi Allāhu kúrò níbi kí n̄g jẹ́ ara àwọn òpè.”');
INSERT INTO "aykyuni" VALUES(2,68,'2:68','Wọ́n wí pé: “Pe Olúwa rẹ fún wa, kí Ó ṣàlàyé irú èyí tí ó bá jẹ́ fún wa.” Ó sọ pé: “Dájúdájú Ó ń sọ pé abo màálù ni. Kò níí jẹ́ ògbólógbòó, kò sì níí jẹ́ gódógbó. Ọdún rẹ̀ máa wà láààrin (méjèèjì) yẹn. Nítorí náà, ẹ ṣe ohun tí wọ́n ń pa yín láṣẹ.”');
INSERT INTO "aykyuni" VALUES(2,69,'2:69','Wọ́n wí pé: “Pe Olúwa rẹ fún wa, kí Ó ṣàlàyé irú èyí tí àwọ̀ rẹ̀ bá jẹ́ fún wa.” Ó sọ pé: “Dájúdájú Ó ń sọ pé abo màálù, aláwọ̀ omi-ọsàn ni. Àwọ̀ rẹ̀ sì máa mọ́ fónífóní, tí ó máa dùn-ún wò lójú àwọn olùwòran.');
INSERT INTO "aykyuni" VALUES(2,70,'2:70','Wọ́n wí pé: “Pe Olúwa rẹ fún wa, kí Ó ṣàlàyé irú èyí tí ó bá jẹ́ fún wa sẹ́.” Dájúdájú àwọn abo màálù jọra wọn lójú wa. Àti pé dájúdájú, tí Allāhu bá fẹ́, àwa máa mọ̀nà (tí a ó gbà rí i.)”');
INSERT INTO "aykyuni" VALUES(2,71,'2:71','Ó sọ pé: “Dájúdájú Ó ń sọ pé abo màálù ni. Kò níí jẹ́ màálù yẹpẹrẹ tí ó ń kọ ebè sórí ilẹ̀ (tó ń ro oko) àti èyí tí ó ń fomi wọ́n oko. Ó máa ní àlàáfíà, kò sì níí ní àbàwọ́n kan lára.” Wọ́n wí pé: “Nísinsìn yìí lo mú òdodo wá.” Wọ́n sì pa màálù náà. Wọ́n fẹ́ẹ̀ má ṣe é mọ́.');
INSERT INTO "aykyuni" VALUES(2,72,'2:72','(Ẹ rántí) nígbà tí ẹ pa ẹnì kan, ẹ sì ń tì í síra yín. Allāhu yó sì ṣàfi hàn ohun tí ẹ̀ ń fi pamọ́.');
INSERT INTO "aykyuni" VALUES(2,73,'2:73','Nítorí náà, A sọ pé: “Ẹ fi burè kan (lára màálù) lu (òkú náà).”Báyẹn ni Allāhu ṣe ń sọ òkú di alààyè. Ó sì ń fi àwọn àmì Rẹ̀ hàn yín, nítorí kí ẹ lè ṣe làákàyè. ');
INSERT INTO "aykyuni" VALUES(2,74,'2:74','Lẹ́yìn náà, ọkàn yín le lẹ́yìn ìyẹn. Ó sì dà bí òkúta tàbí líle tó lágbára jù bẹ́ẹ̀ lọ.Dájúdájú ó ń bẹ nínú àwọn òkúta tí àwọn odò ń ṣàn jáde láti inú rẹ̀. Dájúdájú ó tún ń bẹ nínú wọn tí ó máa sán kànkàn. Omi sì máa jáde láti inú rẹ̀. Dájúdájú ó tún ń bẹ nínú wọn tí ó ń wó lulẹ̀ gbì fún ìpáyà Allāhu. Allāhu kò sì níí gbàgbé ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(2,75,'2:75','Ṣé ẹ̀ ń rankàn pé wọn yóò gbà yín gbọ́, nígbà tí ó jẹ́ pé igun kan nínú wọn kúkú ń gbọ́ ọ̀rọ̀ Allāhu. Lẹ́yìn náà, wọ́n á yí i padà sódì lẹ́yìn tí wọ́n ti gbọ́ ọ yé; wọ́n sì mọ̀.');
INSERT INTO "aykyuni" VALUES(2,76,'2:76','Nígbà tí wọ́n bá sì pàdé àwọn tó gbàgbọ́ ní òdodo, wọ́n á wí pé: “A gbàgbọ́.” Nígbà tí ó bá sì ku apá kan wọn ku apá kan, wọ́n á wí pé: “Ṣé kì í ṣe pé ẹ̀ ń bá wọn sọ̀rọ̀ nípa ohun tí Allāhu ti ṣípayá rẹ̀ fún yín, kí wọ́n lè fi jà yín níyàn ní ọ̀dọ̀ Olúwa yín? Ṣé ẹ ò ṣe làákàyè ni!”');
INSERT INTO "aykyuni" VALUES(2,77,'2:77','Ṣé wọn kò mọ̀ pé dájúdájú Allāhu mọ ohun tí wọ́n ń fi pamọ́ àti ohun tí wọ́n ń ṣàfi hàn rẹ̀?');
INSERT INTO "aykyuni" VALUES(2,78,'2:78','Àti pé ó wà nínú wọn, àwọn aláìmọ̀ọ́nkọmọ̀ọ́nkà, tí wọn kò nímọ̀ nípa Tírà àfi àwọn irọ́. Kí ni wọ́n (ń sọ) bí kò ṣe pé wọ́n ń dá àbá (àròsọ).');
INSERT INTO "aykyuni" VALUES(2,79,'2:79','Nítorí náà, ègbé ni fún àwọn tó ń fi ọwọ́ ara wọn kọ tírà, lẹ́yìn náà tí wọ́n ń wí pé: “Èyí wá láti ọ̀dọ̀ Allāhu.” Nítorí kí wọ́n lè tà á ní owó pọ́ọ́kú. Ègbé ni fún wọn sẹ́ nípa ohun tí ọwọ́ wọn kọ. Ègbé sì ni fún wọn pẹ̀lú nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,80,'2:80','Wọ́n sì wí pé: “Iná kò lè fọwọ́ bà wá tayọ ọjọ́ tó lóǹkà.” Sọ pé: “Ṣé ẹ ti rí àdéhùn kan gbà lọ́dọ̀ Allāhu ni?”Allāhu kò sì níí yapa àdéhùn Rẹ̀. Àbí ṣé ẹ̀yin yóò máa pa irọ́ ohun tí ẹ kò nímọ̀ nípa rẹ̀ mọ́ Allāhu ni?');
INSERT INTO "aykyuni" VALUES(2,81,'2:81','Rárá (Iná kò rí bí wọ́n ṣe rò ó sí); ẹnikẹ́ni tí ó bá ṣe iṣẹ́ ibi kan, tí àwọn ẹ̀ṣẹ̀ rẹ̀ tún yí i ká, àwọn wọ̀nyẹn ni èrò inú Iná. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(2,82,'2:82','Àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, àwọn wọ̀nyẹn ni èrò inú Ọgbà Ìdẹ̀ra. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(2,83,'2:83','(Ẹ rántí) nígbà tí A gba àdéhùn lọ́wọ́ àwọn ọmọ ’Isrọ̄’īl pé, ẹ̀yin kò gbọdọ̀ jọ́sìn fún ọlọ́hun kan àyàfi Allāhu. Kí ẹ sì ṣe dáadáa sí àwọn òbí méjèèjì, ìbátan, àwọn ọmọ òrukàn àti àwọn mẹ̀kúnnù.Ẹ bá àwọn ènìyàn sọ̀rọ̀ rere. Ẹ kírun, kí ẹ sì yọ Zakāh. Lẹ́yìn náà lẹ pẹ̀yìn dà àfi díẹ̀ nínú yín. Ẹ̀yin sì ń gbúnrí (kúrò níbi àdéhùn). ');
INSERT INTO "aykyuni" VALUES(2,84,'2:84','(Ẹ rántí) nígbà tí A gba àdéhùn lọ́wọ́ yínpé, ẹ̀yin kò gbọdọ̀ ta ẹ̀jẹ̀ yín sílẹ̀, ẹ̀yin kò sì gbọdọ̀ léra yín jáde kúrò nínú ilé yín. Lẹ́yìn náà, ẹ fi rinlẹ̀, ẹ sì ń jẹ́rìí sí i. ');
INSERT INTO "aykyuni" VALUES(2,85,'2:85','Lẹ́yìn náà, ẹ̀yin wọ̀nyí lẹ̀ ń pa ara yín. Ẹ tún ń lé apá kan nínú yín jáde kúrò nínú ilé wọn. Ẹ̀ ń fi ẹ̀ṣẹ̀ àti àbòsí ṣèrànwọ́ (fún àwọn ọ̀tá) lórí wọn. Tí wọ́n bá sì wá ba yín (tí wọ́n ti di) ẹrú, ẹ̀yin ń rà wọ́n (láti fi’ra yín ṣẹrú). Èèwọ̀ sì fẹ̀ẹ̀kan ni fún yín láti lé wọn jáde. Ṣé ẹ̀yin yóò gba apá kan Tírà gbọ́, ẹ sì ń ṣàì gbàgbọ́ nínú apá kan? Nítorí náà, kí ni ẹ̀san fún ẹni tó ṣè yẹn nínú yín bí kò ṣe àbùkù nínú ìṣẹ̀mí ayé. Ní ọjọ́ Àjíǹde, wọ́n sì máa dá wọn padà sínú ìyà tó le jùlọ. Allāhu kò sì níí gbàgbé ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,86,'2:86','Àwọn wọ̀nyẹn ni àwọn tí wọ́n fi ọ̀run ra ìṣẹ̀mí ayé.Nítorí náà, A ò níí ṣe ìyà ní fífúyẹ́ fún wọn, A ò sì níí ràn wọ́n lọ́wọ́. ');
INSERT INTO "aykyuni" VALUES(2,87,'2:87','Dájúdájú A ti fún (Ànábì) Mūsā ní Tírà. A sì mú àwọn Òjíṣẹ́ wá ní tẹ̀léǹtẹ̀lé lẹ́yìn rẹ̀. A fún (Ànábì) ‘Īsā ọmọ Mọryam ní àwọn ẹ̀rí tó yanjú.A sì fi Ẹ̀mí Mímọ́ (ìyẹn, Mọlāika Jibrīl) ràn án lọ́wọ́. Ṣé gbogbo ìgbà tí Òjíṣẹ́ kan bá wá ba yín pẹ̀lú ohun tí ọkàn yín kò fẹ́ ni ẹ ó máa ṣègbéraga? Ẹ sì pe apá kan (àwọn Ànábì) ní òpùrọ́, ẹ sì ń pa apá kan! ');
INSERT INTO "aykyuni" VALUES(2,88,'2:88','Wọ́n sì wí pé: “Èbìbò bò wá lọ́kàn.” Kò sì rí bẹ́ẹ̀. Allāhu ti ṣẹ́bi lé wọn ni, nítorí àìgbàgbọ́ wọn. Ṣebí díẹ̀ ni wọ́n ń gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(2,89,'2:89','Nígbà tí Tírà kan sì dé bá wọn láti ọ̀dọ̀ Allāhu, tí ó ń fi ìdí òdodo múlẹ̀ nípa ohun tí ó wà pẹ̀lú wọn (ìyẹn tírà Taorāt), bẹ́ẹ̀ sì ni tẹ́lẹ̀tẹ́lẹ̀ wọ́n ti ń tọrọ ìṣẹ́gun lórí àwọn tó ṣàì gbàgbọ́, àmọ́ nígbà tí ohun tí wọ́n nímọ̀ nípa rẹ̀ dé bá wọn, wọ́n ṣàì gbàgbọ́ nínú rẹ̀. Nítorí náà, ibi dandan Allāhu kí ó máa bá àwọn aláìgbàgbọ́.1 ');
INSERT INTO "aykyuni" VALUES(2,90,'2:90','Aburú ni ohun tí wọ́n rà fún ẹ̀mí ara wọn nípa bí wọ́n ṣe ṣàì gbàgbọ́ nínú ohun tí Allāhu sọ̀kalẹ̀, ní ti ìlara pé Allāhu ń sọ (Tírà) kalẹ̀ nínú oore àjùlọ Rẹ̀ fún ẹni tí Ó fẹ́ nínú àwọn ẹrúsìn Rẹ̀. Wọ́n sì padà pẹ̀lú ìbínú (mìíràn) lórí ìbínú (Allāhu tí ó ti wà lórí wọn tẹ́lẹ̀). Ìyà tí í yẹpẹrẹ (ẹ̀dá) sì ń bẹ fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(2,91,'2:91','Nígbà tí wọ́n bá sọ fún wọn pé: “Ẹ gbàgbọ́ nínú ohun tí Allāhu sọ̀kalẹ̀.” Wọ́n á wí pé: “A gbàgbọ́ nínú ohun tí wọ́n sọ̀kalẹ̀ fún wa.” Wọ́n sì ń ṣàì gbàgbọ́ nínú ohun tó wà lẹ́yìn rẹ̀, òhun sì ni òdodo, tí ó ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó wà pẹ̀lú wọn. Sọ pé: “Nítorí kí ni ẹ fi ń pa àwọn Ànábì Allāhu tẹ́lẹ̀tẹ́lẹ̀ tí ẹ bá jẹ́ onígbàgbọ́ òdodo?');
INSERT INTO "aykyuni" VALUES(2,92,'2:92','Àti pé dájúdájú (Ànábì) Mūsā ti mú àwọn ẹ̀rí tó yanjú wá ba yín. Lẹ́yìn náà, ẹ tún bọ ọ̀bọrọgidi ọmọ màálù lẹ́yìn rẹ̀. Alábòsí sì ni yín.”');
INSERT INTO "aykyuni" VALUES(2,93,'2:93','(Ẹ rántí) nígbà tí A gba àdéhùn lọ́wọ́ yín, A sì gbé àpáta s’ókè orí yín, (A sọ pé:) “Ẹ gbá ohun tí A fún yín mú dáradára. Kí ẹ sì tẹ́tí gbọ́rọ̀.” Wọ́n wí pé: “A gbọ́ (àṣẹ), a sì yapa (rẹ̀).” Wọ́n ti kó ìfẹ́ bíbọ ọ̀bọrọgidi ọmọ màálù sínú ọkàn wọn nípasẹ̀ àìgbàgbọ́ wọn. Sọ pé: “Aburú ni ohun tí ìgbàgbọ́ (ìbọ̀rìṣà) yín ń pa yín láṣẹ rẹ̀, tí ẹ bá jẹ́ onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(2,94,'2:94','Sọ pé: “Tí ó bá jẹ́ pé tiyín nìkan ni Ilé Ìkẹ́yìn tí ń bẹ ní ọ̀dọ̀ Allāhu, tí kò sì níí jẹ́ ti àwọn ènìyàn (mìíràn), ẹ tọrọ ikú, tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(2,95,'2:95','Wọn kò níí tọrọ rẹ̀ láéláé nítorí ohun tí ọwọ́ wọn tì síwájú. Allāhu sì ni Onímọ̀ nípa àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(2,96,'2:96','Dájúdájú o máa rí wọn pé àwọn ni ènìyàn tó l’ójú kòkòrò jùlọ nípa ìṣẹ̀mí ayé, (wọ́n tún l’ójú kòkòrò ju) àwọn ọ̀ṣẹbọ lọ. Ìkọ̀ọ̀kan wọn ń fẹ́ pé tí A bá lè fún òun ní ẹgbẹ̀rún ọdún lò láyé. Bẹ́ẹ̀ sì ni, kì í ṣe ohun tí ó máa là á nínú ìyà ni pé, kí Á fún un ní ìṣẹ̀mí gígùn lò. Allāhu sì ni Olùríran nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,97,'2:97','Sọ pé: “Ẹnikẹ́ni tí ó bá jẹ́ ọ̀tá fún (mọlāika) Jibrīl,(ó ti di ọ̀tá Allāhu) nítorí pé, dájúdájú (mọlāika) Jibrīl ló sọ al-Ƙur’ān kalẹ̀ sínú ọkàn rẹ pẹ̀lú àṣẹ Allāhu. Al-Ƙur’ān sì ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó wá ṣíwájú rẹ̀. Ó jẹ́ ìmọ̀nà àti ìdùnnú fún àwọn onígbàgbọ́ òdodo. ');
INSERT INTO "aykyuni" VALUES(2,98,'2:98','Ẹnikẹ́ni tí ó bá jẹ́ ọ̀tá fún Allāhu, àwọn mọlāika Rẹ̀, àwọn Òjíṣẹ́ Rẹ̀, mọlāika Jibrīl àti mọlāika Mīkāl, dájúdájú Allāhu ni ọ̀tá fún àwọn aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(2,99,'2:99','Dájúdájú A ti sọ àwọn āyah tó yanjú kalẹ̀ fún ọ. Ẹnì kan kò níí ṣàì gbàgbọ́ nínú rẹ̀ àyàfi àwọn arúfin.');
INSERT INTO "aykyuni" VALUES(2,100,'2:100','Àti pé ṣé gbogbo ìgbà tí wọ́n bá dá májẹ̀mu kan ni apá kan nínú wọn yóò máa jù ú nù? Rárá, ọ̀pọ̀lọ́pọ̀ wọn gan-an ni kò gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(2,101,'2:101','Àti pé nígbà tí Òjíṣẹ́ kan láti ọ̀dọ̀ Allāhu dé bá wọn, tí ó ń jẹ́rìí sí èyí tó jẹ́ òdodo nínú ohun tó wà pẹ̀lú wọn, apá kan nínú àwọn tí A fún ní Tírà gbé Tírà Allāhu jù s’ẹ́yìn lẹ́yìn wọn bí ẹni pé wọn kò mọ̀ (pé àsọọ́lẹ̀ nípa Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a - wà nínú rẹ̀)!');
INSERT INTO "aykyuni" VALUES(2,102,'2:102','Wọ́n sì tẹ̀lé ohun tí àwọn èṣù àlùjànnú ń kà (fún wọn nínú idán lásìkò) ìjọba (Ànábì) Sulaemọ̄n. (Ànábì) Sulaemọ̄n kò sì di kèfèrí, ṣùgbọ́n àwọn èṣù àlùjànnú ni wọ́n di kèfèrí, wọ́n ń kọ́ àwọn ènìyàn ní idán àti ohun tí wọ́n sọ̀kalẹ̀ fún àwọn mọlāika méjèèjì, Hārūt àti Mọ̄rūt ní ìlú Bābil.Àwọn mọlāika méjèèjì náà kò sì níí kọ́ ẹnikẹ́ni àyàfi kí wọ́n sọ pé: “Àdánwò ni wá. Nítorí náà, má di kèfèrí.” Wọ́n sì ń kẹ́kọ̀ọ́ ohun tí wọn yóò fi ṣòpínyà láààrin ọmọnìyàn àti ẹnì kejì rẹ̀ lọ́dọ̀ wọn. - Wọn kò sì lè kó ìnira bá ẹnikẹ́ni àyàfi pẹ̀lú ìyọ̀ǹda Allāhu. - Wọ́n ń kẹ́kọ̀ọ́ ohun tí ó máa kó ìnira bá wọn, tí kò sì níí ṣe wọ́n ní àǹfààní. Wọ́n kúkú ti mọ̀ pé ẹnikẹ́ni tí ó bá ra idán, kò níí sí ìpín rere kan fún un ní Ọjọ́ Ìkẹ́yìn. Aburú sì ni ohun tí wọ́n rà fún ẹ̀mí ara wọn tí ó bá jẹ́ pé wọ́n mọ̀. ');
INSERT INTO "aykyuni" VALUES(2,103,'2:103','Tí ó bá jẹ́ pé dájúdájú wọ́n gbàgbọ́ ní òdodo, tí wọ́n sì bẹ̀rù (Allāhu), dájúdájú ẹ̀san tí ó máa wá láti ọ̀dọ̀ Allāhu l’óore jùlọ (fún wọn), tí wọ́n bá mọ̀.');
INSERT INTO "aykyuni" VALUES(2,104,'2:104','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má sọ pé “rọ̄‘inā”. (Àmọ́) ẹ sọ pé “unṭḥurnā” kí ẹ sì máa tẹ́tí gbọ́ (ọ̀rọ̀ ẹ̀sìn). Ìyà ẹlẹ́ta-eléro sì wà fún àwọn aláìgbàgbọ́. ');
INSERT INTO "aykyuni" VALUES(2,105,'2:105','Àwọn tó ṣàì gbàgbọ́ nínú àwọn tí A fún ní Tírà àti àwọn ọ̀ṣẹbọ kò níí fẹ́ kí wọ́n sọ oore kan kan kalẹ̀ fún yín láti ọ̀dọ̀ Olúwa yín. Allāhu ló ń fi ìkẹ́ Rẹ̀ ṣa ẹni tí Ó bá fẹ́ lẹ́ṣà. Allāhu sì ni Olóore ńlá.');
INSERT INTO "aykyuni" VALUES(2,106,'2:106','Ohunkóhun tí A bá parẹ́ (tàbí pààrọ̀) nínú āyah kantàbí tí A sọ di ìgbàgbé, A máa mú èyí tó dára jù ú lọ tàbí irú rẹ̀ wá. Ṣé ìwọ kò mọ̀ pé dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan? ');
INSERT INTO "aykyuni" VALUES(2,107,'2:107','Ṣé ìwọ kò mọ̀ pé dájúdájú ti Allāhu ni ìjọba àwọn sánmọ̀ àti ilẹ̀? Kò sì sí aláàbò àti alárànṣe kan fún yín lẹ́yìn Allāhu.');
INSERT INTO "aykyuni" VALUES(2,108,'2:108','Tàbí ẹ fẹ́ máa bèèrè (ọ̀rọ̀kọ́rọ̀) lọ́wọ́ Òjíṣẹ́ yín ni gẹ́gẹ́ bí wọ́n ṣe bèèrè (ọ̀rọ̀kọ́rọ̀) lọ́wọ́ (Ànábì) Mūsā ṣíwájú? Ẹnikẹ́ni tí ó bá fi àìgbàgbọ́ rọ́pò ìgbàgbọ́, dájúdájú ó ti ṣìnà (kúrò) l’ójú ọ̀nà tààrà.');
INSERT INTO "aykyuni" VALUES(2,109,'2:109','Ọ̀pọ̀lọ́pọ̀ nínú àwọn tí A fún ní Tírà fẹ́ láti da yín padà sípò kèfèrí lẹ́yìn tí ẹ ti ní ìgbàgbọ́ òdodo, ní ti kèéta láti inú ẹ̀mí wọn, (àti) lẹ́yìn tí òdodo (’Islām) ti fojú hàn sí wọn. Nítorí náà, ẹ foríjìn wọ́n, kí ẹ ṣàmójú kúrò fún wọn (nípa ìnira tí wọ́n ń fi kàn yín) títí Allāhu yó fi mú àṣẹ Rẹ̀ wá (láti jà wọ́n lógun). Dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(2,110,'2:110','Ẹ kírun, ẹ yọ Zakāh; ohunkóhun tí ẹ bá sì tì síwájú fún ẹ̀mí ara yín ní rere, ẹ máa bá a lọ́dọ̀ Allāhu. Dájúdájú Allāhu ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,111,'2:111','Wọ́n wí pé: “Ẹnì kan kò níí wọ inú Ọgbà Ìdẹ̀ra àfi ẹni tí ó bá jẹ́ yẹhudi tàbí nasọ̄rọ̄.” Ìyẹn ni ohun tí wọ́n ń fẹ́. Sọ pé: “Ẹ mú ẹ̀rí ọ̀rọ̀ yín wá tí ẹ bá jẹ́ olódodo.');
INSERT INTO "aykyuni" VALUES(2,112,'2:112','Bẹ́ẹ̀ kọ́ (wọn kò níí wọ inú Ọgbà Ìdẹ̀ra àfi) ẹni tí ó bá ju ara rẹ̀ sílẹ̀ (sínú ’Islām) fún Allāhu,tí ó sì jẹ́ olùṣe rere. Nítorí náà, ẹ̀san (rere) rẹ̀ ń bẹ fún un lọ́dọ̀ Olúwa rẹ̀. Kò níí sí ìbẹ̀rù fún wọn.2 Wọn kò sì níí banújẹ́.3 ');
INSERT INTO "aykyuni" VALUES(2,113,'2:113','Àwọn yẹhudi wí pé: “Àwọn nasọ̄rọ̄ kò rí n̄ǹkan kan ṣe (nínú ẹ̀sìn).” Àwọn nasọ̄rọ̄ náà wí pé: “Àwọn yẹhudi kò rí n̄ǹkan kan ṣe (nínú ẹ̀sìn.)” Wọ́n sì ń ké Tírà! Báyẹn ni àwọn tí kò nímọ̀ ṣe sọ irú ọ̀rọ̀ wọn (yìí). Nítorí náà, Allāhu á dájọ́ láààrin wọn ní Ọjọ́ Àjíǹde nípa ohun tí wọ́n ń yapa-ẹnu lórí rẹ̀.');
INSERT INTO "aykyuni" VALUES(2,114,'2:114','Àti pé ta ló ṣàbòsí ju ẹni tí ó ṣe àwọn mọ́sálásí Allāhu ní èèwọ̀ láti ṣèrántí orúkọ Allāhu nínú rẹ̀, tí ó tún ṣiṣẹ́ lórí ìparun àwọn mọ́sálásí náà?Àwọn wọ̀nyẹn, kò lẹ́tọ̀ọ́ fún wọn láti wọ inú rẹ̀ àyàfi pẹ̀lú ìbẹ̀rù. Àbùkù ń bẹ fún wọn n’ílé ayé. Ìyà ńlá sì ń bẹ fún wọn ní ọ̀run. ');
INSERT INTO "aykyuni" VALUES(2,115,'2:115','Ti Allāhu ni ibùyọ òòrùn àti ibùwọ̀ òòrùn. Nítorí náà, ibikíbi tí wọ́n bá dojú yín kọ ibẹ̀ yẹn náà ni ojú rere Allāhu (ìyẹn ni pé, ẹ ti dojúkọ Allāhu ní ibi tí wọ́n bá pa yín láṣẹ pé kí ẹ dojúkọ kírun.) Dájúdájú Allāhu ni Olùgbààyè, Onímọ̀.1 ');
INSERT INTO "aykyuni" VALUES(2,116,'2:116','Wọ́n wí pé: “Allāhu sọ ẹnì kan di ọmọ.” Mímọ́ ni fún Un! Ọ̀rọ̀ kò rí bẹ́ẹ̀, (àmọ́) tiRẹ̀ ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀. Ẹnì kọ̀ọ̀kan sì ni olùtẹ̀lé-àṣẹ Rẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(2,117,'2:117','Olùpilẹ̀dá àwọn sánmọ̀ àti ilẹ̀ ni (Allāhu). Nígbà tí Ó bá sì pèbùbù kiní kan, Ó kàn máa sọ fún un pé: “Jẹ́ bẹ́ẹ̀.” Ó sì máa jẹ́ bẹ́ẹ̀.');
INSERT INTO "aykyuni" VALUES(2,118,'2:118','Àwọn tí kò nímọ̀ wí pé: “Tí ó bá jẹ́ pé Allāhu ń bá wa sọ̀rọ̀ ni tàbí kí àmì kan wá bá wa (àwa ìbá gbàgbọ́)?” Báyẹn ni àwọn tó ṣíwájú wọn ṣe sọ irú ọ̀rọ̀ wọn (yìí). Ọkàn wọn jọra wọn. A kúkú ti ṣe àlàyé àwọn āyah fún ìjọ tó ní àmọ̀dájú.');
INSERT INTO "aykyuni" VALUES(2,119,'2:119','Dájúdájú Àwa fi òdodo rán ọ níṣẹ́. (O sì jẹ́) oníròó-ìdùnnú àti olùkìlọ̀ (fún gbogbo ayé). Wọn kò sì níí bi ọ́ léèrè nípa àwọn èrò inú Iná.');
INSERT INTO "aykyuni" VALUES(2,120,'2:120','Àwọn yẹhudi àti nasọ̄rọ̄ kò níí yọ́nú sí ọ títí o fi máa tẹ̀lé ẹ̀sìn wọn. Sọ pé: “Dájúdajú ìmọ̀nà ti Allāhu ni ìmọ̀nà.” Dájúdajú tí o bá sì tẹ̀lé ìfẹ́-inú wọn lẹ́yìn èyí tí ó dé bá ọ nínú ìmọ̀ (’Islām), kò níí sí aláàbò àti alárànṣe kan fún ọ lọ́dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(2,121,'2:121','Àwọn tí A fún ní Tírà (al-Ƙur’ān), wọ́n ń ké e ní kíké ẹ̀tọ́. Àwọn wọ̀nyẹn gbà á gbọ́ ní òdodo. Àwọn tó ṣàì gbàgbọ́ nínú rẹ̀, àwọn wọ̀nyẹn gan-an ni ẹni òfò.');
INSERT INTO "aykyuni" VALUES(2,122,'2:122','Ẹ̀yin ọmọ ’Isrọ̄’īl, ẹ rántí ìkẹ́ Mi, èyí tí Mo ṣe fún yín. Dájúdájú Èmi tún ṣoore àjùlọ fún yín lórí àwọn ẹ̀dá (àsìkò tiyín).1 ');
INSERT INTO "aykyuni" VALUES(2,123,'2:123','Ẹ bẹ̀rù ọjọ́ kan tí ẹ̀mí kan kò níí rọrọ̀ kiní kan fún ẹ̀mí kan. A ò sì níí gba ààrọ̀ l’ọ́wọ́ rẹ̀. Ìṣìpẹ̀ kan kò níí wúlò fún un. A ò sì níí ràn wọ́n lọ́wọ́.1 ');
INSERT INTO "aykyuni" VALUES(2,124,'2:124','(Ẹ rántí) nígbà tí Olúwa fi àwọn ọ̀rọ̀ kan dán (Ànábì) ’Ibrọ̄hīm wò. Ó sì parí wọn ní pípé. (Allāhu) sọ pé: “Dájúdájú Èmi yó ṣe ọ́ ní aṣíwájú fún àwọn ènìyàn.” (Ànábì ’Ibrọ̄hīm) sọ pé: “Àti nínú àwọn àrọ́mọdọ́mọ mi.” (Allāhu) sọ pé: “Àdéhùn Mi (láti sọ ẹnì kan di Òjíṣẹ́ tàbí Ànábì) kò níí tẹ àwọn alábòsí lọ́wọ́.”');
INSERT INTO "aykyuni" VALUES(2,125,'2:125','(Ẹ rántí) nígbà tí A ṣe Ilé (Kaaba) ní àyè tí àwọn ènìyàn yóò máa wá àti àyè ìfàyàbalẹ̀. Kí ẹ sì mú ibùdúró ’Ibrọ̄hīm ní ibùkírun. A sì pa (Ànábì) ’Ibrọ̄hīm àti (Ànábì) ’Ismọ̄‘īl láṣẹ pé “Ẹ ṣe Ilé Mi ní mímọ́ fún àwọn olùyípo rẹ̀, àwọn olùkóraró sínú rẹ̀ àti àwọn olùdáwọ́tẹ-orúnkún, àwọn olùforíkanlẹ̀ (lórí ìrun).');
INSERT INTO "aykyuni" VALUES(2,126,'2:126','(Ẹ rántí) nígbà tí (Ànábì) ’Ibrọ̄hīm sọ pé: “Olúwa mi, ṣe ìlú yìí ní ìlú ìfàyàbalẹ̀. Kí O sì pèsè àwọn èso fún àwọn ará ibẹ̀ (ìyẹn) ẹnikẹ́ni nínú wọn tí ó bá ní ìgbàgbọ́ òdodo nínú Allāhu àti Ọjọ́ Ìkẹ́yìn.” (Allāhu) sọ pé: “Àti ẹni tí ó bá ṣàì gbàgbọ́, Èmi yóò fún un ní ìgbádùn díẹ̀. Lẹ́yìn náà, Mo máa taari rẹ̀ sínú ìyà Iná. Ìkángun náà sì burú.”');
INSERT INTO "aykyuni" VALUES(2,127,'2:127','(Ẹ rántí) nígbà tí (Ànábì) ’Ibrọ̄hīm àti ’Ismọ̄‘īl gbé àwọn ìpìlẹ̀ Ilé náà dúró. (Wọ́n ṣàdúà pé) “Olúwa wa, gbà á lọ́wọ́ wa, dájúdájú Ìwọ, Ìwọ ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(2,128,'2:128','Olúwa wa, ṣe wá ní mùsùlùmí fún Ọ. Kí O sì ṣe nínú àrọ́mọdọ́mọ wa ní ìjọ mùsùlùmí fún Ọ. Fi ìlànà ẹ̀sìn wa hàn wá. Kí O sì gba ìronúpìwàdà wa. Dájúdájú Ìwọ, Ìwọ ni Olùgba-ìronúpìwàdà, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,129,'2:129','Olúwa wa, gbé dìde nínú wọn Òjíṣẹ́ kan láààrin wọn, (ẹni tí) ó máa ké àwọn āyah Rẹ fún wọn, tí ó máa kọ́ wọn ní Tírà àti ọgbọ́n ìjìnlẹ̀ (sunnah), tí ó sì máa sọ wọ́n di ẹni mímọ́. Dájúdájú Ìwọ, Ìwọ ni Alágbára, Ọlọ́gbọ́n.”');
INSERT INTO "aykyuni" VALUES(2,130,'2:130','Ta sì ni ó máa kọ ẹ̀sìn (Ànábì) ’Ibrọ̄hīm sílẹ̀ àfi ẹni tí ó bá gọ ẹ̀mí ara rẹ̀. A kúkú ti ṣà á lẹ́ṣà n’ílé ayé. Dájúdájú ó tún wà nínú àwọn ẹni rere ní ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,131,'2:131','(Ẹ rántí) nígbà tí Olúwa rẹ̀ sọ fún un pé: “Jẹ́ mùsùlùmí.” Ó sọ pé: “Mo jẹ́ mùsùlùmí fún Olúwa gbogbo ẹ̀dá.”');
INSERT INTO "aykyuni" VALUES(2,132,'2:132','(Ànábì) ’Ibrọ̄hīm sì pa á láṣẹ fún àwọn ọmọ rẹ̀. (Ànábì) Ya‘ƙūb náà ṣe bẹ́ẹ̀. (Ìkíní kejì sọ pé): “Ẹ̀yin ọmọ mi, dájúdájú Allāhu yan ẹ̀sìn náà fún yín. Nítorí náà, ẹ ò gbọdọ̀ kú àyàfi kí ẹ jẹ́ mùsùlùmí.”');
INSERT INTO "aykyuni" VALUES(2,133,'2:133','Tàbí ẹ̀yín jẹ́ ẹlẹ́rìí nígbà tí ikú dé bá (Ànábì) Ya‘ƙūb? Nígbà tí ó sọ fún àwọn ọmọ rẹ̀ pé: “Kí ni ẹ̀yin yóò máa jọ́sìn fún lẹ́yìn (ikú) mi?” Wọ́n sọ pé: “Àwa yó máa jọ́sìn fún Ọlọ́hun rẹ àti Ọlọ́hun àwọn bàbá rẹ, (àwọn Ànábì) ’Ibrọ̄hīm, ’Ismọ̄‘īl àti ’Ishāƙ, Ọlọ́hun Ọ̀kan ṣoṣo tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo. Àwa sì ni mùsùlùmí (olùjuwọ́-jusẹ̀-sílẹ̀) fún Un.”');
INSERT INTO "aykyuni" VALUES(2,134,'2:134','Ìjọ kan nìyẹn tí ó ti lọ. Tiwọn ni ohun tí wọ́n ṣe níṣẹ́. Tiyín ni ohun tí ẹ ṣe níṣẹ́. Wọn kò sì níí bi yín léèrè nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,135,'2:135','Wọ́n wí pé: “Ẹ jẹ́ yẹhudi tàbí nasọ̄rọ̄ kí ẹ mọ̀nà.” Sọ pé: “Rárá, ẹ̀sìn (Ànábì) ’Ibrọ̄hīm (lẹ̀sìn), ó jẹ́ olùdúró-déédé, kò sì sí nínú àwọn ọ̀ṣẹbọ.”');
INSERT INTO "aykyuni" VALUES(2,136,'2:136','Ẹ sọ pé: “A gbàgbọ́ nínú Allāhu àti ohun tí wọ́n sọ̀kalẹ̀ fún wa àti ohun tí wọ́n sọ̀kalẹ̀ fún (àwọn Ànábì) ’Ibrọ̄hīm, ’Ismọ̄‘īl, ’Ishāƙ, Ya‘ƙūb àti àwọn àrọ́mọdọ́mọ Ya‘ƙūb. (A gbàgbọ́ nínú) ohun tí wọ́n fún (àwọn Ànábì) Mūsā àti ‘Īsā, àti ohun tí wọ́n fún àwọn Ànábì (yòókù) láti ọ̀dọ̀ Olúwa wọn. A kò ya ẹnì kan sọ́tọ̀ nínú wọn. Àwa sì ni mùsùlùmí (olùjuwọ́-jusẹ̀-sílẹ̀) fún Un.”');
INSERT INTO "aykyuni" VALUES(2,137,'2:137','Nítorí náà, tí wọ́n bá gbàgbọ́ nínú irú ohun tí ẹ gbàgbọ́, wọ́n ti mọ̀nà. Tí wọ́n bá sì gbúnrí, wọ́n ti wà nínú ìyapa (òdodo). Allāhu sì máa tó ọ (níbi aburú) wọn. Òun ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(2,138,'2:138','(Ẹ dúró ti) aró Allāhu. Ta sì ló dára ju Allāhu lọ ní aró? Àwa sì ni olùjọ́sìn fún Un.1 ');
INSERT INTO "aykyuni" VALUES(2,139,'2:139','Sọ pé: “Ṣé ẹ̀yin yóò jà wá níyàn nípa Allāhu ni?” Òun sì ni Olúwa wa àti Olúwa yín. Tiwa ni àwọn iṣẹ́ wa. Tiyín sì ni àwọn iṣẹ́ yín. Àwa (mùsùlùmí) sì ni olùṣàfọ̀mọ́-ẹ̀sìn fún Un.');
INSERT INTO "aykyuni" VALUES(2,140,'2:140','Tàbí ẹ̀ ń wí pé: “Dájúdájú (àwọn Ànábì) ’Ibrọ̄hīm, ’Ismọ̄‘īl, ’Ishāƙ, Ya‘ƙūb àti àwọn àrọ́mọdọ́mọ Ya‘ƙūb, wọ́n jẹ́ yẹhudi tàbí nasọ̄rọ̄.”Sọ pé: “Ṣé ẹ̀yin lẹ nímọ̀ jùlọ (nípa wọn ni) tàbí Allāhu?” Ta ló ṣàbòsí ju ẹni tó daṣọ bo ẹ̀rí ọ̀dọ̀ rẹ̀ (tí ó sọ̀kalẹ̀) láti ọ̀dọ̀ Allāhu? Allāhu kò sì níí gbàgbé ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(2,141,'2:141','Ìjọ kan nìyẹn tí ó ti lọ. Tiwọn ni ohun tí wọ́n ṣe níṣẹ́. Tiyín ni ohun tí ẹ ṣe níṣẹ́. Wọn kò sì níí bi yín léèrè nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,142,'2:142','Àwọn òmùgọ̀ nínú àwọn ènìyàn máa wí pé: “Kí ni ó mú wọn yí kúrò níbi Ƙiblah wọn tí wọ́n ti wà tẹ́lẹ̀?” Sọ pé: “Ti Allāhu ni ibùyọ òòrùn àti ibùwọ̀ òòrùn. Ó sì ń tọ́ ẹni tí Ó bá fẹ́ sí ọ̀nà tààrà (’Islām).”');
INSERT INTO "aykyuni" VALUES(2,143,'2:143','Báyẹn ni A ṣe yín ní ẹ̀ṣà ìjọ tó lóore jùlọ, nítorí kí ẹ lè jẹ́ ẹlẹ́rìí fún àwọn ènìyàn àti nítorí kí Òjíṣẹ́ náà lè jẹ́ ẹlẹ́rìí fún yín. A kò sì ṣe Ƙiblah tí o wà lórí rẹ̀ tẹ́lẹ̀ ní ibùkọjú-kírun bí kò ṣe pé nítorí kí Á lè ṣe àfihàn ẹni tí ó máa tẹ̀lé Òjíṣẹ́ yàtọ̀ sí ẹni tí ó máa yísẹ̀ padà. Dájúdájú ó lágbára àyàfi fún àwọn tí Allāhu tọ́ sọ́nà. Allāhu kò sì níí fi ìgbàgbọ́ yín ráre. Dájúdájú Allāhu ni Aláàánú, Oníkẹ̀ẹ́ fún àwọn ènìyàn.');
INSERT INTO "aykyuni" VALUES(2,144,'2:144','A kúkú rí yíyí tí ò ń yí ojú rẹ sí sánmọ̀. Nítorí náà, dájúdájú A óò dojú rẹ kọ Ƙiblah kan tí o yọ́nú sí; nítorí náà, kọjú rẹ sí agbègbè Mọ́sálásí Haram (ní Mọkkah). Ibikíbi tí ẹ bá tún wà, ẹ kọjú yín sí agbègbè rẹ̀. Dájúdájú àwọn tí A fún ní Tírà kúkú mọ̀ pé dájúdájú òdodo ni (àṣẹ Ƙiblah) láti ọ̀dọ̀ Olúwa wọn. Allāhu kò sì níí gbàgbé ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,145,'2:145','Dájúdájú tí o bá fún àwọn tí A fún ní Tírà ní gbogbo āyah, wọn kò níí tẹ̀lé Ƙiblah rẹ. Ìwọ náà kò gbọdọ̀ tẹ̀lé Ƙiblah wọn. Apá kan wọn kò sì níí tẹ̀lé Ƙiblah apá kan. Dájúdájú tí o bá tẹ̀lé ìfẹ́-inú wọn lẹ́yìn ohun tí ó dé bá ọ nínú ìmọ̀, dájúdájú nígbà náà ìwọ wà nínú àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(2,146,'2:146','Àwọn tí A fún ní Tírà, wọ́n mọ Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a - gẹ́gẹ́ bí wọ́n ṣe mọ àwọn ọmọ wọn. Dájúdájú àwọn ìjọ kan wà nínú wọn tí wọ́n kúkú ń fi òdodo pamọ́, wọ́n sì mọ̀.');
INSERT INTO "aykyuni" VALUES(2,147,'2:147','Òdodo náà wá láti ọ̀dọ̀ Olúwa rẹ. Nítorí náà, o ò gbọdọ̀ wà lára àwọn oníyèméjì.');
INSERT INTO "aykyuni" VALUES(2,148,'2:148','Ìkọ̀ọ̀kan (ìjọ ẹlẹ́sìn) ló ní ibùkọjúsí tó ń kọjú sí. Nítorí náà, ẹ yára gbawájú níbi àwọn iṣẹ́ rere. Ibikíbi tí ẹ bá wà, Allāhu yóò mú gbogbo yín wá (ní Ọjọ́ Àjíǹde). Dájúdájú Alágbára ni Allāhu lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(2,149,'2:149','Ibikíbi tí o bá jáde lọ, kọjú rẹ sí agbègbè Mọ́sálásí Haram, nítorí pé dájúdájú òdodo ni láti ọ̀dọ̀ Olúwa rẹ. Allāhu kò sì níí gbàgbé ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,150,'2:150','Ibikíbi tí o bá jáde lọ, kọjú rẹ sí agbègbè Mọ́sálásí Haram. Àti pé ibikíbi tí ẹ bá wà, ẹ kọjú yín sí agbègbè rẹ̀,nítorí kí àwọn ènìyàn má baà ní àwíjàre lórí yín, àyàfi àwọn tí wọ́n ṣàbòsí nínú wọn (tí wọn kò yé jà yín níyàn). Nítorí náà, ẹ má ṣe páyà wọn. Ẹ páyà Mi, nítorí kí N̄g lè pé ìdẹ̀ra Mi fún yín àti nítorí kí ẹ lè mọ̀nà. ');
INSERT INTO "aykyuni" VALUES(2,151,'2:151','Gẹ́gẹ́ bí A ṣe rán Òjíṣẹ́ kan si yín láààrin ara yín,tí ó ń ké àwọn āyah Wa fún yín, tí ó ń sọ yín di ẹni mímọ́, tí ó ń kọ yín ní Tírà àti ọgbọ́n ìjìnlẹ̀ (sunnah), tí ó sì ń kọ yín ní ohun tí ẹ kò mọ̀ tẹ́lẹ̀, ');
INSERT INTO "aykyuni" VALUES(2,152,'2:152','nítorí náà, ẹ rántí Mi, Kí N̄g rántí yín.Ẹ dúpẹ́ fún Mi, ẹ má ṣàì moore sí Mi. ');
INSERT INTO "aykyuni" VALUES(2,153,'2:153','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo,ẹ fi sùúrù àti ìrun kíkí tọrọ oore (lọ́dọ̀ Allāhu). Dájúdájú Allāhu ń bẹ pẹ̀lú àwọn onísùúrù.');
INSERT INTO "aykyuni" VALUES(2,154,'2:154','Ẹ má ṣe pe àwọn tí wọ́n ń pa sí ojú-ogun ẹ̀sìn Allāhu ní òkú (ìyà), àmọ́ alààyè (ẹni ìkẹ́) ni wọ́n, ṣùgbọ́n ẹ̀yin kò fura.1 ');
INSERT INTO "aykyuni" VALUES(2,155,'2:155','Dájúdájú A ó máa dán-an yín wò pẹ̀lú kiní kan látara ẹ̀rù, ebi, àdínkù nínú àwọn dúkìá, àwọn ẹ̀mí àti àwọn èso. Kí o sì fún àwọn onísùúrù ní ìró ìdùnnú.');
INSERT INTO "aykyuni" VALUES(2,156,'2:156','Àwọn tí (ó jẹ́ pé) nígbà tí àdánwò kan bá kàn wọ́n, wọ́n á sọ pé: “Dájúdájú Allāhu l’Ó ni àwa; dájúdájú ọ̀dọ̀ Rẹ̀ sì ni àwa yóò padà sí.”');
INSERT INTO "aykyuni" VALUES(2,157,'2:157','Àwọn wọ̀nyẹn ni àforíjìn àti ìkẹ́ yó máa bẹ fún láti ọ̀dọ̀ Olúwa wọn. Àwọn wọ̀nyẹn, àwọn sì ni olùmọ̀nà.');
INSERT INTO "aykyuni" VALUES(2,158,'2:158','Dájúdájú (àpáta) Sọfā àti (àpáta) Mọrwah wà nínú àwọn àríṣàmì fún ẹ̀sìn Allāhu. Nítorí náà, ẹnikẹ́ni tí ó bá ṣe iṣẹ́ Hajj sí Ilé náà tàbí ó ṣe iṣẹ́ ‘Umrah, kò sí ẹ̀ṣẹ̀ fún un láti rìn yíká (láààrin àpáta) méjèèjì. Ẹnikẹ́ni tí ó bá sì fínnú-fíndọ̀ ṣe iṣẹ́ àṣegbọrẹ, dájúdájú Allāhu ni Ọlọ́pẹ́Onímọ̀. ');
INSERT INTO "aykyuni" VALUES(2,159,'2:159','Dájúdájú àwọn tó ń daṣọ bo ohun tí A sọ̀kalẹ̀ nínú àwọn ẹ̀rí tó yanjú àti ìmọ̀nà, lẹ́yìn tí A ti ṣe àlàyé rẹ̀ fún àwọn ènìyàn sínú Tírà, àwọn wọ̀nyẹn ni Allāhu ń ṣẹ́bi lé. Àwọn olùṣẹ́bi sì ń ṣẹ́bi lé wọn.');
INSERT INTO "aykyuni" VALUES(2,160,'2:160','Àyàfi àwọn tó ronú pìwàdà, tí wọ́n ṣe àtúnṣe, tí wọ́n sì ṣàfi hàn òdodo, nítorí náà, àwọn wọ̀nyẹn ni Mo máa gba ìronúpìwàdà wọn. Èmi sì ni Olùgba-ìronúpìwàdà, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,161,'2:161','Dájúdájú àwọn tó ṣàì gbàgbọ́, tí wọ́n sì kú nígbà tí wọ́n jẹ́ aláìgbàgbọ́, àwọn wọ̀nyẹn ni ègún Allāhu, àti (ègún) àwọn mọlāika àti (ègún) ènìyàn pátápátá ń bẹ lórí wọn.');
INSERT INTO "aykyuni" VALUES(2,162,'2:162','Olùṣegbére ni wọ́n nínú rẹ̀. A ò níí ṣe ìyà náà ní fífúyẹ́ fún wọn A ò sì níí fún wọn ní ìsinmi (nínú Iná).');
INSERT INTO "aykyuni" VALUES(2,163,'2:163','Ọlọ́hun yín tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo, Ọlọ́hun Ọ̀kan ṣoṣo ni. Kò sí ọlọ́hun kan tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun, Àjọkẹ́-ayé, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,164,'2:164','Dájúdájú àwọn àmì wà nínú ìṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀, ìtẹ̀léǹtẹ̀lé òru àti ọ̀sán, àti àwọn ọkọ̀ ojú-omi tó ń rìn lórí omi pẹ̀lú (ríru) ohun tó ń ṣe àwọn ènìyàn ní àǹfààní, àti ohun tí Allāhu ń sọ̀kalẹ̀ ní omi láti sánmọ̀, tí Ó sì ń fi sọ ilẹ̀ di ààyè lẹ́yìn tí ó ti kú, àti (bí) Ó ṣe fọ́n gbogbo ẹranko ká sí orí ilẹ̀, àti ìyípadà atẹ́gùnàti ẹ̀ṣújò tí A tẹ̀ba láààrin sánmọ̀ àti ilẹ̀; (àmì wà nínú wọn) fún ìjọ tó ń ṣe làákàyè. ');
INSERT INTO "aykyuni" VALUES(2,165,'2:165','Ó sì ń bẹ nínú àwọn ènìyàn, ẹni tí ó ń jọ́sìn fún àwọn òrìṣà lẹ́yìn Allāhu. Wọ́n nífẹ̀ẹ́ wọn gẹ́gẹ́ bí ìfẹ́ (tó yẹ kí wọ́n ní sí) Allāhu. Àwọn tó gbàgbọ́ ní òdodo sì le jùlọ nínú ìfẹ́ sí Allāhu. Tí ó bá jẹ́ pé àwọn tó ṣàbòsí lè rí ìgbà tí wọ́n máa rí Ìyà náà ni, (wọn ìbá mọ̀ pé) dájúdájú gbogbo agbára ń jẹ́ ti Allāhu. Àti pé dájúdájú Allāhu le (níbi) ìyà.');
INSERT INTO "aykyuni" VALUES(2,166,'2:166','(Rántí) nígbà tí àwọn tí wọ́n tẹ̀lé (ìyẹn àwọn aṣíwájú kèfèrí) bá yọwọ́ yọsẹ̀ kúrò nínú ọ̀rọ̀ àwọn tó tẹ̀lé wọn; (nígbà tí) wọ́n bá fojú rí Ìyà, tí ohun tó so wọ́n pọ̀ sì já pátápátá.');
INSERT INTO "aykyuni" VALUES(2,167,'2:167','Àwọn tó tẹ̀lé wọn yó sì wí pé: “Tí ó bá jẹ́ pé dájúdájú ìpadàwáyé lè wà fún wa ni, àwa ìbá yọwọ́ yọsẹ̀ kúrò nínú ọ̀rọ̀ wọn gẹ́gẹ́ bí wọ́n ṣe yọwọ́ yọsẹ̀ kúrò nínú ọ̀rọ̀ wa.” Báyẹn ni Allāhu yó ṣe fi àwọn iṣẹ́ wọn hàn wọ́n ní (iṣẹ́) òfò fún wọn. Wọn kò sì níí jáde kúrò nínú Iná.');
INSERT INTO "aykyuni" VALUES(2,168,'2:168','Ẹ̀yin ènìyàn, ẹ jẹ nínú ohun tí ń bẹ nínú ilẹ̀ (tó jẹ́) ẹ̀tọ́ (àti n̄ǹkan) dáadáa. Ẹ má sì ṣe tẹ̀lé àwọn ojú-ẹsẹ̀ Èṣù. Dájúdájú òun ni ọ̀tá pọ́nńbélé fún yín.');
INSERT INTO "aykyuni" VALUES(2,169,'2:169','Ohun tí ó máa pa yín ní àṣẹ rẹ̀ ni (iṣẹ́) aburú, ìbàjẹ́ àti pé kí ẹ máa pa irọ́ ohun tí ẹ kò nímọ̀ nípa rẹ̀ mọ́ Allāhu.');
INSERT INTO "aykyuni" VALUES(2,170,'2:170','Nígbà tí wọ́n bá sọ fún wọn pé: “Ẹ tẹ̀lé ohun tí Allāhu sọ̀kalẹ̀.” Wọ́n á wí pé: “Rárá, A óò máa tẹ̀lé ohun tí a bá l’ọ́wọ́ àwọn bàbá wa ni.” Ṣé pẹ̀lú bí ó ṣe jẹ́ pé àwọn bàbá wọn kò ṣe làákàyè kan kan (nípa ẹ̀sìn), tí wọn kò sì mọ̀nà?');
INSERT INTO "aykyuni" VALUES(2,171,'2:171','Àpèjúwe àwọn tó ṣàì gbàgbọ́ dà bí àpèjúwe ẹni tí ó ń kígbe (mọ́ ẹranko) tí kò gbọ́rọ̀ bí kò ṣe ìpè àti igbe (asán). Adití, ayaya, afọ́jú ni wọ́n; wọn kò sì níí ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(2,172,'2:172','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ jẹ nínú àwọn n̄ǹkan dáadáa tí A pèsè fún yín, kí ẹ sì dúpẹ́ fún Allāhu tí ó bá jẹ́ pé Òun nìkan ṣoṣo ni ẹ̀ ń jọ́sìn fún.');
INSERT INTO "aykyuni" VALUES(2,173,'2:173','Ohun tí (Allāhu) ṣe ní èèwọ̀ fún yín ni òkúǹbete ẹ̀jẹ̀2, ẹran ẹlẹ́dẹ̀ àti ohun tí wọ́n pe orúkọ mìíràn lé lórí yàtọ̀ sí (orúkọ) Allāhu. Ṣùgbọ́n ẹni tí ìnira (ebi) bá mú (jẹ ẹran èèwọ̀), tí kì í ṣe ẹni tó ń wá èèwọ̀ kiri àti olùtayọ-ẹnu-ààlà, kò sí ẹ̀ṣẹ̀ fún un. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.3 ');
INSERT INTO "aykyuni" VALUES(2,174,'2:174','Dájúdájú àwọn tó ń daṣọ bo ohun tí Allāhu sọ̀kalẹ̀ nínú Tírà, tí wọ́n sì ń tà á ní owó pọ́ọ́kú, àwọn wọ̀nyẹn kò jẹ kiní kan sí inú wọn bí kò ṣe Iná. Allāhu kò sì níí bá wọn sọ̀rọ̀ (ìdùnnú) ní Ọjọ́ Àjíǹde, kò sì níí fọ̀ wọ́n mọ́ (nínú ẹ̀ṣẹ̀). Ìyà ẹlẹ́ta-eléro sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(2,175,'2:175','Àwọn wọ̀nyẹn ni àwọn tó fi ìmọ̀nà ra ìṣìnà, (wọ́n tún fi) àforíjìn ra ìyà.Ṣé wọn sì lè ṣèfaradà fún Iná! (Àbí kí ló kì wọ́n láyà láti ṣe iṣẹ́ Iná!) ');
INSERT INTO "aykyuni" VALUES(2,176,'2:176','Ìyẹn rí bẹ́ẹ̀ nítorí pé dájúdájú Allāhu sọ Tírà (al-Ƙur’ān) kalẹ̀ pẹ̀lú òdodo. Dájúdájú àwọn tó sì yapa Tírà náà kúkú ti wà nínú ìyapà tó jìnnà (sí òdodo).');
INSERT INTO "aykyuni" VALUES(2,177,'2:177','Kì í ṣe ohun rere ni pé, kí ẹ kọjú sí agbègbè ibùyọ òòrùn àti ibùwọ̀ òòrùn,àmọ́ (olùṣe) rere ni ẹnikẹ́ni tó bá gbàgbọ́ nínú Allāhu, Ọjọ́ Ìkẹ́yìn, àwọn mọlāika, Tírà (al-Ƙur’ān), àti àwọn Ànábì. Tòhun ti ìfẹ́ tí olùṣe-rere ní sí dúkìá, ó tún ń fi dúkìá náà tọrẹ fún àwọn ẹbí, àwọn ọmọ òrukàn, àwọn mẹ̀kúnnù, onírìn-àjò (tí agara dá), àwọn atọrọjẹ àti fún ìtúsílẹ̀ ẹrú l’óko ẹrú. (Ẹni rere) yó máa kírun, yó sì máa yọ Zakāh. (Ẹni rere ni) àwọn tó ń mú àdéhùn wọn ṣẹ nígbà tí wọ́n bá ṣe àdéhùn. (Wọ́n jẹ́) onísùúrù lásìkò ìpọ́njú, lásìkò àìlera àti l’ójú ogun ẹ̀sìn. Àwọn wọ̀nyẹn ni àwọn tó ṣe (iṣẹ́) òdodo. Àwọn wọ̀nyẹn, àwọn sì ni olùbẹ̀rù (Allāhu). ');
INSERT INTO "aykyuni" VALUES(2,178,'2:178','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, A ṣe ẹ̀san gbígbà níbi ìpànìyàn ní ọ̀ran-anyàn fún yín. Olómìnira fún olómìnira, ẹrú fún ẹrú, obìnrin fún obìnrin. Ẹnikẹ́ni tí wọ́n bá ṣàmójú kúrò kiní kan fún láti ọ̀dọ̀ arákùnrin rẹ̀ (ìyẹn ni pé, ti aláṣẹ ẹni tí wọ́n pa bá gbà láti gba owó ẹ̀mí dípò fífi ẹ̀mí apànìyàn dí ẹ̀mí ẹni tí wọ́n pa, kí ẹni tí ó gba owó ẹ̀mí dípò ẹ̀mí) ṣe dáadáa tẹ̀lé (owó tí ó gbà, ìyẹn ni pé, kí ó fi ọ̀nà ẹ̀rọ̀ gba owó ẹ̀mí náà. Àti pé kí ẹni tí ó máa san owó ẹ̀mí) san ań fún un ní ọ̀nà tó dára. Ìyẹn ni ìgbéfúyẹ́ àti àánú láti ọ̀dọ̀ Olúwa yín. Ṣùgbọ́n ẹnikẹ́ni tí ó bá tayọ ẹnu-ààlà (òfin) lẹ́yìn ìyẹn, ìyà ẹlẹ́ta-eléro ń bẹ fún un.');
INSERT INTO "aykyuni" VALUES(2,179,'2:179','Ìsẹ̀mí wà fún yín nínú (òfin) ìgbẹ̀san, ẹ̀yin onílàákáyè, kí ẹ lè bẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(2,180,'2:180','A ṣe é ní ọ̀ran-anyàn fún yín, nígbà tí (àwọn àmì) ikúbá dé bá ẹnì kan nínú yín, tí ó sì máa fi dúkìá sílẹ̀, pé kí ó sọ àsọọ́lẹ̀2 ní ọ̀nà tó dára fún àwọn òbí méjèèjì àti àwọn ẹbí. (Èyí jẹ́) ojúṣe fún àwọn olùbẹ̀rù (Allāhu). ');
INSERT INTO "aykyuni" VALUES(2,181,'2:181','Ẹnikẹ́ni tí ó bá yí i padà lẹ́yìn tí ó ti gbọ́ ọ, ẹ̀ṣẹ̀ rẹ̀ yó sì wà lọ́rùn àwọn tó ń yí i padà. Dájúdájú Allāhu ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(2,182,'2:182','Ṣùgbọ́n ẹni tí ó bá páyà àìṣe déédé tàbí ìwà ẹ̀ṣẹ̀ láti ọ̀dọ̀ ẹni tí ó sọ àsọọ́lẹ̀, tí ó sì ṣe àtúnṣe láààrin (àwọn tí ogún tọ́ sí), nítorí náà, kò sí ẹ̀ṣẹ̀ fún un. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,183,'2:183','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, A ṣe ààwẹ̀ náà ní ọ̀ran-anyàn fún yín, gẹ́gẹ́ bí A ti ṣe é ní ọ̀ran-anyàn fún àwọn tó ṣíwájú yín, nítorí kí ẹ lè bẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(2,184,'2:184','(Ẹ gba ààwẹ̀ náà) fún àwọn ọjọ́ tí ó ní òǹkà. (Ṣùgbọ́n) ẹnikẹ́ni nínú yín tí ó bá jẹ́ aláìsàn, tàbí ó wà lórí ìrìn-àjò, (ó máa san) òǹkà ọjọ́ (gbèsè ààwẹ̀ rẹ̀) nínú àwọn ọjọ́ mìíràn. Àti pé ìtánràn (ìyẹn) fífún mẹ̀kúnnù ní oúnjẹ l’ó di dandan fún àwọn tó máa fi ìnira gba ààwẹ̀. Ẹnikẹ́ni tí ó bá fínnúfíndọ̀ ṣe (àlékún) iṣẹ́ olóore, ó kúkú lóore jùlọ fún un. Àti pé kí ẹ gba ààwẹ̀ lóore jùlọ fún yín tí ẹ bá mọ̀.1 ');
INSERT INTO "aykyuni" VALUES(2,185,'2:185','Oṣù Rọmọdọ̄n èyí tí A sọ al-Ƙur’ān kalẹ̀ nínú rẹ̀(tí ó jẹ́) ìmọ̀nà, àwọn àlàyé pọ́nńbélé nípa ìmọ̀nà àti ọ̀rọ̀-ìpínyà2 fún àwọn ènìyàn; nítorí náà, ẹnikẹ́ni nínú yín tí oṣù náà bá bá láyé (nínú ìlú rẹ̀ pẹ̀lú àlàáfíà ara),3 kí ó gba ààwẹ̀ oṣù náà. Ẹnikẹ́ni tí ó bá jẹ́ aláìsàn tàbí tí ó bá wà lórí ìrìn-àjò, (ó máa san) òǹkà ọjọ́ (gbèsè ààwẹ̀ rẹ̀) nínú àwọn ọjọ́ mìíràn. Allāhu fẹ́ ìrọ̀rùn fún yín, kò sì fẹ́ ìnira fún yín. Ẹ parí òǹkà ọjọ́ (ààwẹ̀ náà), kí ẹ sì gbé títóbi fún Allāhu nítorí pé, Ó fi ọ̀nà mọ̀ yín àti nítorí kí ẹ lè dúpẹ́ fún Un. ');
INSERT INTO "aykyuni" VALUES(2,186,'2:186','Nígbà tí àwọn ẹrúsìn Mi bá bi ọ́ léèrè nípa Mi, dájúdájú Èmi ni Olùsúnmọ́. Èmi yóò jẹ́pè àdúà aládùúà nígbà tí ó bá pè Mí. Nítorí náà, kí wọ́n jẹ́’pè Mi (nípa ìtẹ̀lé àṣẹ Mi). Kí wọ́n sì gbà Mí gbọ́ nítorí kí wọ́n lè mọ̀nà (gbígbà àdúà).');
INSERT INTO "aykyuni" VALUES(2,187,'2:187','Wọ́n ṣe alẹ́ ààwẹ̀ ní ẹ̀tọ́ fún yín láti súnmọ́ àwọn ìyàwó yín; àwọn ni aṣọ yín, ẹ̀yin sì ni aṣọ wọn. Allāhu mọ̀ pé dájúdájú ẹ̀ ń tan ara yín jẹ (nípa àìfẹ́ sun oorun ìfẹ́ ní alẹ́ ààwẹ̀). Ó ti gba ìronúpìwàdà yín, Ó sì ṣe àmójúkúrò fún yín. Ní báyìí, ẹ súnmọ́ wọn, kí ẹ sì wá ohun tí Allāhu kọ mọ́ yín (ní ọmọ). Ẹ jẹ, ẹ mu títí ẹ óò fi rí ìyàtọ̀ láààrin òwú funfun (ìyẹn, ìmọ́lẹ̀ òwúrọ̀) àti òwú dúdú (ìyẹn, òkùnkùn òru) nípasẹ̀ àfẹ̀mọ́júmọ́ òdodo. Lẹ́yìn náà, ẹ parí ààwẹ̀ náà sí alẹ́ (nígbà tí òòrùn bá wọ̀). Ẹ má ṣe súnmọ́ wọn nígbà tí ẹ bá ń kóra ró nínú àwọn mọ́sálásí. Ìwọ̀nyẹn ni àwọn ẹnu-ààlà (òfin tí) Allāhu (gbékalẹ̀), ẹ má ṣe súnmọ́ ọn (ẹ má ṣe kọlu àwọn òfin náà). Báyẹn ni Allāhu ṣe ń ṣàlàyé àwọn āyah Rẹ̀ fún àwọn ènìyàn nítorí kí wọ́n lè bẹ̀rù (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(2,188,'2:188','Ẹ̀yin kò gbọdọ̀ fi èrú jẹ dúkìá yín láààrin ara yín. Ẹ̀yin kò sì gbọdọ̀ gbé dúkìá lọ bá àwọn adájọ́ (ní àbẹ̀tẹ́lẹ̀) nítorí kí ẹ lè fi ẹ̀ṣẹ̀ jẹ ìpín kan nínú dúkìá àwọn ènìyàn, ẹ̀yin sì mọ̀ (pé ìjà àbòsí lẹ̀ ń jà).');
INSERT INTO "aykyuni" VALUES(2,189,'2:189','Wọ́n ń bi ọ́ léèrè nípa àwọn ìlétéṣù. Sọ pé: “Òhun ni (òǹkà) àkókò fún àwọn ènìyàn àti (òǹkà àkókò fún) iṣẹ́ Hajj. Kì í ṣe iṣẹ́ rere (fún yín) láti gba ẹ̀yìn-ìnkùlé wọnú ilé, ṣùgbọ́n (olùṣe) rere ni ẹni tí ó bá bẹ̀rù (Allāhu). Ẹ gba ẹnu-ọ̀nà wọnú ilé. Kí ẹ sì bẹ̀rù Allāhu nítorí kí ẹ lè jèrè.');
INSERT INTO "aykyuni" VALUES(2,190,'2:190','Fún ààbò ẹ̀sìn Allāhu, ẹ pa àwọn tó ń jà yín lógun. Kí ẹ sì má ṣe tayọ ẹnu-ààlà.Dájúdájú Allāhu kò nífẹ̀ẹ́ àwọn olùtayọ ẹnu-ààlà. ');
INSERT INTO "aykyuni" VALUES(2,191,'2:191','Ẹ pa wọ́n níbikíbi tí ọwọ́ yín bá ti bà wọ́n. Kí ẹ sì lé wọn jáde kúrò níbi tí wọ́n ti le yín jáde. Ìfòòró le ju pípa lọ. Ẹ má ṣe bá wọn jagun ní Mọ́sálásí Haram àfi ìgbà tí wọ́n bá ń ba yín jà nínú rẹ̀. Nítorí náà, tí wọ́n bá jà yín lógun, ẹ jà wọ́n lógun. Báyẹn ni ẹ̀san àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(2,192,'2:192','Tí wọ́n bá sì jáwọ́ (nínú ìbọ̀rìṣà), dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,193,'2:193','Ẹ gbógun tì wọ́n títí kò fi níí sí ìfòòró (ìbọ̀rìṣà) mọ́. Ẹ̀sìn ’Islām yó sì wà (ní òmìnira) fún Allāhu. Nítorí náà, tí wọ́n bá jáwọ́ (nínú ìbọ̀rìṣà), kò sí ogun mọ́ àyàfi lórí àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(2,194,'2:194','Oṣù ọ̀wọ̀ fún oṣù ọ̀wọ̀. Àwọn n̄ǹkan ọ̀wọ̀ sì ní (òfin) ìgbẹ̀san.Nítorí náà, ẹnikẹ́ni tí ó bá tayọ ẹnu-ààlà si yín, ẹ gb’ẹ̀san ìtayọ ẹnu-ààlà lára rẹ̀ pẹ̀lú irú ohun tí ó fi tayọ ẹnu-ààlà si yín. Ẹ bẹ̀rù Allāhu. Kí ẹ sì mọ̀ pé dájúdájú Allāhu ń bẹ pẹ̀lú àwọn olùbẹ̀rù (Rẹ̀). ');
INSERT INTO "aykyuni" VALUES(2,195,'2:195','Ẹ náwó fún ogun ẹ̀sìn Allāhu. Kí ẹ sì má ṣe fi ọwọ́ ara yín fa ìparun (nípa sísá fún ogun ẹ̀sìn). Ẹ ṣe rere. Dájúdájú Allāhu nífẹ̀ẹ́ àwọn olùṣe-rere.');
INSERT INTO "aykyuni" VALUES(2,196,'2:196','Ẹ ṣe àṣepé iṣẹ́ Hajj àti ‘Umrah fún Allāhu. Tí wọ́n bá sì se yín mọ́ ojú ọ̀nà, ẹ fi èyí tí ó bá rọrùn nínú ẹran ṣe ọrẹ. Ẹ̀yin kò sì gbọdọ̀ fá irun orí yín títí di ìgbà tí ẹran ọrẹ náà yó fi dé àyè rẹ̀. Ẹnikẹ́ni nínú yín tí ó bá jẹ́ aláìsàn tàbí ìnira kan ń bẹ ní orí rẹ̀, ó máa fi ààwẹ̀ tàbí sàráà tàbí ẹran pípa ṣe ìtánràn (fún kíkánjú fá irun orí). Nígbà tí ẹ bá fọkànbalẹ̀ (nínú ewu), ẹnikẹ́ni tí ó bá ṣe ‘Umrah àti Hajj nínú oṣù iṣẹ́ Hajj, ó máa fi èyí tí ó bá rọrùn nínú ẹran ṣe ọrẹ. Ẹni tí kò bá rí (ẹran ọrẹ), kí ó gba ààwẹ̀ ọjọ́ mẹ́ta nínú (iṣẹ́) hajj, méje nígbà tí ẹ bá darí wálé. Ìyẹn ni (ààwẹ̀) mẹ́wàá tó pé. Ìyẹn wà fún ẹni tí kò sí ẹbí rẹ̀ ní Mọ́sálásí Haram. Ẹ bẹ̀rù Allāhu. Kí ẹ sì mọ̀ pé dájúdájú Allāhu le (níbi) ìyà.');
INSERT INTO "aykyuni" VALUES(2,197,'2:197','Hajj ṣíṣe (wà) nínú àwọn oṣù tí wọ́n ti mọ̀. Nítorí náà, ẹnikẹ́ni tí ó bá ṣe é ní ọ̀ran-anyàn lórí ara rẹ̀ láti ṣe Hajj nínú àwọn oṣù náà, kò gbọdọ̀ sí oorun ìfẹ́, rírú òfin àti àríyànjiyàn nínú iṣẹ́ Hajj. Ohunkóhun tí ẹ bá ṣe ní rere, Allāhu mọ̀ ọ́n. Ẹ mú èsè ìrìn-àjò lọ́wọ́. Dájúdájú èsè ìrìn-àjò tó lóore jùlọ ni ìṣọ́ra (níbi èsè ẹlòmíìràn àti agbe ṣíṣe l’ásìkò iṣẹ́ Hajj). Ẹ bẹ̀rù Mi, ẹ̀yin onílàákàyè.');
INSERT INTO "aykyuni" VALUES(2,198,'2:198','Kò sí ìbáwí fún yín (níbi òwò ṣíṣe l’ásìkò iṣẹ́ hajj) pé kí ẹ wá oore kan láti ọ̀dọ̀ Olúwa yín. Nítorí náà, nígbà tí ẹ bá ń darí bọ̀ láti ‘Arafah, ẹ ṣe ìrántí Allāhu ní àyè alápọ̀n-ọ́nlé (Muzdalifah). Ẹ ṣe ìrántí Rẹ̀ nítorí pé, Ó fi ọ̀nà mọ̀ yín, bí ó tilẹ̀ jẹ́ pé tẹ́lẹ̀tẹ́lẹ̀ ẹ wà nínú àwọn olùṣìnà.');
INSERT INTO "aykyuni" VALUES(2,199,'2:199','Lẹ́yìn náà, ẹ dà kúrò láti ibi tí àwọn ènìyàn ti dà kúrò (ìyẹn ní ‘Arafah), kí ẹ sì tọrọ àforíjìn Allāhu. Dájúdájú, Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,200,'2:200','Nígbà tí ẹ bá parí ìjọ́sìn (Hajj) yín, ẹ ṣèrántí Allāhu gẹ́gẹ́ bí ẹ ṣe ń ṣèrántí àwọn baba ńlá yín. Tàbí kí ìrántí náà lágbára jù bẹ́ẹ̀ lọ. Nítorí náà, ó ń bẹ nínú àwọn ènìyàn, ẹni tó ń wí pé: “Olúwa wa, fún wa ní oore ayé.” Kò sì níí sí ìpín oore kan fún un ní ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,201,'2:201','Ó sì ń bẹ nínú wọn, ẹni tó ń sọ pé: “Olúwa wa, fún wa ní oore ní ayé àti oore ní ọ̀run, kí O sì ṣọ́ wa níbi ìyà Iná.”');
INSERT INTO "aykyuni" VALUES(2,202,'2:202','Àwọn wọ̀nyẹn, tiwọn ni ìpín oore nípa ohun tí wọ́n ṣe níṣẹ́. Allāhu sì ni Olùyára níbi ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,203,'2:203','Ẹ ṣèrántí Allāhu láààrin àwọn ọjọ́ tó ní òǹkà. Nítorí náà, ẹni tí ó bá kánjú (ṣe é) fún ọjọ́ méjì, kò sí ẹ̀ṣẹ̀ fún un. Ẹni tí ó bá kẹ́yìn (tí ó dúró di ọjọ́ kẹta), kò sí ẹ̀ṣẹ̀ fún un fún ẹni tí ó bá ṣọ́ra (fún ìwà ẹ̀ṣẹ̀). Ẹ bẹ̀rù Allāhu. Kí ẹ sì mọ̀ pé dájúdájú wọn yóò ko yín jọ sí ọ̀dọ̀ Rẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(2,204,'2:204','Ó ń bẹ nínú àwọn ènìyàn, ẹni tí ọ̀rọ̀ (ẹnu) rẹ̀ yó máa ṣe ọ́ ní kàyéfì nínú ìṣẹ̀mí ayé yìí, tí yó sì máa fi Allāhu jẹ́rìí sí ohun tí ń bẹ nínú ọkàn rẹ̀ (pé kò sí ìjà mọ́), oníjà tó le jùlọ sì ni.');
INSERT INTO "aykyuni" VALUES(2,205,'2:205','Nígbà tí ó bá sì yísẹ̀ padà, ó máa ṣiṣẹ́ káàkiri lórí ilẹ̀ nítorí kí ó lè ṣèbàjẹ́ sórí ilẹ̀, kí ó sì lè pa n̄ǹkan oko àti ẹran-ọ̀sìn run. Allāhu kò sì fẹ́ràn ìbàjẹ́.');
INSERT INTO "aykyuni" VALUES(2,206,'2:206','Àti pé nígbà tí wọ́n bá sọ fún un pé: “Bẹ̀rù Allāhu.” Ìgbéraga sì máa mú un dẹ́ṣẹ̀. Nítorí náà, iná Jahanamọ yóò tó o (ní ẹ̀san). Ibùgbé náà sì burú.');
INSERT INTO "aykyuni" VALUES(2,207,'2:207','Ó ń bẹ nínú àwọn ènìyàn, ẹni tó ń ta ẹ̀mí ara rẹ̀ (ìyẹn, olùjagun-ẹ̀sìn) láti wá ìyọ́nú Allāhu. Allāhu sì ni Aláàánú fún àwọn ẹrúsìn (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(2,208,'2:208','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ kó s’ínú ẹ̀sìn ’Islām pátápátá. Ẹ̀yin kò sì gbọdọ̀ tẹ̀lé ojú-ẹsẹ̀ Èṣù. Dájúdájú òun ni ọ̀tá pọ́nńbélé fún yín.');
INSERT INTO "aykyuni" VALUES(2,209,'2:209','Nítorí náà, tí ẹsẹ̀ yín bá yẹ̀ (kúrò nínú ’Islām) lẹ́yìn tí àwọn ẹ̀rí tó yanjú ti dé ba yín, kí ẹ mọ̀ pé dájúdájú Allāhu ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(2,210,'2:210','Ṣé wọ́n ń retí kiní kan bí kò ṣe pé kí Allāhu wá bá wọn nínú ibòji ẹ̀ṣújò funfun,àwọn mọlāika náà (sì máa wá, nígbà náà), A ó sì yanjú ọ̀rọ̀ (ìṣírò iṣẹ́ ẹ̀dá)! Ọ̀dọ̀ Allāhu sì ni wọ́n máa ṣẹ́rí àwọn ọ̀rọ̀ ẹ̀dá padà sí. ');
INSERT INTO "aykyuni" VALUES(2,211,'2:211','Bi àwọn ọmọ ’Isrọ̄’īl léèrè pé: “Mélòó ni A ti fún wọn nínú āyah tó yanjú (àmọ́ tí wọ́n ṣàì gbàgbọ́ nínú rẹ̀)?” Ẹnikẹ́ni tí ó bá (fi àìgbàgbọ́) jìrọ̀ ìdẹ̀ra Allāhu lẹ́yìn tí ó dé bá a, dájúdájú Allāhu le (níbi) ìyà.');
INSERT INTO "aykyuni" VALUES(2,212,'2:212','Wọ́n ṣe ìṣẹ̀mí ayé ní ọ̀ṣọ́ (ẹ̀tàn) fún àwọn aláìgbàgbọ́. (Tí ayé bá sì yẹ wọ́n tán,) wọn yó máa fi àwọn tó gbàgbọ́ ní òdodo ṣe yẹ̀yẹ́. Àwọn tó sì bẹ̀rù Allāhu máa wà l’ókè wọn ní Ọjọ́ Àjíǹde. Àti pé Allāhu ń pèsè ìjẹ-ìmu àti ìgbádùn fún ẹni tí Ó bá fẹ́ láì níí ní ìṣírò.1 ');
INSERT INTO "aykyuni" VALUES(2,213,'2:213','Àwọn ènìyàn jẹ́ ìjọ kan ṣoṣo (ẹlẹ́sìn ’Islām bẹ̀rẹ̀ lórí Ànábì Ādam - kí ọlà Allāhu máa bá a -). Lẹ́yìn náà, Allāhu gbé àwọn Ànábì dìde, tí wọ́n ń jẹ́ oníròó ìdùnnú àti olùkìlọ̀. Ó sọ Tírà kalẹ̀ fún wọn pẹ̀lú òdodo nítorí kí Ó lè fi ṣe ìdájọ́ láààrin àwọn ènìyàn nípa ohun tí wọ́n yapa-ẹnu lórí rẹ̀. Kò sì sí ẹni tó yapa-ẹnu (lórí ’Islām) àfi àwọn tí A fún ní Tírà, lẹ́yìn tí àwọn ẹ̀rí tó yanjú dé bá wọn ní ti ìlara láààrin ara wọn (sí àwọn Ànábì - kí ọlà Allāhu máa bá wọn -). Nítorí náà, Allāhu tọ́ àwọn tó gbàgbọ́ ní òdodo sọ́nà pẹ̀lú ìyọ̀ǹda Rẹ̀ nípa ohun tí (àwọn onílara)yapa-ẹnu lórí rẹ̀ nípa òdodo (’Islām). Allāhu yó máa tọ́ ẹni tí Ó bá fẹ́ sí ọ̀nà tààrà.2 ');
INSERT INTO "aykyuni" VALUES(2,214,'2:214','Tàbí ẹ lérò pé ẹ máa wọ inú Ọgbà Ìdẹ̀ra nígbà tí irúfẹ́ (àdánwò) tó kan àwọn tó ti lọ ṣíwájú yín kò ì kàn yín? Ìpọ́njú àti àìlera mú wọn. Wọ́n sì rí àmìwò tó bẹ́ẹ̀ gẹ́ tí Òjíṣẹ́ àti àwọn tó gbàgbọ́ ní òdodo pẹ̀lú rẹ̀ fi sọ pé: “Ìgbà wo ni àrànṣe Allāhu máa dé sẹ́?” Kíyè sí i! Dájúdájú àrànṣe Allāhu súnmọ́.');
INSERT INTO "aykyuni" VALUES(2,215,'2:215','Wọ́n ń bí ọ léèrè pé kí ni àwọn yó máa náwó sí. Sọ pé: “Ohun tí ẹ bá ná nínú ohun rere, kí ó máa jẹ́ ti àwọn òbí méjèèjì, àwọn ẹbí, àwọn ọmọ òrukàn, àwọn mẹ̀kúnnù àti onírìn-àjò (tí agara dá). Ohunkóhun tí ẹ bá ṣe nínú ohun rere, dájúdájú Allāhu ni Onímọ̀ nípa rẹ̀.”');
INSERT INTO "aykyuni" VALUES(2,216,'2:216','A ṣe ogun ẹ̀sìn ní ọ̀ran-anyàn le yín lórí, ohun ìkórira sì ni fún yín. Ó sì lè jẹ́ pé ẹ kórira kiní kan, kí ohun náà sì jẹ́ oore fún yín. Ó sì tún lè jẹ́ pé ẹ nífẹ̀ẹ́ sí kiní kan, kí ohun náà sì jẹ́ aburú fún yín. Allāhu nímọ̀, ẹ̀yin kò sì nímọ̀.');
INSERT INTO "aykyuni" VALUES(2,217,'2:217','Wọ́n ń bi ọ́ léèrè nípa ogun jíjà nínú oṣù ọ̀wọ̀. Sọ pé: “Ẹ̀ṣẹ̀ ńlá ni ogun jíjà nínú rẹ̀. Àti pé ṣíṣẹ́rí àwọn ènìyàn kúrò l’ójú ọ̀nà (ẹ̀sìn) Allāhu, ṣíṣe àìgbàgbọ́ nínú Allāhu, dídí àwọn mùsùlùmí lọ́wọ́ láti wọ inú Mọ́sálásí Haram àti lílé àwọn mùsùlùmí jáde kúrò nínú rẹ̀, (ìwọ̀nyí) tún tóbi jùlọ ní ẹ̀ṣẹ̀ ní ọ̀dọ̀ Allāhu.” Ìfòòró tóbi ju ìpànìyàn. Wọn kò ní yéé gbógun tì yín títí wọn yó fi ṣẹ́ yín lórí kúrò nínú ẹ̀sìn yín, tí wọ́n bá lágbára (ọ̀nà láti ṣe bẹ́ẹ̀). Ẹnikẹ́ni nínú yín tí ó bá ṣẹ́rí kúrò nínú ẹ̀sìn rẹ̀, tí ó sì kú sí ipò kèfèrí, nítorí náà àwọn wọ̀nyẹn ni àwọn tí iṣẹ́ wọn ti bàjẹ́ ní ayé àti ní ọ̀run. Àwọn wọ̀nyẹn sì ni èrò inú Iná. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(2,218,'2:218','Dájúdájú àwọn tó gbàgbọ́ ní òdodo àti àwọn tó gbé ìlú (wọn) jù sílẹ̀ àti àwọn tó jagun fún ààbò ẹ̀sìn Allāhu, àwọn wọ̀nyẹn ń retí ìkẹ́ Allāhu. Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(2,219,'2:219','Wọ́n ń bi ọ́ léèrè nípa ọtí àti tẹ́tẹ́. Sọ pé: “Ẹ̀ṣẹ̀ tó tóbi àti àwọn àǹfààní kan wà nínú méjèèjì fún àwọn ènìyàn. Ẹ̀ṣẹ̀ méjèèjì sì tóbi ju àǹfààní wọn.”Wọ́n tún ń bi ọ́ léèrè pé kí ni àwọn yó máa ná ní sàráà. Sọ pé: “Ohun tí ó bá ṣẹ́kù lẹ́yìn tí ẹ bá ti gbọ́ bùkátà inú ilé tán (ni kí ẹ ṣe sàráà).” Báyẹn ni Allāhu ṣe ń ṣ’àlàyé àwọn āyah fún yín nítorí kí ẹ lè ronú jinlẹ̀ ');
INSERT INTO "aykyuni" VALUES(2,220,'2:220','nípa ayé àti ọ̀run. Wọ́n ń bi ọ́ léèrè nípa àwọn ọmọ òrukàn. Sọ pé: “Ṣíṣe àtúnṣe dúkìá wọn (láì níí dà á pọ̀ mọ́ dúkìá yín) l’ó dára jùlọ. Tí ẹ bá sì dà á pọ̀ mọ́ dúkìá yín, ọmọ ìyá yín (nínú ẹ̀sìn) kúkú ni wọ́n. Allāhu sì mọ òbìlẹ̀jẹ́ yàtọ̀ sí alátùn-únṣe. Àti pé tí ó bá jẹ́ pé Allāhu bá fẹ́ (kí ẹ ya dúkìá wọn sí ọ̀tọ̀ nìkan ni) ìbá kó ìnira ba yín. Dájúdájú Allāhu ni Alágbára, Ọlọ́gbọ́n.”');
INSERT INTO "aykyuni" VALUES(2,221,'2:221','Ẹ má fi àwọn abọ̀rìṣà lóbìnrin ṣaya títí wọn yó fi gbàgbọ́ ní òdodo. Dájúdájú ẹrúbìnrin onígbàgbọ́ òdodo lóore ju abọ̀rìṣà lóbìnrin, kódà kí abọ̀rìṣà lóbìnrin jọ yín lójú.Ẹ má sì fi onígbàgbọ́ òdodo lóbìnrin fún àwọn abọ̀rìṣà lọ́kùnrin títí wọn yó fi gbàgbọ́ ní òdodo. Ẹrúkùnrin onígbàgbọ́ òdodo lóore ju abọ̀rìṣà lọ́kùnrin, kódà kí abọ̀rìṣà lọ́kùnrin jọ yín lójú. Àwọn (abọ̀rìṣà) wọ̀nyẹn ń pèpè sínú Iná. Allāhu sì ń pèpè sínú Ọgbà Ìdẹ̀ra àti àforíjìn pẹ̀lú ìyọ̀ǹda Rẹ̀. Ó sì ń ṣàlàyé àwọn āyah Rẹ̀ fún àwọn ènìyàn nítorí kí wọ́n lè lo ìrántí. ');
INSERT INTO "aykyuni" VALUES(2,222,'2:222','Wọ́n sì ń bi ọ́ léèrè nípa n̄ǹkan oṣù (obìnrin). Sọ pé: “Ìnirani (sísúnmọ́ wọn lásìkò náà). Nítorí náà, ẹ yẹra fún àwọn obìnrin l’ásìkò n̄ǹkan oṣù. Ẹ má ṣe súnmọ́ wọn (fún oorun ìfẹ́) títí wọn yó fi ṣe ìmọ́ra. Tí wọ́n bá sì ti ṣe ìmọ́ra, ẹ súnmọ́ wọn ní àyè tí Allāhu pa láṣẹ fún yín. Dájúdájú Allāhu nífẹ̀ẹ́ àwọn olùronú-pìwàdà. Ó sì nífẹ̀ẹ́ àwọn olùmọ́ra.”2 ');
INSERT INTO "aykyuni" VALUES(2,223,'2:223','Oko yín ni àwọn obìnrin yín (ìyẹn àwọn ìyàwó yín). Ẹ lọ s’ínú oko yín bí ẹ bá ṣe fẹ́, kí ẹ sì ti (iṣẹ́ rere) ṣíwájú fún ẹ̀mí ara yín. Ẹ bẹ̀rù Allāhu. Kí ẹ mọ̀ pé dájúdájú ẹ máa pàdé Rẹ̀. Kí o sì fún àwọn onígbàgbọ́ òdodo ní ìró ìdùnnú.');
INSERT INTO "aykyuni" VALUES(2,224,'2:224','Ẹ má ṣe fi Allāhu ṣe ìkẹ́wọ́ fún ìbúra yín pé ẹ kò níí ṣe rere, ẹ kò níí ṣọ́ra (níbi ìwà ẹ̀ṣẹ̀), ẹ kò sì níí ṣe àtúnṣe láààrin àwọn ènìyàn. Allāhu ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(2,225,'2:225','Allāhu kò níí fi ìbúra yín tí kò ti inú yín wá bi yín, ṣùgbọ́n Ó máa fi ohun tí ó bá ti inú ọkàn yín wá bi yín. Allāhu ni Aláforíjìn, Aláfaradà.');
INSERT INTO "aykyuni" VALUES(2,226,'2:226','Ìkóraró fún oṣù mẹ́rin wà fún àwọn tó búra pé àwọn kò níí súnmọ́ obìnrin wọn. Tí wọ́n bá ṣẹ́rí padà (láààrin ìgbà náà), dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.1 ');
INSERT INTO "aykyuni" VALUES(2,227,'2:227','Tí wọ́n bá sì pinnu ìkọ̀sílẹ̀, (kí wọ́n kọ̀ wọ́n sílẹ̀.) Dájúdájú Allāhu ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(2,228,'2:228','Àwọn obìnrin tí wọ́n kọ̀ sílẹ̀ máa kóraró fún héélà mẹ́ta.Kò sì lẹ́tọ̀ọ́ fún wọn láti fi ohun tí Allāhu ṣ’ẹ̀dá (rẹ̀) sí inú àpò-ọmọ wọn pamọ́, tí wọ́n bá jẹ́ ẹni tó gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn. Àwọn ọkọ wọn ní ẹ̀tọ́ sí dídá wọn padà láààrin (àsìkò) yẹn, tí wọ́n bá gbèrò àtúnṣe. Àwọn ìyàwó ní ẹ̀tọ́ l’ọ́dọ̀ ọkọ wọn gẹ́gẹ́ bí irú èyí tí àwọn ọkọ wọn ní l’ọ́dọ̀ wọn lọ́nà tó dára. Ipò àjùlọ tún wà fún àwọn ọkùnrin lórí wọn.2 Allāhu ni Alágbára, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(2,229,'2:229','Ẹ̀ẹ̀ mejì ni ìkọ̀sílẹ̀. Nítorí náà, ẹ mú wọn mọ́ra pẹ̀lú dáadáa tàbí kí ẹ fi wọ́n sílẹ̀ pẹ̀lú dáadáa. Kò sì lẹ́tọ̀ọ́ fún yín láti gba kiní kan nínú ohun tí ẹ ti fún wọn àyàfi tí àwọn méjèèjì bá ń páyà pé àwọn kò níí lè ṣọ́ àwọn ẹnu-ààlà (òfin) Allāhu (láààrin ara wọn). Nítorí náà, tí ẹ bá páyà pé àwọn méjèèjì kò níí lè ṣọ́ àwọn ẹnu-ààlà (òfin) Allāhu, kò sí ẹ̀ṣẹ̀ fún wọn nígbà náà nípa ohun tí obìnrin bá fi ṣèràpadà (ẹ̀mí ara rẹ̀).Ìwọ̀nyí ni àwọn ẹnu-ààlà (òfin) tí Allāhu gbékalẹ̀. Nítorí náà, ẹ má ṣe tayọ rẹ̀. Ẹnikẹ́ni tí ó bá tayọ àwọn ẹnu-ààlà (òfin) tí Allāhu gbékalẹ̀, àwọn wọ̀nyẹn gan-an ni alábòsí. ');
INSERT INTO "aykyuni" VALUES(2,230,'2:230','Nítorí náà, tí ọkọ bá kọ̀ ọ́ sílẹ̀ (ní ẹ̀ẹ̀ kẹta), obìnrin náà kò lẹ́tọ̀ọ́ sí i mọ́ lẹ́yìn náà títí obìnrin náà yóò fi fẹ́ ẹlòmíìràn. Tí ẹni náà bá tún kọ̀ ọ́ sílẹ̀, kò sí ẹ̀ṣẹ̀ fún àwọn méjèèjì nígbà náà láti padà sí ọ̀dọ̀ ara wọn, tí àwọn méjèèjì bá ti lérò pé àwọn máa ṣọ́ àwọn ẹnu-ààlà (òfin) tí Allāhu gbékalẹ̀. Ìwọ̀nyẹn ni àwọn ẹnu-ààlà (òfin) tí Allāhu gbékalẹ̀ (fún wọn), tí Ó ń ṣàlàyé rẹ̀ fún ìjọ tó nímọ̀.');
INSERT INTO "aykyuni" VALUES(2,231,'2:231','Nígbà tí ẹ bá kọ àwọn obìnrin sílẹ̀ (ní ẹ̀ẹ̀ kíní tàbí ẹ̀ẹ̀ kejì), tí àsìkò (opó) wọn súnmọ́ kó parí, ẹ lè mú wọn mọ́ra pẹ̀lú dáadáa tàbí kí ẹ tú wọn sílẹ̀ (ní ìparí opó wọn) pẹ̀lú dáadáa. Ẹ má ṣe mú wọn mọ́ra ní ọ̀nà ìnira láti lè tayọ ẹnu-ààlà. Ẹnikẹ́ni tí ó bá ṣe ìyẹn, ó kúkú ti ṣàbòsí sí ẹ̀mí ara rẹ̀. Ẹ má ṣe sọ àwọn āyah Allāhu di n̄ǹkan yẹ̀yẹ́. Ẹ rántí ìdẹ̀ra Allāhu lórí yín àti ohun tí Ó sọ̀kalẹ̀ fún yín nínú Tírà àti òye ìjìnlẹ̀ (ìyẹn, sunnah Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -, tí Ó ń fi ṣe ìṣítí fún yín. Ẹ bẹ̀rù Allāhu, kí ẹ sì mọ̀ pé dájúdájú Allāhu ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(2,232,'2:232','Nígbà tí ẹ bá kọ àwọn obìnrin sílẹ̀ (ní ẹ̀ẹ̀ kíní tàbí ẹ̀ẹ̀ kejì), tí wọ́n sì parí àsìkò (opó) wọn, ẹ má ṣe dí wọn lọ́wọ́ láti fẹ́ ọkọ wọn (padà), nígbà tí wọ́n bá jọ yọ́nú síra wọn (tí wọ́n sì gba) ọ̀nà tó dára. Ìyẹn ni À ń fi ṣe wáàsí fún ẹnikẹ́ni nínú yín, tó gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn. Ìyẹn l’ó fọ̀ yín mọ́ jùlọ. Ó sì tún ṣàfọ̀mọ́ (ọkàn yín) jùlọ. Allāhu nímọ̀, ẹ̀yin kò sì nímọ̀.1 ');
INSERT INTO "aykyuni" VALUES(2,233,'2:233','Àwọn abiyamọ yóò máa fún àwọn ọmọ wọn ní ọyàn mu fún ọdún méjì gbáko, fún ẹni tí ó bá fẹ́ parí (àsìkò) ìfọ́mọlọ́yàn. Ojúṣe ni fún ẹni tí wọ́n bímọ fún láti máa ṣe (ètò) ìjẹ-ìmu wọn àti aṣọ wọn ní ọ̀nà tó dára. Wọn kò làbọ ẹ̀mí kan lọ́rùn àfi ìwọ̀n agbára rẹ̀. Wọn kò níí kó ìnira bá abiyamọ nítorí ọmọ rẹ̀. Wọn kò sì níí kó ìnira bá ẹni tí wọ́n bímọ fún nítorí ọmọ rẹ̀. Irú (ojúṣe) yẹn tún ń bẹ fún olùjogún.Tí àwọn méjèèjì bá sì fẹ́ gba ọyàn l’ẹ́nu ọmọ pẹ̀lú ìpanupọ̀ àti àṣàrò àwọn méjèèjì, kò sí ẹ̀ṣẹ̀ fún àwọn méjèèjì. Tí ẹ bá sì fẹ́ gba ẹni tí ó máa fún àwọn ọmọ yín lọ́yàn mu, kò sí ẹ̀ṣẹ̀ fún yín, tí ẹ bá ti fún wọn ní ohun tí ẹ fẹ́ fún wọn (ní owó-ọ̀yà) ní ọ̀nà tó dára. Ẹ bẹ̀rù Allāhu. Kí ẹ sì mọ̀ pé dájúdájú Allāhu ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(2,234,'2:234','Àwọn tí wọ́n kú nínú yín, tí wọ́n sì fi àwọn ìyàwó sílẹ̀, àwọn ìyàwó wọn yóò kóraró fún oṣù mẹ́rin àti ọjọ́ mẹ́wàá. Nígbà tí wọ́n bá sì parí àsìkò (opó) wọn, kò sí ẹ̀ṣẹ̀ fún yín nípa ohun tí wọ́n bá ṣe fúnra wọn (láti ní ọkọ mìíràn) ní ọ̀nà tó dára. Allāhu sì ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,235,'2:235','Kò sì sí ẹ̀ṣẹ̀ fún yín nípa ohun tí ẹ pẹ́sọ nínú ìbánisọ̀rọ̀ ìfẹ́ tàbí tí ẹ fi pamọ́ sínú ẹ̀mí yín. Allāhu mọ̀ pé dájúdájú ẹ̀yin ọkùnrin yó máa rántí wọn, ṣùgbọ́n ẹ má ṣe bá wọn ṣe àdéhùn ní ìkọ̀kọ̀, àyàfi pé kí ẹ máa sọ ọ̀rọ̀ dáadáa. Ẹ má ṣe pinnu títa kókó yìgì títí àsìkò (opó) máa fi parí. Ẹ mọ̀ pé dájúdájú Allāhu mọ ohun tí ń bẹ nínú ẹ̀mí yín, nítorí náà, ẹ bẹ̀rù Rẹ̀. Kí ẹ sì mọ̀ pé dájúdájú Allāhu ni Aláforíjìn, Aláfaradà.');
INSERT INTO "aykyuni" VALUES(2,236,'2:236','Kò sí ẹ̀ṣẹ̀ fún yín, tí ẹ bá kọ àwọn obìnrin sílẹ̀, láì tí ì súnmọ́ wọn tàbí láì tí ì sọ òdíwọ̀n sọ̀daàkí kan fún wọn ní pàtó. Kí ẹ sì fún wọn ní ẹ̀bùn (ìkọ̀sílẹ̀); kí olùgbòòrò (nínú arísìkí) fi ìwọ̀n (agbára) rẹ̀ sílẹ̀, kí tálíkà sì fi ìwọ̀n (agbára) rẹ̀ sílẹ̀ ní ọ̀nà tó dára. Ojúṣe l’ó jẹ́ fún àwọn olùṣe-rere.');
INSERT INTO "aykyuni" VALUES(2,237,'2:237','Tí ẹ bá sì kọ̀ wọ́n sílẹ̀ ṣíwájú kí ẹ tó súnmọ́ wọn, tí ẹ sì ti sọ òdíwọ̀n sọ̀daàkí kan fún wọn, ìlàjì ohun tí ẹ ti ṣòdíwọ̀n rẹ̀ ní sọ̀daàkí (ni kí ẹ fún wọn), àfi tí àwọn obìnrin náà bá ṣàmójú kúrò (níbi gbogbo rẹ̀) tàbí tí ẹni tí ìtakókó yìgì ń bẹ ní ọwọ́ rẹ̀bá ṣàmójú kúrò (níbi gbogbo rẹ̀). Kí ẹ ṣàmójú kúrò ló súnmọ́ ìbẹ̀rù Allāhu jùlọ. Ẹ má ṣe gbàgbé oore àjùlọ ààrin yín. Dájúdájú Allāhu ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(2,238,'2:238','Ẹ ṣọ́ àwọn ìrun (wákàtí márààrún) àti (ní pàápàá jùlọ) ìrun ààrin. Kí ẹ sì dúró kírun fún Allāhu láì níí sọ̀rọ̀ (tó jẹmọ́ tayé lórí ìrun).');
INSERT INTO "aykyuni" VALUES(2,239,'2:239','Ṣùgbọ́n tí ẹ bá ń bẹ̀rù (ọ̀tá l’ójú ogun ẹ̀sìn), ẹ kírun yín lórí ìrìn (ẹsẹ̀) tàbí lórí n̄ǹkan ìgùn. Nígbà tí ọkàn yín bá balẹ̀, ẹ rántí Allāhu (ẹ kírun) gẹ́gẹ́ bí Ó ṣe fi ohun tí ẹ̀yin kò mọ̀ (tẹ́lẹ̀) mọ̀ yín.');
INSERT INTO "aykyuni" VALUES(2,240,'2:240','Àwọn tí wọ́n kú nínú yín, tí wọ́n sì fi àwọn ìyàwó sáyé lọ, kí wọ́n sọ àsọọ́lẹ̀ ìjẹ-ìmu ọdún kan fún àwọn ìyàwó wọn, láì sì níí lé wọn jáde kúrò nínú ilé wọn. Tí wọ́n bá sì jáde (fúnra wọn lẹ́yìn ìjáde opó), kò sí ẹ̀ṣẹ̀ fún yín nípa ohun tí wọ́n bá fi’ra wọn ṣe ní dáadáa (láti ní ọkọ mìíràn). Allāhu ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(2,241,'2:241','N̄ǹkan ìgbádùn ní ọ̀nà tó dára tún máa wà fún àwọn obìnrin tí wọ́n kọ̀sílẹ̀. Ojúṣe l’ó jẹ́ fún àwọn olùbẹ̀rù Allāhu.');
INSERT INTO "aykyuni" VALUES(2,242,'2:242','Báyẹn ni Allāhu ṣe ń ṣàlàyé àwọn āyah Rẹ̀ fún yín, nítorí kí ẹ lè ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(2,243,'2:243','Ṣé ìwọ kò wòye sí àwọn tó jáde láti inú ilé wọn lẹ́gbẹẹgbẹ̀rún nítorí ìbẹ̀rù ikú! Allāhu sì sọ fún wọn pé: “Ẹ kú.” Lẹ́yìn náà, Ó sọ wọ́n di alààyè. Dájúdájú Allāhu ni Olóore-àjùlọ lórí àwọn ènìyàn, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ ènìyàn kò dúpẹ́ (fún Un).1 ');
INSERT INTO "aykyuni" VALUES(2,244,'2:244','Ẹ jagun fún ààbò ẹ̀sìn Allāhu, kí ẹ sì mọ̀ pé dájúdájú Allāhu ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(2,245,'2:245','Ta ni ẹni tí ó máa yá Allāhu ní dúkìá tó dára, kí Allāhu sì ṣe àdìpèlé (ẹ̀san) fún un ní àdìpèlé púpọ̀? Allāhu ń ká ọrọ̀ nílẹ̀, Ó sì ń tẹ́ ẹ sílẹ̀. Ọ̀dọ̀ Rẹ̀ sì ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(2,246,'2:246','Ṣé ìwọ kò wòye sí àwọn aṣíwájú nínú àwọn ọmọ ’Isrọ̄’īl, lẹ́yìn (ìgbà Ànábì) Mūsā? Nígbà tí wọ́n sọ fún Ànábì tiwọn pé: “Yan ọba kan fún wa, kí á lọ jagun fún ààbò ẹ̀sìn Allāhu.” Ó sọ pé: “Ṣebí ó ṣeé ṣe pé tí wọ́n bá ṣe ogun jíjà ní ọ̀ran-anyàn le yín lórí tán, ẹ ò kúkú níí jagun?” Wọ́n wí pé: “Kí ni ó máa dí wa lọ́wọ́ láti jagun fún ààbò ẹ̀sìn Allāhu? Wọ́n kúkú ti lé àwa àti àwọn ọmọ wa jáde kúrò nínú ilé wa!” Àmọ́ nígbà tí A ṣe ogun ẹ̀sìn ní ọ̀ran-anyàn lé wọn lórí tán, wọ́n pẹ̀yìn dà àfi díẹ̀ nínú wọn. Allāhu sì ni Onímọ̀ nípa àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(2,247,'2:247','Ànábì wọn sọ fún wọn pé: “Dájúdájú Allāhu ti gbé Tọ̄lūt dìde fún yín ní ọba.” Wọ́n wí pé: “Báwo ni ó ṣe lè jẹ ọba lé wa lórí nígbà tí ó jẹ́ pé àwa ní ẹ̀tọ́ sí ipò ọba jù ú lọ? Wọn kò sì fún un ní owó púpọ̀?” Ó sọ pé: “Dájúdájú Allāhu ṣà á lẹ́ṣà le yín lórí. Ó sì fún un ní àlékún púpọ̀ nínú ìmọ̀ àti okun ara. Allāhu ń fún ẹni tí Ó bá fẹ́ ní ìjọba Rẹ̀. Allāhu ni Olùgbààyè, Onímọ̀.”');
INSERT INTO "aykyuni" VALUES(2,248,'2:248','Ànábì wọn tún sọ fún wọn pé: “Dájúdájú àmì ìjọba rẹ̀ ni pé, àpótí yóò wá ba yín. N̄ǹkan ìfàyàbalẹ̀ láti ọ̀dọ̀ Olúwa yín àti ohun tó ṣẹ́kù nínú ohun tí àwọn ènìyàn (Ànábì) Mūsā àti ènìyàn (Ànábì) Hārūn fi sílẹ̀ ń bẹ nínú àpótí náà. Àwọn mọlāika máa rù ú wá (ba yín). Dájúdájú àmì wà nínú ìyẹn fún yín, tí ẹ bá jẹ́ onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(2,249,'2:249','Nígbà tí Tọ̄lūt jáde pẹ̀lú àwọn ọmọ ogun, ó sọ pé: “Dájúdájú Allāhu máa fi odò kan dan yín wò. Nítorí náà, ẹnikẹ́ni tí ó bá mu nínú rẹ̀, kì í ṣe ẹni mi. Ẹni tí kò bá tọ́ ọ wò dájúdájú òun ni ẹni mi, àyàfi ẹni tí ó bá bu ìwọ̀n ẹ̀kúnwọ́ rẹ̀ kan mu.” (Ṣùgbọ́n) wọ́n mu nínú rẹ̀ àfi díẹ̀ nínú wọn. Nígbà tí òun àti àwọn tó gbàgbọ́ ní òdodo pẹ̀lú rẹ̀ sọdá odò náà, wọ́n sọ pé: “Kò sí agbára kan fún wa lónìí tí a lè fi k’ojú Jālūt àti àwọn ọmọ ogun rẹ̀.” Àwọn tó mọ̀ pé dájúdájú àwọn máa pàdé Allāhu, wọ́n sọ pé: “Mélòó mélòó nínú àwọn ìjọ (ogun) kékeré tó ti ṣẹ́gun ìjọ (ogun) púpọ̀ pẹ̀lú ìyọ̀ǹda Allāhu. Allāhu ń bẹ pẹ̀lú àwọn onísùúrù.”');
INSERT INTO "aykyuni" VALUES(2,250,'2:250','Nígbà tí wọ́n jáde sí Jālūt àti àwọn ọmọ ogun rẹ̀, wọ́n sọ pé: “Olúwa wa, fún wa ní omi sùúrù mu, fi ẹsẹ̀ wa rinlẹ̀ ṣinṣin, kí O sì ràn wá lọ́wọ́ lórí ìjọ aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(2,251,'2:251','Wọ́n sì ṣẹ́gun wọn pẹ̀lú ìyọ̀ǹda Allāhu. Dāwūd pa Jālūt. Allāhu sì fún un ní ìjọba àti ọgbọ́n. Ó tún fi ìmọ̀ mọ̀ ọ́n nínú ohun tí Ó fẹ́. Tí kò bá jẹ́ pé Allāhu ń dènà (aburú) fún àwọn ènìyàn ni, tí Ó ń fi apá kan wọn dènà (aburú) fún apá kan, orí ilẹ̀ ìbá ti bàjẹ́. Ṣùgbọ́n Allāhu ni Olóore àjùlọ lórí gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(2,252,'2:252','Ìwọ̀nyí ni àwọn āyah Allāhu, tí À ń ké e fún ọ pẹ̀lú òdodo. Àti pé dájúdájú ìwọ wà lára àwọn Òjíṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,253,'2:253','Àwọn Òjíṣẹ́ wọ̀nyẹn, A ṣoore àjùlọ fún apá kan wọn lórí apá kan. Ó ń bẹ nínú wọn, ẹni tí Allāhu bá sọ̀rọ̀ (tààrà). Ó sì ṣe àgbéga àwọn ipò fún apá kan wọn. A fún (Ànábì) ‘Īsā ọmọ Mọryam ní àwọn ẹ̀rí tó yanjú. A tún fi Ẹ̀mí Mímọ́ (ìyẹn, mọlāika Jibrīl) ràn án lọ́wọ́. Àti pé tí ó bá jẹ́ pé Allāhu bá fẹ́, àwọn tó wá lẹ́yìn wọn ìbá tí bára wọn jà lẹ́yìn tí àwọn ẹ̀rí tó yanjú ti dé bá wọn. Ṣùgbọ́n wọ́n yapa-ẹnu (lórí ẹ̀sìn ’Islām). Ó ń bẹ nínú wọn ẹni tó gbàgbọ́ ní òdodo (tí ó jẹ́ mùsùlùmí). Ó sì ń bẹ nínú wọn ẹni tó ṣàì gbàgbọ́ (tí ó di nasọ̄rọ̄).Àti pé tí Allāhu bá fẹ́, wọn ìbá tí bá’ra wọn jà, ṣùgbọ́n Allāhu ń ṣe ohun tí Ó bá fẹ́. ');
INSERT INTO "aykyuni" VALUES(2,254,'2:254','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ ná nínú ohun tí A ṣe ní arísìkí fún yín ṣíwájú kí ọjọ́ kan tó dé. Kò níí sí títà-rírà kan nínú rẹ̀. Kò níí sí olólùfẹ́ kan, kò sì níí sí ìṣìpẹ̀ kan (fún àwọn aláìgbàgbọ́). Àwọn aláìgbàgbọ́, àwọn sì ni alábòsí.1 ');
INSERT INTO "aykyuni" VALUES(2,255,'2:255','Allāhu, kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun, Alààyè, Alámòjúútó-ẹ̀dá. Òògbé kì í ta Á. Àti pé oorun kì í kùn Ún. TiRẹ̀ ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Ta ni ẹni tí ó máa ṣìpẹ̀ lọ́dọ̀ Rẹ̀ àfi pẹ̀lú ìyọ̀ǹda Rẹ̀?Ó mọ ohun tí ń bẹ níwájú wọn àti ohun tí ń bẹ lẹ́yìn wọn. Wọn kò sì ní ìmọ̀ àmọ̀tán nípa kiní kan nínú ìmọ̀ Rẹ̀ àfi ohun tí Ó bá fẹ́ (fi mọ̀ wọ́n). Àga Rẹ̀ gbààyè ju àwọn sánmọ̀ àti ilẹ̀.2 Ṣíṣọ́ sánmọ̀ àti ilẹ̀ kò sì dá A lágara. Allāhu ga, Ó tóbi.3 ');
INSERT INTO "aykyuni" VALUES(2,256,'2:256','Kò sí ìjẹnípá nínú ẹ̀sìn. Ìmọ̀nà ti fojú hàn kúrò nínú ìṣìnà. Ẹnikẹ́ni tí ó bá lòdì sí àwọn òrìṣà, tí ó sì ní ìgbàgbọ́ òdodo nínú Allāhu, ó kúkú ti dìmọ́ okùn tó fọkàn balẹ̀ jùlọ, tí kò níí já. Allāhu ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(2,257,'2:257','Allāhu ni Alárànṣe àwọn tó gbàgbọ́ ní òdodo. Ó ń mú wọn jáde kúrò nínú àwọn òkùnkùn wá sínú ìmọ́lẹ̀. Àwọn tó sì ṣàì gbàgbọ́, àwọn òrìṣà ni aláfẹ̀yìntì wọn. Àwọn òrìṣà ń mú wọn jáde kúrò nínú ìmọ́lẹ̀ wá sínú àwọn òkùnkùn. Àwọn wọ̀nyẹn ni èrò inú Iná. Olùṣegbére sì ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(2,258,'2:258','Ṣé ìwọ kò wòye sí ẹni tó bá (Ànábì) ’Ibrọ̄hīm jiyàn nípa Olúwa rẹ̀, nítorí pé Allāhu fún un ní ìjọba? Nígbà tí (Ànábì) ’Ibrọ̄hīm sọ pé: “Olúwa mi ni Ẹni tí ń sọ ẹ̀dá di alààyè. Ó sì ń sọ ẹ̀dá di òkú.” Ó wí pé: “Èmi náà ń sọ ẹ̀dá di alààyè. Mo sì ń sọ ẹ̀dá di òkú.” (Ànábì) ’Ibrọ̄hīm sọ pé: “Dájúdájú Allāhu ń mú òòrùn wá láti ibùyọ. Mú un wa nígbà náà láti ibùwọ̀.” Wọ́n sì pa ọ̀rọ̀ mọ́ ẹni tó ṣàì gbàgbọ́ lẹ́nu. Allāhu kò níí fi ọ̀nà mọ ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(2,259,'2:259','Tàbí gẹ́gẹ́ bí ẹni tí ó kọjá nínú ìlú kan nígbà tí (àwọn ilé ibẹ̀) ti dàwó lulẹ̀ tòrùlé-tòrùlé wọn. Ó sọ pé: “Báwo ni Allāhu yó ṣe sọ èyí di alààyè lẹ́yìn ikú rẹ̀!” Allāhu sì sọ ọ́ di òkú fún ọgọ́rùn-ún ọdún. Lẹ́yìn náà, Ó gbé e dìde. Ó sọ pé: “Ìgbà wo ni o ti wà níbí?” Ó sọ pé: “Mo wà níbí fún ọjọ́ kan tàbí ìlàjì ọjọ́.” Ó sọ pé: “Bẹ́ẹ̀ kọ́, o ti wà níbí fún ọgọ́rùn-ún ọdún. Wo oúnjẹ rẹ àti omi rẹ, kò yí padà. Tún wo kẹ́tẹ́kẹ́tẹ́ rẹ, kí Á lè fi ọ́ ṣe àmì kan fún àwọn ènìyàn (ni ìwọ̀nyí jẹ́). Wo eegun (ràkúnmí náà), wo bí A ó ṣe tò wọ́n papọ̀ mọ́ra wọn. Lẹ́yìn náà, (wo) bí A ó ṣe da ẹran bò ó lára. Nígbà tí ó fojú hàn sí i kedere (bẹ́ẹ̀), ó sọ pé: “Mo mọ̀ pé dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan.”1 ');
INSERT INTO "aykyuni" VALUES(2,260,'2:260','Nígbà tí (Ànábì) ’Ibrọ̄hīm sọ pé: “Olúwa mi, fi hàn mí bí Ìwọ yó ṣe sọ àwọn òkú di alààyè.” (Allāhu) sọ pé: “Ṣé ìwọ kò gbàgbọ́ ni?” Ó sọ pé: “Bẹ́ẹ̀ ni (mo gbàgbọ́), ṣùgbọ́n kí ọkàn mi lè balẹ̀ ni”. (Allāhu) sọ pé: “Mú mẹ́rin nínú àwọn ẹyẹ, kí o so wọ́n mọ́lẹ̀ sí ọ̀dọ̀ rẹ (kí o pa wọ́n, kí o sì gún wọn papọ̀ mọ́ra wọn). Lẹ́yìn náà, fi ìpín nínú wọn sórí àpáta kọ̀ọ̀kan. Lẹ́yìn náà, pè wọ́n. Wọ́n máa sáré wá bá ọ. Kí o sì mọ̀ pé dájúdájú Allāhu ni Alágbára, Ọlọ́gbọ́n.”');
INSERT INTO "aykyuni" VALUES(2,261,'2:261','Àpèjúwe àwọn tó ń ná dúkìá wọn fún ẹ̀sìn Allāhu dà bí àpèjúwe kóró èso kan tí ó hu ṣiri méje jáde, tí ọgọ́rùn-ún kóró sì wà lára ṣiri kọ̀ọ̀kan. Allāhu yó ṣe àdìpèlé fún ẹni tí Ó bá fẹ́. Allāhu ni Olùgbààyè, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(2,262,'2:262','Àwọn tó ń ná dúkìá wọn fún ẹ̀sìn Allāhu, tí wọn kò sì fi ìrègún àti ìpalára tẹ̀lé ohun tí wọ́n ná, ẹ̀san wọn ń bẹ fún wọn lọ́dọ̀ Olúwa wọn. Ìbẹ̀rù kò níí sí fún wọn. Wọn kò sì níí banújẹ́.');
INSERT INTO "aykyuni" VALUES(2,263,'2:263','Ọ̀rọ̀ rere àti àforíjìn lóore ju sàráà tí ìpalára tẹ̀lé. Allāhu sì ni Ọlọ́rọ̀, Aláfaradà.');
INSERT INTO "aykyuni" VALUES(2,264,'2:264','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe fi ìrègún àti ìpalára ba àwọn sàráà yín jẹ́, bí ẹni tí ó ń ná dúkìá rẹ̀ pẹ̀lú ṣekárími, kò sì gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn. Àpèjúwe rẹ̀ dà bí àpèjúwe àpáta kan tí erùpẹ̀ ń bẹ lórí rẹ̀. Òjò ńlá rọ̀ sí i, ó sì kó erùpẹ̀ kúrò lórí rẹ̀ pátápátá. Wọn kò ní agbára lórí kiní kan nínú ohun tí wọ́n ṣe níṣẹ́. Allāhu kò níí fi ọ̀nà mọ ìjọ aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(2,265,'2:265','Àpèjúwe àwọn tó ń ná dúkìá wọn láti wá ìyọ́nú Allāhu àti (nítorí pé) ó rinlẹ̀ nínú ẹ̀mí wọn (pé ẹ̀san rere ń bẹ fún àwọn lórí rẹ̀ lọ́dọ̀ Allāhu), ó dà bí àpèjúwe ọgbà oko tó wà lórí ilẹ̀ gíga kan, tí òjò ńlá rọ̀ sí, tí àwọn èso rẹ̀ sì yọ jáde ní ìlọ́po méjì. Tí òjò ńlá kò bá sì rọ̀ sí i, ìrì ń ṣẹ̀ sí i. Allāhu sì ni Olùríran nípa ohun tí ẹ̀ ń ṣe.');
INSERT INTO "aykyuni" VALUES(2,266,'2:266','Ǹjẹ́ ẹnì kan nínú yín nífẹ̀ẹ́ sí kí òun ní ọgbà oko dàbínù àti àjàrà, tí àwọn odò ń ṣàn ní abẹ́ rẹ̀, tí oríṣiríṣi èso tún wà fún un nínú rẹ̀, kí ogbó dé bá a, ó sì ní àwọn ọmọ wẹẹrẹ tí kò lágbara (iṣẹ́ oko ṣíṣe), kí atẹ́gùn líle tí iná ń bẹ nínú rẹ̀ kọlu oko náà, kí ó sì jóná? Báyẹn ni Allāhu ṣe ń ṣ’àlàyé àwọn āyah náà fún yín nítorí kí ẹ lè ronú jinlẹ̀.');
INSERT INTO "aykyuni" VALUES(2,267,'2:267','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ ná nínú àwọn n̄ǹkan dáadáa tí ẹ ṣe níṣẹ́ àti nínú àwọn n̄ǹkan tí A mú jáde fún yín láti inú ilẹ̀. Ẹ má ṣe gbèrò láti ná nínú èyí tí kò dára. Ẹ̀yin náà kò níí gbà á àfi kí ẹ dijú gbà á. Kí ẹ sì mọ̀ pé dájúdájú Allāhu ni Ọlọ́rọ̀, Ẹlẹ́yìn.1 ');
INSERT INTO "aykyuni" VALUES(2,268,'2:268','Èṣù ń fi òṣì dẹ́rù bà yín, ó sì ń pa yín ní àṣẹ ìbàjẹ́ ṣíṣe. Allāhu sì ń ṣe àdéhùn àforíjìn àti oore àjùlọ láti ọ̀dọ̀ Rẹ̀ fún yín. Allāhu ní Oníbúọlá, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(2,269,'2:269','Ó ń fún ẹni tí Ó bá fẹ́ ní òye ìjìnlẹ̀. Ẹni tí A bá sì fún ní òye ìjìnlẹ̀ A kúkú ti fún un ní oore púpọ̀. Ẹnì kan kò níí lo ìrántí àfi àwọn onílàákàyè. ');
INSERT INTO "aykyuni" VALUES(2,270,'2:270','Ohunkóhun tí ẹ bá ná ní ìnáwó tàbí (ohunkóhun) tí ẹ bá jẹ́ ní ẹ̀jẹ́, dájúdájú Allāhu mọ̀ ọ́n. Kò sì níí sí olùrànlọ́wọ́ kan fún àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(2,271,'2:271','Tí ẹ bá ṣàfi hàn àwọn sàráà, ó kúkú dára. Tí ẹ bá sì fi pamọ́, tí ẹ lọ́ fún àwọn aláìní, ó sì dára jùlọ fún yín. Allāhu sì máa pa àwọn ìwà àìdáa yín rẹ́ fún yín. Allāhu sì ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(2,272,'2:272','Ìmọ̀nà wọn kò sí lọ́rùn rẹ, ṣùgbọ́n Allāhu ń fi ẹni tí Ó bá fẹ́ mọ̀nà. Ohunkóhun tí ẹ bá ń ná ní ohun rere, fún ẹ̀mí ara yín ni. Ẹ ò sì gbọ́dọ̀ náwó àfi láti fi wá ojú rere Allāhu. Ohunkóhun tí ẹ bá sì ná ní ohun rere, A ó san yín ní ẹ̀san (rẹ̀) ní ẹ̀kún-rẹ́rẹ́. Wọn kò sì níí ṣàbòsí si yín.');
INSERT INTO "aykyuni" VALUES(2,273,'2:273','(Ẹ tọrẹ) fún àwọn aláìní tí wọ́n sé (ara wọn mọ́nú mọ́sálásí Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a - nítorí kí wọ́n lè máa jagun) fún ààbò ẹ̀sìn Allāhu. Wọn kò sì lágbára lílọ-bíbọ̀ lórí ilẹ̀ (fún òkòwò ṣíṣe). Ẹni tí kò mọ̀ wọ́n máa kà wọ́n kún ọlọ́rọ̀ látara àìṣagbe. O máa mọ̀ wọ́n pẹ̀lú àmì wọn. Wọn kò níí tọrọ n̄ǹkan lọ́wọ́ ènìyàn lemọ́lemọ́.Ohunkóhun tí ẹ bá ná ní ohun rere, dájúdájú Allāhu ni Onímọ̀ nípa rẹ̀. ');
INSERT INTO "aykyuni" VALUES(2,274,'2:274','Àwọn tó ń ná dúkìá wọn ní òru àti ní ọ̀sán, ní ìkọ̀kọ̀ àti ní gban̄gba, ẹ̀san wọn ń bẹ fún wọn ní ọ̀dọ̀ Olúwa wọn. Kò sí ìpáyà fún wọn. Wọn kò sì níí banújẹ́.');
INSERT INTO "aykyuni" VALUES(2,275,'2:275','Àwọn tó ń jẹ èlé kò níí dìde (nínú sàréè) àfi bí ẹni tí Èṣù fọwọ́ bà tí ń ta gẹ̀ẹ́gẹ̀ẹ́ yó ṣe dìde. Ìyẹn rí bẹ́ẹ̀ nítorí pé wọ́n wí pé: “Òwò ṣíṣe dà bí òwò èlé.” Allāhu sì ṣe òwò ṣíṣe ní ẹ̀tọ́, Ó sì ṣe òwò èlé ní èèwọ̀. Ẹnikẹ́ni tí wáàsí bá dé bá láti ọ̀dọ̀ Olúwa rẹ̀, tí ó sì jáwọ́, tirẹ̀ ni èyí tó ṣíwájú, ọ̀rọ̀ rẹ̀ sì di ọ̀dọ̀ Allāhu. Ẹnikẹ́ni tí ó bá sì padà (síbi òwò èlé), àwọn wọ̀nyẹn ni èrò inú Iná. Olùṣegbére sì ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(2,276,'2:276','Allāhu máa run òwò èlé. Ó sì máa bù sí àwọn ọrẹ. Allāhu kò nífẹ̀ẹ́ gbogbo aláìgbàgbọ́, ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(2,277,'2:277','Dájúdájú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n ṣe àwọn iṣẹ́ rere, tí wọ́n kírun, tí wọ́n sì yọ Zakāh, ẹ̀san wọn ń bẹ fún wọn ní ọ̀dọ̀ Olúwa wọn. Kò sí ìpáyà fún wọn. Wọn kò sì níí banújẹ́.');
INSERT INTO "aykyuni" VALUES(2,278,'2:278','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ bẹ̀rù Allāhu, kí ẹ sì fi ohun tó ṣẹ́kù nínú èlé sílẹ̀, tí ẹ bá jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(2,279,'2:279','Tí ẹ kò bá ṣe bẹ́ẹ̀, ẹ lọ mọ̀ pé ogun kan láti ọ̀dọ̀ Allāhu àti Òjíṣẹ́ Rẹ̀ (ń bọ̀ wá bá yín). Tí ẹ bá sì ronú pìwàdà, tiyín ni ojú-owó yín. Ẹ kò níí ṣàbòsí (sí wọn). Wọn kò sì níí ṣàbòsí sí yín.');
INSERT INTO "aykyuni" VALUES(2,280,'2:280','Tí (onígbèsè) bá jẹ́ ẹni tí ara ń ni, ẹ wò ó níran títí di àsìkò ìdẹ̀ra. Àti pé kí ẹ fị ṣe sàráà (fún un) lóore jùlọ fún yín, tí ẹ bá mọ̀.');
INSERT INTO "aykyuni" VALUES(2,281,'2:281','Ẹ bẹ̀rù ọjọ́ kan tí wọ́n máa da yín padà sí ọ̀dọ̀ Allāhu. Lẹ́yìn náà, wọ́n máa san ẹ̀mí kọ̀ọ̀kan ní ẹ̀san ní ẹ̀kún rẹ́rẹ́ (nípa) ohun tí ó ṣe níṣẹ́. Wọn kò sì níí ṣàbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(2,282,'2:282','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nígbà tí ẹ bá ń ṣe kátà-kárà ní àwìn fún gbèdéke àkókò kan, ẹ ṣe àkọsílẹ̀ rẹ̀. Kí akọ̀wé ṣe àkọsílẹ̀ rẹ̀ láààrin yín ní ọ̀nà ẹ̀tọ́. Kí akọ̀wé má ṣe kọ̀ láti ṣe àkọsílẹ̀ gẹ́gẹ́ bí Allāhu ṣe fi mọ̀ ọ́n. Nítorí náà. kí ó ṣe àkọsílẹ̀. Kí ẹni tí ẹ̀tọ́ wà lọ́rùn rẹ̀ pè é fún un. Kí ó bẹ̀rù Allāhu, Olúwa rẹ̀. Kò má ṣe dín kiní kan kù nínú rẹ̀. Tí ẹni tí ẹ̀tọ́ wà lọ́rùn rẹ̀ bá jẹ́ aláìlóye tàbí aláìsàn, tàbí kò lè pè é (fúnra rẹ̀), kí alámòjúútó rẹ̀ bá a pè é ní ọ̀nà ẹ̀tọ́. Kí ẹ pe ẹlẹ́rìí méjì nínú àwọn ọkùnrin yín láti jẹ́rìí sí i. Tí kò bá sí ọkùnrin méjì, kí ẹ wá ọkùnrin kan àti obìnrin méjì nínú àwọn ẹlẹ́rìí tí ẹ yọ́nú sí; nítorí pé tí ọ̀kan nínú àwọn obìnrin méjèèjì bá gbàgbé, ọ̀kan yó sì rán ìkejì létí. Kí àwọn ẹlẹ́rìí má ṣe kọ̀ tí wọ́n bá pè wọ́n (láti jẹ́rìí). Ẹ má ṣe káàárẹ̀ láti ṣe àkọsílẹ̀ rẹ̀, ó kéré ni tàbí ó tóbi, títí di àsìkò ìsangbèsè rẹ̀. Ìyẹn ni déédé jùlọ lọ́dọ̀ Allāhu. Ó sì gbé ìjẹ́rìí dúró jùlọ. Ó tún fi súnmọ́ jùlọ pé ẹ̀yin kò fi níí ṣeyèméjì. Àfi tí ó bá jẹ́ kátà-kárà ojú ẹsẹ̀ tí ẹ̀ ń ṣe ní àtọwọ́dọ́wọ́ láààrin ara yín, kò sí ẹ̀ṣẹ̀ fún yín tí ẹ kò bá ṣe àkọsílẹ̀ rẹ̀. Ẹ wá ẹlẹ́rìí sẹ́ nígbà tí ẹ bá ń ṣe kátà-kárà (ọjà àwìn). Wọn kò sì gbọ́dọ̀ kó ìnira bá akọ̀wé àti ẹlẹ́rìí. Tí ẹ bá sì ṣe bẹ́ẹ̀, dájúdájú ìbàjẹ́ l’ẹ ṣe. Ẹ bẹ̀rù Allāhu. Allāhu yó sì máa fi ìmọ̀ mọ̀ yín. Allāhu ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(2,283,'2:283','Àti pé tí ẹ bá wà lórí ìrìn-àjò, tí ẹ̀yin kò sì rí akọ̀wé, ẹ gba ohun ìdógò. (Ṣùgbọ́n) tí apá kan yín bá fi ọkàn tán apá kan, kí ẹni tí wọ́n fi ọkàn tán dá ohun tí wọ́n fi ọkàn tán an lé lórí padà, kí ó sì bẹ̀rù Allāhu, Olúwa rẹ̀. Ẹ má ṣe fi ẹ̀rí pamọ́. Ẹnikẹ́ni tí ó bá fi pamọ́, dájúdájú ọkàn rẹ̀ ti dẹ́ṣẹ̀. Allāhu sì ni Onímọ̀ nípa ohun tí ẹ̀ ń ṣe.');
INSERT INTO "aykyuni" VALUES(2,284,'2:284','Ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Tí ẹ bá ṣàfi hàn ohun tó wà nínú ẹ̀mí yín, tàbí ẹ fi pamọ́, Allāhu yó ṣírò rẹ̀ fún yín Lẹ́yìn náà, Ó máa foríjin ẹni tí Ó bá fẹ́. Ó sì máa jẹ ẹni tí Ó bá fẹ́ níyà. Allāhu sì ni Alágbára lórí gbogbo n̄ǹkan. ');
INSERT INTO "aykyuni" VALUES(2,285,'2:285','Òjíṣẹ́ náà - kí ìkẹ́ àti ọlà máa bá a - gbàgbọ́ nínú ohun tí wọ́n sọ̀kalẹ̀ fún un láti ọ̀dọ̀ Olúwa rẹ̀. Àwọn onígbàgbọ́ òdodo náà (gbàgbọ́ nínú rẹ̀). Ẹnì kọ̀ọ̀kan (wọn) gbàgbọ́ nínú Allāhu, àwọn mọlāika Rẹ̀, àwọn Tírà Rẹ̀ àti àwọn Òjíṣẹ́ Rẹ̀.A kò ya ẹnì kan sọ́tọ̀ nínú àwọn Òjíṣẹ́ Rẹ̀. Wọ́n sì sọ pé: “A gbọ́ (àṣẹ), a sì tẹ̀lé (àṣẹ). À ń tọrọ àforíjìn Rẹ, Olúwa wa. Ọ̀dọ̀ Rẹ sì ni àbọ̀ ẹ̀dá.” ');
INSERT INTO "aykyuni" VALUES(2,286,'2:286','Allāhu kò làbọ ẹ̀mí kan lọ́rùn àfi ìwọ̀n agbára rẹ̀. (Ẹ̀san) ohun tí ó ṣe níṣẹ́ (rere) ń bẹ fún un. (Ìyà) ohun tí ó ṣe níṣẹ́ (ibi) ń bẹ fún un pẹ̀lú. Olúwa wa, má ṣe mú wa tí a bá gbàgbéra tàbí (tí) a bá ṣàṣìṣe. Olúwa wa, má ṣe di ẹrù tó wúwo lé wa lórí, gẹ́gẹ́ bí O ṣe dì í ru àwọn tó ṣíwájú wa. Olúwa wa, má ṣe dìrù wá ohun tí kò sí agbára rẹ̀ fún wa. Ṣàmójú kúrò fún wa, foríjìn wá, kí O sì ṣàánú wa. Ìwọ ni Aláfẹ̀yìntì wa. Nítorí náà, ràn wá lọ́wọ́ lórí ìjọ aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(3,1,'3:1','’Alif lām mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(3,2,'3:2','Allāhu, kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun, Alààyè, Alámòjúútó-ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(3,3,'3:3','Ó sọ Tírà (al-Ƙur’ān) kalẹ̀ fún ọ pẹ̀lú òdodo, ó ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó wà ṣíwájú rẹ̀. Ó sì sọ at-Taorāh àti al-’Injīl kalẹ̀.');
INSERT INTO "aykyuni" VALUES(3,4,'3:4','Tẹ́lẹ̀tẹ́lẹ̀ wọ́n jẹ́ ìmọ̀nà fún àwọn ènìyàn.Ó tún sọ ọ̀rọ̀-ìpínyà (ọ̀rọ̀ tó ń ṣèpínyà láààrin òdodo àti irọ́) kalẹ̀.2 Dájúdájú àwọn tó ṣàì gbàgbọ́ nínú àwọn āyah Allāhu, ìyà tó le ń bẹ fún wọn. Allāhu sì ni Alágbára, Olùgbẹ̀san. ');
INSERT INTO "aykyuni" VALUES(3,5,'3:5','Dájúdájú Allāhu, kò sí kiní kan tó pamọ́ fún Un nínú ilẹ̀ àti nínú sánmọ̀.');
INSERT INTO "aykyuni" VALUES(3,6,'3:6','Òun ni Ẹni tí Ó ń yàwòrán yín sínú àpòlùkẹ́ bí Ó ṣe fẹ́. Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun, Alágbára Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(3,7,'3:7','Òun ni Ẹni tí Ó sọ Tírà kalẹ̀ fún ọ; àwọn āyah aláìnípọ́n-na wà nínú rẹ̀ - àwọn sì ni ìpìlẹ̀ Tírà-, onípọ́n-na sì ni ìyókù.2 Ní ti àwọn tí ìgbúnrí kúrò níbi òdodo wà nínú ọkàn wọn, wọn yóò máa tẹ̀lé èyí tó ní pọ́n-na nínú rẹ̀ láti fi wá wàhálà àti láti fí wá ìtúmọ̀ (òdì) fún un. Kò sì sí ẹni tó nímọ̀ pàápàá ìtúmọ̀ rẹ̀ àfi Allāhu. Àwọn àgbà nínú ìmọ̀ ẹ̀sìn, wọ́n ń sọ pé: “A gbà á gbọ́. Láti ọ̀dọ̀ Olúwa wa ni gbogbo rẹ̀ (ti sọ̀kalẹ̀).” Kò sí ẹni tó ń lo ìrántí àyàfi àwọn onílàákàyè. ');
INSERT INTO "aykyuni" VALUES(3,8,'3:8','Olúwa wa, má ṣe yí wa lọ́kàn padà lẹ́yìn tí O ti tọ́ wa sọ́nà. Ta wá lọ́rẹ ìkẹ́ láti ọ̀dọ̀ Rẹ, dájúdájú Ìwọ gan-an ni Ọlọ́rẹ.');
INSERT INTO "aykyuni" VALUES(3,9,'3:9','Olúwa wa, dájúdájú Ìwọ l’O máa kó àwọn ènìyàn jọ ní ọjọ́ kan, tí kò sí iyèméjì nínú rẹ̀. Dájúdájú Allāhu kì í yẹ àdéhùn.');
INSERT INTO "aykyuni" VALUES(3,10,'3:10','Dájúdájú àwọn tó ṣàì gbàgbọ́, àwọn dúkìá wọn àti àwọn ọmọ wọn kò níí rọ̀ wọ́n lọ́rọ̀ kiní kan lọ́dọ̀ Allāhu. Àwọn wọ̀nyẹn, àwọn ni n̄ǹkan ìkoná.');
INSERT INTO "aykyuni" VALUES(3,11,'3:11','(Ìṣesí wọn) dà bí ìṣesí àwọn ènìyàn Fir‘aon àti àwọn tó ṣíwájú wọn, tí wọ́n pe àwọn āyah Wa ní irọ́. Nítorí náà, Allāhu mú wọn nítorí ẹ̀ṣẹ̀ wọn. Allāhu sì le níbi ìyà.');
INSERT INTO "aykyuni" VALUES(3,12,'3:12','Sọ fún àwọn tó ṣàì gbàgbọ́ pé: “Wọ́n máa ṣẹ́gun yín. Wọ́n sì máa ko yín jọ lọ sí iná Jahanamọ. Ibùgbé náà sì burú.”');
INSERT INTO "aykyuni" VALUES(3,13,'3:13','Àmì kúkú wà fún yín níbi àwọn ìjọ méjì tí wọ́n pàdé (ara wọn). Ìjọ kan ń jà fún ààbò ẹ̀sìn Allāhu. Aláìgbàgbọ́ sì ni ìjọ kejì tí ó ń rí ìjọ kìíní bí ìlọ́po méjì wọn ní rírí ojú.Allāhu ń fi àrànṣe Rẹ̀ ṣe ìkúnlọ́wọ́ fún ẹni tí Ó bá fẹ́. Dájúdájú àríwòye wà nínú ìyẹn fún àwọn olùríran. ');
INSERT INTO "aykyuni" VALUES(3,14,'3:14','Wọ́n ṣe é ní ọ̀ṣọ́ fún àwọn ènìyàn; ìfẹ́ ìgbádùn ara lára àwọn obìnrin, àwọn ọmọkùnrin, àwọn owó wúrà àti fàdákà púpọ̀ jaburata àti àwọn ẹṣin tí wọ́n ṣe ní ọ̀ṣọ́, àwọn ẹran-ọ̀sìn àti (n̄ǹkan) oko. Ìyẹn ni n̄ǹkan ìgbáyé-gbádùn. Ọ̀dọ̀ Allāhu sì ni àbọ̀ rere wà.');
INSERT INTO "aykyuni" VALUES(3,15,'3:15','Sọ pé: “Ṣé kí n̄g sọ n̄ǹkan tó dára ju ìyẹn lọ fún yín?” Àwọn Ọgbà tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀ ń bẹ fún àwọn tó bá bẹ̀rù (Allāhu) ní ọ̀dọ̀ Olúwa wọn. Olùṣegbére ni wọ́n nínú rẹ̀. Àwọn ìyàwó mímọ́ àti ìyọ́nú láti ọ̀dọ̀ Allāhu (tún ń bẹ fún wọn). Allāhu sì ni Olùríran nípa àwọn ẹrúsìn,');
INSERT INTO "aykyuni" VALUES(3,16,'3:16','Àwọn tó ń sọ pé: “Olúwa wa, dájúdájú àwa gbàgbọ́. Nítorí náà, forí àwọn ẹ̀ṣẹ̀ wa jìn wá. Kí O sì ṣọ́ wa níbi ìyà Iná.”');
INSERT INTO "aykyuni" VALUES(3,17,'3:17','(Ọgbà Ìdẹ̀ra yẹn wà fún) àwọn onísùúrù, àwọn olódodo, àwọn olùtẹ̀lé-àṣẹ Allāhu, àwọn olùnáwó-fẹ́sìn àti àwọn olùtọrọ-àforíjìn ní àsìkò sààrì.');
INSERT INTO "aykyuni" VALUES(3,18,'3:18','Allāhu jẹ́rìí pé dájúdájú kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Àwọn mọlāika àti onímọ̀ ẹ̀sìn (tún jẹ́rìí bẹ́ẹ̀.), Allāhu ni Onídéédé. Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun, Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(3,19,'3:19','Dájúdájú ẹ̀sìn tó wà lọ́dọ̀ Allāhu ni ’Islām. Àwọn tí A fún ní Tírà (àwọn yẹhudi àti nasọ̄rọ̄) kò yapa-ẹnu (lórí ẹ̀sìn náà) àfi lẹ́yìn tí ìmọ̀ dé bá wọn ní ti ìlara láààrin ara wọn (sí àwọn Ànábì - kí ọlà Allāhu máa bá wọn -). Ẹni tó bá ṣàì gbàgbọ́ nínú àwọn āyah Allāhu, dájúdájú Allāhu ni Olùyára níbi ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(3,20,'3:20','Tí wọ́n bá sì jà ọ́ níyàn, sọ pé: “Èmi àti ẹni tí ó tẹ̀lé mi juwọ́ jusẹ̀ sílẹ̀ (mùsùlùmí ni wá) fún Allāhu.” Kí o sì sọ fún àwọn tí A fún ní Tírà àti àwọn aláìmọ̀ọ́nkọmọ̀ọ́nkà (aláìnítírà) pé: “Ṣé ẹ máa gba ’Islām?” Tí wọ́n bá gba ’Islām, wọ́n ti mọ̀nà. Tí wọ́n bá sì kẹ̀yìn (sí’Islām), ìkéde (ẹ̀sìn) nìkan ni ojúṣe tìrẹ. Allāhu sì ni Olùríran nípa àwọn ẹrúsìn.');
INSERT INTO "aykyuni" VALUES(3,21,'3:21','Dájúdájú àwọn tó ń ṣàì gbàgbọ́ nínú àwọn āyah Allāhu, wọ́n tún ń pa àwọn Ànábì láì lẹ́tọ̀ọ́, wọ́n tún ń pa àwọn ènìyàn tí ń pàṣẹ ṣíṣe déédé àti ẹ̀tọ́, fún wọn ní ìró ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(3,22,'3:22','Àwọn wọ̀nyẹn ni àwọn tí iṣẹ́ wọn ti bàjẹ́ ní ilé ayé àti ní ọ̀run. Wọn kò sì níí rí àwọn olùrànlọ́wọ́.');
INSERT INTO "aykyuni" VALUES(3,23,'3:23','Ṣé ìwọ kò wòye sí àwọn tí A fún ní ìpín kan nínú Tírà, tí À ń pè síbi Tírà Allāhu, nítorí kí ó lè ṣe ìdájọ́ láààrin wọn, lẹ́yìn náà tí ìjọ kan nínú wọn ń pẹ̀yìn dà, tí wọ́n sì ń gbúnrí?');
INSERT INTO "aykyuni" VALUES(3,24,'3:24','Ìyẹn nítorí pé dájúdájú wọ́n wí pé: “Iná kò lè jó wa tayọ àwọn ọjọ́ tó lóǹkà.” Ohun tí wọ́n ń dá ní àdápa irọ́ sì tàn wọ́n jẹ nínú ẹ̀sìn wọn.');
INSERT INTO "aykyuni" VALUES(3,25,'3:25','Nítorí náà, báwo ni (ó ṣe máa rí fún wọn) nígbà tí A bá kó wọn jọ ní ọjọ́ kan, tí kò sí iyèméjì nínú rẹ̀? Àti pé A máa san ẹ̀mí kọ̀ọ̀kan ní ẹ̀san ohun tí ó ṣe níṣẹ́ ní ẹ̀kún-rẹ́rẹ́. Wọn kò sì níí ṣàbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(3,26,'3:26','Sọ pé: “Allāhu, Olùkápá ìjọba, Ò ń fi ìjọba fún ẹni tí O bá fẹ́. Ò ń gba ìjọba lọ́wọ́ ẹni tí O bá fẹ́. Ò ń buyì kún ẹni tí O bá fẹ́. O sì ń tàbùkù ẹni tí O bá fẹ́. Ọwọ́ Rẹ ni oore wà. Dájúdájú Ìwọ ni Alágbárá lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(3,27,'3:27','Ò ń mú òru wọnú ọ̀sán.Ò ń mú ọ̀sán wọnú òru.2 Ò ń mú alààyè jáde lára òkú. Ò ń mú òkú jáde lára alààyè. O sì ń pèsè ìjẹ-ìmu àti ìgbádùn fún ẹni tí Ó bá fẹ́ láì níí ní ìṣírò.”3 ');
INSERT INTO "aykyuni" VALUES(3,28,'3:28','Àwọn onígbàgbọ́ òdodo kò gbọ́dọ̀ sọ àwọn aláìgbàgbọ́ di ọ̀rẹ́ àyò (ọ̀rẹ́ finúhannú) lẹ́yìn àwọn onígbàgbọ́ òdodo (ẹgbẹ́ wọn). Ẹnikẹ́ni tí ó bá ń ṣe ìyẹn, kò sí kiní kan fún un mọ́ lọ́dọ̀ Allāhu.Àfi tí ẹ bá ń (fi ọ̀rẹ́ orí ahọ́n) wá ìṣọ́ra lọ́dọ̀ wọn) dáadáa (lórí ìgbàgbọ́ yín).2 Allāhu ń kìlọ̀ ara Rẹ̀ fún yín. Ọ̀dọ̀ Allāhu sì ni àbọ̀ ẹ̀dá. ');
INSERT INTO "aykyuni" VALUES(3,29,'3:29','Sọ pé: “Tí ẹ bá fi ohun tí ń bẹ nínú ọkàn yín pamọ́ tàbí ẹ fi hàn, Allāhu mọ̀ ọ́n. Ó sì mọ ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Allāhu sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(3,30,'3:30','Ní ọjọ́ tí ẹ̀mí kọ̀ọ̀kan yóò rí ohun tí ó ṣe nínú iṣẹ́ rere níwájú (rẹ̀) àti ohun tí ó ṣe nínú iṣẹ́ ibi, ó máa fẹ́ kí àkókò tó jìnnà wà láààrin òun àti iṣẹ́ ibi rẹ̀. Allāhu sì ń kìlọ̀ ara Rẹ̀ fún yín. Allāhu sì ni Aláàánú àwọn olùjọ́sìn.');
INSERT INTO "aykyuni" VALUES(3,31,'3:31','Sọ pé: “Tí ẹ̀yin bá nífẹ̀ẹ́ Allāhu, ẹ tẹ̀lé mi, Allāhu máa nífẹ̀ẹ́ yín, Ó sì máa forí ẹ̀ṣẹ̀ yín jìn yín. Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(3,32,'3:32','Sọ pé: “Ẹ tẹ̀lé (ti) Allāhu àti Òjíṣẹ́. Tí ẹ bá sì pẹ̀yìn dà, dájúdájú Allāhu kò nífẹ̀ẹ́ àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(3,33,'3:33','Dájúdájú Allāhu ṣa Ādam, Nūh, ará ilé ’Ibrọ̄hīm àti ará ilé ‘Imrọ̄n lẹ́ṣà lórí àwọn ẹ̀dá (àsìkò tiwọn).');
INSERT INTO "aykyuni" VALUES(3,34,'3:34','Wọ́n jẹ́ àrọ́mọdọ́mọ; apá kan wọn wá láti ara apá kan. Allāhu sì ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(3,35,'3:35','(Ẹ rántí) nígbà tí aya ‘Imrọ̄n sọ pé: “Olúwa mi, dájúdájú èmi fi ohun tí ń bẹ nínú ikùn mi jẹ́jẹ̀ẹ́ fún Ọ (pé) mo máa yà á sọ́tọ̀ (fún ẹ̀sìn Rẹ). Nítorí náà, gbà á lọ́wọ́ mi, dájúdájú Ìwọ ni Olùgbọ́, Onímọ̀.”');
INSERT INTO "aykyuni" VALUES(3,36,'3:36','Nígbà tí ó bí i, ó sọ pé: “Olúwa mi, dájúdájú mo bí i ní obìnrin - Allāhu sì nímọ̀ jùlọ nípa ohun tí ó bí – ọkùnrin kò sì dà bí obìnrin. Dájúdájú èmi sọ ọ́ ní Mọryam. Àti pé dájúdájú mò ń fi Ọ́ wá ààbò fún òun àti àrọ́mọdọ́mọ rẹ̀ lọ́dọ̀ Èṣù ẹni ẹ̀kọ̀.”');
INSERT INTO "aykyuni" VALUES(3,37,'3:37','Olúwa rẹ̀ sì gba àdúà rẹ̀ ní gbígbà dáadáa. Ó sì mú ọmọ náà dàgbà ní ìdàgbà dáadáa. Ó sì fi Zakariyyā ṣe alágbàtọ́ rẹ̀. Ìgbàkígbà tí Zakariyyā bá wọlé tọ̀ ọ́ nínú ilé ìjọ́sìn, ó máa bá èsè (èso) lọ́dọ̀ rẹ̀. (Zakariyyā á) sọ pé: “Mọryam, báwo ni èyí ṣe jẹ́ tìrẹ?” (Mọryam á) sọ pé: “Ó wá láti ọ̀dọ̀ Allāhu. Dájúdájú Allāhu ń pèsè ìjẹ-ìmu àti ìgbádùn fún ẹni tí Ó bá fẹ́ láì níí ní ìṣírò.”1 ');
INSERT INTO "aykyuni" VALUES(3,38,'3:38','Ibẹ̀yẹn ni Zakariyyā ti pe Olúwa rẹ̀. Ó sọ pé: “Olúwa mi, ta mí ní ọrẹ àrọ́mọdọ́mọ dáadáa láti ọ̀dọ̀ Rẹ. Dájúdájú Ìwọ ni Olùgbọ́-àdúà.”');
INSERT INTO "aykyuni" VALUES(3,39,'3:39','Nítorí náà, àwọn mọlāika pè é nígbà tí ó ń kírun lọ́wọ́ nínú ilé ìjọ́sìn, (wọ́n sọ pé): “Dájúdájú Allāhu ń fún ọ ní ìró ìdùnnú nípa (bíbí) Yahyā. Ó máa fi òdodo rinlẹ̀ nípa ọ̀rọ̀ kan láti ọ̀dọ̀ Allāhu.(Ó máa jẹ́) aṣíwájú, tí kò kúndùn obìnrin.2 (Ó máa jẹ́) Ànábì. Ó sì wà nínú àwọn ẹni rere.” ');
INSERT INTO "aykyuni" VALUES(3,40,'3:40','(Zakariyyā) sọ pé: “Olúwa mi, báwo ni èmi yóò ṣe ní ọmọkùnrin; mo mà ti dàgbàlágbà, àgàn sì ni obìnrin mi?” (Mọlāika) sọ pé: “Báyẹn ni Allāhu ṣe ń ṣe ohun tí Ó bá fẹ́.”');
INSERT INTO "aykyuni" VALUES(3,41,'3:41','(Zakariyyā) sọ pé: “Olúwa mi, fún mi ní àmì kan.” (Mọlāika) sọ pé: “Àmì rẹ ni pé ìwọ kò níí lè bá ènìyàn sọ̀rọ̀ fún ọjọ́ mẹ́ta àyàfi títọ́ka (sí n̄ǹkan). Rántí Olúwa rẹ lọ́pọ̀lọ́pọ̀. Kí o sì ṣàfọ̀mọ́ (fún Un) ní àṣálẹ́ àti ní òwúrọ̀ kùtù.”');
INSERT INTO "aykyuni" VALUES(3,42,'3:42','(Ẹ rántí) nígbà tí àwọn mọlāika sọ pé: “Mọryam, dájúdájú Allāhu ṣà ọ́ lẹ́ṣà. Ó fọ̀ ọ́ mọ́. Ó sì ṣà ọ́ lẹ́ṣà lórí àwọn obìnrin ayé (àsìkò tìrẹ).1 ');
INSERT INTO "aykyuni" VALUES(3,43,'3:43','Mọryam, tẹ̀lé àṣẹ Olúwa rẹ. Forí kanlẹ̀ fún Un. Kí o sì dáwọ́tẹ orúnkún pẹ̀lú àwọn olùdáwọ́tẹ-orúnkún (lórí ìrun).');
INSERT INTO "aykyuni" VALUES(3,44,'3:44','Ìyẹn wà nínú ìró ìkọ̀kọ̀ tí À ń fi ìmísí rẹ̀ ránṣẹ́ sí ọ. Ìwọ kò kúkú sí lọ́dọ̀ wọn nígbà tí wọ́n ń ju gègé wọn (sínú odò láti mọ) ta ni nínú wọn ni ó máa gba Mọryam wò.Ìwọ kò sì sí lọ́dọ̀ wọn nígbà tí wọ́n ń ṣe ìfàn̄fà (lórí rẹ̀). ');
INSERT INTO "aykyuni" VALUES(3,45,'3:45','(Ẹ rántí) nígbà tí àwọn mọlāikasọ pé: “Mọryam, dájúdájú Allāhu ń fún ọ ní ìró ìdùnnú (nípa dídá ẹ̀dá kan pẹ̀lú) ọ̀rọ̀ kan láti ọ̀dọ̀ Rẹ̀. Orúkọ rẹ̀ ni Mọsīh ‘Īsā ọmọ Mọryam. Ẹni iyì ni ní ayé àti ní ọ̀run.2 Ó sì wà lára alásùn-únmọ́ (Allāhu). ');
INSERT INTO "aykyuni" VALUES(3,46,'3:46','Ó máa bá àwọn ènìyàn sọ̀rọ̀ lórí ìtẹ́ ní òpóǹló àti nígbà tó bá dàgbà.Ó sì wà nínú àwọn ẹni rere.” ');
INSERT INTO "aykyuni" VALUES(3,47,'3:47','(Mọryam) sọ pé: “Olúwa mi báwo ni èmi yó ṣe ní ọmọkùnrin, (nígbà tí) abara kan kò fọwọ́ kàn mí.” Ó sọ pé: Báyẹn ni Allāhu ṣe ń dá ohun tí Ó bá fẹ́. Nígbà tí Ó bá (gbèrò) láti dá ẹ̀dá kan ohun tí Ó máa sọ fún un ni pé: “Jẹ́ bẹ́ẹ̀.” Ó sì máa jẹ́ bẹ́ẹ̀.');
INSERT INTO "aykyuni" VALUES(3,48,'3:48','(Allāhu) yóò fún un ní ìmọ̀ ìkọ̀wé, òye ìjìnlẹ̀, at-Taorāh àti al-’Injīl.');
INSERT INTO "aykyuni" VALUES(3,49,'3:49','(Ó sì jẹ́) Òjíṣẹ́ (tí A rán níṣẹ́) sí àwọn ọmọ ’Isrọ̄’īl. (Ó sì máa sọ fún wọn pé) “Dájúdájú èmi ti mú àmì kan wá fún yín láti ọ̀dọ̀ Olúwa yín. Dájúdájú èmi yóò mọ n̄ǹkan fún yín láti inú amọ̀ bí ìrísí ẹyẹ. Èmi yóò fẹ́ atẹ́gùn sínú rẹ̀. Ó sì máa di ẹyẹ pẹ̀lú ìyọ̀ǹda Allāhu. Èmi yóò ṣe ìwòsàn fún afọ́jú àti adẹ́tẹ̀, mo sì máa sọ òkú di alààyè pẹ̀lú ìyọ̀ǹda Allāhu. Èmi yóò máa fún yín ní ìró ohun tí ẹ̀ ń jẹ àti ohun tí ẹ̀ ń fi pamọ́ sínú ilé yín. Dájúdájú àmì kan wà nínú ìyẹn fún yín, tí ẹ bá jẹ́ onígbàgbọ́ òdodo.1 ');
INSERT INTO "aykyuni" VALUES(3,50,'3:50','Mo sì ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó ṣíwájú mi nínú at-Taorāh nítorí kí èmi lè ṣe apá kan èyí tí wọ́n ṣe ní èèwọ̀ fún yín ní ẹ̀tọ́ fún yín. Mo ti mú àmì kan wá fún yín láti ọ̀dọ̀ Olúwa yín. Nítorí náà, ẹ bẹ̀rù Allāhu, kí ẹ sì tẹ̀lé mi.');
INSERT INTO "aykyuni" VALUES(3,51,'3:51','Dájúdájú Allāhu ni Olúwa mi àti Olúwa yín. Nítorí náà, ẹ jọ́sìn fún Un. Èyí ni ọ̀nà tààrà.”');
INSERT INTO "aykyuni" VALUES(3,52,'3:52','Nígbà tí (Ànábì) ‘Īsā fura sí àìgbàgbọ́ lọ́dọ̀ wọn (pé wọ́n fẹ́ pa òun), ó sọ pé: “Ta ni olùrànlọ́wọ́ mi sí ọ̀dọ̀ Allāhu?” Àwọn ọmọlẹ́yìn (rẹ̀) sọ pé: “Àwa ni olùrànlọ́wọ́ fún (ẹ̀sìn) Allāhu. A gba Allāhu gbọ́. Kí o sì jẹ́rìí pé dájúdájú mùsùlùmí ni wá.');
INSERT INTO "aykyuni" VALUES(3,53,'3:53','Olúwa wa, a gbàgbọ́ nínú ohun tí O sọ̀kalẹ̀. A sì tẹ̀lé Òjíṣẹ́. Nítorí náà, kọ wá mọ́ àwọn olùjẹ́rìí.”');
INSERT INTO "aykyuni" VALUES(3,54,'3:54','Wọ́n déte, Allāhu sì déte. Allāhu sì l’óore jùlọ nínú àwọn adéte.1 ');
INSERT INTO "aykyuni" VALUES(3,55,'3:55','(Ẹ rántí) nígbà tí Allāhu sọ pé: “‘Īsā, dájúdájú Mo máa gbà ọ́ kúrò lọ́wọ́ wọn pátápátá.Mo máa gbé ọ wá sókè lọ́dọ̀ Mi. Mo sì máa fọ̀ ọ́ mọ́ lọ́dọ̀ àwọn tó ṣàì gbàgbọ́.2 Mo sì máa fi àwọn tó tẹ̀lé ọ borí àwọn tó ṣàì gbàgbọ́ títí di Ọjọ́ Àjíǹde. Lẹ́yìn náà, ọ̀dọ̀ Mi ni ibùpadàsí yín. Mo sì máa ṣe ìdájọ́ láààrin yín nípa ohun tí ẹ̀ ń yapa ẹnu sí. ');
INSERT INTO "aykyuni" VALUES(3,56,'3:56','Ní ti àwọn tó ṣàì gbàgbọ́, Èmi yóò jẹ wọ́n níyà líle ní ayé àti ní ọ̀run. Kò sì níí sí àwọn olùrànlọ́wọ́ fún wọn.');
INSERT INTO "aykyuni" VALUES(3,57,'3:57','Ní ti àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, (Allāhu) máa fún wọn ní ẹ̀san wọn ní kíkún. Allāhu kò sì fẹ́ràn àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(3,58,'3:58','Ìyẹn ni À ń ké fún ọ nínú àwọn āyah àti ìrántí tí ó kún fún òye ìjìnlẹ̀(ìyẹn, al-Ƙur’ān).');
INSERT INTO "aykyuni" VALUES(3,59,'3:59','Dájúdájú irú ‘Īsā lọ́dọ̀ Allāhu dà bí irú Ādam; (Allāhu) dá a láti ara erùpẹ̀. Lẹ́yìn náà, Ó sọ pé: “Jẹ́ bẹ́ẹ̀.” Ó sì jẹ́ bẹ́ẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(3,60,'3:60','Òdodo ni láti ọ̀dọ̀ Olúwa rẹ. Nítorí náà, má ṣe wà nínú àwọn oníyèméjì.');
INSERT INTO "aykyuni" VALUES(3,61,'3:61','Nítorí náà, ẹnikẹ́ni tí ó bá jà ọ́ níyàn nípa rẹ̀ lẹ́yìn ohun tí ó dé bá ọ nínú ìmọ̀, kí o sọ pé: “Ẹ wá! Kí á pe àwọn ọmọkùnrin wa àti àwọn ọmọkùnrin yín àti àwọn obìnrin wa àti àwọn obìnrin yín àti àwa àti ẹ̀yin náà. Lẹ́yìn náà, kí á ṣàdúà ìparun (lórí òpùrọ́ nínú wa). Nítorí náà, a óò sọ pé, “ibi dandan (ìgbéjìnnà sí ìkẹ́) Allāhu kí ó máa jẹ́ ti àwọn òpùrọ́.”1 ');
INSERT INTO "aykyuni" VALUES(3,62,'3:62','Dájúdájú èyí, òhun ni ìtàn òdodo. Àti pé kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Allāhu. Dájúdájú Allāhu, Òun ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(3,63,'3:63','Tí wọ́n bá sì pẹ̀yìndà (kúrò níbi òdodo), dájúdájú Allāhu ni Onímọ̀ nípa àwọn òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(3,64,'3:64','Sọ pé: “Ẹ̀yin onítírà, ẹ wá síbi ọ̀rọ̀ kan tó dọ́gba láààrin àwa àti ẹ̀yin, pé a ò níí jọ́sìn fún ẹnì kan àfi Allāhu. A ò sì níí fi kiní kan wá akẹgbẹ́ fún Un. Àti pé apá kan wa kò níí sọ apá kan di olúwa lẹ́yìn Allāhu.” Tí wọ́n bá sì gbúnrí, ẹ sọ pé: “Ẹ jẹ́rìí pé dájúdájú mùsùlùmí ni àwa.” 1 ');
INSERT INTO "aykyuni" VALUES(3,65,'3:65','Ẹ̀yin onítírà, nítorí kí ni ẹ óò fi jiyàn nípa (Ànábì) ’Ibrọ̄hīm? A kò sì sọ at-Taorāh àti al-’Injīl kalẹ̀ bí kò ṣe lẹ́yìn rẹ̀, ṣé ẹ kò ṣe làákàyè ni?');
INSERT INTO "aykyuni" VALUES(3,66,'3:66','Ẹ̀yin ni ìwọ̀nyí tí ẹ̀ ń jiyàn nípa ohun tí ẹ nímọ̀ nípa rẹ̀! Kí ni ó tún ń mu yín jiyàn nípa ohun tí ẹ ò nímọ̀ nípa rẹ̀?Allāhu nímọ̀. Ẹ̀yin kò sì nímọ̀. ');
INSERT INTO "aykyuni" VALUES(3,67,'3:67','(Ànábì) ’Ibrọ̄hīm kì í ṣe yẹhudi, kì í ṣe nasọ̄rọ̄, ṣùgbọ́n ó jẹ́ olùdúró-déédé, mùsùlùmí. Kò sì sí nínú àwọn ọ̀ṣẹbọ.');
INSERT INTO "aykyuni" VALUES(3,68,'3:68','Dájúdájú àwọn ènìyàn tó súnmọ́ (Ànábì) ’Ibrọ̄hīm jùlọ (nínú ẹ̀sìn ’Islām) ni àwọn tó tẹ̀lé e àti Ànábì yìí - kí ìkẹ́ àti ọlà máa bá a - àti àwọn tó gbàgbọ́ ní òdodo (ìyẹn, àwọn mùsùlùmí). Allāhu ni Alátìlẹ́yìn fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(3,69,'3:69','Igun kan nínú àwọn onítírà fẹ́ láti ṣì yín lọ́nà. Wọn kò lè ṣi ẹnikẹ́ni lọ́nà àfi ara wọn, wọn kò sì fura.');
INSERT INTO "aykyuni" VALUES(3,70,'3:70','Ẹ̀yin onítírà, nítorí kí ni ẹ óò fi ṣàì gbàgbọ́ nínú àwọn āyah Allāhu, ẹ sì ń jẹ́rìí (sí òdodo rẹ̀)!');
INSERT INTO "aykyuni" VALUES(3,71,'3:71','Ẹ̀yin onítírà, nítorí kí ni ẹ óò fi da irọ́ pọ̀ mọ́ òdodo, ẹ sì ń fi òdodo pamọ́ nígbà tí ẹ̀yin mọ (òdodo)?');
INSERT INTO "aykyuni" VALUES(3,72,'3:72','Igun kan nínú àwọn onítírà wí pé: “Ẹ lọ gbàgbọ́ nínú ohun tí Wọ́n sọ̀kalẹ̀ fún àwọn tó gbàgbọ́ ní òdodo níbẹ̀rẹ̀ ọjọ́, kí ẹ sì takò ó níparí rẹ̀, bóyá àwọn mùsùlùmí (kan) máa ṣẹ́rí padà (sẹ́yìn nínú ẹ̀sìn ’Islām).1 ');
INSERT INTO "aykyuni" VALUES(3,73,'3:73','(Wọ́n tún wí pé:) “Ẹ má gbàgbọ́ àyàfi ẹni tí ó bá tẹ̀lé ẹ̀sìn yín.” Sọ pé: “Dájúdájú ìmọ̀nà (’Islām) ni ìmọ̀nà ti Allāhu.” (Wọ́n tún wí pé:) “(Ẹ má gbàgbọ́) pé wọ́n fún ẹnì kan ní irú ohun tí wọ́n fún yín tàbí pé wọn yóò takò yín (tí wọn yó sì jàre yín) lọ́dọ̀ Olúwa yín.” Sọ pé: “Dájúdájú oore àjùlọ wà ní ọwọ́ Allāhu. Ó ń fún ẹni tí Ó bá fẹ́. Allāhu ni Olùgbààyè, Onímọ̀.”');
INSERT INTO "aykyuni" VALUES(3,74,'3:74','(Allāhu) ń fi ìkẹ́ Rẹ̀ṣa ẹni tí Ó bá fẹ́ lẹ́ṣà. Allāhu ni Olóore ńlá. ');
INSERT INTO "aykyuni" VALUES(3,75,'3:75','Ó ń bẹ nínú àwọn onítírà, ẹni tí ó jẹ́ pé tí o bá fi ọkàn tán an pẹ̀lú ọ̀pọ̀lọ́pọ̀ owó, ó máa dá a padà fún ọ. Ó sì ń bẹ nínú wọn, ẹni tí ó jẹ́ pé tí o bá fi ọkàn tán an pẹ̀lú owó dinar kan (owó kékeré), kò níí dá a padà fún ọ àyàfi tí o bá dógò tì í lọ́rùn. Ìyẹn nítorí pé wọ́n wí pé: “Wọn kò lè fí ọ̀nà kan kan bá wa wí nítorí àwọn aláìmọ̀ọ́nkọmọ̀ọ́nkà.” Ńṣe ni wọ́n ń pa irọ́ mọ́ Allāhu, wọ́n sì mọ̀.');
INSERT INTO "aykyuni" VALUES(3,76,'3:76','Rárá (A máa bá wọn wí). Ẹnikẹ́ni tí ó bá mú àdéhùn rẹ̀ ṣẹ, tí ó sì bẹ̀rù (Allāhu), dájúdájú Allāhu nífẹ̀ẹ́ àwọn olùbẹ̀rù (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(3,77,'3:77','Dájúdájú àwọn tó ń ta májẹ̀mu Allāhu àti ìbúra wọn ní owó kékeré, àwọn wọ̀nyẹn, kò níí sí ìpín oore kan fún wọn ní Ọjọ́ Ìkẹ́yìn. Allāhu kò níí bá wọn sọ̀rọ̀, kò sì níí ṣíjú wò wọ́n ní Ọjọ́ Àjíǹde.Kò sì níí fọ̀ wọ́n mọ́ (nínú ẹ̀ṣẹ̀) Ìyà ẹlẹ́ta-eléro sì ń bẹ fún wọn. ');
INSERT INTO "aykyuni" VALUES(3,78,'3:78','Dájúdájú ìjọ kan ń bẹ nínú wọn tó ń fi ahọ́n wọn yí tírà (Allāhu) padà, nítorí kí ẹ lè lérò pé lára tírà ló wà, kò sì sí lára tírà. Wọ́n sì ń wí pé: “Ó wá láti ọ̀dọ̀ Allāhu.” Kò sì wá láti ọ̀dọ̀ Allāhu. Wọ́n ń parọ́ mọ́ Allāhu, wọ́n sì mọ̀.');
INSERT INTO "aykyuni" VALUES(3,79,'3:79','Kò lẹ́tọ̀ọ́ fún abara kan nígbà tí Allāhu bá fún un ní tírà, òye ìjìnlẹ̀ àti ipò Ànábì, lẹ́yìn náà kí ó máa sọ fún àwọn ènìyàn pé, “ẹ jẹ́ ẹrúsìn fún mi lẹ́yìn Allāhu.” Ṣùgbọ́n (ó máa sọ pé) “ẹ jẹ́ olùjọ́sìn fún Olúwa (kí ẹ sì máa fi ẹ̀kọ́ Rẹ̀ kọ́ àwọn ènìyàn) nítorí pé ẹ jẹ́ ẹni tí ń kọ́ àwọn ènìyàn ní tírà àti nítorí pé ẹ jẹ́ ẹni tí ń kọ́ ẹ̀kọ́ (nípa ẹ̀sìn).”');
INSERT INTO "aykyuni" VALUES(3,80,'3:80','(Ànábì kan) kò sì níí pa yín ní àṣẹ pé kí ẹ sọ àwọn mọlāika àti àwọn Ànábì di olúwa. Ṣé ó máa pa yín ní àṣẹ ṣíṣe àìgbàgbọ́ lẹ́yìn tí ẹ ti jẹ́ mùsùlùmí ni?');
INSERT INTO "aykyuni" VALUES(3,81,'3:81','(Ẹ rántí) nígbà tí Allāhu gba àdéhùn lọ́wọ́ àwọn Ànábìpé: “Bí ó tilẹ̀ wù kí ó rí nígbà tí Mo bá fún yín ní tírà àti òye ìjìnlẹ́, lẹ́yìn náà Òjíṣẹ́ kan (ìyẹn, Ànábì Muhammad - kí ìkẹ́ àti ọlà Allāhu máa bá a - yóò dé ba yín; ó máa fi èyí tó jẹ́ òdodo rinlẹ̀ nípa èyí tó wà pẹ̀lú yín, ẹ gbọ́dọ̀ gbà á gbọ́, ẹ sì gbọ́dọ̀ ràn án lọ́wọ́.” (Allāhu) sọ pé: “Ǹjẹ́ ẹ gbà? Ṣé ẹ sì máa lo àdéhùn Mi yìí?” Wọ́n sọ pé: “A gbà.” (Allāhu) sọ pé: “Nítorí náà, ẹ jẹ́rìí sí (àdéhùn náà). Èmi ń bẹ pẹ̀lú yín nínú àwọn Olùjẹ́rìí.” ');
INSERT INTO "aykyuni" VALUES(3,82,'3:82','Nítorí náà, ẹni tí ó bá kẹ̀yìn sí (Ànábì Muhammad - kí ìkẹ́ àti ọlà Allāhu máa bá a -) lẹ́yìn (àdéhùn) yẹn, àwọn wọ̀nyẹn ni òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(3,83,'3:83','Ṣé ẹ̀sìn mìíràn yàtọ̀ sí ẹ̀sìn Allāhu ni wọ́n ń wá ni? Nígbà tí ó jẹ́ pé tiRẹ̀ ni gbogbo ẹni tí ń bẹ nínú àwọn sánmọ̀ àti ilẹ̀ juwọ́ jusẹ̀ sílẹ̀ fún, wọ́n fẹ́ wọ́n kọ̀. Ọ̀dọ̀ Rẹ̀ sì ni Wọ́n máa dá wọn padà sí.');
INSERT INTO "aykyuni" VALUES(3,84,'3:84','Sọ pé: “A gbàgbọ́ nínú Allāhu àti ohun tí wọ́n sọ̀kalẹ̀ fún wa àti ohun tí wọ́n sọ̀kalẹ̀ fún (Ànábì) ’Ibrọ̄hīm, ’Ismọ̄‘īl, ’Ishāƙ, Ya‘ƙūb, àti àwọn àrọ́mọdọ́mọ (rẹ̀. A gbàgbọ́ nínú) ohun tí wọ́n fún (Ànábì) Mūsā àti ‘Īsā àti àwọn Ànábì láti ọ̀dọ̀ Olúwa wọn; A kò ya ẹnì kan kan sọ́tọ̀ nínú wọn. Àwa sì ni mùsùlùmí (olùjuwọ́-jusẹ̀-sílẹ̀) fún Un.”');
INSERT INTO "aykyuni" VALUES(3,85,'3:85','Ẹnikẹ́ni tí ó bá wá ẹ̀sìn kan ṣe yàtọ̀ sí ’Islām, A ò níí gbà á lọ́wọ́ rẹ̀. Ní Ọjọ́ Ìkẹ́yìn, ó sì máa wà nínú àwọn ẹni òfò.');
INSERT INTO "aykyuni" VALUES(3,86,'3:86','Báwo ni Allāhu yó ṣe fi ọ̀nà mọ ìjọ kan tó ṣàì gbàgbọ́ lẹ́yìn ìgbàgbọ́ wọn? Wọ́n sì jẹ́rìí pé dájúdájú Òjíṣẹ́ náà, òdodo ni. Àwọn ẹ̀rí tó yanjú sì ti dé bá wọn. Allāhu kì í fi ọ̀nà mọ ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(3,87,'3:87','Àwọn wọ̀nyẹn, ẹ̀san wọn ni pé, dájúdájú ègún Allāhu àti (ègún) àwọn mọlāika àti (ègún) gbogbo àwọn ènìyàn ń bẹ lórí wọn.');
INSERT INTO "aykyuni" VALUES(3,88,'3:88','Olùṣegbére ni wọ́n nínú ègún. Nítorí náà, A ò níí ṣe ìyà náà ní fífúyẹ́ fún wọn, A ò sì níí fún wọn ní ìsinmi (nínú Iná).');
INSERT INTO "aykyuni" VALUES(3,89,'3:89','Àyàfi àwọn tó ronú pìwàdà lẹ́yìn ìyẹn, tí wọ́n sì ṣe àtúnṣe, nítorí pé dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(3,90,'3:90','Dájúdájú àwọn tó ṣàì gbàgbọ́ lẹ́yìn ìgbàgbọ́ wọn, lẹ́yìn náà, wọ́n lékún ní àìgbàgbọ́, A ò níí gba ìronúpìwàdà wọn. Àwọn wọ̀nyẹn, àwọn ni olùṣìnà.');
INSERT INTO "aykyuni" VALUES(3,91,'3:91','Dájúdájú àwọn tó ṣàì gbàgbọ́, tí wọ́n sì kú nígbà tí wọ́n jẹ́ aláìgbàgbọ́, A ò níí gba ẹ̀kún ilẹ̀ wúrà lọ́wọ́ ẹnikẹ́ni nínú wọn, ìbáà fi ṣèràpadà (fún ẹ̀mí ara rẹ̀ níbi Iná). Àwọn wọ̀nyẹn ni ìyà ẹlẹ́ta-eléro ń bẹ fún. Kò sì níí sí àwọn olùrànlọ́wọ́ fún wọn.');
INSERT INTO "aykyuni" VALUES(3,92,'3:92','Ọwọ́ yin kò lè ba oore àyàfi tí ẹ bá ń ná nínú ohun tí ẹ nífẹ̀ẹ́ sí. Àti pé ohunkóhun tí ẹ bá ná, dájúdájú Allāhu ni Onímọ̀ nípa rẹ̀.');
INSERT INTO "aykyuni" VALUES(3,93,'3:93','Gbogbo oúnjẹ ló jẹ́ ẹ̀tọ́ fún àwọn ọmọ ’Isrọ̄’īl àyàfi èyí tí ’Isrọ̄’īl bá ṣe ní èèwọ̀ fún ẹ̀mí ara rẹ̀ ṣíwájú kí A tó sọ at-Taorāh kalẹ̀. Sọ pé: “Nítorí náà, ẹ mú at-Taorāh wá, kí ẹ sì kà á síta tí ẹ̀yin bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(3,94,'3:94','Nítorí náà, ẹnikẹ́ni tí ó bá dá àdápa irọ́ mọ́ Allāhu lẹ́yìn ìyẹn, àwọn wọ̀nyẹn, àwọn ni alábòsí.');
INSERT INTO "aykyuni" VALUES(3,95,'3:95','Sọ pé: “Allāhu sọ òdodo. Nítorí náà, ẹ tẹ̀lé ẹ̀sìn (Ànábì) ’Ibrọ̄hīm, olùdúró-déédé-nínú-’Islām. Kò sì sí nínú àwọn ọ̀ṣẹbọ.”');
INSERT INTO "aykyuni" VALUES(3,96,'3:96','Dájúdájú ilé àkọ́kọ́ tí A fi lélẹ̀ fún àwọn ènìyàn ni èyí tí ó wà ní Bakkah.(Ó jẹ́ ilé) ìbùkún àti ìmọ̀nà fún gbogbo ẹ̀dá. ');
INSERT INTO "aykyuni" VALUES(3,97,'3:97','Àwọn àmì tó yanjú wà nínú rẹ̀; ibùdúró (Ànábì) ’Ibrọ̄hīm. Ẹnikẹ́ni tí ó bá wọ inú rẹ̀ ti di ẹni ìfàyàbalẹ̀. Allāhu ṣe àbẹ̀wò sí Ilé náà ní dandan fún àwọn ènìyàn, tó lágbára ọ̀nà tí ó máa gbà débẹ̀. Ẹnikẹ́ni tí ó bá sì ṣàì gbàgbọ́, dájúdájú Allāhu ni Ọlọ́rọ̀ tí kò bùkátà sí gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(3,98,'3:98','Sọ pé: “Ẹ̀yin onítírà, nítorí kí ni ẹ óò fi ṣàì gbàgbọ́ nínú àwọn āyah Allāhu? Allāhu sì ni Arínú-róde ohun tí ẹ̀ ń ṣe níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(3,99,'3:99','Sọ pé: “Ẹ̀yin onítírà, nítorí kí ni ẹ fi ń ṣẹ́rí ẹni tó gbàgbọ́ lódodo kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu, tí ẹ̀ ń fẹ́ kí ó wọ́,ẹ sì jẹ́rìí (sí òdodo ’Islām)? Allāhu kì í ṣe Onígbàgbé nípa ohun tí ẹ̀ ń ṣe níṣẹ́.” ');
INSERT INTO "aykyuni" VALUES(3,100,'3:100','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, tí ẹ bá tẹ̀lé apá kan nínú àwọn tí A fún ní tírà, wọ́n máa da yín padà lẹ́yìn ìgbàgbọ́ òdodo yín sí ipò aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(3,101,'3:101','Báwo ni ẹ̀yin ṣe lè ṣàì gbàgbọ́, ẹ̀yin mà ni wọ́n ń ké àwọn āyah Allāhu fún, Òjíṣẹ́ Rẹ̀ sì wà láààrin yín! Ẹnikẹ́ni tí ó bá dúró ṣinṣin ti Allāhu, A ti tọ́ ọ sí ọ̀nà tààrà.1 ');
INSERT INTO "aykyuni" VALUES(3,102,'3:102','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ bẹ̀rù Allāhu bí ó ṣe tọ́ láti bẹ̀rù Rẹ̀. Ẹ̀yin kò sì gbọdọ̀ kú àyàfi kí ẹ wà ní ipò mùsùlùmí.');
INSERT INTO "aykyuni" VALUES(3,103,'3:103','Ẹ dìmọ́ okùn Allāhu (al-Ƙur’ān) ní àpapọ̀, ẹ má ṣe pín sí ìjọ ọ̀tọ̀ọ̀tọ̀. Kí ẹ sì rántí ìkẹ́ Allāhu tí ń bẹ lórí yín (pé) nígbà tí ẹ jẹ́ ọ̀tá (ara yín nígbà àìmọ̀kan), Ó pa ọkàn yín pọ̀ mọ́ra wọn (pẹ̀lú ’Islām), ẹ sì di ọmọ ìyá pẹ̀lú ìkẹ́ Rẹ̀; àti (nígbà tí) ẹ wà létí ọ̀gbun Iná, Ó gbà yín là kúrò nínú rẹ̀. Báyẹn ni Allāhu ṣe ń ṣe àlàyé àwọn āyah Rẹ̀ fún yín nítorí kí ẹ lè mọ̀nà (’Islām).');
INSERT INTO "aykyuni" VALUES(3,104,'3:104','Kí ó máa bẹ nínú yín, ìjọ kan tí yóò máa pèpè síbi ohun tó lóore, wọn yóò máa pàṣẹ ohun rere, wọn yó sì máa kọ ohun burúkú. Àwọn wọ̀nyẹn, àwọn sì ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(3,105,'3:105','Ẹ má ṣe dà bí àwọn tó sọra wọn di ìjọ ọ̀tọ̀ọ̀tọ̀, tí wọ́n sì yapa ẹnu (sí ’Islām) lẹ́yìn tí àwọn àlàyé tó yanjú ti dé bá wọn. Àwọn wọ̀nyẹn sì ni ìyà ńlá ń bẹ fún.');
INSERT INTO "aykyuni" VALUES(3,106,'3:106','Ní ọjọ́ tí àwọn ojú kan yóò funfun (ìmọ́lẹ̀). Àwọn ojú kan yó sì dúdú. Ní ti àwọn tí ojú wọn dúdú, (A ó bi wọ́n pé:) "Ṣé ẹ ṣàì gbàgbọ́ lẹ́yìn ìgbàgbọ́ yín ni?" Nítorí náà, ẹ tọ́ ìyà wò nítorí pé ẹ ṣàì gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(3,107,'3:107','Ní ti àwọn tí ojú wọn funfun (ìmọ́lẹ̀), nínú ìkẹ́ Allāhu ni wọn yóò wà. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(3,108,'3:108','Ìwọ̀nyí ni àwọn āyah Allāhu, tí À ń ké e fún ọ pẹ̀lú òdodo. Allāhu kò sì gbèrò àbòsí kan sí gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(3,109,'3:109','Ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Ọ̀dọ̀ Allāhu sì ni wọ́n máa ṣẹ́rí àwọn ọ̀rọ̀ ẹ̀dá padà sí.');
INSERT INTO "aykyuni" VALUES(3,110,'3:110','Ẹ jẹ́ ìjọ tó lóore jùlọ, tí A gbé dìde fún àwọn ènìyàn; ẹ̀ ń pàṣẹ ohun rere, ẹ̀ ń kọ ohun burúkú, ẹ sì gbàgbọ́ nínú Allāhu. Tí ó bá jẹ́ pé àwọn ahlul-kitāb gbàgbọ́ lódodo ni, ìbá lóore jùlọ fún wọn. Onígbàgbọ́ òdodo wà nínú wọn,ọ̀pọ̀lọ́pọ̀ wọn sì ni òbìlẹ̀jẹ́. ');
INSERT INTO "aykyuni" VALUES(3,111,'3:111','Wọn kò níí kó ìnira ba yín àyàfi ìpalára díẹ̀. Tí wọ́n bá sì ba yín jà, wọ́n á sá fún yín. Lẹ́yìn náà, A ò níí ràn wọ́n lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(3,112,'3:112','Àbùkù yóò máa bá wọn níbikíbi tí ọwọ́ àwọn (mùsùlùmí) bá ti bà wọ́n àfi (tí wọ́n bá ń bẹ) pẹ̀lú àdéhùn àti ààbò láti ọ̀dọ̀ Allāhu (ìyẹn ni pé, kí wọ́n gba ’Islām) àti àdéhùn àti ààbò láti ọ̀dọ̀ àwọn ènìyàn (ìyẹn ni pé, kí wọ́n gbà láti máa san owó ìsákọ́lẹ̀ fún ìjọba ’Islām). Wọ́n ṣẹ́rí padà pẹ̀lú ìbínú láti ọ̀dọ̀ Allāhu. Wọ́n sì kó òṣì bá wọn. Ìyẹn nítorí pé, dájúdájú wọ́n ṣàì gbàgbọ́ nínú àwọn āyah Allāhu, wọ́n tún ń pa àwọn Ànábì láì lẹ́tọ̀ọ́. Ìyẹn nítorí pé, wọ́n yapa (àṣẹ Allāhu), wọ́n sì ń tayọ ẹnu ààlà.');
INSERT INTO "aykyuni" VALUES(3,113,'3:113','(Àwọn onítírà) kò rí bákan náà. Ìjọ kan tó dúró déédé wà nínú àwọn onítírà, tí ń ké àwọn āyah Allāhu ní àkókò òru, tí wọ́n sì ń forí kanlẹ̀ (lórí ìrun).');
INSERT INTO "aykyuni" VALUES(3,114,'3:114','Wọ́n gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn. Wọ́n ń pàṣẹ ohun rere, wọ́n ń kọ ohun burúkú, wọ́n sì ń yara níbi àwọn iṣẹ́ olóoore. Àwọn wọ̀nyẹn wà nínú àwọn ẹni rere.1 ');
INSERT INTO "aykyuni" VALUES(3,115,'3:115','Ohunkóhun tí wọ́n bá ń ṣe ní iṣẹ́ rere, A ò níí jẹ́ kí ẹ̀san rẹ̀ gbé. Allāhu sì ni Onímọ̀ nípa àwọn olùbẹ̀rù (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(3,116,'3:116','Dájúdájú àwọn tó ṣàì gbàgbọ́, àwọn dúkìá wọn àti àwọn ọmọ wọn kò níí rọ̀ wọ́n lọ́rọ̀ kiní kan (níbi ìyà) lọ́dọ̀ Allāhu. Àwọn wọ̀nyẹn sì ni èrò inú Iná. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(3,117,'3:117','Àpèjúwe ohun tí wọ́n ń ná nínú ìṣẹ̀mí ayé yìí dà bí àpèjúwe afẹ́fẹ́ tí atẹ́gùn òtútù líle wà nínú rẹ̀. Ó fẹ́ lu oko àwọn ènìyàn tó ṣe àbòsí s’órí ara wọn. Ó sì pa á run. Allāhu kò sì ṣe àbòsí sí wọn, ṣùgbọ́n ẹ̀mí ara wọn ni wọ́n ń ṣàbòsí sí.');
INSERT INTO "aykyuni" VALUES(3,118,'3:118','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe mú ọ̀rẹ́ finúhannú kan yàtọ̀ sí ara yín. Wọn kò níí géwọ́ aburú kúrú fún yín. Wọ́n sì nífẹ̀ẹ́ sí ohun tí ó máa kó ìnira ba yín. Ìkórira kúkú ti fojú hàn láti ẹnu wọn. Ohun tí ó sì pamọ́ sínú ọkàn wọn tóbi jùlọ. A ti ṣàlàyé àwọn āyah fún yín, tí ẹ̀yin bá jẹ́ onílàákàyè.1 ');
INSERT INTO "aykyuni" VALUES(3,119,'3:119','Kíyè sí i, ẹ̀yin wọ̀nyí nífẹ̀ẹ́ wọn, àwọn kò sì nífẹ̀ẹ́ yín. Ẹ̀yin gbàgbọ́ nínú gbogbo àwọn tírà pátápátá. Nígbà tí wọ́n bá sì pàdé yín, wọ́n á wí pé: “Àwa gbàgbọ́.” Nígbà tí ó bá sì ku àwọn nìkan, wọn yóò máa deyín mọ́ ìka lórí yín ní ti ìbínú. Sọ pé: “Ẹ kú pẹ̀lú ìbínú yín.” Dájúdájú Allāhu ni Onímọ̀ nípa ohun tí ń bẹ nínú àwọn igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(3,120,'3:120','Tí dáadáa kan bá kàn yín, ó máa kó ìbànújẹ́ bà wọn. Tí aburú kan bá sì ṣẹlẹ̀ si yín, wọ́n máa dunnú sí i. Tí ẹ̀yin bá ṣe sùúrù, tí ẹ sì ṣọ́ra (fún wọn), ète wọn kò níí kó ìnira kan kan ba yín. Dájúdájú Allāhu ni Alámọ̀tán nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(3,121,'3:121','(Rántí) nígbà tí o jí kúrò lọ́dọ̀ àwọn ara ilé rẹ ní ìdájí kùtùkùtù, tí ò ń fi àwọn onígbàgbọ́ òdodo sí àyè ibùjagun, Allāhu ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(3,122,'3:122','(Rántí) nígbà tí ìjọ méjì nínú yín rò láti ṣojo. Allāhu sì ni Aláfẹ̀yìntì àwọn méjèèjì. Allāhu sì ni kí àwọn onígbàgbọ́ òdodo gbáralé.');
INSERT INTO "aykyuni" VALUES(3,123,'3:123','Allāhu kúkú fún yín ní ìṣẹ́gun ní ogun Badr, nígbà tí ẹ̀yin jẹ́ aláìlágbára. Nítorí náà, ẹ bẹ̀rù Allāhu kí ẹ lè dúpẹ́ (fún Un).');
INSERT INTO "aykyuni" VALUES(3,124,'3:124','Rántí nígbà tí ò ń sọ fún àwọn onígbàgbọ́ òdodo pé: “Ṣé kò níí to yín tí Olúwa yín bá ṣe ìrànlọ́wọ́ fún yín pẹ̀lú ẹgbẹ̀rún mẹ́ta nínú àwọn mọlāika, tí Wọ́n máa sọ̀kalẹ̀?”');
INSERT INTO "aykyuni" VALUES(3,125,'3:125','Bẹ́ẹ̀ ni (ó máa tó wa.). Tí ẹ̀yin bá ṣe sùúrú, tí ẹ sì bẹ̀rù Allāhu, tí àwọn (ọ̀tá) bá dé ba yín lójijì, Olúwa yín yóò ràn yín lọ́wọ́ pẹ̀lú ẹgbẹ̀rún máàrún nínú àwọn mọlāika pẹ̀lú àmì ìdánimọ̀ lára wọn.');
INSERT INTO "aykyuni" VALUES(3,126,'3:126','Allāhu kò ṣe é lásán bí kò ṣe kí ó lè jẹ́ ìró ìdùnnú fún yín àtí nítorí kí ọkàn yín lè balẹ̀ pẹ̀lú rẹ̀. Kò sí àrànṣe lórí ọ̀tá (láti ibì kan kan) bí kò ṣe láti ọ̀dọ̀ Allāhu, Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(3,127,'3:127','(Allāhu ṣe àrànṣe náà fún yín) nítorí kí Ó lè gé apá kan dànù nínú àwọn tó ṣàì gbàgbọ́, tàbí nítorí kí Ó lè dójú tì wọ́n, tí wọ́n sì máa padà wálé lófo.');
INSERT INTO "aykyuni" VALUES(3,128,'3:128','Kò sí ohun tó kàn ọ́ nínú ọ̀rọ̀ náà. Yálà (Allāhu) máa gba ìronúpìwàdà wọn tàbí Ó máa jẹ wọ́n níyà; dájúdájú alábòsí ni wọ́n.1 ');
INSERT INTO "aykyuni" VALUES(3,129,'3:129','Ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Ó ń foríjin ẹni tí Ó bá fẹ́, Ó sì ń jẹ ẹni tí Ó bá fẹ́ níyà. Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(3,130,'3:130','Ẹ̀yin onígbàgbọ́ òdodo, ẹ má ṣe jẹ èlé, àdìpèlé lórí àdìpèlé. Kí ẹ sì bẹ̀rù Allāhu nítorí kí ẹ lè jèrè.');
INSERT INTO "aykyuni" VALUES(3,131,'3:131','Kí ẹ sì ṣọ́ra fún Iná tí wọ́n ti pa lésè sílẹ̀ de àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(3,132,'3:132','Kí ẹ tẹ̀lé ti Allāhu àti Òjíṣẹ́ nítorí kí A lè kẹ yín.');
INSERT INTO "aykyuni" VALUES(3,133,'3:133','Ẹ yára wá àforíjìn lọ́dọ̀ Olúwa yín àti Ọgbà Ìdẹ̀ra kan, èyí tí ìbú rẹ̀ tó àwọn sánmọ̀ àti ilẹ̀, tí wọ́n pa lésè sílẹ̀ fún àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(3,134,'3:134','Àwọn tó ń ná dúkìá wọn nígbà ìdẹ̀ra àti nígbà ìnira, àwọn tí ń gbé ìbínú mì, àwọn alámòjúúkúrò fún àwọn ènìyàn níbi àṣìṣe; Allāhu nífẹ̀ẹ́ àwọn olùṣe-rere.');
INSERT INTO "aykyuni" VALUES(3,135,'3:135','Àwọn tí (ó jẹ́ pé) nígbà tí wọ́n bá ṣe ìbàjẹ́ kan tàbí tí wọ́n bá ṣàbòsí sí ẹ̀mí ara wọn, wọ́n á rántí Allāhu, wọ́n á sì tọrọ àforíjìn fún ẹ̀ṣẹ̀ wọn, - Ta sì ni Ó ń forí àwọn ẹ̀ṣẹ̀ jin (ẹ̀dá) bí kò ṣe Allāhu. Wọn kò sì takú sórí ohun tí wọ́n ṣe nígbà tí wọ́n mọ̀ (pé ẹ̀ṣẹ̀ ni). -');
INSERT INTO "aykyuni" VALUES(3,136,'3:136','Àwọn wọ̀nyẹn, ẹ̀san wọn lọ́dọ̀ Olúwa wọn ni àforíjìn àti àwọn Ọgbà Ìdẹ̀ra kan tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀. Ẹ̀san olùṣe-iṣẹ́ rere sì dára.');
INSERT INTO "aykyuni" VALUES(3,137,'3:137','Àwọn orípa kan kúkú ti lọ ṣíwájú yín. Nítorí náà, ẹ rin ilẹ̀ lọ, kí ẹ wòye sí bí ìgbẹ̀yìn àwọn tó pe òdodo nírọ́ ṣe rí.');
INSERT INTO "aykyuni" VALUES(3,138,'3:138','Èyí ni àlàyé fún àwọn ènìyàn. Ìmọ̀nà àti wáàsí sì ni fún àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(3,139,'3:139','Ẹ má ṣe kọ́lẹ, ẹ má ṣe banújẹ́; ẹ̀yin l’ẹ máa lékè tí ẹ bá jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(3,140,'3:140','Tí ìpalára kan bá kàn yín, irú ìpalára bẹ́ẹ̀ kúkú ti kan ìjọ kèfèrí. Àwọn ọjọ́ wọ̀nyí, À ń yí i po láààrin àwọn ènìyàn ni. Àti pé nítorí kí Allāhu lè ṣe àfihàn àwọn tó gbàgbọ́ ní òdodo àti nítorí kí Ó lè mú àwọn tó máa kú fún Un nínú yín. Allāhu kò sì nífẹ̀ẹ́ àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(3,141,'3:141','Àti pé (ó tún rí bẹ́ẹ̀) nítorí kí Allāhu lè ṣàfọ̀mọ́ àwọn tó gbàgbọ́ ní òdodo àti nítorí kí Ó lè run àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(3,142,'3:142','Tàbí ẹ lérò pé ẹ máa wọ inú Ọgbà Ìdẹ̀ra nígbà tí Allāhu kò tí ì ṣàfi hàn àwọn tó máa jagun (ẹ̀sìn) nínú yín, tí kò sì tí ì ṣàfi hàn àwọn onísùúrù.');
INSERT INTO "aykyuni" VALUES(3,143,'3:143','Dájúdájú ẹ ti ń wá ikú (ogun ẹ̀sìn) ṣíwájú kí ẹ tó pàdé rẹ̀. Ẹ kúkú ti rí i (báyìí), ẹ sì ń wòran.');
INSERT INTO "aykyuni" VALUES(3,144,'3:144','Kí ni (Ànábì) Muhammad bí kò ṣe Òjíṣẹ́, tí àwọn Òjíṣẹ́ kan ti lọ ṣíwájú rẹ̀.Ṣé tí ó bá kú tàbí tí wọ́n bá pa á, ẹ máa pẹ̀yìndà (sẹ́sìn)? Ẹnikẹ́ni tí ó bá pẹ̀yìndà (sẹ́sìn) kò lè kó ìnira kan kan bá Allāhu. Allāhu yó sì san àwọn olùdúpẹ́ ní ẹ̀san rere. ');
INSERT INTO "aykyuni" VALUES(3,145,'3:145','Ẹ̀mí kan kò lè kú àfi pẹ̀lú ìyọ̀ǹda Allāhu. (Ikú jẹ́) àkọsílẹ̀ onígbèdéke. Ẹnikẹ́ni tí ó bá ń fẹ́ ẹ̀san (ní) ayé, A máa fún un nínú rẹ̀(ní ayé). Ẹnikẹ́ni tí ó bá sì ń fẹ́ ẹ̀san (ní) ọ̀run, A máa fún un nínú rẹ̀ (ní ọ̀run). A ó sì san àwọn olùdúpẹ́ ní ẹ̀san rere. ');
INSERT INTO "aykyuni" VALUES(3,146,'3:146','Mélòó mélòó nínú àwọn Ànábì tí wọ́n ti jagun ẹ̀sìn pẹ̀lú ọ̀pọ̀lọ́pọ̀ ẹgbẹẹgbẹ̀rún (ọmọ-ẹ̀yìn wọn). Wọn kò ní ìrẹ̀wẹ̀sì ọkàn nípa ohun tí ó ṣẹlẹ̀ sí wọn ní ojú ogun ẹ̀sìn Allāhu. Wọn kò kọ́lẹ, wọn kò sì jura wọn sílẹ̀ fún ọ̀tá ẹ̀sìn. Allāhu sì nífẹ̀ẹ́ àwọn onísùúrù.');
INSERT INTO "aykyuni" VALUES(3,147,'3:147','Kò sì sí ohun kan tí wọ́n sọ tayọ pé wọ́n sọ pé: “Olúwa wa forí àwọn ẹ̀ṣẹ̀ wa àti àṣejù wa nínú ọ̀rọ̀ (àwọn àṣìṣe) wa jìn wá, mú ẹsẹ̀ wa dúró ṣinṣin, kí O sì ṣe àrànṣe fún wa lórí ìjọ aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(3,148,'3:148','Nítorí náà, Allāhu fún wọn ní ẹ̀san ayé àti dáadáa ẹ̀san ọ̀run. Allāhu sì nífẹ̀ẹ́ àwọn olùṣe-rere.');
INSERT INTO "aykyuni" VALUES(3,149,'3:149','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, tí ẹ bá tẹ̀lé àwọn tó ṣàì gbàgbọ́, wọ́n máa yi yín lẹ́sẹ̀ padà kúrò nínú ẹ̀sìn. Nígbà náà, ẹ máa padà di ẹni òfò (sínú àìgbàgbọ́).');
INSERT INTO "aykyuni" VALUES(3,150,'3:150','Allāhu sì ni Alárànṣe yín.Ó sì lóore jùlọ nínú àwọn alárànṣe.2 ');
INSERT INTO "aykyuni" VALUES(3,151,'3:151','A máa fi ẹ̀rù jẹ̀jẹ̀ sínú ọkàn àwọn tó ṣàì gbàgbọ́ nítorí pé wọ́n bá Allāhu wá akẹgbẹ́, èyí tí kò sọ ẹ̀rí kan kalẹ̀ fún. Iná ni ibùgbé wọn; ilé àwọn alábòsí sì burú.');
INSERT INTO "aykyuni" VALUES(3,152,'3:152','Dájúdájú Allāhu ti mú àdéhùn Rẹ̀ ṣẹ fún yín nígbà tí ẹ̀ ń pa wọ́n pẹ̀lú ìyọ̀ǹda Rẹ̀, títí di ìgbà tí ẹ fi ṣojo, tí ẹ sì ń jiyàn sí ọ̀rọ̀. Ẹ sì yapa àṣẹ (Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -) lẹ́yìn tí Allāhu fi ohun tí ẹ nífẹ̀ẹ́ sí hàn yín. - Ó wà nínú yín ẹni tí ń fẹ́ ayé, ó sì ń bẹ nínú yín ẹni tí ń fẹ́ ọ̀run. - Lẹ́yìn náà, (Allāhu) yí ojú yín kúrò ní ọ̀dọ̀ wọn nítorí kí Ó lè dan yín wò. Ó sì ti ṣàmójú kúrò fún yín; Allāhu ni Olóore àjùlọ lórí àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(3,153,'3:153','(Ẹ rántí) nígbà tí ẹ̀ ń gùnkè sá lọ, tí ẹ̀yin kò sì bojú wo ẹnì kan kan mọ́ lẹ́yìn, Òjíṣẹ́ sì ń pè yín láti ẹ̀yìn yín. Nítorí náà, (Allāhu) fi ìbànújẹ́(pípa tí àwọn ọ̀ṣẹbọ pa àwọn kan láààrin yín) san yín ní ẹ̀san ìbànújẹ́ (tí ẹ fi kan Ànábì nípasẹ̀ àìtẹ̀lé àṣẹ rẹ̀ lójú ogun ’Uhd. (Ó rí bẹ́ẹ̀) nítorí kí ẹ má baà banújẹ́ lórí ohun tí ó bọ́ fún yín àti ohun tí ó ṣẹlẹ̀ si yín. Allāhu ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(3,154,'3:154','Lẹ́yìn náà, Ó sọ ìfàyàbalẹ̀ kalẹ̀ fún yín lẹ́yìn ìbànújẹ́; òògbé ta igun kan lọ nínú yín. Igun kan tí ẹ̀mí ara wọn sì ti kó èrò bá, tí wọ́n sì ń ní èròkérò sí Allāhu lọ́nà àìtọ́, èròkérò ìgbà àìmọ̀kan, wọ́n sì ń wí pé: “Ǹjẹ́ a ní àṣẹ kan (tí a lè mú wá) lórí ọ̀rọ̀ náà bí!” Sọ pé: “Dájúdájú gbogbo àṣẹ ń jẹ́ ti Allāhu pátápátá.” Wọ́n ń fi pamọ́ sínú ọkàn wọn ohun tí wọn kò lè ṣàfi hàn rẹ̀ fún ọ. Wọ́n ń wí pé: “Tí ó bá jẹ́ pé a ní àṣẹ kan lórí ọ̀rọ̀ náà ni, wọn ìbá tí pa wá síbí.” Sọ pé: “Tí ó bá jẹ́ pé ẹ wà nínú ilé yín, àwọn tí A ti kọ àkọọ́lè pípa sí ibùjàgun wọn mọ ìbá kúkú jáde lọ síbẹ̀.”(Ogun yìí rí bẹ́ẹ̀) nítorí kí Allāhu lè gbìdánwò ohun tí ń bẹ nínú igbá-àyà yín àti nítorí kí Ó lè ṣàfọ̀mọ́ ohun tí ń bẹ nínú ọkàn yín. Allāhu sì ni Onímọ̀ nípa ohun tí ń bẹ nínú igbá-àyà ẹ̀dá. ');
INSERT INTO "aykyuni" VALUES(3,155,'3:155','Dájúdájú àwọn tó pẹ̀yìndà nínú yín ní ọjọ́ tí ìjọ méjì pàdé, dájúdájú Èṣù l’ó yẹ̀ wọ́n lẹ́sẹ̀ nítorí apá kan ohun tí wọ́n ṣe níṣẹ́. Allāhu sì kúkú ti ṣàmójú kúrò fún wọn. Dájúdájú Allāhu ni Aláforíjìn, Aláfaradà.');
INSERT INTO "aykyuni" VALUES(3,156,'3:156','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe dà bí àwọn tó ṣàì gbàgbọ́, tí wọ́n sọ fún àwọn ọmọ ìyá wọn, nígbà tí wọ́n ń rìn kiri ní orí ilẹ̀tàbí (nígbà) tí wọ́n ń jagun, pé: “Tí ó bá jẹ́ pé wọ́n ń bẹ ní ọ̀dọ̀ wa ni, wọn ìbá tí kú, àti pé wọn ìbá tí pa wọ́n.” (Wọ́n wí bẹ́ẹ̀) nítorí kí Allāhu lè fi ìyẹn ṣe àbámọ̀ sínú ọkàn wọn. Allāhu l’Ó ń sọ ẹ̀dá di alààyè. Ó sì ń sọ ẹ̀dá di òkú. Allāhu sì ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(3,157,'3:157','Dájúdájú tí wọ́n bá pa yín s’ójú ogun ẹ̀sìn Allāhu tàbí ẹ kú (sínú ilé), dájúdájú àforíjìn àti àánú láti ọ̀dọ̀ Allāhu lóore ju ohun tí ẹ̀ ń kójọ (nílé ayé).');
INSERT INTO "aykyuni" VALUES(3,158,'3:158','Dájúdájú tí ẹ bá kú (sínú ilé) tàbí wọ́n pa yín (s’ójú ogun ẹ̀sìn), dájúdájú ọ̀dọ̀ Allāhu ni wọn máa ko yín jọ sí.');
INSERT INTO "aykyuni" VALUES(3,159,'3:159','Pẹ̀lú ìkẹ́ kan láti ọ̀dọ̀ Allāhu l’ó kúkú fi rọ̀ fún wọn; tí ó bá jẹ́ pé o jẹ́ ẹni burúkú, ọlọ́kàn líle, wọn ìbá ti túká kúrò lọ́dọ̀ rẹ. Ṣàmójú kúrò fún wọn, bá wọn tọrọ àforíjìn, kí o sì bá wọn jíròrò lórí ọ̀rọ̀. Tí o bá sì pinnu (ọ̀rọ̀ kan), kí o gbáralé Allāhu. Dájúdájú Allāhu nífẹ̀ẹ́ àwọn olùgbáralé E.');
INSERT INTO "aykyuni" VALUES(3,160,'3:160','Tí Allāhu bá ṣe àrànṣe fún yín, kò sí ẹni tí ó máa borí yín. Tí Ó bá sì yẹpẹrẹ yín, ta sì ni ẹni tí ó máa ṣe àrànṣe fún yín lẹ́yìn Rẹ̀? Allāhu ni kí àwọn onígbàgbọ́ òdodo gbáralé.');
INSERT INTO "aykyuni" VALUES(3,161,'3:161','Kò lẹ́tọ̀ọ́ fún Ànábì kan láti jí mú nínú ọrọ̀ ogun ṣíwájú kí wọ́n tó pín in. Ẹnikẹ́ní tí ó bá jí ọrọ̀ ogun mú ṣíwájú kí wọ́n tó pín in, ó máa dá ohun tí ó jí mú nínú ọrọ̀ ogun náà padà ní Ọjọ́ Àjíǹde. Lẹ́yìn náà, A óò san ẹ̀mí kọ̀ọ̀kan ní ẹ̀san ohun tí ó ṣe níṣẹ́ ní ẹ̀kúnrẹ́rẹ́. A ò sì níí ṣe àbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(3,162,'3:162','Ṣé ẹni tí ó (ṣiṣẹ́) tọ ìyọ́nú Allāhu dà bí ẹni tí ó padà wálé pẹ̀lú ìbínú láti ọ̀dọ̀ Allāhu, ibùgbé rẹ̀ sì ni iná Jahanamọ? Ìkángun náà sì burú.');
INSERT INTO "aykyuni" VALUES(3,163,'3:163','(Èkínní kejì èrò inú Ọgbà Ìdẹ̀ra àti èrò inú Iná ni) wọ́n ní ipò (ọ̀tọ̀ọ̀tọ̀) ní ọ̀dọ̀ Allāhu. Allāhu sì ni Olùríran nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(3,164,'3:164','Allāhu kúkú ti ṣe oore fún àwọn onígbàgbọ́ òdodo nígbà tí Ó fi lè gbé Òjíṣẹ́ kan dìde sí wọn láààrin ara wọn. (Òjíṣẹ́ náà) ń ké àwọn āyah Rẹ̀ fún wọn. Ó ń fọ̀ wọ́n mọ́ (nínú ẹ̀ṣẹ̀). Ó sì ń kọ́ wọn ní Tírà (al-Ƙur’ān) àti òye ìjìnlẹ̀ (sunnah), bí ó tilẹ̀ jẹ́ pé tẹ́lẹ̀tẹ́lẹ̀ wọ́n wà nínú ìṣìnà pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(3,165,'3:165','Ṣé gbogbo ìgbà tí àdánwò kan bá kàn yín, tí ẹ sì ti fi méjì irú rẹ̀ (kan àwọn kèfèrí), ni ẹ̀yin yóò máa sọ pé: “Ọ̀nà wo ni èyí gbà ṣẹlẹ̀ sí wa?” Sọ pé: “Ó wá láti ọ̀dọ̀ ara yín.” Dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(3,166,'3:166','Ohun tí ó ṣẹlẹ̀ si yín ní ọjọ́ tí ikọ̀ ogun méjèèjì pàdé, (ó ṣẹlẹ̀) pẹ̀lú ìyọ̀ǹda Allāhu. Àti pé nítorí kí Ó lè ṣàfi hàn àwọn onígbàgbọ́ òdodo ni.');
INSERT INTO "aykyuni" VALUES(3,167,'3:167','(Ó tún rí bẹ́ẹ̀) nítorí kí Ó lè ṣàfi hàn àwọn tó ṣọ̀bẹ-ṣèlu (nínú àwọn mùsùlùmí). (Àwọn onígbàgbọ́ òdodo) sì sọ fún wọn pé: “Ẹ máa bọ̀, kí á lọ jagun fún ẹ̀sìn Allāhu tàbí ẹ wá dáàbò bo ẹ̀mí ara yín.” Wọ́n wí pé: “Àwa ìbá mọ ogun-ún jà àwa ìbá tẹ̀lé yín.” Wọ́n súnmọ́ àìgbàgbọ́ ju ìgbàgbọ́ ní ọjọ́ yẹn. Wọ́n ń fi ẹnu wọn sọ ohun tí kò sí nínú ọkàn wọn. Allāhu sì nímọ̀ jùlọ nípa ohun tí wọ́n ń fi pamọ́.');
INSERT INTO "aykyuni" VALUES(3,168,'3:168','Àwọn tó sọ nípa àwọn ọmọ ìyá wọn (tó kú s’ójú ogun ẹ̀sìn) pé – tí àwọn sì jòkóò kalẹ̀ sílé (ní tiwọn)-, "Tí ó bá jẹ́ pé wọ́n tẹ̀lé tiwa ni, wọn ìbá tí pa wọ́n." Sọ pé: “Ẹ yẹkú dànù fún ẹ̀mí yín tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(3,169,'3:169','Ẹ má ṣe lérò pé òkú (ìyà) ni àwọn tí wọ́n pa s’ójú ogun ẹ̀sìn Allāhu, bí kò ṣe pé alààyè (ẹni ìkẹ́) ni wọ́n. Wọ́n sì ń pèsè ìjẹ-ìmu fún wọn lọ́dọ̀ Olúwa wọn.1 ');
INSERT INTO "aykyuni" VALUES(3,170,'3:170','Wọ́n ń dunnú nítorí ohun tí Allāhu fún wọn nínú oore-àjùlọ Rẹ̀. Wọ́n sì ń yọ̀ fún àwọn tí kò tí ì pàdé wọn nínú àwọn tí wọ́n fi sílẹ̀ pé: “Kò sí ìpáyà fún wọn. Wọn kò sì níí banújẹ́.”');
INSERT INTO "aykyuni" VALUES(3,171,'3:171','Wọ́n ń bá wọn yọ̀ fún ìkẹ́ àti oore-àjùlọ (tí ń dúró dè wọ́n) lọ́dọ̀ Allāhu. Àti pé dájúdájú Allāhu kò níí fi ẹ̀san àwọn onígbàgbọ́ òdodo ráre.');
INSERT INTO "aykyuni" VALUES(3,172,'3:172','(Àwọn ni) àwọn tó jẹ́pè Allāhu àti Òjíṣẹ́ lẹ́yìn ìgbà tí wọ́n ti f’ara gbọgbẹ́. Ẹ̀san ńlá wà fún àwọn tó ṣe rere, tí wọ́n sì bẹ̀rù (Allāhu) nínú wọn.');
INSERT INTO "aykyuni" VALUES(3,173,'3:173','(Àwọn ni) àwọn tí àwọn ènìyàn wí fún pé: “Wọ́n mà ti kóra jọ dè yín, nítorí náà ẹ bẹ̀rù wọn.” (Èyí) lékún ìgbàgbọ́ òdodo wọn. Wọ́n sì sọ pé: “Allāhu tó fún wa. Ó sì dára ni Alámòójútó.”');
INSERT INTO "aykyuni" VALUES(3,174,'3:174','Nítorí náà, wọ́n padà délé pẹ̀lú ìkẹ́ àti oore-àjùlọ láti ọ̀dọ̀ Allāhu. Aburú kan kò sì kàn wọ́n. Àti pé wọ́n (ṣiṣẹ́) tọ ìyọ́nú Allāhu. Allāhu sì ni Olóore ńlá.');
INSERT INTO "aykyuni" VALUES(3,175,'3:175','Dájúdájú ìyẹn ni Èṣù, tí ó ń fi àwọn ọ̀rẹ́ rẹ̀ dẹ́rù bà yín. Nítorí náà, ẹ má ṣe bẹ̀rù wọn. Kí ẹ sì bẹ̀rù Mi tí ẹ bá jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(3,176,'3:176','Má ṣe jẹ́ kí àwọn tí ń sáré kó sínú àìgbàgbọ́ kó ìbànújẹ́ bá ọ. Dájúdájú wọn kò lè kó ìnira kan kan bá Allāhu. Allāhu kò fẹ́ kí ìpín kan nínú oore wà fún wọn ní ọ̀run (ni); ìyà ńlá sì ń bẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(3,177,'3:177','Dájúdájú àwọn tó fi ìgbàgbọ́ òdodo ra àìgbàgbọ́,wọn kò lè kó ìnira kan kan bá Allāhu. Ìyà ẹlẹ́ta-eléro sì wà fún wọn. ');
INSERT INTO "aykyuni" VALUES(3,178,'3:178','Kí àwọn tó ṣàì gbàgbọ́ má ṣe lérò pé bí A ṣe ń lọ́ra fún wọn jẹ́ oore fún wọn. A kàn ń lọ́ra fún wọn nítorí kí wọ́n lè lékún nínú ẹ̀ṣẹ̀ ni. Ìyà tí í yẹpẹrẹ ẹ̀dá sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(3,179,'3:179','Allāhu kò níí gbé àwọn onígbàgbọ́ òdodo jù sílẹ̀ lórí ohun tí ẹ wà lórí rẹ̀ (níbi àìmọ onígbàgbọ́ òdodo lọ́tọ̀ yàtọ̀ sí ṣọ̀bẹ-ṣèlu) títí Ó máa fi ya ẹni burúkú sọ́tọ̀ kúrò lára ẹni dáadáa. Allāhu kò sì níí fi ìmọ̀ ìkọ̀kọ̀ mọ̀ yín, ṣùgbọ́n Allāhu yóò ṣẹ̀ṣà ẹni tí Ó bá fẹ́ nínú àwọn Òjíṣẹ́ Rẹ̀. Nítorí náà, ẹ gbàgbọ́ nínú Allāhu àti àwọn Òjíṣẹ́ Rẹ̀. Tí ẹ bá gbàgbọ́ ní òdodo, tí ẹ sì bẹ̀rù (Allāhu), ẹ̀san ńlá yó sì wà fún yín. ');
INSERT INTO "aykyuni" VALUES(3,180,'3:180','Kí àwọn tó ń ṣahun pẹ̀lú ohun tí Allāhu fún wọn nínú oore-àjùlọ Rẹ̀ má ṣe lérò pé oore ni fún wọn, bí kò ṣe pé aburú ni fún wọn. A ó fí ohun tí wọ́n fi ṣahun kọ́ wọn lọ́rùn ní Ọjọ́ Àjíǹde. Ti Allāhu sì ni ogún àwọn sánmọ̀ àti ilẹ̀. Allāhu sì ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(3,181,'3:181','Allāhu kúkú ti gbọ́ ọ̀rọ̀ àwọn tó wí pé: “Dájúdájú aláìní ni Allāhu, àwa sì ni ọlọ́rọ̀.” A máa ṣàkọsílẹ̀ ohun tí wọ́n wí àti pípa tí wọ́n pa àwọn Ànábì láì lẹ́tọ̀ọ́. A sì máa sọ pé: “Ẹ tọ́ ìyà Iná jónijóni wò.”');
INSERT INTO "aykyuni" VALUES(3,182,'3:182','Ìyẹn (rí bẹ́ẹ̀) nítorí ohun tí ọwọ́ yín tì síwájú. Àti pé dájúdájú Allāhu kò níí ṣe àbòsí fún àwọn ẹrú (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(3,183,'3:183','Àwọn tó wí pé: “Dájúdájú Allāhu ṣàdéhùn fún wa pé a ò gbọdọ̀ gba Òjíṣẹ́ kan gbọ́ títí ó máa fí mú ẹran sàráà kan wá fún wa tí iná (àtọ̀runwá) yóò fi lánu.” Sọ pé: “Dájúdájú àwọn Òjíṣẹ́ kan ti wá ba yín ṣíwájú mi pẹ̀lú àwọn ẹ̀rí to yanjú àti èyí tí ẹ wí (yìí), nítorí kí ni ẹ fi pa wọ́n tí ẹ bá jẹ́ olódodo?”');
INSERT INTO "aykyuni" VALUES(3,184,'3:184','Tí wọ́n bá pè ọ́ ní òpùrọ́, wọ́n kúkú ti pe àwọn Òjíṣẹ́ tó ṣíwájú rẹ ní òpùrọ́. Wọ́n wá pẹ̀lú àwọn ẹ̀rí tó yanjú, ìpín-ìpín Tírà àti Tírà tó ń tànmọ́lẹ̀ sí ọ̀rọ̀ ẹ̀sìn.1 ');
INSERT INTO "aykyuni" VALUES(3,185,'3:185','Ẹ̀mí kọ̀ọ̀kan ló máa tọ́ ikú wò. A ó sì san yín ní ẹ̀san yín ní ẹ̀kúnrẹ́rẹ́ ní Ọjọ́ Àjíǹde. Nítorí náà, ẹnikẹ́ni tí A bá mú jìnnà tefé sí Iná, tí A sì mú wọ inú Ọgbà Ìdẹ̀ra, ó kúkú ti jèrè. Kí sì ni ìgbésí ayé bí kò ṣe ìgbádùn ẹ̀tàn.');
INSERT INTO "aykyuni" VALUES(3,186,'3:186','Dájúdájú A ó máa dan yín wò nínú dúkìá yín àti ẹ̀mí yín. Dájúdájú ẹ̀yin yóò máa gbọ́ ọ̀pọ̀lọ́pọ̀ ọ̀rọ̀ ìpalára láti ọ̀dọ̀ àwọn tí A fún ní tírà ṣíwájú yín àti àwọn ọ̀ṣẹbọ. Tí ẹ̀yin bá ṣe sùúrù, tí ẹ sì bẹ̀rù (Allāhu), dájúdájú ìyẹn wà nínú àwọn ìpinnu ọ̀rọ̀ tó pọn dandan.');
INSERT INTO "aykyuni" VALUES(3,187,'3:187','(Ẹ rántí) nígbà tí Allāhu gba àdéhùn àwọn tí A fún ní tírà pé ẹ gbọ́dọ̀ ṣe àlàyé rẹ̀ fún àwọn ènìyàn, ẹ ò sì gbọdọ̀ fi pamọ́. Wọ́n sì jù ú sẹ́yìn lẹ́yìn wọn. Wọ́n sì tà á ní owó kékeré. Nítorí náà, ohun tí wọ́n ń tà mà sì burú (níyà).');
INSERT INTO "aykyuni" VALUES(3,188,'3:188','Ẹ má ṣe lérò pé àwọn tó ń dunnú sí ohun tí wọ́n ṣe (ní àìdáa máa là nínú ìyà). Wọ́n sì nífẹ̀ẹ́ sí kí àwọn (ènìyàn) máa yìn wọ́n fún ohun tí wọn kò ṣe (níṣẹ́ rere). Nítorí náà, ẹ má ṣe rò wọ́n ro ìgbàlà níbi Ìyà. Ìyà ẹlẹ́ta eléro sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(3,189,'3:189','Ti Allāhu ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Allāhu sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(3,190,'3:190','Dájúdájú àwọn àmì wà nínú ìṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀, ìtẹ̀léǹtẹ̀lé òru àti ọ̀sán; (àmì wà nínú wọn) fún àwọn onílàákàyè.1 ');
INSERT INTO "aykyuni" VALUES(3,191,'3:191','Àwọn tó ń rántí Allāhu ní ìnàró, ìjókòó àti ní ìdùbúlẹ̀; tí wọ́n ń ronú sí ìṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀, (wọ́n sì ń sọ pé:) “Olúwa wa, Ìwọ kò ṣẹ̀dàá èyí pẹ̀lú irọ́. Mímọ́ ni fún Ọ. Nítorí náà, ṣọ́ wa níbi ìyà Iná.');
INSERT INTO "aykyuni" VALUES(3,192,'3:192','Olúwa wa, dájúdájú ẹnikẹ́ni tí O bá mú wọ inú Iná, O ti dójú tì í. Kò sì níí sí àwọn olùrànlọ́wọ́ fún àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(3,193,'3:193','Olúwa wa, dájúdájú àwa gbọ́ olùpèpè kan tó ń pe (ìpè) sí ibi ìgbàgbọ́ pé: “Ẹ gba Olúwa yín gbọ́.” A sì gbàgbọ́ ní òdodo. Olúwa wa, nítorí náà, forí àwọn ẹ̀ṣẹ̀ wa jìn wá, kí O sì pa àwọn àṣìṣe wa rẹ́, kí O sì pa wá pẹ̀lú àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(3,194,'3:194','Olúwa wa, fún wa ní ohun tí O ṣàdéhùn rẹ̀ fún wa lórí (ahọ́n) àwọn Òjíṣẹ́ Rẹ. Má sì ṣe dójú tì wá ní Ọjọ́ Àjíǹde. Dájúdájú Ìwọ kì í yapa àdéhùn.”');
INSERT INTO "aykyuni" VALUES(3,195,'3:195','Olúwa wọn sì jẹ́pè wọn pé: “Dájúdájú Èmi kò níí fi iṣẹ́ oníṣẹ́ kan nínú yín ráre; ọkùnrin ni tàbí obìnrin, ara kan náà ni yín (níbi ẹ̀san). Nítorí náà, àwọn tó gbé (ìlú wọn) jù sílẹ̀, tí wọ́n lé jáde kúrò nínú ìlú wọn, tí wọ́n sì fi ìnira kàn wọ́n nítorí ẹ̀sìn Mi, wọ́n jagun ẹ̀sìn, wọ́n sì pa wọ́n, dájúdájú Èmi yóò bá wọn pa àwọn àṣìṣe wọn rẹ́, Èmi yó sì mú wọn wọ inú àwọn Ọgbà Ìdẹ̀ra tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. (Ó jẹ́) ẹ̀san láti ọ̀dọ̀ Allāhu. Ní ọ̀dọ̀ Allāhu sì ni ẹ̀san dáadáa wà.');
INSERT INTO "aykyuni" VALUES(3,196,'3:196','Má ṣe gba ẹ̀tàn pẹ̀lú ìgbòkè-gbodò àwọn tó ṣàì gbàgbọ́ nínú ìlú.');
INSERT INTO "aykyuni" VALUES(3,197,'3:197','Ìgbádùn díẹ̀ (lè wà fún wọn), lẹ́yìn náà iná Jahanamọ ni ibùgbé wọn. Ibùgbé náà sì burú.');
INSERT INTO "aykyuni" VALUES(3,198,'3:198','Ṣùgbọ́n àwọn tó bẹ̀rù Olúwa wọn, àwọn Ọgbà Ìdẹ̀ra tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀ ń bẹ fún wọn. Olùṣegbére ni wọ́n nínú rẹ̀. Ibùdésí kan ni láti ọ̀dọ̀ Allāhu. Ohun tí ń bẹ lọ́dọ̀ Allāhu sì lóore jùlọ fún àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(3,199,'3:199','Dájúdájú ó ń bẹ nínú àwọn onítírà ẹni tó gbàgbọ́ nínú Allāhu, àti ohun tí A sọ̀kalẹ̀ fún yín àti ohun tí A sọ̀kalẹ̀ fún wọn; wọ́n ń páyà Allāhu, wọn kì í ta àwọn ọ̀rọ̀ Allāhu ní owó kékeré. Àwọn wọ̀nyẹn ní ẹ̀san lọ́dọ̀ Olúwa wọn. Dájúdájú Allāhu ni Olùyára níbi ìṣírò-iṣẹ́.1 ');
INSERT INTO "aykyuni" VALUES(3,200,'3:200','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ ṣe sùúrù, ẹ pàrọwà sùúrù, ẹ ṣọ́ bodè ìlú yín (láti lè dènà àwọn ọmọ-ogun ọ̀tá ẹ̀sìn). Kí ẹ sì bẹ̀rù Allāhu nítorí kí ẹ lè jèrè.');
INSERT INTO "aykyuni" VALUES(4,1,'4:1','Ẹ̀yin ènìyàn, ẹ bẹ̀rù Olúwa yín, Ẹni tí Ó ṣẹ̀dá yín láti ara ẹ̀mí ẹyọ kan (ìyẹn, Ànábì Ādam). Ó sì ṣẹ̀dá aya rẹ̀ (Hawā’) láti ara rẹ̀. Ó fọ́n ọ̀pọ̀lọ́pọ̀ ọkùnrin àti obìnrin jáde láti ara àwọn méjèèjì. Kí ẹ sì bẹ̀rù Allāhu, Ẹni tí ẹ̀ ń fi bẹ ara yín. (Ẹ ṣọ́) okùn-ìbí. Dájúdájú Allāhu ń jẹ́ Olùṣọ́ lórí yín.');
INSERT INTO "aykyuni" VALUES(4,2,'4:2','Ẹ fún àwọn ọmọ òrukàn ní dúkìá wọn.Ẹ má ṣe fi n̄ǹkan dáadáa pààrọ̀ n̄ǹkan burúkú.2 Ẹ sì má ṣe jẹ dúkìá wọn mọ́ dúkìá yín; dájúdájú ó jẹ́ ẹ̀ṣẹ̀ tó tóbi. ');
INSERT INTO "aykyuni" VALUES(4,3,'4:3','Tí ẹ bá páyà pé ẹ ò níí ṣe déédé nípa (fífẹ́) ọmọ òrukàn,ẹ fẹ́ ẹni tó lẹ́tọ̀ọ́ si yín nínú àwọn obìnrin; méjì tàbí mẹ́ta tàbí mẹ́rin.2 Ṣùgbọ́n tí ẹ bá páyà pé ẹ ò níí ṣe déédé, (ẹ fẹ́) ẹyọ kan tàbí ẹrúbìnrin yín. Ìyẹn súnmọ́ jùlọ pé ẹ ò níí ṣàbòsí. ');
INSERT INTO "aykyuni" VALUES(4,4,'4:4','Ẹ fún àwọn obìnrin ní sọ̀daàkí wọn tó jẹ́ ọ̀ran-anyàn ní tọkàn-tọkàn. Tí wọ́n bá sì fi inú dídùn yọ̀ǹda kiní kan fún yín nínú rẹ̀, ẹ jẹ ẹ́ pẹ̀lú ìrọ̀rùn àti ìgbádùn.');
INSERT INTO "aykyuni" VALUES(4,5,'4:5','Ẹ má ṣe kó dúkìá yín tí Allāhu fí ṣe gbẹ́mìíró fún yín lé àwọn aláìlóye lọ́wọ́. Ẹ pèsè ìjẹ-ìmu fún wọn nínú rẹ̀, kí ẹ sì raṣọ fún wọn. Kí ẹ sì máa sọ̀rọ̀ dáadáa fún wọn.');
INSERT INTO "aykyuni" VALUES(4,6,'4:6','Ẹ dan àwọn ọmọ òrukàn wotítí wọn yóò fi dàgbà wọ ẹni tí ò lè ṣe ìgbéyàwó.2 Tí ẹ bá sì ti rí òye lára wọn, kí ẹ dá dúkìá wọn padà fún wọn. Ẹ ò gbọdọ̀ jẹ dúkìá wọn ní ìjẹ àpà àti ìjẹkújẹ kí wọ́n tó dàgbà. Ẹni tí ó bá jẹ́ ọlọ́rọ̀, kí ó mójú kúrò (níbẹ̀). Ẹni tí ó bá jẹ́ aláìní, kí ó jẹ nínú rẹ̀ ní ọ̀nà tó dára. Nítorí náà, tí ẹ bá fẹ́ dá dúkìá wọn padà fún wọn, ẹ pe àwọn ẹlẹ́rìí sí wọn. Allāhu sì tó ní Olùṣírò. ');
INSERT INTO "aykyuni" VALUES(4,7,'4:7','Àwọn ọkùnrin ní ìpín nínú ohun tí òbí méjèèjì àti ẹbí fi sílẹ̀. Àti pé àwọn obìnrin náà ní ìpín nínú ohun tí òbí méjéèjì àti ẹbí fi sílẹ̀. Nínú ohun tó kéré nínú rẹ̀ tàbí tó pọ̀; (ó jẹ́) ìpín tí wọ́n ti pín (tí wọ́n ti ṣàdáyanrí rẹ̀ fún wọn).');
INSERT INTO "aykyuni" VALUES(4,8,'4:8','Nígbà tí àwọn ẹbí, àwọn ọmọ òrukàn àti àwọn mẹ̀kúnnù bá wà ní (ìjókòó) ogún pípín, ẹ fún wọn nínú rẹ̀ (ṣíwájú kí ẹ tó pín ogún), kí ẹ sì sọ̀rọ̀ dáadáa fún wọn.');
INSERT INTO "aykyuni" VALUES(4,9,'4:9','Àwọn (olùpíngún àti alágbàwò-ọmọ-òrukàn) tí ó bá jẹ́ pé àwọn náà máa fi ọmọ wẹ́wẹ́ sílẹ̀, tí wọ́n sì ń páyà lórí wọn, kí wọ́n yáa bẹ̀rù Allāhu. Kí wọ́n sì máa sọ̀rọ̀ dáadáa (fún ọmọ òrukàn).');
INSERT INTO "aykyuni" VALUES(4,10,'4:10','Dájúdájú àwọn tó ń jẹ dúkìá ọmọ òrukàn pẹ̀lú àbòsí, Iná kúkú ni wọ́n ń jẹ sínú ikùn wọn. Wọ́n sì máa wọ inú Iná tó ń jó.');
INSERT INTO "aykyuni" VALUES(4,11,'4:11','Allāhu ń sọ àsọọ́lẹ̀ (òfin) fún yín nípa (ogún tí ẹ máa pín fún) àwọn ọmọ yín; ti ọkùnrin ni irú ìpín ti obìnrin méjì. Tí wọ́n bá sì jẹ́ obìnrin (nìkan), méjì sókè, tiwọn ni ìdá méjì nínú ìdá mẹ́ta ohun tí (òkú) fi sílẹ̀. Tí ó bá jẹ́ obìnrin kan ṣoṣo, ìdajì ni tirẹ̀. Ti òbí rẹ̀ méjèèjì, ìdá kan nínú ìdá mẹ́fà ni ti ìkọ̀ọ̀kan àwọn méjèèjì nínú ohun tí òkú fi sílẹ̀, tí ó bá ní ọmọ láyé. Tí kò bá sì ní ọmọ láyé, tí ó sì jẹ́ pé àwọn òbí rẹ̀ méjèèjì l’ó máa jogún rẹ̀, ìdá kan nínú ìdá mẹ́ta ni ti ìyá rẹ̀. Tí ó bá ní arákùnrin (tàbí arábìnrin), ìdá kan nínú ìdá mẹ́fà ni ti ìyá rẹ̀, lẹ́yìn àsọọ́lẹ̀ tí ó sọ tàbí gbèsè. Àwọn bàbá yín àti àwọn ọmọkùnrin yín; ẹ̀yin kò mọ èwo nínú wọn ló máa mú àǹfààní ba yín jùlọ. (Ìpín) ọ̀ran-anyàn ni láti ọ̀dọ̀ Allāhu. Dájúdájú Allāhu ń jẹ́ Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(4,12,'4:12','Tiyín ni ìlàjì ohun tí àwọn ìyàwó yín fi sílẹ̀, tí wọn kò bá ní ọmọ. Tí wọ́n bá sì ní ọmọ, tiyín ni ìdá kan nínú ìdá mẹ́rin ohun tí wọ́n fi sílẹ̀, lẹ́yìn àsọọ́lẹ̀ tí wọ́n sọ tàbí gbèsè. Tiwọn sì ni ìdá kan nínú ìdá mẹ́rin ohun tí ẹ fi sílẹ̀, tí ẹ̀yin kò bá ní ọmọ láyé. Tí ẹ bá ní ọmọ láyé, ìdá kan nínú ìdá mẹ́jọ ni tiwọn nínú ohun tí ẹ fi sílẹ̀ lẹ́yìn àsọọ́lẹ̀ tí ẹ sọ tàbí gbèsè. Tí ọkùnrin kan tàbí obìnrin kan tí wọ́n fẹ́ jogún rẹ̀ kò bá ní òbí àti ọmọ, tí ó sì ní arákùnrin kan tàbí arábìnrin kan, ìdá kan nínú ìdá mẹ́fà ni ti ìkọ̀ọ̀kan àwọn méjèèjì. Tí wọ́n bá sì pọ̀ ju bẹ́ẹ̀ lọ, wọn yóò dìjọ pín ìdá kan nínú ìdá mẹ́ta, lẹ́yìn àsọọ́lẹ̀ tí wọ́n sọ tàbí gbèsè, tí kò níí jẹ́ ìpalára. Àsọọ́lẹ̀ (òfin) kan ni láti ọ̀dọ̀ Allāhu. Allāhu sì ni Onímọ̀, Aláfaradà.');
INSERT INTO "aykyuni" VALUES(4,13,'4:13','Ìyẹn ni àwọn ẹnu-ààlà (tí) Allāhu (gbékalẹ̀ fún ogún pípín). Ẹnikẹ́ni tí ó bá sì tẹ̀lé ti Allāhu àti Òjíṣẹ́ Rẹ̀, (Allāhu) yóò mú un wọ inú àwọn Ọgbà Ìdẹ̀ra kan tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀. Ìyẹn sì ni èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(4,14,'4:14','Ẹnikẹ́ni tí ó bá sì yapa (àṣẹ) Allāhu àti Òjíṣẹ́ Rẹ̀, tí ó sì ń tayọ àwọn ẹnu-ààlà tí Allāhu gbékalẹ̀, (Allāhu) yóò mú un wọ inú Iná kan. Olùṣegbére ni nínú rẹ̀. Ìyà tí í yẹpẹrẹ ẹ̀dá sì ń bẹ fún un.1 ');
INSERT INTO "aykyuni" VALUES(4,15,'4:15','Àwọn tó ń ṣe sìná nínú àwọn obìnrin yín, ẹ wá ẹlẹ́rìí mẹ́rin nínú yín tí ó máa jẹ́rìí lé wọn lórí. Tí wọ́n bá jẹ́rìí lé wọn lórí, kí ẹ dè wọ́n mọ́ inú ilé títí ikú yóò fi pa wọ́n tàbí (títí) Allāhu yóò fi fún wọn ní ọ̀nà (mìíràn).1 ');
INSERT INTO "aykyuni" VALUES(4,16,'4:16','Àwọn méjì tí wọ́n ṣe sìná nínú yín, kí ẹ (fi ẹnu tàbí ọwọ́) bá wọn wí.Tí wọ́n bá sì ronú pìwàdà, tí wọ́n ṣàtúnṣe, kí ẹ mójú kúrò lára wọn. Dájúdájú Allāhu ń jẹ́ Olùgba-ìronúpìwàdà, Àṣàkẹ́- ọ̀run. ');
INSERT INTO "aykyuni" VALUES(4,17,'4:17','Ìronúpìwàdà tí Allāhu yóò gbà ni ti àwọn tó ń ṣe iṣẹ́ aburú pẹ̀lú àìmọ̀kan.Lẹ́yìn náà, wọ́n ronú pìwàdà láìpẹ́ (ìyẹn ṣíwájú ọjọ́ ikú wọn). Àwọn wọ̀nyí ni Allāhu yóò gba ìronúpìwàdà wọn. Allāhu sì ń jẹ́ Onímọ̀, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(4,18,'4:18','Kò sí ìronúpìwàdà fún àwọn tó ń ṣe aburú títí ikú fi dé bá ọ̀kan nínú wọn, kí ó wá wí pé: “Dájúdájú èmi ronú pìwàdà nísisìnyìí.” (Kò tún sí ìronúpìwàdà) fún àwọn tó kú sípò aláìgbàgbọ́. Àwọn wọ̀nyẹn, A ti pèsè ìyà ẹlẹ́ta eléro sílẹ̀ dè wọ́n.');
INSERT INTO "aykyuni" VALUES(4,19,'4:19','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, kò lẹ́tọ̀ọ́ fún yín láti jẹ àwọn obìnrin mógún pẹ̀lú ipá (bíi ṣíṣú obìnrin lópó.) Ẹ má ṣe fúnlẹ̀ mọ́ wọn nítorí kí ẹ le gba apá kan n̄ǹkan tí ẹ fún wọn àyàfi tí wọ́n bá hùwà ìbàjẹ́ tó fojú hàn. Ẹ bá wọn lòpọ̀ pẹ̀lú dáadáa. Tí ẹ bá kórira wọn, ó lè jẹ́ pé ẹ kórira kiní kan, kí Allāhu sì ti fi ọ̀pọ̀lọ́pọ̀ oore sínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(4,20,'4:20','Tí ẹ bá sì fẹ́ fi ìyàwó kan pààrọ̀ àyè ìyàwó kan, tí ẹ sì ti fún ọ̀kan nínú wọn ní ọ̀pọ̀lọ́pọ̀ dúkìá, ẹ ò gbọdọ̀ gba n̄ǹkan kan nínú rẹ̀ mọ́. Ṣé ẹ̀yin yóò gbà á ní ti àdápa irọ́ (tí ẹ̀ ń pa mọ́ wọn) àti ẹ̀ṣẹ̀ tó fojú hàn(tí ẹ̀ ń dá nípa wọn)?1 ');
INSERT INTO "aykyuni" VALUES(4,21,'4:21','Báwo ni ẹ̀yin ṣe lè gbà á padà nígbà tí ó jẹ́ pé ẹ ti wọlé tọ’ra yín, tí wọ́n sì ti gba àdéhùn tó nípọn lọ́wọ́ yín!1 ');
INSERT INTO "aykyuni" VALUES(4,22,'4:22','Ẹ má ṣe fẹ́ ẹni tí àwọn bàbá yín ti fẹ́ nínú àwọn obìnrin àyàfi èyí tí ó ti ré kọjá. Dájúdájú ó jẹ́ ìwà ìbàjẹ́ àti ohun ìkórira. Ó sì burú ní ọ̀nà.');
INSERT INTO "aykyuni" VALUES(4,23,'4:23','A ṣe é ní èèwọ̀ fún yín (láti fẹ́) àwọn ìyá yín àti àwọn ọmọbìnrin yín àti àwọn arábìnrin yín àti àwọn arábìnrin bàbá yín àti àwọn arábìnrin ìyá yín àti àwọn ọmọbìnrin arákùnrin yín àti àwọn ọmọbìnrin arábìnrin yín àti àwọn ìyá yín tí ó fún yín ní ọyàn mu àti àwọn arábìnrin yín nípasẹ̀ ọyàn àti àwọn ìyá ìyàwó yín àti àwọn ọmọbìnrin ìyàwó yín tí ẹ gbà tọ́, èyí tó wà nínú ilé yín,tí ẹ sì ti wọlé tọ àwọn ìyá wọn, - tí ẹ̀yin kò bá sì tí ì wọlé tọ̀ wọ́n, kò sí ẹ̀ṣẹ̀ fún yín (láti fẹ́ ọmọbìnrin wọn), - àti àwọn ìyàwó ọmọ yín, èyí tí ó ti ìbàdí yín jáde. (Ó tún jẹ́ èèwọ̀) láti fẹ́ tẹ̀gbọ́n tàbúrò papọ̀ àyàfi èyí tí ó ti ré kọjá. Dájúdájú Allāhu, Ó ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(4,24,'4:24','(Ó tún jẹ́ èèwọ̀ láti fẹ́) àwọn abilékọ nínú àwọn obìnrin àyàfi àwọn ẹrúbìnrin yín. Òfin Allāhu nìyí lórí yín. Wọ́n sì ṣe ẹni tó ń bẹ lẹ́yìn àwọn wọ̀nyẹn (àwọn obìnrin yòókù) ní ẹ̀tọ́ fún yín pé kí ẹ wá wọn fẹ́ pẹ̀lú dúkìá yín; ẹ fẹ́ wọn ní fífẹ́ ìyàwó, láì nìí bá wọn ṣe sìná (ṣíwájú yìgì síso). Ẹni tí ẹ bá sì fẹ́ ní fífẹ́ ìyàwó nínú wọn, tí ẹ sì ti jẹ ìgbádùn oorun ìfẹ́ lára wọn(àmọ́ tí ẹ fẹ́ kọ̀ wọ́n sílẹ̀), ẹ fún wọn ní sọ̀daàkí wọn tí ó jẹ́ ìpín ọ̀ran-anyàn.2 Kò sì sí ẹ̀ṣẹ̀ fún yín nípa ohun tí ẹ dìjọ yọ́nú sí (láti fojúfò láààrin ara yín) lẹ́yìn sọ̀daàkí (tó jẹ́ ìpín ọ̀ran-anyàn) Dájúdájú Allāhu ń jẹ́ Onímọ̀, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(4,25,'4:25','Ẹni tí kò bá lágbára ọrọ̀ nínú yín láti fẹ́ àwọn olómìnira onígbàgbọ́ òdodo lóbìnrin, kí ó fẹ́ nínú àwọn ẹrúbìnrin yín onígbàgbọ́ òdodo lóbìnrin. Allāhu nímọ̀ jùlọ nípa ìgbàgbọ́ yín, ara kan náà sì ni yín. Nítorí náà, ẹ fẹ́ wọn pẹ̀lú ìyọ̀ǹda àwọn olówó wọn (àwọn ọ̀gá wọn). Kí ẹ sì fún wọn ní sọ̀daàkí wọn ní ọ̀nà tó dára, (ìyẹn) àwọn ọmọlúàbí (ẹrúbìnrin), yàtọ̀ sí àwọn onísìná obìnrin àti àwọn obìnrin tí ń yan àlè Nígbà tí wọ́n bá sì di abilékọ, tí wọ́n bá (tún) lọ ṣe sìná, ìlàjì ìyà tí ń bẹ fún àwọn olómìnira obìnrin ni ìyà tí ń bẹ fún wọn.2 (Fífẹ́ ẹrúbìnrin), ìyẹn wà fún ẹni tó ń páyà ìnira (sìná) nínú yín. Àti pé kí ẹ ṣe sùúrù lóore jùlọ fún yín. Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(4,26,'4:26','Allāhu fẹ́ ṣàlàyé (òfin) fún yín, Ó fẹ́ tọ yín sí ọ̀nà àwọn tó ṣíwájú yín, Ó sì fẹ́ gba ìronúpìwàdà yín. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(4,27,'4:27','Àti pé Allāhu fẹ́ gba ìronúpìwàdà yín. Àwọn tó sì ń tẹ̀lé ìfẹ́-adùn ara sì ń fẹ́ kí ẹ yẹ̀ kúrò (nínú ẹ̀sìn) ní yíyẹ̀ tó tóbi pátápátá.');
INSERT INTO "aykyuni" VALUES(4,28,'4:28','Allāhu fẹ́ gbé e fúyẹ́ fún yín; A sì ṣẹ̀dá ènìyàn ní ọ̀lẹ.1 ');
INSERT INTO "aykyuni" VALUES(4,29,'4:29','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe fi èrú jẹ dúkìá yín láààrin ara yín àyàfi kí ó jẹ́ òwò ṣíṣe pẹ̀lú ìyọ́nú láààrin ara yín. Ẹ má ṣe para yín. Dájúdájú Allāhu ń jẹ́ Aláàánú si yín.');
INSERT INTO "aykyuni" VALUES(4,30,'4:30','Ẹnikẹ́ni tí ó bá sì ṣe ìyẹn láti fi tayọ ẹnu-ààlà àti láti fi ṣe àbòsí, láìpẹ́ A máa mú un wọ inú Iná. Ìrọ̀rùn sì ni ìyẹn jẹ́ fún Allāhu.');
INSERT INTO "aykyuni" VALUES(4,31,'4:31','Tí ẹ bá jìnnà sí àwọn ẹ̀ṣẹ̀ ńlá ńlá tí A kọ̀ fún yín, A máa pa àwọn ìwà àìdáa yín rẹ́ fún yín, A sì máa mu yín wọ àyè alápọ̀n-ọ́nlé.');
INSERT INTO "aykyuni" VALUES(4,32,'4:32','Ẹ má ṣe jẹ̀rankàn ohun tí Allāhu fi ṣ’oore àjùlọ fún apá kan yín lórí apá kan.Ìpín (ẹ̀san) ń bẹ fún àwọn ọkùnrin nípa ohun tí wọ́n ṣe níṣẹ́, ìpín (ẹ̀san) sì ń bẹ fún àwọn obìnrin nípa ohun tí wọ́n ṣe níṣẹ́. Kí ẹ sì tọrọ lọ́dọ̀ Allāhu nínú àlékún oore Rẹ̀. Dájúdájú Allāhu ń jẹ́ Onímọ̀ nípa gbogbo n̄ǹkan. ');
INSERT INTO "aykyuni" VALUES(4,33,'4:33','Ìkọ̀ọ̀kan (àwọn dúkìá) ni A ti ṣe àdáyanrí rẹ̀ fún àwọn tí ó máa jogún rẹ̀ nínú ohun tí àwọn òbí àti ẹbí fi sílẹ̀. Àwọn tí ẹ sì búra fún (pé ẹ máa fún ní n̄ǹkan), ẹ fún wọn ní ìpín wọn. Dájúdájú Allāhu ń jẹ́ Ẹlẹ́rìí lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(4,34,'4:34','Àwọn ọkùnrin ni òpómúlérófún àwọn obìnrin nítorí pé, Allāhu ṣ’oore àjùlọ fún apá kan wọn lórí apá kan2 àti nítorí ohun tí wọ́n ń ná nínú dúkìá wọn.3 Àwọn obìnrin rere ni àwọn olùtẹ̀lé-àṣẹ (Allāhu àti Òjíṣẹ́ Rẹ̀, lẹ́yìn náà àṣẹ ọkọ), àwọn olùṣọ́-ẹ̀tọ́ ọkọ ní kọ̀rọ̀ fún wí pé Allāhu ṣọ́ (ẹ̀tọ́ tiwọn náà fún wọn lọ́dọ̀ ọkọ wọn). Àwọn tí ẹ sì ń páyà oríkunkun wọn4, ẹ ṣe wáàsí fún wọn, ẹ takété sí ibùsùn wọn, ẹ lù wọ́n. Tí wọ́n bá sì tẹ̀lé àṣẹ yín, ẹ má ṣe fí ọ̀nà kan kan wá wọn níjà Dájúdájú Allāhu ga, Ó tóbi.6 ');
INSERT INTO "aykyuni" VALUES(4,35,'4:35','Tí ẹ bà sì mọ̀ pé ìyapa wà láààrin àwọn méjèèjì, ẹ gbé olóye kan dìde láti inú ẹbí ọkọ àti olóye kan láti inú ẹbí ìyàwó. Tí àwọn (tọkọ tìyàwó) méjèèjì bá ń fẹ́ àtúnṣe, Allāhu yóò fi àwọn (olóye méjèèjì) ṣe kòǹgẹ́ àtúnṣe lórí ọ̀rọ̀ ààrin wọn. Dájúdájú Allāhu ń jẹ́ Onímọ̀, Alámọ̀tán.');
INSERT INTO "aykyuni" VALUES(4,36,'4:36','Ẹ jọ́sìn fún Allāhu, ẹ má ṣe fi n̄ǹkan kan ṣẹbọ sí I. Ẹ ṣe dáadáa sí àwọn òbí méjèèjì àti ẹbí àti àwọn ọmọ òrukàn àti àwọn mẹ̀kúnnù àti aládùúgbò tó súnmọ́ àti aládùúgbò tó jìnnà àti ọ̀rẹ́ alábàárìn àti onírìn-àjò (tí agara dá) àti àwọn ẹrú yín. Dájúdájú Allāhu kò nífẹ̀ẹ́ onígbèéraga onífáàrí.');
INSERT INTO "aykyuni" VALUES(4,37,'4:37','Àwọn tó ń ṣahun, (àwọn) tó ń pa ènìyàn láṣẹ ahun ṣíṣe àti (àwọn) tó ń fi ohun tí Allāhu fún wọn nínú oore àjùlọ Rẹ̀ pamọ́; A ti pèsè ìyà tí í yẹpẹrẹ ẹ̀dá sílẹ̀ de àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(4,38,'4:38','Àwọn tó ń ná dúkìá wọn pẹ̀lú ṣekárími, tí wọn kò sì gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn; ẹnikẹ́ni tí Èṣù bá jẹ́ ọ̀rẹ́ fún, ó burú ní ọ̀rẹ́.');
INSERT INTO "aykyuni" VALUES(4,39,'4:39','Kí ni ìpalára tí ó máa ṣe fún wọn tí wọ́n bá gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn, tí wọ́n sì ná nínú ohun tí Allāhu ṣe ní arísìkí fún wọn? Allāhu sì jẹ́ Onímọ̀ nípa wọ́n.');
INSERT INTO "aykyuni" VALUES(4,40,'4:40','Dájúdájú Allāhu kò níí ṣàbòsí òdíwọ̀n ọmọ iná-igún. Tí ó bá jẹ iṣẹ́ rere, Ó máa ṣàdìpèlé (ẹ̀san) rẹ̀. Ó sì máa fún (olùṣe rere) ní ẹ̀san ńlá láti ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(4,41,'4:41','Báwo ni ọ̀rọ̀ wọn yóò ti rí nígbà tí A bá mú ẹlẹ́rìí jáde nínú ìjọ kọ̀ọ̀kan, tí A sì mú ìwọ jáde ní ẹlẹ́rìí lórí àwọn wọ̀nyí?');
INSERT INTO "aykyuni" VALUES(4,42,'4:42','Ní ọjọ́ yẹn, àwọn tó ṣàì gbàgbọ́, tí wọ́n sì yapa Òjíṣẹ́ náà, wọ́n á fẹ́ kí àwọn bá ilẹ̀ dọ́gba (kí wọ́n di erùpẹ̀). Wọn kò sì lè fi ọ̀rọ̀ kan pamọ́ fún Allāhu.');
INSERT INTO "aykyuni" VALUES(4,43,'4:43','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe súnmọ́ ìrun kíkí nígbà tí ọtí bá ń pa yín títí ẹ máa fi mọ ohun tí ẹ̀ ń sọàti àwọn oníjánnábà, àfi àwọn olùkọjá nínú mọ́sálásí, títí ẹ máa fi wẹ̀ (ìwẹ̀ jánnábà). Tí ẹ bá sì jẹ́ aláìsàn tàbí ẹ wà lórí ìrìn-àjò tàbí ẹnì kan nínú yín dé láti ibi ìgbọ̀nsẹ̀ tàbí ẹ súnmọ́ obínrin (yín), tí ẹ kò rí omi, ẹ fi erùpẹ̀ tó dára ṣe táyàmọ́mù; ẹ fi pá ojú yín àti ọwọ́ yín. Dájúdájú Allāhu ń jẹ́ Alámòójúkúrò, Aláforíjìn. ');
INSERT INTO "aykyuni" VALUES(4,44,'4:44','Ṣé ìwọ kò wòye sí àwọn tí A fún ní ìpín kan nínú tírà, tí wọ́n ń ra ìṣìnà, tí wọ́n sì fẹ́ kí ẹ ṣìnà lójú-ọ̀nà?');
INSERT INTO "aykyuni" VALUES(4,45,'4:45','Allāhu sì nímọ̀ jùlọ nípa àwọn ọ̀tá yín. Allāhu tó ní Aláàbò. Allāhu sì tó ní Alárànṣe.');
INSERT INTO "aykyuni" VALUES(4,46,'4:46','Ó ń bẹ nínú àwọn yẹhudi, àwọn tó ń yí ọ̀rọ̀ padà kúrò ní àwọn àyè rẹ̀, wọ́n sì ń wí pé: “A gbọ́, a sì yapa. Gbọ́ tiwa, àwa kò níí gbọ́ tìrẹ, òmùgọ̀ wa.”Wọ́n ń fi ahọ́n wọn yí ọ̀rọ̀ sódì àti pé wọ́n ń bu ẹnu-àtẹ́ lu ẹ̀sìn. Tí ó bá jẹ́ pé wọ́n sọ pé: “A gbọ́, a sì tẹ̀lé e. Gbọ́, kí o sì kíyè sí wa”, ìbá dára fún wọn, ìbá sì tọ̀nà jùlọ. Ṣùgbọ́n Allāhu fi wọ́n gégùn-ún nítorí àìgbàgbọ́ wọn. Nítorí náà, wọn kò níí gbàgbọ́ àfi díẹ̀ (nínú wọn). ');
INSERT INTO "aykyuni" VALUES(4,47,'4:47','Ẹ̀yin tí A fún ní tírà, ẹ gbàgbọ́ nínú ohun tí A sọ̀kalẹ̀, tí ó ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó wà pẹ̀lú yín, ṣíwájú kí Á tó fọ́ àwọn ojú kan, A sì máa dá a padà sí (ìpàkọ́) lẹ́yìn wọn, tàbí kí Á ṣẹ́bi lé wọn gẹ́gẹ́ bí A ṣe ṣẹ́bi lé ìjọ Sabt. Àti pé àṣẹ Allāhu máa ṣẹ.');
INSERT INTO "aykyuni" VALUES(4,48,'4:48','Dájúdájú Allāhu kò níí foríjin (ẹni tí) ó bá ń ṣẹbọ sí I. Ó sì máa ṣàforíjìn fún ohun mìíràn yàtọ̀ sí ìyẹn fún ẹni tí Ó bá fẹ́.Ẹni tí ó bá ń ṣẹbọ sí Allāhu, dájúdájú ó ti dá àdápa irọ́ (tí ó jẹ́) ẹ̀ṣẹ̀ ńlá. ');
INSERT INTO "aykyuni" VALUES(4,49,'4:49','Ṣé ìwọ kò wòye sí àwọn tó ń ṣàfọ̀mọ́ ara wọn ni? Kò sì rí bẹ́ẹ̀, Allāhu l’Ó ń ṣàfọ̀mọ́ ẹni tí Ó bá fẹ́. Wọn kò sì níí fi (ohun tó tó) ìbójú kóró èso dàbínù ṣàbòsí sí wọn.1 ');
INSERT INTO "aykyuni" VALUES(4,50,'4:50','Wo bí wọ́n ṣe ń dá àdápa irọ́ mọ́ Allāhu. Ó sì tó ní ẹ̀ṣẹ̀ pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(4,51,'4:51','Ṣé ìwọ kò wòye sí àwọn tí A fún ní ìpín kan nínú tírà, tí wọ́n ń gbàgbọ́ nínú idán àti òrìṣà, wọ́n sì ń wí fún àwọn aláìgbàgbọ́ pé: “Àwọn (ọ̀ṣẹbọ) wọ̀nyí mọ̀nà ju àwọn onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(4,52,'4:52','Àwọn wọ̀nyẹn ni àwọn tí Allāhu ṣẹ́bi lé. Ẹnikẹ́ni tí Allāhu bá sì ṣẹ́bi lé, o ò níí rí alárànṣe kan fún un.');
INSERT INTO "aykyuni" VALUES(4,53,'4:53','Tàbí ìpín kan ń bẹ fún wọn nínú ìjọba (Wa) ni? Tí ó bá rí bẹ́ẹ̀ wọn kò níí fún àwọn ẹ̀nìyàn ní àmì ẹ̀yìn kóró èso dàbínú.');
INSERT INTO "aykyuni" VALUES(4,54,'4:54','Tàbí wọ́n ń ṣe ìlara àwọn ènìyàn lórí ohun tí Allāhu fún wọn nínú oore àjùlọ Rẹ̀ ni? Dájúdájú A fún àwọn ẹbí (Ànábì) ’Ibrọ̄hīm ní Tírà àti òye ìjìnlẹ̀ (sunnah). A sì fún wọn ní ìjọba ńlá.');
INSERT INTO "aykyuni" VALUES(4,55,'4:55','Nítorí náà, ó ń bẹ nínú wọn ẹni tó gbàgbọ́ nínú rẹ̀. Ó sì ń bẹ nínú wọn ẹni tó ṣẹ́rí kúrò níbẹ̀. Jahanamọ sì tó ní iná tó ń jo.');
INSERT INTO "aykyuni" VALUES(4,56,'4:56','Dájúdájú àwọn tó ṣàì gbàgbọ́ nínú àwọn āyah Wa, láìpẹ́ A máa mú wọn wọ inú Iná. Ìgbàkígbà tí awọ ara wọn bá jóná, A óò máa pààrọ̀ awọ ara mìíràn fún wọn kí wọ́n lè máa tọ́ ìyà wò lọ. Dájúdájú Allāhu ń jẹ́ Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(4,57,'4:57','Àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, A óò mú wọn wọ inú àwọn Ọgbà Ìdẹ̀ra kan, tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀ títí láéláé. Àwọn ìyàwó mímọ́ ń bẹ fún wọn nínú rẹ̀. A sì máa fi wọ́n sí abẹ́ ibòji tó máa ṣíji bò wọ́n dáradára.');
INSERT INTO "aykyuni" VALUES(4,58,'4:58','Dájúdájú Allāhu ń pa yín ní àṣẹ pé kí ẹ dá àgbàfipamọ́ padà fún àwọn olówó wọn. Àti pé nígbà tí ẹ bá ń dájọ́ láààrin àwọn ènìyàn, ẹ dájọ́ pẹ̀lú déédé. Dájúdájú Allāhu ń fi n̄ǹkan tó dára ṣe wáàsí fún yín. Dájúdájú Allāhu ń jẹ́ Olùgbọ́, Olùríran.');
INSERT INTO "aykyuni" VALUES(4,59,'4:59','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ tẹ̀lé (àṣẹ) Allāhu, ẹ tẹ̀lé (àṣẹ) Òjíṣẹ́ náà àti àwọn aláṣẹ nínú yín. Nítorí náà, tí ẹ bá yapa-ẹnu lórí kiní kan, ẹ ṣẹ́rí rẹ̀ sí ọ̀dọ̀ Allāhu àti Òjíṣẹ́ - kí ìkẹ́ àti ọlà Allāhu máa bá a -,tí ẹ bá gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn. Ìyẹn lóore jùlọ, ó sì dára jùlọ fún ìkángun ọ̀rọ̀ (ìyọrísí). ');
INSERT INTO "aykyuni" VALUES(4,60,'4:60','Ṣé ìwọ kò wòye sí àwọn tó ń sọ̀rọ̀ (tí kò sì rí bẹ́ẹ̀) pé dájúdájú àwọn gbàgbọ́ nínú ohun tí A sọ̀kalẹ̀ fún ọ àti ohun tí A sọ̀kalẹ̀ ṣíwájú rẹ, tí wọ́n sì ń gbèrò láti gbé ẹjọ́ lọ bá òrìṣà, A sì ti pa wọ́n láṣẹ pé kí wọ́n lòdì sí i. Èṣù sì fẹ́ ṣì wọ́n lọ́nà ní ìṣìnà tó jìnnà.');
INSERT INTO "aykyuni" VALUES(4,61,'4:61','Nígbà tí wọ́n bá sọ fún wọn pé: “Ẹ wá síbi ohun tí Allāhu sọ̀kalẹ̀, ẹ wá bá Òjíṣẹ́.”, o máa rí àwọn mùnááfìkí tí wọn yóò máa ṣẹ́rí kúrò lọ́dọ̀ rẹ tààrà.');
INSERT INTO "aykyuni" VALUES(4,62,'4:62','Báwo ni ó ṣe jẹ́ pé nígbà tí àdánwò kan bá kàn wọ́n nípasẹ̀ ohun tí ọwọ́ wọ́n tì síwájú, lẹ́yìn náà wọ́n máa wá bá ọ, wọ́n yó sì máa fi Allāhu búra pé kò sí ohun tí a gbàlérò bí kò ṣe dáadáa àti ìrẹ́pọ̀.');
INSERT INTO "aykyuni" VALUES(4,63,'4:63','Àwọn wọ̀nyẹn ni àwọn tí Allāhu mọ ohun tí ń bẹ nínú ọkàn wọn. Nítorí náà, ṣẹ́rí kúrò lọ́dọ̀ wọn. Ṣe wáàsí fún wọn, kí o sì bá wọn sọ ọ̀rọ̀ tó máa mì wọ́n lọ́kàn nípa ọ̀rọ̀ ara wọn.');
INSERT INTO "aykyuni" VALUES(4,64,'4:64','A kò rán Òjíṣẹ́ kan níṣẹ́ àfi nítorí kí wọ́n lè tẹ̀lé e pẹ̀lú ìyọ̀ǹda Allāhu. Tí ó bá jẹ́ pé nígbà tí wọ́n ṣàbòsí sí ẹ̀mí ara wọn, wọ́n wá bá ọ,wọ́n sì tọrọ àforíjìn Allāhu, tí Òjíṣẹ́ sì tún bá wọn tọrọ àforíjìn, wọn ìbá kúkú rí Allāhu ní Olùgba-ìronúpìwàdà, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(4,65,'4:65','Rárá, Èmi fi Olúwa rẹ búra pé wọn kò tí ì gbàgbọ́ ní òdodo títí wọn yóò fi gbà ọ́ ní olùdájọ́ lórí ohun tí ó bá dá yánpọnyánrin sílẹ̀ láààrin wọn. Lẹ́yìn náà, wọn kò níí ní ẹ̀hónú kan sí ìdájọ́ tí o bá dá. Wọ́n sì máa gbà pátápátá.');
INSERT INTO "aykyuni" VALUES(4,66,'4:66','Tí ó bá jẹ́ pé A ṣe é ní ọ̀ran- anyàn fún wọn pé: "Ẹ pa ara yín tàbí ẹ jáde kúrò nínú ìlú yín," wọn kò níí ṣe é àfi díẹ̀ nínú wọn. Tí ó bá tún jẹ́ pé wọ́n ṣe ohun tí A fi ń ṣe wáàsí fún wọn ni, ìbá jẹ́ oore àti ìdúróṣinṣin tó lágbára jùlọ fún wọn.');
INSERT INTO "aykyuni" VALUES(4,67,'4:67','Nígbà náà, dájúdájú Àwa ìbá fún wọn ní ẹ̀san ńlá láti ọ̀dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(4,68,'4:68','Àti pé dájúdájú Àwa ìbá tọ́ wọn sí ọ̀nà tààrà.');
INSERT INTO "aykyuni" VALUES(4,69,'4:69','Ẹnikẹ́ni tí ó bá tẹ̀lé (àṣẹ) Allāhu àti (àṣẹ) Òjíṣẹ́ náà, àwọn wọ̀nyẹn máa wà (nínú Ọgbà Ìdẹ̀ra) pẹ̀lú àwọn tí Allāhu ṣèdẹ̀ra fún nínú àwọn Ànábì, àwọn olódodo, àwọn tó kú ikú ṣẹhīdiàti àwọn ẹni rere. Àwọn wọ̀nyẹn sì dára ní alábàárìn. ');
INSERT INTO "aykyuni" VALUES(4,70,'4:70','Oore àjùlọ yẹn máa wá láti ọ̀dọ̀ Allāhu; Allāhu sì tó ní Onímọ̀.');
INSERT INTO "aykyuni" VALUES(4,71,'4:71','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ mú ohun ìṣọ́ra yín lọ́wọ́, kí ẹ sì tú jáde s’ójú ogun ẹ̀sìn níkọ̀níkọ̀ tàbí kí ẹ tú jáde ní àpapọ̀.1 ');
INSERT INTO "aykyuni" VALUES(4,72,'4:72','Dájúdájú ó ń bẹ nínú yín ẹni tó ń fà sẹ́yìn. Tí àdánwó kan bá kàn yín, ó máa wí pé: “Allāhu kúkú ti ṣe ìdẹ̀ra fún mi nítorí pé èmi kò sí níbẹ̀ pẹ̀lú wọn (wọn kò sì rí mi pa pẹ̀lú àwọn tó kú ikú ṣẹhīdi).”');
INSERT INTO "aykyuni" VALUES(4,73,'4:73','Dájúdájú tí oore àjùlọ kan láti ọ̀dọ̀ Allāhu bá sì tẹ̀ yín lọ́wọ́, dájúdájú ó máa sọ̀rọ̀ - bí ẹni pé kò sí ìfẹ́ láààrin ẹ̀yin àti òun (tẹ́lẹ̀tẹ́lẹ̀) - pé: “Yéè! Èmi ìbá wà pẹ̀lú wọn, èmi ìbá jẹ èrè ńlá.”');
INSERT INTO "aykyuni" VALUES(4,74,'4:74','Nítorí náà, kí àwọn tó ń fi ayé ra ọ̀run máa jagun fún ẹ̀sìn Allāhu. Ẹnikẹ́ni tí ó bá sì jagun nítorí ẹ̀sìn Allāhu, yálà wọ́n pa á tàbí ó ṣẹ́gun, láìpẹ́ A máa fún un ní ẹ̀san ńlá.');
INSERT INTO "aykyuni" VALUES(4,75,'4:75','Kí l’ó ṣe yín tí ẹ ò níí jagun fún (ààbò) ẹ̀sìn Allāhu àti (fún ààbò) àwọn aláìlágbára nínú àwọn ọkùnrin, àwọn obìnrin àti àwọn ọmọdé, àwọn tó ń sọ pé: “Olúwa wa, mú wa jáde kúrò nínú ìlú yìí, ìlú àwọn alábòsí. Fún wa ní aláàbò kan láti ọ̀dọ̀ Rẹ́. Kí Ó sì fún wa ní alárànṣe kan láti ọ̀dọ̀ Rẹ.”');
INSERT INTO "aykyuni" VALUES(4,76,'4:76','Àwọn tó gbàgbọ́ ní òdodo ń jagun fún (ààbò) ẹ̀sìn Allāhu. Àwọn tó ṣàì gbàgbọ́ sì ń jagun fún ẹ̀sìn òrìṣà. Nítorí náà, ẹ ja àwọn ọ̀rẹ́ Èṣù lógun. Dájúdájú ète Èṣù, ó jẹ́ ohun tí ó lẹ.');
INSERT INTO "aykyuni" VALUES(4,77,'4:77','Ṣé ìwọ kò wòye sí àwọn tí A sọ fún pé: “Ẹ káwọ́ ró (ẹ má ì jagun ẹ̀sìn, àmọ́) ẹ máa kírun, kí ẹ sì máa yọ Zakāh.” Ṣùgbọ́n nígbà tí A ṣe ogun ẹ̀sìn ní ọ̀ran-anyàn lé wọn lórí, ìgbà náà ni apá kan nínú wọn ń bẹ̀rù àwọn ènìyàn bí ẹni tí ń bẹ̀rù Allāhu tàbí tí ìbẹ̀rù rẹ̀ le koko jùlọ. Wọ́n sì wí pé: “Olúwa wa, nítorí kí ni O fi ṣe ogun ẹ̀sìn ní ọ̀ran-anyàn lé wa lórí? Kúkú lọ́ wa lára di ìgbà díẹ̀ sí i. Sọ pé: “Bín-íntín ní ìgbádùn ayé, ọ̀run lóore jùlọ fún ẹni tó bá bẹ̀rù Allāhu. Wọn kò sì níí fi (ohun tó tó) ìbójú kóró èso dàbínù ṣàbòsí sí yín.”2 ');
INSERT INTO "aykyuni" VALUES(4,78,'4:78','Ibikíbi tí ẹ bá wà, ikú yóò pàdé yín, ẹ̀yin ìbáà wà nínú odi ilé orí òkè ilé gíga fíofío. Tí oore (ìkógun) kan bá tẹ̀ wọ́n lọ́wọ́, wọ́n á wí pé: “Èyí wá láti ọ̀dọ̀ Allāhu.” Tí aburú (ìfọ́gun) kan bá sì ṣẹlẹ̀ sí wọn, wọ́n á wí pé: “Èyí wá láti ọ̀dọ̀ rẹ.” Sọ pé: “Gbogbo rẹ̀ wá láti ọ̀dọ̀ Allāhu.” Kí ló ń ṣe àwọn ènìyàn wọ̀nyí ná, tí wọn kò fẹ́ẹ̀ gbọ́ àgbọ́yé ọ̀rọ̀ kan.');
INSERT INTO "aykyuni" VALUES(4,79,'4:79','Ohunkóhun tó bá tẹ̀ ọ́ lọ́wọ́ nínú oore, láti ọ̀dọ̀ Allāhu ni. Ohunkóhun tí ó bá sì ṣẹlẹ̀ sí ọ nínú aburú, láti ọ̀dọ̀ ara rẹ ni. A rán ọ níṣẹ́ pé kí o jẹ́ Òjíṣẹ́ fún àwọn ènìyàn. Allāhu sì tó ní Ẹlẹ́rìí.1 ');
INSERT INTO "aykyuni" VALUES(4,80,'4:80','Ẹnikẹ́ni tó bá tẹ̀lé Òjíṣẹ́ náà, ó ti tẹ̀lé (àṣẹ) Allāhu. Ẹnikẹ́ni tó bá sì pẹ̀yìndà, A kò rán ọ níṣẹ́ olùṣọ́ lórí wọn.');
INSERT INTO "aykyuni" VALUES(4,81,'4:81','Wọ́n ń wí pé: “A tẹ̀lé àṣẹ rẹ.” Ṣùgbọ́n nígbà tí wọ́n bá jáde kúrò lọ́dọ̀ rẹ, igun kan nínú wọn máa pètepèrò ní òru n̄ǹkan mìíràn tí ó yàtọ̀ sí èyí tí ó ń sọ (ní ọ̀sán). Allāhu sì ń ṣe àkọsílẹ̀ ohun tí wọ́n ń pètepèrò rẹ̀ ní òru. Nítorí náà, ṣẹ́rí kúrò lọ́dọ̀ wọn, kí o sì gbáralé Allāhu. Allāhu sì tó ní Aláàbò.');
INSERT INTO "aykyuni" VALUES(4,82,'4:82','Ṣé wọn kò ronú nípa al-Ƙur’ān ni? Tí ó bá jẹ́ pé ó wá láti ọ̀dọ̀ ẹlòmíìràn yàtọ̀ sí Allāhu, wọn ìbá rí ọ̀pọ̀lọ́pọ̀ ìtakora nínú rẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(4,83,'4:83','Nígbà tí ọ̀rọ̀ ìfàyàbalẹ̀ tàbí ìpáyà kan bá dé bá wọn, wọ́n sì máa tàn án kálẹ̀. Tí ó bá jẹ́ pé wọ́n ṣẹ́rí rẹ̀ sí (ọ̀rọ̀) Òjíṣẹ́ àti àwọn aláṣẹ (ìyẹn, àwọn onímọ̀ ẹ̀sìn) nínú wọn, àwọn tó ń yọ òdodo jáde nínú ọ̀rọ̀ nínú wọn ìbá mọ̀ ọ́n. Tí kì í bá ṣe oore àjùlọ Allāhu àti àánú Rẹ̀ lórí yín ni, ẹ̀yin ìbá tẹ̀lé Èṣù àfi ìba díẹ̀ (nínú yín).1 ');
INSERT INTO "aykyuni" VALUES(4,84,'4:84','Nítorí náà, jagun fún ààbò ẹ̀sìn Allāhu. Wọn kò là á bọ ẹnì kan lọ́rùn àfi ìwọ. Kí o sì gbẹ àwọn onígbàgbọ́ òdodo lóǹgbẹ ogun ẹ̀sìn. Ó ṣeé ṣe kí Allāhu ká ọwọ́ ìjà àwọn tó ṣàì gbàgbọ́ ró. Àti pé Allāhu le jùlọ ní ogun. Ó sì le jùlọ níbi ìjẹni-níyà.');
INSERT INTO "aykyuni" VALUES(4,85,'4:85','Ẹnikẹ́ni tí ó bá ṣìpẹ̀ ìṣìpẹ̀ rere, ó máa ní kan nínú rẹ̀. Ẹnikẹ́ni tí ó bá sì ṣìpẹ̀ ìṣìpẹ̀ aburú, ó máa ní ìpín kan nínú rẹ̀. Allāhu sì ń jẹ́ Alágbára lórí gbogbo n̄ǹkan ');
INSERT INTO "aykyuni" VALUES(4,86,'4:86','Nígbà tí wọ́n bá ki yín ní kíkí kan, ẹ kí wọn (padà) pẹ̀lú èyí tó dára jù ú lọ tàbí kí ẹ dá a padà (pẹ̀lú bí wọ́n ṣe ki yín). Dájúdájú Allāhu ń jẹ́ Olùṣírò lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(4,87,'4:87','Allāhu, kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Dájúdájú Ó máa ko yín jọ ní Ọjọ́ Àjíǹde, kò sí iyèméjì nínú rẹ̀. Ta ló sọ̀dodo ọ̀rọ̀ ju Allāhu?');
INSERT INTO "aykyuni" VALUES(4,88,'4:88','Kí ni ó máa mu yín pín sí ìjọ méjì nípa àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí! Ṣebí Allāhu l’Ó dá wọn padà sẹ́yìnnítorí ohun tí wọ́n ṣe níṣẹ́. Ṣé ẹ fẹ́ fi ẹni tí Allāhu ṣì lọ́nà mọ̀nà ni? Ẹnikẹ́ni tí Allāhu bá ṣì lọ́nà, o ò níí rí ọ̀nà kan fún un. ');
INSERT INTO "aykyuni" VALUES(4,89,'4:89','Wọ́n fẹ́ kí ẹ ṣàì gbàgbọ́ gẹ́gẹ́ bí wọ́n ṣe ṣàì gbàgbọ́, kí ẹ lè jọ di ẹgbẹ́ kan náà. Nítorí náà, ẹ má ṣe mú ọ̀rẹ́ àyò nínú wọn títí wọ́n fi máa kúrò nínú ìlú ẹbọ wá sí ìlú ’Islām nítorí ẹ̀sìn Allāhu. Tí wọ́n bá kẹ̀yìn (sí hijrah náà), ẹ mú wọn, kí ẹ sì pa wọ́n níbikíbi tí ẹ bá ti bá wọn. Kí ẹ sì má ṣe mú ọ̀rẹ́ àyò àti olùrànlọ́wọ́ kan nínú wọn.');
INSERT INTO "aykyuni" VALUES(4,90,'4:90','Àyàfi àwọn tó bá darapọ̀ mọ́ ìjọ kan tí àdéhùn ń bẹ láààrin ẹ̀yin àti àwọn. Tàbí wọ́n wá ba yín, tí ọkàn wọn ti pami láti ba yín jà tàbí láti bá àwọn ènìyàn wọn jà. Àti pé tí ó bá jẹ́ pé Allāhu bá fẹ́, ìbá fún wọn lágbára (ọkàn-akin) lórí yín, wọn ìbá sì jà yín lógun. Nítorí náà, tí wọ́n bá yẹra fún yín, tí wọn kò sì jà yín lógun, tí wọ́n sì juwọ́ sílẹ̀ fún yín, nígbà náà Allāhu kò fún yín ní ọ̀nà lórí wọn (láti jà wọ́n lógun).');
INSERT INTO "aykyuni" VALUES(4,91,'4:91','Ẹ máa rí àwọn ẹlòmíìràn tí wọ́n ń fẹ́ ààbò lọ́dọ̀ yín (wọ́n bá ní àwọn gba ’Islām), wọ́n tún ń fẹ́ ààbò lọ́dọ̀ àwọn ènìyàn wọn (wọ́n tún ń bá wọn ṣẹbọ), ìgbàkígbà tí wọ́n bá dá wọn padà sínú ìfòòró (ìbọ̀rìṣà), wọ́n sì ń padà sínú rẹ̀. Tí wọn kò bá yẹra fún yín, tí wọn kò juwọ́ sílẹ̀ fún yín, tí wọn kò sì dáwọ́ jíjà yín lógun dúró, nígbà náà ẹ mú wọn, kí ẹ sì pa wọ́n níbikíbi tí ọwọ́ yín bá ti bà wọ́n. Àwọn wọ̀nyẹn ni Allāhu sì fún yín ní agbára tó yanjú lórí wọn (láti jà wọ́n lógun).');
INSERT INTO "aykyuni" VALUES(4,92,'4:92','Kò lẹ́tọ̀ọ́ fún onígbàgbọ́ òdodo kan láti pa onígbàgbọ́ òdodo kan àyàfi tí ó bá ṣèèsì. Ẹni tí ó bá sì ṣèèsì pa onígbàgbọ́ òdodo kan, ó máa tú ẹrú lónígbàgbọ́ òdodo kan sílẹ̀ lóko ẹrú. Ó tún máa san owó ẹ̀mí fún àwọn ènìyàn òkú àfi tí wọ́n bá fi tọrẹ (fún un, tí wọ́n bá yọ̀ǹda rẹ̀ fún un). Tí ó bá sì wà nínú àwọn ènìyàn kan tí ó jẹ́ ọ̀tá fún yín, onígbàgbọ́ sì ni (ẹni tí wọ́n ṣèèsì pa), ó máa tú ẹrú lónígbàgbọ́ òdodo kan sílẹ̀ lóko ẹrú. Tí ó bá jẹ́ ìjọ tí àdéhùn ń bẹ láààrin ẹ̀yin àti àwọn (ni ẹni tí wọ́n ṣèèsì pa), ó máa san owó ẹ̀mí fún àwọn ènìyàn rẹ̀. Ó tún máa tú ẹrú lónígbàgbọ́ òdodo kan sílẹ̀ lóko ẹrú. Ẹni tí kò bá rí (ẹrú lónígbàgbọ́ òdodo), ó máa gba ààwẹ̀ oṣù méjì ní tẹ̀léǹtẹ̀lé. (Ọ̀nà) ìronúpìwàdà kan láti ọ̀dọ̀ Allāhu (nìyí). Allāhu ń jẹ́ Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(4,93,'4:93','Ẹnikẹ́ni tí ó bá mọ̀ọ́mọ̀ pa onígbàgbọ́ òdodo kan, iná Jahanamọ ni ẹ̀san rẹ̀. Olùṣegbére ni nínú rẹ̀. Allāhu yóò bínú sí i. Ó máa ṣẹ́bilé e. Ó sì ti pèsè ìyà ńlá sílẹ̀ dè é.');
INSERT INTO "aykyuni" VALUES(4,94,'4:94','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nígbà tí ẹ bá wà lórí ìrìn-àjò ogun ẹ̀sìn Allāhu, ẹ ṣe pẹ̀lẹ́pẹ̀lẹ́ kí ẹ fi mọ òdodo (nípa àwọn ènìyàn). Ẹ sì má ṣe sọ fún ẹni tí ó bá sálámọ̀ si yín pé kì í ṣe onígbàgbọ́ òdodo nítorí pé ẹ̀yin ń wá dúkìá ìṣẹ̀mí ayé. Ní ọ̀dọ̀ Allāhu kúkú ni ọ̀pọ̀lọ́pọ̀ ọrọ̀ ogun wà. Báyẹn ni ẹ̀yin náà ṣe wà tẹ́lẹ̀, Allāhu sì ṣe ìdẹ̀ra (ẹ̀sìn) fún yín. Nítorí náà, ẹ ṣe pẹ̀lẹ́pẹ̀lẹ́ kí ẹ fi mọ òdodo (nípa àwọn ènìyàn náà). Dájúdájú Allāhu ń jẹ́ Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(4,95,'4:95','Àwọn olùjókòó sínú ilé nínú àwọn onígbàgbọ́ òdodo, yàtọ̀ sí àwọn aláìlera, àti àwọn olùjagun fún ẹ̀sìn Allāhu pẹ̀lú dúkìá wọn àti ẹ̀mí wọn, wọn kò dọ́gba. Allāhu fi ipò kan ṣoore àjùlọ fún àwọn olùjagun fún ẹ̀sìn Allāhu pẹ̀lú dúkìá wọn àti ẹ̀mí wọn lórí àwọn olùjókòó sínú ilé. Olúkùlùkù (wọn) ni Allāhu ṣe àdéhùn ohun rere (Ọgbà Ìdẹ̀ra) fún. Allāhu gbọ́lá fún àwọn olùjagun ẹ̀sìn lórí àwọn olùjókòó sínú ilé pẹ̀lú ẹ̀san ńlá;');
INSERT INTO "aykyuni" VALUES(4,96,'4:96','àwọn ipò kan àti àforíjìn àti àánú láti ọ̀dọ̀ Rẹ̀. Allāhu sì ń jẹ́ Aláforíjìn Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(4,97,'4:97','Dájúdájú àwọn tí mọlāika gba ẹ̀mí wọn, (lásìkò) tí wọ́n ń ṣàbòsí sí ẹ̀mí ara wọn, (àwọn mọlāika) sọ pé: “Kí ni ẹ̀ ń ṣe (nínú ẹ̀sìn)? Wọ́n wí pé: “Wọ́n dá wa lágara lórí ilẹ̀ ni.” (Àwọn mọlāika) sọ pé: “Ṣé ilẹ̀ Allāhu kò fẹ̀ tó (fún yín) láti gbé ẹ̀sìn yín sá lórí ilẹ̀?” Àwọn wọ̀nyẹn, iná Jahanamọ ni ibùgbé wọn. Ó sì burú ní ìkángun. ');
INSERT INTO "aykyuni" VALUES(4,98,'4:98','Àyàfi àwọn aláìlágbára nínú àwọn ọkùnrin, àwọn obìnrin àti àwọn ọmọdé tí kò ní ìkápá ọgbọ́n kan, wọn kò sì dá ọ̀nà mọ̀ (dé ìlú Mọdīnah).');
INSERT INTO "aykyuni" VALUES(4,99,'4:99','Nítorí náà, àwọn wọ̀nyẹn, ó súnmọ́ kí Allāhu ṣàmójúkúrò fún wọn. Allāhu sì ń jẹ́ Alámòjúúkúrò, Aláforíjìn.');
INSERT INTO "aykyuni" VALUES(4,100,'4:100','Ẹnikẹ́ni tí ó bá gbé ìlú rẹ̀ jù sílẹ̀ nítorí ẹ̀sìn Allāhu, ó máa rí ọ̀pọ̀lọ́pọ̀ ibùsásí àti ìgbàláyè lórí ilẹ̀. Ẹnikẹ́ni tí ó bá jáde kúrò nínú ilé rẹ̀ (tí ó jẹ́) olùfìlú-sílẹ̀ nítorí ẹ̀sìn Allāhu àti Òjíṣẹ́ Rẹ̀, lẹ́yìn náà tí ikú bá a (lójú ọ̀nà), ẹ̀san rẹ̀ kúkú ti dúró lọ́dọ̀ Allāhu. Allāhu sì ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run.1 ');
INSERT INTO "aykyuni" VALUES(4,101,'4:101','Nígbà tí ẹ bá ṣe ìrìn-àjò lórí ilẹ̀, kò sí ẹ̀ṣẹ̀ fún yín láti dín ìrun kù,tí ẹ̀yin bá ń bẹ̀rù pé àwọn tó ṣàì gbàgbọ́ yóò fòòró yín. Dájúdájú àwọn aláìgbàgbọ́, wọ́n jẹ́ ọ̀tá pọ́nńbélé fún yín. ');
INSERT INTO "aykyuni" VALUES(4,102,'4:102','Nígbà tí o bá wà láààrin wọn, gbé ìrun dúró fún wọn. Kí igun kan nínú wọn kírun pẹ̀lú rẹ, kí wọ́n mú n̄ǹkan ìjagun wọn lọ́wọ́. Nígbà tí wọ́n bá sì forí kanlẹ̀ (tí wọ́n parí ìrun), kí wọn bọ́ sẹ́yìn yín, kí igun mìíràn tí kò tí ì kírun wá kírun pẹ̀lú rẹ. Kí wọ́n mú ìṣọ́ra wọnàti n̄ǹkan ìjagun wọn lọ́wọ́.2 Àwọn tó ṣàì gbàgbọ́ fẹ́ kí ẹ gbàgbé àwọn n̄ǹkan ìjagun yín àti n̄ǹkan ìgbádùn yín (ẹrù oúnjẹ yín), kí wọ́n lè kọlù yín ní ẹ̀ẹ̀ kan náà. Kò sí ẹ̀ṣẹ̀ fún yín tí ó bá jẹ́ pé ìpalára kan ń bẹ fún yín látara òjò tàbí (pé) ẹ jẹ́ aláìsàn, pé kí ẹ fi n̄ǹkan ìjagun yín sílẹ̀ (lórí ìrun). Ẹ mú n̄ǹkan ìṣọ́ra yín lọ́wọ́. Dájúdájú Allāhu ti pèsè ìyà tí í yẹpẹrẹ ẹ̀dá sílẹ̀ de àwọn aláìgbàgbọ́. ');
INSERT INTO "aykyuni" VALUES(4,103,'4:103','Nígbà tí ẹ bá parí ìrun, kí ẹ ṣe (gbólóhùn) ìrántí Allāhu ní ìdúró, ìjókòó àti ìdùbúlẹ̀ yín. Nígbà tí ẹ bá sì fọkàn balẹ̀, kí ẹ kírun (ní pípé). Dájúdájú ìrun kíkí jẹ́ ọ̀ran-anyàn tí A fi àkókò sí fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(4,104,'4:104','Ẹ má ṣe káàárẹ̀ nípa wíwá àwọn ènìyàn náà (láti jà wọ́n lógun). Tí ẹ̀yin bá ń jẹ ìrora (ọgbẹ́), dájúdájú àwọn náà ń jẹ ìrora (ọgbẹ́) gẹ́gẹ́ bí ẹ̀yin náà ṣe ń jẹ ìrora rẹ̀. Ẹ̀yin sì ń retí ohun tí àwọn kò retí lọ́dọ̀ Allāhu. Allāhu sì ń jẹ́ Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(4,105,'4:105','Dájúdájú Àwa sọ Tírà (al-Ƙur’ān) kalẹ̀ fún ọ pẹ̀lú òdodo nítorí kí o lè máa ṣèdájọ́ láààrin àwọn ènìyàn pẹ̀lú ohun tí Allāhu fi hàn ọ́. Má ṣe jẹ́ olùgbèjà fún àwọn oníjàǹbá.1 ');
INSERT INTO "aykyuni" VALUES(4,106,'4:106','Tọrọ àforíjìn lọ́dọ̀ Allāhu,dájúdájú Allāhu, Ó ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(4,107,'4:107','Má ṣe bá àwọn tó ń jàǹbá ẹ̀mí ara wọn wá àwíjàre. Dájúdájú Allāhu kò fẹ́ràn ẹnikẹ́ni tí ó jẹ́ oníjàǹbá, ẹlẹ́ṣẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(4,108,'4:108','Wọ́n ń fi ara pamọ́ fún àwọn ènìyàn, wọn kò sì lè fara pamọ́ fún Allāhu (nítorí pé) Ó wà pẹ̀lú wọn (pẹ̀lú ìmọ̀ Rẹ̀) nígbà tí wọ́n ń pètepèrò lórí ohun tí (Allāhu) kò yọ́nú sí nínú ọ̀rọ̀ sísọ.Allāhu sì ń jẹ́ Alámọ̀tán nípa ohun tí wọ́n ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(4,109,'4:109','Kíyè sí i, ẹ̀yin wọ̀nyí ni ẹ̀ ń bá wọn mú àwíjàre wá nílé ayé. Ta ni ó máa bá wọn mú àwíjàre wá ní ọ̀dọ̀ Allāhu ní Ọjọ́ Àjíǹde? Tàbí ta ni ó máa jẹ́ aláàbò fún wọn?');
INSERT INTO "aykyuni" VALUES(4,110,'4:110','Ẹnikẹ́ni tí ó bá ṣiṣẹ́ aburú kan tàbí tí ó ṣàbòsí sí ẹ̀mí ara rẹ̀, lẹ́yìn náà tí ó tọrọ àforíjìn lọ́dọ̀ Allāhu, ó máa bá Allāhu ní Aláforíjìn, Aláàánú.');
INSERT INTO "aykyuni" VALUES(4,111,'4:111','Ẹnikẹ́ni tí ó bá dá ẹ̀ṣẹ̀ kan, ó dá a fún ẹ̀mí ara rẹ̀. Allāhu sì ń jẹ́ Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(4,112,'4:112','Ẹnikẹ́ni tí ó bá ṣe àṣìṣe kan tàbí ẹ̀ṣẹ̀ kan, lẹ́yìn náà, ó dì í ru aláìmọwọ́-mẹsẹ̀, ó ti dá ọ̀ràn ìparọ́mọ́ni àti ẹ̀ṣẹ̀ pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(4,113,'4:113','Tí kì í bá ṣe ti oore àjùlọ Allāhu àti àánú Rẹ̀ tí ń bẹ lórí rẹ ni, igun kan nínú wọn ìbá ti gbèrò láti ṣì ọ́ lọ́nà. Wọn kò sì níí ṣi ẹnì kan lọ́nà àfi ẹ̀mí ara wọn. Wọn kò sì lè fi n̄ǹkan kan kó ìnira bá ọ. Allāhu sì sọ Tírà àti òye ìjìnlẹ̀ (ìyẹn, sunnah) kalẹ̀ fún ọ. Ó tún fi ohun tí ìwọ kò mọ̀ tẹ́lẹ̀ mọ̀ ọ́;oore àjùlọ Allāhu lórí rẹ jẹ́ ohun tó tóbi. ');
INSERT INTO "aykyuni" VALUES(4,114,'4:114','Kò sí oore kan nínú ọ̀pọ̀lọ́pọ̀ ọ̀rọ̀ ìkọ̀kọ̀ wọn àfi ẹni tí ó bá pàṣẹ ọrẹ títa tàbí iṣẹ́ rere tàbí àtúnṣe láààrin àwọn ènìyàn. Ẹnikẹ́ni tí ó bá ṣe ìyẹn láti fi wá ìyọ́nú Allāhu, láìpẹ́ A máa fún un ní ẹ̀san ńlá.');
INSERT INTO "aykyuni" VALUES(4,115,'4:115','Ẹnikẹ́ni tí ó bá yapa Òjíṣẹ́ náà lẹ́yìn tí ìmọ̀nà ti fojú han sí i kedere, tí ó tún tẹ̀lé ọ̀nà tó yàtọ̀ sí ti àwọn onígbàgbọ́ òdodo, A óò dojú rẹ̀ kọ ohun tí ó dojú ara rẹ̀ kọ (nínú ìṣìnà),A sì máa mú un wọ inú iná Jahanamọ. Ó sì burú ní ìkángun.');
INSERT INTO "aykyuni" VALUES(4,116,'4:116','Dájúdájú Allāhu kò níí foríjin (ẹni tí) ó bá ń ṣẹbọ sí I. Ó sì máa ṣàforíjìn fún ẹ̀ṣẹ̀ mìíràn yàtọ̀ sí ìyẹn fún ẹni tí Ó bá fẹ́.Ẹnikẹ́ni tí ó bá ń ṣẹbọ sí Allāhu, dájúdájú ó ti ṣìnà ní ìṣìnà tó jìnnà. ');
INSERT INTO "aykyuni" VALUES(4,117,'4:117','Wọn kò pe kiní kan lẹ́yìn Allāhu bí kò ṣe àwọn abo òrìṣà.Wọn kò sì pe kiní kan bí kò ṣe Èṣù olùyapa. ');
INSERT INTO "aykyuni" VALUES(4,118,'4:118','Allāhu ṣẹ́bilé Èṣù. Ó sì wí pé: “Dájúdájú mo máa mú ìpín tí wọ́n pín fún mi nínú àwọn ẹrúsìn Rẹ.');
INSERT INTO "aykyuni" VALUES(4,119,'4:119','Dájúdájú mo máa ṣì wọ́n lọ́nà. Dájúdájú mo máa tàn wọ́n jẹ. Dájúdájú mo máa pa wọ́n láṣẹ, wọn yó sì máa gé etí ẹran-ọ̀sìn (láti yà á sọ́tọ̀ fún òrìṣà). Dájúdájú mo máa pa wọ́n láṣẹ, wọn yó sì máa yí ẹ̀sìn Allāhu padà.”Ẹnikẹ́ni tí ó bá mú Èṣù ní ọ̀rẹ́ lẹ́yìn Allāhu, dájúdájú ó ti ṣòfò ní òfò pọ́nńbélé. ');
INSERT INTO "aykyuni" VALUES(4,120,'4:120','Ó ń ṣàdéhùn fún wọn, ó sì ń tàn wọ́n jẹ. Èṣù kò sì ṣàdéhùn kan fún wọn bí kò ṣe ẹ̀tàn.');
INSERT INTO "aykyuni" VALUES(4,121,'4:121','Àwọn wọ̀nyẹn, iná Jahanamọ ni ibùgbé wọn; wọn kò níí rí ibùsásí kan (wọn kò níí rí ọ̀nà láti sá jàde kúrò nínú rẹ̀).');
INSERT INTO "aykyuni" VALUES(4,122,'4:122','Àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣiṣẹ́ rere, A máa mú wọn wọ inu àwọn Ọgbà Ìdẹ̀ra kan, tí odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọn nínú rẹ̀ títí láéláé. (Ó jẹ́) àdéhùn Allāhu ní ti òdodo. Ta sì ni ó sọ òdodo ju Allāhu?');
INSERT INTO "aykyuni" VALUES(4,123,'4:123','Kì í ṣe ìfẹ́-ọkàn yín, bẹ́ẹ̀ ni kì í ṣe ìfẹ́-ọkàn àwọn onítírà (ni òṣùwọ̀n ìdájọ́ ọ̀run. Àmọ́,) ẹnikẹ́ni tí ó bá ṣiṣẹ́ aburú kan, A óò san án ní ẹ̀san rẹ̀; kò sì níí rí aláfẹ̀yìntì tàbí alárànṣe kan lẹ́yìn Allāhu.1 ');
INSERT INTO "aykyuni" VALUES(4,124,'4:124','Ẹnikẹ́ni tí ó bá ṣiṣẹ́ rere, yálà ọkùnrin tàbí obìnrin, onígbàgbọ́ òdodo sì ni, àwọn wọ̀nyẹn máa wọ inú Ọgbà Ìdẹ̀ra. A ò sì níí fi àmì ẹ̀yìn kóró èso dàbínú ṣàbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(4,125,'4:125','Ta l’ó dára ní ẹ̀sìn ju ẹni tí ó jura rẹ̀ sílẹ̀ fún Allāhu, olùṣe-rere sì tún ni, ó tún tẹ̀lé ẹ̀sìn (Ànábì) ’Ibrọ̄hīm, olùdúró-déédé-nínú-’Islām? Allāhu sì mú (Ànábì) ’Ibrọ̄hīm ní àyànfẹ́.');
INSERT INTO "aykyuni" VALUES(4,126,'4:126','Ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Allāhu sì ń jẹ́ Alámọ̀tán nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(4,127,'4:127','Wọ́n ń bi ọ́ léèrè ìdájọ́ nípa àwọn obìnrin. Sọ pé: “Allāhu l’Ó ń sọ ìdájọ́ wọn fún yín. Ohun tí wọ́n ń ké fún yín nínú Tírà (al-Ƙur’ān náà ń sọ ìdájọ́ fún yín) nípa àwọn ọmọ-òrukàn lóbìnrin tí ẹ kì í fún ní ohun tí wọ́n kọ fún wọn (nínú ogún), tí ẹ tún ń ṣojú-kòkòrò pé ẹ fẹ́ fẹ́ wọn àti nípa àwọn aláìlágbára nínú àwọn ọmọdé (tí ẹ̀ ń jẹ ogún wọn mọ́lẹ̀.) àti nípa pé kí ẹ dúró ti àwọn ọmọ òrukàn pẹ̀lú déédé. Ohunkóhun tí ẹ bá sì ṣe ní rere, dájúdájú Allāhu ń jẹ́ Onímọ̀ nípa rẹ̀.');
INSERT INTO "aykyuni" VALUES(4,128,'4:128','Tí obìnrin kan bá páyà oríkunkun tàbí ìkẹ̀yìnsí láti ọ̀dọ̀ ọkọ rẹ̀, kò sí ẹ̀ṣẹ̀ fún àwọn méjèèjì pé kí wọ́n ṣe àtúnṣe láààrin ara wọn. Àtúnṣe sì dára júlọ. Wọ́n sì ti fi ahun àti ọ̀kánjúà ṣíṣe sínú ẹ̀mí ènìyàn. Tí ẹ bá ṣe rere, tí ẹ sì bẹ̀rù (Allāhu), dájúdájú Allāhu ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.1 ');
INSERT INTO "aykyuni" VALUES(4,129,'4:129','Ẹ kò lè ṣe déédé (nínú ìfẹ́) láààrin àwọn obìnrin, ẹ ò báà jẹ̀rankàn rẹ̀. Nítorí náà, ẹ má ṣe fì síbì kan tán pátápátá, kí ẹ wá pa (ẹnì kan) tì bí ohun tí wọ́n gbékọ́ ara ilé. Tí ẹ bá ṣàtúnṣe, tí ẹ sì bẹ̀rù (Allāhu), dájúdájú Allāhu ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(4,130,'4:130','Tí àwọn méjèèjì bá sì pínyà, Allāhu yóò rọ ìkíní kejì lọ́rọ̀ nínú ọlá Rẹ̀ tó gbòòrò. Allāhu sì ń jẹ́ Olùgbòòrò (nínú ọrọ̀), Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(4,131,'4:131','Ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Dájúdájú A ti pa á láṣẹ fún àwọn tí A fún ní tírà ṣíwájú yín àti ẹ̀yin náa pé kí ẹ bẹ̀rù Allāhu. Tí ẹ bá sì ṣàì gbàgbọ́, dájúdájú ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Allāhu sì ń jẹ́ Ọlọ́rọ̀, Ẹlẹ́yìn (tí ẹyìn tọ́ sí).');
INSERT INTO "aykyuni" VALUES(4,132,'4:132','Ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Allāhu sì tó ní Olùṣọ́.');
INSERT INTO "aykyuni" VALUES(4,133,'4:133','Tí (Allāhu) bá fẹ́, Ó máa ko yín kúrò lórí ilẹ̀, ẹ̀yin ènìyàn. Ó sì máa mú àwọn mìíràn wá. Allāhu sì ń jẹ́ Alágbára lórí ìyẹn.');
INSERT INTO "aykyuni" VALUES(4,134,'4:134','Ẹnikẹ́ni tí ó bá ń fẹ́ ẹ̀san nílé ayé, ṣebí lọ́dọ̀ Allāhu ní ẹ̀san ti ayé àti ti ọ̀run wà. Allāhu sì ń jẹ́ Olùgbọ́, Olùríran.');
INSERT INTO "aykyuni" VALUES(4,135,'4:135','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ jẹ́ olùdúró ṣinṣin lórí déédé nítorí ti Allāhu nígbà tí ẹ bá ń jẹ́rìí, kódà kí (ẹ̀rí jíjẹ́ náà) tako ẹ̀yin fúnra yín tàbí àwọn òbí méjèèjì àti àwọn ẹbí; yálà ó jẹ́ ọlọ́rọ̀ tàbí aláìní, Allāhu súnmọ́ (yín) ju àwọn méjèèjì. Nítorí náà, ẹ má ṣe tẹ̀lé ìfẹ́-inú láti má ṣe déédé. Tí ẹ bá yí ojú-ọ̀rọ̀ sódì tàbí tí ẹ bá gbúnrí kúrò (níbi déédé), dájúdájú Allāhu ń jẹ́ Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.1 ');
INSERT INTO "aykyuni" VALUES(4,136,'4:136','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ gbàgbọ́ dáadáa nínú Allāhu àti Òjíṣẹ́ Rẹ̀, àti Tírà tí (Allāhu) sọ̀kalẹ̀ fún Òjíṣẹ́ Rẹ̀, àti Tírà tí Ó sọ̀kalẹ̀ ṣíwájú. Ẹnikẹ́ni tí ó bá ṣàì gbàgbọ́ nínú Allāhu, àwọn mọlāika Rẹ̀, àwọn Tírà Rẹ̀, àwọn Òjíṣẹ́ Rẹ̀ àti Ọjọ́ Ìkẹyìn, dájúdájú ó ti ṣìnà ní ìṣìnà tó jìnnà tefétefé.');
INSERT INTO "aykyuni" VALUES(4,137,'4:137','Dájúdájú àwọn tó gbàgbọ́, lẹ́yìn náà wọ́n ṣàì gbàgbọ́, lẹ́yìn náà wọ́n gbàgbọ́, lẹ́yìn náà wọ́n ṣàì gbàgbọ́, lẹ́yìn náà wọ́n sì lékún sí i nínú àìgbàgbọ́, Allāhu kò níí forí jìn wọ́n, kò sì níí fí ọ̀nà mọ̀ wọ́n.1 ');
INSERT INTO "aykyuni" VALUES(4,138,'4:138','Fún àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí ní ìró pé dájúdájú ìyà ẹlẹ́ta eléro ń bẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(4,139,'4:139','(Àwọn ni) àwọn tó ń mú àwọn aláìgbàgbọ́ ní ọ̀rẹ́ àyò lẹ́yìn àwọn onígbàgbọ́ òdodo. Ṣé ẹ̀ ń wá agbára lọ́dọ̀ wọn ni? Dájúdájú ti Allāhu ni gbogbo agbára pátápátá.');
INSERT INTO "aykyuni" VALUES(4,140,'4:140','(Allāhu) kúkú ti sọ̀ ọ́ kalẹ̀ fún yín nínú Tírà pé nígbà tí ẹ bá gbọ́ nípa àwọn āyah Allāhu pé wọ́n ń ṣàì gbàgbọ́ nínú rẹ̀, wọ́n sì ń fi ṣe ẹ̀fẹ̀, ẹ má ṣe jókòó tì wọ́n nígbà náà títí wọn yóò fi bọ́ sínú ọ̀rọ̀ mìíràn, bí bẹ́ẹ̀ kọ́ dájúdájú bí irú wọn ni ẹ̀yin náà. Dájúdájú Allāhu yóò pa àwọn ṣòbẹ-ṣèlu mùsùlùmí pọ̀ mọ́ gbogbo àwọn aláìgbàgbọ́ nínú iná Jahanamọ.');
INSERT INTO "aykyuni" VALUES(4,141,'4:141','(Àwọn ni) àwọn tó fẹ́ yọ̀ yín; tí ìṣẹ́gun kan láti ọ̀dọ̀ Allāhu bá jẹ́ tiyín, wọ́n á wí pé: “Ṣé àwa kò wà pẹ̀lú yín bí?” Tí ìpín kan bá sì wà fún àwọn aláìgbàgbọ́, (àwọn mùnááfìkí) á wí pé: “Ṣé àwa kò ti ní ìkápá láti jẹ gàba le yín lórí (àmọ́ tí àwa kò ṣe bẹ́ẹ̀), ṣé àwa kò sì dáàbò bò yín tó lọ́wọ́ àwọn onígbàgbọ́ òdodo (títí ọwọ́ yín fi ba ọrọ̀-ogun, nítorí náà kí ni ìpín tiwa tí ẹ fẹ́ fún wa?)”Nítorí náà, Allāhu yóò ṣèdájọ́ láààrin yín ní Ọjọ́ Àjíǹde. Allāhu kò sì níí fún àwọn aláìgbàgbọ́ ní ọ̀nà (ìṣẹ́gun) kan lórí àwọn onígbàgbọ́ òdodo. ');
INSERT INTO "aykyuni" VALUES(4,142,'4:142','Dájúdájú àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí ń tan Allāhu, Òun náà sì máa tàn wọ́n. Nígbà tí wọ́n bá dúró láti kírun, wọ́n á dúró (ní ìdúró) òròjú, wọn yó sì máa ṣe ṣekárími (lórí ìrun). Wọn kò sì níí ṣe (gbólóhùn) ìrántí Allāhu (lórí ìrun) àfi díẹ̀.');
INSERT INTO "aykyuni" VALUES(4,143,'4:143','Wọ́n ń ṣe bàlàbàlà síhìn-ín sọ́hùn-ún, wọn kò jẹ́ ti àwọn wọ̀nyí, wọn kò sì jẹ́ ti àwọn wọ̀nyí. Ẹnikẹ́ni tí Allāhu bá ṣì lọ́nà, o ò sì níí rí ọ̀nà kan fún un.');
INSERT INTO "aykyuni" VALUES(4,144,'4:144','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe mú àwọn aláìgbàgbọ́ ní ọ̀rẹ́ àyò lẹ́yìn àwọn onígbàgbọ́ òdodo (ẹgbẹ́ yín). Ṣé ẹ fẹ́ ṣe n̄ǹkan tí Allāhu yóò fi ní ẹ̀rí pọ́nńbélé lọ́wọ́ láti jẹ yín níyà ni?');
INSERT INTO "aykyuni" VALUES(4,145,'4:145','Dájúdájú àwọn ṣọ̀be-ṣèlu mùsùlùmí yóò wà nínú àjà ìsàlẹ̀ pátápátá nínú Iná. O ò sì níí rí alárànṣe kan fún wọn.');
INSERT INTO "aykyuni" VALUES(4,146,'4:146','Àyàfi àwọn tó ronú pìwàdà, tí wọ́n ṣe àtúnṣe, tí wọ́n dúró ṣinṣin ti Allāhu, tí wọ́n sì ṣe àfọ̀mọ́ ẹ̀sìn wọn fún Allāhu. Nítorí náà, àwọn wọ̀nyẹn máa wà pẹ̀lú àwọn onígbàgbọ́ òdodo. Láìpẹ́ Allāhu máa fún àwọn onígbàgbọ́ òdodo ní ẹ̀san ńlá.');
INSERT INTO "aykyuni" VALUES(4,147,'4:147','Kí ni Allāhu máa fi ìyà yín ṣe, tí ẹ bá dúpẹ́, tí ẹ sì gbàgbọ́ ní òdodo? Allāhu sì ń jẹ́ Ọlọ́pẹ́,Onímọ̀. ');
INSERT INTO "aykyuni" VALUES(4,148,'4:148','Allāhu kò nífẹ̀ẹ́ sí ariwo ọ̀rọ̀ burúkú (láti ọ̀dọ̀ ẹnikẹ́ni)àyàfi ẹni tí wọ́n bá ṣe àbòsí sí. Allāhu sì ń jẹ́ Olùgbọ́, Onímọ̀. ');
INSERT INTO "aykyuni" VALUES(4,149,'4:149','Tí ẹ bá ṣàfi hàn rere tàbí ẹ fi pamọ́ tàbí ẹ ṣàmójú kúrò níbi aburú, dájúdájú Allāhu ń jẹ́ Alámòjúúkúrò, Alágbára.');
INSERT INTO "aykyuni" VALUES(4,150,'4:150','Dájúdájú àwọn tó ń ṣàì gbàgbọ́ nínú Allāhu àti àwọn Òjíṣẹ́ Rẹ̀, wọ́n sì fẹ́ máa ṣòpínyà láààrin Allāhu àti àwọn Òjíṣẹ́ Rẹ̀, wọ́n sì ń wí pé: “A gbàgbọ́ nínú apá kan, a sì ṣàì gbàgbọ́ nínú apá kan,” wọ́n sì fẹ́ mú ọ̀nà kan tọ̀ (lẹ́sìn) láààrin ìyẹn.');
INSERT INTO "aykyuni" VALUES(4,151,'4:151','Ní òdodo, àwọn wọ̀nyẹn, àwọn ni aláìgbàgbọ́. A sì ti pèsè Ìyà tí í yẹpẹrẹ ẹ̀dá sílẹ̀ de àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(4,152,'4:152','Àwọn tó gbàgbọ́ nínú Allāhu àti àwọn Òjíṣẹ́ Rẹ̀, tí wọn kò sì ya ẹnì kan sọ́tọ̀ nínú wọn, láìpẹ́ àwọn wọ̀nyẹn, (Allāhu) máa fún wọn ní ẹ̀san wọn. Allāhu sì ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(4,153,'4:153','Àwọn onítírà ń bi ọ́ léèrè pé kí o sọ tírà kan kalẹ̀ láti inú sánmọ̀. Wọ́n kúkú bi (Ànábì) Mūsā ní ohun tí ó tóbi ju ìyẹn lọ. Wọ́n wí pé: “Fi Allāhu hàn wá ní ojúkojú.” Nítorí náà, iná láti ojú sánmọ̀ gbá wọn mú nítorí àbòsí ọwọ́ wọn. Lẹ́yìn náà, wọ́n tún sọ ọ̀bọrọgidi ọmọ màálù di n̄ǹkan tí wọ́n jọ́sìn fún lẹ́yìn tí àwọn ẹ̀rí tó yanjú ti dé bá wọn. A tún ṣàmójú kúrò níbi ìyẹn. A sì fún (Ànábì) Mūsā ní ẹ̀rí pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(4,154,'4:154','A gbé àpáta sókè orí wọn nítorí májẹ̀mu wọn. A sì sọ fún wọn pé: “Ẹ gba ẹnu-ọ̀nà ìlú wọlé ní olùdáwọ́tẹ-orúnkún.” A tún sọ fún wọn pé: “Ẹ má ṣe tayọ ẹnu-ààlà ní ọjọ́ Sabt.” A sì gba àdéhùn tó nípọn lọ́wọ́ wọn.');
INSERT INTO "aykyuni" VALUES(4,155,'4:155','Nítorí yíyẹ̀ tí wọ́n fúnra wọn yẹ àdéhùn wọn, àti ṣíṣàìgbàgbọ́ wọn nínú àwọn ọ̀rọ̀ Allāhu, àti pípa tí wọ́n ń pa àwọn Ànábì láì lẹ́tọ̀ọ́ àti wíwí tí wọ́n ń wí pé: “Ọkàn wa ti di.” - rárá, Allāhu ti fi èdídí dí ọkàn wọn ni nítorí àìgbàgbọ́ wọn. - Nítorí náà, wọn kò níí gbàgbọ́ àfi díẹ̀ (nínú wọn).');
INSERT INTO "aykyuni" VALUES(4,156,'4:156','Àti nítorí àìgbàgbọ́ wọn àti ọ̀rọ̀ wọn lórí Mọryam ní ti ìbànilórúkọjẹ́ ńlá (Allāhu tún fi èdídí dí ọkàn wọn).');
INSERT INTO "aykyuni" VALUES(4,157,'4:157','Àti nítorí ọ̀rọ̀ wọn (yìí): “Dájúdájú àwa pa Mọsīh ‘Īsā ọmọ Mọryam Òjíṣẹ́ Allāhu.” Wọn kò pa á, wọn kò sì kàn án mọ́ àgbélébùú, ṣùgbọ́n A gbé àwòrán rẹ̀ wọ ẹlòmíìràn fún wọn ni.Dájúdájú àwọn tó yapa-ẹnu lórí rẹ̀, kúkú wà nínú iyèméjì lórí rẹ̀; kò sí ìmọ̀ kan fún wọn nípa rẹ̀ àfi títẹ̀lé àbá dídá. Wọn kò pa á ní àmọ̀dájú, ');
INSERT INTO "aykyuni" VALUES(4,158,'4:158','bí kò ṣe pé Allāhu gbé e wá sókè lọ́dọ̀ Rẹ̀ ni. Allāhu sì ń jẹ́ Alágbára, Ọlọ́gbọ́n.1 ');
INSERT INTO "aykyuni" VALUES(4,159,'4:159','Kò sì níí sí ẹnì kan nínú àwọn onítírà (láyé nígbà tí ‘Īsā ọmọ Mọryam bá sọ̀kalẹ̀ padà láti ojú sánmọ̀) àfi kí ó gbà á gbọ́ ní òdodo ṣíwájú ikú rẹ̀.í Ọjọ́ Àjíǹde, ó sì máa jẹ́ ẹlẹ́rìí lórí wọn. ');
INSERT INTO "aykyuni" VALUES(4,160,'4:160','Nítorí àbòsí láti ọ̀dọ̀ àwọn tí wọ́n di yẹhudi, A ṣe àwọn n̄ǹkan dáadáa kan ní èèwọ̀ fún wọn, èyí tí wọ́n ṣe ní ẹ̀tọ́ fún wọn (tẹ́lẹ̀. A ṣe é ní èèwọ̀ fún wọn sẹ́) nípa bí wọ́n ṣe ń ṣẹ́rí ọ̀pọ̀lọ́pọ̀ kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu.');
INSERT INTO "aykyuni" VALUES(4,161,'4:161','Àti gbígbà tí wọ́n ń gba owó èlé, tí A sì ti kọ̀ ọ́ fún wọn, àti jíjẹ tí wọ́n ń jẹ dúkìá àwọn ènìyàn lọ́nà èrú. A sì ti pèsè ìyà ẹlẹ́ta-eléro sílẹ̀ de àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(4,162,'4:162','Ṣùgbọ́n àwọn tó jindò nínú ìmọ̀ nínú wọn àti àwọn onígbàgbọ́ òdodo, wọ́n gbàgbọ́ nínú ohun tí A sọ̀kalẹ̀ fún ọ àti ohun tí A sọ̀kalẹ̀ ṣíwájú rẹ, (wọ́n tún gbàgbọ́ nínú àwọn mọlāika) tó ń kírun, àtí àwọn tó ń yọ Zakāh àti àwọn tó gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn, àwọn wọ̀nyẹn ni A máa fún ní ẹ̀san ńlá.');
INSERT INTO "aykyuni" VALUES(4,163,'4:163','Dájúdájú Àwa (fi ìmísí) ránṣẹ́ sí ọ gẹ́gẹ́ bí A ṣe fi ránṣẹ́ sí (Ànábì) Nūh àti àwọn Ànábì (mìíràn) lẹ́yìn rẹ̀. A fi ìmísí ránṣẹ́ sí (àwọn Ànábì) ’Ibrọ̄hīm, ’Ismọ̄‘īl, ’Ishāƙ, Ya‘ƙūb àti àwọn àrọ́mọdọ́mọ (rẹ̀), àti (Ànábì) ‘Īsā, ’Ayyūb, Yūnus, Hārūn àti Sulaemọ̄n. A sì fún (Ànábì) Dāwūd ní Zabūr.');
INSERT INTO "aykyuni" VALUES(4,164,'4:164','Àti àwọn Òjíṣẹ́ kan tí A ti sọ ìtàn wọn fún ọ ṣíwájú pẹ̀lú àwọn Òjíṣẹ́ kan tí A kò sọ ìtàn wọn fún ọ. Allāhu sì bá (Ànábì) Mūsā sọ̀rọ̀ tààrà.');
INSERT INTO "aykyuni" VALUES(4,165,'4:165','(A ṣe wọ́n ní) Òjíṣẹ́, oníròó ìdùnnú àti olùkìlọ̀ nítorí kí àwíjàre má lè wà fún àwọn ènìyàn lọ́dọ̀ Allāhu lẹ́yìn (tí) àwọn Òjíṣẹ́ (ti jíṣẹ́). Allāhu sì ń jẹ́ Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(4,166,'4:166','Ṣùgbọ́n Allāhu ń jẹ́rìí sí ohun tí Ó sọ̀kalẹ̀ fún ọ. Ó sọ̀ ọ́ kalẹ̀ pẹ̀lú ìmọ̀ Rẹ̀. Àwọn mọlāika náà ń jẹ́rìí (sí i). Allāhu sì tó ní Ẹlẹ́rìí.');
INSERT INTO "aykyuni" VALUES(4,167,'4:167','Dájúdájú àwọn tó ṣàì gbàgbọ́, tí wọ́n sì ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu, wọ́n kúkú ti ṣìnà ní ìṣìnà tó jìnnà.');
INSERT INTO "aykyuni" VALUES(4,168,'4:168','Dájúdájú àwọn tó ṣàì gbàgbọ́, tí wọ́n tún ṣàbòsí, Allāhu kò níí forí jìn wọ́n, kò sì níí fi ojú-ọ̀nà kan mọ̀ wọ́n.');
INSERT INTO "aykyuni" VALUES(4,169,'4:169','Àyàfi ojú-ọ̀nà iná Jahanamọ. Olùṣegbére ni wọ́n nínú rẹ̀ títí láéláé. Ìyẹn sì ń jẹ́ ìrọ̀rùn fún Allāhu.1 ');
INSERT INTO "aykyuni" VALUES(4,170,'4:170','Ẹ̀yin ènìyàn, dájúdájú Òjíṣẹ́ náà ti dé ba yín pẹ̀lú òdodo láti ọ̀dọ̀ Olúwa yín. Nítorí náà, kí ẹ gbà á gbọ́ l’ó dára jùlọ fún yín. Tí ẹ bá sì ṣàì gbàgbọ́, dájúdájú ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀. Allāhu sì ń jẹ́ Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(4,171,'4:171','Ẹ̀yin onítírà, ẹ má ṣe tayọ ẹnu-ààlà nínú ẹ̀sìn yín, kí ẹ sì má sọ ohun kan nípa Allāhu àfi òdodo. Òjíṣẹ́ Allāhu ni Mọsīh ‘Īsā ọmọ Mọryam. Ọ̀rọ̀ Rẹ̀ (kun fayakūn) tí Ó sọ ránṣẹ́ sí Mọryam l’Ó sì fi ṣẹ̀dá rẹ̀. Ẹ̀mí kan (tí Allāhu ṣẹ̀dá) láti ọ̀dọ̀ Rẹ̀ sì ni (Ó fi dá Mọsīh ‘Īsā ọmọ Mọryam). Nítorí náà, ẹ gbàgbọ́ nínú Allāhu àti àwọn Òjíṣẹ́ Rẹ̀. Ẹ yé sọ mẹ́ta (lọ́kan) mọ́. Kí ẹ jáwọ́ níbẹ̀ ló jẹ́ oore fún yín. Allāhu nìkan ni Ọlọ́hun, Ọ̀kan ṣoṣo tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo. Ó mọ́ tayọ kí Ó ní ọmọ. TiRẹ̀ ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Allāhu sì tó ní Alámòjúútó.1 ');
INSERT INTO "aykyuni" VALUES(4,172,'4:172','Mọsīh kò kọ̀ láti jẹ́ ẹrú fún Allāhu. Bẹ́ẹ̀ náà ni àwọn mọlāika tí wọ́n súnmọ́ Allāhu. Ẹnikẹ́ni tí ó bá kọ̀ láti jọ́sìn fún Un, tí ó tún ṣègbéraga, (Allāhu) yóò kó wọn jọ papọ̀ pátápátá sí ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(4,173,'4:173','Ní ti àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣiṣẹ́ rere, (Allāhu) yóò san wọ́n ní ẹ̀san rere wọn ní ẹ̀kúnrẹ́rẹ́. Ó sì máa ṣe àlékún fún wọn nínú oore àjùlọ Rẹ̀. Ní ti àwọn tó bá sì kọ̀ (láti jọ́sìn fún Allāhu), tí wọ́n sì ṣègbéraga, (Allāhu) yóò jẹ wọ́n níyà ẹlẹ́ta-eléro. Wọn kò sì níí rí alátìlẹ́yìn tàbí alárànṣe kan lẹ́yìn Allāhu.');
INSERT INTO "aykyuni" VALUES(4,174,'4:174','Ẹ̀yin ènìyàn, dájúdájú ẹ̀rí ọ̀rọ̀ ti dé ba yín láti ọ̀dọ̀ Olúwa yín. A sì tún sọ ìmọ́lẹ̀ tó yanjú kalẹ̀ fún yín.');
INSERT INTO "aykyuni" VALUES(4,175,'4:175','Ní ti àwọn tó gbàgbọ́ nínú Allāhu, tí wọ́n sì dúró ṣinṣin tì Í, Ó máa fi wọ́n sínú ìkẹ́ àti ọlá kan láti ọ̀dọ̀ Rẹ̀. Ó sì máa fi wọ́n mọ ọ̀nà tààrà sí ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(4,176,'4:176','Wọ́n ń bi ọ́ léèrè ìbéèrè, sọ pé: “Allāhu ń fún yín ní ìdájọ́ nípa ẹni tí kò ní òbí, kò sì ní ọmọ. Tí ènìyàn kan bá kú, tí kò ní ọmọ láyé, tí ó sì ní arábìnrin kan, ìdajì ni tirẹ̀ nínú ohun tí ó fi sílẹ̀. (Arákùnrin) l’ó máa jẹ gbogbo ogún arábìnrin rẹ̀, tí kò bá ní ọmọ láyé. Tí arábìnrin bá sì jẹ́ méjì, ìdá méjì nínú ìdá mẹ́ta ni tiwọn nínú ohun tí ó fi sílẹ̀. Tí wọ́n bá sì jẹ́ arákùnrin (púpọ̀) lọ́kùnrin àti lóbìnrin, ti ọkùnrin kan ni ìpín obìnrin méjì. Allāhu ń ṣe àlàyé fún yín kí ẹ má baà ṣìnà. Allāhu sì ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(5,1,'5:1','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ mú àwọn àdéhùn ṣẹ. Wọ́n ṣe àwọn ẹran-ọ̀sìnní ẹ̀tọ́ fún yín àfi èyí tí wọ́n bá ń kà fún yín (ní èèwọ̀), ẹ má ṣe sọ ìdọdẹ ẹranko di ẹ̀tọ́ nígbà tí ẹ bá wà nínú aṣọ húrùmí. Dájúdájú Allāhu ń ṣe ìdájọ́ ohun tí Ó bá fẹ́. ');
INSERT INTO "aykyuni" VALUES(5,2,'5:2','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe rú òfin àwọn n̄ǹkan àríṣàmì tí Allāhu gbékalẹ̀ fún ẹ̀sìn Rẹ̀ (nínú iṣẹ́ hajj). Ẹ má ṣe rú òfin oṣù ọ̀wọ̀, àti ẹran ọrẹ (tí wọn kò ṣàmì sí lọ́rùn) àti (àwọn ẹran ọrẹ) tí wọ́n ṣàmì sí lọ́rùn. Ẹ má ṣe ìdíwọ́ fún àwọn tó ń gbèrò láti lọ sí Ilé Haram, tí wọ́n ń wá oore àjùlọ àti ìyọ́nú láti ọ̀dọ̀ Olúwa wọn. Tí ẹ bá sì ti túra sílẹ̀ nínú aṣọ hurumi, nígbà náà ẹ (lè) dọdẹ (ẹranko). Ẹ má ṣe jẹ́ kí ìkórira àwọn ènìyàn kan tì yín láti tayọ ẹnu-ààlà nítorí pé, wọ́n ṣe yín lórí kúrò ní Mọ́sálásí Haram. Ẹ ran ara yín lọ́wọ́ lórí iṣẹ́ rere àti ìbẹ̀rù Allāhu. Ẹ má ṣe ran ara yín lọ́wọ́ lórí (ìwà) ẹ̀ṣẹ̀ àti ìtayọ ẹnu-ààlà. Ẹ bẹ̀rù Allāhu. Dájúdájú Allāhu le (níbi) ìyà.');
INSERT INTO "aykyuni" VALUES(5,3,'5:3','A ṣe é ní èèwọ̀ fún yín ẹran òkúǹbete àti ẹ̀jẹ̀ àti ẹran ẹlẹ́dẹ̀ àti èyí tí wọ́n pa pẹ̀lú orúkọ tí kì í ṣe “Allāhu” àti ẹran tí wọ́n fún lọ́rùn pa àti ẹran tí wọ́n lù pa àti ẹran tí ó ré lulẹ̀ tí ó kú àti ẹran tí wọ́n kàn pa àti èyí tí ẹranko abìjàwàrà jẹ kù àfi èyí tí ẹ bá rí dú (ṣíwájú kí ó tó kú) àti èyí tí wọ́n pa sídìí òrìṣà. Èèwọ̀ sì ni fún yín láti yẹṣẹ́ wò.Ìwọ̀nyẹn ni ìbàjẹ́. Lónìí ni àwọn tó ṣàì gbàgbọ́ sọ̀rètí nù nípa ẹ̀sìn yín. Nítorí náà, ẹ má ṣe páyà wọn. Ẹ páyà Mi. Mo parí ẹ̀sìn yín fún yín lónìí. Mo sì ṣe àṣepé ìdẹ̀ra Mi fún yín. Mo sì yọ́nú sí ’Islām ní ẹ̀sìn fún yín. Nítorí náà, ẹni tí ìnira ebi bá mú (jẹ ẹran èèwọ̀) nínú ebi tó lágbára gan-an, tí kì í ṣe ẹni tí ẹ̀ṣẹ̀ ń wùú dá, dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.2 ');
INSERT INTO "aykyuni" VALUES(5,4,'5:4','Wọ́n ń bi ọ́ léèrè pé: “Kí ni wọ́n ṣe ní ẹ̀tọ́ fún wọn?” Sọ pé: “Wọ́n ṣe àwọn n̄ǹkan dáadáa ní ẹ̀tọ́ fún yín àti (ẹran tí ẹ pa nípasẹ̀) èyí tí ẹ kọ́ ní ẹ̀kọ́ (ìdọdẹ) nínú àwọn ẹranko àti ẹyẹ tí ń dọdẹ. Ẹ kọ́ àwọn ajá lẹ́kọ̀ọ́ ìdọdẹ, kí ẹ kọ́ wọn nínú ohun tí Allāhu fi mọ̀ yín. Nítorí náà, ẹ jẹ nínú ohun tí wọ́n bá pa fún yín, kí ẹ sì ṣe bismillāh sí i Ẹ bẹ̀rù Allāhu. Dájúdájú Allāhu ni Olùyára níbi ìṣírò-iṣẹ́. ');
INSERT INTO "aykyuni" VALUES(5,5,'5:5','Wọ́n ṣe àwọn n̄ǹkan dáadáa ní ẹ̀tọ́ fún yín lónìí. Àti oúnjẹ àwọn tí A fún ní tírà, ẹ̀tọ́ ni fún yín.Oúnjẹ tiyín náà, ẹ̀tọ́ ni fún wọn. (Ẹ̀tọ́ ni fún yín láti fẹ́) àwọn olómìnira nínú àwọn onígbàgbọ́ òdodo lóbìnrin àti àwọn olómìnira lóbìnrin nínú àwọn tí A fún ní tírà ṣíwájú yín, nígbà tí ẹ bá ti fún wọn ní ṣọ̀daàkí wọn; ẹ fẹ́ wọn ní fífẹ́ (bí ’Islām ṣe ní kí ẹ fẹ́ ìyàwó), láì níí máa bá wọn ṣe sìná (ṣíwájú yìgì) láì sì níí máa yàn wọ́n lálè.2 Ẹnikẹ́ni tí ó bá lòdì sí ìgbàgbọ́ òdodo3, iṣẹ́ rẹ̀ ti bàjẹ́. Ní Ọjọ́ Ìkẹ́yìn, ó sì máa wà nínú àwọn ẹni òfò. ');
INSERT INTO "aykyuni" VALUES(5,6,'5:6','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nígbà tí ẹ bá fẹ́ kírun, ẹ wẹ ojú yín àti ọwọ́ yín títí dé ìgúnpá. Ẹ fi omi pá orí yín. Ẹ wẹ ẹsẹ̀ yín títí dé kókósẹ̀ méjèèjì. Tí ẹ bá ní jánnábà lára, ẹ wẹ ìwẹ̀ ìmọ́ra. Tí ẹ bá jẹ́ aláìsàn, tàbí ẹ wà lórí ìrìn-àjò tàbí ẹnì kan nínú yín dé láti ilé ìgbọ̀nsẹ̀, tàbí ẹ súnmọ́ obìnrin, tí ẹ ò bá rí omi, nígbà náà kí ẹ fi erùpẹ̀ mímọ́ ṣe tayamọmu. Ẹ fi pá ojú yín àti ọwọ́ yín lára rẹ̀. Allāhu kò fẹ́ láti kó wàhálà ba yín, ṣùgbọ́n Ó fẹ́ láti fọ̀ yín mọ́. Ó sì fẹ́ ṣe àṣepé ìdẹ̀ra Rẹ̀ fún yín nítorí kí ẹ lè dúpẹ́.');
INSERT INTO "aykyuni" VALUES(5,7,'5:7','Ẹ rántí ìdẹ̀ra tí Allāhu ṣe fún yín àti àdéhùn Rẹ̀, èyí tí Ó ba yín ṣe, nígbà tí ẹ̀yin sọ pé: “A gbọ́, a sì tẹ̀lé e.” Ẹ bẹ̀rù Allāhu. Dájúdájú Allāhu ni Onímọ̀ nípa ohun tí ń bẹ nínú igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(5,8,'5:8','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ jẹ́ olùdúró ṣinṣin lórí déédé nítorí ti Allāhu nígbà tí ẹ bá ń jẹ́rìí. Ẹ má ṣe jẹ́ kí ìkórira àwọn ènìyàn kan tì yín láti má ṣe déédé. Ẹ ṣe déédé, òhun l’ó súnmọ́ ìbẹ̀rù Allāhu jùlọ. Kí ẹ sì bẹ̀rù Allāhu. Dájúdájú Allāhu ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.1 ');
INSERT INTO "aykyuni" VALUES(5,9,'5:9','Allāhu ṣe àdéhùn fún àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣiṣẹ́ rere (pé) àforíjìn àti ẹ̀san ńlá ń bẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(5,10,'5:10','Àwọn tó ṣàì gbàgbọ́, tí wọ́n sì pe àwọn āyah Wa ní irọ́, àwọn wọ̀nyẹn ní èrò inú iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(5,11,'5:11','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ rántí ìdẹ̀ra tí Allāhu ṣe fún yín; nígbà tí ìjọ kan gbèrò látinawọ́ ìjà si yín, (Allāhu) sì kó wọn lọ́wọ́ rọ̀ fún yín. Ẹ bẹ̀rù Allāhu. Àti pé Allāhu ni kí àwọn onígbàgbọ́ òdodo gbáralé.');
INSERT INTO "aykyuni" VALUES(5,12,'5:12','Dájúdájú Allāhu gba àdéhùn lọ́wọ́ àwọn ọmọ ’Isrọ̄’īl. A sì gbé ìjòyè méjìlá dìde nínú wọn. Allāhu sì sọ (fún wọn) pé: “Dájúdájú Mò ń bẹ pẹ̀lú yín, tí ẹ bá ń kírun, tí ẹ̀ bá ń yọ Zakāh, tí ẹ bá gba àwọn Òjíṣẹ́ Mi gbọ́, tí ẹ ràn wọ́n lọ́wọ́, tí ẹ sì yá Allāhu ní dúkìá tó dára. Dájúdájú Mo máa pa àwọn àṣìṣe yín rẹ́. Dájúdájú Mo máa mu yín wọ inú àwọn Ọgbà Ìdẹ̀ra kan, tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Àmọ́ ẹnikẹ́ni tí ó bá ṣàì gbàgbọ́ lẹ́yìn ìyẹn nínú yín, ó kúkú ti ṣìnà kúrò lójú ọ̀nà tààrà.”');
INSERT INTO "aykyuni" VALUES(5,13,'5:13','Nítorí yíyẹ̀ tí wọ́n fúnra wọn yẹ àdéhùn wọn ni A fi ṣẹ́bi lé wọn. A sì mú ọkàn wọn le koko (nítorí pé), wọ́n ń gbé àwọn ọ̀rọ̀ kúrò ní àwọn àyè rẹ̀, wọ́n sì gbàgbé ìpín kan nínúoore tí A rán wọn létí rẹ̀. O ò níí yé rí oníjàǹbá láààrin wọn àfi díẹ̀ nínú wọn. Nítorí náà, ṣe àmójúkúrò fún wọn, kí o sì foríjìn wọ́n. Dájúdájú Allāhu fẹ́ràn àwọn olùṣe-rere.');
INSERT INTO "aykyuni" VALUES(5,14,'5:14','Àti pé lọ́dọ̀ àwọn tó wí pé: “Dájúdájú nasọ̄rọ̄ ni àwa.”, A gba àdéhùn lọ́wọ́ wọn, wọ́n sì gbàgbé ìpín kan nínú oore tí A rán wọn létí rẹ̀. Nítorí náà, A dá ọ̀tá àti ọ̀tẹ̀ sáààrin wọn títí di Ọjọ́ Àjíǹde. Láìpẹ́ Allāhu máa fún wọn ní ìró ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(5,15,'5:15','Ẹ̀yin onítírà, dájúdájú Òjíṣẹ́ Wa ti wá ba yín. Ó ń ṣàlàyé ọ̀pọ̀lọ́pọ̀ ohun tí ẹ fi pamọ́ nínú tírà fún yín. Ó sì ń ṣàmójúkúrò níbi ọ̀pọ̀lọ́pọ̀. Ìmọ́lẹ̀ àti Tírà tó yanjú kúkú ti dé ba yín láti ọ̀dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(5,16,'5:16','(Tírà tó yanjú náà, al-Ƙur’ān), Allāhu ń fi ṣe ìmọ̀nà fún ẹnikẹ́ni tí ó bá tẹ̀lé (àwọn n̄ǹkan) tí Allāhu yọ́nú sí, àwọn ọ̀nà àlàáfíà (àwọn ọ̀nà ọlà). Ó sì ń mú wọn jáde láti inú àwọn òkùnkùn wá sínú ìmọ́lẹ̀ pẹ̀lú ìyọ̀ǹda Allāhu. Ó sì ń tọ́ wọn sí ọ̀nà tààrà (’Islām).');
INSERT INTO "aykyuni" VALUES(5,17,'5:17','Wọ́n kúkú ti di kèfèrí, àwọn tó wí pé: “Dájúdájú Allāhu ni Mọsīh ọmọ Mọryam.” Sọ pé: “Ta ló ní ìkápá kiní kan lọ́dọ̀ Allāhu tí (Allāhu) bá fẹ́ pa Mọsīh ọmọ Mọryam àti ìyá rẹ̀ àti àwọn tó ń bẹ lórí ilẹ̀ ayé run pátápátá?” Ti Allāhu ni ìjọba àwọn sánmọ̀ àti ilẹ̀ àti ohunkóhun tí ń bẹ láààrin àwọn méjèèjì. (Allāhu) ń ṣẹ́dàá ohunkóhun tí Ó bá fẹ́. Allāhu sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(5,18,'5:18','Àwọn yẹhudi àti nasọ̄rọ̄ wí pé: “Àwa ni ọmọ Allāhu àti olólùfẹ́ Rẹ̀.” Sọ pé: “Kí ni ìdí tí Ó ṣe ń fi ìyà ẹ̀ṣẹ̀ yín jẹ yín nígbà náà?” Rárá (kò rí bí ẹ ṣe wí, àmọ́) abara ni yín nínú àwọn tí Ó ṣẹ̀dá. Ó ń ṣàforíjìn fún ẹni tí Ó bá fẹ́. Ó sì ń jẹ ẹni tí Ó bá fẹ́ níyà. Ti Allāhu ni ìjọba àwọn sánmọ̀ àti ilẹ̀ àti ohunkóhun tí ń bẹ láààrin àwọn méjèèjì. Ọ̀dọ̀ Rẹ̀ sì ni àbọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(5,19,'5:19','Ẹ̀yin onítírà, dájúdájú Òjíṣẹ́ Wa ti dé ba yín, tí ó ń ṣe àlàyé (ọ̀rọ̀) fún yín lẹ́yìn àsìkò tí A ti dá àwọn Òjísẹ́ dúró, nítorí kí ẹ má ṣe wí pé: “Kò sí oníròó ìdùnnú tàbí olùkìlọ̀ kan tí ó wá bá wa.” Nítorí náà, dájúdájú oníròó ìdùnnú àti olùkìlọ̀ kan ti wá ba yín. Allāhu sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(5,20,'5:20','Nígbà tí (Ànábì) Mūsā sọ fún ìjọ rẹ̀ pé: “Ẹ̀yin ìjọ mi, ẹ rántí ìdẹ̀ra tí Allāhu ṣe fún yín, nígbà tí Ó fi àwọn Ànábì sáààrin yín, tí Ó ṣe yín ní ọba, tí Ó tún fún yín ní n̄ǹkan tí kò fún ẹnì kan rí ní àgbáyé (lásìkò tiyín).');
INSERT INTO "aykyuni" VALUES(5,21,'5:21','Ẹ̀yin ìjọ mi, ẹ wọ orí ilẹ̀ mímọ́ tí Allāhu pa láṣẹ fún yín. Ẹ má se padà sẹ́yìn nítorí kí ẹ má baà padà di ẹni òfò. ');
INSERT INTO "aykyuni" VALUES(5,22,'5:22','Wọ́n wí pé: “Mūsā, dájúdájú ìjọ ajẹni-nípá wà nínú ìlú náà. Dájúdájú àwa kò sì níí wọ inú rẹ̀ títí wọn yóò fi jáde kúrò nínú rẹ̀. Tí wọ́n bá jáde kúrò nínú rẹ̀, àwa máa wọ inú rẹ̀ nígbà náà.”');
INSERT INTO "aykyuni" VALUES(5,23,'5:23','Àwọn ọkùnrin méjì kan nínú àwọn tí ń páyà (Allāhu) - Allāhu sì kẹ́ àwọn méjèèjì- wọ́n sọ pé: “Ẹ gba ẹnu bodè wọ inú ìlú tọ̀ wọ́n. Tí ẹ bá wọ inú ìlú tọ̀ wọ́n, dájúdájú ẹ máa borí wọn. Allāhu sì ni kí ẹ gbáralé, tí ẹ bá jẹ́ onígbàgbọ́ òdodo.” ');
INSERT INTO "aykyuni" VALUES(5,24,'5:24','Wọ́n wí pé: “Ìwọ Mūsā, dájúdájú àwa kò níí wọ inú ìlú náà láéláé níwọ̀n ìgbà tí wọ́n bá sì wà nínú rẹ̀. Nítorí náà, kí ìwọ àti Olúwa rẹ lọ (síbẹ̀). Kí ẹ̀yin méjèèjì jà wọ́n lógun. Dájúdájú ibí yìí ni àwa yóò jókòó sí ná.”');
INSERT INTO "aykyuni" VALUES(5,25,'5:25','Ó sọ pé: “Olúwa mi, dájúdájú èmi kò ní ìkápá lórí ẹnì kan bí kò ṣe (lórí) ara mi àti arákùnrin mi. Nítorí náà, ya àwa àti ìjọ òbìlẹ̀jẹ́ sí ọ̀tọ̀ọ̀tọ̀.”1 ');
INSERT INTO "aykyuni" VALUES(5,26,'5:26','(Allāhu) sọ pé: “Dájúdájú ó ti di èèwọ̀ fún wọn (láti wọnú ìlú náà) fún ogójì ọdún tí wọn yóò fi rin àrìnnù lórí ilẹ̀. Nítorí náà, má ṣe banújẹ́ nítorí ìjọ òbìlẹ̀jẹ́.”1 ');
INSERT INTO "aykyuni" VALUES(5,27,'5:27','Ka ọ̀rọ̀ àwọn ọmọ (Ànábì) Ādam méjèèjì fún wọn pẹ̀lú òdodo. (Rántí) nígbà tí àwọn méjèèjì ṣe ọrẹ àsè (láti fi súnmọ́ Allāhu). A gba ti ọ̀kan nínú àwọn méjèèjì, A kò sì gba ti ìkejì. (Ẹni tí A kò gba tirẹ̀) wí pé: “Dájúdájú èmi yóò pa ọ́.” (Ìkejì) sì sọ pé: “(Ọrẹ àsè) tàwọn olùbẹ̀rù nìkan ni Allāhu máa gbà.');
INSERT INTO "aykyuni" VALUES(5,28,'5:28','Dájúdájú tí o bá na ọwọ́ rẹ sí mi láti pa mí, èmi kò sì níí na ọwọ́ mi sí ọ láti pa ọ́, nítorí pé dájúdáju èmi ń bẹ̀rù Allāhu, Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(5,29,'5:29','Dájúdájú èmi fẹ́ kí o padà (sí ọ̀dọ̀ Allāhu ní Ọjọ́ Àjíǹde) pẹ̀lú ẹ̀ṣẹ̀ (bí ó ṣe pa) mí àti ẹ̀ṣẹ̀ tìrẹ nítorí kí o lè jẹ́ ọ̀kan nínú àwọn èrò inú Iná. Ìyẹn sì ni ẹ̀san àwọn alábòsí.”');
INSERT INTO "aykyuni" VALUES(5,30,'5:30','Ó mú ọkàn ara rẹ̀ gírí láti pa arákùnrin rẹ̀. Ó pa á. Ó sì di ọ̀kan nínú àwọn ẹni òfò.');
INSERT INTO "aykyuni" VALUES(5,31,'5:31','Allāhu sì gbé ẹyẹ kannakánná kan dìde, tí ó ń fi ẹsẹ̀ walẹ̀ nítorí kí ó lè fi bí ó ṣe máa bo òkúarákùnrin rẹ̀ mọ́ inú ilẹ̀ hàn án. Ó wí pé: “Tèmí bá mi o! Ṣé mo kágara ni láti dà bí irú ẹyẹ kannakánná yìí, kí èmi náà sì lè bo òkú arákùnrin mi mọ́lẹ̀?” Ó sì di ara àwọn alábàámọ̀. ');
INSERT INTO "aykyuni" VALUES(5,32,'5:32','Nítorí ìyẹn, A sì ṣe é ní òfin fún àwọn ọmọ ’Isrọ̄’īl pé, dájúdájú ẹnikẹ́ni tí ó bá pa ẹ̀mí (ènìyàn) kan láìjẹ́ nítorí pípa ẹ̀mí (ènìyàn) kan tàbí ṣíṣe ìbàjẹ́ kan lórí ilẹ̀ ó dà bí ẹni tí ó pa gbogbo ènìyàn pátápátá. Ẹnikẹ́ni tí ó bá sì mú ẹ̀mí (ènìyàn) ṣẹ̀mí, ó dà bí ẹni tí ó mú gbogbo ènìyàn ṣẹ̀mí pátápátá. Àwọn Òjíṣẹ́ Wa sì ti wá bá wọn pẹ̀lú àwọn ẹ̀rí tó yanjú. Lẹ́yìn náà, dájúdájú ọ̀pọ̀lọ́pọ̀ nínú wọn lẹ́yìn ìyẹn ni alákọyọ lórí ilẹ̀. ');
INSERT INTO "aykyuni" VALUES(5,33,'5:33','Ẹ̀san àwọn tó ń gbógun ti Allāhu àti Òjíṣẹ́ Rẹ̀, tí wọ́n sì ń ṣe ìbàjẹ́ lórí ilẹ̀ ni pé, kí ẹ pa wọ́n tàbí kí ẹ kàn wọ́n mọ́ igi àgbélébùú tàbí kí ẹ gé ọwọ́ wọn àti ẹsẹ̀ wọn ní ìpasípayọ tàbí kí ẹ lé wọn kúrò nínú ìlú. Ìyẹn ni ìyẹpẹrẹ fún wọn nílé ayé. Ìyà ńlá sì ń bẹ fún wọn ní ọ̀run.');
INSERT INTO "aykyuni" VALUES(5,34,'5:34','Àyàfi àwọn tó ronú pìwàdà ṣíwájú kí ẹ tó lágbára lórí wọn. Nítorí náà, ẹ mọ̀ pé dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(5,35,'5:35','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ bẹ̀rù Allāhu, kí ẹ sì wá àtẹ̀gùn sí ọ̀dọ̀ Rẹ̀.Ẹ jagun fún ààbò ẹ̀sìn Rẹ̀ nítorí kí ẹ lè jèrè. ');
INSERT INTO "aykyuni" VALUES(5,36,'5:36','Dájúdájú àwọn tó ṣàì gbàgbọ́, tí ó bá jẹ́ pé gbogbo ohun tí ń bẹ lórí ilẹ̀ ayé pátápátá jẹ́ tiwọn àti irú rẹ̀ pẹ̀lú rẹ̀ láti fi gba ara wọn sílẹ̀ níbi ìyà Ọjọ́ Àjíǹde, A ò níí gbà á ní ọwọ́ wọn. Ìyà ẹlẹ́ta-eléro sìwà fún wọn.');
INSERT INTO "aykyuni" VALUES(5,37,'5:37','Wọn yóò fẹ́ jáde kúrò nínú Iná, wọn kò sì níí lè jáde kúrò nínú rẹ̀. Ìyà gbére sì wà fún wọn.1 ');
INSERT INTO "aykyuni" VALUES(5,38,'5:38','Olè lọ́kùnrin àti olè lóbìnrin, ẹ gé ọwọ́ àwọn méjèèjì.(Ó jẹ́) ẹ̀san fún iṣẹ́ ọwọ́ àwọn méjèèjì. (Ó sì jẹ́) ìjìyà láti ọ̀dọ̀ Allāhu. Allāhu sì ni Alágbára, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(5,39,'5:39','Ẹnikẹ́ni tí ó bá ronú pìwàdà lẹ́yìn àbòsí (ọwọ́) rẹ̀, tí ó sì ṣe àtúnṣe, dájúdájú Allāhu yóò gba ìronúpìwàdà rẹ̀. Dájúdájú Allāhu ni Aláforíjìn, Aláàánú.');
INSERT INTO "aykyuni" VALUES(5,40,'5:40','Ṣé o ò mọ̀ pé dájúdájú ti Allāhu ni ìjọba àwọn sánmọ̀ àti ilẹ̀? Ó máa jẹ ẹni tí Ó bá fẹ́ níyà. Ó sì máa forí jin ẹni tí Ó bá fẹ́. Allāhu sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(5,41,'5:41','Ìwọ Òjíṣẹ́, má ṣe jẹ́ kí ó bà ọ́ nínú jẹ́ àwọn tó ń yára lọ sínú àìgbàgbọ́ nínú àwọn tó fi ẹnu ara wọn wí pé: “A gbàgbọ́.” - tí ọkàn wọn kò sì gbàgbọ́ ní òdodo - àti àwọn tó di yẹhudi, tí wọ́n ń tẹ́tí sí irọ́, tí wọ́n sì ń tẹ́tí sí ọ̀rọ̀ fún àwọn ènìyàn mìíràn tí kò wá sí ọ̀dọ̀ rẹ, tí wọ́n ń yí ọ̀rọ̀ padà kúrò ní àwọn àyè rẹ̀, wọ́n sì ń wí pé: “Tí wọ́n bá fún yín ní èyí ẹ gbà á. Tí wọn kò bá sì fún yín ẹ ṣọ́ra.”Ẹnikẹ́ni tí Allāhu bá fẹ́ fòòró rẹ̀, o ò ní ìkápá kiní kan lọ́dọ̀ Allāhu fún un. Àwọn wọ̀nyẹn ni àwọn tí Allāhu kò fẹ́ fọ ọkàn wọn mọ́. Ìdójútì ń bẹ fún wọn ní ayé. Ìyà ńlá sì ń bẹ fún wọn ní ọ̀run. ');
INSERT INTO "aykyuni" VALUES(5,42,'5:42','Wọ́n ń tẹ́tí gbọ́ irọ́, wọ́n sì ń jẹ n̄ǹkan èèwọ̀. Nítorí náà, tí wọ́n bá wá bá ọ, ṣèdájọ́ láààrin wọn tàbí kí o ṣẹ́rí kúrò lọ́dọ̀ wọn. Tí o bá ṣẹ́rí kúrò lọ́dọ̀ wọn, wọn kò lè kó ìnira kan kan bá ọ. Tí o bá sì fẹ́ dájọ́, ṣe ìdájọ́ láààrin wọn pẹ̀lú déédé. Dájúdájú Allāhu nífẹ̀ẹ́ àwọn onídéédé.');
INSERT INTO "aykyuni" VALUES(5,43,'5:43','Báwo ni wọ́n á ṣe fi ọ́ ṣe adájọ́, nígbà tí ó jẹ́ pé at-Taorāh wà lọ́dọ̀ wọn. Ìdájọ́ Allāhu sì wà nínú rẹ̀. Lẹ́yìn náà, wọ́n ń pẹ̀yìndà lẹ́yìn ìyẹn. Àwọn wọ̀nyẹn kì í ṣe onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(5,44,'5:44','Dájúdájú Àwa sọ at-Taorāh kalẹ̀. Ìmọ̀nà àti ìmọ́lẹ̀ ń bẹ nínú rẹ̀. Àwọn Ànábì tí wọ́n jẹ́ mùsùlùmí ń fi ṣe ìdájọ́ fún àwọn tó di yẹhudi. Bẹ́ẹ̀ náà ni àwọn àlùfáà àti àwọn amòfin ẹ̀sìn (ń fi ṣe ìdájọ́) nítorí ohun tí A fún wọn ṣọ́ nínú tírà Allāhu. Wọ́n sì jẹ́ ẹlẹ́rìí lórí rẹ̀. Nítorí náà, má ṣe páyà ènìyàn. Ẹ páyà Mi. Ẹ má ṣe ta àwọn āyah Mi ní owó kékeré. Ẹnikẹ́ni tí kò bá ṣèdájọ́ pẹ̀lú ohun tí Allāhu sọ̀kalẹ̀, àwọn wọ̀nyẹn ni aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(5,45,'5:45','A sì ṣe é ní òfin sínú rẹ̀ fún wọn pé dájúdájú ẹ̀mí fún ẹ̀mí, ojú fún ojú, imú fún imú, etí fún etí àti eyín fún eyín. Òfin ẹ̀san gbígbà sì wà fún ojú-ọgbẹ́. Ẹnikẹ́ni tí ó bá sí yọ̀ǹda ìgbẹ̀san, ó sì máa jẹ́ pípa ẹ̀ṣẹ̀ rẹ́ fún un. Ẹnikẹ́ni tí kò bá ṣèdájọ́ pẹ̀lú ohun tí Allāhu sọ̀kalẹ̀, àwọn wọ̀nyẹn ni alábòsí.');
INSERT INTO "aykyuni" VALUES(5,46,'5:46','A fi ‘Īsā ọmọ Mọryam tẹ̀lé orípa wọn (ìyẹn, àwọn Ànábì tí wọ́n jẹ́ mùsùlùmí); tí ó ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó ṣíwájú rẹ̀ nínú at-Taorāh. A sì fún un ní al-’Injīl. Ìmọ̀nà àti ìmọ́lẹ̀ wà nínú rẹ̀, tí ó ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó ṣíwájú rẹ̀ nínú at-Taorāh. (Ó jẹ́) ìmọ̀nà àti wáàsí fún àwọn olùbẹ̀rù (Allāhu ní àsìkò tirẹ̀).');
INSERT INTO "aykyuni" VALUES(5,47,'5:47','Kí àwọn tí A fún ní al-’Injīl ṣèdájọ́ pẹ̀lú ohun tí Allāhu sọ̀kalẹ̀ nínú rẹ̀. Ẹnikẹ́ni tí kò bá ṣèdájọ́ pẹ̀lú ohun tí Allāhu sọ̀kalẹ̀, àwọn wọ̀nyẹn ni òbìlẹ̀jẹ́.1 ');
INSERT INTO "aykyuni" VALUES(5,48,'5:48','A sọ Tírà (al-Ƙur’ān) kalẹ̀ fún ọ pẹ̀lú òdodo. Ó ń jẹ́rìí sí èyí tó jẹ́ òdodo nínú èyí tó ṣíwájú rẹ̀ nínú Tírà. Ó ń wá ààbò fún àwọn òfin inú rẹ̀. Nítorí náà, fi ohun tí Allāhu sọ̀kalẹ̀ dájọ́ láààrin wọn. Má ṣe tẹ̀lé ìfẹ́-inú wọn tó yapa sí ohun tí ó dé bá ọ nínú òdodo. Olúkùlùkù nínú yín ni A ti ṣe òfin àti ìlànà fún. Àti pé tí ó bá jẹ́ pé Allāhu bá fẹ́, ìbá ṣe yín ní ìjọ kan ṣoṣo (sínú ’Islām), ṣùgbọ́n nítorí kí Ó lè dan yín wò nínú ohun tí Ó fún yín ni. Nítorí náà, ẹ gbawájú níbi iṣẹ́ rere. Ọ̀dọ̀ Allāhu ni ibùpadàsí gbogbo yín pátápátá. Ó sì máa fún yín ní ìró nípa ohun tí ẹ̀ ń yapa-ẹnu lórí rẹ̀.');
INSERT INTO "aykyuni" VALUES(5,49,'5:49','Àti pé kí o fi ohun tí Allāhu sọ̀kalẹ̀ ṣe ìdájọ́ láààrin wọn. Má ṣe tẹ̀lé ìfẹ́-inú wọn. Ṣọ́ra fún wọn kí wọ́n má baà fòòró rẹ kúrò níbi apá kan ohun tí Allāhu sọ̀kalẹ̀ fún ọ. Tí wọ́n bá sì gbúnrí, mọ̀ pé Allāhu kàn fẹ́ fi àdánwò kàn wọ́n ni nítorí apá kan ẹ̀ṣẹ̀ wọn.Àti pé dájúdájú ọ̀pọ̀lọ́pọ̀ nínú ènìyàn ni òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(5,50,'5:50','Ṣé ìdájọ́ ìgbà àìmọ̀kan ni wọ́n ń wá ni? Ta ni ẹni tí ó dára ju Allāhu lọ níbi ìdájọ́ fún ìjọ tó ní àmọ̀dájú (nípa Rẹ̀)?');
INSERT INTO "aykyuni" VALUES(5,51,'5:51','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe mú àwọn yẹhudi àti nasọ̄rọ̄ ní ọ̀rẹ́ àyò. Apá kan wọn lọ̀rẹ́ apá kan. Ẹnikẹ́ni tí ó bá mú wọn ní ọ̀rẹ́ àyò nínú yín, dájúdájú ó ti di ara wọn. Dájúdájú Allāhu kò níí fi ọ̀nà mọ ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(5,52,'5:52','Nítorí náà, o máa rí àwọn tí àárẹ̀ wà nínú ọkàn wọn, tí wọn yóò máa yára lọ sáààrin wọn. Wọn yóò máa wí pé: “À ń bẹ̀rù pé kí àpadàsí ìgbà má baà kàn wá ni.” Ó súnmọ́ kí Allāhu mú ìṣẹ́gun tàbí àṣẹ kan wá láti ọ̀dọ̀ Rẹ̀. Wọn yó sì di alábàámọ̀ lórí ohun tí wọ́n fi pamọ́ sínú ọkàn wọn.');
INSERT INTO "aykyuni" VALUES(5,53,'5:53','Àwọn tó gbàgbọ́ ní òdodo sì máa sọ pé: “Ṣé àwọn (yẹhudi) wọ̀nyí kọ́ ni àwọn tó fi Allāhu búra tí ìbúra wọn sì lágbára gan-an pé, dájúdájú àwọn ń bẹ pẹ̀lú ẹ̀yin (munāfiki)?” Iṣẹ́ wọn ti bàjẹ́. Nítorí náà, wọ́n sì di ẹni òfò.');
INSERT INTO "aykyuni" VALUES(5,54,'5:54','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹnikẹ́ni tí ó bá pẹ̀yìndà níbi ẹ̀sìn rẹ̀ nínú yín, láìpẹ́ Allāhu máa mú àwọn ènìyàn kan wá; Ó máa nífẹ̀ẹ́ wọn, àwọn náà máa nífẹ̀ẹ́ Rẹ̀. Wọn yóò rọ̀ fún àwọn onígbàgbọ́ òdodo. Wọn yó sì le mọ́ àwọn aláìgbàgbọ́. Wọn yóò máa jagun fún ààbò ẹ̀sìn Allāhu. Wọn kò sì níí bẹ̀rù èébú eléèébú. Ìyẹn ni oore àjùlọ Allāhu. Ó ń fún ẹni tí Ó bá fẹ́. Allāhu sì ni Olùgbòòrò, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(5,55,'5:55','Allāhu àti Òjíṣẹ́ Rẹ̀ àti àwọn tó gbàgbọ́ ní òdodo nìkan ni ọ̀rẹ́ yín, àwọn tó ń kírun, tí wọ́n sì ń yọ Zakāh, àwọn sì ni olùdáwọ́tẹ-orúnkún (lórí ìrun).');
INSERT INTO "aykyuni" VALUES(5,56,'5:56','Ẹnikẹ́ni tí ó bá mú Allāhu àti Òjíṣẹ́ Rẹ̀ àti àwọn onígbàgbọ́ òdodo ní ọ̀rẹ́; dájúdájú ìjọ Allāhu, àwọn ni olùborí.');
INSERT INTO "aykyuni" VALUES(5,57,'5:57','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe mú àwọn tó sọ ẹ̀sìn yín di n̄ǹkan yẹ̀yẹ́ àti eré ṣíṣe ní ọ̀rẹ́ nínú àwọn tí A fún ní tírà ṣíwájú yín àti àwọn aláìgbàgbọ́. Ẹ bẹ̀rù Allāhu tí ẹ bá jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(5,58,'5:58','Nígbà tí ẹ bá pèpè síbi ìrun kíkí, wọn yóò sọ ọ́ di n̄ǹkan yẹ̀yẹ́ àti eré ṣíṣe. Ìyẹn nítorí pé dájúdájú àwọn ni ìjọ tí kò ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(5,59,'5:59','Sọ pé: “Ẹ̀yin onítírà, ǹjẹ́ ẹ rí àlèébù kan (ǹjẹ́ ẹ sì kórira kiní kan) lára wa bí kò ṣe pé a gbàgbọ́ nínú Allāhu àti ohun tí Wọ́n sọ̀kalẹ̀ fún wa àti ohun tí Wọ́n sọ̀kalẹ̀ ṣíwájú?” Dájúdájú ọ̀pọ̀lọ́pọ̀ yín ni òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(5,60,'5:60','Sọ pé: “Ṣé kí n̄g fún yín ní ìró nípa èyí tí ó burú ju ìyẹn lọ ní ẹ̀san ní ọ̀dọ̀ Allāhu?”Òhun ni ẹ̀san) ẹni tí Allāhu ṣẹ́bilé, tí Ó sì bínú sí, tí Ó sì sọ àwọn kan nínú wọn di ọ̀bọ àti ẹlẹ́dẹ̀ àti (ẹ̀san) ẹni tí ó ń bọ àwọn Ànábì kan, tí wọn kò pe ara wọn ní ọlọ́hun, tí wọn kò sì yọ̀nù sí bí ẹ ṣe sọ wọ́n di òrìṣà àkúnlẹ̀bọ. Àwọn wọ̀nyẹn ni ipò wọn burú jùlọ, àwọn sì ni wọ́n ṣìnà jùlọ kúrò lójú-ọ̀nà tààrà. ');
INSERT INTO "aykyuni" VALUES(5,61,'5:61','Nígbà tí wọ́n bá wá sí ọ̀dọ̀ yín, wọ́n á wí pé: “A gbàgbọ́.” Dájúdájú wọ́n wọlé (tì yín) pẹ̀lú àìgbàgbọ́, wọ́n sì ti jáde pẹ̀lú rẹ̀. Allāhu sì nímọ̀ jùlọ nípa ohun tí wọ́n ń fi pamọ́.1 ');
INSERT INTO "aykyuni" VALUES(5,62,'5:62','O máa rí ọ̀pọ̀lọ́pọ̀ nínú wọn, tí wọ́n ń yára kó sínú ẹ̀ṣẹ̀, ìtayọ ẹnu-ààlà àti jíjẹ tí wọ́n ń jẹ owó èrú. Dájúdájú ohun tí wọ́n ń ṣe níṣẹ́ burú.');
INSERT INTO "aykyuni" VALUES(5,63,'5:63','Kí ni kò jẹ́ kí àwọn àlùfáà àti àwọn amòfin ẹ̀sìn máa kọ̀ fún wọn nípa ọ̀rọ̀ ẹnu wọn (tí ó jẹ́) ẹ̀ṣẹ̀ àti jíjẹ tí wọ́n ń jẹ owó èrú! Dájúdájú ohun tí wọ́n ń ṣe níṣẹ́ burú.');
INSERT INTO "aykyuni" VALUES(5,64,'5:64','Àwọn yẹhudi wí pé: “Ọwọ́ Allāhu wà ní dídì pa.” A di ọwọ́ wọn pa. A sì ṣẹ́bi lé wọn nítorí ohun tí wọ́n wí. Ọ̀rọ̀ kò rí bí wọ́n ṣe sọ ọ́ bí kò ṣe pé, ọwọ́ Rẹ̀ méjèèjì wà ní títẹ́ sílẹ̀. Ó sì ń tọrẹ bí Ó ṣe fẹ́. Ohun tí wọ́n sọ̀kalẹ̀ fún ọ láti ọ̀dọ̀ Olúwa Rẹ̀ yóò kúkú jẹ́ kí ọ̀pọ̀lọ́pọ̀ wọn lékún ní ìgbéraga àti àìgbàgbọ́ ni. A sì ju ọ̀tá àti ìkórira sáààrin wọn títí di Ọjọ́ Àjíǹde. Ìgbàkígbà tí wọ́n bá dáná ogun, Allāhu sì máa paná rẹ̀. Wọ́n sì ń ṣe ìbàjẹ́ lórí ilẹ̀. Allāhu kò sì nífẹ̀ẹ́ àwọn òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(5,65,'5:65','Tí ó bá jẹ́ pé dájúdájú àwọn onítírà gbàgbọ́ ní òdodo, kí wọ́n sì bẹ̀rù (Allāhu), Àwa ìbá pa àwọn àìda wọn rẹ́ fún wọn, Àwa ìbá sì mú wọn wọ inú àwọn Ọgbà Ìdẹ̀ra.');
INSERT INTO "aykyuni" VALUES(5,66,'5:66','Tí ó bá jẹ́ pé dájúdájú wọ́n lo at-Taorāh àti al-’Injīl àti ohun tí A sọ̀kalẹ̀ fún wọn láti ọ̀dọ̀ Olúwa wọn (ìyẹn, al-Ƙur’ān), wọn ìbá máa jẹ láti òkè wọn àti láti ìsàlẹ̀ ẹsẹ̀ wọn. Ìjọ kan ń bẹ nínú wọn tó dúró déédé, (àmọ́) ohun tí ọ̀pọ̀lọ́pọ̀ nínú wọn ń ṣe níṣẹ́ burú.1 ');
INSERT INTO "aykyuni" VALUES(5,67,'5:67','Ìwọ Òjíṣẹ́, kéde gbogbo ohun tí wọ́n sọ̀kalẹ̀ fún ọ láti ọ̀dọ̀ Olúwa rẹ. Tí o kò bá ṣe bẹ́ẹ̀, o ò jẹ́ iṣẹ́ Rẹ̀ pé. Allāhu yó sì dáàbò bò ọ́ lọ́dọ̀ àwọn ènìyàn. Dájúdájú Allāhu kò níí fi ọ̀nà mọ ìjọ aláìgbàgbọ́ (láti ṣe ọ́ ní aburú).');
INSERT INTO "aykyuni" VALUES(5,68,'5:68','Sọ pé: “Ẹ̀yin onítírà, ẹ ò rí kiní kan ṣe (nínú ẹ̀sìn) títí ẹ máa fi lo at-Taorāh àti al-’Injīl àti ohun tí A sọ̀kalẹ̀ fún yín láti ọ̀dọ̀ Olúwa yín (ìyẹn, al-Ƙur’ān).” Ohun tí wọ́n sọ̀kalẹ̀ fún ọ láti ọ̀dọ̀ Olúwa Rẹ yóò kúkú jẹ́ kí ọ̀pọ̀lọ́pọ̀ wọn lékún ní ìgbéraga àti àìgbàgbọ́ ni. Nítorí náà, má ṣe banújẹ́ nítorí ìjọ aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(5,69,'5:69','Dájúdájú àwọn tó gbàgbọ́ ní òdodo àti àwọn tó di yẹhudi, - àwọn sọ̄bi’ūn àti àwọn nasọ̄rọ̄, - ẹnikẹ́ni tí ó bá ní ìgbàgbọ́ òdodo nínú Allāhu àti Ọjọ́ Ìkẹ́yìn, tí ó sì ṣiṣẹ́ rere, kò níí sí ìbẹ̀rù fún wọn. Wọn kò sì níí banújẹ́.1 ');
INSERT INTO "aykyuni" VALUES(5,70,'5:70','A kúkú gba àdéhùn lọ́wọ́ àwọn ọmọ ’Isrọ̄’īl. A sì rán àwọn Òjíṣẹ́ kan sí wọn. Ìgbàkígbà tí Òjíṣẹ́ kan bá dé bá wọn pẹ̀lú ohun tí ẹ̀mí wọn kò fẹ́, wọ́n pe igun kan ní òpùrọ́, wọ́n sì ń pa igun kan.');
INSERT INTO "aykyuni" VALUES(5,71,'5:71','Wọ́n sì lérò pé kò níí sí ìfòòró;wọ́n fọ́jú, wọ́n sì dití (sí òdodo). Lẹ́yìn náà, Allāhu gba ìronúpìwàdà wọn. Lẹ́yìn náà, wọ́n fọ́jú, wọ́n tún dití (sí òdodo); ọ̀pọ̀lọ́pọ̀ nínú wọn (ló ṣe bẹ́ẹ̀). Allāhu sì ni Olùríran nípa ohun tí wọ́n ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(5,72,'5:72','Wọ́n kúkú ti di kèfèrí, àwọn tó wí pé: “Dájúdájú Allāhu, Òun ni Mọsīh ọmọ Mọryam.” Mọsīh sì sọ pé: “Ẹ̀yin ọmọ ’Isrọ̄’īl, ẹ jọ́sìn fún Allāhu, Olúwa mi àti Olúwa yín. Dájúdájú ẹnikẹ́ni tí ó bá bá Allāhu wá akẹgbẹ́, Allāhu ti ṣe Ọgbà Ìdẹ̀ra ní èèwọ̀ fún un. Iná sì ni ibùgbé rẹ̀. Kò sì níí sí alárànṣe kan fún àwọn alábòsí.”');
INSERT INTO "aykyuni" VALUES(5,73,'5:73','Wọ́n kúkú ti di kèfèrí, àwọn tó wí pé: “Dájúdájú Allāhu ni Ìkẹta (àwọn) mẹ́ta.” Kò sì sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Ọlọ́hun, Ọ̀kan ṣoṣo. Tí wọn kò bá jáwọ́ níbi ohun tí wọ́n ń wí, dájúdájú ìyà ẹlẹ́ta eléro l’ó máa jẹ àwọn tó di kèfèrí nínú wọn.');
INSERT INTO "aykyuni" VALUES(5,74,'5:74','Nítorí náà, ṣé wọn kò níí ronú pìwàdà sọ́dọ̀ Allāhu, kí wọ́n sì tọrọ àforíjìn lọ́dọ̀ Rẹ̀? Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(5,75,'5:75','Kí ni Mọsīh bí kò ṣe Òjíṣẹ́ kan. Àwọn Òjíṣẹ́ sì ti lọ ṣíwájú rẹ̀. Olódodo sì ni ìyá rẹ̀. Àwọn méjèèjì máa ń jẹ oúnjẹ. Wo bí A ṣe ń ṣàlàyé àwọn āyah náà fún wọn. Lẹ́yìn náà, wo bí wọ́n ṣe ń ṣẹ́rí wọn kúrò níbi òdodo!');
INSERT INTO "aykyuni" VALUES(5,76,'5:76','Sọ pé: “Ṣé ẹ̀ ń jọ́sìn fún lẹ́yìn Allāhu ohun tí kò ní ìkápá ìnira àti àǹfààní kan fún yín? Allāhu, Òun sì ni Olùgbọ́, Onímọ̀.”');
INSERT INTO "aykyuni" VALUES(5,77,'5:77','Sọ pé: “Ẹ̀yin onítírà, ẹ má ṣe tayọ ẹnu-ààlà nínú ẹ̀sìn yín, (ẹ má sọ ohun tí) kì í ṣe òdodo. Ẹ má sì ṣe tẹ̀lé ìfẹ́-inú ìjọ kan tó ti ṣìnà ṣíwájú (ìyẹn, àwọn yẹhudi). Wọ́n ṣi ọ̀pọ̀lọ́pọ̀ lọ́nà. Wọ́n sì ṣìnà kúrò ní ojú ọ̀nà tààrà.”');
INSERT INTO "aykyuni" VALUES(5,78,'5:78','Wọ́n ṣẹ́bi lé àwọn tó ṣàì gbàgbọ́ nínú àwọn ọmọ ’Isrọ̄’īl lórí ahọ́n (Ànábì) Dāwūd àti ‘Īsā ọmọ Mọryam. Ìyẹn nítorí pé, wọ́n yapa (àṣẹ Allāhu), wọ́n sì ń tayọ ẹnu-ààlà.');
INSERT INTO "aykyuni" VALUES(5,79,'5:79','Wọn kì í kọ ìwà ìbàjẹ́ tí wọ́n ń ṣe níṣẹ́ láààrin ara wọn. Ohun tí wọ́n ń ṣe níṣẹ́ sì burú.');
INSERT INTO "aykyuni" VALUES(5,80,'5:80','O máa rí ọ̀pọ̀lọ́pọ̀ nínú wọn tó ń bá àwọn tó ṣàì gbàgbọ́ ṣọ̀rẹ́. Ohun tí ẹ̀mí wọn tì síwájú fún wọn sì burú tó bẹ́ẹ̀ gẹ́ tí Allāhu fi bínú sí wọn. Olùṣegbére sì ni wọ́n nínú Ìyà.');
INSERT INTO "aykyuni" VALUES(5,81,'5:81','Tí ó bá jẹ́ pé wọ́n gbàgbọ́ nínú Allāhu àti Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -, àti ohun tí A sọ̀kalẹ̀ fún un, wọn kò níí mú wọn ní ọ̀rẹ́ àyò, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ nínú wọn ni òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(5,82,'5:82','Dájúdájú o máa rí i pé àwọn ènìyàn tí ọ̀tá wọn le jùlọ sí àwọn tó gbàgbọ́ ní òdodo ni àwọn yẹhudi àti àwọn ọ̀ṣẹbọ. Dájúdájú o sì máa rí i pé àwọn ènìyàn tó súnmọ́ àwọn tó gbàgbọ́ ní òdodo jùlọ ní ìfẹ́ ni àwọn tó wí pé: “Dájúdájú nasọ̄rọ̄ ni àwa.” Ìyẹn nítorí pé àwọn àlùfáà àti olùfọkànsìn ń bẹ láààrin wọn. Àti pé dájúdáju wọn kò níí ṣègbéraga (sí òdodo).');
INSERT INTO "aykyuni" VALUES(5,83,'5:83','Nígbà tí wọ́n bá gbọ́ ohun tí A sọ̀kalẹ̀ fún Òjíṣẹ́ náà, o máa rí ẹyinjú wọn tí ó máa damije nítorí ohun tí wọ́n ti mọ̀ nínú òdodo. Wọ́n á sì sọ pé: “Olúwa wa, a gbàgbọ́ ní òdodo, kọ wá mọ́ ara àwọn olùjẹ́rìí (òdodo).');
INSERT INTO "aykyuni" VALUES(5,84,'5:84','Kí ni ó máa dí wa lọ́wọ́ tí a ò fi níí gbàgbọ́ nínú Allāhu àti ohun tí ó dé bá wa nínú òdodo (ìyẹn, al-Ƙur’ān), tí a sì ń jẹ̀rankàn pé kí Olúwa wa fi wá sínú àwọn ẹni-ire.”');
INSERT INTO "aykyuni" VALUES(5,85,'5:85','Nítorí náà, nítorí ohun tí wọ́n sọ, Allāhu san wọ́n ní ẹ̀san pẹ̀lú àwọn Ọgbà Ìdẹ̀ra, tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀. Ìyẹn sì ni ẹ̀san fún àwọn olùṣe-rere.');
INSERT INTO "aykyuni" VALUES(5,86,'5:86','Àwọn tó sì ṣàì gbàgbọ́, tí wọ́n pe àwọn āyah Wa nírọ́, àwọn wọ̀nyẹn ni èrò inú iná Jẹhīm.1 ');
INSERT INTO "aykyuni" VALUES(5,87,'5:87','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe sọ àwọn n̄ǹkan dáadáa tí Allāhu ṣe ní ẹ̀tọ́ fún yín di èèwọ̀. Ẹ sì má ṣe tayọ ẹnu-ààlà. Dájúdájú Allāhu kò fẹ́ràn àwọn olùtayọ ẹnu-ààlà.');
INSERT INTO "aykyuni" VALUES(5,88,'5:88','Ẹ jẹ n̄ǹkan ẹ̀tọ́ tó dára nínú ohun tí Allāhu ṣe ní arísìkí fún yín. Àti pé ẹ bẹ̀rù Allāhu, Ẹni tí ẹ̀yin gbàgbọ́ nínú Rẹ̀.');
INSERT INTO "aykyuni" VALUES(5,89,'5:89','Allāhu kò níí fí ìbúra tí ó bọ́ lẹ́nu yín bi yín, ṣùgbọ́n Ó máa fi àwọn ìbúra tí ẹ fínnúfíndọ̀ mú wá bi yín. Nítorí náà, ìtánràn rẹ̀ ni bíbọ́ tálíkà mẹ́wàá pẹ̀lú oúnjẹ tí ó wà ní ìwọ̀ntún-wọ̀nsì tí ẹ̀ ń fi bọ́ ará ilé yín, tàbí kí ẹ raṣọ fún wọn, tàbí kí ẹ tú ẹrú kan sílẹ̀ lóko ẹrú. Ẹni tí kò bá rí (èyí ṣe), ó máa gba ààwẹ̀ ọjọ́ mẹ́ta. Ìyẹn ni ìtánràn ìbúra yín nígbà tí ẹ bá búra. Ẹ ṣọ́ ìbúra yín. Báyẹn ni Allāhu ṣe ń ṣàlàyé àwọn āyah Rẹ̀ fún yín, nítorí kí ẹ lè dúpẹ́.');
INSERT INTO "aykyuni" VALUES(5,90,'5:90','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, dájúdájú ọtí,tẹ́tẹ́, àwọn òrìṣà àti iṣẹ́ yíyẹ̀wò2, ẹ̀gbin nínú iṣẹ́ Èṣù ni. Nítorí náà, ẹ jìnnà sí i nítorí kí ẹ lè jèrè. ');
INSERT INTO "aykyuni" VALUES(5,91,'5:91','Ohun tí Èṣù ń fẹ́ ni pé ó máa dá ọ̀tá àti ìkórira sílẹ̀ láààrin yín níbi ọtí àti tẹ́tẹ́. Ó sì fẹ́ ṣẹ yín lórí kúrò níbi ìrántí Allāhu àti níbi ìrun kíkí. Ṣé ẹ̀yin kò níí jáwọ́ (níbi iṣẹ́ Èṣù) ni?');
INSERT INTO "aykyuni" VALUES(5,92,'5:92','Ẹ tẹ̀lé ti Allāhu, ẹ tẹ̀lé ti Òjíṣẹ́. Kí ẹ sì ṣọ́ra. Nítorí náà, tí ẹ bá gbúnrí, kí ẹ mọ̀ pé ìkéde (ẹ̀sìn) tó yanjú ni ojúṣe Òjíṣẹ́ Wa.');
INSERT INTO "aykyuni" VALUES(5,93,'5:93','Kò sí ìbáwí fún àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣiṣẹ́ rere nípa ohun tí wọ́n jẹ (nínú ouńjẹ ṣíwájú òfin) nígbà tí wọ́n bá ti bẹ̀rù Allāhu, tí wọ́n sì gbàgbọ́ ní òdodo, tí wọ́n tún ṣiṣẹ́ rere, lẹ́yìn náà, tí wọ́n bẹ̀rù Allāhu, tí wọ́n sì gbàgbọ́ ní òdodo, lẹ́yìn náà, tí wọ́n bẹ́rù Allāhu, tí wọ́n tún ṣiṣẹ́ rere. Allāhu sì fẹ́ràn àwọn olùṣe-rere.1 ');
INSERT INTO "aykyuni" VALUES(5,94,'5:94','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, dájúdájú Allāhu yóò fi kiní kan dan yín wò níbi ẹran-ìgbẹ́ tí ọwọ́ yín àti ọ̀kọ̀ yín bà (nínú aṣọ hurumi) nítorí kí Allāhu lè ṣàfi hàn ẹni tó ń páyà Rẹ̀ ní ìkọ̀kọ̀. Ẹnikẹ́ni tí ó bá tayọ ẹnu-ààlà lẹ́yìn ìyẹn, ìyà ẹlẹ́ta-eléro ń bẹ fún un.');
INSERT INTO "aykyuni" VALUES(5,95,'5:95','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe pa ẹran-ìgbẹ́ nígbà tí ẹ bá wà nínú aṣọ hurumi (hajj tàbí ‘umrah) Ẹnikẹ́ni tí ó bá mọ̀ọ́mọ̀ pa á nínú yín, ẹ̀san rẹ̀ ni (pé ó máa pa) irú ohun tí ó pa nínú ẹran ọ̀sìn. Àwọn onídéédé méjì nínú yín l’ó sì máa ṣe ìdájọ́ (òṣùwọ̀n) rẹ̀ (fún un. Ó máa jẹ́) ẹran ọrẹ tí ó máa mú dé Kaaba. Tàbí kí ó fi bíbọ́ àwọn tálíkà ṣe ìtánràn. Tàbí kí ó fi ààwẹ̀ dípò ìyẹn, nítorí kí ó lè tọ́ bí ọ̀ràn rẹ̀ ṣe lágbára tó wò. Allāhu ti mójú kúrò níbi ohun tó ré kọjá. Ẹnikẹ́ni tí ó bá tún padà (mọ̀ọ́mọ̀ dọdẹ nínú aṣọ húrùmí tàbí nínú húrùmí), Allāhu yóò gbẹ̀san lára rẹ̀ rẹ̀. Allāhu sì ni Alágbára, Olùgbẹ̀san. ');
INSERT INTO "aykyuni" VALUES(5,96,'5:96','Wọ́n ṣe odò dídẹ àti jíjẹ oúnjẹ (òkúǹbete) inú rẹ̀ ní ẹ̀tọ́ fún yín. Ó jẹ́ n̄ǹkan ìgbádùn fún ẹ̀yin àti àwọn onírìn-àjò. Wọ́n sì ṣe ìgbẹ́ dídẹ ní èèwọ̀ fún yín nígbà tí ẹ bá wà ní nínú aṣọ hurumi (hajj tàbí ‘umrah). Ẹ bẹ̀rù Allāhu, Ẹni tí wọ́n máa ko yín jọ sí ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(5,97,'5:97','Allāhu ṣe Kaabah Ilé Ọ̀wọ̀ ní àyè ààbò fún àwọn ènìyàn. (N̄ǹkan ààbò náà ni) àwọn oṣù ọ̀wọ̀, àwọn ẹran ọrẹ (tí wọn kò ṣàmì sí lọ́rùn) àti (àwọn ẹran ọrẹ) tí wọ́n ṣàmì sí lọ́rùn. Ìyẹn rí bẹ́ẹ̀ nítorí kí ẹ lè mọ̀ pé dájúdájú Allāhu mọ ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Àti pé dájúdájú Allāhu ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(5,98,'5:98','Ẹ mọ̀ pé dájúdájú Allāhu le níbi ìyà. Àti pé dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(5,99,'5:99','Kò sí ojúṣe kan fún Òjíṣẹ́ náà bí kò ṣe ìkéde (ẹ̀sìn). Allāhu sì mọ ohun tí ẹ̀ ń ṣàfi hàn rẹ̀ àti ohun tí ẹ̀ ń fi pamọ́.');
INSERT INTO "aykyuni" VALUES(5,100,'5:100','Sọ pé: “Ohun burúkú àti ohun dáadáa kò dọ́gba, púpọ̀ ohun burúkú ìbáà jọ ọ́ lójú”. Nítorí náà, ẹ bẹ̀rù Allāhu, ẹ̀yin onílàákàyè, nítorí kí ẹ lè jèrè.”');
INSERT INTO "aykyuni" VALUES(5,101,'5:101','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe máa bèèrè nípa àwọn n̄ǹkan sá. Tí A bá fi hàn yín, ó máa kó ìpalára ba yín. Tí ẹ bá sì bèèrè nípa (àwọn n̄ǹkan tó yẹ láti bèèrè nípa ẹ̀sìn) nígbà tí À ń sọ al-Ƙur’ān kalẹ̀ lọ́wọ́, A máa fi hàn yín. Allāhu ti ṣe àmójúkúrò nípa rẹ̀. Allāhu ni Aláforíjìn, Aláfaradà.1 ');
INSERT INTO "aykyuni" VALUES(5,102,'5:102','Ìjọ kan kúkú ti bèèrè rẹ̀ ṣíwájú yín. Lẹ́yìn náà, wọ́n sì di aláìgbàgbọ́ nípasẹ̀ rẹ̀.');
INSERT INTO "aykyuni" VALUES(5,103,'5:103','Allāhu kò fi ẹran bahīrah, ẹran sā’ibah, ẹran wasīlah àti ẹran hām lọ́lẹ̀.Ṣùgbọ́n àwọn tó ṣàì gbàgbọ́ ń dá àdápa irọ́ mọ́ Allāhu ni. Ọ̀pọ̀lọ́pọ̀ wọn ni kò ṣe làákàyè. ');
INSERT INTO "aykyuni" VALUES(5,104,'5:104','Nígbà tí wọ́n bá sọ fún wọn pé: “Ẹ máa bọ̀ wá síbi ohun tí Allāhu sọ̀kalẹ̀. (Ẹ máa bọ̀ wá) sí ọ̀dọ̀ Òjíṣẹ́ náà.”Wọ́n á wí pé: “Ohun tí a bá àwọn bàbá wa lórí rẹ̀ ti tó wa.” Ṣé pẹ̀lú bí ó ṣe jẹ́ pé àwọn bàbá wọn kò nímọ̀ kan kan, tí wọn kò sì mọ̀nà? ');
INSERT INTO "aykyuni" VALUES(5,105,'5:105','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ̀mí ara yín dọwọ́ yín. Ẹni tí ó ti ṣìnà kò lè kó ìnira ba yín nígbà tí ẹ bá ti mọ̀nà. Ọ̀dọ̀ Allāhu ni ibùpadàsí gbogbo yín pátápátá. Nígbà náà, Ó máa fún yín ní ìró ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(5,106,'5:106','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, (ẹ wá) ẹ̀rí jíjẹ́ láààrin yín nígbà tí (ìpọ́kàkà) ikú bá dé bá ẹnì kan nínú yín tí ó fẹ́ sọ àsọọ́lẹ̀. (Ẹ wá) onídéédé méjì nínú yín.Tàbí àwọn méjì mìíràn yàtọ̀ si yín tí ẹ̀yin bá wà lórí ìrìn-àjò tí àjálù ikú bá fẹ́ ṣẹlẹ̀ si yín.2 Ẹ dá àwọn méjèèjì dúró lẹ́yìn ìrun, kí wọ́n fi Allāhu búra, tí ẹ bá ṣeyèméjì (sí òdodo wọn, kí wọ́n sì sọ pé:) “A ò níí ta ìbúra wa ní iye kan kan, kódà kó jẹ́ ẹbí. A ò sì níí fi ẹ̀rí jíjẹ́ tí Allāhu (pa láṣẹ) pamọ́. (Bí bẹ́ẹ̀ kọ́) nígbà náà, dájúdájú àwa wà nínú àwọn ẹlẹ́ṣẹ̀.” ');
INSERT INTO "aykyuni" VALUES(5,107,'5:107','Tí wọ́n bá sì mọ̀ pé àwọn méjèèjì dá ẹ̀ṣẹ̀ (nípa yíyí àsọọ́lẹ̀ padà tàbí jíjí dúkìá aláìsí), kí àwọn méjì mìíràn nínú ẹbí òkú tí àsọọ́lẹ̀ lẹ́tọ̀ọ́ sì jùlọ,(ìyẹn) ènìyàn méjì tí ó súnmọ́ òkú jùlọ rọ́pò àwọn méjèèjì àkọ́kọ́ (níbi mímú àsọọ́lẹ̀ mìíràn wá fún òkú). Kí àwọn náà sì fi Allāhu búra pé: “Dájúdájú ẹ̀rí jíjẹ́ tiwa jẹ́ òdodo ju ẹ̀rí jíjẹ́ ti àwọn méjèèjì (àkọ́kọ́). A ò sì níí tayọ ẹnu-ààlà. (Bí bẹ́ẹ̀ kọ́) nígbà náà, dájúdájú a ti wà nínú àwọn alábòsí.” ');
INSERT INTO "aykyuni" VALUES(5,108,'5:108','Ìyẹn súnmọ́ jùlọ láti mú ẹ̀rí jíjẹ́ wá ní ojú-pọ̀nnà rẹ̀ tàbí láti (lè là nínú) ìpáyà pé wọ́n yóò da ìbúra kan nù (ìbúra ẹni méjì àkọ́kọ́) lẹ́yìn ìbúra tiwọn (ìbúrà ẹni méjì kejì). Ẹ bẹ̀rù Allāhu, kí ẹ sì gbọ́ràn. Allāhu kò níí fi ọ̀nà mọ ìjọ òbìlẹ̀jẹ́.1 ');
INSERT INTO "aykyuni" VALUES(5,109,'5:109','Ní ọjọ́ tí Allāhu yóò kó àwọn Òjíṣẹ́ jọ, Ó sì máa sọ pé: “Kí ni èsì tí wọ́n fún yín?” Wọ́n á sọ pé: “Kò sí ìmọ̀ kan fún wa (nípa rẹ̀). Dájúdájú Ìwọ, Ìwọ ni Onímọ̀ nípa àwọn ìkọ̀kọ̀.”');
INSERT INTO "aykyuni" VALUES(5,110,'5:110','(Rántí) nígbà tí Allāhu sọ pé: “‘Īsā ọmọ Mọryam, rántí ìdẹ̀ra Mi lórí rẹ àti lórí ìyá rẹ, nígbà tí Mo fi ẹ̀mí Mímọ́ (mọlāika Jibrīl) ràn ọ́ lọ́wọ́, tí o sì ń bá àwọn ènìyàn sọ̀rọ̀ lórí ìtẹ́ ní òpóǹló àti nígbà tí o dàgbà. (Rántí) nígbà tí Mo fún ọ ní ìmọ̀ Tírà, òye ìjìnlẹ̀, at-Taorāh àti al-’Injīl. (Rántí) nígbà tí ò ń mọ n̄ǹkan láti inú amọ̀ bí ìrísí ẹyẹ, tí ò ń fẹ́ atẹ́gùn sínú rẹ̀, tí ó ń di ẹyẹ pẹ̀lú ìyọ̀ǹda Mi. Ò ń wo afọ́jú àti adẹ́tẹ̀ sàn pẹ̀lú ìyọ̀ǹda Mi. (Rántí) nígbà tí ò ń mú àwọn òkú jáde (ní alààyè láti inú sàréè) pẹ̀lú ìyọ̀ǹda Mi. (Rántí) nígbà tí Mo kó àwọn ọmọ ’Isrọ̄’īl lọ́wọ́ ró, nígbà tí o mú àwọn ẹ̀rí tó yanjú wá bá wọn. Àwọn tó ṣàì gbàgbọ́ nínú wọn sì wí pé: “Kí ni èyí bí kò ṣe idán pọ́nńbélé.”1 ');
INSERT INTO "aykyuni" VALUES(5,111,'5:111','(Rántí) nígbà tí Mo fi mọ àwọn ọmọlẹ́yìn rẹnínú ọkàn wọn pé kí wọ́n gbàgbọ́ nínú Èmi àti Òjíṣẹ́ Mi. Wọ́n sọ pé: “A gbàgbọ́. Kí o sì jẹ́rìí pé dájúdájú mùsùlùmí ni wá.” ');
INSERT INTO "aykyuni" VALUES(5,112,'5:112','(Rántí) nígbà tí àwọn ọmọlẹ́yìn rẹ sọ pé: “‘Īsā ọmọ Mọryam, ǹjẹ́ Olúwa rẹ lè sọ ọpọ́n oúnjẹ kan kalẹ̀ fún wa láti sánmọ̀?” Ó sọ pé: “Ẹ bẹ̀rù Allāhu tí ẹ bá jẹ́ onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(5,113,'5:113','Wọ́n sọ pé: “A fẹ́ jẹ nínú rẹ̀, a sì fẹ́ kí ọkàn wa balẹ̀, kí á sì lè mọ̀ pé o ti sọ òdodo fún wa. A sì máa wà nínú àwọn olùjẹ́rìí.”');
INSERT INTO "aykyuni" VALUES(5,114,'5:114','‘Īsā ọmọ Mọryam sọ pé: “Allāhu, Olúwa wa, sọ ọpọ́n oúnjẹ kan kalẹ̀ fún wa láti inú sánmọ̀, kí ó jẹ́ ọdún fún ẹni àkọ́kọ́ wa àti ẹni ìkẹ́yìn wa.Kí ó sì jẹ́ àmì kan láti ọ̀dọ̀ Rẹ. Pèsè fún wa, Ìwọ sì lóore jùlọ nínú àwọn olùpèsè.”2 ');
INSERT INTO "aykyuni" VALUES(5,115,'5:115','Allāhu sọ pé: “Dájúdájú Èmi yóò sọ̀ ọ́ kalẹ̀ fún yín. Ṣùgbọ́n ẹnikẹ́ni tí ó bá ṣàì gbàgbọ́ lẹ́yìn náà nínú yín, dájúdájú Mo máa jẹ ẹ́ níyà kan tí Mi ò fi jẹ ẹnì kan rí nínú gbogbo ẹ̀dá.”');
INSERT INTO "aykyuni" VALUES(5,116,'5:116','(Rántí) nígbà tí Allāhu sọ pé: “‘Īsā ọmọ Mọryam, ṣé ìwọ l’o sọ fún àwọn ènìyàn pé: “Ẹ mú èmi àti ìyá mi ní ọlọ́hun méjì tí ẹ óò máa jọ́sìn fún lẹ́yìn Allāhu?” Ó sọ pé: “Mímọ́ ni fún Ọ, kò tọ́ fún mi láti sọ ohun tí èmi kò lẹ́tọ̀ọ́ (sí láti sọ). Tí mo bá sọ bẹ́ẹ̀, O kúkú ti mọ̀. O mọ ohun tí ń bẹ nínú ẹ̀mí mi. Èmi ò sì mọ ohun tí ń bẹ nínú ẹ̀mí Rẹ. Dájúdájú Ìwọ ni Onímọ̀ nípa àwọn ìkọ̀kọ̀.');
INSERT INTO "aykyuni" VALUES(5,117,'5:117','Èmi kò sọ ohun kan fún wọn bí kò ṣe ohun tí O pa mí láṣẹ rẹ̀ pé: “Ẹ jọ́sìn fún Allāhu, Olúwa mi àti Olúwa yín.” Mo sì jẹ́ ẹlẹ́rìí lórí wọn níwọ̀n ìgbà tí mò ń bẹ láààrin wọn. Ṣùgbọ́n nígbà tí O gbà mí kúrò lọ́wọ́ wọn, Ìwọ ni Olùṣọ́ lórí wọn. Ìwọ sì ni Arínú-róde gbogbo n̄ǹkan.1 ');
INSERT INTO "aykyuni" VALUES(5,118,'5:118','Tí O bá jẹ wọ́n níyà, dájúdájú ẹrú Rẹ ni wọ́n. Tí O bá sì forí jìn wọ́n, dájúdájú Ìwọ, Ìwọ ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(5,119,'5:119','Allāhu sọ pé: “Èyí ni ọjọ́ tí òdodo àwọn olódodo yóò ṣe wọ́n ní àǹfààní. Àwọn Ọgbà Ìdẹ̀ra kan tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ ń bẹ fún wọn. Olùṣegbére ni wọ́n nínú rẹ̀ títí láéláé. Allāhu yọ́nú sí wọn. Wọ́n sì yọ́nú sí (ohun tí Allāhu fún wọn). Ìyẹn ni èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(5,120,'5:120','Ti Allāhu ni ìjọba àwọn sánmọ̀ àti ilẹ̀ àti ohunkóhun tó wà nínú wọn. Òun sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(6,1,'6:1','Gbogbo ẹyìn ń jẹ́ ti Allāhu,Ẹni tí Ó dá àwọn sánmọ̀ àti ilẹ̀. Ó tún dá òkùnkùn àti ìmọ́lẹ̀. Síbẹ̀síbẹ̀ àwọn tó ṣàì gbàgbọ́ ń bá Olúwa wọn wá akẹgbẹ́. ');
INSERT INTO "aykyuni" VALUES(6,2,'6:2','Òun ni Ẹni tí Ó da yín láti inú ẹrẹ̀. Lẹ́yìn náà, Ó fi gbèdéke àkókó kan sí (ìṣẹ̀mí ayé yín). Àti pé gbèdéke àkókò kan (tún wà fún ayé) lọ́dọ̀ Rẹ̀. Lẹ́yìn náà, ẹ tún ń ṣeyèméjì.');
INSERT INTO "aykyuni" VALUES(6,3,'6:3','Òun ni Allāhu (Ẹni tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo) nínú àwọn sánmọ̀ àti nínú ilẹ̀. Ó mọ ìkọ̀kọ̀ yín àti gban̄gba yín. Ó sì mọ ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(6,4,'6:4','Kò sí āyah kan (àti àmì kan) tí ó máa dé bá wọn nínú àwọn āyah (àti àmì) Olúwa wọn, àfi kí wọ́n máa gbúnrí kúrò níbẹ̀.');
INSERT INTO "aykyuni" VALUES(6,5,'6:5','Wọ́n kúkú pe òdodo ní irọ́ nígbà tí ó dé bá wọn. Nítorí náà, láìpẹ́ àwọn ìró ohun tí wọ́n máa ń fi ṣe yẹ̀yẹ́ ń bọ̀ wá bá wọn.');
INSERT INTO "aykyuni" VALUES(6,6,'6:6','Ṣé wọn kò wòye pé ó pọ̀ nínú àwọn ìran tí A ti parẹ́ ṣíwájú wọn? Àwọn tí A fún ní ipò lórí ilẹ̀, (irú) ipò tí A kò fún ẹ̀yin. A sì rọ omi òjò púpọ̀ fún wọn láti sánmọ̀. A sì ṣe àwọn odò tí ń ṣàn sí ìsàlẹ̀ (ilé) wọn. Lẹ́yìn náà, A pa wọ́n rẹ́ nítorí ẹ̀ṣẹ̀ wọn. A sì dá àwọn ìran mìíràn lẹ́yìn wọn.');
INSERT INTO "aykyuni" VALUES(6,7,'6:7','Tí ó bá jẹ́ pé A sọ tírà kan tí A kọ sínú tákàdá kalẹ̀ fún ọ, kí wọ́n sì fi ọwọ́ ara wọn gbá a mú (báyìí), dájúdájú àwọn tó ṣàì gbàgbọ́ ìbá wí pé: “Kí ni èyí bí kò ṣe idán pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(6,8,'6:8','Wọ́n sì wí pé: “Nítorí kí ni Wọn kò ṣe sọ mọlāika kan kalẹ̀ fún un?” Tí ó bá jẹ́ pé A sọ mọlāika kan kalẹ̀, ọ̀rọ̀ ìbá ti yanjú. Lẹ́yìn náà, A ò sì níí lọ́ wọn lára mọ́.');
INSERT INTO "aykyuni" VALUES(6,9,'6:9','Tí ó bá jẹ́ pé A ṣe é ní mọlāika ni, Àwa ìbá ṣe é ní ọkùnrin. Àti pé Àwa ìbá tún fi ohun tí wọ́n ń darú mọ́ra wọn lójú rú wọn lójú.1 ');
INSERT INTO "aykyuni" VALUES(6,10,'6:10','Wọ́n kúkú ti fi àwọn Òjíṣẹ́ kan ṣe yẹ̀yẹ́ ṣíwájú rẹ! Nítorí náà, ohun tí wọ́n ń fi ṣe yẹ̀yẹ́ sì dìyà tó yí àwọn tó ń fi wọ́n ṣe yẹ̀yẹ́ po.');
INSERT INTO "aykyuni" VALUES(6,11,'6:11','Sọ pé: “Ẹ rin orí ilẹ̀ lọ, lẹ́yìn náà kí ẹ wo báwo ni àtubọ̀tán àwọn tó ń pe òdodo nírọ́ ṣe rí.”');
INSERT INTO "aykyuni" VALUES(6,12,'6:12','Sọ pé: “Ti ta ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀?” Sọ pé: “Ti Allāhu ni.” Ó ṣe àánú ní ọ̀ran-anyàn léra Rẹ̀ lórí.Dájúdájú Ó máa ko yín jọ ní Ọjọ́ Àjíǹde, kò sí iyèméjì nínú rẹ̀. Àwọn tó ṣe ẹ̀mí wọn lófò (sínú àìgbàgbọ́), wọn kò níí gbàgbọ́. ');
INSERT INTO "aykyuni" VALUES(6,13,'6:13','TiRẹ̀ ni ohunkóhun tó ń bẹ nínú òru àti ọ̀sán. Òun sì ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(6,14,'6:14','Sọ pé: “Ṣé kí n̄g mú olùrànlọ́wọ́ kan yàtọ̀ sí Allāhu, Ẹlẹ́dàá àwọn sánmọ̀ àti ilẹ̀? Òun sì ni Ó ń bọ́ (ẹ̀dá), wọn kì í bọ́ Ọ.” Sọ pé: “Dájúdájú Wọ́n pa mí láṣẹ pé kí n̄g jẹ́ ẹni àkọ́kọ́ tí ó máa ṣe ’Islām (ní àsìkò tèmi).”Ìwọ kò sì gbọdọ̀ wà nínú àwọn ọ̀ṣẹbọ. ');
INSERT INTO "aykyuni" VALUES(6,15,'6:15','Sọ pé: “Dájúdájú èmi ń páyà ìyà Ọjọ́ Ńlá, tí mo bá fi lè yapa Olúwa mi.”');
INSERT INTO "aykyuni" VALUES(6,16,'6:16','Ẹnikẹ́ni tí wọ́n bá darí rẹ̀ kúrò níbi (ìyà) ní Ọjọ́ yẹn, (Allāhu) ti ṣàánú rẹ̀. Ìyẹn sì ni èrèǹjẹ pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(6,17,'6:17','Tí Allāhu bá fi ìnira kan kàn ọ́, kò sí ẹni tí ó lè mú un kúrò àfi Òun náà. Tí Ó bá sì mú oore kan bá ọ, Òun sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(6,18,'6:18','Òun sì ni Olùborí tó wà lókè àwọn ẹrú Rẹ̀. Òun ni Ọlọ́gbọ́n, Alámọ̀tán.1 ');
INSERT INTO "aykyuni" VALUES(6,19,'6:19','Sọ pé: “Kí ni ohun tí ó tóbi jùlọ ní ẹ̀rí?” Sọ pé: “Allāhu ni Ẹlẹ́rìí láààrin èmi àti ẹ̀yin.” Ó sì fi ìmísí al-Ƙur’ān yìí ránṣẹ́ sí mi, nítorí kí n̄g lè fi ṣe ìkìlọ̀ fun ẹ̀yin àti ẹnikẹ́ni tí (al-Ƙur’ān) bá dé etí ìgbọ́ rẹ̀.Ṣé dájúdájú ẹ̀yin ń jẹ́rìí pé àwọn ọlọ́hun mìíràn tún wà pẹ̀lú Allāhu ni? Sọ pé: “Èmi kò níí jẹ́rìí bẹ́ẹ̀.” Sọ pé: “Òun nìkan ni Ọlọ́hun Ọ̀kan ṣoṣo tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo. Àti pé dájúdájú èmi yọwọ́ yọsẹ̀ nínú ohun tí ẹ̀ ń fi ṣẹbọ (sí Allāhu).” ');
INSERT INTO "aykyuni" VALUES(6,20,'6:20','Àwọn tí A fún ní Tírà, wọ́n mọ̀ ọ́n gẹ́gẹ́ bí wọ́n ṣe mọ àwọn ọmọ wọn. Àwọn tó ṣe ẹ̀mí wọn lófò (sínú àìgbàgbọ́), wọn kò sí níí gbàgbọ́.1 ');
INSERT INTO "aykyuni" VALUES(6,21,'6:21','Ta l’ó sì ṣàbòsí ju ẹni tí ó dá àdápa irọ́ mọ́ Allāhu tàbí (ẹni tí) ó pe àwọn āyah Rẹ̀ nírọ́? Dájúdájú àwọn alábòsí kò níí jèrè.');
INSERT INTO "aykyuni" VALUES(6,22,'6:22','Àti pé (rántí) Ọjọ́ tí A óò kó gbogbo wọn jọ pátápátá, lẹ́yìn náà A óò sọ fún àwọn tó bá (Allāhu) wá akẹgbẹ́ pé: “Níbo ni àwọn òrìṣà yín wà, àwọn tí ẹ̀ ń sọ nípa wọn láì ní ẹ̀rí lọ́wọ́ pé wọ́n jẹ́ akẹgbẹ́ Allāhu?”');
INSERT INTO "aykyuni" VALUES(6,23,'6:23','Lẹ́yìn náà, ìfòòró wọn (lórí ìbéèrè náà) kò jẹ́ kiní kan tayọ pé wọ́n á wí pé: “A fi Allāhu Olúwa wa búra, àwa kì í ṣe ọ̀ṣẹbọ.”');
INSERT INTO "aykyuni" VALUES(6,24,'6:24','Wo bí wọ́n ṣe parọ́ mọ́ra wọn. Ohun tí wọ́n ń dá ní àdápa irọ́ sì di òfo mọ́ wọn lọ́wọ́.1 ');
INSERT INTO "aykyuni" VALUES(6,25,'6:25','Ẹni tí ń gbọ́ ọ̀rọ̀ ní ọ̀dọ̀ rẹ ń bẹ nínú wọn. A sì fi èbìbò bo ọkàn wọn kí wọ́n má baà gbọ́ àgbọ́yé rẹ̀. (A tún fi) èdídí sínú etí wọn. Tí wọ́n bá rí gbogbo āyah (àmì), wọn kò níí gbà á gbọ́ débi pé nígbà tí wọ́n bá wá bá ọ, wọn yó sì máa bá ọ jiyàn; àwọn tó ṣàì gbàgbọ́ sì máa wí pé: “Kí ni èyí bí kò ṣe àkọsílẹ̀ àlọ́ àwọn ẹni àkọ́kọ́.”');
INSERT INTO "aykyuni" VALUES(6,26,'6:26','Àwọn ni wọ́n ń kọ̀ (fún àwọn ènìyàn láti tẹ̀lé Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -.), àwọn náà sì ń takété sí i. Wọn kò sì kó ìparun bá ẹnikẹ́ni bí kò ṣe ẹ̀mí ara wọn; wọn kò sì fura.');
INSERT INTO "aykyuni" VALUES(6,27,'6:27','Tí ó bá jẹ́ pé o rí (wọn ni) nígbà tí wọ́n bá dá wọn dúró síbi Iná, wọ́n sì máa wí pé: “Yéè! Kí ó sì jẹ́ pé wọ́n dá wa padà (sílé ayé), àwa kò sì níí pe àwọn āyah Olúwa wa nírọ́ (mọ́), a sì máa wà nínú àwọn onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(6,28,'6:28','Kò rí bẹ́ẹ̀, ohun tí wọ́n ń fi pamọ́ láti ẹ̀yìn wá ti hàn sí wọn ni. Tí ó bá jẹ́ pé wọ́n bá dá wọn padà (sílé ayé), wọn yóò kúkú padà síbi ohun tí A kọ̀ fún wọn. Dájúdájú òpùrọ́ mà ni wọ́n.');
INSERT INTO "aykyuni" VALUES(6,29,'6:29','Wọ́n wí pé: “Kí ni (ó tún ń jẹ́ ìṣẹ̀mí ọ̀run) bí kò ṣe ìṣẹ̀mí wa nílé ayé; Wọn kò sì níí gbé wa dìde (ní ọ̀run).”');
INSERT INTO "aykyuni" VALUES(6,30,'6:30','Tí ó bá jẹ́ pé o rí (wọn ni) nígbà tí wọ́n bá dá wọn dúró sí ọ̀dọ̀ Olúwa wọn, Ó sì máa sọ pé: “Ṣé èyí kì í ṣe òdodo bí?” Wọ́n á sì wí pé: “Bẹ́ẹ̀ ni (òdodo ni), Olúwa wa.” (Allāhu) sọ pé: “Nítorí náà, ẹ tọ́ Ìyà wò nítorí pé ẹ máa ń ṣàì gbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(6,31,'6:31','Dájúdájú àwọn tó pe pípàdé Allāhu (lọ́run) nírọ́ ti ṣòfò débi pé nígbà tí Àkókò náà bá dé bá wọn lójijì, wọ́n á wí pé: “A ká àbámọ̀ lórí ohun tí a fi jáfira.” Wọ́n sì máa ru ẹ̀ṣẹ̀ wọn sẹ́yìn wọn. Kíyè sí i, ohun tí wọn yóò rù lẹ́ṣẹ̀ sì burú.');
INSERT INTO "aykyuni" VALUES(6,32,'6:32','Ìṣẹ̀mí ayé kò jẹ́ kiní kan bí kò ṣe eré àti ìranù. Ọgbà Ìkẹ́yìn sì lóore jùlọ fún àwọn tó ń bẹ̀rù (Allāhu). Ṣé ẹ ò ṣe làákàyè ni?');
INSERT INTO "aykyuni" VALUES(6,33,'6:33','A kúkú ti mọ̀ pé ohun tí wọ́n ń wí ń bà ọ́ nínú jẹ́. Dájúdájú wọn kò lè pè ọ́ ní òpùrọ́, ṣùgbọ́n àwọn alábòsí ń tako àwọn āyah Allāhu ni.');
INSERT INTO "aykyuni" VALUES(6,34,'6:34','Wọ́n kúkú ti pe àwọn Òjíṣẹ́ kan lópùrọ́ ṣíwájú rẹ. Wọ́n sì ṣe sùúrù lórí n̄ǹkan tí wọ́n fi pè wọ́n ní òpùrọ́. Wọ́n sì fi ìnira kàn wọ́n títí di ìgbà tí àrànṣe Wa fi dé bá wọn. Kò sì sí aláyìípadà kan fún àwọn ọ̀rọ̀ Allāhu.Dájúdájú ìró àwọn Òjíṣẹ́ ti dé bá ọ. ');
INSERT INTO "aykyuni" VALUES(6,35,'6:35','Tí ó bá sì jẹ́ pé gbígbúnrí wọn lágbára lára rẹ, nígbà náà tí o bá lágbára láti wá ihò kan sínú (àjà) ilẹ̀, tàbí àkàbà kan sínú sánmọ̀ (ṣe bẹ́ẹ̀) kí o lè mú àmì kan wá fún wọn. Àti pé tí ó bá jẹ́ pé Allāhu bá fẹ́, dájúdájú ìbá kó wọn jọ papọ̀ sínú ìmọ̀nà(’Islām). Nítorí náà, o ò gbọdọ̀ wà lára àwọn aláìmọ̀kan.');
INSERT INTO "aykyuni" VALUES(6,36,'6:36','Ọ̀wọ́ ẹni tó ń jẹ́pè ni àwọn tó ń gbọ́rọ̀. (Ní ti) àwọn òkú, Allāhu yó gbé wọn dìde. Lẹ́yìn náà, ọ̀dọ̀ Rẹ̀ ni wọn yóò dá wọn padà sí.');
INSERT INTO "aykyuni" VALUES(6,37,'6:37','Wọ́n tún wí pé: “Nítorí kí ni wọn kò ṣe sọ àmì kan kalẹ̀ fún un láti ọ̀dọ̀ Olúwa rẹ̀?” Sọ pé: “Dájúdájú Allāhu lágbára láti sọ àmì kan kalẹ̀, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò mọ̀.”');
INSERT INTO "aykyuni" VALUES(6,38,'6:38','Kò sí ohun abẹ̀mí kan (tó ń rìn) lórí ilẹ̀, tàbí ẹyẹ kan tó ń fò pẹ̀lú apá rẹ̀ méjèèjì bí kò ṣe àwọn ẹ̀dá kan (bí) irú yín. A kò fi kiní kan sílẹ̀ (láì ṣàkọsílẹ̀ rẹ̀) sínú Tírà (ìyẹn, ummul-kitāb). Lẹ́yìn náà, ọ̀dọ̀ Olúwa wọn ni wọn yóò kó wọn jọ sí.');
INSERT INTO "aykyuni" VALUES(6,39,'6:39','Àwọn tó pe àwọn āyah Wa nírọ́, adití àti ayaya tó wà nínú àwọn òkùnkùn ni wọ́n. Ẹnikẹ́ni tí Allāhu bá fẹ́, Ó máa ṣì í lọ́nà. Ẹnikẹ́ni tí Ó bá sì fẹ́, Ó máa fi sójú ọ̀nà tààrà (’Islām).');
INSERT INTO "aykyuni" VALUES(6,40,'6:40','Sọ pé: “Ẹ sọ fún mi, tí ìyà Allāhu bá dé ba yín tàbí tí Àkókò náà bá dé ba yín, ṣe n̄ǹkan mìíràn lẹ́yìn Allāhu ni ẹ̀yin máa pè, tí ẹ bá jẹ́ olódodo!”');
INSERT INTO "aykyuni" VALUES(6,41,'6:41','Bẹ́ẹ̀ ni, Òun (nìkan) lẹ máa pè. Ó sì máa mú (ìnira) tí ẹ̀ ń pè É sí kúrò (fún yín) tí Ó bá fẹ́. Ẹ̀yin yó sì gbàgbé ohun tí ẹ̀ ń fi ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(6,42,'6:42','Dájúdájú A ti ránṣẹ́ sí àwọn ìjọ kan ṣíwájú rẹ. A sì fi àwọn ìpọ́njú àti àwọn àìlera gbá wọn mú nítorí kí wọ́n lè rawọ́ rasẹ̀ (sí Allāhu).1 ');
INSERT INTO "aykyuni" VALUES(6,43,'6:43','Wọn kò ṣe rawọ́ rasẹ̀ (sí Wa) nígbà tí ìyà Wa dé bá wọn! Ṣùgbọ́n ọkàn wọn le koko. Èṣù sì ṣe ohun tí wọ́n ń ṣe níṣẹ́ ní ọ̀ṣọ́ fún wọn.');
INSERT INTO "aykyuni" VALUES(6,44,'6:44','Nítorí náà, nígbà tí wọ́n gbàgbé ohun tí A fi ṣe ìrántí fún wọn, A ṣí àwọn ọ̀nà gbogbo n̄ǹkan sílẹ̀ fún wọn, títí di ìgbà tí wọ́n yọ àyọ̀pọ̀rọ́ sí ohun tí A fún wọn (nínú oore ayé.), A sì mú wọn lójijì. Wọ́n sì di olùsọ̀rètínù.');
INSERT INTO "aykyuni" VALUES(6,45,'6:45','Nítorí náà, A pa ìjọ tó ṣàbòsí run pátápátá. Gbogbo ẹyìn ń jẹ́ ti Allāhu nìkan ṣoso, Olúwa gbogbo ẹ̀dá.1 ');
INSERT INTO "aykyuni" VALUES(6,46,'6:46','Sọ pé: “Ẹ sọ fún mi, tí Allāhu bá gba ìgbọ́rọ̀ yín àti ìríran yín, tí Ó sì di ọkàn yín pa, ọlọ́hun wo lẹ́yìn Allāhu ni ó máa mú un wá fún yín? Wo bí A ṣe ń mú àwọn āyah wá lóníran-ànran ọ̀nà. Lẹ́yìn náà, wọ́n sì ń gbúnrí (kúrò níbi òdodo)!');
INSERT INTO "aykyuni" VALUES(6,47,'6:47','Sọ pé: “Ẹ sọ fún mi, tí ìyà Allāhu bá dé ba yín ní òjijì tàbí ní ojúkojú, ṣé wọ́n máa pa ẹnì kan run bí kò ṣe ìjọ alábòsí!”');
INSERT INTO "aykyuni" VALUES(6,48,'6:48','A kò rán àwọn Òjíṣẹ́ náà níṣẹ́ bí kò ṣe pé (kí wọ́n jẹ́) oníròó ìdùnnú àti olùkìlọ̀. Nítorí náà, ẹnikẹ́ni tí ó bá gbàgbọ́ ní òdodo, tí ó sì ṣe àtúnṣe (iṣẹ́ rẹ̀), kò níí sí ìbẹ̀rù fún wọn, wọn kò sì níí banújẹ́.');
INSERT INTO "aykyuni" VALUES(6,49,'6:49','Àwọn tó sì pe àwọn āyah Wa nírọ́, ọwọ́ ìyà yóò tẹ̀ wọ́n nítorí pé wọ́n máa ń ṣèbàjẹ́.');
INSERT INTO "aykyuni" VALUES(6,50,'6:50','Sọ pé: “Èmi kò sọ fún yín pé àwọn ilé-ọrọ̀ Allāhu wà lọ́dọ̀ mi, èmi kò sì nímọ̀ ìkọ̀kọ̀. Èmi kò sì sọ fún yín pé mọlāika kan ni mí. Èmi kò tẹ̀lé kiní kan àyàfi ohun tí wọ́n fi ránṣẹ́ sí mi ní ìmísí.” Sọ pé: “Ǹjẹ́ afọ́jú àti olùríran dọ́gba bí?Ṣé ẹ ò ronú jinlẹ̀ ni?” ');
INSERT INTO "aykyuni" VALUES(6,51,'6:51','Fi (al-Ƙur’ān) ṣe ìkìlọ̀ fún àwọn tó ń páyà pé wọ́n máa kó àwọn jọ sí ọ̀dọ̀ Olúwa wọn, kò sì níí sí aláàbò tàbí olùṣìpẹ̀ kan fún wọn lẹ́yìn Rẹ̀.(Kìlọ̀ fún wọn) kí wọ́n lè bẹ̀rù (Allāhu). ');
INSERT INTO "aykyuni" VALUES(6,52,'6:52','Má ṣe lé àwọn tó ń pe Olúwa wọn ní òwúrọ̀ àti ní àṣálẹ́ dànù; wọ́n ń fẹ́ Ojú rere Rẹ̀ ni. Ìṣirò-iṣẹ́ wọn kò sí ní ọrùn rẹ ní ọ̀nà kan kan. Kò sì sí ìṣirò-iṣẹ́ tìrẹ náà ní ọrùn wọn ní ọ̀nà kan kan. Tí o bá lé wọn dànù, o sì máa wà nínú àwọn alábòsí.1 ');
INSERT INTO "aykyuni" VALUES(6,53,'6:53','Báyẹn ni A ṣe fi apá kan wọn ṣe àdánwò fún apá kan nítorí kí (àwọn aláìgbàgbọ́) lè wí pé: “Ṣé àwọn (mùsùlùmí aláìní) wọ̀nyí náà ni Allāhu ṣe ìdẹ̀ra (ìmọ̀nà) fún láààrin wa!?” Ṣé Allāhu kọ́ l’Ó nímọ̀ jùlọ nípa àwọn olùdúpẹ́ ni?');
INSERT INTO "aykyuni" VALUES(6,54,'6:54','Nígbà tí àwọn tó gba àwọn āyah Wa gbọ́ bá wá bá ọ, sọ (fún wọn) pé: “Kí àlàáfíà máa ba yín. Olúwa yín ṣe àánú ní ọ̀ran-anyàn léra Rẹ̀ lórí pé, dájúdájú ẹnikẹ́ni nínú yín tí ó bá ṣe iṣẹ́ aburú pẹ̀lú àìmọ̀kan, lẹ́yìn náà, tí ó ronú pìwàdà lẹ́yìn rẹ̀, tí ó sì ṣe àtúnṣe, dájúdájú Òun ni Aláforíjìn, Àṣàkẹ́-ọ̀run.”1 ');
INSERT INTO "aykyuni" VALUES(6,55,'6:55','Báyẹn ni A ṣe ń ṣàlàyé àwọn āyah náà nítorí kí ọ̀nà àwọn ẹlẹ́ṣẹ̀ lè fojú hàn kedere.');
INSERT INTO "aykyuni" VALUES(6,56,'6:56','Sọ pé: “Dájúdájú Wọ́n kọ̀ fún mi láti jọ́sìn fún àwọn tí ẹ̀ ń pè lẹ́yìn Allāhu.” Sọ pé: “Èmi kò níí tẹ̀lé ìfẹ́-inú yín. Bí bẹ́ẹ̀ kọ́ nígbà náà, mo ti ṣìnà, èmi kò sì sí nínú àwọn olùmọ̀nà (tí mo bá tẹ̀lé ìfẹ́-inú yín).”');
INSERT INTO "aykyuni" VALUES(6,57,'6:57','Sọ pé: “Dájúdájú mo wà lórí ẹ̀rí tó yanjú láti ọ̀dọ̀ Olúwa mi, ẹ̀yin sì pè é nírọ́. Kò sí ohun tí ẹ̀ ń wá pẹ̀lú ìkánjú ní ọ̀dọ̀ mi. Kò sí ìdájọ́ náà (fún ẹnikẹ́ni) àyàfi fún Allāhu, Ẹni tí ń sọ (ìdájọ́) òdodo. Ó sì lóore jùlọ nínú àwọn olùdájọ́.”1 ');
INSERT INTO "aykyuni" VALUES(6,58,'6:58','Sọ pé: “Tí ó bá jẹ́ pé dájúdájú ohun tí ẹ̀ ń wá pẹ̀lú ìkánjú ń bẹ ní ọ̀dọ̀ mi ni, Wọn ìbá ti ṣe ìdájọ́ ọ̀rọ̀ náà láààrin èmi àti ẹ̀yin. Allāhu sì nímọ̀ jùlọ nípa àwọn alábòsí.”');
INSERT INTO "aykyuni" VALUES(6,59,'6:59','Ọ̀dọ̀ Rẹ̀ sì ni àwọn kọ́kọ́rọ́ ìkọ̀kọ̀ wà. Kò sí ẹni tó nímọ̀ rẹ̀ àfi Òun. Ó nímọ̀ ohun tí ń bẹ nínú ilẹ̀ àti odò. Ewé kan kò sì níí já bọ́ àfi kí Ó nímọ̀ rẹ̀. Kò sì sí kóró èso kan nínú òkùnkùn (inú) ilẹ̀, kò sí ohun tútù tàbí gbígbẹ kan àfi kí ó wà nínú àkọsílẹ̀ tó yanjú.');
INSERT INTO "aykyuni" VALUES(6,60,'6:60','Òun ni Ẹni tó ń kùn yín ní oorun ní alẹ́.Ó sì nímọ̀ nípa ohun tí ẹ ṣe níṣẹ́ ní ọ̀sán. Lẹ́yìn náà, Ó ń gbe yín dìde (fún ìjẹ-ìmu) ní (ọ̀sán) nítorí kí wọ́n lè parí gbèdéke àkókò kan. Lẹ́yìn náà, ọ̀dọ̀ Rẹ̀ ni ibùpadàsí yín. Lẹ́yìn náà, Ó máa fún yín ní ìró ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(6,61,'6:61','Òun ni Olùborí tó wà lókè àwọn ẹrú Rẹ̀.Ó sì ń rán àwọn ẹ̀ṣọ́ kan (nínú àwọn mọlāikah) si yín títí di ìgbà tí ikú yóò fi dé bá ẹnì kọ̀ọ̀kan yín. Àwọn Òjíṣẹ́ wa yó sì gba ẹ̀mí rẹ̀, wọn kò sì níí jáfira. ');
INSERT INTO "aykyuni" VALUES(6,62,'6:62','Lẹ́yìn náà, wọ́n yóò dá wọn padà sí ọ̀dọ̀ Allāhu Olúwa wọn Òdodo. Kíyè sí i, tiRẹ̀ ni ìdájọ́. Ó sì yára jùlọ nínú àwọn olùṣírò.1 ');
INSERT INTO "aykyuni" VALUES(6,63,'6:63','Sọ pé: “Ta ni ẹni tó ń gbà yín là nínú òkùnkùn ilẹ̀ àti ibúdò, Ẹni tí ẹ̀ ń pè pẹ̀lú ìrawọ́rasẹ̀ àti ní ìkọ̀kọ̀ pé: “Dájúdájú tí Ó bá gbà wá là nínú èyí, dájúdájú àwa yóò wà nínú àwọn olùdúpẹ́ (fún Un)?”');
INSERT INTO "aykyuni" VALUES(6,64,'6:64','Sọ pé: “Allāhu l’Ó ń gbà yín là nínú rẹ̀ àti nínú gbogbo ìbànújẹ́. Lẹ́yìn náà, ẹ tún ń ṣẹbọ.”');
INSERT INTO "aykyuni" VALUES(6,65,'6:65','Sọ pé: “Ó lágbára láti fi ìyà ránṣẹ́ si yín láti òkè yín tàbí láti ìsàlẹ̀ ẹsẹ̀ yín, tàbí kí Ó dà yín pọ̀ mọ́ onírúurú ìjọ, nítorí kí Ó lè mu apá kan yín fìnira kan apá kan. Wo bí A ṣe ń mú àwọn āyah wá lóníran-ànran ọ̀nà nítorí kí wọ́n lè gbọ́ àgbọ́yé.”');
INSERT INTO "aykyuni" VALUES(6,66,'6:66','Àwọn ènìyàn rẹ pe al-Ƙur’ān nírọ́! (Àmọ́) òdodo ni. Sọ pé: “Èmi kì í ṣe olùṣọ́ lórí yín.”');
INSERT INTO "aykyuni" VALUES(6,67,'6:67','Gbogbo ìró (àsọtẹ́lẹ̀) l’ó máa wá sí ìmúṣẹ. Láìpẹ́ ẹ máa mọ̀.');
INSERT INTO "aykyuni" VALUES(6,68,'6:68','Nígbà tí o bá rí àwọn tó ń sọ ìsọkúsọ nípa àwọn āyah Wa, nígbà náà ṣẹ́rí kúrò ní ọ̀dọ̀ wọn títí wọn yóò fi bọ́ sínú ọ̀rọ̀ mìíràn. Tí Èṣù bá ń mú ọ gbàgbé (tẹ́lẹ̀), ní báyìí lẹ́yìn ìrántí má ṣe jókòó ti ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(6,69,'6:69','Kiní kan nínú ìṣírò-iṣẹ́ wọn kò sí lọ́rùn àwọn tó ń bẹ̀rù (Allāhu), ṣùgbọ́n ìṣítí ni nítorí kí wọ́n lè ṣọ́ra (fún Iná).');
INSERT INTO "aykyuni" VALUES(6,70,'6:70','Pa àwọn tó sọ ẹ̀sìn wọn di eré ṣíṣe àti ìranù tì. Ìṣẹ̀mí ayé sì tàn wọ́n jẹ. Fi al-Ƙur’ān ṣe ìṣítí nítorí kí wọ́n má baà fa ẹ̀mí kalẹ̀ sínú ìparun nípasẹ̀ ohun tí ó ṣe níṣẹ́ (aburú). Kò sì sí aláàbò tàbí olùṣìpẹ̀ kan fún un lẹ́yìn Allāhu. Tí ó bá sì fi gbogbo ààrọ̀ ṣèràpadà, A ò níí gbà á lọ́wọ́ rẹ̀.Àwọn wọ̀nyẹn ni àwọn tí wọ́n fà kalẹ̀ fún ìparun nípasẹ̀ ohun tí wọ́n ṣe níṣẹ́. Ohun mímu tó gbóná parí àti ìyà ẹlẹ́ta eléro ń bẹ fún wọn nítorí pé wọ́n máa ń ṣàì gbàgbọ́. ');
INSERT INTO "aykyuni" VALUES(6,71,'6:71','Sọ pé: “Ṣé a óò máa pè lẹ́yìn Allāhu, ohun tí kò lè ṣe wá ní àǹfààní, tí kò sì lè kó ìnira bá wa; (ṣé) kí wọ́n tún dá wa padà sí ẹsẹ̀-àárọ̀ wa lẹ́yìn ìgbà tí Allāhu ti fi ọ̀nà mọ̀ wá (kí á lè dà) bí ẹni tí àwọn èṣù tàn lọ (sínú igbó), ó sì ń dààmú kiri lórí ilẹ̀, ó sì kúkú ní àwọn ọ̀rẹ́ (alábàárìn) kan tí wọ́n ń pè é sí ọ̀nà (ilé) pé, “Máa bọ̀ wá bá wa.” (àmọ́ kò gbọ́ mọ́). Sọ pé: “Dájúdájú ìmọ̀nà ti Allāhu (’Islām), òhun ni ìmọ̀nà. Wọ́n sì pa wá láṣẹ pé kí á gba ẹ̀sìn ’Islām nítorí ti Olúwa gbogbo ẹ̀dá.”');
INSERT INTO "aykyuni" VALUES(6,72,'6:72','Àti pé kí ẹ kírun, kí ẹ sì bẹ̀rù Allāhu. Òun ni Ẹni tí wọ́n yóò ko yín jọ sí ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(6,73,'6:73','Òun ni Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ pẹ̀lú òdodo. Àti pé (rántí) ọjọ́ tí (Allāhu máa yí ilẹ̀ àti sánmọ̀ padà sí n̄ǹkan mìíràn) Ó sì máa sọ pé: “Jẹ́ bẹ́ẹ̀.” Ó sì máa jẹ́ bẹ́ẹ̀. Òdodo ni ọ̀rọ̀ Rẹ̀. TiRẹ̀ ni ìjọba ní ọjọ́ tí wọ́n á fọn fèrè oníwo fún àjíǹde. Onímọ̀-ìkọ̀kọ̀ àti gban̄gba ni. Òun sì ni Ọlọ́gbọ́n, Alámọ̀tán. ');
INSERT INTO "aykyuni" VALUES(6,74,'6:74','(Rántí) nígbà tí ’Ibrọ̄hīm sọ fún bàbá rẹ̀ Āzar (pé): “Ṣé o máa sọ àwọn ère òrìṣà di ọlọ́hun ni? Dájúdájú èmi rí ìwọ àti àwọn ènìyàn rẹ nínú ìṣìnà pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(6,75,'6:75','Báyẹn ni wọ́n ṣe fi (àwọn àmì) ìjọba Allahu tí ń bẹ nínú àwọn sánmọ̀ àti ilẹ̀ han (Ànábì) ’Ibrọ̄hīm nítorí kí ó lè wà nínú àwọn alámọ̀dájú.');
INSERT INTO "aykyuni" VALUES(6,76,'6:76','Nígbà tí òkùnkùn alẹ́ bò ó mọ́lẹ̀, ó rí ìràwọ̀ kan, ó sọ pé: “Èyí ni olúwa mi.” Nígbà tí ó wọ̀, ó sọ pé: “Èmi kò nífẹ̀ẹ́ sí àwọn (olúwa) tó ń wọ̀ọ̀kùn.”');
INSERT INTO "aykyuni" VALUES(6,77,'6:77','Nígbà tí ó rí òṣùpá tó yọ, ó sọ pé: “Èyí ni olúwa mi.” Nígbà tí ó wọ̀, ó sọ pé: “Dájúdájú tí Olúwa mi kò bá tọ́ mi sọ́nà, dájúdájú mo máa wà nínú àwọn olùṣìnà ènìyàn.”');
INSERT INTO "aykyuni" VALUES(6,78,'6:78','Nígbà tí ó rí òòrùn tó yọ, ó sọ pé: “Èyí ni olúwa mi; èyí tóbi jùlọ.” Nígbà tó wọ̀, ó sọ pé: “Ẹ̀yin ènìyàn mi, dájúdájú èmi yọwọ́ yọsẹ̀ nínú ohun tí ẹ ń fi ṣẹbọ (sí Allāhu).1 ');
INSERT INTO "aykyuni" VALUES(6,79,'6:79','Dájúdájú èmi dojú mi kọ Ẹni tí Ó pilẹ̀ ìṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀, (mo) dúró déédé (sínú ’Islām fún Un). Èmi kò sì sí nínú àwọn ọ̀ṣẹbọ.”');
INSERT INTO "aykyuni" VALUES(6,80,'6:80','Àwọn ènìyàn rẹ̀ sì jà á níyàn. Ó sọ pé: "Ṣé ẹ̀yin yóò jà mí níyàn nípa Allāhu, Ó sì ti fi ọ̀nà mọ̀ mí? Èmi kò sì páyà (àwọn òrìṣà) tí ẹ sọ di akẹgbẹ́ fún Un, àfi bí Allāhu bá fẹ́ kiní kan (pé kó ṣẹlẹ̀). Olúwa mi fi ìmọ̀ gbòòrò ju gbogbo n̄ǹkan. Nítorí náà, ṣé ẹ ò níí lo ìrántí ni?');
INSERT INTO "aykyuni" VALUES(6,81,'6:81','Báwo ni èmi yóò ṣe páyà (àwọn òrìṣà) tí ẹ sọ di akẹgbẹ́ fún Allāhu, tí ẹ̀yin kò sì páyà pé ẹ̀ ń bá Allāhu wá akẹgbẹ́ pẹ̀lú ohun tí kò sọ ẹ̀rí kan kalẹ̀ fún yín lórí rẹ̀? Èwo nínú ikọ̀ méjèèjì l’ó lẹ́tọ̀ọ́ jùlọ sí ìfọ̀kànbalẹ̀ tí ẹ bá nímọ̀?');
INSERT INTO "aykyuni" VALUES(6,82,'6:82','Àwọn tó gbàgbọ́ ní òdodo, tí wọn kò sì da ìgbàgbọ́ wọn pọ̀ mọ́ àbòsí (ìbọ̀rìṣà), àwọn wọ̀nyẹn ni ìfọ̀kànbalẹ̀ ń bẹ fún. Àwọn sì ni olùmọ̀nà.');
INSERT INTO "aykyuni" VALUES(6,83,'6:83','Ìyẹn ni àwíjàre Wa tí A fún (Ànábì) ’Ibrọ̄hīm lórí àwọn ènìyàn rẹ̀. À ń ṣe àgbéga ipò fún ẹni tí A bá fẹ́. Dájúdájú Olúwa rẹ ni Ọlọ́gbọ́n, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(6,84,'6:84','Ati pé A ta á lọ́rẹ (Ànábì) ’Ishāƙ àti Ya‘ƙūb (tí ó jẹ́ ọmọ ’Ishāƙ). Ìkọ̀ọ̀kan wọn ni A fi mọ̀nà. A sì fi (Ànábì) Nūh mọ̀nà ṣíwájú. Àti pé nínú àrọ́mọdọ́mọ (Ànábì ’Ibrọ̄hīm tí A fi mọ̀nà ni àwọn Ànábì) Dāwūd, Sulaemọ̄n, ’Ayyūb, Yūsuf, Mūsā àti Hārūn. Báyẹn ni A ṣe ń san àwọn olùṣe-rere ní ẹ̀san (rere).');
INSERT INTO "aykyuni" VALUES(6,85,'6:85','Àti (Ànábì) Zakariyyā, Yahyā, ‘Īsā àti ’Ilyās; gbogbo wọn wà nínú àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(6,86,'6:86','Àti ’Ismọ̄‘īl, al-Yasa‘, Yūnus àti Lūt; ìkọ̀ọ̀kan wọn ni A ṣoore àjùlọ fún lórí àwọn ẹ̀dá (àsìkò wọn).');
INSERT INTO "aykyuni" VALUES(6,87,'6:87','Àti pé nínú àwọn bàbá wọn, àrọ́mọdọ́mọ wọn àti àwọn arákùnrin wọn, A tún ṣà wọ́n lẹ́ṣà. A sì fi wọ́n mọ̀nà tààrà (’Islām).');
INSERT INTO "aykyuni" VALUES(6,88,'6:88','Ìyẹn ni ìmọ̀nà Allāhu. Ó sì ń fi tọ́ ẹni tí Ó bá fẹ́ sí ọ̀nà nínú àwọn ẹrú Rẹ̀. Tí wọ́n bá fi lè ṣẹbọ ni, dájúdájú ohun tí wọ́n ń ṣe níṣẹ́ (rere) ìbá bàjẹ́ mọ́ wọn lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(6,89,'6:89','Àwọn wọ̀nyẹn ni àwọn tí A fún ní Tírà, òye ìjìnlẹ̀ (ìyẹn, sunnah) àti (ipò) Ànábì. Nítorí náà, tí àwọn wọ̀nyí bá ṣàì gbàgbọ́ nínú rẹ̀, dájúdájú A ti gbé e lé àwọn ènìyàn kan lọ́wọ́, tí wọn kò níí ṣàì gbàgbọ́ nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(6,90,'6:90','Àwọn wọ̀nyẹn ni àwọn tí Allāhu tọ́ sí ọ̀nà. Nítorí náà, ọ̀nà wọn ni kí o tẹ̀lé. Sọ pé: “Èmi kò bi yín ní owó-ọ̀yà kan lórí rẹ̀. Kí sì ni al-Ƙur’ān bí kò ṣe ìrántí fún gbogbo ẹ̀dá.”');
INSERT INTO "aykyuni" VALUES(6,91,'6:91','Wọn kò fún Allāhu ní iyì tí ó tọ́ sí I, nígbà tí wọ́n wí pé: “Allāhu kò sọ n̄ǹkan kan kalẹ̀ fún abara kan.” Sọ pé: “Ta ni Ó sọ tírà tí (Ànábì) Mūsā mú wá kalẹ̀, (èyí tó jẹ́) ìmọ́lẹ̀ àti ìmọ̀nà fún àwọn ènìyàn, èyí tí ẹ ṣàkọsílẹ̀ rẹ̀ sínú ìwé àjákọ, tí ẹ̀ ń ṣe àfihàn rẹ̀, tí ẹ sì ń fi ọ̀pọ̀lọ́pọ̀ rẹ̀ pamọ́, A sì fi ohun tí ẹ ò mọ̀ mọ̀ yín, ẹ̀yin àti àwọn bàbá yín?” Sọ pé: “Allāhu ni.” Lẹ́yìn náà, fi wọ́n sílẹ̀ sínú ìsọkúsọ wọn, kí wọ́n máa ṣeré.');
INSERT INTO "aykyuni" VALUES(6,92,'6:92','Èyí (al-Ƙur’ān) tún ni Tírà ìbùkún tí A sọ̀kalẹ̀; ó ń jẹ́rìí sí èyí tó jẹ́ òdodo nínú èyí tó ṣíwájú rẹ̀ àti pé nítorí kí o lè ṣe ìkìlọ̀ fún ’Ummul-Ƙurọ̄ (ìyẹn, ará ìlú Mọkkah) àti ẹnikẹ́ni tí ó bá wà ní àyíká rẹ̀ (ìyẹn, ará ìlú yòókù). Àwọn tó gbàgbọ́ nínú Ọjọ́ Ìkẹ́yìn, wọ́n gbàgbọ́ nínú rẹ̀. Àwọn sì ni wọ́n ń ṣọ́ ìrun wọn.');
INSERT INTO "aykyuni" VALUES(6,93,'6:93','Ta l’ó ṣàbòsí ju ẹni tí ó dá àdápa irọ́ mọ́ Allāhu tàbí ẹni tí ó wí pé wọ́n fi ìmísí ránṣẹ́ sí mi - A ò sì fi kiní kan ránṣẹ́ sí i - àti ẹni tí ó wí pé “Èmi náà yóò sọ irú ohun tí Allāhu sọ̀kalẹ̀ kalẹ̀.”? Tí ó bá jẹ́ pé ìwọ rí i nígbà tí àwọn alábòsí bá wà nínú ìpọ́kàkà ikú, tí àwọn mọlāika nawọ́ wọn (sí wọn pé) “Ẹ mú ẹ̀mí yín jáde wá. Lónìí ni Wọn yóò san yín ní ẹ̀san àbùkù ìyà nítorí ohun tí ẹ̀ máa ń sọ nípa Allāhu ní àìtọ́. Ẹ sì máa ń ṣe ìgbéraga sí àwọn āyah Rẹ̀.”');
INSERT INTO "aykyuni" VALUES(6,94,'6:94','Dájúdájú ẹ ti wá bá wa ní ìkọ̀ọ̀kan gẹ́gẹ́ bí A ṣe ṣẹ̀dá yín nígbà àkọ́kọ́. Ẹ sì ti fi ohun tí A fún yín (nínú ọrọ̀ ayé) sílẹ̀ sí ẹ̀yìn yín. A ò mà rí àwọn olùṣìpẹ̀ yín pẹ̀lú yín, àwọn tí ẹ sọ láì ní ẹ̀rí pé dájúdájú láààrin yín àwọn ni akẹgbẹ́ (fún Allāhu). Dájúdájú àṣepọ̀ ààrin yín ti já pátápátá. Àti pé ohun tí ẹ̀ ń sọ nípa wọn (láì ní ẹ̀rí lọ́wọ́ lórí ìṣìpẹ̀ wọn) sì di òfo mọ yín lọ́wọ́.1 ');
INSERT INTO "aykyuni" VALUES(6,95,'6:95','Dájúdájú Allāhu l’Ó ń mú kóró èso irúgbìn àti kóró èso dàbínù hù jáde. Ó ń mú alààyè jáde láti ara òkú. Ó sì ń mú òkú jáde láti ara alààyè. Ìyẹn ni Allāhu. Nítorí náà, báwo ni wọ́n ṣe ń ṣẹ yín lórí kúrò níbi òdodo?');
INSERT INTO "aykyuni" VALUES(6,96,'6:96','Ó ń mú ojúmọ́ mọ́. Ó ṣe òru ní ìsinmi. (Ó ń mú) òòrùn àti òṣùpá (rìn) fún ìṣírò (ọjọ́ ayé). Ìyẹn ni ètò Alágbára, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(6,97,'6:97','Òun ni Ẹni tí Ó fi àwọn ìràwọ̀ ṣe (ìmọ́lẹ̀) fún yín kí ẹ lè fi ríran nínú òkùnkùn ilẹ̀ àti ibúdò. A kúkú ti ṣàlàyé àwọn āyah fún àwọn ènìyàn tó nímọ̀.');
INSERT INTO "aykyuni" VALUES(6,98,'6:98','Òun ni Ẹni tí Ó ṣẹ̀dá yín láti ara ẹ̀mí ẹyọ kan. Nítorí náà, ibùgbé (nílé ayé) àti ibùpadàsí (ní ọ̀run wà fún yín). A kúkú ti ṣàlàyé àwọn āyah fún àwọn ènìyàn tó ní àgbọ́yé.');
INSERT INTO "aykyuni" VALUES(6,99,'6:99','Òun ni Ẹni tó ń sọ omi kalẹ̀ láti sánmọ̀. A fi mú gbogbo n̄ǹkan ọ̀gbìn jáde. A tún mú ewéko tó ń dán lọ̀gbọ́lọ̀gbọ́ jáde láti inú rẹ̀. A tún ń mú ṣiri èso jáde láti inú rẹ̀. (A sì ń mú jáde) láti ara igi dàbínù, láti ara èso àkọ́yọ rẹ̀, èso tó ṣùjọ mọ́ra wọn tó rọ̀ dẹ̀dẹ̀ wálẹ̀. (À ń ṣe) àwọn ọgbà oko èso àjàrà, èso zaetūn àti èso rummọ̄n (ní àwọn èso tó) jọra àti (àwọn èyí tí) kò jọra. Ẹ wo èso rẹ̀ nígbà tí ó bá so àti (nígbà tí ó bá) pọ́n. Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(6,100,'6:100','Wọ́n sì fi àwọn àlùjànnú ṣe akẹgbẹ́ fún Allāhu. Òun sì l’Ó ṣẹ̀dàá wọn! Wọ́n tún parọ́ mọ́ Ọn (pé) Ó bí ọmọkùnrin àti ọmọbìnrin, láì nímọ̀ kan (nípa rẹ̀). Mímọ́ ni fún Un. Ó sì ga tayọ ohun tí wọ́n ń fi ròyìn (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(6,101,'6:101','Olùpilẹ̀dá àwọn sánmọ̀ àti ilẹ̀ ni. Báwo ni Ó ṣe ní ọmọ nígbà tí kò ní aya. Ó dá gbogbo n̄ǹkan ni. Òun sì ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(6,102,'6:102','Ìyẹn ni Allāhu, Olúwa yín; kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun, Ẹlẹ́dàá gbogbo n̄ǹkan. Nítorí náà, ẹ jọ́sìn fún Un. Òun sì ni Olùṣọ́ lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(6,103,'6:103','Àwọn ojú (ẹ̀dá) kò lè ká Allāhu. Òun sì ká àwọn ojú. Òun sì ni Aláàánú, Alámọ̀tán.');
INSERT INTO "aykyuni" VALUES(6,104,'6:104','Ìmọ̀ àmọ̀dájú àti ẹ̀rí kúkú ti dé ba yín láti ọ̀dọ̀ Olúwa yín. Nítorí náà, ẹnikẹ́ni tó bá ríran, fún ẹ̀mí ara rẹ̀ ni. Ẹnikẹ́ni tó bá sì fọ́jú, fún ẹ̀mí ara rẹ̀ ni. Èmi kì í ṣe olùṣọ́ lórí yín.');
INSERT INTO "aykyuni" VALUES(6,105,'6:105','Báyẹn ni A ṣe ń mú àwọn āyah wá lóníran-ànran ọ̀nà. - Kí wọ́n máa sọ pé, “O kẹ́kọ̀ọ́ (rẹ̀ lọ́dọ̀ àwọn onítírà ni.” Dípò kí wọ́n wí pé: “Wọ́n sọ̀ ọ́ kalẹ̀ fún ọ ni.”- (A mú un wá lóníran-ànran ọ̀nà sẹ́) nítorí kí A lè ṣàlàyé rẹ̀ fún àwọn ènìyàn tó nímọ̀. ');
INSERT INTO "aykyuni" VALUES(6,106,'6:106','Tẹ̀lé ohun tí wọ́n fi rán ọ́ ní ìmísí láti ọ̀dọ̀ Olúwa rẹ. Kò sí ẹni tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Kí o sì ṣẹ́rí kúrò ní ọ̀dọ̀ àwọn ọ̀ṣẹbọ.');
INSERT INTO "aykyuni" VALUES(6,107,'6:107','Tí ó bá jẹ́ pé Allāhu bá fẹ́ (ìmọ̀nà fún wọn), wọn ìbá tí ṣẹbọ. A ò sì fi ọ́ ṣe olùṣọ́ lórí wọn. Àti pé ìwọ kọ́ ni alámòjúútó wọn.');
INSERT INTO "aykyuni" VALUES(6,108,'6:108','Ẹ má ṣe bú àwọn (òrìṣà) tí wọ́n ń pè lẹ́yìn Allāhu, kí àwọn (abọ̀rìṣà) má baà bú Allāhu ní ti àbòsí àti àìnímọ̀. Báyẹn ni A ti ṣe iṣẹ́ ìjọ kọ̀ọ̀kan ní ọ̀ṣọ́ fún wọn. Lẹ́yìn náà, ọ̀dọ̀ Olúwa wọn ni ibùpadàsí wọn. Nítorí náà, Ó máa fún wọn ní ìró ohun tí wọ́n máa ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(6,109,'6:109','Wọ́n sì fi Allāhu búra tí ìbúra wọn sì lágbára gan-an pé, dájúdájú tí àmì (ìyanu) kan bá dé bá àwọn, àwọn gbọ́dọ̀ gbà á gbọ́. Sọ pé: “Ọ̀dọ̀ Allāhu nìkan ni àwọn àmì (ìyanu) wà.” Kí sì ni ó máa mu yín fura mọ̀ pé dájúdájú nígbà tí ó bá dé (bá wọn) wọn máa gbà á gbọ́?');
INSERT INTO "aykyuni" VALUES(6,110,'6:110','A máa yí ọkàn wọn àti ojú wọn sódì ni (wọn kò sì níí gbà á gbọ́) gẹ́gẹ́ bí wọn kò ṣe gbàgbọ́ nínú (èyí tó ṣíwájú nínú àwọn àmì ìyanu) nígbà àkọ́kọ́. A ó sì fí wọn sílẹ̀ sínú àgbéré wọn, tí wọn yóò máa pa rìdàrìdà.');
INSERT INTO "aykyuni" VALUES(6,111,'6:111','Dájúdájú tí A bá sọ àwọn mọlāika kalẹ̀ fún wọn, tí àwọn òkú ń bá wọn sọ̀rọ̀, tí A tún kó gbogbo n̄ǹkan jọ síwájú wọn, wọn kò níí gbàgbọ́ àfi tí Allāhu bá fẹ́. Ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni aláìmọ̀kan.');
INSERT INTO "aykyuni" VALUES(6,112,'6:112','Báyẹn ni A ti ṣe àwọn èṣù ènìyàn àti èṣù àlùjànnú ní ọ̀tá fún Ànábì kọ̀ọ̀kan; apá kan wọn ń fi ọ̀rọ̀ dídùn (odù irọ́) ránṣẹ́ sí apá kan ní ti ẹ̀tàn. Tí ó bá jẹ́ pé Olúwa rẹ bá fẹ́ (láti tọ́ wọn sọ́nà ni) wọn ìbá tí ṣe bẹ́ẹ̀. Nítorí náà, fi wọn sílẹ̀ tòhun ti àdápa irọ́ tí wọ́n ń dá.');
INSERT INTO "aykyuni" VALUES(6,113,'6:113','Kí àwọn ọkàn àwọn tí kò gba Ọjọ́ Ìkẹ́yìn gbọ́ máa tẹ́tí bẹ̀lẹ̀jẹ́ sí (odù irọ́ Èṣù), kí wọ́n yọ́nú sí i, kí wọ́n sì máa dá ohun tí wọ́n ń dá lẹ́ṣẹ̀ ǹ só.');
INSERT INTO "aykyuni" VALUES(6,114,'6:114','Ṣé n̄ǹkan mìíràn ni kí n̄g wá ní olùdájọ́ lẹ́yìn Allāhu ni? Òun sì ni Ẹni tí Ó sọ Tírà kalẹ̀ fún yín tí wọ́n fi ṣàlàyé ìdájọ́.Àwọn tí A sì fún ní tírà mọ̀ pé, dájúdájú wọ́n sọ̀ ọ́ kalẹ̀ pẹ̀lú òdodo láti ọ̀dọ̀ Olúwa rẹ. Nítorí náà, o ò gbọdọ̀ wà lára àwọn oníyèméjì. ');
INSERT INTO "aykyuni" VALUES(6,115,'6:115','Ọ̀rọ̀ Olúwa rẹ pé ní òdodo àti ní déédé. Kò sí aláyìípadà kan fún àwọn ọ̀rọ̀ Rẹ̀. Òun sì ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(6,116,'6:116','Tí o bá tẹ̀lé ọ̀pọ̀lọ́pọ̀ àwọn tí ń bẹ lórí ilẹ̀, wọ́n máa ṣì ọ́ lọ́nà kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu. Wọn kò tẹ̀lé kiní kan bí kò ṣe àbá dídá. Kí sì ni wọn (ń ṣe) bí kò ṣe pé wọ́n ń parọ́.');
INSERT INTO "aykyuni" VALUES(6,117,'6:117','Dájúdájú Olúwa rẹ, Òun l’Ó nímọ̀ jùlọ nípa ẹni tó ṣìnà lójú ọ̀nà (ẹ̀sìn) Rẹ̀. Àti pé, Òun l’Ó nímọ̀ jùlọ nípa àwọn olùmọ̀nà.');
INSERT INTO "aykyuni" VALUES(6,118,'6:118','Nítorí náà, ẹ jẹ nínú ohun tí wọ́n bá fi orúkọ Allāhu pa, tí ẹ bá gbàgbọ́ nínú àwọn āyah Rẹ̀.');
INSERT INTO "aykyuni" VALUES(6,119,'6:119','Kí ni ó máa kọ̀ fún yín láti jẹ nínú ohun tí wọ́n fi orúkọ Allāhu pa! Ó kúkú ti ṣàlàyé fún yín ohun tí Ó ṣe ní èèwọ̀ fún yín àyàfi èyí tí wọ́n bá fi ìnira tì yín débẹ̀. Dájúdájú ọ̀pọ̀lọ́pọ̀ ni wọ́n ń fi ìfẹ́-inú wọn pẹ̀lú àìnímọ̀ (wọn) ṣi àwọn ènìyàn lọ́nà. Dájúdájú Olúwa rẹ, Ó nímọ̀ jùlọ nípa àwọn olùtayọ ẹnu-ààlà.');
INSERT INTO "aykyuni" VALUES(6,120,'6:120','Ẹ fi èyí tó hàn nínú ẹ̀ṣẹ̀ àti èyí tó pamọ́ nínú rẹ̀ sílẹ̀. Dájúdájú àwọn tó ń ṣe iṣẹ́ ẹ̀ṣẹ̀, A óò san wọ́n ní ẹ̀san ohun tí wọ́n ń dá lẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(6,121,'6:121','Ẹ̀yin kò sì gbọdọ̀ jẹ nínú ohun tí wọn kò fi orúkọ Allāhu pa.Dájúdájú ìbàjẹ́ ni. Àti pé dájúdájú àwọn èṣù, wọn yóò máa fi ọ̀rọ̀ irọ́ ránṣẹ́ sí àwọn ẹni wọn, nítorí kí wọ́n lè takò yín. Tí ẹ bá fi lè tẹ̀lé wọn, dájúdájú ẹ ti di ọ̀ṣẹbọ. ');
INSERT INTO "aykyuni" VALUES(6,122,'6:122','Ǹjẹ́ ẹni tí (àfiwé rẹ̀) jẹ́ òkú (ìyẹn, aláìgbàgbọ́), tí A sọ di alààyè (nípa pé ó gba ’Islām), tí A sì fún un ní ìmọ́lẹ̀ (ìyẹn, ìmọ̀ ẹ̀sìn), tí ó sì ń lò ó láààrin àwọn ènìyàn, (ǹjẹ́) ó dà bí ẹni tí àfiwé tirẹ̀ jẹ́ (ẹni tí) ń bẹ nínú àwọn òkùnkùn (àìgbàgbọ́), tí kò sì jáde kúrò nínú rẹ̀? Báyẹn ni wọ́n ṣe ní ọ̀ṣọ́ fún àwọn aláìgbàgbọ́ ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(6,123,'6:123','Báyẹn ni A ṣe sọ àwọn ọ̀tọ̀kùlúkan di ọ̀daràn ìlú nínú ìlú kọ̀ọ̀kan, kí wọ́n lè máa dète níbẹ̀. Wọn kò sì dète sí ẹnikẹ́ni bí kò ṣe sí ara wọn, wọn kò sì fura. ');
INSERT INTO "aykyuni" VALUES(6,124,'6:124','Nígbà tí āyah kan bá sì dé bá wọn, wọ́n á wí pé: “Àwa kò níí gbà á gbọ́ títí di ìgbà tí wọ́n bá tó fún àwa náà ní irú ohun tí wọ́n fún àwọn Òjíṣẹ́ Allāhu.” Allāhu ló nímọ̀ jùlọ nípa ibi tí Ó ń fí iṣẹ́-rírán Rẹ̀ sí.Láìpẹ́ ìyẹpẹrẹ àti ìyà líle láti ọ̀dọ̀ Allāhu yóò dé bá àwọn tó dẹ́ṣẹ̀ nítorí ohun tí wọ́n ń dá léte. ');
INSERT INTO "aykyuni" VALUES(6,125,'6:125','Ẹnikẹ́ni tí Allāhu bá fẹ́ fọ̀nà mọ̀, Ó máa ṣí igbá-àyà rẹ̀ payá fún ’Islām. Ẹnikẹ́ni tí Ó bá sì fẹ́ ṣì lọ́nà, Ó máa fún igbá-àyà rẹ̀ pa gádígádí bí ẹni pé ó la ìgùnkè lọ sínú sánmọ̀ bọra rẹ̀ lọ́rùn. Báyẹn ni Allāhu ṣe ṣe ìyà fún àwọn tí kò gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(6,126,'6:126','Èyí ni ojú ọ̀nà Olúwa rẹ (tó jẹ́) ọ̀nà tààrà. A kúkú ti ṣàlàyé àwọn āyah fún ìjọ tó ń lo ìrántí.');
INSERT INTO "aykyuni" VALUES(6,127,'6:127','Ilé àlàáfíà ń bẹ fun wọn ní ọ̀dọ̀ Olúwa wọn. Òun sì ni Alátìlẹ́yìn wọn nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(6,128,'6:128','(Rántí) Ọjọ́ tí (Allāhu) yóò kó gbogbo wọn jọ pátápátá, (Ó máa sọ pé): “Ẹ̀yin àwùjọ àlùjànnú, dájúdájú ẹ ti kó ọ̀pọ̀lọ́pọ̀ ènìyàn sọnù.” Àwọn ọ̀rẹ́ wọn nínú àwọn ènìyàn yóò wí pé: “Olúwa wa, apá kan wa gbádùn apá kan ni. A sì ti lo àsìkò wa tí O bù fún wa (láti lò).” (Allāhu) sọ pé: “Iná ni ibùgbé yín; olùṣegbére ni yín nínú rẹ̀ àfi ohun tí Allāhu bá fẹ́ Dájúdájú Olúwa rẹ ni Ọlọ́gbọ́n, Onímọ̀. ');
INSERT INTO "aykyuni" VALUES(6,129,'6:129','Báyẹn ni A ṣe fi apá kan àwọn alábòsí ṣọ̀rẹ́ apá kan (wọn) nítorí ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(6,130,'6:130','Ẹ̀yin àwùjọ àlùjànnú àti ènìyàn, ṣé àwọn Òjíṣẹ́ kan láààrin yín kò wá ba yín, tí wọ́n ń ké àwọn āyah Mi fún yín, tí wọ́n sì ń fi ìpàdé yín Òní yìí ṣèkìlọ̀ fún yín? Wọ́n wí pé: “A jẹ́rìí léra wa lórí (pé wọ́n wá).” Ìṣẹ́mí ayé tàn wọ́n jẹ. Wọ́n sì jẹ́rìí léra wọn lórí pé dájúdájú àwọn jẹ́ aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(6,131,'6:131','Ìyẹn nítorí pé, Olúwa rẹ kò níí pa àwọn ìlú run nípasẹ̀ àbòsí (ọwọ́ wọn), lásìkò tí àwọn ara ìlú náà jẹ́ aláìmọ̀ (títí Òjíṣẹ́ yóò fi dé bá wọn).');
INSERT INTO "aykyuni" VALUES(6,132,'6:132','Àwọn ipò (ìkẹ́ àti ipò ìyà) ń bẹ fún ẹnì kọ̀ọ̀kan nípasẹ̀ ohun tí wọ́n bá ṣe níṣẹ́. Olúwa rẹ kì í ṣe onígbàgbé nípa n̄ǹkan tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(6,133,'6:133','Olúwa rẹ ni Ọlọ́rọ̀, Aláàánú. Tí Ó bá fẹ́, Ó máa ko yín kúrò (lórí ilẹ̀). Ó sì máa fi ohun tí Ó bá fẹ́ rọ́pò (yín) lẹ́yìn (ìparun) yín gẹ́gẹ́ bí Ó ti ṣe mu yín jáde lẹ́yìn (ìparun) àrọ́mọdọ́mọ àwọn ìjọ mìíràn.');
INSERT INTO "aykyuni" VALUES(6,134,'6:134','Dájúdájú ohun tí À ń ṣe ní àdéhùn fún yín, ó kúkú ń bọ̀ wá ṣẹlẹ̀; ẹ̀yin kò sì lè dá Allāhu lágara.1 ');
INSERT INTO "aykyuni" VALUES(6,135,'6:135','Sọ pé: “Ẹ̀yin ènìyàn mi, ẹ ṣisẹ́ ní àyè yín. Dájúdájú èmi náà ń ṣiṣẹ́.Láìpẹ́ ẹ̀ máa mọ ẹni tí Ilé Ìkángun-rere (Ọgbà Ìdẹ̀ra) yóò jẹ́ tirẹ̀. Dájúdájú àwọn alábòsí kò níí jèrè.” ');
INSERT INTO "aykyuni" VALUES(6,136,'6:136','Wọ́n sì fi ìpín kan fún Allāhu nínú ohun tí Ó dá nínú n̄ǹkan oko àti ẹran-ọ̀sìn; wọ́n wí pé: “Èyí ni ti Allāhu - pẹ̀lú ọ̀rọ̀ wọn láì ní ẹ̀rí lọ́wọ́, - èyí sì ni ti àwọn òrìṣà wa.” Nítorí náà, ohun tí ó bá jẹ́ ti àwọn òrìṣà kò níí dàpọ̀ mọ́ ti Allāhu. Ohun tí ó bá sì jẹ́ ti Allāhu, ó ń dàpọ̀ mọ́ ti àwọn òrìṣà wọn; ohun tí wọ́n ń dá lẹ́jọ́ burú.');
INSERT INTO "aykyuni" VALUES(6,137,'6:137','Báyẹn ni àwọn òrìṣà wọn ṣe pípa àwọn ọmọ wọn ní ọ̀ṣọ́ fún ọ̀pọ̀lọ́pọ̀ nínú àwọn ọ̀ṣẹbọ nítorí kí wọ́n lè pa wọ́n run àti nítorí kí wọ́n lè d’ojú ẹ̀sìn wọn rú mọ́ wọn lọ́wọ́. Tí ó bá jẹ́ pé Allāhu bá fẹ́, wọn ìbá tí ṣe (bẹ́ẹ̀). Nítorí náà, fi wọ́n sílẹ̀ tòhun ti ohun tí wọ́n ń dá ní àdápa irọ́.');
INSERT INTO "aykyuni" VALUES(6,138,'6:138','Wọ́n tún wí pé: “Èèwọ̀ ni àwọn ẹran-ọ̀sìn àti n̄ǹkan oko wọ̀nyí. Ẹnì kan kò gbọdọ̀ jẹ ẹ́ àfi ẹni tí a bá fẹ́, pẹ̀lú ọ̀rọ̀ wọn láì ní ẹ̀rí lọ́wọ́.” - Àwọn ẹran-ọ̀sìn kan tún ń bẹ tí wọ́n ṣe ẹ̀yìn wọn ní èèwọ̀ (fún gígùn àti ẹrù rírù), àwọn ẹran kan tún ń bẹ tí wọn kì í fi orúkọ Allāhu pa. (Wọ́n fi àwọn n̄ǹkan wọ̀nyí) dá àdápa irọ́ mọ́ Allāhu ni. Ó sì máa san wọ́n ní ẹ̀san ohun tí wọ́n ń dá ní àdápa irọ́.');
INSERT INTO "aykyuni" VALUES(6,139,'6:139','Wọ́n tún wí pé: “Ohun tí ń bẹ nínú ikùn àwọn ẹran-ọ̀sìn wọ̀nyí jẹ́ ti àwọn ọkùnrin wa nìkan, ó sì jẹ́ èèwọ̀ fún àwọn obìnrin wa.” Tí ó bá sì jẹ́ òkú ọmọ-ẹran, akẹgbẹ́ sì ni wọn nínú (ìpín) rẹ̀.(Allāhu) yóò san wọ́n ní ẹ̀san irọ́ (ẹnu) wọn. Dájúdájú Òun ni Ọlọ́gbọ́n, Onímọ̀. ');
INSERT INTO "aykyuni" VALUES(6,140,'6:140','Àwọn tó fi agọ̀ àti àìmọ̀ pa àwọn ọmọ wọn kúkú ti ṣòfò; wọ́n tún ṣe ohun tí Allāhu pa lésè fún wọn ní èèwọ̀, ní ti dídá àdápa irọ́ mọ́ Allāhu. Wọ́n kúkú ti ṣìnà, wọn kò sì jẹ́ olùmọ̀nà.');
INSERT INTO "aykyuni" VALUES(6,141,'6:141','(Allāhu) Òun ni Ẹni tí Ó ṣẹ̀dá àwọn (n̄ǹkan) ọgbà oko èyí tí ẹ̀ ń fọwọ́ ara yín gbìn àti èyí tó ń lalẹ̀ hù àti dàbínù àti irúgbìn tí (adùn) jíjẹ rẹ̀ yàtọ̀ síra wọn, àti èso zaetūn àti èso rummọ̄n tó jọra wọn àti èyí tí kò jọra wọn. Ẹ jẹ nínú èso rẹ̀ nígbà tí ó bá so, kí ẹ sì yọ Zakāh rẹ̀ ní ọjọ́ ìkórè rẹ̀. Kí ẹ sì má yàpà. Dájúdájú (Allāhu) kò nífẹ̀ẹ́ àwọn àpà.');
INSERT INTO "aykyuni" VALUES(6,142,'6:142','Ó sì ń bẹ nínú àwọn ẹran-ọ̀sìn, èyí tó lè ru ẹrù àti èyí tí kò lè ru ẹrù. Ẹ jẹ nínú ohun tí Allāhu pa lésè fún yín. Kí ẹ sì má ṣe tẹ̀lé àwọn ojú-ẹsẹ̀ Èṣù;dájúdájú òun ni ọ̀tá pọ́nńbélé fún yín. ');
INSERT INTO "aykyuni" VALUES(6,143,'6:143','(Allāhu dá ẹran) mẹ́jọ ní takọ- tabo; méjì nínú àgùtàn (takọ-tabo), méjì nínú ewúrẹ́ (takọ-tabo). Sọ pé: “Ṣé àwọn akọ méjèèjì ni Allāhu ṣe ní èèwọ̀ ni tàbí abo méjèèjì, tàbí ohun tí ń bẹ nínú àpò-ìbímọ àwọn abo ẹran méjèèjì. Ẹ fún mi ní ìró pẹ̀lú ìmọ̀ tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(6,144,'6:144','Méjì nínú ràkúnmí (takọ-tabo) àti méjì nínú màálù (takọ-tabo). Sọ pé: “Ṣé àwọn akọ méjèèjì ni Allāhu ṣe ní èèwọ̀ ni tàbí abo méjèèjì, tàbí ohun tí ń bẹ nínú àpò-ìbímọ àwọn abo ẹran méjèèjì. Tàbí ṣé ẹ̀yin wà níbẹ̀ nígbà tí Allāhu pa yín láṣẹ èyí ni?” Nítorí náà, ta ni ó ṣe àbòsí tó tún tayọ ẹni tó dá àdápa irọ́ mọ́ Allāhu láti lè ṣi àwọn ènìyàn lọ́nà pẹ̀lú àìnímọ̀. Dájúdájú Allāhu kò níí fi ọ̀nà mọ ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(6,145,'6:145','Sọ pé: “Èmi kò rí nínú ohun tí wọ́n fi ránṣẹ́ sí mi tí wọ́n ṣe ní èèwọ̀ fún ẹni tí ó ń jẹ ẹ́ àfi ohun tí ó bá jẹ́ ẹran òkúǹbete tàbí ẹ̀jẹ̀ tí ó tú jáde lára ẹran (yálà nípasẹ̀ dídúńbú, gígún tàbí títalọ́fà) tàbí ẹran ẹlẹ́dẹ̀ nítorí pé dájúdájú ẹ̀gbin ni, tàbí ẹran ìyapa (àṣẹ Allāhu) tí wọ́n pa pẹ̀lú pípe orúkọ mìíràn lé e lórí yàtọ̀ sí (orúkọ) Allāhu. Nítorí náà, ẹni tí ìnira (ebi) bá mú, yàtọ̀ sí ẹni tó ń wá èèwọ̀ kiri àti olùtayọ-ẹnu-ààlà,2 dájúdájú Olúwa rẹ ni Aláforíjìn, Àṣàkẹ́-ọ̀run.3 ');
INSERT INTO "aykyuni" VALUES(6,146,'6:146','A ṣe é ní èèwọ̀ fún àwọn tó di yẹhudi gbogbo ẹran tó ní èékánná tàbí pátákò tó ṣùpọ̀ mọ́ra wọn. Nínú ẹran màálù àti àgùtàn, A tún ṣe ọ̀rá àwọn méjèèjì ní èèwọ̀ fún wọn àfi èyí tí ó bá lẹ̀mọ́ ẹ̀yìn wọn tàbí ìfun tàbí èyí tí ó bá yípọ̀ mọ́ eegun. Ìyẹn ni A fi san wọ́n ní ẹ̀san nítorí àbòsí ọwọ́ wọn. Dájúdájú Àwa sì ni Olódodo.');
INSERT INTO "aykyuni" VALUES(6,147,'6:147','Tí wọ́n bá pè ọ́ ní òpùrọ́, sọ nígbà náà pé: “Olúwa yín ni Oníkẹ̀ẹ́ tó gbòòrò. Kò sì sí ẹni tó lè gbé ìyà Rẹ̀ kúrò lórí ìjọ ẹlẹ́ṣẹ̀.”');
INSERT INTO "aykyuni" VALUES(6,148,'6:148','Àwọn tó ń ṣẹbọ yóò wí pé: “Tí kò bá jẹ́ pé Allāhu bá fẹ́ ni àwa àti àwọn bàbá wa ìbá tí ṣẹbọ, àti pé àwa ìbá tí ṣe n̄ǹkan kan ní èèwọ̀.” Báyẹn ni àwọn tó ṣíwájú wọn ṣe pe ọ̀rọ̀ Allāhu nírọ́ títí wọ́n fi tọ́ ìyà Wa wò. Sọ pé: “Ǹjẹ́ ìmọ̀ kan ń bẹ ní ọ̀dọ̀ yín, kí ẹ mú un jáde fún wa?” Ẹ̀yin kò tẹ̀lé kiní kan bí kò ṣe àbá dídá. Kí sì ni ẹ̀ ń sọ bí kò ṣe pé ẹ̀ ń parọ́.');
INSERT INTO "aykyuni" VALUES(6,149,'6:149','Sọ pé: “Ti Allāhu ni ẹ̀rí tó dópin. Nítorí náà, tí Ó bá fẹ́ ni, ìbá fi gbogbo yín mọ̀nà pátápátá.” ');
INSERT INTO "aykyuni" VALUES(6,150,'6:150','Sọ pé: “Ẹ mú àwọn ẹlẹ́rìí yín jáde, àwọn tó máa jẹ́rìí pé dájúdájú Allāhu l’ó ṣe èyí ní èèwọ̀.” Tí wọ́n bá jẹ́rìí (irọ́), ìwọ má ṣe bá wọn jẹ́rìí (sí i). Má sì ṣe tẹ̀lé ìfẹ́-inú àwọn tó pe àwọn āyah Wa nírọ́ àti àwọn tí kò gba Ọjọ́ Ìkẹ́yìn gbọ́. Àwọn sì ni wọ́n ń bá Olúwa wọn wá akẹgbẹ́.');
INSERT INTO "aykyuni" VALUES(6,151,'6:151','Sọ pé: “Ẹ wá kí n̄g ka ohun tí Olúwa yín ṣe ní èèwọ̀ fún yín pé; kí ẹ má ṣe fí n̄ǹkan kan ṣẹbọ sí I, (kí ẹ sì ṣe) dáadáa sí àwọn òbí (yín) méjèèjì, kí ẹ sì má ṣe pa àwọn ọmọ yín nítorí (ìbẹ̀rù) òṣì, - Àwa ni À ń pèsè fun ẹ̀yin àti àwọn - kí ẹ sì má ṣe súnmọ́ àwọn ìwà ìbájẹ́ - èyí tó hàn nínú rẹ̀ àti èyí tó pamọ́, - àti pé kí ẹ sì má ṣe pa ẹ̀mí (ènìyàn) tí Allāhu ṣe ní èèwọ̀ àyàfi ní ọ̀nà ẹ̀tọ́. Ìyẹn l’Ó pa láṣẹ fún yín nítorí kí ẹ lè ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(6,152,'6:152','Ẹ má ṣe súnmọ́ dúkìá ọmọ òrukàn àyàfi ní ọ̀nà tó dára jùlọ títí ó fi máa dàgbà dáadáa. Ẹ wọn kóńgò àti òṣùwọ̀n kún ní dọ́gban̄n̄dọ́gba.A kò làbọ ẹ̀mí kan lọ́rùn àyàfi ìwọ̀n agbára rẹ̀. Tí ẹ bá sọ̀rọ̀, ẹ ṣe déédé, ìbáà jẹ́ ìbátan. Kí ẹ sì pé àdéhùn Allāhu. Ìyẹn l’Ó pa láṣẹ fún yín nítorí kí ẹ lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(6,153,'6:153','Àti pé dájúdájú èyí ni ọ̀nà Mi (tí ó jẹ́ ọ̀nà) tààrà. Nítorí náà, ẹ tẹ̀lé e. Ẹ má ṣe tẹ̀lé àwọn ojú ọ̀nà (mìíràn) nítorí kí ó má baà mu yín yapa ojú ọ̀nà (ẹ̀sìn) Rẹ̀. Ìyẹn l’Ó pa láṣẹ fún yín nítorí kí ẹ lè bẹ̀rù (Rẹ̀).”');
INSERT INTO "aykyuni" VALUES(6,154,'6:154','Lẹ́yìn náà, A fún (Ànábì) Mūsā ní Tírà ní pípé pérépéré fún ẹni tí ó máa ṣe dáadáa. (Ó jẹ́) àlàyé fún gbogbo n̄ǹkan. (Ó tún jẹ́) ìmọ̀nà àti ìkẹ́ nítorí kí wọ́n lè ní ìgbàgbọ́ nínú ìpàdé Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(6,155,'6:155','Èyí ni Tírà ìbùkún tí A sọ̀kalẹ̀. Nítorí náà, ẹ tẹ̀lé e. Kí ẹ sì bẹ̀rù (Allāhu) nítorí kí A lè kẹ yín.');
INSERT INTO "aykyuni" VALUES(6,156,'6:156','Nítorí kí ẹ má ṣe wí pé: “Ọ̀wọ́ ìjọ méjì tí ó ṣíwájú wa ni wọ́n sọ Tírà kalẹ̀ fún. A sì jẹ́ aláìnímọ̀ nípa ẹ̀kọ́ wọn.”');
INSERT INTO "aykyuni" VALUES(6,157,'6:157','Tàbí kí ẹ má baà wí pé: “Dájúdájú wọn ìbá sọ Tírà kalẹ̀ fún wa ni, àwa ìbá mọ̀nà jù wọ́n lọ.” Ẹ̀rí tó yanjú, ìmọ̀nà àti ìkẹ́ kúkú ti dé ba yín láti ọ̀dọ̀ Olúwa yín. Nítorí náà, ta ni ó ṣe àbòsí ju ẹni tó pe àwọn āyah Allāhu nírọ́, tó tún gbúnrí kúrò níbẹ̀? A máa san àwọn tó ń gbúnrí kúrò níbi àwọn āyah Wa ní (ẹ̀san) ìyà burúkú nítorí pé wọ́n ń gbúnrí(kúrò níbẹ̀).');
INSERT INTO "aykyuni" VALUES(6,158,'6:158','Ṣé wọ́n ń retí n̄ǹkan (mìíràn) bí kò ṣe pé kí àwọn mọlāika wá bá wọn tàbí kí Olúwa rẹ wá bá wọn tàbí kí apá kan àwọn àmì Olúwa rẹ wá bá wọn? Ní ọjọ́ tí apá kan àwọn àmì Olúwa rẹ bá wá bá wọn, ìgbàgbọ́ (tí) ẹ̀mí kan kò tí ì gbàgbọ́ tẹ́lẹ̀tẹ́lẹ̀ tàbí iṣẹ́ rere (tí) ẹ̀mí kan kò tí ì fi ìgbàgbọ́ rẹ̀ ṣe (tẹ́lẹ̀tẹ́lẹ̀) kò níí ṣe é ní àǹfààní (lásìkò náà). Sọ pé: “Ẹ máa retí, dájúdájú Àwa náà ń retí (Ọjọ́ náà).');
INSERT INTO "aykyuni" VALUES(6,159,'6:159','Dájúdájú àwọn tó ya ẹ̀sìn wọn sí ọ̀tọ̀ọ̀tọ̀, tí wọ́n sì di ìjọ-ìjọ, ìwọ kò ní n̄ǹkan kan ṣepọ̀ pẹ̀lú wọn. Ọ̀rọ̀ wọn sì ń bẹ ní ọ̀dọ̀ Allāhu. Lẹ́yìn náà, Ó máa fún wọn ní ìró ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(6,160,'6:160','Ẹnikẹ́ni tó bá mú iṣẹ́ dáadáa wá, (ẹ̀san) mẹ́wàá irú rẹ̀ l’ó máa wà fún un. Ẹnikẹ́ni tó bá sì mú iṣẹ́ aburú wa, A ò níí san án ní ẹ̀san àyàfi irú rẹ̀. A ò sì níí ṣàbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(6,161,'6:161','Sọ pé: “Dájúdájú Olúwa mi ti fi ọ̀nà tààrà (’Islām) mọ̀ mí, ẹ̀sìn tó fẹsẹ̀ rinlẹ̀, ẹ̀sìn (Ànábì) ’Ibrọ̄hīm, olùdúró-déédé-nínú-ẹ̀sìn, kò sì sí nínú àwọn ọ̀ṣẹbọ.”');
INSERT INTO "aykyuni" VALUES(6,162,'6:162','Sọ pé: “Dájúdájú ìrun mi, ẹran(pípa) mi, ìṣẹ̀mí ayé mi àti ikú mi ń jẹ́ ti Allāhu, Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(6,163,'6:163','Kò sí akẹgbẹ́ fún Un. Ìyẹn ni wọ́n pa láṣẹ fún mi. Èmi sì ni ẹni-àkọ́kọ́ àwọn mùsùlùmí (ní àsìkò tèmi).”1 ');
INSERT INTO "aykyuni" VALUES(6,164,'6:164','Sọ pé: “Ṣé èmi yóò tún wá olúwa kan yàtọ̀ sí Allāhu ni, nígbà tí ó jẹ́ pé Òun ni Olúwa gbogbo n̄ǹkan. Ẹ̀mí kan kò sì níí ṣe iṣẹ́ kan àfi fún ẹ̀mí ara rẹ̀. Ẹlẹ́rù-ẹ̀ṣẹ̀ kan kò sì níí ru ẹ̀ṣẹ̀ ẹlòmíìràn. Lẹ́yìn náà, ọ̀dọ̀ Olúwa yín ni ibùpadàsí yín. Ó sì máa fún yín ní ìró ohun tí ẹ̀ ń yapa ẹnu sí.”');
INSERT INTO "aykyuni" VALUES(6,165,'6:165','Òun ni Ẹni tí Ó ṣe yín ní àrólé lórí ilẹ̀. Ó sì fi àwọn ipò gbe yín ga ju ara yín lọ nítorí kí Ó lè dan yín wò nínú ohun tí Ó fún yín. Dájúdájú Olúwa rẹ ni Olùyára níbi ìyà. Dájúdájú Òun sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.”');
INSERT INTO "aykyuni" VALUES(7,1,'7:1','’Alif lām mīm sọ̄d. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(7,2,'7:2','Tírà kan tí A sọ̀kalẹ̀ fún ọ (nìyí). Nítorí náà, wàhálà (iyèméjì) kan kò gbọ́dọ̀ sí nínú ọkàn rẹ lórí rẹ̀ láti fi ṣe ìkìlọ̀ àti ìrántí fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(7,3,'7:3','Ẹ tẹ̀lé ohun tí wọ́n sọ̀kalẹ̀ fún yín láti ọ̀dọ̀ Olúwa yín. Kí ẹ sì má ṣe tẹ̀lé àwọn wòlíì(èṣù àti òrìṣà) lẹ́yìn Rẹ̀. Díẹ̀ l’ẹ̀ ń lò nínú ìrántí. ');
INSERT INTO "aykyuni" VALUES(7,4,'7:4','Mélòó mélòó nínú àwọn ìlú tí A ti parẹ́! Ìyà Wa dé bá wọn ní òru tàbí nígbà tí wọ́n ń sun oorun ọ̀sán lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(7,5,'7:5','Igbe ẹnu wọn kò jẹ́ kiní kan nígbà tí ìyà Wa dé bá wọn ju pé wọ́n wí pé: “Dájúdájú àwa jẹ́ alábòsí.”');
INSERT INTO "aykyuni" VALUES(7,6,'7:6','Nítorí náà, dájúdájú A óò bi àwọn tí A ránṣẹ́ sí léèrè (nípa ìjẹ́pè). Dájúdájú A ó sì bi àwọn Òjíṣẹ́ léèrè (nípa ìjíṣẹ́).');
INSERT INTO "aykyuni" VALUES(7,7,'7:7','Dájúdájú A óò ròyìn (iṣẹ́ ọwọ́ wọn) fún wọn pẹ̀lú ìmọ̀. Àwa kò ṣàì wà pẹ̀lú wọn (pẹ̀lú ìmọ̀) ');
INSERT INTO "aykyuni" VALUES(7,8,'7:8','Òdodo ni òṣùwọ̀n Ọjọ́ yẹn. Nítorí náà, ẹnikẹ́ni tí àwọn òṣùwọ̀n (iṣẹ́ rere) rẹ̀ bá tẹ̀ wọ̀n; àwọn wọ̀nyẹn, àwọn ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(7,9,'7:9','Ẹnikẹ́ni tí àwọn òṣùwọ̀n (iṣẹ́ rere) rẹ̀ bá fúyẹ́, àwọn wọ̀nyẹn ni àwọn tó ṣ’ẹ̀mí ara wọn lófò nítorí pé wọ́n ń ṣàbòsí sí àwọn āyah Wa.');
INSERT INTO "aykyuni" VALUES(7,10,'7:10','Dájúdájú A fún yín ní ipò àti ibùgbé lórí ilẹ̀. A sì ṣe ọ̀nà ìjẹ-ìmu fún yín lórí rẹ̀. Díẹ̀ ni ọpẹ́ tí ẹ̀ ń dá.');
INSERT INTO "aykyuni" VALUES(7,11,'7:11','Dájúdájú A da yín, lẹ́yìn náà A ya àwòrán yín, lẹ́yìn náà A sọ fún àwọn mọlāika pé: “Ẹ forí kanlẹ̀ kí Ādam.” Wọ́n sì forí kanlẹ̀ kí i àfi ’Iblīs, tí kò sí nínú àwọn olùforíkanlẹ̀.');
INSERT INTO "aykyuni" VALUES(7,12,'7:12','(Allāhu) sọ pé: “Kí ni ó kọ̀ fún ọ láti forí kanlẹ̀ kí i nígbà tí Mo pa á láṣẹ fún ọ.” (Èṣù) wí pé: “Èmi lóore jù ú lọ; O dá èmi láti ara iná, O sì dá òun láti inú ẹrẹ̀.”');
INSERT INTO "aykyuni" VALUES(7,13,'7:13','(Allāhu) sọ pé: “Sọ̀kalẹ̀ kúrò níbí nítorí pé kò lẹ́tọ̀ọ́ fún ọ láti ṣègbéraga níbí. Nítorí náà, jáde dájúdájú ìwọ ń bẹ nínú àwọn ẹni yẹpẹrẹ.”');
INSERT INTO "aykyuni" VALUES(7,14,'7:14','(Èṣù) wí pé: “Lọ́ mi lára títí di ọjọ́ tí wọn yóò gbé àwọn ènìyàn dìde.”');
INSERT INTO "aykyuni" VALUES(7,15,'7:15','(Allāhu) sọ pé: “Dájúdájú ìwọ ń bẹ nínú àwọn tí A óò lọ́ lára.”');
INSERT INTO "aykyuni" VALUES(7,16,'7:16','(Èṣù) wí pé: “Fún wí pé O ti fi mí sínú anù, èmi yóò jókòó dè wọ́n ní ọ̀nà tààrà Rẹ.');
INSERT INTO "aykyuni" VALUES(7,17,'7:17','Lẹ́yìn náà, dájúdájú èmi yóò wá bá wọn láti iwájú wọn, láti ẹ̀yìn wọn, láti ọ̀tún wọn àti òsì wọn. Ìwọ kò sì níí rí ọ̀pọ̀lọ́pọ̀ wọn ní olùdúpẹ́ (fún Ọ).”');
INSERT INTO "aykyuni" VALUES(7,18,'7:18','(Allāhu) sọ pé: “Jáde kúrò níbí (kí o sì di) alábùkù, ẹni-àlédànù. Dájúdájú ẹnikẹ́ni tó bá tẹ̀lé ọ nínú wọn, dájúdájú Èmi yóò fi gbogbo yín kún inú iná Jahanamọ.”');
INSERT INTO "aykyuni" VALUES(7,19,'7:19','Ādam, kí ìwọ àti ìyàwó rẹ máa gbé nínú Ọgbà Ìdẹ̀ra. Nítorí náà, ẹ máa jẹ níbikíbi tí ẹ bá fẹ́. Kí ẹ sì má ṣe súnmọ́ igi yìí nítorí kí ẹ má baà wà nínú àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(7,20,'7:20','Èṣù sì kó ròyíròyí bá àwọn méjèèjì nítorí kí ó lè fi ohun tí A fi pamọ́ nínú ìhòhò ara wọn hàn wọ́n. Ó sì wí pé: “Olúwa ẹ̀yin méjèèjì kò kọ igi yìí fún yín bí kò ṣe pé kí ẹ̀yin méjèèjì má baà di mọlāika tàbí kí ẹ̀yin méjèèjì má baà di olùṣegbére.”');
INSERT INTO "aykyuni" VALUES(7,21,'7:21','Ó sì búra fún àwọn méjèèjì pé: “Dájúdájú èmi wà nínú àwọn onímọ̀ràn fún ẹ̀yin méjèèjì.”');
INSERT INTO "aykyuni" VALUES(7,22,'7:22','Ó sì fi ẹ̀tàn mú àwọn méjèèjì balẹ̀ (kúrò níbi ìtẹ̀lé àṣẹ síbi ìyapa àṣẹ). Nígbà tí àwọn méjèèjì tọ́ igi náà wò, ìhòhò àwọn méjèèjì hàn síra wọn. Wọ́n bá bẹ̀rẹ̀ sí fi àwọn ewé Ọgbà Ìdẹ̀ra bora wọn. Olúwa àwọn méjèèjì sì pè wọ́n pé: “Ǹjẹ́ Èmi kò ti kọ igi náà fun ẹ̀yin méjèèjì? (Ṣé) Èmi kò sì ti sọ fún ẹ̀yin méjèèjì pé ọ̀tá pọ́nńbélé ni Èṣù jẹ́ fún yín?”');
INSERT INTO "aykyuni" VALUES(7,23,'7:23','Àwọn méjèèjì sọ pé: “Olúwa wa, a ti ṣàbòsí sí ẹ̀mí wa. Tí O ò bá foríjìn wá, kí O sì kẹ́ wa, dájúdájú a máa wà nínú àwọn ẹni òfò.” 1 ');
INSERT INTO "aykyuni" VALUES(7,24,'7:24','(Allāhu) sọ pé: “Ẹ sọ̀kalẹ̀, ọ̀tá ní apá kan yín fún apá kan. Ibùgbé àti n̄ǹkan ìgbádùn sì ń bẹ fún yín ní orí ilẹ̀ fún ìgbà (díẹ̀).”');
INSERT INTO "aykyuni" VALUES(7,25,'7:25','(Allāhu) sọ pé: “Lórí ilẹ̀ ni ẹ̀yin yóò ti máa ṣẹ̀mí, lórí rẹ̀ ni ẹ̀yin yóò máa kú sí, A ó sì mu yín jáde láti inú rẹ̀.”');
INSERT INTO "aykyuni" VALUES(7,26,'7:26','Ọmọ (Ànábì) Ādam, dájúdájú A ti sọ aṣọ kalẹ̀ fún yín, tí ó máa bo ìhòhò yín àti ohun àmúṣọrọ̀. Aṣọ ìbẹ̀rù Allāhu, ìyẹn l’ó sì lóore jùlọ. Ìyẹn wà nínú àwọn àmì Allāhu nítorí kí wọ́n lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(7,27,'7:27','Ọmọ (Ànábì) Ādam, ẹ má ṣe jẹ́ kí Èṣù kó ìfòòró ba yín gẹ́gẹ́ bí ó ṣe yọ àwọn òbí yín méjèèjì jáde kúrò nínú Ọgbà Ìdẹ̀ra. Ó gba aṣọ kúrò lára àwọn méjèèjì nítorí kí ó lè fi ìhòhò wọn hàn wọ́n. Dájúdájú ó ń ri yín; òun àti àwọn ọmọ ogun rẹ̀ (ń ri yín) ní àyè tí ẹ̀yin kò ti rí wọn. Dájúdájú Àwa fi Èṣù ṣe ọ̀rẹ́ fún àwọn tí kò gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(7,28,'7:28','Nígbà tí wọ́n bá ṣe ìbàjẹ́ kan, wọ́n á wí pé: “A bá àwọn bàbá wa lórí rẹ̀ ni. Allāhu l’Ó sì pa á láṣẹ fún wa.” Sọ pé: “Dájúdájú Allāhu kì í p’àṣẹ ìbàjẹ́. Ṣé ẹ fẹ́ ṣàfitì ohun tí ẹ ò nímọ̀ nípa rẹ̀ sọ́dọ̀ Allāhu ni?”');
INSERT INTO "aykyuni" VALUES(7,29,'7:29','Sọ pé: “Olúwa mi p’àṣẹ ṣíṣe déédé. Kí ẹ sì dojú yín kọ (Allāhu) ní gbogbo mọ́sálásí. Ẹ pè É lẹ́ni tí yóò máa fi àfọ̀mọ́-ọkàn (àníyàn mímọ́) ṣe ẹ̀sìn fún Un.Gẹ́gẹ́ bí Ó ṣe da yín (tí ẹ fi di alààyè), ẹ máa padà (di alààyè lẹ́yìn ikú yín).” ');
INSERT INTO "aykyuni" VALUES(7,30,'7:30','Apá kan ni Ó fi mọ̀nà, apa kan sì ni ìṣìnà kò lé lórí. (Nítorí pé) dájúdájú wọ́n mú àwọn èṣù ní aláfẹ̀yìntí lẹ́yìn Allāhu. Wọ́n sì ń lérò pé dájúdájú àwọn ni olùmọ̀nà.');
INSERT INTO "aykyuni" VALUES(7,31,'7:31','Ọmọ (Ànábì) Ādam, ẹ wọ (aṣọ) ọ̀ṣọ́ yín nígbàkígbà tí ẹ bá ń lọ sí mọ́sálásí. Ẹ jẹ, ẹ mu, kí ẹ sì má yàpà. Dájúdájú (Allāhu) kò nífẹ̀ẹ́ àwọn àpà.');
INSERT INTO "aykyuni" VALUES(7,32,'7:32','Sọ pé: “Ta l’ó ṣe ọ̀ṣọ́ Allāhu, tí Ó mú jáde fún àwọn ẹrúsìn Rẹ̀ àti àwọn n̄ǹkan dáadáa nínú arísìkí ní èèwọ̀?” Sọ pé: “Ó wà fún àwọn tó gbàgbọ́ lódodo nínú ìṣẹ̀mí ayé. Tiwọn nìkan sì ni l’Ọ́jọ́ Àjíǹde.” Báyẹn ni A ṣe ń ṣàlàyé àwọn āyah fún ìjọ tó nímọ̀.”');
INSERT INTO "aykyuni" VALUES(7,33,'7:33','Sọ pé: “Ohun tí Olúwa mi ṣe ní èèwọ̀ ni àwọn ìwà ìbàjẹ́ - èyí tó fojú hàn nínú rẹ̀ àti èyí tó pamọ́ -, ìwà ẹ̀ṣẹ̀, ọ̀tẹ̀ ṣíṣe láì lẹ́tọ̀ọ́, bíbá Allāhu wá akẹgbẹ́ - èyí tí kò sọ ẹ̀rí kan kalẹ̀ fún - àti ṣíṣe àfitì ohun tí ẹ ò nímọ̀ nípa rẹ̀ sọ́dọ̀ Allāhu.”');
INSERT INTO "aykyuni" VALUES(7,34,'7:34','Àkókò kan ti wà fún ìjọ kọ̀ọ̀kan; nígbà tí àkókò wọn bá sì dé, wọn kò níí lè sún un ṣíwájú di àkókò kan, wọn kò sì níí lè fà á sẹ́yìn.1 ');
INSERT INTO "aykyuni" VALUES(7,35,'7:35','Ọmọ (Ànábì) Ādam, nígbà tí àwọn Òjíṣẹ́ nínú yín bá wá ba yín, tí wọn yóò máa ka àwọn āyah Mi fún yín, ẹnikẹ́ni tí ó bá bẹ̀rù (Mi), tí ó sì ṣàtúnṣe, kò níí sí ìbẹ̀rù kan fún wọn, wọn kò sì níí banújẹ́.');
INSERT INTO "aykyuni" VALUES(7,36,'7:36','Àwọn tó bá sì pe àwọn āyah Wa nírọ́, tí wọ́n sì ṣègbéraga sí i, àwọn wọ̀nyẹn ni èrò inú Iná. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(7,37,'7:37','Nítorí náà, ta l’ó ṣàbòsí ju ẹni tó dá àdápa irọ́ mọ́ Allāhu tàbí tó pe àwọn āyah Rẹ̀ ní irọ́? Ìpín àwọn wọ̀nyẹn nínú kádàrá yóò máa tẹ̀ wọ́n lọ́wọ́ títí di ìgbà tí àwọn Òjíṣẹ́ Wa yóò wá bá wọn, tí wọn yóò gba ẹ̀mí wọn. Wọn yó sì sọ pé: “Níbo ni àwọn n̄ǹkan tí ẹ̀yin ń pè lẹ́yìn Allāhu wà?” Wọn yóò wí pé: “Wọ́n ti di òfò mọ́ wa lọ́wọ́.” Wọ́n sì jẹ́rìí léra wọn lórí pé dájúdájú àwọn jẹ́ aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(7,38,'7:38','(Allāhu) sọ pé: “Ẹ wọlé ti àwọn ìjọ tó ti ré kọjá lọ ṣíwájú yín nínú àwọn àlùjànnú àti ènìyàn (tí wọ́n ti wà) nínú Iná.” Ìgbàkígbà tí ìjọ kan bá wọ (inú Iná), wọn yóò máa ṣẹ́bi fún àwọn ìjọ (irú) rẹ̀ (tó ti wà níbẹ̀ ṣíwájú wọn), títí di ìgbà tí gbogbo wọn yóò fi pàdé ara wọn nínú Iná. (Ìgbà yìí ni) àwọn ẹni ìkẹ́yìn wọn yóò wí fún àwọn ẹni ìṣáájú wọn pé: “Olúwa wa, àwọn wọ̀nyí ni wọ́n ṣì wá lọ́nà. Nítorí náà, fún wọn ni àdìpèlé ìyà nínú Iná.” (Allāhu) sọ pé: “Ìkọ̀ọ̀kan (yín) l’ó ní àdìpèlé ìyà, ṣùgbọ́n ẹ̀yin kò mọ̀.”');
INSERT INTO "aykyuni" VALUES(7,39,'7:39','Àwọn ẹni àkọ́kọ́ wọn yó sí wí fún àwọn ẹni ìkẹ́yìn wọn pé: “Kò sí àjùlọ kan fún yín lórí wa. Nítorí náà, ẹ tọ́ Ìyà wò nítorí ohun tí ẹ̀ ń ṣe níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(7,40,'7:40','Dájúdájú àwọn tó pe àwọn āyah Wa ní irọ́, tí wọ́n sì ṣègbéraga sí i, wọn kò níí ṣí àwọn ìlẹ̀kùn sánmọ̀ fún wọn, wọn kò sì níí wọ inú Ọgbà Ìdẹ̀ra àfi tí ràkúnmí bá lè wọ inú ihò ìdí abẹ́rẹ́. Báyẹn ni A ṣe ń san àwọn ẹlẹ́ṣẹ̀ ní ẹ̀san.');
INSERT INTO "aykyuni" VALUES(7,41,'7:41','Ìtẹ́ wà fún wọn nínú iná Jahanamọ. Èbìbò iná sì wà fún wọn ní òkè wọn. Báyẹn ni A ṣe ń san àwọn alábòsí ní ẹ̀san.');
INSERT INTO "aykyuni" VALUES(7,42,'7:42','Àwọn tó sì gbàgbọ́ lódodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere - A kò làbọ ẹ̀mí kan lọ́rùn àfi ìwọ̀n agbára rẹ̀ - àwọn wọ̀nyẹn ni èrò inú Ọgbà Ìdẹ̀ra. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(7,43,'7:43','A ó sì mú inúnibíni kúrò nínú ọkàn wọn. Àwọn odò yó sì máa ṣàn ní ìsàlẹ̀ wọn. Wọn yóò sọ pé: “Gbogbo ẹyìn ń jẹ́ ti Allāhu,Ẹni tí Ó fi wá mọ̀nà yìí. Àwa ìbá tí mọ̀nà, tí kì í bá ṣe pé Allāhu tọ́ wa sí ọ̀nà. Dájúdájú àwọn Òjíṣẹ́ Olúwa wa ti mú òdodo wá.” Wọ́n sì máa pè wọ́n pé: “Ìyẹn ni Ọgbà Ìdẹ̀ra tí A jogún rẹ̀ fún yín nítorí ohun tí ẹ̀ ń ṣe níṣẹ́.”2 ');
INSERT INTO "aykyuni" VALUES(7,44,'7:44','Àwọn èrò inú Ọgbà Ìdẹ̀ra yóò pe àwọn èrò inú Iná pé: “A ti rí ohun tí Olúwa wa ṣe àdéhùn rẹ̀ fún wa ní òdodo. Ǹjẹ́ ẹ̀yin náà rí ohun tí Olúwa yín ṣe àdéhùn (rẹ̀ fún yín) ní òdodo?” Wọn yóò wí pé: “Bẹ́ẹ̀ ni.” Olùpèpè kan yó sì pèpè láààrin wọn pé: “Kí ibi dandan Allāhu máa bẹ lórí àwọn alábòsí.”');
INSERT INTO "aykyuni" VALUES(7,45,'7:45','(Àwọn ni) àwọn tó ń ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu, wọ́n sì ń fẹ́ kó wọ́. Àwọn sì ni aláìgbàgbọ́ nínú Ọjọ́ Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(7,46,'7:46','Gàgáyóò wà láààrin èrò inú Ọgbà Ìdẹ̀ra àti èrò inú Iná. Àwọn ènìyàn kan máa wà lórí ògiri gàgá náà,2 wọn yó sì dá ẹnì kọ̀ọ̀kan (nínú ìjọ méjèèjì) mọ̀ pẹ̀lú àmì wọn. Wọn yóò pe àwọn èrò inú Ọgbà Ìdẹ̀ra pé: “Kí àlàáfíà máa bẹ fún yín.” Àwọn ará orí gàgá kò ì wọ (inú) Ọgbà Ìdẹ̀ra, wọ́n sì ti ń jẹ̀rankàn (rẹ̀, wọ́n ti ní ìrètí pé àwọn náà máa wọ inú rẹ̀). ');
INSERT INTO "aykyuni" VALUES(7,47,'7:47','Nígbà tí wọ́n bá yíjú wọn sí ọ̀gangan àwọn èrò inú Iná, wọn yóò sọ pé: “Olúwa wa, má fi wá sọ́dọ̀ àwọn ìjọ alábòsí.”');
INSERT INTO "aykyuni" VALUES(7,48,'7:48','Àwọn ará orí ògiri gàgá yóò pe àwọn ènìyàn kan tí wọ́n mọ̀ wọ́n pẹ̀lú àmì wọn, wọn yó sì sọ pé: “Ohun tí ẹ kójọ nílé ayé àti ṣíṣe ìgbéraga yín sí ìgbàgbọ́ òdodo kò rọ̀ yín lọ́rọ̀ mọ́ (báyìí).”');
INSERT INTO "aykyuni" VALUES(7,49,'7:49','(Allāhu) yóò sọ fún èrò inú Iná pé: “Ṣé àwọn (èrò orí ògiri) wọ̀nyí ni ẹ̀yin ń búra pé Allāhu kò níí ṣíjú àánú wò? (Nítorí náà, ẹ̀yin èrò orí ògiri) ẹ wọ inú Ọgbà Ìdẹ̀ra. Kò níí sí ìbẹ̀rù kan fún yín. Ẹ̀yin kò sì níí banújẹ́.”');
INSERT INTO "aykyuni" VALUES(7,50,'7:50','Èrò inú Iná yóò pe èrò inú Ọgbà Ìdẹ̀ra pé: “Ẹ fún wa nínú omi tàbí nínú ohun tí Allāhu pa lésè fún yín.” Wọn yóò sọ pé: “Dájúdájú Allāhu ti ṣe méjèèjì ní èèwọ̀ fún àwọn aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(7,51,'7:51','Àwọn tó sọ ẹ̀sìn wọn di ìranù àti eré ṣíṣe, tí ìṣẹ̀mí ayé sì kó ẹ̀tàn bá wọn, ní òní ni A óò gbàgbé wọngẹ́gẹ́ bí wọ́n ṣe gbàgbé ìpàdé ọjọ́ wọn ti òní yìí àti (bí) wọ́n ṣe ń tako àwọn āyah Wa. ');
INSERT INTO "aykyuni" VALUES(7,52,'7:52','A kúkú ti mú tírà kan wá bá wọn, tí A ṣàlàyé rẹ̀ pẹ̀lú ìmọ̀. (Ó jẹ́) ìmọ̀nà àti ìkẹ́ fún ìjọ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(7,53,'7:53','Kí ni wọ́n ń retí bí kò ṣe ẹ̀san Rẹ̀?Lọ́jọ́ tí ẹ̀san Rẹ̀ bá dé, àwọn tó gbàgbé rẹ̀ ṣíwájú yóò wí pé: “Dájúdájú àwọn Òjíṣẹ́ Olúwa wa ti mú òdodo wá. Nítorí náà, ǹjẹ́ a lè rí àwọn olùṣìpẹ̀, kí wọ́n wá ṣìpẹ̀ fún wa tàbí kí wọ́n dá wa padà sílé ayé, kí á lè ṣe iṣẹ́ mìíràn yàtọ̀ sí èyí tí a máa ń ṣe?” Dájúdájú wọ́n ti ṣe ẹ̀mí wọn lófò. Ohun tí wọ́n sì ń dá ní àdápa irọ́ sì di òfo mọ́ wọn lọ́wọ́.2 ');
INSERT INTO "aykyuni" VALUES(7,54,'7:54','Dájúdájú Olúwa yín ni Allāhu, Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ fún ọjọ́ mẹ́fà. Lẹ́yìn náà, Ó gúnwà sí orí Ìtẹ́-ọlá. Ó ń fi òru bo ọ̀sán lójú, tí òru ń wá ọ̀sán ní kíákíá.Òòrùn, òṣùpá àti àwọn ìràwọ̀ ni wọ́n ti rọ̀ pẹ̀lú àṣẹ Rẹ̀. Gbọ́! TiRẹ̀ ni ẹ̀dá àti àṣẹ. Ìbùkún ni fún Allāhu, Olúwa gbogbo ẹ̀dá. ');
INSERT INTO "aykyuni" VALUES(7,55,'7:55','Ẹ pe Olúwa yín pẹ̀lú ìrawọ́rasẹ̀ àti ohùn jẹ́ẹ́jẹ́. Dájúdájú (Allāhu) kò fẹ́ràn àwọn alákọyọ.');
INSERT INTO "aykyuni" VALUES(7,56,'7:56','Ẹ má ṣèbàjẹ́ lórí ilẹ̀ lẹ́yìn àtúnṣe rẹ̀. Ẹ pe Allāhu pẹ̀lú ìbẹ̀rù àti ìrètí. Dájúdájú àánú Allāhu súnmọ́ àwọn olùṣe-rere.');
INSERT INTO "aykyuni" VALUES(7,57,'7:57','Òun ni Ẹni tó ń rán atẹ́gùn wá (tó jẹ́) ìró ìdùnnú ṣíwájú àánú Rẹ̀, títí (atẹ́gùn náà) yóò fi gbé ẹ̀ṣújò tó wúwo, tí A sì máa wà á lọ sí òkú ilẹ̀. Nígbà náà, A máa fi sọ omi kalẹ̀. A sì máa fi mú gbogbo àwọn èso jáde. Báyẹn ni A ó ṣe mú àwọn òkú jáde nítorí kí ẹ lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(7,58,'7:58','(Ilẹ̀) ìlú tó dára, àwọn irúgbìn rẹ̀ yóò jáde pẹ̀lú àṣẹ Olúwa rẹ̀. Èyí tí kò sì dára, (irúgbìn rẹ̀) kò níí jáde àfi (díẹ̀) pẹ̀lú ìnira. Báyẹn ni A ṣe ń mú àwọn āyah wá lóníran-ànran ọ̀nà fún ìjọ tó ń dúpẹ́.');
INSERT INTO "aykyuni" VALUES(7,59,'7:59','Dájúdájú A rán (Ànábì) Nūh níṣẹ́ sí ìjọ rẹ̀. Ó sì sọ pé: “Ẹ̀yin ìjọ mi, ẹ jọ́sìn fún Allāhu. Ẹ kò ní ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo lẹ́yìn Rẹ̀. Dájúdájú mò ń bẹ̀rù ìyà Ọjọ́ Ńlá fún yín.”');
INSERT INTO "aykyuni" VALUES(7,60,'7:60','Àwọn àgbààgbà nínú ìjọ rẹ̀ wí pé: “Dájúdájú àwa ń rí ọ pé o wà nínú ìṣìnà pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(7,61,'7:61','Ó sọ pé: “Ẹ̀yin ìjọ mi, kò sí ìṣìnà kan lọ́dọ̀ mi, ṣùgbọ́n dájúdájú èmi ni Òjíṣẹ́ kan láti ọ̀dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(7,62,'7:62','Mò ń jẹ́ àwọn iṣẹ́ Olúwa mi fún yín. Mò ń fún yín ní ìmọ̀ràn rere. Àti pé ohun tí ẹ ò mọ̀ ni èmi mọ̀ láti ọ̀dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(7,63,'7:63','Àti pé ṣé ẹ ṣèèmọ̀ pé ìrántí láti ọ̀dọ̀ Olúwa yín dé ba yín ni, (èyí tí wọ́n fún) ọkùnrin kan nínú yín nítorí kí ó lè kìlọ̀ fún yín; nítorí kí ẹ lè bẹ̀rù (Allāhu) àti nítorí kí wọ́n lè kẹ yín?”');
INSERT INTO "aykyuni" VALUES(7,64,'7:64','Wọ́n sì pè é lópùrọ́. Nítorí náà, A gba òun àti àwọn tó wà pẹ̀lú rẹ̀ là nínú ọkọ̀ ojú-omi. A sì tẹ àwọn tó pe àwọn āyah Wa nírọ́ rì. Dájúdájú wọ́n jẹ́ ìjọ tó fọ́jú (nípa òdodo).');
INSERT INTO "aykyuni" VALUES(7,65,'7:65','A tún rán (ẹnì kan) sí àwọn ará ‘Ād, arákùnrin wọn Hūd. Ó sọ pé: “Ẹ̀yin ìjọ mi, ẹ jọ́sìn fún Allāhu. Ẹ kò ní ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo lẹ́yìn Rẹ̀. Ṣé ẹ ò níí bẹ̀rù (Rẹ̀) ni?”');
INSERT INTO "aykyuni" VALUES(7,66,'7:66','Àwọn àgbààgbà tó ṣàì gbàgbọ́ nínú ìjọ rẹ̀ wí pé: “Dájúdájú àwa ń rí ọ pé o wà nínú agọ̀. Àti pé dájúdájú ohun tí à ń rò sí ọ ni pé, o wà nínú àwọn òpùrọ́.”');
INSERT INTO "aykyuni" VALUES(7,67,'7:67','Ó sọ pé: “Ẹ̀yin ìjọ mi, kò sí agọ̀ kan lára mi, ṣùgbọ́n dájúdájú èmi ni Òjíṣẹ́ kan láti ọ̀dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(7,68,'7:68','Mò ń jẹ́ àwọn iṣẹ́ Olúwa mi fún yín. Èmi sì ni onímọ̀ràn rere, olùfọkàntán fún yín.');
INSERT INTO "aykyuni" VALUES(7,69,'7:69','Àti pé ṣé ẹ ṣèèmọ̀ pé ìrántí láti ọ̀dọ̀ Olúwa yín dé ba yín ni, (èyí tí wọ́n fún) ọkùnrin kan nínú yín, nítorí kí ó lè kìlọ̀ fún yín? Ẹ rántí nígbà tí (Allāhu) fi yín ṣe àrólé lẹ́yìn ìjọ (Ànábì) Nūh. Ó tún ṣe àlékún agbára fún yín nínú ìṣẹ̀dá (yín). Nítorí náà, ẹ rántí àwọn ìdẹ̀ra Allāhu, nítorí kí ẹ lè jèrè.”');
INSERT INTO "aykyuni" VALUES(7,70,'7:70','Wọ́n wí pé: “Ṣé o wá bá wa nítorí kí á lè jọ́sìn fún Allāhu nìkan ṣoṣo àti nítorí kí á lè pa ohun tí àwọn bàbá ńlá wa ń jọ́sìn fún tì? Nítorí náà, mú ohun tí o ṣe ní ìlérí fún wa ṣẹ tí o bá wà nínú àwọn olódodo.”');
INSERT INTO "aykyuni" VALUES(7,71,'7:71','Ó sọ pé: “Ìyà àti ìbínú kúkú ti sọ̀kalẹ̀ sórí yín láti ọ̀dọ̀ Olúwa yín. Ṣé ẹ óò máa jà mí níyàn nípa àwọn orúkọ (òrìṣà) kan tí ẹ̀yin àti àwọn bàbá yín fún lórúkọ - Allāhu kò sì sọ ẹ̀rí kan kalẹ̀ lórí rẹ̀? - Nítorí náà, ẹ máa retí, dájúdájú èmi náà wà pẹ̀lú yín nínú àwọn olùretí.”');
INSERT INTO "aykyuni" VALUES(7,72,'7:72','Nítorí náà, pẹ̀lú àánú láti ọ̀dọ̀ Wa, A gba òun àti àwọn tó wà pẹ̀lú rẹ̀ là. A sì pa àwọn tó pe àwọn āyah Wa nírọ́ run pátápátá; wọn kì í ṣe onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(7,73,'7:73','A tún rán (ẹnì kan) sí àwọn ìran Thamūd, arákùnrin wọn Sọ̄lih. Ó sọ pé: “Ẹ̀yin ìjọ mi, ẹ jọ́sìn fún Allāhu. Ẹ kò ní ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo lẹ́yìn Rẹ̀. Ẹ̀rí kan (iṣẹ́ ìyanu kan) kúkú ti dé ba yín láti ọ̀dọ̀ Olúwa yín; èyí ni abo ràkúnmí Allāhu. (Ó jẹ́) àmì kan fún yín. Nítorí náà, ẹ fi sílẹ̀, kí ó máa jẹ kiri lórí ilẹ̀ Allāhu. Ẹ má ṣe fi ọwọ́ aburú kàn án nítorí kí ìyà ẹlẹ́ta-eléro má baà jẹ yín.');
INSERT INTO "aykyuni" VALUES(7,74,'7:74','Ẹ tún rántí nígbà tí (Allāhu) ṣe yín ní àrólé lẹ́yìn àwọn ara ‘Ād. Ó sì fún yín ní ilé ìgbé lórí ilẹ̀; ẹ̀ ń kọ́ ilé ńláńlá sínú pẹ̀tẹ́lẹ̀, ẹ sì ń gbẹ́ àwọn ilé sínú àpáta. Ẹ rántí àwọn ìdẹ̀ra Allāhu, kí ẹ sì má ṣèbàjẹ́ sórí ilẹ̀.');
INSERT INTO "aykyuni" VALUES(7,75,'7:75','Àwọn àgbààgbà tó ṣègbéraga nínú ìjọ rẹ̀ wí fún àwọn tí wọ́n fojú ọ̀lẹ wò (ìyẹn) àwọn tó gbàgbọ́ lódodo nínú wọn pé: “Ǹjẹ́ ẹ mọ̀ pé dájúdájú Sọ̄lih ni Òjíṣẹ́ láti ọ̀dọ̀ Olúwa rẹ̀?” Wọ́n sọ pé: “Dájúdájú àwa gbàgbọ́ nínú ohun tí wọ́n fi rán an níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(7,76,'7:76','Àwọn tó ṣègbéraga wí pé: “Dájúdájú àwa ṣàì gbàgbọ́ nínú ohun tí ẹ̀yin gbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(7,77,'7:77','Nítorí náà, wọ́n gún abo ràkúnmí náà pa. Wọ́n sì tàpá sí àṣẹ Olúwa wọn. Wọ́n sì wí pé: “Sọ̄lih, mú ohun tí o ṣe ní ìlérí fún wa ṣẹ tí o bá wà nínú àwọn Òjíṣẹ́.”');
INSERT INTO "aykyuni" VALUES(7,78,'7:78','Nítorí náà, ohùn igbe líle tó mi ilẹ̀ tìtì gbá wọn mú. Wọ́n sì di ẹni tó dà lulẹ̀, tí wọ́n ti dòkú sínú ìlú wọn.');
INSERT INTO "aykyuni" VALUES(7,79,'7:79','Nígbà náà, (Sọ̄lih) ṣẹ́rí kúrò lọ́dọ̀ wọn, ó sì sọ pé: “Ẹ̀yin ìjọ mi, mo kúkú ti jẹ́ iṣẹ́ Olúwa mi fún yín. Mo sì ti fún yín ní ìmọ̀ràn rere ṣùgbọ́n ẹ̀yin kò fẹ́ràn àwọn onímọ̀ràn rere.”');
INSERT INTO "aykyuni" VALUES(7,80,'7:80','(Rántí Ànábì) Lūt, nígbà tí ó sọ fún ìjọ rẹ̀ pé: “Ṣé ẹ óò máa ṣe ìbàjẹ́ ni tí kò sí ẹnì kan nínú àwọn ẹ̀dá tó ṣe irú rẹ̀ rí?');
INSERT INTO "aykyuni" VALUES(7,81,'7:81','Dájúdájú ẹ̀yin (ọkùnrin) ń lọ jẹ adùn (ìbálòpọ̀) lára àwọn ọkùnrin (ẹgbẹ́ yín) dípò àwọn obìnrin! Àní sẹ́, ìjọ alákọyọ ni yín.”');
INSERT INTO "aykyuni" VALUES(7,82,'7:82','Kí sì ni ó jẹ́ èsì ìjọ rẹ̀ bí kò ṣe pé, wọ́n wí pé: “Ẹ lé wọn jáde kúrò nínú ìlú yín, dájúdájú wọ́n jẹ́ ènìyàn kan tó ń fọra wọn mọ́ (níbi ẹ̀ṣẹ̀).”');
INSERT INTO "aykyuni" VALUES(7,83,'7:83','Nígbà náà, A gba òun àti ẹbí rẹ̀ là àfi ìyàwó rẹ̀ tí ó wà nínú àwọn tó ṣẹ́kù lẹ́yìn sínú ìparun.');
INSERT INTO "aykyuni" VALUES(7,84,'7:84','A rọ òjò kan lé wọn lórí tààrà. Wo bí ìkángun àwọn ẹlẹ́ṣẹ̀ ṣe rí!');
INSERT INTO "aykyuni" VALUES(7,85,'7:85','(A tún rán ẹnì kan) sí àwọn ará Mọdyan, arákùnrin wọn, Ṣu‘aeb. Ó sọ pé: “Ẹ̀yin ìjọ mi, ẹ jọ́sìn fún Allāhu. Ẹ kò ní ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo lẹ́yìn Rẹ̀. Dájúdájú ẹ̀rí tó yanjú ti dé ba yín láti ọ̀dọ̀ Olúwa yín. Nítorí náà, ẹ wọn kóńgò àti òṣùwọ̀n kún. Ẹ má ṣe dín n̄ǹkan àwọn ènìyàn kù. Ẹ sì má ṣe ìbàjẹ́ lórí ilẹ̀ lẹ́yìn àtúnṣe rẹ̀. Ìyẹn sì lóore jùlọ fún yín tí ẹ bá jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(7,86,'7:86','Ẹ má ṣe lúgọ sí gbogbo ọ̀nà láti máa dẹ́rùba àwọn ènìyàn; ẹ̀ ń ṣẹ́rí ẹni tó gbagbọ́ kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu, ẹ sì ń fẹ́ kí ó wọ́. Ẹ rántí o, nígbà tí ẹ kéré (lóǹkà), Allāhu sọ yín di púpọ̀. Ẹ sì wo bí ìkángun àwọn òbìlẹ̀jẹ́ ṣe rí.');
INSERT INTO "aykyuni" VALUES(7,87,'7:87','Tí ó bá jẹ́ pé igun kan nínú yín gbàgbọ́ nínú ohun tí wọ́n fi rán mi níṣẹ́, tí igun kan kò sì gbàgbọ́, nígbà náà ẹ ṣe sùúrù títí Allāhu yóò fi ṣe ìdájọ́ láààrin wa. Òun sì lóore jùlọ nínú àwọn olùdájọ́.');
INSERT INTO "aykyuni" VALUES(7,88,'7:88','Àwọn àgbààgbà tó ṣègbéraga nínú ìjọ rẹ̀ wí pé: “Dájúdájú a máa lé ìwọ Ṣu‘aeb àti àwọn tó gbàgbọ́ pẹ̀lú rẹ jáde kúrò nínú ìlú wa tàbí kí ẹ padà sínú ẹ̀sìn wa.” (Ṣu‘aeb) sọ pé: “Ṣé (ẹ máa dá wa padà sínú ìbọ̀rìṣà) tó sì jẹ́ pé ẹ̀mí wa kọ̀ ọ́?”');
INSERT INTO "aykyuni" VALUES(7,89,'7:89','Dájúdájú a ti dá àdápa irọ́ mọ́ Allāhu tí a bá fi lè padà sínú ẹ̀sìn yín lẹ́yìn ìgbà tí Allāhu ti yọ wá kúrò nínú rẹ̀. Àti pé kò lẹ́tọ̀ọ́ fún wa láti padà sínú rẹ̀ àfi tí Allāhu Olúwa wa bá fẹ́. Olúwa wa gbòòrò tayọ gbogbo n̄ǹkan pẹ̀lú ìmọ̀. Allāhu l’a gbáralé. Olúwa wa, ṣe ìdájọ́ láààrin àwa àti ìjọ wa pẹ̀lú òdodo, Ìwọ sì lóore jùlọ nínú àwọn olùdájọ́.”');
INSERT INTO "aykyuni" VALUES(7,90,'7:90','Àwọn àgbààgbà tó ṣàì gbàgbọ́ nínú ìjọ rẹ̀ wí pé: “Dájúdájú tí ẹ̀yin bá tẹ̀lé Ṣu‘aeb, nígbà náà ẹ̀yin ti di ẹni òfò nìyẹn.”');
INSERT INTO "aykyuni" VALUES(7,91,'7:91','Nítorí náà, ohùn igbe líle tó mi ilẹ̀ tìtì gbá wọn mú. Wọ́n sì di ẹni tó dà lulẹ̀, tí wọ́n ti dòkú sínú ìlú wọn.1 ');
INSERT INTO "aykyuni" VALUES(7,92,'7:92','Àwọn tó pe Ṣu‘aeb lópùrọ́ sì dà bí ẹni tí kò gbé nínú ìlú wọn rí; àwọn tó pe Ṣu‘aeb ní òpùrọ́ ni wọ́n jẹ́ ẹni òfò.');
INSERT INTO "aykyuni" VALUES(7,93,'7:93','Nítorí náà, (Ṣu‘aeb) ṣẹ́rí kúrò lọ́dọ̀ wọn, ó sì sọ pé: “Ẹ̀yin ìjọ mi, mo kúkú ti jẹ́ àwọn iṣẹ́ Olúwa mi fún yín. Mo sì ti fún yín ní ìmọ̀ràn rere. Báwo ni èmi yóò ṣe tún máa banújẹ́ lórí ìjọ aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(7,94,'7:94','A kò rán Ànábì kan sí ìlú kan láì jẹ́ pé A fi ìpọ́njú àti ìnira kan àwọn ará ìlú náà nítorí kí wọ́n lè rawọ́-rasẹ̀ (sí Allāhu).');
INSERT INTO "aykyuni" VALUES(7,95,'7:95','Lẹ́yìn náà, A fi (ohun) rere rọ́pò aburú (fún wọn) títí wọ́n fi pọ̀ (lóǹkà àti lọ́rọ̀). Wọ́n sì wí pé: “Ọwọ́ ìnira àti ìdẹ̀ra kúkú ti kan àwọn bàbá wa rí.”Nítorí náà, A gbá wọn mú lójijì, wọn kò sì fura. ');
INSERT INTO "aykyuni" VALUES(7,96,'7:96','Tí ó bá jẹ́ pé dájúdájú àwọn ará ìlú náà gbàgbọ́ lódodo ni, tí wọ́n sì bẹ̀rù (Allāhu), A ìbá ṣínà àwọn ìbùkún fún wọn láti inú sánmọ̀àti ilẹ̀. Ṣùgbọ́n wọ́n pe òdodo nírọ́. Nígbà náà, A mú wọn nítorí ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(7,97,'7:97','Ṣé àwọn ará ìlú náà fàyà balẹ̀ pé ìyà Wa (kò lè) dé bá wọn ní alẹ́ ni, nígbà tí wọ́n bá ń sùn lọ́wọ́?');
INSERT INTO "aykyuni" VALUES(7,98,'7:98','Ṣé àwọn ará ìlú náà fàyà balẹ̀ pé ìyà Wa (kò lè) dé bá wọn ní ìyálẹ̀ta ni, nígbà tí wọ́n bá ń ṣeré lọ́wọ́?');
INSERT INTO "aykyuni" VALUES(7,99,'7:99','Ṣé wọ́n fàyà balẹ̀ sí ète Allāhu ni? Kò mà sí ẹni tó máa fàyà balẹ̀ sí ète Allāhu àfi ìjọ olófò.1 ');
INSERT INTO "aykyuni" VALUES(7,100,'7:100','Ṣé kò hàn sí àwọn tó jogún ilẹ̀ lẹ́yìn àwọn onílẹ̀ pé tí ó bá jẹ́ pé A bá fẹ́ (bẹ́ẹ̀ ni), Àwa ìbá fi ẹ̀ṣẹ̀ wọn mú wọn, Àwa ìbá sì fi èdídí bo ọkàn wọn; wọn kò sì níí gbọ́rọ̀ (mọ́).');
INSERT INTO "aykyuni" VALUES(7,101,'7:101','Àwọn ìlú wọ̀nyí ni À ń fún ọ ní ìró nínú ìròyìn wọn. Dájúdájú àwọn Òjíṣẹ́ wọn ti mú àwọn ẹ̀rí tó yánjú wá bá wọn. (Ìkọ̀ọ̀kan ìjọ náà,) wọn kò kúkú níí gbàgbọ́ nínú ohun tí (ijọ́ tó ṣíwájú wọn) pè nírọ́ ṣíwájú (wọn). Báyẹn ni Allāhu ṣe ń fi èdídí bo ọkàn àwọn aláìgbàgbọ́.1 ');
INSERT INTO "aykyuni" VALUES(7,102,'7:102','Àwa kò rí (pípé) àdéhùn ní ọ̀dọ̀ ọ̀pọ̀lọ́pọ̀ wọn. Àti pé ńṣe ni A rí ọ̀pọ̀lọ́pọ̀ wọn ní òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(7,103,'7:103','Lẹ́yìn náà, A gbé (Ànábì) Mūsā dìde lẹ́yìn wọn pẹ̀lú àwọn àmì Wa sí Fir‘aon àti àwọn ìjòyè rẹ̀. Wọ́n sì ṣe àbòsí sí àwọn àmì náà. Nítorí náà, wo bí ìkángun àwọn òbìlẹ̀jẹ́ ṣe rí.');
INSERT INTO "aykyuni" VALUES(7,104,'7:104','(Ànábì) Mūsā sọ pé: “Fir‘aon, dájúdájú èmi ni Òjíṣẹ́ kan láti ọ̀dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(7,105,'7:105','Dandan ni fún mi pé èmi kò níí ṣàfitì ọ̀rọ̀ kan sọ́dọ̀ Allāhu àfi òdodo. Mo sì kúkú ti mú ẹ̀rí tó yanjú wá ba yín láti ọ̀dọ̀ Olúwa yín. Nítorí náà, jẹ́ kí àwọn ọmọ ’Isrọ̄’īl máa bá mi lọ.”');
INSERT INTO "aykyuni" VALUES(7,106,'7:106','(Fir‘aon) wí pé: “Tí o bá jẹ́ o mú àmì kan wá, mú un jáde tí ìwọ bá wà nínú àwọn olódodo.”');
INSERT INTO "aykyuni" VALUES(7,107,'7:107','Nígbà náà, ó ju ọ̀pá rẹ̀ sílẹ̀, ó sì di ejò pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(7,108,'7:108','Ó tún fa ọwọ́ rẹ̀ yọ, ó sì di funfun (gbòlà) fún àwọn olùwòran.');
INSERT INTO "aykyuni" VALUES(7,109,'7:109','Àwọn ìjòyè nínú ìjọ Fir‘aon wí pé: “Dájúdájú èyí ni onímọ̀ nípa idán pípa.');
INSERT INTO "aykyuni" VALUES(7,110,'7:110','Ó fẹ́ ko yín kúrò lórí ilẹ̀ yín ni.” (Fir‘aon wí pé: ) “Kí ni ohun tí ẹ máa mú wá ní ìmọ̀ràn?”1 ');
INSERT INTO "aykyuni" VALUES(7,111,'7:111','Wọ́n sì wí pé: “Fún òun àti arákùnrin rẹ̀ lọ́jọ́,kí o sì rán àwọn akónijọ2 sí àwọn ìlú, ');
INSERT INTO "aykyuni" VALUES(7,112,'7:112','(pé) kí gbogbo onímọ̀ nípa idán pípa wá bá ọ.”');
INSERT INTO "aykyuni" VALUES(7,113,'7:113','Àwọn òpìdán sì dé wá bá Fir‘aon. Wọ́n wí pé: “Dájúdájú ẹ̀san gbọ́dọ̀ wà fún wa tí àwa bá jẹ́ olùborí.”');
INSERT INTO "aykyuni" VALUES(7,114,'7:114','Ó wí pé: “Bẹ́ẹ̀ ni. Àti pé dájúdájú ẹ̀yin gbọ́dọ̀ wà nínú àwọn alásùn-únmọ́ (mi).”');
INSERT INTO "aykyuni" VALUES(7,115,'7:115','(Àwọn òpìdán) wí pé: “Mūsā, o máa (kọ́kọ́) ju ọ̀pá sílẹ̀ ni, tàbí àwa ni a máa (kọ́kọ́) jù ú sílẹ̀.”');
INSERT INTO "aykyuni" VALUES(7,116,'7:116','(Ànábì Mūsā) sọ pé: “Ẹ ju (tiyín) sílẹ̀ (ná).” Nígbà tí wọ́n jù ú sílẹ̀, wọ́n lo àlùpàyídà lójú àwọn ènìyàn. Wọ́n sì ṣẹ̀rùbà wọ́n. Àní sẹ́ wọ́n pidán ńlá.');
INSERT INTO "aykyuni" VALUES(7,117,'7:117','A sì fi ìmísí ránṣẹ́ sí (Ànábì) Mūsā pé: “Ju ọ̀pá rẹ sílẹ̀.” Nígbà náà, ó sì ń gbé ohun tí wọ́n ti pa nírọ́ kalẹ̀ mì kálókáló.');
INSERT INTO "aykyuni" VALUES(7,118,'7:118','Nítorí náà, òdodo hàn (ó sì fìdí múlẹ̀). Ohun tí wọ́n ń ṣe níṣẹ́ sì bàjẹ́.');
INSERT INTO "aykyuni" VALUES(7,119,'7:119','Nítorí náà, A borí wọn níbẹ̀ yẹn. Wọ́n sì darí wálé ní ẹni yẹpẹrẹ.');
INSERT INTO "aykyuni" VALUES(7,120,'7:120','Iṣẹ́ ìyanu (Ànábì Mūsā) mú àwọn òpìdán wó lulẹ̀, tí wọ́n forí kanlẹ̀ (fún Allāhu).');
INSERT INTO "aykyuni" VALUES(7,121,'7:121','Wọ́n sọ pé: “A gbàgbọ́ nínú Olúwa gbogbo ẹ̀dá,');
INSERT INTO "aykyuni" VALUES(7,122,'7:122','Olúwa (Ànábì) Mūsā àti Hārūn.”');
INSERT INTO "aykyuni" VALUES(7,123,'7:123','Fir‘aon wí pé: “Ẹ ti gbà Á gbọ́ ṣíwájú kí n̄g tó yọ̀ǹda fún yín! Dájúdájú èyí ni ète kan tí ẹ dá nínú ìlú nítorí kí ẹ lè kó àwọn ará ìlú jáde kúrò nínú rẹ̀. Nítorí náà, láìpẹ́ ẹ máa mọ̀.');
INSERT INTO "aykyuni" VALUES(7,124,'7:124','Dájúdájú mo máa gé ọwọ́ yín àti ẹsẹ̀ yín ní ìpasípayọ. Lẹ́yìn náà, dájúdájú mo máa kan gbogbo yín mọ́gi.”');
INSERT INTO "aykyuni" VALUES(7,125,'7:125','Wọ́n sọ pé: “Dájúdájú ọ̀dọ̀ Olúwa wa sì ni a máa fàbọ̀ sí.');
INSERT INTO "aykyuni" VALUES(7,126,'7:126','Àti pé o ò kórira kiní kan lára wa (o ò sì rí àlèébù kan lára wa) bí kò ṣe nítorí pé, a gba àwọn àmì Olúwa wa gbọ́ nígbà tí ó dé bá wa. Olúwa wa, fún wa ní omi sùúrù mu. Kí O sì pa wá sípò mùsùlùmí.”');
INSERT INTO "aykyuni" VALUES(7,127,'7:127','Àwọn ìjòyè nínú ìjọ Fir‘aon wí pé: “Ṣé ìwọ yóò fi Mūsā àti àwọn ènìyàn rẹ̀ sílẹ̀ nítorí kí wọ́n lè ṣèbàjẹ́ lórí ilẹ̀ àti nítorí kí ó lè pa ìwọ àti àwọn òrìṣà rẹ tì?” (Fir‘aon) wí pé: “A óò máa pa àwọn ọmọkùnrin wọn ni. A ó sì máa dá àwọn ọmọbìnrin wọn sí. Dájúdájú àwa ni alágbára lórí wọn.”');
INSERT INTO "aykyuni" VALUES(7,128,'7:128','(Ànábì) Mūsā sọ fún ìjọ rẹ̀ pé: “Ẹ fi Allāhu wá ìrànlọ́wọ́, kí ẹ sì ṣe sùúrù. Dájúdájú ti Allāhu ni ilẹ̀. Ó sì ń jogún rẹ̀ fún ẹni tó bá fẹ́ nínú àwọn ẹrúsìn Rẹ̀. Ìgbẹ̀yìn (rere) sì wà fún àwọn olùbẹ̀rù (Allāhu).”');
INSERT INTO "aykyuni" VALUES(7,129,'7:129','Wọ́n sọ pé: “Wọ́n ni wá lára ṣíwájú kí o tó wá bá wa àti lẹ́yìn tí o dé bá wa.” (Ànábì Mūsā) sọ pé: “Ó lè jẹ́ pé Allāhu yóò pa àwọn ọ̀tá yín run. Ó sì máa fi yín rọ́pò (wọn) lórí ilẹ̀. Nígbà náà, (Allāhu) yó sì wo bí ẹ̀yin náà yóò ṣe máa ṣe.”');
INSERT INTO "aykyuni" VALUES(7,130,'7:130','Dájúdájú A gbá ènìyàn Fir‘aon mú pẹ̀lú ọ̀dá òjò àti àdínkù tó bá àwọn èso nítorí kí wọ́n lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(7,131,'7:131','Nígbà tí ohun rere bá dé bá wọn, wọ́n á wí pé: “Tiwa ni èyí.” Tí aburú kan bá sì ṣẹlẹ̀ sí wọn, wọ́n á ṣàfitì aburú náà sọ́dọ̀ (Ànábì) Mūsā àti ẹni tó wà pẹ̀lú rẹ̀. Kíyè sí i, àmì aburú wọn kúkú wà (nínú kádàrá wọn) lọ́dọ̀ Allāhu, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò nímọ̀.');
INSERT INTO "aykyuni" VALUES(7,132,'7:132','Wọ́n sì wí pé: “Ohunkóhun tí o bá mú wá fún wa ní àmì láti fi pidán fún wa, àwa kò níí gbà ọ́ gbọ́.”');
INSERT INTO "aykyuni" VALUES(7,133,'7:133','Nítorí náà, A sì fi ẹ̀kún omi, àwọn eṣú, àwọn kòkòrò iná, àwọn ọ̀pọ̀lọ́ àti ẹ̀jẹ̀ ránṣẹ́ sí wọn ní àwọn àmì tí ń tẹ̀léra wọn tó fojú hàn. Ńṣe ni wọ́n tún ṣègbéraga; wọ́n sì jẹ́ ìjọ ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(7,134,'7:134','Nígbà tí ìyà sọ̀kalẹ̀ lé wọn lórí, wọ́n wí pé: “Mūsā, pe Olúwa rẹ fún wa nítorí àdéhùn tí Ó ṣe fún ọ. Dájúdájú tí o bá fi lè gbé ìyà náà kúrò fún wa (pẹ̀lú àdúà rẹ), dájúdájú a máa gbà ọ́ gbọ́, dájúdájú a sì máa jẹ́ kí àwọn ọmọ ’Isrọ̄’īl máa bá ọ lọ.”');
INSERT INTO "aykyuni" VALUES(7,135,'7:135','Nígbà tí A bá gbé ìyà náà kúrò fún wọn fún àkókò kan tí wọn yóò lò (nínú ìṣẹ̀mí wọn), nígbà náà ni wọ́n tún ń yẹ àdéhùn.');
INSERT INTO "aykyuni" VALUES(7,136,'7:136','Nítorí náà, A gbẹ̀san lára wọn, A sì tẹ̀ wọ́n rì sínú agbami odò nítorí pé, wọ́n pe àwọn āyah Wa nírọ́. Wọ́n sì jẹ́ afọ́núfọ́ra nípa rẹ̀.');
INSERT INTO "aykyuni" VALUES(7,137,'7:137','A sì jogún àwọn ibùyọ òòrùn lórí ilẹ̀ ayé àti ibùwọ̀ òòrùn rẹ̀, tí A fi ìbùnkún sí, fún àwọn ènìyàn tí wọ́n kà sí ọ̀lẹ. Ọ̀rọ̀ Olúwa Rẹ tó dára sì ṣẹ lórí àwọn ọmọ ’Isrọ̄’īl nítorí pé, wọ́n ṣe sùúrù. A sì pa ohun tí Fir‘aon àti ìjọ rẹ̀ ń ṣe níṣẹ́ àti ohun tí wọ́n ń kọ́ nílé run.');
INSERT INTO "aykyuni" VALUES(7,138,'7:138','A mú àwọn ọmọ ’Isrọ̄‘īl la agbami òkun kọjá. Nígbà náà, wọ́n kọjá lọ́dọ̀ àwọn ènìyàn kan tí wọ́n dúró ti àwọn òrìṣà wọn. Wọ́n sì wí pé: “Mūsā, ṣe òrìṣà kan fún wa, gẹ́gẹ́ bí àwọn (wọ̀nyí) ṣe ní àwọn òrìṣà kan.” (Ànábì) Mūsā sọ pé: “Dájúdájú ẹ̀yin ni ìjọ aláìmọ̀kan.1 ');
INSERT INTO "aykyuni" VALUES(7,139,'7:139','Dájúdájú àwọn wọ̀nyí, ohun tí wọ́n ń ṣe (yìí) máa parun. Ohun tí wọ́n ń ṣe sì máa di òfo.”');
INSERT INTO "aykyuni" VALUES(7,140,'7:140','(Ànábì) Mūsā sọ pé: “Ṣé kí èmi tún ba yín wá ọlọ́hun kan tí ẹ̀yin yóò máa jọ́sìn fún lẹ́yìn Allāhu ni? Òun l’Ó sì ṣe àjùlọ oore fún yín lórí àwọn ẹ̀dá (àsìkò yín).');
INSERT INTO "aykyuni" VALUES(7,141,'7:141','(Ẹ rántí) nígbà tí A là yín lọ́wọ́ àwọn ènìyàn Fir‘aon tí wọ́n ń fi ìyà burúkú jẹ yín, wọ́n ń pa àwọn ọmọkùnrin yín tààrà, wọ́n sì ń dá àwọn ọmọbìnrin yín sí. Àdánwò ńlá wà nínú ìyẹn láti ọ̀dọ̀ Olúwa yín.”');
INSERT INTO "aykyuni" VALUES(7,142,'7:142','Àti pé A yan ọgbọ̀n òru fún (Ànábì) Mūsā. A tún fi mẹ́wàá kún un. Nítorí náà, àkókò (tí) Olúwa Rẹ̀(dá fún un fún ìbánisọ̀rọ̀) pé ní òru ogójì.(Ànábì) Mūsā sọ fún arákùnrin rẹ̀ Hārūn, pé: “Rólé dè mí láààrin àwọn ènìyàn mi. Kí o máa ṣe àtúnṣe. Má sì ṣe tẹ̀lé ọ̀nà àwọn òbìlẹ̀jẹ́.” ');
INSERT INTO "aykyuni" VALUES(7,143,'7:143','Nígbà tí (Ànábì) Mūsā dé ní àkókò tí A fún un (fún ìbánisọ̀rọ̀ náà), Olúwa rẹ̀ sì bá a sọ̀rọ̀. (Ànábì Mūsā) sọ pé: “Olúwa mi, fi ara Rẹ hàn mí, kí èmi lè rí Ọ.” (Allāhu) sọ pé: “Ìwọ kò lè rí Mi. Ṣùgbọ́n wo àpáta (yìí), tí ó bá dúró ṣinṣin sí àyè rẹ̀, láìpẹ́ o máa rí Mi.” Nígbà tí Olúwa rẹ̀ sì (rọra) fi ara Rẹ̀ han àpáta, Ó sọ ọ́ di pẹ̀tẹ́lẹ̀.(Ànábì) Mūsā sì ṣubú lulẹ̀, ó dákú. Nígbà tí ó jí, ó sọ pé: “Mímọ́ ni fún Ọ, èmi ronú pìwàdà sí ọ̀dọ̀ Rẹ. Èmi sì ni àkọ́kọ́ àwọn onígbàgbọ́ òdodo (ní àsìkò tèmi.)2 ');
INSERT INTO "aykyuni" VALUES(7,144,'7:144','(Allāhu) sọ pé: “Mūsā, dájúdájú Èmi ṣà ọ́ lẹ́ṣà lórí àwọn ènìyàn pẹ̀lú àwọn iṣẹ́ Mi àti ọ̀rọ̀ Mi.Nítorí náà, gbá ohun tí Mo fún ọ mú. Kí o sì wà nínú àwọn olùdúpẹ́.” ');
INSERT INTO "aykyuni" VALUES(7,145,'7:145','A sì kọ gbogbo n̄ǹkan fún un sínú àwọn wàláà. (Ó jẹ́) wáàsí àti àlàyé ọ̀rọ̀ fún gbogbo n̄ǹkan. Nítorí náà, ṣàmúlò rẹ̀ dáradára. Kí o sì pa ìjọ rẹ láṣẹ pé kí wọ́n ṣàmúlò n̄ǹkan dáadáa tó ń bẹ nínú rẹ̀. Èmi yóò fi ilé àwọn arúfin hàn yín.1 ');
INSERT INTO "aykyuni" VALUES(7,146,'7:146','Èmi yóò ṣẹ́rí àwọn tó ń ṣègbéraga lórí ilẹ̀ láì lẹ́tọ̀ọ́ kúrò níbi àwọn āyahMi; tí wọ́n bá rí gbogbo āyah, wọn kò níí gbà á gbọ́. Tí wọ́n bá rí ọ̀nà ìmọ̀nà, wọn kò níí mú un ní ọ̀nà. Tí wọ́n bá sì rí ọ̀nà ìṣìnà, wọn yóò mú un ní ọ̀nà. Ìyẹn nítorí pé, wọ́n pe àwọn āyah Wa nírọ́; wọ́n sì jẹ́ afọ́núfọ́ra nípa rẹ̀. ');
INSERT INTO "aykyuni" VALUES(7,147,'7:147','Àwọn tó sì pe àwọn āyah Wa àti ìpàdé Ọjọ́ Ìkẹ́yìn ní irọ́, àwọn iṣẹ́ wọn ti bàjẹ́. Ṣé A óò san wọ́n ní ẹ̀san kan ni bí kò ṣe ohun tí wọ́n ń ṣe níṣẹ́?');
INSERT INTO "aykyuni" VALUES(7,148,'7:148','Àwọn ènìyàn (Ànábì) Mūsā, lẹ́yìn rẹ̀ (nígbà tí ó fi lọ bá Allāhu sọ̀rọ̀), wọ́n mú nínú ọ̀ṣọ́ wọn (láti fi ṣe) ère ọmọ màálù ọ̀bọrọgidi, ó sì ń dún (bíi màálù). Ṣé wọn kò wòye sí i pé dájúdájú kò lè bá wọn sọ̀rọ̀ ni, kò sì lè fi ọ̀nà mọ̀ wọ́n? Wọ́n sì sọ ọ́ di àkúnlẹ̀bọ. Wọ́n sì jẹ́ alábòsí.');
INSERT INTO "aykyuni" VALUES(7,149,'7:149','Nígbà tí ó di àbámọ̀ mọ́ wọn lọ́wọ́ tán, wọ́n rí i pé àwọn ti ṣìnà, wọ́n wí pé: “Tí Olúwa wa kò bá ṣàánú wa, kí Ó sì foríjìn wá, dájúdájú àwa yóò wà nínú àwọn ẹni òfò.”');
INSERT INTO "aykyuni" VALUES(7,150,'7:150','Nígbà tí (Ànábì) Mūsā sì padà sí ọ̀dọ̀ ìjọ rẹ̀ pẹ̀lú ìbínú àti ìbànújẹ́, ó sọ pé: “Ohun tí ẹ fi rólé dè mí lẹ́yìn mi burú. Ṣé ẹ ti kánjú pa àṣẹ Olúwa yín tì ni?” Ó sì ju àwọn wàláà sílẹ̀, ó gbá orí arákùnrin rẹ̀ mú, ó sì ń wọ́ ọ mọ́ra. (Hārūn) sọ pé: “Ọmọ ìyá mi ò, dájúdájú àwọn ènìyàn ni wọ́n fojú kéré mi, wọ́n sì fẹ́ẹ̀ pa mí. Nítorí náà, má ṣe jẹ́ kí àwọn ọ̀tá yọ̀ mí. Má sì ṣe mú mi mọ́ ìjọ alábòsí.”');
INSERT INTO "aykyuni" VALUES(7,151,'7:151','(Ànábì Mūsā) sọ pé: “Olúwa mi, foríjin èmi àti arákùnrin mi. Kí O sì fi wá sínú ìkẹ́ Rẹ. Ìwọ sì ni Aláàánú-jùlọ nínú àwọn aláàánú.”');
INSERT INTO "aykyuni" VALUES(7,152,'7:152','Dájúdájú àwọn tó sọ (ère) ọmọ màálù di àkúnlẹ̀bọ, ìbínú láti ọ̀dọ̀ Olúwa wọn àti ìyẹpẹrẹ yóò bá wọn nínú ìṣẹ̀mí ayé yìí. Báyẹn ni A ṣe ń san àwọn aládapa irọ́ ní ẹ̀san.');
INSERT INTO "aykyuni" VALUES(7,153,'7:153','Àwọn tó sì ṣe àwọn iṣẹ́ aburú, lẹ́yìn náà tí wọ́n ronú pìwàdà lẹ́yìn rẹ̀, tí wọ́n sì gbàgbọ́ ní òdodo, dájúdájú lẹ́yìn èyí Olúwa rẹ ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(7,154,'7:154','Nígbà tí ìbínú (Ànábì) Mūsā sì wálẹ̀, ó mú àwọn wàláà náà. Ìmọ̀nà àti àánú ń bẹ nínú àkọsílẹ̀ rẹ̀ fún àwọn tó ń bẹ̀rù Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(7,155,'7:155','(Ànábì) Mūsā sì yan àádọ́rin ọkùnrin nínú ìjọ rẹ̀ fún àkókò tí A fún un (láti wá tọrọ àforíjìn fún àwọn ènìyàn rẹ̀. Nígbà tí wọ́n dé ibi àpáta Sīnā’), ohùn igbe líle tó mi ilẹ̀ tìtì sì gbá wọn mú, ó sọ pé: “Olúwa mi, tí O bá fẹ́ bẹ́ẹ̀ ni, Ìwọ ìbá ti pa àwọn àti èmi rẹ́ ṣíwájú (kí á tó wá síbí); ṣé Ìwọ yóò pa wá rẹ́ nítorí ohun tí àwọn òmùgọ̀ nínú wa ṣe ni? Kí ni ohun (tí wọ́n ṣe) bí kò ṣe àdánwò Rẹ; Ò ń fi ṣi ẹni tí O bá fẹ́ lọ́nà, O sì ń tọ́ ẹni tí O bá fẹ́ sọ́nà. Ìwọ ni Aláàbò wa. Nítorí náà, foríjìn wá, kí O sì kẹ́ wa. Ìwọ sì lóore jùlọ nínú àwọn aláforíjìn.');
INSERT INTO "aykyuni" VALUES(7,156,'7:156','Kí O sì kọ àkọsílẹ̀ rere fún wa ní ayé yìí àti ní ọ̀run. Dájúdájú àwa ṣẹ́rí padà sí ọ̀dọ̀ Rẹ.” (Allāhu) sọ pé: “Ìyà Mi, Mo ń fi jẹ ẹni tí Mo bá fẹ́. Àti pé ìkẹ́ Mi gbòòrò ju gbogbo n̄ǹkan. Èmi yó sì kọ (ìkẹ́ Mi) mọ́ àwọn tó ń bẹ̀rù (Mi), tí wọ́n sì ń yọ Zakāh àti àwọn tí wọ́n ní ìgbàgbọ́ òdodo nínú àwọn āyah Wa.”');
INSERT INTO "aykyuni" VALUES(7,157,'7:157','Àwọn tó ń tẹ̀lé Òjíṣẹ́ náà, Ànábì aláìmọ̀ọ́nkọmọ̀ọ́nkà, ẹni tí wọ́n yóò bá àkọsílẹ̀ nípa rẹ̀ lọ́dọ̀ wọn nínú at-Taorāh àti al-’Injīl, tí ó ń pa wọ́n láṣẹ ohun rere, tí ó ń kọ ohun burúkú fún wọn, tí ó ń ṣe àwọn n̄ǹkan dáadáa ní ẹ̀tọ́ fún wọn, tí ó sì ń ṣe àwọn n̄ǹkan àìdáa ní èèwọ̀ fún wọn, tí ó tún máa gbé ẹrù (àdéhùn tó wúwo) àti àjàgà tó ń bẹ lọ́rùn wọnkúrò fún wọn; àwọn tó bá gbàgbọ́ nínú rẹ̀, tí wọ́n bu iyì fún un, tí wọ́n ràn án lọ́wọ́, tí wọ́n sì tẹ̀lé ìmọ́lẹ̀ náà (ìyẹn, al-Ƙur’ān) tí A sọ̀kalẹ̀ fún un, àwọn wọ̀nyẹn, àwọn ni olùjèrè. ');
INSERT INTO "aykyuni" VALUES(7,158,'7:158','Sọ pé: “Ẹ̀yin ènìyàn, dájúdájú èmi ni Òjíṣẹ́ Allāhu sí gbogbo yín pátápátá. (Allāhu) Ẹni tó ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Ó ń sọ ẹ̀dá di alààyè. Ó sì ń sọ ẹ̀dá di òkú. Nítorí náà, ẹ gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀. Ànábì aláìmọ̀ọ́nkọmọ̀ọ́nkà, ẹni tó gbàgbọ́ nínú Allāhu àti àwọn ọ̀rọ̀ Rẹ̀. Ẹ tẹ̀lé e nítorí kí ẹ lè mọ̀nà.”');
INSERT INTO "aykyuni" VALUES(7,159,'7:159','Ó wà nínú ìjọ (Ànábì) Mūsā, ìjọ kan tó ń fi òdodo tọ́ (àwọn ènìyàn) sọ́nà. Wọ́n sì ń ṣe ẹ̀tọ́ pẹ̀lú rẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(7,160,'7:160','A sì pín wọn sí igun méjìlá (igun kọ̀ọ̀kan sì ní) ìran àti ìjọ. A sì fi ìmísí ránṣẹ́ sí (Ànábì) Mūsā, nígbà tí àwọn ènìyàn rẹ̀ wá omi wá sí ọ̀dọ̀ rẹ̀ pé: “Fi ọ̀pá rẹ na àpáta.” (Ó ṣe bẹ́ẹ̀) orísun omi méjìlá sì ṣẹ́yọ lára rẹ̀. Àwọn ènìyàn kọ̀ọ̀kan sì ti dá ibùmu wọn mọ̀. A tún fi ẹ̀ṣújò ṣe ibòji fún wọn. A sọ (ohun mímu) mọ́nnùàti (ohun jíjẹ) salwā2 kalẹ̀ fun wọ́n. Ẹ jẹ nínú àwọn n̄ǹkan dáadáa tí A pèsè fún yín. Wọn kò sì ṣe àbòsí sí Wa, ṣùgbọ́n ẹ̀mí ara wọn ni wọ́n ń ṣàbòsí sí. ');
INSERT INTO "aykyuni" VALUES(7,161,'7:161','(Ẹ rántí) nígbà tí A sọ fún wọn pé: “Ẹ wọ inú ìlú yìí. Ẹ máa jẹ nínú rẹ̀ níbikíbi tí ẹ bá fẹ́. Kí ẹ sì sọ pé: ‘Ha ẹ̀ṣẹ̀ wa dànù.’ Ẹ gba ẹnu-ọ̀nà (ìlú) náà wọlé ní ìtẹríba.A máa forí àwọn ẹ̀ṣẹ̀ yín jìn yín. A ó sì ṣe àlékún (ẹ̀san rere) fún àwọn olùṣe-rere.” ');
INSERT INTO "aykyuni" VALUES(7,162,'7:162','Àwọn tó ṣàbòsí yí ọ̀rọ̀ náà padà (sí n̄ǹkan mìíràn) yàtọ̀ sí èyí tí A sọ fún wọn. Nítorí náà, A fi ìyà ránsẹ́ sí wọn láti sánmọ̀ nítorí pé wọ́n ń ṣe àbòsí.');
INSERT INTO "aykyuni" VALUES(7,163,'7:163','Bi wọ́n léèrè nípa ìlú tó wà ní ẹ̀bádò, nígbà tí wọ́n kọjá ẹnu-ààlà nípa ọjọ́ Sabt, nígbà tí ẹja wọn ń wá bá wọn ní ọjọ́ Sabt wọn, wọ́n sì máa léfòó sí ojú odò, ọjọ́ tí kì í bá sì ṣe ọjọ́ Sabt wọn, wọn kò níí wá bá wọn. Báyẹn ni A ṣe ń dán wọn wò nítorí pé wọ́n jẹ́ òbìlẹ̀jẹ́.1 ');
INSERT INTO "aykyuni" VALUES(7,164,'7:164','(Rántí) nígbà tí ìjọ kan nínú wọn wí pé: “Nítorí kí ni ẹ fí ń ṣe wáàsí fún ìjọ kan tí Allāhu máa parẹ́ tàbí tí Ó máa jẹ ní ìyà tó lágbára?” Wọ́n sọ pé: “(Kí ó lè jẹ́) àwáwí lọ́dọ̀ Olúwa yín àti nítorí kí wọ́n lè bẹ̀rù (Allāhu) ni.”');
INSERT INTO "aykyuni" VALUES(7,165,'7:165','Nígbà tí wọ́n sì gbàgbé ohun tí wọ́n fi ṣèrántí fún wọn, A gba àwọn tó ń kọ aburú là. A sì fi ìyà tó le jẹ àwọn tó ṣàbòsí nítorí pé wọ́n jẹ́ òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(7,166,'7:166','Nígbà tí wọ́n tayọ ẹnu-ààlà níbi ohun tí A kọ̀ fún wọn, A sọ fún wọn pé: “Ẹ di ọ̀bọ, ẹni ìgbéjìnnà sí ìkẹ́.”');
INSERT INTO "aykyuni" VALUES(7,167,'7:167','(Rántí) nígbà tí Olúwa rẹ sọ ọ́ di mímọ̀ (fún wọn) pé dájúdájú Òun yóò gbé ẹni tí ó máa fi ìyà burúkú jẹ wọ́n dìde sí wọn títí di Ọjọ́ Àjíǹde. Dájúdájú Olúwa rẹ ni Olùyára níbi ìyà. Dájúdájú Òun sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(7,168,'7:168','A dá wọn kékekèle sí orí ilẹ̀ ní ìjọ-ìjọ; àwọn ẹni rere wà nínú wọn, àwọn mìíràn tún wà nínú wọn. A sì fi àwọn ohun rere àti ohun burúkú dán wọn wò kí wọ́n lè ṣẹ́rí padà (síbi òdodo).');
INSERT INTO "aykyuni" VALUES(7,169,'7:169','Àwọn àrólé kan sì rólé lẹ́yìn wọn; wọ́n jogún Tírà (Taorāt àti ’Injīl), wọ́n sì ń gba (àbẹ̀tẹ́lẹ̀) dúkìá ilé ayé yìí (láti kọ ìkọkúkọ sínú rẹ̀), wọ́n sì ń wí pé: “Wọn yóò foríjìn wá.” Tí (àbẹ̀tẹ́lẹ̀) dúkìá irú rẹ̀ bá tún wá bá wọn, wọ́n máa gbà á. Ṣé A kò ti bá wọn ṣe àdéhùn nínú Tírà pé, wọn kò gbọ́dọ̀ ṣe àfitì ọ̀rọ̀ kan sọ́dọ̀ Allāhu àfi òdodo? Wọ́n sì ti kẹ́kọ̀ọ́ nípa ohun tí ń bẹ nínú rẹ̀! Ilé ìkẹ́yìn sì lóore jùlọ fún àwọn tó ń bẹ̀rù (Allāhu). Ṣé ẹ ò níí ṣe làákàyè ni?');
INSERT INTO "aykyuni" VALUES(7,170,'7:170','Àwọn tó sì ń mú Tírà lò dáradára, tí wọ́n ń kírun, dájúdájú Àwa kò níí fi ẹ̀san àwọn tó ń ṣe àtúnṣe (iṣẹ́ wọn) ráre.1 ');
INSERT INTO "aykyuni" VALUES(7,171,'7:171','(Rántí) nígbà tí A gbé àpáta ga sókè orí wọn, ó sì dà bí ibòji. Wọ́n sì lérò pé ó máa wó lu àwọn mọ́lẹ̀, (A sì sọ fún wọn pé): “Ẹ gbá ohun tí A fún yín mú dáadáa, kí ẹ sì máa rántí ohun tó ń bẹ nínú rẹ̀, nítorí kí ẹ lè bẹ̀rù (Allāhu).”');
INSERT INTO "aykyuni" VALUES(7,172,'7:172','(Rántí) nígbà tí Olúwa rẹ gba àdéhùn lọ́wọ́ àwọn ọmọ Ādam nínú àrọ́mọdọ́mọ wọn tó fà jáde láti (ìbàdí ní) ẹ̀yìn (bàbá ńlá) wọn, Ó sì fi wọ́n ṣe ẹlẹ́rìí lórí ara wọn pé: “Ṣé Èmi kọ́ ni Olúwa yín ni?” Wọ́n sọ pé: “Bẹ́ẹ̀ ni – (Ìwọ ni Olúwa wa) - a jẹ́rìí sí i.” Nítorí kí ẹ má baà sọ ní Ọjọ́ Àjíǹdé pé: “Dájúdájú àwa jẹ́ aláìmọ̀ nípa èyí.”');
INSERT INTO "aykyuni" VALUES(7,173,'7:173','Tàbí kí ẹ má baà wí pé: “Àwọn bàbá wa ló kúkú bá Allāhu wá akẹgbẹ́ ṣíwájú (wa), àwa sì jẹ́ àrọ́mọdọ́mọ lẹ́yìn wọn (ni a fi wò wọ́n kọ́ṣe pẹ̀lú àìmọ̀kan). Ṣé Ìwọ yóò pa wá run nítorí ohun tí àwọn tó ń ba iṣẹ́ ọwọ́ ara wọn jẹ́ṣe ni?” ');
INSERT INTO "aykyuni" VALUES(7,174,'7:174','Báyẹn ni A ṣe ń ṣàlàyé àwọn āyah Wa, nítorí kí wọ́n lè ṣẹ́rí padà (síbi òdodo).');
INSERT INTO "aykyuni" VALUES(7,175,'7:175','Ka ìròyìn ẹni tí A fún ní àwọn āyah Wa fún wọn, tí ó yọra rẹ̀ sílẹ̀ níbi àwọn āyah náà.Nítorí náà, Èṣù tẹ̀lé e lẹ́yìn. Ó sì wà nínú àwọn olùṣìnà. ');
INSERT INTO "aykyuni" VALUES(7,176,'7:176','Àti pé tí ó bá jẹ́ pé A bá fẹ́, Àwa ìbá fi (ìmọ̀ nípa àwọn āyah Wa) ṣàgbéga fún un. Ṣùgbọ́n ó wayé mọ́yà. Ó sì tẹ̀lé ìfẹ́-inú rẹ̀. Nítorí náà, àfiwé rẹ̀ dà bí ajá. Tí o bá lé e síwájú, ó máa yọ ahọ́n síta. Tí o bá sì fi sílẹ̀, ó tún máa yọ ahọ́n síta. Ìyẹn ni àfiwé ìjọ tó pe àwọn āyah Wa nírọ́. Sọ ìtàn náà fún wọn nítorí kí wọ́n lè ronú jinlẹ̀.');
INSERT INTO "aykyuni" VALUES(7,177,'7:177','Ìjọ tí ó pe àwọn āyah Wa nírọ́, wọ́n burú ní àfiwé. Ara wọn sì ni wọ́n ń ṣàbòsí sí.');
INSERT INTO "aykyuni" VALUES(7,178,'7:178','Ẹnikẹ́ni tí Allāhu bá fi mọ̀nà (’Islām), òun ni olùmọ̀nà. Ẹnikẹ́ni tí Ó bá sì ṣì lọ́nà, àwọn wọ̀nyẹn, àwọn ni ẹni òfò.');
INSERT INTO "aykyuni" VALUES(7,179,'7:179','Dájúdájú A ti dá ọ̀pọ̀lọ́pọ̀ ènìyàn àti àlùjànnú fún iná Jahanamọ (nítorí pé) wọ́n ní ọkàn, wọn kò sì fi gbọ́ àgbọ́yé; wọ́n ní ojú, wọn kò sì fi ríran; wọ́n ní etí, wọn kò sì fi gbọ́ràn. Àwọn wọ̀nyẹn dà bí ẹran-ọ̀sìn. Wọ́n wulẹ̀ sọnù jùlọ. Àwọn wọ̀nyẹn, àwọn ni afọ́nú-fọ́ra.');
INSERT INTO "aykyuni" VALUES(7,180,'7:180','Ti Allāhu ni àwọn orúkọ tó dára jùlọ. Nítorí náà, ẹ fi pè É. Kí ẹ sì pa àwọn tó ń darí àwọn orúkọ Rẹ̀ kọ ọ̀nà òdì tì.A óò san wọ́n ní ẹ̀san ohun tí wọ́n ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(7,181,'7:181','Ó wà nínú àwọn tí A dá, ìjọ kan tó ń fi òdodo tọ́ (àwọn ènìyàn) sọ́nà, tí wọ́n sì ń ṣe ẹ̀tọ́ pẹ̀lú rẹ̀.');
INSERT INTO "aykyuni" VALUES(7,182,'7:182','Àwọn tó sì pe àwọn āyah Wa nírọ́, A óò máa dẹ wọ́n lẹ́kẹ láti jẹ wọ́n níyà ní àyè tí wọn kò níí mọ̀.');
INSERT INTO "aykyuni" VALUES(7,183,'7:183','Èmi yóò lọ́ wọn lára. Dájúdájú ète Mi lágbára.1 ');
INSERT INTO "aykyuni" VALUES(7,184,'7:184','Ṣé wọn kò ronú jinlẹ̀ ni? Kò sí wèrè kan kan lára ẹni wọn (Ànábì Muhammad - kí ìkẹ́ àti ọlà máa bá a -. Ta sì ni bí kò ṣe olùkìlọ̀ pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(7,185,'7:185','Ṣé wọn kò wo ìjọba àwọn sánmọ̀ àti ilẹ̀ pẹ̀lú gbogbo n̄ǹkan tí Allāhu dá? Àmọ́ sá ó lè jẹ́ pé Àkókò ikú wọn ti súnmọ́. Nígbà náà, ọ̀rọ̀ wo ni wọn yóò gbàgbọ́ lẹ́yìn rẹ̀?1 ');
INSERT INTO "aykyuni" VALUES(7,186,'7:186','Ẹnikẹ́ni tí Allāhu bá ṣì lọ́nà kò lè sí afinimọ̀nà kan fún un. (Allāhu) yó sì fi wọ́n sílẹ̀ sínú àgbéré wọn, tí wọn yóò máa pa rìdàrìdà.');
INSERT INTO "aykyuni" VALUES(7,187,'7:187','Wọ́n ń bi ọ́ léèrè nípa Àkókò náà pé ìgbà wo ni ìṣẹ̀lẹ̀ rẹ̀. Sọ pé: “Ọ̀dọ̀ Olúwa mi nìkan ni ìmọ̀ rẹ̀ wà. Kò sí ẹni tó lè ṣàfi hàn Àkókò rẹ̀ àfi Òun. Ó ṣòro (láti mọ̀) fún àwọn ará sánmọ̀ àti ará ilẹ̀. Kò níí dé ba yín àfi lójijì.” Wọ́n tún ń bi ọ́ léèrè bí ẹni pé ìwọ nímọ̀ nípa rẹ̀. Sọ pé: “Ọ̀dọ̀ Olúwa mi nìkan ni ìmọ̀ rẹ̀ wà, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ ènìyàn ni kò mọ̀.”');
INSERT INTO "aykyuni" VALUES(7,188,'7:188','Sọ pé: “Èmi kò ní agbára àǹfààní tàbí ìnira kan tí mo lè fi kan ara mi àyàfi ohun tí Allāhu bá fẹ́. Tí ó bá jẹ́ pé mo ní ìmọ̀ ìkọ̀kọ̀ ni, èmi ìbá ti kó ohun púpọ̀ jọ nínú oore ayé (sí ọ̀dọ̀ mi) àti pé aburú ayé ìbá tí kàn mí. Ta ni èmi bí kò ṣe olùkìlọ̀ àti oníròó ìdùnnú fún ìjọ onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(7,189,'7:189','(Allāhu) Òun ni Ẹni tó da yín láti ara ẹ̀mí ẹyọ kan. Ó sì dá aya fún un láti ara rẹ̀ nítorí kí ó lè jẹ̀gbádùn ìgbépọ̀ pẹ̀lú rẹ̀. Nígbà tí ọkọ súnmọ́ ìyàwó rẹ̀, tí ó sì ru ẹrù (àtọ̀) fífúyẹ́. Ó sì rù ú kiri. Nígbà tí ó sì diwọ́ disẹ̀ sínú tán, àwọn méjèèjì pe Allāhu Olúwa wọn pé: “Tí O bá fún wa ni ọmọ rere (tó pé ní ẹ̀dá), dájúdájú a máa wà nínú àwọn olùdúpẹ́.”');
INSERT INTO "aykyuni" VALUES(7,190,'7:190','Ṣùgbọ́n nígbà tí Allāhu fún àwọn méjèèjì ní ọmọ rere, wọ́n sọ (àwọn ẹ̀dá kan) di akẹgbẹ́ fún Un nípasẹ̀ ohun tí Ó fún àwọn méjèèjì. Allāhu sì ga tayọ n̄ǹkan tí wọ́n ń fi ṣẹbọ sí I.1 ');
INSERT INTO "aykyuni" VALUES(7,191,'7:191','Ṣé wọn yóò sọ n̄ǹkan tí kò lè dá n̄ǹkan kan di akẹgbẹ́ Rẹ̀, A sì ṣẹ̀dá wọn ni?');
INSERT INTO "aykyuni" VALUES(7,192,'7:192','Àwọn òrìṣà kò lè ṣe àrànṣe kan fún àwọn abọ̀rìṣà. Àti pé àwọn òrìṣà gan-an kò lè ṣe àrànṣe fún ẹ̀mí ara wọ́n.');
INSERT INTO "aykyuni" VALUES(7,193,'7:193','Tí ẹ bá pè wọ́n síbi ìmọ̀nà, wọn kò níí tẹ̀lé yín. Bákan náà ni fún yín; ẹ pè wọ́n tàbí ẹ̀yin dákẹ́ ẹnu (fún wọn).');
INSERT INTO "aykyuni" VALUES(7,194,'7:194','Dájúdájú àwọn tí ẹ̀ ń pè lẹ́yìn Allāhu, ẹrú bí irú yin ni wọ́n. Nítorí náà, ẹ pè wọ́n wò, kí wọ́n da yín lóhùn tí ẹ bá jẹ́ olódodo.');
INSERT INTO "aykyuni" VALUES(7,195,'7:195','Ṣé wọ́n ní ẹsẹ̀ tí wọ́n lè fi rìn ni? Tàbí ṣé wọ́n ní ọwọ́ tí wọ́n lè fi gbá n̄ǹkan mú? Tàbí ṣé wọ́n ní ojú tí wọ́n lè fi ríran? Tàbí ṣé wọ́n ní etí tí wọ́n lè fi gbọ́rọ̀? Sọ pé: “Ẹ pe àwọn òrìṣà yín, lẹ́yìn náà kí ẹ déte sí mi, kí ẹ sì má ṣe lọ́ mi lára.');
INSERT INTO "aykyuni" VALUES(7,196,'7:196','Dájúdájú Alátìlẹ́yìn mi ni Allāhu, Ẹni tí Ó sọ Tírà náà kalẹ̀. Àti pé Òun l’Ó ń ṣàtìlẹ́yìn fún àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(7,197,'7:197','Àwọn tí ẹ̀ ń pè lẹ́yìn Rẹ̀, wọn kò lè ṣe àrànṣe fún yín. Wọn kò sì lè ṣe àrànṣe fún ẹ̀mí ara wọ́n.');
INSERT INTO "aykyuni" VALUES(7,198,'7:198','Tí ẹ bá sì pè wọ́n sínú ìmọ̀nà, wọn kò níí gbọ́. Ò ń rí wọn pé wọ́n ń wò ọ́ ni, (ṣùgbọ́n) wọn kò ríran.');
INSERT INTO "aykyuni" VALUES(7,199,'7:199','Ṣàmójúkúrò, pàṣẹ ohun rere, kí o sì ṣẹ́rí kúrò lọ́dọ̀ àwọn aláìmọ̀kan.');
INSERT INTO "aykyuni" VALUES(7,200,'7:200','Tí ìbínú òdì kan bá sì ṣẹ́rí sínú ọkàn rẹ̀ láti ọ̀dọ̀ Èṣù, fi Allāhu wá ìṣọ́rí. Dájúdájú Òun ní Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(7,201,'7:201','Dájúdájú àwọn tó bẹ̀rù (Allāhu), nígbà tí ròyíròyí kan bá ṣẹlẹ̀ sí wọn láti ọ̀dọ̀ Èṣù, tí wọ́n bá rántí (Allāhu), nígbà náà ojú wọn yó sì ríran (rí òdodo).');
INSERT INTO "aykyuni" VALUES(7,202,'7:202','(Àmọ́ ní ti) àwọn ọmọ ìyá (Èṣù), ńṣe ni àwọn Èṣù yóò máa kún wọn lọ́wọ́ nínú ìṣìnà. Lẹ́yìn náà, wọn kò sì níí dáràn mọ níwọ̀n.');
INSERT INTO "aykyuni" VALUES(7,203,'7:203','Nígbà tí o ò bá mú àmì wá bá wọn, wọ́n á wí pé: “Ìwọ kò ṣe ṣe àdáhun rẹ̀?” Sọ pé: “Ohun tí Wọ́n fi ránṣẹ́ sí mi ní ìmísí láti ọ̀dọ̀ Olúwa mi ni mò ń tẹ̀lé. (al-Ƙur’ān) yìí sì ni ìmọ̀ àmọ̀dájú àti ẹ̀rí láti ọ̀dọ̀ Olúwa yín. Ìmọ̀nà àti ìkẹ́ sì ni fún àwọn ìjọ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(7,204,'7:204','Nígbà tí wọ́n ba ń ke al-Ƙur’ān, ẹ tẹ́tí sí i, kí ẹ sì dákẹ́ nítorí kí A lè kẹ yín.');
INSERT INTO "aykyuni" VALUES(7,205,'7:205','Ṣe ìrántí Olúwa Rẹ nínú ẹ̀mí rẹ pẹ̀lú ìrawọ́rasẹ̀ àti ìbẹ̀rù (Allāhu), kò sì níí jẹ́ ọ̀rọ̀ ariwo, ní òwúrọ̀ àti ní ìrọ̀lẹ́. Ìwọ kò sì gbọdọ̀ wà nínú àwọn olùgbàgbéra (nípa ìrántí Allāhu).');
INSERT INTO "aykyuni" VALUES(7,206,'7:206','Dájúdájú àwọn tó ń bẹ lọ́dọ̀ Olúwa rẹ, wọn kì í jọra wọn lójú láti jọ́sìn fún Un. Wọ́n ń ṣàfọ̀mọ́ fún Un. Wọ́n sì ń forí kanlẹ̀ fún Un.');
INSERT INTO "aykyuni" VALUES(8,1,'8:1','Wọ́n ń bi ọ́ léèrè nípa ọrọ̀ ogun. Sọ pé: “Ọrọ̀ ogun jẹ́ ti Allāhu àti ti Òjíṣẹ́. Nítorí náà, ẹ bẹ̀rù Allāhu. Kí ẹ sì ṣe àtúnṣe n̄ǹkan tí ń bẹ láààrin yín. Ẹ tẹ̀lé ti Allāhu àti Òjíṣẹ́ Rẹ̀ tí ẹ bá jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(8,2,'8:2','Àwọn onígbàgbọ́ òdodo ni àwọn tó jẹ́ pé nígbà tí wọ́n bá fi (ìyà) Allāhu ṣe ìrántí (fún wọn), ọkàn wọn yóò wárìrì, nígbà tí wọ́n bá sì ké àwọn āyah Rẹ̀ fún wọn, wọn yóò lékún ní ìgbàgbọ́ òdodo. Olúwa wọn sì ni wọ́n ń gbáralé.');
INSERT INTO "aykyuni" VALUES(8,3,'8:3','(Àwọn ni) àwọn tó ń kírun, tí wọ́n sì ń ná nínú ohun tí A pèsè fún wọn.');
INSERT INTO "aykyuni" VALUES(8,4,'8:4','Ní òdodo, àwọn wọ̀nyẹn ni onígbàgbọ́ òdodo. Àwọn ipò gíga, àforíjìn àti arísìkí alápọ̀n-ọ́nlé ń bẹ fún wọn lọ́dọ̀ Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(8,5,'8:5','Gẹ́gẹ́ bí Olúwa rẹ ṣe mú ọ jáde (lọ sí ojú ogun) láti inú ilé rẹ pẹ̀lú òdodo, dájúdájú igun kan nínú àwọn onígbàgbọ́ ni ẹ̀mí wọn kórira rẹ̀.');
INSERT INTO "aykyuni" VALUES(8,6,'8:6','Wọ́n sì ń takò ọ́ níbi òdodo lẹ́yìn tí ó ti fojú hàn (sí wọn) àfi bí ẹni pé wọ́n ń wà wọ́n lọ sí ọ̀dọ̀ ikú, tí wọ́n sì ń wò ó (báyìí).');
INSERT INTO "aykyuni" VALUES(8,7,'8:7','(Rántí) nígbà tí Allāhu ṣàdéhùn ọ̀kan nínú àwọn ìjọ méjèèjì fún yín pé ó máa jẹ́ tiyín, ẹ̀yin sì ń fẹ́ kí ìjọ tí kò dira ogun jẹ́ tiyín. Allāhu sì fẹ́ mú òdodo ṣẹ pẹ̀lú àwọn ọ̀rọ̀ Rẹ̀, Ó sì (fẹ́) pa àwọn aláìgbàgbọ́ run pátápátá');
INSERT INTO "aykyuni" VALUES(8,8,'8:8','nítorí kí (Allāhu) lè fi òdodo (’Islām) rinlẹ̀ ní òdodo, kí Ó sì lè paná irọ́ (ìbọ̀rìṣà), àwọn ẹlẹ́ṣẹ̀ (ọ̀ṣẹbọ) ìbáà kórira rẹ̀.');
INSERT INTO "aykyuni" VALUES(8,9,'8:9','(Rántí) nígbà tí ẹ̀ ń wá ìrànlọ́wọ́ Olúwa yín, Ó sì da yín lóhùn pé dájúdájú Èmi yóò ràn yín lọ́wọ́ pẹ̀lú ẹgbẹ̀rún kan nínú àwọn mọlāika tó máa wá ní tẹ̀léǹtẹ̀lé.');
INSERT INTO "aykyuni" VALUES(8,10,'8:10','Allāhu kò sì ṣe (ìrànlọ́wọ́ àwọn mọlāika) lásán bí kò ṣe nítorí kí ó lè jẹ́ ìró ìdùnnú àti nítorí kí ọkàn yín lè balẹ̀ pẹ̀lú rẹ̀. Kò sí àrànṣe kan àfi láti ọ̀dọ̀ Allāhu. Dájúdájú Allāhu ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(8,11,'8:11','(Rántí) nígbà tí (Allāhu) ta yín ní òògbé lọ (kí ó lè jẹ́) ìfọ̀kànbalẹ̀ (fún yín) láti ọ̀dọ̀ Rẹ̀. Ó tún sọ omi kalẹ̀ fún yín láti sánmọ̀ nítorí kí Ó fi lè fọ̀ yín mọ́ (pẹ̀lú ìwẹ̀ ọ̀ran-anyàn), àti nítorí kí Ó fi lè mú èròkérò Èṣù (bí àlá ìbálòpò ojú oorun) kúrò fún yín àti nítorí kí Ó lè kì yín lọ́kàn àti nítorí kí Ó lè mú ẹsẹ̀ yín dúró ṣinṣin.');
INSERT INTO "aykyuni" VALUES(8,12,'8:12','(Rántí) nígbà tí Olúwa rẹ fi mọ àwọn mọlāika pé dájúdájú Èmi ń bẹ pẹ̀lú yín. Nítorí náà, ẹ fi ẹsẹ̀ àwọn tó gbàgbọ́ ní òdodo rinlẹ̀. Èmi yóò ju ẹ̀rù sínú ọkàn àwọn tó ṣàì gbàgbọ́. Nítorí náà, ẹ máa gé (wọn) lọ́rùn. Kí ẹ sì máa gé gbogbo ọmọ ìka wọn.');
INSERT INTO "aykyuni" VALUES(8,13,'8:13','Ìyẹn nítorí pé, wọ́n yapa (ọ̀rọ̀) Allāhu àti Òjíṣẹ́ Rẹ̀. Ẹnikẹ́ni tí ó bá sì yapa (ọ̀rọ̀) Allāhu àti Òjíṣẹ́ Rẹ̀, dájúdájú Allāhu le (níbi) ìyà.');
INSERT INTO "aykyuni" VALUES(8,14,'8:14','Ìyẹn (ni ìyà tayé). Nítorí náà, ẹ tọ́ ọ wò. Àti pé dájúdájú ìyà Iná ń bẹ fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(8,15,'8:15','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nígbà tí ẹ bá pàdé àwọn tó ṣàì gbàgbọ́, tí wọ́n ń wọ́ jáde níkọ̀ níkọ̀, ẹ̀yin kò gbọdọ̀ pẹ̀yìn dà sí wọn (láti ságun).');
INSERT INTO "aykyuni" VALUES(8,16,'8:16','Ẹnikẹ́ni tí ó bá sì pẹ̀yìn dà sí wọn ní ọjọ́ yẹn, yàtọ̀ sí ẹni tó yẹ̀ sí ẹ̀gbẹ́ kan (láti túnra mú) fún ogun tàbí ẹni tó fẹ́ darapọ̀ mọ́ ikọ̀ (mùsùlùmí láti fún wọn ní ìró ọ̀tẹlẹ̀-múyẹ́), dájúdájú (aságun) ti padà wálé pẹ̀lú ìbínú láti ọ̀dọ̀ Allāhu. Iná Jahanamọ sì ni ibùgbé rẹ̀. Ìkángun náà sì burú.');
INSERT INTO "aykyuni" VALUES(8,17,'8:17','Ẹ̀yin kọ́ lẹ pa wọ́n, ṣùgbọ́n Allāhu l’Ó pa wọ́n. Àti pé ìwọ kọ́ lo jùkò nígbà tí o jùkò, àmọ́ dájúdájú Allāhu l’Ó jùkònítorí kí (Allāhu) lè fi àmìwò dáadáa kan àwọn onígbàgbọ́ òdodo láti ọ̀dọ̀ Rẹ̀ ni. Dájúdájú Allāhu ni Olùgbọ́, Onímọ̀. ');
INSERT INTO "aykyuni" VALUES(8,18,'8:18','Ìyẹn (báyẹn). Dájúdájú Allāhu máa sọ ète àwọn aláìgbàgbọ́ di lílẹ.');
INSERT INTO "aykyuni" VALUES(8,19,'8:19','Tí ẹ̀yin (aláìgbàgbọ́) bá ń wá ìdájọ́, dájúdájú ìdájọ́ ti kò le yín lórí. Tí ẹ bá jáwọ́ (níbi ogun), ó sì dára jùlọ fún yín. Tí ẹ bá tún padà (síbi ogun), A máa padà (ran àwọn onígbàgbọ́ òdodo lọ́wọ́); àkójọ yín kò sì níí rọ̀ yín lọ́rọ̀ kan kan, ìbáà pọ̀. Dájúdájú Allāhu ń bẹ pẹ̀lú àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(8,20,'8:20','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ tẹ̀lé ti Allāhu àti Òjíṣẹ́ Rẹ̀. Ẹ má ṣe gbúnrí kúrò ní ọ̀dọ̀ rẹ̀ nígbà tí ẹ bá ń gbọ́ (āyah) lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(8,21,'8:21','Ẹ má se dà bí àwọn tí wọ́n wí pé: “A gbọ́.” Wọn kò sì gbọ́.');
INSERT INTO "aykyuni" VALUES(8,22,'8:22','Dájúdájú ẹ̀dá abẹ̀mí tó burú jùlọ ní ọ̀dọ̀ Allāhu ni àwọn adití, ayaya tí kò ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(8,23,'8:23','Tí ó bá jẹ́ pé Allāhu mọ oore kan lára wọn ni, ìbá jẹ́ kí wọ́n gbọ́. Tí (Allāhu) bá sì fún wọn gbọ́ ni, dájúdájú wọn máa pẹ̀yìn dà, wọn yó sì máa gbúnrí.');
INSERT INTO "aykyuni" VALUES(8,24,'8:24','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ jẹ́pè ti Allāhu àti ti Òjíṣẹ́ nígbà tí ó bá pè yín síbi n̄ǹkan tí ó máa mu yín ṣẹ̀mí. Kí ẹ sì mọ̀ pé dájúdájú Allāhu ń ṣèdíwọ́ láààrin ènìyàn àti ọkàn rẹ̀. Dájúdájú ọ̀dọ̀ Rẹ̀ sì ni wọ́n máa ko yín jọ sí.');
INSERT INTO "aykyuni" VALUES(8,25,'8:25','Ẹ ṣọrá fún àdánwò kan tí kò níí mọ lórí àwọn tó ṣàbòsí nínú yín nìkan. Kí ẹ sì mọ̀ pé dájúdájú Allāhu le níbi ìyà.');
INSERT INTO "aykyuni" VALUES(8,26,'8:26','Ẹ rántí pé nígbà tí ẹ̀yin kéré ní òǹkà, tí ẹ wà ní ipò ọ̀lẹ (lábẹ́ wọn) lórí ilẹ̀ (ìyẹn nínú ìlú Mọkkah), ẹ sì ń páyà pé àwọn ènìyàn máa ji yín gbé lọ, nígbà náà (Allāhu) ṣe ibùgbé fún yín (nínú ìlú Mọdīnah Onímọ̀ọ́lẹ̀), Ó sì fi àrànṣe Rẹ ṣe ìkúnlọ́wọ́ fún yín. Ó tún pèsè fún yín nínú àwọn n̄ǹkan dáadáa nítorí kí ẹ lè dúpẹ́.');
INSERT INTO "aykyuni" VALUES(8,27,'8:27','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe jàǹbá (ọ̀ran-anyàn) Allāhu àti (sunnah) Òjíṣẹ́; ẹ má ṣe jàǹbá àwọn àgbàfipamọ́ yín, ẹ sì mọ (ìjàǹbá).');
INSERT INTO "aykyuni" VALUES(8,28,'8:28','Kí ẹ sì mọ̀ pé dájúdájú ìfòòró ni àwọn dúkìá yín àti àwọn ọmọ yín, àti pé dájúdájú Allāhu ni ẹ̀san ńlá wà ní ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(8,29,'8:29','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, tí ẹ bá bẹ̀rù Allāhu, Ó máa fún yín ní ọ̀nà àbáyọ. Ó máa pa àwọn àṣìṣe yín rẹ́ fún yín. Ó sì máa foríjìn yín. Allāhu sì ni Ọlọ́lá tó tóbi.');
INSERT INTO "aykyuni" VALUES(8,30,'8:30','(Rántí) nígbà tí àwọn tó ṣàì gbàgbọ́ ń déte sí ọ láti dè ọ́ mọ́lẹ̀ tàbí láti pa ọ́ tàbí láti yọ ọ́ kúrò nínú ìlú; wọ́n ń déte, Allāhu sì ń déte. Allāhu sì lóore jùlọ nínú àwọn adéte.1 ');
INSERT INTO "aykyuni" VALUES(8,31,'8:31','Nígbà tí A bá ń ké àwọn āyah Wa fún wọn, wọ́n á wí pé: “A kúkú ti gbọ́. Tí ó bá jẹ́ pé a bá fẹ́ ni. àwa náà ìbá sọ irú èyí. Kí ni èyí bí kò ṣe àkọsílẹ̀ àlọ́ àwọn ẹni àkọ́kọ́.”');
INSERT INTO "aykyuni" VALUES(8,32,'8:32','(Rántí) nígbà tí wọ́n wí pé: “Allāhu, tí ó bá jẹ́ pé èyí ni òdodo láti ọ̀dọ̀ Rẹ, rọ òjò òkúta lé wa lórí láti sánmọ̀ tàbí kí O mú ìyà ẹlẹ́ta-eléro kan wá bá wa.”');
INSERT INTO "aykyuni" VALUES(8,33,'8:33','Allāhu kò níí jẹ wọ́n níyà nígbà tí ìwọ sì ń bẹ láààrin wọn. Allāhu kò sì níí jẹ wọ́n níyà nígbà tí wọ́n sì ń tọrọ àforíjìn.');
INSERT INTO "aykyuni" VALUES(8,34,'8:34','Kò sí ìdí tí Allāhu kò fi níí jẹ wọ́n níyà níwọ̀n ìgbà tí wọ́n bá ń ṣẹ́ àwọn ènìyàn lórí kúrò nínú Mọ́sálásí Haram. Àti pé àwọn ọ̀ṣẹbọ kọ́ ni alámòjúútó rẹ̀. Kò sí alámòjúútó kan fún un bí kò ṣe àwọn olùbẹ̀rù (Allāhu), ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò mọ̀.');
INSERT INTO "aykyuni" VALUES(8,35,'8:35','Ìjọ́sìn àwọn ọ̀ṣẹbọ ní Ilé (Kaabah) kò sì tayọ ṣíṣú ìfé àti pípa àtẹ́wọ́. Nítorí náà, ẹ tọ́ Ìyà wò nítorí pé ẹ jẹ́ aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(8,36,'8:36','Dájúdájú àwọn tó ṣàì gbàgbọ́, wọ́n ń ná dúkìá wọn nítorí kí wọ́n lè ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu. Wọ́n sì ń ná a lọ (bẹ́ẹ̀). Lẹ́yìn náà, ó máa di àbámọ̀ fún wọn. Lẹ́yìn náà, A sì máa borí wọn. Àti pé àwọn tó ṣàì gbàgbọ́, inú iná Jahanamọ ni A máa kó wọn jọ sí');
INSERT INTO "aykyuni" VALUES(8,37,'8:37','nítorí kí Allāhu lè ya àwọn ẹni ibi sọ́tọ̀ kúrò lára àwọn ẹni ire àti nítorí kí Ó lè kó àwọn ẹni ibi papọ̀; apá kan wọn mọ́ apá kan, kí Ó sì lè rọ́ gbogbo wọn papọ̀ pátápátá (sójú kan náà), Ó sì máa fi wọ́n sínú iná Jahanamọ. Àwọn wọ̀nyẹn gan-an ni ẹni òfò.');
INSERT INTO "aykyuni" VALUES(8,38,'8:38','Sọ fún àwọn tó ṣàì gbàgbọ́ pé tí wọ́n bá jáwọ́ (nínú àìgbàgbọ́), A máa ṣàforíjìn ohun tó ti ré kọjá fún wọn. Tí wọ́n bá tún padà (síbẹ̀, àpèjúwe) ìparun àwọn ẹni àkọ́kọ́ kúkú ti ré kọjá (ní ìkìlọ̀ fún wọn).');
INSERT INTO "aykyuni" VALUES(8,39,'8:39','Ẹ jà wọ́n lógun títí kò fi níí sí ìfòòró (ìbọ̀rìṣà) mọ́, gbogbo ẹ̀sìn yó sì lè jẹ́ ti Allāhu pátápátá. Nítorí náà, tí wọ́n bá jáwọ́ (nínú àìgbàgbọ́), dájúdájú Allāhu ni Olùríran nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(8,40,'8:40','Tí wọ́n bá sì gbúnrí (kúrò nínú ìgbàgbọ́), ẹ mọ̀ pé dájúdájú Allāhu ni Aláfẹ̀yìntì yín. Ó dára ní Aláfẹ̀yìntì. Ó sì dára ní Alárànṣe.');
INSERT INTO "aykyuni" VALUES(8,41,'8:41','Ẹ mọ̀ pé ohunkóhun tí ẹ bá kó ní ọrọ̀ ogun, dájúdájú ti Allāhu ni ìdá márùn-ún rẹ̀. Ó sì wà fún Òjíṣẹ́ àti ẹbí (rẹ̀) àti àwọn ọmọ òrukàn, àwọn mẹ̀kúnnù àti onírìn-àjò (tí agara dá), tí ẹ bá jẹ́ ẹni tó gbàgbọ́ nínú Allāhu àti n̄ǹkan tí A sọ̀kalẹ̀ fún ẹrúsìn Wa ní Ọjọ́ ìpínyà, ọjọ́ tí ìjọ méjì pàdé (lójú ogun Badr). Allāhu sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(8,42,'8:42','(Ẹ rántí) nígbà tí ẹ̀yin wà ní etí kòtò tó súnmọ́ (ìlú Mọdīnah), àwọn (kèfèrí) sì wà ní etí kòtò tó jìnnà sí (ìlú Mọdīnah), àwọn kèfèrí kan tó ń gun n̄ǹkan ìgùn (bọ̀ láti ìrìn-àjò òkòwò), wọ́n sì wà ní ìsàlẹ̀ ọ̀dọ̀ tiyín. Tí ó bá jẹ́ pé ẹ̀yin àti àwọn kèfèrí jọ mú ọjọ́ fún ogun náà ni, ẹ̀yin ìbá yapa àdéhùn náà, ṣùgbọ́n (ogun náà ṣẹlẹ̀ bẹ́ẹ̀) nítorí kí Allāhu lè mú ọ̀rọ̀ kan tí ó gbọ́dọ̀ ṣẹ wá sí ìmúṣẹ; nítorí kí ẹni tí ó máa parun (sínú àìgbàgbọ́) lè parun nípasẹ̀ ẹ̀rí tó yanjú, kí ẹni tó sì máa yè (nínú ìgbàgbọ́ òdodo) lè yè nípasẹ̀ ẹ̀rí tó yanjú. Àti pé dájúdájú Allāhu kúkú ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(8,43,'8:43','(Rántí) nígbà tí Allāhu fi wọ́n hàn ọ́ nínú àlá rẹ (pé wọ́n) kéré. Tí ó bá jẹ́ pé Ó fi wọ́n hàn ọ́ (pé wọ́n) pọ̀ ni, ẹ̀yin ìbá ṣojo, ẹ̀yin ìbá sì ṣàríyànjiyàn nípa ọ̀rọ̀ náà. Ṣùgbọ́n Allāhu ko (yín) yọ. Dájúdájú Òun ni Onímọ̀ nípa n̄ǹkan tí ń bẹ nínú igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(8,44,'8:44','(Rántí) nígbà tí Ó ń fi wọ́n hàn yín pé wọ́n kéré lójú yín - nígbà tí ẹ pàdé (lójú ogun) - Ó sì mú ẹ̀yin náà kéré lójú tiwọn náà, nítorí kí Allāhu lè mú ọ̀rọ̀ kan tí ó gbọ́dọ̀ ṣẹ wá sí ìmúṣẹ. Ọ̀dọ̀ Allāhu sì ni wọ́n máa ṣẹ́rí àwọn ọ̀rọ̀ ẹ̀dá padà sí.');
INSERT INTO "aykyuni" VALUES(8,45,'8:45','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nígbà tí ẹ bá pàdé ìjọ (ogun) kan, ẹ dúró ṣinṣin, kí ẹ sì ṣe (gbólóhùn) ìrántí Allāhu ní (òǹkà) púpọ̀ nítorí kí ẹ lè jèrè.');
INSERT INTO "aykyuni" VALUES(8,46,'8:46','Ẹ tẹ̀lé ti Allāhu àti Òjíṣẹ́ Rẹ̀. Ẹ má ṣe jara yín níyàn (nípa ogun ẹ̀sìn) nítorí kí ẹ má baà ṣojo àti nítorí kí agbára yín má baà kúrò lọ́wọ́ yín. Ẹ ṣe sùúrù, dájúdájú Allāhu wà pẹ̀lú àwọn onísùúrù.');
INSERT INTO "aykyuni" VALUES(8,47,'8:47','Ẹ má ṣe dà bí àwọn tó jáde kúrò nínú ilé wọn pẹ̀lú fáààrí àti ṣekárími. Wọ́n sì ń ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu. Allāhu sì ni Alámọ̀tán nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(8,48,'8:48','(Rántí) nígbà tí Èṣù ṣe iṣẹ́ wọn ní ọ̀ṣọ́ fún wọn, ó sì wí pé: “Kò sí ẹni tí ó máa borí yín lónìí nínú àwọn ènìyàn. Dájúdájú èmi ni alárànṣe yín.” Ṣùgbọ́n nígbà tí àwọn ìjọ ogun méjèèjì fojú kanra wọn, ó padà sẹ́yìn, ó fẹsẹ̀ fẹ́ẹ, ó wí pé: “Dájúdájú èmi ti yọwọ́-yọsẹ̀ nínú ọ̀rọ̀ yín. Dájúdájú èmi ń rí ohun tí ẹ̀yin kò rí. Èmi ń pàyà Allāhu. Allāhu sì le níbi ìyà.”');
INSERT INTO "aykyuni" VALUES(8,49,'8:49','(Rántí) nígbà tí àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí àti àwọn tí àrùn ń bẹ nínú ọkàn wọn wí pé: “Ẹ̀sìn àwọn wọ̀nyí tàn wọ́n jẹ.” Ẹnikẹ́ni tó bá gbáralé Allāhu, dájúdájú Allāhu ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(8,50,'8:50','Tí ó bá jẹ́ pé ìwọ rí i nígbà tí àwọn mọlāika bá ń gba ẹ̀mí àwọn tó ṣàì gbàgbọ́ ni, tí wọ́n ń lu ojú wọn àti ẹ̀yìn wọn, (wọ́n sì máa sọ fún wọn pé), “Ẹ tọ́ ìyà Iná jónijóni wò.”');
INSERT INTO "aykyuni" VALUES(8,51,'8:51','Ìyẹn nítorí ohun tí ọwọ́ yín tì ṣíwájú. Àti pé dájúdájú Allāhu kò níí ṣe àbòsí fún àwọn ẹrú (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(8,52,'8:52','(Ìṣesí àwọn aláìgbàgbọ́) dà bí ìṣesí àwọn ènìyàn Fir‘aon àti àwọn tó ṣíwájú wọn - wọ́n ṣàì gbàgbọ́ nínú àwọn āyah Allāhu. - Nítorí náà, Allāhu mú wọn nítorí ẹ̀ṣẹ̀ wọn. Dájúdájú Allāhu ni Alágbára, Ó le níbi ìyà.');
INSERT INTO "aykyuni" VALUES(8,53,'8:53','Ìyẹn nítorí pé dájúdájú Allāhu kò níí ṣe ìyípadà ìdẹ̀ra tí Ó ṣe fún ìjọ kan títí wọn yóò fi ṣe ìyípadà n̄ǹkan tí ń bẹ lọ́dọ̀ ara wọn. Dájúdájú Allāhu sì ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(8,54,'8:54','(Ìṣesí àwọn aláìmoore) dà bí ìṣesí àwọn ènìyàn Fir‘aon àti àwọn tó ṣíwájú wọn, tí wọ́n pe àwọn āyah Olúwa wọn nírọ́. Nítorí náà, A pa wọ́n run nítorí ẹ̀ṣẹ̀ wọn; A sì tẹ àwọn ènìyàn Fir‘aon rì sínú agbami. Gbogbo wọn sì jẹ́ alábòsí.');
INSERT INTO "aykyuni" VALUES(8,55,'8:55','Dájúdájú ẹ̀dá abẹ̀mí tó burú jùlọ lọ́dọ̀ Allāhu ni àwọn tó ṣàì gbàgbọ́. Nítorí náà, wọn kò níí gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(8,56,'8:56','Àwọn tí o bá ṣe àdéhùn nínú wọn, lẹ́yìn náà tí wọ́n ń tú àdéhùn wọn ní gbogbo ìgbà, tí wọn kò sì bẹ̀rù (Allāhu),');
INSERT INTO "aykyuni" VALUES(8,57,'8:57','yálà tí ọwọ́ yín bá bà wọ́n lójú ogun, (ẹ pa wọ́n) kí ẹ sì (fi ogun) tú àwọn ọmọlẹ́yìn wọn ká nítorí kí wọ́n lè lo ìrántí;');
INSERT INTO "aykyuni" VALUES(8,58,'8:58','tàbí tí o bá sì ń páyà ìwà ìjàǹbá láti ọ̀dọ̀ ìjọ kan, ju àdéhùn wọn sílẹ̀ fún wọn kí ẹ dì jọ mọ̀ pé kò sí àdéhùn kan kan mọ́ láààrin ẹ̀yin méjèèjì. Dájúdájú Allāhu kò nífẹ̀ẹ́ àwọn oníjàǹbá.');
INSERT INTO "aykyuni" VALUES(8,59,'8:59','Kí àwọn tó ṣàì gbàgbọ́ má ṣe lérò pé àwọn ti gbawájú. Dájúdájú wọn kò lè mórí bọ́ (nínú ìyà).');
INSERT INTO "aykyuni" VALUES(8,60,'8:60','Ẹ pèsè sílẹ̀ dè wọ́n n̄ǹkan ìjà tí agbára yín ká nínú n̄ǹkan àmúṣagbára àti àwọn ẹṣin orí ìso, tí ẹ̀yin yóò fi máa dẹ́rù ba ọ̀tá Allāhu àti ọ̀tá yín pẹ̀lú àwọn mìíràn yàtọ̀ sí wọn, tí ẹ̀yin kò mọ̀ wọ́n – Allāhu sì mọ̀ wọ́n. – Ohunkóhun tí ẹ bá ná lójú ọ̀nà (ẹ̀sìn) Allāhu, A óò san án padà fún yín ní kíkún; A ò sì níí ṣe àbòsí fún yín.');
INSERT INTO "aykyuni" VALUES(8,61,'8:61','Tí wọ́n bá tẹ̀ síbi (ètò) àlàáfíà (fún dídá ogun dúró), ìwọ náà tẹ̀ síbẹ̀, kí o sì gbáralé Allāhu. Dájúdájú Òun mà ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(8,62,'8:62','Tí wọ́n bá tún fẹ́ tàn ọ́ jẹ, dájúdájú Allāhu á tó ọ. Òun ni Ẹni tí Ó fi àrànṣe Rẹ̀ àti àwọn onígbàgbọ́ òdodo ràn ọ́ lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(8,63,'8:63','Allāhu pa ọkàn wọn pọ̀ mọ́ra wọn. Tí ó bá jẹ́ pé o ná gbogbo n̄ǹkan tí ń bẹ lórí ilẹ̀ pátápátá, ìwọ kò lè pa ọkàn wọn pọ̀ mọ́ra wọn. Ṣùgbọ́n Allāhu pa ọkàn wọn pọ̀ mọ́ra wọn, dájúdájú Òun ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(8,64,'8:64','Ìwọ Ànábì, Allāhu ti tó ìwọ àti àwọn tó tẹ̀lé ọ nínú àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(8,65,'8:65','Ìwọ Ànábì, gba àwọn onígbàgbọ́ òdodo ní ìyànjú lórí ogun ẹ̀sìn. Tí ogún onísùúrù bá wà nínú yín, wọn yóò borí igba. Tí ọgọ́rùn-ún bá sì wà nínú yín, wọn yóò borí ẹgbẹ̀rún nínú àwọn tó ṣàì gbàgbọ́ nítorí pé dájúdájú wọ́n jẹ́ ìjọ tí kò gbọ́ àgbọ́yé.');
INSERT INTO "aykyuni" VALUES(8,66,'8:66','Nísisìn yìí, Allāhu ti ṣe é ní fífúyẹ́ fún yín. Ó sì mọ̀ pé dájúdájú àìlágbára ń bẹ fún yín. Nítorí náà, tí ọgọ́rùn-ún onísùúrù bá wà nínú yín, wọn yóò borí igba. Tí ẹgbẹ̀rún kan bá sì wà nínú yín, wọn yóò borí ẹgbẹ̀rún méjì pẹ̀lú àṣẹ Allāhu. Allāhu sì wà pẹ̀lú àwọn onísùúrù.');
INSERT INTO "aykyuni" VALUES(8,67,'8:67','Kò yẹ fún Ànábì kan láti ní ẹrú ogun títí ọwọ́ rẹ̀ máa fi ba àwọn ọ̀tá rẹ̀ lórí ilẹ̀ dáadáa ní ti pípa.Ẹ̀yin ń fẹ́ ìgbádùn ayé, Allāhu sì ń fẹ́ ọ̀run. Allāhu sì ni Alágbára, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(8,68,'8:68','Tí kì í bá ṣe ti àkọsílẹ̀ kan tó ti gbawájú láti ọ̀dọ̀ Allāhu ni, ọwọ́ ìyà ńlá kò bá tẹ̀ yín nípa n̄ǹkan tí ẹ gbà (ní owó ìtúsílẹ̀).');
INSERT INTO "aykyuni" VALUES(8,69,'8:69','Nítorí náà, ẹ jẹ nínú ohun tí ẹ kó ní ọrọ̀ ogun (tí ó jẹ́) ẹ̀tọ́ (àti) n̄ǹkan dáadáa. Kí ẹ sì bẹ̀rù Allāhu. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(8,70,'8:70','Ìwọ Ànábì, sọ fún àwọn tó ń bẹ lọ́wọ́ yín nínú àwọn ẹrú ogun pé, “Tí Allāhu bá mọ dáadáa kan nínú ọkàn yín, Ó máa fún yín ní ohun tó dára ju ohun tí wọ́n gbà lọ́wọ́ yín. Ó sì máa foríjìn yín. Allāhu sì ni Aláforíjìn, Oníkẹ̀ẹ́.”');
INSERT INTO "aykyuni" VALUES(8,71,'8:71','Tí wọ́n bá sì ń gbèrò ìjàǹbá sí ọ, wọ́n kúkú ti jàǹbá Allāhu ṣíwájú. Ó sì fún ọ lágbára lórí wọn. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(8,72,'8:72','Dájúdájú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n gbé ìlú wọn jù sílẹ̀, tí wọ́n sì fi dúkìá wọn àti ẹ̀mí wọn jagun lójú ọ̀nà (ẹ̀sìn) Allāhu, àti àwọn tó gbà wọ́n sọ́dọ̀ (nínú ìlú), tí wọ́n sì ràn wọ́n lọ́wọ́; àwọn wọ̀nyẹn, apá kan wọn l’ọ̀rẹ́ apá kan (tó lè jogún ara wọn). Àwọn tó gbàgbọ́, tí wọn kò sì gbé ìlú wọn jù sílẹ̀, kò lẹ́tọ̀ọ́ fún yín láti mú wọn lọ́rẹ̀ẹ́ níbì kan kan (tó lè mu yín jogún ara yín), títí wọn yóò fi gbé ìlú wọn jù sílẹ̀.Tí wọ́n bá sì wá ìrànlọ́wọ́ yín (lórí ọ̀tá) nípa ẹ̀sìn, ìrànlọ́wọ́ náà di dandan fún yín àyàfi lórí ìjọ kan tí àdéhùn ń bẹ láààrin ẹ̀yin àti àwọn. Allāhu sì ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(8,73,'8:73','Àwọn aláìgbàgbọ́; apá kan wọn ni alátìlẹ́yìn apá kan. Àfi kí ẹ̀yin náà ṣe bẹ́ẹ̀ ni ìfòòró àti ìbàjẹ́ tó tóbi kò fi níí wà lórí ilẹ̀.');
INSERT INTO "aykyuni" VALUES(8,74,'8:74','Àwọn tó gbàgbọ́ ní òdodo, tí wọ́n gbé ìlú wọn jù sílẹ̀, tí wọ́n sì jagun l’ójú ọ̀nà (ẹ̀sìn) Allāhu àti àwọn tó gbà wọ́n sọ́dọ̀ (nínú ìlú), tí wọ́n sì ràn wọ́n lọ́wọ́; ní ti òdodo àwọn wọ̀nyẹn, àwọn ni onígbàgbọ́ òdodo. Àforíjìn àti arísìkí alápọ̀n-ọ́nlé sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(8,75,'8:75','Àwọn tó gbàgbọ́ ní òdodo lẹ́yìn (wọn), tí wọ́n gbé ìlú wọn jù sílẹ̀, tí wọ́n sì jagun ẹ̀sìn pẹ̀lú yín, àwọn wọ̀nyẹn náà wà lára yín. Àti pé àwọn ìbátan, apá kan wọn ní ẹ̀tọ́ jùlọ sí apá kan (nípa ogún jíjẹ) nínú Tírà Allāhu. Dájúdájú Allāhu ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(9,1,'9:1','(Èyí ni) ìyọwọ́-yọsẹ̀ láti ọ̀dọ̀ Allāhu àti Òjíṣẹ́ Rẹ̀ sí àwọn tí ẹ ṣe àdéhùn fún nínú àwọn ọ̀ṣẹbọ. ');
INSERT INTO "aykyuni" VALUES(9,2,'9:2','Nítorí náà, (ẹ̀yin ọ̀ṣẹbọ) ẹ rìn (kiri) lórí ilẹ̀ fún oṣù mẹ́rin. Kí ẹ sì mọ̀ pé dájúdájú ẹ̀yin kò lè mórí bọ́ (nínú ìyà) Allāhu. Àti pé dájúdájú Allāhu yóò dójú ti àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(9,3,'9:3','Ìkéde kan láti ọ̀dọ̀ Allāhu àti Òjíṣẹ́ Rẹ̀ sí àwọn ènìyàn ní ọjọ́ Hajj Ńláni pé, “Dájúdájú Allāhu yọwọ́yọsẹ̀ (nínú ọ̀rọ̀) àwọn ọ̀ṣẹbọ. Òjíṣẹ́ Rẹ̀ náà (yọwọ́yọsẹ̀). Tí ẹ bá ronú pìwàdà, ó sì lóore jùlọ fún yín. Tí ẹ bá gbúnrí, ẹ mọ̀ pé dájúdájú ẹ kò lè móríbọ́ nínú (ìyà) Allāhu.” Kí o sì fún àwọn tó ṣàì gbàgbọ́ ní ìró ìyà ẹlẹ́ta-eléro. ');
INSERT INTO "aykyuni" VALUES(9,4,'9:4','Àyàfi àwọn tí ẹ bá ṣe àdéhùn nínú àwọn ọ̀ṣẹbọ, lẹ́yìn náà, tí wọn kò sì fi ọ̀nà kan kan yẹ àdéhùn yín, tí wọn kò sì ṣàtìlẹ́yìn fún ẹnì kan kan le yín lórí. Nítorí náà, ẹ pé àdéhùn wọn fún wọn títí di àsìkò wọn. Dájúdájú Allāhu nífẹ̀ẹ́ àwọn olùbẹ̀rù (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(9,5,'9:5','Nítorí náà, nígbà tí àwọn oṣù ọ̀wọ̀ bá lọ tán ẹ pa àwọn ọ̀ṣẹbọ níbikíbi tí ẹ bá ti bá wọn. Ẹ mú wọn, ẹ ṣéde mọ́ wọn, kí ẹ sì ba dè wọ́n ní gbogbo ibùba. Tí wọ́n bá sì ronú pìwàdà, tí wọ́n ń kírun, tí wọ́n sì ń yọ Zakāh, ẹ yàgò fún wọn lójú ọ̀nà. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(9,6,'9:6','Tí ẹnì kan nínú àwọn ọ̀sẹbọ bá wá ètò ààbò wá sọ́dọ̀ rẹ, ṣ’ètò ààbò fún un títí ó fi máa gbọ́ ọ̀rọ̀ Allāhu. Lẹ́yìn náà, mú un dé àyè ìfọ̀kànbalẹ̀ rẹ̀. Ìyẹn nítorí pé dájúdájú àwọn ni ìjọ tí kò nímọ̀.');
INSERT INTO "aykyuni" VALUES(9,7,'9:7','Báwo ni àdéhùn kan yóò ṣe wà fún àwọn ọ̀ṣẹbọ lọ́dọ̀ Allāhu àti lọ́dọ̀ Òjísẹ́ Rẹ̀ yàtọ̀ sí àwọn tí ẹ bá ṣe àdéhùn nítòsí Mọ́sálásí Haram? Nítorí náà, tí wọ́n bá dúró déédé pẹ̀lú yín, ẹ dúró déédé pẹ̀lú wọn. Dájúdájú Allāhu nífẹ̀ẹ́ àwọn olùbẹ̀rù (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(9,8,'9:8','Báwo (ni àdéhùn kan yóò ṣe wà fún wọn) nígbà tí ó jẹ́ pé tí wọ́n bá borí yín, wọn kò níí ṣọ́ okùn ìbí àti àdéhùn. Wọ́n ń fi ẹnu wọn wí pé àwọn yọ́nú si yín, ọkàn wọn sì kọ̀ ọ́. Ọ̀pọ̀lọ́pọ̀ wọn sì ni òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(9,9,'9:9','Wọ́n ta àwọn āyah Allāhu ní owó pọ́ọ́kú, wọ́n sì ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Rẹ̀. Dájúdájú àwọn (wọ̀nyí), ohun tí wọ́n ń ṣe níṣẹ́ burú.');
INSERT INTO "aykyuni" VALUES(9,10,'9:10','Àwọn ọ̀ṣẹbọ kò níí ṣọ́ okùn ìbí àti àdéhùn kan fún onígbàgbọ́ òdodo kan. Àwọn wọ̀nyẹn, àwọn sì ni olùtayọ ẹnu-ààlà.');
INSERT INTO "aykyuni" VALUES(9,11,'9:11','Nítorí náà, tí wọ́n bá ronú pìwàdà, tí wọ́n ń kírun, tí wọ́n sì ń yọ Zakāh, nígbà náà ọmọ-ìyá yín nínú ẹ̀sìn ni wọ́n. À ń ṣàlàyé àwọn āyah náà fún ìjọ tó nímọ̀.');
INSERT INTO "aykyuni" VALUES(9,12,'9:12','Tí wọ́n bá rú ìbúra wọn lẹ́yìn àdéhùn wọn, tí wọ́n sì ń sọ̀rọ̀ àìdara sí ẹ̀sìn yín, nígbà náà ẹ ja àwọn olórí aláìgbàgbọ́ lógun - dájúdájú ìbúra wọn kò ní ìtúmọ̀ kan sí wọn - kí wọ́n lè jáwọ́ (níbi aburú).');
INSERT INTO "aykyuni" VALUES(9,13,'9:13','Ṣé ẹ ò níí gbógun ti ìjọ kan tó ba ìbúra rẹ̀ jẹ́ (ó sì jù ú nù), tí wọ́n sì gbèrò láti lé Òjíṣẹ́ kúrò (nínú ìlú); àwọn sì ni wọ́n kọ́ bẹ̀rẹ̀ sí gbógun tì yín ní ìgbà àkọ́kọ́? Ṣé ẹ̀ ń páyà wọn ni? Allāhu l’Ó ní ẹ̀tọ́ jùlọ sí pé kí ẹ páyà Rẹ̀ tí ẹ bá jẹ́ onígbàgbọ́ òdodo.1 ');
INSERT INTO "aykyuni" VALUES(9,14,'9:14','Ẹ jà wọ́n lógun. Allāhu yóò jẹ wọ́n níyà láti ọwọ́ yín. Ó máa yẹpẹrẹ wọn. Ó máa ràn yín lọ́wọ́ lórí wọn. Ó sì máa wo ọkàn ìjọ onígbàgbọ́ òdodo sàn.');
INSERT INTO "aykyuni" VALUES(9,15,'9:15','Ó tún máa kó ìbínú ọkàn wọn lọ. Ó sì máa gba ìronúpìwàdà lọ́wọ́ ẹni t’Ó bá fẹ́. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(9,16,'9:16','Tàbí ẹ lérò pé A óò fi yín sílẹ̀ láì jẹ́ pé Allāhu ti ṣàfi hàn àwọn tó máa jagun ẹ̀sìn nínú yín, tí wọn kò sì ní ọ̀rẹ́ àyò kan lẹ́yìn Allāhu àti Òjíṣẹ́ Rẹ̀ àti àwọn onígbàgbọ́ òdodo? Allāhu ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(9,17,'9:17','Kò lẹ́tọ̀ọ́ fún àwọn ọ̀ṣẹbọ láti máa ṣàmójútó àwọn mọ́sálásí Allāhu, nígbà tí wọ́n jẹ́ ẹlẹ́rìí sí àìgbàgbọ́ lórí ara wọn. Àwọn wọ̀nyẹn ni iṣẹ́ wọn ti bàjẹ́. Olùṣegbére sì ni wọ́n nínú Iná.');
INSERT INTO "aykyuni" VALUES(9,18,'9:18','Ẹni tí yóò máa ṣàmójútó àwọn mọ́sálásí Allāhu ni ẹni tó gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn, tó sì ń kírun, tó ń yọ Zakāh, kò sì páyà (òrìṣà kan) lẹ́yìn Allāhu. Àwọn wọ̀nyẹn ni wọ́n kúkú wà nínú àwọn olùmọ̀nà.');
INSERT INTO "aykyuni" VALUES(9,19,'9:19','Ṣé ẹ máa ṣe fífún alálàájì ní omi mu àti ṣíṣe àmójútó Mọ́sálásí Haram ní ohun tó dọ́gba sí ẹni tó gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn, tó sì jagun sójú ọ̀nà (ẹ̀sìn) Allāhu? Wọn kò dọ́gba lọ́dọ̀ Allāhu. Allāhu kò níí fi ọ̀nà mọ ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(9,20,'9:20','Àwọn tó gbàgbọ́ ní òdodo, tí wọ́n gbé ìlú wọn jù sílẹ̀,tí wọ́n sì fi dúkìá wọn àti ẹ̀mí wọn jagun fún ẹ̀sìn Allāhu, wọ́n tóbi jùlọ ní ipò lọ́dọ̀ Allāhu. Àwọn wọ̀nyẹn, àwọn sì ni olùjèrè. ');
INSERT INTO "aykyuni" VALUES(9,21,'9:21','Olúwa wọn ń fún wọn ní ìró ìdùnnú nípa ìkẹ́ láti ọ̀dọ̀ Rẹ̀ pẹ̀lú ìyọ́nú àti àwọn Ọgbà Ìdẹ̀ra kan tí ìdẹ̀ra gbére wà fún wọn nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(9,22,'9:22','Olùṣegbére ni wọ́n nínú rẹ̀ títí láéláé. Dájúdájú Allāhu, ọ̀dọ̀ Rẹ̀ ni ẹ̀san ńlá wà.');
INSERT INTO "aykyuni" VALUES(9,23,'9:23','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe mú àwọn bàbá yín àti àwọn arakùnrin yín ní ọ̀rẹ́ àyò bí wọ́n bá gbọ́lá fún àìgbàgbọ́ lórí ìgbàgbọ́ òdodo. Ẹnikẹ́ni tó bá mú wọn ní ọ̀rẹ́ àyò nínú yín, àwọn wọ̀nyẹn gan-an ni alábòsí.1 ');
INSERT INTO "aykyuni" VALUES(9,24,'9:24','Sọ pé: “Tí ó bá jẹ́ pé àwọn bàbá yín, àwọn ọmọkùnrin yín, àwọn arákùnrin yín, àwọn ìyàwó yín àti àwọn ìbátan yín pẹ̀lú àwọn dúkìá kan tí ẹ ti kó jọ àti òkòwò kan tí ẹ̀ ń bẹ̀rù pé kí ó má kùtà àti àwọn ibùgbé tí ẹ yọ́nú sí, (tí ìwọ̀nyí) bá wù yín ju Allāhu àti Òjíṣẹ́ Rẹ̀ pẹ̀lú jíja ogun sójú ọ̀nà (ẹ̀sìn) Rẹ̀, ẹ máa retí (ìkángun) nígbà náà títí Allāhu yóò fi mú àṣẹ Rẹ̀ wá. Allāhu kò níí fi ọ̀nà mọ ìjọ òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(9,25,'9:25','Dájúdájú Allāhu ti ṣe àrànṣe fún yín ní ọ̀pọ̀lọ́pọ̀ ojú ogun àti ní ọjọ́ (ogun) Hunaen, nígbà tí pípọ̀ yín jọ yín lójú, (àmọ́) kò rọ̀ yín lọ́rọ̀ kan kan; ilẹ̀ sì fún mọ yín tòhun ti bí ó ṣe fẹ̀ tó. Lẹ́yìn náà, ẹ pẹ̀yìn dà, ẹ sì ń sá lọ.');
INSERT INTO "aykyuni" VALUES(9,26,'9:26','Lẹ́yìn náà, Allāhu sọ ìfàyàbalẹ̀ Rẹ̀ kalẹ̀ fún Òjíṣẹ́ Rẹ̀ àti fún àwọn onígbàgbọ́ òdodo. Ó tún sọ àwọn ọmọ ogun kan kalẹ̀, tí ẹ ò fojú rí wọn. Ó sì jẹ àwọn tó ṣàì gbàgbọ́ níyà. Ìyẹn sì ni ẹ̀san àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(9,27,'9:27','Lẹ́yìn náà, Allāhu yóò gba ìronúpìwàdà lọ́wọ́ ẹni t’Ó bá fẹ́ lẹ́yìn ìyẹn. Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(9,28,'9:28','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, dájúdájú ẹ̀gbin ni àwọn ọ̀sẹbọ. Nítorí náà, wọn kò gbọdọ̀ súnmọ́ Mọ́sálásí Haram lẹ́yìn ọdún wọn yìí. Tí ẹ bá ń bẹ̀rù òṣì, láìpẹ́ Allāhu máa rọ̀ yín lọ́rọ̀ nínú ọlá Rẹ̀, tí Ó bá fẹ́. Dájúdájú Allāhu ni Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(9,29,'9:29','Ẹ gbógun ti àwọn tí kò gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn àti àwọn tí kò ṣe ní èèwọ̀ ohun tí Allāhu àti Òjíṣẹ́ Rẹ̀ ṣe ní èèwọ̀àti àwọn tí kò ṣe ẹ̀sìn òdodo nínú àwọn tí A fún ní tírà. (Ẹ gbógun tì wọ́n) títí wọ́n yóò fi máa fi ọwọ́ ará wọn san owó-orí ní ẹni yẹpẹrẹ. ');
INSERT INTO "aykyuni" VALUES(9,30,'9:30','Àwọn yẹhudi wí pé: “‘Uzaer ni ọmọ Allāhu.” Àwọn nasọ̄rọ̄ sì wí pé: “Mọsīh ni ọmọ Allāhu.” Ìyẹn ni ọ̀rọ̀ wọn ní ẹnu wọn. Wọ́n ń fi jọ ọ̀rọ̀ àwọn tó ṣàì gbàgbọ́ ṣíwájú (wọn). Allāhu fi wọ́n gégùn-ún. Báwo ni wọ́n ṣe ń ṣẹ́rí wọn kúrò níbi òdodo!');
INSERT INTO "aykyuni" VALUES(9,31,'9:31','Wọ́n mú àwọn àlùfáà wọn (nínú yẹhudi) àti àwọn àlùfáà wọn (nínú nasọ̄rọ̄) ní olúwa lẹ́yìn Allāhu. (Wọ́n tún mú) Mọsīh ọmọ Mọryam (ní olúwa lẹ́yìn Allāhu). Bẹ́ẹ̀ sì ni A ò pa wọ́n láṣẹ kan tayọ jíjọ́sìn fún Ọlọ́hun, Ọ̀kan ṣoṣo. Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Ó mọ́ tayọ n̄ǹkan tí wọ́n ń fi ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(9,32,'9:32','Wọ́n fẹ́ fi ẹnu wọn pa ìmọ́lẹ̀ (ẹ̀sìn) Allāhu, Allāhu yó sì kọ̀ (fún wọn) títí Ó fi máa pé ìmọ́lẹ̀ (ẹ̀sìn) Rẹ̀, àwọn aláìgbàgbọ́ ìbáà kórira rẹ̀.');
INSERT INTO "aykyuni" VALUES(9,33,'9:33','(Allāhu) Òun ni Ẹni tí Ó fi ìmọ̀nà àti ẹ̀sìn òdodo (ẹ̀sìn ’Islām) rán Òjíṣẹ́ Rẹ̀ nítorí kí Ó lè fi borí gbogbo ẹ̀sìn (mìíràn) pátápátá, àwọn ọ̀ṣẹbọ ìbáà kórira rẹ̀.');
INSERT INTO "aykyuni" VALUES(9,34,'9:34','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ọ̀pọ̀lọ́pọ̀ nínú àwọn àlùfáà (nínú yẹhudi) àti àwọn àlùfáà (nínú nasọ̄rọ̄), wọ́n kúkú ń fi ọ̀nà èrú jẹ dúkìá àwọn ènìyàn ni, wọ́n sì ń ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu. Àwọn tó sì ń kó wúrà àti fàdákà jọ, wọn kò sì ná an fún ẹ̀sìn Allāhu, fún wọn ní ìró ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(9,35,'9:35','Ní ọjọ́ tí A óò máa yọ́ (wúrà àti fàdákà náà) nínú iná Jahanamọ, A ó sì máa fi jó iwájú wọn, ẹ̀gbẹ́ wọn àti ẹ̀yìn wọn. (A sì máa sọ pé): “Èyí ni ohun tí ẹ kó jọ fún ẹ̀mí ara yín. Nítorí náà, ẹ tọ́ ohun tí ẹ kó jọ wò.”');
INSERT INTO "aykyuni" VALUES(9,36,'9:36','Dájúdájú òǹkà àwọn oṣù lọ́dọ̀ Allāhu ń jẹ́ oṣù méjìlá nínú àkọsílẹ̀ ti Allāhu ní ọjọ́ tí Ó ti dá àwọn sánmọ̀ àti ilẹ̀. Mẹ́rin ni oṣù ọ̀wọ̀ nínú rẹ̀.Ìyẹn ni ẹ̀sìn tó fẹsẹ̀ rinlẹ̀.2 Nítorí náà, ẹ má ṣàbòsí sí ara yín nínú àwọn oṣù ọ̀wọ̀. Kí gbogbo yín sì gbógun ti àwọn ọ̀ṣẹbọ gẹ́gẹ́ bí gbogbo wọn ṣe ń gbógun tì yín. Kí ẹ sì mọ̀ pé dájúdájú Allāhu wà pẹ̀lú àwọn olùbẹ̀rù (Rẹ̀). ');
INSERT INTO "aykyuni" VALUES(9,37,'9:37','Àlékún nínú àìgbàgbọ́ ni dídájọ́ sí àwọn oṣù ọ̀wọ̀ (láti ọwọ́ àwọn ọ̀ṣẹbọ). Wọ́n ń fi kó ìṣìnà bá àwọn tó ṣàì gbàgbọ́ (nípa pé) wọ́n ń ṣe oṣù ọ̀wọ̀ kan ní oṣù ẹ̀tọ́ (fún ogun jíjà) nínú ọdún kan, wọ́n sì ń bu ọ̀wọ̀ fún oṣù (tí kì í ṣe oṣù ọ̀wọ̀) nínú ọdún (mìíràn, wọn kò níí jagun nínú rẹ̀, wọ́n sì máa yọ òǹkà oṣù náà síra wọn) nítorí kí òǹkà tí Allāhu ṣe ní èèwọ̀ lè papọ̀ mọ́ra wọn (sínú àwọn oṣù tí kì í ṣe oṣù ọ̀wọ̀). Wọ́n sì tipasẹ̀ bẹ́ẹ̀ ṣe ní ẹ̀tọ́ ohun tí Allāhu ṣe ní èèwọ̀. Wọ́n ṣe iṣẹ́ aburú wọn ní ọ̀ṣọ́ fún wọn. Allāhu kò níí fi ọ̀nà mọ ìjọ aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(9,38,'9:38','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, kí ni ó mu yín tí ó fi jẹ́ pé nígbà tí wọ́n bá sọ fún yín pé kí ẹ tú jáde (láti jagun) lójú ọ̀nà (ẹ̀sìn) Allāhu, (ìgbà náà ni) ẹ máa kàndí mọ́lẹ̀! Ṣé ẹ yọ́nú sí ìṣẹ̀mí ayé (yìí) ju ti ọ̀run ni? Ìgbádùn ìṣẹ̀mí ayé (yìí) kò sì jẹ́ kiní kan nínú ti ọ̀run àfi díẹ̀.');
INSERT INTO "aykyuni" VALUES(9,39,'9:39','Àfi kí ẹ tú jáde (sógun ẹ̀sìn) ni Allāhu kò fi níí jẹ yín níyà ẹlẹ́ta-eléro àti pé ni kò fi níí fi ìjọ tó yàtọ̀ si yín pààrọ̀ yín. Ẹ kò sì lè fi kiní kan kó ìnira bá (Allāhu). Allāhu sì ni Alagbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(9,40,'9:40','Àfi kí ẹ ràn án lọ́wọ́, Allāhu kúkú ti ràn án lọ́wọ́ nígbà tí àwọn tó ṣàì gbàgbọ́ lé e jáde (kúrò nínú ìlú). Ó sì jẹ́ ọ̀kan nínú àwọn méjì.Nígbà tí àwọn méjèèjì wà nínú ọ̀gbun, tí (Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -) sì ń sọ fún olùbárìn rẹ̀ pé: “Má ṣe banújẹ́, dájúdájú Allāhu wà pẹ̀lú wa.”2 Nígbà náà, Allāhu sọ ìfàyàbalẹ̀ Rẹ̀ kalẹ̀ fún un. Ó fi àwọn ọmọ ogun kan tí ẹ kò fojú rí ràn án lọ́wọ́. Ó sì mú ọ̀rọ̀ àwọn tó ṣàì gbàgbọ́ wálẹ̀. Ọ̀rọ̀ Allāhu, òhun ló sì lékè. Allāhu sì ni Alágbára, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(9,41,'9:41','Ẹ tú jáde (fún ogun ẹ̀sìn) pẹ̀lú okun àti ìrọ́jú. Kí ẹ sì fi àwọn dúkìá yín àti ẹ̀mí yin jagun fún ẹ̀sìn Allāhu. Ìyẹn lóore jùlọ fún yín tí ẹ bá mọ̀.');
INSERT INTO "aykyuni" VALUES(9,42,'9:42','Tí ó bá jẹ́ pé n̄ǹkan ìgbádùn (ọrọ̀ ogun) àrọ́wótó àti ìrìn-àjò tí kò jìnnà (lo pè wọ́n sí ni), wọn ìbá tẹ̀lé ọ. Ṣùgbọ́n ìrìn-àjò tó lágbára (ti t''ogun Tabūk) jìnnà lójú wọn. Wọ́n yó sì máa fi Allāhu búra pé: “Tí ó bá jẹ́ pé a lágbára ni, àwa ìbá jáde (fún ogun ẹ̀sìn) pẹ̀lú yín.” - Wọ́n sì ń kó ìparun bá ẹ̀mí ara wọn (nípa ṣíṣe ìṣọ̀bẹ-ṣèlu.) - Allāhu sì mọ̀ pé dájúdájú òpùrọ́ ni wọ́n.');
INSERT INTO "aykyuni" VALUES(9,43,'9:43','Allāhu ti ṣàmójúkúrò fún ọ. Kí ló mú ọ yọ̀ǹda fún wọn (pé kí wọ́n dúró sílé? Ìwọ ìbá má ṣe bẹ́ẹ̀) títí ọ̀rọ̀ àwọn tó sòdodo yóò fi hàn sí ọ kedere. Ìwọ yó sì mọ àwọn òpùrọ́.');
INSERT INTO "aykyuni" VALUES(9,44,'9:44','Àwọn tó gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn kò níí tọrọ ìyọ̀ǹda lọ́dọ̀ rẹ láti má fi dúkìá wọn àti ẹ̀mí wọn jagun fún ẹ̀sìn Allāhu. Allāhu sì ni Onímọ̀ nípa àwọn olùbẹ̀rù (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(9,45,'9:45','Àwọn tó ń tọrọ ìyọ̀ǹda lọ́dọ̀ rẹ (láti jókòó sílé, dípò lílọ sí ojú-ogun) ni àwọn tí kò gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn; ọkan wọn sì ń ṣeyèméjì. Nítorí náà, wọ́n ń dààmú kiri níbi ìṣeyè-méjì wọn.');
INSERT INTO "aykyuni" VALUES(9,46,'9:46','Tí ó bá jẹ́ pé wọn gbèrò ìjáde fún ogun ẹ̀sìn ni, wọn ìbá ṣe ìpalẹ̀mọ́ fún un. Ṣùgbọ́n Allāhu kórira ìdìde wọn fún ogun ẹ̀sìn, Ó sì kó ìfàsẹ́yìn bá wọn. Wọ́n sì sọ fún wọn pé: “Ẹ jókòó pẹ̀lú àwọn olùjókòó sílé.”');
INSERT INTO "aykyuni" VALUES(9,47,'9:47','Tí ó bá jẹ́ pé wọ́n jáde pẹ̀lú yín, wọn kò níí kun yín àfi pẹ̀lú ìbàjẹ́. Wọn yó sì sáré máa dá rúkèrúdò sílẹ̀ láààrin yín, tí wọn yóò máa ko yín sínú ìyọnu. Àti pé wọ́n ní olùgbọ́rọ̀ fún wọn láààrin yín. Allāhu sì ni Onímọ̀ nípa àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(9,48,'9:48','Wọ́n kúkú ti wá ìyọnu tẹ́lẹ̀tẹ́lẹ̀, wọ́n sì fimú fínlẹ̀ sì ọ (tí wọ́n dete sì ọ) títí òdodo fi dé, tí àṣẹ Allāhu sì borí; ẹ̀mí wọn sì kórira rẹ̀.');
INSERT INTO "aykyuni" VALUES(9,49,'9:49','Ó wà nínú wọn, ẹni tí ń wí pé: “Yọ̀ǹda fún mi (kí n̄g jókòó sílé); má ṣe kó mi sínú àdánwò.” Inú àdánwò (sísá fógun ẹ̀sìn) má ni wọ́n ti ṣubú sí yìí. Dájúdájú iná Jahanamọ yó sì kúkú yí àwọn aláìgbàgbọ́ po.');
INSERT INTO "aykyuni" VALUES(9,50,'9:50','Tí dáadáa kan bá ṣẹlẹ̀ sí ọ, ó máa bà wọ́n nínú jẹ́. Tí aburú kan bá sì ṣẹlẹ̀ sí ọ, wọ́n á wí pé: “A kúkú ti gba àṣẹ tiwa ṣíwájú (láti jókòó sílé.)” Wọ́n á pẹ̀yìn dà; wọn yó sì máa dunnú.');
INSERT INTO "aykyuni" VALUES(9,51,'9:51','Sọ pé: “Kò sí ohun kan tó máa ṣẹlẹ̀ sí wa àyàfi ohun tí Allāhu kọ mọ́ wa. Òun ni Aláàbò wa.” Allāhu sì ni kí àwọn onígbàgbọ́ òdodo gbáralé.');
INSERT INTO "aykyuni" VALUES(9,52,'9:52','Sọ pé: “Ṣé ẹ̀ ń retí kiní kan pẹ̀lú wa ni bí kò ṣe ọ̀kan nínú dáadáa méjì (ikú ogun tàbí ìṣẹ́gun)? Àwa náà ń retí pẹ̀lú yín pé kí Allāhu mú ìyà kan wá ba yín láti ọ̀dọ̀ Rẹ̀ tàbí láti ọwọ́ wa. Nítorí náà, ẹ máa retí, dájúdájú àwa náà wà pẹ̀lú yín tí à ń retí.”');
INSERT INTO "aykyuni" VALUES(9,53,'9:53','Sọ pé: “Ẹ fínnú-fíndọ̀ náwó ni tàbí pẹ̀lú tipátipá, A ò níí gbà á lọ́wọ́ yín (nítorí pé) dájúdájú ẹ̀yin jẹ́ ìjọ òbìlẹ̀jẹ́.”');
INSERT INTO "aykyuni" VALUES(9,54,'9:54','Kò sì sí ohun kan tí kò jẹ́ kí Á gba ìnáwó wọn lọ́wọ́ wọn bí kò ṣe pé dájúdájú wọ́n ṣàì gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀. Wọn kò níí wá kírun àfi kí wọ́n jẹ́ òròjú aláìníkan-ánṣe. Wọn kò sì níí náwó fẹ́sìn àfi kí ẹ̀mí wọn kórira rẹ̀.');
INSERT INTO "aykyuni" VALUES(9,55,'9:55','Nítorí náà, má ṣe jẹ́ kí àwọn dúkìá wọn àti àwọn ọmọ wọn jọ ọ́ lójú; Allāhu kàn fẹ́ fi jẹ wọ́n níyà nínú ìṣẹ̀mí ayé (yìí) ni. Ẹ̀mí yó sì bọ́ lára wọn, tí wọ́n máa wà nípò aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(9,56,'9:56','Wọ́n ń fi Allāhu búra pé dájúdájú àwọn kúkú wà lára yín. Wọn kò sì sí lára yín, ṣùgbọ́n dájúdájú wọ́n jẹ́ ìjọ kan tó ń bẹ̀rù.1 ');
INSERT INTO "aykyuni" VALUES(9,57,'9:57','Tí ó bá jẹ́ pé wọ́n rí ibùsásí kan, tàbí àwọn ihò àpáta kan, tàbí ibùsáwọ̀ kan, wọn ìbá ṣẹ́rí síbẹ̀ ní wéréwéré.');
INSERT INTO "aykyuni" VALUES(9,58,'9:58','Ó tún wà nínú wọn, ẹni tí ń bú ọ níbi (pípín) àwọn ọrẹ. Tí A bá fún wọn nínú rẹ̀, wọ́n á yọ́nú (sí i). Tí A ò bá sì fún wọn nínú rẹ̀, nígbà náà ni wọn yóò máa bínú.');
INSERT INTO "aykyuni" VALUES(9,59,'9:59','Àti pé (ìbá lóore fún wọn) tí ó bá jẹ́ pé dájúdájú wọ́n yọ́nú sí ohun tí Allāhu àti Òjíṣẹ́ Rẹ̀ fún wọn, kí wọ́n sì sọ pé: “Allāhu tó wa. Allāhu yó sì fún wa nínú oore àjùlọ Rẹ̀ àti pé Òjíṣẹ́ Rẹ̀ (sì máa pín ọrẹ) dájúdájú ọ̀dọ̀ Allāhu sì ni àwa ń wá oore sí.” ');
INSERT INTO "aykyuni" VALUES(9,60,'9:60','Àwọn tí ọrẹ (Zakāh) wà fún ni àwọn aláìní, àwọn mẹ̀kúnnù,àwọn òṣìṣẹ́ Zakāh, àwọn tí ọkàn wọn fẹ́ gba ’Islām (ìyẹn àwọn tí wọ́n fẹ́ fi fa ọkàn wọn mọ́ra sínú ẹ̀sìn), àwọn ẹrú (fún gbígba òmìnira), àwọn onígbèsè, àwọn tó wà lójú ogun (ẹ̀sìn) Allāhu àti onírìn-àjò (tí agara dá). Ọ̀ran-anyàn ni láti ọ̀dọ̀ Allāhu. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(9,61,'9:61','Àwọn tó ń kó ìnira bá Ànábì wà nínú wọn, tí wọ́n sì ń wí pé: “Elétí-ọfẹ ni.” Sọ pé: “Elétí-ọfẹ rere ni fún yín; ó gbàgbọ́ nínú Allāhu. Ó sì gba àwọn onígbàgbọ́ òdodo gbọ́. Ìkẹ́ ni fún àwọn onígbàgbọ́ òdodo nínú yín. Àti pé àwọn tó ń kó ìnira bá Òjíṣẹ́ Allāhu, ìyà ẹlẹ́ta-eléro ń bẹ fún wọn.”');
INSERT INTO "aykyuni" VALUES(9,62,'9:62','Wọ́n ń fi Allāhu búra fún yín láti wá ìyọ́nú yín. Allāhu àti Òjíṣẹ́ Rẹ̀ ló sì lẹ́tọ̀ọ́ jùlọ pe kí wọ́n wá ìyọ́nú Rẹ̀ tí wọ́n bá jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(9,63,'9:63','Ṣé wọn kò mọ̀ pé dájúdájú ẹnikẹ́ni tó bá ń tako Allāhu àti Òjíṣẹ́ Rẹ̀, dájúdájú iná Jahanamọ ti wà fún un ni? Olùṣegbére sì ni nínú rẹ̀. Ìyẹn sì ni àbùkù ńlá.');
INSERT INTO "aykyuni" VALUES(9,64,'9:64','Àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí ń bẹ̀rù pé kí Á má ṣe sọ sūrah kan kalẹ̀ nípa wọn, tí ó máa fún wọn ní ìró ohun tí ń bẹ nínú ọkàn wọn. Sọ pé: “Ẹ máa ṣe yẹ̀yẹ́ lọ. Dájúdájú Allāhu yóò ṣàfi hàn ohun tí ẹ̀ ń bẹ̀rù.”');
INSERT INTO "aykyuni" VALUES(9,65,'9:65','Tí o bá kúkú bi wọ́n léèrè, dájúdájú wọ́n á wí pé: “Àwa kàn ń rojọ́ lásán ni, a sì ń ṣàwàdà ni.” Sọ pé: “Ṣé Allāhu, àwọn āyah Rẹ̀ àti Òjíṣẹ́ Rẹ̀ ni ẹ̀ ń fi ṣe yẹ̀yẹ́?”');
INSERT INTO "aykyuni" VALUES(9,66,'9:66','Ẹ má ṣe mú àwáwí wá. Dájúdájú ẹ ti ṣàì gbàgbọ́ lẹ́yìn ìgbàgbọ́ yín. Tí A bá ṣe àmójúkúrò fún apá kan nínú yín, A óò fìyà jẹ apá kan nítorí pé wọ́n jẹ́ ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(9,67,'9:67','Àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí lọ́kùnrin àti àwọn ṣọ̀bẹ-ṣèlu mùsùlùmọ́ lóbìnrin, irú kan-ùn ni wọ́n; wọ́n ń pàṣẹ ohun burúkú, wọ́n ń kọ ohun rere, wọ́n sì ń káwọ́ gbera (láti náwó fẹ́sìn). Wọ́n gbàgbé Allāhu. Nítorí náà, Allāhu gbàgbé wọn.Dájúdájú àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí, àwọn ni òbìlẹ̀jẹ́. ');
INSERT INTO "aykyuni" VALUES(9,68,'9:68','Allāhu sì ti ṣe àdéhùn iná Jahanamọ fún àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí lọ́kùnrin àti àwọn ṣọ̀bẹ-ṣèlu mùsùlùmọ́ lóbìnrin àti àwọn aláìgbàgbọ́. Olùṣegbére ni wọn nínú rẹ̀. Iná máa tó wọn. Allāhu sì ti ṣẹ́bi lé wọn. Ìyà gbére sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(9,69,'9:69','(Àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí dà) gẹ́gẹ́ bí àwọn tó ṣíwájú yín; wọ́n le jù yín lọ ní agbára, wọ́n sì pọ̀ (jù yín lọ) ní àwọn dúkìá àti àwọn ọmọ. Nígbà náà, wọ́n jẹ ìgbádùn ìpín tiwọn (nínú oore ayé). Ẹ̀yin (ṣọ̀bẹ-ṣèlu wọ̀nyìí náà yóò) jẹ ìgbádùn ìpín tiyín gẹ́gẹ́ bí àwọn tó ṣíwájú yín ṣe jẹ ìgbádùn ìpín tiwọn. Ẹ̀yin náà sì sọ̀sọkúsọ bí èyí tí àwọn náà sọ ní ìsọkúsọ. Àwọn wọ̀nyẹn, àwọn iṣẹ́ wọn ti bàjẹ́ ní ayé àti ní ọ̀run. Àwọn wọ̀nyẹn, àwọn sì ni ẹni òfò.');
INSERT INTO "aykyuni" VALUES(9,70,'9:70','Ṣé ìròyìn àwọn tó ṣíwájú wọn kò tí ì dé bá wọn ni? (Ìròyìn) ìjọ (Ànábì) Nūh, ìran ‘Ād, ìran Thamūd, ìjọ (Ànábì) ’Ibrọ̄hīm, àwọn ará Mọdyan àti àwọn ìlú tí A dojú rẹ̀ bolẹ̀ (ìjọ Ànábì Lūt); àwọn Òjíṣẹ́ wọn wá bá wọn pẹ̀lú àwọn ẹ̀rí tó yanjú. Nítorí náà, Allāhu kò ṣe àbòsí sí wọn, ṣùgbọ́n ẹ̀mí ara wọn ni wọ́n ṣàbòsí sí.');
INSERT INTO "aykyuni" VALUES(9,71,'9:71','Àwọn onígbàgbọ́ òdodo lọ́kùnrin àti àwọn onígbàgbọ́ òdodo lóbìnrin, apá kan wọn lọ̀rẹ́ alátìlẹ́yìn fún apá kan; wọ́n ń pàṣẹ ohun rere, wọ́n ń kọ ohun burúkú, wọ́n ń kírun, wọ́n ń yọ Zakāh, wọ́n sì ń tẹ̀lé ti Allāhu àti Òjíṣẹ́ Rẹ̀. Àwọn wọ̀nyẹn ni Allāhu yóò ṣàkẹ́. Dájúdájú Allāhu ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(9,72,'9:72','Allāhu ṣe àdéhùn àwọn Ọgbà Ìdẹ̀ra fún àwọn onígbàgbọ́ òdodo lọ́kùnrin àti àwọn onígbàgbọ́ òdodo lóbìnrin, èyí tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀. (Ó tún ṣe àdéhùn) àwọn ibùgbé tó dára nínú àwọn ọgbà ìdẹ̀ra gbére (fún wọn). Ìyọ́nú láti ọ̀dọ̀ Allāhu ló sì tóbi jùlọ (fún wọn). Ìyẹn, òhun ni èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(9,73,'9:73','Ìwọ Ànábì, gbógun ti àwọn aláìgbàgbọ́ àti àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí. Kí o sì le mọ́ wọn. Iná Jahanamọ ni ibùgbé wọn. Ìkángun náà sì burú.');
INSERT INTO "aykyuni" VALUES(9,74,'9:74','Wọ́n ń fi Allāhu búra pé àwọn kò sọ̀rọ̀ (burúkú). Wọ́n sì kúkú ti sọ ọ̀rọ̀ àìgbàgbọ́, wọ́n sì ṣàì gbàgbọ́ lẹ́yìn tí wọ́n ti gba ’Islām. Wọ́n tún gbèròkérò sí n̄ǹkan tí ọwọ́ wọn kò níí bà. Wọn kò rí àlèébù kan (wọn kò sì kórira kiní kan) bí kò ṣe nítorí pé, Allāhu àti Òjíṣẹ́ Rẹ̀ rọ àwọn (Sọhābah) lọ́rọ̀ nínú ọlá Rẹ̀. Tí wọ́n bá ronú pìwàdà, ó máa dára fún wọn. Tí wọ́n bá sì gbúnrí (tí wọ́n kọ̀yìn si yín), Allāhu yóò jẹ wọ́n níyà ẹlẹ́ta-eléro ní ayé àti ní ọ̀run. Kò sì níí sí aláàbò àti alárànṣe kan fún wọn lórí ilẹ̀ ayé.');
INSERT INTO "aykyuni" VALUES(9,75,'9:75','Ó wà nínú wọn, ẹni tó bá Allāhu ṣe àdéhùn pé: “Tí Ó bá fún wa nínú oore àjùlọ Rẹ̀, dájúdájú a óò máa tọrẹ, dájúdájú a ó sì wà nínú àwọn ẹni ire.”');
INSERT INTO "aykyuni" VALUES(9,76,'9:76','Àmọ́ nígbà tí Ó fún wọn nínú oore àjùlọ Rẹ̀, wọ́n ṣahun sí I. Wọ́n pẹ̀yìn dà, wọ́n sì ń gbúnrí (láti náwó fẹ́sìn).');
INSERT INTO "aykyuni" VALUES(9,77,'9:77','Nítorí náà, ahun wọn mú ọ̀rọ̀ wọn kángun sí ìṣọ̀bẹ-ṣèlu nínú ọkàn wọn títí di ọjọ́ tí wọn yóò pàdé Allāhu nítorí pé wọ́n yẹ àdéhùn tí wọ́n bá Allāhu ṣe àti nítorí pé wọ́n ń parọ́.');
INSERT INTO "aykyuni" VALUES(9,78,'9:78','Ṣé wọn kò mọ̀ pé Allāhu mọ àṣírí wọn àti ọ̀rọ̀ àtẹ̀sọ wọn, àti pé dájúdájú Allāhu ni Onímọ̀ nípa àwọn ìkọ̀kọ̀?');
INSERT INTO "aykyuni" VALUES(9,79,'9:79','Àwọn tó ń bú àwọn olùtọrẹ-àánú nínú àwọn onígbàgbọ́ òdodo lórí ọrẹ títa àti àwọn tí kò rí n̄ǹkan kan tayọ ìwọ̀n agbára wọn, wọ́n sì ń fi wọn ṣe yẹ̀yẹ́, Allāhu fi àwọn náà ṣe yẹ̀yẹ́. Ìyà ẹlẹ́ta-eléro sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(9,80,'9:80','Yálà o tọrọ àforíjìn fún wọ́n tàbí o ò tọrọ àforíjìn fún wọn - kódà kí o tọrọ àforíjìn fún wọn nígbà ààdọ́rin - Allāhu kò níí foríjìn wọ́n. Ìyẹn nítorí pé, dájúdájú wọ́n ṣàì gbàgbọ́ nínú Allāhu àti Òjísẹ́ Rẹ̀. Allāhu kò níí fi ọ̀nà mọ ìjọ òbìlẹ̀jẹ́.1 ');
INSERT INTO "aykyuni" VALUES(9,81,'9:81','Àwọn olùsásẹ́yìn fún ogun ẹ̀sìn dunnú sí jíjókòó sínú ilé wọn lẹ́yìn Òjíṣẹ́ Allāhu. Wọ́n sì kórira láti fi dúkìá wọn àti ẹ̀mí wọn jagun lójú ọ̀nà (ẹ̀sìn) Allāhu. Wọ́n tún wí pé: “Ẹ má lọ jagun nínú ooru gbígbóná.” Sọ pé: “Iná Jahanamọ le jùlọ ní gbígbóná, tí ó bá jẹ́ pé wọ́n gbọ́ àgbọ́yé ọ̀rọ̀.”');
INSERT INTO "aykyuni" VALUES(9,82,'9:82','Nítorí náà, kí wọ́n rẹ́rìn-ín díẹ̀, kí wọ́n sì sunkún púpọ̀; (ó jẹ́) ẹ̀san (fún) ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(9,83,'9:83','Tí Allāhu bá mú ọ délé bá igun kan nínú wọn, tí wọ́n bá wá ń gbàṣẹ lọ́dọ̀ rẹ fún jíjáde fún ogun ẹ̀sìn, sọ nígbà náà pé: “Ẹ̀yin kò lè jáde fún ogun ẹ̀sìn mọ́ pẹ̀lú mi. Ẹ̀yin kò sì lè ja ọ̀tá kan lógun mọ́ pẹ̀lú mi, nítorí pé ẹ ti yọ́nú sí ìjókòó sílé ní ìgbà àkọ́kọ́. Nítorí náà, ẹ jókòó sílé ti àwọn olùsásẹ́yìn fún ogun ẹ̀sìn.”');
INSERT INTO "aykyuni" VALUES(9,84,'9:84','Láéláé, o ò gbọdọ̀ kírun sí ẹnì kan kan lára nínú wọn, tí ó bá kú, o ò sì gbọdọ̀ dúró níbi sàréè rẹ̀, nítorí pé wọ́n ṣàì gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀. Wọ́n sì kú nígbà tí wọ́n wà nípò òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(9,85,'9:85','Má ṣe jẹ́ kí àwọn dúkìá wọn àti àwọn ọmọ wọn jọ ọ́ lójú; Allāhu kàn fẹ́ fi jẹ wọ́n níyà nínú ìṣẹ̀mí ayé (yìí) ni. (Ó sì fẹ́ kí) ẹ̀mí bọ́ lára wọn, nígbà tí wọ́n bá wà nípò aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(9,86,'9:86','Nígbà tí A bá sọ sūrah kan kalẹ̀ pé kí wọ́n gbàgbọ́ nínú Allāhu, kí wọ́n sí jagun pẹ̀lú Òjíṣẹ́ Rẹ̀, (nígbà náà ni) àwọn ọlọ́rọ̀ nínú wọn yóò máa tọrọ ìyọ̀ǹda lọ́dọ̀ rẹ, wọ́n á sì wí pé: “Fi wá sílẹ̀ kí á wà pẹ̀lú àwọn olùjókòó sílé.”');
INSERT INTO "aykyuni" VALUES(9,87,'9:87','Wọ́n yọ́nú sí kí wọ́n máa wà pẹ̀lú àwọn olùsásẹ́yìn fún ogun ẹ̀sìn. Wọ́n ti fi èdídí dí ọkàn wọn pa; nítorí náà, wọn kò níí gbọ́ àgbọ́yé.');
INSERT INTO "aykyuni" VALUES(9,88,'9:88','Ṣùgbọ́n Òjíṣẹ́ àti àwọn tó gbàgbọ́ ní òdodo pẹ̀lú rẹ̀, wọ́n fi dúkìá wọn àti ẹ̀mí wọn jagun ẹ̀sìn. Àwọn wọ̀nyẹn, àwọn oore ń bẹ fún wọn. Àwọn wọ̀nyẹn, àwọn sì ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(9,89,'9:89','Allāhu ti pèsè sílẹ̀ dè wọ́n àwọn Ọgbà Ìdẹ̀ra kan tí odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀. Ìyẹn ni èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(9,90,'9:90','Àwọn aláwàáwí nínú àwọn Lárúbáwá oko wá (bá ọ) nítorí kí wọ́n lè yọ̀ǹda (ìjókòó sílé) fún wọn. Àwọn tó sì pe ọ̀rọ̀ Allāhu àti ọ̀rọ̀ Òjíṣẹ́ nírọ́ náà jókòó sílé. Ọwọ́ ìyà ẹlẹ́ta-eléro máa ba àwọn aláìgbàgbọ́ nínú wọn.');
INSERT INTO "aykyuni" VALUES(9,91,'9:91','Kò sí ẹ̀ṣẹ̀ fún àwọn aláìlágbára, àwọn aláìsàn àti àwọn tí kò rí ohun tí wọn máa ná ní owó (láti fi jagun ẹ̀sìn) nígbà tí wọ́n bá ti ní òtítọ́-inú sí Allāhu àti Òjíṣẹ́ Rẹ̀. Kò sí ọ̀nà kan (tó máa já sí ìyà) fún àwọn olùṣe-rere sẹ́. Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(9,92,'9:92','Kò tún sí ẹ̀ṣẹ̀ fún àwọn tí (ó jẹ́ pé) nígbà tí wọ́n bá wá bá ọ pé kí o fún àwọn ní n̄ǹkan tí àwọn yóò gùn (lọ sójú ogun), tí o sì sọ pé, “Èmi kò rí n̄ǹkan tí mo lè fún yín gùn (lọ sójú ogun), wọ́n máa padà pẹ̀lú ojú wọn tí yóò máa damije ní ti ìbànújẹ́ pé wọn kò rí n̄ǹkan tí wọ́n máa ná (lọ sójú ogun ẹ̀sìn).');
INSERT INTO "aykyuni" VALUES(9,93,'9:93','Àwọn tí ọ̀nà kan (tó máa já sí ìyà) wà fún ni àwọn tó ń tọrọ ìyọ̀ǹda lọ́dọ̀ rẹ (láti jókòó sílé, tí) wọ́n sì jẹ́ ọlọ́rọ̀, tí wọ́n yọ́nú sí kí wọ́n wà pẹ̀lú àwọn olùsásẹ́yìn fún ogun ẹ̀sìn. Allāhu ti fi èdídí dí ọkàn wọn pa; nítorí náà, wọn kò sì mọ̀.');
INSERT INTO "aykyuni" VALUES(9,94,'9:94','Wọ́n yóò mú àwáwí wá fún yín nígbà tí ẹ bá dé bá wọn nílé. Sọ pé: "Ẹ má ṣe mú àwáwí wá; a ò níí gbà yín gbọ́. Allāhu kúkú ti sọ àwọn ọ̀rọ̀ yín fún wa. Allāhu àti Òjíṣẹ́ Rẹ̀ yó sì rí iṣẹ́ (ọwọ́) yín.Lẹ́yìn náà, A óò da yín padà sí ọ̀dọ̀ Onímọ̀-ìkọ̀kọ̀ àti gban̄gba. Nígbà náà, Ó máa fún yín ní ìró ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(9,95,'9:95','Wọ́n yóò máa fi Allāhu búra fún yín nígbà tí ẹ bá dé bá wọn, nítorí kí ẹ lè pa wọ́n tì. Nítorí náà, ẹ pa wọ́n tì; dájúdájú ẹ̀gbin ni wọ́n. Iná Jahanamọ sì ni ibùgbé wọn. (Ó jẹ́) ẹ̀san nítorí ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(9,96,'9:96','Wọ́n yóò máa búra fún yín nítorí kí ẹ lè yọ́nú sí wọn. Tí ẹ bá yọ́nú sí wọn, dájúdájú Allāhu kò níí yọ́nú sí ìjọ òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(9,97,'9:97','Àwọn Lárúbáwá oko le nínú àìgbàgbọ́ àti ìṣọ̀bẹ-ṣèlu. Ó sì súnmọ́ jùlọ pé wọ́n kò mọ àwọn ẹnu-ààlà ohun tí Allāhu sọ̀kalẹ̀ fún Òjíṣẹ́ Rẹ̀. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(9,98,'9:98','Àti pé ó wà nínú àwọn Lárúbáwá oko ẹni tí ó ka ìnáwó tó ń ná (fún ẹ̀sìn) sí owó ọ̀ràn. Ó sì ń retí àpadàsí aburú fún yín. Àwọn sì ni àpadàsí aburú yóò dé bá. Allāhu sì ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(9,99,'9:99','Ó tún wà nínú àwọn Lárúbáwá oko, ẹni tí ó gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn, tí ó sì sọ ìnáwó tó ń ná (fún ẹ̀sìn) di ohun tí ó máa mú wọn súnmọ́ Allāhu àti ohun tí ó máa mú wọn gba àdùá lọ́dọ̀ Òjíṣẹ́. Kíyè sí i, dájúdájú ó máa mú wọn súnmọ́ Allāhu. Allāhu yó sì fi wọ́n sínú ìkẹ́ Rẹ̀. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(9,100,'9:100','Àwọn aṣíwájú, àwọn ẹni àkọ́kọ́ nínú àwọn Muhājirūn àti àwọn ’Ansọ̄r àti àwọn tó fi dáadáa tẹ̀lé wọn, Allāhu yọ́nú sí wọn. Wọ́n sì yọ́nú sí (ohun tí Allāhu fún wọn). Ó tún pa lésè sílẹ̀ dè wọ́n àwọn Ọgbà Ìdẹ̀ra tí odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ títí láéláé. Ìyẹn ni èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(9,101,'9:101','Àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí kan ń bẹ nínú àwọn tí ó wà ní àyíká yín nínú àwọn Lárúbáwá oko àti nínú àwọn ará ìlú Mọdīnah, tí wọ́n won̄koko mọ́ ìṣọ̀bẹ-ṣèlú. Ìwọ kò mọ̀ wọ́n, Àwa l’A mọ̀ wọ́n. A óò jẹ wọ́n níyà ní ẹ̀ẹ̀ mejì.Lẹ́yìn náà, A óò dá wọn padà sínú ìyà ńlá. ');
INSERT INTO "aykyuni" VALUES(9,102,'9:102','Àwọn mìíràn jẹ́wọ́ ẹ̀ṣẹ̀ wọn. Wọ́n da iṣẹ́ rere pọ̀ mọ́ iṣẹ́ mìíràn tó burú. Bóyá Allāhu yóò gba ìronúpìwàdà wọn. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(9,103,'9:103','Gba ọrẹ (Zakāh) nínú dúkìá wọn, kí o fi sọ wọ́n di ẹni mímọ́, kí o sì fi ṣe àfọ̀mọ́ fún wọn. Ṣe àdúà fún wọn. Dájúdájú àdúà rẹ ni ìfàyàbalẹ̀ fún wọn. Allāhu sì ni Olùgbọ́, Onímọ̀.1 ');
INSERT INTO "aykyuni" VALUES(9,104,'9:104','Ṣé wọn kò mọ̀ pé dájúdájú Allāhu Òun l’Ó ń gba ìronúpìwàdà lọ́wọ́ àwọn ẹrúsìn Rẹ̀, Ó sì ń gba àwọn ọrẹ, àti pé dájúdájú Allāhu, Òun ni Olùgba-ìronúpìwàdà, Àṣàkẹ́-ọ̀run?');
INSERT INTO "aykyuni" VALUES(9,105,'9:105','Sọ pé: “Ẹ ṣiṣẹ́. Allāhu á rí iṣẹ́ yín. Òjíṣẹ́ Rẹ̀ àti àwọn onígbàgbọ́ òdodo náà (máa rí i).Wọ́n sì máa da yín padà sọ́dọ̀ Onímọ̀-ìkọ̀kọ̀-àti-gban̄gba. Ó sì máa fún yín ní ìró ohun tí ẹ̀ ń ṣe níṣẹ́.” ');
INSERT INTO "aykyuni" VALUES(9,106,'9:106','Wọ́n so ọ̀rọ̀ àwọn yòókù rọ̀ ná títí àṣẹ Allāhu máa fi dé; yálà kí Ó jẹ wọ́n níyà tàbí kí Ó gba ìronúpìwàdà wọn.Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n. 2 ');
INSERT INTO "aykyuni" VALUES(9,107,'9:107','Àwọn tó kọ́ mọ́sálásí láti fi dá ìnira àti àìgbàgbọ́ sílẹ̀ àti láti fi ṣe òpínyà láààrin àwọn onígbàgbọ́ òdodo àti láti fi ṣe ibùlúgọ fún àwọn tó gbógun ti Allāhu àti Òjíṣẹ́ Rẹ̀ ní ìṣáájú - dájúdájú wọ́n ń búra pé “A ò gbèrò kiní kan bí kò ṣe ohun rere.” - Allāhu sì ń jẹ́rìí pé dájúdájú òpùrọ́ ni wọ́n.');
INSERT INTO "aykyuni" VALUES(9,108,'9:108','Má ṣe dúró (kírun) nínú rẹ̀ láéláé.Dájúdájú mọ́sálásí tí wọ́n bá fi ìpìlẹ̀ rẹ̀ lélẹ̀ lórí ìbẹ̀rù Allāhu láti ọjọ́ àkọ́kọ́ lo lẹ́tọ̀ọ́ jùlọ pé kí o dúró (kírun) nínú rẹ̀. Àwọn ènìyàn tó nífẹ̀ẹ́ láti máa ṣe ìmọ́ra wà nínú rẹ̀. Allāhu sì fẹ́ràn àwọn olùṣèmọ́ra. ');
INSERT INTO "aykyuni" VALUES(9,109,'9:109','Ǹjẹ́ ẹni tí ó fi ìpìlẹ̀ ilé mímọ tirẹ̀ lélẹ̀ lórí ìbẹ̀rù Allāhu àti ìyọ́nú (Rẹ̀) ló lóore jùlọ ni tàbí ẹni tí ó fi ìpìlẹ̀ ilé mímọ tirẹ̀ lélẹ̀ ní ẹ̀gbẹ́ kan létí ọ̀gbun tó máa yẹ̀ lulẹ̀, tí ó sì máa yẹ̀ ẹ́ lẹ́sẹ̀ sínú iná Jahanamọ? Allāhu kò níí fi ọ̀nà mọ ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(9,110,'9:110','Ilé wọn tí wọ́n mọ kalẹ̀ kò níí yé kó iyèméjì sínú ọkàn wọn títí ọkàn wọn yóò fi já kélekèle. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(9,111,'9:111','Dájúdájú Allāhu ra ẹ̀mí àwọn onígbàgbọ́ òdodo àti dúkìá wọn nítorí pé dájúdájú tiwọn ni Ọgbà Ìdẹ̀ra. Wọ́n ń jagun lójú ọ̀nà (ẹ̀sìn) Allāhu; wọ́n ń pa ọ̀tá ẹ̀sìn, wọ́n sì ń pa àwọn náà. (Ó jẹ́) àdéhùn òdodo lọ́dọ̀ Allāhu nínú at-Taorāh, al-’Injīl àti al-Ƙur’ān. Ta sì ni ó lè mú àdéhùn rẹ̀ ṣẹ ju Allāhu? Nítorí náà, ẹ dunnú sí òkòwò yín tí ẹ (fi ẹ̀mí àti dúkìá yin) ṣe. Ìyẹn, òhun sì ni èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(9,112,'9:112','Àwọn olùronúpìwàdà, àwọn olùjọ́sìn (fún Allāhu), àwọn olùdúpẹ́ (fún Allāhu), àwọn aláààwẹ̀, àwọn olùdáwọ́tẹ-orúnkún (lórí ìrun), àwọn olùforíkanlẹ̀ (fún Allāhu), àwọn olùpàṣẹ-ohun rere, àwọn olùkọ-ohun burúkú àti àwọn olùṣọ́-ẹnu-ààlà tí Allāhu gbékalẹ̀, fún àwọn onígbàgbọ́ òdodo (wọ̀nyí) ní ìró ìdùnnú (Ọgbà Ìdẹ̀ra).');
INSERT INTO "aykyuni" VALUES(9,113,'9:113','Kò yẹ fún Ànábì àti àwọn tó gbàgbọ́ lódodo láti tọrọ àforíjìn fún àwọn ọ̀sẹbọ, wọn ìbáà jẹ́ ẹbí, lẹ́yìn tí ó ti hàn sí wọn pé dájúdájú èrò inú iná Jẹhīm ni wọ́n.');
INSERT INTO "aykyuni" VALUES(9,114,'9:114','Àti pé àforíjìn tí (Ànábì) ’Ibrọ̄hīm tọrọ fún bàbá rẹ̀ kò sì jẹ́ kiní kan bí kò ṣe nítorí àdéhùn tó ṣe fún un. Ṣùgbọ́n nígbà tí ó hàn sí i pé dájúdájú ọ̀tá Allāhu ni (bàbá rẹ̀), ó yọwọ́ yọsẹ̀ kúrò nínú rẹ̀. Dájúdájú (Ànábì) ’Ibrọ̄hīm ni olùrawọ́rasẹ̀, olùfaradà.');
INSERT INTO "aykyuni" VALUES(9,115,'9:115','Allāhu kì í mú ìṣìnà bá ìjọ kan, lẹ́yìn ìgbà tí Ó ti tọ́ wọn sọ́nà, títí (Allāhu) yóò fi ṣe àlàyé n̄ǹkan tí wọ́n máa ṣọ́ra fún fún wọn. Dájúdájú Allāhu ni Onímọ̀ nípa gbogbo n̄ǹkan.1 ');
INSERT INTO "aykyuni" VALUES(9,116,'9:116','Dájúdájú Allāhu ló ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Ó ń sọ ẹ̀dá di alààyè. Ó sì ń sọ ẹ̀dá di òkú. Kò sì sí aláàbò àti alárànṣe kan fún yín lẹ́yìn Allāhu.');
INSERT INTO "aykyuni" VALUES(9,117,'9:117','Dájúdájú Allāhu ti gba ìronúpìwàdà Ànábì, àwọn Muhājirūn àti àwọn ’Ansọ̄r, àwọn tó tẹ̀lé e ní àkókò ìṣòro lẹ́yìn tí ọkàn ìgun kan nínú wọn fẹ́ẹ̀ yí padà, (àmọ́) lẹ́yìn náà, Allāhu gba ìronúpìwàdà wọn. Dájúdájú Òun ni Aláàánú, Àṣàkẹ́-ọ̀run fún wọn.');
INSERT INTO "aykyuni" VALUES(9,118,'9:118','(Ó tún gba ìronúpìwàdà) àwọn mẹ́ta tí A so ọ̀rọ̀ gbígba ìronúpìwàdà wọn rọ̀ (nínú àwọn olùsásẹ́yìn fún ogun Tabūk. Àwọn mùsùlùmí dẹ́yẹ sí wọn) títí ilẹ̀ fi fún mọ́ wọn tòhun ti bí ó ṣe fẹjú tó. Ọ̀rọ̀ ara wọn sì ṣú ara wọn. Wọ́n sì mọ̀ (ní àmọ̀dájú) pé kò sí ibùsásí kan tí àwọn fi lè sá mọ́ Allāhu lọ́wọ́ àfi kí wọ́n sá sí ọ̀dọ̀ Rẹ̀. Lẹ́yìn náà, Allāhu gba ìronúpìwàdà wọn nítorí kí wọ́n lè máa ronú pìwàdà. Dájúdájú Allāhu, Òun ni Olùgba-ìronúpìwàdà, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(9,119,'9:119','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ bẹ̀rù Allāhu, kí ẹ sì wà pẹ̀lú àwọn olódodo.');
INSERT INTO "aykyuni" VALUES(9,120,'9:120','Kò lẹ́tọ̀ọ́ fún àwọn ará ìlú Mọdīnah àti ẹni tí ó wà ní àyíká wọn nínú àwọn Lárúbáwá oko láti sá sẹ́yìn fún Òjíṣẹ́ Allāhu (nípa ogun ẹ̀sìn. Kò sì lẹ́tọ̀ọ́ fún wọn) láti fẹ́ràn ẹ̀mí ara wọn ju ẹ̀mí rẹ̀. Ìyẹn nítorí pé dájúdájú òǹgbẹ, ìnira àti ebi kan kò níí ṣẹlẹ̀ sí wọn lójú ogun ẹ̀sìn Allāhu, wọn kò sì níí tẹ ọ̀nà kan tí ń bí àwọn aláìgbàgbọ́ nínú, àti pé ọwọ́ wọn kò níí ba kiní kan lára ọ̀tá (yálà ọrọ̀ ogun tàbí ẹ̀mí wọn) àfi kí wọ́n fi kọ iṣẹ́ rere sílẹ̀ fún wọn. Dájúdájú Allāhu kò níí fi ẹ̀san àwọn olùṣe-rere ráre.');
INSERT INTO "aykyuni" VALUES(9,121,'9:121','Wọn kò sì níí ná owó kékeré tàbí púpọ̀ (fún ogun ẹ̀sìn), tàbí kí wọ́n la àfonífojì kan kọ já àfi kí wọ́n kọ ọ́ sílẹ̀ fún wọn nítorí kí Allāhu lè san wọ́n ní ẹ̀san rere tó dára ju ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(9,122,'9:122','Àwọn mùsùlùmí kò ní láti tú jáde tán nílé pátápátá. Èé ṣe tí igun kan nínú wọn nínú ikọ̀ kọ̀ọ̀kan kò fi jáde láti wá àgbọ́yé ìmọ̀ nípa ẹ̀sìn, kí wọ́n sì máa ṣe ìkìlọ̀ fún àwọn ènìyàn wọn nígbà tí wọ́n bá padà sọ́dọ̀ wọn nítorí kí wọ́n lè ṣọ́ra ṣe.');
INSERT INTO "aykyuni" VALUES(9,123,'9:123','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ gbógun ti àwọn tó súnmọ́ yín nínú àwọn aláìgbàgbọ́; kí wọ́n rí ìlekoko lára yín. Kí ẹ sì mọ̀ pé dájúdájú Allāhu wà pẹ̀lú àwọn olùbẹ̀rù (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(9,124,'9:124','Nígbà tí A bá sì sọ sūrah kan kalẹ̀, ó ń bẹ nínú wọn ẹni tí ó máa wí pé: “Èwo nínú yín ni èyí lé ìgbàgbọ́ (rẹ̀) kún?” Ní ti àwọn tó gbàgbọ́ ní òdodo, (āyah náà) yóò lé ìgbàgbọ́ (wọn) kún. Wọn yó sì máa dunnú.');
INSERT INTO "aykyuni" VALUES(9,125,'9:125','Ní ti àwọn tí àìsàn kan wà nínú ọkàn wọn, (āyah náà) yó sì ṣe àlékún ẹ̀gbin sí ẹ̀gbin wọn. Wọn yó sì kú sí ipò aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(9,126,'9:126','Ṣé wọn kò rí i pé wọ́n ń dán wọn wò ní ẹ̀ẹ̀ kan tàbí ẹ̀ẹ̀ méjì ní ọdọọdún. Lẹ́yìn náà, wọn kò ronú pìwàdà, wọn kò sì lo ìrántí.');
INSERT INTO "aykyuni" VALUES(9,127,'9:127','Àti pé nígbà tí A bá sọ sūrah kan kalẹ̀, apá kan wọn yóò wo apá kan lójú (wọn yó sì wí pé): “Ṣé ẹnì kan ń wò yín bí?” Lẹ́yìn náà, wọ́n máa pẹ̀yìndà. Allāhu sì pa ọkàn wọn dà (sódì) nítorí pé dájúdájú àwọn ni ìjọ kan tí kò gbọ́ àgbọ́yé.');
INSERT INTO "aykyuni" VALUES(9,128,'9:128','Dájúdájú Òjíṣẹ́ kan ti wá ba yín láti ààrin yín. Ohun tí ó máa kó wàhálà ba yín lágbára lára rẹ̀. Ó ń ṣe àkólékàn (oore) fún yín; aláàánú àti oníkẹ̀ẹ́ ni fún àwọn oní gbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(9,129,'9:129','Nítorí náà, tí wọ́n bá pẹ̀yìndà, sọ pé: “Allāhu tó fún mi. Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Òun ni mo gbáralé. Òun sì ni Olúwa Ìtẹ́ ńlá.');
INSERT INTO "aykyuni" VALUES(10,1,'10:1','’Alif Lām Rọ̄. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)Ìwọ̀nyí ni àwọn āyah Tírà tí ó kún fún ọgbọ́n.2 ');
INSERT INTO "aykyuni" VALUES(10,2,'10:2','Ṣé ó jẹ́ kàyééfì fún àwọn ènìyàn pé A fi ìmísí ránṣẹ́ sí arákùnrin kan nínú wọn pé: “Ṣe ìkìlọ̀ fún àwọn ènìyàn, kí o sì fún àwọn tó gbàgbọ́ ní ìró ìdùnnú pé ẹ̀san rere (iṣẹ́ tí wọ́n ṣe) ṣíwájú ti wà ní ọ̀dọ̀ Olúwa wọn.”? Àwọn aláìgbàgbọ́ wí pé: “Dájúdájú òpìdán pọ́nńbélé mà ni (Ànábì) yìí.”');
INSERT INTO "aykyuni" VALUES(10,3,'10:3','Dájúdájú Olúwa yín ni Allāhu, Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ fún ọjọ́ mẹ́fà. Lẹ́yìn náà, Ó gúnwà sí orí Ìtẹ́-ọlá. Ó ń ṣètò ọ̀rọ̀ (ẹ̀dá). Kò sí olùṣìpẹ̀ kan àfi lẹ́yìn ìyọ̀ǹda Rẹ̀. Ìyẹn ni Allāhu, Olúwa yín. Nítorí náà, ẹ jọ́sìn fún Un. Ṣé ẹ ò níí lo ìrántí ni?');
INSERT INTO "aykyuni" VALUES(10,4,'10:4','Ọ̀dọ̀ Rẹ̀ ni ibùpadàsí gbogbo yín. (Ó jẹ́) àdéhùn Allāhu ní ti òdodo. Dájúdájú Òun l’Ó ń pilẹ̀ dídá ẹ̀dá. Lẹ́yìn náà, Ó máa da á padà (sọ́dọ̀ Rẹ̀) nítorí kí Ó lè fi déédé san ẹ̀san fún àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe iṣẹ́ rere. Àwọn tó sì ṣàì gbàgbọ́, ohun mímu tó gbóná parí àti ìyà ẹlẹ́ta-eléro ń bẹ fún wọn nítorí pé, wọ́n ṣàì gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(10,5,'10:5','(Allāhu) Òun ni Ẹni tí Ó ṣe òòrùn ní ìtànsán. (Ó ṣe) òṣùpá ní ìmọ́lẹ̀. Ó sì díwọ̀n (ìrísí) rẹ̀ sínú àwọn ibùsọ̀ nítorí kí ẹ lè mọ òǹkà àwọn ọdún àti ìṣírò (ọjọ́). Allāhu kò dá ìyẹn bí kò ṣe pẹ̀lú òdodo. Ó ń ṣàlàyé àwọn āyah fún ìjọ tó nímọ̀.');
INSERT INTO "aykyuni" VALUES(10,6,'10:6','Dájúdájú àwọn àmì wà nínú ìtẹ̀léǹtẹ̀lé òru àti ọ̀sán àti ohun tí Allāhu dá sínú àwọn sánmọ̀ àti ilẹ̀; (àmì wà nínú wọn) fún ìjọ tó ń bẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(10,7,'10:7','Dájúdájú àwọn tí kò retí ìpàdé Wa (ní ọ̀run), tí wọ́n yọ́nú sí ìṣẹ̀mí ayé, tí ọkàn wọn sì balẹ̀ dódó sí (ìṣẹ̀mí ayé yìí) àti àwọn afọ́nú-fọ́ra nípa àwọn āyah Wa,');
INSERT INTO "aykyuni" VALUES(10,8,'10:8','àwọn wọ̀nyẹn, ibùgbé wọn ni Iná nítorí ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(10,9,'10:9','Dájúdájú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, Olúwa wọn yóò fi ìgbàgbọ́ òdodo wọn tọ́ wọn sọ́nà.Àwọn odò yó sì máa ṣàn ní ìsàlẹ̀ ọ̀dọ̀ wọn nínú Ọgbà Ìdẹ̀ra. ');
INSERT INTO "aykyuni" VALUES(10,10,'10:10','Àdúà wọn nínú rẹ̀ ni “mímọ́ ni fún Ọ, Allāhu”. Ìkíni wọn nínú rẹ̀ ni “àlàáfíà”. Ìparí àdúà wọn sì ni pé, “Gbogbo ẹyìn ń jẹ́ ti Allāhu,Olúwa gbogbo ẹ̀dá.” ');
INSERT INTO "aykyuni" VALUES(10,11,'10:11','Tí ó bá jẹ́ pé Allāhu ń kánjú mú aburú bá àwọn ènìyàn (nípasẹ̀ èpè ẹnu wọn, gẹ́gẹ́ bí Ó ṣe ń) tètè mú oore bá wọn (nípasẹ̀ àdúà), A ìbá ti mú òpin ba ìṣẹ́mí wọn. Nítorí náà, A máa fi àwọn tí kò retí ìpàdé Wa sílẹ̀ sínú àgbéré wọn, tí wọn yóò máa pa rìdàrìdà.');
INSERT INTO "aykyuni" VALUES(10,12,'10:12','Nígbà tí ìnira bá kan ènìyàn, ó máa pè Wá lórí ìdùbúlẹ̀ rẹ̀ tàbí ní ìjókòó tàbí ní ìnàró. Nígbà tí A bá mú ìnira rẹ̀ kúrò fún un, ó máa tẹ̀ síwájú (nínú àìgbàgbọ́) bí ẹni pé kò pè Wá sí ìnira tí ó mú un. Báyẹn ni wọ́n ṣe ní ọ̀ṣọ́ fún àwọn alákọyọ ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(10,13,'10:13','A kúkú ti pa àwọn ìran kan rẹ́ ṣíwájú yín nígbà tí wọ́n ṣe àbòsí. Àwọn Òjíṣẹ́ wọn mú àwọn ẹ̀rí tó yanjú wá bá wọn. Wọn kò sì gbàgbọ́. Báyẹn ni A ṣe ń san ìjọ ẹlẹ́ṣẹ̀ ní ẹ̀san.');
INSERT INTO "aykyuni" VALUES(10,14,'10:14','Lẹ́yìn náà, A ṣe yín ní àrólé lórí ilẹ̀ lẹ́yìn wọn nítorí kí Á lè wo bí ẹ̀yin náà yóò ṣe máa ṣe.');
INSERT INTO "aykyuni" VALUES(10,15,'10:15','Nígbà tí wọ́n bá ń ké àwọn āyah Wa tó yanjú fún wọn, àwọn tí kò retí ìpàdé Wa (ní ọ̀run) yóò máa wí pé: “Mú Ƙur’ān kan wá yàtọ̀ sí èyí tàbí kí o yí i padà.” Sọ pé: “Kò lẹ́tọ̀ọ́ fún mi láti yí i padà láti ọ̀dọ̀ ara mi. Èmi kò tẹ̀lé kiní kan àyàfi ohun tí Wọ́n fi ránṣẹ́ sí mi ní ìmísí. Dájúdájú èmi ń páyà ìyà Ọjọ́ ńlá tí mo bá fi lè yapa Olúwa mi.”');
INSERT INTO "aykyuni" VALUES(10,16,'10:16','Sọ pé: “Tí ó bá jẹ́ pé Allāhu bá fẹ́ ni, èmi ìbá tí lè ké al-Ƙur’ān fún yín, àti pé Allāhu ìbá tí fi ìmọ̀ rẹ̀ mọ̀ yín. Mo kúkú ti lo àwọn ọdún kan láààrin yín ṣíwájú (ìsọ̀kalẹ̀) rẹ̀, ṣé ẹ ò ṣe làákàyè ni?”');
INSERT INTO "aykyuni" VALUES(10,17,'10:17','Nítorí náà, ta ló ṣàbòsí ju ẹni tó dá àdápa irọ́ mọ́ Allāhu tàbí tó pe àwọn āyah Rẹ̀ nírọ́? Dájúdájú àwọn ẹlẹ́ṣẹ̀ kò níí jèrè.');
INSERT INTO "aykyuni" VALUES(10,18,'10:18','Wọ́n ń jọ́sìn fún ohun tí kò lè kó ìnira bá wọn, tí kò sì lè ṣe wọ́n ní àǹfààní lẹ́yìn Allāhu. Wọ́n sì ń wí pé: “Àwọn wọ̀nyí ni olùṣìpẹ̀ wa lọ́dọ̀ Allāhu.” Sọ pé: “Ṣé ẹ máa fún Allāhu ní ìró ohun tí kò mọ̀ nínú àwọn sánmọ̀ àti ilẹ̀ ni?” Mímọ́ ni fún Un, Ó ga tayọ n̄ǹkan tí wọ́n ń fi ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(10,19,'10:19','Kí ni àwọn ènìyàn jẹ́ (ní ìpìlẹ̀) bí kò ṣe ìjọ ẹyọ kan (ìjọ ’Islām). Lẹ́yìn náà ni wọ́n yapa-ẹnu. Tí kò bá jẹ́ pé ọ̀rọ̀ kan tó ti ṣíwájú lọ́dọ̀ Olúwa rẹ, Àwa ìbá ti yanjú ohun tí wọ́n ń yapa-ẹnu lórí rẹ̀ láààrin ara wọn.');
INSERT INTO "aykyuni" VALUES(10,20,'10:20','Wọ́n ń wí pé: “Kí ni kò jẹ́ kí àmì kan sọ̀kalẹ̀ fún un láti ọ̀dọ̀ Olúwa rẹ̀?” Nítorí náà, sọ pé: “Ti Allāhu ni ìkọ̀kọ̀. Nítorí náà, ẹ máa retí. Dájúdájú èmi náà wà pẹ̀lú yín nínú àwọn olùretí.”');
INSERT INTO "aykyuni" VALUES(10,21,'10:21','Nígbà tí A bá fún àwọn ènìyàn ní ìdẹ̀ra kan tọ́wò lẹ́yìn tí ìnira ti fọwọ́ bà wọ́n, nígbà náà ni wọ́n yóò máa dète sí àwọn āyah Wa. Sọ pé: “Allāhu yára jùlọ níbi ète.Dájúdájú àwọn Òjíṣẹ́ Wa ń ṣe àkọsílẹ̀ ohun tí ẹ̀ ń dá ní ète.” ');
INSERT INTO "aykyuni" VALUES(10,22,'10:22','(Allāhu) Òun ni Ẹni tí Ó mu yín rìn lórí ilẹ̀ àti ní ojú omi, títí di ìgbà tí ẹ bá wà nínú ọkọ̀ ojú-omi, tí ó ń gbé wọn lọ pẹ̀lú atẹ́gùn tó dára, inú wọn yó sì máa dùn sí i. (Àmọ́) atẹ́gùn líle kọ lù ú, ìgbì omi dé bá wọn ní gbogbo àyè, wọ́n sì mọ̀ pé dájúdájú wọ́n ti fi (àdánwò) yí àwọn po, wọ́n máa pe Allāhu lẹ́ni tí yóò máa fi àfọ̀mọ́-ọkàn (àníyàn mímọ́) ṣe ẹ̀sìn fún Un(wọ́n sì máa sọ pé): “Dájúdájú tí O bá fi lè gbà wá là níbi èyí, dájúdájú a máa wà nínú àwọn olùdúpẹ́.” ');
INSERT INTO "aykyuni" VALUES(10,23,'10:23','Ṣùgbọ́n nígbà tí Ó gbà wọ́n là tán ìgbà náà ni wọ́n tún ń ṣe ìbàjẹ́ kiri lórí ilẹ̀ láì lẹ́tọ̀ọ́. Ẹ̀yin ènìyàn, dájúdájú ìbàjẹ́ yín ń bẹ lórí yín. (Ìbàjẹ́ yín sì jẹ́) ìgbádùn ìṣẹ̀mí ayé. Lẹ́yìn náà, ọ̀dọ̀ Wa ni ibùpadàsí yín. A sì máa fún yín ní ìró ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(10,24,'10:24','Àpèjúwe ìṣẹ̀mí ayé dà bí omi kan tí A sọ̀kalẹ̀ láti sánmọ̀, tí àwọn irúgbìn nínú ohun tí ènìyàn àti àwọn ẹran-ọ̀sìn ń jẹ sì gbà á sára, títí di ìgbà tí ilẹ̀ yóò fi lọ́ràá. Ó sì mú ọ̀ṣọ́ (ara) rẹ̀ jáde. Àwọn tó ni ín sì lérò pé àwọn ni alágbára lórí rẹ̀, (nígbà náà ni) àṣẹ Wa dé bá a ní òru tàbí ní ọ̀sán. A sì sọ ọ́ di oko tí wọ́n fà tu dànù bí ẹni pé kò sí níbẹ̀ rárá rí ní àná. Báyẹn ni A ṣe ń ṣàlàyé àwọn āyah fún ìjọ aláròjinlẹ̀.');
INSERT INTO "aykyuni" VALUES(10,25,'10:25','Allāhu ń pèpè sí ilé Àlàáfíà. Ó sì ń tọ́ ẹni tí Ó bá fẹ́ sí ọ̀nà tààrà (’Islām).');
INSERT INTO "aykyuni" VALUES(10,26,'10:26','Rere àti àlékún (oore) wà fún àwọn tó ṣe rere. Eruku tàbí ìyẹpẹrẹ kan kò níí bò wọ́n lójú mọ́lẹ̀. Àwọn wọ̀nyẹn ni èrò inú Ọgbà Ìdẹ̀ra. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(10,27,'10:27','Àwọn tó sì ṣ’iṣẹ́ aburú, ẹ̀san aburú bí irú rẹ̀ (ni ẹ̀san wọn). Ìyẹpẹrẹ sì máa bò wọ́n mọ́lẹ̀. Kò sí aláàbò kan fún wọn lọ́dọ̀ Allāhu. (Wọ́n máa dà bí) ẹni pé wọ́n fi apá kan òru tó ṣókùnkùn bò wọ́n lójú pa. Àwọn wọ̀nyẹn ni èrò inú Iná. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(10,28,'10:28','Àti pé (rántí) Ọjọ́ tí A óò kó gbogbo wọn jọ, lẹ́yìn náà A óò sọ fún àwọn tó bá Allāhu wá akẹgbẹ́ pé: “Ẹ dúró pa sí àyè yín, ẹ̀yin àti àwọn òrìṣà yín.” Nítorí náà, A yà wọ́n sí ọ̀tọ̀ọ̀tọ̀. Àwọn òrìṣà wọn sì wí pé: “Àwa kọ́ ni ẹ̀ ń jọ́sìn fún.”');
INSERT INTO "aykyuni" VALUES(10,29,'10:29','Nítorí náà, Allāhu tó ní Ẹlẹ́rìí láààrin àwa àti ẹ̀yin pé àwa jẹ́ aláìmọ̀ nípa ìjọ́sìn yín (tí ẹ̀ ṣe fún wa).”');
INSERT INTO "aykyuni" VALUES(10,30,'10:30','Níbẹ̀ yẹn, ẹ̀mí kọ̀ọ̀kan máa dá ohun tó ṣe síwájú mọ̀. Wọn yó sì da wọ́n padà sọ́dọ̀ Allāhu Olúwa wọn Òdodo. Ohun tí wọ́n ń dá ní àdápa irọ́ sì di òfo mọ́ wọn lọ́wọ́.1 ');
INSERT INTO "aykyuni" VALUES(10,31,'10:31','Sọ pé: “Ta ni Ó ń pèsè fún yín láti inú sánmọ̀ àti ilẹ̀? Ta ni Ó ní ìkápá lórí ìgbọ́rọ̀ àti ìríran? Ta ni Ó ń mú alààyè jáde láti ara òkú, tí Ó tún ń mú òkú jáde láti ara alààyè? Ta sì ni Ó ń ṣe ètò ọ̀rọ̀ (ẹ̀dá)?” Wọn yóò wí pé: “Allāhu” Nígbà náà, sọ pé: “Ṣé ẹ ò níí bẹ̀rù (Rẹ̀) ni?”');
INSERT INTO "aykyuni" VALUES(10,32,'10:32','Ìyẹn ni Allāhu Olúwa yín Òdodo. Kí sì ni ó ń bẹ lẹ́yìn Òdodo bí kò ṣe ìṣìnà? Nítorí náà, báwo ni wọ́n ṣe ń ṣẹ́rí yín kúrò níbi òdodo?');
INSERT INTO "aykyuni" VALUES(10,33,'10:33','Báyẹn ni ọ̀rọ̀ Olúwa rẹ ṣe wá sí ìmúṣẹ lórí àwọn tó ṣèbàjẹ́ pé dájúdájú wọn kò níí gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(10,34,'10:34','Sọ pé: “Ǹjẹ́ ó wà nínú àwọn òrìṣà yín ẹni tí ó lè pilẹ̀ dídá ẹ̀dá, lẹ́yìn náà, tí ó máa dá a padà (sí ìpìlẹ̀ rẹ̀ lẹ́yìn ikú)?” Sọ pé: “Allāhu l’Ó ń pilẹ̀ dídá ẹ̀dá. Lẹ́yìn náà, Ó máa dá a padà (sí ìpìlẹ̀ rẹ̀ lẹ́yìn ikú). Nítorí náà, báwo ni wọ́n ṣe ń ṣẹ́rí yín kúrò níbi òdodo?');
INSERT INTO "aykyuni" VALUES(10,35,'10:35','Sọ pé: “Ǹjẹ́ ó wà nínú àwọn òrìṣà yín ẹni tó ń fini mọ̀nà síbi òdodo?” Sọ pé: “Allāhu l’Ó ń fini mọ̀nà síbi òdodo. Nígbà náà, ṣé Ẹni t’Ó ń fini mọ̀nà síbi òdodo ló lẹ́tọ̀ọ́ jùlọ sí pé kí wọ́n máa tẹ̀lé ni tàbí ẹni tí kò lè dá ọ̀nà mọ̀ fúnra rẹ̀ àfi tí A bá fi mọ̀nà?” Nítorí náà, kí ló ń ṣe yín tí ẹ fi ń dájọ́ báyìí?');
INSERT INTO "aykyuni" VALUES(10,36,'10:36','Ọ̀pọ̀lọ́pọ̀ wọn kò sì rí kiní kan tẹ̀lé bí kò ṣe àròsọ. Dájúdájú àbá dídá kò sì lè rọrọ̀ kiní kan níwájú òdodo. Dájúdájú Allāhu ni Onímọ̀ nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(10,37,'10:37','Al-Ƙur’ān yìí kì í ṣe n̄ǹkan tí ó ṣe é dáhun (láti ọ̀dọ̀ ẹlòmíìràn) lẹ́yìn Allāhu, ṣùgbọ́n ó ń jẹ́rìí sí èyí tó jẹ́ òdodo nínú èyí tó ṣíwájú rẹ̀, ó ń ṣe àlàyé (àwọn) Tírà náà. Kò sí iyèméjì nínú rẹ̀. (Ó wá) láti ọ̀dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(10,38,'10:38','Tàbí wọ́n ń wí pé: “Ó hun ún ni? Sọ pé: “Ẹ mú sūrah kan bí irú rẹ̀ wá. Kí ẹ sì pe ẹnikẹ́ni tí ẹ bá lè pè lẹ́yìn Allāhu, tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(10,39,'10:39','Kò rí bẹ́è, wọ́n pe ohun tí wọn kò ní ìmọ̀ rẹ̀ nírọ́ ni. Àti pé ìmúṣẹ ọ̀rọ̀ Rẹ̀ kò tí ì dé bá wọn (ni wọ́n fi pè é nírọ́). Báyẹn ni àwọn tó ṣíwájú wọn ṣe pe (ọ̀rọ̀ Allāhu) nírọ́. Nítorí náà, wo bí àtubọ̀tán àwọn alábòsí ṣe rí.');
INSERT INTO "aykyuni" VALUES(10,40,'10:40','Ó sì ń bẹ nínú wọn ẹni tó gbà á gbọ́ ní òdodo. Ó tún ń bẹ́ nínú wọn ẹni tí kò gbà á gbọ́. Olúwa rẹ sì ni Onímọ̀-jùlọ nípa àwọn òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(10,41,'10:41','Tí wọ́n bá pè ọ́ ní òpùrọ́, sọ pé: “Tèmi ni iṣẹ́ mi. Tiyín ni iṣẹ́ yín. Ẹ̀yin yọwọ́ yọsẹ̀ nínú ohun tí mò ń ṣe níṣẹ́. Èmi náà sì yọwọ́ yọsẹ̀ nínú ohun tí ẹ̀ ń ṣe níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(10,42,'10:42','Ó sì wà nínú wọn, àwọn tó ń tẹ́tí sí ọ. Ṣé ìwọ lo máa mú adití gbọ́rọ̀ ni, bí ó tilẹ̀ jẹ́ pé wọn kì í ṣe làákàyè?1 ');
INSERT INTO "aykyuni" VALUES(10,43,'10:43','Ó tún ń bẹ nínú wọn ẹni tó ń wò ọ́. Ṣé ìwọ lo máa fi afọ́jú mọ̀nà ni, bí ó tilẹ̀ jẹ́ pé wọn kì í ríran?');
INSERT INTO "aykyuni" VALUES(10,44,'10:44','Dájúdájú Allāhu kò níí fí kiní kan ṣàbòsí sí ènìyàn. Ṣùgbọ́n ènìyàn ń ṣàbòsí sí ẹ̀mí ara wọn.');
INSERT INTO "aykyuni" VALUES(10,45,'10:45','Àti pé (rántí) Ọjọ́ tí (Allāhu) yóò kó wọn jọ àfi bí ẹni pé wọn kò gbé ilé ayé ju àkókò kan nínú ọ̀sán. Wọn yó sì dára wọn mọ̀. Dájúdájú àwọn tó pe ìpàdé Allāhu nírọ́ ti ṣòfò; wọn kò sì jẹ́ olùmọ̀nà.');
INSERT INTO "aykyuni" VALUES(10,46,'10:46','Ó ṣeé ṣe kí A fi apá kan èyí tí A ṣe ní ìlérí fún wọn hàn ọ́ tàbí kí A ti gba ẹ̀mí rẹ (ṣíwájú àsìkò náà), ọ̀dọ̀ Wa kúkú ni ibùpadàsí wọn. Lẹ́yìn náà, Allāhu ni Arínú-róde ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(10,47,'10:47','Òjíṣẹ́ ti wà fún ìjọ kọ̀ọ̀kan.Nítorí náà, nígbà tí Òjíṣẹ́ wọn bá dé, A óò ṣèdájọ́ láààrin wọn pẹ̀lú déédé; A ò sì níí ṣàbòsí sí wọn. ');
INSERT INTO "aykyuni" VALUES(10,48,'10:48','Wọ́n ń wí pé: “Ìgbà wo ni àdéhùn yìí yóò ṣẹ tí ẹ bá jẹ́ olódodo?”');
INSERT INTO "aykyuni" VALUES(10,49,'10:49','Sọ pé: “Èmi kò ní ìkápá ìnira tàbí oore kan fún ẹ̀mí ara mi àfi ohun tí Allāhu bá fẹ́. Gbèdéke àkókò ti wà fún ìjọ kọ̀ọ̀kan. Nígbà tí àkókò wọn bá dé, wọn kò níí lè sún un ṣíwájú di àkókò kan, wọn kò sì níí lè fà á sẹ́yìn.”1 ');
INSERT INTO "aykyuni" VALUES(10,50,'10:50','Sọ pé: “Ẹ sọ fún mi, tí ìyà Rẹ̀ bá dé ba yín ní òru tàbí ní ọ̀sán? Èwo nínú rẹ̀ ni àwọn ẹlẹ́ṣẹ̀ ń wá pẹ̀lú ìkánjú?”');
INSERT INTO "aykyuni" VALUES(10,51,'10:51','Ṣé lẹ́yìn ìgbà tí ó bá ṣẹlẹ̀ tán ni ẹ máa gbà á gbọ́? Ṣé nísinsìn yìí (ni ẹ óò gbà á gbọ́), tí ẹ sì kúkú ti ń wá a pẹ̀lú ìkánjú?');
INSERT INTO "aykyuni" VALUES(10,52,'10:52','Lẹ́yìn náà, A óò sọ fún àwọn tó ṣàbòsí pé: “Ẹ tọ́ ìyà gbére wò.” Ṣé A óò san yín ní ẹ̀san kan bí kò ṣe ohun tí ẹ̀ ń ṣe níṣẹ́?');
INSERT INTO "aykyuni" VALUES(10,53,'10:53','Wọ́n sì ń bèèrè fún ìró rẹ̀ lọ́dọ̀ rẹ̀ pé, “ṣé òdodo ni?” Sọ pé: “Bẹ́ẹ̀ ni. Mo fi Olúwa mi búra. Dájúdájú òdodo ni. Ẹ̀yin kò sì níí mórí bọ́.”');
INSERT INTO "aykyuni" VALUES(10,54,'10:54','Àti pé tí ó bá jẹ́ pé gbogbo n̄ǹkan tí ń bẹ lórí ilẹ̀ jẹ́ ti ẹ̀mí kọ̀ọ̀kan tó ṣàbòsí, ìbá fi ṣèràpadà (fún ẹ̀mí ara rẹ̀ níbi ìyà). Wọn yóò fi igbe àbámọ̀ pamọ́ nígbà tí wọ́n bá rí Ìyà. A ó ṣe ìdájọ́ láààrin wọn pẹ̀lú déédé; Wọn kò sì níí ṣàbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(10,55,'10:55','Gbọ́, dájúdájú ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀. Gbọ́! Dájúdájú àdéhùn Allāhu ni òdodo, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn kò nímọ̀.');
INSERT INTO "aykyuni" VALUES(10,56,'10:56','Ó ń sọ ẹ̀dá di alààyè. Ó sì ń sọ ẹ̀dá di òkú. Ọ̀dọ̀ Rẹ̀ sì ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(10,57,'10:57','Èyin ènìyàn, ìṣítí kan láti ọ̀dọ̀ Olúwa yín ti dé ba yín. Ìwòsàn ni fún n̄ǹkan tó wà nínú àwọn igbá-àyà ẹ̀dá. Ìmọ̀nà àti ìkẹ́ ni fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(10,58,'10:58','Sọ pé: “Pẹ̀lú oore àjùlọ Allāhu (ìyẹn, al-Ƙur’ān) àti àánú Rẹ̀ (ìyẹn, ’Islām), nítorí ìyẹn ni kí wọ́n máa fi dunnú; ó sì lóore ju ohun tí àwọn (aláìgbàgbọ́) ń kó jọ (nínú oore ayé).”');
INSERT INTO "aykyuni" VALUES(10,59,'10:59','Sọ pé: “Ẹ sọ fún mi nípa àwọn n̄ǹkan tí Allāhu sọ̀kalẹ̀ fún yín nínú arísìkí, tí ẹ̀yin fúnra yín ṣe àwọn kan ní èèwọ̀ àti ẹ̀tọ́.” Sọ pé, “Ṣé Allāhu l’Ó yọ̀ǹda fún yín (láti ṣe bẹ́ẹ̀ ni) tàbí ẹ̀ ń dá àdápa irọ́ mọ́ Allāhu?”');
INSERT INTO "aykyuni" VALUES(10,60,'10:60','Kí ni èrò-ọkàn àwọn tó ń dá àdápa irọ́ mọ́ Allāhu ní Ọjọ́ Àjíǹde ná? Dájúdájú Allāhu ni Ọlọ́lá-jùlọ lórí àwọn ènìyàn, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kì í dúpẹ́ (fún Un).');
INSERT INTO "aykyuni" VALUES(10,61,'10:61','Ìwọ kò níí wà nínú ìṣe kan, ìwọ kò sì níí ké (āyah kan) nínú al-Ƙur’ān, ẹ̀yin kò sì níí ṣe iṣẹ́ kan àfi kí Àwa jẹ́ Ẹlẹ́rìí lórí yín nígbà tí ẹ bá bẹ̀rẹ̀ sí ṣe é. Kiní kan kò pamọ́ fún Olúwa rẹ; ohun tí ó mọ ní òdíwọ̀n ọmọ iná-igún nínú ilẹ̀ àti nínú sánmọ̀, èyí tí ó kéré sí ìyẹn àti èyí tí ó tóbi jù ú lọ àfi kí ó wà nínú àkọsílẹ̀ tó yanjú.');
INSERT INTO "aykyuni" VALUES(10,62,'10:62','Gbọ́, dájúdájú àwọn ọ̀rẹ́ Allāhu, kò níí sí ìbẹ̀rù (ìyà ọ̀run) fún wọn, wọn kò sì níí banújẹ́ (lórí oore ayé).1 ');
INSERT INTO "aykyuni" VALUES(10,63,'10:63','(Àwọn ni) àwọn tó gbàgbọ́ lódodo, wọ́n sì máa ń bẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(10,64,'10:64','Ti wọn ni ìró ìdùnnú nínú ìṣẹ̀mí ayé (yìí) àti ní Ọjọ́ Ìkẹ́yìn. Kò sí ìyípadà kan fún àwọn ọ̀rọ̀ Allāhu.Ìyẹn, òhun ni èrèǹjẹ ńlá. ');
INSERT INTO "aykyuni" VALUES(10,65,'10:65','Má ṣe jẹ́ kí ọ̀rọ̀ (ẹnu) wọn bà ọ́ nínú jẹ́. Dájúdájú gbogbo agbára pátápátá ń jẹ́ ti Allāhu. Òun ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(10,66,'10:66','Gbọ́, dájúdájú ti Allāhu ni ẹnikẹ́ni tó ń bẹ nínú àwọn sánmọ̀ àti ẹnikẹ́ni tó ń bẹ lórí ilẹ̀. Àwọn tó ń pe àwọn òrìṣà lẹ́yìn Allāhu kò (rí) akẹgbẹ́ Allāhu tẹ̀lé (torí pé, kò sí akẹgbẹ́ fún Allāhu níbìkan kan) Wọn kò tẹ̀lé (kiní kan) bí kò ṣe àbá dídá.Wọn kò sì ṣe kiní kan bí kò ṣe pé wọ́n ń parọ́. ');
INSERT INTO "aykyuni" VALUES(10,67,'10:67','Òun ni Ẹni t’Ó ṣe òru fún yín nítorí kí ẹ lè sinmi nínú rẹ̀. (Ó ṣe) ọ̀sán ní (àsìkò) ìríran (láti rìn káàkiri). Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ tó ń gbọ́rọ̀ (òdodo).');
INSERT INTO "aykyuni" VALUES(10,68,'10:68','Wọ́n wí pé: “Allāhu sọ ẹnì kan di ọmọ.” - Mímọ́ ni fún Un. Òun ní Ọlọ́rọ̀.TiRẹ̀ ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. - Kò sí ẹ̀rí kan lọ́dọ̀ yín lórí èyí. Ṣé ẹ̀yin yóò máa ṣe àfitì ọ̀rọ̀ tí ẹ ò nímọ̀ rẹ̀ sọ́dọ̀ Allāhu ni? ');
INSERT INTO "aykyuni" VALUES(10,69,'10:69','Sọ pé: “Dájúdájú àwọn tó ń dá àdápa irọ́ mọ́ Allāhu, wọn kò níí jèrè.”');
INSERT INTO "aykyuni" VALUES(10,70,'10:70','Ìgbádùn bín-íntín (lè wà fún wọn) nílé ayé. Lẹ́yìn náà, ọ̀dọ̀ Wa ni ibùpadàsí wọn. Lẹ́yìn náà, A óò fún wọn ní ìyà líle tọ́ wò nítorí pé wọ́n ń ṣàì gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(10,71,'10:71','Ka ìròyìn (Ànábì) Nūh fún wọn. Nígbà tí ó sọ fún ìjọ rẹ̀ pé: “Ẹ̀yin ènìyàn mi, tí ó bá jẹ́ pé ìdúró mi (pẹ̀lú yín) àti bí mo ṣe ń fi àwọn āyah Allāhu ṣe ìṣítí fún yín bá lágbára lára yín, nígbà náà Allāhu ni mo gbáralé. Nítorí náà, ẹ pa ìmọ̀ràn yín pọ̀, (kí ẹ sì ké pe) àwọn òrìṣà yín. Lẹ́yìn náà, kí ìpinnu ọ̀rọ̀ yín má ṣe wà ní bòńkẹ́lẹ́ láààrin yín. Lẹ́yìn náà, kí ẹ yanjú ọ̀rọ̀ mi. Kí ẹ sì má ṣe lọ́ mi lára mọ́.');
INSERT INTO "aykyuni" VALUES(10,72,'10:72','Tí ẹ bá sì kọ̀yìn (sí ìrántí), èmi kò kúkú tọrọ owó-ọ̀yà kan lọ́wọ́ yín. Kò sí ẹ̀san kan fún mi (níbì kan) bí kò ṣe lọ́dọ̀ Allāhu. Wọ́n sì ti pa mí láṣẹ pé kí n̄g wà nínú àwọn mùsùlùmí.”');
INSERT INTO "aykyuni" VALUES(10,73,'10:73','Nígbà náà, wọ́n pè é ní òpùrọ́. Nítorí náà, A gbà á là, òun àti àwọn tó ń bẹ pẹ̀lú rẹ̀ nínú ọkọ̀ ojú-omi. A sì ṣe wọ́n ní àrólé (lórí ilẹ̀). A sì tẹ àwọn tó pe āyah Wa nírọ́ rì sínú agbami. Nítorí náà, wo bí ìkángun àwọn ẹni-akìlọ̀-fún ṣe rí.');
INSERT INTO "aykyuni" VALUES(10,74,'10:74','Lẹ́yìn náà, A gbé àwọn Òjíṣẹ́ kan dìde sí àwọn ìjọ wọn. Wọ́n sì mú àwọn ẹ̀rí tó yanjú wá bá wọn. Àwọn náà kò kúkú gbàgbọ́ nínú ohun tí (ijọ́ Ànábì Nūh) pè nírọ́ ṣíwájú (wọn, ìyẹn ni pé, irú kan-ùn ni wọ́n).Báyẹn ni A ṣe ń fi èdídí bo ọkàn àwọn alákọyọ. ');
INSERT INTO "aykyuni" VALUES(10,75,'10:75','Lẹ́yìn náà, lẹ́yìn wọn A fi àwọn àmì Wa rán (Ànábì) Mūsā àti Hārūn níṣẹ́ sí Fir‘aon àti àwọn ìjòyè rẹ̀. Nígbà náà, wọ́n ṣègbéraga. Wọ́n sì jẹ́ ìjọ ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(10,76,'10:76','Nígbà tí òdodo dé bá wọn láti ọ̀dọ̀ Wa, wọ́n wí pé: “Dájúdájú èyí ni idán pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(10,77,'10:77','(Ànábì) Mūsā sọ pé: “Ṣé n̄ǹkan tí ẹ̀yin yóò máa wí nípa òdodo nìyẹn nígbà tí ó dé ba yín? Ṣé idán sì ni èyí! Àwọn òpìdán kò sì níí jèrè.”');
INSERT INTO "aykyuni" VALUES(10,78,'10:78','Wọ́n wí pé: “Ṣé o wá bá wa nítorí kí o lè ṣẹ́rí wa kúrò níbi ohun tí a bá àwọn bàbá wa lórí rẹ̀ (nínú ìbọ̀rìṣà) àti nítorí kí títóbi sì lè jẹ́ tẹ̀yin méjèèjì lórí ilẹ̀? Àwa kò sì níí gba ẹ̀yin méjèèjì gbọ́.”');
INSERT INTO "aykyuni" VALUES(10,79,'10:79','Fir‘aon wí pé: “Ẹ lọ mú gbogbo àwọn onímọ̀ nípa idán pípa wá fún mi.”');
INSERT INTO "aykyuni" VALUES(10,80,'10:80','Nígbà tí àwọn òpìdán dé, (Ànábì) Mūsā sọ fún wọn pé: “Ẹ ju ohun tí ẹ fẹ́ jù sílẹ̀.”');
INSERT INTO "aykyuni" VALUES(10,81,'10:81','Nígbà tí wọ́n jù ú sílẹ̀, (Ànábì) Mūsā sọ pé: “Idán ni ohun tí ẹ mú wá. Dájúdájú Allāhu sì máa bà á jẹ́. Dájúdájú Allāhu kò sì níí ṣàtúnṣe iṣẹ́ àwọn òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(10,82,'10:82','Allāhu sì máa mú òdodo ṣẹ pẹ̀lú àwọn ọ̀rọ̀ Rẹ̀, àwọn ẹlẹ́ṣẹ̀ ìbáà kórira rẹ̀.');
INSERT INTO "aykyuni" VALUES(10,83,'10:83','Nítorí náà, kò sí ẹni tó gba (Ànábì) Mūsā gbọ́ àfi àwọn àrọ́mọdọ́mọ kan nínú àwọn ènìyàn rẹ̀ pẹ̀lú ìbẹ̀rù-bojo (wọn) sí Fir‘aon àti àwọn ìjòyè wọn pé ó máa fòòró àwọn. Dájúdájú Fir‘aon kúkú ṣègbéraga lórí ilẹ̀. Àti pé, dájúdájú ó wà nínú àwọn alákọyọ.');
INSERT INTO "aykyuni" VALUES(10,84,'10:84','(Ànábì) Mūsā sọ pé: “Ẹ̀yin ènìyàn mi, tí ẹ bá jẹ́ ẹni tó gbàgbọ́ nínú Allāhu, Òun náà ni kí ẹ gbáralé tí ẹ bá jẹ́ mùsùlùmí.”');
INSERT INTO "aykyuni" VALUES(10,85,'10:85','Nítorí náà, wọ́n sọ pé: “Allāhu la gbáralé. Olúwa wa, má ṣe wá ní àdánwòfún ìjọ alábòsí. ');
INSERT INTO "aykyuni" VALUES(10,86,'10:86','Kí Ó sì fi àánú Rẹ gbà wá là lọ́wọ́ ìjọ aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(10,87,'10:87','A sì ránṣẹ́ sí (Ànábì) Mūsā àti arákùnrin rẹ̀ pé: “Kí ẹ̀yin méjèèjì mú àwọn ibùgbé fún àwọn ènìyàn yín sí ìlú Misrọ. Kí ẹ sì sọ ibùgbé yín di ibùkírun.Kí ẹ sì máa kírun. Àti pé, fún àwọn onígbàgbọ́ òdodo ní ìró ìdùnnú.” ');
INSERT INTO "aykyuni" VALUES(10,88,'10:88','(Ànábì) Mūsā sọ pé: “Olúwa wa, dájúdájú Ìwọ l’O fún Fir‘aon àti àwọn ìjòyè rẹ̀ ní ọ̀ṣọ́ àti dúkìá nínú ìṣẹ̀mí ayé. Olúwa wa, (O fún wọn) torí kí wọ́n lè ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Rẹ.Olúwa wa, pa dúkìá wọn rẹ́, kí O sì mú ọkàn wọn le, kí wọ́n má gbàgbọ́ mọ́ títí wọn fi máa rí ìyà ẹlẹ́ta-eléro.” ');
INSERT INTO "aykyuni" VALUES(10,89,'10:89','(Allāhu) sọ pé: “Dájúdájú Mo ti gba àdúà ẹ̀yin méjèèjì. Nítorí náà, kí ẹ̀yin méjèèjì dúró ṣinṣin. Ẹ má ṣe tẹ̀lé ojú ọ̀nà àwọn tí kò nímọ̀.”');
INSERT INTO "aykyuni" VALUES(10,90,'10:90','A mú àwọn ọmọ ’Isrọ̄’īl la agbami òkun já. Fir‘aon àti àwọn ọmọ ogun rẹ̀ sì gbá tọ̀ wọ́n lẹ́yìn, ní ti ìlara àti ìtayọ ẹnu-ààlà, títí ìtẹ̀rì sínú agbami òkun fi bá a. Ó sì wí pé: “Mo gbàgbọ́ pé dájúdájú kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Ẹni tí àwọn ọmọ ’Isrọ̄’īl gbàgbọ́. Mo sì wà nínú àwọn mùsùlùmí.”1 ');
INSERT INTO "aykyuni" VALUES(10,91,'10:91','Ṣé nísinsìn yìí, tí ìwọ ti yapa ṣíwájú, tí o sì wà nínú àwọn òbìlẹ̀jẹ́?');
INSERT INTO "aykyuni" VALUES(10,92,'10:92','Nítorí náà, ní òní ni A óò gbé òkú rẹ jáde sí orí ilẹ̀ téńté nítorí kí o lè jẹ́ àmì (fèyíkọ́gbọ́n) fún àwọn tó ń bọ̀ lẹ́yìn rẹ. Dájúdájú ọ̀pọ̀lọ́pọ̀ nínú àwọn ènìyàn mà ni afọ́nú-fọ́ra nípa àwọn āyah Wa.');
INSERT INTO "aykyuni" VALUES(10,93,'10:93','A kúkú ṣe ibùgbé fún àwọn ọmọ ’Isrọ̄’īl ní ibùgbé dáadáa. A sì pèsè fún wọn nínú àwọn n̄ǹkan dáadáa. Nígbà náà, wọn kò yapa-ẹnu (lórí ’Islām) títí ìmọ̀ fi dé bá wọn.Dájúdájú Olúwa rẹ yóò ṣèdájọ́ láààrin wọn ní Ọjọ́ Àjíǹde nípa ohun tí wọ́n ń yapa-ẹnu lórí rẹ̀. ');
INSERT INTO "aykyuni" VALUES(10,94,'10:94','Tí o bá wà nínú iyèméjì nípa n̄ǹkan tí A sọ̀kalẹ̀ fún ọ (pé orúkọ rẹ àti àsọtẹ́lẹ̀ nípa rẹ wà nínú at-Taorāt àti al-’Injīl), bi àwọn tó ń ka Tírà ṣíwájú rẹ léèrè wò.Dájúdájú òdodo ti dé bá ọ láti ọ̀dọ̀ Olúwa rẹ. Nítorí náà, o ò gbọdọ̀ wà lára àwọn oníyèméjì. ');
INSERT INTO "aykyuni" VALUES(10,95,'10:95','O ò gbọdọ̀ wà lára àwọn tó pe àwọn āyah Allāhu nírọ́, nítorí kí o má baà wà lára àwọn ẹni òfò.');
INSERT INTO "aykyuni" VALUES(10,96,'10:96','Dájúdájú àwọn tí ọ̀rọ̀ Olúwa rẹ ti kò lé lórí, wọn kò níí gbàgbọ́,');
INSERT INTO "aykyuni" VALUES(10,97,'10:97','gbogbo āyah ìbáà dé bá wọn, títí wọ́n máa fi rí ìyà ẹlẹ́ta-eléro.1 ');
INSERT INTO "aykyuni" VALUES(10,98,'10:98','Kí ó sì jẹ́ pé ìlú kàn gbàgbọ́ (lásìkò ìsọ̀kalẹ̀ ìyà), kí ìgbàgbọ́ rẹ̀ sì ṣe é ní àǹfààní (kò kúkú sí) àfi ìjọ (Ànábì) Yūnus (nítorí pé), nígbà tí wọ́n gbàgbọ́, A mú àbùkù ìyà kúrò fún wọn nínú ìṣẹ̀mí ayé. A sì jẹ́ kí wọ́n jẹ ìgbádùn ayé fún ìgbà díẹ̀.');
INSERT INTO "aykyuni" VALUES(10,99,'10:99','Tí ó bá jẹ́ pé Olúwa rẹ bá fẹ́, gbogbo àwọn tí ń bẹ lórí ilẹ̀ pátápátá ìbá gbàgbọ́. Nítorí náà, ṣé ìwọ lo máa jẹ àwọn ènìyàn nípá títí wọn yóò fi di onígbàgbọ́ òdodo ni?');
INSERT INTO "aykyuni" VALUES(10,100,'10:100','Ẹ̀mí kan kò lè gbàgbọ́ àfi pẹ̀lú ìyọ̀ǹda Allāhu. Ó sì máa fi ìyà jẹ àwọn tí kò ṣe làákàyè.1 ');
INSERT INTO "aykyuni" VALUES(10,101,'10:101','Sọ pé: “Ẹ wòye sí n̄ǹkan tí ń bẹ nínú àwọn sánmọ̀ àti ilẹ̀. Àwọn āyah àti ìkìlọ̀ kò sì níí rọ ìjọ aláìgbàgbọ́ lọ́rọ̀.”');
INSERT INTO "aykyuni" VALUES(10,102,'10:102','Nítorí náà, ṣé wọ́n tún ń retí (n̄ǹkan mìíràn) ni bí kò ṣe (ìparun) irú ti ọjọ́ àwọn tó ré kọjá lọ ṣíwájú wọn. Sọ pé: “Ẹ máa retí nígbà náà. Dájúdájú èmi náà wà pẹ̀lú yín nínú àwọn olùretí.”');
INSERT INTO "aykyuni" VALUES(10,103,'10:103','Lẹ́yìn náà, A óò gba àwọn Òjíṣẹ́ Wa àti àwọn tó gbàgbọ́ lódodo là. Báyẹn ní ó ṣe jẹ́ ojúṣe Wa láti gba àwọn onígbàgbọ́ òdodo là.');
INSERT INTO "aykyuni" VALUES(10,104,'10:104','Sọ pé: “Ẹ̀yin ènìyàn, tí ẹ bá wà nínú iyèméjì nípa ẹ̀sìn mi. Nígbà náà, èmi kò níí jọ́sìn fún àwọn tí ẹ̀ ń jọ́sìn fún lẹ́yìn Allāhu. Ṣùgbọ́n èmi yóò máa jọ́sìn fún Allāhu, Ẹni tí Ó máa gba ẹ̀mí yín. Wọ́n sì pa mí ní àṣẹ pé kí n̄g wà nínú àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(10,105,'10:105','(Wọ́n tún pa mí láṣẹ pé): “Dojú rẹ kọ ẹ̀sìn náà, (kí o jẹ́) olùdúró-déédé-nínú-ẹ̀sìn.Ìwọ kò sì gbọdọ̀ wà nínú àwọn ọ̀ṣẹbọ. ');
INSERT INTO "aykyuni" VALUES(10,106,'10:106','Àti pé má ṣe pè lẹ́yìn Allāhu n̄ǹkan tí kò lè ṣe ọ́ ní àǹfààní, tí kò sì lè kó ìnira bá ọ. Tí o bá ṣe bẹ́ẹ̀ nígbà náà, dájúdájú ìwọ ti wà nínú àwọn alábòsí.”');
INSERT INTO "aykyuni" VALUES(10,107,'10:107','Tí Allāhu bá mú ìnira bá ọ, kò sí ẹni tí ó lè mú un kúrò àfi Òun. Tí Ó bá sì gbèrò oore kan pẹ̀lú rẹ, kò sí ẹni tí ó lè dá oore Rẹ̀ padà. Ó ń ṣoore fún ẹni tí Ó bá fẹ́ nínú àwọn ẹrúsìn Rẹ̀. Òun ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(10,108,'10:108','Sọ pé: “Ẹ̀yin ènìyàn, dájúdájú òdodo ti dé ba yín láti ọ̀dọ̀ Olúwa yín. Nítorí náà, ẹnikẹ́ni tí ó bá mọ̀nà, ó mọ̀nà fún ẹ̀mí ara rẹ̀. Ẹnikẹ́ni tí ó bá sì ṣìnà, ó ṣìnà fún ẹ̀mí ara rẹ̀. Èmi sì kọ́ ni olùṣọ́ fún yín.”');
INSERT INTO "aykyuni" VALUES(10,109,'10:109','Tẹ̀lé ohun tí A fi ránṣẹ́ sí ọ nínú ìmísí. Kí o sì ṣe sùúrù títí Allāhu yóò fi ṣèdájọ́. Òun sì lóore julọ nínú àwọn olùdájọ́.');
INSERT INTO "aykyuni" VALUES(11,1,'11:1','’Alif lām rọ̄. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)(Èyí ni) Tírà tí wọ́n ti to àwọn āyah inú rẹ̀ ní àtògún régé, lẹ́yìn náà wọ́n ṣàlàyé rẹ̀ yékéyéké láti ọ̀dọ̀ Ọlọ́gbọ́n, Alámọ̀tán. ');
INSERT INTO "aykyuni" VALUES(11,2,'11:2','(A ti ṣàlàyé rẹ̀) pé ẹ má jọ́sìn fún kiní kan àfi Allāhu. Dájúdájú èmi ni olùkìlọ̀ àti oníròó ìdùnnú fún yín láti ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(11,3,'11:3','(A ti ṣàlàyé rẹ̀) pé kí ẹ tọrọ àforíjìn lọ́dọ̀ Olúwa yín. Lẹ́yìn náà, kí ẹ ronú pìwàdà sọ́dọ̀ Rẹ̀. Ó máa fún yín ní ìgbádùn dáadáa títí di gbèdéke àkókò kan. Ó máa fún oníṣẹ́-àṣegbọrẹ ní ẹ̀san iṣẹ́ àṣegbọrẹ rẹ̀. Tí ẹ bá sì pẹ̀yìndà, dájúdájú èmi ń páyà ìyà Ọjọ́ ńlá fún yín.');
INSERT INTO "aykyuni" VALUES(11,4,'11:4','Ọ̀dọ̀ Allāhu ni ibùpadàsí yín. Òun sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(11,5,'11:5','Gbọ́, dájúdájú àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí ń ká (iyèméjì àti ọ̀tá) sínú igbá-àyà wọn láti lè fara pamọ́ fún Allāhu. Kíyè sí i, nígbà tí wọ́n ń yíṣọ wọn bora, Allāhu mọ n̄ǹkan tí wọ́n ń fi pamọ́ àti n̄ǹkan tí wọ́n ń ṣàfi hàn rẹ̀. Dájúdájú Òun ni Onímọ̀ nípa n̄ǹkan tí ń bẹ nínú igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(11,6,'11:6','Kò sí ẹ̀dá abẹ̀mí kan tó wà lórí ilẹ̀ àfi kí arísìkí rẹ̀ wà lọ́dọ̀ Allāhu. Ó sì mọ ibùgbé rẹ̀ (nílé ayé) àti ilẹ̀ tí ó máa kú sí. Gbogbo rẹ̀ ti wà nínú àkọsílẹ̀ tó yanjú.');
INSERT INTO "aykyuni" VALUES(11,7,'11:7','Òun ni Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ fún ọjọ́ mẹ́fà - Ìtẹ́ ọlá Rẹ̀ sì wà lórí omi (ṣíwájú èyí) - nítorí kí Ó lè dan yín wò pé, èwo nínú yín ló dára jùlọ (níbi) iṣẹ́ rere. Tí o bá kúkú sọ pé dájúdájú wọn yóò gbe yín dìde lẹ́yìn ikú, dájúdájú àwọn tó ṣàì gbàgbọ́ yóò wí pé: “Èyí kò jẹ́ kiní kan bí kò ṣe idán pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(11,8,'11:8','Dájúdájú tí A bá sún ìyà ṣíwájú fún wọn di ìgbà tó ní òǹkà, dájúdájú wọn yóò wí pé: “Kí ló ń dá a dúró ná?” Gbọ́, ní ọjọ́ tí ìyà yóò dé bá wọn, kò níí ṣe é gbé kúrò fún wọn. Àti pé ohun tí wọ́n ń fi ṣe yẹ̀yẹ́ sì máa dìyà tó máa yí wọn po.');
INSERT INTO "aykyuni" VALUES(11,9,'11:9','Àti pé dájúdájú tí A bá fi ìkẹ́ kan tọ́ ènìyàn lẹ́nu wò láti ọ̀dọ̀ Wa, lẹ́yìn náà, tí A bá gbà á kúrò lọ́wọ́ rẹ̀, dájúdájú ó máa di olùsọ̀rètínù, aláìmoore.');
INSERT INTO "aykyuni" VALUES(11,10,'11:10','Dájúdájú tí A bá sì fún un ní ìdẹ̀ra tọ́wò lẹ́yìn ìnira tó fọwọ́ bà á, dájúdájú ó máa wí pé: “Àwọn aburú ti kúrò lọ́dọ̀ mi.” Dájúdájú ó máa di aláyọ̀pọ̀rọ́, onífáàrí.');
INSERT INTO "aykyuni" VALUES(11,11,'11:11','Àyàfi àwọn tó bá ṣe sùúrù, tí wọ́n sì ṣe àwọn iṣẹ́ rere. Àwọn wọ̀nyẹn, ti wọn ni àforíjìn àti ẹ̀san tó tóbi.');
INSERT INTO "aykyuni" VALUES(11,12,'11:12','Bóyá o fẹ́ gbé apá kan ohun tí A fi ránṣẹ́ sí ọ sílẹ̀, kí ìrẹ̀wẹ̀sì sì bá ọkàn rẹ nípa rẹ̀ (ní ti ìpáyà pé) wọ́n ń wí pé: “Kí ó sì jẹ́ pé àpótí-ọrọ̀ kan sọ̀kalẹ̀ fún un, tàbí kí mọlāika kan bá a wá?” Olùkìlọ̀ ni ìwọ. Allāhu sì ni Olùṣọ́ lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(11,13,'11:13','Tàbí wọ́n ń wí pé: “Ó hun ún ni.” Sọ pé: “Ẹ mú sūrah mẹ́wàá àdáhun bí irú rẹ̀ wá, kí ẹ sì ké sí ẹni tí ẹ bá lè ké sí lẹ́yìn Allāhu tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(11,14,'11:14','Nígbà náà tí wọn kò bá da yín lóhùn, kí ẹ mọ̀ pé dájúdájú wọ́n sọ (al-Ƙur’ān) kalẹ̀ pẹ̀lú ìmọ̀ Allāhu. Àti pé kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Nítorí náà, ṣé ẹ̀yin yóò di mùsùlùmí?');
INSERT INTO "aykyuni" VALUES(11,15,'11:15','Ẹnikẹ́ni tí ó bá ń fẹ́ ìṣẹ̀mí ayé àti ọ̀ṣọ́ rẹ̀, A óò san wọ́n ní ẹ̀san iṣẹ́ wọn nílé ayé. A ò sì níí dín kiní kan kù fún wọn nílé ayé.');
INSERT INTO "aykyuni" VALUES(11,16,'11:16','Àwọn wọ̀nyẹn ni àwọn tí kò níí sí kiní kan fún wọn mọ́ ní Ọjọ́ Ìkẹ́yìn àfi Iná. N̄ǹkan tí wọ́n gbélé ayé ṣe sì máa bàjẹ́. Òfò sì ni ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(11,17,'11:17','Ǹjẹ́ ẹni tó ń bẹ́ lórí ẹ̀rí tó yanjú láti ọ̀dọ̀ Olúwa rẹ̀ (ìyẹn, Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -), tí olùjẹ́rìí kan (ìyẹn, mọlāika Jibrīl) sì ń ké al-Ƙur’ān (fún un) láti ọ̀dọ̀ Allāhu, tí tírà (Ànábì) Mūsā sì ti wá ṣíwájú rẹ̀, tí ó jẹ́ tírà tí wọ́n ń tẹ̀lé, tí ó sì jẹ́ ìkẹ́ (fún wọn), (ǹjẹ́ Ànábì yìí - kí ìkẹ́ àti ọlà Allāhu máa bá a - jọ olùṣìnà bí?) Àwọn (mùsùlùmí) wọ̀nyí ni wọ́n sì gbà á gbọ́ ní òdodo. Ẹnikẹ́ni tó bá ṣàì gbà á gbọ́ nínú àwọn ìjọ (nasọ̄rọ̄, yẹhudi àti ọ̀ṣẹbọ), nígbà náà Iná ni àdéhùn rẹ̀. Nítorí náà, má ṣe wà nínú iyèméjì nípa al-Ƙur’ān. Dájúdájú òdodo ni láti ọ̀dọ̀ Olúwa rẹ, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ ènìyàn ni kò gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(11,18,'11:18','Àti pé ta ni ó ṣàbòsí ju ẹni tó dá àdápa irọ́ mọ́ Allāhu? Àwọn wọ̀nyẹn ni wọ́n yóò kó wá síwájú Olúwa wọn. Àwọn ẹlẹ́rìí (ìyẹn, àwọn mọlāika) yó sì máa sọ pé: “Àwọn wọ̀nyí ni àwọn tó parọ́ mọ́ Olúwa wọn.” Gbọ́, ibi dandan Allāhu kí ó máa bá àwọn alábòsí,');
INSERT INTO "aykyuni" VALUES(11,19,'11:19','àwọn tó ń ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu, tí wọ́n sì ń fẹ́ kó wọ́. Àwọn sì ni aláìgbàgbọ́ nínú Ọjọ́ Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(11,20,'11:20','Àwọn wọ̀nyẹn kò lè mórí bọ́ nínú ìyà Allāhu lórí ilẹ̀ àti pé kò sí aláàbò kan fún wọn lẹ́yìn Allāhu. A sì máa ṣe àdìpèlé ìyà fún wọn. Wọn kò lè gbọ́. Àti pé wọn kì í ríran (rí òdodo).');
INSERT INTO "aykyuni" VALUES(11,21,'11:21','Àwọn wọ̀nyẹn ni àwọn tó ṣe ẹ̀mí ara wọn lófò. Ohun tí wọ́n ń dá ní àdápa irọ́ sì di òfo mọ́ wọn lọ́wọ́.1 ');
INSERT INTO "aykyuni" VALUES(11,22,'11:22','Ní ti òdodo, dájúdájú àwọn gan-an ni ẹni òfò jùlọ ní Ọjọ́ Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(11,23,'11:23','Dájúdájú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n ṣe àwọn iṣẹ́ rere, tí wọ́n sì dúnní mọ́ ìronúpìwàdà sọ́dọ̀ Olúwa wọn, àwọn wọ̀nyẹn ni èrò inú Ọgbà Ìdẹ̀ra. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(11,24,'11:24','Àpèjúwe ìjọ méjèèjì dà bí afọ́jú àti adití pẹ̀lú olùríran àti olùgbọ́rọ̀. Ǹjẹ́ àwọn méjèèjì dọ́gba ní àpèjúwe bí? Ṣé ẹ ò níí lo ìrántí ni?');
INSERT INTO "aykyuni" VALUES(11,25,'11:25','A sì kúkú ti rán (Ànábì) Nūh sí ìjọ rẹ̀ (láti sọ pé) dájúdájú olùkìlọ̀ pọ́nńbélé ni mo jẹ́ fún yín.');
INSERT INTO "aykyuni" VALUES(11,26,'11:26','(Mò ń kìlọ̀) pé kí ẹ má ṣe jọ́sìn fún ẹnì kan àfi Allāhu. Dájúdájú èmi ń páyà ìyà ọjọ́ ẹlẹ́ta-eléro fún yín.');
INSERT INTO "aykyuni" VALUES(11,27,'11:27','Àwọn aṣíwájú tó ṣàì gbàgbọ́ nínú ìjọ rẹ̀ sì wí pé: “Àwa kò mọ̀ ọ́ sí ẹnì kan bí kò ṣe abara bí irú wa. Àwa kò sì mọ àwọn tó tẹ̀lé ọ sí ẹnì kan kan bí kò ṣe àwọn ẹni yẹpẹrẹ nínú wa, ọlọ́pọlọ bín-íntín. Àti pé àwa kò ri yín sí ẹni tó fi ọ̀nà kan kan ní àjùlọ lórí wa, ṣùgbọ́n a kà yín kún òpùrọ́.”');
INSERT INTO "aykyuni" VALUES(11,28,'11:28','(Ànábì Nūh) sọ pé: “Ẹ̀yin ìjọ mi, ẹ sọ fún mi, tí mo bá wà lórí ẹ̀rí tó yanjú láti ọ̀dọ̀ Olúwa mi, tí Ó sì fún mi ní ìkẹ́ kan láti ọ̀dọ̀ Rẹ̀, tí wọ́n sì dì yín lójú pa sí i (tí ẹ kò ríran rí òdodo náà), ǹjẹ́ a óò fi dandan mu yín bí, nígbà tí ẹ kórira rẹ̀?');
INSERT INTO "aykyuni" VALUES(11,29,'11:29','Ẹ̀yin ìjọ mi, èmi kò bi yín léèrè dúkìá kan lórí rẹ̀. Kò sí ẹ̀san mi níbì kan bí kò ṣe lọ́dọ̀ Allāhu. Èmi kò sì níí le àwọn tó gbàgbọ́ lódodo dànù. Dájúdájú wọn yóò pàdé Olúwa wọn, ṣùgbọ́n dájúdájú èmi ń ri yín sí ìjọ aláìmọ̀kan.');
INSERT INTO "aykyuni" VALUES(11,30,'11:30','Ẹ̀yin ìjọ mi, ta ni ó máa ràn mí lọ́wọ́ níbi (ìyà) Allāhu tí mo bá lé wọn dànù? Ṣé ẹ ò níí lo ìrántí ni?');
INSERT INTO "aykyuni" VALUES(11,31,'11:31','Èmi kò sì sọ fún yín pé àwọn ilé-ọrọ̀ Allāhu ń bẹ lọ́dọ̀ mi. Èmi kò sì mọ ìkọ̀kọ̀. Èmi kò sì sọ pé mọlāika ni mí. Èmi kò sì lè sọ fún àwọn tí ẹ̀ ń fojú bín-íntín wò pé, Allāhu kò níí ṣoore fún wọn. Allāhu l''Ó nímọ̀ jùlọ nípa n̄ǹkan tó ń bẹ nínú ẹ̀mí wọn. Bí bẹ́ẹ̀ kọ́ nígbà náà, dájúdájú mo ti wà nínú àwọn alábòsí.”');
INSERT INTO "aykyuni" VALUES(11,32,'11:32','Wọ́n wí pé: “Nūh, o mà kúkú ti jà wá níyàn. O sì ṣe àríyànjiyàn púpọ̀ pẹ̀lú wa. Nítorí náà, mú ohun tí o ṣe ní ìlérí fún wa wá tí o bá wà lára àwọn olódodo.”');
INSERT INTO "aykyuni" VALUES(11,33,'11:33','(Ànábì Nūh) sọ pé: “Allāhu l’Ó máa mú un wá ba yín tí Ó bá fẹ́. Ẹ̀yin kò sì níí mórí bọ́.');
INSERT INTO "aykyuni" VALUES(11,34,'11:34','Ìmọ̀ràn mi kò sì lè ṣe yín ní àǹfààní tí mo bá fẹ́ gbà yín nìmọ̀ràn, tí Allāhu bá fẹ́ pa yín run. Òun ni Olúwa yín. Ọ̀dọ̀ Rẹ̀ sì ni wọn yóò da yín padà sí.”');
INSERT INTO "aykyuni" VALUES(11,35,'11:35','Tàbí wọ́n ń wí pé: “Ó hun ún ni.” Sọ pé: “Tí mó bá hun ún, èmi ni mo ni ẹ̀ṣẹ̀ mi. Èmi sì yọwọ́ yọsẹ̀ nínú ohun tí ẹ̀ ń dá lẹ́ṣẹ̀.”');
INSERT INTO "aykyuni" VALUES(11,36,'11:36','A sì fi ìmísí ránṣẹ́ sí (Ànábì) Nūh pé, dájúdájú kò sí ẹnì kan tí ó máa gbàgbọ́ mọ́ nínú ìjọ rẹ àfi ẹni tó ti gbàgbọ́ tẹ́lẹ̀. Nítorí náà, má ṣe banújẹ́ nípa n̄ǹkan tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(11,37,'11:37','Kí o sì kan ọkọ̀ ojú-omi náà lójú Wa (báyìí) pẹ̀lú ìmísí Wa. Má sì ṣe bá Mi sọ̀rọ̀ nípa àwọn tó ṣàbòsí. Dájúdájú wọ́n máa tẹ̀ wọ́n rì ni.');
INSERT INTO "aykyuni" VALUES(11,38,'11:38','Ó sì ń kan ọkọ̀ ojú-omi náà lọ. Ìgbàkígbà tí àwọn ọ̀tọ̀kùlú nínú ìjọ rẹ̀ bá kọjá lọ́dọ̀ rẹ̀, wọ́n yó sì máa fi ṣe yẹ̀yẹ́. (Ànábì Nūh) sì sọ pé: “Tí ẹ bá fi wá ṣe yẹ̀yẹ́, dájúdájú àwa náà yóò fi yín ṣe yẹ̀yẹ́ gẹ́gẹ́ bí ẹ ṣe ń fi wá ṣe yẹ̀yẹ́.1 ');
INSERT INTO "aykyuni" VALUES(11,39,'11:39','Nítorí náà, láìpẹ́ ẹ máa mọ ẹni tí ìyà máa dé bá, tí ó máa yẹpẹrẹ rẹ̀, tí ìyà gbere sì máa kò lé lórí.”');
INSERT INTO "aykyuni" VALUES(11,40,'11:40','(Báyìí ni ọ̀rọ̀ wà) títí di ìgbà tí àṣẹ Wa dé, omi sì ṣẹ́yọ gbùú láti ojú-àrò (tí wọ́n ti ń ṣe búrẹ́dì). A sọ pé: “Kó gbogbo n̄ǹkan ní takọ-tabo méjìméjì àti ará ilé rẹ sínú ọkọ̀ ojú-omi - àyàfi ẹni tí ọ̀rọ̀ náà ti kò lé lórí (fún ìparun) - àti ẹnikẹ́ni tó gbàgbọ́ ní òdodo. Kò sì sí ẹni tó ní ìgbàgbọ́ òdodo pẹ̀lú rẹ̀ àfi àwọn díẹ̀.');
INSERT INTO "aykyuni" VALUES(11,41,'11:41','(Ànábì Nūh) sọ pé: “Ẹ wọ inú ọkọ̀ ojú-omi náà. Ní orúkọ Allāhu ni ìrìn rẹ̀ àti ìgúnlẹ̀ rẹ̀. Dájúdájú Olúwa mi ni Aláforíjìn, Àṣàkẹ́-ọ̀run.”');
INSERT INTO "aykyuni" VALUES(11,42,'11:42','Ó sì ń gbé wọn lọ nínú ìgbì omi tó dà bí àpáta. (Ànábì Nūh) sì ké sí ọmọkùnrin rẹ̀, tí ó ti yara rẹ̀ sọ́tọ̀: “Ọmọkùnrin mi, gun ọkọ̀ ojú-omi pẹ̀lú wa. Má sì ṣe wà pẹ̀lú àwọn aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(11,43,'11:43','Ó wí pé: “Èmi yóò fara pamọ́ síbi àpáta kan tí ó máa gbà mí là níbi omi.” (Ànábì Nūh) sọ pé: “Kò sí olùgbàlà kan (kò sí ẹni tí wọ́n máa gbàlà) ní òní níbi àṣẹ Allāhu (ẹ̀kún omi) àfi ẹni tí Allāhu bá ṣàánú.” Ìgbì omi sì la àwọn méjèèjì láààrin. Ó sì di ara àwọn olùtẹ̀rì sínú omi.');
INSERT INTO "aykyuni" VALUES(11,44,'11:44','A sọ pé: “Ilẹ̀, fa omi rẹ mu. Sánmọ̀, dáwọ́ (òjò) dúró.” Wọ́n sì dín omi kù. Àṣẹ (ìparun wọn) sì wá sí ìmúṣẹ. (Ọkọ̀ ojú-omi) sì gúnlẹ̀ sórí àpáta Jūdī. Wọ́n sì sọ pé: “Kí ìjìnnà sí ìkẹ́ Allāhu máa jẹ́ ti ìjọ alábòsí.”');
INSERT INTO "aykyuni" VALUES(11,45,'11:45','(Ànábì) Nūh pe Olúwa rẹ̀, ó sì sọ pé: “Olúwa mi, dájúdájú ọmọ mi wà nínú ará ilé mi. Àti pé dájúdájú àdéhùn Rẹ, òdodo ni. Ìwọ l’O sì mọ ẹjọ́ dá jùlọ nínú àwọn adájọ́.');
INSERT INTO "aykyuni" VALUES(11,46,'11:46','Allāhu sọ pé: “Nūh, dájúdájú kò sí nínú ará ilé rẹ (ní ti ẹ̀sìn). Dájúdájú iṣẹ́ tí kò dára ni.Nítorí náà, o ò gbọdọ̀ bi Mí léèrè n̄ǹkan tí o ò ní ìmọ̀ nípa rẹ̀. Dájúdájú Èmi ń kìlọ̀ fún ọ nítorí kí o má baà di ara àwọn aláìmọ̀kan.” ');
INSERT INTO "aykyuni" VALUES(11,47,'11:47','Ó sọ pé: “Olúwa mi, dájúdájú èmi ń sá di Ọ́ níbi kí n̄g bi Ọ́ léèrè n̄ǹkan tí èmi kò ní ìmọ̀ rẹ̀. Tí O ò bá foríjìn mí, kí O sì ṣàánú mi, èmi yóò wà lára àwọn ẹni òfò.”');
INSERT INTO "aykyuni" VALUES(11,48,'11:48','A sọ pé: “Nūh, sọ̀kalẹ̀ pẹ̀lú àlàáfíà láti ọ̀dọ̀ Wa. Àti pé kí ìbùkún wà pẹ̀lú rẹ àti àwọn ìjọ nínú àwọn tó ń bẹ pẹ̀lú rẹ. Àwọn ìjọ kan (tún ń bọ̀), tí A óò fún wọn ní ìgbádùn (oore ayé). Lẹ́yìn náà, ìyà ẹlẹ́ta-eléro yó sì fọwọ́ bà wọ́n láti ọ̀dọ̀ Wa.”');
INSERT INTO "aykyuni" VALUES(11,49,'11:49','Ìwọ̀nyí wà nínú ìró ìkọ̀kọ̀ tí À ń fi (ìmísí rẹ̀) ránṣẹ́ sí ọ. Ìwọ àti ìjọ rẹ kò nímọ̀ rẹ̀ ṣíwájú èyí (tí A sọ̀kalẹ̀ fún ọ). Nítorí náà, ṣe sùúrù. Dájúdájú ìkángun rere wà fún àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(11,50,'11:50','(Ẹni tí A rán níṣẹ́) sí ìran ‘Ād ni arákùnrin wọn, (Ànábì) Hūd. Ó sọ pé: “Ẹ̀yin ìjọ mi, ẹ jọ́sìn fún Allāhu. Ẹ kò ní ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo lẹ́yìn Rẹ̀. Ẹ̀yin kò sì jẹ́ kiní kan bí kò ṣe aládàápa irọ́.');
INSERT INTO "aykyuni" VALUES(11,51,'11:51','Ẹ̀yin ìjọ mi, èmi kò bi yín léèrè owó-ọ̀yà kan lórí rẹ̀. Kò sí ẹ̀san mi níbì kan bí kò ṣe lọ́dọ̀ Ẹni tí Ó pilẹ̀ ìṣẹ̀dá mi. Ṣé ẹ ò níí ṣe làákàyè ni?');
INSERT INTO "aykyuni" VALUES(11,52,'11:52','Ẹ̀yin ìjọ mi, ẹ tọrọ àforíjìn lọ́dọ̀ Olùwa Ẹlẹ́dàá yín, lẹ́yìn náà, kí ẹ ronú pìwàdà sọ́dọ̀ Rẹ̀ nítorí kí Ó lè rọ̀jò fún yín ní púpọ̀ láti sánmọ̀ àti nítorí kí Ó lè ṣe àlékún agbára kún agbára yín. Ẹ má ṣe pẹ̀yìn dà láti di ẹlẹ́ṣẹ̀ (sínú àìgbàgbọ́).”');
INSERT INTO "aykyuni" VALUES(11,53,'11:53','Wọ́n wí pé: “Hūd, o ò mú ẹ̀rí kan tó yanjú wá fún wa. Àwa kò sì níí gbé àwọn òrìṣà wa jù sílẹ̀ nítorí ọ̀rọ̀ (ẹnu) rẹ. Àti pé àwa kò ní ìgbàgbọ́ nínú rẹ.');
INSERT INTO "aykyuni" VALUES(11,54,'11:54','A kò níí sọ n̄ǹkan kan (sí ọ) bí kò ṣe pé, àwọn kan nínú àwọn òrìṣà wa ti fi ìnira kàn ọ́ (ló fi ń sọ ìsọkúsọ nípa wọn).” Ó sọ pé: “Dájúdájú èmi ń fi Allāhu ṣe Ẹlẹ́rìí àti pé kí ẹ̀yin náà jẹ́rìí pé dájúdájú èmi yọwọ́ yọsẹ̀ nínú ohun tí ẹ̀ ń fi ṣẹbọ');
INSERT INTO "aykyuni" VALUES(11,55,'11:55','dípò jíjọ́sìn fún Allāhu. Nítorí náà, ẹ parapọ̀ déte sí mi. Lẹ́yìn náà, kí ẹ má ṣe lọ́ mi lára.');
INSERT INTO "aykyuni" VALUES(11,56,'11:56','Dájúdájú èmi gbáralé Allāhu, Olúwa mi àti Olúwa yín. Kò sí ẹ̀dá abẹ̀mí kan àfi kí (ó jẹ́ pé) Òun l’Ó máa fi àásó orí rẹ̀ mú un. Dájúdájú Olúwa mi wà lórí ọ̀nà tààrà.');
INSERT INTO "aykyuni" VALUES(11,57,'11:57','Nítorí náà, tí ẹ bá pẹ̀yìn dà (níbi òdodo), mo kúkú ti fi ohun tí Wọ́n fi rán mi níṣẹ́ jíṣẹ́ fún yín, Olúwa mi yó sì fi ìjọ kan tó máa yàtọ̀ sí ẹ̀yin rọ́pò yín. Ẹ kò sì lè kó ìnira kan kan bá A. Dájúdájú Olúwa mi ni Olùṣọ́ lórí gbogbo n̄ǹkan.”');
INSERT INTO "aykyuni" VALUES(11,58,'11:58','Nígbà tí àṣẹ Wa dé, A gba (Ànábì) Hūd àti àwọn tó gbàgbọ́ pẹ̀lú rẹ̀ là pẹ̀lú ìkẹ́ láti ọ̀dọ̀ Wa; A gbà wọ́n là nínú ìyà tó nípọn.');
INSERT INTO "aykyuni" VALUES(11,59,'11:59','Ìran ‘Ād nìyẹn; wọ́n tako àwọn āyah Olúwa wọn. Wọ́n sì yapa àwọn Òjíṣẹ́ wọn. Wọ́n sì tẹ̀lé àṣẹ gbogbo àwọn aláfojúdi alátakò-òdodo.');
INSERT INTO "aykyuni" VALUES(11,60,'11:60','A fi ègún tẹ̀lé wọn nílé ayé yìí àti ní Ọjọ́ Àjíǹde. Gbọ́, dájúdájú ìran ‘Ād ṣàì gbàgbọ́ nínú Olúwa wọn. Kíyè sí i, kí ìjìnnà sí ìkẹ́ máa jẹ́ ti ìran ‘Ād, ìjọ (Ànábì) Hūd.');
INSERT INTO "aykyuni" VALUES(11,61,'11:61','(Ẹni tí A rán níṣẹ́) sí ìran Thamūd ni arákùnrin wọn, (Ànábì) Sọ̄lih. Ó sọ pé: “Ẹ̀yin ìjọ mi, ẹ jọ́sìn fún Allāhu. Ẹ kò ní ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo lẹ́yìn Rẹ̀. Òun l’Ó pílẹ̀ ìṣẹ̀dá yín láti ara (erùpẹ̀) ilẹ̀. Ó sì fún yín ní ìṣẹ̀mí lò lórí rẹ̀. Nítorí náà, ẹ tọrọ àforíjìn lọ́dọ̀ Rẹ̀. Lẹ́yìn náà, ẹ ronú pìwàdà sọ́dọ̀ Rẹ̀. Dájúdájú Olúwa mi ni Olùsúnmọ́, Olùjẹ́pè (ẹ̀dá).”');
INSERT INTO "aykyuni" VALUES(11,62,'11:62','Wọ́n wí pé: “Sọ̄lih, o ti wà láààrin wa ní ẹni tí a ní ìrètí sí (pé o máa sanjọ́) ṣíwájú (ọ̀rọ̀ tí o sọ) yìí. Ṣé o máa kọ̀ fún wa láti jọ́sìn fún ohun tí àwọn bàbá wa ń jọ́sìn fún ni? Dájúdájú àwa mà wà nínú iyèméjì tó gbópọn nípa n̄ǹkan tí ò ń pè wá sí.”');
INSERT INTO "aykyuni" VALUES(11,63,'11:63','Ó sọ pé: “Ẹ̀yin ìjọ mi, ẹ sọ fún mi, tí ó bá jẹ́ pé èmi wà lórí ẹ̀rí tó yanjú láti ọ̀dọ̀ Olúwa mi, tí ìkẹ́ kan sì dé bá mi láti ọ̀dọ̀ Rẹ̀, ta ni ẹni tí ó máa ràn mí lọ́wọ́ nínú (ìyà) Allāhu tí mo bá yapa Rẹ̀? Kò sí kiní kan tí ẹ lè fi ṣe àlékún rẹ̀ fún mi yàtọ̀ sí ìparun.');
INSERT INTO "aykyuni" VALUES(11,64,'11:64','Ẹ̀yin ìjọ mi, èyí ni abo ràkúnmí Allāhu. Ó jẹ́ àmì fún yín. Nítorí náà, ẹ fi sílẹ̀, kí ó máa jẹ́ kiri lórí ilẹ̀ Allāhu. Ẹ má ṣe fi ìnira kàn án nítorí kí ìyà tó súnmọ́ má báa jẹ yín.”');
INSERT INTO "aykyuni" VALUES(11,65,'11:65','Wọ́n sì gún un pa. Ó sọ pé: “Ẹ jẹ̀gbádùn nínú ilé yín fún ọjọ́ mẹ́ta (kí ìyà yín tó dé). Ìyẹn ni àdéhùn tí kì í ṣe irọ́.”');
INSERT INTO "aykyuni" VALUES(11,66,'11:66','Nígbà tí àṣẹ Wa dé, A gba (Ànábì) Sọ̄lih àti àwọn tó gbàgbọ́ pẹ̀lú rẹ̀ là pẹ̀lú ìkẹ́ láti ọ̀dọ̀ Wa. (A tún gbà wọ́n là) nínú àbùkù ọjọ́ yẹn. Dájúdájú Olúwa Rẹ, Òun ni Alágbára, Olùborí (ẹ̀dá).');
INSERT INTO "aykyuni" VALUES(11,67,'11:67','Ohùn igbe líle gbá àwọn tó ṣàbòsí mú. Wọ́n sì dà lulẹ̀, tí wọ́n ti dòkú sínú ìlú wọn.');
INSERT INTO "aykyuni" VALUES(11,68,'11:68','Àfi bí ẹni pé wọn kò gbénú ìlú náà rí.Gbọ́, dájúdájú ìran Thamūd ṣàì gbàgbọ́ nínú Olúwa wọn. Kíyè sí i, kí ìjìnnà sí ìkẹ́ máa jẹ́ ti ìjọ Thamūd. ');
INSERT INTO "aykyuni" VALUES(11,69,'11:69','Dájúdájú àwọn Òjíṣẹ́ Wa ti mú ìró ìdùnnú wá bá (Ànábì) ’Ibrọ̄hīm. Wọ́n sọ pé: “Àlàáfíà (fún ọ).” Ó sọ pé: “Àlàáfíà (fún yín).” Kò sì pẹ́ rárá tó fi gbé ọmọ màálù àyangbẹ wá.');
INSERT INTO "aykyuni" VALUES(11,70,'11:70','Nígbà tí ó rí ọwọ́ wọn pé kò kan oúnjẹ náà, ó pajú dà sí wọn (ó f’ojú òdì wo àìjẹun wọn). Ìbẹ̀rù wọn sì mú un.Wọ́n sọ pé: “Má ṣe bẹ̀rù. Dájúdájú àwọn ènìyàn (Ànábì) Lūt ni Wọ́n rán àwa sí.” ');
INSERT INTO "aykyuni" VALUES(11,71,'11:71','Ìyàwó rẹ̀ wà ní ìdúró, ó rẹ́rìn-ín. A sì fún un ní ìró ìdùnnú (pé ó máa bí) ’Ishāƙ. Àti pé lẹ́yìn ’Ishāƙ ni Ya‘ƙūb (ìyẹn, ọmọọmọ).1 ');
INSERT INTO "aykyuni" VALUES(11,72,'11:72','Ó sọ pé: “Hẹ̀n-ẹ́n! Ṣé pé mo máa bímọ, arúgbóbìnrin ni mí, baálé mi yìí sì ti dàgbàlágbà! Dájúdájú èyí ni n̄ǹkan ìyanu.”');
INSERT INTO "aykyuni" VALUES(11,73,'11:73','Wọ́n sọ pé: “Ṣé o máa ṣèèmọ̀ nípa àṣẹ Allāhu ni? Ìkẹ́ Allāhu àti ìbùkún Rẹ̀ kí ó máa bẹ fún yín, ẹ̀yin ará ilé (yìí). Dájúdájú Allāhu ni Ẹlẹ́yìn, Ológo.”');
INSERT INTO "aykyuni" VALUES(11,74,'11:74','Nígbà tí ìbẹ̀rù kúrò lára (Ànábì) ’Ibrọ̄hīm, tí ìró ìdùnnú sì ti dé bá a ló bá ń pàrọwà fún (àwọn Òjíṣẹ́) Wa nípa ìjọ (Ànábì) Lūt.');
INSERT INTO "aykyuni" VALUES(11,75,'11:75','Dájúdájú (Ànábì) ’Ibrọ̄hīm ni aláfaradà, olùrawọ́rasẹ̀, olùronúpìwàdà.');
INSERT INTO "aykyuni" VALUES(11,76,'11:76','(Ànábì) ’Ibrọ̄hīm, ṣẹ́rí kúrò níbi èyí. Dájúdájú àṣẹ Olúwa rẹ ti dé. Àti pé dájúdájú àwọn (wọ̀nyẹn) ni ìyà tí kò ṣe é dá padà yóò dé bá.');
INSERT INTO "aykyuni" VALUES(11,77,'11:77','Nígbà tí àwọn Òjíṣẹ́ Wa dé bá (Ànábì) Lūt, ó banújẹ́ nítorí wọn. Agbára rẹ̀ kò sì ká ọ̀rọ̀ wọn mọ́. Ó sì sọ pé: “Ọjọ́ tó le gan-an ní èyí.”');
INSERT INTO "aykyuni" VALUES(11,78,'11:78','Àwọn ènìyàn rẹ̀ wá bá a, tí wọ́n ń sáré gbọ̀n wá sí ọ̀dọ̀ rẹ̀. Tẹ́lẹ̀tẹ́lẹ̀ wọ́n sì ti ń ṣe iṣẹ́ aburú (bí ìbálòpọ̀ ọkùnrin sí ọkùnrin). Ó sọ pé: “Ẹ̀yin ènìyàn mi, àwọn wọ̀nyí ni ọmọbìnrin mi wọ́n mọ́ jùlọ fún yín (láti fi ṣaya dípò ìbálòpọ̀ ọkùnrin sí ọkùnrin). Nítorí náà, ẹ bẹ̀rù Allāhu. Kí ẹ sì má ṣe dójú tì mí lọ́dọ̀ àlejò mi. Ṣé kò sí ọkùnrin kan tí ó lóye lórí nínú yín ni?” ');
INSERT INTO "aykyuni" VALUES(11,79,'11:79','Wọ́n wí pé: “Ìwọ kúkú ti mọ̀ pé àwa kò ní bùkátà kan sí àwọn ọmọbìnrin rẹ. O sì ti mọ n̄ǹkan tí à ń fẹ́.”');
INSERT INTO "aykyuni" VALUES(11,80,'11:80','Ó sọ pé: “Tí ó bá jẹ́ pé dájúdájú agbára kan wà fún mi lórí yín ni, tàbí (pé) mo lè darapọ̀ mọ́ ẹbí kan (tàbí ikọ̀ ọmọ-ogun kan) tó lágbára ni, (èmi ìbá di yín lọ́wọ́ níbi aburú yín).”');
INSERT INTO "aykyuni" VALUES(11,81,'11:81','(Àwọn mọlāika) sọ pé: “(Ànábì) Lūt, dájúdájú Òjíṣẹ́ Olúwa rẹ ni àwa. Wọn kò lè (fọwọ́ aburú) kàn ọ́. Nítorí náà, mú àwọn ará ilé rẹ jáde ní abala kan nínú òru.Kí ẹnì kan nínú yín má sì ṣe ṣíjú wẹ̀yìn wò, àfi ìyàwó rẹ, dájúdájú ohun tó máa ṣẹlẹ̀ sí wọn ló máa ṣẹlẹ̀ sí i. Dájúdájú àkókò (àdánwò) wọn ni òwúrọ̀. Ṣé òwúrọ̀ kò súnmọ́ ni? ');
INSERT INTO "aykyuni" VALUES(11,82,'11:82','Nígbà tí àṣẹ Wa dé, A sọ òkè ìlú wọn di ìsàlẹ̀ rẹ̀ (ìlú wọ́n sì dojú bolẹ̀). A tún rọ òjò òkúta amọ̀ sísun lé wọn lórí ní tẹ̀léǹtẹ̀lé.');
INSERT INTO "aykyuni" VALUES(11,83,'11:83','(Àwọn òkúta náà) ti ní àmì (orúkọ ẹnì kọ̀ọ̀kan wọn lára) láti ọ̀dọ̀ Olúwa rẹ. Wọn kò sì jìnnà sí àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(11,84,'11:84','(Ẹni tí A rán níṣẹ́) sí ìran Mọdyan ni arákùnrin wọn (Ànábì) Ṣua‘eb. Ó sọ pé: “Ẹ̀yin ìjọ mi, ẹ jọ́sìn fún Allāhu. Ẹ kò ní ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo lẹ́yìn Rẹ̀. Ẹ má ṣe dín kóńgò àti òṣùwọ̀n kù. Mo rí yín pẹ̀lú dáadáa (tí Allāhu ṣe fún yín). Dájúdájú èmi sì ń páyà ìyà ọjọ́ kan tí ó máa yi yín po.');
INSERT INTO "aykyuni" VALUES(11,85,'11:85','Ẹ̀yin ìjọ mi, ẹ wọn kóńgò àti òṣùwọ̀n kún ní dọ́gban̄n̄dọ́gba. Ẹ má ṣe dín n̄ǹkan àwọn ènìyàn kù. Kí ẹ sì má ṣèbàjẹ́ sórí ilẹ̀.');
INSERT INTO "aykyuni" VALUES(11,86,'11:86','Ohun tí Allāhu bá ṣẹ́kù fún yín (nínú halāl) lóore jùlọ fún yín tí ẹ bá jẹ́ onígbàgbọ́ òdodo. Èmi kì í sì ṣe olùṣọ́ lórí yín.”');
INSERT INTO "aykyuni" VALUES(11,87,'11:87','Wọ́n wí pé: “Ṣu‘aeb, ṣé ìrun rẹ ló ń pa ọ́ láṣẹ pé kí á gbé ohun tí àwọn bàbá wa ń jọ́sìn fún jù sílẹ̀, tàbí (ìrun rẹ ló ń kọ̀ fún wa) láti ṣe ohun tí a bá fẹ́ nínú dúkìá wa? Dájúdájú ìwọ mà ni olùfaradà, olóye (lójú ara rẹ nìyẹn).”1 ');
INSERT INTO "aykyuni" VALUES(11,88,'11:88','Ó sọ pé: “Ẹ̀yin ìjọ mi, ẹ sọ fún mi, tí ó bá jẹ́ pé mo wà lórí ẹ̀rí tó yanjú láti ọ̀dọ̀ Olúwa mi, tí (Olúwa mi) sì fún mi ní arísìkí láti ọ̀dọ̀ Rẹ̀ ní arísìkí tó dára (ṣé kí n̄g máa jẹ harāmu pẹ̀lú rẹ̀ ni?). Èmi kò sì fẹ́ yapa yín nípa n̄ǹkan tí mò ń kọ̀ fún yín (ìyẹn ni pé, èmi náà ń lo ohun tí mò ń sọ fún yín). Kò sí ohun tí mo fẹ́ bí kò ṣe àtúnṣe pẹ̀lú bí mo ṣe lágbára mọ. Kò sí kòǹgẹ́ ìmọ̀nà kan fún mi bí kò ṣe pẹ̀lú (ìyọ̀ǹda) Allāhu. Òun ni mo gbáralé. Ọ̀dọ̀ Rẹ̀ sì ni mo máa ṣẹ́rí sí (fún ìronúpìwàdà).');
INSERT INTO "aykyuni" VALUES(11,89,'11:89','Ẹ̀yin ìjọ mi, ẹ má ṣe jẹ́ kí (bí) ẹ ṣe ń yapa mi mu yín lùgbàdì irú ohun tí ó ṣẹlẹ̀ sí ìjọ (Ànábì) Nūh tàbí ìjọ (Ànábì) Hūd tàbí ìjọ (Ànábì) Sọ̄lih. Ìjọ (Ànábì) Lūt kò sì jìnnà si yín.');
INSERT INTO "aykyuni" VALUES(11,90,'11:90','Kí ẹ sì tọrọ àforíjìn lọ́dọ̀ Olúwa yín. Lẹ́yìn náà, kí ẹ ronú pìwàdà sọ́dọ̀ Rẹ̀. Dájúdájú Olúwa mi ni Aláàánú, Olólùfẹ́ (ẹ̀dá).”');
INSERT INTO "aykyuni" VALUES(11,91,'11:91','Wọ́n wí pé: “Ṣu‘aeb, a kò gbọ́ àgbọ́yé ọ̀pọ̀lọ́pọ̀ nínú ohun tí ò ń sọ. Dájúdájú àwa sì ń rí ọ ní ọ̀lẹ láààrin wa. Tí kò bá jẹ́ pé nítorí ti ẹbí rẹ ni, àwa ìbá ti kù ọ́ lókò pa; ìwọ kò sì lágbára kan lórí wa.”');
INSERT INTO "aykyuni" VALUES(11,92,'11:92','Ó sọ pé: “Ẹ̀yin ìjọ mi, ṣé ẹbí mi ló lágbára lójú yin ju Allāhu? Ẹ sì sọ Allāhu di Ẹni tí ẹ kọ̀yìn sí! Dájúdájú Olúwa mi ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(11,93,'11:93','Ẹ̀yin ìjọ mi, ẹ máa ṣe bí ẹ ti fẹ́ nínú ẹ̀sìn yín, dájúdájú èmi náà yóò máa bá ẹ̀sìn mi lọ. Láìpẹ́ ẹ̀yin yóò mọ ẹni tí ìyà máa dé bá, tí ó máa yẹpẹrẹ rẹ̀, (ẹ̀yin yó sì mọ) ta ni òpùrọ́. Nítorí náà, ẹ máa retí. Dájúdájú èmi náà wà pẹ̀lú yín tí mò ń retí (ìkángun ọ̀rọ̀ yín).”');
INSERT INTO "aykyuni" VALUES(11,94,'11:94','Nígbà tí àṣẹ Wa dé, A gba (Ànábì) Ṣu‘aeb àti àwọn tó gbàgbọ́ pẹ̀lú rẹ̀ là pẹ̀lú ìkẹ́ láti ọ̀dọ̀ Wa. Ohùn igbe líle gbá àwọn tó ṣàbòsí mú. Wọ́n sì dà lulẹ̀, tí wọ́n ti dòkú sínú ìlú wọn.');
INSERT INTO "aykyuni" VALUES(11,95,'11:95','Àfi bí ẹni pé wọn kò gbénú ìlú náà rí. Gbọ́, kí ìjìnnà sí ìkẹ́ máa jẹ́ ti ìran Mọdyan gẹ́gẹ́ bí ìran Thamūd ṣe jìnnà sí ìkẹ́. ');
INSERT INTO "aykyuni" VALUES(11,96,'11:96','Dájúdájú A fi àwọn àmì Wa àti ẹ̀rí pọ́nńbélé rán (Ànábì) Mūsā níṣẹ́');
INSERT INTO "aykyuni" VALUES(11,97,'11:97','sí Fir‘aon àti àwọn ìjòyè rẹ̀. Àwọn ènìyàn sì tẹ̀lé àṣẹ Fir‘aon. Àṣẹ Fir‘aon kò sì jẹ́ ìmọ̀nà.');
INSERT INTO "aykyuni" VALUES(11,98,'11:98','Ó máa ṣíwájú ìjọ rẹ̀ ní Ọjọ́ Àjíǹde. Ó sì máa kó wọn wọ inú Iná. Wíwọ Iná náà sì burú.');
INSERT INTO "aykyuni" VALUES(11,99,'11:99','A fi ègún tẹ̀lé wọn nílé ayé yìí àti ní Ọjọ́ Àjíǹde. Ẹ̀bùn náà tí wọ́n fún wọn ní tẹ̀léǹtẹ̀lé (ègún àti Iná) sì burú.');
INSERT INTO "aykyuni" VALUES(11,100,'11:100','Ìyẹn wà nínú àwọn ìró ìlú tí À ń sọ ìtàn rẹ̀ fún ọ. Ó wà nínú àwọn ìlú náà èyí tí ó sì ń jẹ́ ìlú àti èyí tí wọ́n ti parẹ́.');
INSERT INTO "aykyuni" VALUES(11,101,'11:101','A kò ṣàbòsí sí wọn, ṣùgbọ́n wọ́n ṣàbòsí sí ẹ̀mí ara wọn. Nítorí náà, àwọn òrìṣà wọn, tí wọ́n ń pè lẹ́yìn Allāhu, kò rọ̀ wọ́n lọ́rọ̀ nígbà tí àṣẹ Olúwa rẹ dé. Wọn kò ṣe àlékún kan fún wọn bí kò ṣe ìparun.');
INSERT INTO "aykyuni" VALUES(11,102,'11:102','Báyẹn ni ìgbámú Olúwa rẹ. Nígbà tí Ó bá gbá àwọn ìlú alábòsí mú, dájúdájú Ó máa gbá a mú pẹ̀lú ìyà ẹlẹ́ta-eléro tó le.');
INSERT INTO "aykyuni" VALUES(11,103,'11:103','Dájúdájú àmì (fèyíkọ́gbọ́n) wà nínú ìyẹn fún ẹnikẹ́ni tó páyà ìyà Ọjọ́ Ìkẹ́yìn. Ìyẹn ni ọjọ́ tí A óò kó àwọn ènìyàn jọ fún. Àti pé ìyẹn ni ọjọ́ tí gbogbo ẹ̀dá yóò fojú rí.');
INSERT INTO "aykyuni" VALUES(11,104,'11:104','A kò sì fi falẹ̀ bí kò ṣe títí di àkókò kan tí A ti ṣírò kalẹ̀.');
INSERT INTO "aykyuni" VALUES(11,105,'11:105','Ní ọjọ́ tó bá dé, ẹ̀mí kan kò níí sọ̀rọ̀ àfi pẹ̀lú ìyọ̀ǹda Allāhu. Olórí burúkú àti olórí ire yó sì wà nínú àwọn ẹ̀dá (ní ọjọ́ náà).');
INSERT INTO "aykyuni" VALUES(11,106,'11:106','Ní ti àwọn tó bá ṣorí burúkú, wọn yóò wà nínú Iná. Wọn yóò máa gbin tòò, wọn yó sì máa ké tòò.');
INSERT INTO "aykyuni" VALUES(11,107,'11:107','Olùṣegbére ni wọ́n nínú rẹ̀ ní òdiwọ̀n ìgbà tí àwọn sánmọ̀ àti ilẹ̀ fi ń bẹ, àfi ohun tí Olúwa rẹ bá fẹ́ Dájúdájú Olúwa ni Aṣèyí-ó-wùú. ');
INSERT INTO "aykyuni" VALUES(11,108,'11:108','Ní ti àwọn tí A bá ṣe ní olórí-ire, wọ́n máa wà nínú Ọgbà Ìdẹ̀ra. Olùṣegbére ni wọ́n nínú rẹ̀ ní òdíwọ̀n ìgbà tí àwọn sánmọ̀ àti ilẹ̀ fi ń bẹ àfi ohun tí Olúwa rẹ bá fẹ́.2 (Ọgbà Ìdẹ̀ra jẹ́) ọrẹ àìlópin. ');
INSERT INTO "aykyuni" VALUES(11,109,'11:109','Nítorí náà, má ṣe wà nínú iyèméjì nípa ohun tí àwọn wọ̀nyí ń jọ́sìn fún. Wọn kò jọ́sìn fún kiní kan bí kò ṣe bí àwọn bàbá wọn náà ṣe ń jọ́sìn (fún àwọn òrìṣà) ní ìṣáájú. Dájúdájú Àwa yóò san wọ́n ní ẹ̀san ìpín (ìyà) wọn láì níí dín in kù.');
INSERT INTO "aykyuni" VALUES(11,110,'11:110','A kúkú fún (Ànábì) Mūsā ní Tírà. Wọ́n sì yapa-ẹnu nípa rẹ̀. Àti pé tí kò bá jẹ́ pé ọ̀rọ̀ kan ti ṣíwájú láti ọ̀dọ̀ Olúwa rẹ ni, Àwa ìbá ti ṣèdájọ́ láààrin wọn. Dájúdájú wọ́n kúkú wà nínú iyèméjì tó gbópọn nípa al-Ƙur’ān.');
INSERT INTO "aykyuni" VALUES(11,111,'11:111','Dájúdájú ẹnì kọ̀ọ̀kan wọn ni Olúwa rẹ yóò san ní ẹ̀san iṣẹ́ wọn. Dájúdájú Òun ni Alámọ̀tán nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(11,112,'11:112','Dúró ṣinṣin gẹ́gẹ́ bí A ṣe pa ìwọ àti ẹnikẹ́ni tí ó bá rónú pìwàdà pẹ̀lú rẹ láṣẹ. Ẹ má ṣe tayọ ẹnu-ààlà. Dájúdájú Òun ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(11,113,'11:113','Ẹ má ṣe tẹ̀ sọ́dọ̀ àwọn tó ṣàbòsí nítorí kí Iná má baà fọwọ́ bà yín. Kò sì níí sí àwọn aláàbò fún yín lẹ́yìn Allāhu. Lẹ́yìn náà, A ò níí ràn yín lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(11,114,'11:114','Kírun ní etí ọ̀sán méjèèjìàti ní abala kan nínú òru Dájúdájú àwọn iṣẹ́ rere ń pa àwọn iṣẹ́ aburú rẹ́. Ìyẹn ni ìrántí fún àwọn olùrántí (Allāhu). ');
INSERT INTO "aykyuni" VALUES(11,115,'11:115','Ṣe sùúrù. Dájúdájú Allāhu kò níí fi ẹ̀san àwọn olùṣe-rere ráre.');
INSERT INTO "aykyuni" VALUES(11,116,'11:116','Kí ó sì jẹ́ pé àwọn onílàákàyè kan wà nínú àwọn ìran tó ṣíwájú yín (nínú àwọn tí A parẹ́) kí wọ́n máa kọ ìbàjẹ́ lórí ilẹ̀ - àfi ènìyàn díẹ̀ lára àwọn tí A gbàlà nínú wọn. - Àwọn tó ṣàbòsí sì tẹ̀lé n̄ǹkan tí A fi ṣe gbẹdẹmukẹ fún wọn. Wọ́n sì jẹ́ ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(11,117,'11:117','Olúwa rẹ kì í pa àwọn ìlú run lọ́nà àbòsí, nígbà tí àwọn ènìyàn rẹ̀ bá jẹ́ olùṣàtùn-únṣe.1 ');
INSERT INTO "aykyuni" VALUES(11,118,'11:118','Tí ó bá jẹ́ pé Olúwa rẹ bá fẹ́, ìbá ṣe àwọn ènìyàn ní ìjọ ẹlẹ́sìn kan ṣoṣo. (Àmọ́) wọn kò níí yé yapa-ẹnu (lórí ’Islām)');
INSERT INTO "aykyuni" VALUES(11,119,'11:119','àfi ẹni tí Olúwa rẹ bá kẹ́. Nítorí ìyẹn l’Ó fi ṣẹ̀dá wọn. Ọ̀rọ̀ Olúwa rẹ sì ti ṣẹ (báyìí pé): “Dájúdájú Èmi yóò mú nínú àwọn àlùjànnú àti ènìyàn ní àpapọ̀ kúnnú iná Jahanamọ.” 1 ');
INSERT INTO "aykyuni" VALUES(11,120,'11:120','Gbogbo (n̄ǹkan tí) À ń sọ ìtàn rẹ̀ fún ọ nínú àwọn ìró àwọn Òjísẹ́ ni èyí tí A fi ń fi ọ́ lọ́kàn balẹ̀. Òdodo tún dé bá ọ nínú (sūrah) yìí. Wáàsí àti ìrántí tún ni fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(11,121,'11:121','Kí o sì sọ fún àwọn tí kò gbàgbọ́ pé: “Ẹ máa ṣe bí ẹ ti fẹ́ nínú ẹ̀sìn yín. Dájúdájú àwa náà yóò máa bá ẹ̀sìn wa lọ.');
INSERT INTO "aykyuni" VALUES(11,122,'11:122','Ẹ máa retí, dájúdájú Àwa náà ń retí.”');
INSERT INTO "aykyuni" VALUES(11,123,'11:123','Ti Allāhu ni ìkọ̀kọ̀ àwọn sánmọ̀ àti ilẹ̀. Ọ̀dọ̀ Rẹ̀ sì ni wọ́n máa ṣẹ́rí gbogbo ọ̀rọ̀ ẹ̀dá padà sí pátápátá. Nítorí náà, jọ́sìn fún Un, kí o sì gbáralé E. Olúwa rẹ kì í sì ṣe onígbàgbéra nípa n̄ǹkan tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(12,1,'12:1','’Alif lām rọ̄. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)Ìwọ̀nyí ni àwọn āyah Tírà tó yanjú ọ̀rọ̀ ẹ̀dá. ');
INSERT INTO "aykyuni" VALUES(12,2,'12:2','Dájúdájú Àwa sọ̀ ọ́ kalẹ̀ ní n̄ǹkan kíké ní èdè Lárúbáwá nítorí kí ẹ lè ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(12,3,'12:3','Àwa ń sọ ìtàn tó dára jùlọ fún ọ pẹ̀lú ohun tí A fi ránṣẹ́ sí ọ (nínú ìmísí) al-Ƙur’ān yìí, bí ó tilẹ̀ jẹ́ pé ṣíwájú ìmísí náà ìwọ wà lára àwọn tí kò nímọ̀ (nípa rẹ̀).');
INSERT INTO "aykyuni" VALUES(12,4,'12:4','(Rántí) nígbà tí (Ànábì) Yūsuf sọ fún bàbá rẹ̀ pé: “Bàbá mi, dájúdájú èmi (lálàá) rí àwọn ìràwọ̀ mọ́kànlá, òòrùn àti òṣùpá. Mo rí wọn tí wọ́n forí kanlẹ̀ fún mi.”');
INSERT INTO "aykyuni" VALUES(12,5,'12:5','Ó sọ pé: “Ọmọ mi, má ṣe rọ́ àlá rẹ fún àwọn ọbàkan rẹ kí wọ́n má baà déte sí ọ. Dájúdájú Èṣù ni ọ̀tá pọ́nńbélé fún ènìyàn.');
INSERT INTO "aykyuni" VALUES(12,6,'12:6','Báyẹn ni Olúwa rẹ yóò ṣe ṣà ọ́ lẹ́ṣà. Ó máa fi ìtúmọ̀ àlá mọ̀ ọ́. Ó sì máa pé ìdẹ̀ra Rẹ̀ fún ìwọ àti àwọn ẹbí Ya‘ƙūb, gẹ́gẹ́ bí Ó ṣe pé e ṣíwájú fún àwọn bàbá rẹ méjèèjì, (Ànábì) ’Ibrọ̄hīm àti ’Ishāƙ. Dájúdájú Olúwa rẹ ni Onímọ̀, Ọlọ́gbọ́n.”');
INSERT INTO "aykyuni" VALUES(12,7,'12:7','Dájúdájú àwọn àmì (àríwòye) ń bẹ fún àwọn oníbèéèrè nípa (ìtàn Ànábì) Yūsuf àti àwọn ọbàkan rẹ̀.');
INSERT INTO "aykyuni" VALUES(12,8,'12:8','(Rántí) nígbà tí wọ́n sọ pé: “Dájúdájú Yūsuf àti ọmọ-ìyá rẹ̀ ni bàbá wa nífẹ̀ẹ́ sí ju àwa. Ọ̀pọ̀ ènìyàn sì ni àwa. Dájúdájú bàbá wa mà ti wà nínú àṣìṣe pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(12,9,'12:9','Ẹ pa Yūsuf tàbí kí ẹ lọ gbé e jùnù sí ilẹ̀ kan nítorí kí bàbá yín lè rójú gbọ́ tiyín. Ẹ sì máa di ènìyàn rere lẹ́yìn rẹ̀.”');
INSERT INTO "aykyuni" VALUES(12,10,'12:10','Òǹsọ̀rọ̀ kan nínú wọn sọ pé: “Ẹ má ṣe pa Yūsuf. Tí ó bá jẹ́ pé ẹ ṣá fẹ́ wá n̄ǹkan ṣe (sí ọ̀rọ̀ rẹ̀), ẹ gbé e jù sínú ìsàlẹ̀ẹ̀sàlẹ̀ kànǹga nítorí kí apá kan nínú àwọn onírìn-àjò lè he é lọ.”');
INSERT INTO "aykyuni" VALUES(12,11,'12:11','Wọ́n sọ pé: “Bàbá wa, kí ni kò mú ọ fi ọkàn tán wa lórí Yūsuf. Dájúdájú àwa yóò máa tọ́jú rẹ̀ dáadáa.');
INSERT INTO "aykyuni" VALUES(12,12,'12:12','Jẹ́ kí ó bá wa lọ ní ọ̀la kí ó lè dára yá, kí ó sì lè ṣeré. Àti pé dájúdájú àwa yóò máa ṣọ́ ọ.”');
INSERT INTO "aykyuni" VALUES(12,13,'12:13','Ó sọ pé: “Dájúdájú ó máa bà mí nínú jẹ́ pé kí ẹ mú u lọ. Mo sì ń páyà kí ìkokò má lọ pa á jẹ nígbà tí ẹ̀yin bá gbàgbé rẹ̀ (síbì kan).”');
INSERT INTO "aykyuni" VALUES(12,14,'12:14','Wọ́n sọ pé: “Tí ìkokò bá fi pa á jẹ, ọ̀pọ̀ ènìyàn sì ni wá, dájúdájú àwa nígbà náà ni ẹni òfò.”');
INSERT INTO "aykyuni" VALUES(12,15,'12:15','Nígbà tí wọ́n mú un lọ tán, wọ́n panupọ̀ pé kí àwọn jù ú sí ìsàlẹ̀ẹ̀sàlẹ̀ kànǹga. À si fí ìmísí ránṣẹ́ sí Yūsuf (níbẹ̀) pé: “Dájúdájú ìwọ yóò fún wọn ní ìró nípa ọ̀rọ̀ wọn yìí nígbà tí wọn kò níí fura.”');
INSERT INTO "aykyuni" VALUES(12,16,'12:16','Wọ́n dé bá bàbá wọn ní àṣálẹ́, tí wọ́n ń sunkún.');
INSERT INTO "aykyuni" VALUES(12,17,'12:17','Wọ́n sọ pé: “Bàbá wa, àwa lọ díje eré sísá. A sì fi Yūsuf sílẹ̀ síbi àwọn ẹrù wa. Nígbà náà ni ìkokò pa á jẹ. Ìwọ kò sì níí gbà wá gbọ́, àwa ìbáà jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(12,18,'12:18','Wọ́n dé pẹ̀lú ẹ̀jẹ̀ irọ́ lára ẹ̀wù rẹ̀. (Ànábì Ya‘ƙūb) sọ pé: “Rárá o, ẹ̀mí yín ló ṣe ọ̀ràn kan lọ́ṣọ̀ọ́ fún yín. Nítorí náà, sùúrù abiyì (lọ̀rọ̀ mi kàn). Allāhu sì ni Ẹni tí à ń wá àánú Rẹ̀ lórí ohun tí ẹ̀ ń ròyìn (yìí).”');
INSERT INTO "aykyuni" VALUES(12,19,'12:19','Àwọn onírìn-àjò kan sì dé. Wọ́n rán apọnmi wọn (ní omi). Ó sì ju doro rẹ̀ sínú kànǹga. (Yūsuf sì dìrọ̀ mọ́ okùn doro bọ́ síta. Apọnmi sì) sọ pé: “Ire ìdùnnú rè é! Èyí ni ọmọdékùnrin.” Wọ́n sì fi pamọ́ fún títà (bí ọjà). Allāhu sì ni Onímọ̀ nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(12,20,'12:20','Wọ́n tà á ní ẹ̀dínwó pẹ̀lú owó diriham tó níye. Wọ́n kò sì wowọ́ mọ́ ọn.1 ');
INSERT INTO "aykyuni" VALUES(12,21,'12:21','Ẹni tí ó rà á ní (ìlú) Misrọ sọ fún aya rẹ̀ pé: “Ṣàpọ́nlé ibùgbé rẹ̀. Ó súnmọ́ kí ó wúlò fún wa tàbí kí á fi ṣọmọ.” Báyẹn ni A ṣe fi àyè gba Yūsuf lórí ilẹ̀. Àti pé kí Á tún lè fi ìtúmọ̀ àlá mọ̀ ọ́n. Allāhu ni Olùborí lórí àṣẹ Rẹ̀, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ ènìyàn ni kò mọ̀.');
INSERT INTO "aykyuni" VALUES(12,22,'12:22','Nígbà tí ó sì dàgbà dáadáa, A fún un ní ọgbọ́n ìjìnlẹ̀ àti ìmọ̀. Báyẹn ni A ṣe ń san àwọn olùṣe-rere ní ẹ̀san rere.');
INSERT INTO "aykyuni" VALUES(12,23,'12:23','Obìnrin tí Yūsuf wà nínú ilé rẹ̀ sì jírẹ̀ẹ́bẹ̀ fún eré ìfẹ́ lọ́dọ̀ rẹ̀. Ó sì ti àwọn ìlẹ̀kùn pa. Ó sọ pé: “Súnmọ́ mi.” (Yūsuf) sọ pé: “Mo sádi Allāhu. Dájúdájú ọkọ rẹ ni ọ̀gá mi. Ó sì ṣe ibùgbé mi dáadáa. Dájúdájú àwọn alábòsí kò níí jèrè.”');
INSERT INTO "aykyuni" VALUES(12,24,'12:24','(Obìnrin náà) kúkú gbèrò eré ìfẹ́ sí i. Òun náà gbèrò rẹ̀,Tí kò bá jẹ́ pé ó rí ẹ̀rí Olúwa rẹ̀ (pé harāmu ni sìná, ìbá súnmọ́ ọn). Báyẹn ni (ọ̀rọ̀ náà rí) nítorí kí Á lè ṣẹ́rí aburú àti sìná ṣíṣe kúrò lọ́dọ̀ rẹ̀. Dájúdájú ó wà nínú àwọn ẹrúsìn Wa, àwọn tí A ṣà lẹ́ṣà (fún ipò ànábì).” ');
INSERT INTO "aykyuni" VALUES(12,25,'12:25','Àwọn méjèèjì sáré lọ síbi ìlẹ̀kùn. (Obìnrin yìí) sì fa ẹ̀wù (Yūsuf) ya lẹ́yìn. Àwọn méjèèjì sì bá ọ̀gá rẹ̀ lẹ́nu ọ̀nà. (Obìnrin yìí) sì sọ pé: “Kí ni ẹ̀san fún ẹni tí ó gbèrò aburú sí ará ilé rẹ bí kò ṣe pé kí wọ́n sọ ọ́ sínú ọgbà ẹ̀wọ̀n tàbí ìyà ẹlẹ́ta-eléro.”');
INSERT INTO "aykyuni" VALUES(12,26,'12:26','(Yūsuf) sọ pé: “Òun ló jírẹ̀ẹ́bẹ̀ fún eré ìfẹ́ lọ́dọ̀ mi.” Ẹlẹ́rìí kan nínú ará ile (obìnrin náà) sì jẹ́rìí pé: “Tí ó bá jẹ́ pé wọ́n fa ẹ̀wù rẹ̀ ya níwájú, (obìnrin yìí) ló sọ òdodo, (Yūsuf) sì wà nínú àwọn òpùrọ́.');
INSERT INTO "aykyuni" VALUES(12,27,'12:27','Tí ó bá sì jẹ́ pé wọ́n fa ẹ̀wù rẹ̀ ya lẹ́yìn, (obìnrin yìí) ló parọ́, (Yūsuf) sì wà nínú àwọn olódodo.”1 ');
INSERT INTO "aykyuni" VALUES(12,28,'12:28','Nígbà tí (ọkọ) rí ẹ̀wù rẹ̀ tí wọ́n ti fà á ya lẹ́yìn, ó sọ pé: “Dájúdájú nínú ète ẹ̀yin obìnrin ni (èyí). Dájúdájú ète yín tóbi.');
INSERT INTO "aykyuni" VALUES(12,29,'12:29','Yūsuf, ṣẹ́rí kúrò níbi èyí. Kí ìwọ (obìnrin yìí) sì tọrọ àforíjìn fún ẹ̀ṣẹ̀ rẹ. Dájúdájú ìwọ wà lára àwọn aláṣìṣe.”');
INSERT INTO "aykyuni" VALUES(12,30,'12:30','Àwọn obìnrin kan nínú ìlú sọ pé: “Ayaba ń jírẹ̀ẹ́bẹ̀ fún eré ìfẹ́ lọ́dọ̀ ọmọ ọ̀dọ̀ rẹ̀; ìfẹ́ kúkú ti kó sí i lọ́kàn. Dájúdájú àwa ń rí òun pé ó wà nínú àṣìṣe pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(12,31,'12:31','Nígbà tí (ayaba) gbọ́ ète (ọ̀rọ̀) wọn (tí wọ́n ń sọ nípa rẹ̀), ó ránṣẹ́ sí wọn. Ó sì pèsè ohun jíjẹ tí wọ́n máa rọ̀gbọ̀kú jẹ lórí tìmùtìmù àti ìrọ̀rí sílẹ̀ fún wọn. Ó fún ìkọ̀ọ̀kan wọn ní ọ̀bẹ (tí ó máa fi jẹun). Ó sì sọ pé: “Jáde sí wọn, (Yūsuf).” Nígbà tí wọ́n rí i, wọ́n gbé e tóbi (fún ẹwà ara rẹ̀). Wọ́n sì rẹ́ra wọn lọ́wọ́ (fún ìyanu ẹwà ara rẹ̀). Wọ́n sì wí pé: “Mímọ́ ni fún Allāhu! Èyí kì í ṣe abara. Kí ni èyí bí kò ṣe mọlāika alápọ̀n-ọ́nlé.”');
INSERT INTO "aykyuni" VALUES(12,32,'12:32','(Ayaba) sọ pé: “Ìyẹn ni ẹni tí ẹ bú mi fún. Dájúdájú mo jírẹ̀ẹ́bẹ̀ fún eré ìfẹ́ lọ́dọ̀ rẹ̀, ó sì wá ìṣọ́ra. Dájúdájú tí kò bá ṣe ohun tí mò ń pa láṣẹ fún un, wọ́n kúkú máa fi sínú ọgbà ẹ̀wọ̀n ni. Dájúdájú ó sì máa wà nínú àwọn ẹni yẹpẹrẹ.”');
INSERT INTO "aykyuni" VALUES(12,33,'12:33','(Yūsuf) sọ pé: “Olúwa (Ẹlẹ́dàá) mi, ọgbà ẹ̀wọ̀n ni mo nífẹ̀ẹ́ sí ju ohun tí wọ́n ń pè mí sí. Tí O ò bá sì ṣẹ́rí ète wọn kúrò lọ́dọ̀ mi, èmi yóò kó sínú ète wọn, èmi yó sì wà lára àwọn aláìmọ̀kan.”');
INSERT INTO "aykyuni" VALUES(12,34,'12:34','Nítorí náà, Olúwa rẹ̀ jẹ́pè rẹ̀. Ó sì ṣẹ́rí ète wọn kúrò lọ́dọ̀ rẹ̀. Dájúdájú Allāhu, Òun ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(12,35,'12:35','Lẹ́yìn ìgbà tí wọ́n ti rí àwọn àmì (pé Yūsuf kò jẹ̀bi ẹ̀sùn), lẹ́yìn náà, ó hàn sí wọn pé àwọn gbọ́dọ̀ sọ ọ́ sínú ọgbà ẹ̀wọ̀n títí di àsìkò kan ná.');
INSERT INTO "aykyuni" VALUES(12,36,'12:36','Àwọn ọ̀dọ́kùnrin méjì kan wọ inú ọgbà ẹ̀wọ̀n pẹ̀lú rẹ̀. Ọ̀kan nínú àwọn méjèèjì sọ pé: “Èmi lálàá rí ara mi pé mò ń fún ọtí.” Ẹnì kejì sọ pé: “Dájúdájú èmi lálàá rí ara mi pé mo gbé búrẹ́dì rù sórí mi, ẹyẹ sì ń jẹ nínú rẹ̀.” Sọ ìtúmọ̀ rẹ̀ fún wa. Dájúdájú àwa ń rí ọ pé o wà nínú àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(12,37,'12:37','Ó sọ pé: “Oúnjẹ tí wọ́n ń pèsè fún ẹ̀yin méjèèjì kò ní tí ì dé ba yín, àfi kí èmi ti sọ ìtúmọ̀ rẹ̀ fún ẹ̀yin méjèèjì ṣíwájú kí ó tó dé ba yín. Ìyẹn wà nínú n̄ǹkan tí Olúwa mi fi mọ̀ mí. Dájúdájú èmi fi ẹ̀sìn àwọn ènìyàn tí kò gbàgbọ́ nínú Allāhu sílẹ̀. Àwọn sì ni aláìgbàgbọ́ nínú Ọjọ́ Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(12,38,'12:38','Mo sì tẹ̀lé ẹ̀sìn àwọn bàbá mi, (àwọn Ànábì) ’Ibrọ̄hīm, ’Ishāƙ àti Ya‘ƙūb. Kò tọ́ sí wa pé kí á sọ n̄ǹkan kan di akẹgbẹ́ fún Allāhu. Ìyẹn wà nínú oore àjùlọ Allāhu lórí àwa àti lórí àwọn ènìyàn, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn kì í dúpẹ́ (fún Allāhu).');
INSERT INTO "aykyuni" VALUES(12,39,'12:39','Ẹ̀yin akẹgbẹ́ mi méjéèjì nínú ẹ̀wọ̀n, ṣé àwọn olúwa ọ̀tọ̀ọ̀tọ̀ ló lóore jùlọ (láti jọ́sìn fún) tàbí Allāhu, Ọ̀kan ṣoṣo, Olùborí?');
INSERT INTO "aykyuni" VALUES(12,40,'12:40','Kò sí ohun kan tí ẹ̀ ń jọ́sìn fún lẹ́yìn Rẹ̀ bí kò ṣe àwọn orúkọ kan tí ẹ sọ wọ́n - ẹ̀yin àti àwọn bàbá yín - Allāhu kò sọ ẹ̀rí kan kalẹ̀ fún un. Ìdájọ́ (ta ni a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo) kò sí fún ẹnì kan àyàfi Allāhu. Ó sì ti pàṣẹ pé ẹ má ṣe jọ́sìn fún kiní kan àyàfi Òun nìkan. Ìyẹn ni ẹ̀sìn tó fẹsẹ̀ rinlẹ̀, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn kò mọ̀.');
INSERT INTO "aykyuni" VALUES(12,41,'12:41','Ẹ̀yin akẹgbẹ́ mi méjéèjì nínú ẹ̀wọ̀n, ní ti ẹnì kan nínú yín, ó máa fún ọ̀gá rẹ̀ ní ọtí mu. Ní ti ẹnì kejì, wọn yóò kàn án mọ́ igi, ẹyẹ yó sì máa jẹ nínú orí rẹ̀. Wọ́n ti parí ọ̀rọ̀ tí ẹ̀yin méjèèjì ń ṣe ìbéèrè nípa rẹ̀.”');
INSERT INTO "aykyuni" VALUES(12,42,'12:42','Ó sì sọ fún ẹni tí ó rò pé ó máa là nínú àwọn méjèèjì pé: “Rántí mi lọ́dọ̀ ọ̀gá rẹ.” Èṣù sì mú (Yūsuf) gbàgbé ìrántí Olúwa rẹ̀ (nípa wíwá ìrànlọ́wọ́ sọ́dọ̀ ọ̀gá rẹ̀). Nítorí náà, ó wà nínú ọgbà ẹ̀wọ̀n fún àwọn ọdún díẹ̀.');
INSERT INTO "aykyuni" VALUES(12,43,'12:43','Ọbá sọ pé: “Dájúdájú èmi lálàá rí màálù méje tó sanra. Àwọn màálù méje tó rù sì ń jẹ wọ́n. (Mo tún lálàá rí) ṣiri méje tutù àti òmíràn gbígbẹ. Ẹ̀yin ìjòyè, ẹ fún mi ní àlàyé ìtúmọ̀ àlá mi tí ẹ bá jẹ́ ẹni tó máa ń túmọ̀ àlá.”');
INSERT INTO "aykyuni" VALUES(12,44,'12:44','Wọ́n sọ pé: “Àwọn àlá tí ó lọ́pọ̀ mọ́ra wọn tí kò ní ìtúmọ̀ (nìyí). Àti pé àwa kì í ṣe onímọ̀ nípa ìtúmọ̀ àwọn àlá.”');
INSERT INTO "aykyuni" VALUES(12,45,'12:45','Ẹni tí ó là nínú àwọn (ẹlẹ́wọ̀n) méjèèjì sọ pé, - ó rántí (Yūsuf) lẹ́yìn ìgbà pípẹ́ -: “Èmi yóò fún yín ní ìró nípa ìtúmọ̀ rẹ̀. Nítorí náà, ẹ fi iṣẹ́ rán mi ná (kí n̄g lọ ṣe ìwádìí rẹ̀).”');
INSERT INTO "aykyuni" VALUES(12,46,'12:46','Yūsuf, ìwọ olódodo, fún wa ní àlàyé ìtúmọ̀ àlá (yìí): màálù méje tó sanra. Àwọn màálù méje tó rù sì ń jẹ wọ́n. Àti ṣiri méje tutù àti òmíràn gbígbẹ. (Kí ni ìtúmọ̀ rẹ̀) nítorí kí n̄g lè fi àbọ̀ sọ́dọ̀ àwọn ènìyàn àti nítorí kí wọ́n lè mọ (ìtúmọ̀ rẹ̀).');
INSERT INTO "aykyuni" VALUES(12,47,'12:47','Ó sọ pé: “Ẹ̀yin yóò gbin n̄ǹkan ọ̀gbìn fún ọdún méje gbáko (gẹ́gẹ́ bí) ìṣe (yín). Ohunkòhun tí ẹ bá kó lérè oko, kí ẹ fi sílẹ̀ sínú ṣiri rẹ̀ àfi díẹ̀ tí ẹ máa jẹ nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(12,48,'12:48','Lẹ́yìn náà, ọdún méje tó le (fún ọ̀dá òjò) yóò dé lẹ́yìn ìyẹn, (àwọn ará ìlú) yó sì lè jẹ ohun tí ẹ ti (kó lérè oko) ṣíwájú àfi díẹ̀ tí ẹ máa fi pamọ́ (fún gbígbìn).');
INSERT INTO "aykyuni" VALUES(12,49,'12:49','Lẹ́yìn náà, ọdún kan tí Wọn yóò rọ̀jò fún àwọn ènìyàn yóò dé lẹ́yìn ìyẹn. Àwọn ènìyàn yó sì máa fún èso àti wàrà mu nínú ọdún náà.');
INSERT INTO "aykyuni" VALUES(12,50,'12:50','Ọba sọ pé: “Ẹ mú (Yūsuf) wá fún mi.” Nígbà tí ìránṣẹ́ náà dé ọ̀dọ̀ rẹ. (Yūsuf) sọ pé: “Padà sọ́dọ̀ ọ̀gá rẹ, kí o bi í léèrè pé, kí ni ó ṣẹlẹ̀ sí àwọn obìnrin tí wọ́n rẹ́ ọwọ́ ara wọn. Dájúdájú Olúwa mi ni Onímọ̀ nípa ète wọn.”');
INSERT INTO "aykyuni" VALUES(12,51,'12:51','(Ọba) sọ pé: “Kí ni ọ̀rọ̀ tiyín ti jẹ́ tí ẹ fi jírẹ̀ẹ́bẹ̀ fún eré ìfẹ́ lọ́dọ̀ Yūsuf?” Wọ́n sọ pé: “Mímọ́ ni fún Allāhu. àwa kò mọ aburú kan mọ Yūsuf.” Ayaba sọ pé: “Nísinsìn yìí ni òdodo fojú hàn. Èmi ni mo jírẹ̀ẹ́bẹ̀ fún eré ìfẹ́ lọ́dọ̀ rẹ̀. Dájúdájú ó wà nínú àwọn olódodo.”');
INSERT INTO "aykyuni" VALUES(12,52,'12:52','(Yūsuf sọ pé): “Ìyẹn nítorí kí ọba lè mọ̀ pé dájúdájú èmi kò jàǹbá rẹ̀ ní ìkọ̀kọ̀. Àti pé dájúdájú Allāhu kò níí fi ìmọ̀nà síbi ète àwọn oníjàǹbá.');
INSERT INTO "aykyuni" VALUES(12,53,'12:53','Èmi kò sì fọra mi mọ́ (níbi èròkérò); dájúdájú ẹ̀mí kúkú ń pàṣẹ èròkérò (fún ẹ̀dá) ni àfi ẹni tí Olúwa mi bá kẹ́. Dájúdájú Olúwa mi ni Aláforíjìn, Àṣàkẹ́-ọ̀run.”');
INSERT INTO "aykyuni" VALUES(12,54,'12:54','Ọba sọ pé: “Ẹ mú (Yūsuf) wá fún mi. Èmi yóò ṣe é ní ẹni ẹ̀ṣà lọ́dọ̀ mi.” Nígbà tí ọba bá a sọ̀rọ̀ tán, ó sọ pé: “Lọ́dọ̀ wa ní òní dájúdájú ìwọ ni ẹni pàtàkì, olùfọkàntán.”');
INSERT INTO "aykyuni" VALUES(12,55,'12:55','(Yūsuf) sọ pé: “Fi mí ṣe aláṣẹ lórí àwọn ọrọ̀ ìlú, dájúdájú alámòjúútó onímọ̀ ni mí.”');
INSERT INTO "aykyuni" VALUES(12,56,'12:56','Báyẹn ni A ṣe fún Yūsuf ní ipò àti ibùgbé lórí ilẹ̀. Ó sì ń gbé ní ibi tí ó bá fẹ́. Àwa ń mú ìkẹ́ Wa bá ẹnikẹ́ni tí A bá fẹ́. A ò sì níí fi ẹ̀san àwọn olùṣe-rere ráre.');
INSERT INTO "aykyuni" VALUES(12,57,'12:57','Dájúdájú ẹ̀san ti ọ̀run lóore jùlọ fún àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ń bẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(12,58,'12:58','Àwọn ọbàkan (Ànábì) Yūsuf dé, wọ́n sì wọlé tọ̀ ọ́. Ó dá wọn mọ̀. Àwọn kò sì mọ̀ ọ́n mọ́.');
INSERT INTO "aykyuni" VALUES(12,59,'12:59','Nígbà tí ó sì fún wọn ní ẹrù oúnjẹ wọn tán, ó sọ pé: “Ẹ lọ mú ọbàkan yín wá láti ọ̀dọ̀ bàbá yín. Ṣé ẹ kò rí i pé dájúdájú mò ń wọn kóńgò ní ẹ̀kún rẹ́rẹ́ ni? Èmi sì dára jùlọ nínú àwọn olùgbàlejò.');
INSERT INTO "aykyuni" VALUES(12,60,'12:60','Tí ẹ ò bá sì mú un wá, kò sí (ẹrù oúnjẹ) wíwọ̀n fún yín lọ́dọ̀ mi. Kí ẹ sì má ṣe súnmọ́ mi.”');
INSERT INTO "aykyuni" VALUES(12,61,'12:61','Wọ́n sọ pé: “A máa jírẹ̀ẹ́bẹ̀ fún un lọ́dọ̀ bàbá rẹ̀. Dájúdájú àwa máa ṣe bẹ́ẹ̀.”');
INSERT INTO "aykyuni" VALUES(12,62,'12:62','Ó sì sọ fún àwọn ọmọ ọ̀dọ̀ rẹ̀ pé: “Ẹ fi owó-ọjà wọn sínú ẹrù (oúnjẹ) wọn nítorí kí wọ́n lè mọ̀ nígbà tí wọ́n bá padà dé ọ̀dọ̀ ará ilé wọ́n (pé a ti dá a padà fún wọn), wọn yó sì lè padà wá.”');
INSERT INTO "aykyuni" VALUES(12,63,'12:63','Nígbà tí wọ́n padà dé ọ̀dọ̀ bàbá wọn, wọ́n sọ pé: “Bàbá wa, wọ́n kọ̀ láti wọn oúnjẹ fún wa. Nítorí náà, jẹ́ kí ọbàkan wa bá wa lọ nítorí kí á lè rí oúnjẹ wọ̀n (wálé). Dájúdájú àwa yóò máa ṣọ́ ọ.”');
INSERT INTO "aykyuni" VALUES(12,64,'12:64','Ó sọ pé: “Ṣé kí n̄g gbà yín gbọ́ lórí rẹ̀ bí kò ṣe bí mó ṣe gbà yín gbọ́ ní ìṣaájú lórí ọmọ-ìyá rẹ̀? Nítorí náà, Allāhu lóore jùlọ ní Olùṣọ́. Òun sì ni Aláàánú-jùlọ nínú àwọn aláàánú.”');
INSERT INTO "aykyuni" VALUES(12,65,'12:65','Nígbà tí wọ́n tú ẹrù oúnjẹ wọn, wọ́n rí owó-ọjà wọn tí wọ́n dá padà fún wọn (nínú rẹ̀). Wọ́n sọ pé: “Bàbá wa, kí ni a tún ń fẹ́? Owó-ọjà wa nìyí tí wọ́n ti dá padà fún wa. A ó si tún fi wá oúnjẹ rà fún àwọn ẹbí wa. A ó sì dáàbò bo ọbàkan wa. A sì máa rí àlékún ẹrù oúnjẹ (tí) ràkúnmí kan lè rù sí i. Ìwọ̀n oúnjẹ kékeré sì nìyẹn (lọ́dọ̀ ọba).”');
INSERT INTO "aykyuni" VALUES(12,66,'12:66','Ó sọ pé: “Èmi kò níí jẹ́ kí ó ba yín lọ títí ẹ máa fi ṣe àdéhùn fún mi ní (orúkọ) Allāhu pé dájúdájú ẹ máa mú un padà wá bá mi àfi tí (àwọn ọ̀tá) bá ka yín mọ́.” Nígbà tí wọ́n fún un ní àdéhùn wọn tán, ó sọ pé: “Allāhu ni Ẹlẹ́rìí lórí ohun tí à ń sọ (yìí).”');
INSERT INTO "aykyuni" VALUES(12,67,'12:67','Ó tún sọ pé: “Ẹ̀yin ọmọ mi, ẹ má ṣe gba ẹnu ọ̀nà ẹyọ kan wọlé. Ẹ gba ẹnu ọ̀nà ọ̀tọ̀ọ̀tọ̀ wọlé. Èmi kò lè fí n̄ǹkan kan rọ̀ yín lọ́rọ̀ lọ́dọ̀ Allāhu. Kò sí ìdájọ́ (kan) àfi ti Allāhu. Òun ni mo gbáralé. Òun sì ni kí àwọn olùgbáralé gbáralé.”');
INSERT INTO "aykyuni" VALUES(12,68,'12:68','Nígbà tí wọ́n wọ (inú ìlú) ní àwọn àyè tí bàbá wọn pa láṣẹ fún wọn, kò sì rọ̀ wọ́n lọ́rọ̀ kan kan lọ́dọ̀ Allāhu bí kò ṣe bùkátà kan nínú ẹ̀mí (Ànábì) Ya‘ƙūb tí ó múṣẹ. Dájúdájú onímọ̀ ni nípa ohun tí A fi mọ̀ ọ́n, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn kò mọ̀.');
INSERT INTO "aykyuni" VALUES(12,69,'12:69','Nígbà tí wọ́n wọlé ti (Ànábì) Yūsuf, ó mú ọmọ-ìyá rẹ̀ mọ́ra sọ́dọ̀ rẹ̀. Ó sọ pé: “Dájúdájú èmi ni ọmọ-ìyá rẹ. Nítorí náà, má ṣe banújẹ́ nípa ohun tí wọ́n ń ṣe níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(12,70,'12:70','Nígbà tí ó sì fún wọn ní ẹrù wọn tán, ó fi ife-ìmumi sínú ẹrù ọmọ-ìyá rẹ̀. Lẹ́yìn náà, olùpèpè kan kéde pé: “Ẹ̀yin èrò-ràkúnmí, dájúdájú olè mà ni yín.”');
INSERT INTO "aykyuni" VALUES(12,71,'12:71','Wọ́n dojú kọ wọ́n, wọ́n sọ pé: “Kí l’ẹ ṣàfẹ́kù?”');
INSERT INTO "aykyuni" VALUES(12,72,'12:72','Wọ́n sọ pé: “A ṣàfẹ́kù ife ọba ni. Ẹni tí ó bá sì mú un wá yóò rí ẹrù oúnjẹ ràkúnmí kan gbà. Èmi sì ni onídùróó fún ẹ̀bùn náà.”');
INSERT INTO "aykyuni" VALUES(12,73,'12:73','Wọ́n sọ pé: “A fi Allāhu búra, dájúdájú ẹ mọ̀ pé a kò wá láti ṣèbàjẹ́ lórí ilẹ̀. Àti pé àwa kì í ṣe olè.”');
INSERT INTO "aykyuni" VALUES(12,74,'12:74','Wọ́n sọ pé: “Kí ni ẹ̀san (fún ẹni tí) ó jí i, tí (ó bá hàn pé) ẹ̀yin jẹ́ òpùrọ́?”');
INSERT INTO "aykyuni" VALUES(12,75,'12:75','Wọ́n sọ pé: “Ẹ̀san rẹ̀; ẹni tí wọ́n bá bá a nínú ẹrù rẹ̀, òun náà ni ẹ̀san rẹ̀. Báyẹn ni àwa ṣe ń san àwọn alábòsí ní ẹ̀san (ní ọ̀dọ̀ tiwa).”1 ');
INSERT INTO "aykyuni" VALUES(12,76,'12:76','Ó sì bẹ̀rẹ̀ (àyẹ̀wò) pẹ̀lú àpò wọn ṣíwájú àpò ọmọ-ìyá rẹ̀. Lẹ́yìn náà, ó mú un jáde láti inú àpò ọmọ-ìyá rẹ̀. Báyẹn ní A ṣe fún (Ànábì) Yūsuf ní ète dá. Kò lè fi òfin ọba mú ọmọ-ìyá rẹ̀ àfi tí Allāhu bá fẹ́.À ń ṣàgbéga àwọn ipò fún ẹni tí A bá fẹ́. Onímọ̀ wà lókè gbogbo onímọ̀.2 ');
INSERT INTO "aykyuni" VALUES(12,77,'12:77','Wọ́n sọ pé: “Tí ó bá jalè, dájúdájú ọmọ-ìyá rẹ̀ kan ti jalè ṣíwájú.” (Ànábì) Yūsuf sì fi ọ̀rọ̀ náà pamọ́ sínú ẹ̀mí rẹ̀, kò sì fi hàn sí wọn pé (kò rí bẹ́ẹ̀). Ó (sì) sọ pé: “Ẹ̀yin lẹ burú jùlọ ní ipò. Allāhu l’Ó sì nímọ̀ jùlọ nípa ohun tí ẹ̀ ń ròyìn rẹ̀.”');
INSERT INTO "aykyuni" VALUES(12,78,'12:78','Wọ́n sọ pé: “Ìwọ ọba, dájúdájú ó ní bàbá arúgbó àgbàlágbà kan. Nítorí náà, mú ẹnì kan nínú wa dípò rẹ̀. Dájúdájú àwa ń rí ìwọ pé o wà nínú àwọn ẹni-rere.”');
INSERT INTO "aykyuni" VALUES(12,79,'12:79','Ó sọ pé: “Mo sádi Allāhu pé kí á mú ẹnì kan àfi ẹni tí a bá ẹrù wa lọ́dọ̀ rẹ. (Tí a bá mú ẹlòmíìràn) dájúdájú nígbà náà alábòsí ni wá.”');
INSERT INTO "aykyuni" VALUES(12,80,'12:80','Nígbà tí wọ́n sọ̀rètí nù lórí ọ̀rọ̀ rẹ̀, wọ́n tẹ̀ sọ̀rọ̀. Àgbà (nínú) wọn sọ pé: “Ṣé ẹ kò mọ̀ pé dájúdájú bàbá yín ti gba àdéhùn yín tí (ẹ ṣe ní orúkọ) Allāhu. Àti pé ṣíwájú (èyí, ohun tí ẹ ṣe ní) àṣeètó lórí ọ̀rọ̀ (Ànábì) Yūsuf. Nítorí náà, èmi kò níí fi ilẹ̀ (Misrọ) sílẹ̀ títí bàbá mi yóò fi yọ̀ǹda fún mi tàbí (títí) Allāhu yóò fi ṣe ìdájọ́ fún mi. Ó sì lóore jùlọ nínú àwọn olùdájọ́.');
INSERT INTO "aykyuni" VALUES(12,81,'12:81','Ẹ padà sọ́dọ̀ bàbá yín, kí ẹ sì sọ pé: “Bàbá wa, dájúdájú ọmọ rẹ jalè. A kò sì lè jẹ́rìí (sí kiní kan) àfi ohun tí a bá nímọ̀ (nípa) rẹ̀. Àwa sì kọ́ ni à ń ṣọ ìkọ̀kọ̀ (ẹ̀dá).');
INSERT INTO "aykyuni" VALUES(12,82,'12:82','Kí o sì bi àwọn ará ìlú tí a wà nínú rẹ̀ àti èrò-ràkúnmí tí a jọ darí dé léèrè wò. Dájúdájú olódodo ni wá.”');
INSERT INTO "aykyuni" VALUES(12,83,'12:83','(Ànábì Ya‘ƙūb) sọ pé: “Rárá o, ẹ̀mí yín ló ṣe ọ̀ràn kan lọ́ṣọ̀ọ́ fún yín. Nítorí náà, sùúrù tó rẹwà (lọ̀rọ̀ mi kàn báyìí). Ó ṣeé ṣe kí Allāhu bá mi mú gbogbo wọn wá. Dájúdájú Allāhu, Òun ni Onímọ̀, Ọlọ́gbọ́n.”');
INSERT INTO "aykyuni" VALUES(12,84,'12:84','Ó pa wọ́n tì, ó sì sọ pé: “Ìbànújẹ́ ń bẹ fún mi lórí Yūsuf!” Ojú rẹ̀ méjèèjì sì funfun wá (kò ríran mọ́) látara ìbànújẹ́ náà; ó sì pa á mọ́ra.');
INSERT INTO "aykyuni" VALUES(12,85,'12:85','Wọ́n sọ pé: “A fi Allāhu búra, ìwọ kò yé rántí Yūsuf títí o máa fi di yánnayànna tàbí (títí) o máa fi di ara àwọn òkú.”');
INSERT INTO "aykyuni" VALUES(12,86,'12:86','Ó sọ pé: “Allāhu mà ni èmi ń ro ẹ̀dùn ọkàn mi àti ìbànújẹ́ mi fún. Mo sì mọ ohun tí ẹ kò mọ̀ nípa Allāhu.');
INSERT INTO "aykyuni" VALUES(12,87,'12:87','Ẹ̀yin ọmọ mi, ẹ lọ wádìí nípa Yūsuf àti ọmọ-ìyá rẹ̀. Ẹ sì má ṣe sọ̀rètí nù nínú ìkẹ́ Allāhu. Dájúdájú kò sí ẹnì kan tí ó máa sọ̀rètí nù nínú ìkẹ́ Allāhu àfi ìjọ aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(12,88,'12:88','Nìgbà tí wọ́n wọlé tọ Yūsuf, wọ́n sọ pé: “Ìwọ ọba, ìnira (ebi) ti mú àwa àti ará ilé wa. A sì mú owó kan tí ẹ máa ta dànù lọ́wọ́.Nítorí náà, wọn kóńgò (oúnjẹ) náà kún fún wa, kí ó sì ta wá lọ́rẹ. Dájúdájú Allāhu yóò san àwọn olùtọrẹ ní ẹ̀san rere.” ');
INSERT INTO "aykyuni" VALUES(12,89,'12:89','Ó sọ pé: “Ǹjẹ́ ẹ mọ ohun tí ẹ ṣe fún Yūsuf àti ọmọ ìyá rẹ̀ nígbà tí ẹ̀yin jẹ́ aláìmọ̀kan?”');
INSERT INTO "aykyuni" VALUES(12,90,'12:90','Wọ́n sọ pé: “Ṣé dájúdájú ìwọ ni Yūsuf?” Ó sọ pé: “Èmi ni Yūsuf. Ọmọ ìyá mi sì nìyí. Dájúdájú Allāhu ti ṣe ìdẹ̀ra fún wa. Dájúdájú ẹnikẹ́ni tí ó bá bẹ̀rù (Allāhu), tí ó sì ṣe sùúrù, dájúdájú Allāhu kò níí fi ẹ̀san àwọn olùṣe-rere ráre.”');
INSERT INTO "aykyuni" VALUES(12,91,'12:91','Wọ́n sọ pé: “A fi Allāhu búra! Dájúdájú Allāhu ti gbọ́lá fún ọ jù wá lọ. Àti pé dájúdájú àwa ni aláṣìṣe.”');
INSERT INTO "aykyuni" VALUES(12,92,'12:92','Ó sọ pé: “Kò sí ìbáwí fún yín ní òní. Allāhu yóò foríjìn yín. Òun sì ni Aláàánú-jùlọ nínú àwọn aláàánú.');
INSERT INTO "aykyuni" VALUES(12,93,'12:93','Ẹ mú ẹ̀wù mi yìí lọ, kí ẹ fi lé iwájú bàbá mi, ó máa padà di olùríran. Kí ẹ sì kó gbogbo ẹbí yín wá bá mi.”');
INSERT INTO "aykyuni" VALUES(12,94,'12:94','Nígbà tí èrò-ràkúnmí náà sí pínyà (kúrò lọ́dọ̀ Ànábì Yūsuf), bàbá wọn sọ pé: “Dájúdájú èmi ń gbọ́ òórùn Yūsuf tí ẹ kò bá níí sọ pé mò ń jarán.”');
INSERT INTO "aykyuni" VALUES(12,95,'12:95','Wọ́n sọ pé: “A fi Allāhu búra! Dájúdájú ìwọ sì wà nínú àṣìṣe rẹ ti àtijọ́ (nípa ìfẹ́ Yūsuf).”');
INSERT INTO "aykyuni" VALUES(12,96,'12:96','Nígbà tí oníròó-ìdùnnú dé, ó ju (ẹ̀wù náà) lé e níwájú, ó sì ríran padà. Ó sọ pé: “Ǹjẹ́ èmi kò sọ fún yín pé dájúdájú mo mọ ohun tí ẹ kò mọ̀ nípa Allāhu.”');
INSERT INTO "aykyuni" VALUES(12,97,'12:97','Wọ́n sọ pé: “Bàbá wa, tọrọ àforíjìn ẹ̀ṣẹ̀ wa fún wa, dájúdájú àwa jẹ́ aláṣìṣe.”');
INSERT INTO "aykyuni" VALUES(12,98,'12:98','Ó sọ pé: “Láìpẹ́ mo máa tọrọ àforíjìn fún yín lọ́dọ̀ Olúwa mi. Dájúdájú Allāhu, Òun ni Aláforíjìn, Àṣàkẹ́-ọ̀run.”');
INSERT INTO "aykyuni" VALUES(12,99,'12:99','Nígbà tí wọ́n wọlé tọ (Ànábì) Yūsuf, ó kó àwọn òbí rẹ̀ méjèèjì mọ́ra sọ́dọ̀ rẹ̀, ó sì sọ pé: “Ẹ wọ ìlú Misrọ ní olùfàyàbalẹ̀ - tí Allāhu bá fẹ́.-”');
INSERT INTO "aykyuni" VALUES(12,100,'12:100','Ó gbé àwọn òbí rẹ̀ méjèèjì sórí ìtẹ́. Wọ́n sì wó lulẹ̀ fún un, tí wọ́n forí kanlẹ̀ kí i.Ó sọ pé: “Bàbá mi, èyí ni ìtúmọ̀ àlá mi (tí mo lá) ṣíwájú. Dájúdájú Olúwa mi ti sọ ọ́ di òdodo. Ó sì ṣe dáadáa fún mi nígbà tí Ó mú mi jáde kúrò nínú ọgbà ẹ̀wọ̀n. Ó tún mu yín wá (bá mi) láti inú oko lẹ́yìn tí Èṣù ti saburú sáààrin èmi àti àwọn ọbà-kan mi. Dájúdájú Olúwa mi ni Aláàánú fún ohun tí Ó bá fẹ́. Dájúdájú Allāhu, Òun ni Onímọ̀, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(12,101,'12:101','Olúwa mi, dájúdájú O ti fún mi nínú ìjọba. O tún fún mi nínú ìmọ̀ ìtúmọ̀ àlá. Olùpilẹ̀dá àwọn sánmọ̀ àti ilẹ̀, Ìwọ ni Alátìlẹ́yìn mi ní ayé àti ní ọ̀run, pa mí sípò mùsùlùmí. Kí O sì fi mí pẹ̀lú àwọn ẹni rere.”');
INSERT INTO "aykyuni" VALUES(12,102,'12:102','Ìyẹn wà nínú ìró ìkọ̀kọ̀ tí A fi ìmísí (rẹ̀) ránṣẹ́ sí ọ. Àti pé ìwọ kò sí lọ́dọ̀ wọn nígbà tí wọ́n fẹnukò lórí ọ̀rọ̀ wọn, tí wọ́n ń déte.');
INSERT INTO "aykyuni" VALUES(12,103,'12:103','Ìwọ ìbáà ṣojú kòkòrò (ìgbàlà wọn), ọ̀pọ̀lọ́pọ̀ ènìyàn ni kò níí gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(12,104,'12:104','Ìwọ kò sì bi wọ́n léèrè owó-ọ̀yà kan lórí rẹ̀. Kò sì jẹ́ kiní kan bí kò ṣe ìrántí fún gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(12,105,'12:105','Àti pé mélòó mélòó nínú àwọn àmì tí ń bẹ ní sánmọ̀ àti ní orí ilẹ̀ tí wọ́n ń ré kọjá lórí rẹ̀, tí wọ́n sì ń gbúnrí kúrò níbẹ̀.');
INSERT INTO "aykyuni" VALUES(12,106,'12:106','Ọ̀pọ̀lọ́pọ̀ wọn sì ni kò níí gbàgbọ́ nínú Allāhu àfi kí wọ́n tún máa ṣẹbọ.1 ');
INSERT INTO "aykyuni" VALUES(12,107,'12:107','Ṣé wọ́n fàyà balẹ̀ pé ohun tí ń bo ẹ̀dá mọ́lẹ̀ nínú ìyà Allāhu kò lè dé bá àwọn ni tàbí pé Àkókò náà kò lè kò lé àwọn lórí ní òjijì, tí wọn kò sì níí fura?');
INSERT INTO "aykyuni" VALUES(12,108,'12:108','Sọ pé: “Èyí ni ojú ọ̀nà (ẹ̀sìn) mi, èmi àti àwọn tó tẹ̀lé mi sì ń pèpè sí (ẹ̀sìn) Allāhu pẹ̀lú ìmọ̀ àmọ̀dájú àti ẹ̀rí. Mímọ́ ni fún Allāhu. Èmi kò sì sí lára àwọn ọ̀ṣẹbọ.”');
INSERT INTO "aykyuni" VALUES(12,109,'12:109','A kò rán ẹnì kan ní iṣẹ́-òjíṣẹ́ ṣíwájú rẹ àfi àwọn ọkùnrin tí À ń fi ìmísí ránṣẹ́ sí nínú àwọn ará ìlú.Ṣé wọn kò rìn kiri lórí ilẹ̀ kí wọ́n wo bí ìkángun àwọn tó ṣíwájú wọn ṣe rí? Dájúdájú Ilé ìkẹ́yìn lóore jùlọ fún àwọn tó bẹ̀rù (Allāhu). Ṣé ẹ ò ṣe làákàyè ni? ');
INSERT INTO "aykyuni" VALUES(12,110,'12:110','(A kì í jẹ àwọn ènìyàn níyà) títí di ìgbà tí àwọn Òjíṣẹ́ bá tó sọ̀rètí nù (nípa ìgbàgbọ́ wọn), tí wọ́n sì mọ̀ pé wọ́n ti pe àwọn ní òpùrọ́. Àrànṣe Wa yó sì dé bá wọn. A sì máa gba ẹni tí A bá fẹ́ là. Wọn kò lè dá ìyà Wa padà lórí ìjọ ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(12,111,'12:111','Dájúdájú àríwòye wà nínú ìtàn wọn fún àwọn onílàákàyè. (Al-Ƙur’ān) kì í ṣe ọ̀rọ̀ kan tí wọ́n ń hun, ṣùgbọ́n ó ń jẹ́rìí sí èyí tó jẹ́ òdodo nínú èyí tó ṣíwájú rẹ̀, ó ń ṣàlàyé gbogbo n̄ǹkan;ó jẹ́ ìmọ̀nà àti ìkẹ́ fún ìjọ onígbàgbọ́ òdodo. ');
INSERT INTO "aykyuni" VALUES(13,1,'13:1','’Alif lām mīm rọ̄. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)Ìwọ̀nyí ni àwọn āyah Tírà náà. Àti pé òdodo ni ohun tí wọ́n sọ̀kalẹ̀ fún ọ láti ọ̀dọ̀ Olúwa rẹ, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn kò gbàgbọ́. ');
INSERT INTO "aykyuni" VALUES(13,2,'13:2','Allāhu ni Ẹni tí Ó gbé àwọn sánmọ̀ ga sókè láì sí àwọn òpó kan (fún un) tí ẹ lè fojú rí. Lẹ́yìn náà, Ó gúnwà sórí Ìtẹ́-ọlá. Ó rọ òòrùn àti òṣùpá; ìkọ̀ọ̀kan wọn máa rìn fún gbèdéke àkókò kan.Ó ń ṣe ètò ọ̀rọ̀ (ẹ̀dá). Ó ń ṣe àlàyé àwọn āyah nítorí kí ẹ lè mọ àmọ̀dájú nípa ìpàdé Olúwa yín. ');
INSERT INTO "aykyuni" VALUES(13,3,'13:3','Òun ni Ẹni tí Ó tẹ́ ilẹ̀ pẹrẹsẹ. Ó sì fi àwọn àpáta tó dúró gbagidi àti àwọn odò tó ń ṣàn sínú ilẹ̀. Àti pé nínú gbogbo àwọn èso, Ó ṣe é ní oríṣi méjì-méjì.Ó ń fi òru bo ọ̀sán lójú. Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ tó ní àròjinlẹ̀. ');
INSERT INTO "aykyuni" VALUES(13,4,'13:4','Ó tún wà nínú ilẹ̀ àwọn abala-abala ilẹ̀ (oníran-ànran) tó wà nítòsí ara wọnàti àwọn ọgbà oko àjàrà, irúgbìn àti igi dàbínù tó pẹka àti èyí tí kò pẹka, tí wọ́n ń fún wọn ní omi ẹyọ kan mu. (Síbẹ̀síbẹ̀) A ṣe àjùlọ fún apá kan rẹ̀ lórí apá kan níbi àwọn èso wọn. Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ tó ní làákàyè. ');
INSERT INTO "aykyuni" VALUES(13,5,'13:5','Tí o bá ṣèèmọ̀, èèmọ̀ mà ni ọ̀rọ̀ (ẹnu) wọn (nípa bí wọ́n ṣe sọ pé): “Ṣé nígbà tí a bá ti di erùpẹ̀ tán, ṣé nígbà náà ni àwa yóò tún di ẹ̀dá titun?” Àwọn wọ̀nyẹn ni àwọn tó ṣàì gbàgbọ́ nínú Olúwa wọn. Àwọn wọ̀nyẹn ni ẹ̀wọ̀n ń bẹ lọ́rùn wọn. Àwọn wọ̀nyẹn sì ni èrò inú Iná. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(13,6,'13:6','Wọ́n tún ń kán ọ lójú pé kí aburú ṣẹlẹ̀ ṣíwájú rere. Àwọn ìyà (tó jẹ irú wọn) kúkú ti ṣẹlẹ̀ ṣíwájú wọn. Àti pé dájúdájú Olúwa rẹ ni Aláforíjìn fún àwọn ènìyàn lórí àbòsí (ọwọ́) wọn. Dájúdájú Olúwa rẹ le níbi ìyà.');
INSERT INTO "aykyuni" VALUES(13,7,'13:7','Àwọn tó ṣàì gbàgbọ́ ń wí pé: “Kí ni kò jẹ́ kí wọ́n sọ àmì kan kalẹ̀ fún un láti ọ̀dọ̀ Olúwa rẹ̀?” Olùkìlọ̀ mà ni ìwọ. Olùtọ́sọ́nà sì wà fún ìjọ kọ̀ọ̀kan.1 ');
INSERT INTO "aykyuni" VALUES(13,8,'13:8','Allāhu mọ ohun tí obìnrin kọ̀ọ̀kan ní lóyún àti ohun tí ilé ọmọ yóò fi dín kù (nínú ọjọ́ ìbímọ) àti ohun tí ó máa fi lékún. N̄ǹkan kọ̀ọ̀kan ló sì ní òdíwọ̀n lọ́dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(13,9,'13:9','(Allāhu ni) Onímọ̀-ìkọ̀kọ̀-àti- gban̄gba. Ó tóbi, Ó ga jùlọ.');
INSERT INTO "aykyuni" VALUES(13,10,'13:10','Bákan náà ni (lọ́dọ̀ Allāhu), ẹni tí ó fi ọ̀rọ̀ pamọ́ sínú nínú yín àti ẹni tí ó sọ ọ́ síta pẹ̀lú ẹni tí ó fi òru bojú àti ẹni tí ó ń rìn kiri ní ọ̀sán.');
INSERT INTO "aykyuni" VALUES(13,11,'13:11','Ó ń bẹ fún ènìyàn (kọ̀ọ̀kan) àwọn mọlāika tí ń gbaṣẹ́ fúnra wọn; wọ́n wà níwájú (ènìyàn) àti lẹ́yìn rẹ̀; wọ́n ń ṣọ́ (iṣẹ́ ọwọ́) rẹ̀ pẹ̀lú àṣẹ Allāhu. Dájúdájú Allāhu kò níí ṣe ìyípadà n̄ǹkan tó ń bẹ lọ́dọ̀ ìjọ kan títí wọn yóò fi yí n̄ǹkan tó ń bẹ nínú ẹ̀mí wọn padà. Nígbà tí Allāhu bá gbèrò aburú kan ro ìjọ kan, kò sí ẹni tó lè dá a padà. Kò sì sí aláààbò kan fún wọn lẹ́yìn Rẹ̀.');
INSERT INTO "aykyuni" VALUES(13,12,'13:12','Òun ni Ẹni tó ń fi mọ̀nàmọ́ná hàn yín ní ìbẹ̀rù àti ní ìrètí (fún yín). Ó sì ń ṣẹ̀dá ẹ̀ṣújò tó ṣú dẹ̀dẹ̀.');
INSERT INTO "aykyuni" VALUES(13,13,'13:13','Àrá ń ṣe àfọ̀mọ́ pẹ̀lú ẹyìn fún Un, àwọn mọlāika náà (ń ṣe bẹ́ẹ̀) fún ìbẹ̀rù Rẹ̀. Ó ń rán iná láti ojú sánmọ̀ níṣẹ́. Ó sì ń fi kọlu ẹni tí Ó bá fẹ́. Síbẹ̀ wọ́n ń jiyàn nípa Allāhu. (Allāhu) sì lágbára gan-an.');
INSERT INTO "aykyuni" VALUES(13,14,'13:14','TiRẹ̀ ni ìpè òdodo. Àwọn tí wọ́n ń pè lẹ́yìn Rẹ̀, wọn kò lè fi kiní kan jẹ́ pè wọn àfi bí ẹni tí ó tẹ́wọ́ rẹ̀ méjèèjì (lásán) sí omi nítorí kí omi lè dé ẹnu rẹ̀. Omi kò sì lè dé ẹnu rẹ̀. Àdúà (àti ìpè) àwọn aláìgbàgbọ́ kò sì jẹ́ kiní kan bí kò ṣe sínú ìṣìnà.1 ');
INSERT INTO "aykyuni" VALUES(13,15,'13:15','Allāhu ni àwọn tó wà nínú àwọn sánmọ̀ àti ilẹ̀ ń forí kanlẹ̀ fún, wọ́n fẹ́, wọ́n kọ̀, - òòji wọn (náà ń ṣe bẹ́ẹ̀) - ní òwúrọ̀ àti ní ìrọ̀lẹ́.1 ');
INSERT INTO "aykyuni" VALUES(13,16,'13:16','Sọ pé: “Ta ni Olúwa àwọn sánmọ̀ àti ilẹ̀?” Sọ pé: “Allāhu ni.” Sọ pé: “Ṣé lẹ́yìn Rẹ̀ ni ẹ tún mú àwọn aláfẹ̀yìntì kan, tí wọn kò ní ìkápá oore àti ìnira fún ẹ̀mí ara wọn?” Sọ pé: “Ṣé afọ́jú àti olùríran dọ́gba bí? Tàbí àwọn òkùnkùn àti ìmọ́lẹ̀ dọ́gba? Tàbí wọ́n yóò fún Allāhu ní àwọn akẹgbẹ́ kan tí àwọn náà dá ẹ̀dá bíi ti ẹ̀dá Rẹ̀, (tó bẹ́ẹ̀ gẹ́) tí ẹ̀dá fi jọra wọn lójú wọn?” Sọ pé: “Allāhu ni Ẹlẹ́dàá gbogbo n̄ǹkan. Òun sì ni Ọ̀kan ṣoṣo, Olùborí.');
INSERT INTO "aykyuni" VALUES(13,17,'13:17','Ó ń sọ omi kalẹ̀ láti sánmọ̀. Àwọn ojú-odò sì ń ṣàn pẹ̀lú òdiwọ̀n rẹ̀. Àgbàrá sì ń gbé ìfòfó orí-omi tó ga lọ. Bákan náà, nínú n̄ǹkan tí wọ́n ń yọ́ nínú iná láti fi ṣe n̄ǹkan-ọ̀ṣọ́ tàbí n̄ǹkan-èlò, òhun náà ní ìfòfó bí irú rẹ̀. Báyẹn ni Allāhu ṣe ń mú àkàwé wá fún òdodo àti irọ́. Ní ti ìfòfó, ó máa lọ ní ìlọ pàǹtí. Ní ti èyí tí ó sì máa ṣe àwọn ènìyàn ní àǹfààní, ó máa dúró sí orí ilẹ̀. Báyẹn ni Allāhu ṣe ń mú àwọn àkàwé wá.1 ');
INSERT INTO "aykyuni" VALUES(13,18,'13:18','Ohun rere (ìyẹn Ọgbà Ìdẹ̀ra) wà fún àwọn tó jẹ́pè Olúwa wọn. Àwọn tí kò sì jẹ́pè Rẹ̀, tí ó bá jẹ́ pé tiwọn ni gbogbo n̄ǹkan tó ń bẹ lórí ilẹ̀ pátápátá àti irú rẹ̀ pẹ̀lú rẹ̀, wọn ìbá fi ṣèràpadà (fún ẹ̀mí ara wọn níbi Iná). Àwọn wọ̀nyẹn ni aburú ìṣírò-iṣẹ́ wà fún.Iná Jahanamọ ni ibùgbé wọn; ibùgbé náà sì burú. ');
INSERT INTO "aykyuni" VALUES(13,19,'13:19','Ǹjẹ́ ẹni tí ó mọ̀ pé òdodo kúkú ni n̄ǹkan tí wọ́n sọ̀kalẹ̀ fún ọ láti ọ̀dọ̀ Olúwa rẹ, ó wa dà bí ẹni tí ó fọ́jú (nípa rẹ̀ bí)? Àwọn onílàákàyè ló ń lo ìrántí.');
INSERT INTO "aykyuni" VALUES(13,20,'13:20','(Àwọn ni) àwọn tó ń mú àdéhùn Allāhu ṣẹ. Àti pé wọn kì í tú àdéhùn.');
INSERT INTO "aykyuni" VALUES(13,21,'13:21','(Àwọn ni) àwọn tó ń da ohun tí Allāhu pa láṣẹ pé kí wọ́n dàpọ̀ pọ̀. Wọ́n ń páyà Olúwa wọn. Wọ́n sì ń páyà aburú ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(13,22,'13:22','(Àwọn ni) àwọn tó ṣe sùúrù láti fi wá Ojú rere Olúwa wọn. Wọ́n ń kírun. Wọ́n ń ná nínú ohun tí A pèsè fún wọn ní ìkọ̀kọ̀ àti ní gban̄gba. Wọ́n sì ń fi rere dènà aburú.Àwọn wọ̀nyẹn ni àtubọ̀tán Ilé rere ń bẹ fún. ');
INSERT INTO "aykyuni" VALUES(13,23,'13:23','Inú àwọn Ọgbà Ìdẹ̀ra gbére ni wọ́n máa wọ̀, (àwọn) àti ẹni tó bá ṣe rere nínú àwọn bàbá wọn, àwọn ìyàwó wọn àti àwọn àrọ́mọdọ́mọ wọn. Àwọn mọlāika yó sì máa wọlé tọ̀ wọ́n láti ẹnu ọ̀nà kọ̀ọ̀kan.');
INSERT INTO "aykyuni" VALUES(13,24,'13:24','(Wọn yóò sọ pé): “Àlàáfíà fún yín nítorí sùúrù yín.” Àtubọ̀tán Ilé rere náà sì dára.');
INSERT INTO "aykyuni" VALUES(13,25,'13:25','Àwọn tó ń tú àdéhùn Allāhu lẹ́yìn tí wọ́n ti gba àdéhùn Rẹ̀, tí wọ́n ń já ohun tí Allāhu pa láṣẹ pé kí wọ́n dàpọ̀, tí wọ́n sì ń ṣe ìbàjẹ́ lórí ilẹ̀; àwọn wọ̀nyẹn ni ègún wà fún. Àti pé Ilé (Iná) burúkú wà fún wọn.');
INSERT INTO "aykyuni" VALUES(13,26,'13:26','Allāhu ń tẹ́ arísìkí sílẹ̀ rẹgẹdẹ fún ẹni tí Ó bá fẹ́. Ó sì ń díwọ̀n rẹ̀ (fún ẹni tí Ó bá fẹ́). Wọ́n sì dunnú sí ìṣẹ̀mí ilé ayé! Kí sì ni ìṣẹ̀mí ayé ní ẹ̀gbẹ́ ti ọ̀run bí kò ṣe ìgbádùn bín-íntín.');
INSERT INTO "aykyuni" VALUES(13,27,'13:27','Àwọn tó ṣàì gbàgbọ́ ń wí pé: “Kí ni kò jẹ́ kí wọ́n sọ àmì kan kalẹ̀ fún un láti ọ̀dọ̀ Olúwa rẹ̀?” Sọ pé: “Dájúdájú Allāhu ń ṣi ẹni tí Ó bá fẹ́ lọ́nà. Ó sì ń fi ọ̀nà mọ ẹni tí ó bá ṣẹ́rí padà sọ́dọ̀ Rẹ̀.”');
INSERT INTO "aykyuni" VALUES(13,28,'13:28','Àwọn tó gbàgbọ́ ní òdodo, tí ọkàn wọn sì balẹ̀ pẹ̀lú ìrántí Allāhu; ẹ gbọ́, pẹ̀lú ìrántí Allāhu ni àwọn ọkàn máa fi balẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(13,29,'13:29','Àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, orí ire àti àbọ̀ rere wà fún wọn.');
INSERT INTO "aykyuni" VALUES(13,30,'13:30','Báyẹn ni A ṣe rán ọ níṣẹ́ sí ìjọ kan, - àwọn ìjọ kan kúkú ti ré kọjá lọ ṣíwájú wọn -, nítorí kí o lè máa ka n̄ǹkan tí A fi ránṣẹ́ sí ọ ní ìmísí fún wọn. Síbẹ̀síbẹ̀ wọ́n ń ṣàì gbàgbọ́ nínú Àjọkẹ́-ayé. Sọ pé: “Òun ni Olúwa mi. Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Òun ni mo gbáralé. Ọ̀dọ̀ Rẹ̀ sì ni àbọ̀ mi.”');
INSERT INTO "aykyuni" VALUES(13,31,'13:31','Tí ó bá jẹ́ pé dájúdájú al-Ƙur’ān, wọ́n ké e láti fi mú àwọn àpáta rìn (kúrò ní àyè wọn), tàbí wọ́n fi gé ilẹ̀ (kélekèle, kí ó yanu fún àwọn àlùmọ́nì), tàbí wọ́n fi bá àwọn òkú sọ̀rọ̀ (kí wọ́n sì jí dìde, wọn kò níí gbàgbọ́). Àmọ́ sá, ti Allāhu ni gbogbo àṣẹ pátápátá. Ṣé àwọn tó gbàgbọ́ kò mọ̀ pé tí ó bá jẹ́ pé Allāhu bá fẹ́, ìbá tọ́ gbogbo ènìyàn sọ́nà. Àjálù kò sì níí yé ṣẹlẹ̀ sí àwọn aláìgbàgbọ́ nítorí ohun tí wọ́n ṣe níṣẹ́ tàbí (àjálù náà kò níí yé) sọ̀kalẹ̀ sí tòsí ilé wọn títí di ìgbà tí àdéhùn Allāhu yóò fi dé. Dájúdájú Allāhu kò níí yapa àdéhùn.');
INSERT INTO "aykyuni" VALUES(13,32,'13:32','Dájúdájú wọn ti fi àwọn Òjíṣẹ́ kan ṣe yẹ̀yẹ́ ṣíwájú rẹ. Mo sì lọ́ àwọn tó ṣàì gbàgbọ́ lára. Lẹ́yìn náà, Mo gbá wọn mú. Nítorí náà, báwo ni ìyà (tí mo fi jẹ wọ́n) ti rí (lára wọn ná)!');
INSERT INTO "aykyuni" VALUES(13,33,'13:33','Ǹjẹ́ Ẹni tí Ó ń mójútó ẹ̀mí kọ̀ọ̀kan nípa iṣẹ́ tí ó ṣe (dà bí ẹni tí kò lè ṣe bẹ́ẹ̀ bí? Síbẹ̀) wọ́n tún bá Allāhu wá àwọn akẹgbẹ́ kan! Sọ pé: “Ẹ dárúkọ wọn ná.” Tàbí ẹ̀yin yóò fún (Allāhu) ní ìró ohun tí kò mọ̀ lórí ilẹ̀ tàbí (ẹ fẹ́ mú) irọ́ àti àbá dídá wá? Rárá, wọ́n ti ṣe ète àwọn tó ṣàì gbàgbọ́ ní ọ̀ṣọ́ fún wọn ni. Wọ́n sì ṣẹ́rí wọn kúrò lójú ọ̀nà òdodo. Ẹnikẹ́ni tí Allāhu bá ṣì lọ́nà, kò níí sí olùtọ́sọ́nà kan fún un.');
INSERT INTO "aykyuni" VALUES(13,34,'13:34','Ìyà wà fún wọn nínú ìṣẹ̀mí ayé (yìí). Ìyà ti Ọjọ́ Ìkẹ́yìn kúkú gbópọn jùlọ. Kò sì níí sì aláàbò kan fún wọn lọ́dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(13,35,'13:35','Àpèjúwe Ọgbà Ìdẹ̀ra tí wọ́n ṣe ní àdéhùn fún àwọn olùbẹ̀rù Allāhu (ni èyí tí) àwọn odò ń ṣàn kọjá nísàlẹ̀ rẹ̀. Èso rẹ̀ àti ibòji rẹ̀ yó máa wà títí láéláé. Ìyẹn ni ìkángun àwọn tó bẹ̀rù (Allāhu). Iná sì ni ìkángun àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(13,36,'13:36','Àwọn tí A fún ní Tírà ń dunnú sí ohun tí A sọ̀kalẹ̀ fún ọ. Ó sì wà nínú àwọn ìjọ kèfèrí-parapọ̀ tó ń ṣe àtakò sí apá kan rẹ̀. Sọ pé: “N̄ǹkan tí wọ́n pa mí láṣẹ rẹ̀ ni pé kí n̄g jọ́sìn fún Allāhu. Èmi kò sì gbọdọ̀ wá akẹgbẹ́ fún Un. Ọ̀dọ̀ Rẹ̀ ni mò ń pèpè sí. Ọ̀dọ̀ Rẹ̀ sì ni àbọ̀ mi.”');
INSERT INTO "aykyuni" VALUES(13,37,'13:37','Báyẹn ni A ṣe sọ̀ ọ́ kalẹ̀ ní ìdájọ́ pẹ̀lú èdè Lárúbáwá. Dájúdájú tí o bá fi lè tẹ̀lé ìfẹ́-inú wọn, lẹ́yìn n̄ǹkan tí ó dé bá ọ nínú ìmọ̀, kò sí aláàbò àti olùṣọ́ kan fún ọ mọ́ lọ́dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(13,38,'13:38','Dájúdájú A ti rán àwọn Òjíṣẹ́ kan níṣẹ́ ṣíwájú rẹ. A sì fún wọn ní àwọn ìyàwó àti àwọn ọmọọmọ.Kò lẹ́tọ̀ọ́ fún Òjíṣẹ́ kan láti mú àmì kan wá àyàfi pẹ̀lú ìyọ̀ǹda Allāhu.2 Gbogbo ìṣẹ̀lẹ̀ ló ní àkọsílẹ̀. ');
INSERT INTO "aykyuni" VALUES(13,39,'13:39','Allāhu ń pa ohun tí Ó bá fẹ́ rẹ́. Ó sì ń mú (ohun tí Ó bá fẹ́) ṣẹ. Ọ̀dọ̀ Rẹ̀ sì ni Tírà Ìpìlẹ̀ wà.1 ');
INSERT INTO "aykyuni" VALUES(13,40,'13:40','Ó ṣeé ṣe kí Á fi apá kan èyí tí A ṣe ní ìlérí fún wọn hàn ọ́ tàbí kí Á ti gba ẹ̀mí rẹ (ṣíwájú àsìkò náà), ìkéde (ẹ̀sìn) nìkan ni ojúṣe tìrẹ, ìṣírò-iṣẹ́ sì ni tiWa.');
INSERT INTO "aykyuni" VALUES(13,41,'13:41','Ṣé wọn kò wòye pé dájúdájú À ń dé sórí ilẹ̀, À ń dín in kù (mọ́ àwọn aláìgbàgbọ́ lọ́wọ́) láti àwọn eteetí ìlú (wọ inú ìlú nípa fífún àwọn mùsùlùmí ní ìṣẹ́gun lórí wọn)? Allāhu l’Ó ń ṣe ìdájọ́. Kò sí ẹnì kan tí ó lè dá ìdájọ́ Rẹ̀ padà (fún Un). Òun sì ni Olùyára níbi ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(13,42,'13:42','Dájúdájú àwọn tó ṣíwájú wọn déte. Ti Allāhu sì ni gbogbo ète pátápátá.Ó mọ ohun tí ẹ̀mí kọ̀ọ̀kan ń ṣe níṣẹ́. Àti pé àwọn aláìgbàgbọ́ ń bọ̀ wá mọ ẹni tí àtubọ̀tán Ilé (rere) wà fún. ');
INSERT INTO "aykyuni" VALUES(13,43,'13:43','Àwọn aláìgbàgbọ́ ń wí pé: “Ìwọ kì í ṣe Òjíṣẹ́.” Sọ pé: “Allāhu tó ní Ẹlẹ́rìí láààrin èmi àti ẹ̀yin àti ẹni tí ìmọ̀ Tírà ń bẹ lọ́dọ̀ rẹ̀. ');
INSERT INTO "aykyuni" VALUES(14,1,'14:1','’Alif lām rọ̄. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)(Èyí ni) Tírà kan tí A sọ̀kalẹ̀ fún ọ nítorí kí o lè mú àwọn ènìyàn kúrò láti inú àwọn òkùnkùn wá sínú ìmọ́lẹ̀ pẹ̀lú ìyọ̀ǹda Olúwa wọn. (Wọn yó sì bọ́) sí ọ̀nà Alágbára, Ọlọ́pẹ́ (tí ọpẹ́ tọ́ sí), ');
INSERT INTO "aykyuni" VALUES(14,2,'14:2','Allāhu, Ẹni tí Ó ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Ègbé ni fún àwọn aláìgbàgbọ́ níbi ìyà líle.');
INSERT INTO "aykyuni" VALUES(14,3,'14:3','Àwọn tó ń fẹ́ràn ìṣẹ̀mí ayé ju tọ̀run, tí wọ́n ń ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu, wọ́n sì ń fẹ́ kó wọ́; àwọn wọ̀nyẹn wà nínú ìṣìnà tó jìnnà.');
INSERT INTO "aykyuni" VALUES(14,4,'14:4','A kò rán Òjíṣẹ́ kan níṣẹ́ àfi pẹ̀lú èdèàwọn ènìyàn rẹ̀2 nítorí kí ó lè ṣàlàyé (ẹ̀sìn) fún wọn. Nígbà náà, Allāhu yóò ṣi ẹni tí Ó bá fẹ́ lọ́nà. Ó sì máa tọ́ ẹni tí Ó bá fẹ́ sọ́nà; Òun ni Alágbára, Ọlọ́gbọ́n.3 ');
INSERT INTO "aykyuni" VALUES(14,5,'14:5','A kúkú fi àwọn āyah Wa rán (Ànábì) Mūsā níṣẹ́ pé: “Mú ìjọ rẹ kúrò láti inú àwọn òkùnkùn bọ́ sínú ìmólẹ̀. Kí o sì rán wọn létí àwọn ìdẹ̀ra Allāhu (lórí wọn).” Dájúdájú àwọn àmì wà nínú ìyẹn fún gbogbo onísùúrù, olùdúpẹ́.');
INSERT INTO "aykyuni" VALUES(14,6,'14:6','(Rántí) nígbà tí (Ànábì) Mūsā sọ fún ìjọ rẹ̀ pé, ẹ rántí ìkẹ́ Allāhu lórí yín nígbà tí Ó gbà yín là lọ́wọ́ àwọn ènìyàn Fir‘aon, tí wọ́n ń fi ìyà burúkú jẹ yín; wọ́n ń dúnńbú àwọn ọmọkùnrin yín, wọ́n sì ń dá àwọn ọmọbìnrin yín sí. Àdánwò ńlá wà nínú ìyẹn láti ọ̀dọ̀ Olúwa yín.');
INSERT INTO "aykyuni" VALUES(14,7,'14:7','(Ẹ rántí) nígbà tí Olúwa Ẹlẹ́dàá yín sọ ọ́ di mímọ̀ (fún yín pé): “Dájúdájú tí ẹ bá dúpẹ́, Èmi yóò ṣàlékún fún yín. Dájúdájú tí ẹ bá sì ṣàì moore, dájúdájú ìyà Mi mà le.”');
INSERT INTO "aykyuni" VALUES(14,8,'14:8','(Ànábì) Mūsā sọ pé: “Tí ẹ bá ṣàì moore, ẹ̀yin àti àwọn tó ń bẹ lórí ilẹ̀ ayé pátápátá, dájúdájú Allāhu ni Ọlọ́rọ̀, Ọlọ́pẹ́.”');
INSERT INTO "aykyuni" VALUES(14,9,'14:9','Ṣé ìró àwọn tó ṣíwájú yín kò tí ì dé ba yín ni? Ìjọ (Ànábì) Nūh, ìjọ ‘Ād, ìjọ Thamūd àti àwọn tó wá lẹ́yìn wọn; kò sí ẹni tí ó mọ̀ wọ́n àfi Allāhu. Àwọn Òjíṣẹ́ wọn mú àwọn ẹ̀rí tó yanjú wá bá wọn. Nígbà náà, wọ́n dá ọwọ́ wọn padà sí ẹnu wọn wọ́n sì wí pé: “Dájúdájú àwa ṣàì gbàgbọ́ nínú n̄ǹkan tí wọ́n fi ran yín níṣẹ́. Àti pé dájúdájú àwa wà nínú iyèméjì tó gbópọn nípa ohun tí ẹ̀ ń pè wá sí.” ');
INSERT INTO "aykyuni" VALUES(14,10,'14:10','Àwọn Òjíṣẹ́ wọn sọ pé: “Ṣé iyèméjì kan ń bẹ níbi (bíbẹ) Allāhu, Olùpilẹ̀dá àwọn sánmọ̀ àti ilẹ̀? Ó ń pè yín nítorí kí Ó lè forí àwọn ẹ̀ṣẹ̀ yín jìn yín àti nítorí kí Ó lè lọ yín lára di gbèdéke àkókò kan.” Wọ́n wí pé: “Ẹ̀yin kò jẹ́ kiní kan bí kò ṣe abara bí irú wa. Ẹ̀yin sì fẹ́ ṣẹ́ wa lórí kúrò níbi n̄ǹkan tí àwọn bàbá wa ń jọ́sìn fún. Nítorí náà, ẹ fún wa ní ẹ̀rí pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(14,11,'14:11','Àwọn Òjíṣẹ́ wọn sọ fún wọn pé: “Àwa kò jẹ́ kiní kan bí kò ṣe abara bí irú yín. Ṣùgbọ́n Allāhu ń ṣoore fún ẹni tí Ó bá fẹ́ nínú àwọn ẹrúsìn Rẹ̀. Kò sì lẹ́tọ̀ọ́ fún wa láti fún yín ní ẹ̀rí kan àfi pẹ̀lú ìyọ̀ǹda Allāhu. Àti pé, Allāhu ni kí àwọn onígbàgbọ́ òdodo gbáralé.');
INSERT INTO "aykyuni" VALUES(14,12,'14:12','Kí ni ó máa ṣe wá tí a ò níí gbáralé Allāhu, Ó kúkú ti fi àwọn ọ̀nà wa mọ̀ wá. Dájúdájú a máa ṣe sùúrù lórí ohun tí ẹ bá fi kó ìnira bá wa. Allāhu sì ni kí àwọn olùgbáralé gbáralé.”');
INSERT INTO "aykyuni" VALUES(14,13,'14:13','Àwọn tó ṣàì gbàgbọ́ wí fún àwọn Òjíṣẹ́ wọn pé: “Dájúdájú àwa yóò le yín jáde kúrò lórí ilẹ̀ wa tàbí kí ẹ kúkú padà sínú ẹ̀sìn wa.” Nígbà náà, Olúwa wọn fi ìmísí ránṣẹ́ sí wọn pé: “Dájúdájú A máa pa àwọn alábòsí run.');
INSERT INTO "aykyuni" VALUES(14,14,'14:14','Dájúdájú A sì máa fún yín ní ibùgbé lórí ilẹ̀ lẹ́yìn wọn. “ Ìyẹn wà fún ẹni tí ó bá páyà ìdúró (níwájú) Mi, tí ó tún páyà ìlérí Mi.');
INSERT INTO "aykyuni" VALUES(14,15,'14:15','Wọ́n sì tọrọ àrànṣe (Allāhu lórí ìjọ wọn). Gbogbo aláfojúdi, olóríkunkun sì parun.');
INSERT INTO "aykyuni" VALUES(14,16,'14:16','Iná Jahanamọ ń bẹ lẹ́yìn (ìparun) rẹ̀; A ó sì máa fún un ní omi àwọyúnwẹ̀jẹ̀ mu.');
INSERT INTO "aykyuni" VALUES(14,17,'14:17','Ó ma máa mu ún díẹ̀díẹ̀, kò sì níí fẹ́ẹ̀ lè gbé e mì. (Ìnira) ikú yó sì yọ sí i ní gbogbo àyè, síbẹ̀ kò níí kú. Ìyà tó nípọn tún wà fún un lẹ́yìn rẹ̀.');
INSERT INTO "aykyuni" VALUES(14,18,'14:18','Àfiwé àwọn tó ṣàì gbàgbọ́ nínú Olúwa wọn: àwọn iṣẹ́ wọn dà bí eérú tí atẹ́gùn fẹ́ dànù pátápátá ní ọjọ́ ìjì atẹ́gùn. Wọn kò ní agbára kan lórí ohun tí wọ́n ṣe níṣẹ́. Ìyẹn ni ìṣìnà tó jìnnà.');
INSERT INTO "aykyuni" VALUES(14,19,'14:19','Ṣé o kò wòye pé dájúdájú Allāhu l’Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ pẹ̀lú òdodo? Tí Ó bá fẹ́, Ó máa ko yín kúrò. Ó sì máa mú ẹ̀dá titun wá.');
INSERT INTO "aykyuni" VALUES(14,20,'14:20','Ìyẹn kò sì lè dá Allāhu lágara.');
INSERT INTO "aykyuni" VALUES(14,21,'14:21','Gbogbo ẹ̀dá sì máa jáde sọ́dọ̀ Allāhu (lọ́jọ́ Àjíǹde). Nígbà náà, àwọn aláìlágbára yóò wí fún àwọn tó ṣègbéraga pé: “Dájúdájú àwa jẹ́ ọmọlẹ́yìn fún yín, ǹjẹ́ ẹ̀yin lè gbé n̄ǹkan kan kúrò fún wa nínú ìyà Allāhu?” Wọn yóò wí pé: “Tí ó bá jẹ́ pé Allāhu tọ́ wa sọ́nà ni, àwa ìbá tọ yín sọ́nà. Bákan náà sì ni fún wa, yálà a káyà sókè tàbí a ṣàtẹ̀mọ́ra (ìyà); kò sí ibùsásí kan fún wa.”');
INSERT INTO "aykyuni" VALUES(14,22,'14:22','Èṣù yó sì wí nígbà tí A bá ṣèdájọ́ (ẹ̀dá) tán, pé: “Dájúdájú Allāhu ṣe àdéhùn fún yín ní àdéhùn òdodo. Èmi náà ṣe àdéhùn fún yín. Mo sì yapa àdéhùn tí mo ṣe fún yín. Èmi kò sì ní agbára kan lórí yín bí kò ṣe pé mo pè yín ẹ sì jẹ́pè mi. Nítorí náà, ẹ má ṣe bú mi; ara yín ni kí ẹ bú. Èmi kò lè gbà yín là (nínú ìyà), Ẹ̀yin náà kò sì lè gbà mí là (nínú ìyà). Dájúdájú èmi ti lòdì sí ohun tí ẹ fi sọ mí di akẹgbẹ́ Allāhu ṣíwájú.” Dájúdájú àwọn alábòsí, ìyà ẹlẹ́ta-eléro wà fún wọn.');
INSERT INTO "aykyuni" VALUES(14,23,'14:23','Wọ́n sì máa mú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n ṣe àwọn iṣẹ́ rere wọ inú àwọn Ọgbà Ìdẹ̀ra, èyí tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀ pẹ̀lú ìyọ̀nda Olúwa wọn. Ìkíni wọn nínú rẹ̀ ni ‘àlàáfíà’.');
INSERT INTO "aykyuni" VALUES(14,24,'14:24','Ṣé o kò wòye sí bí Allāhu ṣe ṣàkàwé ọ̀rọ̀ dáadáa pẹ̀lú igi dáadáa, tí gbòǹgbò rẹ̀ fi ìdí múlẹ̀ ṣinṣin, tí ẹ̀ka rẹ̀ sì wà nínú sánmọ̀,');
INSERT INTO "aykyuni" VALUES(14,25,'14:25','tó sì ń so èso rẹ̀ ní gbogbo ìgbà pẹ̀lú ìyọ̀ǹda Olúwa rẹ̀? Allāhu ń fún àwọn ènìyàn ní àwọn àkàwé nítorí kí wọ́n lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(14,26,'14:26','Àkàwé ọ̀rọ̀ tí kò dára sì dà bí igi tí kò dára, tí wọ́n fà tu lókè ilẹ̀, tí kò rí ìdí fi jókòó lórí ilẹ̀.');
INSERT INTO "aykyuni" VALUES(14,27,'14:27','Allāhu yóò máa fi àwọn tó gbàgbọ́ ní òdodo rinlẹ̀ pẹ̀lú ọ̀rọ̀ tó rinlẹ̀ nínú ìṣẹ̀mí ayé àti ní Ọjọ́ Ìkẹ́yìn. Ó sì máa ṣi àwọn alábòsí lọ́nà. Àti pé Allāhu ń ṣe ohun tí Ó bá fẹ́.');
INSERT INTO "aykyuni" VALUES(14,28,'14:28','Ṣé o kò wòye sí àwọn tó yí ìdẹ̀ra Allāhu padà sí àìgbàgbọ́, wọ́n sì mú ìjọ wọn gúnlẹ̀ sí ilé ìparun?');
INSERT INTO "aykyuni" VALUES(14,29,'14:29','Iná Jahanamọ ni wọn yóò gúnlẹ̀ sí; ibùgbé náà sì burú.');
INSERT INTO "aykyuni" VALUES(14,30,'14:30','Wọ́n sọ (àwọn kan di) akẹgbẹ́ fún Allāhu nítorí kí wọ́n lè ṣẹ́rí àwọn ènìyàn kúrò nínú ẹ̀sìn Rẹ̀. Sọ pé: “Ẹ máa gbádùn ǹsó, nítorí pé dájúdájú inú Iná ni ẹ máa gúnlẹ̀ sí.”');
INSERT INTO "aykyuni" VALUES(14,31,'14:31','Sọ fún àwọn ẹrúsìn Mi àwọn tó gbàgbọ́ ní òdodo pé, kí wọ́n kírun, kí wọ́n sì ná nínú ohun tí A pèsè fún wọn ní ìkọ̀kọ̀ àti ní gban̄gba ṣíwájú kí ọjọ́ kan tó dé, tí kò níí sí títà-rírà kan àti yíyan ọ̀rẹ́ kan nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(14,32,'14:32','Allāhu ni Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀. Ó ń sọ omi kalẹ̀ láti sánmọ̀. Ó sì fi ń mú àwọn èso jáde; (ó jẹ́) arísìkí fún yín. Ó sì rọ ọkọ̀ ojú-omi fún yín kí ó lè rìn lójú omi pẹ̀lú àṣẹ Rẹ̀. Ó tún rọ àwọn odò fún yín.');
INSERT INTO "aykyuni" VALUES(14,33,'14:33','Ó rọ òòrùn àti òṣùpá fún yín, tí méjèèjì ń rìn láì sinmi. Ó tún rọ òru àti ọ̀sán fún yín.');
INSERT INTO "aykyuni" VALUES(14,34,'14:34','Àti pé Ó ń fún yín nínú gbogbo n̄ǹkan tí ẹ tọrọ lọ́dọ̀ Rẹ̀. Tí ẹ bá ṣe òǹkà ìdẹ̀ra Allāhu, ẹ kò lè kà á tán. Dájúdájú ènìyàn ni alábòsí aláìmoore.');
INSERT INTO "aykyuni" VALUES(14,35,'14:35','(Rántí) nígbà tí (Ànábì) ’Ibrọ̄hīm sọ pé: “Olúwa mi, ṣe ìlú yìí ní ìlú ìfàyàbalẹ̀. Kí O sì mú èmi àti àwọn ọmọ mi jìnnà sí jíjọ́sìn fún àwọn òrìṣà.');
INSERT INTO "aykyuni" VALUES(14,36,'14:36','Olúwa mi, dájúdájú àwọn òrìṣà ti kó ọ̀pọ̀lọ́pọ̀ nínú àwọn ènìyàn ṣìnà. Nítorí náà, ẹnikẹ́ni tí ó bá tẹ̀lé mi, dájúdájú òun ni ẹni mi. Ẹnikẹ́ni tí ó bá sì yapa mi, dájúdájú Ìwọ ni Aláforíjìn, Àṣàkẹ́.');
INSERT INTO "aykyuni" VALUES(14,37,'14:37','Olúwa wa, dájúdájú èmi wá ibùgbé fún àrọ́mọdọ́mọ mi sí ilẹ̀ àfonífojì, ilẹ̀ tí kò ní èso, nítòsí Ilé Abọ̀wọ̀ Rẹ. Olúwa wa, nítorí kí wọ́n lè kírun ni. Nítorí náà, jẹ́ kí ọkàn àwọn ènìyàn fà sọ́dọ̀ wọn. Kí O sì pèsè àwọn èso fún wọn nítorí kí wọ́n lè dúpẹ́ (fún Ọ).');
INSERT INTO "aykyuni" VALUES(14,38,'14:38','Olúwa wa, dájúdájú Ìwọ l’O mọ ohun tí à ń fi pamọ́ àti ohun tí à ń ṣe àfihàn rẹ̀. Kò sì sí kiní kan nínú ilẹ̀ àti nínú sánmọ̀ tó pamọ́ fún Allāhu.');
INSERT INTO "aykyuni" VALUES(14,39,'14:39','Gbogbo ẹyìn ń jẹ́ ti Allāhu,Ẹni tí Ó fún mi ní ’Ismọ̄‘īl àti ’Ishāƙ nígbà tí mo ti darúgbó. Dájúdájú, Olúwa mi ni Olùgbọ́ àdúà. ');
INSERT INTO "aykyuni" VALUES(14,40,'14:40','Olúwa mi, ṣe èmi àti nínú àrọ́mọdọ́mọ mi ní olùkírun. Olúwa wa, kí O sì gba àdúà mi.');
INSERT INTO "aykyuni" VALUES(14,41,'14:41','Olúwa mi, ṣàforíjìn fún èmi àti àwọn òbí mi méjèèjì àti àwọn onígbàgbọ́ òdodo ní ọjọ́ tí ìṣírò-iṣẹ́ yóò ṣẹlẹ̀.”');
INSERT INTO "aykyuni" VALUES(14,42,'14:42','Má ṣe lérò pé Allāhu gbàgbé n̄ǹkan tí àwọn alábòsí ń ṣe níṣẹ́. Ó kàn ń lọ́ wọn lára dí ọjọ́ kan tí àwọn ojú yóò yọ síta ràngàndàn.');
INSERT INTO "aykyuni" VALUES(14,43,'14:43','Wọn yóò má sáré (lọ síbi àkójọ fún ìṣírò-iṣẹ́), wọn yóò gbé orí wọn sókè, ìpéǹpéjú wọn kò sì níí padà sọ́dọ̀ wọn, àwọn ọkàn wọn yó sì pa sófo pátápátá (fún ìbẹ̀rù).');
INSERT INTO "aykyuni" VALUES(14,44,'14:44','Kìlọ̀ fún àwọn ènìyàn nípa ọjọ́ tí ìyà yóò dé bá wọn, àwọn tó ṣàbòsí yó sì wí pé: “Olúwa wa, lọ́ wa lára fún àsìkò díẹ̀ sí i, a máa jẹ́pè Rẹ, a sì máa tẹ̀lé àwọn Òjíṣẹ́.” Ṣé ẹ̀yin kò ti búra ṣíwájú pé ẹ̀yin kò níí kúrò nílé ayé?');
INSERT INTO "aykyuni" VALUES(14,45,'14:45','Ẹ sì gbé nínú ibùgbé àwọn tó ṣàbòsí sí ẹ̀mí ara wọn. Ó sì hàn si yín bí A ti ṣe pẹ̀lú wọn. A tún fún yín ni àwọn àpẹ̀ẹrẹ (gẹ́gẹ́ bí ẹ̀rí àti àlàyé ọ̀rọ̀).');
INSERT INTO "aykyuni" VALUES(14,46,'14:46','Wọ́n kúkú ti dá ète wọn, ọ̀dọ̀ Allāhu sì ni ète wọn wà, bí ó tilẹ̀ jẹ́ pé (pẹ̀lú) ète wọn àpáta fẹ́ẹ̀ lè yẹ̀ lulẹ̀.');
INSERT INTO "aykyuni" VALUES(14,47,'14:47','Nítorí náà, má ṣe lérò pé Allāhu yóò yapa àdéhùn Rẹ̀ tí Ó ṣe fún àwọn Òjíṣẹ́ Rẹ̀. Dájúdájú Allāhu ni Alágbára, Olùgbẹ̀san.');
INSERT INTO "aykyuni" VALUES(14,48,'14:48','Ní ọjọ́ tí A máa yí ilẹ̀ ayé padà sì n̄ǹkan mìíràn. (A máa yí) àwọn sánmọ̀ náà (padà. Àwọn ẹ̀dá) sì máa jáde (síwájú) Allāhu, Ọ̀kan ṣoṣo, Olùborí.');
INSERT INTO "aykyuni" VALUES(14,49,'14:49','O sì máa rí àwọn ẹlẹ́ṣẹ̀ ní ọjọ́ yẹn, tí wọn yóò so wọ́n papọ̀ mọ́ra wọn sínú sẹ́kẹ́sẹkẹ̀.');
INSERT INTO "aykyuni" VALUES(14,50,'14:50','Oje gbígbóná ni àwọn èwù wọn. Iná yó sì bo ojú wọn mọ́lẹ̀ bámúbámú');
INSERT INTO "aykyuni" VALUES(14,51,'14:51','nítorí kí Allāhu lè san ẹ̀san ohun tí ẹ̀mí kọ̀ọ̀kan ṣe níṣẹ́ fún un. Dájúdájú Allāhu ni Olùyára níbi ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(14,52,'14:52','Èyí ni ìkéde (ẹ̀sìn) fún àwọn ènìyàn nítorí kí wọ́n lè fi ṣe ìkìlọ̀, kí wọ́n sì lè mọ̀ pé (Allāhu) Òun nìkan ni Ọlọ́hun Ọkan ṣoṣo tí wọ́n gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àti nítorí kí àwọn onílàákàyè lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(15,1,'15:1','’Alif lām rọ̄. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)Ìwọ̀nyí ni àwọn āyah Tírà náà àti (àwọn āyah) al-Ƙur’ān tó ń yanjú ọ̀rọ̀ ẹ̀dá. ');
INSERT INTO "aykyuni" VALUES(15,2,'15:2','Ó ṣeé ṣe kí àwọn tó ṣàì gbàgbọ́ nífẹ̀ẹ́ sí pé àwọn ìbá sì ti jẹ́ mùsùlùmí (nílé ayé).');
INSERT INTO "aykyuni" VALUES(15,3,'15:3','Fi wọ́n sílẹ̀, kí wọ́n jẹ, kí wọ́n gbádùn, kí ìrètí (ẹ̀mí gígùn) kó àìrójú ráàyè bá wọn (láti ṣẹ̀sìn); láìpẹ́ wọ́n máa mọ̀.');
INSERT INTO "aykyuni" VALUES(15,4,'15:4','A kò pa ìlú kan run rí àfi kí ó ní àkọsílẹ̀ tí A ti mọ̀.');
INSERT INTO "aykyuni" VALUES(15,5,'15:5','Kò sí ìjọ kan (tí ó parun) ṣíwájú àkókò rẹ̀ (nínú Laohul-Mahfuuṭḥ); wọn kò sì níí sún un ṣíwájú fún wọn.');
INSERT INTO "aykyuni" VALUES(15,6,'15:6','Wọ́n wí pé: “Ìwọ tí wọ́n sọ al-Ƙur’ān kalẹ̀ fún, dájúdájú wèrè ni ọ́.');
INSERT INTO "aykyuni" VALUES(15,7,'15:7','Kí ni kò jẹ́ kí ìwọ mú àwọn mọlāika wá bá wa, tí ìwọ bá wà nínú àwọn olódodo?”');
INSERT INTO "aykyuni" VALUES(15,8,'15:8','A kò níí sọ mọlāika kalẹ̀ bí kò ṣe pẹ̀lú òdodo. Wọn kò sì níí lọ́ wọn lára mọ́ nígbà náà (tí àwọn mọlāika bá sọ̀kalẹ̀).');
INSERT INTO "aykyuni" VALUES(15,9,'15:9','Dájúdájú Àwa l’A sọ Tírà Ìrántí kalẹ̀ (ìyẹn al-Ƙur’ān). Dájúdájú Àwa sì ni Olùṣọ́ rẹ̀.');
INSERT INTO "aykyuni" VALUES(15,10,'15:10','Dájúdájú ṣíwájú rẹ A ti rán (àwọn Òjíṣẹ́) níṣẹ́ sí àwọn ìjọ, àwọn ẹni àkọ́kọ́.');
INSERT INTO "aykyuni" VALUES(15,11,'15:11','Òjíṣẹ́ kan kò sì níí dé bá wọn àfi kí wọ́n máa fi ṣe yẹ̀yẹ́.');
INSERT INTO "aykyuni" VALUES(15,12,'15:12','Báyẹn náà ni A ṣe máa fi (àìsàn pípe al-Ƙur''ān nírọ́) sínú ọkàn àwọn ẹlẹ́ṣẹ̀ (nínú ìjọ tìrẹ náà).');
INSERT INTO "aykyuni" VALUES(15,13,'15:13','Wọn kò níí gbà á gbọ́; ìṣe (Allāhulórí) àwọn ẹni àkọ́kọ́ kúkú ti ré kọjá. ');
INSERT INTO "aykyuni" VALUES(15,14,'15:14','Tí ó bá jẹ́ pé A ṣí ìlẹ̀kùn kan sílẹ̀ fún wọn láti inú sánmọ̀, tí wọn kò sì yé gbabẹ̀ gùnkè lọ (sínú sánmọ̀),');
INSERT INTO "aykyuni" VALUES(15,15,'15:15','dájúdájú wọ́n á wí pé: “Wọ́n kan fi n̄ǹkan bo ojú wa ni. Rárá, ènìyàn tí wọ́n ń dan ni àwa sẹ́.”');
INSERT INTO "aykyuni" VALUES(15,16,'15:16','A kúkú fi àwọn ibùsọ̀ ìràwọ̀ sínú sánmọ̀ (ayé). A ṣe é ní ọ̀ṣọ́ fún àwọn olùwòran.');
INSERT INTO "aykyuni" VALUES(15,17,'15:17','A sì ṣọ́ ọ kúrò lọ́dọ̀ gbogbo èṣù, ẹni ẹ̀kọ̀.1 ');
INSERT INTO "aykyuni" VALUES(15,18,'15:18','Àfi (èṣù) tí ó bá jí ọ̀rọ̀ gbọ́. Nígbà náà sì ni ògúnná pọ́nńbélé yó tẹ̀lé e láti ẹ̀yìn.');
INSERT INTO "aykyuni" VALUES(15,19,'15:19','Ilẹ̀, A tẹ́ ẹ pẹrẹsẹ. A sì ju àwọn àpáta tó dúró gbagidi sínú rẹ̀. A sì mú gbogbo n̄ǹkan tí ó ní òdíwọ̀n hù jáde láti inú rẹ̀.');
INSERT INTO "aykyuni" VALUES(15,20,'15:20','A ṣe ọ̀nà ìjẹ-ìmu sínú (ilé ayé) fun ẹ̀yin àti àwọn tí ẹ̀yin kò lè pèsè fún.');
INSERT INTO "aykyuni" VALUES(15,21,'15:21','Kò sì sí kiní kan àfi kí ilé-ọrọ̀ rẹ̀ wà lọ́dọ̀ Wa. Àwa kò sì níí sọ̀ ọ́ kalẹ̀ àfi pẹ̀lú òdíwọ̀n tí A ti mọ̀.');
INSERT INTO "aykyuni" VALUES(15,22,'15:22','Àti pé A rán àwọn atẹ́gùn láti kó àwọn ẹ̀ṣújò jọ. A sì sọ omi kalẹ̀ láti sánmọ̀. Nítorí náà, A fún yín mu. Ẹ̀yin sì kọ́ ni ẹ kó omi òjò jọ (sójú sánmọ̀).');
INSERT INTO "aykyuni" VALUES(15,23,'15:23','Dájúdájú Àwa, Àwa ń sọ (ẹ̀dá) di alààyè. A sì ń sọ ọ́ di òkú. Àwa sì ni A óò jogún (ẹ̀dá).');
INSERT INTO "aykyuni" VALUES(15,24,'15:24','Àti pé dájúdájú A mọ àwọn olùgbawájú nínú yín. Dájúdájú A sì mọ àwọn olùgbẹ̀yìn.');
INSERT INTO "aykyuni" VALUES(15,25,'15:25','Dájúdájú Olúwa rẹ, Òun l’Ó máa kó wọn jọ. Dájúdájú Òun ni Ọlọ́gbọ́n, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(15,26,'15:26','Dájúdájú A ṣẹ̀dá ènìyàn láti ara amọ̀ tó ń dún koko, tó ti pàwọ̀dà.');
INSERT INTO "aykyuni" VALUES(15,27,'15:27','Àti pé àlùjànnú, A ṣẹ̀dá rẹ̀ ṣíwájú (ènìyàn) láti ara iná alátẹ́gùn gbígbóná tí kò ní èéfín.');
INSERT INTO "aykyuni" VALUES(15,28,'15:28','(Rántí) nígbà tí Olúwa rẹ sọ fún àwọn mọlāika pé: “Dájúdájú Èmi yóò ṣe ẹ̀dá abara kan láti ara amọ̀ tó ń dún koko, tó ti pàwọ̀dà.');
INSERT INTO "aykyuni" VALUES(15,29,'15:29','Nígbà tí Mo bá ṣe é tó gún régétán, tí Mo fẹ́ ẹ̀mí sí i (lára) nínú ẹ̀mí Mi (tí Mo dá), nígbà náà ẹ dojú bolẹ̀ fún un ní olùforíkanlẹ̀-kíni.”1 ');
INSERT INTO "aykyuni" VALUES(15,30,'15:30','Nítorí náà, àwọn mọlāika, gbogbo wọn pátápátá sì forí kanlẹ̀ kí i.');
INSERT INTO "aykyuni" VALUES(15,31,'15:31','Àyàfi ’Iblīs. Ó kọ̀ láti wà nínú àwọn olùforíkanlẹ̀-kíni náà.');
INSERT INTO "aykyuni" VALUES(15,32,'15:32','(Allāhu) sọ pé: “’Iblīs, kí ló dí ọ lọ́wọ́ láti wà nínú àwọn olùforíkanlẹ̀-kíni?”');
INSERT INTO "aykyuni" VALUES(15,33,'15:33','Ó wí pé: “Èmi kò níí forí kanlẹ̀ kí abara kan tí O ṣẹ̀dá rẹ̀ láti ara amọ̀ tó ń dún koko, tó ti pàwọ̀dà.”');
INSERT INTO "aykyuni" VALUES(15,34,'15:34','(Allāhu) sọ pé: “Nítorí náà, jáde kúrò nínú rẹ̀. Dájúdájú, ìwọ ni ẹni-ẹ̀kọ̀.');
INSERT INTO "aykyuni" VALUES(15,35,'15:35','Àti pé, dájúdájú ègún ń bẹ lórí rẹ títí di Ọjọ́ ẹ̀san.”');
INSERT INTO "aykyuni" VALUES(15,36,'15:36','Ó wí pé: “Olúwa mi, lọ́ mi lára títí di Ọjọ́ tí wọ́n máa gbé ẹ̀dá dìde.”');
INSERT INTO "aykyuni" VALUES(15,37,'15:37','(Allāhu) sọ pé: “Dájúdájú ìwọ wà lára àwọn tí A óò lọ́ lára');
INSERT INTO "aykyuni" VALUES(15,38,'15:38','títí di ọjọ́ àkókò tí A ti mọ.”1 ');
INSERT INTO "aykyuni" VALUES(15,39,'15:39','Ó wí pé: “Olúwa mi, fún wí pé O ti ṣe mí ní ẹni anù, dájúdájú emí yóò ṣe (aburú) ní ọ̀ṣọ́ fún wọn lórí ilẹ̀. Dájúdájú èmi yó sì kó gbogbo wọn sínú anù');
INSERT INTO "aykyuni" VALUES(15,40,'15:40','àfi àwọn ẹrúsìn Rẹ, àwọn tí O ṣà lẹ́ṣà (fún ìgbàgbọ́ òdodo).”1 ');
INSERT INTO "aykyuni" VALUES(15,41,'15:41','(Allāhu) sọ pé: “(Ìdúróṣinṣin lójú) ọ̀nà tààrà, Ọwọ́ Mi ni èyí wà.”');
INSERT INTO "aykyuni" VALUES(15,42,'15:42','Dájúdájú àwọn ẹrúsìn Mi, kò sí agbára kan fún ọ lórí wọn, àyàfi ẹni tí ó bá tẹ̀lé ọ nínú àwọn ẹni anù.');
INSERT INTO "aykyuni" VALUES(15,43,'15:43','Àti pé dájúdájú iná Jahanamọ ni àdéhùn fún gbogbo wọn.');
INSERT INTO "aykyuni" VALUES(15,44,'15:44','Àwọn ojú ọ̀nà méje ń bẹ fún (iná). Àtúnpín tún wà fún ojú-ọ̀nà kọ̀ọ̀kan.');
INSERT INTO "aykyuni" VALUES(15,45,'15:45','Dájúdájú àwọn olùbẹ̀rù (Allāhu) máa wà nínú àwọn Ọgbà Ìdẹ̀ra pẹ̀lú àwọn odò (tó ń ṣàn nísàlẹ̀ rẹ̀).');
INSERT INTO "aykyuni" VALUES(15,46,'15:46','(A máa sọ pé): “Ẹ wọ inú rẹ̀ pẹ̀lú àlàáfíà ní olùfàyàbalẹ̀.”');
INSERT INTO "aykyuni" VALUES(15,47,'15:47','A sì máa yọ ohun tí ń bẹ nínú ọkàn wọn tó jẹ́ inúnibíni kúrò; (wọn yóò di) ọmọ-ìyá (ara wọn. Wọn yóò wà) lórí ibùsùn, tí wọn yóò kọjú síra wọn.');
INSERT INTO "aykyuni" VALUES(15,48,'15:48','Wàhálà kan kò níí bá wọn nínú rẹ̀. Wọn kò sì níí mú wọn jáde kúrò nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(15,49,'15:49','Fún àwọn ẹrúsìn Mi ní ìró pé dájúdájú Èmi ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(15,50,'15:50','Àti pé dájúdájú ìyà Mi ni ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(15,51,'15:51','Fún wọn ní ìró nípa àlejò (Ànábì) ’Ibrọ̄hīm.');
INSERT INTO "aykyuni" VALUES(15,52,'15:52','(Rántí) nígbà tí wọ́n wọlé tọ̀ ọ́, wọ́n sì sọ pé: “Àlàáfíà”. Ó sọ pé: “Dájúdájú ẹ̀rù yín ń ba àwa.”');
INSERT INTO "aykyuni" VALUES(15,53,'15:53','Wọ́n sọ pé: “Má ṣe bẹ̀rù. Dájúdájú àwa yóò fún ọ ní ìró ìdùnnú (nípa bíbí) ọmọkùnrin kan tí ó máa ní ìmọ̀.”');
INSERT INTO "aykyuni" VALUES(15,54,'15:54','Ó sọ pé: “Ṣé ẹ̀ ń fún mi ní ìró ìdùnnú (nípa bíbí ọmọ) nígbà tí ogbó ti dé sí mi? Ìró ìdùnnú wo ni ẹ̀ ń fún mi ná?”');
INSERT INTO "aykyuni" VALUES(15,55,'15:55','Wọ́n sọ pé: “À ń fún ọ ní ìró ìdùnnú pẹ̀lú òdodo. Nítorí náà, má ṣe wà lára àwọn olùjákànmùná.”');
INSERT INTO "aykyuni" VALUES(15,56,'15:56','Ó sọ pé: “Kò mà sí ẹni tí ó máa jákànmùná nínú ìkẹ́ Olúwa Rẹ̀ bí kò ṣe àwọn olùṣìnà.”');
INSERT INTO "aykyuni" VALUES(15,57,'15:57','Ó sọ pé: “Kí tún ni ọ̀rọ̀ tí ẹ bá wá, ẹ̀yin Òjíṣẹ́?”');
INSERT INTO "aykyuni" VALUES(15,58,'15:58','Wọ́n sọ pé: “Dájúdájú Wọ́n rán wa níṣẹ́ sí ìjọ ẹlẹ́ṣẹ̀ ni.');
INSERT INTO "aykyuni" VALUES(15,59,'15:59','Àfi ará ilé (Ànábì) Lūt. Dájúdájú àwa yóò gba wọn là pátápátá.');
INSERT INTO "aykyuni" VALUES(15,60,'15:60','Àfi ìyàwó rẹ̀; A ti kọ kádàrá rẹ̀ (pé) dájúdájú ó máa wà nínú àwọn tó máa ṣẹ́kù lẹ́yìn sínú ìparun.”');
INSERT INTO "aykyuni" VALUES(15,61,'15:61','Nígbà tí àwọn Òjíṣẹ́ dé ọ̀dọ̀ ará ilé (Ànábì) Lūt,');
INSERT INTO "aykyuni" VALUES(15,62,'15:62','(Ànábì Lūt) sọ pé: “Dájúdájú ẹ̀yin jẹ́ àjòjì ènìyàn.”');
INSERT INTO "aykyuni" VALUES(15,63,'15:63','Wọ́n sọ pé: “Rárá, a wá sọ́dọ̀ rẹ nítorí ohun tí wọ́n ń ṣeyèméjì nípa rẹ̀.');
INSERT INTO "aykyuni" VALUES(15,64,'15:64','A mú òdodo wá bá ọ ni. Àti pé dájúdájú olódodo ni àwa.');
INSERT INTO "aykyuni" VALUES(15,65,'15:65','Nítorí náà, mú ará ilé rẹ jáde ní apá kan òru. Kí o sì tẹ̀lé wọn lẹ́yìn. Ẹnì kan nínú yín kò sì gbọdọ̀ ṣíjú wo ẹ̀yìn wò. Kí ẹ sì lọ sí àyè tí wọ́n ń pa láṣẹ fún yín.”');
INSERT INTO "aykyuni" VALUES(15,66,'15:66','A sì jẹ́ kí ìdájọ́ ọ̀rọ̀ yìí hàn sí i pé dájúdájú A máa pa àwọn (ẹlẹ́ṣẹ̀) wọ̀nyí run pátápátá nígbà tí wọ́n bá mọ́júmọ́.');
INSERT INTO "aykyuni" VALUES(15,67,'15:67','Àwọn ará ìlú náà dé, wọ́n sì ń yọ̀ sẹ̀sẹ̀.');
INSERT INTO "aykyuni" VALUES(15,68,'15:68','(Ànábì Lūt) sọ pé: “Dájúdájú àwọn wọ̀nyí ni àlejò mi. Nítorí náà, ẹ má ṣe dójú tì mí.');
INSERT INTO "aykyuni" VALUES(15,69,'15:69','Kí ẹ bẹ̀rù Allāhu. Kí ẹ sì má ṣe yẹpẹrẹ mi.”');
INSERT INTO "aykyuni" VALUES(15,70,'15:70','Wọ́n wí pé: “Ǹjẹ́ àwa kò ti kọ̀ fún ọ (nípa gbígba àlejò) àwọn ẹ̀dá?”');
INSERT INTO "aykyuni" VALUES(15,71,'15:71','Ó sọ pé: “Àwọn ọmọbìnrin mi nìwọ̀nyí, tí ẹ bá ní n̄ǹkan tí ẹ fẹ́ (fi wọ́n) ṣe.”1 ');
INSERT INTO "aykyuni" VALUES(15,72,'15:72','(Allāhu sọ pé): “Mo fi ìṣẹ̀mí ìwọ (Ànábì Muhammad) búra;dájúdájú wọ́n kúkú wà nínú ìṣìnà àti àìmọ̀kan wọn, tí wọ́n ń pa rìdàrìdà.” ');
INSERT INTO "aykyuni" VALUES(15,73,'15:73','Nítorí náà, ohùn igbe mú wọn nígbà tí òòrùn òwúrọ̀ yọ sí wọn.');
INSERT INTO "aykyuni" VALUES(15,74,'15:74','A sì sọ òkè ìlú wọn di ìsàlẹ̀ rẹ̀ (ìlú wọ́n dojú bolẹ̀). A tún rọ òjò òkúta amọ̀ (sísun) lé wọn lórí.');
INSERT INTO "aykyuni" VALUES(15,75,'15:75','Dájúdájú àwọn àmì wà nínú ìyẹn fún àwọn alárìíwòye.');
INSERT INTO "aykyuni" VALUES(15,76,'15:76','Dájúdájú (ìlú náà) kúkú wà lójú ọ̀nà (tí ẹ̀ ń tọ̀ lọ tọ̀ bọ̀).');
INSERT INTO "aykyuni" VALUES(15,77,'15:77','Dájúdájú àmì wà nínú ìyẹn fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(15,78,'15:78','Àwọn ará ’Aekahnáà jẹ́ alábòsí. ');
INSERT INTO "aykyuni" VALUES(15,79,'15:79','A sì gbẹ́san lára wọn. Dájújájú àwọn ìlú méjèèjì ló kúkú wà lójú ọ̀nà gban̄gba (tí ẹ̀ ń tọ̀ lọ tọ̀ bọ̀).');
INSERT INTO "aykyuni" VALUES(15,80,'15:80','Dájúdájú àwọn ará Ihò àpáta pe àwọn Òjíṣẹ́ ní òpùrọ́.');
INSERT INTO "aykyuni" VALUES(15,81,'15:81','A sì fún wọn ní àwọn àmì Wa. Àmọ́ wọ́n gbúnrí kúrò níbẹ̀.');
INSERT INTO "aykyuni" VALUES(15,82,'15:82','Wọ́n máa ń gbẹ́ àwọn ilé ìgbé ìfàyàbalẹ̀ sínú àwọn àpáta.');
INSERT INTO "aykyuni" VALUES(15,83,'15:83','Ohùn igbe sì mú wọn nígbà tí wọ́n mọ́júmọ́.');
INSERT INTO "aykyuni" VALUES(15,84,'15:84','Ohun tí wọ́n ń ṣe níṣẹ́ kò sì rọ̀ wọ́n lọ́rọ̀ (níbi ìyà).');
INSERT INTO "aykyuni" VALUES(15,85,'15:85','A kò ṣẹ̀dá àwọn sánmọ̀, ilẹ̀ àti n̄ǹkan tó wà láààrin àwọn méjèèjì bí kò ṣe pẹ̀lú òdodo. Dájúdájú Àkókò náà máa dé. Nítorí náà, ṣàmójúkúrò ní àmójúkúrò tó rẹwà.');
INSERT INTO "aykyuni" VALUES(15,86,'15:86','Dájúdájú Olúwa rẹ ni Ẹlẹ́dàá, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(15,87,'15:87','Dájúdájú A fún ọ ní Sab‘u Mọthānī (ìyẹn, sūrah al-Fātihah) àti al-Ƙur’ān Ńlá.');
INSERT INTO "aykyuni" VALUES(15,88,'15:88','Ìwọ kò gbọdọ̀ ṣíjú rẹ méjèèjì wo ohun tí A fi ṣe ìgbádún ayé fún oríṣiríṣi àwọn kan nínú àwọn aláìgbàgbọ́. Má ṣe banújẹ́ nítorí wọn. Kí o sì rẹ apá rẹ nílẹ̀ fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(15,89,'15:89','Kí o sì sọ pé: “Dájúdájú èmi ni olùkìlọ̀ pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(15,90,'15:90','Gẹ́gẹ́ bí A ṣe sọ (ìyà) kalẹ̀ fún àwọn tó ń pín òdodo mọ́ irọ́;');
INSERT INTO "aykyuni" VALUES(15,91,'15:91','àwọn tí wọ́n sọ al-Ƙur’ān di ìpínkúpìn-ín,1 ');
INSERT INTO "aykyuni" VALUES(15,92,'15:92','Olúwa rẹ fi Ara Rẹ̀ búra fún ọ pé, A máa bi gbogbo wọn ní ìbéèrè');
INSERT INTO "aykyuni" VALUES(15,93,'15:93','nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(15,94,'15:94','Nítorí náà, kéde n̄ǹkan tí A pa láṣẹ fún ọ. Kí o sì ṣẹ́rí kúrò lọ́dọ̀ àwọn ọ̀ṣẹbọ.');
INSERT INTO "aykyuni" VALUES(15,95,'15:95','Dájúdájú Àwa yóò tó ọ (níbi aburú) àwọn oníyẹ̀yẹ́,');
INSERT INTO "aykyuni" VALUES(15,96,'15:96','àwọn tó ń mú ọlọ́hun mìíràn mọ́ Allāhu. Láìpẹ́ wọ́n máa mọ̀.');
INSERT INTO "aykyuni" VALUES(15,97,'15:97','Àwa kúkú ti mọ̀ pé dájúdájú ohun tí wọ́n ń wí ń kó ìnira bá ọ.');
INSERT INTO "aykyuni" VALUES(15,98,'15:98','Nítorí náà, ṣe àfọ̀mọ́ pẹ̀lú ẹyìn fún Olúwa rẹ.Kí o sì wà nínú àwọn olùforíkanlẹ̀ (fún Un). ');
INSERT INTO "aykyuni" VALUES(15,99,'15:99','Jọ́sìn fún Olúwa rẹ títí àmọ̀dájú (ìyẹn, ikú) yóò fi dé bá ọ.');
INSERT INTO "aykyuni" VALUES(16,1,'16:1','Àṣẹ Allāhu dé tán. Ẹ ma wulẹ̀ wá a pẹ̀lú ìkánjú. Mímọ́ ni fún Un. Ó sì ga tayọ n̄ǹkan tí wọ́n ń fi ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(16,2,'16:2','(Allāhu) ń fi àṣẹ Rẹ̀ sọ mọlāika (Jibrīl) kalẹ̀ láti máa mú ìmísí wá díẹ̀díẹ̀ fún ẹnikẹ́ni tí Ó bá fẹ́ (bẹ́ẹ̀ fún) nínú àwọn ẹrúsìn Rẹ̀ nítorí kí ẹ lè fi ṣe ìkìlọ̀ pé: “Dájúdájú kò sí ọlọ́hun kan tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Èmi. Nítorí náà, ẹ bẹ̀rù Mi.”');
INSERT INTO "aykyuni" VALUES(16,3,'16:3','Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ pẹ̀lú òdodo. Ó ga tayọ n̄ǹkan tí wọ́n ń fi ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(16,4,'16:4','Ó dá ènìyàn láti inú àtọ̀. (Ènìyàn) sì di olùjiyàn pọ́nńbélé (nípa Àjíǹde).');
INSERT INTO "aykyuni" VALUES(16,5,'16:5','Àwọn ẹran-ọ̀sìn, Ó ṣẹ̀dá wọn. Aṣọ òtútù àti àwọn àǹfààní (mìíràn) ń bẹ fún yín lára wọn. Àti pé ẹ̀ ń jẹ nínú wọn.');
INSERT INTO "aykyuni" VALUES(16,6,'16:6','Ọ̀ṣọ́ tún ń bẹ fún yín lára wọn nígbà tí ẹ bá ń dà wọ́n lọ (sí ilé wọn) àti nígbà tí ẹ bá ń kó wọn jáde lọ jẹko.1 ');
INSERT INTO "aykyuni" VALUES(16,7,'16:7','Wọ́n sì ń ru àwọn ẹrù yín tó wúwo lọ sí ìlú kan tí ẹ kò lè dé àfi pẹ̀lú wàhálà ẹ̀mí. Dájúdájú Olúwa yín mà ni Aláàánú, Oníkẹ̀ẹ́.');
INSERT INTO "aykyuni" VALUES(16,8,'16:8','(Ó ṣẹ̀dá) ẹṣin, ìbaaka àti kẹ́tẹ́kẹ́tẹ́ nítorí kí ẹ lè gùn wọ́n. (Wọ́n tún jẹ́) ọ̀ṣọ́. Ó tún ń ṣẹ̀dá ohun tí ẹ kò mọ̀.');
INSERT INTO "aykyuni" VALUES(16,9,'16:9','Allāhu l’Ó ń ṣàlàyé (’Islām) ọ̀nà tààrà. Àwọn ọ̀nà ẹ̀sìn wíwọ́ tún wà (lọ́tọ̀) Tí Ó bá fẹ́ bẹ́ẹ̀ ni, ìbá tọ́ gbogbo yín sí ọ̀nà (ẹ̀sìn ’Islām). ');
INSERT INTO "aykyuni" VALUES(16,10,'16:10','Òun ni Ẹni tó ń sọ omi kalẹ̀ láti sánmọ̀. Mímu wà fún yín nínú rẹ̀. Igi ewéko tún ń wù jáde láti inú rẹ̀, tí ẹ fi ń bọ́ àwọn ẹran-ọ̀sìn.');
INSERT INTO "aykyuni" VALUES(16,11,'16:11','(Allāhu) tún ń fi (omi yìí) hu àwọn irúgbìn, igi òróró zaetūn, igi dàbínù, igi àjàrà àti gbogbo àwọn èso (yòókù) jáde fún yín. Dájúdájú àmì wà nínú ìyẹn fún ìjọ tó ní àròjinlẹ̀.');
INSERT INTO "aykyuni" VALUES(16,12,'16:12','Ó rọ òru, ọ̀sán, òòrùn, òṣùpá àti àwọn ìràwọ̀ fún yín pẹ̀lú àṣẹ Rẹ̀. Dájúdájú àmì wà nínú ìyẹn fún ìjọ tó ní làákàyè.');
INSERT INTO "aykyuni" VALUES(16,13,'16:13','Àti ohun tí Ó tún ṣẹ̀dá rẹ̀ fún yín lórí ilẹ̀, tí àwọn àwọ̀ rẹ̀ jẹ́ oríṣiríṣi. Dájúdájú àmì wà nínú ìyẹn fún ìjọ tó ń lo ìrántí.');
INSERT INTO "aykyuni" VALUES(16,14,'16:14','Òun ni Ẹni tí Ó rọ odò nítorí kí ẹ lè jẹ ẹran (ẹja) tútù àti nítorí kí ẹ lè mú n̄ǹkan ọ̀ṣọ́ tí ẹ óò máa wọ̀ sára jáde láti inú (odò), - ó sì máa rí àwọn ọkọ̀ ojú-omi tí yóò máa la ojú omi kọjá lọ bọ̀ - àti nítorí kí ẹ lè wá nínú àwọn oore àjùlọ Rẹ̀ àti nítorí kí ẹ lè dúpẹ́ (fún Allāhu).');
INSERT INTO "aykyuni" VALUES(16,15,'16:15','Ó sì fi àwọn àpáta tó dúró gbagidi sórí ilẹ̀ kí ó má baà mì mọ yín lẹ́sẹ̀ àti àwọn odò àti àwọn ojú-ọ̀nà nítorí kí ẹ lè dá ojú ọ̀nà mọ̀;');
INSERT INTO "aykyuni" VALUES(16,16,'16:16','àti àwọn àmì òpópónà (fún ìtọ́sọ́nà ìrìn ọ̀sán). Wọ́n tún ń fi ìràwọ̀ dá ojú ọ̀nà mọ̀ (lálẹ́).1 ');
INSERT INTO "aykyuni" VALUES(16,17,'16:17','Ǹjẹ́ Ẹni tí Ó dá ẹ̀dá dà bí ẹni tí kò dá ẹ̀dá bí? Nítorí náà, ṣé ẹ ò níí lo ìrántí ni?');
INSERT INTO "aykyuni" VALUES(16,18,'16:18','Tí ẹ bá ṣòǹkà ìdẹ̀ra Allāhu, ẹ kò lè kà á tán. Dájúdájú Allāhu mà ni Aláforíjìn, Oníkẹ̀ẹ́.');
INSERT INTO "aykyuni" VALUES(16,19,'16:19','Àti pé Allāhu mọ ohun tí ẹ̀ ń fi pamọ́ àti ohun tí ẹ̀ ń ṣe àfihàn rẹ̀.');
INSERT INTO "aykyuni" VALUES(16,20,'16:20','Àwọn (òrìṣà) tí wọ́n ń pè lẹ́yìn Allāhu; wọn kò lè dá kiní kan. Allāhu l’Ó sì ṣẹ̀dá wọn.');
INSERT INTO "aykyuni" VALUES(16,21,'16:21','Òkú (ni wọ́n), wọn kì í ṣe alààyè.Wọn kò sì mọ àkókò tí A óò jí wọn dìde (nínú sàréè). ');
INSERT INTO "aykyuni" VALUES(16,22,'16:22','Ọlọ́hun yín tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo, Ọlọ́hun Ọ̀kan ṣoṣo ni. Àmọ́ àwọn tí kò ní ìgbàgbọ́ nínú Ọjọ́ Ìkẹ́yìn ni ọkàn wọn takò ó, tí wọ́n sì ń ṣègbéraga.');
INSERT INTO "aykyuni" VALUES(16,23,'16:23','Kò sí tàbí-ṣùgbọ́n, dájúdájú Allāhu mọ ohun tí wọ́n ń fi pamọ́ àti ohun tí wọ́n ń ṣe àfihàn rẹ̀. Àti pé dájúdájú (Allāhu) kò nífẹ̀ẹ́ àwọn onígbèéraga.');
INSERT INTO "aykyuni" VALUES(16,24,'16:24','Nígbà tí wọ́n bá sọ fún wọn pé: “Kí ni Olúwa yín sọ̀kalẹ̀? Wọ́n á wí pé: “Àkọsílẹ̀ àlọ́ àwọn ẹni àkọ́kọ́ ni.”');
INSERT INTO "aykyuni" VALUES(16,25,'16:25','(Wọ́n sọ bẹ́ẹ̀) nítorí kí wọ́n lè ru ẹrù ẹ̀ṣẹ̀ tiwọn ní pípé pérépéré ní Ọjọ́ Àjíǹde àti (nítorí kí wọ́n lè rù) nínú ẹrù ẹ̀ṣẹ̀ àwọn tí wọ́n ń ṣì lọ́nà pẹ̀lú àìnímọ̀. Gbọ́, ohun tí wọn yóò rù ní ẹ̀ṣẹ̀, ó burú.1 ');
INSERT INTO "aykyuni" VALUES(16,26,'16:26','Àwọn tó ṣíwájú wọn kúkú déte. Allāhu sì da ilé wọn wó látibi àwọn òpó ilé. Òrùlé sì wó lù wọ́n mọ́lẹ̀ láti òkè wọn. Àti pé ìyà dé bá wọn ní àyè tí wọn kò ti fura.');
INSERT INTO "aykyuni" VALUES(16,27,'16:27','Lẹ́yìn náà, ní Ọjọ́ Àjíǹde (Allāhu) yóò yẹpẹrẹ wọn. Ó sì máa sọ pé: “Ibo ni àwọn (tí ẹ sọ di) akẹgbẹ́ Mi wà, àwọn tí ẹ tìtorí wọn yapa (ọ̀rọ̀ Mi)?” Àwọn tí A fún ní ìmọ̀ ẹ̀sìn yó sì sọ pé: “Dájúdájú àbùkù àti aburú ọjọ́ òní wà fún àwọn aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(16,28,'16:28','(Àwọn ni) àwọn tí mọlāika ń pa nígbà tí wọ́n ń ṣàbòsí sí ẹ̀mí ara wọn lọ́wọ́. Ní àsìkò yìí ni wọ́n juwọ́ jusẹ̀ sílẹ̀ (wọ́n sì wí pé): “Àwa kò ṣe iṣẹ́ aburú kan kan.” Bẹ́ẹ̀ ni! Dájúdájú Allāhu ni Onímọ̀ nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(16,29,'16:29','Nítorí náà, ẹ wọ àwọn ẹnu ọ̀nà Iná lọ, olùṣegbére ni yín nínú rẹ̀. Ibùgbé àwọn onígbèéraga sì burú.');
INSERT INTO "aykyuni" VALUES(16,30,'16:30','Wọ́n sọ fún àwọn tó bẹ̀rù (Allāhu) pé: “Kí ni Olúwa yín sọ̀kalẹ̀? Wọ́n á wí pé: “Rere ni.” Rere ti wà fún àwọn tó ṣe rere ní ilé ayé yìí. Dájúdájú Ilé Ìkẹ́yìn lóore jùlọ. Àti pé dájúdájú ilé àwọn olùbẹ̀rù (Allāhu) dára.');
INSERT INTO "aykyuni" VALUES(16,31,'16:31','Àwọn Ọgbà Ìdẹ̀ra ''Adn ni wọn yóò wọ inú rẹ̀. Àwọn odò yó sì máa ṣàn ní ìsàlẹ̀ rẹ̀. Ohunkóhun tí wọ́n bá ń fẹ́ máa wà fún wọn nínú rẹ̀. Báyẹn ni Allāhu ṣe ń san ẹ̀san rere fún àwọn olùbẹ̀rù (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(16,32,'16:32','Àwọn tí mọlāika ń pa, nígbà tí wọ́n ń ṣe rere lọ́wọ́, (àwọn mọlāika) ń sọ pé: “Àlàáfíà fún yín. Ẹ wọ inú Ọgbà Ìdẹ̀ra nítorí ohun tí ẹ̀ ń ṣe níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(16,33,'16:33','Ṣé wọ́n ń retí ohun kan yàtọ̀ sí pé kí àwọn mọlāika wá bá wọn tàbí kí àṣẹ Olúwa rẹ dé? Báyẹn ni àwọn tó ṣíwájú wọn ti ṣe. Allāhu kò sì ṣe àbòsí sí wọn, ṣùgbọ́n ẹ̀mí ara wọn ni wọ́n ń ṣàbòsí sí.');
INSERT INTO "aykyuni" VALUES(16,34,'16:34','Nítorí náà, àwọn aburú ohun tí wọ́n ṣe níṣẹ́ ṣẹlẹ̀ sí wọn. Àti pé ohun tí wọ́n ń fi ṣe yẹ̀yẹ́ sì dìyà tó yí wọn po.');
INSERT INTO "aykyuni" VALUES(16,35,'16:35','Àwọn tí wọ́n bá Allāhu wá akẹgbẹ́ yó sì wí pé: “Tí ó bá jẹ́ pé Allāhu fẹ́ ni àwa ìbá tí jọ́sìn fún kiní kan lẹ́yìn Rẹ̀, àwa àti àwọn bàbá wa. Bákàn náà, àwa ìbá tí ṣe kiní kan ní èèwọ̀ lẹ́yìn Rẹ̀.” Báyẹn ni àwọn tó ṣíwájú wọn ti ṣe. Ǹjẹ́ ojúṣe kan wà fún àwọn Òjíṣẹ́ bí kò ṣe ìkéde (ẹ̀sìn) tó yanjú.');
INSERT INTO "aykyuni" VALUES(16,36,'16:36','Dájúdájú A ti gbé Òjíṣẹ́ dìde nínú ìjọ kọ̀ọ̀kan (láti jíṣẹ́) pé: “Ẹ jọ́sìn fún Allāhu. Kí ẹ sì jìnnà sí àwọn òrìṣà.” Nítorí náà, ó wà nínú wọn, ẹni tí Allāhu tọ́ sí ọ̀nà. Ó sì wà nínú wọn, ẹni tí ìṣìnà kò lé lórí. Nítorí náà, ẹ rìn kiri lórí ilẹ̀, kí ẹ sì wòye sí bí àtubọ̀tán àwọn tó pé àwọn Òjíṣẹ́ ní òpùrọ́ ṣe rí?');
INSERT INTO "aykyuni" VALUES(16,37,'16:37','Tí o bá ń ṣojú kòkòrò sí ìmọ̀nà wọn, dájúdájú Allāhu, ẹni tí Ó bá ṣì lọ́nà, kò níí mọ̀nà,kò sì níí sí àwọn alárànṣe kan kan fún wọn. ');
INSERT INTO "aykyuni" VALUES(16,38,'16:38','Wọ́n sì fi Allāhu búra tí ìbúra wọn sì lágbára gan-an pé: “Allāhu kò níí gbé ẹni tí ó kú dìde.” Bẹ́ẹ̀ ni, (àjíǹde jẹ́) àdéhùn òdodo lọ́dọ̀ Allāhu, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ ènìyàn kò mọ̀.');
INSERT INTO "aykyuni" VALUES(16,39,'16:39','(Allāhu yóò gbé ẹ̀dá dìde) nítorí kí Ó lè ṣàlàyé ohun tí wọ́n ń yapa ẹnu sí fún wọn àti nítorí kí àwọn tó ṣàì gbàgbọ́ lè mọ̀ pé dájúdájú àwọn ni wọ́n jẹ́ òpùrọ́.');
INSERT INTO "aykyuni" VALUES(16,40,'16:40','Ọ̀rọ̀ Wa fún kiní kan nígbà tí A bá gbèrò rẹ̀ ni pé, A máa sọ fún un pé: “Jẹ́ bẹ́ẹ̀.” Ó sì máa jẹ́ bẹ́ẹ̀.”');
INSERT INTO "aykyuni" VALUES(16,41,'16:41','Àwọn tó gbé ìlú wọn jù sílẹ̀ nítorí ti Allāhu, lẹ́yìn tí àwọn aláìgbàgbọ́ ti ṣàbòsí sí wọn, dájúdájú Àwa yóò wá ibùgbé rere fún wọn. Ẹ̀san Ọjọ́ Ìkẹ́yìn sì tóbi jùlọ, tí ó bá jẹ́ pé (àwọn tí kò ṣe hijrah) mọ̀.');
INSERT INTO "aykyuni" VALUES(16,42,'16:42','(Àwọn ni) àwọn tó ṣe sùúrù, wọ́n sì ń gbáralé Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(16,43,'16:43','A kò rán ẹnì kan ní iṣẹ́-òjíṣẹ́ ṣíwájú rẹ àfi àwọn ọkùnrin tí À ń fi ìmísí ránṣẹ́ sí. Nítorí náà, ẹ bèèrè lọ́dọ̀ àwọn oníràn-ántí tí ẹ̀yin kò bá mọ̀.1 ');
INSERT INTO "aykyuni" VALUES(16,44,'16:44','(A fi) àwọn ẹ̀rí tó yanjú àti ìpín-ìpín Tírà (rán wọn níṣẹ́). Àti pé A sọ tírà Ìrántí (ìyẹn, al-Ƙur’ān) kalẹ̀ fún ọ nítorí kí o lè ṣàlàyé fún àwọn ènìyàn ohun tí A sọ̀kalẹ̀ fún wọnàti nítorí kí wọ́n lè ronú jinlẹ̀. ');
INSERT INTO "aykyuni" VALUES(16,45,'16:45','Ṣé àwọn tó dá ète aburú fi ọkàn balẹ̀ pé Allāhu kò lè mú ilẹ̀ ri mọ́ wọn lẹ́sẹ̀ ni, tàbí pé ìyà kò lè dé bá wọn láti àyè tí wọn kò ti níí fura?');
INSERT INTO "aykyuni" VALUES(16,46,'16:46','Tàbí (Allāhu) kò lè gbá wọn mú lórí ìrìnkè-rindò wọn ni? Wọn kò sì níí mórí bọ́.');
INSERT INTO "aykyuni" VALUES(16,47,'16:47','Tàbí (Allāhu) kò lè máa gbá wọn mú díẹ̀díẹ̀ ni?Nítorí náà, dájúdájú Olúwa yin má ni Aláàánú, Oníkẹ̀ẹ́. ');
INSERT INTO "aykyuni" VALUES(16,48,'16:48','Àti pé ṣé wọn kò wòye sí gbogbo n̄ǹkan tí Allāhu ṣẹ̀dá rẹ̀, tí òòji rẹ̀ ń tẹ̀ sí ọ̀tún àti òsìtí wọ́n ń forí kanlẹ̀ fún Allāhu; tí wọ́n sì ń yẹpẹrẹ ara wọn (fún Un)? ');
INSERT INTO "aykyuni" VALUES(16,49,'16:49','Allāhu sì ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ ní n̄ǹkan abẹ̀mí àti àwọn mọlāika ń forí kanlẹ̀ fún. Wọn kò sì ṣègbéraga.1 ');
INSERT INTO "aykyuni" VALUES(16,50,'16:50','Wọ́n ń páyà Olúwa wọn tó ń bẹ lókè wọn. Wọ́n sì ń ṣe ohun tí À ń pa láṣẹ fún wọn.1 ');
INSERT INTO "aykyuni" VALUES(16,51,'16:51','Allāhu sọ pé: “Ẹ má ṣe jọ́sìn fún ọlọ́hun méjì. Òun nìkan ni Ọlọ́hun Ọ̀kan ṣoṣo tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo. Nítorí náà, Èmi (nìkan) ni kí ẹ bẹ̀rù.”');
INSERT INTO "aykyuni" VALUES(16,52,'16:52','TiRẹ̀ sì ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀. TiRẹ̀ sì ni (àfọ̀mọ́) ẹ̀sìn títí láéláéNítorí náà, ṣé (n̄ǹkan mìíràn) yàtọ̀ sí Allāhu ni ẹ̀yin yóò máa bẹ̀rù ni? ');
INSERT INTO "aykyuni" VALUES(16,53,'16:53','Ohunkóhun tí ẹ ní nínú ìdẹ̀ra, láti ọ̀dọ̀ Allāhu ni. Lẹ́yìn náà, tí ọwọ́ ìnira bá tẹ̀ yín, Òun ni ẹ̀ ń ké pè (fún ìdáǹdè.)');
INSERT INTO "aykyuni" VALUES(16,54,'16:54','Lẹ́yìn náà, nígbà tí Ó bá mú ìnira náà kúrò fún yín tán, nígbà náà ni apá kan nínú yín yó sì máa ṣẹbọ sí Olúwa wọn');
INSERT INTO "aykyuni" VALUES(16,55,'16:55','nítorí kí wọ́n lè ṣàì moore sí n̄ǹkan tí A fún wọn. Nítorí náà, ẹ máa gbádùn ǹsó. Láìpẹ́ ẹ máa mọ̀.');
INSERT INTO "aykyuni" VALUES(16,56,'16:56','Wọ́n ń fi ìpín kan nínú ohun tí A pèsè fún wọn lélẹ̀ fún ohun tí wọn kò nímọ̀ nípa rẹ̀.Mo fi Allāhu búra, dájúdájú wọn yóò bi yín léèrè nípa ohun tí ẹ̀ ń dá ní àdápa irọ́. ');
INSERT INTO "aykyuni" VALUES(16,57,'16:57','Wọ́n tún ń fi (bíbí) àwọn ọmọbìnrin lélẹ̀ fún Allāhu - Mímọ́ ni fún Un (kò bímọ). - Wọ́n sì ń fi ohun tí wọ́n ń fẹ́ lélẹ̀ fún tiwọn!');
INSERT INTO "aykyuni" VALUES(16,58,'16:58','Nígbà tí wọ́n bá fún ọ̀kan nínú wọn ní ìró ìdùnnú (pé ó bí) ọmọbìnrin, ojú rẹ̀ yóò di dúdú fún ìbànújẹ́, tí ó sì máa pa á mọ́ra.');
INSERT INTO "aykyuni" VALUES(16,59,'16:59','Ó sì máa fi ara pamọ́ fún àwọn ènìyàn nítorí ìró aburú tí wọ́n fún un. Ṣé ó máa gbà a mú ní n̄ǹkan àbùkù ni tàbí ó máa bò ó mọ́lẹ̀ láàyè? Ẹ gbọ́, ohun tí wọ́n ń dá lẹ́jọ́ burú!');
INSERT INTO "aykyuni" VALUES(16,60,'16:60','Ti àwọn tí kò gbàgbọ́ nínú Ọjọ́ Ìkẹ́yìn ni àkàwé aburú. Ti Allāhu sì ni àkàwé tó ga jùlọ. Àti pé Òun ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(16,61,'16:61','Tí ó bá jẹ́ pé Allāhu ń gbá àwọn ènìyàn mú nítorí àbòsí ọwọ́ wọn, ìbá tí ṣẹ́ abẹ̀mí kan kan kù sórí ilẹ̀. Ṣùgbọ́n Ó ń lọ́ wọn lára di gbèdéke àkókò kan. Nígbà tí àkókò náà bá dé, wọn kò níí lè sún un ṣíwájú di àkókò kan, wọn kò sì níí lè fà á sẹ́yìn.”1 ');
INSERT INTO "aykyuni" VALUES(16,62,'16:62','Wọ́n ń fi (ọmọbìnrin) - n̄ǹkan tí wọn kórira - lélẹ̀ fún Allāhu. Ahọ́n wọn sì ń ròyìn irọ́ pé dájúdájú rere (Ọgbà Ìdẹ̀ra) ni tàwọn. Kò sí tàbí-ṣùgbọ́n, dájúdájú Iná ni tiwọn. Àti pé dájúdájú wọ́n máa pa wọ́n tì sínú rẹ̀ ni.');
INSERT INTO "aykyuni" VALUES(16,63,'16:63','Allāhu fi ara Rẹ̀ búra,dájúdájú A ti rán àwọn Òjíṣẹ́ níṣẹ́ sí àwọn ìjọ kan ṣíwájú rẹ. Nígbà náà, Èṣù ṣe iṣẹ́ wọn ní ọ̀ṣọ́ fún wọn; òun sì ni ọ̀rẹ́ wọn ní òní. Ìyà ẹlẹ́ta-eléro sì wà fún wọn. ');
INSERT INTO "aykyuni" VALUES(16,64,'16:64','A kò sọ Tírà náà kalẹ̀ fún ọ bí kò ṣe pé kí o lè ṣàlàyé fún wọn ohun tí wọ́n ń yapa ẹnu sí;ó sì jẹ́ ìmọ̀nà àti ìkẹ́ fún ìjọ onígbàgbọ́ òdodo. ');
INSERT INTO "aykyuni" VALUES(16,65,'16:65','Allāhu l’Ó ń sọ omi kalẹ̀ láti sánmọ̀. Ó sì ń fi jí ilẹ̀ lẹ́yìn tí ó ti kú. Dájúdájú àmì wà nínú ìyẹn fún ìjọ tó ń gbọ́rọ̀ (òdodo).');
INSERT INTO "aykyuni" VALUES(16,66,'16:66','Dájúdájú àríwòye wà fún yín lára àwọn ẹran-ọ̀sìn, tí À ń fún yín mu nínú n̄ǹkan tó wà nínú rẹ̀, (èyí) tí ó ń jáde wá láti ààrin bọ́tọ inú agbẹ̀du àti ẹ̀jẹ̀. (Ó sì ń di) wàrà mímọ́ tó ń lọ tìnrín ní ọ̀fun àwọn tó ń mu ún.');
INSERT INTO "aykyuni" VALUES(16,67,'16:67','Àti pé láti ara àwọn èso dàbínù àti èso àjàrà ni ẹ ti ń ṣe ọtíàti n̄ǹkan jíjẹ-mímu tó dára. Dájúdájú àmì wà nínú ìyẹn fún ìjọ tó ní làákàyè. ');
INSERT INTO "aykyuni" VALUES(16,68,'16:68','Olúwa rẹ sì fi mọ kòkòrò oyin pé: “Mu ilé sínú àpáta, (mú un sínú) igi, (kí ó sì mú un) sínú ohun tí (àwọn ènìyàn) mọ ga.');
INSERT INTO "aykyuni" VALUES(16,69,'16:69','Lẹ́yìn náà, jẹ lára gbogbo èso, kí o sì tọ àwọn ojú ọ̀nà Olúwa rẹ pẹ̀lú ìtẹríba.” Ohun mímu tí àwọ̀ rẹ̀ yàtọ̀ síra wọn yóò máa jáde láti inú kòkòrò oyin. Ìwòsàn wà nínú rẹ̀ fún àwọn ènìyàn. Dájúdájú àmì wà nínú ìyẹn fún ìjọ tó ní àròjinlẹ̀.');
INSERT INTO "aykyuni" VALUES(16,70,'16:70','Allāhu l’Ó ṣẹ̀dá yín. Lẹ́yìn náà, Ó ń gba ẹ̀mí yín. Ó sì wà nínú yín ẹni tí A óò dá padà sí ogbó kùjọ́kùjọ́ nítorí kí ó má lè mọ n̄ǹkan kan mọ́ lẹ́yìn tí ó ti mọ̀ ọ́n. Dájúdájú Allāhu ni Onímọ̀, Alágbára.');
INSERT INTO "aykyuni" VALUES(16,71,'16:71','Allāhu l’Ó ṣoore àjùlọ fún apá kan yín lórí apá kan nínú arísìkí. Àwọn tí A fún ní oore àjùlọ, kí wọ́n fún àwọn ẹrú wọn ní arísìkí wọn, kí wọ́n sì jọ pín in ní dọ́gban̄n̄dọ́gba!Nítorí náà, ṣe ìdẹ̀ra Allāhu ni wọn yóò máa takò? ');
INSERT INTO "aykyuni" VALUES(16,72,'16:72','Àti pé Allāhu ṣe àwọn ìyàwó fún yín láti ara yín. Ó fún yín ní àwọn ọmọ àti ọmọọmọ láti ara àwọn ìyàwó yín. Ó sì pèsè arísìkí fún yín nínú àwọn n̄ǹkan dáadáa. Ṣé irọ́ (ìyẹn, òrìṣà) ni wọn yóò gbàgbọ́, ìdẹ̀ra Allāhu ni wọn yó sì máa ṣàì moore sí?');
INSERT INTO "aykyuni" VALUES(16,73,'16:73','Dípò (kí wọ́n jọ́sìn fún) Allāhu, wọ́n ń jọ́sìn fún ohun tí kò ní ìkápá arísìkí kan kan fún wọn nínú àwọn sánmọ̀ àti ilẹ̀; wọn kò sì lágbára (láti ṣe n̄ǹkan kan).');
INSERT INTO "aykyuni" VALUES(16,74,'16:74','Nítorí náà, ẹ má ṣe fi àwọn àkàwé náà lélẹ̀ nípa Allāhu. Dájúdájú Allāhu nímọ̀; ẹ̀yin kò sì nímọ̀.');
INSERT INTO "aykyuni" VALUES(16,75,'16:75','Allāhu fi àkàwé kan lélẹ̀ (nípa) ẹrú kan tí ó wà lábẹ́ ọ̀gá, tí kò sì lè dá n̄ǹkan kan ṣe àti ẹni tí A fún ní arísìkí tó dára láti ọ̀dọ̀ wa, tí ó sì ń ná nínú rẹ̀ ní ìkọ̀kọ̀ àti ní gban̄gba. Ṣé wọ́n dọ́gba bí?Gbogbo ẹyìn ń jẹ́ ti Allāhu. Ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò mọ̀. ');
INSERT INTO "aykyuni" VALUES(16,76,'16:76','Allāhu tún fi àkàwé kan lélẹ̀ (nípa) ọkùnrin méjì kan, tí ọ̀kan nínú wọn jẹ́ odi, tí kò lè dá n̄ǹkan kan ṣe, tí ó tún jẹ́ wàhálà fún ọ̀gá rẹ̀ (nítorí pé) ibikíbi tí ó bá rán an lọ, kò níí mú oore kan bọ̀ (fún un láti ibẹ̀). Ṣé ó dọ́gba pẹ̀lú ẹni tí Ó ń pàṣẹ ṣíṣe ẹ̀tọ́, tí ó sì wà lójú ọ̀nà tààrà?1 ');
INSERT INTO "aykyuni" VALUES(16,77,'16:77','Ti Allāhu ni ìkọ̀kọ̀ àwọn sánmọ̀ àti ilẹ̀. Ọ̀rọ̀ Àkókò náà kò kúkú tayọ ìṣẹ́jú tàbí kí ó tún súnmọ́ julọ. Dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(16,78,'16:78','Allāhu l’Ó mu yín jáde láti inú ikùn àwọn ìyá yín nígbà tí ẹ̀yin kò tí ì dá n̄ǹkan kan mọ̀. Ó sì ṣe ìgbọ́rọ̀, àwọn ìríran àti àwọn ọkàn fún yín nítorí kí ẹ lè dúpẹ́ (fún Un).');
INSERT INTO "aykyuni" VALUES(16,79,'16:79','Ṣé wọn kò wòye sí àwọn ẹyẹ tí A rọ̀ (fún fífò) nínú òfurufú (lábẹ́) sánmọ̀? Kò sí ẹni tó ń mú wọn dúró (sínú òfurufú) bí kò ṣe Allāhu. Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(16,80,'16:80','Allāhu ṣe ibùsinmi fún yín sínú ilé yín. Láti ara awọ ẹran-ọ̀sìn, Ó tún ṣe àwọn ilé (àtíbàbà) kan tó fúyẹ́ fún yín láti gbé rìn ní ọjọ́ ìrìn-àjò yín àti ní ọjọ́ tí ẹ bá wà nínú ìlú. Láti ara irun àgùtàn,irun ràkúnmí àti irun ewúrẹ́, ẹ tún ń rí àwọn n̄ǹkan ọ̀ṣọ́ àti n̄ǹkan ìgbádùn lò títí fún ìgbà díẹ̀. ');
INSERT INTO "aykyuni" VALUES(16,81,'16:81','Allāhu ṣe àwọn ibòji fún yín láti ara àwọn n̄ǹkan tí Ó dá. Ó ṣe àwọn ibùgbé fún yín láti ara àwọn àpáta. Ó tún ṣe àwọn aṣọ fún yín tó ń dáàbò bò yín lọ́wọ́ ooru àti àwọn aṣọ tó ń dáàbò bò yín lójú ogun yín.Báyẹn l’Ó ṣe ń pé ìdẹ̀ra Rẹ̀ le yín lórí nítorí kí ẹ lè jẹ́ mùsùlùmí. ');
INSERT INTO "aykyuni" VALUES(16,82,'16:82','Nítorí náà, tí wọ́n bá gbúnrí, ìkéde (ẹ̀sìn) tó yanjú ni ojúṣe tìrẹ.');
INSERT INTO "aykyuni" VALUES(16,83,'16:83','Wọ́n mọ ìdẹ̀ra Allāhu, lẹ́yìn náà wọ́n ń takò ó. Ọ̀pọ̀lọ́pọ̀ wọn sì ni aláìmoore.');
INSERT INTO "aykyuni" VALUES(16,84,'16:84','(Rántí) ọjọ́ tí A óò gbé ẹlẹ́rìí kan dìde nínú gbogbo ìjọ (àwọn Ànábì), lẹ́yìn náà, A ò níí yọ̀ǹda (àròyé ṣíṣe) fún àwọn tó ṣàì gbàgbọ́. Wọn kò sì níí fún wọn ní àyè láti padà ṣe ohun tí wọn yóò fi rí ìyọ́nú Allāhu.');
INSERT INTO "aykyuni" VALUES(16,85,'16:85','Nígbà tí àwọn tó ṣàbòsí bá rí Ìyà, nígbà náà, A ò níí ṣe ìyà náà ní fífúyẹ́ fún wọn, A ò sì níí fún wọn ní ìsinmi (nínú Iná).');
INSERT INTO "aykyuni" VALUES(16,86,'16:86','Nígbà tí àwọn tó bá Allāhu wá akẹgbẹ́ bá rí àwọn òrìṣà wọn, wọ́n á wí pé: “Olúwa wa, àwọn wọ̀nyí ni àwọn òrìṣà wa tí à ń pè lẹ́yìn Rẹ.” Nígbà náà (àwọn òrìṣà) yóò ju ọ̀rọ̀ náà padà sí wọ́n pé: “Dájúdájú òpùrọ́ mà ni ẹ̀yin.”');
INSERT INTO "aykyuni" VALUES(16,87,'16:87','Wọ́n sì máa jura wọn sílẹ̀ fún Allāhu ní ọjọ́ yẹn. Ohun tí wọ́n ń dá ní àdápa irọ́ sì di òfo mọ́ wọn lọ́wọ́.1 ');
INSERT INTO "aykyuni" VALUES(16,88,'16:88','Àwọn tó ṣàì gbàgbọ́, tí wọ́n sì ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu, A ó ṣe àlékún ìyà lórí ìyà fún wọn ni nítorí pé wọ́n ń ṣèbàjẹ́.');
INSERT INTO "aykyuni" VALUES(16,89,'16:89','(Rántí) Ọjọ́ tí A óò gbé ẹlẹ́rìí dìde fún ìjọ kọ̀ọ̀kan láààrin ara wọn, A sì máa mú ìwọ wá ní ẹlẹ́rìí fún àwọn wọ̀nyí. A sọ Tírà kalẹ̀ fún ọ; (ó jẹ́) àlàyé fún gbogbo n̄ǹkan,ìmọ̀nà, ìkẹ́ àti ìró ìdùnnú fún àwọn mùsùlùmí. ');
INSERT INTO "aykyuni" VALUES(16,90,'16:90','Dájúdájú Allāhu ń pàṣẹ ṣíṣe déédé, ṣíṣe rere àti fífún ẹbí (ní n̄ǹkan). Ó sì ń kọ ìwà ìbàjẹ́, ohun burúkú àti rúkèrúdò. Ó ń ṣe wáàsí fún yín nítorí kí ẹ lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(16,91,'16:91','Kí ẹ sì mú àdéhùn Allāhu ṣẹ nígbà tí ẹ bá ṣe àdéhùn. Ẹ má ṣe tú ìbúra lẹ́yìn ìfirinlẹ̀ rẹ̀. Ẹ sì kúkú ti fi Allāhu ṣe Ẹlẹ́rìí lórí ara yín. Dájúdájú Allāhu mọ ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(16,92,'16:92','Kí ẹ sì má ṣe dà bí (obìnrin) èyí tí ó tú òwú dídì rẹ̀ palẹ̀ yẹ́lẹyẹ̀lẹ lẹ́yìn tí ó tí dì í le. Ńṣe ni ẹ̀ ń lo ìbúra yín fún ẹ̀tàn láààrin ara yín nítorí pé ìran kan pọ̀ ju ìran kan. Dájúdájú Allāhu ń dan yín wò pẹ̀lú rẹ̀ ni. Àti pé ní Ọjọ́ Àjíǹde Ó kúkú máa ṣàlàyé fún yín ohun tí ẹ̀ ń yapa ẹnu sí.');
INSERT INTO "aykyuni" VALUES(16,93,'16:93','Àti pé tí ó bá jẹ́ pé Allāhu bá fẹ́, ìbá ṣe yín ní ìjọ kan ṣoṣo (sínú ’Islām), ṣùgbọ́n Ó ń ṣi ẹni tí Ó bá fẹ́ lọ́nà, Ó sì ń tọ́ ẹni tí Ó bá fẹ́ sọ́nà. Dájúdájú wọ́n máa bi yín léèrè nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(16,94,'16:94','Ẹ má ṣe lo ìbúra yín fún ẹ̀tàn láààrin ara yín, nítorí kí ẹsẹ̀ yín má baà yẹ̀ gẹ̀rẹ̀ lẹ́yìn ìdúróṣinṣin rẹ̀, àti nítorí kí ẹ má baà tọ́ ìyà burúkú wò nípa bí ẹ ṣe ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu. Ìyà ńlá yó sì wà fún yín.');
INSERT INTO "aykyuni" VALUES(16,95,'16:95','Ẹ má ṣe ta àdéhùn Allāhu ní owó pọ́ọ́kú. Ohun tí ń bẹ lọ́dọ̀ Allāhu, ó lóore jùlọ fún yín tí ẹ bá mọ̀.');
INSERT INTO "aykyuni" VALUES(16,96,'16:96','Ohun tó ń bẹ lọ́dọ̀ yín máa tán. Ohun tó ń bẹ lọ́dọ̀ Allāhu sì máa wà títí láéláé. Dájúdájú A sì máa san àwọn tó ṣe sùúrù ní ẹ̀san rere wọn tó dára ju ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(16,97,'16:97','Ẹnikẹ́ni tí ó bá ṣe iṣẹ́ rere, ọkùnrin ni tàbí obìnrin, ó sì jẹ́ onígbàgbọ́ òdodo, dájúdájú A óò jẹ́ kí ó lo ìgbésí ayé tó dára. Dájúdájú A sì máa san wọ́n ní ẹ̀san rere wọn tó dára ju ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(16,98,'16:98','Nígbà tí o bá (fẹ́) ké al-Ƙur’ān, sá di Allāhu níbi (aburú) Èṣù, ẹni ẹ̀kọ̀.');
INSERT INTO "aykyuni" VALUES(16,99,'16:99','Dájúdájú kò sí agbára kan fún un lórí àwọn tó gbàgbọ́, tí wọ́n sì ń gbáralé Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(16,100,'16:100','Ẹni tó lágbára lórí rẹ̀ ni àwọn tó ń mú un ní ọ̀rẹ́ àti àwọn tó sọ ọ́ di akẹgbẹ́ Allāhu.');
INSERT INTO "aykyuni" VALUES(16,101,'16:101','Nígbà tí A bá pààrọ̀ āyah kan sí àyè āyah kan, Allāhu l’Ó sì nímọ̀ jùlọ nípa ohun tó ń sọ̀kalẹ̀, wọ́n á wí pé: “Ìwọ kàn jẹ́ aládapa irọ́ ni.” Àmọ́ ọ̀pọ̀lọ́pọ̀ wọn ni kò nímọ̀.');
INSERT INTO "aykyuni" VALUES(16,102,'16:102','Sọ pé: “Ẹ̀mí mímọ́ (mọlāika Jibrīl) ló sọ̀ ọ́ kalẹ̀ láti ọ̀dọ̀ Olúwa rẹ pẹ̀lú òdodo nítorí kí ó lè mú ẹsẹ̀ àwọn tó gbàgbọ́ lódodo rinlẹ̀. (Kí ó sì lè jẹ́) ìmọ̀nà àti ìró ìdùnnú fún àwọn mùsùlùmí.');
INSERT INTO "aykyuni" VALUES(16,103,'16:103','A sì kúkú ti mọ̀ pé dájúdájú wọ́n ń wí pé: “Ṣebí abara kan ló ń kọ́ ọ (ní al-Ƙur’ān).” Èdè ẹni tí wọ́n ń darí al-Ƙur’ān kọ ní ọ̀nà òdì (pé ó ń kọ́ Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -) kì í ṣe elédè Lárúbáwá. Èdè Lárúbáwá pọ́nńbélé sì ni (al-Ƙur’ān) yìí.1 ');
INSERT INTO "aykyuni" VALUES(16,104,'16:104','Dájúdájú àwọn tí kò gbàgbọ́ nínú àwọn āyah Allāhu, Allāhu kò níí tọ́ wọn sọ́nà. Ìyà ẹlẹ́ta-eléro sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(16,105,'16:105','Àwọn tí kò gbàgbọ́ nínú àwọn āyah Allāhu ló ń dá àdápa irọ́ (mọ́ Allāhu). Àwọn wọ̀nyẹn gan-an sì ni òpùrọ́.');
INSERT INTO "aykyuni" VALUES(16,106,'16:106','Ẹnikẹ́ni tí ó bá ṣàì gbàgbọ́ nínú Allāhu lẹ́yìn ìgbàgbọ́ rẹ̀, yàtọ̀ sí ẹni tí wọ́n jẹ nípá, tí ọkàn rẹ̀ sì balẹ̀ pẹ̀lú ìgbàgbọ́ òdodo, ṣùgbọ́n ẹnikẹ́ni tí ó bá ṣí igbá-àyà rẹ̀ sílẹ̀ fún àìgbàgbọ́, ìbínú láti ọ̀dọ̀ Allāhu ń bẹ lórí wọn. Ìyà ńlá sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(16,107,'16:107','Ìyẹn nítorí pé wọ́n fẹ́ràn ìṣẹ̀mí ayé ju ọ̀run. Allāhu kò sì níí fi ọ̀nà mọ ìjọ aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(16,108,'16:108','Àwọn wọ̀nyẹn ni àwọn tí Allāhu ti fi èdídí bo ọkàn wọn, ìgbọ́rọ̀ wọn àti ìríran wọn. Àwọn wọ̀nyẹn gan-an ni afọ́núfọ́ra.');
INSERT INTO "aykyuni" VALUES(16,109,'16:109','Kò sí tàbí-ṣùgbọ́n, dájúdájú àwọn ni olófò ní ọ̀run.');
INSERT INTO "aykyuni" VALUES(16,110,'16:110','Lẹ́yìn náà, dájúdájú Olúwa rẹ - nípa àwọn tó gbé ìlú wọn jù sílẹ̀ lẹ́yìn tí àwọn aláìgbàgbọ́ ti kó ìfòòró bá wọn, lẹ́yìn náà, tí wọ́n jagun ẹ̀sìn, tí wọ́n sì ṣe sùúrù - dájúdájú Olúwa rẹ ni Aláforíjìn, Àṣàkẹ́-ọ̀run (fún wọn) lẹ́yìn (ìfòòró tí wọ́n rí lọ́dọ̀ àwọn aláìgbàgbọ́).');
INSERT INTO "aykyuni" VALUES(16,111,'16:111','(Rántí) Ọjọ́ tí ẹ̀mí kọ̀ọ̀kan yóò dé tí yóò máa rojọ́ gbe ara rẹ̀. A ó sì san ẹ̀mí kọ̀ọ̀kan ní ẹ̀san ohun tí ó ṣe níṣẹ́; wọn kò sì níí ṣàbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(16,112,'16:112','Allāhu fi àkàwé lélẹ̀ nípa ìlú kan, tí ó jẹ́ (ìlú) ààbò àti ìfàyàbalẹ̀, tí arísìkí rẹ̀ ń tẹ̀ ẹ́ lọ́wọ́ ní púpọ̀ ní gbogbo àyè. Àmọ́ (wọ́n) ṣe àìmoore sí àwọn ìdẹ̀ra Allāhu. Nítorí náà, Allāhu fún wọn ní ìyà ebi àti ìpáyà tọ́ wò nítorí ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(16,113,'16:113','Òjíṣẹ́ kúkú ti dé bá wọn láààrin ara wọn. Wọ́n sì pè é ní òpùrọ́. Nítorí náà, ọwọ́ ìyà bà wọ́n. Alábòsí sì ni wọ́n.');
INSERT INTO "aykyuni" VALUES(16,114,'16:114','Ẹ jẹ nínú ohun tí Allāhu pa lésè fún yín ní n̄ǹkan ẹ̀tọ́, tó dára. Kí ẹ sì dúpẹ́ oore Allāhu tí ó bá jẹ́ pé Òun nìkan ṣoṣo ni ẹ̀ ń jọ́sìn fún ní ọ̀nà òdodo.');
INSERT INTO "aykyuni" VALUES(16,115,'16:115','Ohun tí (Allāhu) ṣe ní èèwọ̀ fún yín ni ẹran òkúǹbete, ẹ̀jẹ̀, ẹran ẹlẹ́dẹ̀ àti èyí tí wọ́n pa pẹ̀lú orúkọ tó yàtọ́ sí “Allāhu”. Ṣùgbọ́n ẹni tí ìnira (ebi) bá mú (jẹ ẹran èèwọ̀), tí kì í ṣe ẹni tó ń wá èèwọ̀ kiri àti olùtayọ-ẹnu-ààlà, dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(16,116,'16:116','Ẹ má ṣe sọ nípa ohun tí ahọ́n yín ròyìn ní irọ́ pé: “Èyí ni ẹ̀tọ́, èyí sì ni èèwọ̀” nítorí kí ẹ lè dá àdápa irọ́ mọ́ Allāhu. Dájúdájú àwọn tó ń dá àdápa irọ́ mọ́ Allāhu, wọn kò níí jèrè.');
INSERT INTO "aykyuni" VALUES(16,117,'16:117','Ìgbádùn ayé bín-íntín (lè wà fún wọn, àmọ́) ìyà ẹlẹ́ta-eléro wà fún wọn (ní ọ̀run).');
INSERT INTO "aykyuni" VALUES(16,118,'16:118','Àti pé A ṣe ohun tí A sọ fún ọ ṣíwájú ní èèwọ̀ fún àwọn tó di yẹhudi.A kò sì ṣàbòsí sí wọn, ṣùgbọ́n àwọn ni wọ́n n ṣàbòsí sí ẹ̀mí ara wọn. ');
INSERT INTO "aykyuni" VALUES(16,119,'16:119','Lẹ́yìn náà, dájúdájú Olúwa rẹ - nípa àwọn tó ṣe aburú pẹ̀lú àìmọ̀kan, lẹ́yìn náà, tí wọ́n ronú pìwàdà lẹ́yìn ìyẹn, tí wọ́n sì ṣe àtúnṣe - lẹ́yìn rẹ̀ (lẹ́yìn ìronú-pìwàdà) dájúdájú Olúwa rẹ mà ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(16,120,'16:120','Dájúdájú (Ànábì) ’Ibrọ̄hīm jẹ́ aṣíwájú-àwòkọ́ṣe-rere, olùtẹ̀lé-àṣẹ Allāhu, olùdúró-déédé-nínú-ẹ̀sìn. Kò sì jẹ́ ara àwọn ọ̀ṣẹbọ.');
INSERT INTO "aykyuni" VALUES(16,121,'16:121','Ó máa ń dúpẹ́ àwọn ìdẹ̀ra Allāhu. Allāhu ṣà á lẹ́ṣà. Ó sì tọ́ ọ sí ọ̀nà tààrà (’Islām).');
INSERT INTO "aykyuni" VALUES(16,122,'16:122','A ṣe ohun rere fún un nílé ayé. Àti pé ní ọ̀run dájúdájú ó máa wà nínú àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(16,123,'16:123','Lẹ́yìn náà, A fi ìmísí ránṣẹ́ sí ọ pé kí o tẹ̀lé ẹ̀sìn (Ànábì) ’Ibrọ̄hīm, olùdúró-déédé-nínú-ẹ̀sìn. Kò sì wà lára àwọn ọ̀ṣẹbọ.');
INSERT INTO "aykyuni" VALUES(16,124,'16:124','Àwọn tí wọ́n ṣe (àgbéga) ọjọ́ Sabt fún ni àwọn tó yapa-ẹnu nípa lórí rẹ̀.Dájúdájú Olúwa rẹ yóò kúkú ṣe ìdájọ́ láààrin wọn ní Ọjọ́ Àjíǹde nípa ohun tí wọ́n ń yapa-ẹnu lórí rẹ̀. ');
INSERT INTO "aykyuni" VALUES(16,125,'16:125','Pèpè sí ojú ọ̀nà Olúwa rẹ pẹ̀lú ọgbọ́n ìjìnlẹ̀ (ìyẹn, al-Ƙur’ān àti sunnah Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -) àti wáàsí rere. Kí o sì jà wọ́n níyàn pẹ̀lú èyí tó dára jùlọ. Dájúdájú Olúwa rẹ, Òun l’Ó nímọ̀ jùlọ nípa ẹni tí ó ṣìnà kúrò lójú ọ̀nà Rẹ̀ (’Islām). Òun l’Ó sì nímọ̀ jùlọ nípa àwọn olùmọ̀nà (àwọn mùsùlùmí).');
INSERT INTO "aykyuni" VALUES(16,126,'16:126','Tí ẹ (bá fẹ́) gbẹ̀san (ìyà), ẹ gbẹ̀san irú ìyà tí wọ́n fi jẹ yín. Dájúdájú tí ẹ bá sì ṣe sùúrù, òhun ló mà lóore jùlọ fún àwọn onísùúrù.');
INSERT INTO "aykyuni" VALUES(16,127,'16:127','Ṣe sùúrù. Ìwọ kò sì lè rí sùúrù ṣe àfi pẹ̀lú (ìrànlọ́wọ́) Allāhu. Má ṣe banújẹ́ nítorí wọn. Má sì ṣe wà nínú ìbànújẹ́ nítorí ohun tí wọ́n ń dá ní ète.');
INSERT INTO "aykyuni" VALUES(16,128,'16:128','Dájúdájú Allāhu wà pẹ̀lú àwọn tó bẹ̀rù (Rẹ̀) àti àwọn tó ń ṣe rere.');
INSERT INTO "aykyuni" VALUES(17,1,'17:1','Mímọ́ ni fún Ẹni tí Ó mú ẹrúsìn Rẹ̀ ṣe ìrìn-àjò ní alẹ́ láti Mọ́sálásí Haram sí Mọ́sálásí Aƙsọ̄ tí A fi ìbùkún yí ká, nítorí kí Á lè fi nínú àwọn àmì Wa hàn án. Dájúdájú Allāhu, Òun ni Olùgbọ́, Olùríran.1 ');
INSERT INTO "aykyuni" VALUES(17,2,'17:2','A fún (Ànábì) Mūsā ní Tírà. A sì ṣe é ní ìmọ̀nà fún àwọn ọmọ ’Isrọ̄’īl. (A sì sọ) pé: “Ẹ má ṣe mú aláààbò kan lẹ́yìn Mi.”');
INSERT INTO "aykyuni" VALUES(17,3,'17:3','(Ẹ jẹ́) àrọ́mọdọ́mọ àwọn tí A gbé gun ọkọ̀ ojú-omi pẹ̀lú (Ànábì) Nūh. Dájúdájú (Nūh) jẹ́ ẹrúsìn, olùdúpẹ́.');
INSERT INTO "aykyuni" VALUES(17,4,'17:4','A sì fi mọ àwọn ọmọ ’Isrọ̄’īl nínú Tírà pé: “Dájúdájú ẹ máa ṣèbàjẹ́ lórí ilẹ̀ nígbà mejì.Dájúdájú ẹ tún máa ṣègbéraga tó tóbi.” ');
INSERT INTO "aykyuni" VALUES(17,5,'17:5','Nítorí náà, nígbà tí àdéhùn fún àkọ́kọ́ nínú méjèèjì bá ṣẹlẹ̀,A máa gbé àwọn ẹrúsìn Wa kan, tí wọ́n lágbára gan-an, dìde si yín. Wọn yóò dá rògbòdìyàn sílẹ̀ láààrin ìlú (yín). Ó jẹ́ àdéhùn kan tí A máa mú ṣẹ. ');
INSERT INTO "aykyuni" VALUES(17,6,'17:6','Lẹ́yìn náà, A dá ìṣẹ́gun lórí wọn padà fún yín. A sì fi àwọn dúkìá àti àwọn ọmọ ṣèrànwọ́ fún yín. A sì ṣe yín ní ìjọ tó pọ̀ jùlọ.');
INSERT INTO "aykyuni" VALUES(17,7,'17:7','Tí ẹ bá ṣe rere, ẹ ṣe rere fún ẹ̀mí ara yín. Tí ẹ bá sì ṣe aburú, fún ẹ̀mí ara yín ni. Nígbà tí àdéhùn ìkẹ́yìn bá dé, (A óò gbé ọmọ ogun mìíràn dìde) nítorí kí wọ́n lè kó ìbànújẹ́ ba yín àti nítorí kí wọ́n lè wọ inú Mọ́sálásí gẹ́gẹ́ bí wọ́n ṣe wọ̀ ọ́ nígbà àkọ́kọ́ àti nítorí kí wọ́n lè pa ohun tí (àwọn ọmọ ’Isrọ̄’īl) jọba lórí rẹ̀ run pátápátá.');
INSERT INTO "aykyuni" VALUES(17,8,'17:8','Ó lè jẹ́ pé Olúwa yín máa ṣààánú yín. Tí ẹ bá sì padà (síbi ẹ̀ṣẹ̀), A máa padà (síbi ẹ̀san). A sì ṣe iná Jahanamọ ní ẹ̀wọ̀n fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(17,9,'17:9','Dájúdájú al-Ƙur’ān yìí, ó ń fini mọ̀nà sí ọ̀nà tààrà, ó sì ń fún àwọn onígbàgbọ́ òdodo tó ń ṣe àwọn iṣẹ́ rere ní ìró ìdùnnú pé dájúdájú ẹ̀san tó tóbi wà fún wọn.');
INSERT INTO "aykyuni" VALUES(17,10,'17:10','Dájúdájú àwọn tí kò gba Ọjọ́ Ìkẹ́yìn gbọ́, A ti pèsè ìyà ẹlẹ́ta-eléro sílẹ̀ dè wọ́n.');
INSERT INTO "aykyuni" VALUES(17,11,'17:11','Ènìyàn ń tọrọ aburú bí ó ṣe ń tọrọ ohun rere; ènìyàn sì jẹ́ olùkánjú.1 ');
INSERT INTO "aykyuni" VALUES(17,12,'17:12','A ṣe alẹ́ àti ọ̀sán ní àmì méjì; A pa àmì alẹ́ rẹ́, A sì ṣe àmì ọ̀sán ní ìrírannítorí kí ẹ lè wá oore àjùlọ láti ọ̀dọ̀ Olúwa yín àti nítorí kí ẹ lè mọ òǹkà àwọn ọdún àti ìṣírò (àwọn ọjọ́). Gbogbo n̄ǹkan ni A ti ṣàlàyé rẹ̀ ní ìfọ́síwẹ́wẹ́.2 ');
INSERT INTO "aykyuni" VALUES(17,13,'17:13','Ènìyàn kọ̀ọ̀kan, A ti so iṣẹ́ ọwọ́ rẹ̀ mọ́ ọn lọ́rùn. A sì máa mú ìwé kan jáde fún un ní Ọjọ́ Àjíǹde. Ó máa pàdé rẹ̀ ní ṣíṣí sílẹ̀.');
INSERT INTO "aykyuni" VALUES(17,14,'17:14','Ka ìwé (iṣẹ́) rẹ. Ìwọ ti tó ní olùṣírò fún ẹ̀mí ara rẹ lónìí.');
INSERT INTO "aykyuni" VALUES(17,15,'17:15','Ẹnikẹ́ni tó bá mọ̀nà, ó mọ̀nà fún ẹ̀mí ara rẹ̀. Ẹnikẹ́ni tó bá sì ṣìnà, ó ń ṣìnà fún ẹ̀mí ara rẹ̀. Ẹlẹ́rù-ẹ̀ṣẹ̀ kan kò níí ru ẹ̀ṣẹ̀ ẹlòmíìràn.A ò sì níí jẹ àwọn ẹ̀dá níyà títí A fi máa gbé òjíṣẹ́ kan dìde (sí wọn). ');
INSERT INTO "aykyuni" VALUES(17,16,'17:16','Nígbà tí A bá sì gbèrò láti pa ìlú kan run, A máa pa àwọn onígbẹdẹmukẹ ìlú náà láṣẹ (rere). Àmọ́ wọ́n máa ṣèbàjẹ́ sínú ìlú. Ọ̀rọ̀ náà yó sì kò lé wọn lórí. A ó sì pa wọ́n rẹ́ pátápátá.1 ');
INSERT INTO "aykyuni" VALUES(17,17,'17:17','Mélòó mélòó nínú àwọn ìran tí A ti parun lẹ́yìn (Ànábì) Nūh! Olúwa rẹ tó ní Alámọ̀tán, Olùríran nípa àwọn ẹ̀ṣẹ̀ ẹrúsìn Rẹ̀.');
INSERT INTO "aykyuni" VALUES(17,18,'17:18','Ẹni tí ó bá ń gbèrò (oore) ayé yìí (nìkan), A máa taari ohun tí A bá fẹ́ sí i nínú rẹ̀ ní kíákíá fún ẹni tí A bá fẹ́. Lẹ́yìn náà, A máa ṣe iná Jahanamọ fún un; ó máa wọ inú rẹ̀ ní ẹni yẹpẹrẹ, ẹni ẹ̀kọ̀.');
INSERT INTO "aykyuni" VALUES(17,19,'17:19','Ẹni tí ó bá sì gbèrò (oore) ọ̀run, tí ó sì ṣe iṣẹ́ rẹ̀ fún ọ̀run náà, ó sì jẹ́ onígbàgbọ́ òdodo, àwọn wọ̀nyẹn, wọ́n máa fi ẹ̀san rere àdìpèlé dúpẹ́ iṣẹ́ wọn fún wọn.');
INSERT INTO "aykyuni" VALUES(17,20,'17:20','Àwọn wọ̀nyí (tó ń gbèrò oore ayé) àti àwọn wọ̀nyí (tó ń gbèrò oore ọ̀run), gbogbo wọn ni À ń ṣe oore ayé fún láti inú ọrẹ Olúwa rẹ. Wọn kò níí dí ọrẹ Olúwa rẹ lọ́wọ́ (fún ìkíní kejì nílé ayé).');
INSERT INTO "aykyuni" VALUES(17,21,'17:21','Wo bí A ṣe fún apá kan wọn lóore àjùlọ lórí apá kan (nílé ayé). Dájúdájú tọ̀run tún tóbi jùlọ ní ipò, ó sì tóbi jùlọ lóore àjùlọ.');
INSERT INTO "aykyuni" VALUES(17,22,'17:22','Má ṣe mú ọlọ́hun mìíràn mọ́ Allāhu nítorí kí o má baà jókòó (sínú Iná) ní ẹni àbùkù, ẹni ẹ̀kọ̀ (tí wọ́n máa dá ìṣòro rẹ̀ dá).');
INSERT INTO "aykyuni" VALUES(17,23,'17:23','Olúwa rẹ pàṣẹ pé: “Ẹ má ṣe jọ́sìn fún ẹnì kan àyàfi Òun. Kí ẹ sì ṣe dáadáa sí àwọn òbí (yín) méjèèjì. Tí ọ̀kan nínú àwọn méjèèjì tàbí ìkíní kejì wọn bá dàgbà sí ọ lọ́dọ̀, má ṣe ṣíọ̀ sí wọn, má ṣe jágbe mọ́ wọn. Máa bá àwọn méjèèjì sọ ọ̀rọ̀ àpọ́nlé.');
INSERT INTO "aykyuni" VALUES(17,24,'17:24','Rẹ apá rẹ nílẹ̀ dáadáa (bọ̀wọ̀) fún àwọn méjèèjì nípa ṣíṣe àánú wọn. Kí o sì sọ pé: “Olúwa Ẹlẹ́dàá mi, kẹ́ àwọn méjèèjì nítorí pé, àwọn méjèèjì náà tọ́ mi ní kékeré.”');
INSERT INTO "aykyuni" VALUES(17,25,'17:25','Olúwa yín nímọ̀ jùlọ nípa ohun tí ń bẹ nínú ẹ̀mí yín, tí ẹ bá jẹ́ ẹni rere. Dájúdájú Ó ń jẹ́ Aláforíjìn fún àwọn olùronúpìwàdà.');
INSERT INTO "aykyuni" VALUES(17,26,'17:26','Ẹ fún ẹbí ní ẹ̀tọ́ rẹ̀. (Ẹ fún) mẹ̀kúnnù àti onírìn-àjò (tí agara dá ní n̄ǹkan). Kí ẹ sì má ṣe ná dúkìá yín ní ìná-àpà.');
INSERT INTO "aykyuni" VALUES(17,27,'17:27','Dájúdájú àwọn àpà, wọ́n jẹ́ ọmọ ìyá Èṣù. Aláìmoore sì ni Èṣù jẹ́ sí Olúwa rẹ̀.');
INSERT INTO "aykyuni" VALUES(17,28,'17:28','Tí o bá ṣẹ́rí kúrò lọ́dọ̀ wọn (ìyẹn àwọn aláìní) láti wá ìkẹ́ kan tí ò ń retí láti ọ̀dọ̀ Olúwa rẹ, bá wọn sọ ọ̀rọ̀ ẹ̀lẹ̀.');
INSERT INTO "aykyuni" VALUES(17,29,'17:29','Má di ọwọ́ rẹ mọ́ ọrùn rẹ (má ya ahun), má sì tẹ́ ẹ sílẹ̀ tán pátápátá (má ya àpà), kí o má baà jókòó kalẹ̀ ní ẹni èébú (tí o bá jẹ́ ahun), ẹni tí kò níí sí lọ́wọ́ rẹ̀ mọ́ (tí o bá jẹ́ àpà).');
INSERT INTO "aykyuni" VALUES(17,30,'17:30','Dájúdájú Olúwa rẹ l’Ó ń tẹ́ arísìkí sílẹ̀ fún ẹni tí Ó bá fẹ́. Ó sì ń díwọ̀n rẹ̀ (fún ẹlòmíìràn). Dájúdájú Ó ń jẹ́ Onímọ̀-ìkọ̀kọ̀, Olùríran nípa àwọn ẹrúsìn Rẹ̀.');
INSERT INTO "aykyuni" VALUES(17,31,'17:31','Ẹ má pa àwọn ọmọ yín nítorí ìpáyà òṣì. Àwa l’À ń pèsè fún àwọn àti ẹ̀yin. Dájúdájú pípa wọ́n jẹ́ ẹ̀ṣẹ̀ tó tóbi.');
INSERT INTO "aykyuni" VALUES(17,32,'17:32','Ẹ má ṣe súnmọ́ àgbèrè. Dájúdájú ó jẹ́ ìwà burúkú. Ó sì burú ní ojú ọ̀nà.');
INSERT INTO "aykyuni" VALUES(17,33,'17:33','Ẹ má pa ẹ̀mí tí Allāhu ṣe (pípa rẹ̀) ní èèwọ̀ àyàfi ní ọ̀nà ẹ̀tọ́.Ẹni tí wọ́n bá pa ní ìpa àbòsí, dájúdájú A ti fún alámòjúútó rẹ̀ ní agbára (láti gbẹ̀san). Nítorí náà, kí ẹnikẹ́ni má ṣe tayọ ẹnu-ààlà níbi ìpànìyàn (nítorí pé) dájúdájú A máa ran (ẹbí òkú) lọ́wọ́ (láti gbẹ̀san).2 ');
INSERT INTO "aykyuni" VALUES(17,34,'17:34','Ẹ má ṣe súnmọ́ dúkìá ọmọ òrukàn, àyàfi ní ọ̀nà tó dára jùlọ, títí ó fi máa dàgbà dáadáa. Kí ẹ sì mú àdéhùn ṣẹ. Dájúdájú àdéhùn jẹ́ ohun tí A ó bèèrè nípa rẹ̀.');
INSERT INTO "aykyuni" VALUES(17,35,'17:35','Ẹ wọn òṣùwọ̀n kún nígbà tí ẹ bá wọ̀n ọ́n. Ẹ fi ìwọ̀n tó tọ́wọ̀n ọ́n. Ìyẹn lóore jùlọ, ó sì dára jùlọ ní ìkángun. ');
INSERT INTO "aykyuni" VALUES(17,36,'17:36','Má ṣe tẹ̀lé ohun tí ìwọ kò nímọ̀ nípa rẹ̀. Dájúdájú ìgbọ́rọ̀, ìríran àti ọkàn; ìkọ̀ọ̀kan ìwọ̀nyẹn ni A óò bèèrè nípa rẹ̀.');
INSERT INTO "aykyuni" VALUES(17,37,'17:37','Má ṣe rìn lórí ilẹ̀ pẹ̀lú ìgbéraga; dájúdájú ìwọ kò lè dá ilẹ̀ lu, ìwọ kò sì lè ga tó àpáta.');
INSERT INTO "aykyuni" VALUES(17,38,'17:38','Gbogbo ìyẹn, aburú rẹ̀ jẹ́ ohun ìkórira lọ́dọ̀ Olúwa rẹ.');
INSERT INTO "aykyuni" VALUES(17,39,'17:39','Ìyẹn wà nínú ohun tí Olúwa rẹ fi ránṣẹ́ sí ọ nínú ìjìnlẹ̀ ọgbọ́n (al-Ƙur’ān). Má ṣe mú ọlọ́hun mìíràn mọ́ Allāhu kí wọ́n má baà jù ọ́ sínú iná Jahanamọ ní ẹni àbùkù, ẹni ẹ̀kọ̀.');
INSERT INTO "aykyuni" VALUES(17,40,'17:40','Ṣé Olúwa yín fi àwọn ọmọkùnrin ṣà yín lẹ́ṣà, Òun wá mú àwọn ọmọbìnrin nínú àwọn mọlāika (ní tiRẹ̀)? Dájúdájú ẹ mà ń sọ ọ̀rọ̀ ńlá (tó burú).');
INSERT INTO "aykyuni" VALUES(17,41,'17:41','Dájúdájú A ti ṣe àlàyé sínú al- Ƙur’ān yìí nítorí kí wọ́n lè lo ìṣítí. Síbẹ̀síbẹ̀ kò ṣe àlékún kan fún wọn bí kò ṣe sísá (fún ìrántí).');
INSERT INTO "aykyuni" VALUES(17,42,'17:42','Sọ pé: “Tí ó bá jẹ́ pé àwọn ọlọ́hun kan tún wà pẹ̀lú Rẹ̀ gẹ́gẹ́ bí wọ́n ṣe ń wí, wọn ìbá wá ọ̀nà láti súnmọ́ (Allāhu) Onítẹ̀ẹ́-ọlá ni.”1 ');
INSERT INTO "aykyuni" VALUES(17,43,'17:43','Mímọ́ ni fún Un. Ó sì ga ní gíga tó tóbi tayọ ohun tí wọ́n ń wí (ní àìda nípa Rẹ̀).');
INSERT INTO "aykyuni" VALUES(17,44,'17:44','Àwọn sánmọ̀ méjèèje, ilẹ̀ àti àwọn tí wọ́n wà nínú wọn ń ṣe àfọ̀mọ́ fún Un. Kò sì sí kiní kan àfi kí ó máa ṣe àfọ̀mọ́ pẹ̀lú ẹyìn fún Un.Ṣùgbọ́n ẹ kò lè gbọ́ àgbọ́yé àfọ̀mọ́ wọn. Dájúdájú Allāhu ń jẹ́ Aláfaradà, Aláforíjìn. ');
INSERT INTO "aykyuni" VALUES(17,45,'17:45','Nígbà tí o bá ń ké al-Ƙur’ān, A máa fi gàgá ààbò sáààrin ìwọ àti àwọn tí kò gbàgbọ́ nínú Ọjọ́ Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(17,46,'17:46','A fi èbìbò bo ọkàn wọn nítorí kí wọ́n má baà gbọ́ ọ yé. (A tún fi) èdídí sínú etí wọn. Nígbà tí o bá sì dárúkọ Olúwa rẹ nìkan ṣoṣo nínú al-Ƙur’ān, wọ́n máa pẹ̀yìndà tí wọn yóò máa sá lọ.');
INSERT INTO "aykyuni" VALUES(17,47,'17:47','Àwa nímọ̀ jùlọ nípa ohun tí wọ́n ń gbọ́ nígbà tí wọ́n bá ń tẹ́tí sí ọ àti nígbà tí wọ́n bá ń sọ̀rọ̀ kẹ́lẹ́kẹ́lẹ́, nígbà tí àwọn alábòsí bá ń wí pé: “Ẹ kò tẹ̀lé ẹnì kan bí kò ṣe ọkùnrin eléèdì kan.”');
INSERT INTO "aykyuni" VALUES(17,48,'17:48','Wo bí wọ́n ṣe fún ọ ní àwọn àfiwé (burúkú)! Nítorí náà, wọ́n ti ṣìnà; wọn kò sì lè mọ̀nà.');
INSERT INTO "aykyuni" VALUES(17,49,'17:49','Wọ́n wí pé: “Ṣé nígbà tí a bá ti di egungun, tí a ti jẹrà, ṣé Wọ́n máa gbé wa dìde ní ẹ̀dá titun ni?”');
INSERT INTO "aykyuni" VALUES(17,50,'17:50','Sọ pé: “Ẹ di òkúta tàbí irin.');
INSERT INTO "aykyuni" VALUES(17,51,'17:51','Tàbí (kí ẹ di) ẹ̀dá kan nínú ohun tí ó tóbi nínú ọkàn yín.” Síbẹ̀síbẹ̀ wọn yóò wí pé: “Ta ni Ó máa dá wa padà (fún àjíǹde)?” Sọ pé: “Ẹni tí Ó pilẹ̀ ìṣẹ̀dá yín ní ìgbà àkọ́kọ́ ni.” Síbẹ̀síbẹ̀ wọn yóò mi orí wọn sí ọ (ní ti àbùkù). Nígbà náà, wọn yóò wí pé: “Ìgbà wo ni?” Sọ pé: “Ó lè jẹ́ pé ó ti súnmọ́.”');
INSERT INTO "aykyuni" VALUES(17,52,'17:52','Ọjọ́ tí Allāhu yóò pè yín. Ẹ sì máa jẹ́pè pẹ̀lú ẹyìn Rẹ̀. Ẹ sì máa lérò pé ẹ kò gbé ilé ayé bí kò ṣe fún ìgbà díẹ̀.');
INSERT INTO "aykyuni" VALUES(17,53,'17:53','Sọ fún àwọn ẹrúsìn Mi pé kí wọ́n máa sọ èyí tó dára jùlọ (nínú ọ̀rọ̀). Dájúdájú Èṣù yóò máa dá yánpọn-yánrin sílẹ̀ láààrin wọn. Dájúdájú Èṣù jẹ́ ọ̀tá pọ́nńbélé fún ènìyàn.');
INSERT INTO "aykyuni" VALUES(17,54,'17:54','Olúwa yín nímọ̀ jùlọ nípa yín. Tí Ó bá fẹ́, Ó máa kẹ yín. Tàbí tí Ó bá sì fẹ́, Ó máa jẹ yín níyà. A kò rán ọ pé kí o jẹ́ olùṣọ́ lórí wọn.');
INSERT INTO "aykyuni" VALUES(17,55,'17:55','Olúwa rẹ nímọ̀ jùlọ nípa àwọn tó ń bẹ nínú àwọn sánmọ̀ àti ilẹ̀. Dájúdájú A ti ṣe oore àjùlọ fún apá kan àwọn Ànábì lórí apá kan. A sì fún (Ànábì) Dāwūd ní Zabūr.');
INSERT INTO "aykyuni" VALUES(17,56,'17:56','Sọ pé: “Ẹ pe àwọn tí ẹ sọ nípa wọn - láì ní ẹ̀rí lọ́wọ́ - pé wọ́n jẹ́ ọlọ́hun lẹ́yìn Rẹ̀, (ẹ máa rí i pé) wọn kò ní ìkápá láti gbé ìnira kúrò tàbí láti ṣe ìyípadà kan fún yín.”');
INSERT INTO "aykyuni" VALUES(17,57,'17:57','Àwọn wọ̀nyẹn tí wọ́n ń pè (lẹ́yìn Rẹ̀) ń wá àtẹ̀gùn sọ́dọ̀ Olúwa wọn ni! - Èwo nínú wọn ló súnmọ́ (Allāhu) jùlọ (báyìí)? - Àwọn náà ń retí ìkẹ́ Allāhu, wọ́n sì ń páyà ìyà Rẹ̀. Dájúdájú ìyà Olúwa rẹ jẹ́ ohun tí wọ́n gbọ́dọ̀ ṣọ́ra fún.');
INSERT INTO "aykyuni" VALUES(17,58,'17:58','Kò sí ìlú kan (tó ṣàbòsí) àfi kí Àwa pa á rẹ́ ṣíwájú Ọjọ́ Àjíǹde tàbí kí Á jẹ ẹ́ níyà líle. Ìyẹn ti wà ní kíkọ sílẹ̀ nínú Tírà (Laohul-Mahfūṭḥ).');
INSERT INTO "aykyuni" VALUES(17,59,'17:59','Àti pé kò sí ohun tí ó dí Wa lọ́wọ́ láti fi àwọn àmì (iṣẹ́ ìyanu) ránṣẹ́ bí kò ṣe pé àwọn ẹni àkọ́kọ́ ti pè é nírọ́. A fún ìjọ Thamūd ní abo ràkúnmí; (àmì) tó fojú hàn kedere ni. Àmọ́ wọ́n ṣàbòsí sí i. A ò sì níí fi àwọn àmì ránṣẹ́ bí kò ṣe fún ìdẹ́rùbà.');
INSERT INTO "aykyuni" VALUES(17,60,'17:60','(Rántí) nígbà tí A sọ fún ọ pé: “Dájúdájú Olúwa rẹ yí àwọn ènìyàn po (pẹ̀lú agbára Rẹ̀). Àti pé A kò ṣe ìran (wíwò) tí A fi hàn ọ́ àti igi (zaƙūm) tí A ṣẹ́bi lé nínú al-Ƙur’ān ní kiní kan bí kò ṣe pé (ó jẹ́) àdánwò fún àwọn ènìyàn. À ń dẹ́rù bà wọ́n, àmọ́ kò ṣe àlékún kan fún wọn bí kò ṣe ìwà àgbéré tó tóbi.1 ');
INSERT INTO "aykyuni" VALUES(17,61,'17:61','(Rántí) nígbà tí A sọ fún àwọn mọlāika pé: “Ẹ forí kanlẹ̀ kí (Ànábì) Ādam.” Wọ́n sì forí kanlẹ̀ kí i àfi ’Iblīs. Ó wí pé: “Ṣé kí n̄g forí kanlẹ̀ kí ẹni tí O fi ẹrẹ̀ ṣẹ̀dá rẹ̀ ni?”');
INSERT INTO "aykyuni" VALUES(17,62,'17:62','Ó wí pé: “Sọ fún mi nípa èyí tí O gbé àpọ́nlé fún lórí mí, (nítorí kí ni?) Dájúdájú tí O bá lè lọ́ mi lára di Ọjọ́ Àjíǹde, dájúdájú mo máa jẹgàba lórí àwọn àrọ́mọdọ́mọ rẹ̀ àfi àwọn díẹ̀.”');
INSERT INTO "aykyuni" VALUES(17,63,'17:63','(Allāhu) sọ pé: “Máa lọ. Ẹnikẹ́ni tí ó bá tẹ̀lé ọ nínú wọn, dájúdájú iná Jahanamọ ni ẹ̀san yín. (Ó sì jẹ́) ẹ̀san tó kún kẹ́kẹ́.');
INSERT INTO "aykyuni" VALUES(17,64,'17:64','Fi ohùn rẹ kó ẹni tí agbára rẹ bá ká nínú wọn láyà jẹ. Fi àwọn ọmọ ogun ẹlẹ́sin rẹ àti ọmọ ogun ẹlẹ́sẹ̀ rẹ pè wọ́n (sínú ìṣìnà). Kópa pẹ̀lú wọn nínú àwọn dúkìá àti àwọn ọmọ.Kí o sì ṣe àdéhùn fún wọn.” Èṣù kò sì níí ṣe àdéhùn kan fún wọn bí kò ṣe ẹ̀tàn. 2 ');
INSERT INTO "aykyuni" VALUES(17,65,'17:65','Dájúdájú àwọn ẹrúsìn Mi, kò sí agbára kan fún ọ lórí wọn. Olúwa rẹ sì tó ní Olùṣọ́.');
INSERT INTO "aykyuni" VALUES(17,66,'17:66','Olúwa yín ni Ẹni tó ń mú ọkọ̀ ojú-omi rìn fún yín nítorí kí ẹ lè wá nínú oore àjùlọ Rẹ̀. Dájúdájú Ó ń jẹ́ Aláàánú fún yín.');
INSERT INTO "aykyuni" VALUES(17,67,'17:67','Nígbà tí ìnira bá fọwọ́ bà yín lójú omi, ẹni tí ẹ̀ ń pè yó sì dòfo (mọ yín lọ́wọ́) àfi Òun nìkan (Allāhu). Nígbà ti Ó bá sì gbà yín là (tí ẹ) gúnlẹ̀ sórí ilẹ̀, ẹ̀ ń gbúnrí (kúrò lọ́dọ̀ Rẹ̀). Ènìyàn sì jẹ́ aláìmoore.');
INSERT INTO "aykyuni" VALUES(17,68,'17:68','Ṣé ẹ fọkàn balẹ̀ pé (Allāhu) kò lè mú apá kan ilẹ̀ ri mọ yín lẹ́sẹ̀ ni? Tàbí pé kò lè fi òkúta iná ránṣẹ́ si yín? Lẹ́yìn náà, ẹ̀ ko níí rí olùṣọ́ kan tí ó máa gbà yín là.');
INSERT INTO "aykyuni" VALUES(17,69,'17:69','Tàbí ẹ fọkàn balẹ̀ pé (Allāhu) kò níí padà mu yín wá sí (orí omi) nígbà mìíràn ni; tí Ó máa rán ìjì atẹ́gùn si yín, tí Ó sì máa tẹ̀ yín rì (sínú omi) nítorí pé ẹ ṣàì moore? Lẹ́yìn náà, ẹ ò sì níí rí olùrànlọ́wọ́ kan tó máa ba yín gbẹ̀san lára Wa.');
INSERT INTO "aykyuni" VALUES(17,70,'17:70','Dájúdájú A ṣe àpọ́nlé fún àwọn ọmọ (Ànábì) Ādam; A gbé wọn rìn lórí ilẹ̀ àti lórí omi; A fún wọn ní ìjẹ-ìmu nínú àwọn n̄ǹkan dáadáa; A sì ṣoore àjùlọ fún wọn gan-an lórí ọ̀pọ̀lọ́pọ̀ nínú àwọn tí A dá.');
INSERT INTO "aykyuni" VALUES(17,71,'17:71','Ní ọjọ́ tí A óò máa pe gbogbo ènìyàn pẹ̀lú aṣíwájú wọn Nígbà náà, ẹnikẹ́ni tí A bá fún ní ìwé (iṣẹ́) rẹ̀ ní ọwọ́ ọ̀tún rẹ̀, àwọn wọ̀nyẹn ni wọn yóò máa ka ìwé (iṣẹ́) wọn. A ò sì níí fi ìbójú kóró èso dàbínú ṣàbòsí sí wọn. ');
INSERT INTO "aykyuni" VALUES(17,72,'17:72','Ẹnikẹ́ni tí ó bá jẹ́ afọ́jú (nípa ’Islām) nílé ayé yìí, òun ni afọ́jú ní ọ̀run. Ó sì (ti) ṣìnà jùlọ.');
INSERT INTO "aykyuni" VALUES(17,73,'17:73','Wọ́n fẹ́ẹ̀ kó ìyọnu bá ọ nípa n̄ǹkan tí A mú wá fún ọ ní ìmísí nítorí kí o lè hun n̄ǹkan mìíràn nípa Wa. Nígbà náà, wọn ìbá mú ọ ní ọ̀rẹ́ àyò.');
INSERT INTO "aykyuni" VALUES(17,74,'17:74','Tí kò bá jẹ́ pé A fi ẹsẹ̀ rẹ rinlẹ̀ ni, dájúdájú o fẹ́ẹ̀ fi n̄ǹkan díẹ̀ tẹ̀ sí ọ̀dọ̀ wọn.');
INSERT INTO "aykyuni" VALUES(17,75,'17:75','Nígbà náà, Àwa ìbá jẹ́ kí o tọ́ ìlọ́po (ìyà) ìṣẹ̀mí ayé àti ìlọ́po (ìyà lẹ́yìn) ikú wò. Lẹ́yìn náà, ìwọ kò níí rí olùrànlọ́wọ́ kan tí ó máa gbà ọ́ sílẹ̀ níbi ìyà Wa.');
INSERT INTO "aykyuni" VALUES(17,76,'17:76','Wọ́n fẹ́ẹ̀ pin ọ́ lẹ́mìí nínú ìlú nítorí kí wọ́n lè lé ọ jáde kúrò nínú rẹ̀. Nígbà náà, àwọn náà kò níí wà nínú rẹ̀ lẹ́yìn rẹ tayọ ìgbà díẹ̀.');
INSERT INTO "aykyuni" VALUES(17,77,'17:77','(Èyí jẹ́) ìṣe (Allāhu) lórí àwọn tí A ti rán níṣẹ́ nínú àwọn Òjíṣẹ́ Wa ṣíwájú rẹ. O ò sì níí rí ìyípadà kan fún ìṣe Wa.');
INSERT INTO "aykyuni" VALUES(17,78,'17:78','Gbé ìrun kíkí dúró ní (ìgbà tí) òòrùn bá yẹ̀tàrí títí di ìgbà tí alẹ́ yóò fi lẹ́. Àti pé ìrun Subh, dájúdájú ìrun Subh jẹ́ ohun tí (àwọn mọlāika alẹ́ àti ọ̀sán) yóò jẹ́rìí sí.1 ');
INSERT INTO "aykyuni" VALUES(17,79,'17:79','Àti pé nínú òru, fi kíké al-Ƙur’ān (lórí ìrun) ṣàì sùn. (Èyí jẹ́) iṣẹ́ àṣegbọrẹ fún ọ. Láìpẹ́, Olúwa rẹ yóò gbé ọ sí ipò kan tí àwọn ẹ̀dá máa ti yìn ọ́.1 ');
INSERT INTO "aykyuni" VALUES(17,80,'17:80','Sọ pé: “Olúwa mi, mú mi wọ inú ìlú ní ìwọ̀lú ọ̀nà òdodo. Mú mi jáde kúrò nínú ìlú ní ìjáde ọ̀nà òdodo. Kí O sì fún mi ní àrànṣe tó lágbára láti ọ̀dọ̀ Rẹ.');
INSERT INTO "aykyuni" VALUES(17,81,'17:81','Sọ pé: “Òdodo dé. Irọ́ sì sá lọ. Dájúdájú irọ́ jẹ́ ohun tí ó máa sá lọ.”');
INSERT INTO "aykyuni" VALUES(17,82,'17:82','À ń sọ ohun tí ó jẹ́ ìwòsàn àti ìkẹ́ kalẹ̀ sínú al-Ƙur’ān fún àwọn onígbàgbọ́ òdodo. (al-Ƙur’ān) kò sì ṣàlékún fún àwọn alábòsí bí kò ṣe òfò.');
INSERT INTO "aykyuni" VALUES(17,83,'17:83','Nígbà tí A bá ṣèdẹ̀ra fún ènìyàn, ó máa gbúnrí (kúrò níbi òdodo), ó sì máa takété (sí àṣẹ Allāhu). Nígbà tí aburú bá sì fọwọ́ bà á, ó máa di olùsọ̀rètínù.');
INSERT INTO "aykyuni" VALUES(17,84,'17:84','Sọ pé: “Ẹnì kọ̀ọ̀kan ń ṣiṣẹ́ lórí àdámọ́ rẹ̀. Ṣùgbọ́n Olúwa yín nímọ̀ jùlọ nípa ẹni tí ó mọ̀nà.”');
INSERT INTO "aykyuni" VALUES(17,85,'17:85','Wọ́n ń bi ọ́ léèrè nípa ẹ̀mí. Sọ pé: “Ẹ̀mí ń bẹ nínú àṣẹ Olúwa mi. A ò sì fún yín nínú ìmọ̀ bí kò ṣe díẹ̀.”');
INSERT INTO "aykyuni" VALUES(17,86,'17:86','Àti pé dájúdájú tí A bá fẹ́ ni, A ìbá kúkú gba ìmísí tí A fi ránṣẹ́ sí ọ (kúrò ní ọ̀dọ̀ rẹ). Lẹ́yìn náà, ìwọ kò níí rí aláàbò kan tí ó máa là ọ́ lọ́dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(17,87,'17:87','Àyàfi ìkẹ́ láti ọ̀dọ̀ Olúwa rẹ (ni kò fi ṣe bẹ́ẹ̀). Dájúdájú oore àjùlọ Rẹ̀ lórí rẹ tóbi.');
INSERT INTO "aykyuni" VALUES(17,88,'17:88','Sọ pé: “Dájúdájú tí àwọn ènìyàn àti àlùjànnú bá parapọ̀ láti mú irú al-Ƙur’ān yìí wá, wọn kò lè mú irú rẹ̀ wá, apá kan wọn ìbáà jẹ́ olùrànlọ́wọ́ fún apá kan.”1 ');
INSERT INTO "aykyuni" VALUES(17,89,'17:89','Dájúdájú A ti fi àkàwé kọ̀ọ̀kan ṣàlàyé (oríṣiríṣi) fún àwọn ènìyàn sínú al-Ƙur’ān yìí. Síbẹ̀síbẹ̀ ọ̀pọ̀lọ́pọ̀ ènìyàn kọ̀ láti gbà àfi àtakò sá.');
INSERT INTO "aykyuni" VALUES(17,90,'17:90','Wọ́n sì wí pé: “A kò níí gbàgbọ́ nínú rẹ títí o máa fi mú omi ìṣẹ́lẹ̀rú ṣẹ́yọ fún wa.');
INSERT INTO "aykyuni" VALUES(17,91,'17:91','Tàbí kí o ní ọgbà oko dàbínù àti ọgbà oko àjàrà, tí o sì máa jẹ́ kí àwọn odò ṣàn kọ já dáadáa láààrin wọn.');
INSERT INTO "aykyuni" VALUES(17,92,'17:92','Tàbí kí o jẹ́ kí sánmọ̀ já lù wá ní kélekèle gẹ́gẹ́ bí o ṣe sọ (pé Allāhu lè ṣe bẹ́ẹ̀). Tàbí kí o mú Allāhu àti àwọn mọlāika wá (bá wa) ní ojúkojú.');
INSERT INTO "aykyuni" VALUES(17,93,'17:93','Tàbí kí o ní ilé wúrà kan, tàbí kí o gun òkè sánmọ̀ lọ. A kò sì níí gba gígun-sánmọ̀ rẹ gbọ́ títí o fi máa sọ tírà kan tí a óò máa ké kalẹ̀ fún wa.” Sọ pé: “Mímọ́ ni fún Olúwa mi, ǹjẹ́ mo jẹ́ kiní kan bí kò ṣe Òjíṣẹ́ abara?”');
INSERT INTO "aykyuni" VALUES(17,94,'17:94','Kò sí ohun tí ó kọ̀ fún àwọn ènìyàn láti gbàgbọ́ ní òdodo nígbà tí ìmọ̀nà dé bá wọn àfi kí wọ́n wí pé: “Ṣé Allāhu rán Òjíṣẹ́ abara níṣẹ́ ni?”');
INSERT INTO "aykyuni" VALUES(17,95,'17:95','Sọ pé: “Tí ó bá jẹ́ pé àwọn mọlāika wà lórí ilẹ̀ ayé, tí wọ́n ń rìn kiri, (tí wọ́n ń gbé lórí ilẹ̀) pẹ̀lú ìfàyàbalẹ̀, A ìbá sọ mọlāika kan kalẹ̀ fún wọn láti inú sánmọ̀ (láti jẹ́) Òjíṣẹ́.”1 ');
INSERT INTO "aykyuni" VALUES(17,96,'17:96','Sọ pé: “Allāhu tó ní Ẹlẹ́rìí láààrin èmi àti ẹ̀yin. Dájúdájú Ó ń jẹ́ Onímọ̀-ìkọ̀kọ̀, Olùríran nípa àwọn ẹrúsìn Rẹ̀.”');
INSERT INTO "aykyuni" VALUES(17,97,'17:97','Ẹnikẹ́ni tí Allāhu bá fi mọ̀nà (’Islām), òun ni olùmọ̀nà. Ẹnikẹ́ni tí Ó bá ṣì lọ́nà, o kò níí rí àwọn olùrànlọ́wọ́ kan fún wọn lẹ́yìn Rẹ̀. Àti pé A máa kó wọn jọ ní Ọjọ́ Àjíǹde ní ìdojúbolẹ̀. (Wọn yóò di) afọ́jú, ayaya àti odi. Iná Jahanamọ ni ibùgbé wọn. Nígbàkígbà tí Iná bá jó lọọlẹ̀, A óò máa ṣàlékún iná (mìíràn) tó ń jó fún wọn.');
INSERT INTO "aykyuni" VALUES(17,98,'17:98','Ìyẹn ni ẹ̀san wọn nítorí pé, dájúdájú wọ́n ṣàì gbàgbọ́ nínú àwọn āyah Wa. Wọ́n sì wí pé: “Ṣé nígbà tí a bá ti di egungun, tí a sì ti jẹrà, ṣé wọ́n tún máa gbé wa dìde ní ẹ̀dá titun ni?”');
INSERT INTO "aykyuni" VALUES(17,99,'17:99','Ṣé wọn kò wòye pé dájúdájú Allāhu, Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ lágbára láti ṣẹ̀dá irú wọn (mìíràn)? Ó sì máa fún wọn ní gbèdéke àkókò kan, tí kò sí iyèméjì nínú rẹ̀. Síbẹ̀síbẹ̀ àwọn alábòsí kọ̀ láti gbà àfi àtakò sá.');
INSERT INTO "aykyuni" VALUES(17,100,'17:100','Sọ pé: “Tí ó bá jẹ́ pé ẹ̀yin l’ẹ ni ìkápá lórí àwọn ilé ọrọ̀ ìkẹ́ Olúwa mi ni, nígbà náà ẹ̀yin ìbá diwọ́ mọ́ ọn ní ti ìbẹ̀rù òṣì. Ènìyàn sì jẹ́ ahun.');
INSERT INTO "aykyuni" VALUES(17,101,'17:101','Dájúdájú A fún (Ànábì) Mūsā ní àwọn àmì mẹ́sàn-án tó yanjú. Bi àwọn ọmọ ’Isrọ̄’īl léèrè wò. Rántí, nígbà tí ó dé ọ̀dọ̀ wọn, Fir‘aon wí fún un pé: “Dájúdájú èmi lérò pé eléèdì ni ọ́, Mūsā.”');
INSERT INTO "aykyuni" VALUES(17,102,'17:102','(Ànábì Mūsā) sọ pé: “O kúkú mọ̀ pé kò sí ẹni tí ó sọ àwọn (àmì) wọ̀nyí kalẹ̀ bí kò ṣe Olúwa àwọn sánmọ̀ àti ilẹ̀; (ó sì jẹ́) ìmọ̀ àmọ̀dájú àti ẹ̀rí. Dájúdájú èmi náà lérò pé ẹni ìparun ni ọ́, Fir‘aon.”');
INSERT INTO "aykyuni" VALUES(17,103,'17:103','Nítorí náà, (Fir‘aon) fẹ́ kó ìdààmú bá wọn lórí ilẹ̀. A sì tẹ òun àti àwọn tó wà pẹ̀lú rẹ̀ rì pátápátá.');
INSERT INTO "aykyuni" VALUES(17,104,'17:104','A sì sọ fún àwọn ọmọ ’Isrọ̄’īl lẹ́yìn (ikú) rẹ̀ pé: “Ẹ máa gbé orí ilẹ̀ náà. Ṣùgbọ́n nígbà tí Àdéhùn Ìkẹ́yìn bá dé, A óò mú gbogbo yín wá ní àpapọ̀ (ní ọjọ́ ẹ̀san).');
INSERT INTO "aykyuni" VALUES(17,105,'17:105','A sọ al-Ƙur’ān kalẹ̀ pẹ̀lú òdodo. Ó sì sọ̀kalẹ̀ pẹ̀lú òdodo. A kò sì rán ọ níṣẹ́ bí kò ṣe pé (kí o jẹ́) oníròó-ìdùnnú àti olùkìlọ̀.1 ');
INSERT INTO "aykyuni" VALUES(17,106,'17:106','Àti pé al-Ƙur’ān, A ṣàlàyé rẹ̀ (fún ọ) nítorí kí o lè ké e fún àwọn ènìyàn pẹ̀lú pẹ̀lẹ́pẹ̀lẹ́. A sì sọ̀ ọ́ kalẹ̀ díẹ̀ díẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(17,107,'17:107','Sọ pé: “Ẹ gbàgbọ́ nínú al-Ƙur’ān gbọ́ tàbí ẹ kò gbàgbọ́ nínú rẹ̀, dájúdájú àwọn tí A fún ní ìmọ̀ ṣíwájú (ìsọ̀kalẹ̀) rẹ̀, nígbà tí wọ́n bá ń ké e fún wọn, wọn yóò dojú bolẹ̀, tí wọ́n yóò forí kanlẹ̀ (fún Allāhu).');
INSERT INTO "aykyuni" VALUES(17,108,'17:108','Wọ́n ń sọ pé: “Mímọ́ ni fún Olúwa wa. Dájúdájú àdéhùn Olúwa wa máa wá sí ìmúṣẹ ni.”');
INSERT INTO "aykyuni" VALUES(17,109,'17:109','Wọ́n ń dojú bolẹ̀, tí wọ́n yóò máa sunkún (fún Allāhu. Al-Ƙur’ān) sì ń ṣàlékún ìtẹríba fún wọn.');
INSERT INTO "aykyuni" VALUES(17,110,'17:110','Sọ pé: “Ẹ pe Allāhu tàbí ẹ pe ar-Rahmọ̄n (Àjọkẹ́-ayé).”Èyíkéyìí tí ẹ bá fi pè É (nínú rẹ̀), ṣebí tiRẹ̀ ni àwọn orúkọ tó dára jùlọ. Má ṣe kígbe lórí ìrun (àti àdúà) rẹ. Má sì ṣe wà ní ìdákẹ́ rọ́rọ́. Wá ọ̀nà sáààrin méjèèjì yẹn. ');
INSERT INTO "aykyuni" VALUES(17,111,'17:111','Kí o sì sọ pé: “Gbogbo ẹyìn ń jẹ́ ti Allāhu,Ẹni tí kò fi ẹnì kan ṣe ọmọ. Kò sì ní akẹgbẹ́ nínú ìjọba. Kò sì ní ọ̀rẹ́ kan torí àìlágbára.”2 Gbé títóbi fún Un gan-an. ');
INSERT INTO "aykyuni" VALUES(18,1,'18:1','Gbogbo ẹyìn ń jẹ́ ti Allāhu, Ẹni tí Ó sọ Tírà kalẹ̀ fún ẹrúsìn Rẹ̀. Kò sì dojú ọ̀rọ̀ kan rú nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(18,2,'18:2','(Ó ṣe é ni tírà) tó dúró déédénítorí kí (Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -) lè fi ṣe ìkìlọ̀ ìyà líle láti ọ̀dọ̀ (Allāhu) àti nítorí kí ó lè fún àwọn onígbàgbọ́ òdodo, àwọn tó ń ṣe iṣẹ́ rere ní ìró ìdùnnú pé dájúdájú ẹ̀san rere ń bẹ fún wọn (nínú Ọgbà Ìdẹ̀ra); ');
INSERT INTO "aykyuni" VALUES(18,3,'18:3','Wọn yóò máa gbé nínú rẹ̀ lọ títí láéláé.');
INSERT INTO "aykyuni" VALUES(18,4,'18:4','Àti nítorí kí ó lè ṣe ìkìlọ̀ fún àwọn tó wí pé: “Allāhu mú ẹnì kan ní ọmọ.”');
INSERT INTO "aykyuni" VALUES(18,5,'18:5','Kò sí ìmọ̀ kan kan fún àwọn àti bàbá wọn nípa rẹ̀. Ọ̀rọ̀ tó ń jáde lẹ́nu wọn tóbi. Dájúdájú wọn kò wí kiní kan bí kò ṣe irọ́.');
INSERT INTO "aykyuni" VALUES(18,6,'18:6','Nítorí náà, nítorí kí ni o ṣe máa fi ìbànújẹ́ para rẹ lórí ìgbésẹ̀ wọn pé wọn kò gba ọ̀rọ̀ (al-Ƙur’ān) yìí gbọ́.');
INSERT INTO "aykyuni" VALUES(18,7,'18:7','Dájúdájú Àwa ṣe ohun tí ń bẹ lórí ilẹ̀ ní ọ̀ṣọ́ fún ará-ayé nítorí kí Á lè dán wọn wò (pé) èwo nínú wọn ló máa dára jùlọ níbi iṣẹ́ ṣíṣe (nínú ẹ̀sìn).');
INSERT INTO "aykyuni" VALUES(18,8,'18:8','Àti pé dájúdájú Àwa máa sọ n̄ǹkan tí ń bẹ lórí (ilẹ̀) di erùpẹ̀ gbígbẹ tí kò níí hu irúgbìn.');
INSERT INTO "aykyuni" VALUES(18,9,'18:9','Tàbí o lérò pé dájúdájú àwọn ará inú ọ̀gbun àpáta àti wàláhà orúkọ wọn jẹ́ èèmọ̀ kan nínú àwọn àmì Wa?');
INSERT INTO "aykyuni" VALUES(18,10,'18:10','(Rántí) nígbà tí àwọn ọ̀dọ́kùnrin náà wá ibùgbé sínú ọ̀gbun àpáta, wọ́n sọ pé: “Olúwa wa, fún wa ní ìkẹ́ láti ọ̀dọ̀ Rẹ, kí O ṣe ìmọ̀nà ní ìrọ̀rùn fún wa nínú ọ̀rọ̀ wa.”');
INSERT INTO "aykyuni" VALUES(18,11,'18:11','A kùn wọ́n l’óorun àsùnpiyè nínú ọ̀gbun àpáta f’ọ́dún gbọọrọ.');
INSERT INTO "aykyuni" VALUES(18,12,'18:12','Lẹ́yìn náà, A gbé wọn dìde nítorí kí Á lè ṣàfi hàn èwo nínú ìjọ méjèèjì ló mọ gbèdéke òǹkà ọdún tó lò (nínú ọ̀gbun àpáta náà).');
INSERT INTO "aykyuni" VALUES(18,13,'18:13','Àwa ń sọ ìròyìn wọn fún ọ pẹ̀lú òdodo. Dájúdájú ọ̀dọ́kùnrin ni wọ́n. Wọ́n gbàgbọ́ nínú Olúwa wọn. A sì ṣàlékún ìmọ̀nà fún wọn.');
INSERT INTO "aykyuni" VALUES(18,14,'18:14','A sì kì wọ́n lọ́kàn nígbà tí wọ́n dìde, tí wọ́n sọ pé: “Olúwa wa ni Olúwa àwọn sánmọ̀ àti ilẹ̀. A ò sì níí pe ọlọ́hun kan lẹ́yìn Rẹ̀. (Tí a bá pe ọlọ́hun kan lẹ́yìn Rẹ̀) dájúdájú nígbà náà a ti sọ̀rọ̀ tó tayọ ẹnu-ààlà.');
INSERT INTO "aykyuni" VALUES(18,15,'18:15','Àwọn ìjọ wa wọ̀nyí sọ àwọn kan di ọlọ́hun lẹ́yìn Allāhu. Kí ni kò jẹ́ kí wọ́n mú ẹ̀rí tó yanjú wá nípa wọn? Nítorí náà, ta ni ó ṣe àbòsí ju ẹni tó dá àdápa irọ́ mọ́ Allāhu?”');
INSERT INTO "aykyuni" VALUES(18,16,'18:16','(Rántí nígbà tí àwọn ọ̀dọ́kùnrin náà sọ fúnra wọn pé:) nígbà tí ẹ bá yẹra fún àwọn àti n̄ǹkan tí wọ́n ń jọ́sìn fún lẹ́yìn Allāhu, kí ẹ wá ibùgbé sínú ọ̀gbun àpáta, Olúwa yín yóò tẹ́ nínú ìkẹ́ Rẹ̀ sílẹ̀ fún yín. Ó sì máa ṣe ọ̀rọ̀ yín ní ìrọ̀rùn fún yín.');
INSERT INTO "aykyuni" VALUES(18,17,'18:17','O máa rí òòrùn nígbà tí ó bá yọ, ó máa yẹ̀bá kúrò níbi ọ̀gbun wọn sí ọwọ́ ọ̀tún. Nígbà tí ó bá tún wọ̀, ó máa fi wọ́n sílẹ̀ sí ọwọ́ òsì. Wọ́n sì wà nínú àyè tí ó fẹjú nínú ọ̀gbun àpáta. Ìyẹn wà nínú àwọn àmì Allāhu. Ẹnikẹ́ni tí Allāhu bá fi mọ̀nà (’Islām), òun ni olùmọ̀nà. Ẹnikẹ́ni tí Ó bá ṣì lọ́nà, o ò sì níí rí olùrànlọ́wọ́ atọ́ni-sọ́nà kan fún un.');
INSERT INTO "aykyuni" VALUES(18,18,'18:18','(Tí o bá rí wọn), ó máa lérò pé wọ́n kò sí lójú oorun, ojú oorun ni wọ́n sì wà. A sì ń yí wọn ní ẹ̀gbẹ́ padà sí ọ̀tún àti sí òsì. Ajá wọn sì na apá rẹ̀ méjèèjì sílẹ̀ ní ẹnu-ọ̀nà. Tí ó bá jẹ́ pé o yọjú wò wọ́n ni, ìwọ ìbá pẹ̀yìndà láti họ fún wọn, ìwọ ìbá sì kún fún ìbẹ̀rù-bojo láti ara wọn.');
INSERT INTO "aykyuni" VALUES(18,19,'18:19','Báyẹn (ni wọ́n wà) tí A fi gbé wọn dìde padà nítorí kí wọ́n lè bi ara wọn léèrè ìbéèrè. Òǹsọ̀rọ̀ kan nínú wọn sọ pé: “Ìgbà wo lẹ ti wà níbí?” Wọ́n sọ pé: “A wà níbí fún ọjọ́ kan tàbí apá kan nínú ọjọ́.” Wọ́n sọ pé: “Olúwa yín lÓ nímọ̀ jùlọ nípa ìgbà tí ẹ ti wà níbí.” Nítorí náà, ẹ gbé ọ̀kan nínú yín dìde lọ sí inú ìlú pẹ̀lú owó fàdákà yín yìí. Kí ó wo èwó nínú oúnjẹ ìlú ló mọ́ jùlọ, kí ó sì mú àsè wá fún yín nínú rẹ̀. Kí ó ṣe pẹ̀lẹ́pẹ̀lẹ́, kò sì gbọdọ̀ jẹ́ kí ẹnì kan kan fura si yín.');
INSERT INTO "aykyuni" VALUES(18,20,'18:20','(Nítorí pé) dájúdájú tí wọ́n bá fi lè mọ̀ nípa yín, wọn yóò jù yín lókò tàbí kí wọ́n da yín padà sínú ẹ̀sìn wọn. (Tí ó bá sì fi rí bẹ́ẹ̀) nígbà náà, ẹ kò níí jèrè mọ́ láéláé.');
INSERT INTO "aykyuni" VALUES(18,21,'18:21','Báyẹn ni A ṣe jẹ́ kí àwọn ènìyàn rí wọn nítorí kí wọ́n lè mọ̀ pé dájúdájú àdéhùn Allāhu ni òdodo. Àti pé dájúdájú Àkókò náà kò sí iyèméjì nínú rẹ̀. Rántí (nígbà tí àwọn ènìyàn) ń ṣe àríyànjiyàn láààrin ara wọn nípa ọ̀rọ̀ wọn. Wọ́n sọ pé: “Ẹ mọ ilé kan lé wọn lórí. Olúwa wọn lÓ nímọ̀ jùlọ nípa wọn.” Àwọn tó borí lórí ọ̀rọ̀ wọn sì wí pé: “Dájúdájú a máa sọ orí àpáta wọn di mọ́sálásí.”1 ');
INSERT INTO "aykyuni" VALUES(18,22,'18:22','Wọ́n ń wí pé: “Mẹ́ta ni wọ́n. Ajá wọn ṣe ìkẹrin wọn.” Wọ́n tún ń wí pé: “Márùn-ún ni wọ́n. Ajá wọn ṣe ìkẹfà wọn.” Ọ̀rọ̀ tó pamọ́ fún wọn ni wọ́n ń sọ, (wọ́n ń dá àbá ni). Wọ́n tún ń wí pé: “Méje ni wọ́n. Ajá wọn ṣe ìkẹjọ wọn.” Sọ pé: “Olúwa mi lÓ nímọ̀ jùlọ nípa òǹkà wọn. Kò sí (ẹni tí) ó mọ (òǹkà) wọn àfi àwọn díẹ̀. Nítorí náà, má ṣe bá wọn ṣe àríyànjiyàn nípa (òǹkà) wọn àfi (kí o fi) àríyànjiyàn (náà tì síbi ẹ̀rí) tó yanjú (tí A sọ̀kalẹ̀ fún ọ yìí). Má sì ṣe bi ẹnì kan nínú wọn léèrè nípa (òǹkà) wọn.”');
INSERT INTO "aykyuni" VALUES(18,23,'18:23','Má ṣe sọ nípa kiní kan pé: “Dájúdájú èmi yóò ṣe ìyẹn ní ọ̀la.”');
INSERT INTO "aykyuni" VALUES(18,24,'18:24','Àyàfi (kí o fi kún un pé) “tí Allāhu bá fẹ́.” Ṣe ìrántí Olúwa rẹ nígbà tí o bá gbàgbé (láti sọ bẹ́ẹ̀ lásìkò náà). Kí o sì sọ (fún wọn) pé: “Ó rọrùn kí Olúwa mi tọ́ mi sọ́nà lórí rẹ̀ pẹ̀lú èyí tí ó súnmọ́ jù èyí lọ ní ìmọ̀nà (fún yín).”1 ');
INSERT INTO "aykyuni" VALUES(18,25,'18:25','Wọ́n wà nínú ọ̀gbun àpáta wọn fún ọ̀ọ́dúnrún ọdún. Wọ́n tún lo àlékún ọdún mẹ́sàn-án.');
INSERT INTO "aykyuni" VALUES(18,26,'18:26','Sọ pé: “Allāhu lÓ nímọ̀ jùlọ nípa ohun tí wọ́n lò (nínú ọ̀gbun àpáta). TiRẹ̀ ni ìkọ̀kọ̀ àwọn sánmọ̀ àti ilẹ̀. Kì ni kò rí tán, kì sí ni kò gbọ́ tán! Kò sí aláàbò kan fún wọn lẹ́yìn Rẹ̀. Kò sì fi ẹnì kan ṣe akẹgbẹ́ nínú ìdájọ́ Rẹ̀.”');
INSERT INTO "aykyuni" VALUES(18,27,'18:27','Ké ohun tí A fi ránṣẹ́ sí ọ nínú Tírà Olúwa rẹ. Kò sí ẹni tí ó lè yí àwọn ọ̀rọ̀ Rẹ̀ padà. Ìwọ kò sì lè rí ibùsásí kan lẹ́yìn ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(18,28,'18:28','Ṣe sùúrù fún ẹ̀mí rẹ láti wà pẹ̀lú àwọn tó ń pe Olúwa wọn ní òwúrọ̀ àti ní àṣálẹ́, tí wọ́n ń wá Ojú rere Rẹ̀. Má ṣe fojú pa wọ́n rẹ́ láti wá ọ̀ṣọ́ ìṣẹ̀mí ayé (yìí). Má sì ṣe tẹ̀lé ẹni tí A mú ọkàn rẹ̀ gbàgbé ìrántí Wa. Ó tẹ̀lé ìfẹ́-inú rẹ̀; ọ̀rọ̀ rẹ̀ sì jẹ́ àṣejù.1 ');
INSERT INTO "aykyuni" VALUES(18,29,'18:29','Sọ pé: “Òdodo (nìyí) láti ọ̀dọ̀ Olúwa yín. Nítorí náà, ẹni tí ó bá fẹ́ kí ó gbàgbọ́ ní òdodo. Ẹni tí ó bá sì fẹ́ kí ó ṣàì gbàgbọ́. Dájúdájú Àwa pèsè Iná sílẹ̀ de àwọn alábòsí, tí ọgbà rẹ̀ yóò yí wọn po. Tí wọ́n bá ń tọrọ omi mímu, A óò fún wọn ní omi mímu kan tó dà bí ògéré epo gbígbóná, tí (ìgbóná rẹ̀) yó sì máa ṣe àwọn ojú. Ohun mímu náà burú. Ó sì burú ní ibùkójọ.');
INSERT INTO "aykyuni" VALUES(18,30,'18:30','Dájúdájú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, dájúdájú Àwa kò níí fi ẹ̀san ẹni tí ó bá ṣe iṣẹ́ rere ráre.');
INSERT INTO "aykyuni" VALUES(18,31,'18:31','Àwọn wọ̀nyẹn, tiwọn ni àwọn Ọgbà Ìdẹ̀ra ‘Adn, tí àwọn odò yóò máa ṣàn ní ìsàlẹ̀ wọn. Wọn máa ṣe wọ́n ní ọ̀ṣọ́ nínú rẹ̀ pẹ̀lú àwọn n̄ǹkan ọwọ́ látara wúrà. Wọn yóò máa wọ aṣọ àrán aláwọ̀ ewéko (èyí tí ó) fẹ́lẹ́ àti (èyí tí) ó nípọn. Wọn yó sì máa rọ̀gbọ̀kú lórí ibùsùn ọlá nínú (Ọgbà Ìdẹ̀ra). Ẹ̀san náà dára. Ó sì dára ní ibùkójọ.');
INSERT INTO "aykyuni" VALUES(18,32,'18:32','Fi àwọn ọkùnrin méjì kan ṣe àpẹ̀ẹrẹ fún wọn; A fún ọ̀kan nínú wọn ní ọgbà èso àjàrà méjì. A sì fi àwọn igi dàbínù yí wọn ká. A tún fi àwọn igi eléso là wọ́n láààrin.');
INSERT INTO "aykyuni" VALUES(18,33,'18:33','Ìkíní kejì àwọn oko méjèèjì ń so èso wọn. Àwọn èso rẹ̀ kì í pẹ̀ dín. A sì jẹ́ kí odò ṣàn kọjá láààrin oko méjèèjì.');
INSERT INTO "aykyuni" VALUES(18,34,'18:34','Ó ní èso (sẹ́). Ó sì sọ fún ọ̀rẹ́ rẹ̀ nígbà tí ó ń bá a jiyàn (báyìí) pé: “Èmi ní dúkìá lọ́wọ́ jù ọ́ lọ. Mo tún fi àwọn ènìyàn ní agbára jù ọ́ lọ.”');
INSERT INTO "aykyuni" VALUES(18,35,'18:35','Ó wọ inú oko rẹ̀ ní ẹni tí ó ń ṣàbòsí sí ẹ̀mí ara rẹ̀, ó wí pé: “Èmi kò lérò pé èyí máa parun láéláé.');
INSERT INTO "aykyuni" VALUES(18,36,'18:36','Èmi kò sì lérò pé Àkókò náà máa ṣẹlẹ̀. Àti pé tí wọ́n bá tiẹ̀ dá mi padà sí ọ̀dọ̀ Olúwa mi, dájúdájú mo tún máa rí ibùdésí tó dára ju èyí lọ.”');
INSERT INTO "aykyuni" VALUES(18,37,'18:37','Ọ̀rẹ́ rẹ̀ sọ fún un nígbà tí ó ń jà á níyàn (báyìí) pé: “Ṣé o máa ṣàì gbàgbọ́ nínú Ẹni tí Ó ṣẹ̀dá rẹ láti inú erùpẹ̀, lẹ́yìn náà, láti inú àtọ̀. Lẹ́yìn náà, Ó ṣe ọ́ ní ọkùnrin tó pé ní ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(18,38,'18:38','Ṣùgbọ́n ní tèmi, Olúwa mi, Òun ni Allāhu. Èmi kò sì níí sọ ẹnì kan di akẹgbẹ́ fún Olúwa mi.');
INSERT INTO "aykyuni" VALUES(18,39,'18:39','Nígbà tí o wọ inú ọgbà oko rẹ, kí ni kò mú ọ sọ pé: “Ohun tí Allāhu bá fẹ́! Kò sí agbára kan bí kò ṣe pẹ̀lú ìyọ̀ǹda Allāhu. Tí o bá sì rí èmi pé mo kéré sí ọ ní dúkìá àti ọmọ,');
INSERT INTO "aykyuni" VALUES(18,40,'18:40','ó súnmọ́ kí Olúwa mi fún èmi náà ní oore kan tí ó máa dára ju ọgbà oko tìrẹ. (Ó sì súnmọ́) kí Ó sọ ìyà kan kalẹ̀ sínú ọgbà oko rẹ láti sánmọ̀; ó sì máa di ilẹ̀ aṣálẹ̀.');
INSERT INTO "aykyuni" VALUES(18,41,'18:41','Tàbí kí omi rẹ̀ gbẹ. O ò sì níí lè wá omi kàn.”');
INSERT INTO "aykyuni" VALUES(18,42,'18:42','Wọ́n sì pa èso rẹ̀ run pátápátá. Ó sì di ẹni tó ń fí ọwọ́ rẹ̀ méjèèjì lura wọn pẹ́ẹ́pẹ́nípa ohun tí ó ti ná sórí rẹ̀. (Àwọn ilé rẹ̀) dàwó lulẹ̀ tòrùlé-tòrùlé wọn. Ó sì ń wí pé: “Yéè! Èmi ìbá tí sọ ẹnì kan kan di akẹgbẹ́ fún Olúwa mi. (Èmi ìbá má ṣẹbọ)” ');
INSERT INTO "aykyuni" VALUES(18,43,'18:43','Kò ní ìjọ kan tí ó máa ràn án lọ́wọ́ mọ́ lẹ́yìn Allāhu. Kò sì lè ran ara rẹ̀ lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(18,44,'18:44','Níbẹ̀ yẹn, ti Allāhu, Ọba Òdodo ni ìjọba. Ó lóore jùlọ ní ẹ̀san, Ó sì lóore jùlọ ní ìkángun (rere).');
INSERT INTO "aykyuni" VALUES(18,45,'18:45','Ṣe àkàwé ilé ayé fún wọn; ó dà bí omi tí A sọ̀kalẹ̀ láti sánmọ̀. Lẹ́yìn náà, ó ròpọ̀ mọ́ irúgbìn ilẹ̀. Lẹ́yìn náà, (irúgbìn) di gbígbẹ, tí atẹ́gùn ń fọ́nká. Allāhu sì ń jẹ́ Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(18,46,'18:46','Ọ̀ṣọ́ ìṣẹ̀mí ayé ni dúkìá àti àwọn ọmọ. Àwọn iṣẹ́ rere tó máa wà títí láéláé lóore jùlọ ní ẹ̀san lọ́dọ̀ Olúwa rẹ, ó sì lóore jùlọ ní ìrètí.');
INSERT INTO "aykyuni" VALUES(18,47,'18:47','(Rántí) ọjọ́ tí A máa mú àwọn àpáta rìn (kúrò ní àyè wọn). O sì máa rí ilẹ̀ tí ó máa fojú hàn (tí kò níí sí n̄ǹkan kan mọ́ lórí rẹ̀). A máa kó wọn jọ. A ò sì níí fi ẹnì kan sílẹ̀ nínú wọn.');
INSERT INTO "aykyuni" VALUES(18,48,'18:48','Wọn yó sì kó wọn wá síwájú Olúwa rẹ ní ọ̀wọ̀ọ̀wọ́. Dájúdájú ẹ ti wá bá Wa (báyìí) gẹ́gẹ́ bí A ṣe da yín nígbà àkọ́kọ́. Àmọ́ ẹ sọ láì ní ẹ̀rí lọ́wọ́ pé A ò níí mú ọjọ́ àdéhùn ṣẹ fún yín.');
INSERT INTO "aykyuni" VALUES(18,49,'18:49','A máa gbé ìwé iṣẹ́ ẹ̀dá kalẹ̀ (fún wọn). Nígbà náà, o máa rí àwọn ẹlẹ́ṣẹ̀ tí wọn yóò máa bẹ̀rù nípa ohun tí ń bẹ nínú ìwé iṣẹ́ wọn. Wọn yóò wí pé: “Ègbé wa! Kí ló mú ìwé yìí ná; kò fi ohun kékeré àti ńlá kan sílẹ̀ láì kọ ọ́ sílẹ̀?” Wọ́n sì bá ohun tí wọ́n ṣe níṣẹ́ níbẹ̀. Olúwa rẹ kò sì níí ṣàbòsí sí ẹnì kan.');
INSERT INTO "aykyuni" VALUES(18,50,'18:50','(Rántí) nígbà tí A sọ fún àwọn mọlāika pé: “Ẹ forí kanlẹ̀ kí (Ànábì) Ādam.” Wọ́n sì forí kanlẹ̀ kí i àfi ’Iblīs, (tí) ó jẹ́ ọ̀kan nínú àwọn àlùjànnú. Ó sì yapa sí àṣẹ Olúwa rẹ̀. Nítorí náà, ṣé ẹ máa mú òun àti àwọn àrọ́mọdọ́mọ rẹ̀ ní aláfẹ̀yìntì lẹ́yìn Mi ni, ọ̀tá yín sì ni wọ́n. Pàṣípààrọ̀ tó burú ni fún àwọn alábòsí.1 ');
INSERT INTO "aykyuni" VALUES(18,51,'18:51','Èmi kò fi wọ́n ṣe ẹlẹ́rìí sí ìṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀, àti ìṣẹ̀dá àwọn gan-an alára. Èmi kò sì mú àwọn aṣinilọ́nà ní olùrànlọ́wọ́.1 ');
INSERT INTO "aykyuni" VALUES(18,52,'18:52','(Rántí) ọjọ́ tí (Allāhu) yóò sọ pé: “Ẹ pe àwọn akẹgbẹ́ Mi tí ẹ sọ nípa wọn láì ní ẹ̀rí lọ́wọ́ (pé olùṣìpẹ̀ ni wọ́n).” Wọ́n pè wọ́n. Wọn kò sì dá wọn lóhùn. A sì ti fi àyè ìparun sáààrin wọn.');
INSERT INTO "aykyuni" VALUES(18,53,'18:53','Àwọn ẹlẹ́ṣẹ̀ sì rí Iná. Wọ́n sì mọ̀ dájúdájú pé àwọn yóò kó sínú rẹ̀. Wọn kò sì rí ibùsásí kan kúrò nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(18,54,'18:54','Dájúdájú A ti ṣe àlàyé oníran-ànran sínú al-Ƙur’ān fún àwọn ènìyàn pẹ̀lú gbogbo àkàwé. Ènìyàn sì jẹ́ olùjiyàn ju ohunkóhun lọ.');
INSERT INTO "aykyuni" VALUES(18,55,'18:55','Kò sí ohun tó dí àwọn ènìyàn lọ́wọ́ láti gbàgbọ́ ní òdodo nígbà tí ìmọ̀nà dé bá wọn, (kò sì sí ohun tó dí wọn lọ́wọ́ láti) tọrọ àforíjìn Olúwa wọn, bí kò ṣe pé (wọ́n fẹ́) kí ìṣe (Allāhu nípa ìparun) àwọn ẹni àkọ́kọ́ dé bá àwọn náà tàbí kí ìyà dé bá wọn ní ojúkojú.');
INSERT INTO "aykyuni" VALUES(18,56,'18:56','Àti pé A kò rán àwọn Òjíṣẹ́ níṣẹ́ (lásán) àfi kí wọ́n jẹ́ oníròó-ìdùnnú àti olùkìlọ̀. Àwọn tó ṣàì gbàgbọ́ sì ń fi irọ́ ṣàtakò nítorí kí wọ́n lè fi ba òdodo jẹ́. Wọ́n sì sọ àwọn āyah Mi àti ohun tí A fi ṣèkìlọ̀ fún wọn di yẹ̀yẹ́.');
INSERT INTO "aykyuni" VALUES(18,57,'18:57','Ta sì ló ṣàbòsí ju ẹni tí wọ́n fi àwọn āyah Olúwa rẹ̀ ṣèrántí fún, tí ó gbúnrí kúrò níbẹ̀, tí ó sì gbàgbé ohun tí ọwọ́ rẹ̀ méjèèjì tì síwájú? Dájúdájú Àwa fi èbìbò bo ọkàn wọn nítorí kí wọ́n má baà gbọ́ ọ yé. A sì fi èdídí sínú etí wọn. Tí ìwọ bá pè wọ́n síbi ìmọ̀nà, nígbà náà wọn kò sì níí mọ̀nà láéláé.');
INSERT INTO "aykyuni" VALUES(18,58,'18:58','Olúwa rẹ ni Aláforíjìn, Oníkẹ̀ẹ́. Bí ó bá jẹ́ pé Ó máa fi ohun tí wọ́n ṣe níṣẹ́ mú wọn ni, ìbá tètè mú ìyà wá fún wọn. Ṣùgbọ́n àkókò àdéhùn (àjíǹde) wà fún wọn. Wọn kò sì níí rí ibùsásí kan lẹ́yìn rẹ̀.');
INSERT INTO "aykyuni" VALUES(18,59,'18:59','Ìwọ́nyí ni àwọn ìlú tí A ti parẹ́ nígbà tí wọ́n ṣàbòsí. A sì fún wọn ní (àkókò) àdéhùn fún ìparun wọn.');
INSERT INTO "aykyuni" VALUES(18,60,'18:60','(Rántí) nígbà tí (Ànábì) Mūsā sọ fún ọmọ-ọ̀dọ̀ rẹ̀ pé: “Èmi kò níí yé rìn títí mo máa fi dé ibi tí odò méjì ti papọ̀ tàbí (títí) mo máa fi lo ọ̀pọ̀lọ́pọ̀ ọdún.”');
INSERT INTO "aykyuni" VALUES(18,61,'18:61','Nígbà tí àwọn méjèèjì sì dé ibi tí odò méjèèjì ti papọ̀, wọ́n gbàgbé ẹja wọn. (Ẹja náà) sì sọ ojú ọ̀nà rẹ̀ di poro ọ̀nà nínú odò.');
INSERT INTO "aykyuni" VALUES(18,62,'18:62','Nígbà tí àwọn méjèèjì ré kọjá (àyè náà), ó sọ fún ọmọ-ọ̀dọ̀ rẹ̀ pé: “Fún wa ní oúnjẹ ọ̀sán wa. Dájúdájú a ti ko wàhálà nínú ìrìn-àjò wa yìí.”');
INSERT INTO "aykyuni" VALUES(18,63,'18:63','(Ọmọ-ọ̀dọ̀ rẹ̀) sọ pé: “Ṣé o rí i! Nígbà tí a wà níbi àpáta (fún ìsinmi), dájúdájú mo ti gbàgbé ẹja náà (síbẹ̀)? Kò sì sí ohun tí ó mú mi gbàgbé rẹ̀ bí kò ṣe Èṣù, tí kò jẹ́ kí n̄g rántí rẹ̀. (Ẹja náà) sì ti mú ọ̀nà rẹ̀ tọ̀ lọ nínú odò ní ọ̀nà ìyanu.”');
INSERT INTO "aykyuni" VALUES(18,64,'18:64','(Ànábì Mūsā) sọ pé: “(Ibi tí ẹja ti lọ) yẹn ni ohun tí à ń wá.”Àwọn méjèèjì sì padà sẹ́yìn láti tọ orípa ẹsẹ̀ wọn bí wọ́n ti ṣe tọ̀ ọ́ wá. ');
INSERT INTO "aykyuni" VALUES(18,65,'18:65','Àwọn méjèèjì sì bá ẹrúsìn kan nínú àwọn ẹrúsìn Wa (níbẹ̀), tí A fún ní ìkẹ́ kan láti ọ̀dọ̀ Wa. A sì fún un ní ìmọ̀ láti ọ̀dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(18,66,'18:66','(Ànábì) Mūsā sọ fún un pé: “Ṣé kí n̄g tẹ̀lé ọ nítorí kí o lè kọ́ mi nínú ohun tí Wọ́n fi mọ̀ ọ́ ní ìmọ̀nà.”1 ');
INSERT INTO "aykyuni" VALUES(18,67,'18:67','(Kidr) sọ pé: “Dájúdájú ìwọ kò lè ṣe sùúrù pẹ̀lú mi.');
INSERT INTO "aykyuni" VALUES(18,68,'18:68','Àti pé báwo ni o ṣe máa ṣe sùúrù lórí ohun tí o ò fi ìmọ̀ rọkiri ká rẹ̀?”');
INSERT INTO "aykyuni" VALUES(18,69,'18:69','(Ànábì Mūsā) sọ pé: “Tí Allāhu bá fẹ́, ó máa bá mi ní onísùúrù. Èmi kò sì níí yapa àṣẹ rẹ.”');
INSERT INTO "aykyuni" VALUES(18,70,'18:70','(Kidr) sọ pé: “Tí o bá tẹ̀lé mi, má ṣe bi mí ní ìbéèrè nípa n̄ǹkan kan títí mo fi máa kọ́kọ́ mú ìrántí wá fún ọ nípa rẹ̀.”');
INSERT INTO "aykyuni" VALUES(18,71,'18:71','Nítorí náà, àwọn méjèèjì lọ títí di ìgbà tí wọ́n fi wọ inú ọkọ̀ ojú-omi. (Kidr) sì dá ọkọ̀ náà lu. (Ànábì Mūsā) sọ pé: “O ṣe dá a lu, (ṣé) kí àwọn èrò rẹ̀ lè tẹ̀ rì ni? Dájúdájú o ti ṣe n̄ǹkan aburú kan!”');
INSERT INTO "aykyuni" VALUES(18,72,'18:72','(Kidr) sọ pé: “Ǹjẹ́ èmi kò sọ fún ọ pé dájúdájú ìwọ kò níí lè ṣe sùúrú pẹ̀lú mi.”');
INSERT INTO "aykyuni" VALUES(18,73,'18:73','(Ànábì Mūsā) sọ pé: “Má ṣe bá mi wí nípa ohun tí mo gbàgbé. Má sì ṣe kó ìnira bá mi nínú ọ̀rọ̀ (ìrìn-àjò) mi (pẹ̀lú rẹ).”');
INSERT INTO "aykyuni" VALUES(18,74,'18:74','Lẹ́yìn náà, àwọn méjèèjì lọ títí di ìgbà tí wọ́n fi pàdé ọmọdékùnrin kan. (Kidr) sì pa á. (Ànábì Mūsā) sọ pé: “O ṣe pa ẹ̀mí (ènìyàn) mímọ́, láì gba ẹ̀mí? Dájúdájú o ti ṣe n̄ǹkan aburú o.”');
INSERT INTO "aykyuni" VALUES(18,75,'18:75','(Kidr) sọ pé: “Ǹjẹ́ èmi kò sọ fún ọ pé dájúdájú ìwọ kò níí lè ṣe sùúrú pẹ̀lú mi.”');
INSERT INTO "aykyuni" VALUES(18,76,'18:76','(Ànábì Mūsā) sọ pé: “Tí mo bá tún bi ọ́ nípa kiní kan lẹ́yìn rẹ̀, má ṣe bá mi rìn mọ́. Dájúdájú o ti mú àwáwí dé òpin lọ́dọ̀ mi.”');
INSERT INTO "aykyuni" VALUES(18,77,'18:77','Lẹ́yìn náà, àwọn méjèèjì lọ títí di ìgbà tí wọ́n dé ọ̀dọ̀ àwọn ará ìlú kan. Wọ́n tọrọ oúnjẹ lọ́dọ̀ àwọn ará ìlú náà. Wọ́n sì kọ̀ láti ṣe wọ́n ní àlejò. Àwọn méjèèjì sì bá ògiri kan níbẹ̀ tí ó fẹ́ wó. (Kidr) sì gbé e dìde. (Ànábì Mūsā) sọ pé: “Tí ó bá jẹ́ pé o bá fẹ́, kí o sì gba owó-ọ̀yà lórí rẹ̀.”');
INSERT INTO "aykyuni" VALUES(18,78,'18:78','(Kidr) sọ pé: “Èyí ni òpínyà láààrin èmi àti ìwọ. Mo sì máa fún ọ ní ìtúmọ̀ ohun tí ìwọ kò lè ṣe sùúrù lórí rẹ̀.”');
INSERT INTO "aykyuni" VALUES(18,79,'18:79','Ní ti ọkọ̀ ojú-omi, ó jẹ́ ti àwọn mẹ̀kúnnù tí wọ́n ń ṣiṣẹ́ lórí omi. Mo sì fẹ́ láti fi àlébù kàn án (nítorí pé), ọba kan wà níwájú wọn tó ń gba gbogbo ọkọ̀ ojú-omi pẹ̀lú ipá.');
INSERT INTO "aykyuni" VALUES(18,80,'18:80','Ní ti ọmọdékùnrin náà, àwọn òbí rẹ̀ méjèèjì jẹ́ onígbàgbọ́ òdodo. A sì ń bẹ̀rù pé kí ó màa kó ìtayọ ẹnu-ààlà àti àìgbàgbọ́ bá àwọn méjèèjì.1 ');
INSERT INTO "aykyuni" VALUES(18,81,'18:81','Nítorí náà, A fẹ́ kí Olúwa àwọn méjèèjì pààrọ̀ rẹ̀ fún wọn pẹ̀lú (èyí) tó lóore jù ú lọ tó máa jẹ́ ẹni mímọ́ jù ú lọ àti (èyí) tó máa jẹ́ ọmọ rere olújú-àánú sí (àwọn òbí rẹ̀) jù ú lọ.');
INSERT INTO "aykyuni" VALUES(18,82,'18:82','Nípa ti ògiri, ó jẹ́ ti àwọn ọmọdékùnrin, ọmọ òrukàn méjì kan nínú ìlú náà. Àpótí-ọrọ̀ kan sì ń bẹ fún àwọn méjèèjì lábẹ́ ògiri náà. Bàbá àwọn méjèèjì sì jẹ́ ẹni rere. Nítorí náà, Olúwa rẹ fẹ́ kí àwọn méjèèjì dàgbà dáadáa (bá dúkìá náà), kí wọ́n sì hú dúkìá wọn jáde (kí ó lè jẹ́) ìkẹ́ kan láti ọ̀dọ̀ Olúwa rẹ. Èmi kò dá a ṣe láti ọ̀dọ̀ ara mi; (Allāhu l’Ó pa mí láṣẹ rẹ̀). Ìyẹn ni ìtúmọ̀ ohun tí ìwọ kò lè ṣe sùúrù fún.');
INSERT INTO "aykyuni" VALUES(18,83,'18:83','Wọ́n ń bi ọ́ léèrè nípa Thul-Ƙọrneen. Sọ pé: “Mo máa mú ọ̀rọ̀ ìrántí wá fún yín nípa rẹ̀.”');
INSERT INTO "aykyuni" VALUES(18,84,'18:84','Dájúdájú Àwa fún un ní ipò lórí ilẹ̀. A sì fún un ní ọ̀nà tó lè gbà ṣe gbogbo n̄ǹkan (tí ó bá fẹ́ ṣe).');
INSERT INTO "aykyuni" VALUES(18,85,'18:85','Nítorí náà, ó mú ọ̀nà kan tọ̀');
INSERT INTO "aykyuni" VALUES(18,86,'18:86','títí ó fi dé ibùwọ̀ òòrùn ayé. Ó rí i tí ń wọ̀ sínú ìṣẹ́lẹ̀rú pẹ̀tẹ̀pẹ́tẹ̀ dúdú kan. Ó sì bá àwọn ènìyàn kan níbẹ̀. A sọ pé: “Thul-Ƙọrneen, yálà kí o jẹ wọ́n níyà tàbí kí o mú ohun rere jáde lára wọn.”');
INSERT INTO "aykyuni" VALUES(18,87,'18:87','(Allāhu) sọ pé: “Ní ti ẹni tí ó bá ṣàbòsí, láìpẹ́ A máa jẹ ẹ́ níyà. Lẹ́yìn náà, wọn máa dá a padà sí ọ̀dọ̀ Olúwa rẹ̀. Ó sì máa jẹ ẹ́ níyà tó burú.');
INSERT INTO "aykyuni" VALUES(18,88,'18:88','Ṣùgbọ́n ní ti ẹni tí ó bá gbàgbọ́ ní òdodo, tí ó sì ṣe iṣẹ́ rere, tirẹ̀ ni ẹ̀san rere. A ó sì sọ̀rọ̀ ìrọ̀rùn fún un nínú àṣẹ Wa.”');
INSERT INTO "aykyuni" VALUES(18,89,'18:89','Lẹ́yìn náà, ó mú ọ̀nà kan tọ̀');
INSERT INTO "aykyuni" VALUES(18,90,'18:90','títí ó fi dé ibùyọ òòrùn ayé. Ó rí i tí ń yọ lórí àwọn ènìyàn kan, tí A kò fún ní gàgá (ààbò) kan níbi òòrùn.');
INSERT INTO "aykyuni" VALUES(18,91,'18:91','Báyẹn ni (Thul-Ƙọrneen ṣe ń tẹ̀ síwájú). Dájúdájú A rọkiriká ohun tí ń bẹ lọ́dọ̀ rẹ̀ pẹ̀lú ìmọ̀.');
INSERT INTO "aykyuni" VALUES(18,92,'18:92','Lẹ́yìn náà, ó mú ọ̀nà kan tọ̀');
INSERT INTO "aykyuni" VALUES(18,93,'18:93','títí ó fi dé ààrin àpáta méjì. Ó sì bá àwọn ènìyàn kan níwájú rẹ̀. Wọn kò sì fẹ́ẹ̀ gbọ́ àgbọ́yé ọ̀rọ̀ kan (nínú èdè mìíràn).');
INSERT INTO "aykyuni" VALUES(18,94,'18:94','Wọ́n sọ pé: “Thul-Ƙọrneen, dájúdájú (ìran) Ya’jūj àti Ma’jūj ń ṣe ìbàjẹ́ lórí ilẹ̀. Ṣé kí á fún ọ ní owó-òde nítorí kí o lè bá wa mọ odi kan sáààrin àwa àti àwọn?”');
INSERT INTO "aykyuni" VALUES(18,95,'18:95','Ó sọ pé: “Ohun tí Olúwa mi fún mi nínú ipò lóore jùlọ. Nítorí náà, ẹ fi agbára ràn mí lọ́wọ́ ni nítorí kí n̄g lè mọ odi sáààrin ẹ̀yin àti àwọn.');
INSERT INTO "aykyuni" VALUES(18,96,'18:96','Ẹ máa fún mi ni ègígé irin títí di ìgbà tí ó fi máa bá ẹ̀gbẹ́ àpáta méjèèjì dọ́gba.” Ó sọ pé: “Ẹ máa fẹ́ atẹ́gùn ẹwìrì (sí i lára) títí di ìgbà tí ó máa (pọ́n wẹ̀ẹ̀ bí) iná.” Ó sọ pé: “Ẹ mu idẹ wá fún mi kí n̄g yọ́ ọ lé e lórí.”');
INSERT INTO "aykyuni" VALUES(18,97,'18:97','Nítorí náà, wọn kò lè gùn ún, wọn kò sì lè dá a lu.');
INSERT INTO "aykyuni" VALUES(18,98,'18:98','Ó sọ pé: “Èyí ni ìkẹ́ kan láti ọ̀dọ̀ Olúwa mi. Nígbà tí àdéhùn Olúwa mi bá dé, (Allāhu) yó sì sọ ọ́ di pẹ̀tẹ́lẹ̀. Àdéhùn Olúwa mi sì jẹ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(18,99,'18:99','A máa fi àwọn (ènìyàn àti àlùjànnú) sílẹ̀ ní ọjọ́ yẹn, tí apá kan wọn yó sì máa rúpọ̀ mọ́ apá kan. Wọ́n á fọn fèrè oníwo fún àjíǹde. A ó sì kó gbogbo wọn jọ papọ̀ pátápátá.');
INSERT INTO "aykyuni" VALUES(18,100,'18:100','Ní ọjọ́ yẹn, A ó sì fi iná Jahanamọ han àwọn aláìgbàgbọ́ kedere.');
INSERT INTO "aykyuni" VALUES(18,101,'18:101','(Àwọn ni) àwọn tí ojú wọn wà nínú èbìbò nípa ìrántí Mi. Wọn kò sì lè gbọ́rọ̀.');
INSERT INTO "aykyuni" VALUES(18,102,'18:102','Ṣé àwọn tó ṣàì gbàgbọ́ lérò pé àwọn yóò mú àwọn ẹrúsìn Mi ní alátìlẹ́yìn lẹ́yìn Mi ni? Dájúdájú Àwa pèsè iná Jahanamọ sílẹ̀ ní ibùdésí fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(18,103,'18:103','Sọ pé: “Ṣé kí Á fún yín ní ìró àwọn ẹni òfò jùlọ nípa iṣẹ́ (ọwọ́ wọn)?');
INSERT INTO "aykyuni" VALUES(18,104,'18:104','(Àwọn ni) àwọn tí iṣẹ́ wọn ti bàjẹ́ nínú ìṣẹ̀mí ayé, (àmọ́ tí) wọ́n ń lérò pé dájúdájú àwọn ń ṣe iṣẹ́ rere.');
INSERT INTO "aykyuni" VALUES(18,105,'18:105','Àwọn wọ̀nyẹn ni àwọn tó ṣàì gbàgbọ́ nínú àwọn āyah Olúwa wọn àti ìpàdé Rẹ̀. Àwọn iṣẹ́ wọn sì bàjẹ́. Nítorí náà, A ò níí jẹ́ kí wọ́n jámọ́ n̄ǹkan kan lórí ìwọ̀n ní Ọjọ́ Àjíǹde.');
INSERT INTO "aykyuni" VALUES(18,106,'18:106','Jahanamọ, ìyẹn ni ẹ̀san wọn nítorí pé wọ́n ṣàì gbàgbọ́, wọ́n sì sọ àwọn āyah Mi àti àwọn Òjíṣẹ́ Mi di oníyẹ̀yẹ́.');
INSERT INTO "aykyuni" VALUES(18,107,'18:107','Dájúdájú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, àwọn ọgbà Firdaos ti wà fún wọn ní ibùdésí.');
INSERT INTO "aykyuni" VALUES(18,108,'18:108','Olùṣegbére ni wọ́n nínú rẹ̀. Wọn kò sì níí fẹ́ kúrò nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(18,109,'18:109','Sọ pé: “Tí ó bá jẹ́ pé ibúdò jẹ́ tàdáà fún àwọn ọ̀rọ̀ Olúwa Mi, ibúdò kúkú máa tán ṣíwájú kí àwọn ọ̀rọ̀ Olúwa Mi tó tán, kódà kí Á tún mú (ibúdò) irú rẹ̀ wá ní àlékún.”');
INSERT INTO "aykyuni" VALUES(18,110,'18:110','Sọ pé: “Abara ni èmi bí irú yín. Wọ́n ń fí ìmísí ránṣẹ́ sí mi pé Ọlọ́hun yín, Ọlọ́hun Ọ̀kan ṣoṣo ni. Nítorí náà, ẹnikẹ́ni tí ó bá ń retí ìpàdé Olúwa rẹ̀, kí ó ṣe iṣẹ́ rere. Kò sì gbọdọ̀ fi ẹnì kan kan ṣe akẹgbẹ́ níbi jíjọ́sìn fún Olúwa rẹ̀ (kò gbọ́dọ̀ ṣẹbọ sí Olúwa rẹ̀).”');
INSERT INTO "aykyuni" VALUES(19,1,'19:1','Kāf hā yā ‘aēn sọ̄d. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(19,2,'19:2','(Èyí ni) ìrántí nípa ìkẹ́ tí Olúwa Rẹ ṣe fún ẹrúsìn Rẹ̀ (Ànábì) Zakariyyā.');
INSERT INTO "aykyuni" VALUES(19,3,'19:3','(Rántí) nígbà tí ó pe Olúwa rẹ̀ ní ìpè ìkọ̀kọ̀.');
INSERT INTO "aykyuni" VALUES(19,4,'19:4','Ó sọ pé: “Olúwa mi, dájúdájú eegun ara mi ti lẹ, orí (mi) ti kún fún ewú, èmi kò sì níí pasán nípa bí mo ṣe ń pè Ọ́, Olúwa mi.');
INSERT INTO "aykyuni" VALUES(19,5,'19:5','Àti pé dájúdájú mò ń páyà àwọn ìbátan mi lẹ́yìn (ikú) mi. Ìyàwó mi sì jẹ́ àgàn. Nítorí náà, ta mí lọ́rẹ láti ọ̀dọ̀ Rẹ ọmọ rere kan,');
INSERT INTO "aykyuni" VALUES(19,6,'19:6','(tí) ó máa jogún mi, tí ó sì máa jogún àwọn ẹbí (Ànábì) Ya‘ƙūb. Kí O sì ṣe é ní ẹni tí O yọ́nú sí, Olúwa mi.”');
INSERT INTO "aykyuni" VALUES(19,7,'19:7','Zakariyyā, dájúdájú Àwa máa fún ọ ní ìró ìdùnnú (nípa bíbí) ọmọkùnrin kan, orúkọ rẹ̀ ni Yahyā. A kò fún ẹnì kan ní (irú) orúkọ (yìí) rí ṣíwájú (rẹ̀).');
INSERT INTO "aykyuni" VALUES(19,8,'19:8','Ó sọ pé: “Olúwa mi, báwo ni mo ṣe máa ní ọmọ nígbà tí ìyàwó mi jẹ́ àgàn, tí mo sì ti di àgbàlagbà gan-an.”');
INSERT INTO "aykyuni" VALUES(19,9,'19:9','(Mọlāika) sọ pé: “Báyẹn ni (ó máa rí).” Olúwa rẹ sọ pé: “Ó rọrùn fún Mi. Mó kúkú dá ìwọ náà ṣíwájú (rẹ̀), nígbà tí ìwọ kò tí ì jẹ́ n̄ǹkan kan.”');
INSERT INTO "aykyuni" VALUES(19,10,'19:10','(Zakariyyā) sọ pé: “Olúwa mi, fún mi ní àmì kan.” (Mọlāika) sọ pé: “Àmì rẹ ni pé, o ò níí lè bá ènìyàn sọ̀rọ̀ fún ọjọ́ mẹ́ta, kì í ṣe ti àmódi.”');
INSERT INTO "aykyuni" VALUES(19,11,'19:11','Nígbà náà, ó jáde sí àwọn ènìyàn rẹ̀ láti inú ilé ìjọ́sìn. Ó sì tọ́ka sí wọn pé kí wọ́n máa ṣe àfọ̀mọ́ (fún Allāhu) ní òwúrọ̀ àti ní ìrọ̀lẹ́.');
INSERT INTO "aykyuni" VALUES(19,12,'19:12','Yahyā, mú Tírà náà dání dáradára. A sì fún un ní àgbọ́yé ẹ̀sìn láti kékeré.');
INSERT INTO "aykyuni" VALUES(19,13,'19:13','(Ànábì Yahyā jẹ́) ìkẹ́ àti ẹni mímọ́ kan láti ọ̀dọ̀ Wa. Ó sì jẹ́ olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(19,14,'19:14','(Ó tún jẹ́) oníwà rere sí àwọn òbí rẹ̀ méjèèjì. Kò sì jẹ́ ajẹninípá, olùyapa.');
INSERT INTO "aykyuni" VALUES(19,15,'19:15','Àlàáfíà ni fún un ní ọjọ́ tí wọ́n bí i, àti ní ọjọ́ tí ó máa kú àti ní ọjọ́ tí A óò gbé e dìde láàyè (ní Ọjọ́ Àjíǹde).');
INSERT INTO "aykyuni" VALUES(19,16,'19:16','Sọ ìtàn Mọryam tí ó wà nínú al-Ƙur’ān. (Rántí) nígbà tí ó yẹra kúrò lọ́dọ̀ àwọn ènìyàn rẹ̀ sí àyè kan ní ìlà òòrùn.');
INSERT INTO "aykyuni" VALUES(19,17,'19:17','Ó mú gàgá kan (láti fi ara rẹ̀ pamọ́) fún wọn. A sì rán mọlāika Wa sí i. Ó sì fara hàn án ní àwòrán abara pípé.1 ');
INSERT INTO "aykyuni" VALUES(19,18,'19:18','(Mọryam) sọ pé: “Èmi sá di Àjọkẹ́-ayé kúrò lọ́dọ̀ rẹ, tí o bá jẹ́ olùbẹ̀rù (Allāhu).”1 ');
INSERT INTO "aykyuni" VALUES(19,19,'19:19','(Mọlāika) sọ pé: “Èmi ni Òjíṣẹ́ Olúwa rẹ, (Ó rán mi sí ọ) pé kí n̄g fún ọ ní ọmọkùnrin mímọ́ kan.”1 ');
INSERT INTO "aykyuni" VALUES(19,20,'19:20','Ó sọ pé: “Báwo ni mo ṣe máa ní ọmọ nígbà tí abara kan kò fọwọ́ kàn mí, tí èmi kò sì jẹ́ alágbèrè.”');
INSERT INTO "aykyuni" VALUES(19,21,'19:21','(Mọlāika) sọ pé: “Báyẹn ló máa rí.” Olúwa rẹ sọ pé: “Ó rọrùn fún mi. Àti pé nítorí kí Á lè ṣe é ní àmì fún àwọn ènìyàn ni. Ó sì jẹ́ ìkẹ́ láti ọ̀dọ̀ Wa. Ó tún jẹ́ ọ̀rọ̀ tí A ti parí (tí ó gbọ́dọ̀ wá sí ìmúṣẹ.)”');
INSERT INTO "aykyuni" VALUES(19,22,'19:22','Nítorí náà, ó lóyún rẹ̀. Ó sì yẹra pẹ̀lú rẹ̀ sí àyè kan tó jìnnà.');
INSERT INTO "aykyuni" VALUES(19,23,'19:23','Ìrọbí ọmọ sì mú un wá sí ìdí igi dàbínù. Ó sọ pé: “Háà! Kí n̄g ti kú ṣíwájú èyí, kí n̄g sì ti di ẹni tí wọ́n ti gbàgbé pátápátá.”');
INSERT INTO "aykyuni" VALUES(19,24,'19:24','Nígbà náà, (mọlāika) pè é láti ìsàlẹ̀ ọ̀dọ̀ rẹ̀ pé: "Má ṣe banújẹ́. Dájúdájú Olúwa rẹ ti ṣe odò kékeré kan sí ìsàlẹ̀ ọ̀dọ̀ rẹ.');
INSERT INTO "aykyuni" VALUES(19,25,'19:25','Mi igi dàbínù náà sọ́dọ̀ rẹ, kí dàbínú tútù, tó tó ká jẹ sì máa jábọ́ sílẹ̀ fún ọ.');
INSERT INTO "aykyuni" VALUES(19,26,'19:26','Nítorí náà, jẹ, mu kí ojú rẹ sì tutù. Tí o bá sì rí ẹnì kan nínú abara, sọ fún un pé: “Dájúdájú mo jẹ́jẹ̀ẹ́ ìkẹ́nuró fún Àjọkẹ́-ayé. Nítorí náà, èmi kò níí bá ènìyàn kan sọ̀rọ̀ ní òní.”');
INSERT INTO "aykyuni" VALUES(19,27,'19:27','Ó sì mú ọmọ náà wá bá àwọn ènìyàn rẹ̀ (ní ẹni tí) ó gbé e dání. Wọ́n sọ pé: “Mọryam, dájúdájú o ti gbé n̄ǹkan aburú ńlá wá.');
INSERT INTO "aykyuni" VALUES(19,28,'19:28','Arábìnrin Hārūn, bàbá rẹ kì í ṣe ènìyàn burúkú. Àti pé ìyá rẹ kì í ṣe alágbèrè.”');
INSERT INTO "aykyuni" VALUES(19,29,'19:29','Ó sì tọ́ka sí ọmọ náà. Wọ́n sọ pé: “Báwo ni a ó ṣe bá ẹni tó wà lórí ìtẹ́, tó jẹ́ ọmọ òpóǹló sọ̀rọ̀?”');
INSERT INTO "aykyuni" VALUES(19,30,'19:30','(Ọmọ náà) sọ̀rọ̀ pé: “Dájúdájú ẹrú Allāhu ni èmi. (Allāhu) fún mi ní Tírà. Ó sì ṣe mí ní Ànábì. 1 ');
INSERT INTO "aykyuni" VALUES(19,31,'19:31','Ó ṣe mí ní ẹni ìbùkún ní ibikíbi tí mo bá wà. Ó pa mí láṣẹ ìrun kíkí àti Zakāh yíyọ lódiwọ̀n ìgbà tí mo bá wà nípò alààyè (lórí ilẹ̀ ayé).');
INSERT INTO "aykyuni" VALUES(19,32,'19:32','(Ó ṣe mí ní) oníwà rere sí ìyá mi. Kò sì ṣe mí ní ajẹninípá, olórí burúkú.');
INSERT INTO "aykyuni" VALUES(19,33,'19:33','Àlàáfíà ni fún mi ní ọjọ́ tí wọ́n bí mi, àti ní ọjọ́ tí mo máa kú àti ní ọjọ́ tí Wọ́n á gbé mi dìde ní alààyè (ní Ọjọ́ Àjíǹde).”');
INSERT INTO "aykyuni" VALUES(19,34,'19:34','Ìyẹn ni (Ànábì) ‘Īsā ọmọ Mọryam. (Èyí jẹ́) ọ̀rọ̀ òdodo tí àwọn (yẹhudi àti nasọ̄rọ̄) ń ṣeyèméjì nípa rẹ̀.');
INSERT INTO "aykyuni" VALUES(19,35,'19:35','Kò yẹ fún Allāhu láti sọ ẹnì kan kan di ọmọ. Mímọ́ ni fún Un. Nígbà tí Ó bá pèbùbù kiní kan, Ó kàn máa sọ fún un pé: “Jẹ́ bẹ́ẹ̀.” Ó sì máa jẹ́ bẹ́ẹ̀.');
INSERT INTO "aykyuni" VALUES(19,36,'19:36','Àti pé dájúdájú Allāhu ni Olúwa mi àti Olúwa yín. Nítorí náà, ẹ jọ́sìn fún Un. Èyí ni ọ̀nà tààrà.');
INSERT INTO "aykyuni" VALUES(19,37,'19:37','Àwọn ìjọ (yẹhudi àti nasọ̄rọ̄) sì yapa-ẹnu (lórí èyí) láààrin ara wọn. Nítorí náà, ègbé ni fún àwọn tó ṣàì gbàgbọ́ (ní àsìkò) ìjẹ́rìí gban̄gba l’ọ́jọ́ ńlá (Ọjọ́ Àjíǹde).');
INSERT INTO "aykyuni" VALUES(19,38,'19:38','Kí ni wọn kò níí gbọ́, kí sì ni wọn kò níí rí ní ọjọ́ tí wọn yóò wá bá Wa!Ṣùgbọ́n àwọn alábòsí ní ọjọ́ náà ti wà nínú ìṣìnà pọ́nńbélé. ');
INSERT INTO "aykyuni" VALUES(19,39,'19:39','Ṣèkìlọ̀ ọjọ́ àbámọ̀ fún wọn nígbà tí A bá parí ọ̀rọ̀, (pé kò níí sí ikú mọ́, àmọ́) wọ́n wà nínú ìgbàgbéra báyìí ná, wọn kò sì gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(19,40,'19:40','Dájúdájú Àwa l’A máa jogún ilẹ̀ àti àwọn tó ń bẹ lórí rẹ̀. Ọ̀dọ̀ Wa sì ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(19,41,'19:41','Mú (ìtàn) ’Ibrọ̄hīm wá sí ìrántí (bí ó ṣe) wà nínú al-Ƙur’ān. Dájúdájú ó jẹ́ olódodo pọ́nńbélé, (ó sì jẹ́) Ànábì.');
INSERT INTO "aykyuni" VALUES(19,42,'19:42','Rántí nígbà tí ó sọ fún bàbá rẹ̀ pé: “Bàbá mi, nítorí kí ni o fi ń jọ́sìn fún ohun tí kò gbọ́rọ̀, tí kò ríran, tí kò sì lè rọ̀ ọ́ lọ́rọ̀ kan kan.');
INSERT INTO "aykyuni" VALUES(19,43,'19:43','Bàbá mi, dájúdájú ìmọ̀ tí ìwọ kò ní ti dé bá mi. Nítorí náà, tẹ̀lé mi, kí n̄g fi ọ̀nà tààrà mọ̀ ọ́.');
INSERT INTO "aykyuni" VALUES(19,44,'19:44','Bàbá mi, má ṣe bọ Èṣù. Dájúdájú Èṣù jẹ́ olùyapa àṣẹ Àjọkẹ́-ayé.');
INSERT INTO "aykyuni" VALUES(19,45,'19:45','Bàbá mi, dájúdájú èmi ń páyà pé kí ìyà kan láti ọ̀dọ̀ Àjọkẹ́-ayé má fọwọ́ bà ọ́ nítorí kí ìwọ má baà di ọ̀rẹ́ Èṣù (nínú Iná).”');
INSERT INTO "aykyuni" VALUES(19,46,'19:46','(Bàbá rẹ̀) wí pé: “Ṣé ìwọ yóò kọ àwọn ọlọ́hun mi sílẹ̀ ni, ’Ibrọ̄hīm? Dájúdájú tí o ò bá jáwọ́ (nínú ohun tí ò ń sọ), dájúdájú mo máa lẹ̀ ọ́ lókò pa. Tíẹ̀ yẹra fún mi wọ́ọ́rọ́wọ́ ná.”');
INSERT INTO "aykyuni" VALUES(19,47,'19:47','(’Ibrọ̄hīm) sọ pé: “Àlàáfíà kí ó máa bá ọ. Mo máa bá ọ tọrọ àforíjìn lọ́dọ̀ Olúwa mi. Dájúdájú Ó jẹ́ Olóore mi.');
INSERT INTO "aykyuni" VALUES(19,48,'19:48','Àti pé mo máa yẹra fún ẹ̀yin àti n̄ǹkan tí ẹ̀ ń jọ́sìn fún lẹ́yìn Allāhu. Èmi yó sì máa pe Olúwa mi. Ó sì súnmọ́ pé èmi kò níí pasán pẹ̀lú àdúà mi sí Olúwa mi.”');
INSERT INTO "aykyuni" VALUES(19,49,'19:49','Nígbà tí ó yẹra fún àwọn àti n̄ǹkan tí wọ́n ń jọ́sìn fún lẹ́yìn Allāhu, A sì ta á lọ́rẹ (ọmọ), ’Ishāƙ àti Ya‘ƙūb (ọmọọmọ rẹ̀). A sì ṣe ọ̀kọ̀ọ̀kan wọn ní Ànábì.1 ');
INSERT INTO "aykyuni" VALUES(19,50,'19:50','Àti pé A ta wọ́n lọ́rẹ láti inú ìkẹ́ Wa. A sì fi òdodo sórí ahọ́n gbogbo àwọn ènìyàn nìpa wọn (ìyẹn ni pé, gbogbo ìjọ ẹlẹ́sìn ló ń sọ̀rọ̀ wọn ní dáadáa).');
INSERT INTO "aykyuni" VALUES(19,51,'19:51','Sọ ìtàn (Ànábì) Mūsā tí ó wà nínú al-Ƙur’ān. Dájúdájú ó jẹ́ ẹni-ẹ̀ṣà, ó jẹ́ Òjíṣẹ́, (ó tún jẹ́) Ànábì.');
INSERT INTO "aykyuni" VALUES(19,52,'19:52','A pè é ní ẹ̀bá àpáta ní apá ọwọ́ ọ̀tún (Mūsā). A sì mú un súnmọ́ tòsí láti bá a sọ̀rọ̀.');
INSERT INTO "aykyuni" VALUES(19,53,'19:53','A sì fi arákùnrin rẹ̀, Hārūn, (tí ó jẹ́) Ànábì, ta á lọ́rẹ láti inú ìkẹ́ Wa.');
INSERT INTO "aykyuni" VALUES(19,54,'19:54','Sọ ìtàn (Ànábì) ’Ismọ̄‘īl tí ó wà nínú al-Ƙur’ān. Dájúdájú ó jẹ́ olùmú-àdéhùn-ṣẹ, ó jẹ́ Òjíṣẹ́, (ó tún jẹ́) Ànábì.');
INSERT INTO "aykyuni" VALUES(19,55,'19:55','Ó máa ń pa ará ilé rẹ̀ ní àṣẹ ìrun kíkí àti Zakāh yíyọ. Ó sì jẹ́ ẹni ìyọ́nú lọ́dọ̀ Olúwa rẹ̀.');
INSERT INTO "aykyuni" VALUES(19,56,'19:56','Sọ ìtàn (Ànábì) ’Idrīs tí ó wà nínú al-Ƙur’ān. Dájúdájú ó jẹ́ olódodo pọ́nńbélé, (ó sì jẹ́) Ànábì.');
INSERT INTO "aykyuni" VALUES(19,57,'19:57','A sì gbé e sí àyè gíga (nínú sánmọ̀).');
INSERT INTO "aykyuni" VALUES(19,58,'19:58','Àwọn wọ̀nyẹn ni àwọn tí Allāhu ṣe ìdẹ̀ra fún nínú àwọn Ànábì, nínú àwọn àrọ́mọdọ́mọ (Ànábì) Ādam àti nínú àwọn tí A gbé gun ọkọ̀ ojú-omi pẹ̀lú (Ànábì) Nūh àti nínú àrọ́mọdọ́mọ (Ànábì) ’Ibrọ̄hīm àti ’Isrọ̄’īl àti nínú àwọn tí A ti fi mọ̀nà, tí A sì ṣà lẹ́ṣà. Nígbà tí wọ́n bá ń ké àwọn āyah Àjọkẹ́-ayé fún wọn, wọ́n máa dojú bolẹ̀; tí wọ́n á foríkanlẹ̀, tí wọ́n á sì máa sunkún.');
INSERT INTO "aykyuni" VALUES(19,59,'19:59','Àwọn àrólé kan sì rólé lẹ́yìn wọn, tí wọ́n rá ìrun kíkí láre, tí wọ́n tẹ̀lé àwọn adùn. Láìpẹ́ wọn máa pàdé òfò (nínú Iná).');
INSERT INTO "aykyuni" VALUES(19,60,'19:60','Àyàfi ẹni tí ó bá ronú pìwàdà, tí ó gbàgbọ́ ní òdodo, tí ó sì ṣe iṣẹ́ rere. Àwọn wọ̀nyẹn ni wọn yóò wọ inú Ọgbà Ìdẹ̀ra. A ò sì níí ṣàbòsí kan kan sí wọn.');
INSERT INTO "aykyuni" VALUES(19,61,'19:61','(Wọn yóò wọ inú) àwọn Ọgbà Ìdẹ̀ra gbére tí Àjọkẹ́-ayé ṣe ní àdéhùn ní ìkọ̀kọ̀fún àwọn ẹrúsìn Rẹ̀. Dájúdájú (Allāhu), àdéhùn Rẹ̀ ń bọ̀ wá ṣẹ. ');
INSERT INTO "aykyuni" VALUES(19,62,'19:62','Wọn kò níí gbọ́ ìsọkúsọ nínú rẹ̀ àfi àlàáfíà. Ìjẹ-ìmu wọn wà nínú rẹ̀ ní òwúrọ̀ àti ní ìrọ̀lẹ́.');
INSERT INTO "aykyuni" VALUES(19,63,'19:63','Ìyẹn ni Ọgbà Ìdẹ̀ra tí A óò jogún rẹ̀ fún ẹni tí ó jẹ́ olùbẹ̀rù (Mi) nínú àwọn ẹrúsìn Wa.');
INSERT INTO "aykyuni" VALUES(19,64,'19:64','Àwa (mọlāika) kì í sọ̀kalẹ̀ àyàfi pẹ̀lú àṣẹ Olúwa rẹ. TiRẹ̀ ni ohun tí ń bẹ níwájú wa, ohun tí ń bẹ ní ẹ̀yìn wa àti ohun tí ń bẹ láààrin (méjèèjì) yẹn. Àti pé Olúwa rẹ kì í ṣe onígbàgbé.');
INSERT INTO "aykyuni" VALUES(19,65,'19:65','Olúwa àwọn sánmọ̀ àti ilẹ̀ àti ohun tí ń bẹ láààrin àwọn méjèèjì. Nítorí náà, jọ́sìn fún Un, kí ó sì ṣe sùúrù lórí ìjọ́sìn Rẹ̀. Ǹjẹ́ o mọ ẹni tí ó tún ń jẹ́ orúkọ Rẹ̀?');
INSERT INTO "aykyuni" VALUES(19,66,'19:66','Ènìyàn ń wí pé: “Ṣé nígbà tí mo bá kú, wọn yóò tún mú mi jáde láìpẹ́ ní alààyè?”');
INSERT INTO "aykyuni" VALUES(19,67,'19:67','Ṣé ènìyàn kò rántí pé dájúdájú Àwa ni A ṣẹ̀dá rẹ̀ tẹ́lẹ̀tẹ́lẹ̀ nígbà tí kò tí ì jẹ́ n̄ǹkan kan?');
INSERT INTO "aykyuni" VALUES(19,68,'19:68','Nítorí náà, mo fi Olúwa rẹ búra; dájúdájú A máa kó àwọn àti àwọn èṣù jọ. Lẹ́yìn náà, dájúdájú A máa mú wọn wá sí ayíká iná Jahanamọ lórí ìkúnlẹ̀.');
INSERT INTO "aykyuni" VALUES(19,69,'19:69','Lẹ́yìn náà, dájúdájú A máa mú jáde nínú ìjọ kọ̀ọ̀kan ẹni tí ó le jùlọ nínú wọn níbi ẹ̀ṣẹ̀ dídá sí Àjọkẹ́-ayé.');
INSERT INTO "aykyuni" VALUES(19,70,'19:70','Lẹ́yìn náà, dájúdájú Àwa nímọ̀ jùlọ nípa àwọn tó lẹ́tọ̀ọ́ jùlọ sí wíwọ inú Iná.');
INSERT INTO "aykyuni" VALUES(19,71,'19:71','Kò sí ẹnì kan nínú yín àfi kí ó débẹ̀ (àfi kí ó gba ibẹ̀ kọjá). Ó di dandan kí ó ṣẹlẹ̀ lọ́dọ̀ Olúwa rẹ.');
INSERT INTO "aykyuni" VALUES(19,72,'19:72','Lẹ́yìn náà, A máa gba àwọn tó bẹ̀rù (Allāhu) là. A sì máa fi àwọn alábósì sílẹ̀ sínú Iná lórí ìkúnlẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(19,73,'19:73','Nígbà tí wọ́n bá ń ké àwọn āyah Wa tó yanjú fún wọn, àwọn tó ṣàì gbàgbọ́ yóò sọ fún àwọn tó gbàgbọ́ ní òdodo pé: “Èwo nínú ìjọ méjèèjì (àwa tàbí ẹ̀yin) ló lóore jùlọ ní ibùgbé, ló sì dára jùlọ ní ìjókòó (afẹ́)?”');
INSERT INTO "aykyuni" VALUES(19,74,'19:74','Àti pé mélòó mélòó nínú àwọn ìran tí A ti parẹ́ ṣíwájú wọn, tí wọ́n dára jùlọ ní ọrọ̀ (ayé) àti ní ìrísí!');
INSERT INTO "aykyuni" VALUES(19,75,'19:75','Sọ pé: “Ẹni tí ó bá wà nínú ìṣìnà, Àjọkẹ́-ayé yó sì fẹ (ìṣìnà) lójú fún un tààrà, títí di ìgbà tí wọn máa rí ohun tí wọ́n ń ṣe ní àdéhùn fún wọn, yálà ìyà tàbí Àkókò náà. Nígbà náà, wọn yóò mọ ẹni tí ó burú jùlọ ní ipò, tí ó sì lẹ jùlọ ní ọmọ ogun.');
INSERT INTO "aykyuni" VALUES(19,76,'19:76','Allāhu yó sì ṣàlékún ìmọ̀nà fún àwọn tó mọ̀nà. Àwọn iṣẹ́ rere ẹlẹ́san gbére lóore jùlọ ní ẹ̀san, ó sì lóore jùlọ ní ibùdésí lọ́dọ̀ Olúwa rẹ.');
INSERT INTO "aykyuni" VALUES(19,77,'19:77','Sọ fún mi nípa ẹni tó ṣàì gbàgbọ́ nínú àwọn āyah Wa, tí ó sì wí pé: “Dájúdájú wọn yóò fún mi ní dúkìá àti ọmọ!”');
INSERT INTO "aykyuni" VALUES(19,78,'19:78','Ṣé ó rí ìkọ̀kọ̀ ni tàbí ó rí àdéhùn kan gbà lọ́dọ̀ Àjọkẹ́-ayé?');
INSERT INTO "aykyuni" VALUES(19,79,'19:79','Rárá. A máa ṣàkọsílẹ̀ ohun tó ń wí. A sì máa fẹ ìyà lójú fún un tààrà.');
INSERT INTO "aykyuni" VALUES(19,80,'19:80','A ó sì jogún ohun tó ń wí fún un. Ó sì máa wá bá Wa ní òun nìkan.');
INSERT INTO "aykyuni" VALUES(19,81,'19:81','Wọ́n sọ àwọn kan di ọlọ́hun lẹ́yìn Allāhu nítorí kí wọ́n lè fún wọn ní agbára.');
INSERT INTO "aykyuni" VALUES(19,82,'19:82','Rárá. Wọ́n máa tako ìjọ́sìn wọn, wọ́n si máa di ọ̀tá wọn.');
INSERT INTO "aykyuni" VALUES(19,83,'19:83','Ṣé ìwọ kò wòye pé dájúdájú Àwa ń dẹ àwọn èṣù sí àwọn aláìgbàgbọ́ ni, tí wọ́n sì ń gùn wọ́n gan-an (síbi ẹ̀ṣẹ̀)?');
INSERT INTO "aykyuni" VALUES(19,84,'19:84','Nítorí náà, má ṣe kánjú nípa (ìyà) wọn. A kúkú ń ka (ọjọ́) fún wọn ní kíkà tààrà.');
INSERT INTO "aykyuni" VALUES(19,85,'19:85','(Rántí) ọjọ́ tí A óò kó àwọn olùbẹ̀rù (Allāhu) jọ sọ́dọ̀ Àjọkẹ́-ayé lórí n̄ǹkan ìgùn.');
INSERT INTO "aykyuni" VALUES(19,86,'19:86','A sì máa da àwọn ẹlẹ́ṣẹ̀ lọ sínú iná Jahanamọ wìtìwìtì.');
INSERT INTO "aykyuni" VALUES(19,87,'19:87','Wọn kò níí ní ìkápá ìṣìpẹ̀ àfi ẹni tí ó bá gba àdéhùn láti ọ̀dọ̀ Àjọkẹ́-ayé.');
INSERT INTO "aykyuni" VALUES(19,88,'19:88','Wọ́n wí pé: “Àjọkẹ́-ayé fi ẹnì kan ṣe ọmọ.”');
INSERT INTO "aykyuni" VALUES(19,89,'19:89','Dájúdájú ẹ ti mú n̄ǹkan aburú wá.');
INSERT INTO "aykyuni" VALUES(19,90,'19:90','Àwọn sánmọ̀ fẹ́rẹ̀ fàya pẹ́rẹpẹ̀rẹ nítorí rẹ̀, ilẹ̀ fẹ́rẹ̀ fàya pẹ́rẹpẹ̀rẹ, àwọn àpáta sì fẹ́rẹ̀ dàwó lulẹ̀ gbì');
INSERT INTO "aykyuni" VALUES(19,91,'19:91','fún wí pé wọ́n pe ẹnì kan ní ọmọ Àjọkẹ́-ayé.');
INSERT INTO "aykyuni" VALUES(19,92,'19:92','Kò sì yẹ fún Àjọkẹ́-ayé láti fi ẹnì kan ṣe ọmọ.');
INSERT INTO "aykyuni" VALUES(19,93,'19:93','Kò sí ẹnì kan nínú àwọn sánmọ̀ àti ilẹ̀ àyàfi kí ó wá bá Àjọkẹ́-ayé ní ipò ẹrúsìn.');
INSERT INTO "aykyuni" VALUES(19,94,'19:94','Dájúdájú (Allāhu) mọ̀ wọ́n. Ó sì ka òǹkà wọn tààrà.');
INSERT INTO "aykyuni" VALUES(19,95,'19:95','Gbogbo wọn yó sì wá bá A ní Ọjọ́ Àjíǹde ní ìkọ̀ọ̀kan.');
INSERT INTO "aykyuni" VALUES(19,96,'19:96','Dájúdájú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe iṣẹ́ rere, Àjọkẹ́-ayé yóò fi ìfẹ́ sáààrin wọn.');
INSERT INTO "aykyuni" VALUES(19,97,'19:97','Nítorí náà, A kúkú ṣe kíké al-Ƙur’ān ní ìrọ̀rùn (fún ọ) pẹ̀lú èdè abínibí rẹ nítorí kí o lè fi ṣe ìró ìdùnnú fún àwọn olùbẹ̀rù (Allāhu) àti nítorí kí o lè fi ṣe ìkìlọ̀ fún ìjọ oníyàn-jíjà.');
INSERT INTO "aykyuni" VALUES(19,98,'19:98','Mélòó mélòó nínú ìran tí A ti parẹ́ ṣíwájú wọn! Ǹjẹ́ o gbọ́ ìró ẹnì kan kan nínú wọn mọ́ tàbí (ǹjẹ́) o gbọ́ ohùn kẹ́lẹ́kẹ́lẹ́ kan láti ọ̀dọ̀ wọn bí?');
INSERT INTO "aykyuni" VALUES(20,1,'20:1','Tọ̄ hā. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(20,2,'20:2','A kò sọ al-Ƙur’ān kalẹ̀ fún ọ láti kó wàhálà bá ọ.');
INSERT INTO "aykyuni" VALUES(20,3,'20:3','Bí kò ṣe pé ó jẹ́ ìrántí fún ẹni tó ń páyà (Allāhu).');
INSERT INTO "aykyuni" VALUES(20,4,'20:4','(Ó jẹ́) ìmísí tó sọ̀kalẹ̀ láti ọ̀dọ̀ Ẹni tí Ó dá ilẹ̀ àti àwọn sánmọ̀ gíga.');
INSERT INTO "aykyuni" VALUES(20,5,'20:5','Àjọkẹ́-ayé gúnwà sórí Ìtẹ́-ọlá.');
INSERT INTO "aykyuni" VALUES(20,6,'20:6','TiRẹ̀ ni ohunkóhun tó wà nínú àwọn sánmọ̀, ohunkóhun tó wà nínú ilẹ̀ àti ohunkóhun tó wà láààrin méjèèjì àti ohunkóhun tó wà lábẹ́ ilẹ̀.');
INSERT INTO "aykyuni" VALUES(20,7,'20:7','Tí o bá sọ ọ̀rọ̀ jáde, dájúdájú (Allāhu) mọ ìkọ̀kọ̀ àti ohun tí ó (tún) pamọ́ jùlọ.');
INSERT INTO "aykyuni" VALUES(20,8,'20:8','Allāhu, kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. TiRẹ̀ ni àwọn orúkọ tó dára jùlọ.');
INSERT INTO "aykyuni" VALUES(20,9,'20:9','Ǹjẹ́ ọ̀rọ̀ (Ànábì) Mūsā dé ọ̀dọ̀ rẹ?');
INSERT INTO "aykyuni" VALUES(20,10,'20:10','(Rántí) nígbà tí ó rí iná kan, ó sọ fún àwọn ará ilé rẹ̀ pé: “Ẹ dúró síbí. Dájúdájú èmi rí iná kan. Ó ṣeé ṣe kí n̄g rí ògúnná mú wá fún yín nínú rẹ̀ tàbí kí n̄g rí ojú ọ̀nà kan nítòsí iná náà.”');
INSERT INTO "aykyuni" VALUES(20,11,'20:11','Nígbà tí ó dé ibẹ̀, A pè é “Mūsā,');
INSERT INTO "aykyuni" VALUES(20,12,'20:12','Dájúdájú Èmi ni Olúwa rẹ. Nítorí náà, bọ́ bàtà rẹ méjèèjì sílẹ̀.Dájúdájú ìwọ wà ní àfonífojì mímọ́, Tuwā. ');
INSERT INTO "aykyuni" VALUES(20,13,'20:13','Àti pé Èmi ṣà ọ́ lẹ́ṣà. Nítorí náà, fi etí sí ohun tí A máa fi ránṣẹ́ sí ọ nínú ìmísí.');
INSERT INTO "aykyuni" VALUES(20,14,'20:14','Dájúdájú Èmi ni Allāhu. Kò sí ọlọ́hun kan tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Èmi. Nítorí náà, jọ́sìn fún Mi. Kí o sì kírun fún ìrántí Mi.1 ');
INSERT INTO "aykyuni" VALUES(20,15,'20:15','Dájúdájú Àkókò náà ń bọ̀. Ó súnmọ́ kí N̄g ṣàfi hànrẹ̀ (pẹ̀lú àwọn àmì kan. Àkókò náà wà fún) kí Á lè san ẹ̀mí kọ̀ọ̀kan ní ẹ̀san n̄ǹkan tí ó ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(20,16,'20:16','Nítorí náà, ẹni tí kò gbàgbọ́ nínú rẹ̀, tí ó sì tẹ̀lé ìfẹ́-inú rẹ̀, má ṣe jẹ́ kí ó ṣẹ́rí rẹ kúrò níbẹ̀, kí o má baà parun.');
INSERT INTO "aykyuni" VALUES(20,17,'20:17','Kí ni ìyẹn lọ́wọ́ ọ̀tún rẹ, Mūsā?”');
INSERT INTO "aykyuni" VALUES(20,18,'20:18','Ó sọ pé: “Ọ̀pá mi ni. Mò ń fi rọ̀gbọ̀kú. Mo tún ń fi já ewé fún àwọn àgùtàn mi. Mo sì tún ń lò ó fún àwọn bùkátà mìíràn.”');
INSERT INTO "aykyuni" VALUES(20,19,'20:19','(Allāhu) sọ pé: “Jù ú sílẹ̀, Mūsā.”');
INSERT INTO "aykyuni" VALUES(20,20,'20:20','Nígbà náà, ó jù ú sílẹ̀. Ó sì di ejò tó ń sáré.');
INSERT INTO "aykyuni" VALUES(20,21,'20:21','(Allāhu) sọ pé: “Mú un dání, má sì ṣe bẹ̀rù. A máa dá a padà sí ìrísí rẹ̀ àkọ́kọ́.');
INSERT INTO "aykyuni" VALUES(20,22,'20:22','Fi ọwọ́ rẹ mọ́ (abẹ́) apá rẹ, ó máa jáde ní funfun, tí kì í ṣe taburú. (Ó tún jẹ́) àmì mìíràn.');
INSERT INTO "aykyuni" VALUES(20,23,'20:23','Kí Á lè fi èyí tó tóbi nínú àwọn àmì Wa hàn ọ́.');
INSERT INTO "aykyuni" VALUES(20,24,'20:24','Lọ sí ọ̀dọ̀ Fir‘aon. Dájúdájú ó ti tayọ ẹnu-ààlà.”');
INSERT INTO "aykyuni" VALUES(20,25,'20:25','(Ànábì Mūsā) sọ pé: “Olúwa mi, ṣí ọkàn mi payá fún mi.');
INSERT INTO "aykyuni" VALUES(20,26,'20:26','Ṣọ̀rọ̀ mi dìrọ̀rùn fún mi.');
INSERT INTO "aykyuni" VALUES(20,27,'20:27','Tú kókó orí ahọ́n mi');
INSERT INTO "aykyuni" VALUES(20,28,'20:28','kí wọ́n máa gbọ́ ọ̀rọ̀ mi yé.');
INSERT INTO "aykyuni" VALUES(20,29,'20:29','Àti pé fún mi ní amúgbálẹ́gbẹ̀ẹ́ kan nínú àwọn ará ilé mi,');
INSERT INTO "aykyuni" VALUES(20,30,'20:30','Hārūn, arákùnrin mi ni.');
INSERT INTO "aykyuni" VALUES(20,31,'20:31','Fi kún mi lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(20,32,'20:32','Fi ṣakẹgbẹ́ mi nínú iṣẹ́ mi');
INSERT INTO "aykyuni" VALUES(20,33,'20:33','torí kí á lè ṣàfọ̀mọ́ (orúkọ) Rẹ ní ọ̀pọ̀lọ́pọ̀,');
INSERT INTO "aykyuni" VALUES(20,34,'20:34','kí á tún lè ṣèrántí Rẹ ní ọ̀pọ̀lọ́pọ̀.');
INSERT INTO "aykyuni" VALUES(20,35,'20:35','Dájúdájú Ìwọ ń rí wa.”');
INSERT INTO "aykyuni" VALUES(20,36,'20:36','(Allāhu) sọ pé: “Dájúdájú A ti fún ọ ní ìbéèrè rẹ, Mūsā.');
INSERT INTO "aykyuni" VALUES(20,37,'20:37','Àti pé dájúdájú A ti ṣe oore fún ọ nígbà kan rí.');
INSERT INTO "aykyuni" VALUES(20,38,'20:38','(Rántí) nígbà tí A ṣípayá ohun tí A ṣípayá fún ìyá rẹ');
INSERT INTO "aykyuni" VALUES(20,39,'20:39','pé: “Ju Mūsā sínú àpótí. Kí o sì gbé e jù sínú odò. Odò yó sì gbé e jù sí etí odò. Ọ̀tá Mi àti ọ̀tá rẹ̀ sì máa gbé e.” Mo sì da ìfẹ́ láti ọ̀dọ̀ Mi bò ọ́ nítorí kí wọ́n lè tọ́jú rẹ lójú Mi.”');
INSERT INTO "aykyuni" VALUES(20,40,'20:40','(Rántí) nígbà tí arábìnrin rẹ ń rìn lọ, ó sì ń sọ pé: “Ṣé kí n̄g júwe yín sí ẹni tí ó máa gbà á tọ́?” A sì dá ọ padà sọ́dọ̀ ìyá rẹ nítorí kí ó lè ní ìtutù ojú, àti pé kí ò má baà banújẹ́. Àti pé o pa ẹ̀mí ẹnì kan. A sì mú ọ kúrò nínú ìbànújẹ́. A tún mú àwọn àdánwò kan bá ọ. O tún lo àwọn ọdún kan lọ́dọ̀ àwọn ará Mọdyan. Lẹ́yìn náà, o dé (ní àkókò) tí ó ti wà nínú kádàrá, Mūsā.');
INSERT INTO "aykyuni" VALUES(20,41,'20:41','Mo sì ṣà ọ́ lẹ́ṣà fún (iṣẹ́) Mi.');
INSERT INTO "aykyuni" VALUES(20,42,'20:42','Lọ, ìwọ àti arákùnrin rẹ pẹ̀lú àwọn àmì (iṣẹ́ ìyanu) Mi. Kí ẹ sì má ṣe kọ́lẹ níbi ìrántí Mi.');
INSERT INTO "aykyuni" VALUES(20,43,'20:43','Ẹ lọ bá Fir‘aon. Dájúdájú ó ti tayọ ẹnu-ààlà.');
INSERT INTO "aykyuni" VALUES(20,44,'20:44','Ẹ sọ ọ̀rọ̀ ẹ̀lẹ̀ fún un. Ó ṣeé ṣe kí ó lo ìṣítí tàbí kí ó páyà (Mi).”');
INSERT INTO "aykyuni" VALUES(20,45,'20:45','Àwọn méjèèjì sọ pé: “Olúwa wa, dájúdájú àwa ń páyà pé ó má yára jẹ wá níyà tàbí pé ó má tayọ ẹnu-ààlà.”');
INSERT INTO "aykyuni" VALUES(20,46,'20:46','(Allāhu) sọ pé: “Ẹ má ṣe páyà. Dájúdájú Èmi wà pẹ̀lú ẹ̀yin méjèèjì; Mò ń gbọ (yín), Mo sì ń ri (yín).”1 ');
INSERT INTO "aykyuni" VALUES(20,47,'20:47','Nítorí náà, ẹ lọ bá a, kí ẹ sọ fún un pé: “Dájúdájú Òjíṣẹ́ Olúwa rẹ ni àwa méjèèjì. Nítorí náà, jẹ́ kí àwọn ọmọ ’Isrọ̄’īl máa bá wa lọ. Má ṣe fìyà jẹ wọ́n. Dájúdájú a ti mú àmì kan wá bá ọ láti ọ̀dọ̀ Olúwa rẹ. Kí àlàáfíà sì máa bá ẹni tí ó bá tẹ̀lé ìmọ̀nà náà.');
INSERT INTO "aykyuni" VALUES(20,48,'20:48','Dájúdájú wọ́n ti fi ìmísí ránṣẹ́ sí wa pé, dájúdájú ìyà ń bẹ fún ẹni tí ó bá pe òdodo ní irọ́, tí ó sì kẹ̀yìn sí i.”');
INSERT INTO "aykyuni" VALUES(20,49,'20:49','(Fir‘aon) wí pé: “Ta ni Olúwa ẹ̀yin méjèèjì, Mūsā?”');
INSERT INTO "aykyuni" VALUES(20,50,'20:50','(Ànábì Mūsā) sọ pé: “Olúwa wa ni Ẹni tí Ó fún gbogbo n̄ǹkan ní ìṣẹ̀dá rẹ̀.Lẹ́yìn náà, Ó tọ́ ọ sí ọ̀nà.” ');
INSERT INTO "aykyuni" VALUES(20,51,'20:51','(Fir‘aon) wí pé: “Kí ló mú àwọn ìran àkọ́kọ́ (tí wọn kò fi mọ̀nà)?”');
INSERT INTO "aykyuni" VALUES(20,52,'20:52','(Ànábì Mūsā) sọ pé: “Ìmọ̀ rẹ̀ wà nínú tírà kan lọ́dọ̀ Olúwa mi. Olúwa mi kò níí ṣàṣìṣe. Kò sì níí gbàgbé.');
INSERT INTO "aykyuni" VALUES(20,53,'20:53','(Òun ni) Ẹni tí Ó tẹ́ ilẹ̀ ní pẹrẹsẹ fún yín. Ó sì la àwọn ọ̀nà sínú rẹ̀ fún yín. Ó sì sọ omi kalẹ̀ láti sánmọ̀. A sì fi mú oríṣiríṣi jáde nínú àwọn irúgbìn ní ọ̀tọ̀ọ̀tọ̀.');
INSERT INTO "aykyuni" VALUES(20,54,'20:54','Ẹ jẹ, kí ẹ sì fi bọ́ àwọn ẹran-ọ̀sìn yín. Dájúdájú àwọn àmì wà nínú ìyẹn fún àwọn onílàákàyè.');
INSERT INTO "aykyuni" VALUES(20,55,'20:55','Láti ara erùpẹ̀ ni A ti da yín. Inú rẹ̀ sì ni A óò da yín padà sí. Láti inú rẹ̀ sì ni A óò ti mu yín jáde padà nígbà mìíràn.”');
INSERT INTO "aykyuni" VALUES(20,56,'20:56','Dájúdájú A ti fi àwọn àmì Wa hàn án, gbogbo rẹ̀. Àmọ́ ó pè é ní irọ́. Ó sì kọ̀ jálẹ̀.');
INSERT INTO "aykyuni" VALUES(20,57,'20:57','Ó wí pé: “Ṣé o wá bá wa nítorí kí o lè mú wa jáde kúrò lórí ilẹ̀ wa pẹ̀lú idán rẹ ni, Mūsā?');
INSERT INTO "aykyuni" VALUES(20,58,'20:58','Dájúdájú a máa mú idán irú rẹ̀ wá bá ọ. Nítorí náà, mú àyè àdéhùn ìpàdé láààrin àwa àti ìwọ, tí àwa àti ìwọ kò sì níí yapa rẹ̀, (kí ó jẹ́) àyè kan tí kò níí fì sọ́dọ̀ igun méjèèjì.”');
INSERT INTO "aykyuni" VALUES(20,59,'20:59','(Ànábì Mūsā) sọ pé: “Àkókò ìpàdé yín ni ọjọ́ Ọ̀ṣọ́ (ìyẹn, ọjọ́ ọdún). Kí wọ́n sì kó àwọn ènìyàn jọ ní ìyálẹ̀ta.”');
INSERT INTO "aykyuni" VALUES(20,60,'20:60','Fir‘aon pẹ̀yìndà. Ó sì sa ète rẹ̀ jọ mọ́ra wọn. Lẹ́yìn náà, ó dé (síbẹ̀).');
INSERT INTO "aykyuni" VALUES(20,61,'20:61','(Ànábì Mūsā) sọ pé: “Ègbé yín ò! Ẹ má ṣe dá àdápa irọ́ mọ́ Allāhu nítorí kí Ó má baà fi ìyà pa yín rẹ́. Àti pé dájúdájú ẹnikẹ́ni tí ó bá dá àdápa irọ́ (mọ́ Allāhu) ti pòfo.”');
INSERT INTO "aykyuni" VALUES(20,62,'20:62','Wọ́n fọ̀rọ̀ jomi toro ọ̀rọ̀ láààrin ara wọn. Wọ́n sì tẹ̀ sọ̀rọ̀.');
INSERT INTO "aykyuni" VALUES(20,63,'20:63','Wọ́n wí pé: “Dájúdájú àwọn méjèèjì yìí, òpìdán ni wọ́n o. Wọ́n fẹ́ mu yín jáde kúrò lórí ilẹ̀ yín pẹ̀lú idán wọn ni. Wọ́n sì (fẹ́) gba ojú ọ̀nà yín tó dára jùlọ mọ yín lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(20,64,'20:64','Nítorí náà, ẹ kó ète yín jọ papọ̀ mọ́ra wọn. Lẹ́yìn náà, kí ẹ tò wá ní ọ̀wọ̀ọ̀wọ́. Dájúdájú ẹni tí ó bá borí ní òní ti jèrè.”');
INSERT INTO "aykyuni" VALUES(20,65,'20:65','(Àwọn òpìdán) wí pé: “Mūsā, ìwọ ni o máa kọ́kọ́ ju (ọ̀pá sílẹ̀ ni) tàbí àwa ni a óò kọ́kọ́ jù ú sílẹ̀.”');
INSERT INTO "aykyuni" VALUES(20,66,'20:66','(Mūsā) sọ pé: “Ẹ ju (tiyín) sílẹ̀ (ná).” Nígbà náà ni àwọn okùn wọn àti ọ̀pá wọn bá ń yíra padà lójú rẹ̀ nípasẹ̀ idán wọn, bí ẹni pé dájúdájú wọ́n ń sáré.');
INSERT INTO "aykyuni" VALUES(20,67,'20:67','Ìbẹ̀rù wọn sì mú (Ànábì) Mūsā.1 ');
INSERT INTO "aykyuni" VALUES(20,68,'20:68','A sọ pé: “Má ṣe páyà. Dájúdájú ìwọ, ìwọ lo máa lékè.');
INSERT INTO "aykyuni" VALUES(20,69,'20:69','Ju ohun tí ń bẹ ní ọwọ́ ọ̀tún rẹ sílẹ̀. Ó sì máa gbé ohun tí wọ́n ṣe kalẹ̀ mì káló. Dájúdájú ohun tí wọ́n ṣe kalẹ̀, ète òpìdán ni. Òpìdán kò sì níí jèrè ní ibikíbi tí ó bá dé.”');
INSERT INTO "aykyuni" VALUES(20,70,'20:70','Nítorí náà, iṣẹ́ ìyanu (Ànábì Mūsā) mú àwọn òpìdán wó lulẹ̀, tí wọ́n forí kanlẹ̀ (fún Allāhu). Wọ́n wí pé: “A gbàgbọ́ nínú Olúwa Hārūn àti Mūsā.”');
INSERT INTO "aykyuni" VALUES(20,71,'20:71','(Fir‘aon) wí pé: “Ṣé ẹ ti gbàfà fún un ṣíwájú kí èmi tó yọ̀ǹda fún yín? Dájúdájú òun ni àgbà yín tí ó kọ yín ní idán pípa. Nítorí náà, dájúdájú mo máa gé àwọn ọwọ́ yín àti ẹsẹ̀ yín ní ìpasípayọ. Dájúdájú mo tún máa kàn yín mọ́ àwọn igi dàbínù. Dájúdájú ẹ̀yin yóò mọ èwo nínú wa ni ìyà (rẹ̀) yóò le jùlọ, tí ó sì máa pẹ́ jùlọ.”');
INSERT INTO "aykyuni" VALUES(20,72,'20:72','(Àwọn òpìdán) sọ pé: “Àwa kò níí gbọ́lá fún ọ lórí ohun tí ó dé bá wa nínú àwọn ẹ̀rí tó dájú, (a kò sì níí gbọ́lá fún ọ lórí) Ẹni tí Ó pilẹ̀ ìṣẹ̀dá wa. Nítorí náà, dá ohun tí ó bá fẹ́ dá ní ẹjọ́. Ilé ayé yìí nìkan ni o ti lè dájọ́.');
INSERT INTO "aykyuni" VALUES(20,73,'20:73','Dájúdájú àwa gbàgbọ́ nínú Olúwa wa nítorí kí Ó lè ṣe àforíjìn àwọn ẹ̀ṣẹ̀ wa fún wa àti ohun tí o jẹ wá nípá ṣe nínú idán pípa. Allāhu lóore jùlọ, Ó sì máa wà títí láéláé.');
INSERT INTO "aykyuni" VALUES(20,74,'20:74','Dájúdájú ẹnikẹ́ni tí ó bá wá bá Olúwa rẹ̀ ní (ipò) ẹlẹ́ṣẹ̀, dájúdájú iná Jahanamọ ti wà fún un. Kò níí kú sínú rẹ̀, kò sì níí wà lààyè.');
INSERT INTO "aykyuni" VALUES(20,75,'20:75','Ẹnikẹ́ni tí ó bá sì wá bá A (ní ipò) onígbàgbọ́ òdodo, tí ó sì ti ṣe àwọn iṣẹ́ rere, àwọn wọ̀nyẹn ni àwọn ipò gíga ń bẹ fún.');
INSERT INTO "aykyuni" VALUES(20,76,'20:76','(Ohun ni) àwọn Ọgbà ìdẹ̀ra gbére tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀. Ìyẹn sì ni ẹ̀san fún ẹnikẹ́ni tí ó bá ṣàfọ̀mọ́ (iṣẹ́ rẹ̀).”');
INSERT INTO "aykyuni" VALUES(20,77,'20:77','Àti pé dájúdájú A ti fi ìmísí ránṣẹ́ sí (Ànábì) Mūsā báyìí pé: “Mú àwọn ẹrúsìn Mi rìn ní òru. Fi (ọ̀pá rẹ) na omi fún wọn (kí ó di) ojú ọ̀nà gbígbẹ nínú omi òkun. Má ṣe bẹ̀rù àlébá, má sì ṣe páyà (ìtẹ̀rì sínú omi òkun).”');
INSERT INTO "aykyuni" VALUES(20,78,'20:78','Fir‘aon sì tẹ̀lé wọn pẹ̀lú àwọn ọmọ ogun rẹ̀. Ohun tí ó bò wọ́n mọ́lẹ̀ dáru nínú odò sì bò wọ́n mọ́lẹ̀ dáru.');
INSERT INTO "aykyuni" VALUES(20,79,'20:79','Fir‘aon ṣi àwọn ènìyàn rẹ̀ lọ́nà; òun (náà) kò sì mọ̀nà.');
INSERT INTO "aykyuni" VALUES(20,80,'20:80','Ẹ̀yin ọmọ ’Isrọ̄’īl, dájúdájú A ti gbà yín là lọ́wọ́ ọ̀tá yín. A sì ṣe àdéhùn fún yín (pé kí Ànábì Mūsā wá, kí Á bá a sọ̀rọ̀ tààrà) ní ẹ̀gbẹ́ àpáta ní ọwọ́ ọ̀tún(Ànábì Mūsā láààrin àpáta àti àfonífojì2 ní ìwọ̀ òòrùn3). A sì sọ ohun mímu mọnu àti ohun jíjẹ salwā kalẹ̀ fún yín.4 ');
INSERT INTO "aykyuni" VALUES(20,81,'20:81','Ẹ jẹ nínú àwọn n̄ǹkan dáadáa tí A pa lésè fún yín. Ẹ má sì kọjá ẹnu ààlà nínú rẹ̀ nítorí kí ìbínú Mi má baà kò le yín lórí. Ẹnikẹ́ni tí ìbínú Mi bá sì kò lé lórí, dájúdájú ó ti jábọ́ (sínú ìparun).');
INSERT INTO "aykyuni" VALUES(20,82,'20:82','Dájúdájú Èmi ni Aláforíjìn fún ẹnikẹ́ni tí ó bá ronú pìwàdà, tí ó gbàgbọ́ ní òdodo, tí ó sì ṣe iṣẹ́ rere, lẹ́yìn náà tí ó tún tẹ̀lé ìmọ̀nà.');
INSERT INTO "aykyuni" VALUES(20,83,'20:83','Kí ni ó mú ọ kánjú ya àwọn ènìyàn rẹ sílẹ̀, Mūsā?');
INSERT INTO "aykyuni" VALUES(20,84,'20:84','Ó sọ pé: “Àwọn nìwọ̀nyí tí wọ́n ń tọ orípa mi bọ̀ (lẹ́yìn mi). Èmi sì kánjú wá sọ́dọ̀ Rẹ nítorí kí O lè yọ́nú sí mi ni, Olúwa mi.”');
INSERT INTO "aykyuni" VALUES(20,85,'20:85','(Allāhu) sọ pé: “Dájúdájú Àwa ti dán ìjọ rẹ wò lẹ́yìn rẹ. Àti pé Sāmiriy ṣì wọ́n lọ́nà.”');
INSERT INTO "aykyuni" VALUES(20,86,'20:86','(Ànábì) Mūsā sì padà sọ́dọ̀ ìjọ rẹ̀ pẹ̀lú ìbínú àti ìbànújẹ́. Ó sọ pé: “Ẹ̀yin ìjọ mi, ṣé Olúwa yín kò ti ba yín ṣe àdéhùn dáadáa ni? Ṣé àdéhùn ti pẹ́ jù lójú yín ni tàbí ẹ fẹ́ kí ìbínú kò le yín lórí láti ọ̀dọ̀ Olúwa yín ni ẹ fi yapa àdéhùn mi?”');
INSERT INTO "aykyuni" VALUES(20,87,'20:87','Wọ́n wí pé: “Àwa kò fínnúfíndọ̀ yapa àdéhùn fúnra wa, ṣùgbọ́n wọ́n di ẹrù ọ̀ṣọ́ àwọn ènìyàn (Misrọ) rù wá lọ́rùn. A sì jù ú sínú iná (pẹ̀lú àṣẹ Sāmiriyy). Báyẹn náà sì ni Sāmiriyy ṣe ju tirẹ̀.”');
INSERT INTO "aykyuni" VALUES(20,88,'20:88','Ó sì (fi mọ) ọmọ màálù ọ̀bọrọgidi kan jáde fún wọn, tó ń dún (bíi màálù). Wọ́n sì wí pé: “Èyí ni ọlọ́hun yín àti ọlọ́hun Mūsā. Àmọ́ ó ti gbàgbé (rẹ̀).”');
INSERT INTO "aykyuni" VALUES(20,89,'20:89','Ṣé wọn kò rí i pé kò lè dá èsì ọ̀rọ̀ kan padà fún wọn ni, kò sì ní agbára láti kó ìnira tàbí àǹfààní kan bá wọn!');
INSERT INTO "aykyuni" VALUES(20,90,'20:90','Àti pé dájúdájú Hārūn ti sọ fún wọn ṣíwájú pé: “Ẹ̀yin ìjọ mi, wọ́n kàn fi ṣe àdánwò fún yín ni. Dájúdájú Olúwa yín ni Àjọkẹ́-ayé. Nítorí náà, ẹ tẹ̀lé mi. Kí ẹ sì tẹ̀lé àṣẹ mi.”');
INSERT INTO "aykyuni" VALUES(20,91,'20:91','Wọ́n wí pé: “A ò níí yé dúró sọ́dọ̀ rẹ̀ tí a óò máa jọ́sìn fún un títí (Ànábì) Mūsā yóò fi padà sọ́dọ̀ wa.”');
INSERT INTO "aykyuni" VALUES(20,92,'20:92','(Ànábì) Mūsā sọ pé: “Hārūn, kí ni ó kọ̀ fún ọ nígbà tí o rí wọn tí wọ́n ṣìnà');
INSERT INTO "aykyuni" VALUES(20,93,'20:93','láti tọ̀ mí wá? Ṣé o yapa àṣẹ mi ni?”');
INSERT INTO "aykyuni" VALUES(20,94,'20:94','(Ànábì Hārūn) sọ pé: “Ọmọ ìyá mi, má ṣe fi irungbọ̀n mi mú mi, (má ṣe fi irun) orí mi mú mi. Dájúdájú èmi páyà pé ó máa sọ pé: ''O dá òpínyà sílẹ̀ láààrin àwọn ọmọ ’Isrọ̄’īl. O ò sì ṣọ́ ọ̀rọ̀ mi.''“');
INSERT INTO "aykyuni" VALUES(20,95,'20:95','(Ànábì Mūsā) sọ pé: “Kí ni ọ̀rọ̀ tìrẹ ti jẹ́, Sāmiriyy?”');
INSERT INTO "aykyuni" VALUES(20,96,'20:96','(Sāmiriyy) wí pé: “Mo rí ohun tí wọn kò rí. Mo sì bu ẹ̀kúnwọ́ kan níbi (erùpẹ̀) ojú ẹsẹ̀ (ẹṣin) Òjíṣẹ́ (ìyẹn, mọlāika Jibrīl). Mo sì dà á sílẹ̀ (láti fi mọ ère). Báyẹn ni ẹ̀mí mi ṣe (aburú) ní ọ̀ṣọ́ fún mi.”');
INSERT INTO "aykyuni" VALUES(20,97,'20:97','(Ànábì Mūsā) sọ pé: “Nítorí náà, máa lọ. Dájúdájú (ìjìyà) rẹ nílé ayé yìí ni kí o máa wí pé, “Má fi ara kàn mí”. Àti pé dájúdájú ọjọ́ ìyà kan ń bẹ fún ọ tí wọn kò níí gbé fò ọ́. Kí o sì wo ọlọ́hun rẹ, èyí tí o takú tì lọ́rùn tí ò ń bọ, dájúdájú a máa dáná sun ún. Lẹ́yìn náà, dájúdájú a máa ku eérú rẹ̀ dànù pátápátá sínú odò.');
INSERT INTO "aykyuni" VALUES(20,98,'20:98','Ọlọ́hun yín ni Allāhu nìkan, ẹni tí kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Ó fi ìmọ̀ gbòòrò ju gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(20,99,'20:99','Báyẹn ni A ṣe ń sọ ìtàn fún ọ nínú àwọn ìró tí ó ti ṣíwájú. Láti ọ̀dọ̀ Wa sì ni A kúkú ti fún ọ ní ìrántí (ìyẹn, al-Ƙur’ān).');
INSERT INTO "aykyuni" VALUES(20,100,'20:100','Ẹnikẹ́ni tí ó bá gbúnrí kúrò níbẹ̀, dájúdájú ó máa ru ẹrù ẹ̀ṣẹ̀ ní Ọjọ́ Àjíǹde.');
INSERT INTO "aykyuni" VALUES(20,101,'20:101','Olùṣegbére ni wọ́n nínú (ìyà) rẹ̀. (Ẹrù ẹ̀ṣẹ̀) sì burú fún wọn láti rù ní Ọjọ́ Àjíǹde.');
INSERT INTO "aykyuni" VALUES(20,102,'20:102','Ní ọjọ́ tí wọ́n á fọn fèrè oníwo fún àjíǹde. A sí máa kó àwọn ẹlẹ́ṣẹ̀ jọ ní ọjọ́ yẹn, tí ojú wọn yó sì dúdú batakun.');
INSERT INTO "aykyuni" VALUES(20,103,'20:103','Wọn yó sì máa sọ̀rọ̀ ní jẹ́ẹ́jẹ́ láààrin ara wọn pé: “Ẹ̀yin kò gbé ilé ayé tayọ (ọjọ́) mẹ́wàá.”');
INSERT INTO "aykyuni" VALUES(20,104,'20:104','Àwa nímọ̀ jùlọ nípa ohun tí wọ́n ń wí nígbà tí ẹni tí ọpọlọ rẹ̀ pé jùlọ nínú wọn wí pé: “Ẹ kò gbé ilé ayé tayọ ọjọ́ kan.”');
INSERT INTO "aykyuni" VALUES(20,105,'20:105','Wọ́n ń bi ọ́ léèrè nípa àwọn àpáta. Sọ pé: “Olúwa mi yóò kù wọ́n dànù pátápátá.');
INSERT INTO "aykyuni" VALUES(20,106,'20:106','Nígbà náà, Ó máa fi wọ́n sílẹ̀ ni pẹ̀tẹ́lẹ̀ gban̄sasa.');
INSERT INTO "aykyuni" VALUES(20,107,'20:107','O ò sì níí rí ọ̀gbun tàbí gelemọ kan nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(20,108,'20:108','Ní ọjọ́ yẹn, àwọn ènìyàn yóò tẹ̀lé olùpèpè náà, kò níí sí yíyà kúrò lẹ́yìn rẹ̀. Àwọn ohùn yó sì palọ́lọ́ fún Àjọkẹ́-ayé. Nítorí náà, o ò níí gbọ́ ohùn kan bí kò ṣe gìrìgìrì ẹsẹ̀.');
INSERT INTO "aykyuni" VALUES(20,109,'20:109','Ní ọjọ́ yẹn, ìṣìpẹ̀ kò níí ṣe àǹfààní àfi ẹni tí Àjọkẹ́-ayé bá yọ̀ǹda fún, tí Ó sì yọ́nú sí ọ̀rọ̀ fún un.1 ');
INSERT INTO "aykyuni" VALUES(20,110,'20:110','(Allāhu) mọ ohun tí ń bẹ níwájú wọn àti ohun tí ń bẹ ní ẹ̀yìn wọn. Àwọn kò sì fi ìmọ̀ rọkiri ká Rẹ̀.');
INSERT INTO "aykyuni" VALUES(20,111,'20:111','Àwọn ojú yó sì wólẹ̀ fún Alààyè, Alámòjúútó-ẹ̀dá. Dájúdájú ẹnikẹ́ni tí ó bá di àbòsí mẹ́rù ti pòfo.');
INSERT INTO "aykyuni" VALUES(20,112,'20:112','Ẹnikẹ́ni tí ó bá ṣe nínú àwọn iṣẹ́ rere, tí ó sì jẹ́ onígbàgbọ́ òdodo, kí ó má ṣe páyà àbòsí tàbí ìrẹ́jẹ kan (láti ọ̀dọ̀ Allāhu).');
INSERT INTO "aykyuni" VALUES(20,113,'20:113','Báyẹn ni A ṣe sọ (ìrántí) kalẹ̀ ní ohun kíké pẹ̀lú èdè Lárúbáwá. A sì ṣe àlàyé oníran-ànran ìlérí sínú rẹ̀ nítorí kí wọ́n lè ṣọ́ra (fún ìyà) tàbí nítorí kí ó lè jẹ́ ìrántí fún wọn.');
INSERT INTO "aykyuni" VALUES(20,114,'20:114','Nítorí náà, Allāhu Ọba Òdodo ga jùlọ. Má ṣe kánjú pẹ̀lú al-Ƙur’ān ṣíwájú kí wọ́n tó parí ìmísí rẹ̀ fún ọ. Kí o sì sọ pé: “Olúwa mi, ṣàlékún ìmọ̀ fún mi.”');
INSERT INTO "aykyuni" VALUES(20,115,'20:115','Dájúdájú A ṣe àdéhùn fún (Ànábì) Ādam ṣíwájú. Ṣùgbọ́n ó gbàgbé. A kò sì bá a pẹ̀lú ìpinnu ọkàn.');
INSERT INTO "aykyuni" VALUES(20,116,'20:116','Nígbà tí A sọ fún àwọn mọlāika pé: “Ẹ forí kanlẹ̀ kí Ādam. Wọ́n sì forí kanlẹ̀ kí i àfi ’Iblīs, (tí) ó kọ̀.”');
INSERT INTO "aykyuni" VALUES(20,117,'20:117','Nítorí náà, A sọ pé: “Ādam, dájúdájú èyí ni ọ̀tá fún ìwọ àti ìyàwó rẹ. Nítorí náà, kò gbọdọ̀ mú ẹ̀yin méjèèjì jáde kúrò nínú Ọgbà Ìdẹ̀ra nítorí kí o má baà máa dààmú (fún ìjẹ-ìmu).');
INSERT INTO "aykyuni" VALUES(20,118,'20:118','Dájúdájú ebi kò níí pa ọ́, o ò sì níí rìn hòhò nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(20,119,'20:119','Àti pé dájúdájú òǹgbẹ kò níí gbẹ ọ́ nínú rẹ̀, òòrùn kò sì níí pa ọ́.”');
INSERT INTO "aykyuni" VALUES(20,120,'20:120','Ṣùgbọ́n Èṣù kó ròyíròyí bá a; ó wí pé: “Ādam, ǹjẹ́ mo lè júwe rẹ sí igi gbére àti ìjọba tí kò níí tán?”');
INSERT INTO "aykyuni" VALUES(20,121,'20:121','Nítorí náà, àwọn méjèèjì jẹ nínú (èso) rẹ̀. Ìhòhò àwọn méjèèjì sì hàn síra wọn; àwọn méjèèjì sì bẹ̀rẹ̀ sí da ewé Ọgbà Ìdẹ̀ra bo ara wọn. Ādam yapa àṣẹ Olúwa rẹ̀. Ó sì ṣìnà.');
INSERT INTO "aykyuni" VALUES(20,122,'20:122','Lẹ́yìn náà, Olúwa rẹ̀ ṣà á lẹ́ṣà; Ó gba ìronúpìwàdà rẹ̀, Ó sì tọ́ ọ sí ọ̀nà.');
INSERT INTO "aykyuni" VALUES(20,123,'20:123','(Allāhu) sọ pé: “Ẹ̀yin méjèèjì, ẹ jáde kúrò nínú rẹ̀ pátápátá. Apá kan yín sì jẹ́ ọ̀tá sí apá kan - ẹ̀yin àti Èṣù. Nígbà tí ìmọ̀nà bá sì dé ba yín láti ọ̀dọ̀ Mi, ẹnikẹ́ni tí ó bá tẹ̀lé ìmọ̀nà Mi, kò níí ṣìnà, kò sì níí ṣorí burúkú.');
INSERT INTO "aykyuni" VALUES(20,124,'20:124','Ẹnikẹ́ni tí ó bá sì ṣẹ́rí kúrò níbi ìrántí Mi, dájúdájú ìṣẹ̀mí ìnira máa wà fún un. A sì máa gbé e dìde ní afọ́jú ní Ọjọ́ Àjíǹde.”');
INSERT INTO "aykyuni" VALUES(20,125,'20:125','(Ó) máa wí pé: “Olúwa mi, nítorí kí ni O fi gbé mi dìde ní afọ́jú? Mo ti jẹ́ olùríran tẹ́lẹ̀!”');
INSERT INTO "aykyuni" VALUES(20,126,'20:126','(Allāhu) sọ pé: “Báyẹn ni àwọn āyah Wa ṣe dé bá ọ, o sì gbàgbé rẹ̀ (o pa á tì). Ní òní, báyẹn ni wọ́n ṣe máa gbàgbé ìwọ náà (sínú Iná).');
INSERT INTO "aykyuni" VALUES(20,127,'20:127','Àti pé báyẹn ni A ṣe ń san ẹ̀san fún ẹnikẹ́ni tí ó bá ṣe àṣejù, tí kò sì gbàgbọ́ nínú àwọn āyah Olúwa rẹ̀. Dájúdájú ìyà Ọjọ́ Ìkẹ́yìn le jùlọ. Ó sì máa wà títí láéláé.”');
INSERT INTO "aykyuni" VALUES(20,128,'20:128','Ṣé kò hàn sí wọn pé mélòó mélòó nínú àwọn ìran tí A ti parun ṣíwájú wọn, tí (àwọn wọ̀nyí náà) sì ń rìn kọjá ní ibùgbé wọn! Dájúdájú àwọn àmì wà nínú ìyẹn fún àwọn olóye.');
INSERT INTO "aykyuni" VALUES(20,129,'20:129','Tí kò bá jẹ́ pé ọ̀rọ̀ kan tó ṣíwájú àti gbèdéke àkókò kan lọ́dọ̀ Olúwa rẹ (ìyà ẹ̀ṣẹ̀ wọn) ìbá ti di dandan.');
INSERT INTO "aykyuni" VALUES(20,130,'20:130','Nítorí náà, ṣe sùúrù lórí ohun tí wọ́n ń wí, kí o sì ṣe àfọ̀mọ́ pẹ̀lú ẹyìn fún Olúwa Rẹ ṣíwájú yíyọ òòrùn àti ṣíwájú wíwọ̀ rẹ̀. Ṣe àfọ̀mọ́ (fún Allāhu) ní àwọn àsìkò alẹ́ àti ní àwọn abala ọ̀sán nítorí kí (Á lè san ọ́ ní ẹ̀san rere tí) ó máa yọ́nú sí.');
INSERT INTO "aykyuni" VALUES(20,131,'20:131','Má ṣe fẹjú rẹ sí ohun tí A fi ṣe ìgbádùn tó jẹ́ òdòdó ìṣẹ̀mí ilé ayé fún àwọn ìran-ìran kan nínú wọn. (A fún wọn) nítorí kí Á lè fi ṣe àdánwò fún wọn ni. Arísìkí Olúwa rẹ lóore jùlọ. Ó sì máa wà títí láéláé.');
INSERT INTO "aykyuni" VALUES(20,132,'20:132','Pa ará ilé rẹ láṣẹ ìrun kíkí, kí o sì ṣe sùúrù lórí rẹ̀. A ò níí bèèrè èsè kan lọ́wọ́ rẹ; Àwa l’À ń pèsè fún ọ. Ìgbẹ̀yìn rere sì wà fún ìbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(20,133,'20:133','Wọ́n wí pé: “Nítorí kí ni kò fi mú àmì kan wá fún wa láti ọ̀dọ̀ Olúwa rẹ̀?” Ṣé ẹ̀rí tó yanjú tó wà nínú àwọn tákàdá àkọ́kọ́ kò dé bá wọn ni?');
INSERT INTO "aykyuni" VALUES(20,134,'20:134','Tí ó bá jẹ́ pé dájúdájú A ti fi ìyà kan pa wọ́n run ṣíwájú rẹ̀ ni, wọn ìbá wí pé: “Olúwa wa nítorí kí ni O ò ṣe rán Òjíṣẹ́ kan sí wa, àwa ìbá tẹ̀lé àwọn āyah Rẹ ṣíwájú kí á tó yẹpẹrẹ, (kí á sì tó) kàbùkù.”');
INSERT INTO "aykyuni" VALUES(20,135,'20:135','Sọ pé: “Ẹnì kọ̀ọ̀kan ni olùretí (ìkángun).” Nítorí náà, ẹ máa retí (rẹ̀). Ẹ máa mọ ta ni èrò ọ̀nà tààrà àti ẹni tó mọ̀nà.');
INSERT INTO "aykyuni" VALUES(21,1,'21:1','Ìṣírò-iṣẹ́ súnmọ́ fún àwọn ènìyàn, wọ́n sì wà nínú ìfọ́núfọ́ra, tí wọ́n ń gbúnrí (kúrò níbi òdodo).');
INSERT INTO "aykyuni" VALUES(21,2,'21:2','Kò sí ìrántí titun kan tí ó máa dé bá wọn láti ọ̀dọ̀ Olúwa wọn àfi kí wọ́n gbọ́ ọ lẹ́ni tí yóò máa (fi) ṣeré.');
INSERT INTO "aykyuni" VALUES(21,3,'21:3','Wọ́n fọ́núfọ́ra ni. Àwọn tó ṣàbòsí sì fi ọ̀rọ̀ ìkọ̀kọ̀ (ààrin ara wọn) pamọ́ pé: “Ṣé èyí tayọ abara bí irú yín ni? Ṣé ẹ máa tẹ̀lé idán ni, nígbà tí ẹ̀yin náà ríran?”');
INSERT INTO "aykyuni" VALUES(21,4,'21:4','Ó sọ pé: “Olúwa mi mọ ọ̀rọ̀ tó ń bẹ nínú sánmọ̀ àti ilẹ̀. Òun ni Olùgbọ́, Onímọ̀.”');
INSERT INTO "aykyuni" VALUES(21,5,'21:5','Rárá, wọ́n tún wí pé: “Àwọn àlá tí ó lọ́pọ̀ mọ́ra wọn tí kò ní ìtúmọ̀ ni (al-Ƙur’ān.” Wọ́n tún wí pé): “Rárá, ó hun ún ni.” (Wọ́n tún wí pé): “Rárá, eléwì ni. (Bí bẹ́ẹ̀ kọ́) kí ó mú àmì kan wá fún wa gẹ́gẹ́ bí wọ́n ṣe fi rán àwọn ẹni àkọ́kọ́.”');
INSERT INTO "aykyuni" VALUES(21,6,'21:6','Kò sí àwọn tó gbàgbọ́ ṣíwájú wọn nínú àwọn ìlú tí A ti parẹ́ (lẹ́yìn ìsọ̀kalẹ̀ àmì). Ṣé àwọn sì máa gbàgbọ́ (nígbà tí àmì bá dé)?');
INSERT INTO "aykyuni" VALUES(21,7,'21:7','A kò rán ẹnì kan ní iṣẹ́-òjíṣẹ́ ṣíwájú rẹ àfi àwọn ọkùnrin tí À ń fi ìmísí ránṣẹ́ sí. Nítorí náà, ẹ bèèrè lọ́dọ̀ àwọn oníràn-ántí tí ẹ̀yin kò bá mọ̀.1 ');
INSERT INTO "aykyuni" VALUES(21,8,'21:8','A kò sì ṣe wọ́n ní abara tí kì í jẹun. Wọn kò sì jẹ́ olùṣegbére (nílé ayé).1 ');
INSERT INTO "aykyuni" VALUES(21,9,'21:9','Lẹ́yìn náà, A mú àdéhùn Wa ṣẹ fún wọn. A sì gba àwọn Òjíṣẹ́ àti àwọn tí A bá fẹ́ là. A sì pa àwọn olùtayọ ẹnu-ààlà run.');
INSERT INTO "aykyuni" VALUES(21,10,'21:10','Dájúdájú A ti sọ tírà kan kalẹ̀ fún yín, tí ìrántí nípa ọ̀rọ̀ ara yín wà nínú rẹ̀. Nítorí náà, ṣé ẹ ò ṣe làákàyè ni?');
INSERT INTO "aykyuni" VALUES(21,11,'21:11','Mélòó mélòó nínú ìlú tí A ti parẹ́, tí ó jẹ́ alábòsí! A sì gbé àwọn ìjọ mìíràn dìde lẹ́yìn wọn.');
INSERT INTO "aykyuni" VALUES(21,12,'21:12','Nígbà tí wọ́n rí ìyà Wa (tó ń bọ̀), wọ́n sì ń sá lọ fún un.');
INSERT INTO "aykyuni" VALUES(21,13,'21:13','Ẹ má ṣe sá lọ. Ẹ padà síbi n̄ǹkan tí A fi ṣe gbẹdẹmukẹ fún yín àti àwọn ibùgbé yín nítorí kí A lè bi yín ní ìbéèrè.');
INSERT INTO "aykyuni" VALUES(21,14,'21:14','Wọ́n wí pé: “Ègbé wa ò! Dájúdájú àwa jẹ́ alábòsí.”');
INSERT INTO "aykyuni" VALUES(21,15,'21:15','Igbe wọn yìí kò dúró títí A fi sọ wọ́n di koríko tí wọ́n gé, wọ́n sì dòkú kalẹ̀.');
INSERT INTO "aykyuni" VALUES(21,16,'21:16','Àwa kò dá sánmọ̀ àti ilẹ̀ àti ohun tí ń bẹ láààrin àwọn méjèèjì pẹ̀lú eré ṣíṣe.');
INSERT INTO "aykyuni" VALUES(21,17,'21:17','Tí ó bá jẹ́ pé A gbèrò láti ṣèré ni, ọ̀dọ̀ Wa ni A kúkú tí máa ṣe é tí A bá jẹ́ olùṣe-(eré).');
INSERT INTO "aykyuni" VALUES(21,18,'21:18','Rárá o! À ń sọ òdodo lu irọ́ (mọ́lẹ̀ ni). Òdodo sì máa fọ́ agbárí irọ́. Irọ́ sì máa pòórá. Ègbé sì ni fún yín nípa ohun tí ẹ ń fi ròyìn (Rẹ̀ ní ti irọ́).');
INSERT INTO "aykyuni" VALUES(21,19,'21:19','TiRẹ̀ ni àwọn tó wà nínú àwọn sánmọ̀ àti ilẹ̀. Àti pé àwọn tó ń bẹ lọ́dọ̀ Rẹ̀, wọn kì í ṣègbéraga níbi ìjọ́sìn Rẹ̀. Kò sì rẹ̀ wọ́n.');
INSERT INTO "aykyuni" VALUES(21,20,'21:20','Wọ́n ń ṣàfọ̀mọ́ ní òru àti ní ọ̀sán; wọn kò sì sinmi.');
INSERT INTO "aykyuni" VALUES(21,21,'21:21','Tàbí wọ́n ní àwọn ọlọ́hun kan tí wọ́n mú jáde láti ara ilẹ̀, tí wọ́n ń jí òkú dìde ni?');
INSERT INTO "aykyuni" VALUES(21,22,'21:22','Tí ó bá jẹ́ pé àwọn ọlọ́hun kan wà nínú sánmọ̀ àti ilẹ̀ lẹ́yìn Allāhu, sánmọ̀ àti ilẹ̀ ìbá ti bàjẹ́. Mímọ́ ni fún Allāhu, Olúwa Ìtẹ́-ọlá, tayọ ohun tí wọ́n ń fi ròyìn (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(21,23,'21:23','Wọn kò níí bi (Allāhu) ní ìbéèrè nípa n̄ǹkan tí Ó ń ṣe. Àwọn ni wọ́n máa bi ní ìbéèrè (nípa iṣẹ́ ọwọ́ wọn).');
INSERT INTO "aykyuni" VALUES(21,24,'21:24','Tàbí wọ́n sọ àwọn kan di ọlọ́hun lẹ́yìn Allāhu ni? Sọ pé: “Ẹ mú ẹ̀rí ọ̀rọ̀ yín wa.” (Al-Ƙur’ān) yìí ni ọ̀rọ̀ nípa àwọn tó wà pẹ̀lú mi àti ọ̀rọ̀ nípa àwọn tó ṣíwájú mi.Ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn kò mọ òdodo; wọ́n sì ń gbúnrí kúrò níbẹ̀. ');
INSERT INTO "aykyuni" VALUES(21,25,'21:25','A kò rán òjíṣẹ́ kan níṣẹ́ ṣíwájú rẹ àfi kí A fi ìmísí ránṣẹ́ sí i pé: “Dájúdájú kò sí ọlọ́hun kan tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Èmi (Allāhu). Nítorí náà, ẹ jọ́sìn fún Mi.”1 ');
INSERT INTO "aykyuni" VALUES(21,26,'21:26','Wọ́n wí pé: “Àjọkẹ́-ayé sọ ẹnì kan di ọmọ (nínú àwọn mọlāikah Rẹ̀).” Mímọ́ ni fún Un - Kò rí bẹ́ẹ̀; ẹrúsìn alápọ̀n-ọ́nlé ni wọ́n.');
INSERT INTO "aykyuni" VALUES(21,27,'21:27','Wọn kì í ṣíwájú Allāhu sọ ọ̀rọ̀. Wọ́n sì ń ṣiṣẹ́ pẹ̀lú àṣẹ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(21,28,'21:28','(Allāhu) mọ ohun tó ń bẹ níwájú wọn àti ohun tó ń bẹ lẹ́yìn wọn. Wọn kò sì níí ṣìpẹ̀ (ẹnì kan) àfi ẹni tí (Allāhu) bá yọ́nú sí. Wọ́n tún ń páyà fún ìbẹ̀rù Rẹ̀.');
INSERT INTO "aykyuni" VALUES(21,29,'21:29','Àti pé ẹnikẹ́ni nínú wọn tí ó bá wí pé: “Dájúdájú èmi ni ọlọ́hun lẹ́yìn Rẹ̀.” Nítorí ìyẹn sì ni A óò fi san án ní ẹ̀san iná Jahanamọ. Báyẹn sì ni A ṣe ń san àwọn alábòsí ní ẹ̀san.');
INSERT INTO "aykyuni" VALUES(21,30,'21:30','Ṣé àwọn tó ṣàì gbàgbọ́ kò wòye pé dájúdájú àwọn sánmọ̀ lẹ̀pọ̀ àti ilẹ̀ náà lẹ̀pọ̀ tẹ́lẹ̀ ni, A sì yà wọ́n sí ọ̀tọ̀ọ̀tọ̀, A sì ṣe gbogbo n̄ǹkan ní abẹ̀mí láti inú omi? Nítorí náà, ṣé wọn kò níí gbàgbọ́ ni?1 ');
INSERT INTO "aykyuni" VALUES(21,31,'21:31','A sì fi àwọn àpáta tó dúró gbagidi sínú ilẹ̀ kí ó má fi lè mì mọ́ wọn lẹ́sẹ̀. A sì fi àwọn ọ̀nà fífẹ̀ sáààrin àwọn àpáta nítorí kí wọ́n lè rí ọ̀nà tọ̀.');
INSERT INTO "aykyuni" VALUES(21,32,'21:32','A tún ṣe sánmọ̀ ní àjà tí wọ́n ń ṣọ́ (níbi jíjábọ́). Síbẹ̀síbẹ̀ wọ́n ń gbúnrí kúrò níbi àwọn àmì rẹ̀.');
INSERT INTO "aykyuni" VALUES(21,33,'21:33','Òun ni Ẹni tí Ó dá òru àti ọ̀sán pẹ̀lú òòrùn àti òṣùpá. Ìkọ̀ọ̀kan (òòrùn àti òṣùpá) wà ní òpópónà roboto tó ń tọ̀.');
INSERT INTO "aykyuni" VALUES(21,34,'21:34','A kò ṣe bíbẹ gbére fún abara kan ṣíwájú rẹ. Ǹjẹ́ tí o bá kú, àwọn sì ni olùṣegbére bí?1 ');
INSERT INTO "aykyuni" VALUES(21,35,'21:35','Ẹ̀mí kọ̀ọ̀kan ló máa tọ́ ikú wò. A sì máa fi aburú àti rere ṣàdánwò fún yín. Ọ̀dọ̀ Wa sì ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(21,36,'21:36','Nígbà tí àwọn tó ṣàì gbàgbọ́ bá rí ọ, wọn kò sì níí kà ọ́ kún kiní kan bí kò ṣe oníyẹ̀yẹ́ pé: “Ṣé èyí ni ẹni tó ń sọ̀rọ̀ nípa àwọn ọlọ́hun yín?” Aláìgbàgbọ́ sì ni àwọn náà nípa ìrántí Àjọkẹ́-ayé.');
INSERT INTO "aykyuni" VALUES(21,37,'21:37','Wọ́n fi ìkánjú sínú àdámọ́ ènìyàn. Mo máa fi àwọn àmì Mi hàn yín. Nítorí náà, ẹ má ṣe kán Mi lójú.');
INSERT INTO "aykyuni" VALUES(21,38,'21:38','Wọ́n ń wí pé: “Ìgbà wo ni àdéhùn yìí yóò ṣẹ tí ẹ bá jẹ́ olódodo?”');
INSERT INTO "aykyuni" VALUES(21,39,'21:39','Tí ó bá jẹ́ pé àwọn tó ṣàì gbàgbọ́ mọ ìgbà tí wọn kò níí dá Iná dúró níwájú wọn àti ní ẹ̀yìn wọn, tí A ò sì níí ràn wọ́n lọ́wọ́, (wọn ìbá tí wá ìyà pẹ̀lú ìkánjú).');
INSERT INTO "aykyuni" VALUES(21,40,'21:40','Ṣùgbọ́n (ìyà Iná) yóò bá wọn ní òjijì, nígbà náà ó máa kó ìdààmú bá wọn; wọn kò sì níí lè dá a padà. A kò sì níí sún (ìyà náà) síwájú fún wọn.');
INSERT INTO "aykyuni" VALUES(21,41,'21:41','Wọ́n kúkú ti fi àwọn Òjíṣẹ́ kan ṣe yẹ̀yẹ́ ṣíwájú rẹ! Nítorí náà, ohun tí wọ́n ń fi ṣe yẹ̀yẹ́ sì dìyà tó yí àwọn tó ń fi wọ́n ṣe yẹ̀yẹ́ po.');
INSERT INTO "aykyuni" VALUES(21,42,'21:42','Sọ pé: “Ta ni ẹni tí ó ń ṣọ yín ní òru àti ní ọ̀sán níbi ìyà Àjọkẹ́-ayé?” Àmọ́ ńṣe ni wọ́n ń gbúnrí kúrò níbi ìrántí Àjọkẹ́-ayé.');
INSERT INTO "aykyuni" VALUES(21,43,'21:43','Tàbí wọ́n ní àwọn ọlọ́hun kan tó máa gbà wọ́n sílẹ̀ lẹ́yìn Wa ni? Wọn kò lè ran ara wọn lọ́wọ́. Wọn kò sì lè gbà wọ́n sílẹ̀ lọ́dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(21,44,'21:44','Síbẹ̀síbẹ̀ A fún àwọn wọ̀nyí àti àwọn bàbá wọn ní ìgbádùn títí ìṣẹ̀mí ayé wọn fi gùn. Ṣé wọn kò rí i pé dájúdájú Àwa l’À ń mú ilẹ̀ dínkù (mọ́ àwọn aláìgbàgbọ́ lọ́wọ́) láti àwọn etí ìlú (wọnú ìlú nípa fífún àwọn mùsùlùmí ní ìṣẹ́gun lórí wọn). Nítorí náà, ṣé àwọn ni wọ́n máa borí ni?');
INSERT INTO "aykyuni" VALUES(21,45,'21:45','Sọ pé: “Ìmísí ni mo fi ń kìlọ̀ fún yín. Àwọn adití kò sì níí gbọ́ ìpè nígbà tí wọ́n bá ń kìlọ̀ fún wọn.”');
INSERT INTO "aykyuni" VALUES(21,46,'21:46','Àti pé dájúdájú tí abala kan nínú ìyà Olúwa rẹ bá fọwọ́ bà wọ́n, dájúdájú wọ́n á wí pé: “Ègbé wa ò; dájúdájú àwa jẹ́ alábòsí.”');
INSERT INTO "aykyuni" VALUES(21,47,'21:47','A máa gbé àwọn òṣùwọ̀n déédé kalẹ̀ ní Ọjọ́ Àjíǹde. Nítorí náà, wọn kò níí ṣe àbòsí kiní kan fún ẹ̀mí kan. Kí (iṣẹ́) jẹ́ ìwọ̀n èso kardal (bín-íntín), A máa mú un wá. A sì tó ní Olùṣírò.');
INSERT INTO "aykyuni" VALUES(21,48,'21:48','Dájúdájú A fún (Ànábì) Mūsā àti (Ànábì) Hārūn ní ọ̀rọ̀-ìpínyà (ohun tó ń ṣòpínyà láààrin òdodo àti irọ́), ìmọ́lẹ̀ àti ìrántí fún àwọn olùbẹ̀rù(Allāhu).1 ');
INSERT INTO "aykyuni" VALUES(21,49,'21:49','(Àwọn ni) àwọn tó ń páyà Olúwa wọn ní ìkọ̀kọ̀. Olùpáyà sì ni wọ́n nípa Àkókò náà.');
INSERT INTO "aykyuni" VALUES(21,50,'21:50','Èyí sì ni ìrántí oníbùkún tí A sọ̀kalẹ̀. Ṣé ẹ̀yin yóò takò ó ni?');
INSERT INTO "aykyuni" VALUES(21,51,'21:51','Dájúdájú A ti fún (Ànábì) ’Ibrọ̄hīm ní ìmọ̀nà tirẹ̀ ṣíwájú (kí ó tó dàgbà). Àwa sì ń jẹ́ Onímọ̀ nípa rẹ̀.');
INSERT INTO "aykyuni" VALUES(21,52,'21:52','(Rántí) nígbà tí ó sọ fún bàbá rẹ̀ àti ìjọ rẹ̀ pé: “Kí ni àwọn ère wọ̀nyí tí ẹ̀ ń dúró tì, tí ẹ̀ ń jọ́sìn fún ṣe jẹ́ ná?”');
INSERT INTO "aykyuni" VALUES(21,53,'21:53','Wọ́n wí pé: “A bá àwọn bàbá wa tí wọ́n ń jọ́sìn fún wọn ni.”');
INSERT INTO "aykyuni" VALUES(21,54,'21:54','Ó sọ pé: “Dájúdájú ẹ̀yin àti àwọn bàbá yín ti wà nínú ìṣìnà pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(21,55,'21:55','Wọ́n wí pé: “Ṣé o mú òdodo wá fún wa ni tàbí ìwọ wà nínú àwọn ẹlẹ́fẹ̀.”');
INSERT INTO "aykyuni" VALUES(21,56,'21:56','Ó sọ pé: “Rárá. Olúwa yin ni Olúwa àwọn sánmọ̀ àti ilẹ̀; Ẹni tí Ó pílẹ̀ ìṣẹ̀dá wọn. Èmi sì wà nínú àwọn ẹlẹ́rìí lórí ìyẹn.');
INSERT INTO "aykyuni" VALUES(21,57,'21:57','Àti pé mo fi Allāhu búra, dájúdájú mo máa dète sí àwọn òrìṣà yín lẹ́yìn tí ẹ bá pẹ̀yìn dà, tí ẹ lọ.”');
INSERT INTO "aykyuni" VALUES(21,58,'21:58','Ó sì fọ́ wọn sí wẹ́wẹ́ àfi àgbà (òrìṣà) wọn nítorí kí wọ́n lè padà dé bá a.');
INSERT INTO "aykyuni" VALUES(21,59,'21:59','Wọ́n wí pé: “Ta ni ó ṣe èyí pẹ̀lú àwọn ọlọ́hun wa? Dájúdájú onítọ̀un wà nínú àwọn alábòsí.”');
INSERT INTO "aykyuni" VALUES(21,60,'21:60','Wọ́n wí pé: “A gbọ́ tí ọ̀dọ́kùnrin kan ń bu ẹnu àtẹ́ lù wọ́n. Wọ́n ń pe (ọmọ náà) ní ’Ibrọ̄hīm.”');
INSERT INTO "aykyuni" VALUES(21,61,'21:61','Wọ́n wí pé: “Ẹ mú un wá han àwọn ènìyàn kí wọ́n lè jẹ́rìí lé e lórí.”');
INSERT INTO "aykyuni" VALUES(21,62,'21:62','Wọ́n wí pé: “Ṣé ìwọ lo ṣe èyí pẹ̀lú àwọn ọlọ́hun wa, ’Ibrọ̄hīm?”');
INSERT INTO "aykyuni" VALUES(21,63,'21:63','Ó sọ pé: “Rárá o, àgbà wọn yìí ló ṣe (wọ́n bẹ́ẹ̀). Nítorí náà, ẹ bi wọ́n léèrè wò tí wọ́n bá máa ń sọ̀rọ̀.”1 ');
INSERT INTO "aykyuni" VALUES(21,64,'21:64','Nígbà náà, wọ́n yíjú síra wọn, wọ́n sì wí pé: “Dájúdájú ẹ̀yin ni alábòsí.”');
INSERT INTO "aykyuni" VALUES(21,65,'21:65','Lẹ́yìn náà, wọ́n sorí kọ́ (wọ́n sì wí pé): “Ìwọ náà kúkú mọ̀ pé àwọn wọ̀nyí kì í sọ̀rọ̀.”');
INSERT INTO "aykyuni" VALUES(21,66,'21:66','Ó sọ pé: “Ṣé ẹ̀yin yóò máa jọ́sìn fún n̄ǹkan tí kò lè ṣe yín ní àǹfààní, tí kò sì lè kó ìnira ba yín lẹ́yìn Allāhu?');
INSERT INTO "aykyuni" VALUES(21,67,'21:67','Ṣíọ̀ ẹ̀yin àti n̄ǹkan tí ẹ̀ ń jọ́sìn fún lẹ́yìn Allāhu, ṣé ẹ̀yin kò níí ṣe làákàyè ni?"');
INSERT INTO "aykyuni" VALUES(21,68,'21:68','Wọ́n wí pé: “Ẹ dáná sun ún; kí ẹ sì ran àwọn ọlọ́hun yín lọ́wọ́, tí ẹ̀yin bá níkan-án ṣe.”');
INSERT INTO "aykyuni" VALUES(21,69,'21:69','A sọ pé: “Iná, di tútù àti àlàáfíà lára ’Ibrọ̄hīm.”');
INSERT INTO "aykyuni" VALUES(21,70,'21:70','Wọ́n gbèrò ète sí i, ṣùgbọ́n A ṣe wọ́n ní olófò jùlọ.');
INSERT INTO "aykyuni" VALUES(21,71,'21:71','A sì gba òun àti Lūt là sórí ilẹ̀ tí A fi ìbùkún sí fún gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(21,72,'21:72','A ta á ní ọrẹ ọmọ, ’Is-hāƙ àti Ya‘ƙūb tí ó jẹ́ àlékún (ìyẹn, ọmọọmọ). Olúkùlùkù (wọn) ni A ṣe ní ẹni rere.');
INSERT INTO "aykyuni" VALUES(21,73,'21:73','A ṣe wọ́n ní aṣíwájú tó ń tọ́ àwọn ènìyàn sí ọ̀nà pẹ̀lú àṣẹ Wa. A sì fi ìmísí ránṣẹ́ sí wọn nípa ṣíṣe iṣẹ́ rere, ìrun kíkí àti Zakāh yíyọ. Wọ́n sì jẹ́ olùjọ́sìn fún Wa.');
INSERT INTO "aykyuni" VALUES(21,74,'21:74','(Ẹ rántí Ànábì) Lūt. A fún un ní ipò Ànábì àti ìmọ̀. A sì gbà á là nínú ìlú tí wọ́n ti ń ṣe àwọn iṣẹ́ burúkú. Dájúdájú wọ́n jẹ́ ìjọ burúkú, òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(21,75,'21:75','A sì mú un wọ inú ìkẹ́ Wa. Dájúdájú ó wà nínú àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(21,76,'21:76','(Ẹ rántí Ànábì) Nūh, nígbà tí ó pe ìpè ṣíwájú. A sì jẹ́pè rẹ̀. A sì gba òun àti àwọn ènìyàn rẹ̀ là nínú ìbànújẹ́ ńlá.');
INSERT INTO "aykyuni" VALUES(21,77,'21:77','A sì ṣàrànṣe fún un lórí àwọn ènìyàn tó pe àwọn āyah Wa nírọ́. Dájúdájú wọ́n jẹ́ ìjọ burúkú. A sì tẹ̀ wọ́n rì pátápátá.');
INSERT INTO "aykyuni" VALUES(21,78,'21:78','(Ẹ rántí Ànábì) Dāwūd àti (Ànábì) Sulaemọ̄n, (rántí) nígbà tí àwọn méjèèjì ń ṣe ìdájọ́ lórí (ọ̀rọ̀) oko, nígbà tí àgùtàn ìjọ kan jẹko wọ inú oko náà. Àwa sì jẹ́ Olùjẹ́rìí sí ìdájọ́ wọn.');
INSERT INTO "aykyuni" VALUES(21,79,'21:79','A sì fún (Ànábì) Sulaemọ̄n ní àgbọ́yé rẹ̀. Àti pé ìkọ̀ọ̀kan (wọn) ni A fún ní ipò Ànábì àti ìmọ̀. A sì tẹ àwọn àpáta àti ẹyẹ ba; wọ́n ń ṣe àfọ̀mọ́ (fún Allāhu) pẹ̀lú (Ànábì) Dāwūd. Àwa l’A ṣe wọ́n bẹ́ẹ̀.');
INSERT INTO "aykyuni" VALUES(21,80,'21:80','A tún fún un ní ìmọ̀ nípa ṣíṣe ẹ̀wù irin fún yín nítorí kí ó lè jẹ́ odi ìṣọ́ fún yín níbi ogun yín. Nítorí náà, ǹjẹ́ olùdúpẹ́ (fún Allāhu) ni ẹ̀yin bí?');
INSERT INTO "aykyuni" VALUES(21,81,'21:81','A sì tẹ atẹ́gùn líle lórí ba fún (Ànábì) Sulaemọ̄n. Ó ń gbé e lọ pẹ̀lú àṣẹ rẹ̀ sórí ilẹ̀ tí A fi ìbùkún sí. A sì ń jẹ́ Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(21,82,'21:82','Nínú àwọn èṣù, àwọn tó ń wa kùsà òkun tún wà fún un. Wọ́n tún ń ṣe iṣẹ́ mìíràn yàtọ̀ sí ìyẹn. Àwa sì ń jẹ́ Olùṣọ́ fún wọn.');
INSERT INTO "aykyuni" VALUES(21,83,'21:83','(Ẹ rántí Ànábì) ’Ayyūb, nígbà tí ó pe Olúwa rẹ̀ pé: “Dájúdájú ọwọ́ ìnira ti kàn mí. Ìwọ sì ni Aláàánú jùlọ nínú àwọn aláàánú.”');
INSERT INTO "aykyuni" VALUES(21,84,'21:84','A sì jẹ́pè rẹ̀. A mú gbogbo ìnira rẹ̀ kúrò fún un. A tún fún un ní àwọn ẹbí rẹ̀ (padà) àti irú wọn pẹ̀lú wọn; (ó jẹ́) ìkẹ́ láti ọ̀dọ̀ Wa àti ìrántí fún àwọn olùjọ́sìn (fún Wa).');
INSERT INTO "aykyuni" VALUES(21,85,'21:85','(Ẹ rántí Ànábì) ’Ismọ̄‘īl àti (Ànábì) ’Idrīs àti (Ànábì) Thul-Kifl; ẹnì kọ̀ọ̀kan (wọn) wà nínú àwọn onísùúrù.');
INSERT INTO "aykyuni" VALUES(21,86,'21:86','A sì fi wọ́n sínú ìkẹ́ Wa. Dájúdájú wọ́n wà nínú àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(21,87,'21:87','(Ẹ rántí) ẹlẹ́ja, nígbà tí ó bá ìbínú lọ, ó sì lérò pé A ò níí gbá òun mú. Ó sì pe (Olúwa rẹ̀) nínú àwọn òkùnkùn (inú ẹja) pé: "Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Ìwọ (Allāhu). Mímọ́ ni fún Ọ. Dájúdájú èmi wà nínú àwọn alábòsí.”1 ');
INSERT INTO "aykyuni" VALUES(21,88,'21:88','A jẹ́pè rẹ̀. A sì gbà á là nínú ìbànújẹ́. Báyẹn ni A ṣe ń gba àwọn onígbàgbọ́ òdodo là.');
INSERT INTO "aykyuni" VALUES(21,89,'21:89','(Ẹ rántí Ànábì) Zakariyyā nígbà tí ó pe Olúwa rẹ̀ pé: “Olúwa mi, má fi mí sílẹ̀ ní èmi nìkan (tí kò níí bímọ). Ìwọ sì lóore jùlọ nínú àwọn olùjogún.”1 ');
INSERT INTO "aykyuni" VALUES(21,90,'21:90','A jẹ́pè rẹ̀. A sì ta á lọ́rẹ ọmọ, (Ànábì) Yahyā. A sì ṣe àtúnṣe ìyàwó rẹ̀ fún un. Dájúdájú wọ́n ń yára gágá níbi àwọn iṣẹ́ rere. Wọ́n ń pè Wá pẹ̀lú ìrètí àti ìpáyà. Wọ́n sì jẹ́ olùtẹríba fún Wa.');
INSERT INTO "aykyuni" VALUES(21,91,'21:91','(Ẹ rántí obìnrin) èyí tí ó ṣọ́ abẹ́ rẹ̀. A sì fẹ́ atẹ́gùn nínú àwọn atẹ́gùn ẹ̀mí tí A dá sí i lára. A sì ṣe òun àti ọmọkùnrin rẹ̀ ní àmì fún gbogbo ẹ̀dá.1 ');
INSERT INTO "aykyuni" VALUES(21,92,'21:92','Dájúdájú (’Islām) yìí ni ẹ̀sìn yín. (Ó jẹ́) ẹ̀sìn kan ṣoṣo. Èmi sì ni Olúwa yín. Nítorí náà, ẹ jọ́sìn fún Mi.');
INSERT INTO "aykyuni" VALUES(21,93,'21:93','(Àwọn yẹhudi àti nasọ̄rọ̄), wọ́n sì dá ọ̀rọ̀ (ẹ̀sìn) wọn sí kélekèle láààrin ara wọn; ẹnì kọ̀ọ̀kan (wọn) sì máa padà sí ọ̀dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(21,94,'21:94','Nítorí náà, ẹnikẹ́ni tí ó bá ṣe nínú àwọn iṣẹ́ rere, tí ó sì jẹ́ onígbàgbọ́ òdodo, kò sí kíkọ̀ fún iṣẹ́ rẹ̀. Àti pé dájúdájú Àwa máa ṣe àkọsílẹ̀ rẹ̀ fún un.');
INSERT INTO "aykyuni" VALUES(21,95,'21:95','Èèwọ̀ ni fún ará ìlú kan tí A ti parẹ́ (pé kí ó tún padà sí ayé); dájúdájú wọn kò níí padà mọ́ (sí ilé ayé).');
INSERT INTO "aykyuni" VALUES(21,96,'21:96','(Wọn yóò wà nínú barsaku) títí A óò fi ṣí àwọn Ya’jūj àti Ma’jūj sílẹ̀. Àwọn (wọ̀nyí) yó sì máa sáré jáde láti inú gbogbo àyè gíga.');
INSERT INTO "aykyuni" VALUES(21,97,'21:97','Àdéhùn òdodo náà súnmọ́ pẹ́kípẹ́kí. Nígbà náà, àwọn tó ṣàì gbàgbọ́ yóò máa ranjú kalẹ̀ ràngàndàn, (wọ́n yó sì wí pé): “Ègbé ni fún wa! Dájúdájú àwa ti wà nínú ìgbàgbéra nípa èyí. Rárá, àwa jẹ́ alábòsí ni.”');
INSERT INTO "aykyuni" VALUES(21,98,'21:98','Dájúdájú ẹ̀yin àti n̄ǹkan tí ẹ̀ ń jọ́sìn fún lẹ́yìn Allāhu ni ìkoná Jahanamọ; ẹ̀yin yó sì wọ inú rẹ̀.');
INSERT INTO "aykyuni" VALUES(21,99,'21:99','Tí ó bá jẹ́ pé àwọn wọ̀nyí ni ọlọ́hun ni, wọn ìbá tí wọ inú Iná. Olùṣegbére sì ni ẹnì kọ̀ọ̀kan wọn nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(21,100,'21:100','Iná yóò máa kùn yùn-ùn sí wọn létí; wọn kò sì níí gbọ́ (ọ̀rọ̀ mìíràn).');
INSERT INTO "aykyuni" VALUES(21,101,'21:101','Dájúdájú àwọn tí rere ti ṣíwájú fún láti ọ̀dọ̀ Wa, àwọn wọ̀nyẹn ni A óò gbé jìnnà sí Iná.');
INSERT INTO "aykyuni" VALUES(21,102,'21:102','Wọn kò sì níí gbọ́ kíkùn rẹ̀. Olùṣegbére sì ni wọ́n nínú ìgbádùn tí ẹ̀mí wọn ń fẹ́.');
INSERT INTO "aykyuni" VALUES(21,103,'21:103','Ìpáyà tó tóbi jùlọ kò níí bà wọ́n nínú jẹ́. Àwọn mọlāika yóò máa pàdé wọn, (wọn yóò sọ pé): “Èyí ni ọjọ́ yín tí À ń ṣe ní àdéhùn fún yín.”');
INSERT INTO "aykyuni" VALUES(21,104,'21:104','(Ọjọ́ náà ni) ọjọ́ tí A máa ká sánmọ̀ bíi kíká ewé tírà.Gẹ́gẹ́ bí A ti ṣe bẹ̀rẹ̀ ìṣẹ̀dá ẹ̀dá ní ìbẹ̀rẹ̀ pẹ̀pẹ̀ (ibẹ̀ náà ni) A óò dá a padà sí. (Ó jẹ́) àdéhùn tí A ṣe. Dájúdájú Àwa máa ṣe bẹ́ẹ̀. ');
INSERT INTO "aykyuni" VALUES(21,105,'21:105','Àti pé A ti kọ ọ́ sínú àwọn ìpín-ìpín Tírà (tí A sọ̀kalẹ̀) lẹ́yìn (èyí tí ó wà nínú) Tírà Ìpìlẹ̀ (ìyẹn, Laohul- Mahfūṭḥ) pé dájúdájú ilẹ̀ (ìyẹn ilẹ̀ Ọgbà Ìdẹ̀ra),àwọn ẹrúsìn Mi, àwọn ẹni rere, ni wọn yóò jogún rẹ̀. ');
INSERT INTO "aykyuni" VALUES(21,106,'21:106','Dájúdájú ohun tí ó máa tó ẹ̀dá (nípa ọ̀rọ̀ ayé àti ọ̀rọ̀ ọ̀run)wà nínú (al-Ƙur’ān) yìí fún ìjọ tó ń jọ́sìn (fún Allāhu ní ọ̀nà òdodo). ');
INSERT INTO "aykyuni" VALUES(21,107,'21:107','A kò sì fi iṣẹ́ ìmísí rán ọ bí kò ṣe pé kí o lè jẹ́ ìkẹ́ fún gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(21,108,'21:108','Sọ pé: “Ohun tí Wọ́n ń fi ránṣẹ́ sí mi ní ìmísí ni pé, Ọlọ́hun yín tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo, Ọlọ́hun Ọ̀kan ṣoṣo ni. Nítorí náà, ṣé ẹ̀yin máa di mùsùlùmí?”');
INSERT INTO "aykyuni" VALUES(21,109,'21:109','Tí wọ́n bá gbúnrí, sọ nígbà náà pé: “Èmi ń fi to yín létí pé èmi àti ẹ̀yin dìjọ dá dúró fún ogun ẹ̀sìn (tí ó máa ṣẹlẹ̀ láààrin wa báyìí). Èmi kò sì mọ̀ bóyá ohun tí Wọ́n ń ṣe ní àdéhùn fún yín ti súnmọ́ tàbí ó sì jìnnà.');
INSERT INTO "aykyuni" VALUES(21,110,'21:110','Dájúdájú (Allāhu) mọ ohun tí ó hàn nínú ọ̀rọ̀. Ó sì mọ ohun tí ẹ̀ ń fi pamọ́.');
INSERT INTO "aykyuni" VALUES(21,111,'21:111','Èmi kò sì mọ̀ bóyá (lílọ́ yín lára) máa jẹ́ àdánwò àti ìgbádùn fún yín títí di ìgbà díẹ̀.”');
INSERT INTO "aykyuni" VALUES(21,112,'21:112','Ó sọ pé: “Olúwa mi, ṣèdájọ́ pẹ̀lú òdodo. Olúwa wa ni Àjọkẹ́-ayé, Ẹni tí à ń wá àánú Rẹ̀ lórí ohun tí ẹ̀ ń fi ròyìn (Rẹ̀).”1 ');
INSERT INTO "aykyuni" VALUES(22,1,'22:1','Ẹ̀yin ènìyàn, ẹ bẹ̀rù Olúwa yín. Dájúdájú ìmì tìtì Àkókò náà, n̄ǹkan ńlá ni.');
INSERT INTO "aykyuni" VALUES(22,2,'22:2','(Àkókò náà ni) ọjọ́ tí ẹ máa rí i tí gbogbo obìnrin tó ń fún ọmọ lọ́mú mu yóò gbàgbé ọmọ tí ó ń fún lọ́mú àti pé gbogbo aboyún yó sì máa bí oyún wọn. Ìwọ yó sì rí àwọn ènìyàn tí ọtí yóò máa pa wọ́n. Ọtí kò sì pa wọ́n, ṣùgbọ́n ìyà Allāhu (tó) le (ló fà á).');
INSERT INTO "aykyuni" VALUES(22,3,'22:3','Ó sì ń bẹ nínú àwọn ènìyàn ẹni tó ń jiyàn nípa (ẹ̀sìn) Allāhu láì ní ìmọ̀. Ó sì ń tẹ̀lé gbogbo èṣù olórí kunkun.');
INSERT INTO "aykyuni" VALUES(22,4,'22:4','Wọ́n kọ ọ́ lé (Èṣù) lórí pé dájúdájú ẹnikẹ́ni tí ó bá tẹ̀lé e, ó máa ṣì í lọ́nà. Ó sì máa tọ́ ọ sí ọ̀nà ìyà Iná tó ń jo.');
INSERT INTO "aykyuni" VALUES(22,5,'22:5','Ẹ̀yin ènìyàn, tí ẹ bá wà nínú iyèméjì nípa àjíǹde, dájúdájú Àwa ṣẹ̀dá yín láti inú erùpẹ̀, lẹ́yìn náà láti inú àtọ̀, lẹ́yìn náà láti inú ẹ̀jẹ̀ dídì, lẹ́yìn náà láti inú bááṣí ẹran tí ó pé ní ẹ̀dá àti èyí tí kò pé ní ẹ̀dá nítorí kí A lè ṣàlàyé (agbára Wa) fún yín. A sì ń mú ohun tí A bá fẹ́ dúró sínú àpò ìbímọ títí di gbèdéke àkókò kan. Lẹ́yìn náà, A óò mu yín jáde ní òpóǹló. Lẹ́yìn náà, (ẹ óò máa ṣẹ̀mí lọ) nítorí kí ẹ lè sánn̄gun dópin agbára yín. Ẹni tí wọ́n máa pa (ní kékeré) wà nínú yín. Ó sì wà nínú yín ẹni tí wọ́n máa dá (ìṣẹ̀mí) rẹ̀ sí di àsìkò ogbó kùjọ́kùjọ́ nítorí kí ó má lè mọ́ n̄ǹkan kan mọ́ lẹ́yìn tí ó ti mọ̀ ọ́n. Àti pé o máa rí ilẹ̀ ní gbígbẹ. Nígbà tí A bá sì sọ òjò kalẹ̀ lé e lórí, ó máa yíra padà. Ó máa gbèrú. Ó sì máa mú gbogbo oríṣiríṣi irúgbìn tó dára jáde.');
INSERT INTO "aykyuni" VALUES(22,6,'22:6','Ìyẹn (rí bẹ́ẹ̀) nítorí pé dájúdájú Allāhu, Òun ni Òdodo. Dájúdájú Òun l’Ó ń sọ àwọn òkú di alààyè. Dájúdájú Òun sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(22,7,'22:7','Àti pé dájúdájú Àkókò náà ń bọ̀. Kò sí iyèméjì nínú rẹ̀. Dájúdájú Allāhu yóò gbé àwọn tó ń bẹ nínú sàréè dìde.');
INSERT INTO "aykyuni" VALUES(22,8,'22:8','Ó ń bẹ nínú àwọn ènìyàn ẹni tó ń jiyàn nípa (ẹ̀sìn) Allāhu láì ní ìmọ̀ (al-Ƙur’ān) àti ìmọ̀nà (sunnah Ànábì) àti tírà (onímọ̀-ẹ̀sìn) tó ń tan ìmọ́lẹ̀ (sí ọ̀rọ̀ ẹ̀sìn).');
INSERT INTO "aykyuni" VALUES(22,9,'22:9','Ó ń ṣègbéraga (sí òdodo, ó ń gbúnrí kúrò níbi òdodo) nítorí kí ó lè kó ìṣìnà bá àwọn ènìyàn lójú ọ̀nà (ẹ̀sìn) Allāhu. Àbùkù ń bẹ fún un nílé ayé. Ní Ọjọ́ Àjíǹde, A sì máa fún un ní ìyà Iná jónijóni tọ́ wò.');
INSERT INTO "aykyuni" VALUES(22,10,'22:10','Ìyẹn (rí bẹ́ẹ̀) nítorí ohun tí ọwọ́ rẹ̀ tì síwájú. Dájúdájú Allāhu kò níí ṣàbòsí sí àwọn ẹrúsìn Rẹ̀.');
INSERT INTO "aykyuni" VALUES(22,11,'22:11','Ó tún wà nínú àwọn ènìyàn ẹni tó ń jọ́sìn fún Allāhu lórí ahọ́n. Tí rere bá ṣẹlẹ̀ sí i, ó máa fi ọkàn balẹ̀ (sínú ẹ̀sìn). Tí ìfòòró bá sì ṣẹlẹ̀ sí i, ó máa yíjú rẹ̀ padà kúrò nínú ẹ̀sìn. Ó ṣòfò láyé àti lọ́run. Ìyẹn sì ni òfò pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(22,12,'22:12','Ó ń pè lẹ́yìn Allāhu ohun tí kò lè kó ìnira bá a, tí kò sì lè ṣe é ní oore; ìyẹn ni ìṣìnà tó jìnnà (sí ìmọ̀nà).');
INSERT INTO "aykyuni" VALUES(22,13,'22:13','Ó ń pe ohun tí ìnira rẹ̀ súnmọ́ ju àǹfààní rẹ̀ lọ. Dájúdájú (òrìṣà) burú ní aláfẹ̀yìntì, ó sì burú ní alásùn-únmọ́.');
INSERT INTO "aykyuni" VALUES(22,14,'22:14','Dájúdájú Allāhu yóò mú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere wọ inú Ọgbà Ìdẹ̀ra tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Dájúdájú Allāhu ń ṣe ohun tí Ó bá fẹ́.');
INSERT INTO "aykyuni" VALUES(22,15,'22:15','Ẹnikẹ́ni tí ó bá lérò pé Allāhu kò níí ran (Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -) lọ́wọ́ láyé àti lọ́run, kí ó na okùn sí sánmọ̀ (àjà ilé rẹ̀) lẹ́yìn náà kí ó gé e (ìyẹn ni pé, kí ó pokùn so). Kí ó wò ó nígbà náà bóyá ète rẹ̀ lè mú (àrànṣe) tó ń bínú sí kúrò (lọ́dọ̀ Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -).1 ');
INSERT INTO "aykyuni" VALUES(22,16,'22:16','Báyẹn ni A ṣe sọ̀ ọ́ kalẹ̀ ní àwọn āyah tó yanjú. Dájúdájú Allāhu ń tọ́ ẹni tí Ó bá fẹ́ sí ọ̀nà.');
INSERT INTO "aykyuni" VALUES(22,17,'22:17','Dájúdájú àwọn tó gbàgbọ́ ní òdodo àti àwọn tó di yẹhudi àti àwọn sọ̄bi’ūn àti àwọn nasọ̄rọ̄ àti àwọn mọjūs àti àwọn tó ṣẹbọ, dájúdájú Allāhu yóò ṣèdájọ́ láààrin wọn ní Ọjọ́ Àjíǹde. Dájúdájú Allāhu ni Arínú-róde gbogbo n̄ǹkan.1 ');
INSERT INTO "aykyuni" VALUES(22,18,'22:18','Ṣé o ò wòye pé dájúdájú Allāhu ni àwọn tó ń bẹ nínú àwọn sánmọ̀ àti àwọn tó ń bẹ lórí ilẹ̀, àti òòrùn, òṣùpá, àwọn ìràwọ̀, àwọn àpáta, àwọn igi, àwọn n̄ǹkan ẹlẹ́mìí àti ọ̀pọ̀lọ́pọ̀ nínú àwọn ènìyàn ń forí kanlẹ̀ fún? Ọ̀pọ̀lọ́pọ̀ sì ni ìyà ti kò lé lórí. Ẹnikẹ́ni tí Allāhu bá fi àbùkù kàn, kò sí ẹnì kan tí ó máa ṣe àpọ́nlé rẹ̀. Dájúdájú Allāhu ń ṣe ohun tí Ó bá fẹ́.1 ');
INSERT INTO "aykyuni" VALUES(22,19,'22:19','Àwọn oníjà méjì (kan) nìyí, wọ́n ń takò ara wọn nípa Olúwa wọn. Nítorí náà, àwọn tó ṣàì gbàgbọ́, wọ́n máa gé aṣọ Iná fún wọn, wọ́n yó sì máa da omi tó gbóná gan-an lé wọn lórí láti òkè orí wọn.');
INSERT INTO "aykyuni" VALUES(22,20,'22:20','Wọ́n máa fi yọ́ ohun tí ń bẹ nínú ikùn wọn àti awọ ara wọn.');
INSERT INTO "aykyuni" VALUES(22,21,'22:21','Àwọn òdùrọ irin (ìyà) sì wà fún wọn pẹ̀lú.');
INSERT INTO "aykyuni" VALUES(22,22,'22:22','Ìgbàkígbà tí wọ́n bá fẹ́ jáde kúrò nínú rẹ̀ látara ìbànújẹ́, wọ́n á dá wọn padà sínú rẹ̀. (A óò sọ pé): “Ẹ tọ́ ìyà Iná jónijóni wò.”');
INSERT INTO "aykyuni" VALUES(22,23,'22:23','Dájúdájú Allāhu yóò fi àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere wọ inú àwọn Ọgbà Ìdẹ̀ra, tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. A óò ṣe wọ́n ní ọ̀ṣọ́ nínú rẹ̀ pẹ̀lú ẹ̀gbà wúrà àti àlúùlúù. Aṣọ àlàárì sì ni aṣọ wọn nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(22,24,'22:24','A fún wọn ní ìmọ̀nà síbi ohun tó dára nínú ọ̀rọ̀.A sì tọ́ wọn sí ọ̀nà Ẹlẹ́yìn. ');
INSERT INTO "aykyuni" VALUES(22,25,'22:25','Dájúdájú àwọn tó ṣàì gbàgbọ́, tí wọ́n ń ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu àti Mọ́sálásí Haram, èyí tí A ṣe ní dọ́gbadọ́gba fún àwọn ènìyàn, olùgbé-inú rẹ̀ àti àlejò (fún ìjọ́sìn ṣíṣe), ẹnikẹ́ni tí ó bá ní èrò láti ṣe ìyípadà kan níbẹ̀ pẹ̀lú àbòsí, A máa mú un tọ́ ìyà ẹlẹ́ta-eléro wò.');
INSERT INTO "aykyuni" VALUES(22,26,'22:26','(Ẹ rántí) nígbà tí A ṣàfi hàn àyè ilé náà fún (Ànábì) ’Ibrọ̄hīm (A sì pa á láṣẹ) pé o ò gbọdọ̀ sọ kiní kan di akẹgbẹ́ fún Mi. Àti pé kí o ṣe ilé Mi ní mímọ́ fún àwọn olùyípo rẹ̀, olùkírun, olùdáwọ́tẹ-orúnkún àti olùforíkanlẹ̀.');
INSERT INTO "aykyuni" VALUES(22,27,'22:27','Kí o sì pe ìpè fún àwọn ènìyàn fún iṣẹ́ Hajj. Wọn yóò wá bá ọ pẹ̀lú ìrìn ẹsẹ̀ àti lórí gbogbo n̄ǹkan ìgùn, wọn yóò máa wá láti àwọn ọ̀nà jíjìn');
INSERT INTO "aykyuni" VALUES(22,28,'22:28','nítorí kí wọ́n lè rí àwọn àǹfààní tó ń bẹ fún wọn àti nítorí kí wọ́n lè ṣèrántí orúkọ Allāhu fún àwọn ọjọ́ tí wọ́n ti mọ̀lórí n̄ǹkan tí Allāhu pa lésè fún wọn nínú àwọn ẹran-ọ̀sìn. Nítorí náà, ẹ jẹ nínú rẹ̀, kí ẹ sì fi bọ́ aláìlera, tálíkà. ');
INSERT INTO "aykyuni" VALUES(22,29,'22:29','Lẹ́yìn náà, kí wọ́n yanjú ìdọ̀tí ara wọn fún ìparí iṣẹ́ Hajj wọn, kí wọ́n mú àwọn ẹ̀jẹ́ wọn ṣẹ, kí wọ́n sì yípo Ilé Láéláé náà.');
INSERT INTO "aykyuni" VALUES(22,30,'22:30','Ìyẹn (nìyẹn). Ẹnikẹ́ni tí ó bá sì pàtàkì àwọn n̄ǹkan tí Allāhu bu ọ̀wọ̀ fún, òhun ló lóore jùlọ fún un lọ́dọ̀ Olúwa rẹ̀. Wọ́n sì ṣe àwọn ẹran-ọ̀sìn ní ẹ̀tọ́ fún yín àyàfi àwọn tí wọ́n ń kà (ní èèwọ̀) fún yín. Nítorí náà, ẹ jìnnà sí ẹ̀gbin òrìṣà. Kí ẹ sì jìnnà sí ọ̀rọ̀ irọ́.');
INSERT INTO "aykyuni" VALUES(22,31,'22:31','(Ẹ jẹ́) olùdúró-déédé-nínú-ẹ̀sìn fún Allāhu, ẹ má ṣe jẹ́ ẹni tí yóò máa ṣẹbọ sí I. Ẹnikẹ́ni tí ó bá ṣẹbọ sí Allāhu, ó dà bí ẹni tó jábọ́ láti ojú sánmọ̀, tí ẹyẹ sì gbé e lọ tàbí (tí) atẹ́gùn jù ú sínú àyè tó jìnnà.1 ');
INSERT INTO "aykyuni" VALUES(22,32,'22:32','Ìyẹn (nìyẹn). Ẹnikẹ́ni tí ó bá sì pàtàkì àwọn n̄ǹkan tí Allāhu fi ṣe àríṣàmì fún ẹ̀sìn ’Islām, dájúdájú ó wà lára níní ìbẹ̀rù Allāhu nínú ọkàn.');
INSERT INTO "aykyuni" VALUES(22,33,'22:33','Àwọn àǹfààní wà fún yín lára àwọn ẹran-ọ̀sìn títí di gbèdéke àkókò kan. Lẹ́yìn náà, Ilé Láéláé náà ni ibùpa àwọn ẹran náà.');
INSERT INTO "aykyuni" VALUES(22,34,'22:34','Ìkọ̀ọ̀kan ìjọ (mùsùlùmí tó ṣíwájú) ni A yan ẹran pípa fún nítorí kí wọ́n lè dárúkọ Allāhu lórí ohun tí Ó pèsè fún wọn nínú àwọn ẹran-ọ̀sìn. Nítorí náà, Ọlọ́hun yín tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo, Ọlọ́hun Ọ̀kan ṣoṣo ni. Òun ni kí ẹ jẹ́ mùsùlùmí fún. Kí o sì fún àwọn ọlọ́kàn ìrẹ̀lẹ̀, àwọn olùfọkànbalẹ̀ sọ́dọ̀ Allāhu ní ìró ìdùnnú.');
INSERT INTO "aykyuni" VALUES(22,35,'22:35','(Àwọn ni) àwọn tí ó jẹ́ pé tí A bá dárúkọ Allāhu (fún wọn), ọkàn wọn máa gbọ̀n rìrì. (Wọ́n jẹ́) onísùúrù lórí ohun tí ó bá ṣẹlẹ̀ sí wọn. (Wọ́n jẹ́) olùkírun. Wọ́n sì ń ná nínú ohun tí A pa lésè fùn wọn.');
INSERT INTO "aykyuni" VALUES(22,36,'22:36','Àwọn ràkúnmí, A ṣe wọ́n nínú àwọn n̄ǹkan àríṣàmì fún ẹ̀sìn Allāhu fún yín. Oore wà lára wọn fún yín. Nítorí náà, ẹ dárúkọ Allāhu lé wọn lórí (kí ẹ sì gún wọn) ní ìdúró. Nígbà tí wọ́n bá fi ẹ̀gbẹ́ lélẹ̀, ẹ jẹ nínú rẹ̀. Ẹ fi bọ́ ẹni tó ní ìtẹ́lọ́rùn àti atọrọjẹ. Báyẹn ni A ṣe rọ̀ wọ́n fún yín nítorí kí ẹ lè dúpẹ́ (fún Un).');
INSERT INTO "aykyuni" VALUES(22,37,'22:37','Ẹran (tí ẹ pa) àti ẹ̀jẹ̀ rẹ̀ kò níí dé ọ̀dọ̀ Allāhu. Ṣùgbọ́n ìbẹ̀rù Allāhu láti ọ̀dọ̀ yín ló máa dé ọ̀dọ̀ Rẹ̀. Báyẹn ni (Allāhu) ṣe rọ̀ wọ́n fún yín nítorí kí ẹ lè ṣe ìgbétóbi fún Allāhu nípa bí Ó ṣe fi ọ̀nà mọ̀ yín. Kí o sì fún àwọn olùṣe-rere ní ìró ìdùnnú.');
INSERT INTO "aykyuni" VALUES(22,38,'22:38','Dájúdájú Allāhu ń ti aburú kúrò fún àwọn tó gbàgbọ́ ní òdodo. Dájúdájú Allāhu kò nífẹ̀ẹ́ gbogbo àwọn oníjàǹbá, aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(22,39,'22:39','A yọ̀ǹda (ogun ẹ̀sìn jíjà) fún àwọn (mùsùlùmí) tí (àwọn kèfèrí) ń gbógun tì nítorí pé (àwọn kèfèrí) ti ṣe àbòsí sí wọn. Dájúdájú Allāhu ni Alágbára lórí àrànṣe wọn.');
INSERT INTO "aykyuni" VALUES(22,40,'22:40','(Àwọn ni) àwọn tí wọ́n lé jáde kúrò nínú ìlú wọn ní ọ̀nà àìtọ́ àfi (nítorí pé) wọ́n ń sọ pé: “Allāhu ni Olúwa wa.” Tí kò bá jẹ́ pé Allāhu ń dènà (aburú) fún àwọn ènìyàn ni, tí Ó ń fi apá kan wọn dènà (aburú) fún apá kan, wọn ìbá ti wó ilé ìsìn àwọn fadá, ṣọ́ọ̀ṣì, sínágọ́gù àti àwọn mọ́sálásí tí wọ́n ti ń dárúkọ Allāhu ní ọ̀pọ̀lọ́pọ̀.Dájúdájú Allāhu yóò ṣe àrànṣe fún ẹnikẹ́ni tó ń ran (ẹ̀sìn ’Islām) Rẹ̀ lọ́wọ́. Dájúdájú Allāhu mà ni Alágbára, Olùborí.2 ');
INSERT INTO "aykyuni" VALUES(22,41,'22:41','(Àwọn náà ni) àwọn tí ó jẹ́ pé tí A bá fún wọn ní ipò lórí ilẹ̀, wọn yóò kírun, wọn yóò yọ Zakāh, wọn yóò pàṣẹ ohun rere, wọn yó sì kọ ohun burúkú. Ti Allāhu sì ni ìkángun àwọn ọ̀rọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(22,42,'22:42','Tí wọ́n bá pè ọ́ ní òpùrọ́, àwọn ìjọ Nūh, ìjọ ‘Ād àti ìjọ Thamūd kúkú ti pe àwọn Òjíṣẹ́ wọn ní òpùrọ́ ṣíwájú wọn.');
INSERT INTO "aykyuni" VALUES(22,43,'22:43','Àti pé ìjọ ’Ibrọhīm àti ìjọ (Ànábì) Lūt,');
INSERT INTO "aykyuni" VALUES(22,44,'22:44','àti àwọn ará ìlú Mọdyan (àwọn náà ṣe bẹ́ẹ̀). Wọ́n tún pe (Ànábì) Mūsā ní òpùrọ́. Mo sì lọ́ àwọn aláìgbàgbọ́ lára. Lẹ́yìn náà, Mo gbá wọn mú. Báwo sì ni bí Mo ṣe (fi ìyà) kọ (aburú fún wọn) ti rí!');
INSERT INTO "aykyuni" VALUES(22,45,'22:45','Nítorí náà, mélòó mélòó nínú ìlú tí A ti parẹ́ nígbà tí wọ́n jẹ́ alábòsí; àwọn ilé wọn dàwó lulẹ̀ pẹ̀lú òrùlé rẹ̀. (Mélòó mélòó nínú) kànǹga tí wọ́n ti patì (nípasẹ̀ ìparun) àti ilé pẹ̀tẹ́ẹ̀sì oníbíríkì (tó ti dahoro)!');
INSERT INTO "aykyuni" VALUES(22,46,'22:46','Ṣé wọn kò rìn kiri lórí ilẹ̀ kí wọ́n sì ní àwọn ọkàn tí wọ́n máa fi ṣe làákàyè tàbí àwọn etí tí wọn máa fi gbọ́rọ̀? Dájúdájú àwọn ojú kò fọ́, ṣùgbọ́n àwọn ọkàn tó wà nínú igbá-àyà ló ń fọ́.');
INSERT INTO "aykyuni" VALUES(22,47,'22:47','Wọ́n sì ń kán ọ lójú fún ìyà náà. Allāhu kò sì níí yapa àdéhùn Rẹ̀. Dájúdájú ọjọ́ kan lọ́dọ̀ Olúwa rẹ dà bí ẹgbẹ̀rún ọdún nínú ohun tí ẹ̀ ń kà (ní òǹkà).1 ');
INSERT INTO "aykyuni" VALUES(22,48,'22:48','Mélòó mélòó nínú ìlú tí Mo lọ́ lára, tí wọ́n jẹ́ alábòsí. Lẹ́yìn náà, Mo gbá wọn mú. Ọ̀dọ̀ Mi sì ni àbọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(22,49,'22:49','Sọ pé: “Ẹ̀yin ènìyàn, èmi mà ni olùkìlọ̀ pọ́nńbélé fún yín.”');
INSERT INTO "aykyuni" VALUES(22,50,'22:50','Nítorí náà, àwọn tó bá gbàgbọ́ ní òdodo, tí wọ́n sì ṣe iṣẹ́ rere, àforíjìn àti arísìkí alápọ̀n-ọ́nlé ń bẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(22,51,'22:51','Àwọn tó ṣe iṣẹ́ (burúkú) nípa àwọn āyah Wa, tí wọ́n ń dá àwọn ènìyàn ní agara (láti tẹ̀lé àwọn āyah Wa, tí wọ́n sì lérò pé àwọn máa mórí bọ́ lọ́dọ̀ Allāhu); àwọn wọ̀nyẹn ni èrò inú iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(22,52,'22:52','A kò rán Òjíṣẹ́ kan tàbí Ànábì kan níṣẹ́ ṣíwájú rẹ àfi kí ó jẹ́ pé nígbà tí ó bá sọ̀rọ̀, Èṣù máa ju (n̄ǹkan) sínú ọ̀rọ̀ rẹ̀. Nítorí náà, Allāhu yóò pa ohun tí Èṣù ń jù sínú rẹ̀ rẹ́. Lẹ́yìn náà, Ó máa fi òdodo àwọn āyah Rẹ̀ rinlẹ̀. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n. 1 ');
INSERT INTO "aykyuni" VALUES(22,53,'22:53','(Èyí ń ṣẹlẹ̀) nítorí kí (Allāhu) lè fi ohun tí Èṣù ń jù (sínú rẹ̀) ṣe àdánwò fún àwọn tí àìsàn ń bẹ nínú ọkàn wọn àti àwọn tí ọkàn wọn le. Dájúdájú àwọn alábòsí sì wà nínú ìyapa tó jìnnà (sí òdodo).');
INSERT INTO "aykyuni" VALUES(22,54,'22:54','(Èyí ń ṣẹlẹ̀) nítorí kí àwọn tí A fún ní ìmọ̀ lè mọ̀ pé dájúdájú al-Ƙur’ān jẹ́ òdodo láti ọ̀dọ̀ Olúwa rẹ. Nítorí náà, wọn yó sì gbàgbọ́ nínú rẹ̀, ọkàn wọn yó sì balẹ̀ sí i. Dájúdájú Allāhu l’Ó ń fi ẹsẹ̀ àwọn tó gbàgbọ́ ní òdodo rinlẹ̀ sójú ọ̀nà tààrà (’Islām).');
INSERT INTO "aykyuni" VALUES(22,55,'22:55','Àwọn tó ṣàì gbàgbọ́ kò sì níí yé wà nínú iyèméjì nípa rẹ̀ títí Àkókò náà yóò fi dé bá wọn ní òjijì tàbí (títí) ìyà ọjọ́ ìparun yóò fi dé bá wọn.');
INSERT INTO "aykyuni" VALUES(22,56,'22:56','Gbogbo ìjọba ọjọ́ yẹn ń jẹ́ ti Allāhu tí Ó máa ṣèdájọ́ láààrin wọn. Nítorí náà, àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, (wọn yóò wà) nínú àwọn Ọgbà Ìdẹ̀ra.');
INSERT INTO "aykyuni" VALUES(22,57,'22:57','Àwọn tó sì ṣàì gbàgbọ́, tí wọ́n tún pe àwọn āyah Wa ní irọ́; àwọn wọ̀nyẹn ni ìyà tí í yẹpẹrẹ ẹ̀dá sì wà fún.');
INSERT INTO "aykyuni" VALUES(22,58,'22:58','Àwọn tí wọ́n gbé ìlú wọn jù sílẹ̀ nítorí ẹ̀sìn Allāhu, lẹ́yìn náà, tí wọ́n pa wọ́n tàbí tí wọ́n kú; dájúdájú Allāhu yóò pèsè fún wọn ní ìpèsè tó dára. Dájúdájú Allāhu, Ó mà l’óore jùlọ nínú àwọn olùpèsè.1 ');
INSERT INTO "aykyuni" VALUES(22,59,'22:59','Dájúdájú (Allāhu) yóò fi wọ́n sínú àyè kan tí wọn yóò yọ́nú sí. Dájúdájú Allāhu mà ni Onímọ̀, Aláfaradà.');
INSERT INTO "aykyuni" VALUES(22,60,'22:60','Ìyẹn (nìyẹn). Ẹnikẹ́ni tí ó bá sì gbẹ̀san irú ìyà tí wọ́n fi jẹ ẹ́, lẹ́yìn náà tí wọ́n bá tún ṣàbòsí sí i, dájúdájú Allāhu yóò ṣàrànṣe fún un. Dájúdájú Allāhu mà ni Alámòjúúkúrò, Aláforíjìn.');
INSERT INTO "aykyuni" VALUES(22,61,'22:61','Ìyẹn nítorí pé dájúdájú Allāhu ń fi òru bọ inú ọ̀sán, Ó sì ń fi ọ̀sán bọ inú òru. Allāhu sì ni Olùgbọ́, Olùríran.');
INSERT INTO "aykyuni" VALUES(22,62,'22:62','Ìyẹn nítorí pé dájúdájú Allāhu, Òun ni Òdodo. Àti pé dájúdájú ohun tí wọ́n ń pè lẹ́yìn Rẹ̀ ni irọ́. Dájúdájú Allāhu, Òun ni Ó ga, Ó tóbi.');
INSERT INTO "aykyuni" VALUES(22,63,'22:63','Ṣé o ò rí i pé dájúdájú Allāhu ń sọ omi kalẹ̀ láti sánmọ̀, ilẹ̀ sì máa lọ́ràá wá? Dájúdájú Allāhu ni Aláàánú, Alámọ̀tán.');
INSERT INTO "aykyuni" VALUES(22,64,'22:64','TiRẹ̀ ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Dájúdájú Allāhu, Òun mà ni Ọlọ́rọ̀, Ẹlẹ́yìn.');
INSERT INTO "aykyuni" VALUES(22,65,'22:65','Ṣé o ò rí i pé dájúdájú Allāhu rọ ohunkóhun tó wà nínú ilẹ̀ fún yín, àti ọkọ̀ ojú-omi tó ń rìn nínú agbami odò pẹ̀lú àṣẹ Rẹ̀, Ó ń mú sánmọ̀ dání tí kò fi jábọ́ àyàfi pẹ̀lú ìyọ̀ǹda Rẹ̀? Dájúdájú Allāhu mà ni Aláàánú, Oníkẹ̀ẹ́ fún àwọn ènìyàn.');
INSERT INTO "aykyuni" VALUES(22,66,'22:66','Òun sì ni Ẹni tó ṣe yín ni alààyè, lẹ́yìn náà Ó ń sọ yín di òkú, lẹ́yìn náà Ó máa sọ yín di alààyè. Dájúdájú ènìyàn mà ni aláìmoore.');
INSERT INTO "aykyuni" VALUES(22,67,'22:67','Ìjọ kọ̀ọ̀kan l’A fún ní ìlànà tí wọ́n máa lò. Nítorí náà, kí wọ́n má ṣe jà ọ́ níyàn nípa ọ̀rọ̀ náà. Kí o sì pèpè sọ́dọ̀ Olúwa rẹ. Dájúdájú o kúkú wà lójú ọ̀nà tààrà. 1 ');
INSERT INTO "aykyuni" VALUES(22,68,'22:68','Tí wọ́n bá sì jà ọ́ níyàn, sọ nígbà náà pé: “Allāhu nímọ̀ jùlọ nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(22,69,'22:69','Allāhu máa ṣèdájọ́ láààrin yín ní Ọjọ́ Àjíǹde nípa ohun tí ẹ̀ ń yapa ẹnu sí.”');
INSERT INTO "aykyuni" VALUES(22,70,'22:70','Ṣé o ò mọ̀ pé dájúdájú Allāhu l’Ó mọ ohun tí ń bẹ nínú sánmọ̀ àti ilẹ̀? Dájúdájú (àkọsílẹ̀) ìyẹn wà nínú tírà kan. Dájúdájú ìyẹn rọrùn fún Allāhu.');
INSERT INTO "aykyuni" VALUES(22,71,'22:71','Lẹ́yìn Allāhu, wọ́n sì ń jọ́sìn fún ohun tí (Allāhu) kò sọ ẹ̀rí kan kalẹ̀ fún àti ohun tí kò sí ìmọ̀ kan fún wọn lórí rẹ̀. Kò sì níí sí alárànṣe kan fún àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(22,72,'22:72','Nígbà tí wọ́n bá ń ké àwọn āyah Wa tó yanjú fún wọn, o máa rí ohun tí ẹ̀mí kọ̀ (ìkorò-ojú) nínú ojú àwọn tó ṣàì gbàgbọ́; wọn sì fẹ́ẹ̀ fọwọ́ ìnira kan àwọn tí ń ké àwọn āyah Wa fún wọn. Sọ pé: “Ṣé kí n̄g fún yín ní ìró ohun tó burú ju ìyẹn? Iná tí Allāhu ṣèlérí rẹ̀ fún àwọn tó ṣàì gbàgbọ́ ni. Ìkángun náà sì burú.”');
INSERT INTO "aykyuni" VALUES(22,73,'22:73','Ẹ̀yin ènìyàn, Wọ́n fi àkàwé kan lélẹ̀. Nítorí náà, ẹ tẹ́tí sí i. Dájúdájú àwọn tí ẹ̀ ń pè lẹ́yìn Allāhu, wọn kò lè dá eṣinṣin kan, wọn ìbàá para pọ̀ láti ṣe bẹ́ẹ̀. Tí eṣinṣin náà bá sì gba kiní kan mọ́ wọn lọ́wọ́, wọn kò lè gbà á padà lọ́wọ́ rẹ̀. Ọ̀lẹ ni ẹni tí ń wá n̄ǹkan (lọ́dọ̀ òrìṣà) àti (òrìṣà) tí wọ́n ń wá n̄ǹkan lọ́dọ̀ rẹ̀.');
INSERT INTO "aykyuni" VALUES(22,74,'22:74','Wọn kò bu ọ̀wọ̀ fún Allāhu ní ọ̀wọ̀ tí ó tọ́ sí I. Dájúdájú Allāhu mà ni Alágbára, Olùborí.');
INSERT INTO "aykyuni" VALUES(22,75,'22:75','Allāhu l’Ó ń ṣa àwọn kan lẹ́ṣà (láti jẹ́) Òjíṣẹ́ nínú àwọn mọlāika àti nínú àwọn ènìyàn. Dájúdájú Allāhu ni Olùgbọ́, Olùríran.1 ');
INSERT INTO "aykyuni" VALUES(22,76,'22:76','Ó mọ ohun tí ń bẹ níwájú wọn àti ní ẹ̀yìn wọn. Ọ̀dọ̀ Allāhu sì ni wọ́n máa ṣẹ́rí àwọn ọ̀rọ̀ ẹ̀dá padà sí.');
INSERT INTO "aykyuni" VALUES(22,77,'22:77','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ dáwọ́tẹ orúnkún (lórí ìrun), ẹ forí kanlẹ̀, ẹ jọ́sìn fún Olúwa yín, kí ẹ sì ṣe rere nítorí kí ẹ lè jèrè.');
INSERT INTO "aykyuni" VALUES(22,78,'22:78','Ẹ jagun fún ẹ̀sìn Allāhu ní ọ̀nà ẹ̀tọ́ tí ẹ lè gbà jagun fún Un. Òun l’Ó ṣà yín lẹ́ṣà, kò sì kó ìdààmú kan kan ba yín nínú ẹ̀sìn. (Ẹ tẹ̀lé) ẹ̀sìn bàbá yín (Ànábì) ’Ibrọ̄hīm. (Allāhu) l’Ó sọ yín ní mùsùlùmí ṣíwájú (àsìkò yìí) àti nínú (al-Ƙur’ān) yìí nítorí kí Òjíṣẹ́ lè jẹ́ ẹlẹ́rìí fún yín àti nítorí kí ẹ̀yin náà lè jẹ́ ẹlẹ́rìí fún àwọn ènìyàn.Nítorí náà, ẹ kírun, ẹ yọ Zakāh, kí ẹ sì bá Allāhu dúró. Òun ni Aláàbò yín. Ó dára ni Aláàbò. Ó sì dára ní Alárànṣe. ');
INSERT INTO "aykyuni" VALUES(23,1,'23:1','Dájúdájú àwọn onígbàgbọ́ òdodo ti jèrè.');
INSERT INTO "aykyuni" VALUES(23,2,'23:2','Àwọn tó ń páyà (Allāhu) nínú ìrun wọn,');
INSERT INTO "aykyuni" VALUES(23,3,'23:3','àti àwọn tó ń ṣẹ́rí kúrò níbi ìsọkúsọ,');
INSERT INTO "aykyuni" VALUES(23,4,'23:4','àti àwọn tó ń ṣe àfọ̀mọ́ (ẹ̀mí àti ara) wọn.1 ');
INSERT INTO "aykyuni" VALUES(23,5,'23:5','àti àwọn tó ń ṣọ́ abẹ́ wọn,');
INSERT INTO "aykyuni" VALUES(23,6,'23:6','àfi ní ọ̀dọ̀ àwọn ìyàwó wọn àti ẹrúbìnrin wọn. Dájúdájú wọn kì í ṣe ẹni èébú (tí wọ́n bá súnmọ́ wọn),');
INSERT INTO "aykyuni" VALUES(23,7,'23:7','nítorí náà, ẹnikẹ́ni tí ó bá wá òmíràn (súnmọ́) lẹ́yìn ìyẹn, àwọn wọ̀nyẹn ni àwọn olùtayọ ẹnu-ààlà,1 ');
INSERT INTO "aykyuni" VALUES(23,8,'23:8','àti àwọn tó ń ṣọ́ àgbàfipamọ́ wọn àti àdéhùn wọn,');
INSERT INTO "aykyuni" VALUES(23,9,'23:9','àti àwọn tó ń ṣọ́ ìrun wọn;');
INSERT INTO "aykyuni" VALUES(23,10,'23:10','àwọn wọ̀nyẹn ni àwọn olùjogún,');
INSERT INTO "aykyuni" VALUES(23,11,'23:11','àwọn tó máa jogún ọgbà ìdẹ̀ra Firdaos. Olùṣegbére ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(23,12,'23:12','Dájúdájú A ti ṣe ẹ̀dá ènìyàn láti inú ohun tí A mọ jáde láti inú ẹrẹ̀.');
INSERT INTO "aykyuni" VALUES(23,13,'23:13','Lẹ́yìn náà, A ṣe é ní àtọ̀ sínú àyè ìrọ̀rùn kan (ìyẹn, ilé-ọmọ).');
INSERT INTO "aykyuni" VALUES(23,14,'23:14','Lẹ́yìn náà, A sọ àtọ̀ di ẹ̀jẹ̀ dídì. Lẹ́yìn náà, A sọ ẹ̀jẹ̀ dídì di bááṣí ẹran. Lẹ́yìn náà, A sọ bááṣí ẹran di eegun. Lẹ́yìn náà, A fi ẹran bo eegun. Lẹ́yìn náà, A sọ ọ́ di ẹ̀dá mìíràn.Nítorí náà, ìbùkún ni fún Allāhu, Ẹni tó dára jùlọ nínú àwọn ẹlẹ́dàá (oníṣẹ́-ọnà).2 ');
INSERT INTO "aykyuni" VALUES(23,15,'23:15','Lẹ́yìn náà, lẹ́yìn ìyẹn, dájúdájú ẹ̀yin yóò di òkú.');
INSERT INTO "aykyuni" VALUES(23,16,'23:16','Lẹ́yìn náà, dájúdájú ní Ọjọ́ Àjíǹde wọ́n máa gbe yín dìde.');
INSERT INTO "aykyuni" VALUES(23,17,'23:17','Dájúdájú A dá àwọn sánmọ̀ méje sí òkè yín. Àwa kò sì níí dágunlá sí àwọn ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(23,18,'23:18','À ń sọ omi kalẹ̀ láti sánmọ̀ níwọ̀n níwọ̀n. Lẹ́yìn náà, À ń mú un dúró sínú ilẹ̀. Dájúdájú Àwa kúkú lágbára láti mú un lọ.');
INSERT INTO "aykyuni" VALUES(23,19,'23:19','Lẹ́yìn náà, A fi dá àwọn ọgbà oko dàbínù àti àjàrà fún yín; ọ̀pọ̀lọ́pọ̀ èso wà nínú rẹ̀ fún yín, ẹ sì ń jẹ nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(23,20,'23:20','Àti igi kan tó ń hù jáde níbi àpáta Sīnā’; ó ń mú epo àti n̄ǹkan ìṣebẹ̀ jáde fún àwọn tó ń jẹ ẹ́.');
INSERT INTO "aykyuni" VALUES(23,21,'23:21','Àti pé dájúdájú àríwòye wà fún yín lára àwọn ẹran-ọ̀sìn; À ń fún yín mu nínú ohun tí ó wà nínú ikùn wọn. Àwọn àǹfààní ọ̀pọ̀lọ́pọ̀ (mìíràn) tún wà fún yín lára wọn, ẹ sì ń jẹ nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(23,22,'23:22','Ẹ sì ń kó ẹrù sí orí àwọn ẹran ọ̀sìn àti ọkọ̀ ojú-omi.');
INSERT INTO "aykyuni" VALUES(23,23,'23:23','Dájúdájú A rán (Ànábì) Nūh níṣẹ́ sí ìjọ rẹ̀. Ó sì sọ pé: “Ẹ̀yin ìjọ mi, ẹ jọ́sìn fún Allāhu. Ẹ kò ní ọlọ́hun mìíràn tí ẹ máa jọ́sìn fún ní ọ̀nà òdodo lẹ́yìn Rẹ̀. Ṣé ẹ kò níí bẹ̀rù (Rẹ̀) ni?”');
INSERT INTO "aykyuni" VALUES(23,24,'23:24','Àwọn olórí tí wọ́n ṣàì gbàgbọ́ nínú àwọn ènìyàn rẹ̀ sọ pé: “Kí ni èyí bí kò ṣe abara kan bí irú yín, tí ó fẹ́ gbé àjùlọ fún ara rẹ̀ lórí yín. Àti pé tí ó bá jẹ́ pé Allāhu bá fẹ́ (ránṣẹ́ sí wa ni), ìbá sọ mọlāika kan kalẹ̀ ni. A kò sì gbọ́ èyí rí lọ́dọ̀ àwọn bàbá wa, àwọn ẹni àkọ́kọ́.');
INSERT INTO "aykyuni" VALUES(23,25,'23:25','Kò jẹ́ kiní kan bí kò ṣe ọkùnrin kan tí àlùjànnú ń bẹ lára rẹ̀. Nítorí náà, ẹ máa retí (ìkángun) rẹ̀ títí di ìgbà kan ná.”');
INSERT INTO "aykyuni" VALUES(23,26,'23:26','(Ànábì) Nūh sọ pé: “Olúwa mi, ṣàrànṣe fún mi nítorí pé wọ́n pè mí ní òpùrọ́.”');
INSERT INTO "aykyuni" VALUES(23,27,'23:27','Nítorí náà, A fi ìmísí ránṣẹ́ sí i pé: “Ṣe ọkọ̀ ojú-omi ní ojú Wa (báyìí) pẹ̀lú ìmísí Wa. Nígbà tí àṣẹ Wa bá dé, tí omi bá ṣẹ́ yọ ní ojú àrò, nígbà náà ni kí o kó sínú ọkọ̀ ojú-omi gbogbo n̄ǹkan ní oríṣi méjì takọ-tabo àti ará ilé rẹ, àyàfi ẹni tí ọ̀rọ̀ náà kò lé lórí nínú wọn (fún ìparun). Má ṣe bá Mi sọ̀rọ̀ nípa àwọn tó ṣàbòsí; dájúdájú A óò tẹ̀ wọ́n rì sínú omi ni.');
INSERT INTO "aykyuni" VALUES(23,28,'23:28','Nígbà tí o bá jókòó sínú ọkọ̀ ojú-omi náà, ìwọ àti àwọn tó wà pẹ̀lú rẹ, sọ pé: “Gbogbo ẹyìn ń jẹ́ ti Allāhu, Ẹni tí Ó gbà wá là lọ́wọ́ ijọ alábòsí.”');
INSERT INTO "aykyuni" VALUES(23,29,'23:29','Sọ pé: “Olúwa mi, sọ̀ mí kalẹ̀ sí ibùsọ̀ ìbùkún. Ìwọ sì lóore jùlọ nínú àwọn amúnigúnlẹ̀.”1 ');
INSERT INTO "aykyuni" VALUES(23,30,'23:30','Dájúdájú àwọn àmì wà nínú ìyẹn. Àwa sì ni À ń dán ẹ̀dá wò.');
INSERT INTO "aykyuni" VALUES(23,31,'23:31','Lẹ́yìn náà, A mú àwọn ìran mìíràn wá lẹ́yìn wọn.');
INSERT INTO "aykyuni" VALUES(23,32,'23:32','A tún rán Òjíṣẹ́ kan sí wọn láààrin ara wọn (láti jíṣẹ́) pé: “Ẹ jọ́sìn fún Allāhu. Ẹ kò ní ọlọ́hun mìíràn tí ẹ máa jọ́sìn fún ní ọ̀nà òdodo lẹ́yìn Rẹ̀. Nítorí náà, ṣé ẹ kò níí bẹ̀rù (Rẹ̀) ni?”');
INSERT INTO "aykyuni" VALUES(23,33,'23:33','Àwọn olórí tó ṣàì gbàgbọ́ nínú ìjọ rẹ̀, àwọn tó pe ìpàdé Ọjọ́ Ìkẹ́yìn nírọ́, àwọn tí A ṣe gbẹdẹmukẹ fún nínú ìṣẹ̀mí ayé yìí, wọ́n wí pé: “Kí ni èyí bí kò ṣe abara kan bí irú yín; ó ń jẹ nínú ohun tí ẹ̀ ń jẹ, ó sì ń mu nínú ohun tí ẹ̀ ń mu.');
INSERT INTO "aykyuni" VALUES(23,34,'23:34','Tí ẹ bá fi lè tẹ̀lé abara kan bí irú yín, dájúdájú ẹ mà ti di ẹni òfò nìyẹn.');
INSERT INTO "aykyuni" VALUES(23,35,'23:35','Ṣé ó ń ṣe àdéhùn fún yín pé nígbà tí ẹ bá kú, tí ẹ sì di erùpẹ̀ àti egungun tán, dájúdájú wọn yóò mu yín jáde (ní alààyè)?');
INSERT INTO "aykyuni" VALUES(23,36,'23:36','Ohun tí Wọ́n ń ṣe ní àdéhùn fún yín (yìí) jìnnà tefétefé (sí òdodo).');
INSERT INTO "aykyuni" VALUES(23,37,'23:37','Kò sí ìṣẹ̀mí ayé kan mọ́ àyàfi èyí tí à ń lò yìí; àwa kan ń kú, àwa kan ń wáyé. Àti pé wọn kò níí gbé wa dìde mọ́.');
INSERT INTO "aykyuni" VALUES(23,38,'23:38','(Òjíṣẹ́ náà) kò jẹ́ kiní kan bí kò ṣe ọkùnrin kan tí ó dá àdápa irọ́ mọ́ Allāhu. Àwa kò sì níí gbà á gbọ́.”');
INSERT INTO "aykyuni" VALUES(23,39,'23:39','(Òjíṣẹ́ náà) sọ pé: “Olúwa mi, ṣàrànṣe fún mi nítorí pé wọ́n pè mí ní òpùrọ́.”');
INSERT INTO "aykyuni" VALUES(23,40,'23:40','(Allāhu) sọ pé: “Láìpẹ́ ńṣe ni wọn yóò di alábàámọ̀.”');
INSERT INTO "aykyuni" VALUES(23,41,'23:41','Nítorí náà, ohùn igbe mú wọn ní ọ̀nà ẹ̀tọ́. A sì sọ wọ́n di pàǹtí. Nítorí náà, kí ìjìnnà sí ìkẹ́ wà fún ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(23,42,'23:42','Lẹ́yìn náà, A tún mú àwọn ìran mìíràn wá lẹ́yìn wọn.');
INSERT INTO "aykyuni" VALUES(23,43,'23:43','Ìjọ kan kò níí lọ ṣíwájú àkókò (ìparun) rẹ̀, wọn kò sì níí sún (ìparun wọn) síwájú.');
INSERT INTO "aykyuni" VALUES(23,44,'23:44','Lẹ́yìn náà, A rán àwọn Òjíṣẹ́ Wa níṣẹ́ ní tẹ̀léǹtẹ̀lé. Ìgbàkígbà tí Òjíṣẹ́ ìjọ kan bá dé, wọ́n á pè é ní òpùrọ́. A sì mú apá kan wọn tẹ̀lé apá kan nínú ìparun. A tún sọ wọ́n di ìtàn. Nítorí náà, kí ìjìnnà sí ìkẹ́ wà fún ìjọ tí kò gbàgbọ́ ní òdodo.');
INSERT INTO "aykyuni" VALUES(23,45,'23:45','Lẹ́yìn náà, A rán (Ànábì) Mūsā àti ọmọ-ìyá rẹ̀ (Ànábì) Hārūn níṣẹ́ pẹ̀lú àwọn àmì Wa àti ẹ̀rí pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(23,46,'23:46','(A rán wọn níṣẹ́) sí Fir‘aon àti àwọn ìjòyè rẹ̀. Ṣùgbọ́n wọ́n ṣègbéraga, wọ́n sì jẹ́ ìjọ olùjẹgàba.');
INSERT INTO "aykyuni" VALUES(23,47,'23:47','Wọ́n wí pé: “Ṣé kí á gba abara méjì bí irú wa gbọ́, nígbà tí ó jẹ́ pé ẹrú wa ni àwọn ènìyàn wọn?”');
INSERT INTO "aykyuni" VALUES(23,48,'23:48','Nítorí náà, wọ́n pe àwọn méjèèjì ní òpùrọ́. Wọ́n sì wà nínú àwọn olùparun.');
INSERT INTO "aykyuni" VALUES(23,49,'23:49','Dájúdájú A fún (Ànábì) Mūsā ní Tírà nítorí kí wọ́n lè mọ̀nà.');
INSERT INTO "aykyuni" VALUES(23,50,'23:50','A ṣe ọmọ Mọryam àti ìyá rẹ̀ ní àmì kan. A sì ṣe ibùgbé fún àwọn méjèèjì sí ibi gíga kan, tó ní ibùgbé, tó sì ní omi ìṣẹ́lẹ̀rú.1 ');
INSERT INTO "aykyuni" VALUES(23,51,'23:51','Ẹ̀yin Òjíṣẹ́, ẹ jẹ nínú àwọn n̄ǹkan dáadáa, kí ẹ sì ṣe iṣẹ́ rere. Dájúdájú Èmi ni Onímọ̀ nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(23,52,'23:52','Àti pé dájúdájú (’Islām) yìí ni ẹ̀sìn yín. (Ó jẹ́) ẹ̀sìn kan ṣoṣo. Èmi sì ni Olúwa yín. Nítorí náà, ẹ bẹ̀rù Mi.');
INSERT INTO "aykyuni" VALUES(23,53,'23:53','Ṣùgbọ́n wọ́n gé ọ̀rọ̀ ẹ̀sìn wọn láààrin ara wọn sí kélekèle. Ìjọ kọ̀ọ̀kan sì ń yọ̀ sí ohun tó ń bẹ ní ọ̀dọ̀ wọn.');
INSERT INTO "aykyuni" VALUES(23,54,'23:54','Nítorí náà, fi wọ́n sílẹ̀ sínú ìṣìnà wọn títí di ìgbà kan ná.');
INSERT INTO "aykyuni" VALUES(23,55,'23:55','Ṣé wọ́n ń lérò pé (bí) A ṣe ń fi dúkìá àti ọmọ nìkan ṣe ìrànlọ́wọ́ fún wọn,');
INSERT INTO "aykyuni" VALUES(23,56,'23:56','ni A óò ṣe máa yára ti àwọn oore sí ọ̀dọ̀ wọn lọ (títí láéláé). Rárá, wọn kò fura ni!1 ');
INSERT INTO "aykyuni" VALUES(23,57,'23:57','Dájúdájú àwọn tó ń páyà (ìṣírò-iṣẹ́) nípa ìpáyà wọn nínú Olúwa wọn;');
INSERT INTO "aykyuni" VALUES(23,58,'23:58','àti àwọn tó ń gba àwọn āyah Olúwa wọn gbọ́;');
INSERT INTO "aykyuni" VALUES(23,59,'23:59','àti àwọn tí kì í ṣẹbọ sí Olúwa wọn,');
INSERT INTO "aykyuni" VALUES(23,60,'23:60','àti àwọn tí wọ́n ń ṣe ohun wọ́n ń ṣe (nínú àwọn iṣẹ́ rere) pẹ̀lú ìbẹ̀rù nínú ọkàn wọn pé dájúdájú àwọn yóò padà sí ọ̀dọ̀ Olúwa wọn;');
INSERT INTO "aykyuni" VALUES(23,61,'23:61','àwọn wọ̀nyẹn ni wọ́n ń yára ṣe àwọn iṣẹ́ rere. Àwọn sì ni aṣíwájú nínú (àwọn ẹ̀san) iṣẹ́ náà.');
INSERT INTO "aykyuni" VALUES(23,62,'23:62','Àti pé A kò làbọ ẹ̀mí kan lọ́rùn àfi ìwọ̀n agbára rẹ̀. Tírà kan tó ń sọ òdodo (nípa iṣẹ́ ẹ̀dá) ń bẹ lọ́dọ̀ Wa. A kò sì níí ṣàbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(23,63,'23:63','Àmọ́ ọkàn àwọn aláìgbàgbọ́ wà nínú ìfọ́núfọ́ra nípa (al-Ƙur’ān) yìí. Àti pé wọ́n ní àwọn iṣẹ́ kan lọ́wọ́, tí ó yàtọ̀ sí iṣẹ́ rere yẹn.Wọ́n sì ń ṣe iṣẹ́ náà ');
INSERT INTO "aykyuni" VALUES(23,64,'23:64','títí di ìgbà tí A máa gbá àwọn onígbẹdẹmukẹ nínú wọn mú pẹ̀lú ìyà. Nígbà náà ni wọ́n máa kígbe (fún ìrànlọ́wọ́).');
INSERT INTO "aykyuni" VALUES(23,65,'23:65','Ẹ má ṣe kígbe (fún ìrànlọ́wọ́) ní òní. Dájúdájú kò sí ẹni tí ó máa ràn yín lọ́wọ́ lọ́dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(23,66,'23:66','Wọ́n kúkú ń ka àwọn āyah Mi fún yín. Àmọ́ ńṣe ni ẹ̀ ń fà sẹ́yìn (níbi òdodo).');
INSERT INTO "aykyuni" VALUES(23,67,'23:67','Ẹ̀ sì ń fi (Kaaba) ṣègbéraga, ẹ tún sọ ìsọkúsọ (nípa al-Ƙur’ān) ní alẹ́!');
INSERT INTO "aykyuni" VALUES(23,68,'23:68','Ṣé wọn kò ronú sí ọ̀rọ̀ náà ni? Tàbí ohun tó dé bá wọn jẹ́ ohun tí kò dé bá àwọn bàbá wọn àkọ́kọ́ rí ni?');
INSERT INTO "aykyuni" VALUES(23,69,'23:69','Tàbí wọn kò mọ Òjíṣẹ́ wọn mọ́ ni wọ́n fi di alátakò rẹ̀?');
INSERT INTO "aykyuni" VALUES(23,70,'23:70','Tàbí wọn ń wí pé àlùjànnú kan ń bẹ lára rẹ̀ ni? Rárá o. Ó mú òdodo wá bá wọn ni. Àmọ́ ọ̀pọ̀lọ́pọ̀ wọn sì jẹ́ olùkórira òdodo.');
INSERT INTO "aykyuni" VALUES(23,71,'23:71','Tí ó bá jẹ́ pé (Allāhu), Ọba Òdodo tẹ̀lé ìfẹ́-inú wọn ni, àwọn sánmọ̀ àti ilẹ̀ àti àwọn tó wà nínú wọn ìbá kúkú ti bàjẹ́. Ṣùgbọ́n A mú ìṣítí nípa ọ̀rọ̀ ara wọn wá fún wọn ni, wọ́n sì ń gbúnrí kúrò níbi ìṣítí (tí A mú wá fún) wọn.');
INSERT INTO "aykyuni" VALUES(23,72,'23:72','Tàbí ò ń béèrè owó-ọ̀yà kan lọ́wọ́ wọn ni? Owó-ọ̀yà Olúwa rẹ̀ lóore jùlọ. Àti pé Òun l’óore jùlọ nínú àwọn olùpèsè.1 ');
INSERT INTO "aykyuni" VALUES(23,73,'23:73','Àti pé dájúdájú ò ń pè wọ́n sójú ọ̀nà tààrà ni.');
INSERT INTO "aykyuni" VALUES(23,74,'23:74','Àwọn tí kò sì gbàgbọ́ nínú Ọjọ́ Ìkẹ́yìn ni wọ́n ń ṣẹ́rí kúrò lójú ọ̀nà náà.');
INSERT INTO "aykyuni" VALUES(23,75,'23:75','Tí ó bá jẹ́ pé A kẹ́ wọn (nílé ayé), tí A sì gbé gbogbo ìṣòro ara wọn kúrò fún wọn, wọn ìbá tún won̄koko mọ́ ìtayọ ẹnu-ààlà wọn, tí wọn yóò máa pa rìdàrìdà (nínú ìṣìnà).');
INSERT INTO "aykyuni" VALUES(23,76,'23:76','Dájúdájú A fi ìyà jẹ wọ́n. Àmọ́ wọn kò tẹríba fún Olúwa wọn, wọn kò sì rawọ́ rasẹ̀ sí I');
INSERT INTO "aykyuni" VALUES(23,77,'23:77','títí di ìgbà tí A fi ṣílẹ̀kùn ìyà líle fún wọn; nígbà náà ni wọ́n sọ̀rètí nù.');
INSERT INTO "aykyuni" VALUES(23,78,'23:78','(Allāhu), Òun ni Ẹni tí Ó ṣe ìgbọ́rọ̀, ìríran àti ọkàn fún yín, ìdúpẹ́ yín sì kéré.');
INSERT INTO "aykyuni" VALUES(23,79,'23:79','Òun ni Ẹni tí Ó da yín sórí ilẹ̀, ọ̀dọ̀ Rẹ̀ sì ni wọ́n yóò ko yín jọ sí.');
INSERT INTO "aykyuni" VALUES(23,80,'23:80','Òun ni Ẹni tó ń sọ ẹ̀dá di alààyè, Ó ń sọ ẹ̀dá di òkú, tiRẹ̀ sì ni ìtẹ̀léǹtẹ̀lé òru àti ọ̀sán, ṣé ẹ ò níí ṣe làákàyè ni?');
INSERT INTO "aykyuni" VALUES(23,81,'23:81','Rárá (wọn kò níí ṣe làákàyè, ṣebí) irú ohun tí àwọn ẹni àkọ́kọ́ wí ni àwọn náà wí.');
INSERT INTO "aykyuni" VALUES(23,82,'23:82','Wọ́n wí pé: “Ṣé nígbà tí a bá ti kú, tí a ti di erùpẹ̀ àti egungun, ṣé wọn yóò gbé wa dìde ni?');
INSERT INTO "aykyuni" VALUES(23,83,'23:83','Wọ́n kúkú ti ṣàdéhùn èyí fún àwa àti àwọn bàbá wa tẹ́lẹ̀tẹ́lẹ̀. Èyí kò jẹ́ kiní kan bí kò ṣe àkọsílẹ̀ àlọ́ àwọn ẹni àkọ́kọ́.”');
INSERT INTO "aykyuni" VALUES(23,84,'23:84','Sọ pé: “Ta ni Ẹni tí Ó ni ilẹ̀ àti ẹni tí ó wà ní orí rẹ̀ tí ẹ bá mọ̀?”');
INSERT INTO "aykyuni" VALUES(23,85,'23:85','Wọ́n á wí pé: “Ti Allāhu ni.” Sọ pé: “Ṣé ẹ kò níí lo ìrántí ni?”');
INSERT INTO "aykyuni" VALUES(23,86,'23:86','Sọ pé: “Ta ni Olúwa àwọn sánmọ̀ méjèèje àti Olúwa Ìtẹ́ ńlá?”');
INSERT INTO "aykyuni" VALUES(23,87,'23:87','Wọ́n á wí pé: “Ti Allāhu ni.” Sọ pé: “Ṣé ẹ kò níí bẹ̀rù (Rẹ̀) ni?”');
INSERT INTO "aykyuni" VALUES(23,88,'23:88','Sọ pé: “Ta ni Ẹni tí ìjọba gbogbo n̄ǹkan wà ní ọwọ́ Rẹ̀, (Ẹni tí) Ó ń gba ẹ̀dá là nínú ìyà, (tí) kò sì sí ẹni tí ó lè gba ẹ̀dá là níbi ìyà Rẹ̀ tí ẹ bá mọ̀?”');
INSERT INTO "aykyuni" VALUES(23,89,'23:89','Wọ́n á wí pé: “Ti Allāhu ni.” Sọ pé: “Nítorí náà, báwo ni wọ́n ṣe ń dan yín?”');
INSERT INTO "aykyuni" VALUES(23,90,'23:90','Àmọ́ sá, òdodo ni A mú wá ba wọn. Dájúdájú àwọn sì ni òpùrọ́.');
INSERT INTO "aykyuni" VALUES(23,91,'23:91','Allāhu kò fi ẹnì kan kan ṣe ọmọ, kò sì sí ọlọ́hun kan pẹ̀lú Rẹ̀. (Tí kò bá rí bẹ́ẹ̀) nígbà náà, ọlọ́hun kọ̀ọ̀kan ìbá ti kó ẹ̀dá tó dá lọ, apá kan wọn ìbá sì borí apá kan. Mímọ́ ni fún Allāhu tayọ ohun tí wọ́n ń fi ròyìn (Rẹ̀ nípa ọmọ bíbí).');
INSERT INTO "aykyuni" VALUES(23,92,'23:92','(Allāhu) Onímọ̀-ìkọ̀kọ̀ àti gban̄gba. Nítorí náà, Ó ga tayọ n̄ǹkan tí wọ́n fi ń ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(23,93,'23:93','Sọ pé: “Olúwa mi, Ó ṣeé ṣe kí Ó fi ohun tí wọ́n ń ṣèlérí rẹ̀ fún wọn hàn mí,');
INSERT INTO "aykyuni" VALUES(23,94,'23:94','Olúwa mi, má ṣe fi mí sínú ìjọ alábòsí.”');
INSERT INTO "aykyuni" VALUES(23,95,'23:95','Dájúdájú Àwa lágbára láti fi ohun tí A ṣèlérí rẹ̀ fún wọn hàn ọ́.');
INSERT INTO "aykyuni" VALUES(23,96,'23:96','Fi èyí tó dára tí àìdára dànù. Àwa nímọ̀ jùlọ nípa ohun tí wọ́n fi ń ròyìn (Wa).');
INSERT INTO "aykyuni" VALUES(23,97,'23:97','Sọ pé: “Olúwa mi, mò ń sádi Ọ́ níbi ròyíròyí t’àwọn èṣù.');
INSERT INTO "aykyuni" VALUES(23,98,'23:98','Mò ń sádi Ọ́, Olúwa mi, níbi kí wọ́n wá bá mi.”');
INSERT INTO "aykyuni" VALUES(23,99,'23:99','(Wọ́n ń ṣẹ̀mí lọ) títí di ìgbà tí ikú fi máa dé bá ọ̀kan nínú wọn. Ó sì máa wí pé: “Olúwa mi, Ẹ dá mi padà (sílé ayé)');
INSERT INTO "aykyuni" VALUES(23,100,'23:100','nítorí kí èmi lè ṣe iṣẹ́ rere nínú èyí tí mo gbé jù sílẹ̀. Kò rí bẹ́ẹ̀ rárá, dájúdájú ó jẹ́ ọ̀rọ̀ kan tí ó kàn ń wí ni, (àmọ́) gàgá ti wà ní ẹ̀yìn wọn títí di ọjọ́ tí A óò gbé wọn dìde.1 ');
INSERT INTO "aykyuni" VALUES(23,101,'23:101','Nígbà tí wọ́n bá fọn fèrè oníwo fún àjíǹde, kò níí sí ìbátan láààrin wọn ní ọjọ́ yẹn, wọn kò sì níí bira wọn léèrè ìbéèrè.1 ');
INSERT INTO "aykyuni" VALUES(23,102,'23:102','Ẹnikẹ́ni tí òṣùwọ̀n (iṣẹ́ rere) rẹ̀ bá tẹ̀wọ̀n, àwọn wọ̀nyẹn ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(23,103,'23:103','Ẹnikẹ́ni tí òṣùwọ̀n (iṣẹ́ rere) rẹ̀ bá fúyẹ́, àwọn wọ̀nyẹn ni àwọn tó ṣe ẹ̀mí ara wọn lófò. Olùṣegbére sì ni wọ́n nínú iná Jahanamọ.');
INSERT INTO "aykyuni" VALUES(23,104,'23:104','Iná yóò máa jó wọn lójú. Wọn yó sì wà nínú rẹ̀, tí Iná yóò ti bà wọ́n lójú jẹ́.');
INSERT INTO "aykyuni" VALUES(23,105,'23:105','Ṣé wọ́n kì í ka àwọn āyah Mi fún yín ni, àmọ́ tí ẹ̀ ń pè é ní irọ́?');
INSERT INTO "aykyuni" VALUES(23,106,'23:106','Wọ́n wí pé: “Olúwa wa, orí burúkú ló kọlù wá; àwa sì jẹ́ ìjọ olùṣìnà.');
INSERT INTO "aykyuni" VALUES(23,107,'23:107','Olúwa wa, mú wa jáde kúrò nínú Iná. Tí a bá fi lè padà (síbi iṣẹ́ ibi nílé ayé), dájúdájú nígbà náà alábòsí ni àwa.”');
INSERT INTO "aykyuni" VALUES(23,108,'23:108','(Allāhu) sọ pé: “Ẹ tẹ̀rì sínú rẹ̀. Ẹ má ṣe bá Mi sọ̀rọ̀.”');
INSERT INTO "aykyuni" VALUES(23,109,'23:109','Dájúdájú igun kan wà nínú àwọn ẹrúsìn Mi, tí wọ́n ń sọ pé: “Olúwa wa, a gbàgbọ́ ní òdodo. Nítorí náà, foríjìn wá, kí O sì kẹ́ wa. Ìwọ sì l’óore jùlọ nínú àwọn aláàánú.”1 ');
INSERT INTO "aykyuni" VALUES(23,110,'23:110','Ṣùgbọ́n ẹ sọ wọ́n di oníyẹ̀yẹ́ tó bẹ́ẹ̀ gẹ́ tí àwọn yẹ̀yẹ́ (yín) fi mu yín gbàgbé ìrántí Mi. Ẹ̀yin sì ń fi wọ́n rẹ́rìn-ín.');
INSERT INTO "aykyuni" VALUES(23,111,'23:111','Dájúdájú Mo san wọ́n ní ẹ̀san ní òní nítorí sùúrù wọn; dájúdájú àwọn, àwọn ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(23,112,'23:112','(Allāhu) sọ pé: “Ọdún mélòó ni ẹ̀yin (aláìgbàgbọ́) lò lórí ilẹ̀ ayé?”');
INSERT INTO "aykyuni" VALUES(23,113,'23:113','Wọ́n wí pé: “A lo ọjọ́ kan tàbí abala ọjọ́ kan, bi àwọn olùṣírò-ọjọ́ léèrè wò.”');
INSERT INTO "aykyuni" VALUES(23,114,'23:114','(Allāhu) sọ pé: “Ẹ kò gbé ilé ayé bí kò ṣe fún ìgbà díẹ̀, tí ó bá jẹ́ pé ẹ mọ̀.”');
INSERT INTO "aykyuni" VALUES(23,115,'23:115','Ṣé ẹ lérò pé A kàn ṣẹ̀dá yín fún ìranù ni, àti pé dájúdájú wọn ò níí da yín padà sọ́dọ̀ Wa?”');
INSERT INTO "aykyuni" VALUES(23,116,'23:116','Nítorí náà, Allāhu Ọba Òdodo ga. Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun, Olúwa Ìtẹ́ alápọ̀n-ọ́nlé.');
INSERT INTO "aykyuni" VALUES(23,117,'23:117','Ẹni tí ó bá pe ọlọ́hun mìíràn pẹ̀lú Allāhu, kò ní ẹ̀rí lọ́wọ́ lórí rẹ̀. Dájúdájú ìṣírò-iṣẹ́ rẹ̀ sì wà lọ́dọ̀ Olúwa rẹ̀. Dájúdájú àwọn aláìgbàgbọ́ kò níí jèrè.');
INSERT INTO "aykyuni" VALUES(23,118,'23:118','Sọ pé: “Olúwa mi, ṣàforíjìn, kí O sì kẹ́ (mi). Ìwọ sì l’óore jùlọ nínú àwọn aláàánú.”1 ');
INSERT INTO "aykyuni" VALUES(24,1,'24:1','(Èyí ni) Sūrah kan tí A sọ̀kalẹ̀. A ṣe ìdájọ́ inú rẹ̀ ní òfin (dandan). A sì sọ àwọn āyah tó yanjú kalẹ̀ sínú rẹ̀ nítorí kí ẹ lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(24,2,'24:2','Onísìná lóbìnrin àti onísìná lọ́kùnrin, ẹ na ẹnì kọ̀ọ̀kan nínú àwọn méjèèjì ní ọgọ́rùn-ún kòbókò. Ẹ má ṣe jẹ́ kí àánú wọn ṣe yín nípa ìdájọ́ Allāhu tí ẹ bá ní ìgbàgbọ́ òdodo nínú Allāhu àti Ọjọ́ Ìkẹ́yìn. Kí igun kan nínú àwọn onígbàgbọ́ òdodo sì jẹ́rìí sí ìyà àwọn méjèèjì.1 ');
INSERT INTO "aykyuni" VALUES(24,3,'24:3','Onísìná lọ́kùnrin kò níí ṣe sìná pẹ̀lú ẹnì kan bí kò ṣe onísìná lóbìnrin (ẹgbẹ́ rẹ̀) tàbí ọ̀ṣẹbọ lóbìnrin. Onísìná lóbìnrin, ẹnì kan kò níí bá a ṣe sìná bí kò ṣe onísìná lọ́kùnrin (ẹgbẹ́ rẹ̀) tàbí ọ̀ṣẹbọ lọ́kùnrin.A sì ṣe ìyẹn (sìná ṣíṣe) ní èèwọ̀ fún àwọn onígbàgbọ́ òdodo.2 ');
INSERT INTO "aykyuni" VALUES(24,4,'24:4','Àwọn tó ń fi ẹ̀sùn sìná kan àwọn ọmọlúàbí lóbìnrin, lẹ́yìn náà tí wọn kò mú àwọn ẹlẹ́rìí mẹ́rin wá, ẹ nà wọ́n ní ọgọ́rin kòbókò. Ẹ má ṣe gba ẹ̀rí wọn mọ́ láéláé; àwọn wọ̀nyẹn sì ni òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(24,5,'24:5','Àyàfi àwọn tó ronú pìwàdà lẹ́yìn ìyẹn, tí wọ́n sì ṣe àtúnṣe. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.1 ');
INSERT INTO "aykyuni" VALUES(24,6,'24:6','Àwọn tó ń fi ẹ̀sùn sìná kan àwọn ìyàwó wọn, tí kò sì sí ẹlẹ́rìí fún wọn àfi àwọn fúnra wọn, ẹ̀rí ẹnì kọ̀ọ̀kan wọn ni kí ó fi Allāhu jẹ́rìí ní ẹ̀ẹ̀ mẹrin pé dájúdájú òun wà nínú àwọn olódodo.');
INSERT INTO "aykyuni" VALUES(24,7,'24:7','Ẹ̀ẹ̀ karùn-ún sì ni pé kí ibi dandan Allāhu máa bá òun, tí òun bá wà nínú àwọn òpùrọ́.');
INSERT INTO "aykyuni" VALUES(24,8,'24:8','Ohun tí ó máa yẹ ìyà fún ìyàwó ni pé, kí ó fi Allāhu jẹ́rìí ní ẹ̀ẹ̀ mẹrin pé dájúdájú ọkọ òun wà nínú àwọn òpùrọ́.');
INSERT INTO "aykyuni" VALUES(24,9,'24:9','Ẹ̀ẹ̀ karùn-ún sì ni pé kí ìbínú Allāhu máa bá òun, tí ọkọ òun bá wà nínú àwọn olódodo.1 ');
INSERT INTO "aykyuni" VALUES(24,10,'24:10','Tí kì í bá ṣe oore àjùlọ Allāhu àti ìkẹ́ Rẹ̀ lórí yín ni (Allāhu ìbá máa tú àṣírí yín ni). Dájúdájú Allāhu ni Olùgba-ìronúpìwàdà, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(24,11,'24:11','Dájúdájú àwọn tó mú àdápa irọ́ wá jẹ́ ìjọ kan nínú yín. Ẹ má ṣe lérò pé aburú ni fún yín. Rárá o, oore ni fún yín. Ẹnì kọ̀ọ̀kan nínú wọn ti ní ohun tí ó dá ní ẹ̀ṣẹ̀. Àti pé ẹni tí ó dá (ẹ̀ṣẹ̀) jùlọ nínú wọn, ìyà ńlá ni tirẹ̀.');
INSERT INTO "aykyuni" VALUES(24,12,'24:12','Nígbà tí ẹ gbọ́ ọ, kí ni kò jẹ́ kí àwọn onígbàgbọ́ òdodo lọ́kùnrin àti àwọn onígbàgbọ́ òdodo lóbìnrin ro dáadáa rora wọn, kí wọ́n sì sọ pé: “Èyí ni àdápa irọ́ pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(24,13,'24:13','Kí ni kò jẹ́ kí wọ́n mú ẹlẹ́rìí mẹ́rin wá lórí rẹ̀? Nítorí náà, nígbà tí wọn kò ti mú àwọn ẹlẹ́rìí wá, àwọn wọ̀nyẹn, àwọn ni òpùrọ́ lọ́dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(24,14,'24:14','Tí kì í bá ṣe ti oore àjùlọ Allāhu àti ìkẹ́ Rẹ̀ lórí yín ní ilé ayé àti ní ọ̀run ni, dájúdájú ìyà ńlá ìbá fọwọ́ bà yín nípa ohun tí ẹ̀ ń sọ kiri ní ìsọkúsọ.');
INSERT INTO "aykyuni" VALUES(24,15,'24:15','(Ẹ rántí) nígbà tí ẹ̀ ń gbà á (láààrin ara yín) pẹ̀lú ahọ́n yín, tí ẹ sì ń fi ẹnu yín sọ ohun tí ẹ kò nímọ̀ nípa rẹ̀; ẹ lérò pé n̄ǹkan tó fúyẹ́ ni, n̄ǹkan ńlá sì ni lọ́dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(24,16,'24:16','Nígbà tí ẹ gbọ́ ọ, kí ni kò jẹ́ kí ẹ sọ pé: “Kì í ṣe ẹ̀tọ́ fún wa pé kí á sọ èyí. Mímọ́ ni fún Ọ (Allāhu)! Èyí ni ìparọ́-mọ́ni tó tóbi.”');
INSERT INTO "aykyuni" VALUES(24,17,'24:17','Allāhu ń ṣe wáàsí fún yín pé kí ẹ má ṣe padà síbi irú rẹ̀ mọ́ láéláé tí ẹ bá jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(24,18,'24:18','Allāhu ń ṣàlàyé àwọn āyah náà fún yín. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(24,19,'24:19','Dájúdájú àwọn tó nífẹ̀ẹ́ sí kí (ọ̀rọ̀) ìbàjẹ́ máa tàn kálẹ̀ nípa àwọn tó gbàgbọ́ ní òdodo, ìyà ẹlẹ́ta-eléro ń bẹ fún wọn ní ayé àti ní ọ̀run. Allāhu nímọ̀, ẹ̀yin kò sì nímọ̀.');
INSERT INTO "aykyuni" VALUES(24,20,'24:20','Tí kì í bá ṣe oore àjùlọ Allāhu àti ìkẹ́ Rẹ̀ lórí yín ni (Allāhu ìbá tètè fìyà jẹ yín ni). Dájúdájú Allāhu ni Aláàánú, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(24,21,'24:21','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe tẹ̀lé àwọn ojú-ẹsẹ̀ Èṣù. Ẹnikẹ́ni tí ó bá sì tẹ̀lé àwọn ojú-ẹsẹ̀ Èṣù, dájúdájú (Èṣù) yóò pàṣẹ ìwà ìbàjẹ́ àti aburú (fún un). Tí kì í bá ṣe oore àjùlọ Allāhu àti ìkẹ́ Rẹ̀ lórí yín ni, ẹnì kan kan ìbá tí mọ́ nínú yín láéláé. Ṣùgbọ́n Allāhu ń ṣàfọ̀mọ́ ẹni tí Ó bá fẹ́. Allāhu sì ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(24,22,'24:22','Kí àwọn tó ní ọlá àti ọrọ̀ púpọ̀ nínú yín má ṣe búra pé àwọn kò níí ṣe dáadáa sí àwọn ìbátan, àwọn mẹ̀kúnnù àti àwọn tó gbé ìlú wọn jù sílẹ̀ nítorí ẹ̀sìn Allāhu. Kí wọ́n ṣàforíjìn, kí wọ́n sì ṣàmójúkúrò. Ṣé ẹ kò fẹ́ kí Allāhu foríjìn yín ni? Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(24,23,'24:23','Dájúdájú àwọn tó ń parọ́ sìná mọ́ àwọn ọmọlúàbí lóbìnrin, àwọn tí sìná kò sí lórí ọkàn wọn, àwọn onígbàgbọ́ òdodo lóbìnrin, A ti ṣẹ́bilé wọn ní ayé àti ní ọ̀run. Ìyà ńlá sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(24,24,'24:24','Ní ọjọ́ tí àwọn ahọ́n wọn, ọwọ́ wọn àti ẹsẹ̀ wọn yóò máa jẹ́rìí takò wọ́n nípa ohun tí wọ́n ń ṣe níṣẹ́,');
INSERT INTO "aykyuni" VALUES(24,25,'24:25','ní ọjọ́ yẹn ni Allāhu yóò san wọ́n ní ẹ̀san wọn tí ó tọ́ sí wọn. Wọn yó sì mọ̀ pé dájúdájú Allāhu, Òun ni Òdodo pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(24,26,'24:26','Àwọn obìnrin burúkú wà fún àwọn ọkùnrin burúkú. Àwọn ọkùnrin burúkú sì wà fún àwọn obìnrin burúkú. Àwọn obìnrin rere wà fún àwọn ọkùnrin rere. Àwọn ọkùnrin rere sì wà fún àwọn obìnrin rere.Àwọn (ẹni rere) wọ̀nyí mọ́wọ́-mọ́sẹ̀ nínú (àìdaa) tí wọ́n ń sọ sí wọn. Àforíjìn àti èsè alápọ̀n-ọ́nlé ń bẹ fún wọn. ');
INSERT INTO "aykyuni" VALUES(24,27,'24:27','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe wọ àwọn ilé kan yàtọ̀ sí àwọn ilé yín títí ẹ máa fi tọrọ ìyọ̀ǹda àti (títí) ẹ máa fi sálámọ̀ sí àwọn ará inú ilé náà. Ìyẹn lóore jùlọ fún yín nítorí kí ẹ lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(24,28,'24:28','Tí ẹ ò bá sì bá ẹnì kan kan nínú ilé náà, ẹ má ṣe wọ inú rẹ̀ títí wọn yóò fi yọ̀ǹda fún yín. Tí wọ́n bá sì sọ fún yín pé kí ẹ padà, nítorí náà ẹ padà. Òhun ló fọ̀ yín mọ́ jùlọ. Allāhu sì ni Onímọ̀ nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(24,29,'24:29','Kò sí ẹ̀ṣẹ̀ fún yín láti wọ inú àwọn ilé kan tí kì í ṣe ibùgbé, tí àǹfààní wà nínú rẹ̀ fún yín Allāhu mọ ohun tí ẹ̀ ń ṣàfi hàn rẹ̀ àti ohun tí ẹ̀ ń fi pamọ́. ');
INSERT INTO "aykyuni" VALUES(24,30,'24:30','Sọ fún àwọn onígbàgbọ́ òdodo lọ́kùnrin pé kí wọ́n rẹ ojú wọn nílẹ̀, kí wọ́n sì ṣọ́ abẹ́ wọn. Ìyẹn fọ̀ wọ́n mọ́ jùlọ. Dájúdájú Allāhu ni Alámọ̀tán nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(24,31,'24:31','Sọ fún àwọn onígbàgbọ́ òdodo lóbìnrin pé kí wọ́n rẹ ojú wọn nílẹ̀, kí wọ́n sì ṣọ́ abẹ́ wọn. Wọn kò gbọ́dọ̀ ṣàfi hàn ọ̀ṣọ́ wọn àfi èyí tí ó bá hàn nínú rẹ̀ Kí wọ́n fi ìbòrí wọn bo igbá-àyà wọn.2 Àti pé wọn kò gbọ́dọ̀ ṣàfi hàn ọ̀ṣọ́ wọn àfi fún àwọn ọkọ wọn tàbí àwọn bàbá wọn tàbí àwọn bàbá ọkọ wọn tàbí àwọn ọmọkùnrin wọn tàbí àwọn ọmọkùnrin ọkọ wọn tàbí àwọn arákùnrin wọn tàbí àwọn ọmọkùnrin arákùnrin wọn tàbí àwọn ọmọkùnrin arábìnrin wọn tàbí àwọn obìnrin (ẹgbẹ́) wọn tàbí àwọn ẹrúkùnrin wọn tàbí àwọn tó ń tẹ̀lé obìnrin fún iṣẹ́ rírán, tí wọ́n jẹ́ ọkùnrin akúra tàbí àwọn ọmọdé tí kò dá ìhòhò àwọn obìnrin mọ̀.3 Wọn kò gbọ́dọ̀ fi ẹsẹ̀ wọn rin ìrin-kokokà nítorí kí àwọn (ènìyàn) lè mọ ohun tí wọ́n fi pamọ́ (sára) nínú ọ̀ṣọ́ wọn. Kí gbogbo yín sì ronú pìwàdà sọ́dọ̀ Allāhu, ẹ̀yin onígbàgbọ́ òdodo nítorí kí ẹ lè jèrè.4 ');
INSERT INTO "aykyuni" VALUES(24,32,'24:32','Ẹ so yìgì fún àwọn ọkùnrin tí kò ní aya àti àwọn obìnrin tí kò ní ọkọ láààrin yín, (ẹ tún so yìgì) fún àwọn ẹni rere nínú àwọn ẹrúkùnrin yín àti àwọn ẹrúbìnrin yín. Tí wọ́n bá jẹ́ aláìní, Allāhu yóò rọ̀ wọ́n lọ́rọ̀ nínú ọlá Rẹ̀. Allāhu sì ni Olùgbààye, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(24,33,'24:33','Kí àwọn tí kò ní (ìkápá) ìgbéyàwó mú ojú wọn kúrò níbi ìṣekúṣe títí Allāhu yóò fi rọ̀ wọ́n lọ́rọ̀ nínú ọlá Rẹ̀.Àwọn tó ń wá ìwé òmìnira nínú àwọn ẹrú yín, ẹ kọ ìwé òmìnira fún wọn tí ẹ bá mọ ohun rere nípa wọn. Kí ẹ sì fún wọn nínú dúkìá Allāhu tí Ó fún yín. (Nítorí dúkìá ayé tí ẹ̀ ń wá), ẹ má ṣe jẹ àwọn ẹrúbìnrin yín nípá láti lọ ṣe sìná, tí wọ́n bá fẹ́ ṣọ́ abẹ́ wọn níbi ìṣekúṣe.2 Ẹnikẹ́ni tí ó bá jẹ wọ́n nípá, dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run lẹ́yìn tí ẹ ti jẹ wọ́n nípá.3 ');
INSERT INTO "aykyuni" VALUES(24,34,'24:34','Dájúdájú A ti sọ àwọn àmì tó yanjú, ìtàn àwọn tó ti lọ ṣíwájú yín àti wáàsí fún àwọn olùbẹ̀rù (Allāhu) kalẹ̀ fún yín.');
INSERT INTO "aykyuni" VALUES(24,35,'24:35','Allāhu ni Olùtan-ìmọ́lẹ̀ sínú àwọn sánmọ̀ àti ilẹ̀. Àpèjúwe ìmọ́lẹ̀ Rẹ̀ dà bí òpó àtùpà kan tí àtùpà wà nínú rẹ̀. Àtùpà náà sì wà nínú díńgí. Díńgí náà dà bí ìràwọ̀ kan tó ń tàn yànrànyànràn. Wọ́n ń tan (ìmọ́lẹ̀ náà) láti ara igi ìbùkún kan, igi zaetūn, tí kò gba ìmọ́lẹ̀ ìlà òòrùn àti ìwọ̀ òòrùn (nìkan mọ).Epo rẹ̀ fẹ́rẹ̀ tànmọ́lẹ̀, iná ìbáà má dé ibẹ̀. Ìmọ́lẹ̀ lórí ìmọ́lẹ̀ ni.2 Allāhu ń tọ́ ẹnikẹ́ni tí Ó bá fẹ́ sí (ọ̀nà) ìmọ́lẹ̀ Rẹ̀. Allāhu ń fi àwọn àkàwé lélẹ̀ fún àwọn ènìyàn. Allāhu sì ni Onímọ̀ nípa gbogbo n̄ǹkan. ');
INSERT INTO "aykyuni" VALUES(24,36,'24:36','(Àwọn àtùpà ìbùkún náà wà) nínú àwọn ilé kan (ìyẹn àwọn mọ́sálásí) èyí tí Allāhu yọ̀ǹda pé kí wọ́n gbéga,kí wọ́n sì máa dárúkọ Rẹ̀ nínú rẹ̀. (Àwọn ènìyàn) yó sì máa ṣàfọ̀mọ́ fún Un nínú rẹ̀ ní àárọ̀ àti ní ìrọ̀lẹ́;2 ');
INSERT INTO "aykyuni" VALUES(24,37,'24:37','Àwọn ọkùnrin tí òwò àti kárà-kátà kò dí lọ́wọ́ níbi ìrántí Allāhu, ìrun kíkí àti Zakāh yíyọ, tí wọ́n ń páyà ọjọ́ kan tí àwọn ọkàn àti ojú yóò máa yí sí ọ̀tún yí sí òsì.');
INSERT INTO "aykyuni" VALUES(24,38,'24:38','(Wọ́n ṣe rere wọ̀nyí) nítorí kí Allāhu lè san wọ́n ní ẹ̀san rere tó dára ju ohun tí wọ́n ṣe níṣẹ́ àti nítorí kí Ó lè ṣe àlékún fún wọn nínú oore àjùlọ Rẹ̀. Àti pé Allāhu ń pèsè ìjẹ-ìmu àti ìgbádùn fún ẹni tí Ó bá fẹ́ láì níí ní ìṣírò.1 ');
INSERT INTO "aykyuni" VALUES(24,39,'24:39','Àwọn tó ṣàì gbàgbọ́, àwọn iṣẹ́ wọn dà bí ahúnpeéná tó wà ní pápá, tí ẹni tí òǹgbẹ ń gbẹ sì lérò pé omi ni títí di ìgbà tí ó dé síbẹ̀, kò sì bá n̄ǹkan kan níbẹ̀. Ó sì bá Allāhu níbi (iṣẹ́) rẹ̀ (ní ọ̀run). (Allāhu) sì ṣe àṣepé ìṣírò-iṣẹ́ rẹ̀ fún un. Allāhu ni Olùyára níbi ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(24,40,'24:40','Tàbí (iṣẹ́ àwọn aláìgbàgbọ́) dà bí àwọn òkùnkùn kan nínú ibúdò jíjìn, tí ìgbì omi ń bò ó mọ́lẹ̀, tí ìgbì omi mìíràn tún wà ní òkè rẹ̀, tí ẹ̀ṣújò sì wà ní òkè rẹ̀; àwọn òkùnkùn biribiri tí apá kan wọ́n wà lórí apá kan (nìyí). Nígbà tí ó bá nawọ́ ara rẹ̀ jáde, kò ní fẹ́ẹ̀ rí i. Ẹnikẹ́ni tí Allāhu kò bá fún ní ìmọ́lẹ̀, kò lè sí ìmọ́lẹ̀ kan fún un.');
INSERT INTO "aykyuni" VALUES(24,41,'24:41','Ṣé o ò rí i pé dájúdájú Allāhu ni gbogbo ẹni tó ń bẹ nínú àwọn sánmọ̀ àti lórí ilẹ̀ ń ṣe àfọ̀mọ́ fún? Àwọn ẹyẹ náà (ń ṣe bẹ́ẹ̀) nígbà tí wọ́n bá ń na ìyẹ́ apá wọn? Ìkọ̀ọ̀kan wọn kúkú ti mọ bí ó ṣe máa kírun rẹ̀ àti bí ó ṣe máa ṣe àfọ̀mọ́ rẹ̀ (fún Un). Allāhu sì ni Onímọ̀ nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(24,42,'24:42','Ti Allāhu ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Ọ̀dọ̀ Allāhu sì ni àbọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(24,43,'24:43','Ṣé ìwọ kò wòye pé dájúdájú Allāhu ń da ẹ̀ṣújò káàkiri ni? Lẹ́yìn náà Ó ń kó o jọ mọ́ra wọn. Lẹ́yìn náà, Ó ń gbé wọn gun ara wọn, nígbà náà ni o máa rí òjò tí ó máa jáde láti ààrin rẹ̀. Láti inú sánmọ̀, Ó sì ń sọ àwọn yìyín kan (tó dà bí) àwọn àpáta kalẹ̀ sí orí ilẹ̀ ayé. Ó ń mú un kọlu ẹni tí Ó bá fẹ́. Ó sì ń ṣẹ́rí rẹ̀ kúrò fún ẹni tí Ó bá fẹ́. Kíkọ yànrànyànràn mọ̀nàmọ́ná inú ẹ̀ṣújò sì fẹ́ẹ̀ lè fọ́ àwọn ojú.');
INSERT INTO "aykyuni" VALUES(24,44,'24:44','Allāhu ń yí òru àti ọ̀sán padà (ìkíní kejì n wá ní tẹ̀léǹtẹ̀lé pẹ̀lú àlékún àti àdínkù). Dájúdájú àríwòye wà nínú ìyẹn fún àwọn tó ní ojú ìríran.');
INSERT INTO "aykyuni" VALUES(24,45,'24:45','Allāhu ṣẹ̀dá gbogbo abẹ̀mí láti inú omi. Ó wà nínú wọn, èyí tó ń fi àyà rẹ̀ wọ́. Ó wà nínú wọn, èyí tó ń fi ẹsẹ̀ méjì rìn. Ó sì wà nínú wọn, èyí tó ń fi mẹ́rin rìn. Allāhu ń ṣẹ̀dá ohun tí Ó bá fẹ́. Dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(24,46,'24:46','Dájúdájú A ti sọ àwọn āyah tó ń yanjú ọ̀rọ̀ kalẹ̀. Allāhu sì ń tọ́ ẹnikẹ́ni tí Ó bá fẹ́ sí ọ̀nà tààrà (’Islām).');
INSERT INTO "aykyuni" VALUES(24,47,'24:47','Wọ́n ń wí pé: “A gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀. A sì tẹ̀lé (àṣẹ Wọn).” Lẹ́yìn náà, igun kan nínú wọn ń pẹ̀yìndà lẹ́yìn ìyẹn. Àwọn wọ̀nyẹn kì í ṣe onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(24,48,'24:48','Àti pé nígbà tí wọ́n bá pè wọ́n sí ti Allāhu àti ti Òjíṣẹ́ Rẹ̀ nítorí kí ó lè ṣèdájọ́ láààrin wọn, nígbà náà ni igun kan nínú wọn yóò máa gbúnrí.');
INSERT INTO "aykyuni" VALUES(24,49,'24:49','Tí ó bá sì jẹ́ pé àwọn ni wọ́n ni ẹ̀tọ́ (àre) ni, wọn yóò wá bá ọ ní tọkàn-tara.');
INSERT INTO "aykyuni" VALUES(24,50,'24:50','Ṣé àrùn wà nínú ọkàn wọn ni tàbí wọ́n ṣeyèméjì? Tàbí wọ́n ń páyà pé Allāhu àti Òjíṣẹ́ Rẹ̀ yóò fi ẹjọ́ wọn ṣègbè ni? Rárá o. Àwọn wọ̀nyẹn, àwọn ni alábòsí.');
INSERT INTO "aykyuni" VALUES(24,51,'24:51','Ohun tí ó máa jẹ́ ọ̀rọ̀ àwọn onígbàgbọ́ òdodo nígbà tí wọ́n bá pè wọ́n sí ti Allāhu àti ti Òjíṣẹ́ Rẹ̀ nítorí kí ó lè ṣèdájọ́ láààrin wọn ni pé, wọ́n á sọ pé: “A gbọ́ (àṣẹ), a sì tẹ̀lé (àṣẹ).” Àwọn wọ̀nyẹn, àwọn ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(24,52,'24:52','Ẹnikẹ́ni tí ó bá tẹ̀lé ti Allāhu àti ti Òjíṣẹ́ Rẹ̀, tí ó ń páyà Allāhu, tí ó sì ń bẹ̀rù Rẹ̀, àwọn wọ̀nyẹn, àwọn ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(24,53,'24:53','Wọ́n sì fi Allāhu búra tí ìbura wọn sì ní agbára pé: “Dájúdájú tí o bá pàṣẹ fún àwọn, dájúdájú àwọn yóò jáde.” Sọ pé: “Ẹ má ṣe búra mọ́. Títẹ̀lé àṣẹ (pẹ̀lú ìbúra irọ́ ẹnu yín) ti di ohun mímọ̀ (fún wa).” Dájúdájú Allāhu ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(24,54,'24:54','Sọ pé: “Ẹ tẹ̀lé ti Allāhu. Kí ẹ sì tẹ̀lé ti Òjíṣẹ́ náà. Tí wọ́n bá pẹ̀yìndà, ohun tí A gbé kà á lọ́rùn ló ń bẹ lọ́rùn rẹ̀, ohun tí A sì gbé kà yín lọ́rùn ló ń bẹ lọ́rùn yín. Tí ẹ bá sì tẹ̀lé e, ẹ ti mọ̀nà tààrà. Kò sì sí ojúṣe kan fún Òjíṣẹ́ bí kò ṣe ìkéde (ẹ̀sìn) tó yanjú.”');
INSERT INTO "aykyuni" VALUES(24,55,'24:55','Allāhu ṣàdéhùn fún àwọn tó gbàgbọ́ ní òdodo nínú yín, tí wọ́n sì ṣe àwọn iṣẹ́ rere pé dájúdájú Ó máa fi wọ́n ṣe àrólé lórí ilẹ̀ gẹ́gẹ́ bí Ó ṣe fi àwọn tó ṣíwájú wọn ṣe àrólé.Dájúdájú Ó máa fi àyè gba ẹ̀sìn wọn fún wọn, èyí tí Ó yọ́nú sí fún wọn. Lẹ́yìn ìbẹ̀rù wọn, dájúdájú Ó máa fi ìfàyàbalẹ̀ dípò rẹ̀ fún wọn. Wọ́n ń jọ́sìn fún Mi, wọn kò sì fi n̄ǹkan kan ṣẹbọ sí Mi. Ẹnikẹ́ni tí ó bá sì ṣàì moore lẹ́yìn ìyẹn, àwọn wọ̀nyẹn, àwọn ni òbìlẹ̀jẹ́. ');
INSERT INTO "aykyuni" VALUES(24,56,'24:56','Ẹ kírun. Ẹ yọ Zakāh. Ẹ tẹ̀lé Òjíṣẹ́ náà nítorí kí A lè kẹ yín.');
INSERT INTO "aykyuni" VALUES(24,57,'24:57','Má ṣe lérò pé àwọn tó ṣàì gbàgbọ́ mórí bọ́ nínú ìyà Allāhu lórí ilẹ̀. Iná ni ibùgbé wọn. Ìkángun náà sì burú.');
INSERT INTO "aykyuni" VALUES(24,58,'24:58','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, kí àwọn ẹrú yín àti àwọn tí kò tí ì bàlágà nínú yín máa gba ìyọ̀ǹda lọ́dọ̀ yín nígbà mẹ́ta (wọ̀nyí): ṣíwájú ìrun Subh, nígbà tí ẹ bá ń bọ́ aṣọ yín sílẹ̀ fún òòrùn ọ̀sán àti lẹ́yìn ìrun alẹ́. (Ìgbà) mẹ́ta fún ìbọ́rasílẹ̀ yín (nìyí). Kò sí ẹ̀ṣẹ̀ fún ẹ̀yin àti àwọn lẹ́yìn (àsìkò) náà pé kí wọ́n wọlé tọ̀ yín; kí apá kan yín wọlé tọ apá kan. Báyẹn ni Allāhu ṣe ń ṣàlàyé àwọn āyah náà fún yín. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(24,59,'24:59','Nígbà tí àwọn ọmọdé yín bá sì bàlágà kí àwọn náà máa gba ìyọ̀ǹda gẹ́gẹ́ bí àwọn tó ṣíwájú wọn ṣe gba ìyọ̀ǹda. Báyẹn ni Allāhu ṣe ń ṣàlàyé àwọn āyah Rẹ̀ fún yín. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(24,60,'24:60','Àwọn tó ti dàgbà kọjá ọmọ bíbí nínú àwọn obìnrin, àwọn tí kò retí ìbálòpò oorun ìfẹ́ mọ́, kò sí ẹ̀ṣẹ̀ fún wọn láti bọ́ àwọn aṣọ jilbāb wọn sílẹ̀, tí wọn kò sì níí ṣe àfihàn ọ̀ṣọ́ kan síta. Kí wọ́n kóra wọn ní ìjánu (kí wọ́n máa wọ aṣọ jilbāb wọn lọ bẹ́ẹ̀) lóore jùlọ fún wọn. Allāhu sì ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(24,61,'24:61','Kò sí ẹ̀ṣẹ̀ fún afọ́jú, kò sí ẹ̀ṣẹ̀ fún arọ, kò sí ẹ̀ṣẹ̀ fún aláìsàn, kò sì sí ẹ̀ṣẹ̀ fún ẹ̀yin náà láti jẹun nínú ilé yín, tàbí ilé àwọn bàbá yín, tàbí ilé àwọn ìyá yín, tàbí ilé àwọn arákùnrin yín, tàbí ilé àwọn arábìnrin yín, tàbí ilé àwọn arákùnrin bàbá yín, tàbí ilé àwọn arábìnrin bàbá yín, tàbí ilé àwọn arákùnrin ìyá yín, tàbí ilé àwọn arábìnrin ìyá yín, tàbí (ilé) tí ẹ ní ìkápá lórí àwọn kọ́kọ́rọ́ rẹ̀, tàbí (ilé) ọ̀rẹ́ yín. Kò sí ẹ̀ṣẹ̀ fún yín láti jẹun papọ̀ tàbí ní ọ̀tọ̀ọ̀tọ̀. Nítorí náà, tí ẹ bá wọ àwọn inú ilé kan, ẹ sálámọ̀ síra yín. (Èyí jẹ́) ìkíni ìbùkún tó dára láti ọ̀dọ̀ Allāhu. Báyẹn ni Allāhu ṣe ń ṣàlàyé àwọn āyah náà fún yín nítorí kí ẹ lè ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(24,62,'24:62','Àwọn onígbàgbọ́ ni àwọn tó gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀. Nígbà tí wọ́n bá sì wà pẹ̀lú rẹ̀ lórí ọ̀rọ̀ kan tí ó kan gbogbogbò, wọn kò níí lọ títí wọn yóò fi gba ìyọ̀ǹda lọ́dọ̀ rẹ̀. Dájúdájú àwọn tó ń gba ìyọ̀ǹda lọ́dọ̀ rẹ, àwọn wọ̀nyẹn ni àwọn tó gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀. Nítorí náà, tí wọ́n bá gba ìyọ̀ǹda lọ́dọ̀ rẹ fún apá kan ọ̀rọ̀ ara wọn, fún ẹni tí o bá fẹ́ ní ìyọ̀ǹda nínú wọn, kí o sì bá wọn tọrọ àforíjìn lọ́dọ̀ Allāhu. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(24,63,'24:63','Ẹ má ṣe ládìsọ́kàn pé ìpè Òjíṣẹ́láààrin yín dà bí ìpè apá kan fún apá kan. Allāhu kúkú ti mọ àwọn tó ń yọ́ sá lọ nínú yín. Nítorí náà, kí àwọn tó ń yapa àṣẹ rẹ̀ ṣọ́ra nítorí kí ìdààmú má baà dé bá wọn, tàbí nítorí kí ìyà ẹlẹ́ta-eléro má baá jẹ wọ́n. ');
INSERT INTO "aykyuni" VALUES(24,64,'24:64','Gbọ́! Dájúdájú ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀. Ó sì ti mọ ohun tí ẹ wà lórí rẹ̀. (Rántí) ọjọ́ tí wọn yó dá wọn padà sí ọ̀dọ̀ Rẹ̀, Ó sì máa fún wọn ní ìró ohun tí wọ́n ṣe níṣẹ́. Allāhu sì ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(25,1,'25:1','Ìbùkún ni fún Ẹni tí Ó sọ ọ̀rọ̀-ìpínyà (ohun tó ń ṣòpínyà láààrin òdodo àti irọ́) kalẹ̀ fún ẹrúsìn Rẹ̀ nítorí kí ó lè jẹ́ olùkìlọ̀ fún gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(25,2,'25:2','(Òun ni) Ẹni tí Ó ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Kò mú ẹnì kan kan ní ọmọ. Kò sí akẹgbẹ́ fún Un nínú ìjọba (Rẹ̀). Ó dá gbogbo n̄ǹkan. Ó sì yan òdíwọ̀n (ìrísí, ìṣẹ̀mí àti àyànmọ́) fún un níwọ̀n-níwọ̀n.');
INSERT INTO "aykyuni" VALUES(25,3,'25:3','(Àwọn aláìgbàgbọ́) sọ àwọn kan di ọlọ́hun lẹ́yìn Allāhu, àwọn tí kò lè dá n̄ǹkan kan, A sì dá wọn ni. Wọn kò sì ní ìkápá ìnira tàbí àǹfààní kan fún ẹ̀mí ara wọn. Àti pé wọn kò ní ìkápá lórí ikú, ìṣẹ̀mí àti àjíǹde.');
INSERT INTO "aykyuni" VALUES(25,4,'25:4','Àwọn tó ṣàì gbàgbọ́ wí pé: “Kí ni èyí bí kò ṣe àdápa irọ́ kan tí ó dá àdápa rẹ̀ (mọ́ Allāhu), tí àwọn ènìyàn mìíràn sì ràn án lọ́wọ́ lórí rẹ̀.” Dájúdájú (àwọn aláìgbàgbọ́) ti gbé àbòsí àti irọ́ dé.');
INSERT INTO "aykyuni" VALUES(25,5,'25:5','Wọ́n tún wí pé: “Àkọsílẹ̀ àlọ́ àwọn ẹni àkọ́kọ́, tí ó ṣàdàkọ rẹ̀ ni. Òhun ni wọ́n ń pè fún un ní òwúrọ̀ àti ní ìrọ̀lẹ́.”');
INSERT INTO "aykyuni" VALUES(25,6,'25:6','Sọ pé: “Ẹni tí Ó mọ ìkọ̀kọ̀ tí ń bẹ nínú àwọn sánmọ̀ àti ilẹ̀ l’Ó sọ̀ ọ́ kalẹ̀. Dájúdájú Òun ni Ó ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run.”');
INSERT INTO "aykyuni" VALUES(25,7,'25:7','Wọ́n tún wí pé: “Kí ló mú Òjíṣẹ́ yìí, tó ń jẹun, tó ń rìn nínú àwọn ọjà? Nítorí kí ni Wọn kò ṣe sọ mọlāika kan kalẹ̀ fún un kí ó lè jẹ́ olùkìlọ̀ pẹ̀lú rẹ̀?');
INSERT INTO "aykyuni" VALUES(25,8,'25:8','Tàbí (nítorí kí ni) wọn kò ṣe ju àpótí-ọrọ̀ kan sọ́dọ̀ rẹ̀, tàbí kí ó ní ọgbà oko kan tí ó ma máa jẹ nínú rẹ̀?” Àwọn alábòsí sì tún wí pé: “Ta ni ẹ̀ ń tẹ̀lé bí kò ṣe ọkùnrin eléèdì kan.”');
INSERT INTO "aykyuni" VALUES(25,9,'25:9','Wo bí wọ́n ṣe fún ọ ní àwọn àfiwé (burúkú)! Nítorí náà, wọ́n ti ṣìnà; wọn kò sì lè mọ̀nà.');
INSERT INTO "aykyuni" VALUES(25,10,'25:10','Ìbùkún ni fún Ẹni tí (ó jẹ́ pé) bí Ó bá fẹ́, Ó máa ṣoore tó dára ju ìyẹn fún ọ. (Ó sì máa jẹ́) àwọn ọgbà tí àwọn odò yóò máa ṣàn ní ìsàlẹ̀ rẹ̀. Ó sì máa fún ọ ni àwọn ààfin kan.');
INSERT INTO "aykyuni" VALUES(25,11,'25:11','Ńṣe ni wọ́n pe Àkókò náà nírọ́. A sì ti pèsè Iná tó ń jo sílẹ̀ de ẹnikẹ́ni tí ó bá pe Àkókò náà nírọ́.');
INSERT INTO "aykyuni" VALUES(25,12,'25:12','Nígbà tí (Iná náà) bá rí wọn láti àyè kan tí ó jìnnà, wọn yó sì máa gbọ́ ohùn ìbínú àti kíkùn (rẹ̀).');
INSERT INTO "aykyuni" VALUES(25,13,'25:13','Nígbà tí wọ́n bá sì jù wọ́n sí àyè tó há gádígádí nínú (Iná), tí wọ́n de ọwọ́ wọn mọ́ wọn lọ́rùn, wọn yó sì máa kígbe ìparun níbẹ̀.');
INSERT INTO "aykyuni" VALUES(25,14,'25:14','(A ó sọ pé): “Ẹ má ṣe kígbe ìparun ẹyọ kan, ẹ kígbe ìparun lọ́pọ̀lọ́pọ̀.”');
INSERT INTO "aykyuni" VALUES(25,15,'25:15','Sọ pé: “Ǹjẹ́ ìyẹn ló lóore jùlọ ni tàbí Ọgbà Ìdẹ̀ra gbére, èyí tí A ṣe ní àdéhùn fún àwọn olùbẹ̀rù Allāhu. Ó sì jẹ́ ẹ̀san àti ìkángun rere fún wọn.');
INSERT INTO "aykyuni" VALUES(25,16,'25:16','Ohunkóhun tí wọ́n bá ń fẹ́ wà fún wọn nínú rẹ̀. Olùṣegbére ni wọ́n (nínú rẹ̀). (Èyí) jẹ́ àdéhùn tí wọ́n ti tọ̀rọ̀ lọ́dọ̀ Olúwa rẹ.');
INSERT INTO "aykyuni" VALUES(25,17,'25:17','Àti pé (rántí) ọjọ́ tí (Allāhu) yóò kó àwọn abọ̀rìṣà àti n̄ǹkan tí wọ́n ń jọ́sìn fún lẹ́yìn Allāhu jọ, (Allāhu) yó sì sọ pé: “Ṣé ẹ̀yin l’ẹ ṣi àwọn ẹrúsìn Mi wọ̀nyí lọ́nà ni tàbí àwọn ni wọ́n ṣìnà (fúnra wọn)?”1 ');
INSERT INTO "aykyuni" VALUES(25,18,'25:18','Wọ́n wí pé: “Mímọ́ ni fún Ọ! Kò tọ́ fún wa láti mú àwọn kan ní alátìlẹ́yìn lẹ́yìn Rẹ. Ṣùgbọ́n Ìwọ l’O fún àwọn àti àwọn bàbá wọn ní ìgbádùn, títí wọ́n fi gbàgbé Ìrántí. Wọ́n sì jẹ́ ẹni ìparun.”');
INSERT INTO "aykyuni" VALUES(25,19,'25:19','(Allāhu sọ pé): “Dájúdájú àwọn òrìṣà ti pe ẹ̀yin abọ̀rìṣà ní òpùrọ́ nípa ohun tí ẹ̀ ń sọ (pé olùṣìpẹ̀ ni wọ́n). Ní báyìí wọn kò lè gbé ìyà Iná kúrò fún yín, wọn kò sì lè ràn yín lọ́wọ́. Ẹnikẹ́ni tí ó ṣe àbòsí (ẹbọ ṣíṣe) nínú yín, A sì máa fún un ní ìyà tó tóbi tọ́ wò.”');
INSERT INTO "aykyuni" VALUES(25,20,'25:20','A kò rán àwọn Òjíṣẹ́ níṣẹ́ rí ṣíwájú rẹ àfi kí wọ́n jẹun, kí wọ́n sì rìn nínú ọjà. A ti fi apá kan yín ṣe àdánwò fún apá kan, ǹjẹ́ ẹ máa ṣe sùúrù bí? Olúwa Rẹ sì ń jẹ́ Olùríran.');
INSERT INTO "aykyuni" VALUES(25,21,'25:21','Àwọn tí kò retí ìpàdé Wa (ní ọ̀run) wí pé: “Wọn kò ṣe sọ àwọn mọlāika kalẹ̀ fún wa tàbí kí á rí Olúwa wa (ní ojúkojú)? Dájúdájú wọ́n ti ṣègbéraga nínú ẹ̀mí wọn. Wọ́n sì ti tayọ ẹnu-ààlà ní ìtayọ ẹnu-ààlà tó tóbi.');
INSERT INTO "aykyuni" VALUES(25,22,'25:22','(Rántí) ọjọ́ tí wọ́n yóò rí àwọn mọlāika, kò níí sí ìró ìdùnnú fún àwọn ẹlẹ́ṣẹ̀ ní ọjọ́ yẹn. (Àwọn mọlāika) yó sì sọ pé: “Èèwọ̀, èèwọ̀ (ni ìró ìdùnnú fún ẹ̀yin ẹlẹ́ṣẹ̀).”');
INSERT INTO "aykyuni" VALUES(25,23,'25:23','Àti pé A máa wá ṣíbi ohun tí wọ́n ṣe níṣẹ́, A sì máa sọ ọ́ di eruku tí wọ́n fẹ́ dànù.');
INSERT INTO "aykyuni" VALUES(25,24,'25:24','Àwọn èrò inú Ọgbà Ìdẹ̀ra, ní ọjọ́ yẹn, (ọgbà wọn) máa lóore jùlọ ní ibùgbé. Ó sì máa dára jùlọ ní ibùsinmi.');
INSERT INTO "aykyuni" VALUES(25,25,'25:25','Àti pé (rántí) ọjọ́ tí sánmọ̀ yó fàya pẹ́rẹpẹ̀rẹ pẹ̀lú àwọn ẹ̀ṣújò funfun (tí yóò máa tú jáde nínú wọn).A sì máa sọ àwọn mọlāika kalẹ̀ tààrà. ');
INSERT INTO "aykyuni" VALUES(25,26,'25:26','Ìjọba òdodo ti ọjọ́ yẹn ń jẹ́ ti Àjọkẹ́-ayé. Ó sì jẹ́ ọjọ́ kan tí ó máa nira fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(25,27,'25:27','(Rántí) ọjọ́ tí alábòsí yóò jẹ ìka ọwọ́ rẹ̀ méjèèjì, ó sì máa wí pé: “Yéè! Èmi ìbá ti tọ ọ̀nà kan (náà) pẹ̀lú Òjíṣẹ́ náà.');
INSERT INTO "aykyuni" VALUES(25,28,'25:28','Ègbé mi o, yéè! Èmi ìbá tí mú lágbájá ní ọ̀rẹ́ àyò.');
INSERT INTO "aykyuni" VALUES(25,29,'25:29','Dájúdájú ó ti ṣì mí lọ́nà kúrò níbi Ìrántí lẹ́yìn tí ó dé bá mi. Dájúdájú Èṣù ń jẹ́ adáni-dáṣòro fún ọmọnìyàn.”');
INSERT INTO "aykyuni" VALUES(25,30,'25:30','Òjíṣẹ́ náà sọ pé: “Olúwa mi dájúdájú àwọn ènìyàn mi tí pa al-Ƙur’ān yìí tì.”');
INSERT INTO "aykyuni" VALUES(25,31,'25:31','Báyẹn ni A ṣe àwọn kan nínú àwọn ẹlẹ́ṣẹ̀ ní ọ̀tá fún Ànábì kọ̀ọ̀kan. Olúwa rẹ sì tó ní Afinimọ̀nà àti Alárànṣe.');
INSERT INTO "aykyuni" VALUES(25,32,'25:32','Àwọn tó ṣàì gbàgbọ́ wí pé: “Wọn kò ṣe sọ al-Ƙur’ān kalẹ̀ fún un ní àpapọ̀ ní ẹ̀ẹ̀ kan náà?” (A sọ̀ ọ́ kalẹ̀ díẹ̀díẹ̀) báyẹn nítorí kí A lè fi rinlẹ̀ sínú ọkàn rẹ. A sì ké e fún ọ díẹ̀díẹ̀.');
INSERT INTO "aykyuni" VALUES(25,33,'25:33','Wọn kò níí mú àpẹ̀ẹrẹ kan wá fún ọ (gẹ́gẹ́ bí ìbéèrè láti fi takò ọ́) àfi kí Á mú òdodo (ìyẹn, al-ƙur’ān) àti àlàyé tó dára jùlọ wá fún ọ (lórí rẹ̀).');
INSERT INTO "aykyuni" VALUES(25,34,'25:34','Àwọn tí A máa kó jọ lọ sínú iná Jahanamọ ní ìdojúbolẹ̀, àwọn wọ̀nyẹn ni àyè wọn burú jùlọ. Wọ́n sì ṣìnà jùlọ.');
INSERT INTO "aykyuni" VALUES(25,35,'25:35','Dájúdájú A fún (Ànábì) Mūsā ní Tírà. A tún ṣe arákùnrin rẹ̀, Hārūn, ní amúgbálẹ́gbẹ̀ẹ́ fún un.');
INSERT INTO "aykyuni" VALUES(25,36,'25:36','A sì sọ pé: “Ẹ̀yin méjèèjì, ẹ lọ sọ́dọ̀ ìjọ tó pe àwọn āyah Wa nírọ́. A sì pa ìjọ náà run pátápátá.');
INSERT INTO "aykyuni" VALUES(25,37,'25:37','(Rántí) ìjọ (Ànábì) Nūh. Nígbà tí wọ́n pe àwọn Òjíṣẹ́ ní òpùrọ́, A tẹ̀ wọ́n rì sínú omi. A sì ṣe wọ́n ní àmì kan fún àwọn ènìyàn. A sì ti pèsè ìyà ẹlẹ́ta-eléro sílẹ̀ de àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(25,38,'25:38','(Rántí) àwọn ìjọ ‘Ād, ìjọ Thamūd, ìjọ Rass àti àwọn ọ̀pọ̀lọ́pọ̀ ìran mìíràn (tó ń bẹ) láààrin wọn.');
INSERT INTO "aykyuni" VALUES(25,39,'25:39','Ìkọ̀ọ̀kan wọn ni A fún ní àwọn àpẹ̀ẹrẹ (gẹ́gẹ́ bí ẹ̀rí àti àlàyé ọ̀rọ̀). Ìkọ̀ọ̀kan wọn sì ni A parun pátápátá (torí àìgbàgbọ́ wọn).');
INSERT INTO "aykyuni" VALUES(25,40,'25:40','Àti pé dájúdájú àwọn náà kọjá ní ìlú tí A rọ òjò burúkú lé lórí. Ṣé wọn kì í rí i ni? Rárá, ńṣe ni wọn kò retí Àjíǹde.');
INSERT INTO "aykyuni" VALUES(25,41,'25:41','Nígbà tí wọ́n bá sì rí ọ, kò sí ohun tí wọn yóò fi ọ́ ṣe bí kò ṣe yẹ̀yẹ́. (Wọ́n á wí pé): “Ṣé èyí ni ẹni tí Allāhu gbé dìde ní Òjíṣẹ́!?');
INSERT INTO "aykyuni" VALUES(25,42,'25:42','Ó mà fẹ́ẹ̀ ṣẹ́rí wa kúrò níbi àwọn ọlọ́hun wa, tí kì í bá ṣe pé a takú sorí rẹ̀.” Láìpẹ́ wọn máa mọ ẹni tí ó ṣìnà jùlọ nígbà tí wọ́n bá rí ìyà.');
INSERT INTO "aykyuni" VALUES(25,43,'25:43','Sọ fún mi nípa ẹni tí ó sọ ìfẹ́-inú rẹ̀ di ọlọ́hun rẹ̀! Nítorí náà, ṣé ìwọ máa jẹ́ aláàbò fún un ni (níbi ìyà)?');
INSERT INTO "aykyuni" VALUES(25,44,'25:44','Tàbí ìwọ ń rò pé dájúdájú ọ̀pọ̀lọ́pọ̀ wọn ń gbọ́ràn tàbí pé wọ́n ń ṣe làákàyè? Kí ni wọ́n ná, bí kò ṣe bí àgùtàn. Rárá, wọ́n ṣìnà jùlọ.');
INSERT INTO "aykyuni" VALUES(25,45,'25:45','Ṣé o ò rí (iṣẹ́) Olúwa rẹ ni, bí Ó ṣe fẹ òkùnkùn òwúrọ̀ lójú (sójú sánmọ̀)? Tí Ó bá fẹ́ ni, ìbá dá a dúró síbẹ̀. Lẹ́yìn náà, A fi òòrùn ṣe atọ́ka sí bíbẹ òkùnkùn.');
INSERT INTO "aykyuni" VALUES(25,46,'25:46','Lẹ́yìn náà, A (fi ìmọ́lẹ̀ òòrùn) mú òkùnkùn (kúrò níta) wá sí ọ̀dọ̀ Wa ní mímú díẹ̀díẹ̀ (kí ojúmọ́ lè mọ́).');
INSERT INTO "aykyuni" VALUES(25,47,'25:47','Àti pé Òun ni Ẹni tí Ó ṣe òru ní ìbora fún yín. (Ó ṣe) oorun ní ìsinmi (fún yín). Ó tún ṣe ọ̀sán ní àsìkò ìtúsíta (fún wíwá ìjẹ-ìmu).');
INSERT INTO "aykyuni" VALUES(25,48,'25:48','Òun sì ni Ẹni t’Ó ń rán atẹ́gùn ní ìró-ìdùnnú ṣíwájú ìkẹ́ Rẹ̀. A sì sọ omi mímọ́ kalẹ̀ láti sánmọ̀');
INSERT INTO "aykyuni" VALUES(25,49,'25:49','nítorí kí A lè fi sọ òkú ilẹ̀ di àyè ilẹ̀ àti nítorí kí A lè fún ẹran-ọ̀sìn àti ọ̀pọ̀lọ́pọ̀ ènìyàn nínú àwọn tí A dá ní omi mu.');
INSERT INTO "aykyuni" VALUES(25,50,'25:50','Dájúdájú À ń darí omi yìí káàkiri láààrin wọnnítorí kí wọ́n lè rántí (ìkẹ́ Olúwa wọn). Ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ ènìyàn kọ̀ (láti rántí) àfi àìmoore. ');
INSERT INTO "aykyuni" VALUES(25,51,'25:51','Tí ó bá jẹ́ pé A bá fẹ́ ni, A ìbá gbé olùkìlọ̀ kan dìde nínú ìlú kọ̀ọ̀kan.1 ');
INSERT INTO "aykyuni" VALUES(25,52,'25:52','Nítorí náà, má ṣe tẹ̀lé àwọn aláìgbàgbọ́. Kí o sì fi (al-Ƙur’ān) jà wọ́n ní ogun tó tóbi.');
INSERT INTO "aykyuni" VALUES(25,53,'25:53','Òun ni Ẹni tí Ó mú àwọn odò méjì ṣàn kiri. Èyí (ni omi) tó dùn gan-an. Èyí sì (ni omi) iyọ̀ tó móró. Ó sì fi gàgá sí ààrin àwọn méjèèjì. (Ó sì) ṣe é ní èèwọ̀ pọ́nńbélé (fún wọn láti kó ìnira bá ẹ̀dá).');
INSERT INTO "aykyuni" VALUES(25,54,'25:54','Òun ni Ẹni tí Ó ṣẹ̀dá abara láti inú omi. Ó ṣe ìbátan ẹbí àti ìbátan àna fún un, Olúwa rẹ sì ń jẹ́ Alágbára.1 ');
INSERT INTO "aykyuni" VALUES(25,55,'25:55','Wọ́n ń jọ́sìn fún lẹ́yìn Allāhu ohun tí kò lè ṣe wọ́n ní àǹfààní, tí kò sì lè kó ìnira bá wọn. Aláìgbàgbọ́ jẹ́ olùrànlọ́wọ́ (fún Èṣù) láti tako Olúwa rẹ̀.');
INSERT INTO "aykyuni" VALUES(25,56,'25:56','A kò sì rán ọ níṣẹ́ tayọ kí o jẹ́ oníròó-ìdùnnú àti olùkìlọ̀.');
INSERT INTO "aykyuni" VALUES(25,57,'25:57','Sọ pé: “Èmi kò bèèrè owó-ọ̀yà kan lọ́wọ́ yín lórí rẹ̀ àfi ẹni tí ó bá fẹ́ mú ọ̀nà (dáadáa) tọ̀ lọ sí ọ̀dọ̀ Olúwa rẹ̀.”1 ');
INSERT INTO "aykyuni" VALUES(25,58,'25:58','Gbáralé Alààyè tí kò níí kú. Ṣe àfọ̀mọ́ pẹ̀lú ẹyìn fún Un.Allāhu sì tó ní Alámọ̀tán nípa ẹ̀ṣẹ̀ àwọn ẹrúsìn Rẹ̀. ');
INSERT INTO "aykyuni" VALUES(25,59,'25:59','(Òun ni) Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀, ilẹ̀ àti àwọn n̄ǹkan tó ń bẹ láààrin méjèèjì láààrin ọjọ́ mẹ́fà. Lẹ́yìn náà, Ó gúnwà sórí Ìtẹ́-ọlá. Àjọkẹ́-ayé ni, nítorí náà, bèèrè nípa Rẹ̀ (lọ́dọ̀ Rẹ̀ nítorí pé, Ó jẹ́) Alámọ̀tán.');
INSERT INTO "aykyuni" VALUES(25,60,'25:60','Nígbà tí wọ́n bá sọ fún wọn pé: “Ẹ forí kanlẹ̀ fún Àjọkẹ́-ayé.” Wọ́n á wí pé: “Kí ni Àjọkẹ-ayé? Ṣé kí á forí kanlẹ̀ fún ohun tí ò ń pa wá láṣẹ rẹ̀ ni?” (Ìpèpè náà) sì mú wọn lékún ní sísá-sẹ́yìn.');
INSERT INTO "aykyuni" VALUES(25,61,'25:61','Ìbùkún ni fún Ẹni tí Ó ṣe àwọn ibùsọ̀ ìràwọ̀ sínú sánmọ̀. Ó tún ṣe òòrùn àti òṣùpá ní ìmọ́lẹ̀ sínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(25,62,'25:62','Òun ni Ẹni tí Ó ṣe òru àti ọ̀sán ní ìtẹ̀léǹtẹ̀lé (tí ìkíní yàtọ̀ sí ìkejì) nítorí ẹni tí ó bá gbèrò láti ṣe ìrántí (Allāhu) tàbí tí ó bá gbèrò ìdúpẹ́ (fún Un).');
INSERT INTO "aykyuni" VALUES(25,63,'25:63','Àwọn ẹrúsìn Àjọkẹ́-ayé ni àwọn tó ń rìn jẹ́ẹ́jẹ́ lórí ilẹ̀. Nígbà tí àwọn òpè bá sì dojú ọ̀rọ̀ kọ wọ́n, wọn yóò sọ ọ̀rọ̀ àlàáfíà.');
INSERT INTO "aykyuni" VALUES(25,64,'25:64','(Àwọn ni) àwọn tó ń lo òru wọn ní ìforíkanlẹ̀ àti ní ìdúró-kírun fún Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(25,65,'25:65','(Àwọn ni) àwọn tó ń sọ pé: “Olúwa wa, gbé ìyà iná Jahanamọ kúrò fún wa. Dájúdájú ìyà rẹ̀ jẹ́ ìyà àìnípẹ̀kun.');
INSERT INTO "aykyuni" VALUES(25,66,'25:66','Dájúdájú ó burú ní ibùgbé àti ibùdúró.');
INSERT INTO "aykyuni" VALUES(25,67,'25:67','Àwọn tí ó jẹ́ pé nígbà tí wọ́n bá náwó, wọn kò ná ìná-àpà, wọn kò sì ṣahun; (ìnáwó wọn) wà láààrin ìyẹn ní ìwọ̀ntún-wọ̀nsì.');
INSERT INTO "aykyuni" VALUES(25,68,'25:68','(Àwọn ni) àwọn tí kò pe ọlọ́hun mìíràn mọ́ Allāhu. Wọn kò pa ẹ̀mí tí Allāhu ṣe (pípa rẹ̀) ní èèwọ̀ àfi ní ọ̀nà ẹ̀tọ́. Àti pé wọn kò ṣe zinā. Ẹnikẹ́ni tí ó bá ṣe (aburú) yẹn, ó máa pàdé (ìyà) ẹ̀ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(25,69,'25:69','A óò ṣe ìyà ní ìlọ́po fún un ní Ọjọ́ Àjíǹde. Ó sì máa ṣe gbére nínú rẹ̀ ní ẹni yẹpẹrẹ.');
INSERT INTO "aykyuni" VALUES(25,70,'25:70','Àfi ẹni tí ó bá ronú pìwàdà, tí ó gbàgbọ́ ní òdodo, tí ó sì ṣe iṣẹ́ rere. Àwọn wọ̀nyẹn ni Allāhu yóò fi iṣẹ́ rere rọ́pò iṣẹ́ aburú wọn (ìyẹn, nípa ìronúpìwàdà wọn). Allāhu sì ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(25,71,'25:71','Ẹnikẹ́ni tí ó bá ronú pìwàdà, tí ó sì ṣe iṣẹ́ rere, (kí ó mọ̀) pé dájúdájú ó ń ronú pìwàdà pátápátá sọ́dọ̀ Allāhu ni.');
INSERT INTO "aykyuni" VALUES(25,72,'25:72','(Àwọn ni) àwọn tí kò jẹ́rìí èké. Tí wọ́n bá sì kọjá níbi ìbàjẹ́, wọ́n á kọjá pẹ̀lú àpọ́nlé.1 ');
INSERT INTO "aykyuni" VALUES(25,73,'25:73','(Àwọn ni) àwọn tí ó jẹ́ pé tí wọ́n bá fi àwọn āyah Olúwa wọn ṣèrántí fún wọn, wọn kò dà lulẹ̀ bí adití àti afọ́jú.');
INSERT INTO "aykyuni" VALUES(25,74,'25:74','(Àwọn ni) àwọn tó ń sọ pé: “Olúwa wa, ta wá ní ọrẹ ìtutù-ojú (ìdùnnú àti ayọ̀) láti ara àwọn ìyàwó wa àti àwọn ọmọ wa. Kí O sì ṣe wá ní aṣíwájú (àwòkọ́ṣe rere) fún àwọn olùbẹ̀rù (Rẹ).”');
INSERT INTO "aykyuni" VALUES(25,75,'25:75','Àwọn wọ̀nyẹn, ipò gíga (nínú Ọgbà Ìdẹ̀ra) ni A máa fi san wọ́n ní ẹ̀san nítorí pé, wọ́n ṣe sùúrù. Ìkíni àti sísálámọ̀ ni A óò fi máa pàdé wọn nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(25,76,'25:76','Olúṣegbére ni wọ́n nínú rẹ̀. Ó dára ní ibùgbé àti ibùdúró.');
INSERT INTO "aykyuni" VALUES(25,77,'25:77','Sọ pé: “Olúwa mi kò kà yín kún tí kì í bá ṣe àdúà yín (àti ìjọ́sìn yín). Ẹ kúkú ti pe (àwọn āyah Rẹ̀) nírọ́. Láìpẹ́ ó sì máa di ìyà àìnípẹ̀kun (fún yín).”');
INSERT INTO "aykyuni" VALUES(26,1,'26:1','Tọ̄ sīn mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(26,2,'26:2','Ìwọ̀nyí ni àwọn āyah Tírà tó ń yanjú ọ̀rọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(26,3,'26:3','Nítorí kí ni o ṣe máa para rẹ pé wọn kò jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(26,4,'26:4','Tí A bá fẹ́, A máa sọ àmì kan kalẹ̀ fún wọn láti sánmọ̀. Àwọn ọrùn wọn kò sì níí yé tẹríba fún un.');
INSERT INTO "aykyuni" VALUES(26,5,'26:5','Kò sí ìrántí kan tí ó máa dé bá wọn ní titun láti ọ̀dọ̀ Àjọkẹ́-ayé àfi kí wọ́n jẹ́ olùgbúnrí kúrò níbẹ̀.');
INSERT INTO "aykyuni" VALUES(26,6,'26:6','Dájúdájú wọ́n ti pe (āyah Wa) nírọ́. Nítorí náà, àwọn ìró ohun tí wọ́n ń fi ṣe yẹ̀yẹ́ ń bọ̀ wá bá wọn.');
INSERT INTO "aykyuni" VALUES(26,7,'26:7','Ṣé wọn kò rí ilẹ̀ pé mélòó mélòó nínú gbogbo oríṣiríṣi n̄ǹkan alápọ̀n-ọ́nlé tí A mú hù jáde láti inú rẹ̀?');
INSERT INTO "aykyuni" VALUES(26,8,'26:8','Dájúdájú àmì wà nínú ìyẹn. Síbẹ̀síbẹ̀ ọ̀pọ̀lọ́pọ̀ wọn kò jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(26,9,'26:9','Dájúdájú Olúwa rẹ, Òun mà ni Alágbára, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(26,10,'26:10','(Rántí) nígbà tí Olúwa rẹ pe (Ànábì) Mūsā pé: “Lọ bá ìjọ alábòsí,');
INSERT INTO "aykyuni" VALUES(26,11,'26:11','ìjọ Fir‘aon (kí o sì sọ̱ fún wọn) pé, ‘Ṣé wọn kò níí bẹ̀rù (Allāhu) ni.’ ”');
INSERT INTO "aykyuni" VALUES(26,12,'26:12','Ó sọ pé: “Olúwa mi, dájúdájú èmi ń bẹ̀rù pé wọ́n máa pè mí ní òpùrọ́.');
INSERT INTO "aykyuni" VALUES(26,13,'26:13','Ó sì máa jẹ́ ìnira fún mi (tí wọ́n bá pè mí ní òpùrọ́). Ahọ́n mi kò sì já gaaraga. Nítorí náà, ránṣẹ́ sí Hārūn.');
INSERT INTO "aykyuni" VALUES(26,14,'26:14','Àti pé mo ní ẹ̀ṣẹ̀ kan lọ́rùn lọ́dọ̀ wọn. Nítorí náà, mò ń bẹ̀rù pé wọ́n máa pa mí.”');
INSERT INTO "aykyuni" VALUES(26,15,'26:15','(Allāhu) sọ pé: “Rárá (wọn kò lè pa ọ́). Nítorí náà, kí ẹ̀yin méjèèjì mú àwọn àmì (iṣẹ́ ìyanu) Wa lọ (bá wọn). Dájúdájú Àwa ń bẹ pẹ̀lú yín; À ń gbọ́ (ọ̀rọ̀ yín).');
INSERT INTO "aykyuni" VALUES(26,16,'26:16','Ẹ̀yin méjèèjì, ẹ lọ bá Fir‘aon. Kí ẹ sọ fún un pé: “Dájúdájú àwa ni Òjíṣẹ́ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(26,17,'26:17','(À ń sọ fún ọ) pé kí o jẹ́ kí àwọn ọmọ ’Isrọ̄’īl máa bá wa lọ.”');
INSERT INTO "aykyuni" VALUES(26,18,'26:18','(Fir‘aon) wí pé: “Ṣé kì í ṣe ní ààrin wa ni a ti tọ́ ọ ní kékeré ni, tí o sì gbé ní ọ̀dọ̀ wa fún ọdún púpọ̀ nínú ìṣẹ̀mí ayé rẹ?');
INSERT INTO "aykyuni" VALUES(26,19,'26:19','O sì ṣe iṣẹ́ ọwọ́ rẹ tí o ṣe (sí wa). Ìwọ sì wà nínú àwọn aláìmoore.”');
INSERT INTO "aykyuni" VALUES(26,20,'26:20','(Ànábì Mūsā) sọ pé: “Mo ṣe é nígbà náà nígbà tí mo wà nínú àwọn aláṣìṣe (èmi kò mọ̀ọ́mọ̀ ṣe é).');
INSERT INTO "aykyuni" VALUES(26,21,'26:21','Mo sì sá fún yín nígbà tí mo bẹ̀rù yín. Lẹ́yìn náà, Allāhu ta mí lọ́rẹ ipò Ànábì. Ó sì ṣe mí ní (ọ̀kan) nínú àwọn Òjíṣẹ́.');
INSERT INTO "aykyuni" VALUES(26,22,'26:22','Àti pé ìyẹn ni ìdẹ̀ra tí ò ń ṣe ìrègún rẹ̀ lé mi lórí. Pé ó sọ àwọn ọmọ ’Isrọ̄’īl di ẹrú (ńkọ́?).1 ');
INSERT INTO "aykyuni" VALUES(26,23,'26:23','Fir‘aon wí pé: “Kí ni Olúwa gbogbo ẹ̀dá?”');
INSERT INTO "aykyuni" VALUES(26,24,'26:24','(Ànábì Mūsā) sọ pé: “(Allāhu ni) Olúwa àwọn sánmọ̀, ilẹ̀ àti ohunkóhun tó ń bẹ láààrin àwọn méjèèjì, tí ẹ̀yin bá jẹ́ alámọ̀dájú.”');
INSERT INTO "aykyuni" VALUES(26,25,'26:25','(Fir‘aon) wí fún àwọn tó wà ní àyíká rẹ̀ pé: “Ṣé ẹ ẹ̀ gbọ́ ọ̀rọ̀ (tó ń sọ ni)?”');
INSERT INTO "aykyuni" VALUES(26,26,'26:26','(Ànábì Mūsā) sọ pé: “(Allāhu ni) Olúwa yín àti Olúwa àwọn bàbá yín, àwọn ẹni àkọ́kọ́.”');
INSERT INTO "aykyuni" VALUES(26,27,'26:27','(Fir‘aon) wí pé: “Dájúdájú Òjíṣẹ́ yín èyí tí wọ́n rán si yín, wèrè mà ni.”');
INSERT INTO "aykyuni" VALUES(26,28,'26:28','(Ànábì Mūsā) sọ pé: “(Allāhu ni) Olúwa ibùyọ òòrùn, ibùwọ̀ òòrùn àti ohunkóhun tó ń bẹ láààrin àwọn méjèèjì, tí ẹ bá máa ń ṣe làákàyè.”');
INSERT INTO "aykyuni" VALUES(26,29,'26:29','(Fir‘aon) wí pé: “Dájúdájú tí o bá fi lè jọ́sìn fún ọlọ́hun kan yàtọ̀ sí mi, dájúdájú mo máa sọ ọ́ di ara àwọn ẹlẹ́wọ̀n.”');
INSERT INTO "aykyuni" VALUES(26,30,'26:30','(Ànábì Mūsā) sọ pé: “Tí ó bá jẹ́ pé mo mú kiní kan tó yanjú wá fún ọ ńkọ́?”');
INSERT INTO "aykyuni" VALUES(26,31,'26:31','(Fir‘aon) wí pé: “Mú un wá nígbà náà tí ìwọ bá wà nínú àwọn olódodo.”');
INSERT INTO "aykyuni" VALUES(26,32,'26:32','Nítorí náà, ó ju ọ̀pá rẹ̀ sílẹ̀, ó sì di ejò pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(26,33,'26:33','Ó tún fa ọwọ́ rẹ̀ yọ síta, ó sì di funfun fún àwọn olùwòran.');
INSERT INTO "aykyuni" VALUES(26,34,'26:34','(Fir‘aon) wí fún àwọn ìjòyè tí wọ́n wà ní àyíká rẹ̀ pé: “Dájúdájú èyí ni onímọ̀ nípa idán pípa.');
INSERT INTO "aykyuni" VALUES(26,35,'26:35','Ó fẹ́ ko yín kúrò lórí ilẹ̀ yín ni. Kí ni ohun tí ẹ máa mú wá ní ìmọ̀ràn?”1 ');
INSERT INTO "aykyuni" VALUES(26,36,'26:36','Wọ́n wí pé: “Dá òun àti arákùnrin rẹ̀ dúró ná, kí o sì rán àwọn akónijọsí àwọn ìlú ');
INSERT INTO "aykyuni" VALUES(26,37,'26:37','(pé) kí gbogbo onímọ̀ nípa idán pípa wá bá ọ.”');
INSERT INTO "aykyuni" VALUES(26,38,'26:38','Wọ́n sì kó àwọn òpìdán jọ ní àsìkò tí wọ́n fi àdéhùn sí ní ọjọ́ tí (gbogbo ìlú) ti mọ̀.');
INSERT INTO "aykyuni" VALUES(26,39,'26:39','Wọ́n sì wí fún àwọn ènìyàn pé: “Ṣé ẹ̀yin máa kó jọ (síbẹ̀) bí?');
INSERT INTO "aykyuni" VALUES(26,40,'26:40','Nítorí kí á lè tẹ̀lé àwọn òpìdán tí ó bá jẹ́ pé àwọn ni olùborí.”');
INSERT INTO "aykyuni" VALUES(26,41,'26:41','Nígbà tí àwọn òpìdán dé, wọ́n wí fún Fir‘aon pé: “Ǹjẹ́ owó-ọ̀yà kan wà fún wa, tí ó bá jẹ́ pé àwa gan-an la jẹ́ olùborí?”');
INSERT INTO "aykyuni" VALUES(26,42,'26:42','(Fir‘aon) wí pé: “Bẹ́ẹ̀ ni, dájúdájú nígbà náà ẹ máa wà nínú àwọn alásùn-únmọ́ (mi).”');
INSERT INTO "aykyuni" VALUES(26,43,'26:43','(Ànábì) Mūsā sọ fún wọn pé: “Ẹ ju ohun tí ẹ fẹ́ jù sílẹ̀.”');
INSERT INTO "aykyuni" VALUES(26,44,'26:44','Wọ́n sì ju àwọn okùn wọn àti ọ̀pá wọn sílẹ̀. Wọ́n wí pé: “Pẹ̀lú ògo Fir‘aon, dájúdájú àwa, àwa ni olùborí.”');
INSERT INTO "aykyuni" VALUES(26,45,'26:45','Nígbà náà, (Ànábì) Mūsā ju ọ̀pá rẹ̀ sílẹ̀. Ó sì ń gbé ohun tí wọ́n pa nírọ́ kalẹ̀ mì kálókáló.');
INSERT INTO "aykyuni" VALUES(26,46,'26:46','Nítorí náà, iṣẹ́ ìyanu (Ànábì Mūsā) mú àwọn òpìdán wó lulẹ̀, tí wọ́n forí kanlẹ̀ (fún Allāhu).');
INSERT INTO "aykyuni" VALUES(26,47,'26:47','Wọ́n sọ pé: “A gbàgbọ́ nínú Olúwa gbogbo ẹ̀dá,');
INSERT INTO "aykyuni" VALUES(26,48,'26:48','Olúwa (Ànábì) Mūsā àti Hārūn.”');
INSERT INTO "aykyuni" VALUES(26,49,'26:49','Fir‘aon wí pé: “Ẹ gbà Á gbọ́ ṣíwájú kí n̄g tó yọ̀ǹda fun yín! Dájúdájú òun ni àgbà yín tí ó kọ yín ní idán pípa. Láìpẹ́ ẹ̀ ń bọ̀ wá mọ̀. Dájúdájú mo máa gé ọwọ́ yín àti ẹsẹ̀ yín ní ìpasípayọ. Lẹ́yìn náà, dájúdájú mo máa kan gbogbo yín mọ́gi.”');
INSERT INTO "aykyuni" VALUES(26,50,'26:50','(Àwọn òpìdán) sọ pé: “Kò sí ìnira fún wa! Dájúdájú àwa yó sì fàbọ̀ sí ọ̀dọ̀ Olúwa wa.');
INSERT INTO "aykyuni" VALUES(26,51,'26:51','Dájúdájú àwa n retí pé Olúwa wa yóò ṣe àforíjìn àwọn àṣìṣe wa fún wa nítorí pé àwa jẹ́ ẹni àkọ́kọ́ (tí ó jẹ́) onígbàgbọ́ òdodo.”1 ');
INSERT INTO "aykyuni" VALUES(26,52,'26:52','A fi ìmísí ránṣẹ́ sí (Ànábì) Mūsā pé: “Mú àwọn ẹrúsìn Mi rìn ní òru (nítorí pé), dájúdájú wọn yóò lépa yín.”');
INSERT INTO "aykyuni" VALUES(26,53,'26:53','Fir‘aon sì rán àwọn akónijọ sínú àwọn ìlú (láti sọ pé):');
INSERT INTO "aykyuni" VALUES(26,54,'26:54','“Dájúdájú àwọn (ọmọ ’Isrọ̄’īl) wọ̀nyí, ìjọ péréte díẹ̀ ni wọ́n.');
INSERT INTO "aykyuni" VALUES(26,55,'26:55','Àti pé dájúdájú wọ́n ti ṣe ohun tó ń bí wa nínú.');
INSERT INTO "aykyuni" VALUES(26,56,'26:56','Dájúdájú gbogbo wa ni kí á sì wà tìfura-tìfura.”');
INSERT INTO "aykyuni" VALUES(26,57,'26:57','Nítorí náà, A mú (ìjọ Fir‘aon) jáde kúrò nínú àwọn ọgbà oko àti ìṣẹ́lẹ̀rú omi,');
INSERT INTO "aykyuni" VALUES(26,58,'26:58','àti àwọn ilé ọrọ̀ àti ibùjókòó àpọ́nlé.');
INSERT INTO "aykyuni" VALUES(26,59,'26:59','Báyẹn (lọ̀rọ̀ rí). A sì jogún rẹ̀ fún àwọn ọmọ ’Isrọ̄’īl.');
INSERT INTO "aykyuni" VALUES(26,60,'26:60','Wọ́n sì lépa wọn ní àsìkò tí òòrùn yọ.');
INSERT INTO "aykyuni" VALUES(26,61,'26:61','Nígbà tí ìjọ méjèèjì ríra wọn, àwọn ìjọ (Ànábì) Mūsā sọ pé: “Dájúdájú wọn yóò lé wa bá.”');
INSERT INTO "aykyuni" VALUES(26,62,'26:62','(Ànábì) Mūsā sọ pé: “Rárá, dájúdájú Olúwa mi ń bẹ pẹ̀lú mi. Ó sì máa fi ọ̀nà mọ̀ mí.”');
INSERT INTO "aykyuni" VALUES(26,63,'26:63','A sì fi ìmísí ránṣẹ́ sí (Ànábì) Mūsā pé: “Fi ọ̀pá rẹ na agbami òkun.” (Ó fi nà á). Ó sì pín (sí ọ̀nà méjìlá). Ìpín kọ̀ọ̀kan sì dà bí àpáta ńlá.');
INSERT INTO "aykyuni" VALUES(26,64,'26:64','A sì mú ìjọ kejì (ìyẹn, ìjọ Fir‘aon) súnmọ́ ibẹ̀ yẹn.');
INSERT INTO "aykyuni" VALUES(26,65,'26:65','A gba (Ànábì) Mūsā àti gbogbo àwọn tó ń bẹ pẹ̀lú rẹ̀ là pátápátá.');
INSERT INTO "aykyuni" VALUES(26,66,'26:66','Lẹ́yìn náà, A tẹ ìjọ kejì rì (sínú agbami).');
INSERT INTO "aykyuni" VALUES(26,67,'26:67','Dájúdájú àmì wà nínú ìyẹn. Síbẹ̀síbẹ̀ ọ̀pọ̀lọ́pọ̀ wọn kò jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(26,68,'26:68','Dájúdájú Olúwa rẹ, Òun mà ni Alágbára, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(26,69,'26:69','Ka ìròyìn (Ànábì) ’Ibrọ̄hīm fún wọn.');
INSERT INTO "aykyuni" VALUES(26,70,'26:70','Nígbà tí ó sọ fún bàbá rẹ̀ àti àwọn ènìyàn rẹ̀ pé: “Kí ni n̄ǹkan tí ẹ̀ ń jọ́sìn fún ná?”');
INSERT INTO "aykyuni" VALUES(26,71,'26:71','Wọ́n wí pé: “À ń jọ́sìn fún àwọn ère òrìṣà kan ni. A ò sì níí yé takú tì wọ́n lọ́rùn.”');
INSERT INTO "aykyuni" VALUES(26,72,'26:72','(Ànábì ’Ibrọ̄hīm) sọ pé: “Ǹjẹ́ wọ́n ń gbọ́ ọ̀rọ̀ yín nígbà tí ẹ bá ń pè wọ́n?');
INSERT INTO "aykyuni" VALUES(26,73,'26:73','Tàbí (ṣé) wọ́n lè ṣe yín ní àǹfààní tàbí wọ́n lè fi ìnira kàn yín?”');
INSERT INTO "aykyuni" VALUES(26,74,'26:74','Wọ́n wí pé: “Rárá o! A bá àwọn bàbá wa tí wọ́n ń ṣe bẹ́ẹ̀ ni”');
INSERT INTO "aykyuni" VALUES(26,75,'26:75','(Ànábì ’Ibrọ̄hīm) sọ pé: “ Ẹ sọ fún mi, kí ni n̄ǹkan tí ẹ̀ ń jọ́sìn fún,');
INSERT INTO "aykyuni" VALUES(26,76,'26:76','ẹ̀yin àti àwọn bàbá yín àwọn ẹni ìṣáájú?');
INSERT INTO "aykyuni" VALUES(26,77,'26:77','Dájúdájú àwọn ni ọ̀tá fún mi àyàfi Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(26,78,'26:78','(Òun ni) Ẹni tí Ó ṣẹ̀dá mi. Nítorí náà, Ó máa fi ọ̀nà mọ̀ mí;');
INSERT INTO "aykyuni" VALUES(26,79,'26:79','Ẹni tó ń fún mi ní jíjẹ, tó ń fún mi ní mímu;');
INSERT INTO "aykyuni" VALUES(26,80,'26:80','Àti pé nígbà tí ara mi kò bá yá, Òun l’Ó ń wò mí sàn;');
INSERT INTO "aykyuni" VALUES(26,81,'26:81','Ẹni tí Ó máa pa mí, lẹ́yìn náà, Ó máa sọ mí di alààyè;');
INSERT INTO "aykyuni" VALUES(26,82,'26:82','Ẹni tí mo ní ìrètí sí pé Ó máa forí àwọn àṣìṣe mi jìn mí ní Ọjọ́ Ẹ̀san.');
INSERT INTO "aykyuni" VALUES(26,83,'26:83','Olúwa mi, ta mí ní ọrẹ ipò Ànábì. Dà mí pọ̀ mọ́ àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(26,84,'26:84','Fi òdodo sórí ahọ́n àwọn tó ń bọ̀ lẹ́yìn mi nìpa mi (ìyẹn ni pé, jẹ́ kí àwọn tó ń bọ̀ lẹ́yìn mi máa sọ̀rọ̀ mi ní dáadáa).1 ');
INSERT INTO "aykyuni" VALUES(26,85,'26:85','Ṣe mí ní ọ̀kan nínú àwọn olùjogún Ọgbà Ìdẹ̀ra.');
INSERT INTO "aykyuni" VALUES(26,86,'26:86','Kí O sì foríjin bàbá mi. Dájúdájú ó jẹ́ ọ̀kan nínú àwọn olùṣìnà.');
INSERT INTO "aykyuni" VALUES(26,87,'26:87','Má ṣe dójú tì mí ní ọjọ́ tí wọn yóò gbé ẹ̀dá dìde.');
INSERT INTO "aykyuni" VALUES(26,88,'26:88','Ọjọ́ tí dúkìá kan tàbí àwọn ọmọ kò níí ṣàǹfààní.');
INSERT INTO "aykyuni" VALUES(26,89,'26:89','Àyàfi ẹni tí ó bá dé ọ̀dọ̀ Allāhu pẹ̀lú ọkàn mímọ́.”1 ');
INSERT INTO "aykyuni" VALUES(26,90,'26:90','A máa mú Ọgbà Ìdẹ̀ra súnmọ́ àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(26,91,'26:91','Wọ́n sì máa fi iná Jẹhīm han àwọn olùṣìnà,');
INSERT INTO "aykyuni" VALUES(26,92,'26:92','wọn yó sì sọ fún wọn pé: “Níbo ni n̄ǹkan tí ẹ̀ ń jọ́sìn fún wà?”');
INSERT INTO "aykyuni" VALUES(26,93,'26:93','(N̄ǹkan tí ẹ̀ ń jọ́sìn fún) lẹ́yìn Allāhu, ṣé wọ́n lè ràn yín lọ́wọ́ tàbí ṣé wọ́n lè ran ara wọn lọ́wọ́?');
INSERT INTO "aykyuni" VALUES(26,94,'26:94','Wọ́n sì máa taari gbogbo wọn ṣubú sínú Iná; àwọn (olùṣìnà) àti àwọn ọ̀gá wọn (nínú ìṣìnà),');
INSERT INTO "aykyuni" VALUES(26,95,'26:95','àti àwọn ọmọ ogun ’Iblīs pátápátá.');
INSERT INTO "aykyuni" VALUES(26,96,'26:96','(Àwọn aláìgbàgbọ́), nígbà tí wọ́n bá ń bá ara wọn jiyàn nínú Iná, wọ́n á wí pé:');
INSERT INTO "aykyuni" VALUES(26,97,'26:97','“A fi Allāhu búra, dájúdájú àwa kúkú ti wà nínú ìṣìnà pọ́nńbélé');
INSERT INTO "aykyuni" VALUES(26,98,'26:98','nígbà tí a fi yín ṣe akẹgbẹ́ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(26,99,'26:99','Kò sí ohun tí ó ṣì wá lọ́nà bí kò ṣe àwọn (èṣù) ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(26,100,'26:100','Kò sì sí àwọn olùṣìpẹ̀ kan fún wa mọ́.');
INSERT INTO "aykyuni" VALUES(26,101,'26:101','Kò tún sí ọ̀rẹ́ ìmùlẹ̀ kan (fún wa.)');
INSERT INTO "aykyuni" VALUES(26,102,'26:102','Tí ó bá jẹ́ pé ìpadà sí ilé ayé wà fún wa ni, nígbà náà àwa ìbá wà nínú àwọn onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(26,103,'26:103','Dájúdájú àmì wà nínú ìyẹn. Síbẹ̀síbẹ̀ ọ̀pọ̀lọ́pọ̀ wọn ni kò sì jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(26,104,'26:104','Dájúdájú Olúwa rẹ, Òun mà ni Alágbára, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(26,105,'26:105','Ìjọ (Ànábì) Nūh pe àwọn Òjíṣẹ́ ní òpùrọ́.');
INSERT INTO "aykyuni" VALUES(26,106,'26:106','Nígbà tí arákùnrin wọn, (Ànábì) Nūh, sọ pé: “Ṣé ẹ kò níí bẹ̀rù (Allāhu) ni?');
INSERT INTO "aykyuni" VALUES(26,107,'26:107','Dájúdájú èmi ni Òjíṣẹ́ olùfọkàntán fún yín.');
INSERT INTO "aykyuni" VALUES(26,108,'26:108','Nítorí náà, ẹ bẹ̀rù Allāhu, kí ẹ sì tẹ̀lé mi.');
INSERT INTO "aykyuni" VALUES(26,109,'26:109','Èmi kò sì bèèrè owó-ọ̀yà kan ní ọwọ́ yín lórí rẹ̀. Kò sí ẹ̀san mi lọ́dọ̀ ẹnì kan àfi lọ́dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(26,110,'26:110','Nítorí náà, ẹ bẹ̀rù Allāhu, kí ẹ sì tẹ̀lé mi.”');
INSERT INTO "aykyuni" VALUES(26,111,'26:111','Wọ́n wí pé: “Ṣé kí á gbà ọ́ gbọ́ nígbà tí ó jẹ́ pé àwọn ènìyàn yẹpẹrẹ l’ó ń tẹ̀lé ọ!?”');
INSERT INTO "aykyuni" VALUES(26,112,'26:112','Ó sọ pé: “Èmi kò nímọ̀ sí ohun tí wọ́n ń ṣe (lẹ́yìn mi).');
INSERT INTO "aykyuni" VALUES(26,113,'26:113','Kò sí ìṣírò-iṣẹ́ wọn lọ́dọ̀ ẹnì kan bí kò ṣe lọ́dọ̀ Olúwa mi, tí ó bá jẹ́ pé ẹ bá fura.');
INSERT INTO "aykyuni" VALUES(26,114,'26:114','Èmi kò sì níí lé àwọn onígbàgbọ́ òdodo dànù.');
INSERT INTO "aykyuni" VALUES(26,115,'26:115','Èmi kò sì jẹ́ kiní kan bí kò ṣe olùkìlọ̀ pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(26,116,'26:116','Wọ́n wí pé: “Dájúdájú tí ìwọ Nūh kò bá jáwọ́ (nibí ìpèpè rẹ) dájúdájú o máa wà nínú àwọn tí a máa jù lókò.”');
INSERT INTO "aykyuni" VALUES(26,117,'26:117','Ó sọ pé: “Olúwa mi, dájúdájú àwọn ènìyàn mi ti pè mí ní òpùrọ́.');
INSERT INTO "aykyuni" VALUES(26,118,'26:118','Nítorí náà, ṣèdájọ́ tààrà láààrin èmi àti àwọn. Kí o sì la èmi àti àwọn tó wà pẹ̀lú mi nínú àwọn onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(26,119,'26:119','A la òun àti àwọn tó wà pẹ̀lú rẹ̀ nínú ọkọ̀ ojú-omi tó kún kẹ́kẹ́.');
INSERT INTO "aykyuni" VALUES(26,120,'26:120','Lẹ́yìn náà, A tẹ àwọn tó ṣẹ́kù rì sínú omi.');
INSERT INTO "aykyuni" VALUES(26,121,'26:121','Dájúdájú àmì wà nínú ìyẹn. Ọ̀pọ̀lọ́pọ̀ wọn ni kò sì jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(26,122,'26:122','Dájúdájú Olúwa rẹ, Òun mà ni Alágbára, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(26,123,'26:123','Àwọn ìran ‘Ād pe àwọn Òjíṣẹ́ ní òpùrọ́.');
INSERT INTO "aykyuni" VALUES(26,124,'26:124','Nígbà tí arákùnrin wọn, (Ànábì) Hūd, sọ pé: “Ṣé ẹ kò níí bẹ̀rù (Allāhu) ni?');
INSERT INTO "aykyuni" VALUES(26,125,'26:125','Dájúdájú èmi ni Òjíṣẹ́ olùfọkàntán fún yín.');
INSERT INTO "aykyuni" VALUES(26,126,'26:126','Nítorí náà, ẹ bẹ̀rù Allāhu, kí ẹ sì tẹ̀lé mi.');
INSERT INTO "aykyuni" VALUES(26,127,'26:127','Èmi kò sì bèèrè owó-ọ̀yà kan ní ọwọ́ yín lórí rẹ̀. Kò sí ẹ̀san mi lọ́dọ̀ ẹnì kan àfi lọ́dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(26,128,'26:128','Ṣé ẹ óò máa kọ́ ilé ńlá ńlá sí àwọn àyè gíga (kí ẹ lè) máa fi àwọn olùrékọjá ṣẹ̀fẹ̀?');
INSERT INTO "aykyuni" VALUES(26,129,'26:129','Ẹ sì ń kọ́ àwọn ilé ńlá ńlá bí ẹni pé ẹ máa ṣe gbére (nílé ayé yìí).');
INSERT INTO "aykyuni" VALUES(26,130,'26:130','Nígbà tí ẹ bá sì gbá (ènìyàn mú láti fìyà jẹ wọ́n), ẹ̀ ń gbá wọn mú ní ìgbámú aláìlójú-àánú.');
INSERT INTO "aykyuni" VALUES(26,131,'26:131','Nítorí náà, ẹ bẹ̀rù Allāhu, kí ẹ sì tẹ̀lé mi.');
INSERT INTO "aykyuni" VALUES(26,132,'26:132','Ẹ bẹ̀rù Ẹni tí Ó ràn yín lọ́wọ́ pẹ̀lú ohun tí ẹ mọ̀;');
INSERT INTO "aykyuni" VALUES(26,133,'26:133','Ó ràn yín lọ́wọ́ pẹ̀lú àwọn ẹran-ọ̀sìn àti àwọn ọmọ,');
INSERT INTO "aykyuni" VALUES(26,134,'26:134','àti àwọn ọgbà oko pẹ̀lú àwọn ìṣẹ́lẹ̀rú omi.');
INSERT INTO "aykyuni" VALUES(26,135,'26:135','Dájúdájú èmi ń páyà ìyà ọjọ́ ńlá fún yín.”');
INSERT INTO "aykyuni" VALUES(26,136,'26:136','Wọ́n wí pé: “Bákan náà ni fún wa; yálà o ṣe wáàsí fún wa tàbí o ò sí nínú àwọn oníwáàsí.”');
INSERT INTO "aykyuni" VALUES(26,137,'26:137','- Èyí kò jẹ́ kiní kan bí kò ṣe ìwà àwọn ẹni àkọ́kọ́.– ');
INSERT INTO "aykyuni" VALUES(26,138,'26:138','Wọn kò sì níí jẹ wá níyà.”');
INSERT INTO "aykyuni" VALUES(26,139,'26:139','Nítorí náà, wọ́n pè é ní òpùrọ́. A sì pa wọ́n run. Dájúdájú àmì wà nínú ìyẹn. Ọ̀pọ̀lọ́pọ̀ wọn ni kò sì jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(26,140,'26:140','Dájúdájú Olúwa rẹ, Òun mà ni Alágbára, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(26,141,'26:141','Àwọn ìran Thamūd pe àwọn Òjíṣẹ́ ní òpùrọ́.');
INSERT INTO "aykyuni" VALUES(26,142,'26:142','Nígbà tí arákùnrin wọn, (Ànábì) Sọ̄lih, sọ pé: “Ṣé ẹ kò níí bẹ̀rù (Allāhu) ni?');
INSERT INTO "aykyuni" VALUES(26,143,'26:143','Dájúdájú emi ni Òjíṣẹ́ olùfọkàntán fún yín.');
INSERT INTO "aykyuni" VALUES(26,144,'26:144','Nítorí náà, ẹ bẹ̀rù Allāhu, kí ẹ sì tẹ̀lé mi.');
INSERT INTO "aykyuni" VALUES(26,145,'26:145','Èmi kò sì bèèrè owó-ọ̀yà kan ní ọwọ́ yín lórí rẹ̀. Kò sí ẹ̀san mi lọ́dọ̀ ẹnì kan àfi lọ́dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(26,146,'26:146','Ṣé kí wọ́n fi yín sílẹ̀ síbi n̄ǹkan tí ó wà (nílé ayé) níbí yìí (nínú ìgbádùn ayé, kí ẹ jẹ́) olùfọkànbalẹ̀');
INSERT INTO "aykyuni" VALUES(26,147,'26:147','nínú àwọn ọgbà oko àti ìṣẹ́lẹ̀rú omi.');
INSERT INTO "aykyuni" VALUES(26,148,'26:148','àti oko irúgbìn pẹ̀lú igi dàbínù tí àwọn èso wọn ti gbó?');
INSERT INTO "aykyuni" VALUES(26,149,'26:149','Ẹ sì ń gbẹ́ àwọn ilé sínú àwọn àpáta (gẹ́gẹ́ bí) akọ́ṣẹ́-mọṣẹ́ olùgbéraga!');
INSERT INTO "aykyuni" VALUES(26,150,'26:150','Nítorí náà, ẹ bẹ̀rù Allāhu, kí ẹ sì tẹ̀lé mi.');
INSERT INTO "aykyuni" VALUES(26,151,'26:151','Kí ẹ sì má ṣe tẹ̀lé àṣẹ àwọn alákọyọ (aláṣejù),');
INSERT INTO "aykyuni" VALUES(26,152,'26:152','àwọn tó ń ṣèbàjẹ́ lórí ilẹ̀, tí wọn kò sì ṣe rere.”');
INSERT INTO "aykyuni" VALUES(26,153,'26:153','Wọ́n wí pé: “Ìwọ kúkú wà nínú àwọn eléèdì.');
INSERT INTO "aykyuni" VALUES(26,154,'26:154','Ìwọ kò sì jẹ́ kiní kan bí kò ṣe abara bí irú wa. Nítorí náà, mú àmì kan wá tí o bá wà nínú àwọn olódodo.”');
INSERT INTO "aykyuni" VALUES(26,155,'26:155','Ó sọ pé: “Èyí ni abo ràkúnmí kan. Omi wà fún òhun, omi sì wà fún ẹ̀yin náà ní ọjọ́ tí a ti mọ̀ (ní ọ̀tọ̀ọ̀tọ̀).');
INSERT INTO "aykyuni" VALUES(26,156,'26:156','Ẹ má ṣe fi aburú kan kàn án nítorí kí ìyà ọjọ́ ńlá má baà jẹ yín.”');
INSERT INTO "aykyuni" VALUES(26,157,'26:157','Wọ́n sì gún un pa. Nítorí náà, wọ́n sì di alábàámọ̀.');
INSERT INTO "aykyuni" VALUES(26,158,'26:158','Ìyà náà jẹ wọ́n. Dájúdájú àmì wà nínú ìyẹn. Ọ̀pọ̀lọ́pọ̀ wọn ni kò sì jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(26,159,'26:159','Dájúdájú Olúwa rẹ, Òun mà ni Alágbára, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(26,160,'26:160','Ìjọ Lūt pe àwọn Òjíṣẹ́ ní òpùrọ́.');
INSERT INTO "aykyuni" VALUES(26,161,'26:161','Nígbà tí arákùnrin wọn, (Ànábì) Lūt, sọ pé: “Ṣé ẹ kò níí bẹ̀rù (Allāhu) ni?');
INSERT INTO "aykyuni" VALUES(26,162,'26:162','Dájúdájú èmi ni Òjíṣẹ́ olùfọkàntán fún yín.');
INSERT INTO "aykyuni" VALUES(26,163,'26:163','Nítorí náà, ẹ bẹ̀rù Allāhu, kí ẹ sì tẹ̀lé mi.');
INSERT INTO "aykyuni" VALUES(26,164,'26:164','Èmi kò sì bèèrè owó-ọ̀yà kan ní ọwọ́ yín lórí rẹ̀. Kò sí ẹ̀san mi lọ́dọ̀ ẹnì kan àfi lọ́dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(26,165,'26:165','Ṣé àwọn ọkùnrin nínú ẹ̀dá ni ẹ̀yin ọkùnrin yóò lọ máa bá (fún adùn ìbálòpọ̀)?');
INSERT INTO "aykyuni" VALUES(26,166,'26:166','Ẹ sì ń pa ohun tí Olúwa yín dá fún yín tì nínú àwọn ìyàwó yín! Àní sẹ́, ìjọ alákọyọ ni ẹ̀yin.”');
INSERT INTO "aykyuni" VALUES(26,167,'26:167','Wọ́n wí pé: “Tí ìwọ Lūt kò bá jáwọ́ (nibí ìpèpè rẹ) dájúdájú o máa wà nínú àwọn tí a máa lé jáde kúrò nínú ìlú.”');
INSERT INTO "aykyuni" VALUES(26,168,'26:168','Ó sọ pé: “Dájúdájú èmi wà nínú àwọn olùbínú sí iṣẹ́ (aburú) ọwọ́ yín.');
INSERT INTO "aykyuni" VALUES(26,169,'26:169','Olúwa mi, la èmi àti àwọn ènìyàn mi níbí ohun tí wọ́n ń ṣe níṣẹ́ (aburú).”');
INSERT INTO "aykyuni" VALUES(26,170,'26:170','Nítorí náà, A la òun àti àwọn ènìyàn rẹ̀ pátápátá.');
INSERT INTO "aykyuni" VALUES(26,171,'26:171','Àfi arúgbóbìnrin kan tí ó wà nínú àwọn tó ṣẹ́ kù lẹ́yìn (láààrin àwọn tí A parẹ́).1 ');
INSERT INTO "aykyuni" VALUES(26,172,'26:172','Lẹ́yìn náà, A pa àwọn yòókù run.');
INSERT INTO "aykyuni" VALUES(26,173,'26:173','A rọ òjò lé wọn lórí tààrà. Òjò àwọn tí A kìlọ̀ fún sì burú.');
INSERT INTO "aykyuni" VALUES(26,174,'26:174','Dájúdájú àmì wà nínú ìyẹn. Ọ̀pọ̀lọ́pọ̀ wọn ni kò sì jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(26,175,'26:175','Dájúdájú Olúwa rẹ, Òun mà ni Alágbára, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(26,176,'26:176','Àwọn ará ’Aekah pe àwọn Òjíṣẹ́ ní òpùrọ́.');
INSERT INTO "aykyuni" VALUES(26,177,'26:177','Nígbà tí arákùnrin wọn, (Ànábì) Ṣu‘aeb, sọ pé: “Ṣé ẹ̀yin kò níí bẹ̀rù (Allāhu) ni?');
INSERT INTO "aykyuni" VALUES(26,178,'26:178','Dájúdájú èmi ni Òjíṣẹ́ olùfọkàntán fún yín.');
INSERT INTO "aykyuni" VALUES(26,179,'26:179','Nítorí náà, ẹ bẹ̀rù Allāhu, kí ẹ sì tẹ̀lé mi.');
INSERT INTO "aykyuni" VALUES(26,180,'26:180','Èmi kò sì bèèrè owó-ọ̀yà kan ní ọwọ́ yín lórí rẹ̀. Kò sí ẹ̀san mi lọ́dọ̀ ẹnì kan àfi lọ́dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(26,181,'26:181','Ẹ wọn òṣùwọ̀n kún. Kí ẹ sì má ṣe wà nínú àwọn tó ń dín òṣùwọ̀n kù.');
INSERT INTO "aykyuni" VALUES(26,182,'26:182','Ẹ fi ìwọ̀n tó tọ́wọn n̄ǹkan. ');
INSERT INTO "aykyuni" VALUES(26,183,'26:183','Ẹ má ṣe dín n̄ǹkan àwọn ènìyàn kù. Kí ẹ sì má ṣèbàjẹ́ sórí ilẹ̀.');
INSERT INTO "aykyuni" VALUES(26,184,'26:184','Kí ẹ sì bẹ̀rù Ẹni tí Ó dá yín àti àwọn ẹ̀dá àkọ́kọ́.”');
INSERT INTO "aykyuni" VALUES(26,185,'26:185','Wọ́n wí pé: “Ìwọ kúkú wà nínú àwọn eléèdì.');
INSERT INTO "aykyuni" VALUES(26,186,'26:186','Ìwọ kò sì jẹ́ kiní kan bí kò ṣe abara bí irú wa. Àti pé a kò rò ọ́ sí kiní kan bí kò ṣe pé o wà nínú àwọn òpùrọ́.');
INSERT INTO "aykyuni" VALUES(26,187,'26:187','Nítorí náà, jẹ́ kí apá kan nínú sánmọ̀ ya lù wá mọ́lẹ̀, tí o bá wà nínú àwọn olódodo.”');
INSERT INTO "aykyuni" VALUES(26,188,'26:188','Ó sọ pé: “Olúwa mi nímọ̀ jùlọ nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(26,189,'26:189','Wọ́n sì pè é ní òpùrọ́. Nítorí náà, ìyà ọjọ́ ẹ̀ṣújò sì jẹ wọ́n. Dájúdájú ó jẹ́ ìyà ọjọ́ ńlá.');
INSERT INTO "aykyuni" VALUES(26,190,'26:190','Dájúdájú àmì wà nínú ìyẹn. Ọ̀pọ̀lọ́pọ̀ wọn ni kò sì jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(26,191,'26:191','Dájúdájú Olúwa rẹ, Òun mà ni Alágbára, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(26,192,'26:192','Dájúdájú al-Ƙur’ān, wọ́n sọ̀ ọ́ kalẹ̀ ni láti ọ̀dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(26,193,'26:193','Mọlāika Jibrīl, olùfọkàntán l’ó sọ̀ ọ́ kalẹ̀');
INSERT INTO "aykyuni" VALUES(26,194,'26:194','sínú ọkàn rẹ nítorí kí o lè wà nínú àwọn olùkìlọ̀');
INSERT INTO "aykyuni" VALUES(26,195,'26:195','pẹ̀lú èdè Lárúbáwá pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(26,196,'26:196','Dájúdájú (ọ̀rọ̀ nípa rẹ̀) wà nínú ìpín-ìpín Tírà àwọn ẹni àkọ́kọ́.');
INSERT INTO "aykyuni" VALUES(26,197,'26:197','Ṣé kò jẹ́ àmì kan fún wọn pé àwọn onímọ̀ (nínú) àwọn ọmọ ’Isrọ̄’īl nímọ̀ (sí àsọọ́lẹ̀ nípa) rẹ̀ (nínú tírà ọwọ́ wọn)?1 ');
INSERT INTO "aykyuni" VALUES(26,198,'26:198','Tí ó bá jẹ́ pé A sọ̀ ọ́ kalẹ̀ fún apá kan àwọn elédè mìíràn ni,');
INSERT INTO "aykyuni" VALUES(26,199,'26:199','tí ó sì ké e fún wọn, wọn kò níí gbà á gbọ́ ní òdodo.');
INSERT INTO "aykyuni" VALUES(26,200,'26:200','Báyẹn ni A ṣe mú (àtakò) wọ inú ọkàn àwọn ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(26,201,'26:201','Wọn kò níí gbà á gbọ́ ní òdodo títí wọn yóò fi rí ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(26,202,'26:202','(Ìyà náà) yóò dé bá wọn ní òjijì; wọn kò sì níí fura.');
INSERT INTO "aykyuni" VALUES(26,203,'26:203','Nígbà náà ni wọn yóò wí pé: “Ǹjẹ́ wọ́n lè lọ́ wa lára bí?”');
INSERT INTO "aykyuni" VALUES(26,204,'26:204','Ṣé ìyà Wa ni wọ́n ń wá pẹ̀lú ìkánjú?1 ');
INSERT INTO "aykyuni" VALUES(26,205,'26:205','Sọ fún Mi tí A bá fún wọn ní ìgbádùn ayé fún ọdún gbọọrọ!');
INSERT INTO "aykyuni" VALUES(26,206,'26:206','Lẹ́yìn náà, kí ohun tí À ń ṣe ní ìlérí fún wọn dé bá wọn.');
INSERT INTO "aykyuni" VALUES(26,207,'26:207','Ohun tí A ṣe ní ìgbádùn fún wọn kò sì níí rọ̀ wọ́n lọ́rọ̀.');
INSERT INTO "aykyuni" VALUES(26,208,'26:208','A ò sì pa ìlú kan run àfi kí wọ́n ti ní àwọn olùkìlọ̀.');
INSERT INTO "aykyuni" VALUES(26,209,'26:209','Ìrántí (dé fún wọn sẹ́). Àti pé Àwa kì í ṣe alábòsí.');
INSERT INTO "aykyuni" VALUES(26,210,'26:210','Kì í ṣe àwọn èṣù ni wọ́n sọ (al-Ƙur’ān) kalẹ̀.');
INSERT INTO "aykyuni" VALUES(26,211,'26:211','Kò yẹ fún wọn. Wọn kò sì lágbára rẹ̀.');
INSERT INTO "aykyuni" VALUES(26,212,'26:212','Dájúdájú A ti mú wọn takété sí gbígbọ́ rẹ̀ (láti ojú sánmọ̀).');
INSERT INTO "aykyuni" VALUES(26,213,'26:213','Nítorí náà, má ṣe pe ọlọ́hun mìíràn mọ́ Allāhu, nítorí kí ìwọ má baà wà nínú àwọn tí A máa jẹ níyà.');
INSERT INTO "aykyuni" VALUES(26,214,'26:214','Kí o sì kìlọ̀ fún àwọn ẹbí rẹ tó súnmọ́ jùlọ.');
INSERT INTO "aykyuni" VALUES(26,215,'26:215','Kí o sì rẹ apá rẹ nílẹ̀ fún àwọn tó tẹ̀lé ọ nínú àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(26,216,'26:216','Tí wọ́n bá sì yapa (àṣẹ) rẹ, sọ pé: “Dájúdájú èmi yọwọ́ yọsẹ̀ nínú ohun tí ẹ̀ ń ṣe (níṣẹ́ aburú).”');
INSERT INTO "aykyuni" VALUES(26,217,'26:217','Kí o sì gbáralé Alágbára, Àṣàkẹ́-ọ̀run;');
INSERT INTO "aykyuni" VALUES(26,218,'26:218','Ẹni tó ń rí ọ nígbà tí ò ń dìde nàró (fún ìrun kíkí)');
INSERT INTO "aykyuni" VALUES(26,219,'26:219','àti ìyírapadà rẹ (fún rukuu àti ìforíkanlẹ̀) láààrin àwọn olùforíkanlẹ̀.');
INSERT INTO "aykyuni" VALUES(26,220,'26:220','Dájúdájú Òun mà ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(26,221,'26:221','Ṣé kí n̄g sọ ẹni tí àwọn èṣù ń sọ̀kalẹ̀ wá bá fún yín?');
INSERT INTO "aykyuni" VALUES(26,222,'26:222','Wọ́n ń sọ̀kalẹ̀ wá bá gbogbo àwọn òpùrọ́, ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(26,223,'26:223','(Àwọn èṣù) ń dẹtí (sí ìró sánmọ̀). Ọ̀pọ̀lọ́pọ̀ wọn sì ni òpùrọ́.');
INSERT INTO "aykyuni" VALUES(26,224,'26:224','Àwọn eléwì, àwọn olùṣìnà l’ó ń tẹ̀lé wọn.');
INSERT INTO "aykyuni" VALUES(26,225,'26:225','Ṣé o ò rí i pé gbogbo ọ̀gbun ọ̀rọ̀ ni wọ́n ń tẹnu bọ̀ ni?');
INSERT INTO "aykyuni" VALUES(26,226,'26:226','Àti pé dájúdájú wọ́n ń sọ ohun tí wọn kò níí ṣe.');
INSERT INTO "aykyuni" VALUES(26,227,'26:227','Àfi àwọn tó gbàgbọ́ ní òdodo, tí wọ́n ṣe iṣẹ́ rere, tí wọ́n rántí Allāhu ní ọ̀pọ̀lọ́pọ̀, tí wọ́n sì jàjà gbara lẹ́yìn tí wọ́n ti ṣàbòsí sí wọn. Àwọn tó ṣàbòsí sì máa mọ irú ìkángun tí wọ́n máa gúnlẹ̀ sí.');
INSERT INTO "aykyuni" VALUES(27,1,'27:1','Tọ̄ sīn. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)Ìwọ̀nyí ni àwọn āyah al-Ƙur’ān àti (àwọn āyah) Tírà tó ń yanjú ọ̀rọ̀ ẹ̀dá. ');
INSERT INTO "aykyuni" VALUES(27,2,'27:2','(Ó jẹ́) ìmọ̀nà àti ìró ìdùnnú fún àwọn onígbàgbọ́ òdodo,');
INSERT INTO "aykyuni" VALUES(27,3,'27:3','àwọn tó ń kírun, tí wọ́n ń yọ zakāh. Àwọn sì ni wọ́n ní àmọ̀dájú nípa Ọjọ́ Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(27,4,'27:4','Dájúdájú àwọn tí kò gba Ọjọ́ Ìkẹ́yìn gbọ́, A ti ṣe àwọn iṣẹ́ (aburú ọwọ́) wọn ní ọ̀ṣọ́ fún wọn, Nítorí náà, wọn yó sì máa pa rìdàrìdà.');
INSERT INTO "aykyuni" VALUES(27,5,'27:5','Àwọn wọ̀nyẹn ni àwọn tí ìyà burúkú wà fún. Àwọn sì ni ẹni òfò jùlọ ní Ọjọ́ Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(27,6,'27:6','Dájúdájú ò ń gba al-Ƙur’ān làti ọ̀dọ̀ Ọlọ́gbọ́n, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(27,7,'27:7','(Rántí) nígbà tí (Ànábì) Mūsā sọ fún àwọn ará ilé rẹ̀ pé: “Dájúdájú mo rí iná kan. Mo sì máa lọ mú ìró kan wá fún yín láti ibẹ̀, tàbí kí n̄g mú ògúnná kan tó ń tanná wá fún yín nítorí kí ẹ lè yẹ́ná.');
INSERT INTO "aykyuni" VALUES(27,8,'27:8','Nígbà tí ó dé ibẹ̀, A pèpè pé kí ìbùkún máa bẹ fún ẹni tí ó wà níbi iná náà àti ẹni tí ó wà ní àyíká rẹ̀. Mímọ́ sì ni fún Allāhu, Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(27,9,'27:9','Mūsā, dájúdájú (ẹni tí ó ń bá ọ sọ̀rọ̀), Èmi ni Allāhu, Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(27,10,'27:10','Ju ọ̀pá rẹ sílẹ̀. (Ó sì ṣe bẹ́ẹ̀.) Àmọ́ nígbà tí ó rí i tó ń mira bí ẹni pé ejò ni, Mūsā pẹ̀yìndà, ó ń sá lọ. Kò sì padà sẹ́yìn. (Allāhu sọ pé:) Mūsā, má ṣe páyà. Dájúdájú àwọn Òjíṣẹ́ kì í páyà lọ́dọ̀ Mi.');
INSERT INTO "aykyuni" VALUES(27,11,'27:11','Àyàfi ẹni tí ó bá ṣàbòsí,lẹ́yìn náà tí ó yí i padà sí rere lẹ́yìn aburú. Dájúdájú Èmi ni Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(27,12,'27:12','Ti ọwọ́ rẹ bọ igbá-àyà ẹ̀wù rẹ. Ó sì máa jáde ní funfun, tí kì í ṣe ti aburú. (Èyí wà) nínú àwọn àmì mẹ́sàn-án (tí o máa mú lọ) bá Fir‘aon àti àwọn ènìyàn rẹ̀. Dájúdájú wọ́n jẹ́ ìjọ òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(27,13,'27:13','Nígbà tí àwọn àmì Wa tó fojú hàn kedere sì dé bá wọn, wọ́n wí pé: “Èyí ni idán pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(27,14,'27:14','Wọ́n fi àbòsí àti ìgbéraga takò ó, ẹ̀mí wọn sì ní àmọ̀dájú pé òdodo ni. Nítorí náà, wòye sí bí àtubọ̀tán àwọn òbìlẹ̀jẹ́ ti rí.');
INSERT INTO "aykyuni" VALUES(27,15,'27:15','Dájúdájú A fún (Ànábì) Dāwūd àti (Ànábì) Sulaemọ̄n ní ìmọ̀. Àwọn méjèèjì sì sọ pé: “Gbogbo ẹyìn ń jẹ́ ti Allāhu, Ẹni tí Ó fún wa ní oore àjùlọ lórí ọ̀pọ̀lọ́pọ̀ nínú àwọn ẹrúsìn Rẹ̀, àwọn onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(27,16,'27:16','(Ànábì) Sulaemọ̄n sì jogún (Ànábì) Dāwūd. Ó sọ pé: “Ẹ̀yin ènìyàn, Wọ́n fi ohùn ẹyẹ mọ̀ wá. Wọ́n sì fún wa ní gbogbo n̄ǹkan.Dájúdájú èyí, òhun ni oore àjùlọ pọ́nńbélé.” ');
INSERT INTO "aykyuni" VALUES(27,17,'27:17','A sì kó jọ fún (Ànábì) Sulaemọ̄n, àwọn ọmọ ogun rẹ̀ nínú àlùjànnú, ènìyàn àti ẹyẹ. A sì ń kó wọn jọ papọ̀ mọ́ra wọn sí ọ̀dọ̀ rẹ̀ tí wọ́n ń bá a rìn káàkiri');
INSERT INTO "aykyuni" VALUES(27,18,'27:18','títí wọ́n fi dé àfonúfojì àwọn àwúrèbe. Àwúrèbe kan sì wí pé: “Ẹ̀yin àwúrèbe, ẹ wọ inú ilé yín lọ, kí (Ànábì) Sulaemọ̄n àti àwọn ọmọ ogun rẹ̀ má baà tẹ̀ yín rẹ́ mọ́lẹ̀. Wọn kò sì níí furamọ̀.”');
INSERT INTO "aykyuni" VALUES(27,19,'27:19','Nígbà náà, (Ànábì Sulaemọ̄n) rẹ́rìn-ín músẹ́, ẹ̀rín pa á nítorí ọ̀rọ̀ (àwúrèbe náà). Ó sọ pé: “Olúwa mi, fi mọ̀ mí bí mo ṣe máa dúpẹ́ ìdẹ̀ra Rẹ, èyí tí O fi ṣe ìdẹ̀ra fún èmi àti àwọn òbí mi méjèèjì. (Fi mọ̀ mí) bí mo ṣe máa ṣe iṣẹ́ rere, èyí tí O yọ́nú sí. Pẹ̀lú àánú Rẹ, fi mí sáààrin àwọn ẹrúsìn Rẹ, àwọn ẹni rere.”');
INSERT INTO "aykyuni" VALUES(27,20,'27:20','Ó yẹ àwọn ẹyẹ wò, ó sì sọ pé: “Nítorí kí ni èmi kò ṣe rí ẹyẹ hudhuda? Àbí ó jẹ́ ọ̀kan nínú àwọn tí kò wá ni?');
INSERT INTO "aykyuni" VALUES(27,21,'27:21','Dájúdájú mo máa jẹ ẹ́ níyà líle, tàbí kí n̄g dúńbú rẹ̀, tàbí kí ó mú ẹ̀rí tó yanjú wá fún mi.”');
INSERT INTO "aykyuni" VALUES(27,22,'27:22','(Ànábì Sulaemọ̄n) kò dúró pẹ́ (títí ó fi dé), ó sì wí pé: “Mo mọ n̄ǹkan tí o ò mọ̀. Mo sì mú ìró kan tó dájú wá bá ọ láti ìlú Saba’i.');
INSERT INTO "aykyuni" VALUES(27,23,'27:23','Dájúdájú èmi bá obìnrin kan (ní ìlú Saba’i), tó ń jọba lé wọn lórí. Wọ́n fún un ní gbogbo n̄ǹkan. Ó sì ní ìtẹ́ ńlá kan.');
INSERT INTO "aykyuni" VALUES(27,24,'27:24','Mo sì bá òun àti àwọn ènìyàn rẹ̀ tí wọ́n ń forí kanlẹ̀ fún òòrùn lẹ́yìn Allāhu. Èṣù sì ṣe àwọn iṣẹ́ wọn ní ọ̀ṣọ́ fún wọn. Ó ṣẹ́rí wọn kúrò lójú ọ̀nà (òdodo). Nítorí náà, wọn kò sì mọ̀nà.');
INSERT INTO "aykyuni" VALUES(27,25,'27:25','(Èṣù ṣe àwọn iṣẹ́ wọn ní ọ̀ṣọ́ fún wọn) torí kí wọ́n má lè forí kanlẹ̀ fún Allāhu, Ẹni tó ń mú ohùn tó pamọ́ sínú àwọn sánmọ̀ àti ilẹ̀ jáde, Ó sì mọ ohun tí ẹ̀ ń fi pamọ́ àti ohun tí ẹ̀ ń ṣàfi hàn.');
INSERT INTO "aykyuni" VALUES(27,26,'27:26','Allāhu, kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun, Olúwa Ìtẹ́ ńlá.”');
INSERT INTO "aykyuni" VALUES(27,27,'27:27','Ó sọ pé: “A máa wòye bóyá òdodo l’o sọ tàbí o wà nínú àwọn òpùrọ́.');
INSERT INTO "aykyuni" VALUES(27,28,'27:28','Mú ìwé mi yìí lọ, kí o jù ú sí wọn. Lẹ́yìn náà, kí o takété sí wọn, kí o sì wo ohun tí wọn yóò dá padà (ní èsì).”');
INSERT INTO "aykyuni" VALUES(27,29,'27:29','(Bilƙīs) sọ pé: “Ẹ̀yin ìjòyè, dájúdájú wọ́n ti ju ìwé alápọ̀n-ọ́nlé kan sí mi.');
INSERT INTO "aykyuni" VALUES(27,30,'27:30','Dájúdájú ó wá láti ọ̀dọ̀ (Ànábì) Sulaemọ̄n. Dájúdájú ó (sọ pé) “Ní orúkọ Allāhu, Àjọkẹ́-ayé, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(27,31,'27:31','Kí ẹ má ṣègbéraga sí mi, kí ẹ sì wá sí ọ̀dọ̀ mi (láti di) mùsùlùmí.”');
INSERT INTO "aykyuni" VALUES(27,32,'27:32','(Bilƙīs) sọ pé: “Ẹ̀yin ìjòyè, ẹ gbà mí ní ìmọ̀ràn nípa ọ̀rọ̀ mi. Èmi kì í gbé ìgbésẹ̀ lórí ọ̀rọ̀ kan títí ẹ máa fi jẹ́rìí sí i.”');
INSERT INTO "aykyuni" VALUES(27,33,'27:33','Wọ́n wí pé: “Alágbára ni àwa. Akọni ogun sì tún ni wá. Ọ̀dọ̀ rẹ ni ọ̀rọ̀ wà. Nítorí náà, wòye sí ohun tí o máa pa láṣẹ.”');
INSERT INTO "aykyuni" VALUES(27,34,'27:34','(Bilƙīs) wí pé: “Dájúdájú àwọn ọba, nígbà tí wọ́n bá wọ inú ìlú kan, wọn yóò bà á jẹ́. Wọn yó sì sọ àwọn ẹni abiyì ibẹ̀ di ẹni yẹpẹrẹ. Báyẹn ni wọ́n máa ń ṣe.');
INSERT INTO "aykyuni" VALUES(27,35,'27:35','Dájúdájú mo máa fi ẹ̀bùn kan ránṣẹ́ sí wọn. Mo sì máa wo ohun tí àwọn ìránṣẹ́ yóò mú padà (ní èsì).”');
INSERT INTO "aykyuni" VALUES(27,36,'27:36','Nígbà tí (ìránṣẹ́) dé ọ̀dọ̀ (Ànábì) Sulaemọ̄n, ó sọ pé: “Ṣé ẹ máa fi owó ràn mí lọ́wọ́ ni? Ohun tí Allāhu fún mi lóore jùlọ sí ohun tí ẹ fún mi. Síbẹ̀síbẹ̀, ẹ̀yin tún ń yọ̀ lórí ẹ̀bùn yín.');
INSERT INTO "aykyuni" VALUES(27,37,'27:37','Padà sí ọ̀dọ̀ wọn. Dájúdájú à ń bọ̀ wá bá wọn pẹ̀lú àwọn ọmọ ogun tí wọn kò lè kojú rẹ̀. Dájúdájú a máa mú wọn jáde kúrò nínú (ìlú wọn) ní ẹni yẹpẹrẹ, tí wọn yó sì di ẹni àbùkù.”');
INSERT INTO "aykyuni" VALUES(27,38,'27:38','(Ànábì Sulaemọ̄n) sọ pé: “Ẹ̀yin ìjòyè, èwo nínú yín l’ó máa gbé ìtẹ́ (Bilƙīs) wá bá mi, ṣíwájú kí wọ́n tó wá bá mi (láti di) mùsùlùmí.”');
INSERT INTO "aykyuni" VALUES(27,39,'27:39','‘Ifrīt nínú àwọn àlùjànnú sọ pé: “Èmi yóò gbe wá fún ọ ṣíwájú kí o tó dìde kúrò ní ibùjókòó rẹ. Dájúdájú èmi ni alágbára, olùfọkàntán lórí rẹ̀.”');
INSERT INTO "aykyuni" VALUES(27,40,'27:40','Ẹni tí ìmọ̀ kan láti inú tírà wà ní ọ̀dọ̀ rẹ̀ sọ pé: “Èmi yóò gbé e wá fún ọ ṣíwájú kí o tó ṣẹ́jú.” Nígbà tí ó rí i tí ó dé sí ọ̀dọ̀ rẹ̀, ó sọ pé: “Èyí wà nínú oore àjùlọ Olúwa mi láti fi dán mi wò bóyá mo máa dúpẹ́ tàbí mo máa ṣàì moore. Ẹnikẹ́ni tí ó bá dúpẹ́ (fún Allāhu), ó dúpẹ́ fún ẹ̀mí ara rẹ̀. Ẹnikẹ́ni tí ó bá sì ṣàìmoore, dájúdájú Olúwa mi ni Ọlọ́rọ̀, Alápọ̀n-ọ́nlé.”');
INSERT INTO "aykyuni" VALUES(27,41,'27:41','Ó sọ pé: “Ẹ pa á lójú dà fún un sí ìrísí mìíràn tí kò fi níí dá a mọ̀, kí á wò ó bóyá ó máa dá a mọ̀ tàbí ó máa wà nínú àwọn tí kò níí dá a mọ̀.”');
INSERT INTO "aykyuni" VALUES(27,42,'27:42','Nígbà tí (Bilƙīs) dé, wọ́n sọ fún un pé: “Ṣé bí ìtẹ́ rẹ ṣe rí nìyí?” Ó wí pé: "Ó dà bí ẹni pé òhun ni." (Ànábì Sulaemọ̄n sì sọ pé): “Wọ́n ti fún wa ní ìmọ̀ ṣíwájú Bilƙīs. A sì jẹ́ mùsùlùmí.”');
INSERT INTO "aykyuni" VALUES(27,43,'27:43','Ohun tó ń jọ́sìn fún lẹ́yìn Allāhu sì ṣẹ́rí rẹ̀ (kúrò níbi jíjọ́sìn fún Allāhu). Dájúdájú ó wà nínú ìjọ aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(27,44,'27:44','Wọ́n sọ fún un pé: “Wọ inú ààfin.” Nígbà tí ó rí i, ó ṣe bí ibúdò ni. Ó sì ká aṣọ kúrò ní ojúgun rẹ̀ méjèèjì. (Ànábì Sulaemọ̄n) sọ pé: “Dájúdájú ààfin tí a ṣe rigidin pẹ̀lú díńgí ni.” (Bilƙīs) sọ pé: “Olúwa mi, dájúdájú mo ṣàbòsí sí ẹ̀mí ara mi. Mo sì di mùsùlùmí pẹ̀lú (Ànábì) Sulaemọ̄n, fún Allāhu, Olúwa gbogbo ẹ̀dá.”');
INSERT INTO "aykyuni" VALUES(27,45,'27:45','Dájúdájú A ti ránṣẹ́ sí ìjọ Thamūd. (A rán) arákùnrin wọn, Sọ̄lih (níṣẹ́ sí wọn), pé: “Ẹ jọ́sìn fún Allāhu.” Nígbà náà ni wọ́n pín sí ìjọ méjì, tó ń bára wọn jiyàn.');
INSERT INTO "aykyuni" VALUES(27,46,'27:46','Ó sọ pé: “Ẹ̀yin ìjọ mi, nítorí kí ni ẹ ṣe ń wá (ìyà) aburú pẹ̀lú ìkánjú ṣíwájú (ohun) rere (tó yẹ kí ẹ mú ṣe níṣẹ́)? Ẹ kò ṣe tọrọ àforíjìn lọ́dọ̀ Allāhu nítorí kí Wọ́n lè ṣàánú yín?”');
INSERT INTO "aykyuni" VALUES(27,47,'27:47','Wọ́n wí pé: “A rí àmì aburú láti ọ̀dọ̀ rẹ àti àwọn tó wà pẹ̀lú rẹ.” (Ànábì Sọ̄lih) sọ pé: “Àmì aburú yín wà (nínú kádàrá yín) lọ́dọ̀ Allāhu. Kò sì rí bí ẹ ṣe rò ó àmọ́ ìjọ aládàn-ánwò ni yín.”');
INSERT INTO "aykyuni" VALUES(27,48,'27:48','Àwọn ènìyàn mẹ́sàn-án kan sì wà nínú ìlú, tí wọ́n ń ṣèbàjẹ́ lórí ilẹ̀, tí wọn kò sì ṣàtúnṣe.');
INSERT INTO "aykyuni" VALUES(27,49,'27:49','Wọ́n wí (fúnra wọn) pé: “Kí á dìjọ fí Allāhu búra pé dájúdájú a máa lọ bá òun àti àwọn ènìyàn rẹ̀ ní alẹ́ (láti pa á). Lẹ́yìn náà, dájúdájú a máa sọ fún ẹbí rẹ̀ pé ìparun àwọn ènìyàn rẹ̀ kò ṣojú wa. Dájúdájú olódodo sì ni àwa.”');
INSERT INTO "aykyuni" VALUES(27,50,'27:50','Wọ́n déte gan-an, Àwa náà sì déte gan-an, wọn kò sì fura.1 ');
INSERT INTO "aykyuni" VALUES(27,51,'27:51','Wo bí àtubọ̀tán ète wọn ti rí. Dájúdájú A pa àwọn àti ìjọ wọn run pátápátá.');
INSERT INTO "aykyuni" VALUES(27,52,'27:52','Nítorí náà, ìwọ̀nyẹn ni ilé wọn. Ó tí di ilé ahoro nítorí pé wọ́n ṣàbòsí. Dájúdájú àmì wà nínú ìyẹn fún ìjọ tó nímọ̀.');
INSERT INTO "aykyuni" VALUES(27,53,'27:53','A gba àwọn tó gbàgbọ́ ní òdodo là, tí wọ́n sì ń bẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(27,54,'27:54','(A tún fi iṣẹ́ ìmísí rán Ànábì) Lūt. (Rántí) nígbà tí ó sọ fún ìjọ rẹ̀ pé: “Ṣé iṣẹ́ aburú ni ẹ óò máa ṣe ni, ẹ sì ríran?');
INSERT INTO "aykyuni" VALUES(27,55,'27:55','Ṣé dájúdájú ẹ̀yin (ọkùnrin) yóò máa lọ jẹ adùn (ìbálòpọ̀) lára àwọn ọkùnrin (ẹgbẹ́ yín ni) dípò àwọn obìnrin? Àní sẹ́, òpè ènìyàn ni yín.”');
INSERT INTO "aykyuni" VALUES(27,56,'27:56','Kò sí ohun kan tí ìjọ rẹ̀ fọ̀ ní èsì tayọ pé wọ́n wí pé: “Ẹ lé àwọn ènìyàn Lūt jáde kúrò nínú ìlú yín. Dájúdájú wọ́n jẹ́ ènìyàn tó ń fọra wọn mọ́ (níbi ẹ̀ṣẹ̀).”');
INSERT INTO "aykyuni" VALUES(27,57,'27:57','Nítorí náà, A gba òun àti ẹbí rẹ̀ là àfi ìyàwó rẹ̀; A ti kọ ọ́ pé ó máa wà nínú àwọn tó máa ṣẹ́kù lẹ́yìn sínú ìparun.');
INSERT INTO "aykyuni" VALUES(27,58,'27:58','A rọ òjò lé wọn lórí tààrà. Òjò àwọn tí A kìlọ̀ fún sì burú.');
INSERT INTO "aykyuni" VALUES(27,59,'27:59','Sọ pé: “Gbogbo ọpẹ́ ń jẹ́ ti Allāhu. Kí àlàáfíà máa bá àwọn ẹrúsìn Rẹ̀, àwọn tí Ó ṣà lẹ́ṣà. Ṣé Allāhu l’Ó lóore jùlọ ni tàbí n̄ǹkan tí wọ́n ń sọ di akẹgbẹ́ Rẹ̀?”');
INSERT INTO "aykyuni" VALUES(27,60,'27:60','(Ṣé ìbọ̀rìṣà l’ó lóore jùlọ ni) tàbí (jíjọ́sìn fún) Ẹni tí Ó dá àwọn sánmọ̀ àti ilẹ̀, tí Ó sọ omi kalẹ̀ fún yín láti sánmọ̀, tí A sì fi omi náà hu àwọn ọgbà oko tí ó dára jáde? Kò sì sí agbára fún yín láti mu igi rẹ̀ hù jáde. Ṣé ọlọ́hun kan tún wà pẹ̀lú Allāhu ni? Rárá, ìjọ tó ń ṣẹbọ sí Allāhu ni wọ́n ni.');
INSERT INTO "aykyuni" VALUES(27,61,'27:61','(Ṣé ìbọ̀rìṣà l’ó dára jùlọ ni) tàbí (jíjọ́sìn fún) Ẹni tí Ó ṣe ilẹ̀ ní ibùgbé, tí Ó fi àwọn odò sáààrin rẹ̀, tí Ó fi àwọn àpáta gbagidi sínú ilẹ̀, tí Ó sì fi gàgá sáààrin ibúdò méjèèjì? Ṣé ọlọ́hun kan tún wà pẹ̀lú Allāhu ni? Síbẹ̀síbẹ̀ ọ̀pọ̀lọ́pọ̀ wọn ni kò mọ̀.');
INSERT INTO "aykyuni" VALUES(27,62,'27:62','(Ṣé ìbọ̀rìṣà l’ó dára jùlọ ni) tàbí (jíjọ́sìn fún) Ẹni tí Ó ń jẹ́pè ẹni tí ara ń ni nígbà tí ó bá pè É, tí Ó sì máa gbé aburú kúrò fún un, tí Ó sì ń ṣe yín ní àrólé lórí ilẹ̀? Ṣé ọlọ́hun kan tún wà pẹ̀lú Allāhu ni? Díẹ̀ ni ohun tí ẹ̀ ń lò nínú ìrántí!');
INSERT INTO "aykyuni" VALUES(27,63,'27:63','(Ṣé ìbọ̀rìṣà l’ó dára jùlọ ni) tàbí (jíjọ́sìn fún) Ẹni tí Ó ń tọ yín sọ́nà nínú àwọn òkùnkùn orí ilẹ̀ àti inú ibúdò, tí Ó tún ń fi atẹ́gùn ránṣẹ́ ní ìró ìdùnnú ṣíwájú ìkẹ́ Rẹ̀? Ṣé ọlọ́hun kan tún wà pẹ̀lú Allāhu ni? Allāhu ga tayọ n̄ǹkan tí wọ́n ń fi ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(27,64,'27:64','(Ṣé ìbọ̀rìṣà l’ó dára jùlọ ni) tàbí (jíjọ́sìn fún) Ẹni tí Ó pilẹ̀ ìṣẹ̀dá ẹ̀dá, lẹ́yìn náà, tí Ó máa dá a padà (lẹ́yìn ikú), tí Ó sì ń pèsè fún yín láti sánmọ̀ àti ilẹ̀? Ṣé ọlọ́hun kan tún wà pẹ̀lú Allāhu ni? Sọ pé: “Ẹ mú ẹ̀rí yín wá tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(27,65,'27:65','Sọ pé: “Àwọn tó wà nínú àwọn sánmọ̀ àti ilẹ̀ kò nímọ̀ ìkọ̀kọ̀ àfi Allāhu. Àti pé wọn kò sì mọ àsìkò wo ni A máa gbé àwọn òkú dìde.”');
INSERT INTO "aykyuni" VALUES(27,66,'27:66','Rárá, ìmọ̀ wọn máa kẹ́sẹjá ní ọ̀run (orí wọn máa pé ní ọ̀run nígbà tí wọ́n bá fojú rí ìyà). Síbẹ̀síbẹ̀ wọ́n wà nínú iyèméjì nípa rẹ̀ (báyìí). Àní sẹ́, wọ́n fọ́jú nípa rẹ̀ ni.');
INSERT INTO "aykyuni" VALUES(27,67,'27:67','Àwọn tó ṣàì gbàgbọ́ wí pé: “Ṣé nígbà tí àwa àti àwọn bàbá wa bá ti di erùpẹ̀, ṣé (nígbà náà ni) wọn yóò mú wa jáde?');
INSERT INTO "aykyuni" VALUES(27,68,'27:68','Wọ́n kúkú tí ṣe àdéhùn èyí fún àwa àti àwọn bàbá wa tẹ́lẹ̀tẹ́lẹ̀. Èyí kò jẹ́ kiní kan bí kò ṣe àkọsílẹ̀ àlọ́ àwọn ẹni àkọ́kọ́.”');
INSERT INTO "aykyuni" VALUES(27,69,'27:69','Sọ pé: “Ẹ rìn kiri lórí ilẹ̀, kí ẹ wo bí àtubọ̀tán àwọn ẹlẹ́ṣẹ̀ ṣe rí.”');
INSERT INTO "aykyuni" VALUES(27,70,'27:70','Má ṣe banújẹ́ nítorí wọn. Má sì jẹ́ kí ohun tí wọ́n ń dá ní ète kó ìnira bá ọ.');
INSERT INTO "aykyuni" VALUES(27,71,'27:71','Wọ́n ń wí pé: “Ìgbà wo ni àdéhùn yìí yóò ṣẹ tí ẹ bá jẹ́ olódodo?”');
INSERT INTO "aykyuni" VALUES(27,72,'27:72','Sọ pé: “Ó lè jẹ́ pé apá kan ohun tí ẹ̀ ń wá pẹ̀lú ìkánjú ti súnmọ́ yín tán.”');
INSERT INTO "aykyuni" VALUES(27,73,'27:73','Dájúdájú Olúwa rẹ mà ni Ọlọ́lá lórí àwọn ènìyàn, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn kì í dúpẹ́ (fún Un).');
INSERT INTO "aykyuni" VALUES(27,74,'27:74','Dájúdájú Olúwa rẹ, Ó kúkú mọ ohun tí igbá-àyà wọn ń fi pamọ́ àti ohun tí wọ́n ń ṣàfi hàn rẹ̀.');
INSERT INTO "aykyuni" VALUES(27,75,'27:75','Àti pé kò sí ohun ìkọ̀kọ̀ kan nínú sánmọ̀ àti ilẹ̀ àfi kí ó wà nínú àkọsílẹ̀ tó hàn kedere.');
INSERT INTO "aykyuni" VALUES(27,76,'27:76','Dájúdájú al-Ƙur’ān yìí yóò máa ṣàlàyé ọ̀rọ̀ fún àwọn ọmọ ’Isrọ̄’īl nípa ọ̀pọ̀lọ́pọ̀ ohun tí wọ́n ń yapa-ẹnu sí.');
INSERT INTO "aykyuni" VALUES(27,77,'27:77','Àti pé dájúdájú ìmọ̀nà àti ìkẹ́ ni fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(27,78,'27:78','Dájúdájú Olúwa rẹ máa ṣe ìdájọ́ láààrin wọn pẹ̀lú ìdájọ́ Rẹ̀. Òun ni Alágbára, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(27,79,'27:79','Nítorí náà, gbáralé Allāhu. Dájúdájú ìwọ wà lórí òdodo pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(27,80,'27:80','Dájúdájú ìwọ kọ́ l’o máa mú àwọn òkú gbọ́rọ̀. O ò sì níí mú àwọn adití gbọ́ ìpè nígbà tí wọ́n bá kẹ̀yìn sí ọ, tí wọ́n ń lọ.');
INSERT INTO "aykyuni" VALUES(27,81,'27:81','Ìwọ kọ́ l’o máa fi ọ̀nà mọ àwọn afọ́jú níbi ìṣìnà wọn. Kò sí ẹni tí o máa mú gbọ́ ọ̀rọ̀ àfi ẹni tí ó bá gba àwọn āyah Wa gbọ́ nítorí pé, àwọn ni (mùsùlùmí) olùjupá-jusẹ̀-sílẹ̀ fún Allāhu.1 ');
INSERT INTO "aykyuni" VALUES(27,82,'27:82','Nígbà tí ọ̀rọ̀ náà bá kò lé wọn lórí, A máa mú ẹranko kan jáde fún wọn láti inú ilẹ̀, tí ó máa bá wọn sọ̀rọ̀ pé: “Dájúdájú àwọn ènìyàn kì í ní ìmọ̀ àmọ̀dájú nípa àwọn āyah Wa.”');
INSERT INTO "aykyuni" VALUES(27,83,'27:83','Àti pé (rántí) ọjọ́ tí A óò kó ìjọ kan jọ nínú ìjọ kọ̀ọ̀kan nínú àwọn tó ń pe àwọn āyah Wa ní irọ́. A ó sì kó wọn jọ papọ̀ mọ́ra wọn tí wọ́n máa tẹ̀lé ara wọn.');
INSERT INTO "aykyuni" VALUES(27,84,'27:84','Títí (di) ìgbà tí wọ́n bá dé (tán), (Allāhu) yóò sọ pé: “Ṣé ẹ pe àwọn āyah Mi ní irọ́, tí ẹ̀yin kò sì ní ìmọ̀ kan nípa rẹ̀ (tí ẹ lè fi já a nírọ́)? Tàbí kí ni ẹ̀ ń ṣe níṣẹ́ (nílé ayé)?”');
INSERT INTO "aykyuni" VALUES(27,85,'27:85','Ọ̀rọ̀ náà sì kò lé wọn lórí nítorí pé wọ́n ṣàbòsí. Wọn kò sì níí sọ̀rọ̀.');
INSERT INTO "aykyuni" VALUES(27,86,'27:86','Ṣé wọn kò wòye pé dájúdájú Àwa l’A dá òru nítorí kí wọ́n lè sinmi nínú rẹ̀, (A sì dá) ọ̀sán (nítorí kí wọ́n lè) ríran? Dájúdájú àmì wà nínú ìyẹn fún ìjọ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(27,87,'27:87','(Rántí) ọjọ́ tí wọ́n á fọn fèrè oníwo, gbogbo ẹni tí ó wà nínú sánmọ̀ àti ilẹ̀ yó sì jáyà tó máa mú wọn kú àfi ẹni tí Allāhu bá fẹ́. Gbogbo ẹ̀dá l’ó sì máa yẹpẹrẹ wá bá Allāhu.');
INSERT INTO "aykyuni" VALUES(27,88,'27:88','O sì máa rí àwọn àpáta, tí o lérò pé n̄ǹkan gbagidi tó dúró sojú kan náà ni, tí ó máa rin ìrìn ẹ̀ṣújò.(Ìyẹn jẹ́) iṣẹ́ Allāhu, Ẹni tí Ó ṣe gbogbo n̄ǹkan dáadáa. Dájúdájú Alámọ̀tán ni nípa ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(27,89,'27:89','Ẹnikẹ́ni tí ó bá dé pẹ̀lú iṣẹ́ rere, tirẹ̀ ni rere tó lóore ju èyí tó ṣe. Àwọn sì ni olùfàyàbalẹ̀ nínú ìjáyà ọjọ́ yẹn.');
INSERT INTO "aykyuni" VALUES(27,90,'27:90','Ẹnikẹ́ni tí ó bá sì dé pẹ̀lú iṣẹ́ aburú, A sì máa da ojú wọn bolẹ̀ nínú Iná. Ǹjẹ́ A óò san yín ní ẹ̀san kan bí kò ṣe ohun tí ẹ̀ ń ṣe níṣẹ́?');
INSERT INTO "aykyuni" VALUES(27,91,'27:91','Ohun tí wọ́n pa mí ní àṣẹ rẹ̀ ni pé kí n̄g jọ́sìn fún Olúwa Ìlú yìí, Ẹni tí Ó ṣe é ní àyè ọ̀wọ̀. TiRẹ̀ sì ni gbogbo n̄ǹkan. Wọ́n sì pa mí ní àṣẹ pé kí èmi wà nínú àwọn mùsùlùmí.');
INSERT INTO "aykyuni" VALUES(27,92,'27:92','Àti pé kí èmi máa ké al-Ƙur’ān. Nítorí náà, ẹnikẹ́ni tí ó bá mọ̀nà, ó mọ̀nà fún ẹ̀mí ara rẹ̀. Ẹnikẹ́ni tí ó bá sì ṣìnà, sọ pé: “Dájúdájú èmi wà nínú àwọn olùkìlọ̀.”');
INSERT INTO "aykyuni" VALUES(27,93,'27:93','Sọ pé: “Gbogbo ẹyìn ń jẹ́ ti Allāhu.” (Allāhu) yóò fi àwọn àmì Rẹ̀ hàn yín. Ẹ̀yin yó sì mọ̀ wọ́n. Olúwa rẹ kò sì gbàgbé ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(28,1,'28:1','Tọ̄ sīn mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(28,2,'28:2','Ìwọ̀nyí ni àwọn āyah Tírà tó ń yanjú ọ̀rọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(28,3,'28:3','À ń ké nínú ìró (Ànábì) Mūsā àti Fir‘aon fún ọ pẹ̀lú òdodo nítorí ìjọ tó gbàgbọ́ lódodo.');
INSERT INTO "aykyuni" VALUES(28,4,'28:4','Dájúdájú Fir‘aon ṣègbéraga lórí ilẹ̀. Ó ṣe àwọn ènìyàn rẹ̀ ní ìjọ-ìjọ; Ó ń sọ igun kan nínú wọn di ọ̀lẹ, ó ń pa àwọn ọmọkùnrin wọn, ó sì ń dá àwọn obìnrin wọn sí. Dájúdájú ó wà nínú àwọn òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(28,5,'28:5','A sì fẹ́ ṣèdẹ̀ra fún àwọn tí wọ́n sọ di ọ̀lẹ lórí ilẹ̀, A (fẹ́) ṣe wọ́n ní aṣíwájú. A sì (fẹ́) kí wọ́n jogún (ilẹ̀).');
INSERT INTO "aykyuni" VALUES(28,6,'28:6','A (fẹ́) gbà wọ́n láàyè lórí ilẹ̀. Láti ara wọn, A sì (fẹ́) fi han Fir‘aon àti Hāmọ̄n pẹ̀lú àwọn ọmọ ogun àwọn méjèèjì ohun tí wọ́n ń ṣọ́ra fún lára wọn.');
INSERT INTO "aykyuni" VALUES(28,7,'28:7','A sì ránṣẹ́ sí ìyá (Ànábì) Mūsā pé: “Fún un ní ọmú mu. Tí o bá sì ń páyà lórí rẹ̀, jù ú sínú odò. Má ṣe bẹ̀rù. Má sì ṣe banújẹ́. Dájúdájú Àwa máa dá a padà sí ọ̀dọ̀ rẹ. A ó sì ṣe é ní ara àwọn Òjíṣẹ́.”');
INSERT INTO "aykyuni" VALUES(28,8,'28:8','Nígbà náà, àwọn ènìyàn Fir‘aon rí i he nítorí kí ó lè jẹ́ ọ̀tá àti ìbànújẹ́ fún wọn. Dájúdájú Fir‘aon, Hāmọ̄n àti àwọn ọmọ ogun àwọn méjèèjì, wọ́n jẹ́ aláṣìṣe.');
INSERT INTO "aykyuni" VALUES(28,9,'28:9','Ìyàwó Fir‘aon wí pé: “Ìtutù-ojú ni (ọmọ yìí) fún èmi àti ìwọ. Ẹ má ṣe pa á. Ó lè jẹ́ pé ó máa ṣe wá ní àǹfààní tàbí kí a fi ṣe ọmọ.” Wọn kò sì fura.');
INSERT INTO "aykyuni" VALUES(28,10,'28:10','Ọkàn ìyá (Ànábì) Mūsā pami (lórí ọ̀rọ̀ Mūsā, kò sì rí n̄ǹkan mìíràn rò mọ́ tayọ rẹ̀) ó kúkú fẹ́ẹ̀ ṣàfi hàn rẹ̀ (pé ọmọ òun ni nígbà tí wọ́n pè é dé ọ̀dọ̀ Fir‘aon) tí kò bá jẹ́ pé A kì í lọ́kàn nítorí kí ó lè wà nínú àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(28,11,'28:11','Ó sì sọ fún arábìnrin rẹ̀ pé: “Tọ ipa rẹ̀ lọ.” Ó sì ń wò ó láti ibi tó jìnnà. Wọn kò sì fura (sí i).');
INSERT INTO "aykyuni" VALUES(28,12,'28:12','A kò sì jẹ́ kí ó mu ọyàn (mìíràn) ṣíwájú (tìyá rẹ̀.) (Arábìnrin rẹ̀ sì) wí pé: “Ṣé kí èmi júwe yín sí ará ilé kan tí ó máa gbà á tọ́ fún yín? Wọn yó sì jẹ́ olùtọ́jú rẹ̀.”');
INSERT INTO "aykyuni" VALUES(28,13,'28:13','A sì dá a padà sí ọ̀dọ̀ ìyá rẹ̀ nítorí kí ojú rẹ̀ lè tutù (fún ìdùnnú) àti nítorí kí ó má baà banújẹ́. Àti pé nítorí kí ó lè mọ̀ pé dájúdájú àdéhùn Allāhu, òdodo ni, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò mọ̀.');
INSERT INTO "aykyuni" VALUES(28,14,'28:14','Nígbà tí ó dàgbà dáadáa, tí ó di géńdé, A fún un ní ọgbọ́n àti ìmọ̀. Báyẹn ni A ṣe ń san àwọn olùṣe-rere ní ẹ̀san (rere).');
INSERT INTO "aykyuni" VALUES(28,15,'28:15','Ó wọ inú ìlú nígbà tí àwọn ará ìlú ti gbàgbé (nípa ọ̀rọ̀ rẹ̀).Ó rí àwọn ọkùnrin méjì kan tí wọ́n ń bara wọn jà. Èyí wá láti inú ìran rẹ̀. Èyí sì wá láti (inú ìran) ọ̀tá rẹ̀. Èyí tí ó wá láti inú ìran rẹ̀ wá ìrànlọ́wọ́ rẹ̀ lórí èyí tí ó wá láti (inú ìran) ọ̀tá rẹ̀. Mūsā kàn án ní ẹ̀ṣẹ́. Ó sì pa á. Ó sọ pé: “Èyí wà nínú iṣẹ́ Èṣù. Dájúdájú (èṣù) ni ọ̀tá aṣini-lọ́nà pọ́nńbélé.” ');
INSERT INTO "aykyuni" VALUES(28,16,'28:16','Ó sọ pé: “Olúwa mi, dájúdájú èmi ti ṣàbòsí sí ẹ̀mí ara mi. Nítorí náà, foríjìn mí.” Ó sì foríjìn ín. Dájúdájú Allāhu, Òun ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(28,17,'28:17','Ó sọ pé: “Olúwa mi, fún wí pé O ti ṣe ìkẹ́ fún mi, èmi kò níí jẹ́ olùrànlọ́wọ́ fún àwọn ẹlẹ́ṣẹ̀.”');
INSERT INTO "aykyuni" VALUES(28,18,'28:18','Nítorí náà, ó di ẹni tó ń bẹ̀rù nínú ìlú, ó sì ń retí (ẹ̀hónú ìran Fir‘aon). Nígbà náà ni ẹni tí ó wá ìrànlọ́wọ́ rẹ̀ ní àná tún ń lọgun rẹ̀ fún ìrànlọ́wọ́ rẹ̀. Mūsā sọ fún un pé: “Dájúdájú ìwọ ni olùṣìnà pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(28,19,'28:19','Àmọ́ nígbà tí ó fẹ́ gbá ẹni tí ó jẹ́ ọ̀tá fún àwọn méjèèjì mú, ẹni náà wí pé: “Mūsā, ṣé o fẹ́ pa mí bí ó ṣe pa ẹnì kan ní àná? O ò gbèrò kan tayọ kí o jẹ́ aláìlójú-àánú lórí ilẹ̀; o ò sì gbèrò láti wà nínú àwọn alátùn-únṣe.”');
INSERT INTO "aykyuni" VALUES(28,20,'28:20','Ọkùnrin kan sáré dé láti òpin ìlú náà, ó wí pé: “Mūsā, dájúdájú àwọn ìjòyè ń dá ìmọ̀ràn lórí rẹ láti pa ọ́. Nítorí náà, jáde (kúrò nínú ìlú), dájúdájú èmi wà nínú àwọn onímọ̀ràn rere fún ọ.”');
INSERT INTO "aykyuni" VALUES(28,21,'28:21','Ó sì jáde kúrò nínú (ìlú) pẹ̀lú ìpáyà, tí ó ń retí (ẹ̀hónú wọn), ó sì sọ pé: “Olúwa mi, là mí lọ́wọ́ ìjọ alábòsí.”');
INSERT INTO "aykyuni" VALUES(28,22,'28:22','Nígbà tí ó sì dojú kọ ọ̀kánkán ìlú Mọdyan, ó sọ pé: “Ó súnmọ́ kí Olúwa mi fọ̀nà tààrà (sí ìlú Mọdyan) mọ̀ mí.”');
INSERT INTO "aykyuni" VALUES(28,23,'28:23','Nígbà tí ó dé ibi (kànǹga) omi (ìlú) Mọdyan, ó bá ìjọ ènìyàn kan níbẹ̀ tí wọ́n ń fún àwọn ẹran-ọ̀sìn wọn ní omi mu. Lẹ́yìn wọn, ó tún rí àwọn obìnrin méjì kan tí wọ́n ń fà sẹ́yìn (pẹ̀lú ẹran-ọ̀sìn wọn). Ó sọ pé: “Kí l’ó ṣe ẹ̀yin méjèèjì?” Wọ́n sọ pé: “A kò lè fún àwọn ẹran-ọ̀sìn wa ní omi mu títí àwọn adaran bá tó kó àwọn ẹran-ọ̀sìn wọn lọ. Àgbàlágbà arúgbó sì ni bàbá wa.”');
INSERT INTO "aykyuni" VALUES(28,24,'28:24','Ó sì bá wọn fún (ẹran-ọ̀sìn) wọn ní omi mu. Lẹ́yìn náà, ó padà síbi ibòji, ó sì sọ pé: “Olúwa mi, dájúdájú èmi bùkátà sí ohun tí O bá sọ̀kalẹ̀ fún mi nínú oore.”');
INSERT INTO "aykyuni" VALUES(28,25,'28:25','Ọ̀kan nínú àwọn méjèèjì wá bá a, ó sì ń rìn pẹ̀lú ìtìjú, ó sọ pé: “Dájúdájú bàbá mi ń pè ọ́ nítorí kí ó lè san ọ́ ní ẹ̀san omi tí o fún (àwọn ẹran-ọ̀sìn) wa mu.” Nígbà tí ó dé ọ̀dọ̀ rẹ̀, ó sọ ìtàn (ara rẹ̀) fún un. (Bàbá náà) sọ pé: “Má bẹ̀rù. O ti là lọ́wọ́ ìjọ alábòsí.”');
INSERT INTO "aykyuni" VALUES(28,26,'28:26','Ọ̀kan nínú (àwọn ọmọbìnrin) méjèèjì sọ pé: “Bàbá mi o, gbà á síṣẹ́. Dájúdájú ẹni tó dára jùlọ tí o lè gbà síṣẹ́ ni alágbára, olùfọkàntán.”');
INSERT INTO "aykyuni" VALUES(28,27,'28:27','Ó sọ pé: “Dájúdájú mo fẹ́ fi ọ̀kan nínú àwọn ọmọbìnrin mi méjèèjì wọ̀nyí fún ọ ní aya lórí (àdéhùn) pé o máa bá mi ṣiṣẹ́ fún ọdún mẹ́jọ. Ṣùgbọ́n tí o bá ṣe é pé ọdún mẹ́wàá, láti ọ̀dọ̀ rẹ nìyẹn. Èmi kò sì fẹ́ kó ìnira bá ọ. Tí Allāhu bá fẹ́, o máa rí mi pé mo wà nínú àwọn ẹni rere.”');
INSERT INTO "aykyuni" VALUES(28,28,'28:28','(Mūsā) sọ pé: “(Àdéhùn) yìí wà láààrin èmi àti ìwọ. Èyíkéyìí tí mo bá mú ṣẹ nínú àdéhùn méjèèjì náà, ìwọ kò gbọdọ̀ ṣàbòsí sí mi. Allāhu sì ni Ẹlẹ́rìí lórí ohun tí à ń sọ (yìí).”');
INSERT INTO "aykyuni" VALUES(28,29,'28:29','Nígbà tí Mūsā parí àkókò náà, ó mú ará ilé rẹ̀ rin (ìrìn-àjò). Ó sì rí iná kan ní ẹ̀bá àpáta. Ó sọ fún ará ilé rẹ̀ pé: “Ẹ dúró (síbí ná). Èmi rí iná kan. Ó ṣe é ṣe kí n̄g mú ìró kan wá ba yín láti ibẹ̀ tàbí (kí n̄g mú) ògúnná kan wá nítorí kí ẹ lè rí ohun yẹ́ná.”');
INSERT INTO "aykyuni" VALUES(28,30,'28:30','Nígbà tí ó dé ibẹ̀, A pè é níbi etí àfonífojì ní ọwọ́ ọ̀tún (rẹ̀) ní àyè ìbùkún láti ibi igi náà. (A sọ pé): “Mūsā, dájúdájú Èmi (tí Mò ń bá ọ sọ̀rọ̀), Èmi ni Allāhu, Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(28,31,'28:31','Ju ọ̀pá rẹ sílẹ̀.” (Ó sì jù ú sílẹ̀). Nígbà tí ó rí i tó ń mira bí ẹni pé ejò ni, Mūsā pẹ̀yìndà, ó ń sá lọ́, kò sì padà sẹ́yìn. (Allāhu sọ pé): “Mūsā, máa bọ̀ padà, má sì ṣe páyà. Dájúdájú ìwọ wà nínú àwọn olùfàyàbalẹ̀.');
INSERT INTO "aykyuni" VALUES(28,32,'28:32','Ti ọwọ́ rẹ bọ igbá-àyà ẹ̀wù rẹ, ó sì máa jáde ní funfun tí kì í ṣe ti aburú. Pa ọwọ́ rẹ mọ́ra níbi ẹ̀rù.Nítorí náà, àwọn ẹ̀rí méjì nìwọ̀nyí láti ọ̀dọ̀ Olúwa rẹ sí Fir‘aon àti àwọn ìjòyè rẹ̀. Dájúdájú wọ́n jẹ́ ìjọ òbìlẹ̀jẹ́.” ');
INSERT INTO "aykyuni" VALUES(28,33,'28:33','(Ànábì Mūsā) sọ pé: “Olúwa mi, dájúdájú èmi pa ẹnì kan nínú wọn. Nítorí náà, mò ń bẹ̀rù pé wọ́n máa pa mí.');
INSERT INTO "aykyuni" VALUES(28,34,'28:34','Àti pé arákùnrin mi Hārūn, ó dá láhọ́n jù mí lọ. Nítorí náà, rán an níṣẹ́ pẹ̀lú mi, kí ó jẹ́ olùrànlọ́wọ́ tí yóò máa jẹ́rìí sí òdodo ọ̀rọ̀ mi. Dájúdájú èmi ń bẹ̀rù pé wọ́n máa pè mí ní òpùrọ́.”');
INSERT INTO "aykyuni" VALUES(28,35,'28:35','(Allāhu) sọ pé: “A máa fi arákùnrin rẹ kún ọ lọ́wọ́. A sì máa fún ẹ̀yin méjèèjì ní agbára, wọn kò níí lè dé ọ̀dọ̀ ẹ̀yin méjèèjì. Pẹ̀lú àwọn àmì Wa, ẹ̀yin méjèèjì àti àwọn tó bá tẹ̀lé ẹ̀yin méjèèjì l’ó máa borí.”');
INSERT INTO "aykyuni" VALUES(28,36,'28:36','Nígbà tí (Ànábì) Mūsā dé bá wọn pẹ̀lú àwọn àmì Wa, wọ́n wí pé: “Kí ni èyí bí kò ṣe idán àdáhun. A kò gbọ́ èyí (rí) láààrin àwọn bàbá wa, àwọn ẹni àkọ́kọ́.”');
INSERT INTO "aykyuni" VALUES(28,37,'28:37','(Ànábì) Mūsā sì sọ pé: “Olúwa mi nímọ̀ jùlọ nípa ẹni tí ó mú ìmọ̀nà wá láti ọ̀dọ̀ Rẹ̀ àti ẹni tí àtubọ̀tán ilé (ìkẹ́) máa jẹ́ tirẹ̀. Dájúdájú àwọn alábòsí kò níí jèrè.”');
INSERT INTO "aykyuni" VALUES(28,38,'28:38','Fir‘aon wí pé: “Ẹ̀yin ìjòyè, èmi kò mọ̀ pé ọlọ́hun kan tún wà lẹ́yìn mi fún yín! Nítorí náà, Hāmọ̄n, dá iná fún mi, kí o fi mọ amọ̀, kí o sì kọ́ ilé gíga kan fún mi nítorí kí èmi lè yọjú wo Ọlọ́hun Mūsā. (Nítorí pé) dájúdájú mò ń rò ó sí ara àwọn òpùrọ́.”');
INSERT INTO "aykyuni" VALUES(28,39,'28:39','Òun àti àwọn ọmọ ogun rẹ̀ sì ṣègbéraga lórí ilẹ̀ láì lẹ́tọ̀ọ́. Wọ́n sì rò pé dájúdájú A ò níí dá wọn padà sí ọ̀dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(28,40,'28:40','Nítorí náà, A gbá òun àti àwọn ọmọ ogun rẹ̀ mú; A jù wọ́n sínú agbami odò. Wo bí àtubọ̀tán àwọn alábòsí ti rí.');
INSERT INTO "aykyuni" VALUES(28,41,'28:41','A ṣe wọ́n ní aṣíwájú tó ń pèpè sínú Iná. Tí ó bá sì di Ọjọ́ Àjíǹde, A ò níí ràn wọ́n lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(28,42,'28:42','Àti pé A fi ègún tẹ̀lé wọn ní ilé ayé yìí. Ní Ọjọ́ Àjíǹde, wọn yó sì wà nínú àwọn ẹni ìparun.');
INSERT INTO "aykyuni" VALUES(28,43,'28:43','Dájúdájú A ti fún (Ànábì) Mūsā ní Tírà lẹ́yìn ìgbà tí A ti pa àwọn ìjọ àkọ́kọ́ rẹ́. (Tírà náà jẹ́) ìmọ̀ àmọ̀dájú àti ẹ̀rí fún àwọn ènìyàn, ìmọ̀nà àti ìkẹ́ nítorí kí wọ́n lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(28,44,'28:44','Ìwọ kò sí ní ẹ̀bá ìwọ̀-òòrùn nígbà tí A fi ọ̀rọ̀ náà ránṣẹ́ sí (Ànábì) Mūsā. Àti pé ìwọ kò sí nínú àwọn tó wà níbẹ̀.');
INSERT INTO "aykyuni" VALUES(28,45,'28:45','Ṣùgbọ́n A ṣẹ̀dá àwọn ìran kan tí ẹ̀mí wọn gùn. O ò kúkú gbé láààrin ará ìlú Mọdyan, tí ò ń ké àwọn āyah Wa fún wọn, ṣùgbọ́n Àwa l’à ń rán àwọn Òjíṣẹ́ níṣẹ́.');
INSERT INTO "aykyuni" VALUES(28,46,'28:46','Ìwọ kò sì sí ní ẹ̀bá àpáta náà, nígbà tí A pèpè, ṣùgbọ́n ó jẹ́ ìkẹ́ kan láti ọ̀dọ̀ Olúwa rẹ nítorí kí o lè ṣèkìlọ̀ fún àwọn ènìyàn tí olùkìlọ̀ kan kò wá bá rí ṣíwájú rẹ nítorí kí wọ́n lè lo ìrántí.1 ');
INSERT INTO "aykyuni" VALUES(28,47,'28:47','Tí ó bá jẹ́ pé àdánwò kan kàn wọ́n nítorí ohun tí wọ́n fi ọwọ́ wọn tì síwájú, wọn ìbá wí pé: “Olúwa wa, ti Ó bá jẹ́ pé O rán Òjíṣẹ́ kan sí wa ni, a à bá tẹ̀lé àwọn āyah Rẹ, a à bá sì wà nínú àwọn onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(28,48,'28:48','Ṣùgbọ́n nígbà tí òdodo dé bá wọn láti ọ̀dọ̀ Wa, wọ́n wí pé: “Kí ni kò jẹ́ kí Wọ́n fún un ní irú ohun tí Wọ́n fún (Ànábì) Mūsā?” Ṣé wọn kò sì ṣàì gbàgbọ́ nínú ohun tí A fún (Ànábì) Mūsā ṣíwájú bí? Wọ́n wí pé: “Òpìdán méjì tó ń ranra wọn lọ́wọ́ (ni wọ́n).” Wọ́n sì tún wí pé: “Dájúdájú àwa jẹ́ aláìgbàgbọ́ nínú ìkọ̀ọ̀kan (wọn).”');
INSERT INTO "aykyuni" VALUES(28,49,'28:49','Sọ pé: “Nítorí náà, ẹ mú tírà kan wá láti ọ̀dọ̀ Allāhu, tó ń tọ́ ni sí ọ̀nà ju ti àwọn méjèèjì lọ, mo sì máa tẹ̀lé e, tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(28,50,'28:50','Tí wọn kò bá jẹ́pè rẹ, mọ̀ pé wọ́n kàn ń tẹ̀lé ìfẹ́-inú wọn ni. Ta l’ó sì ṣìnà ju ẹni tó tẹ̀lé ìfẹ́-inú rẹ̀, láì sí ìmọ̀nà (fún un) láti ọ̀dọ̀ Allāhu! Dájúdájú Allāhu kò níí fi ọ̀nà mọ ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(28,51,'28:51','Àti pé dájúdájú A ti ṣe àlàyé ọ̀rọ̀ náà fún wọn nítorí kí wọ́n lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(28,52,'28:52','Àwọn tí A fún ní Tírà ṣíwájú rẹ̀, wọ́n gbàgbọ́ nínú al-Ƙur’ān.');
INSERT INTO "aykyuni" VALUES(28,53,'28:53','Àti pé nígbà tí wọ́n bá ń ké e fún wọn, wọ́n á sọ pé: “A gbà á gbọ́. Dájúdájú òdodo ni láti ọ̀dọ̀ Olúwa wa. Dájúdájú àwa ti jẹ́ mùsùlùmí ṣíwájú rẹ̀.”1 ');
INSERT INTO "aykyuni" VALUES(28,54,'28:54','Àwọn wọ̀nyẹn ni A máa fún wọn ní ẹ̀san wọn ní ẹ̀ẹ̀ mejì nítorí pé wọ́n ṣe sùúrù, wọ́n sì ń fi dáadáa ti àìdaa dànù. Àti pé wọ́n ń ná nínú ohun tí A pèsè fún wọn.');
INSERT INTO "aykyuni" VALUES(28,55,'28:55','Àti pé nígbà tí wọ́n bá gbọ́ ọ̀rọ̀ burúkú, wọ́n á ṣẹ́rí kúrò níbẹ̀, wọ́n á sì sọ pé: “Tiwa ni àwọn iṣẹ́ wa. Tiyín sì ni àwọn iṣẹ́ yín. Kí àlàáfíà máa ba yín. A kò wá àwọn aláìmọ̀kan (ní alábàáṣepọ̀).”');
INSERT INTO "aykyuni" VALUES(28,56,'28:56','Dájúdájú ìwọ kò lè fi ọ̀nà mọ ẹni tí o fẹ́ràn, ṣùgbọ́n Allāhu l’Ó ń fi ọ̀nà mọ ẹni tí Ó bá fẹ́.Àti pé Ó nímọ̀ jùlọ nípa àwọn olùmọ̀nà. ');
INSERT INTO "aykyuni" VALUES(28,57,'28:57','Wọ́n wí pé: “Tí a bá (fi lè) tẹ̀lé ìmọ̀nà pẹ̀lú rẹ, àwọn ọ̀ṣẹbọ yóò kó wa kúrò lórí ilẹ̀ wa.” Ṣé A ò fún wọn ní ibùgbé (tí ó jẹ́) àyè ọ̀wọ̀ ìfọ̀kànbalẹ̀, tí wọ́n sì ń kó àwọn èso gbogbo n̄ǹkan wá síbẹ̀, tí ó jẹ́ èsè láti ọ̀dọ̀ Wa? Ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò mọ̀.');
INSERT INTO "aykyuni" VALUES(28,58,'28:58','Mélòó mélòó nínú ìlú tí A parẹ́, tí wọ́n yọ àyọ̀pọ̀rọ́ ayé-jíjẹ nínú ìlú. Ìwọ̀nyẹn ni àwọn ibùgbé wọn, tí A kò jẹ́ kí ẹnì kan kan gbé ibẹ̀ lẹ́yìn wọn bí kò ṣe fún ìgbà díẹ̀. Àwa sì jẹ́ Olùjogún.');
INSERT INTO "aykyuni" VALUES(28,59,'28:59','Olúwa rẹ kò sì níí pa àwọn ìlú run (ní àsìkò tìrẹ yìí) títí Ó fi máa gbé Òjíṣẹ́ kan dìde nínú Olú-ìlú-ayé (ìyẹn, ìlú Mọkkah, ẹni tí) yóò máa ké àwọn āyah Wa fún wọn. (Ó sì ti ṣe bẹ́ẹ̀.) A ò sì níí pa àwọn ìlú (yìí) run sẹ́ àfi kí àwọn ara ibẹ̀ jẹ́ alábòsí.');
INSERT INTO "aykyuni" VALUES(28,60,'28:60','Kò sí ohun tí A fún yín bí kò ṣe nítorí ìgbádùn ayé àti ọ̀ṣọ́ rẹ̀. Ohun tó ń bẹ lọ́dọ̀ Allāhu lóore jùlọ, ó sì máa wà títí láéláé. Nítorí náà, ṣé ẹ kò níí ṣe làákàyè ni?');
INSERT INTO "aykyuni" VALUES(28,61,'28:61','Ǹjẹ́ ẹni tí A bá ṣàdéhùn ní àdéhùn tó dára, tí ó sì máa pàdé rẹ̀, dà bí ẹni tí A fún ní ìgbádùn ìṣẹ̀mí ayé bí, lẹ́yìn náà ní Ọjọ́ Àjíǹde tí ó máa wà nínú àwọn tí wọn yóò mú wá (fún ìyà Iná)?');
INSERT INTO "aykyuni" VALUES(28,62,'28:62','(Rántí) ọjọ́ tí (Allāhu) yóò pè wọ́n, Ó sì máa sọ pé: “Níbo ni àwọn akẹgbẹ́ Mi tí ẹ̀ ń sọ nípa wọn láì ní ẹ̀rí lọ́wọ́ wà?”');
INSERT INTO "aykyuni" VALUES(28,63,'28:63','Àwọn tí ọ̀rọ̀ náà kò lé lórí wí pé: “Olúwa wa, àwọn wọ̀nyí tí a kó ṣìnà, a kó wọn ṣìnà gẹ́gẹ́ bí àwa náà ṣe ṣìnà. A yọwọ́ yọsẹ̀ (nínú ọ̀rọ̀ wọn) níwájú Rẹ (báyìí); kì í ṣe àwa ni wọ́n ń jọ́sìn fún.”');
INSERT INTO "aykyuni" VALUES(28,64,'28:64','A sọ (fún wọn) pé: “Ẹ pe àwọn òrìṣà yín.” Wọ́n sì pè wọ́n, ṣùgbọ́n wọn kò dá wọn lóhùn. Wọ́n ti rí Iná. Tí ó bá jẹ́ pé dájúdájú wọ́n jẹ́ olùmọ̀nà ni (ìyà ìbá tí jẹ wọ́n.)');
INSERT INTO "aykyuni" VALUES(28,65,'28:65','(Rántí) ọjọ́ tí (Allāhu) yóò pè wọ́n, Ó sì máa sọ pé: “Kí ni ẹ fọ̀ ní èsì fún àwọn Òjíṣẹ́?”');
INSERT INTO "aykyuni" VALUES(28,66,'28:66','Èsì ọ̀rọ̀ yó sì fọ́jú pọ̀ mọ́ wọn lọ́wọ́ ní ọjọ́ yẹn; wọn kò sì níí bira wọn léèrè ìbéèrè.1 ');
INSERT INTO "aykyuni" VALUES(28,67,'28:67','Ṣùgbọ́n ní ti ẹni tí ó bá ronú pìwàdà, tí ó gbàgbọ́ ní òdodo, tí ó sì ṣe iṣẹ́ rere, ó súnmọ́ pé ó máa wà nínú àwọn olùjèrè.');
INSERT INTO "aykyuni" VALUES(28,68,'28:68','Olúwa rẹ l’Ó ń ṣẹ̀dá ohun tí Ó bá fẹ́. Ó sì ń ṣa (ohun tí Ó bá fẹ́) ní ẹ̀ṣà. Ṣíṣa ẹ̀ṣà kò tọ́ sí wọn.Mímọ́ ni fún Allāhu. Ó sì ga tayọ n̄ǹkan tí wọ́n ń fi ṣẹbọ sí I. ');
INSERT INTO "aykyuni" VALUES(28,69,'28:69','Olúwa rẹ sì mọ ohun tí igbá-àyà wọn ń fi pamọ́ àti ohun tí wọ́n ń ṣe àfihàn rẹ̀.');
INSERT INTO "aykyuni" VALUES(28,70,'28:70','Òun sì ni Allāhu. Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. TiRẹ̀ ni gbogbo ẹyìn ní ilé ayé yìí àti ní ọ̀run. TiRẹ̀ sì ni ìdájọ́. Ọ̀dọ̀ Rẹ sì ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(28,71,'28:71','Sọ pé: “Ẹ sọ fún mi, tí Allāhu bá sọ òru di ohun tí ó máa wà títí láéláé fún yín di Ọjọ́ Àjíǹde, ọlọ́hun wo lẹ́yìn Allāhu l’ó máa fún yín ní ìmọ́lẹ̀? Ṣé ẹ kò gbọ́rọ̀ ni?”');
INSERT INTO "aykyuni" VALUES(28,72,'28:72','Sọ pé: “Ẹ sọ fún mi, tí Allāhu bá sọ ọ̀sán di ohun tí ó máa wà títí láéláé fún yín di Ọjọ́ Àjíǹde, ọlọ́hun wo lẹ́yìn Allāhu l’ó máa fún yín ní alẹ́ tí ẹ óò máa sinmi nínú rẹ̀? Ṣé ẹ kò ríran ni?”');
INSERT INTO "aykyuni" VALUES(28,73,'28:73','Nínú ìkẹ́ Rẹ̀ (ni pé), Ó ṣe òru àti ọ̀sán fún yín; nítorí kí ẹ lè sinmi nínú (òru) àti nítorí kí ẹ lè wá nínú oore Rẹ̀ (ní ọ̀sán) àti nítorí kí ẹ lè dúpẹ́.');
INSERT INTO "aykyuni" VALUES(28,74,'28:74','(Rántí) ọjọ́ tí (Allāhu) yóò pè wọ́n, Ó sì máa sọ pé: “Níbo ni àwọn akẹgbẹ́ Mi tí ẹ̀ ń sọ nípa wọn láì ní ẹ̀rí lọ́wọ́ wà?”');
INSERT INTO "aykyuni" VALUES(28,75,'28:75','A sì máa mú ẹlẹ́rìí kan jáde nínú ìjọ kọ̀ọ̀kan. A máa sọ pé: “Ẹ mú ẹ̀rí wá.” Nígbà náà, wọn yóò mọ̀ pé dájúdájú òdodo ń jẹ́ ti Allāhu. Ohun tí wọ́n ń dá ní àdápa irọ́ sì di òfo mọ́ wọn lọ́wọ́.1 ');
INSERT INTO "aykyuni" VALUES(28,76,'28:76','Dájúdájú Ƙọ̄rūn wà nínú ìjọ (Ànábì) Mūsā, ṣùgbọ́n ó ṣègbéraga sí wọn. A sì fún un ní àwọn àpótí-ọrọ̀ èyí tí àwọn kọ́kọ́rọ́ rẹ̀ wúwo láti gbé fún ọ̀pọ̀ ènìyàn àwọn alágbára. (Rántí) nígbà tí àwọn ènìyàn rẹ̀ sọ fún un pé: “Má ṣe yọ ayọ̀ àyọ̀jù. Dájúdájú Allāhu kò fẹ́ràn àwọn aláyọ̀jù.');
INSERT INTO "aykyuni" VALUES(28,77,'28:77','Fi n̄ǹkan tí Allāhu fún ọ wá ilé Ìkẹ́yìn. Má sì ṣe gbàgbé ìpín rẹ ní ilé ayé. Ṣe dáadáa gẹ́gẹ́ bí Allāhu ti ṣe dáadáa fún ọ. Má sì ṣe wá ìbàjẹ́ ṣe lórí ilẹ̀. Dájúdájú Allāhu kò fẹ́ràn àwọn òbìlẹ̀jẹ́.”');
INSERT INTO "aykyuni" VALUES(28,78,'28:78','Ó wí pé: “Dájúdájú wọ́n fún mi pẹ̀lú ìmọ̀ tó ń bẹ lọ́dọ̀ mi ni.”Ṣé kò mọ̀ pé dájúdájú Allāhu ti parun nínú àwọn ìran tó ṣíwájú rẹ̀, ẹni tí ó lágbára jù ú lọ, tí ó sì ní àkójọ ọrọ̀ jù ú lọ? A ò sì níí bi àwọn arúfin léèrè (ẹ̀ṣẹ̀ wọn. Ó kúkú ti wà ní àkọsílẹ̀.) ');
INSERT INTO "aykyuni" VALUES(28,79,'28:79','Ó jáde sí àwọn ènìyàn rẹ̀ pẹ̀lú ọ̀ṣọ́ rẹ̀. Àwọn tó ń fẹ́ ìṣẹ̀mí ayé yìí sì wí pé: “Háà! Kí á sì ní irú ohun tí wọ́n fún Ƙọ̄rūn. Dájúdájú ó ní ìpín ńlá nínú oore.”');
INSERT INTO "aykyuni" VALUES(28,80,'28:80','Àwọn tí A fún ní ìmọ̀ sọ pé: “Ègbé ni fún yín! Ẹ̀san Allāhu lóore jùlọ fún ẹni tó bá gbàgbọ́ ní òdodo, tí ó sì ṣe iṣẹ́ rere. Kò sì sí ẹni tí ó máa rí (ẹ̀san náà) gbà àfi àwọn onísùúrù.”');
INSERT INTO "aykyuni" VALUES(28,81,'28:81','Nítorí náà, A jẹ́ kí ilẹ̀ ríri gbé òun àti ilé rẹ̀ mì. Kò ní ìjọ kan tí ó lè ràn án lọ́wọ́ lẹ́yìn Allāhu. Kò sì sí nínú àwọn tó lè ran ara rẹ̀ lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(28,82,'28:82','Àwọn tó ń rankàn ipò rẹ̀ ní àná sì bẹ̀rẹ̀ sí wí pé: “Ṣé ẹ rí i pé dájúdájú Allāhu l’Ó ń tẹ́ ọrọ̀ sílẹ̀ fún ẹni tí Ó bá fẹ́ nínú àwọn ẹrúsìn Rẹ̀. Ó sì ń díwọ̀n rẹ̀ (fún ẹlòmíìràn). Tí kò bá jẹ́ pé Allāhu kẹ́ wa ni, ìbá jẹ́ kí ilẹ̀ ríri gbé àwa náà mì ni. Ṣé ẹ rí i pé àwọn aláìmoore kò níí jèrè.”');
INSERT INTO "aykyuni" VALUES(28,83,'28:83','Ilé Ìkẹ́yìn yẹn, A máa fi fún àwọn tí kò fẹ́ máa ṣe ìgbéraga àti ìbàjẹ́ lórí ilẹ̀. Ìgbẹ̀yìn rere sì wà fún àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(28,84,'28:84','Ẹnikẹ́ni tí ó bá dé pẹ̀lú iṣẹ́ rere, tirẹ̀ ni rere tó lóore ju èyí tó mú wá. Ẹnikẹ́ni tí ó bá sì dé pẹ̀lú iṣẹ́ aburú, A kò níí san àwọn tó ṣe iṣẹ́ aburú ní ẹ̀san kan àfi ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(28,85,'28:85','Dájúdájú Ẹni tí Ó ṣe al-Ƙur’ān ní ọ̀ran-anyàn fún ọ (láti mú un lò àti láti pèpè sí i), Ó sì máa dá ọ padà sí ibùpadàsí kan (nínú Ọgbà Ìdẹ̀ra). Sọ pé: “Olúwa mi nímọ̀ jùlọ nípa ẹni tí ó mú ìmọ̀nà wá àti ta ni ó wà nínú ìṣìnà pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(28,86,'28:86','Ìwọ kò sì retí pé A óò sọ tírà al-Ƙur’ān kalẹ̀ fún ọ (tẹ́lẹ̀), àmọ́ ó jẹ́ ìkẹ́ kan láti ọ̀dọ̀ Olúwa rẹ. Nítorí náà, o ò gbọdọ̀ jẹ́ olùrànlọ́wọ́ fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(28,87,'28:87','Má ṣe jẹ́ kí wọ́n ṣẹ́ ọ lórí kúrò níbi àwọn āyah Allāhu lẹ́yìn ìgbà tí A ti sọ̀ ọ́ kalẹ̀ fún ọ. Pèpè sí ọ̀dọ̀ Olúwa rẹ. Ìwọ kò sì gbọ́dọ̀ wà nínú àwọn ọ̀ṣẹbọ.');
INSERT INTO "aykyuni" VALUES(28,88,'28:88','Má ṣe pe ọlọ́hun mìíràn mọ́ Allāhu. Kò sí ọlọ́hun kan tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Gbogbo n̄ǹkan l’ó máa parun àfi Ojú Rẹ̀ (ìyẹn pàápàá bíbẹ Allāhu -subhānu wa ta‘ālā -). TiRẹ̀ ni ìdájọ́. Ọ̀dọ̀ Rẹ sì ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(29,1,'29:1','’Alif lām mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(29,2,'29:2','Ǹjẹ́ àwọn ènìyàn lérò pé A óò fi wọ́n sílẹ̀ kí wọ́n máa sọ pé: “A gbàgbọ́”, tí A ò sì níí dán wọn wò?');
INSERT INTO "aykyuni" VALUES(29,3,'29:3','A kúkú ti dán àwọn tó ṣíwájú wọn wò. Nítorí náà, dájúdájú Allāhu máa ṣàfi hàn àwọn tó sọ òdodo. Dájúdájú Ó sì máa ṣàfi hàn àwọn òpùrọ́.');
INSERT INTO "aykyuni" VALUES(29,4,'29:4','Àbí àwọn tó ń ṣe iṣẹ́ aburú lérò pé àwọn máa mórí bọ́ mọ́ Wa lọ́wọ́ ni? Ohun tí wọ́n ń dá lẹ́jọ́ burú.');
INSERT INTO "aykyuni" VALUES(29,5,'29:5','Ẹnikẹ́ni tí ó bá ń retí ìpàdé Allāhu, dájúdájú àkókò Allāhu kúkú ń bọ̀. Òun sì ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(29,6,'29:6','Ẹnikẹ́ni tí ó bá gbìyànjú, ó ń gbìyànjú fún ẹ̀mí ara rẹ̀ ni. Dájúdájú Allāhu ni Ọlọ́rọ̀ tí kò bùkátà sí gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(29,7,'29:7','Àwọn tó bá gbàgbọ́ ní òdodo, tí wọ́n sì ṣe iṣẹ́ rere, dájúdájú A máa ha àwọn iṣẹ́ aburú wọn dànù fún wọn. Dájúdájú A ó sì san wọ́n lẹ́san pẹ̀lú èyí tó dára ju ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(29,8,'29:8','A pa á ní àṣẹ fún ènìyàn láti ṣe dáadáa sí àwọn òbí rẹ̀ méjèèjì. Tí àwọn méjèèjì bá sì jà ọ́ lógun pé kí ó fi ohun tí ìwọ kò ní ìmọ̀ nípa rẹ̀ ṣẹbọ sí Mi, má ṣe tẹ̀lé àwọn méjèèjì.Ọ̀dọ̀ Mi ni ibùpadàsí yín. Nítorí náà, Mo máa fún yín ní ìró ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(29,9,'29:9','Àwọn tó bá gbàgbọ́ ní òdodo, tí wọ́n sì ṣe iṣẹ́ rere, dájúdájú A máa fi wọ́n sínú àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(29,10,'29:10','Ó ń bẹ nínú àwọn ènìyàn, ẹni tó ń wí pé: “A gba Allāhu gbọ́.” Nígbà tí wọ́n bá sì fi ìnira kàn án nínú ẹ̀sìn Allāhu, ó máa mú ìnira àwọn ènìyàn gẹ́gẹ́ bí ìyà ti Allāhu. (Ó sì máa padà sínú àìgbàgbọ́.) Tí àrànṣe kan láti ọ̀dọ̀ Olúwa rẹ bá sì dé, dájúdájú wọn yóò wí pé: “Dájúdájú àwa wà pẹ̀lú yín.” Ṣé Allāhu kọ́ l’Ó nímọ̀ jùlọ nípa ohun tí ń bẹ nínú igbá-àyà gbogbo ẹ̀dá ni?');
INSERT INTO "aykyuni" VALUES(29,11,'29:11','Dájúdájú Allāhu máa ṣàfi hàn àwọn tó gbàgbọ́ ní òdodo. Ó sì máa ṣàfi hàn àwọn ṣọ̀be-ṣèlu mùsùlùmí.');
INSERT INTO "aykyuni" VALUES(29,12,'29:12','Àwọn tó ṣàì gbàgbọ́ wí fún àwọn tó gbàgbọ́ ní òdodo pé: “Ẹ tẹ̀lé ojú ọ̀nà tiwa nítorí kí á lè ru àwọn ẹ̀ṣẹ̀ yín.” Wọn kò sì lè ru kiní kan nínú ẹ̀ṣẹ̀ wọn. Dájúdájú òpùrọ́ mà ni wọ́n.');
INSERT INTO "aykyuni" VALUES(29,13,'29:13','Dájúdájú wọn yóò ru ẹrù ẹ̀ṣẹ̀ tiwọn àti àwọn ẹrù ẹ̀ṣẹ̀ kan mọ́ ẹrù ẹ̀ṣẹ̀ tiwọn. Àti pé dájúdájú A óò bi wọ́n léèrè ní Ọjọ́ Àjíǹde nípa ohun tí wọ́n ń dá ní àdápa irọ́.1 ');
INSERT INTO "aykyuni" VALUES(29,14,'29:14','Dájúdájú A rán (Ànábì) Nūh níṣẹ́ sí ìjọ rẹ̀. Ó gbé láààrin wọn fún ẹgbẹ̀rún ọdún àfi àádọ́ta ọdún. Ẹ̀kún-omi sì gbá wọn mú nígbà tí wọ́n jẹ́ alábòsí.');
INSERT INTO "aykyuni" VALUES(29,15,'29:15','A sì la òun àti àwọn èrò inú ọkọ̀ ojú-omi. A sì ṣe wọ́n ní àmì fún gbogbo ẹ̀dá.1 ');
INSERT INTO "aykyuni" VALUES(29,16,'29:16','(Rántí Ànábì) ’Ibrọ̄hīm. Nígbà tí ó sọ fún ìjọ rẹ̀ pé: “Ẹ jọ́sìn fún Allāhu. Kí ẹ sì páyà Rẹ̀. Ìyẹn lóore jùlọ fún yín tí ẹ bá mọ̀.”');
INSERT INTO "aykyuni" VALUES(29,17,'29:17','Ẹ kàn ń jọ́sìn fún àwọn òrìṣà lẹ́yìn Allāhu. Ẹ sì ń dá àdápa irọ́. Dájúdájú àwọn tí ẹ̀ ń jọ́sìn fún lẹ́yìn Allāhu, wọn kò ní ìkápá arísìkí kan fún yín. Ẹ wá arísìkí sí ọ̀dọ̀ Allāhu. Kí ẹ jọ́sìn fún Un. Kí ẹ sì dúpẹ́ fún Un. Ọ̀dọ̀ Rẹ̀ ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(29,18,'29:18','Tí ẹ bá sì pe (òdodo) ní irọ́, àwọn ìjọ kan tó ṣíwájú yín kúkú ti pe (òdodo) ní irọ́. Kò sì sí ojúṣe kan fún Òjíṣẹ́ bí kò ṣe ìkéde (ẹ̀sìn) tó yanjú.');
INSERT INTO "aykyuni" VALUES(29,19,'29:19','Tàbí wọn kò wòye sí bí Allāhu ti ṣe bẹ̀rẹ̀ ẹ̀dá ni? Lẹ́yìn náà, O máa dá a padà (sí ọ̀dọ̀ Rẹ̀). Dájúdájú ìyẹn rọrùn fún Allāhu.');
INSERT INTO "aykyuni" VALUES(29,20,'29:20','Sọ pé: “Ẹ rìn kiri lórí ilẹ̀, kí ẹ wo bí Ó ṣe bẹ̀rẹ̀ ẹ̀dá (ní ìpìlẹ̀). Lẹ́yìn náà, Allāhu l’Ó máa mú ìṣẹ̀dá ìkẹ́yìn wá (fún àjíǹde). Dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan.”');
INSERT INTO "aykyuni" VALUES(29,21,'29:21','Ó ń jẹ ẹni tí Ó bá fẹ́ níyà. Ó sì ń kẹ́ ẹni tí Ó bá fẹ́. Ọ̀dọ̀ Rẹ̀ sì ni wọn yóò da yín padà sí.1 ');
INSERT INTO "aykyuni" VALUES(29,22,'29:22','Àti pé ẹ̀yin kò níí mórí bọ́ mọ́ Allāhu lọ́wọ́ lórí ilẹ̀ àti nínú sánmọ̀. Kò sì sí aláàbò àti alárànṣe kan fún yín lẹ́yìn Allāhu.');
INSERT INTO "aykyuni" VALUES(29,23,'29:23','Àwọn tó ṣàì gbàgbọ́ nínú àwọn āyah Allāhu àti ìpàdé Rẹ̀ (lọ́run), àwọn wọ̀nyẹn ti sọ̀rètí nù nínú ìkẹ́ Mi. Àwọn wọ̀nyẹn ni ìyà ẹlẹ́ta-eléro ń bẹ fún.');
INSERT INTO "aykyuni" VALUES(29,24,'29:24','Èsì ìjọ rẹ̀ kò jẹ́ kiní kan tayọ pé wọ́n wí pé: “Ẹ pa á tàbí kí ẹ sun ún níná.” Allāhu sì gbà á là nínú iná. Dájúdájú àwọn àmì kúkú wà nínú ìyẹn fún ìjọ tó gbàgbọ́ ní òdodo.');
INSERT INTO "aykyuni" VALUES(29,25,'29:25','Ó sì sọ pé: “Ẹ kàn mú àwọn òrìṣà lẹ́yìn Allāhu, ní ohun tí ẹ nífẹ̀ẹ́ sí (láti jọ́sìn fún) láààrin ara yín nínú ìṣẹ̀mí ayé (yìí). Lẹ́yìn náà, ní Ọjọ́ Àjíǹde apá kan yin yóò tako apá kan. Apá kan yín yó sì ṣẹ́bi lé apá kan. Iná sì ni ibùgbé yín. Kò sì níí sí àwọn alárànṣe kan fún yín.”');
INSERT INTO "aykyuni" VALUES(29,26,'29:26','(Ànábì) Lūt sì gbà á gbọ́. (Ànábì ’Ibrọ̄hīm) sọ pé: “Dájúdájú èmi yóò fi ìlú yìí sílẹ̀ nítorí ti Olúwa mi.Dájúdájú Òun ni Alágbára, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(29,27,'29:27','A fi ’Ishāƙ àti Ya‘ƙūb (ọmọọmọ rẹ̀) ta á lọ́rẹ. A sì ṣe ipò jíjẹ́ Ànábì àti fífúnni ní tírà sínú àrọ́mọdọ́mọ rẹ̀. A fún un ní ẹ̀san rẹ̀ ní ayé yìí. Dájúdájú ní ọ̀run, ó tún wà nínú àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(29,28,'29:28','(Rántí Ànábì) Lūt. Nígbà tí ó sọ fún ìjọ rẹ̀ pé: “Dájúdájú ẹ̀yin ń ṣe ìbàjẹ́ tí kò sí ẹnì kan nínú ẹ̀dá tí ó ṣe é rí ṣíwájú yín.');
INSERT INTO "aykyuni" VALUES(29,29,'29:29','Ṣé dájúdájú ẹ̀yin (ọkùnrin) yóò máa tọ àwọn ọkùnrin (ẹgbẹ́ yín) lọ (fún adùn ìbálòpọ̀), ẹ tún ń dánà, ẹ tún ń ṣe ohun burúkú nínú àkójọ yín? Èsì ìjọ rẹ̀ kò jẹ́ kiní kan àfi kí wọ́n wí pé “Mú ìyà Allāhu wá fún wa tí o bá wà nínú àwọn olódodo.” ');
INSERT INTO "aykyuni" VALUES(29,30,'29:30','Ó sọ pé: “Olúwa mi, ṣàrànṣe fún mí lórí ìjọ òbìlẹ̀jẹ́.”');
INSERT INTO "aykyuni" VALUES(29,31,'29:31','Nígbà tí àwọn Òjíṣẹ́ Wa sì mú ìró ìdùnnú dé bá (Ànábì) ’Ibrọ̄hīm, wọ́n sọ pé: “Dájúdájú àwa máa pa àwọn ará ìlú yìí run. Dájúdájú àwọn ará ìlú náà jẹ́ alábòsí.”');
INSERT INTO "aykyuni" VALUES(29,32,'29:32','Ó sọ pé: “Dájúdájú (Ànábì) Lūt wà níbẹ̀! Wọ́n sọ pé: “Àwa nímọ̀ jùlọ nípa àwọn tó wà níbẹ̀. Dájúdájú àwa yóò la òun àti àwọn ará ilé rẹ̀ àfi ìyàwó rẹ̀ tí ó máa wà nínú àwọn tó máa ṣẹ́kù lẹ́yìn sínú ìparun.”');
INSERT INTO "aykyuni" VALUES(29,33,'29:33','Nígbà tí àwọn Òjíṣẹ́ Wa dé ọ̀dọ̀ (Ànábì) Lūt, ó banújẹ́ nítorí wọn. Agbára rẹ̀ kò sì ká ọ̀rọ̀ wọn mọ́. Wọ́n sì sọ fún un pé: “Má bẹ̀rù, má sì ṣe banújẹ́. Dájúdájú a máa la ìwọ àti àwọn ará ilé rẹ àfi ìyàwó rẹ tí ó máa wà nínú àwọn olùṣẹ́kù-lẹ́yìn sínú ìparun.”');
INSERT INTO "aykyuni" VALUES(29,34,'29:34','Dájúdájú a máa sọ ìyà kan kalẹ̀ lé àwọn ará ìlú yìí lórí láti inú sánmọ̀ nítorí pé, wọ́n jẹ́ òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(29,35,'29:35','Dájúdájú A ti fi àmì kan tó fojú hàn lélẹ̀ nínú rẹ̀ fún ìjọ tó ní làákàyè.');
INSERT INTO "aykyuni" VALUES(29,36,'29:36','A tún ránṣẹ́ sí ará ìlú Mọdyan. (A rán) arákùnrin wọn, (Ànábì) Ṣu‘aeb (níṣẹ́ sí wọn). Ó sì sọ pé: “Ẹ̀yin ìjọ mi, ẹ jọ́sìn fún Allāhu. Ẹ retí Ọjọ́ Ìkẹ́yìn, kí ẹ sì má ṣèbàjẹ́ sórí ilẹ̀.”');
INSERT INTO "aykyuni" VALUES(29,37,'29:37','Wọ́n pè é ní òpùrọ́. Nítorí náà, ohùn igbe líle tó mi ilẹ̀ tìtì gbá wọn mú. Wọ́n sì di ẹni tó dà lulẹ̀, tí wọ́n ti dòkú sínú ìlú wọn.');
INSERT INTO "aykyuni" VALUES(29,38,'29:38','(A tún ránṣẹ́ sí àwọn) ará ‘Ād àti ará Thamūd. (Ìparun wọn) sì kúkú ti fojú hàn kedere si yín nínú àwọn ibùgbé wọn. Èṣù ṣe àwọn iṣẹ́ wọn ní ọ̀ṣọ́ fún wọn. Ó sì ṣẹ́rí wọn kúrò nínú ẹ̀sìn (Allāhu). Wọ́n sì jẹ́ olùríran (nípa ọ̀rọ̀ ayé).');
INSERT INTO "aykyuni" VALUES(29,39,'29:39','(A tún ránṣẹ́ sí àwọn) Ƙọ̄rūn, Fir‘aon àti Hāmọ̄n. Dájúdájú (Ànábì) Mūsā mú àwọn ẹ̀rí tó yanjú wá bá wọn. Wọ́n sì ṣègbéraga lórí ilẹ̀. Wọn kò sì lè mórí bọ́ nínú ìyà.');
INSERT INTO "aykyuni" VALUES(29,40,'29:40','A sì mú ìkọ̀ọ̀kan wọn nípa ẹ̀ṣẹ̀ rẹ̀. Ó wà nínú wọn, ẹni tí A fi òkúta iná ránṣẹ́ sí. Ó wà nínú wọn ẹni tí igbe líle gbá mú. Ó wà nínú wọn ẹni tí A jẹ́ kí ilẹ̀ ríri gbé e mì. Ó sì wà nínú wọn ẹni tí A tẹ̀rì sínú omi. Allāhu kò sì níí ṣàbòsí sí wọn, ṣùgbọ́n ẹ̀mí ara wọn ni wọ́n ń ṣàbòsí sí.');
INSERT INTO "aykyuni" VALUES(29,41,'29:41','Àpèjúwe àwọn tó mú àwọn (òrìṣà) ní alátìlẹ́yìn lẹ́yìn Allāhu, ó dà bí àpèjúwe aláǹtàakùn tí ó kọ́ ilé kan. Dájúdájú ilé tí ó yẹpẹrẹ jùlọ ni ilé aláǹtàakùn, tí wọ́n bá mọ̀.');
INSERT INTO "aykyuni" VALUES(29,42,'29:42','Dájúdájú Allāhu mọ ohunkóhun tí wọ́n ń pè lẹ́yìn Rẹ̀. Òun sì ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(29,43,'29:43','Àwọn àkàwé wọ̀nyí, tí À ń fún àwọn ènìyàn, kò sì sí ẹni tí ó máa ṣe làákàyè nípa rẹ̀ àfi àwọn onímọ̀.');
INSERT INTO "aykyuni" VALUES(29,44,'29:44','Allāhu ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ pẹ̀lú òdodo. Dájúdájú àmì kan wà nínú ìyẹn fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(29,45,'29:45','Ké ohun tí A fi ránṣẹ́ sí ọ nínú Tírà. Kí o sì kírun. Dájúdájú ìrun kíkí ń kọ ìwà ìbàjẹ́ àti aburú. Àti pé ìrántí Allāhu tóbi jùlọ. Allāhu sì mọ ohun tí ẹ̀ ń ṣe.1 ');
INSERT INTO "aykyuni" VALUES(29,46,'29:46','Ẹ má ṣe bá àwọn onítírà ṣàríyàn jiyàn àfi ní ọ̀nà tó dára jùlọ (èyí ni lílo al-Ƙur’ān àti hadīth. Ẹ má sì ṣe jà wọ́n lógun) àfi àwọn tó bá ṣàbòsí nínú wọn.Kí ẹ sì sọ pé: “A gbàgbọ́ nínú ohun tí Wọ́n sọ̀kalẹ̀ fún àwa àti ohun tí Wọ́n sọ̀kalẹ̀ fún ẹ̀yin. Ọlọ́hun wa àti Ọlọ́hun yín, (Allāhu) Ọ̀kan ṣoṣo ni. Àwa sì ni mùsùlùmí (olùjuwọ́-jusẹ̀-sílẹ̀) fún Un.” ');
INSERT INTO "aykyuni" VALUES(29,47,'29:47','Báyẹn ni A ṣe sọ Tírà kalẹ̀ fún ọ. Nítorí náà, àwọn tí A fún ní Tírà, wọ́n gbà á gbọ́.Ó sì wà nínú àwọn wọ̀nyí (ìyẹn, àwọn ará Mọkkah), ẹni tó gbà á gbọ́. Kò sì sí ẹni tó ń tako àwọn āyah Wa àfi àwọn aláìgbàgbọ́. ');
INSERT INTO "aykyuni" VALUES(29,48,'29:48','Ìwọ kò ké tírà kan rí ṣíwájú al-Ƙur’ān, ìwọ kò sì fi ọwọ́ ọ̀tún rẹ kọ n̄ǹkan rí. Bí bẹ́ẹ̀ kọ́ nígbà náà, kí àwọn òpùrọ́ ṣeyèméjì (sí al-Ƙur’ān).');
INSERT INTO "aykyuni" VALUES(29,49,'29:49','Rárá (kò rí bí wọ́n ṣe rò ó. al-Ƙur’ān), òhun ni àwọn āyah tó yanjú nínú igbá-àyà àwọn tí A fún ní ìmọ̀-ẹ̀sìn. Kò sì sí ẹni tó ń tako àwọn āyah Wa àfi àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(29,50,'29:50','Wọ́n sì wí pé: “Nítorí kí ni Wọn kò ṣe sọ àwọn àmì (ìyanu) kan kalẹ̀ fún un láti ọ̀dọ̀ Olúwa rẹ̀?” Sọ pé: “Allāhu nìkan ni àwọn àmì (ìyanu) wà lọ́dọ̀ Rẹ̀. Àti pé olùkìlọ̀ pọ́nńbélé ni èmi.”');
INSERT INTO "aykyuni" VALUES(29,51,'29:51','Ṣé kò tó fún wọn (ní àmì ìyanu) pé A sọ tírà (al-Ƙur’ān) kalẹ̀ fún ọ, tí wọ́n ń ké e fún wọn? Dájúdájú ìkẹ́ àti ìrántí wà nínú ìyẹn fún ìjọ tó gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(29,52,'29:52','Sọ pé: “Allāhu tó ní Ẹlẹ́rìí láààrin èmi àti ẹ̀yin. Ó mọ ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀. Àwọn tó gba irọ́ (òrìṣà) gbọ́, tí wọ́n sì ṣàì gbàgbọ́ nínú Allāhu; àwọn wọ̀nyẹn, àwọn ni ẹni òfò.');
INSERT INTO "aykyuni" VALUES(29,53,'29:53','Wọ́n sì ń kán ọ lójú nípa ìyà! Tí kò bá jẹ́ pé ó ti ní gbèdéke àkókò kan ni, ìyà náà ìbá kúkú dé bá wọn. (Ìyà) ìbá dé bá wọn ní òjijì sẹ́, tí wọn kò sì níí fura.');
INSERT INTO "aykyuni" VALUES(29,54,'29:54','Wọ́n ń kán ọ lójú nípa ìyà! Dájúdájú iná Jahanamọ kúkú máa yí àwọn aláìgbàgbọ́ po.');
INSERT INTO "aykyuni" VALUES(29,55,'29:55','(Rántí) ọjọ́ tí ìyà yóò bò wọ́n mọ́lẹ̀ láti òkè wọn àti ìsàlẹ̀ ẹsẹ̀ wọn, (Allāhu) sì máa sọ pé: “Ẹ tọ́ (ìyà) ohun tí ẹ̀ ń ṣe níṣẹ́ wò.”');
INSERT INTO "aykyuni" VALUES(29,56,'29:56','Ẹ̀yin ẹrúsìn Mi, tí ẹ gbàgbọ́ ní òdodo, dájúdájú ilẹ̀ Mi gbòòrò. Nítorí náà, Èmi nìkan ni kí ẹ jọ́sìn fún.');
INSERT INTO "aykyuni" VALUES(29,57,'29:57','Gbogbo ẹ̀mí l’ó máa tọ́ ikú wò. Lẹ́yìn náà, ọ̀dọ̀ Wa ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(29,58,'29:58','Àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, dájúdájú A máa fi wọn sínú àwọn ilé pẹ̀tẹ́ẹ̀sì gígá kan nínú Ọgbà Ìdẹ̀ra, tí odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀. Ẹ̀san àwọn olùṣe-rere sì dára.');
INSERT INTO "aykyuni" VALUES(29,59,'29:59','(Àwọn ni) àwọn tó ṣe sùúrù. Olúwa wọn sì ni wọ́n ń gbáralé.');
INSERT INTO "aykyuni" VALUES(29,60,'29:60','Mélòó mélòó nínú àwọn ẹranko tí kò lè dá bùkátà ìjẹ-ìmu rẹ̀ gbé, tí Allāhu sì ń ṣe ìjẹ-ìmu fún àwọn àti ẹ̀yin. Òun sì ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(29,61,'29:61','Tí o bá bi wọ́n léèrè pé: “Ta ni Ó dá àwọn sánmọ̀ àti ilẹ̀, tí Ó sì rọ òòrùn àti òṣùpá?”, dájúdájú wọ́n á wí pé: “Allāhu ni.” Nítorí náà, báwo ni wọ́n ṣe ń ṣẹ́rí wọn kúrò níbi òdodo?');
INSERT INTO "aykyuni" VALUES(29,62,'29:62','Allāhu l’Ó ń tẹ́ ọrọ̀ sílẹ̀ fún ẹni tí Ó bá fẹ́ nínú àwọn ẹrúsìn Rẹ̀. Ó sì ń díwọ̀n rẹ̀ fún ẹlòmíìràn. Dájúdájú Allāhu ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(29,63,'29:63','Tí o bá bi wọ́n léèrè pé: “Ta ni Ó sọ omi kalẹ̀ láti sánmọ̀, tí Ó fi ta ilẹ̀ jí lẹ́yìn tí ó ti kú?”, dájúdájú wọ́n á wí pé: “Allāhu ni.” Sọ pé: “Gbogbo ẹyìn ń jẹ́ ti Allāhu, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò ṣe làákàyè.”');
INSERT INTO "aykyuni" VALUES(29,64,'29:64','Kí ni ìṣẹ̀mí ayé yìí bí kò ṣe ìranù àti eré ṣíṣe. Àti pé dájúdájú Ilé Ìkẹ́yìn sì ni ìṣẹ̀mí gbére tí wọ́n bá mọ̀.');
INSERT INTO "aykyuni" VALUES(29,65,'29:65','Nígbà tí wọ́n bá gun ọkọ̀ ojú-omi, wọ́n máa pe Allāhu lẹ́ni tí yóò máa fi àfọ̀mọ́-ọkàn (àníyàn mímọ́) ṣe ẹ̀sìn fún Un.Àmọ́ nígbà tí Ó bá gbà wọ́n là sí orí ilẹ̀, nígbà náà ni wọn yóò máa ṣẹbọ ');
INSERT INTO "aykyuni" VALUES(29,66,'29:66','nítorí kí wọ́n lè ṣàì moore sí n̄ǹkan tí A fún wọn àti nítorí kí wọ́n lè jayékáyé. Nítorí náà, láìpẹ́ wọ́n máa mọ̀.');
INSERT INTO "aykyuni" VALUES(29,67,'29:67','Ṣé wọn kò rí i pé dájúdájú Àwa ṣe Haram (Mọkkah) ni àyè ìfàyàbalẹ̀, tí wọ́n sì ń jí àwọn ènìyàn gbé lọ ní àyíká wọn? Ṣé irọ́ (òrìṣà) ni wọn yóò gbàgbọ́, tí wọn yó sì ṣàì moore sí ìdẹ̀ra Allāhu?');
INSERT INTO "aykyuni" VALUES(29,68,'29:68','Ta ló ṣàbòsí ju ẹni tó dá àdápa irọ́ mọ́ Allāhu, tàbí tó pe òdodo ní irọ́ nígbà tí ó dé bá a? Ṣé inú iná Jahanamọ kọ́ ni ibùgbé fún àwọn aláìgbàgbọ́ ni?');
INSERT INTO "aykyuni" VALUES(29,69,'29:69','Àwọn tó gbìyànjú nípa Wa, dájúdájú A máa fi wọ́n mọ àwọn ọ̀nà Wa. Àti pé dájúdájú Allāhu wà pẹ̀lú àwọn olúṣe-rere.');
INSERT INTO "aykyuni" VALUES(30,1,'30:1','’Alif lām mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(30,2,'30:2','Wọ́nṣẹ́gun Rōmu ');
INSERT INTO "aykyuni" VALUES(30,3,'30:3','ní àwọn ilẹ̀ tó wà nítòsí (erékùsù Lárúbáwá).Lẹ́yìn ìṣẹ́gun wọn, àwọn náà máa ṣẹ́gun wọn ');
INSERT INTO "aykyuni" VALUES(30,4,'30:4','ní ọdún díẹ̀ (sí ìgbà náà). Ti Allāhu ni àṣẹ ní ìṣáájú àti ní ìkẹ́yìn. Ní ọjọ́ yẹn, àwọn onígbàgbọ́ òdodo máa dunnú');
INSERT INTO "aykyuni" VALUES(30,5,'30:5','sí àrànṣe Allāhu. Ó ń ṣàrànṣe fún ẹni tí Ó bá fẹ́. Òun sì ni Alágbára, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(30,6,'30:6','(Èyí jẹ́) àdéhùn Allāhu. Allāhu kò sì níí yapa àdéhùn Rẹ̀, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn kò mọ̀.');
INSERT INTO "aykyuni" VALUES(30,7,'30:7','Wọ́n nímọ̀ nípa gban̄gba nínú ọ̀rọ̀ ìṣẹ̀mí ayé (yìí). Afọ́nú-fọ́ra sì ni wọ́n nípa Ọjọ́ Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(30,8,'30:8','Ṣé wọn kò ronú nípa ọ̀rọ̀ ara wọn ni? Allāhu kò ṣẹ̀dá àwọn sánmọ̀, ilẹ̀ àti n̄ǹkan tó wà láààrin àwọn méjèèjì bí kò ṣe pẹ̀lú òdodo àti fún gbèdéke àkókò kan. Dájúdájú ọ̀pọ̀lọ́pọ̀ nínú àwọn ènìyàn mà ni aláìgbàgbọ́ nínú ìpàdé Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(30,9,'30:9','Ṣé wọn kò rìn kiri lórí ilẹ̀ kí wọ́n wo bí ìkángun àwọn tó ṣíwájú wọn ṣe rí? Wọ́n ní agbára jù wọ́n lọ. Wọ́n fi ilẹ̀ dáko. Wọ́n sì lo ilẹ̀ fún ohun tí ó pọ̀ ju bí (àwọn ará Mọkkah) ṣe lò ó. Àwọn Òjíṣẹ́ wọn sì mú àwọn ẹ̀rí tó yanjú wá bá wọn. Nítorí náà, Allāhu kò ṣàbòsí sí wọn, ṣùgbọ́n ẹ̀mí ara wọn ni wọ́n ń ṣàbòsí sí.');
INSERT INTO "aykyuni" VALUES(30,10,'30:10','Lẹ́yìn náà, aburú jẹ́ àtubọ̀tán àwọn tó ṣaburú nítorí pé, wọ́n pe àwọn āyah Allāhu ní irọ́. Wọ́n sì ń fi ṣe yẹ̀yẹ́.');
INSERT INTO "aykyuni" VALUES(30,11,'30:11','Allāhu ń pilẹ̀ ẹ̀dá dídá. Lẹ́yìn náà, O máa dá a padà (sípò alààyè). Lẹ́yìn náà, ọ̀dọ̀ Rẹ̀ ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(30,12,'30:12','Ní ọjọ́ tí Àkókò náà máa ṣẹlẹ̀, àwọn ẹlẹ́ṣẹ̀ yó sọ̀rètí nù.');
INSERT INTO "aykyuni" VALUES(30,13,'30:13','Àti pé kò níí sí àwọn olùṣìpẹ̀ fún wọn nínú àwọn òrìṣà wọn. Wọ́n sì máa jẹ́ alátakò òrìṣà wọn.');
INSERT INTO "aykyuni" VALUES(30,14,'30:14','Ní ọjọ́ tí Àkókò náà máa ṣẹlẹ̀, ní ọjọ́ yẹn ni wọn yóò pínyà sí ọ̀tọ̀ọ̀tọ̀.');
INSERT INTO "aykyuni" VALUES(30,15,'30:15','Ní ti àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe iṣẹ́ rere, wọn yóò wà ní àbàtà Ọgbà Ìdẹ̀ra,tí wọn yó sì máa dùn wọ́n nínú. ');
INSERT INTO "aykyuni" VALUES(30,16,'30:16','Ní ti àwọn tó ṣàì gbàgbọ́, tí wọ́n sì pé àwọn āyah Wa àti ìpàdé Ọjọ́ Ìkẹ́yìn ní irọ́, àwọn wọ̀nyẹn ni (àwọn mọlāika) máa kó wá sínú Iná.');
INSERT INTO "aykyuni" VALUES(30,17,'30:17','Nítorí náà, ẹ ṣe àfọ̀mọ́ fún Allāhu nígbà tí ẹ bá wà ní ìrọ̀lẹ́ àti nígbà tí ẹ bá wà ní òwúrọ̀.');
INSERT INTO "aykyuni" VALUES(30,18,'30:18','TiRẹ̀ sì ni ọpẹ́ nínú àwọn sánmọ̀ àti lórí ilẹ̀ ní àṣálẹ́ àti nígbà tí ẹ bá wà ní ọ̀sán.');
INSERT INTO "aykyuni" VALUES(30,19,'30:19','(Allāhu) ń yọ alààyè jáde láti ara òkú. Ó ń yọ òkú jáde láti ara alààyè. Ó ń ta ilẹ̀ jí lẹ́yìn tí ilẹ̀ ti kú. Báyẹn ni A óò ṣe mu ẹ̀yin náà jáde.');
INSERT INTO "aykyuni" VALUES(30,20,'30:20','Ó wà nínú àwọn àmì Rẹ̀ pé, Ó ṣẹ̀dá yín láti ara erùpẹ̀. Lẹ́yìn náà, nígbà náà ẹ̀yin di abara tí ẹ̀ ń fọ́nká (lórí ilẹ̀ ayé).');
INSERT INTO "aykyuni" VALUES(30,21,'30:21','Ó wà nínú àwọn àmì Rẹ̀ pé, Ó ṣẹ̀dá àwọn aya fún yín láti ara yín nítorí kí ẹ lè rí ìfàyàbalẹ̀ lọ́dọ̀ wọn. Ó sì fi ìfẹ́ àti ìkẹ́ sí ààrin yín. Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ tó láròjinlẹ̀.');
INSERT INTO "aykyuni" VALUES(30,22,'30:22','Ó wà nínú àwọn àmì Rẹ̀, dídá àwọn sánmọ̀ àti ilẹ̀ àti ọ̀kan-ò-jọ̀kan àwọn èdè yín àti àwọn àwọ̀ ara yín. Dájúdájú àwọn àmì wà nínú ìyẹn fún àwọn onímọ̀.');
INSERT INTO "aykyuni" VALUES(30,23,'30:23','Nínú àwọn àmì Rẹ̀ ni oorun yín ní alẹ́ àti ní ọ̀sán àti wíwá tí ẹ̀ ń wá nínú oore Rẹ̀ (fún ìjẹ-ìmu). Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ tó ń gbọ́rọ̀.');
INSERT INTO "aykyuni" VALUES(30,24,'30:24','Nínú àwọn àmì Rẹ̀ tún ni fífi mọ̀nàmọ́ná hàn yín ní ti ẹ̀rù àti ìrètí. Ó sì ń sọ omi kalẹ̀ láti sánmọ̀. Ó ń fi ta ilẹ̀ jí lẹ́yìn tí ilẹ̀ ti kú. Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ tó ń ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(30,25,'30:25','Àti pé nínú àwọn àmì Rẹ̀ ni pé sánmọ̀ àti ilẹ̀ dúró pẹ̀lú àṣẹ Rẹ̀. Lẹ́yìn náà, nígbà tí Ó bá pè yín ní ìpè kan nígbà náà ni ẹ̀yin yóò máa jáde láti inú ilẹ̀.');
INSERT INTO "aykyuni" VALUES(30,26,'30:26','TiRẹ̀ ni àwọn tó wà nínú àwọn sánmọ̀ àti ilẹ̀. Ẹnì kọ̀ọ̀kan ni olùtẹ̀lé-àṣẹ Rẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(30,27,'30:27','Òun ni Ẹni tí Ó ń bẹ̀rẹ̀ ẹ̀dá dídá. Lẹ́yìn náà, Ó máa dá a padà (sípò alààyè fún àjíǹde). Ó sì rọrùn jùlọ fún Un (láti ṣe bẹ́ẹ̀). TiRẹ̀ ni ìròyìn tó ga jùlọ nínú àwọn sánmọ̀ àti ilẹ̀.Òun sì ni Alágbára, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(30,28,'30:28','(Allāhu) ṣàkàwé kan fún yín nípa ara yín. Ǹjẹ́ ẹ ní akẹgbẹ́ nínú àwọn ẹrú yín lórí ohun tí A fún yín ní arísìkí, tí ẹ jọ máa pín (dúkìá náà) ní dọ́gban̄dọ́gba, tí ẹ ó sì máa páyà wọn gẹ́gẹ́ bí wọ́n ṣe ń páyà ẹ̀yin náà? Báyẹn ni A ṣe ń ṣ’àlàyé àwọn āyah fún ìjọ tó ń ṣe làákàyè.1 ');
INSERT INTO "aykyuni" VALUES(30,29,'30:29','Ńṣe ni àwọn tó ṣàbòsí tẹ̀lé ìfẹ́-inú wọn láì sí ìmọ̀ kan (fún wọn). Ta sì ni ó lè fi ọ̀nà mọ ẹni tí Allāhu bá ṣì lọ́nà? Kò sì níí sí àwọn alárànṣe fún wọn.');
INSERT INTO "aykyuni" VALUES(30,30,'30:30','Nítorí náà, dojú rẹ kọ ẹ̀sìn náà, (kí o jẹ́) olùdúró-déédé-nínú-ẹ̀sìn Islām, ẹ̀sìn àdámọ́ Allāhu èyí tí Ó dá mọ́ àwọn ènìyàn. Kò sí ìyípadà fún ẹ̀sìn Allāhu.(ìyẹn ni pé, ẹ kò gbọ́dọ̀ yí ẹ̀sìn Allāhu padà.)2 Ìyẹn ni ẹ̀sìn tó fẹsẹ̀rinlẹ̀, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn kò mọ̀.3 ');
INSERT INTO "aykyuni" VALUES(30,31,'30:31','(Ẹ jẹ́) olùṣẹ́rí padà sí ọ̀dọ̀ Allāhu (nípa ìronúpìwàdà). Ẹ páyà Rẹ̀. Ẹ kí ìrun. Ẹ má ṣe wà nínú àwọn ọ̀ṣẹbọ.');
INSERT INTO "aykyuni" VALUES(30,32,'30:32','(Ẹ má ṣe wà) nínú àwọn tó dá ẹ̀sìn wọn sí kélekèle, tí wọ́n sì di ìjọ-ìjọ. Ìjọ kọ̀ọ̀kan sì ń yọ̀ sí ohun tí ó wà lọ́dọ̀ wọn.');
INSERT INTO "aykyuni" VALUES(30,33,'30:33','Nígbà tí ìnira kan bá fọwọ́ ba ènìyàn, wọn yóò pe Olúwa wọn, tí wọn yóò máa ṣẹ́rí padà sọ́dọ̀ Rẹ̀. Lẹ́yìn náà, nígbà tí (Allāhu) bá fún wọn ní ìkẹ́ kan tọ́ wò láti ọ̀dọ̀ Rẹ̀, nígbà náà ni igun kan nínú wọn yóò máa ṣẹbọ sí Olúwa wọn');
INSERT INTO "aykyuni" VALUES(30,34,'30:34','nítorí kí wọ́n lè ṣàì moore sí ohun tí (Allāhu) fún wọn. Ẹ máa jayé lọ. Nítorí náà, láìpẹ́ ẹ máa mọ̀.');
INSERT INTO "aykyuni" VALUES(30,35,'30:35','Tàbí A sọ ẹ̀rí kan kalẹ̀ fún wọn, tó ń sọ̀rọ̀ nípa n̄ǹkan tí wọ́n ń sọ di akẹgbẹ́ Rẹ̀?');
INSERT INTO "aykyuni" VALUES(30,36,'30:36','Nígbà tí A bá fi ìkẹ́ kan tọ́ ènìyàn lẹ́nu wò, wọn yóò dunnú sí i. Tí aburú kan bá sì kàn wọ́n nípa ohun tí ọwọ́ wọn tì síwájú, nígbà náà ni wọn yóò sọ̀rètí nù.');
INSERT INTO "aykyuni" VALUES(30,37,'30:37','Ṣé wọn kò wòye pé dájúdájú Allāhu l’Ó ń tẹ́ ọrọ̀ sílẹ̀ fún ẹni tí Ó bá fẹ́, (Ó sì ń) díwọ̀n rẹ̀ (fún ẹni tí Ó bá fẹ́)? Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ tó gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(30,38,'30:38','Nítorí náà, fún ẹbí ni ẹ̀tọ́ rẹ̀. (Fún) mẹ̀kúnnù àti onírìn-àjò (tí agara dá ní n̄ǹkan). Ìyẹn lóore jùlọ fún àwọn tó ń fẹ́ Ojú rere Allāhu. Àwọn wọ̀nyẹn, àwọn sì ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(30,39,'30:39','Ohunkóhun tí ẹ bá fún (àwọn ènìyàn) ní ẹ̀yáwó, nítorí kí ó lè di èlé láti ara dúkìá àwọn ènìyàn, kò lè lékún ní ọ̀dọ̀ Allāhu. Ohunkóhun tí ẹ bá sì (fún àwọn ènìyàn) ní Zakāh, tí ẹ̀ ń fẹ́ ojú rere Allāhu, àwọn wọ̀nyẹn ni A máa fún ní àdìpèlé ẹ̀san.');
INSERT INTO "aykyuni" VALUES(30,40,'30:40','Allāhu, Ẹni tí Ó ṣẹ̀dá yín, lẹ́yìn náà, Ó fún yín ní arísìkí, lẹ́yìn náà, Ó máa sọ yín di òkú, lẹ́yìn náà, O máa sọ yín di alààyè. Ǹjẹ́ ó wà nínú àwọn òrìṣà yín ẹni tí ó lè ṣe n̄ǹkan kan nínú ìyẹn? Mímọ́ ni fún Un. Ó ga tayọ n̄ǹkan tí wọ́n ń fi ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(30,41,'30:41','Ìbàjẹ́ hàn lórí ilẹ̀ àti lójú omi nípasẹ̀ ohun tí ọwọ́ àwọn ènìyàn ṣe níṣẹ́ (aburú) nítorí kí (Allāhu) lè fi (ìyà) apá kan èyí tí wọ́n ṣe níṣẹ́ (aburú) tọ́ wọn lẹ́nu wò nítorí kí wọ́n lè ṣẹ́rí padà (níbi aburú).');
INSERT INTO "aykyuni" VALUES(30,42,'30:42','Sọ pé: “Ẹ rìn lórí ilẹ̀, kí ẹ wo bí àtubọ̀tán àwọn tó ṣíwájú ṣe rí! Ọ̀pọ̀lọ́pọ̀ wọn ni wọ́n jẹ́ ọ̀ṣẹbọ.”');
INSERT INTO "aykyuni" VALUES(30,43,'30:43','Nítorí náà, dojú rẹ kọ ẹ̀sìn tó fẹsẹ̀rinlẹ̀ ṣíwájú kí ọjọ́ kan tó dé, tí kò sí n̄ǹkan tí ó lè yẹ̀ ẹ́ lọ́dọ̀ Allāhu. Ní ọjọ́ yẹn ni àwọn ènìyàn yóò pínyà sí (èrò Ọgbà Ìdẹ̀ra àti èrò Iná).');
INSERT INTO "aykyuni" VALUES(30,44,'30:44','Ẹnikẹ́ni tí ó bá ṣàì gbàgbọ́, orí ara rẹ̀ ni (ìyà) àìgbàgbọ́ rẹ̀ wà. Ẹnikẹ́ni tí ó bá sì ṣe iṣẹ́ rere, ẹ̀mí ara wọn ni wọ́n ń tẹ́ ìtẹ́ (Ọgbà Ìdẹ̀ra) sílẹ̀ fún');
INSERT INTO "aykyuni" VALUES(30,45,'30:45','nítorí kí (Allāhu) lè san ẹ̀san rere nínú oore àjùlọ Rẹ̀ fún àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe iṣẹ́ rere. Dájúdájú (Allāhu) kò nífẹ̀ẹ́ àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(30,46,'30:46','Nínú àwọn àmì Rẹ̀ ni pé, Ó ń rán atẹ́gùn ní ìró ìdùnnú. Àti pé nítorí kí (Allāhu) lè fún yín tọ́ wò nínú ìkẹ́ Rẹ̀; àti nítorí kí ọkọ̀ ojú-omi lè rìn lójú-omi pẹ̀lú àṣẹ Rẹ̀; àti nítorí kí ẹ lè wá nínú oore Rẹ̀; àti nítorí kí ẹ lè dúpẹ́.');
INSERT INTO "aykyuni" VALUES(30,47,'30:47','Dájúdájú A ti rán àwọn Òjíṣẹ́ kan níṣẹ́ ṣíwájú rẹ sí àwọn ìjọ wọn. Wọ́n sì wá bá wọn pẹ̀lú àwọn ẹ̀rí tó yanjú. A sì gbẹ̀san lára àwọn tó dẹ́ṣẹ̀. Ó sì jẹ́ ẹ̀tọ́ fún wa láti ṣe àrànṣe fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(30,48,'30:48','Allāhu ni Ẹni tó ń fi àwọn atẹ́gùn ránṣẹ́. (Atẹ́gùn náà) sì máa gbé ẹ̀ṣújò dìde. (Allāhu) yó sì tẹ́ (ẹ̀ṣújò) sílẹ̀ s’ójú sánmọ̀ bí Ó bá ṣe fẹ́. Ó sì máa dá a kélekèle (sí ojú sánmọ̀). O sì máa rí òjò tí ó ma máa jáde láààrin rẹ̀. Nígbà tí Ó bá sì rọ (òjò náà) fún ẹni tí Ó bá fẹ́ nínú àwọn ẹrúsìn Rẹ̀, nígbà náà wọn yó sì máa dunnú.');
INSERT INTO "aykyuni" VALUES(30,49,'30:49','Bí ó tilẹ̀ jẹ́ pé ṣíwájú kí Ó tó sọ̀ ọ́ kalẹ̀ fún wọn, wọ́n ti sọ̀rètí nù ṣíwájú rẹ̀.');
INSERT INTO "aykyuni" VALUES(30,50,'30:50','Nítorí náà, wòye sí àwọn orípa ìkẹ́ Allāhu, (wo) bí (Allāhu) ṣe ń ta ilẹ̀ jí lẹ́yìn tí ilẹ̀ ti kú. Dájúdájú (Allāhu) yẹn ni Ó kúkú máa sọ àwọn òkú di alààyè. Òun sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(30,51,'30:51','Dájúdájú tí A bá rán atẹ́gùn kan (sí wọn), kí wọ́n sì rí (n̄ǹkan ọ̀gbìn wọn) ní pípọ́n (jíjóná), dájúdájú wọn yó sì máa ṣàì moore lọ lẹ́yìn rẹ̀.');
INSERT INTO "aykyuni" VALUES(30,52,'30:52','Dájúdájú ìwọ kọ́ l’o máa mú àwọn òkú gbọ́rọ̀. O ò sì níí mú àwọn adití gbọ́ ìpè nígbà tí wọ́n bá kẹ̀yìn sí ọ, tí wọ́n ń lọ.');
INSERT INTO "aykyuni" VALUES(30,53,'30:53','Ìwọ kọ́ l’o máa fi ọ̀nà mọ àwọn afọ́jú níbi ìṣìnà wọn. Kò sí ẹni tí o máa mú gbọ́ ọ̀rọ̀ àfi ẹni tí ó bá gba àwọn āyah Wa gbọ́. Àwọn sì ni (mùsùlùmí) olùjupá-jusẹ̀-sílẹ̀ fún Allāhu.1 ');
INSERT INTO "aykyuni" VALUES(30,54,'30:54','Allāhu, Ẹni tí Ó ṣẹ̀dá yín láti (ara n̄ǹkan) lílẹ, lẹ́yìn náà, lẹ́yìn lílẹ Ó fun (yín ní) agbára, lẹ́yìn náà, lẹ́yìn agbára, Ó tún fi lílẹ àti ogbó (si yín lára). Ó ń dá ohunkóhun tí Ó bá fẹ́. Òun sì ni Onímọ̀, Alágbára.');
INSERT INTO "aykyuni" VALUES(30,55,'30:55','Àti pé ní ọjọ́ tí Àkókò náà bá ṣẹlẹ̀ (ìyẹn, ọjọ́ Àjíǹde), àwọn ẹlẹ́ṣẹ̀ yóò máa búra pé àwọn kò gbé (ilé ayé) ju àkókò kan lọ.” Báyẹn ni wọ́n ṣe máa ń ṣẹ́rí wọn kúrò níbi òdodo.');
INSERT INTO "aykyuni" VALUES(30,56,'30:56','Àwọn tí A fún ní ìmọ̀ àti ìgbàgbọ́ òdodo yóò sọ pé: “Dájúdájú nínú àkọsílẹ̀ ti Allāhu, ẹ ti gbé ilé ayé títí Ọjọ́ Àjíǹde (fi tó). Nítorí náà, èyí ni Ọjọ́ Àjíǹde, ṣùgbọ́n ẹ̀yin kò mọ̀.”');
INSERT INTO "aykyuni" VALUES(30,57,'30:57','Nítorí náà ní ọjọ́ yẹn, àwọn tó ṣàbòsí, àwáwí wọn kò níí ṣe wọ́n ní àǹfààní. Wọn kò sì níí fún wọn ní àyè láti ṣe ohun tí wọn yóò fi rí ìyọ́nú Allāhu.');
INSERT INTO "aykyuni" VALUES(30,58,'30:58','A kúkú ti fi oríṣiríṣi àkàwé lélẹ̀ nínú al-Ƙur’ān yìí fún àwọn ènìyàn. Dájúdájú tí o bá mú āyah kan wá fún wọn, dájúdájú àwọn tó ṣàì gbàgbọ́ yóò wí pé: “Ẹ̀yin (Òjíṣẹ́) kò jẹ́ kiní kan bí kò ṣe òpùrọ́.”');
INSERT INTO "aykyuni" VALUES(30,59,'30:59','Báyẹn ni Allāhu ṣe ń fi èdídí dí ọkàn àwọn tí kò nímọ̀.');
INSERT INTO "aykyuni" VALUES(30,60,'30:60','Nítorí náà, ṣe sùúrù. Dájúdájú àdéhùn Allāhu ni òdodo. Má ṣe jẹ́ kí àwọn tí kò mọ àmọ̀dájú (nípa Ọjọ́ ẹ̀san) sọ ọ́ di òpè (bí irú wọn).');
INSERT INTO "aykyuni" VALUES(31,1,'31:1','’Alif lām mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(31,2,'31:2','Ìwọ̀nyí ni àwọn āyah Tírà tí ó kún fún ọgbọ́n.1 ');
INSERT INTO "aykyuni" VALUES(31,3,'31:3','(Ó jẹ́) ìmọ̀nà àti ìkẹ́ fún àwọn olùṣe-rere;');
INSERT INTO "aykyuni" VALUES(31,4,'31:4','àwọn tó ń kírun, tí wọ́n ń yọ zakāh. Àwọn sì ni wọ́n ní àmọ̀dájú nípa Ọjọ́ Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(31,5,'31:5','Àwọn wọ̀nyẹn wà lórí ìmọ̀nà láti ọ̀dọ̀ Olúwa wọn. Àwọn wọ̀nyẹn sì ni àwọn olùjèrè.');
INSERT INTO "aykyuni" VALUES(31,6,'31:6','Ó wà nínú àwọn ènìyàn, ẹni tó ń ra ọ̀rọ̀ eréláti fi ṣi àwọn ènìyàn lọ́nà kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu pẹ̀lú àìnímọ̀ àti nítorí kí ó lè sọ ẹ̀sìn di yẹ̀yẹ́. Àwọn wọ̀nyẹn sì ni ìyà tí í yẹpẹrẹ ẹ̀dá wà fún. ');
INSERT INTO "aykyuni" VALUES(31,7,'31:7','Àti pé nígbà tí wọ́n bá ń ké àwọn āyah Wa fún un, ó máa pẹ̀yìndà ní ti ìgbéraga, àfi bí ẹni pé kò gbọ́ ọ, àfi bí ẹni pé èdídí wà nínú etí rẹ̀ méjèèjì. Nítorí náà, fún un ní ìró ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(31,8,'31:8','Dájúdájú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe iṣẹ́ rere, àwọn Ọgbà Ìdẹ̀ra ń bẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(31,9,'31:9','Olùṣegbére ni wọ́n nínú rẹ̀. (Ó jẹ́) àdéhùn Allāhu ní ti òdodo. Òun sì ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(31,10,'31:10','Ó dá àwọn sánmọ̀ láì ní òpó tí ẹ lè rí. Ó sì ju àwọn àpáta tó dúró gbagidi sínú ilẹ̀ kí ó má fi lè mì mọ yín lẹ́sẹ̀. Ó fọ́n gbogbo n̄ǹkan abẹ̀mí ká sórí ilẹ̀. A tún sọ omi kalẹ̀ láti sánmọ̀, A sì fi mú gbogbo oríṣiríṣi èso dáadáa hù jáde láti inú ilẹ̀.');
INSERT INTO "aykyuni" VALUES(31,11,'31:11','Èyí ni ẹ̀dá ti Allāhu. Nítorí náà, ẹ fi ohun tí àwọn mìíràn lẹ́yìn Rẹ̀ dá hàn mí! Rárá o! Àwọn alábòsí wà nínú ìṣìnà pọ́nńbélé ni.');
INSERT INTO "aykyuni" VALUES(31,12,'31:12','Dájúdájú A ti fún Luƙmọ̄n ní ọgbọ́n, pé: “Dúpẹ́ fún Allāhu.” Ẹnikẹ́ni tí ó bá dúpẹ́, ó ń dúpẹ́ fún ẹ̀mí ara rẹ̀ ni. Ẹnikẹ́ni tí ó bá sì ṣàì moore, dájúdájú Allāhu ni Ọlọ́rọ̀, Ẹlẹ́yìn (tí ẹyìn tọ́ sí).');
INSERT INTO "aykyuni" VALUES(31,13,'31:13','(Rántí) nígbà tí Luƙmọ̄n sọ fún ọmọ rẹ̀ nígbà tí ó ń ṣe wáàsí fún un, pé: “Ọmọ mi, má ṣe ṣẹbọ sí Allāhu. Dájúdájú ẹbọ ṣíṣe ni àbòsí ńlá.');
INSERT INTO "aykyuni" VALUES(31,14,'31:14','Àti pé A pa á ní àṣẹ fún ènìyàn nípa àwọn òbí rẹ̀ méjèèjì - ìyá rẹ̀ gbé e ká (nínú oyún) pẹ̀lú àìlera lórí àìlera, ó sì gba ọmú lẹ́nu rẹ̀ láààrin ọdún méjì - (A sọ) pé: “Dúpẹ́ fún Èmi àti àwọn òbí rẹ méjèèjì.” Ọ̀dọ̀ Mi sì ni àbọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(31,15,'31:15','Tí àwọn méjèèjì bá sì jà ọ́ lógun pé kí ó fi ohun tí ìwọ kò ní ìmọ̀ nípa rẹ̀ ṣẹbọ sí Mi, má ṣe tẹ̀lé àwọn méjèèjì.Fi dáadáa bá àwọn méjèèjì lòpọ̀ ní ilé ayé.2 Kí o sì tẹ̀lé ojú ọ̀nà ẹni tí ó bá ṣẹ́rí padà sí ọ̀dọ̀ Mi (ní ti ìronúpìwàdà). Lẹ́yìn náà, ọ̀dọ̀ Mi ni ibùpadàsí yín. Nítorí náà, Mo máa fún yín ní ìró ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(31,16,'31:16','Ọmọ mi, dájúdájú tí ó bá jẹ́ pé òdiwọ̀n èso kardal kanló wà nínú àpáta, tàbí nínú àwọn sánmọ̀, tàbí nínú ilẹ̀, Allāhu yóò mú un wá. Dájúdájú Allāhu ni Aláàánú, Alámọ̀tán. ');
INSERT INTO "aykyuni" VALUES(31,17,'31:17','Ọmọ mi, kírun, p’àṣẹ rere, kọ aburú, kí o sì ṣe sùúrù lórí ohun tí ó bá ṣẹlẹ̀ sí ọ. Dájúdájú ìyẹn wà nínú àwọn ìpinnu ọ̀rọ̀ tó pọn dandan.');
INSERT INTO "aykyuni" VALUES(31,18,'31:18','Má ṣe kọ párìkẹ́ rẹ sí ènìyàn. Má sì ṣe rìn lórí ilẹ̀ pẹ̀lú fáàrí. Dájúdájú Allāhu kò fẹ́ràn gbogbo onígbèéraga, onífáàrí.');
INSERT INTO "aykyuni" VALUES(31,19,'31:19','Jẹ́ kí ìrìn ẹsẹ̀ rẹ wà ní ìwọ̀ntún-wọ̀nsì, kí o sì rẹ ohùn rẹ nílẹ̀. Dájúdájú ohùn tó burú jùlọ mà ni ohùn kẹ́tẹ́kẹ́tẹ́.”');
INSERT INTO "aykyuni" VALUES(31,20,'31:20','Ṣé ẹ kò wòye pé, dájúdájú Allāhu rọ ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ fún yín, Ó sì pé àwọn ìkẹ́ Rẹ̀ fún yín ní gban̄gba àti ní kọ̀rọ̀? Ó sì wà nínú àwọn ènìyàn ẹni tó ń jiyàn nípa Allāhu láì ní ìmọ̀ (al-Ƙur’ān) àti ìmọ̀nà (nínú sunnah Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -) àti tírà (onímọ̀-ẹ̀sìn) tó ń tan ìmọ́lẹ̀ (sí ọ̀rọ̀ ẹ̀sìn).');
INSERT INTO "aykyuni" VALUES(31,21,'31:21','Nígbà tí wọ́n bá sì sọ fún wọn pé: “Ẹ tẹ̀lé ohun tí Allāhu sọ̀kalẹ̀.” Wọ́n á wí pé: “Rárá! A máa tẹ̀lé ohun tí a bá lọ́wọ́ àwọn bàbá wa ni.” (Ṣé wọn yóò tẹ̀lé àwọn bàbá wọn) t’òhun ti bí Èṣù ṣe ń pè wọ́n síbi ìyà Iná tó ń jò fòfò?');
INSERT INTO "aykyuni" VALUES(31,22,'31:22','Ẹnikẹ́ni tí ó bá jura rẹ̀ sílẹ̀ fún Allāhu, tí ó jẹ́ olùṣe-rere, dájúdájú onítọ̀un ti dìrọ̀ mọ́ okùn tó fọkàn balẹ̀ jùlọ. Àti pé ọ̀dọ̀ Allāhu ni ìkángun àwọn ọ̀rọ̀ (ẹ̀dá) wà.');
INSERT INTO "aykyuni" VALUES(31,23,'31:23','Ẹnikẹ́ni tí ó bá sì ṣàì gbàgbọ́, má ṣe jẹ́ kí àìgbàgbọ́ rẹ̀ kó ìbànújẹ́ bá ọ. Ọ̀dọ̀ Wa ni ibùpadàsí wọn. Nígbà náà, A máa fún wọn ní ìró ohun tí wọ́n ṣe níṣẹ́. Dájúdájú Allāhu ni Onímọ̀ nípa ohun tí ń bẹ nínú igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(31,24,'31:24','A máa fún wọn ní ìgbádùn díẹ̀. Lẹ́yìn náà, A máa taari wọn sínú ìyà tó nípọn.');
INSERT INTO "aykyuni" VALUES(31,25,'31:25','Dájúdájú tí o bá bi wọ́n léèrè pé: “Ta ni Ó dá àwọn sánmọ̀ àti ilẹ̀?”, dájúdájú wọ́n á wí pé: “Allāhu ni.” Sọ pé: “Gbogbo ẹyìn ń jẹ́ ti Allāhu, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn kò mọ̀.');
INSERT INTO "aykyuni" VALUES(31,26,'31:26','Ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀. Dájúdájú Allāhu, Òun ni Ọlọ́rọ̀, Ẹlẹ́yìn.');
INSERT INTO "aykyuni" VALUES(31,27,'31:27','Tí ó bá jẹ́ pé kìkìdá ohun tó ń bẹ lórí ilẹ̀ ní igi ni gègé ìkọ̀wé, kí agbami odò jẹ́ tàdáà rẹ̀, agbami odò méje (tún wà) lẹ́yìn rẹ̀ (tó máa kún un), àwọn ọ̀rọ̀ Allāhu kò níí tán (lẹ́yìn tí àwọn n̄ǹkan ìkọ̀wé wọ̀nyí bá tán nílẹ̀). Dájúdájú Allāhu ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(31,28,'31:28','Ìṣẹ̀dá yín àti àjíǹde yín kò tayọ bí (ìṣẹ̀dá àti àjíǹde) ẹ̀mí ẹyọ kan. Dájúdájú Allāhu ni Olùgbọ́, Olùríran.');
INSERT INTO "aykyuni" VALUES(31,29,'31:29','Ṣé o ò rí i pé dájúdájú Allāhu l’Ó ń ti òru bọ inú ọ̀sán, Ó ń ti ọ̀sán bọ inú òru, Ó sì rọ òòrùn àti òṣùpá? Ìkọ̀ọ̀kan wọn máa rìn títí di gbèdéke àkókò kan.Àti pé (ṣé o ò rí i pé) dájúdájú Allāhu ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́? ');
INSERT INTO "aykyuni" VALUES(31,30,'31:30','Ìyẹn nítorí pé, dájúdájú Allāhu, Òun ni Òdodo. Àti pé dájúdájú ohun tí wọ́n ń pè lẹ́yìn Rẹ̀ ni irọ́. Dájúdájú Allāhu, Ó ga, Ó tóbi.');
INSERT INTO "aykyuni" VALUES(31,31,'31:31','Ṣé o ò rí i pé dájúdájú ọkọ̀ ojú-omi ń rìn ní ojú omi pẹ̀lú ìdẹ̀ra Rẹ̀, (ṣebí) nítorí kí (Allāhu) lè fi hàn yín nínú àwọn àmì Rẹ̀ ni? Dájúdájú àwọn àmì wà nínú ìyẹn fún gbogbo onísùúrù, olùdúpẹ́.');
INSERT INTO "aykyuni" VALUES(31,32,'31:32','Nígbà tí ìgbì omi bá bò wọ́n mọ́lẹ̀ dáru bí ẹ̀ṣújò, wọ́n máa pe Allāhu lẹ́ni tí yóò máa fi àfọ̀mọ́-ọkàn (àníyàn mímọ́) ṣe ẹ̀sìn fún Un.Nígbà tí Ó bá sì gbà wọ́n là sórí ilẹ̀, onídéédé sì máa wà nínú wọn. Kò sì sí ẹni tó máa tako àwọn āyah Wa àfi gbogbo ọ̀dàlẹ̀, aláìmoore. ');
INSERT INTO "aykyuni" VALUES(31,33,'31:33','Ẹ̀yin ènìyàn, ẹ bẹ̀rù Olúwa yín. Kí ẹ sì páyà ọjọ́ kan tí òbí kan kò níí ṣàǹfààní fún ọmọ rẹ̀; àti pé ọmọ kan, òun náà kò níí ṣàǹfààní kiní kan fún òbí rẹ̀. Dájúdájú àdéhùn Allāhu ni òdodo. Nítorí náà, ìṣẹ̀mí ayé yìí kò gbọdọ̀ tàn yín jẹ. (Èṣù) ẹlẹ́tàn kò sì gbọdọ̀ tàn yín jẹ nípa Allāhu.');
INSERT INTO "aykyuni" VALUES(31,34,'31:34','Dájúdájú Allāhu, ọ̀dọ̀ Rẹ̀ ni ìmọ̀ Àkókò náà wà. Ó ń sọ òjò kalẹ̀. Ó mọ ohun tó wà nínú àpòlùkẹ́.Ẹ̀mí kan kò sì mọ ohun tí ó máa ṣe níṣẹ́ ní ọ̀la. Ẹ̀mí kan kò sì mọ ilẹ̀ wo l’ó máa kú sí. Dájúdájú Allāhu ni Onímọ̀, Alámọ̀tán. ');
INSERT INTO "aykyuni" VALUES(32,1,'32:1','’Alif lām mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(32,2,'32:2','Ìsọ̀kalẹ̀ Tírà náà, kò sí iyèméjì nínú rẹ̀ (pé ó wá) láti ọ̀dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(32,3,'32:3','Tàbí wọ́n ń wí pé: “Ó dá àdápa irọ́ rẹ̀ ni.” Rárá o! Òdodo ni láti ọ̀dọ̀ Olúwa rẹ nítorí kí o lè fi ṣèkìlọ̀ fún àwọn ènìyàn kan, tí olùkìlọ̀ kan kò wá bá wọn rí ṣíwájú rẹ, nítorí kí wọ́n lè mọ̀nà.1 ');
INSERT INTO "aykyuni" VALUES(32,4,'32:4','Allāhu ni Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀, ilẹ̀ àti n̄ǹkan tó wà láààrin méjèèjì láààrin ọjọ́ mẹ́fà. Lẹ́yìn náà, Ó gúnwà sórí Ìtẹ́-ọlá. Kò sí aláàbò àti olùṣìpẹ̀ kan fún yín lẹ́yìn Rẹ̀. Ṣé ẹ kò níí lo ìrántí ni?');
INSERT INTO "aykyuni" VALUES(32,5,'32:5','Ó ń ṣètò ọ̀rọ̀ (ẹ̀dá) láti sánmọ̀ wá sórí ilẹ̀. Lẹ́yìn náà, (àbọ̀ ọ̀rọ̀ ẹ̀dá) yóò gùnkè tọ̀ Ọ́ lọ láààrin ọjọ́ kan tí òdiwọ̀n rẹ̀ tó ẹgbẹ̀rún ọdún nínú òǹkà tí ẹ̀ ń kà.1 ');
INSERT INTO "aykyuni" VALUES(32,6,'32:6','Ìyẹn ni Onímọ̀-ìkọ̀kọ̀ àti gban̄gba, Alágbára, Àṣàkẹ́-ọ̀run,');
INSERT INTO "aykyuni" VALUES(32,7,'32:7','Ẹni tó ṣe gbogbo n̄ǹkan tí Ó ṣẹ̀dá rẹ̀ ní dáadáa. Ó sì bẹ̀rẹ̀ ìṣẹ̀dá ènìyàn láti inú ẹrẹ̀.');
INSERT INTO "aykyuni" VALUES(32,8,'32:8','Lẹ́yìn náà, Ó ṣe àwọn àrọ́mọdọ́mọ rẹ̀ láti ara ohun tí A mú jáde láti ara omi lílẹ yẹpẹrẹ.');
INSERT INTO "aykyuni" VALUES(32,9,'32:9','Lẹ́yìn náà, Ó to (oríkèéríkèé) rẹ̀ dọ́gba. Ó sì fẹ́ (ẹ̀mí) sí i lára nínú ẹ̀mí Rẹ̀ (tí Ó dá).Ó tún ṣe ìgbọ́rọ̀, ìríran àti ọkàn fún yín. Díẹ̀ ni ọpẹ́ tí ẹ̀ ń dá! ');
INSERT INTO "aykyuni" VALUES(32,10,'32:10','Wọ́n sì wí pé: “Ǹjẹ́ nígbà tí a bá ti pòórá sínú ilẹ̀, ǹjẹ́ àwa tún lè wà ní ẹ̀dá titun mọ́? Àní sẹ́, àwọn ni aláìgbàgbọ́ nínú ìpàdé Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(32,11,'32:11','Sọ pé: “Mọlāika ikú èyí tí A yàn fún yín máa gba ẹ̀mí yín. Lẹ́yìn náà, ọ̀dọ̀ Olúwa yín ni wọn máa da yín padà sí.”1 ');
INSERT INTO "aykyuni" VALUES(32,12,'32:12','(Ìwọ ìbá rí èèmọ̀) tí ó bá jẹ́ pé o rí àwọn ẹlẹ́ṣẹ̀ nígbà tí wọ́n bá sorí kọ́ ní ọ̀dọ̀ Olúwa wọn, (wọ́n sì máa wí pé): “Olúwa wa, a ti ríran, a sì ti gbọ́ràn (báyìí), nítorí náà, dá wa padà (sí ilé ayé) nítorí kí á lè lọ ṣe iṣẹ́ rere; dájúdájú àwa ni alámọ̀dájú.”');
INSERT INTO "aykyuni" VALUES(32,13,'32:13','Tí ó bá jẹ́ pé A bá fẹ́ ni, dájúdájú A ìbá fún gbogbo ẹ̀mí kọ̀ọ̀kan ní ìmọ̀nà rẹ̀, ṣùgbọ́n ọ̀rọ̀ náà ti ṣẹ láti ọ̀dọ̀ Mi (báyìí pé): “Dájúdájú Mo máa mú nínú àwọn àlùjànnú àti ènìyàn ní àpapọ̀ kún inú iná Jahanamọ.”');
INSERT INTO "aykyuni" VALUES(32,14,'32:14','Nítorí náà, ẹ tọ́ ìyà wò nítorí pé ẹ ti gbàgbé ìpàdé ọjọ́ yín (òní) yìí. Dájúdájú Àwa náà yóò gbàgbé yín (sínú Iná). Ẹ tọ́ ìyà gbére wò nítorí ohun tí ẹ̀ ń ṣe níṣẹ́.1 ');
INSERT INTO "aykyuni" VALUES(32,15,'32:15','Àwọn tó gbàgbọ́ nínú àwọn āyah Wa ni àwọn tí ó jẹ́ pé nígbà tí wọ́n bá fi ṣe ìṣítí fún wọn, wọn yóò dojú bolẹ̀ ní olùforíkanlẹ̀, wọn yó sì ṣe àfọ̀mọ́ pẹ̀lú ìdúpẹ́ fún Olúwa wọn. Wọn kò sì níí ṣègbéraga.');
INSERT INTO "aykyuni" VALUES(32,16,'32:16','Wọ́n ń gbé ẹ̀gbẹ́ wọn jìnnà sí ibùsùn. Wọ́n sì ń pe Olúwa wọn ní ti ìpáyà àti ìrètí. Wọ́n sì ń ná nínú ohun tí A pèsè fún wọn.');
INSERT INTO "aykyuni" VALUES(32,17,'32:17','Kò sí ẹ̀mí kan tí ó mọ ohun tí A fi pamọ́ fún wọn nínú àwọn n̄ǹkan ìtutù ojú. (Ó jẹ́) ẹ̀san ohun tí wọ́n ń ṣe níṣẹ́ (rere).');
INSERT INTO "aykyuni" VALUES(32,18,'32:18','Ǹjẹ́ ẹni tó jẹ́ onígbàgbọ́ òdodo dà bí ẹni tó jẹ́ òbìlẹ̀jẹ́ bí? Wọn kò dọ́gba.');
INSERT INTO "aykyuni" VALUES(32,19,'32:19','Ní ti àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe iṣẹ́ rere, àwọn ibùgbé (nínú) Ọ̀gbà Ìdẹ̀ra ń bẹ fún wọn. (Ó jẹ́) ohun tí A pèsè sílẹ̀ (dè wọ́n) nítorí ohun tí wọ́n ń ṣe níṣẹ́ (rere).');
INSERT INTO "aykyuni" VALUES(32,20,'32:20','Ní ti àwọn tó balẹ̀jẹ́, Iná ni ibùgbé wọn. Ìgbàkígbà tí wọ́n bá fẹ́ jáde kúrò nínú rẹ̀, A ó sì máa dá wọn padà sínú rẹ̀. A sì máa sọ fún wọn pé: “Ẹ tọ́ ìyà Iná tí ẹ̀ ń pè ní irọ́ wò.”1 ');
INSERT INTO "aykyuni" VALUES(32,21,'32:21','Dájúdájú A máa fún wọn tọ́ wò nínú ìyà tó kéré jùlọ (nílé ayé) yàtọ̀ sí ìyà tó tóbi jùlọ (lọ́run) nítorí kí wọ́n lè ṣẹ́rí padà (síbi òdodo ṣíwájú ikú wọn).');
INSERT INTO "aykyuni" VALUES(32,22,'32:22','Àti pé ta l’ó ṣe àbòsí tó tayọ ẹni tí wọn fi àwọn āyah Wa ṣe ìṣítí fún, lẹ́yìn náà, tí ó gbúnrí kúrò níbẹ̀? Dájúdájú Àwa máa gbẹ̀san lára àwọn ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(32,23,'32:23','Dájúdájú A fún (Ànábì) Mūsā ní Tírà. Nítorí náà, má ṣe wà nínú iyèméjì nípa bí ó ṣe pàdé rẹ̀ (ìyẹn, nínú ìrìn-àjò òru àti gígun sánmọ̀).A sì ṣe Tírà A sì ṣe Tírà náà ní ìmọ̀nà fún àwọn ọmọ ’Isrọ̄’īl. ');
INSERT INTO "aykyuni" VALUES(32,24,'32:24','Àti pé A ṣe àwọn kan nínú wọn ní Imām (aṣíwájú) tí wọ́n ń fi àṣẹ Wa (ohun tí A pa wọ́n ní àṣẹ rẹ̀ nínú at-Taorāh) tọ́ àwọn ènìyàn wọn sí ọ̀nà òdodo. (A ṣe wọ́n bẹ́ẹ̀) nígbà tí wọ́n ṣe sùúrù, tí wọ́n sì ní àmọ̀dájú nípa àwọn āyah Wa.');
INSERT INTO "aykyuni" VALUES(32,25,'32:25','Dájúdájú Olúwa rẹ, Ó máa ṣe ìdájọ́ láààrin wọn ní Ọjọ́ Àjíǹde nípa ohun tí wọ́n ń yapa-ẹnu lórí rẹ̀.');
INSERT INTO "aykyuni" VALUES(32,26,'32:26','Ṣé kò fojú hàn sí wọn pé, mélòó mélòó nínú àwọn ìran tí A ti parẹ́ ṣíwájú wọn. Àwọn náà sì ń rìn kọjá nínú àwọn ibùgbé wọn! Dájúdájú àwọn àmì wà nínú ìyẹn. Nítorí náà, ṣé wọn kò níí tẹ́tí gbọ́rọ̀ (òdodo ni)?');
INSERT INTO "aykyuni" VALUES(32,27,'32:27','Tàbí wọn kò rí i pé dájúdájú Àwa l’À ń wa omi òjò lọ sórí ilẹ̀ gbígbẹ, tí A sì ń fi mú irúgbìn jáde? Àwọn ẹran-ọ̀sìn wọn àti àwọn náà sì ń jẹ nínú rẹ̀. Nítorí náà, ṣé wọn kò ríran ni?');
INSERT INTO "aykyuni" VALUES(32,28,'32:28','Wọ́n sì ń wí pé: “Ìgbà wo ni Ìdájọ́ yìí máa ṣẹlẹ̀ tí ẹ bá jẹ́ olódodo?”');
INSERT INTO "aykyuni" VALUES(32,29,'32:29','Sọ pé: “Ní Ọjọ́ Ìdájọ́, ìgbàgbọ́ tí àwọn ọ̀ṣẹbọ máa fẹ́ ní nígbà tí wọ́n bá fojú rí ìyà Iná kò níí ṣe wọ́n ní àǹfààní. A kò sì níí fún wọn ní ìsinmi (nínú Iná).”1 ');
INSERT INTO "aykyuni" VALUES(32,30,'32:30','Nítorí náà, ṣẹ́rí kúrò lọ́dọ̀ wọn, kí o sì máa retí (Ọjọ́ Ìdájọ́). Dájúdájú àwọn náà ń retí (rẹ̀).');
INSERT INTO "aykyuni" VALUES(33,1,'33:1','Ìwọ Ànábì, bẹ̀rù Allāhu. Má sì tẹ̀lé àwọn aláìgbàgbọ́ àti àwọn ṣọ̀bẹ-ṣẹ̀lu mùsùlùmí. Dájúdájú Allāhu ń jẹ́ Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(33,2,'33:2','Tẹ̀lé ohun tí A ń mú wá fún ọ ní ìmísí láti ọ̀dọ̀ Olúwa rẹ. Dájúdájú Allāhu ń jẹ́ Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(33,3,'33:3','Kí o sì gbáralé Allāhu. Allāhu sì tó ní Olùṣọ́.');
INSERT INTO "aykyuni" VALUES(33,4,'33:4','Allāhu kò fún ènìyàn kan ní ọkàn méjì nínú ikùn rẹ̀. (Allāhu) kò sì sọ àwọn ìyàwó yín, tí ẹ̀ ń fi ẹ̀yìn wọn wé ẹ̀yìn ìyá yín, di ìyá yín. Àti pé (Allāhu) kò sọ àwọn ọmọ-ọlọ́mọ tí ẹ̀ ń pè ní ọmọ yín di ọmọ yín. Ìyẹn ni ọ̀rọ̀ ẹnu yín. Allāhu ń sọ òdodo. Àti pé Òun l’Ó ń fi (ẹ̀dá) mọ̀nà.');
INSERT INTO "aykyuni" VALUES(33,5,'33:5','Ẹ pè wọ́n pẹ̀lú orúkọ bàbá wọn. Òhun l’ó ṣe déédé jùlọ lọ́dọ̀ Allāhu, ṣùgbọ́n tí ẹ kò bá mọ (orúkọ) bàbá wọn, ọmọ ìyá yín nínú ẹ̀sìn àti ẹrú yín kúkú ni wọ́n.Kò sí ẹ̀ṣẹ̀ fún yín níbi ohun tí ẹ ti ṣàṣìṣe, ṣùgbọ́n (ẹ̀ṣẹ̀ wà níbi) ohun tí ọkàn yín mọ̀ọ́mọ̀ ṣe. Allāhu sì ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(33,6,'33:6','Ànábì ní ẹ̀tọ́ sí àwọn onígbàgbọ́ òdodo ju ẹ̀mí ara wọn lọ (nípa ìfẹ́ rẹ̀ àti ìdájọ́ rẹ̀).Àwọn aya rẹ̀ sì ni ìyá wọn. Nínú Tírà Allāhu, àwọn ẹbí, apá kan wọn ní ẹ̀tọ́ sí ogún jíjẹ ju apá kan lọ. (Àwọn ẹbí tún ní ẹ̀tọ́ sí ogún jíjẹ) ju àwọn onígbàgbọ́ òdodo àti àwọn tó kúrò nínú ìlú Mọkkah fún ààbò ẹ̀sìn, àfi tí ẹ bá máa ṣe dáadáa kan sí àwọn ọ̀rẹ́ yín (wọ̀nyí ni ogún lè fi kàn wọ́n pẹ̀lú àsọọ́lẹ̀).2 Ìyẹn wà nínú Tírà (Laohul-Mahfūṭḥ) ní àkọsílẹ̀. ');
INSERT INTO "aykyuni" VALUES(33,7,'33:7','(Rántí) nígbà tí A gba àdéhùn ní ọwọ́ àwọn Ànábì àti ní ọwọ́ rẹ, àti ní ọwọ́ (Ànábì) Nūh, ’Ibrọ̄hīm, Mūsā àti ‘Īsā ọmọ Mọryam. A gba àdéhùn ní ọwọ́ wọn ní àdéhùn tó nípọn');
INSERT INTO "aykyuni" VALUES(33,8,'33:8','nítorí kí (Allāhu) lè bèèrè (òdodo) àwọn olódodo nípa òdodo wọn. Ó sì pèsè ìyà ẹlẹ́ta-eléro sílẹ̀ de àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(33,9,'33:9','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ rántí ìdẹ̀ra Allāhu lórí yín, nígbà tí àwọn ọmọ ogun (oníjọ) dé ba yín. A sì rán atẹ́gùn àti àwọn ọmọ ogun tí ẹ kò fójú rí sí wọn. Allāhu sì ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́.1 ');
INSERT INTO "aykyuni" VALUES(33,10,'33:10','(Ẹ rántí) nígbà tí wọ́n dé ba yín láti òkè yín àti ìsàlẹ̀ yín, àti nígbà tí àwọn ojú yẹ̀ (sọ́tùn-ún sósì), tí àwọn ọkàn sí dé ọ̀nà-ọ̀fun (ní ti ìpáyà). Ẹ sì ń ro àwọn èrò kan nípa Allāhu.');
INSERT INTO "aykyuni" VALUES(33,11,'33:11','Níbẹ̀ yẹn ni wọ́n ti fi àdánwò kan àwọn onígbàgbọ́ òdodo. Wọ́n sì milẹ̀ mọ́ wọn lẹ́sẹ̀ ní ìmìtìtì líle.');
INSERT INTO "aykyuni" VALUES(33,12,'33:12','(Ẹ rántí) nígbà tí àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí àti àwọn tí àìsàn wà nínú ọkàn wọn ń wí pé: “Allāhu àti Òjíṣẹ́ Rẹ̀ kò ṣe àdéhùn kan fún wa bí kò ṣe ẹ̀tàn.”');
INSERT INTO "aykyuni" VALUES(33,13,'33:13','(Ẹ rántí) nígbà tí igun kan nínú wọn wí pé: “Ẹ̀yin ará Yẹthrib,kò sí àyè (ìṣẹ́gun) fún yín, nítorí náà, ẹ ṣẹ́rí padà (lọ́dọ̀ Òjíṣẹ́).” Apá kan nínú wọn sì ń tọrọ ìyọ̀ǹda lọ́wọ́ Ànábì, wọ́n ń wí pé: “Dájúdájú ilé wa dá páropáro ni.” (Ilé wọn) kò sì dá páropáro. Wọn kò sì gbèrò ohun kan tayọ síságun. ');
INSERT INTO "aykyuni" VALUES(33,14,'33:14','Àti pé tí ó bá jẹ́ pé (ọmọ ogun oníjọ) wọlé tọ̀ wọ́n wá láti àwọn ìloro ìlú (Mọdīnah), lẹ́yìn náà, kí wọ́n pe (àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí) sínú ẹbọ ṣíṣe, wọn ìbá ṣẹbọ. Wọn kò sì níí gbé nínú ìlú mọ́ tayọ ìgbà díẹ̀ (tí wọn yóò fi parẹ́).');
INSERT INTO "aykyuni" VALUES(33,15,'33:15','Dájúdájú wọ́n ti bá Allāhu ṣe àdéhùn ṣíwájú pé àwọn kò níí pẹ̀yìndà (láti ságun). Àdéhùn Allāhu sì jẹ́ ohun tí wọ́n máa bèèrè (lọ́wọ́ wọn).');
INSERT INTO "aykyuni" VALUES(33,16,'33:16','Sọ pé: “Síságun yín kò lè ṣe yín ní àǹfààní, tí ẹ bá sá fún ikú tàbí pípa (sí ojú ogun ẹ̀sìn. Tí ẹ bá sì ságun) nígbà náà, A kò níí fún yín ní ìgbádùn ayé bí kò ṣe fún ìgbà díẹ̀.');
INSERT INTO "aykyuni" VALUES(33,17,'33:17','Sọ pé: “Ta ni ẹni tí ó lè dá ààbò bò yín lọ́dọ̀ Allāhu tí Ó bá fẹ́ fi aburú kàn yín tàbí tí Ó bá fẹ́ kẹ yín?” Wọn kò sì lè rí aláàbò tàbí alárànṣe kan lẹ́yìn Allāhu.');
INSERT INTO "aykyuni" VALUES(33,18,'33:18','Dájúdájú Allāhu ti mọ àwọn tó ń fa ènìyàn sẹ́yìn nínú yín àti àwọn tó ń wí fún àwọn arakùnrin wọn pé: “Ẹ máa bọ̀ wá sí ọ̀dọ̀ wa.” Wọn kò sì níí lọ sí ojú ogun ẹ̀sìn àfi (ogun) díẹ̀.');
INSERT INTO "aykyuni" VALUES(33,19,'33:19','Wọ́n ní ahun si yín (láti ṣe ìrànlọ́wọ́). Nígbà tí ìbẹ̀rù (ogun) bá dé, o máa rí wọn tí wọn yóò máa wò ọ́. Ojú wọn yó sì máa yí kiri ràkọ̀ràkọ̀ (ní ti ìbẹ̀rù) bí ẹni tí ikú kì mọ́lẹ̀ láti pa, ṣùgbọ́n nígbà tí ìbẹ̀rù (ogun) bá lọ, (tí ìkógun bá dé), wọn yóò máa fi àwọn ahọ́n kan tó mú bérébéré ba yín sọ̀rọ̀ ní ti ṣíṣe ọ̀kánjúà sí oore náà. Àwọn wọ̀nyẹn kò gbàgbọ́ ní òdodo. Nítorí náà, Allāhu ba àwọn iṣẹ́ wọn jẹ́. Ìyẹn sì ń jẹ́ ìrọ̀rùn fún Allāhu.');
INSERT INTO "aykyuni" VALUES(33,20,'33:20','Wọ́n ń lérò pé àwọn ọmọ ogun oníjọ kò tí ì lọ, (wọ́n sì ti túká). Tí àwọn ọmọ ogun oníjọ bá (sì padà) dé, dájúdájú (àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí) yóò fẹ́ kí àwọn ti wà ní oko láààrin àwọn Lárúbáwá oko, kí wọ́n máa bèèrè nípa àwọn ìró yín (pé ṣé ẹ ti kú tán tàbí ẹ sì wà láyé). Tí ó bá sì jẹ́ pé wọ́n wà láààrin yín, wọn kò níí jagun bí kò ṣe fún ìgbà díẹ̀.');
INSERT INTO "aykyuni" VALUES(33,21,'33:21','Dájúdájú àwòkọ́ṣe rere wà fún yín lára Òjíṣẹ́ Allāhu fún ẹnikẹ́ni tí ó bá ń retí (ẹ̀san) Allāhu àti Ọjọ́ Ìkẹ́yìn, tí ó sì rántí Allāhu ní ọ̀pọ̀lọ́pọ̀ (ìgbà).');
INSERT INTO "aykyuni" VALUES(33,22,'33:22','Nígbà tí àwọn onígbàgbọ́ òdodo rí àwọn ọmọ ogun oníjọ, wọ́n sọ pé: “Èyí ni ohun tí Allāhu àti Òjíṣẹ́ Rẹ̀ ṣe ní àdéhùn fún wa.Allāhu àti Òjíṣẹ́ Rẹ̀ ti sọ òdodo ọ̀rọ̀.” (Rírí wọn) kò ṣe àlékún kan fún wọn bí kò ṣe (àlékún) ìgbàgbọ́ òdodo àti ìjuwọ́-jusẹ̀-sílẹ̀ (fún àṣẹ Allāhu). ');
INSERT INTO "aykyuni" VALUES(33,23,'33:23','Ó wà nínú àwọn onígbàgbọ́ òdodo, àwọn ọkùnrin kan tí wọ́n jẹ́ olódodo nípa àdéhùn tí wọ́n bá Allāhu ṣe; ó wà nínú wọn ẹni tí ó pé àdéhùn rẹ̀ (tó sì kú sójú ogun ẹ̀sìn), ó sì wà nínú wọn ẹni tó ń retí (ikú tirẹ̀). Wọn kò sì yí (àdéhùn) padà rárá.');
INSERT INTO "aykyuni" VALUES(33,24,'33:24','(Ìwọ̀nyí rí bẹ́ẹ̀) nítorí kí Allāhu lè fi (òdodo) àwọn olódodo san wọ́n ní ẹ̀san òdodo wọn, àti nítorí kí Ó lè jẹ àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí ní ìyà tí Ó bá fẹ́ tàbí nítorí kí Ó lè gba ìronúpìwàdà wọn. Dájúdájú Allāhu, Ó ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(33,25,'33:25','Allāhu sì dá àwọn tó ṣàì gbàgbọ́ padà tòhun ti ìbínú wọn; ọwọ́ wọn kò sì tẹ oore kan. Allāhu sì tó àwọn onígbàgbọ́ òdodo níbi ogun náà. Allāhu sì ń jẹ́ Alágbára, Olùborí.');
INSERT INTO "aykyuni" VALUES(33,26,'33:26','(Allāhu) sì mú àwọn tó ṣèrànlọ́wọ́ fún (àwọn ọmọ ogun oníjọ) nínú àwọn onítírà sọ̀kalẹ̀ kúrò nínú àwọn odi wọn. Ó sì ju ẹ̀rù sínú ọkàn wọn. Ẹ̀ ń pa igun kan (nínú wọn), ẹ sì ń kó igun kan lẹ́rú.1 ');
INSERT INTO "aykyuni" VALUES(33,27,'33:27','(Allāhu) sì jogún ilẹ̀ wọn, ilé wọn, dúkìá wọn àti ilẹ̀ tí ẹ kò tẹ̀ rí fún yín. Allāhu sì ń jẹ́ Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(33,28,'33:28','Ìwọ Ànábì, sọ fún àwọn ìyàwó rẹ pé: “Tí ẹ bá fẹ́ ìṣẹ̀mí ayé yìí àti ọ̀ṣọ́ rẹ̀, ẹ wá níbí kí n̄g fún yín ní ẹ̀bùn ìkọ̀sílẹ̀,kí n̄g sì fi yín sílẹ̀ ní ìfisílẹ̀ tó rẹwà. ');
INSERT INTO "aykyuni" VALUES(33,29,'33:29','Tí ẹ bá sì fẹ́ ti Allāhu àti ti Òjíṣẹ́ Rẹ̀ àti ilé Ìkẹ́yìn, dájúdájú Allāhu ti pèsè ẹ̀san ńlá sílẹ̀ de àwọn olùṣe-rere lóbìnrin nínú yín.”');
INSERT INTO "aykyuni" VALUES(33,30,'33:30','Ẹ̀yin ìyàwó Ànábì, ẹnikẹ́ni nínú yín tí ó bá ṣe ìbàjẹ́ tó fojú hàn, A máa di àdìpèlé ìyà ìlọ́po méjì fún un. Ìyẹn sì ń jẹ́ ìrọ̀rùn fún Allāhu.');
INSERT INTO "aykyuni" VALUES(33,31,'33:31','Ẹnikẹ́ni tí ó bá ń tẹ̀lé ti Allāhu àti ti Òjíṣẹ́ Rẹ̀ nínú yín, tí ó sì ń ṣe iṣẹ́ rere, A máa fún un ní ẹ̀san ìlọ́po méjì. A sì ti pèsè ìjẹ-ìmu alápọ̀n-ọ́nlé sílẹ̀ dè é.”');
INSERT INTO "aykyuni" VALUES(33,32,'33:32','Ẹ̀yin ìyàwó Ànábì, ẹ kò dà bí ẹnì kan kan nínú àwọn obìnrin, tí ẹ bá ti bẹ̀rù (Allāhu). Ẹ má ṣe dínhùn (sí àwọn ọkùnrin létí) nítorí kí ẹni tí àrùn wà nínú ọkàn rẹ̀ má baá jẹ̀rankàn. Kí ẹ sì máa sọ ọ̀rọ̀ tó dára.');
INSERT INTO "aykyuni" VALUES(33,33,'33:33','Ẹ fìdí mọ́lé yín. Ẹ má ṣe fi ara àti ọ̀ṣọ́ hàn níta gẹ́gẹ́ bí ti ìfara-fọ̀ṣọ́-hàn ìgbà àìmọ̀kan àkọ́kọ́ (ìyẹn, ṣíwájú kí ẹ tó di mùsùlùmí). Ẹ kírun. Ẹ yọ Zakāh. Kí ẹ sì tẹ̀lé (àṣẹ) Allāhu àti (àṣẹ) Òjíṣẹ́ Rẹ̀. Allāhu kàn ń gbèrò láti mú ẹ̀gbin kúrò lára yín, ẹ̀yin ará ilé (Ànábì). Àti pé Ó (kàn ń gbèrò láti) fọ̀ yín mọ́ tónítóní ni.');
INSERT INTO "aykyuni" VALUES(33,34,'33:34','Ẹ rántí ohun tí wọ́n ń ké nínú ilé yín nínú àwọn āyah Allāhu àti ìjìnlẹ̀ òye (ìyẹn, sunnah Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -). Dájúdájú Allāhu ń jẹ́ Aláàánú, Onímọ̀-ìkọ̀kọ̀.1 ');
INSERT INTO "aykyuni" VALUES(33,35,'33:35','Dájúdájú àwọn mùsùlùmí lọ́kùnrin àti mùsùlùmí lóbìnrin, àwọn onígbàgbọ́ òdodo lọ́kùnrin àti àwọn onígbàgbọ́ òdodo lóbìnrin, àwọn olùtẹ̀lé-àṣẹ Allāhu lọ́kùnrin àti àwọn olùtẹ̀lé-àṣẹ Allāhu lóbìnrin, àwọn olódodo lọ́kùnrin àti àwọn olódodo lóbìnrin, àwọn onísùúrù lọ́kùnrin àti àwọn onísùúrù lóbìnrin, àwọn olùtẹríba fún Allāhu lọ́kùnrin àti àwọn olùtẹríba fún Allāhu lóbìnrin, àwọn olùtọrẹ lọ́kùnrin àti àwọn olùtọrẹ lóbìnrin, àwọn aláàwẹ̀ lọ́kùnrin àti àwọn aláàwẹ̀ lóbìnrin, àwọn tó ń ṣọ́ abẹ́ wọn lọ́kùnrin àti àwọn tó ń ṣọ́ abẹ́ wọn lóbìnrin, àwọn olùrántí Allāhu ní ọ̀pọ̀lọ́pọ̀ lọ́kùnrin àti àwọn olùrántí Allāhu (ní ọ̀pọ̀lọ́pọ̀) lóbìnrin; Allāhu ti pèsè àforíjìn àti ẹ̀san ńlá sílẹ̀ dè wọ́n.');
INSERT INTO "aykyuni" VALUES(33,36,'33:36','Kò tọ́ fún onígbàgbọ́ òdodo lọ́kùnrin àti onígbàgbọ́ òdodo lóbìnrin, nígbà tí Allāhu àti Òjíṣẹ́ Rẹ̀ bá ti parí ọ̀rọ̀ kan, láti ní ẹ̀ṣà (ọ̀rọ̀ mìíràn) fún ọ̀rọ̀ ara wọn. Àti pé ẹnikẹ́ni tí ó bá yapa (àṣẹ) Allāhu àti (àṣẹ) Òjíṣẹ́ Rẹ̀, dájúdájú ó ti ṣìnà ní ìṣìnà pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(33,37,'33:37','(Rántí) nígbà tí ò ń sọ fún ẹni tí Allāhu ṣèdẹ̀ra fún, tí ìwọ náà ṣèdẹ̀ra fúnpé: “Mú ìyàwó rẹ dání, kí o sì bẹ̀rù Allāhu.” O sì ń fi pamọ́ sínú ẹ̀mí rẹ ohun tí Allāhu yó ṣàfi hàn rẹ̀. Àti pé ò ń páyà àwọn ènìyàn. Allāhu l’Ó sì lẹ́tọ̀ọ́ jùlọ pé kí o páyà Rẹ̀. Nígbà tí Zaed ti parí bùkátà rẹ̀ lọ́dọ̀ rẹ̀ (tí ó sì ti kọ̀ ọ́ sílẹ̀), A ti ṣe é ní ìyàwó fún ọ nítorí kí ó má baà jẹ́ láìfí fún àwọn onígbàgbọ́ òdodo láti fẹ́ ìyàwó ọmọ-ọlọ́mọ tí wọ́n ń pè ní ọmọ wọn nígbà tí wọ́n bá ti parí bùkátà wọn lọ́dọ̀ wọn (tí wọ́n sì ti kọ̀ wọ́n sílẹ̀). Àṣẹ Allāhu sì gbọ́dọ̀ ṣẹ.2 ');
INSERT INTO "aykyuni" VALUES(33,38,'33:38','Kò sí láìfí fún Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a - nípa ohun tí Allāhu ṣe ní ẹ̀tọ́ fún un. (Ó jẹ́) ìlànà Allāhu lórí àwọn tó ti lọ ṣíwájú. Àti pé àṣẹ Allāhu jẹ́ àkọọ́lẹ̀ kan tó gbọ́dọ̀ ṣẹ.');
INSERT INTO "aykyuni" VALUES(33,39,'33:39','(Kọ́ṣe àwọn tó ti lọ ṣíwájú nínú àwọn Òjíṣẹ́) àwọn tó ń jẹ́ iṣẹ́ Allāhu, tí wọ́n ń páyà Rẹ̀, tí wọn kò sì páyà ẹnì kan àyàfi Allāhu. Allāhu sì tó ní Olùṣírò.');
INSERT INTO "aykyuni" VALUES(33,40,'33:40','(Ànábì) Muhammad kì í ṣe bàbá ẹnì kan kan nínú àwọn ọkùnrin yín, ṣùgbọ́n (ó jẹ́) Òjíṣẹ́ Allāhu àti òpin àwọn Ànábì.Allāhu sì ń jẹ́ Onímọ̀ nípa gbogbo n̄ǹkan. ');
INSERT INTO "aykyuni" VALUES(33,41,'33:41','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ rántí Allāhu ní ìrántí púpọ̀.');
INSERT INTO "aykyuni" VALUES(33,42,'33:42','Ẹ ṣàfọ̀mọ́ fún Allāhu ní àárọ̀ àti ní ìrọ̀lẹ́.');
INSERT INTO "aykyuni" VALUES(33,43,'33:43','(Allāhu) Òun ni Ẹni tó ń kẹ yín, àwọn mọlāika Rẹ̀ (sì ń tọrọ àforíjìn fún yín), nítorí kí Allāhu lè mu yín kúrò láti inú àwọn òkùnkùn wá sínú ìmọ́lẹ̀. Àti pé Ó ń jẹ́ Àṣàkẹ́-ọ̀run fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(33,44,'33:44','Ìkíni wọn ní ọjọ́ tí wọn yóò pàdé Rẹ̀ ni “àlàáfíà”. Ó sì ti pèsè ẹ̀san alápọ̀n-ọ́nlẹ́ sílẹ̀ dè wọ́n.');
INSERT INTO "aykyuni" VALUES(33,45,'33:45','Ìwọ Ànábì, dájúdájú Àwa rán ọ níṣẹ́ (pé kí o jẹ́) olùjẹ́rìí, oníròó-ìdùnnú, olùkìlọ̀,');
INSERT INTO "aykyuni" VALUES(33,46,'33:46','olùpèpè sí ọ̀dọ̀ Allāhu pẹ̀lú ìyọ̀ǹda Rẹ̀ àti àtùpà ìmọ́lẹ̀.');
INSERT INTO "aykyuni" VALUES(33,47,'33:47','Fún àwọn onígbàgbọ́ òdodo ní ìró ìdùnnú pé dájúdájú oore àjùlọ ńlá wà fún wọn lọ́dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(33,48,'33:48','Má ṣe tẹ̀lé àwọn aláìgbàgbọ́ àti àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí. Fi bí wọ́n ṣe ń kó ìnira bá ọ sílẹ̀. Kí o sì gbáralé Allāhu. Allāhu sì ń tó ní Alámòjúútó.');
INSERT INTO "aykyuni" VALUES(33,49,'33:49','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo lọ́kùnrin, nígbà tí ẹ bá fẹ́ àwọn onígbàgbọ́ òdodo lóbìnrin,lẹ́yìn náà tí ẹ kọ̀ wọ́n sílẹ̀ ṣíwájú kí ẹ tó bá wọn ní àṣepọ̀ lọ́kọ-láya, kò sí opó ṣíṣe kan tí wọn yóò ṣe fún yín. Nítorí náà, ẹ fún wọn ní ẹ̀bùn ìkọ̀sílẹ̀. Kí ẹ sì fi wọ́n sílẹ̀ ní ìfisílẹ̀ tó rẹwà. ');
INSERT INTO "aykyuni" VALUES(33,50,'33:50','Ìwọ Ànábì, dájúdájú Àwa ṣe é ní ẹ̀tọ́ fún ọ àwọn ìyàwó rẹ̀, tí o fún ní owó-orí wọn, àti àwọn ẹrú rẹ nínú àwọn tí Allāhu fí ṣe ìkógun fún ọ àti àwọn ọmọbìnrin arákùnrin bàbá rẹ àti àwọn ọmọbìnrin arábìnrin bàbá rẹ, àti àwọn ọmọbìnrin arákùnrin ìyá rẹ, àti àwọn ọmọbìnrin arábìnrin ìyá rẹ, àwọn tó fi ìlú Mọkkah sílẹ̀ wá sí ìlú Mọdīnah pẹ̀lú rẹ, àti onígbàgbọ́ òdodo lóbìnrin, tí ó bá fi ara rẹ̀ tọrẹ fún Ànábì, tí Ànábì náà sì fẹ́ fi ṣe ìyàwó. Ìwọ nìkan ni (èyí) wà fún, kò sí fún àwọn onígbàgbọ́ òdodo lọ́kùnrin. A ti mọ ohun tí A ṣe ní ẹ̀tọ́ fún wọn nípa àwọn ìyàwó wọn àti àwọn ẹrú wọn.(Èyí rí bẹ́ẹ̀) nítorí kí ó má baà sí láìfí fún ọ. Allāhu sì ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(33,51,'33:51','Dájọ́ sí sísúnmọ́ ẹni tí o bá fẹ́ nínú wọn. Fa ẹni tí o bá fẹ́ mọ́ra.Àti pé ẹni kẹ́ni tí o bá tún wá (láti súnmọ́) nínú àwọn tí o kò pín oorun fún, kò sí ẹ̀ṣẹ̀ fún ọ (láti ṣe bẹ́ẹ̀). Ìyẹn súnmọ́ jùlọ láti mú ojú wọn tutù ìdùnnú. Wọn kò sì níí banújẹ́. Gbogbo wọn yó sì yọ́nú sí ohunkóhun tí o bá fún wọn. Allāhu mọ ohun tí ń bẹ nínú ọkàn yín. Allāhu sì ń jẹ́ Onímọ̀, Aláfaradà. ');
INSERT INTO "aykyuni" VALUES(33,52,'33:52','Kò lẹ́tọ̀ọ́ fún ọ (láti fẹ́) àwọn obìnrin (mìíràn) lẹ́yìn (ìsọ̀rí àwọn obìnrin tí A ti sọ ṣíwájú kò sì lẹ́tọ̀ọ́ fún ọ) láti fi àwọn obìnrin (mìíràn) pààrọ̀ wọn, kódà kí dáadáa wọn jọ ọ́ lójú, àfi àwọn ẹrú rẹ. Allāhu sì ń jẹ́ Olùṣọ́ lórí gbogbo n̄ǹkan.2 ');
INSERT INTO "aykyuni" VALUES(33,53,'33:53','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe wọ àwọn inú ilé Ànábì àfi tí wọ́n bá yọ̀ǹda fún yín láti wọlé jẹun láì níí jẹ́ ẹni tí yóò máa retí kí oúnjẹ jinná, ṣùgbọ́n tí wọ́n bá pè yín (fún oúnjẹ) ẹ wọ inú ilé nígbà náà. Nígbà tí ẹ bá sì jẹun tán, ẹ túká, ẹ má ṣe jókòó kalẹ̀ tira yin fún ọ̀rọ̀ kan mọ́ (nínú ilé rẹ̀). Dájúdájú ìyẹn ń kó ìnira bá Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -. Ó sì ń tijú yín. Allāhu kò sì níí tijú níbi òdodo. Nígbà tí ẹ bá sì fẹ́ bèèrè n̄ǹkan ní ọ̀dọ̀ àwọn ìyàwó rẹ̀, ẹ bèèrè rẹ̀ ní ọ̀dọ̀ wọn ní ẹ̀yìn gàgá. Ìyẹn jẹ́ àfọ̀mọ́ jùlọ fún ọkàn yín àti ọkàn wọn. Kò tọ́ fún yín láti kó ìnira bá Òjíṣẹ́ Allāhu. (Kò sì tọ́ fún yín) láti fẹ́ àwọn ìyàwó rẹ̀ lẹ́yìn (ikú) rẹ̀ láéláé. Dájúdájú ìyẹn jẹ́ n̄ǹkan ńlá ní ọ̀dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(33,54,'33:54','Tí ẹ bá ṣàfi hàn kiní kan tàbí ẹ fi pamọ́, dájúdájú Allāhu ń jẹ́ Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(33,55,'33:55','Kò sí ẹ̀ṣẹ̀ fún àwọn ìyàwó Ànábì nípa àwọn bàbá wọn àti àwọn ọmọkùnrin wọn àti àwọn arákùnrin wọn àti àwọn ọmọkùnrin arákùnrin wọn àti àwọn ọmọkùnrin arábìnrin wọn àti àwọn obìnrin (ẹgbẹ́) wọn àti àwọn ẹrúkùnrin wọn (láti wọlé tì wọ́n.) Ẹ bẹ̀rù Allāhu. Dájúdájú Allāhu ń jẹ́ Arínú-róde gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(33,56,'33:56','Dájúdájú Allāhu àti àwọn mọlāika Rẹ̀ ń kẹ́ Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -. Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ tọrọ ìkẹ́ fún un, kí ẹ sì kí i ní kíkí àlàáfíà.1 ');
INSERT INTO "aykyuni" VALUES(33,57,'33:57','Dájúdájú àwọn tó ń fi ìnira kan Allāhuàti Òjíṣẹ́ Rẹ̀, Allāhu ti ṣẹ́bi lé wọn nílé ayé àti ní ọ̀run. Ó sì ti pèsè ìyà tó ń yẹpẹrẹ ẹ̀dá sílẹ̀ dè wọ́n. ');
INSERT INTO "aykyuni" VALUES(33,58,'33:58','Àwọn tó ń fi ìnira kan àwọn onígbàgbọ́ òdodo lọ́kùnrin àti onígbàgbọ́ òdodo lóbìnrin nípa n̄ǹkan tí wọn kò ṣe, dájúdájú wọ́n ti ru ẹrù (ọ̀ràn) ìparọ́mọ́ni àti ẹ̀ṣẹ̀ pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(33,59,'33:59','Ìwọ Ànábì sọ fún àwọn ìyàwó rẹ, àwọn ọmọbìnrin rẹ àti àwọn obìnrin onígbàgbọ́ òdodo pé kí wọ́n máa gbé àwọn aṣọ jilbāb wọn wọ̀ sí ara wọn pátápátá. Ìyẹn súnmọ́ jùlọ láti fi mọ̀ wọ́n (ní olùbẹ̀rù Allāhu). Nípa bẹ́ẹ̀, wọn kò níí fi ìnira kàn wọ́n. Allāhu sì ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run.1 ');
INSERT INTO "aykyuni" VALUES(33,60,'33:60','Tí àwọn ṣọ̀bẹ-ṣẹ̀lu mùsùlùmí, àti àwọn tí àìsàn ń bẹ nínú ọkàn wọn àti àwọn túlétúlénínú ìlú Mọdīnah kò bá jáwọ́ (níbi aburú), dájúdájú A máa dẹ ọ́ sí wọn, (o sì máa borí wọn). Lẹ́yìn náà, wọn kò sì níí bá ọ gbé àdúgbò pọ̀ mọ́ àfi fún ìgbà díẹ̀. ');
INSERT INTO "aykyuni" VALUES(33,61,'33:61','Wọ́n di ẹni-ìṣẹ́bilé níbikíbi tí ọwọ́ bá ti bà wọ́n; wọ́n máa mú wọn, wọ́n sì máa pa wọ́n tààrà.');
INSERT INTO "aykyuni" VALUES(33,62,'33:62','(Ó jẹ́) ìṣe Allāhu lórí àwọn tó ti lọ ṣíwájú.O ò sì níí rí ìyípadà kan fún ìṣe Allāhu. ');
INSERT INTO "aykyuni" VALUES(33,63,'33:63','Àwọn ènìyàn ń bi ọ́ léèrè nípa Àkókò náà. Sọ pé: “Ọ̀dọ̀ Allāhu nìkan ṣoṣo ni ìmọ̀ nípa rẹ̀ wà. Àti pé kí l’ó máa fi mọ̀ ọ́ pé ó ṣeé ṣe kí Àkókò náà ti súnmọ́!”');
INSERT INTO "aykyuni" VALUES(33,64,'33:64','Dájúdájú Allāhu ṣẹ́bi lé àwọn aláìgbàgbọ́. Ó sì pèsè Iná tó ń jó sílẹ̀ dè wọ́n.');
INSERT INTO "aykyuni" VALUES(33,65,'33:65','Olùṣegbére ni wọ́n nínú rẹ̀ títí láéláé; wọn kò níí rí olùṣọ́ tàbí alárànṣe kan.');
INSERT INTO "aykyuni" VALUES(33,66,'33:66','Ní ọjọ́ tí A óò máa yí ojú wọn padà nínú Iná, wọn yó sì wí pé: “Yéè! Àwa ìbá tẹ̀lé ti Allāhu, àwa ìbá sì tẹ̀lé ti Òjíṣẹ́ náà.”');
INSERT INTO "aykyuni" VALUES(33,67,'33:67','Wọ́n wí pé: “Olúwa wa! Dájúdájú àwa tẹ̀lé àwọn aṣíwájú wa àti àwọn àgbààgbà wa. Wọ́n sì ṣì wá lọ́nà.');
INSERT INTO "aykyuni" VALUES(33,68,'33:68','Olúwa wa! Fún wọn ní ìlọ́po méjì nínú ìyà. Kí O sì ṣẹ́bi lé wọn ní ìṣẹ́bilé tó tóbi.”');
INSERT INTO "aykyuni" VALUES(33,69,'33:69','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe dà bí àwọn tó fi ìnira kan (Ànábì) Mūsā. Allāhu sì ṣàfọ̀mọ́ rẹ̀ nínú ohun tí wọ́n wí. Ó sì jẹ́ ẹni iyì lọ́dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(33,70,'33:70','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ bẹ̀rù Allāhu. Kí ẹ sì sọ ọ̀rọ̀ déédé.');
INSERT INTO "aykyuni" VALUES(33,71,'33:71','(Allāhu) máa ṣàtúnṣe àwọn iṣẹ́ yín fún yín, Ó sì máa ṣàforíjìn àwọn ẹ̀ṣẹ̀ yín fún yín. Àti pé ẹni tí ó bá tẹ̀lé ti Allāhu àti Òjíṣẹ́ Rẹ̀, ó kúkú ti jèrè ní èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(33,72,'33:72','Dájúdájú Àwa fi àgbàfipamọ́ (iṣẹ́ ẹ̀sìn àṣegbaláádá) lọ àwọn sánmọ̀ àti ilẹ̀ àti àpáta. Wọ́n kọ̀ láti gbé e; wọ́n páyà rẹ̀. Ènìyàn sì gbé e. Dájúdájú (ènìyàn) jẹ́ alábòsí, aláìmọ̀kan.1 ');
INSERT INTO "aykyuni" VALUES(33,73,'33:73','(Ènìyàn tẹ́rí gba ẹ̀sìn àṣegbaláádá) nítorí kí Allāhu lè fi ìyà jẹ àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí lọ́kùnrin, àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí lóbìnrin, àwọn ọ̀sẹbọ lọ́kùnrin àti àwọn ọ̀ṣẹbọ lóbìnrin àti nítorí kí Allāhu lè gba ìronúpìwàdà fún àwọn onígbàgbọ́ òdodo lọ́kùnrin àti àwọn onígbàgbọ́ òdodo lóbìnrin. Allāhu sì ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(34,1,'34:1','Gbogbo ẹyìn ń jẹ́ ti Allāhu, Ẹni tí ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ ń jẹ́ tiRẹ̀. Àti pé tiRẹ̀ ni gbogbo ẹyìn ní ọ̀run. Òun sì ni Ọlọ́gbọ́n, Alámọ̀tán.');
INSERT INTO "aykyuni" VALUES(34,2,'34:2','Ó mọ ohunkóhun tó ń wọnú ilẹ̀ àti ohunkóhun tó ń jáde látinú rẹ̀. (Ó mọ) ohunkóhun tó ń sọ̀kalẹ̀ látinú sánmọ̀ àti ohunkóhun tó ń gùnkè lọ sínú rẹ̀. Òun sì ni Àṣàkẹ́-ọ̀run, Aláforíjìn.');
INSERT INTO "aykyuni" VALUES(34,3,'34:3','Àwọn aláìgbàgbọ́ wí pé: “Àkókò náà kò níí dé bá wa.” Sọ pé: “Bẹ́ẹ̀ ni. Èmi fi Olúwa mi búra. Dájúdájú ó máa dé ba yín (láti ọ̀dọ̀) Onímọ̀-ìkọ̀kọ̀ (Ẹni tí) òdiwọ̀n ọmọ iná-igún kò pamọ́ fún nínú àwọn sánmọ̀ àti nínú ilẹ̀. (Kò sí n̄ǹkan tí ó) kéré sí ìyẹn tàbí tí ó tóbi (jù ú lọ) àfi kí ó wà nínú àkọsílẹ̀ tó yanjú.”');
INSERT INTO "aykyuni" VALUES(34,4,'34:4','(Ó máa ṣẹlẹ̀) nítorí kí Allāhu lè san ẹ̀san fún àwọn tó gbàgbọ́ ní òdodo, tí wọ́n tún ṣe iṣẹ́ rere. Àwọn wọ̀nyẹn ni àforíjìn àti èsè alápọ̀n-ọ́nlé ń bẹ fún.');
INSERT INTO "aykyuni" VALUES(34,5,'34:5','Àwọn tó ṣe iṣẹ́ (burúkú) nípa àwọn āyah Wa, tí wọ́n ń dá àwọn ènìyàn ní agara (láti tẹ̀lé àwọn āyah Wa, tí wọ́n sì lérò pé àwọn máa mórí bọ́ lọ́dọ̀ Allāhu); àwọn wọ̀nyẹn, ìyà kan nínú ìyà ẹlẹ́ta-eléró ń bẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(34,6,'34:6','Àwọn tí A fún ní ìmọ̀ mọ̀ pé, èyí tí wọ́n sọ̀kalẹ̀ fún ọ láti ọ̀dọ̀ Olúwa rẹ, òhun ni òdodo, àti pé ó ń ṣe ìtọ́sọ́nà sí ọ̀nà Alágbára, Ẹlẹ́yìn.');
INSERT INTO "aykyuni" VALUES(34,7,'34:7','Àwọn tó ṣàì gbàgbọ́ wí pé: “Ṣé kí á tọ́ka yín sí ọkùnrin kan tí ó máa fún yín ní ìró pé nígbà tí wọ́n bá tú (ara) yín ká tán pátápátá (sínú erùpẹ̀, tí ẹ ti di erùpẹ̀), pé dájúdájú ẹ máa padà wà ní ẹ̀dá titun?');
INSERT INTO "aykyuni" VALUES(34,8,'34:8','Ṣé ó dá àdápa irọ́ mọ́ Allāhu ni tàbí àlùjànnú ń bẹ lára rẹ̀ ni?” Rárá o! Àwọn tí kò gba Ọjọ́ Ìkẹ́yìn gbọ́ ti wà nínú ìyà àti ìṣìnà tó jìnnà.');
INSERT INTO "aykyuni" VALUES(34,9,'34:9','Ṣé wọn kò rí ohun tó ń bẹ níwájú wọn àti ohun tó ń bẹ lẹ́yìn wọn ní sánmọ̀ àti ilẹ̀? Tí A bá fẹ́, Àwa ìbá jẹ́ kí ilẹ̀ ri mọ́ wọn lẹ́sẹ̀, tàbí kí Á já apá kan nínú sánmọ̀ lulẹ̀ lé wọn lórí mọ́lẹ̀. Dájúdájú àmì kan wà nínú ìyẹn fún gbogbo ẹrúsìn, olùronúpìwàdà.');
INSERT INTO "aykyuni" VALUES(34,10,'34:10','Dájúdájú A ti fún (Ànábì) Dāwūd ní oore àjùlọ láti ọ̀dọ̀ Wa; Ẹ̀yin àpáta, ẹ ṣe àfọ̀mọ́ pẹ̀lú rẹ̀. (A pe) àwọn ẹyẹ náà (pé kí wọ́n ṣe bẹ́ẹ̀.) A sì rọ irin fún un.');
INSERT INTO "aykyuni" VALUES(34,11,'34:11','(A sọ fún un) pé ṣe àwọn ẹ̀wù irin tó máa bo ara dáadáa, ṣe òrùka fún ẹ̀wù irin náà níwọ̀n-níwọ̀n. Kí ẹ sì ṣe rere. Dájúdájú Èmi ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(34,12,'34:12','Àti pé (A tẹ) atẹ́gùn lórí bá fún (Ànábì) Sulaemọ̄n, ìrìn oṣù kan ni ìrìn òwúrọ̀ rẹ̀, ìrìn oṣù kan sì ni ìrìn ìrọ̀lẹ́ rẹ̀ A sì mú kí odò idẹ máa ṣàn nínú ilẹ̀ fún un. Ó sì wà nínú àwọn àlùjànnú, èyí tó ń ṣiṣẹ́ (fún un) níwájú rẹ̀ pẹ̀lú ìyọ̀ǹda Olúwa rẹ̀. Àti pé ẹni tí ó bá yẹ̀ kúrò níbi àṣẹ Wa nínú wọn, A máa fún un ní ìyà iná tó ń jó tọ́ wò. ');
INSERT INTO "aykyuni" VALUES(34,13,'34:13','Wọ́n ń ṣe iṣẹ́ tí ó bá fẹ́ fún un nípa mímọ àwọn ilé (ilé ìjọ́sìn àti ilé ìgbé), àwọn ère,àwọn àwo tó jìn kòtò tó fẹ̀ bí àbàtà2 àti àwọn ìkòkò tó rídìí múlẹ̀.3 Ẹ̀yin ènìyàn (Ànábì) Dāwūd, ẹ ṣiṣẹ́ ìdúpẹ́ (fún Allāhu). Díẹ̀ nínú àwọn ẹrúsìn Mi sì ni olùdúpẹ́. ');
INSERT INTO "aykyuni" VALUES(34,14,'34:14','Nígbà tí A pàṣẹ pé kí ikú pa (Ànábì) Sulaemọ̄n, kò sí ohun tí ó mú àwọn àlùjànnú mọ̀ pé ó ti kú bí kò ṣe kòkòrò inú ilẹ̀ kan tí ó jẹ ọ̀pá rẹ̀. Nígbà tí ó wó lulẹ̀, ó hàn kedere sí àwọn àlùjànnú pé tí ó bá jẹ́ pé àwọn ní ìmọ̀ ìkọ̀kọ̀ ni, àwọn ìbá tí wà nínú (iṣẹ́) ìyà tó ń yẹpẹrẹ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(34,15,'34:15','Dájúdájú àmì kan wà fún àwọn Saba’ nínú ibùgbé wọn; (òhun ni) ọgbà oko méjì tó wà ní ọ̀tún àti ní òsì.“Ẹ jẹ nínú arísìkí Olúwa yín. Kí ẹ sì dúpẹ́ fún Un.” Ìlú tó dára ni (ilẹ̀ àwọn Saba’. Allāhu sì ni) Olúwa Aláforíjìn. ');
INSERT INTO "aykyuni" VALUES(34,16,'34:16','Wọ́n gbúnrí (níbi ẹ̀sìn). Nítorí náà, A rán adágún odò tí wọ́n mọ odi yíká sí wọn. A sì pààrọ̀ oko wọn méjèèjì fún wọn pẹ̀lú oko èso méjì mìíràn tí ó jẹ́ oko èso tó korò, oko igi àti kiní kan nínú igi sidir díẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(34,17,'34:17','Ìyẹn ni A fi san wọ́n ní ẹ̀san nítorí pé, wọ́n ṣàì moore. Ǹjẹ́ A máa san ẹnì kan lẹ́san ìyà bí kò ṣe aláìmoore?');
INSERT INTO "aykyuni" VALUES(34,18,'34:18','A sì fi àwọn ìlú kan tó wà ní gban̄gba sí ààrin ìlú tí A rán adágún odò síàti àwọn ìlú tí A fi ìbùkún sí. A sì ṣètò ibùsọ̀ níwọ̀n-níwọ̀n fún ìrìn-àjò ṣíṣe sínú àwọn ìlú náà.2 Ẹ rìn lọ sínú wọn ní òru àti ní ojú ọjọ́ pẹ̀lú ìfàyàbalẹ̀. ');
INSERT INTO "aykyuni" VALUES(34,19,'34:19','(Àwọn ará ìlú Saba’) wí pé: “Olúwa wa, mú àwọn ìrìn-àjò wa láti ìlú kan sí ìlú mìíràn jìnnà síra wọn.” Wọ́n ṣe àbòsí sí ẹ̀mí ara wọn.A sì sọ wọ́n di ìtàn. A tú wọn ká pátápátá (ìlú wọn di ahoro). Dájúdájú àwọn àmì kan wà nínú ìyẹn fún gbogbo onísùúrù, olùdúpẹ́. ');
INSERT INTO "aykyuni" VALUES(34,20,'34:20','Dájúdájú ’Iblīs ti sọ àbá-modá rẹ̀ di òdodo lórí wọn. Wọ́n sì tẹ̀lé e àfi igun kan nínú àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(34,21,'34:21','(’Iblīs) kò sì ní agbára kan lórí wọn bí kò ṣe pé kí A lè ṣàfi hàn ẹni tó gba Ọjọ́ Ìkẹ́yìn gbọ́ kúrò lára ẹni tó wà nínú iyèméjì nípa rẹ̀. Olùṣọ́ sì ni Olúwa rẹ lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(34,22,'34:22','Sọ pé: “Ẹ pe àwọn tí ẹ sọ pé (wọ́n jẹ́ ọlọ́hun) lẹ́yìn Allāhu.” Wọn kò ní ìkápá òdiwọ̀n ọmọ-iná igún nínú àwọn sánmọ̀ tàbí nínú ilẹ̀. Wọn kò sì ní ìpín kan nínú méjèèjì. Àti pé kò sí olùrànlọ́wọ́ kan fún Allāhu láààrin wọn.');
INSERT INTO "aykyuni" VALUES(34,23,'34:23','Ìṣìpẹ̀ kò sì níí ṣàǹfààní lọ́dọ̀ Allāhu àfi fún ẹni tí Ó bá yọ̀ǹda fún. (Inúfu-àyàfu ní àwọn olùṣìpẹ̀ àti àwọn olùṣìpẹ̀-fún máa wà) títí A óò fi yọ ìjáyà kúrò nínú ọkàn wọn. Wọ́n sì máa sọ (fún àwọn mọlāika) pé: “Kí ni Olúwa yín sọ (ní èsì ìṣìpẹ̀)?” Wọ́n máa sọ pé: “Òdodo l’Ó sọ (ìṣìpẹ̀ yin ti wọlé. Allāhu) Òun l’Ó ga, Ó tóbi.1 ');
INSERT INTO "aykyuni" VALUES(34,24,'34:24','Sọ pé: “Ta ni Ó ń pèsè fún yín láti inú àwọn sánmọ̀ àti ilẹ̀?” Sọ pé: “Allāhu ni.” Dájúdájú àwa tàbí ẹ̀yin wà nínú ìmọ̀nà tàbí nínú ìṣìnà pọ́nńbélé.1 ');
INSERT INTO "aykyuni" VALUES(34,25,'34:25','Sọ pé: “Wọn kò níí bi yín léèrè nípa ẹ̀ṣẹ̀ tí a dá; wọn kò sì níí bi àwa náà nípa ohun tí ẹ̀ ń ṣe níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(34,26,'34:26','Sọ pé: “Olúwa wa yóò kó wa jọ papọ̀. Lẹ́yìn náà, Ó máa fi òdodo ṣèdájọ́ láààrin wa. Òun sì ni Onídàájọ́, Onímọ̀.”');
INSERT INTO "aykyuni" VALUES(34,27,'34:27','Sọ pé: “Ẹ fi hàn mí ná àwọn òrìṣà tí ẹ dàpọ̀ mọ́ Allāhu.” Rárá (kò ní akẹgbẹ́). Rárá sẹ́, Òun ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(34,28,'34:28','A kò rán ọ níṣẹ́ àfi sí gbogbo ènìyàn pátápátá; (o jẹ́) oníròó-ìdùnnú àti olùkìlọ̀ ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn kò mọ̀.');
INSERT INTO "aykyuni" VALUES(34,29,'34:29','Wọ́n ń wí pé: “Ìgbà wo ni àdéhùn yìí yóò ṣẹ tí ẹ bá jẹ́ olódodo?”');
INSERT INTO "aykyuni" VALUES(34,30,'34:30','Sọ pé: “Àdéhùn ọjọ́ kan ń bẹ fún yín, tí ẹ kò níí lè sún un ṣíwájú di àkókò kan, ẹ kò sì níí lè fà á sẹ́yìn.”1 ');
INSERT INTO "aykyuni" VALUES(34,31,'34:31','Àwọn tó ṣàì gbàgbọ́ wí pé: “A kò níí ní ìgbàgbọ́ nínú al-Ƙur’ān yìí àti èyí tó wá ṣíwájú rẹ̀.” Tí ó bá jẹ́ pé o bá rí wọn ni nígbà tí wọ́n bá dá àwọn alábòsí dúró níwájú Olúwa wọn, (o máa rí wọn tí) apá kan wọn yóò dá ọ̀rọ̀ náà padà sí apá kan; àwọn tí wọ́n sọ di ọ̀lẹ (nínú wọn) yóò wí fún àwọn tó ṣègbéraga (nínú wọn) pé: “Tí kì í bá ṣe ẹ̀yin ni, àwa ìbá jẹ́ onígbàgbọ́ òdodo.”');
INSERT INTO "aykyuni" VALUES(34,32,'34:32','Àwọn tó ṣègbéraga yó sì wí fún àwọn tí wọ́n sọ di ọ̀lẹ pé: “Ṣé àwa l’a ṣẹ yín lórí kúrò nínú ìmọ̀nà lẹ́yìn tí ó dé ba yín? Rárá o! Ọ̀daràn ni yín ni.”');
INSERT INTO "aykyuni" VALUES(34,33,'34:33','Àwọn tí wọ́n sọ di ọ̀lẹ yó sì wí fún àwọn tó ṣègbéraga pé: “Rárá! Ète òru àti ọ̀sán (láti ọ̀dọ̀ yín ló kóbá wa) nígbà tí ẹ̀ ń pa wá ní àṣẹ pé kí á ṣàì gbàgbọ́ nínú Allāhu, kí á sì sọ (àwọn kan) di ẹgbẹ́ Rẹ̀.” Wọ́n fi àbámọ̀ wọn pamọ́ nígbà tí wọ́n rí ìyà. A sì kó ẹ̀wọ̀n sí ọrùn àwọn tó ṣàì gbàgbọ́. Ṣé A óò san wọ́n ní ẹ̀san kan bí kò ṣe ohun tí wọ́n ń ṣe níṣẹ́?');
INSERT INTO "aykyuni" VALUES(34,34,'34:34','Àti pé A kò rán olùkìlọ̀ kan sí ìlú kan àyàfi kí àwọn onígbẹdẹmukẹ ìlú náà wí pé: “Dájúdájú àwa ṣàì gbàgbọ́ nínú ohun tí Wọ́n fi ran yín níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(34,35,'34:35','Wọ́n tún wí pé: “Àwa ní dúkìá àti ọmọ jù (yín lọ); wọn kò sì níí jẹ wá níyà.”');
INSERT INTO "aykyuni" VALUES(34,36,'34:36','Sọ pé: “Dájúdájú Olúwa mi, Ó ń tẹ́ arísìkí sílẹ̀ fún ẹni tí Ó bá fẹ́. Ó sì ń díwọ̀n rẹ̀ (fún ẹlòmíìràn), ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn kò mọ̀.');
INSERT INTO "aykyuni" VALUES(34,37,'34:37','Kì í ṣe àwọn dúkìá yín, kì í sì ṣe àwọn ọmọ yín ni n̄ǹkan tí ó máa mu yín súnmọ́ Wa pẹ́kípẹ́kí àfi ẹni tí ó bá gbàgbọ́ ní òdodo, tí ó sì ṣe iṣẹ́ rere. Àwọn wọ̀nyẹn ni ẹ̀san ìlọ́po wà fún nípa ohun tí wọ́n ṣe níṣẹ́. Wọn yó sì wà nínú àwọn àyè gíga (nínú Ọgbà Ìdẹ̀ra) lẹ́ni ìfàyàbalẹ̀.');
INSERT INTO "aykyuni" VALUES(34,38,'34:38','Àwọn tó ń ṣe iṣẹ́ (burúkú) nípa àwọn āyah Wa, tí wọ́n ń dá àwọn ènìyàn ní agara (láti tẹ̀lé àwọn āyah Wa, tí wọ́n sì lérò pé àwọn máa mórí bọ́ lọ́dọ̀ Allāhu); àwọn wọ̀nyẹn ni (àwọn mọlāika) máa kó wá sínú Iná.”');
INSERT INTO "aykyuni" VALUES(34,39,'34:39','Sọ pé: “Dájúdájú Olúwa mi, Ó ń tẹ́ arísìkísílẹ̀ fún ẹni tí Ó bá fẹ́. Ó sì ń díwọ̀n rẹ̀ fún ẹlòmíìràn. Àti pé ohunkóhun tí ẹ bá ná, Òun l’Ó máa fi (òmíràn) rọ́pò rẹ̀. Ó sì l’óore jùlọ nínú àwọn olùpèsè.”2 ');
INSERT INTO "aykyuni" VALUES(34,40,'34:40','Ní ọjọ́ tí Allāhu yóò kó gbogbo wọn jọ pátápátá, lẹ́yìn náà Ó máa sọ fún àwọn mọlāika pé: “Ṣé ẹ̀yin ni wọ́n ń jọ́sìn fún?”');
INSERT INTO "aykyuni" VALUES(34,41,'34:41','Wọ́n á sọ pé: “Mímọ́ ni fún Ọ! Ìwọ ni Aláàbò wa, kì í ṣe àwọn. Rárá (wọn kò jọ́sìn fún wa). Àwọn àlùjànnú ni wọ́n ń jọ́sìn fún; ọ̀pọ̀lọ́pọ̀ wọn ni wọ́n gbàgbọ́ nínú àwọn àlùjànnú.');
INSERT INTO "aykyuni" VALUES(34,42,'34:42','Nítorí náà, ní òní apá kan yín kò ní ìkápá àǹfààní, kò sì ní ìkápá ìnira fún apá kan. A sì máa sọ fún àwọn tó ṣàbòsí pé: “Ẹ tọ́ ìyà Iná tí ẹ̀ ń pè ní irọ́ wò.”');
INSERT INTO "aykyuni" VALUES(34,43,'34:43','Àti pé nígbà tí wọ́n bá ń ké àwọn āyah Wa tó yanjú fún wọn, wọ́n á wí pé: “Kí ni èyí bí kò ṣe ọkùnrin kan tí ó fẹ́ ṣẹ yín lórí kúrò níbi n̄ǹkan tí àwọn bàbá yín ń jọ́sìn fún.” Wọ́n tún wí pé: “Kí ni èyí bí kò ṣe àdápa irọ́.” Àti pé àwọn tó ṣàì gbàgbọ́ wí nípa òdodo nígbà tí ó dé bá wọn pé: “Kí ni èyí bí kò ṣe idán pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(34,44,'34:44','A kò fún wọn ní àwọn tírà kan kan tí wọ́n ń kọ́ ẹ̀kọ́ nínú rẹ̀, A kò sì rán olùkìlọ̀ kan sí wọn ṣíwájú rẹ.1 ');
INSERT INTO "aykyuni" VALUES(34,45,'34:45','Àwọn tó ṣíwájú wọn náà pe òdodo ní irọ́. (Ọwọ́ àwọn wọ̀nyí) kò sì tí ì tẹ ìdá kan ìdá mẹ́wàá nínú ohun tí A fún (àwọn tó ṣíwájú). Síbẹ̀síbẹ̀ wọ́n pe àwọn Òjíṣẹ́ Mi ní òpùrọ́. Báwo sì ni bí Mo ṣe (fi ìyà) kọ (aburú fún wọn) ti rí!');
INSERT INTO "aykyuni" VALUES(34,46,'34:46','Sọ pé: “Ohun kan ṣoṣo ni mò ń ṣe wáàsí rẹ̀ fún yín pé, ẹ dúró nítorí ti Allāhu ní méjì àti ní ẹyọ kọ̀ọ̀kan. Lẹ́yìn náà, kí ẹ ronú jinlẹ̀. Kò sí àlùjànnú kan lára ẹni yín. Kò sì jẹ́ kiní kan bí kò ṣe olùkìlọ̀ fún yín ṣíwájú ìyà líle kan.”');
INSERT INTO "aykyuni" VALUES(34,47,'34:47','Sọ pé: “Èmi kò bi yín léèrè owó-ọ̀yà kan, ẹ̀yin lẹ kúkú lowó yín. Kò sí owó ọ̀yà mi (lọ́dọ̀ ẹnì kan) àfi lọ́dọ̀ Allāhu. Òun sì ni Arínú-róde gbogbo n̄ǹkan.”');
INSERT INTO "aykyuni" VALUES(34,48,'34:48','Sọ pé: “Dájúdájú Olúwa mi, Onímọ̀-ìkọ̀kọ̀ l’Ó ń mú òdodo náà wá.”');
INSERT INTO "aykyuni" VALUES(34,49,'34:49','Sọ pé: “Òdodo ti dé. Irọ́ (èṣù) kò lè mú n̄ǹkan bẹ, kò sì lè dá n̄ǹkan padà (lẹ́yìn tó ti kú).”');
INSERT INTO "aykyuni" VALUES(34,50,'34:50','Sọ pé: “Tí mo bá ṣìnà, mo ṣìnà fún ẹ̀mí ara mi ni. Tí mo bá sì mọ̀nà, nípa ohun tí Olúwa mi fi ránṣẹ́ sì mi ní ìmísí ni. Dájúdájú Òun ni Olùgbọ́, Alásùn-únmọ́ ẹ̀dá.”1 ');
INSERT INTO "aykyuni" VALUES(34,51,'34:51','Tí ó bá jẹ́ pé o lè rí (ẹ̀sín wọn ni) nígbà tí ẹ̀rù bá dé bá wọn (ní Ọjọ́ Àjíǹde, o máa rí i pé), kò níí sí ìmóríbọ́ kan (fún wọn). A sì máa gbá wọn mú láti àyè tó súnmọ́.');
INSERT INTO "aykyuni" VALUES(34,52,'34:52','Wọn yóò wí pé: “A gbàgbọ́ nínú (al-Ƙur’ān báyìí).” Báwo ni ọwọ́ wọn ṣe lè tẹ ìgbàgbọ́ òdodo láti àyè tó jìnnà (ìyẹn, ọ̀run).”');
INSERT INTO "aykyuni" VALUES(34,53,'34:53','Wọ́n kúkú ti ṣàì gbàgbọ́ nínú rẹ̀ ṣíwájú (nílé ayé). Wọ́n sì ń sọ̀rọ̀ nípa ìkọ̀kọ̀ láti àyè tó jìnnà (ìyẹn, ilé ayé).');
INSERT INTO "aykyuni" VALUES(34,54,'34:54','A sì fi gàgá sí ààrin àwọn àti ohun tí wọ́n ń ṣojú kòkòrò rẹ̀gẹ́gẹ́ bí A ti ṣe fún àwọn ẹgbẹ́ wọn ní ìṣáájú. Dájúdájú wọ́n wà nínú iyèméjì tó gbópọn (nípa Ọjọ́ Àjíǹde). ');
INSERT INTO "aykyuni" VALUES(35,1,'35:1','Gbogbo ẹyìn ń jẹ́ ti Allāhu, Olùpilẹ̀dá àwọn sánmọ̀ àti ilẹ̀, (Ẹni tí) Ó ṣe àwọn mọlāika alápá méjì àti mẹ́ta àti mẹ́rin ní Òjíṣẹ́. Ó ń ṣe àlékún ohun tí Ó bá fẹ́ lára ẹ̀dá. Dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan.1 ');
INSERT INTO "aykyuni" VALUES(35,2,'35:2','Ohunkóhun tí Allāhu bá ṣí ọ̀nà rẹ̀ sílẹ̀ nínú ìkẹ́ fún àwọn ènìyàn, kò sí ẹni tí ó lè dì í mọ́wọ́ (láti dá a dúró). Ohunkóhun tí Ó bá sì mú dání, kò sí ẹni tí ó lè mú un wá lẹ́yìn Rẹ̀. Òun sì ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(35,3,'35:3','Ẹ̀yin ènìyàn, ẹ rántí ìdẹ̀ra Allāhu lórí yín. Ǹjẹ́ ẹ̀lẹ́dàá kan yàtọ̀ sí Allāhu tún wà tí ó ń pèsè fún yín láti inú sánmọ̀ àti ilẹ̀? Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Nítorí náà, báwo ni wọ́n ṣe ń ṣẹ yín lórí kúrò níbi òdodo?');
INSERT INTO "aykyuni" VALUES(35,4,'35:4','Tí wọ́n bá pè ọ́ ní òpùrọ́, wọ́n kúkú ti pe àwọn Òjíṣẹ́ kan ní òpùrọ́ ṣíwájú rẹ. Ọ̀dọ̀ Allāhu sì ni wọ́n máa ṣẹ́rí àwọn ọ̀rọ̀ ẹ̀dá padà sí.');
INSERT INTO "aykyuni" VALUES(35,5,'35:5','Ẹ̀yin ènìyàn, dájúdájú àdéhùn Allāhu ni òdodo. Ẹ má ṣe jẹ́ kí ìṣẹ̀mí ayé tàn yín jẹ. Kí ẹ sì má ṣe jẹ́ kí (Èṣù) ẹlẹ́tàn tàn yín jẹ.');
INSERT INTO "aykyuni" VALUES(35,6,'35:6','Dájúdájú Èṣù ni ọ̀tá fún yín. Nítorí náà, ẹ mú un ní ọ̀tá. Ó kàn ń pe àwọn ìjọ rẹ̀ nítorí kí wọ́n lè jẹ́ èrò inú Iná tó ń jó.');
INSERT INTO "aykyuni" VALUES(35,7,'35:7','Àwọn tó ṣàì gbàgbọ́, ìyà líle ń bẹ fún wọn. Àwọn tó sì gbàgbọ́ ní òdodo, tí wọ́n ṣe iṣẹ́ rere, àforíjìn àti ẹ̀san tó tóbi ń bẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(35,8,'35:8','Ṣé ẹni tí wọ́n ṣe iṣẹ́ aburú ọwọ́ rẹ̀ ní ọ̀ṣọ́ fún, tí ó sì ń rí i ní (iṣẹ́) dáadáa, (l’o fẹ́ máa banújẹ́ nítorí rẹ̀?) Dájúdájú Allāhu ń ṣi ẹni tí Ó bá fẹ́ lọ́nà. Ó sì ń fi ọ̀nà mọ ẹni tí Ó bá fẹ́. Nítorí náà, má ṣe fi ìbanújẹ́ pa ara rẹ̀ nítorí tiwọn. Dájúdájú Allāhu ni Onímọ̀ nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(35,9,'35:9','Allāhu ni Ẹni tí Ó fi àwọn atẹ́gùn ránṣẹ́. (Atẹ́gùn náà) sì máa tú ẹ̀ṣújò sókè. A sì fi fún ìlú (tí ilẹ̀ rẹ̀) ti kú lómi mu. A sì fi ta ilẹ̀ náà jí lẹ́yìn tí ó ti kú. Báyẹn ni àjíǹde (ẹ̀dá yó ṣe rí).');
INSERT INTO "aykyuni" VALUES(35,10,'35:10','Ẹní tí ó bá ń fẹ́ iyì dájúdájú ti Allāhu ni gbogbo iyì pátápátá. Ọ̀dọ̀ Rẹ̀ ni ọ̀rọ̀ dáadáa ń gòkè lọ. Ó sì ń gbé iṣẹ́ rere gòkè (sí ọ̀dọ̀ Rẹ̀). Àwọn tí wọ́n ń pète àwọn aburú, ìyà líle ń bẹ fún wọn. Ète àwọn wọ̀nyẹn sì máa parun.');
INSERT INTO "aykyuni" VALUES(35,11,'35:11','Allāhu da yín láti ara erùpẹ̀. Lẹ́yìn náà, (Ó tún da yín) láti ara àtọ̀. Lẹ́yìn náà, Ó ṣe yín ní akọ-abo. Obìnrin kan kò níí lóyún, kò sì níí bímọ àfi pẹ̀lú ìmọ̀ Rẹ̀. Àti pé A kò níí fa ẹ̀mí ẹlẹ́mìí-gígùn gùn, A kò sì ní ṣe àdínkù nínú ọjọ́ orí (ẹlòmíìràn), àfi kí ó ti wà nínú tírà kan. Dájúdájú ìyẹn rọrùn fún Allāhu.');
INSERT INTO "aykyuni" VALUES(35,12,'35:12','Àwọn odò méjì náà kò dọ́gba; èyí ni (omi) dídùn gan-an, tí mímu rẹ̀ ń lọ tìnrín ní ọ̀fun. Èyí sì ni (omi) iyọ̀ tó móró. Àti pé nínú gbogbo (omi odò wọ̀nyí) l’ẹ ti ń jẹ ẹran (odò àti ẹja) tútù. Ẹ sì ń wa kùsà àwọn ohun ọ̀ṣọ́ tí ẹ̀ ń wọ̀ (sára). O sì ń rí àwọn ọkọ̀ ojú-omi tó ń la ààrin omi kọjá nítorí kí ẹ lè wá nínú oore Allāhu àti nítorí kí ẹ lè dúpẹ́ (fún Un).');
INSERT INTO "aykyuni" VALUES(35,13,'35:13','(Allāhu) ń ti òru bọ inú ọ̀sán. Ó sì ń ti ọ̀sán bọ inú òru. Ó rọ òòrùn àti òṣùpá; ìkọ̀ọ̀kan wọn máa rìn fún gbèdéke àkókò kan.Ìyẹn ni Allāhu, Olúwa yín. TiRẹ̀ ni ìjọba. Àwọn tí ẹ sì ń pè lẹ́yìn Rẹ̀, wọn kò ní ìkápá kan lórí èpo kóró inú dàbínù. ');
INSERT INTO "aykyuni" VALUES(35,14,'35:14','Tí ẹ bá pè wọ́n, wọn kò lè gbọ́ ìpè yín. Tí ó bá sì jẹ́ pé wọ́n gbọ́, wọn kò lè da yín lóhùn. Àti pé ní Ọjọ́ Àjíǹde, wọn yóò tako bí ẹ ṣe fi wọ́n ṣe akẹgbẹ́ fún Allāhu. Kò sì sí ẹni tí ó lè fún ọ ní ìró kan (nípa ẹ̀dá ju) irú (ìró tí) Onímọ̀-ìkọ̀kọ̀ (fún ọ nípa wọn).');
INSERT INTO "aykyuni" VALUES(35,15,'35:15','Ẹ̀yin ènìyàn, ẹ̀yin ni aláìní (tí ẹ ní bùkátà) sí Allāhu. Allāhu, Òun ni Ọlọ́rọ̀, Ẹlẹ́yìn.');
INSERT INTO "aykyuni" VALUES(35,16,'35:16','Tí Ó bá fẹ́, Ó máa mu yín kúrò lórí ilẹ̀. Ó sì máa mú ẹ̀dá titun wá.');
INSERT INTO "aykyuni" VALUES(35,17,'35:17','Ìyẹn kò sì lágbara kan lára Allāhu.1 ');
INSERT INTO "aykyuni" VALUES(35,18,'35:18','Ẹlẹ́rù ẹ̀ṣẹ̀ kan kò níí ru ẹrù ẹ̀ṣẹ̀ ẹlòmíìràn. Kódà kí ẹnì kan tí ẹrù ẹ̀ṣẹ̀ wọ̀ lọ́rùn ké sí (ẹlòmíìràn) fún àbárù ẹrù ẹ̀ṣẹ̀ rẹ̀, wọn kò níí bá a ru kiní kan nínú rẹ̀, ìbáà jẹ́ ìbátan.Àwọn tí ò ń ṣèkìlọ̀ fún ni àwọn tó ń páyà Olúwa wọn ní ìkọ̀kọ̀, tí wọ́n sì ń kírun. Ẹni tí ó bá ṣàfọ̀mọ́ (ara rẹ̀ kúrò nínú ìwà ẹ̀ṣẹ̀), ó ṣàfọ̀mọ́ fún ẹ̀mí ara rẹ̀ ni. Ọ̀dọ̀ Allāhu sì ni àbọ̀ ẹ̀dá. ');
INSERT INTO "aykyuni" VALUES(35,19,'35:19','Afọ́jú àti olùríran kò dọ́gba.');
INSERT INTO "aykyuni" VALUES(35,20,'35:20','Àwọn òkùnkùn àti ìmọ́lẹ̀ (kò dọ́gba).');
INSERT INTO "aykyuni" VALUES(35,21,'35:21','Àwọn ibòji àti ìgbóná òòrùn (kò dọ́gba).');
INSERT INTO "aykyuni" VALUES(35,22,'35:22','Àwọn alààyè àti òkú kò dọ́gba. Dájúdájú Allāhu l’Ó ń fún ẹni tí Ó bá fẹ́ ní ọ̀rọ̀ gbọ́. Ìwọ kò sì lè fún ẹni tí ó wà nínú sàréè ní ọ̀rọ̀ gbọ́.1 ');
INSERT INTO "aykyuni" VALUES(35,23,'35:23','Ìwọ kò jẹ́ kiní kan bí kò ṣe olùkìlọ̀.');
INSERT INTO "aykyuni" VALUES(35,24,'35:24','Dájúdájú Àwa fi òdodo rán ọ níṣẹ́. (O sì jẹ́) oníròó-ìdùnnú àti olùkìlọ̀. Kò sí ìjọ kan (ṣíwájú rẹ) àfi kí olùkìlọ̀ ti rè kọjá láààrin wọn.1 ');
INSERT INTO "aykyuni" VALUES(35,25,'35:25','Tí wọ́n bá sì ń pè ọ́ ní òpùrọ́, àwọn tó ṣíwájú wọn náà kúkú pe (ọ̀rọ̀ Allāhu) ní irọ́ (nígbà tí) àwọn Òjíṣẹ́ wọn wá bá wọn pẹ̀lú àwọn ẹ̀rí tó yanjú, àwọn ìpín-ìpín Tírà àti Tírà tó ń tànmọ́lẹ̀ sí ọ̀rọ̀ ẹ̀sìn.');
INSERT INTO "aykyuni" VALUES(35,26,'35:26','Lẹ́yìn náà, Mo gbá àwọn tó ṣàì gbàgbọ́ mú. Báwo sì ni bí Mo ṣe (fi ìyà) kọ (aburú fún wọn) ti rí!');
INSERT INTO "aykyuni" VALUES(35,27,'35:27','Ṣé o kò rí i pé dájúdájú Allāhu l’Ó sọ omi kalẹ̀ láti sánmọ̀? A sì fi mú àwọn èso tí àwọ̀ rẹ̀ yàtọ̀ síra wọn jáde. Àti pé àwọn ojú ọ̀nà funfun àti pupa tí àwọ̀ rẹ̀ yàtọ̀ síra wọn àti aláwọ̀ dúdú kirikiri wà nínú àwọn àpáta.');
INSERT INTO "aykyuni" VALUES(35,28,'35:28','Àti pé ó wà nínú àwọn ènìyàn, àwọn n̄ǹkan ẹlẹ́mìí àti àwọn ẹran-ọ̀sìn tí àwọ̀ wọn yàtọ̀ síra wọn, gẹ́gẹ́ bí (àwọ̀ èso àti àpáta) wọ̀nyẹn (ṣe yàtọ̀ síra wọn). Àwọn tó ń páyà Allāhu nínú àwọn ẹrúsìn Rẹ̀ ni àwọn onímọ̀ (tí wọ́n mọ̀ pé), dájúdájú Allāhu ni Alágbára, Aláforíjìn.1 ');
INSERT INTO "aykyuni" VALUES(35,29,'35:29','Dájúdájú àwọn tó ń ké tírà Allāhu, tí wọ́n ń kírun, tí wọ́n sì ń ná nínú n̄ǹkan tí A ṣe ní arísìkí fún wọn ní ìkọ̀kọ̀ àti ní gban̄gba, wọ́n ń nírètí sí òwò kan tí kò níí parun.');
INSERT INTO "aykyuni" VALUES(35,30,'35:30','(Wọ́n ń ṣe bẹ́ẹ̀) nítorí kí Allāhu lè san wọ́n ní ẹ̀san rere wọn ní ẹ̀kúnrẹ́rẹ́ àti nítorí kí Ó lè ṣe àlékún fún wọn nínú oore àjùlọ Rẹ̀. Dájúdájú Òun ni Aláforíjìn, Ọlọ́pẹ́.1 ');
INSERT INTO "aykyuni" VALUES(35,31,'35:31','Ohun tí A fi ránṣẹ́ sí ọ nínú Tírà, òhun ni òdodo tí ó ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó ṣíwájú rẹ̀. Dájúdájú Allāhu ni Onímọ̀-ìkọ̀kọ̀, Olùríran nípa àwọn ẹrúsìn Rẹ̀.');
INSERT INTO "aykyuni" VALUES(35,32,'35:32','Lẹ́yìn náà, A jogún tírà (al-Ƙur’ān) fún àwọn tí A ṣà lẹ́ṣà nínú àwọn ẹrúsìn Wa. Nítorí náà, alábòsí tó ń bo ẹ̀mí ara rẹ̀ sí wà nínú wọn. Olùṣe-déédé wà nínú wọn. Olùgbawájú níbi àwọn iṣẹ́ rere tún wà nínú wọn pẹ̀lú ìyọ̀ǹda Allāhu. Ìyẹn sì ni oore àjùlọ tó tóbi.1 ');
INSERT INTO "aykyuni" VALUES(35,33,'35:33','Àwọn Ọgbà Ìdẹ̀ra gbére ni wọn yóò wọ inú rẹ̀. Àwọn kerewú wúrà àti òkúta olówó iyebíye ni A óò máa fi ṣe ọ̀ṣọ́ fún wọn nínú rẹ̀. Àti pé aṣọ àláárì ni aṣọ wọn nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(35,34,'35:34','Wọ́n yóò sọ pé: “Gbogbo ẹyìn ń jẹ́ ti Allāhu, Ẹni tí Ó mú ìbànújẹ́ kúrò fún wa. Dájúdájú Olúwa wa ni Aláforíjìn, Ọlọ́pẹ́,1 ');
INSERT INTO "aykyuni" VALUES(35,35,'35:35','Ẹni tí Ó sọ̀ wá kalẹ̀ sínú ibùgbé gbére nínú oore àjùlọ Rẹ̀. Wàhálà kan kò níí kàn wá nínú rẹ̀. Ìkáàárẹ̀ kan kò sì níí bá wa nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(35,36,'35:36','Àti pé àwọn tó ṣàì gbàgbọ́, iná Jahanamọ ń bẹ fún wọn. A ò níí pa wọ́n (sínú rẹ̀), áḿbọ̀sìbọ́sí pé wọ́n yóò kú. A ò níí ṣe ìyà inú rẹ̀ ní fífúyẹ́ fún wọn. Báyẹn ni A ṣe ń san gbogbo àwọn aláìgbàgbọ́ ní ẹ̀san.');
INSERT INTO "aykyuni" VALUES(35,37,'35:37','Wọn yóò máa lọgun ìrànlọ́wọ́ nínú rẹ̀ pé: “Olúwa wa, mú wa jáde nítorí kí á lè lọ́ ṣe iṣẹ́ rere, yàtọ̀ sí èyí tí a máa ń ṣe.” Ṣé A ò fún yín ní ẹ̀mí gígùn lò tó fún ẹni tí ọ́ bá fẹ́ lo ìṣítí láti rí i lò nínú àsìkò náà ni? Olùkìlọ̀ sì wá ba yín. Nítorí náà, ẹ tọ́ ìyà wò. Kò sì níí sí alárànṣe kan fún àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(35,38,'35:38','Dájúdájú Allāhu ni Onímọ̀-ìkọ̀kọ̀ àwọn sánmọ̀ àti ilẹ̀. Dájúdájú Òun ni Onímọ̀ nípa ohun tó wà nínú igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(35,39,'35:39','Òun ní Ẹni tó ń fi yín ṣe àrólé lórí ilẹ̀. Nítorí náà, ẹni tí ó bá ṣàì gbàgbọ́, (ìyà) àìgbàgbọ́ rẹ̀ wà lórí rẹ̀. Àìgbàgbọ́ àwọn aláìgbàgbọ́ kò sì níí fún wọn ní àlékún kan lọ́dọ̀ Olúwa wọn bí kò ṣe ìbínú. Àìgbàgbọ́ àwọn aláìgbàgbọ́ kò sì níí fún wọn ní àlékún kan bí kò ṣe òfò.');
INSERT INTO "aykyuni" VALUES(35,40,'35:40','Sọ pé: “Ẹ sọ fún mi nípa àwọn òrìṣà yín tí ẹ̀ ń pè lẹ́yìn Allāhu, ẹ fi ohun tí wọ́n dá hàn mí lórí ilẹ̀? Tàbí wọ́n ní ìpín kan (pẹ̀lú Allāhu) níbi (ìṣẹ̀dá) àwọn sánmọ̀? Tàbí A fún wọn ní tírà kan, tí wọ́n ní ẹ̀rí tó yanjú lọ́wọ́ nínú rẹ̀? Rárá o! Kò sí àdéhùn kan tí àwọn alábòsí, apá kan wọn ń ṣe fún apá kan bí kò ṣe ẹ̀tàn.”');
INSERT INTO "aykyuni" VALUES(35,41,'35:41','Dájúdájú Allāhu l’Ó ń mú àwọn sánmọ̀ àti ilẹ̀ dúró ṣinṣin tí wọn kò fi yẹ̀ lulẹ̀. Dájúdájú tí wọ́n bá sì yẹ̀ lulẹ̀, kò sí ẹnì kan kan tí ó máa mú un dúró lẹ́yìn Rẹ̀. Dájúdájú Allāhu ń jẹ́ Aláfaradà, Aláforíjìn.');
INSERT INTO "aykyuni" VALUES(35,42,'35:42','Wọ́n sì fi Allāhu búra tí ìbúra wọn sì lágbára gan-an pé: “Dájúdájú tí olùkìlọ̀ kan bá fi lè wá bá àwọn, dájúdájú àwọn yóò mọ̀nà tààrà ju èyíkéyìí nínú ìjọ (tó ti ṣíwájú).” Nígbà tí olùkìlọ̀ kan sì wá bá wọn, (èyí) kò ṣe àlékún kan fún wọn bí kò ṣe sísá (fún òdodo)');
INSERT INTO "aykyuni" VALUES(35,43,'35:43','ní ti ṣíṣe ìgbéraga lórí ilẹ̀ àti ní ti ète aburú. Ète aburú kò sì níí yí ẹnì kan po àfi oníṣẹ́ aburú. Ṣé wọ́n ń retí kiní kan bí kò ṣe ìṣe(Allāhu lórí) àwọn ẹni àkọ́kọ́?Nítorí náà, o ò níí rí ìpààrọ̀ fún ìṣe Allāhu. Àní sẹ́, o ò níí rí ìyípadà fún ìṣe Allāhu. ');
INSERT INTO "aykyuni" VALUES(35,44,'35:44','Ṣé wọn kò rìn kiri lórí ilẹ̀ kí wọ́n wo bí ìkángun àwọn tó ṣíwájú wọn ṣe rí? Wọ́n sì ní agbára ju àwọn (wọ̀nyí) lọ! Àti pé kò sí kiní kan nínú àwọn sánmọ̀ àti ilẹ̀ tí ó lè mórí bọ́ lọ́wọ́ Allāhu. Dájúdájú Allāhu ń jẹ́ Onímọ̀, Alágbára.');
INSERT INTO "aykyuni" VALUES(35,45,'35:45','Tí ó bá jẹ́ pé Allāhu yó (tètè) máa mú ènìyàn pẹ̀lú ohun tí wọ́n ṣe níṣẹ́ ni, ìbá tí ṣẹ́ ku n̄ǹkan ẹlẹ́mìí kan kan mọ́ lórí ilẹ̀. Ṣùgbọ́n Ó ń lọ́ wọn lára di gbèdéke àkókò kan. Nígbà tí àkókò náà bá dé, dájúdájú Allāhu ni Olùríran nípa àwọn ẹrúsìn Rẹ̀.');
INSERT INTO "aykyuni" VALUES(36,1,'36:1','Yā sīn (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(36,2,'36:2','(Allāhu) fi Al-Ƙur’ān tí ó kún fún ọgbọ́n búra.1 ');
INSERT INTO "aykyuni" VALUES(36,3,'36:3','Dájúdájú ìwọ wà nínú àwọn Òjíṣẹ́.');
INSERT INTO "aykyuni" VALUES(36,4,'36:4','(O) wà lórí ọ̀nà tààrà (’Islām).');
INSERT INTO "aykyuni" VALUES(36,5,'36:5','(Al-Ƙur’ān jẹ́) ìmísí tó sọ̀kalẹ̀ láti ọ̀dọ̀ Alágbára, Àṣàkẹ́-ọ̀run');
INSERT INTO "aykyuni" VALUES(36,6,'36:6','nítorí kí o lè ṣèkìlọ̀ fún àwọn ènìyàn kan, (àwọn) tí wọn kò ṣe ìkìlọ̀ fún àwọn bàbá wọn rí. Nítorí náà, afọ́núfọ́ra sì ni wọ́n (nípa ìmọ̀nà).1 ');
INSERT INTO "aykyuni" VALUES(36,7,'36:7','Dájúdájú ọ̀rọ̀ náà ti kò lé ọ̀pọ̀lọ́pọ̀ wọn lórí; wọn kò sì níí gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(36,8,'36:8','Dájúdájú Àwa ti kó ẹ̀wọ̀n sí wọn lọ́rùn (mọ́ ọwọ́ wọn). Ó sì ga dé àgbọ̀n (wọn). Wọ́n sì gà wọ́n lọ́rùn sókè.');
INSERT INTO "aykyuni" VALUES(36,9,'36:9','Àti pé Àwa fi gàgá kan síwájú wọn, gàgá kan sẹ́yìn wọn; A bò wọ́n lójú, wọn kò sì ríran.');
INSERT INTO "aykyuni" VALUES(36,10,'36:10','Bákan náà ni fún wọn, yálà o kìlọ̀ fún wọn tàbí o kò kìlọ̀ fún wọn; wọn kò níí gbàgbọ́.1 ');
INSERT INTO "aykyuni" VALUES(36,11,'36:11','Ẹni tí ìkìlọ̀ rẹ (máa wúlò fún) ni ẹni tí ó tẹ̀lé Ìrántí (al-Ƙur’ān), tí ó sì páyà Àjọkẹ́-ayé ní ìkọ̀kọ̀. Nítorí náà, fún un ní ìró ìdùnnú nípa àforíjìn àti ẹ̀san alápọ̀n-ọ́nlé.');
INSERT INTO "aykyuni" VALUES(36,12,'36:12','Dájúdájú Àwa, Àwa l’À ń sọ àwọn òkú di alàyè. A sì ń ṣe àkọsílẹ̀ ohun tí wọ́n tì síwájú àti orípa (iṣẹ́ ọwọ́) wọn. Gbogbo n̄ǹkan ni A ṣe àkọsílẹ̀ rẹ̀ sínú tírà kan tó yanjú.');
INSERT INTO "aykyuni" VALUES(36,13,'36:13','Fi àpèjúwe kan lélẹ̀ fún wọn nípa àwọn ará ìlú kan nígbà tí àwọn Òjíṣẹ́ wá bá wọn.');
INSERT INTO "aykyuni" VALUES(36,14,'36:14','(Rántí) nígbà tí A rán Òjíṣẹ́ méjì níṣẹ́ sí wọn. Wọ́n pe àwọn méjèèjì ní òpùrọ́. A sì fi ẹnì kẹta ró àwọn méjèèjì lágbára. Wọ́n sì sọ pé: “Dájúdájú àwa ni Òjíṣẹ́ tí Wọ́n rán níṣẹ́ si yín.”1 ');
INSERT INTO "aykyuni" VALUES(36,15,'36:15','Wọ́n wí pé: “Ẹ̀yin kò jẹ́ kiní kan bí kò ṣe abara bí irú tiwa. Àjọkẹ́-ayé kò sì sọ n̄ǹkan kan kalẹ̀. Ẹ̀yin kò sì jẹ́ kiní kan bí kò ṣe òpùrọ́.”');
INSERT INTO "aykyuni" VALUES(36,16,'36:16','Wọ́n sọ pé: “Olúwa wa mọ̀ pé dájúdájú àwa ni Òjíṣẹ́ tí Wọ́n rán níṣẹ́ si yín.');
INSERT INTO "aykyuni" VALUES(36,17,'36:17','Kò sì sí ojúṣe kan fún wa bí kò ṣe ìkéde (ẹ̀sìn) tó yanjú.”');
INSERT INTO "aykyuni" VALUES(36,18,'36:18','Wọ́n wí pé: “Dájúdájú àwa rí àmì aburú lára yín. Tí ẹ kò bá jáwọ́ (níbi ìpèpè yín), dájúdájú a máa sọ yín lókò pa. Ìyà ẹlẹ́ta-eléro yó sì jẹ yín láti ọ̀dọ̀ wa.”');
INSERT INTO "aykyuni" VALUES(36,19,'36:19','Wọ́n sọ pé: “Àmì aburú yín ń bẹ pẹ̀lú yín. Ṣé nítorí pé wọ́n ṣe ìṣítí fún yín (l’ẹ fi rí àmì aburú lára wa)? Kò rí bẹ́ẹ̀! Ìjọ alákọyọ ni yín ni.”');
INSERT INTO "aykyuni" VALUES(36,20,'36:20','Ọkùnrin kan sáré dé láti òpin ìlú náà, ó wí pé: “Ẹ̀yin ènìyàn mi, ẹ tẹ̀lé àwọn Òjíṣẹ́ náà.');
INSERT INTO "aykyuni" VALUES(36,21,'36:21','Ẹ tẹ̀lé ẹni tí kò bèèrè owó-ọ̀yà kan ní ọwọ́ yín. Olùmọ̀nà sì ni wọ́n.');
INSERT INTO "aykyuni" VALUES(36,22,'36:22','Kí ni ó máa mú mi tí èmi kò fi níí jọ́sìn fún Ẹni tí Ó pilẹ̀ ẹ̀dá mi? Ọ̀dọ̀ Rẹ̀ sì ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(36,23,'36:23','Ṣé kí èmi sọ àwọn kan di ọlọ́hun lẹ́yìn Allāhu ni? Tí Àjọkẹ́-ayé bá fẹ́ fi ìnira kàn mí, ìṣìpẹ̀ wọn kò lè rọ̀ mí lọ́rọ̀ kiní kan, wọn kò sì lè gbà mí là.');
INSERT INTO "aykyuni" VALUES(36,24,'36:24','(Bí èmi kò bá jọ́sìn fún Allāhu) nígbà náà dájúdájú mo ti wà nínú ìṣìnà pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(36,25,'36:25','Dájúdájú èmi gbàgbọ́ nínú Olúwa Ẹlẹ́dàá yín. Nítorí náà, ẹ gbọ́ mi.” (Àwọn aláìgbàgbọ́ sì pa onígbàgbọ́ òdodo yìí.)');
INSERT INTO "aykyuni" VALUES(36,26,'36:26','(Àwọn mọlāika) wọ́n sọ pé: “Wọ inú Ọgbà Ìdẹ̀ra.” Ó sọ pé: “Àwọn ènìyàn mi ìbá ní ìmọ̀');
INSERT INTO "aykyuni" VALUES(36,27,'36:27','nípa bí Olúwa mi ṣe foríjìn mí àti (bí) Ó ṣe fi mí sí ara àwọn alápọ̀n-ọ́nlé (wọn ìbá ronú pìwàdà).”');
INSERT INTO "aykyuni" VALUES(36,28,'36:28','A kò sọ ọmọ ogun kan kalẹ̀ lé àwọn ènìyàn rẹ̀ lórí láti sánmọ̀ lẹ́yìn rẹ̀. A kò sì sọ (mọlāika kan) kalẹ̀ (fún ìparun wọn).');
INSERT INTO "aykyuni" VALUES(36,29,'36:29','(Ìparun wọn) kò jẹ́ kiní kan bí kò ṣe igbe ẹyọ kan; nígbà náà ni wọ́n di òkú kalẹ̀.');
INSERT INTO "aykyuni" VALUES(36,30,'36:30','Àbámọ̀ mà ni fún àwọn ẹrúsìn náà (nígbà tí wọ́n bá fojú ara wọn rí Iná); òjíṣẹ́ kan kò níí wá bá wọn àyàfi kí wọ́n máa fi ṣe yẹ̀yẹ́.');
INSERT INTO "aykyuni" VALUES(36,31,'36:31','Ṣé wọn kò wòye pé mélòó mélòó nínú àwọn ìran tí A ti parẹ́ ṣíwájú wọn? (Ṣé wọn kò wòye pé) dájúdájú wọn kò padà sí ọ̀dọ̀ wọn mọ́ (nílé ayé) ni?');
INSERT INTO "aykyuni" VALUES(36,32,'36:32','Dájúdájú gbogbo wọn pátápátá sì ni (àwọn mọlāika) máa kó wá sí ọ̀dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(36,33,'36:33','Àmì ni òkú ilẹ̀jẹ́ fún wọn. A sọ ọ́ di ààyè. A sì mú èso jáde láti inú rẹ̀. Wọ́n sì ń jẹ nínú rẹ̀. ');
INSERT INTO "aykyuni" VALUES(36,34,'36:34','A tún ṣe àwọn ọgbà dàbínù àti àjàrà sórí ilẹ̀. A sì mú àwọn odò ìṣẹ́lẹ̀rú ṣàn láti inú rẹ̀');
INSERT INTO "aykyuni" VALUES(36,35,'36:35','nítorí kí wọ́n lè jẹ nínú èso rẹ̀ àti èyí tí wọ́n fi ọwọ́ ara wọn ṣe!Ṣé wọn kò níí dúpẹ́ ni? ');
INSERT INTO "aykyuni" VALUES(36,36,'36:36','Mímọ́ ni fún Ẹni tí Ó ṣẹ̀dá gbogbo n̄ǹkan ní oríṣiríṣi nínú ohun tí ilẹ̀ ń hù jáde àti nínú ẹ̀mí ara wọn àti nínú ohun tí wọn kò mọ̀.1 ');
INSERT INTO "aykyuni" VALUES(36,37,'36:37','Àmì kan ni òru jẹ́ fún wọn, tí À ń yọ ọ̀sán jáde láti inú rẹ̀. Nígbà náà (tí A bá yọ ọ́ tán) wọn yóò tún wà nínú òkùnkùn (alẹ́ mìíràn).1 ');
INSERT INTO "aykyuni" VALUES(36,38,'36:38','Àti pé òòrùn yóò máa rìn lọ sí àyè rẹ̀. Ìyẹn ni ètò (ti) Alágbára, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(36,39,'36:39','Òṣùpá náà, A ti ṣe òdíwọ̀n àwọn ibùsọ̀ fún un (tí ó ti ma máa tóbi sí i) títí ó máa fi padà dà bíi àran ọ̀pẹ tó ti pẹ́ (gbígbẹ).');
INSERT INTO "aykyuni" VALUES(36,40,'36:40','Kò yẹ fún òòrùn láti lo àsìkò òṣùpá. Kò sì yẹ fún alẹ́ láti lo àsìkò ọ̀sán. Ìkọ̀ọ̀kan wà ní òpópónà róbótó tó ń tọ̀.');
INSERT INTO "aykyuni" VALUES(36,41,'36:41','Àmì kan tún ni fún wọn pé, dájúdájú Àwa gbé àwọn àrọ́mọdọ́mọ wọn gun ọkọ̀ ojú-omi tó kún kẹ́kẹ́.');
INSERT INTO "aykyuni" VALUES(36,42,'36:42','A tún ṣẹ̀dá (òmíràn) fún wọn nínú irú rẹ̀ tí wọn yóò máa gún.1 ');
INSERT INTO "aykyuni" VALUES(36,43,'36:43','Tí A bá fẹ́ Àwa ìbá tẹ̀ wọ́n rì sínú omi. Kò níí sí olùrànlọ́wọ́ kan fún wọn. Wọn kò sì níí gbà wọ́n là.');
INSERT INTO "aykyuni" VALUES(36,44,'36:44','Àfi (kí Á fi) ìkẹ́ kan láti ọ̀dọ̀ Wa (yọ wọ́n jáde, kí A sì tún fún wọn ní) ìgbádùn ayé títí di ìgbà díẹ̀.');
INSERT INTO "aykyuni" VALUES(36,45,'36:45','(Wọ́n máa gbúnrí) nígbà tí A bá sọ fún wọn pé: “Ẹ ṣọ́ra fún ohun tó wà níwájú yínàti ohun tó wà lẹ́yìn yín2 nítorí kí A lè kẹ yín.” ');
INSERT INTO "aykyuni" VALUES(36,46,'36:46','Àti pé āyah kan nínú àwọn āyah Olúwa wọn kò níí wá bá wọn àfi kí wọ́n máa gbúnrí kúrò níbẹ̀.');
INSERT INTO "aykyuni" VALUES(36,47,'36:47','Nígbà tí A bá sì sọ fún wọn pé: “Ẹ ná nínú ohun tí Allāhu ṣe ní arísìkí fún yín.”, àwọn tó ṣàì gbàgbọ́ yóò wí fún àwọn tó gbàgbọ́ pé: “Ṣé kí á bọ́ ẹni tí (ó jẹ́ pé) Allāhu ìbá fẹ́ ìbá bọ́ ọ (àmọ́ kò bọ́ ọ. Àwa náà kò sì níí bá A bọ́ ọ)?” Kí ni ẹ̀yin bí kò ṣe pé (ẹ wà) nínú ìṣìnà pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(36,48,'36:48','Wọ́n ń wí pé: “Ìgbà wo ni àdéhùn yìí yóò ṣẹ tí ẹ bá jẹ́ olódodo?”');
INSERT INTO "aykyuni" VALUES(36,49,'36:49','Wọn kò retí kiní kan bí kò ṣe igbe ẹyọ kan ṣoṣo tí ó máa gbá wọn mú nígbà tí wọ́n bá ń ṣe àríyànjiyàn lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(36,50,'36:50','Nígbà náà, wọn kò níí lè sọ àsọọ́lẹ̀ kan. Wọn kò sì níí lè padà sí ọ̀dọ̀ ará ilé wọn.');
INSERT INTO "aykyuni" VALUES(36,51,'36:51','Wọ́n á sì fọn fèrè oníwo fún àjíǹde, nígbà náà ni wọn yóò máa sáré jáde láti inú sàréè wá sí ọ̀dọ̀ Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(36,52,'36:52','Wọ́n á wí pé: “Ègbé wa ò! Ta ni ó ta wá jí láti ojú oorun wa?” Èyí ni n̄ǹkan tí Àjọkẹ́-ayé ṣe ní àdéhùn. Àwọn Òjíṣẹ́ sì ti sọ òdodo (nípa rẹ̀).');
INSERT INTO "aykyuni" VALUES(36,53,'36:53','Kò jẹ́ kiní kan tayọ igbe ẹyọ kan ṣoṣo. Nígbà náà ni (àwọn mọlāika) yóò kó gbogbo wọn wá sí ọ̀dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(36,54,'36:54','Nítorí náà, ní òní wọn kò níí ṣe àbòsí kiní kan fún ẹ̀mí kan. A kò sì níí san yín ní ẹ̀san àfi ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(36,55,'36:55','Dájúdájú ní òní, àwọn èrò inú Ọgbà Ìdẹ̀ra yóò kún fún ìgbádùn.');
INSERT INTO "aykyuni" VALUES(36,56,'36:56','Àwọn àti àwọn ìyàwó wọn yóò wà lábẹ́ àwọn ibòji, wọn yó sì rọ̀gbọ̀kú sórí àwọn ibùsùn.');
INSERT INTO "aykyuni" VALUES(36,57,'36:57','Èso wà nínú rẹ̀ fún wọn. Ohun tí wọ́n yóò máa bèèrè fún tún wà fún wọn pẹ̀lú.');
INSERT INTO "aykyuni" VALUES(36,58,'36:58','Àlàáfíà ni ọ̀rọ̀ tí ó má máa wá láti ọ̀dọ̀ Olúwa, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(36,59,'36:59','Ẹ̀yin ẹlẹ́ṣẹ̀, ẹ bọ́ sí ọ̀tọ̀ ní òní.');
INSERT INTO "aykyuni" VALUES(36,60,'36:60','Ẹ̀yin ọmọ (Ànábì) Ādam, ṣe Èmi kò ti pa yín ní àṣẹ pé, ẹ kò gbọ́dọ̀ jọ́sìn fún Èṣù? Dájúdájú òun ni ọ̀tá pọ́nńbélé fún yín.');
INSERT INTO "aykyuni" VALUES(36,61,'36:61','Àti pé kí ẹ jọ́sìn fún Mi. Èyí ni ọ̀nà tààrà.');
INSERT INTO "aykyuni" VALUES(36,62,'36:62','Àti pé (Èṣù) kúkú ti ṣi ọ̀pọ̀lọ́pọ̀ ẹ̀dá lọ́nà nínú yín. Ṣé ẹ kò níí ṣe làákàyè ni?');
INSERT INTO "aykyuni" VALUES(36,63,'36:63','Èyí ni iná Jahanamọ tí À ń ṣe ní àdéhùn fún yín.');
INSERT INTO "aykyuni" VALUES(36,64,'36:64','Ẹ wọ inú rẹ̀ ní òní nítorí pé, ẹ̀yin jẹ́ aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(36,65,'36:65','Ní òní, A máa di ẹnu wọn pa. Àwọn ọwọ́ wọn yó sì máa bá Wa sọ̀rọ̀. Àwọn ẹsẹ̀ wọn yó sì máa jẹ́rìí sí ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(36,66,'36:66','Tí ó bá jẹ́ pé A bá fẹ́, Àwa ìbá fọ́ wọn lójú, wọn ìbá sì yára wá si ojú ọ̀nà, báwo ni wọ́n ṣe máa ríran ná?');
INSERT INTO "aykyuni" VALUES(36,67,'36:67','Tí ó bá jẹ́ pé A bá fẹ́, Àwa ìbá yí wọn padà sí ẹ̀dá mìíràn nínú ibùgbé wọn. Wọn kò sì níí lágbára láti lọ síwájú. Wọn kò sì níí padà sẹ́yìn.');
INSERT INTO "aykyuni" VALUES(36,68,'36:68','Ẹnikẹ́ni tí A bá fún ní ẹ̀mí gígùn lò, A óò sọ ẹ̀dá rẹ̀ di ọ̀lẹ, ṣé wọn kò níí ṣe làákàyè ni!');
INSERT INTO "aykyuni" VALUES(36,69,'36:69','A kò kọ́ Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a - ní ewì. Kò yẹ ẹ́ (kò sì rọ̀ ọ́ lọ́rùn láti kéwì). Kí ni ohun (tí A fi ránṣẹ́ sí i) bí kò ṣe ìrántí àti al-Ƙur’ān pọ́nńbélé');
INSERT INTO "aykyuni" VALUES(36,70,'36:70','nítorí kí ó lè ṣèkìlọ̀ fún ẹni tí ó jẹ́ alààyè àti nítorí kí ọ̀rọ̀ náà lè kò lórí àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(36,71,'36:71','Ṣé wọn kò wòye pé dájúdájú Àwa l’A ṣẹ̀dá nínú ohun tí A fi ọwọ́ Wa ṣe (tí ó jẹ́) àwọn ẹran-ọ̀sìn fún wọn? Wọ́n sì ní ìkápá lórí wọn.');
INSERT INTO "aykyuni" VALUES(36,72,'36:72','A tẹ̀ wọ́n lórí ba fún wọn; wọ́n ń gùn nínú wọn, wọ́n sì ń jẹ nínú wọn.');
INSERT INTO "aykyuni" VALUES(36,73,'36:73','Àwọn àǹfààní àti ohun mímu tún wà fún wọn nínú rẹ̀. Ṣé wọn kò níí dúpẹ́ ni?');
INSERT INTO "aykyuni" VALUES(36,74,'36:74','Wọ́n sọ àwọn kan di ọlọ́hun lẹ́yìn Allāhu nítorí kí wọ́n lè ṣàrànṣe fún wọn!');
INSERT INTO "aykyuni" VALUES(36,75,'36:75','Wọn kò sì lè ṣe àrànṣe fún wọn; ṣebí àwọn òrìṣà ni ọmọ ogun tí (àwọn mọlāika) máa kó wá sínú Iná fún àwọn abọ̀rìṣà (láti fi jẹ wọ́n níyà).1 ');
INSERT INTO "aykyuni" VALUES(36,76,'36:76','Nítorí náà, má ṣe jẹ́ kí ọ̀rọ̀ wọn bà ọ́ nínú jẹ́. Dájúdájú Àwa mọ ohun tí wọ́n ń fi pamọ́ àti ohun tí wọ́n ń ṣàfi hàn rẹ̀.');
INSERT INTO "aykyuni" VALUES(36,77,'36:77','Ṣé ènìyàn kò wòye pé dájúdájú Àwa l’A ṣẹ̀dá rẹ̀ láti inú àtọ̀? (Ṣebí lẹ́yìn) ìgbà náà l’ó di alátakò pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(36,78,'36:78','Àti pé ó fi àkàwé lélẹ̀ nípa Wa.Ó sì gbàgbé ìṣẹ̀dá rẹ̀. Ó wí pé: “Ta ni Ó máa sọ egungun di alààyè nígbà tí ó ti kẹfun?” ');
INSERT INTO "aykyuni" VALUES(36,79,'36:79','Sọ pé: “Ẹni tí Ó ṣẹ̀dá rẹ̀ nígbà àkọ́kọ́ l’Ó máa sọ ọ́ di alààyè. Òun sì ni Onímọ̀ nípa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(36,80,'36:80','(Òun ni) Ẹni tí Ó mú iná jáde fún yín láti ara igi tútù. Ẹ sì ń fi dáná.');
INSERT INTO "aykyuni" VALUES(36,81,'36:81','Ǹjẹ́ Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ kò ní agbára láti dá irú wọn (mìíràn) bí? Bẹ́ẹ̀ ni (Ó ní agbára). Òun sì ni Ẹlẹ́dàá, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(36,82,'36:82','Àṣẹ Rẹ̀ nígbà tí Ó bá gbèrò kiní kan ni pé, Ó máa sọ fún un pé “Jẹ́ bẹ́ẹ̀.” Ó sì máa jẹ́ bẹ́ẹ̀.');
INSERT INTO "aykyuni" VALUES(36,83,'36:83','Nítorí náà, mímọ́ ni fún Ẹni tí ìjọba gbogbo n̄ǹkan wà ní ọwọ́ Rẹ̀. Ọ̀dọ̀ Rẹ̀ sì ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(37,1,'37:1','Allāhu fi àwọn mọlāika tí ó ń tò ní ọ̀wọ̀ọ̀wọ́ búra.');
INSERT INTO "aykyuni" VALUES(37,2,'37:2','Allāhu tún fi àwọn mọlāika tó ń wọ́ ẹ̀ṣújò kiri tààrà bura.');
INSERT INTO "aykyuni" VALUES(37,3,'37:3','Allāhu tún fi àwọn mọlāika tó ń ka ìrántí bura.');
INSERT INTO "aykyuni" VALUES(37,4,'37:4','Dájúdájú ọ̀kan ṣoṣo ni Ọlọ́hun yín tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo.');
INSERT INTO "aykyuni" VALUES(37,5,'37:5','Olúwa àwọn sánmọ̀ àti ilẹ̀ àti ohunkóhun tí ń bẹ láààrin àwọn méjèèjì. Òun sì ni Olúwa àwọn ibùyọ òòrùn.');
INSERT INTO "aykyuni" VALUES(37,6,'37:6','Dájúdájú Àwa fi àwọn ìràwọ̀ ṣe sánmọ̀ ilé ayé ní ọ̀ṣọ́');
INSERT INTO "aykyuni" VALUES(37,7,'37:7','àti ìṣọ́ kúrò níbi (aburú) gbogbo èṣù olóríkunkun.');
INSERT INTO "aykyuni" VALUES(37,8,'37:8','(Àwọn èṣù) kò sì níí lè tẹ́tí sí ìjọ (mọlāika tó wà ní) àyè gíga jùlọ (nínú sánmọ̀. Bí wọ́n bá sì gbìyànjú láti yọ́ ọ̀rọ̀ gbọ́ láti sánmọ̀, àwọn mọlāika) yó sì máa kù wọ́n lókò (ẹ̀ta ìràwọ̀) ní gbogbo agbègbè (sánmọ̀).');
INSERT INTO "aykyuni" VALUES(37,9,'37:9','Wọ́n máa lé wọn dànù. Ìyà àìlópin sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(37,10,'37:10','(Wọn kò níí gbọ́ ọ̀rọ̀ ní ẹ̀kúnrẹ́rẹ́) àyàfi ẹni tí ó bá rí ọ̀rọ̀ àjígbọ́ gbé, nígbà náà (ni mọlāika) yó sì fi ẹ̀ta ìràwọ̀ iná tó máa jó o tẹ̀lé e.1 ');
INSERT INTO "aykyuni" VALUES(37,11,'37:11','Bi wọ́n léèrè wò pé, ṣé àwọn (ènìyàn) ni ìṣẹ̀dá wọn lágbára jùlọ ni tàbí àwọn (n̄ǹkan mìíràn)tí A dá? Dájúdájú Àwa dá àwọn (ènìyàn) láti ara ẹrẹ̀ tó lẹ̀ mọ́ra wọn. ');
INSERT INTO "aykyuni" VALUES(37,12,'37:12','Rárá (kò jọra wọn); ìwọ ṣèèmọ̀ (nípa al-Ƙur’ān), àwọn sì ń ṣe yẹ̀yẹ́.');
INSERT INTO "aykyuni" VALUES(37,13,'37:13','Àti pé nígbà tí wọ́n bá ṣe ìrántí fún wọn, wọn kò níí lo ìrántí.');
INSERT INTO "aykyuni" VALUES(37,14,'37:14','Nígbà tí wọ́n bá tún rí àmì kan, wọn yóò máa ṣe yẹ̀yẹ́.');
INSERT INTO "aykyuni" VALUES(37,15,'37:15','Wọ́n sì wí pé: “Èyí kò jẹ́ kiní kan bí kò ṣe idán pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(37,16,'37:16','Ṣé nígbà tí a bá kú tán, tí a ti di erùpẹ̀ àti egungun, ṣé dájúdájú Wọn yóò tún gbé wa dìde ni?');
INSERT INTO "aykyuni" VALUES(37,17,'37:17','Ṣé àti àwọn bàbá wa, àwọn ẹni àkọ́kọ́?”');
INSERT INTO "aykyuni" VALUES(37,18,'37:18','Sọ pé: “Bẹ́ẹ̀ ni. Ẹ̀yin yó sì di ẹni yẹpẹrẹ.”');
INSERT INTO "aykyuni" VALUES(37,19,'37:19','Nítorí náà, igbe ẹyọ kan mà ni. Nígbà náà ni wọn yóò máa wò sùn.');
INSERT INTO "aykyuni" VALUES(37,20,'37:20','Wọ́n á wí pé: “Ègbé wa ò! Èyí ni Ọjọ́ ẹ̀san.”');
INSERT INTO "aykyuni" VALUES(37,21,'37:21','Èyí ni ọjọ́ Ìdájọ́ èyí tí ẹ̀ ń pè ní irọ́.');
INSERT INTO "aykyuni" VALUES(37,22,'37:22','Ẹ kó àwọn tó ṣàbòsí jọ, àwọn ìyàwó wọn àti ohun tí wọ́n ń jọ́sìn fún');
INSERT INTO "aykyuni" VALUES(37,23,'37:23','lẹ́yìn Allāhu. Kí ẹ sì júwe wọn sí ojú ọ̀nà iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(37,24,'37:24','Ẹ dá wọn dúró ná, dájúdájú wọ́n máa bi wọ́n léèrè ìbéèrè.');
INSERT INTO "aykyuni" VALUES(37,25,'37:25','Kí ni kò jẹ́ kí ẹ ran ara yín lọ́wọ́?');
INSERT INTO "aykyuni" VALUES(37,26,'37:26','Rárá, wọ́n ti juwọ́ jusẹ̀ sílẹ̀ ní òní ni.');
INSERT INTO "aykyuni" VALUES(37,27,'37:27','Apá kan wọn yóò dojú kọ apá kan, tí wọn yóò máa bi ara wọn léèrè ìbéèrè.1 ');
INSERT INTO "aykyuni" VALUES(37,28,'37:28','(Àwọn ènìyàn) wí pé: “Dájúdájú ẹ̀yin l’ẹ̀ ń gba ọ̀tún wọlé sí wa lára (láti ṣì wá lọ́nà).”1 ');
INSERT INTO "aykyuni" VALUES(37,29,'37:29','(Àwọn àlùjànnú) wí pé: “Rárá o (àwa kọ́), ẹ̀yin ni ẹ kò jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(37,30,'37:30','Àti pé àwa kò ní agbára kan lórí yín, ṣùgbọ́n ẹ̀yin jẹ́ ìjọ olùtayọ-ẹnu ààlà.');
INSERT INTO "aykyuni" VALUES(37,31,'37:31','Ọ̀rọ̀ Olúwa wa sì kò lé wa lórí pé dájúdájú àwa yóò tọ́ Iná wò.');
INSERT INTO "aykyuni" VALUES(37,32,'37:32','Nítorí náà, a ṣì yín lọ́nà. Dájúdájú àwa náà sì jẹ́ olùṣìnà.”');
INSERT INTO "aykyuni" VALUES(37,33,'37:33','Dájúdájú ní ọjọ́ yẹn, akẹgbẹ́ ni wọ́n nínú Ìyà. (Wọ́n dìjọ máa wà nínú Iná.)');
INSERT INTO "aykyuni" VALUES(37,34,'37:34','Dájúdájú báyẹn ni Àwa yóò ti ṣe pẹ̀lú àwọn ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(37,35,'37:35','Dájúdájú nígbà tí wọ́n bá sọ fún wọn pé “Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Allāhu.”, wọn yóò máa ṣègbéraga.');
INSERT INTO "aykyuni" VALUES(37,36,'37:36','Wọ́n sì ń wí pé: “Ṣé a máa fi àwọn òrìṣà wa sílẹ̀ nítorí eléwì wèrè kan?”');
INSERT INTO "aykyuni" VALUES(37,37,'37:37','Kò rí bẹ́ẹ̀. Ó mú òdodo wá ni. Ó sì ń jẹ́rìí sí òdodo àwọn Òjíṣẹ́ náà.');
INSERT INTO "aykyuni" VALUES(37,38,'37:38','Dájúdájú ẹ máa tọ́ ìyà ẹlẹ́ta-eléro wò.');
INSERT INTO "aykyuni" VALUES(37,39,'37:39','A kò níí san yín ní ẹ̀san kan àfi ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(37,40,'37:40','Àfi àwọn ẹrúsìn Allāhu, àwọn tí A ṣà lẹ́ṣà (fún ìgbàgbọ́ òdodo).');
INSERT INTO "aykyuni" VALUES(37,41,'37:41','Ti àwọn wọ̀nyẹn sì ni arísìkí tí wọ́n ti mọ̀;');
INSERT INTO "aykyuni" VALUES(37,42,'37:42','àwọn èso. Àwọn sì ni alápọ̀n-ọ́nlé');
INSERT INTO "aykyuni" VALUES(37,43,'37:43','nínú àwọn Ọgbà Ìdẹ̀ra.');
INSERT INTO "aykyuni" VALUES(37,44,'37:44','Lórí àwọn ìtẹ́ ni wọn yóò ti kọjú síra wọn.');
INSERT INTO "aykyuni" VALUES(37,45,'37:45','Wọn yóò máa gbé ife ọtí mímọ́ láti inú odò kan tó ń ṣàn káà kiri ọ̀dọ̀ wọn.');
INSERT INTO "aykyuni" VALUES(37,46,'37:46','Funfun ni (ọtí náà). Ó ń dùn lẹ́nu àwọn tó máa mu ún.');
INSERT INTO "aykyuni" VALUES(37,47,'37:47','Kò sí ìpalára kan fún wọn nínú rẹ̀, wọn kò sì níí hunrírà lórí rẹ̀.');
INSERT INTO "aykyuni" VALUES(37,48,'37:48','Àwọn obìnrin tí kì í wo ẹlòmíìràn, àwọn ẹlẹ́yinjú ẹgẹ́ yó sì wà lọ́dọ̀ wọn.');
INSERT INTO "aykyuni" VALUES(37,49,'37:49','Wọ́n dà bí ẹyin tí wọn kò ì bó èèpo rẹ̀ kúrò lára rẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(37,50,'37:50','Apá kan wọn yóò dojú kọ apá kan, tí wọn yóò máa bira wọn léèrè ìbéèrè.');
INSERT INTO "aykyuni" VALUES(37,51,'37:51','Òǹsọ̀rọ̀ kan nínú wọn yóò sọ pé: "Dájúdájú èmi ní ọ̀rẹ́ kan');
INSERT INTO "aykyuni" VALUES(37,52,'37:52','tí ó ń wí pé, “Ṣé dájúdájú ìwọ wà nínú àwọn onígbàgbọ́ nínú Ọjọ́ Àjíǹde?');
INSERT INTO "aykyuni" VALUES(37,53,'37:53','Ṣé nígbà tí a bá kú tán, tí a ti di erùpẹ̀ àti egungun, ṣé dájúdájú Wọn yóò san wá ní ẹ̀san ni?”');
INSERT INTO "aykyuni" VALUES(37,54,'37:54','Ó sọ pé: "Ṣé ẹ̀yin yóò yọjú wo (ẹni náà tí kò gbàgbọ́ nínú Ọjọ́ àjíǹde)?”');
INSERT INTO "aykyuni" VALUES(37,55,'37:55','Ó yọjú wò ó. Ó sì rí i láààrin gbùngbùn nínú iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(37,56,'37:56','Ó (sì) sọ pé: “Mó fi Allāhu búra, ìwọ fẹ́ẹ̀ kó ìparun bá mi tán.');
INSERT INTO "aykyuni" VALUES(37,57,'37:57','Àti pé tí kì í bá ṣe ìdẹ̀ra Olúwa mi ni, èmi náà ìbá wà nínú ẹni tí wọ́n máa mú wá sínú Iná.');
INSERT INTO "aykyuni" VALUES(37,58,'37:58','Àwa kò sá níí kú mọ́ báyìí?');
INSERT INTO "aykyuni" VALUES(37,59,'37:59','Àfi ikú wa àkọ́kọ́. Wọn kò sì níí jẹ àwa níyà.');
INSERT INTO "aykyuni" VALUES(37,60,'37:60','Dájúdájú èyí, òhun ni èrèǹjẹ ńlá.”');
INSERT INTO "aykyuni" VALUES(37,61,'37:61','Nítorí irú èyí, kí àwọn olùṣẹ̀sìn máa ṣẹ̀sìn lọ.');
INSERT INTO "aykyuni" VALUES(37,62,'37:62','Ǹjẹ́ ìyẹn l’ó lóore jùlọ ní ibùdésí ni tàbí igi zaƙūm (igi ẹlẹ́gùn-ún)?');
INSERT INTO "aykyuni" VALUES(37,63,'37:63','Dájúdájú Àwa ṣe é ní àdánwò fún àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(37,64,'37:64','Dájúdájú òhun ni igi kan tí ó máa hù jáde láti ìdí iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(37,65,'37:65','Èso rẹ̀ dà bí orí àwọn èṣù.');
INSERT INTO "aykyuni" VALUES(37,66,'37:66','Dájúdájú wọn yóò máa jẹ nínú rẹ̀. Wọ́n sì máa jẹ ẹ́ ní àjẹyo bámúbámú.');
INSERT INTO "aykyuni" VALUES(37,67,'37:67','Lẹ́yìn náà, dájúdájú wọn yóò mu àwọnúwẹ̀jẹ̀ olómi gbígbóná tó gbóná parí lé e lórí.');
INSERT INTO "aykyuni" VALUES(37,68,'37:68','Lẹ́yìn náà, dájúdájú ibùpadàsí wọn ni iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(37,69,'37:69','Dájúdájú wọ́n bá àwọn bàbá wọn ní olùṣìnà.');
INSERT INTO "aykyuni" VALUES(37,70,'37:70','Àwọn náà sì ń yára tẹ̀lé orípa wọn.');
INSERT INTO "aykyuni" VALUES(37,71,'37:71','Dájúdájú ọ̀pọ̀lọ́pọ̀ àwọn ẹni àkọ́kọ́ ti ṣìnà ṣíwájú wọn.');
INSERT INTO "aykyuni" VALUES(37,72,'37:72','Dájúdájú A ti rán àwọn olùkìlọ̀ sí wọn.');
INSERT INTO "aykyuni" VALUES(37,73,'37:73','Nítorí náà, wo bí àtubọ̀tán àwọn ẹni-akìlọ̀-fún ti rí.');
INSERT INTO "aykyuni" VALUES(37,74,'37:74','Àfi àwọn ẹrúsìn Allāhu, àwọn tí A ṣà lẹ́ṣà (fún ìgbàgbọ́ òdodo).');
INSERT INTO "aykyuni" VALUES(37,75,'37:75','Dájúdájú (Ànábì) Nūh pè Wá. A sì dára ní olùjẹ́pè.');
INSERT INTO "aykyuni" VALUES(37,76,'37:76','A gba òun àti àwọn ará ilé rẹ̀ là kúrò nínú ìbànújẹ́ ńlá náà.');
INSERT INTO "aykyuni" VALUES(37,77,'37:77','A dá àwọn àrọ́mọdọ́mọ rẹ̀ sí lẹ́yìn rẹ̀.');
INSERT INTO "aykyuni" VALUES(37,78,'37:78','A sì fún un ní orúkọ rere láààrin àwọn ẹni ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(37,79,'37:79','Àlàáfíà kí ó máa bá (Ànábì) Nūh láààrin gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(37,80,'37:80','Dájúdájú báyẹn ni Àwa ṣe ń san àwọn olùṣe-rere ní ẹ̀san (rere).');
INSERT INTO "aykyuni" VALUES(37,81,'37:81','Dájúdájú ó wà nínú àwọn ẹrúsìn Wa, àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(37,82,'37:82','Lẹ́yìn náà, A tẹ àwọn yòókù rì sínú agbami.');
INSERT INTO "aykyuni" VALUES(37,83,'37:83','Dájúdájú (Ànábì) ’Ibrọ̄hīm wà nínú ìjọ (tí ó tẹ̀lé ìlànà) rẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(37,84,'37:84','(Ẹ rántí) nígbà tí ó dojú kọ Olúwa rẹ̀ pẹ̀lú ọkàn mímọ́.');
INSERT INTO "aykyuni" VALUES(37,85,'37:85','(Rántí) nígbà tí ó sọ fún bàbá rẹ̀ àti àwọn ènìyàn rẹ̀ pé: "Kí ni ẹ̀ ń jọ́sìn fún?');
INSERT INTO "aykyuni" VALUES(37,86,'37:86','Ṣé ẹ̀ ń gbèrò láti sọ irọ́ di ọlọ́hun lẹ́yìn Allāhu?');
INSERT INTO "aykyuni" VALUES(37,87,'37:87','Kí sì ni èrò ọkàn yín sí Olúwa gbogbo ẹ̀dá?”1 ');
INSERT INTO "aykyuni" VALUES(37,88,'37:88','Ó ṣíjú wo àwọn ìràwọ̀ ní wíwò kan.');
INSERT INTO "aykyuni" VALUES(37,89,'37:89','Ó sì sọ pé: “Dájúdájú ó rẹ̀ mí.”');
INSERT INTO "aykyuni" VALUES(37,90,'37:90','Nítorí náà, wọ́n pa á tì, wọ́n sì lọ.');
INSERT INTO "aykyuni" VALUES(37,91,'37:91','Nígbà náà, ó lọ sí ọ̀dọ̀ àwọn òrìṣà wọn. Ó sì sọ pé: “Ṣé ẹ kò níí jẹun ni?');
INSERT INTO "aykyuni" VALUES(37,92,'37:92','Kí ni ó ṣe yín tí ẹ kò sọ̀rọ̀?”');
INSERT INTO "aykyuni" VALUES(37,93,'37:93','Nígbà náà, ó dojú lílù kọ wọ́n pẹ̀lú ọwọ́ ọ̀tún (ó sì rún wọn wómúwómú).');
INSERT INTO "aykyuni" VALUES(37,94,'37:94','(Àwọn abọ̀rìṣà) sáré wá dojú kọ ọ́.');
INSERT INTO "aykyuni" VALUES(37,95,'37:95','Ó sọ pé: “Ṣé ẹ óò máa jọ́sìn fún n̄ǹkan tí ẹ gbẹ́ kalẹ̀ lére ni?');
INSERT INTO "aykyuni" VALUES(37,96,'37:96','Allāhu l’Ó sì dá ẹ̀yin àti n̄ǹkan tí ẹ̀ ń ṣe níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(37,97,'37:97','Wọ́n wí pé: “Ẹ mọ ilé kan fún un, kí ẹ sì jù ú sínú iná.”');
INSERT INTO "aykyuni" VALUES(37,98,'37:98','Wọ́n gbèrò ète sí i. A sì sọ wọ́n di ẹni yẹpẹrẹ.');
INSERT INTO "aykyuni" VALUES(37,99,'37:99','Ó sọ pé: “Dájúdájú mo máa lọ sí (ìlú tí) Olúwa mi (pa mí láṣẹ láti lọ). Ó sì máa tọ́ mi sọ́nà (láti dé ibẹ̀).1 ');
INSERT INTO "aykyuni" VALUES(37,100,'37:100','Olúwa mi, ta mí lọ́rẹ ọmọ nínú àwọn ẹni rere.”');
INSERT INTO "aykyuni" VALUES(37,101,'37:101','A sì fún un ní ìró ìdùnnú (nípa bíbí) ọmọkùnrin kan tí ó máa ní ìfaradà.');
INSERT INTO "aykyuni" VALUES(37,102,'37:102','Nígbà tí (ọmọ náà) dàgbà, tí ó tó bá (bàbá rẹ̀) ṣe iṣẹ́, (bàbá rẹ̀) sọ pé: "Ọmọ mi, dájúdájú èmi lá àlá pé dájúdájú mò ń dúńbú rẹ. Nítorí náà, wòye sí i, kí ni o rí sí i." Ó sọ pé: “Bàbá mi, ṣe ohun tí Wọ́n pa láṣẹ fún ọ. O máa bá mi nínú àwọn onísùúrù, tí Allāhu bá fẹ́.”');
INSERT INTO "aykyuni" VALUES(37,103,'37:103','Nígbà tí àwọn méjèèjì juwọ́ jusẹ̀ sílẹ̀, tí (Ànábì ’Ibrọ̄hīm) sì dojú(ọmọ) rẹ̀ bolẹ̀, ');
INSERT INTO "aykyuni" VALUES(37,104,'37:104','A pè é (báyìí) pé, ’Ibrọ̄hīm,');
INSERT INTO "aykyuni" VALUES(37,105,'37:105','o kúkú ti mú àlá náà ṣẹ. Dájúdájú báyẹn ni Àwa ṣe ń san àwọn olùṣe-rere ní ẹ̀san (rere).');
INSERT INTO "aykyuni" VALUES(37,106,'37:106','Dájúdájú èyí, òhun ni àdánwò pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(37,107,'37:107','A sì fi àgbò ńlá ṣèràpadà rẹ̀.');
INSERT INTO "aykyuni" VALUES(37,108,'37:108','A tún fún un ní orúkọ rere láààrin àwọn ẹni ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(37,109,'37:109','Àlàáfíà kí ó máa bá (Ànábì) ’Ibrọ̄hīm.');
INSERT INTO "aykyuni" VALUES(37,110,'37:110','Báyẹn ni A ṣe ń san àwọn olùṣe-rere ní ẹ̀san (rere).');
INSERT INTO "aykyuni" VALUES(37,111,'37:111','Dájúdájú ó wà nínú àwọn ẹrúsìn Wa, àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(37,112,'37:112','A tún fún un ní ìró ìdùnnú (nípa bíbí) ’Ishāƙ, (ó máa jẹ́) Ànábì. (Ó sì máa wà) nínú àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(37,113,'37:113','Àti pé A fún òun àti ’Ishāƙ ní ìbùkún. Ẹni rere àti alábòsí pọ́nńbélé (tó ń ṣàbòsí sí) ẹ̀mí ara rẹ̀ wà nínú àrọ́mọdọ́mọ àwọn méjèèjì.1 ');
INSERT INTO "aykyuni" VALUES(37,114,'37:114','Dájúdájú A ṣoore fún (Ànábì) Mūsā àti Hārūn.');
INSERT INTO "aykyuni" VALUES(37,115,'37:115','A gba àwọn méjèèjì àti ìjọ àwọn méjèèjì là kúrò nínú ìbànújẹ́ ńlá náà.');
INSERT INTO "aykyuni" VALUES(37,116,'37:116','A tún ṣàrànṣe fún wọn. Àwọn ni wọ́n jẹ́ olùborí (ọ̀tá wọn).');
INSERT INTO "aykyuni" VALUES(37,117,'37:117','A sì fún àwọn méjèèjì ní Tírà tó yanjú.');
INSERT INTO "aykyuni" VALUES(37,118,'37:118','A tún fi àwọn méjèèjì mọ̀nà tààrà.');
INSERT INTO "aykyuni" VALUES(37,119,'37:119','A fún àwọn méjèèjì ní orúkọ rere láààrin àwọn ẹni ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(37,120,'37:120','Àlàáfíà kí ó máa bá (Ànábì) Mūsā àti Hārūn.');
INSERT INTO "aykyuni" VALUES(37,121,'37:121','Dájúdájú báyẹn ni Àwa ṣe ń san àwọn olùṣe-rere ní ẹ̀san (rere).');
INSERT INTO "aykyuni" VALUES(37,122,'37:122','Dájúdájú àwọn méjèèjì wà nínú àwọn ẹrúsìn Wa, àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(37,123,'37:123','Dájúdájú (Ànábì) ’Ilyās wà nínú àwọn Òjíṣẹ́.');
INSERT INTO "aykyuni" VALUES(37,124,'37:124','(Ẹ rántí) nígbà tí ó sọ fún ìjọ rẹ̀ pé: “Ṣé ẹ ò níí bẹ̀rù Allāhu ni?');
INSERT INTO "aykyuni" VALUES(37,125,'37:125','Ṣé ẹ óò máa pe òrìṣà kan, ẹ sì máa fi Ẹni tó dára jùlọ nínú àwọn ẹlẹ́dàá sílẹ̀?1 ');
INSERT INTO "aykyuni" VALUES(37,126,'37:126','(ẹ̀ ń fi) Allāhu (sílẹ̀), Olúwa yín àti Olúwa àwọn bàbá yín, àwọn ẹni àkọ́kọ́?”');
INSERT INTO "aykyuni" VALUES(37,127,'37:127','Wọ́n sì pè é ní òpùrọ́. Nítorí náà, wọ́n máa mú wọn wá sínú Iná.');
INSERT INTO "aykyuni" VALUES(37,128,'37:128','Àfi àwọn ẹrúsìn Allāhu, àwọn tí A ṣà lẹ́ṣà (fún ìgbàgbọ́ òdodo).');
INSERT INTO "aykyuni" VALUES(37,129,'37:129','A fún un ní orúkọ rere láààrin àwọn ẹni ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(37,130,'37:130','Àlàáfíà kí ó máa bá (Ànábì) ’Ilyās.');
INSERT INTO "aykyuni" VALUES(37,131,'37:131','Dájúdájú báyẹn ni Àwa ṣe ń san àwọn olùṣe-rere ní ẹ̀san (rere).');
INSERT INTO "aykyuni" VALUES(37,132,'37:132','Dájúdájú ó wà nínú àwọn ẹrúsìn Wa, àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(37,133,'37:133','Dájúdájú (Ànábì) Lūt wà nínú àwọn Òjíṣẹ́.');
INSERT INTO "aykyuni" VALUES(37,134,'37:134','(Ẹ rántí) nígbà tí A gba òun àti gbogbo àwọn ará ilé rẹ̀ là.');
INSERT INTO "aykyuni" VALUES(37,135,'37:135','Àyàfi arúgbóbìnrin kan tí ó wà nínú àwọn tó ṣẹ́ kù lẹ́yìn (láààrin àwọn tí A parẹ́).1 ');
INSERT INTO "aykyuni" VALUES(37,136,'37:136','Lẹ́yìn náà, A pa àwọn yòókù run.');
INSERT INTO "aykyuni" VALUES(37,137,'37:137','Dájúdájú ẹ̀yin náà kúkú ń gba ọ̀dọ̀ wọn kọjá láàárọ̀-láàárọ̀');
INSERT INTO "aykyuni" VALUES(37,138,'37:138','àti ní alaalẹ́. Ṣé ẹ kò níí ṣe làákàyè ni?');
INSERT INTO "aykyuni" VALUES(37,139,'37:139','Dájúdájú (Ànábì) Yūnus kúkú wà nínú àwọn Òjíṣẹ́.');
INSERT INTO "aykyuni" VALUES(37,140,'37:140','(Ẹ rántí) nígbà tí ó sá lọ sínú ọkọ̀ ojú-omi tó kún (fún èrò).');
INSERT INTO "aykyuni" VALUES(37,141,'37:141','Ó sì bá wọn ṣe kúríà papọ̀. Ó sì wà nínú àwọn tí kúrìá já.1 ');
INSERT INTO "aykyuni" VALUES(37,142,'37:142','Nítorí náà, ẹja gbé e mì; ó sì ń dá ara rẹ̀ lẹ́bi.');
INSERT INTO "aykyuni" VALUES(37,143,'37:143','Nítorí náà, tí kò bá jẹ́ pé ó wà nínú àwọn olùṣàfọ̀mọ́ fún Allāhu,');
INSERT INTO "aykyuni" VALUES(37,144,'37:144','dájúdájú ìbá wà nínú ẹja títí di ọjọ́ tí wọn yóò gbé ẹ̀dá dìde.');
INSERT INTO "aykyuni" VALUES(37,145,'37:145','Nítorí náà, A jù ú sí orí ilẹ́ gban̄sasa létí omi. Ó sì jẹ́ aláìlera.');
INSERT INTO "aykyuni" VALUES(37,146,'37:146','A sì mú igi Yẹƙtīn hù jáde láti ṣíji bò ó.');
INSERT INTO "aykyuni" VALUES(37,147,'37:147','A tún rán an níṣẹ́ sí ọ̀kẹ́ márùn-ún (ènìyàn) tàbí kí wọ́n lékún sí i.1 ');
INSERT INTO "aykyuni" VALUES(37,148,'37:148','Wọ́n gbàgbọ́ ní òdodo. A sì fún wọn ní ìgbádùn títí di ìgbà díẹ̀.');
INSERT INTO "aykyuni" VALUES(37,149,'37:149','Bi wọ́n léèrè wò pé: “Ṣé Olúwa rẹ l’ó ni àwọn ọmọbìnrin, tiwọn sì ni ọmọkùnrin?');
INSERT INTO "aykyuni" VALUES(37,150,'37:150','Tàbí A dá àwọn mọlāika ní obìnrin, tí àwọn sì jẹ́ ẹlẹ́rìí?”');
INSERT INTO "aykyuni" VALUES(37,151,'37:151','Gbọ́, nínú irọ́ wọn ni pé dájúdájú wọ́n ń wí pé:');
INSERT INTO "aykyuni" VALUES(37,152,'37:152','“Allāhu bímọ.” Dájúdájú òpùrọ́ sì ni wọ́n.');
INSERT INTO "aykyuni" VALUES(37,153,'37:153','Ṣé (Allāhu) ṣẹ̀ṣà àwọn ọmọbìnrin lórí àwọn ọmọkùnrin ni?');
INSERT INTO "aykyuni" VALUES(37,154,'37:154','Kí l’ó ń ṣe yín tí ẹ fi ń dájọ́ báyìí?');
INSERT INTO "aykyuni" VALUES(37,155,'37:155','Ṣé ẹ kò níí lo ìrántí ni?');
INSERT INTO "aykyuni" VALUES(37,156,'37:156','Tàbí ẹ ní ẹ̀rí tó yanjú lọ́wọ́ ni?');
INSERT INTO "aykyuni" VALUES(37,157,'37:157','Ẹ mú tírà yín wà nígbà náà tí ẹ bá jẹ́ olódodo.');
INSERT INTO "aykyuni" VALUES(37,158,'37:158','Wọ́n tún fi ìbátan sáààrin (Allāhu) àti àlùjànnú! Àwọn àlùjànnú sì ti mọ̀ pé, dájúdájú wọ́n kúkú máa mú àwọn wá sínú Iná ni.');
INSERT INTO "aykyuni" VALUES(37,159,'37:159','Mímọ́ ni fún Allāhu tayọ ohun tí wọ́n ń fi ròyìn (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(37,160,'37:160','Àfi àwọn ẹrúsìn Allāhu, àwọn tí A ṣà lẹ́ṣà (fún ìgbàgbọ́ òdodo).');
INSERT INTO "aykyuni" VALUES(37,161,'37:161','Dájúdájú ẹ̀yin àti n̄ǹkan tí ẹ̀ ń jọ́sìn fún (lẹ́yìn Allāhu),');
INSERT INTO "aykyuni" VALUES(37,162,'37:162','ẹ kò lè fi ṣi ẹnì kan kan lọ́nà');
INSERT INTO "aykyuni" VALUES(37,163,'37:163','àfi ẹni tí ó bá fẹ́ wọ inú iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(37,164,'37:164','Kò sí ẹnì kan nínú àwa (mọlāika) àfi kí ó ní ibùdúró tí wọ́n ti mọ̀.');
INSERT INTO "aykyuni" VALUES(37,165,'37:165','Dájúdájú àwa, a kúkú wà ní ọ̀wọ̀ọ̀wọ́ ni.');
INSERT INTO "aykyuni" VALUES(37,166,'37:166','Àti pé dájúdájú àwa, àwa kúkú ni olùṣàfọ̀mọ́ (fún Allāhu).');
INSERT INTO "aykyuni" VALUES(37,167,'37:167','Wọ́n kúkú ń wí pé:');
INSERT INTO "aykyuni" VALUES(37,168,'37:168','“Tí ó bá jẹ́ pé dájúdájú a bá ní (tírà) ìrántí kan nínú (àwọn tírà) àwọn ẹni àkọ́kọ́,');
INSERT INTO "aykyuni" VALUES(37,169,'37:169','dájúdájú àwa ìbá jẹ́ ẹrúsìn Allāhu, àwọn tí A ṣà lẹ́ṣà (fún ìgbàgbọ́ òdodo).”');
INSERT INTO "aykyuni" VALUES(37,170,'37:170','Ṣùgbọ́n wọ́n ṣàì gbàgbọ́ nínú (Allāhu). Láìpẹ́ wọ́n máa mọ̀.');
INSERT INTO "aykyuni" VALUES(37,171,'37:171','Dájúdájú ọ̀rọ̀ Wa ti ṣíwájú fún àwọn ẹrúsìn Wa, àwọn Òjíṣẹ́.');
INSERT INTO "aykyuni" VALUES(37,172,'37:172','Dájúdájú àwọn, àwọn l’a kúkú máa ṣàrànṣe fún.');
INSERT INTO "aykyuni" VALUES(37,173,'37:173','Àti pé dájúdájú àwọn ọmọ ogun Wa, àwọn kúkú ni olùborí.');
INSERT INTO "aykyuni" VALUES(37,174,'37:174','Nítorí náà, ṣẹ́rí kúrò lọ́dọ̀ wọn fún ìgbà díẹ̀.');
INSERT INTO "aykyuni" VALUES(37,175,'37:175','Àti pé máa wò wọ́n níran ná, àwọn náà ń bọ̀ wá ríran wò.');
INSERT INTO "aykyuni" VALUES(37,176,'37:176','Ṣé ìyà Wa ni wọ́n ń wá pẹ̀lú ìkánjú?');
INSERT INTO "aykyuni" VALUES(37,177,'37:177','Nígbà tí ó bá sọ̀kalẹ̀ sí gbàgede wọn, òwúrọ̀ ọjọ́ náà yó sì burú fún àwọn ẹni-akìlọ̀-fún.');
INSERT INTO "aykyuni" VALUES(37,178,'37:178','Kúrò lọ́dọ̀ wọn títí di ìgbà kan ná.');
INSERT INTO "aykyuni" VALUES(37,179,'37:179','Àti pé máa wò wọ́n níran ná, àwọn náà ń bọ̀ wá ríran wò.');
INSERT INTO "aykyuni" VALUES(37,180,'37:180','Mímọ́ ni fún Olúwa rẹ, Olúwa (tó ni) agbára, tayọ ohun tí wọ́n ń fi ròyìn (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(37,181,'37:181','Kí àlàáfíà máa bá àwọn Òjíṣẹ́');
INSERT INTO "aykyuni" VALUES(37,182,'37:182','Gbogbo ẹyìn ń jẹ́ ti Allāhu, Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(38,1,'38:1','Sọ̄d. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú háràfí náà.)Allāhu fi al-Ƙur’ān tí ó kún fún ìrántí bura. ');
INSERT INTO "aykyuni" VALUES(38,2,'38:2','Síbẹ̀síbẹ̀ àwọn tó ṣàì gbàgbọ́ wà nínú ìgbéraga àti ìyapa (òdodo).');
INSERT INTO "aykyuni" VALUES(38,3,'38:3','Mélòó mélòó nínú àwọn ìran tí A ti parẹ́ ṣíwájú wọn. Nígbà náà, wọ́n kígbe (fún ìrànlọ́wọ́) nígbà tí kò sí ibùsásí kan.');
INSERT INTO "aykyuni" VALUES(38,4,'38:4','Wọ́n ṣèèmọ̀ pé olùkìlọ̀ kan nínú wọn wá bá wọn. Àwọn aláìgbàgbọ́ sì wí pé: “Èyí ni òpìdán, òpùrọ́.”');
INSERT INTO "aykyuni" VALUES(38,5,'38:5','Ṣé ó máa sọ àwọn òrìṣà di Ọlọ́hun Ọ̀kan ṣoṣo tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo? Dájúdájú èyí mà ni n̄ǹkan ìyanu.');
INSERT INTO "aykyuni" VALUES(38,6,'38:6','Àwọn aṣíwájú nínú wọn sì lọ (káà kiri láti wí fún àwọn ọmọlẹ́yìn wọn) pé: “Ẹ máa bá (ìbọ̀rìṣà) lọ, kí ẹ sì dúró ṣinṣin ti àwọn òrìṣà yín. Dájúdájú èyí (jíjẹ́ ọ̀kan ṣoṣo Allāhu) ni n̄ǹkan tí wọ́n ń gbàlérò (láti fi pa àwọn òrìṣà yín run).');
INSERT INTO "aykyuni" VALUES(38,7,'38:7','Àwa kò gbọ́ èyí nínú ẹ̀sìn ìkẹ́yìn (ìyẹn, ẹ̀sìn nasọ̄rọ̄) Èyí kò jẹ́ kiní kan bí kò ṣe àdápa irọ́. ');
INSERT INTO "aykyuni" VALUES(38,8,'38:8','Ṣé Wọ́n sọ (tírà) Ìrántí kalẹ̀ fún un láààrin wa ni?” Rárá, wọ́n wà nínú iyèméjì nípa (tírà) Ìrántí Mi (tí Mo sọ̀kalẹ̀ ni). Rárá, wọn kò tí ì tọ́ ìyà Mi wò ni.');
INSERT INTO "aykyuni" VALUES(38,9,'38:9','Tàbí tiwọn ni àwọn ilé ọ̀rọ̀ Olúwa rẹ, Alágbára, Ọlọ́rẹ?');
INSERT INTO "aykyuni" VALUES(38,10,'38:10','Tàbí tiwọn ni ìjọba àwọn sánmọ̀ àti ilẹ̀ àti ohun tí ń bẹ láààrin méjèèjì? Tí ó bá rí bẹ́ẹ̀, kí wọn wá àwọn ọ̀nà láti fi gùnkè wá (bá Wa).');
INSERT INTO "aykyuni" VALUES(38,11,'38:11','A máa ṣẹ́gun ọmọ ogun tó wà níbẹ̀ yẹn nínú ọmọ ogun oníjọ.1 ');
INSERT INTO "aykyuni" VALUES(38,12,'38:12','Àwọn ìjọ Nūh, ìjọ ‘Ād àti Fir‘aon, eléèkàn, wọ́n pe òdodo ní irọ́ ṣíwájú wọn.');
INSERT INTO "aykyuni" VALUES(38,13,'38:13','Ìjọ Thamūd, ìjọ Lūt àti àwọn ará ’Aekah, àwọn wọ̀nyẹn (tún ni) àwọn ìjọ (tó pe òdodo ní irọ́).');
INSERT INTO "aykyuni" VALUES(38,14,'38:14','Kò sí ẹnì kan nínú wọn tí kò pe àwọn Òjíṣẹ́ ní òpùrọ́. Nítorí náà, ìyà Mi sì kò lé wọn lórí.');
INSERT INTO "aykyuni" VALUES(38,15,'38:15','Àwọn wọ̀nyí kò retí kiní kan bí kò ṣe igbe ẹyọ kan, tí kò níí ní ìdádúró.1 ');
INSERT INTO "aykyuni" VALUES(38,16,'38:16','Wọ́n wí pé: “Olúwa wa, yára fi ìwé iṣẹ́ wa (àti ìpín ẹ̀san wa) lé wa lọ́wọ́ ṣíwájú Ọjọ́ ìṣírò-iṣẹ́.”');
INSERT INTO "aykyuni" VALUES(38,17,'38:17','Ṣe sùúrù lórí n̄ǹkan tí wọ́n ń sọ. Kí o sì ṣèrántí ìtàn ẹrúsìn Wa, (Ànábì) Dāwūd, alágbára. Dájúdájú ó jẹ́ olùronúpìwàdà.');
INSERT INTO "aykyuni" VALUES(38,18,'38:18','Dájúdájú Àwa tẹ àwọn àpáta lórí ba tí wọ́n ń ṣe àfọ̀mọ́ pẹ̀lú rẹ̀ (fún Allāhu) ní ìrọ̀lẹ́ àti nígbà tí òòrùn bá yọ (ní òwúrọ̀).');
INSERT INTO "aykyuni" VALUES(38,19,'38:19','Àti àwọn ẹyẹ náà, A kó wọn jọ fún un. Ìkọ̀ọ̀kan (wọn) ń tẹ̀lé àṣẹ rẹ̀ (láti ṣàfọ̀mọ́ fún Allāhu).');
INSERT INTO "aykyuni" VALUES(38,20,'38:20','Àti pé A fún ìjọba rẹ̀ ní agbára. A sì fún un ní ipò Ànábì àti ẹ̀kọ́ ọ̀rọ̀ àti ìdájọ́.');
INSERT INTO "aykyuni" VALUES(38,21,'38:21','Ǹjẹ́ ìró àwọn oníjà ti dé ọ̀dọ̀ rẹ; nígbà tí wọ́n pọ́n ògiri ilé ìjọ́sìn?');
INSERT INTO "aykyuni" VALUES(38,22,'38:22','Nígbà tí wọ́n wọlé tọ (Ànábì) Dāwūd, ẹ̀rù wọn bà á. Wọ́n sọ pé: “Má ṣe bẹ̀rù. Oníjà méjì (ni wá). Apá kan wa tayọ ẹnu-ààlà sí apá kan. Nítorí náà, fi òdodo dájọ́ láààrin wa. Má ṣàbòsí. Kí o sì tọ́ wa sí ọ̀nà tààrà.');
INSERT INTO "aykyuni" VALUES(38,23,'38:23','Dájúdájú èyí ni arákùnrin mi. Ó ní abo ewúrẹ́ mọ́kàndín-lọ́gọ́rùn-ún. Èmi sì ní abo ewúrẹ́ ẹyọ kan. Ó sì sọ pé: “Fà á lé mi lọ́wọ́ (Fi mí ṣe alámòójútó rẹ̀). Ó sì borí mi nínú ọ̀rọ̀.” (Ó fi ọ̀rọ̀ tirẹ̀ borí ọ̀rọ̀ tèmi.)');
INSERT INTO "aykyuni" VALUES(38,24,'38:24','(Ànábì Dāwūd) sọ pé: “Ó ti ṣàbòsí sí ọ nípa bíbèèrè abo ewúrẹ́ tìrẹ mọ́ àwọn abo ewúrẹ́ tirẹ̀. Dájúdájú ọ̀pọ̀lọ́pọ̀ nínú àwọn olùbáda-n̄ǹkanpọ̀, apá kan wọn máa ń tayọ ẹnu-ààlà lórí apá kan àfi àwọn tó bá gbàgbọ́ ní òdodo, tí wọ́n sì ṣe iṣẹ́ rere. Díẹ̀ sì ni wọ́n.” (Ànábì) Dāwūd sì mọ̀ dájú pé A kàn fi (ìbéèrè náà) ṣàdánwò fún òun ni. Nítorí náà, ó tọrọ àforíjìn lọ́dọ̀ Olúwa rẹ̀ (nípa àìtẹ́tí gbọ́ ọ̀rọ̀ lẹ́nu ẹni-afẹ̀sùnkàn). Ó dojú bolẹ̀ láti forí kanlẹ̀. Ó sì ronú pìwàdà (sọ́dọ̀ Allāhu).1 ');
INSERT INTO "aykyuni" VALUES(38,25,'38:25','Nítorí náà, A ṣàforíjìn ìyẹn fún un. Dájúdájú ìsúnmọ́ (Wa) àti àbọ̀ rere sì wà fún un lọ́dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(38,26,'38:26','(Ànábì) Dāwūd, dájúdájú Àwa ṣe ọ́ ní àrólé lórí ilẹ̀. Nítorí náà, fi òdodo dájọ́ láààrin àwọn ènìyàn. Má ṣe tẹ̀lé ìfẹ́-inú nítorí kí ó má baà ṣìnà kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu. Dájúdájú àwọn tó ń sọnù kúrò nínú ẹ̀sìn Allāhu, ìyà líle wà fún wọn nítorí pé, wọ́n gbàgbé Ọjọ́ ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(38,27,'38:27','A kò fi irọ́ ṣẹ̀dá sánmọ̀, ilẹ̀ àti ohunkóhun tó wà láààrin méjèèjì. (Irọ́), ìyẹn ni èrò ọkàn àwọn tó ṣàì gbàgbọ́ (sí Wa). Nítorí náà, ègbé ni fún àwọn tó ṣàì gbàgbọ́ nínú Iná.');
INSERT INTO "aykyuni" VALUES(38,28,'38:28','Ṣé kí Á ṣe àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere bí (A ó ti ṣe) àwọn òbìlẹ̀jẹ́ lórí ilẹ̀? Tàbí ṣé kí Á ṣe àwọn olùbẹ̀rù (Mi bí A ó ti ṣe) àwọn aṣebi?');
INSERT INTO "aykyuni" VALUES(38,29,'38:29','(Èyí ni) Tírà ìbùkún tí A sọ̀kalẹ̀ fún ọ nítorí kí wọ́n lè ronú jinlẹ̀ nípa àwọn āyah rẹ̀ àti nítorí kí àwọn onílàákàyè lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(38,30,'38:30','A fi (Ànábì) Sulaemọ̄n ta (Ànábì) Dāwūd lọ́rẹ. Ẹrúsìn rere ni. Dájúdájú olùṣẹ́rí sọ́dọ̀ (Allāhu) ni (nípa ìronúpìwàdà).');
INSERT INTO "aykyuni" VALUES(38,31,'38:31','(Rántí) nígbà tí wọ́n kó àwọn ẹṣin akáwọ́ọ̀jà-lérí asárétete wá bá a ní ìrọ̀lẹ́,');
INSERT INTO "aykyuni" VALUES(38,32,'38:32','ó sọ pé: “Dájúdájú èmi fẹ́ràn ìfẹ́ ohun rere (ìyẹn, àwọn ẹṣin náà) dípò ìrántí Olúwa Mi (ìyẹn, ìrun ‘Asr) títí òòrùn fi wọ̀.');
INSERT INTO "aykyuni" VALUES(38,33,'38:33','Ẹ dá wọn padà sọ́dọ̀ mi.” Ó sì bẹ̀rẹ̀ sí í fi idà gé wọn ní ẹsẹ̀ àti ní ọrùn.');
INSERT INTO "aykyuni" VALUES(38,34,'38:34','Dájúdájú A dán (Ànábì) Sulaemọ̄n wò. A ju abara kan sórí àga rẹ̀. Lẹ́yìn náà, (Ànábì Sulaemọ̄n) ṣẹ́rí padà (pẹ̀lú ìronúpìwàdà).1 ');
INSERT INTO "aykyuni" VALUES(38,35,'38:35','Ó sọ pé: “Olúwa mi, forí jìn mí. Kí O sì ta mí ní ọrẹ ìjọba kan èyí tí kò níí tọ́ sí ẹnì kan kan mọ́ lẹ́yìn mi. Dájúdájú Ìwọ, Ìwọ ni Ọlọ́rẹ.”');
INSERT INTO "aykyuni" VALUES(38,36,'38:36','Nítorí náà, A tẹ atẹ́gùn ba fún un. Ó ń fẹ́ pẹ̀lú àṣẹ rẹ̀ ní ìrọ̀rùn síbi tí ó bá fẹ́.');
INSERT INTO "aykyuni" VALUES(38,37,'38:37','Àti àwọn èṣù àlùjànnú; gbogbo àwọn ọ̀mọ̀lé àti àwọn awakùsà (ni A tẹ̀ ba fún un).');
INSERT INTO "aykyuni" VALUES(38,38,'38:38','Àti àwọn (àlùjànnú) mìíràn tí wọ́n fi ẹ̀wọ̀n dè mọ́lẹ̀ (A tẹ̀ wọ́n ba fún un).');
INSERT INTO "aykyuni" VALUES(38,39,'38:39','Èyí ni ọrẹ Wa. Nítorí náà, fi tọrẹ tàbí kí ó mú un dáníláì níí ní ìṣírò.2 ');
INSERT INTO "aykyuni" VALUES(38,40,'38:40','Dájúdájú ìsúnmọ́ (Wa) àti àbọ̀ rere sì wà fún un ní ọ̀dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(38,41,'38:41','Ṣèrántí ẹrúsìn Wa, (Ànábì)’Ayyūb. Nígbà tí ó pe Olúwa rẹ̀ (pé): “Dájúdájú Èṣù ti kó ìnira (àìsàn) àti ìyà bá mi.”1 ');
INSERT INTO "aykyuni" VALUES(38,42,'38:42','(Mọlāika sọ fún un pé): “Fi ẹsẹ̀ rẹ janlẹ̀. Èyí ni omi ìwẹ̀ tútù àti omi mímu (fún ìwòsàn rẹ).”');
INSERT INTO "aykyuni" VALUES(38,43,'38:43','A sì ta á lọ́rẹ àwọn ará ilé rẹ̀ (padà) àti irú wọn pẹ̀lú wọn. (Ó jẹ́) ìkẹ́ láti ọ̀dọ̀ Wa àti ìrántí fún àwọn onílàákàyè.');
INSERT INTO "aykyuni" VALUES(38,44,'38:44','Fi ọwọ́ rẹ mú ìdì igi koríko tútù kí o fi lu (ìyàwó) rẹ. Má ṣe yapa ìbúra rẹ.Dájúdájú Àwa rí (’Ayyūb) ní onísùúrù. Ẹrúsìn rere ni. Dájúdájú olùṣẹ́rí sí ọ̀dọ̀ Allāhu ni (nípa ìronúpìwàdà). ');
INSERT INTO "aykyuni" VALUES(38,45,'38:45','Ṣèrántí àwọn ẹrúsìn Wa, (Ànábì) ’Ibrọ̄hīm, ’Ishāƙ àti Ya‘ƙūb; àwọn alágbára àti olùríran (nípa ẹ̀sìn).');
INSERT INTO "aykyuni" VALUES(38,46,'38:46','Dájúdájú Àwa ṣà wọ́n lẹ́ṣà pẹ̀lú ẹ̀ṣà kan; ìrántí Ilé Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(38,47,'38:47','Dájúdájú wọ́n wà nínú àwọn ẹni ẹ̀ṣà, ẹni rere jùlọ ní ọ̀dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(38,48,'38:48','Ṣèrántí (àwọn Ànábì) ’Ismọ̄‘īl, al-Yasa‘ àti Thul-Kifl. Ìkọ̀ọ̀kan wọn wà nínú àwọn ẹni rere jùlọ.');
INSERT INTO "aykyuni" VALUES(38,49,'38:49','Èyí ni ìrántí. Àti pé dájúdájú àbọ̀ rere wà fún àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(38,50,'38:50','Àwọn Ọgbà Ìdẹ̀ra gbére (ni). Wọ́n sì máa ṣí àwọn ìlẹ̀kùn rẹ̀ sílẹ̀ fún wọn.');
INSERT INTO "aykyuni" VALUES(38,51,'38:51','Wọn yóò rọ̀gbọ̀kú nínú rẹ̀. Wọn yóò máa bèèrè fún àwọn èso púpọ̀ àti ohun mímu nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(38,52,'38:52','Àwọn obìnrin tí kì í wo ẹlòmíìràn, tí ọjọ́ orí wọn kò jura wọn lọ yó sì wà ní ọ̀dọ̀ wọn.');
INSERT INTO "aykyuni" VALUES(38,53,'38:53','Èyí ni ohun tí Wọ́n ń ṣe ní àdéhùn fún yín fún Ọjọ́ ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(38,54,'38:54','Dájúdájú èyí ni arísìkí Wa. Kò sì níí tán.');
INSERT INTO "aykyuni" VALUES(38,55,'38:55','Èyí (rí bẹ́ẹ̀). Àti pé dájúdájú àbọ̀ burúkú ni ti àwọn alágbèéré (sí Allāhu).');
INSERT INTO "aykyuni" VALUES(38,56,'38:56','Iná Jahanamọ ni wọn yó wọ̀. Ó sì burú ní ibùgbé.');
INSERT INTO "aykyuni" VALUES(38,57,'38:57','Èyí (rí bẹ́ẹ̀). Nítorí náà, kí wọ́n tọ́ ọ wò; omi tó gbóná parí àti àwọnúwẹ̀jẹ̀.');
INSERT INTO "aykyuni" VALUES(38,58,'38:58','Oríṣiríṣi (ìyà) mìíràn bí irú rẹ̀ (tún wà fún wọn).');
INSERT INTO "aykyuni" VALUES(38,59,'38:59','Èyí ni ìjọ kan tó máa wọ inú Iná pẹ̀lú yín. (Àwọn aṣíwájú nínú Iná sì máa wí pé:) “Kò sí máawolẹ̀-máarọra fún wọn.” Dájúdájú wọn yóò wọ inú Iná ni.');
INSERT INTO "aykyuni" VALUES(38,60,'38:60','(Àwọn ọmọlẹ́yìn nínú Iná) máa wí pé: “Rárá, ẹ̀yin náà kò sí máawolẹ̀-máarọra fún yín. Ẹ̀yin l’ẹ pè wá síbi èyí (tó bí Iná).” Ó sì burú ní ibùgbé.');
INSERT INTO "aykyuni" VALUES(38,61,'38:61','Wọ́n (tún) wí pé: “Olúwa wa, ẹni tí ó pè wá (síbi ìyà) yìí, ṣe àlékún àdìpèlé ìyà fún un nínú Iná.”');
INSERT INTO "aykyuni" VALUES(38,62,'38:62','Wọ́n tún wí pé: “Kí ló ṣẹlẹ̀ sí wa tí a kò rí àwọn ọkùnrin kan, àwọn tí à ń kà mọ́ àwọn ẹni burúkú.');
INSERT INTO "aykyuni" VALUES(38,63,'38:63','Ṣèbí a fi wọ́n ṣe yẹ̀yẹ́ tàbí àwọn ojú ti fò wọ́n ni (l’a ò fi rí wọn nínú Iná)?”');
INSERT INTO "aykyuni" VALUES(38,64,'38:64','Dájúdájú ìyẹn, àríyànjiyàn àwọn èrò inú Iná, òdodo mà ni.');
INSERT INTO "aykyuni" VALUES(38,65,'38:65','Sọ pé: “Èmi ni olùkìlọ̀. Àti pé kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Allāhu, Ọ̀kan ṣoṣo, Olùborí.');
INSERT INTO "aykyuni" VALUES(38,66,'38:66','Olúwa àwọn sánmọ̀ àti ilẹ̀ àti ohunkóhun tó wà láààrin méjèèjì, Alágbára, Aláforíjìn.”');
INSERT INTO "aykyuni" VALUES(38,67,'38:67','Sọ pé: “(al-Ƙur’ān) ni ìró ńlá.');
INSERT INTO "aykyuni" VALUES(38,68,'38:68','Ẹ̀yin sì ń gbúnrí kúrò níbẹ̀.');
INSERT INTO "aykyuni" VALUES(38,69,'38:69','Èmi kò sì nímọ̀ nípa àwọn mọlāika tí ó wà ní àyè gíga nígbà tí wọ́n ń ṣàròyé.');
INSERT INTO "aykyuni" VALUES(38,70,'38:70','Kí ni wọ́n fi ránṣẹ́ sí mi bí kò ṣe pé èmi ni olùkìlọ̀ pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(38,71,'38:71','(Rántí) nígbà tí Olúwa rẹ sọ fún àwọn mọlāika pé: “Dájúdájú Èmi yóò dá abara kan láti inú ẹrẹ̀.');
INSERT INTO "aykyuni" VALUES(38,72,'38:72','Nígbà tí Mo bá ṣe é tó dọ́gba jálẹ̀ tán, tí Mo sì fẹ́ nínú atẹ́gùn ẹ̀mí tí Mo dá sínú rẹ̀, nígbà náà ẹ dojú bolẹ̀ fún un, kí ẹ forí kanlẹ̀ kí i.”');
INSERT INTO "aykyuni" VALUES(38,73,'38:73','Gbogbo àwọn mọlāika pátápátá sì forí kanlẹ̀ kí i.');
INSERT INTO "aykyuni" VALUES(38,74,'38:74','Àyàfi ’Iblīs, tí ó ṣègbéraga. Ó sì wà nínú àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(38,75,'38:75','(Allāhu) sọ pé: “’Iblīs, kí l’ó kọ̀ fún ọ láti forí kanlẹ̀ kí ohun tí Mo fi ọwọ́ Mi méjèèjì dá? Ṣé o ṣègbéraga ni tàbí o wà nínú àwọn ẹni gíga?”');
INSERT INTO "aykyuni" VALUES(38,76,'38:76','Ó wí pé: “Èmi lóore jù ú lọ; O dá èmi láti ara iná. O sì dá òun láti ara ẹrẹ̀.”');
INSERT INTO "aykyuni" VALUES(38,77,'38:77','(Allāhu) sọ pé: “Jáde kúrò nínú (Ọgbà Ìdẹ̀ra) nítorí pé, dájúdájú ìwọ ni ẹni ẹ̀kọ̀.');
INSERT INTO "aykyuni" VALUES(38,78,'38:78','Àti pé dájúdájú ègún Mi yóò wà lórí rẹ títí di Ọjọ́ ẹ̀san.”');
INSERT INTO "aykyuni" VALUES(38,79,'38:79','(Èṣù) wí pé: “Olúwa mi, lọ́ mi lára títí di ọjọ́ tí Wọn yóò gbé ẹ̀dá dìde.”');
INSERT INTO "aykyuni" VALUES(38,80,'38:80','(Allāhu) sọ pé: “Dájúdájú ìwọ wà nínú àwọn tí wọ́n máa lọ́ lára');
INSERT INTO "aykyuni" VALUES(38,81,'38:81','títí di ọjọ́ àkókò tí A ti mọ̀.”');
INSERT INTO "aykyuni" VALUES(38,82,'38:82','(Èṣù) wí pé: “Mo fi agbára Rẹ búra, dájúdájú èmi yóò kó gbogbo wọn sínú ìṣìnà');
INSERT INTO "aykyuni" VALUES(38,83,'38:83','àfi àwọn ẹrúsìn Rẹ, àwọn tí O ṣà lẹ́ṣà (fún ìgbàgbọ́ òdodo) nínú wọn.”');
INSERT INTO "aykyuni" VALUES(38,84,'38:84','(Allāhu) sọ pé: “Òdodo (ni ìbúra Mi), òdodo sì ni Èmi ń sọ, (pé)');
INSERT INTO "aykyuni" VALUES(38,85,'38:85','dájúdájú Mo máa fi ìwọ àti gbogbo àwọn tó bá tẹ̀lé ọ nínú wọn kún inú iná Jahanamọ.”');
INSERT INTO "aykyuni" VALUES(38,86,'38:86','Sọ pé: “Èmi kò bi yín léèrè owó-ọ̀yà kan lórí rẹ̀. Èmi kò sì sí nínú àwọn onítàn-àròsọ.”');
INSERT INTO "aykyuni" VALUES(38,87,'38:87','Kí ni al-Ƙur’ān bí kò ṣe ìrántí fún gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(38,88,'38:88','Àti pé dájúdájú ẹ máa mọ ìró rẹ̀ (sí òdodo) láìpẹ́.');
INSERT INTO "aykyuni" VALUES(39,1,'39:1','Tírà náà sọ̀kalẹ̀ díẹ̀ díẹ̀láti ọ̀dọ̀ Allāhu, Alágbára, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(39,2,'39:2','Dájúdájú Àwa sọ Tírà (al-Ƙur’ān) kalẹ̀ fún ọ pẹ̀lú òdodo. Nítorí náà, jọ́sìn fún Allāhu lẹ́ni tí yóò máa fi àfọ̀mọ́-ọkàn (àníyàn mímọ́) ṣe ẹ̀sìn fún Un.1 ');
INSERT INTO "aykyuni" VALUES(39,3,'39:3','Gbọ́! Ti Allāhu ni ẹ̀sìn mímọ́.Àwọn tí wọ́n sì mú àwọn aláfẹ̀yìntì kan yàtọ̀ sí Allāhu, (wọ́n wí pé): “A kò jọ́sìn fún wọn bí kò ṣe pé, nítorí kí wọ́n lè mú wa súnmọ́ Allāhu pẹ́kípẹ́kí ni.” Dájúdájú Allāhu l’Ó máa dájọ́ láààrin wọn nípa ohun tí wọ́n ń yapa-ẹnu nípa rẹ̀ (ìyẹn, ẹ̀sìn ’Islām). Dájúdájú Allāhu kò níí fi ọ̀nà mọ ẹni tí ó jẹ́ òpùrọ́, aláìgbàgbọ́. ');
INSERT INTO "aykyuni" VALUES(39,4,'39:4','Tí ó bá jẹ́ pé Allāhu bá fẹ́ fi ẹnì kan ṣe ọmọ ni, ìbá ṣẹ̀ṣà ohun tí Ó bá fẹ́ (fi ṣọmọ) nínú n̄ǹkan tí Ó dá. Mímọ́ ni fún Un (níbi èyí). Òun ni Allāhu, Ọ̀kan ṣoṣo, Olùborí.');
INSERT INTO "aykyuni" VALUES(39,5,'39:5','Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ pẹ̀lú òdodo. Ó ń ti òru bọnú ọ̀sán. Ó sì ń ti ọ̀sán bọnú òru. Ó sì rọ òòrùn àti òṣùpá; ìkọ̀ọ̀kan wọn máa rìn fún gbèdéke àkókò kan. Gbọ́! Òun ni Alágbára, Aláforíjìn.');
INSERT INTO "aykyuni" VALUES(39,6,'39:6','Ó ṣẹ̀dá yín láti ara ẹ̀mí ẹyọ kan. Lẹ́yìn náà, Ó dá ìyàwó rẹ̀ láti ara rẹ̀. Ó sì sọ mẹ́jọ kalẹ̀ fún yín nínú ẹran-ọ̀sìn ní takọ- tabo. Ó ń ṣẹ̀dá yín sínú ìyá yín, ẹ̀dá kan lẹ́yìn ẹ̀dá kan nínú àwọn òkùnkùn mẹ́ta.Ìyẹn ni Allāhu, Olúwa yín. TiRẹ̀ ni ìjọba. Kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Nítorí náà, báwo ni wọ́n ṣe ń ṣẹ yín lórí kúrò níbi òdodo? ');
INSERT INTO "aykyuni" VALUES(39,7,'39:7','Tí ẹ bá ṣàì moore, dájúdájú Allāhu rọrọ̀ láì sí ẹ̀yin (kò sì ní bùkátà si yín). Kò sì yọ́nú sí àìmoore fún àwọn ẹrúsìn Rẹ̀. Tí ẹ bá dúpẹ́, Ó máa yọ́nú sí i fún yín. Ẹlẹ́rù-ẹ̀ṣẹ̀ kan kò sì níí ru ẹrù ẹ̀ṣẹ̀ ẹlòmíìràn. Lẹ́yìn náà, ọ̀dọ̀ Olúwa yín ni ibùpadàsí yín. Ó sì máa fún yín ní ìró ohun tí ẹ̀ ń ṣe níṣẹ́. Dájúdájú Òun ni Onímọ̀ nípa ohun tí ń bẹ nínú igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(39,8,'39:8','Nígbà tí ìnira kan bá fọwọ́ ba ènìyàn, ó máa pe Olúwa rẹ̀ lẹ́ni tí ó ń ṣẹ́rí padà sí ọ̀dọ̀ Rẹ̀. Lẹ́yìn náà, nígbà tí Ó bá fún un ní ìdẹ̀ra kan láti ọ̀dọ̀ Rẹ̀, ó máa gbàgbé ohun tó ṣe ní àdúà sí (Olúwa rẹ) ṣíwájú. Ó sì máa sọ (àwọn ẹ̀dá kan) di akẹgbẹ́ fún Allāhu nítorí kí ó lè kó ìṣìnà bá (ẹlòmíìràn) ní ojú ọ̀nà ẹ̀sìn Rẹ̀ (’Islām). Sọ pé: “Fi àìgbàgbọ́ rẹ jẹ̀gbádùn ayé fún ìgbà díẹ̀. Dájúdájú ìwọ wà nínú àwọn èrò inú Iná.”');
INSERT INTO "aykyuni" VALUES(39,9,'39:9','Ǹjẹ́ ẹni tí ó jẹ́ olùtẹ̀lé ti Allāhu (tó jẹ́) olùforíkanlẹ̀ àti olùdìdedúró (lórí ìrun kíkí) ní àwọn àkókò alẹ́, tó ń ṣọ́ra fún ọ̀run, tó sì ń ní àgbẹ́kẹ̀lé nínú àánú Olúwa rẹ̀ (dà bí ẹlẹ́ṣẹ̀ bí?) Sọ pé: “Ǹjẹ́ àwọn tí wọ́n nímọ̀ àti àwọn tí kò nímọ̀ dọ́gba bí? Àwọn onílàákàyè nìkan l’ó ń lo ìrántí.');
INSERT INTO "aykyuni" VALUES(39,10,'39:10','Sọ pé: “Ẹ̀yin ẹrúsìn Mi tí ẹ gbàgbọ́ ní òdodo, ẹ bẹ̀rù Olúwa yín. Ẹ̀san rere wà fún àwọn tó ṣe rere ní ilé ayé yìí.Ilẹ̀ Allāhu sì gbòòrò.2 Àwọn onísùúrù ni Wọ́n sì máa fún ní ẹ̀san (rere iṣẹ́) wọn ní ẹ̀kúnrẹ́rẹ́ láì níí ní ìṣírò.”3 ');
INSERT INTO "aykyuni" VALUES(39,11,'39:11','Sọ pé: “Dájúdájú Wọ́n pa mí ní àṣẹ pé kí n̄g jọ́sìn fún Allāhu lẹ́ni tí yóò máa fi àfọ̀mọ́-ọkàn (àníyàn mímọ́) ṣe ẹ̀sìn fún Un.1 ');
INSERT INTO "aykyuni" VALUES(39,12,'39:12','Wọ́n tún pa mí ní àṣẹ pé kí èmi jẹ́ ẹni àkọ́kọ́ (nínú) àwọn mùsùlùmí (ní àsìkò tèmi).”1 ');
INSERT INTO "aykyuni" VALUES(39,13,'39:13','Sọ pé: “Dájúdájú èmi ń páyà ìyà ọjọ́ ńlá, tí mo bá fi lè yapa àṣẹ Olúwa mi.”');
INSERT INTO "aykyuni" VALUES(39,14,'39:14','Sọ pé: “Allāhu ni èmi yóò máa jọ́sìn fún lẹ́ni tí èmi yóò máa fi àfọ̀mọ́-ọkàn (àníyàn mímọ́) ṣe ẹ̀sìn mi fún Un');
INSERT INTO "aykyuni" VALUES(39,15,'39:15','Ẹ̀yin ẹ jọ́sìn fún ohun tí ẹ bá fẹ́ lẹ́yìn Rẹ̀.”Sọ pé: “Dájúdájú àwọn ẹni òfò ni àwọn tó ṣe ẹ̀mí ara wọn àti ará ilé wọn ní òfò ní Ọjọ́ Àjíǹde. Gbọ́! Ìyẹn, òhun ni òfò pọ́nńbélé.” ');
INSERT INTO "aykyuni" VALUES(39,16,'39:16','Àwọn àjà Iná máa wà ní òkè wọn. Àwọn àjà yó sì wà ní ìsàlẹ̀ wọn. Ìyẹn ni Allāhu fi ń dẹ́rù ba àwọn ẹrúsìn Rẹ̀ (báyìí pé:) “Ẹ̀yin ẹrúsìn Mi, ẹ bẹ̀rù Mi.”');
INSERT INTO "aykyuni" VALUES(39,17,'39:17','Àwọn tí wọ́n takété sí àwọn òrìṣà tí wọn kò jọ́sìn fún un,wọ́n sì ṣẹ́rí padà sí (jíjọ́sìn fún) Allāhu, ìró ìdùnnú ń bẹ fún wọn. Nítorí náà, fún àwọn ẹrúsìn Mi ní ìró ìdùnnú. ');
INSERT INTO "aykyuni" VALUES(39,18,'39:18','Àwọn tó ń tẹ́tí gbọ́ ọ̀rọ̀, tí wọ́n sì ń tẹ̀lé èyí tó dára jùlọ nínú rẹ̀, àwọn wọ̀nyẹn ni àwọn tí Allāhu fi mọ̀nà. Àwọn wọ̀nyẹn, àwọn ni onílàákàyè.');
INSERT INTO "aykyuni" VALUES(39,19,'39:19','Ṣé ẹni tí ọ̀rọ̀ ìyà Iná ti kòlé lórí (nípa àìgbàgbọ́ rẹ̀, ṣé kò níí wọná ni?) Ṣé ìwọ l’ó máa la ẹni tó wà nínú Iná (nípasẹ̀ àìgbàgbọ́ rẹ̀) ni?');
INSERT INTO "aykyuni" VALUES(39,20,'39:20','Ṣùgbọ́n àwọn tó bẹ̀rù Olúwa wọn, tiwọn ni àwọn ilé gíga, tí àwọn ilé gíga tún wà lókè rẹ̀, àwọn odò yó sì máa ṣàn ní ìsàlẹ̀ rẹ̀. Àdéhùn Allāhu ni (èyí). Allāhu kò sì níí yẹ àdéhùn náà.');
INSERT INTO "aykyuni" VALUES(39,21,'39:21','Ṣé o kò wòye pé dájúdájú Allāhu l’Ó sọ omi kalẹ̀ láti sánmọ̀, Ó sì mú omi náà wọnú àwọn omi ìṣẹ́lẹ̀rú nínú ilẹ̀, lẹ́yìn náà, Ó ń fi mú irúgbìn tí àwọ̀ wọn yàtọ̀ síra wọn jáde, lẹ́yìn náà, (irúgbìn náà) yóò gbẹ, o sì máa rí i ní pípọ́n, lẹ́yìn náà, Ó máa sọ ọ́ di rírún? Dájúdájú ìrántí wà nínú ìyẹn fún àwọn onílàákàyè.');
INSERT INTO "aykyuni" VALUES(39,22,'39:22','Ǹjẹ́ ẹni tí Allāhu gba ọkàn rẹ̀ láàyè fún ẹ̀sìn ’Islām, tí ó sì wà nínú ìmọ́lẹ̀ láti ọ̀dọ̀ Olúwa rẹ̀ (dà bí aláìgbàgbọ́ bí)? Ègbé ni fún àwọn tí ọkàn wọn le sí ìrántí Allāhu. Àwọn wọ̀nyẹn wà nínú ìṣìnà pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(39,23,'39:23','Allāhu l’Ó sọ ọ̀rọ̀ tó dára jùlọ kalẹ̀, (ó jẹ́) Tírà, tí (àwọn ọ̀rọ̀ inú rẹ̀) jọra wọn ní ọ̀rọ̀ àsọtúnsọ, tí awọ ara àwọn tó ń páyà Olúwa wọn yó sì máa wárìrì nítorí rẹ̀. Lẹ́yìn náà, awọ ara wọn àti ọkàn wọn yóò máa rọ̀ níbi ìrántí Allāhu. Ìyẹn ni ìmọ̀nà Allāhu. Ó sì ń fi ṣe ìmọ̀nà fún ẹni tí Ó bá fẹ́. Ẹni tí Allāhu bá ṣì lọ́nà, kò sí níí sí afinimọ̀nà kan fún un.');
INSERT INTO "aykyuni" VALUES(39,24,'39:24','Ǹjẹ́ ẹni tí ó máa fojú ara rẹ̀ ko aburú ìyà Iná ní Ọjọ́ Àjíǹde (dà bí ẹni tí ó máa wọ inú Ọgbà Ìdẹ̀ra wọ́ọ́rọ́wọ́)? Wọn yó sì sọ fún àwọn alábòsí pé: “Ẹ tọ́ ìyà ohun tí ẹ̀ ń ṣe níṣẹ́ wò.”');
INSERT INTO "aykyuni" VALUES(39,25,'39:25','Àwọn tó ṣíwájú wọn pe òdodo ní irọ́. Nítorí náà, ìyà dé bá wọn ní àyè tí wọn kò ti fura.');
INSERT INTO "aykyuni" VALUES(39,26,'39:26','Allāhu fún wọn ní àbùkù ìyà tọ́ wò nínú ìṣẹ̀mí ayé yìí. Ìyà ọ̀run sì tóbi jùlọ tí ó bá jẹ́ pé wọ́n mọ̀.');
INSERT INTO "aykyuni" VALUES(39,27,'39:27','A sì ti ṣe gbogbo àkàwé (àpèjúwe àti àpẹ̀ẹrẹ) fún àwọn ènìyàn nínú al-Ƙur’ān yìí nítorí kí wọ́n lè lo ìrántí');
INSERT INTO "aykyuni" VALUES(39,28,'39:28','Al-Ƙur’ān (tí A sọ̀kalẹ̀ pẹ̀lú) èdè Lárúbáwá, èyí tí ọ̀rọ̀ inú rẹ̀ kò dojú rú nítorí kí wọ́n lè bẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(39,29,'39:29','Allāhu fi àkàwé kan lélẹ̀; ẹrúkùnrin kan tó wà lábẹ́ àṣẹ ọ̀gá púpọ̀ tí wọ́n ń ṣe ìfàn̄fà lórí rẹ̀ (kò sì mọ ta ni ó máa dá lóhùn nínú àwọn ọ̀gá rẹ̀) àti (àkàwé) ẹrúkùnrin kan tó dá wà gédégbé lábẹ́ ọ̀gákùnrin kan. Ǹjẹ́ àwọn (ẹrú) méjèèjì dọ́gba ní àkàwé bí? Gbogbo ọpẹ́ ń jẹ́ ti Allāhu, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò mọ̀.');
INSERT INTO "aykyuni" VALUES(39,30,'39:30','Dájúdájú ìwọ máa kú. Dájúdájú àwọn náà máa kú.');
INSERT INTO "aykyuni" VALUES(39,31,'39:31','Lẹ́yìn náà, ní Ọjọ́ Àjíǹde dájúdájú ẹ̀yin yóò máa bá ara yín ṣe àríyànjiyàn ní ọ̀dọ̀ Olúwa yín.');
INSERT INTO "aykyuni" VALUES(39,32,'39:32','Nítorí náà, ta l’ó ṣàbòsí ju ẹni tó parọ́ mọ́ Allāhu, tó tún pe òdodo ní irọ́ nígbà tí ó dé bá a? Ṣé kì í ṣe Iná ni ibùgbé fún àwọn aláìgbàgbọ́ ni?');
INSERT INTO "aykyuni" VALUES(39,33,'39:33','Ẹni tí ó sì mú òdodo wá (ìyẹn, Ànábì Muhammad - kí ìkẹ́ àti ọlà Allāhu máa bá a -) àti (àwọn) ẹni tó jẹ́rìí sí òdodo náà; àwọn wọ̀nyẹn, àwọn ni olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(39,34,'39:34','Ohunkóhun tí wọ́n bá ń fẹ́ wà fún wọn ní ọ̀dọ̀ Olúwa wọn. Ìyẹn sì ni ẹ̀san àwọn ẹni rere');
INSERT INTO "aykyuni" VALUES(39,35,'39:35','nítorí kí Allāhu lè bá wọn pa aburú tí wọ́n ṣe níṣẹ́ rẹ́ àti (nítorí) kí Ó lè fi èyí tó dára ju ohun tí wọ́n ń ṣe níṣẹ́ san wọ́n ní ẹ̀san wọn.');
INSERT INTO "aykyuni" VALUES(39,36,'39:36','Ṣé Allāhu kò tó fún ẹrúsìn Rẹ̀ ni? Wọ́n sì ń fi àwọn ẹlòmíìràn lẹ́yìn Rẹ̀ dẹ́rù bà ọ́! Ẹnikẹ́ni tí Allāhu bá ṣì lọ́nà, kò lè sí afinimọ̀nà kan fún un.');
INSERT INTO "aykyuni" VALUES(39,37,'39:37','Ẹnikẹ́ni tí Allāhu bá fi mọ̀nà (’Islām), kò lè sí aṣinilọ́nà fún un. Ṣé Allāhu kọ́ ni Alágbára, Olùgba-ẹ̀san?');
INSERT INTO "aykyuni" VALUES(39,38,'39:38','Dájúdájú tí o bá bi wọ́n léèrè pé: “Ta ni Ó dá àwọn sánmọ̀ àti ilẹ̀?”, dájúdájú wọ́n á wí pé: “Allāhu ni.” Sọ pé: “Ẹ sọ fún mi nípa àwọn n̄ǹkan tí ẹ̀ ń pè lẹ́yìn Allāhu, tí Allāhu bá gbèrò ìnira kan rò mí, ǹjẹ́ wọ́n lè mú ìnira Rẹ̀ kúrò fún mi? Tàbí tí Ó bá gbèrò ìkẹ́ kan sí mi, ǹjẹ́ wọ́n lè dá ìkẹ́ Rẹ̀ dúró bí?” Sọ pé: “Allāhu tó fún mi. Òun sì ni àwọn olùgbáralé ń gbáralé.”');
INSERT INTO "aykyuni" VALUES(39,39,'39:39','Sọ pé: “Ẹ̀yin ènìyàn mi, ẹ ṣe tiyín ní àyè yín. Èmi náà ń ṣe tèmi. Nítorí náà, láìpẹ́ ẹ máa mọ');
INSERT INTO "aykyuni" VALUES(39,40,'39:40','ẹni tí ìyà tí ó máa yẹpẹrẹ rẹ̀ máa dé bá, tí ìyà gbere sì máa kò lé lórí.”');
INSERT INTO "aykyuni" VALUES(39,41,'39:41','Dájúdájú Àwa sọ Tírà kalẹ̀ fún ọ pẹ̀lú òdodo (kí o lè fi ṣe ìrántí) fún àwọn ènìyàn. Nítorí náà, ẹni tí ó bá mọ̀nà, ó mọ̀nà fún ẹ̀mí ara rẹ̀. Ẹni tí ó bá sì ṣìnà, ó ṣìnà fún ẹ̀mí ara rẹ̀. Ìwọ sì kọ́ ni olùṣọ́ lórí wọn.');
INSERT INTO "aykyuni" VALUES(39,42,'39:42','Allāhu l’Ó ń gba àwọn ẹ̀mí ní àkókò ikú wọn àti (àwọn ẹ̀mí) tí kò kú sójú oorun wọn. Ó ń mú (àwọn ẹ̀mí) tí Ó ti pèbùbù ikú lé lórí mọ́lẹ̀. Ó sì ń fi àwọn yòókù sílẹ̀ títí di gbèdéke àkókò kan. Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ tó ní àròjinlẹ̀.');
INSERT INTO "aykyuni" VALUES(39,43,'39:43','Tàbí wọ́n mú àwọn olùṣìpẹ̀ mìíràn lẹ́yìn Allāhu ni? Sọ pé: “(Wọ́n mú wọn ní olùṣìpẹ̀) tó sì jẹ́ pé wọn kò ní agbára kan kan, wọn kò sì níí làákàyè!”');
INSERT INTO "aykyuni" VALUES(39,44,'39:44','Sọ pé: “Ti Allāhu ni gbogbo ìṣìpẹ̀ pátápátá. TiRẹ̀ ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Lẹ́yìn náà, ọ̀dọ̀ Rẹ̀ ni wọn yóò da yín padà sí.”');
INSERT INTO "aykyuni" VALUES(39,45,'39:45','Nígbà tí wọ́n bá dárúkọ Allāhu nìkan ṣoṣo, ọkàn àwọn tí kò gbàgbọ́ nínú Ọjọ́ Ìkẹ́yìn máa sá kúrò (níbi mímú Allāhu ní ọ̀kan ṣoṣo). Nígbà tí wọ́n bá sì dárúkọ àwọn mìíràn (tí wọ́n ń jọ́sìn fún) lẹ́yìn Rẹ̀, nígbà náà ni wọn yóò máa dunnú.');
INSERT INTO "aykyuni" VALUES(39,46,'39:46','Sọ pé: “Allāhu, Olúpilẹ̀dá àwọn sánmọ̀ àti ilẹ̀, Onímọ̀-ìkọ̀kọ̀ àti gban̄gba, Ìwọ l’O máa dájọ́ láààrin àwọn ẹrúsìn Rẹ nípa ohun tí wọ́n ń yapa-ẹnu lórí rẹ̀.”');
INSERT INTO "aykyuni" VALUES(39,47,'39:47','Tí ó bá jẹ́ pé dájúdájú gbogbo n̄ǹkan tí ó wà lórí ilẹ̀ jẹ́ ti àwọn alábòsí àti irú rẹ̀ mìíràn pẹ̀lú rẹ̀ (tún jẹ́ tiwọn ni), wọn ìbá fi ṣèràpadà (fún ẹ̀mí ara wọn) níbi aburú ìyà ní Ọjọ́ Àjíǹde. (Nígbà yẹn) ohun tí wọn kò lérò máa hàn sí wọn ní ọ̀dọ̀ Allāhu;');
INSERT INTO "aykyuni" VALUES(39,48,'39:48','Àwọn aburú ohun tí wọ́n ṣe níṣẹ́ máa hàn sí wọn. Àti pé ohun tí wọ́n ń fi ṣe yẹ̀yẹ́ sì máa dìyà tó máa yí wọn po.');
INSERT INTO "aykyuni" VALUES(39,49,'39:49','Nígbà tí ìnira kan ba fọwọ́ ba ènìyàn, ó máa pè Wá. Lẹ́yìn náà, nígbà tí A bá fún un ní ìdẹ̀ra kan láti ọ̀dọ̀ Wa, ó máa wí pé: “Wọ́n fún mi pẹ̀lú ìmọ̀ ni.”Kò sì rí bẹ́ẹ̀, àdánwò ni, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò mọ̀. ');
INSERT INTO "aykyuni" VALUES(39,50,'39:50','Àwọn tó ṣíwájú wọn kúkú wí (irú) rẹ̀, ṣùgbọ́n ohun tí wọ́n ń ṣe níṣẹ́ kò rọ̀ wọ́n lọ́rọ̀.');
INSERT INTO "aykyuni" VALUES(39,51,'39:51','Aburú ohun tí wọ́n ṣe níṣẹ́ sì bá wọn. Àwọn alábòsí nínú àwọn wọ̀nyí náà, aburú ohun tí wọ́n ṣe níṣẹ́ yóò bá wọn. Wọn kò sì níí mórí bọ́.');
INSERT INTO "aykyuni" VALUES(39,52,'39:52','Ṣé wọn kò mọ̀ pé dájúdájú Allāhu l’Ó ń tẹ́ arísìkí sílẹ̀ fún ẹni tí Ó bá fẹ́. Ó sì ń díwọ̀n rẹ̀ (fún ẹlòmíìràn). Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(39,53,'39:53','Sọ pé: “Ẹ̀yin ẹrúsìn Mi, tí ẹ ti dẹ́ṣẹ̀ púpọ̀ sí ẹ̀mí ara yín lọ́rùn, ẹ má ṣe sọ̀rètí nù nípa ìkẹ́ Allāhu. Dájúdájú Allāhu l’Ó ń ṣàforíjìn gbogbo ẹ̀ṣẹ̀ pátápátá. Dájúdájú Allāhu, Òun ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(39,54,'39:54','Ẹ ṣẹ́rí padà (ní ti ìronúpìwàdà) sí ọ̀dọ̀ Olúwa yín. Kí ẹ sì juwọ́-jusẹ̀ sílẹ̀ fún Un ṣíwájú kí ìyà náà tó wá ba yín. (Bí bẹ́ẹ̀ kọ́) lẹ́yìn náà, A kò níí ràn yín lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(39,55,'39:55','Kí ẹ sì tẹ̀lé ohun tí ó dára jùlọ tí wọ́n sọ̀kalẹ̀ fún yín láti ọ̀dọ̀ Olúwa yín ṣíwájú kí ìyà náà tó wá ba yín ní òjijì, nígbà tí ẹ̀yin kò níí fura.”');
INSERT INTO "aykyuni" VALUES(39,56,'39:56','Nítorí kí ẹ̀mí kan má baà wí pé: “Mo ká àbámọ̀ lórí bí mo ṣe jáfara lórí àìtẹ̀lé àṣẹ Allāhu. Àti pé èmi wà nínú àwọn tó ń fi (ọ̀rọ̀ Rẹ̀) ṣe yẹ̀yẹ́.”');
INSERT INTO "aykyuni" VALUES(39,57,'39:57','Tàbí kí ó má baà wí pé: “Tí ó bá jẹ́ pé Allāhu fi ọ̀nà mọ̀ mí ni, dájúdájú èmi ìbá wà nínú àwọn olùbẹ̀rù (Rẹ̀).”');
INSERT INTO "aykyuni" VALUES(39,58,'39:58','Tàbí nígbà tí ó bá rí Iná kí ó má baà wí pé: “Tí ó bá jẹ́ pé ìpadàsáyé wà fún mi ni, èmi ìbá sì wà nínú àwọn olùṣe-rere.”');
INSERT INTO "aykyuni" VALUES(39,59,'39:59','Bẹ́ẹ̀ ni! Àwọn āyah Mi kúkú ti dé bá ọ. O pè é ní irọ́. O tún ṣègbéraga. O sì wà nínú àwọn aláìgbàgbọ́. 1 ');
INSERT INTO "aykyuni" VALUES(39,60,'39:60','Àti pé ní Ọjọ́ Àjíǹde, o máa rí àwọn tó parọ́ mọ́ Allāhu tí ojú wọn máa dúdú. Ṣé inú iná Jahnamọ kọ́ ni ibùgbé fún àwọn onígbèéraga ni?');
INSERT INTO "aykyuni" VALUES(39,61,'39:61','Allāhu yóò gba àwọn tó bẹ̀rù (Rẹ̀) là sínú ilé ìgbàlà wọn (ìyẹn, Ọgbà Ìdẹ̀ra). Aburú náà kò níí fọwọ́ bà wọ́n. Wọn kò sì níí banújẹ́.');
INSERT INTO "aykyuni" VALUES(39,62,'39:62','Allāhu ni Ẹlẹ́dàá gbogbo n̄ǹkan. Òun sì ni Olùṣọ́ lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(39,63,'39:63','TiRẹ̀ ni àwọn kọ́kọ́rọ́ ilé-ọ̀rọ̀ àwọn sánmọ̀ àti ilẹ̀. Àwọn tó ṣàì gbàgbọ́ nínú àwọn āyah Allāhu, àwọn wọ̀nyẹn, àwọn ni ẹni òfò.');
INSERT INTO "aykyuni" VALUES(39,64,'39:64','Sọ pé: “Ṣé n̄ǹkan mìíràn yàtọ̀ sí Allāhu l’ẹ̀ ń pa mí lásẹ pé kí n̄g máa jọ́sìn fún, ẹ̀yin aláìmọ̀kan?”');
INSERT INTO "aykyuni" VALUES(39,65,'39:65','Dájúdájú A ti fi ìmísí ránṣẹ́ sí ìwọ àti àwọn tó ṣíwájú rẹ (pé) “Dájúdájú tí o bá ṣẹbọ, iṣẹ́ rẹ máa bàjẹ́. Dájúdájú o sì máa wà nínú àwọn ẹni òfò.”');
INSERT INTO "aykyuni" VALUES(39,66,'39:66','Rárá (má ṣẹbọ). Allāhu nìkan ni kí o jọ́sìn fún. Kí o sì wà nínú àwọn olùdúpẹ́ (fún Un).');
INSERT INTO "aykyuni" VALUES(39,67,'39:67','Wọn kò bu iyì fún Allāhu bí ó ṣe tọ́ láti bu iyì fún Un. Gbogbo ilẹ̀ pátápátá sì ni (Allāhu) máa fọwọ́ ara Rẹ̀ gbámú ní Ọjọ́ Àjíǹde. Ó sì máa fi ọwọ́ ọ̀tún Rẹ̀ ká àwọn sánmọ̀ kóróbójó. Mímọ́ ni fún Un. Ó sì ga tayọ n̄ǹkan tí wọ́n ń fi ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(39,68,'39:68','Wọ́n á fọn fèrè oníwo (fún ikú). Àwọn tó wà nínú àwọn sánmọ̀ àti ilẹ̀ sì máa kú àfi ẹni tí Allāhu bá fẹ́. Lẹ́yìn náà, wọ́n máa fọn ọ́n ní ẹ̀ẹ̀ kejì, nígbà náà wọ́n máa wà ní ìdìde. Wọn yó sì máa wò sùn.');
INSERT INTO "aykyuni" VALUES(39,69,'39:69','Àti pé ilẹ̀ náà yóò máa tàn yànrànyànràn pẹ̀lú ìmọ́lẹ̀ Olúwa rẹ̀. Wọ́n máa gbé ìwé iṣẹ́ ẹ̀dá lélẹ̀. Wọ́n sì máa mú àwọn Ànábì àti àwọn ẹlẹ́rìí wá. A máa fi òdodo ṣèdájọ́ láààrin wọn. A kò sì níí ṣàbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(39,70,'39:70','Àti pé Wọ́n máa san ẹ̀mí kọ̀ọ̀kan ní ẹ̀san ohun tí ó ṣe níṣẹ́. Allāhu sì nímọ̀ jùlọ nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(39,71,'39:71','Wọn yó sì da àwọn tó ṣàì gbàgbọ́ lọ sínú iná Jahanamọ níjọníjọ, títí di ìgbà tí wọ́n bá dé ibẹ̀, wọ́n máa ṣí àwọn ìlẹ̀kùn rẹ̀ sílẹ̀ (fún wọn). Àwọn ẹ̀ṣọ́ rẹ̀ yó sì sọ fún wọn pé: “Ǹjẹ́ àwọn Òjíṣẹ́ kan kò wá ba yín láti ààrin ara yín, tí wọ́n ń ké àwọn āyah Olúwa yín fún yín, tí wọ́n sì ń kìlọ̀ ìpàdé ọjọ́ yín òní yìí fún yín?” Wọ́n wí pé: “Bẹ́ẹ̀ ni (wọ́n wá bá wa).” Ṣùgbọ́n ọ̀rọ̀ ìyà kò lé àwọn aláìgbàgbọ́ lórí ni.');
INSERT INTO "aykyuni" VALUES(39,72,'39:72','A óò sọ pé: “Ẹ wọ àwọn ẹnu ọ̀nà iná Jahanamọ. Olùṣegbére (ni yín) nínú rẹ̀. Ibùgbé àwọn onígbèéraga sì burú.');
INSERT INTO "aykyuni" VALUES(39,73,'39:73','Àti pé A óò kó àwọn tó bẹ̀rù Olúwa wọn lọ sínú Ọgbà Ìdẹ̀ra níjọníjọ, títí di ìgbà tí wọ́n bá dé ibẹ̀, wọ́n máa ṣí àwọn ìlẹ̀kùn rẹ̀ sílẹ̀ (fún wọn). Àwọn ẹ̀ṣọ́ rẹ̀ yó sì sọ fún wọn pé: “Kí àlàáfíà máa ba yín. Ẹ̀yin dára (nítorí iṣẹ́ yín tó dára). Nítorí náà, ẹ wọ inú Ọgbà Ìdẹ̀ra, (kí ẹ di) olùṣegbére(nínú rẹ̀).”1 ');
INSERT INTO "aykyuni" VALUES(39,74,'39:74','Wọ́n á sọ pé: “Gbogbo ẹyìn ń jẹ́ ti Allāhu, Ẹni tí Ó mú àdéhùn Rẹ̀ ṣẹ fún wa. Ó tún jogún ilẹ̀ náà fún wa, tí à ń gbé níbikíbi tí a bá fẹ́ nínú Ọgbà Ìdẹ̀ra.” Ẹ̀san àwọn olùṣe-rere mà sì dára.');
INSERT INTO "aykyuni" VALUES(39,75,'39:75','O sì máa rí àwọn Mọlāika tí wọ́n ń rọkiriká ní àyíká Ìtẹ́-ọlá. Wọ́n ń ṣe àfọ̀mọ́ àti ìdúpẹ́ fún Olúwa wọn. A máa fi òdodo ṣèdájọ́ láààrin àwọn ẹ̀dá. Wọ́n sì máa sọ pé: “Gbogbo ẹyìn ń jẹ́ ti Allāhu, Olúwa gbogbo ẹ̀dá.”');
INSERT INTO "aykyuni" VALUES(40,1,'40:1','Hā mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(40,2,'40:2','Tírà náà sọ̀kalẹ̀ díẹ̀ díẹ̀ láti ọ̀dọ̀ Allāhu, Alágbára, Onímọ̀,');
INSERT INTO "aykyuni" VALUES(40,3,'40:3','Aláforíjìn-ẹ̀ṣẹ̀, Olùgba-ìronúpìwàdà, Ẹni líle níbi ìyà, Ọlọ́rẹ, kò sí ọlọ́hun kan tí a gbọ́dọ̀ máa jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Ọ̀dọ̀ Rẹ̀ ni àbọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(40,4,'40:4','Kò sí ẹni tí ó máa ṣe àtakò sí àwọn āyah Allāhu àfi àwọn tó ṣàì gbàgbọ́. Nítorí náà, má ṣe jẹ́ kí ìgbòkègbodò wọn nínú ìlú kó ẹ̀tàn bá ọ.');
INSERT INTO "aykyuni" VALUES(40,5,'40:5','Ìjọ (Ànábì) Nūh pe òdodo ní irọ́ ṣíwájú wọn. Àwọn ìjọ (mìíràn) lẹ́yìn wọn (náà ṣe bẹ́ẹ̀). Ìjọ kọ̀ọ̀kan ló gbèrò láti ki Òjíṣẹ́ wọn mọ́lẹ̀. Wọ́n fi irọ́ ja òdodo níyàn nítorí kí wọ́n lè fi wó òdodo lulẹ̀. Mo sì gbá wọn mú. Nítorí náà, báwo ni ìyà (tí Mo fi jẹ wọ́n) ti rí (lára wọn ná)!');
INSERT INTO "aykyuni" VALUES(40,6,'40:6','Àti pé báyẹn ni ọ̀rọ̀ Olúwa rẹ ṣe kò lórí àwọn tó ṣàì gbàgbọ́ pé: “Dájúdájú àwọn ni èrò inú Iná.”');
INSERT INTO "aykyuni" VALUES(40,7,'40:7','Àwọn tó gbé Ìtẹ́-ọlá náà rù àti àwọn tó wà ní àyíká rẹ̀, wọ́n ń ṣe àfọ̀mọ́ pẹ̀lú ìdúpẹ́ fún Olúwa wọn. Wọ́n ní ìgbàgbọ́ òdodo nínú Allāhu. Wọ́n sì ń tọrọ àforíjìn fún àwọn tó gbàgbọ́ ní òdodo (báyìí pé): “Olúwa wa, ìkẹ́ àti ìmọ̀ (Rẹ) gbòòrò ju gbogbo n̄ǹkan lọ, nítorí náà, ṣàforíjìn fún àwọn tó ronú pìwàdà, tí wọ́n sì tẹ̀lé ojú-ọ̀nà Rẹ. Kí O sì ṣọ́ wọn nínú ìyà iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(40,8,'40:8','Olúwa wa, fi wọ́n sínú àwọn Ọgbà Ìdẹ̀ra gbére èyí tí Ó ṣe ní àdéhùn fún àwọn àti ẹni tó ṣe iṣẹ́ rere nínú àwọn bàbá wọn, àwọn aya wọn àti àwọn àrọ́mọdọ́mọ wọn. Dájúdájú Ìwọ, Ìwọ ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(40,9,'40:9','Ṣọ́ wọn nínú àwọn aburú. Ẹnikẹ́ni tí O bá ṣọ́ níbi àwọn aburú (ìyà) ní ọjọ́ yẹn, dájúdájú O ti kẹ́ ẹ. Ìyẹn sì ni èrèǹjẹ ńlá.”');
INSERT INTO "aykyuni" VALUES(40,10,'40:10','Dájúdájú àwọn tó ṣàì gbàgbọ́, wọn yóò pè wọ́n (láti sọ fún wọn nínú Iná pé): “Dájúdájú ìbínú ti Allāhu tóbi ju ìbínú tí ẹ̀ ń bí síra yín (nínú Iná, ṣebí) nígbà tí wọ́n ń pè yín síbi ìgbàgbọ́ òdodo, ńṣe ni ẹ̀ ń ṣàì gbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(40,11,'40:11','Wọ́n wí pé: “Olúwa wa, O pa wá ní ẹ̀ẹ̀ méjì. O sì ji wá ní ẹ̀ẹ̀ méjì.Nítorí náà, a jẹ́wọ́ àwọn ẹ̀ṣẹ̀ wa. Ǹjẹ́ ọ̀nà kan wà láti jáde (padà sí ilé ayé) bí?” ');
INSERT INTO "aykyuni" VALUES(40,12,'40:12','(Ẹ wà nínú Iná) yẹn nítorí pé dájúdájú nígbà tí wọ́n bá pe Allāhu nìkan ṣoṣo, ẹ̀yin ṣàì gbàgbọ́. Nígbà tí wọ́n bá ṣẹbọ sí I, ẹ sì máa gbàgbọ́ (nínú ẹbọ). Nítorí náà, ìdájọ́ ń jẹ́ ti Allāhu, Ó ga, Ó tóbi.');
INSERT INTO "aykyuni" VALUES(40,13,'40:13','Òun ni Ẹni tó ń fi àwọn àmì Rẹ̀ hàn yín. Ó sì ń sọ arísìkí kalẹ̀ fún yín láti sánmọ̀. Kò sí ẹni tó ń lo ìrántí àfi ẹni tó ń ṣẹ́rí padà sí ọ̀dọ̀ Allāhu (nípasẹ̀ ìronúpìwàdà).');
INSERT INTO "aykyuni" VALUES(40,14,'40:14','Nítorí náà, ẹ pe Allāhu lẹ́ni tí yóò máa fi àfọ̀mọ́-ọkàn (àníyàn mímọ́) ṣe ẹ̀sìn fún Un.àwọn aláìgbàgbọ́ ìbáà kórira Rẹ̀. ');
INSERT INTO "aykyuni" VALUES(40,15,'40:15','(Allāhu) Ẹni tó ni gbogbo ipò àjùlọ, Olúwa Ìtẹ́-ọlá, Ó ń fi ìmísí mímọ́ ránṣẹ́ pẹ̀lú àṣẹ Rẹ̀ sí ẹni tí Ó bá fẹ́ nínú àwọn ẹrúsìn Rẹ̀nítorì kí ó lè fi ṣèkìlọ̀ nípa Ọjọ́ ìpàdé náà. ');
INSERT INTO "aykyuni" VALUES(40,16,'40:16','Ní ọjọ́ tí wọn yóò yọ jáde (láti inú sàréè), kiní kan kò sì níí pamọ́ nípa wọn fún Allāhu. (Allāhu yó sì sọ pé): “Ti ta ni ìjọba ní ọjọ́ òní?” Ti Allāhu, Ọ̀kan ṣoṣo, Olùborí ni.');
INSERT INTO "aykyuni" VALUES(40,17,'40:17','Ní òní ni A óò san ẹ̀mí kọ̀ọ̀kan ní ẹ̀san ohun tí ó ṣe níṣẹ́. Kò sì sí àbòsí kan ní òní. Dájúdájú Allāhu ni Olùyára níbi ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(40,18,'40:18','Àti pé ṣèkìlọ̀ fún wọn nípa ọjọ́ tí ó súnmọ́ (ìyẹn Ọjọ́ Àjíǹde). Nígbà tí àwọn ọkàn bá ga dé gògòńgò, tí ó máa kún fún ìbànújẹ́, kò níí sí ọ̀rẹ́ ìmùlẹ̀ àti olùṣìpẹ̀ kan tí wọ́n máa tẹ̀lé ọ̀rọ̀ rẹ̀ lórí (ọ̀rọ̀) àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(40,19,'40:19','(Allāhu) mọ ojú ìjàǹbá àti ohun tí àwọn ọkàn fi pamọ́.1 ');
INSERT INTO "aykyuni" VALUES(40,20,'40:20','Àti pé Allāhu l’Ó máa fi òdodo dájọ́. Àwọn tí wọ́n ń pè lẹ́yìn Rẹ̀, wọn kò lè ṣe ìdájọ́ kan kan. Dájúdájú Allāhu, Òun ni Olùgbọ́, Olùríran.');
INSERT INTO "aykyuni" VALUES(40,21,'40:21','Ṣé wọn kò rìn kiri lórí ilẹ̀ kí wọ́n wo bí ìkángun àwọn tó ṣíwájú wọn ṣe rí? Wọ́n ní agbára jù wọ́n lọ. Wọ́n sì lo ilẹ̀ (fún oko dídá jù wọ́n lọ). Síbẹ̀síbẹ̀ Allāhu mú wọn nítorí ẹ̀ṣẹ̀ wọn. Kò sì sí olùṣọ́ kan fún wọn (níbi ìyà) Allāhu.');
INSERT INTO "aykyuni" VALUES(40,22,'40:22','Ìyẹn nítorí pé, àwọn Òjíṣẹ́ wọn ń wá bá wọn pẹ̀lú àwọn ẹ̀rí tó yanjú, ṣùgbọ́n wọ́n ṣàì gbàgbọ́. Nítorí náà, Allāhu mú wọn. Dájúdájú Òun ni Alágbára, Ẹni líle níbi ìyà.');
INSERT INTO "aykyuni" VALUES(40,23,'40:23','Dájúdájú A fi àwọn àmì Wa àti ẹ̀rí tó yanjú rán (Ànábì) Mūsā níṣẹ́');
INSERT INTO "aykyuni" VALUES(40,24,'40:24','sí Fir‘aon àti Hāmọ̄n àti Ƙọ̄rūn. Ṣùgbọ́n wọ́n wí pé: “Òpìdán, òpùrọ́ ni (Ànábì Mūsā).”');
INSERT INTO "aykyuni" VALUES(40,25,'40:25','Nígbà tí ó mú òdodo náà dé ọ̀dọ̀ wọn láti ọ̀dọ̀ Wa, wọ́n wí pé: “Ẹ pa àwọn ọmọkùnrin àwọn tó gbàgbọ́ ní òdodo nínú Allāhu pẹ̀lú rẹ̀, kí ẹ sì fi àwọn ọmọbìnrin wọn sílẹ̀.” Ète àwọn aláìgbàgbọ́ kò sí nínú kiní kan bí kò ṣe nínú ìṣìnà.');
INSERT INTO "aykyuni" VALUES(40,26,'40:26','Fir‘aon wí pé: “Ẹ fi mí sílẹ̀ kí n̄g pa Mūsā, kí ó sì pe Olúwa rẹ̀ (wò bóyá Ó máa gbà á sílẹ̀). Dájúdájú èmi ń bẹ̀rù pé ó máa yí ẹ̀sìn yín padà tàbí pé ó máa ṣàfi hàn ìbàjẹ́ lórí ilẹ̀.”');
INSERT INTO "aykyuni" VALUES(40,27,'40:27','(Ànábì) Mūsā sọ pé: “Dájúdájú èmi sá di Olúwa mi àti Olúwa yín kúrò lọ́wọ́ gbogbo onígbèéraga, tí kò ní ìgbàgbọ́ òdodo nínú Ọjọ́ ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(40,28,'40:28','Ọkùnrin onígbàgbọ́ òdodo kan tó ń fi ìgbàgbọ́ rẹ̀ pamọ́ nínú àwọn ènìyàn Fir‘aon sọ pé: “Ṣé ẹ máa pa ọkùnrin kan nítorí pé ó ń sọ pé, “Allāhu ni Olúwa mi.” Ó sì kúkú ti mú àwọn ẹ̀rí tó yanjú wá ba yín láti ọ̀dọ̀ Olúwa yín. Tí ó bá jẹ́ òpùrọ́, (ìyà) irọ́ rẹ̀ wà lórí rẹ̀. Tí ó bá sì jẹ́ olódodo, apá kan èyí tí ó ṣe ní ìlérí fún yín yó sì kò le yín lórí. Dájúdájú Allāhu kò níí fi ọ̀nà mọ ẹni tí ó jẹ́ aláṣejù, òpùrọ́.');
INSERT INTO "aykyuni" VALUES(40,29,'40:29','Ẹ̀yin ènìyàn mi, ẹ̀yin l’ẹ ni ìjọba lónìí, tí ẹ ga jùlọ lórí ilẹ̀ náà (ìyẹn ilẹ̀ Misrọ). Ṣùgbọ́n ta ni ó máa ràn wá lọ́wọ́ tí ìyà Allāhu bá dé bá wa?” Fir‘aon wí pé: “Èmi kò fi n̄ǹkan kan hàn yín bí kò ṣe ohun tí mo rí (nínú òye mi). Èmi kò sì tọ yín sí ọ̀nà kan bí kò ṣe ojú ọ̀nà ìmọ̀nà.”');
INSERT INTO "aykyuni" VALUES(40,30,'40:30','Ẹni tó gbàgbọ́ ní òdodo tún sọ pé: “Ẹ̀yin ènìyàn mi, dájúdájú irú ọjọ́ (ẹ̀san ìyà tó ṣẹlẹ̀ sí) àwọn ọmọ-ogun oníjọ ni èmi ń páyà lórí yín;');
INSERT INTO "aykyuni" VALUES(40,31,'40:31','Irú ìṣe (ìparun tó ṣẹlẹ̀ sí àwọn) ènìyàn Nūh, ‘Ād, Thamūd àti àwọn tó tẹ̀lé wọn. Allāhu kò sì gbèrò àbòsí sí àwọn ẹrúsìn náà.');
INSERT INTO "aykyuni" VALUES(40,32,'40:32','Ẹ̀yin ènìyàn mi, dájúdájú èmi ń páyà Ọjọ́ ìpè fún yín.1 ');
INSERT INTO "aykyuni" VALUES(40,33,'40:33','Ọjọ́ tí ẹ máa pẹ̀yìndà láti sá lọ; kò sì níí sí aláàbò kan fún yín lọ́dọ̀ Allāhu. Àti pé ẹni tí Allāhu bá ṣì lọ́nà, kò lè sí afinimọ̀nà kan fún un.');
INSERT INTO "aykyuni" VALUES(40,34,'40:34','Àti pé dájúdájú (Ànábì) Yūsuf ti tọ̀ yín wá ṣíwájú pẹ̀lú àwọn àlàyé ọ̀rọ̀ tó yanjú, ṣùgbọ́n ẹ̀yin kò yé wà nínú iyèméjì nípa ohun tí ó mú wá ba yín títí di ìgbà tí ó fi kú, tí ẹ fi wí pé: “Allāhu kò níí gbé Òjíṣẹ́ kan dìde mọ́ lẹ́yìn rẹ̀. Báyẹn ni Allāhu ṣe ń sọ ẹni tí ó jẹ́ aláṣejù, oníyèméjì nù.”');
INSERT INTO "aykyuni" VALUES(40,35,'40:35','Àwọn tó ń jiyàn nípa àwọn āyah Allāhu láìsí ẹ̀rí kan tí ó dé bá wọn. Ẹ̀ṣẹ̀ ńlá sì ni ní ọ̀dọ̀ Allāhu àti ní ọ̀dọ̀ àwọn tó gbàgbọ́ ní òdodo. Báyẹn ni Allāhu ṣe ń fi èdídí bo gbogbo ọkàn onígbèéraga, ajẹninípá.”');
INSERT INTO "aykyuni" VALUES(40,36,'40:36','Fir‘aon wí pé: “Hāmọ̄n, mọ ilé gíga fíofío kan fún mi nítorí kí èmi lè dé àwọn ojú ọ̀nà náà.');
INSERT INTO "aykyuni" VALUES(40,37,'40:37','Àwọn ojú ọ̀nà (inú) sánmọ̀ ni, nítorí kí èmi lè yọjú wo Ọlọ́hun Mūsā nítorí pé, dájúdájú èmi ń rò ó sí òpùrọ́.” Báyẹn ni wọ́n ṣe iṣẹ́ aburú (ọwọ́) Fir‘aon ní ọ̀ṣọ́ fún un. Wọ́n sì ṣẹ́rí rẹ̀ kúrò lójú ọ̀nà (ẹ̀sìn Allāhu). Ète Fir‘aon kò sì wà nínú kiní kan bí kò ṣe nínú òfò.');
INSERT INTO "aykyuni" VALUES(40,38,'40:38','Ẹni tó gbàgbọ́ ní òdodo tún sọ pé: “Ẹ̀yin ènìyàn mi, ẹ tẹ̀lé mi, mo máa júwe yín sí ojú ọ̀nà ìmọ̀nà.');
INSERT INTO "aykyuni" VALUES(40,39,'40:39','Ẹ̀yin ènìyàn mi, ìgbádùn lásán ni ìṣẹ̀mí ilé ayé yìí. Dájúdájú ọ̀run sì ni ilé gbére.');
INSERT INTO "aykyuni" VALUES(40,40,'40:40','Ẹni tí ó bá ṣe aburú kan, Wọn kò níí san án ní ẹ̀san kan àyàfi irú rẹ̀. Ẹni tí ó bá sì ṣe iṣẹ́ rere ní ọkùnrin tàbí ní obìnrin, tí ó sì jẹ́ onígbàgbọ́ òdodo,àwọn wọ̀nyẹn ni wọ́n máa wọ inú Ọgbà Ìdẹ̀ra. Wọn yóò máa pèsè ìjẹ-ìmu àti ìgbádùn fún wọn nínú rẹ̀ láì níí ní ìṣírò.2 ');
INSERT INTO "aykyuni" VALUES(40,41,'40:41','Ẹ̀yin ènìyàn mi, kí ló mú yín tí mò ń pè yín síbi ìgbàlà, tí ẹ̀yin sì ń pè mí síbi Iná?');
INSERT INTO "aykyuni" VALUES(40,42,'40:42','Ẹ̀yin ń pè mí pé kí n̄g ṣàì gbàgbọ́ nínú Allāhu, kí n̄g sì sọ n̄ǹkan tí èmi kò nímọ̀ nípa rẹ̀ di akẹgbẹ́ fún Un.Èmi sì ń pè yín sí ọ̀dọ̀ Alágbára, Aláforíjìn. ');
INSERT INTO "aykyuni" VALUES(40,43,'40:43','Kò sí tàbí-ṣùgbọ́n, dájúdájú n̄ǹkan tí ẹ̀ ń pè mí sí, kò ní ẹ̀tọ́ sí ìpè kan ní ayé àti ní ọ̀run. Àti pé dájúdájú ọ̀dọ̀ Allāhu ni àbọ̀ wa. Dájúdájú àwọn olùtayọ-ẹnu-ààlà, àwọn ni èrò inú Iná.');
INSERT INTO "aykyuni" VALUES(40,44,'40:44','Nítorí náà, ẹ máa rántí ohun tí mò ń sọ fún yín. Mo sì ń fi ọ̀rọ̀ mi tì sí ọ̀dọ̀ Allāhu. Dájúdájú Allāhu ni Olùríran nípa àwọn ẹrúsìn.”');
INSERT INTO "aykyuni" VALUES(40,45,'40:45','Allāhu sì ṣọ́ ọ níbi àwọn aburú tí wọ́n dète rẹ̀. Ìyà burúkú sì yí àwọn ènìyàn Fir‘aon po.');
INSERT INTO "aykyuni" VALUES(40,46,'40:46','Iná ni Wọ́n yóò máà ṣẹ́rí wọn sí ní òwúrọ̀ àti ní ìrọ̀lẹ́. Àti pé ní ọjọ́ tí Àkókò náà bá dé (A máa sọ pé): “Ẹ mú àwọn ènìyàn Fir‘aon wọ inú ìyà Iná tó le jùlọ.”');
INSERT INTO "aykyuni" VALUES(40,47,'40:47','(Rántí) nígbà tí wọ́n bá ń bá ara wọn ṣe àríyànjiyàn nínú Iná. Àwọn ọ̀lẹ yóò wí fún àwọn tí wọ́n ṣègbéraga pé: “Dájúdájú àwa jẹ́ ọmọlẹ́yìn fún yín, ǹjẹ́ ẹ̀yin lè gbé ìpín kan kúrò fún wa nínú ìyà Iná?”');
INSERT INTO "aykyuni" VALUES(40,48,'40:48','Àwọn tó ṣègbéraga máa wí pé: “Dájúdájú gbogbo wa l’a wà nínú rẹ̀. Dájúdájú Allāhu kúkú ti dájọ́ láààrin àwọn ẹrú náà.”');
INSERT INTO "aykyuni" VALUES(40,49,'40:49','Àwọn tó wà nínú Iná tún máa wí fún àwọn ẹ̀ṣọ́-Iná pé: “Ẹ bá wa pe Olúwa yín, kí Ó ṣe ìyà ní fífúyẹ́ fún wa fún ọjọ́ kan.”');
INSERT INTO "aykyuni" VALUES(40,50,'40:50','Wọ́n yóò sọ pé: “Ǹjẹ́ àwọn Òjísẹ́ yín kì í mú àwọn ẹ̀rí tó yanjú wá ba yín bí?” Wọ́n á wí pé: “Bẹ́ẹ̀ ni, (wọ́n mú un wá).” Wọ́n máa sọ pé: “Ẹ ṣàdúà wò.” Àdúà (àti ìpè) àwọn aláìgbàgbọ́ kò sì jẹ́ kiní kan bí kò ṣe sínú ìṣìnà.1 ');
INSERT INTO "aykyuni" VALUES(40,51,'40:51','Dájúdájú Àwa kúkú máa ṣàrànṣe fún àwọn Òjíṣẹ́ Wa àti àwọn tó gbàgbọ́ ní òdodo nínú ìgbésí ayé yìí àti ní ọjọ́ tí àwọn ẹlẹ́rìí yó dìde (ní Ọjọ́ Àjíǹde).');
INSERT INTO "aykyuni" VALUES(40,52,'40:52','Ní ọjọ́ tí àwáwí àwọn alábòsí kò níí ṣe wọ́n ní àǹfààní; ègún ń bẹ fún wọn, ilé (ìyà) burúkú sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(40,53,'40:53','Dájúdájú A fún (Ànábì) Mūsā ní ìmọ̀nà. A sì jogún Tírà fún àwọn ọmọ ’Isrọ̄’īl.');
INSERT INTO "aykyuni" VALUES(40,54,'40:54','(Ó jẹ́) ìmọ̀nà àti ìrántí fún àwọn onílàákàyè.');
INSERT INTO "aykyuni" VALUES(40,55,'40:55','Nítorí náà, ṣe sùúrù. Dájúdájú àdéhùn Allāhu ni òdodo. Tọrọ àforíjìn fún ẹ̀ṣẹ̀ rẹ.Kí o sì ṣe àfọ̀mọ́ pẹ̀lú ẹyìn fún Olúwa rẹ2 ní ìrọ̀lẹ́ àti ní òwúrọ̀ kùtùkùtù. ');
INSERT INTO "aykyuni" VALUES(40,56,'40:56','Dájúdájú àwọn tó ń jiyàn nípa àwọn āyah Allāhu láìní ẹrí kan (lọ́wọ́) tí ó wá bá wọn (láti ọ̀dọ̀ Allāhu), kò sí kiní kan nínú igbá-àyà wọn àyàfi ọkàn-gíga. Wọn kò sì lè dé ibi gíga (pẹ̀lú ọkàn gíga). Nítorí náà, sá di Allāhu. Dájúdájú Allāhu, Òun ni Olùgbọ́, Olùríran.');
INSERT INTO "aykyuni" VALUES(40,57,'40:57','Dájúdájú ìṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ tóbi ju ìṣẹ̀dá àwọn ènìyàn; ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn ni kò mọ̀.');
INSERT INTO "aykyuni" VALUES(40,58,'40:58','Afọ́jú àti olùríran kò dọ́gba. Àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere àti oníṣẹ́-aburú (kò dọ́gba). Díẹ̀ l’ẹ̀ ń lò nínú ìrántí.');
INSERT INTO "aykyuni" VALUES(40,59,'40:59','Dájúdájú Àkókò náà ń bọ̀, kò sì sí iyèméjì nínú rẹ̀. Ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn ni kò gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(40,60,'40:60','Olúwa yín sọ pé: “Ẹ pè Mí, kí N̄g jẹ́pè yín. Dájúdájú àwọn tó ń ṣègbéraga nípa jíjọ́sìn fún Mi,wọn yóò wọ inú iná Jahanamọ ní ẹni yẹpẹrẹ.” ');
INSERT INTO "aykyuni" VALUES(40,61,'40:61','Allāhu ni Ẹni tí Ó ṣe òru fún yín nítorí kí ẹ lè sinmi nínú rẹ̀. (Ó sì ṣe) ọ̀sán (nítorí kí ẹ lè fi) ríran. Dájúdájú Allāhu ni Ọlọ́lá-jùlọ lórí àwọn ènìyàn, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ àwọn ènìyàn kì í dúpẹ́ (fún Un).');
INSERT INTO "aykyuni" VALUES(40,62,'40:62','Ìyẹn ni Allāhu, Olúwa yín, Ẹlẹ́dàá gbogbo n̄ǹkan. Kò sí ọlọ́hun kan tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Nítorí náà, báwo ni wọ́n ṣe ń ṣẹ yín lórí kúrò níbi òdodo?');
INSERT INTO "aykyuni" VALUES(40,63,'40:63','Báyẹn náà ni wọ́n ṣe ń ṣẹ́ àwọn tó ń tako àwọn āyah Allāhu lórí kúrò níbi òdodo (ṣíwájú tiwọn).');
INSERT INTO "aykyuni" VALUES(40,64,'40:64','Allāhu ni Ẹni tí Ó ṣe ilẹ̀ fún yín ní ibùgbé. Ó sì ṣe sánmọ̀ ní àjà (le yín lórí). Ó ya àwòrán yín. Ó sì ya àwòrán yín dáradára. Ó pèsè arísìkí fún yín nínú àwọn n̄ǹkan dáadáa. Ìyẹn ni Allāhu, Olúwa yín. Nítorí náà, mímọ́ ni fún Allāhu, Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(40,65,'40:65','Òun ni Alààyè. Kò sí ọlọ́hun kan tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Nítorí náà, ẹ pè É lẹ́ni tí yóò máa fi àfọ̀mọ́-ọkàn (àníyàn mímọ́) ṣe ẹ̀sìn fún Un.Gbogbo ẹyìn ń jẹ́ ti Allāhu, Olúwa gbogbo ẹ̀dá. ');
INSERT INTO "aykyuni" VALUES(40,66,'40:66','Sọ pé: “Dájúdájú Wọ́n kọ̀ fún mi pé kí n̄g jọ́sìn fún àwọn tí ẹ̀ ń pè lẹ́yìn Allāhu, nígbà tí àwọn ẹ̀rí tó yanjú ti dé bá mi láti ọ̀dọ̀ Olúwa mi, tí wọ́n sì pa mí ní àṣẹ pé kí n̄g juwọ́ jusẹ̀ sílẹ̀ (kí n̄g jẹ́ mùsùlùmí) fún Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(40,67,'40:67','Òun ni Ẹni tí Ó ṣẹ̀dá yín láti inú erùpẹ̀, lẹ́yìn náà láti inú àtọ̀, lẹ́yìn náà láti inú ẹ̀jẹ̀ dídì, lẹ́yìn náà Ó mu yín jáde ní òpóǹló. Lẹ́yìn náà, (Ó da yín sí) nítorí kí ẹ lè sánn̄gun dópin agbára yín. Lẹ́yìn náà (Ó tún da yín sí) nítorí kí ẹ lè di àgbàlágbà. Ó wà nínú yín ẹni tí A óò ti gba ẹ̀mí rẹ̀ ṣíwájú (ipò àgbà). Àti pé (Ó da yín sí) nítorí kí ẹ lè dàgbà dé gbèdéke àkókò kan àti nítorí kí ẹ lè ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(40,68,'40:68','Òun ni Ẹni tí ń sọ ẹ̀dá di alààyè. Ó sì ń sọ ẹ̀dá di òkú. Nígbà tí Ó bá sì pèbùbù kiní kan, Ó kàn máa sọ fún un pé: “Jẹ́ bẹ́ẹ̀.” Ó sì máa jẹ́ bẹ́ẹ̀.');
INSERT INTO "aykyuni" VALUES(40,69,'40:69','Ṣé o kò wòye sí àwọn tí ń ṣàríyànjiyàn nípa àwọn āyah Allāhu bí wọ́n ṣe ń ṣẹ́rí wọn kúrò níbi òdodo!');
INSERT INTO "aykyuni" VALUES(40,70,'40:70','Àwọn tó pe Tírà náà àti ohun tí A fi rán àwọn Òjíṣẹ́ Wa níṣẹ́ ní irọ́, láìpẹ́ wọ́n máa mọ̀.');
INSERT INTO "aykyuni" VALUES(40,71,'40:71','Nígbà tí àwọn sẹ́kẹ́sẹkẹ̀ àti ẹ̀wọ̀n bá wà ní ọrùn wọn, tí wọn yóò fi máa wọ́ wọn');
INSERT INTO "aykyuni" VALUES(40,72,'40:72','sínú omi tó gbóná parí. Lẹ́yìn náà, wọn yóò fi wọ́n ko Iná.');
INSERT INTO "aykyuni" VALUES(40,73,'40:73','Lẹ́yìn náà, wọ́n máa sọ fún wọn pé: “Ibo ni ohun tí ẹ sọ di òrìṣà wà?”');
INSERT INTO "aykyuni" VALUES(40,74,'40:74','“(Ohun tí ẹ jọ́sìn fún) lẹ́yìn Allāhu (dà)?” Wọ́n á wí pé: “Wọ́n ti di òfò mọ́ wa lọ́wọ́. Bẹ́ẹ̀ tiẹ̀ kọ́, àwa kì í pe n̄ǹkan kan tẹ́lẹ̀tẹ́lẹ̀.” Báyẹn ni Allāhu ṣe ń ṣi àwọn aláìgbàgbọ́ lọ́nà.');
INSERT INTO "aykyuni" VALUES(40,75,'40:75','Ìyẹn nítorí pé ẹ̀ ń yọ lórí ilẹ̀ ní ọ̀nà àìtọ́ àti nítorí pé, ẹ̀ ń ṣe fáàrí (lórí àìgbàgbọ́).');
INSERT INTO "aykyuni" VALUES(40,76,'40:76','Ẹ wọ àwọn ẹnu ọ̀na iná Jahanamọ; olùṣegbére ni wọ́n nínú rẹ̀. Ó sì burú ní ibùgbé fún àwọn onígbèéraga.');
INSERT INTO "aykyuni" VALUES(40,77,'40:77','Nítorí náà, ṣe sùúrù. Dájúdájú àdéhùn Allāhu ni òdodo. Ó ṣeé ṣe kí A fi apá kan èyí tí A ṣe ní ìlérí fún wọn hàn ọ́ tàbí kí A ti gba ẹ̀mí rẹ (ṣíwájú àsìkò náà). Ọ̀dọ̀ Wa kúkú ni wọ́n máa dá wọn padà sí.');
INSERT INTO "aykyuni" VALUES(40,78,'40:78','Àti pé A kúkú ti rán àwọn Òjísẹ́ níṣẹ́ ṣíwájú rẹ. Àwọn tí A sọ ìtàn wọn fún ọ wà nínú wọn. Àwọn tí A kò sọ ìtàn wọn fún ọ wà nínú wọn. Àti pé kò tọ́ fún Òjísẹ́ kan láti mú àmì kan wá àyàfi pẹ̀lú ìyọ̀ǹda Allāhu. Nígbà tí àṣẹ Allāhu bá sì dé, A máa fi òdodo dájọ́. Àwọn òpùrọ́ sì máa ṣòfò dànù níbẹ̀ yẹn.1 ');
INSERT INTO "aykyuni" VALUES(40,79,'40:79','Allāhu ni Ẹni tí Ó dá àwọn ẹran-ọ̀sìn fún yín nítorí kí ẹ lè máa gùn nínú wọn. Ẹ ó sì máa jẹ nínú wọn.');
INSERT INTO "aykyuni" VALUES(40,80,'40:80','Àwọn àǹfààní (mìíràn) wà fún yín lára ẹran-ọ̀sìn. Àti nítorí kí ẹ lè dé ọ̀nà jíjìn tó wà lọ́kàn yín lórí (gígùn) wọ́n (kiri). Ẹ sì ń fi àwọn (ẹran-ọ̀sìn) àti ọkọ̀ ojú-omi gbé àwọn ẹrù.');
INSERT INTO "aykyuni" VALUES(40,81,'40:81','(Allāhu) ń fi àwọn àmì Rẹ̀ hàn yín. Nítorí náà, èwo nínú àwọn àmì Allāhu l’ẹ máa takò?1 ');
INSERT INTO "aykyuni" VALUES(40,82,'40:82','Ṣé wọn kò rìn kiri lórí ilẹ̀ kí wọ́n wo bí ìkángun àwọn tó ṣíwájú wọn ṣe rí? Wọ́n pọ̀ (ní òǹkà) jù wọ́n lọ. Wọ́n ní agbára jù wọ́n lọ. Wọ́n sì lo ilẹ̀(fún oko dídá jù wọ́n lọ). Síbẹ̀síbẹ̀ ohun tí wọ́n ń ṣe níṣẹ́ kò rọ̀ wọ́n lọ́rọ̀ (níbi ìyà).');
INSERT INTO "aykyuni" VALUES(40,83,'40:83','Nígbà tí àwọn Òjíṣẹ́ wọn mú àwọn ẹ̀rí tó yanjú wá bá wọn, wọ́n yọ àyọ̀pọ̀rọ́ nítorí ohun tó wà lọ́dọ̀ wọn nínú ìmọ̀ (ayé). Ohun tí wọ́n sì ń fi ṣe yẹ̀yẹ́ sì dìyà tó yí wọn po.');
INSERT INTO "aykyuni" VALUES(40,84,'40:84','Nígbà tí wọ́n rí ìyà Wa, wọ́n wí pé: “A gbàgbọ́ nínú Allāhu nìkan ṣoṣo. A sì lòdì sí n̄ǹkan tí a sọ di akẹgbẹ́ fún Un.”');
INSERT INTO "aykyuni" VALUES(40,85,'40:85','Ìgbàgbọ́ wọn kò sì ṣe wọ́n ní àǹfààní nígbà tí wọ́n rí ìyà Wa. Ìṣe Allāhu, èyí tí ó ti ṣẹlẹ̀ ṣíwájú sí àwọn ẹrú Rẹ̀ (ni èyí). Àwọn aláìgbàgbọ́ sì ṣòfò dànù níbẹ̀ yẹn.');
INSERT INTO "aykyuni" VALUES(41,1,'41:1','Hā mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(41,2,'41:2','Ìmísí kan (nìyí) tó sọ̀kalẹ̀ láti ọ̀dọ̀ Àjọkẹ́-ayé, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(41,3,'41:3','Tírà kan ni tí wọ́n ṣàlàyé àwọn āyah inú rẹ̀,(ó ń jẹ́) al-Ƙur’ān (tí wọ́n sọ̀kalẹ̀ ní) èdè Lárúbáwá fún ìjọ tó nímọ̀. ');
INSERT INTO "aykyuni" VALUES(41,4,'41:4','(Ó jẹ́) ìró-ìdùnnú àti ìkìlọ̀, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn gbúnrí kúrò níbẹ̀. Wọn kò sì tẹ́tí sí i.');
INSERT INTO "aykyuni" VALUES(41,5,'41:5','Wọ́n sì wí pé: “Ọkàn wa wà ní títì pa sí ohun tí ẹ̀ ń pè wá sí. Èdídí sì wà nínú etí wa. Àti pé gàgá wà láààrin àwa àti ìwọ. Nítorí náà, máa ṣe tìrẹ. Dájúdájú àwa náà ń ṣe tiwa.”');
INSERT INTO "aykyuni" VALUES(41,6,'41:6','Sọ pé: “Abara bí irú yín kúkú ni èmi náà. Wọ́n ń fi ìmísí ránṣẹ́ sí mi ni, pé Ọlọ́hun yín tí ẹ gbọ́dọ̀ máa jọ́sìn fún ní ọ̀nà òdodo, Ọlọ́hun Ọ̀kan ṣoṣo ni. Nítorí náà, ẹ dúró ṣinṣin tì Í (nínú ìjọ́sìn), kí ẹ sì tọrọ àforíjìn ní ọ̀dọ̀ Rẹ̀. Ègbé sì ni fún àwọn ọ̀ṣẹbọ.');
INSERT INTO "aykyuni" VALUES(41,7,'41:7','Àwọn tí kò yọ Zakāh, àwọn sì ni aláìgbàgbọ́ nínú Ọjọ́ Ìkẹ́yìn.1 ');
INSERT INTO "aykyuni" VALUES(41,8,'41:8','Dájúdájú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, ẹ̀san tí kò níí pẹ̀dín tí kò níí dáwọ́ dúró ń bẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(41,9,'41:9','Sọ pé: “Ṣé dájúdájú ẹ̀yin máa ṣàì gbàgbọ́ nínú Ẹni tí Ó ṣẹ̀dá ilẹ̀ fún ọjọ́ méjì, ẹ sì ń sọ (ẹ̀dá Rẹ̀) di akẹgbẹ́ Rẹ̀ (tí ẹ̀ ń bọ wọ́n)?” Ìyẹn (Ẹlẹ́dàá ilẹ̀) sì ni Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(41,10,'41:10','Ó sì fi àwọn àpáta sínú ilẹ̀ láti òkè rẹ̀. Ó fi ìbùkún sínú rẹ̀. Ó sì pèbùbù àwọn arísìkí (àti ohun àmúsọrọ̀) sínú rẹ̀ láààrin ọjọ́ mẹ́rin.(Àwọn ọjọ́ náà) dọ́gba (síra wọn) fún àwọn olùbèèrè (nípa rẹ̀). ');
INSERT INTO "aykyuni" VALUES(41,11,'41:11','Lẹ́yìn náà, Allāhu wà ní òkè sánmọ̀, nígbà tí sánmọ̀ wà ní èéfín. Ó sì sọ fún òhun àti ilẹ̀ pé: “Ẹ wá bí ẹ fẹ́ tàbí ẹ kọ̀.” Àwọn méjèèjì sọ pé: “A wá pẹ̀lú ìfínnú-fíndọ̀.”1 ');
INSERT INTO "aykyuni" VALUES(41,12,'41:12','Ó sì parí (ìṣẹ̀dá) rẹ̀ sí sánmọ̀ méje fún ọjọ́ méjì. Ó sì fi iṣẹ́ sánmọ̀ kọ̀ọ̀kan ránṣẹ́ sínú rẹ̀. A sì fi àwọn àtùpà (ìràwọ̀) ṣe sánmọ̀ ilé ayé ní ọ̀ṣọ́ àti ààbò (fún un). Ìyẹn ni ètò Alágbára, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(41,13,'41:13','Tí wọ́n bá gbúnrí, sọ pé: “Èmi ń ṣèkìlọ̀ iná láti ojú sánmọ̀ irú iná láti ojú sánmọ̀ tó gbá ìjọ ‘Ād àti Thamūd mú fún yín ni.”');
INSERT INTO "aykyuni" VALUES(41,14,'41:14','Nígbà tí àwọn Òjíṣẹ́ wọn wá bá wọn níwájú wọn àti lẹ́yìn wọn,(wọ́n sọ) pé: “Ẹ̀yin kò gbọdọ̀ jọ́sìn fún ẹnì kan àyàfi Allāhu.” Wọ́n wí pé: “Tí ó bá jẹ́ pé Olúwa wa bá fẹ́ ni, ìbá sọ àwọn mọlāika kalẹ̀ (fún ìpèpè yìí). Dájúdájú àwa jẹ́ aláìgbàgbọ́ nínú ohun tí wọ́n fi ran yín níṣẹ́.” ');
INSERT INTO "aykyuni" VALUES(41,15,'41:15','Ní ti ìjọ ‘Ād, wọ́n ṣègbéraga ní orí ilẹ̀ láì lẹ́tọ̀ọ́. Wọ́n sì wí pé: “Ta ni ó lágbára jù wá lọ ná?” Ṣé wọn kò rí i pé dájúdájú Allāhu tí Ó ṣẹ̀dá wọn, Ó ní agbára jù wọ́n lọ ni? Wọ́n sì ń tako àwọn āyah Wa!');
INSERT INTO "aykyuni" VALUES(41,16,'41:16','Nítorí náà, A rán atẹ́gùn líle sí wọn ní àwọn ọjọ́ burúkú kan nítorí kí Á lè jẹ́ kí wọ́n tọ́ ìyà yẹpẹrẹ wò nínú ìṣẹ̀mí ayé yìí. Ìyà ọjọ́ Ìkẹ́yìn sì máa yẹpẹrẹ wọn jùlọ; Wọn kò sì níí ràn wọ́n lọ́wọ́.1 ');
INSERT INTO "aykyuni" VALUES(41,17,'41:17','Ní ti ìjọ Thamūd, A ṣàlàyé ìmọ̀nà fún wọn,ṣùgbọ́n wọ́n nífẹ̀ẹ́ sí àìríran dípò ìmọ̀nà. Nítorí náà, ìyà iná láti ojú sánmọ̀ tó ń yẹpẹrẹ ẹ̀dá gbá wọn mú nítorí ohun tí wọ́n ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(41,18,'41:18','A sì gba àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ń bẹ̀rù (Allāhu) là.');
INSERT INTO "aykyuni" VALUES(41,19,'41:19','Ní ọjọ́ tí wọ́n máa kó àwọn ọ̀tá Allāhu jọ síbi Iná, wọn yó sì kó àwọn ẹni àkọ́kọ́ wọn papọ̀ mọ́ àwọn ẹni ìkẹ́yìn wọn');
INSERT INTO "aykyuni" VALUES(41,20,'41:20','títí di ìgbà tí wọ́n bá dé ibẹ̀ tán, ìgbọ́rọ̀ wọn, ìríran wọn àti awọ ara wọn yó sì máa jẹ́rìí lé wọn lórí nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(41,21,'41:21','Wọn yóò wí fún awọ ara wọn pé: “Nítorí kí ni ẹ ṣe jẹ́rìí lé wa lórí ná?” Wọ́n yóò sọ pé: “Allāhu, Ẹni tí Ó fún gbogbo n̄ǹkan ní ọ̀rọ̀ sọ, Òun l’Ó fún wa ní ọ̀rọ̀ sọ. Òun sì l’Ó ṣẹ̀dá yín nígbà àkọ́kọ́. Ọ̀dọ̀ Rẹ̀ sì ni wọn yóò da yín padà sí.”');
INSERT INTO "aykyuni" VALUES(41,22,'41:22','Ẹ̀yin kò lè para yín mọ́ (kúrò níbi ẹ̀ṣẹ̀, nítorí) kí ìgbọ́rọ̀ yín, ìríran yín àti awọ ara yín má fi lè jẹ́rìí le yín lórí. Ṣùgbọ́n ẹ lérò pé dájúdájú Allāhu kò mọ ọ̀pọ̀lọ́pọ̀ nínú ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(41,23,'41:23','Ìyẹn, èrò ọkàn yín tí ẹ lérò sí Olúwa yín ló kó ìparun ba yín. Ẹ sì di ẹni òfò.');
INSERT INTO "aykyuni" VALUES(41,24,'41:24','Tí wọ́n bá ṣe sùúrù (fún ìyà), Iná kúkú ni ibùgbé fún wọn. Tí wọ́n bá sì jẹ́wọ́ ẹ̀ṣẹ̀ wọn láti fi wá ìyọ́nú Allāhu (ní àsìkò yìí), wọn kò sí níí wà nínú àwọn tí wọ́n máa rí ìyọ́nú Allāhu gbà.');
INSERT INTO "aykyuni" VALUES(41,25,'41:25','Àwa ti yan àwọn alábàárìn kan fún wọn,tí wọ́n ṣe ohun tí ń bẹ níwájú wọn2 àti ohun tí ń bẹ ní ẹ̀yìn wọn3 ní ọ̀ṣọ́ fún wọn. Ọ̀rọ̀ náà sì kò lé wọn lórí (gẹ́gẹ́ bí ó ṣe ṣẹlẹ̀) sí àwọn ìjọ tó ṣíwájú wọn nínú àwọn àlùjànǹú àti ènìyàn pé dájúdájú àwọn ni wọ́n jẹ́ ẹni òfò. ');
INSERT INTO "aykyuni" VALUES(41,26,'41:26','Àwọn tó ṣàì gbàgbọ́ wí pé: “Ẹ má ṣe tẹ́tí sí al-Ƙur’ān yìí. Kí ẹ sì sọ ìsọkúsọ nípa rẹ̀ kí ẹ lè borí.”');
INSERT INTO "aykyuni" VALUES(41,27,'41:27','Nítorí náà, dájúdájú A óò fún àwọn tó ṣàì gbàgbọ́ ní ìyà líle tọ́ wò. Àti pé dájúdájú A óò fi èyí tó burú ju èyí tí wọ́n ń ṣe níṣẹ́ san wọ́n ní ẹ̀san.');
INSERT INTO "aykyuni" VALUES(41,28,'41:28','Iná, ìyẹn ni ẹ̀san àwọn ọ̀tá Allāhu. Ilé gbére wà nínú rẹ̀ fún wọn. Ó jẹ́ ẹ̀san nítorí pé wọ́n ń tako àwọn āyah Wa.');
INSERT INTO "aykyuni" VALUES(41,29,'41:29','Àwọn tó ṣàì gbàgbọ́ wí pé: “Olúwa wa, fi àwọn méjèèjì tí wọ́n ṣì wá lọ́nà nínú àwọn àlùjànnú àti ènìyàn hàn wá, kí á fi àwọn méjèèjì sí abẹ́ ẹsẹ̀ wa, nítorí kí àwọn méjèèjì lè wà ní ìsàlẹ̀ pátápátá.”');
INSERT INTO "aykyuni" VALUES(41,30,'41:30','Dájúdájú àwọn tó sọ pé: “Allāhu ni Olúwa wa.” lẹ́yìn náà, tí wọ́n dúró ṣinṣin (sínú ẹ̀sìn títí di ọjọ́ ikú wọn), àwọn mọlāika yóò máa sọ̀kalẹ̀ wá bá wọn (ní ọjọ́ ikú wọn, wọ́n sì máa sọ pé: “Ẹ má ṣe páyà, ẹ má ṣe banújẹ́. Kí ẹ sì dunnú sí Ọgbà Ìdẹ̀ra èyí tí wọ́n ń ṣe ní àdéhùn fún yín.');
INSERT INTO "aykyuni" VALUES(41,31,'41:31','Àwa ni ọ̀rẹ́ yín nínú ìṣẹ̀mí ayé àti ní ọ̀run. Ohun tí ẹ̀mí yín ń fẹ́ ti wà fún yín nínú Ọgbà Ìdẹ̀ra. Ohun tí ẹ̀ ń bèèrè fún sì ti wà nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(41,32,'41:32','Ó jẹ́ n̄ǹkan ìgbàlejò (fún yín) láti ọ̀dọ̀ Aláforíjìn, Àṣàkẹ́-ọ̀run.”');
INSERT INTO "aykyuni" VALUES(41,33,'41:33','Ta ni ó dára jùlọ ní ọ̀rọ̀ sísọ ju ẹni tí ó pèpè sí ọ̀dọ̀ Allāhu, tí ó ṣe iṣẹ́ rere, tí ó sì sọ pé: “Dájúdájú èmi wà nínú àwọn mùsùlùmí.”');
INSERT INTO "aykyuni" VALUES(41,34,'41:34','Rere àti aburú kò dọ́gba. Fi èyí tí ó dára jùlọ dènà (aburú).Nígbà náà ni ẹni tí ọ̀tá wà láààrin ìwọ àti òun máa dà bí ọ̀rẹ́ alásùn-únmọ́ pẹ́kípẹ́kí. ');
INSERT INTO "aykyuni" VALUES(41,35,'41:35','A ku n yín) sìn títí di ọjọá hu pátápátá.n ní Ọjọ́ Àjíǹde.́ wọn gbàgbé Allāhu.ò níí fún ẹnì kan ní (ìwà rere yìí) àfi àwọn tó ṣe sùúrù. A kò níí fún ẹnì kan sẹ́ àfi olórí-ire ńlá.');
INSERT INTO "aykyuni" VALUES(41,36,'41:36','Àti pé tí èròkérò kan láti ọ̀dọ̀ Èṣù bá fẹ́ ṣẹ́ ọ lórí (kúrò níbi ìwà rere yìí), sádi Allāhu (fi Allāhu wá ìṣọ́rí). Dájúdájú Òun ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(41,37,'41:37','Nínú àwọn àmì Rẹ̀ ni òru, ọ̀sán, òòrùn àti òṣùpá. Ẹ kò gbọ́dọ̀ forí kanlẹ̀ fún òòrùn àti òṣùpá. Ẹ forí kanlẹ̀ fún Allāhu, Ẹni tí Ó dá wọn, tí ẹ̀yin bá jẹ́ ẹni tó ń jọ́sìn fún Òun nìkan ṣoṣo.');
INSERT INTO "aykyuni" VALUES(41,38,'41:38','Tí wọ́n bá sì ṣègbéraga, àwọn tó wà ní ọ̀dọ̀ Olúwa rẹ, wọ́n ń ṣàfọ̀mọ́ fún Un ní alẹ́ àti ní ọ̀sán; wọn kò sì kágara.');
INSERT INTO "aykyuni" VALUES(41,39,'41:39','Nínú àwọn àmì Rẹ̀ tún ni pé dájúdájú ìwọ yóò rí ilẹ̀ ní asálẹ̀. Nígbà tí A bá sì sọ omi kalẹ̀ lé e lórí, ó máa rúra wá, ó sì máa ga (fún híhu irúgbìn jáde). Dájúdájú Ẹni tí Ó talẹ̀ jí, Òun mà ni Ẹni tí Ó máa sọ àwọn òkú di alààyè. Dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(41,40,'41:40','Dájúdájú àwọn tó ń darí àwọn āyah Wa kọ ọ̀nà òdì,wọn kò pamọ́ fún Wa. Ṣé ẹni tí wọ́n máa jù sínú Iná l’ó lóore jùlọ ni tàbí ẹni tí ó máa wá ní olùfàyàbalẹ̀ ní Ọjọ́ Àjíǹde? Ẹ máa ṣe ohun tí ẹ bá fẹ́. Dájúdájú Allāhu ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(41,41,'41:41','Dájúdájú àwọn tó ṣàì gbàgbọ́ nínú Tírà Ìrántí náà (ìyẹn, al-Ƙur’ān) nígbà tí ó dé bá wọn (ẹni ìparun ni wọ́n.) Dájúdájú òhun mà ni Tírà tó lágbára.');
INSERT INTO "aykyuni" VALUES(41,42,'41:42','Ìbàjẹ́ kò níí kàn án láti iwájú rẹ̀ àti láti ẹ̀yìn rẹ̀.Ìmísí tí wọ́n sọ̀kalẹ̀ ni láti ọ̀dọ̀ Ọlọ́gbọ́n, Ẹlẹ́yìn. ');
INSERT INTO "aykyuni" VALUES(41,43,'41:43','Wọn kò sọ ọ̀rọ̀ kan sí ọ bí kò ṣe ohun tí wọ́n ti sọ sí àwọn Òjíṣẹ́ tó ṣíwájú rẹ. Dájúdájú Olúwa rẹ mà ni Aláforíjìn, Ó sì ní ìyà ẹlẹ́ta-eléro (lọ́dọ̀).');
INSERT INTO "aykyuni" VALUES(41,44,'41:44','Tí ó bá jẹ́ pé A ṣe al-Ƙur’ān ní n̄ǹkan kíké ní èdè mìíràn yàtọ̀ sí èdè Lárúbáwá ni, wọn ìbá wí pé: “Kí ni kò jẹ́ kí wọ́n ṣe àlàyé àwọn āyah rẹ̀?” Báwo ni al-Ƙur’ān ṣe lè jẹ́ èdè mìíràn (yàtọ̀ sí èdè Lárúbáwá), nígbà tí Ànábì (Muhammad - kí ìkẹ́ àti ọlà Allāhu máa bá a -) jẹ́ Lárúbáwá? Sọ pé: “Ó jẹ́ ìmọ̀nà àti ìwòsàn fún àwọn tó gbàgbọ́ ní òdodo. Àwọn tí kò sì gbàgbọ́, èdídí wà nínú etí wọn ni. Fọ́júǹǹfọ́jú sì wà nínú ojú wọn sí (òdodo al-Ƙur’ān). Àwọn wọ̀nyẹn ni wọ́n sì ń pè (síbi òdodo al-Ƙur’ān) láti àyè tó jìnnà.1 ');
INSERT INTO "aykyuni" VALUES(41,45,'41:45','A kúkú fún (Ànábì) Mūsā ní Tírà. Wọ́n sì yapa-ẹnu sí i. Tí kò bá jẹ́ pé ọ̀rọ́ kan tí ó ti ṣíwájú ní ọ̀dọ̀ Olúwa rẹ ni, A ìbá ṣe ìdájọ́ láààrin wọn. Dájúdájú wọ́n tún wà nínú iyèméjì tó gbópọn nípa al-Ƙur’ān.');
INSERT INTO "aykyuni" VALUES(41,46,'41:46','Ẹnikẹ́ni tí ó bá ṣe iṣẹ́ rere, ó ṣe é fún ẹ̀mí ara rẹ̀. Ẹnikẹ́ni tí ó bá sì ṣe aburú, ó ṣe é fún ẹ̀mí ara rẹ̀. Olúwa rẹ kò sì níí ṣe àbòsí sí àwọn ẹrúsìn.');
INSERT INTO "aykyuni" VALUES(41,47,'41:47','Ọ̀dọ̀ Rẹ̀ ni wọn yóò máa dá ìmọ̀ Àkókò náà padà sí. Kò sí èso kan tí ó máa jáde nínú apó rẹ̀, obìnrin kan kò sì níí lóyún, kò sì níí bímọ àfi pẹ̀lú ìmọ̀ Rẹ̀. (Rántí) ọjọ́ tí Ó sì máa pè wọ́n pé: “Ibo ni àwọn akẹgbẹ́ Mi (tí ẹ jọ́sìn fún) wà?”Wọ́n á wí pé: “Àwa ń jẹ́ kí O mọ̀ pé kò sí olùjẹ́rìí kan nínú wa (tí ó máa jẹ́rìí pé, O ní akẹgbẹ́.)” ');
INSERT INTO "aykyuni" VALUES(41,48,'41:48','Ohun tí wọ́n ń pè tẹ́lẹ̀tẹ́lẹ̀ sì di òfo mọ́ wọn lọ́wọ́.Wọ́n sì mọ̀ ní àmọ̀dájú pé, kò sí ibùsásí kan fún àwọn. ');
INSERT INTO "aykyuni" VALUES(41,49,'41:49','Ènìyàn kò kó ṣúṣú níbi àdúà (láti tọrọ) oore. Tí aburú bá sì fọwọ́ bà á, ó máa di olùsọ̀rètínù, olùjákànmùná.');
INSERT INTO "aykyuni" VALUES(41,50,'41:50','Dájúdájú tí A bá ṣe ìdẹ̀ra fún un láti ọ̀dọ̀ Wa lẹ́yìn aburú tí ó fọwọ́ bà á, dájúdájú ó máa wí pé: “Èyí ni tèmi. Èmi kò sì ní àmọ̀dájú pé Àkókò náà máa ṣẹlẹ̀. Àti pé dájúdájú tí Wọ́n bá dá mi padà sí ọ̀dọ̀ Olúwa mi, dájúdájú rere tún wà fún mi ní ọ̀dọ̀ Rẹ̀.” Dájúdájú Àwa yóò fún àwọn tó ṣàì gbàgbọ́ ní ìró ohun tí wọ́n ṣe níṣẹ́. Dájúdájú A sì máa fún wọn ní ìyà tó nípọn tọ́ wò.');
INSERT INTO "aykyuni" VALUES(41,51,'41:51','Àti pé nígbà tí A bá ṣe ìdẹ̀ra fún ènìyàn, ó máa gbúnrí (kúrò ní ọ̀dọ̀ Wa). Ó sì máa ṣègbéraga. Nígbà tí aburú bá sì fọwọ́ bà á, nígbà náà l’ó máa di aládùáà rẹgẹdẹ.');
INSERT INTO "aykyuni" VALUES(41,52,'41:52','Sọ pé: “Ẹ sọ fún mi, tí ó bá jẹ́ pé al-Ƙur’ān wá láti ọ̀dọ̀ Allāhu, lẹ́yìn náà tí ẹ ṣàì gbàgbọ́ nínú rẹ̀ (ṣé ẹ̀yin kò ti wà nínú ìyapa bí?)” Ta l’ó ṣìnà ju ẹni tí ó wà nínú ìyapa tó jìnnà (sí òdodo)!');
INSERT INTO "aykyuni" VALUES(41,53,'41:53','A óò máa fi àwọn àmì Wa hàn wọ́n nínú òfurufú àti nínú ẹ̀mí ara wọn títí ó máa fi hàn kedere sí wọn pé dájúdájú al-Ƙur’ān ni òdodo. Ǹjẹ́ Olúwa rẹ kò tó kí ó jẹ́ pé dájúdájú Òun ni Arínú-róde lórí gbogbo n̄ǹkan?');
INSERT INTO "aykyuni" VALUES(41,54,'41:54','Gbọ́, dájúdájú wọ́n wà nínú iyèméjì nípa ìpàdé Olúwa wọn. Gbọ́, dájúdájú Allāhu yí gbogbo n̄ǹkan ká.');
INSERT INTO "aykyuni" VALUES(42,1,'42:1','Hā mīm.');
INSERT INTO "aykyuni" VALUES(42,2,'42:2','‘Aēn sīn ƙọ̄f. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(42,3,'42:3','Báyẹn ni Allāhu, Alágbára, Ọlọ́gbọ́n ṣe ń fi ìmísí ránṣẹ́ sí ìwọ àti àwọn tó ṣíwájú rẹ.1 ');
INSERT INTO "aykyuni" VALUES(42,4,'42:4','TiRẹ̀ ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Ó ga, Ó tóbi.');
INSERT INTO "aykyuni" VALUES(42,5,'42:5','Sánmọ̀ fẹ́ẹ̀ fàya láti òkè wọn (nípa títóbi Allāhu). Àwọn mọlāika sì ń ṣe àfọ̀mọ́ pẹ̀lú ẹyìn fún Olúwa wọn. Wọ́n sì ńtọrọ àforíjìn fún àwọn tó wà lórí ilẹ̀. Gbọ́, dájúdájú Allāhu, Òun ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(42,6,'42:6','Àwọn tó mú àwọn alátìlẹ́yìn kan lẹ́yìn Rẹ̀, Allāhu sì ni Aláàbò lórí wọn. Ìwọ sì kọ́ ni olùṣọ́ lórí wọn.');
INSERT INTO "aykyuni" VALUES(42,7,'42:7','Báyẹn ni A ṣe fi al-Ƙur’ān ránṣẹ́ sí ọ ní èdè Lárúbáwá nítorí kí o lè fi ṣe ìkìlọ̀ fún ’Ummul-Ƙurọ̄ (ìyẹn, ará ìlú Mọkkah) àti ẹnikẹ́ni tí ó bá wà ní àyíká rẹ̀ (ìyẹn, ará ìlú yòókù), àti nítorí kí o lè fi ṣe ìkìlọ̀ nípa Ọjọ́ Àkójọ, tí kò sí iyèméjì nínú rẹ̀. Ìjọ kan yóò wà nínú Ọgbà Ìdẹ̀ra. Ìjọ kan yó sì wà nínú Iná tó ń jo.');
INSERT INTO "aykyuni" VALUES(42,8,'42:8','Tí ó bá jẹ́ pé Allāhu bá fẹ́, ìbá ṣe wọ́n ní ìjọ ẹlẹ́sìn ẹyọ kan. Ṣùgbọ́n Ó ń fi ẹni tí Ó bá fẹ́ sínú ìkẹ́ Rẹ̀. Àwọn alábòsí, kò sì níí sí aláàbò àti alárànṣe kan fún wọn.');
INSERT INTO "aykyuni" VALUES(42,9,'42:9','Ṣé wọ́n mú àwọn aláàbò kan lẹ́yìn Rẹ̀ ni? Allāhu, Òun sì ni Aláàbò. Òun l’Ó ń sọ àwọn òkú di alààyè. Òun sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(42,10,'42:10','Ohunkóhun tí ẹ bá yapa-ẹnu lórí rẹ̀, ìdájọ́ rẹ̀ di ọ̀dọ̀ Allāhu. Ìyẹn ni Allāhu, Olúwa mi. Òun ni mo gbáralé. Ọ̀dọ̀ Rẹ̀ sì ni mo máa ṣẹ́rí padà sí (ní ti ìronúpìwàdà). 1 ');
INSERT INTO "aykyuni" VALUES(42,11,'42:11','(Òun ni) Olùpilẹ̀dá àwọn sánmọ̀ àti ilẹ̀. Ó ṣẹ̀dá àwọn obìnrin fún yín láti ara yín. Ó tún ṣẹ̀dá àwọn abo ẹran-ọ̀sìn láti ara àwọn akọ ẹran-ọ̀sìn. Ó ń mu yín pọ̀ sí i (nípa ìṣẹ̀dá yín ní akọ-abo). Kò sí kiní kan bí irú Rẹ̀. Òun sì ni Olùgbọ́, Olùríran.');
INSERT INTO "aykyuni" VALUES(42,12,'42:12','TiRẹ̀ ni àwọn kọ́kọ́rọ́ ilé ọ̀rọ̀ àwọn sánmọ̀ àti ilẹ̀. Ó ń tẹ́ arísìkí sílẹ̀ fún ẹni tí Ó bá fẹ́. Ó sì ń díwọ̀n rẹ̀ (fún ẹlòmíìràn). Dájúdájú Òun ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(42,13,'42:13','(Allāhu) ṣe ní òfin fún yín nínú ẹ̀sìn (’Islām) ohun tí Ó pa ní àṣẹ fún (Ànábì) Nūh àti èyí tí Ó fi ránṣẹ́ sí ọ, àti ohun tí A pa láṣẹ fún (Ànábì) ’Ibrọ̄hīm, (Ànábì) Mūsā àti (Ànábì) ‘Īsā pé, kí ẹ gbé ẹ̀sìn náà dúró. Kí ẹ sì má ṣe pín sí ìjọ ọ̀tọ̀ọ̀tọ̀ nínú rẹ̀. Wàhálà l’ó jẹ́ fún àwọn ọ̀ṣẹbọ nípa n̄ǹkan tí ò ń pè wọ́n sí (níbi mímú Allāhu ní ọ̀kan ṣoṣo). Allāhu l’Ó ń ṣẹ̀ṣà ẹni tí Ó bá fẹ́ sínú ẹ̀sìn Rẹ̀ (tí ò ń pè wọ́n sí). Ó sì ń fi ọ̀nà mọ ẹni tí ó bá ń ṣẹ́rí padà sí ọ̀dọ̀ Rẹ̀ (nípasẹ̀ ìronúpìwàdà).');
INSERT INTO "aykyuni" VALUES(42,14,'42:14','(Àwọn ọ̀ṣẹbọ) kò sì pín sí ìjọ ọ̀tọ̀ọ̀tọ̀ àfi lẹ́yìn tí ìmọ̀ (’Islām) dé bá wọn ní ti ìlara láààrin ara wọn (sí àwọn Ànábì - kí ọlà Allāhu máa bá wọn -). Tí kò bá jẹ́ pé ọ̀rọ̀ kan tí ṣíwájú lọ́dọ̀ Olúwa rẹ (pé Òun yóò lọ́ wọn lára) títí di gbèdéke àkókò kan ni, Wọn ìbá ti dájọ́ láààrin wọn. Dájúdájú àwọn tí A jogún Tírà fún lẹ́yìn wọn sì tún wà nínú iyèméjì tó gbópọn nípa ’Islām.');
INSERT INTO "aykyuni" VALUES(42,15,'42:15','Nítorí ìyẹn, pèpè (sínú ’Islām), kí o sì dúró ṣinṣin gẹ́gẹ́ bí Wọ́n ṣe pa ọ́ lásẹ. Má sì ṣe tẹ̀lé ìfẹ́-inú wọn. Kí o sì sọ pé: “Mo gbàgbọ́ nínú èyíkéyìí Tírà tí Allāhu sọ̀kalẹ̀. Wọ́n sì pa mí láṣẹ pé kí n̄g ṣe déédé láààrin yín. Allāhu ni Olúwa wa àti Olúwa yín. Tiwa ni àwọn iṣẹ́ wa. Tiyín sì ni àwọn iṣẹ́ yín. Kò sí ìjà láààrin àwa àti ẹ̀yin.Allāhu l’Ó sì máa kó wa jọ papọ̀. Ọ̀dọ̀ Rẹ̀ sì ní àbọ̀ ẹ̀dá.” ');
INSERT INTO "aykyuni" VALUES(42,16,'42:16','Àwọn tó ń bá (Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -) jà nípa (ẹ̀sìn) Allāhu lẹ́yìn ìgbà tí àwọn ènìyàn ti gbà fún un, ẹ̀rí wọn máa wó lọ́dọ̀ Olúwa wọn. Ìbínú ń bẹ lórí wọn. Ìyà líle sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(42,17,'42:17','Allāhu ni Ẹni tí Ó sọ Tírà (al-Ƙur’ān) àti (òfin) òṣùwọ̀n (déédé) kalẹ̀ pẹ̀lú òdodo.Àti pé kí l’ó máa fi mọ̀ ọ́ pé ó ṣeé ṣe kí Àkókò náà ti súnmọ́! ');
INSERT INTO "aykyuni" VALUES(42,18,'42:18','Àwọn tí kò ní ìgbàgbọ́ òdodo nínú rẹ̀ ń wá a pẹ̀lú ìkánjú. Àwọn tó sì gbàgbọ́ ní òdodo ń páyà rẹ̀. Wọ́n sì mọ̀ pé dájúdájú òdodo ni. Kíyè sí i, dájúdájú àwọn tó ń jiyàn nípa Àkókò náà ti wà nínú ìṣìnà tó jìnnà.');
INSERT INTO "aykyuni" VALUES(42,19,'42:19','Allāhu ni Aláàánú fún àwọn ẹrúsìn Rẹ̀. Ó ń ṣe arísìkí fún ẹni tí Ó bá fẹ́. Òun sì ni Alágbára, Olùborí.');
INSERT INTO "aykyuni" VALUES(42,20,'42:20','Ẹnikẹ́ni tí ó bá gbèrò láti gba èso (iṣẹ́ rẹ̀ ní) ọ̀run, A máa ṣe àlékún sí èso rẹ̀ fún un. Ẹnikẹ́ni tí ó bá sì gbèrò láti gba èso (iṣẹ́ rẹ̀ ní) ayé, A máa fún un nínú rẹ̀. Kò sì níí sí ìpín kan kan fún un mọ́ ní ọ̀run.');
INSERT INTO "aykyuni" VALUES(42,21,'42:21','Tàbí wọ́n ní àwọn òrìṣà kan tó ṣòfin (ìbọ̀rìṣà) fún wọn nínú ẹ̀sìn, èyí tí Allāhu kò yọ̀ǹda rẹ̀? Tí kò bá jẹ́ ti ọ̀rọ̀ àsọyán (pé A kò níí kánjú jẹ wọ́n ní ìyà), Àwa ìbá ti mú ìdájọ́ wá láààrin wọn. Dájúdájú àwọn alábòsí, ìyà ẹlẹ́ta-eléro sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(42,22,'42:22','O máa rí àwọn alábòsí tí wọn yóò máa páyà nítorí ohun tí wọ́n ṣe níṣẹ́, tí ó sì máa kò lé wọn lórí. Àwọn tó sì gbàgbọ́ ní òdodo, tí wọ́n ṣe iṣẹ́ rere máa wà ní àwọn àbàtà Ọgbà Ìdẹ̀ra, Ohun tí wọ́n bá ń fẹ́ máa wà fún wọn ní ọ̀dọ̀ Olúwa wọn. Ìyẹn, òhun ni oore àjùlọ tó tóbi. ');
INSERT INTO "aykyuni" VALUES(42,23,'42:23','Ìyẹn ni èyí tí Allāhu fi ń ṣe ìró ìdùnnú fún àwọn ẹrúsìn Rẹ̀, àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere. Sọ pé: “Èmi kò bèèrè owó-ọ̀yà lọ́wọ́ yín lórí rẹ̀ bí kò ṣe ìfẹ́ ẹbí.”Ẹnikẹ́ni tí ó bá ṣe rere kan, A máa ṣe àlékún rere fún un. Dájúdájú Allāhu ni Aláforíjìn, Ọlọ́pẹ́.2 ');
INSERT INTO "aykyuni" VALUES(42,24,'42:24','Tàbí wọ́n ń wí pé: “Ó dá àdápa irọ́ mọ́ Allāhu ni?” Nítorí náà, tí Allāhu bá fẹ́, Ó máa fi èdídí dí ọkàn rẹ pa. Àti pé Allāhu yóò pa irọ́ rẹ́. Ó sì máa fi òdodo rinlẹ̀ pẹ̀lú àwọn ọ̀rọ̀ Rẹ̀. Dájúdájú Òun ni Onímọ̀ nípa ohun tó wà nínú igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(42,25,'42:25','Òun ni Ẹni tó ń gba ìronúpìwàdà láti ọ̀dọ̀ àwọn ẹrúsìn Rẹ̀. Ó ń ṣe àmójúkúrò níbi àwọn àṣìṣe. Ó sì mọ ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(42,26,'42:26','Ó ń gba àdúà àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere. Ó sì ń ṣe àlékún fún wọn nínú ọlá Rẹ̀. Àwọn aláìgbàgbọ́, ìyà líle sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(42,27,'42:27','Tí ó bá jẹ́ pé Allāhu tẹ́ arísìkí sílẹ̀ rẹgẹdẹ fún àwọn ẹrúsìn Rẹ̀ ni, wọn ìbá tayọ ẹnu-ààlà lórí ilẹ̀. Ṣùgbọ́n Ó ń sọ (arísìkí) tí Ó bá fẹ́ kalẹ̀ níwọ̀n-níwọ̀n. Dájúdájú Òun ni Alámọ̀tán, Olùríran nípa àwọn ẹrúsìn Rẹ̀.');
INSERT INTO "aykyuni" VALUES(42,28,'42:28','Òun sì ni Ẹni tó ń sọ omi òjò kalẹ̀ lẹ́yìn ìgbà tí wọ́n ti sọ̀rètí nù. Ó sì ń fọ́n ìkẹ́ Rẹ̀ ká. Òun sì ni Aláàbò, Ẹlẹ́yìn.');
INSERT INTO "aykyuni" VALUES(42,29,'42:29','Àti pé nínú àmì Rẹ̀ ni ìṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ àti n̄ǹkan tí Ó fọ́nká sáààrin méjèèjì nínú àwọn n̄ǹkan abẹ̀mí. Òun sì ni Alágbára lórí ìkójọ wọn nígbà tí Ó bá fẹ́.');
INSERT INTO "aykyuni" VALUES(42,30,'42:30','Ohunkóhun tí ó bá kàn yín nínú àdánwò, ohun tí ẹ fi ọwọ́ ara yín fà ni.Ó sì ń ṣàmójúkúrò níbi ọ̀pọ̀lọ́pọ̀. ');
INSERT INTO "aykyuni" VALUES(42,31,'42:31','Ẹ̀yin kò lè mórí bọ́ (mọ́ Allāhu lọ́wọ́) lórí ilẹ̀. Kò sì sí aláàbò àti alárànṣe kan fún yín lẹ́yìn Allāhu.');
INSERT INTO "aykyuni" VALUES(42,32,'42:32','Ó tún wà nínú àwọn àmì Rẹ̀, àwọn ọkọ̀ ojú-omi tó ń rìn lójú omi (tó dà) bí àwọn òkè àpáta gíga.');
INSERT INTO "aykyuni" VALUES(42,33,'42:33','Tí Allāhu bá fẹ́, Ó máa dá atẹ́gùn náà dúró, (àwọn ọkọ̀ ojú-omi náà) sì máa dúró pa sójú kan náà lórí omi. Dájúdájú àwọn àmì wà nínú ìyẹn fún gbogbo onísùúrù, olùdúpẹ́.');
INSERT INTO "aykyuni" VALUES(42,34,'42:34','Tàbí kí Ó kó ìparun bá (àwọn ọkọ̀ ojú-omi náà) nítorí ohun tí àwọn èrò ọkọ̀ ojú-omi ṣe (níṣẹ́ aburú). Ó sì ń ṣàmójúkúrò níbi ọ̀pọ̀lọ́pọ̀.');
INSERT INTO "aykyuni" VALUES(42,35,'42:35','Àwọn tó ń (fi irọ́) ja àwọn āyah Wa ní iyàn sì máa mọ̀ pé kò sí ibùsásí kan fún wọn.');
INSERT INTO "aykyuni" VALUES(42,36,'42:36','Nítorí náà, ohunkóhun tí A bá fún yín, ìgbádùn ayé nìkan ni. Ohun tí ó wà lọ́dọ̀ Allāhu lóore jùlọ, ó sì máa ṣẹ́kù títí láéláé fún àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ń gbáralé Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(42,37,'42:37','(Ohun tí ó wà lọ́dọ̀ Allāhu tún wà fún) àwọn tó ń jìnnà sí àwọn ẹ̀ṣẹ̀ ńlá ńlá àti àwọn ìwà ìbàjẹ́, àti (àwọn tó jẹ́ pé) nígbà tí wọ́n bá bínú, wọn yóò ṣàforíjìn.');
INSERT INTO "aykyuni" VALUES(42,38,'42:38','(Ohun tí ó wà lọ́dọ̀ Allāhu tún wà fún) àwọn tó jẹ́pè ti Olúwa wọn, tí wọ́n ń kírun, ọ̀rọ̀ ara wọn sì jẹ́ ìjíròrò láààrin ara wọn, wọ́n tún ń ná nínú arísìkí tí A pèsè fún wọn.');
INSERT INTO "aykyuni" VALUES(42,39,'42:39','(Ohun tí ó wà lọ́dọ̀ Allāhu tún wà fún) àwọn tí (ó jẹ́ pé) nígbà tí wọ́n bá ṣe àbòsí sí wọn, wọn yóò gbẹ̀san wọn padà.');
INSERT INTO "aykyuni" VALUES(42,40,'42:40','Ẹ̀san aburú sì ni aburú bí irú rẹ̀.Ẹnikẹ́ni tí ó bá sì ṣàmójúkúrò, tí ó tún ṣàtúnṣe, ẹ̀san rẹ̀ wà lọ́dọ̀ Allāhu. Dájúdájú (Allāhu) kò nífẹ̀ẹ́ àwọn alábòsí. ');
INSERT INTO "aykyuni" VALUES(42,41,'42:41','Dájúdájú ẹni tí ó bá sì gbẹ̀san lẹ́yìn tí wọ́n ṣe àbòsí sí i, àwọn wọ̀nyẹn (tí wọ́n ṣàbòsí sí), kò sí ìbáwí kan kan fún wọn.');
INSERT INTO "aykyuni" VALUES(42,42,'42:42','Àwọn tí ìbáwí wà fún ni àwọn tó ń ṣàbòsí sí àwọn ènìyàn, tí wọ́n tún ń tayọ ẹnu-ààlà lórí ilẹ̀ láì lẹ́tọ̀ọ́. Àwọn wọ̀nyẹn ni ìyà ẹlẹ́ta-eléro wà fún.');
INSERT INTO "aykyuni" VALUES(42,43,'42:43','Ẹnikẹ́ni tí ó bá ṣe sùúrù, tí ó tún ṣàforíjìn, dájúdájú ìyẹn wà nínú àwọn ìpinnu ọ̀rọ̀.');
INSERT INTO "aykyuni" VALUES(42,44,'42:44','Ẹnikẹ́ni tí Allāhu bá ṣì lọ́nà, kò tún sí aláàbò kan fún un mọ́ lẹ́yìn Rẹ̀. O sì máa rí àwọn alábòsí nígbà tí wọ́n bá rí Iná, wọn yóò wí pé: “Ǹjẹ́ ọ̀nà kan kan wà tí a lè fi padà sí ilé ayé?”');
INSERT INTO "aykyuni" VALUES(42,45,'42:45','O sì máa rí wọn tí A máa kó wọn lọ sínú Iná; wọn yóò palọ́lọ́ láti ara ìyẹpẹrẹ, wọn yó si máa wò fín-ín-fín. Àwọn tó gbàgbọ́ ní òdodo sì máa sọ pé: “Dájúdájú àwọn ẹni òfò ni àwọn tó ṣe ẹ̀mí ara wọn àti ara ilé wọn ní òfò ní Ọjọ́ Àjíǹde.” Kíyè sí i, dájúdájú àwọn alábòsí yóò wà nínú ìyà gbére.');
INSERT INTO "aykyuni" VALUES(42,46,'42:46','Kò sì níí sí aláàbò kan fún wọn tí ó máa ràn wọ́n lọ́wọ́ lẹ́yìn Allāhu. Àti pé ẹnikẹ́ni tí Allāhu bá ṣì lọ́nà, kò níí sí ọ̀nà kan kan fún un.');
INSERT INTO "aykyuni" VALUES(42,47,'42:47','Ẹ jẹ́pè Olúwa yín ṣíwájú kí ọjọ́ kan tí kò ṣe é dá padà tó dé láti ọ̀dọ̀ Allāhu. Kò sí ibùsásí kan fún yín ní ọjọ́ yẹn. Kò sì níí sí ẹni tí ó máa bá yín kọ ìyà náà.');
INSERT INTO "aykyuni" VALUES(42,48,'42:48','Nítorí náà, tí wọ́n bá gbúnrí, A kò rán ọ pé kí o jẹ́ olùṣọ́ fún wọn. Kò sí kiní kan tó di dandan fún ọ bí kò ṣe ìkéde (ẹ̀sìn). Àti pé dájúdájú nígbà tí A bá fún ènìyàn ní ìkẹ́ kan tọ́ wò láti ọ̀dọ̀ Wa, ó máa dunnú sí i. Tí aburú kan bá sì kàn án nípasẹ̀ ohun tí ọwọ́ wọn tì ṣíwájú (ní iṣẹ́ aburú), dájúdájú ènìyàn ni aláìmoore.');
INSERT INTO "aykyuni" VALUES(42,49,'42:49','Ti Allāhu ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Ó ń ṣẹ̀dá ohun tí Ó bá fẹ́. Ó ń ta ẹni tí Ó bá fẹ́ lọ́rẹ ọmọbìnrin. Ó sì ń ta ẹni tí Ó bá fẹ́ lọ́rẹ ọmọkùnrin.');
INSERT INTO "aykyuni" VALUES(42,50,'42:50','Tàbí kí Ó ṣe wọ́n ní oríṣi méjì; ọmọkùnrin àti ọmọbìnrin. Ó sì ń ṣe ẹni tí Ó bá fẹ́ ní àgàn. Dájúdájú Òun ni Onímọ̀, Alágbára.');
INSERT INTO "aykyuni" VALUES(42,51,'42:51','Kò lẹ́tọ̀ọ́ fún abara kan pé kí Allāhu bá a sọ̀rọ̀ àfi kí (ọ̀rọ̀ náà) jẹ́ ìmísí (ìṣípayá mímọ́), tàbí kí ó jẹ́ lẹ́yìn gàgá, tàbí kí Ó rán Òjíṣẹ́ kan (tí ó jẹ́ mọlāika) sí i, ó sì máa fi ìmísí jíṣẹ́ fún (abara náà) pẹ̀lú ìyọ̀ǹda (Allāhu). Dájúdájú Ó ga. Ọlọ́gbọ́n sì ni.');
INSERT INTO "aykyuni" VALUES(42,52,'42:52','Báyẹn sì ni A ṣe fi ìmísí ránṣẹ́ sí ọ nínú àṣẹ Wa. Ìwọ kò mọ kí ni Tírà àti ìgbàgbọ́ òdodo tẹ́lẹ̀ (ṣíwájú ìmísí náà),ṣùgbọ́n A ṣe ìmísí náà ní ìmọ́lẹ̀ kan tí À ń fi ṣe ìmọ̀nà fún ẹni tí A bá fẹ́ nínú àwọn ẹrúsìn Wa. Dájúdájú ìwọ ń pèpè sí ọ̀nà tààrà (’Islām).2 ');
INSERT INTO "aykyuni" VALUES(42,53,'42:53','Ọ̀nà Allāhu, Ẹni tí ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ ń jẹ́ tiRẹ̀. Gbọ́! Ọ̀dọ̀ Allāhu ni àwọn ọ̀rọ̀ ẹ̀dá yóò padà sí.');
INSERT INTO "aykyuni" VALUES(43,1,'43:1','Hā mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(43,2,'43:2','(Allāhu) fi Tírà tó ń yanjú ọ̀rọ̀ ẹ̀dá búra.');
INSERT INTO "aykyuni" VALUES(43,3,'43:3','Dájúdájú Àwa se é ní al-Ƙur’ān (n̄ǹkan kíké) ní èdè Lárúbáwá nítorí kí ẹ lè ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(43,4,'43:4','Àti pé dájúdájú nínú Tírà Ìpìlẹ̀ tó wà lọ́dọ̀ Wa, al-Ƙur’ān mà ga, ó sì kún fún ọgbọ́n.');
INSERT INTO "aykyuni" VALUES(43,5,'43:5','Ṣé kí Á ká Tírà Ìrántí (al-Ƙur’ān) kúrò nílẹ̀ fún yín pátápátá (kí Á máa wò yín ní ìran) nítorí pé, ẹ jẹ́ ìjọ alákọyọ?');
INSERT INTO "aykyuni" VALUES(43,6,'43:6','Mélòó mélòó nínú àwọn Ànábì tí A ti rán sí àwọn ẹni àkọ́kọ́.');
INSERT INTO "aykyuni" VALUES(43,7,'43:7','Kò sí Ànábì kan tí ó wá bá wọn àyàfi kí wọ́n máa fi ṣe yẹ̀yẹ́.');
INSERT INTO "aykyuni" VALUES(43,8,'43:8','Nítorí náà, A ti pa àwọn tó ní agbára ju àwọn (wọ̀nyí) rẹ́. Àpèjúwe (ìparun) àwọn ẹni àkọ́kọ́ sì ti ṣíwájú.');
INSERT INTO "aykyuni" VALUES(43,9,'43:9','Tí o bá kúkú bi wọ́n léèrè pé: “Ta ni Ó dá àwọn sánmọ̀ àti ilẹ̀?”, dájúdájú wọ́n á wí pé: “Ẹni tó dá wọn ni Alágbára, Onímọ̀;');
INSERT INTO "aykyuni" VALUES(43,10,'43:10','Ẹni tí Ó ṣe ilẹ̀ ní ìtẹ́ fún yín. Ó sì fi àwọn ojú ọ̀nà sínú rẹ̀ fún yín nítorí kí ẹ lè mọ̀nà;');
INSERT INTO "aykyuni" VALUES(43,11,'43:11','Ẹni tí Ó ń sọ omi kalẹ̀ láti sánmọ̀ níwọ̀n-níwọ̀n. Ó sì ń fi sọ òkú ilẹ̀ di àyè ilẹ̀. Báyẹn náà ni wọn yóò ṣe mu yín jáde.');
INSERT INTO "aykyuni" VALUES(43,12,'43:12','Ẹni tí Ó ṣẹ̀dá gbogbo n̄ǹkan ní oríṣiríṣi.Ó sì ṣe n̄ǹkan tí ẹ̀ ń gùn fún yín láti ara àwọn ọkọ̀ ojú-omi àti àwọn ẹran-ọ̀sìn. ');
INSERT INTO "aykyuni" VALUES(43,13,'43:13','Nítorí kí ẹ lè jókòó dáadáa sẹ́yìn rẹ̀, lẹ́yìn náà kí ẹ lè ṣe ìrántí ìdẹ̀ra Olúwa yín nígbà tí ẹ bá jókòó dáadáa tán sórí rẹ̀, kí ẹ sì sọ pé: “Mímọ́ ni fún Ẹni tí Ó rọ èyí fún wa, kì í ṣe pé a jẹ́ alágbára lórí rẹ̀.');
INSERT INTO "aykyuni" VALUES(43,14,'43:14','Àti pé dájúdájú ọ̀dọ̀ Olúwa wa ni àwa yóò padà sí.”');
INSERT INTO "aykyuni" VALUES(43,15,'43:15','Wọ́n fi ìpín kan nínú àwọn ẹrúsìn (Allāhu) tì sí ọ̀dọ̀ Rẹ̀ (ní ti ọmọ bíbí). Dájúdájú ènìyàn ni aláìmoore pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(43,16,'43:16','Tàbí (ẹ̀yin ń wí pé): “Ó mú nínú n̄ǹkan tí Ó dá ní ọmọbìnrin (fúnra Rẹ̀). Ó sì fi àwọn ọmọkùnrin ṣa ẹ̀yin lẹ́ṣà.”');
INSERT INTO "aykyuni" VALUES(43,17,'43:17','Nígbà tí wọ́n bá fún ẹnì kan nínú wọn ní ìró ìdùnnú ohun tí ó fi ṣe àpẹ̀ẹrẹ fún Àjọkẹ́-ayé, ojú rẹ̀ máa dúdú wá (pé òun bí ọmọbìnrin).Ó sì máa kún fún ìbànújẹ́. ');
INSERT INTO "aykyuni" VALUES(43,18,'43:18','Ṣé ẹni tí wọ́n ń tọ́ nínú ọ̀ṣọ́ (tí ó ń to ọ̀ṣọ́ mọ́ra), tí kò sì lè bọ́ sí gban̄gba níbi ìjà (ni ẹ̀ ń pè ní ọmọ Àjọkẹ́-ayé)!?');
INSERT INTO "aykyuni" VALUES(43,19,'43:19','Àwọn mọlāika, àwọn tó jẹ́ ẹrúsìn Àjọkẹ́-ayé, wọ́n tún sọ wọ́n di ọmọbìnrin! Ṣé wọ́n fojú rí ìṣẹ̀dá wọn ni? Wọ́n máa ṣàkọsílẹ̀ ohun tí wọ́n fojú rí. Wọ́n sì máa bi wọ́n léèrè (nípa rẹ̀).');
INSERT INTO "aykyuni" VALUES(43,20,'43:20','Wọ́n tún wí pé: “Tí Àjọkẹ́-ayé bá fẹ́, àwa ìbá tí jọ́sìn fún wọn.” Kò sí ìmọ̀ kan fún wọn nípa ìyẹn. Wọn kò sì jẹ́ kiní kan bí kò ṣe pé wọ́n ń parọ́.');
INSERT INTO "aykyuni" VALUES(43,21,'43:21','Tàbí A ti fún wọn ní Tírà kan ṣíwájú al-Ƙur’ān, tí wọ́n ń lò ní ẹ̀rí?');
INSERT INTO "aykyuni" VALUES(43,22,'43:22','Rárá. Wọ́n wí pé: “Dájúdájú àwa bá àwọn bàbá wa lórí ẹ̀sìn kan. Dájúdájú àwa sì ni olùmọ̀nà lórí orípa wọn.”1 ');
INSERT INTO "aykyuni" VALUES(43,23,'43:23','Bákan náà, Àwa kò rán olùkìlọ̀ kan sí ìlú kan ṣíwájú rẹ, àyàfi kí àwọn onígbẹdẹmukẹ ìlú náà wí pé: “Dájúdájú àwa bá àwọn bàbá wa lórí ẹ̀sìn kan. Dájúdájú àwa sì ni olùtẹ̀lé wọn lórí orípa wọn.”');
INSERT INTO "aykyuni" VALUES(43,24,'43:24','(Olùkìlọ̀) sì sọ pé: “Ǹjẹ́ èmi kò ti mú wá fún yín ohun tí ó jẹ́ ìmọ̀nà jùlọ sí ohun tí ẹ bá àwọn bàbá yín lórí rẹ̀?” Wọ́n wí pé: “Dájúdájú àwa jẹ́ aláìgbàgbọ́ nínú ohun tí wọ́n fi ran yín níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(43,25,'43:25','Nítorí náà, A gbẹ̀san lára wọn. Wo bí ìkángun àwọn tó pe òdodo ní irọ́ ti rí!');
INSERT INTO "aykyuni" VALUES(43,26,'43:26','(Rántí) nígbà tí (Ànábì) ’Ibrọ̄hīm sọ fún bàbá rẹ̀ àti àwọn ènìyàn rẹ̀ pé: “Dájúdájú èmi yọwọ́ yọsẹ̀ kúrò nínú ohun tí ẹ̀ ń jọ́sìn fún.');
INSERT INTO "aykyuni" VALUES(43,27,'43:27','Àfi Ẹni tí Ó ṣẹ̀dá mi. Dájúdájú Òun l’Ó máa fi ọ̀nà tààrà mọ̀ mí.”');
INSERT INTO "aykyuni" VALUES(43,28,'43:28','Allāhu sì ṣe é ní ọ̀rọ̀ kan tó máa wà títí láéláé láààrin àwọn àrọ́mọdọ́mọ rẹ̀ nítorí kí wọ́n lè ṣẹ́rí padà (síbi òdodo).1 ');
INSERT INTO "aykyuni" VALUES(43,29,'43:29','Ṣùgbọ́n Mo fún àwọn wọ̀nyí àti àwọn bàbá wọn ní ìgbádùn ayé títí òdodo àti Òjíṣẹ́ pọ́nńbélé fi dé bá wọn.');
INSERT INTO "aykyuni" VALUES(43,30,'43:30','Nígbà tí òdodo dé bá wọn, wọ́n wí pé: “Idán ni èyí; dájúdájú àwa kò sì níí gbà á gbọ́.”');
INSERT INTO "aykyuni" VALUES(43,31,'43:31','Wọ́n tún wí pé: “Kí ni kò jẹ́ kí Wọ́n sọ al-Ƙur’ān yìí kalẹ̀ fún ọkùnrin ńlá kan nínú àwọn ìlú méjèèjì?”');
INSERT INTO "aykyuni" VALUES(43,32,'43:32','Ṣé àwọn ni wọn máa pín ìkẹ́ Olúwa rẹ ni? Àwa l’A pín n̄ǹkan ìṣẹ̀mí wọn fún wọn nínú ìgbésí ayé. A sì fi àwọn ipò gíga gbé apá kan wọn ga ju apá kan lọ nítorí kí apá kan wọn lè máa ṣiṣẹ́ fún apá kan. Ìkẹ́ Olúwa rẹ sì lóore jùlọ sí ohun tí wọ́n ń kó jọ.');
INSERT INTO "aykyuni" VALUES(43,33,'43:33','Tí kò bá jẹ́ pé àwọn ènìyàn máa di ìjọ kan ṣoṣo (tí gbogbo wọn máa di kèfèrí nítorí ọrọ̀ ayé), A ìbá ṣe àwọn òrùlé àti àwọn àkàbà tí wọn yóò fi máa gùnkè nínú ilé ní fàdákà fún ẹni tó ń ṣàì gbàgbọ́ nínú Àjọkẹ́-ayé.');
INSERT INTO "aykyuni" VALUES(43,34,'43:34','(A ìbá tún ṣe) àwọn ẹnu ọ̀nà ilé wọn àti àwọn ibùsùn tí wọn yóò máa rọ̀gbọ̀kú lé lórí (ní fàdákà)');
INSERT INTO "aykyuni" VALUES(43,35,'43:35','àti góòlù.Gbogbo n̄ǹkan wọ̀nyí kò jẹ́ n̄ǹkan kan bí kò ṣe ìgbádùn ìṣẹ̀mí ayé lásán. (Ọgbà Ìdẹ̀ra) Ọ̀run sì wà ní ọ̀dọ̀ Olúwa rẹ fún àwọn olùbẹ̀rù (Allāhu). ');
INSERT INTO "aykyuni" VALUES(43,36,'43:36','Ẹnikẹ́ni tí ó bá gbúnrí kúrò níbi ìrántí Àjọké-ayé, A máa yan èṣù kan fún un. Òun sì ni alábàárìn rẹ̀.');
INSERT INTO "aykyuni" VALUES(43,37,'43:37','Dájúdájú àwọn èṣù náà yóò máa ṣẹ́rí wọn kúrò lójú ọ̀nà (ẹ̀sìn Allāhu). Wọn yó sì máa lérò pé dájúdájú àwọn ni olùmọ̀nà.');
INSERT INTO "aykyuni" VALUES(43,38,'43:38','Títí di ìgbà tí ó fi máa wá bá Wa, ó sì máa wí pé: “Háà! Kí ó sì jẹ́ pé (ìtakété bí) ìtakété ibùyọ-òòrùn àti ibùwọ̀ rẹ̀ sì wà láààrin èmi àti ìwọ (èṣù alábàárìn yìí, ìbá dára); alábàárìn burúkú sì ni.');
INSERT INTO "aykyuni" VALUES(43,39,'43:39','(Àbámọ̀ yín) kò níí ṣe yín ní àǹfààní ní Òní nígbà tí ẹ ti ṣàbòsí. (Àti pé) dájúdájú ẹ̀yin (àti òrìṣà yín) ni ẹ dìjọ máa kẹ́gbẹ́ nínú ìyà.');
INSERT INTO "aykyuni" VALUES(43,40,'43:40','Ṣé ìwọ l’o máa fún àwọn adití ní ọ̀rọ̀ gbọ́ tàbí o máa fún àwọn afọ́jú ní ìmọ̀nà àti ẹni tí ó wà nínú ìṣìnà pọ́nńbélé?1 ');
INSERT INTO "aykyuni" VALUES(43,41,'43:41','Nítorí náà, ó ṣeé ṣe kí Á ti mú ọ kúrò (lórí ilẹ̀ ayé ṣíwájú àsìkò ìyà wọn), dájúdájú Àwa yóò gbẹ̀san (ìyà) lára wọn.');
INSERT INTO "aykyuni" VALUES(43,42,'43:42','Tàbí kí Á fi ohun tí A ṣe ní ìlérí fún wọn hàn ọ́, dájúdájú Àwa jẹ́ Alágbára lórí wọn.');
INSERT INTO "aykyuni" VALUES(43,43,'43:43','Nítorí náà, di ohun tí A fi ránṣẹ́ sí ọ mú ṣinṣin. Dájúdájú ìwọ wà lójú ọ̀nà tààrà (’Islām).');
INSERT INTO "aykyuni" VALUES(43,44,'43:44','Dájúdájú al-Ƙur’ān ni tírà ìrántí fún ìwọ àti ìjọ rẹ. Láìpẹ́ wọ́n máa bi yín léèrè (nípa rẹ̀).');
INSERT INTO "aykyuni" VALUES(43,45,'43:45','Bèèrè wò lọ́wọ́ àwọn tí A rán níṣẹ́ ṣíwájú rẹ nínú àwọn Òjíṣẹ́ Wa (pé): “Ǹjẹ́ A sọ àwọn kan di ọlọ́hun tí wọn yóò máa jọ́sìn fún lẹ́yìn Àjọkẹ́-ayé bí?”1 ');
INSERT INTO "aykyuni" VALUES(43,46,'43:46','A kúkú fi àwọn àmì Wa rán (Ànábì) Mūsā níṣẹ́ sí Fir‘aon àti àwọn ìjòyè rẹ̀. Ó sì sọ pé: “Dájúdájú èmi ni Òjíṣẹ́ Olúwa gbogbo ẹ̀dá.”');
INSERT INTO "aykyuni" VALUES(43,47,'43:47','Ṣùgbọ́n nígbà tí ó mú àwọn àmì Wa wá bá wọn, ńṣe ni wọ́n ń fi àwọn àmì náà rẹ́rìn-ín nígbà náà.');
INSERT INTO "aykyuni" VALUES(43,48,'43:48','A kò níí fi àmì kan hàn wọ́n àyàfi kí ó tóbi ju irú rẹ̀ (tó ṣíwájú). A sì fi ìyà jẹ wọ́n nítorí kí wọ́n lè ṣẹ́rí padà (síbi òdodo).');
INSERT INTO "aykyuni" VALUES(43,49,'43:49','Wọ́n sì wí pé: “Ìwọ òpìdán, pe Olúwa rẹ fún wa nítorí àdéhùn tí Ó ṣe pẹ̀lú rẹ. Dájúdájú àwa máa tẹ̀lé ìmọ̀nà Rẹ̀.”');
INSERT INTO "aykyuni" VALUES(43,50,'43:50','Ṣùgbọ́n nígbà tí A bá mú ìyà kúrò fún wọn, nígbà náà ni wọ́n tún ń yẹ àdéhùn.');
INSERT INTO "aykyuni" VALUES(43,51,'43:51','Fir‘aon sì pèpè láààrin àwọn ènìyàn rẹ̀, ó wí pé: “Ẹ̀yin ènìyàn mi, ṣé tèmi kọ́ ni ìjọba Misrọ àti àwọn odò wọ̀nyí tó ń ṣàn nísàlẹ̀ (ọ̀dọ̀) mi? Ṣé ẹ kò ríran ni?');
INSERT INTO "aykyuni" VALUES(43,52,'43:52','Tàbí ṣé èmi kò lóore ju èyí tí ó jẹ́ ọ̀lẹ ẹni yẹpẹrẹ, tí ó fẹ́ẹ̀ má lè dá ọ̀rọ̀ sọ yanjú?');
INSERT INTO "aykyuni" VALUES(43,53,'43:53','Kí ni kò jẹ́ kí wọ́n fún un ní àwọn ẹ̀gbà-ọwọ́ góòlù tàbí (kí ni kò jẹ́ kí) àwọn mọlāika wá pẹ̀lú rẹ̀, kí wọ́n jẹ́ alábàárìn (tí wọn yóò máa jẹ́rìí rẹ̀)?”');
INSERT INTO "aykyuni" VALUES(43,54,'43:54','Nítorí náà, ó sọ àwọn ènìyàn rẹ̀ di òpè. Wọ́n sì tẹ̀lé e. Dájúdájú wọ́n jẹ́ ìjọ òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(43,55,'43:55','Nígbà tí wọ́n wá ìbínú Wa, A gba ẹ̀san (ìyà) lára wọn. Nítorí náà, A tẹ gbogbo wọn rì (sínú agbami odò).');
INSERT INTO "aykyuni" VALUES(43,56,'43:56','A sì ṣe wọ́n ní ìjọ aṣíwájú (nínú ìparun) àti àpẹ̀ẹrẹ (fèyíkọ́gbọ́n) fún àwọn ẹni ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(43,57,'43:57','Nígbà tí àwọn ọ̀ṣẹbọ fi ọmọ Mọryam ṣe àpẹ̀ẹrẹ (fún òrìṣà wọn), nígbà náà ni àwọn ènìyàn rẹ bá ń fi (àpẹ̀ẹrẹ náà) rẹ́rìn-ín.');
INSERT INTO "aykyuni" VALUES(43,58,'43:58','Wọ́n sì wí pé: “Ṣé àwọn òrìṣà wa l’ó lóore jùlọ ni tàbí òun? Wọn kò wulẹ̀ fi ṣàpẹ̀ẹrẹ fún ọ bí kò ṣe (fún) àtakò. Àní sẹ́, ìjọ oníyànjíjà ni wọ́n.');
INSERT INTO "aykyuni" VALUES(43,59,'43:59','Kí ni òun bí kò ṣe ẹrúsìn kan tí A ṣe ìdẹ̀ra fún. A sì ṣe é ní àpẹ̀ẹrẹ rere fún àwọn ọmọ ’Isrọ̄’īl.1 ');
INSERT INTO "aykyuni" VALUES(43,60,'43:60','Tí ó bá jẹ́ pé A bá fẹ́, A ìbá máa fi àwọn mọlāika rọ́pò yín lórí ilẹ̀.');
INSERT INTO "aykyuni" VALUES(43,61,'43:61','Dájúdájú òun ni ìmọ̀ (tàbí àmì fún ìsúnmọ́) Àkókò náà. Nítorí náà, ẹ kò gbọdọ̀ ṣeyèméjì lórí rẹ̀. Kí ẹ sì tẹ̀lé mi. Èyí ni ọ̀nà tààrà.1 ');
INSERT INTO "aykyuni" VALUES(43,62,'43:62','Ẹ má ṣe jẹ́ kí Èṣù ṣẹ yín lórí (kúrò lójú ọ̀nà tààrà náà); dájúdájú òun ni ọ̀tá pọ́nńbélé fún yín.');
INSERT INTO "aykyuni" VALUES(43,63,'43:63','Nígbà tí (Ànábì) ‘Īsā dé pẹ̀lú àwọn ẹ̀rí tó yanjú, ó sọ pé: “Dájúdájú mo dé wá ba yín pẹ̀lú ọgbọ́n ìjìnlẹ̀. (Mo sì dé) nítorí kí n̄g lè ṣe àlàyé apá kan èyí tí ẹ̀ ń yapa-ẹnu lórí rẹ̀ fún yín. Nítorí náà, ẹ bẹ̀rù Allāhu, kí ẹ sì tẹ̀lé mi.');
INSERT INTO "aykyuni" VALUES(43,64,'43:64','Dájúdájú Allāhu ni Olúwa mi àti Olúwa yín. Nítorí náà, ẹ jọ́sìn fún Un. Èyí ni ọ̀nà tààrà.”');
INSERT INTO "aykyuni" VALUES(43,65,'43:65','Àwọn ìjọ (rẹ̀) sì yapa (ẹ̀sìn ’Islām) láààrin ara wọn. Nítorí náà, ègbé ni fún àwọn tó ṣàbòsí ní ọjọ́ ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(43,66,'43:66','Ṣé wọ́n ń retí kiní kan bí kò ṣe Àkókò náà; tí ó máa dé bá wọn ní òjijì; wọn kò sì níí fura?');
INSERT INTO "aykyuni" VALUES(43,67,'43:67','Àwọn ọ̀rẹ́ àyò ní ọjọ́ yẹn, apá kan wọn yóò jẹ́ ọ̀tá fún apá kan àyàfi àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(43,68,'43:68','Ẹ̀yin ẹrúsìn Mi, kò sí ìbẹ̀rù fún yín ní òní. Ẹ̀yin kò sì níí banújẹ́.');
INSERT INTO "aykyuni" VALUES(43,69,'43:69','(Àwọn ni) àwọn tó gbàgbọ́ nínú àwọn āyah Wa, tí wọ́n sì jẹ́ mùsùlùmí (olùjuwọ́-jusẹ̀ sílẹ̀ fún Allāhu).');
INSERT INTO "aykyuni" VALUES(43,70,'43:70','Ẹ wọ inú Ọgbà Ìdẹ̀ra, ẹ̀yin àti àwọn ìyàwó yín; kí wọ́n máa dun yín nínú.');
INSERT INTO "aykyuni" VALUES(43,71,'43:71','Wọn yóò máa gbé àwọn àwo góòlù àti àwọn ife ìmumi káà kiri ọ̀dọ̀ wọn. Ohun tí ẹ̀mí ń fẹ́ àti (ohun tí) ojú yóò máa dúnnú sí wà nínú rẹ̀. Olùṣegbére sì ni yín nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(43,72,'43:72','Ìyẹn ni Ọgbà Ìdẹ̀ra tí A jogún rẹ̀ fún yín nítorí ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(43,73,'43:73','Èso púpọ̀ wà fún yín nínú rẹ̀. Ẹ̀yin yó sì máa jẹ nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(43,74,'43:74','Dájúdájú àwọn ẹlẹ́ṣẹ̀ ni olùṣegbére nínú ìyà iná Jahanamọ.');
INSERT INTO "aykyuni" VALUES(43,75,'43:75','A kò níí gbé e fúyẹ́ fún wọn. Wọn yó sì sọ̀rètí nù nínú Iná.');
INSERT INTO "aykyuni" VALUES(43,76,'43:76','A kò ṣàbòsí sí wọn, ṣùgbọ́n àwọn ni wọ́n jẹ́ alábòsí.');
INSERT INTO "aykyuni" VALUES(43,77,'43:77','Wọn yóò pe (mọlāika kan) pé: “Mọ̄lik (ẹ̀ṣọ́ Iná), jẹ́ kí Olúwa rẹ pa wá ráúráú.” (Mọ̄lik) yóò sọ pé: “Dájúdájú ẹ̀yin yóò máa gbé inú rẹ̀ ni.”');
INSERT INTO "aykyuni" VALUES(43,78,'43:78','Dájúdájú A ti mú òdodo wá ba yín, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ yin kórira òdodo.');
INSERT INTO "aykyuni" VALUES(43,79,'43:79','Tàbí àwọn (aláìgbàgbọ́) ti pinnu ọ̀rọ̀ kan ni? Dájúdájú Àwa náà ń pinnu ọ̀rọ̀.');
INSERT INTO "aykyuni" VALUES(43,80,'43:80','Tàbí wọ́n ń lérò pé Àwa kò gbọ́ ọ̀rọ̀ àṣírí wọn àti ọ̀rọ̀ ìkọ̀kọ̀ wọn? Bẹ́ẹ̀ ni (À ń gbọ́ ọ); àwọn Òjíṣẹ́ Wa wà ní ọ̀dọ̀ wọn, tí wọ́n ń ṣe àkọsílẹ̀ (ọ̀rọ̀ wọn).');
INSERT INTO "aykyuni" VALUES(43,81,'43:81','Sọ pé: “Kò sí ọmọ kan fún Àjọké ayé. Nítorí náà, èmi ni ẹni àkọ́kọ́ nínú àwọn olùjọ́sìn (fún Allāhu ní àsìkò tèmi).1 ');
INSERT INTO "aykyuni" VALUES(43,82,'43:82','Mímọ́ ni fún Olúwa àwọn sánmọ̀ àti ilẹ̀, Olúwa Ìtẹ́-ọlá tayọ irọ́ tí wọ́n ń pa (mọ́ Ọn).');
INSERT INTO "aykyuni" VALUES(43,83,'43:83','Nítorí náà, fi wọ́n sílẹ̀ kí wọ́n máa sọ ìsọkúsọ (wọn), kí wọ́n sì máa ṣeré wọn lọ títí wọn yóò fi pàdé ọjọ́ wọn, tí À ń ṣe ní àdéhùn fún wọn.');
INSERT INTO "aykyuni" VALUES(43,84,'43:84','(Allāhu) Òun ni Ọlọ́hun tí wọ́n ń jọ́sìn fún ní ọ̀nà òdodo nínú sánmọ̀. Òun náà sì ni Ọlọ́hun tí wọ́n gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo lórí ilẹ̀. Òun ni Ọlọ́gbọ́n, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(43,85,'43:85','Ìbùkún ni fún Ẹni tí Ó ni ìjọba àwọn sánmọ̀ àti ilẹ̀ àti ohunkóhun tó wà láààrin méjèèjì. Ọ̀dọ̀ Rẹ̀ sì ni ìmọ̀ Àkókò náà wà. Ọ̀dọ̀ Rẹ̀ sì ni wọ́n máa da yín padà sí.');
INSERT INTO "aykyuni" VALUES(43,86,'43:86','Àwọn tí wọ́n ń pè lẹ́yìn Rẹ̀, wọn kò ní ìkápá ìṣìpẹ̀ àyàfi ẹni tí ó bá jẹ́rìí sí òdodo (kalmọtuṣ-ṣahaadah), tí wọ́n sì nímọ̀ (rẹ̀).1 ');
INSERT INTO "aykyuni" VALUES(43,87,'43:87','Àti pé tí o bá bi wọ́n léèrè pé: “Ta ni Ó dá wọn?”, dájúdájú wọn á wí pé: “Allāhu ni.” Nítorí náà, báwo ni wọ́n ṣe ń ṣẹ́rí wọn kúrò níbi òdodo?');
INSERT INTO "aykyuni" VALUES(43,88,'43:88','Ọ̀rọ̀ (tí Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a - ń sọ fún Allāhu ni pé): “Olúwa Mi, dájúdájú àwọn wọ̀nyí ni ìjọ tí kò gbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(43,89,'43:89','Nítorí náà, ṣàmójú kúrò fún wọn, kí ó sì sọ pé: “Àlàáfíà!” Láìpẹ́ wọ́n máa mọ̀.');
INSERT INTO "aykyuni" VALUES(44,1,'44:1','Hā mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(44,2,'44:2','(Allāhu) fi Tírà tó ń yanjú ọ̀rọ̀ ẹ̀dá búra.');
INSERT INTO "aykyuni" VALUES(44,3,'44:3','Dájúdájú Àwa sọ̀ ọ́ kalẹ̀ nínú òru ìbùkún.Dájúdájú Àwa ń jẹ́ Olùkìlọ̀. ');
INSERT INTO "aykyuni" VALUES(44,4,'44:4','Nínú òru náà ni wọ́n ti máa yanjú gbogbo ọ̀rọ̀ tí kò níí tàsé (lórí ẹ̀dá).');
INSERT INTO "aykyuni" VALUES(44,5,'44:5','Àṣẹ kan ni láti ọ̀dọ̀ Wa. Dájúdájú Àwa l’À ń rán àwọn Òjíṣẹ́ níṣẹ́.');
INSERT INTO "aykyuni" VALUES(44,6,'44:6','Ìkẹ́ kan ni láti ọ̀dọ̀ Olúwa rẹ. Dájúdájú Allāhu, Òun ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(44,7,'44:7','Olúwa àwọn sánmọ̀ àti ilẹ̀ àti ohunkóhun tí ń bẹ láààrin méjèèjì tí ẹ̀yin bá jẹ́ alámọ̀dájú.');
INSERT INTO "aykyuni" VALUES(44,8,'44:8','Kò sí ọlọ́hun kan tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Ó ń sọ ẹ̀dá di alààyè. Ó sì ń sọ ẹ̀dá di òkú. Olúwa yín àti Olúwa àwọn bàbá yín, àwọn ẹni àkọ́kọ́.');
INSERT INTO "aykyuni" VALUES(44,9,'44:9','Síbẹ̀, wọ́n sì wà nínú iyèméjì, tí wọ́n ń ṣeré.');
INSERT INTO "aykyuni" VALUES(44,10,'44:10','Nítorí náà, máa retí ọjọ́ tí sánmọ̀ yóò mú èéfín pọ́nńbélé wá.');
INSERT INTO "aykyuni" VALUES(44,11,'44:11','Ó máa bo àwọn ènìyàn mọ́lẹ̀. Èyí ni ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(44,12,'44:12','(Àwọn ènìyàn yóò wí pé): Olúwa wa, gbé ìyà náà kúrò fún wa, dájúdájú àwa yóò gbàgbọ́ ní òdodo.');
INSERT INTO "aykyuni" VALUES(44,13,'44:13','Báwo ni ìrántí ṣe lè wúlò fún wọn (lásìkò ìyà)? Òjíṣẹ́ pọ́nńbélé kúkú ti dé bá wọn.');
INSERT INTO "aykyuni" VALUES(44,14,'44:14','náà, wọ́n gbúnrí kúrò ní ọ̀dọ̀ rẹ̀. Wọ́n sì wí pé: “Wèrè tí àwọn ènìyàn kan ń kọ́ ní ẹ̀kọ́ ni.”');
INSERT INTO "aykyuni" VALUES(44,15,'44:15','Dájúdájú Àwa máa gbé ìyà náà kúrò fún ìgbà díẹ̀. Dájúdájú ẹ̀yin yóò tún padà (sínú àìgbàgbọ́).');
INSERT INTO "aykyuni" VALUES(44,16,'44:16','Ọjọ́ tí A óò gbá (wọn mú) ní ìgbámú tó tóbi jùlọ; dájúdájú Àwa yóò gba ẹ̀san ìyà (lára wọn).');
INSERT INTO "aykyuni" VALUES(44,17,'44:17','Dájúdájú A dán àwọn ènìyàn Fir‘aon wò ṣíwájú wọn. Òjíṣẹ́ alápọ̀n-ọ́nlé sì dé wá bá wọn.');
INSERT INTO "aykyuni" VALUES(44,18,'44:18','(Ó sọ pé): “Ẹ kó àwọn ẹrúsìn Allāhu lé mi lọ́wọ́. Dájúdájú èmi ni Òjíṣẹ́ olùfọkàntán fún yín.');
INSERT INTO "aykyuni" VALUES(44,19,'44:19','Ẹ má ṣe ṣègbéraga sí Allāhu. Dájúdájú èmi ti mú ẹ̀rí pọ́nńbélé wá ba yín.');
INSERT INTO "aykyuni" VALUES(44,20,'44:20','Dájúdájú Èmi sá di Olúwa mi àti Olúwa yín pé kí ẹ má ṣe sọ mí ní òkò.');
INSERT INTO "aykyuni" VALUES(44,21,'44:21','Tí ẹ kò bá sì gbà mí gbọ́, ẹ fi mí sílẹ̀ jẹ́.”');
INSERT INTO "aykyuni" VALUES(44,22,'44:22','Nítorí náà, ó pe Olúwa rẹ̀ pé dájúdájú àwọn wọ̀nyí ni ìjọ ẹlẹ́sẹ̀.');
INSERT INTO "aykyuni" VALUES(44,23,'44:23','(Allāhu sọ pé): “Mú àwọn ẹrúsìn Mi rìn ní alẹ́ (nítorí pé) wọn yóò tọ̀ yín lẹ́yìn.');
INSERT INTO "aykyuni" VALUES(44,24,'44:24','Kí o sì fi agbami odò sílẹ̀ ní yíyanu sílẹ̀. Dájúdájú àwọn ni ọmọ ogun tí A máa tẹ̀rì sínú agbami.');
INSERT INTO "aykyuni" VALUES(44,25,'44:25','Mélòó mélòó nínú àwọn ọgbà oko àti odò ìṣẹ́lẹ̀rú tí wọ́n fi sílẹ̀ (lẹ́yìn ìparun wọn).');
INSERT INTO "aykyuni" VALUES(44,26,'44:26','Àti àwọn irúgbìn pẹ̀lú àyè àpọ́nlé (tí wọ́n fi sílẹ̀).');
INSERT INTO "aykyuni" VALUES(44,27,'44:27','Àti ìdẹ̀ra tí wọ́n ń gbádùn nínú rẹ̀ (ṣíwájú ìparun wọn).');
INSERT INTO "aykyuni" VALUES(44,28,'44:28','Báyẹn (ni ọ̀rọ̀ wọn ṣe rí). A sì jogún (ìlú) wọ́n fún ìjọ ènìyàn mìíràn.');
INSERT INTO "aykyuni" VALUES(44,29,'44:29','Nígbà náà, sánmọ̀ àti ilẹ̀ kò sunkún wọn. Wọn kò sì fi ìyà wọn falẹ̀.');
INSERT INTO "aykyuni" VALUES(44,30,'44:30','Dájúdájú A gba àwọn ọmọ ’Isrọ̄’īl là nínú ìyà yẹpẹrẹ.');
INSERT INTO "aykyuni" VALUES(44,31,'44:31','(A là wọ́n) lọ́wọ́ Fir‘aon. Dájúdájú ó jẹ́ onígbèéraga. Ó sì wà nínú àwọn alákọyọ.');
INSERT INTO "aykyuni" VALUES(44,32,'44:32','A kúkú ṣà wọ́n lẹ́ṣà lórí àwọn ẹ̀dá (àsìkò tiwọn) pẹ̀lú ìmọ̀ (tí a mọ̀ nípa wọn).1 ');
INSERT INTO "aykyuni" VALUES(44,33,'44:33','A sì fún wọn ní àwọn àmì tí àdánwò pọ́nńbélé wà nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(44,34,'44:34','Dájúdájú àwọn wọ̀nyí ń wí pé:');
INSERT INTO "aykyuni" VALUES(44,35,'44:35','“Kò sí ikú kan àyàfi ikú àkọ́kọ́ (tí ó pa wá nílé ayé). Wọn kò sì níí gbé wa dìde.');
INSERT INTO "aykyuni" VALUES(44,36,'44:36','(Bí bẹ́ẹ̀ kọ́), ẹ mú àwọn bàbá wa wá tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(44,37,'44:37','Ṣé àwọn ni wọ́n lóore jùlọ ni tàbí àwọn ènìyàn Tubba‘u àti àwọn tó ṣíwájú wọn? A pa wọ́n rẹ́; dájúdájú wọ́n jẹ́ ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(44,38,'44:38','A kò ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ àti ohunkóhun tí ń bẹ láààrin méjèèjì pẹ̀lú eré ṣíṣe.');
INSERT INTO "aykyuni" VALUES(44,39,'44:39','A kò dá àwọn méjèèjì bí kò ṣe pẹ̀lú òdodo, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò mọ̀.');
INSERT INTO "aykyuni" VALUES(44,40,'44:40','Dájúdájú ọjọ́ òpínyà (ìyẹn, ọjọ́ àjíǹde) ni àkókò àdéhùn fún gbogbo wọn pátápátá.');
INSERT INTO "aykyuni" VALUES(44,41,'44:41','Ní ọjọ́ tí ọ̀rẹ́ kan kò níí fi kiní kan rọ ọ̀rẹ́ kan lọ́rọ̀. A kò sì níí ràn wọ́n lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(44,42,'44:42','Àyàfi ẹni tí Allāhu bá kẹ́. Dájúdájú Allāhu, Òun ni Alágbára, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(44,43,'44:43','Dájúdájú igi zaƙūm');
INSERT INTO "aykyuni" VALUES(44,44,'44:44','ni oúnjẹ ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(44,45,'44:45','Ó dà bí ògéré epo gbígbóná tí ń hó nínú ikùn');
INSERT INTO "aykyuni" VALUES(44,46,'44:46','(tó) dà bí híhó omi tó gbóná gan-an.');
INSERT INTO "aykyuni" VALUES(44,47,'44:47','Ẹ mú un. Kí ẹ wọ́ ọ sáàrin gbùngbùn inú iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(44,48,'44:48','Lẹ́yìn náà, ẹ rọ́ ìyà olómi gbígbóná lé e lórí.');
INSERT INTO "aykyuni" VALUES(44,49,'44:49','Tọ́ ọ wò (ṣebí) dájúdájú ìwọ ni alágbára, alápọ̀n-ọ́nlé (gẹ́gẹ́ bí ó ṣe pe ara rẹ).');
INSERT INTO "aykyuni" VALUES(44,50,'44:50','Dájúdájú (ìyà) èyí ni n̄ǹkan tí ẹ̀ ń ṣeyèméjì nípa rẹ̀!');
INSERT INTO "aykyuni" VALUES(44,51,'44:51','Dájúdájú àwọn olùbẹ̀rù Allāhu yóò wà ní àyè ìfàyàbalẹ̀.');
INSERT INTO "aykyuni" VALUES(44,52,'44:52','(Wọn yóò wà) nínú àwọn Ọgbà Ìdẹ̀ra pẹ̀lú àwọn omi ìṣẹ́lẹ̀rú (ní ìsàlẹ̀ rẹ̀).');
INSERT INTO "aykyuni" VALUES(44,53,'44:53','Wọn yóò máa wọ aṣọ àrán fẹ́lẹ́fẹ́lẹ́ àti àrán tó nípọn; wọn yó sì máà kọjú síra wọn (sọ̀rọ̀).');
INSERT INTO "aykyuni" VALUES(44,54,'44:54','Báyẹn (ni ọ̀rọ̀ wọn yó ṣe rí). A sì máa fi àwọn obìnrin ẹlẹ́yinjú-ẹgẹ́ ṣe ìyàwó fún wọn.');
INSERT INTO "aykyuni" VALUES(44,55,'44:55','Wọn yóò máa bèèrè fún gbogbo n̄ǹkan eléso nínú (Ọgbà Ìdẹ̀ra) lẹ́ni ìfàyàbalẹ̀.');
INSERT INTO "aykyuni" VALUES(44,56,'44:56','Wọn kò níí tọ́ ikú wò níbẹ̀ àyàfi ikú àkọ́kọ́ (tí wọ́n ti kú nílé ayé). (Allāhu) sì máa ṣọ́ wọn níbi ìyà iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(44,57,'44:57','Ó jẹ́ oore àjùlọ láti ọ̀dọ̀ Olúwa rẹ. Ìyẹn ni èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(44,58,'44:58','Nítorí náà, A ṣe (al-Ƙur’ān) ní ìrọ̀rùn lórí ahọ́n rẹ̀ nítorí kí wọ́n lè lo ìrántí.');
INSERT INTO "aykyuni" VALUES(44,59,'44:59','Nítorí náà, máa retí Dájúdájú àwọn náà ń retí. ');
INSERT INTO "aykyuni" VALUES(45,1,'45:1','Hā mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(45,2,'45:2','Tírà náà sọ̀kalẹ̀ díẹ̀ díẹ̀láti ọ̀dọ̀ Allāhu, Alágbára, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(45,3,'45:3','Dájúdájú nínú àwọn sánmọ̀ àti ilẹ̀ àwọn àmì wà (nínú wọn) fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(45,4,'45:4','Àwọn àmì wà nínú ìṣẹ̀dá yín àti ohun tí Allāhu ń fọ́nká (sórí ilẹ̀) nínú àwọn n̄ǹkan abẹ̀mí; (àmì wà nínú wọn) fún ìjọ tó ní àmọ̀dájú.1 ');
INSERT INTO "aykyuni" VALUES(45,5,'45:5','(Nínú) ìtẹ̀léǹtẹ̀lé òru àti ọ̀sán, àti ohun tí Allāhu ń sọ̀kalẹ̀ ní arísìkí, tí Ó ń fi sọ ilẹ̀ di àyè lẹ́yìn tó ti kú àti ìyípadà atẹ́gùn, àwọn àmì tún wà (nínú wọn) fún ìjọ tó ní làákàyè.');
INSERT INTO "aykyuni" VALUES(45,6,'45:6','Ìwọ̀nyí ni àwọn āyah Allāhu tí À ń ké (ní kéú) fún ọ pẹ̀lú òdodo. Nítorí náà, ọ̀rọ̀ wo lẹ́yìn (ọ̀rọ̀) Allāhu àti àwọn àmì Rẹ̀ ni wọn yóò tún gbàgbọ́?');
INSERT INTO "aykyuni" VALUES(45,7,'45:7','Ègbé ni fún gbogbo ògbólógbòó òpùrọ́, ògbólógbòó ẹlẹ́ṣẹ̀,');
INSERT INTO "aykyuni" VALUES(45,8,'45:8','tí ó ń gbọ́ àwọn āyah Allāhu, tí wọ́n ń ké e fún un, lẹ́yìn náà, tí ó takú (sórí irọ́) ní ti ìgbéraga bí ẹni pé kò gbọ́ ọ. Fún un ní ìró ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(45,9,'45:9','Nígbà tí ó bá nímọ̀ nípa kiní kan nínú àwọn āyah Wa (tí òun náà jẹ́rìí sí òdodo rẹ̀), ó máa mú un ní n̄ǹkan yẹ̀yẹ́. Àwọn wọ̀nyẹn ni ìyà tí í yẹpẹrẹ ẹ̀dá ń bẹ fún.');
INSERT INTO "aykyuni" VALUES(45,10,'45:10','Iná Jahanamọ wà níwájú wọn. Ohun tí wọ́n ṣe níṣẹ́ àti ohun tí wọ́n mú ní alátìlẹ́yìn lẹ́yìn Allāhu kò sì níí rọ̀ wọ́n lọ́rọ̀ kan níbi ìyà. Ìyà ńlá sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(45,11,'45:11','Èyí ni ìmọ̀nà. Àwọn tó sì ṣàì gbàgbọ́ nínú àwọn āyah Olúwa wọn, ìyà ẹlẹ́gbin ẹlẹ́ta-eléro wà fún wọn.');
INSERT INTO "aykyuni" VALUES(45,12,'45:12','Allāhu ni Ẹni tí Ó rọ agbami odò fún yín nítorí kí àwọn ọkọ̀ ojú-omi lè rìn nínú rẹ̀ pẹ̀lú àṣẹ Rẹ̀ àti nítorí kí ẹ tún lè wá nínú oore Rẹ̀ àti nítorí kí ẹ lè dúpẹ́ (fún Un).');
INSERT INTO "aykyuni" VALUES(45,13,'45:13','Ó tún rọ ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ pátápátá fún yín láti ọ̀dọ̀ Rẹ̀. Dájúdájú àwọn àmì wà nínú ìyẹn fún ìjọ tó ní àròjinlẹ̀.');
INSERT INTO "aykyuni" VALUES(45,14,'45:14','Sọ fún àwọn tó gbàgbọ́ ní òdodo pé kí wọ́n ṣàmójú kúrò fún àwọn tí kò retí àwọn ọjọ́ (tí) Allāhu (yóò ṣe àrànṣe fún àwọn onígbàgbọ́ òdodo) nítorí kí Ó lè san ẹ̀san fún ìjọ kan nípa ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(45,15,'45:15','Ẹníkẹ́ni tí ó bá ṣe iṣẹ́ rere, fún ẹ̀mí ara rẹ̀ ni. Ẹnikẹ́ni tí ó bá sì ṣe iṣẹ́ aburú, fún ẹ̀mí ara rẹ̀ ni. Lẹ́yìn náà, ọ̀dọ̀ Olúwa yín ni wọn yóò da yín padà sí.');
INSERT INTO "aykyuni" VALUES(45,16,'45:16','Dájúdájú A fún àwọn ọmọ ’Isrọ̄’īl ní Tírà àti ìdájọ́ ṣíṣe àti ipò Ànábì. A sì ṣe arísìkí fún wọn nínú àwọn n̄ǹkan dáadáa. A sì ṣoore àjùlọ fún wọn lórí àwọn ẹ̀dá (àsìkò tiwọn).');
INSERT INTO "aykyuni" VALUES(45,17,'45:17','A tún fún wọn ní àwọn ẹ̀rí tó yanjú nípa ọ̀rọ̀ náà. Nítorí náà, wọn kò pín sí ìjọ ọ̀tọ̀ọ̀tọ̀ àfi lẹ́yìn tí ìmọ̀ (’Islām) dé bá wọn ní ti ìlara láààrin ara wọn (sí àwọn Ànábì). Dájúdájú Olúwa rẹ l’Ó máa ṣe ìdájọ́ láààrin wọn ní Ọjọ́ Àjíǹde nípa n̄ǹkan tí wọ́n ń yapa-ẹnu lórí rẹ̀.');
INSERT INTO "aykyuni" VALUES(45,18,'45:18','Lẹ́yìn náà, A fi ọ́ sí ojú ọ̀nà kan nínú ọ̀rọ̀ náà (ẹ̀sìn ’Islām). Nítorí náà, tẹ̀lé e. Kí o sì má ṣe tẹ̀lé ìfẹ́-inú àwọn tí kò nímọ̀.');
INSERT INTO "aykyuni" VALUES(45,19,'45:19','Dájúdájú wọn kò lè rọ̀ ọ́ lọ́rọ̀ kiní kan níbi ìyà ní ọ̀dọ̀ Allāhu. Àti pé dájúdájú àwọn alábòsí, apá kan wọn lọ̀rẹ́ apá kan. Allāhu sì ni Ọ̀rẹ́ àwọn olùbẹ̀rù (Rẹ̀).');
INSERT INTO "aykyuni" VALUES(45,20,'45:20','Èyí ni ìmọ̀ àmọ̀dájú àti ẹ̀rí fún àwọn ènìyàn. Ìmọ̀nà àti ìkẹ́ ni fún ìjọ tó ní àmọ̀dájú.');
INSERT INTO "aykyuni" VALUES(45,21,'45:21','Tàbí àwọn tó ń ṣe iṣẹ́ aburú lérò pé A máa ṣe wọ́n gẹ́gẹ́ bí (A ti ṣe) àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, kí ìṣẹ́mí ayé wọn àti ikú wọn rí bákan náà? Ohun tí wọ́n ń dá ní ẹjọ́, ó burú.');
INSERT INTO "aykyuni" VALUES(45,22,'45:22','Allāhu ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ pẹ̀lú òdodo àti nítorí kí Á lè san ẹ̀mí kọ̀ọ̀kan ní ẹ̀san n̄ǹkan tí ó ṣe níṣẹ́. A kò sì níí ṣe àbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(45,23,'45:23','Sọ fún mi nípa ẹni tí ó sọ ìfẹ́-inú rẹ̀ di ọlọ́hun rẹ̀, tí Allāhu sì ṣì í lọ́nà pẹ̀lú ìmọ̀ tí Ó sì fi èdídí dí ìgbọ́rọ̀ rẹ̀ àti ọkàn rẹ̀, tí Ó tún fi èbìbò bo ojú rẹ̀! Ta ni ó máa fi ọ̀nà mọ̀ ọ́n lẹ́yìn Allāhu? Ṣé ẹ kò níí lo ìrántí ni? ');
INSERT INTO "aykyuni" VALUES(45,24,'45:24','Wọ́n wí pé: “Kò sí ìṣẹ̀mí kan mọ́ bí kò ṣe ìṣẹ̀mí ayé wa yìí; à ń kú, a sì ń ṣẹ̀mí. Àti pé kò sí n̄ǹkan tó ń pa wá bí kò ṣe ìgbà.” Wọn kò sì ní ìmọ̀ kan nípa ìyẹn; wọn kò ṣe kiní kan bí kò ṣe pé wọ́n ń ro èròkérò.');
INSERT INTO "aykyuni" VALUES(45,25,'45:25','Àti pé nígbà tí wọ́n bá ń ké àwọn āyah Wa tó yanjú fún wọn, àwíjàre wọn kò jẹ́ kiní kan àyàfi kí wọ́n wí pé: “Ẹ mú àwọn bàbá wa wá tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(45,26,'45:26','Sọ pé: “Allāhu l’Ó ń sọ yín di alààyè. Lẹ́yìn náà, Ó máa sọ yín di òkú. Lẹ́yìn náà, Ó máa ko yín jọ fún Ọjọ́ Àjíǹde, kò sí iyèméjì nínú rẹ̀, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ ènìyàn ni kò mọ̀.');
INSERT INTO "aykyuni" VALUES(45,27,'45:27','Àti pé ti Allāhu ni ìjọba àwọn sánmọ̀ àti ilẹ̀. (Rántí) ọjọ́ tí Àkókò náà máa ṣẹlẹ̀; ọjọ́ yẹn ni àwọn òpùrọ́ máa ṣòfò.');
INSERT INTO "aykyuni" VALUES(45,28,'45:28','O sì máa rí gbogbo ìjọ kọ̀ọ̀kan lórí ìkúnlẹ̀. Wọn yó sì máa pe gbogbo ìjọ kọ̀ọ̀kan síbi ìwé iṣẹ́ wọn. Ní òní ni A óò san yín ní ẹ̀san ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(45,29,'45:29','Èyí ni ìwé àkọ́sílẹ̀ Wa, tí ó ń sọ òdodo nípa yín. Dájúdájú Àwa ń ṣe àkọsílẹ̀ ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(45,30,'45:30','Ní ti àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, Olúwa wọn yóò fi wọ́n sínú ìkẹ́ Rẹ̀. Ìyẹn ni èrèǹjẹ pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(45,31,'45:31','Ní ti àwọn tó sì ṣàì gbàgbọ́, (A óò bi wọ́n léèrè pé:) “Ǹjẹ́ wọn kì í ké àwọn āyah Mi fún yín bí?” Ṣùgbọ́n ẹ ṣègbéraga. Ẹ sì jẹ́ ìjọ ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(45,32,'45:32','Nígbà tí a bá sọ pé: “Dájúdájú àdéhùn Allāhu ni òdodo. Àti pé Àkókò náà, kò sí iyèméjì nínú rẹ̀.” Ẹ̀yin ń wí pé: “Àwa kò mọ ohun tí Àkókò náà jẹ́. Àwa kò sì rò ó sí kiní kan bí kò ṣe èròkérò; àwa kò sì ní àmọ̀dájú (nípa rẹ̀).”');
INSERT INTO "aykyuni" VALUES(45,33,'45:33','Àwọn aburú ohun tí wọ́n ṣe níṣẹ́ sì hàn sí wọn. Àti pé ohun tí wọ́n ń fi ṣe yẹ̀yẹ́ sì dìyà tó yí wọn po.');
INSERT INTO "aykyuni" VALUES(45,34,'45:34','Wọ́n sì máa sọ (fún wọn) pé: “Ní òní, Àwa yóò gbàgbé yín (sínú Iná) gẹ́gẹ́ bí ẹ̀yin náà ṣe gbàgbé ìpàdé ọjọ́ yín (òní) yìí. Iná sì ni ibùgbé yín. Àti pé kò níí sí àwọn olùrànlọ́wọ́ fún yín.”');
INSERT INTO "aykyuni" VALUES(45,35,'45:35','Báyẹn ni nítorí pé, ẹ sọ àwọn āyah Allāhu di n̄ǹkan yẹ̀yẹ́. Àti pé ìṣẹ̀mí ayé tàn yín jẹ. Nítorí náà, ní òní wọn kò níí mú wọn jáde kúrò nínú Iná. Wọn kò sì níí fún wọn ní àyè láti ṣe ohun tí wọn yóò fi rí ìyọ́nú Allāhu.');
INSERT INTO "aykyuni" VALUES(45,36,'45:36','Nítorí náà, gbogbo ẹyìn ń jẹ́ ti Allāhu, Olúwa àwọn sánmọ̀ àti Olúwa ilẹ̀, Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(45,37,'45:37','TiRẹ̀ ni títóbi nínú àwọn sánmọ̀ àti ilẹ̀. Òun sì ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(46,1,'46:1','Hā mīm. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)1 ');
INSERT INTO "aykyuni" VALUES(46,2,'46:2','Tírà náà sọ̀kalẹ̀ díẹ̀ díẹ̀ láti ọ̀dọ̀ Allāhu, Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(46,3,'46:3','A kò ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀, àti ohunkóhun tó wà láààrin méjèèjì bí kò ṣe pẹ̀lú òdodo àti fún gbèdéke àkókò kan. Àwọn tó ṣàì gbàgbọ́ yó sì máa gbúnrí kúrò níbi ohun tí A fi ṣèkìlọ̀ fún wọn.');
INSERT INTO "aykyuni" VALUES(46,4,'46:4','Sọ pé: “Ẹ sọ fún mi nípa n̄ǹkan tí ẹ̀ ń pè lẹ́yìn Allāhu; ẹ fi wọ́n hàn mí ná, kí ni wọ́n ṣẹ̀dá nínú (ohun tó wà lórí) ilẹ̀. Tàbí wọ́n ní ìpín kan (pẹ̀lú Allāhu) nínú (ìṣẹ̀dá) àwọn sánmọ̀? Ẹ mú Tírà kan wá fún mi tó ṣíwájú (al-Ƙur’ān) yìí tàbí orípa kan nínú ìmọ̀ tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(46,5,'46:5','Ta l’ó sì ṣìnà ju ẹni tí ó ń pè lẹ́yìn Allāhu, ẹni tí kò lè jẹ́ ìpè rẹ̀ títí di Ọjọ́ Àjíǹde! Àti pé wọn kò ní òye sí pípè tí wọ́n ń pè wọ́n.1 ');
INSERT INTO "aykyuni" VALUES(46,6,'46:6','Nígbà tí A bá sì kó àwọn ènìyàn jọ (fún Àjíǹde), àwọn òrìṣà yóò di ọ̀tá fún àwọn abọ̀rìṣà. Wọ́n sì máa tako ìjọ́sìn tí wọ́n ṣe fún wọn.');
INSERT INTO "aykyuni" VALUES(46,7,'46:7','Nígbà tí wọ́n bá ń ké áwọn āyah Wa tó yanjú fún wọn, àwọn tó ṣàì gbàgbọ́ yó sì máa sọ ìsọkúsọ sí òdodo nígbà tí ó dé bá wọn pé: “Èyí ni idán pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(46,8,'46:8','Tàbí wọ́n ń wí pé: “Ó dá (al-Ƙur’ān) hun fúnra rẹ̀ ni.” Sọ pé: “Tí mo bá hun ún fúnra mi, ẹ kò ní ìkápá kiní kan fún mi ní ọ̀dọ̀ Allāhu (níbi ìyà Rẹ̀). Òun ni Onímọ̀-jùlọ nípa ìsọkúsọ tí ẹ̀ ń sọ nípa rẹ̀. Ó (sì) tó ní Ẹlẹ́rìí láààrin èmi àti ẹ̀yin. Òun ni Aláforíjìn, Àṣàkẹ́-ọ̀run.”');
INSERT INTO "aykyuni" VALUES(46,9,'46:9','Sọ pé: “Èmi kì í ṣe àkọ́kọ́ nínú àwọn Òjíṣẹ́. Èmi kò sì mọ n̄ǹkan tí wọ́n máa ṣe fún èmi àti ẹ̀yin.Èmi kò tẹ̀lé kiní kan àyàfi ohun tí Wọ́n fi ránṣẹ́ sí mi ní ìmísí. Èmi kò sì jẹ́ kiní kan bí kò ṣe olùkìlọ̀ pọ́nńbélé.” ');
INSERT INTO "aykyuni" VALUES(46,10,'46:10','Sọ pé: “Ẹ sọ fún mi, tí ó bá jẹ́ pé láti ọ̀dọ̀ Allāhu ni (al-Ƙur’ān) ti wá, tí ẹ sì ṣàì gbàgbọ́ nínú rẹ̀, tí ẹlẹ́rìí kan nínú àwọn ọmọ ’Isrọ̄’īl sì jẹ́rìí lórí irú rẹ̀, tí ó sì gbà á gbọ́, (àmọ́) tí ẹ̀yin ṣègbéraga sí i, (ṣé ẹ ò ti ṣàbòsí báyẹn bí?). Dájúdájú Allāhu kò níí fi ọ̀nà mọ ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(46,11,'46:11','Àwọn tó ṣàì gbàgbọ́ wí fún àwọn tó gbàgbọ́ pé: “Tí ó bá jẹ́ pé al-Ƙur’ān jẹ́ oore ni, wọn kò níí ṣíwájú wa débẹ̀.” Nígbà tí àwọn ọ̀ṣẹbọ kò ti tẹ̀lé ìmọ̀nà rẹ̀, ni wọ́n ń wí pé: “Irọ́ ijọ́un ni èyí.”');
INSERT INTO "aykyuni" VALUES(46,12,'46:12','Tírà (Ànábì) Mūsā sì ti wá ṣíwájú rẹ̀, tí ó jẹ́ tírà tí wọ́n ń tẹ̀lé, ó sì jẹ́ ìkẹ́ (fún wọn. Al-Ƙur’ān) yìí tún ni Tírà kan tó ń jẹ́rìí sí òdodo ní èdè Lárúbáwá nítorí kí ó lè ṣèkìlọ̀ fún àwọn tó ṣàbòsí, (ó sì jẹ́) ìró ìdùnnú fún àwọn olùṣe-rere.');
INSERT INTO "aykyuni" VALUES(46,13,'46:13','Dájúdájú àwọn tó sọ pé: “Allāhu ni Olúwa wa”, lẹ́yìn náà, tí wọ́n dúró ṣinṣin nínú ẹ̀sìn, kò níí sí ìpáyà fún wọn. Wọn kò sì níí banújẹ́.');
INSERT INTO "aykyuni" VALUES(46,14,'46:14','Àwọn wọ̀nyẹn ni èrò inú Ọgbà Ìdẹ̀ra. Olùṣegbére ni wọ́n nínú rẹ̀. (Ó jẹ́) ẹ̀san nítorí ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(46,15,'46:15','A pa á ní àsẹ fún ènìyàn pé kí ó máa ṣe dáadáa sí àwọn òbí rẹ̀ méjèèjì. Ìyá rẹ̀ ní oyún rẹ̀ pẹ̀lú wàhálà. Ó sì bí i pẹ̀lú wàhálà. Oyún rẹ̀ àti gbígba ọmú lẹ́nu rẹ̀ jẹ́ ọgbọ̀n oṣù. (Ó sì ń tọ́ ọ) títí ó fi dàgbà dáadáa, tí ó fi di ọmọ ogójì ọdún, ó sì sọ pé: “Olúwa mi, fi mọ̀ mí kí n̄g máa dúpẹ́ ìdẹ̀ra Rẹ, èyí tí Ó fi ṣe ìdẹ̀ra fún mi àti fún àwọn òbí mi méjèèjì, kí n̄g sì máa ṣe iṣẹ́ rere, èyí tí O yọ́nú sí. Kí O sì ṣe rere fún mi lórí àwọn àrọ́mọdọ́mọ mi. Dájúdájú èmi ti ronú pìwàdà sí ọ̀dọ̀ Rẹ. Dájúdájú èmi sì wà nínú àwọn mùsùlùmí.”');
INSERT INTO "aykyuni" VALUES(46,16,'46:16','Àwọn wọ̀nyẹn ni àwọn tí A máa gba iṣẹ́ rere tí wọ́n ṣe níṣẹ́, A sì máa ṣe àmójúkúrò níbi àwọn aburú iṣẹ́ wọn; wọ́n máa wà nínú àwọn èrò inú Ọgbà Ìdẹ̀ra. (Ó jẹ́) àdéhùn òdodo tí À ń ṣe ní àdéhùn fún wọn.');
INSERT INTO "aykyuni" VALUES(46,17,'46:17','Ẹni tí ó sọ fún àwọn òbí rẹ̀ méjèèjì pé: “Ṣíọ̀ ẹ̀yin méjèèjì! Ṣé ẹ̀yin yóò máa ṣèlérí fún mi pé Wọn yóò mú mi jáde (láàyè láti inú sàréè), ṣebí àwọn ìran kan ti ré kọjá lọ ṣíwájú mi (tí Wọn kò tí ì mú wọn jáde láti inú sàréè wọn).” Àwọn (òbí rẹ̀) méjèèjì sì ń tọrọ ìgbàlà ní ọ̀dọ̀ Allāhu (fún ọmọ yìí. Wọ́n sì sọ pé): “Ègbé ni fún ọ! (O jẹ́) gbàgbọ́ ní òdodo. Dájúdájú àdéhùn Allāhu ni òdodo.” (Ọ̀mọ̀ náà sì) wí pé: “Èyí kò jẹ́ kiní kan bí kò ṣe àkọsílẹ̀ àlọ́ àwọn ẹni àkọ́kọ́.”');
INSERT INTO "aykyuni" VALUES(46,18,'46:18','Àwọn (aláìgbàgbọ́) wọ̀nyẹn ni àwọn tí ọ̀rọ̀ náà ti kò lé lórí nínú àwọn ìjọ kan tí ó ti ré kọjá ṣíwájú wọn nínú àwọn àlùjànnú àti ènìyàn. Dájúdájú wọ́n jẹ́ ẹni òfò.');
INSERT INTO "aykyuni" VALUES(46,19,'46:19','Àwọn ipò yóò wà fún ẹnì kọ̀ọ̀kan nípa iṣẹ́ tí wọ́n ṣe níṣẹ́. Àti pé (èyí rí bẹ́ẹ̀) nítorí kí (Allāhu) lè san wọ́n ní ẹ̀san iṣẹ́ wọn ní ẹ̀kúnrẹ́rẹ́. Àwa kò sì níí ṣàbòsí sí wọn.');
INSERT INTO "aykyuni" VALUES(46,20,'46:20','Ní ọjọ́ tí wọ́n máa darí àwọn tó ṣàì gbàgbọ́ kọ Iná, (wọ́n máa sọ fún wọn pé:) “Ẹ ti lo ìgbádùn yín tán nínú ìṣẹ̀mí ayé? Ẹ sì ti jẹ ìgbádùn ayé? Nítorí náà, ní òní, wọ́n máa san yín ní ẹ̀san àbùkù ìyà nítorí pé ẹ̀ ń ṣègbéraga ní orí ilẹ̀ láì lẹ́tọ̀ọ́ àti nítorí pé ẹ̀ ń ṣèbàjẹ́.”');
INSERT INTO "aykyuni" VALUES(46,21,'46:21','Ṣèrántí arákùnrin (ìjọ) ‘Ād. Nígbà tí ó ṣèkìlọ̀ fún àwọn ènìyàn rẹ̀ tó ń gbé nínú yanrìn tí atẹ́gùn kójọ bí òkè. Àwọn olùkìlọ̀ sì ti ré kọjá ṣíwájú rẹ̀ àti lẹ́yìn rẹ̀. (Ó sì sọ pé:) “Ẹ̀yin kò gbọdọ̀ jọ́sìn fún kiní kan àyàfi Allāhu. Dájúdájú èmi ń páyà ìyà ọjọ́ ńlá kan fún yín.”');
INSERT INTO "aykyuni" VALUES(46,22,'46:22','Wọ́n wí pé: “Ṣé ìwọ wá bá wa láti ṣẹ́ wa lórí kúrò níbi àwọn òrìṣà wa ni? Mú ohun tí ò ń ṣe ìlérí rẹ̀ fún wa wá tí ìwọ bá wà nínú àwọn olódodo.”');
INSERT INTO "aykyuni" VALUES(46,23,'46:23','Ó sọ pé: “Ìmọ̀ (nípa rẹ̀) wà ní ọ̀dọ̀ Allāhu nìkan ṣoṣo. Èmi yó sì jẹ́ ohun tí Wọ́n fi rán mi níṣẹ́ dé òpin fún yín, ṣùgbọ́n èmi ń ri ẹ̀yin ní ìjọ aláìmọ̀kan.”');
INSERT INTO "aykyuni" VALUES(46,24,'46:24','Nígbà tí wọ́n rí ìyà náà ní ẹ̀ṣújò rẹgẹdẹ, tó ń wọ́ bọ̀ wá sínú àwọn kòtò ìlú wọn, wọ́n wí pé: “Èyí ni ẹ̀ṣújò rẹgẹdẹ, tí ó máa rọ̀jò fún wa.” Kò sì rí bẹ́ẹ̀, ohun tí ẹ̀ ń wá pẹ̀lú ìkánjú ni. Atẹ́gùn tí ìyà ẹlẹ́ta-eléro wà nínú rẹ̀ ni.');
INSERT INTO "aykyuni" VALUES(46,25,'46:25','Ó ń pa gbogbo n̄ǹkan rẹ́ (ìyẹn nínú ìlú wọn) pẹ̀lú àṣẹ Olúwa rẹ̀. Nígbà náà, wọ́n di ẹni tí wọn kò rí mọ́ àfi àwọn ibùgbé wọn. Báyẹn ni A ṣe ń san ìjọ ẹlẹ́ṣẹ̀ ní ẹ̀san.');
INSERT INTO "aykyuni" VALUES(46,26,'46:26','A kúkú fún wọn ní ipò tí A kò fún ẹ̀yin. A sì fún wọn ní ìgbọ́rọ̀, àwọn ìríran àti àwọn ọkàn. Àmọ́ ìgbọ́rọ̀ wọn àti àwọn ìríran wọn pẹ̀lú àwọn ọkàn wọn kò rọ̀ wọ́n lọ́rọ̀ kan kan níbi ìyà nítorí pé wọ́n ń ṣe àtakò sí àwọn āyah Allāhu. Àti pé ohun tí wọ́n ń fi ṣe yẹ̀yẹ́ sì dìyà tó yí wọn po.');
INSERT INTO "aykyuni" VALUES(46,27,'46:27','Àwa kúkú ti pa rẹ́ nínú àwọn ìlú tí ó wà ní àyíká yín. Àwa sì ti ṣàlàyé àwọn āyah náà ní oríṣiríṣi ọ̀nà nítorí kí wọ́n lè ṣẹ́rí padà (síbi òdodo).');
INSERT INTO "aykyuni" VALUES(46,28,'46:28','Àwọn ọlọ́hun tí wọ́n sọ di ohun tí ó máa mú wọn súnmọ́ (ìgbàlà) lẹ́yìn Allāhu kò ṣe ràn wọ́n lọ́wọ́ mọ́? Rárá (kò lè sí àrànṣe fún wọn)! Wọ́n ti di ofò mọ́ wọn lọ́wọ́. Ìyẹn sì ni (ọlọ́hun) irọ́ wọn àti ohun tí wọ́n ń dá ní àdápa irọ́.');
INSERT INTO "aykyuni" VALUES(46,29,'46:29','(Rántí) nígbà tí A darí ìjọ kan nínú àwọn àlùjànnú sí ọ, tí wọ́n ń tẹ́tí gbọ́ al-Ƙur’ān. Nígbà tí wọ́n dé síbẹ̀, wọ́n sọ pé: “Ẹ dákẹ́ (fún al-Ƙur’ān).” Nígbà tí wọ́n sì parí (kíké rẹ̀ tán), wọ́n padà sí ọ̀dọ̀ ìjọ wọn, tí wọ́n ń ṣèkìlọ̀ (fún wọn).');
INSERT INTO "aykyuni" VALUES(46,30,'46:30','Wọ́n sọ pé: “Ẹ̀yin ìjọ wa, dájúdájú àwa gbọ́ (nípa) Tírà kan tí wọ́n sọ̀kalẹ̀ lẹ́yìn (Ànábì) Mūsā, tí ó ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó wá ṣíwájú rẹ̀, tí ó sì ń tọ́ni sí ọ̀nà òdodo àti ọ̀nà tààrà.');
INSERT INTO "aykyuni" VALUES(46,31,'46:31','Ẹ̀yin ìjọ wa, ẹ jẹ́ ìpè olùpèpè Allāhu. Kí ẹ sì gbà á gbọ́ ní òdodo. (Allāhu) yó sì forí àwọn ẹ̀ṣẹ̀ yín jìn yín. Ó sì máa gbà yín là kúrò nínú ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(46,32,'46:32','Ẹnikẹ́ni tí kò bá sì jẹ́pè olùpèpè Allāhu, kò lè mórí bọ́ mọ́ (Allāhu) lọ́wọ́ lórí ilẹ̀. Kò sì sí aláàbò kan fún un lẹ́yìn Allāhu. Àwọn wọ̀nyẹn sì wà nínú ìṣìnà pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(46,33,'46:33','Ṣé wọn kò wòye pé dájúdájú Allāhu, Ẹni tí Ó dá àwọn sánmọ̀ àti ilẹ̀, tí kò sì káàárẹ̀ nípa ìṣẹ̀dá wọn, (ṣé kò) ní agbára láti sọ àwọn òkú di alààyè ni? Bẹ́ẹ̀ ni, (Ó lágbára láti ṣe bẹ́ẹ̀). Dájúdájú Ó ń jẹ́ Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(46,34,'46:34','(Rántí) ọjọ́ tí wọ́n máa darí àwọn tó ṣàì gbàgbọ́ kọ Iná, (wọ́n sì máa sọ fún wọn pé:) “Ṣé èyí kì í ṣe òdodo bí?” Wọn yóò wí pé: “Bẹ́ẹ̀ ni, (òdodo ni) Olúwa wa.” (Allāhu máa) sọ pé: “Nítorí náà, ẹ tọ́ ìyà wò nítorí pé, ẹ̀yin jẹ́ aláìgbàgbọ́.”');
INSERT INTO "aykyuni" VALUES(46,35,'46:35','Nítorí náà, ṣe sùúrù gẹ́gẹ́ bí àwọn onípinnu ọkàn nínú àwọn Òjíṣẹ́ ti ṣe sùúrù. Má ṣe bá wọn wá ìyà pẹ̀lú ìkánjú. Ní ọjọ́ tí wọ́n bá rí ohun tí À ń ṣe ní àdéhùn fún wọn, wọn yóò dà bí ẹni pé wọn kò gbé ilé ayé (yìí) tayọ àkókò kan nínú ọ̀sán. Ìkéde (ẹ̀sìn nìyí fún wọn). Ta ni ó sì máa parun bí kò ṣe ìjọ òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(47,1,'47:1','Àwọn tó ṣàì gbàgbọ́, tí wọ́n tún ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu, (Allāhu) máa sọ iṣẹ́ wọn di òfò.');
INSERT INTO "aykyuni" VALUES(47,2,'47:2','Àwọn tó gbàgbọ́ ní òdodo, tí wọ́n ṣe àwọn iṣẹ́ rere, tí wọ́n tún gbàgbọ́ nínú ohun tí wọ́n sọ̀kalẹ̀ fún (Ànábì) Muhammad - kí ìkẹ́ àti ọlà Allāhu máa bá a -, òhun sì ni òdodo láti ọ̀dọ̀ Olúwa wọn, (Allāhu) máa pa àwọn (iṣẹ́) aburú wọn rẹ́, Ó sì máa ṣe àtúnṣe ọ̀rọ̀ wọn sí dáadáa.');
INSERT INTO "aykyuni" VALUES(47,3,'47:3','Ìyẹn (rí bẹ́ẹ̀) nítorí pé, dájúdájú àwọn tó ṣàì gbàgbọ́ tẹ̀lé irọ́. Àti pé dájúdájú àwọn tó gbàgbọ́ tẹ̀lé òdodo láti ọ̀dọ̀ Olúwa wọn. Báyẹn ni Allāhu ṣe ń fi àpèjúwe wọn lélẹ̀ fún àwọn ènìyàn.');
INSERT INTO "aykyuni" VALUES(47,4,'47:4','Nítorí náà, nígbà tí ẹ bá pàdé àwọn tó ṣàì gbàgbọ́ (lójú ogun ẹ̀sìn), ẹ máa bẹ́ wọn lọ́rùn ǹsó títí di ìgbà tí ẹ máa fi rí wọn pa dáadáa. (Nígbà tí ọwọ́ yín bá ti bà wọ́n tán), ẹ dè wọ́n mọ́lẹ̀ sórí ìgbèkùn. Lẹ́yìn náà, ẹ lè tú wọn sílẹ̀ ní ọ̀fẹ́ tàbí kí ẹ tú wọn sílẹ̀ pẹ̀lú owó ìtúsílẹ̀ títí kò fi níí sí ogun ẹ̀sìn mọ́.Ìyẹn (wà bẹ́ẹ̀). Tí ó bá jẹ́ pé Allāhu bá fẹ́, ìbá gbẹ̀san fúnra Rẹ̀ (láì níí la ogun lọ), ṣùgbọ́n nítorí kí Ó lè dán apá kan yín wò lára apá kan ni. Àwọn mùsùlùmí tí wọ́n sì pa sí ojú ogun ẹ̀sìn, Allāhu kò níí sọ iṣẹ́ wọn dòfo. ');
INSERT INTO "aykyuni" VALUES(47,5,'47:5','Ó máa fi ọ̀nà (Ọgbà Ìdẹ̀ra) mọ̀ wọ́n. Ó sì máa tún ọ̀rọ̀ wọn ṣe sí dáadáa.');
INSERT INTO "aykyuni" VALUES(47,6,'47:6','Ó máa fi wọ́n wọ inú Ọgbà Ìdẹ̀ra, èyí tí Ó ti fi mọ̀ wọ́n.1 ');
INSERT INTO "aykyuni" VALUES(47,7,'47:7','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, tí ẹ bá ran (ẹ̀sìn) Allāhu lọ́wọ́, (Allāhu) máa ràn ẹ̀yin náà lọ́wọ́. Ó sì máa mú ẹsẹ̀ yín dúró ṣinṣin (nínú ẹ̀sìn).');
INSERT INTO "aykyuni" VALUES(47,8,'47:8','Àwọn tó ṣàì gbàgbọ́, ìparun ni fún wọn. (Allāhu) sì máa sọ iṣẹ́ wọn di òfò.');
INSERT INTO "aykyuni" VALUES(47,9,'47:9','Ìyẹn nítorí pé, dájúdájú wọ́n kórira ohun tí Allāhu sọ̀kalẹ̀. Nítorí náà, (Allāhu) sì ba iṣẹ́ wọn jẹ́.');
INSERT INTO "aykyuni" VALUES(47,10,'47:10','Ṣé wọn kò rìn kiri lórí ilẹ̀ kí wọ́n wo bí ìkángun àwọn tó ṣíwájú wọn ṣe rí? Allāhu pa wọ́n rẹ́. Irú rẹ̀ tún wà fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(47,11,'47:11','Ìyẹn nítorí pé dájúdájú Allāhu ni Olùrànlọ́wọ́ àwọn tó gbàgbọ́ ní òdodo. Dájúdájú àwọn aláìgbàgbọ́, kò sí olùrànlọ́wọ́ kan fún wọn.');
INSERT INTO "aykyuni" VALUES(47,12,'47:12','Dájúdájú Allāhu yóò fi àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere wọ inú àwọn Ọgbà Ìdẹ̀ra, èyí tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Àwọn tó sì ṣàì gbàgbọ́, àwọn ń gbádùn, wọ́n sì ń jẹ (kiri) gẹ́gẹ́ bí àwọn ẹran-ọ̀sìn ṣe ń jẹ (kiri). Iná sì ni ibùgbé fún wọn.');
INSERT INTO "aykyuni" VALUES(47,13,'47:13','Mélòó mélòó nínú àwọn (ará) ìlú tí ó lágbára ju (ará) ìlú rẹ, tí ó lé ọ jáde, tí A sì ti pa wọ́n rẹ́. Kò sì sí alárànṣe kan fún wọn.');
INSERT INTO "aykyuni" VALUES(47,14,'47:14','Ǹjẹ́ ẹni tí ó wà lórí ẹ̀rí tó yanjú láti ọ̀dọ̀ Olúwa rẹ̀, ṣé ó dà bí ẹni tí wọ́n ṣe iṣẹ́ aburú rẹ̀ ní ọ̀ṣọ́ fún, tí wọ́n sì tẹ̀lé ìfẹ́-inú wọn?');
INSERT INTO "aykyuni" VALUES(47,15,'47:15','Àpèjúwe Ọgbà Ìdẹ̀ra èyí tí wọ́n ṣe ní àdéhùn fún àwọn olùbẹ̀rù Allāhu (nìyí): àwọn omi odò wà nínú rẹ̀ tí kò níí yí padà àti àwọn odò wàrà tí adùn rẹ̀ kò níí yí padà àti àwọn odò ọtí dídùn fún àwọn tó máa mu ún àti àwọn odò oyin mímọ́. Gbogbo èso wà fún wọn nínú rẹ̀ àti àforíjìn láti ọ̀dọ̀ Olúwa wọn. (Ṣé ẹni tí ó wà nínú Ọgbà Ìdẹ̀ra yìí) dà bí olùṣegbére nínú Iná bí, tí wọ́n ń fún wọn ní omi tó gbóná parí mu, tí ó sì máa já àwọn ìfun wọn pútupùtu?');
INSERT INTO "aykyuni" VALUES(47,16,'47:16','Ó wà nínú wọn, ẹni tí ó ń tẹ́tí sí ọ̀rọ̀ rẹ títí di ìgbà tí wọ́n bá jáde kúrò ní ọ̀dọ̀ rẹ tán, wọn yó sì wí fún àwọn onímọ̀-ẹ̀sìn pé: “Kí l’ó sọ lẹ́ẹ̀ẹ̀ kan ná?” Àwọn wọ̀nyẹn ni àwọn tí Allāhu ti dí ọkàn wọn pa. Wọ́n sì tẹ̀lé ìfẹ́-inú wọn.');
INSERT INTO "aykyuni" VALUES(47,17,'47:17','Àwọn tó sì tẹ̀lé ìmọ̀nà, (Allāhu) ṣàlékún ìmọ̀nà fún wọn. Ó sì máa fún wọn ní ìbẹ̀rù wọn (nínú Allāhu).');
INSERT INTO "aykyuni" VALUES(47,18,'47:18','Ṣé wọ́n ń retí kiní kan bí kò ṣe Àkókò náà, tí ó máa dé bá wọn ní òjijì? Àwọn àmì rẹ̀ kúkú ti dé. Nítorí náà, báwo ni ìrántí (yó ṣe wúlò fún) wọ́n nígbà tí ó bá dé bá wọn?');
INSERT INTO "aykyuni" VALUES(47,19,'47:19','Nítorí náà, mọ̀ pé dájúdájú kò sí ọlọ́hun tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Allāhu. Kí o sì tọrọ àforíjìn fún ẹ̀ṣẹ̀ rẹàti fún àwọn onígbàgbọ́ òdodo lọ́kùnrin àti àwọn onígbàgbọ́ òdodo lóbìnrin. Allāhu mọ lílọ-bíbọ̀ yín àti ibùgbé yín. ');
INSERT INTO "aykyuni" VALUES(47,20,'47:20','Àwọn tó gbàgbọ́ ní òdodo ń sọ pé: “Kí ni kò jẹ́ kí wọ́n sọ sūrah kan kalẹ̀?” Nígbà tí wọ́n bá sọ sūrah aláìnípọ́n-na kalẹ̀,tí wọ́n sì sọ ọ̀rọ̀ ogun ẹ̀sìn nínú rẹ̀, ìwọ yóò rí àwọn tí àrùn wà nínú ọkàn wọn, tí wọn yóò máa wò ọ́ ní wíwò bí ẹni pé wọ́n ti dákú lọ pọnrangandan. Ohun tí ó sì dára jùlọ fún wọn ');
INSERT INTO "aykyuni" VALUES(47,21,'47:21','ni ìtẹ̀lé àṣẹ (Allāhu) àti (sísọ) ọ̀rọ̀ rere. Nígbà tí ọ̀rọ̀ náà (ìyẹn ogun ẹ̀sìn) sì ti di dandan, wọn ìbá gbà pé Allāhu sọ òdodo, ìbá dára jùlọ fún wọn.');
INSERT INTO "aykyuni" VALUES(47,22,'47:22','Ó súnmọ́ pé tí ẹ̀yin bá kọ̀yìn sí ogun ẹ̀sìn, (ẹ̀ ń lọ sí oko ìparun nìyẹn, ẹ sì máa padà síbi ìwà ìgbà àìmọ̀kan) tí ẹ̀yin yóò máa ṣèbàjẹ́ lórí ilẹ̀, tí ẹ óò sì máa já okùn-ìbí yín?1 ');
INSERT INTO "aykyuni" VALUES(47,23,'47:23','Àwọn wọ̀nyẹn ni àwọn tí Allāhu ti ṣẹ́bi lé. Nítorí náà, Ó di wọ́n létí pa. Ó sì fọ́ ìríran wọn.');
INSERT INTO "aykyuni" VALUES(47,24,'47:24','Ṣé wọn kò níí ronú nípa al-Ƙur’ān ni tàbí àwọn àgádágodo ti wà lórí ọkàn wọn ni?');
INSERT INTO "aykyuni" VALUES(47,25,'47:25','Dájúdájú àwọn tó pẹ̀yìn dà (sí ’Islām) lẹ́yìn tí ìmọ̀nà ti fojú hàn sí wọn, Èṣù ló ṣe ìṣìnà ní ọ̀ṣọ́ fún wọn. Ó sì fún wọn ní ìrètí asán nípa ẹ̀mí gígùn.');
INSERT INTO "aykyuni" VALUES(47,26,'47:26','Ìyẹn nítorí pé (àwọn aláìsàn ọkàn) ń sọ fún àwọn tó kórira ohun tí Allāhu sọ̀kalẹ̀ (ìyẹn àwọn yẹhudi) pé: “Àwa yóò tẹ̀lé yin nínú apá kan ọ̀rọ̀ náà.”Allāhu sì mọ àṣírí wọn. ');
INSERT INTO "aykyuni" VALUES(47,27,'47:27','Báwo ni (ó ṣe máa rí fún wọn) nígbà tí àwọn mọlāika bá gba ẹ̀mí wọn, tí wọn yó sì máa lù wọ́n lójú, lù wọ́n lẹ́yìn?');
INSERT INTO "aykyuni" VALUES(47,28,'47:28','Ìyẹn nítorí pé, dájúdájú wọ́n tẹ̀lé ohun tí ó bí Allāhu nínú. Wọ́n sì kórira ìyọ́nú Rẹ̀. Nítorí náà, Allāhu ba àwọn iṣẹ́ wọn jẹ́.');
INSERT INTO "aykyuni" VALUES(47,29,'47:29','Tàbí àwọn tí àrùn wà nínú ọkàn wọn ń lérò pé Allāhu kò níí ṣe àfihàn àdìsọ́kàn burúkú wọn ni?');
INSERT INTO "aykyuni" VALUES(47,30,'47:30','Àti pé tí A bá fẹ́ Àwa ìbá fi wọ́n hàn ọ́, ìwọ ìbá sì mọ̀ wọ́n pẹ̀lú àmì wọn. Dájúdájú ìwọ yóò mọ̀ wọ́n níbi pé wọ́n máa ń pẹ́kọrọ ọ̀rọ̀.Allāhu sì mọ àwọn iṣẹ́ (ọwọ́) yín. ');
INSERT INTO "aykyuni" VALUES(47,31,'47:31','Dájúdájú A máa dan yín wò títí A fi máa ṣàfi hàn àwọn olùjagun-ẹ̀sìn àti àwọn onísùúrù nínú yín. A sì máa gbìdánwò àwọn ìró yín wò.1 ');
INSERT INTO "aykyuni" VALUES(47,32,'47:32','Dájúdájú àwọn tó ṣàì gbàgbọ́, tí wọ́n sì ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu, tí wọ́n tún yapa Òjíṣẹ́ Allāhu lẹ́yìn tí ìmọ̀nà ti fojú hàn sí wọn, wọn kò lè kó ìnira kiní kan bá Allāhu. (Allāhu) yó sì ba iṣẹ́ wọn jẹ́.');
INSERT INTO "aykyuni" VALUES(47,33,'47:33','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ tẹ̀lé (ọ̀rọ̀) Allāhu, ẹ tẹ̀lé (ọ̀rọ̀) Òjíṣẹ́ náà, kí ẹ sì má ṣe ba àwọn iṣẹ́ yín jẹ́.');
INSERT INTO "aykyuni" VALUES(47,34,'47:34','Dájúdájú àwọn tó ṣàì gbàgbọ́, tí wọ́n sì ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu, lẹ́yìn náà tí wọ́n kú nígbà tí wọ́n jẹ́ aláìgbàgbọ́, Allāhu kò níí foríjìn wọ́n.');
INSERT INTO "aykyuni" VALUES(47,35,'47:35','Ẹ má ṣe káàárẹ̀, kí ẹ sì má ṣe pèpè fún kòsógunmọ́, nígbà tí ẹ bá ń lékè lọ́wọ́. Allāhu wà pẹ̀lú yín; kò sì níí kó àdínkù bá ẹ̀san àwọn iṣẹ́ yín.1 ');
INSERT INTO "aykyuni" VALUES(47,36,'47:36','Eré àti ìranù ni ìṣẹ̀mí ayé. (Àmọ́) tí ẹ bá gbàgbọ́ (nínú Allāhu), tí ẹ sì bẹ̀rù (Rẹ̀), Ó máa fún yín ní àwọn ẹ̀san yín. Kò sì níí bèèrè àwọn dúkìá yín (pé kí ẹ fi gbogbo rẹ̀ yọ Zakāh).');
INSERT INTO "aykyuni" VALUES(47,37,'47:37','Tí (Allāhu) bá bèèrè rẹ̀ ní ọwọ́ yín (pé, kí ẹ fi gbogbo rẹ̀ yọ Zakāh), tí Ó sì won̄koko mọ yín, ẹ máa ṣahun. Ó sì máa mú àrùn ọkàn yín jadé (àṣírí yín yó sì tú síta).');
INSERT INTO "aykyuni" VALUES(47,38,'47:38','Ẹ̀yin náà mà nìwọ̀nyí tí wọ́n ń pè láti náwó fún ẹ̀sìn Allāhu. Ṣùgbọ́n ẹni tó ń ṣahun wà nínú yín. Ẹnikẹ́ni tí ó bá ṣahun, ó ṣe é fún ẹ̀mí ara rẹ̀. Allāhu ni Ọlọ́rọ̀. Ẹ̀yin sì ni aláìní. Tí ẹ bá gbúnrí, (Allāhu) máa fi ìjọ mìíràn pààrọ̀ yín. Lẹ́yìn náà, wọn kò sì níí dà bí irú yin.');
INSERT INTO "aykyuni" VALUES(48,1,'48:1','Dájúdájú Àwa ṣẹ́gun fún ọ ní ìṣẹ́gun tó fojú hàn');
INSERT INTO "aykyuni" VALUES(48,2,'48:2','nítorí kí Allāhu lè ṣàforíjìn ohun tí ó ṣíwájú nínú àṣìṣe rẹ àti ohun tí ó kẹ́yìn (nínú rẹ̀),àti nítorí kí Ó lè ṣàṣepé ìdẹ̀ra Rẹ̀ lé ọ lórí àti nítorí kí Ó lè fi ẹsẹ̀ rẹ rinlẹ̀ sójú ọ̀nà tààrà (’Islām), ');
INSERT INTO "aykyuni" VALUES(48,3,'48:3','àti nítorí kí Allāhu lè ṣàrànṣe fún ọ ní àrànṣe tó lágbára.');
INSERT INTO "aykyuni" VALUES(48,4,'48:4','Òun ni Ẹni tí Ó sọ ìfàyàbalẹ̀ sínú ọkàn àwọn onígbàgbọ́ òdodo nítorí kí wọ́n lè lékún ní ìgbàgbọ́ sí ìgbàgbọ́ wọn. Ti Allāhu sì ni àwọn ọmọ ogun sánmọ̀ àti ilẹ̀. Allāhu sì ń jẹ́ Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(48,5,'48:5','(Ó ṣe bẹ́ẹ̀ fún wọn) nítorí kí Ó lè mú àwọn onígbàgbọ́ òdodo lọ́kùnrin àti onígbàgbọ́ òdodo lóbìnrin wọ inú àwọn Ọgbà Ìdẹ̀ra kan, èyí tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ, olùṣegbére ni wọ́n nínú rẹ̀, àti nítorí kí Ó lè pa àwọn àṣìṣe wọn rẹ́. Ìyẹn sì jẹ́ èrèǹjẹ ńlá ní ọ̀dọ̀ Allāhu.');
INSERT INTO "aykyuni" VALUES(48,6,'48:6','Àti nítorí kí Ó lè fìyà jẹ àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí lọ́kùnrin àti àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí lóbìnrin, pẹ̀lú àwọn ọ̀ṣẹbọ lọ́kùnrin àti àwọn ọ̀ṣẹbọ lóbìnrin, àwọn eléròkérò nípa Allāhu ní ti èrò aburú. Àpadàsí aburú ń bẹ fún wọn. Allāhu ti bínú sí wọn. Ó ti ṣẹ́bi lé wọn. Ó sì ti pèsè iná Jahanamọ sílẹ̀ dè wọ́n. Ó sì burú ní ìkángun.');
INSERT INTO "aykyuni" VALUES(48,7,'48:7','Ti Allāhu sì ni àwọn ọmọ ogun sánmọ̀ àti ilẹ̀. Allāhu sì ń jẹ́ Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(48,8,'48:8','Dájúdájú Àwa rán ọ níṣẹ́ (pé kí o jẹ́) olùjẹ́rìí, oníròó-ìdùnnú àti olùkìlọ̀');
INSERT INTO "aykyuni" VALUES(48,9,'48:9','nítorí kí ẹ lè ní ìgbàgbọ́ òdodo nínú Allāhu àti Òjíṣẹ́ Rẹ̀, àti (nítorí kí) ẹ lè ṣe ìrànlọ́wọ́ fún (Òjíṣẹ́ náà) àti nítorí kí ẹ lè pàtàkì rẹ̀, àti nítorí kí ẹ lè ṣàfọ̀mọ́ fún (Allāhu) ní òwúrọ̀ àti ní ìrọ̀lẹ́.');
INSERT INTO "aykyuni" VALUES(48,10,'48:10','Dájúdájú àwọn tó ń ṣàdéhùn fún ọ (pé àwọn máa dúró tì ọ́ lójú ogun), dájúdájú Allāhu ni wọ́n ń ṣàdéhùn fún. Ọwọ́ Allāhu wà lókè ọwọ́ wọn.Ẹnikẹ́ni tí ó bá tú àdéhùn rẹ̀, ó tú u fún ẹ̀mí ara rẹ̀. Ẹnikẹ́ni tí ó bá sì mú àdéhùn tó ṣe fún Allāhu ṣẹ, (Allāhu) yóò fún un ní ẹ̀san ńlá. ');
INSERT INTO "aykyuni" VALUES(48,11,'48:11','Àwọn olùsásẹ́yìn fún ogun ẹ̀sìn nínú àwọn Lárúbáwá oko yóò máa wí fún ọ pé: “Àwọn dúkìá wa àti àwọn ará ilé wa l’ó kó àìrójú bá wa. Nítorí náà, tọrọ àforíjìn fún wa.” Wọ́n ń fi ahọ́n wọn wí ohun tí kò sí nínú ọkàn wọn. Sọ pé: “Ta ni ó ní ìkápá kiní kan fún yín lọ́dọ̀ Allāhu tí Ó bá gbèrò (láti fi) ìnira kàn yín tàbí tí Ó bá gbèrò àǹfààní kan fún yín? Rárá (kò sí). Allāhu sì ń jẹ́ Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(48,12,'48:12','Rárá (kì í ṣe iṣẹ́ kan l’ó di yín lọ́wọ́ láti lọ jagun, àmọ́) ẹ ti lérò pé Òjíṣẹ́ àti àwọn onígbàgbọ́ òdodo kò níí padà sí ọ̀dọ̀ ará ilé wọn mọ́ láéláé. Wọ́n ṣe ìyẹn ni ọ̀ṣọ́ sínú ọkàn yín. Ẹ sì ro èrò aburú. Ẹ sì jẹ́ ìjọ ìparun.');
INSERT INTO "aykyuni" VALUES(48,13,'48:13','Ẹnikẹ́ni tí kò bá gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀, dájúdájú Àwa ti pèsè Iná sílẹ̀ de àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(48,14,'48:14','Ti Allāhu ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Ó ń ṣàforíjìn fún ẹni tí Ó bá fẹ́. Ó sì ń jẹ ẹni tí Ó bá fẹ́ níyà.Allāhu sì ń jẹ́ Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(48,15,'48:15','Àwọn olùsásẹ́yìn fún ogun (Hudaebiyyah) ń wí pé: “Nígbà tí ẹ bá ń lọ síbi ọrọ̀-ogun (Kọebar) nítorí kí ẹ lè rí i kó, ẹ fi wá sílẹ̀ kí á lè tẹ̀lé yín lọ.” (Àwọn olùsásẹ́yìn wọ̀nyí) sì ń gbèrò láti yí ọ̀rọ̀ Allāhu padà ni.(Ìwọ Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -) sọ (fún àwọn olùsásẹ́yìn fún ogun Hudaebiyyah) pé: “Ẹ̀yin kò gbọdọ̀ tẹ̀lé wa. Báyẹn ni Allāhu ṣe sọ tẹ́lẹ̀tẹ́lẹ̀.” Àwọn olùsásẹ́yìn náà sì ń wí pé: “Rárá (kò rí bẹ́ẹ̀), ẹ̀ ń ṣe kèéta wa ni.”2 Rárá (ẹ̀yin kò ṣe kèéta wọn, àmọ́), wọ́n kì í gbọ́ àgbọ́yé (ọ̀rọ̀) àfi díẹ̀. ');
INSERT INTO "aykyuni" VALUES(48,16,'48:16','Sọ fún àwọn olùsásẹ́yìn fún ogun nínú àwọn Lárúbáwá oko pé: “Wọ́n máa pè yín sí àwọn ènìyàn kan tí wọ́n ní agbára ogun jíjà gan-an. Ẹ máa jà wọ́n lógun tàbí kí wọ́n juwọ́ jusẹ̀ sílẹ̀ (fún ’Islām). Tí ẹ̀yin bá tẹ̀lé àṣẹ (yìí), Allāhu yóò fún yín ní ẹ̀san tó dára. Tí ẹ̀yin bá sì pẹ̀yìndà gẹ́gẹ́ bí ẹ ṣe pẹ̀yìndà ṣíwájú, (Allāhu) yó sì fi ìyà ẹlẹ́ta-eléro jẹ yín.1 ');
INSERT INTO "aykyuni" VALUES(48,17,'48:17','Kò sí ẹ̀ṣẹ̀ fún afọ́jú, kò sí ẹ̀ṣẹ̀ fún arọ, kò sì sí ẹ̀ṣẹ̀ fún aláìsàn (tí wọn kò bá lọ sójú ogun). Ẹnikẹ́ni tí ó bá tẹ̀lé ti Allāhu àti Òjíṣẹ́ Rẹ̀, (Allāhu) yóò mú un wọ inú àwọn Ọgbà Ìdẹ̀ra kan, èyí tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Ẹnikẹ́ni tí ó bá sì pẹ̀yìndà, Ó máa jẹ ẹ́ ní ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(48,18,'48:18','Dájúdájú Allāhu ti yọ́nú sí àwọn onígbàgbọ́ òdodo nígbà tí wọ́n ń ṣàdéhùn fún ọ lábẹ́ igi (pé àwọn máa dúró tì ọ́ lójú ogun Hudaebiyyah). Nítorí náà, (Allāhu) mọ ohun tí ó wà nínú ọkàn wọn. Ó sì sọ ìfọ̀kànbalẹ̀ kalẹ̀ fún wọn. Ó sì san wọ́n ní ẹ̀san ìṣẹ́gun tó súnmọ́ (ìyẹn ogun Hudaebiyyah)');
INSERT INTO "aykyuni" VALUES(48,19,'48:19','àti àwọn ọrọ̀ ogun ọ̀pọ̀lọ́pọ̀ tí wọn yóò rí kó. Allāhu ń jẹ́ Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(48,20,'48:20','Àti pé Allāhu ṣàdéhùn àwọn ọrọ̀ ogun ọ̀pọ̀lọ́pọ̀ tí ẹ̀yin yóò rí kó fún yín. Ó sì tètè mú èyí wá fún yín. Ó tún kó àwọn ènìyàn lọ́wọ́ ró fún yín nítorí kí ó lè jẹ́ àmì kan fún àwọn onígbàgbọ́ òdodo àti nítorí kí Ó lè fi ẹsẹ̀ yín rinlẹ̀ sójú ọ̀nà tààrà (’Islām).');
INSERT INTO "aykyuni" VALUES(48,21,'48:21','Àti òmíràn tí ẹ kò lágbára lórí rẹ̀, (àmọ́ tí) Allāhu ti rọkiriká rẹ̀. Allāhu sì ń jẹ́ Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(48,22,'48:22','Tí ó bá jẹ́ pé àwọn tó ṣàì gbàgbọ́ gbógun dìde si yín ni, wọn ìbá pẹ̀yìndà (láti ságun fún yín). Lẹ́yìn náà, wọn kò níí rí aláàbò tàbí alárànṣe kan.');
INSERT INTO "aykyuni" VALUES(48,23,'48:23','Ìṣe Allāhu, èyí tó ti ṣẹlẹ̀ ṣíwájú (ni èyí). O ò sì níí rí ìyípadà fún ìṣe Allāhu (lórí àwọn ọ̀tá Rẹ̀).');
INSERT INTO "aykyuni" VALUES(48,24,'48:24','Òun sì ni Ẹni tí Ó kó wọn lọ́wọ́ ró fún yín. Ó sì kó ẹ̀yin náà lọ́wọ́ ró fún wọn nínú ìlú Mọkkah lẹ́yìn ìgbà tí Ó ti fi yín borí wọn. Allāhu sì ń jẹ́ Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(48,25,'48:25','Àwọn (ọ̀ṣẹbọ) ni àwọn tó ṣàì gbàgbọ́, tí wọ́n ṣẹ yín lórí kúrò ní Mọ́sálásí Haram, tí wọ́n tún de ẹran ọrẹ mọ́lẹ̀ kí ó má lè dé àyè rẹ̀. Tí kì í bá ṣe ti àwọn ọkùnrin (tí wọ́n ti di) onígbàgbọ́ òdodo àti àwọn obìnrin (tí wọ́n ti di) onígbàgbọ́ òdodo (nínú ìlú Mọkkah), tí ẹ̀yin kò sì mọ̀ wọ́n, kí ẹ̀yin má lọ pa wọ́n, kí ẹ̀yin má lọ fara kó ẹ̀ṣẹ̀ láti ara wọn nípasẹ̀ àìmọ̀, (Allāhu ìbá tí ko yín lọ́wọ́ ró fún wọn. Allāhu ko yín lọ́wọ́ ró fún wọn sẹ́) nítorí kí Ó lè fi ẹni tí ó bá fẹ́ sínú ìkẹ́ Rẹ̀. Tí ó bá jẹ́ pé wọ́n wà ní ọ̀tọ̀ọ̀tọ̀ ni (onígbàgbọ́ òdodo lọ́tọ̀, aláìgbàgbọ́ lọ́tọ̀), Àwa ìbá jẹ àwọn tó ṣàì gbàgbọ́ nínú wọn ní ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(48,26,'48:26','(Rántí) nígbà tí àwọn tó ṣàì gbàgbọ́ kó ìgbónára sínú ọkàn wọn ní ìgbónára ti ìgbà àìmọ̀kan, Allāhu sì sọ ìfọ̀kànbalẹ̀ kalẹ̀ fún Òjíṣẹ́ Rẹ̀ àti àwọn onígbàgbọ́ òdodo. Ó sì jẹ́ kí ọ̀rọ̀ ìbẹ̀rù (Rẹ̀) wà pẹ̀lú wọn; Wọ́n ní ẹ̀tọ́ sí i jùlọ, àwọn ni wọ́n sì ni ín. Allāhu sì ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(48,27,'48:27','Dájúdájú Allāhu ti sọ àlá Òjíṣẹ́ Rẹ̀ di òdodo pé - tí Allāhu bá fẹ́ - dájúdájú ẹ̀yin yóò wọ inú Mọ́sálásí Haram ní ẹni ìfàyàbalẹ̀, ní ẹni tí ó máa fá irun orí yín, tí ẹ sì máa gé irun (orí yín mọ́lẹ̀), tí kò níí sí ìpáyà fún yín. Nítorí náà, (Allāhu) mọ ohun tí ẹ̀yin kò mọ̀. Yàtọ̀ sí ìyẹn, Ó tún ṣe ìṣẹ́gun tó súnmọ́ (fún yín).');
INSERT INTO "aykyuni" VALUES(48,28,'48:28','(Allāhu) Òun ni Ẹni tí Ó fi ìmọ̀nà àti ẹ̀sìn òdodo (’Islām) rán Òjíṣẹ́ Rẹ̀ nítorí kí Ó lè fi borí gbogbo ẹ̀sìn (mìíràn) pátápátá. Allāhu sì tó ní Ẹlẹ́rìí.');
INSERT INTO "aykyuni" VALUES(48,29,'48:29','Muhammad ni Òjíṣẹ́ Allāhu. Àwọn tó wà pẹ̀lú rẹ̀, wọ́n le mọ́ àwọn aláìgbàgbọ́, aláàánú sì ni wọ́n láààrin ara wọn. O máa rí wọn ní olùdáwọ́tẹ-orúnkún àti olùforíkanlẹ̀ (lórí ìrun), tí wọ́n ń wá oore àjùlọ àti ìyọ́nú láti ọ̀dọ̀ Allāhu. Àmì wọn wà ní ojú wọn níbi orípa ìforíkanlẹ̀. Ìyẹn ni àpèjúwe wọn nínú Taorāh àti àpèjúwe wọn nínú ’Injīl, (wọ́n dà) gẹ́gẹ́ bí irúgbìn kan tó yọ ẹ̀ka rẹ̀ jáde. Lẹ́yìn náà, ó fún un lágbára, ó nípọn, ó sì dúró gbagidi lórí igi rẹ̀. Ó sì ń jọ àwọn àgbẹ̀ lójú. (Allāhu fi àyè gba Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a - àti àwọn Sọhābah rẹ̀) nítorí kí Ó lè fi wọ́n ṣe ohun ìbínú fún àwọn aláìgbàgbọ́. Allāhu ṣàdéhùn àforíjìn àti ẹ̀san ńlá fún àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere nínú wọn.');
INSERT INTO "aykyuni" VALUES(49,1,'49:1','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ̀yin kò gbọdọ̀ gbawájú mọ́ Allāhu àti Òjíṣẹ́ Rẹ̀ lọ́wọ́. Kí ẹ sì bẹ̀rù Allāhu. Dájúdájú Allāhu ni Olùgbọ́, Onímọ̀.');
INSERT INTO "aykyuni" VALUES(49,2,'49:2','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe gbé ohùn yín ga borí ohùn Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -. Kí ẹ sì má ṣe fi ohùn ariwo bá a sọ̀rọ̀ gẹ́gẹ́ bí apá kan yín ṣe ń fi ohùn ariwo bá apá kan sọ̀rọ̀ nítorí kí àwọn iṣẹ́ yín má baà bàjẹ́, ẹ̀yin kò sì níí fura.');
INSERT INTO "aykyuni" VALUES(49,3,'49:3','Dájúdájú àwọn tó ń rẹ ohùn wọn nílẹ̀ lọ́dọ̀ Òjíṣẹ́ Allāhu, àwọn wọ̀nyẹn ni àwọn tí Allāhu ti gbìdánwò ọkàn wọn fún ìbẹ̀rù (Rẹ̀). Àforíjìn àti ẹ̀san ńlá wà fún wọn.');
INSERT INTO "aykyuni" VALUES(49,4,'49:4','Dájúdájú àwọn tó ń pè ọ́ láti ẹ̀yìn àwọn yàrá ọ̀pọ̀lọ́pọ̀ wọn ni kò ṣe làákàyè. ');
INSERT INTO "aykyuni" VALUES(49,5,'49:5','Tí ó bá jẹ́ pé dájúdájú wọ́n ṣe sùúrù títí o máa fi jáde sí wọn ni, ìbá dára jùlọ fún wọn. Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(49,6,'49:6','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, tí arúfin kan bá mú ìró kan wá ba yín, ẹ ṣe pẹ̀lẹ́pẹ̀lẹ́ láti mọ òdodo (nípa ọ̀rọ̀ náà) nítorí kí ẹ má baà ṣe àwọn ènìyàn kan ní ṣùtá pẹ̀lú àìmọ̀, kí ẹ má baà di olùkábàámọ̀ lórí ohun tí ẹ ṣe.');
INSERT INTO "aykyuni" VALUES(49,7,'49:7','Kí ẹ sì mọ̀ pé dájúdájú Òjíṣẹ́ Allāhu wà lààrin yín.Tí ó bá jẹ́ pé ó ń tẹ̀lé yín níbi ọ̀pọ̀lọ́pọ̀ nínú ọ̀rọ̀ (tó ń ṣẹlẹ̀) ni, dájúdájú ẹ̀yin ìbá ti kó ara yín sínú wàhálà. Ṣùgbọ́n Allāhu jẹ́ kí ẹ nífẹ̀ẹ́ sí ìgbàgbọ́ òdodo. Ó ṣe é ní ọ̀ṣọ́ sínú ọkàn yín. Ó sì jẹ́ kí ẹ kórira àìgbàgbọ́, ìwà burúkú àti ìyapa àṣẹ. Àwọn wọ̀nyẹn ni àwọn olùmọ̀nà. ');
INSERT INTO "aykyuni" VALUES(49,8,'49:8','(Èyí jẹ́) oore àjùlọ àti ìdẹ̀ra láti ọ̀dọ̀ Allāhu. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(49,9,'49:9','Tí igun méjì nínú àwọn onígbàgbọ́ òdodo bá ń bá ara wọn jà, ẹ ṣe àtúnṣe láààrin àwọn méjèèjì. Tí ọ̀kan nínú àwọn méjèèjì bá sì kọjá ẹnu-ààlà lórí ìkejì, ẹ bá èyí tí ó kọjá ẹnu-ààlà jà títí ó fi máa ṣẹ́rí padà síbi àṣẹ Allāhu. Tí ó bá sì ṣẹ́rí padà, ẹ ṣe àtúnṣe láààrin àwọn méjèèjì pẹ̀lú déédé. Kí ẹ ṣe déédé. Dájúdájú Allāhu fẹ́ràn àwọn olùṣe- déédé.');
INSERT INTO "aykyuni" VALUES(49,10,'49:10','Ọmọ ìyá (ẹ̀sìn) ni àwọn onígbàgbọ́ òdodo. Nítorí náà, ẹ ṣàtúnṣe láààrin àwọn ọmọ ìyá yín méjèèjì. Kí ẹ sì bẹ̀rù Allāhu nítorí kí A lè kẹ yín.');
INSERT INTO "aykyuni" VALUES(49,11,'49:11','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, àwọn ènìyàn kan kò gbọ́dọ̀ fi àwọn ènìyàn kan ṣe yẹ̀yẹ́. Ó lè jẹ́ pé àwọn (tí wọ́n sọ di oníyẹ̀yẹ́) dára ju àwọn (tó ń ṣe yẹ̀yẹ́). Àwọn obìnrin kan (kò sì gbọ́dọ̀ fi) àwọn obìnrin kan (ṣe yẹ̀yẹ́). Ó lè jẹ́ pé àwọn (tí wọ́n sọ di oníyẹ̀yẹ́) dára ju àwọn (tó ń ṣe yẹ̀yẹ́). Ẹ má ṣe búra yín. Ẹ má pe’ra yín ní ìnagijẹ burúkú. Orúkọ burúkú (tí kò bá òfin ẹ̀sìn mu) burú lẹ́yìn ìgbàgbọ́ òdodo. Ẹnikẹ́ni tí kò bá ronú pìwàdà, àwọn wọ̀nyẹn gan-an ni alábòsí.');
INSERT INTO "aykyuni" VALUES(49,12,'49:12','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ jìnnà sí ọ̀pọ̀lọ́pọ̀ àbá dídá. Dájúdájú ẹ̀ṣẹ̀ ni apá kan àbá dídá. Ẹ má ṣe tọpinpin ara yín. Kí apá kan yín má ṣe sọ̀rọ̀ apá kan lẹ́yìn. Ṣé ọ̀kan nínú yín nífẹ̀ẹ́ sí láti jẹ ẹran-ara ọmọ ìyá rẹ̀ ní òkú? Ẹ sì kórira rẹ̀. Ẹ bẹ̀rù Allāhu. Dájúdájú Allāhu ni Olùgba-ìronúpìwàdà, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(49,13,'49:13','Ẹ̀yin ènìyàn, dájúdájú Àwa ṣẹ̀dá yín láti ara ọkùnrin àti obìnrin. A sì ṣe yín ní orílẹ̀-èdè orílẹ̀-èdè àti ìran-ìran nítorí kí ẹ lè dára yín mọ̀. Dájúdájú alápọ̀n-ọ́nlé jùlọ nínú yín lọ́dọ̀ Allāhu ni ẹni tó bẹ̀rù (Rẹ̀) jùlọ. Dájúdájú Allāhu ni Onímọ̀, Alámọ̀tán.');
INSERT INTO "aykyuni" VALUES(49,14,'49:14','Àwọn Lárúbáwá oko sọ pé: “A gbàgbọ́ ní òdodo.” Sọ pé: “Ẹ̀yin kò gbàgbọ́ ní òdodo.” Ṣùgbọ́n ẹ sọ pé: “A gba ’Islām.” níwọ̀n ìgbà tí ìgbàgbọ́ òdodo kò tí ì wọ’nú ọkàn yín. Tí ẹ bá tẹ̀lé (àṣẹ) Allāhu àti Òjíṣẹ́ Rẹ̀, (Allāhu) kò níí dín kiní kan kù fún yín nínú (ẹ̀san) àwọn iṣẹ́ yín. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(49,15,'49:15','Àwọn onígbàgbọ́ òdodo ni àwọn tó gbàgbọ́ ní òdodo nínú Allāhu àti Òjíṣẹ́ Rẹ̀, lẹ́yìn náà tí wọn kò ṣeyèméjì, tí wọ́n sì fi dúkìá wọn àti ẹ̀mí wọn jagun fún ẹ̀sìn Allāhu. Àwọn wọ̀nyẹn gan-an ni olódodo.');
INSERT INTO "aykyuni" VALUES(49,16,'49:16','Sọ pé: “Ṣé ẹ̀yin yóò máa sọ nípa ẹ̀sìn yín fún Allāhu ni (pé ìgbàgbọ́ òdodo ti wà nínú ọkàn yín)?” Allāhu sì mọ ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀. Allāhu sì ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(49,17,'49:17','Wọ́n ń ṣèrègún lórí rẹ pé àwọn gba ’Islām. Sọ pé: “Ẹ má fi ’Islām yín ṣèrègún lórí mi. Bí kò ṣe pé Allāhu l’Ó máa ṣèrègún lórí yín pé Ó fi yín mọ̀nà síbi ìgbàgbọ́ òdodo tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(49,18,'49:18','Dájúdájú Allāhu mọ ìkọ̀kọ̀ àwọn sánmọ̀ àti ilẹ̀. Àti pé Allāhu ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(50,1,'50:1','Ƙọ̄f. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú àwọn háràfí náà.)(Allāhu fi) al-Ƙur’ān alápọ̀n-ọ́nlé búra. ');
INSERT INTO "aykyuni" VALUES(50,2,'50:2','Ṣùgbọ́n wọ́n ṣèèmọ̀ nítorí pé olùkìlọ̀ kan wá bá wọn láti ààrin ara wọn. Àwọn aláìgbàgbọ́ sì wí pé: “Èyí ni ohun ìyanu.”');
INSERT INTO "aykyuni" VALUES(50,3,'50:3','Ṣé nígbà tí a bá ti kú, tí a ti di erùpẹ̀ (ni a óò tún jí dìde padà). Ìyẹn ni ìdápadà tó jìnnà (sí n̄ǹkan tí ó lè ṣẹlẹ̀).');
INSERT INTO "aykyuni" VALUES(50,4,'50:4','Dájúdájú A ti mọ ohun tí ilẹ̀ ń mú jẹ nínú wọn. Tírà (iṣẹ́ ẹ̀dá) tí wọ́n ń ṣọ́ sì wà ní ọ̀dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(50,5,'50:5','Síbẹ̀síbẹ̀ wọ́n pe òdodo (al-Ƙur’ān) ní irọ́ nígbà tí ó dé bá wọn. Nítorí náà, wọ́n ti wà nínú ìdààmú.');
INSERT INTO "aykyuni" VALUES(50,6,'50:6','Ṣé wọn kò wo sánmọ̀ òkè wọn bí A ti ṣe mọ ọ́n pa àti (bí) A ti ṣe ṣe é ní ọ̀ṣọ́, tí kò sì sí àlàfo sísán kan lára rẹ̀?');
INSERT INTO "aykyuni" VALUES(50,7,'50:7','Àti pé ilẹ̀, A tẹ́ ẹ pẹrẹsẹ. A sì ju àwọn àpáta tó dúró gbagidi sínú rẹ̀. A sì mú oríṣiríṣi irúgbìn tó dára hù jáde láti inú rẹ̀.');
INSERT INTO "aykyuni" VALUES(50,8,'50:8','(Ó jẹ́) àríwòye àti ìrántí fún gbogbo ẹrúsìn tó ń ṣẹ́rí padà (síbi òdodo).');
INSERT INTO "aykyuni" VALUES(50,9,'50:9','A ń sọ omi ìbùkún kalẹ̀ láti sánmọ̀. A sì ń fi mú àwọn ọgbà oko àti àwọn èso tí wọ́n ń kórè rẹ̀ hù jáde.');
INSERT INTO "aykyuni" VALUES(50,10,'50:10','(A tún mú) igi dàbínù hù ga, tí àwọn èso orí rẹ̀ so jìgbìnnì.');
INSERT INTO "aykyuni" VALUES(50,11,'50:11','Arísìkí ni fún àwọn ẹrú (Allāhu). A sì ń fi sọ òkú ilẹ̀ di àyè. Báyẹn ni ìjáde ẹ̀dá (láti inú sàréè yó ṣe rí).');
INSERT INTO "aykyuni" VALUES(50,12,'50:12','Àwọn ènìyàn (Ànábì) Nūh, àwọn ènìyàn Rass àti àwọn (ènìyàn) Thamūd pe òdodo ní irọ́ ṣíwájú wọn.');
INSERT INTO "aykyuni" VALUES(50,13,'50:13','Àti àwọn ‘Ād, Fir‘aon àti àwọn ọmọ ìyá (Ànábì) Lūt,');
INSERT INTO "aykyuni" VALUES(50,14,'50:14','àti àwọn ará ’Aekah àti àwọn ìjọ Tubba‘u; gbogbo wọn pe àwọn Òjíṣẹ́ ní òpùrọ́. Nítorí náà, ìlérí Mi sì kò (lórí wọn).');
INSERT INTO "aykyuni" VALUES(50,15,'50:15','Ǹjẹ́ ìṣẹ̀dá àkọ́kọ́ kó agara bá Wa bí? Rárá, wọ́n wà nínú iyèméjì (àti ìrújú) nípa ìṣẹ̀dá (wọn) ní ọ̀tun ni.');
INSERT INTO "aykyuni" VALUES(50,16,'50:16','Dájúdájú A ṣẹ̀dá ènìyàn. A sì mọ ohun tí ẹ̀mí rẹ̀ ń sọ fún un ní ròyíròyí. Àwa sì súnmọ́ ọn ju isan ọrùn rẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(50,17,'50:17','(Rántí) nígbà tí (mọlāika) àwọn olùgbọ̀rọ̀-sílẹ̀ méjì bá bẹ̀rẹ̀sí gba ọ̀rọ̀ (sílẹ̀), tí ọ̀kan jókòó sí ọ̀tún, tí ọ̀kan jókòó sí òsì.');
INSERT INTO "aykyuni" VALUES(50,18,'50:18','(Ẹnì kan) kò sì níí sọ ọ̀rọ̀ kan àyàfi kí ẹ̀ṣọ́ kan ti wà níkàlẹ̀ pẹ̀lú rẹ̀ (fún àkọsílẹ̀ rẹ̀).');
INSERT INTO "aykyuni" VALUES(50,19,'50:19','Àti pé ìpọ́kàkà ikú yóò dé láti fi òdodo ọ̀run rinlẹ̀. Ìyẹn ni ohun tí ìwọ ń sá fún.');
INSERT INTO "aykyuni" VALUES(50,20,'50:20','Wọ́n á fọn fèrè oníwo fún àjíǹde. Ìyẹn ni ọjọ́ ìlérí náà.');
INSERT INTO "aykyuni" VALUES(50,21,'50:21','Ẹ̀mí kọ̀ọ̀kan yóò wá pẹ̀lú olùdarí kan àti ẹlẹ́rìí kan (nínú àwọn mọlāika).');
INSERT INTO "aykyuni" VALUES(50,22,'50:22','Dájúdájú ìwọ ti wà nínú ìgbàgbéra níbi èyí. A sì ti ṣí èbìbò (ojú) rẹ kúrò fún ọ báyìí. Nítorí náà, ìríran rẹ yóò ríran kedere ní òní.');
INSERT INTO "aykyuni" VALUES(50,23,'50:23','(Mọlāika) alábàárìn rẹ̀ yóò sọ pé: “Èyí ti wà nílẹ̀ ni ohun tí ń bẹ ní ọ̀dọ̀ mi (gẹ́gẹ́ bí àkọsílẹ̀ iṣẹ́ rẹ̀).”');
INSERT INTO "aykyuni" VALUES(50,24,'50:24','Ẹ̀yin mọlāika méjèèjì,ẹ ju gbogbo aláìgbàgbọ́ olóríkunkun sínú iná Jahanamọ. ');
INSERT INTO "aykyuni" VALUES(50,25,'50:25','(Ẹ ju) ẹni tí ó dènà iṣẹ́ rere, alákọyọ, oníyèméjì (sínú Iná).');
INSERT INTO "aykyuni" VALUES(50,26,'50:26','Ẹni tó mú ọlọ́hun mìíràn mọ́ Allāhu, ẹ jù ú sínú ìyà líle.');
INSERT INTO "aykyuni" VALUES(50,27,'50:27','(Èṣù) alábàárìn rẹ̀ yóò wí pé: “Olúwa wa, èmi kọ́ ni mo mú un tayọ ẹnu-ààlà, ṣùgbọ́n ó ti wà nínú ìṣìnà tó jìnnà (sí òdodo) tẹ́lẹ̀tẹ́lẹ̀.”');
INSERT INTO "aykyuni" VALUES(50,28,'50:28','(Allāhu) yóò sọ pé: “Ẹ má se ṣàríyànjiyàn lọ́dọ̀ Mi. Mo kúkú ti ṣe ìlérí fún yín ṣíwájú.');
INSERT INTO "aykyuni" VALUES(50,29,'50:29','Wọn kò sì lè yí ọ̀rọ̀ náà padà ní ọ̀dọ̀ Mi. Èmi kò sì níí ṣe àbòsí sí àwọn ẹrú Mi.”');
INSERT INTO "aykyuni" VALUES(50,30,'50:30','(Rántí) ọjọ́ tí A óò sọ fún iná Jahanamọ pé: Ṣèbí o ti kún?” Ó sì máa sọ pé: “Ṣèbí àfikún tún wà?”');
INSERT INTO "aykyuni" VALUES(50,31,'50:31','Wọ́n máa sún Ọgbà Ìdẹ̀ra mọ́ àwọn olùbẹ̀rù (Allāhu). Kò sì níí jìnnà (sí wọn).');
INSERT INTO "aykyuni" VALUES(50,32,'50:32','Èyí ni ohun tí À ń ṣe ní àdéhùn fún gbogbo olùronúpìwàdà, olùṣọ́ (òfin Allāhu).');
INSERT INTO "aykyuni" VALUES(50,33,'50:33','Ẹnikẹ́ni tí ó bá páyà Allāhu ní ìkọ̀kọ̀, tí ó tún dé pẹ̀lú ọkàn tó ṣẹ́rí padà sí ọ̀dọ̀ Allāhu (nípa ìronúpìwàdà),');
INSERT INTO "aykyuni" VALUES(50,34,'50:34','(A óò sọ fún wọn pé): “Ẹ wọ inú (Ọgbà Ìdẹ̀ra) pẹ̀lú àlàáfíà. Ìyẹn ni ọjọ́ gbére.”');
INSERT INTO "aykyuni" VALUES(50,35,'50:35','Ohun tí wọ́n ń fẹ́ wà nínú rẹ̀. Àlékún sì tún wà ní ọ̀dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(50,36,'50:36','Mélòó mélòó nínú àwọn ìjọ tí A ti parẹ́ ṣíwájú wọn. Wọ́n sì ní agbára jù wọ́n lọ. (Nígbà tí ìyà dé) wọ́n sá àsálà kiri nínú ìlú. Ǹjẹ́ ibùsásí kan wà (fún wọn bí?)');
INSERT INTO "aykyuni" VALUES(50,37,'50:37','Dájúdájú ìrántí wà nínú ìyẹn fún ẹni tó ní ọkàn tàbí ẹni tó fi etí sílẹ̀, tó wà níbẹ̀ (pẹ̀lú ọkàn rẹ̀).');
INSERT INTO "aykyuni" VALUES(50,38,'50:38','Dájúdájú A ṣẹ̀dá àwọn sánmọ̀, ilẹ̀ àti n̄ǹkan tó wà láààrin àwọn méjèèjì fún ọjọ́ mẹ́fà. Kò sì rẹ̀ Wá rárá (áḿbọ̀sìbọ́sí pé A óò sinmi ní ọjọ́ keje).');
INSERT INTO "aykyuni" VALUES(50,39,'50:39','Nítorí náà, ṣe sùúrù lórí ohun tí wọ́n ń wí. Kí o sì ṣe àfọ̀mọ́ pẹ̀lú ẹyìn fún Olúwa Rẹṣíwájú yíyọ òòrùn àti wíwọ̀ (rẹ̀). ');
INSERT INTO "aykyuni" VALUES(50,40,'50:40','Àti pé ní òru àti ni ẹ̀yìn ìrun ṣe àfọ̀mọ́ fún Un.');
INSERT INTO "aykyuni" VALUES(50,41,'50:41','Kí o sì tẹ́tí sí (ọ̀rọ̀) ọjọ́ tí olùpèpè yóò pèpè láti àyè kan tó súnmọ́.');
INSERT INTO "aykyuni" VALUES(50,42,'50:42','Ọjọ́ tí wọn yóò gbọ́ igbe pẹ̀lú òdodo. Ìyẹn ni ọjọ́ ìjáde ẹ̀dá (láti inú sàréè).');
INSERT INTO "aykyuni" VALUES(50,43,'50:43','Dájúdájú Àwa, Àwa l’À ń sọ ẹ̀dá di alààyè. A sì ń sọ ọ́ di òkú. Ọ̀dọ̀ Wa sì ni àbọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(50,44,'50:44','(Rántí) ọjọ́ tí ilẹ̀ yóò fàya pẹ́rẹpẹ̀rẹ mọ́ wọn lára, (tí wọn yóò máa) yára (jáde láti inú ilẹ̀). Ìyẹn ni àkójọ tó rọrùn fún Wa.');
INSERT INTO "aykyuni" VALUES(50,45,'50:45','Àwa nímọ̀ jùlọ nípa ohun tí wọ́n ń wí. Ìwọ kò sì níí jẹ wọ́n nípá (láti gbàgbọ́). Nítorí náà, fi al-Ƙur’ān ṣe ìrántí fún ẹni tí ó ń páyà ìlérí Mi.');
INSERT INTO "aykyuni" VALUES(51,1,'51:1','Allāhu fi atẹ́gùn tó ń tu erùpẹ̀ jáde nílẹ̀ ní títu-jáde tààrà búra.');
INSERT INTO "aykyuni" VALUES(51,2,'51:2','Allāhu fi àwọn ẹ̀ṣújò tó ru òjò tó wúwo búra.');
INSERT INTO "aykyuni" VALUES(51,3,'51:3','Allāhu fi àwọn ọkọ̀ ojú-omi tó ń rìn pẹ̀lú ìrọ̀rùn búra.');
INSERT INTO "aykyuni" VALUES(51,4,'51:4','Allāhu fi àwọn mọlāika tó ń pín n̄ǹkan tí Ó ti pín fún ẹ̀dá búra.');
INSERT INTO "aykyuni" VALUES(51,5,'51:5','Òdodo mà ni ohun tí À ń ṣe ní àdéhùn fún yín.');
INSERT INTO "aykyuni" VALUES(51,6,'51:6','Àti pé dájúdájú ẹ̀san iṣẹ́ máa ṣẹlẹ̀.');
INSERT INTO "aykyuni" VALUES(51,7,'51:7','Allāhu tún fi sánmọ̀ ọlọ́ṣọ̀ọ́ tó gún régé búra.');
INSERT INTO "aykyuni" VALUES(51,8,'51:8','Dájúdájú ẹ̀yin wà lórí ọ̀rọ̀ tó ń takora wọn (nípa al-Ƙur’ān).');
INSERT INTO "aykyuni" VALUES(51,9,'51:9','Ẹni tí wọ́n ń ṣẹ́ lórí kúrò níbi al-Ƙur’ān ni ẹni tí Wọ́n ti ṣẹ́ lórí kúrò níbẹ̀ (láti inú Laohul-Mahfūṭḥ).');
INSERT INTO "aykyuni" VALUES(51,10,'51:10','Ègbé ni fún àwọn òpùrọ́;');
INSERT INTO "aykyuni" VALUES(51,11,'51:11','àwọn tí wọ́n wà nínú àìmọ̀kan tó gbópọn tí wọ́n ti gbàgbéra.');
INSERT INTO "aykyuni" VALUES(51,12,'51:12','tí wọ́n ń bèèrè ìgbà tí Ọjọ́ Ẹ̀san máa ṣẹlẹ̀.');
INSERT INTO "aykyuni" VALUES(51,13,'51:13','Ní ọjọ́ náà sì ni wọn yóò máa fi Iná jẹ wọ́n níyà.');
INSERT INTO "aykyuni" VALUES(51,14,'51:14','Ẹ tọ́ ìyà yín wò. Èyí ni n̄ǹkan tí ẹ ti ń wá pẹ̀lú ìkánjú.');
INSERT INTO "aykyuni" VALUES(51,15,'51:15','Dájúdájú àwọn olùbẹ̀rù (Allāhu) yóò wà nínú àwọn Ọgbà Ìdẹ̀ra pẹ̀lú àwọn omi odò ìṣẹ́lẹ̀rú (ní ìsàlẹ̀ rẹ̀).');
INSERT INTO "aykyuni" VALUES(51,16,'51:16','Wọn yóò máa gba n̄ǹkan tí Olúwa wọn bá fún wọn. Dájúdájú wọ́n ti jẹ́ olùṣe-rere ṣíwájú ìyẹn.');
INSERT INTO "aykyuni" VALUES(51,17,'51:17','Wọ́n máa ń sun oorun díẹ̀ nínú òru.');
INSERT INTO "aykyuni" VALUES(51,18,'51:18','Wọ́n máa ń tọrọ àforíjìn ní àfẹ̀mọ́júmọ́.');
INSERT INTO "aykyuni" VALUES(51,19,'51:19','Nínú dúkìá wọn, wọ́n ní ojúṣe tí wọ́n ń ṣe fún alágbe àti ẹni tí A ṣe arísìkí ní èèwọ̀ fún.');
INSERT INTO "aykyuni" VALUES(51,20,'51:20','Àwọn àmì wà ní orí ilẹ̀ fún àwọn alámọ̀dájú.');
INSERT INTO "aykyuni" VALUES(51,21,'51:21','Nínú ẹ̀mí ara yín gan-an (àmì ìyanu wà nínú rẹ̀). Ṣé ẹ kò ríran ni?');
INSERT INTO "aykyuni" VALUES(51,22,'51:22','Arísìkí yín àti ohun tí À ń ṣe ní àdéhùn fún yín wà nínú sánmọ̀.');
INSERT INTO "aykyuni" VALUES(51,23,'51:23','Nítorí náà, Mó fi Olúwa sánmọ̀ àti ilẹ̀ búra, dájúdájú òdodo ni (ọ̀rọ̀ náà) gẹ́gẹ́ bí ó ṣe jẹ́ òdodo pé ẹ̀ ń fẹnu sọ̀rọ̀.');
INSERT INTO "aykyuni" VALUES(51,24,'51:24','Ǹjẹ́ ọ̀rọ̀ àwọn àlejò (Ànábì) ’Ibrọ̄hīm, àwọn alápọ̀n-ọ́nlé, ti dé ọ̀dọ̀ rẹ?');
INSERT INTO "aykyuni" VALUES(51,25,'51:25','(Rántí) nígbà tí wọ́n wọlé tọ̀ ọ́ wá, wọ́n sì sọ pé: “Àlàáfíà (fún ọ).” Òun náà sọ pé: “Àlàáfíà (fún yín), ẹ̀yin àjòjì ènìyàn.”');
INSERT INTO "aykyuni" VALUES(51,26,'51:26','Ó lọ sí ọ̀dọ̀ àwọn ará ilé rẹ̀, ó sì dé pẹ̀lú ọmọ màálù tó ní ọ̀rá (tí wọ́n ti yangbẹ).');
INSERT INTO "aykyuni" VALUES(51,27,'51:27','Ó gbé e súnmọ́ ọ̀dọ̀ wọn. Ó sì sọ pé: “Ṣé ẹ kò níí jẹun ni?”');
INSERT INTO "aykyuni" VALUES(51,28,'51:28','Nígbà náà, ìbẹ̀rù wọn sì mú un. Wọ́n sọ pé: “Má ṣe páyà.” Wọ́n sì fún un ní ìró ìdùnnú nípa bíbí ọmọkùnrin kan tí ó máa ní ìmọ̀. ');
INSERT INTO "aykyuni" VALUES(51,29,'51:29','Nígbà náà, ìyàwó rẹ̀ wọlé pẹ̀lú igbe, ó sì gbára rẹ̀ lójú, ó sọ pé: “Arúgbó, àgàn (mà ni mí)!”');
INSERT INTO "aykyuni" VALUES(51,30,'51:30','Wọ́n sọ pé: “Báyẹn ni Olúwa rẹ sọ. Dájúdájú Òun ni Ọlọ́gbọ́n, Onímọ̀.”');
INSERT INTO "aykyuni" VALUES(51,31,'51:31','(Ànábì ’Ibrọ̄hīm) sọ pé: “Kí tún ni ọ̀rọ̀ tí ẹ bá wá, ẹ̀yin Òjíṣẹ́?”');
INSERT INTO "aykyuni" VALUES(51,32,'51:32','Wọ́n sọ pé: “Dájúdájú Wọ́n rán wa níṣẹ́ sí ìjọ ẹlẹ́ṣẹ̀ ni');
INSERT INTO "aykyuni" VALUES(51,33,'51:33','nítorí kí á lè fi òkúta amọ̀ (sísun) ránṣẹ́ sí wọn.');
INSERT INTO "aykyuni" VALUES(51,34,'51:34','Wọ́n ti fi àmì sí i lára lọ́dọ̀ Olúwa rẹ fún àwọn alákọyọ.”');
INSERT INTO "aykyuni" VALUES(51,35,'51:35','Nítorí náà, A mú àwọn tó jẹ́ onígbàgbọ́ òdodo jáde kúrò nínú (ìlú náà).');
INSERT INTO "aykyuni" VALUES(51,36,'51:36','A kò sì rí nínú (ìlú náà) yàtọ̀ sí ilé kan tó jẹ́ ti àwọn mùsùlùmí.');
INSERT INTO "aykyuni" VALUES(51,37,'51:37','A sì fi àmì kan sílẹ̀ nínú (ìlú náà) fún àwọn tó ń páyà ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(51,38,'51:38','(Àmì tún wà) lára (Ànábì) Mūsā. Nígbà tí A fi ẹ̀rí pọ́nńbélé rán an níṣẹ́ sí Fir‘aon.');
INSERT INTO "aykyuni" VALUES(51,39,'51:39','Ṣùgbọ́n ó lo gbogbo agbára rẹ̀ láti kẹ̀yìn sí òdodo. Ó sì wí pé: “Òpìdán tàbí wèrè kan (nìyí).”');
INSERT INTO "aykyuni" VALUES(51,40,'51:40','Nítorí náà, A mú òun àti àwọn ọmọ ogun rẹ̀. A sì jù wọ́n sínú agbami odò nígbà tí ó jẹ́ alábùkù.');
INSERT INTO "aykyuni" VALUES(51,41,'51:41','(Àmì tún wà) lára ìjọ ‘Ād. (Rántí) nígbà tí A rán atẹ́gùn ìparun sí wọn.');
INSERT INTO "aykyuni" VALUES(51,42,'51:42','Kò fi kiní kan tó fẹ́ sí lára sílẹ̀ bẹ́ẹ̀ àfi kí ó sọ ọ́ di n̄ǹkan tó kẹfun.1 ');
INSERT INTO "aykyuni" VALUES(51,43,'51:43','(Àmì tún wà) lára ìjọ Thamūd. (Rántí) nígbà tí A sọ fún wọn pé: “Ẹ jayé fún ìgbà díẹ̀ ná.”');
INSERT INTO "aykyuni" VALUES(51,44,'51:44','Wọ́n ṣègbéraga sí àṣẹ Olúwa wọn. Nítorí náà, iná láti ojú sánmọ̀ gbá wọn mú; wọ́n sì ń wò bọ̀ọ̀.');
INSERT INTO "aykyuni" VALUES(51,45,'51:45','Wọn kò lè dìde nàró. Wọn kò sì lè ran ara wọn lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(51,46,'51:46','Àti ìjọ Nūh tí ó ṣíwájú (wọn), dájúdájú wọ́n jẹ́ ìjọ arúfin.');
INSERT INTO "aykyuni" VALUES(51,47,'51:47','Àti pé sánmọ̀, A mọ ọ́n (sí òkè yín) pẹ̀lú agbára. Dájúdájú Àwa sì ni Alágbára (tí a mú un tóbi).');
INSERT INTO "aykyuni" VALUES(51,48,'51:48','Àti ilẹ̀, A ṣe é ní ìtẹ́. (Àwa sì ni) olùtẹ́-ilẹ̀ sílẹ̀ tó dára.');
INSERT INTO "aykyuni" VALUES(51,49,'51:49','Àti pé gbogbo n̄ǹkan ni A ṣẹ̀dá rẹ̀ ní oríṣi méjì-méjì nítorí kí ẹ lè lo ìrántí.1 ');
INSERT INTO "aykyuni" VALUES(51,50,'51:50','Nítorí náà, ẹ sá lọ sí ọ̀dọ̀ Allāhu (nípa ìronúpìwàdà). Dájúdájú èmi ni olùkìlọ̀ pọ́nńbélé fún yín láti ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(51,51,'51:51','Ẹ má ṣe mú ọlọ́hun mìíràn mọ́ Allāhu. Dájúdájú èmi ni olùkìlọ̀ pọ́nńbélé fún yín láti ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(51,52,'51:52','Báyẹn ni (ọ̀rọ̀ ṣe rí)! Òjíṣẹ́ kan kò wá bá àwọn tó ṣíwájú wọn àfi kí wọ́n wí pé: “Òpìdán tàbí wèrè ni.”');
INSERT INTO "aykyuni" VALUES(51,53,'51:53','Ṣé wọn sọ àsọọ́lẹ̀ nípa rẹ̀ láààrin ara wọn ni? Rárá o! Ìjọ olùtayọ-ẹnu-ààlà ni wọ́n ni.');
INSERT INTO "aykyuni" VALUES(51,54,'51:54','Nítorí náà, ṣẹ́rí kúrò lọ́dọ̀ wọn ná. Ìwọ kì í ṣe ẹni tí A máa bá wí.');
INSERT INTO "aykyuni" VALUES(51,55,'51:55','Ṣèrántí nítorí pé, dájúdájú ìrántí máa wúlò fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(51,56,'51:56','Àti pé Èmi kò ṣẹ̀dá àlùjànnú àti ènìyàn bí kò ṣe pé kí wọ́n lè jọ́sìn fún Mi.');
INSERT INTO "aykyuni" VALUES(51,57,'51:57','Èmi kò gbèrò arísìkí kan láti ọ̀dọ̀ wọn. Èmi kò sì gbèrò pé kí wọ́n máa bọ́ Mi.');
INSERT INTO "aykyuni" VALUES(51,58,'51:58','Dájúdájú Allāhu, Òun ni Olùpèsè, Alágbára líle.');
INSERT INTO "aykyuni" VALUES(51,59,'51:59','Dájúdájú ìpín ìyà tí ó máa jẹ àwọn tó ṣàbòsí (wọ̀nyí) ni irú ìpín ìyà tí ó jẹ ìjọ ẹlẹ́ṣẹ̀ (irú) wọn. Nítorí náà, kí wọ́n má ṣe kán Mi lójú (nípa ìyà wọn).');
INSERT INTO "aykyuni" VALUES(51,60,'51:60','Nítorí náà, ègbé ni fún àwọn tó ṣàì gbàgbọ́ ní ọjọ́ wọn tí À ń ṣe ní àdéhùn fún wọn.');
INSERT INTO "aykyuni" VALUES(52,1,'52:1','Allāhu fi àpáta Tūr búra.');
INSERT INTO "aykyuni" VALUES(52,2,'52:2','Ó tún fi Tírà tí wọ́n kọ n̄ǹkan sínú rẹ̀ búra .');
INSERT INTO "aykyuni" VALUES(52,3,'52:3','(Wọ́n kọ̀ ọ́) sínú n̄ǹkan ìkọ̀wé tí wọ́n tẹ́ ojú rẹ̀ sílẹ̀.');
INSERT INTO "aykyuni" VALUES(52,4,'52:4','Allāhu fi Ilé tí àwọn mọlāika ń wọnú rẹ̀ ní ojoojúmọ́ nínú sánmọ̀ búra.');
INSERT INTO "aykyuni" VALUES(52,5,'52:5','Ó tún fi àjà tí wọ́n gbé sókè (ìyẹn, sánmọ̀) búra.');
INSERT INTO "aykyuni" VALUES(52,6,'52:6','Ó tún fi agbami odò tí wọ́n máa mú iná jáde nínú rẹ̀ búra.');
INSERT INTO "aykyuni" VALUES(52,7,'52:7','Dájúdájú ìyà Olúwa rẹ máa ṣẹlẹ̀.');
INSERT INTO "aykyuni" VALUES(52,8,'52:8','Kò sí ẹni tí ó máa dènà rẹ̀.');
INSERT INTO "aykyuni" VALUES(52,9,'52:9','(Ó máa ṣẹlẹ̀) ní ọjọ́ tí sánmọ̀ máa mì tìtì tààrà.');
INSERT INTO "aykyuni" VALUES(52,10,'52:10','àpáta sì máa rìn lọ dànù.');
INSERT INTO "aykyuni" VALUES(52,11,'52:11','Ní ọjọ́ yẹn, ègbé ni fún àwọn tó pe òdodo ní irọ́,');
INSERT INTO "aykyuni" VALUES(52,12,'52:12','àwọn tí wọ́n wà nínú ìsọkúsọ, tí wọ́n ń ṣeré.');
INSERT INTO "aykyuni" VALUES(52,13,'52:13','(Rántí) ọjọ́ tí wọn yóò máa tì wọ́n lọ́ sínú iná Jahanamọ ní ìtìkutì.');
INSERT INTO "aykyuni" VALUES(52,14,'52:14','Èyí ni Iná náà tí ẹ̀ ń pè ní irọ́.');
INSERT INTO "aykyuni" VALUES(52,15,'52:15','Ṣé idán ni èyí ni tàbí ẹ̀yin kò ríran?');
INSERT INTO "aykyuni" VALUES(52,16,'52:16','Ẹ wọ inú rẹ̀ lọ. Ẹ fara dà á tàbí ẹ kò fara dà á, bákan náà ni fún yín. Ohun tí ẹ̀ ń ṣe níṣẹ́ ni A óò fi san yín ní ẹ̀san.');
INSERT INTO "aykyuni" VALUES(52,17,'52:17','Dájúdájú àwọn olùbẹ̀rù (Allāhu) máa wà nínú àwọn Ọgbà àti ìdẹ̀ra.');
INSERT INTO "aykyuni" VALUES(52,18,'52:18','Wọn yóò máa jẹ ìgbádùn pẹ̀lú ohun tí Olúwa wọn fún wọn. Àti pé (Allāhu) yóò ṣọ́ wọn kúrò níbi ìyà iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(52,19,'52:19','Ẹ máa jẹ, ẹ máa mu ní gbẹdẹmukẹ nítorí ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(52,20,'52:20','Wọn yóò rọ̀gbọ̀kú sórí àwọn ibùsùn tí wọ́n tò ní ọ̀wọ̀ọ̀wọ́. A sì máa fún wọn ní àwọn ìyàwó ẹlẹ́yinjú ẹgẹ́.');
INSERT INTO "aykyuni" VALUES(52,21,'52:21','Àti pé àwọn tó gbàgbọ́ ní òdodo, tí àwọn àrọ́mọdọ́mọ wọn tún tẹ̀lé wọn nínú ìgbàgbọ́ òdodo, A máa da àwọn àrọ́mọdọ́mọ wọn pọ̀ mọ́ wọn. A kò sì níí dín kiní kan kù nínú iṣẹ́ wọn. Ẹnì kọ̀ọ̀kan ló máa dúró fún ohun tó ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(52,22,'52:22','A máa fún wọn ní àlékún èso àti ẹran tí wọ́n nífẹ̀ẹ́ sí.');
INSERT INTO "aykyuni" VALUES(52,23,'52:23','Wọn yó sì máa gba ife ọtí mu láààrin ara wọn nínú Ọgbà Ìdẹ̀ra. Kò sí ọ̀rọ̀ ìsọkúsọ àti ìwà ẹ̀ṣẹ̀ nínú Ọgbà Ìdẹ̀ra.');
INSERT INTO "aykyuni" VALUES(52,24,'52:24','Àwọn ọmọ-ọ̀dọ̀ wọn yó sì máa lọ bọ̀ láààrin wọn. (Wọ́n) dà bí àlúùúlù (òkúta olówó-iye-bíye) tí wọ́n fi pamọ́ sínú apó rẹ̀.');
INSERT INTO "aykyuni" VALUES(52,25,'52:25','Apá kan wọn yóò dojú kọ apá kan, wọn yó sì máa bèèrè ọ̀rọ̀ lọ́wọ́ ara wọn.');
INSERT INTO "aykyuni" VALUES(52,26,'52:26','Wọn yóò sọ pé: “Dájúdájú tẹ́lẹ̀tẹ́lẹ̀ àwa máa ń páyà láààrin àwọn ènìyàn wa.');
INSERT INTO "aykyuni" VALUES(52,27,'52:27','Ṣùgbọ́n Allāhu ṣàánú wa. Ó sì là wá kúrò níbi ìyà Iná.');
INSERT INTO "aykyuni" VALUES(52,28,'52:28','Dájúdájú àwa máa ń pè É tẹ́lẹ̀tẹ́lẹ̀. Dájúdájú Allāhu, Òun ni Olóore, Àṣàkẹ́-ọ̀run.”');
INSERT INTO "aykyuni" VALUES(52,29,'52:29','Nítorí náà, ṣèrántí. Ìwọ kì í ṣe adábigba tàbí wèrè lórí ìdẹ̀ra Olúwa rẹ.');
INSERT INTO "aykyuni" VALUES(52,30,'52:30','Tàbí wọ́n ń wí pé: “Eléwì kan tí à ń retí àpadàsí aburú fún ni.”');
INSERT INTO "aykyuni" VALUES(52,31,'52:31','Sọ pé: “Ẹ máa retí. Dájúdájú èmi náà wà pẹ̀lú yín nínú àwọn olùretí.');
INSERT INTO "aykyuni" VALUES(52,32,'52:32','Tàbí ọpọlọ wọn ló ń pa wọ́n láṣẹ èyí ni? Tàbí ìjọ alákọyọ ni wọ́n ni?');
INSERT INTO "aykyuni" VALUES(52,33,'52:33','Tàbí wọ́n ń wí pé: “Ó dá (al-Ƙur’ān) hun fúnra rẹ̀ ni.” Rárá o, wọn kò gbàgbọ́ ni.');
INSERT INTO "aykyuni" VALUES(52,34,'52:34','Kí àwọn náà mú ọ̀rọ̀ kan bí irú rẹ̀ wá tí wọ́n bá jẹ́ olódodo.');
INSERT INTO "aykyuni" VALUES(52,35,'52:35','Tàbí wọ́n ṣẹ̀dá wọn láì sí Aṣẹ̀dá? Tàbí àwọn ni wọ́n ṣẹ̀dá ara wọn ni?');
INSERT INTO "aykyuni" VALUES(52,36,'52:36','Tàbí àwọn ni wọ́n ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀? Rárá o, wọn kò màmọ̀ dájú ni.');
INSERT INTO "aykyuni" VALUES(52,37,'52:37','Tàbí lọ́dọ̀ wọn ni àwọn ilé ọrọ̀ Olúwa rẹ wà? Tàbí àwọn ni olùborí?');
INSERT INTO "aykyuni" VALUES(52,38,'52:38','Tàbí wọ́n ní àkàbà kan tí wọ́n ń fi gbọ́rọ̀ (nínú sánmọ̀)? Kí ẹni tí ó ń bá wọn gbọ́ ọ̀rọ̀ mú ẹ̀rí tó yanjú wá?');
INSERT INTO "aykyuni" VALUES(52,39,'52:39','Tàbí àwọn ọmọbìnrin ni tiRẹ̀, àwọn ọmọkùnrin sì ni tiyín?');
INSERT INTO "aykyuni" VALUES(52,40,'52:40','Tàbí ò ń bèèrè owó-ọ̀yà kan lọ́wọ́ wọn, ni gbèsè fi wọ̀ wọ́n lọ́rùn?');
INSERT INTO "aykyuni" VALUES(52,41,'52:41','Tàbí ìmọ̀ ìkọ̀kọ̀ wà lọ́dọ̀ wọn, tí wọ́n sì ń kọ ọ́ sílẹ̀?');
INSERT INTO "aykyuni" VALUES(52,42,'52:42','Tàbí wọ́n ń gbèrò ète kan ni? Nígbà náà, àwọn tó ṣàì gbàgbọ́, àwọn l’ó máa f’orí kó ète náà.');
INSERT INTO "aykyuni" VALUES(52,43,'52:43','Tàbí wọ́n ní ọlọ́hun kan lẹ́yìn Allāhu ni? Mímọ́ ni fún Allāhu tayọ n̄ǹkan tí wọ́n ń fi ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(52,44,'52:44','Tí wọ́n bá rí apá kan nínú sánmọ̀ tó ya lúlẹ̀, wọ́n á wí pé: “Ẹ̀ṣújò rẹgẹdẹ ni (wọn kò níí gbàgbọ́).”');
INSERT INTO "aykyuni" VALUES(52,45,'52:45','Nítorí náà, fi wọ́n sílẹ̀ títí wọn yóò fi bá ọjọ́ wọn tí wọn máa pa wọ́n sínú rẹ̀ pàdé.');
INSERT INTO "aykyuni" VALUES(52,46,'52:46','Ọjọ́ tí ète wọn kò níí fi kiní kan rọ̀ wọ́n lọ́rọ̀. A kò sì níí ràn wọ́n lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(52,47,'52:47','Àti pé dájúdájú ìyà kan ń bẹ fún àwọn tó ṣàbòsí yàtọ̀ sí (ìyà ọ̀run) yẹn, ṣùgbọ́n ọ̀pọ̀lọ́pọ̀ wọn ni kò mọ̀.');
INSERT INTO "aykyuni" VALUES(52,48,'52:48','Ṣe sùúrù fún ìdájọ́ Olúwa Rẹ, dájúdájú ìwọ wà ní ojútó Wa. Kí ó sì ṣe àfọ̀mọ́ pẹ̀lú ẹyìn fún Olúwa rẹ nígbà tí o bá fẹ́ dìde.1 ');
INSERT INTO "aykyuni" VALUES(52,49,'52:49','Àti pé ní alẹ́ àti nígbà tí àwọn ìràwọ̀ bá kúrò níta tán ṣàfọ̀mọ́ fún Un. ');
INSERT INTO "aykyuni" VALUES(53,1,'53:1','Allāhu fi gbólóhùn kọ̀ọ̀kan nínú al-Ƙur’ān alápọ̀n-ọ́nlé nígbà tí ó bá sọ̀kalẹ̀ láti ojú sánmọ̀ búra.1 ');
INSERT INTO "aykyuni" VALUES(53,2,'53:2','Ẹni yín kò ṣìnà, kò sì sọnù.1 ');
INSERT INTO "aykyuni" VALUES(53,3,'53:3','Àti pé kò níí sọ̀rọ̀ ìfẹ́-inú.');
INSERT INTO "aykyuni" VALUES(53,4,'53:4','Kò níí sọ ohun kan tayọ ìmísí tí A fi ránṣẹ́ sí i.1 ');
INSERT INTO "aykyuni" VALUES(53,5,'53:5','Alágbára (ìyẹn, mọlāika Jibrīl) l’ó kọ́ ọ ní ìmọ̀ (al-Ƙur’ān).');
INSERT INTO "aykyuni" VALUES(53,6,'53:6','Ó ní àlàáfíà tó péye, ó sì dúró wámúwámú,');
INSERT INTO "aykyuni" VALUES(53,7,'53:7','nígbà tí ó wà nínú òfurufú lókè pátápátá.');
INSERT INTO "aykyuni" VALUES(53,8,'53:8','Lẹ́yìn náà, ó sún mọ́ (Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -). Ó sì sọ̀kalẹ̀ (tọ̀ ọ́ wá).');
INSERT INTO "aykyuni" VALUES(53,9,'53:9','(Àlàfo ààrin àwọn méjèèjì) sì tó ìwọ̀n ọrún ọfà méjì, tàbí kí ó kéré (sí ìyẹn).1 ');
INSERT INTO "aykyuni" VALUES(53,10,'53:10','Allāhu sì fún ẹrúsìn Rẹ̀ ní ìmísí tó fún un.');
INSERT INTO "aykyuni" VALUES(53,11,'53:11','Ọkàn (Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -) kò parọ́ ohun tó rí.');
INSERT INTO "aykyuni" VALUES(53,12,'53:12','Ṣé ẹ óò jà á níyàn nípa ohun tó rí ni?');
INSERT INTO "aykyuni" VALUES(53,13,'53:13','Àti pé dájúdájú ó tún rí i nígbà kejì1 ');
INSERT INTO "aykyuni" VALUES(53,14,'53:14','níbi igi sidirah al-Muntahā,');
INSERT INTO "aykyuni" VALUES(53,15,'53:15','nítòsí rẹ̀ ni Ọgbà Ibùgbé (gbére) wà.');
INSERT INTO "aykyuni" VALUES(53,16,'53:16','(Rántí) nígbà tí ohun tó bo igi Sidirah bò ó mọ́lẹ̀.');
INSERT INTO "aykyuni" VALUES(53,17,'53:17','Ojú (Ànábì) kò yẹ̀, kò sì tayọ ẹnu-ààlà.');
INSERT INTO "aykyuni" VALUES(53,18,'53:18','Dájúdájú ó rí nínú àwọn àmì Olúwa rẹ̀, tó tóbi.');
INSERT INTO "aykyuni" VALUES(53,19,'53:19','Ẹ sọ fún mi nípa òrìṣà Lāt àti òrìṣà ‘Uzzā,');
INSERT INTO "aykyuni" VALUES(53,20,'53:20','àti Mọnāh, òrìṣà kẹta mìíràn.1 ');
INSERT INTO "aykyuni" VALUES(53,21,'53:21','Ṣé ọmọkùnrin ni tiyín, ọmọbìnrin sì ni tiRẹ̀?');
INSERT INTO "aykyuni" VALUES(53,22,'53:22','Ìpín àbòsí nìyẹn nígbà náà.');
INSERT INTO "aykyuni" VALUES(53,23,'53:23','(Àwọn orúkọ òrìṣà wọ̀nyẹn) kò sì jẹ́ kiní kan bí kò ṣe àwọn orúkọ kan tí ẹ̀yin àti àwọn bàbá yín sọ (àwọn òrìṣà yín fúnra yín). Allāhu kò sọ ẹ̀rí ọ̀rọ̀ kan kalẹ̀ nípa rẹ̀. Wọn kò sì tẹ̀lé kiní kan bí kò ṣe àbá dídá àti ohun tí ẹ̀mí (wọn) ń fẹ́ (ìfẹ́-inú). Ìmọ̀nà kúkú ti dé bá wọn láti ọ̀dọ̀ Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(53,24,'53:24','Tàbí ti ènìyàn ni n̄ǹkan tó bá ń fẹ́!');
INSERT INTO "aykyuni" VALUES(53,25,'53:25','Ti Allāhu sì ni ọ̀run àti ayé.');
INSERT INTO "aykyuni" VALUES(53,26,'53:26','Àti pé mélòó mélòó nínú àwọn mọlāika tó wà nínú àwọn sánmọ̀, tí ìṣìpẹ̀ wọn kò níí rọ̀ wọ́n lọ́rọ̀ kiní kan àyàfi lẹ́yìn ìgbà tí Allāhu bá yọ̀ǹda fún ẹni tí Ó bá fẹ́, tí Ó sì yọ́nú sí.');
INSERT INTO "aykyuni" VALUES(53,27,'53:27','Dájúdájú àwọn tí kò ní ìgbàgbọ́ òdodo nínú Ọjọ́ Ìkẹ́yìn, àwọn ni wọ́n ń fún àwọn mọlāika ní orúkọ obìnrin.');
INSERT INTO "aykyuni" VALUES(53,28,'53:28','Kò sí ìmọ̀ kan fún wọn nípa rẹ̀. Wọn kò sì tẹ̀lé kiní kan bí kò ṣe àbá dídá. Dájúdájú àbá dídá kò sì níí rọrọ̀ kiní kan lára òdodo.');
INSERT INTO "aykyuni" VALUES(53,29,'53:29','Nítorí náà, ṣẹ́rí kúrò ní ọ̀dọ̀ ẹni tó kẹ̀yìn sí ìrántí Wa, tí kò sì gbèrò kiní kan àfi ìṣẹ̀mí ayé.');
INSERT INTO "aykyuni" VALUES(53,30,'53:30','Ìyẹn ni ohun tí wọ́n wọ̀n nínú ìmọ̀. Dájúdájú Olúwa rẹ lÓ nímọ̀ jùlọ nípa ẹni tó ṣìnà kúrò lójú ọ̀nà Rẹ̀. Ó sì nímọ̀ jùlọ nípa ẹni tó mọ̀nà.');
INSERT INTO "aykyuni" VALUES(53,31,'53:31','Ti Allāhu ni ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ nítorí kí Ó lè san àwọn tó ṣe iṣẹ́ aburú ní ẹ̀san ohun tí wọ́n ṣe níṣẹ́ àti nítorí kí Ó lè san àwọn tó ṣe iṣẹ́ rere ni ẹ̀san rere.');
INSERT INTO "aykyuni" VALUES(53,32,'53:32','Àwọn tó ń jìnnà sí ẹ̀ṣẹ̀ ńlá ńlá àti ìwà ìbàjẹ́ àyàfi àwọn ẹ̀ṣẹ̀ wẹẹrẹ, dájúdájú Olúwa rẹ gbòòrò ní àforíjìn. Ó nímọ̀ jùlọ nípa yín nígbà tí Ó ṣẹ̀dá yín láti inú ilẹ̀ àti nígbà tí ẹ wà ní ọlẹ̀ nínú àwọn ikùn ìyá yín. Nítorí náà, ẹ má ṣe fọra yín mọ́.Òun l’Ó nímọ̀ jùlọ nípa ẹni tó bẹ̀rù (Rẹ̀). ');
INSERT INTO "aykyuni" VALUES(53,33,'53:33','Sọ fún mi nípa ẹni tó gbúnrí (kúrò níbi òdodo),');
INSERT INTO "aykyuni" VALUES(53,34,'53:34','tí ó fi díẹ̀ tọrẹ, tí ó sì di ìyókù mọ́wọ́!');
INSERT INTO "aykyuni" VALUES(53,35,'53:35','Ṣé ìmọ̀ ìkọ̀kọ̀ wà ní ọ̀dọ̀ rẹ, tí ó sì ń rí i (pé ẹlòmíìràn l’ó ma bá òun jìyà lọ́run)?');
INSERT INTO "aykyuni" VALUES(53,36,'53:36','Tàbí wọn kò fún un ní ìró ohun tó wà nínú tírà (Ànábì) Mūsā ni,');
INSERT INTO "aykyuni" VALUES(53,37,'53:37','àti (tírà Ànábì) ’Ibrọ̄hīm, ẹni tó mú (òfin Allāhu) ṣẹ,');
INSERT INTO "aykyuni" VALUES(53,38,'53:38','pé dájúdájú ẹlẹ́rù-ẹ̀ṣẹ̀ kan kò níí ru ẹrù-ẹ̀ṣẹ̀ ẹlòmíìràn.1 ');
INSERT INTO "aykyuni" VALUES(53,39,'53:39','Àti pé kò sí kiní kan fún ènìyàn àfi ohun tó ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(53,40,'53:40','Àti pé dájúdájú iṣẹ́ rẹ̀, láìpẹ́ wọ́n máa fi hàn án.');
INSERT INTO "aykyuni" VALUES(53,41,'53:41','Lẹ́yìn náà, wọ́n máa san án ní ẹ̀san rẹ̀, ní ẹ̀san tó kún jùlọ.');
INSERT INTO "aykyuni" VALUES(53,42,'53:42','Àti pé dájúdájú ọ̀dọ̀ Olúwa rẹ ni òpin (ìrìn-àjò ẹ̀dá).');
INSERT INTO "aykyuni" VALUES(53,43,'53:43','Dájúdájú Òun l’Ó ń pa (ẹ̀dá) ní ẹ̀rín. Ó sì ń pa (ẹ̀dá) ní ẹkún.');
INSERT INTO "aykyuni" VALUES(53,44,'53:44','Dájúdájú Òun l’Ó ń sọ (ẹ̀dá) di òkú. Ó sì ń sọ (ẹ̀dá) di alààyè.');
INSERT INTO "aykyuni" VALUES(53,45,'53:45','Dájúdájú Òun l’Ó dá ẹ̀dá ní oríṣi méjì, ọkùnrin àti obìnrin,1 ');
INSERT INTO "aykyuni" VALUES(53,46,'53:46','láti ara àtọ̀ nígbà tí wọ́n bá dà á sínú àpòlùkẹ́.');
INSERT INTO "aykyuni" VALUES(53,47,'53:47','Dájúdájú Òun l’Ó máa ṣe ìṣẹ̀dá ìkẹ́yìn fún àjíǹde,');
INSERT INTO "aykyuni" VALUES(53,48,'53:48','Dájúdájú Òun l’Ó ń fún ẹ̀dá ní ọrọ̀ dúkìá, Ó sì ń ta ẹ̀dá ní òṣì.');
INSERT INTO "aykyuni" VALUES(53,49,'53:49','Dájúdájú Òun sì ni Olúwa ìràwọ̀ Ṣi‘rọ̄ (tí wọ́n sọ di òrìṣà).');
INSERT INTO "aykyuni" VALUES(53,50,'53:50','Dájúdájú Òun l’Ó pa ìran ‘Ād rẹ́, àwọn ẹni àkọ́kọ́;');
INSERT INTO "aykyuni" VALUES(53,51,'53:51','àti ìran Thamūd, kò sì ṣẹ́ wọn kù;');
INSERT INTO "aykyuni" VALUES(53,52,'53:52','àti ìjọ Nūh tó ṣíwájú (wọn), dájúdájú wọ́n jẹ́ alábòsí jùlọ, wọ́n sì tayọ ẹnu-ààlà jùlọ;');
INSERT INTO "aykyuni" VALUES(53,53,'53:53','àti ìlú tó dojú bolẹ̀ (ìlú Ànábì Lūt), Allāhu ló yẹ̀ ẹ́ lulẹ̀ (láti òkè).');
INSERT INTO "aykyuni" VALUES(53,54,'53:54','Ohun tó bò wọ́n mọ́lẹ̀ bò wọ́n mọ́lẹ̀.');
INSERT INTO "aykyuni" VALUES(53,55,'53:55','Nítorí náà, èwo nínú ìdẹ̀ra Olúwa rẹ ni ìwọ (ènìyàn) ń jà níyàn?');
INSERT INTO "aykyuni" VALUES(53,56,'53:56','Èyí ni ìkìlọ̀ kan nínú àwọn ìkìlọ̀ àkọ́kọ́.');
INSERT INTO "aykyuni" VALUES(53,57,'53:57','Ohun tó súnmọ́súnmọ́. ');
INSERT INTO "aykyuni" VALUES(53,58,'53:58','Lẹ́yìn Allāhu, kò sí ẹni tó lè tú gbèdéke Àkókò náà jáde.1 ');
INSERT INTO "aykyuni" VALUES(53,59,'53:59','Ṣé ọ̀rọ̀ yìí l’ó ń ṣe yín ní kàyéfì?');
INSERT INTO "aykyuni" VALUES(53,60,'53:60','Ẹ̀ ń rẹ́rìn-ín, ẹ kò sì sunkún!');
INSERT INTO "aykyuni" VALUES(53,61,'53:61','Afọ́nú-fọ́ra ni yín.');
INSERT INTO "aykyuni" VALUES(53,62,'53:62','Nítorí náà, ẹ forí kanlẹ̀ fún Allāhu, kí ẹ jọ́sìn (fún Un).');
INSERT INTO "aykyuni" VALUES(54,1,'54:1','Àkókò náà súnmọ́. Òṣùpá sì là pẹrẹgẹdẹ (sí méjì).1 ');
INSERT INTO "aykyuni" VALUES(54,2,'54:2','Tí wọ́n bá rí àmì kan, wọ́n máa gbúnrí. Wọn yó sí wí pé: “Idán kan tó máa parẹ́ (ni èyí).”1 ');
INSERT INTO "aykyuni" VALUES(54,3,'54:3','Wọ́n pè é ní irọ́. Wọ́n sì tẹ̀lé ìfẹ́-inú wọn. Gbogbo iṣẹ́ ẹ̀dá sì máa jókòó tì í lọ́rùn. 1 ');
INSERT INTO "aykyuni" VALUES(54,4,'54:4','Dájúdájú wáàsí tí ó ń kọ aburúwà nínú àwọn ìró tó dé bá wọn. ');
INSERT INTO "aykyuni" VALUES(54,5,'54:5','(al-Ƙur’ān sì ni) ọgbọ́n tó péye pátápátá; ṣùgbọ́n àwọn ìkìlọ̀ náà kò rọ̀ wọ́n lọ́rọ̀.');
INSERT INTO "aykyuni" VALUES(54,6,'54:6','Ṣẹ́rí kúrò ní ọ̀dọ̀ wọn. Ní ọjọ́ tí olùpèpè yóò pèpè fún kiní kan tí ẹ̀mí kórira (ìyẹn, Àjíǹde),');
INSERT INTO "aykyuni" VALUES(54,7,'54:7','Ojú wọn máa wálẹ̀ ní ti àbùkù. Wọn yó sì máa jáde láti inú sàréè bí ẹni pé eṣú tí wọ́n fọ́nká síta ni wọ́n.');
INSERT INTO "aykyuni" VALUES(54,8,'54:8','Wọn yó sì máa yára lọ sí ọ̀dọ̀ olùpèpè náà. Àwọn aláìgbàgbọ́ yó sì wí pé: “Èyí ni ọjọ́ ìṣòro.”');
INSERT INTO "aykyuni" VALUES(54,9,'54:9','Ìjọ (Ànábì) Nūh pe òdodo ní irọ́ ṣíwájú wọn. Nígbà náà, wọ́n pe ẹrúsìn Wa ní òpùrọ́. Wọ́n sì wí pé: “Wèrè ni.” Wọ́n sì kọ̀ fún un pẹ̀lú ohùn líle.1 ');
INSERT INTO "aykyuni" VALUES(54,10,'54:10','Nítorí náà, ó pe Olúwa rẹ̀ pé: “Dájúdájú wọ́n ti borí mi. Bá mi gbẹ̀san.”');
INSERT INTO "aykyuni" VALUES(54,11,'54:11','Nítorí náà, A ṣí àwọn ìlẹ̀kùn sánmọ̀ sílẹ̀ pẹ̀lú omi tó lágbára.');
INSERT INTO "aykyuni" VALUES(54,12,'54:12','A tún mú àwọn odò ṣàn jáde láti inú ilẹ̀. Omi (sánmọ̀) pàdé (omi ilẹ̀) pẹ̀lú àṣẹ tí A ti kọ (lé wọn lórí).');
INSERT INTO "aykyuni" VALUES(54,13,'54:13','A sì gbé (Ànábì) Nūh gun ọkọ̀ onípákó, ọkọ̀ eléṣòó,');
INSERT INTO "aykyuni" VALUES(54,14,'54:14','tí ó ń rìn (lórí omi) lójú Wa. (Ó jẹ́) ẹ̀san nítorí ẹni tí wọ́n ṣàì gbàgbọ́ nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(54,15,'54:15','Dájúdájú A ṣẹ́ ẹ kù (tí A fi ṣe) àmì. Ǹjẹ́ olùrántí wà báyìí bí?');
INSERT INTO "aykyuni" VALUES(54,16,'54:16','Báwo ni ìyà Mi àti ìkìlọ̀ Mi ti rí ná (lára wọn)?');
INSERT INTO "aykyuni" VALUES(54,17,'54:17','Dájúdájú A ṣe al-Ƙur’ān ní ìrọ̀rùn fún ṣíṣe ìrántí. Ǹjẹ́ olùrántí wà báyìí bí?');
INSERT INTO "aykyuni" VALUES(54,18,'54:18','Ìran ‘Ād pé òdodo ní irọ́. Báwo ni ìyà Mi àti ìkìlọ̀ Mi ti rí ná (lára wọn)?');
INSERT INTO "aykyuni" VALUES(54,19,'54:19','Dájúdájú Àwa rán atẹ́gùn líle sí wọn ní ọjọ́ burúkú kan tó ń tẹ̀ síwájú.1 ');
INSERT INTO "aykyuni" VALUES(54,20,'54:20','Ó ń fa àwọn ènìyàn tu bí ẹni pé kùkùté igi ọ̀pẹ tí wọ́n fà tu tegbòtegbò ni wọ́n.');
INSERT INTO "aykyuni" VALUES(54,21,'54:21','Báwo ni ìyà Mi àti ìkìlọ̀ Mi ti rí ná (lára wọn)?');
INSERT INTO "aykyuni" VALUES(54,22,'54:22','Dájúdájú A ṣe al-Ƙur’ān ní ìrọ̀rùn fún ṣíṣe ìrántí. Ǹjẹ́ olùrántí wà báyìí bí?');
INSERT INTO "aykyuni" VALUES(54,23,'54:23','Ìjọ Thamūd pe àwọn ìkìlọ̀ ní irọ́.');
INSERT INTO "aykyuni" VALUES(54,24,'54:24','Wọ́n wí pé: “Ṣé abara kan, ẹnì kan ṣoṣo nínú wa ni a óò máa tẹ̀lé. (Bí ó bá rí bẹ́ẹ̀) nígbà náà dájúdájú àwa ti wà nínú ìṣìnà àti wàhálà.');
INSERT INTO "aykyuni" VALUES(54,25,'54:25','Ṣé òun ni wọ́n sọ tírà ìrántí kalẹ̀ fún láààrin wa? Rárá o, òpùrọ́ onígbèéraga ni.”');
INSERT INTO "aykyuni" VALUES(54,26,'54:26','Ní ọ̀la ni wọn yóò mọ ta ni òpùrọ́ onígbèéraga.');
INSERT INTO "aykyuni" VALUES(54,27,'54:27','Dájúdájú Àwa máa rán abo ràkúnmí sí wọn; (ó máa jẹ́) àdánwò fún wọn. Nítorí náà, máa wò wọ́n níran ná, kí o sì ṣe sùúrù.1 ');
INSERT INTO "aykyuni" VALUES(54,28,'54:28','Kí o sì fún wọn ní ìró pé dájúdájú pípín ni omi láààrin wọn. Gbogbo ìpín omi sì wà fún ẹni tí ó bá kàn láti wá sí odò.');
INSERT INTO "aykyuni" VALUES(54,29,'54:29','Wọ́n pe ẹni wọn. Ó yọ́ kẹ́lẹ́kẹ́lẹ́ nawọ́ mú (ràkúnmí náà mọ́lẹ̀). Ó sì pa á.');
INSERT INTO "aykyuni" VALUES(54,30,'54:30','Báwo ni ìyà Mi àti ìkìlọ̀ Mi ti rí ná (lára wọn)?');
INSERT INTO "aykyuni" VALUES(54,31,'54:31','Dájúdájú Àwa rán igbe kan ṣoṣo sí wọn. Wọ́n sì dà bí koríko gbígbẹ tí àgbẹ̀ dáná sun.');
INSERT INTO "aykyuni" VALUES(54,32,'54:32','Dájúdájú A ṣe al-Ƙur’ān ní ìrọ̀rùn fún ṣíṣe ìrántí. Ǹjẹ́ olùrántí wà báyìí bí?');
INSERT INTO "aykyuni" VALUES(54,33,'54:33','Ìjọ Lūt pe àwọn ìkìlọ̀ ní irọ́.');
INSERT INTO "aykyuni" VALUES(54,34,'54:34','Dájúdájú Àwa fi òkúta iná ránṣẹ́ sí wọn àfi ará ilé Lūt, tí A gbàlà ní àsìkò sààrì.');
INSERT INTO "aykyuni" VALUES(54,35,'54:35','(Ó jẹ́) ìkẹ́ kan láti ọ̀dọ̀ Wa. Báyẹn ni A ṣe ń san ẹ̀san fún ẹni tó bá dúpẹ́ (fún Wa).');
INSERT INTO "aykyuni" VALUES(54,36,'54:36','Ó kúkú fi ìgbámú Wa ṣe ìkìlọ̀ fún wọn, ṣùgbọ́n wọ́n ja àwọn ìkìlọ̀ níyàn.');
INSERT INTO "aykyuni" VALUES(54,37,'54:37','Wọ́n kúkú làkàkà lọ́dọ̀ rẹ̀ láti bá àwọn aléjò rẹ̀ ṣèbàjẹ́. Nítorí náà, A fọ́ ojú wọn. Ẹ tọ́ ìyà Mi àti ìkìlọ̀ Mi wò.');
INSERT INTO "aykyuni" VALUES(54,38,'54:38','Àti pé dájúdájú ìyà gbére ni wọ́n mọ́júmọ́ sínú rẹ̀ ní òwúrọ̀ kùtùkùtù.');
INSERT INTO "aykyuni" VALUES(54,39,'54:39','Nítorí náà, ẹ tọ́ ìyà Mi àti ìkìlọ̀ Mi wò.');
INSERT INTO "aykyuni" VALUES(54,40,'54:40','Dájúdájú A ṣe al-Ƙur’ān ní ìrọ̀rùn fún ṣíṣe ìrántí. Ǹjẹ́ olùrántí wà báyìí bí?');
INSERT INTO "aykyuni" VALUES(54,41,'54:41','Dájúdájú àwọn ìkìlọ̀ dé bá àwọn ènìyàn Fir‘aon.');
INSERT INTO "aykyuni" VALUES(54,42,'54:42','Wọ́n pe gbogbo àwọn āyah Wa ní irọ́ pátápátá. A sì gbá wọn mú ní ìgbámú (tí) Alágbára, Olùkápá (máa ń gbá ẹ̀dá mú).');
INSERT INTO "aykyuni" VALUES(54,43,'54:43','Ṣé àwọn aláìgbàgbọ́ (nínú) yín l’ó lóore ju àwọn wọ̀nyẹn (tí wọ́n ti parẹ́ bọ́ sẹ́yìn) ni tàbí ẹ̀yin ní ìmóríbọ́ kan nínú ìpín-ìpín Tírà (pé ẹ̀yin kò níí jìyà)?');
INSERT INTO "aykyuni" VALUES(54,44,'54:44','Tàbí wọ́n ń wí pé: “Àwa wà papọ̀ tí a óò ranra wa lọ́wọ́ láti borí (Òjíṣẹ́)”');
INSERT INTO "aykyuni" VALUES(54,45,'54:45','A máa fọ́ àkójọ náà lógun. Wọn sì máa fẹsẹ̀ fẹ́ẹ lójú ogun.');
INSERT INTO "aykyuni" VALUES(54,46,'54:46','Àmọ́ sá, Àkókò náà ni ọjọ́ àdéhùn wọn. Àkókò náà burú jùlọ. Ó sì korò jùlọ.');
INSERT INTO "aykyuni" VALUES(54,47,'54:47','Dájúdájú àwọn ẹlẹ́ṣẹ̀ wà nínú ìṣìnà àti wàhálà.');
INSERT INTO "aykyuni" VALUES(54,48,'54:48','Ní ọjọ́ tí A óò dojú wọn bolẹ̀ wọ inú Iná, (A ó sì sọ pé): Ẹ tọ́ ìfọwọ́bà Iná wò.');
INSERT INTO "aykyuni" VALUES(54,49,'54:49','Dájúdájú A ṣẹ̀dá gbogbo n̄ǹkan pẹ̀lú kádàrá.');
INSERT INTO "aykyuni" VALUES(54,50,'54:50','Àṣẹ Wa (fún mímú n̄ǹkan bẹ) kò tayọ (àṣẹ) ẹyọ kan ṣoṣo gẹ́gẹ́ bí ìṣẹ́jú.1 ');
INSERT INTO "aykyuni" VALUES(54,51,'54:51','Àti pé dájúdájú A ti pa àwọn irú yín rẹ́. Ǹjẹ́ olùrántí wà báyìí bí?');
INSERT INTO "aykyuni" VALUES(54,52,'54:52','Gbogbo n̄ǹkan tí wọ́n ṣe níṣẹ́ sì wà nínú ìpín-ìpín tírà.');
INSERT INTO "aykyuni" VALUES(54,53,'54:53','Àti pé gbogbo n̄ǹkan kékeré àti n̄ǹkan ńlá (tí wọ́n ṣe) wà ní àkọsílẹ̀.');
INSERT INTO "aykyuni" VALUES(54,54,'54:54','Dájúdájú àwọn olùbẹ̀rù (Allāhu) yóò wà nínú àwọn Ọgbà (Ìdẹ̀ra) pẹ̀lú àwọn odò (tó ń ṣàn ní ìsàlẹ̀ rẹ̀).');
INSERT INTO "aykyuni" VALUES(54,55,'54:55','Ní ibùjókòó òdodo nítòsí Ọba Alágbára Olùkápá.');
INSERT INTO "aykyuni" VALUES(55,1,'55:1','Àjọkẹ́-ayé,');
INSERT INTO "aykyuni" VALUES(55,2,'55:2','Ó fi ìmọ̀ al-Ƙur’ān mọ (ẹni tí Ó fẹ́).');
INSERT INTO "aykyuni" VALUES(55,3,'55:3','Ó ṣẹ̀dá ènìyàn.');
INSERT INTO "aykyuni" VALUES(55,4,'55:4','Ó sì fi àlàyé (ọ̀rọ̀ sísọ) mọ̀ ọ́n.');
INSERT INTO "aykyuni" VALUES(55,5,'55:5','Òòrùn àti òṣùpá (ń rìn) fún ìṣírò (ọjọ́ ayé).');
INSERT INTO "aykyuni" VALUES(55,6,'55:6','Àwọn ìtàkùn ilẹ̀ àti igi ń forí kanlẹ̀ (fún Allāhu).');
INSERT INTO "aykyuni" VALUES(55,7,'55:7','Àti sánmọ̀, Allāhu gbé e sókè. Ó sì fi òṣùwọ̀n òfin déédé lélẹ̀ (fún ẹ̀dá)');
INSERT INTO "aykyuni" VALUES(55,8,'55:8','pé kí ẹ má ṣe tayọ ẹnu-ààlà níbi òṣùwọ̀n.');
INSERT INTO "aykyuni" VALUES(55,9,'55:9','Ẹ gbé òṣùwọ̀n náà dúró pẹ̀lú dọ́gbadọ́gba. Kí ẹ sì má ṣe dín òṣùwọ̀n kù.');
INSERT INTO "aykyuni" VALUES(55,10,'55:10','Ilẹ̀, (Allāhu) gbé e kalẹ̀ (sí abẹ́ sánmọ̀ fún àwọn ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(55,11,'55:11','Èso àti dàbínù alápó wà lórí (ilẹ̀).');
INSERT INTO "aykyuni" VALUES(55,12,'55:12','Àti èso onípòpórò àti olóòórùn dídùn (wà lórí ilẹ̀).');
INSERT INTO "aykyuni" VALUES(55,13,'55:13','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,14,'55:14','(Allāhu) ṣẹ̀dá ènìyàn láti ara amọ̀ gbígbẹ tó ń dún kokoko bí ìkòkò amọ̀.');
INSERT INTO "aykyuni" VALUES(55,15,'55:15','Ó sì ṣẹ̀dá àlùjànnú láti ara ahọ́n iná tí kò ní èéfín.');
INSERT INTO "aykyuni" VALUES(55,16,'55:16','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,17,'55:17','Olúwa ibùyọ òòrùn méjèèjì àti ibùwọ̀ òòrùn méjèèjì.1 ');
INSERT INTO "aykyuni" VALUES(55,18,'55:18','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,19,'55:19','(Allāhu) mú odò méjì (odò oníyọ̀ àti odò aládùn) ṣàn pàdé ara wọn.');
INSERT INTO "aykyuni" VALUES(55,20,'55:20','Gàgá kan sì wà láààrin àwọn méjèèjì tí wọn kò sì lè tayọ ẹnu-ààlà (rẹ̀ láààrin ara wọn).');
INSERT INTO "aykyuni" VALUES(55,21,'55:21','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,22,'55:22','Òkúta oníyebíye àti ìlẹ̀kẹ̀ iyùn ń jáde nínú àwọn (odò) méjèèjì náà.');
INSERT INTO "aykyuni" VALUES(55,23,'55:23','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,24,'55:24','Ti (Allāhu) ni àwọn ọkọ̀ ojú-omi gogoro tó ń rìn nínú agbami òdò (tí wọ́n dà) bí àpáta gíga.');
INSERT INTO "aykyuni" VALUES(55,25,'55:25','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,26,'55:26','Gbogbo ẹni tó wà lórí ilẹ̀ máa tán.');
INSERT INTO "aykyuni" VALUES(55,27,'55:27','Ojú Olúwa rẹ, Atóbi, Alápọ̀n-ọ́nlé sì máa wà títí láéláé.');
INSERT INTO "aykyuni" VALUES(55,28,'55:28','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,29,'55:29','Àwọn tó wà nínú àwọn sánmọ̀ àti ilẹ̀ ń bèèrè (n̄ǹkan) lọ́dọ̀ Rẹ̀; Ó sì wà lórí ìṣe lójoojúmọ́.');
INSERT INTO "aykyuni" VALUES(55,30,'55:30','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,31,'55:31','A óò mú tiyín gbọ́ (lọ́jọ́ ẹ̀san), ẹ̀yin ènìyàn àti àlùjànnú.');
INSERT INTO "aykyuni" VALUES(55,32,'55:32','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,33,'55:33','Ẹ̀yin àwùjọ àlùjànnú àti ènìyàn, tí ẹ bá lágbára láti sá jáde nínú àwọn agbègbè sánmọ̀ àti ilẹ̀, ẹ sá jáde. Ẹ kò lè sá jáde àfi pẹ̀lú agbára.');
INSERT INTO "aykyuni" VALUES(55,34,'55:34','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,35,'55:35','Wọ́n máa ju ahọ́n iná àti èéfín (tàbí oje idẹ) iná lé ẹ̀yin méjèèjì lórí, ẹ̀yin méjèèjì kò sì níí lè ranra yín lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(55,36,'55:36','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,37,'55:37','Nítorí náà, nígbà tí sánmọ̀ bá fàya pẹ́rẹpẹ̀rẹ, ó sì máa pọ́n wá wẹ̀ bí epo pupa gbígbá.');
INSERT INTO "aykyuni" VALUES(55,38,'55:38','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,39,'55:39','Nítorí náà, ní ọjọ́ yẹn Wọn kò níí bi ènìyàn àti àlùjànnú kan léèrè nípa ẹ̀ṣẹ̀ rẹ̀; (ó ti wà ní àkọsílẹ̀ ní ọ̀dọ̀ Wa).');
INSERT INTO "aykyuni" VALUES(55,40,'55:40','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,41,'55:41','Wọ́n máa dá àwọn ẹlẹ́ṣẹ̀ mọ̀ pẹ̀lú àmì ara wọn. Wọ́n sì máa fi àásó orí àti ẹsẹ̀ gbá wọn mú.');
INSERT INTO "aykyuni" VALUES(55,42,'55:42','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,43,'55:43','Èyí ni iná Jahanamọ tí àwọn ẹlẹ́ṣẹ̀ ń pè ní irọ́.');
INSERT INTO "aykyuni" VALUES(55,44,'55:44','Wọn yóò máa rìn lọ rìn bọ̀ láààrin Iná àti omi tó gbóná parí.1 ');
INSERT INTO "aykyuni" VALUES(55,45,'55:45','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,46,'55:46','Ọgbà Ìdẹ̀ra méjì ń bẹ fún ẹni tó bá páyà ìdúró rẹ̀ níwájú Olúwa rẹ̀.');
INSERT INTO "aykyuni" VALUES(55,47,'55:47','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,48,'55:48','(Àwọn Ọgbà Ìdẹ̀ra méjèèjì ní) àwọn ẹ̀ka igi tó kún fún àwọn èso oríṣiríṣi.');
INSERT INTO "aykyuni" VALUES(55,49,'55:49','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,50,'55:50','Odò méjì tó ń ṣàn wà nínú ọgbà méjèèjì.');
INSERT INTO "aykyuni" VALUES(55,51,'55:51','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,52,'55:52','Oríṣi méjì méjì ni èso kọ̀ọ̀kan tó wà nínú ọgbà méjèèjì.1 ');
INSERT INTO "aykyuni" VALUES(55,53,'55:53','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,54,'55:54','Wọn yóò rọ̀gbọ̀kú lórí ìtẹ́, tí àwọn ìtẹ́ inú rẹ̀ jẹ́ àrán tó nípọn. Àwọn èso ọgbà méjèèjì sì wà ní àrọ́wọ́tó.');
INSERT INTO "aykyuni" VALUES(55,55,'55:55','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,56,'55:56','Àwọn obìnrin tí kì í wo ọkùnrin mìíràn wà nínú Ọgbà Ìdẹ̀ra. Ènìyàn àti àlùjànnú kan kò sì fọwọ́ kàn wọ́n rí ṣíwájú wọn.');
INSERT INTO "aykyuni" VALUES(55,57,'55:57','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,58,'55:58','Wọ́n dà bíi ìlẹ̀kẹ̀ ṣẹ̀gi àti ìlẹ̀kẹ̀ iyùn (nípa ẹwà àti dídára wọn).');
INSERT INTO "aykyuni" VALUES(55,59,'55:59','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,60,'55:60','Ǹjẹ́ ẹ̀san mìíràn wà fún ṣíṣe rere bí kò ṣe (ẹ̀san) rere.');
INSERT INTO "aykyuni" VALUES(55,61,'55:61','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,62,'55:62','Ọgbà Ìdẹ̀ra méjì kan tún ń bẹ yàtọ̀ sí méjì (àkọ́kọ́ yẹn).');
INSERT INTO "aykyuni" VALUES(55,63,'55:63','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,64,'55:64','Aláwọ̀ ewéko (ni Ọgbà méjèèjì náà).');
INSERT INTO "aykyuni" VALUES(55,65,'55:65','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,66,'55:66','Àwọn odò méjì tó ń tú omi jáde láì dáwọ́ dúró wà (nínú ọgbà méjèèjì náà).');
INSERT INTO "aykyuni" VALUES(55,67,'55:67','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,68,'55:68','Èso ìpanu, dàbínù àti èso rumọ̄n wà nínú ọgbà méjèèjì náà.');
INSERT INTO "aykyuni" VALUES(55,69,'55:69','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,70,'55:70','Àwọn obìnrin rere, arẹwà wà nínú àwọn Ọgbà Ìdẹ̀ra náà.');
INSERT INTO "aykyuni" VALUES(55,71,'55:71','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,72,'55:72','Àwọn ẹlẹ́yinjú-ẹgẹ́ kan (ni wọ́n), tí A fi pamọ́ sínú ilé-ọ̀ṣọ́.');
INSERT INTO "aykyuni" VALUES(55,73,'55:73','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,74,'55:74','Ènìyàn àti àlùjànnú kan kò fọwọ́ kàn wọ́n rí ṣíwájú wọn.');
INSERT INTO "aykyuni" VALUES(55,75,'55:75','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,76,'55:76','Wọn yóò rọ̀gbọ̀kú lórí àwọn tìmùtìmù aláwọ̀ ewéko àti ìtẹ́ tó dára.');
INSERT INTO "aykyuni" VALUES(55,77,'55:77','Nítorí náà, èwo nínú àwọn ìdẹ̀ra Olúwa ẹ̀yin ènìyàn àti àlùjànnú ni ẹ máa pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(55,78,'55:78','Ìbùkún ni fún orúkọ Olúwa rẹ, Atóbi, Alápọ̀n-ọ́nlé.');
INSERT INTO "aykyuni" VALUES(56,1,'56:1','Nígbà tí Ìṣẹ̀lẹ̀ (Àjíǹde) bá ṣẹlẹ̀,');
INSERT INTO "aykyuni" VALUES(56,2,'56:2','- kò sí irọ́ kan nípa ìṣẹ̀lẹ̀ rẹ̀-');
INSERT INTO "aykyuni" VALUES(56,3,'56:3','ó máa mú (àwọn kan) wálẹ̀ (sínú Iná), ó sì máa gbé (àwọn kan) sókè (nínú Ọgbà Ìdẹ̀ra).');
INSERT INTO "aykyuni" VALUES(56,4,'56:4','Nígbà tí wọ́n bá mi ilẹ̀ tìtì ní mímìtìtì,');
INSERT INTO "aykyuni" VALUES(56,5,'56:5','àti nígbà tí Wọ́n bá fọ́ àwọn àpáta ní fífọ́ wómúwómú,');
INSERT INTO "aykyuni" VALUES(56,6,'56:6','- wọ́n sì máa di eruku àfẹ́dànù -');
INSERT INTO "aykyuni" VALUES(56,7,'56:7','ẹ̀yin sì máa jẹ́ oríṣi mẹ́ta.1 ');
INSERT INTO "aykyuni" VALUES(56,8,'56:8','Àwọn èrò ọwọ́ ọ̀tún, kí ni (ó máa ti dára tó fún) àwọn èrò ọwọ́ ọ̀tún?');
INSERT INTO "aykyuni" VALUES(56,9,'56:9','Àwọn èrò ọwọ́ òsì, kí ni (ó máa ti burú tó fún) àwọn èrò ọwọ́ òsì?');
INSERT INTO "aykyuni" VALUES(56,10,'56:10','Àwọn aṣíwájú sì ni àwọn aṣíwájú.');
INSERT INTO "aykyuni" VALUES(56,11,'56:11','Àwọn wọ̀nyẹn ni alásùn-únmọ́ (Allāhu)');
INSERT INTO "aykyuni" VALUES(56,12,'56:12','nínú Ọgbà Ìdẹ̀ra.');
INSERT INTO "aykyuni" VALUES(56,13,'56:13','Wọ́n pọ̀ nínú àwọn ẹni àkọ́kọ́.');
INSERT INTO "aykyuni" VALUES(56,14,'56:14','Wọ́n sì kéré nínú àwọn ẹni Ìkẹ́yìn.');
INSERT INTO "aykyuni" VALUES(56,15,'56:15','(Wọn yóò wà) lórí ìtẹ́ tí wọ́n fi góòlù hun.');
INSERT INTO "aykyuni" VALUES(56,16,'56:16','Wọn yóò rọ̀gbọ̀kú sórí rẹ̀, wọn sì máa dojú kọra wọn.');
INSERT INTO "aykyuni" VALUES(56,17,'56:17','Àwọn ọ̀dọ́kùnrin tí kò níí darúgbó yó sì máa lọ bọ̀ láààrin wọn');
INSERT INTO "aykyuni" VALUES(56,18,'56:18','pẹ̀lú àwọn ife omi, ohun ìrọmi fẹ̀ǹfẹ̀ àti ife ọtí aládùn láti inú odò kan tó ń ṣàn (káà kiri ọ̀dọ̀ wọn).');
INSERT INTO "aykyuni" VALUES(56,19,'56:19','Kò níí fọ́ wọn lórí; kò sì níí mú wọn húnrìrà.');
INSERT INTO "aykyuni" VALUES(56,20,'56:20','Èso tí wọn yóò máa ṣẹ̀ṣà nínú rẹ̀ (tún wà fún wọn).');
INSERT INTO "aykyuni" VALUES(56,21,'56:21','Àti ẹran ẹyẹ, èyí tí wọ́n ń fẹ́ (yó wà fún wọn).');
INSERT INTO "aykyuni" VALUES(56,22,'56:22','Àwọn obìnrin ẹlẹ́yinjú ẹgẹ́ (tún wà fún wọn).');
INSERT INTO "aykyuni" VALUES(56,23,'56:23','Wọ́n dà bí àlúùúlù (òkúta olówó-iye-bíye) tí wọ́n fi pamọ́ sínú apó rẹ̀.');
INSERT INTO "aykyuni" VALUES(56,24,'56:24','(Ìwọ̀nyí ni) ẹ̀san fún ohun tí wọ́n ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(56,25,'56:25','Wọn kò níí gbọ́ ìsọkúsọ àti ọ̀rọ̀ ẹ̀ṣẹ̀ nínú Ọgbà Ìdẹ̀ra.');
INSERT INTO "aykyuni" VALUES(56,26,'56:26','Àyàfi ọ̀rọ̀ àlàáfíà, àlàáfíà.');
INSERT INTO "aykyuni" VALUES(56,27,'56:27','Àwọn èrò ọwọ́ ọtún, kí ni (ó máa ti dára tó fún) àwọn èrò ọwọ́ ọtún?');
INSERT INTO "aykyuni" VALUES(56,28,'56:28','(Wọn yóò wà) ní ìdí igi tí kò ní ẹ̀gún,');
INSERT INTO "aykyuni" VALUES(56,29,'56:29','àti ọ̀gẹ̀dẹ̀ tó so jìgbìnnì,');
INSERT INTO "aykyuni" VALUES(56,30,'56:30','àti ibòji tó gbòòrò,');
INSERT INTO "aykyuni" VALUES(56,31,'56:31','àti omi tó ń ṣàn láì dáwọ́ dúró,');
INSERT INTO "aykyuni" VALUES(56,32,'56:32','àti ọ̀pọ̀lọ́pọ̀ èso,');
INSERT INTO "aykyuni" VALUES(56,33,'56:33','tí kò níí já (ní ọ̀dọ̀ wọn), kò sì níí dèèwọ̀ (fún wọn).');
INSERT INTO "aykyuni" VALUES(56,34,'56:34','(Wọ́n máa wà lórí) ìtẹ́ tí wọ́n gbé sókè.');
INSERT INTO "aykyuni" VALUES(56,35,'56:35','Dájúdájú Àwa dá (àwọn obìnrin Ọgbà Ìdẹ̀ra) ní ẹ̀dá kan (tó yàtọ̀ sí tayé).');
INSERT INTO "aykyuni" VALUES(56,36,'56:36','A sì ṣe wọ́n ní wúńdíá,');
INSERT INTO "aykyuni" VALUES(56,37,'56:37','olólùfẹ́ ọkọ. Wọ́n sì dọ́gba ní ọjọ́ orí.');
INSERT INTO "aykyuni" VALUES(56,38,'56:38','(Wọ́n wà) fún àwọn èrò ọwọ́ ọ̀tún.');
INSERT INTO "aykyuni" VALUES(56,39,'56:39','Wọ́n pọ̀ nínú àwọn ẹni àkọ́kọ́.');
INSERT INTO "aykyuni" VALUES(56,40,'56:40','Wọ́n tún pọ̀ nínú àwọn ẹni Ìkẹ́yìn.1 ');
INSERT INTO "aykyuni" VALUES(56,41,'56:41','Àwọn èrò ọwọ́ òsì, kí ni (ó máa ti burú tó fún) àwọn èrò ọwọ́ òsì?');
INSERT INTO "aykyuni" VALUES(56,42,'56:42','(Wọn yóò wà) nínú atẹ́gùn gbígbóná (iná) àti omi tó gbóná parí,');
INSERT INTO "aykyuni" VALUES(56,43,'56:43','àti ní abẹ́ ibòji eléèéfín dúdú,');
INSERT INTO "aykyuni" VALUES(56,44,'56:44','kò tutù, kò sì dára.');
INSERT INTO "aykyuni" VALUES(56,45,'56:45','Dájúdájú wọ́n ti jẹ́ onígbẹdẹmukẹ ṣíwájú ìyẹn.');
INSERT INTO "aykyuni" VALUES(56,46,'56:46','Wọ́n sì máa ń ṣoríkunkun lórí ẹ̀ṣẹ̀ ńlá.');
INSERT INTO "aykyuni" VALUES(56,47,'56:47','Wọ́n sì máa ń wí pé: “Ṣé nígbà tí a bá di òkú, tí a sì di erùpẹ̀ àti egungun, ǹjẹ́ Wọn yóò tún gbé wa dìde ni?');
INSERT INTO "aykyuni" VALUES(56,48,'56:48','Ṣé àti àwọn bàbá wa, àwọn ẹni àkọ́kọ́?”');
INSERT INTO "aykyuni" VALUES(56,49,'56:49','Sọ pé: “Dájúdájú àwọn ẹni àkọ́kọ́ àti àwọn ẹni Ìkẹ́yìn,');
INSERT INTO "aykyuni" VALUES(56,50,'56:50','dájúdájú A óò kó wọn jọ papọ̀ ní àkókò ọjọ́ kan tí A ti mọ̀.”');
INSERT INTO "aykyuni" VALUES(56,51,'56:51','Lẹ́yìn náà, dájúdájú ẹ̀yin olùṣìnà, olùpe-òdodo-nírọ́,');
INSERT INTO "aykyuni" VALUES(56,52,'56:52','dájúdájú ẹ̀yin máa jẹ nínú igi zaƙƙūm (igi iwọ).');
INSERT INTO "aykyuni" VALUES(56,53,'56:53','Ikùn yín sì máa kún bámúbámú fún (jíjẹ) igi náà.');
INSERT INTO "aykyuni" VALUES(56,54,'56:54','Ẹ̀yin yó sì máa mu omi tó gbóná parí lé e lórí.');
INSERT INTO "aykyuni" VALUES(56,55,'56:55','Ẹ̀yin yóò máa mu ún ní ìmumi ràkúnmí tí òǹgbẹ ń gbẹ.');
INSERT INTO "aykyuni" VALUES(56,56,'56:56','Èyí ni n̄ǹkan àlejò wọn ní Ọjọ́ ẹ̀san.');
INSERT INTO "aykyuni" VALUES(56,57,'56:57','Àwa l’A ṣẹ̀dá yín. Kí ni kò jẹ́ kí ẹ gbàgbọ́ ní òdodo!');
INSERT INTO "aykyuni" VALUES(56,58,'56:58','Ẹ sọ fún Mi nípa àtọ̀ tí ẹ̀ ń dà jáde (sínú àpòlùkẹ́),');
INSERT INTO "aykyuni" VALUES(56,59,'56:59','ṣé ẹ̀yin lẹ ṣẹ̀dá rẹ̀ ni tàbí Àwa l’A ṣẹ̀dá rẹ̀?');
INSERT INTO "aykyuni" VALUES(56,60,'56:60','Àwa l’A yan kádàrá (ọjọ́) ikú fún gbogbo yín. Àwa kò sì lè kágara');
INSERT INTO "aykyuni" VALUES(56,61,'56:61','láti yí irú yín padà (láti erùpẹ̀ sí alààyè), kí á sì tun yín dá (ní ọ̀tun) sínú ohun tí ẹ kò mọ̀.');
INSERT INTO "aykyuni" VALUES(56,62,'56:62','Ẹ kúkú mọ ìṣẹ̀dá àkọ́kọ́ (pé láti ibi àìsí ni ẹ ti di alààyè), ẹ kò ṣe lo ìrántí?');
INSERT INTO "aykyuni" VALUES(56,63,'56:63','Ẹ sọ fún Mi nípa n̄ǹkan tí ẹ̀ ń gbìn sínú ilẹ̀,');
INSERT INTO "aykyuni" VALUES(56,64,'56:64','ṣé ẹ̀yin l’ẹ̀ ń jẹ́ kí ó hù jáde tàbí Àwa l’À ń jẹ́ kí ó hù jáde?');
INSERT INTO "aykyuni" VALUES(56,65,'56:65','Tí A bá fẹ́ ni, Àwa ìbá sọ ọ́ di gbígbẹ, ẹ̀yin yó sì máa ṣèèmọ̀ (tí ẹ óò máa ká àbámọ̀).');
INSERT INTO "aykyuni" VALUES(56,66,'56:66','(Ẹ̀yin yó sì wí pé:) “Dájúdájú àwa ti di onígbèsè.”');
INSERT INTO "aykyuni" VALUES(56,67,'56:67','(Àwọn mìíràn yó sì wí pé:) “Rárá o! Wọ́n ṣe ìkórè oko ní èèwọ̀ fún wa ni.”');
INSERT INTO "aykyuni" VALUES(56,68,'56:68','Ẹ sọ fún mi nípa omi tí ẹ̀ ń mu,');
INSERT INTO "aykyuni" VALUES(56,69,'56:69','ṣé ẹ̀yin l’ẹ̀ ń sọ̀ ọ́ kalẹ̀ láti inú ẹ̀ṣújò ni tàbí Àwa l’À ń sọ̀ ọ́ kalẹ̀?');
INSERT INTO "aykyuni" VALUES(56,70,'56:70','Tí A bá fẹ́ ni, Àwa ìbá ṣe é ní omi tó móró. Ẹ ò ṣe máa dúpẹ́?');
INSERT INTO "aykyuni" VALUES(56,71,'56:71','Ẹ sọ fún Mi nípa iná tí ẹ̀ ń dá,');
INSERT INTO "aykyuni" VALUES(56,72,'56:72','ṣé ẹ̀yin l’ẹ ṣẹ̀dá igi rẹ̀ ni tàbí Àwa ni Aṣẹ̀dá?');
INSERT INTO "aykyuni" VALUES(56,73,'56:73','Àwa ṣe iná ayé ní ìrántí (fún Iná ọ̀run) àti n̄ǹkan èlò fún àwọn onírìn-àjò.');
INSERT INTO "aykyuni" VALUES(56,74,'56:74','Nítorí náà, ṣàfọ̀mọ́ fún orúkọ Olúwa rẹ, Atóbi.');
INSERT INTO "aykyuni" VALUES(56,75,'56:75','Nítorí náà, Mò ń fi àwọn àyè tí ìkọ̀ọ̀kan gbólóhùn al-Ƙur’ān wà nínú sánmọ̀ búra.1 ');
INSERT INTO "aykyuni" VALUES(56,76,'56:76','Dájúdájú ìbúra ńlá ni, tí ẹ bá mọ̀.');
INSERT INTO "aykyuni" VALUES(56,77,'56:77','Dájúdájú (al-Ƙur’ān) ni n̄ǹkan kíké alápọ̀n-ọ́nlé.');
INSERT INTO "aykyuni" VALUES(56,78,'56:78','Ó wà nínú Tírà kan tí wọ́n ń fi ààbò bò (ìyẹn Laohul-Mahfūṭḥ).');
INSERT INTO "aykyuni" VALUES(56,79,'56:79','Kò sí ẹni tó lè fọwọ́ kàn án (ní ọ̀dọ̀ Allāhu) àfi àwọn ẹni mímọ́ (ìyẹn, àwọn mọlāika).');
INSERT INTO "aykyuni" VALUES(56,80,'56:80','Wọ́n sọ al-Ƙur’ān kalẹ̀ láti ọ̀dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(56,81,'56:81','Nítorí náà, ṣé ọ̀rọ̀ (al-Ƙur’ān) yìí l’ẹ̀ ń pè ní irọ́?');
INSERT INTO "aykyuni" VALUES(56,82,'56:82','Ẹ sì ń sọ ìdúpẹ́ arísìkí yín di pé dájúdájú ẹ̀ ń pe òdodo ní irọ́.1 ');
INSERT INTO "aykyuni" VALUES(56,83,'56:83','Kí ni ó máa ti rí (fún yín) nígbà tí ẹ̀mí (yín) bá dé ọ̀nà ọ̀fun,');
INSERT INTO "aykyuni" VALUES(56,84,'56:84','tí ẹ̀yin yó sì máa wò bọ̀ọ̀ nígbà yẹn?');
INSERT INTO "aykyuni" VALUES(56,85,'56:85','Àwa sì súnmọ́ ọn ju ẹ̀yin lọ, ṣùgbọ́n ẹ̀yin kò ríran.1 ');
INSERT INTO "aykyuni" VALUES(56,86,'56:86','Ẹ̀yin kò ṣe jẹ́ ẹni tí A ò níí gbẹ̀san lára rẹ̀,');
INSERT INTO "aykyuni" VALUES(56,87,'56:87','kí ẹ sì dá (ẹ̀mí tí ó fẹ́ bọ́) padà, tí ẹ bá jẹ́ olódodo.');
INSERT INTO "aykyuni" VALUES(56,88,'56:88','Ní ti ẹni tí ó bá wà nínú àwọn alásùn-únmọ́ (Wa),');
INSERT INTO "aykyuni" VALUES(56,89,'56:89','ìsinmi, èsè tó dára àti Ọgbà Ìdẹ̀ra (ni tirẹ̀).');
INSERT INTO "aykyuni" VALUES(56,90,'56:90','Ní ti ẹni tí ó bá wà nínú àwọn èrò ọwọ́ ọ̀tún,');
INSERT INTO "aykyuni" VALUES(56,91,'56:91','àlàáfíà ni fún ọ láààrin àwọn èrò ọwọ́ ọ̀tún.');
INSERT INTO "aykyuni" VALUES(56,92,'56:92','Ní ti ẹni tí ó bá wà nínú àwọn olùpe-òdodo-nírọ́, àwọn olùṣìnà,');
INSERT INTO "aykyuni" VALUES(56,93,'56:93','n̄ǹkan àlejò (wọn) ni omi tó gbóná parí');
INSERT INTO "aykyuni" VALUES(56,94,'56:94','àti wíwọ inú iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(56,95,'56:95','Dájúdájú èyí, òhun ni òdodo tó dájú.');
INSERT INTO "aykyuni" VALUES(56,96,'56:96','Nítorí náà, ṣàfọ̀mọ́ fún orúkọ Olúwa rẹ, Atóbi.');
INSERT INTO "aykyuni" VALUES(57,1,'57:1','Ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀ ń ṣàfọ̀mọ́ fún Allāhu. Òun sì ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(57,2,'57:2','TiRẹ̀ ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Ó ń sọ ẹ̀dá di alààyè. Ó sì ń sọ ẹ̀dá di òkú. Òun ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(57,3,'57:3','Òun ni Àkọ́kọ́ àti Ìkẹ́yìn. Ó hàn (pẹ̀lú àmì bíbẹ Rẹ̀). Ó sì pamọ́ (fún gbogbo ojú nílé ayé). Òun ni Onímọ̀ nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(57,4,'57:4','Òun ni Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ fún ọjọ́ mẹ́fà. Lẹ́yìn náà, Ó gúnwà sí orí Ìtẹ́-ọlá. Ó mọ n̄ǹkan tó ń wọ inú ilẹ̀ àti n̄ǹkan tó ń jáde láti inú rẹ̀, àti n̄ǹkan tó ń sọ̀kalẹ̀ láti inú sánmọ̀ àti n̄ǹkan tó ń gùnkè sínú rẹ̀. Àti pé Ó wà pẹ̀lú yín ní ibikíbi tí ẹ bá wà (pẹ̀lú ìmọ̀ Rẹ̀).Allāhu sì ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́. ');
INSERT INTO "aykyuni" VALUES(57,5,'57:5','TiRẹ̀ ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Ọ̀dọ̀ Allāhu sì ni wọ́n máa ṣẹ́rí àwọn ọ̀rọ̀ ẹ̀dá padà sí.');
INSERT INTO "aykyuni" VALUES(57,6,'57:6','Ó ń ti òru bọ inú ọ̀sán. Ó sì ń ti ọ̀sán bọ inú òru. Òun sì ni Onímọ̀ nípa ohun tó wà nínú igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(57,7,'57:7','Ẹ gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀. Kí ẹ sì ná nínú ohun tí Ó fi yín ṣe àrólé lórí rẹ̀. Nítorí náà, àwọn tó gbàgbọ́ ní òdodo nínú yín, tí wọ́n sì náwó (sí ojú ọ̀nà ẹ̀sìn), ẹ̀san tó tóbi wà fún wọn.');
INSERT INTO "aykyuni" VALUES(57,8,'57:8','Kí ni ó mu yín tí ẹ̀yin kò fi níí gbàgbọ́ ní òdodo nínú Allāhu? Òjíṣẹ́ náà sì ń pè yín nítorí kí ẹ lè gbàgbọ́ ní òdodo nínú Olúwa yín. Allāhu sì ti gba àdéhùn lọ́wọ́ yín, tí ẹ̀yin bá jẹ́ onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(57,9,'57:9','Òun ni Ẹni tó ń sọ àwọn āyah tó yanjú kalẹ̀ fún ẹrúsìn Rẹ̀ nítorí kí ó lè mu yín kúrò nínú àwọn òkùnkùn wá sínú ìmọ́lẹ̀. Dájúdájú Allāhu ni Aláàánú, Àṣàkẹ́-ọ̀run fún yín.');
INSERT INTO "aykyuni" VALUES(57,10,'57:10','Kí ni ó mu yín tí ẹ̀yin kò fi níí náwó fún ẹ̀sìn Allāhu? Ti Allāhu sì ni ogún àwọn sánmọ̀ àti ilẹ̀. Ẹni tí ó náwó ṣíwájú ṣíṣí ìlú Mọkkah, tí ó tún jagun ẹ̀sìn, kò dọ́gba láààrin yín (sí àwọn mìíràn). Àwọn wọ̀nyẹn ni ẹ̀san wọn tóbi ju ti àwọn tó náwó lẹ́yìn ìgbà náà, tí wọ́n tún jagun ẹ̀sìn. Ẹnì kọ̀ọ̀kan sì ni Allāhu ṣ’àdéhùn ẹ̀san rere fún. Allāhu sì ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(57,11,'57:11','Ta ni ẹni tí ó máa yá Allāhu ní dúkìá tó dára, Ó sì máa ṣàdìpèlé rẹ̀ fún un. Ẹ̀san alápọ̀n-ọ́nlé sì tún wà fún un.');
INSERT INTO "aykyuni" VALUES(57,12,'57:12','Ní ọjọ́ tí o máa rí àwọn onígbàgbọ́ òdodo lọ́kùnrin àti onígbàgbọ́ òdodo lóbìnrin, tí ìmọ́lẹ̀ wọn yóò máa tàn níwájú wọn àti ní ọwọ́ ọ̀tún wọn, (A óò sọ pé:) “́Ìró ìdùnnú yín ní òní ni àwọn Ọgbà Ìdẹ̀ra kan tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀.” Olùṣegbére ni yín nínú rẹ̀. Ìyẹn sì ni èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(57,13,'57:13','Ní ọjọ́ tí àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí lọ́kùnrin àti àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí lóbìnrin yóò wí fún àwọn tó gbàgbọ́ ní òdodo pé: “Ẹ dúró fún wa ná, ẹ jẹ́ kí á mú nínú ìmọ́lẹ̀ yín.” A óò sọ fún wọn pé: “Ẹ padà s’ẹ́yìn yín, kí ẹ lọ mú ìmọ́lẹ̀.”Wọ́n sì máa fi ògiri kan tó ní ìlẹ̀kùn sáàrin wọn. Ìkẹ́ wà nínú rẹ̀ (ìyẹn ní ẹ̀yìn ìlẹ̀kùn náà ní ibi tí àwọn onígbàgbọ́ òdodo wà), ìyà sì wà ní òde rẹ̀ ní ọwọ́ iwájú rẹ̀ (ní ibi tí àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí). ');
INSERT INTO "aykyuni" VALUES(57,14,'57:14','(Àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí) yóò pe (àwọn onígbàgbọ́ òdodo) pé: “́Ṣé àwa kò ti wà pẹ̀lú yín ni?”́ Wọ́n sọ pé: “́Rárá, ṣùgbọ́n ẹ kó ìyọnu bá ẹ̀mí ara yín (nípasẹ̀ ìṣọ̀bẹ-ṣèlu), ẹ retí ìparun (wá), ẹ sì ṣeyèméjì (sí òdodo). Àwọn ìfẹ́-ọkàn àti ọ̀rọ̀ irọ́ sì tàn yín jẹ títí àṣẹ Allāhu fi dé. Àti pé (Èṣù) ẹlẹ́tàn sì tàn yín jẹ nípa Allāhu.');
INSERT INTO "aykyuni" VALUES(57,15,'57:15','Nítorí náà, ní òní Àwa kò níí gba ìtánràn kan ní ọwọ́ ẹ̀yin àti àwọn tó ṣàì gbàgbọ́. Iná ni ibùgbé yín. Òhun l’ó tọ́ si yín. Ìkángun náà sì burú.');
INSERT INTO "aykyuni" VALUES(57,16,'57:16','Ṣé àkókò kò tí ì tó fún àwọn tó gbàgbọ́ ní òdodo pé kí ọkàn wọn rọ̀ fún ìrántí Allāhu àti ohun tó sọ̀kalẹ̀ nínú òdodo (ìyẹn, al-Ƙur’ān). Kí wọ́n sì má ṣe dà bí àwọn tí A fún ní Tírà ní ìṣáájú; ìgbà pẹ́ lórí wọn, ọkàn wọn bá le. Ọ̀pọ̀lọ́pọ̀ nínú wọn sì ni òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(57,17,'57:17','Ẹ mọ̀ pé dájúdájú Allāhu l’Ó ń sọ ilẹ̀ di àyè lẹ́yìn tí ilẹ̀ ti kú. A kúkú ti ṣàlàyé àwọn āyah náá nítorí kí ẹ lè ṣe làákàyè.');
INSERT INTO "aykyuni" VALUES(57,18,'57:18','Dájúdájú àwọn olùtọrẹ lọ́kùnrin àti àwọn olùtọrẹ lóbìnrin, tí wọ́n tún yá Allāhu ní dúkìá tó dára, (Allāhu) yóò ṣàdìpèlé rẹ̀ fún wọn. Ẹ̀san alápọ̀n-ọ́nlé sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(57,19,'57:19','Àwọn tó gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀, àwọn wọ̀nyẹn ni àwọn olódodo àti ẹlẹ́rìí ní ọ̀dọ̀ Olúwa wọn. Ẹ̀san wọn àti ìmọ́lẹ̀ wọn wà fún wọn. Àwọn tó sì ṣàì gbàgbọ́, tí wọ́n tún pe àwọn āyah Wa ní irọ́, àwọn wọ̀nyẹn ni èrò inú iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(57,20,'57:20','Ẹ mọ̀ pé dájúdájú ìṣẹ̀mí ayé yìí jẹ́ eré, ìranù, ọ̀ṣọ́, ṣíṣe ìyanràn láààrin ara yín àti wíwá ọ̀pọ̀ dúkìá àti ọmọ. (Ìwọ̀nyí) dà bí òjò (tó mú irúgbìn jáde). Irúgbìn rẹ̀ sì ń jọ àwọn aláìgbàgbọ́ lójú. Lẹ́yìn náà, ó máa gbẹ. O sì máa rí i ní pípọ́n. Lẹ́yìn náà, ó máa di rírún (tí atẹ́gùn máa fẹ́ dànù). Ìyà líle àti àforíjìn pẹ̀lú ìyọ́nú láti ọ̀dọ̀ Allāhu sì wà ní ọ̀run. Kí sì ni ìṣẹ̀mí ayé yìí bí kò ṣe ìgbádùn ẹ̀tàn.');
INSERT INTO "aykyuni" VALUES(57,21,'57:21','Ẹ yára lọ síbi àforíjìn láti ọ̀dọ̀ Olúwa yín àti Ọgbà Ìdẹ̀ra tí fífẹ̀ rẹ̀ dà bí fífẹ̀ sánmọ̀ àti ilẹ̀. Wọ́n pa á lésè sílẹ̀ de àwọn tó gbàgbọ́ nínú Allāhu àti àwọn Òjíṣẹ́ Rẹ̀. Ìyẹn ni oore àjùlọ Allāhu tí Ó ń fún ẹni tí Ó bá fẹ́. Allāhu sì ni Olóore ńlá.');
INSERT INTO "aykyuni" VALUES(57,22,'57:22','Àdánwò kan kò níí ṣẹlẹ̀ ní orí ilẹ̀ tàbí (kí ó ṣẹlẹ̀) sí ẹ̀yin àyàfi kí ó ti wà nínú Tírà ṣíwájú kí A tó dá ẹ̀dá. Dájúdájú ìyẹn rọ̀rùn fún Allāhu.');
INSERT INTO "aykyuni" VALUES(57,23,'57:23','(Ìṣẹ̀lẹ̀ ń tọ kádàrá lẹ́yìn) nítorí kí ẹ má ṣe banújẹ́ lórí ohun tí ó bá bọ́ fún yín àti nítorí kí ẹ má ṣe yọ àyọ̀jù lórí ohun tí Ó bá fún yín. Allāhu kò sì nífẹ̀ẹ́ sí gbogbo onígbèéraga, onífáàrí.');
INSERT INTO "aykyuni" VALUES(57,24,'57:24','Àwọn tó ń ṣahun, tí wọ́n sì ń pa àwọn ènìyàn láṣẹ ahun ṣíṣe (kí wọ́n mọ̀ pé) ẹnikẹ́ni tí ó bá pẹ̀yìndà (tí kò náwó fún ẹ̀sìn), dájúdájú Allāhu, Òun ni Ọlọ́rọ̀, Ẹlẹ́yìn.');
INSERT INTO "aykyuni" VALUES(57,25,'57:25','Dájúdájú A fi àwọn ẹ̀rí tó yanjú rán àwọn Òjíṣẹ́ Wa. A sọ Tírà àti òṣùwọ̀n kalẹ̀ fún wọn nítorí kí àwọn ènìyàn lè rí déédé ṣe (láààrin ara wọn). A tún sọ irin kalẹ̀. Ohun ìjagun tó lágbára àti àwọn àǹfààní wà nínú rẹ̀ fún àwọn ènìyàn nítorí kí Allāhu lè ṣàfi hàn ẹni tó ń ran (ẹ̀sìn) Rẹ̀ àti Òjíṣẹ́ Rẹ̀ lọ́wọ́ ní ìkọ̀kọ̀. Dájúdájú Allāhu ni Alágbára, Olùborí.');
INSERT INTO "aykyuni" VALUES(57,26,'57:26','Dájúdájú A fi iṣẹ́ rán (Ànábì) Nūh àti (Ànábì) ’Ibrọ̄hīm. A fi ipò Ànábì sínú àwọn àrọ́mọdọ́mọ àwọn méjèèjì pẹ̀lú Tírà (tí A fún wọn). Olùmọ̀nà wà nínú wọn. Ọ̀pọ̀lọ́pọ̀ nínú wọn sì ni òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(57,27,'57:27','Lẹ́yìn náà, A fi àwọn Òjíṣẹ́ Wa tẹ̀lé orípa wọn. A sì mú ‘Īsā ọmọ Mọryam tẹ̀lé wọn. A fún un ni al-’Injīl. A fi àánú àti ìkẹ́ sínú ọkàn àwọn tó tẹ̀lé e. Àṣà àdáwà (láì lọ́kọ tàbí láì laya) wọ́n ṣe àdádáálẹ̀ rẹ̀ ni. A kò ṣe é ní ọ̀ran-anyàn fún wọn. (Wọn kò sì ṣe àdáwà fún kiní kan) bí kò ṣe láti fi wá ìyọ́nú Allāhu. Wọn kò sì rí i ṣọ́ bí ó ṣe yẹ kí wọ́n ṣọ́ ọ. Nítorí náà, A fún àwọn tó gbàgbọ́ ní òdodo nínú wọn ní ẹ̀san wọn. Ọ̀pọ̀lọ́pọ̀ nínú wọn sì ni òbìlẹ̀jẹ́.1 ');
INSERT INTO "aykyuni" VALUES(57,28,'57:28','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ bẹ̀rù Allāhu, kí ẹ sì gba Òjíṣẹ́ Rẹ̀ gbọ́ ní òdodo, Allāhu máa fún yín ní ìlọ́po méjì nínú ìkẹ́ Rẹ̀, Ó máa fún yín ní ìmọ́lẹ̀ tí ẹ óò máa fi rìn,Ó sì máa forí jìn yín. Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(57,29,'57:29','(Allāhu ṣe bẹ́ẹ̀ fún yín) nítorí kí àwọn ahlu-l-kitāb lè mọ̀ pé àwọn kò ní agbára lórí kiní kan nínú oore àjùlọ Allāhu. Àti pé dájúdájú oore àjùlọ, ọwọ́ Allāhu l’ó wà. Ó sì ń fún ẹni tí Ó bá fẹ́. Allāhu sì ni Olóore ńlá.');
INSERT INTO "aykyuni" VALUES(58,1,'58:1','Dájúdájú Allāhu ti gbọ́ ọ̀rọ̀ (obìnrin) tó ń bá ọ ṣe àríyànjiyàn nípa ọkọ rẹ̀, tí ó sì ń ṣàròyé fún Allāhu. Allāhu sì ń gbọ ìsọ̀rọ̀gbèsì ẹ̀yin méjèèjì. Dájúdájú Allāhu ni Olùgbọ́, Olùríran.');
INSERT INTO "aykyuni" VALUES(58,2,'58:2','Àwọn tó ń fi ẹ̀yìn ìyàwó wọn wé ti ìyá wọn nínú yín, àwọn ìyàwó kì í ṣe ìyá wọn. Kò sí ẹni tí ó jẹ́ ìyá wọn bí kò ṣe ìyá tí ó bí wọn lọ́mọ.Dájúdájú wọ́n ń sọ aburú nínú ọ̀rọ̀ àti ọ̀rọ̀ irọ́. Dájúdájú Allāhu ni Alámòójúkúrò, Aláforíjìn. ');
INSERT INTO "aykyuni" VALUES(58,3,'58:3','Àwọn tó ń fi ẹ̀yìn ìyàwó wọn wé ti ìyá wọn, lẹ́yìn náà tí wọ́n ń ṣẹ́rí kúrò níbi ohun tí wọ́n sọ, wọn máa tú ẹrú kan sílẹ̀ lóko ẹrú ṣíwájú kí àwọn méjèèjì tó lè súnmọ́ ara wọn. Ìyẹn ni À ń fi ṣe wáàsí fún yín. Allāhu sì ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(58,4,'58:4','Ẹni tí kò bá rí (ẹrú), ó máa gba ààwẹ̀ oṣù méjì ní tẹ̀léǹtẹ̀lé ṣíwájú kí àwọn méjèèjì tó lè súnmọ́ ara wọn. Ẹni tí kò bá ní agbára (ààwẹ̀), ó máa bọ́ ọgọ́ta tálíkà. Ìyẹn nítorí kí ẹ lè ní ìgbàgbọ́ òdodo nínú Allāhu àti Òjíṣẹ́ Rẹ̀. Ìwọ̀nyí sì ni àwọn ẹnu-ààlà (òfin) tí Allāhu gbé kalẹ̀ fún ẹ̀dá. Ìyà ẹlẹ́ta-eléro sì wà fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(58,5,'58:5','Dájúdájú àwọn tó ń yapa Allāhu àti Òjíṣẹ́ Rẹ̀, A óò yẹpẹrẹ wọn gẹ́gẹ́ bí A ṣe yẹpẹrẹ àwọn tó ṣíwájú wọn. A kúkú ti sọ àwọn āyah tó yanjú kalẹ̀. Ìyà tí í yẹpẹrẹ ẹ̀dá sì wà fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(58,6,'58:6','Ní ọjọ́ tí Allāhu yóò gbé gbogbo wọn dìde, Ó sì máa fún wọn ní ìró ohun tí wọ́n ṣe níṣẹ́. Allāhu ṣe àkọsílẹ̀ rẹ̀, àwọn sì gbàgbé rẹ̀. Allāhu sì ni Arínú-róde gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(58,7,'58:7','Ṣé o kò wòye pé dájúdájú Allāhu mọ ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ ni? Kò sí ọ̀rọ̀ ìkọ̀kọ̀ kan láààrin ẹni mẹ́ta àfi kí Allāhu ṣe ìkẹrin wọn àti ẹni márùn-ún àfi kí Ó ṣe ìkẹfà wọn. Wọ́n kéré sí ìyẹn, wọ́n tún pọ̀ (ju ìyẹn) àfi kí Ó wà pẹ̀lú wọn ní ibikíbi tí wọ́n bá wà.Lẹ́yìn náà, Ó máa fún wọn ní ìró ohun tí wọ́n ṣe níṣẹ́ ní Ọjọ́ Àjíǹde. Dájúdájú Allāhu ni Onímọ̀ nípa gbogbo n̄ǹkan. ');
INSERT INTO "aykyuni" VALUES(58,8,'58:8','Ṣé o kò rí àwọn tí A kọ ọ̀rọ̀ ìkọ̀kọ̀ fún, lẹ́yìn náà tí wọ́n tún padà síbi ohun tí A kọ̀ fún wọn. Wọ́n ń bára wọn sọ ọ̀rọ̀ ìkọ̀kọ̀ lórí ẹ̀ṣẹ̀, àbòsí àti ìyapa Òjíṣẹ́. Nígbà tí wọ́n bá sì dé ọ̀dọ̀ rẹ, wọn yóò kí ọ ní kíkí tí Allāhu kò fi kí ọ. Wọ́n sì ń wí sínú ẹ̀mí wọn pé: “Kí ni kò jẹ́ kí Allāhu jẹ wá níyà lórí ohun tí à ń wí (ní ìkọ̀kọ̀)!” Iná Jahanamọ máa tó wọn. Wọ́n máa wọ inú rẹ̀. Ìkángun náà sì burú.');
INSERT INTO "aykyuni" VALUES(58,9,'58:9','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nígbà tí ẹ bá ń bára yín sọ ọ̀rọ̀ ìkọ̀kọ̀, ẹ má ṣe sọ ọ̀rọ̀ ìkọ̀kọ̀ lórí ẹ̀ṣẹ̀, àbòsí àti ìyapa Òjíṣẹ́. Ẹ bára yín sọ ọ̀rọ̀ ìkọ̀kọ̀ lórí iṣẹ́ rere àti ìbẹ̀rù Allāhu. Ẹ bẹ̀rù Allāhu, Ẹni tí wọn yóò ko yín jọ sí ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(58,10,'58:10','Dájúdájú ọ̀rọ̀ ìkọ̀kọ̀ (burúkú) ń wá láti ọ̀dọ̀ Èṣù nítorí kí ó lè kó ìbànújẹ́ bá àwọn tó gbàgbọ́ ní òdodo. Kò sì lè fi kiní kan kó ìnira bá wọn àfi pẹ̀lú ìyọ̀ǹda Allāhu. Allāhu sì ni kí àwọn onígbàgbọ́ òdodo gbáralé.');
INSERT INTO "aykyuni" VALUES(58,11,'58:11','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nígbà tí wọ́n bá sọ fún yín pé kí ẹ gbara yín láyè nínú àwọn ibùjókòó, ẹ gbara yín láyè. Allāhu yóò f’àyè gbà yín. Nígbà tí wọ́n bá sọ pé: “Ẹ dìde (síbi iṣẹ́ rere).” Ẹ dìde (sí i). Allāhu yóò ṣàgbéga àwọn ipò fún àwọn tó gbàgbọ́ ní òdodo àti àwọn tí A fún ní ìmọ̀ nínú yín. Allāhu sì ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(58,12,'58:12','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nígbà tí ẹ bá bá Òjíṣẹ́ sọ̀rọ̀ àtẹ̀sọ,ẹ fi ọrẹ títa ṣíwájú ọ̀rọ̀ àtẹ̀sọ yín. Ìyẹn lóore jùlọ fún yín. Ó sì fọ̀ yín mọ́ jùlọ. Tí ẹ ò bá sì rí (ọrẹ), dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(58,13,'58:13','Ṣé ẹ̀ ń páyà (òṣì) níbi kí ẹ máa ti ọrẹ ṣíwájú àwọn ọ̀rọ̀ àtẹ̀sọ yín ni? Nígbà tí ẹ ò ṣe é, Allāhu sì gba ìronúpìwàdà yín. Nítorí náà, ẹ kírun, ẹ yọ Zakāh. Ẹ tẹ̀lé ti Allāhu àti Òjíṣẹ́ Rẹ̀. Allāhu ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(58,14,'58:14','Ṣé o ò rí àwọn (ṣọ̀bẹ-ṣèlu mùsùlùmí) tí wọ́n mú ìjọ kan tí Allāhu bínú sí ní ọ̀rẹ́ (ìyẹn àwọn yẹhudi)? Wọn kì í ṣe ara yín, (àwọn munāfiki kì í ṣe ara ẹ̀yin mùsùlùmí) wọn kì í sì ṣe ara wọn (àwọn munāfiki kì í sì ṣe ara àwọn yẹhudi). Wọ́n yó sì máa búra lórí irọ́, wọ́n sì mọ̀.');
INSERT INTO "aykyuni" VALUES(58,15,'58:15','Allāhu ti pèsè ìyà líle dè wọ́n. Dájúdájú ohun tí wọ́n ń ṣe níṣẹ́ burú.');
INSERT INTO "aykyuni" VALUES(58,16,'58:16','Wọ́n fi ìbúra wọn ṣe ààbò; wọ́n sì ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu. Nítorí náà, ìyà tí í yẹpẹrẹ ẹ̀dá wà fún wọn.');
INSERT INTO "aykyuni" VALUES(58,17,'58:17','Àwọn dúkìá wọn àti àwọn ọmọ wọn kò níí fi kiní kan rọ̀ wọ́n lọ́rọ̀ ní ọ̀dọ̀ Allāhu. Àwọn wọ̀nyẹn ni èrò inú Iná. Olùṣegbére sì ni wọ́n nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(58,18,'58:18','Ní ọjọ́ tí Allāhu yóò gbé gbogbo wọn dìde pátápátá, wọn yó sì máa búra fún Un gẹ́gẹ́ bí wọ́n ṣe ń búra fún yín. Wọ́n ń lérò pé dájúdájú àwọn ti rí n̄ǹkan ṣe. Gbọ́! Dájúdájú àwọn gan-an ni òpùrọ́.');
INSERT INTO "aykyuni" VALUES(58,19,'58:19','Èṣù jẹ gàba lé wọn lórí. Ó sì mú wọn gbàgbé ìrántí Allāhu. Àwọn wọ̀nyẹn ni ìjọ Èṣù. Gbọ́! Dájúdájú ìjọ èṣù, àwọn ni ẹni òfò.');
INSERT INTO "aykyuni" VALUES(58,20,'58:20','Dájúdájú àwọn tó ń yapa Allāhu àti Òjíṣẹ́ Rẹ̀, àwọn wọ̀nyẹn wà nínú àwọn olùyẹpẹrẹ jùlọ.');
INSERT INTO "aykyuni" VALUES(58,21,'58:21','Allāhu kọ ọ́ pé: “Dájúdájú Mo máa borí; Èmi àti àwọn Òjíṣẹ́ Mi.” Dájúdájú Allāhu ni Alágbára, Olùborí.');
INSERT INTO "aykyuni" VALUES(58,22,'58:22','O ò níí rí ìjọ kan tó gbàgbọ́ nínú Allāhu àti Ọjọ́ Ìkẹ́yìn, kí wọ́n máa nífẹ̀ẹ́ àwọn tó bá yapa Allāhu àti Òjíṣẹ́ Rẹ̀ kódà kí wọ́n jẹ́ àwọn bàbá wọn, tàbí àwọn ọmọ wọn tàbí àwọn arákùnrin wọn tàbí àwọn ìbátan wọn. Àwọn wọ̀nyẹn ni Allāhu ti kọ ìgbàgbọ́ òdodo sínú ọkàn wọn. Allāhu sì fi ẹ̀rí òdodo láti ọ̀dọ̀ Rẹ̀ ràn wọ́n lọ́wọ́.Ó sì máa mú wọn wọ inú àwọn Ọgbà Ìdẹ̀ra, èyí tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀. Allāhu yọ́nú sí wọn. Wọ́n sì yọ́nú sí (ohun tí Allāhu fún wọn). Àwọn wọ̀nyẹn ni ìjọ Allāhu. Gbọ́! Dájúdájú ìjọ Allāhu, àwọn ni olùjèrè.2 ');
INSERT INTO "aykyuni" VALUES(59,1,'59:1','Ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ ń ṣàfọ̀mọ́ fún Allāhu. Òun sì ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(59,2,'59:2','Òun ni Ẹni tí Ó mú àwọn tó ṣàì gbàgbọ́ nínú àwọn onítíràjáde kúrò nínú ilé wọn fún àkọ́kọ́ àkójọ (wọ́n sínú ìlú Ṣām). Ẹ̀yin kò lérò pé wọn yóò jáde. Àwọn náà sì lérò pé dájúdájú àwọn odi ilé wọn yóò dáàbò bo àwọn lọ́dọ̀ Allāhu. Ṣùgbọ́n Allāhu wá bá wọn ní ibi tí wọn kò ti lérò; Allāhu sì ju ìbẹ̀rù sínú ọkàn wọn. Wọ́n ń fi ọwọ́ ara wọn àti ọwọ́ àwọn onígbàgbọ́ òdodo wó àwọn ilé wọn. Nítorí náà, ẹ wòye, ẹ̀yin tí ẹ ní ojú ìríran. ');
INSERT INTO "aykyuni" VALUES(59,3,'59:3','Àti pé tí kò bá jẹ́ pé Allāhu ti kọ ìjáde kúrò nínú ìlú Mọdīnah lé àwọn onítírà lórí ni, dájúdájú (Allāhu) ìbá jẹ wọ́n níyà nílé ayé. Ìyà Iná sì tún wà fún wọn ní ọ̀run.1 ');
INSERT INTO "aykyuni" VALUES(59,4,'59:4','Ìyẹn nítorí pé, dájúdájú wọ́n yapa (ọ̀rọ̀) Allāhu àti Òjíṣẹ́ Rẹ̀. Ẹnikẹ́ni tí ó bá sì yapa (ọ̀rọ̀) Allāhu, dájúdájú Allāhu le níbi ìyà.');
INSERT INTO "aykyuni" VALUES(59,5,'59:5','Ẹ̀yin kò níí ge igi dàbínù kan tàbí kí ẹ fi sílẹ̀ kí ó dúró sórí gbòǹgbò rẹ̀, (àfi) pẹ̀lú ìyọ̀ǹda Allāhu. (Ó rí bẹ́ẹ̀) nítorí kí Ó lè dójú ti àwọn òbìlẹ̀jẹ́ ni.1 ');
INSERT INTO "aykyuni" VALUES(59,6,'59:6','Ohunkóhun tí Allāhu bá dá padà láti ọ̀dọ̀ wọn fún Òjíṣẹ́ Rẹ̀, tí ẹ kò gbé ẹṣin àti ràkúnmí sáré fún (ti Òjíṣẹ́ Rẹ̀ ni). Ṣùgbọ́n Allāhu yóò máa fún àwọn Òjíṣẹ́ Rẹ̀ ní agbára lórí ẹni tí Ó bá fẹ́. Allāhu sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(59,7,'59:7','Ohunkóhun tí Allāhu bá dá padà láti ọ̀dọ̀ àwọn ará ìlú fún Òjíṣẹ́ Rẹ̀, (ó jẹ́) ti Allāhu àti ti Òjíṣẹ́ àti ti àwọn ìbátan, àwọn ọmọ òrukàn, àwọn tálíkà àti onírìn-àjò (tí agara dá) nítorí kí ó má baá jẹ́ àdápín láààrin àwọn ọlọ́rọ̀ nínú yín. Ohunkóhun tí Òjíṣẹ́ bá fún yín, ẹ gbà á. Ohunkóhun tí ó bá sì kọ̀ fún yín, ẹ jáwọ́ nínú rẹ. Kí ẹ sì bẹ̀rù Allāhu. Dájúdájú Allāhu le níbi ìyà.');
INSERT INTO "aykyuni" VALUES(59,8,'59:8','(Ìkógun náà tún wà fún) àwọn aláìní, (ìyẹn,) al-Muhājirūn, àwọn tí wọ́n lé jáde kúrò nínú ilé wọn àti níbi dúkìá wọn, tí wọ́n sì ń wá oore àjùlọ àti ìyọ́nú láti ọ̀dọ̀ Allāhu, tí wọ́n sì ń ṣàrànṣe fún (ẹ̀sìn) Allāhu àti Òjíṣẹ́ Rẹ̀. Àwọn wọ̀nyẹn gan-an ni olódodo.');
INSERT INTO "aykyuni" VALUES(59,9,'59:9','Àwọn tó ń gbé nínú ìlú Mọdīnah, tí wọ́n sì ní ìgbàgbọ́ òdodo ṣíwájú wọn,wọ́n nífẹ̀ẹ́ àwọn tó fi ìlú Mọkkah sílẹ̀ wá sí ọ̀dọ̀ wọn (ní ìlú Mọdīnah). Wọn kò sì ní bùkátà kan nínú igbá-àyà wọn sí n̄ǹkan tí wọ́n bá fún wọn. Wọ́n sì ń gbé àjùlọ fún wọn lórí ẹ̀mí ara wọn, kódà kí ó jẹ́ pé àwọn gan-an ní bùkátà (sí ohun tí wọ́n fún wọn). Ẹnikẹ́ni tí A bá ṣọ́ níbi ahun àti ọ̀kánjúà inú ẹ̀mí rẹ̀, àwọn wọ̀nyẹn gan-an ni olùjèrè. ');
INSERT INTO "aykyuni" VALUES(59,10,'59:10','Àwọn tó dé lẹ́yìn wọn ń sọ pé: “Olúwa wa, foríjin àwa àti àwọn ọmọ ìyá wa tó ṣíwájú wa nínú ìgbàgbọ́ òdodo. Má ṣe jẹ́ kí inúnibíni wà nínú ọkàn wa sí àwọn tó gbàgbọ́ ní òdodo. Olúwa wa, dájúdájú Ìwọ ni Aláàánú, Àṣàkẹ́-ọ̀run.”1 ');
INSERT INTO "aykyuni" VALUES(59,11,'59:11','Ṣé o ò rí àwọn tó ṣàgàbà ǹgebè nínú ẹ̀sìn tí wọ́n ń wí fún àwọn ọmọ ìyá wọn, àwọn tó ṣàì gbàgbọ́ nínú àwọn onítírà, pé “Dájúdájú tí wọ́n bá le yín jáde (kúrò nínú ìlú), àwa náà gbọ́dọ̀ jáde pẹ̀lú yín ni. Àwa kò sì níí tẹ̀lé àṣẹ ẹnì kan lórí yín láéláé. Tí wọ́n bá sì gbé ogun dìde si yín, àwa gbọ́dọ̀ ṣe ìrànlọ́wọ́ fún yín ni.” Allāhu sì ń jẹ́rìí pé dájúdájú òpùrọ́ ni wọ́n.');
INSERT INTO "aykyuni" VALUES(59,12,'59:12','Dájúdájú tí wọ́n bá lé wọn jáde, wọn kò níí jáde pẹ̀lú wọn.Dájúdájú tí wọ́n bá sì gbógun tì wọ́n, wọn kò níí ṣe ìrànlọ́wọ́ fún wọn. Tí wọ́n bá sì wá ìrànlọ́wọ́ wọn, dájúdájú wọn yóò pẹ̀yìndà (láti fẹsẹ̀ fẹ́ẹ). Lẹ́yìn náà, wọn kò sì níí ràn wọ́n lọ́wọ́. ');
INSERT INTO "aykyuni" VALUES(59,13,'59:13','Dájúdájú ẹ̀yin ni ẹ̀rùjẹ̀jẹ̀ tó lágbára ju Allāhu lọ nínú igbá-àyà wọn. Ìyẹn nítorí pé, dájúdájú àwọn ni ìjọ tí kò gbọ́ àgbọ́yé.');
INSERT INTO "aykyuni" VALUES(59,14,'59:14','Wọn kò lè parapọ̀ dojú ìjà kọ yín àfi (kí wọ́n) wà nínú odi ìlú tàbí (kí wọ́n wà) lẹ́yìn àwọn ògiri. Ogun ààrin ara wọn gan-an le. Ò ń lérò pé wọ́n wà ní ìrẹ́pọ̀ ni, ọ̀tọ̀ọ̀tọ̀ sì ni ọkàn wọn wà. Ìyẹn nítorí pé, dájúdájú àwọn ni ìjọ tí kò ní làákàyè.');
INSERT INTO "aykyuni" VALUES(59,15,'59:15','Àpẹ̀ẹrẹ wọn dà bí àwọn tó ṣíwájú wọn tí kò tí ì pẹ́ lọ títí. Wọ́n tọ́ aburú ọ̀rọ̀ ara wọn wò. Ìyà ẹlẹ́ta-eléro sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(59,16,'59:16','Àpẹ̀ẹrẹ wọn dà bí Èṣù nígbà tí ó bá wí fún ènìyàn pé: “Ṣàì gbàgbọ́.” Nígbà tí ó bá ṣàì gbàgbọ́ (tán), ó máa wí pé: “Dájúdájú èmi yọwọ́ yọsẹ̀ kúrò nínú ọ̀rọ̀ rẹ. Dájúdájú èmi ń páyà Allāhu, Olúwa gbogbo ẹ̀dá.”');
INSERT INTO "aykyuni" VALUES(59,17,'59:17','Nítorí náà, ìkángun àwọn méjèèjì ni pé, dájúdájú àwọn méjèèjì yóò wà nínú Iná. Olùṣegbére ni àwọn méjèèjì nínú rẹ̀. Ìyẹn sì ni ẹ̀san àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(59,18,'59:18','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ bẹ̀rù Allāhu. Kí ẹ̀mí kan sì wòye sí ohun tí ó tì ṣíwájú fún ọ̀la (àlùkìyáámọ̀). Ẹ bẹ̀rù Allāhu. Dájúdájú Allāhu ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(59,19,'59:19','Kí ẹ sì má ṣe dà bí àwọn tó gbàgbé Allāhu. (Allāhu) sì mú wọn gbàgbé ẹ̀mí ara wọn. Àwọn wọ̀nyẹn gan-an ni arúfin.');
INSERT INTO "aykyuni" VALUES(59,20,'59:20','Àwọn èrò inú Iná àti àwọn èrò inú Ọgbà Ìdẹ̀ra kò dọ́gba. Àwọn èrò inú Ọgbà Ìdẹ̀ra, àwọn ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(59,21,'59:21','Tí ó bá jẹ́ pé A sọ al-Ƙur’ān yìí kalẹ̀ sórí àpáta ni, dájúdájú o máa rí i tí ó máa wálẹ̀, tí ó máa fọ́ pẹ́tẹpẹ̀tẹ fún ìpáyà Allāhu. Ìwọ̀nyí ni àwọn àkàwé tí À ń fi lélẹ̀ fún àwọn ènìyàn nítorí kí wọ́n lè ronú jinlẹ̀.');
INSERT INTO "aykyuni" VALUES(59,22,'59:22','Òun ni Allāhu, kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Onímọ̀ ìkọ̀kọ̀ àti gban̄gba. Òun ni Àjọkẹ́-ayé, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(59,23,'59:23','Òun ni Allāhu, kò sí ọlọ́hun kan tí a gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Ọba (ẹ̀dá), Ẹni-mímọ́ jùlọ, Aláìlábùkù, Olùjẹ́rìí-Òjíṣẹ́-Rẹ̀, Olùjẹ́rìí-ẹ̀dá, Alágbára, Olùjẹni-nípá, Atóbi. Mímọ́ ni fún Allāhu tayọ ohun tí wọ́n fi ń ṣẹbọ sí I.');
INSERT INTO "aykyuni" VALUES(59,24,'59:24','Òun ni Allāhu, Ẹlẹ́dàá, Olùpilẹ̀-ẹ̀dá, Olùyàwòrán-ẹ̀dá. TiRẹ̀ ni àwọn orúkọ tó dára jùlọ. Ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀ ń ṣàfọ̀mọ́ fún Un. Òun sì ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(60,1,'60:1','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe mú ọ̀tá Mi àti ọ̀tá yín ní ọ̀rẹ́ tí ẹ óò máa fi ìfẹ́ hàn sí. Wọ́n kúkú ti ṣàì gbàgbọ́ nínú ohun tí ó dé ba yín nínú òdodo. Wọ́n yọ Òjíṣẹ́ àti ẹ̀yin náà kúrò nínú ìlú nítorí pé, ẹ gbàgbọ́ nínú Allāhu, Olúwa yín. Tí ẹ̀yin bá jáde fún ogun nítorí ẹ̀sìn Mi àti nítorí wíwá ìyọ́nú Mi, (ṣé) ẹ̀yin yóò tún máa ní ìfẹ́ kọ̀rọ̀ sí wọn ni! Èmi sì nímọ̀ jùlọ nípa ohun tí ẹ fi pamọ́ àti ohun tí ẹ fi hàn. Ẹnikẹ́ni tí ó bá ṣe bẹ́ẹ̀ nínú yín, dájúdájú ó ti ṣìnà kúrò lójú ọ̀nà tààrà.');
INSERT INTO "aykyuni" VALUES(60,2,'60:2','Tí ọwọ́ wọn bá bà yín, wọ́n máa di ọ̀tá fún yín. Wọn yó sì fi ọwọ́ wọn àti ahọ́n wọn nawọ́ aburú si yín. Wọ́n sì máa fẹ́ kí ó jẹ́ pé ẹ di aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(60,3,'60:3','Àwọn ẹbí yín àti àwọn ọmọ yín kò lè ṣe yín ní àǹfààní; ní Ọjọ́ Àjíǹde ni Allāhu máa ṣèpínyà láààrin yín. Allāhu sì ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(60,4,'60:4','Dájúdájú àwòkọ́ṣe rere ti wà fún yín ní ara (Ànábì) ’Ibrọ̄hīm àti àwọn tó wà pẹ̀lú rẹ̀. Nígbà tí wọ́n sọ fún ìjọ wọn pé: "Dájúdájú àwa yọwọ́-yọsẹ̀ kúrò nínú ọ̀rọ̀ yín àti ohun tí ẹ̀ ń jọ́sìn fún lẹ́yìn Allāhu. A takò yín. Ọ̀tá àti ìkórira sì ti hàn láààrin wa títí láéláé àyàfi ìgbà tí ẹ bá tó gbàgbọ́ nínú Allāhu nìkan ṣoṣo. - Àfi ọ̀rọ̀ tí (Ànábì) ’Ibrọ̄hīm sọ fún bàbá rẹ̀ pé: “Dájúdájú mo máa tọrọ àforíjìn fún ọ, èmi kò sì ní ìkápá kiní kan fún ọ lọ́dọ̀ Allāhu. - Olúwa wa, Ìwọ l’a gbáralé. Ọ̀dọ̀ Rẹ l’a ṣẹ́rí padà sí (nípa ìronúpìwàdà). Ọ̀dọ̀ Rẹ sì ni àbọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(60,5,'60:5','Olúwa wa, má ṣe wá ní àdánwò fún àwọn tó ṣàì gbàgbọ́.Foríjìn wá, Olúwa wa. Dájúdájú Ìwọ ni Alágbára, Ọlọ́gbọ́n.” ');
INSERT INTO "aykyuni" VALUES(60,6,'60:6','Dájúdájú àwòkọ́ṣe rere ti wà fún yín ní ara wọn fún ẹni tó ń retí (ẹ̀sán) Allāhu àti Ọjọ́ Ìkẹ́yìn. Ẹnikẹ́ni tí ó bá gbúnrí, dájúdájú Allāhu, Òun ni Ọlọ́rọ̀, Ẹlẹ́yìn.');
INSERT INTO "aykyuni" VALUES(60,7,'60:7','Ó lè jẹ́ pé Allāhu yóò fi ìfẹ́ sí ààrin ẹ̀yin àti àwọn tí ẹ mú ní ọ̀tá nínú wọn (ìyẹn, nígbà tí wọ́n bá di mùsùlùmí). Allāhu ni Alágbára. Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(60,8,'60:8','Allāhu kò kọ̀ fún yín nípa àwọn tí kò gbé ogun tì yín nípa ẹ̀sìn, tí wọn kò sì lè yín jáde kúrò nínú ìlú yín, pé kí ẹ ṣe dáadáa sí wọn, kí ẹ sì ṣe déédé sí wọn. Dájúdájú Allāhu fẹ́ràn àwọn olùṣe-déédé.');
INSERT INTO "aykyuni" VALUES(60,9,'60:9','Ohun tí Allāhu kọ̀ fún yín nípa àwọn tó gbógun tì yín nínú ẹ̀sìn, tí wọ́n sì lè yín jáde kúrò nínú ìlú yín, tí wọ́n tún ṣe ìrànlọ́wọ́ (fún àwọn ọ̀tá yín) láti le yín jáde, ni pé (Ó kọ̀ fún yín) láti mú wọn ní ọ̀rẹ́. Ẹnikẹ́ni tí ó bá mú wọn ní ọ̀rẹ́, àwọn wọ̀nyẹn gan-an ni alábòsí.');
INSERT INTO "aykyuni" VALUES(60,10,'60:10','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nígbà tí àwọn onígbàgbọ́ òdodo lóbìnrin bá wá ba yín, tí wọ́n gbé ìlú àwọn aláìgbàgbọ́ jù sílẹ̀ fún ààbò ẹ̀sìn wọn, ẹ ṣe ìdánwò fún wọn. Allāhu nímọ̀ jùlọ nípa ìgbàgbọ́ wọn. Nítorí náà, tí ẹ bá mọ̀ wọ́n sí onígbàgbọ́ òdodo, ẹ má ṣe dá wọn padà sí ọ̀dọ̀ àwọn aláìgbàgbọ́. Àwọn onígbàgbọ́ òdodo lóbìnrin kò lẹ́tọ̀ọ́ sí wọn. Àwọn aláìgbàgbọ́ lọ́kùnrin kò sì lẹ́tọ̀ọ́ sí wọn.Ẹ fún (àwọn aláìgbàgbọ́ lọ́kùnrin) ní owó tí wọ́n ná (ní owó-orí obìnrin náà). Kò sì sí ẹ̀ṣẹ̀ fún ẹ̀yin náà pé kí ẹ fẹ́ wọn nígbà tí ẹ bá ti fún (àwọn obìnrin wọ̀nyí) ní owó-orí wọn. Ẹ má ṣe fi owó-orí àwọn obìnrin yín tí wọ́n jẹ́ aláìgbàgbọ́ mú wọn mọ́lẹ̀ (gẹ́gẹ́ bí ìyàwó yín láì jẹ́ pé wọ́n gba ’Islām pẹ̀lú yín).2 Ẹ bèèrè ohun tí ẹ ná (ní owó-orí wọn lọ́wọ́ aláìgbàgbọ́ lọ́kùnrin tí wọ́n sá lọ bá). Kí àwọn (aláìgbàgbọ́ lọ́kùnrin náà) bèèrè ohun tí àwọn náà ná (ní owó-orí wọn lọ́wọ́ ẹ̀yin tí onígbàgbọ́ òdodo lóbìnrin sá wá bá). Ìyẹn ni ìdájọ́ Allāhu. Ó sì ń dájọ́ láààrin yín. Allāhu sì ni Onímọ̀, Ọlọ́gbọ́n. ');
INSERT INTO "aykyuni" VALUES(60,11,'60:11','Tí kiní kan láti ọ̀dọ̀ àwọn ìyàwó yín bá bọ́ mọ yín lọ́wọ́ sí ọ̀dọ̀ àwọn aláìgbàgbọ́, tí ẹ sì kórè ogun, ẹ fún àwọn tí ìyàwó wọn sá lọ ní irú òdiwọ̀n ohun tí wọ́n ná (ní owó-orí). Kí ẹ sì bẹ̀rù Allāhu, Ẹni tí ẹ̀yin ní ìgbàgbọ́ òdodo nínú Rẹ̀.');
INSERT INTO "aykyuni" VALUES(60,12,'60:12','Ìwọ Ànábì, nígbà tí àwọn onígbàgbọ́ òdodo lóbìnrin bá wá bá ọ, tí wọ́n ń ṣe àdéhùn fún ọ pé àwọn kò níí fi kiní kan ṣẹbọ sí Allāhu, àwọn kò níí jalè, àwọn kò níí ṣe sìná, àwọn kò níí pa ọmọ wọn, àwọn kò níí mú àdápa irọ́ wá, àdápa irọ́ tí wọ́n ń mú wá láààrin ọwọ́ wọn àti ẹsẹ̀ wọn (ìyẹn ni gbígbé oyún olóyún fún ọkọ wọn), àwọn kò sì níí yapa rẹ níbi n̄ǹkan dáadáa, nígbà náà bá wọn ṣe àdéhùn, kí ó sì bá wọn tọrọ àforíjìn lọ́dọ̀ Allāhu. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(60,13,'60:13','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe mú ìjọ kan tí Allāhu ti bínú sí ní ọ̀rẹ́. Dájúdájú wọ́n ti sọ̀rètí nù nípa ọ̀run, gẹ́gẹ́ bí àwọn aláìgbàgbọ́ tó wà nínú sàréè (ṣe sọ̀rètí nù nípa ìkẹ́).');
INSERT INTO "aykyuni" VALUES(61,1,'61:1','Ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ ń ṣàfọ̀mọ́ fún Allāhu. Òun sì ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(61,2,'61:2','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nítorí kí ni ẹ ṣe ń sọ ohun tí ẹ ò níí ṣe níṣẹ́?');
INSERT INTO "aykyuni" VALUES(61,3,'61:3','Ó jẹ́ ohun ìbínú tó tóbi ní ọ̀dọ̀ Allāhu pé kí ẹ sọ ohun tí ẹ ò níí ṣe.');
INSERT INTO "aykyuni" VALUES(61,4,'61:4','Dájúdájú Allāhu fẹ́ràn àwọn tó ń jagun fún ẹ̀sìn Rẹ̀ (tí wọ́n tò ní) ọ̀wọ̀ọ̀wọ́ bí ẹni pé ilé tí wọ́n lẹ̀pọ̀ mọ́ra wọn ni wọ́n.');
INSERT INTO "aykyuni" VALUES(61,5,'61:5','(Rántí) nígbà tí (Ànábì) Mūsā sọ fún ìjọ rẹ̀ pé: “Ẹ̀yin ìjọ mi, nítorí kí ni ẹ óò ṣe fi ìnira kàn mí. Ẹ sì kúkú mọ̀ pé, dájúdájú Òjíṣẹ́ Allāhu ni èmi jẹ́ si yín.” Nígbà tí wọ́n sì yẹ̀ (kúrò níbi òdodo), Allāhu yẹ ọkàn wọn. Allāhu kò sì níí fi ọ̀nà mọ ìjọ òbìlẹ̀jẹ́.');
INSERT INTO "aykyuni" VALUES(61,6,'61:6','(Rántí) nígbà tí ‘Īsā ọmọ Mọryam sọ pé: “Ẹ̀yin ọmọ ’Isrọ̄’īl, dájúdájú èmi ni Òjíṣẹ́ Allāhu si yín. Mò ń fi ohun tó jẹ́ òdodo rinlẹ̀ nípa èyí tó ṣíwájú mi nínú Taorāh. Mo sì ń mú ìró-ìdùnnú wá nípa Òjíṣẹ́ kan tó ń bọ̀ lẹ́yìn mi. Orúkọ rẹ̀ ni ’Ahmọd.”Nígbà tí ó bá sì wá bá wọn pẹ̀lú àwọn ẹ̀rí tó yanjú, wọ́n á wí pé: “Idán pọ́nńbélé ni èyí.” ');
INSERT INTO "aykyuni" VALUES(61,7,'61:7','Ta sì ni ó ṣàbòsí tó tayọ ẹni tí ó dá àdápa irọ́ mọ́ Allāhu, tí wọ́n sì ń pè é sínú ’Islām! Allāhu kò sì níí fi ọ̀nà mọ ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(61,8,'61:8','Wọ́n ń gbèrò láti fi (ọ̀rọ̀) ẹnu wọn pa ìmọ́lẹ̀ Allāhu. Allāhu yó sì mú ìmọ́lẹ̀ Rẹ̀ tàn kárí, àwọn aláìgbàgbọ́ ìbáà kórira rẹ̀.');
INSERT INTO "aykyuni" VALUES(61,9,'61:9','(Allāhu) Òun ni Ẹni tí Ó fi ìmọ̀nà àti ẹ̀sìn òdodo (’Islām) rán Òjíṣẹ́ Rẹ̀ nítorí kí Ó lè fi borí gbogbo ẹ̀sìn (mìíràn) pátápátá, àwọn ọ̀ṣẹbọ ìbáà kórira rẹ̀.');
INSERT INTO "aykyuni" VALUES(61,10,'61:10','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ṣé kí N̄g tọ́ka yín sí òkòwò kan tí ó máa gbà yín là nínú ìyà ẹlẹ́ta-eléro?');
INSERT INTO "aykyuni" VALUES(61,11,'61:11','Ẹ gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀, ẹ jagun sójú ọ̀nà (ẹ̀sìn) Allāhu pẹ̀lú àwọn dúkìá yín àti ẹ̀mí yín. Ìyẹn lóore jùlọ fún yín tí ẹ bá mọ̀.');
INSERT INTO "aykyuni" VALUES(61,12,'61:12','(Allāhu) máa forí ẹ̀ṣẹ̀ yín jìn yín. Ó sì máa mu yín wọ inú àwọn Ọgbà Ìdẹ̀ra, tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀ àti àwọn ibùgbé tó dára nínú àwọn Ọgbà Ìdẹ̀ra gbére. Ìyẹn ni èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(61,13,'61:13','Àti n̄ǹkan mìíràn tí ẹ tún nífẹ̀ẹ́ sí; (ìyẹn) àrànṣe láti ọ̀dọ̀ Allāhu àti ìṣẹ́gun tó súnmọ́. Kí o sì fún àwọn onígbàgbọ́ òdodo ní ìró ìdùnnú.');
INSERT INTO "aykyuni" VALUES(61,14,'61:14','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ jẹ́ alárànṣe fún ẹ̀sìn Allāhu gẹ́gẹ́ bí (Ànábì) ‘Īsā ọmọ Mọryam ṣe sọ fún àwọn ọmọlẹ́yìn rẹ̀ pé: “Ta ni ó máa ṣe ìrànlọ́wọ́ fún mi nípa ẹ̀sìn Allāhu?” Àwọn ọmọlẹ́yìn rẹ̀ sọ pé: “Àwa ni alárànṣe fún ẹ̀sìn Allāhu.” Igun kan nínú àwọn ọmọ ’Isrọ̄’īl gbàgbọ́ ní òdodo nígbà náà, igun kan sì ṣàì gbàgbọ́ nínú rẹ̀. A sì fún àwọn tó gbàgbọ́ lágbára lórí àwọn ọ̀tá wọn; wọ́n sì di olùborí.1 ');
INSERT INTO "aykyuni" VALUES(62,1,'62:1','Ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ ń ṣe àfọ̀mọ́ fún Allāhu, Ọba ẹ̀dá, Ẹni-Mímọ́ jùlọ, Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(62,2,'62:2','Òun ni Ẹni tí Ó gbé Òjíṣẹ́ kan dìde láààrin àwọn aláìmọ̀ọ́nkọmọ̀ọ́nkà (àwọn aláìnítírà), tí ó ń ké àwọn āyah Rẹ̀ fún wọn. Ó ń fọ̀ wọ́n mọ́ (nínú ẹ̀ṣẹ̀). Ó sì ń kọ́ wọn ní Tírà àti ọgbọ́n ìjìnlẹ̀ (ìyẹn, sunnah), bí ó tilẹ̀ jẹ́ pé tẹ́lẹ̀tẹ́lẹ̀ wọ́n ti wà nínú ìṣìnà pọ́nńbélé.');
INSERT INTO "aykyuni" VALUES(62,3,'62:3','(Iṣẹ́ jíjẹ́ rẹ̀ tún wà fún) àwọn mìíràn tí wọ́n máa wà nínú àwọn (ọmọlẹ́yìn rẹ̀), àmọ́ tí wọn kò ì pàdé wọn. (Allāhu) Òun ni Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(62,4,'62:4','Ìyẹn ni oore àjùlọ Allāhu. Ó ń fún ẹni tí Ó bá fẹ́. Allāhu sì ni Olóore-àjùlọ ńlá.');
INSERT INTO "aykyuni" VALUES(62,5,'62:5','Àpèjúwe ìjọ tí A la at-Taorāh bọ̀ lọ́rùn, lẹ́yìn náà tí wọn kò lò ó, ó dà bí àpèjúwe kẹ́tẹ́kẹ́tẹ́ tí ó ru ẹrù àwọn tírà ńlá ńlá. Aburú ni àpèjúwe àwọn tó pe àwọn āyah Allāhu ní irọ́. Allāhu kò sì níí fi ọ̀nà mọ ìjọ alábòsí.');
INSERT INTO "aykyuni" VALUES(62,6,'62:6','Sọ pé: “Ẹ̀yin yẹhudi, tí ẹ bá lérò pé dájúdájú ẹ̀yin ni ọ̀rẹ́ Allāhu dípò àwọn ènìyàn (yòókù), ẹ tọrọ ikú nígbà náà tí ẹ bá jẹ́ olódodo.”');
INSERT INTO "aykyuni" VALUES(62,7,'62:7','Wọn kò níí tọrọ rẹ̀ láéláé nítorí ohun tí ọwọ́ wọn ti tì ṣíwájú. Allāhu sì ni Onímọ̀ nípa àwọn alábòsí.');
INSERT INTO "aykyuni" VALUES(62,8,'62:8','Sọ pé: “Dájúdájú ikú tí ẹ̀ ń sá fún, dájúdájú ó máa pàdé yín. Lẹ́yìn náà, wọn yóò da yín padà sí ọ̀dọ̀ Onímọ̀-ìkọ̀kọ̀ àti gban̄gba. Ó sì máa fún yín ní ìró ohun tí ẹ̀ ń ṣe níṣẹ́.”');
INSERT INTO "aykyuni" VALUES(62,9,'62:9','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, nígbà tí wọ́n bá pe ìrun ní ọjọ́ Jum‘ah, ẹ yára lọ síbi ìrántí Allāhu, kí ẹ sì pa kátà-kárà tì. Ìyẹn lóore jùlọ fún yín tí ẹ bá mọ̀.');
INSERT INTO "aykyuni" VALUES(62,10,'62:10','Nígbà tí wọ́n bá sì parí ìrun, ẹ túká sí orí ilẹ̀, kí ẹ sì máa wa nínú oore Allāhu. Ẹ rántí Allāhu ní ọ̀pọ̀lọ́pọ̀ nítorí kí ẹ lè jèrè.');
INSERT INTO "aykyuni" VALUES(62,11,'62:11','Nígbà tí wọ́n bá rí ọjà kan tàbí ìranù kan, wọn yóò dà lọ síbẹ̀. Wọn yó sì fi ọ́ sílẹ̀ lórí ìdúró. Sọ pé: “N̄ǹkan tí ó wà ní ọ̀dọ̀ Allāhu l’óore ju ìranù àti ọjà. Allāhu sì l’óore jùlọ nínú àwọn olùpèsè.');
INSERT INTO "aykyuni" VALUES(63,1,'63:1','Nígbà tí àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí bá wá sí ọ̀dọ̀ rẹ, wọ́n á wí pé: “À ń jẹ́rìí pé dájúdájú ìwọ, Òjíṣẹ́ Allāhu ni ọ́.” Allāhu sì mọ̀ pé dájúdájú ìwọ, Òjíṣẹ́ Rẹ̀ ni ọ́. Allāhu sì ń jẹ́rìí pé dájúdájú àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí, òpùrọ́ ni wọ́n.');
INSERT INTO "aykyuni" VALUES(63,2,'63:2','Wọ́n fi àwọn ìbúra wọn ṣe ààbò (fún ẹ̀mí ara wọn), wọ́n sì ṣẹ́rí àwọn ènìyàn kúrò lójú ọ̀nà (ẹ̀sìn) Allāhu; dájúdájú ohun tí wọ́n ń ṣe níṣẹ́ burú.');
INSERT INTO "aykyuni" VALUES(63,3,'63:3','(Wọ́n ṣe) ìyẹn nítorí pé, wọ́n gbàgbọ́, lẹ́yìn náà wọ́n ṣàì gbàgbọ́. Nítorí náà, A ti fi èdídí dí ọkàn wọn; wọn kò sì níí gbọ́ àgbọ́yé.');
INSERT INTO "aykyuni" VALUES(63,4,'63:4','Nígbà tí o bá rí wọn, ìrísí wọn yóò jọ ọ́ lójú. Tí wọ́n bá ń sọ̀rọ̀, ìwọ yóò tẹ́tí gbọ́ ọ̀rọ̀ wọn. Wọ́n sì dà bí igi tí wọ́n gbé ti ògiri.Wọ́n sì ń lérò pé gbogbo igbe (ìbòsí) ń bẹ lórí wọn (nípa ìṣọ̀bẹ-ṣèlu wọn).2 Ọ̀tá ni wọ́n. Nítorí náà, ṣọ́ra fún wọn. Allāhu ti ṣẹ́bi lé wọn. Báwo ni wọ́n ṣe ń ṣẹ́rí wọn kúrò níbi òdodo! ');
INSERT INTO "aykyuni" VALUES(63,5,'63:5','Àti pé nígbà tí wọ́n bá sọ fún wọn pé: “Ẹ wá kí Òjíṣẹ́ ba yín tọrọ àforíjìn.” Wọ́n á gbúnrí. O sì máa rí wọn tí wọn yóò máa gbúnrí lọ, tí wọn yóò máa ṣègbéraga.');
INSERT INTO "aykyuni" VALUES(63,6,'63:6','Bákan náà ni fún wọn; yálà o bá wọn tọrọ àforíjìn tàbí o ò bá wọn tọrọ àforíjìn.Allāhu kò níí foríjìn wọ́n. Dájúdájú Allāhu kò nií fi ọ̀nà mọ ìjọ òbìlẹ̀jẹ́. ');
INSERT INTO "aykyuni" VALUES(63,7,'63:7','Àwọn ni àwọn tó ń wí pé: “Ẹ̀yin kò gbọ́dọ̀ ná owó fún àwọn tó wà lọ́dọ̀ Ójíṣẹ́ Allāhu títí wọn yóò fi fọ́nká. Ti Allāhu sì ni àwọn ilé ọrọ̀ sánmọ̀ àti ilẹ̀, ṣùgbọ́n àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí kò gbọ́ àgbọ́yé.');
INSERT INTO "aykyuni" VALUES(63,8,'63:8','Wọ́n ń wí pé: “Dájúdájú tí a bá padà dé sínú ìlú Mọdīnah, ńṣe ni àwọn alágbára (ìlú) yóò yọ àwọn ẹni yẹpẹrẹ jáde kúrò nínú ìlú.” Agbára sì ń jẹ́ ti Allāhu, àti ti Òjíṣẹ́ Rẹ̀ àti àwọn onígbàgbọ́ òdodo, ṣùgbọ́n àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí kò mọ̀.');
INSERT INTO "aykyuni" VALUES(63,9,'63:9','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ má ṣe jẹ́ kí àwọn dúkìá yín àti àwọn ọmọ yín kó àìrójú ba yín níbi ìrántí Allāhu. Ẹnikẹ́ni tí ó bá kó sínú (àìrójú) yẹn, àwọn wọ̀nyẹn gan-an ni ẹni òfò.');
INSERT INTO "aykyuni" VALUES(63,10,'63:10','Ẹ ná nínú ohun tí A pèsè fún yín ṣíwájú kí ikú tó dé bá ẹnì kan yín, kí ó wá wí pé: “Olúwa mi, kí ó sì jẹ́ pé O lọ́ mi lára sí i títí di ìgbà kan tó súnmọ́, èmi ìbá sì lè máa tọrẹ, èmi ìbá sì wà lára àwọn ẹni rere.”');
INSERT INTO "aykyuni" VALUES(63,11,'63:11','Allāhu kò sì níí lọ́ ẹ̀mí kan lára nígbà tí ọjọ́ ikú rẹ̀ bá dé. Allāhu sì ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(64,1,'64:1','Ohunkóhun tó wà nínú àwọn sánmọ̀ àti ohunkóhun tó wà nínú ilẹ̀ ń ṣe àfọ̀mọ́ fún Allāhu. TiRẹ̀ ni ìjọba. TiRẹ̀ sì ni ẹyìn. Òun sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(64,2,'64:2','Òun ni Ẹni tí Ó ṣẹ̀dá yín. Aláìgbàgbọ́ wà nínú yín. Onígbàgbọ́ òdodo sì wà nínú yín. Allāhu sì ni Olùríran nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(64,3,'64:3','Ó ṣẹ̀dá àwọn sánmọ̀ àti ilẹ̀ pẹ̀lú òdodo. Ó ya àwòrán yín. Ó sì ṣe àwọn àwòrán yín dáradára. Ọ̀dọ̀ Rẹ̀ sì ni àbọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(64,4,'64:4','Ó mọ ohunkóhun tó wà nínú àwọn sánmọ̀ àti ilẹ̀. Ó sì mọ ohun tí ẹ̀ ń fi pamọ́ àti ohun tí ẹ̀ ń ṣàfi hàn rẹ̀. Allāhu sì ni Onímọ̀ nípa ohun tó wà nínú igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(64,5,'64:5','Ṣé ìró àwọn tó ṣàì gbàgbọ́ ní ìṣáájú kò tí ì dé ba yín ni? Nítorí náà, wọ́n tọ́ ìyà ọ̀ràn wọn wò. Ìyà ẹlẹ́ta-eléro sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(64,6,'64:6','Ìyẹn nítorí pé, dájúdájú àwọn Òjíṣẹ́ wọn ń wá bá wọn pẹ̀lú àwọn ẹ̀rí tó yanjú. Wọ́n sì wí pé: “Ṣé abara l’ó máa fi ọ̀nà mọ̀ wá?” Nítorí náà, wọ́n ṣàì gbàgbọ́. Wọ́n sì pẹ̀yìndà (sí òdodo). Allāhu sì rọrọ̀ láì sí àwọn. Àti pé Allāhu ni Ọlọ́rọ̀, Ẹlẹ́yìn.');
INSERT INTO "aykyuni" VALUES(64,7,'64:7','Àwọn tó ṣàì gbàgbọ́ lérò pé A ò níí gbé wọn dìde. Sọ pé: “Bẹ́ẹ̀ ni, mo fi Olúwa mi búra, dájúdájú Wọn yóò gbe yín dìde. Lẹ́yìn náà, Wọn yóò fún yín ní ìró ohun tí ẹ ṣe níṣẹ́. Ìyẹn sì jẹ́ ìrọ̀rùn fún Allāhu.”');
INSERT INTO "aykyuni" VALUES(64,8,'64:8','Nítorí náà, ẹ gbàgbọ́ nínú Allāhu àti Òjíṣẹ́ Rẹ̀ àti ìmọ́lẹ̀ tí A sọ̀kalẹ̀. Allāhu sì ni Alámọ̀tán nípa ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(64,9,'64:9','Ní ọjọ́ tí (Allāhu) yóò ko yín jọ fún ọjọ́ àkójọ. Ìyẹn ni ọjọ́ èrè àti àdánù.Ẹnikẹ́ni tí ó bá gba Allāhu gbọ́ ní òdodo, tí ó sì ṣe iṣẹ́ rere, (Allāhu) yóò pa àwọn àṣìṣe rẹ̀ rẹ́ fún un. Ó sì máa mú un wọ inú Ọgbà Ìdẹ̀ra, tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀ títí láéláé. Ìyẹn sì ni èrèǹjẹ ńlá. ');
INSERT INTO "aykyuni" VALUES(64,10,'64:10','Àwọn tó sì ṣàì gbàgbọ́, tí wọ́n tún pe àwọn āyah Wa ní irọ́, àwọn wọ̀nyẹn ni èrò inú Iná. Olùṣegbére ni wọ́n nínú rẹ̀. Ìkángun náà sì burú.');
INSERT INTO "aykyuni" VALUES(64,11,'64:11','Àdánwò kan kò lè ṣẹlẹ̀ àyàfi pẹ̀lú ìyọ̀ǹda Allāhu. Ẹnikẹ́ni tí ó bá gbàgbọ́ nínú Allāhu ní òdodo, Allāhu máa fi ọkàn rẹ̀ mọ̀nà.Allāhu sì ni Onímọ̀ nípa gbogbo n̄ǹkan. ');
INSERT INTO "aykyuni" VALUES(64,12,'64:12','Kí ẹ tẹ̀lé ti Allāhu. Kí ẹ sì tẹ̀lé ti Òjíṣẹ́. Tí ẹ bá gbúnrí, ìkede ẹ̀sìn tó yanjú ni ojúṣe Òjíṣẹ́ Wa.');
INSERT INTO "aykyuni" VALUES(64,13,'64:13','Allāhu, kò sí ọlọ́hun kan tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àyàfi Òun. Allāhu sì ni kí àwọn onígbàgbọ́ òdodo gbáralé.');
INSERT INTO "aykyuni" VALUES(64,14,'64:14','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, dájúdájú ọ̀tá wà fún yín nínú àwọn ìyàwó yín àti àwọn ọmọ yín. Nítorí náà, ẹ ṣọ́ra fún wọn. Tí ẹ bá ṣàmójúkúrò, tí ẹ ṣàfojúfò, tí ẹ sì ṣàforíjìn (fún wọn), dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(64,15,'64:15','Ìfòòró ni àwọn dúkìá yín àti àwọn ọmọ yín. Allāhu sì ni ẹ̀san ńlá wà ní ọ̀dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(64,16,'64:16','Nítorí náà, ẹ bẹ̀rù Allāhu bí ẹ bá ṣe lágbára mọ. Ẹ gbọ́ ọ̀rọ̀ (Allāhu), ẹ tẹ̀lé e, kí ẹ sì náwó (fún ẹ̀sìn Rẹ̀) lóore jùlọ fún ẹ̀mí yín. Ẹnikẹ́ni tí A bá ṣọ́ níbi ahun àti ọ̀kánjúà inú ẹ̀mí rẹ̀, àwọn wọ̀nyẹn gan-an ni olùjèrè.');
INSERT INTO "aykyuni" VALUES(64,17,'64:17','Tí ẹ bá yá Allāhu ní dúkìá tó dára, Ó máa ṣàdìpèlé rẹ̀ fún yín. Ó sì máa foríjìn yín. Allāhu sì ni Ọlọ́pẹ́ Aláfaradà, ');
INSERT INTO "aykyuni" VALUES(64,18,'64:18','Onímọ̀-ìkọ̀kọ̀ àti gban̄gba, Alágbára, Ọlọ́gbọ́n.');
INSERT INTO "aykyuni" VALUES(65,1,'65:1','Ìwọ Ànábì, nígbà tí ẹ bá fẹ́ kọ àwọn obìnrin sílẹ̀, ẹ kọ̀ wọ́n sílẹ̀ pẹ̀lú òǹkà ọjọ́ opó ìkọ̀sílẹ̀ tí wọ́n máa ṣe.Kí ẹ sì ṣọ́ òǹkà ọjọ́ opó ìkọ̀sílẹ̀ náà. Kí ẹ bẹ̀rù Allāhu, Olúwa yín. Ẹ má ṣe mú wọn jáde kúrò nínú ilé wọn, àwọn náà kò sì gbọdọ̀ jáde àfi tí wọ́n bá lọ ṣe ìbàjẹ́ tó fojú hàn.2 Ìwọ̀nyẹn sì ni àwọn ẹnu-ààlà òfin tí Allāhu gbékalẹ̀. Ẹnikẹ́ni tí ó bá tayọ àwọn ẹnu-ààlà òfin tí Allāhu gbékalẹ̀, o kúkú ti ṣàbòsí sí ẹ̀mí ara rẹ̀. Ìwọ kò sì mọ̀ bóyá Allāhu máa mú ọ̀rọ̀ titun kan ṣẹlẹ̀ lẹ́yìn ìyẹn. ');
INSERT INTO "aykyuni" VALUES(65,2,'65:2','Nígbà tí wọ́n bá fẹ́ parí òǹkà ọjọ́ opó wọn, ẹ mú wọn mọ́ra ní ọ̀nà tó dára tàbí kí ẹ kọ̀ wọ́n sílẹ̀ ní ọ̀nà tó dára.Ẹ fi àwọn onídéédé méjì nínú yín jẹ́rìí sí i.2 Kí ẹ sì gbé ìjẹ́rìí náà dúró ní tìtorí ti Allāhu. Ìyẹn ni À ń fi ṣe wáàsí fún ẹnikẹ́ni tí ó bá ní ìgbàgbọ́ òdodo nínú Allāhu àti Ọjọ́ Ìkẹ́yìn. Ẹnikẹ́ni tí ó bá ń bẹ̀rù Allāhu, Ó máa fún un ní ọ̀nà àbáyọ (nínú ìṣòro). ');
INSERT INTO "aykyuni" VALUES(65,3,'65:3','Ó sì máa pèsè fún un ní àyè tí kò ti rokàn. Àti pé ẹnikẹ́ni tí ó bá gbẹ́kẹ̀lé Allāhu, Ó máa tó o. Dájúdájú Allāhu yóò mú àṣẹ Rẹ̀ ṣẹ. Dájúdájú Allāhu ti kọ òdíwọ̀n àkókò fún gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(65,4,'65:4','Àwọn obìnrin tó ti sọ̀rètí nù nípa n̄ǹkan oṣù ṣíṣe nínú àwọn obìnrin yín, tí ẹ bá ṣeyèméjì, òǹkà ọjọ́ ìkọ̀sílẹ̀ wọn ni oṣù mẹ́ta. (Bẹ́ẹ̀ náà ni fún) àwọn tí kò tí ì máa ṣe n̄ǹkan oṣù. Àwọn olóyún, ìparí òǹkà ọjọ́ opó ìkọ̀sílẹ̀ tiwọn ni pé kí wọ́n bí oyún inú wọn. Ẹnikẹ́ni tí ó bá ń bẹ̀rù Allāhu, Ó máa ṣe ọ̀rọ̀ rẹ̀ ní ìrọ̀rùn fún un.');
INSERT INTO "aykyuni" VALUES(65,5,'65:5','Ìyẹn ni àṣẹ Allāhu, tí Ó sọ̀kalẹ̀ fún yín. Ẹnikẹ́ni tí ó bá ń bẹ̀rù Allāhu, Ó máa pa àwọn àṣìṣe rẹ̀ rẹ́. Ó sì máa jẹ́ kí ẹ̀san rẹ̀ tóbi.');
INSERT INTO "aykyuni" VALUES(65,6,'65:6','Ẹ fún wọn ní ibùgbé nínú ibùgbé yín bí àyè ṣe gbà yín mọ. Ẹ má ṣe ni wọ́n lára láti kó ìfúnpinpin bá wọn. Tí wọ́n bá jẹ́ olóyún, ẹ náwó lé wọn lórí títí wọn yóò fi bí oyún inú wọn. Tí wọ́n bá ń fún ọmọ yín ní ọyàn mu, ẹ fún wọn ní owó-ọ̀yà wọn. Ẹ dámọ̀ràn láààrin ara yín ní ọ̀nà tó dára. Tí ọ̀rọ̀ kò bá sì rọgbọ láààrin ara yín, kí ẹlòmíìràn bá ọkọ (fún ọmọ) ní ọyàn mu.');
INSERT INTO "aykyuni" VALUES(65,7,'65:7','Kí ọlọ́rọ̀ ná nínú ọrọ̀ rẹ̀. Ẹni tí A sì díwọ̀n arísìkí rẹ̀ fún (níwọ̀nba), kí ó ná nínú ohun tí Allāhu fún un. Allāhu kò làbọ ẹ̀mí kan lọ́rùn àyàfi n̄ǹkan tí Ó fún un. Allāhu yó sì mú ìrọ̀rùn wá lẹ́yìn ìnira.');
INSERT INTO "aykyuni" VALUES(65,8,'65:8','Mélòó mélòó nínú àwọn ìlú tí wọ́n yapa àṣẹ Olúwa Rẹ̀ àti àwọn Òjíṣẹ́ Rẹ̀. A sì máa ṣírò iṣẹ́ wọn ní ìṣírò líle. Àti pé A máa jẹ wọ́n níyà tó burú gan-an.');
INSERT INTO "aykyuni" VALUES(65,9,'65:9','Nítorí náà, wọn máa tọ́ ìyà ọ̀ràn wọn wò. Ìkángun ọ̀rọ̀ wọn sì jẹ́ òfò.');
INSERT INTO "aykyuni" VALUES(65,10,'65:10','Allāhu ti pèsè ìyà líle sílẹ̀ dè wọ́n. Nítorí náà, ẹ bẹ̀rù Allāhu, ẹ̀yin onílàákàyè tí ẹ gbàgbọ́ ní òdodo. Allāhu kúkú ti sọ tírà ìrántí kalẹ̀ fún yín.');
INSERT INTO "aykyuni" VALUES(65,11,'65:11','Òjíṣẹ́ kan tí ó máa ké àwọn āyah Allāhu tó yanjú fún yín nítorí kí ó lè mú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n tún ṣe àwọn iṣẹ́ rere, kúrò nínú àwọn òkùnkùn lọ sínú ìmọ́lẹ̀. Ẹnikẹ́ni tí ó bá gbàgbọ́ nínú Allāhu ní òdodo, tí ó sì ń ṣe iṣẹ́ rere, Ó máa mú un wọ inú àwọn Ọgbà Ìdẹ̀ra, tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀ títí láéláé. Dájúdájú Allāhu ti ṣe arísìkí rẹ̀ (ìjẹ-ìmu àti ìgbádùn rẹ̀) ní dáadáa fún un.');
INSERT INTO "aykyuni" VALUES(65,12,'65:12','Allāhu ni Ẹni tí Ó ṣẹ̀dá àwọn sánmọ̀ méje àti ilẹ̀ ní (òǹkà) irú rẹ̀. Àṣẹ ń sọ̀kalẹ̀ láààrin wọn nítorí kí ẹ lè mọ̀ pé dájúdájú Allāhu ni Alágbára lórí gbogbo n̄ǹkan. Àti pé dájúdájú Allāhu fi ìmọ̀ rọ̀gbà yí gbogbo n̄ǹkan ká.1 ');
INSERT INTO "aykyuni" VALUES(66,1,'66:1','Ìwọ Ànábì, nítorí kí ni o ṣe máa sọ n̄ǹkan tí Allāhu ṣe ní ẹ̀tọ́ fún ọ di èèwọ̀? Ìwọ ń wá ìyọ́nú àwọn ìyàwó rẹ. Allāhu sì ni Aláforíjìn, Àṣàkẹ́-ọ̀run.');
INSERT INTO "aykyuni" VALUES(66,2,'66:2','Dájúdájú Allāhu ti ṣe àlàyé òfin ìtánràn ìbúra yín fún yín. Allāhu ni Olùrànlọ́wọ́ yín. Òun sì ni Onímọ̀, Ọlọ́gbọ́n.1 ');
INSERT INTO "aykyuni" VALUES(66,3,'66:3','Rántí nígbà tí Ànábì bá ọ̀kan nínú àwọn ìyàwó rẹ̀ s’ọ̀rọ̀ àṣírí. Nígbà tí (ẹni tó bá sọ̀rọ̀, ìyẹn Hafsọh) sì sọ̀rọ̀ náà fún (‘Ā’iṣah), Allāhu sì fi han Ànábì (pé ẹlòmíìràn ti gbọ́ sí i).Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a - sì sọ apá kan rẹ̀ (fún Hafsọh pé: “O ti fi ọ̀rọ̀ Mọ̄riyah tó ‘Ā’iṣah létí.”). Ó sì fi apá kan sílẹ̀ (ìyẹn, ọ̀rọ̀ nípa ipò àrólé). Àmọ́ nígbà tí (Ànábì) fi ìró náà tó o létí, Hafsọh) sọ pé: “Ta ni ó fún ọ ní ìró èyí?” (Ànábì) sọ pé: “Onímọ̀, Alámọ̀tán l’Ó fún mi ní ìró náà.” ');
INSERT INTO "aykyuni" VALUES(66,4,'66:4','Tí ẹ̀yin méjèèjì bá ronú pìwàdà sí ọ̀dọ̀ Allāhu, (Ó máa gba ìronúpìwàdà yín). Ṣebí ọkàn yín kúkú ti tẹ̀ (síbi kí Ànábì má fẹ̀ẹ́ Mọ̄riyah). Tí ẹ bá sì ran ara yín lọ́wọ́ (láti kó ìbànújẹ́) bá a, dájúdájú Allāhu ni Olùrànlọ́wọ́ rẹ̀, àti (mọlāika) Jibrīl àti ẹni rere nínú àwọn onígbàgbọ́ òdodo. Lẹ́yìn ìyẹn, àwọn mọlāika tí wọ́n jẹ́ olùrànlọ́wọ́(tún wà fún un).');
INSERT INTO "aykyuni" VALUES(66,5,'66:5','Ó ṣeé ṣe, tí ó bá kọ̀ yín sílẹ̀, kí Olúwa rẹ̀ fi àwọn obìnrin kan, tí wọn lóore jù yín lọ rọ́pò fún un, (tí wọ́n máa jẹ́) mùsùlùmí, onígbàgbọ́ òdodo, olùtẹ̀lé-àṣẹ, olùronúpìwàdà, olùjọ́sìn, olùgbààwẹ̀ (tí wọ́n máa jẹ́) adélébọ̀ àti wúńdíá.');
INSERT INTO "aykyuni" VALUES(66,6,'66:6','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ ṣọ́ ẹ̀mí ara yín àti àwọn ará ilé yín níbi Iná. Ènìyàn àti òkúta ni n̄ǹkan ìkoná rẹ̀. Àwọn mọlāika tó rorò, tí wọ́n le ni ẹ̀ṣọ́ rẹ̀. Wọn kò níí yapa àṣẹ Allāhu níbi ohun tí Ó bá pa láṣẹ fún wọn. Wọ́n sì ń ṣe ohun tí wọ́n bá pa láṣẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(66,7,'66:7','Ẹ̀yin tí ẹ ṣàì gbàgbọ́, ẹ má ṣe ṣàròyé ní ọjọ́ òní. Ohun tí ẹ̀ ń ṣe níṣẹ́ ni A óò fi san yín ní ẹ̀san.');
INSERT INTO "aykyuni" VALUES(66,8,'66:8','Ẹ̀yin tí ẹ gbàgbọ́ ní òdodo, ẹ ronú pìwàdà sí ọ̀dọ̀ Allāhu ní ìronúpìwàdà òdodo. Ó ṣeé ṣe pé Olúwa yín máa pa àwọn àṣìṣe yín rẹ́ fún yín. Ó sì máa mu yín wọ inú àwọn Ọgbà Ìdẹ̀ra tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Ní ọjọ́ tí Allāhu kò níí dójú ti Ànábì àti àwọn tó gbàgbọ́ ní òdodo pẹ̀lú rẹ̀. Ìmọ́lẹ̀ wọn yóò máa tàn ní iwájú wọn àti ní ọwọ́ ọ̀tún wọn. Wọn yóò sọ pé: “Olúwa wa, pé ìmọ́lẹ̀ wa fún wa, kí O sì foríjìn wá. Dájúdájú Ìwọ ni Alágbára lórí gbogbo n̄ǹkan.”');
INSERT INTO "aykyuni" VALUES(66,9,'66:9','Ìwọ Ànábì, gbógun ti àwọn aláìgbàgbọ́ àti àwọn ṣọ̀bẹ-ṣèlu mùsùlùmí. Kí o sì le mọ́ wọn. Ibùgbé wọn sì ni iná Jahanamọ. Ìkángun náà sì burú.');
INSERT INTO "aykyuni" VALUES(66,10,'66:10','Allāhu fi ìyàwó (Ànábì) Nūh àti ìyàwó (Ànábì) Lūt ṣe àpẹ̀ẹrẹ fún àwọn tó ṣàì gbàgbọ́. Àwọn méjèèjì wà lábẹ́ ẹrúsìn méjì rere nínú àwọn ẹrúsìn Wa. Àwọn (obìnrin) méjèèjì sì jàǹbá àwọn ọkọ wọn. Àwọn ọkọ wọn kò sì fi kiní kan rọ̀ wọ́n lọ́rọ̀ lọ́dọ̀ Allāhu. A ó sì sọ fún àwọn obìnrin méjèèjì pé: “Ẹ wọ inú Iná pẹ̀lú àwọn olùwọlé (sínú Iná).”');
INSERT INTO "aykyuni" VALUES(66,11,'66:11','Allāhu tún fi ìyàwó Fir‘aon ṣe àpẹ̀ẹrẹ fún àwọn tó gbàgbọ́ ní òdodo. Nígbà tí ó sọ pé: “Olúwa mi, kọ́ ilé kan fún mi lọ́dọ̀ Rẹ̀ nínú Ọgbà Ìdẹ̀ra. Là mí lọ́wọ́ Fir‘aon àti iṣẹ́ ọwọ́ rẹ̀. Kí O sì là mí lọ́wọ́ ìjọ alábòsí.”');
INSERT INTO "aykyuni" VALUES(66,12,'66:12','Àti Mọryam ọmọbìnrin ‘Imrọ̄n, èyí tí ó ṣọ́ abẹ́ rẹ̀. A sì fẹ́ atẹ́gùn sí i lára nínú atẹ́gùn ẹ̀mí tí A dá. Ó gbàgbọ́ lódodo nínú àwọn ọ̀rọ̀ Olúwa rẹ̀ àti àwọn Tírà Rẹ̀. Ó sì wà nínú àwọn olùtẹ̀lé-àṣẹ Allāhu.');
INSERT INTO "aykyuni" VALUES(67,1,'67:1','Ìbùkún ni fún Ẹni tí gbogbo ìjọba wà ní Ọwọ́ Rẹ̀. Òun sì ni Alágbára lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(67,2,'67:2','Ẹni tí Ó dá ikú àti ìṣẹ̀mí nítorí kí ó lè dan yín wò; èwo nínú yín l’ó máa ṣe iṣẹ́ rere jùlọ. Òun sì ni Alágbára, Aláforíjìn.');
INSERT INTO "aykyuni" VALUES(67,3,'67:3','Ẹni tí Ó dá àwọn sánmọ̀ méje ní ìpele ìpele. O ò lè rí àìgúnrégé kan lára ẹ̀dá Àjọkẹ́-ayé. Nítorí náà, wò ó padà, ǹjẹ́ o rí ojú sísán kan (lára sánmọ̀)?');
INSERT INTO "aykyuni" VALUES(67,4,'67:4','Lẹ́yìn náà, wò ó padà ní ẹ̀ẹ̀ mejì, kí ojú rẹ padà sí ọ̀dọ̀ rẹ pẹ̀lú ìyẹpẹrẹ. Ó sì máa káàárẹ̀.');
INSERT INTO "aykyuni" VALUES(67,5,'67:5','Dájúdájú A ti fi (àwọn ìràwọ̀ tó ń tànmọ́lẹ̀ bí) àtùpà ṣe sánmọ̀ ilé ayé ní ọ̀ṣọ́. A tún ṣe wọ́n ni ẹ̀ta-ìràwọ̀ tí wọ́n ń jù mọ́ àwọn èṣù. A sì pèsè ìyà Iná tó ń jó sílẹ̀ dè wọ́n.');
INSERT INTO "aykyuni" VALUES(67,6,'67:6','Ìyà iná Jahanamọ sì wà fún àwọn tó ṣàì gbàgbọ́. Ìkángun náà sì burú.');
INSERT INTO "aykyuni" VALUES(67,7,'67:7','Nígbà tí wọ́n bá jù wọ́n sínú rẹ̀, wọn yóò máa gbọ́ kíkùn rẹ̀, tí ó sì máa ru sókè.');
INSERT INTO "aykyuni" VALUES(67,8,'67:8','Iná máa fẹ́ẹ̀ pínra rẹ̀ láti ara ìbínú. Ìgbàkígbà tí wọ́n bá ju ìjọ kan sínú rẹ̀, àwọn ẹ̀ṣọ́ Iná yóò máa bi wọ́n léèrè pé: “Ǹjẹ́ olùkìlọ̀ kan kò wá ba yín bí?”');
INSERT INTO "aykyuni" VALUES(67,9,'67:9','Wọn yóò wí pé: “Bẹ́ẹ̀ ni, dájúdájú olùkìlọ̀ kan ti wá bá wa, ṣùgbọ́n a pè é ní òpùrọ́. A sì wí pé Allāhu kò sọ n̄ǹkan kan kalẹ̀. Kí ni ẹ̀yin bí kò ṣe pé ẹ wà nínú ìṣìnà tó tóbi.”');
INSERT INTO "aykyuni" VALUES(67,10,'67:10','Wọ́n tún wí pé: “Tí ó bá jẹ́ pé a gbọ́ràn ni tàbí pé a ṣe làákàyè ni, àwa kò níí wà nínú èrò inú Iná tó ń jó.”');
INSERT INTO "aykyuni" VALUES(67,11,'67:11','Wọ́n sì jẹ́wọ́ ẹ̀ṣẹ̀ wọn. Nítorí náà, ìjìnnà sí ìkẹ́ Allāhu yó wà fún àwọn èrò inú Iná tó ń jó.');
INSERT INTO "aykyuni" VALUES(67,12,'67:12','Dájúdájú àwọn tó ń páyà Olúwa wọn ní ìkọ̀kọ̀, àforíjìn àti ẹ̀san tó tóbi ń bẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(67,13,'67:13','Ẹ wí ọ̀rọ̀ yín ní jẹ́ẹ́jẹ́ tàbí ẹ wí i sókè, dájúdájú Òun ni Onímọ̀ nípa ohun tó wà nínú àwọn igbá-àyà ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(67,14,'67:14','Ṣé kò mọ Ẹni tí Ó ṣẹ̀dá ni?Òun sì ni Aláàánú, Alámọ̀tán. ');
INSERT INTO "aykyuni" VALUES(67,15,'67:15','Òun ni Ẹni tí Ó rọ ilẹ̀ fún yín. Nítorí náà, ẹ rìn ní àwọn agbègbè rẹ̀ káàkiri, kí ẹ sì jẹ nínú arísìkí Rẹ̀. Ọ̀dọ̀ Rẹ̀ sì ni àjíǹde ẹ̀dá wà.');
INSERT INTO "aykyuni" VALUES(67,16,'67:16','Ṣé ẹ fọkànbalẹ̀ sí Ẹni tí Ó wà ní (òkè) sánmọ̀ pé kò lè jẹ́ kí ilẹ̀ gbe yín mì? Nígbà náà, ilẹ̀ yó sì máa mì tìtì.');
INSERT INTO "aykyuni" VALUES(67,17,'67:17','Tàbí ẹ̀ fọkànbalẹ̀ sí Ẹni tí Ó wà ní (òkè) sánmọ̀ pé kò lè fi òkúta iná ránṣẹ́ si yín ni? Nígbà náà, ẹ sì máa mọ bí ìkìlọ̀ Mi ti rí.1 ');
INSERT INTO "aykyuni" VALUES(67,18,'67:18','Àwọn tó ṣíwájú wọn kúkú pe òdodo ní irọ́. Báwo sì ni bí Mo ṣe (fi ìyà) kọ (aburú fún wọn) ti rí!');
INSERT INTO "aykyuni" VALUES(67,19,'67:19','Ṣé wọn kò rí àwọn ẹyẹ tí ó wà ní òkè wọn, tí (wọ́n) ń na ìyẹ́ apá (wọn), tí wọ́n sì ń pa á mọ́ra? Kiní kan kò mú wọn dúró (sínú òfurufú) àfi Àjọkẹ́-ayé. Dájúdájú Òun sì ni Olùríran nípa gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(67,20,'67:20','Ta ni ẹni tí ó máa jẹ́ ọmọ ogun fún yín, tí ó máa ràn yín lọ́wọ́ lẹ́yìn Àjọkẹ́-ayé? (Nínú) kí ni àwọn aláìgbàgbọ́ wà bí kò ṣe nínú ẹ̀tàn.');
INSERT INTO "aykyuni" VALUES(67,21,'67:21','Ta ni ẹni tí ó máa pèsè fún yín tí Ó bá dá arísìkí Rẹ̀ dúró? Ńṣe ni wọ́n ń ṣorí kunkun sí i nínú ìgbéraga àti sísá fún òdodo.');
INSERT INTO "aykyuni" VALUES(67,22,'67:22','Ǹjẹ́ ẹni tó ń rìn ní ìdojúbolẹ̀ l’ó mọ̀nà jùlọ ni tàbí ẹni tó ń rìn sàn án lójú ọ̀nà tààrà?');
INSERT INTO "aykyuni" VALUES(67,23,'67:23','Sọ pé: “Òun ni Ẹni tí Ó ṣẹ̀dá yín. Ó sì ṣe ìgbọ́rọ̀, ìríran àti àwọn ọkàn fún yín. Ọpẹ́ tí ẹ̀ ń dá sì kéré púpọ̀.”');
INSERT INTO "aykyuni" VALUES(67,24,'67:24','Sọ pé: “Òun ni Ẹni tí Ó da yín sí orí ilẹ̀. Ọ̀dọ̀ Rẹ̀ sì ni wọn yóò ko yín jọ sí.”');
INSERT INTO "aykyuni" VALUES(67,25,'67:25','Wọ́n ń wí pé: “Ìgbà wo ni àdéhùn yìí yóò ṣẹ tí ẹ bá jẹ́ olódodo?”');
INSERT INTO "aykyuni" VALUES(67,26,'67:26','Sọ pé: “Ọ̀dọ̀ Allāhu nìkan ni ìmọ̀ (nípa) rẹ̀ wà. Èmi kàn jẹ́ olùkìlọ̀ pọ́nńbélé ni.”');
INSERT INTO "aykyuni" VALUES(67,27,'67:27','Nígbà tí wọ́n bá rí i tó súnmọ́, ojú àwọn tó ṣàì gbàgbọ́ yóò korò wá fún ìbànújẹ́. A ó sì sọ fún wọn pé: “Èyí ni ohun tí ẹ̀ ń pè.”');
INSERT INTO "aykyuni" VALUES(67,28,'67:28','Sọ pé: “Ẹ sọ fún mi, tí Allāhu bá pa èmi àti àwọn tó wà pẹ̀lú mi rẹ́, tàbí tí Ó bá kẹ́ wa (ṣé ẹ lè dí I lọ́wọ́ ni?) Nítorí náà, ta ni ó máa gba àwọn aláìgbàgbọ́ là nínú ìyà ẹlẹ́ta-eléro?');
INSERT INTO "aykyuni" VALUES(67,29,'67:29','Sọ pé: “Òun ni Àjọkẹ́-ayé. Àwa gbà Á gbọ́. Òun sì la gbáralé. Láìpẹ́ ẹ máa mọ ta ni ó wà nínú ìṣìnà pọ́nńbélé.”');
INSERT INTO "aykyuni" VALUES(67,30,'67:30','Sọ pé: “Ẹ sọ fún mi, tí omi yín bá gbẹ wá, ta ni ẹni tí ó máa mú omi tó ń ṣàn wá fún yín?”1 ');
INSERT INTO "aykyuni" VALUES(68,1,'68:1','Nūn. (Allāhu ló mọ ohun tí Ó gbàlérò pẹ̀lú háràfí náà.)Allāhu fi gègé ìkọ̀wé àti ohun tí àwọn mọlāika ń kọ sílẹ̀ búra. ');
INSERT INTO "aykyuni" VALUES(68,2,'68:2','Ìwọ kì í ṣe wèrè nípa ìdẹ̀ra Olúwa rẹ.');
INSERT INTO "aykyuni" VALUES(68,3,'68:3','Àti pé dájúdájú ẹ̀san tí kò níí dáwọ́ dúró ti wà fún ọ.');
INSERT INTO "aykyuni" VALUES(68,4,'68:4','Dájúdájú ìwọ sì wà lórí ìwà ńlá (ìwà alápọ̀n-ọ́nlé).');
INSERT INTO "aykyuni" VALUES(68,5,'68:5','Láìpẹ́ ìwọ yóò rí i, àwọn náà yó sì rí i;');
INSERT INTO "aykyuni" VALUES(68,6,'68:6','èwo nínú yín ni aládàn-ánwò (wèrè)?');
INSERT INTO "aykyuni" VALUES(68,7,'68:7','Dájúdájú Olúwa rẹ, Ó nímọ̀ jùlọ nípa ẹni tó ṣìnà kúrò lójú ọ̀nà Rẹ̀. Ó sì nímọ̀ jùlọ nípa àwọn olùmọ̀nà.');
INSERT INTO "aykyuni" VALUES(68,8,'68:8','Nítorí náà, má ṣe tẹ̀lé àwọn olùpe-òdodo-nírọ́.');
INSERT INTO "aykyuni" VALUES(68,9,'68:9','Àti pé wọ́n fẹ́ kí o dẹwọ́, kí àwọn náà sì dẹwọ́.');
INSERT INTO "aykyuni" VALUES(68,10,'68:10','Má ṣe tẹ̀lé gbogbo ẹni tí ìbúra rẹ̀ pọ̀, ẹni yẹpẹrẹ,');
INSERT INTO "aykyuni" VALUES(68,11,'68:11','abúni, alárìnká tó ń sòfófó kiri,');
INSERT INTO "aykyuni" VALUES(68,12,'68:12','olùdènà iṣẹ́ rere, alákọyọ, ẹlẹ́ṣẹ̀,');
INSERT INTO "aykyuni" VALUES(68,13,'68:13','ọ̀dájú. Lẹ́yìn ìyẹn, ògbólógbòó ẹni ibi (àsáwọ̀ ọmọ zina) ni. ');
INSERT INTO "aykyuni" VALUES(68,14,'68:14','Nítorí pé ó ní dúkìá àti àwọn ọmọkùnrin,');
INSERT INTO "aykyuni" VALUES(68,15,'68:15','nígbà tí wọ́n bá ń ké àwọn āyah Wa fún un, ó máa wí pé: “Àkọsílẹ̀ àlọ́ àwọn ẹni àkọ́kọ́ (nìwọ̀nyí)”.');
INSERT INTO "aykyuni" VALUES(68,16,'68:16','A máa fi àmì sí i ní góńgórí imú.');
INSERT INTO "aykyuni" VALUES(68,17,'68:17','Dájúdájú Àwa dán wọn wò gẹ́gẹ́ bí A ṣe dán àwọn ọlọ́gbà wò nígbà tí wọ́n búra pé, dájúdájú àwọn yóò ká gbogbo èso ọgbà náà ní òwúrọ̀ kùtùkùtù.');
INSERT INTO "aykyuni" VALUES(68,18,'68:18','Wọn kò sì ṣe àyàfi pé, “tí Allāhu bá fẹ́”.');
INSERT INTO "aykyuni" VALUES(68,19,'68:19','Àdánwò tó ń yí n̄ǹkan po láti ọ̀dọ̀ Olúwa rẹ sì yí ọgbà náà po, nígbà tí wọ́n sùn lọ.');
INSERT INTO "aykyuni" VALUES(68,20,'68:20','Ó sì dà bí oko àgédànù (tí wọ́n ti résun).');
INSERT INTO "aykyuni" VALUES(68,21,'68:21','Wọ́n ń pera wọn ní òwúrọ̀ kùtùkùtù');
INSERT INTO "aykyuni" VALUES(68,22,'68:22','pé ẹ jí lọ sí oko yín tí ẹ bá fẹ́ ká èso.');
INSERT INTO "aykyuni" VALUES(68,23,'68:23','Wọ́n lọ, wọ́n sì ń fi ohùn jẹ́ẹ́jẹ́ bára wọn sọ̀rọ̀');
INSERT INTO "aykyuni" VALUES(68,24,'68:24','pé ní òní mẹ̀kúnnù kan kò gbọdọ̀ wọnú rẹ̀ wá ba yín.');
INSERT INTO "aykyuni" VALUES(68,25,'68:25','Wọ́n jí lọ (sí oko wọn) lórí èrò-ọkàn kan (pé) àwọn lágbára.');
INSERT INTO "aykyuni" VALUES(68,26,'68:26','Nígbà tí wọ́n rí i, wọ́n wí pé: “Dájúdájú àwa ti ṣìnà.');
INSERT INTO "aykyuni" VALUES(68,27,'68:27','Rárá o, wọ́n ṣe ìkórè oko léèwọ̀ fún wa ni.”');
INSERT INTO "aykyuni" VALUES(68,28,'68:28','Ẹni tó dúró déédé jùlọ nínú wọn sọ pé: “Ṣé èmi kò sọ fún yín pé ẹ̀yin kò ṣe ṣàfọ̀mọ́ fún Allāhu (pé “a máa kórè oko wa, ’in ṣā ’Allāhu.)”?');
INSERT INTO "aykyuni" VALUES(68,29,'68:29','Wọ́n wí pé: “Mímọ́ ni fún Olúwa wa, dájúdájú àwa jẹ́ alábòsí.”');
INSERT INTO "aykyuni" VALUES(68,30,'68:30','Apá kan wọn kọjú sí apá kan; wọ́n sì ń dára wọn lẹ́bi.');
INSERT INTO "aykyuni" VALUES(68,31,'68:31','Wọ́n wí pé: “Ègbé wa ò! Dájúdájú àwa jẹ́ olùtayọ-ẹnu ààlà.');
INSERT INTO "aykyuni" VALUES(68,32,'68:32','Ó ṣeé ṣe kí Olúwa wa fi èyí tó dára ju èyí lọ pààrọ̀ rẹ̀ fún wa. Dájúdájú ọ̀dọ̀ Olúwa wa ni àwa ń rokàn oore sí.”');
INSERT INTO "aykyuni" VALUES(68,33,'68:33','Báyẹn ni ìyà náà ṣe rí (fún wọn). Ìyà ọ̀run sì tóbi jùlọ, tí ó bá jẹ́ pé wọ́n mọ̀.');
INSERT INTO "aykyuni" VALUES(68,34,'68:34','Dájúdájú àwọn Ọgbà Ìdẹ̀ra wà fún àwọn olùbẹ̀rù Allāhu lọ́dọ̀ Olúwa wọn.');
INSERT INTO "aykyuni" VALUES(68,35,'68:35','Ṣé A máa ṣe àwọn mùsùlùmí bí (A ṣe máa ṣe) àwọn ẹlẹ́ṣẹ̀ bí?');
INSERT INTO "aykyuni" VALUES(68,36,'68:36','Kí l’ó ń ṣe yín tí ẹ fi ń dájọ́ báyìí ná?1 ');
INSERT INTO "aykyuni" VALUES(68,37,'68:37','Tàbí tírà kan wà fún yín tí ẹ̀ ń kẹ́kọ̀ọ́ nínú rẹ̀');
INSERT INTO "aykyuni" VALUES(68,38,'68:38','pé dájúdájú ẹ̀yin lẹ ni ohun tí ẹ bá ń ṣà lẹ́ṣà nínú ìdájọ́?');
INSERT INTO "aykyuni" VALUES(68,39,'68:39','Ǹjẹ́ ẹ ni àwọn àdéhùn kan lọ́dọ̀ wa tí ó máa wà títí di Ọjọ́ Àjíǹde pé: “Dájúdájú tiyín ni ìdájọ́ tí ẹ bá ti mú wá.”?');
INSERT INTO "aykyuni" VALUES(68,40,'68:40','Bi wọ́n léèrè wò pé èwo nínú wọn l’ó lè fọwọ́ ìyẹn sọ̀yà!');
INSERT INTO "aykyuni" VALUES(68,41,'68:41','Tàbí wọ́n ní (àwọn òrìṣà kan) tí wọ́n jẹ́ akẹgbẹ́ Wa? Kí wọ́n mú àwọn òrìṣà wọn wá nígbà náà tí wọ́n bá jẹ́ olódodo.');
INSERT INTO "aykyuni" VALUES(68,42,'68:42','Ní ọjọ́ tí A máa ṣí ojúgun sílẹ̀, A sì máa pè wọ́n síbi ìforíkanlẹ̀, wọn kò sì níí lè ṣe é.');
INSERT INTO "aykyuni" VALUES(68,43,'68:43','Ojú wọn yóò wálẹ̀. Ìyẹpẹrẹ máa bò wọ́n mọ́lẹ̀. Wọ́n kúkú ti pè wọ́n síbi ìforíkanlẹ̀ nígbà tí wọ́n ní àlàáfíà (wọn kò sì kírun).');
INSERT INTO "aykyuni" VALUES(68,44,'68:44','Nítorí náà, fi Èmi àti ẹni tó ń pe ọ̀rọ̀ yìí ní irọ́ sílẹ̀. A óò máa dẹ wọ́n lẹ́kẹ láti jẹ wọ́n níyà ní àyè tí wọn kò mọ̀.');
INSERT INTO "aykyuni" VALUES(68,45,'68:45','Mò ń lọ́ra fún wọn ni. Dájúdájú ète Mi lágbára.1 ');
INSERT INTO "aykyuni" VALUES(68,46,'68:46','Tàbí ò ń bèèrè owó-ọ̀yà kan lọ́wọ́ wọn, ni gbèsè fi wọ̀ wọ́n lọ́rùn?');
INSERT INTO "aykyuni" VALUES(68,47,'68:47','Tàbí lọ́dọ̀ wọn ni (wàláà) ìkọ̀kọ̀ wà ni wọ́n bá ń kọ ọ́ (síta)?');
INSERT INTO "aykyuni" VALUES(68,48,'68:48','Nítorí náà, ṣe sùúrù fún ìdájọ́ Olúwa rẹ. Kí ìwọ má sì ṣe dà bí ẹlẹ́ja (ìyẹn, Ànábì Yūnus) nígbà tí ó pe (Allāhu) pẹ̀lú ìbànújẹ́.');
INSERT INTO "aykyuni" VALUES(68,49,'68:49','Tí kò bá jẹ́ pé ìkẹ́ kan láti ọ̀dọ̀ Olúwa rẹ̀ lé e bá, wọn ìbá jù ú sórí ilẹ̀ gban̄sasa (láti inú ẹja) ní ẹni-èébú.');
INSERT INTO "aykyuni" VALUES(68,50,'68:50','Nítorí náà, Olúwa rẹ̀ ṣà á lẹ́ṣà. Ó sì ṣe é ní ara àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(68,51,'68:51','Àwọn tó ṣàì gbàgbọ́ fẹ́ẹ̀ fi ojú wọn gbé ọ ṣubú nígbà tí wọ́n gbọ́ ìrántí náà. Wọ́n sì ń wí pé: “Dájúdájú wèrè mà ni.”');
INSERT INTO "aykyuni" VALUES(68,52,'68:52','Kí sì ni al-Ƙur’ān bí kò ṣe ìrántí fún gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(69,1,'69:1','Ìṣẹ̀lẹ̀ Òdodo náà!');
INSERT INTO "aykyuni" VALUES(69,2,'69:2','Kí ni Ìṣẹ̀lẹ̀ Òdodo náà?');
INSERT INTO "aykyuni" VALUES(69,3,'69:3','Kí sì ni ó mú ọ mọ ohun tó ń jẹ́ Ìṣẹ̀lẹ̀ Òdodo náà?');
INSERT INTO "aykyuni" VALUES(69,4,'69:4','Àwọn ìjọ Thamūd àti ìjọ ‘Ād pe Àjíǹde (ajámọláya bí àrá) ní irọ́.1 ');
INSERT INTO "aykyuni" VALUES(69,5,'69:5','Ní ti ìjọ Thamūd, wọ́n fi igbe tó tayọ ẹnu-ààlà pa wọ́n rẹ́.');
INSERT INTO "aykyuni" VALUES(69,6,'69:6','Ní ti ìjọ ‘Ād, wọ́n fi atẹ́gùn líle tó tayọ ẹnu-ààlà pa wọ́n rẹ́.');
INSERT INTO "aykyuni" VALUES(69,7,'69:7','Ó dẹ ẹ́ sí wọn fún òru méje àti ọ̀sán mẹ́jọ láì dáwọ́ dúró. O sì máa rí ìjọ náà tí wọ́n ti kú sínú rẹ̀ bí ẹni pé kùkùté igi dàbínù tó ti luhò nínú ni wọ́n.1 ');
INSERT INTO "aykyuni" VALUES(69,8,'69:8','Ǹjẹ́ o rí ẹnì kan nínú wọn tó ṣẹ́ kù bí?');
INSERT INTO "aykyuni" VALUES(69,9,'69:9','Fir‘aon àti àwọn tó ṣíwájú rẹ̀ àti àwọn ìlú tí wọ́n dojú rẹ̀ bolẹ̀ dé pẹ̀lú àwọn àṣìṣe.');
INSERT INTO "aykyuni" VALUES(69,10,'69:10','Wọ́n yapa Òjíṣẹ́ Olúwa wọn. Ó sì gbá wọn mú ní ìgbámú tóléken̄kà.');
INSERT INTO "aykyuni" VALUES(69,11,'69:11','Dájúdájú nígbà tí omi tayọ ẹnu-ààlà. A gbe yín gun ọkọ̀ ojú-omi.');
INSERT INTO "aykyuni" VALUES(69,12,'69:12','Nítorí kí Á lè fi ṣe ìrántí fún yín àti nítorí kí etí tó ń ṣọ́ n̄ǹkan lè ṣọ́ ọ.');
INSERT INTO "aykyuni" VALUES(69,13,'69:13','Nígbà tí wọ́n bá fọn fèrè oníwo fún ikú ní fífọn ẹyọ kan,');
INSERT INTO "aykyuni" VALUES(69,14,'69:14','tí wọ́n gbé ilẹ̀ àti àpáta sókè, tí wọ́n sì rún méjèèjì wómúwómú ní rírún ẹ̀ẹ̀ kan,');
INSERT INTO "aykyuni" VALUES(69,15,'69:15','ní ọjọ́ yẹn ni Ìṣẹ̀lẹ̀ máa ṣẹlẹ̀.');
INSERT INTO "aykyuni" VALUES(69,16,'69:16','Àti pé sánmọ̀ yó fàya pẹ́rẹpẹ̀rẹ. Ó sì máa fúyẹ́ gẹgẹ ní ọjọ́ yẹn.');
INSERT INTO "aykyuni" VALUES(69,17,'69:17','Àwọn mọlāika sì máa wà ní àwọn ẹ̀gbẹ́ẹ̀gbẹ́ rẹ̀. Àti pé ní ọjọ́ yẹn, lókè wọn, àwọn (mọlāika) mẹ́jọ ni wọ́n máa ru Ìtẹ́ Olúwa rẹ.');
INSERT INTO "aykyuni" VALUES(69,18,'69:18','Ní ọjọ́ yẹn, wọn yóò máa ṣẹ́rí yín wá (fún ìṣírò-iṣẹ́). Kò sì níí sí àṣírí yín kan tó máa pamọ́ (fún Wa).');
INSERT INTO "aykyuni" VALUES(69,19,'69:19','Nítorí náà, ní ti ẹni tí wọ́n bá fún ní tírà rẹ̀ ní ọwọ́ ọ̀tún rẹ̀, ó máa sọ pé: “Ẹ gbà, ẹ ka tírà mi wò.');
INSERT INTO "aykyuni" VALUES(69,20,'69:20','Dájúdájú èmi ti mọ àmọ̀dájú pé dájúdájú èmi yóò bá ìṣírò-iṣẹ́ mi pàdé.”');
INSERT INTO "aykyuni" VALUES(69,21,'69:21','Nítorí náà, ó máa wà nínú ìṣẹ̀mí tó máa yọ́nú sí');
INSERT INTO "aykyuni" VALUES(69,22,'69:22','nínú Ọgbà Ìdẹ̀ra gíga.');
INSERT INTO "aykyuni" VALUES(69,23,'69:23','Àwọn èso rẹ̀ (sì) máa wà ní àrọ́wọ́tó.');
INSERT INTO "aykyuni" VALUES(69,24,'69:24','Ẹ jẹ, kí ẹ sì mu pẹ̀lú ìgbàdún nítorí ohun tí ẹ tì síwájú nínú àwọn ọjọ́ tí ó ti ré kọjá.');
INSERT INTO "aykyuni" VALUES(69,25,'69:25','Ní ti ẹni tí wọ́n bá sì fún ní tírà rẹ̀ ní ọwọ́ òsì rẹ̀, ó máa wí pé: “Háà! Kí wọ́n sì má fún mi ni tírà mi!1 ');
INSERT INTO "aykyuni" VALUES(69,26,'69:26','Àti pé kí èmi sì má mọ ohun tí ìṣírò-iṣẹ́ mi jẹ́.');
INSERT INTO "aykyuni" VALUES(69,27,'69:27','Háà! Kí ikú sì jẹ́ òpin (ọ̀rọ̀ ẹ̀dá).');
INSERT INTO "aykyuni" VALUES(69,28,'69:28','Dúkìá mi kò sì rọ̀ mí lọ́rọ̀.');
INSERT INTO "aykyuni" VALUES(69,29,'69:29','Agbára mi sì ti parun.”');
INSERT INTO "aykyuni" VALUES(69,30,'69:30','Ẹ mú un, kí ẹ dè é ní ọwọ́ mọ́ ọrùn.');
INSERT INTO "aykyuni" VALUES(69,31,'69:31','Lẹ́yìn náà, inú iná Jẹhīm ni kí ẹ mú un wọ̀.');
INSERT INTO "aykyuni" VALUES(69,32,'69:32','Lẹ́yìn náà, inú ẹ̀wọ̀n tí gígùn rẹ̀ jẹ́ àádọ́rin gígùn thar‘uni kí ẹ kì í sí. ');
INSERT INTO "aykyuni" VALUES(69,33,'69:33','Dájúdájú òun kì í gbàgbọ́ nínú Allāhu, Atóbi.');
INSERT INTO "aykyuni" VALUES(69,34,'69:34','Kì í sì gba ènìyàn níyànjú láti bọ́ àwọn mẹ̀kúnnù.');
INSERT INTO "aykyuni" VALUES(69,35,'69:35','Nítorí náà, kò níí sí ọ̀rẹ́ ìmùlẹ̀ kan fún un níbí yìí ní òní.');
INSERT INTO "aykyuni" VALUES(69,36,'69:36','Kò sì níí sí oúnjẹ kan (fún un) bí kò ṣe (oúnjẹ) àwọnúwẹ̀jẹ̀.');
INSERT INTO "aykyuni" VALUES(69,37,'69:37','Kò sí ẹni tí ó máa jẹ ẹ́ bí kò ṣe àwọn ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(69,38,'69:38','Nítorí náà, Èmi ń fi ohun tí ẹ̀ ń fojú rí búra');
INSERT INTO "aykyuni" VALUES(69,39,'69:39','àti ohun tí ẹ ò fojú rí.');
INSERT INTO "aykyuni" VALUES(69,40,'69:40','Dájúdájú al-Ƙur’ān ni ọ̀rọ̀ (tí A fi rán) Òjíṣẹ́, alápọ̀n-ọ́nlé.1 ');
INSERT INTO "aykyuni" VALUES(69,41,'69:41','Kì í ṣe ọ̀rọ̀ eléwì. Ohun tí ẹ gbàgbọ́ kéré púpọ̀.');
INSERT INTO "aykyuni" VALUES(69,42,'69:42','Kì í sì ṣe ọ̀rọ̀ adábigba. Díẹ̀ lẹ̀ ń lò nínú ìrántí.');
INSERT INTO "aykyuni" VALUES(69,43,'69:43','Wọ́n sọ̀ ọ́ kalẹ̀ láti ọ̀dọ̀ Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(69,44,'69:44','Tí ó bá jẹ́ pé (Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a - ) dá àdápa apá kan ọ̀rọ̀ náà mọ́ Wa ni,');
INSERT INTO "aykyuni" VALUES(69,45,'69:45','Àwa ìbá gbá a mú lọ́wọ́ ọ̀tún.');
INSERT INTO "aykyuni" VALUES(69,46,'69:46','Lẹ́yìn náà, Àwa ìbá já isan ọrùn rẹ̀.');
INSERT INTO "aykyuni" VALUES(69,47,'69:47','Kò sì sí ẹnì kan nínú yín tí ó máa gbà á sílẹ̀ lọ́wọ́ (Wa).');
INSERT INTO "aykyuni" VALUES(69,48,'69:48','Dájúdájú (al-Ƙur’ān) ni ìrántí fún àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(69,49,'69:49','Dájúdájú Àwa sì mọ̀ pé àwọn tó ń pe al-Ƙur’ān ní irọ́ wà nínú yín.');
INSERT INTO "aykyuni" VALUES(69,50,'69:50','Dájúdájú al-Ƙur’ān máa jẹ́ àbámọ̀ ńlá fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(69,51,'69:51','Dájúdájú al-Ƙur’ān ni òdodo tó dájú.');
INSERT INTO "aykyuni" VALUES(69,52,'69:52','Nítorí náà, ṣàfọ̀mọ́ fún orúkọ Olúwa Rẹ, Atóbi.');
INSERT INTO "aykyuni" VALUES(70,1,'70:1','Oníbèéèrè kan bèèrè nípa ìyà tó máa ṣẹlẹ̀');
INSERT INTO "aykyuni" VALUES(70,2,'70:2','sí àwọn aláìgbàgbọ́. Kò sì sí ẹni tí ó máa dí i lọ́wọ́');
INSERT INTO "aykyuni" VALUES(70,3,'70:3','lọ́dọ̀ Allāhu, Onípò-àyè gíga.');
INSERT INTO "aykyuni" VALUES(70,4,'70:4','Àwọn mọlāika àti mọlāika Jibrīl ń gùnkè wá sí ọ̀dọ̀ Rẹ̀ nínú ọjọ́ kan tí òdiwọ̀n rẹ̀ jẹ́ ọ̀kẹ́ méjì ààbọ̀ ọdún (tí ẹ bá fẹ́ rìn ín).1 ');
INSERT INTO "aykyuni" VALUES(70,5,'70:5','Nítorí náà, ṣe sùúrù ní sùúrù tó rẹwà.');
INSERT INTO "aykyuni" VALUES(70,6,'70:6','Dájúdájú wọ́n ń wo (ìyà náà) ní ohun tó jìnnà.');
INSERT INTO "aykyuni" VALUES(70,7,'70:7','A sì ń wò ó ní ohun tó súnmọ́.');
INSERT INTO "aykyuni" VALUES(70,8,'70:8','(Ọjọ́ náà ni) ọjọ́ tí sánmọ̀ yó dà bí ògéré epo gbígbóná,');
INSERT INTO "aykyuni" VALUES(70,9,'70:9','àwọn àpáta yó sì dà bí ẹ̀gbọ̀n òwú-irun,');
INSERT INTO "aykyuni" VALUES(70,10,'70:10','ọ̀rẹ́ ìmùlẹ̀ kan kò sì níí bèèrè ọ̀rẹ́ ìmùlẹ̀ (rẹ̀).');
INSERT INTO "aykyuni" VALUES(70,11,'70:11','Wọ́n máa rí ara wọn (wọ́n sì máa dára wọn mọ̀). Ẹlẹ́ṣẹ̀ sì máa fẹ́ kí òun fi àwọn ọmọ rẹ̀ ṣèràpadà ẹ̀mí ara rẹ̀ níbi Ìyà ní ọjọ́ yẹn.');
INSERT INTO "aykyuni" VALUES(70,12,'70:12','Ìyàwó rẹ̀ àti arákùnrin rẹ̀,');
INSERT INTO "aykyuni" VALUES(70,13,'70:13','àti àwọn ẹbí rẹ̀ tó pa tì, tí àwọn sì ń gbà á sọ́dọ̀ nílé ayé,');
INSERT INTO "aykyuni" VALUES(70,14,'70:14','àti gbogbo ẹni tó wà lórí ilẹ̀ ayé pátápátá, (ó máa fẹ́ fi wọ́n ṣèràpadà ẹ̀mí ara rẹ̀ níbi Ìyà). Lẹ́yìn náà, (ó máa fẹ́) kí Allāhu gba òun là (nínú ìyà Iná).');
INSERT INTO "aykyuni" VALUES(70,15,'70:15','Rárá! Dájúdájú (ó máa wọ) iná Laṭḥọ̄ (iná tó ń jò fòfò).');
INSERT INTO "aykyuni" VALUES(70,16,'70:16','Ó máa bó awọ orí tòró.');
INSERT INTO "aykyuni" VALUES(70,17,'70:17','(Iná) yó máa ké sí ẹni tó kẹ̀yìn sí (ìgbágbọ́ òdodo), tí ó sì takété sí i,');
INSERT INTO "aykyuni" VALUES(70,18,'70:18','ó kó (ọrọ̀) jọ, ó sì fi pamọ́ (kò ná an fún ẹ̀sìn).');
INSERT INTO "aykyuni" VALUES(70,19,'70:19','Dájúdájú ènìyàn, A ṣẹ̀dá rẹ̀ ní aláìlọ́kàn, ọ̀kánjúà.');
INSERT INTO "aykyuni" VALUES(70,20,'70:20','Nígbà tí aburú bá fọwọ́ bà á, ó máa kanra gógó.');
INSERT INTO "aykyuni" VALUES(70,21,'70:21','Nígbà tí ọwọ́ rẹ̀ bá sì ba oore, ó máa yahun.');
INSERT INTO "aykyuni" VALUES(70,22,'70:22','Àyàfi àwọn olùkírun,');
INSERT INTO "aykyuni" VALUES(70,23,'70:23','àwọn tí wọ́n dúnnímọ́ ìrun wọn;');
INSERT INTO "aykyuni" VALUES(70,24,'70:24','àwọn tó mọ ojúṣe (wọn) nínú dúkìá wọn');
INSERT INTO "aykyuni" VALUES(70,25,'70:25','sí alágbe àti ẹni tí A ṣe arísìkí ní èèwọ̀ fún;');
INSERT INTO "aykyuni" VALUES(70,26,'70:26','àwọn tó ń gba ọjọ́ ẹ̀san gbọ́ ní òdodo;');
INSERT INTO "aykyuni" VALUES(70,27,'70:27','àwọn tó ń páyà ìyà Olúwa wọn,');
INSERT INTO "aykyuni" VALUES(70,28,'70:28','dájúdájú ìyà Olúwa wọn kò ṣeé fàyàbalẹ̀ sí;');
INSERT INTO "aykyuni" VALUES(70,29,'70:29','àwọn tó ń ṣọ́ abẹ́ wọn,');
INSERT INTO "aykyuni" VALUES(70,30,'70:30','àyàfi lọ́dọ̀ àwọn ìyàwó wọn tàbí ẹrúbìnrin wọn ni wọn kò ti lè jẹ́ ẹni-èébú,');
INSERT INTO "aykyuni" VALUES(70,31,'70:31','nítorí náà, ẹnikẹ́ni tó bá ń wá n̄ǹkan mìíràn lẹ́yìn ìyẹn, àwọn wọ̀nyẹn gan-an ni olùtayọ-ẹnu ààlà;');
INSERT INTO "aykyuni" VALUES(70,32,'70:32','àwọn tó ń ṣọ́ àgbàfipamọ́ wọn àti àdéhùn wọn;');
INSERT INTO "aykyuni" VALUES(70,33,'70:33','àwọn tó ń dúró ti ìjẹ́rìí (òdodo) wọn;');
INSERT INTO "aykyuni" VALUES(70,34,'70:34','àti àwọn tó ń ṣọ́ àwọn ìrun wọn.');
INSERT INTO "aykyuni" VALUES(70,35,'70:35','Àwọn wọ̀nyẹn ni alápọ̀n-ọ́nlé nínú àwọn Ọgbà Ìdẹ̀ra.');
INSERT INTO "aykyuni" VALUES(70,36,'70:36','Kí ló mú àwọn tí kò gbàgbọ́ tí ara wọn kò balẹ̀ níwájú rẹ,');
INSERT INTO "aykyuni" VALUES(70,37,'70:37','tí wọ́n sì jókòó ní ìsọ̀rí ìsọ̀rí sí ọwọ́ ọ̀tún àti ọwọ́ òsì (rẹ)?');
INSERT INTO "aykyuni" VALUES(70,38,'70:38','Ṣé ẹnì kọ̀ọ̀kan nínú wọn ń jẹ̀rankàn pé A óò mú òun wọ inú Ọgbà Ìdẹ̀ra ni?');
INSERT INTO "aykyuni" VALUES(70,39,'70:39','Rárá o! Dájúdájú Àwa ṣẹ̀dá wọn láti inú ohun tí wọ́n mọ̀.');
INSERT INTO "aykyuni" VALUES(70,40,'70:40','Nítorí náà, Èmi ń fi Olúwa àwọn ibùyọ òòrùn àti àwọn ibùwọ̀ òòrùnbúra, dájúdájú Àwa ni Alágbára ');
INSERT INTO "aykyuni" VALUES(70,41,'70:41','láti lè fi àwọn tó dára jù wọ́n lọ pààrọ̀ wọn. Kò sì sí ẹni tó lè kó agara bá Wa.');
INSERT INTO "aykyuni" VALUES(70,42,'70:42','Nítorí náà, fi wọ́n sílẹ̀ kí wọ́n máa sọ ìsọkúsọ (wọn), kí wọ́n sì máa ṣeré títí wọn yóò fi pàdé ọjọ́ wọn tí À ń ṣe ní àdéhùn fún wọn.');
INSERT INTO "aykyuni" VALUES(70,43,'70:43','Ọjọ́ tí wọn yóò sáré jáde wéréwéré láti inú àwọn sàréè, wọn yó sì dà bí ẹni pé wọ́n ń yára lọ sídìí ohun àfojúsùn kan.');
INSERT INTO "aykyuni" VALUES(70,44,'70:44','Ojú wọn yóò wálẹ̀. Ìyẹpẹrẹ máa bò wọ́n mọ́lẹ̀. Ọjọ́ yẹn ni èyí tí À ń ṣe ní àdéhùn fún wọn.');
INSERT INTO "aykyuni" VALUES(71,1,'71:1','Dájúdájú Àwa rán (Ànábì) Nūh níṣẹ́ sí ìjọ rẹ̀ pé: “Ṣèkìlọ̀ fún ìjọ rẹ ṣíwájú kí ìyà ẹlẹ́ta-eléro tó dé bá wọn.”');
INSERT INTO "aykyuni" VALUES(71,2,'71:2','Ó sọ pé: “Ẹ̀yin ìjọ mi, dájúdájú èmi ni olùkìlọ̀ pọ́nńbélé fún yín');
INSERT INTO "aykyuni" VALUES(71,3,'71:3','pé kí ẹ jọ́sìn fún Allāhu, kí ẹ bẹ̀rù Rẹ̀, kí ẹ sì tẹ̀lé mi.');
INSERT INTO "aykyuni" VALUES(71,4,'71:4','(Allāhu) máa ṣàforíjìn àwọn ẹ̀ṣẹ̀ yín fún yín. Ó sì máa lọ yín lára títí di gbèdéke àkókò kan. Dájúdájú àkókò (ikú tí) Allāhu (kọ mọ́ ẹ̀dá), nígbà tí ó bá dé, wọn kò níí sún un ṣíwájú tí ó bá jẹ́ pé ẹ mọ̀.”');
INSERT INTO "aykyuni" VALUES(71,5,'71:5','Ó sọ pé: “Olúwa mi, dájúdájú èmi pe ìjọ mi ní òru àti ní ọ̀sán.');
INSERT INTO "aykyuni" VALUES(71,6,'71:6','Ìpè mi kò sì ṣe àlékún kan fún wọn àyàfi sísá sẹ́yìn (fún òdodo).');
INSERT INTO "aykyuni" VALUES(71,7,'71:7','Àti pé ìgbàkígbà tí mo bá pè wọ́n pé kí O lè foríjìn wọ́n, wọ́n ń fi ọmọníka wọn dí etí wọn, wọ́n ń yíṣọ wọn borí, wọ́n ń takú (sórí ẹ̀ṣẹ̀), wọ́n sì ń ṣègbéraga gan-an.');
INSERT INTO "aykyuni" VALUES(71,8,'71:8','Lẹ́yìn náà, dájúdájú èmi pè wọ́n pẹ̀lú ohùn òkè.');
INSERT INTO "aykyuni" VALUES(71,9,'71:9','Lẹ́yìn náà, dájúdájú èmi kéde fún wọn, mo sì tún pè wọ́n ní ìdá kọ́ńkọ́.');
INSERT INTO "aykyuni" VALUES(71,10,'71:10','Mo sì sọ pé, ẹ tọrọ àforíjìn ní ọ̀dọ̀ Olúwa yín, dájúdájú Ó ń jẹ́ Aláforíjìn.');
INSERT INTO "aykyuni" VALUES(71,11,'71:11','Ó máa rọ̀jò fún yín ní òjò púpọ̀,');
INSERT INTO "aykyuni" VALUES(71,12,'71:12','Ó máa fi àwọn dúkìá àti àwọn ọmọkùnrin ràn yín lọ́wọ́. Ó máa ṣe àwọn ọgbà oko fún yín. Ó sì máa ṣe àwọn odò fún yín.');
INSERT INTO "aykyuni" VALUES(71,13,'71:13','Kí ló mu yín tí ẹ kò páyà títóbi Allāhu?');
INSERT INTO "aykyuni" VALUES(71,14,'71:14','Ó sì kúkú ṣẹ̀dá yín láti ìrísí kan sí òmíràn (nínú oyún).1 ');
INSERT INTO "aykyuni" VALUES(71,15,'71:15','Ṣé ẹ kò wòye sí bí Allāhu ṣe dá àwọn sánmọ̀ méje ní ìpele ìpele ni?');
INSERT INTO "aykyuni" VALUES(71,16,'71:16','Ó sì ṣe òṣùpá ní ìmọ́lẹ̀ sínú wọn. Ó tún ṣe òòrùn ní àtùpà.');
INSERT INTO "aykyuni" VALUES(71,17,'71:17','Allāhu mu yín jáde láti ara erùpẹ̀ ilẹ̀.');
INSERT INTO "aykyuni" VALUES(71,18,'71:18','Lẹ́yìn náà, Ó máa da yín padà sínú rẹ̀. Ó sì máa mu yín jáde tààrà.');
INSERT INTO "aykyuni" VALUES(71,19,'71:19','Allāhu ṣe ilẹ̀ fún yín ní ìtẹ́');
INSERT INTO "aykyuni" VALUES(71,20,'71:20','nítorí kí ẹ lè tọ àwọn ojú ọ̀nà fífẹ̀ gbẹ̀gẹ̀dẹ̀ lórí rẹ̀.”');
INSERT INTO "aykyuni" VALUES(71,21,'71:21','(Ànábì) Nūh sọ pé: “Olúwa mi, dájúdájú wọ́n yapa mi. Wọ́n sì tẹ̀lé ẹni tí dúkìá rẹ̀ àti ọmọ rẹ̀ kò ṣe àlékún kan fún bí kò ṣe òfò.');
INSERT INTO "aykyuni" VALUES(71,22,'71:22','Wọ́n sì déte ní ète tó tóbi.”');
INSERT INTO "aykyuni" VALUES(71,23,'71:23','Wọ́n sì wí pé: “Ẹ kò gbọ́dọ̀ fi àwọn òrìṣà yín sílẹ̀. Ẹ kò gbọ́dọ̀ fi òrìṣà Wadd, Suwā‘u, Yẹgūth, Yẹ‘ūƙ àti Nasr sílẹ̀.”');
INSERT INTO "aykyuni" VALUES(71,24,'71:24','Dájúdájú wọ́n ti kó ọ̀pọ̀lọ́pọ̀ ṣìnà. (Olúwa mi) má ṣe jẹ́ kí àwọn alábòsí lékún ní kiní kan bí kò ṣe ìṣìnà.');
INSERT INTO "aykyuni" VALUES(71,25,'71:25','Nítorí ẹ̀ṣẹ̀ wọn, wọ́n tẹ̀ wọ́n rì sínú omi. Wọ́n sì máa fi wọ́n sínú Iná. Wọn kò sì níí rí àwọn alárànṣe kan fún wọn lẹ́yìn Allāhu.');
INSERT INTO "aykyuni" VALUES(71,26,'71:26','(Ànábì) Nūh tún sọ pé: “Olúwa mi, má ṣe fi ẹnì kan kan nínú àwọn aláìgbàgbọ́ sílẹ̀ lórí ilẹ̀.');
INSERT INTO "aykyuni" VALUES(71,27,'71:27','Dájúdájú tí Ìwọ bá fi wọ́n sílẹ̀, wọn yóò kó ìṣìnà bá àwọn ẹrú Rẹ. Wọn kò sì níí bí ọmọ kan àfi ẹni burúkú, aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(71,28,'71:28','Olúwa mi foríjìn èmi, àwọn òbí mi méjèèjì àti ẹni tó bá wọ inú ilé mi (tí ó jẹ́) onígbàgbọ́ òdodo àti àwọn onígbàgbọ́ òdodo lọ́kùnrin àti onígbàgbọ́ òdodo lóbìnrin. Má sì ṣàlékún kan fún àwọn alábòsí àfi ìparun.”');
INSERT INTO "aykyuni" VALUES(72,1,'72:1','Sọ pé: “Wọ́n fi ìmísí ránṣẹ́ sí mi pé, dájúdájú ìjọ kan nínú àwọn àlùjànnú tẹ́tí (sí al-Ƙur’ān).” Wọ́n sì sọ pé: “Dájúdájú àwa gbọ́ al-Ƙur’ān ìyanu.');
INSERT INTO "aykyuni" VALUES(72,2,'72:2','Ó ń ṣètọ́sọ́nà síbi ìmọ̀nà. Nítorí náà, a gbà á gbọ́. Àwa kò sì níí fi ẹnì kan kan ṣe akẹgbẹ́ fún Olúwa wa (àwa kò níí ṣẹbọ).');
INSERT INTO "aykyuni" VALUES(72,3,'72:3','Àti pé dájúdájú títóbi Olúwa wa ga. Kò fi ẹnì kan kan ṣe aya àti ọmọ.');
INSERT INTO "aykyuni" VALUES(72,4,'72:4','Dájúdájú òmùgọ̀ nínú wa máa ń sọ ìsọkúsọ nípa Allāhu.');
INSERT INTO "aykyuni" VALUES(72,5,'72:5','Dájúdájú àwa sì ń rò pé ènìyàn àti àlùjànnú kò níí pa irọ́ mọ́ Allāhu ni.');
INSERT INTO "aykyuni" VALUES(72,6,'72:6','Àti pé dájúdájú àwọn ọkùnrin kan nínú ènìyàn máa ń fi àwọn ọkùnrin kan nínú àwọn àlùjànnú wá ààbò. Àwọn àlùjànnú sì ṣe àlékún aburú fún wọn.');
INSERT INTO "aykyuni" VALUES(72,7,'72:7','Dájúdájú àwọn àlùjànnú lérò gẹ́gẹ́ bí ẹ̀yin ènìyàn náà ṣe lérò pé, Allāhu kò níí gbé ẹnì kan kan dìde (lẹ́yìn ikú).');
INSERT INTO "aykyuni" VALUES(72,8,'72:8','Dájúdájú àwa wá (ọ̀rọ̀ àyọ́gbọ́) wá sí sánmọ̀, a sì bá a tí ó ti kún fún àwọn ẹ̀ṣọ́ tó lágbára àti àwọn ògúnná.');
INSERT INTO "aykyuni" VALUES(72,9,'72:9','Dájúdájú àwa máa ń jókòó síbẹ̀ ní àwọn ibùdó kan fún gbígbọ́ ọ̀rọ̀. Ṣùgbọ́n ẹni tó bá jọ́rọ̀ gbọ́ lásìkò yìí, ó máa rí ògúnná tó ti lúgọ dè é.');
INSERT INTO "aykyuni" VALUES(72,10,'72:10','Dájúdájú àwa kò sì mọ̀ bóyá aburú ni wọ́n gbàlérò pẹ̀lú àwọn tó wà lórí ilẹ̀ tàbí Olúwa wọn gbèrò ìmọ̀nà fún wọn.');
INSERT INTO "aykyuni" VALUES(72,11,'72:11','Dájúdájú àwọn ẹni rere wà nínú wa. Àwọn mìíràn yàtọ̀ sí ìyẹn tún wà nínú wa. A wà lójú ọ̀nà (ẹ̀sìn) ọ̀tọ̀ọ̀tọ̀.');
INSERT INTO "aykyuni" VALUES(72,12,'72:12','Dájúdájú àwa mọ àmọ̀dájú pé àwa kò lè mórí bọ́ mọ́ Allāhu lọ́wọ́ lórí ilẹ̀. Àwa kò sì lè sá mọ́ Ọn lọ́wọ́.');
INSERT INTO "aykyuni" VALUES(72,13,'72:13','Dájúdájú nígbà tí a gbọ́ nípa ìmọ̀nà (láti inú al-Ƙur’ān), a gbà á gbọ́. Nítorí náà, ẹnikẹ́ni tí ó bá gbàgbọ́ ní òdodo nínú Olúwa rẹ̀, kí ó má bẹ̀rù àdínkù(nínú ẹ̀san rere) àti (àlékún) aburú.1 ');
INSERT INTO "aykyuni" VALUES(72,14,'72:14','Dájúdájú àwọn mùsùlùmí wà nínú wa. Àwọn arúfin náà wà nínú wa. Nítorí náà, ẹnikẹ́ni tí ó bá ṣe ’Islām, àwọn wọ̀nyẹn ni wọ́n ṣe àwárí ìmọ̀nà.”');
INSERT INTO "aykyuni" VALUES(72,15,'72:15','Ní ti àwọn arúfin, àwọn ni wọ́n máa jẹ́ igi ìkoná fún iná Jahanamọ.”');
INSERT INTO "aykyuni" VALUES(72,16,'72:16','Àti pé tí ó bá jẹ́ pé wọ́n dúró ṣinṣin lójú ọ̀nà (’Islām) ni, dájúdájú Àwa ìbá fún wọn ní omi púpọ̀ mu.');
INSERT INTO "aykyuni" VALUES(72,17,'72:17','Nítorí kí Á lè fi dán wọn wò ni. Ẹnikẹ́ni tí ó bá gbúnrí kúrò níbi ìrántí Olúwa rẹ̀, Allāhu máa mú un wọ inú ìyà ìnira.');
INSERT INTO "aykyuni" VALUES(72,18,'72:18','Dájúdájú àwọn mọ́sálásí ń jẹ́ ti Allāhu.Nítorí náà, ẹ kò gbọ́dọ̀ pe ẹnì kan pẹ̀lú Allāhu. ');
INSERT INTO "aykyuni" VALUES(72,19,'72:19','Dájúdájú nígbà tí ẹrúsìn Allāhu dìde dúró tó ń pe Allāhu, àwọn àlùjànnú fẹ́ẹ̀ máa dìfún-ùn-ùnfún lọ́dọ̀ rẹ̀ (láti tẹ́tí sí al-Ƙur’ān).');
INSERT INTO "aykyuni" VALUES(72,20,'72:20','Sọ pé: “Olúwa mi ni mò ń pè. Àti pé èmi kò níí sọ ẹnì kan kan di akẹgbẹ́ fún Un.”');
INSERT INTO "aykyuni" VALUES(72,21,'72:21','Sọ pé: “Dájúdájú èmi kò ní ìkápá lórí ìnira àti ìmọ̀nà fún yín.”');
INSERT INTO "aykyuni" VALUES(72,22,'72:22','Sọ pé: “Dájúdájú èmi, kò sí ẹnì kan tó lè gbà mí là lọ́dọ̀ Allāhu. Èmi kò sì rí ibùsásí kan lẹ́yìn Rẹ̀.');
INSERT INTO "aykyuni" VALUES(72,23,'72:23','Àfi (kí n̄g ṣe) ìkédé ẹ̀sìn (tó wá) láti ọ̀dọ̀ Allāhu àti pé (kí n̄g sì jẹ́) àwọn iṣẹ́ Rẹ̀ (t’Ó fi rán mi). Ẹnikẹ́ni tó bá yapa Allāhu àti Òjíṣẹ́ Rẹ̀, dájúdájú iná Jahanamọ ń bẹ fún un. Olùṣegbére ni wọ́n nínú rẹ̀ títí láéláé.');
INSERT INTO "aykyuni" VALUES(72,24,'72:24','(Aláìgbàgbọ́ kò níí yé ṣàì gbàgbọ́) títí di ìgbà tí wọ́n yóò fi rí ohun tí À ń ṣe ní àdéhùn fún wọn. Nígbà náà, wọn yóò mọ ta ni ó lẹ jùlọ ní alárànṣe, tí ó sì kéré jùlọ ní òǹkà.');
INSERT INTO "aykyuni" VALUES(72,25,'72:25','Sọ pé: “Èmi kò mọ̀ bóyá ohun tí Wọ́n ń ṣe ní àdéhùn fún yín ti súnmọ́ tàbí Olúwa mi yóò fún un ní àkókò tó jìnnà.');
INSERT INTO "aykyuni" VALUES(72,26,'72:26','Onímọ̀-ìkọ̀kọ̀, kò sì fi (ìmọ̀) ìkọ̀kọ̀ Rẹ̀ han ẹnì kan');
INSERT INTO "aykyuni" VALUES(72,27,'72:27','àyàfi ẹni tí Ó ṣàlẹ́ṣà tí ó jẹ́ Òjísẹ́.Dájúdájú Ó ń mú ẹ̀ṣọ́ kan rìn níwájú Òjíṣẹ́ náà àti lẹ́yìn rẹ̀ ');
INSERT INTO "aykyuni" VALUES(72,28,'72:28','nítorí kí Ó lè ṣàfi hàn pé wọ́n ti jẹ́ àwọn iṣẹ́ Olúwa wọn dé ògòńgó. Àti pé Allāhu fi ìmọ̀ rọkiri ká ohun tí ń bẹ lọ́dọ̀ wọn. Ó sì ṣọ́ òǹkà gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(73,1,'73:1','Ìwọ olùdaṣọbora.');
INSERT INTO "aykyuni" VALUES(73,2,'73:2','Dìde (kírun) ní òru àyàfi fún ìgbà díẹ̀ (ni kí o fi sùn nínú òru rẹ).');
INSERT INTO "aykyuni" VALUES(73,3,'73:3','Lo ìlàjì rẹ̀ tàbí dín díẹ̀ kù nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(73,4,'73:4','Tàbí fi kún un. Kí o sì ké al-Ƙur’ān pẹ̀lú pẹ̀lẹ́pẹ̀lẹ́.1 ');
INSERT INTO "aykyuni" VALUES(73,5,'73:5','Dájúdájú Àwa máa gbé ọ̀rọ̀ tó lágbára fún ọ.');
INSERT INTO "aykyuni" VALUES(73,6,'73:6','Dájúdájú ìdìde kírun lóru, ó wọnú ọkàn jùlọ, ó sì dára jùlọ fún kíké (al-Ƙur’ān).');
INSERT INTO "aykyuni" VALUES(73,7,'73:7','Dájúdájú iṣẹ́ púpọ̀ wà fún ọ ní ọ̀sán.');
INSERT INTO "aykyuni" VALUES(73,8,'73:8','Rántí orúkọ Olúwa rẹ. Kí o sì da ọkàn kọ Ọ́ pátápátá.');
INSERT INTO "aykyuni" VALUES(73,9,'73:9','Olúwa ibùyọ òòrùn àti ibùwọ̀ òòrùn, kò sí ọlọ́hun kan tí ẹ gbọ́dọ̀ jọ́sìn fún ní ọ̀nà òdodo àfi Òun. Nítorí náà, mú Un ní Aláfẹ̀yìntì.');
INSERT INTO "aykyuni" VALUES(73,10,'73:10','Kí o sì ṣe sùúrù lórí ohun tí wọ́n ń wí. Pa wọ́n tì ní ìpatì tó rẹwà.');
INSERT INTO "aykyuni" VALUES(73,11,'73:11','Fi Mí dá àwọn olùpe-òdodo-nírọ́, àwọn ọlọ́rọ̀. Kí o sì lọ́ra fún wọn fún ìgbà díẹ̀.');
INSERT INTO "aykyuni" VALUES(73,12,'73:12','Dájúdájú ṣẹ́kẹ́ṣẹkẹ̀ àti Iná Jẹhīm ń bẹ lọ́dọ̀ Wa.');
INSERT INTO "aykyuni" VALUES(73,13,'73:13','Oúnjẹ háfunháfun àti ìyà ẹlẹ́ta-eléro (tún wà fún wọn).');
INSERT INTO "aykyuni" VALUES(73,14,'73:14','Ní ọjọ́ tí ilẹ̀ àti àwọn àpáta yóò máa mì tìtì. Àwọn àpáta sì máa di yanrìn tí wọ́n kójọ tí wọ́n túká.');
INSERT INTO "aykyuni" VALUES(73,15,'73:15','Dájúdájú Àwa rán Òjíṣẹ́ kan níṣẹ́ si yín (tí ó jẹ́) olùjẹ́rìí lórí yín gẹ́gẹ́ bí A ṣe rán Òjíṣẹ́ kan níṣẹ́ sí Fir‘aon.');
INSERT INTO "aykyuni" VALUES(73,16,'73:16','Ṣùgbọ́n Fir‘aon yapa Òjíṣẹ́ náà. A sì gbá a mú ní ìgbámú líle.');
INSERT INTO "aykyuni" VALUES(73,17,'73:17','Tí ẹ̀yin bá ṣàì gbàgbọ́, báwo ni ẹ̀yin yóò ṣe bẹ̀rù ọjọ́ kan tó máa mú àwọn ọmọdé hewú?');
INSERT INTO "aykyuni" VALUES(73,18,'73:18','Sánmọ̀ sì máa fàya pẹ́rẹpẹ̀rẹ nínú rẹ̀. Àdéhùn Rẹ̀ jẹ́ ohun tí ó máa ṣẹ.');
INSERT INTO "aykyuni" VALUES(73,19,'73:19','Dájúdájú èyí ni ìrántí. Nítorí náà, ẹni tí ó bá fẹ́ kí ó mú ojú ọ̀nà kan tọ̀ dé ọ̀dọ̀ Olúwa rẹ̀.');
INSERT INTO "aykyuni" VALUES(73,20,'73:20','Dájúdájú Olúwa rẹ mọ̀ pé dájúdájú ìwọ ń dìde kírun fún ohun tó kéré sí ìlàta méjì òru tàbí ìlàjì rẹ̀ tàbí ìlàta rẹ̀. Igun kan nínú àwọn tó wà pẹ̀lú rẹ (náà ń ṣe bẹ́ẹ̀). Allāhu l’Ó ń ṣòdíwọ̀n òru àti ọ̀sán. Ó sì mọ̀ pé ẹ kò lè ṣọ́ ọ.Nítorí náà, Ó ti dá a padà sí fífúyẹ́ fún yín. Nítorí náà, ẹ ké ohun tí ó bá rọrùn (fún yín) nínú al-Ƙur’ān. Ó mọ̀ pé àwọn aláìsàn yóò wà nínú yín. Àwọn mìíràn sì ń rìrìn àjò lórí-ilẹ̀, tí wọ́n ń wá nínú oore Allāhu. Àwọn mìíràn sì ń jagun fún ẹ̀sìn Allāhu. Nítorí náà, ẹ ké ohun tí ó bá rọrùn (fún yín) nínú rẹ̀. Ẹ kírun (ọ̀ran-an-yàn). Ẹ yọ Zakāh. Kí ẹ sì yá Allāhu ní dúkìá tó dára. Ohunkóhun tí ẹ bá tì síwájú fún ẹ̀mí ara yín nínú ohun rere, ẹ̀yin yóò bá a lọ́dọ̀ Allāhu ní ohun rere àti ní ohun tí ó tóbi jùlọ ní ẹ̀san. Ẹ tọrọ àforíjìn lọ́dọ̀ Allāhu. Dájúdájú Allāhu ni Aláforíjìn, Àṣàkẹ́-ọ̀run. ');
INSERT INTO "aykyuni" VALUES(74,1,'74:1','Ìwọ olùdaṣọbora.');
INSERT INTO "aykyuni" VALUES(74,2,'74:2','Dìde kí o ṣèkìlọ̀.');
INSERT INTO "aykyuni" VALUES(74,3,'74:3','Olúwa rẹ sì ni kí o gbé títóbi fún.');
INSERT INTO "aykyuni" VALUES(74,4,'74:4','Aṣọ rẹ ni kí o fọ̀ mọ́.');
INSERT INTO "aykyuni" VALUES(74,5,'74:5','Òrìṣà ni kí o jìnnà sí.');
INSERT INTO "aykyuni" VALUES(74,6,'74:6','Má ṣe tọrẹ nítorí kí o lè rí púpọ̀ gbà.');
INSERT INTO "aykyuni" VALUES(74,7,'74:7','Olúwa rẹ ni kí o ṣe sùúrù fún.');
INSERT INTO "aykyuni" VALUES(74,8,'74:8','Nígbà tí wọ́n bá fọn fèrè oníwo fún àjíǹde,');
INSERT INTO "aykyuni" VALUES(74,9,'74:9','ìyẹn, ní ọjọ́ yẹn, ni ọjọ́ ìnira,');
INSERT INTO "aykyuni" VALUES(74,10,'74:10','tí kò níí rọrùn fún àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(74,11,'74:11','Fi Mí dá ẹni tí Mo dá ní òun nìkan (ìyẹn nínú ikùn ìyá rẹ̀).');
INSERT INTO "aykyuni" VALUES(74,12,'74:12','Mo sì fún un ní ọ̀pọ̀lọ́pọ̀ dúkìá');
INSERT INTO "aykyuni" VALUES(74,13,'74:13','àti àwọn ọmọkúnrin tó ń bẹ pẹ̀lú rẹ̀.');
INSERT INTO "aykyuni" VALUES(74,14,'74:14','Mo sì fi àyè ìrọ̀rùn gbá a dáadáa.');
INSERT INTO "aykyuni" VALUES(74,15,'74:15','Lẹ́yìn náà, ó tún ń jẹ̀rankàn pé kí N̄g ṣe àlékún.');
INSERT INTO "aykyuni" VALUES(74,16,'74:16','Rárá o! Dájúdájú ó jẹ́ alátakò sí àwọn āyah Wa.');
INSERT INTO "aykyuni" VALUES(74,17,'74:17','Èmi yóò la ìyà pọ́nkèpọ́nkè bọ̀ ọ́ lọ́rùn (nínú Iná) ');
INSERT INTO "aykyuni" VALUES(74,18,'74:18','Dájúdájú ó ronú (lódì nípa al-Ƙur’ān). Ó sì pinnu (ọ̀rọ̀ kan nínú ẹ̀mí rẹ̀).');
INSERT INTO "aykyuni" VALUES(74,19,'74:19','Wọ́n sì ṣẹ́bi lé e nípa bí ó ṣe pinnu.');
INSERT INTO "aykyuni" VALUES(74,20,'74:20','Lẹ́yìn náà, Wọ́n tún ṣẹ́bi lé e nípa bí ó ṣe pinnu.');
INSERT INTO "aykyuni" VALUES(74,21,'74:21','Lẹ́yìn náà, ó wò sùn.');
INSERT INTO "aykyuni" VALUES(74,22,'74:22','Lẹ́yìn náà, ó fajú ro, ó sì dijú mágbárí.');
INSERT INTO "aykyuni" VALUES(74,23,'74:23','Lẹ́yìn náà, ó pẹ̀yìndà, ó sì ṣègbéraga.');
INSERT INTO "aykyuni" VALUES(74,24,'74:24','Ó sì wí pé: “Kí ni èyí bí kò ṣe idán àtọwọ́dọ́wọ́.');
INSERT INTO "aykyuni" VALUES(74,25,'74:25','Kí ni èyí bí kò ṣe ọ̀rọ̀ abara”');
INSERT INTO "aykyuni" VALUES(74,26,'74:26','Èmi yóò fi sínú iná Saƙọr.');
INSERT INTO "aykyuni" VALUES(74,27,'74:27','Kí l’ó mú ọ mọ ohun tó ń jẹ́ iná Saƙọr?');
INSERT INTO "aykyuni" VALUES(74,28,'74:28','(Saƙọr) kò níí ṣẹ́ ẹ kù, kò sì níí pa á tì.1 ');
INSERT INTO "aykyuni" VALUES(74,29,'74:29','Ó máa jó awọ ara di dúdú.');
INSERT INTO "aykyuni" VALUES(74,30,'74:30','(Àwọn mọlāika) mọ́kàndínlógún ni ẹ̀ṣọ́ rẹ̀.');
INSERT INTO "aykyuni" VALUES(74,31,'74:31','Àwa kò ṣe ẹnì kan ní ẹ̀ṣọ́ Iná àfi àwọn mọlāika. Àwa kò sì ṣe òǹkà wọn (bẹ́ẹ̀) bí kò ṣe (nítorí kí ó lè jẹ́) àdánwò fún àwọn tó ṣàì gbàgbọ́, àti nítorí kí àwọn tí A fún ní Tírà lè ní àmọ̀dájú àti nítorí kí àwọn tí wọ́n gbàgbọ́ ní òdodo lè lékún ní ìgbàgbọ́ òdodo. (Ó rí bẹ́ẹ̀) nítorí kí àwọn tí A fún ní Tírà àti àwọn onígbàgbọ́ òdodo má baà ṣeyèméjì àti nítorí kí àwọn tí àrùn wà nínú ọkàn wọn àti àwọn aláìgbàgbọ́ lè wí pé: “Kí ni Allāhu gbà lérò pẹ̀lú àpèjúwe yìí?” Báyẹn ni Allāhu ṣe ń ṣi ẹni tí Ó bá fẹ́ lọ́nà, Ó sì ń tọ́ ẹni tí Ó bá fẹ́ sọ́nà. Kò sì sí ẹni tí ó mọ àwọn ọmọ ogun Olúwa rẹ àfi Òun náà. (Iná Saƙọr) kò sì jẹ́ kiní kan bí kò ṣe ìrántí fún abara.');
INSERT INTO "aykyuni" VALUES(74,32,'74:32','Ẹ gbọ́! Allāhu fi òṣùpá búra.');
INSERT INTO "aykyuni" VALUES(74,33,'74:33','Ó tún fi alẹ́ nígbà tí ó bá lọ búra.');
INSERT INTO "aykyuni" VALUES(74,34,'74:34','Ó tún fi òwúrọ̀ nígbà tí ojúmọ́ bá mọ́ búra.');
INSERT INTO "aykyuni" VALUES(74,35,'74:35','Dájúdájú (iná Saƙọr) ni ọ̀kan nínú (àwọn àdánwò) tó tóbi.');
INSERT INTO "aykyuni" VALUES(74,36,'74:36','(Ó jẹ́) ìkìlọ̀ fún abara.');
INSERT INTO "aykyuni" VALUES(74,37,'74:37','Fún ẹni tí ó bá fẹ́ nínú yín láti tẹ̀ síwájú tàbí láti fà sẹ́yìn (nínú ẹ̀ṣẹ̀).');
INSERT INTO "aykyuni" VALUES(74,38,'74:38','Ẹ̀mí kọ̀ọ̀kan ni onídùúró fún ohun tó ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(74,39,'74:39','Àyàfi àwọn èrò ọwọ́ ọ̀tún.');
INSERT INTO "aykyuni" VALUES(74,40,'74:40','Wọn yóò máa bira wọn léèrè ọ̀rọ̀ nínú àwọn Ọgbà Ìdẹ̀ra');
INSERT INTO "aykyuni" VALUES(74,41,'74:41','nípa àwọn ẹlẹ́ṣẹ̀ pé:');
INSERT INTO "aykyuni" VALUES(74,42,'74:42','“Kí ni ó mu yín wọ inú iná Saƙọr?”');
INSERT INTO "aykyuni" VALUES(74,43,'74:43','Wọn yóò wí pé: “Àwa kò sí nínú àwọn olùkírun ni.');
INSERT INTO "aykyuni" VALUES(74,44,'74:44','Àwa kò sì sí nínú àwọn tó ń bọ́ àwọn mẹ̀kúnnù.');
INSERT INTO "aykyuni" VALUES(74,45,'74:45','Àwa sì máa ń sọ ìsọkúsọ pẹ̀lú àwọn onísọkúsọ.');
INSERT INTO "aykyuni" VALUES(74,46,'74:46','Àti pé àwa máa ń pe Ọjọ́ ẹ̀san ní irọ́');
INSERT INTO "aykyuni" VALUES(74,47,'74:47','títí ikú fi dé bá wa.”');
INSERT INTO "aykyuni" VALUES(74,48,'74:48','Ìpẹ̀ àwọn olùṣìpẹ̀ kò sì níí ṣe wọ́n ní àǹfààní.');
INSERT INTO "aykyuni" VALUES(74,49,'74:49','Kí ló mú wọn ná tí wọ́n ń gbúnrí kúrò níbi ìrántí');
INSERT INTO "aykyuni" VALUES(74,50,'74:50','bí ẹni pé àwọn ni kẹ́tẹ́kẹ́tẹ́ tó ń sá lọ,');
INSERT INTO "aykyuni" VALUES(74,51,'74:51','tó sá fún kìnìhún?');
INSERT INTO "aykyuni" VALUES(74,52,'74:52','Rárá, ńṣe ni ẹnì kọ̀ọ̀kan nínú wọn ń fẹ́ kí Á fún òun náà ní Tírà tí ó máa ṣeé tẹ́ han àwọn ènìyàn (bíi ti al-Ƙur’ān).');
INSERT INTO "aykyuni" VALUES(74,53,'74:53','Rárá. Wọn kò páyà ọ̀run ni.');
INSERT INTO "aykyuni" VALUES(74,54,'74:54','Ẹ gbọ́! Dájúdájú al-Ƙur’ān ni ìrántí.');
INSERT INTO "aykyuni" VALUES(74,55,'74:55','Nítorí náà, ẹni tí ó bá fẹ́ kó rántí rẹ̀.');
INSERT INTO "aykyuni" VALUES(74,56,'74:56','Wọn kò sì níí rántí (al-Ƙur’ān) àfi tí Allāhu bá fẹ́. (Allāhu) Òun l’a gbọ́dọ̀ bẹ̀rù. Òun l’ó sì ni àforíjìn.');
INSERT INTO "aykyuni" VALUES(75,1,'75:1','Èmi (Allāhu) ń fi Ọjọ́ Àjíǹde búra.');
INSERT INTO "aykyuni" VALUES(75,2,'75:2','Mo tún ń fi ẹ̀mí tó máa dá ara rẹ̀ ní ẹ̀bi búra.');
INSERT INTO "aykyuni" VALUES(75,3,'75:3','Ṣé ènìyàn lérò pé A kò níí kó àwọn eegun rẹ̀ jọ ni?');
INSERT INTO "aykyuni" VALUES(75,4,'75:4','Bẹ́ẹ̀ ni A sì lágbára láti to eegun ọmọníka rẹ̀ dọ́gba ');
INSERT INTO "aykyuni" VALUES(75,5,'75:5','Rárá ènìyàn fẹ́ máa ṣe aburú lọ bẹ́ẹ̀ nínú ìṣẹ̀mí ayé rẹ̀');
INSERT INTO "aykyuni" VALUES(75,6,'75:6','ni ó fi ń bèèrè pé: “Ìgbà wo ni Ọjọ́ Àjíǹde?”');
INSERT INTO "aykyuni" VALUES(75,7,'75:7','Nígbà tí ojú bá rí ìdààmú tó sì yà sílẹ̀ tí kò lè padé,');
INSERT INTO "aykyuni" VALUES(75,8,'75:8','àti (nígbà) tí òṣùpá bá wọ̀ọ̀kùn,');
INSERT INTO "aykyuni" VALUES(75,9,'75:9','àti (nígbà) tí wọ́n bá pa òòrùn àti òṣùpá pọ̀ mọ́ra wọn tí ìmọ́lẹ̀ méjèèjì pòóró,');
INSERT INTO "aykyuni" VALUES(75,10,'75:10','ènìyàn yóò wí ní ọjọ́ yẹn pé: “Níbo ni ibùsásí wà?”');
INSERT INTO "aykyuni" VALUES(75,11,'75:11','Rárá, kò sí ibùsásí.');
INSERT INTO "aykyuni" VALUES(75,12,'75:12','Ọ̀dọ̀ Olúwa rẹ ni ibùdúró ní ọjọ́ yẹn.');
INSERT INTO "aykyuni" VALUES(75,13,'75:13','Wọ́n máa fún ènìyàn ní ìró ní ọjọ́ yẹn nípa ohun tó tì síwájú (nínú iṣẹ́ rẹ̀) àti ohun tó fi kẹ́yìn (nínú orípa iṣẹ́ rẹ̀).');
INSERT INTO "aykyuni" VALUES(75,14,'75:14','Ṣebí ènìyàn dá ara rẹ̀ mọ̀,');
INSERT INTO "aykyuni" VALUES(75,15,'75:15','ìbáà mú àwọn àwáwí rẹ̀ wá.');
INSERT INTO "aykyuni" VALUES(75,16,'75:16','Má ṣe gbé ahọ́n rẹ sí ìmísí láti kánjú ké e.');
INSERT INTO "aykyuni" VALUES(75,17,'75:17','Dájúdájú Àwa l’A máa ṣe àkójọ rẹ̀ (sínú ọkàn rẹ) àti kíké rẹ̀.');
INSERT INTO "aykyuni" VALUES(75,18,'75:18','Nígbà tí A bá ń ké e (fún ọ), kí o sì máa tẹ̀lé kíké rẹ̀.');
INSERT INTO "aykyuni" VALUES(75,19,'75:19','Lẹ́yìn náà, dájúdájú Àwa l’A máa ṣe àlàyé rẹ̀ fún ọ.');
INSERT INTO "aykyuni" VALUES(75,20,'75:20','Kò rí bẹ́ẹ̀, bí kò ṣe pé, ẹ nífẹ̀ẹ́ ayé ni.');
INSERT INTO "aykyuni" VALUES(75,21,'75:21','Ẹ sì ń gbé ọ̀run jù sílẹ̀.');
INSERT INTO "aykyuni" VALUES(75,22,'75:22','Àwọn ojú kan ní ọjọ́ yẹn yóò tutù (fún ìdùnnú).');
INSERT INTO "aykyuni" VALUES(75,23,'75:23','Olúwa wọn ni wọn yóò máa wò.');
INSERT INTO "aykyuni" VALUES(75,24,'75:24','Àwọn ojú kan ní ọjọ́ yẹn sì máa bàjẹ́.');
INSERT INTO "aykyuni" VALUES(75,25,'75:25','(Ojú náà) yóò mọ̀ dájú pé wọ́n máa fi aburú kan òhun.');
INSERT INTO "aykyuni" VALUES(75,26,'75:26','Kò rí bẹ́ẹ̀, nígbà tí ẹ̀mí bá dé gògòńgò.');
INSERT INTO "aykyuni" VALUES(75,27,'75:27','A sì máa sọ pé: “Ǹjẹ́ olùpọfọ̀ kan wà bí (tí ó máa pọfọ̀ láti gbà á sílẹ̀ lọ́wọ́ ikú)?');
INSERT INTO "aykyuni" VALUES(75,28,'75:28','Ó sì máa dá a lójú pé dájúdájú ìpínyà ayé ti dé.');
INSERT INTO "aykyuni" VALUES(75,29,'75:29','Ojúgun yó sì lọ́mọ́ ojúgun.');
INSERT INTO "aykyuni" VALUES(75,30,'75:30','Ọ̀dọ̀ Olúwa rẹ sì ni wíwa ẹ̀dá lọ ní ọjọ́ yẹn.');
INSERT INTO "aykyuni" VALUES(75,31,'75:31','Kò kúkú gba òdodo gbọ́, kò sì kírun.');
INSERT INTO "aykyuni" VALUES(75,32,'75:32','Ṣùgbọ́n ó pe òdodo ní irọ́. Ó sì kẹ̀yìn sí i.');
INSERT INTO "aykyuni" VALUES(75,33,'75:33','Lẹ́yìn náà, ó lọ sí ọ̀dọ̀ àwọn ènìyàn rẹ̀, ó sì ń ṣe fáàrí.');
INSERT INTO "aykyuni" VALUES(75,34,'75:34','Ègbé ni fún ọ, ègbé ni sẹ́.');
INSERT INTO "aykyuni" VALUES(75,35,'75:35','Lẹ́yìn náà, ègbé ni fún ọ, ègbé sì ni fún ọ sẹ́.');
INSERT INTO "aykyuni" VALUES(75,36,'75:36','Ṣé ènìyàn lérò pé A máa fi òun sílẹ̀ lásán ni?');
INSERT INTO "aykyuni" VALUES(75,37,'75:37','Ṣé kò ti jẹ́ àtọ̀ mọnī tí wọ́n dà jáde sínú àpòlùkẹ́ ni?');
INSERT INTO "aykyuni" VALUES(75,38,'75:38','Lẹ́yìn náà, ó di ẹ̀jẹ̀ dídì. (Allāhu) sì ṣẹ̀dá (rẹ̀). Ó sì ṣe é tó dọ́gba jálẹ̀ léèyàn.');
INSERT INTO "aykyuni" VALUES(75,39,'75:39','(Allāhu) sì ṣe ìran méjì jáde láti ara rẹ̀, akọ àti abo.');
INSERT INTO "aykyuni" VALUES(75,40,'75:40','Ǹjẹ́ Ẹni tí Ó ṣe ìyẹn kò níí ní agbára láti jí àwọn òkú dìde bí?');
INSERT INTO "aykyuni" VALUES(76,1,'76:1','Ṣebí àkókò kan nínú ìgbà ti ré kọjá lórí ènìyàn tí (ènìyàn) kò jẹ́ kiní kan tí wọ́n ń dárúkọ?');
INSERT INTO "aykyuni" VALUES(76,2,'76:2','Dájúdájú Àwa ṣẹ̀dá ènìyàn láti ara àtọ̀ tó ròpọ̀ mọ́ra wọn (láti ara ọkùnrin àti obìnrin), A sì máa dán an wò.A sì ṣe é ní olùgbọ́, olùríran. ');
INSERT INTO "aykyuni" VALUES(76,3,'76:3','Dájúdájú A fi ọ̀nà mọ̀ ọ́n; Ó lè jẹ́ olùdúpẹ́, ó sì lè jẹ́ aláìmoore.');
INSERT INTO "aykyuni" VALUES(76,4,'76:4','Dájúdájú Àwa pèsè àwọn ẹ̀wọ̀n, àjàgà àti Iná sílẹ̀ de àwọn aláìgbàgbọ́.');
INSERT INTO "aykyuni" VALUES(76,5,'76:5','Dájúdájú àwọn ẹni rere, wọn yóò máa mu omi kan nínú ife. Ohun tí A pòpọ̀ mọ́ (omi náà) ni káfúrà.');
INSERT INTO "aykyuni" VALUES(76,6,'76:6','Omi ìṣẹ́lẹ̀rú kan ni, tí àwọn ẹrúsìn Allāhu yóò máa mu, tí wọn yó sì máa mú un ṣàn jáde dáadáa (ní ibi tí wọ́n bá ti fẹ́ ẹ).');
INSERT INTO "aykyuni" VALUES(76,7,'76:7','Wọ́n ń mú ẹ̀jẹ́ wọn ṣẹ. Wọ́n sì ń páyà ọjọ́ kan, tí aburú rẹ̀ jẹ́ ohun tí ó máa fọ́nká gan-an.');
INSERT INTO "aykyuni" VALUES(76,8,'76:8','Tòhun ti bí wọ́n ṣe ní ìfẹ́ sí (ọrọ̀ tó), wọ́n ń fún mẹ̀kúnnù, ọmọ òrukàn àti ẹrú ní oúnjẹ jẹ.');
INSERT INTO "aykyuni" VALUES(76,9,'76:9','(Wọ́n sì ń sọ pé:) “Nítorí Ojú rere Allāhu (nìkan) l’a fi ń fun yín ní oúnjẹ jẹ; àwa kò sì gbèrò ẹ̀san tàbí ìdúpẹ́ kan láti ọ̀dọ̀ yín.');
INSERT INTO "aykyuni" VALUES(76,10,'76:10','Dájúdájú àwa ń páyà ọjọ́ kan tí (ẹ̀dá yóò) fajú ro kókó ní ọ̀dọ̀ Olúwa wa.”');
INSERT INTO "aykyuni" VALUES(76,11,'76:11','Nítorí náà, Allāhu yóò ṣọ́ wọn níbi aburú ọjọ́ yẹn. Ó sì máa fún wọn ni ìtutù ojú àti ìdùnnú.');
INSERT INTO "aykyuni" VALUES(76,12,'76:12','Ó sì máa san wọ́n ní ẹ̀san Ọgbà Ìdẹ̀ra àti aṣọ àlàárì nítorí pé wọ́n ṣe sùúrù.');
INSERT INTO "aykyuni" VALUES(76,13,'76:13','Wọn yóò máa rọ̀gbọ̀kú sórí àwọn ibùsùn wọn. Wọn kò sì níí rí òòrùn tàbí òtútù.');
INSERT INTO "aykyuni" VALUES(76,14,'76:14','Àwọn ibòji (inú Ọgbà Ìdẹ̀ra) yóò súnmọ́ wọn. A sì máa mú àwọn esò ibẹ̀ súnmọ́ àrọ́wọ́tó wọn ní ti sísúnmọ́.');
INSERT INTO "aykyuni" VALUES(76,15,'76:15','Àti pé wọn yóò máa gbé ife ìmumi ńlá tí wọ́n ṣe láti ara fàdákà àti àwọn ife ìmumi kékeré tí ó jẹ́ aláwo káà kiri ọ̀dọ̀ wọn.');
INSERT INTO "aykyuni" VALUES(76,16,'76:16','Ife aláwo tí wọ́n ṣe láti ara fàdákà ni (wọ́n). A sì ṣe wọ́n níwọ̀n- níwọ̀n (tí ó ṣe wẹ́kú ohun tí wọ́n fẹ́ mu).');
INSERT INTO "aykyuni" VALUES(76,17,'76:17','Àti pé wọn yóò máa fún wọn ní ife ọtí kan mu nínú (Ọgbà Ìdẹ̀ra). Ohun tí A pòpọ̀ mọ́ (ọtí náà) ni atalẹ̀.');
INSERT INTO "aykyuni" VALUES(76,18,'76:18','Nínú Ọgbà Ìdẹ̀ra (wọn yóò tún máa mu) omi ìṣẹ́lẹ̀rú kan, tí À ń pè ní omi salsabīl.');
INSERT INTO "aykyuni" VALUES(76,19,'76:19','Àwọn ọ̀dọ́kùnrin tí kò níí gbó yó sì máa lọ bọ̀ láààrin wọn. Nígbà tí ìwọ bá sì rí wọn, ìwọ yóò kà wọ́n kún òkúta olówó iyebíye tí wọ́n tàn kalẹ̀.');
INSERT INTO "aykyuni" VALUES(76,20,'76:20','Nígbà tí ìwọ bá sì wo ibẹ̀ yẹn (nínú Ọgbà Ìdẹ̀ra), ìwọ yóò rí ìdẹ̀ra àti ọlá tó tóbi.');
INSERT INTO "aykyuni" VALUES(76,21,'76:21','Aṣọ àlàárì fẹ́lẹ́fẹ́lẹ́ aláwọ̀ ewéko àti aṣọ àlàárì tó nípọn máa wà lókè wọn (àti lára wọn). A sì máa fi ẹ̀gbà-ọwọ́ onífàdákà ṣe wọ́n ní ọ̀ṣọ́. Olúwa wọn yó sì fún wọn ní ohun mímu mímọ́ mu.');
INSERT INTO "aykyuni" VALUES(76,22,'76:22','Dájúdájú èyí jẹ́ ẹ̀san fun yín. Wọ́n sì máa fi ẹ̀san rere àdìpèlé dúpẹ́ iṣẹ́ yín fun yín.');
INSERT INTO "aykyuni" VALUES(76,23,'76:23','Dájúdájú Àwa l’A sọ al-Ƙur’ān kalẹ̀ fún ọ díẹ̀ díẹ̀.1 ');
INSERT INTO "aykyuni" VALUES(76,24,'76:24','Nítorí náà, ṣe sùúrù fún ìdájọ́ Olúwa rẹ. Má ṣe tẹ̀lé ẹlẹ́ṣẹ̀ tàbí aláìmoore kan nínú wọn.');
INSERT INTO "aykyuni" VALUES(76,25,'76:25','Rántí orúkọ Olúwa rẹ ní àárọ̀ àti ní ìrọ̀lẹ́.');
INSERT INTO "aykyuni" VALUES(76,26,'76:26','Àti ní òru, forí kanlẹ̀ fún Un. Kí o sì ṣàfọ̀mọ́ fún Un ní òru fún ìgbà pípẹ́.');
INSERT INTO "aykyuni" VALUES(76,27,'76:27','Dájúdájú àwọn wọ̀nyí fẹ́ràn ayé. Wọ́n sì ń pa ọjọ́ tó wúwo tì sẹ́yìn wọn.');
INSERT INTO "aykyuni" VALUES(76,28,'76:28','Àwa l’A ṣẹ̀dá wọn. A sì ṣe ìṣẹ̀dá wọn dáradára. Nígbà tí A bá sì fẹ́, A óò fi irú wọn pààrọ̀ wọn ní ti ìpààrọ̀.');
INSERT INTO "aykyuni" VALUES(76,29,'76:29','Dájúdájú èyí ni ìrántí. Nítorí náà, ẹni tí ó bá fẹ́ kí ó mú ojú ọ̀nà tọ̀ sí ọ̀dọ̀ Olúwa rẹ̀.');
INSERT INTO "aykyuni" VALUES(76,30,'76:30','Ẹ̀yin kò sì níí fẹ́ (kiní kan) àfi tí Allāhu bá fẹ́. Dájúdájú Allāhu ń jẹ́ Onímọ̀, Ọlọ́gbọ́n.1 ');
INSERT INTO "aykyuni" VALUES(76,31,'76:31','Ó máa fi ẹni tí ó bá fẹ́ sínú ìkẹ́ rẹ̀. (Ní ti) àwọn alábòsí, Ó sì pèsè ìyà ẹlẹ́ta-eléro sílẹ̀ dè wọ́n.');
INSERT INTO "aykyuni" VALUES(77,1,'77:1','Allāhu fi àwọn atẹ́gùn tó ń sáré ní tẹ̀léǹtẹ̀lé búra.');
INSERT INTO "aykyuni" VALUES(77,2,'77:2','Ó fi àwọn ìjì atẹ́gùn tó ń jà búra.');
INSERT INTO "aykyuni" VALUES(77,3,'77:3','Ó fi àwọn atẹ́gùn tó ń tú èṣújò ká búra.');
INSERT INTO "aykyuni" VALUES(77,4,'77:4','Ó fi àwọn tó ń ṣèpínyà láààrin òdodo àti irọ́ búra.');
INSERT INTO "aykyuni" VALUES(77,5,'77:5','Ó fi àwọn mọlāika tó ń mú ìrántí wá (bá àwọn Òjíṣẹ́) búra.');
INSERT INTO "aykyuni" VALUES(77,6,'77:6','(Ìrántí náà jẹ́) àwíjàre tàbí ìkìlọ̀.');
INSERT INTO "aykyuni" VALUES(77,7,'77:7','Dájúdájú ohun tí A ṣe ní àdéhùn fún yín kúkú máa ṣẹlẹ̀.');
INSERT INTO "aykyuni" VALUES(77,8,'77:8','Nítorí náà, nígbà tí wọ́n bá pa (ìmọ́lẹ̀) ìràwọ̀ rẹ́,');
INSERT INTO "aykyuni" VALUES(77,9,'77:9','àti nígbà tí wọ́n bá ṣí sánmọ̀ sílẹ̀ gbagada,');
INSERT INTO "aykyuni" VALUES(77,10,'77:10','àti nígbà tí wọ́n bá ku àwọn àpáta dànù,');
INSERT INTO "aykyuni" VALUES(77,11,'77:11','àti nígbà tí wọ́n bá fún àwọn Òjíṣẹ́ ní àsìkò láti kójọ (pẹ̀lú ìjọ wọn),');
INSERT INTO "aykyuni" VALUES(77,12,'77:12','ọjọ́ wo ni wọ́n so (àwọn ìṣẹ̀lẹ̀ wọ̀nyí) rọ̀ fún ná?');
INSERT INTO "aykyuni" VALUES(77,13,'77:13','Fún ọjọ́ ìpínyà (láààrin àwọn ẹ̀dá) ni.');
INSERT INTO "aykyuni" VALUES(77,14,'77:14','Kí sì ni ó mú ọ mọ ohun tó ń jẹ́ Ọjọ́ ìpínyà?');
INSERT INTO "aykyuni" VALUES(77,15,'77:15','Ègbé ni ní ọjọ́ yẹn fún àwọn olùpe-òdodo-nírọ́.');
INSERT INTO "aykyuni" VALUES(77,16,'77:16','Ǹjẹ́ Àwa kò ti pa àwọn ẹni àkọ́kọ́ rẹ́ bí?');
INSERT INTO "aykyuni" VALUES(77,17,'77:17','Lẹ́yìn náà, A sì máa fi àwọn ẹni Ìkẹ́yìn tẹ̀lé wọn (nínú ìparun).');
INSERT INTO "aykyuni" VALUES(77,18,'77:18','Báyẹn ni A ó ti ṣe pẹ̀lú àwọn ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(77,19,'77:19','Ègbé ni ní ọjọ́ yẹn fún àwọn olùpe-òdodo-nírọ́.');
INSERT INTO "aykyuni" VALUES(77,20,'77:20','Ṣé A kò ṣẹ̀dá yin láti inú omi lílẹ yẹpẹrẹ bí?');
INSERT INTO "aykyuni" VALUES(77,21,'77:21','Lẹ́yìn náà, A fi sínú àyè ààbò (ìyẹn, ilé-ọmọ)');
INSERT INTO "aykyuni" VALUES(77,22,'77:22','títí di gbèdéke àkókò kan tí A ti mọ̀ (ìyen, ọjọ́ ìbímọ).');
INSERT INTO "aykyuni" VALUES(77,23,'77:23','A sì ní ìkápá àti àyànmọ́ (lórí rẹ̀. Àwa sì ni) Olùkápá àti Olùpèbùbù ẹ̀dá tó dára.');
INSERT INTO "aykyuni" VALUES(77,24,'77:24','Ègbé ni ní ọjọ́ yẹn fún àwọn olùpe-òdodo-nírọ́.');
INSERT INTO "aykyuni" VALUES(77,25,'77:25','Ǹjẹ́ Àwa kò ṣe ilẹ̀ ní ohun tó ń kó ẹ̀dá jọ mọ́ra wọn;');
INSERT INTO "aykyuni" VALUES(77,26,'77:26','(ìyẹn) àwọn alààyè àti àwọn òkú?');
INSERT INTO "aykyuni" VALUES(77,27,'77:27','A sì fi àwọn àpáta gbagidi gíga-gíga sínú rẹ̀. A sì fún yín ní omi dídùn mu.');
INSERT INTO "aykyuni" VALUES(77,28,'77:28','Ègbé ni ní ọjọ́ yẹn fún àwọn olùpe-òdodo-nírọ́.');
INSERT INTO "aykyuni" VALUES(77,29,'77:29','Ẹ máa lọ sí ibi tí ẹ̀ ń pè ní irọ́.');
INSERT INTO "aykyuni" VALUES(77,30,'77:30','Ẹ máa lọ sí ibi èéfín ẹlẹ́ka mẹ́ta.');
INSERT INTO "aykyuni" VALUES(77,31,'77:31','Kì í ṣe ibòji tútù. Kò sì níí rọ̀ wọ́n lọ́rọ̀ nínú ìjòfòfò Iná.');
INSERT INTO "aykyuni" VALUES(77,32,'77:32','Dájúdájú (Iná náà) yóò máa ju ẹ̀tapàrà (rẹ̀ sókè tó máa dà) bí pẹ̀tẹ́ẹ̀sì.');
INSERT INTO "aykyuni" VALUES(77,33,'77:33','(Ó máa dà) bí àwọn ràkúnmí aláwọ̀ omi ọsàn.');
INSERT INTO "aykyuni" VALUES(77,34,'77:34','Ègbé ni ní ọjọ́ yẹn fún àwọn olùpe-òdodo-nírọ́');
INSERT INTO "aykyuni" VALUES(77,35,'77:35','Èyí ni ọjọ́ tí wọn kò níí sọ̀rọ̀.');
INSERT INTO "aykyuni" VALUES(77,36,'77:36','A kò sì níí yọ̀ǹda (ọ̀rọ̀ sísọ) fún wọn, áḿbọ̀sìbọ́sí pé wọ́n yóò mú àwáwí wá.');
INSERT INTO "aykyuni" VALUES(77,37,'77:37','Ègbé ni ní ọjọ́ yẹn fún àwọn olùpe-òdodo-nírọ́.');
INSERT INTO "aykyuni" VALUES(77,38,'77:38','Èyí ni ọjọ́ ìpínyà. Àwa yó sì kó ẹ̀yin àti àwọn ẹni àkọ́kọ́ jọ.');
INSERT INTO "aykyuni" VALUES(77,39,'77:39','Tí ẹ bá ní ète kan lọ́wọ́, ẹ déte sí Mi wò.');
INSERT INTO "aykyuni" VALUES(77,40,'77:40','Ègbé ni ní ọjọ́ yẹn fún àwọn olùpe-òdodo-nírọ́.');
INSERT INTO "aykyuni" VALUES(77,41,'77:41','Dájúdájú àwọn olùbẹ̀rù Allāhu yóò wà níbi ibòji àti àwọn omi ìṣẹ́lẹ̀rú,');
INSERT INTO "aykyuni" VALUES(77,42,'77:42','àti àwọn èso èyí tí wọ́n bá ń fẹ́.');
INSERT INTO "aykyuni" VALUES(77,43,'77:43','Ẹ jẹ, kí ẹ sì mu pẹ̀lú ìgbádùn nítorí ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(77,44,'77:44','Dájúdájú báyẹn ni Àwa ṣe ń san àwọn olùṣe-rere ní ẹ̀san (rere).');
INSERT INTO "aykyuni" VALUES(77,45,'77:45','Ègbé ni ní ọjọ́ yẹn fún àwọn olùpe-òdodo-nírọ́.');
INSERT INTO "aykyuni" VALUES(77,46,'77:46','Ẹ jẹ, kí ẹ sì gbádùn fún ìgbà díẹ̀. Dájúdájú ẹlẹ́ṣẹ̀ ni yín.');
INSERT INTO "aykyuni" VALUES(77,47,'77:47','Ègbé ni ní ọjọ́ yẹn fún àwọn olùpe-òdodo-nírọ́.');
INSERT INTO "aykyuni" VALUES(77,48,'77:48','Nígbà tí wọ́n bá sọ fún wọn pé kí wọ́n kírun, wọn kò níí kírun.');
INSERT INTO "aykyuni" VALUES(77,49,'77:49','Ègbé ni ní ọjọ́ yẹn fún àwọn olùpe-òdodo-nírọ́.');
INSERT INTO "aykyuni" VALUES(77,50,'77:50','Nígbà náà, ọ̀rọ̀ wo ni wọn yóò gbàgbọ́ lẹ́yìn rẹ̀?1 ');
INSERT INTO "aykyuni" VALUES(78,1,'78:1','Nípa kí ni wọ́n ń bíra wọn léèrè ná?');
INSERT INTO "aykyuni" VALUES(78,2,'78:2','Nípa ìró ìkọ̀kọ̀ ńlá ni,');
INSERT INTO "aykyuni" VALUES(78,3,'78:3','èyí tí wọ́n ń yapa-ẹnu lórí rẹ̀.');
INSERT INTO "aykyuni" VALUES(78,4,'78:4','Rárá! Wọ́n ń bọ̀ wá mọ̀.');
INSERT INTO "aykyuni" VALUES(78,5,'78:5','Lẹ́yìn náà, rárá sẹ́, wọ́n ń bọ̀ wá mọ̀.');
INSERT INTO "aykyuni" VALUES(78,6,'78:6','Ǹjẹ́ Àwa kò ṣe ilẹ̀ ní ìtẹ́ bí,');
INSERT INTO "aykyuni" VALUES(78,7,'78:7','àti àwọn àpáta ní èèkàn (fún ilẹ̀)?');
INSERT INTO "aykyuni" VALUES(78,8,'78:8','A sì ṣẹ̀dá yín ní oríṣiríṣi (akọ àti abo).');
INSERT INTO "aykyuni" VALUES(78,9,'78:9','A ṣe oorun yín ní ìsinmi.');
INSERT INTO "aykyuni" VALUES(78,10,'78:10','A tún ṣe alẹ́ ní ìbora.');
INSERT INTO "aykyuni" VALUES(78,11,'78:11','A tún ṣe ọ̀sán ní (àsìkò fún) wíwá ìjẹ-ìmu.');
INSERT INTO "aykyuni" VALUES(78,12,'78:12','A tún mọ sánmọ̀ méje tó lágbára sókè yín.');
INSERT INTO "aykyuni" VALUES(78,13,'78:13','A tún ṣe òòrùn ní ìmọ́lẹ̀ tó ń tàn gbòlà.');
INSERT INTO "aykyuni" VALUES(78,14,'78:14','Àti pé A sọ omi tó ń bọ́ tẹ̀léra wọn kalẹ̀ láti inú àwọn ẹ̀ṣújò');
INSERT INTO "aykyuni" VALUES(78,15,'78:15','nítorí kí Á lè fi mú kóró èso àti irúgbìn jáde');
INSERT INTO "aykyuni" VALUES(78,16,'78:16','pẹ̀lú àwọn ọgbà tó kún digbí fún n̄ǹkan oko.');
INSERT INTO "aykyuni" VALUES(78,17,'78:17','Dájúdájú ọjọ́ ìpínyà, ó ní gbèdéke àkókò kan.');
INSERT INTO "aykyuni" VALUES(78,18,'78:18','(Ìyẹn ni) ọjọ́ tí wọ́n á fọn fèrè oníwo fún àjíǹde. Ẹ̀yin yó sì máa wá níjọ-níjọ.');
INSERT INTO "aykyuni" VALUES(78,19,'78:19','Wọ́n sì máa ṣí sánmọ̀ sílẹ̀. Ó sì máa di àwọn ìlẹ̀kùn.');
INSERT INTO "aykyuni" VALUES(78,20,'78:20','Wọ́n máa mú àwọn àpáta rìn (bọ́ sí àyè mìíràn). Ó sì máa di ahúnpeéná.');
INSERT INTO "aykyuni" VALUES(78,21,'78:21','Dájúdájú iná Jahanamọ, ó lúgọ sílẹ̀ lójú ọ̀nà.');
INSERT INTO "aykyuni" VALUES(78,22,'78:22','(Ó jẹ́) ibùgbé fún àwọn alákọyọ.');
INSERT INTO "aykyuni" VALUES(78,23,'78:23','Wọn yóò máa gbé inú rẹ̀ fún ìgbà gbọọrọ.');
INSERT INTO "aykyuni" VALUES(78,24,'78:24','Wọn kò níí rí ìtura tàbí ohun mímu kan tọ́wò nínú rẹ̀,');
INSERT INTO "aykyuni" VALUES(78,25,'78:25','àyàfi omi tó gbóná parí àti àwọnúwẹ̀jẹ̀.');
INSERT INTO "aykyuni" VALUES(78,26,'78:26','(Ó jẹ́) ẹ̀san tó ṣe wẹ́kú (iṣẹ́ wọn).');
INSERT INTO "aykyuni" VALUES(78,27,'78:27','Dájúdájú wọn kì í retí ìṣírò-iṣẹ́.');
INSERT INTO "aykyuni" VALUES(78,28,'78:28','Wọ́n sì pe àwọn āyah Wa ní irọ́ gan-an.');
INSERT INTO "aykyuni" VALUES(78,29,'78:29','Gbogbo n̄ǹkan sì ni A ti ṣe àkọsílẹ̀ rẹ̀ sínú Tírà kan.');
INSERT INTO "aykyuni" VALUES(78,30,'78:30','Nítorí náà, ẹ tọ́ (ìyà) wò. A ò sì níí ṣe àlékún kan fún yín bí kò ṣe ìyà.');
INSERT INTO "aykyuni" VALUES(78,31,'78:31','Dájúdájú ìgbàlà wà fún àwọn olùbẹ̀rù (Allāhu).');
INSERT INTO "aykyuni" VALUES(78,32,'78:32','Àwọn ọgbà oko àti èso àjàrà,');
INSERT INTO "aykyuni" VALUES(78,33,'78:33','àti àwọn ọlọ́mọge, tí wọn kò júra wọn lọ lọ́jọ́-orí,');
INSERT INTO "aykyuni" VALUES(78,34,'78:34','àti ife ọtí tó kún dẹnu (ni ẹ̀san wọn).');
INSERT INTO "aykyuni" VALUES(78,35,'78:35','Wọn kò níí gbọ́ ìsọkúsọ àti ọ̀rọ̀ irọ́ nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(78,36,'78:36','(Ó jẹ́) ẹ̀san láti ọ̀dọ̀ Olúwa rẹ, (ó sì jẹ́) ọrẹ ní ìbámu sí ìṣírò-iṣẹ́ (wọn).');
INSERT INTO "aykyuni" VALUES(78,37,'78:37','(Ẹsan náà wá láti ọ̀dọ̀) Olúwa àwọn sánmọ̀, ilẹ̀ àti ohun tó wà láààrin méjèèjì, Àjọkẹ́-ayé. Wọn kò sì ní ìkápá ọ̀rọ̀ lọ́dọ̀ Rẹ̀.');
INSERT INTO "aykyuni" VALUES(78,38,'78:38','Ọjọ́ tí mọlāika Jibrīl àti àwọn mọlāika (mìíràn) yóò dúró ní ọ̀wọ̀ọ̀wọ́. Wọn kò sì níí sọ̀rọ̀ àfi ẹni tí Àjọké-ayé bá yọ̀ǹda fún. Onítọ̀ún sì máa sọ̀rọ̀ tó máa ṣe wẹ́kú.');
INSERT INTO "aykyuni" VALUES(78,39,'78:39','Ìyẹn ni ọjọ́ òdodo. Nítorí náà, ẹni tí ó bá fẹ́ kí ó mú ọ̀nà tó máa gbà ṣẹ́rí padà sí ọ̀dọ̀ Olúwa rẹ̀ pọ̀n (ní ti ìronúpìwàdà).');
INSERT INTO "aykyuni" VALUES(78,40,'78:40','Dájúdájú A fi ìyà tó súnmọ́ ṣe ìkìlọ̀ fún yín. Ọjọ́ tí ènìyàn yóò máa wo ohun tí ó tì síwájú, aláìgbàgbọ́ yó sì wí pé: “Yéè, èmi ìbá sì jẹ́ erùpẹ̀, (èmi ìbá là nínú ìyà).”');
INSERT INTO "aykyuni" VALUES(79,1,'79:1','Allāhu fi àwọn mọlāika tó ń fi ọ̀nà èle gba ẹ̀mí àwọn aláìgbàgbọ́ búra.');
INSERT INTO "aykyuni" VALUES(79,2,'79:2','Ó tún fi àwọn mọlāika tó ń fi ọ̀nà ẹ̀rọ̀ gba ẹ̀mí àwọn onígbàgbọ́ òdodo búra.');
INSERT INTO "aykyuni" VALUES(79,3,'79:3','Ó tún fi àwọn mọlāika tó ń yára gágá níbi àṣẹ Rẹ̀ búra.');
INSERT INTO "aykyuni" VALUES(79,4,'79:4','Ó tún fi àwọn mọlāika tó máa ṣíwájú ẹ̀mí àwọn onígbàgbọ́ òdodo wọnú Ọgbà Ìdẹ̀ra tààrà búra.');
INSERT INTO "aykyuni" VALUES(79,5,'79:5','Ó tún fi àwọn mọlāika tó ń ṣètò nínú sánmọ̀ àti ilé ayé pẹ̀lú àṣẹ Allāhu búra.');
INSERT INTO "aykyuni" VALUES(79,6,'79:6','Ní ọjọ́ tí ìmìtìtì máa mi gbogbo ayé tìtì.1 ');
INSERT INTO "aykyuni" VALUES(79,7,'79:7','Ohun tí ó máa tẹ̀lé e máa ṣẹlẹ̀ tẹ̀lé e.1 ');
INSERT INTO "aykyuni" VALUES(79,8,'79:8','Àwọn ọkàn yóò máa gbọ̀n pẹ̀pẹ̀ ní ọjọ́ yẹn.');
INSERT INTO "aykyuni" VALUES(79,9,'79:9','Ojú wọn yó sì wálẹ̀ ní ti ìyẹpẹrẹ.');
INSERT INTO "aykyuni" VALUES(79,10,'79:10','Wọn yóò wí pé: “Ṣé Wọ́n tún máa dá wa padà sí ìbẹ̀rẹ̀ ìṣẹ̀mí (bíi tayé ni)?');
INSERT INTO "aykyuni" VALUES(79,11,'79:11','Ṣé nígbà tí a ti di eegun tó kẹfun tán?”');
INSERT INTO "aykyuni" VALUES(79,12,'79:12','Wọ́n wí pé: “Ìdápadà òfò nìyẹn nígbà náà (fún ẹni tó pè é ní irọ́.)”');
INSERT INTO "aykyuni" VALUES(79,13,'79:13','Nítorí náà, igbe ẹyọ kan sì ni.');
INSERT INTO "aykyuni" VALUES(79,14,'79:14','Nígbà náà ni wọn yóò bára wọn lórí ilẹ̀ gban̄sasa.');
INSERT INTO "aykyuni" VALUES(79,15,'79:15','Ǹjẹ́ ọ̀rọ̀ (Ànábì) Mūsā ti dé ọ̀dọ̀ rẹ?');
INSERT INTO "aykyuni" VALUES(79,16,'79:16','(Rántí) nígbà tí Olúwa rẹ̀ pè é ní àfonúfojì mímọ́, Tuwā.');
INSERT INTO "aykyuni" VALUES(79,17,'79:17','Lọ bá Fir‘aon, dájúdájú ó ti tayọ ẹnu-ààlà.');
INSERT INTO "aykyuni" VALUES(79,18,'79:18','Kí o sì sọ pé: “Ǹjẹ o máa ṣàfọ̀mọ́ ara rẹ (kúrò nínú àìgbàgbọ́) bí?');
INSERT INTO "aykyuni" VALUES(79,19,'79:19','Kí èmi sì fi ọ̀nà mọ̀ ọ́ dé ọ̀dọ̀ Olúwa rẹ, kí o sì páyà (Rẹ̀).”');
INSERT INTO "aykyuni" VALUES(79,20,'79:20','Ó sì fi àmì tó tóbi hàn án.');
INSERT INTO "aykyuni" VALUES(79,21,'79:21','(Àmọ́) ó pè é ní òpùrọ́. Ó sì yapa (rẹ̀).');
INSERT INTO "aykyuni" VALUES(79,22,'79:22','Lẹ́yìn náà, ó kẹ̀yìn sí i. Ó sì ń ṣiṣẹ́ (takò ó).');
INSERT INTO "aykyuni" VALUES(79,23,'79:23','Ó kó (àwọn ènìyàn) jọ, ó sì ké gbàjarì.');
INSERT INTO "aykyuni" VALUES(79,24,'79:24','Ó sì wí pé: “Èmi ni olúwa yín, ẹni gíga jùlọ.”');
INSERT INTO "aykyuni" VALUES(79,25,'79:25','Nítorí náà, Allāhu gbá a mú pẹ̀lú ìyà ìkẹ́yìn àti àkọ́kọ́ (nípa ọ̀rọ̀ ẹnu rẹ̀ ìkẹ́yìn yìí àti àkọ́kọ́).1 ');
INSERT INTO "aykyuni" VALUES(79,26,'79:26','Dájúdájú àríwòye wà nínú ìyẹn fún ẹni tó ń páyà (Allāhu).');
INSERT INTO "aykyuni" VALUES(79,27,'79:27','Ṣé ẹ̀yin lẹ lágbára jùlọ ní ìṣẹ̀dá ni tàbí sánmọ̀ tí Allāhu mọ?');
INSERT INTO "aykyuni" VALUES(79,28,'79:28','Allāhu gbé àjà rẹ̀ ga sókè. Ó sì ṣe é ní pípé tó gún régé.');
INSERT INTO "aykyuni" VALUES(79,29,'79:29','Ó ṣe òru rẹ̀ ní dúdú. Ó sì fa ìyálẹ̀ta rẹ̀ yọ jáde.');
INSERT INTO "aykyuni" VALUES(79,30,'79:30','Àti ilẹ̀, Ó tẹ́ ẹ pẹrẹsẹ lẹ́yìn ìyẹn.1 ');
INSERT INTO "aykyuni" VALUES(79,31,'79:31','Ó mú omi rẹ̀ àti irúgbìn rẹ̀ jáde láti inú rẹ̀.');
INSERT INTO "aykyuni" VALUES(79,32,'79:32','Àti àwọn àpáta, Ó fi ìdí wọn múlẹ̀ ṣinṣin.');
INSERT INTO "aykyuni" VALUES(79,33,'79:33','Ìgbádùn ni fún yín àti fún àwọn ẹran-ọ̀sìn yín.');
INSERT INTO "aykyuni" VALUES(79,34,'79:34','Ṣùgbọ́n nígbà tí ìparun ńlá bá dé,');
INSERT INTO "aykyuni" VALUES(79,35,'79:35','ní ọjọ́ tí ènìyàn yóò rántí ohun tó ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(79,36,'79:36','Wọ́n sì máa fi Iná hàn kedere fún (gbogbo) ẹni tó ríran.');
INSERT INTO "aykyuni" VALUES(79,37,'79:37','Nítorí náà, ní ti ẹni tó tayọ ẹnu-ààlà,');
INSERT INTO "aykyuni" VALUES(79,38,'79:38','tí ó tún gbé àjùlọ fún ìṣẹ̀mí ayé,');
INSERT INTO "aykyuni" VALUES(79,39,'79:39','dájúdájú iná Jẹhīm, òhun ni ibùgbé (rẹ̀).');
INSERT INTO "aykyuni" VALUES(79,40,'79:40','Ní ti ẹni tí ó páyà ìdúró níwájú Olúwa rẹ̀, tí ó tún kọ ìfẹ́-inú fún ẹ̀mí (ara rẹ̀),');
INSERT INTO "aykyuni" VALUES(79,41,'79:41','dájúdájú Ọgbà Ìdẹ̀ra, òhun ni ibùgbé (rẹ̀).');
INSERT INTO "aykyuni" VALUES(79,42,'79:42','Wọ́n ń bi ọ́ léèrè nípa Àkókò náà pé: “Ìgbà wo l’ó máa ṣẹlẹ̀?”');
INSERT INTO "aykyuni" VALUES(79,43,'79:43','Níbo ni ìwọ wà sí ìmọ̀ rẹ̀ ná?1 ');
INSERT INTO "aykyuni" VALUES(79,44,'79:44','Ọ̀dọ̀ Olúwa rẹ ni òpin ìmọ̀ nípa rẹ̀ wà.');
INSERT INTO "aykyuni" VALUES(79,45,'79:45','Ìwọ kúkú ni olùkìlọ̀ fún ẹni tó ń páyà rẹ̀.');
INSERT INTO "aykyuni" VALUES(79,46,'79:46','Ní ọjọ́ tí wọ́n máa rí i, wọn máa dà bí ẹni pé wọn kò gbé ilé ayé tayọ ìrọ̀lẹ́ tàbí ìyálẹ̀ta (ọjọ́) kan.');
INSERT INTO "aykyuni" VALUES(80,1,'80:1','Ó fajú ro, ó sì pẹ̀yìndà1 ');
INSERT INTO "aykyuni" VALUES(80,2,'80:2','nítorí pé afọ́jú wá bá a.');
INSERT INTO "aykyuni" VALUES(80,3,'80:3','Kí sì l’ó máa fi mọ̀ ọ́ pé ó ṣeé ṣe kí ó ṣàfọ̀mọ́ (ara rẹ̀ kúrò nínú àìgbàgbọ́)');
INSERT INTO "aykyuni" VALUES(80,4,'80:4','tàbí kí ó gbọ́ ìrántí, kí ìrántí náà sì ṣe é ní àǹfààní?');
INSERT INTO "aykyuni" VALUES(80,5,'80:5','Ní ti ẹni tí ó ka ara rẹ̀ kún ọlọ́rọ̀,');
INSERT INTO "aykyuni" VALUES(80,6,'80:6','òun ni ìwọ tẹ́tí sí.');
INSERT INTO "aykyuni" VALUES(80,7,'80:7','Kò sì sí ẹ̀ṣẹ̀ fún ọ bí kò bá ṣe àfọ̀mọ́ (ara rẹ̀ kúrò nínú àìgbàgbọ́).');
INSERT INTO "aykyuni" VALUES(80,8,'80:8','Ní ti ẹni tí ó sì wá bá ọ, tó ń yára gágá (sí ìrántí, ìyẹn afọ́jú),');
INSERT INTO "aykyuni" VALUES(80,9,'80:9','tí ó sì ń páyà (Allāhu),');
INSERT INTO "aykyuni" VALUES(80,10,'80:10','ìwọ kò sì kọbi ara sí i.');
INSERT INTO "aykyuni" VALUES(80,11,'80:11','Rárá (kò tọ́ bẹ́ẹ̀). Dájúdájú al-Ƙur’ān ni ìrántí.');
INSERT INTO "aykyuni" VALUES(80,12,'80:12','Nítorí náà, ẹni tí ó bá fẹ́ kí ó rántí rẹ̀.');
INSERT INTO "aykyuni" VALUES(80,13,'80:13','(Al-Ƙur’ān) wà nínú àwọn tàkádà alápọ̀n-ọ́nlé,');
INSERT INTO "aykyuni" VALUES(80,14,'80:14','A gbé e ga, A sì ṣe é ní mímọ́');
INSERT INTO "aykyuni" VALUES(80,15,'80:15','ní ọwọ́ àwọn òǹkọtíà (ìyẹn, àwọn mọlāika),');
INSERT INTO "aykyuni" VALUES(80,16,'80:16','àwọn alápọ̀n-ọ́nlé, àwọn ẹni rere.');
INSERT INTO "aykyuni" VALUES(80,17,'80:17','Wọ́n ti fi ènìyàn gégùn-ún (nípa) bí ó ṣe jẹ́ aláìmoore jùlọ!');
INSERT INTO "aykyuni" VALUES(80,18,'80:18','Kí sì ni Allāhu fi ṣẹ̀dá rẹ̀?');
INSERT INTO "aykyuni" VALUES(80,19,'80:19','Nínú àtọ̀ l’Ó ti ṣẹ̀dá rẹ̀. Ó sì pèbùbù (ẹ̀yà-ara) rẹ̀.');
INSERT INTO "aykyuni" VALUES(80,20,'80:20','Lẹ́yìn náà, Ó ṣe ọ̀nà àtiwáyé ní ìrọ̀rùn fún un.');
INSERT INTO "aykyuni" VALUES(80,21,'80:21','Lẹ́yìn náà, Ó máa pa á. Ó sì máa fi sínú sàréè.');
INSERT INTO "aykyuni" VALUES(80,22,'80:22','Lẹ́yìn náà, nígbà tí Allāhu bá fẹ́, Ó máa gbé e dìde.');
INSERT INTO "aykyuni" VALUES(80,23,'80:23','Rárá o! Ènìyàn kò tí ì ṣe n̄ǹkan tí Allāhu pa láṣẹ fún un.');
INSERT INTO "aykyuni" VALUES(80,24,'80:24','Nítorí náà, kí ènìyàn wòye sí oúnjẹ rẹ̀.');
INSERT INTO "aykyuni" VALUES(80,25,'80:25','Dájúdájú Àwa ń rọ òjò ní púpọ̀.');
INSERT INTO "aykyuni" VALUES(80,26,'80:26','Lẹ́yìn náà, A mú ilẹ̀ sán kànkàn.');
INSERT INTO "aykyuni" VALUES(80,27,'80:27','A sì mú kóró èso hù jáde láti inú rẹ̀;');
INSERT INTO "aykyuni" VALUES(80,28,'80:28','àti èso àjàrà àti kànnáfùrù,');
INSERT INTO "aykyuni" VALUES(80,29,'80:29','àti igi òróró Zaetūn àti dàbínù,');
INSERT INTO "aykyuni" VALUES(80,30,'80:30','àti àwọn ọgbà tó kún fún igi,');
INSERT INTO "aykyuni" VALUES(80,31,'80:31','àti àwọn èso (mìíràn) pẹ̀lú ewé tí ẹranko ń jẹ, (A mú wọn hù jáde).');
INSERT INTO "aykyuni" VALUES(80,32,'80:32','(Wọ́n jẹ́ n̄ǹkan) ìgbádùn fún ẹ̀yin àti àwọn ẹran-ọ̀sìn yín.');
INSERT INTO "aykyuni" VALUES(80,33,'80:33','Nígbà tí fífọn sínú ìwo nígbà kejì bá ṣẹlẹ̀,');
INSERT INTO "aykyuni" VALUES(80,34,'80:34','ní ọjọ́ tí ènìyàn yóò sá fún arákùnrin rẹ̀,');
INSERT INTO "aykyuni" VALUES(80,35,'80:35','àti ìyá rẹ̀ àti bàbá rẹ̀,');
INSERT INTO "aykyuni" VALUES(80,36,'80:36','àti ìyàwó rẹ̀ àti àwọn ọmọ rẹ̀.');
INSERT INTO "aykyuni" VALUES(80,37,'80:37','Ẹnì kọ̀ọ̀kan wọn ní ọjọ́ yẹn l’ó ti ní ọ̀ràn tó máa tó o ó rán.');
INSERT INTO "aykyuni" VALUES(80,38,'80:38','Àwọn ojú kan lọ́jọ́ yẹn yóò mọ́lẹ̀.');
INSERT INTO "aykyuni" VALUES(80,39,'80:39','Wọn yó máa rẹ́rìn-ín, wọn yó sì máa dunnú.');
INSERT INTO "aykyuni" VALUES(80,40,'80:40','Àwọn ojú kan lọ́jọ́ yẹn ni eruku yó sì bò mọ́lẹ̀.');
INSERT INTO "aykyuni" VALUES(80,41,'80:41','Òkùnkùn yó sì bò wọ́n mọ́lẹ̀.');
INSERT INTO "aykyuni" VALUES(80,42,'80:42','Àwọn wọ̀nyẹn gan-an ni aláìgbàgbọ́, ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(81,1,'81:1','Nígbà tí wọ́n bá ká òòrùn kóróbójó dànù,');
INSERT INTO "aykyuni" VALUES(81,2,'81:2','àti nígbà tí àwọn ìràwọ̀ bá já bọ́ káàkiri,');
INSERT INTO "aykyuni" VALUES(81,3,'81:3','àti nígbà tí wọ́n bá mú àwọn àpáta rìn (kúrò ní àyè wọn, tí wọ́n kù wọ́n dànù),');
INSERT INTO "aykyuni" VALUES(81,4,'81:4','àti nígbà tí wọ́n bá pa àwọn ràkúnmí aboyún tì,');
INSERT INTO "aykyuni" VALUES(81,5,'81:5','àti nígbà tí wọ́n bá ko àwọn ẹranko jọ,');
INSERT INTO "aykyuni" VALUES(81,6,'81:6','àti nígbà tí wọ́n bá mú àwọn agbami odò gbiná,');
INSERT INTO "aykyuni" VALUES(81,7,'81:7','àti nígbà tí wọ́n bá so àwọn ẹ̀mí pọ̀ mọ́ra wọn, (ẹni rere pẹ̀lú ẹni rere, ẹni ibi pẹ̀lú ẹni ibi)');
INSERT INTO "aykyuni" VALUES(81,8,'81:8','àti nígbà tí ọmọbìnrin tí wọ́n bò mọ́lẹ̀ láàyè bá bèèrè pé');
INSERT INTO "aykyuni" VALUES(81,9,'81:9','ẹ̀ṣẹ̀ wo ni wọ́n pa òun fún,');
INSERT INTO "aykyuni" VALUES(81,10,'81:10','àti nígbà tí wọ́n bá ṣí àwọn tákàdá (iṣẹ́ ẹ̀dá) sílẹ̀,');
INSERT INTO "aykyuni" VALUES(81,11,'81:11','àti nígbà tí wọ́n bá ká sánmọ̀ kúrò lókè,');
INSERT INTO "aykyuni" VALUES(81,12,'81:12','àti nígbà tí wọ́n bá mú iná Jẹhīm jò fòfò,');
INSERT INTO "aykyuni" VALUES(81,13,'81:13','àti nígbà tí wọ́n bá sún Ọgbà Ìdẹ̀ra mọ́ (àwọn onígbàgbọ́ òdodo),');
INSERT INTO "aykyuni" VALUES(81,14,'81:14','(nígbà náà ni) ẹ̀mí (kọ̀ọ̀kan) yóò mọ ohun tí ó mú wá (nínú iṣẹ́ ire àti iṣẹ́ ibi).');
INSERT INTO "aykyuni" VALUES(81,15,'81:15','Nítorí náà, Èmi (Allāhu) ń fi àwọn ìràwọ̀ tó ń yọ ní alẹ́, tó ń wọ̀ọ̀kùn ní ọ̀sán búra,');
INSERT INTO "aykyuni" VALUES(81,16,'81:16','(ìyẹn) àwọn ìràwọ̀ tó ń rìn lọ rìn bọ̀, tó ń wọ̀ọ̀kùn sí ibùwọ̀ wọn.');
INSERT INTO "aykyuni" VALUES(81,17,'81:17','Mo tún fi alẹ́ nígbà tí ó bá lọ búra.1 ');
INSERT INTO "aykyuni" VALUES(81,18,'81:18','Mo tún fi òwúrọ̀ nígbà tí ó bá mọ́lẹ̀ búra.');
INSERT INTO "aykyuni" VALUES(81,19,'81:19','Dájúdájú (al-Ƙur’ān) ni ọ̀rọ̀ (tí A fi rán Jibrīl) Òjíṣẹ́, alápọ̀n-ọ́nlé,1 ');
INSERT INTO "aykyuni" VALUES(81,20,'81:20','alágbára, ẹni pàtàkì ní ọ̀dọ̀ Ẹni t’Ó ni Ìtẹ́-ọlá,');
INSERT INTO "aykyuni" VALUES(81,21,'81:21','ẹni tí wọ́n ń tẹ̀lé àṣẹ rẹ̀ níbẹ̀ yẹn (nínú sánmọ̀), olùfọkàntán.');
INSERT INTO "aykyuni" VALUES(81,22,'81:22','Àti pé ẹni yín (Ànábì Muhammad - kí ìkẹ́ àti ọlà Allāhu máa bá a -) kì í ṣe wèrè.');
INSERT INTO "aykyuni" VALUES(81,23,'81:23','Ó kúkú rí i nínú òfurufú kedere.1 ');
INSERT INTO "aykyuni" VALUES(81,24,'81:24','Àti pé kì í ṣe ahun (tàbí ẹni-afurasí) lórí ìró ìkọ̀kọ̀ (tí A fi ránṣẹ́ sí i).');
INSERT INTO "aykyuni" VALUES(81,25,'81:25','Al-Ƙur’ān kì í sì ṣe ọ̀rọ̀ (tí A fi rán) Èṣù, ẹni ẹ̀kọ̀.');
INSERT INTO "aykyuni" VALUES(81,26,'81:26','Nítorí náà, ibo l’ẹ̀ ń lọ?');
INSERT INTO "aykyuni" VALUES(81,27,'81:27','Kí sì ni al-Ƙur’ān bí kò ṣe ìrántí fún gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(81,28,'81:28','(Ó wà) fún ẹni tí ó bá fẹ́ nínú yín láti dúró déédé.');
INSERT INTO "aykyuni" VALUES(81,29,'81:29','Ẹ̀yin kò sì níí fẹ́ (láti dúró déédé) àyàfi tí Allāhu bá fẹ́, Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(82,1,'82:1','Nígbà tí sánmọ̀ bá fàya pẹ́rẹpẹ̀rẹ,1 ');
INSERT INTO "aykyuni" VALUES(82,2,'82:2','àti nígbà tí àwọn ìràwọ̀ bá já bọ́ káàkiri,');
INSERT INTO "aykyuni" VALUES(82,3,'82:3','àti nígbà tí àwọn ibúdò bá ṣàn jára wọn,');
INSERT INTO "aykyuni" VALUES(82,4,'82:4','àti (nígbà tí A bá ta ilẹ̀ sókè), tí A sì mú àwọn òkú jáde (láàyè) láti inú àwọn sàréè,');
INSERT INTO "aykyuni" VALUES(82,5,'82:5','(nígbà náà ni) ẹ̀mí kọ̀ọ̀kan yóò mọ ohun tí ó tì síwájú (nínú iṣẹ́ rẹ̀) àti ohun tí ó fi sẹ́yìn (nínú orípa iṣẹ́ rẹ̀).');
INSERT INTO "aykyuni" VALUES(82,6,'82:6','Ìwọ ènìyàn, kí ni ó tàn ọ́ jẹ nípa Olúwa rẹ, Alápọ̀n-ọ́nlé,');
INSERT INTO "aykyuni" VALUES(82,7,'82:7','Ẹni t’Ó ṣẹ̀dá rẹ? Ó ṣẹ̀dá rẹ̀ ní pípé. Ó sì mú ọ dọ́gba jalẹ̀ léèyàn.');
INSERT INTO "aykyuni" VALUES(82,8,'82:8','Ó to ẹ̀yà ara rẹ papọ̀ sínú èyíkéyìí àwòrán tí Ó fẹ́.');
INSERT INTO "aykyuni" VALUES(82,9,'82:9','Rárá! Bí kò ṣe pé ẹ̀ ń pe Ọjọ́ ẹ̀san ní irọ́.');
INSERT INTO "aykyuni" VALUES(82,10,'82:10','Dájúdájú àwọn ẹ̀ṣọ́ sì wà ní ọ̀dọ̀ yín.');
INSERT INTO "aykyuni" VALUES(82,11,'82:11','(Wọ́n jẹ́) alápọ̀n-ọ́nlé, òǹkọ̀wé-iṣẹ́ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(82,12,'82:12','Wọ́n mọ ohun tí ẹ̀ ń ṣe níṣẹ́.');
INSERT INTO "aykyuni" VALUES(82,13,'82:13','Dájúdájú àwọn ẹni rere yóò kúkú wà nínú ìgbádùn.');
INSERT INTO "aykyuni" VALUES(82,14,'82:14','Dájúdájú àwọn ẹni ibi yó sì kúkú wà nínú iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(82,15,'82:15','Wọn yóò wọ inú rẹ̀ ní Ọjọ́ ẹ̀san.');
INSERT INTO "aykyuni" VALUES(82,16,'82:16','Wọn kò sì níí kúrò nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(82,17,'82:17','Kí sì ni ó mú ọ mọ ohun tó ń jẹ́ Ọjọ́ ẹ̀san?');
INSERT INTO "aykyuni" VALUES(82,18,'82:18','Lẹ́yìn náà, kí ni ó mú ọ mọ ohun tó ń jẹ́ Ọjọ́ ẹ̀san?');
INSERT INTO "aykyuni" VALUES(82,19,'82:19','(Ọjọ́ ẹ̀san ni) ọjọ́ tí ẹ̀mí kan kò níí kápá kiní kan fún ẹ̀mí kan. Gbogbo àṣẹ ọjọ́ yẹn sì ń jẹ́ ti Allāhu.');
INSERT INTO "aykyuni" VALUES(83,1,'83:1','Ègbé ni fún àwọn olùdín-òṣùwọ̀n-kù,');
INSERT INTO "aykyuni" VALUES(83,2,'83:2','àwọn (òǹtajà) tó jẹ́ pé nígbà tí wọ́n bá wọn n̄ǹkan lọ́dọ̀ àwọn ènìyàn,wọ́n á gbà á ní ẹ̀kún, ');
INSERT INTO "aykyuni" VALUES(83,3,'83:3','nígbà tí àwọn (òǹtajà náà) bá sì lo ìwọ̀n fún àwọn (òǹrajà) tàbí lo òṣùwọ̀n fún wọn, wọn yóò dín in kù.');
INSERT INTO "aykyuni" VALUES(83,4,'83:4','Ṣé àwọn wọ̀nyẹn kò mọ̀ dájú pé dájúdájú A máa gbé wọn dìde');
INSERT INTO "aykyuni" VALUES(83,5,'83:5','ní Ọjọ́ ńlá kan?');
INSERT INTO "aykyuni" VALUES(83,6,'83:6','Ní ọjọ́ tí àwọn ènìyàn yóò dìde dúró fún Olúwa gbogbo ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(83,7,'83:7','Ní ti òdodo dájúdájú ìwé iṣẹ́ àwọn ẹni burúkú (tó ń dín òṣùwọ̀n kù) kúkú máa wà nínú Sijjīn.');
INSERT INTO "aykyuni" VALUES(83,8,'83:8','Kí sì l’ó mú ọ mọ ohun tó ń jẹ́ Sijjīn!');
INSERT INTO "aykyuni" VALUES(83,9,'83:9','Ìwé tí wọ́n ti kọ iṣẹ́ aburú ẹ̀dá sínú rẹ̀ (tí wọ́n sì fi pamọ́ sínú ilẹ̀ keje ni Sijjīn).');
INSERT INTO "aykyuni" VALUES(83,10,'83:10','Ní ọjọ́ yẹn, ègbé ni fún àwọn olùpe-òdodo- nírọ́,');
INSERT INTO "aykyuni" VALUES(83,11,'83:11','àwọn tó ń pe Ọjọ́ ẹ̀san ní irọ́.');
INSERT INTO "aykyuni" VALUES(83,12,'83:12','Kò sì sí ẹni tó ń pè é ní irọ́ àyàfi gbogbo alákọyọ, ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(83,13,'83:13','Nígbà tí wọ́n bá ń ké àwọn āyah Wa fún un, ó máa wí pé: “Àwọn àkọsílẹ̀ àlọ́ àwọn ẹni àkọ́kọ́ (nìwọ̀nyí).”');
INSERT INTO "aykyuni" VALUES(83,14,'83:14','Rárá (al-Ƙur’ān kì í ṣe àkọsílẹ̀ àlọ́ àwọn ẹni àkọ́kọ́) bí kò ṣe pé ohun tí wọ́n ń ṣe níṣẹ́ aburú l’ó jọba lórí ọkàn wọn.');
INSERT INTO "aykyuni" VALUES(83,15,'83:15','Rárá (kò rí bí wọ́n ṣe rò ó sí sẹ́), dájúdájú wọn yóò wà nínú gàgá, wọn kò sì níí rí Olúwa wọn ní ọjọ́ yẹn.');
INSERT INTO "aykyuni" VALUES(83,16,'83:16','Lẹ́yìn náà, dájúdájú wọn yóò wọ inú iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(83,17,'83:17','Lẹ́yìn náà, wọ́n máa sọ (fún wọn) pe: “Èyí ni ohun tí ẹ̀ ń pè ní irọ́.”');
INSERT INTO "aykyuni" VALUES(83,18,'83:18','Ní ti òdodo, dájúdájú ìwé iṣẹ́ àwọn ẹni rere wà nínú ‘illiyyūn.');
INSERT INTO "aykyuni" VALUES(83,19,'83:19','Kí sì l’ó mú ọ mọ ohun tó ń jẹ́ ‘illiyyūn!');
INSERT INTO "aykyuni" VALUES(83,20,'83:20','Ìwé tí wọ́n ti kọ iṣẹ́ rere ẹ̀dá sínú rẹ̀ (tí wọ́n sì fi pamọ́ sí òkè sánmọ̀ ni ‘illiyyūn).');
INSERT INTO "aykyuni" VALUES(83,21,'83:21','Àwọn (mọlāika) tí wọ́n súnmọ́ Allāhu ń jẹ́rìí sí i.');
INSERT INTO "aykyuni" VALUES(83,22,'83:22','Dájúdájú àwọn ẹni rere máa wà nínú ìgbádùn.');
INSERT INTO "aykyuni" VALUES(83,23,'83:23','Wọn yóò máa wòran lórí àwọn ibùsùn.');
INSERT INTO "aykyuni" VALUES(83,24,'83:24','Ìwọ yóò dá ìtutù ojú ìgbádùn mọ̀ nínú ojú wọn.');
INSERT INTO "aykyuni" VALUES(83,25,'83:25','Wọn yóò máa fún wọn mu nínú ọtí onídèérí.');
INSERT INTO "aykyuni" VALUES(83,26,'83:26','Àlùmísìkí ni (òórùn) ìparí rẹ̀. Kí àwọn alápàáǹtètè ṣàpáǹtètè nínú ìyẹn.');
INSERT INTO "aykyuni" VALUES(83,27,'83:27','Tẹsnīm ni wọ́n yóò máa pòpọ̀ (mọ́ ọtí náà).');
INSERT INTO "aykyuni" VALUES(83,28,'83:28','(Tẹsnīm ni) omi ìṣẹ́lẹ̀rú kan, tí àwọn olùsúnmọ́ (Allāhu) yóò máa mu.');
INSERT INTO "aykyuni" VALUES(83,29,'83:29','Dájúdájú àwọn tó dẹ́ṣẹ̀, wọ́n máa ń fi àwọn tó gbàgbọ́ ní òdodo rẹ́rìn-ín.');
INSERT INTO "aykyuni" VALUES(83,30,'83:30','Nígbà tí wọ́n bá gba ẹ̀gbẹ́ wọn kọjá, (àwọn olùdẹ́ṣẹ̀) yóò máa ṣẹ́jú síra wọn ní ti yẹ̀yẹ́.');
INSERT INTO "aykyuni" VALUES(83,31,'83:31','Nígbà tí wọ́n bá sì padà sí ọ̀dọ̀ àwọn ènìyàn wọn, wọ́n á padà tí wọn yóò máa ṣe fáàrí.');
INSERT INTO "aykyuni" VALUES(83,32,'83:32','Àti pé nígbà tí wọ́n bá rí (àwọn onígbàgbọ́ òdodo) wọ́n á wí pé: “Dájúdájú àwọn wọ̀nyí ni olùṣìnà.”');
INSERT INTO "aykyuni" VALUES(83,33,'83:33','Àwa kò sì rán wọn ní iṣẹ́ olùṣọ́ sí wọn.');
INSERT INTO "aykyuni" VALUES(83,34,'83:34','Nítorí náà, ní òní àwọn tó gbàgbọ́ ní òdodo yóò fi àwọn aláìgbàgbọ́ rẹ́rìn-ín.');
INSERT INTO "aykyuni" VALUES(83,35,'83:35','Wọn yóò máa wòran lórí àwọn ibùsùn.');
INSERT INTO "aykyuni" VALUES(83,36,'83:36','Ṣebí wọ́n ti fi ohun tí àwọn aláìgbàgbọ́ ń ṣe níṣẹ́ san wọ́n lẹ́san (báyìí)?');
INSERT INTO "aykyuni" VALUES(84,1,'84:1','Nígbà tí sánmọ̀ bá fàya pẹ́rẹpẹ̀rẹ');
INSERT INTO "aykyuni" VALUES(84,2,'84:2','- ó gbọ́, ó sì tẹ̀lé àṣẹ Olúwa rẹ̀ ni. Ó sì di dandan fún un láti ṣe bẹ́ẹ̀- ');
INSERT INTO "aykyuni" VALUES(84,3,'84:3','àti nígbà tí A bá fẹ ilẹ̀ lójú,');
INSERT INTO "aykyuni" VALUES(84,4,'84:4','ó máa ju ohun tí ó wà nínú rẹ̀ síta, ó sì máa pa sófo');
INSERT INTO "aykyuni" VALUES(84,5,'84:5','- ó gbọ́, ó sì tẹ̀lé àṣẹ Olúwa rẹ̀ ni. Ó sì di dandan fún un láti ṣe bẹ́ẹ̀ - (ní ọjọ́ yẹn ni ẹ̀dá máa rí iṣẹ́ ọwọ́ rẹ̀)');
INSERT INTO "aykyuni" VALUES(84,6,'84:6','Ìwọ ènìyàn dájúdájú ìwọ ń ṣe iṣẹ́ kárakára ní iṣẹ́ àṣepàdé Olúwa rẹ. O sì máa mú un pàdé Rẹ̀.');
INSERT INTO "aykyuni" VALUES(84,7,'84:7','Nítorí náà, ní ti ẹni tí wọ́n bá fún ní ìwé iṣẹ́ rẹ̀ ní ọwọ́ ọ̀tún rẹ̀,');
INSERT INTO "aykyuni" VALUES(84,8,'84:8','láìpẹ́ A máa ṣe ìṣírò-iṣẹ́ (rẹ̀) ní ìṣírò ìrọ̀rùn.');
INSERT INTO "aykyuni" VALUES(84,9,'84:9','Ó sì máa padà sí ọ̀dọ̀ àwọn ènìyàn rẹ̀ (nínú Ọgbà Ìdẹ̀ra) lẹ́ni ìdùnnú.');
INSERT INTO "aykyuni" VALUES(84,10,'84:10','Ní ti ẹni tí wọ́n bá sì fún ní ìwé iṣẹ́ rẹ̀ láti ẹ̀yìn rẹ̀,1 ');
INSERT INTO "aykyuni" VALUES(84,11,'84:11','láìpẹ́ ó máa kígbe ìparun.');
INSERT INTO "aykyuni" VALUES(84,12,'84:12','Ó sì máa wọ inú Iná tó ń jó.');
INSERT INTO "aykyuni" VALUES(84,13,'84:13','Dájúdájú ó ti wà láààrin àwọn ènìyàn rẹ̀ lẹ́ni ìdùnnú (sí àìgbàgbọ́ nílé ayé).');
INSERT INTO "aykyuni" VALUES(84,14,'84:14','Dájúdájú ó lérò pé òun kò níí padà (sí ọ̀dọ̀ Allāhu ni).');
INSERT INTO "aykyuni" VALUES(84,15,'84:15','Bẹ́ẹ̀ ni, (ó máa padà). Dájúdájú Olúwa rẹ̀ jẹ́ Olùríran nípa rẹ̀.');
INSERT INTO "aykyuni" VALUES(84,16,'84:16','Nítorí náà, Èmi (Allāhu) ń fi àwòǹpapa búra.');
INSERT INTO "aykyuni" VALUES(84,17,'84:17','Mo tún ń fi òru àti ohun tí ó kó jọ sínú rẹ̀ búra.');
INSERT INTO "aykyuni" VALUES(84,18,'84:18','Mo tún ń fi òṣùpá nígbà tí ó bá dégbá (tí ìmọ́lẹ̀ rẹ̀ kún) búra.');
INSERT INTO "aykyuni" VALUES(84,19,'84:19','Dájúdájú ẹ̀yin yóò máa bọ́ sínú wàhálà kan láti inú wàhálà kan.');
INSERT INTO "aykyuni" VALUES(84,20,'84:20','Kí l’ó ṣe wọ́n ná tí wọn kò fi gbàgbọ́ ní òdodo?');
INSERT INTO "aykyuni" VALUES(84,21,'84:21','Nígbà tí wọ́n bá sì ké al-Ƙur’ān fún wọn, wọn kò níí forí kanlẹ̀.');
INSERT INTO "aykyuni" VALUES(84,22,'84:22','Rárá, ńṣe ni àwọn tó ṣàì gbàgbọ́ ń pè é ní irọ́.');
INSERT INTO "aykyuni" VALUES(84,23,'84:23','Allāhu sì nímọ̀ jùlọ nípa ohun tí wọ́n ń fi pamọ́ (sínú ọkàn wọn).');
INSERT INTO "aykyuni" VALUES(84,24,'84:24','Nítorí náà, fún wọn ní ìró ìyà ẹlẹ́ta-eléro.');
INSERT INTO "aykyuni" VALUES(84,25,'84:25','Àyàfi àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, ẹ̀san tí kò níí pinń bẹ fún wọn. ');
INSERT INTO "aykyuni" VALUES(85,1,'85:1','(Allāhu) fi sánmọ̀ tí àwọn ibùsọ̀ (òòrùn, òṣùpá àti àwọn ìràwọ̀) wà nínú rẹ̀ búra.');
INSERT INTO "aykyuni" VALUES(85,2,'85:2','Ó tún fi ọjọ́ tí wọ́n ṣe ní àdéhùn (ìyẹn Ọjọ́ Àjíǹde) búra.');
INSERT INTO "aykyuni" VALUES(85,3,'85:3','Ó tún fi olùjẹ́rìí àti ohun tó jẹ́rìí sí búra.');
INSERT INTO "aykyuni" VALUES(85,4,'85:4','A ṣẹ́bi lé àwọn tó gbẹ́ kòtò (iná),');
INSERT INTO "aykyuni" VALUES(85,5,'85:5','(ìyẹn) iná tí wọ́n ń fi igi kò (nínú kòtò).');
INSERT INTO "aykyuni" VALUES(85,6,'85:6','(Rántí) nígbà tí wọ́n jókòó sí ìtòsí rẹ̀.');
INSERT INTO "aykyuni" VALUES(85,7,'85:7','Wọ́n sì ń wo ohun tí wọ́n ń ṣe fún àwọn onígbàgbọ́ òdodo.');
INSERT INTO "aykyuni" VALUES(85,8,'85:8','Wọn kò rí àlèébù kan (wọn kò sì kórira kiní kan) lára wọn bí kò ṣe pé, wọ́n ní ìgbàgbọ́ òdodo nínú Allāhu, Alágbára, Ẹlẹ́yìn,');
INSERT INTO "aykyuni" VALUES(85,9,'85:9','Ẹni tí Ó ni ìjọba àwọn sánmọ̀ àti ilẹ̀. Allāhu sì ni Ẹlẹ́rìí lórí gbogbo n̄ǹkan.');
INSERT INTO "aykyuni" VALUES(85,10,'85:10','Dájúdájú àwọn tó fi ìnira kan àwọn onígbàgbọ́ òdodo lọ́kùnrin àti àwọn onígbàgbọ́ òdodo lóbìnrin, lẹ́yìn náà tí wọn kò ronú pìwàdà, ìyà iná Jahanamọ ń bẹ fún wọn. Ìyà iná tó ń jó sì wà fún wọn.');
INSERT INTO "aykyuni" VALUES(85,11,'85:11','Dájúdájú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere, tiwọn ni àwọn Ọgbà Ìdẹ̀ra kan, tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Ìyẹn sì ni èrèǹjẹ ńlá.');
INSERT INTO "aykyuni" VALUES(85,12,'85:12','Dájúdájú ìgbámú ti Olúwa rẹ mà le.');
INSERT INTO "aykyuni" VALUES(85,13,'85:13','Dájúdájú Òun ni Ó pìlẹ̀ (ẹ̀dá), Ó sì máa dá (ẹ̀dá) padà (fún àjíǹde).');
INSERT INTO "aykyuni" VALUES(85,14,'85:14','Òun ni Aláforíjìn, Olólùfẹ́ (ẹ̀dá),');
INSERT INTO "aykyuni" VALUES(85,15,'85:15','Òun l’Ó ni Ìtẹ́-ọlá, Ológo (Ẹni-ọ̀wọ̀ jùlọ),');
INSERT INTO "aykyuni" VALUES(85,16,'85:16','Olùṣe-ohun-t’Ó-bá-fẹ́.');
INSERT INTO "aykyuni" VALUES(85,17,'85:17','Ṣebí ìró àwọn ọmọ ogun ti dé ọ̀dọ̀ rẹ,');
INSERT INTO "aykyuni" VALUES(85,18,'85:18','(ọmọ ogun) Fir‘aon àti (ìjọ) Thamūd?');
INSERT INTO "aykyuni" VALUES(85,19,'85:19','Síbẹ̀síbẹ̀ àwọn tó ṣàì gbàgbọ́ sì ń pe òdodo ní irọ́.');
INSERT INTO "aykyuni" VALUES(85,20,'85:20','Allāhu sì yí wọn ká lẹ́yìn wọn.');
INSERT INTO "aykyuni" VALUES(85,21,'85:21','Àmọ́ sá, ohun (tí A fi ránṣẹ́ sí ọ ni) al-Ƙur’ān alápọ̀n-ọ́nlé,');
INSERT INTO "aykyuni" VALUES(85,22,'85:22','tí ó wà nínú wàláà tí wọ́n ń ṣọ́.');
INSERT INTO "aykyuni" VALUES(86,1,'86:1','(Allāhu) fi sánmọ̀ àti Tọ̄riƙ búra.');
INSERT INTO "aykyuni" VALUES(86,2,'86:2','Kí sì ni ó mú ọ mọ ohun tó ń jẹ́ Tọ̄riƙ?');
INSERT INTO "aykyuni" VALUES(86,3,'86:3','Ìràwọ̀ tí ìmọ́lẹ̀ rẹ̀ ń tàn ròrò (ní alẹ́ ni).');
INSERT INTO "aykyuni" VALUES(86,4,'86:4','Kò sí ẹ̀mí kan àfi kí ẹ̀ṣọ́ kan wà fún un (nínú àwọn mọlāika).');
INSERT INTO "aykyuni" VALUES(86,5,'86:5','Nítorí náà, kí ènìyàn wòye sí ohun tí A fi ṣẹ̀dá rẹ̀.');
INSERT INTO "aykyuni" VALUES(86,6,'86:6','Wọ́n ṣẹ̀dá rẹ̀ láti inú omi tó ń tú jáde kọ̀ọ́kọ̀ọ́.');
INSERT INTO "aykyuni" VALUES(86,7,'86:7','Ó ń jáde láti ààrin ìbàdí ọkùnrin àti àwọn ẹfọ́nhà igbá-àyà obìnrin.');
INSERT INTO "aykyuni" VALUES(86,8,'86:8','Dájúdájú Allāhu ni Alágbára lóri ìdápadà rẹ̀');
INSERT INTO "aykyuni" VALUES(86,9,'86:9','ní ọjọ́ tí wọ́n máa ṣe àyẹ̀wò àwọn (iṣẹ́) àṣepamọ́.');
INSERT INTO "aykyuni" VALUES(86,10,'86:10','Nígbà náà, kò níí sí agbára tàbí alárànṣe kan fún un.');
INSERT INTO "aykyuni" VALUES(86,11,'86:11','Allāhu fi sánmọ̀ tó ń rọ òjò ní ọdọọdún búra.');
INSERT INTO "aykyuni" VALUES(86,12,'86:12','Ó tún fi ilẹ̀ tó ń sán kànkàn (fún híhùjáde èso) búra.');
INSERT INTO "aykyuni" VALUES(86,13,'86:13','Dájúdájú al-Ƙur’ān ni ọ̀rọ̀-ìpínyà (láààrin òdodo àti irọ́).');
INSERT INTO "aykyuni" VALUES(86,14,'86:14','Kì í sì ṣe àwàdà.');
INSERT INTO "aykyuni" VALUES(86,15,'86:15','Dájúdájú wọ́n ń déte gan-an.');
INSERT INTO "aykyuni" VALUES(86,16,'86:16','Èmi náà sì ń déte gan-an.1 ');
INSERT INTO "aykyuni" VALUES(86,17,'86:17','Nítorí náà, lọ́ra fún àwọn aláìgbàgbọ́. Lọ́ wọn lára sẹ́ fún ìgbà díẹ̀.');
INSERT INTO "aykyuni" VALUES(87,1,'87:1','Ṣàfọ̀mọ́ fún orúkọ Olúwa rẹ, Ẹni gíga jùlọ.');
INSERT INTO "aykyuni" VALUES(87,2,'87:2','Ẹni tí Ó dá ẹ̀dá. Ó sì ṣe (oríkèé-ríkèé rẹ̀) ní dọ́gbadọ́gba.');
INSERT INTO "aykyuni" VALUES(87,3,'87:3','Ẹni tí Ó yan kádàrá (fún ẹ̀dá). Ó sì tọ́ ọ sọ́nà.1 ');
INSERT INTO "aykyuni" VALUES(87,4,'87:4','Ẹni tí Ó mú koríko tútù hù jáde,');
INSERT INTO "aykyuni" VALUES(87,5,'87:5','Ó sì sọ ọ́ di gbígbẹ tó dúdú.');
INSERT INTO "aykyuni" VALUES(87,6,'87:6','Àwa yóò máa ké (al-Ƙur’ān) fún ọ. Ìwọ kò sì níí gbàgbé.');
INSERT INTO "aykyuni" VALUES(87,7,'87:7','Àyàfi ohun tí Allāhu bá fẹ́.Dájúdájú Ó mọ gban̄gba àti ohun tó pamọ́. ');
INSERT INTO "aykyuni" VALUES(87,8,'87:8','Àwa yó sì ṣe iṣẹ́ rere ní ìrọ̀rùn fún ọ.');
INSERT INTO "aykyuni" VALUES(87,9,'87:9','Nítorí náà, ṣèrántí ní àyè tí ìrántí ti wúlò.1 ');
INSERT INTO "aykyuni" VALUES(87,10,'87:10','Ẹni tí ó máa páyà (Allāhu) máa lo ìrántí.');
INSERT INTO "aykyuni" VALUES(87,11,'87:11','Olórí-burúkú sì máa takété sí i.');
INSERT INTO "aykyuni" VALUES(87,12,'87:12','(Òun sì ni) ẹni tí ó máa wọ inú Iná tó tóbi.');
INSERT INTO "aykyuni" VALUES(87,13,'87:13','Lẹ́yìn náà, kò níí kú sínú rẹ̀, kò sì níí ṣẹ̀mí (àlàáfíà).');
INSERT INTO "aykyuni" VALUES(87,14,'87:14','Dájúdájú ẹni tí ó bá ṣe àfọ̀mọ́ (ẹ̀sìn rẹ̀) ti jèrè.1 ');
INSERT INTO "aykyuni" VALUES(87,15,'87:15','Ó sì rántí orúkọ Olúwa rẹ̀, ó sì kírun.');
INSERT INTO "aykyuni" VALUES(87,16,'87:16','Rárá, ńṣe l’ẹ̀ ń gbé àjùlọ fún ìṣẹ̀mí ayé.');
INSERT INTO "aykyuni" VALUES(87,17,'87:17','Ọ̀run sì lóore jùlọ, ó sì máa wà títí láéláé.');
INSERT INTO "aykyuni" VALUES(87,18,'87:18','Dájúdájú èyí wà nínú àwọn tákàǹdá àkọ́kọ́,');
INSERT INTO "aykyuni" VALUES(87,19,'87:19','tákàdá (Ànábì) ’Ibrọ̄hīm àti (Ànábì) Mūsā.');
INSERT INTO "aykyuni" VALUES(88,1,'88:1','Ṣebí ọ̀rọ̀ nípa ohun tó máa bo ẹ̀dá mọ́lẹ̀ ti dé ọ̀dọ̀ rẹ?');
INSERT INTO "aykyuni" VALUES(88,2,'88:2','Àwọn ojú kan yóò wálẹ̀ (ní ti ìyẹpẹrẹ) ní ọjọ́ yẹn.');
INSERT INTO "aykyuni" VALUES(88,3,'88:3','Wọ́n ṣe iṣẹ́ àṣekúdórógbó (ni nílé ayé).');
INSERT INTO "aykyuni" VALUES(88,4,'88:4','Wọ́n sì máa wọ inú Iná tó gbóná janjan (ní ọ̀run).');
INSERT INTO "aykyuni" VALUES(88,5,'88:5','Wọ́n sì máa fún (wọn) ní omi ìṣẹ́lẹ̀rú gbígbóná mu.');
INSERT INTO "aykyuni" VALUES(88,6,'88:6','Kò sí oúnjẹ kan fún wọn àyàfi igi ẹlẹ́gùn-ún gbígbẹ.');
INSERT INTO "aykyuni" VALUES(88,7,'88:7','Kò níí mú wọn sanra. Kò sì níí rọ̀ wọ́n lọ́rọ̀ nínú ebi.');
INSERT INTO "aykyuni" VALUES(88,8,'88:8','Àwọn ojú kan yó sì kún fún ìgbádùn ní ọjọ́ yẹn');
INSERT INTO "aykyuni" VALUES(88,9,'88:9','nítorí pé (ó) yọ́nú sí (ẹ̀san) iṣẹ́ rẹ̀.');
INSERT INTO "aykyuni" VALUES(88,10,'88:10','(Ó máa) wà nínú Ọgbà Ìdẹ̀ra gíga.');
INSERT INTO "aykyuni" VALUES(88,11,'88:11','Wọn kò níí gbọ́ ìsọkúsọ nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(88,12,'88:12','Omi ìṣẹ́lẹ̀rú tó ń ṣàn wà nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(88,13,'88:13','Àwọn ibùsùn tí wọ́n gbé sókè wà nínú rẹ̀,');
INSERT INTO "aykyuni" VALUES(88,14,'88:14','àti àwọn ife ìmumi tí wọ́n gbé kalẹ̀ (sí àrọ́wọ́tó wọn),');
INSERT INTO "aykyuni" VALUES(88,15,'88:15','àti àwọn ìrọ̀rí tí wọ́n tò sí ẹ̀gbẹ́ ara wọn,');
INSERT INTO "aykyuni" VALUES(88,16,'88:16','àti àwọn ìtẹ́ àtẹ́ẹ̀ká.');
INSERT INTO "aykyuni" VALUES(88,17,'88:17','Nítorí náà, ṣé wọn kò wòye sí ràkúnmí, bí A ti ṣẹ̀dá rẹ̀ ni;');
INSERT INTO "aykyuni" VALUES(88,18,'88:18','àti sánmọ̀, bí A ti ṣe gbé e sókè;');
INSERT INTO "aykyuni" VALUES(88,19,'88:19','àti àpáta, bí A ti ṣe gbé e nàró (sínú ilẹ̀);');
INSERT INTO "aykyuni" VALUES(88,20,'88:20','àti ilẹ̀, bí A ti ṣe tẹ́ ẹ sílẹ̀ pẹrẹsẹ?');
INSERT INTO "aykyuni" VALUES(88,21,'88:21','Nítorí náà, ṣèrántí. Ìwọ kúkú ni olùṣèràntí.');
INSERT INTO "aykyuni" VALUES(88,22,'88:22','Ìwọ kì í ṣe ajẹni-nípá lórí wọn');
INSERT INTO "aykyuni" VALUES(88,23,'88:23','àfi ẹni tí ó bá pẹ̀yìndà (sí òdodo), tí ó sì ṣàì gbàgbọ́.');
INSERT INTO "aykyuni" VALUES(88,24,'88:24','Allāhu sì máa jẹ ẹ́ níyà tó tóbi jùlọ.');
INSERT INTO "aykyuni" VALUES(88,25,'88:25','Dájúdájú ọ̀dọ̀ Wa ni àbọ̀ wọn wà.');
INSERT INTO "aykyuni" VALUES(88,26,'88:26','Lẹ́yìn náà, dájúdájú Àwa l’A máa ṣe ìṣírò-iṣẹ́ wọn.');
INSERT INTO "aykyuni" VALUES(89,1,'89:1','Allāhu fi àfẹ̀mọ́júmọ́ búra.');
INSERT INTO "aykyuni" VALUES(89,2,'89:2','Ó tún fi àwọn òru mẹ́wàá kan búra.');
INSERT INTO "aykyuni" VALUES(89,3,'89:3','Ó tún fi n̄ǹkan tí eéjì ń pín geerege àti n̄ǹkan tí eéjì kò pín geerege búra.');
INSERT INTO "aykyuni" VALUES(89,4,'89:4','Ó tún fi òru nígbà tí ó bá dé búra.');
INSERT INTO "aykyuni" VALUES(89,5,'89:5','Ǹjẹ́ ìbúra wà nínú ìyẹn fún onílàákàyè?');
INSERT INTO "aykyuni" VALUES(89,6,'89:6','Ṣé o kò wòye sí bí Olúwa rẹ ti ṣe ọ̀rọ̀ ará ‘Ād,');
INSERT INTO "aykyuni" VALUES(89,7,'89:7','(ìyẹn àwọn ìran) ’Irọm, àwọn òmìrán,');
INSERT INTO "aykyuni" VALUES(89,8,'89:8','àwọn tí A kò dá irú wọn mọ́ sínú ìlú?');
INSERT INTO "aykyuni" VALUES(89,9,'89:9','Àti àwọn Thamūd tí wọ́n gbẹ́ (ilé ìgbé) sínú àpáta nínú àfonúfojì,');
INSERT INTO "aykyuni" VALUES(89,10,'89:10','àti Fir‘aon eléèkàn (ìyà)?');
INSERT INTO "aykyuni" VALUES(89,11,'89:11','(Àwọn wọ̀nyí ni) àwọn tó tayọ ẹnu-ààlà nínú ìlú.');
INSERT INTO "aykyuni" VALUES(89,12,'89:12','Wọ́n sì ṣe ìbàjẹ́ púpọ̀ sínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(89,13,'89:13','Olúwa rẹ sì rọ̀jò ìyà lé wọn lórí.');
INSERT INTO "aykyuni" VALUES(89,14,'89:14','Dájúdájú Olúwa rẹ wà ní ibùṣọ́ (iṣẹ́ ẹ̀dá).');
INSERT INTO "aykyuni" VALUES(89,15,'89:15','Nítorí náà, ní ti ènìyàn, nígbà tí Olúwa rẹ̀ bá dán an wò, tí Ó pọ́n ọn lé, tí Ó sì ṣe ìdẹ̀ra fún un, nígbà náà ó máa wí pé: “Olúwa mi ti pọ́n mi lé.”');
INSERT INTO "aykyuni" VALUES(89,16,'89:16','Ṣùgbọ́n nígbà tí Ó bá dán an wò, tí Ó gán arísìkí rẹ̀ lé e lọ́wọ́, nígbà náà ó máa wí pé: “Olúwa mi ti yẹpẹrẹ mi.”');
INSERT INTO "aykyuni" VALUES(89,17,'89:17','Rárá, kò rí bẹ́ẹ̀, bí kò ṣe pé, ẹ̀yin kò ṣàpọ́nlé ọmọ-òrukàn.');
INSERT INTO "aykyuni" VALUES(89,18,'89:18','Àti pé ẹ̀yin kò gbẹra yín lóǹgbẹ láti fún mẹ̀kúnnù ní oúnjẹ.');
INSERT INTO "aykyuni" VALUES(89,19,'89:19','Ẹ tún ń jẹ ogún ní ìjẹkújẹ.');
INSERT INTO "aykyuni" VALUES(89,20,'89:20','Àti pé ẹ nífẹ̀ẹ́ sí dúkìá ní ìfẹ́ púpọ̀.');
INSERT INTO "aykyuni" VALUES(89,21,'89:21','Rárá, (kò yẹ kó rí bẹ́ẹ̀). Nígbà tí wọ́n bá mi ilẹ̀ tìtì ní mímì tìtì (tó rún wómúwómú, tí kò sí kòtò, tí kò sí gelemọ),');
INSERT INTO "aykyuni" VALUES(89,22,'89:22','(tí) Olúwa rẹ dé pẹ̀lú àwọn mọlāika ní ọ̀wọ̀ọ̀wọ́ ọ̀wọ̀ọ̀wọ́,');
INSERT INTO "aykyuni" VALUES(89,23,'89:23','ní ọjọ́ yẹn ni wọ́n máa mú iná Jahanamọ wá. Ní ọjọ́ yẹn, ènìyàn yóò rántí (ọ̀rọ̀ ara rẹ̀). Báwo sì ni ìrántí náà ṣe lè wúlò fún un!');
INSERT INTO "aykyuni" VALUES(89,24,'89:24','Ó máa wí pé: “Yéè, èmi ìbá ti ti (iṣẹ́ rere) síwájú fún ìṣẹ̀mí mi.”');
INSERT INTO "aykyuni" VALUES(89,25,'89:25','Ní ọjọ́ yẹn, ẹnì kan kò lè fi ìyà jẹ (ẹ̀dá) bí irú ìyà (tí) Allāhu (yóò fi jẹ ẹ́).');
INSERT INTO "aykyuni" VALUES(89,26,'89:26','Ẹnì kan kò sì lè de (ẹ̀dá) ní ìgbèkùn bí irú ìgbèkùn (tí) Allāhu (yóò fi dè é).');
INSERT INTO "aykyuni" VALUES(89,27,'89:27','Ìwọ ẹ̀mí olùfọ̀kànbalẹ̀,');
INSERT INTO "aykyuni" VALUES(89,28,'89:28','padà sí ọ̀dọ̀ Olúwa rẹ ní olùyọ́nú, ẹni ìyọ́nú.');
INSERT INTO "aykyuni" VALUES(89,29,'89:29','Nítorí náà, wọlé sáààrin àwọn ẹrúsìn Mi.');
INSERT INTO "aykyuni" VALUES(89,30,'89:30','Wọ inú Ọgbà Ìdẹ̀ra Mi.');
INSERT INTO "aykyuni" VALUES(90,1,'90:1','Èmi (Allāhu) ń fi ìlú yìíbúra. ');
INSERT INTO "aykyuni" VALUES(90,2,'90:2','Ìwọ sì ní ẹ̀tọ́ sí ìlú yìí (láti jagun nínú rẹ̀).1 ');
INSERT INTO "aykyuni" VALUES(90,3,'90:3','Èmi (Allāhu) ń fi òbí àti ohun tó bí búra.');
INSERT INTO "aykyuni" VALUES(90,4,'90:4','Dájúdájú A ṣẹ̀dá ènìyàn sínú ìṣòro.');
INSERT INTO "aykyuni" VALUES(90,5,'90:5','Ṣé ó ń lérò pé ẹnì kan kò ní agbára lórí òun ni?');
INSERT INTO "aykyuni" VALUES(90,6,'90:6','Ó (sì) ń wí pé: “Èmi ti bàná dúkìá ọ̀pọ̀lọ́pọ̀ (láti fi tako Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a -)”');
INSERT INTO "aykyuni" VALUES(90,7,'90:7','Ṣé ó ń lérò pé ẹnì kan kò rí òun ni?');
INSERT INTO "aykyuni" VALUES(90,8,'90:8','Ṣé A kò ṣe ojú méjì fún un?');
INSERT INTO "aykyuni" VALUES(90,9,'90:9','Àti ahọ́n pẹ̀lú ètè méjì (tí A ṣe fún un)?');
INSERT INTO "aykyuni" VALUES(90,10,'90:10','A sì fi ọ̀nà méjì mọ̀ ọ́n.');
INSERT INTO "aykyuni" VALUES(90,11,'90:11','Kò sì lọ rin ojú-ọ̀nà orí àpáta tó ṣòroó gùn fún ìgbàlà ẹ̀mí níbi Iná!');
INSERT INTO "aykyuni" VALUES(90,12,'90:12','Kí sì ni ó mú ọ mọ ohun tó ń jẹ́ ojú-ọ̀nà orí àpáta tó ṣòroó gùn fún ìgbàlà ẹ̀mí?');
INSERT INTO "aykyuni" VALUES(90,13,'90:13','(Òhun ni) títú ẹrú sílẹ̀ lóko ẹrú.');
INSERT INTO "aykyuni" VALUES(90,14,'90:14','Tàbí fífún ènìyàn ní oúnjẹ ní ọjọ́ ebi.');
INSERT INTO "aykyuni" VALUES(90,15,'90:15','(Ẹ lè fún) ọmọ-òrukàn nínú ẹbí,');
INSERT INTO "aykyuni" VALUES(90,16,'90:16','tàbí mẹ̀kúnnù tí kò ní gá tí kò ní go (olòṣì paraku).');
INSERT INTO "aykyuni" VALUES(90,17,'90:17','Lẹ́yìn náà, kí ó wà nínú àwọn tó gbàgbọ́ ní òdodo, tí wọ́n ń gbara wọn níyànjú sùúrù ṣíṣe, tí wọ́n sì tún ń gbara wọn níyànjú àánú ṣíṣe.');
INSERT INTO "aykyuni" VALUES(90,18,'90:18','Àwọn wọ̀nyẹn ni èrò ọwọ́ ọ̀tún.');
INSERT INTO "aykyuni" VALUES(90,19,'90:19','Àwọn tó sì ṣàì gbàgbọ́ nínú àwọn āyah Wa, àwọn ni èrò ọwọ́ òsì.1 ');
INSERT INTO "aykyuni" VALUES(90,20,'90:20','Wọ́n máa ti àwọn ìlẹ̀kùn Iná pa mọ́ wọn lórí pátápátá.');
INSERT INTO "aykyuni" VALUES(91,1,'91:1','Allāhu fi òòrùn àti ìyálẹ̀ta rẹ̀ búra.');
INSERT INTO "aykyuni" VALUES(91,2,'91:2','Ó tún fi òṣùpá nígbà tí ó bá (yọ) tẹ̀lé (òòrùn) búra.');
INSERT INTO "aykyuni" VALUES(91,3,'91:3','Ó tún fi ọ̀sán nígbà tí ó bá mú ìmọ́lẹ̀ bá òkùnkùn òru búra.');
INSERT INTO "aykyuni" VALUES(91,4,'91:4','Ó tún fi alẹ́ nígbà tí ó bá bo ọ̀sán mọ́lẹ̀ búra.');
INSERT INTO "aykyuni" VALUES(91,5,'91:5','Ó tún fi sánmọ̀ àti Ẹni tí Ó mọ ọ́nbúra? ');
INSERT INTO "aykyuni" VALUES(91,6,'91:6','Ó tún fi ilẹ̀ àti Ẹni tí Ó tẹ́ ẹ kalẹ̀ pẹrẹsẹ búra.');
INSERT INTO "aykyuni" VALUES(91,7,'91:7','Ó tún fi ẹ̀mí (ènìyàn) àti Ẹni tí Ó ṣe (oríkèé rẹ̀) dọ́gba búra.');
INSERT INTO "aykyuni" VALUES(91,8,'91:8','Ó sì fi ẹ̀ṣẹ̀ (tí ẹ̀mí lè dá) àti ìbẹ̀rù rẹ̀ mọ̀ ọ́n.');
INSERT INTO "aykyuni" VALUES(91,9,'91:9','Ẹnikẹ́ni tí ó bá ṣe àfọ̀mọ́ (ẹ̀mí ara) rẹ̀ (níbi ẹ̀ṣẹ̀), ó mà ti jèrè.1 ');
INSERT INTO "aykyuni" VALUES(91,10,'91:10','Ẹnikẹ́ni tí ó bá fi (ìwà ẹ̀ṣẹ̀) ba ẹ̀mí (ara) rẹ̀ jẹ́, ó mà ti pàdánù.');
INSERT INTO "aykyuni" VALUES(91,11,'91:11','Ìjọ Thamūd pe òdodo ní irọ́ nípa ìtayọ ẹnu-ààlà wọn.');
INSERT INTO "aykyuni" VALUES(91,12,'91:12','(Rántí) nígbà tí ẹni tí orí rẹ̀ burú jùlọ nínú wọn sáré dìde (láti gún ràkúnmí pa).');
INSERT INTO "aykyuni" VALUES(91,13,'91:13','Nígbà náà, Òjíṣẹ́ Allāhu sọ fún wọn pé: “(Ẹ fi) abo ràkúnmí Allāhu àti omi rẹ̀ (sílẹ̀).”');
INSERT INTO "aykyuni" VALUES(91,14,'91:14','Wọ́n pè é ní òpùrọ́. Wọ́n sì gún (ràkúnmí) pa. Nítorí náà, Olúwa wọn pa wọ́n rẹ́ nítorí ẹ̀ṣẹ̀ wọn. Ó sì fi ìparun náà kárí wọn.');
INSERT INTO "aykyuni" VALUES(91,15,'91:15','(Ẹni tó gún ràkúnmí pa) kò sì páyà ìkángun ọ̀rọ̀ wọn.');
INSERT INTO "aykyuni" VALUES(92,1,'92:1','Allāhu fi alẹ́ nígbà tí (ilẹ̀) bá ṣú búra.');
INSERT INTO "aykyuni" VALUES(92,2,'92:2','Ó tún fi ọ̀sán nígbà tí (ọ̀sán) bá pọ́n búra.');
INSERT INTO "aykyuni" VALUES(92,3,'92:3','Ó tún fi Ẹni tí Ó dá akọ àti abo búra.1 ');
INSERT INTO "aykyuni" VALUES(92,4,'92:4','Dájúdájú ọ̀tọ̀ọ̀tọ̀ ni iṣẹ́ yín.');
INSERT INTO "aykyuni" VALUES(92,5,'92:5','Ní ti ẹni tí ó bá tọrẹ, tí ó sì bẹ̀rù (Allāhu),');
INSERT INTO "aykyuni" VALUES(92,6,'92:6','(tí) ó tún pe ẹ̀san rere (Ọgbà Ìdẹ̀ra) ní òdodo,');
INSERT INTO "aykyuni" VALUES(92,7,'92:7','A sì máa ṣe iṣẹ́ rere ní ìrọ̀rùn fún un.');
INSERT INTO "aykyuni" VALUES(92,8,'92:8','Ní ti ẹni tí ó bá yahun, tí ó sì rí ara rẹ̀ lẹ́ni tó rọrọ̀ tayọ ẹ̀san ọ̀run,');
INSERT INTO "aykyuni" VALUES(92,9,'92:9','(tí) ó tún pe ẹ̀san rere (Ọgbà Ìdẹ̀ra) ní irọ́,');
INSERT INTO "aykyuni" VALUES(92,10,'92:10','A sì máa ṣe iṣẹ́ aburú ní ìrọ̀rùn fún un.');
INSERT INTO "aykyuni" VALUES(92,11,'92:11','Dúkìá rẹ̀ kò sì níí rọ̀ ọ́ lọ́rọ̀ nígbà tí ó bá parun, tí ó já bọ́ (sínú Iná).');
INSERT INTO "aykyuni" VALUES(92,12,'92:12','Dájúdájú tiWa ni (láti ṣàlàyé) ìmọ̀nà.');
INSERT INTO "aykyuni" VALUES(92,13,'92:13','Àti pé dájúdájú tiWa ni ọ̀run àti ayé.');
INSERT INTO "aykyuni" VALUES(92,14,'92:14','Nítorí náà, Mo ti fi Iná tó ń jò fòfò kìlọ̀ fún yín.');
INSERT INTO "aykyuni" VALUES(92,15,'92:15','Kò sí ẹni tí ó máa wọ inú rẹ̀ àfi olórí burúkú,');
INSERT INTO "aykyuni" VALUES(92,16,'92:16','ẹni tí ó pe òdodo ní irọ́, tí ó sì kẹ̀yìn sí i.');
INSERT INTO "aykyuni" VALUES(92,17,'92:17','Wọ́n sì máa gbé (Iná) jìnnà sí olùbẹ̀rù (Allāhu),');
INSERT INTO "aykyuni" VALUES(92,18,'92:18','ẹni tó ń fi dúkìá rẹ̀ tọrẹ, tí ó ń ṣàfọ̀mọ́ (rẹ̀).');
INSERT INTO "aykyuni" VALUES(92,19,'92:19','Kò sì sí oore ìdẹ̀ra kan tí (ó ní lọ́kàn) láti gbà ní ẹ̀san ní ọ̀dọ̀ ẹnikẹ́ni,');
INSERT INTO "aykyuni" VALUES(92,20,'92:20','bí kò ṣe pé láti fi wá ojú rere Olúwa rẹ̀, Ẹni Gíga jùlọ.');
INSERT INTO "aykyuni" VALUES(92,21,'92:21','Láìpẹ́ ó sì máa yọ́nú (sí ẹ̀san rere rẹ̀).');
INSERT INTO "aykyuni" VALUES(93,1,'93:1','Allāhu fi ìyálẹ̀ta búra.');
INSERT INTO "aykyuni" VALUES(93,2,'93:2','Ó tún fi alẹ́ nígbà tí (ilẹ̀) bá ṣú búra.');
INSERT INTO "aykyuni" VALUES(93,3,'93:3','Olúwa rẹ kò pa ọ́ tì, kò sì bínú sí ọ.');
INSERT INTO "aykyuni" VALUES(93,4,'93:4','Dájúdájú ọ̀run lóore fún ọ ju ayé.');
INSERT INTO "aykyuni" VALUES(93,5,'93:5','Dájúdájú láìpẹ́ Olúwa rẹ máa fún ọ ní (oore púpọ̀ ní ọ̀run). Nítorí náà, o sì máa yọ́nú sí i.');
INSERT INTO "aykyuni" VALUES(93,6,'93:6','Ṣé (Allāhu) kò rí ọ ní ọmọ-òrukàn ni? Ó sì fún ọ ní ibùgbé.');
INSERT INTO "aykyuni" VALUES(93,7,'93:7','Ó sì rí ọ ní aláìmọ̀nà (ìyẹn ṣíwájú ìsọ̀kalẹ̀ ìmísí). Ó sì fi ọ̀nà mọ̀ ọ́.1 ');
INSERT INTO "aykyuni" VALUES(93,8,'93:8','Ó tún rí ọ ní aláìní, Ó sì rọ̀ ọ́ lọ́rọ̀.');
INSERT INTO "aykyuni" VALUES(93,9,'93:9','Nítorí náà, ní ti ọmọ-òrukàn, má ṣe jẹ gàba (lé e lórí).');
INSERT INTO "aykyuni" VALUES(93,10,'93:10','Ní ti alágbe, má sì ṣe jágbe (mọ́ ọn).');
INSERT INTO "aykyuni" VALUES(93,11,'93:11','Ní ti ìdẹ̀ra Olúwa rẹ, sọ ọ́ jáde.');
INSERT INTO "aykyuni" VALUES(94,1,'94:1','Ṣé A kò ṣípayá igbá-àyà rẹ fún ọ bí?');
INSERT INTO "aykyuni" VALUES(94,2,'94:2','A sì gbé ẹ̀ṣẹ̀ rẹ kúrò lọ́rùn rẹ,');
INSERT INTO "aykyuni" VALUES(94,3,'94:3','èyí tí ó wọ̀ ọ́ lọ́rùn.1 ');
INSERT INTO "aykyuni" VALUES(94,4,'94:4','A sì gbé ìrántí orúkọ rẹ ga fún ọ.');
INSERT INTO "aykyuni" VALUES(94,5,'94:5','Nítorí náà, dájúdájú ìdẹ̀kùn wà lẹ́yìn ìnira.');
INSERT INTO "aykyuni" VALUES(94,6,'94:6','Dájúdájú ìdẹ̀kùn wà lẹ́yìn ìnira sẹ́.');
INSERT INTO "aykyuni" VALUES(94,7,'94:7','Nítorí náà, nígbà tí o bá bùṣe (lórí ohun tí ó jẹmọ́ táyé), gbìyànjú (dáadáa lórí ìjọ́sìn).');
INSERT INTO "aykyuni" VALUES(94,8,'94:8','Ọ̀dọ̀ Olúwa rẹ sì ni kí o ṣojú kòkòrò oore sí.');
INSERT INTO "aykyuni" VALUES(95,1,'95:1','Allāhu fi èso tīn àti èso zaetūn búra.');
INSERT INTO "aykyuni" VALUES(95,2,'95:2','Ó tún fi àpáta Sīnīn búra.');
INSERT INTO "aykyuni" VALUES(95,3,'95:3','Ó tún fi ìlú ìfàyàbalẹ̀ yìí búra.');
INSERT INTO "aykyuni" VALUES(95,4,'95:4','Dájúdájú A ṣẹ̀dá ènìyàn pẹ̀lú ìrísí tó dára jùlọ.');
INSERT INTO "aykyuni" VALUES(95,5,'95:5','Lẹ́yìn náà, A máa dá a padà sí ìsàlẹ̀ pátápátá (nínú Iná).');
INSERT INTO "aykyuni" VALUES(95,6,'95:6','Àyàfi àwọn tó gbàgbọ́ ní òdodo, tí wọ́n sì ṣe àwọn iṣẹ́ rere. Nítorí náà, ẹ̀san tí kò níí pin (tí kò níí pẹ̀dín) ń bẹ fún wọn.');
INSERT INTO "aykyuni" VALUES(95,7,'95:7','Ta l’ó tún ń pè ọ́ ní òpùrọ́ nípa Ọjọ́ ẹ̀san lẹ́yìn (ọ̀rọ̀ yìí)?');
INSERT INTO "aykyuni" VALUES(95,8,'95:8','Ṣé Allāhu kọ́ ni Ẹni t’Ó mọ ẹ̀jọ́ dá jùlọ nínú àwọn adájọ́ ni?');
INSERT INTO "aykyuni" VALUES(96,1,'96:1','Ké (al-Ƙur’ān) pẹ̀lú orúkọ Olúwa rẹ, Ẹni tí Ó dá ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(96,2,'96:2','Ó ṣẹ̀dá ènìyàn láti ara ẹ̀jẹ̀ dídì.');
INSERT INTO "aykyuni" VALUES(96,3,'96:3','Ké e. Olúwa rẹ ni Alápọ̀n-ọ́nlé jùlọ.');
INSERT INTO "aykyuni" VALUES(96,4,'96:4','Ẹni tí Ó fi ìmọ̀ nípa gègé ìkọ̀wé mọ ènìyàn.');
INSERT INTO "aykyuni" VALUES(96,5,'96:5','Ó fi ìmọ̀ tí ènìyàn kò mọ̀ mọ̀ ọ́n.');
INSERT INTO "aykyuni" VALUES(96,6,'96:6','Ní ti òdodo, dájúdájú ènìyàn kúkú ń tayọ ẹnu-ààlà');
INSERT INTO "aykyuni" VALUES(96,7,'96:7','nítorí pé ó rí ara rẹ̀ ní ọlọ́rọ̀.');
INSERT INTO "aykyuni" VALUES(96,8,'96:8','Dájúdájú ọ̀dọ̀ Olúwa rẹ ni àbọ̀ ẹ̀dá.');
INSERT INTO "aykyuni" VALUES(96,9,'96:9','Sọ fún mi nípa ẹni tó ń kọ̀');
INSERT INTO "aykyuni" VALUES(96,10,'96:10','fún ẹrúsìn kan nígbà tí (ẹrúsìn náà) kírun!');
INSERT INTO "aykyuni" VALUES(96,11,'96:11','Sọ fún mi (nípa ẹ̀san rẹ̀) tí ó bá jẹ́ pé ó wà lórí ìmọ̀nà (’Islām)');
INSERT INTO "aykyuni" VALUES(96,12,'96:12','tàbí pé ó pàṣẹ ìbẹ̀rù (Allāhu)!');
INSERT INTO "aykyuni" VALUES(96,13,'96:13','Sọ fún mi (nípa ìyà rẹ̀) tí ó bá jẹ́ pé ó pe òdodo ní irọ́, tí ó sì kẹ̀yìn sí i?');
INSERT INTO "aykyuni" VALUES(96,14,'96:14','Ṣé kò mọ̀ pé dájúdájú Allāhu ń rí (òun ni?)');
INSERT INTO "aykyuni" VALUES(96,15,'96:15','Rárá (ọ̀rọ̀ kò rí bí ọ̀tá Allāhu ṣe rò ó sí. Dájúdájú tí kò bá jáwọ́ (níbi dídí Ànábì - kí ìkẹ́ àti ọlà Allāhu máa bá a - lọ́wọ́), dájúdájú A máa fi àásó orí2 rẹ̀ wọ́ ọ (sínú Iná); ');
INSERT INTO "aykyuni" VALUES(96,16,'96:16','àásó orí òpùrọ́, ẹlẹ́ṣẹ̀.');
INSERT INTO "aykyuni" VALUES(96,17,'96:17','Nítorí náà, kí ó pe àwọn olùbájókòó rẹ̀.');
INSERT INTO "aykyuni" VALUES(96,18,'96:18','Àwa máa pe àwọn mọlāika ẹ̀ṣọ́ Iná.');
INSERT INTO "aykyuni" VALUES(96,19,'96:19','Rárá (ọ̀rọ̀ kò rí bí ó ṣe ń sọ nípa Wa). Má ṣe tẹ̀lé tirẹ̀. Forí kanlẹ̀ (kí o kírun), kí o sì súnmọ́ (Allāhu).');
INSERT INTO "aykyuni" VALUES(97,1,'97:1','Dájúdájú Àwa sọ al-Ƙur’ān kalẹ̀ nínú Òru Abiyì.1 ');
INSERT INTO "aykyuni" VALUES(97,2,'97:2','Kí sì ni ó mú ọ mọ ohun tó ń jẹ́ Òru Abiyì?');
INSERT INTO "aykyuni" VALUES(97,3,'97:3','Òru Abiyì lóore ju ẹgbẹ̀rún oṣù.');
INSERT INTO "aykyuni" VALUES(97,4,'97:4','Àwọn mọlāika àti Jibrīl yó sì máa sọ̀kalẹ̀ nínú òru náà pẹ̀lú àṣẹ Olúwa wọn fún gbogbo ọ̀rọ̀ ẹ̀dá kọ̀ọ̀kan.');
INSERT INTO "aykyuni" VALUES(97,5,'97:5','Àlàáfíà ni òru náà títí di àsìkò àfẹ̀mọ́júmọ́.');
INSERT INTO "aykyuni" VALUES(98,1,'98:1','Àwọn tó ṣàì gbàgbọ́ nínú àwọn ahlul-kitāb àti àwọn ọ̀ṣẹbọ, wọn kò níí kúrò nínú àìgbàgbọ́ wọn títí di ìgbà tí ẹ̀rí tó yanjú yó fi máa dé ọ̀dọ̀ wọn.');
INSERT INTO "aykyuni" VALUES(98,2,'98:2','Òjíṣẹ́ kan láti ọ̀dọ̀ Allāhu yó (sì) máa ké àwọn tákàdá mímọ́.');
INSERT INTO "aykyuni" VALUES(98,3,'98:3','Àwọn ìwé òfin tó fẹsẹ̀ rinlẹ̀ sì wà nínú rẹ̀.');
INSERT INTO "aykyuni" VALUES(98,4,'98:4','Àwọn tí A fún ní tírà kò sì di ìjọ ọ̀tọ̀ọ̀tọ̀ àfi lẹ́yìn tí ẹ̀rí tó yanjú dé bá wọn.');
INSERT INTO "aykyuni" VALUES(98,5,'98:5','Àwa kò sì pa wọ́n ní àṣẹ kan bí kò ṣe pé kí wọ́n jọ́sìn fún Allāhu lẹ́ni tí yóò máa ṣe àfọ̀mọ́ ẹ̀sìn fún Un,lẹ́ni tí yóò dúró ṣinṣin nínú ẹ̀sìn.2 Kí wọ́n kírun, kí wọ́n sì yọ Zakāh. Ìyẹn sì ni ẹ̀sìn tó fẹsẹ̀ rinlẹ̀. ');
INSERT INTO "aykyuni" VALUES(98,6,'98:6','Dájúdájú àwọn tó ṣàì gbàgbọ́ (wọ̀nyí) nínú àwọn ahlul-kitāb àti àwọn ọ̀ṣẹbọ, wọn yóò wà nínú Iná. Olùṣegbére ni wọ́n nínú rẹ̀. Àwọn wọ̀nyẹn, àwọn ni ẹ̀dá tó burú jùlọ.');
INSERT INTO "aykyuni" VALUES(98,7,'98:7','Dájúdájú àwọn tó gbàgbọ́ ní òdodo (ìyẹn, àwọn mùsùlùmí), tí wọ́n sì ṣe àwọn iṣẹ́ rere, àwọn wọ̀nyẹn, àwọn ni ẹ̀dá tó dára jùlọ.');
INSERT INTO "aykyuni" VALUES(98,8,'98:8','Ẹ̀san wọn ní ọ̀dọ̀ Olúwa wọn ni àwọn Ọgbà Ìdẹ̀ra gbére, tí àwọn odò ń ṣàn ní ìsàlẹ̀ rẹ̀. Olùṣegbére ni wọ́n nínú rẹ̀ títí láéláé. Allāhu yọ́nú sí wọn. Àwọn náà yọ́nú sí (ohun tí Allāhu fún wọn). Ìyẹn wà fún ẹnikẹ́ni tí ó bá páyà Olúwa rẹ̀.');
INSERT INTO "aykyuni" VALUES(99,1,'99:1','Nígbà tí wọ́n bá mi ilẹ̀ tìtì ní ìmìtìtì rẹ̀,');
INSERT INTO "aykyuni" VALUES(99,2,'99:2','àti (nígbà) tí ilẹ̀ bá tú àwọn ẹrù tó wúwo nínú rẹ̀ jáde,');
INSERT INTO "aykyuni" VALUES(99,3,'99:3','ènìyàn yó sì wí pé: “Kí l’ó mú un?”');
INSERT INTO "aykyuni" VALUES(99,4,'99:4','Ní ọjọ́ yẹn ni (ilẹ̀) yóò sọ̀rọ̀ nípa àwọn ìró rẹ̀ (tí ẹ̀dá gbé orí ilẹ̀ ṣe).');
INSERT INTO "aykyuni" VALUES(99,5,'99:5','Nítorí pé dájúdájú Olúwa rẹ l’Ó fún un ní àṣẹ (láti sọ̀rọ̀).');
INSERT INTO "aykyuni" VALUES(99,6,'99:6','Ní ọjọ́ yẹn, àwọn ènìyàn yóò máa gba ọ̀nà ọ̀tọ̀ọ̀tọ̀ lọ nítorí kí wọ́n lè fi àwọn iṣẹ́ wọn hàn wọ́n.');
INSERT INTO "aykyuni" VALUES(99,7,'99:7','Nítorí náà, ẹni tí ó bá ṣe iṣẹ́ rere ní òdíwọ̀n ọmọ iná-igún, ó máa rí i.');
INSERT INTO "aykyuni" VALUES(99,8,'99:8','Ẹni tí ó bá sì ṣe iṣẹ́ aburú ní òdíwọ̀n ọmọ iná-igún, ó máa rí i.1 ');
INSERT INTO "aykyuni" VALUES(100,1,'100:1','Allāhu fi àwọn ẹṣin tó ń sáré tó ń mí hẹlẹhẹlẹ ní ojú-ogun búra.');
INSERT INTO "aykyuni" VALUES(100,2,'100:2','Ó tún fi àwọn ẹṣin tí pátákò ẹsẹ̀ wọn ń ṣáná (níbi eré sísá) búra.');
INSERT INTO "aykyuni" VALUES(100,3,'100:3','Ó tún fi àwọn ẹṣin tó ń kọlu ọ̀tá ẹ̀sìn ní òwúrọ̀ kùtùkùtù búra.');
INSERT INTO "aykyuni" VALUES(100,4,'100:4','Wọ́n sì fi (pátákò ẹsẹ̀ wọn) tu eruku (ilẹ̀ ọ̀tá) sókè.');
INSERT INTO "aykyuni" VALUES(100,5,'100:5','Wọ́n tún bẹ́ gìjà pẹ̀lú eruku ẹsẹ̀ wọn sáààrin àkójọ ọ̀tá.');
INSERT INTO "aykyuni" VALUES(100,6,'100:6','Dájúdájú ènìyàn ni aláìmoore sí Olúwa rẹ̀.');
INSERT INTO "aykyuni" VALUES(100,7,'100:7','Dájúdájú Allāhu sì ń jẹ́ Ẹlẹ́rìí lórí ìyẹn.');
INSERT INTO "aykyuni" VALUES(100,8,'100:8','Àti pé dájúdájú ènìyàn le gan-an níbi ìfẹ́ oore ayé.');
INSERT INTO "aykyuni" VALUES(100,9,'100:9','Ṣé kò mọ̀ pé nígbà tí wọ́n bá tú ohun tó wà nínú sàréè jáde (fún àjíǹde),');
INSERT INTO "aykyuni" VALUES(100,10,'100:10','tí wọ́n sì tú ohun tó wà nínú igbá-àyà ẹ̀dá síta pátápátá,');
INSERT INTO "aykyuni" VALUES(100,11,'100:11','dájúdájú Olúwa wọn ni Alámọ̀tán nípa wọn ní Ọjọ́ yẹn?');
INSERT INTO "aykyuni" VALUES(101,1,'101:1','Àkókò ìjáyà.');
INSERT INTO "aykyuni" VALUES(101,2,'101:2','Kí ni Àkókò ìjáyà?');
INSERT INTO "aykyuni" VALUES(101,3,'101:3','Kí sì l’ó mú ọ mọ ohun tó ń jẹ́ Àkókò ìjáyà?');
INSERT INTO "aykyuni" VALUES(101,4,'101:4','(Òhun ni) ọjọ́ tí ènìyàn yó dà bí àfòpiná tí wọ́n fọ́nká síta,');
INSERT INTO "aykyuni" VALUES(101,5,'101:5','àwọn àpáta yó sì dà bí ẹ̀gbọ̀n òwú tí wọ́n gbọ̀n dànù.');
INSERT INTO "aykyuni" VALUES(101,6,'101:6','Nítorí náà, ní ti ẹni tí òṣùwọ̀n (iṣẹ́ rere) rẹ̀ bá tẹ̀wọ̀n,');
INSERT INTO "aykyuni" VALUES(101,7,'101:7','ó sì máa wà nínú ìṣẹ̀mí tó máa yọ́nú sí.');
INSERT INTO "aykyuni" VALUES(101,8,'101:8','Ní ti ẹni tí òṣùwọ̀n (iṣẹ́ rere) rẹ̀ bá sì fúyẹ́,');
INSERT INTO "aykyuni" VALUES(101,9,'101:9','Hāwiyah sì ni ibùgbé rẹ̀.');
INSERT INTO "aykyuni" VALUES(101,10,'101:10','Kí sì ni ó mú ọ mọ ohun tó ń jẹ́ bẹ́ẹ̀?');
INSERT INTO "aykyuni" VALUES(101,11,'101:11','(Òhun ni) Iná gbígbóná tó ń jó gan-an.');
INSERT INTO "aykyuni" VALUES(102,1,'102:1','Wíwá oore ayé ní ọ̀pọ̀lọ́pọ̀ fún ìyanràn ṣíṣe ti kó àìrójú fẹ́sìn ba yín');
INSERT INTO "aykyuni" VALUES(102,2,'102:2','títí ẹ fi wọ inú sàréè.');
INSERT INTO "aykyuni" VALUES(102,3,'102:3','Rárá (kò yẹ kó rí bẹ́ẹ̀, àmọ́) láìpẹ́ ẹ máa mọ̀.');
INSERT INTO "aykyuni" VALUES(102,4,'102:4','Lẹ́yìn náà, ní ti òdodo, láìpẹ́ ẹ máa mọ̀.');
INSERT INTO "aykyuni" VALUES(102,5,'102:5','Ní ti òdodo, tí ó bá jẹ́ pé ẹ ni ìmọ̀ àmọ̀dájú ni (ẹ̀yin ìbá tí ṣe bẹ́ẹ̀).');
INSERT INTO "aykyuni" VALUES(102,6,'102:6','Dájúdájú ẹ máa rí iná Jẹhīm.');
INSERT INTO "aykyuni" VALUES(102,7,'102:7','Lẹ́yìn náà, dájúdájú ẹ máa fi ojú rí i ní àrídájú.');
INSERT INTO "aykyuni" VALUES(102,8,'102:8','Lẹ́yìn náà, dájúdájú ní ọjọ́ yẹn wọ́n máa bi yín léèrè nípa ìgbádùn (ayé yìí).');
INSERT INTO "aykyuni" VALUES(103,1,'103:1','Allāhu fi àkókò ìrọ̀lẹ́ ayé búra.');
INSERT INTO "aykyuni" VALUES(103,2,'103:2','Dájúdájú ènìyàn wà nínú òfò.');
INSERT INTO "aykyuni" VALUES(103,3,'103:3','Àyàfi àwọn tó gbàgbọ́ ní òdodo, tí wọ́n ṣe àwọn iṣẹ́ rere, tí wọ́n sọ àsọtẹ́lẹ̀ òdodo láààrin ara wọn, tí wọ́n sì tún sọ àsọtẹ́lẹ̀ sùúrù láààrin ara wọn.');
INSERT INTO "aykyuni" VALUES(104,1,'104:1','Ègbé ni fún gbogbo abúnilójú-ẹni, abúnilẹ́yìn-ẹni,');
INSERT INTO "aykyuni" VALUES(104,2,'104:2','ẹni tí ó kó dúkìá jọ, tí ó sì kà á lákàtúnkà (láì ná an fún ẹ̀sìn).');
INSERT INTO "aykyuni" VALUES(104,3,'104:3','Ó ń lérò pé dájúdájú dúkìá rẹ̀ yóò mú un ṣe gbére (nílé ayé).');
INSERT INTO "aykyuni" VALUES(104,4,'104:4','Rárá (kò rí bẹ́ẹ̀). Dájúdájú wọ́n máa kù ú lókò sínú Hutọmọh ni.');
INSERT INTO "aykyuni" VALUES(104,5,'104:5','Kí sì ni ó mú ọ mọ ohun tó ń jẹ́ Hutọmọh?');
INSERT INTO "aykyuni" VALUES(104,6,'104:6','(Òhun ni) Iná Allāhu tí wọ́n ń kò (tó ń jó geregere),');
INSERT INTO "aykyuni" VALUES(104,7,'104:7','èyí tí ó máa jó (ẹ̀dá) wọ inú ọkàn lọ.');
INSERT INTO "aykyuni" VALUES(104,8,'104:8','Dájúdájú wọ́n máa ti (àwọn ìlẹ̀kùn) Iná pa mọ́ wọn lórí pátápátá.');
INSERT INTO "aykyuni" VALUES(104,9,'104:9','(Wọ́n máa wà) láààrin àwọn òpó kìrìbìtì gíga (nínú Iná).');
INSERT INTO "aykyuni" VALUES(105,1,'105:1','Ṣé o kò wòye sí bí Olúwa rẹ ti ṣe ọ̀rọ̀ ìjọ elérin ni?');
INSERT INTO "aykyuni" VALUES(105,2,'105:2','Ṣé kò sọ ète wọn di òfo àti anù bí?');
INSERT INTO "aykyuni" VALUES(105,3,'105:3','Ó sì rán àwọn ẹyẹ níkọ̀níkọ̀ sí wọn.');
INSERT INTO "aykyuni" VALUES(105,4,'105:4','Wọ́n ń jù wọ́n ní òkúta amọ̀ (tó ti gbaná sára).');
INSERT INTO "aykyuni" VALUES(105,5,'105:5','Ó sì sọ wọ́n di bíi pòpórò gbígbẹ tí ẹranko jẹ lájẹdàsílẹ̀.');
INSERT INTO "aykyuni" VALUES(106,1,'106:1','(Ìkẹ́ ni láti ọ̀dọ̀ Allāhu) fún ìdílé Ƙuraeṣ láti wà papọ̀ nínú ààbò.');
INSERT INTO "aykyuni" VALUES(106,2,'106:2','(Ìkẹ́ ni sẹ́ láti ọ̀dọ̀ Allāhu) fún wọn láti wà papọ̀ nínú ààbò lórí ìrìn-àjò ní ìgbà òtútù àti ní ìgbà ooru.');
INSERT INTO "aykyuni" VALUES(106,3,'106:3','Nítorí náà, kí wọ́n jọ́sìn fún Olúwa Ilé (Ka‘bah) yìí.');
INSERT INTO "aykyuni" VALUES(106,4,'106:4','Ẹni tí Ó fún wọn ní jíjẹ (ní àsìkò) ebi. Ó sì fi wọ́n lọ́kàn balẹ̀ nínú ìpáyà.');
INSERT INTO "aykyuni" VALUES(107,1,'107:1','Sọ fún mi nípa ẹni tó ń pe Ọjọ́ Ẹ̀san ní irọ́!');
INSERT INTO "aykyuni" VALUES(107,2,'107:2','Ìyẹn ni ẹni tó ń lé ọmọ-òrukàn dànù.');
INSERT INTO "aykyuni" VALUES(107,3,'107:3','Kò sì níí gbìyànjú láti bọ́ mẹ̀kúnnù.');
INSERT INTO "aykyuni" VALUES(107,4,'107:4','Ègbé ni fún àwọn (mùnááfìkí) tó ń kírun;1 ');
INSERT INTO "aykyuni" VALUES(107,5,'107:5','àwọn tó ń gbàgbé láti kírun wọn ní àsìkò rẹ̀;');
INSERT INTO "aykyuni" VALUES(107,6,'107:6','àwọn tó ń ṣe ṣekárími;');
INSERT INTO "aykyuni" VALUES(107,7,'107:7','àti pé wọ́n ń hánnà ohun èlò (fún àwọn ènìyàn).');
INSERT INTO "aykyuni" VALUES(108,1,'108:1','Dájúdájú Àwa fún ọ ní ọ̀pọ̀lọ́pọ̀ oore.');
INSERT INTO "aykyuni" VALUES(108,2,'108:2','Nítorí náà, kírun fún Olúwa rẹ, kí o sì gúnran (fún Un).');
INSERT INTO "aykyuni" VALUES(108,3,'108:3','Dájúdájú, ẹni tó ń bínú rẹ, òun ni kò níí lẹ́yìn.');
INSERT INTO "aykyuni" VALUES(109,1,'109:1','Sọ pé: “Ẹ̀yin aláìgbàgbọ́,');
INSERT INTO "aykyuni" VALUES(109,2,'109:2','Èmi kò níí jọ́sìn fún ohun tí ẹ̀ ń jọ́sìn fún.');
INSERT INTO "aykyuni" VALUES(109,3,'109:3','Ẹ̀yin náà kò jọ́sìn fún Ẹni tí mò ń jọ́sìn fún.');
INSERT INTO "aykyuni" VALUES(109,4,'109:4','Èmi kò níí jọ́sìn fún ohun tí ẹ jọ́sìn fún sẹ́.');
INSERT INTO "aykyuni" VALUES(109,5,'109:5','Ẹ̀yin náà kò kú jọ́sìn fún Ẹni tí mò ń jọ́sìn fún.');
INSERT INTO "aykyuni" VALUES(109,6,'109:6','Tiyín ni ẹ̀sìn yín, tèmi sì ni ẹ̀sìn mi.”');
INSERT INTO "aykyuni" VALUES(110,1,'110:1','Nígbà tí àrànṣe Allāhu (lórí ọ̀tá ẹ̀sìn) àti ṣíṣí ìlú (Mọ́kkah) bá ṣẹlẹ̀,');
INSERT INTO "aykyuni" VALUES(110,2,'110:2','tí o sì rí àwọn ènìyàn tí wọ́n wọnú ẹ̀sìn Allāhu níjọníjọ,');
INSERT INTO "aykyuni" VALUES(110,3,'110:3','nítorí náà, ṣe àfọ̀mọ́ àti ẹyìn fún Olúwa rẹ. Kí o sì tọrọ àforíjìn lọ́dọ̀ Rẹ̀.Dájúdájú Ó ń jẹ́ Olùgba-ìronúpìwàdà. ');
INSERT INTO "aykyuni" VALUES(111,1,'111:1','Ọwọ́ Abu-Lahab méjèèjì ti ṣòfò. Òun náà sì ṣòfò.');
INSERT INTO "aykyuni" VALUES(111,2,'111:2','Àwọn dúkìá rẹ̀ àti ohun tí ó ṣe níṣẹ́ (ìyẹn, àwọn ọmọ rẹ̀) kò sì níí rọ̀ ọ́ lọ́rọ̀ (níbi ìyà).');
INSERT INTO "aykyuni" VALUES(111,3,'111:3','Ó sì máa wọ inú Iná eléjò fòfò.');
INSERT INTO "aykyuni" VALUES(111,4,'111:4','Àti ìyàwó rẹ̀, aláàárù-igi-ìṣẹ́pẹ́ ẹlẹ́gùn-ún, (ó máa wọná).');
INSERT INTO "aykyuni" VALUES(111,5,'111:5','Igbà-ọ̀pẹ pọ̀npọ̀nràn máa wà ní ọrùn rẹ̀ (nínú Iná).');
INSERT INTO "aykyuni" VALUES(112,1,'112:1','Sọ pé: “Òun ni Allāhu, Ọ̀kan ṣoṣo.');
INSERT INTO "aykyuni" VALUES(112,2,'112:2','Allāhu ni Aṣíwájú tí ẹ̀dá ní bùkátà sí níbi jíjọ́sìn fún Un àti títọrọ oore ní ọ̀dọ̀ Rẹ̀, tí Òun kò sì ní bùkátà sí wọn ní ọ̀nà kan kan.');
INSERT INTO "aykyuni" VALUES(112,3,'112:3','Kò bímọ. Wọn kò sì bí I.');
INSERT INTO "aykyuni" VALUES(112,4,'112:4','Kò sì sí ẹnì kan kan tí ó jọ Ọ́.”1 ');
INSERT INTO "aykyuni" VALUES(113,1,'113:1','Sọ pé: “Mo sá di Olúwa òwúrọ̀ kùtùkùtù');
INSERT INTO "aykyuni" VALUES(113,2,'113:2','níbi aburú ohun tí Ó dá,1 ');
INSERT INTO "aykyuni" VALUES(113,3,'113:3','àti níbi aburú òru nígbà tí ó bá ṣóòkùn,');
INSERT INTO "aykyuni" VALUES(113,4,'113:4','àti níbi aburú àwọn (òpìdán) tó ń fẹnu fátẹ́gùn túẹ́túẹ́ sínú àwọn ońdè,');
INSERT INTO "aykyuni" VALUES(113,5,'113:5','àti níbi aburú onílara nígbà tí ó bá ṣe ìlara.”');
INSERT INTO "aykyuni" VALUES(114,1,'114:1','Sọ pé: “Mo sá di Olúwa àwọn ènìyàn,');
INSERT INTO "aykyuni" VALUES(114,2,'114:2','Ọba àwọn ènìyàn,');
INSERT INTO "aykyuni" VALUES(114,3,'114:3','Ọlọ́hun àwọn ènìyàn,1 ');
INSERT INTO "aykyuni" VALUES(114,4,'114:4','níbi aburú (Èṣù) oníròyíròyí, olùsásẹ́yìn (fún ẹni tó bá ń dárúkọ Allāhu. ');
INSERT INTO "aykyuni" VALUES(114,5,'114:5','(Èṣù ni) ẹni tí ó ń kó ròyíròyí sínú àwọn ọkàn ènìyàn.');
INSERT INTO "aykyuni" VALUES(114,6,'114:6','(Èṣù náà) wà nínú àwọn àlùjànnú àti àwọn ènìyàn.”');
